INSERT INTO "transliteration" VALUES(39,11,'39:11','Qul innee omirtu an aAAbuda Allahamukhlisan lahu addeen');
INSERT INTO "transliteration" VALUES(39,12,'39:12','Waomirtu li-an akoona awwala almuslimeen');
INSERT INTO "transliteration" VALUES(39,13,'39:13','Qul innee akhafu in AAasayturabbee AAathaba yawmin AAatheem');
INSERT INTO "transliteration" VALUES(39,14,'39:14','Quli Allaha aAAbudu mukhlisanlahu deenee');
INSERT INTO "transliteration" VALUES(39,15,'39:15','FaAAbudoo ma shi/tum mindoonihi qul inna alkhasireena allatheena khasirooanfusahum waahleehim yawma alqiyamati ala thalikahuwa alkhusranu almubeen');
INSERT INTO "transliteration" VALUES(39,16,'39:16','Lahum min fawqihim thulalunmina annari wamin tahtihim thulalunthalika yukhawwifu Allahu bihi AAibadahu yaAAibadi fattaqoon');
