INSERT INTO "transliteration" VALUES(39,19,'39:19','Afaman haqqa AAalayhi kalimatu alAAathabiafaanta tunqithu man fee annar');
INSERT INTO "transliteration" VALUES(39,20,'39:20','Lakini allatheena ittaqawrabbahum lahum ghurafun min fawqiha ghurafun mabniyyatuntajree min tahtiha al-anharu waAAda Allahila yukhlifu Allahu almeeAAad');
INSERT INTO "transliteration" VALUES(39,21,'39:21','Alam tara anna Allaha anzala mina assama-imaan fasalakahu yanabeeAAa fee al-ardithumma yukhriju bihi zarAAan mukhtalifan alwanuhu thummayaheeju fatarahu musfarran thumma yajAAaluhu hutamaninna fee thalika lathikra li-olee al-albab');
