INSERT INTO "transliteration" VALUES(39,23,'39:23','Allahu nazzala ahsana alhadeethikitaban mutashabihan mathaniya taqshaAAirruminhu juloodu allatheena yakhshawna rabbahum thummataleenu julooduhum waquloobuhum ila thikri Allahithalika huda Allahi yahdee bihi man yashaowaman yudlili Allahu fama lahu min had');
