INSERT INTO "transliteration" VALUES(40,21,'40:21','Awa lam yaseeroo fee al-ardi fayanthurookayfa kana AAaqibatu allatheena kanoomin qablihim kanoo hum ashadda minhum quwwatan waatharanfee al-ardi faakhathahumu Allahu bithunoobihimwama kana lahum mina Allahi min waq');
