INSERT INTO "transliteration" VALUES(40,27,'40:27','Waqala moosa innee AAuthtubirabbee warabbikum min kulli mutakabbirin la yu/minubiyawmi alhisab');
INSERT INTO "transliteration" VALUES(40,28,'40:28','Waqala rajulun mu/minun min alifirAAawna yaktumu eemanahu ataqtuloona rajulan an yaqoolarabbiyya Allahu waqad jaakum bilbayyinatimin rabbikum wa-in yaku kathiban faAAalayhi kathibuhuwa-in yaku sadiqan yusibkum baAAdu allatheeyaAAidukum inna Allaha la yahdee man huwa musrifunkaththab');
INSERT INTO "transliteration" VALUES(40,29,'40:29','Ya qawmi lakumu almulku alyawma thahireenafee al-ardi faman yansuruna min ba/si Allahiin jaana qala firAAawnu ma oreekumilla ma ara wama ahdeekum illasabeela arrashad');
INSERT INTO "transliteration" VALUES(40,30,'40:30','Waqala allathee amana yaqawmi innee akhafu AAalaykum mithla yawmi al-ahzab');
INSERT INTO "transliteration" VALUES(40,31,'40:31','Mithla da/bi qawmi noohin waAAadinwathamooda wallatheena min baAAdihim wamaAllahu yureedu thulman lilAAibad');
INSERT INTO "transliteration" VALUES(40,32,'40:32','Waya qawmi innee akhafuAAalaykum yawma attanad');
INSERT INTO "transliteration" VALUES(40,33,'40:33','Yawma tuwalloona mudbireena ma lakummina Allahi min AAasimin waman yudlili Allahufama lahu min had');
INSERT INTO "transliteration" VALUES(40,34,'40:34','Walaqad jaakum yoosufu min qablu bilbayyinatifama ziltum fee shakkin mimma jaakum bihi hattaitha halaka qultum lan yabAAatha Allahu minbaAAdihi rasoolan kathalika yudillu Allahuman huwa musrifun murtab');
INSERT INTO "transliteration" VALUES(40,35,'40:35','Allatheena yujadiloona fee ayatiAllahi bighayri sultanin atahum kaburamaqtan AAinda Allahi waAAinda allatheena amanookathalika yatbaAAu Allahu AAala kulliqalbi mutakabbirin jabbar');
INSERT INTO "transliteration" VALUES(40,36,'40:36','Waqala firAAawnu ya hamanuibni lee sarhan laAAallee ablughu al-asbab');
INSERT INTO "transliteration" VALUES(40,37,'40:37','Asbaba assamawatifaattaliAAa ila ilahi moosa wa-inneelaathunnuhu kathiban wakathalikazuyyina lifirAAawna soo-o AAamalihi wasudda AAani assabeeliwama kaydu firAAawna illa fee tabab');
INSERT INTO "transliteration" VALUES(40,38,'40:38','Waqala allathee amana yaqawmi ittabiAAooni ahdikum sabeela arrashad');
INSERT INTO "transliteration" VALUES(40,39,'40:39','Ya qawmi innama hathihialhayatu addunya mataAAunwa-inna al-akhirata hiya daru alqarar');
INSERT INTO "transliteration" VALUES(40,40,'40:40','Man AAamila sayyi-atan fala yujzailla mithlaha waman AAamila salihanmin thakarin aw ontha wahuwa mu/minun faola-ikayadkhuloona aljannata yurzaqoona feeha bighayri hisab');
INSERT INTO "transliteration" VALUES(40,41,'40:41','Waya qawmi malee adAAookum ilaannajati watadAAoonanee ila annar');
