INSERT INTO "transliteration" VALUES(40,43,'40:43','La jarama annama tadAAoonaneeilayhi laysa lahu daAAwatun fee addunya walafee al-akhirati waanna maraddana ila Allahiwaanna almusrifeena hum as-habu annar');
INSERT INTO "transliteration" VALUES(40,44,'40:44','Fasatathkuroona ma aqoolulakum waofawwidu amree ila Allahi inna Allahabaseerun bilAAibad');
INSERT INTO "transliteration" VALUES(40,45,'40:45','Fawaqahu Allahu sayyi-atima makaroo wahaqa bi-ali firAAawna soo-oalAAathab');
INSERT INTO "transliteration" VALUES(40,46,'40:46','Annaru yuAAradoonaAAalayha ghuduwwan waAAashiyyan wayawma taqoomu assaAAatuadkhiloo ala firAAawna ashadda alAAathab');
INSERT INTO "transliteration" VALUES(40,47,'40:47','Wa-ith yatahajjoona fee annarifayaqoolu adduAAafao lillatheenaistakbaroo inna kunna lakum tabaAAan fahal antummughnoona AAanna naseeban mina annar');
INSERT INTO "transliteration" VALUES(40,48,'40:48','Qala allatheena istakbaroo innakullun feeha inna Allaha qad hakama baynaalAAibad');
INSERT INTO "transliteration" VALUES(40,49,'40:49','Waqala allatheena fee annarilikhazanati jahannama odAAoo rabbakum yukhaffif AAannayawman mina alAAathab');
