INSERT INTO "transliteration" VALUES(40,51,'40:51','Inna lanansuru rusulanawallatheena amanoo fee alhayatiaddunya wayawma yaqoomu al-ashhad');
INSERT INTO "transliteration" VALUES(40,52,'40:52','Yawma la yanfaAAu aththalimeenamaAAthiratuhum walahumu allaAAnatu walahum soo-o addar');
INSERT INTO "transliteration" VALUES(40,53,'40:53','Walaqad atayna moosaalhuda waawrathna banee isra-eela alkitab');
INSERT INTO "transliteration" VALUES(40,54,'40:54','Hudan wathikra li-olee al-albab');
INSERT INTO "transliteration" VALUES(40,55,'40:55','Fasbir inna waAAda Allahihaqqun wastaghfir lithanbika wasabbihbihamdi rabbika bilAAashiyyi wal-ibkar');
INSERT INTO "transliteration" VALUES(40,56,'40:56','Inna allatheena yujadiloonafee ayati Allahi bighayri sultanin atahumin fee sudoorihim illa kibrun ma hum bibaligheehifastaAAith billahi innahu huwa assameeAAualbaseer');
INSERT INTO "transliteration" VALUES(40,57,'40:57','Lakhalqu assamawati wal-ardiakbaru min khalqi annasi walakinna aktharaannasi la yaAAlamoon');
INSERT INTO "transliteration" VALUES(40,58,'40:58','Wama yastawee al-aAAma walbaseeruwallatheena amanoo waAAamiloo assalihatiwala almusee-o qaleelan ma tatathakkaroon');
INSERT INTO "transliteration" VALUES(40,59,'40:59','Inna assaAAata laatiyatunla rayba feeha walakinna akthara annasila yu/minoon');
