INSERT INTO "transliteration" VALUES(40,61,'40:61','Allahu allathee jaAAala lakumuallayla litaskunoo feehi wannahara mubsiraninna Allaha lathoo fadlin AAala annasiwalakinna akthara annasi layashkuroon');
INSERT INTO "transliteration" VALUES(40,62,'40:62','Thalikumu Allahu rabbukum khaliqukulli shay-in la ilaha illa huwa faannatu/fakoon');
INSERT INTO "transliteration" VALUES(40,63,'40:63','Kathalika yu/faku allatheena kanoobi-ayati Allahi yajhadoon');
