INSERT INTO "transliteration" VALUES(40,66,'40:66','Qul innee nuheetu an aAAbuda allatheenatadAAoona min dooni Allahi lamma jaaniyaalbayyinatu min rabbee waomirtu an oslima lirabbi alAAalameen');
INSERT INTO "transliteration" VALUES(40,67,'40:67','Huwa allathee khalaqakum min turabinthumma min nutfatin thumma min AAalaqatin thummayukhrijukum tiflan thumma litablughoo ashuddakum thummalitakoonoo shuyookhan waminkum man yutawaffa min qabluwalitablughoo ajalan musamman walaAAallakum taAAqiloon');
