INSERT INTO "transliteration" VALUES(40,81,'40:81','Wayureekum ayatihi faayya ayatiAllahi tunkiroon');
INSERT INTO "transliteration" VALUES(40,82,'40:82','Afalam yaseeroo fee al-ardi fayanthurookayfa kana AAaqibatu allatheena min qablihimkanoo akthara minhum waashadda quwwatan waatharanfee al-ardi fama aghna AAanhum ma kanooyaksiboon');
