INSERT INTO "transliteration" VALUES(41,30,'41:30','Inna allatheena qaloo rabbunaAllahu thumma istaqamoo tatanazzalu AAalayhimualmala-ikatu alla takhafoo wala tahzanoowaabshiroo biljannati allatee kuntum tooAAadoon');
