INSERT INTO "transliteration" VALUES(41,40,'41:40','Inna allatheena yulhidoona feeayatina la yakhfawna AAalaynaafaman yulqa fee annari khayrun ammanya/tee aminan yawma alqiyamati iAAmaloo mashi/tum innahu bima taAAmaloona baseer');
