INSERT INTO "transliteration" VALUES(57,24,'57:24','Allatheena yabkhaloona waya/muroona annasabilbukhli waman yatawalla fa-inna Allaha huwaalghaniyyu alhameed');
INSERT INTO "transliteration" VALUES(57,25,'57:25','Laqad arsalna rusulana bilbayyinatiwaanzalna maAAahumu alkitaba walmeezanaliyaqooma annasu bilqisti waanzalnaalhadeeda feehi ba/sun shadeedun wamanafiAAu linnasiwaliyaAAlama Allahu man yansuruhu warusulahu bilghaybiinna Allaha qawiyyun AAazeez');
INSERT INTO "transliteration" VALUES(57,26,'57:26','Walaqad arsalna noohan wa-ibraheemawajaAAalna fee thurriyyatihima annubuwwatawalkitaba faminhum muhtadin wakatheerun minhum fasiqoon');
INSERT INTO "transliteration" VALUES(57,27,'57:27','Thumma qaffayna AAala atharihimbirusulina waqaffayna biAAeesa ibni maryamawaataynahu al-injeela wajaAAalna fee quloobiallatheena ittabaAAoohu ra/fatan warahmatan warahbaniyyatanibtadaAAooha ma katabnaha AAalayhimilla ibtighaa ridwani Allahifama raAAawha haqqa riAAayatihafaatayna allatheena amanoo minhumajrahum wakatheerun minhum fasiqoon');
INSERT INTO "transliteration" VALUES(57,28,'57:28','Ya ayyuha allatheena amanooittaqoo Allaha waaminoo birasoolihi yu/tikumkiflayni min rahmatihi wayajAAal lakum nooran tamshoonabihi wayaghfir lakum wallahu ghafoorun raheem');
INSERT INTO "transliteration" VALUES(57,29,'57:29','Li-alla yaAAlama ahlu alkitabialla yaqdiroona AAala shay-in min fadli Allahiwaanna alfadla biyadi Allahi yu/teehi man yashaowallahu thoo alfadli alAAatheem');
INSERT INTO "transliteration" VALUES(58,1,'58:1','Qad samiAAa Allahu qawla allatee tujadilukafee zawjiha watashtakee ila Allahi wallahuyasmaAAu tahawurakuma inna Allaha sameeAAunbaseer');
INSERT INTO "transliteration" VALUES(58,2,'58:2','Allatheena yuthahiroonaminkum min nisa-ihim ma hunna ommahatihim inommahatuhum illa alla-ee waladnahumwa-innahum layaqooloona munkaran mina alqawli wazooran wa-innaAllaha laAAafuwwun ghafoor');
INSERT INTO "transliteration" VALUES(58,3,'58:3','Wallatheena yuthahiroonamin nisa-ihim thumma yaAAoodoona lima qaloofatahreeru raqabatin min qabli an yatamassa thalikumtooAAathoona bihi wallahu bimataAAmaloona khabeer');
INSERT INTO "transliteration" VALUES(58,4,'58:4','Faman lam yajid fasiyamushahrayni mutatabiAAayni min qabli an yatamassafaman lam yastatiAA fa-itAAamu sitteenamiskeenan thalika litu/minoo billahiwarasoolihi watilka hudoodu Allahi walilkafireenaAAathabun aleem');
INSERT INTO "transliteration" VALUES(58,5,'58:5','Inna allatheena yuhaddoona Allahawarasoolahu kubitoo kama kubita allatheena minqablihim waqad anzalna ayatin bayyinatinwalilkafireena AAathabun muheen');
INSERT INTO "transliteration" VALUES(58,6,'58:6','Yawma yabAAathuhumu Allahu jameeAAanfayunabbi-ohum bima AAamiloo ahsahu Allahuwanasoohu wallahu AAala kulli shay-inshaheed');
INSERT INTO "transliteration" VALUES(58,7,'58:7','Alam tara anna Allaha yaAAlamu mafee assamawati wama fee al-ardima yakoonu min najwa thalathatin illahuwa rabiAAuhum wala khamsatin illa huwa sadisuhumwala adna min thalika wala aktharailla huwa maAAahum ayna ma kanoo thummayunabbi-ohum bima AAamiloo yawma alqiyamati innaAllaha bikulli shay-in AAaleem');
INSERT INTO "transliteration" VALUES(58,8,'58:8','Alam tara ila allatheena nuhooAAani annajwa thumma yaAAoodoona lima nuhooAAanhu wayatanajawna bil-ithmi walAAudwaniwamaAAsiyati arrasooli wa-itha jaookahayyawka bima lam yuhayyika bihi Allahuwayaqooloona fee anfusihim lawla yuAAaththibunaAllahu bima naqoolu hasbuhum jahannamu yaslawnahafabi/sa almaseer');
INSERT INTO "transliteration" VALUES(58,9,'58:9','Ya ayyuha allatheena amanooitha tanajaytum fala tatanajaw bil-ithmiwalAAudwani wamaAAsiyati arrasooliwatanajaw bilbirri wattaqwa wattaqooAllaha allathee ilayhi tuhsharoon');
INSERT INTO "transliteration" VALUES(58,10,'58:10','Innama annajwa mina ashshaytaniliyahzuna allatheena amanoo walaysa bidarrihimshay-an illa bi-ithni Allahi waAAalaAllahi falyatawakkali almu/minoon');
INSERT INTO "transliteration" VALUES(58,11,'58:11','Ya ayyuha allatheena amanooitha qeela lakum tafassahoo fee almajalisifafsahoo yafsahi Allahu lakum wa-ithaqeela onshuzoo fanshuzoo yarfaAAi Allahu allatheenaamanoo minkum wallatheena ootoo alAAilmadarajatin wallahu bima taAAmaloonakhabeer');
INSERT INTO "transliteration" VALUES(58,12,'58:12','Ya ayyuha allatheena amanooitha najaytumu arrasoola faqaddimoo baynayaday najwakum sadaqatan thalika khayrunlakum waatharu fa-in lam tajidoo fa-inna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(58,13,'58:13','Aashfaqtum an tuqaddimoo bayna yaday najwakumsadaqatin fa-ith lam tafAAaloo watabaAllahu AAalaykum faaqeemoo assalatawaatoo azzakata waateeAAoo Allahawarasoolahu wallahu khabeerun bimataAAmaloon');
INSERT INTO "transliteration" VALUES(58,14,'58:14','Alam tara ila allatheenatawallaw qawman ghadiba Allahu AAalayhim mahum minkum wala minhum wayahlifoona AAalaalkathibi wahum yaAAlamoon');
INSERT INTO "transliteration" VALUES(58,15,'58:15','aAAadda Allahu lahum AAathabanshadeedan innahum saa ma kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(58,16,'58:16','Ittakhathoo aymanahum junnatanfasaddoo AAan sabeeli Allahi falahum AAathabunmuheen');
INSERT INTO "transliteration" VALUES(58,17,'58:17','Lan tughniya AAanhum amwaluhum walaawladuhum mina Allahi shay-an ola-ika as-habuannari hum feeha khalidoon');
INSERT INTO "transliteration" VALUES(58,18,'58:18','Yawma yabAAathuhumu Allahu jameeAAanfayahlifoona lahu kama yahlifoona lakum wayahsaboonaannahum AAala shay-in ala innahum humu alkathiboon');
INSERT INTO "transliteration" VALUES(58,19,'58:19','Istahwatha AAalayhimu ashshaytanufaansahum thikra Allahi ola-ika hizbuashshaytani ala inna hizba ashshaytanihumu alkhasiroon');
INSERT INTO "transliteration" VALUES(58,20,'58:20','Inna allatheena yuhaddoona Allahawarasoolahu ola-ika fee al-athalleen');
INSERT INTO "transliteration" VALUES(58,21,'58:21','Kataba Allahu laaghlibanna anawarusulee inna Allaha qawiyyun AAazeez');
INSERT INTO "transliteration" VALUES(58,22,'58:22','La tajidu qawman yu/minoona billahiwalyawmi al-akhiri yuwaddoona man haddaAllaha warasoolahu walaw kanoo abaahumaw abnaahum aw ikhwanahum aw AAasheeratahum ola-ikakataba fee quloobihimu al-eemana waayyadahum biroohinminhu wayudkhiluhum jannatin tajree min tahtihaal-anharu khalideena feeha radiya AllahuAAanhum waradoo AAanhu ola-ika hizbu Allahiala inna hizba Allahi humu almuflihoon');
INSERT INTO "transliteration" VALUES(59,1,'59:1','Sabbaha lillahi ma fee assamawatiwama fee al-ardi wahuwa alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(59,2,'59:2','Huwa allathee akhraja allatheenakafaroo min ahli alkitabi min diyarihim li-awwalialhashri ma thanantum an yakhrujoo wathannooannahum maniAAatuhum husoonuhum mina Allahifaatahumu Allahu min haythu lam yahtasiboowaqathafa fee quloobihimu arruAAba yukhriboonabuyootahum bi-aydeehim waaydee almu/mineena faAAtabiroo yaolee al-absar');
INSERT INTO "transliteration" VALUES(59,3,'59:3','Walawla an kataba AllahuAAalayhimu aljalaa laAAaththabahum fee addunyawalahum fee al-akhirati AAathabu annar');
INSERT INTO "transliteration" VALUES(59,4,'59:4','Thalika bi-annahum shaqqoo Allahawarasoolahu waman yushaqqi Allaha fa-inna Allahashadeedu alAAiqab');
INSERT INTO "transliteration" VALUES(59,5,'59:5','Ma qataAAtum min leenatin awtaraktumooha qa-imatan AAala osoolihafabi-ithni Allahi waliyukhziya alfasiqeen');
INSERT INTO "transliteration" VALUES(59,6,'59:6','Wama afaa Allahu AAalarasoolihi minhum fama awjaftum AAalayhi min khaylin walarikabin walakinna Allaha yusalliturusulahu AAala man yashao wallahuAAala kulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(59,7,'59:7','Ma afaa Allahu AAalarasoolihi min ahli alqura falillahi walirrasooliwalithee alqurba walyatama walmasakeeniwabni assabeeli kay la yakoona doolatanbayna al-aghniya-i minkum wama atakumuarrasoolu fakhuthoohu wama nahakumAAanhu fantahoo wattaqoo Allaha inna Allahashadeedu alAAiqab');
INSERT INTO "transliteration" VALUES(59,8,'59:8','Lilfuqara-i almuhajireena allatheenaokhrijoo min diyarihim waamwalihim yabtaghoona fadlanmina Allahi waridwanan wayansuroonaAllaha warasoolahu ola-ika humu assadiqoon');
INSERT INTO "transliteration" VALUES(59,9,'59:9','Wallatheena tabawwaoo addarawal-eemana min qablihim yuhibboona man hajarailayhim wala yajidoona fee sudoorihim hajatanmimma ootoo wayu/thiroona AAala anfusihim walaw kanabihim khasasatun waman yooqa shuhha nafsihi faola-ikahumu almuflihoon');
INSERT INTO "transliteration" VALUES(59,10,'59:10','Wallatheena jaoo minbaAAdihim yaqooloona rabbana ighfir lana wali-ikhwaninaallatheena sabaqoona bil-eemani walatajAAal fee quloobina ghillan lillatheena amanoorabbana innaka raoofun raheem');
INSERT INTO "transliteration" VALUES(59,11,'59:11','Alam tara ila allatheena nafaqooyaqooloona li-ikhwanihimu allatheena kafaroo minahli alkitabi la-in okhrijtum lanakhrujanna maAAakum walanuteeAAu feekum ahadan abadan wa-in qootiltum lanansurannakumwallahu yashhadu innahum lakathiboon');
INSERT INTO "transliteration" VALUES(59,12,'59:12','La-in okhrijoo la yakhrujoonamaAAahum wala-in qootiloo la yansuroonahum wala-innasaroohum layuwallunna al-adbara thumma layunsaroon');
INSERT INTO "transliteration" VALUES(59,13,'59:13','Laantum ashaddu rahbatan fee sudoorihimmina Allahi thalika bi-annahum qawmun layafqahoon');
INSERT INTO "transliteration" VALUES(59,14,'59:14','La yuqatiloonakum jameeAAanilla fee quran muhassanatin aw min wara-ijudurin ba/suhum baynahum shadeedun tahsabuhum jameeAAanwaquloobuhum shatta thalika bi-annahum qawmun layaAAqiloon');
INSERT INTO "transliteration" VALUES(59,15,'59:15','Kamathali allatheena min qablihimqareeban thaqoo wabala amrihim walahum AAathabunaleem');
INSERT INTO "transliteration" VALUES(59,16,'59:16','Kamathali ashshaytani ithqala lil-insani okfur falamma kafara qalainnee baree-on minka innee akhafu Allaha rabba alAAalameen');
INSERT INTO "transliteration" VALUES(59,17,'59:17','Fakana AAaqibatahumaannahuma fee annari khalidayni feehawathalika jazao aththalimeen');
INSERT INTO "transliteration" VALUES(59,18,'59:18','Ya ayyuha allatheena amanooittaqoo Allaha waltanthur nafsun maqaddamat lighadin wattaqoo Allaha inna Allahakhabeerun bima taAAmaloon');
INSERT INTO "transliteration" VALUES(59,19,'59:19','Wala takoonoo kallatheenanasoo Allaha faansahum anfusahum ola-ikahumu alfasiqoon');
INSERT INTO "transliteration" VALUES(59,20,'59:20','La yastawee as-habu annariwaas-habu aljannati as-habu aljannatihumu alfa-izoon');
INSERT INTO "transliteration" VALUES(59,21,'59:21','Law anzalna hatha alqur-anaAAala jabalin laraaytahu khashiAAan mutasaddiAAanmin khashyati Allahi watilka al-amthalu nadribuhalinnasi laAAallahum yatafakkaroon');
INSERT INTO "transliteration" VALUES(59,22,'59:22','Huwa Allahu allathee lailaha illa huwa AAalimu alghaybi washshahadatihuwa arrahmanu arraheem');
INSERT INTO "transliteration" VALUES(59,23,'59:23','Huwa Allahu allathee lailaha illa huwa almaliku alquddoosu assalamualmu/minu almuhayminu alAAazeezu aljabbaru almutakabbirusubhana Allahi AAamma yushrikoon');
INSERT INTO "transliteration" VALUES(59,24,'59:24','Huwa Allahu alkhaliqu albari-oalmusawwiru lahu al-asmao alhusnayusabbihu lahu ma fee assamawatiwal-ardi wahuwa alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(60,1,'60:1','Ya ayyuha allatheena amanoola tattakhithoo AAaduwwee waAAaduwwakum awliyaatulqoona ilayhim bilmawaddati waqad kafaroo bima jaakummina alhaqqi yukhrijoona arrasoola wa-iyyakuman tu/minoo billahi rabbikum in kuntum kharajtumjihadan fee sabeelee wabtighaa mardateetusirroona ilayhim bilmawaddati waana aAAlamu bimaakhfaytum wama aAAlantum waman yafAAalhu minkum faqad dallasawaa assabeel');
INSERT INTO "transliteration" VALUES(60,2,'60:2','In yathqafookum yakoonoo lakam aAAdaanwayabsutoo ilaykum aydiyahum waalsinatahum bissoo-iwawaddoo law takfuroon');
INSERT INTO "transliteration" VALUES(60,3,'60:3','Lan tanfaAAakum arhamukum walaawladukum yawma alqiyamati yafsilu baynakumwallahu bima taAAmaloona baseer');
INSERT INTO "transliteration" VALUES(60,4,'60:4','Qad kanat lakum oswatun hasanatunfee ibraheema wallatheena maAAahu ithqaloo liqawmihim inna buraao minkum wamimmataAAbudoona min dooni Allahi kafarna bikum wabadabaynana wabaynakumu alAAadawatu walbaghdaoabadan hatta tu/minoo billahi wahdahuilla qawla ibraheema li-abeehi laastaghfiranna lakawama amliku laka mina Allahi min shay-in rabbanaAAalayka tawakkalna wa-ilayka anabna wa-ilayka almaseer');
INSERT INTO "transliteration" VALUES(60,5,'60:5','Rabbana la tajAAalnafitnatan lillatheena kafaroo waghfir lanarabbana innaka anta alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(60,6,'60:6','Laqad kana lakum feehim oswatun hasanatunliman kana yarjoo Allaha walyawma al-akhirawaman yatawalla fa-inna Allaha huwa alghanniyyu alhameed');
INSERT INTO "transliteration" VALUES(60,7,'60:7','AAasa Allahu an yajAAalabaynakum wabayna allatheena AAadaytum minhummawaddatan wallahu qadeerun wallahughafoorun raheem');
INSERT INTO "transliteration" VALUES(60,8,'60:8','La yanhakumu AllahuAAani allatheena lam yuqatilookum fee addeeniwalam yukhrijookum min diyarikum an tabarroohum watuqsitooilayhim inna Allaha yuhibbu almuqsiteen');
INSERT INTO "transliteration" VALUES(60,9,'60:9','Innama yanhakumu AllahuAAani allatheena qatalookum fee addeeniwaakhrajookum min diyarikum wathaharooAAala ikhrajikum an tawallawhum waman yatawallahumfaola-ika humu aththalimoon');
INSERT INTO "transliteration" VALUES(60,10,'60:10','Ya ayyuha allatheena amanooitha jaakumu almu/minatu muhajiratinfamtahinoohunna Allahu aAAlamu bi-eemanihinnafa-in AAalimtumoohunna mu/minatin falatarjiAAoohunna ila alkuffari la hunna hillunlahum wala hum yahilloona lahunna waatoohumma anfaqoo wala junaha AAalaykum an tankihoohunnaitha ataytumoohunna ojoorahunna walatumsikoo biAAisami alkawafiri was-aloo maanfaqtum walyas-aloo ma anfaqoo thalikum hukmuAllahi yahkumu baynakum wallahuAAaleemun hakeem');
INSERT INTO "transliteration" VALUES(60,11,'60:11','Wa-in fatakum shay-on min azwajikumila alkuffari faAAaqabtum faatoo allatheenathahabat azwajuhum mithla ma anfaqoo wattaqooAllaha allathee antum bihi mu/minoon');
INSERT INTO "transliteration" VALUES(60,12,'60:12','Ya ayyuha annabiyyu ithajaaka almu/minatu yubayiAAnaka AAalaan la yushrikna billahi shay-an walayasriqna wala yazneena wala yaqtulna awladahunnawala ya/teena bibuhtanin yaftareenahu baynaaydeehinna waarjulihinna wala yaAAseenaka feemaAAroofin fabayiAAhunna wastaghfir lahunna Allahainna Allaha ghafoorun raheem');
INSERT INTO "transliteration" VALUES(60,13,'60:13','Ya ayyuha allatheena amanoola tatawallaw qawman ghadiba AllahuAAalayhim qad ya-isoo mina al-akhirati kama ya-isaalkuffaru min as-habi alquboor');
INSERT INTO "transliteration" VALUES(61,1,'61:1','Sabbaha lillahi ma fee assamawatiwama fee al-ardi wahuwa alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(61,2,'61:2','Ya ayyuha allatheena amanoolima taqooloona ma la tafAAaloon');
INSERT INTO "transliteration" VALUES(61,3,'61:3','Kabura maqtan AAinda Allahi antaqooloo ma la tafAAaloon');
INSERT INTO "transliteration" VALUES(61,4,'61:4','Inna Allaha yuhibbu allatheenayuqatiloona fee sabeelihi saffan kaannahum bunyanunmarsoos');
INSERT INTO "transliteration" VALUES(61,5,'61:5','Wa-ith qala moosaliqawmihi ya qawmi lima tu/thoonanee waqadtaAAlamoona annee rasoolu Allahi ilaykum falamma zaghooazagha Allahu quloobahum wallahu layahdee alqawma alfasiqeen');
INSERT INTO "transliteration" VALUES(61,6,'61:6','Wa-ith qala AAeesa ibnumaryama ya banee isra-eela innee rasoolu Allahiilaykum musaddiqan lima bayna yadayya mina attawratiwamubashshiran birasoolin ya/tee min baAAdee ismuhu ahmadufalamma jaahum bilbayyinati qaloohatha sihrun mubeen');
INSERT INTO "transliteration" VALUES(61,7,'61:7','Waman athlamu mimmani iftaraAAala Allahi alkathiba wahuwa yudAAaila al-islami wallahu layahdee alqawma aththalimeen');
INSERT INTO "transliteration" VALUES(61,8,'61:8','Yureedoona liyutfi-oo noora Allahibi-afwahihim wallahu mutimmu noorihi walawkariha alkafiroon');
INSERT INTO "transliteration" VALUES(61,9,'61:9','Huwa allathee arsala rasoolahu bilhudawadeeni alhaqqi liyuthhirahu AAala addeenikullihi walaw kariha almushrikoon');
INSERT INTO "transliteration" VALUES(61,10,'61:10','Ya ayyuha allatheena amanoohal adullukum AAala tijaratin tunjeekum min AAathabinaleem');
INSERT INTO "transliteration" VALUES(61,11,'61:11','Tu/minoona billahiwarasoolihi watujahidoona fee sabeeli Allahi bi-amwalikumwaanfusikum thalikum khayrun lakum in kuntum taAAlamoon');
INSERT INTO "transliteration" VALUES(61,12,'61:12','Yaghfir lakum thunoobakumwayudkhilkum jannatin tajree min tahtihaal-anharu wamasakina tayyibatan fee jannatiAAadnin thalika alfawzu alAAatheem');
INSERT INTO "transliteration" VALUES(61,13,'61:13','Waokhra tuhibboonaha nasrunmina Allahi wafathun qarreebun wabashshirialmu/mineen');
INSERT INTO "transliteration" VALUES(61,14,'61:14','Ya ayyuha allatheena amanookoonoo ansara Allahi kama qala AAeesaibnu maryama lilhawariyyeena man ansaree ilaAllahi qala alhawariyyoona nahnuansaru Allahi faamanat ta-ifatun minbanee isra-eela wakafarat ta-ifatun faayyadnaallatheena amanoo AAala AAaduwwihim faasbahoothahireen');
INSERT INTO "transliteration" VALUES(62,1,'62:1','Yusabbihu lillahi ma feeassamawati wama fee al-ardialmaliki alquddoosi alAAazeezi alhakeem');
INSERT INTO "transliteration" VALUES(62,2,'62:2','Huwa allathee baAAatha feeal-ommiyyeena rasoolan minhum yatloo AAalayhim ayatihiwayuzakkeehim wayuAAallimuhumu alkitaba walhikmatawa-in kanoo min qablu lafee dalalin mubeen');
INSERT INTO "transliteration" VALUES(62,3,'62:3','Waakhareena minhum lamma yalhaqoobihim wahuwa alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(62,4,'62:4','Thalika fadlu Allahiyu/teehi man yashao wallahu thooalfadli alAAatheem');
INSERT INTO "transliteration" VALUES(62,5,'62:5','Mathalu allatheena hummiloo attawratathumma lam yahmilooha kamathali alhimariyahmilu asfaran bi/sa mathalu alqawmi allatheenakaththaboo bi-ayati Allahi wallahula yahdee alqawma aththalimeen');
INSERT INTO "transliteration" VALUES(62,6,'62:6','Qul ya ayyuha allatheenahadoo in zaAAamtum annakum awliyao lillahimin dooni annasi fatamannawoo almawta in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(62,7,'62:7','Wala yatamannawnahu abadan bimaqaddamat aydeehim wallahu AAaleemun biththalimeen');
INSERT INTO "transliteration" VALUES(62,8,'62:8','Qul inna almawta allathee tafirroonaminhu fa-innahu mulaqeekum thumma turaddoona ila AAalimialghaybi washshahadati fayunabbi-okum bimakuntum taAAmaloon');
INSERT INTO "transliteration" VALUES(62,9,'62:9','Ya ayyuha allatheena amanooitha noodiya lissalati min yawmialjumuAAati fasAAaw ila thikri Allahiwatharoo albayAAa thalikum khayrun lakum in kuntumtaAAlamoon');
INSERT INTO "transliteration" VALUES(62,10,'62:10','Fa-itha qudiyati assalatufantashiroo fee al-ardi wabtaghoo min fadliAllahi wathkuroo Allaha katheeranlaAAallakum tuflihoon');
INSERT INTO "transliteration" VALUES(62,11,'62:11','Wa-itha raaw tijaratan awlahwan infaddoo ilayha watarakooka qa-imanqul ma AAinda Allahi khayrun mina allahwi wamina attijaratiwallahu khayru arraziqeen');
INSERT INTO "transliteration" VALUES(63,1,'63:1','Itha jaaka almunafiqoonaqaloo nashhadu innaka larasoolu Allahi wallahuyaAAlamu innaka larasooluhu wallahu yashhadu innaalmunafiqeena lakathiboon');
INSERT INTO "transliteration" VALUES(63,2,'63:2','Ittakhathoo aymanahum junnatanfasaddoo AAan sabeeli Allahi innahum saa makanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(63,3,'63:3','Thalika bi-annahum amanoothumma kafaroo fatubiAAa AAala quloobihim fahum layafqahoon');
INSERT INTO "transliteration" VALUES(63,4,'63:4','Wa-itha raaytahum tuAAjibuka ajsamuhumwa-in yaqooloo tasmaAA liqawlihim kaannahum khushubunmusannadatun yahsaboona kulla sayhatinAAalayhim humu alAAaduwwu fahtharhum qatalahumuAllahu anna yu/fakoon');
INSERT INTO "transliteration" VALUES(63,5,'63:5','Wa-itha qeela lahum taAAalawyastaghfir lakum rasoolu Allahi lawwaw ruoosahumwaraaytahum yasuddoona wahum mustakbiroon');
INSERT INTO "transliteration" VALUES(63,6,'63:6','Sawaon AAalayhim astaghfarta lahum amlam tastaghfir lahum lan yaghfira Allahu lahum inna Allahala yahdee alqawma alfasiqeen');
INSERT INTO "transliteration" VALUES(63,7,'63:7','Humu allatheena yaqooloona latunfiqoo AAala man AAinda rasooli Allahi hattayanfaddoo walillahi khaza-inu assamawatiwal-ardi walakinna almunafiqeena layafqahoon');
INSERT INTO "transliteration" VALUES(63,8,'63:8','Yaqooloona la-in rajaAAna ilaalmadeenati layukhrijanna al-aAAazzu minha al-athallawalillahi alAAizzatu walirasoolihi walilmu/mineena walakinnaalmunafiqeena la yaAAlamoon');
