INSERT INTO "ur_maududi" VALUES(1,1,'1:1','اللہ کےنام سے جو رحمان و رحیم ہے ');
INSERT INTO "ur_maududi" VALUES(1,2,'1:2','تعریف اللہ ہی کے لیے ہے جو تمام کائنات کا رب ہے');
INSERT INTO "ur_maududi" VALUES(1,3,'1:3','رحمان اور رحیم ہے ');
INSERT INTO "ur_maududi" VALUES(1,4,'1:4','روزِ جزا کا مالک ہے');
INSERT INTO "ur_maududi" VALUES(1,5,'1:5','ہم تیری ہی عبادت کرتے ہیں اور تجھی سے مدد مانگتے ہیں ');
INSERT INTO "ur_maududi" VALUES(1,6,'1:6','ہمیں سیدھا راستہ دکھا ');
INSERT INTO "ur_maududi" VALUES(1,7,'1:7','ان لوگوں کاراستہ جن پر تو نے انعام فرمایا ، جو معتوب نہیں ہوئے، جو بھٹکے ہوئے نہیں ہیں ');
INSERT INTO "ur_maududi" VALUES(2,1,'2:1','الف،لام،میم ');
INSERT INTO "ur_maududi" VALUES(2,2,'2:2','یہ اللہ کی کتاب ہے ،اس میں کوئی شک نہیں ہے۔ہدایت ہے اُن پرہیزگاروں کے لئے');
INSERT INTO "ur_maududi" VALUES(2,3,'2:3','جو غیب پر ایمان لاتے ہیں،نماز قائم کرتے ہیں ،جو رزق ہم نے اُن کو دیا ہے ،اس میں سے خرچ کرتے ہیں ');
INSERT INTO "ur_maududi" VALUES(2,4,'2:4','جو کتاب تم پر نازل کی گئی ہے (یعنی قرآن) اور جو کتابیں تم سے پہلے نازل کی گئی تھیں ان سب پر ایمان لاتےہیں ، اور آخرت پر یقین رکھتے ہیں ');
INSERT INTO "ur_maududi" VALUES(2,5,'2:5','ایسے لوگ اپنے رب کی طرف سے راہ راست پر ہیں اور وہی فلاح پانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(2,6,'2:6','جن لوگوں نے(ان باتوں کو تسلیم کرنے سے) انکار کر دیا، اُن کے لئے یکساں ہے،خواہ تم انہیں خبردار کرویا نہ کرو،بہر حال وہ ماننے والےنہیں');
INSERT INTO "ur_maududi" VALUES(2,7,'2:7','اللہ نے اُن کے دلوں اور ان کے کانوں پر مُہرلگادی ہے اور ان کی آنکھوں پر پردہ پڑگیاہے۔وہ سخت سزا کے مستحق ہیں');
INSERT INTO "ur_maududi" VALUES(2,8,'2:8','بعض لوگ ایسے بھی ہیں جو کہتے ہیں کہ ہم اللہ پر اور آخرت کے دن پر ایمان لائے ہیں، حالانکہ در حقیقت وہ مومن نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(2,9,'2:9','وہ اللہ اور ایمان لانے والوں کے ساتھ دھوکہ بازی کررہے ہیں،مگر دراصل وہ خود اپنے آپ ہی کو دھوکے میں ڈال رہے ہیں اور انہیں اس کا شعُورنہیں ہے ');
INSERT INTO "ur_maududi" VALUES(2,10,'2:10','ان کے دلوں میں ایک بیماری ہےجسے اللہ نے اور زیادہ بڑھا دیا،اور جو جھوٹ وہ بولتے ہیں،اس کی پاداش میں ان کے لئے درد ناک سزا ہے');
INSERT INTO "ur_maududi" VALUES(2,11,'2:11','جب کبھی ان سے کہا گیا کہ زمین پر فساد برپا نہ کرو تو انہوں نے یہی کہا کہ ہم تو اصلاح کرنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(2,12,'2:12','خبردار! حقیقت میں یہی لوگ مفسد ہیں مگر انہیں شعور نہیں ہے');
INSERT INTO "ur_maududi" VALUES(2,13,'2:13','اور جب ان سے کہا گیا کہ جس طرح دوسرے لوگ ایمان لائے ہیں اُسی طرح تم بھی ایمان لاوٴ تو انہوں نے یہی جواب دیا کیا ہم بیوقوفوں کی طرح ایمان لائیں؟ خبردار ! حقیقت میں تو یہ خود بیوقوف ہیں،مگر یہ جانتے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(2,14,'2:14','جب یہ اہلِ ایمان سے ملتے ہیں تو کہتے ہیں کہ ہم ایمان لائے ہیں، اور جب علیٰحدگی میں اپنے شیطانوں سے ملتے ہیں،تو کہتے ہیں کہ اصل میں تو ہم تمھارے ساتھ ہیں اور اُن لوگوں سے محض مذاق کررہے ہیں');
INSERT INTO "ur_maududi" VALUES(2,15,'2:15','اللہ ان سے مذاق کر رہا ہے، وہ ان کی رسی دراز کیے جاتا ہے، اور یہ اپنی سرکشی میں اندھوں کی طرح بھٹکتے چلے جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(2,16,'2:16','یہ وہ لوگ ہیں جنہوں نے ہدایت کے بدلے گمراہی خرید لی ہے، مگر یہ سودا ان کے لیے نفع بخش نہیں ہے اور یہ ہرگز صحیح راستے پر نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(2,17,'2:17','ان کی مثال ایسی ہے جیسے ایک شخص نے آگ روشن کی اور جب اُس نے سارے ماحول کو روشن کردیا تو اللہ نے ان کا نورِبصارت سلب کرلیااور انہیں اس حال میں چھوڑدیاکہ تاریکیوںمیں انہیں کچھ نظر نہیں آتا۔ ');
INSERT INTO "ur_maududi" VALUES(2,18,'2:18','یہ بہرے ہیں،گونگے ہیں،اندھے ہیں،یہ اب نہ پلٹیں گے');
INSERT INTO "ur_maududi" VALUES(2,19,'2:19','یا پھر ان کی مثال یوں سمجھو کہ آسمان سے زور کی بارش ہورہی ہےاور اس کے ساتھ اندھیر ی گھٹا اور کڑک اور چمک بھی ہے، یہ بجلی کے کڑاکے سُن کے اپنی جانوں کے خوف سے کانوںمیں اُنگلیاں ٹھونسے لیتے ہیں اور اللہ ان منکرین حق کو ہر طرف سے گھیرے میں لیے ہوئے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,20,'2:20','چمک سے ان کی حالت یہ ہو رہی ہے کہ گویا عنقریب بجلی اِن کی بصارت اُچک لے جائے گی۔جب ذرا کچھ روشنی انہیں محسُوس ہوتی ہے تو اس میں کچھ دُور چل لیتے ہیں اور جب ان پر اندھیرا چھا جاتا ہے تو کھڑے ہو جاتے ہیں۔اللہ چاہتا تو ان کی سماعت اور بصارت بالکل سَلب کر لیتا، یقینًا وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_maududi" VALUES(2,21,'2:21','لوگو، بندگی اختیار کرو اپنے اُس ربّ کی جو تمھارا اور تم سے پہلے لوگ ہو گزرے ہیں اُن سب کا خالِق ہے، تمھارے بچنے کی توقع اِسی صورت سے ہو سکتی ہے');
INSERT INTO "ur_maududi" VALUES(2,22,'2:22','وہی تو ہے جِس نے تمہارے لیے زمین کا فرش بچھایا، آسمان کی چھت بنائی، اوپر سے پانی برسایا اور اس کے ذریعے سے ہر طرح کی پیداوار نکال کر تمہارے لیے رزق بہم پہنچایا پس جب تم یہ جانتے ہو تو دوسروں کو اللہ کا مد مقابل نہ ٹھیراؤ');
INSERT INTO "ur_maududi" VALUES(2,23,'2:23','اور اگر تمھیں اس امرمیں شک ہےکہ یہ کتاب جو ہم نے اپنے بندے پر اتاری ہے ،یہ ہماری ہے یا نہیں،تو اس کے مانند ایک ہی سورت بنا لاوٴ،اپنے سارے ہم نواوٴں کو بُلالو، ایک اللہ کو چھوڑ کر باقی جس جس کی چاہو، مدد لےلو،اگر تم سچے ہو');
INSERT INTO "ur_maududi" VALUES(2,24,'2:24','تو یہ کام کرکے دکھاوٴ۔لیکن اگر تم نے ایسا نہ کیا ،اور یقینًا کبھی نہیں کر سکتے ،تو ڈرو اس آگ سے جس کا ایندھن بنیں گے انسان اور پتھر ،جومہیّاکی گئی ہےمنکرینِ حق کے لیے');
INSERT INTO "ur_maududi" VALUES(2,25,'2:25','اور اے پیغمبر،جو لوگ اس کتاب پر ایمان لے آئیں اور (اس کے مطابق)اپنے عمل درست کر لیں،انہیں خوشخبری دے دو کہ اُن کے لیے ایسے باغ ہیں،جن کے نیچے نہریں بہتی ہوں گی۔ان باغوں کے پھل صورت میں دُنیا کے پھلوں سے مِلتے جُلتے ہونگے۔ جب کوئی پھل انہیں کھانے کو دیا جائےگا، تو وہ کہیں گے کہ ایسے ہی پھل اس سے پہلے دُنیا میں ہم کو دیے جاتے تھے۔ان کے لیے وہاں پاکیزہ بیویاں ہونگی،اور وہ وہاں ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(2,26,'2:26','ہاں ، اللہ اس سے ہر گز نہیں شرماتا کہ مچھّر یا اس سے بھی حقیر تر کسی چیز کی تمثیلیں دے۔ جو لوگ حق بات کو قبو ل کرنے والے ہیں،وہ انہی تمثیلوں کو دیکھ کرجان لیتے ہیں کہ یہ حق ہے جو ان کے ربّ ہی کی طرف سے آیا ہے ،اور جو ماننے والے نہیں ہے ، وہ انہیں سُن کر کہنے لگتے ہیں کہ ایسی تمثیلوں سے اللہ کو کیا سروکار؟اس طرح اللہ ایک ہی بات سے بہتوں کو گمراہی میں مبتلا کر دیتا ہے اور بہتوں کو راہِ راست دکھا دیتا ہے۔ اور گمراہی میں وہ انہی کو مبتلا کر تا ہے، جو فاسق ہیں ،');
INSERT INTO "ur_maududi" VALUES(2,27,'2:27','اللہ کے عہد کو مضبوط باندھ لینے کے بعد توڑ دیتے ہیں، اللہ نے جسے جوڑنے کا حکم دیا ہےاُسے کاٹتے ہیں، اور زمین میں فساد بر پا کرتے ہیں۔ حقیقت میں یہ لوگ نقصان اُٹھانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(2,28,'2:28','تم اللہ کے ساتھ کفر کا رویہ کیسے اختیار کرتے ہو حالانکہ تم بے جان تھے، اس نے تم کو زندگی عطا کی، پھر وہی تمھاری جان سلب کرے گا، پھر وہی تمہیں دوبارہ زندگی عطا کرے گا، پھر اسی کی طرف تمہیں پلٹ کر جانا ہے');
INSERT INTO "ur_maududi" VALUES(2,29,'2:29','وہی تو ہے جس نے تمھارے لیے زمین کی ساری چیزیں پیدا کیں،پھر اُوپر کی طرف توجّہ فرمائی اور سات آسمان استوار کیے۔اور وہ ہر چیز کا علم رکھنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,30,'2:30',' پھر ذرا اس وقت کا تصوّر کرو جب تمہارے ربّ نے فرشتوں سے کہا تھاکہ”میں زمین میں ایک خلیفہ بنانے والا ہوں“ انہوں نے عرض کیا ”کیا آپ زمین میں کسی ایسے کو مقرر کرنے والے ہیں، جو اس کے انتظام کو بگاڑ دے گا اور خونریزیاں کرےگا؟ آپ کی حمد و ثناء کے ساتھ تسبیح اور آپ کےلیے تقدیس تو ہم کر ہی رہے ہیں “ فرمایا ”میں جانتا ہوں،جو کچھ تم نہیں جانتے “');
INSERT INTO "ur_maududi" VALUES(2,31,'2:31','اس کے بعد اللہ نے آدم کو ساری چیزوں کے نام سکھائے ، پھر انہیں فرشتوں کے سامنے پیش کیا اور فرمایا ”اگر تمہارا خیال صحیح ہے (کہ کسی خلیفہ کے تقرر سےانتظام بگڑ جائے گا) تو ذرا ان چیزوں کے نام بتاو');
INSERT INTO "ur_maududi" VALUES(2,32,'2:32','انہوں نے عرض کیا ”نقص سے پاک تو آپ ہی کی ذات ہے ،ہم تو بس اتنا ہی علم رکھتے ہیں ، جتنا آپ نے ہم کو دے دیا ہے۔ حقیقت میں سب کچھ جاننے والا اور سمجھنے والا آپ کے سوا کوئی نہیں۔“');
INSERT INTO "ur_maududi" VALUES(2,33,'2:33','پھر اللہ نے آدم سے کہا ”تم اِنہیں اِن چیزوں کے نام بتاوٴ۔“ جب اُس نے ان کو اُن سب کے نام بتادیے ،تو اللہ نے فرمایا ”میں نے تم سے کہا نہ تھا کہ میں آسمانوں اور زمین کی وہ ساری حقیقتیں جانتا ہوں جو تم سے مخفی ہیں،جو کچھ تم ظاہر کرتے ہو ، وہ بھی مجھے معلوم ہے اور جو کچھ تم چھُپاتے ہو، اُسے بھی میں جانتا ہوں۔“');
INSERT INTO "ur_maududi" VALUES(2,34,'2:34','پھر جب ہم نے فرشتوں کو حکم دیا کہ آدم کے آگے جُھک جاوٴ، تو سب جُھک گئے، مگر ابلیس نے انکار کیا وہ اپنی بڑائی کے گھمنڈ میں پڑگیا اور نافرمانوں میں شامل ہوگیا۔ ');
INSERT INTO "ur_maududi" VALUES(2,35,'2:35','پھر ہم نے آدم سے کہا کہ”تم اور تمہاری بیوی، دونوں جنت میں رہو اور یہاں بفراغت جو چاہو کھاوٴ، مگر اس درخت کا رُخ نہ کرنا ،ورنہ ظالموں میں شمارہوگے۔“');
INSERT INTO "ur_maududi" VALUES(2,36,'2:36','آخر کار شیطان نے ان دونوں کو اس درخت کی تر غیب دیکر ہمارے حکم کی پیروی سے ہٹایا اور انہیں اس حالت سے نکلواکر چھوڑا، جس میں وہ تھے۔ہم نے حکم دیا کہ ”اب تم سب یہاں سے اُتر جاوٴ،تم ایک دوسرے کے دُشمن ہو اور تمہیں ایک خاص وقت تک زمین میں ٹھیرنا اور وہیں گزر بسر کرنا ہے۔“');
INSERT INTO "ur_maududi" VALUES(2,37,'2:37','اس وقت آدم نے اپنے ربّ سے چند کلمات سیکھ کر توبہ کی ،جس کو اس کے ربّ نے قبول کرلیا ،کیونکہ وہ بڑا معاف کرنے والا اور رحم فرمانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,38,'2:38','ہم نے کہا کہ”تم سب یہاں سے اُتر جاوٴ۔ پھر جو میری طرف سے کوئی ہدایت تمہارے پاس پہنچے ، تو جو لوگ میری اس ہدایت کی پیر وی کریں گے ،ان کے لیے کسی خوف اور رنج کا موقع نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(2,39,'2:39','اور جو اس کو قبو ل کرنے سے انکار کریں گے اور ہماری آیات کو جھٹلائیں گے ، وہ آگ میں جانے والے لوگ ہیں،جہاں وہ ہمیشہ رہیں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(2,40,'2:40','اے بنی اسرائیل ! ذرا خیال کرو اس نعمت کا جو میں نے تم کو عطا کی تھی ، میرے ساتھ تمہارا جو عہد تھا اُسے تم پورا کرو، تو میرا جو عہد تمہارے ساتھ تھا اُسے میں پورا کروں اور مجھ ہی سے ڈرو');
INSERT INTO "ur_maududi" VALUES(2,41,'2:41','اور میں نے جو کتاب بھیجی ہے اس پر ایمان لاوٴ۔یہ اس کتاب کی تائید میں ہے جو تمہارے پاس پہلے سے موجود تھی، لہٰذا سب سے پہلے تم ہی اس کے منکر نہ بن جاوٴ۔تھوڑی قیمت پر میری آیات کو نہ بیچ ڈالو اور میرے غضب سے بچو');
INSERT INTO "ur_maududi" VALUES(2,42,'2:42','باطل کا رنگ چڑھاکر حق کو مشتبہ نہ بناوٴاور نہ جانتے بوجھتےحق کو چھپانے کی کوشش کرو۔ ');
INSERT INTO "ur_maududi" VALUES(2,43,'2:43','نماز قائم کرو ، زکوٰة دو ، اور جو لوگ میرے آگے جُھک رہے ہیں اُن کے ساتھ تم بھی جُھک جاو');
INSERT INTO "ur_maududi" VALUES(2,44,'2:44','تم دوسروں کو تو نیکی کا راستہ اختیار کرنے کے لیے کہتے ہو، مگر اپنے آپ کو بھول جاتے ہو؟ حالانکہ تم کتاب کی تلاوت کرتے ہو کیا تم عقل سے بالکل ہی کام نہیں لیتے؟');
INSERT INTO "ur_maududi" VALUES(2,45,'2:45','صبر اور نماز سے مدد لو، بیشک نماز ایک سخت مشکل کام ہے'' مگر ان فرماںبردار بندوں کے لیے مشکل نہیں');
INSERT INTO "ur_maududi" VALUES(2,46,'2:46','جو سمجھتے ہیں کہ آخر کار انہیں اپنے ربّ سے ملنا اور اسی کی طرف پلٹ جانا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,47,'2:47','اے بنی اسرائیل ! یاد کرو میری اُس نعمت کو ، جس سے میں نے تمہیں نوازا تھا اور اس بات کو کہ میں نے تمہیں دنیا کی ساری قوموں پر فضیلت عطا کی تھی ');
INSERT INTO "ur_maududi" VALUES(2,48,'2:48','اور ڈرو اس دن سے جب کوئی کسی کے ذرا کام نہ آئے گا ، نہ کسی کی طرف سے سفارش قبول ہوگی ، نہ کسی کو فدیہ لے کر چھوڑا جائے گا، اور نہ مجرموں کو کہیں سے مدد مِل سکے گی۔ ');
INSERT INTO "ur_maududi" VALUES(2,49,'2:49',' یاد کرو وہ وقت، جب ہم نے تم کو فرعونیوں کی غلامی سے نجات بخشی۔اُنہوں نے تمہیں سخت عذاب میں مبتلا کر رکھا تھا،تمہارے لڑکوں کو ذبح کرتے تھے تمہاری لڑکیوں کو زندہ رہنے دیتے تھے اور اس حالت میں تمہارے ربّ کی طرف سے تمہاری بڑی آزمائش تھی ');
INSERT INTO "ur_maududi" VALUES(2,50,'2:50','یاد کرو وہ وقت، جب ہم نے سمندر پھاڑ کر تمہارے لیے راستہ بنایا، پھر اس میں سے تمہیں بخیریت گزروا دیا، پھر وہیں تمہاری آنکھوں کے سامنے فرعونوں کو غرقاب کیا');
INSERT INTO "ur_maududi" VALUES(2,51,'2:51','یاد کرو ، جب ہم نے موسیٰ ؑ کو چالیس شبانہ روز کی قرارداد پر بُلایا، تو اس کے پیچھے تم بچھڑے کو اپنا معبُود بنا بیٹھے۔ اُس وقت تم نے بڑی زیادتی کی تھی');
INSERT INTO "ur_maududi" VALUES(2,52,'2:52','مگر اس پر بھی ہم نے تمہیں معاف کر دیا کہ شاید اب تم شکر گزار بنو');
INSERT INTO "ur_maududi" VALUES(2,53,'2:53','یاد کرو (ٹھیک اُس وقت جب تم یہ ظلم کررہے تھے) ہم نے موسیٰ ؑ کو کتاب اور فرقان عطا کی تاکہ تم اس کے ذریعے سے سیدھا راستہ پاسکو');
INSERT INTO "ur_maududi" VALUES(2,54,'2:54','یاد کرو جب موسیٰ ؑ (یہ نعمت لیے ہوئے پلٹا، تو اُس ) نے اپنی قوم سے کہا کہ ”لوگو، تم نے بچھڑے کو معبُود بناکر اپنے اُوپر سخت ظلم کیا ہے ،لہٰذا تم لوگ اپنے خالق کے حضو ر توبہ کرو اور اپنی جانوں کو ہلاک کرو ، اسی میں تمہارے خالِق کے نزدیک تمہاری بہتری ہے۔“ اُس وقت تمہارے خالِق نے تمہاری توبہ قبول کر لی کہ وہ بڑا معاف کرنے والا ہے اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(2,55,'2:55','یاد کرو جب تم نے موسیٰؑ سے کہا تھا کہ ہم تمہارے کہنے کا ہرگز یقین نہ کریں گے، جب تک کہ اپنی آنکھوں سے علانیہ خدا کو (تم سے کلام کرتے) نہ دیکھ لیں اس وقت تمہارے دیکھتے دیکھتے ایک زبردست صاعقے نے تم کو آ لیا');
INSERT INTO "ur_maududi" VALUES(2,56,'2:56','تم بے جان ہوکر گر چکے تھے ،مگر پھر ہم نے تم کو جِلا اُٹھایا ، شاید کہ اس احسان کے بعد تم شکر گزار بن جاوٴ ');
INSERT INTO "ur_maududi" VALUES(2,57,'2:57','ہم نے تم پر اکابر کا سایہ کیا ، من وسلویٰ کی غذا تمہارے لیے فراہم کی اور تم سے کہا کہ جو پاک چیزیں ہم نے تمہیں بخشی ہیں، اُنھیں کھاوٴ، مگر تمہارے اسلاف نے جو کچھ کیا ،وہ ہم پر ظلم نہ تھا، بلکہ انہوں نے آپ اپنے ہی اُوپر ظلم کیا');
INSERT INTO "ur_maududi" VALUES(2,58,'2:58','پھر یاد کرو جب ہم نے کہا تھا کہ ”یہ بستی جو تمہارے سامنے ہےاس میں داخل ہو جاوٴاس کی پیداوار جس طرح چاہومزے سے کھاوٴمگر بستی کے دروازے میں سجدہ ریز ہوتے ہوےٴداخل ہونااور کہتے جانا حِطَّةٌ ، حِطَّةٌ، ہم تمہاری خطاوٴں سے درگزر کریں گے اور نیکو کاروں کو مزید فضل و کرم سے نوازیں گے۔“');
INSERT INTO "ur_maududi" VALUES(2,59,'2:59','مگر جو بات کہی گئی تھی، ظالموں نے اُسے بدل کر کچھ اور کر دیا آخر کار ہم نے ظلم کرنے والوں پر آسمان سے عذاب نازل کیا یہ سزا تھی ان نافرمانیوں کی، جو وہ کر رہے تھے');
INSERT INTO "ur_maududi" VALUES(2,60,'2:60','یاد کرو ، جب موسیٰ ؑ نے اپنی قوم کے لیے پانی کی دعا کی تو ہم نے کہاکہ فلاں چٹان پر اپنا عصا مارو۔چنانچہ اُس سے بارہ چشمے پھوٹ نکلےاور ہر قبیلے نے جان لیاکہ کون سی جگہ اُس کے پانی لینے کی ہے۔اُس وقت یہ ہدایت کر دی گئی تھی کہ اللہ کا دیا ہُوا رزق کھاوٴ پیو،اور زمین میں فساد نہ پھیلاتے پھرو');
INSERT INTO "ur_maududi" VALUES(2,61,'2:61','یاد کرو ، جب تم نے کہا تھا کہ”اے موسیٰ ؑ، ہم ایک ہی طرح کے کھانے پر صبر نہیں کر سکتے۔ اپنے ربّ سے دُعا کرو کہ ہمارے لیے زمین کی پیداوار ساگ،ترکاری،گیہوں،لہسن،پیاز،دال وغیرہ پیدا کریں۔“ تو موسیٰ ؑ نے کہا”کیا ایک بہتر چیز کے بجائےتم ادنیٰ درجے کی چیزیں لینا چاہتے ہو؟اچھا ،کسی شہری آبادی میں جا رہو جو کچھ تم مانگتے ہووہاں مل جائے گا۔“ آخر کار نوبت یہاں تک پہنچی کہ ذلّت و خواری اور پستی و بدحالی اُن پر مسلط ہو گئی اور وہ اللہ کے غضب میں گھِر گئے۔ یہ نتیجہ تھا اس کا کہ وہ اللہ کی آیات سےکفر کرنے لگے اور پیغمبروں کو ناحق قتل کرنے لگے۔ی ہ نتیجہ تھا اُن کی نافرمانیوں کا اور اس بات کا کہ وہ حدودِ شرع سےنکل نکل جاتے تھے');
INSERT INTO "ur_maududi" VALUES(2,62,'2:62','یقین جانو کہ نبی عربی کو ماننے والے ہوں یا یہودی، عیسائی ہوں یا صابی،جو بھی اللہ اور روزِ آخر پر ایمان لائے گااور نیک عمل کرے گا، اُس کا اجر اُس کے ربّ کے پاس ہے اور اُس کے لئے کسی خوف اور رنج کا موقع نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,63,'2:63','یاد کرو وہ وقت، جب ہم نے طور کو تم پر اُٹھاکرتم سے پُختہ عہد لیاتھا اور کہا تھاکہ ”جو کتاب ہم تمہیں دے رہے ہیں اُسے مضبوطی کے ساتھ تھامنااور جو احکام و ہدایات اس میں درج ہیں اُنہیں یاد رکھنا۔ اسی ذریعے سےتوقع کی جاسکتی ہےکہ تم تقوٰی کی روش پر چل سکو گے“');
INSERT INTO "ur_maududi" VALUES(2,64,'2:64','مگر اس کے بعد تم اپنے عہد سے پھِر گئے اس پر بھی اللہ کے فضل اور اس کی رحمت نے تمہارا ساتھ نہ چھوڑا، ورنہ تم کبھی کے تباہ ہو چکے ہوتے');
INSERT INTO "ur_maududi" VALUES(2,65,'2:65','پھر تمہیں اپنی قوم کے اُن لوگوں کا قصّہ تو معلوم ہی ہےجنہوں نےسَبت کا قانون توڑا تھا۔ہم نے اُنہیں کہ دیا کہ بندر بن جاوٴ اور اس حالت میں رہو کہ ہر طرف سے تم پر دھتکار پھٹکار پڑے۔ ');
INSERT INTO "ur_maududi" VALUES(2,66,'2:66','اس طرح ہم نے اُن کے انجام کو اُس زمانے کے لوگوں اور بعد کی آنے والی نسلوں کے لیے عبرت اور ڈرنے والوں کے لیے نصیحت بنا کر چھوڑا');
INSERT INTO "ur_maududi" VALUES(2,67,'2:67','پھر وہ واقعہ یاد کرو، جب موسیٰؑ نے اپنے قوم سے کہا کہ، اللہ تمہیں ایک گائے ذبح کرنے کا حکم دیتا ہے کہنے لگے کیا تم ہم سے مذاق کرتے ہو؟ موسیٰؑ نے کہا، میں اس سے خدا کی پناہ مانگتا ہوں کہ جاہلوں کی سی باتیں کروں');
INSERT INTO "ur_maududi" VALUES(2,68,'2:68','بولے اچھا، اپنے رب سے درخواست کرو کہ وہ ہمیں گائے کی کچھ تفصیل بتائے موسیٰؑ نے کہا، اللہ کا ارشاد ہے کہ وہ ایسی گائے ہونی چاہیے جو نہ بوڑھی ہو نہ بچھیا، بلکہ اوسط عمر کی ہو لہٰذا جو حکم دیا جاتا ہے اس کی تعمیل کرو');
INSERT INTO "ur_maududi" VALUES(2,69,'2:69','پھر کہنے لگے اپنے رب سے یہ اور پوچھ دو کہ اُس کا رنگ کیسا ہو موسیٰؑ نے کہا وہ فرماتا ہے زرد رنگ کی گائے ہونی چاہیے، جس کا رنگ ایسا شوخ ہو کہ دیکھنے والوں کا جی خوش ہو جائے');
INSERT INTO "ur_maududi" VALUES(2,70,'2:70','پھر بولے اپنے رب سے صاف صاف پوچھ کر بتاؤ کیسی گائے مطلوب ہے، ہمیں اس کی تعین میں اشتباہ ہو گیا ہے اللہ نے چاہا، تو ہم اس کا پتہ پالیں گے');
INSERT INTO "ur_maududi" VALUES(2,71,'2:71','موسیٰ ؑ ؑ نے جواب دیا: اللہ کہتا ہے کہ وہ ایسی گائے ہےجس سے خدمت نہیں لی جاتی ، نہ زمین جوتتی ہے نہ پانی کھینچتی ہے، صحیح سالم اور بے داغ ہو۔اس پر وہ پکار اُٹھے کہ ہاں، اب تم نے ٹھیک پتہ بتایا ہے۔ پھر اُنہوں نے اُسے ذبح کیا ، ورنہ وہ ایسا کرتے معلوم نہ ہوتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(2,72,'2:72','اور تمہیں یاد ہے وہ واقعہ جب تم نے ایک شخص کی جان لی تھی، پھر اس کے بارے میں جھگڑنے اور ایک دوسرے پر قتل کا الزام تھوپنے لگے تھے اور اللہ نے فیصلہ کر لیا تھا کہ جو کچھ تم چھپاتے ہو، اسے کھول کر رکھ دے گا');
INSERT INTO "ur_maududi" VALUES(2,73,'2:73','اُس وقت ہم نے حکم دیا کہ مقتو ل کی لاش کو اس کے ایک حصّے سے ضرب لگاوٴ۔ دیکھو، اسطرح اللہ مُردوں کو زندگی بخشتا ہے اور تمھیں اپنی نشانیاں دکھاتا ہے تا کہ تم سمجھو ۔');
INSERT INTO "ur_maududi" VALUES(2,74,'2:74','مگر ایسی نشانیاں دیکھنے کے بعد بھی آخر کار تمہارے دل سخت ہوگئے، پتھروں کی طرف سخت، بلکہ سختی میں کچھ ان سے بھی بڑھے ہوئے، کیونکہ پتھروں میں سے تو کوئی ایسا بھی ہوتا ہے جس میں سے چشمے پھوٹ بہتے ہیں، کوئی پھٹتا ہے اور اس میں سے پانی نکل آتا ہے اور کوئی خدا کے خوف سے لرز کر گر بھی پڑتا ہے اللہ تمہارے کرتوتوں سے بے خبر نہیں ہے');
INSERT INTO "ur_maududi" VALUES(2,75,'2:75','اے مسلمانو!اب کیا ان لوگوں سے تم یہ توقع رکھتے ہو کہ یہ تمھاری دعوت پر ایمان لے آئیں گے ؟حالانکہ ان میں سے ایک گروہ کا شیوہ یہ رہا ہےکہ اللہ کا کلام سنا اور پھر خُوب سمجھ بُوجھ کر دانستہ اس میں تحریف کی۔ ');
INSERT INTO "ur_maududi" VALUES(2,76,'2:76','(محمد رسول اللہصلى الله عليه وسلم پر) ایمان لانے والوں سے ملتے ہیں تو کہتے ہیں کہ ہم بھی انہیں مانتے ہیں، اور جب آپس میں ایک دوسرے سے تخلیے کی بات چیت ہوتی ہے تو کہتے ہیں کہ بے وقوف ہوگئے ہو؟ اِن لوگوں کو وہ باتیں بتاتے ہو جو اللہ نے تم پر کھولی ہیں تاکہ تمہارے رب کے پاس تمہارے مقابلے میں انہیں حُجتّ؟ میں پیش کریں ');
INSERT INTO "ur_maududi" VALUES(2,77,'2:77','اور کیا یہ جانتے نہیں ہیں کہ جو کچھ یہ چھپاتے ہیں اور جو کچھ ظاہر کرتے ہیں، اللہ کو سب باتوں کی خبر ہے');
INSERT INTO "ur_maududi" VALUES(2,78,'2:78','ان میں ایک دوسرا گروہ امیّوں کا ہے، جو کتاب کا علم رکھتے نہیں ، بس اپنی بے بنیاد امیدوں اور آرزووٴں کو لیے بیٹھے ہیں اور محض وہم وگمان پر چلے جا رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,79,'2:79','پس ہلاکت اور تباہی ہے اُن لوگوں کیلے جو اپنے ہاتھوں سے شرع کا نوشتہ لکھتے ہیں پھر لوگوں سے کہتے ہیں کہ یہ اللہ کے پاس سے آیا ہوا ہے تاکہ اس کے معاوضے میں تھوڑا سا فائدہ حاصل کر لیں ۔ اُن کے ہاتھوں کا یہ لکھا بھی ان کے لیے تباہی کا سامان ہے اور ان کی یہ کمائی بھی ان کے لیے موجبِ ہلاکت');
INSERT INTO "ur_maududi" VALUES(2,80,'2:80','وہ کہتے ہیں کہ دوزخ کی آگ ہمیں ہرگزچُھونے والی نہیں اِلّا یہ کہ چند روز کی سزا مِل جائے تو مِل جائے ۔ اِن سے پوچھو ، کیا تم نے اللہ سے کوئی عہد لے لیا ہے ، جس کی خلاف ورزی وہ نہیں کر سکتا؟ یا بات یہ ہے کہ تم اللہ کے ذمّے ڈال کر ایسی باتیں کہ دیتے ہو جن کے متعلق تمہیں علم نہیں ہے کہ اُس نے ان کا ذمہ لیا ہے؟ آخر تمہیں دوزخ کی آگ کیوں نہ چُھوئے گی؟');
INSERT INTO "ur_maududi" VALUES(2,81,'2:81','جو بھی بدی کمائے گا اور اپنی خطا کاری کے چکر میں پڑا رہے گا، وہ دوزخی ہے او ر دوزخ ہی میں وہ ہمیشہ رہے گا');
INSERT INTO "ur_maududi" VALUES(2,82,'2:82','اور جو لوگ ایمان لائیں گے اور نیک عمل کریں گے وہی جنتی ہیں اور جنت میں وہ ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(2,83,'2:83','یاد کرو، اسرائیل کی اولاد سے ہم نے پختہ عہد لیا تھا کہ اللہ کے سوا کسی کی عبادت نہ کرنا، ماں باپ کے ساتھ، رشتے داروں کے ساتھ، یتیموں اور مسکینوں کے ساتھ نیک سلوک کرنا، لوگوں سے بھلی بات کہنا، نماز قائم کرنا اور زکوٰۃ دینا، مگر تھوڑے آدمیوں کے سو ا تم سب اس عہد سے پھر گئے اور اب تک پھر ے ہوئے ہو');
INSERT INTO "ur_maududi" VALUES(2,84,'2:84','پھر ذرا یاد کرو، ہم نے تم سے مضبوط عہد لیا تھا کہ آپس میں ایک دوسرے کا خون نہ بہانا اور نہ ایک دوسرے کو گھر سے بے گھر کرنا تم نے اس کا اقرار کیا تھا، تم خود اس پر گواہ ہو');
INSERT INTO "ur_maududi" VALUES(2,85,'2:85','مگر آج وہی تم ہو کہ اپنےبھائی بندوں کو قتل کرتے ہو، اپنی برادری کے کچھ لوگوں کو بے خانماں کر دیتے ہو، ظلم وزیادتی کے ساتھ ان کے خلاف جتھے بندیاں کرتے ہو، اور جب وہ لڑائی میں پِٹے ہوئے تمہارے پاس آتے ہیں ، تو ان کی رہائی کے لیے فدیہ کالیں دین کرتے ہو، حالانکہ انہیں ان کے گھروں سے نکالنا ہی سرے سے تم پر حرام تھا، تو کیا تم کتاب کےایک حصّے پر ایمان لاتے ہو اوردُوسرے حصّے کے ساتھ کفر کرتے ہو؟ پھر تم میں سے جو لوگ ایسا کریں ، ان کی سزا اس کے سوا اور کیا ہےکہ دنیا کی زندگی میں ذلیل و خوار ہو کر رہیں اور آخرت میں شدید ترین عذاب کی طرف پھیر دیے جائیں ؟ اللہ ان حرکات سے بے خبر نہیں ہے، جو تم کر رہے ہو');
INSERT INTO "ur_maududi" VALUES(2,86,'2:86','یہ وہ لوگ ہیں جنہوں نے آخرت بیچ کر دنیا کی زندگی خرید لی ہے، لہٰذا نہ اِن کی سزا میں کوئی تخفیف ہوگی اور نہ انہیں کوئی مدد پہنچ سکے گی');
INSERT INTO "ur_maududi" VALUES(2,87,'2:87','ہم نے موسیٰ ؑ کو کتاب دی، اس کے بعد پے در پے رسُول بھیجے، آخر کار عیسٰی ابنِ مر یم کو روشن نشانیاں دے کر بھیجا اور رُوحِ پاک سے اس کی مدد کی۔ پھر یہ تمہارا کیا ڈھنگ ہے کہ جب بھی کوئی رسُول تمہاری خواہشات نفس کے خلاف کوئی چیز لے کر تمہارے پاس آیا، تو تم نے اس کے مقابلے میں سر کشی ہی کی، کسی کو جھٹلایا اور کسی کو قتل کر ڈالا!');
INSERT INTO "ur_maududi" VALUES(2,88,'2:88','وُہ کہتے ہیں، تمارے دل محفوظ ہیں۔نہیں ، اصل بات یہ ہے کہ ان کے کفر کی وجہ سے ان پر اللہ کی پھٹکار پڑی ہے، اس لیے وہ کم ہی ایمان لاتے ہیں');
INSERT INTO "ur_maududi" VALUES(2,89,'2:89','اور اب جوایک کتاب اللہ کی طرف سے ان کے پاس آئی ہے، اس کے ساتھ ان کا کیا برتاؤ ہے؟ باوجودیکہ وہ اس کتاب کی تصدیق کرتی ہے جو ان کے پاس پہلے سے موجود تھی ، باوجودیکہ اس کی آمد سے پہلے وہ خود کفار کے مقابلے میں فتح و نصرت کی دعائیں مانگا کرتے تھے، مگر جب وہ چیز آگئی جسے وہ پہچان بھی گئے، تو انھوں نے اسے ماننے سے انکار کر دیا۔ خدا کی لعنت ان منکرین پر');
INSERT INTO "ur_maududi" VALUES(2,90,'2:90','کیسا بُرا ذریعہ ہے جس سے یہ اپنے نفس کی تسلی حاصل کرتے ہیں کہ جو ہدایت اللہ نے نازل کی ہے، اس کو قبول کرنے سے صرف اس ضد کی بنا پر انکار کر رہے ہیں کہ اللہ نے اپنے فضل ( وحی ورسالت) سے اپنے جس بندے کو خود چاہا، نواز دیا! لہٰذا اب یہ غضب با لا ئے غضب کے مستحق ہو گئے ہیں اور ایسے کافروں کیلئےسخت ذلت آمیز سزا مقرر ہے');
INSERT INTO "ur_maududi" VALUES(2,91,'2:91','جب اُن سے کہا جاتا ہے کہ جو کچھ اللہ نے نازل کیا ہے اس پر ایمان لاؤ، تو وہ کہتے ہیں "ہم تو صرف اُس چیز پر ایمان لاتے ہیں، جو ہمارے ہاں (یعنی نسل اسرائیل) میں اتری ہے" اس دائرے کے باہر جو کچھ آیا ہے، اسے ماننے سے وہ انکار کرتے ہیں، حالانکہ وہ حق ہے اور اُس تعلیم کی تصدیق و تائید کر رہا ہے جو ان کے ہاں پہلے سے موجود تھی اچھا، ان سے کہو: اگر تم اس تعلیم ہی پر ایمان رکھنے والے ہو جو تمہارے ہاں آئی تھی، تو اس سے پہلے اللہ کے اُن پیغمبروں کو (جو خود بنی اسرائیل میں پیدا ہوئے تھے) کیوں قتل کرتے رہے؟');
INSERT INTO "ur_maududi" VALUES(2,92,'2:92','تمہارے پاس موسیٰؑ کیسی کیسی روشن نشانیوں کے ساتھ آیا پھر بھی تم ایسے ظالم تھے کہ اس کے پیٹھ موڑتے ہی بچھڑے کو معبود بنا بیٹھے');
INSERT INTO "ur_maududi" VALUES(2,93,'2:93','پھر ذرا اُس میثاق کو یاد کرو، جو طُور کو تمہارے اوپر اٹھا کر ہم نے تم سے لیا تھا ہم نے تاکید کی تھی کہ جو ہدایات ہم دے رہے ہیں، ان کی سختی کے ساتھ پابندی کرو اور کان لگا کر سنو تمہارے اسلاف نے کہا کہ ہم نے سن لیا، مگر مانیں گے نہیں اور ان کی باطل پرستی کا یہ حال تھا کہ دلوں میں ان کے بچھڑا ہی بسا ہوا تھا کہو: اگر تم مومن ہو، تو عجیب ایمان ہے، جو ایسی بری حرکات کا تمہیں حکم دیتا ہے');
INSERT INTO "ur_maududi" VALUES(2,94,'2:94','اِن سے کہو اگر واقعی اللہ کے نزدیک آخرت کا گھر تمام انسانوں کو چھوڑ کر صرف تمہارے ہی لیے مخصُوص ہے ،تب تو تمہیں چاہیے کہ موت کی تمنّا کرو ، اگر تم اس خیال میں سچےّ ہو');
INSERT INTO "ur_maududi" VALUES(2,95,'2:95','یقین جانو کہ یہ کبھی اس کی تمنا نہ کریں گے، اس لیے کہ اپنے ہاتھوں جو کچھ کما کر انہوں نے وہاں بھیجا ہے، اس کا اقتضا یہی ہے (کہ یہ وہاں جانے کی تمنا نہ کریں) اللہ ان ظالموں کے حال سے خوب واقف ہے');
INSERT INTO "ur_maududi" VALUES(2,96,'2:96','تم انہیں سب سے بڑھ کر جینے کا حریص پاوٴ گے حتّٰی کہ یہ اس معاملے میں مشرکوں سے بھی بڑھے ہوئے ہیں۔ ان میں سے ایک ایک شخص یہ چاہتا ہے کہ کسی طرح ہزار برس جیے ، حالانکہ لمبی عمر بہر حال اُسے عذاب سے تودُور نہیں پھینک سکتی۔ جیسے کچھ اعمال یہ کر رہے ہیں، اللہ تو انہیں دیکھ ہی رہا ہے');
INSERT INTO "ur_maududi" VALUES(2,97,'2:97','ان سے کہو کہ جو کوئی جبریل سے عداوت رکھتا ہو ، اسے معلوم ہو نا چاہیے کہ جبریل نے اللہ ہی کے اذن سے یہ قرآن تمہارے قلب پر نازل کیا ہے ، جو پہلے آئی ہوئی کتابوں کی تصدیق و تائید کرتا ہے اور ایمان لانے والوں کے لیےہدایت اور کامیابی کی بشارت بن کر آیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,98,'2:98','(اگر جبریل سے ان کی عداوت کا سبب یہی ہے، تو کہہ دو کہ) جو اللہ اور اس کے فرشتوں اور اس کے رسولوں اور جبریل اور میکائیل کے دشمن ہیں، اللہ ان کافروں کا دشمن ہے');
INSERT INTO "ur_maududi" VALUES(2,99,'2:99','ہم نے تمہاری طرف ایسی آیات نازل کی ہیں جو صاف صاف حق کا اظہار کر نے والی ہیں اور ان کی پیروی سے صرف وہی لوگ انکار کرتے ہیں، جو فاسق ہیں');
INSERT INTO "ur_maududi" VALUES(2,100,'2:100','کیا ہمیشہ ایسا ہی نہیں ہوتا رہا ہے کہ جب انہوں نے کوئی عہد کیا، تو ان میں سے ایک نہ ایک گروہ نے اسے ضرور ہی بالا ئے طاق رکھ دیا؟ بلکہ ان میں سے اکثر ایسے ہی ہیں، جو سچے دل سے ایمان نہیں لاتے');
INSERT INTO "ur_maududi" VALUES(2,101,'2:101','اور جب ان کے پاس اللہ کی طرف سے کوئی رسول اس کتاب کی تصدیق و تائید کرتا ہوا آیا جو ان کے ہاں پہلے سے موجود تھی، تو اِن اہل کتاب میں سے ایک گروہ نے کتاب اللہ کو اس طرح پس پشت ڈالا، گویا کہ وہ کچھ جانتے ہی نہیں');
INSERT INTO "ur_maududi" VALUES(2,102,'2:102','اور لگے ان چیزوں کی پیروی کرنے ، جو شیاطین سلیمان ؑ کی سلطنت کا نام لے کر پیش کیا کرتے تھے ، حالانکہ سلیمان ؑ نے کبھی کفر نہیں کیا ، کفر کے مرتکب تو وہ شیا طین تھے جو لوگو ں کو جادوگری کی تعلیم دیتے تھے۔ اور پیچھے پڑے اس چیز کے جو بابل میں دو فرشتوں ، ھاروت و ماروت پر نازل کی گئی تھی ، حالانکہ وہ (فرشتے) جب بھی کسی کو اس کی تعلیم دیتے تھے ، تو پہلے صاف طور پر متنبہّ کر دیا کرتے تھے کہ ”دیکھ ، ہم محض ایک آزمائش ہیں ، تُوکفر میں مبتلا نہ ہو “ پھر بھی یہ لوگ ان سے وہ چیز سیکھتے تھے جس سے شوہر اور بیوی میں جُدائی ڈال دیں ۔ ظاہر تھا کہ اذن ِ الٰہی کہ بغیر وہ اس ذریعے سے کسی کو بھی ضرر نہ پہنچا سکتے تھے ، مگر اس کے با وجو د وہ ایسی چیز سیکھتے تھے جو خود ان کے لیے نفع بخش نہیں ، بلکہ نقصان دہ تھی اور انھیں خوب معلوم تھا کہ جو اس چیز کا خریدار بنا ، اس کے لیے آخرت میں کوئی حصہ نہیں۔ کتنی بُری متاع تھی جس کے بدلہ انھوں نے اپنی جا نوں کو بیچ ڈالا، کاش انھیں معلوم ہو تا');
INSERT INTO "ur_maududi" VALUES(2,103,'2:103','اگر وہ ایمان اور تقویٰ اختیار کرتے، تو اللہ کے ہاں اس کا جو بدلہ ملتا، وہ ان کے لیے زیادہ بہتر تھا، کاش اُنہیں خبر ہوتی');
INSERT INTO "ur_maududi" VALUES(2,104,'2:104','اے لوگو جو ایمان لائے ہو ؛ رَاعِنَا نہ کہا کرو ، بلکہ اُنظُرنَا کہو اور توجّہ سے بات کو سنو ، یہ کافر تو عذابِ الیم کے مستحق ہیں');
INSERT INTO "ur_maududi" VALUES(2,105,'2:105','یہ لوگ جنہوں نے دعوت حق کو قبول کرنے سے انکار کر دیا ہے، خواہ اہل کتاب میں سے ہوں یا مشرک ہوں، ہرگز یہ پسند نہیں کرتے کہ تمہارے رب کی طرف سے تم پر کوئی بھلائی نازل ہو، مگر اللہ جس کو چاہتا ہے، اپنی رحمت کے لیے چن لیتا ہے اور وہ بڑا فضل فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(2,106,'2:106','ہم اپنی جس آیت کو منسوخ کر دیتے ہیں یا بھلا دیتے ہیں ، اس کی جگہ اس سے بہتر لاتے ہیں یا کم از کم ویسی ہی ۔ کیا تم جا نتے نہیں ہو کہ اللہ ہر چیز پر قدرت رکھتا ہے؟');
INSERT INTO "ur_maududi" VALUES(2,107,'2:107','کیا تمہیں خبر نہیں ہے کہ زمیں اور آسمان کی فرمانروائی اللہ ہی کے لیے ہے اور اس کے سوا کوئی تمہاری خبر گیری کرنے اور تمہاری مدد کرنے والا نہیں ہے؟');
INSERT INTO "ur_maududi" VALUES(2,108,'2:108','پھر کیا تم اپنے رسو ل سے اس قسم کے سوالات اور مطالبے کرنا چاہتے ہو، جیسے اس سے پہلے موسیٰ ؑ سے کیے جا چکے ہیں؟ حالانکہ جس شخص نے ایمان کی روش کو کفر کی روش سے بدل لیا ، وہ راہ راست سے بھٹک گیا');
INSERT INTO "ur_maududi" VALUES(2,109,'2:109','اہل کتاب میں سے اکثر لوگ یہ چاہتے ہیں کہ کسی طرح تمہیں ایمان سے پھیر کر پھر کفر کی طرف پلٹا لے جائیں۔ اگرچہ حق ان پر ظاہر ہو چکا ہے مگر اپنے نفس کے حسد کی بنا پر تمہارے لیے ان کی یہ خواہش ہے۔ اس کے جواب میں تم عفو و درگزر سے کام لو یہاں تک کہ اللہ خود ہی اپنا فیصلہ نافذ کر دے۔ مطمئن رہو کہ اللہ تعالیٰ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(2,110,'2:110','نماز قائم کرو اور زکوٰۃ دو تم اپنی عاقبت کے لیے جو بھلائی کما کر آگے بھیجو گے، اللہ کے ہاں اسے موجود پاؤ گے جو کچھ تم کرتے ہو، وہ سب اللہ کی نظر میں ہے');
INSERT INTO "ur_maududi" VALUES(2,111,'2:111','ان کا کہنا ہے کہ کوئی شخص جنت میں نہ جائے گا جب تک کہ وہ یہودی نہ ہو یا ( عیسائیوں کے خیال کے مطابق ) عیسائی نہ ہو۔ یہ ان کی تمنائیں ہیں ۔ ان سے کہو ، اپنی دلیل پیش کرو، اگر تم اپنے دعوے میں سچے ہو');
INSERT INTO "ur_maududi" VALUES(2,112,'2:112','دراصل نہ تمہاری کچھ خصوصیت ہے، نہ کسی اور کی حق یہ ہے کہ جو بھی اپنی ہستی کو اللہ کی اطاعت میں سونپ دے اور عملاً نیک روش پر چلے، اس کے لیے اس کے رب کے پاس اُس کا اجر ہے اور ایسے لوگوں کے لیے کسی خوف یا رنج کا کوئی موقع نہیں');
INSERT INTO "ur_maududi" VALUES(2,113,'2:113','یہودی کہتے ہیں : عیسائیوں کے پاس کچھ نہیں۔ عیسا ئی کہتے ہیں : یہودیوں کے پاس کچھ نہیں حالانکہ دونوں ہی کتاب پڑھتے ہیں۔ اور اسی قسم کے دعوے ان لوگو ں کے بھی ہیں جن کے پاس کتاب کا علم نہیں ہے ۔ یہ اختلافات جن میں یہ لوگ مبتلا ہیں ان کا فیصلہ اللہ قیامت کے روز کر دے گا');
INSERT INTO "ur_maududi" VALUES(2,114,'2:114','اور اس شخص سے بڑھ کر ظالم کو ن ہو گا جو اللہ کے معبدوں میں اس کے نام کی یاد سے روکے اور ان کی ویرانی کے در پے ہو؟ ایسے لوگ اس قابل ہیں کہ ان عبادت گاہوں میں قدم نہ رکھیں اور اگر وہاں جائیں بھی تو ڈرتے ہوئے جائیں ۔ ان کے لیے تو دنیا میں رسوائی ہےاور آخرت میں عذاب عظیم');
INSERT INTO "ur_maududi" VALUES(2,115,'2:115','مشرق اور مغرب سب اللہ کے ہیں۔ جس طرف بھی تم رُخ کرو گے ، اسی طرف اللہ کا رُخ ہے اللہ بڑی وسعت والا ہے اور سب کچھ جاننے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,116,'2:116','ان کا قول ہے کہ اللہ نے کسی کو بیٹا بنایا ہے اللہ پاک ہے ان باتوں سے اصل حقیقت یہ ہے کہ زمین اور آسمانوں کی تمام موجودات اس کی ملک ہیں، سب کے سب اس کے مطیع فرمان ہیں');
INSERT INTO "ur_maududi" VALUES(2,117,'2:117','وہ آسمانوں اور زمین کا موجد ہے اور جس بات کا وہ فیصلہ کرتا ہے، اس کے لیے بس یہ حکم دیتا ہے کہ "ہو جا" اور وہ ہو جاتی ہے');
INSERT INTO "ur_maududi" VALUES(2,118,'2:118','نادان کہتے ہیں کہ اللہ خود ہم سے بات کیوں نہیں کرتایا کوئی نشانی ہمارے پاس کیوں نہیں آتی؟ ایسی ہی باتیں ان سے پہلے لوگ بھی کیا کرتے تھے۔ ان سب (اگلے پچھلے گمراہوں ) کی ذہنیتیں ایک جیسی ہیں۔ یقین لانے والو ں کے لیے تو ہم نشانیاں صاف صاف نمایاں کر چکے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,119,'2:119','(اس سے بڑھ کر نشانی کیا ہو گی کہ ) ہم نے تم کو علمِ حق کے ساتھ خوشخبری دینے والا اور ڈرانے والا بنا کر بھیجا۔ اب جو لوگ جہنم سے رشتہ جوڑ چکے ہیں ، ان کی طرف سے تم ذمہ دار و جواب دہ نہیں ہو');
INSERT INTO "ur_maududi" VALUES(2,120,'2:120','یہودی اور عیسائی تم سے ہر گز راضی نہ ہو نگے ، جب تک تم ان کے طریقے پر نہ چلنے لگو۔ صاف کہہ دو کہ راستہ بس وہی ہے جو اللہ نے بتایا ہے۔ ورنہ اگر اس علم کے بعد جو تمہارے پس آچکا ہے، تم نے اُن کی خواہشات کی پیروی کی ، تو اللہ کی پکڑ سے بچانے والا کوئی دوست اور مددگار تمہارے لیے نہیں ہے');
INSERT INTO "ur_maududi" VALUES(2,121,'2:121','جن لوگوں کو ہم نے کتاب دی ہے، وہ اُسے اس طرح پڑھتے ہیں جیسا کہ پڑھنےکا حق ہے۔ وہ اس پر سچّے دل سے ایمان لاتے ہیں۔ اور جو اس کے ساتھ کفر کا رویہّ اختیار کریں، وہی اصل میں نقصان اُٹھانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(2,122,'2:122',' اے بنی اسرائیل !یاد کرو میری وہ نعمت، جس سے میں نے تمہیں نوازا تھا، اور یہ کہ میں نے تمہیں دنیا کی تمام قوموں پر فضیلت دی تھی');
INSERT INTO "ur_maududi" VALUES(2,123,'2:123','اور ڈرو اُس دن سے، جب کوئی کسی کے ذرا کام نہ آئے گا، نہ کسی سے فدیہ قبول کیا جائے گا، نہ کوئی سفارش ہی آدمی کو فائدہ دے گی، اور نہ مجرموں کو کہیں سے کوئی مدد پہنچ سکے گی');
INSERT INTO "ur_maududi" VALUES(2,124,'2:124','یاد کرو کہ جب ابرہیم ؑ کو اس کے رب نے چند باتوں میں آزمایا اور وہ اُن سب میں پورا اتر گیا، تو اس نے کہا : ”میں تجھے سب لوگوں کا پیشوا بنانے والا ہوں“۔ ابرہیم ؑنے عرض کیا : ”اور کیا میری اولاد سے بھی یہی وعدہ ہے“؟ اس نے جواب دیا:”میرا وعدہ ظالموں سے متعلق نہیں ہے“ ');
INSERT INTO "ur_maududi" VALUES(2,125,'2:125','اور یہ کہ ہم نے اس گھر (کعبے) کو لوگوں کے لیے مرکز اور امن کی جگہ قرار دیاتھا اور لوگوں کو حکم دیا تھا کہ ابراہیمؑ جہاں عبادت کے لیے کھڑا ہو تا ہے اس مقام کو مستقل جائے نماز بنالو، اور ابرہیم ؑاور اسماعیل ؑ کو تاکید کی تھی کہ میرے اس گھر کو طواف اور اعتکاف اور رکوع اورسجدہ کرنے والوں کے لیے پاک رکھو۔ ');
INSERT INTO "ur_maududi" VALUES(2,126,'2:126','اور یہ کہ ابراھیم ؑ نے دعا کی : ”اے میرے رب ، اس شہر کو امن کا شہر بنا دے اور اس کے باشندو ں میں سے جو اللہ اور آخرت کو مانیں ، انھیں ہر قسم کے پھلوں کا رزق دے۔“ جو اب میں اس کے رب نے فرمایا ”اور جو نہ مانیں گا ، دنیا کی چند روزہ زندگی کا سامان تو میں اسے بھی دوں گا، مگر آخر کار اسے عذاب جہنم کی طرف گھسیٹوں گا، اور وہ بد ترین ٹھکانا ہے۔“');
INSERT INTO "ur_maududi" VALUES(2,127,'2:127','اور یاد کرو ابراہیمؑ اور اسمٰعیلؑ جب اس گھر کی دیواریں اٹھا رہے تھے، تو دعا کرتے جاتے تھے: "اے ہمارے رب، ہم سے یہ خدمت قبول فرما لے، تو سب کی سننے اور سب کچھ جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(2,128,'2:128','اے رب، ہم دونوں کو اپنا مسلم (مُطیع فرمان) بنا، ہماری نسل سے ایک ایسی قوم اٹھا، جو تیری مسلم ہو، ہمیں اپنی عبادت کے طریقے بتا، اور ہماری کوتاہیوں سے در گزر فرما، تو بڑا معاف کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(2,129,'2:129','اور اے رب ، ان لوگوں میں خود انھیں کی قوم سے ایک ایسا رسول اٹھائیو ، جو انھیں تیری آیات سنائے ، ان کو کتاب اور حکمت کی تعلیم دے اور ان کی زندگیاں سنوارے ۔ تُو بڑا مقتدر اور حکیم ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(2,130,'2:130','اب کون ہے، جو ابراہیمؑ کے طریقے سے نفرت کرے؟ جس نے خود اپنے آپ کو حماقت و جہالت میں مبتلا کر لیا ہو، اس کے سو ا کون یہ حرکت کرسکتا ہے؟ ابراہیمؑ تو وہ شخص ہے، جس کو ہم نے دنیا میں اپنے کام کے لیے چُن لیا تھا اور آخرت میں اس کا شمار صالحین میں ہوگا');
INSERT INTO "ur_maududi" VALUES(2,131,'2:131','اس کا حال یہ تھاکہ جب اس کے رب نے اس سے کہا، ”مسلم ہوجا ،“ تو اس نے فوراً کہا ”میں مالک کائنات کا ”مسلم“ہو گیا۔“');
INSERT INTO "ur_maududi" VALUES(2,132,'2:132','اسی طریقے پر چلنے کی ہدایت اس نے اپنی اولاد کو کی تھی اور اسی کی وصیت یعقوب ؑ اپنی اولاد کو کر گیا۔ اس نے کہا تھا کہ ”میرے بچو، اللہ نے تمھارے لیے یہی دین پسند کیا ہے۔ لہٰذا مرتے دم تک تم مسلم ہی رہنا۔“');
INSERT INTO "ur_maududi" VALUES(2,133,'2:133','پھر کیا تم اس وقت موجو د تھے جب یعقوب ؑ اس دنیا سے رخصت ہو رہا تھا؟ اس نے مر تے وقت اپنے بیٹوں سے پوچھا ”بچو!میرے بعد تم کس کی بندگی کرو گے ؟“ ان سب نے جواب دیا ”ہم اسی ایک خدا کی بندگی کریں گے ، جسے آپ نے اور آپ کے بزرگوں ابراھیم ؑ ، اسماعیل ؑ اور اسحاق ؑ نے خدا مانا ہےاور ہم اسی کے مسلم ہیں۔ “');
INSERT INTO "ur_maududi" VALUES(2,134,'2:134','وہ کچھ لوگ تھے ، جو گزر گئے۔ جو کچھ انھوں نے کمایا۔ وہ ان کے لیے ہے اور جو کچھ تم کماؤ گے ،وہ تمہارے لیے ہے۔ تم سے یہ نہ پوچھا جائے گا کہ وہ کیا کرتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(2,135,'2:135','یہودی کہتے ہیں: یہودی ہو تو راہ راست پاؤ گے۔ عیسا ئی کہتے ہیں : عیسائی ہو ، تو ہدایت ملے گی۔ ان سے کہو: ”نہیں ، بلکہ سب کو چھوڑکر ابراھیم ؑ کا طریقہ۔ اور ابراھیم ؑ مشرکوں میں سے نہ تھا۔ “');
INSERT INTO "ur_maududi" VALUES(2,136,'2:136','مسلمانو! کہو کہ ”ہم ایمان لائے او راس ہدایت پر جو ہماری طرف نازل ہوئی ہے اور جو ابراھیم ؑ ، اسماعیل ؑ ، اسحاق ؑ ، یعقوب ؑ اور اولاد یعقوب ؑ کی طرف نازل ہوئی تھی اور جو موسیٰ ؑ اور عیسیٰ ؑ ؑ اور دوسرے تمام پیغمبروں کو ان کے رب کی طرف سے دی گئی تھی۔ ہم ان کے درمیان کوئی تفریق نہیں کرتےاور ہم اللہ کے مسلم ہیں ۔“');
INSERT INTO "ur_maududi" VALUES(2,137,'2:137','پھر اگر وہ اُسی طرح ایمان لائیں، جس طرح تم لائے ہو، تو ہدایت پر ہیں، اور اگراس سے منہ پھیریں، تو کھلی بات ہے کہ وہ ہٹ دھرمی میں پڑ گئے ہیں لہٰذا اطمینان رکھو کہ اُن کے مقابلے میں اللہ تمہاری حمایت کے لیے کافی ہے وہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "ur_maududi" VALUES(2,138,'2:138','کہو:”اللہ کا رنگ اختیار کرو۔ اس کے رنگ سے اچھا اور کس کا رنگ ہو گا؟ اور ہم اسی کی بندگی کرنے والے لوگ ہیں۔“');
INSERT INTO "ur_maududi" VALUES(2,139,'2:139','اے نبی! ان سے کہو: ”کیا تم اللہ کے بارے میں ہم سے جھگڑتے ہو ؟ حالانکہ وہی ہمارا رب بھی ہے اور تمہارا رب بھی۔ ہمارے اعمال ہمارے لیے تمہارے اعمال تمہارے لیے، اور ہم اللہ ہی کے لیے اپنی بندگی کو خالص کر چکے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,140,'2:140','یا پھر کیا تمہارا کہنا یہ ہے کہ ابراھیم ؑ ، اسماعیل ؑ ، اسحاق ؑ ، یعقوب ؑ اور اولاد یعقو ب ؑ سب کے سب یہودی تھے یا نصرانی تھے؟ کہو ”تم زیادہ جانتے ہو یا اللہ ؟ اس شخص سے بڑا ظالم اور کو ن ہو گا، جس کے ذمے اللہ کی طرف سے ایک گواہی ہو اور وہ اسے چھپائے ؟ تمہاری حرکات سے اللہ غافل تو نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,141,'2:141','وہ کچھ لوگ تھے، جو گزر چکے اُن کی کمائی اُن کے لیے تھی اور تمہاری کمائی تمہارے لیے تم سے اُن کے اعمال کے متعلق سوال نہیں ہوگا"');
INSERT INTO "ur_maududi" VALUES(2,142,'2:142','نادان لوگ ضرور کہیں گے :اِنہیں کیا ہوا کہ پہلے یہ جس قبلے کی طرف رُخ کرکے نماز پڑھتے تھے ، اس سے یکایک پھر گئے ؟ اے نبیؐ ، ان سے کہو : ”مشرق اور مغرب سب اللہ کے ہیں۔ اللہ جسے چاہتا ہے ،سیدھی راہ دکھاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,143,'2:143','اِسی طرح تو ہم نے تمہیں ایک ”اُمّتِ وَسَط“بنایا ہے تاکہ تم دُنیا کے لوگوں پر گواہ ہو اور رُسول تم پر گواہ ہو۔ پہلے جس طرف تم رُخ کرتے تھے ، اس کو تو ہم نے صرف یہ دیکھنے کے لیے مقرر کیا تھا کہ کون رُسول کی پیروی کرتا ہے اور کو ن اُلٹا پھر جاتا ہے۔ یہ معاملہ تھا تو بڑا سخت ، مگر اُن لوگوں کے لیے کچھ بھی سخت نہ ثابت ہوا ، جو اللہ کی ہدایت سے فیض یاب تھے ، اللہ تمہارے اس ایمان کو ہر گز ضائع نہ کرے گا، یقین جانو کہ وہ لوگوں کے حق میں نہایت شفیق و رحیم ہے');
INSERT INTO "ur_maududi" VALUES(2,144,'2:144','یہ تمہارے منہ کا بار بار آسمان کی طرف اُٹھنا ہم دیکھ رہے ہیں۔ لو، ہم اُسی قبلے کی طرف تمہیں پھیرے دیتے ہیں، جسے تم پسند کرتے ہو۔ مسجدِ حرام کی طرف رُخ پھیردو۔ اب جہاں کہیں تم ہو ، اُسی کی طرف منہ کرکے نماز پڑھاکرو۔ یہ لوگ جنہیں کتاب دی گئی تھی ، خُوب جانتے ہیں کہ(تحویلِ قبلہ کا )یہ حکم ان کے ربّ ہی کی طرف سے ہے اور بر حق ہے، مگر اس کے باوجود جو کچھ یہ کر رہے ہیں،اللہ اس سے غافل نہیں ہے');
INSERT INTO "ur_maududi" VALUES(2,145,'2:145','تم ان اہلِ کتاب کے پاس خواہ کوئی نشانی لے آوٴ ، ممکن نہیں کہ یہ تمہارے قبلے کی پیروی کرنے لگیں، اور نہ تمہارے لیے یہ ممکن ہے کہ ان کے قبلے کی پیروی کرو، اور ان میں سے کوئی گروہ بھی دُوسرے کے قبلے کی پیروی کے لیے تیار نہیں، اور اگر تم نے اس علم کے بعد ، جو تمہارے پاس آچکا ہے ، ان کی خواہشات کی پیروی کی ، تو یقیناً تمہارا شمار ظالموں میں ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(2,146,'2:146','جِن لوگوں کو ہم نے کتاب دی ہے ، وہ اس مقام کو(جسے قبلہ بنایا گیا ہے) ایسا پہچانتے ہیں، جیسا اپنی اولاد کو پہچانتے ہیں، مگر ان میں سے ایک گروہ جانتے بوجھتے حق کو چھپا رہا ہے');
INSERT INTO "ur_maududi" VALUES(2,147,'2:147','یہ قطعی ایک امر حق ہے تمہارے رب کی طرف سے، لہٰذا اس کے متعلق تم ہرگز کسی شک میں نہ پڑو');
INSERT INTO "ur_maududi" VALUES(2,148,'2:148','ہر ایک کے لیے ایک رُخ ہے ، جس کی طرف وہ مڑتا ہے۔ پس تم بھلائیوں کی طرف سبقت کرو۔ جہاں بھی تم ہو گے ، اللہ تمہیں پا لے گا۔ اُس کی قدرت سے کوئی چیز باہر نہیں');
INSERT INTO "ur_maududi" VALUES(2,149,'2:149','تمہارا گزر جس مقام سے بھی ہو، وہیں اپنا رُخ (نماز کے وقت) مسجد حرام کی طرف پھیر دو، کیونکہ یہ تمہارے رب کا بالکل حق فیصلہ ہے اور اللہ تم لوگوں کے اعمال سے بے خبر نہیں ہے');
INSERT INTO "ur_maududi" VALUES(2,150,'2:150','اور جہاں بھی تم ہو، اُسی کی طرف منہ کرکے نماز پڑھو تاکہ لوگوں کو تمہارے خلاف کوئی حجّت نہ ملے۔ ہاں جو ظالم ہیں ، اُن کی زبان کسی حال میں بند نہ ہوگی۔ تو اُن سے تم نہ ڈرو ، بلکہ مجھ سے ڈرو۔ اور اس لیے کہ میں تم پر اپنی نعمت پوری کردوں اور اس توقع پر کہ میرے اس حکم کی پیروی سے تم اسی طرح فلاح کا راستہ پاوٴ گے');
INSERT INTO "ur_maududi" VALUES(2,151,'2:151','جس طرح (تمہیں اِس چیز سے فلاح نصیب ہوئی کہ) میں نے تمہارے درمیان خود تم میں سے ایک رسول بھیجا، جو تمہیں میری آیات سناتا ہے، تمہاری زندگیوں کو سنوارتا ہے، تمہیں کتاب اور حکمت کی تعلیم دیتا ہے، اور تمہیں وہ باتیں سکھاتا ہے، جو تم نہ جانتے تھے');
INSERT INTO "ur_maududi" VALUES(2,152,'2:152','لہٰذا تم مجھے یاد رکھو، میں تمہیں یاد رکھوں گا اور میرا شکر ادا کرو، کفران نعمت نہ کرو');
INSERT INTO "ur_maududi" VALUES(2,153,'2:153',' اے لوگو جو ایمان لائے ہو صبر اور نماز سے مدد لو۔ اللہ صبر کرنے والوں کے ساتھ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,154,'2:154','اور جو لوگ اللہ کی راہ میں مارے جائیں ، انہیں مُردہ نہ کہو ، ایسے لوگ تو حقیقت میں زندہ ہیں ، مگر تمہیں ان کی زندگی کا شعور نہیں ہوت ا');
INSERT INTO "ur_maududi" VALUES(2,155,'2:155','اور ہم ضرور تمہیں خوف و خطر، فاقہ کشی، جان و مال کے نقصانات اور آمدنیوں کے گھاٹے میں مبتلا کر کے تمہاری آزمائش کریں گے');
INSERT INTO "ur_maududi" VALUES(2,156,'2:156','ان حالات میں جو لوگ صبر کریں اور جب کوئی مصیبت پڑے ، تو کہیں کہ ”ہم اللہ ہی کے ہیں اور اللہ ہی کی طرف پلٹ کر جا نا ہے ،“');
INSERT INTO "ur_maududi" VALUES(2,157,'2:157','انہیں خوش خبری دے دو ان پر ان کے رب کی طرف سے بڑی عنایات ہوں گی، اُس کی رحمت اُن پر سایہ کرے گی اور ایسے ہی لوگ راست رَو ہیں');
INSERT INTO "ur_maududi" VALUES(2,158,'2:158','یقیناً صفا اور مروہ اللہ کی نشانیوں میں سے ہیں۔ لہٰذا جو شخص بیت اللہ کا حج یا عمرہ کرے ، اس کے لیے کوئی گناہ کی بات نہیں کہ وہ ان دونوں پہاڑیوں کے درمیان سعی کر لے اور جو برضا و رغبت کوئی بھلائی کا کام کرے گا اللہ کو اس کا علم ہے اوروہ اس کی قدر کرنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,159,'2:159','جو لوگ ہماری نازل کی ہوئی روشن تعلیمات اور ہدایات کو چھپاتے ہیں در آں حال یہ کہ ہم انھیں سب انسانوں کی رہنمائی کے لیے اپنی کتاب میں بیان کر چکے ہیں ، یقین جانو کہ اللہ بھی ان پر لعنت کرتا ہےاور تمام لعنت کرنے والے بھی ان پر لعنت بھیجتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,160,'2:160','البتہ جو اس روش سے باز آ جائیں اور اپنے طرز عمل کی اصلاح کر لیں اور جو کچھ چھپاتے تھے، اُسے بیان کرنے لگیں، اُن کو میں معاف کر دوں گا اور میں بڑا در گزر کرنے والا اور رحم کرنے والا ہوں');
INSERT INTO "ur_maududi" VALUES(2,161,'2:161','جن لوگوں نے کفر کا رویہ اختیار کیا اور کفر کی حالت ہی میں جان دی، ان پر اللہ اور فرشتوں اور تمام انسانوں کی لعنت ہے');
INSERT INTO "ur_maududi" VALUES(2,162,'2:162','اسی لعنت زدگی کی حالت میں وہ ہمیشہ رہیں گے، نہ اُن کی سز امیں تخفیف ہوگی اور نہ انہیں پھر کوئی دوسری مہلت دی جائے گی');
INSERT INTO "ur_maududi" VALUES(2,163,'2:163','تمہارا خدا ایک ہی خدا ہے، اُس رحمان اور رحیم کے سوا کوئی اور خدا نہیں ہے');
INSERT INTO "ur_maududi" VALUES(2,164,'2:164','(اس حقیقت کوپہچاننے کے لیے اگر کوئی نشانی اور علامت درکار ہے تو )جو لوگ عقل سے کا م لیتے ہیں ان کے لیے آسمانوں اور زمین کی ساخت میں ، رات اور دن کےپیہم ایک دوسرے کے بعد آنے میں ، ان کشتیوں میں جو انسان کے نفع کی چیزیں لیے ہوئے دریاؤں اور سمندروں میں چلتی پھرتی ہیں ، بارش کے اس پانی میں جسے اللہ اوپر سے برساتا ہے پھر اس کے ذریعے سے زمین کو زندگی بخشتا ہےاور اپنے اسی انتظام کی بدولت زمین میں ہر قسم کی جاندار مخلوق کو پھیلاتا ہے، ہواؤں کی گردش میں ، اور ان بادلوں میں جو آسمان اور زمین کے درمیان تابع فرمان بنا کر رکھے گئے ہیں ، بے شمار نشانیاں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,165,'2:165','(مگر وحدتِ خدا وندی پر دلالت کرنے والے ان کھلے کھلے آثار کے ہوتے ہوئے بھی ) کچھ لوگ ایسے ہیں جو اللہ کے سوا دوسروں کو اس کا ہمسر اور مد مقابل بناتے ہیں اور ان کے ایسے گرویدہ ہیں جیسی اللہ کے ساتھ گرویدگی ہونی چاہیے۔ حالانکہ ایمان رکھنے والے لوگ سب سے بڑھ کر اللہ کو محبوب رکھتے ہیں۔ کاش ، جو کچھ عذاب کو سامنے دیکھ کر انھیں سوجھنے والاہےوہ آج ہی ان ظالموں کو سوجھ جائے کہ ساری طاقتیں اور سارے اختیارات اللہ ہی کےقبضے میں ہیں اور یہ کہ اللہ سزا دینے میں بھی بہت سخت ہے');
INSERT INTO "ur_maududi" VALUES(2,166,'2:166','جب وہ سزا دے گا اس وقت کیفیت یہ ہوگی کہ وہی پیشوا اور رہنما، جن کی دنیا میں پیروی کی گئی تھی، اپنے پیروؤں سے بے تعلقی ظاہر کریں گے، مگر سزا پا کر رہیں گے اور ان کے سارے اسباب و وسائل کا سلسلہ کٹ جائے گا');
INSERT INTO "ur_maududi" VALUES(2,167,'2:167','اور وہ لوگ جو دنیا میں ان کی پیروی کرتے تھے ، کہیں گے کہ کاش ہم کو پھر ایک موقع دیا جاتا تو جس طرح آج یہ ہم سے بے زاری ظاہر کر رہے ہیں ، ہم ان سے بے زار ہو کر دکھا دیتے۔ یوں اللہ ان لوگو ں کے وہ اعمال ، جو یہ دنیا میں کر رہے ہیں ،ان کے سامنے اس طرح لائے گا کہ یہ حسرتوں اور پشیمانیوں کے ساتھ ہاتھ ملتے رہیں گے مگر آگ سے نکلنے کی کوئی راہ نہ پائیں گے');
INSERT INTO "ur_maududi" VALUES(2,168,'2:168','لوگو! زمین میں جو حلال اور پاک چیزیں ہیں انھیں کھاؤ اور شیطان کے بتائے ہوئے راستوں پر نہ چلو۔ وہ تمہارا کھلا دشمن ہے');
INSERT INTO "ur_maududi" VALUES(2,169,'2:169','تمہیں بدی اور فحش کا حکم دیتا ہے اور سکھاتا ہےکہ تم اللہ کے نام پر وہ باتیں کہو جن کے متعلق تمہیں علم نہیں ہے کہ وہ اللہ نے فرمائی ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,170,'2:170','ن سے جب کہا جاتا ہے کہ اللہ نے جو احکام نازل کیےہیں ان کی پیروی کرو تو جواب دیتے ہیں کہ ہم تو اسی طریقے کی پیروی کریں گےجس پر ہم نے اپنے باپ دادا کو پایا ہے۔ اچھا اگر ان کے باپ دادا نے عقل سے کچھ بھی کام نہ لیا ہو اور راہ راست نہ پائی ہو تو کیا پھر بھی یہ انہیں کی پیروی کیے چلے جائیں گے؟');
INSERT INTO "ur_maududi" VALUES(2,171,'2:171','یہ لوگ جنھوں نے خدا کے بتائے ہوئے طریقے پر چلنے سے انکار کردیا ہے ان کی حالت بالکل ایسی ہے جیسے چرواہا جانوروں کو پکارتا ہے اور وہ ہانک پکار کی صدا کے سواکچھ نہیں سنتے۔ یہ بہرے ہیں ، گونگے ہیں، اندھے ہیں، اس لیے کوئی بات ان کی سمجھ میں نہیں آتی۔');
INSERT INTO "ur_maududi" VALUES(2,172,'2:172','اے لوگو جو ایمان لائے ہو، اگر تم حقیقت میں اللہ ہی کی بندگی کرنے والے ہو تو جو پاک چیزیں ہم نے تمہیں بخشی ہیں انہیں بےتکلف کھاؤ اور اللہ کا شکر ادا کرو۔ ');
INSERT INTO "ur_maududi" VALUES(2,173,'2:173','اللہ کی طرف سے اگر کوئی پابندی تم پر ہے تو وہ یہ ہے، کہ مردار نہ کھاؤ، خون سے اور سُور کے گوشت سے پرہیز کرو، اور کوئی ایسی چیز نہ کھاؤ جس پر اللہ کے سِوا کسی اور کا نام لیا گیاہو۔ ہاں جو شخص مجبوری کی حالت میں ہواور وہ ان میں سے کوئی چیز کھا لے بغیر اس کے کہ وہ قانون شکنی کا ارادہ رکھتا ہو یا ضرورت کی حد سے تجاوز کرے، تو اس پر کچھ گناہ نہیں ، اللہ بخشنے والا اور رحم کرنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,174,'2:174','حق یہ ہے کہ جو لوگ ان احکام کو چھپاتےہیں جو اللہ نے اپنی کتاب میں نازل کیے ہیں اور تھوڑے سے دنیوی فائدوں پر انہیں بھینٹ چڑھاتےہیں ، وہ دراصل اپنے پیٹ آگ سے بھر رہے ہیں ۔قیامت کے روز اللہ ہرگزان سے بات نہ کرے گا، نہ انھیں پاکیزہ ٹھیرائے گا، اور ان کے لیے دردناک سزا ہے');
INSERT INTO "ur_maududi" VALUES(2,175,'2:175','یہ وہ لوگ ہیں جنہوں نے ہدایت کے بدلے ضلالت خریدی اور مغفرت کے بدلے عذاب مول لے لیا کیسا عجیب ہے ان کا حوصلہ کہ جہنم کا عذاب برداشت کرنے کے لیے تیار ہیں');
INSERT INTO "ur_maududi" VALUES(2,176,'2:176','یہ سب کچھ اس وجہ سے ہوا کہ اللہ نے تو ٹھیک ٹھیک حق کے مطابق کتاب نازل کی تھی مگر جن لوگوں نے کتاب میں اختلاف نکالے وہ اپنے جھگڑوں میں حق سے بہت دور نکل گئے');
INSERT INTO "ur_maududi" VALUES(2,177,'2:177','نیکی یہ نہیں ہے کہ تم نے اپنے چہرے مشرق کی طرف کر لیے یا مغرب کی طرف ، بلکہ نیکی یہ ہے کہ آدمی اللہ کو اور یومِ آخر اور ملائکہ کو اور اللہ کی نازل کی ہوئی کتاب اور اس کے پیغمبروں کو دل سے مانے اور اللہ کی محبت میں اپنا دل پسند مال رشتے داروں اور یتیموں پر ، مسکینوں اور مسا فروں پر، مدد کے لیے ہاتھ پھیلانے والوں پر اور غلاموں کی رہائی پر خرچ کرے، نماز قائم کرے اور زکوٰة دے۔ اور نیک وہ لوگ ہیں کہ جب عہد کریں تو اسے وفا کریں، اور تنگدستی و مصیبت کے وقت میں اور حق و باطل کی جنگ میں صبر کریں۔ یہ ہیں راستباز لوگ اور یہی لوگ متقی ہیں');
INSERT INTO "ur_maududi" VALUES(2,178,'2:178','اے لوگو جو ایمان لائے ہو، تمہارےلیے قتل کے مقدموں میں قصاص کا حکم لکھ دیا گیا ہے۔ آزاد آدمی نےقتل کیا ہو تو آزاد ہی سے بدلہ لیا جائے ، غلام قاتل ہو تو غلام ہی قتل کیا جائے،اور عورت اس جرم کی مرتکب ہو تو اس عورت ہی سے قصاص لیا جائے۔ ہاں اگر کسی قاتل کے ساتھ اس کا بھائی کچھ نرمی کرنے کے لیے تیار ہو، تو معروف طریقے کے مطابق خوں بہا کا تصفیہ ہو نا چاہیے اور قاتل کو لازم ہے کہ راستی کے ساتھ خوں بہا ادا کرے۔ یہ تمہارے رب کی طرف سے تخفیف اور رحمت ہے۔ اس پر بھی جو زیادتی کرے، اس کے لیےدرد ناک سزاہے');
INSERT INTO "ur_maududi" VALUES(2,179,'2:179','قل و خردر کھنے والو!تمہارے لیے قصاص میں زندگی ہے۔ امید ہے کہ تم اس قانون کی خلاف ورزی سے پرہیز کرو گے');
INSERT INTO "ur_maududi" VALUES(2,180,'2:180','تم پر فرض کیا گیا ہے کہ جب تم میں سے کسی کی موت کا وقت آئے اور وہ اپنے پیچھے مال چھوڑ رہا ہو، تو والدین اور رشتہ داروں کے لیے معروف طریقے سے وصیت کرے۔ یہ حق ہے متقی لوگوں پر۔ ');
INSERT INTO "ur_maududi" VALUES(2,181,'2:181','پھر جنہوں نے وصیت سنی اور بعد میں اُسے بدل ڈالا، توا س کا گناہ ان بدلنے والوں پر ہوگا اللہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "ur_maududi" VALUES(2,182,'2:182','البتہ جس کو یہ اندیشہ ہو کہ وصیت کرنے والے نے نادانستہ یا قصداً حق تلفی کی ہے، اور پھر معاملے سے تعلق رکھنے والوں کے درمیان وہ اصلاح کرے، تو اس پر کچھ گناہ نہیں ہے، اللہ بخشنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(2,183,'2:183','اے لوگو جو ایمان لائے ہو ، تم پر روزے فرض کر دیے گئے ، جس طرح تم سے پہلے انبیا کے پیرووں پر فرض کیے گئے تھے۔ اس سے توقع ہے کہ تم میں تقویٰ کی صفت پیدا ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(2,184,'2:184','چند مقرر دنوں کے روزے ہیں۔ اگر تم میں سے کوئی بیمار ہو ، یا سفر پر ہو تو دُوسرے دنوں میں اتنی ہی تعداد پوری کر لے۔ اور جو لوگ روزہ رکھنے کی قدرت رکھتے ہوں(پھر نہ رکھیں) تو وہ فدیہ دیں۔ ایک روزے کا فدیہ ایک مسکین کو کھانا کھلانا ہے اور جو اپنی خوشی سے زیادہ بھلائی کرے ، تو یہ اسی کے لیے بہتر ہے۔ لیکن اگر تم سمجھو ، تو تمہارے حق میں اچھا یہی ہے کہ تم روزہ رکھو۔ ');
INSERT INTO "ur_maududi" VALUES(2,185,'2:185','رمضان وہ مہینہ ہے ، جس میں قرآن نازل کیا گیا جو انسانوں کے لیے سراسر ہدایت ہے اور ایسی واضح تعلیمات پر مشتمل ہے ، جو راہِ راست دکھانے والی اور حق و باطل کا فرق کھول کر رکھ دینے والی ہے۔ لہٰذا اب سے جو شخص اس مہینے کو پائے ، اُس پر لازم ہے کہ اس پورے مہینے کے روزے رکھے۔ اور جو کوئی مریض ہویا سفر پر ہو ، تو وہ دُوسرے دنوں میں تعداد پوری کرے۔ اللہ تمہارے ساتھ نرمی کرنا چاہتا ہے ، سختی کرنا نہیں چاہتا۔ اس لیے یہ طریقہ تمہیں بتایا جارہا ہے تاکہ تم روزوں کی تعداد پوری کرسکو اور جس ہدایت سے اللہ نے تمہیں سرفراز کیا ہے ، اُس پر اللہ کی کبریائی کا اظہار واعتراف کرو اور شکر گزار بنو۔ ');
INSERT INTO "ur_maududi" VALUES(2,186,'2:186','اور اے نبیؐ ، میرے بندے اگر تم سے میرے متعلق پوچھیں، تو اُ نھیں بتا دو کہ میں اُن سے قریب ہی ہوں۔پکارنے والا جب مجھے پکارتا ہے ، میں اُس کی پکار سنتا اور جواب دیتا ہوں۔ لہٰذا انھیں چاہیے کہ میری دعوت پر لبیک کہیں اور مجھ پر ایمان لائیں یہ بات تم اُنھیں سُنادو شاید کہ وہ راہِ راست پالیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,187,'2:187','تمہارے لیے روزوں کے زمانے میں راتوں کو اپنی بیویوں کے پاس جانا حلال کردیا گیا ہے۔ وہ تمہارے لیے لباس ہیں اور تم اُن کے لیے۔ اللہ کو معلوم ہوگیا کہ تم لوگ چُپکے چُپکے اپنے آپ سے خیانت کر رہے تھے ، مگر اُس نے تمہارا قصُور معاف کر دیا اور تم سے درگزر فرمایا۔ اب تم اپنی بیویوں کے ساتھ شب باشی کرو اور جو لُطف اللہ نے تمہارے لیے جائز کردیا ہے ، اُسے حاصل کرو۔ نیز راتوں کو کھاوٴ پیو یہاں تک کہ تم کو سیاہیِ شب کی دھاری سے سپیدہ ٴ صبح کی دھاری نمایا ں نظر آجائے۔ تب یہ سب کام چھوڑ کر رات تک اپنا روزہ پُورا کرو۔ اور جب تم مسجدوں میں معتکف ہو ، تو بیویوں سے مباشرت نہ کرو۔ یہ اللہ کی باندھی ہوئی حدیں ہیں، ان کے قریب نہ پھٹکنا۔ اس طرح اللہ اپنے احکام لوگوں کے لیے بصراحت بیان کرتا ہے ، توقع ہے کہ وہ غلط رویےّ سے بچیں گے');
INSERT INTO "ur_maududi" VALUES(2,188,'2:188','اور تم لوگ نہ تو آپس میں ایک دُوسرے کے مال ناروا طریقہ سے کھاوٴ اور نہ حاکموں کے آگے ان کو اس غرض کے لیے پیش کرو کہ تمہیں دُوسروں کے مال کا کوئی حصہّ قصداً ظالمانہ طریقے سے کھانے کا موقع مل جائے۔ ');
INSERT INTO "ur_maududi" VALUES(2,189,'2:189','لوگ تم سے چاند کی گھٹتی بڑھتی صورتوں کے متعلق پوچھتے ہیں۔ کہو : یہ لوگوں کے لیے تاریخوں کی تعیین کی اور حج کی علامتیں ہیں۔ نیز ان سےکہو : یہ کوئی نیکی کا کام نہیں ہے کہ تم اپنے گھروں میں پیچھے کی طرف سے داخل ہوتے ہو۔نیکی تو اصل میں یہ ہے کہ آدمی اللہ کی ناراضی سے بچے۔ لہٰذا تم اپنے گھروں میں دروازےہی سے آیا کرو۔ البتہ اللہ سے ڈرتے رہو۔ شاید کہ تمھیں فلاح نصیب ہو جائے۔ ');
INSERT INTO "ur_maududi" VALUES(2,190,'2:190','اور تم اللہ کی راہ میں ان لوگوں سے لڑو، جو تم سے لڑتے ہیں ، مگر زیادتی نہ کرو کہ اللہ زیادتی کرنے والوں کو پسند نہیں کرتا ');
INSERT INTO "ur_maududi" VALUES(2,191,'2:191','ان سے لڑو جہاں بھی تمہارا ان سے مقابلہ پیش آئے اور انھیں نکالو جہاں سے انھوں نے تم کو نکالا ہے، اس لیے کہ قتل اگرچہ بُرا ہے، مگر فتنہ اس سے زیادہ بُرا ہے۔ اور مسجد حرام کے قریب جب تک وہ تم سے نہ لڑیں ، تم بھی نہ لڑو، مگر جب وہ وہاں لڑنے سے نہ چوکیں، تو تم بھی بے تکلف انھیں مارو کہ ایسے کافروں کی یہی سزا ہے');
INSERT INTO "ur_maududi" VALUES(2,192,'2:192','پھر اگر وہ باز آجائیں، تو جان لو کہ اللہ معاف کرنے والا اور رحم فرمانے والاہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,193,'2:193','تم ان سے لڑتے رہو یہاں تک کہ فتنہ باقی نہ رہے اور دین اللہ کے لیے ہو جائے۔ پھر اگر وہ باز آجائیں ، تو سمجھ لو کہ ظالموں کے سوا اور کسی پر دست درازی روا نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,194,'2:194','ماہِ حرام کا بدلہ ماہِ حرام ہی ہے اور تمام حرمتوں کا لحاظ برابری کے ساتھ ہوگا۔ لہٰذا جو تم پر دست درازی کرے، تم بھی اسی طرح اس پر دست درازی کرو۔ البتہ اللہ سے ڈرتے رہو اور یہ جان رکھو کہ اللہ انھیں لوگوں کے ساتھ ہے، جو اس کی حدود توڑنے سے پرہیز کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(2,195,'2:195','اللہ کی راہ میں خرچ کرو اور اپنے آپ کو ہلاکت میں نہ ڈالو۔ احسان کا طریقہ اختیا ر کرو کہ اللہ محسنوں کو پسند کرتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,196,'2:196','اللہ کی خوشنودی کے لیے جب حج اور عمرے کی نیت کر و، تو اسے پورا کرو، اور اگر کہیں گر جاؤ تو جو قربانی میسر آئے، اللہ کی جناب میں پیش کرو اور اپنےسر نہ مونڈو جب تک کہ قربانی اپنی جگہ نہ پہنچ جائے۔ مگر جو شخص مریض ہو یا جس کے سر میں کوئی تکلیف ہو اس بناء پر اپنا سر منڈوالے، تو اسے چاہیے کہ فدیہ کے طور پر روزے رکھے یا صدقہ دے یا قربانی کرے۔ پھر اگر تمہیں امن نصیب ہو جا ئے (اور تم حج سے پہلے مکہ پہنچ جاؤ )، تو جو شخص تم میں سے حج کا زمانہ آنے تک عمرے کا فائدہ اٹھائے ، وہ حسب مقدور قربانی دے، اور اگر قربانی میسر نہ ہو ، تو تین روزے حج کے زمانے میں اور سات گھر پہنچ کر ، اس طرح پورے دس روزے رکھ لے۔ یہ رعایت ان لوگو ں کے لیے ہے ، جن کے گھر بار مسجد حرام کے قریب نہ ہوں۔ اور اللہ کے ان احکام کی خلاف ورزی سے بچو اور خوب جان لو کہ اللہ سخت سزا دینے والا ہے');
INSERT INTO "ur_maududi" VALUES(2,197,'2:197','حج کے مہینے سب کو معلوم ہیں۔ جو شخص ان مقرر مہینوں میں حج کی نیّت کرے، اُسے خبردار رہنا چاہیے کہ حج کے دوران میں اس سے کوئی شہوانی فعل ، کوئی بدعملی، کوئی لڑائی جھگڑے کی بات سرزد نہ ہو۔ اور جو نیک کام تم کرو گے ، وہ اللہ کے علم میں ہوگا۔ سفرِحج کے لیے زادِ راہ ساتھ لے جاوٴ۔ اور سب سے بہتر زادِراہ پرہیزگاری ہے۔ پس اے ہوشمندو ! میری نافرمانی سے پرہیزکرو۔ ');
INSERT INTO "ur_maududi" VALUES(2,198,'2:198','اور اگر حج کے ساتھ ساتھ تم اپنے ربّ کا فضل بھی تلاش کرتے جاوٴ۔ تو اس میں کوئی مضائقہ نہیں۔ پھر جب عرفات سے چلو، تو مشعرِ حرام (مزدلفہ) کے پاس ٹھیر کر اللہ کو یاد کرو اور اُس طرح یاد کرو۔ جس کی ہدایت اس نے تمہیں دی ہے، ورنہ اس سے پہلے تم لوگ بھٹکے ہوئے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(2,199,'2:199','پھر جہاں سے اور سب لوگ پلٹتے ہیں وہیں سے تم بھی پلٹو اور اللہ سے معافی چاہو، یقیناً وہ معاف کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(2,200,'2:200','پھر جب اپنے حج کے ارکان ادا کر چکو، تو جس طرح پہلے اپنے آبا واجداد کا ذکر کرتے تھے ، اُسی طرح اب اللہ کا ذکر و، بلکہ اس سے بھی بڑھ کر۔ (مگر اللہ کو یاد کرنے والے لوگوں میں بھی بہت فرق ہے ) اُن میں سے کوئی تو ایسا ہے ، جو کہتا ہے کہ اے ہمارے ربّ ، ہمیں دنیا ہی میں سب کچھ دے دے۔ ایسے شخص کے لیے آخرت میں کوئی حصہ نہیں');
INSERT INTO "ur_maududi" VALUES(2,201,'2:201','اور کوئی کہتا ہے کہ اے ہمارے رب! ہمیں دنیا میں بھی بھلائی دے اور آخرت میں بھی بھلائی اور آگ کے عذاب سے ہمیں بچا');
INSERT INTO "ur_maududi" VALUES(2,202,'2:202','ایسے لوگ اپنی کمائی کے مطابق (دونوں جگہ) حصہ پائیں گے اور اللہ کو حساب چکاتے کچھ دیر نہیں لگتی');
INSERT INTO "ur_maududi" VALUES(2,203,'2:203','یہ گنتی کے چندروز ہیں ، جو تمہیں اللہ کے یاد میں بسر کرنے چاہئیں۔ پھر جو کوئی جلدی کرکےدو ہی دن میں واپس ہو گیا تو کوئی حرج نہیں، اور جو کچھ دیر زیادہ ٹھیر کرپلٹا تو بھی کوئی حرج نہیں۔ بشرطیکہ یہ دن اس نے تقوٰی کے ساتھ بسر کیے ہوں۔۔۔۔ اللہ کی نافرمانی سے بچو اور خوب جان رکھو کہ ایک روز اس کے حضور میں تمہاری پیشی ہونے والی ہے');
INSERT INTO "ur_maududi" VALUES(2,204,'2:204','انسانوں میں کوئی تو ایسا ہے، جس کی باتیں دنیا کی زندگی میں تمہیں بہت بھلی معلوم ہوتی ہیں، اور اپنی نیک نیتی پر وہ بار بار خدا کو گواہ ٹھیراتا ہے ، مگر حقیقت میں وہ بد ترین دشمن ِ حق ہوتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,205,'2:205','جب اسے اقتدار حاصل ہو جاتا ہے ، تو زمین میں اس کی ساری دوڑ دھوپ اس لیے ہو تی ہےکہ فساد پھیلائے، کھیتوں کو غارت کرے اور نسل ِ انسانی کو تباہ کرے۔۔۔۔حالانکہ اللہ (جسے وہ گواہ بنا رہاتھا) فساد کو ہر گز پسند نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(2,206,'2:206','اور جب اس سے کہا جاتا ہے کہ اللہ سے ڈر، تو اپنے وقار کا خیال اُس کو گناہ پر جما دیتا ہے ایسے شخص کے لیے تو بس جہنم ہی کافی ہے اور وہ بہت برا ٹھکانا ہے');
INSERT INTO "ur_maududi" VALUES(2,207,'2:207','دوسری طرف انسانوں ہی میں کوئی ایسا بھی ہے، جو رضا ئے الٰہی کی طلب میں اپنی جان کھپا دیتا ہے اور ایسے بندوں پر اللہ بہت مہربان ہے');
INSERT INTO "ur_maududi" VALUES(2,208,'2:208','اے ایمان لانے والوں! تم پورے کے پورے اسلام میں آجاؤ اور شیطان کی پیروی نہ کرو کہ وہ تمہارا کھلا دشمن ہے');
INSERT INTO "ur_maududi" VALUES(2,209,'2:209','جو صاف صاف ہدایات تمہارے پاس آچکی ہیں، اگر ان کو پا لینے کے بعد پھر تم نے لغزش کھائی، تو خوب جان رکھو کہ اللہ سب پر غالب اور حکیم و دانا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,210,'2:210','(ان ساری نصیحتو ں اور ہدایتوں کے بعد بھی لوگ سیدھے نہ ہو ں ، تو )کیا اب وہ اس کے منتظر ہیں کہ اللہ بادلوں کا چتر لگائے فرشتوں کے پرے ساتھ لیے خود سامنے آ موجود ہو اور فیصلہ ہی کر ڈالاجائے؟ ۔۔۔۔آخر کار سارے معاملات پیش تو اللہ ہی کے حضور ہونے والے ہیں');
INSERT INTO "ur_maududi" VALUES(2,211,'2:211','بنی اسرئیل سے پوچھو: کیسی کھلی کھلی نشانیاں ہم نے انھیں دکھائی ہیں(اور پھر یہ بھی ان ہی سے پوچھ لو کہ )اللہ کی نعمت پانے کے بعد جو قوم اس کو شقاوت سے بدلتی ہے اسے اللہ کیسی سخت سزا دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,212,'2:212','جن لوگوں نے کفر کی راہ اختیار کی ہے، اُن کے لیے دنیا کی زندگی بڑی محبوب و دل پسند بنا دی گئی ہے ایسے لوگ ایمان کی راہ اختیار کرنے والوں کا مذاق اڑاتے ہیں، مگر قیامت کے روز پرہیز گار لوگ ہی اُن کے مقابلے میں عالی مقام ہوں گے رہا دنیا کا رزق، تو اللہ کو اختیار ہے، جسے چاہے بے حساب دے');
INSERT INTO "ur_maududi" VALUES(2,213,'2:213','ابتداء میں سب لوگ ایک ہی طریقے پر تھے۔ (پھر یہ حالت باقی نہ رہی اور اختلافات رونما ہوئے)تب اللہ نے نبی بھیجے جو راست روی پر بشارت دینے والے اور کج ر وی کے نتائج سےڈرانے والے تھے، اور ان کے ساتھ کتاب ِبر حق نازل کی تا کہ حق کے بارے میں لوگوں کے درمیان جو اختلافات رونما ہو گئے تھے، ان کا فیصلہ کرے۔۔۔۔(اور ان اختلافات کے رونما ہونے کی وجہ یہ نہ تھی کہ ابتداء میں لوگوں کو حق بتایا نہیں گیا تھا۔ نہیں،) اختلاف ان لوگوں نے کیا ، جنہیں حق کا علم دیا چکاتھا۔ انھوں نے روشن ہدایات پا لینے کے بعد محض اس لیے حق کو چھوڑ کر مختلف طریقے نکالےکہ وہ آپس میں زیادتی کرنا چاہتے تھے ۔۔۔۔ پس جو لوگ انبیاؑ پر ایمان لے آئے، انہیں اللہ نے اپنے اذن سے اس حق کا راستہ دکھادیا، جس میں لوگوں نے اختلاف کیا تھا۔ اللہ جسے چاہتا ہے ، راہِ راست دکھا دیتا ہے');
INSERT INTO "ur_maududi" VALUES(2,214,'2:214','پھر کیا تم لوگوں نے یہ سمجھ رکھا ہے کہ یوُ نہی جنت کا داخلہ تمہیں مِل جائے گا، حالانکہ ابھی تم پر وہ سب کچھ نہیں گزرا ہے، جو تم سے پہلے ایمان لانے والوں پر گزر چکا ہے؟ ان پر سختیاں گزر یں، مصیبتیں آئیں، ہلا مارے گئے، حتٰی کہ وقت کا رسول اور اس کےساتھی اہل ایمان چیخ اٹھے کہ اللہ کی مدد کب آئے گی۔۔۔۔ اس وقت انھیں تسلّی دی گئی کہ ہاں اللہ کی مدد قریب ہے');
INSERT INTO "ur_maududi" VALUES(2,215,'2:215','لوگ پوچھتے ہیں کہ ہم کیا خرچ کریں؟ جواب دو کہ جو مال بھی تم خرچ کرو اپنے والدین پر، رشتے داروں پر، یتیموں اور مسکینوں اور مسافروں پر خرچ کرو اور جو بھلائی بھی تم کرو گے، اللہ اس سے باخبر ہوگا');
INSERT INTO "ur_maududi" VALUES(2,216,'2:216','تمہیں جنگ کا حکم دیا گیا ہے اور وہ تمہیں ناگوار ہے ہوسکتا ہے کہ ایک چیز تمہیں ناگوار ہو اور وہی تمہارے لیے بہتر ہو اور ہوسکتا ہے کہ ایک چیز تمہیں پسند ہو اور وہی تمہارے لیے بری ہو اللہ جانتا ہے، تم نہیں جانتے');
INSERT INTO "ur_maududi" VALUES(2,217,'2:217','لوگ پوچھتے ہیں ماہِ حرام میں لڑنا کیسا ہے؟ کہو: اس میں لڑنا بہت برا ہے ، مگرراہِ خُدا سے لوگوں کو روکنا اور اللہ سے کفر کرنا اور مسجدِحرام کا راستہ خُدا پرستوں پر بند کرنا اور حرم کے رہنے والوں کو وہاں سے نکالنا اللہ کےنزدیک اس سے بھی زیادہ برا ہے اور فتنہ خونریزی سے شدیدتر ہے۔ وہ تو تم سے لڑے ہی جائیں گےحتٰی کہ اگر ان کا بس چلے، تو تمہیں اس دین سے پھیر لے جائیں۔ (اور یہ خوب سمجھ لو کہ ) تم میں سے جو کوئی اس دین سے پھرے گا اور کفر کی حالت میں جان دے گا، اس کے اعمال دنیا اور آخرت دونوں میں ضائع ہو جائیں گے۔ ایسے سب لوگ جہنمی ہیں اور ہمیشہ جہنم ہی میں رہیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(2,218,'2:218','بخلاف اس کے جو لوگ ایمان لائے ہیں اور جنھوں نے خدا کی راہ میں اپنا گھر بار چھوڑا اور جہاد کیا ہے ، وہ رحمت ِ الٰہی کے جائز امید وار ہیں اور اللہ ان کی لغزشوں کو معاف کرنے والا اور اپنی رحمت سے انھیں نوازنے والا ہے');
INSERT INTO "ur_maududi" VALUES(2,219,'2:219','پوچھتے ہیں : شراب اور جوئے کا کیا حکم ہے؟ : ان دونوں چیزوں میں بڑی خرابی ہے۔ اگرچہ ان میں لوگوں کے لیے کچھ منافع بھی ہیں ، مگر ان کا گناہ ان کے فائدے سے بہت زیادہ ہے۔ پوچھتے ہیں : ہم راہ خدا میں کیا خرچ کریں ؟ کہو : جو کچھ تمہاری ضرورت سے زیادہ ہو۔ اس طرح اللہ تمہارے لیےصاف صاف احکام بیان کرتا ہے،شاید کہ تم دنیا اور آخرت دونوں کی فکر کرو');
INSERT INTO "ur_maududi" VALUES(2,220,'2:220','پوچھتے ہیں: یتیموں کے ساتھ کیا معاملہ کیا جائے؟ کہو : جس طرز عمل میں ان کے لیے بھلائی ہو ، وہی اختیار کرنا بہتر ہے۔ اگر تم اپنا اور ان کا خرچ اور رہنا سہنا مشترک رکھو، تو اس میں کوئی مضائقہ نہیں۔ آخر وہ تمہارے بھائی بند ہی تو ہیں۔ برائی کرنے والے اور بھلائی کرنے والے، دونوں کا حال اللہ پر روشن ہے۔اللہ چاہتا تو اس معاملہ میں تم پر سختی کرتا مگر وہ صاحب اختیار ہونے کے ساتھ صاحب حکمت بھی ہے');
INSERT INTO "ur_maududi" VALUES(2,221,'2:221','تم مشرک عورتوں سے ہرگز نکاح نہ کرنا ، جب تک کہ وہ ایمان نہ لے آئیں۔ ایک مومن لونڈی مشرک شریف زادی سے بہتر ہے ، اگرچہ وہ تمہیں بہت پسند ہو۔ اور اپنی عورتوں کے نکاح مشرک مردوں سے کبھی نہ کرنا، جب تک کہ وہ ایمان نہ لے آئیں۔ ایک مومن غلام مشرک شریف سے بہتر ہے اگرچہ وہ تمہیں بہت پسند ہو۔ یہ لوگ تمہیں آگ کر طرف بلاتے ہیں اور اللہ اپنے اذن سے تم کو جنت اور مغفرت کی طرف بلاتا ہے، اور وہ اپنے احکام واضح طور پر لوگوں کے سامنے بیان کرتا ہے، توقع ہے کہ وہ سبق لیں گے اور نصیحت قبول کریں گے');
INSERT INTO "ur_maududi" VALUES(2,222,'2:222','پوچھتے ہیں: حیض کا کیا حکم ہے؟ کہو: وہ ایک گندگی کی حالت ہے۔ اس میں عورتوں سے الگ رہو اور ان کے قریب نہ جاؤ، جب تک کہ وہ پاک صاف نہ ہو جائیں ۔پھر جب وہ پاک ہو جائیں، تو ان کے پاس جاؤ اس طرح جیسا کہ اللہ نے تم کو حکم دیا ہے۔ اللہ ان لوگوں کو پسند کرتا ہے، جو بدی سے باز رہیں اور پاکیزگی اختیار کریں');
INSERT INTO "ur_maududi" VALUES(2,223,'2:223','تمہاری عورتیں تمہاری کھیتیاں ہیں۔ تمہیں اختیار ہے، جس طرح چاہو، اپنی کھیتی میں جاؤ ، مگر اپنے مستقبل کی فکر کرو اور اللہ کی ناراضی سے بچو۔ خوب جان لو کہ تمہیں ایک دن اس سے ملنا ہے۔ اور اے نبیؐ ! جو تمہاری ہدایت کو مان لیں انہیں فلاح وسعادت کا مژدہ سنادو');
INSERT INTO "ur_maududi" VALUES(2,224,'2:224','اللہ کے نام کو ایسی قسمیں کھانے کے لیے استعمال نہ کرو، جن سے مقصود نیکی اور تقوٰی اور بندگان خدا کی بھلائی کے کاموں سے باز رہنا ہو۔ اللہ تمہاری باتین سن رہا ہے اور سب کچھ جانتا ہے');
INSERT INTO "ur_maududi" VALUES(2,225,'2:225','جو بے معنی قسمیں تم بلا ارادہ کھا لیا کرتے ہو، ان پر اللہ گرفت نہیں کرتا، مگر جو قسمیں تم سچے دل سے کھاتے ہو، ان کی باز پرس وہ ضرور کرے گا۔ اللہ بہت درگزر کرنے والا اور بردبار ہے');
INSERT INTO "ur_maududi" VALUES(2,226,'2:226','جو لوگ اپنی عورتوں سے تعلق نہ رکھنے کی قسم کھا بیٹھتے ہیں، ان کے لیے چار مہینے کی مہلت ہے ۔ اگر انھوں نے رجوع کرلیا، تو اللہ معاف کرنے والا اور رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,227,'2:227','اور اگر انہوں نے طلاق ہی کی ٹھان لی ہو تو جانے رہیں کہ اللہ سب کچھ سنتا اور جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,228,'2:228','جن عورتوں کو طلاق دی گئی ہو ، وہ تین مرتبہ ایّام ِ ماہواری آنے تک اپنے آپ کو روکے رکھیں اور ان کے لیے یہ جائز نہیں ہے کہ اللہ نے ان کے رحم میں جو کچھ خَلق فرمایا ہو، اسے چھپائیں۔ انھیں ہرگز ایسا نہ کرنا چاہیے، اگر وہ اللہ اور روزِ آخر پر ایمان رکھتی ہیں۔ ان کے شوہر تعلقات درست کر لینے پر آمادہ ہوں، تو وہ اس عدّت کے دوران میں انھیں پھر اپنی زوجیّت میں واپس لے لینے کے حق دار ہیں۔ عورتوں کے لیے بھی معروف طریقے پر ویسے ہی حقوق ہیں، جیسے مَردوں کے حقوق ان پر ہیں۔ البتہ مردوں کو ان پر ایک درجہ حاصل ہے۔ اور سب پر اللہ غالب اقتدار رکھنے والا اور حکیم ودانا موجود ہے');
INSERT INTO "ur_maududi" VALUES(2,229,'2:229','طلاق دو بار ہے۔ پھر یا تو سیدھی طرح عورت کو روک لیا جائے یا بھلے طریقے سے اس کو رخصت کر دیا جائے۔ اور رخصت کرتے ہوئے ایسا کرنا تمہارے لیے جائز نہیں ہے کہ جو کچھ تم انہیں دے چکے ہو ، اس میں سے کچھ واپس لے لو۔ البتہ یہ صورت مستشنٰی ہےکہ زوجین کو اللہ کے حدود پر قائم نہ رہ سکنے کا اندیشہ ہو۔ ایسی صورت میں اگر تمہیں یہ خوف ہو کہ وہ دونوں حدود الٰہی پر قائم نہ رہیں گے، تو ان دونوں کے درمیان یہ معاملہ ہو جانے میں مضائقہ نہیں کہ عورت اپنے شوہر کو معاوضہ دے کر علیحدگی حاصل کرلے۔ یہ اللہ کے مقرر کردہ حدود ہیں، ان سے تجاوز نہ کرو۔ اور جو لوگ حدود الٰہی سے تجاوز کریں، وہی ظالم ہیں');
INSERT INTO "ur_maududi" VALUES(2,230,'2:230','پھر اگر( دوبار طلاق دینے کے بعد شوہر نے عورت کو تیسری بار)طلاق دے دی، تو وہ عورت پھر اس کے لیے حلال نہ ہو گی، اِلّا یہ کہ اس کا نکاح کسی دوسرے شخص سے ہو اور وہ اسے طلاق دیدے۔ تب اگر پہلا شوہر اوریہ عورت دونوں یہ خیال کریں کہ حدودِ الٰہی پر قائم رہیں گے،تو ان کے لیے ایک دوسرےکی طرف رجوع کر لینے میں کوئی مضائقہ نہیں۔ یہ اللہ کی مقرر کردہ حدیں ہیں، جنھیں وہ ان لوگوں کی ہدایت کے لیے واضح کر رہا ہے، جو (اس کی حدوں کو توڑنے کا انجام )جانتے ہیں');
INSERT INTO "ur_maududi" VALUES(2,231,'2:231','اور جب تم عورتوں کو طلاق دیدو اور ان کی عدّت پوری ہونے کو آجائے، تو یا بھلے طریقے سے انہیں روک لو یا بھلے طریقے سے رخصت کردو۔ محض ستانے کی خاطر انہیں نہ روکے رکھنا کہ یہ زیادتی ہو گی اور جو ایسا کرے گا، وہ درحقیقت آپ اپنے ہی اوپر ظلم کرے گا۔ اللہ کی آیات کا کھیل نہ بناؤ۔ بھول نہ جاؤ کہ اللہ نے کس نعمت عظمٰی سے تمہیں سرفراز کیا ہے۔ وہ تمہیں نصیحت کرتا ہےکہ جو کتاب اور حکمت اس نے تم پر نازل کی ہے، اس کا احترام ملحوظ رکھو۔ اللہ سے ڈرو اور خوب جان لو کہ اللہ کو ہر بات کی خبر ہے');
INSERT INTO "ur_maududi" VALUES(2,232,'2:232','جب تم اپنی عورتوں کو طلاق دے چکو اور وہ عدّت پوری کر لیں، تو پھر اس میں مانع نہ ہو کہ وہ اپنے زیرِ تجویز شوہروں سے نکاح کر لیں، جب کہ وہ معروف طریقے سے با ہم مناکحت پر راضی ہوں۔ تمہیں نصیحت کی جاتی ہے کہ ایسی حرکت ہرگز نہ کرنا، اگر تم اللہ اور روزِ آخر پر ایمان لانے والے ہو۔ تمہارے لیے شائستہ اور پاکیزہ طریقہ یہی ہے کہ اس سے باز رہو۔ اللہ جانتا ہے تم نہیں جانتے');
INSERT INTO "ur_maududi" VALUES(2,233,'2:233','جو باپ چاہتے ہوں کہ ان کی اولاد پوری مدّت رضاعت تک دودھ پیے، تو مائیں اپنے بچّوں کو کامل دو سال دودھ پلائیں۔ اس صورت میں بچّے کے باپ کو معروف طریقے سے انہیں کھانا کپڑا دینا ہو گا۔ مگر کسی پر اس کی وسعت سے بڑھ کر با ر نہ ڈالنا چاہیے، نہ تو ماں کو اس وجہ سے تکلیف میں ڈالا جائے کہ بچّہ اس کا ہے، اور نہ باپ ہی کو اس وجہ سے تنگ کیا جائے کہ بچّہ اس کا ہے۔۔۔۔ دودھ پلانے والی کا یہ حق جیسا بچّے کے باپ پر ہے، ویسا ہی اس کے وارث پربھی ہے ۔۔۔۔ لیکن اگر فریقین باہمی رضا مندی اور مشورے سے دودھ چھڑانا چاہیں، تو ایسا کرنے میں کوئی مضائقہ نہیں۔ اور اگر تمہارا خیال اپنی اولاد کو کسی غیر عورت سے دودھ پلوانے کا ہو، تو اس میں بھی کوئی حرج نہیں بشرطیکہ اس کا جو کچھ معاوضہ طے کرو، وہ معروف طریقے پر ادا کردو۔ اللہ سے ڈرو اور جان رکھو کہ جو کچھ تم کرتے ہو ، سب اللہ کی نظر میں ہے');
INSERT INTO "ur_maududi" VALUES(2,234,'2:234','تم میں سے جو لوگ مر جائیں ، ان کے پیچھے اگر ان کی بیویاں زندہ ہوں، تو وہ اپنے آپ کو چار مہینے ، دس دن روکے رکھیں۔ پھر جب ان کی عدّت پوری ہو جائے، تو انہیں اختیار ہے، اپنی ذات کے معاملے میں معروف طریقے سے جو چاہیں، کریں۔ تم پر اس کی کوئی ذمّے داری نہیں۔ اللہ تم سب کے اعمال سے باخبر ہے');
INSERT INTO "ur_maududi" VALUES(2,235,'2:235','زمانہ عدت میں خواہ تم اُن بیوہ عورتوں کے ساتھ منگنی کا ارادہ اشارے کنایے میں ظاہر کر دو، خواہ دل میں چھپائے رکھو، دونوں صورتوں میں کوئی مضائقہ نہیں اللہ جانتا ہے کہ اُن کا خیال تو تمہارے دل میں آئے گا ہی مگر دیکھو! خفیہ عہد و پیمان نہ کرنا اگر کوئی بات کرنی ہے، تو معرف طریقے سے کرو اور عقد نکاح باندھنے کا فیصلہ اُس وقت تک نہ کرو، جب تک کہ عدت پوری نہ ہو جائے خوب سمجھ لو کہ اللہ تمہارے دلوں کا حال تک جانتا ہے لہٰذا اس سے ڈرو اور یہ بھی جان لو کہ اللہ بردبار ہے، چھوٹی چھوٹی باتوں سے درگزر فرماتا ہے');
INSERT INTO "ur_maududi" VALUES(2,236,'2:236','تم پر کچھ گناہ نہیں، اگر اپنی عورتوں کو طلاق دے دو قبل اس کے کہ ہاتھ لگانے کی نوبت آئے یا مہر مقرر ہو۔ اس صورت میں انھیں کچھ نہ کچھ دینا ضرور چاہیے۔ خوش حال آدمی اپنی مقدرت کے مطابق اور غریب اپنی مقدرت کے مطابق معروف طریقہ سے دے۔ یہ حق ہے نیک آدمیوں پر');
INSERT INTO "ur_maududi" VALUES(2,237,'2:237','اور اگر تم نے ہاتھ لگانے سے پہلے طلاق دی ہو ، لیکن مہرمقرر کیا جاچکا ہو، جس کے اختیار میں عقدِ نکاح ہے، نرمی سے کام لے( اور پورا مہر دیدے)، اور تم (یعنی مرد)نرمی سے کام لو، تو یہ تقوٰی سے زیادہ مناسبت رکھتا ہے۔ آپس کے معاملات میں فیّاضی کو نہ بھولو۔ تمہارے اعمال کو اللہ دیکھ رہاہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,238,'2:238','اپنی نمازوں کی نگہداشت رکھو، خصوصاً ایسی نماز کی جو محاسِن صلوٰة کی جامع ہو۔ اللہ کے آگے اس طرح کھڑے ہو، جیسے فرماں بردار غلام کھڑے ہوتےہیں');
INSERT INTO "ur_maududi" VALUES(2,239,'2:239','بدامنی کی حالت ہو، تو خواہ پیدل ہو، خواہ سوار، جس طرح ممکن ہو، نماز پڑھو اور جب امن میسر آجائے، تو اللہ کو اُس طریقے سے یاد کرو، جو اُس نے تمہیں سکھا دیا ہے، جس سے تم پہلے نا واقف تھے');
INSERT INTO "ur_maududi" VALUES(2,240,'2:240','تم میں سے جو لوگ وفات پائیں اور پیچھے بیویاں چھوڑرہےہوں، ان کو چاہیے کہ اپنی بیویوں کے حق میں یہ وصیّت کر جائیں کہ ایک سال تک ان کو نان و نفقہ دیا جائے اور وہ گھر سے نہ نکالی جائیں۔پھر اگر وہ خود نکل جائیں، تو اپنی ذات کے معاملے میں معروف طریقے سے وہ جو کچھ بھی کریں، اس کی کوئی ذمّہ داری تم پر نہیں ہے، اللہ سب پر غالب اقتدار رکھنے والا اور حکیم و دانا ہے');
INSERT INTO "ur_maududi" VALUES(2,241,'2:241','اِسی طرح جن عورتوں کو طلاق دی گئی ہو، انہیں بھی مناسب طور پر کچھ نہ کچھ دے کر رخصت کیا جائے یہ حق ہے متقی لوگوں پر');
INSERT INTO "ur_maududi" VALUES(2,242,'2:242','اِس طرح اللہ اپنے احکام تمہیں صاف صاف بتاتا ہے امید ہے کہ تم سمجھ بوجھ کر کام کرو گے');
INSERT INTO "ur_maududi" VALUES(2,243,'2:243',' تم نے ان لوگوں کے حال پر بھی کچھ غور کیا، جو موت کے ڈر سے اپنے گھر بار چھوڑکر نکلے تھے اور ہزاروں کی تعداد میں تھے؟ اللہ نے ان سے فرمایا: مر جاؤ۔ پھر اس نےا ن کو دوبارہ زندگی بخشی۔ حقیقت یہ ہے کہ اللہ انسان پر بڑا فضل فرمانے والا ہے، مگر اکثر لوگ شکر ادا نہیں کرتے');
INSERT INTO "ur_maududi" VALUES(2,244,'2:244','مسلمانو! اللہ کی راہ میں جنگ کرو اور خوب جان رکھو کہ اللہ سننے والا اور جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(2,245,'2:245','تم میں کون ہے جو اللہ کو قرض ِ حَسَن دے تاکہ اللہ اسے کئی گنا بڑھا چڑھا کر واپس کرے؟ گھٹانا بھی اللہ کے اختیار میں ہے اور بڑھا نا بھی، اور اسی کی طرف تمہیں پلٹ کر جانا ہے');
INSERT INTO "ur_maududi" VALUES(2,246,'2:246','پھر تم نے اس معاملے پر بھی غور کیا، جو موسیٰ ؑ کے بعد سردار ان بنی اسرائیل کو پیش آیا تھا؟ انہوں نے اپنے نبی سے کہا: ہمارے لیے ایک بادشاہ مقرر کر دو تاکہ ہم اللہ کی راہ میں جنگ کریں۔ نبی نے پوچھا: کہیں ایسا تو نہ ہو گا کہ تم کو لڑائی کا حکم دیا جائے اور پھر تم نہ لڑو۔ وہ کہنےلگے: بھلا یہ کیسے ہو سکتا ہے کہ ہم راہ ِ خدا میں نہ لڑیں، جبکہ ہمیں اپنے گھروں سے نکال دیاگیا اور ہمارے بال بچے ہم سے جُدا کر دیے گئے ہیں مگر جب ان کو جنگ کا حکم دیا گیا ، تو ایک قلیل تعداد کے سوا وہ سب پیٹھ موڑ گئے، اور اللہ ان میں سے ایک ایک ظالم کو جانتا ہے');
INSERT INTO "ur_maududi" VALUES(2,247,'2:247','ان کے نبی نے ان سے کہا کہ اللہ نے طالوت کو تمہارے لیے بادشاہ مقرر کیا ہے۔ یہ سن کر وہ بولے: ”ہم پر بادشاہ بننےکا وہ کیسے حقدار ہو گیا؟ اس کے مقابلے میں بادشاہی کے ہم زیادہ مستحق ہیں۔ وہ تو کوئی بڑا مالدار آدمی نہیں ہے“۔ نبی نے جواب دیا:”اللہ نے تمہارے مقابلے میں اسی کو منتخب کیا ہے اور اس کو دماغ و جسمانی دونوں قسم کی اہلیتیں فراوانی کے ساتھ عطا فرمائی ہیں اور اللہ کو اختیار ہے کہ اپنا ملک جسے چاہے دے، اللہ بڑی وسعت رکھتا ہے او ر سب کچھ اس کےعلم میں ہے۔“');
INSERT INTO "ur_maududi" VALUES(2,248,'2:248','اس کے ساتھ ان کے نبی نے ان کو یہ بھی بتایا کہ”خدا کی طرف سے اس کے بادشاہ مقرر ہونے کی علامت یہ ہے کہ اس کے عہد میں وہ صندوق تمہیں واپس مل جائے گا، جس میں آلِ موسیٰ ؑ اور آلِ ہارونؑ کے چھوڑے ہوئے تبّرکات ہیں، اور جس کو اس وقت فرشتے سنبھالے ہوئے ہیں۔ اگر تم مومن ہو، تو یہ تمہارے لیے بہت بڑی نشانی ہے');
INSERT INTO "ur_maududi" VALUES(2,249,'2:249','ھرجب طالوت لشکر لے کر چلا، تو اس نے کہا:”ایک دریا پر اللہ کی طرف سے تمہاری آزمائش ہونے والی ہے۔ جو اس کا پانی پیے گا، وہ میرا ساتھی نہیں۔ میرا ساتھی صرف وہ ہےجو اس سے پیاس نہ بجھائے ، ہاں ایک آدھ چُلّو کوئی پی لے“مگر ایک گروہ قلیل کے سوا وہ سب اس دریا سے سیراب ہوئے۔ پھر جب طالوت اور اس کے ساتھی مسلمان دریا پار کرکے آگے بڑھے، تو انہوں نے طالوت سے کہہ دیا کہ آج ہم میں جالوت اور اس کےلشکر وں کا مقابلہ کرنے کی طاقت نہیں ہے۔ لیکن جو لوگ یہ سمجھتے تھے کہ انھیں ایک دن اللہ سےملنا ہے، انھوں نے کہا: ”بار ہا ایسا ہوا ہے کہ ایک قلیل گروہ اللہ کے اذن سے ایک بڑے گروہ پر غالب آگیا ہے۔ اللہ صبر کرنے والوں کا ساتھی ہے');
INSERT INTO "ur_maududi" VALUES(2,250,'2:250','اور جب وہ جالوت اور اس کے لشکروں کے مقابلہ پر نکلے، تو انہوں نے دعا کی: "اے ہمارے رب ہم پر صبر کا فیضان کر، ہمارے قدم جما دے اور اس کافر گروہ پر ہمیں فتح نصیب کر"');
INSERT INTO "ur_maududi" VALUES(2,251,'2:251','آ خر کا ر اللہ کےاذن سے انھوں نے کافروں کومار بھگایا اور داوٴد ؑ نے جالوت کو قتل کردیا اور اللہ نے اسے سلطنت اور حکمت سے نوازا اور جِن جِن چیزوں کا چاہا، اس کو علم دیا۔۔۔۔ اگر اس طرح اللہ انسانوں کے ایک گروہ کو دوسرے گروہ کے ذریعے سے ہٹاتا نہ رہتا، تو زمین کا نظام بگڑ جاتا، لیکن دنیا کے لوگوں پر اللہ کا بڑا فضل ہے(کہ وہ اس طرح دفع فساد کا انتظام کرتا رہتا ہے)');
INSERT INTO "ur_maududi" VALUES(2,252,'2:252','یہ اللہ کی آیات ہیں، جو ہم ٹھیک ٹھیک تم کو سنا رہے ہیں اور تم یقیناً ان لوگوں میں سے ہو، جو رسول بنا کر بھیجے گئے ہیں');
INSERT INTO "ur_maududi" VALUES(2,253,'2:253','یہ رسول (جو ہماری طرف سے انسانوں کی ہدایت پر مامور ہوئے)ہم نے ان کو ایک دوسرے سے بڑھ چڑھ کر مرتبے عطا کیے۔ ان میں کوئی ایسا تھا جس سے خدا خود ہمکلام ہوا، کسی کو اس نے دوسری حیثیتوں سے بلند درجے دیے، اور آخر میں عیسٰی ؑ ابن مریم کو روشن نشانیاں عطا کیں اور روحِ پاک سے اس کی مدد کی۔ اگر اللہ چاہتا ، تو ممکن نہ تھا کہ ان رسولوں کے بعد جو لوگ روشن نشانیاں دیکھ چکے تھے، وہ آپس میں لڑتے۔ مگر( اللہ کی مشیّت یہ نہ تھی کہ وہ لوگوں کو جبّراً اختلاف سے روکے، اس وجہ سے) انہوں نے باہم اختلاف کیا، پھر کوئی ایمان لایا اور کسی نےکفر کی راہ اختیار کی۔ ہاں اللہ چاہتا، تو وہ ہر گز نہ لڑتے، مگر اللہ جو چاہتا ہے کرتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,254,'2:254','اے لوگوں جو ایمان لائے ہو، جو کچھ مال متاع ہم نے تم کو بخشا ہے، اس میں سے خرچ کرو قبل اس کے کہ وہ دن آئے، جس میں نہ خرید و فروخت ہو گی، نہ دوستی کام آئے گی اور نہ سفارش چلے گی۔ اور ظالم اصل میں وہی ہیں، جو کفر کی روش اختیار کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,255,'2:255','اللہ، وہ زندہ جاوید ہستی ، جو تمام کائنات کو سنبھالے ہوئے ہے، اس کے سوا کوئی خدا نہیں ہے۔ نہ وہ سوتا ہے ، اور نہ اسے اونگھ لگتی ہے۔ زمین اور آسمانوں میں جو کچھ ہے، اسی کا ہے۔ ، کون ہے جو اس کی جناب میں اس کی اجازت کے بغیر سفارش کر سکے؟ جو کچھ بندوں کے سامنے ہے اسے بھی وہ جانتا ہے او ر جو کچھ ان سے اوجھل ہے، اس سے بھی وہ واقف ہے اور اس کی معلومات میں سے کوئی چیز ان کی گرفت ادراک میں نہیں آسکتی اِلّا یہ کہ کسی چیز کا علم وہ خود ہی ان کو دینا چاہے۔ اس کی حکومت آسمانوں اور زمین پر چھائی ہوئی ہے اور ان کی نگہبانی اس کے لیے کوئی تھکا دینے والا کام نہیں ہے۔ بس وہی ایک بزرگ و برتر ذات ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,256,'2:256','دین کے معاملے میں کوئی زورزبردستی نہیں ہے۔ صحیح بات غلط خیالات سے الگ چھانٹ کر رکھ دی گئی ہے۔ اب جو کوئی طاغوت کا انکار کر کے اللہ پر ایمان لے آیا، اس نے ایک ایسا مضبوط سہارا تھام لیا، جو کبھی ٹوٹنے والا نہیں، اور اللہ( جس کا سہارا اس نے لیا ہے) سب کچھ سننے اور جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(2,257,'2:257','جو لوگ ایمان لاتے ہیں، ان کا حامی و مدد گار اللہ ہے اور وہ ان کو تاریکیوں سے روشنی میں نکال لاتا ہے۔ اور جو لوگ کفر کی راہ اختیار کرتے ہیں، ان کےحامی ومدد گار طاغوت ہیں اور وہ انھیں روشنی سے تاریکیوں کی طرف کھینچ لے جاتے ہیں۔ یہ آگ میں جانے والے لوگ ہیں، جہاں یہ ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(2,258,'2:258',' کیا تم نے اس شخص کے حال پر غور نہیں کیا ، جس نے ابراہیم ؑ سے جھگڑا کیا تھا؟ جھگڑا اس بات پر کہ ابراہیمؑ کا رب کون ہے، اور اس بنا پر کہ اس شخص کو اللہ نے حکومت دے رکھی تھی۔ جب ابراہیم ؑ نے کہا کہ”میرا رب وہ ہے، جس کے اختیار میں زندگی اور موت ہے،”تو اس نے جواب دیا:”زندگی اور موت میرے اختیار میں ہے۔” ابراہیم ؑ نےکہا :”اچھا، اللہ سورج کو مشرق سے نکالتا ہے، تو ذرا اسے مغرب سے نکال لا۔”یہ سن کر وہ منکر ِ حق ششدر رہ گیا، مگراللہ ظالموں کو راہ راست نہیں دکھایا کرتا');
INSERT INTO "ur_maududi" VALUES(2,259,'2:259','یا پھر مثال کے طور پر اس شخص کو د یکھو، جس کا گزر ایک ایسی بستی پر ہوا، جو اپنی چھتیوں پر اوندھی گری پڑی تھی۔ اس نے کہا:”یہ آبادی، جو ہلاک ہو چکی ہے، اسےاللہ کس طرح دو بارہ زندگی بخشے گا؟” اس پر اللہ نےاس کی رُوح قبض کر لی اور وہ سو برس تک مردہ پڑا رہا۔ پھر اللہ نے اسے دوبارہ زندگی بخشی اور اس سے پوچھا:”بتاؤ ، کتنی مدّت پڑے رہے ہو؟”اس نے کہا:”ایک دن یا چند گھنٹے رہاہوں گا۔”فرمایا:”تم پر سو برس اسی حالت میں گزر چکے ہیں۔ اب ذرا اپنے کھانے اور پانی کو دیکھو کہ اس میں ذرا تغیّر نہیں آیا ہے۔ دوسری طرف ذرا اپنے گدھے کو بھی دیکھو(کہ اسکا پنجر تک بوسیدہ ہورہا ہے )۔ اور یہ ہم نے اس لیے کیا ہے کہ ہم تمہیں لوگوں کےلیے ایک نشانی بنا دینا چاہتے ہیں ۔پھر دیکھو کے ہڈیوں کے اس پنجر کو ہم کس طرح اٹھا کر گوشت پوست اس پر چڑھاتے ہیں۔”اس طرح جب حقیقت اس کے سامنے بالکل نمایا ں ہو گئی ، تو اس نے کہا”میں جانتا ہو ں کہ اللہ ہر چیز پر قدرت رکھتا ہے۔”');
INSERT INTO "ur_maududi" VALUES(2,260,'2:260','اور وہ واقعہ بھی پیش نظر رہے، جب ابراہیم ؑ نے کہا تھا کہ”میرے مالک ! مجھے دکھا دے تُو مردوں کو کیسے زندہ کرتا ہے۔ فرمایا: ”کیا تو ایمان نہیں رکھتا؟”اس نے عرض کیا”ایمان تو رکھتاہوں ، مگر دل کا اطمینان درکار ہے۔” فرمایا:”اچھا ، تو چار پرندے لے اورا ن کو اپنے سے مانوس کر لے۔ پھر ان کا ایک ایک جز ایک ایک پہاڑ پر رکھ دے۔ پھر ان کو پکار ، وہ تیرے پاس دوڑے چلے آئیں گے۔ خوب جان لو کہ اللہ نہایت بااقتدار اور حکیم ہے۔” ');
INSERT INTO "ur_maududi" VALUES(2,261,'2:261',' جو لوگ اپنے مال اللہ کی راہ میں صرف کرتے ہیں ، ان کے خرچ کی مثال ایسی ہے، جیسے ایک دانا بویا جائے اور اس کے سات بالیں نکلیں اور ہر بال میں سو دانے ہوں۔ اسی طرح اللہ جس کے عمل کو چاہتا ہے، افزونی عطا فرماتا ہے۔ وہ فراخ دست بھی ہے اور علیم بھی۔ ');
INSERT INTO "ur_maududi" VALUES(2,262,'2:262','جو لوگ اپنے مال اللہ کی راہ میں خرچ کرتے ہیں اور خرچ کرکے پھر احسان نہیں جتاتے، نہ دکھ دیتے ہیں ، ان کا اجر ان کے رب کے پاس ہے اور ان کے لیے کسی رنج اور خوف کا موقع نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,263,'2:263','ایک میٹھا بول اور کسی ناگوار بات پر ذرا سی چشم پوشی اس خیرات سے بہتر ہے، جس کے پیچھےدکھ ہو۔ اللہ بے نیاز ہے اور برد باری اس کی صفت ہے');
INSERT INTO "ur_maududi" VALUES(2,264,'2:264','اے ایمان لانے والو!اپنے صدقات کو احسان جتاکر اور دکھ دے کر اس شخص کی طرح خاک میں نہ ملا دو ، جو اپنا مال محض لوگوں کے دکھانے کو خرچ کرتا ہےاور نہ اللہ پر ایمان رکھتا ہے، نہ آخرت پر۔ اس کے خرچ کی مثال ایسی ہے، جیسے ایک چٹان تھی، جس پر مٹی کی تہہ جمی ہوئی تھی۔ اس پر جب زور کا مینہ برسا، تو ساری مٹی بہہ گئ اور صاف چٹان کی چٹان رہ گئی۔ ایسے لوگ اپنے نزدیک خیرات کرکے جو نیکی کماتے ہیں، اس سے کچھ بھی ان کے ہاتھ نہیں آتا، اور کافروں کو سیدھی راہ دکھا نا اللہ کا دستور نہیں ہے ');
INSERT INTO "ur_maududi" VALUES(2,265,'2:265','بخلاف اس کے جو لوگ اپنے مال محض اللہ کی رضا جوئی کے لیے دل کے پورے ثبات و قرار کے ساتھ خرچ کرتے ہیں، ان کےخرچ کی مثال ایسی ہے، جیسے کسی سطح مرتفع پر ایک باغ ہو۔ اگر زور کی بارش ہو جائے تو دوگنا پھل لائے، اور اگر زور کی بارش نہ بھی ہو تو ایک ہلکی پھوارہی اس کے لیے کافی ہو جائے۔ تم جو کچھ کرتے ہو ، سب اللہ کی نظر میں ہے');
INSERT INTO "ur_maududi" VALUES(2,266,'2:266','کیا تم میں سے کوئی یہ پسند کرتا ہے کہ اس کے پاس ایک ہرا بھرا باغ ہو، نہروں سے سیراب، کھجوروں اور انگوروں اور ہر قسم کے پھلوں سے لدا ہوا ہو، اور وہ عین اس وقت ایک تیز بھگولے کی زد میں آکر جھلس جائے، جبکہ وہ خود بوڑھا ہو اور اس کے کمسن بچے ابھی کسی لائق نہ ہوں؟ اس طرح اللہ اپنی باتیں تمہارے سامنے بیان کرتا ہے، شاید کہ تم غور وفکر کرو');
INSERT INTO "ur_maududi" VALUES(2,267,'2:267','اے لوگو جو ایمان لائے ہو، جو مال تم نے کمائے ہیں اور جو کچھ ہم نے زمین سے تمہارے لیے نکالا ہے، اس میں سے بہتر حصہ راہ خدا میں خرچ کرو۔ ایسا نہ ہو کہ اس کی راہ میں دینے کےلیے بری سے بری چیز چھانٹنے کی کوشسش کرنے لگو، حالانکہ وہی چیز اگر کوئی تمہیں دے ، تو تم ہرگز اسے لینا گوارا نہ کرو گےاِلّا یہ کہ اس کو قبول کرنے میں تم اغماض برت جاؤ۔تمہیں جان لینا چاہیے کہ اللہ بے نیاز ہے اور بہترین صفات سے متصف ہے ');
INSERT INTO "ur_maududi" VALUES(2,268,'2:268','شیطان تمہیں مفلسی سے ڈراتا ہے اور شرمناک طرز عمل اختیار کرنے کی ترغیب دیتا ہے، مگر اللہ تمہیں اپنی بخشش اور فضل کی امید دلاتا ہے اللہ بڑا فراخ دست اور دانا ہے');
INSERT INTO "ur_maududi" VALUES(2,269,'2:269','جس کو چاہتا ہے حکمت عطا کرتا ہے، اور جس کو حکمت ملی ، اُسے حقیقت میں بڑی دولت مل گئی۔ اِن باتوں سے صرف وہی لوگ سبق لیتے ہیں ،جو دانشمند ہیں');
INSERT INTO "ur_maududi" VALUES(2,270,'2:270','تم نے جو کچھ بھی خرچ کیا ہو اور جو نظر بھی مانی ہو ، اللہ کو اُس کا علم ہے، اور ظالموں کا کوئی مدد گار نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,271,'2:271','اگر اپنے صدقات اعلانیہ دو، تو یہ بھی اچھا ہے، لیکن اگر چھپا کر حاجت مندوں کودو، تو یہ تمہارے حق میں زیادہ بہتر ہے۔ تمہاری بہت سی بُرائیاں اِس طرزِ عمل سے محوہوجاتی ہیں۔ اور جو کچھ تم کرتے ہو اللہ کو بہر حال اُس کی خبر ہے');
INSERT INTO "ur_maududi" VALUES(2,272,'2:272','لوگوں کو ہدایت بخش دینے کی ذمّےداری تم پر نہیں ہے۔ ہدایت تو اللہ ہی جسے چاہتا ہے بخشتا ہے۔ اور خیرات میں جو مال تم خرچ کرتے ہو وہ تمہارے اپنے لیے بھلاہے۔ آخر تم اسی لیے تو خرچ کرتے ہو کہ اللہ کی رضا حاصل ہو۔ تو جو کچھ مال تم خیرات کروگے، اس کا پُورا پُورا اجر تمہیں دیا جائے گا اور تمہاری حق تلفی ہرگز نہ ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(2,273,'2:273','خاص طور پر مدد کے مستحق وہ تنگ دست لوگ ہیں جو اللہ کے کام میں ایسے گھِر گئے ہیں کہ اپنی ذاتی کسبِ معاش کے لیے زمین میں کوئی دَوڑ دھوپ نہیں کر سکتے۔ان کی خودداری دیکھ کرنا واقف آدمی گمان کرتا ہے کہ یہ خوش حال ہیں۔ تم ان کے چہروں سے ان کی اندرونی حالت پہچان سکتے ہو۔ مگر وہ ایسے لوگ نہیں ہیں کہ لوگوں کے پیچھے پڑکر کچھ مانگیں۔ اُن کی اعانت میں جو کچھ مال تم خرچ کروگےوہ اللہ سے پوشیدہ نہ رہےگا۔ ');
INSERT INTO "ur_maududi" VALUES(2,274,'2:274','جو لو گ اپنے مال شب و روز کھلے اور چھپے خرچ کرتے ہیں ان کا اجر ان کے رب کے پاس ہے اور اُن کے لیے کسی خوف اور رنج کا مقام نہیں');
INSERT INTO "ur_maududi" VALUES(2,275,'2:275','مگر جو لوگ سُود کھاتے ہیں ، اُن کا حال اُس شخص کاسا ہوتا ہے، جسے شیطان نے چُھوکر باوٴلا کر دیا ہو۔ اور اس حالت میں اُن کے مبتلا ہونے کی وجہ یہ ہے کہ وہ کہتے ہیں:”تجارت بھی تو آخر سُود ہی جیسی ہے “، حالانکہ اللہ نے تجارت کو حلال کیا ہے اور سُود کو حرام۔ لہٰذا جس شخص کو اس کے ربّ کی طرف سے یہ نصیحت پہنچے اور آئندہ کے لیے وہ سُود خوری سے باز آجائے، تو جو کچھ وہ پہلے کھا چکا ، اس کا معاملہ اللہ کے حوالے ہے۔ اور جو اس حکم کے بعد پھر اسی حرکت کا اعادہ کرے، وہ جہنمی ہے، جہاں وہ ہمیشہ رہے گا');
INSERT INTO "ur_maududi" VALUES(2,276,'2:276','اللہ سُود کا مَٹھ ماردیتا ہے اور صدقات کو نشونما دیتا ہے۔ اور اللہ کِسی ناشکرے بد عمل انسان کو پسند نہیں کرتا ');
INSERT INTO "ur_maududi" VALUES(2,277,'2:277','ہاں، جو لوگ ایمان لے آئیں اور نیک عمل کریں اور نماز قائم کریں اور زکوٰة دیں، اُن کا اجر بے شک ان کے ربّ کے پاس ہے اور ان کےلیے کسی خوف اور رنج کا موقع نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(2,278,'2:278','اے لوگو جو ایمان لائے ہو، خدا سے ڈرو اور جو کچھ تمہارا سود لوگوں پر باقی رہ گیا ہے، اسے چھوڑ دو، اگر واقعی تم ایمان لائے ہو');
INSERT INTO "ur_maududi" VALUES(2,279,'2:279','لیکن اگر تم نے ایسا نہ کیا ، تو آگاہ ہو جاوٴ کہ اللہ اور اُس کے رسول ؐ کی طرف سے تمہارے خلاف اعلانِ جنگ ہے۔ اب بھی توبہ کرلو (اور سُود چھوڑ دو)تو اپنا اصل سرمایہ لینے کے تم حق دار ہو۔ نہ تم ظلم کرو ، نہ تم پر ظلم کیا جائے');
INSERT INTO "ur_maududi" VALUES(2,280,'2:280','تمہارا قرض دار تنگ دست ہو تو ہاتھ کھُلنے تک اُسے مہلت دو، اور جو صد قہ کردو ، تو یہ تمہارے لیے زیادہ بہتر ہے، اگر تم سمجھو ');
INSERT INTO "ur_maududi" VALUES(2,281,'2:281','اس دن کی رسوائی و مصیبت سے بچو، جبکہ تم اللہ کی طرف واپس ہو گے، وہاں ہر شخص کو اس کی کمائی ہوئی نیکی یا بدی کا پورا پورا بدلہ مل جائے گا اور کسی پر ظلم ہرگز نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(2,282,'2:282','اے لوگو جو ایمان لائےہو، جب کسی مقرّر مدت کےلیے تم آپس میں لین دین کرو ، تو اُسے لکھ لیا کرو۔فریقین کے درمیان انصاف کے ساتھ ایک شخص دستاویز تحریر کرے۔ جسے اللہ نے لکھنے پڑھنے کی قابلیت بخشی ہو، اُسے لکھنے سے انکار نہ کرنا چاہیے۔وہ لکھے اور املا وہ شخص کرائےجس پر حق آتا ہے(یعنی قرض لینے والا)، اور اُسے اللہ ،اپنے ربّ سے ڈرنا چاہیےکہ جو معاملہ طے ہوا ہو اس میں کوئی کمی بیشی نہ کرے۔ لیکن اگر قرض لینے والا خود نادان یا ضعیف ہو ، یا املا نہ کراسکتا ہو، تو اس کا ولی انصاف کے ساتھ املا کرائے۔ پھر اپنے مردوں میں سے دو آدمیوں کی اس پر گواہی کرالو۔ اور اگر دو مرد نہ ہوں تو ایک مرد اور دو عورتیں ہوں تاکہ ایک بھول جائے، تو دوسری اُسے یاد دلائے۔ یہ گواہ ایسے لوگوں میں سے ہونے چاہییں، جن کی گواہی تمہارے درمیان مقبول ہو۔ گواہوں کو جب گواہ بننے کے لیے کہا جائے ، تو انہیں انکار نہ کرنا چاہیے۔ معاملہ خواہ چھوٹا ہو یا بڑا ، میعاد کی تعیین کے ساتھ اس کی دستاویز لکھوا لینے میں تسا ہل نہ کرو۔ اللہ کے نزدیک یہ طریقہ تمہارے لیے زیادہ مبنی بر انصاف ہے، اس سے شہادت قائم ہونے میں زیادہ سہولت ہوتی ہے۔ اور تمہارے شکوک وشبہات میں مبتلا ہونے کا امکان کم رہ جاتا ہے۔ ہاں جو تجارتی لین دین دست بدست تم لوگ آپس میں کرتے ہو، اس کو نہ لکھا جائے تو کوئی حرج نہیں ، مگر تجارتی معاملے طے کرتے وقت گواہ کر لیا کرو۔ کاتب اور گواہ کو ستایا نہ جائے ۔ ایسا کروگے ، تو گناہ کا ارتکاب کرو گے۔ اللہ کے غضب سے بچو۔ وہ تم کو صحیح طریقِ عمل کی تعلیم دیتا ہے اور اسے ہر چیز کا علم ہے');
INSERT INTO "ur_maududi" VALUES(2,283,'2:283','اگر تم سفر کی حالت میں ہو اور دستاویز لکھنے کےلیے کوئی کاتب نہ ملے ، رہن بالقبض پر معاملہ کرو۔ اگر تم میں سے کوئی شخص دُوسرے پر بھروسہ کرکے اس کے ساتھ کوئی معاملہ کرے، تو جس پر بھروسہ کیا گیا ہے ، اسے چاہیے کہ امانت ادا کرے اور اللہ ، اپنے ربّ سے ڈرے۔ اور شہادت ہرگز نہ چھپاوٴ۔ جو شہادت چھپاتاہے ، اس کا دل گناہ میں آلودہ ہے۔ اور اللہ تمہارے اعمال سے بے خبر نہیں ہے');
INSERT INTO "ur_maududi" VALUES(2,284,'2:284',' آسمانوں اور زمین میں جو کچھ ہے ، سب اللہ کاہے۔ تم اپنے دل کی باتیں خواہ ظاہر کرو یا چُھپاوٴ اللہ بہرحال ان کا حساب تم سے لے لے گا۔ پھر اسے اختیار ہے جسے چاہے ، معاف کردے اور جسے چاہے، سزا دے۔ وہ ہر چیز پر قدرت رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(2,285,'2:285','رسُول اُس ہدایت پر ایمان لایا ہے جو اس کے ربّ کی طرف سے اس پر نازل ہوئی ہے۔ اور جو لوگ اِس رسُول کے ماننے والے ہیں، انہوں نے بھی اس ہدایت کو دل سے تسلیم کر لیا ہے۔ یہ سب اللہ اور اس کے فرشتوں اور اس کی کتابوں اور اس کے رسولوں کو مانتے ہیں اور ان کا قول یہ ہے کہ ”ہم اللہ کے رسولوں کو ایک دوسرے سے الگ نہیں کرتے ، ہم نے حکم سُنا اور اطاعت قبول کی۔ مالک !ہم تجھ سے خطابخشی کے طالب ہیں اور ہمیں تیری ہی طرف پلٹنا ہے۔ “');
INSERT INTO "ur_maududi" VALUES(2,286,'2:286','اللہ کسی متنفّس پر اُس کی مقدرت سے بڑھ کر ذمّہ داری کا بوجھ نہیں ڈالتا۔ ہر شخص نے جو نیکی کمائی ہے، اس کا پھل اسی کے لیے ہے اور جو بدی سمیٹی ہے، اس کا وبال اسی پر ہے۔ (ایمان لانے والو! تم یوں دُعا کرو ) اے ہمارے ربّ ! ہم سے بھول چُوک میں جو قصور ہو جائیں،ان پر گرفت نہ کرنا۔ مالک ! ہم پر وہ بوجھ نہ ڈال ، جو تو نے ہم سے پہلے لوگوں پر ڈالے تھے۔ پروردگار !جس بار کو اُٹھانے کی ہم میں طاقت نہیں ہے ، وہ ہم پر نہ رکھ۔ ہمارے ساتھ نرمی کر ، ہم سے درگزر فرما، ہم پر رحم کر، تُو ہمارا مولیٰ ہے، کافروں کے مقابلے میں ہماری مدد کر۔ ');
INSERT INTO "ur_maududi" VALUES(3,1,'3:1','الف لام میم');
INSERT INTO "ur_maududi" VALUES(3,2,'3:2','اللہ، وہ زندہ جاوید ہستی ، جو نظامِ کائنات کو سنبھالے ہوئے ہے، حقیقت میں اُس کے سوا کوئی خدا نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,3,'3:3','اُس نے تم پر یہ کتاب نازل کی ، جو حق لے کر آئی ہے اور اُن کتابوں کی تصدیق کر رہی ہے جو پہلے سے آئی ہوئی تھیں۔ اس سے پہلے وہ انسانوں کی ہدایت کے لیے تو رات اور انجیل نازل کر چکا ہے، ');
INSERT INTO "ur_maududi" VALUES(3,4,'3:4','اور اس نے وہ کسوٹی اتاری ہے (جو حق اور باطل کا فرق د کھانے والی ہے) اب جو لوگ اللہ کے فرامین کو قبول کرنے سے انکار کریں، ان کو یقیناً سخت سزا ملے گی اللہ بے پناہ طاقت کا مالک ہے اور برائی کا بدلہ دینے والا ہے');
INSERT INTO "ur_maududi" VALUES(3,5,'3:5','زمین اور آسمان کی کوئی چیز اللہ سے پوشیدہ نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(3,6,'3:6','وہی تو ہے جو تمہاری ماؤں کے پیٹ میں تمہاری صورتیں، جیسی چاہتا ہے، بناتا ہے۔ اُس زبر دست حکمت والے کے سوا کوئی اور خدا نہیں ہے');
INSERT INTO "ur_maududi" VALUES(3,7,'3:7','وہی خدا ہے، جس نے یہ کتاب تم پر نازل کی ہے۔ اس کتاب میں دو طرح کی آیات ہیں: ایک محکمات، جو کتاب کی اصل بنیاد ہیں اور دُوسری متشابہات ۔ جن لوگوں کو دلوں میں ٹیڑھ ہے، وہ فتنے کی تلاش میں ہمیشہ متشابہات ہی کے پیچھے پڑے رہتے ہیں اور اُن کو معنی پہنانے کی کو شش کیا کرتے ہیں، حالانکہ ان کا حقیقی مفہوم اللہ کے سوا کوئی نہیں جانتا۔ بخلااِ س کے جو لوگ علم میں پختہ کار ہیں، وہ کہتے ہیں کہ ”ہمارا اُن پر ایمان ہے، یہ سب ہمارے رب ہی کی طرف سے ہیں۔ “ اور سچ یہ ہے کہ کسی چیز سے صحیح سبق صرف دانشمند لوگ ہی حاصل کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(3,8,'3:8','وہ اللہ سے دعا کرتے رہتے ہیں کہ: "پروردگار! جب تو ہمیں سیدھے رستہ پر لگا چکا ہے، تو پھر کہیں ہمارے دلوں کو کجی میں مبتلا نہ کر دیجیو ہمیں اپنے خزانہ فیض سے رحمت عطا کر کہ تو ہی فیاض حقیقی ہے');
INSERT INTO "ur_maududi" VALUES(3,9,'3:9','پروردگار! تو یقیناً سب لوگوں کو ایک روز جمع کرنے والا ہے، جس کے آنے میں کوئی شبہ نہیں تو ہرگز اپنے وعدے سے ٹلنے والا نہیں ہے"');
INSERT INTO "ur_maududi" VALUES(3,10,'3:10','جن لوگو ں نے کفر کا رویّہ اختیار کیا ہے، انہیں اللہ کے مقابلے میں نہ اُن کا مال کچھ کام دے گا، نہ اولاد۔ وہ دوزخ کا ایندھن بن کر رہیں گے');
INSERT INTO "ur_maududi" VALUES(3,11,'3:11','اُن کا انجام ویسا ہی ہوگا، جیسا فرعون کے ساتھیوں اور اُن سے پہلے کے نافرمانوں کا ہو چکا ہے کہ اُنہوں نے آیات الٰہی کو جھٹلایا، نتیجہ یہ ہوا کہ اللہ نے ان کے گناہوں پر انہیں پکڑ لیا اور حق یہ ہے کہ اللہ سخت سزا دینے والا ہے');
INSERT INTO "ur_maududi" VALUES(3,12,'3:12','پس اے محمدؐ! جن لوگوں نے تمہاری دعوت کو قبول کرنے سے انکار کر دیا ہے، اُن سے کہہ دو کہ قریب ہے وہ وقت، جب تم مغلوب ہو جاؤ گے اور جہنم کی طرف ہانکے جاؤ گے اور جہنم بڑا ہی برا ٹھکانا ہے');
INSERT INTO "ur_maududi" VALUES(3,13,'3:13','تمہارے لیے اُن دو گروہوں میں ایک نشان عبرت تھا، جو ( بدر میں) ایک دوسرے سے نبرد آزما ہو ئے۔ ایک گروہ اللہ کی راہ میں لڑ رہا تھا اور دُوسرا گروہ کافر تھا۔ دیکھنے والے بچشمِ سر دیکھ رہے تھے کہ کافر گروہ مومن گروہ سے دو چند ہے۔ مگر (نتیجے نے ثابت کردیا کہ) اللہ اپنی فتح و نصرت سے جس کی چاہتا ہے، مدد کر دیتا ہے۔ دیدۂ بینا رکھنے والوں کے لیے اس میں بڑا سبق پوشیدہ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,14,'3:14','لوگوں کے لیے مرغوبات نفس، عورتیں، اولا د، سونے چاندی کے ڈھیر، چیدہ گھوڑے، مویشی او ر زرعی زمینیں بڑی خوش آئند بنا دی گئی ہیں، مگر یہ سب دنیا کی چند روزہ زندگی کے سامان ہیں حقیقت میں جو بہتر ٹھکانا ہے، وہ تو اللہ کے پاس ہے');
INSERT INTO "ur_maududi" VALUES(3,15,'3:15','کہو: میں تمھیں بتاؤں کہ ان سے زیادہ اچھی چیز کیا ہے؟ جو لوگ تقویٰ کی روش اختیار کریں، اُن کے لیے ان کے رب کے پاس باغ ہیں، جن کے نیچے نہریں بہتی ہوں گی، وہاں انہیں ہمیشگی کی زندگی حاصل ہو گی، پاکیزہ بیویاں ان کی رفیق ہوں گی اور اللہ کی رضا سے وہ سرفراز ہوں گے۔ اللہ اپنے بندوں کے رویّے پر گہری نظر رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,16,'3:16','یہ وہ لوگ ہیں، جو کہتے ہیں کہ "مالک! ہم ایمان لائے، ہماری خطاؤں سے در گزر فرما اور ہمیں آتش دوزخ سے بچا لے"');
INSERT INTO "ur_maududi" VALUES(3,17,'3:17','یہ لوگ صبر کرنے والے ہیں، راستباز ہیں، فرمانبردار اور فیاض ہیں اور رات کی آخری گھڑیوں میں اللہ سے مغفرت کی دعائیں مانگا کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(3,18,'3:18','اللہ نے خود اس بات کی شہادت دی ہے کہ اس کے سوا کوئی خدا نہیں ہے اور (یہی شہادت) فرشتوں اور سب اہلِ علم نے بھی دی ہے۔ وہ انصاف پر قائم ہے۔ اُس زبر دست حکیم کے سوا فی الواقع کوئی خدا نہیں ہے');
INSERT INTO "ur_maududi" VALUES(3,19,'3:19','اللہ کے نزدیک دین صرف اسلام ہے۔ اس دین سے ہٹ کر جو مختلف طریقے اُن لوگوں نے اختیار کیے، جنہیں کتاب دی گئی تھی، اُن کے اِس طرز عمل کی کوئی وجہ اس کے سوا نہ تھی کہ انہوں نے علم آ جانے کے بعد آپس میں ایک دُوسرے پر زیادتی کرنے کے لیے ایسا کیا اور جو کو ئی اللہ کے احکام و ہدایات کی اطاعت سے انکار کر دے، اللہ کو اس سے حساب لیتے کچھ دیر نہیں لگتی');
INSERT INTO "ur_maududi" VALUES(3,20,'3:20','اب اگر یہ لوگ تم سے جھگڑا کریں، تو ان سے کہو : ”میں نے اور میرے پیرووں نے تو اللہ کے آگے سرتسلیم خم کر دیا ہے۔“ پھر اہل کتاب اور غیر اہلِ کتاب دونوں سے پوچھو: ”کیا تم نے بھی اس کی اطاعت و بندگی قبول کی؟“ اگر کی تو وہ راہ ِ راست پاگئے ، اور اگر اس سے منہ موڑا تو تم پر صرف پیغام پہنچا دینے کی ذمہ داری تھی۔ آگے اللہ خود اپنے بندوں کے معاملات دیکھنے والا ہے');
INSERT INTO "ur_maududi" VALUES(3,21,'3:21','جو لوگ اللہ کے احکام و ہدایت کو ماننے سے انکار کرتے ہیں اور اس کے پیغمبروں کو نا حق قتل کرتے ہیں اور ایسے لوگو ں کی جان کے درپے ہو جا تے ہیں، جو خلقِ خدا میں سے عدل و راستی کا حکم دینے کے لیے اُٹھیں، ان کو درد ناک سزا کی خوشخبری سُنا دو۔ ');
INSERT INTO "ur_maududi" VALUES(3,22,'3:22','یہ وہ لوگ ہیں جن کے اعمال دُنیا اور آخرت دونوں میں ضائع ہو گئے، اور ان کا مددگار کوئی نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,23,'3:23','تم نے دیکھا نہیں کہ جن لوگوں کو کتاب کے علم میں سے کچھ حصّہ ملا ہے، اُن کا حال کیا ہے؟ اُنہیں جب کتابِ الٰہی کی طرف بُلایا جاتا ہے تاکہ وہ اُن کے درمیان فیصلہ کرے، تو ان میں سے ایک فریق اس سے پہلو تہی کرتا ہے اور اس فیصلے کی طرف آنے سے منہ پھیر جاتا ہے');
INSERT INTO "ur_maududi" VALUES(3,24,'3:24','ان کا یہ طرز عمل اس وجہ سے ہے کہ وہ کہتے ہیں ”آتش ِ دوزخ تو ہمیں مَس تک نہ کرے گی اور اگر دوزخ کی سزا ہم کو ملے گی بھی تو بس چند روز“۔ اُن کے خود ساختہ عقیدوں نے اُن کو اپنے دین کے معاملے میں بڑی غلط فہمیوں میں ڈال رکھا ہے');
INSERT INTO "ur_maududi" VALUES(3,25,'3:25','مگر کیا بنے گی اُن پر جب ہم انہیں اُس روز جمع کریں گے جس کا آنا یقینی ہے؟ اس روز ہر شخص کو اس کی کمائی کا بدلہ پورا پورا دے دیا جائیگا اور کسی پر ظلم نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(3,26,'3:26','کہو! خدایا! مُلک کے مالک! تو جسے چاہے، حکومت دے اور جسے چاہے، چھین لے جسے چاہے، عزت بخشے اور جس کو چاہے، ذلیل کر دے بھلائی تیرے اختیار میں ہے بیشک تو ہر چیز پر قادر ہے');
INSERT INTO "ur_maududi" VALUES(3,27,'3:27','رات کو دن میں پِروتا ہوالے آتا ہے اور دن کو رات میں۔ جاندار میں سے بے جان کو نکالتا ہے اور بے جان میں سے جاندار کو۔ اور جسے چاہتا ہے، بے حساب رزق دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,28,'3:28','مومنین اہل ایمان کو چھوڑکر کافروں کو اپنا رفیق اور دوست ہرگز نہ بنائیں۔ جو ایسا کرے گا اس کا اللہ سے کوئی تعلق نہیں۔ ہاں یہ معاف ہے کہ تم ان کے ظلم سے بچنے کے لیے بظاہر ایسا طرزِ عمل اختیار کر جاؤ۔ مگر اللہ تمہیں اپنے آپ سے ڈراتا ہے اور تمہیں اسی کی طرف پلٹ کر جانا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,29,'3:29','اے نبیؐ! لوگوں کو خبردار کر دو کہ تمہارے دلوں میں جو کچھ ہے، اُسے خواہ تم چھپاؤ یا ظاہر کرو، اللہ بہرحال اسے جانتا ہے، زمین و آسمان کی کوئی چیز اس کے علم سے باہر نہیں ہے اور اُس کا اقتدار ہر چیز پر حاوی ہے');
INSERT INTO "ur_maududi" VALUES(3,30,'3:30','وہ دن آنے والا ہے، جب ہر نفس اپنے کیے کا پھل حاضر پائے گا خواہ اُس نے بھلائی کی ہو یا بُرائی۔ اس روز آدمی یہ تمنا کرے گا کہ کاش ابھی یہ دن اس سے بہت دُور ہوتا! اللہ تمہیں اپنے آپ سے ڈراتا ہے اور وہ اپنے بندوں کا نہایت خیر خواہ ہے ');
INSERT INTO "ur_maududi" VALUES(3,31,'3:31','اے نبیؐ! لوگوں سے کہہ دو کہ، "اگر تم حقیقت میں اللہ سے محبت رکھتے ہو، تو میر ی پیروی اختیار کرو، اللہ تم سے محبت کرے گا اور تمہاری خطاؤں سے درگزر فرمائے گا وہ بڑا معاف کرنے والا اور رحیم ہے"');
INSERT INTO "ur_maududi" VALUES(3,32,'3:32','اُن سے کہو کہ ”اللہ اور رسُول کی اطاعت قبول کر لو“ پھر اگر وہ تمہاری یہ دعوت قبول نہ کریں، تو یقیناً یہ ممکن نہیں ہے کہ اللہ ایسے لوگوں سے محبت کرے، جو اس کی اور اس کے رسُول کی اطاعت سے انکار کرتے ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(3,33,'3:33','اللہ نے آدم ؑ اور نوح ؑ اور آلِ ابراھیم ؑ اور آلِ عمران کو تمام دُنیا والوں پر ترجیح دے کر (اپنی رسالت کے لیے)منتخب کیا تھا');
INSERT INTO "ur_maududi" VALUES(3,34,'3:34','یہ ایک سلسلے کے لوگ تھے، جو ایک دوسرے کی نسل سے پیدا ہوئے تھے۔ اللہ سب کچھ سنتا اور جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,35,'3:35','(وہ اس وقت سُن رہا تھا)جب عمران کی عورت کہہ رہی تھی کہ ”میرے پر ور دگار! میں اس بچے کو جو میرے پیٹ میں ہے تیری نذر کرتی ہوں ، وہ تیرے ہی کام کے لیے وقف ہوگا۔ میری اس پیشکش کو قبو ل فرما۔ تُو سننے اور جاننے والاہے“۔ ');
INSERT INTO "ur_maududi" VALUES(3,36,'3:36','پھر جب وہ بچی اس کے ہاں پیدا ہوئی تو اس نے کہا ”مالک! میرے ہاں تو لڑکی پیدا ہو گئی ہے۔۔۔۔حالانکہ جو کچھ اس نے جنا تھا، اللہ کو اس کی خبر تھی۔۔۔۔اور لڑکا لڑکی کی طرح نہیں ہوتا۔ خیر ، میں نے اس کا نام مریم رکھ دیا ہے اور میں اسے اور اس کی آئندہ نسل کوشیطانِ مردود کے فتنے سے تیری پناہ میں دیتی ہوں“');
INSERT INTO "ur_maududi" VALUES(3,37,'3:37','آخر کار اس کے رب نے اس لڑکی کو بخوشی قبول فرما لیا۔ اُسے بڑی اچھی لڑکی بناکر اُٹھایا۔ اور زکریّا کو اس کا سرپرست بنادیا۔ زکریا جب کبھی اس کے پاس محراب میں جاتا تو اس کے پاس کچھ نہ کچھ کھانے پینے کا سامان پاتا۔ پوچھتا مریم ! یہ تیرے پاس کہا ں سے آیا؟ وہ جواب دیتی اللہ کے پاس سے آیا ہے۔ اللہ جسے چاہتاہے بے حساب دیتا ہے');
INSERT INTO "ur_maududi" VALUES(3,38,'3:38','یہ حال دیکھ کر زکریا نے اپنے رب کو پکارا”پروردگار! اپنی قدرت سے مجھے نیک اولاد عطا کر۔ تُو ہی دُعا سننے والا ہے“۔ ');
INSERT INTO "ur_maududi" VALUES(3,39,'3:39','جواب میں فرشتوں نے آواز دی ، جب کہ وہ محراب میں کھڑا نماز پڑھ رہاتھا، کہ”اللہ تجھے یحی یٰ کی خوشخبری دیتا ہے۔ وہ اللہ کی طرف سے ایک فرمان کی تصدیق کرنے والا بن کر آئے گا۔ اس میں سرداری و بزرگی کی شان ہو گی۔ کمال درجہ کا ضابط ہو گا۔ نبّوت سے سرفراز ہو گا اور صالحین میں شمار کیا جائے گا');
INSERT INTO "ur_maududi" VALUES(3,40,'3:40','زکریّا نے کہا”پروردگار!میرے ہاں لڑکا کہاں سے ہو گا، میں تو بہت بوڑھا ہو چکا ہوں اور میری بیوی بانجھ ہے“۔ جواب ملا”ایسا ہی ہوگا، اللہ جو چاہتا ہےکرتا ہے“');
INSERT INTO "ur_maududi" VALUES(3,41,'3:41','عرض کیا مالک! پھر کوئی نشانی میرے لیے مقرر فرما دے۔کہا” نشانی یہ ہے کہ تم تین دن تک لوگوں سے اشارہ کے سوا کوئی بات چیت نہ کرو گے(یا نہ کر سکو گے)۔ اِس دوران میں اپنے رب کو بہت یاد کرنا اور صبح وشام اس کی تسبیح کرتے رہنا“۔ ');
INSERT INTO "ur_maududi" VALUES(3,42,'3:42','پھر وہ وقت آیا جب مریمؑ سے فرشتوں نے آکر کہا، "اے مریمؑ! اللہ نے تجھے برگزیدہ کیا اور پاکیزگی عطا کی اور تمام دنیا کی عورتوں پر تجھ کو ترجیح دے کر اپنی خدمت کے لیے چن لیا');
INSERT INTO "ur_maududi" VALUES(3,43,'3:43','اے مریمؑ! اپنے رب کی تابع فرمان بن کر رہ، اس کے آگے سر بسجود ہو، اور جو بندے اس کے حضور جھکنے والے ہیں ان کے ساتھ تو بھی جھک جا"');
INSERT INTO "ur_maududi" VALUES(3,44,'3:44','اے محمد ؐ ! یہ غیب کی خبریں ہیں جو ہم تم کو وحی کے ذریعہ سے بتا رہے ہیں، ورنہ تم اُس وقت وہاں موجو د نہ تھے جب ہیکل کے خادم یہ فیصلہ کرنے کے لیے کہ مریم کا سر پرست کون ہو اپنے اپنے قلم پھینک رہے تھے، اور نہ تم اس وقت حاضر تھے جب اُن کے درمیان جھگڑا برپا تھا');
INSERT INTO "ur_maududi" VALUES(3,45,'3:45','اور جب فرشتوں نے کہا، "اے مریمؑ! اللہ تجھے اپنے ایک فرمان کی خوش خبری دیتا ہے اُس کا نام مسیح عیسیٰ ابن مریم ہوگا، دنیا اور آخرت میں معزز ہوگا، اللہ کے مقرب بندوں میں شمار کیا جائے گا');
INSERT INTO "ur_maududi" VALUES(3,46,'3:46','لوگوں سے گہوارے میں بھی کلام کرے گا اور بڑی عمر کو پہنچ کر بھی، اور وہ ایک مرد صالح ہوگا"');
INSERT INTO "ur_maududi" VALUES(3,47,'3:47','یہ سُن کر مریم بولی”پروردگار!میرے ہا ں بچہ کہاں سے ہوگا، مجھے تو کسی شخص نے ہاتھ تک نہیں لگایا“۔ جواب ملا”ایسا ہی ہوگا، اللہ جو چاھتا ہے پیدا کر تا ہے۔ وہ جب کسی کا م کے کرنے کا فیصلہ فرماتا ہے تو بس کہتا ہے کہ ہو جا اور وہ ہو جا تا ہے“');
INSERT INTO "ur_maududi" VALUES(3,48,'3:48','(فرشتوں نے پھر سلسلئہ کلام میں کہا) "اور اللہ اُسے کتاب اور حکمت کی تعلیم دے گا، تورات اور انجیل کا علم سکھائے گا');
INSERT INTO "ur_maududi" VALUES(3,49,'3:49','اور بنی اسرئیل کی طرف اپنا رسول مقرر کرے گا“۔(اور جب وہ بحیثیت رسول بنی اسرائیل کے پاس آیا تو اس نے کہا) ”میں تمہارے رب کی طرف سے تمہارے پاس نشانی لے کر آیا ہوں۔ میں تمہارے سامنے مٹی سے پرندے کی صورت کا ایک مجسمہ بناتا ہوں اور اس میں پھونک مارتا ہوں، وہ اللہ کے حکم سے پرندہ بن جاتا ہے۔ میں اللہ کے حکم سے مادر زاد اندھے اور کوڑھی کو اچھا کرتا ہوں اور مُردے کو زندہ کرتا ہوں۔ میں تمہیں بتاتا ہوں کہ تم کیا کھاتے ہو اور کیا اپنے گھروں میں ذخیرہ کرکے رکھتے ہو۔ اس میں تمہارے لیے کافی نشانی ہے اگر تم ایمان لانے والے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(3,50,'3:50','اور میں اُس تعلیم و ہدایت کی تصدیق کرنے والا بن کر آیا ہوں جو تو رات میں سے اِس وقت میرے زمانہ میں موجود ہے۔ اور اس لیے آیا ہوں کہ تمہارے لیے بعض اُن چیزوں کو حلال کر دوں جو تم پر حرام کر دی گئی ہیں۔ دیکھو ، میں تمہارے رب کی طرف سے تمہارے پاس نشانی لے کر آیا ہوں ، لہٰذا اللہ سے ڈرو اور میری اطاعت کرو');
INSERT INTO "ur_maududi" VALUES(3,51,'3:51','اللہ میرا رب بھی ہے اور تمہارا رب بھی ، لہٰذا تم اُسی کی بندگی اختیار کرو، یہی سیدھا راستہ ہے“۔ ');
INSERT INTO "ur_maududi" VALUES(3,52,'3:52','جب عیسٰی نے محسوس کیا کہ بنی اسرئیل کفر و انکار پر آمادہ ہیں تو اس نے کہا ”کون اللہ کی راہ میں میرا مددگار ہو تا ہے“؟ حواریوں نے جواب دیا”ہم اللہ کے مدد گار ہیں ، ہم اللہ پر ایمان لائے، گواہ رہو کہ ہم مسلم(اللہ کے آگے سرِ اطاعت جھکادینے والے )ہیں');
INSERT INTO "ur_maududi" VALUES(3,53,'3:53','مالک! جو فرمان تو نے نازل کیا ہے ہم نے اسے مان لیا اور رسول کی پیروی قبول کی، ہمارا نام گواہی دینے والوں میں لکھ لے"');
INSERT INTO "ur_maududi" VALUES(3,54,'3:54','پھر بنی اسرائیل (مسیح کے خلاف) خفیہ تدبیریں کرنے لگے جواب میں اللہ نے بھی اپنی خفیہ تدبیر کی اور ایسی تدبیروں میں اللہ سب سے بڑھ کر ہے');
INSERT INTO "ur_maududi" VALUES(3,55,'3:55','(وہ اللہ کی خفیہ تدبیر ہی تھی) جب اس نے کہا کہ ”اے عیسٰی !اب میں تجھے واپس لے لوں گا اور تجھ کو اپنی طرف اٹھا لوں گا اور جنھوں نے تیرا انکار کیا ہے ان سے (یعنی ان کی معیت سے اور ان کے گندے ماحول میں ان کے ساتھ رہنے سے)تجھے پاک کر دوں گا اور تیری پیروی کرنے والوں کو قیامت تک ان لوگوں پر بالا دست رکھوں گا جنھوں نے تیرا انکار کیاہے۔ پھر تم سب کو آخر کار میرے پاس آنا ہے، اس وقت میں ان باتوں کا فیصلہ کر دوں گا جن میں تمہارے درمیان اختلاف ہوا ہے');
INSERT INTO "ur_maududi" VALUES(3,56,'3:56','جن لوگوں نے کفر و انکار کی روش اختیار کی ہے انہیں دنیا اور آخرت دونوں میں سخت سزا دوں گا اور وہ کوئی مددگار نہ پائیں گے');
INSERT INTO "ur_maududi" VALUES(3,57,'3:57','اور جنہوں نے ایمان اور نیک عملی کا رویہ اختیار کیا ہے انہیں اُن کے اجر پورے پورے دے دیے جائیں گے اور خوب جان لے کہ ظالموں سے اللہ ہرگز محبت نہیں کرتا"');
INSERT INTO "ur_maududi" VALUES(3,58,'3:58','یہ آیات اور حکمت سے لبریز تذکرے ہیں جو ہم تمہیں سنا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(3,59,'3:59','اللہ کے نزدیک عیسٰی کی مثال آدم کی سی ہے کہ اللہ نے اسے مٹی سے پیدا کیا اور حکم دیا کہ ہوجا اور وہ ہوگیا۔ ');
INSERT INTO "ur_maududi" VALUES(3,60,'3:60','یہ اصل حقیقت ہے جو تمہارے رب کی طرف سے بتائی جا رہی ہے اور تم ان لوگوں میں شامل نہ ہو جو اس میں شک کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(3,61,'3:61','یہ علم آجانے کے بعد اب جو کوئی اس معاملہ میں تم سے جھگڑا کرے تو اے محمد ؐ!اس سے کہو کہ”آؤ ہم اور تم خود بھی آجا ئیں اور اپنے اپنے بال بچوں کو بھی لے آئیں اور خدا سے دعا کریں کہ جو جھوٹا ہو اس پر خدا کی لعنت ہو“۔ ');
INSERT INTO "ur_maududi" VALUES(3,62,'3:62','یہ بالکل صحیح واقعات ہیں، اور حقیقت یہ ہے کہ اللہ کے سوا کوئی خداوند نہیں ہے، اور وہ اللہ ہی کی ہستی ہے جس کی طاقت سب سے بالا اور جس کی حکمت نظام عالم میں کار فرما ہے');
INSERT INTO "ur_maududi" VALUES(3,63,'3:63','پس اگر یہ لوگ (اِس شرط پر مقابلہ میں آنے سے) منہ موڑیں تو (اُن کا مفسد ہونا صاف کھل جائے گا) اور اللہ تو مفسدوں کے حال سے واقف ہی ہے');
INSERT INTO "ur_maududi" VALUES(3,64,'3:64','کہو، ”اے اہل کتاب! آؤ ایسی بات کی طرف جو ہمارے اور تمہارے درمیان یکساں ہے۔ یہ کہ ہم اللہ کے سوا کسی کی بندگی نہ کریں، اسکے ساتھ کسی کو شریک نہ ٹھہرائیں، اور ہم میں سے کوئی اللہ کے سوا کسی کو اپنا رب نہ بنا لے“۔۔۔۔ اس دعوت کو قبول کرنے سے اگر وہ منہ موڑیں تو صاف کہہ دو کہ گواہ رہو۔ ہم تو مسلم (صرف خدا کی بندگی و اطاعت کرنے والے )ہیں');
INSERT INTO "ur_maududi" VALUES(3,65,'3:65','اے اہل کتاب! تم ابراہیمؑ کے بارے میں ہم سے کیوں جھگڑا کرتے ہو؟ تورات اور انجیل تو ابراہیمؑ کے بعد ہی نازل ہوئی ہیں پھر کیا تم اتنی بات بھی نہیں سمجھتے');
INSERT INTO "ur_maududi" VALUES(3,66,'3:66','تم لوگ جن چیزوں کا علم رکھتے ہو ان میں تو خوب بحثیں کر چکے، اب ان معاملات میں کیوں بحث کرےچلے ہو جن کا تمہارے پاس کچھ بھی علم نہیں۔ اللہ جانتا ہے، تم نہیں جانتے');
INSERT INTO "ur_maududi" VALUES(3,67,'3:67','ابراہیم نہ یہودی تھا نہ عیسائی ، بلکہ وہ تو ایک مسلم ِ یکسُو تھا اور وہ ہر گز مشرکوں میں سے نہ تھا');
INSERT INTO "ur_maududi" VALUES(3,68,'3:68','ابراہیمؑ سے نسبت رکھنے کا سب سے زیادہ حق اگر کسی کو پہنچتا ہے تو اُن لوگوں کو پہنچتا ہے جنہوں نے اس کی پیروی کی اور اب یہ نبی اور اس کے ماننے والے اِس نسبت کے زیادہ حق دار ہیں اللہ صرف اُنہی کا حامی و مددگار ہے جو ایمان رکھتے ہوں');
INSERT INTO "ur_maududi" VALUES(3,69,'3:69','(اے ایمان لانے والو) اہل کتاب میں سے ایک گروہ چاہتا ہے کہ کسی طرح تمہیں راہ راست سے ہٹا دے، حالانکہ در حقیقت وہ اپنے سوا کسی کو گمرا ہی میں نہیں ڈال رہے ہیں مگر انہیں اس کا شعور نہیں ہے');
INSERT INTO "ur_maududi" VALUES(3,70,'3:70','اے اہل کتاب! کیوں اللہ کی آیات کا انکار کرتے ہو حالانکہ تم خود ان کا مشاہدہ کر رہے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(3,71,'3:71','اے اہل کتاب! کیوں حق کو باطل کا رنگ چڑھا کر مشتبہ بناتے ہو؟ کیوں جانتے بوجھتے حق کو چھپاتے ہو؟');
INSERT INTO "ur_maududi" VALUES(3,72,'3:72','اہل کتاب میں سے ایک گروہ کہتا ہےکہ اس کےنبی کے ماننے والوں پر جو کچھ نازل ہوا ہے اس پر صحیح ایمان لاؤ اور شام کو اس سے انکار کردو، شاید اس ترکیب سے یہ لوگ اپنے ایمان سے پھر جائیں۔ ');
INSERT INTO "ur_maududi" VALUES(3,73,'3:73','نیز یہ لوگ آپس میں کہتے ہیں کہ اپنے مذہب والے کے سوا کسی کی بات نہ مانو۔ اے نبی! ان سے کہہ دو کہ”اصل میں ہدایت تو اللہ کی ہدایت ہے اور یہ اُسی کی دین ہے کہ کسی کو وہی کچھ دے دیا جائے جو کبھی تم کو دیا گیا تھا، یا یہ کہ دوسروں کو تمہارے رب کے حضور پیش کرنے کے لیے تمہارے خلاف قوی حجّت مل جائے“۔ اے نبیؐ ! ان سے کہو کہ ”فضل و شرف اللہ کے اختیار میں ہے ، جسے چاہے عطا فرمائے۔ وہ وسیع النظر ہے اور سب کچھ جانتا ہے، ');
INSERT INTO "ur_maududi" VALUES(3,74,'3:74','اپنی رحمت کے لیے جس کو چاہتا ہے مخصوص کر لیتا ہے اور اس کا فضل بہت بڑا ہے"');
INSERT INTO "ur_maududi" VALUES(3,75,'3:75','اہل کتاب میں کوئی تو ایسا ہے کہ اگر تم اس کے اعتماد پر مال و دولت کا ایک ڈھیربھی دے دو تو وہ تمہارا مال تمہیں ادا کردے گا، اور کسی کا حال یہ ہے کہ اگر تم ایک دینار کے معاملہ میں بھی اس پر بھروسہ کرو تو وہ ادا نہ کرے گا اِلّا یہ کہ تم اس کے سر پر سوار ہو جاؤ۔ ان کی اس اخلاقی حالت کا سبب یہ ہے کہ وہ کہتےہیں ”اُمّیوں (غیر یہودی لوگوں) کے معاملہ میں ہم پر کوئی مواخذہ نہیں ہے“۔ اور یہ بات وہ محض جھوٹ گھڑ کر اللہ کی طرف منسوب کرتے ہیں، حالانکہ انہیں معلوم ہے کہ اللہ نے ایسی کوئی بات نہیں فرمائی ہے، آخر کیوں ان سے باز پرس نہ ہو گی؟');
INSERT INTO "ur_maududi" VALUES(3,76,'3:76','جو بھی اپنے عہد کو پورا کرے گا اور برائی سے بچ کر رہے گا وہ اللہ کا محبوب بنے گا، کیونکہ پرہیز گار لوگ اللہ کو پسند ہیں');
INSERT INTO "ur_maududi" VALUES(3,77,'3:77','رہے وہ لوگ جو اللہ کے عہد اور اپنی قسموں کو تھوڑی قیمت پر بیچ ڈالتے ہیں، تو ان کے لیے آخرت میں کوئی حصّہ نہیں، اللہ قیامت کے روز نہ ان سے بات کرے گا نہ ان کی طرف دیکھے گا اور نہ انھیں پاک کرے گا، بلکہ ان کےلیے تو سخت درد ناک سزا ہے');
INSERT INTO "ur_maududi" VALUES(3,78,'3:78','ان میں کچھ لوگ ایسے ہیں جو کتاب پڑھتے ہوئے اس طرح زبان کا اُلٹ پھیر کرتے ہیں کہ تم سمجھو جو کچھ وہ پڑھ رہے ہیں وہ کتاب ہی کی عبارت ہے، حالانکہ وہ کتاب کی عبارت نہیں ہوتی، وہ کہتے ہیں کہ یہ جو کچھ ہم پڑھ رہے ہیں یہ خدا کی طرف سے ہے، حالانکہ وہ خدا کی طرف سے نہیں ہوتا، وہ جا ن بوجھ کر جھوٹ بات اللہ کی طرف منسوب کر دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(3,79,'3:79','کسی انسان کا یہ کام نہیں ہے کہ اللہ تو اس کو کتاب اور حکم اور نبوت عطا فرما ئے اور وہ لوگوں سے کہے کہ اللہ کے بجائے تم میرے بندے بن جاؤ۔ وہ تو یہی کہے گاکہ سچّےربّانی بنو جیسا کہ اس کتاب کی تعلیم کا تقا ضا ہے جسے تم پڑھتے اور پڑھا تےہو');
INSERT INTO "ur_maududi" VALUES(3,80,'3:80','وہ تم سے ہرگز یہ نہ کہے گا کہ فرشتوں کو یا پیغمبروں کواپنا رب بنا لو، کیا یہ ممکن ہے کہ ایک نبی تمہیں کفر کا حکم دے جب کہ تم مسلم ہو؟ ');
INSERT INTO "ur_maududi" VALUES(3,81,'3:81','یاد کرو ، اللہ نے پیغمبروں سے عہد لیا تھا کہ”آج ہم نے تمہیں کتاب اور حکمت و دانش سے نوازا ہے، کل اگر کوئی دوسرا رسول تمہارے پاس اسی تعلیم کی تصدیق کر تا ہو ا آئےجو پہلے سے تمہارے پاس موجود ہے، تو تم کو اس پر ایمان لانا ہو گا اور اس کی مدد کرنی ہو گی“۔ یہ ارشاد فرما کر اللہ نے پوچھا”کیا تم اس کا اقرا ر کرتے ہو اور اس پر میری طرف سے عہد کی بھاری ذمّہ داری اٹھاتے ہو؟“ انہوں نے کہا ہاں ہم اقرار کرتے ہیں۔ اللہ نے فرمایا ”اچھا تو گواہ رہو اور میں بھی تمہارے ساتھ گواہ ہوں');
INSERT INTO "ur_maududi" VALUES(3,82,'3:82','اس کے بعد جو اپنے عہد سے پھر جائے وہی فاسق ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,83,'3:83','اب کیا یہ لوگ اللہ کی اطا عت کے طریقہ (دینُُ اللہ) کوچھوڑ کر کوئی اور طریقہ چاہتے ہیں؟ حالانکہ آسمان و زمین کی ساری چیزیں چارو ناچاراللہ ہی کے تابع فرمان(مسلم)ہیں اور اسی کی طرف سب کو پلٹنا ہے؟');
INSERT INTO "ur_maududi" VALUES(3,84,'3:84','اے نبی ؐ!کہو کہ اللہ کو مانتے ہیں، اس تعلیم کو مانتے ہیں جو ہم پر نازل کی گئی ہے، ان تعلیمات کو بھی مانتے ہیں جو ابراھیم ؑ،اسماعیل ؑ، اسحاق ؑ، یعقوب ؑ اور اولاد یعقوب ؑ پر نازل ہوئی تھیں، اور ان ہدایات پر بھی ایمان رکھتے ہیں جو موسیٰؑ اور عیسٰیؑ اور دوسرے پیغمبروں کو ان کے رب کی طرف سے دی گئیں۔ ہم ان کے درمیان فرق نہیں کرتے اور ہم اللہ کے تابع فرمان(مسلم)ہیں');
INSERT INTO "ur_maududi" VALUES(3,85,'3:85','اس فرماں برداری (اسلام) کے سوا جو شخص کوئی اور طریقہ اختیار کرنا چاہے اس کا وہ طریقہ ہرگز قبول نہ کیا جائے گا اور آخرت میں وہ ناکام و نامراد رہے گا');
INSERT INTO "ur_maududi" VALUES(3,86,'3:86','کیسے ہو سکتا ہے کہ اللہ اُن لوگوں کو ہدایت بخشے جنہوں نے نعمتِ ایمان پا لینے کے بعد پھر کفر اختیار کیا حالانکہ وہ خود اس بات پر گواہی دے چکے ہیں کہ یہ رسول حق پر ہے اور ان کے پاس روشن نشانیاں بھی آچکی ہیں۔ اللہ ظالموں کو تو ہدایت نہیں دیا کرتا');
INSERT INTO "ur_maududi" VALUES(3,87,'3:87','ان کے ظلم کا صحیح بدلہ یہی ہے کہ ان پر اللہ اور فرشتوں اور تمام انسانوں کی پھٹکار ہے');
INSERT INTO "ur_maududi" VALUES(3,88,'3:88','اِسی حالت میں وہ ہمیشہ رہیں گے، نہ ان کی سزا میں تخفیف ہوگی اور نہ انہیں مہلت دی جائے گی');
INSERT INTO "ur_maududi" VALUES(3,89,'3:89','البتہ وہ لوگ بچ جائیں گے جو اِس کے بعد توبہ کر کے اپنے طرز عمل کی اصلاح کر لیں، اللہ بخشنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(3,90,'3:90','مگر جن لوگوں نے ایمان لانے کے بعد کفر اختیار کیا، پھر اپنے کفر میں بڑھتے چلے گئے ان کی توبہ بھی قبو ل نہ ہو گی، ایسے لوگ تو پکّے گمراہ ہیں');
INSERT INTO "ur_maududi" VALUES(3,91,'3:91','یقین رکھو، جن لوگوں نے کفر اختیار کیا اور کفر ہی کی حالت میں جان دی اُن میں سے کوئی اگر اپنے آپ کو سزا سے بچانے کے لیے روئے زمین بھر کر بھی سونا فدیہ میں دے تو اُسے قبول نہ کیا جائے گا ایسے لوگوں کے لیے دردناک سزا تیار ہے اور و ہ اپنا کوئی مددگار نہ پائیں گے');
INSERT INTO "ur_maududi" VALUES(3,92,'3:92','تم نیکی کو نہیں پہنچ سکتے جب تک کہ اپنی وہ چیزیں (خدا کی راہ میں)خرچ نہ کر دو جنہیں تم عزیز رکھتے ہو، اور جو کچھ تم خرچ کرو گے اللہ اس سے بے خبر نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(3,93,'3:93','کھانے کی یہ ساری چیزیں (جو شریعتِ محمد ؐ ی میں حلال ہیں)بنی اسرئیل کے لیے بھی حلال تھیں، البتہ بعض چیزیں ایسی تھیں جنھیں تورات کے نازل کیے جانے سے پہلے اسرائیل نے خود اپنے اوپر حرام کر لیا تھا۔ ان سے کہو، اگر تم (اپنے اعتراض میں)سچّے ہو تو لاؤ تورات اور پیش کرو اس کی کوئی عبارت');
INSERT INTO "ur_maududi" VALUES(3,94,'3:94','اس کے بعد بھی جولو گ اپنی جھوٹی گھڑی ہوئی باتیں اللہ کی طرف منسوب کرتے رہیں و ہی در حقیقت ظالم ہیں');
INSERT INTO "ur_maududi" VALUES(3,95,'3:95','کہو، اللہ نے جو کچھ فرمایا ہے سچ فرمایا ہے،تم کو یکسُو ہو کر ابراہیم ؑ کے طریقہ کی پیروی کرنی چاہیے، اور ابراہیم ؑ شرک کرنے والوں میں سے نہ تھا۔ ');
INSERT INTO "ur_maududi" VALUES(3,96,'3:96','بے شک سب سے پہلی عبادت گا ہ جو انسانوں کے لیے تعمیر ہوئی وہ وہی ہے جو مکّہ میں واقع ہے۔ اس کو خیر و برکت دی گئی تھی اور تمام جہان والوں کے لیے مرکزِ ہدایت بنایا گیا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(3,97,'3:97','اس میں کھلی ہوئی نشانیاں ہیں، ابراہیم ؑ کا مقام عبادت ہے، اور اس کا حال یہ ہے کہ جو اس میں داخل ہوا مامون ہوگیا۔ لوگوں پر اللہ کا یہ حق ہے کہ جو اس گھر تک پہنچنے کی استطاعت رکھتا ہو وہ اس کا حج کرے، اور جو کوئی اس حکم کی پیر وی سے انکار کرے تو اسے معلوم ہو جا نا چاہیے کہ اللہ تما م دنیا والوں سے بے نیاز ہے');
INSERT INTO "ur_maududi" VALUES(3,98,'3:98','کہو، اے اہل کتاب! تم کیوں اللہ کی باتیں ماننے سے انکار کرتے ہو؟ جو حرکتیں تم کر رہے ہو اللہ سب کچھ دیکھ رہا ہے');
INSERT INTO "ur_maududi" VALUES(3,99,'3:99','کہو، اے اہل کتاب! یہ تمہاری کیا روش ہے کہ جو اللہ کی بات مانتا ہے اُسے بھی تم اللہ کے راستہ سے روکتے ہو اور چاہتے ہو کہ وہ ٹیڑھی راہ چلے، حالانکہ تم خود (اس کے راہ راست ہونے پر) گواہ ہو تمہاری حرکتوں سے اللہ غافل نہیں ہے');
INSERT INTO "ur_maududi" VALUES(3,100,'3:100','اے لوگو جو ایمان لائے ہو، اگر تم نے اِن اہل کتاب میں سے ایک گروہ کی بات مانی تو یہ تمہیں ایمان سے پھر کفر کی طرف پھیر لے جائیں گے');
INSERT INTO "ur_maududi" VALUES(3,101,'3:101','تمہارے لیے کفر کی طرف جانے کا اب کیا موقع باقی ہے جب کہ تم کو اللہ کی آیات سنائی جا رہی ہیں اور تمہارے درمیان اس کا رسولؐ موجود ہے؟ جو اللہ کا دامن مضبوطی کے ساتھ تھامے گا وہ ضرور راہ راست پالے گا');
INSERT INTO "ur_maududi" VALUES(3,102,'3:102','اے لوگوں جو ایمان لائے ہو ، اللہ سے ڈرو جیسا کہ اس سے ڈرنے کا حق ہے۔ تم کو موت نہ آئے مگر اس حال میں کہ تم مسلم ہو۔ ');
INSERT INTO "ur_maududi" VALUES(3,103,'3:103','سب مل کر اللہ کی رسی کو مضبوط پکڑ لو اور تفرقہ میں نہ پڑو۔ اللہ کے اس احسان کو یاد رکھو جو اس نے تم پر کیا ہے۔ تم ایک دوسرے کے دشمن تھے، اس نے تمہارے دل جوڑ دیے اور اس کے فضل و کرم سے تم بھائی بھائی بن گئے۔ تم آگ سے بھرے ہوئے ایک گڑھے کے کنارے کھڑے تھے،اللہ نے تم کو اس سے بچالیا۔ اس طرح اللہ اپنی نشانیاں تمہارےسامنے روشن کرتا ہے شاید کہ ان علامتوں سے تمہیں اپنی فلاح کا سیدھا راستہ نظر آجائے۔ ');
INSERT INTO "ur_maududi" VALUES(3,104,'3:104','تم میں کچھ لوگ تو ایسے ضرور ہی رہنے چاہییں جو نیکی کی طرف بلائیں، بھلائی کا حکم دیں، اور برائیوں سے روکتے رہیں جو لوگ یہ کام کریں گے وہی فلاح پائیں گے');
INSERT INTO "ur_maududi" VALUES(3,105,'3:105','کہیں تم ان لوگوں کی طرح نہ ہو جانا جو فرقوں میں بٹ گئے اور کھلی کھلی واضح ہدایا ت پانے کے بعد پھر اختلافات میں مبتلا ہوئے۔ جنھوں نے یہ روش اختیار کی وہ اس روز سخت سزا پائیں گے');
INSERT INTO "ur_maududi" VALUES(3,106,'3:106','جبکہ کچھ لوگ سرخ رو ہوں گے اور کچھ لوگوں کا منہ کالا ہوگا، جن کا منہ کالا ہوگا (ان سے کہا جائے گا کہ) نعمت ایمان پانے کے بعد بھی تم نے کافرانہ رویہ اختیار کیا؟ اچھا تو اب اِس کفران نعمت کے صلہ میں عذاب کا مزہ چکھو');
INSERT INTO "ur_maududi" VALUES(3,107,'3:107','رہے وہ لوگ جن کے چہرے روشن ہوں گے تو اُن کو اللہ کے دامن رحمت میں جگہ ملے گی اور ہمیشہ وہ اسی حالت میں رہیں گے');
INSERT INTO "ur_maududi" VALUES(3,108,'3:108','یہ اللہ کے ارشادات ہیں جو ہم تمہیں ٹھیک ٹھیک سنا رہے ہیں کیو نکہ اللہ دنیا والوں پر ظلم کرنے کا کوئی ارادہ نہیں رکھتا۔ ');
INSERT INTO "ur_maududi" VALUES(3,109,'3:109','زمین و آسمان کی ساری چیزوں کا مالک اللہ ہے اور سارے معاملات اللہ ہی کے حضور پیش ہوتے ہیں');
INSERT INTO "ur_maududi" VALUES(3,110,'3:110','اب دنیا میں وہ بہترین گروہ تم ہو جسے انسانوں کی ہدایت و اصلاح کے لیے میدان میں لایا گیا ہے۔ تم نیکی کا حکم دیتے ہو، بدی سے روکتے ہو اور اللہ پر ایمان رکھتے ہو۔ یہ اہل کتاب ایمان لاتے تو انہی کے حق میں بہتر تھا۔ اگرچہ ان میں کچھ لوگ ایمان دار بھی پائے جاتے ہیں مگر ان کے بیشتر افراد نافرمان ہیں');
INSERT INTO "ur_maududi" VALUES(3,111,'3:111','یہ تمہارا کچھ بگاڑ نہیں سکتے، زیادہ سے زیادہ بس کچھ ستا سکتے ہیں اگر یہ تم سے لڑیں گے تو مقابلہ میں پیٹھ دکھائیں گے، پھر ایسے بے بس ہوں گے کہ کہیں سے اِن کو مدد نہ ملے گی');
INSERT INTO "ur_maududi" VALUES(3,112,'3:112','یہ جہاں بھی پائے گئے ان پر ذلت کی مار ہی پڑی ، کہیں اللہ کے ذمّہ یا انسانوں کے ذمّہ میں پناہ مل گئی تو یہ اور بات ہے، یہ اللہ کے غضب میں گھر چکے ہیں، ان پر محتاجی و مغلوبی مسلط کر دی گئی ہے، اور یہ سب کچھ صرف اس وجہ سے ہوا ہے کہ یہ اللہ کی آیات سے کفر کرتے رہے اور انہوں نے پیغمبروں کو نا حق قتل کیا۔ یہ ان کی نا فرمانیوں اور زیادتیوں کا انجام ہے');
INSERT INTO "ur_maududi" VALUES(3,113,'3:113','مگر سارے اہل کتاب یکساں نہیں ہیں ان میں کچھ لوگ ایسے بھی ہیں جو راہ راست پر قائم ہیں، راتوں کو اللہ کی آیات پڑھتے ہیں اور اسکے آگے سجدہ ریز ہوتے ہیں');
INSERT INTO "ur_maududi" VALUES(3,114,'3:114','اللہ اور روز آخرت پر ایمان رکھتے ہیں، نیکی کا حکم دیتے ہیں، برائیوں سے روکتے ہیں اور بھلائی کے کاموں میں سرگرم رہتے ہیں یہ صالح لوگ ہیں');
INSERT INTO "ur_maududi" VALUES(3,115,'3:115','اور جو نیکی بھی یہ کریں گے اس کی نا قدری نہ کی جائے گی، اللہ پرہیزگار لوگوں کو خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(3,116,'3:116','رہے وہ لوگ جنہوں نے کفر کا رویہ اختیار کیا تو اللہ کے مقابلہ میں ان کو نہ ان کا مال کچھ کام دے گا نہ اولاد، وہ تو آگ میں جانے والے لوگ ہیں اور آگ ہی میں ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(3,117,'3:117','جو کچھ وہ اپنی اس دنیا کی زندگی میں خرچ کر رہے ہیں اس کی مثال اس ہوا کی سی ہے جس میں پالا ہو اور وہ ان لوگوں کی کھیتی پر چلے جنھوں نے اپنے اوپر آپ ظلم کیا ہے اور اسے برباد کر کے رکھ دے۔ اللہ نے ان پر ظلم نہیں کیا درحقیقت یہ خود اپنےاوپر ظلم کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(3,118,'3:118','اے لوگو جو ایمان لائے ہو ، اپنی جماعت کے لوگوں کے سوا دوسروں کو اپنا راز دار نہ بناؤ۔ وہ تمہاری خرابی کے کسی موقع سے فائدہ اٹھا نے میں نہیں چُوکتے۔ تمہیں جس چیز سے نقصان پہنچے وہی ان کو محبوب ہے۔ ان کے دل کا بغض ان کے منہ سے نکلا پڑتا ہے اور جو کچھ وہ اپنے سینوں میں چھپائےہوئے ہیں وہ اس سے شدید ترہے۔ ہم نے تمہیں صاف صاف ہدایات دے دی ہیں، اگر تم عقل رکھتےہو (تو ان سے تعلق رکھنے میں احتیاط برتو گے)');
INSERT INTO "ur_maududi" VALUES(3,119,'3:119','تم ان سے محبت رکھتے ہو مگر وہ تم سے محبت نہیں رکھتے حالانکہ تم تمام کتب ِ آسمانی کو مانتے ہو۔ جب وہ تم سے ملتے ہیں تو کہتے ہیں کہ ہم نے بھی (تمہارے رسول اور تمہاری کتاب کو ) مان لیا ہے ، مگر جب جدا ہوتے ہیں تو تمہارے خلاف غیظ و غضب کا یہ حال ہو تا ہےکہ اپنی انگلیا ں چبانے لگتے ہیں۔۔۔۔ان سے کہہ دو کہ اپنے غصّہ میں آپ جل مرو، اللہ دلوں کے چھپے ہوئے راز تک جانتا ہے');
INSERT INTO "ur_maududi" VALUES(3,120,'3:120','تمہارا بھلا ہوتا ہے تو ان کو برا معلوم ہوتا ہے، اور تم پر کوئی مصیبت آتی ہے تو یہ خوش ہوتے ہیں مگر ان کی کوئی تدبیر تمہارے خلاف کارگر نہیں ہوسکتی بشرطیکہ تم صبر سے کام لو اور اللہ سے ڈر کر کام کرتے رہو جو کچھ یہ کر رہے ہیں اللہ اُس پر حاوی ہے');
INSERT INTO "ur_maududi" VALUES(3,121,'3:121',' (اے پیغمبر ؐ! مسلمانوں کے سامنے اس موقع کا ذکر کر و ) جب تم صبح سویرے اپنے گھر سے نکلے تھے اور(اُحد کے میدان میں) مسلمانوں کو جنگ کے لیے جا بجا مامور کر رہے تھے۔اللہ ساری باتیں سنتا ہےاور وہ نہات باخبر ہے');
INSERT INTO "ur_maududi" VALUES(3,122,'3:122','یاد کرو جب تم میں سے دو گروہ بُزدلی دکھانے پر آمادہ ہو گئے تھے ، حالانکہ اللہ ان کی مدد پر موجود تھا اور مومنوں کو اللہ ہی پر بھروسہ رکھنا چاہیے');
INSERT INTO "ur_maududi" VALUES(3,123,'3:123','آخر اس سے پہلے جنگ بدر میں اللہ تمہاری مدد کر چکا تھا حالانکہ اس وقت تم بہت کمزور تھے لہٰذا تم کو چاہیے کہ اللہ کی ناشکر ی سے بچو، امید ہے کہ اب تم شکر گزار بنو گے');
INSERT INTO "ur_maududi" VALUES(3,124,'3:124','یاد کرو جب تم مومنوں سے کہہ رہے تھے”کیا تمہارے لیے یہ بات کافی نہیں کہ اللہ تین ہزار فرشتے اتار کر تمہاری مدد کرے“؟۔۔۔۔ ');
INSERT INTO "ur_maududi" VALUES(3,125,'3:125','بے شک، اگر تم صبر کرو اور خدا سے ڈرتے ہوئے کام کرو تو جس آن دشمن تمہارے اوپر چڑھ کر آئیں گے اُسی آن تمہارا رب (تین ہزار نہیں) پانچ ہزار صاحب نشان فرشتوں سے تمہاری مدد کرے گا');
INSERT INTO "ur_maududi" VALUES(3,126,'3:126','یہ بات اللہ نے تمہیں اس لیے بتا دی ہے کہ تم خوش ہو جاؤ اور تمہارے دل مطمئن ہو جائیں فتح و نصرت جو کچھ بھی ہے اللہ کی طرف سے ہے جو بڑی قوت والا اور دانا و بینا ہے');
INSERT INTO "ur_maududi" VALUES(3,127,'3:127','(اور یہ مدد وہ تمہیں اس لیے دے گا) تاکہ کفر کی راہ چلنے والوں کا ایک بازو کاٹ دے، یا ان کو ایسی ذلیل شکست دے کہ وہ نامرادی کے ساتھ پسپا ہو جائیں');
INSERT INTO "ur_maududi" VALUES(3,128,'3:128','(اے پیغمبرؐ) فیصلہ کے اختیارات میں تمہارا کوئی حصہ نہیں، اللہ کو اختیار ہے چاہے انہیں معاف کرے، چاہے سزا دے کیونکہ وہ ظالم ہیں');
INSERT INTO "ur_maududi" VALUES(3,129,'3:129','زمین اور آسمانوں میں جو کچھ ہے اس کا مالک اللہ ہے، جس کو چاہے بخش دے اور جس کو چاہے عذاب دے، وہ معاف کرنے والا اور رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,130,'3:130','اے لوگو جو ایما ن لائے ہو ، یہ بڑھتا اور چڑھتا سُود کھانا چھوڑ دو اور اللہ سے ڈرو، امید ہے کہ فلاح پاؤ گے');
INSERT INTO "ur_maududi" VALUES(3,131,'3:131','اُس آگ سے بچو جو کافروں کے لیے مہیا کی گئی ہے');
INSERT INTO "ur_maududi" VALUES(3,132,'3:132','اور اللہ اور رسول کا حکم مان لو، توقع ہے کہ تم پر رحم کیا جائے گا');
INSERT INTO "ur_maududi" VALUES(3,133,'3:133','دوڑ کر چلو اُ س راہ پر جو تمہارے رب کی بخشش اور اُس جنت کی طرف جاتی ہے جس کی وسعت زمین اور آسمانوں جیسی ہے، اور وہ اُن خدا ترس لوگوں کے لیے مہیا کی گئی');
INSERT INTO "ur_maududi" VALUES(3,134,'3:134','جو ہر حال میں اپنے مال خرچ کر تے ہیں خواہ بدحال ہوں یا خوش حال، جو غصے کو پی جاتے ہیں اور دوسروں کے قصور معاف کر دیتے ہیں۔۔۔۔ ایسے نیک لوگ اللہ کو بہت پسند ہیں۔۔۔۔ ');
INSERT INTO "ur_maududi" VALUES(3,135,'3:135','اور جن کا حال یہ ہے کہ اگر کبھی کوئی فحش کام ان سے سرزد ہو جاتا ہے یا کسی گناہ کا ارتکاب کر کے وہ اپنے اوپر ظلم کر بیٹھتے ہیں تو معاً اللہ انہیں یاد آ جاتا ہے اور اس سے وہ اپنے قصوروں کی معافی چاہتے ہیں کیونکہ اللہ کے سوا اور کون ہے جو گناہ معاف کرسکتا ہو او ر وہ دیدہ و دانستہ اپنے کیے پر اصرار نہیں کرتے');
INSERT INTO "ur_maududi" VALUES(3,136,'3:136','ایسے لوگوں کی جزا ان کے رب کے پاس یہ ہے کہ وہ ان کو معاف کر دے گا اور ایسے باغوں میں انہیں داخل کرے گا جن کے نیچے نہریں بہتی ہونگی اور وہاں وہ ہمیشہ رہیں گے کیسا اچھا بدلہ ہے نیک عمل کرنے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(3,137,'3:137','تم سے پہلے بہت سے دور گزر چکے ہیں، زمین میں چل پھر کر دیکھ لو کہ اُن لوگوں کا کیا انجام ہوا جنہوں نے (اللہ کے احکام و ہدایات کو) جھٹلایا');
INSERT INTO "ur_maududi" VALUES(3,138,'3:138','یہ لوگوں کے لیے ایک صاف اور صریح تنبیہ ہے اور جو اللہ سے ڈرتے ہوں اُن کے لیے ہدایت اور نصیحت');
INSERT INTO "ur_maududi" VALUES(3,139,'3:139','دل شکستہ نہ ہو، غم نہ کرو، تم ہی غالب رہو گے اگر تم مومن ہو');
INSERT INTO "ur_maududi" VALUES(3,140,'3:140','اس وقت اگر تمہیں چوٹ لگی ہےتو اس سے پہلے ایسی ہی چوٹ تمہارے مخالف فریق کو بھی لگ چکی ہے۔ یہ تو زمانہ کے نشیب و فراز ہیں جنھیں ہم لوگوں کے درمیان گردش دیتے رہتے ہیں۔ تم پر یہ وقت اس لیے لایا گیا کہ اللہ دیکھنا چاہتا تھا کہ تم میں سچے مومن کو ن ہیں، اور ان لوگوں کو چھانٹ لینا چاہتا تھا جو واقعی(راستی کے) گواہ ہوں۔۔۔۔ کیونکہ ظالم لوگ اللہ کو پسند نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(3,141,'3:141','اور وہ اس آزمائش کے ذریعہ سے مومنوں کو الگ چھانٹ کر کافروں کی سرکوبی کر دینا چاہتا تھا');
INSERT INTO "ur_maududi" VALUES(3,142,'3:142','کیا تم نے یہ سمجھ رکھا ہے کہ یونہی جنت میں چلے جاؤ گے حالانکہ ابھی اللہ نے یہ تو دیکھا ہی نہیں کہ تم میں کون وہ لوگ ہیں جو اس کی راہ میں جانیں لڑانے والے اور اس کی خاطر صبر کرنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(3,143,'3:143','تم تو موت کی تمنائیں کر رہے تھے! مگر یہ اس وقت کی بات تھی جب موت سامنے نہ آئی تھی ، لو اب وہ تمہارے سامنے آگئی اور تم نے اسے آنکھوں دیکھ لیا۔ ');
INSERT INTO "ur_maududi" VALUES(3,144,'3:144','محمد ؐ اس کے سوا کچھ نہیں کہ بس ایک رسول ہیں، ان سے پہلے اور رسول بھی گزر چکے ہیں ، پھر کیا اگر وہ مر جائیں یا قتل کردیے جائیں تو تم لوگ الٹے پاؤں پھر جاؤ گے ؟ یا د رکھو! جو الٹا پھرے گا وہ اللہ کا کچھ نقصان نہ کرے گا، البتہ جو اللہ کے شکر گزار بندے بن کر رہیں گے انھیں وہ اس کی جز ا دے گا');
INSERT INTO "ur_maududi" VALUES(3,145,'3:145','کوئی ذی رُوح اللہ کے اذن کے بغیر نہیں مر سکتا۔ موت کا وقت تو لکھا ہوا ہے۔ جو شخص ثواب دنیا کے ارادہ سے کا م کر ے گا اس کو ہم دنیا ہی میں سے دیں گے، اور جو ثواب آخرت کے ارادہ سے کام کرے گا وہ آخرت کا ثواب پائے گا اور شکر کرنے والوں کو ہم ان کی جزا ضرور عطا کریں گے');
INSERT INTO "ur_maududi" VALUES(3,146,'3:146','اس سے پہلے کتنے ہی نبی ایسے گز ر چکے ہیں جن کے ساتھ مل کر بہت سے خدا پرستوں نے جنگ کی۔ اللہ کی راہ میں جو مصیبتیں ان پر پڑیں ان سے وہ دل شکستہ نہیں ہوئے، انہوں نے کمزوری نہیں دکھائی ، وہ (باطل کے آگے )سرنگوں نہیں ہوئے۔ ایسے ہی صابروں کو اللہ پسند کرتا ہے');
INSERT INTO "ur_maududi" VALUES(3,147,'3:147','ان کی دعا بس یہ تھی کہ، "اے ہمارے رب! ہماری غلطیوں اور کوتاہیوں سے درگزر فرما، ہمارے کام میں تیرے حدود سے جو کچھ تجاوز ہو گیا ہو اُسے معاف کر دے، ہمارے قدم جما دے اور کافروں کے مقابلہ میں ہماری مدد کر"');
INSERT INTO "ur_maududi" VALUES(3,148,'3:148','آخرکار اللہ نے ان کو دنیا کا ثواب بھی دیا اور اس سے بہتر ثواب آخرت بھی عطا کیا اللہ کو ایسے ہی نیک عمل لوگ پسند ہیں');
INSERT INTO "ur_maududi" VALUES(3,149,'3:149','اے لوگو جو ایمان لائے ہو ، اگر تم ان لوگوں کے اشاروں پر چلو گے جنھوں نے کفر کی راہ اختیار کی ہے تو وہ تم کو الٹا پھیر لے جائیں گے اور تم نامراد ہو جا ؤ گے');
INSERT INTO "ur_maududi" VALUES(3,150,'3:150','(اُن کی باتیں غلط ہیں) حقیقت یہ ہے کہ اللہ تمہارا حامی و مددگار ہے اور وہ بہترین مدد کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(3,151,'3:151','عنقریب وہ وقت آنے والا ہے جب ہم منکرین حق کے دلوں میں رعب بٹھا دیں گے، اس لیے کہ اُنہوں نے اللہ کے ساتھ اُن کو خدائی میں شریک ٹھیرایا ہے جن کے شریک ہونے پر اللہ نے کوئی سند نازل نہیں کی اُن کا آخری ٹھکانا جہنم ہے اور بہت ہی بری ہے وہ قیام گاہ جو اُن ظالموں کو نصیب ہوگی');
INSERT INTO "ur_maududi" VALUES(3,152,'3:152','اللہ نے (تائید ونصرت کا)جو وعدہ تم سے کیا تھا وہ تو اس نے پورا کر دیا۔ ابتدا میں اس کے حکم سے تم ہی ان کو قتل کر رہےتھے۔ مگر جب تم نے کمزوری دکھائی اور اپنے کام میں باہم اختلاف کیا، اور جونہی کہ وہ چیز اللہ نے تمہیں دکھائی جس کی محبت میں تم گرفتار تھے( یعنی مالِ غنیمت)تم اپنے سردار کے حکم کی خلاف ورزی کر بیٹھے۔۔۔۔اس لیے کہ تم میں سے کچھ لوگ دنیا کے طالب تھے اور کچھ آخرت کی خواہش رکھتے تھے۔۔۔۔ تب اللہ نے تمہیں کافروں کے مقابلہ میں پسپا کردیا تاکہ تمہاری آزمائش کرے۔ اور حق یہ ہے کہ اللہ نے پھر بھی تمہیں معاف ہی کر دیا کیونکہ مومنوں پر اللہ بڑی نظر عنایت رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(3,153,'3:153','یاد کرو جب تم بھاگے چلے جارہے تھے، کسی کی طرف پلٹ کر دیکھنے تک کا ہوش تمہیں نہ تھا ، اور رسول ؐ تمہارے پیچھے تم کو پکار رہاتھا۔ اس وقت تمہاری اس روش کا بدلہ اللہ نے تمہیں یہ دیا کہ تم کو رنج پر رنج دیے تاکہ آئندہ کے لیے تمہیں یہ سبق ملے کہ جو کچھ تمہارے ہاتھ سے جائے یا جو مصیبت تم پر نازل ہو اس پر ملول نہ ہو۔ اللہ تمہارے سب اعمال سے باخبر ہے');
INSERT INTO "ur_maududi" VALUES(3,154,'3:154','اس غم کے بعد پھر اللہ نے تم میں سے کچھ لوگوں پر ایسی اطمینان کی سی حالت طاری کردی کہ وہ اونگھنے لگے۔ مگر ایک دوسرا گروہ ، جس کے لیے ساری اہمیت بس اپنے مفادہی کی تھی، اللہ کے متعلق طرح طرح کے جاہلانہ گمان کرنے لگا جو سراسر خلافِ حق تھے، یہ لوگ اب کہتےہیں کہ”اس کام کے چلانے میں ہمارا بھی کوئی حصہ ہے؟“ ان سے کہو”(کسی کا کوئی حصہ نہیں) اس کام کے سارے اختیارات اللہ کے ہاتھ میں ہیں“۔ دراصل یہ لوگ اپنے دلوں میں جو بات چھپاتےہوئے ہیں اسے تم پر ظاہر نہیں کرتے، ان کا اصل مطلب یہ ہے کہ ”اگر(قیادت کے) اختیارات میں ہمارا کچھ حصہ ہوتا تو یہاں ہم نہ مارے جاتے۔“ ان سے کہہ دو کہ”اگر تم اپنے گھروں میں بھی ہوتے تو جن لوگوں کی موت لکھی ہوئی تھی وہ خود اپنی قتل گاہوں کی طرف نکل آتے“۔ اور یہ معاملہ جو پیش آیا ، یہ تو اس لیے تھا کہ جو کچھ تمہارے سینوں میں پوشیدہ ہے اللہ اسے آزمالے اور جو کھوٹ تمارے دلوں میں ہے اسےچھانٹ دے، اللہ دلوں کا حال خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(3,155,'3:155','تم میں سے جو لوگ مقابلہ کے دن پیٹھ پھیر گئے تھے اُن کی ا ِس لغزش کا سبب یہ تھا کہ ان کی بعض کمزوریوں کی وجہ سے شیطان نے اُن کے قدم ڈگمگا دیے تھے اللہ نے انہیں معاف کر دیا، اللہ بہت درگزر کرنے والا اور بردبار ہے');
INSERT INTO "ur_maududi" VALUES(3,156,'3:156','اے لوگو جو ایمان لائے ہو ، کافروں کی سی باتیں نہ کرو جن کے عزیز و اقارب اگر کبھی سفر پر جاتے ہیں یا جنگ میں شریک ہوتے ہیں(اور وہاں کسی حادثہ سے دو چار ہو جاتے ہیں) تو وہ کہتے ہیں کہ اگر وہ ہمارے پاس ہوتے تو نہ مارے جاتے اور نہ قتل ہوتے۔ اللہ اس قوم کی باتوں کو ان کے دلوں میں حسرت واندوہ کا سبب بنا دیتا ہے ، ورنہ دراصل مارنے اور جِلانے والا تو اللہ ہی ہے اور تمہاری تمام حرکات پر وہی نگراں ہے');
INSERT INTO "ur_maududi" VALUES(3,157,'3:157','اگر تم اللہ کی راہ میں مارے جاؤ یا مر جاؤ تو اللہ کی جو رحمت اور بخشش تمہارے حصہ میں آئے گی وہ اُن ساری چیزوں سے زیادہ بہتر ہے جنہیں یہ لوگ جمع کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(3,158,'3:158','اور خواہ تم مرو یا مارے جاؤ بہر حال تم سب کو سمٹ کر جانا اللہ ہی کی طرف ہے');
INSERT INTO "ur_maududi" VALUES(3,159,'3:159','(اے پیغمبرؐ) یہ اللہ کی بڑی رحمت ہے کہ تم اِن لوگوں کے لیے بہت نرم مزاج واقع ہوئے ہو ورنہ اگر کہیں تم تند خو اور سنگ دل ہوتے تو یہ سب تمہارے گردو پیش سے چھٹ جاتے اِن کے قصور معاف کر دو، اِن کے حق میں دعا ئے مغفرت کرو، اور دین کے کام میں ان کو بھی شریک مشورہ رکھو، پھر جب تمہارا عزم کسی رائے پر مستحکم ہو جائے تو اللہ پر بھروسہ کرو، اللہ کو وہ لوگ پسند ہیں جو اُسی کے بھروسے پر کام کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(3,160,'3:160','اللہ تمہاری مدد پر ہو تو کوئی طاقت تم پر غالب آنے والی نہیں، اور وہ تمہیں چھوڑ دے، تو اس کے بعد کون ہے جو تمہاری مدد کرسکتا ہو؟ پس جو سچے مومن ہیں ان کو اللہ ہی پر بھروسہ رکھنا چاہیے');
INSERT INTO "ur_maududi" VALUES(3,161,'3:161','کسی نبی کا یہ کام نہیں ہو سکتا کہ وہ خیانت کر جائے۔۔۔۔ اور جو کوئی خیانت کرے تو وہ اپنی خیانت سمیت قیامت کے روز حاضر ہو جائے گا، پھر ہر متنفس کو اس کی کمائی کا پورا پورا بدلہ مل جائے گا اور کسی پر کچھ ظلم نہ ہو گا');
INSERT INTO "ur_maududi" VALUES(3,162,'3:162','بھلا یہ کیسے ہوسکتا ہے کہ جو شخص ہمیشہ اللہ کی رضا پر چلنے والا ہو وہ اُس شخص کے سے کام کرے جو اللہ کے غضب میں گھر گیا ہو اور جس کا آخری ٹھکانا جہنم ہو جو بدترین ٹھکانا ہے؟');
INSERT INTO "ur_maududi" VALUES(3,163,'3:163','اللہ کے نزدیک دونوں قسم کے آدمیوں میں بدرجہا فرق ہے اور اللہ سب کے اعمال پر نظر رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(3,164,'3:164','درحقیقت اہل ایمان پر تو اللہ نے یہ بہت بڑا احسان کیا ہے کہ اُن کے درمیان خود انہی میں سے ایک ایسا پیغمبر اٹھایا جو اس کی آیات انہیں سناتا ہے، اُن کی زندگیوں کو سنوارتا ہے اور اُن کو کتاب اور دانائی کی تعلیم دیتا ہے، حالانکہ اس سے پہلے یہی لوگ صریح گمراہیوں میں پڑے ہوئے تھے');
INSERT INTO "ur_maududi" VALUES(3,165,'3:165','اور یہ تمہارا کیا حال ہے کہ جب تم پر مصیبت آپڑی تو تم کہنے لگے یہ کہاں سے آئی؟ حالانکہ(جنگ بدر میں)اس سے دوگنی مصیبت تمہارے ہاتھوں(فریق مخالف پر )پڑ چکی ہے۔ اے نبی ؐ! ان سے کہو، یہ مصیبت تمہاری اپنی لائی ہوئی ہے، اللہ ہر چیز پر قادر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,166,'3:166','جو نقصان لڑائی کے دن تمہیں پہنچا وہ اللہ کے اذن سے تھا اور اس لیے تھا کہ اللہ دیکھ لے تم میں سے مومن کون ہیں');
INSERT INTO "ur_maududi" VALUES(3,167,'3:167','اور منافق کون۔ وہ منافق کہ جب ان سے کہا گیا آؤ اللہ کی راہ میں جنگ کرو یا کم ازکم(اپنے شہر کی) مدافعت ہی کرو، تو کہنے لگے اگر ہمیں علم ہوتا کہ آج جنگ ہو گی تو ہم ضرور تمہارے ساتھ چلتے۔ یہ بات جب وہ کہہ رہے تھے اس وقت وہ ایمان کی بہ نسبت کفر سے زیادہ قریب تھے۔ وہ اپنی زبانوں سے وہ باتیں کہتے ہیں جو ان کے دلوں میں نہیں ہوتیں، اور جو کچھ وہ دلوں میں چھپاتے ہیں اللہ اسےخوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(3,168,'3:168','یہ وہی لوگ ہیں جو خود تو بیٹھے رہے او ران کے جو بھائی بند لڑنے گئے اور مارے گئے ان کے متعلق انہوں نے کہہ دیا کہ ا گروہ ہماری بات مان لیتے تو نہ مارے جاتے ان سے کہو اگر تم اپنے قول میں سچے ہو تو خود تمہاری موت جب آئے اُسے ٹال کر دکھا دینا');
INSERT INTO "ur_maududi" VALUES(3,169,'3:169','جو لوگ اللہ کی راہ میں قتل ہوئے ہیں انھیں مُردہ نہ سمجھو، وہ تو حقیقت میں زندہ ہیں، اپنے رب کے پاس رزق پا رہے ہیں،');
INSERT INTO "ur_maududi" VALUES(3,170,'3:170','جو کچھ اللہ نے اپنے فضل سے انھیں دیا ہے اُس پر خوش وخُرم ہیں ، اور مطمئن ہیں کہ جو اہل ِ ایمان ان کے پیچھے دنیا میں رہ گئے ہیں اور ابھی وہاں نہیں پہنچے ہیں ان کے لیے بھی کسی خوف اور رنج کاموقع نہیں ہے');
INSERT INTO "ur_maududi" VALUES(3,171,'3:171','وہ اللہ کے انعام اور اس کے فضل پر شاداں و فرحاں ہیں اور ان کو معلوم ہو چکا ہے کہ اللہ مومنوں کے اجر کو ضائع نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(3,172,'3:172','جن لوگوں نے زخم کھانے کے بعد بھی اللہ اور رسول کی پکار پر لبیک کہا ان میں جو اشخاص نیکو کار اور پرہیز گار ہیں ان کے لیے بڑا اجر ہے');
INSERT INTO "ur_maududi" VALUES(3,173,'3:173','اور وہ جن سے لوگوں نے کہاکہ”تمہارے خلاف بڑی فوج جمع ہوئی ہیں، ان سے ڈرو“ تو یہ سن کر ان کا ایمان اور بڑھ گیا اور انہوں نے جواب دیا کہ ہمارے لیے اللہ کافی ہے او ر وہی بہترین کارساز ہے');
INSERT INTO "ur_maududi" VALUES(3,174,'3:174','آخر کار وہ اللہ تعالیٰ کی نعمت اور فضل کے ساتھ پلٹ آئے، ان کو کسی قسم کا ضرر بھی نہ پہنچا اور اللہ کی رضا پر چلنے کا شرف بھی انہیں حاصل ہو گیا، اللہ بڑا فضل فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(3,175,'3:175','اب تمہیں معلوم ہو گیا کہ وہ دراصل شیطان تھا جو اپنے دوستوں سے خواہ مخواہ ڈرارہاتھا۔ لہٰذا آئندہ تم انسانوں سے نہ ڈرنا، مجھ سےڈرنا اگر تم حقیقت میں صاحب ِ ایمان ہو۔ ');
INSERT INTO "ur_maududi" VALUES(3,176,'3:176','(اے پیغمبرؐ) جو لوگ آج کفر کی راہ میں بڑی دوڑ دھوپ کر رہے ہیں ان کی سرگرمیاں تمہیں آزردہ نہ کریں، یہ اللہ کا کچھ بھی نہ بگاڑ سکیں گے اللہ کا ارادہ یہ ہے کہ اُن کے لیے آخرت میں کوئی حصہ نہ رکھے، اور بالآخر ان کو سخت سزا ملنے والی ہے');
INSERT INTO "ur_maududi" VALUES(3,177,'3:177','جو لوگ ایمان کو چھوڑ کر کفر کے خریدار بنے ہیں وہ یقیناً اللہ کا کوئی نقصان نہیں کر رہے ہیں، اُن کے لیے درد ناک عذاب تیار ہے');
INSERT INTO "ur_maududi" VALUES(3,178,'3:178','یہ ڈھیل جو ہم انہیں دیے جاتے ہیں اس کو یہ کافر اپنے حق میں بہتری نہ سمجھیں، ہم تو انہیں اس لیے ڈھیل دے رہے ہیں کہ یہ خوب بار گناہ سمیٹ لیں، پھر اُن کے لیے سخت ذلیل کرنے والی سزا ہے');
INSERT INTO "ur_maududi" VALUES(3,179,'3:179','اللہ مومنوں کو اس حالت میں ہرگز نہ رہنے دے گا جس میں تم اس وقت پائے جاتے ہو۔ وہ پاک لوگوں کو ناپاک لوگوں سے الگ کرکے رہے گا۔ مگر اللہ کا یہ طریقہ نہیں ہے کہ تم کو غیب پر مطلع کردے۔ غیب کی باتیں بتانے کے لیے تووہ اپنے رسولوں میں سے جس کو چاہتا ہے منتخب کر لیتا ہے، لہٰذا( اُمورِ غیب کے بارے میں) اللہ اور اس کے رسول پر ایمان رکھو۔ اگر تم ایمان اور خدا ترسی کی روش پر چلو گے تو تم کو بڑا اجر ملے گا');
INSERT INTO "ur_maududi" VALUES(3,180,'3:180','جن لوگوں کو اللہ نے اپنے فضل سے نوازا ہے اور پھر وہ بخل سے کام لیتےہیں وہ اس خیال میں نہ رہیں کہ یہ بخیلی ا ن کےلیے اچھی ہے۔ نہیں، یہ ان کے حق میں نہایت بری ہے۔ جو کچھ وہ اپنی کنجوسی سے جمع کررہے ہیں وہی قیامت کے روز ان کے گلے کا طوق بن جائے گا۔ زمین اور آسمانوں کی میراث اللہ ہی کے لیے ہے اور تم جو کچھ کرتے ہو اللہ اس سے باخبر ہے');
INSERT INTO "ur_maududi" VALUES(3,181,'3:181','اللہ نےان لوگوں کاقول سنا جو کہتے ہیں کہ اللہ فقیرہے اور ہم غنی ہیں۔ ان کی یہ باتیں بھی ہم لکھ لیں گے ، اور اس سے پہلے جو وہ پیغمبروں کو ناحق قتل کرتے رہے ہیں وہ بھی ان کے نامہٴ اعمال میں ثبت ہے۔ ( جب فیصلہ کا وقت آئےگا اس وقت) ہم ان سے کہیں گے کہ لو، اب عذاب جہنم کا مزا چکھو');
INSERT INTO "ur_maududi" VALUES(3,182,'3:182','یہ تمہارے اپنے ہاتھوں کی کمائی ہے، اللہ اپنے بندوں کے لیے ظالم نہیں ہے');
INSERT INTO "ur_maududi" VALUES(3,183,'3:183','جو لوگ کہتے ہیں”اللہ نےہم کو ہدایت کر دی ہے کہ ہم کسی کو رسول تسلیم نہ کریں جب تک وہ ہمارے سامنے ایسی قربانی نہ کرے جسے(غیب سے آکر) آگ کھا لے“، ان سے کہو ”تمہارے پاس مجھ سے پہلے بہت رسول آچکے ہیں جو بہت سی روشن نشانیاں لائے تھے اور وہ نشانی بھی لائے تھے جس کا تم ذکر کرتےہو ، پھر اگر (ایمان لانے کے لیے یہ شرط پیش کرنے میں) تم سچے ہو تو ان رسولوں کو تم نے کیوں قتل کیا؟“ ');
INSERT INTO "ur_maududi" VALUES(3,184,'3:184','اب اے محمدؐ! اگر یہ لوگ تمہیں جھٹلاتے ہیں تو بہت سے رسول تم سے پہلے جھٹلائے جا چکے ہیں جو کھلی کھلی نشانیاں اور صحیفے اور روشنی بخشنے والی کتابیں لائے تھے');
INSERT INTO "ur_maududi" VALUES(3,185,'3:185','آخر کار ہر شخص کو مرنا ہے اور تم سب اپنے اپنے پورے اجر قیامت کےروز پانے والے ہو۔ کامیاب دراصل وہ ہےجو وہاں آتشِ دوزخ سے بچ جائے اور جنت میں داخل کر دیا جائے۔ رہی یہ دنیا، تو یہ محض ایک ظاہر فریب چیز ہے۔ ');
INSERT INTO "ur_maududi" VALUES(3,186,'3:186','مسلمانو! تمہیں مال و جان دونوں کی آزمائشیں پیش آکر رہیں گی، اور تم اہل کتاب اور مشرکین سے بہت سی تکلیف دِہ باتیں سنو گے۔ اگر ان سب حالات میں تم صبر اور خدا ترسی کی روش پر قائم رہو تو یہ بڑے حوصلہ کا کام ہے');
INSERT INTO "ur_maududi" VALUES(3,187,'3:187','ان اہل کتاب کو وہ عہد بھی یاد دلاؤ جو اللہ نے ان سےلیا تھا کہ تمہیں کتاب کی تعلیمات کو لوگوں میں پھیلانا ہوگا، انھیں پوشیدہ رکھنا نہیں ہوگا۔ مگر انہوں نےکتاب کو پسِ پشت ڈال دیا اور تھوڑی قیمت پر اسے بیچ ڈالا۔ کتنا بُرا کاروبار ہے جو یہ کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(3,188,'3:188','تم ان لوگوں کو عذاب سے محفوظ نہ سمجھو جو اپنے کرتُوتوں پر خوش ہیں اور چاہتے ہیں کہ ایسے کاموں کی تعریف انھیں حاصل ہو جوفی الواقع انہوں نےنہیں کیے ہیں۔ حقیقت میں ان کے لیے درد ناک سزا تیار ہے');
INSERT INTO "ur_maududi" VALUES(3,189,'3:189','زمین اور آسمان کا مالک اللہ ہے اور اس کی قدرت سب پر حاوی ہے');
INSERT INTO "ur_maududi" VALUES(3,190,'3:190','زمین اور آسمانوں کی پیدائش میں اور رات اور دن کے باری باری سے آنے میں ان ہوشمند لوگوں کےلیے بہت نشانیا ں ہیں');
INSERT INTO "ur_maududi" VALUES(3,191,'3:191','جو اٹھتے، بیٹھتے اور لیٹتے، ہر حال میں خدا کو یاد کرتے ہیں اور آسمان و زمین کی ساخت میں غورو فکر کرتے ہیں۔ ( وہ بے اختیار بول اٹھتے ہیں)”پروردگار! یہ سب کچھ تُو نے فضول اور بے مقصد نہیں بنایا ہے، تُو پاک ہے اس سے کہ عبث کا م کرے۔ پس اے رب! ہمیں دوزخ کے عذاب سے بچالے ، ');
INSERT INTO "ur_maududi" VALUES(3,192,'3:192','تو نے جسے دوزخ میں ڈالا اسے در حقیقت بڑی ذلت و رسوائی میں ڈال دیا، اور پھر ایسے ظالموں کا کوئی مدد گار نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(3,193,'3:193','مالک! ہم نے ایک پکار نے والے کو سنا جو ایمان کی طرف بلاتا تھا اور کہتا تھا کہ اپنے رب کو مانو۔ ہم نے اس کی دعوت قبول کر لی، پس اے ہمارے آقا!جو قُصور ہم سے ہوئے ہیں ان سے درگزر فرما، جو برائیاں ہم میں ہیں انھیں دور کردے اور ہمارا خاتمہ نیک لوگوں کے ساتھ کر');
INSERT INTO "ur_maududi" VALUES(3,194,'3:194','خدا وند! جو وعدے تُو نے اپنے رسولوں کے ذریعہ سے کیے ہیں ان کو ہمارے ساتھ پورا کر اور قیامت کےدن ہمیں رسوائی میں نہ ڈال ، بے شک تُو اپنے وعدے کے خلاف کرنے والا نہیں ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(3,195,'3:195','جواب میں ان کے رب نے فرمایا ”میں تم میں سے کسی کا عمل ضائع کرنے والا نہیں ہو ں۔ خواہ مرد ہو یا عورت، تم سب ایک دوسرے کے ہم جنس ہو۔ لہٰذا جن لوگوں نے میری خاطر اپنے وطن چھوڑے اور جو میری راہ میں اپنے گھروں سے نکالے گئے اور ستائے گئے اور میرے لیے لڑے اور مارے گئے ان کے سب قصور میں معاف کردوں گا اور انھیں ایسے باغوں میں داخل کر دوں گا جن کے نیچے نہریں بہتی ہوں گی۔ یہ ان کی جزا ہے اللہ کے ہاں اور بہتر ین جزا اللہ ہی کے پاس ہے“۔ ');
INSERT INTO "ur_maududi" VALUES(3,196,'3:196','اے نبیؐ! دنیا کے ملکوں میں خدا کے نافرمان لوگوں کی چلت پھرت تمہیں کسی دھوکے میں نہ ڈالے');
INSERT INTO "ur_maududi" VALUES(3,197,'3:197','یہ محض چند روزہ زندگی کا تھوڑا سا لطف ہے، پھر یہ سب جہنم میں جائیں گے جو بدترین جائے قرار ہے');
INSERT INTO "ur_maududi" VALUES(3,198,'3:198','برعکس اس کے جو لوگ اپنے رب سے ڈرتے ہوئے زندگی بسر کرتے ہیں ان کے لیے ایسے باغ ہیں جن کے نیچے نہریں بہتی ہیں، ان باغوں میں وہ ہمیشہ رہیں گے، اللہ کی طرف سے یہ سامان ضیافت ہے ان کے لیے، اور جو کچھ اللہ کے پاس ہے نیک لوگوں کے لیے وہی سب سے بہتر ہے');
INSERT INTO "ur_maududi" VALUES(3,199,'3:199','اہل کتاب میں بھی کچھ لوگ ایسے ہیں جو اللہ کو مانتے ہیں، اس کتاب پر ایمان لاتے ہیں جو تمہاری طرف بھیجی گئی ہے اوراُس کتاب پربھی ایمان رکھتے ہیں جو اس سے پہلے خود ان کی طرف بھیجی گئی تھی، اللہ کے آگے جھکے ہوئے ہیں، اور اللہ کی آیات کو تھوڑی سی قیمت پر بیچ نہیں دیتے ان کا اجر ان کے رب کے پاس ہے اور اللہ حساب چکانے میں دیر نہیں لگاتا');
INSERT INTO "ur_maududi" VALUES(3,200,'3:200','اے لوگو جو ایمان لائے ہو ، صبر سے کام لو، باطل پرستوں کے مقابلہ میں پامردی دکھاؤ، حق کی خدمت کے لیے کمر بستہ رہو، اور اللہ سے ڈرتے رہو ، امید ہے کہ فلاح پاؤ گے');
INSERT INTO "ur_maududi" VALUES(4,1,'4:1','لوگو ! اپنے رب سے ڈرو جس نے تم کو ایک جان سے پیدا کیا اور اُسی جان سے اس کا جوڑا بنایا اور ان دونوں سے بہت مرد و عورت دُنیا میں پھیلا دیے۔ اُس خدا سے ڈرو جس کا واسطہ دے کر تم ایک دُوسرے سے اپنے حق مانگتے ہو، اور رشتہ و قرابت کے تعلّقات کو بگاڑنے سے پرہیز کرو۔ یقین جانو کہ اللہ تم پر نگرانی کررہا ہے');
INSERT INTO "ur_maududi" VALUES(4,2,'4:2','یتیموں کے مال اُن کو واپس دو، اچھے مال کو بُرے مال سے نہ بدل لو، اور اُن کے مال اپنے مال کے ساتھ ملا کر نہ کھا جاوٴ، یہ بہت بڑا گناہ ہے');
INSERT INTO "ur_maududi" VALUES(4,3,'4:3','اور اگر تم یتیموں کے ساتھ بے انصافی کرنے سے ڈرتے ہو تو جو عورتیں تم کو پسند آئیں اُن میں سے دو دو، تین تین، چار چار سے نکاح کرلو۔ لیکن اگر تمہیں اندیشہ ہو کہ اُن کے ساتھ عدل نہ کرسکو گے تو پھر ایک ہی بیوی کرو یا اُن عورتوں کو زوجیّت میں لاوٴ جو تمہارے قبضہ میں آئی ہیں، بے انصافی سے بچنے کے لیے یہ زیادہ قرین ِصواب ہے');
INSERT INTO "ur_maududi" VALUES(4,4,'4:4','اور عورتوں کے مہر خوش دلی کے ساتھ (فرض جانتے ہوئے) ادا کرو، البتہ اگر وہ خود اپنی خوشی سے مہر کا کوئی حصّہ تمہیں معاف کردیں تو اُسے تم مزے سے کھاسکتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(4,5,'4:5','اور اپنے وہ مال جنہیں اللہ نے تمہارے لیے قیامِ زندگی کا ذریعہ بنایا ہے، نادان لوگوں کے حوالہ نہ کرو، البتہ انہیں کھانے اور پہننے کے لیے دو اور انہیں نیک ہدایت کرو۔ ');
INSERT INTO "ur_maududi" VALUES(4,6,'4:6','اور یتیموں کی آزمائش کرتے رہو یہاں تک کہ وہ نکاح کے قابل عمر کو پہنچ جائیں۔ پھر اگر تم اُن کے اندر اہلیت پاوٴ تو اُن کے مال اُن کے حوالے کردو۔ ایسا کبھی نہ کرنا کہ حدِّ انصاف سے تجاوز کرکے اِس خوف سے اُن کے مال جلدی جلدی کھا جاوٴ کہ وہ بڑے ہو کر اپنے حق کا مطالبہ کریں گے۔ یتیم کا جو سر پرست مال دار ہو وہ پرہیز گاری سے کام لے اور جو غریب ہو وہ معروف طریقہ سے کھائے۔ پھر جب اُن کے مال اُن کے حوالے کرنے لگو تو لوگوں کو اس پر گواہ بنالو، اور حساب لینے کے لیے اللہ کافی ہے');
INSERT INTO "ur_maududi" VALUES(4,7,'4:7','مردوں کے لیے اُس مال میں حصّہ ہے جو ماں باپ اور رشتہ داروں نے چھوڑا ہو، اور عورتوں کے لیے بھی اُس مال میں حصّہ ہے جو ماں باپ اور رشتہ داروں نے چھوڑا ہو، خواہ تھوڑا ہو یا بہت، اور یہ حصّہ(اللہ کی طرف سے) مقرر ہے');
INSERT INTO "ur_maududi" VALUES(4,8,'4:8','اور جب تقسیم کے موقع پر کنبہ کے لوگ اور یتیم اور مسکین آئیں تو اس مال میں سے ان کو بھی کچھ دو اور اُن کے ساتھ بھلے مانسوں کی سی بات کرو۔ ');
INSERT INTO "ur_maududi" VALUES(4,9,'4:9','لوگوں کو اس بات کا خیال کر کے ڈرنا چاہیے کہ اگر وہ خود اپنے پیچھے بے بس اولاد چھوڑتے تو مرتے وقت انہیں اپنے بچوں کے حق میں کیسے کچھ اندیشے لاحق ہوتے پس چاہیے کہ وہ خدا کا خوف کریں اور راستی کی بات کریں');
INSERT INTO "ur_maududi" VALUES(4,10,'4:10','جو لوگ ظلم کے ساتھ یتیموں کے مال کھاتے ہیں درحقیقت وہ اپنے پیٹ آگ سے بھرتے ہیں اور وہ ضرور جہنّم کی بھڑکتی ہوئی آگ میں جھونکے جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(4,11,'4:11','تمہاری اولاد کے بارے میں اللہ تمہیں ہدایت کرتا ہے کہ : مرد کا حصّہ دو عورتوں کے برابر ہے،اگر (میّت کی وارث) دو سے زائد لڑکیاں ہو ں تو انہیں ترکے کا دو تہائی دیا جائے۔ اور اگر ایک ہی لڑکی وارث ہو تو آدھا ترکہ اس کا ہے۔اگر میّت صاحبِ اولاد ہو تو اس کے والدین میں سے ہر ایک کو ترکے کا چھٹا حصّہ مِلنا چاہیے۔ اور اگر وہ صاحبِ اولاد نہ ہو اور والدین ہی اس کے وارث ہوں تو ماں کو تیسرا حصّہ دیا جائے۔ اور اگر میّت کے بھائی بہن بھی ہوں تو ماں چھٹے حصّہ کی حق دار ہوگی۔ (یہ سب حصّے اُس وقت نکالے جائیں گے) جبکہ وصیّت جو میّت نے کی ہو پُوری کردی جائے اور قرض جو اُس پر ہو ادا کردیا جائے۔ تم نہیں جانتے کہ تمہارے ماں باپ اور تمہاری اولاد میں سے کون بلحاظ نفع تم سے قریب تر ہے۔ یہ حصّے اللہ نے مقرر کردیے ہیں، اور اللہ یقیناً سب حقیقتوں سے واقف اور ساری مصلحتوں کا جاننے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,12,'4:12','اور تمہاری بیویوں نے جو کچھ چھوڑا ہو اس کا آدھا حصّہ تمہیں ملے گا اگر وہ بے اولاد ہوں، ورنہ اولاد ہونے کی صُورت میں ترکہ کا ایک چوتھائی حصّہ تمہارا ہے جبکہ وصیّت جو انہوں نے کی ہو پوری کردی جائے، اور قرض جو اُنہوں نے چھوڑا ہو ادا کردیا جائے۔ اور وہ تمہارے ترکہ میں سے چوتھائی کی حق دار ہوں گی اگر تم بے اولاد ہو، ورنہ صاحبِ اولاد ہونے کی صُورت میں اُن کا حصّہ آٹھواں ہوگا، بعد اس کے کہ جو وصیّت تم نے کی ہو وہ پُوری کردی جائے اور جو قرض تم نے چھوڑا ہو وہ ادا کردیا جائے۔اور اگر وہ مرد یا عورت (جس کی میراث تقسیم طلب ہے) بے اولاد بھی ہو اور اس کے ماں باپ بھی زندہ نہ ہوں، مگر اس کا ایک بھائی یا ایک بہن موجود ہوتو بھائی اور بہن ہر ایک کو چھٹا حصّہ ملے گا، اور بھائی بہن ایک سے زیادہ ہوں تو کُل ترکہ کے ایک تہائی میں وہ سب شریک ہوں گے، جبکہ وصیّت جو کی گئی ہو پوری کردی جائے، اور قرض جو میّت نے چھوڑا ہو ادا کردیا جائے، بشرطیکہ وہ ضرر رساں نہ ہو۔ یہ حکم ہے اللہ کی طرف سے اور اللہ دانا و بینا اور نرم خُو ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,13,'4:13','یہ اللہ کی مقرر کی ہوئی حدیں ہیں جو اللہ اور اُس کے رسول کی اطاعت کرے گا اُسے اللہ ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی اور ان باغوں میں وہ ہمیشہ رہے گا اور یہی بڑی کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(4,14,'4:14','اور جو اللہ اور اُس کے رسول کی نافرمانی کرے گا اور اس کی مقرر کی ہوئی حدوں سے تجاوز کر جائے گا اُسے اللہ آگ میں ڈالے گا جس میں وہ ہمیشہ رہے گا اور اس کے لیے رسوا کن سزا ہے');
INSERT INTO "ur_maududi" VALUES(4,15,'4:15','تمہاری عورتوں میں سے جو بد کاری کی مرتکب ہوں اُن پر اپنے میں سے چار آدمیوں کی گواہی لو، اور اگر چار آدمی گواہی دے دیں تو ان کو گھروں میں بند رکھو یہاں تک کہ انہیں موت آ جائے یا اللہ اُن کے لیے کوئی راستہ نکال دے');
INSERT INTO "ur_maududi" VALUES(4,16,'4:16','اور تم میں سے جو اس فعل کا ارتکاب کریں اُن دونوں کو تکلیف دو، پھر اگر وہ توبہ کریں اور اپنی اصلاح کرلیں تو انہیں چھوڑ دو کہ اللہ بہت توبہ قبول کرنے والا اور رحم فرمانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,17,'4:17','ہاں یہ جان لو کہ اللہ پر توبہ کی قبولیت کا حق اُنہی لوگوں کے لیے ہے جو نادانی کی وجہ سے کوئی برا فعل کر گزرتے ہیں اور اس کے بعد جلدی ہی توبہ کر لیتے ہیں ایسے لوگوں پر اللہ اپنی نظر عنایت سے پھر متوجہ ہو جاتا ہے اور اللہ ساری باتوں کی خبر رکھنے والا اور حکیم و دانا ہے');
INSERT INTO "ur_maududi" VALUES(4,18,'4:18','مگر توبہ اُن لوگوں کے لیے نہیں ہے جو بُرے کام کیے چلے جاتے ہیں یہاں تک کہ جب ان میں سے کسی کی موت کا وقت آ جاتا ہے اُس وقت وہ کہتا ہے کہ اب میں نے توبہ کی۔ اور اسی طرح توبہ اُن کے لیے بھی نہیں ہے جو مرتے دم تک کافر رہیں۔ ایسے لوگوں کے لیے تو ہم نے درد ناک سزا تیار کر رکھی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,19,'4:19','اے لوگو جو ایمان لائے ہو، تمہارے لیے یہ حلال نہیں ہے کہ زبردستی عورتوں کے وارث بن بیٹھو۔ اور نہ یہ حلال ہے کہ انہیں تنگ کرکے اُس مَہر کا کچھ حصّہ اُڑا لینے کی کوشش کرو جو تم انہیں دے چکے ہو۔ ہاں اگر وہ کسی صریح بدچلنی کی مرتکب ہوں (تو ضرور تمہیں تنگ کرنے کا حق ہے)۔ ان کے ساتھ بھلے طریقے سے زندگی بسر کرو۔ اگر وہ تمہیں ناپسند ہوں تو ہوسکتا ہے کہ ایک چیز تمہیں پسند نہ ہو مگر اللہ نے اُسی میں بہت کچھ بَھلائی رکھ دی ہو۔ ');
INSERT INTO "ur_maududi" VALUES(4,20,'4:20','اور اگر تم ایک بیوی کی جگہ دوسری بیوی لے آنے کا ارادہ ہی کر لو تو خواہ تم نے اُسے ڈھیر سا مال ہی کیوں نہ دیا ہو، اس میں سے کچھ واپس نہ لینا کیا تم اُسے بہتان لگا کر اور صریح ظلم کر کے واپس لو گے؟');
INSERT INTO "ur_maududi" VALUES(4,21,'4:21','اور آخر تم اُسے کس طرح لے لوگے جب کہ تم ایک دُوسرے سے لُطف اندوز ہو چکے ہو اور وہ تم سے پختہ عہد لے چکی ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(4,22,'4:22','اور جن عورتوں سے تمہارے باپ نکاح کر چکے ہوں اُن سے ہرگز نکاح نہ کرو، مگر جو پہلے ہوچکا سو ہوچکا۔ درحقیقت یہ ایک بے حیائی کا فعل ہے، ناپسندیدہ ہے اور بُرا چلن ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,23,'4:23','تم پر حرام کی گئیں تمہاری مائیں، بیٹیاں، بہنیں، پُھوپھیاں، خالائیں، بھتیجیاں، بھانجیاں، اور تمہاری وہ مائیں جنہوں نے تم کو دُودھ پلایا ہو، اور تمہاری دُودھ شریک بہنیں، اور تمہاری بیویوں کی مائیں، اور تمہاری بیویوں کی لڑکیاں جنہوں نے تمہاری گودوں میں پرورش پائی ہے۔۔۔۔ اُن بیویوں کی لڑکیاں جن سے تمہارا تعلّقِ زن و شو ہوچکا ہو۔ورنہ اگر (صرف نکاح ہوا ہو اور) تعلقِ زن و شو نہ ہوٴا ہو تو (انہیں چھوڑ کر ان کی لڑکیوں سے نکاح کر لینے میں) تم پرکوئی مواخذہ نہیں ہے۔۔۔۔ اور تمہارے اُن بیٹوں کی بیویاں جو تمہاری صُلب سے ہوں۔ اور یہ بھی تم پر حرام کیا گیا ہے کہ ایک نکاح میں دو بہنوں کو جمع کرو، مگر جو پہلے ہو گیا سو ہو گیا، اللہ بخشنے والا اور رحم کرنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,24,'4:24','اور وہ عورتیں بھی تم پر حرام ہیں جو کسی دُوسرے کے نکاح میں ہوں (مُحْصَنَات) البتہ ایسی عورتیں اس سے مستثنٰی ہیں جو (جنگ میں) تمہارے ہاتھ آئیں۔ یہ اللہ کا قانون ہے جس کی پابندی تم پر لازم کردی گئی ہے۔اِن کے ما سوا جتنی عورتیں ہیں انہیں اپنے اموال کے ذریعہ سے حاصل کرنا تمہارے لیے حلال کردیا گیا ہے، بشرطیکہ حصارِ نکاح میں اُن کو محفوظ کرو، نہ یہ کہ آزاد شہوت رانی کرنے لگو۔ پھر جو ازدواجی زندگی کا لُطف تم ان سے اُٹھاوٴ اس کے بدلے اُن کے مَہر بطور فرض کے ادا کرو، البتّہ مَہر کی قرار داد ہو جانے کے بعد آپس کی رضامندی سے تمہارے درمیان اگر کوئی سمجھوتہ ہوجائے تو اس میں کوئی حرج نہیں، اللہ علیم اور دانا ہے');
INSERT INTO "ur_maududi" VALUES(4,25,'4:25','اور جو شخص تم میں سے اتنی مقدرت نہ رکھتا ہو کہ خاندانی مسلمان عورتوں (مُحْصَنَات) سے نکاح کرسکے اسے چاہیے کہ تمہاری اُن لونڈیوں میں سے کسی کے ساتھ نکاح کرلے جو تمہارے قبضہ میں ہوں اور مومنہ ہوں۔ اللہ تمہارے ایمانوں کا حال خُوب جانتا ہے، تم سب ایک ہی گروہ کے لوگ ہو، لہٰذا اُن کے سرپرستوں کی اجازت سے اُن کے ساتھ نکاح کرلو اور معرُوف طریقہ سے اُن کے مہر ادا کردو، تاکہ وہ حصارِ نکاح میں محفوظ (مُحْصَنَات) ہو کر رہیں، آزاد شہوت رانی نہ کرتی پھریں اور نہ چوری چھُپے آشنائیاں کریں۔ پھر جب وہ حصارِ نکاح میں محفوظ ہوجائیں اور اس کے بعد کسی بد چلنی کی مرتکب ہوں تو ان پر اس سزا کی بنسبت آد ھی سزا ہے۔ جو خاندانی عورتوں (مُحصنَات) کے لیے مقرر ہے۔ یہ سہولت تم میں سے اُن لوگوں کے لیے پیدا کی گئی ہے جن کو شادی نہ کرنے سے بندِ تقویٰ کے ٹوٹ جانے کا اندیشہ ہو۔ لیکن اگر تم صبر کرو تو یہ تمہارے لیے بہتر ہے، اور اللہ بخشنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(4,26,'4:26','اللہ چاہتا ہے کہ تم پر اُن طریقوں کو واضح کرے اور اُنہی طریقوں پر تمہیں چلائے جن کی پیروی تم سے پہلے گزرے ہوئے صلحاء کرتے تھے۔ وہ اپنی رحمت کے ساتھ تمہاری طرف متوجّہ ہونے کا ارادہ رکھتا ہے، اور وہ علیم بھی ہے اور دانا بھی۔ ');
INSERT INTO "ur_maududi" VALUES(4,27,'4:27','ہاں، اللہ تو تم پر رحمت کے ساتھ توجّہ کرنا چاہتا ہے مگر جو لوگ خود اپنی خواہشاتِ نفس کی پیروی کررہے ہیں وہ چاہتے ہیں کہ تم راہِ راست سے ہٹ کر دُور نکل جاوٴ۔ ');
INSERT INTO "ur_maududi" VALUES(4,28,'4:28','اللہ تم پر سے پابندیوں کو ہلکا کرنا چاہتا ہے کیونکہ انسان کمزور پیدا کیا گیا ہے');
INSERT INTO "ur_maududi" VALUES(4,29,'4:29','اے لوگو جو ایمان لائے ہو، آپس میں ایک دُوسرے کے مال باطل طریقوں سے نہ کھاوٴ، لین دین ہونا چاہیے آپس کی رضا مندی سے۔ اور اپنے آپ کو قتل نہ کرو۔ یقین مانو کہ اللہ تمہارے اُوپر مہربان ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,30,'4:30','جو شخص ظلم و زیادتی کے ساتھ ایسا کرے گا اُس کو ہم ضرور آگ میں جھونکیں گے اور یہ اللہ کے لیے کوئی مشکل کام نہیں ہے');
INSERT INTO "ur_maududi" VALUES(4,31,'4:31','اگر تم اُن بڑے بڑے گناہوں سے پرہیز کرتے رہو جن سے تمہیں منع کیا جارہا ہے تو تمہاری چھوٹی موٹی بُرائیوں کو ہم تمہارے حساب سے ساقط کردیں گے اور تم کو عزّت کی جگہ داخل کریں گے');
INSERT INTO "ur_maududi" VALUES(4,32,'4:32','اور جو کچھ اللہ نے تم میں سے کسی کو دُوسروں کے مقابلے میں زیادہ دیا ہے اس کی تمنّا نہ کرو۔ جو کچھ مَردوں نے کمایا ہے اُس کے مطابق ان کا حصّہ ہے اور جو کچھ عورتوں نے کمایا ہے اس کے مطابق اُن کا حصّہ۔ ہاں اللہ سے اس کے فضل کی دُعا مانگتے رہو، یقیناً اللہ ہر چیز کا عِلم رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,33,'4:33','اور ہم نے ہر اُس ترکے کے حق دار مقرر کردیے ہیں جو والدین اور رشتہ دار چھوڑیں۔ اب رہے وہ لوگ جن سے تمہارے عہدو پیمان ہوں تو ان کا حصّہ انہیں دو، یقیناً اللہ ہر چیز پر نگراں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,34,'4:34','مرد عورتوں پر قوّام ہیں، اس بنا پر کہ اللہ نے اُن میں سے ایک کو دُوسرے پر فضیلت دی ہے، اور اس بنا پر کہ مرد اپنے مال خرچ کرتے ہیں۔ پس جو صالح عورتیں ہیں وہ اطاعت شعار ہوتی ہیں اور مردوں کے پیچھے اللہ کی حفاظت و نگرانی میں اُن کے حقوق کی حفاظت کرتی ہیں۔ اور جن عورتوں سے تمہیں سرکشی کا اندیشہ ہو انہیں سمجھاوٴ، خواب گاہوں میں اُن سے علیحدہ رہو اور مارو، پھر اگر وہ تمہاری مطیع ہو جائیں تو خواہ مخواہ ان پر دست درازی کے لیے بہانے تلاش نہ کرو، یقین رکھو کہ اُوپر اللہ موجود ہے جو بڑا اور بالاتر ہے');
INSERT INTO "ur_maududi" VALUES(4,35,'4:35','اور اگر تم لوگوں کو کہیں میاں اور بیوی کے تعلّقات بگڑجانے کا اندیشہ ہو تو ایک حَکَم مرد کے رشتہ داروں میں سے اور ایک عورت کے رشتہ داروں میں سے مقرر کرو، وہ دونوں اصلاح کرنا چاہیں گے تو اللہ اُن کے درمیان موافقت کی صُورت نکال دے گا، اللہ سب کچھ جانتا ہے اور باخبر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,36,'4:36','اور تم سب اللہ کی بندگی کرو، اُس کے ساتھ کسی کو شریک نہ بناوٴ، ماں باپ کے ساتھ نیک برتاوٴ کرو، قرابت داروں اور یتیموں اور مسکینوں کے ساتھ حُسنِ سلوک سے پیش آوٴ، اور پڑوسی رشتہ دار سے، اجنبی ہمسایہ سے، پہلو کے ساتھی اور مسافر سے، اور اُن لونڈی غلاموں سے جو تمہارے قبضہ میں ہوں، احسان کا معاملہ رکھو، یقین جانو اللہ کسی ایسے شخص کو پسند نہیں کرتا جو اپنے پندار میں مغرور ہو اور اپنی بڑائی پر فخر کرے');
INSERT INTO "ur_maududi" VALUES(4,37,'4:37','اور ایسے لوگ بھی اللہ کو پسند نہیں ہیں جو کنجوسی کرتے ہیں اور دُوسروں کو بھی کنجوسی کی ہدایت کرتے ہیں اور جو کچھ اللہ نے اپنے فضل سے انہیں دیا ہے اُسے چُھپاتے ہیں۔ ایسے کافِر نعمت لوگوں کے لیے ہم نے رسوا کُن عذاب مہیّا کر رکھا ہے');
INSERT INTO "ur_maududi" VALUES(4,38,'4:38','اور وہ لوگ بھی اللہ کو ناپسند ہیں جو اپنے مال محض لوگوں کو دکھانے کیلیے خرچ کرتے ہیں اور در حقیقت نہ اللہ پر ایمان رکھتے ہیں نہ روز آخر پر سچ یہ ہے کہ شیطان جس کا رفیق ہوا اُسے بہت ہی بری رفاقت میسر آئی');
INSERT INTO "ur_maududi" VALUES(4,39,'4:39','آخر اِن لوگوں پر کیا آفت آ جاتی اگر یہ اللہ اور روز آخر پر ایمان رکھتے اور جو کچھ اللہ نے دیا ہے اس میں سے خرچ کرتے اگر یہ ایسا کرتے تو اللہ سے ان کی نیکی کا حال چھپا نہ رہ جاتا');
INSERT INTO "ur_maududi" VALUES(4,40,'4:40','اللہ کسی پر ذرہ برابر بھی ظلم نہیں کرتا اگر کوئی ایک نیکی کرے تو اللہ اُسے دو چند کرتا ہے اور پھر اپنی طرف سے بڑا اجر عطا فرماتا ہے');
INSERT INTO "ur_maududi" VALUES(4,41,'4:41','پھر سوچو کہ اُس وقت یہ کیا کریں گے جب ہم ہر اُمّت میں سے ایک گواہ لائیں گے اور ان لوگوں پر تمہیں (یعنی محمد صلى الله عليه وسلم کو) گواہ کی حیثیت سے کھڑا کریں گے۔ ');
INSERT INTO "ur_maududi" VALUES(4,42,'4:42','اُس وقت وہ سب لوگ جنہوں نے رسولؐ کی بات نہ مانی اور اس کی نافرمانی کرتے رہے، تمنا کریں گے کہ کاش زمین پھٹ جائے اور وہ اس میں سما جائیں وہاں یہ اپنی کوئی بات اللہ سے نہ چھپا سکیں گے');
INSERT INTO "ur_maududi" VALUES(4,43,'4:43','اے لوگو جو ایمان لائے ہو، جب تم نشے کی حالت میں ہو تو نماز کے قریب نہ جاوٴ۔ نماز اُس وقت پڑھنی چاہیے جب تم جانو کہ کیا کہہ رہے ہو۔ اور اسی طرح جَنابت کی حالت میں بھی نماز کے قریب نہ جاوٴ جب تک کہ غسل نہ کرلو، اِلّا یہ کہ راستہ سے گزرتے ہو۔ اور اگر کبھی ایسا ہو کہ تم بیمار ہو، یا سفر میں ہو، یا تم میں سے کوئی شخص رفع حاجت کرکے آئے، یا تم نے عورتوں سے لَمس کیا ہو، اور پھر پانی نہ ملے تو پاک مٹی سے کام لو اور اس سے اپنے چہروں اور ہاتھوں پر مسح کرلو، بے شک اللہ نرمی سے کام لینے والا اور بخشش فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(4,44,'4:44','ہم نے اُن لوگوں کو بھی دیکھا جنہیں کتاب کے علم کا کچھ حصّہ دیا گیا ہے؟ وہ خود ضلالت کے خریدار بنے ہوئے ہیں اور چاہتے ہیں کہ تم بھی راہ گم کردو');
INSERT INTO "ur_maududi" VALUES(4,45,'4:45','اللہ تمہارے دشمنوں کو خوب جانتا ہے اور تمہاری حمایت و مدد گاری کے لیے اللہ ہی کافی ہے');
INSERT INTO "ur_maududi" VALUES(4,46,'4:46','جو لوگ یہودی بن گئے ہیں اُن میں کچھ لوگ ہیں جو الفاظ کو اُن کے محل سے پھیردیتے ہیں، اور دینِ حق کے خلاف نیش زنی کرنے کے لیے اپنی زبانوں کو توڑ موڑ کر کہتے ہیں سَمِعْنَا وَ عَصَیْنَا اور اِسمَعْ غَیْرَ مُسْمَعٍ اور رَاعِنَا ۔ حالانکہ اگر وہ کہتے سَمِعْنَا وَ اَطَعْنَا، اور اِسمَعْ اور اُنْظُرْنَا تو یہ انہی کے لیے بہتر تھا اور زیادہ راستبازی کا طریقہ تھا۔ مگر ان پر تو ان کی باطل پرستی کی بدولت اللہ کی پِھٹکار پڑی ہوئی ہے اس لیے وہ کم ہی ایمان لاتے ہیں');
INSERT INTO "ur_maududi" VALUES(4,47,'4:47','اے وہ لوگو جنہیں کتاب دی گئی تھی! مان لو اُس کتاب کو جو ہم نے اب نازل کی ہے اور جو اُس کتاب کی تصدیق و تائید کرتی ہے جو تمہارے پاس پہلے سے موجود تھی۔ اس پر ایمان لے آوٴ قبل اس کے کہ ہم چہرے بگاڑ کر پیچھے پھیر دیں یا ان کو اسی طرح لعنت زدہ کردیں جس طرح سبت والوں کے ساتھ ہم نے کیا تھا، اور یاد رکھو کہ اللہ کا حکم نافذ ہو کر رہتا ہے');
INSERT INTO "ur_maududi" VALUES(4,48,'4:48','اللہ بس شرک ہی کو معاف نہیں کرتا، اِس کے ماسوا دُوسرے جس قدر گناہ ہیں وہ جس کے لیے چاہتا ہے معاف کردیتا ہے۔ اللہ کے ساتھ جس نے کسی اور کو شریک ٹھیرایا اُس نے تو بہت ہی بڑا جُھوٹ تصنیف کیا اور بڑے سخت گناہ کی بات کی');
INSERT INTO "ur_maududi" VALUES(4,49,'4:49','تم نے اُن لوگوں کو بھی دیکھا جو بہت اپنی پاکیز گی نفس کا دم بھرتے ہیں؟ حالانکہ پاکیزگی تو اللہ ہی جسے چاہتا ہے عطا کرتا ہے، اور (انہیں جو پاکیزگی نہیں ملتی تو در حقیقت) ان پر ذرہ برابر بھی ظلم نہیں کیا جاتا');
INSERT INTO "ur_maududi" VALUES(4,50,'4:50','دیکھو تو سہی، یہ اللہ پر بھی جھوٹے افترا گھڑنے سے نہیں چوکتے اور ان کے صریحاً گناہ گا ر ہونے کے لیے یہی ایک گناہ کافی ہے');
INSERT INTO "ur_maududi" VALUES(4,51,'4:51','کیا تم نے اُن لوگوں کو نہیں دیکھا جنہیں کتاب کے عِلم میں سے کچھ حصّہ دیا گیا ہے اور اُن کا حال یہ ہے کہ جِبْت اور طاغوت کو مانتے ہیں اور کافروں کے متعلق کہتے ہیں کہ ایمان لانے والوں سے تو یہی زیادہ صحیح راستے پر ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(4,52,'4:52','ایسے ہی لوگ ہیں جن پر اللہ نے لعنت کی ہے اور جس پر اللہ لعنت کر دے پھر تم اُس کا کوئی مدد گار نہیں پاؤ گے');
INSERT INTO "ur_maududi" VALUES(4,53,'4:53','کیا حکومت میں اُن کا کوئی حصّہ ہے؟ اگر ایسا ہوتا تو یہ دُوسروں کو ایک پُھوٹی کوڑی تک نہ دیتے۔ ');
INSERT INTO "ur_maududi" VALUES(4,54,'4:54','پھر یا یہ دُوسروں سے اس لیے حسد کرتے ہیں کہ اللہ نے انہیں اپنے فضل سے نواز دیا؟ اگر یہ بات ہے تو انہیں معلوم ہوکہ ہم نے تو ابراہیم ؑ کی اولاد کو کتاب اور حکمت عطا کی اور ملکِ عظیم بخش دیا، ');
INSERT INTO "ur_maududi" VALUES(4,55,'4:55','مگر ان میں سے کوئی اس پر ایمان لایا اور کوئی اس سے منہ موڑگیا، اور منہ موڑنے والوں کے لیے تو بس جہنّم کی بھڑکتی ہوئی آگ ہی کافی ہے');
INSERT INTO "ur_maududi" VALUES(4,56,'4:56','جن لوگوں نے ہماری آیات کو ماننے سے انکار کر دیا ہے انہیں بالیقین ہم آگ میں جھونکیں گے اور جب ان کے بدن کی کھال گل جائے گی تو اس کی جگہ دوسری کھال پیدا کر دیں گے تاکہ وہ خوب عذاب کا مزہ چکھیں، اللہ بڑی قدرت رکھتا ہے اور اپنے فیصلوں کو عمل میں لانے کی حکمت خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(4,57,'4:57','اور جن لوگوں نے ہماری آیات کو مان لیا اور نیک عمل کیے اُن کو ہم ایسے باغوں میں داخل کریں گے جن کے نیچے نہریں بہتی ہوں گی، جہاں وہ ہمیشہ ہمیشہ رہیں گے اور اُن کو پاکیزہ بیویاں ملیں گی اور انہیں ہم گھنی چھاؤں میں رکھیں گے');
INSERT INTO "ur_maududi" VALUES(4,58,'4:58','مسلمانو ! اللہ تمہیں حکم دیتا ہے کہ امانتیں اہلِ امانت کے سپرد کرو، اور جب لوگوں کے درمیان فیصلہ کرو تو عدل کے ساتھ کرو، اللہ تم کو نہایت عمدہ نصیحت کرتا ہے اور یقیناً اللہ سب کچھ سُنتا اور دیکھتا ہے');
INSERT INTO "ur_maududi" VALUES(4,59,'4:59','اے لوگو جو ایمان لائے ہو، اطاعت کرو اللہ کی اور اطاعت کرو رسول کی اور اُن لوگوں کی جو تم میں سے صاحبِ امر ہوں، پھر اگر تمہارے درمیان کسی معاملہ میں نزاع ہوجائے تو اسے اللہ اور رسُول کی طرف پھیردو اگر تم واقعی اللہ اور روزِ آخر پر ایمان رکھتے ہو۔ یہی ایک صحیح طریقِ کار ہے اور انجام کے اعتبار سے بھی بہتر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,60,'4:60','اے نبی ! تم نے دیکھا نہیں اُن لوگوں کو جو دعوٰی تو کرتے ہیں کہ ہم ایمان لائے ہیں اُس کتاب پر جو تمہاری طرف نازل کی گئی ہے اور ان کتابوں پر جو تم سے پہلے نازل کی گئی تھیں مگر چاہتے یہ ہیں کہ اپنے معاملات کا فیصلہ کرانے کے لیے طاغوت کی طرف رُجوع کریں، حالانکہ انہیں طاغوت سے کفر کرنے کا حکم دیا گیا تھا۔۔ ۔۔شیطان انہیں بھٹکا کر راہِ راست سے بہت دُور لے جانا چاہتا ہے');
INSERT INTO "ur_maududi" VALUES(4,61,'4:61','اور جب ان سے کہا جاتا ہے کہ آوٴ اُس چیز کی طرف جو اللہ نے نازل کی ہے اور آوٴ رسول ؐ کی طرف تو ان منافقوں کو تم دیکھتے ہو کہ یہ تمہاری طرف آنے سے کتراتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(4,62,'4:62','پھر اس وقت کیا ہوتا ہے جب اِن کے اپنے ہاتھوں کی لائی ہوئی مصیبت ان پر آ پڑتی ہے؟ اُس وقت یہ تمہارے پاس قسمیں کھاتے ہوئے آتے ہیں اور کہتے ہیں کہ خدا کی قسم ہم تو صرف بَھلائی چاہتے تھے اور ہماری نیت تو یہ تھی کہ فریقین میں کسی طرح موافقت ہوجائے');
INSERT INTO "ur_maududi" VALUES(4,63,'4:63','اللہ جانتا ہے جو کچھ ان کے دلوں میں ہے، ان سے تعرض مت کرو، انہیں سمجھاؤ اور ایسی نصیحت کرو جو ان کے دلوں میں اتر جائے');
INSERT INTO "ur_maududi" VALUES(4,64,'4:64','(انہیں بتاوٴ کہ) ہم نے جو رسول بھی بھیجا ہے اسی لیے بھیجا ہے کہ اذنِ خداوندی کی بنا پر اس کی اطاعت کی جائے۔ اگر انہوں نے یہ طریقہ اختیار کیا ہوتا کہ جب یہ اپنے نفس پر ظلم کر بیٹھے تھے تو تمہارے پاس آجاتے اور اللہ سے معافی مانگتے، اور رسول بھی ان کے لیے معافی کی درخواست کرتا، تو یقیناً اللہ کو بخشنے والا اور رحم کرنے والا پاتے');
INSERT INTO "ur_maududi" VALUES(4,65,'4:65','نہیں، اے محمد ؐ !تمہارے رب کی قسم یہ کبھی مومن نہیں ہوسکتے جب تک کہ اپنے باہمی اِختلافات میں یہ تم کو فیصلہ کرنے والا نہ مان لیں، پھر جو کچھ تم فیصلہ کرو اس پر اپنے دلوں میں بھی کوئی تنگی نہ محسُوس کریں، بلکہ سربسر تسلیم کرلیں۔ ');
INSERT INTO "ur_maududi" VALUES(4,66,'4:66','اگر ہم نے انہیں حکم دیا ہوتا کہ اپنے آپ کو ہلاک کردو یا اپنے گھروں سے نکل جاوٴ تو ان میں سے کم ہی آدمی اس پر عمل کرتے۔ حالانکہ جو نصیحت انہیں کی جاتی ہے اگر یہ اس پر عمل کرتے تو یہ ان کے لیے زیادہ بہتری اور زیادہ ثابت قدمی کا موجب ہوتا ');
INSERT INTO "ur_maududi" VALUES(4,67,'4:67','اور جب یہ ایسا کرتے تو ہم انہیں اپنی طرف سے بہت بڑا اجر دیتے');
INSERT INTO "ur_maududi" VALUES(4,68,'4:68','اور انہیں سیدھا راستہ دکھادیتے۔ ');
INSERT INTO "ur_maududi" VALUES(4,69,'4:69','جو اللہ اور رسُول کی اطاعت کرے گا وہ ان لوگوں کے ساتھ ہوگا جن پر اللہ نے انعام فرمایا ہے یعنی انبیاء اور صدّیقین اور شہداء اور صالحین۔ کیسے اچّھے ہیں یہ رفیق جو کسی کو میسّر آئیں۔ ');
INSERT INTO "ur_maududi" VALUES(4,70,'4:70','یہ حقیقی فضل ہے جو اللہ کی طرف سے ملتا ہے اور حقیقت جاننے کے لیے بس اللہ ہی کا علم کافی ہے');
INSERT INTO "ur_maududi" VALUES(4,71,'4:71','اے لوگو جو ایمان لائے ہو، مقابلہ کے لیے ہر وقت تیار رہو، پھر جیسا موقع ہو الگ الگ دستوں کی شکل میں نکلویا اکٹھے ہوکر');
INSERT INTO "ur_maududi" VALUES(4,72,'4:72','ہاں، تم میں کوئی کوئی آدمی ایسا بھی ہےجو لڑائی سے جی چُراتا ہے، اگر تم پر کوئی مصیبت آئے تو کہتا ہے اللہ نے مجھ پربڑا فضل کیا کہ میں اِن لوگوں کے ساتھ نہ گیا');
INSERT INTO "ur_maududi" VALUES(4,73,'4:73','اور اگر اللہ کی طرف سے تم پر فضل ہو تو کہتا ہے اور اس طرح کہتا ہے کہ گویا تمہارے اور اس کے درمیان محبت کا تو کوئی تعلق تھا ہی نہیں کہ کاش میں بھی ان کے ساتھ ہوتا تو بڑا کام بن جاتا');
INSERT INTO "ur_maududi" VALUES(4,74,'4:74','(ایسے لوگوں کو معلوم ہوکہ) اللہ کی راہ میں لڑنا چاہیے اُن لوگوں کو جو آخرت کے بدلے دُنیا کی زندگی کو فروخت کردیں، پھر جو اللہ کی راہ میں لڑے گا اور مارا جائے گا یا غالب رہے گا اُسے ضرور ہم اجرِ عظیم عطا کریں گے');
INSERT INTO "ur_maududi" VALUES(4,75,'4:75','آخر کیا وجہ ہے کہ تم اللہ کی راہ میں اُن بے بس مردوں، عورتوں اور بچّوں کی خاطر نہ لڑو جو کمزور پاکر دبالیے گئے ہیں اور فریاد کررہے ہیں کہ خدا یا ہم کو اس بستی سے نکال جس کے باشندے ظالم ہیں، اور اپنی طرف سے ہمارا کوئی حامی و مددگار پیدا کردے۔ ');
INSERT INTO "ur_maududi" VALUES(4,76,'4:76','جن لوگوں نے ایمان کا راستہ اختیار کیا ہے، وہ اللہ کی راہ میں لڑتے ہیں جنہوں نے کفر کا راستہ اختیار کیا ہے، وہ طاغوت کی راہ میں لڑتے ہیں، پس شیطان کے ساتھیوں سے لڑو اور یقین جانو کہ شیطان کی چالیں حقیقت میں نہایت کمزور ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(4,77,'4:77','تم نے اُن لوگوں کو بھی دیکھا جن سے کہا گیا تھا کہ اپنے ہاتھ روکے رکھو اور نماز قائم کرو اور زکوٰة دو؟ اب جو انہیں لڑائی کا حکم دیا گیا تو ان میں سے ایک فریق کا حال یہ ہے کہ لوگوں سے ایسا ڈر رہے ہیں جیسا خدا سے ڈرنا چاہیے یا کچھ اس سے بھی بڑھ کر۔ کہتے ہیں خدایا! یہ ہم پر لڑائی کا حکم کیوں لکھ دیا؟ کیوں نہ ہمیں ابھی کچھ اور مُہلت دی؟ ان سے کہو، دنیا کا سرمایہ زندگی تھوڑا ہے، اور آخرت ایک خدا ترس انسان کے لیے زیادہ بہتر ہے، اور تم پر ظلم ایک شمّہ برابر بھی نہ کیا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(4,78,'4:78','رہی موت، تو جہاں بھی تم ہو وہ بہرحال تمہیں آکر رہے گی خواہ تم کیسی ہی مضبُوط عمارتوں میں ہو۔اگر انہیں کوئی فائدہ پہنچتا ہے تو کہتے ہیں یہ اللہ کی طرف سے ہے، اور اگر کوئی نقصان پہنچتا ہے تو کہتے ہیں یہ تمہاری بدولت ہے۔ کہو، سب کچھ اللہ ہی کی طرف سے ہے۔ آخر ان لوگوں کو کیا ہو گیا ہے کہ کوئی بات ان کی سمجھ میں نہیں آتی');
INSERT INTO "ur_maududi" VALUES(4,79,'4:79','اے انسان! تجھے جو بھلائی بھی حاصل ہوتی ہے اللہ کی عنایت سے ہوتی ہے، اور جو مصیبت تجھ پر آتی ہے وہ تیرے اپنے کسب و عمل کی بدولت ہے اے محمدؐ! ہم نے تم کو لوگوں کے لیے رسول بنا کر بھیجا ہے اوراس پر خدا کی گواہی کافی ہے');
INSERT INTO "ur_maududi" VALUES(4,80,'4:80','جس نے رسول کی اطاعت کی اس نے دراصل خدا کی اطاعت کی۔ اور جو مُنہ موڑ گیا، تو بہرحال ہم نے تمہیں ان لوگوں پر پاسبان بناکر تو نہیں بھیجا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,81,'4:81','وہ منہ پر کہتے ہیں کہ ہم مطیع فرمان ہیں مگر جب تمہارے پاس سے نکلتے ہیں تو ان میں سے ایک گروہ راتوں کو جمع ہو کر تمہاری باتوں کے خلاف مشورے کرتا ہے اللہ ان کی یہ ساری سرگوشیاں لکھ رہا ہے تم ان کی پروا نہ کرو اور اللہ پر بھروسہ رکھو، وہی بھروسہ کے لیے کافی ہے');
INSERT INTO "ur_maududi" VALUES(4,82,'4:82','کیا یہ لوگ قرآن پر غور نہیں کرتے؟ اگر یہ اللہ کے سوا کسی اور کی طرف سے ہوتا تو اس میں بہت کچھ اختلاف بیانی پائی جاتی۔ ');
INSERT INTO "ur_maududi" VALUES(4,83,'4:83','یہ لوگ جہاں کوئی ا طمینان بخش یا خوفناک خبر سن پاتے ہیں اسے لے کر پھیلا دیتے ہیں، حالانکہ اگر یہ اسے رسُول اور اپنی جماعت کے ذمہ دار اصحاب تک پہنچائیں تو وہ ایسے لوگوں کے علم میں آجائے جو ان کے درمیان اس بات کی صلاحیت رکھتے ہیں کہ اس سے صحیح نتیجہ اخذ کر سکیں۔ تم لوگوں پر اللہ کی مہربانی اور رحمت نہ ہوتی تو (تمہاری کمزور یاں ایسی تھیں کہ ) معدودے چند کے سوا تم سب شیطان کے پیچھے لگ گئے ہوتے');
INSERT INTO "ur_maududi" VALUES(4,84,'4:84','پس اے نبیؐ! تم اللہ کی راہ میں لڑو، تم اپنی ذات کے سوا کسی اور کے لیے ذمہ دار نہیں ہو البتہ اہل ایما ن کو لڑنے کے لیے اکساؤ، بعید نہیں کہ اللہ کافروں کا زور توڑ دے، اللہ کا زور سب سے زیادہ زبردست اور اس کی سزا سب سے زیادہ سخت ہے');
INSERT INTO "ur_maududi" VALUES(4,85,'4:85','جو بھلائی کی سفارش کریگا وہ اس میں سے حصّہ پائے گا اور جو برائی کی سفارش کرے گا وہ اس میں سے حصّہ پائے گا، اوراللہ ہر چیز پر نظر رکھنے والا ہے');
INSERT INTO "ur_maududi" VALUES(4,86,'4:86','اور جب کوئی احترام کے ساتھ تمہیں سلام کرے تو اس کو اس سے بہتر طریقہ کے ساتھ جواب دو یا کم از کم اسی طرح ، اللہ ہر چیز کا حساب لینے والا ہے');
INSERT INTO "ur_maududi" VALUES(4,87,'4:87','اللہ وہ ہے جس کےسوا کوئی خدا نہیں ہے، وہ تم سب کو اس قیامت کے دن جمع کرے گا جس کے آنے میں کوئی شبہہ نہیں، اور اللہ کی بات سے بڑھ کر سچی بات اور کس کی ہو سکتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,88,'4:88','پھر یہ تمہیں کیا ہو گیا ہے کہ منافقین کے بارے میں تمہارے درمیان دورائیں پائی جاتی ہیں، حالانکہ جو برائیاں انہوں نے کمائی ہیں ان کی بدولت اللہ انہیں الٹا پھیر چکا ہے۔ کیا تم چاہتے ہو کہ جسے اللہ نے ہدایت نہیں بخشی اسے تم ہدایت بخش دو؟ حالانکہ جس کو اللہ نے راستہ سے ہٹا دیا اس کے لیے تم کوئی راستہ نہیں پا سکتے');
INSERT INTO "ur_maududi" VALUES(4,89,'4:89','وہ تو یہ چاہتے ہیں کہ جس طرح وہ خود کافر ہیں اسی طرح تم بھی کافر ہو جاؤ تاکہ تم اور وہ سب یکساں ہو جائیں۔ لہٰذا ان میں سے کسی کو اپنا دوست نہ بناؤ جب تک کہ وہ اللہ کی راہ میں ہجرت کرکے نہ آجائیں، اور اگر وہ ہجرت سے باز رہیں تو جہاں پاؤ انہیں پکڑو اور قتل کرو اور ان میں سے کسی کو اپنا دوست اور مدد گار نہ بناؤ');
INSERT INTO "ur_maududi" VALUES(4,90,'4:90','البتہ وہ منافق اس حکم سے مستشنٰی ہیں جو کسی ایسی قوم سے جا ملیں جس کے ساتھ تمہارا معاہدہ ہے۔ اسی طرح وہ منافق بھی مستشنٰی ہیں جو تمہارے پاس آتے ہیں اور لڑائی سے دل برداشتہ ہیں، نہ تم سے لڑنا چاہتے ہیں نہ اپنی قوم سے۔ اللہ چاہتا تو ان کو تم پر مسلّط کر دیتا اور وہ بھی تم سے لڑتے۔ لہٰذا اگر وہ تم سے کنارہ کش ہو جائیں اور لڑنے سے باز رہیں اور تمہاری طرف صلح و آشتی کا ہاتھ بڑھائیں تو اللہ نے تمہارے لیے ان پر دست درازی کی کوئی سبیل نہیں رکھی ہے');
INSERT INTO "ur_maududi" VALUES(4,91,'4:91','ایک اور قسم کے منافق تمہیں ایسے ملیں گے جو چاہتے ہیں کہ تم سے بھی امن میں رہیں اور اپنی قوم سے بھی، مگر جب کبھی فتنہ کا موقع پائیں گے اس میں کود پڑیں گے ایسے لوگ اگر تمہارے مقابلہ سے باز نہ رہیں اور صلح و سلامتی تمہارے آگے پیش نہ کریں اور اپنے ہاتھ نہ روکیں تو جہاں وہ ملیں انہیں پکڑو اور مارو، ان پر ہاتھ اٹھانے کے لیے ہم نے تمہیں کھلی حجت دے دی ہے');
INSERT INTO "ur_maududi" VALUES(4,92,'4:92','کسی مومن کا یہ کا م نہیں ہے کہ دوسرے مومن کو قتل کرے، اِلّا یہ کہ اس سے چُوک ہو جائے۔ اور جو شخص کسی مومن کو غلطی سے قتل کر دے تو اس کا کفّارہ یہ ہے کہ ایک مومن کو غلامی سے آزاد کرے اور مقتول کے وارثوں کو خون بہا دے، اِلّا یہ کہ وہ خون بہا معاف کردیں۔ لیکن اگر وہ مسلمان مقتول کسی ایسی قوم سے تھا جس سے تمہاری دشمنی ہو تو اس کا کفّارہ ایک مومن غلام آزاد کرنا ہے۔ اور اگر وہ کسی ایسی غیر مسلم قوم کا فرد تھا جس سے تمہارا معاہدہ ہو تو اس کے وارثوں کو خوں بہا دیا جائے گا اور ایک مومن غلام کو آزاد کرنا ہوگا۔ پھر جو غلام نہ پائے وہ پے در پے دو مہینے کے روزے رکھے۔ یہ اس گناہ پر اللہ سے توبہ کرنے کا طریقہ ہے اور اللہ علیم و دانا ہے');
INSERT INTO "ur_maududi" VALUES(4,93,'4:93','رہا وہ شخص جو کسی مومن کو جان بوجھ کر قتل کرے تو اُس کی جزا جہنم ہے جس میں وہ ہمیشہ رہے گا اس پر اللہ کا غضب اور اُس کی لعنت ہے اور اللہ نے اس کے لیے سخت عذاب مہیا کر رکھا ہے');
INSERT INTO "ur_maududi" VALUES(4,94,'4:94','اے لوگوں جو ایمان لائے ہو، جب تم اللہ کی راہ میں جہاد کے لیے نکلو تو دوست دشمن میں تمیز کرو اور جو تمہاری طرف سلام سے تقدیم کرے اسے فوراً نہ کہدو کہ تُو مومن نہیں ہے۔ اگر تم دُنیوی فائدہ چاہتے ہو تو اللہ کے پاس تمہارے لیے بہت سے اموال غنیمت ہیں۔ آخر اسی حالت میں تم خود بھی تو اس سے پہلے مبتلا رہ چکے ہو، پھر اللہ نے تم پر احسان کیا ، لہٰذا تحقیق سے کام لو ، جو کچھ تم کرتے ہو اللہ اس سے باخبر ہے');
INSERT INTO "ur_maududi" VALUES(4,95,'4:95','مسلمانوں میں سے وہ لوگ جو کسی معذوری کے بغیر گھر بیٹھے رہتے ہیں اور وہ جو اللہ کی راہ میں جان ومال سے جہاد کرتے ہیں، دونوں کی حیثیت یکساں نہیں ہے۔ اللہ نے بیٹھنے والوں کی بہ نسبت جان و مال سے جہاد کرنے والوں کا درجہ بڑا رکھا ہے۔ اگرچہ ہر ایک کے لیے اللہ نے بھلائی ہی کا وعدہ فرمایا ہے، مگر اس کے ہاں مجاہدوں کی خدمات کا معاوضہ بیٹھنے والوں سےبہت زیادہ ہے، ');
INSERT INTO "ur_maududi" VALUES(4,96,'4:96','اُن کے لیے اللہ کی طرف سے بڑے درجے ہیں اور مغفرت اور رحمت ہے، اور اللہ بڑا معاف کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(4,97,'4:97','جو لوگ اپنے نفس پر ظلم کر رہے تھے اُن کی رُوحیں جب فرشتوں نے قبض کیں تو ان سے پوچھا کہ یہ تم کس حال میں مُبتلا تھے؟ انہوں نے جواب دیا کہ ہم زمین میں کمزور و مجبور تھے۔ فرشتوں نے کہا، کیا خدا کی زمین وسیع نہ تھی کہ تم اس میں ہجرت کرتے؟ یہ وہ لوگ ہیں جن کا ٹھکانا جہنّم ہے اور وہ بڑا ہی بُرا ٹھکانا ہے');
INSERT INTO "ur_maududi" VALUES(4,98,'4:98','ہاں جو مرد، عورتیں اور بچے واقعی بے بس ہیں اور نکلنے کا کوئی راستہ اور ذریعہ نہیں پاتے');
INSERT INTO "ur_maududi" VALUES(4,99,'4:99','بعید نہیں کہ اللہ انہیں معاف کر دے، اللہ بڑا معاف کرنے والا اور درگزر فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(4,100,'4:100','جو کوئی اللہ کی راہ میں ہجرت کرے گا وہ زمین میں پناہ لینے کے لیے بہت جگہ اور بسر اوقات کے لیے بڑی گنجائش پائے گا، اور جو اپنے گھر سے اللہ اور رسُول کی طرف ہجرت کے لیے نکلے، پھر راستہ ہی میں اُسے موت آجائے اُس کا اجر اللہ کے ذمّے واجب ہوگیا، اللہ بہت بخشش فرمانے والا اور رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,101,'4:101','اور جب تم لوگ سفر کے لیے نکلو تو کوئی مضائقہ نہیں اگر نماز میں اختصار کردو (خصوصاً) جبکہ تمہیں اندیشہ ہو کہ کافر تمہیں ستائیں گے کیونکہ وہ کُھلم کُھلّا تمہاری دُشمنی پر تُلے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(4,102,'4:102','اور اے نبی ! جب تم مسلمانوں کے درمیان ہو اور (حالتِ جنگ میں) انہیں نماز پڑھانے کھڑے ہو تو چاہیے کہ ان میں سے ایک گروہ تمہارے ساتھ کھڑا ہو اور اسلحہ لیے رہے، پھر جب وہ سجدہ کرلے تو پیچھے چلا جائے اور دُوسرا گروہ جس نے ابھی نماز نہیں پڑھی ہے آکر تمہارے ساتھ پڑھے اور وہ بھی چوکنّا رہےاور اپنے اسلحہ لیے رہے، کیوں کہ کفّار اِس تاک میں ہیں کہ تم اپنے ہتھیاروں اور اپنے سامان کی طرف سے ذرا غافل ہو تو وہ تم پر یک بارگی ٹوٹ پڑیں۔ البتہ اگر تم بارش کی وجہ سے تکلیف محسوس کرو یا بیمار ہو تو اسلحہ رکھ دینے میں مضائقہ نہیں، مگر پھر بھی چوکنّے رہو، یقین رکھو کہ اللہ نے کافروں کے لیے رُسوا کُن عذاب مہیّا کررکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,103,'4:103','پھر جب نماز سے فارغ ہو جاؤ تو کھڑے اور بیٹھے اور لیٹے، ہر حال میں اللہ کو یاد کرتے رہو اور جب اطمینان نصیب ہو جائے تو پوری نماز پڑھو نما ز در حقیقت ایسا فرض ہے جو پابندی وقت کے ساتھ اہل ایمان پر لازم کیا گیا ہے');
INSERT INTO "ur_maududi" VALUES(4,104,'4:104','اِس گروہ کے تعاقب میں کمزوری نہ دکھاوٴ۔ اگر تم تکلیف اُٹھارہے ہو تو تمہاری طرح وہ بھی تکلیف اُٹھارہے ہیں۔ اور تم اللہ سے اُس چیز کے اُمّیدوار ہو جس کے وہ اُمّیدوار نہیں ہیں۔ اللہ سب کچھ جانتا ہے اور وہ حکیم و دانا ہے');
INSERT INTO "ur_maududi" VALUES(4,105,'4:105','اے نبیؐ ! ہم نے یہ کتاب حق کے ساتھ تمہاری طرف نازل کی ہے تاکہ جو راہِ راست اللہ نے تمہیں دکھائی ہے اس کے مطابق لوگوں کے درمیان فیصلہ کرو۔ تم بد دیانت لوگوں کی طرف سے جھگڑنے والے نہ بنو');
INSERT INTO "ur_maududi" VALUES(4,106,'4:106','اور اللہ سے در گزر کی درخواست کرو، وہ بڑا درگزر فرمانے والا اور رحیم ہے');
INSERT INTO "ur_maududi" VALUES(4,107,'4:107','جو لوگ اپنے نفس سے خیانت کرتے ہیں تم اُن کی حمایت نہ کرو۔ اللہ کو ایسا شخص پسند نہیں ہے جو خیانت کار اور معصیت پیشہ ہو');
INSERT INTO "ur_maududi" VALUES(4,108,'4:108','یہ لو گ انسانوں سے اپنی حرکات چھپا سکتے ہیں مگر خدا سے نہیں چھپا سکتے وہ تواُس وقت بھی اُن کے ساتھ ہوتا ہے جب یہ راتوں کو چھپ کر اُس کی مرضی کے خلاف مشورے کرتے ہیں اِن کے سارے اعمال پر اللہ محیط ہے');
INSERT INTO "ur_maududi" VALUES(4,109,'4:109','ہاں! تم لوگوں نے اِن مجرموں کی طرف سے دنیا کی زندگی میں تو جھگڑا کر لیا، مگر قیامت کے روز ان کی طرف سے کون جھگڑا کرے گا؟آخر وہاں کون اِن کا وکیل ہوگا؟');
INSERT INTO "ur_maududi" VALUES(4,110,'4:110','اگر کوئی شخص برا فعل کر گزرے یا اپنے نفس پر ظلم کر جائے اور اس کے بعد اللہ سے درگزر کی درخواست کرے تو اللہ کو درگزر کرنے والا اور رحیم پائے گا');
INSERT INTO "ur_maududi" VALUES(4,111,'4:111','مگر جو برائی کما لے تو اس کی یہ کمائی اُسی کے لیے وبال ہوگی، اللہ کو سب باتوں کی خبر ہے اور وہ حکیم و دانا ہے');
INSERT INTO "ur_maududi" VALUES(4,112,'4:112','پھر جس نے کوئی خطا یا گناہ کر کے اس کا الزام کسی بے گناہ پر تھوپ دیا اُس نے تو بڑے بہتان اور صریح گناہ کا بار سمیٹ لیا');
INSERT INTO "ur_maududi" VALUES(4,113,'4:113','اے نبی ؐ! اگر اللہ کا فضل تم پر نہ ہوتا اور اس کی رحمت تمہارے شامل حال نہ ہوتی تو ان میں سے ایک گروہ نے تو تمہیں غلط فہمی میں مبتلا کرنے کا فیصلہ کر ہی لیا تھا، حالانکہ درحقیقت وہ خود اپنے سوا کسی کو غلط فہمی میں مبتلا نہیں کر رہے تھے۔ اور تمہارا کوئی نقصان نہ کر سکتے تھے۔ اللہ نے تم پر کتاب اور حکمت نازل کی ہے اور تم کو وہ کچھ بتایا ہے جو تمہیں معلوم نہ تھا، اور اس کا فضل تم پر بہت ہے');
INSERT INTO "ur_maududi" VALUES(4,114,'4:114','لوگوں کی خفیہ سرگوشیوں میں اکثر و بیشتر کوئی بھلائی نہیں ہوتی ہاں اگر کوئی پوشیدہ طور پر صدقہ و خیرات کی تلقین کرے یا کسی نیک کام کے لیے یا لوگوں کے معاملات میں اصلاح کرنے کے لیے کسی سے کچھ کہے تو یہ البتہ بھلی بات ہے، اور جو کوئی اللہ کی رضا جوئی کے لیے ایسا کرے گا اُسے ہم بڑا اجر عطا کریں گے');
INSERT INTO "ur_maududi" VALUES(4,115,'4:115','مگر جو شخص رسول کی مخالفت پر کمر بستہ ہو اور اہل ایمان کی روش کے سوا کسی اور روش پر چلے، درآں حالیکہ اس پر راہ راست واضح ہو چکی ہو ، تو اس کو ہم اسی طرف چلائیں گے جدھر وہ خود پھر گیا اور اسے جہنم میں جھونکیں گے جو بدترین جائے قرار ہے');
INSERT INTO "ur_maududi" VALUES(4,116,'4:116','اللہ کے ہاں بس شرک ہی کی بخشش نہیں ہے، اس کے سوا اور سب کچھ معاف ہو سکتاہے جسے وہ معاف کر نا چاہے۔ جس نے اللہ کے ساتھ کسی کو شریک ٹھہرایا وہ تو گمراہی میں بہت دور نکل گیا');
INSERT INTO "ur_maududi" VALUES(4,117,'4:117','وہ اللہ کو چھوڑ کر دیویوں کو معبود بناتے ہیں۔ وہ اس باغی شیطان کو معبود بناتے ہیں ');
INSERT INTO "ur_maududi" VALUES(4,118,'4:118','جس کو اللہ نے لعنت زدہ کیا ہے۔(وہ اس شیطان کی اطاعت کر رہے ہیں)جس نے اللہ سے کہا تھا کہ”میں تیرے بندوں سے ایک مقرر حصہ لے کر رہوں گا، ');
INSERT INTO "ur_maududi" VALUES(4,119,'4:119','میں انہیں بہکاؤں گا، میں انہیں آرزوؤں میں اُلجھادوں گا، میں انہیں حکم دوں گا اور وہ میرے حکم سے جانوروں کے کان پھاڑیں گے اور میں انہیں حکم دوں گا اور وہ میرے حکم سے خدائی ساخت میں ردوبدل کریں گے۔“ اس شیطان کو جس نے اللہ کے بجائے اپنا ولی و سر پرست بنالیا وہ صریح نقصان میں پڑگیا');
INSERT INTO "ur_maududi" VALUES(4,120,'4:120','وہ ان لوگوں سے وعدے کرتا ہے اور انہیں اُمیدیں دلاتا ہے، مگر شیطان کے سارے وعدے بجز فریب کے اور کچھ نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(4,121,'4:121','اِن لوگوں کا ٹھکانا جہنم ہے جس سے خلاصی کی کوئی صورت یہ نہ پائیں گے');
INSERT INTO "ur_maududi" VALUES(4,122,'4:122','رہے وہ لوگ جو ایمان لے آئیں اور نیک عمل کریں، توا نہیں ہم ایسے باغوں میں داخل کریں گے جن کے نیچے نہریں بہتی ہوں گی اور وہ وہاں ہمیشہ ہمیشہ رہیں گے یہ اللہ کا سچا وعدہ ہے اور اللہ سے بڑھ کر کون اپنی بات میں سچا ہوگا');
INSERT INTO "ur_maududi" VALUES(4,123,'4:123','انجام کار نہ تمہاری آرزوؤں پر موقوف ہے نہ اہل کتاب کی آرزوؤں پر جو بھی برائی کرے گا اس کا پھل پائے گا اور اللہ کے مقابلہ میں اپنے لیے کوئی حامی و مدد گار نہ پاسکے گا');
INSERT INTO "ur_maududi" VALUES(4,124,'4:124','اور جو نیک عمل کرے گا، خواہ مرد ہو یا عورت، بشر طیکہ ہو وہ مومن، تو ایسے ہی لوگ جنت میں داخل ہوں گے اور اُن کی ذرہ برابر حق تلفی نہ ہونے پائے گی');
INSERT INTO "ur_maududi" VALUES(4,125,'4:125','اُس شخص سے بہتر اور کس کا طریق زندگی ہوسکتا ہے جس نے اللہ کے آگے سر تسلیم خم کر دیا اور اپنا رویہ نیک رکھا اور یکسو ہو کر ابراہیمؑ کے طریقے کی پیروی کی، اُس ابراہیمؑ کے طریقے کی جسے اللہ نے اپنا دوست بنا لیا تھا');
INSERT INTO "ur_maududi" VALUES(4,126,'4:126','آسمانوں اور زمین میں جو کچھ ہے اللہ کا ہے اور اللہ ہر چیز پر محیط ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,127,'4:127','لو گ تم سے عورتوں کے معاملہ میں فتویٰ پوچھتے ہیں۔ کہو اللہ تمہیں ان کے معاملہ میں فتویٰ دیتا ہے، اور ساتھ ہی وہ احکام بھی یاد دلاتا ہے جو پہلے سے تم اس کتاب میں سنائے جارہے ہیں۔ یعنی وہ احکام جو ان یتیم لڑکیوں کے متعلق ہیں جن کے حق تم ادا نہیں کرتے اور جن کے نکاح کرنے سے تم باز رہتے ہو(یا لالچ کی بنا ء پر تم خود ان سے نکاح کر لینا چاہتے ہو)، اور وہ احکام جو ان بچّوں کے متعلق ہیں جو بیچارے کوئی زور نہیں رکھتے۔ اللہ تمہیں ہدایت کرتا ہے کہ یتیموں کے ساتھ انصاف پر قائم رہو، جو بھلائی تم کرو گے وہ اللہ کے علم سے چھپی نہ رہ جائے گی');
INSERT INTO "ur_maududi" VALUES(4,128,'4:128','جب کسی عورت کو اپنے شوہر سے بدسلوکی یا بے رُخی کا خطرہ ہو تو کوئی مضائقہ نہیں اگر میاں اور بیوی( کچھ حقوق کی کمی بیشی پر) آپس میں صلح کرلیں۔ صلح بہرحال بہتر ہے۔ نفس تنگ دلی کی طرف جلدی مائل ہو جاتے ہیں، لیکن اگر تم لوگ احسان سے پیش آؤ اور خدا ترسی سے کام لو تو یقین رکھو کہ اللہ تمہارے اس طرز عمل سے بے خبر نہ ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(4,129,'4:129','بیویوں کے درمیان پورا پورا عدل کرنا تمہارے بس میں نہیں ہے۔ تم چاہو بھی تو اس پر قادر نہیں ہو سکتے۔ لہذا(قانونِ الہی کا منشا پورا کرنے کے لیے یہ کافی ہے کہ ) ایک بیوی کی طرف اس طرح نہ جھک جاؤ کہ دوسری کو ادھر لٹکتا چھوڑ دو۔ اگر تم اپنا طرزِ عمل درست رکھو اور اللہ سے ڈرتے رہو تو اللہ چشم پوشی کرنے والا اور رحم فرمانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,130,'4:130','لیکن اگر زوجین ایک دوسرے سے الگ ہی ہو جائیں تو اللہ اپنی وسیع قدرت سے ہر ایک کو دوسرے کی محتاجی سے بے نیاز کر دے گا اللہ کا دامن بہت کشادہ ہے اور وہ دانا و بینا ہے');
INSERT INTO "ur_maududi" VALUES(4,131,'4:131','آسمانوں اور زمین میں جو کچھ ہے سب اللہ ہی کا ہے تم سے پہلے جن کو ہم نے کتاب دی تھی انہیں بھی یہی ہدایت کی تھی اور اب تم کو بھی یہی ہدایت کرتے ہیں کہ خدا سے ڈرتے ہوئے کام کرو لیکن اگر تم نہیں مانتے تو نہ مانو، آسمان و زمین کی ساری چیزوں کا مالک اللہ ہی ہے اور وہ بے نیاز ہے، ہر تعریف کا مستحق');
INSERT INTO "ur_maududi" VALUES(4,132,'4:132','ہاں اللہ ہی مالک ہے ان سب چیزوں کاجو آسمانوں میں ہیں اور جو زمین میں ہیں، اور کار سازی کے لیے بس و ہی کافی ہے');
INSERT INTO "ur_maududi" VALUES(4,133,'4:133','اگر وہ چاہے تو تم لوگوں کو ہٹا کر تمہاری جگہ دوسروں کو لے آئے، اور وہ اِس کی پوری قدرت رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(4,134,'4:134','جو شخص محض ثوابِ دُنیا کو طالب ہو اسے معلوم ہو نا چاہیے کہ اللہ کےپاس ثوابِ دُنیا بھی ہے اور ثوابِ آخرت بھی ، اور اللہ سمیع و بصیر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,135,'4:135','اے لوگو جو ایمان لائےہو ، انصاف کے علمبردار اور خدا واسطے کے گواہ بنو اگرچہ تمہارے انصاف اور تمہاری گواہی کی زد خود تمہاری اپنی ذات پر یا تمہارے والدین اور رشتہ داروں پر ہی کیوں نہ پڑتی ہو۔ فریقِ معاملہ خواہ مالدار ہو یا غریب ، اللہ تم سے زیادہ ان کا خیر خواہ ہے۔ لہٰذا اپنی خواہش نفس کی پیروی میں عدل سے باز نہ رہو۔ اور اگر تم نے لگی پٹی بات کہی یا سچائی سے پہلو بچایا تو جان رکھو کہ جو کچھ تم کرتے ہو اللہ کو اس کی خبر ہے');
INSERT INTO "ur_maududi" VALUES(4,136,'4:136','اے لوگو جو ایمان لائے ہو، ایمان لاؤ اللہ پر اور اس کے رسول پر اور اس کتاب پر جو اللہ نے اپنے رسول پر نازل کی ہے اور ہر اس کتاب پر جو اس سے پہلے وہ نازل کر چکا ہے۔ جس نے اللہ اور اس کے ملائکہ اور اس کی کتابوں اور اس کے رسولوں اور روزِ آخرت سے کفر کیا وہ گمراہی میں بھٹک کر بہت دور نکل گیا');
INSERT INTO "ur_maududi" VALUES(4,137,'4:137','رہے و ہ لوگ جو ایمان لائے، پھر کفر کیا، پھرایمان لائے، پھر کفر کیا، پھر اپنے کفر میں بڑھتے چلے گئے، تو اللہ ہرگز ان کو معاف نہ کرے گا اور نہ کبھی ان کو راہ راست دکھائے گا');
INSERT INTO "ur_maududi" VALUES(4,138,'4:138','اور جو منافق اہل ایمان کو چھوڑ کر کافروں کو اپنا رفیق بناتے ہیں انہیں یہ مثردہ سنا دو کہ اُن کے لیے دردناک سزا تیار ہے');
INSERT INTO "ur_maududi" VALUES(4,139,'4:139','کیا یہ عزت کی طلب میں ان کے پاس جاتے ہیں؟ حالانکہ عزت تو ساری کی ساری اللہ ہی کے لیے ہے');
INSERT INTO "ur_maududi" VALUES(4,140,'4:140','اللہ اس کتاب میں تم کو پہلے ہی حکم دے چکا ہے کہ جہاں تم سنو کہ اللہ کی آیات کے خلاف کفر بکا جارہاہے اور ان اکا مذاق اڑایا جا رہا ہے وہاں نہ بیٹھو جب تک کہ لوگ کسی دوسری بات میں نہ لگ جائیں۔ اب اگر تم ایسا کرتے ہو توتم بھی انہی کی طرح ہو۔ یقین جانو کہ اللہ منافقوں اور کافروں کو جہنم میں ایک جگہ جمع کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(4,141,'4:141','یہ منافق تمہارے معاملہ میں انتظار کر رہے ہیں (کہ اونٹ کس کروٹ بیٹھتا ہے)۔ اگر اللہ کی طرف سے فتح تمہاری ہوئی تو آکر کہیں گے کہ کیا ہم تمہارے ساتھ نہ تھے؟ اگر کافروں کا پلّہ بھاری رہا تو ان سے کہیں گے کہ کیا ہم تمہارے خلاف لڑنے پر قادر نہ تھے اور پھر بھی ہم نے تم کو مسلمانوں سے بچایا؟ بس اللہ ہی تمہارے اور ان کے معاملہ کا فیصلہ قیامت کے روز کرے گا اور (اس فیصلہ میں) اللہ نے کافروں کے لیے مسلمانوں پر غالب آنے کی ہرگز کوئی سبیل نہیں رکھی ہے');
INSERT INTO "ur_maududi" VALUES(4,142,'4:142','یہ منافق اللہ کے ساتھ دھوکہ بازی کر رہے ہیں حالانکہ درحقیقت اللہ ہی نے انہیں دھوکہ میں ڈال رکھا ہے۔ جب یہ نماز کے لیے اٹھتے ہیں تو کسمساتے ہوئے محض لوگوں کود کھانے کی خاطر اٹھتے ہیں اور خدا کو کم ہی یا د کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(4,143,'4:143','کفر و ایمان کے درمیان ڈانوا ڈول ہیں۔ نہ پورے اس طرف ہیں نہ پورے اُس طرف۔ جسے اللہ نے بھٹکا دیا ہو اس کے لیے تم کوئی راستہ نہیں پا سکتے۔ ');
INSERT INTO "ur_maududi" VALUES(4,144,'4:144','اے لوگو جو ایمان لائے ہو، مومنوں کو چھوڑ کر کافروں کو اپنا رفیق نہ بناؤ کیا تم چاہتے ہو کہ اللہ کو اپنے خلاف صریح حجت دے دو؟');
INSERT INTO "ur_maududi" VALUES(4,145,'4:145','یقین جانو کہ منافق جہنم کے سب سے نیچے طبقے میں جائیں گے اور تم کسی کو اُن کا مدد گار نہ پاؤ گے');
INSERT INTO "ur_maududi" VALUES(4,146,'4:146','البتہ جو ان میں سے تائب ہو جا ئیں اور اپنے طرزِ عمل کی اصلاح کر لیں اور اللہ کا دامن تھام لیں اور اپنے دین کو اللہ کے لیے خالص کر دیں، ایسے لوگ مومنوں کے ساتھ ہیں اور اللہ مومنوں کو ضرور اجرِ عظیم عطا فرمائے گا');
INSERT INTO "ur_maududi" VALUES(4,147,'4:147','آخر اللہ کو کیا پڑی ہے کہ تمہیں خواہ مخواہ سزا دے اگر تم شکر گزار بندے بنے رہو اور ایمان کی روش پر چلو۔ اللہ بڑا قدر دان ہے اور سب کے حال سے واقف ہے');
INSERT INTO "ur_maududi" VALUES(4,148,'4:148','اللہ اس کو پسند نہیں کرتا کہ آدمی بد گوئی پر زبان کھولے، الا یہ کہ کسی پر ظلم کیا گیا ہو، اور اللہ سب کچھ سننے اور جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(4,149,'4:149','(مظلوم ہونے کی صورت میں اگر چہ تم کو بد گوئی کا حق ہے ) لیکن اگر تم ظاہر و باطن میں بھلائی ہی کیے جاؤ، یا کم از کم بُرائی سے درگزر کرو، تو اللہ کی صفت بھی یہی ہے کہ وہ بڑا معاف کرنے والا ہے حالانکہ سزا دینے پر پوری قدرت رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,150,'4:150','جو لوگ اللہ اور اس کے رسولوں سے کفر کرتے ہیں، اور چاہتے ہیں کہ اللہ اور اس کے رسولوں کے درمیان تفریق کریں، اور کہتے ہیں کہ ہم کسی کو مانیں گے اور کسی کو نہ مانیں گے، اور کفر و ایمان کے بیچ میں ایک راہ نکالنے کا ارادہ رکھتے ہیں');
INSERT INTO "ur_maududi" VALUES(4,151,'4:151','وہ سب پکے کافر ہیں اور ایسے کافروں کے لیے ہم نے وہ سزا مہیّا کر رکھی ہے جو انہیں ذلیل و خوار کر دینے والی ہو گی');
INSERT INTO "ur_maududi" VALUES(4,152,'4:152','بخلاف اس کے جو لوگ اللہ اور اس کے تمام رسولوں کو مانیں، اور ان کے درمیان تفریق نہ کریں، ان کو ہم ضرور ان کے اجر عطا کریں گے ، اور اللہ بڑا درگزر فرمانے والا اور رحم کرنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,153,'4:153','یہ اہل کتاب اگر آج تم سے مطالبہ کر رہے ہیں کہ تم آسمان سے کوئی تحریر اُن پر نازل کراؤ تو اس سے بڑھ چڑھ کر مجرمانہ مطالبے یہ پہلے موسیٰؑ سے کر چکے ہیں۔ اس سے تو انہوں نے کہا تھا کہ ہمیں خدا کو علانیہ دکھا دو اور اسی سر کشی کی وجہ سے یکایک ان پر بجلی ٹوٹ پڑی تھی۔ پھر انہوں نے بچھڑے کو اپنا معبود بنالیا،حالانکہ یہ کھلی کھلی نشانیاں دیکھ چکے تھے۔ اس پر بھی ہم نے ان سے درگزر کیا۔ ہم نے موسیٰؑ کو صریح فرمان عطا کیا');
INSERT INTO "ur_maududi" VALUES(4,154,'4:154','اور ان لوگوں پر طور کو اٹھا کر ان سے ( اس فرمان کی اطاعت کا)عہد لیا۔ ہم نے ان کو حکم دیا کہ دروازہ میں سجدہ ریز ہوتے ہوئے داخل ہو۔ ہم نے ان سے کہا کہ سَبت کا قانون نہ توڑو اور اس پر اِن سےپختہ عہد لیا۔ ');
INSERT INTO "ur_maududi" VALUES(4,155,'4:155','آخر کار ان کی عہد شکنی کی وجہ سے ، اور اس وجہ سے کہ انہوں نے اللہ کی آیات کو جُھٹلایا، اور متعدد پیغمبروں کو نا حق قتل کیا، اور یہاں تک کہا کہ ہمارے دل غلافوں میں محفوظ ہیں۔۔۔۔ حالانکہ درحقیقت ان کی باطل پرستی کے سبب سے اللہ نے ان کے دلوں پر ٹھپّہ لگا دیا ہے اور اسی وجہ سے یہ بہت کم ایمان لاتے ہیں');
INSERT INTO "ur_maududi" VALUES(4,156,'4:156','پھر اپنے کفر میں اتنے بڑھے کہ مریم پر سخت بہتان لگایا');
INSERT INTO "ur_maududi" VALUES(4,157,'4:157','اور خود کہا کہ ہم نے مسیح، عیسٰی ابن ِ مریم، رسول اللہ کو قتل کر دیا ہے۔۔۔۔ حالانکہ فی الواقع انہوں نے نہ اس کو قتل کیا نہ صلیب پر چڑھایا بلکہ معاملہ ان کے لیے مشتبہ کر دیا گیا۔ اور جن لوگوں نے اس کے بارے میں اختلاف کیا ہے وہ بھی دراصل شک میں مبتلا ہیں ، ان کے پاس اس معاملہ میں کوئی علم نہیں ہے، محض گمان ہی کی پیروی ہے۔ انہوں نے مسیح کو یقین کے ساتھ قتل نہیں کیا');
INSERT INTO "ur_maududi" VALUES(4,158,'4:158','بلکہ اللہ نے اس کو اپنی طرف اٹھا لیا، اللہ زبردست طاقت رکھنے والا اور حکیم ہے');
INSERT INTO "ur_maududi" VALUES(4,159,'4:159','اور اہل کتاب میں سے کوئی ایسا نہ ہوگا جو اس کی موت سے پہلے اس پر ایمان نہ لے آئے گا اور قیامت کے روز وہ ان پر گواہی دے گا، ');
INSERT INTO "ur_maududi" VALUES(4,160,'4:160','غرض ان یہودی بن جا نے والوں کے اسی ظالمانہ رویّہ کی بنا پر، اور اس بنا پر کہ یہ بکثرت اللہ کے راستے سے روکتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(4,161,'4:161','اور سُود لیتے ہیں جس سے انہیں منع کیا گیا تھا، اور لوگوں کےمال ناجائز طریقوں سے کھا تے ہیں، ہم نے بہت سی وہ پاک چیزیں ان پر حرام کر دیں جو پہلے ان کے لیے حلال تھیں، اور جو لوگ ان میں سے کافر ہیں ان کے لیے ہم نے درد ناک عذاب تیار کر رکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,162,'4:162','مگر ان میں جو لوگ پختہ علم رکھنے والے ہیں اور ایماندار ہیں وہ سب اس تعلیم پر ایمان لاتے ہیں جو تمہاری طرف نازل کی گئی ہے اور جو تم سے پہلے نازل کی گئی تھی۔ اس طرح کے ایمان لانے والے اور نماز و زکوٰة کی پابندی کرنے والے اور اللہ اور روز آخرت پر سچّا عقیدہ رکھنے والے لوگوں کو ہم ضرور اجر عظیم عطا کریں گے');
INSERT INTO "ur_maududi" VALUES(4,163,'4:163','اے محمد ؐ! ہم نے تمہاری طرف اسی طرح وحی بھیجی ہے جس طرح نوح ؑ اور اس کے بعد کے پیغمبروں کی طرف بھیجی تھی۔ ہم نے ابراہیم ؑ ، اسماعیل ؑ ، اسحاق ؑ ، یعقوب ؑ اور اولاد یعقوب ؑ ، عیسٰی ؑ ، ایوب ؑ ، یونس ؑ ، ہارون ؑ اور سلیمان ؑ کی طرف وحی بھیجی۔ ہم نے داؤد ؑ کو زبور دی۔ ');
INSERT INTO "ur_maududi" VALUES(4,164,'4:164','ہم نے ان رسولوں پر بھی وحی نازل کی جن کا ذکر ہم اس سے پہلے تم سے کر چکے ہیں اور ان رسولوں پر بھی جن کا ذکر تم سے نہیں کیا۔ ہم نےموسیٰ ؑ سے اس طرح گفتگو کی جس طرح گفتگو کی جاتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,165,'4:165','یہ سارے رسول خوش خبری دینے والے اور ڈرانے والے بنا کر بھیجے گئے تھے تاکہ ان کو مبعوث کر دینے کے بعد لوگوں کے پاس اللہ کے مقابلہ میں کوئی حجّت نہ رہے اور اللہ بہر حال غالب رہنے والا اور حکیم و دانا ہے');
INSERT INTO "ur_maududi" VALUES(4,166,'4:166','(لوگ نہیں مانتے تو نہ مانیں) مگر اللہ گواہی دیتا ہے کہ جو کچھ اُس نے تم پر نازل کیا ہے اپنے علم سے نازل کیا ہے، اور اِس پر ملائکہ بھی گواہ ہیں اگرچہ اللہ کا گواہ ہونا بالکل کفایت کرتا ہے');
INSERT INTO "ur_maududi" VALUES(4,167,'4:167','جو لوگ اِس کو ماننے سے خود انکار کرتے ہیں اور دوسروں کو خدا کے راستہ سے روکتے ہیں وہ یقیناً گمرا ہی میں حق سے بہت دور نکل گئے ہیں');
INSERT INTO "ur_maududi" VALUES(4,168,'4:168','ا ِس طرح جن لوگوں نے کفر و بغاوت کا طریقہ اختیار کیا اور ظلم وستم پر اتر آئے اللہ ان کو ہرگز معاف نہ کرے گا اور انہیں کوئی راستہ،');
INSERT INTO "ur_maududi" VALUES(4,169,'4:169','بجز جہنم کے راستہ کے نہ دکھائے گا جس میں وہ ہمیشہ رہیں گے اللہ کے لیے یہ کوئی مشکل کام نہیں ہے');
INSERT INTO "ur_maududi" VALUES(4,170,'4:170','لوگو! یہ رسول تمہارے پاس تمہارے رب کی طرف سے حق لے کر آگیا ہے، ایمان لے آؤ، تمہارے ہی لیے بہتر ہے ، اور اگر انکا ر کرتے ہو تو جان لو کہ آسمانوں اور زمین میں جو کچھ ہے سب اللہ کا ہے اور اللہ علیم بھی ہے اور حکیم بھی۔ ');
INSERT INTO "ur_maududi" VALUES(4,171,'4:171','اے اہل کتاب ! اپنے دین میں غلو نہ کرو اور اللہ کی طرف حق کے سوا کوئی بات منسُوب نہ کرو۔ مسیح عیسٰی ؑ ابنِ مریم ؑ اس کے سوا کچھ نہ تھا کہ اللہ کا ایک رسول تھا اور ایک فرمان تھا جو اللہ نے مریم ؑ کی طرف بھیجا اور ایک رُوح تھی اللہ کی طرف سے (جس نے مریم کے رِحم میں بچّہ کی شکل اختیار کی) پس تم اللہ اور اُس کے رسُولوں پر ایمان لاوٴ اور نہ کہو کہ ”تین“ ہیں۔ باز آجاوٴ، یہ تمہارے ہی لیے بہتر ہے۔ اللہ تو بس ایک ہی خدا ہے۔ وہ بالا تر ہے اس سے کہ کوئی اس کا بیٹا ہو۔ زمین اور آسمانوں کی ساری چیزیں اس کی مِلک ہیں، اور ان کی کفالت و خبر گیری کے لیے بس وہی کافی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(4,172,'4:172','مسیح نے کبھی اس بات کو عار نہیں سمجھا کہ وہ اللہ کا بندہ ہو، اور نہ مقرب ترین فرشتے اِس کو اپنے لیے عار سمجھتے ہیں اگر کوئی اللہ کی بندگی کو اپنے لیے عار سمجھتا ہے اور تکبر کرتا ہے تو ایک وقت آئے گا جب اللہ سب کو گھیر کر اپنے سامنے حاضر کرے گا');
INSERT INTO "ur_maududi" VALUES(4,173,'4:173','اُس وقت وہ لوگ جنہوں نے ایمان لا کر نیک طرز عمل اختیار کیا ہے اپنے اجر پورے پورے پائیں گے اور اللہ اپنے فضل سے ان کو مزید اجر عطا فرمائے گا، اور جن لوگوں نے بندگی کو عار سمجھا اور تکبر کیا ہے اُن کو اللہ دردناک سزا دے گا اور اللہ کے سوا جن جن کی سرپرستی و مددگاری پر وہ بھروسہ رکھتے ہیں ان میں سے کسی کو بھی وہ وہاں نہ پائیں گے');
INSERT INTO "ur_maududi" VALUES(4,174,'4:174','لوگو! تمہارے رب کی طرف سے تمہارے پاس دلیل روشن آ گئی ہے اور ہم نے تمہاری طرف ایسی روشنی بھیج دی ہے جو تمہیں صاف صاف راستہ دکھانے والی ہے');
INSERT INTO "ur_maududi" VALUES(4,175,'4:175','اب جو لوگ اللہ کی بات مان لیں گے اور اس کی پناہ ڈھونڈیں گے ان کو اللہ اپنی رحمت اور اپنے فضل و کرم کے دامن میں لے لے گا اور اپنی طرف آنے کا سیدھا راستہ ان کو دکھا دے گا');
INSERT INTO "ur_maududi" VALUES(4,176,'4:176','لوگ تم سے کلالہ کے معاملہ میں فتویٰ پُوچھتے ہیں۔ کہو اللہ تمہیں فتویٰ دیتا ہے۔ اگر کوئی شخص بے اولاد مرجائے اور اس کی ایک بہن ہو تو وہ اس کے ترکہ میں سے نصف پائے گی، اور اگر بہن بے اولاد مرے تو بھائی اُس کا وارث ہوگا۔ اگر میّت کی وارث دو بہنیں ہوں تو وہ ترکے میں سے دو تہائی کی حقدار ہوں گی، اور اگر کئی بھائی بہنیں ہوں تو عورتوں کا اکہرا اور مردوں کا دوہرا حصّہ ہوگا۔ اللہ تمہارے لیے احکام کی توضیح کرتا ہے تاکہ تم بھٹکتے نہ پھرو اور اللہ ہر چیز کا علم رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(5,1,'5:1','ے لوگو جو ایمان لائے ہو، بندشوں کی پُوری پابندی کرو۔ تمہارے لیے مویشی کی قسم کے سب جانور حلال کیے گئے، سوائے اُن کے جو آگے چل کر تم کو بتائے جائیں گے۔ لیکن احرام کی حالت میں شکار کو اپنے لیے حلال نہ کرلو، بے شک اللہ جو چاہتا ہے حکم دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(5,2,'5:2','اے لوگو جو ایمان لائے ہو، خدا پرستی کی نشانیوں کو بے حُرمت نہ کرو۔۔۔۔ نہ حرام مہینوں میں سے کسی کو حلال کرلو، نہ قربانی کے جانوروں پر دست درازی کرو، نہ اُن جانوروں پر ہاتھ ڈالو جن کی گردنوں میں نذرِ خداوندی کی علامت کے طور پر پٹے پڑے ہوئے ہوں، نہ اُن لوگوں کو چھیڑو جو اپنے رب کے فضل اور اس کی خوشنودی کی تلاش میں مکانِ محترم (کعبہ) کی طرف جارہے ہوں۔ ہاں جب احرام کی حالت ختم ہوجائے تو شکار تم کرسکتے ہو۔۔۔۔ اور دیکھو، ایک گروہ نے جو تمہارے لیے مسجدِ حرام کا راستہ بند کردیا ہے تو اس پر تمہارا غصّہ تمہیں اتنا مشتعل نہ کردے کہ تم بھی ان کے مقابلہ میں ناروا زیادتیاں کرنے لگو۔ نہیں ! جو کام نیکی اور خدا ترسی کے ہیں ان میں سب سے تعاون کرو اور جو گناہ اور زیادتی کے کام ہیں ان میں کسی سے تعاون نہ کرو۔ اللہ سے ڈرو، اس کی سزا بہت سخت ہے');
INSERT INTO "ur_maududi" VALUES(5,3,'5:3','تم پر حرام کیا گیا مُردار، خُون، سُور کا گوشت، وہ جانور جو خدا کےسوا کسی اور کے نام پر ذبح کیا گیا ہو، وہ جو گلا گُھٹ کر، یا چوٹ کھاکر، یا بلندی سے گِر کر، یا ٹکر کھاکر مرا ہو، یا جسے کسی درندے نے پھاڑا ہو۔۔۔۔سوائے اس کے جسے تم نے زندہ پاکر ذبح کرلیا۔۔۔۔اور وہ جو کسی آستانے پر ذبح کیا گیا ہو۔ نیز یہ بھی تمہارے لیے ناجائز ہے کہ پانْسوں کے ذریعہ سے اپنی قسمت معلوم کرو۔ یہ سب افعال فسق ہیں۔ آج کافروں کو تمہارے دین کی طرف سے پُوری مایوسی ہوچکی ہے لہٰذا تم اُن سے نہ ڈرو بلکہ مجھ سے ڈرو۔ آج میں نے تمہارے دین کو تمہارے لیے مکمل کردیا ہے اور اپنی نعمت تم پر تمام کردی ہے اور تمہارے لیے اسلام کو تمہارے دین کی حیثیت سے قبول کرلیا ہے (لہٰذا حرام و حلال کی جو قیُود تم پر عائد کردی گئی ہیں اُن کی پابندی کرو) ۔ البَتّہ جو شخص بُھوک سے مجبُور ہوکر اُن میں سے کوئی چیز کھالے، بغیر اس کے کہ گناہ کی طرف اس کا میلان ہو تو بیشک اللہ معاف کرنے والا اور رحم فرمانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(5,4,'5:4','لوگ پُوچھتے ہیں کہ ان کے لیے کیا حلال کیا گیا ہے، کہو تمہارے لیے ساری پاک چیزیں حلال کردی گئی ہیں۔ اور جن شکاری جانوروں کو تم نے سَدھا یا ہو۔۔۔۔جن کو خدا کے دیے ہوئے علم کی بنا پر تم شکار کی تعلیم دیا کرتے ہو۔۔۔۔وہ جس جانور کو تمہارے لیے پکڑ رکھیں اس کو بھی تم کھاسکتے ہو، البتہ اس پر اللہ کا نام لے لو اور اللہ کا قانون توڑنے سے ڈرو، اللہ کو حساب لیتے کچھ دیر نہیں لگتی');
INSERT INTO "ur_maududi" VALUES(5,5,'5:5','آج تمہارے لیے ساری پاک چیزیں حلال کردی گئی ہیں۔ اہلِ کتاب کا کھانا تمہارے لیے حلال ہے اور تمہارا کھانا اُن کے لیے۔ اور محفوظ عورتیں بھی تمہارے لیے حلال ہیں خواہ وہ اہلِ ایمان کے گروہ سے ہوں یا اُن قوموں میں سے جن کو تم سے پہلے کتاب دی گئی تھی، بشرطیکہ تم اُن کے مہر ادا کرکے نکاح میں اُن کے محافظ بنو، نہ یہ کہ آزاد شہوت رانی کرنے لگو یا چوری چُھپے آشنائیاں کرو۔ اور جو کسی نے ایمان کی روش پر چلنے سے انکار کیا تو اس کا سارا کا سارا کارنامہٴ زندگی ضائع ہوجائے گا اور وہ آخرت میں دیوالیہ ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(5,6,'5:6','اے لوگو جو ایمان لائے ہو، جب تم نماز کے لیے اُٹھو تو چاہیے کہ اپنے مُنّہ اور ہاتھ کہنیوں تک دھولو، سروں پر ہاتھ پھیر لو اور پاوٴں ٹخنوں تک دھولیا کرو۔ اگر جَنابت کی حالت میں ہو تو نہاکر پاک ہوجاوٴ۔ اگر بیمار ہو یا سفر کی حالت میں ہو یا تم میں سے کوئی شخص رفع حاجت کرکے آئے یا تم نے عورتوں کو ہاتھ لگایا ہو، اور پانی نہ ملے، تو پاک مٹی سے کام لو، بس اُس پر ہاتھ مارکر اپنے مُنّہ اور ہاتھوں پر پھیر لیا کرو۔ اللہ تم پر زندگی کو تنگ نہیں کرنا چاہتا، مگر وہ چاہتا ہے کہ تمہیں پاک کرے اور اپنی نعمت تم پر تمام کردے، شاید کہ تم شکر گزار بنو');
INSERT INTO "ur_maududi" VALUES(5,7,'5:7','اللہ نے تم کو جو نعمت عطا کی ہے اس کا خیال رکھو اور اُس پختہ عہد و پیمان کو نہ بُھولو جو اُس نے تم سے لیا ہے، یعنی تمہارا یہ قول کہ ”ہم نے سُنا اور اطاعت قبول کی“۔ اللہ سے ڈرو، اللہ دلوں کے راز تک جانتا ہے');
INSERT INTO "ur_maududi" VALUES(5,8,'5:8','اے لوگو جو ایمان لائے ہو! اللہ کی خاطر راستی پر قائم رہنے والے اور انصاف کی گواہی دینے والے بنو۔ کسی گروہ کی دُشمنی تم کو اتنا مشتعل نہ کردے کہ انصاف سے پھرجاوٴ۔ عدل کرو، یہ خدا ترسی سے زیادہ مناسبت رکھتا ہے۔ اللہ سے ڈرکر کام کرتے رہو، جو کچھ تم کرتے ہو اللہ اُس سے پُوری طرح باخبر ہے');
INSERT INTO "ur_maududi" VALUES(5,9,'5:9','جو لوگ ایمان لائیں اور نیک عمل کریں، اللہ نے ان سے وعدہ کیا ہے کہ ان کی خطاؤں سے درگزر کیا جائے گا اور انہیں بڑا اجر ملے گا');
INSERT INTO "ur_maududi" VALUES(5,10,'5:10','رہے وہ لوگ جو کفر کریں اور اللہ کی آیات کو جھٹلائیں، تو وہ دوزخ میں جانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(5,11,'5:11','اے لوگو جو ایمان لائے ہو، اللہ کے اُس احسان کو یاد کرو جو اُس نے (ابھی حال میں) تم پر کیا ہے، جبکہ ایک گروہ نے تم پر دست درازی کا ارادہ کرلیا تھا مگر اللہ نے اُن کے ہاتھ تم پر اُٹھنے سے روک دیے۔ اللہ سے ڈرکر کام کرتے رہو، ایمان رکھنے والوں کو اللہ ہی پر بھروسہ کرنا چاہیے');
INSERT INTO "ur_maududi" VALUES(5,12,'5:12','اللہ نے بنی اسرائیل سے پُختہ عہد لیا تھا اور ان میں بارہ نقیب مقرر کیے تھے اور ان سے کہا تھا کہ ”میں تمہارے ساتھ ہوں، اگر تم نے نماز قائم رکھی اور زکوٰة دی اور میرے رسولوں کو مانا اور ان کی مدد کی اور اپنے خدا کو اچھا قرض دیتے رہے تو یقین رکھو کہ میں تمہاری بُرائیاں تم سے زائل کردوں گا اور تم کو ایسے باغوں میں داخل کروں گا جن کے نیچے نہریں بہتی ہوں گی ، مگر اس کے بعد جس نے تم میں سے کفر کی روش اختیار کی تو درحقیقت اُس نے سواء السبیل گم کردی۔“');
INSERT INTO "ur_maududi" VALUES(5,13,'5:13','پھر یہ اُن کا اپنے عہد کو توڑ ڈالنا تھا جس کی وجہ سے ہم نے ان کو اپنی رحمت سے دور پھینک دیا اور ان کے دل سخت کر دیے اب ان کا حال یہ ہے کہ الفاظ کا الٹ پھیر کر کے بات کو کہیں سے کہیں لے جاتے ہیں، جو تعلیم انہیں دی گئی تھی اُس کا بڑا حصہ بھول چکے ہیں، اور آئے دن تمہیں ان کی کسی نہ کسی خیانت کا پتہ چلتا رہتا ہے ان میں سے بہت کم لوگ اس عیب سے بچے ہوئے ہیں (پس جب یہ اِس حال کو پہنچ چکے ہیں تو جو شرارتیں بھی یہ کریں وہ ان سے عین متوقع ہیں) لہٰذا انہیں معاف کرو اور ان کی حرکات سے چشم پوشی کرتے رہو، اللہ اُن لوگوں کو پسند کرتا ہے جو احسان کی روش رکھتے ہیں');
INSERT INTO "ur_maududi" VALUES(5,14,'5:14','اِسی طرح ہم نے اُن لوگوں سے بھی پُختہ عہد لیا تھا جنہوں نے کہا تھا کہ ہم ”نصارٰی“ ہیں، مگر ان کو بھی جو سبق یاد کرایا گیا تھا اس کا ایک بڑا حصّہ اُنہوں نے فراموش کردیا، آخر کار ہم نے ان کے درمیان قیامت تک کے لیے دُشمنی اور آپس کے بُغض و عناد کا بیج بو دیا، اور ضرور ایک وقت آئے گا جب اللہ نہیں بتائے گا کہ وہ دنیا میں کیا بناتے رہے ہیں');
INSERT INTO "ur_maududi" VALUES(5,15,'5:15','اے اہلِ کتاب ! ہمارا رسول تمہارے پاس آگیا ہے جو کتابِ الٰہی کی بہت سی اُن کتابوں کو تمہارے سامنے کھول رہا ہے جن پر تم پردہ ڈالا کرتے تھے، اور بہت سی باتوں سے درگزر بھی کر جاتا ہے۔ تمہارے پاس اللہ کی طرف سے روشنی آگئی ہے اور ایک ایسی حق نما کتاب');
INSERT INTO "ur_maududi" VALUES(5,16,'5:16','جس کے ذریعہ سے اللہ تعالیٰ اُن لوگوں کو جو اس کی رضا کے طالب ہیں سلامتی کے طریقے بتاتا ہے اور اپنے اذن سے اُن کو اندھیروں سے نکال کر اُجالے کی طرف لاتا ہے اور راہِ راست کی طرف ان کی رہنمائی ہے');
INSERT INTO "ur_maududi" VALUES(5,17,'5:17','یقیناً کفر کیا اُن لوگوں نے جنہوں نے کہا کہ مسیح ؑ ابنِ مریم ہی خدا ہے۔ اے محمد ؐ ! ان سے کہو کہ اگر خدا مسیح ابنِ مریم کو اور اس کی ماں اور تمام زمین والوں کو ہلاک کردینا چاہے تو کس کی مجال ہے کہ اُس کو اِس ارادے سے باز رکھ سکے؟ اللہ تو زمین اور آسمانوں کا اور اُن سب چیزوں کا مالک ہے جو زمین اور آسمانوں کے درمیان پائی جاتی ہیں، جو کچھ چاہتا ہے پیدا کرتا ہے اور اس کی قدرت ہر چیز پر حاوی ہے');
INSERT INTO "ur_maududi" VALUES(5,18,'5:18','یہود اور نصاریٰ کہتے ہیں کہ ہم اللہ کے بیٹے اور اس کے چہیتے ہیں ان سے پوچھو، پھر وہ تمہارے گناہوں پر تمہیں سزا کیوں دیتا ہے؟ در حقیقت تم بھی ویسے ہی انسان ہو جیسے اور انسان خدا نے پیدا کیے ہیں وہ جسے چاہتا ہے معاف کرتا ہے اور جسے چاہتا ہے سزا دیتا ہے زمین اور آسمان اور ان کی ساری موجودات اس کی مِلک ہیں، اور اسی کی طرف سب کو جانا ہے');
INSERT INTO "ur_maududi" VALUES(5,19,'5:19','اے اہلِ کتاب ! ہمارا یہ رسُول ایسے وقت تمہارے پاس آیا ہے اور دین کی واضح تعلیم تمہیں دے رہا ہے جبکہ رسولوں کی آمد کا سلسلہ ایک مدّت سے بند تھا، تاکہ تم یہ نہ کہہ سکو کہ ہمارے پاس کوئی بشارت دینے والا اور ڈرانے والا نہیں آیا۔ سو دیکھو ! اب وہ بشارت دینے اور ڈرانے والا آگیا۔۔۔۔اور اللہ ہر چیز پر قادر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(5,20,'5:20','یاد کرو جب موسیٰ ؑ نے اپنی قوم سے کہا تھا کہ ”اے میری قوم کے لوگو! اللہ کی اُس نعمت کا خیال کرو جو اس نے تمہیں عطا کی تھی۔ اُس نے تم میں نبی پیدا کیے، تم کو فرماں روا بنایا، اور تم کو وہ کچھ دیا جو دُنیا میں کسی کو نہ دیا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(5,21,'5:21','اے برادران قوم ! اس مقدّس سرزمین میں داخل ہوجاوٴ جو اللہ نے تمہارے لیے لکھ دی ہے، پیچھے نہ ہٹو ورنہ ناکام و نامراد پلٹو گے“۔ ');
INSERT INTO "ur_maududi" VALUES(5,22,'5:22','انہوں نے جواب دیا "اے موسیٰؑ! وہاں تو بڑے زبردست لوگ رہتے ہیں، ہم وہاں ہرگز نہ جائیں گے جب تک وہ وہاں سے نکل نہ جائیں ہاں اگر وہ نکل گئے تو ہم داخل ہونے کے لیے تیار ہیں"');
INSERT INTO "ur_maududi" VALUES(5,23,'5:23','اُن ڈرنے والوں میں دو شخص ایسے بھی تھے جن کو اللہ نے اپنی نعمت سے نوازا تھا۔ انہوں نے کہا کہ ”اِن جبّاروں کے مقابلہ میں دروازے کے اندر گُھس جاوٴ، جب تم اندر پہنچ جاوٴ گے تو تم ہی غالب رہوگے۔ اللہ پر بھروسہ رکھو اگر تم مومن ہو“');
INSERT INTO "ur_maududi" VALUES(5,24,'5:24','لیکن اُنہوں نے پھر یہی کہا کہ "اے موسیٰؑ! ہم تو وہاں کبھی نہ جائیں گے جب تک وہ وہاں موجود ہیں بس تم اور تمہارا رب، دونوں جاؤ اور لڑو، ہم یہاں بیٹھے ہیں"');
INSERT INTO "ur_maududi" VALUES(5,25,'5:25','اس پر موسیٰؑ نے کہا "اے میرے رب، میرے اختیار میں کوئی نہیں مگر یا میری اپنی ذات یا میرا بھائی، پس تو ہمیں اِن نافرمان لوگوں سے الگ کر دے"');
INSERT INTO "ur_maududi" VALUES(5,26,'5:26','اللہ نے جواب دیا ”اچھا تو وہ مُلک چالیس (۴۰) سال تک ان پر حرام ہے، یہ زمین میں مارے مارے پھریں گے، ان نافرمانوں کی حالت پر ہرگز ترس نہ کھاوٴ“۔ ');
INSERT INTO "ur_maududi" VALUES(5,27,'5:27','اور ذرا انہیں آدم کے دو بیٹوں کا قصّہ بھی بے کم و کاست سُنادو۔ جب اُن دونوں نے قربانی کی تو ان میں سے ایک کی قربانی قبول کی گئی اور دوسرے کی نہ کی گئی۔ اُس نے کہا ”میں تجھے مار ڈالوں گا“۔ اس نے جواب دیا ”اللہ تو متقیوں ہی کی نذریں قبول کرتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(5,28,'5:28','اگرتُو مجھے قتل کرنے کے لیے ہاتھ اُٹھائے گا تو میں تجھے قتل کرنے کے لیے ہاتھ نہ اُٹھاوٴں گا، میں اللہ ربّ العا لمین سے ڈرتا ہوں');
INSERT INTO "ur_maududi" VALUES(5,29,'5:29','میں چاہتا ہوں کہ میرا اور اپنا گناہ تُو ہی سمیٹ لے اور دوزخی بن کررہے۔ ظالموں کے ظلم کا یہی ٹھیک بدلہ ہے“');
INSERT INTO "ur_maududi" VALUES(5,30,'5:30','آخر کار اس کے نفس نے اپنے بھائی کا قتل اس کے لیے آسان کر دیا اور وہ اسے مار کر اُن لوگوں میں شامل ہو گیا جو نقصان اٹھانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(5,31,'5:31','پھر اللہ نے ایک کوّا بھیجا جو زمین کھودنے لگا تاکہ اُسے بتائے کہ اپنے بھائی کی لاش کیسے چُھپائے۔ یہ دیکھ کر وہ بولا افسوس مجھ پر ! میں اس کوّے جیسا بھی نہ ہوسکا کہ اپنے بھائی کی لاش چُھپانے کی تدبیر نکال لیتا۔ اس کے بعد وہ اپنے کیے پر بہت پچھتایا۔ ');
INSERT INTO "ur_maududi" VALUES(5,32,'5:32','اسی وجہ سے بنی اسرائیل پر ہم نے یہ فرمان لکھ دیا تھا کہ ”جس نے کسی انسان کو خُون کے بدلے یا زمین میں فساد پھیلانے کے سوا کسی اور وجہ سے قتل کیا اس نے گویا تمام انسانون کو قتل کردیا اور جس نے کسی کی جان بچائی اُس نے گویا تمام انسانوں کو زندگی بخش دی“۔ مگر اُن کا حال یہ ہے کہ ہمارے رسُول پے درپے ان کے پاس کُھلی کُھلی ہدایات لے کر آئے پھر بھی ان میں بکثرت لوگ زمین میں زیادتیاں کرنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(5,33,'5:33','جو لوگ اللہ اور اس کے رسُول سے لڑتے ہیں اور زمین میں اس لیے تگ و دو کرتے پھرتے ہیں کہ فساد برپا کریں اُن کی سزا یہ ہے کہ قتل کیے جائیں، یا سُولی پر چڑھائے جائیں، یا اُن کے ہاتھ اور پاوٴں مخالف سمتوں سے کاٹ ڈالے جائیں، یا وہ جلا وطن کردیے جائیں۔ یہ ذلّت و رسوائی تو اُن کے لیے دُنیا میں ہے اور آخرت میں اُن کے لیے اس سے بڑی سزا ہے');
INSERT INTO "ur_maududi" VALUES(5,34,'5:34','مگر جو لوگ توبہ کرلیں قبل اس کے کہ تم ان پر قابو پاوٴ۔۔۔۔تمہیں معلوم ہونا چاہیے کہ اللہ معاف کرنے والا اور رحم فرمانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(5,35,'5:35','اے لوگو جو ایمان لائے ہو، اللہ سے ڈرو اور اُس کی جناب میں بار یابی کا ذریعہ تلاش کرو اور اس کی راہ میں جدوجہد کرو، شاید کہ تمہیں کامیابی نصیب ہوجائے');
INSERT INTO "ur_maududi" VALUES(5,36,'5:36','خوب جان لو کہ جن لوگوں نے کفر کا رویہ اختیار کیا ہے، اگر اُن کے قبضہ میں ساری زمین کی دولت ہو اور اتنی ہی اور اس کے ساتھ، اور وہ چاہیں کہ اسے فدیہ میں دے کر روز قیامت کے عذاب سے بچ جائیں، تب بھی وہ ان سے قبول نہ کی جائے گی اور انہیں درد ناک سزا مل کر رہے گی');
INSERT INTO "ur_maududi" VALUES(5,37,'5:37','وہ چاہیں گے کہ دوزخ کی آگ سے نکل بھاگیں مگر نہ نکل سکیں گے اور انہیں قائم رہنے والا عذاب دیا جائے گا');
INSERT INTO "ur_maududi" VALUES(5,38,'5:38','اور چور، خواہ عورت ہو یا مرد، دونوں کے ہاتھ کاٹ دو، یہ اُن کی کمائی کا بدلہ ہے اور اللہ کی طرف سے عبرت ناک سزا۔ اللہ کی قدرت سب پر غالب ہے اور وہ دانا ہ بینا ہے');
INSERT INTO "ur_maududi" VALUES(5,39,'5:39','پھر جو ظلم کرنے کے بعد توبہ کرے اور اپنی اصلاح کرلے تو اللہ کی نظرِ عنایت پھر اس پر مائل ہوجائے گی، اللہ بہت درگزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(5,40,'5:40','کیا تم جانتے نہیں ہو کہ اللہ زمین اور آسمانوں کی سلطنت کا مالک ہے؟ جسے چاہے سزا دے اور جسے چاہے معاف کر دے، وہ ہر چیز کا اختیار رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(5,41,'5:41','اے پیغمبرؐ ! تمہارے لیے باعث رنج نہ ہوں وہ لوگ جو کفر کی راہ میں بڑی تیزگامی دکھا رہے ہیں۔ خواہ وہ اُن میں سے ہوں جو منّہ سے کہتے ہیں، ہم ایمان لائے مگر دل اُن کے ایمان نہیں لائے، یا اُن میں سے ہوں جو یہودی بن گئے ہیں، جن کا حال یہ ہے کہ جُھوٹ کے لیے کان لگاتے ہیں، اور دُوسرے لوگوں کی خاطر، جو تمہارے پاس کبھی نہیں آئے، سُن گُن لیتے پھرتے ہیں، کتاب اللہ کے الفاظ کو اُن کا صحیح محل متعیّن ہونے کے باوجود اصل معنی سے پھیرتے ہیں، اور لوگوں سے کہتے ہیں کہ اگر تمہیں یہ حکم دیا جائے تو مانو، نہیں تو نہ مانو۔ جسے اللہ ہی نے فتنہ میں ڈالنے کا ارادہ کرلیاہو اس کو اللہ کی گرفت سے بچانے کے لیے تم کچھ نہیں کرسکتے، یہ وہ لوگ ہیں جن کے دلوں کو اللہ نے پاک کرنا نہ چاہا، ان کے لیے دُنیا میں رُسوائی ہے اور آخرت میں سخت سزا');
INSERT INTO "ur_maududi" VALUES(5,42,'5:42','یہ جُھوٹ سُننے والے اور حرام کے مال کھانے والے ہیں، لہٰذا اگر یہ تمہارے پاس (اپنے مقدمات لے کر) آئیں تو تمہیں اختیار دیا جاتا ہے کہ چاہو ان کا فیصلہ کرو ورنہ انکار کردو۔ انکار کردو تو یہ تمہارا کچھ بگاڑ نہیں سکتے، اور فیصلہ کرو تو پھر ٹھیک ٹھیک انصاف کے ساتھ کرو کہ اللہ انصاف کرنے والوں کو پسند کرتا ہے۔۔۔۔ ');
INSERT INTO "ur_maududi" VALUES(5,43,'5:43','اور یہ تمہیں کیسے حَکم بناتے ہیں جبکہ ان کے پاس توراة موجود ہے جس میں اللہ کا حکم لکھا ہوٴا ہے اور پھر یہ اس سے منہ موڑ رہے ہیں؟ اصل بات یہ ہے کہ یہ لوگ ایمان ہی نہیں رکھتے');
INSERT INTO "ur_maududi" VALUES(5,44,'5:44','ہم نے توراة نازل کی جس میں ہدایت اور روشنی تھی۔ سارے نبی، جو مُسلِم تھے، اُسی کے مطابق ان یہودی بن جانے والوں کے معاملات کا فیصلہ کرتے تھے، اور اِسی طرح ربّانی اور اَحبار بھی (اسی پر فیصلہ کا مدار رکھتے تھے) کیونکہ انہیں کتاب اللہ کی حفاظت کا ذمہ دار بنایا گیا تھا اور وہ اس پر گواہ تھے۔ پس (اے گروہِ یہود !) تم لوگوں سے نہ ڈرو بلکہ مجھ سے ڈرو اور میری آیات کو ذرا ذرا سے معاوضے لے کر بیچنا چھوڑ دو۔ جو لوگ اللہ کے نازل کردہ قانون کے مطابق فیصلہ نہ کریں وہی کافر ہیں');
INSERT INTO "ur_maududi" VALUES(5,45,'5:45','توراة میں ہم نے یہودیوں پر یہ حکم لکھ دیا تھا کہ جان کے بدلے جان، آنکھ کے بدلے آنکھ، ناک کے بدلے ناک، کان کے بدلے کان، دانت کے بدلے دانت، اور تمام زخموں کے لیے برابر کا بدلہ۔ پھر جو قصاص کا صدقہ کردے تو وہ اس کے لیے کَفّارہ ہے، اور جو لوگ اللہ کے نازل کردہ قانون کے مطابق فیصلہ نہ کریں وہی ظالم ہیں');
INSERT INTO "ur_maududi" VALUES(5,46,'5:46','پھر ہم نے ان پیغمبروں کے بعد مریم ؑ کے بیٹے عیسٰی ؑ کو بھیجا۔ توراة میں سے جو کچھ اس کے سامنے موجود تھا وہ اس کی تصدیق کرنے والا تھا۔ اور ہم نے اس کو انجیل عطا کی جس میں رہنمائی اور روشنی تھی اور وہ بھی توراة میں سے جو کچھ اُس وقت موجود تھا اُس کی تصدیق کرنے والی تھی اور خداترس لوگوں کے لیے سراسر ہدایت اور نصیحت تھی');
INSERT INTO "ur_maududi" VALUES(5,47,'5:47','ہمارا حکم تھا کہ اہلِ انجیل اس قانون کے مطابق فیصلہ کریں جو اللہ نے اس میں نازل کیا ہے اور جو لوگ اللہ کے نازل کردہ قانون کے مطابق فیصلہ نہ کریں وہی فاسق ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(5,48,'5:48','پھر اے محمد ؐ ! ہم نے تمہاری طرف یہ کتاب بھیجی جو حق لے کر آئی ہے اور الکتاب میں سے جو کچھ اس کے آگے موجود ہے اُس کی تصدیق کرنے والی اور اس کی محافظ و نگہبَان ہے۔ لہٰذا تم خدا کے نازل کردہ قانون کے مطابق لوگوں کے معاملات کا فیصلہ کرو اور جو حق تمہارے پاس آیا ہے اُس سے مُنّہ موڑ کر ان کی خواہشات کی پیروی نہ کرو۔۔۔۔ ہم نے تم میں سے ہر ایک کے لیے ایک شریعت اور ایک راہِ عمل مقرر کی۔ اگرچہ تمہارا خدا چاہتا تو تم سب کو ایک اُمّت بھی بناسکتا تھا، لیکن اُس نے یہ اِس لیے کیا کہ جو کچھ اُس نے تم لوگوں کو دیا ہے اس میں تمہاری آزمائش کرے۔ لہٰذا بھلائیوں میں ایک دُوسرے سے سبقت لے جانے کی کوشش کرو۔ آخر کار تم سب کو خدا کی طرف پلٹ کر جانا ہے، پھر وہ تمہیں اصل حقیقت بتادے گا جس میں تم اختلاف کرتے رہے ہو');
INSERT INTO "ur_maududi" VALUES(5,49,'5:49',' پس اے محمدؐ ! تم اللہ کے نازل کردہ قانون کے مطابق ان لوگوں کے معاملات کا فیصلہ کرو اور ان کی خواہشات کی پیروی نہ کرو۔ ہوشیار رہو کہ یہ لوگ تم کو فتنہ میں ڈال کر اُس ہدایت سے ذرہ برابر منحرف نہ کرنے پائیں جو خدا نے تمہاری طرف نازل کی ہے پھر اگر یہ اس سے منہ موڑیں تو جان لو کہ اللہ نے ان کے بعض گناہوں کی پاداش میں ان کو مبتلائے مصیبت کرنے کا ارادہ ہی کرلیا ہے، اور یہ حقیقت ہے کہ اِن لوگوں میں سے اکثر فاسق ہیں');
INSERT INTO "ur_maududi" VALUES(5,50,'5:50','(اگر یہ خدا کے قانون سے مُنّہ موڑتے ہیں) تو کیا پھر جاہلیّت کا فیصلہ چاہتے ہیں؟ حالانکہ جو لوگ اللہ پر یقین رکھتے ہیں ان کے نزدیک اللہ سے بہتر فیصلہ کرنے والا کوئی نہیں ہے');
INSERT INTO "ur_maududi" VALUES(5,51,'5:51','اے لوگو جو ایمان لائے ہو! یہودیوں اور عیسائیوں کو اپنا رفیق نہ بناؤ، یہ آپس ہی میں ایک دوسرے کے رفیق ہیں اور اگر تم میں سے کوئی ان کو اپنا رفیق بناتا ہے تو اس کا شمار بھی پھر انہی میں ہے، یقیناً اللہ ظالموں کو اپنی رہنمائی سے محروم کر دیتا ہے');
INSERT INTO "ur_maududi" VALUES(5,52,'5:52','تم دیکھتے ہو کہ جن کے دلوں میں نفاق کی بیماری ہے وہ اُنہی میں دَوڑ دُھوپ کرتے پھرتے ہیں۔ کہتے ہیں ”ہمیں ڈر لگتا ہے کہ کہیں ہم کسی مصیبت کے چکّر میں نہ پھنس جائیں“۔ مگر بعید نہیں کہ اللہ جب تمہیں فیصلہ کُن فتح بخشے گا یا اپنی طرف سے کوئی اور بات ظاہر کرے گا تو یہ لوگ اپنے اِس نفاق پر جسے یہ دلوں میں چُھپائے ہوئے ہیں نادم ہوں گے');
INSERT INTO "ur_maududi" VALUES(5,53,'5:53','اور اُس وقت اہل ایمان کہیں گے ”کیا یہ وہی لوگ ہیں جو اللہ کے نام سے کڑی کڑی قسمیں کھاکر یقین دلاتے تھے کہ ہم تمہارے ساتھ ہیں“؟۔۔۔۔ان کے سب اعمال ضائع ہوگئے اور آخر کار یہ ناکام ہ نامراد ہوکر رہے۔ ');
INSERT INTO "ur_maududi" VALUES(5,54,'5:54','اے لوگو جو ایمان لائے ہو، اگر تم میں سے کوئی اپنے دین سے پھرتا ہے (تو پھر جائے) اللہ اور بہت سے لوگ ایسے پیدا کردے گا جو اللہ کو محبُوب ہوں گے اور اللہ اُن کو محبُوب ہوگا، جو مومنوں پر نرم اور کفّار پر سخت ہوں گے، جو اللہ کی راہ میں جدوجہد کریں گے اور کسی ملامت کرنے والے کی ملامت سے نہ ڈریں گے۔ یہ اللہ کا فضل ہے، جسے چاہتا ہے عطا کرتا ہے۔ اللہ وسیع ذرائع کا مالک ہے اور سب کچھ جانتا ہے');
INSERT INTO "ur_maududi" VALUES(5,55,'5:55','تمہارے رفیق تو حقیقت میں صرف اللہ اور اللہ کا رسول اور وہ اہل ایمان ہیں جو نماز قائم کرتے ہیں، زکوٰۃ دیتے ہیں اور اللہ کے آگے جھکنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(5,56,'5:56','اور جو اللہ اور اس کے رسول اور اہل ایمان کو اپنا رفیق بنا لے اُسے معلوم ہو کہ اللہ کی جماعت ہی غالب رہنے والی ہے');
INSERT INTO "ur_maududi" VALUES(5,57,'5:57','ا ے لوگو جو ایمان لائے ہو، تمہارے پیش رو اہل کتاب میں سے جن لوگوں نے تمہارے دین کو مذاق اور تفریح کا سامان بنا لیا ہے، اُنہیں اور دوسرے کافروں کو اپنا دوست اور رفیق نہ بناؤ اللہ سے ڈرو اگر تم مومن ہو');
INSERT INTO "ur_maududi" VALUES(5,58,'5:58','جب تم نماز کے لیے منادی کرتے ہو تو وہ اس کا مذاق اُڑاتے اور اس سے کھیلتے ہیں۔ اس کی وجہ یہ ہے کہ وہ عقل نہیں رکھتے۔ ');
INSERT INTO "ur_maududi" VALUES(5,59,'5:59','اِن سے کہو، "اے اہل کتاب! تم جس بات پر ہم سے بگڑے ہو وہ اس کے سوا اور کیا ہے کہ ہم اللہ پر اور دین کی اُس تعلیم پر ایمان لے آئے ہیں جو ہماری طرف نازل ہوئی ہے اور ہم سے پہلے بھی نازل ہوئی تھی، اور تم میں سے اکثر لوگ فاسق ہیں؟"');
INSERT INTO "ur_maududi" VALUES(5,60,'5:60','پھر کہو ”کیا میں اُن لوگوں کی نشاندہی کروں جن کا انجام خدا کے ہاں فاسقوں کے انجام سے بھی بدتر ہے؟ وہ جن پر خدا نے لعنت کی، جن پر اُس کا غضب ٹوٹا، جن میں سے بندر اور سُور بنائے گئے، جنہوں نے طاغوت کی بندگی کی۔ ان کا درجہ اور بھی زیادہ بُرا ہے اور وہ سَوَاءُ السّبیل سے بہت زیادہ بھٹکے ہوئے ہیں“۔ ');
INSERT INTO "ur_maududi" VALUES(5,61,'5:61','جب یہ تم لوگوں کے پاس آتے ہیں تو کہتے ہیں کہ ہم ایمان لائے، حالانکہ کفر لیے ہوئے آئے تھے اور کفر ہی لیے ہوئے واپس گئے اور اللہ خوب جانتا ہے جو کچھ یہ دلوں میں چھپائے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(5,62,'5:62','تم دیکھتے ہو کہ ان میں سے بکثرت لوگ گناہ اور ظلم و زیادتی کے کاموں میں دوڑ دھوپ کرتے پھرتے ہیں اور حرام کے مال کھاتے ہیں بہت بری حرکات ہیں جو یہ کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(5,63,'5:63','کیوں اِن کے عُلما٫ اور مشائخ انہیں گناہ پر زبان کھولنے اور حرام کھانے سے نہیں روکتے؟ یقیناً بہت ہی برا کارنامہ زندگی ہے جو وہ تیار کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(5,64,'5:64','یہُودی کہتے ہیں اللہ کے ہاتھ بندھے ہوئے ہیں۔۔۔۔ باندھے گئے ان کے ہاتھ، اور لعنت پڑی اِن پر اُس بکواس کی بدولت جو یہ کرتے ہیں۔۔۔۔ اللہ کے ہاتھ تو کشادہ ہیں، جس طرح چاہتا ہے خرچ کرتا ہے۔ حقیقت یہ ہے کہ جو کلام تمہارے رب کی طرف سے تم پر نازل ہوا ہے وہ ان میں سے اکثر لوگوں کی سرکشی و باطل پرستی میں اُلٹے اضافہ کا موجب بن گیا ہے، اور (اس کی پاداش میں) ہم نے ان کے درمیان قیامت تک کے لیے عداوت اور دُشمنی ڈال دی ہے۔ جب کبھی یہ جنگ کی آگ بھڑکاتے ہیں اللہ اُس کو ٹھنڈا کردیتا ہے۔ یہ زمین میں فساد پَھیلانے کی سعی کررہے ہیں مگر اللہ فساد برپا کرنے والوں کو ہرگز پسند نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(5,65,'5:65','اگر (اِس سرکشی کے بجائے) یہ اہل کتاب ایمان لے آتے اور خدا ترسی کی روش اختیار کرتے تو ہم اِن کی برائیاں اِن سے دور کر دیتے اور ان کو نعمت بھری جنتوں میں پہنچاتے');
INSERT INTO "ur_maududi" VALUES(5,66,'5:66','کاش انہوں نے توراة اور انجیل اور اُن دوسری کتابوں کو قائم کیا ہوتا جو اِن کے رب کی طرف سے اِن کے پاس بھیجی گئی تھیں۔ ایسا کرتے تو اِن کے لیے اُوپر سے رزق برستا اور نیچے سے اُبلتا۔ اگرچہ ان میں کچھ لوگ راست رَو بھی ہیں لیکن ان کی اکثریت سخت بد عمل ہے');
INSERT INTO "ur_maududi" VALUES(5,67,'5:67','اے پیغمبرؐ! جو کچھ تمہارے رب کی طرف سے تم پر نازل کیا گیا ہے وہ لوگوں تک پہنچا دو اگر تم نے ایسا نہ کیا تو اس کی پیغمبری کا حق ادا نہ کیا اللہ تم کو لوگوں کے شر سے بچانے والا ہے یقین رکھو کہ وہ کافروں کو (تمہارے مقابلہ میں) کامیابی کی راہ ہرگز نہ دکھائے گا');
INSERT INTO "ur_maududi" VALUES(5,68,'5:68','صاف کہہ دو کہ ”اے اہلِ کتاب ! تم ہرگز کسی اصل پر نہیں ہو جب تک کہ توراة اور انجیل اور اُن دُوسری کتابوں کو قائم نہ کرو جو تمہارے رب کی طرف سے نازل کی گئی ہیں“۔ ضرور ہے کہ یہ فرمان جو تم پر نازل کیا گیا ہے ان میں سے اکثر کی سرکشی اور انکار کو اور زیادہ بڑھادے گا۔ مگر انکار کرنے والوں کے حال پر کچھ افسوس نہ کرو');
INSERT INTO "ur_maududi" VALUES(5,69,'5:69','یقین جانو کہ یہاں اجارہ کسی کا بھی نہیں ہے) مسلمان ہوں یا یہودی، صابی ہوں یا عیسائی، جو بھی اللہ اور روزِ آخر پر ایمان لائے گا اور نیک عمل کرے گا بے شک اس کے لیے نہ کسی خوف کا مقام ہے نہ رنج کا۔ ');
INSERT INTO "ur_maududi" VALUES(5,70,'5:70','ہم نے بنی اسرائیل سے پختہ عہد لیا اور اُن کی طرف بہت سے رسول بھیجے، مگر جب کبھی ان کے پاس کوئی رسول اُن کی خواہشات نفس کے خلاف کچھ لے کر آیا تو کسی کو انہوں نے جھٹلایا اور کسی کو قتل کر دیا');
INSERT INTO "ur_maududi" VALUES(5,71,'5:71','اور اپنے نزدیک یہ سمجھے کہ کوئی فتنہ رونما نہ ہوگا، اس لیے اندھے اور بہرے بن گئے پھر اللہ نے اُنہیں معاف کیا تو اُن میں سے اکثر لوگ اور زیادہ اندھے اور بہرے بنتے چلے گئے اللہ اُن کی یہ سب حرکات دیکھتا رہا ہے');
INSERT INTO "ur_maududi" VALUES(5,72,'5:72','یقیناً کفر کیا اُن لوگوں نے جنہوں نے کہا کہ اللہ مسیح ابن مریم ہی ہے حالانکہ مسیح نے کہا تھا کہ "اے بنی اسرائیل! اللہ کی بندگی کرو جو میرا رب بھی ہے اور تمہارا رب بھی" جس نے اللہ کے ساتھ کسی کو شریک ٹھیرایا اُس پر اللہ نے جنت حرام کر دی اور اُس کا ٹھکانا جہنم ہے اور ایسے ظالموں کا کوئی مدد گار نہیں');
INSERT INTO "ur_maududi" VALUES(5,73,'5:73','یقیناً کفر کیا اُن لوگوں نے جنہوں نے کہا کہ اللہ تین میں کا ایک ہے، حالانکہ ایک خدا کے سوا کوئی خدا نہیں ہے اگر یہ لوگ اپنی اِن باتوں سے باز نہ آئے تو ان میں سے جس جس نے کفر کیا ہے اُس کو درد ناک سزا دی جائے گی');
INSERT INTO "ur_maududi" VALUES(5,74,'5:74','پھر کیا یہ اللہ سے توبہ نہ کریں گے اور اس سے معافی نہ مانگیں گے؟ اللہ بہت درگزر فرمانے والا اور رحم کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(5,75,'5:75','مسیح ؑ ابنِ مریم ؑ اِس کے سوا کچھ نہیں کہ بس ایک رسُول تھا، اُس سے پہلے اور بھی بہت سے رسُول گزر چکے تھے، اس کی ماں ایک راستباز عورت تھی، اور وہ دونوں کھانا کھاتے تھے۔ دیکھو ہم کس طرح ان کے سامنے حقیقت کی نشانیاں واضح کرتے ہیں، پھر دیکھو یہ کدھر اُلٹے پھرے جاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(5,76,'5:76','اِن سے کہو، کیا تم اللہ کو چھوڑ کر اُس کی پرستش کرتے ہو جو نہ تمہارے لیے نقصان کا اختیار رکھتا ہے نہ نفع کا؟ حالانکہ سب کی سننے والا اور سب کچھ جاننے والا تو اللہ ہی ہے');
INSERT INTO "ur_maududi" VALUES(5,77,'5:77','کہو، اے اہلِ کتاب ! اپنے دین میں ناحق غلُو نہ کرو اور اُن لوگوں کے تخیّلات کی پیروی نہ کرو جو تم سے پہلے خود گمراہ ہوئے اور بہتوں کو گمراہ کیا اور ”سَوَاءُالسّبیل“ سے بھٹک گئے۔ ');
INSERT INTO "ur_maududi" VALUES(5,78,'5:78','بنی اسرائیل میں سے جن لوگوں نے کفر کی راہ اختیار کی اُن پر داؤدؑ اور عیسیٰ ابن مریمؑ کی زبان سے لعنت کی گئی کیونکہ وہ سرکش ہوگئے تھے اور زیادتیاں کرنے لگے تھے');
INSERT INTO "ur_maududi" VALUES(5,79,'5:79','انہوں نے ایک دُوسرے کو بُرے افعال کے ارتکاب سے روکنا چھوڑ دیا تھا، بُرا طرز عمل تھا جو اُنہوں ے اختیار کیا');
INSERT INTO "ur_maududi" VALUES(5,80,'5:80','آج تم اُن میں بکثرت ایسے لوگ دیکھتے ہو جو (اہل ایمان کے مقابلہ میں) کفار کی حمایت و رفاقت کرتے ہیں یقیناً بہت برا انجام ہے جس کی تیاری اُن کے نفسوں نے اُن کے لیے کی ہے، اللہ اُن پر غضب ناک ہو گیا ہے اور وہ دائمی عذاب میں مبتلا ہونے والے ہیں');
INSERT INTO "ur_maududi" VALUES(5,81,'5:81','اگر فی الواقع یہ لوگ اللہ اور پیغمبر ؐ اور اُس چیز کے ماننے والے ہوتے جو پیغمبر ؐ پر نازل ہوئی تھی تو کبھی (اہلِ ایمان کے مقابلہ میں) کافروں کو اپنا رفیق نہ بناتے۔ مگر ان میں سے تو بیشتر لوگ خدا کی اطاعت سے نکِل چکے ہیں');
INSERT INTO "ur_maududi" VALUES(5,82,'5:82','تم اہل ایمان کی عداوت میں سب سے زیادہ سخت یہود اور مشرکین کو پاؤ گے، اور ایمان لانے والوں کے لیے دوستی میں قریب تر اُن لوگوں کو پا ؤ گے جنہوں نے کہا تھا کہ ہم نصاریٰ ہیں یہ اِس وجہ سے کہ ان میں عبادت گزار عالم اور تارک الدنیا فقیر پائے جاتے ہیں اور اُن میں غرور نفس نہیں ہے');
INSERT INTO "ur_maududi" VALUES(5,83,'5:83','جب وہ اس کلام کو سنتے ہیں جو رسول پر اترا ہے تو تم دیکھتے ہو کہ حق شناسی کے اثر سے اُن کی آنکھیں آنسوؤں سے تر ہو جاتی ہیں وہ بول اٹھتے ہیں کہ "پروردگار! ہم ایمان لائے، ہمارا نام گواہی دینے والوں میں لکھ لے"');
INSERT INTO "ur_maududi" VALUES(5,84,'5:84','اور وہ کہتے ہیں کہ "آخر کیوں نہ ہم اللہ پر ایمان لائیں اور جو حق ہمارے پاس آیا ہے اُسے کیوں نہ مان لیں جبکہ ہم اِس بات کی خواہش رکھتے ہیں کہ ہمارا رب ہمیں صالح لوگوں میں شامل کرے؟"');
INSERT INTO "ur_maududi" VALUES(5,85,'5:85','اُن کے اِس قول کی وجہ سے اللہ نے اُن کو ایسی جنتیں عطا کیں جن کے نیچے نہریں بہتی ہیں اور وہ ان میں ہمیشہ رہیں گے یہ جزا ہے نیک رویہ اختیار کرنے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(5,86,'5:86','رہے وہ لوگ جنہوں نے ہماری آیات کو ماننے سے انکار کیا اور انہیں جھٹلایا، تو وہ جہنم کے مستحق ہیں');
INSERT INTO "ur_maududi" VALUES(5,87,'5:87','اے لوگو جو ایمان لائے ہو، جو پاک چیزیں اللہ نے تمہارے لیے حلال کی ہیں انہیں حرام نہ کرلو اور حد سے تجاوز نہ کرو، اللہ کو زیادتی کرنے والے سخت ناپسند ہیں');
INSERT INTO "ur_maududi" VALUES(5,88,'5:88','جو کچھ حلال و طیب رزق اللہ نے تم کو دیا ہے اُسے کھا ؤ پیو اور اُس خدا کی نافرمانی سے بچتے رہو جس پر تم ایمان لائے ہو');
INSERT INTO "ur_maududi" VALUES(5,89,'5:89','تم لوگ جو مُہمل قسمیں کھالیتے ہو اُن پر اللہ گرفت نہیں کرتا، مگر جو قسمیں تم جان بُوجھ کر کھاتے ہو اُن پر وہ ضرور تم سے مواخذہ کرے گا۔ (ایسی قسم توڑنے کا)کفّارہ یہ ہے کہ دس ( ) مسکینوں کو وہ اَوسط درجہ کا کھانا کھلاوٴ جو تم اپنے بال بچّوں کو کھلاتے ہو، یا انہیں کپڑے پہناوٴ، یا ایک غلام آزاد کرو، اور جو اس کی استطاعت نہ رکھتا ہو وہ تین دن کے روزے رکھے۔ یہ تمہاری قسموں کا کفّارہ ہے جبکہ تم قسم کھاکر توڑدو۔ اپنی قسموں کی حفاظت کیا کرو۔ اس طرح اللہ اپنے احکام تمہارے لیے واضح کرتا ہے شاید کہ تم شکر ادا کرو');
INSERT INTO "ur_maududi" VALUES(5,90,'5:90','اے لوگو جو ایمان لائے ہو، یہ شراب اور جُوا اور یہ آستانے اور پانسے، یہ سب گندے شیطانی کام ہیں، ان سے پرہیز کرو، اُمیّد ہے کہ تمہیں فلاح نصیب ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(5,91,'5:91','شیطان تو یہ چاہتا ہے کہ شراب اور جوئے کے ذریعہ سے تمہارے درمیان عداوت اور بغض ڈال دے اور تمہیں خدا کی یاد سے اور نماز سے روک دے پھر کیا تم ان چیزوں سے باز رہو گے؟');
INSERT INTO "ur_maududi" VALUES(5,92,'5:92','اللہ اور اُس کے رسول کی بات مانو اور باز آ جاؤ، لیکن اگر تم نے حکم عدولی کی تو جان لو کہ ہمارے رسول پر بس صاف صاف حکم پہنچا دینے کی ذمہ داری تھی');
INSERT INTO "ur_maududi" VALUES(5,93,'5:93','جو لوگ ایمان لے آئے اور نیک عمل کرنے لگے انہوں نے پہلے جو کچھ کھایا پیا تھا اس پر کوئی گرفت نہ ہوگی بشرطیکہ وہ آئندہ اُن چیزوں سے بچے رہیں جو حرام کی گئی ہیں اور ایمان پر ثابت قدم رہیں اور اچھے کام کریں، پھر جس جس چیز سے روکا جائے اس سے رکیں اور جو فرمان الٰہی ہو اُسے مانیں، پھر خدا ترسی کے ساتھ نیک رویہ رکھیں اللہ نیک کردار لوگوں کو پسند کرتا ہے');
INSERT INTO "ur_maududi" VALUES(5,94,'5:94','اے لوگو جو ایمان لائے ہو، اللہ تمہیں اُس شکار کے ذریعہ سے سخت آزمائش میں ڈالے گا جو بالکل تمہارے ہاتھوں اور نیزوں کی زد میں ہوگا، یہ دیکھنے کے لیے کہ تم میں سے کون اس سے غائبانہ ڈرتا ہے، پھر جس نے اس تنبیہ کے بعد اللہ کی مقرر کی ہوئی حد سے تجاوز کیا اُس کے لیے درد ناک سزا ہے');
INSERT INTO "ur_maududi" VALUES(5,95,'5:95','اے لوگو جو ایمان لائے ہو ! اِحرام کی حالت میں شکار نہ مارو، اور اگر تم میں سے کوئی جان بوجھ کر ایسا کر گزرے تو جو جانور اس نے مارا ہو اُسی کے ہم پلّہ ایک جانور اُسے مویشیوں میں سے نذر دینا ہوگا جس کا فیصلہ تم میں سے دو عادل آدمی کریں گے، اور یہ نذرانہ کعبہ پہنچایا جائے گا، یا نہیں تو اس گناہ کے کفارہ میں چند مسکینوں کو کھانا کھلانا ہوگا، یا اس کے بقدر روزے رکھنے ہوں گے، تاکہ وہ اپنے کیے کا مزہ چکھے۔ پہلے جو کچھ ہو چکا اُسے اللہ نے معاف کردیا، لیکن اب اگر کسی نے اس حرکت کا اعادہ کیا تو اس سے اللہ بدلہ لے گا، اللہ سب پر غالب ہے اور بدلہ لینے کی طاقت رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(5,96,'5:96','تمہارے لیے سمندر کا شکار اور اس کا کھانا حلال کردیا گیا، جہاں تم ٹھیرو وہاں بھی اُسے کھاسکتے ہو اور قافلے کے لیے زادِراہ بھی بناسکتے ہو۔ البتّہ خشکی کا شکار جب تک تم اِحرام کی حالت میں ہو، تم پر حرام کیا گیا ہے۔ پس بچو اُس خدا کی نا فرمانی سے جس کی پیشی میں تم سب کو گھیر کر حاضر کیا جائے گا');
INSERT INTO "ur_maududi" VALUES(5,97,'5:97','اللہ نے مکانِ مُحترم، کعبہ کو لوگوں کے لیے (اجتماعی زندگی کے) قیام کا ذریعہ بنایا اور ماہِ حرام اور قربانی کے جانوروں اور قَلادوں کو بھی (اِس کام میں معاون بنادیا) تاکہ تمہیں معلوم ہو جائے کہ اللہ آسمانوں اور زمین کے سب حالات سے باخبر ہے اور اُسے ہر چیز کا علم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(5,98,'5:98','خبردار ہو جاؤ! اللہ سزا دینے میں بھی سخت ہے اور اس کے ساتھ بہت درگزر اور رحم بھی کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(5,99,'5:99','رسول پر تو صرف پیغام پہنچا دینے کی ذمہ داری ہے، آگے تمہارے کھلے اور چھپے سب حالات کا جاننے والا اللہ ہے');
INSERT INTO "ur_maududi" VALUES(5,100,'5:100','اے پیغمبر ؐ ! اِن سے کہہ دو کہ پاک اور ناپاک بہرحال یکساں نہیں ہیں خواہ ناپاک کی بہتات تمہیں کتنا ہی فریفتہ کرنے والی ہو، پس اے لوگو جو عقل رکھتے ہو ! اللہ کی نافرمانی سے بچتے رہو، اُمّید ہے کہ تمہیں فلاح نصیب ہوگی');
INSERT INTO "ur_maududi" VALUES(5,101,'5:101','اے لوگو جو ایمان لائے ہو، ایسی باتیں نہ پوچھا کرو جو تم پر ظاہر کردی جائیں تو تمہیں ناگوار ہوں، لیکن اگر تم انہیں ایسے وقت پُوچھوگے جب کہ قرآن نازل ہورہا ہو تو وہ تم پرکھول دی جائیں گی۔ اب تک جو کچھ تم نے کیا اُسے اللہ نے معاف کردیا، وہ درگزر کرنے والا اور بُردبار ہے');
INSERT INTO "ur_maududi" VALUES(5,102,'5:102','تم سے پہلے ایک گروہ نے اِسی قسم کے سوالات کیے تھے، پھر وہ لوگ انہی باتوں کی وجہ سے کفر میں مُبتلا ہوگئے۔ ');
INSERT INTO "ur_maududi" VALUES(5,103,'5:103','اللہ نے نہ کوئی بَحِیرہ مقرر کیا ہے نہ سائبَہ اور نہ وَصیْلہ اور نہ حام۔ مگر یہ کافر اللہ پر جُھوٹی تہمت لگاتے ہیں اور ان میں سے اکثر بے عقل ہیں (کہ ایسے وہمیّات کو مان رہے ہیں)');
INSERT INTO "ur_maududi" VALUES(5,104,'5:104','اور جب ان سے کہا جاتا ہے کہ آؤ اُس قانون کی طرف جو اللہ نے نازل کیا ہے اور آؤ پیغمبرؐ کی طرف تو وہ جواب دیتے ہیں کہ ہمارے لیے تو بس وہی طریقہ کافی ہے جس پر ہم نے اپنے باپ دادا کو پایا ہے کیا یہ باپ دادا ہی کی تقلید کیے چلے جائیں گے خواہ وہ کچھ نہ جانتے ہوں اور صحیح راستہ کی انہیں خبر ہی نہ ہو؟');
INSERT INTO "ur_maududi" VALUES(5,105,'5:105','اے لوگو جو ایمان لائے ہو، اپنی فکر کرو، کسی دُوسرے کی گمراہی سے تمہارا کُچھ نہیں بگڑتا اگر تم خود راہِ راست پر ہو، اللہ کی طرف تم سب کو پلٹ کر جانا ہے، پھر وہ تمہیں بتادے گا کہ تم کیا کرتے رہے ہو');
INSERT INTO "ur_maududi" VALUES(5,106,'5:106','اے لوگو جو ایمان لائے ہو، جب تم میں سے کسی کی موت کا وقت آجائے اور وہ وصیّت کررہا ہو تو اس کے لیے شہادت کا نصاب یہ ہے کہ تمہاری جماعت میں سے دو صاحبِ عدل آدمی گواہ بنائے جائیں، یا اگر تم سفر کی حالت میں ہو اور وہاں موت کی مصیبت پیش آجائے تو غیر مسلموں ہی میں سے دو گواہ لے لیے جائیں۔ پھر اگر کوئی شک پڑجائے تو نماز کے بعد دونوں گواہوں کو مسجد میں روک لیا جائے اور وہ خدا کی قسم کھاکر کہیں کہ ”ہم کسی ذاتی فائدے کے عوض شہادت بیچنے والے نہیں ہیں، اورخواہ کوئی ہمارارشتہ دار ہی کیوں نہ ہو (ہم اس کی رعایت کرنے والے نہیں)، اور نہ خدا واسطے کی گواہی کو ہم چھپانے والے ہیں، اگر ہم نے ایسا کیا تو گناہ گاروں میں شمار ہوں گے“');
INSERT INTO "ur_maududi" VALUES(5,107,'5:107','لیکن اگر پتہ چل جائے کہ ان دونوں نے اپنے آپ کو گناہ میں مبتلا کیا ہے تو پھر ان کی جگہ دو اور شخص جو ان کی بہ نسبت شہادت دینے کے لیے اہل تر ہوں ان لوگوں میں سے کھڑے ہوں جن کی حق تلفی ہوئی ہو، اور وہ خدا کی قسم کھا کر کہیں کہ "ہماری شہادت اُن کی شہادت سے زیادہ بر حق ہے اور ہم نے اپنے گواہی میں کوئی زیادتی نہیں کی ہے، اگر ہم ایسا کریں تو ظالموں میں سے ہوں گے"');
INSERT INTO "ur_maududi" VALUES(5,108,'5:108','اس طریقہ سے زیادہ توقع کی جاسکتی ہے کہ لوگ ٹھیک ٹھیک شہادت دیں گے، یا کم از کم اس بات ہی کا خوف کریں گے کہ ان کی قسموں کے بعد ددسری قسموں سے کہیں ان کی تردید نہ ہو جائے اللہ سے ڈرو اور سنو، اللہ نافرمانی کرنے والوں کو اپنی رہنمائی سے محروم کر دیتا ہے');
INSERT INTO "ur_maududi" VALUES(5,109,'5:109','جس روز اللہ سب رسولوں کو جمع کرکے پُوچھے گا کہ تمہیں کیا جواب دیا گیا، تو وہ عرض کریں گے کہ ہمیں کچھ علم نہیں، آپ ہی تمام پوشیدہ حقیقتوں کو جانتے ہیں');
INSERT INTO "ur_maududi" VALUES(5,110,'5:110','پھر تصوّر کرو اس موقع کا جب اللہ فرمائیگا کہ ”اے مریم کے بیٹے عیسٰی ؑ! یاد کر میری اس نعمت کو جو میں نے تجھے اور تیری ماں کو عطا کی تھی، میں نے رُوح پاک سے تیری مدد کی، تُو گہوارے میں بھی لوگوں سے بات کرتا تھا اور بڑی عمر کو پہنچ کر بھی، میں نے تجھ کو کتاب اور حکمت اور تورات اور انجیل کی تعلیم دی، تُو میرے حکم سے مٹی کا پُتلا پرندے کی شکل کا بناتا اور اس میں پُھونکتا تھا، اور وہ میرے حکم سے پرندہ بن جاتا تھا ، تُو مادر زاد اندھے کو کوڑھی کو میرے حکم سے اچھا کرتا تھا، تُو مُردوں کو میرے حکم سے نکالتا تھا، پھر جب تُو بنی اسرائیل کے پاس صریح نشانیاں لے کر پہنچا اور جو لوگ ان میں سے منکرِ حق تھے انہوں نے کہا کہ یہ نشانیاں جادوگری کے سوا اور کچھ نہیں ہیں تو میں نے ہی تجھے اُن سے بچایا');
INSERT INTO "ur_maududi" VALUES(5,111,'5:111','اور جب میں نے حواریوں کو اشارہ کیا کہ مجھ پر اور میرے رسول پر ایمان لاوٴ تب اُنہوں نے کہا کہ ہم ایمان لائے اور گواہ رہو کہ ہم مسلم ہیں“ ');
INSERT INTO "ur_maududi" VALUES(5,112,'5:112','( حواریوں کے سلسلہ میں) یہ واقعہ بھی یادرہے کہ جب حواریوں نے کہا کہ اے عیسٰی ؑ ابنِ مریم ؑ! کیا آپ کا رب ہم پر آسمان سے کھانے کا ایک خوان اتار سکتا ہے؟ تو عیسٰی ؑ نے کہا اللہ سے ڈرو اگر تم مومن ہو');
INSERT INTO "ur_maududi" VALUES(5,113,'5:113','اُنہوں نے کہا ہم بس یہ چاہتے ہیں کہ اس خوان سے کھانا کھائیں اور ہمارے دل مطمئن ہوں اور ہمیں معلوم ہو جائے کہ آپ نے جو کچھ ہم سے کہا ہے وہ سچ ہے اور ہم اس پر گواہ ہوں');
INSERT INTO "ur_maududi" VALUES(5,114,'5:114','اس پر عیسیٰ ابن مریم نے دعا کی "خدایا! ہمارے رب! ہم پر آسمان سے ایک خوان نازل کر جو ہمارے لیے اور ہمارے اگلوں پچھلوں کے لیے خوشی کا موقع قرار پائے اور تیری طرف سے ایک نشانی ہو، ہم کو رزق دے اور تو بہترین رازق ہے"');
INSERT INTO "ur_maududi" VALUES(5,115,'5:115','اللہ نے جواب دیا ”میں اُس کو تم پر نازل کرنے والا ہوں، مگر اس کے بعد جو تم میں سے کفر کرے گا اسے میں ایسی سزا دوں گا جو دُنیا میں کسی کو نہ دی ہوگی“');
INSERT INTO "ur_maududi" VALUES(5,116,'5:116','غرض جب (یہ احسانات یاد دلاکر) اللہ فرمائے گا کہ ”اے عیسٰی ؑ بن مریم ؑ ! کیا تُو نے لوگوں سے کہا تھا کہ خدا کے سوا مجھے اور میری ماں کو بھی خدا بنالو؟“ تو وہ جواب میں عرض کرے گا ”سبحان اللہ! میرا یہ کام نہ تھا کہ وہ بات کہتا جس کے کہنے کا مجھے حق نہ تھا، اگر میں نے ایسی بات کہی ہوتی تو آپ کو ضرور علم ہوتا، آپ جانتے ہیں جو کچھ میرے دل میں ہے اور میں نہیں جانتا جو کچھ آپ کے دل میں ہے، آپ تو ساری پوشیدہ حقیقتوں کے عالم ہیں');
INSERT INTO "ur_maududi" VALUES(5,117,'5:117','میں نے اُن سے اُس کے سوا کچھ نہیں کہا جس کا آپ نے حکم دیا تھا، یہ کہ اللہ کی بندگی کرو جو میرا رب بھی ہے اور تمہارا رب بھی میں اُسی وقت تک ان کا نگراں تھا جب تک کہ میں ان کے درمیان تھا جب آپ نے مجھے واپس بلا لیا تو آپ ان پر نگراں تھے اور آپ تو ساری ہی چیزوں پر نگراں ہیں');
INSERT INTO "ur_maududi" VALUES(5,118,'5:118','اب اگر آپ انہیں سزا دیں تو وہ آپ کے بندے ہیں اور اگر معاف کر دیں تو آپ غالب اور دانا ہیں"');
INSERT INTO "ur_maududi" VALUES(5,119,'5:119','تب اللہ فرمائے گا "یہ وہ دن ہے جس میں سچوں کو اُن کی سچائی نفع دیتی ہے، ان کے لیے ایسے باغ ہیں جن کے نیچے نہریں بہہ رہی ہیں، یہاں وہ ہمیشہ رہیں گے، اللہ ان سے راضی ہوا اور وہ اللہ سے، یہی بڑی کامیابی ہے"');
INSERT INTO "ur_maududi" VALUES(5,120,'5:120','زمین اور آسمانوں اور تمام موجودات کی بادشاہی اللہ ہی کے لیے ہے اور وہ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(6,1,'6:1','تعریف اللہ کے لیے ہے جس نے زمین اور آسمان بنائے، روشنی اور تاریکیاں پیدا کیں۔ پھر بھی وہ لوگ جنہوں نے دعوتِ حق کو ماننے سے انکار کردیا ہے دُوسروں کو اپنے ربّ کا ہمسر ٹھیرا رہے ہیں ');
INSERT INTO "ur_maududi" VALUES(6,2,'6:2','وہی ہے جس نے تم کو مٹی سے پیدا کیا، پھر تمہارے لیے زندگی کی ایک مدّت مقرر کردی، اور ایک دُوسری مدّت اور بھی جو اس کے ہاں طے شدہ ہے۔ مگر تم لوگ ہو کہ شک میں پڑے ہوئے ہو');
INSERT INTO "ur_maududi" VALUES(6,3,'6:3','وہی ایک خدا آسمانوں میں بھی ہے اور زمین میں بھی، تمہارے کھلے اور چھپے سب حال جانتا ہے اور جو برائی یا بھلائی تم کماتے ہو اس سے خوب واقف ہے');
INSERT INTO "ur_maududi" VALUES(6,4,'6:4','لوگوں کا حال یہ ہے کہ ان کے رب کی نشانیوں میں سے کوئی نشانی ایسی نہیں جو ان کے سامنے آئی ہو اور انہوں نے اس سے منہ نہ موڑ لیا ہو');
INSERT INTO "ur_maududi" VALUES(6,5,'6:5','چنانچہ اب جو حق ان کے پاس آیا تو اسے بھی انہوں نے جھٹلا دیا۔ اچھا، جس چیز کا وہ اب تک مذاق اُڑاتے رہے ہیں عنقریب اس کے متعلق کچھ خبریں انہیں پہنچیں گی۔ ');
INSERT INTO "ur_maududi" VALUES(6,6,'6:6','کیا انہوں نے دیکھا نہیں کہ ان سے پہلے کتنی ایسی قوموں کو ہم ہلاک کر چکے ہیں جن کا اپنے اپنے زمانہ میں دور دورہ رہا ہے؟ اُن کو ہم نے زمین میں وہ اقتدار بخشا تھا جو تمہیں نہیں بخشا ہے، ان پر ہم نے آسمان سے خوب بارشیں برسائیں اور ان کے نیچے نہریں بہا دیں، (مگر جب انہوں نے کفران نعمت کیا تو) آخر کار ہم نے ان کے گناہوں کی پاداش میں انہیں تباہ کر دیا اور ان کی جگہ دوسرے دور کی قوموں کو اٹھایا');
INSERT INTO "ur_maududi" VALUES(6,7,'6:7','اے پیغمبرؐ! اگر ہم تمہارے اوپر کوئی کاغذ میں لکھی لکھائی کتاب بھی اتار دیتے اور لوگ اسے اپنے ہاتھوں سے چھو کر بھی دیکھ لیتے تب بھی جنہوں نے حق کا انکار کیا ہے وہ یہی کہتے کہ یہ تو صریح جادو ہے');
INSERT INTO "ur_maududi" VALUES(6,8,'6:8','کہتے ہیں کہ اس نبی پر کوئی فرشتہ کیوں نہیں اُتارا گیا۔ اگر کہیں ہم نے فرشتہ اُتاردیا ہوتا تو اب تک کبھی کا فیصلہ ہو چکا ہوتا، پھر انہیں کوئی مُہلت نہ دی جاتی۔ ');
INSERT INTO "ur_maududi" VALUES(6,9,'6:9','اور اگر ہم فرشتے کو اُتارتے تب بھی اسے انسانی شکل ہی میں اُتارتے اور اس طرح انہیں اُسی شبہ میں مُبتلا کردیتے جس میں اب یہ مبتلا ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,10,'6:10','اے محمدؐ! تم سے پہلے بھی بہت سے رسولوں کا مذاق اڑایا جا چکا ہے، مگر ان مذاق اڑانے والوں پر آخر کار وہی حقیقت مسلط ہو کر رہی جس کا وہ مذاق اڑاتے تھے');
INSERT INTO "ur_maududi" VALUES(6,11,'6:11','اِن سے کہو، ذرا زمین میں چل پھر کر دیکھو جھٹلانے والوں کا کیا انجام ہوا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(6,12,'6:12','ان سے پوچھو، آسمانوں اور زمین میں جو کچھ ہےوہ کس کا ہے؟۔۔۔۔کہو سب کچھ اللہ ہی کا ہے، اس نے رحم و کرم کا شیوہ اپنے اوپر لازم کرلیا ہے (اسی لیے وہ نافر مانیوں اور سرکشیوں پر تمہیں جلدی سے نہیں پکڑ لیتا) قیامت کے روز وہ تم سب کو ضرور جمع کرے گا، یہ بالکل ایک غیر مشتبہ حقیقت ہے، مگر جن لوگوں نے اپنے آپ کو خود تباہی کے خطرے میں مُبتلا کرلیا ہے وہ اسے نہیں مانتے');
INSERT INTO "ur_maududi" VALUES(6,13,'6:13','رات کے اندھیرے اور دن کے اجالے میں جو کچھ ٹھیرا ہوا ہے، سب اللہ کا ہے اور وہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "ur_maududi" VALUES(6,14,'6:14','کہو، اللہ کو چھوڑ کر کیا میں کسی اور کو اپنا سرپرست بنالوں؟ اُس خدا کو چھوڑ کر جو زمین و آسمان کا خالق ہے اور جو روزی دیتا ہے روزی لیتا نہیں ہے؟ کہو مجھے تو یہی حکم دی گیا ہے کہ سب سے پہلے میں اُس کے آگے سرِ تسلیم خم کروں (اور تائید کی گئی ہے کہ کوئی شرک کرتا ہے تو کرے) تُو بہرحال مشرکوں میں شامل نہ ہو');
INSERT INTO "ur_maududi" VALUES(6,15,'6:15','کہو، اگر میں اپنے رب کی نافرمانی کروں تو ڈرتا ہوں کہ ایک بڑے (خوفناک) دن مجھے سزا بھگتنی پڑے گی');
INSERT INTO "ur_maududi" VALUES(6,16,'6:16','اُس دن جو سزا سے بچ گیا اس پر اللہ نے بڑا ہی رحم کیا اور یہی نمایاں کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(6,17,'6:17','اگر اللہ تمہیں کسی قسم کا نقصان پہنچائے تو اس کے سوا کوئی نہیں جو تمہیں اس نقصان سے بچا سکے، اور اگر وہ تمہیں کسی بھلائی سے بہرہ مند کرے تو وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_maududi" VALUES(6,18,'6:18','وہ اپنے بندوں پر کامل اختیارات رکھتا ہے اور دانا اور باخبر ہے');
INSERT INTO "ur_maududi" VALUES(6,19,'6:19','ان سے پوچھو، کس کی گواہی سب سے بڑھ کر ہے؟۔۔۔۔ کہو، میرے اور تمہارے درمیان اللہ گواہ ہے ، اور یہ قرآن میری طرف بذریعہ وحی بھیجا گیا ہے تاکہ تمہیں اور جس جس کو یہ پہنچے، سب کو متنبّہ کردُوں۔ کیا واقعی تم لوگ یہ شہادت دے سکتے ہو کہ اللہ کے ساتھ دُوسرے خدا بھی ہیں؟ کہو، میں تو اس کی شہادت ہرگز نہیں دے سکتا۔ کہو، خدا تو وہی ایک ہے اور میں اُس شرک سے قطعی بیزار ہوں جس میں تم مبتلا ہو');
INSERT INTO "ur_maududi" VALUES(6,20,'6:20','جن لوگوں کو ہم نے کتاب دی ہے وہ اس بات کو اس طرح غیر مشتبہ طور پر پہچانتے ہیں جیسے ان کو اپنے بیٹوں کے پہچاننے میں کوئی اشتباہ پیش نہیں آتا۔ مگر جنہوں نے اپنے آپ کو خود خسارے میں ڈال دیا ہے وہ اِسے نہیں مانتے');
INSERT INTO "ur_maududi" VALUES(6,21,'6:21','اور اُس شخص سے بڑھ کر ظالم کون ہوگا جو اللہ پر جھوٹا بہتان لگائے، یا اللہ کی نشانیوں کو جھٹلائے؟ یقیناً ایسے ظالم کبھی فلاح نہیں پاسکتے');
INSERT INTO "ur_maududi" VALUES(6,22,'6:22','جس روز ہم ان سب کو اکٹھا کریں گے اور مشرکوں سے پوچھیں گے کہ اب وہ تمہارے ٹھیرائے ہوئے شریک کہاں ہیں جن کو تم اپنا خدا سمجھتے تھے');
INSERT INTO "ur_maududi" VALUES(6,23,'6:23','تو وہ اِس کے سوا کوئی فتنہ نہ اٹھا سکیں گے کہ (یہ جھوٹا بیان دیں کہ) اے ہمارے آقا! تیری قسم ہم ہرگز مشر ک نہ تھے');
INSERT INTO "ur_maududi" VALUES(6,24,'6:24','دیکھو، اُس وقت یہ کس طرح اپنے اوپر آپ جھوٹ گھڑیں گے، اور وہاں اُن کے سارے بناوٹی معبود گم ہو جائیں گے');
INSERT INTO "ur_maududi" VALUES(6,25,'6:25','ان میں سے بعض لوگ ایسے ہیں جو کان لگا کر تمہاری بات سُنتے ہیں مگر حال یہ ہے کہ ہم نے اُن کے دلوں پر پردے ڈال رکھے ہیں جن کی وجہ سے وہ اس کو کچھ نہیں سمجھتے اور ان کے کانوں میں گرانی ڈال دی ہے (کہ سب کچھ سننے پر بھی کچھ نہیں سنتے) ۔ وہ خواہ کوئی نشانی دیکھ لیں، اس پر ایمان لاکر نہ دیں گے۔ حد یہ ہے کہ جب وہ تمہارے پاس آکر تم سے جھگڑتے ہیں تو ان میں سے جن لوگوں نے انکار کا فیصلہ کرلیا ہے وہ (ساری باتیں سننے کے بعد) یہی کہتے ہیں کہ یہ ایک داستانِ پارینہ کے سوا کچھ نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,26,'6:26','وہ اس امر حق کو قبول کرنے سے لوگوں کو روکتے ہیں اور خود بھی اس سے دور بھاگتے ہیں (وہ سمجھتے ہیں کہ اس حرکت سے وہ تمہارا کچھ بگاڑ رہے ہیں) حالانکہ دراصل وہ خو د اپنی ہی تباہی کا سامان کر رہے ہیں مگر انہیں اس کا شعور نہیں ہے');
INSERT INTO "ur_maududi" VALUES(6,27,'6:27','کاش تم اس وقت ان کی حالت دیکھ سکتے جب وہ دوزخ کے کنارے کھڑے کیے جائیں گے اس وقت وہ کہیں گے کہ کاش کوئی صورت ایسی ہو کہ ہم دنیا میں پھر واپس بھیجے جائیں اور اپنے رب کی نشانیوں کو نہ جھٹلائیں اور ایمان لانے والوں میں شامل ہوں');
INSERT INTO "ur_maududi" VALUES(6,28,'6:28','در حقیقت یہ بات وہ محض اس وجہ سے کہیں گے کہ جس حقیقت پر انہوں نے پردہ ڈال رکھا تھا وہ اس وقت بے نقاب ہو کر ان کے سامنے آچکی ہوگی، ورنہ اگر انہیں سابق زندگی کی طرف واپس بھیجا جائے تو پھر وہی سب کچھ کریں جس سے انہیں منع کیا گیا ہے، وہ تو ہیں ہی جھوٹے (اس لیے اپنی اس خواہش کے اظہار میں بھی جھوٹ ہی سے کام لیں گے)');
INSERT INTO "ur_maududi" VALUES(6,29,'6:29','آج یہ لوگ کہتے ہیں کہ زندگی جو کچھ بھی ہے بس یہی دنیا کی زندگی ہے اور ہم مرنے کے بعد ہرگز دوبارہ نہ اٹھائے جائیں گے');
INSERT INTO "ur_maududi" VALUES(6,30,'6:30','کاش وہ منظر تم دیکھ سکو جب یہ اپنے رب کے سامنے کھڑے کیے جائیں گے اس وقت ان کا رب ان سے پوچھے گا "کیا یہ حقیقت نہیں ہے؟" یہ کہیں گے "ہاں اے ہمارے رب! یہ حقیقت ہی ہے" وہ فرمائے گا "اچھا! تو اب اپنے انکار حقیقت کی پاداش میں عذاب کا مزا چکھو"');
INSERT INTO "ur_maududi" VALUES(6,31,'6:31','نقصان میں پڑ گئے وہ لوگ جنہوں نے اللہ سے اپنی ملاقات کی اطلاع کو جھوٹ قرار دیا جب اچانک وہ گھڑی آ جائے گی تو یہی لوگ کہیں گے "افسوس! ہم سے اس معاملہ میں کیسی تقصیر ہوئی" اور اِن کا حال یہ ہوگا کہ اپنی پیٹھوں پر اپنے گناہوں کا بوجھ لادے ہوئے ہوں گے دیکھو! کیسا برا بوجھ ہے جو یہ اٹھا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(6,32,'6:32','دُنیا کی زندگی تو ایک کھیل اور ایک تماشا ہے، حقیقت میں آخرت ہی کا مقام اُن لوگوں کے لیے بہتر ہے جو زیاں کاری سے بچنا چاہتے ہیں، پھر کیا تم لوگ عقل سے کام نہ لوگے؟');
INSERT INTO "ur_maududi" VALUES(6,33,'6:33','اے محمدؐ ! ہمیں معلوم ہے کہ جو باتیں یہ لوگ بناتے ہیں ان سے تمہیں رنج ہوتا ہے، لیکن یہ لوگ تمہیں نہیں جھٹلاتے بلکہ یہ ظالم دراصل اللہ کی آیات کا انکار کر رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,34,'6:34','تم سے پہلے بھی بہت سے رسُول جھٹلائے جا چکے ہیں، مگر اس تکذیب پر اور اُن اذیّتوں پر جو انہیں پہنچائی گئیں، انہوں نے صبر کیا، یہاں تک کہ انہیں ہماری مد پہنچ گئی۔ اللہ کی باتوں کو بدلنے کی طاقت کسی میں نہیں ہے، اور پچھلے رسُولوں کے ساتھ جو کچھ پیش آیا اس کی خبریں تمہیں پہنچ ہی چکی ہیں');
INSERT INTO "ur_maududi" VALUES(6,35,'6:35','تاہم اگر ان لوگوں کی بے رخی تم سے برداشت نہیں ہوتی تو اگر تم میں کچھ زور ہے تو زمین میں کوئی سرنگ ڈھونڈو یا آسمان میں سیڑھی لگاوٴ اور ان کے پاس کوئی نشانی لانے کی کوشش کرو۔ اگر اللہ چاہتا تو ان سب کو ہدایت پر جمع کرسکتا تھا، لہٰذا نادان مت بنو۔ ');
INSERT INTO "ur_maududi" VALUES(6,36,'6:36','دعوتِ حق پر لبیک وہی لوگ کہتے ہیں جو سُننے والے ہیں، رہے مُردے، تو انہیں تو اللہ بس قبروں ہی سے اُٹھائے گا اور پھر وہ (اس کی عدالت میں پیش ہونے کے لیے) واپس لائے جائیں گے');
INSERT INTO "ur_maududi" VALUES(6,37,'6:37','ہ لوگ کہتے ہیں کہ اِس نبی پر اس کے ربّ کی طرف سے کوئی نشانی کیوں نہیں اُتری؟ کہو، اللہ نشانی اتارنے کی پُوری قدرت رکھتا ہے، مگر ان میں سے اکثر لوگ نادانی میں مُبتلا ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,38,'6:38','زمین میں چلنے والے کسی جانور اور ہوا میں پروں سے اڑنے والے کسی پرندے کو دیکھ لو، یہ سب تمہاری ہی طرح کی انواع ہیں، ہم نے ان کی تقدیر کے نوشتے میں کوئی کسر نہیں چھوڑی ہے، پھر یہ سب اپنے رب کی طرف سمیٹے جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(6,39,'6:39','مگر جو لوگ ہماری نشانیوں کو جھٹلاتے ہیں وہ بہرے اور گونگے ہیں، تاریکیوں میں پڑے ہوئےہیں۔ اللہ جسے چاہتا ہے بھٹکا دیتا ہے اور جسے چاہتا ہے سیدھے رستے پر لگا دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(6,40,'6:40','ان سے کہو، ذرا غور کر کے بتاؤ، اگر کبھی تم پر اللہ کی طرف سے کوئی بڑی مصیبت آ جاتی ہے یا آخری گھڑی آ پہنچتی ہے تو کیا اس وقت تم اللہ کے سوا کسی اور کو پکارتے ہو؟ بولو اگر تم سچے ہو');
INSERT INTO "ur_maududi" VALUES(6,41,'6:41','اس وقت تم اللہ ہی کو پُکارتے ہو، پھر اگر وہ چاہتا ہے تو اس مصیبت کو تم پر سے ٹال دیتا ہے۔ ایسے موقعوں پر تم اپنے ٹھیرائے ہوئے شریکوں کو بُھول جاتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(6,42,'6:42','تم سے پہلے بہت سی قوموں کی طرف ہم نے رسول بھیجے اور اُن قوموں کو مصائب و آلام میں مبتلا کیا تاکہ وہ عاجزی کے ساتھ ہمارے سامنے جھک جائیں');
INSERT INTO "ur_maududi" VALUES(6,43,'6:43','پس جب ہماری طرف سے ان پر سختی آئی تو کیوں نہ انہوں نے عاجزی اختیار کی؟ مگر ان کے دل تو اور سخت ہوگئے اور شیطان نے ان کو اطمینان دلایا کہ جو کچھ تم کر رہے ہو خوب کر رہے ہو');
INSERT INTO "ur_maududi" VALUES(6,44,'6:44','پھر جب انہوں نے اس نصیحت کو، جو انہیں کی گئی تھی، بھلا دیا تو ہم نے ہر طرح کی خوشحالیوں کے دروازے ان کے لیے کھول دیے، یہاں تک کہ جب وہ اُن بخششوں میں جو انہیں عطا کی گئی تھیں خوب مگن ہوگئے تو اچانک ہم نے انہیں پکڑ لیا اور اب حال یہ تھا کہ وہ ہر خیر سے مایوس تھے');
INSERT INTO "ur_maududi" VALUES(6,45,'6:45','اس طرح ان لوگوں کی جڑ کاٹ کر رکھ دی گئی جنہوں نے ظلم کیا تھا اور تعریف ہے اللہ رب العالمین کے لیے (کہ اس نے ان کی جڑ کاٹ دی)');
INSERT INTO "ur_maududi" VALUES(6,46,'6:46','اے محمد ؐ ! ان سے کہو، کبھی تم نے یہ بھی سوچا کہ اگر اللہ تمہاری بینائی اور سماعت تم سے چھین لے اور تمہارے دلوں پر مُہر کردے تو اللہ کے سوا اور کونسا خدا ہے جو یہ قوتیں تمہیں واپس دلاسکتا ہو؟ دیکھو، کس طرح ہم بار بار اپنی نشانیاں ان کے سامنے پیش کرتے ہیں اور پھر یہ کس طرح ان سے نظر چُرا جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(6,47,'6:47','کہو، کبھی تم نے سوچا کہ اگر اللہ کی طرف سے اچانک یا علانیہ تم پر عذاب آ جائے تو کیا ظالم لوگوں کے سوا کوئی اور ہلاک ہوگا؟');
INSERT INTO "ur_maududi" VALUES(6,48,'6:48','ہم جو رسول بھیجتے ہیں اسی لیے تو بھیجتے ہیں کہ وہ نیک کردار لوگوں کے لیے خوش خبری دینے والے اور بد کرداروں کے لیے ڈرانے والے ہوں پھر جو لوگ ان کی بات مان لیں اور اپنے طرز عمل کی اصلاح کر لیں ان کے لیے کسی خوف اور رنج کا موقع نہیں ہے');
INSERT INTO "ur_maududi" VALUES(6,49,'6:49','اور جو ہماری آیات کو جھٹلائیں وہ اپنی نافرمانیوں کی پاداش میں سزا بھگت کر رہیں گے');
INSERT INTO "ur_maududi" VALUES(6,50,'6:50','اے محمد ؐ ! ان سے کہو، ”میں تم سے یہ نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں۔ نہ میں غیب کا علم رکھتا ہوں، اور نہ یہ کہتا ہوں کہ میں فرشتہ ہوں۔ میں تو صرف اُس وحی کی پیروی کرتا ہو جو مجھ پر نازل کی جاتی ہے“۔ پھر ان سے پوچھو کیا اندھا اور آنکھوں والا دونوں برابر ہو سکتے ہیں؟ کیا تم غور نہیں کرتے؟');
INSERT INTO "ur_maududi" VALUES(6,51,'6:51','ا ے محمد ؐ ! تم اِس (علم وحی) کے ذریعہ سے اُن لوگوں کو نصیحت کرو جو اس بات کا خوف رکھتے ہیں کہ اپنے ربّ کے سامنے کبھی اس حال میں پیش کیے جائیں گے کہ اُس کے سوا وہاں کوئی (ایسا ذی اقتدار) نہ ہوگا جو ان کا حامی و مدد گار ہو، یا ان کی سفارش کرے، شاید کہ (اس نصیحت سے متنبّہ ہو کر) وہ خداترسی کی روش اختیار کرلیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,52,'6:52','اور جو لوگ اپنے ربّ کو رات دن پکارتے رہتے ہیں اور اس کی خوشنودی کی طلب میں لگے ہوئے ہیں انہیں اپنے سے دُور نہ پھینکو۔ اُن کے حساب میں سے کسی چیز کا بار تم پر نہیں ہے اور تمہارے حساب میں سے کسی چیز کا با اُن پر نہیں۔ اس پر بھی اگر تم انہیں دُور پھینکو گے تو ظالموں میں شمار ہوگے۔ ');
INSERT INTO "ur_maududi" VALUES(6,53,'6:53','دراصل ہم نے اس طرح ان لوگوں میں سے بعض کو بعض کے ذریعہ سے آزمائش میں ڈالا ہے تاکہ وہ انہیں دیکھ کر کہیں ”کیا یہ ہیں وہ لوگ جن پر ہمارے درمیان اللہ کا فضل و کرم ہئوا ہے“؟۔۔۔۔ ہاں! کیا خدا اپنے شکر گزار بندوں کو اِن سے زیادہ نہیں جانتا ہے ؟');
INSERT INTO "ur_maududi" VALUES(6,54,'6:54','جب تمہارے پاس وہ لوگ آئیں جو ہماری آیات پر ایمان لاتے ہیں تو ان سے کہو ”تم پر سلامتی ہے۔ تمہارے رب نے رحم و کرم کا شیوہ اپنے اوپر لازم کرلیا ہے۔ یہ اس کا رحم و کرم ہی ہے کہ اگر تم میں سے کوئی نادانی کے ساتھ کسی بُرائی کا ارتکاب کر بیٹھا ہر پھر اس کے بعد توبہ کرے اور اصلاح کرلے تو وہ اُسے معاف کردیتا ہے اور نرمی سے کام لیتا ہے“ ');
INSERT INTO "ur_maududi" VALUES(6,55,'6:55','اور اس طرح ہم اپنی نشانیاں کھول کھول کر پیش کرتے ہیں تاکہ مجرموں کی راہ بالکل نمایاں ہوجائے۔ ');
INSERT INTO "ur_maududi" VALUES(6,56,'6:56','اے محمدؐ! اِن سے کہو کہ تم لوگ اللہ کے سوا جن دوسروں کو پکارتے ہو اُن کی بندگی کرنے سے مجھے منع کیا گیا ہے کہو، میں تمہاری خواہشات کی پیروی نہیں کروں گا، اگر میں نے ایسا کیا تو گمراہ ہو گیا، راہ راست پانے والوں میں سے نہ رہا');
INSERT INTO "ur_maududi" VALUES(6,57,'6:57','کہو، میں اپنے رب کی طرف سے ایک دلیل روشن پر قائم ہوں اور تم نے اسے جھٹلا دیا ہے، اب میرے اختیار میں وہ چیز ہے نہیں جس کے لیے تم جلدی مچارہے ہو، فیصلہ کا سارا اختیار اللہ کو ہے، وہی امرِ حق بیان کرتا ہے اور وہی بہترین فیصلہ کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(6,58,'6:58','کہو، اگر کہیں وہ چیز میرے اختیار میں ہوتی جس کی تم جلدی مچا رہے ہو تو میرے اور تمہارے درمیان کبھی کا فیصلہ ہو چکا ہوتا مگر اللہ زیادہ بہتر جانتا ہے کہ ظالموں کے ساتھ کیا معاملہ کیا جانا چاہیے');
INSERT INTO "ur_maududi" VALUES(6,59,'6:59','اُسی کے پاس غیب کی کنجیاں ہیں جنہیں اس کے سوا کوئی نہیں جانتا بحر و بر میں جو کچھ ہے سب سے وہ واقف ہے درخت سے گرنے والا کوئی پتہ ایسا نہیں جس کا اسے علم نہ ہو زمین کے تاریک پردوں میں کوئی دانہ ایسا نہیں جس سے وہ باخبر نہ ہو خشک و ترسب کچھ ایک کھلی کتاب میں لکھا ہوا ہے');
INSERT INTO "ur_maududi" VALUES(6,60,'6:60','وہی ہے جو رات کو تمہاری روحیں قبض کرتا ہے اور دن کو جو کچھ تم کرتے ہو اسے جانتا ہے، پھر دوسرے روز وہ تمہیں اِسی کاروبار کے عالم میں واپس بھیج دیتا ہے تاکہ زندگی کی مقرر مدت پوری ہو آخر کار اسی کی طرف تمہاری واپسی ہے، پھر و ہ تمہیں بتا دے گا کہ تم کیا کرتے رہے ہو');
INSERT INTO "ur_maududi" VALUES(6,61,'6:61','اپنے بندوں پر وہ پُوری قدرت رکھتا ہے اور تم پر نگرانی کرنے والے مقرر کرکے بھیجتا ہے، یہاں تک کہ جب تم میں سے کسی کی مَوت کا وقت آ جاتا ہے تو اس کے بھیجے ہوئے فرشتے اس کی جان نکال لیتے ہیں اور اپنا فرض انجام دینے میں ذرا کوتاہی نہیں کرتے');
INSERT INTO "ur_maududi" VALUES(6,62,'6:62','پھر سب کے سب اللہ، اپنے حقیقی آقا کی طرف واپس لائے جاتے ہیں خبردار ہو جاؤ، فیصلہ کے سارے اختیارات اسی کو حاصل ہیں اور وہ حساب لینے میں بہت تیز ہے');
INSERT INTO "ur_maududi" VALUES(6,63,'6:63','اے محمدؐ! ا ن سے پوچھو، صحرا اور سمندر کی تاریکیوں میں کون تمہیں خطرات سے بچاتا ہے؟ کو ن ہے جس سے تم (مصیبت کے وقت) گڑ گڑا، گڑگڑا کر اور چپکے چپکے دعائیں مانگتے ہو؟ کس سے کہتے ہو کہ اگر اس بلا سے تو نے ہم کو بچا لیا تو ہم ضرور شکر گزار ہوں گے؟');
INSERT INTO "ur_maududi" VALUES(6,64,'6:64','کہو، اللہ تمہیں اُس سے اور ہر تکلیف سے نجات دیتا ہے پھر تم دُوسروں کو اُس کا شریک ٹھیراتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(6,65,'6:65','کہو، وہ اِس پر قادر ہے کہ تم پر کوئی عذاب اُوپر سے نازل کردے، یا تمہارے قدموں کے نیچے سے برپا کردے، یا تمہیں گروہوں میں تقسیم کرکے ایک گروہ کو دُوسرے گروہ کی طاقت کا مزہ چکھوادے۔ دیکھو، ہم کس طرح بار بار مختلف طریقوں سے اپنی نشانیاں ان کے سامنے پیش کر رہے ہیں شاید کہ یہ حقیقت کو سمجھ لیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,66,'6:66','تمہاری قوم اُس کا انکار کر رہی ہے حالانکہ وہ حقیقت ہے۔ اِن سے کہہ دو کہ میں تم پر حوالہ دار نہیں بنایا گیا ہوں، ');
INSERT INTO "ur_maududi" VALUES(6,67,'6:67','ہر خبر کے ظہور میں آنے کا ایک وقت مقرر ہے، عنقریب تم کو خود انجام معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(6,68,'6:68','اوراے محمد ؐ ، جب تم دیکھو کہ لوگ ہماری آیات پر نکتہ چینیاں کر رہے ہیں تو ان کے پاس سے ہٹ جاوٴ یہاں تک کہ وہ اس گفتگو کو چھوڑ کر دُوسری باتوں میں لگ جائیں۔ اور اگر کبھی شیطان تمہیں بھُلاوے میں ڈال دے تو جس وقت تمہیں اس غلطی کا احساس ہوجائے اس کے بعد پھر ایسے ظالم لوگوں کے پاس نہ بیٹھو');
INSERT INTO "ur_maududi" VALUES(6,69,'6:69','اُن کے حساب میں سے کسی چیز کی ذمّہ داری پرہیز گار لوگوں پر نہیں ہے، البتّہ نصیحت کرنا اُن کا فرض ہے شاید کہ وہ غلط روی سے بچ جائیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,70,'6:70','چھوڑو اُن لوگوں کو جنہوں نے اپنے دین کو کھیل اور تماشا بنا رکھا ہے اور جنہیں دنیا کی زندگی فریب میں مبتلا کیے ہوئے ہے ہاں مگر یہ قرآن سنا کر نصیحت اور تنبیہ کرتے رہو کہ کہیں کوئی شخص اپنے کیے کرتوتوں کے وبال میں گرفتار نہ ہو جائے، اور گرفتار بھی اِس حال میں ہو کہ اللہ سے بچانے والا کوئی حامی و مدد گار اور کوئی سفارشی اس کے لیے نہ ہو، اور گر وہ ہر ممکن چیز فدیہ میں دے کر چھوٹنا چاہے تو وہ بھی اس سے قبول نہ کی جائے، کیونکہ ایسے لوگ تو خود اپنی کمائی کے نتیجہ میں پکڑے جائیں گے، ان کو تو اپنے انکار حق کے معاوضہ میں کھولتا ہوا پانی پینے کو اور درد ناک عذاب بھگتنے کو ملے گا');
INSERT INTO "ur_maududi" VALUES(6,71,'6:71','اے محمدؐ! ان سے پو چھو کیا ہم اللہ کو چھوڑ کر اُن کو پکاریں جو نہ ہمیں نفع دے سکتے ہیں نہ نقصان؟ اور جبکہ اللہ ہمیں سیدھا راستہ دکھا چکا ہے تو کیا اب ہم الٹے پاؤں پھر جائیں؟ کیا ہم اپنا حال اُس شخص کا سا کر لیں جسے شیطانوں نے صحرا میں بھٹکا دیا ہو اور وہ حیران و سرگرداں پھر رہا ہو درآں حالیکہ اس کے ساتھی اسے پکار رہے ہوں کہ اِدھر آ یہ سیدھی راہ موجود ہے؟کہو، حقیقت میں صحیح رہنمائی تو صرف اللہ ہی کی رہنمائی ہے اور اس کی طرف سے ہمیں یہ حکم ملا ہے کہ مالک کائنات کے آگے سر اطاعت خم کر دو');
INSERT INTO "ur_maududi" VALUES(6,72,'6:72','نماز قائم کرو اور اس کی نافرمانی سے بچو، اسی کی طرف تم سمیٹے جاؤ گے');
INSERT INTO "ur_maududi" VALUES(6,73,'6:73','وہی ہے جس نے آسمان و زمین کو برحق پیدا کیا ہے۔ اور جس دن وہ کہے گا کہ حشر ہوجائے اسی دن وہ ہوجائے گا۔ اس کا ارشاد عین حق ہے۔ اور جس روز صُور پھونکا جائیگا اس روزبادشاہی اُسی کی ہوگی، وہ غیب اور شہادت ہر چیز کا عالم ہے اور دانا اور باخبر ہے');
INSERT INTO "ur_maududi" VALUES(6,74,'6:74','ابراہیمؑ کاواقعہ یاد کرو جبکہ اُس نے اپنے باپ آزر سے کہا تھا ”کیا تُو بتوں کو خدا بناتا ہے؟ میں تو تجھے اور تیری قوم کو کُھلی گمراہی میں پاتا ہوں“');
INSERT INTO "ur_maududi" VALUES(6,75,'6:75','ابراہیمؑ کو ہم اِسی طرح زمین اور آسمانوں کا نظامِ سلطنت دکھاتے تھے اور اس لیے دکھاتے تھے کہ وہ یقین کرنے والوں میں سے ہوجائے۔ ');
INSERT INTO "ur_maududi" VALUES(6,76,'6:76','چنانچہ جب رات اس پر طاری ہوئی تو اُس نے ایک تار ا دیکھا کہا یہ میرا رب ہے مگر جب وہ ڈوب گیا تو بولا ڈوب جانے والوں کا تو میں گرویدہ نہیں ہوں');
INSERT INTO "ur_maududi" VALUES(6,77,'6:77','پھر جب چاند چمکتا نظر آیا تو کہا یہ ہے میرا رب مگر جب وہ بھی ڈوب گیا تو کہا اگر میرے رب نے میر ی رہنمائی نہ کی ہوتی تو میں بھی گمراہ لوگوں میں شامل ہو گیا ہوتا');
INSERT INTO "ur_maududi" VALUES(6,78,'6:78','پھر جب سورج کو روشن دیکھا تو کہا یہ ہے میرا رب، یہ سب سے بڑا ہے۔ مگر جب وہ بھی ڈوبا تو ابراہیم ؑ پکار اُٹھا ”اے برادران ِقوم ! میں اُن سب سے بیزار ہوں جنہیں تم خدا کا شریک ٹھیراتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(6,79,'6:79','میں نے تویکسو ہو کر اپنا رخ اُس ہستی کی طرف کر لیا جس نے زمین اور آسمانوں کو پیدا کیا ہے اور میں ہرگز شرک کرنے والوں میں سے نہیں ہوں"');
INSERT INTO "ur_maududi" VALUES(6,80,'6:80','اس کی قوم اس سے جھگڑنے لگی تو اس نے قوم سے کہا ”کیا تم لوگ اللہ کے معاملے میں مجھ سے جھگڑتے ہو؟ حالانکہ اس نے مجھے راہِ راست دکھادی ہے۔ اور میں تمہارے ٹھیرائے ہوئے شریکوں سے نہیں ڈرتا، ہاں اگر میرا رب کچھ چاہے تو وہ ضرور ہوسکتا ہے۔ میرے رب کا عِلم ہر چیز پر چھایا ہوٴا ہے، پھر کیا تم ہوش میں نہ آوٴ گے؟ ');
INSERT INTO "ur_maududi" VALUES(6,81,'6:81','اور آخر میں تمہارے ٹھیرائے ہوئے شریکوں سے کیسے ڈروں جبکہ تم اللہ کے ساتھ ان چیزوں کو خدائی میں شریک بناتے ہوئے نہیں ڈرتے جن کے لیے اس نے تم پر کوئی سند نازل نہیں کی ہے؟ ہم دونوں فریقوں میں سے کون زیادہ بے خوفی و اطمینان کا مستحق ہے؟ بتاؤ اگر تم کچھ علم رکھتے ہو');
INSERT INTO "ur_maududi" VALUES(6,82,'6:82','حقیقت میں تو امن انہی کے لیے ہے اور راہِراست پر وہی ہیں جو ایمان لائے اور جنھوں نے اپنے ایمان کو ظلم کے ساتھ آلودہ نہیں کیا“۔ ');
INSERT INTO "ur_maududi" VALUES(6,83,'6:83','یہ تھی ہماری وہ حجت جو ہم نے ابراہیمؑ کو اس کی قوم کے مقابلہ میں عطا کی ہم جسے چاہتے ہیں بلند مرتبے عطا کرتے ہیں حق یہ ہے کہ تمہارا رب نہایت دانا اور علیم ہے');
INSERT INTO "ur_maududi" VALUES(6,84,'6:84','پھر ہم نے ابراہیمؑ کو اسحاقؑ اور یعقوبؑ جیسی اولاد دی اور ہر ایک کو راہ راست دکھائی (وہی راہ راست جو) اس سے پہلے نوحؑ کو دکھائی تھی اور اُسی کی نسل سے ہم نے داؤدؑ، سلیمانؑ، ایوبؑ، یوسفؑ، موسیٰؑ اور ہارونؑ کو (ہدایت بخشی) اِس طرح ہم نیکو کاروں کو ان کی نیکی کا بدلہ دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(6,85,'6:85','(اُسی کی اولاد سے) زکریاؑ، یحییٰؑ، عیسیٰؑ اور الیاسؑ کو (راہ یاب کیا) ہر ایک ان میں سے صالح تھا');
INSERT INTO "ur_maududi" VALUES(6,86,'6:86','(اسی کے خاندان سے) اسماعیلؑ، الیسعؑ، اور یونسؑ اور لوطؑ کو (راستہ دکھایا) اِن میں سے ہر ایک کو ہم نے تمام دنیا والوں پر فضیلت عطا کی');
INSERT INTO "ur_maududi" VALUES(6,87,'6:87','نیز ان کے آبا و اجداد اور ان کی اولاد اور ان کے بھائی بندوں میں سے بہتوں کو ہم نے نوازا، انہیں اپنی خدمت کے لیے چن لیا اور سیدھے راستے کی طرف اُن کی رہنمائی کی');
INSERT INTO "ur_maududi" VALUES(6,88,'6:88','یہ اللہ کی ہدایت ہے جس کے ساتھ وہ اپنے بندوں میں سے جس کی چاہتا ہے رہنمائی کرتا ہے۔ لیکن اگر کہیں ان لوگوں نے شرک کیا ہوتا تو ان کا سب کیا کرایا غارت ہوجاتا۔ ');
INSERT INTO "ur_maududi" VALUES(6,89,'6:89','وہ لوگ تھے جن کو ہم نے کتاب اور حکم اور نبوت عطا کی تھی۔ اب اگر یہ لوگ اس کو ماننے سے انکار کرتے ہیں تو (پروا نہیں) ہم نے کچھ اور لوگوں کو یہ نعمت سونپ دی ہے جو اس سے منکر نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,90,'6:90','اے محمدؐ! وہی لوگ اللہ کی طرف سے ہدایت یافتہ تھے، انہی کے راستہ پر تم چلو، اور کہہ دو کہ میں (اس تبلیغ و ہدایت کے) کام پر تم سے کسی اجر کا طالب نہیں ہوں، یہ تو ایک عام نصیحت ہے تمام دنیا والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(6,91,'6:91','ان لوگوں نے اللہ کا بہت غلط اندازہ لگایا جب کہا کہ اللہ نے کسی بشر پر کچھ نازل نہیں کیا ہے۔ ان سے پوچھو، پھر وہ کتاب جسے موسیٰؑ لایا تھا ، جو تمام انسانوں کے لیے روشنی اور ہدایت تھی، جسے تم پارہ پارہ کرکے رکھتے ہو، کچھ دکھاتے ہو اور بہت کچھ چُھپا جاتے ہو ،اور جس کے ذریعہ سے تم کو وہ علم دیا گیا جو نہ تمہیں حاصل تھا اور نہ تمہارے باپ دادا کو، آخر اُس کا نازل کرنے والا کون تھا؟۔۔۔۔ بس اتنا کہہ دو کہ اللہ، پھر اُنہیں اپنی دلیل بازیوں سے کھیلنے کے لیے چھوڑدو');
INSERT INTO "ur_maududi" VALUES(6,92,'6:92','(اُسی کتاب کی طرح) یہ ایک کتاب ہے جسے ہم نے نازل کیا ہے۔ بڑی خیرو برکت والی ہے۔ اُس چیز کی تصدیق کرتی ہے جو اس سے پہلے آئی تھی۔ اور اس لیے نازل کی گئی ہے کہ اس کے ذریعہ سے تم بستیوں کے اس مرکز (یعنی مکّہ) اور اس کے اطراف میں رہنے والوں کو متنبہ کرو۔ جو لوگ آخرت کو مانتے ہیں وہ اس کتاب پر ایمان لاتے ہیں اور ان کا حال یہ ہے کہ اپنی نمازوں کی پابندی کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,93,'6:93','اور اُس شخص سے بڑا ظالم اور کون ہوگا جو اللہ پر جھوٹا بہتان گھڑے، یا کہے کہ مجھ پر وحی آئی ہے درآں حالیکہ اس پر کوئی وحی نازل نہ کی گئی ہو، یا جو اللہ کی نازل کردہ چیز کے مقابلہ میں کہے کہ میں بھی ایسی چیز نازل کر کے دکھا دوں گا؟ کاش تم ظالموں کو اس حالت میں دیکھ سکو جب کہ وہ سکرات موت میں ڈبکیاں کھا رہے ہوتے ہیں اور فرشتے ہاتھ بڑھا بڑھا کر کہہ رہے ہوتے ہیں کہ "لاؤ، نکالو اپنی جان، آج تمہیں اُن باتوں کی پاداش میں ذلت کا عذاب دیا جائے گا جو تم اللہ پر تہمت رکھ کر ناحق بکا کرتے تھے اور اُس کی آیات کے مقابلہ میں سرکشی دکھاتے تھے"');
INSERT INTO "ur_maududi" VALUES(6,94,'6:94','(اور اللہ فرمائے گا) "لو اب تم ویسے ہی تن تنہا ہمارے سامنے حاضر ہوگئے جیسا ہم نے تمہیں پہلی مرتبہ اکیلا پیدا کیا تھا، جو کچھ ہم نے تمہیں دنیا میں دیا تھا وہ سب تم پیچھے چھوڑ آئے ہو، اور اب ہم تمہارے ساتھ تمہارے اُن سفارشیوں کو بھی نہیں دیکھتے جن کے متعلق تم سمجھتے تھے کہ تمہارے کام بنانے میں ان کا بھی کچھ حصہ ہے، تمہارے آپس کے سب رابطے ٹوٹ گئے اور وہ سب تم سے گم ہوگئے جن کا تم زعم رکھتے تھے"');
INSERT INTO "ur_maududi" VALUES(6,95,'6:95','دانے اورگھٹلی کو پھاڑنے والا اللہ ہے۔ وہی زندہ کو مُردہ سے نکالتا ہے اور وہی مُردہ کو زندہ سے خارج کرتا ہے۔ یہ سارے کام تو کرنے والا اللہ ہے، پھر تم کدھر بہکے چلے جا رہے ہو؟');
INSERT INTO "ur_maududi" VALUES(6,96,'6:96','پردہ شب کو چاک کر کے وہی صبح نکالتا ہے اُسی نے رات کو سکون کا وقت بنایا ہے اُسی نے چاند اور سورج کے طلوع و غروب کا حساب مقرر کیا ہے یہ سب اُسی زبردست قدرت اور علم رکھنے والے کے ٹھیرائے ہوئے اندازے ہیں');
INSERT INTO "ur_maududi" VALUES(6,97,'6:97','اور وہی ہے جس نے تمہارے لیے تاروں کو صحرا اور سمندر کی تاریکیوں میں راستہ معلوم کرنے کا ذریعہ بنایا۔ دیکھو ہم نے نشانیاں کھول کر بیان کر دی ہیں اُن لوگوں کے لیے جو علم رکھتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,98,'6:98','اور وہی ہے جس نے ایک مُتنفّس سے تم کو پیدا کیا پھر ہر ایک کے لیے ایک جائے قرار ہے اور ایک اس کے سونپے جانے کی جگہ۔ یہ نشانیاں ہم نے واضح کردی ہیں اُن لوگوں کے لیے جو سمجھ بوجھ رکھتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,99,'6:99','اور وہی ہے جس نے آسمان سے پانی برسایا، پھر اس کے ذریعہ سے ہر قسم کی نباتات اگائی، پھر اس سے ہرے ہرے کھیت اور درخت پیدا کیے، پھر ان سے تہ بہ تہ چڑھے ہوئے دانے نکالے اور کھجور کے شگوفوں سے پھلوں کے گچھے کے گچھے پیدا کیے جو بوجھ کے مارے جھکے پڑتے ہیں، اور انگور، زیتون اور انار کے باغ لگائے جن کے پھل ایک دوسرے سے ملتے جلتے بھی ہیں اور پھر اُن کے پکنے کی کیفیت ذرا غور کی نظر سے دیکھو، اِن چیزوں میں نشانیاں ہیں اُن لوگوں کے لیے جو ایمان لاتے ہیں');
INSERT INTO "ur_maududi" VALUES(6,100,'6:100','اِس پر بھی لوگوں نے جنوں کو اللہ کا شریک ٹھیرا دیا، حالانکہ وہ اُن کا خالق ہے، اور بے جانے بوجھے اس کے لیے بیٹے اور بیٹیاں تصنیف کردیں، حالانکہ وہ پاک اور بالاتر ہے اُن باتوں سے جو یہ لوگ کہتے ہیں۔');
INSERT INTO "ur_maududi" VALUES(6,101,'6:101','وہ تو آسمانوں اور زمین کا موجد ہے اس کا کوئی بیٹا کیسے ہوسکتا ہے جبکہ کوئی اس کی شریک زندگی ہی نہیں ہے اس نے ہر چیز کو پیدا کیا ہے اور وہ ہر چیز کا علم رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(6,102,'6:102','یہ ہے اللہ تمہارا رب، کوئی خدا اس کے سوا نہیں ہے، ہر چیز کا خالق، لہٰذا تم اسی کی بندگی کرو اور وہ ہر چیز کا کفیل ہے');
INSERT INTO "ur_maududi" VALUES(6,103,'6:103','نگاہیں اس کو نہیں پا سکتیں اور وہ نگاہوں کو پا لیتا ہے، وہ نہایت باریک بیں اور باخبر ہے');
INSERT INTO "ur_maududi" VALUES(6,104,'6:104','دیکھو، تمہارے پاس تمہارے رب کی طرف سے بصیرت کی روشنیاں آگئی ہیں، اب جو بینائی سے کام لے گا اپنا ہی بھلا کرے گا اور جو اندھا بنے گا خود نقصان اُٹھائے گا، میں تم پر کوئی پاسبان نہیں ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(6,105,'6:105','اِس طرح ہم اپنی آیات کو بار بار مختلف طریقوں سے بیان کرتے ہیں اور اس لیے کرتے ہیں کہ یہ لوگ کہیں تم کسی سے پڑھ آئے ہو، اور جو لوگ علم رکھتے ہیں ان پر ہم حقیقت کو روشن کردیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,106,'6:106','اے محمدؐ! اُس وحی کی پیروی کیے جاؤ جو تم پر تمہارے رب کی طرف سے نازل ہوئی ہے کیونکہ اُس ایک رب کے سوا کوئی اور خدا نہیں ہے اور ان مشرکین کے پیچھے نہ پڑو');
INSERT INTO "ur_maududi" VALUES(6,107,'6:107','اگر اللہ کی مشیّت ہوتی تو (وہ خود ایسا بندوبست کرسکتا تھا کہ) یہ لوگ شرک نہ کرتے۔ تم کو ہم نے ان پر پاسبان مقرر نہیں کیا ہے اور نہ تم ان پر حوالہ دار ہو۔ ');
INSERT INTO "ur_maududi" VALUES(6,108,'6:108','اور (اے ایمان لانے والو !) یہ لوگ اللہ کے سوا جن کو پکارتے ہیں انہیں گالیاں نہ دو، کہیں ایسا نہ ہو کہ یہ شرک سے آگے بڑھ کر جہالت کی بنا پر اللہ کو گالیاں دینے لگیں۔ ہم نے تو اسی طرح ہر گروہ کے لیے اس کے عمل کو خوشنما بنادیا ہے، پھر انہیں اپنے رب ہی کی طرف پلٹ کر آنا ہے، اُس وقت وہ اُنہیں بتادے گا کہ وہ کیا کرتے رہے ہیں۔');
INSERT INTO "ur_maududi" VALUES(6,109,'6:109','یہ لوگ کڑی کڑی قسمیں کھا کھاکر کہتے ہیں کہ اگر کوئی نشانی ہمارے سامنے آجائے تو ہم اس پر ایمان لے آئیں گے۔ اے محمد ؐ ! ان سے کہو کہ ”نشانیاں تو اللہ کے پاس ہیں“۔ اور تمہیں کیسے سمجھایا جائے کہ اگر نشانیاں آبھی جائیں تو یہ ایمان لانے والے نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,110,'6:110','ہم اُسی طرح ان کے دلوں اور نگاہوں کو پھیر رہے ہیں جس طرح یہ پہلی مرتبہ اس پر ایمان نہیں لائے تھے۔ ہم انہیں ان کی سرکشی ہی میں بھٹکنے کے لیے چھوڑے دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(6,111,'6:111','اگر ہم فرشتے بھی ان پر نازل کردیتے اور مُردے ان سے باتیں کرتے اور دنیا بھر کی چیزوں کو ہم ان کی آنکھوں کے سامنے جمع کردیتے تب بھی یہ ایمان لانے والے نہ تھے، اِلّا یہ کہ مشیَّتِ الٰہی یہی ہو کہ وہ ایمان لائیں، مگر اکثر لوگ نادانی کی باتیں کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(6,112,'6:112','اور ہم نے تو اسی طرح ہمیشہ شیطان انسانون اور شیطان جِنوں کو ہر نبی کا دُشمن بنایا ہے جو ایک دُوسرے پر خوش آیند باتیں دھوکے اور فریب کے طور پر القا کرتے رہے ہیں۔ اگر تمہارے رب کی مشیَّت یہ ہوتی کہ وہ ایسا نہ کریں تو وہ کبھی نہ کرتے۔ پس تم اُنہیں ان کے حال پر چھوڑ دو کہ اپنی افتراپردازیاں کرتے رہیں');
INSERT INTO "ur_maududi" VALUES(6,113,'6:113','(یہ سب کچھ ہم انہیں اسی لیے کرنے دے رہے ہیں کہ) جو لوگ آخرت پر ایمان نہیں رکھتے اُن کے دل اِس (خوشنما دھوکے) کی طرف مائل ہوں اور وہ اس سے راضی ہو جائیں اور اُن برائیوں کا اکتساب کریں جن کا اکتساب وہ کرنا چاہتے ہیں');
INSERT INTO "ur_maududi" VALUES(6,114,'6:114','پھر جب حال یہ ہے تو کیا میں اللہ کے سوا کوئی اور فیصلہ کرنے والا تلاش کروں، حالانکہ اس نے پوری تفصیل کے ساتھ تمہاری طرف کتاب نازل کردی ہے؟ اور جن لوگوں کو ہم نے (تم سے پہلے) کتاب دی تھی وہ جانتے ہیں کہ یہ کتاب تمہارے رب ہی کی طرف سے حق کے ساتھ نازل ہوئی ہے لہٰذا تم شک کرنے والوں میں شامل نہ ہو۔ ');
INSERT INTO "ur_maududi" VALUES(6,115,'6:115','تمہارے رب کی بات سچائی اور انصاف کے اعتبار سے کامل ہے، کوئی اس کے فرامین کو تبدیل کرنے والا نہیں ہے اور وہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "ur_maududi" VALUES(6,116,'6:116','اور اے محمد ؐ ! اگر تم اُن لوگوں کی اکثریّت کے کہنے پر چلو جو زمین میں بستے ہیں تو وہ تمہیں اللہ کے راستہ سے بھٹکا دیں گے۔ وہ تو محض گمان پر چلتے اور قیاس آرا ئیاں کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,117,'6:117','در حقیقت تمہارا رب زیادہ بہتر جانتا ہے کہ کون اُس کے راستے سے ہٹا ہوا ہے اور کون سیدھی راہ پر ہے');
INSERT INTO "ur_maududi" VALUES(6,118,'6:118','پھر اگر تم لوگ اللہ کی آیات پر ایمان رکھتے ہو تو جس جانور پر اللہ کا نام لیا گیا ہو اُس کا گوشت کھاوٴ۔ ');
INSERT INTO "ur_maududi" VALUES(6,119,'6:119','آخر کیا وجہ ہے کہ تم وہ چیز نہ کھاوٴ جس پر اللہ کا نام لیا گیا ہو ، حالانکہ جب چیزوں کا استعمال حالتِ اضطرار کے سوا دُوسری تمام حالتوں میں اللہ نے حرام کردیا ہے اُن کی تفصیل وہ تمہیں بتاچکا ہے۔ بکثرت لوگوں کا حال یہ ہے کہ علم کے بغیر محض اپنی خواہشات کی بنا پر گمراہ کُن باتیں کرتے ہیں، ان حد سے گزرنے والوں کو تمہارا رب خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(6,120,'6:120','تم کھلے گناہوں سے بھی بچو اور چھپے گناہوں سے بھی، جو لوگ گناہ کا اکتساب کرتے ہیں وہ اپنی اس کمائی کا بدلہ پاکر رہیں گے');
INSERT INTO "ur_maududi" VALUES(6,121,'6:121','اور جس جانور کو اللہ کا نام لے کر ذبح نہ کیا گیا ہو اس کا گوشت نہ کھاوٴ، ایسا کرنا فسق ہے۔ شیاطین اپنے ساتھیوں کے دِلوں میں شکوک و اعتراضات القا کرتے ہیں تاکہ وہ تم سے جھگڑا کریں۔ لیکن اگر تم نے اُن کی اطاعت قبول کرلی تو یقیناً تم مشرک ہو۔ ');
INSERT INTO "ur_maududi" VALUES(6,122,'6:122','کیا وہ شخص جو پہلے مُردہ تھا پھر ہم نے اسے زندگی بخشی اور اس کو وہ روشنی عطا کی جس کے اُجالے میں وہ لوگوں کے درمیان زندگی کی راہ طے کرتا ہے اُس شخص کی طرح ہو سکتا ہے جو تاریکیوں میں پڑا ہوا ہو اور کسی طرح اُن سے نہ نکلتا ہو؟ کافروں کے لیے تو اسی طرح ان کے اعمال خوشنما بنادیے گئے ہیں، ');
INSERT INTO "ur_maududi" VALUES(6,123,'6:123','اور اسی طرح ہم نے ہر بستی میں اس کے بڑے بڑے مجرموں کو لگا دیا ہے کہ وہاں اپنے مکر و فریب کا جال پھیلائیں دراصل وہ اپنے فریب کے جال میں آپ پھنستے ہیں، مگر اُنہیں اس کا شعور نہیں ہے');
INSERT INTO "ur_maududi" VALUES(6,124,'6:124','جب ان کے سامنے کوئی آیت آتی ہے تو وہ کہتے ہیں ”ہم نہ مانیں گے جب تک کہ وہ چیز خود ہم کو نہ دی جائے جو اللہ کے رسُولوں کو دی گئی ہے“۔ اللہ زیادہ بہتر جانتا ہے کہ اپنی پیغامبری کا کام کس سے لے اور کس طرح لے۔ قریب ہے وہ وقت جب یہ مجرم اپنی مکّاریوں کی پاداش میں اللہ کے ہاں ذلّت اور سخت عذاب سے دوچار ہوں گے');
INSERT INTO "ur_maududi" VALUES(6,125,'6:125','پس (یہ حقیقت ہے کہ) جسے اللہ ہدایت بخشنے کا ارادہ کرتا ہے اُس کا سینہ اسلام کے لیے کھول دیتا ہے اور جسے گمراہی میں ڈالنے کا ارادہ کرتا ہے اُس کے سینے کو تنگ کردیتا ہے اور ایسا بھینچتا ہے کہ (اسلام کا تصوّر کرتے ہی) اُسے یوں معلوم ہونے لگتا ہے کہ گویا اس کی رُوح آسمان کی طرف پرواز کررہی ہے۔ اس طرح اللہ ( حق سے فرار اور نفرت کی) ناپاکی اُن لوگوں پر مسلّط کردیتا ہے جو ایمان نہیں لاتے');
INSERT INTO "ur_maududi" VALUES(6,126,'6:126','حالانکہ یہ راستہ تمہارے رب کا سیدھا راستہ ہے اور اس کے نشانات اُن لوگوں کے لیے واضح کر دیے گئے ہیں جو نصیحت قبول کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(6,127,'6:127','اُن کیلیے اُن کے رب کے پاس سلامتی کا گھر ہے اور وہ ان کا سرپرست ہے اُس صحیح طرزِ عمل کی وجہ سے جو انہوں نے اختیار کیا');
INSERT INTO "ur_maududi" VALUES(6,128,'6:128','جس روز اللہ ان سب لوگوں کو گھیر کر جمع کرے گا، اس روز وہ جِنوں سے خطاب کرکے فرمائے گا کہ ”اے گروہِ جِن! تم نے تو نوعِ انسانی پر خوب ہاتھ صاف کیا“۔ انسانوں میں سے جو اُن کے رفیق تھے وہ عرض کریں گے ”پروردگار ! ہم میں سے ہر ایک نے دُوسرے کو خوب استعمال کیا ہے، اور اب ہم اُس وقت پر آپہنچے ہیں جو تُونے ہمارے لیے مقرر کردیا تھا“۔ اللہ فرمائے گا ”اچھا اب آگ تمہارا ٹھکانا ہے، اس میں تم ہمیشہ رہوگے“۔ اُس سے بچیں گے صرف وہی جنہیں اللہ بچانا چاہے گا، بیشک تمہارا رب دانا اور علیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(6,129,'6:129','دیکھو، اس طرح ہم (آخرت میں) ظالموں کو ایک دُوسرے کا ساتھی بنائیں گے اُس کمائی کی وجہ سے جو وہ (دُنیا میں ایک دُوسرے کے ساتھ مِل کر) کرتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(6,130,'6:130','(اس موقع پر اللہ ان سے یہ بھی پوچھے گا کہ) ”اے گروہِ جِن و انس ! کیا تمہارے پاس خود تم ہی میں سے وہ پیغمبر نہیں آئے تھے جو تم کو میری آیات سناتے اور اِس دن کے انجام سے ڈراتے تھے“؟ وہ کہیں گے ”ہاں ! ہم اپنے خلاف خود گواہی دیتے ہیں“۔ آج دُنیا کی زندگی نے ان لوگوں کو دھوکے میں ڈال رکھا ہے، مگر اُس وقت وہ خود اپنے خلاف گواہی دیں گے کہ وہ کافر تھے۔ ');
INSERT INTO "ur_maududi" VALUES(6,131,'6:131','(یہ شہادت اُن سے اس لیے لی جائے گی کہ یہ ثابت ہوجائے کہ) تمہارا رب بستیوں کو ظلم کے ساتھ تباہ کرنے والا نہ تھا جبکہ ان کے باشندے حقیقت سے ناواقف ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(6,132,'6:132','ہر شخص کا درجہ اُس کے عمل کے لحاظ سے ہے اور تمہارا رب لوگوں کے اعمال سے بے خبر نہیں ہے');
INSERT INTO "ur_maududi" VALUES(6,133,'6:133','تمہارا رب بے نیاز ہے اور مہربانی اس کا شیوہ ہے۔ اگر وہ چاہے تو تم لوگوں کو لے جائے اور تمہاری جگہ دُوسرے جن لوگوں کو چاہے لے آئے جس طرح اُس نے تمہیں کچھ اور لوگوں کی نسل سے اُٹھایا ہے');
INSERT INTO "ur_maududi" VALUES(6,134,'6:134','تم سے جس چیز کا وعدہ کیا جارہا ہے وہ یقیناً آنے والی ہے اور تم خدا کو عاجز کردینے کی طاقت نہیں رکھتے');
INSERT INTO "ur_maududi" VALUES(6,135,'6:135','اے محمد ؐ !کہہ دو کہ لوگو !تم اپنی جگہ عمل کرتے رہو اور میں بھی اپنی جگہ عمل کررہا ہوں، عنقریب تمہیں معلوم ہوجائے گا کہ انجام کار کس کے حق میں بہتر ہوتا ہے، بہرحال یہ حقیقت ہے کہ ظالم کبھی فلاح نہیں پاسکتے');
INSERT INTO "ur_maududi" VALUES(6,136,'6:136','ان لوگوں نے اللہ کے لیے خود اُسی کی پیدا کی ہوئی کھیتیوں اور مویشیوں میں سے ایک حصّہ مقرر کیا ہے اور کہتے ہیں یہ اللہ کے لیے ہے، بزعمِ خود، اور یہ ہمارے ٹھیراے ہوئے شریکوں کے لیے۔ پھر جو حصّہ ان کے ٹھیرائے ہوے شریکوں کے لیے ہے وہ تو اللہ کو نہیں پہنچتا مگر جو اللہ کے لیے ہے وہ ان کے شریکوں کو پہنچ جاتا ہے۔ کیسے بُرے فیصلے کرتے ہیں یہ لوگ !');
INSERT INTO "ur_maududi" VALUES(6,137,'6:137','اور اسی طرح بہت سے مشرکوں کے لیے ان کے شریکوں نے اپنی اولاد کے قتل کو خوشنما بنادیا ہے تاکہ ان کو ہلاکت میں مُبتلا کریں اور ان پر ان کے دین کو مشتبہ بنادیں۔ اگر اللہ چاہتا تو یہ ایسا نہ کرتے، لہٰذا انہیں چھوڑ دو کہ اپنی افتراپردازیوں میں لگے رہیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,138,'6:138','کہتے ہیں یہ جانور اور یہ کھیت محفوظ ہیں، انہیں صرف وہی لوگ کھاسکتے ہیں نہیں ہم کھلانا چاہیں، حالانکہ یہ پابندی ان کی خود ساختہ ہے۔ پھر کچھ جانور رہیں جن پر سواری اور بار برداری حرام کردی گئی ہے اور کچھ جانور ہیں جن پر اللہ کا نام نہیں لیت ے، اور یہ سب کچھ انہوں نے اللہ پر افتراکیا ہے، عنقریب اللہ انہیں ان افتراپردازیوں کا بدلہ دے گا');
INSERT INTO "ur_maududi" VALUES(6,139,'6:139','اور کہتے ہیں کہ جو کچھ ان جانوروں کے پیٹ میں ہے یہ ہمارے مردوں کے لیے مخصوص ہے اور ہماری عورتوں پر حرام، لیکن اگر وہ مُردہ ہو تو دونوں اس کے کھانے میں شریک ہوسکتے ہیں۔ یہ باتیں جو انہوں نے گھڑلی ہیں ان کا بدلہ اللہ انہیں دے کررہے گا۔ یقیناً وہ حکیم ہے اور سب باتوں کی اسے خبر ہے');
INSERT INTO "ur_maududi" VALUES(6,140,'6:140','یقیناً خسارے میں پڑگئے وہ لوگ جنہوں نے اپنی اولاد کو جہالت و نادانی کی بنا پر قتل کیا اور اللہ کے دیے ہوئے رزق کو اللہ پر افترا پردازی کرکے حرام ٹھیرالیا۔ یقیناً وہ بھٹک گئے اور ہر گز وہ راہِ راست پانے والوں میں سے نہ تھے۔ ');
INSERT INTO "ur_maududi" VALUES(6,141,'6:141','وہ اللہ ہی ہے جس نے طرح طرح کے باغ اور تاکستان اور نخلستان پیدا کیے، کھیتیاں اُگائیں جن سے قسم قسم کے ماکولات حاصل ہوتے ہیں، زیتون اور انار کے درخت پیدا کیے جن کے پھل صُورت میں مشابہ اور مزے میں مختلف ہوتے ہیں۔ کھاوٴ ان کی پیداوار جب کہ یہ پھلیں ، اور اللہ کا حق ادا کرو جب ان کی فصل کاٹو، اور حد سے نہ گزرو کہ اللہ حد سے گزرنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(6,142,'6:142','پھر وہی ہے جس نے مویشیوں میں سے وہ جانور بھی پیدا کیے جن سے سواری و بار برداری کا کام لیا جاتا ہے اور وہ بھی جو کھانے اور بچھانے کے کام آتے ہیں۔ کھاوٴ اُن چیزوں میں سے جو اللہ نے تمہیں بخشی ہیں اور شیطان کی پیروی نہ کرو کہ وہ تمہارا کُھلا دشمن ہے۔ ');
INSERT INTO "ur_maududi" VALUES(6,143,'6:143','یہ آٹھ نر و مادہ ہیں، دو بھیڑ کی قسم سے اور دو بکری کی قسم سے، اے محمد ؐ ! ان سے پُوچھو کہ اللہ نے اُن کے نر حرام کیے ہیں یا مادہ، یا وہ بچّے جو بھیڑوں اور بکریوں کے پیٹ میں ہوں؟ ٹھیک ٹھیک علم کے ساتھ مجھے بتا ؤ اگر تم سچے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(6,144,'6:144','اور اسی طرح دو اُونٹ کی قسم سے ہیں اور دو گائے کی قسم سے۔ پوچھو، اِن کے نر اللہ نے حرام کیے ہیں یا مادہ، یا وہ بچّے جو اُونٹنی اور گائے کے پیٹ میں ہوں؟ کیا تم اُس وقت حاضر تھے جب اللہ نے ان کے حرام ہونے کا حکم تمہیں دیا تھا؟ پھر اُس شخص سے بڑھ کر ظالم اور کون ہوگا جو اللہ کی طرف منسُوب کرکے جُھوٹی بات کہے تاکہ عِلم کے بغیر لوگوں کی غلط راہ نمائی کرے۔ یقیناً اللہ ایسے ظالموں کو راہِ راست نہیں دکھاتا');
INSERT INTO "ur_maududi" VALUES(6,145,'6:145','اے محمد ؐ ! ان سے کہو کہ جو وحی میرے پاس آئی ہے اس میں تو میں کوئی چیز ایسی نہیں پاتا جو کسی کھانے والے پر حرام ہو، اِلّا یہ کہ وہ مردار ہو، یا بہایا ہوا خون ہو، یا سُور کا گوشت ہو کہ وہ ناپاک ہے، یا فسق ہوکہ اللہ کے سوا کسی اور کے نام پر ذبح کیا گیا ہو۔ پھر جو شخص مجبُوری کی حالت میں (کوئی چیز ان میں سے کھالے) بغیر اس کے کہ وہ نافرمانی کا ارادہ رکھتا ہو اور بغیر اس کے کہ وہ حدِ ضرورت سے تجاوز کرے، تو یقیناً تمہارا رب درگذر سے کام لینے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(6,146,'6:146','اور جن لوگوں نے یہودیّت اختیار کی ان پر ہم نے سب ناخن والے جانور حرام کردیے تھے، اور گائے اور بکری کی چربی بھی بجز اُس کے جو اُن کی پیٹھ یا اُن کی آنتوں سے لگی ہوئی ہو یا ہڈّی سے لگی رہ جائے۔ یہ ہم نے ان کی سرکشی کی سزا اُنہیں دی تھی اور یہ جو کچھ ہم کہہ رہے ہیں بالکل سچ کہہ رہے ہیں');
INSERT INTO "ur_maududi" VALUES(6,147,'6:147','اب اگر وہ تمہیں جھٹلائیں تو ان سے کہہ دو کہ تمہارے رب کا دامن ِ رحمت وسیع ہے اور مجرموں سے اس کے عذاب کو پھیرا نہیں جاسکتا۔ ');
INSERT INTO "ur_maududi" VALUES(6,148,'6:148','یہ مشرک لوگ (تمہاری ان باتوں کے جواب میں) ضرور کہیں گے کہ ”اگر اللہ چاہتا تو نہ ہم شرک کرتے اور نہ ہمارے باپ دادا، اور نہ ہم کسی چیز کو حرام ٹھیراتے“۔ ایسی ہی باتیں بنا بنا کر اِن سے پہلے کے لوگوں نے بھی حق کو جھٹلایا تھا یہاں تک کہ آخر کار ہمارے عذاب کا مزا انہوں نے چکھ لیا۔ ان سے کہو ”کیا تمہارے پاس کوئی علم ہے جسے ہمارے سامنے پیش کرسکو؟ تم تو محض گمان پر چل رہے ہو اور نری قیاس آرائیاں کرتے ہو“');
INSERT INTO "ur_maududi" VALUES(6,149,'6:149','پھر کہو (تمہاری اس حجّت کے مقابلہ میں) حقیقت رس حجت تو اللہ کے پاس ہے، بے شک اگر اللہ چاہتا تو تم سب کو ہدایت دے دیتا۔ ');
INSERT INTO "ur_maududi" VALUES(6,150,'6:150','ان سے کہو کہ ”لاوٴ اپنے وہ گواہ جو اس بات کی شہادت دیں کہ اللہ ہی نے اِن چیزوں کو حرام کیا ہے“۔ پھر اگر وہ شہادت دے دیں تو تم ان کے ساتھ شہادت نہ دینا، اور ہرگز اُن لوگوں کی خواہشات کے پیچھے نہ چلنا جنھوں نے ہماری آیات کو جھٹلایا ہے، اور جو آخرت کے منکر ہیں، اور جو دُوسروں کو اپنے رب کا ہمسر بناتے ہیں');
INSERT INTO "ur_maududi" VALUES(6,151,'6:151','اے محمد ؐ ! ان سے کہو کہ آوٴ میں تمہیں سُناوٴں تمہارے رب نے تم پر کیا پابندیاں عائد کی ہیں: ___( ) یہ کہ اس کے ساتھ کسی کو شریک نہ کرو، (۲) اور والدین کے ساتھ نیک سلوک کرو، (۳) اور اپنی اولاد کو مُفلسی کے ڈر سے قتل نہ کرو، ہم تمہیں بھی رزق دیتے ہیں اور ان کو بھی دیں گے۔(۴) اور بے شرمی کی باتوں کے قریب بھی نہ جاو ٴ خواہ وہ کُھلی ہوں یا چُھپی۔(۵) اور کسی جان کو جسے اللہ نے محترم ٹھیرایا ہے ہلاک نہ کرو مگر حق کے ساتھ۔ یہ باتیں ہیں جن کی ہدایت اس نے تمہیں کی ہے، شاید کہ تم سمجھ بوجھ سے کام لو');
INSERT INTO "ur_maududi" VALUES(6,152,'6:152','( ) اور یہ کہ یتیم کے مال کے قریب نہ جاوٴ مگر ایسے طریقہ سے جو بہترین ہو یہاں تک کہ وہ اپنے سنِ رُشد کو پہنچ جائے،(۷) اور ناپ تول میں پورا انصاف کرو، ہم ہر شخص پر ذمہ داری کا اُتنا ہی بار رکھتے ہیں جتنا اس کے امکان میں ہے، (۸) اور جب بات کہو انصاف کی کہو خواہ معاملہ اپنے رشتہ دار ہی کا کیوں نہ ہو، (۹) اور اللہ کے عہد کو پُورا کرو۔ ان باتوں کی ہدایت اللہ نے تمہیں کی ہے شاید کہ تم نصیحت قبول کرو');
INSERT INTO "ur_maududi" VALUES(6,153,'6:153','( ) نیز اس کی ہدایت یہ ہے کہ یہی میرا سیدھا راستہ ہے لہٰذا تم اِسی پر چلو اور دُوسرے راستوں پر نہ چلو کہ وہ اُس کے راستے سے ہٹاکر تمہیں پراگندہ کردیں گے۔ یہ ہے وہ ہدایت جو تمہارے رب نے تمہیں کی ہے، شاید کہ تم کَج روی سے بچو۔');
INSERT INTO "ur_maududi" VALUES(6,154,'6:154','پھر ہم نے موسیٰ ؑ کو کتاب عطا کی تھی جو بھلائی کی روش اختیار کرنے والے انسان پر نعمت کی تکمیل اور ہر ضروری چیز کی تفصیل اور سراسر ہدایت و رحمت تھی (اور اس لیے بھی اسرائیل کو دی گئی تھی کہ) شاید لوگ اپنے رب کی ملاقات پر ایمان لائیں۔ ');
INSERT INTO "ur_maududi" VALUES(6,155,'6:155','اور اسی طرح یہ کتاب ہم نے نازل کی ہے، ایک برکت والی کتاب پس تم اِس کی پیروی کرو اور تقویٰ کی روش اختیار کرو، بعید نہیں کہ تم پر رحم کیا جائے');
INSERT INTO "ur_maududi" VALUES(6,156,'6:156','اب تم یہ نہیں کہہ سکتے کہ کتاب تو ہم سے پہلے کے دو گروہوں کو دی گئی تھی، اور ہم کو کچھ خبر نہ تھی کہ وہ کیا پڑھتے پڑھاتے تھے');
INSERT INTO "ur_maududi" VALUES(6,157,'6:157','اور اب تم یہ بہانہ بھی نہیں کرسکتے کہ اگر ہم پر کتاب نازل کی گئی ہوتی تو ہم ان سے زیادہ راست رو ثابت ہوتے۔ تمہارے پاس تمہارے رب کی طرف سے ایک دلیلِ روشن اور ہدایت اور رحمت آگئی ہے، اب اس سے بڑھ کر ظالم کون ہوگا جو اللہ کی آیات کو جھٹلائے اور ان سے منہ موڑے۔ جو لوگ ہماری آیات سے منہ موڑتے ہیں انہیں اس رُوگردانی کی پاداش میں ہم بدترین سزا دے کر رہیں گے');
INSERT INTO "ur_maududi" VALUES(6,158,'6:158','کیا اب لوگ اس کے منتظر ہیں کہ ان کے سامنے فرشتے آ کھڑے ہوں، یا تمہارا رب خود آجائے، یا تمہارے رب کی بعض صریح نشانیاں نمودار ہوجائیں؟ جس روز تمہارے رب کی بعض مخصوص نشانیاں نمودار ہوجائیں گی پھر کسی ایسے شخص کو اس کا ایمان کچھ فائدہ نہ دے گا جو پہلے ایمان نہ لایا ہو یا جس نے اپنے ایمان میں کوئی بھلائی نہ کمائی ہو۔ اے محمد ؐ ! ان سے کہہ دوکہ اچھا، تم انتظار کرو، ہم بھی انتظار کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(6,159,'6:159','جن لوگوں نے اپنے دین کو ٹکڑے ٹکڑے کردیا اور گروہ گروہ بن گئے یقیناً ان سے تمہارا کچھ واسطہ نہیں، ان کا معاملہ تو اللہ کے سپرد ہے، وہی ان کو بتائے گا کہ انہوں نے کیا کچھ کیا ہے');
INSERT INTO "ur_maududi" VALUES(6,160,'6:160','جو اللہ کے حضور نیکی لے کر آئے گا اس کے لیے دس گنا اجر ہے، اور جو بدی لے کر آئے گا اس کو اتنا ہی بدلہ دیا جائے گا جتنا اس نے قصور کیا ہے، اور کسی پر ظلم نہ کیا جائے گا');
INSERT INTO "ur_maududi" VALUES(6,161,'6:161','اے محمد ؐ ! کہو میرے رب نے بالیقین مجھے سیدھا راستہ دکھا دیا ہے، بالکل ٹھیک دین جس میں کوئی ٹیڑھ نہیں، ابراہیم ؑ کا طریقہ جسے یکسُو ہوکر اُس نے اختیار کیا تھا اور وہ مشرکوں میں سے نہ تھا');
INSERT INTO "ur_maududi" VALUES(6,162,'6:162','کہو، میری نماز، میرے تمام مراسِم عبودیت، میرا جینا اور میرا مرنا، سب کچھ اللہ رب العالمین کے لیے ہے');
INSERT INTO "ur_maududi" VALUES(6,163,'6:163','جس کا کوئی شریک نہیں اسی کا مجھے حکم دیا گیا ہے اور سب سے پہلے سر اطاعت جھکانے والا میں ہوں');
INSERT INTO "ur_maududi" VALUES(6,164,'6:164','کہو، کیا میں اللہ کے سوا کوئی اور رب تلاش کروں حالانکہ وہی ہر چیز کا رب ہے؟ ہر شخص جو کچھ کماتا ہے اس کا ذمہ دار وہ خود ہے، کوئی بوجھ اُٹھانے والا دُوسرے کا بوجھ نہیں اُٹھاتا، پھر تم سب کو اپنے رب کی طرف پلٹنا ہے، اُس وقت وہ تمہارے اختلافات کی حقیقت تم پر کھول دے گ');
INSERT INTO "ur_maududi" VALUES(6,165,'6:165','وہی ہے جس نے تم کو زمین کا خلیفہ بنایا، اور تم میں سے بعض کو بعض کے مقابلہ میں زیادہ بلند درجے دیے، تاکہ جو کچھ تم کو دیا ہے اس میں تمہاری آزمائش کرے۔ بے شک تمہارا رب سزا دینے میں بھی بہت تیز ہے اور بہت درگزر کرنے اور رحم فرمانے والا بھی ہے');
INSERT INTO "ur_maududi" VALUES(7,1,'7:1','ا، ل، م، ص');
INSERT INTO "ur_maududi" VALUES(7,2,'7:2','یہ ایک کتاب ہے جو تمہاری طرف نازل کی گئی ہے، پس اے محمد صلى الله عليه وسلم ، تمہارے دل میں اس سے کوءی جھجک نہ ہو۔ اس کے اُتارنے کی غرض یہ ہے کہ تم اس کے ذریعہ سے (منکرین کو)ڈراؤ اور ایمان لانے والے لوگوں کو یاد دہانی ہو۔ ');
INSERT INTO "ur_maududi" VALUES(7,3,'7:3','لوگو، جو کچھ تمہارے ربّ کی طرف سے تم پر نازل کیا گیا ہے اُس کی پیروی کرو اور اپنے ربّ کو چھوڑ کر دوسرے سرپرستوں کی پیروی نہ کرو۔۔۔ مگر تم نصیحت کم ہی مانتے ہو');
INSERT INTO "ur_maududi" VALUES(7,4,'7:4','کتنی ہی بستیاں ہیں جنہیں ہم نے ہلاک کر دیا اُن پر ہمارا عذاب اچانک رات کے وقت ٹوٹ پڑا، یا دن دہاڑے ایسے وقت آیا جب وہ آرام کر رہے تھے');
INSERT INTO "ur_maududi" VALUES(7,5,'7:5','اور جب ہمارا عذاب اُن پر آگیا تو ان کی زبان پر اِس کے سوا کوئی صدا نہ تھی کہ واقعی ہم ظالم تھے۔ ');
INSERT INTO "ur_maududi" VALUES(7,6,'7:6','پس یہ ضرور ہو کر رہنا ہے کہ ہم اُن لوگوں سے باز پرس کریں، جن کی طرف ہم نے پیغمبر بھیجے ہیں اور پیغمبروں سے بھی پوچھیں( کہ اُنہوں نے پیغام رسانی کا فرض کہاں تک انجام دیا اور انہیں اس کا کیا جواب ملا) ');
INSERT INTO "ur_maududi" VALUES(7,7,'7:7','پھر ہم خود پور ے علم کے ساتھ سرگزشت ان کے آگے پیش کر دیں گے، آخر ہم کہیں غائب تو نہیں تھے');
INSERT INTO "ur_maududi" VALUES(7,8,'7:8','اور وزن اس روز عین حق ہوگا۔ جن کے پلڑے بھاری ہوں گے وہی فلاح پائیں گے');
INSERT INTO "ur_maududi" VALUES(7,9,'7:9','اور جن کے پلڑے ہلکے ہوں گے وہی اپنے آپ کو خسارے میں مبتلا کرنے والے ہوں گے کیونکہ وہ ہماری آیات کے ساتھ ظالمانہ برتاوٴ کرتے رہے تھے');
INSERT INTO "ur_maududi" VALUES(7,10,'7:10','ہم نے تمھیں زمین میں اختیارات کے ساتھ بسایا اور تمہارے لیے یہاں سامان زیست فراہم کیا، مگر تم لوگ کم ہی شکر گزار ہوتے ہو');
INSERT INTO "ur_maududi" VALUES(7,11,'7:11','ہم نے تمہاری تخلیق کی ابتدا کی، پھر تمہاری صورت بنائی، پھر فرشتوں سے کہا آدم کو سجدہ کرو، اس پر سب نے سجدہ کیا مگر ابلیس سجدہ کرنے والوں میں شامل نہ ہوا');
INSERT INTO "ur_maududi" VALUES(7,12,'7:12','پوچھا، "تجھے کس چیز نے سجدہ کرنے سے روکا جب کہ میں نے تجھ کو حکم دیا تھا؟" بولا، "میں اُس سے بہتر ہوں، تو نے مجھے آگ سے پیدا کیا ہے اور اُسے مٹی سے"');
INSERT INTO "ur_maududi" VALUES(7,13,'7:13','فرمایا ، ”اچھا، تو یہاں سے نیچے اُتر۔ تجھے حق نہیں ہے کہ یہاں بڑائی کا گھمنڈ کرے۔ نکل جا کہ درحقیقت تُو اُن لوگوں میں سے ہے جو اپنی ذلّت چاہتے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(7,14,'7:14','بولا، "مجھے اُس دن تک مہلت دے جب کہ یہ سب دوبارہ اٹھائے جائیں گے"');
INSERT INTO "ur_maududi" VALUES(7,15,'7:15','فرمایا، "تجھے مہلت ہے"');
INSERT INTO "ur_maududi" VALUES(7,16,'7:16','بولا، "اچھا تو جس طرح تو نے مجھے گمراہی میں مبتلا کیا میں بھی اب تیری سیدھی راہ پر');
INSERT INTO "ur_maududi" VALUES(7,17,'7:17','اِن انسانوں کی گھات میں لگا رہوں گا، آگے اور پیچھے، دائیں اور بائیں، ہر طرف سے اِن کو گھیروں گا اور تو ان میں سے اکثر کو شکر گزار نہ پائےگا۔“ ');
INSERT INTO "ur_maududi" VALUES(7,18,'7:18','فرمایا، " نکل جا یہاں سے ذلیل اور ٹھکرایا ہوا یقین رکھ کہ اِن میں سے جو تیری پیروی کریں گے، تجھ سمیت ان سب سے جہنم کو بھر دوں گا');
INSERT INTO "ur_maududi" VALUES(7,19,'7:19','اور اے آدمؑ، تو اور تیری بیوی، دونوں اس جنت میں رہو، جہاں جس چیز کو تمہارا جی چاہے کھاؤ، مگر اس درخت کے پاس نہ پھٹکنا ورنہ ظالموں میں سے ہو جاؤ گے"');
INSERT INTO "ur_maududi" VALUES(7,20,'7:20','پھر شیطان نے اُن کو بہکایا تاکہ ان کی شرمگاہیں جو ایک دوسرے سے چھپائی گئی تھیں ان کے سامنے کھول دے اس نے ان سے کہا، "تمہارے رب نے تمہیں جو اس درخت سے روکا ہے اس کی وجہ اِس کے سوا کچھ نہیں ہے کہ کہیں تم فرشتے نہ بن جاؤ، یا تمہیں ہمیشگی کی زندگی حاصل نہ ہو جائے');
INSERT INTO "ur_maududi" VALUES(7,21,'7:21','اور اس نے قسم کھا کر ان سے کہا کہ میں تمہارا سچا خیر خواہ ہوں');
INSERT INTO "ur_maududi" VALUES(7,22,'7:22','اس طرح دھو کا دے کر وہ ان دونوں کو رفتہ رفتہ اپنے ڈھب پرلے آیا آخرکار جب انہوں نے اس درخت کا مزا چکھا تو ان کے ستر ایک دوسرے کے سامنے کھل گئے اور اپنے جسموں کو جنت کے پتوں سے ڈھانکنے لگے تب ان کے رب نے انہیں پکارا، " کیا میں نے تمہیں اس درخت سے نہ روکا تھا اور نہ کہا تھا کہ شیطان تمہارا کھلا دشمن ہے؟"');
INSERT INTO "ur_maududi" VALUES(7,23,'7:23','دونوں بول اُٹھے”اے ربّ! ہم نے اپنے اوپر ستم کیا، اب اگر تُو نے ہم سے درگزر نہ فرمایا اور رحم نہ کیا تو یقیناً ہم تباہ ہو جائیں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(7,24,'7:24','رمایا، ”اُتر جاوٴ ، تم ایک دُوسرے کے دشمن ہو، اور تمہارے لیے ایک خاص مدّت تک زمین ہی میں جائے قرار اور سامانِ زیست ہے۔“');
INSERT INTO "ur_maududi" VALUES(7,25,'7:25','اور فرمایا، "وہیں تم کو جینا اور وہیں مرنا ہے اور اسی میں سے تم کو آخرکار نکالا جائے گا"');
INSERT INTO "ur_maududi" VALUES(7,26,'7:26','اے اولادِآدم ، ہم نے تم پر لباس نازل کیا ہے کہ تمہارے جسم کے قابلِ شرم حصّوں کو ڈھانکے اور تمہارے لیے جسم کی حفاظت اور زینت کا ذریعہ بھی ہو، اور بہترین لباس تقوٰی کا لباس ہے۔ یہ اللہ کی نشانیوں میں سے ایک نشانی ہے، شاید کہ لوگ اِس سے سبق لیں');
INSERT INTO "ur_maududi" VALUES(7,27,'7:27','ے بنی آدم، ایسا نہ ہو کہ شیطان تمہیں پھر اُسی طرح فتنے میں مبتلا کر دے جس طرح اُس نے تمہارے والدین کو جنّت سے نکلوایا تھااور اُن کے لباس اُن پر سے اُتروا دیے تھےتاکہ اُن کی شرمگاہیں ایک دوسرے کے سامنے کھولے۔ وہ اور اُس کے ساتھی تمہیں ایسی جگہ سے دیکھتے ہیں جہاں سے تم اُنھیں نہیں دیکھ سکتے۔ اِن شیاطین کو ہم نے اُن لوگوں کا سرپرست بنا دیا ہے جو ایمان نہیں لاتے۔ ');
INSERT INTO "ur_maududi" VALUES(7,28,'7:28','یہ لوگ جب کو ئی شرمناک کام کرتے ہیں تو کہتے ہیں ہم نے اپنے باپ دادا کواِسی طریقہ پر پایا ہے اور اللہ ہی نے ہمیں ایسا کرنے کا حکم دیا ہے۔ اِن سے کہو اللہ بے حیائی کا حکم کبھی نہیں دیا کرتا۔ کیا تم اللہ کا نام لے کر وہ باتیں کہتے ہوجن کے متعلق تمہیں علم نہیں ہے کہ وہ اللہ کی طرف سے ہیں؟');
INSERT INTO "ur_maududi" VALUES(7,29,'7:29','اے محمدؐ ، اِن سے کہو، میرے ربّ نے تو راستی اور انصاف کا حکم دیا ہے، اور اُس کا حکم تو یہ ہے کہ ہر عبادت میں اپنا رُخ ٹھیک رکھواور اُسی کو پکارو اپنے دین کو اس کے لیے خالص رکھ کر،جس طرح اُس نے تمہیں اب پیدا کیا ہے اسی طرح تم پھر پیدا کیے جاوٴ گے۔ ');
INSERT INTO "ur_maududi" VALUES(7,30,'7:30','ایک گروہ کوتو اس نے سیدھا راستہ دکھا دیا ہے، مگر دوسرے گروہ پر گمراہی چسپاں ہو کر رہ گئی ہے، کیونکہ انہوں نے خدا کے بجائے شیاطین کو اپنا سر پرست بنا لیا ہے اور وہ سمجھ رہے ہیں کہ ہم سیدھی راہ پر ہیں');
INSERT INTO "ur_maududi" VALUES(7,31,'7:31','اے بنی آدم، ہر عبادت کے موقع پر اپنی زینت سے آراستہ رہو اور کھاوٴ پیو اور حد سے تجاوز نہ کرو، اللہ حد سے بڑھنے والوں کو پسند نہیں کرتا۔ ');
INSERT INTO "ur_maududi" VALUES(7,32,'7:32','اے محمد ؐ ، اِن سے کہو کس نے اللہ کی اُس زینت کو حرام کر دیا ہے جسے اللہ نے اپنے بندوں کے لیے نکالا تھا اور کس نے خدا کی بخشی ہوئی پاک چیزیں ممنوع کر دیں؟ کہو، یہ ساری چیزیں دنیا کی زندگی میں بھی ایمان لانے والوں کے لیے ہیں، اور قیامت کے روز تو خالصتًہ انہی کے لیے ہوں گی۔ اِس طرح ہم اپنی باتیں صاف صاف بیان کرتے ہیں اُن لوگوں کے لیے جو علم رکھنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(7,33,'7:33','اے محمد ؐ ، اِن سے کہو کہ میرے ربّ نے جو چیزیں حرام کی ہیں وہ تو یہ ہیں: بے شرمی کے کام۔۔۔۔خواہ کھُلے ہوں یا چھُپے۔۔۔۔ اور گُناہ اور حق کے خلاف زیادتی اور یہ کہ اللہ کے ساتھ کسی کو شریک کرو جس کے لیے اُس نے کوئی سند نازل نہیں کی اور یہ کہ اللہ کے نام پر کوئی ایسی بات کہو جس کے متعلق تمہیں علم نہ ہو کہ وہ حقیقت میں اُس نے فرمائی ہے');
INSERT INTO "ur_maududi" VALUES(7,34,'7:34','ہر قوم کے لیے مہلت کی ایک مدّت مقرر ہے، پھر جب کسی قوم کی مدّت آن پوری ہوتی ہے تو ایک گھڑی بھر کی تاخیر و تقدیم نہیں ہوتی۔ ');
INSERT INTO "ur_maududi" VALUES(7,35,'7:35','(اور یہ بات اللہ نے آغاز تخلیق ہی میں صاف فرما دی تھی کہ) اے بنی آدم، یاد رکھو، اگر تمہارے پاس خود تم ہی میں سے ایسے رسول آئیں جو تمہیں میری آیات سنا رہے ہوں، تو جو کوئی نافرمانی سے بچے گا اور اپنے رویہ کی اصلاح کر لے گا اس کے لیے کسی خوف اور رنج کا موقع نہیں ہے');
INSERT INTO "ur_maududi" VALUES(7,36,'7:36','اور جو لوگ ہماری آیات کو جھُٹلائیں گےاور ان کے مقابلہ میں سرکشی برتیں گےوہی اہلِ دوزخ ہوں گے جہاں وہ ہمیشہ رہیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(7,37,'7:37','ظاہر ہے کہ اُس سے بڑا ظالم اور کون ہو گا جو بالکل جھوٹی باتیں گھڑ کر اللہ کی طرف منسُوب کرے یا اللہ کی سچی آیات کو جھُٹلائے۔ ایسے لوگ اپنے نوشتہٴِ تقدیر کے مطابق اپنا حصّہ پاتے رہیں گے، یہاں تک کہ وہ گھڑی آجائےگی جب ہمارے بھیجے ہوئے فرشتے ان کی روحیں قبض کرنے کے لیے پہنچیں گے۔ اُس وقت وہ اُن سے پوچھیں گے کہ بتاوٴ، اب کہاں ہیں تمہارے وہ معبود جن کو تم خدا کے بجائے پُکارتے تھے؟ وہ کہیں گے کہ ”سب ہم سے گُم ہو گئے“۔ اور وہ خود اپنے خلاف گواہی دیں گے کہ ہم واقعی منکرِ حق تھے');
INSERT INTO "ur_maududi" VALUES(7,38,'7:38','اللہ فرمائے گا جاوٴ،تم بھی اُسی جہنم میں چلے جاوٴ جس میں تم سے پہلے گُزرے ہوئے گروہِ جِن و انس جا چکے ہیں۔ ہر گروہ جب جہنم میں داخل ہوگا تو اپنے پیش رو گروہ پر لعنت کرتا ہوا داخل ہوگا، حتٰی کہ سب وہاں جمع ہو جائیں گےتو ہر بعد والا گروہ پہلے گروہ کے حق میں کہے گا کہ اے ربّ، یہ لوگ تھے جنہوں نے ہم کو گمراہ کیا لہٰذا اِنہیں آگ کا دوہرا عذاب دے۔ جوام میں اِرشاد ہوگا، ہر ایک کے لیے دوہرا ہی عذاب ہےمگر تم جانتے نہیں ہو۔ ');
INSERT INTO "ur_maududi" VALUES(7,39,'7:39','اور پہلا گروہ دوسرے گروہ سے کہے گا(اگر ہم قابلِ الزام تھے) تو تمہی کو ہم پر کون سی فضیلت حاصل تھی، اب اپنی کمائی کے نتیجہ میں عذاب کا مزا چکھو۔ ');
INSERT INTO "ur_maududi" VALUES(7,40,'7:40','یقین جانو، جن لوگوں نے ہماری آیات کو جھٹلایا ہے اور ان کے مقابلہ میں سرکشی کی ہے ان کے لیے آسمان کے دروازے ہرگز نہ کھولے جائیں گے اُن کا جنت میں جانا اتنا ہی ناممکن ہے جتنا سوئی کے ناکے سے اونٹ کا گزرنا مجرموں کو ہمارے ہاں ایسا ہی بدلہ ملا کرتا ہے');
INSERT INTO "ur_maududi" VALUES(7,41,'7:41','ان کے لیے تو جہنم کا بچھونا ہوگا اور جہنم ہی کا اوڑھنا یہ ہے وہ جزا جو ہم ظالموں کو دیا کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(7,42,'7:42','بخلاف اس کے جن لوگوں نے ہماری آیات کو مان لیا ہے اور اچھے کام کیے ہیں اور اس باب میں ہم ہر ایک کو اس کی استطاعت ہی کے مطابق ذمہ دار ٹھیراتے ہیں وہ اہل جنت ہیں جہاں وہ ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(7,43,'7:43','ان کے دلوں میں ایک دُوسرے کے خلاف جو کچھ کدورت ہو گی اسے ہم نکال دیں گے۔ اُن کے نیچے نہریں بہتی ہوں گی، اور وہ کہیں گے کہ”تعریف خدا ہی کے لیے ہے جس نے ہمیں راستہ دکھایا، ہم خود راہ نہ پاسکتے تھے اگر خدا ہماری رہنمائی نہ کرتا ، ہمارے ربّ کے بھیجے ہوئے رسول واقعی حق ہی لے کر آئے تھے“۔ اُس وقت نِدا آئے گی کہ”یہ جنّت جس کے تم وارث بنائے گئے ہو تمہیں اُن کے اعمال کے بدلے میں ملی ہے جو تم کرتے رہے تھے“۔ ');
INSERT INTO "ur_maududi" VALUES(7,44,'7:44','پھر یہ جنت کے لوگ دوزخ والوں سے پکار کر کہیں گے، "ہم نے اُن سارے وعدوں کو ٹھیک پا لیا جو ہمارے رب نے ہم سے کیے تھے، کیا تم نے بھی ان وعدوں کو ٹھیک پایا جو تمہارے رب نے کیے تھے؟" وہ جواب دیں گے "ہاں" تب ایک پکارنے والا ان کے درمیان پکارے گا کہ "خدا کی لعنت اُن ظالموں پر');
INSERT INTO "ur_maududi" VALUES(7,45,'7:45','جو اللہ کے راستے سے لوگوں کو روکتے اور اسے ٹیڑھا کرنا چاہتے تھے اور آخرت کے منکر تھے"');
INSERT INTO "ur_maududi" VALUES(7,46,'7:46','ان دونوں گروہوں کے درمیان ایک اوٹ حائل ہوگی جس کی بلندیوں (اَعراف) پر کچھ اور لوگ ہوں گے۔ یہ ہر ایک کو اس کے قیافہ سے پہچانیں گے اور جنّت والوں سے پکار کر کہیں گے کہ ”سلامتی ہو تم پر“ یہ لوگ جنّت میں داخل تو نہیں ہوئے مگر اس کے امیدوار ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(7,47,'7:47','اور جب ان کی نگاہیں دوزخ والوں کی طرف پھریں گی تو کہیں گے، "اے رب! ہمیں اِن ظالم لوگوں میں شامل نہ کیجیو"');
INSERT INTO "ur_maududi" VALUES(7,48,'7:48','پھر یہ اعراف کے لوگ دوزخ کی چند بڑی بڑی شخصیتوں کو ان کی علامتوں سے پہچان کر پکاریں گے کہ "دیکھ لیا تم نے، آج نہ تمہارے جتھے تمہارے کسی کام آئے اور نہ وہ ساز و سامان جن کو تم بڑی چیز سمجھتے تھے');
INSERT INTO "ur_maududi" VALUES(7,49,'7:49','اور کیا یہ اہل جنت وہی لوگ نہیں ہیں جن کے متعلق تم قسمیں کھا کھا کر کہتے تھے کہ اِن کو تو خدا پنی رحمت میں سے کچھ بھی نہ دے گا؟ آج انہی سے کہا گیا کہ داخل ہو جاؤ جنت میں، تمہارے لیے نہ خوف ہے نہ رنج"');
INSERT INTO "ur_maududi" VALUES(7,50,'7:50','اور دوزخ کے لوگ جنت والوں کو پکاریں گے کہ کچھ تھوڑا سا پانی ہم پر ڈال دو یا جو رزق اللہ نے تمہیں دیا ہے اُسی میں سے کچھ پھینک دو وہ جواب دیں گے کہ "اللہ نے یہ دونوں چیزیں اُن منکرین حق پر حرام کر دی ہیں');
INSERT INTO "ur_maududi" VALUES(7,51,'7:51','جنہوں نے اپنے دین کو کھیل اور تفریح بنا لیا تھا اور جنہیں دنیا کی زندگی نے فریب میں مبتلا کر رکھا تھا۔ اللہ فرماتا ہے کہ آج ہم بھی انہیں اسی طرح بُھلا دیں گے جس طرح وہ اِس دن کی ملاقات بھُولے رہے اور ہماری آیتوں کا انکار کرتے رہے۔“ ');
INSERT INTO "ur_maududi" VALUES(7,52,'7:52','ہم اِن لوگوں کے پاس ایک ایسی کتاب لے آئے ہیں جس کو ہم نے علم کی بنا پر مفصّل بنایا ہے اور جو ایمان لانے والوں کے لیے ہدایت اور رحمت ہے۔ ');
INSERT INTO "ur_maududi" VALUES(7,53,'7:53','اب کیا یہ لوگ اِس کے سوا کسی اور بات کے منتظر ہیں کہ وہ انجام سامنے آجائے جس کی یہ کتاب خبر دے رہی ہے؟ جس روز وہ انجام سامنے آگیا تو وہی لوگ جنہوں نے پہلے اسے نظر انداز کر دیا تھا کہیں گے ”واقعی ہمارے ربّ کے رسول حق لے کر آئے تھے، پھر کیا اب ہمیں کچھ سفارشی ملیں گے جو ہمارے حق میں سفارش کریں؟ یا ہمیں دوبارہ واپس ہی بھیج دیا جائے تاکہ جو کچھ ہم پہلے کرتے تھے اس کے بجائے اب دوسرے طریقے پر کام کر کے دکھائیں “۔۔۔۔۔انہوں نے اپنے آپ کو خسارے میں ڈال دیا اور وہ سارے جھوٹ جو انہوں نے تصنیف کر رکھے تھے آج ان سے گم ہوگئے');
INSERT INTO "ur_maududi" VALUES(7,54,'7:54','درحقیقت تمہارا ربّ اللہ ہی ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا ، پھر اپنے تختِ سلطنت پر جلوہ فرما ہوا۔ جو رات کو دن پر ڈھا نک دیتا ہے اور پھر دن رات کے پیچھے دوڑا چلا آتا ہے۔ جس نے سُورج اور چاند اور تارے پیدا کیے۔ سب اس کے فرمان کے تابع ہیں۔ خبر دار رہو! اُسی کی خلق ہے اور اسی کا امر ہے ۔ بڑا برکت ہے اللہ ، سارے جہانوں کا مالک و پروردگار');
INSERT INTO "ur_maududi" VALUES(7,55,'7:55','ا پنے رب کو پکارو گڑگڑاتے ہوئے اور چپکے چپکے، یقیناً و ہ حد سے گزرنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(7,56,'7:56','زمین میں فساد برپا نہ کرو جبکہ اس کی اصلاح ہو چکی ہے اور خدا ہی کو پکارو خوف کے ساتھ اور طمع کے ساتھ، یقیناً اللہ کی رحمت نیک کردار لوگوں سے قریب ہے');
INSERT INTO "ur_maududi" VALUES(7,57,'7:57','اور وہ اللہ ہی ہے جو ہواؤں کو اپنی رحمت کے آگے آگے خوشخبری لیے ہوئے بھیجتا ہے، پھر جب وہ پانی سے لدے ہوئے بادل اٹھا لیتی ہیں تو انہیں کسی مردہ سر زمین کی طرف حرکت دیتا ہے، اور وہاں مینہ برسا کر (اُسی مری ہوئی زمین سے) طرح طرح کے پھل نکال لاتا ہے دیکھو، اس طرح ہم مُردوں کو حالت موت سے نکالتے ہیں، شاید کہ تم اس مشاہدے سے سبق لو');
INSERT INTO "ur_maududi" VALUES(7,58,'7:58','جو زمین اچھی ہوتی ہے وہ اپنے ربّ کے حکم سے خوب پھل پھول لاتی ہے اور جوزمین خراب ہوتی ہے اس سے ناقص پیداوار کے سوا کچھ نہیں نکلتا۔ اس طرح ہم نشانیوں کو بار بار پیش کرتے ہیں اُن لوگوں کے لیے جو شکر گزار ہونے والے ہیں');
INSERT INTO "ur_maududi" VALUES(7,59,'7:59','ہم نے نوح ؑ کو اُس کی قوم کی طرف بھیجا۔ اس نے کہا”اے برادرانِ وقم، اللہ کی بندگی کرو، اُسکے سوا تمہارا کوئی خدا نہیں ہے۔ میں تمہارے حق میں ایک ہولناک دن کے عذاب سے ڈرتا ہوں۔“');
INSERT INTO "ur_maududi" VALUES(7,60,'7:60','اس کی قوم کے سرداروں نے جواب دیا "ہم کو تو یہ نظر آتا ہے کہ تم صریح گمراہی میں مبتلا ہو"');
INSERT INTO "ur_maududi" VALUES(7,61,'7:61','نوحؑ نے کہا "اے برادران قوم، میں کسی گمراہی میں نہیں پڑا ہو ں بلکہ، میں رب العالمین کا رسول ہوں');
INSERT INTO "ur_maududi" VALUES(7,62,'7:62','تمہیں اپنے رب کے پیغامات پہنچاتا ہوں، تمہارا خیر خواہ ہوں اور مجھے اللہ کی طرف سے وہ کچھ معلوم ہے جو تمھیں معلوم نہیں ہے');
INSERT INTO "ur_maududi" VALUES(7,63,'7:63','کیا تمہیں اس بات پرتعجب ہوا کہ تمہارے پاس خود تمہاری اپنی قوم کے ایک آدمی کے ذریعہ سے تمہارے ربّ کی یاددہانی آئی تاکہ تمہیں خبر دار کرے اور تم غلط روی سے بچ جاوٴ اور تم پر رحم کیا جائے؟ “');
INSERT INTO "ur_maududi" VALUES(7,64,'7:64','مگر انہوں نے اس کو جُھٹلادیا۔ آخرِکار ہم نے اسے اور اس کے ساتھیوں کو ایک کشتی میں نجات دی اور اُن لوگوں کو ڈبو دیا جنہوں نے ہماری آیات کو جُھٹلایا تھا، یقیناًوہ اندھے لوگ ہیں');
INSERT INTO "ur_maududi" VALUES(7,65,'7:65','اور عاد کی طرف ہم نے ان کے بھائی ہود ؑ کو بھیجا۔ اس نے کہا ”اے برادرانِ قوم، اللہ کی بندگی کرو، اُس کے سِوا تمہارا کوئی خدا نہیں ہے۔ پھر کیا تم غلط روی سے پرہیز نہ کروگے؟“');
INSERT INTO "ur_maududi" VALUES(7,66,'7:66','اس کی قوم کے سرداروں نے، جو اس کی بات ماننے سے انکار کر رہے تھے، جواب میں کہا "ہم تو تمہیں بے عقلی میں مبتلا سمجھتے ہیں اور ہمیں گمان ہے کہ تم جھوٹے ہو"');
INSERT INTO "ur_maududi" VALUES(7,67,'7:67','اس نے کہا "اے برادران قوم، میں بے عقلی میں مبتلا نہیں ہوں بلکہ میں رب العالمین کا رسول ہوں');
INSERT INTO "ur_maududi" VALUES(7,68,'7:68','تم کو اپنے رب کے پیغامات پہنچاتا ہوں، اور تمہارا ایسا خیر خواہ ہوں جس پر بھروسہ کیا جاسکتا ہے');
INSERT INTO "ur_maududi" VALUES(7,69,'7:69','کیا تمہیں اس بات پر تعجب ہوا کہ تمہارے پاس خود تمہاری اپنی قوم کے ایک آدمی کے ذریعہ سے تمہارے ربّ کی یاد دہانی آئی تا کہ وہ تمہیں خبردار کرے؟ بھُول نہ جاوٴ کہ تمہارے ربّ نے نوح ؑ کی قوم کے بعد تم کو اُس کا جانشین بنایا اور تمہیں خوب تنومند کیا، پس اللہ کی قدرت کے کرشموں کو یاد رکھو، اُمید ہے کہ فلاح پاوٴ گے۔“');
INSERT INTO "ur_maududi" VALUES(7,70,'7:70','انہوں نے جواب دیا ”کیا تُو ہمارے پاس اِس لیے آیا ہے کہ ہم اکیلے اللہ ہی کی عبادت کریں اور اُنہیں چھوڑ دیں جن کی عبادت ہمارے باپ دادا کرتے آئے ہیں؟ اچھا تو لے آ وہ عذاب جس کی تُو ہمیں دھمکی دیتا ہے اگر تُو سچا ہے۔“');
INSERT INTO "ur_maududi" VALUES(7,71,'7:71','اس نے کہا ”تمہارے ربّ کی پھٹکار تم پر پڑ گئی اور اس کا غضب ٹُوٹ پڑا۔ کیا تم مجھ سے اُن ناموں پر جھگڑتے ہو جو تم نے اور تمہارے باپ دادا نے رکھ لیے ہیں ، جن کے لیے اللہ نے کوئی سند نازل نہیں کی ہے ؟ اچھا تو تم بھی انتظار کرو اور میں بھی تمہارے ساتھ انتظار کرتا ہوں۔“');
INSERT INTO "ur_maududi" VALUES(7,72,'7:72','آخرِکار ہم نے اپنی مہربانی سے ہود ؑ اور اس کے ساتھیوں کو بچا لیا اور اُن لوگوں کی جڑ کاٹ دی جو ہماری آیات کو جُھٹلا چکے تھے اور ایمان لانے والے نہ تھے۔ ');
INSERT INTO "ur_maududi" VALUES(7,73,'7:73','اور ثمود کی طرف ہم نے اُن کے بھائی صالح کو بھیجا۔ اس نے کہا ”اے برادرانِ قوم، اللہ کی بندگی کرو، اس کے سِوا تمہارا کوئی خدا نہیں ہے۔ تمہارے پاس تمہارے ربّ کی کُھلی دلیل آگئی ہے۔ یہ اللہ کی اُونٹنی تمہارے لیے ایک نشانی کے طور پر ہے ، لہٰذا اِسے چھوڑو کہ خدا کی زمین میں چرتی پھرے۔ اس کو کسی بُرے ارادے سے ہاتھ نہ لگانا ورنہ ایک دردناک عذاب تمہیں آلے گا');
INSERT INTO "ur_maududi" VALUES(7,74,'7:74','یا د کرو وہ وقت جب اللہ نے قومِ عاد کے بعد تمہیں اس کا جانشین بنایا اور تم کو زمین میں یہ منزلت بخشی کہ آج تم اُس کے ہموار میدانوں میں عالی شان محل بناتے اور اس کے پہاڑوں کو مکانات کی شکل میں تراشتے ہو۔ پس اس کی قدرت کے کرشموں سے غافل نہ ہوجاوٴ اور زمین میں فساد برپا نہ کرو۔“ ');
INSERT INTO "ur_maududi" VALUES(7,75,'7:75','اُس کی قوم کے سرداروں نے جو بڑے بنے ہوئے تھے، کمزور طبقہ کے اُن لوگوں سے جو ایمان لے آئے تھے کہا "کیا تم واقعی یہ جانتے ہو کہ صالحؑ اپنے رب کا پیغمبر ہے؟" انہوں نے جواب دیا "بے شک جس پیغام کے ساتھ وہ بھیجا گیا ہے اُسے ہم مانتے ہیں"');
INSERT INTO "ur_maududi" VALUES(7,76,'7:76','اُن برائی کے مدعیوں نے کہا جس چیز کو تم نے مانا ہے ہم اس کے منکر ہیں"');
INSERT INTO "ur_maududi" VALUES(7,77,'7:77','پھر انہوں نے اس اُونٹنی کو مار ڈالا اور پُورے تمّرد کے ساتھ اپنے ربّ کے حکم کی خلاف ورزی کر گزرے ، اور صالح سے کہہ دیا کہ”لے آوہ عذاب جس کی تُو ہمیں دھمکی دیتا ہے اگر تُو واقعی پیغمبروں میں سے ہے۔“');
INSERT INTO "ur_maududi" VALUES(7,78,'7:78','آخرِ کار ایک دہلا دینے والی آفت نے اُنہیں آلیااور وہ اپنے گھروں میں اوندھے پڑے کے پڑے رہ گئے');
INSERT INTO "ur_maududi" VALUES(7,79,'7:79','اور صالحؑ یہ کہتا ہوا ان کی بستیوں سے نکل گیا کہ "اے میری قوم، میں نے اپنے رب کا پیغام تجھے پہنچا دیا اور میں نے تیری بہت خیر خواہی کی، مگر میں کیا کروں کہ تجھے اپنے خیر خواہ پسند ہی نہیں ہیں"');
INSERT INTO "ur_maududi" VALUES(7,80,'7:80','اور لوطؑ کو ہم نے پیغمبر بنا کر بھیجا ، پھر یاد کرو جب اُس نے اپنی قوم سے کہا ”کیا تم ایسے بے حیا ہوگئے ہو کہ وہ فحش کام کرتے ہو جو تم سے پہلے دنیا میں کسی نے نہیں کیا؟');
INSERT INTO "ur_maududi" VALUES(7,81,'7:81','تم عورتوں کو چھوڑ کر مردوں سے اپنی خواہش پوری کرتے ہو ، حقیقت یہ ہے کہ تم بالکل ہی حد سے گزر جانے والے لوگ ہو۔“');
INSERT INTO "ur_maududi" VALUES(7,82,'7:82','مگر اس کی قوم کا جواب اس کے سوا کچھ نہ تھا کہ ”نکالو اِن لوگوں کو اپنی بستیوں سے ، بڑے پاکباز بنتے ہیں یہ۔ “');
INSERT INTO "ur_maududi" VALUES(7,83,'7:83','آخرِکار ہم نے لوط ؑ اور اس کے گھر والوں کو۔۔۔۔ بجز اس کی بیوی کے جو پیچھے رہ جانے والوں میں تھی۔۔۔۔ ');
INSERT INTO "ur_maududi" VALUES(7,84,'7:84','بچا کر نکال دیا اور اس قوم پر برسائی ایک بارش ، پھر دیکھو کہ اُن مجرموں کا کیا انجام ہوا۔ ');
INSERT INTO "ur_maududi" VALUES(7,85,'7:85','اور مَدیَن والوں کی طرف ہم نے ان کے بھائی شعیب ؑ کو بھیجا۔ اس نے کہا ”اے برادرانِ قوم، اللہ کی بندگی، اُس کے سوا تمہارا کوئی خدا نہیں ہے۔ تمہارے پاس تمہارے ربّ کی صاف رہنمائی آگئی ہے، لہٰذا وزن اور پیمانے پُورے کرو، لوگوں کو اُن کی چیزوں میں گھاٹا نہ دو ، اور زمین میں فساد برپا نہ کرو جب کہ اس کی اصلاح ہو چکی ہے ، اسی میں تمہاری بھلائی ہے اگر تم واقعی مومن ہو ');
INSERT INTO "ur_maududi" VALUES(7,86,'7:86','اور (زندگی کے) ہر راستے پر رہزن بن کر نہ بیٹھ جاؤ کہ لوگوں کو خوف زدہ کرنے اور ایمان لانے والوں کو خدا کے راستے سے روکنے لگو اور سیدھی راہ کو ٹیڑھا کرنے کے درپے ہو جاؤ یاد کرو وہ زمانہ جبکہ تم تھوڑے تھے پھر اللہ نے تمہیں بہت کر دیا، اور آنکھیں کھول کر دیکھو کہ دنیا میں مفسدوں کا کیا انجام ہوا ہے');
INSERT INTO "ur_maududi" VALUES(7,87,'7:87','اگر تم میں سے ایک گروہ اس تعلیم پر جس کے ساتھ میں بھیجا گیا ہوں، ایمان لاتا ہے اور دوسرا ایمان نہیں لاتا، تو صبر کے ساتھ دیکھتے رہو یہاں تک کہ اللہ ہمارے درمیان فیصلہ کر دے، اور وہی سب سے بہتر فیصلہ کرنے والا ہے"');
INSERT INTO "ur_maududi" VALUES(7,88,'7:88','اس کی قوم کے سرداروں نے، جو اپنی بڑائی کے گھمنڈ میں مبتلا تھے، ا س سے کہا کہ "اے شعیبؑ، ہم تجھے اور اُن لوگوں کو جو تیرے ساتھ ایمان لائے ہیں اپنی بستی سے نکال دیں گے ورنہ تم لوگوں کو ہماری ملت میں واپس آنا ہوگا" شعیبؑ نے جواب دیا "کیا زبردستی ہمیں پھیرا جائے گا خواہ ہم راضی نہ ہوں؟');
INSERT INTO "ur_maududi" VALUES(7,89,'7:89','ہم اللہ پر جھوٹ گھڑنے والے ہوں گے اگر تمہاری مِلّت میں پلٹ آئیں جبکہ اللہ ہمیں اس سے نجات دے چکا ہے۔ ہمارے لیے تو اس کی طرف پلٹنا اب کسی طرح ممکن نہیں اِلّا یہ کہ خدا ہمارا ربّ ہی ایسا چاہے ،ہمارے ربّ کا علم ہر چیز پر حاوی ہے، اُسی پر ہم نے اعتماد کر لیا، اے ربّ! ہمارےاور ہماری قوم کے درمیان ٹھیک ٹھیک فیصلہ کر دے اور تُو بہترین فیصلہ کرنے والا ہے۔“');
INSERT INTO "ur_maududi" VALUES(7,90,'7:90','اس کی قوم کے سرداروں نے ، جو اس کی بات ماننےسے انکار کر چکے تھے، آپس میں کہا”اگر تم نے شعیب ؑ کی پیروی قبول کرلی تو برباد ہو جاوٴ گے۔“');
INSERT INTO "ur_maududi" VALUES(7,91,'7:91','مگر ہوا یہ کہ ایک دہلا دینے والی آفت نے اُن کو آ لیا اور وہ اپنے گھروں میں اوندھے پڑے کے پڑے رہ گئے');
INSERT INTO "ur_maududi" VALUES(7,92,'7:92','جن لوگوں نے شعیب ؑ کو جُھٹلایا وہ ایسے مِٹے کہ گویا کبھی ان گھروں میں بسے ہی نہ تھے۔ شعیب ؑ کے جُھٹلانے والے ہی آخرِکار برباد ہو کر رہے۔ ');
INSERT INTO "ur_maududi" VALUES(7,93,'7:93','اور شعیبؑ یہ کہہ کر ان کی بستیوں سے نِکل گیا کہ ”اے برادرانِ قوم، میں نے اپنے ربّ کے پیغامات تمہیں پہنچا دیے اور تمہاری خیر خواہی کا حق ادا کر دیا۔ اب میں اُس قوم پر کیسے افسوس کروں جو قبولِ حق سے انکار کرتی ہے۔ “');
INSERT INTO "ur_maududi" VALUES(7,94,'7:94','کبھی ایسا نہیں ہوا کہ ہم نے کسی بستی میں نبی بھیجا ہو اور اُس بستی کے لوگوں کو پہلے تنگی اور سختی میں مبتلا نہ کیا ہو اس خیال سے کہ شاید وہ عاجزی پر اتر آئیں');
INSERT INTO "ur_maududi" VALUES(7,95,'7:95','پھر ہم نے اُن کی بدحالی کو خوشحالی سے بدل دیا یہاں تک کہ وہ خُوب پھلے پھُولے اور کہنے لگے کہ ”ہمارے اسلاف پر بھی اچھے اور بُرے دن آتے ہی رہے ہیں۔“آخرِ کار ہم نے اُنہیں اچانک پکڑ لیا اور اُنہیں خبر تک نہ ہوئی۔ ');
INSERT INTO "ur_maududi" VALUES(7,96,'7:96','اگر بستیوں کے لوگ ایمان لاتے اور تقویٰ کی روش اختیار کرتے تو ہم ان پر آسمان اور زمین سے برکتوں کے دروازے کھو ل دیتے، مگر اُنہوں نے تو جھٹلایا، لہٰذا ہم نے اُس بری کمائی کے حساب میں انہیں پکڑ لیا جو وہ سمیٹ رہے تھے');
INSERT INTO "ur_maududi" VALUES(7,97,'7:97','پھر کیا بستیوں کے لوگ اب اس سے بے خوف ہو گئے ہیں کہ ہماری گرفت کبھی اچانک اُن پر رات کے وقت نہ آ جائے گی جب کہ وہ سوتے پڑے ہوں؟');
INSERT INTO "ur_maududi" VALUES(7,98,'7:98','یا انہیں اطمینان ہو گیا ہے کہ ہمارا مضبوط ہاتھ کبھی یکایک ان پر دن کے وقت نہ پڑے گا جب کہ وہ کھیل رہے ہوں؟');
INSERT INTO "ur_maududi" VALUES(7,99,'7:99','کیا یہ لوگ اللہ کی چال سے بے خوف ہیں ؟ حالانکہ اللہ کی چال سے وہی قوم بے خوف ہوتی ہے جو تباہ ہونے والی ہو');
INSERT INTO "ur_maududi" VALUES(7,100,'7:100','اور کیا اُن لوگوں کو جو سابق اہلِ زمین کے بعد زمین کے وارث ہوتے ہیں، اِس امر ِواقعی نے کچھ سبق نہیں دیا کہ اگر ہم چاہیں تو ان کے قصوروں پر انہیں پکڑ سکتے ہیں؟ ( مگر وہ سبق آموز حقائق سے تغافل برتتے رہے) اور ہم ان کےدِلوں پر مُہر لگا دیتے ہیں، پھر وہ کچھ نہیں سُنتے۔ ');
INSERT INTO "ur_maududi" VALUES(7,101,'7:101','یہ قومیں جن کے قصے ہم تمہیں سُنا رہے ہیں (تمہارے سامنے مثال میں موجود ہیں) ان کے رسُول ان کے پاس کھُلی کھُلی نشانیاں لے کر آئے، مگر جس چیز کو وہ ایک دفعہ جھُٹلا چکے تھے پھر اُسے وہ ماننے والے نہ تھے۔ دیکھو اس طرح ہم منکرینِ حق کے دلوں پر مُہر لگا دیتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(7,102,'7:102','ہم ان میں سے اکثر میں کوئی پاس ِ عہد نہ پایا بلکہ اکثر کو فاسق ہی پایا۔ ');
INSERT INTO "ur_maududi" VALUES(7,103,'7:103','پھر اُن قوموں کے بعد (جن کا ذکر اُوپر کیا گیا) ہم نے موسیٰ کو اپنی نشانیوں کے ساتھ فرعون اور اس کی قوم کے سرداروں کے پاس بھیجا مگر انہوں نے بھی ہماری نشانیوں کے ساتھ ظلم کیا، پس دیکھو کہ ان مفسدوں کا کیا انجام ہوا');
INSERT INTO "ur_maududi" VALUES(7,104,'7:104','موسیٰ نے کہا”اے فرعون ، میں کائنات کے مالک کی طرف سے بھیجا گیا ہوں');
INSERT INTO "ur_maududi" VALUES(7,105,'7:105','میرا منصب یہی ہے کہ اللہ کا نام لے کر کوئی بات حق کے سوا نہ کہوں، میں تم لوگوں کے پاس تمہارے ربّ کی طرف سے صریح دلیلِ ماموریّت لے کر آیا ہوں، لہٰذا تُو بنی اسرائیل کو میرے ساتھ بھیج دے۔“ ');
INSERT INTO "ur_maududi" VALUES(7,106,'7:106','فرعون نے کہا "اگر تو کوئی نشانی لایا ہے اور اپنے دعوے میں سچا ہے تو اسے پیش کر"');
INSERT INTO "ur_maududi" VALUES(7,107,'7:107','موسیٰؑ نے اپنا عصا پھینکا اور یکا یک وہ ایک جیتا جاگتا اژدہا تھا');
INSERT INTO "ur_maududi" VALUES(7,108,'7:108','اس نے اپنی جیب سے ہاتھ نکالا اور سب دیکھنے والوں کے سامنے وہ چمک رہا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(7,109,'7:109','ا س پر فرعون کی قوم کے سرداروں نے آپس میں کہا کہ "یقیناً یہ شخص بڑا ماہر جادو گر ہے');
INSERT INTO "ur_maududi" VALUES(7,110,'7:110','تمہیں تمہاری زمین سے بے دخل کرنا چاہتا ہے ، اب کہو کیا کہتے ہو؟“');
INSERT INTO "ur_maududi" VALUES(7,111,'7:111','پھر اُن سب نے فرعون کو مشورہ دیا کہ اسے اور اس کے بھائی کو انتظار میں رکھیے اور تمام شہروں میں ہرکارے بھیج دیجیے');
INSERT INTO "ur_maududi" VALUES(7,112,'7:112','کہ ہر ماہرِ فن جادوگر کو آپ کےپاس لے آئیں۔ ');
INSERT INTO "ur_maududi" VALUES(7,113,'7:113','چنانچہ جادوگر فرعون کے پاس آ گئے اُنہوں نے "اگر ہم غالب رہے تو ہمیں اس کا صلہ تو ضرور ملے گا؟"');
INSERT INTO "ur_maududi" VALUES(7,114,'7:114','فرعون نے جواب دیا "ہاں، اور تم مقرب بارگاہ ہو گے"');
INSERT INTO "ur_maududi" VALUES(7,115,'7:115','پھر اُنہوں نے موسیٰؑ سے کہا "تم پھینکتے ہو یا ہم پھینکیں؟"');
INSERT INTO "ur_maududi" VALUES(7,116,'7:116','موسیٰؑ نے جواب دیا "تم ہی پھینکو" انہوں نے جو اپنے آنچھر پھینکے تو نگاہوں کو مسحور اور دلوں کو خوف زدہ کر دیا اور بڑا ہی زبردست جادو بنا لائے');
INSERT INTO "ur_maududi" VALUES(7,117,'7:117','ہم نے موسیٰ کو اشارہ کیا کہ پھینک اپنا عصا۔ اس کا پھینکنا تھا کہ آن کی آن میں وہ ان کے اس جھُوٹے طلسم کو نگلتا چلا گیا۔ ');
INSERT INTO "ur_maududi" VALUES(7,118,'7:118','اس طرح جو حق تھا وہ حق ثابت ہوا اور جو کچھ اُنہوں نے بنا رکھا تھا وہ باطل ہو کر رہ گیا');
INSERT INTO "ur_maududi" VALUES(7,119,'7:119','فرعون اور اس کے ساتھی میدان مقابلہ میں مغلوب ہوئے اور (فتح مند ہونے کے بجائے) الٹے ذلیل ہو گئے');
INSERT INTO "ur_maududi" VALUES(7,120,'7:120','اور جادوگروں کا حال یہ ہوا کہ گویا کسی چیز نے اندر سے انہیں سجدے میں گرا دیا');
INSERT INTO "ur_maududi" VALUES(7,121,'7:121','کہنے لگے "ہم نے مان لیا رب العالمین کو');
INSERT INTO "ur_maududi" VALUES(7,122,'7:122','اُس ربّ کو جسے موسیٰ اور ہارون مانتے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(7,123,'7:123','فرعون نے کہا "تم اس پر ایمان لے آئے قبل اس کے کہ میں تمہیں اجازت دوں؟ یقیناً یہ کوئی خفیہ ساز ش تھی جو تم لوگوں نے اس دارالسلطنت میں کی تاکہ اس کے مالکوں کو اقتدار سے بے دخل کر دو اچھا توا س کا نتیجہ اب تمہیں معلوم ہوا جاتا ہے');
INSERT INTO "ur_maududi" VALUES(7,124,'7:124','میں تمہارے ہاتھ پاؤں مخالف سمتوں سے کٹوا دوں گا اور اس کے بعد تم سب کو سولی پر چڑھاؤں گا"');
INSERT INTO "ur_maududi" VALUES(7,125,'7:125','انہوں نے جواب دیا "بہر حال ہمیں پلٹنا اپنے رب ہی کی طرف ہے');
INSERT INTO "ur_maududi" VALUES(7,126,'7:126','تُو جس بات پر ہم سے انتقام لینا چاہتا ہے وہ اِس کے سوا کچھ نہیں کہ ہمارے ربّ کی نشانیاں جب ہمارے سامنے آگئیں تو ہم نے انہیں مان لیا۔ اے ربّ ، ہم پر صبر کا فیضان کر اور ہمیں دنیا سے اُٹھا اِس حال میں کہ ہم تیرے فرمانبردار ہوں۔ “');
INSERT INTO "ur_maududi" VALUES(7,127,'7:127','فرعون سے اُس کی قوم کے سرداروں نے کہا”کیا تُو موسیٰ اور اس کی قوم کو یونہی چھوڑ دے گا کہ ملک میں فساد پھیلائیں اور وہ تیری اور تیرے معبُودوں کی بندگی چھوڑ بیٹھے؟“ فرعون نے جواب دیا”میں اُن کے بیٹوں کو قتل کراوٴ ں گا اور اُن کی عورتوں کو جیتا رہنے دوں گا۔ ہمارے اقتدار کی گرفت ان پر مضبوط ہے');
INSERT INTO "ur_maududi" VALUES(7,128,'7:128','موسیٰؑ نے اپنی قوم سے کہا "اللہ سے مدد مانگو اور صبر کرو، زمین اللہ کی ہے، اپنے بندوں میں سے جس کو چاہتا ہے اس کا وارث بنا دیتا ہے، اور آخری کامیابی انہی کے لیے جو اس سے ڈرتے ہوئے کام کریں"');
INSERT INTO "ur_maududi" VALUES(7,129,'7:129','اس کی قوم کے لوگوں نے کہا "تیرے آنے سے پہلے بھی ہم ستائے جاتے تھے اور اب تیرے آنے پر بھی ستائے جا رہے ہیں" اس نے جواب دیا "قریب ہے وہ وقت کہ تمہارا رب تمہارے دشمن کو ہلاک کر دے اور تم کو زمین میں خلیفہ بنائے، پھر دیکھے کہ تم کیسے عمل کرتے ہو"');
INSERT INTO "ur_maududi" VALUES(7,130,'7:130','ہم نے فرعون کے لوگوں کو کئی سال تک قحط اور پیداوار کی کمی میں مبتلا رکھا کہ شاید ان کو ہوش آئے');
INSERT INTO "ur_maududi" VALUES(7,131,'7:131','مگر اُن کا حال یہ تھا کہ جب اچھا زمانہ آتا تو کہتے کہ ہم اِسی کے مستحق ہیں، اور جب برا زمانہ آتا تو موسیٰؑ اور اس کے ساتھیوں کو اپنے لیے فال بد ٹھیراتے، حالانکہ در حقیقت ان کی فال بد تو اللہ کے پاس تھی، مگر ان میں سے اکثر بے علم تھے');
INSERT INTO "ur_maududi" VALUES(7,132,'7:132','انہوں نے موسیٰ سے کہا”تُو ہمیں مسحور کرنے کے لیے خواہ کوئی نشانی لے آئے، ہم تو تیری بات ماننے والے نہیں ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(7,133,'7:133','آخرِ کارہم نے ان پر طوفان بھیجا ، ٹِڈی دل چھوڑے، سُرسُریاں پھیلائیں ، مینڈک نکالے، اور خون برسایا، یہ سب نشانیاں الگ الگ کر کے دکھائیں، مگر وہ سرکشی کیے چلے گئےاور بڑے ہی مجرم لوگ تھے');
INSERT INTO "ur_maududi" VALUES(7,134,'7:134','جب کبھی اُن پر بلا نازل ہو جاتی تو کہتے "اے موسیٰؑ، تجھے اپنے رب کی طرف سے جو منصب حاصل ہے اس کی بنا پر ہمارے حق میں دعا کر، اگر اب کے تو ہم پر سے یہ بلا ٹلوا دے تو ہم تیری بات مان لیں گے اور بنی اسرائیل کو تیرے ساتھ بھیج دیں گے"');
INSERT INTO "ur_maududi" VALUES(7,135,'7:135','مگر جب ہم ان پر سے اپنا عذاب ایک وقت مقرر تک کے لیے، جس کو وہ بہرحال پہنچنے والے تھے، ہٹا لیتے تو وہ یکلخت اپنے عہد سے پھر جاتے');
INSERT INTO "ur_maududi" VALUES(7,136,'7:136','تب ہم نے اُن سے انتقام لیا اور انہیں سمندر میں غرق کر دیا کیونکہ انہوں نے ہماری نشانیوں کو جھٹلایا تھا اور اُن سے بے پروا ہو گئے تھے');
INSERT INTO "ur_maududi" VALUES(7,137,'7:137','اور اُن کی جگہ ہم نے اُن لوگوں کو جو کمزور بنا کر رکھے گئے تھے، اُس سرزمین کے مشرق و مغرب کاوارث بنا دیا جسے ہم نے برکتوں سے مالامال کیا تھا۔ اس طرح بنی اسرائیل کے حق میں تیرے ربّ کا وعدہٴِ خیر پورا ہوکیونکہ اُنہوں نے صبر سے کام لیا تھا اور فرعون اور اُ س کی قوم کا وہ سب کچھ برباد کر دیا گیا جو وہ بناتے اور چڑھاتے تھے');
INSERT INTO "ur_maududi" VALUES(7,138,'7:138','بنی اسرائیل کو ہم نے سمندر سے گزار دیا، پھر وہ چلے اور راستے میں ایک ایسی قوم پر اُن کا گزر ہوا جو اپنے چند بُتوں کی گرویدہ بنی ہوئی تھی۔ کہنے لگے، ”اے موسیٰ،ہمارے لیے بھی کوئی ایسا معبود بنا دے جیسے اِن لوگوں کے معبود ہیں۔ “ موسیٰ نے کہا”تم لوگ بڑی نادانی کی باتیں کرتے ہو');
INSERT INTO "ur_maududi" VALUES(7,139,'7:139','یہ لوگ جس طریقہ کی پیروی کر رہے ہیں وہ تو برباد ہونے والا ہے اور جو عمل وہ کر رہے ہیں وہ سراسر باطل ہے"');
INSERT INTO "ur_maududi" VALUES(7,140,'7:140','پھر موسیٰؑ نے کہا "کیا میں اللہ کے سوا کوئی اور معبود تمہارے لیے تلاش کروں؟ حالانکہ وہ اللہ ہی ہے جس نے تمہیں دنیا بھر کی قوموں پر فضیلت بخشی ہے');
INSERT INTO "ur_maududi" VALUES(7,141,'7:141','اور (اللہ فرماتا ہے) وہ وقت یاد کرو جب ہم نے فرعون والوں سے تمہیں نجات دی جن کا حال یہ تھا کہ تمہیں سخت عذاب میں مبتلا رکھتے تھے، تمہارے بیٹوں کو قتل کرتے اور تمہاری عورتوں کو زندہ رہنے دیتے تھے اور اس میں تمہارے رب کی طرف سے تمہاری بڑی آزمائش تھی');
INSERT INTO "ur_maududi" VALUES(7,142,'7:142','ہم نے موسیٰ کو تیس شب و روز کے لیے (کوہِ سیناپر) طلب کیا اور بعد میں دس( ) دن کا اور اضافہ کر دیا ، اِس طرح اُس کے ربّ کی مقرر کردہ مُدّت پُورے چالیس (۴۰) دن ہوگئی۔ موسیٰ نے چلتے ہوئے اپنے بھائی سے کہا کہ ”میرے پیچھے تم میرے قوم میں میری جانشینی کرنا اور ٹھیک کام کرتے رہنا اور بگاڑ پیدا کرنے والوں کے طریقے پر نہ چلنا۔ “');
INSERT INTO "ur_maududi" VALUES(7,143,'7:143','جب وہ ہمارے مقرر کیے ہوئے وقت پر پہنچا اور اس کے رب نے اس سے کلام کیا تو اس نے التجا کی کہ "اے رب، مجھے یارائے نظر دے کہ میں تجھے دیکھوں" فرمایا "تو مجھے نہیں دیکھ سکتا ہاں ذرا سامنے کے پہاڑ کی طرف دیکھ، اگر وہ اپنی جگہ قائم رہ جائے تو البتہ تو مجھے دیکھ سکے گا" چنانچہ اس کے رب نے جب پہاڑ پر تجلی کی تو اسے ریزہ ریزہ کر دیا اور موسیٰؑ غش کھا کر گر پڑا جب ہوش آیا تو بولا "پاک ہے تیری ذات، میں تیرے حضور توبہ کرتا ہوں اور سب سے پہلا ایمان لانے والا میں ہوں"');
INSERT INTO "ur_maududi" VALUES(7,144,'7:144','فرمایا "اے موسیٰؑ، میں نے تمام لوگوں پر ترجیح دے کر تجھے منتخب کیا کہ میر ی پیغمبری کرے اور مجھ سے ہم کلام ہو پس جو کچھ میں تجھے دوں اسے لے اور شکر بجالا"');
INSERT INTO "ur_maududi" VALUES(7,145,'7:145','اس کے بعد ہم نے موسیٰ کو ہر شعبہٴ زندگی کے متعلق نصیحت اور ہر پہلو کے متعلق واضح ہدایت تختیوں پر لکھ کر دے دی اور اس سے کہا”اِن ہدایات کو مضبوط ہاتھوں سے سنبھال اور اپنی قوم کو حکم دے کہ ان کے بہتر مفہوم کی پیروی کریں۔ عنقریب میں تمہیں فاسقوں کے گھر دکھاوٴں گا۔ ');
INSERT INTO "ur_maududi" VALUES(7,146,'7:146','میں اپنی نشانیوں سے اُن لوگوں کی نگاہیں پھیر دوں گا جو بغیر کِسی حق کے زمین میں بڑے بنتے ہیں ، وہ خواہ کوئی نشانی دیکھ لیں کبھی اس پر ایمان نہ لائیں گے ، اگر سیدھا راستہ اُن کے سامنے آئے تو اسے اختیار نہ کریں گے اور اگر ٹیڑھا راستہ نظر آئے تو اس پر چل پڑیں گے، اس لیے کہ اُنہوں نے ہماری نشانیوں کو جھُٹلا یا اور ان سے بے پروائی کرتے رہے');
INSERT INTO "ur_maududi" VALUES(7,147,'7:147','ہماری نشانیوں کو جس کسی نے جھُٹلا یا اور آخرت کی پیشی کا اِنکار کیا اُس کے سارے اعمال ضائع ہوگئے۔ کیا لوگ اِس کے سِوا کچھ اور جزا پا سکتے ہیں کہ جیسا کریں ویسا بھریں؟“');
INSERT INTO "ur_maududi" VALUES(7,148,'7:148','موسیٰ کے پیچھے اس کی قوم کےلوگوں نے اپنے زیوروں سے ایک بچھڑے کا پُتلا بنایا جس میں سے بیل کی سی آواز نکلتی تھی۔ کیا اُنہیں نظر نہ آتا تھا کہ وہ نہ ان سے بولتا ہے نہ کسی معاملہ میں ان کی رہنمائی کرتا ہے ؟ مگر پھر بھی اُنہوں نے اسے معبود بنا لیا اور وہ سخت ظالم تھے۔ ');
INSERT INTO "ur_maududi" VALUES(7,149,'7:149','پھر جب ان کی فریب خوردگی کا طلسم ٹوٹ گیا او ر اُنہوں نے دیکھ لیا کہ در حقیقت وہ گمراہ ہو گئے ہیں تو کہنے لگے کہ "اگر ہمارے رب نے ہم پر رحم نہ فرمایا اور ہم سے درگزر نہ کیا تو ہم برباد ہو جائیں گے"');
INSERT INTO "ur_maududi" VALUES(7,150,'7:150','اُدھر سے موسیٰ غصّے اور رنج میں بھرا ہوا اپنی قوم کی طرف پلٹا۔ آتے ہی اس نے کہا ”بہت بُری جانشینی کی تم لوگوں نے میرے بعد! کیا تم سے اتنا صبر نہ ہوا کہ اپنے ربّ کے حکم کا انتظار کر لیتے؟“ اور تختیاں پھینک دیں اور اپنے بھائی (ہارون) کے سر کے بال پکڑ کر اسے کھینچا۔ ہارون نے کہا ”اے میری ماں کے بیٹے، اِن لوگوں نے مجھے دبا لیا اور قریب تھا کہ مجھے مار ڈالتے، پس تُو دشمنوں کو مجھ پر ہنسنے کا موقع نہ دے اور اس ظالم گروہ کے ساتھ مجھے نہ شامل کر۔“ ');
INSERT INTO "ur_maududi" VALUES(7,151,'7:151','تب موسیٰؑ نے کہا "اے رب، مجھے اور میرے بھائی کو معاف کر اور ہمیں اپنی رحمت میں داخل فرما، تو سب سے بڑھ کر رحیم ہے"');
INSERT INTO "ur_maududi" VALUES(7,152,'7:152','(جواب میں ارشاد ہوا کہ) "جن لوگوں نے بچھڑے کو معبود بنایا وہ ضرور اپنے رب کے غضب میں گرفتار ہو کر رہیں گے اور دنیا کی زندگی میں ذلیل ہوں گے جھوٹ گھڑنے والوں کو ہم ایسی ہی سزا دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(7,153,'7:153','اور جو لوگ برے عمل کریں پھر توبہ کر لیں اور ایمان لے آئیں تو یقیناً اِس توبہ و ایمان کے بعد تیرا رب درگزر اور رحم فرمانے والا ہے"');
INSERT INTO "ur_maududi" VALUES(7,154,'7:154','پھر جب موسیٰؑ کا غصہ ٹھنڈا ہوا تو اس نے وہ تختیاں اٹھا لیں جن کی تحریر میں ہدایت اور رحمت تھی اُن لوگوں کے لیے جو اپنے رب سے ڈرتے ہیں');
INSERT INTO "ur_maududi" VALUES(7,155,'7:155','اور اُس نے اپنی قوم کے ستّر آدمیوں کو منتخب کیا تاکہ وہ (اُس کے ساتھ) ہمارے مقرر کیے ہوئے وقت پر حاضر ہوں ۔ جب اِن لوگوں کو ایک سخت زلزلے نے آپکڑا تو موسٰی نے عرض کیا”اے میرے سرکار، آپ چاہتے تو پہلے ہی اِن کو اور مجھے ہلاک کر سکتے تھے۔کیا آپ اُس قصور میں جو ہم میں سے چند نادانوں نے کیا تھا ہم سب کو ہلاک کر دیں گے؟ یہ تو آپ کی ڈالی ہوئی ایک آزمائش تھی جس کے ذریعہ سے آپ جسے چاہتےہیں گمراہی میں مبتلاکر دیتے ہیں اور جسے چاہتے ہیں ہدایت بخش دیتے ہیں۔ ہمارے سر پرست تو آپ ہی ہیں۔ پس ہمیں معاف کر دیجیے اور ہم پر رحم فرمائیے، آپ سب سے بڑھ کر معاف فر مانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(7,156,'7:156','اور ہمارے لیے اس دنیا کی بھلائی بھی لکھ دیجیے اور آخرت کی بھی، ہم نے آپ کی طرف رجوع کر لیا۔“جواب میں ارشاد ہوا”سزا تو میں جسے چاہتا ہوں دیتا ہوں، مگر میری رحمت ہر چیز پر چھائی ہوئی ہے ، اور اُسے میں اُن لوگوں کے حق میں لکھوں گا جو نافرمانی سے پرہیز کریں گے، زکوٰة دیں گے اور میری آیات پر ایمان لائیں گے۔“');
INSERT INTO "ur_maududi" VALUES(7,157,'7:157','(پس آج یہ رحمت اُ ن لوگوں کا حِصہ ہے) جو اِس پیغمبرنبی اُمّی کی پیروی اختیار کریں جس کا ذکر اُنہیں اپنے ہاں تورات اور انجیل میں لکھا ہوا ملتا ہے۔ وہ انہیں نیکی کا حکم دیتا ہے، بدی سے روکتا ہے، ان کے لیے پاک چیزیں حلال اور ناپاک چیزیں حرام کرتا ہے ، اور ان پر سے وہ بوجھ اتارتا ہے جو اُن پر لدے ہوئے تھے اور وہ بندشیں کھولتا ہے جن میں وہ جکڑے ہوئے تھے۔ لہٰذا جو لوگ اس پر ایمان لائیں اور اس کی حمایت اور نصرت کریں اور اُس روشنی کی پیروی اختیار کریں جو اس کے ساتھ نازل کی گئی ہے، وہی فلاح پانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(7,158,'7:158','اے محمدؐ، کہو کہ "اے انسانو، میں تم سب کی طرف اُس خدا کا پیغمبر ہوں جو زمین اور آسمانوں کی بادشاہی کا مالک ہے، اُس کے سوا کوئی خدا نہیں ہے، وہی زندگی بخشتا ہے اور وہی موت دیتا ہے، پس ایمان لاؤ اللہ پر اور اس کے بھیجے ہوئے نبی اُمی پر جو اللہ اور اس کے ارشادات کو مانتا ہے، اور پیروی اختیار کرو اُس کی، امید ہے کہ تم راہ راست پا لو گے"');
INSERT INTO "ur_maududi" VALUES(7,159,'7:159',' موسٰی کی قوم میں ایک گروہ ایسا بھی تھا جو حق کے مطابق ہدایت کرتا اور حق ہی کے مطابق انصاف کر تا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(7,160,'7:160','اور ہم نے اس قوم کو بارہ گھرانوں میں تقسیم کرکے انہیں مستقل گرہوں کی شکل دے دی تھی۔ اور جب موسٰی سے اس کی قوم نے پانی مانگا تو ہم نے اس کو اشارہ کیا کہ فلاح چٹان پر اپنی لاٹھی مارو۔چناچہ اس چٹان سے یکایک بارہ چشمے پُھوٹ نِکلے اور ہرگروہ نے اپنےپانی لینے کی جگہ متعیّن کرلی، ہم نے اُن پر بادل کا سایہ کیا اور اُن پر من و سلوٰی اُتارا۔ کھاؤ وہ پاک چیزیں جو ہم نے تم کو بخشی ہیں۔ مگر اس کے بعد انہوں نے جو کچھ کیا تو ہم پر ظلم نہیں کیا بلکہ آپ اپنے ہی اوپر ظلم کرتے رہے');
INSERT INTO "ur_maududi" VALUES(7,161,'7:161',' یاد کرو وہ وقت جب ان سےکہا گیا تھا کہ اِس بستی میں جاکر بس جاؤ اور اس کی پیداوار سے اپنے حسبِ منشا روزی حاصل کرو اور حِطَّةُٗ حِطَّةُٗ کہتے جاؤ اور شہر کے دروازے میں سجدہ ریز ہوتے ہوئے داخل ہو، ہم تمہاری خطائیں معاف کریں گے اور نیک رویہ رکھنے والوں مزید فضل سے نوازیں گے');
INSERT INTO "ur_maududi" VALUES(7,162,'7:162','مگر جو لوگ اُن میں ظالم تھے اُنھوں نے اُس بات کو جو اُن سے کہی گئی تھی بدل ڈالا، اور نتیجہ یہ ہوا کہ ہم نے اُن کے ظلم کی پاداش میں اُن پر آسمان سے عذاب بھیج دیا۔ ');
INSERT INTO "ur_maududi" VALUES(7,163,'7:163','اور ذرا اِ ن سے اُس بستی کا حال بھی پوچھو جو سمندر کے کنارے واقع تھی ۔ اِنہیں یاد دلاؤ وہ واقعہ کہ وہاں کے لوگ سَبْت (ہفتہ) کے دن احکامِ الٰہی کی خلاف ورزی کرتے تھے اور یہ کہ مچھلیاں سبت ہی کے دن اُبھر اُبھر کر سطح پر اُن کے سامنے آتی تھیں اور سَبْت کے سوا باقی دنوں میں نہیں آتی تھیں۔ یہ اس لیےہوتا تھا کہ ہم ان کی نافر مانیوں کی وجہ سے ان کو آزمائش میں ڈال رہے تھے');
INSERT INTO "ur_maududi" VALUES(7,164,'7:164','اور انہیں یہ بھی یاد لاؤ کہ جب اُن میں سے ایک گروہ نے دوسرے گروہ سے کہا تھا کہ "تم ایسے لوگوں کو کیوں نصیحت کرتے ہو جنہیں اللہ ہلاک کرنے والا یا سخت سزا دینے والا ہے " تو انہوں نے جواب دیا تھا کہ "ہم یہ سب کچھ تمہارے رب کے حضور اپنی معذرت پیش کرنے کے لیے کرتے ہیں اور اس امید پر کرتے ہیں کہ شاید یہ لوگ اس کی نافرمانی سے پرہیز کرنے لگیں"');
INSERT INTO "ur_maududi" VALUES(7,165,'7:165','آخر کا ر جب اُن ہدایات کو با لکل ہی فراموش کر گئے جو انہیں یاد کرائی گئی تھیں تو ہم نے اُن لوگوں کو بچا لیا جو بُرائی سے روکتے تھے اور باقی سب لوگوں کو جو ظالم تھے ان کی نافرمانیوں پر سخت عذاب میں پکڑ لیا۔ ');
INSERT INTO "ur_maududi" VALUES(7,166,'7:166','پھر جب وہ پوری سر کشی کے ساتھ وہی کام کیے چلے گئے جس سے انہیں روکا گیا تھا، تو ہم نے کہا کہ بندر ہو جاؤذلیل اور خوار۔ ');
INSERT INTO "ur_maududi" VALUES(7,167,'7:167','اور یاد کرو جبکہ تمہارے رب نے اعلان کر دیا کہ”وہ قیامت تک برابر ایسے لوگ بنی اسرائیل پر مسلّط کرتا رہے گا جو ان کو بدترین عذاب دیں گے، “ یقیناً تمہارا رب سزا دینے میں تیز دست ہے اور یقیناً وہ در گزر اور رحم سے بھی کام لینے والا ہے');
INSERT INTO "ur_maududi" VALUES(7,168,'7:168','ہم نے ان کو زمین میں ٹکڑے ٹکڑے کر کے بہت سی قوموں میں تقسیم کر دیا کچھ لوگ ان میں نیک تھے اور کچھ ا س سے مختلف اور ہم ان کو اچھے اور برے حالات سے آزمائش میں مبتلا کرتے رہے کہ شاید یہ پلٹ آئیں');
INSERT INTO "ur_maududi" VALUES(7,169,'7:169','پھر اگلی نسلوں کے بعد ایسے نا خلف لوگ ان کے جا نشین ہوئے جو کتاب الٰہی کے وارث ہو کر اسی دنیائے دَنی کے فائدے سمیٹتے ہیں اور کہہ دیتے ہیں کہ توقع ہے ہمیں معاف کر دیا جائے گا، اور اگر وہی متاعِ دنیا پھر سامنے آتی ہے تو پھر لپک کر اُسے لے لیتے ہیں۔ کیا ان سے کتاب کا عہد نہیں لیا جا چکا ہےکہ اللہ کے نام پر وہی بات کہیں جو حق ہو؟ اور یہ خود پڑھ چکے ہیں جو کتاب میں لکھا ہے۔ آخرت کی قیام گاہ تو خدا ترس لوگوں کے لیے ہی بہتر ہے ، کیا تم اتنی سی بات نہیں سمجھتے؟');
INSERT INTO "ur_maududi" VALUES(7,170,'7:170','جو لوگ کتاب کی پابندی کر تے ہیں اور جنہوں نے نماز قائم رکھی ہے، یقیناً ایسے نیک کردار لوگوں کا اجر ہم ضائع نہیں کریں گے');
INSERT INTO "ur_maududi" VALUES(7,171,'7:171','انہیں وہ وقت بھی کچھ یاد ہے جبکہ ہم نے پہاڑ کو ہلا کر اُن پر اس طرح چھا دیا تھا کہ گویا وہ چھتری ہےاور یہ گمان کر رہے تھے کہ وہ ان پر آ پڑے گا اور اُس وقت ہم نے اُن سے کہا تھا کہ جو کتاب ہم تمہیں دے رہے ہیں اسے مضبوطی کے ساتھ تھامو اور جو کچھ اس میں لکھا ہے اسے یاد رکھو،توقع ہے کہ تم غلط روی سے بچے رہو گے۔ ');
INSERT INTO "ur_maududi" VALUES(7,172,'7:172',' اور اے نبی، لوگوں کو یاد دلاؤ وہ وقت جبکہ تمہارے رب نے بنی آدم کی پُشتوں سے ان کی نسل کو نکالا تھا اور انہیں خود ان کے اوپر گواہ بناتے ہوئے پوچھا تھا ”کیا میں تمارا رب نہیں ہوں؟“ انہوں نے کہا”ضرور آپ ہی ہمارے رب ہیں ، ہم اس پر گواہی دیتے ہیں “۔ یہ ہم نے اس لیے کیا کہ کہیں تم قیامت کے روز یہ نہ کہہ دو کہ ”ہم اس بات سے بے خبر تھے“');
INSERT INTO "ur_maududi" VALUES(7,173,'7:173','یا یہ نہ کہنے لگو کہ ”شرک کی ابتدا تو ہمارے باپ دادا نے ہم سے پہلے کی تھی اور ہم بعد کو ان کی نسل سے پیدا ہوئے، پھر کیا آپ ہمیں اُس قصُور میں پکڑتے ہیں جو غلط کار لوگوں نے کیا تھا۔“ ');
INSERT INTO "ur_maududi" VALUES(7,174,'7:174','دیکھو، اِس طرح ہم نشانیاں واضح طور پر پیش کرتے ہیں۔ اور اس لیے کرتے ہیں کہ یہ لوگ پلٹ آئیں۔ ');
INSERT INTO "ur_maududi" VALUES(7,175,'7:175','اور اے محمد ؐ ، اِن کے سامنے اُس شخص کا حال بیان کرو جس کو ہم نے اپنی آیات کا علم عطا کیا تھا مگر وہ ان کی پابندی سے نِکل بھاگا۔ آخر کار شیطان اس کے پیچھے پڑ گیا یہاں تک کہ وہ بھٹکنے والوں میں شامل ہو کر رہا');
INSERT INTO "ur_maududi" VALUES(7,176,'7:176','اگر ہم چاہتے اسے اُن آیتوں کے ذریعہ سے بلندی عطا کرتے، مگر وہ تو زمین کی طرف جھک کر رہ گیا اور اپنی خواہش نفس ہی کے پیچھے پڑارہا، لہٰذا اس کی حالت کتّے کی سی ہو گئی کہ تم اس پر حملہ کرو تب بھی زبان لٹکائے رہے اور اسے چھوڑ دو تب بھی زبان لٹکائے رہے۔ یہی مثال ہے اُن لوگوں کی جو ہماری آیات کو جھٹلاتے ہیں۔ تم یہ حکایات اِن کو سناتے رہو، شاید کہ یہ کچھ غوروفکرکریں');
INSERT INTO "ur_maududi" VALUES(7,177,'7:177','بڑی ہی بری مثال ہے ایسے لوگوں کی جنہوں نے ہماری آیات کو جھٹلایا، اور وہ آپ اپنے ہی اوپر ظلم کرتے رہے ہیں');
INSERT INTO "ur_maududi" VALUES(7,178,'7:178','جسے اللہ ہدایت بخشے بس وہی راہ راست پاتا ہے اور جس کو اللہ اپنی رہنمائی سے محروم کر دے وہی ناکام و نامراد ہو کر رہتا ہے');
INSERT INTO "ur_maududi" VALUES(7,179,'7:179','اور یہ حقیقت ہے کہ بہت سے جِن اور انسان ایسے ہیں جن کو ہم نے جہنم ہی کے لیے پیدا کیا ہے۔ ، ان کے پاس دل ہیں مگر وہ ان سے سوچتے نہیں۔ ان کے پاس آنکھیں ہیں مگر وہ ان سے دیکھتے نہیں۔ ان کے پاس کان تو ہیں مگر وہ ان سے سنتے نہیں۔ وہ جانوروں کی طرح ہیں بلکہ ان سے بھی زیادہ گئے گزرے ، یہ وہ لوگ ہیں جو غفلت میں کھوئے گئے ہیں');
INSERT INTO "ur_maududi" VALUES(7,180,'7:180',' اللہ اچھے ناموں کا مستحق ہے، اس کو اچھے ہی ناموں سے پکارو اور اُن لوگو ں کو چھوڑ دوجو اس کے نام رکھنے میں راستی سے منحرف ہو جاتے ہیں۔ جو کچھ وہ کرتے رہے ہیں اس کا بدلہ وہ پاکر رہیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(7,181,'7:181','ہماری مخلوق میں ایک گروہ ایسا بھی ہے جو ٹھیک ٹھیک حق کے مطابق ہدایت اور حق ہی کے مطابق انصاف کرتا ہے');
INSERT INTO "ur_maududi" VALUES(7,182,'7:182','رہے وہ لوگ جنہوں نے ہماری آیات کو جھٹلا دیا ہے، تو انہیں ہم بتدریج ایسے طریقہ سے تباہی کی طرف لے جائیں گے کہ انہیں خبر تک نہ ہوگی');
INSERT INTO "ur_maududi" VALUES(7,183,'7:183','میں ان کو ڈھیل دے رہا ہوں، میری چال کا کوئی توڑ نہیں ہے');
INSERT INTO "ur_maududi" VALUES(7,184,'7:184','اور کیا اِن لوگوں نے کبھی سوچا نہیں؟ اِن کے رفیق پر جنون کا کوئی اثر نہیں ہے وہ تو ایک خبردار ہے جو (برا انجام سامنے آنے سے پہلے) صاف صاف متنبہ کر رہا ہے');
INSERT INTO "ur_maududi" VALUES(7,185,'7:185','کیا ان لوگوں نے آسمان و زمین کے انتظام پر کبھی غور نہیں کیا اور کسی چیز کو بھی جو خدا نے پیدا کی ہے آنکھیں کھول کر نہیں دیکھا؟ اور کیا یہ بھی انہوں نے نہیں سوچا کہ شاید ان کی مہلتِ زندگی پوری ہونے کا وقت قریب آلگا ہو؟ پھر آخر پیغمبر ؐ کی اِس تنبیہ کے بعد اور کون سی بات ایسی ہو سکتی ہے۔ جس پریہ ایمان لائیں؟');
INSERT INTO "ur_maududi" VALUES(7,186,'7:186','جس کو اللہ رہنمائی سے محروم کر دے اُس کے لیے پھر کوئی رہنما نہیں ہے، اور اللہ اِنہیں اِن کی سرکشی ہی میں بھٹکتا ہوا چھوڑے دیتا ہے');
INSERT INTO "ur_maududi" VALUES(7,187,'7:187','یہ لوگ تم سے پوچھتے ہیں کہ آخر وہ قیامت کی گھڑی کب نازل ہوگی؟ کہو "اس کا علم میرے رب ہی کے پاس ہے اُسے اپنے وقت پر وہی ظاہر کرے گا آسمانوں اور زمین میں وہ بڑا سخت وقت ہوگا وہ تم پر اچانک آ جائے گا" یہ لوگ اس کے متعلق تم سے اس طرح پوچھتے ہیں گویا کہ تم اس کی کھوج میں لگے ہوئے ہو کہو "اس کا علم تو صرف اللہ کو ہے مگر اکثر لوگ اس حقیقت سے نا واقف ہیں"');
INSERT INTO "ur_maududi" VALUES(7,188,'7:188','اے محمدؐ ، ان سے کہو کہ ”میں اپنی ذات کے لیے کسی نفع اور نقصان کا اختیار نہیں رکھتا، اللہ ہی جو کچھ چاہتا ہے وہی ہوتا ہے۔ اور اگر مجھے غیب کا علم ہوتا تو میں بہت سے فائدے اپنے لیے حاصل کر لیتا اور مجھے کبھی کوئی نقصان نہ پہنچتا۔میں تو محض ایک خبردار کرنے والا اور خوشخبری سُنانے والا ہوں اُن لوگوں کےلیے جو میری بات مانیں۔“');
INSERT INTO "ur_maududi" VALUES(7,189,'7:189','وہ اللہ ہی ہے جس نے تمہیں ایک جان سے پیدا کیا اور اسی کی جنس سے اس کا جوڑا بنایا تاکہ اس کے پاس سکون حاصل کرے پھر جب مرد نے عورت کو ڈھانک لیا تو اسے ایک خفیف سا حمل رہ گیا جسے لیے لیے وہ چلتی پھرتی رہی پھر جب وہ بوجھل ہو گئی تو دونوں نے مل کر اللہ، اپنے رب سے دعا کی کہ اگر تو نے ہم کو اچھا سا بچہ دیا تو ہم تیرے شکر گزار ہوں گے');
INSERT INTO "ur_maududi" VALUES(7,190,'7:190','مگر جب اللہ نے ان کو ایک صحیح و سالم بچہ دے دیا تو وہ اُس کی اِس بخشش و عنایت میں دوسروں کو اِس کا شریک ٹھہرانے لگے۔ اللہ بہت بلند و برتر ہے اِن مشرکانہ باتوں سے جو یہ لوگ کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(7,191,'7:191','کیسے نادان ہیں یہ لوگ کہ اُن کو خدا کا شریک ٹھیراتے ہیں جو کسی چیز کو بھی پیدا نہیں کرتے بلکہ خود پیدا کیے جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(7,192,'7:192','جو نہ ان کی مدد کر سکتے ہیں اور نہ آپ اپنی مدد ہی پر قادر ہیں');
INSERT INTO "ur_maududi" VALUES(7,193,'7:193','اگر تم انہیں سیدھی راہ پر آنے کی دعوت دو تو وہ تمہارے پیچھے نہ آئیں، تم خواہ انہیں پکارو یا خاموش رہو، دونوں صورتوں میں تمہارے لیے یکساں ہی رہے۔ ');
INSERT INTO "ur_maududi" VALUES(7,194,'7:194','تم لوگ خدا کو چھوڑ کر جنہیں پکارتے ہو وہ تو محض بندے ہیں جیسے تم بندے ہو ان سے دعائیں مانگ دیکھو، یہ تمہاری دعاؤں کا جواب دیں اگر ان کے بارے میں تمہارے خیالات صحیح ہیں');
INSERT INTO "ur_maududi" VALUES(7,195,'7:195','کیا یہ پاوٴں رکھتے ہیں کہ ان سے چلیں؟ کیا یہ ہاتھ رکھتے ہیں کہ ان سے پکڑیں؟ کیا یہ آنکھیں رکھتے ہیں کہ ان سے دیکھیں؟ کیا یہ کان رکھتے ہیں کہ ان سے سُنیں؟ اے محمدؐ ، ان سے کہہ دو کہ”بُلا لواپنے ٹھہرائے ہوئے شریکوں کو پھر تم سب مِل کر میرے خلاف تدبیریں کرو اور مجھے ہر گز مہلت نہ دو');
INSERT INTO "ur_maududi" VALUES(7,196,'7:196','میرا حامی و ناصر وہ خدا ہے جس نے یہ کتا ب نازل کی ہے اور وہ نیک آدمیوں کی حمایت کرتا ہے، ');
INSERT INTO "ur_maududi" VALUES(7,197,'7:197','بخلاف اِس کے تم جنہیں خدا کو چھوڑ کر پکارتے ہو وہ نہ تمہاری مدد کرسکتے ہیں اور نہ خود اپنی مدد ہی کرنے کے قابل ہیں');
INSERT INTO "ur_maududi" VALUES(7,198,'7:198','بلکہ اگر تم انہیں سیدھی راہ پر آنے کے لیے کہو تو وہ تمہاری بات سن بھی نہیں سکتے بظاہر تم کو ایسا نظر آتا ہے کہ وہ تمہاری طرف دیکھ رہے ہیں مگر فی الواقع وہ کچھ بھی نہیں دیکھتے"');
INSERT INTO "ur_maududi" VALUES(7,199,'7:199','اے نبیؐ، نرمی و درگزر کا طریقہ اختیار کرو، معروف کی تلقین کیے جاؤ، اور جاہلوں سے نہ الجھو');
INSERT INTO "ur_maududi" VALUES(7,200,'7:200','اگر کبھی شیطان تمہیں اکسائے تو اللہ کی پناہ مانگو، وہ سب کچھ سننے اور جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(7,201,'7:201','حقیقت میں جو لو گ متقی ہیں اُن کا حال تو یہ ہوتا ہے کہ کبھی شیطان کے اثر سے کوئی برا خیال اگر انہیں چھو بھی جاتا ہے تو وہ فوراً چوکنے ہو جاتے ہیں اور پھر انہیں صاف نظر آنے لگتا ہے کہ ان کے لیے صحیح طریق کار کیا ہے');
INSERT INTO "ur_maududi" VALUES(7,202,'7:202','رہے ان کے (یعنی شیاطین کے) بھائی بند تو وہ اُنہیں ان کی کج روی میں کھینچے لیے چلے جاتے ہیں اور انہیں بھٹکانے میں کوئی کسر اُٹھا نہیں رکھتے۔ ');
INSERT INTO "ur_maududi" VALUES(7,203,'7:203','اے نبی، جب تم ان لوگوں کے سامنے کوئی نشانی (یعنی معجزہ) پیش نہیں کرتے تو یہ کہتے ہیں کہ تم نے اپنے لیے کوئی نشانی کیوں نہ انتخاب کر لی؟ اِن سےکہو”میں تو صرف اُس وحی کی پیروی کرتا ہوں جو میرے ربّ نےمیری طرف بھیجی ہے۔ یہ بصیر ت کی روشنیاں ہیں تمہارے ربّ کی طرف سے اور ہدایت اور رحمت ہے اُن لوگوں کے لیے جو اسے قبول کریں۔ ');
INSERT INTO "ur_maududi" VALUES(7,204,'7:204','جب قرآن تمہارے سامنے پڑھا جائے تو اسے توجہ سے سُنو اور خاموش رہو ، شاید کہ تم پر بھی رحمت ہوجائے۔“ ');
INSERT INTO "ur_maududi" VALUES(7,205,'7:205','اے نبیؐ ، اپنے ربّ کو صبح و شام یاد کیا کرو دل ہی دل میں زاری اور خوف کے ساتھ اور زبان سے بھی ہلکی آواز کے ساتھ۔ تم اُن لوگوں میں نہ ہو جاوٴ جو غفلت میں پڑے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(7,206,'7:206','جو فرشتے تمہارے ربّ کے حضور تقرب کا مقام رکھتے ہیں وہ کبھی اپنی بڑائی کے گھمنڈ میں آکر اُس کی عبادت سےمنہ نہیں موڑتے ، ___ اور اُس کی تسبیح کرتے ہیں، ___ اور اُس کے آگے جُھکتے رہتےہیں۔ ___ ؏۲۴ السجدة ');
INSERT INTO "ur_maududi" VALUES(8,1,'8:1','تم سے اَنفال کے متعلق پُوچھتے ہیں؟ کہو”یہ انفال تو اللہ اور اُس کے رسُول ؐ کے ہیں، پس تم لوگ اللہ سے ڈرو اور اپنے آپس کے تعلقات درست کرو اور اللہ اور اُس کے رسُول ؐ کی اطاعت کرو اگر تم مومن ہو۔ “');
INSERT INTO "ur_maududi" VALUES(8,2,'8:2','سچے اہلِ ایمان تو وہ لوگ ہیں جن کے دل اللہ کا ذکر سُن کرلرز جاتے ہیں اور جب اللہ کی آیات ان کے سامنے پڑھی جاتی ہیں تو ان کا ایمان بڑھ جاتا ہے ، اور وہ اپنے ربّ پر اعتماد رکھتے ہیں');
INSERT INTO "ur_maududi" VALUES(8,3,'8:3','جو نماز قائم کرتے ہیں اور جو کچھ ہم نے ان کو دیا ہے اس میں سے (ہماری راہ میں) خرچ کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(8,4,'8:4','ایسےہی لوگ حقیقی مومن ہیں۔ ان کے لیے ان کے ربّ کے پاس بڑے درجے ہیں۔ قصوروں سے درگزر ہے اور بہترین رزق ہے');
INSERT INTO "ur_maududi" VALUES(8,5,'8:5','(اِس مال غنیمت کے معاملہ میں بھی ویسی ہی صورت پیش آ رہی ہے جیسی اُس وقت پیش آئی تھی جبکہ) تیرا رب تجھے حق کے ساتھ تیرے گھر سے نکال لایا تھا اور مومنوں میں سے ایک گروہ کو یہ سخت ناگوار تھا');
INSERT INTO "ur_maududi" VALUES(8,6,'8:6','وہ اس حق کےمعاملہ میں تجھ سے جھگڑ رہے تھے دراں حالے کہ وہ صاف صاف نمایا ہو چکا تھا۔ ان کا حال یہ تھا کہ گویا وہ آنکھوں دیکھے موت کی طرف ہانکے جارہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(8,7,'8:7','یاد کرو وہ موقع جب کہ اللہ تم سے وعدہ کر رہا تھا کہ دونوں گروہوں میں سے ایک تمہیں مِل جائے گا۔ تم چاہتے تھے کہ کمزور گروہ تمہیں ملے۔ مگر اللہ کا ارادہ یہ تھا کہ اپنے ارشادات سے حق کو حق کر دکھائے اور کافروں کی جڑ کاٹ دے');
INSERT INTO "ur_maududi" VALUES(8,8,'8:8','تاکہ حق حق ہو کر رہے اور باطِل باطِل ہو کر رہ جائے خواہ مجرموں کو یہ کتنا ہی ناگوار ہو۔ ');
INSERT INTO "ur_maududi" VALUES(8,9,'8:9','اور وہ موقع یاد کرو جبکہ تم اپنے رب سے فریاد کر رہے تھے جواب میں اس نے فرمایا کہ میں تمہاری مدد کے لیے پے در پے ایک ہزار فرشتے بھیج رہا ہوں');
INSERT INTO "ur_maududi" VALUES(8,10,'8:10','یہ بات اللہ نے تمہیں صرف اس لیے بتا دی کہ تمہیں خوشخبری ہو اور تمہارے دل اس سے مطمئن ہو جائیں، ورنہ مدد تو جب بھی ہوتی ہے اللہ ہی کی طرف سے ہوتی ہے، یقیناً اللہ زبردست اور دانا ہے');
INSERT INTO "ur_maududi" VALUES(8,11,'8:11','اور وہ وقت جبکہ اللہ اپنی طرف سے غنودگی کی شکل میں تم پر اطمینان و بے خوفی کی کیفیت طاری کر رہا تھا، اور آسمان سے تمہارے اوپر پانی برسا رہا تھا تاکہ تمہیں پاک کرے اور تم سے شیطان کی ڈالی ہوئی نجاست دُور کرے اور تمہاری ہمّت بندھائے اور اس کے ذریعہ سے تمہارے قدم جمادے۔ ');
INSERT INTO "ur_maududi" VALUES(8,12,'8:12','اور وہ وقت جبکہ تمہارا ربّ فرشتوں کو اشارہ کر رہا تھا کہ ”میں تمہارے ساتھ ہوں، تم اہلِ ایمان کو ثابت قدم رکھو، میں ابھی ان کافروں کے دلوں میں رُعب ڈالے دیتا ہوں، پس تم ان کی گردنوں پر ضرب اور جوڑ جوڑ پر چوٹ لگاوٴ۔“ ');
INSERT INTO "ur_maududi" VALUES(8,13,'8:13','یہ اس لیے کہ ان لوگوں نے اللہ اور اس کے رسول ؐ کا مقابلہ کیا اور جو اللہ اور اس کے رسول ؐ کا مقابلہ کرے اللہ اس کے لیے نہایت سخت گیر ہے۔۔۔۔ ');
INSERT INTO "ur_maududi" VALUES(8,14,'8:14','یہ ہے تم لوگوں کی سزا، اب اس کا مزہ چکھو، اور تمہیں معلوم ہو کہ حق کا انکار کرنے والوں کے لیے دوزخ کا عذاب ہے');
INSERT INTO "ur_maududi" VALUES(8,15,'8:15','اے ایمان لانے والو، جب تم ایک لشکر کی صورت میں کفار سے دوچار ہو تو ان کے مقابلہ میں پیٹھ نہ پھیرو');
INSERT INTO "ur_maududi" VALUES(8,16,'8:16','جس نے ایسے موقع پر پیٹھ پھیرے۔۔۔۔ اِلّا یہ کہ جنگی چال کے طور پر ایسا کرے یا کسی دوسری فوج سے جا ملنے کے لیے۔۔۔۔ تو وہ اللہ کے غضب میں گھِر جائے گا، اُس کا ٹھکانا جہنّم ہو گا، اور وہ بہت بُری جائے بازگشت ہے۔ ');
INSERT INTO "ur_maududi" VALUES(8,17,'8:17','پس حقیقت یہ ہے کہ تم نے انہیں قتل نہیں کیا بلکہ اللہ نے ان کو قتل کیا اور تُو نے نہیں پھینکا بلکہ اللہ نے پھینکا ( اور مومنوں کے ہاتھ جو اس کام میں استعمال کیے گئے) تو یہ اس لیے تھا کہ اللہ مومنوں کو ایک بہترین آزمائش سے کامیابی کے ساتھ گزار دے ، یقیناً اللہ سُننے اور جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,18,'8:18','یہ معاملہ تو تمہارے ساتھ ہے اور کافروں کے ساتھ معاملہ یہ ہے کہ اللہ ان کی چالوں کو کمزور کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,19,'8:19','(ان کافروں سے کہہ دو)”اگر تم فیصلہ چاہتے تھے تو لو، فیصلہ تمہارے سامنے آگیا۔ اب باز آجاوٴ، تمہارے ہی لیے بہتر ہے، ورنہ پھر پلٹ کر اسی حماقت کا اعادہ کرو گے تو ہم بھی اُسی سزا کا اعادہ کریں گے اور تمہاری جمعیت، خواہ وہ کتنی ہی زیادہ ہو، تمہارے کچھ کام نہ آسکے گی، اللہ مومنوں کے ساتھ ہے۔“');
INSERT INTO "ur_maududi" VALUES(8,20,'8:20','اے ایمان لانے والو، اللہ اور اُس کے رسُول کی اطاعت کرو اور حکم سُننے کے بعد اس سے سرتابی نہ کرو');
INSERT INTO "ur_maududi" VALUES(8,21,'8:21','اُن لوگوں کی طرح نہ ہو جاوٴ جنہوں نے کہا کہ ہم نے سُنا حالانکہ وہ نہیں سُنتے۔ ');
INSERT INTO "ur_maududi" VALUES(8,22,'8:22','یقیناً خدا کے نزدیک بد ترین قسم کے جانور وہ بہرے گُونگے لوگ ہیں جو عقل سے کام نہیں لیتے');
INSERT INTO "ur_maududi" VALUES(8,23,'8:23','اگر اللہ کو معلوم ہوتا کہ ان میں کچھ بھی بھلائی ہے تو وہ ضرور اُنہیں سُننے کی توفیق دیتا ( لیکن بھلائی کے بغیر) اگر وہ اُن کو سُنواتا تو وہ بے رُخی کے ساتھ منہ پھیر جاتے۔ ');
INSERT INTO "ur_maududi" VALUES(8,24,'8:24','اے ایمان لانے والو، اللہ اور اُس کے رسول کی پکار پر لبیّک کہو جبکہ رسول ؐ تمہیں اُس چیز کی طرف بُلائے جو تمہیں زندگی بخشنے والی ہے، اور جان رکھو کہ اللہ آدمی اور اس کے دل کے درمیان حائل ہے اور اُسی کی طرف تم سمیٹے جاوٴ گے۔ ');
INSERT INTO "ur_maududi" VALUES(8,25,'8:25','اور بچو اُس فتنے سے جس کی شامت مخصوص طور پرصرف اُنہی لوگوں تک محدود نہ رہے گی جنہوں نے تم میں سے گناہ کیا ہو۔ اور جان رکھو کہ اللہ سخت سزا دینے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,26,'8:26','یاد کرو وہ وقت جبکہ تم تھوڑے تھے، زمین میں تم کو بے زور سمجھا جاتا تھا، تم ڈرتے رہتے تھے کہ کہیں لوگ تمہیں مِٹا نہ دیں۔ پھر اللہ نے تم کو جائے پناہ مہیا کر دی، اپنی مدد سے تمہارے ہاتھ مضبوط کیے اور تمہیں اچھا رزق پہنچایا ، شاید کہ تم شکر گزار بنو۔ ');
INSERT INTO "ur_maududi" VALUES(8,27,'8:27','اے ایمان لانے والو، جانتے بُوجھتے اللہ اور اُس کے رسول ؐ کے ساتھ خیانت نہ کرو، اپنی امانتوں میں غداری کے مرتکب نہ ہو');
INSERT INTO "ur_maududi" VALUES(8,28,'8:28','اور جان رکھو کہ تمہارے مال اور تمہاری اولاد حقیقت میں سامانِ آزمائش ہیں اور اللہ کےپاس اجر دینے کے لیے بہت کچھ ہے');
INSERT INTO "ur_maududi" VALUES(8,29,'8:29','اے ایمان لانے والو، اگر تم خدا ترسی اختیار کرو گے تو اللہ تمہارے لیے کسوٹی بہم پہنچا دے گا اور تمہاری بُرائیوں کو تم سے دور کرے گا، اور تمہارے قصور معاف کرے گا۔ اللہ بڑا فضل فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,30,'8:30','وہ وقت بھی یاد کرنے کے قابل ہے جبکہ منکرینِ حق تیرے خلاف تدبیریں سوچ رہے تھے کہ تجھے قید کر دیں یا قتل کر ڈالیں یا جلا وطن کر دیں۔ وہ اپنی چالیں چل رہے تھے اور اللہ اپنی چال چل رہا تھا اور اللہ سب سے بہترین چال چلنے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,31,'8:31','جب ان کو ہماری آیات سنائی جاتی تھیں تو کہتے تھے کہ“ہاں سُن لیا ہم نے، ہم چاہیں تو ایسی ہی باتیں ہم بھی بنا سکتے ہیں، یہ تو وہی پُرانی کہانیاں ہیں جو پہلے سے لوگ کہتے چلے آ رہے ہیں "');
INSERT INTO "ur_maududi" VALUES(8,32,'8:32','اور وہ بات بھی یا د ہے جو اُنہوں نے کہی تھی کہ ”خدایا اگر یہ واقعی حق ہے اور تیری طرف سے ہے تو ہم پر آسمان سے پتھر برسا دے یا کوئی دردناک عذاب ہم پر لے آ۔“ ');
INSERT INTO "ur_maududi" VALUES(8,33,'8:33','اُس وقت تو اللہ ان پر عذاب نازل کرنے والا نہ تھا جبکہ تُو ان کے درمیان موجود تھا۔ اور نہ اللہ کا یہ قاعدہ ہے کہ لوگ استغفار کر رہے ہوں اور وہ اُن کو عذاب دیدے۔ ');
INSERT INTO "ur_maududi" VALUES(8,34,'8:34','لیکن اب کیوں نہ وہ ان پر عذاب نازل کرے جبکہ وہ مسجد حرام کا راستہ روک رہے ہیں، حالانکہ وہ اس مسجد کے جائز متولی نہیں ہیں اس کے جائز متولی تو صرف اہلِ تقویٰ ہی ہو سکتے ہیں مگر اکثر لوگ اس بات کو نہیں جانتے');
INSERT INTO "ur_maududi" VALUES(8,35,'8:35','بیت اللہ کے پاس ان لوگوں کی نماز کیا ہوتی ہے ، بس سیٹیاں بجاتے اور تالیاں پیٹتے ہیں ، پس اب لو، اِس عذاب کا مزہ چکھو اپنے اُس انکارِ حق کی پاداش میں جو تم کرتے رہے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(8,36,'8:36','جن لوگوں نے حق کو ماننے سے انکار کیا ہے وہ اپنے مال خدا کے راستے سے روکنے کے لیے صرف کر رہے ہیں اور ابھی اور خرچ کرتے رہیں گے مگر آخر کار یہی کوششیں ان کے لیے پچھتاوے کا سبب بنیں گی، پھر وہ مغلوب ہوں گے، پھر یہ کافر جہنم کی طرف گھیر لائے جائیں گے');
INSERT INTO "ur_maududi" VALUES(8,37,'8:37','تاکہ اللہ گندگی کو پاکیزگی سے چھانٹ کر الگ کرے اور ہر قسم کی گندگی کو ملا کر اکٹھا کرے پھر اس پلندے کو جہنم میں جھونک دے، یہی لوگ اصلی دیوالیے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(8,38,'8:38','اے نبیؐ، ان کافروں سے کہو کہ اگر اب بھی باز آ جائیں تو جو کچھ پہلے ہو چکا ہے اس سے درگزر کر لیا جائے گا، لیکن اگر یہ اسی پچھلی روش کا اعادہ کریں گے تو گزشتہ قوموں کے ساتھ جو کچھ ہو چکا ہے وہ سب کو معلوم ہے');
INSERT INTO "ur_maududi" VALUES(8,39,'8:39','اے ایمان لانے والو، ان کافروں سے جنگ کرو یہاں تک کہ فتنہ باقی نہ رہےاور دین پورا کا پورا اللہ کے لیے ہو جائے۔ پھر اگر وہ فتنہ سے رُک جائیں تو اُن کے اعمال کا دیکھنے والا اللہ ہے');
INSERT INTO "ur_maududi" VALUES(8,40,'8:40','اور اگر وہ نہ مانیں تو جان رکھو کہ اللہ تمہارا سرپرست ہے اور وہ بہترین حامی و مدد گار ہے');
INSERT INTO "ur_maududi" VALUES(8,41,'8:41','اور تمہیں معلوم ہو کہ جو کچھ مالِ غنیمت تم نے حاصل کیا ہے اس کا پانچواں حصّہ اللہ اور اس کے رسول ؐ اور رشتہ داروں اور یتیموں اور مسکینوں اور مسافروں کے لیے ہے، اگر تم ایمان لائے ہو اللہ پر اور اس چیز پر جو فیصلے کے روز، یعنی دونوں فوجوں کی مڈ بھیڑ کے دن، ہم نے اپنے بندے پر نازل کی تھی، (تو یہ حصّہ بخوشی ادا کرو)۔ اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_maududi" VALUES(8,42,'8:42','یاد کرو وہ وقت جبکہ تم وادی کے اِس جانب تھے اور وہ دُوسری جانب پڑاوٴ ڈالے ہوئے تھے اور قافلہ تم سے نیچے (ساحل ) کی طرف تھا۔ اگر کہیں پہلے سے تمہارے اور ان کے درمیان مقابلہ کی قرار داد ہو چکی ہوتی تو تم ضرور اس موقع پر پہلو تہی کرجاتے، لیکن جو کچھ پیش آیا وہ اس لیے تھا کہ جس بات کا فیصلہ اللہ کر چکا تھا اسے ظہور میں لے آئے تاکہ جسے ہلاک ہونا ہے وہ دلیلِ روشن کے ساتھ ہلاک ہو اور جسے زندہ رہنا ہے وہ دلیلِ روشن کے ساتھ زندہ رہے ، یقیناً خدا سُننے اور جاننے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(8,43,'8:43','اور یاد کرو وہ وقت جبکہ اے نبی ؐ ، خدا اُن کو تمہارے خواب میں تھوڑا دکھا رہا تھا۔ اگر کہیں وہ تمہیں اُن کی تعداد زیادہ دکھا دیتا تو ضرور تم لوگ ہمت ہار جاتے اور لڑائی کے معاملہ میں جھگڑا شروع کر دیتے، لیکن اللہ ہی نے اِس سے تمہیں بچایا، یقیناً وہ سینوں کا حال تک جانتا ہے');
INSERT INTO "ur_maududi" VALUES(8,44,'8:44','اور یاد کرو جب کہ مقابلے کے وقت خدا نے تم لوگوں کی نگاہوں میں دشمنوں کو تھوڑا دکھایا اور ان کی نگاہوں میں تمہیں کم کر کے پیش کیا، تاکہ جو بات ہونی تھی اُسے اللہ ظہُور میں لے آئے، اور آخر کار سارے معاملات اللہ ہی کی طرف رجوع کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(8,45,'8:45','اے ایمان لانے والو، جب کسی گروہ سے تمہارا مقابلہ ہو تو ثابت قدم رہو اور اللہ کو کثرت سے یاد کرو، توقع ہے کہ تمہیں کامیابی نصیب ہو گی');
INSERT INTO "ur_maududi" VALUES(8,46,'8:46','اور اللہ اور اُس کے رسول ؐ کی اطاعت کرو اور آپس میں جھگڑو نہیں ورنہ تمہارے اندر کمزوری پیدا ہو جائے گی اور تمہاری ہوا اُکھڑ جائے گی۔ صبر سے کام لو، یقیناً اللہ صبر کرنے والوں کے ساتھ ہے');
INSERT INTO "ur_maududi" VALUES(8,47,'8:47','اور اُن لوگوں کے سے رنگ ڈھنگ نہ اختیار کرو جو اپنے گھروں سے اِتراتے اور لوگوں کو اپنی شان دِکھاتے ہوئے نکلے اور جن کی روش یہ ہے کہ اللہ کے راستے سے روکتے ہیں، جو کچھ وہ کر رہے ہیں وہ اللہ کی گرفت سے باہر نہیں ہے');
INSERT INTO "ur_maududi" VALUES(8,48,'8:48','ذرا خیال کرو اس وقت کا جب کہ شیطان نے ان لوگوں کے کرتوت ان کی نگاہوں میں خوشنما بنا کر دکھائے تھے اور ان سے کہا تھا کہ آج کوئی تم پر غالب نہیں آ سکتا اور یہ کہ میں تمہارے ساتھ ہوں مگر جب دونوں گروہوں کا آمنا سامنا ہوا تو وہ اُلٹے پاؤں پھر گیا اور کہنے لگا کہ میرا تمہارا ساتھ نہیں ہے، میں وہ کچھ دیکھ رہا ہوں جو تم لوگ نہیں دیکھتے، مجھے خدا سے ڈر لگتا ہے اور خدا بڑی سخت سزا دینے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,49,'8:49','جب کہ منافقین اور وہ سب لوگ جن کے دلوں کو روگ لگا ہوا ہے، کہہ رہے تھے کہ اِن لوگوں کو تو اِن کے دین نے خبط میں مبتلا کر رکھا ہے۔ حالانکہ اگر کوئی اللہ پر بھروسہ کرے تو یقیناً اللہ بڑا زبردست اور دانا ہے');
INSERT INTO "ur_maududi" VALUES(8,50,'8:50','کاش تم اُس حالت کو دیکھ سکتے جبکہ فرشتے مقتول کافروں کی رُوحیں قبض کر رہے تھے وہ ان کے چہروں اور ان کے کولھوں پر ضربیں لگاتے جاتے تھے اور کہتے جاتے تھے“لو اب جلنے کی سزا بھگتو');
INSERT INTO "ur_maududi" VALUES(8,51,'8:51','یہ وہ جزا ہے جس کا سامان تمہارے اپنے ہاتھوں نے پیشگی مہیا کر رکھا تھا، ورنہ اللہ تو اپنے بندوں پر ظلم کرنے والا نہیں ہے "');
INSERT INTO "ur_maududi" VALUES(8,52,'8:52','یہ معاملہ ان کے ساتھ اُسی طرح پیش آیا جس طرح آلِ فرعون اور ان سے پہلے کے دُوسرے لوگوں کے ساتھ پیش آتا رہا ہے کہ انہوں نے اللہ کی آیات کو ماننے سے انکار کیا اور اللہ نے ان کے گناہوں پر انہیں پکڑ لیا اللہ قوت رکھتا ہے اور سخت سزا دینے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,53,'8:53','یہ اللہ کی اِس سنت کے مطابق ہوا کہ وہ کسی نعمت کو جو اس نے کسی قوم کو عطا کی ہو اُس وقت تک نہیں بدلتا جب تک کہ وہ قوم خود اپنے طرزِ عمل کو نہیں بدل دیتی۔ اللہ سب کچھ سُننے اور جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,54,'8:54','آلِ فرعون اور ان سے پہلے کی قوموں کے ساتھ جو کچھ پیش آیا وہ اِسی ضابطہ کے مطابق تھا انہوں نے اپنے رب کی آیات کو جھٹلایا تب ہم نے ان کے گناہوں کی پاداش میں انہیں ہلاک کیا اور آل فرعون کو غرق کر دیا یہ سب ظالم لوگ تھے');
INSERT INTO "ur_maududi" VALUES(8,55,'8:55','یقیناً اللہ کے نزدیک زمین پر چلنے والی مخلوق میں سب سے بدتر وہ لوگ ہیں جنہوں نے حق کو ماننے سے انکار کر دیا پھر کسی طرح وہ اسے قبول کرنے پر تیار نہیں');
INSERT INTO "ur_maududi" VALUES(8,56,'8:56','(خصوصاً) ان میں سے وہ لوگ جن کے ساتھ تُو نے معاہدہ کیا پھر وہ ہر موقع پر اس کو توڑتے ہیں اور ذرا خدا کا خوف نہیں کرتے۔ ');
INSERT INTO "ur_maududi" VALUES(8,57,'8:57','پس اگر یہ لوگ تمہیں لڑائی میں مل جائیں تو ان کی ایسی خبر لو کہ ان کے بعد جو دوسرے لوگ ایسی روش اختیار کرنے والے ہوں ان کے حواس باختہ ہو جائیں۔ توقع ہے کہ بد عہدوں کے اِس انجام سے وہ سبق لیں گے');
INSERT INTO "ur_maududi" VALUES(8,58,'8:58','اور اگر کبھی تمہیں کسی قوم سے خیانت کا اندیشہ ہو تو اس کے معاہدے کے عَلانیہ اس کے آگے پھینک دو ، یقیناً اللہ خائنوں کو پسند نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(8,59,'8:59','منکرینِ حق اس غلط فہمی میں نہ رہیں کہ وہ بازی لے گئے، یقیناً وہ ہم کو ہرا نہیں سکتے');
INSERT INTO "ur_maududi" VALUES(8,60,'8:60','اور تم لوگ ، جہاں تک تمہارا بس چلے، زیادہ سے زیادہ طاقت اور تیار بندھے رہنے والے گھوڑے اُن کے مقابلہ کے لیے مہیّا رکھو تاکہ اس کے ذریعہ سے اللہ کے اپنے دشمنوں کو اور ان دوسرے اعداء کو خوف زدہ کر دو جنہیں تم نہیں جانتے مگر اللہ جانتا ہے۔ اللہ کی راہ میں جو کچھ تم خرچ کرو گے اس کا پورا پورا بدل تمہاری طرف پلٹایا جائے گا اور تمہارے ساتھ ہر گز ظلم نہ ہو گا');
INSERT INTO "ur_maududi" VALUES(8,61,'8:61','اور اے نبیؐ، اگر دشمن صلح و سلامتی کی طرف مائل ہوں تو تم بھی اس کے لیے آمادہ ہو جاؤ اور اللہ پر بھروسہ کرو، یقیناً وہی سننے اور جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,62,'8:62','اور اگر وہ دھوکے کی نیت رکھتے ہوں تو تمہارے لیے اللہ کافی ہے۔ وہی تو ہے جس نے اپنی مدد سے اور مومنوں کے ذریعہ سے تمہاری تائید کی');
INSERT INTO "ur_maududi" VALUES(8,63,'8:63','اور مومنوں کے دل ایک دوسرے کے ساتھ جوڑ دیے۔ تم رُوئے زمین کی ساری دولت بھی خرچ کر ڈالتے تو اِن لوگوں کے دل نہ جوڑ سکتے تھے مگر وہ اللہ ہے جس نے ان لوگوں کے دل جوڑے ، یقیناً وہ بڑا زبردست اور دانا ہے');
INSERT INTO "ur_maududi" VALUES(8,64,'8:64','اے نبیؐ، تمہارے لیے اور تمہارے پیرو اہلِ ایمان کے لیے تو بس اللہ کافی ہے');
INSERT INTO "ur_maududi" VALUES(8,65,'8:65','اے نبی ؐ ، مومنوں کو جنگ پر اُبھارو۔ اگر تم میں سے بیس آدمیں صابر ہوں تو وہ دو سو پر غالب آئیں گے اور اگر سو آدمی ایسے ہوں تو منکرینِ حق میں سے ہزار آدمیوں پر بھاری رہیں گے کیونکہ وہ ایسے لوگ ہیں جو سمجھ نہیں رکھتے۔ ');
INSERT INTO "ur_maududi" VALUES(8,66,'8:66','اچھا، اب اللہ نے تمہارا بوجھ ہلکا کیا اور اسے معلوم ہوا کہ ابھی تم میں کمزوری ہے، پس اگر تم میں سے سو آدمی صابر ہوں تو وہ دو سو پر اور ہزار آدمی ایسے ہوں تو دو ہزار پر اللہ کے حکم سے غالب آئیں گے، اور اللہ ان لوگوں کے ساتھ ہے جو صبر کرنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(8,67,'8:67','کسی نبی کے لیے یہ زیبا نہیں ہے کہ اس کے پاس قیدی ہوں جب تک کہ وہ زمین میں دشمنوں کو اچھی طرح کچل نہ دے تم لوگ دنیا کے فائدے چاہتے ہو، حالانکہ اللہ کے پیشِ نظر آخرت ہے، اور اللہ غالب اور حکیم ہے');
INSERT INTO "ur_maududi" VALUES(8,68,'8:68','اگر اللہ کا نوشتہ پہلے نہ لکھا جا چکا ہوتا تو جو کچھ تم لوگوں نے لیا ہے اس کی پاداش میں تم کو بڑی سزا دی جاتی');
INSERT INTO "ur_maududi" VALUES(8,69,'8:69','پس جو کچھ تم نے مال حاصل کیا ہے اسے کھاوٴ کہ وہ حلال اور پاک ہے اور اللہ سے ڈرتے رہو ، یقیناً اللہ درگزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,70,'8:70','اے نبیؐ، تم لوگوں کے قبضہ میں جو قیدی ہیں ان سے کہو اگر اللہ کو معلوم ہوا کہ تمہارے دلوں میں کچھ خیر ہے تو وہ تمہیں اُس سے بڑھ چڑھ کر دے گا جو تم سے لیا گیا ہے اور تمہاری خطائیں معاف کرے گا، اللہ درگزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(8,71,'8:71','لیکن اگر وہ تیرے ساتھ خیانت کا ارادہ رکھتے ہیں تو اس سے پہلے وہ اللہ کے ساتھ خیانت کر چکے ہیں، چنانچہ اسی کی سزا اللہ نے انہیں دی کہ وہ تیرے قابو میں آ گئے، اللہ سب کچھ جانتا اور حکیم ہے');
INSERT INTO "ur_maududi" VALUES(8,72,'8:72','جن لوگوں نے ایمان قبول کیا اور ہجرت کی اور اللہ کی راہ میں اپنی جانیں لڑائیں اور اپنے مال کھپائے ، اور جن لوگوں نے ہجرت کرنے والوں کو جگہ دی اور ان کی مدد کی، وہی دراصل ایک دوسرے کے ولی ہیں۔ رہے وہ لوگ جو ایمان تو لے آئے مگرہجرت کر کے (دارالاسلام میں) آنہیں گئے تو ان سے تمہارا ولایت کا کوئی تعلق نہیں ہے جب تک کہ وہ ہجرت کر کے نہ آجائیں۔ ہاں اگر وہ دین کےمعاملہ میں تم سے مدد مانگیں تو ان کی مدد کرنا تم پر فرض ہے، لیکن کسی ایسی قوم کے خلاف نہیں جس سے تمہارا معاہدہ ہو۔ جو کچھ تم کرتے ہو اللہ اُسے دیکھتا ہے');
INSERT INTO "ur_maududi" VALUES(8,73,'8:73','جو لوگ منکرِ حق ہیں وہ ایک دوسرے کی حمایت کرتے ہیں۔ اگر تم یہ نہ کرو گے تو زمین میں فتنہ اور بڑا فساد بر پا ہو گا۔ ');
INSERT INTO "ur_maududi" VALUES(8,74,'8:74','جو لوگ ایمان لائے اور جنہوں نے اللہ کی راہ میں گھر بار چھوڑے اور جدوجہد کی اور جنہوں نے پناہ دی اور مدد کی وہی سچے مومن ہیں ان کے لیے خطاؤں سے درگزر ہے اور بہترین رزق ہے');
INSERT INTO "ur_maududi" VALUES(8,75,'8:75','اور جو لوگ بعد میں ایمان لائے اور ہجرت کر کے آگئے اور تمہارے ساتھ مل کر جدوجہد کرنے لگے وہ بھی تم ہی میں شامل ہیں۔ مگر اللہ کی کتاب میں خون کے رشتہ دار ایک دوسرے کے زیادہ حقدار ہیں، یقیناً اللہ ہر چیز کو جانتا ہے');
INSERT INTO "ur_maududi" VALUES(9,1,'9:1',' اِعلان براٴت ہے اللہ اور اُس کے رسُول کی طرف سے اُن مشرکین کو جن سے تم نے معاہدے کیے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(9,2,'9:2','پس تم لوگ ملک میں چار مہینے اور چل پھر لو اور جان رکھو کہ تم اللہ کو عاجز کرنے والے نہیں ہو، اور یہ کہ اللہ منکرینِ حق کو رُسوا کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(9,3,'9:3','اطلاعِ عام ہے اللہ اور کے رسول ؐ کی طرف سے حجِ اکبر کے دن تمام لوگوں کے لیے کہ اللہ مشرکین سے بری الذمّہ ہے اور اُس کا رسول ؐ بھی۔ اب اگر تم لوگ توبہ کر لو تو تمہارے ہی لیے بہتر ہے اور جو منہ پھیرتے ہو تو خوب سمجھ لو کہ تم اللہ کو عاجز کرنے والے نہیں ہو۔ اور اے نبی ؐ ، انکار کرنے والوں کو سخت عذاب کی خوشخبری سُنا دو');
INSERT INTO "ur_maududi" VALUES(9,4,'9:4','بجز اُن مشرکین کے جن سے تم نے معاہدے کیے پھر اُنہوں نے اپنے عہد کو پورا کرنے میں تمہارے ساتھ کوئی کمی نہیں کی اور نہ تمہارے خلاف کسی کی مدد کی، تو ایسے لوگوں کے ساتھ تم بھی مُدتِ معاہدہ تک وفا کرو کیونکہ اللہ متقیوں ہی کو پسند کرتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(9,5,'9:5','پس جب حرام مہینے گزر جائیں تو مشرکین کو قتل کرو جہاں پاوٴ اور انہیں پکڑو اور گھیرو اور ہر گھات میں اُن کی خبر لینے کے لیے بیٹھو۔ پھر اگر وہ توبہ کر لیں اور نماز قائم کریں اور زکوٰة دیں تو انہیں چھوڑ دو۔ اللہ درگزر کرنے والااور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(9,6,'9:6','اور اگر مشرکین میں سے کوئی شخص پناہ مانگ کر تمہارے پاس آنا چاہے ( تاکہ اللہ کا کلام سُنے) تو اُسے پناہ دے دو یہاں تک کہ وہ اللہ کا کلام سُن لے۔ پھر اُسے اس کے مامن تک پہنچا دو، یہ اِس لیے کرنا چاہیے کہ یہ لوگ علم نہیں رکھتے۔ ');
INSERT INTO "ur_maududi" VALUES(9,7,'9:7','اِن مشرکین کے لیے اللہ اور اس کے رسول کے نزدیک کوئی عہد آخر کیسے ہو سکتا ہے۔۔۔۔بجز اُن لوگوں کے جن سے تم نے مسجدِ حرام کے پاس معاہدہ کیا تھا، تو جب تک وہ تمہارے ساتھ سیدھے رہیں تم بھی ان کے ساتھ سیدھے رہو کیونکہ اللہ متقیوںکو پسند کرتا ہے');
INSERT INTO "ur_maududi" VALUES(9,8,'9:8','مگر اِن کے سوا دوسرے مشرکین کے ساتھ کوئی عہد کیسے ہو سکتا ہے جبکہ اُن کا حال یہ ہے کہ تم پر قابو پا جائیں تو نہ تمہارے معاملہ میں کسی قرابت کا لحاظ کریں نہ کسی معاہدہ کی ذمہ داری کا۔ وہ اپنی زبانوں سے تم کو راضی کرنے کی کوشش کرتے ہیں مگر دِل ان کے انکار کرتے ہیں اور ان میں سے اکثر فاسق ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(9,9,'9:9','انہوں نے اللہ کی آیات کے بدلے تھوڑی سی قیمت قبول کر لی پھر اللہ کے راستے میں سدِّ راہ بن کر کھڑے ہوگئے، بہت بُرے کرتُوت تھے جو یہ لوگ کرتے رہے');
INSERT INTO "ur_maududi" VALUES(9,10,'9:10','کسی مومن کے معاملہ میں نہ یہ قرابت کا لحاظ کرتے ہیں اور نہ کسی عہد کی ذمہ داری کا اور زیادتی ہمیشہ انہی کی طرف سے ہوئی ہے');
INSERT INTO "ur_maududi" VALUES(9,11,'9:11','پس اگر یہ توبہ کر لیں اور نماز قائم کریں اور زکوٰة دیں تو تمہارے دینی بھائی ہیں اور جاننے والوں کے لیے ہم اپنے احکام واضح کیے دیتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(9,12,'9:12','اور اگر عہد کرنے کے بعد یہ پھر اپنی قسموں کو توڑ ڈالیں اور تمہارے دین پر حملے کرنے شروع کر دیں تو کُفر کے علمبرداروں سے جنگ کرو کیونکہ ان کی قسموں کا کوئی اعتبار نہیں۔ شاید کہ (پھر تلوار ہی کے زور سے) وہ باز آئیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(9,13,'9:13','کیا تم نہ لڑو گے ایسے لوگوں سے جو اپنے عہد توڑتے رہے ہیں اور جنہوں نے رسُول کو ملک سے نکال دینے کا قصد کیا تھا اور زیادتی کی ابتدا کرنے والے وہی تھے؟ کیا تم ان سے ڈرتے ہو؟ اگر تم مومن ہو تو اللہ اِس کا زیادہ مستحق ہے کہ اس سے ڈرو');
INSERT INTO "ur_maududi" VALUES(9,14,'9:14','ان سے لڑو، اللہ تمہارے ہاتھوں سے ان کو سزا دلوائے گا اور انہیں ذلیل و خوار کرے گا اور ان کے مقابلہ میں تمہاری مدد کرے گا اور بہت سے مومنوں کے دل ٹھنڈے کرے گا');
INSERT INTO "ur_maududi" VALUES(9,15,'9:15','اور ان کے قلوب کی جلن مٹا دے گا، اور جسے چاہے گا توبہ کی توفیق بھی دے گا۔ اللہ سب کچھ جاننے والا اور دانا ہے');
INSERT INTO "ur_maududi" VALUES(9,16,'9:16','کیا تم لوگوں نے یہ سمجھ رکھا ہے کہ یونہی چھوڑ دیے جاوٴ گے حالانکہ ابھی اللہ نے یہ تو دیکھا ہی نہیں کہ تم میں سے کون وہ لوگ ہیں جنہوں نے (اس کی راہ میں) جا ں فشانی کی اور اللہ اور رسول اور مومنین کے سوا کسی کو جگری دوست نہ بنایا ، جو کچھ تم کرتے ہو اللہ اس سے با خبر ہے');
INSERT INTO "ur_maududi" VALUES(9,17,'9:17','مشرکین کا یہ کام نہیں ہے کہ وہ اللہ کی مسجدوں کے مجاور و خادم بنیں در انحا لیکہ اپنے اوپر وہ خود کفر کی شہادت د ے رہے ہیں۔ ان کے تو سارے اعمال ضائع ہوگئے اور جہنّم میں انہیں ہمیشہ رہنا ہے');
INSERT INTO "ur_maududi" VALUES(9,18,'9:18','اللہ کی مسجدوں کے آباد کار (مجاور و خادم) تو وہی لوگ ہوسکتے ہیں جو اللہ اور روز آخر کو مانیں اور نما ز قائم کریں، زکوٰۃ دیں اور اللہ کے سوا کسی سے نہ ڈریں انہی سے یہ توقع ہے کہ سیدھی راہ چلیں گے');
INSERT INTO "ur_maududi" VALUES(9,19,'9:19','کیا تم لوگوں نے حاجیوں کو پانی پلانے اور مسجدِ حرام کی مجاوری کرنے کو اس شخص کے کام کے برابر ٹھہرا لیا ہے جو ایمان لایا اللہ پر اور روزِ آخر پر اور جس نے جانفشانی کی اللہ کی راہ میں؟ اللہ کے نزدیک تو یہ دونوں برابر نہیں ہیں اور اللہ ظالموں کی رہنمائی نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(9,20,'9:20','اللہ کے ہاں تو انہی لوگوں کا درجہ بڑا ہے جو ایمان لائے اور جنہوں نے اس کی راہ میں گھر بار چھوڑے اور جان و مال سے جہاد کیا وہی کامیاب ہیں');
INSERT INTO "ur_maududi" VALUES(9,21,'9:21','ان کا رب انہیں اپنی رحمت اور خوشنودی اور ایسی جنتوں کی بشارت دیتا ہے جہاں ان کے لیے پائیدار عیش کے سامان ہیں');
INSERT INTO "ur_maududi" VALUES(9,22,'9:22','ان میں وہ ہمیشہ رہیں گے یقیناً اللہ کے پاس خدمات کا صلہ دینے کو بہت کچھ ہے');
INSERT INTO "ur_maududi" VALUES(9,23,'9:23','اے لوگو جو ایمان لائے ہو، اپنے باپوں اور بھائیوں کو بھی اپنا رفیق نہ بناؤ اگر وہ ایمان پر کفر کو ترجیح دیں تم میں سے جو ان کو رفیق بنائیں گے وہی ظالم ہوں گے');
INSERT INTO "ur_maududi" VALUES(9,24,'9:24','اے نبی ؐ ، کہہ دو کہ اگر تمہارے باپ اور تمہارے بیٹے، اور تمہارے بھائی اور تمہاری بیویاں اور تمہارے عزیز و اقارب اور تمہارے وہ مال جو تم نے کمائے ہیں ، اور تمہارے وہ کاروبار جن کے ماند پڑ جانے کاتم کو خوف ہے اور تمہارے وہ گھر جو تم کو پسند ہیں ، تم کو اللہ اور اس کے رسُول ؐ اور اس کی راہ میں جہاد سے عزیز تر ہیں تو انتظار کرو یہاں تک کہ اللہ اپنا فیصلہ تمہارے سامنے لے آئے ، اور اللہ فاسق لوگوں کی رہنمائی نہیں کیا کرتا');
INSERT INTO "ur_maududi" VALUES(9,25,'9:25','اللہ اس سے پہلے بہت سے مواقع پر تمہاری مدد کر چکا ہے۔ ابھی غَز وہٴ حُنَین کے روز (اُس کی دستگیری کی شان تم دیکھ چکے ہو )۔ اس روز تمہیں اپنی کثرتِ تعداد کا غَرّہ تھا مگر وہ تمہارے کچھ کام نہ آئی اور زمین اپنی وسعت کے باوجود تم پر تنگ ہو گئی اور تم پیٹھ پھیر کر بھاگ نکلے');
INSERT INTO "ur_maududi" VALUES(9,26,'9:26','پھر اللہ نے اپنی سکینت اپنے رسول پر اور مومنین پر نازل فرمائی اور وہ لشکر اتارے جو تم کو نظر نہ آتے تھے اور منکرین حق کو سزا دی کہ یہی بدلہ ہے اُن لوگوں کے لیے جو حق کا انکار کریں');
INSERT INTO "ur_maududi" VALUES(9,27,'9:27','(تم یہ بھی دیکھ چُکے ہو کہ) اس طرح سزا دینے کے بعد اللہ جس کو چاہتا ہے توبہ کی توفیق بھی بخش دیتا ہے، اللہ درگزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(9,28,'9:28','اے ایمان لانے والو، مشرکین ناپاک ہیں لہٰذا اس سال کے بعد یہ مسجدِ حرام کے قریب نہ پھٹکنے پائیں۔ اور اگر تمہیں تنگ دستی کا خوف ہے تو بعید نہیں کہ اللہ چاہے تو تمہیں اپنے فضل سے غنی کر دے، اللہ علیم و حکیم ہے');
INSERT INTO "ur_maududi" VALUES(9,29,'9:29','جنگ کرو اہلِ کتاب میں سے اُن لوگوں کے خلاف جو اللہ اور روزِ آخر پر ایمان نہیں لاتے اور جو کچھ اللہ اور اس کے رسول ؐ نے حرام قرار دیا ہے اسے حرام نہیں کرتے اور دینِ حق کو اپنا دین نہیں بناتے۔(ان سے لڑو) یہاں تک کہ وہ اپنے ہاتھ سے جزیہ دیں چھوٹے بن کر رہیں۔ ');
INSERT INTO "ur_maududi" VALUES(9,30,'9:30','یہودی کہتے ہیں کہ عُزَیر اللہ کا بیٹا ہے، اور عیسائی کہتے ہیں کہ مسیح اللہ کا بیٹا ہے۔ یہ بے حقیقت باتیں ہوتی ہیں جو وہ اپنی زبانوں سے نکالتے ہیں اُن لوگوں کی دیکھا دیکھی جو ان سے پہلے کُفر میں مبتلا ہوئے تھے۔ خدا کی مار اِن پر، یہ کہاں سے دھوکہ کھا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(9,31,'9:31','انہوں نے اپنے علماء اور درویشوں کو اللہ کے سوا اپنا ربّ بنا لیا ہے اور اسی طرح مسیح اِبنِ مریم کو بھی ، حالانکہ ان کو ایک معبود کے سوا کسی کی بندگی کرنے کا حکم نہیں دیا گیا تھا، وہ جس کے سوا کوئی مستحق ِ عبادت نہیں ، پاک ہے وہ ان مشرکانہ باتوں سے جو یہ لوگ کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(9,32,'9:32','یہ لوگ چاہتے ہیں کہ اللہ کی روشنی کو اپنی پھونکوں سے بجھا دیں مگر اللہ اپنی روشنی کو مکمل کیے بغیر ماننے والا نہیں ہے خواہ کافروں کو یہ کتنا ہی ناگوار ہو');
INSERT INTO "ur_maududi" VALUES(9,33,'9:33','وہ اللہ ہی ہے جس نے اپنے رسول ؐ کو ہدایت اور دینِ حق کے ساتھ بھیجا ہے تاکہ اسے پوری جنسِ دین پر غالب کر دے۔ خواہ مشرکوں کو یہ کتنا ہی ناگوار ہو');
INSERT INTO "ur_maududi" VALUES(9,34,'9:34','اے ایمان لانے والو، اِن اہلِ کتاب کے اکثر علماء اور درویشوں کا حال یہ ہے کہ وہ لوگوں کے مال باطل طریقوں سے کھاتے ہیں اور انہیں اللہ کی راہ سے روکتے ہیں۔ دردناک سزا کی خوشخبری دو ان کو جو سونے اور چاندی جمع کرکے رکھتے ہیں اور انہیں خدا کی راہ میں خرچ نہیں کرتے');
INSERT INTO "ur_maududi" VALUES(9,35,'9:35','ایک دن آئے گا کہ اسی سونے چاندی پر جہنم کی آگ دہکائی جائے گی اور پھر اسی سے ان لوگوں کی پیشانیوں اور پہلوؤں اور پیٹھوں کو داغا جائے گا یہ ہے وہ خزانہ جو تم نے اپنے لیے جمع کیا تھا، لو اب اپنی سمیٹی ہوئی دولت کا مزہ چکھو');
INSERT INTO "ur_maududi" VALUES(9,36,'9:36','حقیقت یہ ہے کہ مہینوں کی تعداد جب سے اللہ نے آسمان و زمین کو پیدا کیا ہے اللہ کے نوشتے میں بارہ ہی ہے ، اور ان میں سے چار مہینے حرام ہیں۔ یہی ٹھیک ضابطہ ہے۔ لہٰذا ان چار مہینوں میں اپنے اوپر ظلم نہ کرو اور مشرکوں سے سب مِل کر لڑو جس طرح وہ سب مل کر تم سے لڑتے ہیں اور جان رکھو کہ اللہ متقیوں ہی کے ساتھ ہے');
INSERT INTO "ur_maududi" VALUES(9,37,'9:37','نَسِی تو کُفر میں ایک مزید کافرانہ حرکت ہے جس سے یہ کافر لوگ گمراہی میں مبتلا کیےجاتے ہیں۔ کسی سال ایک مہینے کو حلال کر لیتے ہیں اور کسی سال اُس کو حرام کر دیتے ہیں ، تاکہ اللہ کے حرام کیے ہوئے مہینوں کی تعداد پوری بھی کر دیں اور اللہ کا حرام کیا ہوا حلال بھی کر لیں۔ ان کے بُرے اعمال ان کے لیے خوشنما بنادیے گئے ہیں اور اللہ منکرینِ حق کو ہدایت نہیں دیا کرتا');
INSERT INTO "ur_maududi" VALUES(9,38,'9:38',' اے لوگو جو ایمان لائے ہو ، تمہیں کیا ہو گیا کہ جب تم سے اللہ کی راہ میں نکلنے کے لیے کہا گیا تو تم زمین سے چمٹ کر رہ گئے؟ کیا تم نے آخرت کے مقابلہ میں دنیا کی زندگی کو پسند کر لیا؟ ایسا ہے تو تمہیں معلوم ہو کہ دُنیوی زندگی کا یہ سب سرو سامان آخرت میں بہت تھوڑا نکلے گا۔ ');
INSERT INTO "ur_maududi" VALUES(9,39,'9:39','تم نہ اُٹھو گے تو خدا تمہیں دردناک سزا دے گا، اور تمہاری جگہ کسی اور گروہ کو اُٹھائے گا، اور تم خدا کا کچھ بھی نہ بگاڑ سکو گے، وہ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(9,40,'9:40','تم نے اگر نبی کی مدد نہ کی تو کچھ پروا نہیں،اللہ اُس کی مدد اُس وقت کر چکا ہے جب کافروں نے اسے نکال دیا تھا، جب وہ صرف دو میں کا دوسرا تھا، جب وہ دونوں غار میں تھے، جب وہ اپنے ساتھی سے کہہ رہا تھا کہ ”غم نہ کر، اللہ ہمارے ساتھ ہے“ ۔ اُس وقت اللہ نے اس پر اپنی طرف سے سکون ِقلب نازل کیا اور اس کی مدد ایسے لشکروں سے کی جو تم کو نظر نہ آتے تھے اور کافروں کا بول نیچا کر دیا۔ اور اللہ کا بول تو اُونچا ہی ہے، اللہ زبر دست اور دانا و بینا ہے');
INSERT INTO "ur_maududi" VALUES(9,41,'9:41','نکلو، خواہ ہلکے ہو یا بوجھل ، اور جہاد کرو اللہ کی راہ میں اپنے مالوں اور اپنی جانوں کے ساتھ ، یہ تمہارے لیے بہتر ہے اگر تم جانو');
INSERT INTO "ur_maududi" VALUES(9,42,'9:42','اے نبی ؐ اگر فائدہ سہل الحصُول ہوتا اور سفر ہلکا ہوتا تو وہ ضرور تمہارے پیچھے چلنے پر آمادہ ہو جاتے، مگر ان پر تو یہ راستہ بہت کٹھن ہو گیا۔ اب وہ خدا کی قسم کھا کھا کر کہیں گے کہ اگر ہم چل سکتے تو یقیناً تمہارے ساتھ چلتے!وہ اپنے آپ کو ہلاکت میں ڈال رہے ہیں۔ اللہ خوب جانتا ہے کہ وہ جھوٹے ہیں');
INSERT INTO "ur_maududi" VALUES(9,43,'9:43','اے نبی ؐ ، اللہ تمہیں معاف کرے، تم نے کیوں انہیں رخصت دے دی؟ (تمہیں چاہیے تھا کہ خود رخصت نہ دیتے) تاکہ تم پر کھُل جاتا کہ کو ن لوگ سچے ہیں اورجھوٹوں کو بھی تم جان لیتے۔ ');
INSERT INTO "ur_maududi" VALUES(9,44,'9:44','جو لوگ اللہ اور روز آخر پر ایمان رکھتے ہیں وہ تو کبھی تم سے یہ درخواست نہ کریں گے کہ انہیں اپنی جان و مال کے ساتھ جہاد کرنے سے معاف رکھا جائے اللہ متقیوں کو خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(9,45,'9:45','ایسی درخواستیں تو صرف وہی لوگ کرتے ہیں جو اللہ اور روزِ آخر پر ایمان نہیں رکھتے، جن کے دلوں میں شک ہے اور وہ اپنے شک ہی میں متردّد ہو رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(9,46,'9:46','اگر واقعی ان کا ارادہ نکلنے کا ہوتا تو وہ اس کے لیے کچھ تیاری کرتے لیکن اللہ کو ان کا اُٹھنا پسند ہی نہ تھا اس لیے اس نے اُنھیں سُست کر دیا اور کہہ دیا کہ بیٹھ رہو بیٹھنے والوں کے ساتھ');
INSERT INTO "ur_maududi" VALUES(9,47,'9:47','اگر وہ تمہارے ساتھ نکلتے تو تمہارے اندر خرابی کے سوا کسی چیز کا اضافہ نہ کرتے وہ تمہارے درمیان فتنہ پردازی کے لیے دوڑ دھوپ کرتے، اور تمہارے گروہ کا حال یہ ہے کہ ابھی اُس میں بہت سے ایسے لوگ موجود ہیں جو اُن کی باتیں کان لگا کر سنتے ہیں، اللہ اِن ظالموں کو خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(9,48,'9:48','اس سے پہلے بھی اِن لوگوں نے فتنہ انگیزی کی کوششیں کی ہیں اور تمہیں ناکام کرنے کے لیے یہ ہر طرح کی تدبیروں کا الٹ پھیر کر چکے ہیں یہاں تک کہ ان کی مرضی کے خلاف حق آ گیا اور اللہ کا کام ہو کر رہا');
INSERT INTO "ur_maududi" VALUES(9,49,'9:49','ان میں سے کوئی ہے جو کہتا ہے کہ ”مجھے رُخصت دے دیجیے اور مجھے فتنے میں نہ ڈالیے“ ۔۔۔۔ سُن رکھو! فتنے ہی میں تو یہ لوگ پڑے ہوئے ہیں اور جہنم نے اِن کافروں کو گھیر رکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(9,50,'9:50','تمہارا بھلا ہوتا ہے تو انہیں رنج ہوتا ہے اور تم پر کوئی مصیبت آتی ہے تو یہ منہ پھیر کر خوش خوش پلٹتے ہیں اور کہتے جاتے ہیں کہ اچھا ہوا ہم نے پہلے ہی اپنا معاملہ ٹھیک کر لیا تھا');
INSERT INTO "ur_maududi" VALUES(9,51,'9:51','ان سے کہو ”ہمیں ہر گز کوئی (بُرائی یا بھلائی) نہیں پہنچتی مگر وہ جو اللہ نے ہمارے لیے لکھ دی ہے۔ اللہ ہی ہمارا مولیٰ ہے، اور اہلِ ایمان کو اسی پر بھروسہ کرنا چاہیے۔ “');
INSERT INTO "ur_maududi" VALUES(9,52,'9:52','ان سے کہو ”تم ہمارے معاملہ میں جس چیز کے منتظر ہو وہ اس کے سوا اور کیا ہے کہ دو بھلائیوں میں سے ایک بھلائی ہے۔ اور ہم تمہارے معاملہ میں جس چیز کے منتظر ہیں وہ یہ ہے کہ اللہ خود تم کو سزا دیتا ہے یا ہمارے ہاتھوں دلواتا ہے؟ اچھا تو اب تم انتظار کرو اور ہم بھی تمہارے ساتھ منتظر ہیں۔“');
INSERT INTO "ur_maududi" VALUES(9,53,'9:53','ان سے کہو ”تم اپنے مال خواہ راضی خوشی خرچ کرو یا بکراہت ، بہرحال وہ قبول نہ کیے جائیں گے کیونکہ تم فاسق لوگ ہو۔“');
INSERT INTO "ur_maududi" VALUES(9,54,'9:54','ان کے دیے ہوئے مال قبول نہ ہونے کی کوئی وجہ اس کے سوا نہیں ہے کہ انہوں نے اللہ اور اس کے رسول سے کفر کیا ہے، نماز کے لیے آتے ہیں تو کسمساتے ہوئے آتے ہیں اور راہ خدا میں خرچ کرتے ہیں تو بادل ناخواستہ خرچ کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(9,55,'9:55','اِن کے مال و دولت اور اِن کی کثرتِ اولاد کو دیکھ کر دھوکا نہ کھاوٴ، اللہ تو یہ چاہتا ہے کہ اِنہی چیزوں کے ذریعہ سے ان کو دنیا کی زندگی میں بھی مبتلائے عذاب کرے اور یہ جان بھی دیں تو اِنکارِ حق ہی کی حالت میں دیں۔ ');
INSERT INTO "ur_maududi" VALUES(9,56,'9:56','وہ خدا کی قسم کھا کھا کر کہتے ہیں کہ ہم تمہی میں سے ہیں، حالانکہ وہ ہرگز تم میں سے نہیں ہیں اصل میں تو وہ ایسے لوگ ہیں جو تم سے خوف زدہ ہیں');
INSERT INTO "ur_maududi" VALUES(9,57,'9:57','اگر وہ کوئی جائے پناہ پا لیں یا کوئی کھوہ یا گھُس بیٹھنے کی جگہ، تو بھاگ کر اُس میں جا چھُپیں۔ ');
INSERT INTO "ur_maududi" VALUES(9,58,'9:58','اے نبی ؐ ، ان میں سے بعض لوگ صدقات کی تقسیم میں تم پر اعتراضات کرتے ہیں۔اگر اس مال میں سے انہیں کچھ دے دیا جائے تو خوش ہو جائیں ، اور نہ دیا جائے تو بگڑنے لگتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(9,59,'9:59','کیا اچھا ہو تا کہ اللہ اور رسول ؐ نے جو کچھ بھی انہیں دیا تھا اس پر وہ راضی رہتے اور کہتے کہ ”اللہ ہمارے لیے کافی ہے، وہ اپنے فضل سے ہمیں اور بہت کچھ دے گا اور اس کا رسول بھی ہم پر عنایت فرمائے گا، ہم اللہ ہی کی طرف نظر جمائے ہوئے ہیں۔ “');
INSERT INTO "ur_maududi" VALUES(9,60,'9:60','یہ صدقات تو دراصل فقیروں اور مسکینوں کے لیے ہیں اور اُن لوگوں کے لیے جو صدقات کے کام پر مامور ہوں ، اور اُن کے لیے جن کی تالیفِ قلب مطلوب ہو۔ نیز یہ گردنوں کے چھُڑانے اور قرضداروں کی مدد کرنے میں اور راہِ خدا میں اور مسافر نوازی میں استعمال کرنے کے لیے ہیں۔ ایک فریضہ ہے اللہ کی طرف سے اور اللہ سب کچھ جاننے والا اور دانا و بینا ہے');
INSERT INTO "ur_maududi" VALUES(9,61,'9:61','ان میں کچھ لوگ ہیں جو اپنی باتوں سے نبی کو دُکھ دیتے ہیں اور کہتے ہیں کہ یہ شخص کانوں کا کچا ہے۔ کہو ”وہ تمہاری بھلائی کے لیے ایسا ہے ، اللہ پر ایمان رکھتا ہے اور اہلِ ایمان پر اعتماد کرتا ہے اور سراسر رحمت ہے ان لوگوں کےلیے جو تم میں سے ایماندار ہیں۔ اور جو لوگ اللہ کے رسول کو دُکھ دیتے ہیں ان کے لیے دردناک سزا ہے۔“');
INSERT INTO "ur_maududi" VALUES(9,62,'9:62','یہ لوگ تمہارے سامنے قسمیں کھاتے ہیں تاکہ تمہیں راضی کریں، حالانکہ اگر یہ مومن ہیں تو اللہ اور رسول اس کے زیادہ حق دار ہیں کہ یہ ان کو راضی کرنے کی فکر کریں');
INSERT INTO "ur_maududi" VALUES(9,63,'9:63','کیا انہیں معلوم نہیں ہے کہ جو اللہ اور اس کے رسول کا مقابلہ کرتا ہے اس کے لیے دوزخ کی آگ ہے جس میں وہ ہمیشہ رہے گا یہ بہت بڑی رسوائی ہے');
INSERT INTO "ur_maududi" VALUES(9,64,'9:64','یہ منافق ڈر رہے ہیں کہ کہیں مسلمانوں پر کوئی ایسی سُورت نازل نہ ہو جائے جو ان کے دلوں کے بھید کھول کر رکھ دے۔ اے نبی ؐ ، ان سے کہو ”اور مذاق اُڑاوٴ، اللہ اُس چیز کو کھول دینے والا ہے جس کے کھُل جانے سے تم ڈرتے ہو۔“');
INSERT INTO "ur_maududi" VALUES(9,65,'9:65','اگر ان سے پوچھو کہ تم کیا باتیں کر رہے تھے ، تو جھٹ کہہ دیں گے کہ ہم تو ہنسی مذاق اور دل لگی کر رہے تھے۔ ان سے کہو”کیا تمہاری ہنسی دل لگی اللہ اور اُس کی آیات اور اس کے رسول ہی کے ساتھ تھی؟');
INSERT INTO "ur_maududi" VALUES(9,66,'9:66','اب عذرات نہ تراشو، تم نے ایمان لانے کے بعد کُفر کیا ہے، اگر ہم نے تم میں سے ایک گروہ کو معاف کر بھی دیا تو دوسرے گروہ کو تو ہم ضرور سزا دیں گے کیونکہ وہ مجرم ہے۔ “');
INSERT INTO "ur_maududi" VALUES(9,67,'9:67','منافق مرد اور منافق عورتیں سب ایک دوسرے کے ہم رنگ ہیں۔ بُرائی کا حکم دیتے ہیں اور بھلائی سے منع کرتے ہیں اور اپنے ہاتھ خیر سے روکے رکھتے ہیں۔ یہ اللہ کو بھُول گئےتو اللہ نے بھی انہیں بھلا دیا۔ یقیناً یہ منافق ہی فاسق ہیں');
INSERT INTO "ur_maududi" VALUES(9,68,'9:68','ان منافق مردوں اور عورتوں اور کافروں کے لیے اللہ نے آتش دوزخ کا وعدہ کیا ہے جس میں وہ ہمیشہ رہیں گے، وہی ان کے لیے موزوں ہے ان پر اللہ کی پھٹکار ہے اور ان کے لیے قائم ر ہنے والا عذاب ہے');
INSERT INTO "ur_maududi" VALUES(9,69,'9:69',' تم لوگوں کے رنگ ڈھنگ وہی ہیں جو تمہارے پیش رووٴں کے تھے۔ وہ تم سے زیادہ زور آور اور تم سے بڑھ کر مال اور اولاد والے تھے۔ پھر انہوں نے دنیا میں اپنے حصّہ کے مزے لُوٹ لیے اور تم نے بھی اپنے حصّہ کے مزے اسی طرح لُوٹے جیسے انہوں نے لُوٹے تھے، اور ویسی ہی بحثوں میں تم بھی پڑے جیسی بحثوں میں وہ پڑے تھے، سوا ان کا انجام یہ ہوا کہ دنیا اور آخرت میں ان کا سب کیا دھرا ضائع ہو گیا اور وہی خسارے میں ہیں');
INSERT INTO "ur_maududi" VALUES(9,70,'9:70',' کیا اِن لوگوں کو اپنے پیش رووٴں کی تاریخ نہیں پہنچی؟ نوح ؑ کی قوم، عاد ، ثمود، ابراہیم ؑ کی قوم، مدیَن کے لوگ اور وہ بستیاں جنہیں اُلٹ دیا گیا ۔ اُن کے رسُول ان کے پاس کھُلی کھُلی نشانیاں لے کر آئے، پھر یہ اللہ کا کام نہ تھا کہ ان پر ظلم کرتامگر وہ آپ ہی اپنے اوپر ظلم کرنے والے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(9,71,'9:71','مومن مر د اور مومن عورتیں، یہ سب ایک دوسرے کے رفیق ہیں، بھلائی کا حکم دیتے اور بُرائی سے روکتے ہیں، تماز قائم کرتے ہیں، زکوٰة دیتے ہیں اور اس کے رسُول ؐ کی اطاعت کرتے ہیں۔ یہ وہ لوگ ہیں جن پر اللہ کی رحمت نازل ہو کر رہے گی، یقیناً اللہ سب پر غالب اور حکیم و دانا ہے');
INSERT INTO "ur_maududi" VALUES(9,72,'9:72','ان مومن مردوں اور عورتوں سے اللہ کا وعدہ ہے کہ انہیں ایسے باغ دے گا جن کے نیچے نہریں بہتی ہوں گی اور وہ ان میں ہمیشہ رہیں گے ان سدا بہار باغوں میں ان کے لیے پاکیزہ قیام گاہیں ہوں گی، اور سب سے بڑھ کر یہ کہ اللہ کی خوشنودی انہیں حاصل ہوگی یہی بڑی کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(9,73,'9:73',' اے نبیؐ ، کفّار اور منافقین دونوں کا پوری قوت سے مقابلہ کرو اور ان کے ساتھ سختی سے پیش آوٴ، آخرِ کار ان کا ٹھکانا جہنّم ہے اور وہ بد ترین جائے قرار ہے');
INSERT INTO "ur_maududi" VALUES(9,74,'9:74','یہ لوگ خدا کی قسم کھا کھا کر کہتے ہیں کہ ہم نے وہ بات نہیں کی، حالانکہ انہوں نے ضرور وہ کافرانہ بات کہی ہے۔ وہ اسلام لانے کے بعد کفر کے مرتکب ہوئے اور انہوں نے وہ کچھ کرنے کا ارادہ کیا جسے کر نہ سکے۔ یہ ان کا سارا غصّہ اسی بات پر ہے تاکہ اللہ اور اس کے رسول نے اپنے فضل سے ان کو غنی کر دیا ہے ! اب اگر یہ اپنی اس روش سے باز آجائیں تو انہی کے لیےبہتر ہے اور اگر یہ باز نہ آئے تو اللہ ان کو نہایت دردناک سزا دے گا ، دنیا میں بھی اور آخرت میں بھی، اور زمین میں کوئی نہیں جو اِن کا حمایتی اور مددگار ہو');
INSERT INTO "ur_maududi" VALUES(9,75,'9:75','ان میں سے بعض ایسے بھی ہیں جنہوں نے اللہ سے عہد کیا تھا کہ اگر اس نے اپنے فضل سے ہم کو نوازا تو ہم خیرات کریں گے اور صالح بن کر رہیں گے');
INSERT INTO "ur_maududi" VALUES(9,76,'9:76','مگر جب اللہ نے اپنے فضل سے ان کو دولتمند کر دیا تو وہ بُخل پر اُتر آئے اور اپنے عہد سے ایسے پھرے کہ اُنہیں اس کی پروا تک نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(9,77,'9:77','نتیجہ یہ نکلا کہ ان کی اِس بد عہدی کی وجہ سے جو انہوں نے اللہ کے ساتھ کی، اور اس جھوٹ کی وجہ سے جو وہ بولتے رہے، اللہ نے ان کے دلوں میں نفاق بٹھا دیا جو اس کے حضور ان کی پیشی کے دن تک ان کا پیچھا نہ چھوڑے گا');
INSERT INTO "ur_maududi" VALUES(9,78,'9:78','کیا یہ لوگ جانتے نہیں ہیں کہ اللہ کو ان کے مخفی راز اور ان کی پوشیدہ سرگوشیاں تک معلوم ہیں اور وہ تمام غیب کی باتوں سے پوری طرح باخبر ہے؟');
INSERT INTO "ur_maududi" VALUES(9,79,'9:79','(وہ خوب جانتا ہے اُن کنجوس دولت مندوں کو) جو برضا و رغبت دینے والے اہلِ ایمان کی مالی قربانیوں پر باتیں چھانٹتے ہیں اور ان لوگوں کا مذاق اُڑاتے ہیں جن کے پاس (راہِ خدا مٰں دینے کے لیے) اُس کے سوا کچھ نہیں ہے جو وہ اپنے اوپر مشقت برداشت کر کے دیتے ہیں۔اللہ اِن مذاق اُڑانے والوں کا مذاق اُڑاتا ہے اور ان کے لیے درد ناک سزا ہے');
INSERT INTO "ur_maududi" VALUES(9,80,'9:80','اے نبیؐ، تم خواہ ایسے لوگوں کے لیے معافی کی درخواست کرو یا نہ کرو، اگر تم ستر مرتبہ بھی انہیں معاف کردینے کی درخواست کرو گے تو اللہ انہیں ہرگز معاف نہ کرے گا اس لیے کہ انہوں نے اللہ اور اس کے رسول کے ساتھ کفر کیا ہے، اور اللہ فاسق لوگوں کو راہ نجات نہیں دکھاتا');
INSERT INTO "ur_maududi" VALUES(9,81,'9:81','جن لوگوں کو پیچھے رہ جانے کی اجازت دے دی گئی تھی وہ اللہ کے رسول کا ساتھ نہ دینے اور گھر بیٹھے رہنے پر خوش ہوئے اور انہیں گوارا نہ ہوا کہ اللہ کی راہ میں جان و مال سے جہاد کریں انہوں نے لوگوں سے کہا کہ "اس سخت گرمی میں نہ نکلو" ان سے کہو کہ جہنم کی آگ اس سے زیادہ گرم ہے، کاش انہیں اس کا شعور ہوتا');
INSERT INTO "ur_maududi" VALUES(9,82,'9:82','اب چاہیے کہ یہ لوگ ہنسنا کم کریں اور روئیں زیادہ، اس لیے کہ جو بدی یہ کماتے رہے ہیں اس کی جزا ایسی ہی ہے (کہ انہیں اس پر رونا چاہیے)');
INSERT INTO "ur_maududi" VALUES(9,83,'9:83','اگر اللہ ان کے درمیان تمہیں واپس لے جائے اور آئندہ ان میں سے کوئی گروہ جہاد کے لیے نکلنے کی تم سے اجازت مانگے تو صاف کہہ دینا کہ، "اب تم میرے ساتھ ہرگز نہیں چل سکتے اور نہ میری معیت میں کسی دشمن سے لڑ سکتے ہو، تم نے پہلے بیٹھ ر ہنے کو پسند کیا تھا تو اب گھر بیٹھنے والوں ہی کے ساتھ بیٹھے رہو"');
INSERT INTO "ur_maududi" VALUES(9,84,'9:84','اور آئندہ ان میں سے جو کوئی مرے اس کی نمازِ جنازہ بھی تم ہر گز نہ پڑھنا اور نہ کبھی اس کی قبر پر کھڑے ہونا کیونکہ انہوں نے اللہ اور اُس کے رسول ؐ کے ساتھ کُفر کیا ہے اور وہ مرے ہیں اس حال میں کہ وہ فاسق تھے۔ ');
INSERT INTO "ur_maududi" VALUES(9,85,'9:85','ان کی مالداری اور ان کی کثرت اولاد تم کو دھوکے میں نہ ڈالے اللہ نے تو ارادہ کر لیا ہے کہ اِس مال و اولاد کے ذریعہ سے ان کو اسی دنیا میں سزا دے اور ان کی جانیں اس حال میں نکلیں کہ وہ کافر ہوں');
INSERT INTO "ur_maududi" VALUES(9,86,'9:86','جب کبھی کوئی سورۃ اس مضمون کی نازل ہوئی کہ اللہ کو مانو اور اس کے رسول کے ساتھ جہاد کرو تو تم نے دیکھا کہ جو لوگ ان میں سے صاحب مقدرت تھے وہی تم سے درخواست کرنے لگے کہ انہیں جہاد کی شرکت سے معاف رکھا جائے اور انہوں نے کہا کہ ہمیں چھوڑ دیجیے کہ ہم بیٹھنے والوں کے ساتھ رہیں');
INSERT INTO "ur_maududi" VALUES(9,87,'9:87','ان لوگوں نے گھر بیٹھنے والیوں میں شامل ہونا پسند کیا اور ان کے دلوں پر ٹھپّہ لگا دیا گیا، اس لیے ان کی سمجھ میں اب کچھ نہیں آتا۔ ');
INSERT INTO "ur_maududi" VALUES(9,88,'9:88','بخلاف اس کے رسولؐ نے اور ان لوگوں نے جو رسولؐ کے ساتھ ایمان لائے تھے ا پنی جان و مال سے جہاد کیا اور اب ساری بھلائیاں انہی کے لیے ہیں اور وہی فلاح پانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(9,89,'9:89','اللہ نے ان کے لیے ایسے باغ تیار رکھے ہیں جن کے نیچے نہریں بہ رہی ہیں، ان میں وہ ہمیشہ رہیں گے یہ ہے عظیم الشان کامیابی');
INSERT INTO "ur_maududi" VALUES(9,90,'9:90','بدوی عربوں میں سے بھی بہت سے لوگ آئے جنہوں نے عذر کیے تاکہ انہیں بھی پیچھے رہ جانے کی اجازت دی جائے۔ اِس طرح بیٹھ رہے وہ لوگ جنہوں نے اللہ اور اس کے رسول سے ایمان کا جھوٹا عہد کیا تھا۔ ان بدویوں میں سے جن جن لوگوں نے کفر کا طریقہ اختیار کیا ہے عنقریب وہ درد ناک سزا سے دوچار ہوں گے');
INSERT INTO "ur_maududi" VALUES(9,91,'9:91','ضعیف اور بیمار لوگ اور وہ لوگ جو شرکت ِ جہاد کےلیے راہ نہیں پاتے ، اگر پیچھے رہ جائیں تو کوئی حرج نہیں جبکہ وہ خلُوصِ دل کے ساتھ اللہ اور اس کے رسول ؐ کے وفادار ہوں۔ ایسے محسنین پر اعتراض کی کوئی گنجائش نہیں ہے اور اللہ درگزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(9,92,'9:92','اسی طرح اُن لوگوں پر بھی کوئی اعتراض کا موقع نہیں ہے جنہوں نے خود آکر تم سے درخواست کی تھی کہ ہمارے لیے سواریاں بہم پہنچائی جائیں ، اور جب تم نے کہا کہ میں تمہارے لیے سواریوں کا انتظام نہیں کر سکتا تو وہ مجبوراً واپس آگئے اور حال یہ تھا کہ ان کی آنکھوں سے آنسُو جاری تھے اور انہیں اس بات کا بڑا رنج تھا کہ وہ اپنے خرچ پر شریکِ جہاد ہونے کی مقدرت نہیں رکھتے۔ ');
INSERT INTO "ur_maududi" VALUES(9,93,'9:93','البتہ اعتراض ان لوگوں پر ہے جو مالدار ہیں اور پھر بھی تم سے درخواستیں کرتے ہیں کہ انہیں شرکت جہاد سے معاف رکھا جائے انہوں نے گھر بیٹھنے والیوں میں شامل ہونا پسند کیا اور اللہ نے ان کے دلوں پر ٹھپہ لگا دیا، اس لیے اب یہ کچھ نہیں جانتے (کہ اللہ کے ہاں ان کی روش کا کیا نتیجہ نکلنے والا ہے)');
INSERT INTO "ur_maududi" VALUES(9,94,'9:94','تم جب پلٹ کر ان کے پاس پہنچو گے تو یہ طرح طرح کے عذرات پیش کریں گے مگر تم صاف کہہ دینا کہ "بہانے نہ کرو، ہم تہاری کسی بات کا اعتبار نہ کریں گے اللہ نے ہم کو تمہارے حالات بتا دیے ہیں اب اللہ اور اس کا رسول تمہارے طرز عمل کو دیکھے گا پھر تم اس کی طرف پلٹائے جاؤ گے جو کھلے اور چھپے سب کا جاننے والا ہے اور وہ تمہیں بتا دے گا کہ تم کیا کچھ کرتے رہے ہو"');
INSERT INTO "ur_maududi" VALUES(9,95,'9:95','تمہاری واپسی پر یہ تمہارے سامنے قسمیں کھائیں گے تاکہ تم ان سے صَرفِ نظر کرو۔ تو بےشک تم اِن سے صَرفِ نظر ہی کر لو، کیونکہ یہ گندگی ہیں اور ان کا اصلی مقام جہنّم ہے جو ان کی کمائی کے بدلے میں انہیں نصیب ہوگی');
INSERT INTO "ur_maududi" VALUES(9,96,'9:96','یہ تمہارے سامنے قسمیں کھائیں گے کہ تاکہ تم ان سے راضی ہو جاؤ حالانکہ اگر تم ان سے راضی ہو بھی گئے تو اللہ ہرگز ایسے فاسق لوگوں سے راضی نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(9,97,'9:97','یہ بدوی عرب کفر و نفاق میں زیادہ سخت ہیں اور ان کے معاملہ میں اس امر کے امکانات زیادہ ہیں کہ اس دین کے حدود سے ناواقف رہیں جو اللہ نے اپنے رسول پر نازل کیا ہے۔ اللہ سب کچھ جانتا ہے اور حکیم و دانا ہے');
INSERT INTO "ur_maududi" VALUES(9,98,'9:98','ان میں ایسے ایسے لوگ موجود ہیں جو راہِ خدا میں کچھ خرچ کرتے ہیں تو اسے اپنے اوپر زبر دستی کی چٹّی سمجھتے ہیں اور تمہارے حق میں زمانہ کی گردشوں کا انتظار کر رہے ہیں ( کہ تم کسی چکر میں پھنسو تو وہ اپنی گردن سے اس نظام کی اطاعت کا قلادہ اُتار پھینکیں جس میں تم نے انہیں کَس دیا ہے)۔ حالانکہ بدی کا چکر خود انہی پر مسلّط ہے اور اللہ سب کچھ سُنتا اور جانتا ہے');
INSERT INTO "ur_maududi" VALUES(9,99,'9:99','اور انہی بدویوں میں کچھ لوگ ایسے بھی ہیں جو اللہ اور روز آخر پر ایمان رکھتے ہیں اور جو کچھ خرچ کرتے ہیں اُسے اللہ کے تقرب کا اور رسولؐ کی طرف سے رحمت کی دعائیں لینے کا ذریعہ بناتے ہیں ہاں! وہ ضرور ان کے لیے تقرب کا ذریعہ ہے اور اللہ ضرور ان کو اپنی رحمت میں داخل کریگا، یقیناً اللہ درگزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(9,100,'9:100','وہ مہاجر و انصار جنہوں نے سب سے پہلے دعوت ایمان پر لبیک کہنے میں سبقت کی، نیز وہ جو بعد میں راستبازی کے ساتھ پیچھے آئے، اللہ ان سے راضی ہوا اور وہ اللہ سے راضی ہوئے، اللہ نے ان کے لیے ایسے باغ مہیا کر رکھے ہیں جن کے نیچے نہریں بہتی ہوں گی اور وہ ان میں ہمیشہ رہیں گے، یہی عظیم الشان کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(9,101,'9:101','تمہارے گر دو پیش جو بدوی رہتے ہیں ان میں بہت سے منافق ہیں اور اسی طرح خود مدینہ کے باشندوں میں بھی منافق موجود ہیں جو نفاق میں طاق ہو گئے ہیں۔ تم انہیں نہیں جانتے ، ہم ان کو جانتے ہیں۔ قریب ہے وہ وقت جب ہم ان کو دوہری سزا دیں گے، پھر وہ زیادہ بڑی سزا کے لیے واپس لائے جائیں گے');
INSERT INTO "ur_maududi" VALUES(9,102,'9:102','کچھ اور لوگ ہیں جنہوں نے اپنے قصوروں کا اعتراف کر لیا ہے ان کا عمل مخلوط ہے، کچھ نیک ہے اور کچھ بد، بعید نہیں کہ اللہ ان پر پھر مہربان ہو جائے کیونکہ وہ درگزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(9,103,'9:103','اے نبیؐ، تم ان کے اموال میں سے صدقہ لے کر انہیں پاک کرو اور (نیکی کی راہ میں) انہیں بڑھاؤ، اور ان کے حق میں دعائے رحمت کرو کیونکہ تمہاری دعا ان کے لیے وجہ تسکین ہوگی، اللہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "ur_maududi" VALUES(9,104,'9:104','کیا اِن لوگوں کو معلوم نہیں ہے کہ وہ اللہ ہی ہے جو اپنے بندوں کی توبہ قبول کرتا ہے اور ان کی خیرات کو قبولیت عطا فرماتا ہے، اور یہ کہ اللہ بہت معاف کرنے والا اور رحیم ہے؟');
INSERT INTO "ur_maududi" VALUES(9,105,'9:105','اور اے نبی ؐ ، اِن لوگوں سے کہہ دو کہ تم عمل کرو ، اللہ اور اس کا رسول اور مومنین سب دیکھیں گے کہ تمہارا طرزِ عمل اب کیا رہتا ہے۔ پھر تم اُس کی طرف پلٹائے جاوٴ گے جو کھُلے اور چھُپے سب کو جانتا ہے اور وہ تمہیں بتادے گا کہ تم کیا کرتے رہے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(9,106,'9:106','کچھ دُوسرے لوگ ہیں جن کا معاملہ ابھی خدا کے حکم پر ٹھہرا ہوا ہے ، چاہے اُنہیں سزا دے اور چاہے اُن پر از سرِ نو مہربان ہو جائے۔ اللہ سب کچھ جانتا ہے اور حکیم و دانا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(9,107,'9:107','کچھ اور لوگ ہیں جنہوں نے ایک مسجد بنائی اِس غرض کے لیے کہ (دعوت حق کو) نقصان پہنچائیں، اور (خدا کی بندگی کرنے کے بجائے) کفر کریں، اور اہل ایمان میں پھوٹ ڈالیں، اور (اس بظاہر عبادت گاہ کو) اُس شخص کے لیے کمین گاہ بنائیں جو اس سے پہلے خدا اور رسولؐ کے خلاف بر سر پیکار ہو چکا ہے وہ ضرور قسمیں کھا کھا کر کہیں گے کہ ہمارا ارادہ تو بھلائی کے سوا کسی دوسری چیز کا نہ تھا مگر اللہ گواہ ہے کہ وہ قطعی جھوٹے ہیں');
INSERT INTO "ur_maududi" VALUES(9,108,'9:108','تم ہر گز اس عمارت میں کھڑے نہ ہونا۔ جو مسجد اول روز سے تقوٰی پر قائم کی گئی تھی وہی اس کے لیے زیادہ موزوں ہے کہ تم اس میں (عبادت کے لیے) کھڑے ہو، اس میں ایسے لوگ ہیں جو پاک رہنا پسند کرتے ہیں اور اللہ کو پاکیزگی اختیار کرنے والے ہی پسند ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(9,109,'9:109','پھر تمہارا کیا خیال ہے کہ بہتر انسان وہ ہے جس نے اپنی عمارت کی بنیاد خدا کے خوف اور اس کی رضا کی طلب پر رکھی ہو یا وہ جس نے اپنی عمارت ایک وادی کی کھوکھلی بے ثبات کگر پر اُٹھائی اور وہ اُسے لے کر سیدھی جہنم کی آگ میں جا گری؟ ایسے ظالم لوگوں کو اللہ کبھی سیدھی راہ نہیں دکھاتا۔ ');
INSERT INTO "ur_maududi" VALUES(9,110,'9:110','یہ عمارت جو انہوں نے بنائی ہے، ہمیشہ ان کے دلوں میں بے یقینی کی جڑ بنی رہے گی( جس کے نکلنے کی اب کوئی صورت نہیں) بجز اس کے کہ ان کے دل ہی پارہ پارہ ہو جائیں۔ اللہ نہایت با خبر اور حکیم و دانا ہے');
INSERT INTO "ur_maududi" VALUES(9,111,'9:111','حقیقت یہ ہے کہ اللہ نے مومنوں سے اُن کے نفس اور اُن کے مال جنّت کے بدلے خرید لیے ہیں۔ وہ اللہ کی راہ میں لڑتے اور مارتے اور مرتے ہیں۔ اُن سے (جنّت کا وعدہ) اللہ کے ذمّے ایک پختہ وعدہ ہے تورات اور انجیل اور قرآن میں۔ اور کون ہے جو اللہ سے بڑھ کر اپنے عہد کا پورا کرنے والا ہو؟ پس خوشیاں مناوٴ اپنے اُس سودے پر جو تم نے خدا سے چُکا لیا ہے، یہی سب سے بڑی کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(9,112,'9:112','اللہ کی طرف بار بار پلٹنے والے ، اُس کی بندگی بجا لانے والے ، اُس کی تعریف کے گُن گانے والے، اُس کی خاطر زمین میں گردش کرنے والے ، اُس کے آگے رکوع اور سجدے کرنے والے ، نیکی کا حکم دینے والے ، بدی سے روکنے والے ، اور اللہ کے حدُود کی حفاظت کرنے والے ( اس شان کے ہوتے ہیں وہ مومن جو اللہ سے خرید و فروخت کا یہ معاملہ طے کرتے ہیں) اور اے نبی ؐ ان مومنوں کو خوشخبری دے دو');
INSERT INTO "ur_maududi" VALUES(9,113,'9:113','نبی کو اور ان لوگوں کو جو ایمان لائے ہیں زیبا نہیں کہ مشرکوں کے لیے مغفرت کی دعا کریں، چاہے وہ اُن کے رشتہ دار ہی کیوں نہ ہوں، جب کہ ان پر یہ بات کھُل چکی ہے کہ وہ جہنّم کے مستحق ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(9,114,'9:114','ابراہیم ؑ نے اپنے باپ کے لیے جو دعائے مغفرت کی تھی وہ تو اُس کے وعدے کی وجہ سے تھی جو اُس نے اپنے باپ سے کیا تھا ، مگر جب اُس پر یہ بات کھُل گئی کہ اُس کا باپ خداکا دشمن ہے تو وہ اس سے بیزار ہو گیا ، حق یہ ہے کہ ابراہیم ؑ بڑا رقیق القلب و خدا ترس اور بُردبار آدمی تھا۔ ');
INSERT INTO "ur_maududi" VALUES(9,115,'9:115','اللہ کا یہ طریقہ نہیں ہے کہ لوگوں کو ہدایت دینے کے بعد پھر گمراہی میں مبتلا کرے جب تک کہ اُنہیں صاف صاف بتا نہ دے کہ اُنہیں کن چیزوں سے بچنا چاہیے۔ در حقیقت اللہ ہر چیز کا علم رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(9,116,'9:116','اور یہ بھی واقعہ ہے کہ اللہ ہی کے قبضہ میں آسمان و زمین کی سلطنت ہے، اسی کے اختیار میں زندگی و موت ہے، اور تمہارا کوئی حامی و مددگار ایسانہیں ہے جو تمہیں اس سے بچا سکے');
INSERT INTO "ur_maududi" VALUES(9,117,'9:117','اللہ نے معاف کر دیا نبی کو اور اُن مہاجرین و انصار کو جنہوں نے بڑی تنگی کے وقت میں نبی ؐ کا ساتھ دیا۔ اگرچہ اُن میں سے کچھ لوگوں کے دل کَجی کی طرف مائل ہو چلے تھے، (مگر جب اُنہوں نے اُس کجی کا اتباع نہ کیا بلکہ نبی ؐ کا ساتھ ہی دیا تو ) اللہ نے اُنہیں معاف کر دیا ، بےشک اُس کا معاملہ ان لوگوں کے ساتھ شفقت و مہربانی کا ہے');
INSERT INTO "ur_maududi" VALUES(9,118,'9:118','اور اُن تینوں کو بھی اُس نے معاف کیا جن کے معاملے کو ملتوی کر دیا گیا تھا ۔ جب زمین اپنی ساری وسعت کے باوجوداُن پر تنگ ہو گئی اور اُن کی اپنی جانیں بھی اُن پر بار ہو نے لگیں اور انہوں نے جان لیا کہ اللہ سے بچنے کے لیے کوئی جائے پناہ خود اللہ ہی کے دامنِ رحمت کے سوا نہیں ہے، تو اللہ اپنی مہربانی سے اُن کی طرف پلٹا تاکہ وہ اُس کی طرف پلٹ آئیں ، یقیناً وہ بڑا معاف کرنے والا اور رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(9,119,'9:119','اے لوگو جو ایمان لائے ہو، اللہ سے ڈرو اور سچے لوگوں کا ساتھ دو');
INSERT INTO "ur_maududi" VALUES(9,120,'9:120','مدینے کے باشندوں اور گرد و نواح کے بدویوں کو یہ ہرگز زبیا نہ تھا کہ اللہ کے رسول کو چھوڑ کر گھر بیٹھ رہتے اور اس کی طرف سے بے پروا ہو کر اپنے اپنے نفس کی فکر میں لگ جاتے اس لیے کہ ایسا کبھی نہ ہوگا کہ اللہ کی راہ میں بھوک پیاس اور جسمانی مشقت کی کوئی تکلیف وہ جھیلیں، اور منکرین حق کو جو راہ ناگوار ہے اُس پر کوئی قدم وہ اٹھائیں، اور کسی دشمن سے (عداوت حق کا) کا کوئی انتقام وہ لیں اور اس کے بدلے ان کے حق میں ایک عمل صالح نہ لکھا جائے یقیناً اللہ کے ہاں محسنوں کا حق الخدمت مارا نہیں جاتا ہے');
INSERT INTO "ur_maududi" VALUES(9,121,'9:121','اسی طرح یہ بھی کبھی نہ ہوگا کہ (راہ خدا میں) تھوڑا یا بہت کوئی خرچ وہ اٹھائیں اور (سعی جہاد میں) کوئی وادی وہ پار کریں اور ان کے حق میں اسے لکھ نہ لیا جائے تاکہ اللہ ان کے اس اچھے کارنامے کا صلہ انہیں عطا کرے');
INSERT INTO "ur_maududi" VALUES(9,122,'9:122','اور یہ کچھ ضروری نہ تھا کہ اہلِ ایمان سارے کے سارے ہی نکل کھڑے ہوتے، مگر ایسا کیوں نہ ہوا کہ ان کے آبادی کے ہر حصہ میں سے کچھ لوگ نکل کر آتے اور دین کی سمجھ پیدا کرتے اور واپس جا کر اپنے علاقے کے باشندوں کو خبر دار کرتے تاکہ وہ (غیر مسلمانہ روش سے ) پرہیز کرتے');
INSERT INTO "ur_maududi" VALUES(9,123,'9:123','اے لوگو جو ایمان لائے ہو ، جنگ کرو اُن منکرینِ حق سے جو تم سے قریب ہیں۔ اور چاہیے کہ وہ تمہارے اندر سختی پائیں، اور جان لو کہ اللہ متقیوں کے ساتھ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(9,124,'9:124','جب کوئی نئی سورت نازل ہوتی ہے تو ان میں سے بعض لوگ (مذاق کے طور پر مسلمانوں سے) پوچھتے ہیں کہ "کہو، تم میں سے کس کے ایمان میں اس سے اضافہ ہوا؟" (اس کا جواب یہ ہے کہ) جو لوگ ایمان لائے ہیں ان کے ایمان میں تو فی الواقع (ہر نازل ہونے والی سورت نے) اضافہ ہی کیا ہے اور وہ اس سے دلشاد ہیں');
INSERT INTO "ur_maududi" VALUES(9,125,'9:125','البتہ جن لوگوں کے دلوں کو (نفاق کا) روگ لگا ہوا تھا اُں کی سابق نجاست پر (ہر نئی سُورت نے ) ایک اور نجاست کا اضافہ کر دیا ہے اور وہ مرتے دم تک کُفر ہی میں مُبتلا رہے');
INSERT INTO "ur_maududi" VALUES(9,126,'9:126','کیا یہ لوگ دیکھتے نہیں کہ ہر سال ایک دو مرتبہ یہ آزمائش میں ڈالے جاتے ہیں ؟ مگر اِس پر بھی نہ توبہ کرتے ہیں نہ کوئی سبق لیتے ہیں');
INSERT INTO "ur_maududi" VALUES(9,127,'9:127','جب کوئی سُورت نازل ہوتی ہے تو یہ لوگ آنکھوں ہی آنکھوں میں ایک دوسرے سے باتیں کرتے ہیں کہ کہیں کوئی تم کو دیکھ تو نہیں رہا ہے، پھر چُپکے سے نکل بھاگتے ہیں۔ اللہ نے ان کے دل پھیر دیے ہیں کیونکہ یہ ناسمجھ لوگ ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(9,128,'9:128','دیکھو! تم لوگوں کے پاس ایک رسول آیا ہے جو خود تم ہی میں سے ہے، تمہارا نقصان میں پڑنا اس پر شاق ہے، تمہاری فلاح کا وہ حریص ہے، ایمان لانے والوں کے لیے وہ شفیق اور رحیم ہے');
INSERT INTO "ur_maududi" VALUES(9,129,'9:129','اب اگر یہ لوگ تم سے منہ پھیرتے ہیں تو اے نبیؐ، ان سے کہدو کہ "میرے لیے اللہ بس کرتا ہے، کوئی معبود نہیں مگر وہ، اُسی پر میں نے بھروسہ کیا اور وہ مالک ہے عرش عظیم کا"');
INSERT INTO "ur_maududi" VALUES(10,1,'10:1','ا ل ر، یہ اُس کتاب کی آیات ہیں جو حکمت و دانش سے لبریز ہے۔ ');
INSERT INTO "ur_maududi" VALUES(10,2,'10:2','کیا لوگوں کے لیے یہ ایک عجیب بات ہو گئی کہ ہم نے خود اُنہی میں سے ایک آدمی کو اشارہ کیا کہ (غفلت میں پڑے ہوئے) لوگوں کو چونکا دے اور جو مان لیں ان کو خوشخبری دیدے کہ ان کے لیے اُن کے ربّ کے پاس سچی عزت و سرفرازی ہے؟ (کیا یہی وہ بات ہے جس پر ) منکرینِ نے کہا کہ یہ شخص تو کھُلا جادوگر ہے؟ ');
INSERT INTO "ur_maududi" VALUES(10,3,'10:3','حقیقت یہ ہے کہ تمہارا ربّ وہی خدا ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا، پھر تختِ حکومت پر جلوہ گر ہوا اور کائنات کا انتظام چلارہا ہے۔ کوئی شفاعت (سفارش) کرنے والا نہیں ہے اِلّا یہ کہ اس کی اجازت کے بعد شفاعت کرے۔ یہی اللہ تمہارا ربّ ہے لہٰذا تم اُسی کی عبادت کرو۔ پھر کیا تم ہوش میں نہ آوٴ گے؟ ');
INSERT INTO "ur_maududi" VALUES(10,4,'10:4','اسی کی طرف تم سب کو پلٹ کا جانا ہے ، یہ اللہ کا پکا وعدہ ہے۔ بے شک پیدائش کی ابتداء وہی کرتا ہے، پھر وہی دوبارہ پیدا کرے گا، تاکہ جو لوگ ایمان لائے اور جنہوں نے نیک اعمال کیے ان کو پورے انصاف کے ساتھ جزا دے، اور جنہوں نے کُفر کا طریقہ اختیار کیا وہ کھولتا ہوا پانی پئیں اور دردناک سزا بُھگتیں اُس انکارِحق کی پاداش میں جو وہ کرتے رہے۔ ');
INSERT INTO "ur_maududi" VALUES(10,5,'10:5','وہی ہے جس نے سُورج کو اجیالا بنایا اور چاند کو چمک دی اور چاند کے گھٹنے بڑھنے کی منزلیں ٹھیک ٹھیک مقرر کر دیں تاکہ تم اُس سے برسوں اور تاریخوں کے حساب معلوم کرو اللہ نے یہ سب کچھ (کھیل کے طور پر نہیں بلکہ) با مقصد ہی بنایا ہے وہ اپنی نشانیوں کو کھول کھول کر پیش کر رہا ہے اُن لوگوں کے لیے جو علم رکھتے ہیں');
INSERT INTO "ur_maududi" VALUES(10,6,'10:6','یقیناً رات اور دن کے اُلٹ پھیر میں اور ہر اُس چیز میں جو اللہ نے زمین اور آسمانوں میں پیدا کی ہے، نشانیاں ہیں اُن لوگوں کے لیے جو (غلط بینی و غلط روی سے) بچنا چاہتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(10,7,'10:7','حقیقت یہ ہے کہ جو لوگ ہم سے ملنے کی توقع نہیں رکھتے اور دنیا کی زندگی ہی پر راضی اور مطمئن ہو گئے ہیں، اور جو لوگ ہماری نشانیوں سے غافل ہیں');
INSERT INTO "ur_maududi" VALUES(10,8,'10:8','اُن کا آخری ٹھکانہ جہنم ہو گااُن برائیوں کی پاداش میں جن کا اکتساب وہ (اپنے اُس غلط عقیدے اور غلط طرزِ عمل کی وجہ سے ) کرتے رہے۔ ');
INSERT INTO "ur_maududi" VALUES(10,9,'10:9','اور یہ بھی حقیقت ہے کہ جو لوگ ایمان لائے (یعنی جنہوں نے اُن صداقتوں کو قبول کر لیا جو اس کتاب میں پیش کی گئی ہیں) اور نیک اعمال کرتے رہے اُنہیں اُن کا ربّ اُن کےایمان کی وجہ سے سیدھی راہ چلائے گا، نعمت بھری جنتّوں میں اُن کے نیچے نہریں بہیں گی، ');
INSERT INTO "ur_maududi" VALUES(10,10,'10:10','وہاں اُن کی صدا یہ ہوگی کہ”پاک ہے تُو اے خدا“، اُن کی یہ دُعا ہوگی کہ ”سلامتی ہو“ اور اُن کی ہر بات کا خاتمہ اِس پر ہو گا کہ ”ساری تعریف اللہ ربّ العالمین ہی کے لیے ہے ۔“');
INSERT INTO "ur_maududi" VALUES(10,11,'10:11','اگر کہیں اللہ لوگوں کے ساتھ بُرا معاملہ کرنے میں بھی اتنی ہی جلدی کرتا جتنی وہ دُنیا کی بھلائی مانگنے میں جلدی کرتے ہیں تو ان کے مہلت ِعمل کبھی کی ختم کر دی گئی ہوتی۔ (مگر ہمارا یہ طریقہ نہیں ہے) اِس لیے ہم اُن لوگوں کو جو ہم سے ملنے کی توقع نہیں رکھتے اُن کی سرکشی میں بھٹکنے کے لیے چھُو ٹ دے دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(10,12,'10:12','انسان کا حال یہ ہے کہ جب اس پر کوئی سخت وقت آتا ہے تو کھڑے اور بیٹھے اور لیٹے ہم کو پکارتا ہے، مگر جب ہم اس کی مصیبت ٹال دیتے ہیں تو ایسا چل نکلتا ہے کہ گویا اس نے کبھی اپنے کسی بُرے وقت پر ہم کو پکارا ہی نہ تھا اس طرح حد سے گزر جانے والوں کے لیے ان کے کرتوت خوشنما بنا دیے گئے ہیں');
INSERT INTO "ur_maududi" VALUES(10,13,'10:13','لوگو، تم سے پہلے کی قوموں کو ( جو اپنے اپنے زمانہ میں برسرِ عرُوج تھیں) ہم نے ہلاک کر دیا جب انہوں نے ظلم کی روِش اختیار کی اور اُن کے رسول اُن کے پاس کھُلی کھُلی نشانیاں لے کر آئے اور انہوں نے ایمان لاکر ہی نہ دیا۔ اِس طرح ہم مجرموں کو اُن کے جرائم کا بدلہ دیا کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(10,14,'10:14','اب اُن کے بعد ہم نے تم کو زمین میں ان کی جگہ دی ہے ، تا کہ دیکھیں تم کیسے عمل کرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(10,15,'10:15','جب انہیں ہماری صاف صاف باتیں سُنائی جاتی ہیں تو وہ لوگ جو ہم سے ملنے کی توقع نہیں رکھتے ، کہتے ہیں کہ ”اِس کے بجائے کوئی اور قرآن لاوٴ یا ا س میں کچھ ترمیم کرو“۔ اے محمد ؐ ، ان سے کہو ”میرا یہ کام نہیں ہے کہ اپنی طرف سے اِس میں کوئی تغیّر و تبدّل کر لوں، میں تو بس اُس وحی کا پیرو ہوں جو میرے پاس بھیجی جاتی ہے۔ اگر میں اپنے ربّ کی نافرمانی کروں تو مجھے ایک بڑے ہولناک دن کے عذاب کا ڈر ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(10,16,'10:16','اور کہو”اگر اللہ کی مشیّت یہی ہوتی تو میں یہ قرآن تمہیں کبھی نہ سُناتا اور اللہ تمہیں اس کی خبر تک نہ دیتا۔ آخر اس سے پہلے میں ایک عمر تمہارے درمیان گُزار چُکا ہوں ، کیا تم عقل سے کام نہیں لیتے ؟');
INSERT INTO "ur_maududi" VALUES(10,17,'10:17','پھر اُس سے بڑھ کر ظالم اور کون ہوگا جو ایک جھوٹی بات گھڑ کر اللہ کی طرف منسُوب کرے یا اللہ کی واقعی آیات کو جھوٹا قرار دے۔ یقیناً مجرم کبھی فلاح نہیں پا سکتے۔“ ');
INSERT INTO "ur_maududi" VALUES(10,18,'10:18','یہ لوگ اللہ کے سوا اُن کی پرستش کر رہے ہیں جو ان کو نا نقصان پہنچا سکتے ہیں نہ نفع اور کہتے یہ ہیں کہ یہ اللہ کے ہاں ہمارے سفارشی ہیں۔ اے محمد ؐ ، ان سے کہو ”کیا تم اللہ کو اُس بات کی خبر دیتے ہو جسے نہ وہ آسمانوں میں جانتا ہے نہ زمین میں؟ “ پاک ہے وہ اور بالا وبرتر ہے اُس شرک سے جو یہ لوگ کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(10,19,'10:19','ابتداءً سارے انسان ایک ہی اُمّت تھے ، بعد میں انہوں نے مختلف عقیدے اور مسلک بنا لیے ، اور اگر تیرے ربّ کی طرف سے پہلے ہی ایک بات طے نہ کر لی گئی ہوتی تو جس چیز میں وہ باہم اختلاف کر رہے ہیں اس کا فیصلہ کر دیا جاتا۔ ');
INSERT INTO "ur_maududi" VALUES(10,20,'10:20','اور یہ جو وہ کہتے ہیں کہ اِس نبی ؐ پر اِ س کے ربّ کی طرف سے کوئی نشانی کیوں نہ اُتاری گئی، تو اِن سے کہو ”غیب کا مالک و مختار تو اللہ ہی ہے، اچھا، انتظار کرو ، میں بھی تمہارے ساتھ انتظار کرتا ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(10,21,'10:21','لوگوں کا حال یہ ہے کہ مصیبت کے بعد جب اُنہیں رحمت کا مزا چکھاتے ہیں تو فوراً ہی وہ ہماری نشانیوں کے معاملے میں چال بازیاں شروع کر دیتے ہیں۔ ان سے کہو”اللہ اپنی چال میں تم سے زیادہ تیز ہے، اُس کے فرشتے تمہاری سب مکّاریوں کو قلمبند کر رہے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(10,22,'10:22','وہ اللہ ہی ہے جو تم کو خشکی اور تری میں چلاتا ہے۔ چنانچہ جب تم کشتیوں میں سوار ہو کر با دِ موافق پر فرحاں و شاداں سفر کر رہے ہوتے ہو اور پھر یکایک بادِ مخالف کا زور ہوتا ہے اور ہر طرف سے موجوں کے تھپیڑے لگتے ہیں اور مسافر سمجھ لیتے ہیں کہ گھِر گئے،اُس وقت سب اپنے دین کو اللہ ہی کے لیے خالص کر کے اُس سے دعائیں مانگتے ہیں کہ ”اگر تُو نے ہم کو اِس بلا سے نجات دے دی تو ہم شُکر گزار بندے بنیں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(10,23,'10:23','مگر جب وہ ان کو بچا لیتا ہے تو پھر وہی لوگ حق سے منحرف ہو کر زمین میں بغاوت کرنے لگتے ہیں لوگو، تمہاری یہ بغاوت اُلٹی تمہارے ہی خلاف پڑ رہی ہے دنیا کے چند روزہ مزے ہیں (لُوٹ لو)، پھر ہماری طرف تمہیں پلٹ کر آنا ہے، اُس وقت ہم تمہیں بتا دیں گے کہ تم کیا کچھ کرتے رہے ہو');
INSERT INTO "ur_maududi" VALUES(10,24,'10:24','دنیا کی یہ زندگی (جس کے نشے میں مست ہو کر تم ہماری نشانیوں سے غفلت برت رہے ہو) اس کی مثال ایسی ہے جیسے آسمان سے ہم نے پانی برسایا تو زمین کی پیداوار، جسے آدمی اور جانور سب کھاتے ہیں، خوب گھنی ہو گئی، پھر عین اُس وقت جبکہ زمین اپنی بہار پر تھی اور کھیتیاں بنی سنوری کھڑی تھیں اور ان کے مالک یہ سمجھ رہے تھے کہ اب ہم ان سے فائدہ اُٹھانے پر قادر ہیں، یکایک رات کو یا دن کو ہمارا حکم آ گیا اور ہم نے اسے ایسا غارت کر کے رکھ دیا کہ گویا کل وہاں کچھ تھا ہی نہیں اس طرح ہم نشانیاں کھول کھول کر پیش کرتے ہیں اُن لوگوں کے لیے جو سوچنے سمجھنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(10,25,'10:25','( تم اس ناپائیدار زندگی کے فریب میں مبتلا ہو رہے ہو) اور اللہ تمہیں دارالسلام کی طرف دعوت دے رہا ہے۔ (ہدایت اُس کے اختیار میں ہے) جس کو وہ چاہتا ہے سیدھا راستہ دکھا دیتا ہے');
INSERT INTO "ur_maududi" VALUES(10,26,'10:26','جن لوگوں نے بھلائی کا طریقہ اختیار کیا اُن کے لیے بھلائی ہے اور مزید فضل۔ ان کے چہروں پر رُو سیاہی اور ذلّت نہ چھائے گی۔ وہ جنّت کے مستحق ہیں جہاں وہ ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(10,27,'10:27','اور جن لوگوں نے بُرائیاں کمائیں ان کی برائی جیسی ہے ویسا ہی وہ بدلہ پائیں گے، ذلّت ان پر مسلّط ہو گی ، کوئی اللہ سے ان کو بچانے والا نہ ہو گا ، ان کے چہروں پر ایسی تاریکی چھائی ہوئی ہو گی جیسے رات کے سیاہ پردے ان پر پڑے ہوئے ہوں، وہ دوزخ کے مستحق ہیں جہاں وہ ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(10,28,'10:28','جس روز ہم ان سب کو ایک ساتھ (اپنی عدالت میں) اکٹھا کریں گے، پھر ان لوگوں سے جنہوں نے شرک کیا ہے کہیں گے کہ ٹھہر جاوٴ تم بھی اور تمہارے بنائے ہوئے شریک بھی ، پھر ہم ان کے درمیان سے اجنبیت کا پردہ ہٹا دیں گے اور ان کے شریک کہیں گے کہ ”تم ہماری عبادت تو نہیں کرتے تھے');
INSERT INTO "ur_maududi" VALUES(10,29,'10:29','ہمارے اور تمہارے درمیان اللہ کی گواہی کافی ہے کہ ( تم اگر ہماری عبادت کرتے بھی تھے تو ) ہم تمہاری اس عبادت سے بالکل بے خبر تھے۔“ ');
INSERT INTO "ur_maududi" VALUES(10,30,'10:30','اُس وقت ہر شخص اپنے کیے کا مزا چکھ لے گا، سب اپنے مالک حقیقی کی طرف پھیر دیے جائیں گے اور وہ سارے جھوٹ جو انہوں نے گھڑ رکھے تھے گم ہو جائیں گے');
INSERT INTO "ur_maududi" VALUES(10,31,'10:31','اِن سے پُوچھو، کون تم کو آسمان اور زمین سے رزق دیتا ہے؟ یہ سماعت اور بینائی کی قوتیں کس کے اختیار میں ہیں؟ کون بے جان میں سے جاندار کو اور جاندار میں سے بے جان کو نکالتا ہے؟ کون اِس نظم عالم کی تدبیر کر رہا ہے؟ وہ ضرور کہیں گے کہ اللہ کہو، پھر تم (حقیقت کے خلاف چلنے سے) پرہیز نہیں کرتے؟');
INSERT INTO "ur_maududi" VALUES(10,32,'10:32','تب تو یہی اللہ تمہارا حقیقی ربّ ہے۔ پھر حق کے بعد گمراہی کے سوا اور کیا باقی رہ گیا؟ آخر یہ تم کدھر پھرائے جار ہے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(10,33,'10:33','(اے نبی ؐ ، دیکھو) اس طرح نافرمانی اختیار کرنے والوں پر تمہارے ربّ کی بات صادق آگئی کہ وہ مان کر نہ دیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(10,34,'10:34','اِن سے پوچھو، تمہارے ٹھہرائے ہوئے شریکوں میں کوئی ہے جو تخلیق کی ابتداء بھی کرتا ہو اور پھر اس کا اعادہ بھی کرے؟۔۔۔۔ کہو وہ صرف اللہ ہے جو تخلیق کی ابتداء کرتا ہے اور اس کا اعادہ بھی، پھر تم یہ کس اُلٹی راہ پر چلائے جا رہے ہو ؟ ');
INSERT INTO "ur_maududi" VALUES(10,35,'10:35','اِن سے پوچھو تمہارے ٹھہرائے ہوئے شریکوں میں کوئی ایسا بھی ہے جو حق کی طرف رہنمائی کرتا ہو؟ ۔۔۔۔ کہو وہ صرف اللہ ہے جو حق کی طرف رہنمائی کرتا ہے۔ پھر بھلا بتاوٴ جو حق کی طرف رہنمائی کرتا ہے وہ اِس دنیا کا زیادہ مستحق ہے کہ اس کی پیروی کی جائے یا وہ جو رہنمائی نہیں کر سکتا اِلّا یہ کہ اس کی رہنمائی کی جائے؟ آخر تمہیں ہو کیا گیا ہے، کیسے اُلٹے فیصلے کرتے ہو؟');
INSERT INTO "ur_maududi" VALUES(10,36,'10:36','حقیقت یہ ہے کہ اِن میں سے اکثر لوگ محض قیاس و گمان کے پیچھے چلے جارہے ہیں ، حالانکہ گمان حق کی ضرورت کو کچھ بھی پُورا نہیں کرتا۔ جو کچھ یہ کر رہے ہیں اللہ اُس کو خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(10,37,'10:37','اور یہ قرآن وہ چیز نہیں ہے جو اللہ وحی و تعلیم کے بغیر تصنیف کر لیا جائے۔ بلکہ یہ تو جو کچھ پہلے آچکا تھا اس کی تصدیق اور الکتاب کی تفصیل ہے۔ اِس میں کوئی شک نہیں کہ یہ فرمانروائے کائنات کی طرف سے ہے');
INSERT INTO "ur_maududi" VALUES(10,38,'10:38','کیا یہ لوگ کہتے ہیں کہ پیغمبر ؐ نے اسے خود تصنیف کر لیا ہے؟ کہو، ”اگر تم اپنے اس الزام میں سچے ہو تو ایک سُورة اس جیسی تصنیف کر لاوٴ اور ایک خدا کو چھوڑ کر جس جس کو بُلا سکتے ہو مدد کے لیے بلا لو۔ “');
INSERT INTO "ur_maududi" VALUES(10,39,'10:39','اصل بات یہ ہے کہ جو چیز اِن کے علم کی گرفت میں نہیں آئی اور جس کا مآل بھی ان کے سامنے نہیں آیا اُس کو اِنہوں نے (خواہ مخواہ اٹکل پِچّو) جھُٹلا دیا۔ اِسی طرح تو ان سے پہلے کے لوگ بھی جھُٹلا چکے ہیں پھر دیکھ لو اُن ظالموں کا کیا انجام ہُوا');
INSERT INTO "ur_maududi" VALUES(10,40,'10:40','اِن میں سے کچھ لوگ ایمان لائیں گے اور کچھ نہیں لائیں گے اور تیرا ربّ اُن مفسدوں کو خوب جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(10,41,'10:41','اگر یہ تجھے جھُٹلاتے ہیں تو کہہ دے کہ ”میرا عمل میرے لیے ہے اور تمہارا عمل تمہارے لیے، جو کچھ میں کرتا ہوں اس کی ذمہ داری سے تم بَری ہو اور جو کچھ تم کر رہے ہو اُس کی ذمہ داری سے میں بَری ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(10,42,'10:42','ان میں بہت سے لوگ ہیں جو تیری باتیں سُنتے ہیں ، مگر کیا تُو بہروں کو سُنائے گا خواہ وہ کچھ نہ سمجھتے ہوں؟ ');
INSERT INTO "ur_maududi" VALUES(10,43,'10:43','اِن میں سے بہت سے لوگ ہیں جو تجھے دیکھتے ہیں ، مگر کیا تُو اندھوں کو راہ بتائے گا خواہ انہیں کچھ نہ سُو جھتا ہو؟ ');
INSERT INTO "ur_maududi" VALUES(10,44,'10:44','حقیقت یہ ہے کہ اللہ لوگوں پر ظلم نہیں کرتا، لوگ خود ہی اپنے اُوپر ظلم کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(10,45,'10:45','(آج یہ دُنیا کی زندگی میں مست ہیں) اور جس روز اللہ اِن کو اکٹھا کرے گا تو (یہی دُنیا کی زندگی اِنہیں ایسی محسُوس ہوگی) گویا یہ محض ایک گھڑی بھر آپس میں جان پہچان کرنے کو ٹھہرے تھے۔ (اُس وقت تحقیق ہو جائے گا کہ) فی الواقع سخت گھاٹے میں رہے وہ لوگ جنہوں نے اللہ کی ملاقات کو جھُٹلایا اور ہر گز وہ راہِ راست پر نہ تھے');
INSERT INTO "ur_maududi" VALUES(10,46,'10:46','جن بُرے نتائج سے ہم انہیں ڈرا رہے ہیں ان کا کوئی حصہ ہم تیرے جیتے جی دکھا دیں یا اس سے پہلے ہی تجھے اُٹھا لیں، بہرحال اِنہیں آنا ہماری ہی طرف ہے اور جو کچھ یہ کر رہے ہیں اس پر اللہ گواہ ہے');
INSERT INTO "ur_maududi" VALUES(10,47,'10:47','ہر اُمّت کے لیے ایک رسُول ہے ، پھر جب کسی اُمّت کے پاس اُس کا رسُول آجاتا ہے تو اس کا فیصلہ پُورے انصاف کے ساتھ چُکا دیا جاتا ہے اور اس پر ذرہ برابر ظلم نہیں کیا جاتا۔ ');
INSERT INTO "ur_maududi" VALUES(10,48,'10:48','کہتے ہیں اگر تمہاری یہ دھمکی سچّی ہے تو آخر یہ کب پُوری ہو گی؟');
INSERT INTO "ur_maududi" VALUES(10,49,'10:49','کہو ”میرے اختیار میں خُود اپنا نفع و ضرر بھی نہیں، سب کچھ اللہ کی مشیّت پر موقوف ہے۔ ہر اُمّت کے لیے مہلت کی ایک مدّت ہے، جب یہ مدّت پُوری ہو جاتی ہے تو گھڑی بھر کی تقدیم و تاخیر بھی نہیں ہوتی۔“ ');
INSERT INTO "ur_maududi" VALUES(10,50,'10:50','اِن سے کہو، کبھی تم نے یہ بھی سوچا کہ اگر اللہ کا عذاب اچانک رات کو یا دن کو آ جائے (تو تم کیا کر سکتے ہو؟) آخر یہ ایسی کونسی چیز ہے جس کے لیے مجرم جلدی مچائیں؟');
INSERT INTO "ur_maududi" VALUES(10,51,'10:51','کیا جب وہ تم پر آ پڑے اسی وقت تم اسے مانو گے؟ اب بچنا چاہتے ہو؟ حالانکہ تم خود ہی اس کے جلدی آنے کا تقاضا کر رہے تھے!');
INSERT INTO "ur_maududi" VALUES(10,52,'10:52','پھر ظالموں سے کہا جائے گا کہ اب ہمیشہ کے عذاب کا مزا چکھو، جو کچھ تم کماتے رہے ہو اس کی پاداش کے سوا اور کیا بدلہ تم کو دیا جا سکتا ہے؟');
INSERT INTO "ur_maududi" VALUES(10,53,'10:53','پھر پُوچھتے ہیں کیا واقعی یہ سچ ہے جو تم کہہ رہے ہو؟ کہو“میرے رب کی قسم، یہ بالکل سچ ہے اور تم اتنا بل بوتا نہیں رکھتے کہ اسے ظہُور میں آنے سے روک دو"');
INSERT INTO "ur_maududi" VALUES(10,54,'10:54','اگر ہر اُس شخص کے پاس جس نے ظلم کیا ہے، رُوئے زمین کی دولت بھی ہو تو اُس عذاب سے بچنے کے لیے وہ اُسے فدیہ میں دینے پر آمادہ ہو جائے گا۔ جب یہ لوگ اس عذاب کو دیکھ لیں گے تو دل ہی دل میں پچھتائیں گے۔ مگر ان کے درمیان پورے انصاف سے فیصلہ کیا جائے گا، کوئی ظلم ان پر نہ ہو گا');
INSERT INTO "ur_maududi" VALUES(10,55,'10:55','سنو! آسمانوں اور زمین میں جو کچھ ہے اللہ کا ہے سُن رکھو! اللہ کا وعدہ سچّا ہے مگر اکثر انسان جانتے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(10,56,'10:56','وہی زندگی بخشتا ہے اور وہی موت دیتا ہے اور اسی کی طرف تم سب کو پلٹنا ہے');
INSERT INTO "ur_maududi" VALUES(10,57,'10:57','لوگو! تمہارے پاس تمہارے رب کی طرف سے نصیحت آ گئی ہے یہ وہ چیز ہے جو دلوں کے امراض کی شفا ہے اور جو اسے قبول کر لیں ان کے لیے رہنمائی اور رحمت ہے');
INSERT INTO "ur_maududi" VALUES(10,58,'10:58','اے نبیؐ، کہو کہ“یہ اللہ کا فضل اور اس کی مہربانی ہے کہ یہ چیز اس نے بھیجی، اس پر تو لوگوں کو خوشی منانی چاہیے، یہ اُن سب چیزوں سے بہتر ہے جنہیں لوگ سمیٹ رہے ہیں"');
INSERT INTO "ur_maududi" VALUES(10,59,'10:59','اے نبی ؐ ، ان سے کہو”تم لوگوں نے کبھی یہ بھی سوچا ہے کہ جو رزق اللہ نے تمہارے لیے اُتارا تھا اس میں سے تم نے خود ہی کسی کو حرام اور کسی کو حلال ٹھہرا لیا “اِن سے پوچھو، اللہ نے تم کو اس کی اجازت دی تھی؟ یا تم اللہ پر افترا کر رہے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(10,60,'10:60','جو لوگ اللہ پر یہ جھوٹا افترا باندھتے ہیں ان کا کیا گمان ہے کہ قیامت کے روز ان سے کیا معاملہ ہو گا؟ اللہ تو لوگوں پر مہربانی کی نظر رکھتا ہے مگر اکثر انسان ایسے ہیں جو شکر نہیں کرتے۔ ');
INSERT INTO "ur_maududi" VALUES(10,61,'10:61','اے نبی ؐ ، تم جس حال میں بھی ہوتے ہو اور قرآن میں سے جو کچھ بھی سُناتے ہو، اور لوگو، تم بھی جو کچھ کرتے ہو اس سب کے دوران میں ہم تم کو دیکھتے رہتے ہیں۔ کوئی ذرہ برابر چیز آسمان اور زمین میں ایسی نہیں ہے، نہ چھوٹی نہ بڑی، جو تیرے ربّ کی نظر سے پوشیدہ ہو اور ایک صاف دفتر میں درج نہ ہو۔ ');
INSERT INTO "ur_maududi" VALUES(10,62,'10:62','سُنو! جو اللہ کے دوست ہیں، جو ایمان لائے، ان کے لیے کسی خوف اور رنج کا موقع نہیں ہے');
INSERT INTO "ur_maududi" VALUES(10,63,'10:63','جو ایمان لائے اور جنہوں نے تقویٰ کا رویہ اختیار کیا ،');
INSERT INTO "ur_maududi" VALUES(10,64,'10:64','دُنیا اور آخرت دونوں زندگیوں میں ان کے لیے بشارت ہی بشارت ہے اللہ کی باتیں بدل نہیں سکتیں یہی بڑی کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(10,65,'10:65','اے نبیؐ، جو باتیں یہ لوگ تجھ پر بناتے ہیں وہ تجھے رنجیدہ نہ کریں، عزّت ساری کی ساری خدا کے اختیار میں ہے، اور وہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "ur_maududi" VALUES(10,66,'10:66','آگاہ رہو! آسمان کے بسنے والے ہوں یا زمین کے، سب سے سب اللہ کے مملوک ہیں اور جو لوگ اللہ کے سوا کچھ (اپنے خودساختہ) شریکوں کو پکار رہے ہیں وہ نِرے وہم و گمان کے پیرو ہیں اور محض قیاس آرائیاں کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(10,67,'10:67','وہ اللہ ہی ہے جس نے تمہارے لیے رات بنائی کہ اس میں سکون حاصل کرو اور دن کو روشن بنایا۔ اس میں نشانیاں ہیں اُن لوگوں کے لیے جو ( کُھلے کانوں سے پیغمبر کی دعوت کو) سُنتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(10,68,'10:68','لوگوں نے کہہ دیا کہ اللہ نے کسی کو بیٹا بنایاہے ، سُبحان اللہ! وہ تو بے نیاز ہے، آسمانوں اور زمین میں جو کچھ ہے سب اُس کی مِلک ہے۔ تمہارے پاس اِس قول کے لیے آخر دلیل کیا ہے؟ کیا تم اللہ کے متعلق وہ باتیں کہتے ہو جو تمہارے علم میں نہیں ہیں؟');
INSERT INTO "ur_maududi" VALUES(10,69,'10:69','اے محمدؐ، کہہ دو کہ جو لوگ اللہ پر جھوٹے افترا باندھتے ہیں وہ ہرگز فلاح نہیں پا سکتے');
INSERT INTO "ur_maududi" VALUES(10,70,'10:70','دنیا کی چند روزہ زندگی میں مزے کر لیں، پھر ہماری طرف اُن کو پلٹنا ہے پھر ہم اس کفر کے بدلے جس کا ارتکاب وہ کر رہے ہیں ان کو سخت عذاب کا مزہ چکھائیں گے');
INSERT INTO "ur_maududi" VALUES(10,71,'10:71','اِن کو نوحؑ کا قصہ سُناوٴ، اُس وقت کا قصہ جب اُس نے اپنی قوم سے کہا تھا ”اے برادرانِ قوم، اگر میرا تمہارے درمیان رہنا اور اللہ کی آیات سُنا سُنا کر تمہیں غفلت سے بیدار کرنا تمہارے لیے ناقابلِ برداشت ہو گیا ہے تو میرا بھروسہ اللہ پر ہے، تم اپنے ٹھہرائے ہوئے شریکوں کو ساتھ لے کر ایک متفقہ فیصلہ کر لو اور جو منصُوبہ تمہارے پیشِ نظر ہو اس کے خوب سوچ سمجھ لو تاکہ اس کا کوئی پہلو تمہاری نگاہ سے پوشیدہ نہ رہے، پھر میرے خلاف اس کو عمل میں لے آوٴ اور مجھے ہر گز مہلت نہ دو۔ ');
INSERT INTO "ur_maududi" VALUES(10,72,'10:72','تم نے میری نصیحت سے منہ موڑا (تو میرا کیا نقصان کیا) میں تم سے کسی اجر کا طلب گار نہ تھا، میرا اجر تو اللہ کے ذمہ ہے اور مجھے حکم دیا گیا ہے کہ (خواہ کوئی مانے یا نہ مانے) میں خود مسلم بن کر رہوں"');
INSERT INTO "ur_maududi" VALUES(10,73,'10:73','انہوں نے اسے جھٹلایا اور نتیجہ یہ ہوا کہ ہم نے اسے اور اُن لوگوں کو جو اس کے ساتھ کشتی میں تھے، بچا لیا اور انہی کو زمین میں جانشین بنایا اور ان سب لوگوں کو غرق کر دیا جنہوں نے ہماری آیات کو جھٹلایا تھا پس دیکھ لو کہ جنہیں متنبہ کیا گیا تھا (اور پھر بھی انہوں نے مان کر نہ دیا) اُن کا کیا انجام ہوا');
INSERT INTO "ur_maududi" VALUES(10,74,'10:74','پھر نوح ؑ کے بعد ہم نے مختلف پیغمبروں کو اُن کی قوموں کی طرف بھیجا اور وہ اُن کے پاس کھُلی کھُلی نشانیاں لے کر آئے، مگر جس چیز کو اُنہوں نے پہلے جُھٹلا دیا تھا اسے پھر مان کر نہ دیا۔ اِس طرح ہم حد سے گزر جانے والوں کے دلوں پر ٹَھپّہ لگا دیتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(10,75,'10:75',' پھر ان کے بعد ہم نے موسیٰ ؑ اور ہارون ؑ کو اپنی نشانیوں کے ساتھ فرعون اور اس کے سرداروں کی طرف بھیجا، مگر انہوں نے اپنی بڑائی کا گھمنڈ کیا اور وہ مجرم لوگ تھے');
INSERT INTO "ur_maududi" VALUES(10,76,'10:76','پس جب ہمارے پاس سے حق اُن کے سامنے آیا تو اُنہوں نے کہہ دیا کہ یہ تو کُھلا جادُو ہے۔ ');
INSERT INTO "ur_maududi" VALUES(10,77,'10:77','موسیٰ ؑ نے کہا: ”تم حق کو یہ کہتے ہو جب کہ وہ تمہارے سامنے آگیا؟ کیا یہ جادُو ہے؟ حالانکہ حالانکہ جادُو گر فلاح نہیں پایا کرتے۔“ ');
INSERT INTO "ur_maududi" VALUES(10,78,'10:78','اُنہوں نے جواب دیا”کیا تُو اِس لیے آیا ہے کہ ہمیں اُس طریقے سے پھیر دے جس پر ہم نے اپنے باپ دادا کو پایا ہے اور زمین میں بڑائی تُم دونوں کی قائم ہو جائے؟ تمہاری بات تو ہم ماننے والے نہیں ہیں۔“');
INSERT INTO "ur_maududi" VALUES(10,79,'10:79','اور فرعون نے (اپنے آدمیوں سے) کہا کہ“ہر ماہر فن جادوگر کو میرے پاس حاضر کرو "');
INSERT INTO "ur_maududi" VALUES(10,80,'10:80','جب جادو گر آ گئے تو موسیٰؑ نے ان سے کہا“جو کچھ تمہیں پھینکنا ہے پھینکو"');
INSERT INTO "ur_maududi" VALUES(10,81,'10:81','پھر جب اُنہوں نے اپنے اَنچھر پھینک دیے تو موسیٰ ؑ نے کہا”یہ جو کچھ تم نے پھینکا ہے یہ جادُو ہے ، اللہ ابھی اسے باطل کیے دیتا ہے، مفسدوں کے کام کو اللہ سُدھرنے نہیں دیتا');
INSERT INTO "ur_maududi" VALUES(10,82,'10:82','اور اللہ اپنے فرمانوں سے حق کو حق کر دکھاتا ہے، خواہ مجرموں کو وہ کتنا ہی ناگوار ہو"');
INSERT INTO "ur_maududi" VALUES(10,83,'10:83','(پھر دیکھو کہ) موسیٰ ؑ کو اِس قوم میں سے چند نوجوانوں کے سوا کسی نے نہ مانا ، فرعون کے ڈر سے اور خود اپنی قوم کے سربرآوردہ لوگوں کے ڈر سے ( جنہیں خوف تھا کہ) فرعون اُن کو عذاب میں مبتلا کرے گا۔ اور واقعہ یہ ہے کہ فرعون زمین میں غلبہ رکھتا تھا اور وہ اُن لوگوں میں سے تھا جو کسی حد پر رُکتے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(10,84,'10:84','موسیٰؑ نے اپنی قوم سے کہا کہ ”لوگو، اگر تم واقعی اللہ پر ایمان رکھتے ہو تو اُس پر بھروسہ کرو اگر مسلمان ہو۔ ');
INSERT INTO "ur_maududi" VALUES(10,85,'10:85','اُنہوں نے جواب دیا” ہم نے اللہ ہی پر بھروسہ کیا ، اے ہمارے ربّ، ہمیں ظالم لوگوں کے لیے فتنہ نہ بنا ');
INSERT INTO "ur_maududi" VALUES(10,86,'10:86','اور اپنی رحمت سے ہم کو کافروں سے نجات دے"');
INSERT INTO "ur_maududi" VALUES(10,87,'10:87','اور ہم نے موسیٰ ؑ اور اس کے بھائی کو اشارہ کیا کہ ”مصر میں چند مکان اپنی قوم کے لیے مہیّا کرو اور اپنے ان مکانوں کو قبلہ ٹھہرا لواور نماز قائم کرو اور اہلِ ایمان کو بشارت دے دو۔“ ');
INSERT INTO "ur_maududi" VALUES(10,88,'10:88','موسیٰؑ نے دعا کی”اے ہمارے ربّ ، تُو نے فرعون اور اُس کے سرداروں کو دُنیا کی زندگی میں زینت اور اموال سے نواز رکھا ہے۔ اے ربّ ، کیا یہ اِس لیے ہے کہ وہ لوگوں کو تیری راہ سے بھٹکائیں؟ اے ربّ ، ان کے مال غارت کر دے اور ان کے دلوں پر ایسی مُہر کر دے کہ ایمان نہ لائیں جب تک دردناک عذاب نہ دیکھ لیں۔“ ');
INSERT INTO "ur_maududi" VALUES(10,89,'10:89','اللہ تعالیٰ نے جواب میں فرمایا ”تم دونوں کی دعا قبول کی گئی۔ ثابت قدم رہو اور ان لوگوں کے طریقے کی ہرگز پیروی نہ کرو جو علم نہیں رکھتے۔“ ');
INSERT INTO "ur_maududi" VALUES(10,90,'10:90','اور ہم بنی اسرائیل کو سمندر سے گُزار لے گئے۔ پھر فرعون اور اُس کے لشکر ظلم اور زیادتی کی غرض سے اُن کے پیچھے چلے۔۔۔۔ حتٰی کہ جب فرعون ڈوبنے لگا تو بول اُٹھا ”میں نے مان لیا کہ خدا وند ِ حقیقی اُس کے سوا کوئی نہیں ہے جِس پر بنی اسرائیل ایمان لائے، اور میں بھی سرِاطاعت جھکا دینے والوں میں سے ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(10,91,'10:91','(جواب دیا گیا)“اب ایمان لاتا ہے! حالانکہ اِس سے پہلے تک تو نافرمانی کرتا رہا اور فساد برپا کرنے والوں میں سے تھا');
INSERT INTO "ur_maududi" VALUES(10,92,'10:92','اب تو ہم صرف تیری لاش ہی کو بچائیں گے تا کہ تُو بعد کی نسلوں کے لیے نشانِ عبرت بنے اگرچہ بہت سے انسان ایسے ہیں جو ہماری نشانیوں سے غفلت برتتے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(10,93,'10:93','ہم نے بنی اسرائیل کو بہت اچھا ٹھکانہ دیا اور نہایت عمدہ وسائل ِ زندگی انہیں عطا کیے۔ پھر اُنہوں نے بہم اختلاف نہیں کیا مگر اُس وقت جب کہ علم اُن کے پاس آچکا تھا۔ یقیناً تیرا ربّ قیامت کے روز اُن کے درمیان اُس چیز کا فیصلہ کر دے گا جس میں وہ اختلاف کر تے رہے ہیں');
INSERT INTO "ur_maududi" VALUES(10,94,'10:94','اب اگر تجھے اُس ہدایت کی طرف سے کچھ بھی شک ہو جو ہم نے تجھ پر نازل کی ہے تو اُن لوگوں سے پوچھ لے جو پہلے سے کتاب پڑھ رہے ہیں فی الواقع یہ تیرے پاس حق ہی آیا ہے تیرے رب کی طرف سے، لہٰذا تو شک کرنے والوں میں سے نہ ہو');
INSERT INTO "ur_maududi" VALUES(10,95,'10:95','اور ان لوگوں میں شامل نہ ہو جنہوں نے اللہ کی آیات کو جُھٹلایا ہے، ورنہ تُو نقصان اُٹھانے والوں میں سے ہو گا۔ ');
INSERT INTO "ur_maududi" VALUES(10,96,'10:96','حقیقت یہ ہے کہ جن لوگوں پر تیرے ربّ کا قول راست آگیا ہے'' وہ ایمان نہیں لانے کے');
INSERT INTO "ur_maududi" VALUES(10,97,'10:97','ان کے سامنے خواہ کوئی نشانی آجائے جب تک کہ دردناک عذاب سامنے آتا نہ دیکھ لیں');
INSERT INTO "ur_maududi" VALUES(10,98,'10:98','پھر کیا ایسی کوئی مثال ہے کہ ایک بستی عذاب دیکھ کر ایمان لائی ہو اور اُس کا ایمان اس کے لیے نفع بخش ثابت ہوا ہو؟ یونسؑ کی قوم کے سوا (اِس کی کوئی نظیر نہیں)۔ وہ قوم جب ایمان لے آئی تھی تو البتہ ہم نے اُس پر سے دُنیا کی زندگی میں رُسوائی کا عذاب ٹال دیا تھا اور اُس کو ایک مدّت تک زندگی سے بہرہ مند ہونے کا موقع دے دیاتھا۔ ');
INSERT INTO "ur_maududi" VALUES(10,99,'10:99','اگر تیرے ربّ کی مشیّت یہ ہوتی (کہ زمین میں سب مومن و فرمانبردار ہی ہوں)تو سارے اہلِ زمین ایمان لے آئے ہوتے۔ پھر کیا تُو لوگوں کو مجبور کرے گا کہ وہ مومن ہو جائیں؟ ');
INSERT INTO "ur_maududi" VALUES(10,100,'10:100','کوئی متنفس اللہ کے اذن کے بغیر ایمان نہیں لاسکتا، اور اللہ کا طریقہ یہ ہے کہ جو لوگ عقل سے کام نہیں لیتے وہ ان پر گندگی ڈال دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(10,101,'10:101','اِن سے کہو”زمین اور آسمانوں میں جو کچھ ہے اُسے آنکھیں کھول کر دیکھو۔“ اور جو لوگ ایمان لانا ہی نہیں چاہتے اُن کے لیے نشانیاں اور تنبیہیں آخر کیا مُفید ہو سکتی ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(10,102,'10:102','اب یہ لوگ اِس کے سوا اور کس چیز کے منتظر ہیں کہ وہی برے دن دیکھیں جو اِن سے پہلے گزرے ہوئے لوگ دیکھ چکے ہیں؟ اِن سے کہو“اچھا، انتظار کرو، میں بھی تمہارے ساتھ انتظار کرتا ہوں"');
INSERT INTO "ur_maududi" VALUES(10,103,'10:103','پھر (جب ایسا وقت آتا ہے تو) ہم اپنے رسولوں کو اور اُن لوگوں کو بچا لیا کرتے ہیں جو ایمان لائے ہوں ہمارا یہی طریقہ ہے ہم پر یہ حق ہے کہ مومنوں کو بچا لیں');
INSERT INTO "ur_maududi" VALUES(10,104,'10:104','اے نبیؐ ! کہہ دو کہ لوگو، ”اگر تم ابھی تک میرے دین کے متعلق کسی شک میں ہو تو سُن لو کہ تم اللہ کے سوا جن کی بندگی کرتے ہو میں اُن کی بندگی نہیں کرتا بلکہ صرف اُسی خدا کر بندگی کرتا ہوں جس کے قبضے میں تمہاری موت ہے۔ مجھے حکم دیا گیا ہے کہ میں ایمان لانے والوں میں سے ہوں');
INSERT INTO "ur_maududi" VALUES(10,105,'10:105','اور مجھ سے فرمایا گیا ہےکہ تُویکسُو ہو کر اپنے آپ کو ٹھیک ٹھیک اس دین پر قائم کر دے ، اور ہرگز ہرگز مشرکوں میں سے نہ ہو۔ ');
INSERT INTO "ur_maududi" VALUES(10,106,'10:106','اور اللہ کو چھوڑ کر کسی ایسی ہستی کو نہ پکار جو تجھے نہ فائدہ پہنچا سکتی ہے نہ نقصان اگر تو ایسا کرے گا تو ظالموں میں سے ہوگا');
INSERT INTO "ur_maududi" VALUES(10,107,'10:107','اگر اللہ تجھے کسی مصیبت میں ڈالے تو خود اس کے سوا کوئی نہیں جو اس مصیبت کو ٹال دے، اور اگر وہ تیرے حق میں کسی بھلائی کا ارادہ کرے تو اس کے فضل کو پھیرنے والا بھی کوئی نہیں ہے وہ اپنے بندوں میں سے جس کو چاہتا ہے اپنے فضل سے نوازتا ہے اور وہ درگزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(10,108,'10:108','اے محمدؐ، کہہ دو کہ“لوگو، تمہارے پاس تمہارے رب کی طرف سے حق آ چکا ہے اب جو سیدھی راہ اختیار کرے اس کی راست روی اسی کے لیے مفید ہے، اور جو گمراہ رہے اس کی گمراہی اسی کے لیے تباہ کن ہے اور میں تمہارے اوپر کوئی حوالہ دار نہیں ہوں"');
INSERT INTO "ur_maududi" VALUES(10,109,'10:109','اور اے نبیؐ، تم اس ہدایت کی پیروی کیے جاؤ جو تمہاری طرف بذریعہ وحی بھیجی جا رہی ہے، اور صبر کرو یہاں تک کہ اللہ فیصلہ کر دے، اور وہی بہترین فیصلہ کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(11,1,'11:1','ا ل ر۔ فرمان ہے ، جس کی آیتیں پُختہ اور مفصّل ارشا د ہوئی ہیں ، ایک دانا اور با خبر ہستی کی طرف سے');
INSERT INTO "ur_maududi" VALUES(11,2,'11:2','کہ تم بندگی نہ کرو مگر صرف اللہ کی میں اُس کی طرف سے تم کو خبردار کرنے والا بھی ہوں اور بشارت دینے والا بھی');
INSERT INTO "ur_maududi" VALUES(11,3,'11:3','اور یہ کہ تم اپنے ربّ سے معافی چاہو اور اُس کی طرف پلٹ آوٴ تو وہ ایک مدّت ِ خاص تک تم کو اچھا سامانِ زندگی دے گا اور ہر صاحبِ فضل کو اس کا فضل عطا کرے گا۔ لیکن اگر تم منہ پھیرتے ہو تو میں تمہارے حق میں ایک بڑے ہولناک دن کے عذاب سے ڈرتا ہوں');
INSERT INTO "ur_maududi" VALUES(11,4,'11:4','تم سب کو اللہ کی طرف پلٹنا ہے اور وہ سب کچھ کرسکتا ہے');
INSERT INTO "ur_maududi" VALUES(11,5,'11:5','دیکھو! یہ لوگ اپنے سینوں کو موڑتے ہیں تاکہ اُس سے چھُپ جائیں۔ خبردار! جب کہ کپڑوں سے اپنے آپ کو ڈھانپتے ہیں ، اللہ ان کے چھُپے کو بھی جانتا ہےاور کُھلے کو بھی ، وہ تو اُن بھیدوں سے بھی واقف ہے جو سینوں میں ہیں۔');
INSERT INTO "ur_maududi" VALUES(11,6,'11:6','زمین میں چلنے والا کوئی جاندار ایسا نہیں ہے جس کا رزق اللہ کے ذمّے نہ ہواور جس کے متعلق وہ نہ جانتا ہو کہ کہاں وہ رہتا ہےاور کہاں وہ سونپا جاتا ہے، سب کچھ ایک صاف دفتر میں درج ہے');
INSERT INTO "ur_maududi" VALUES(11,7,'11:7','اور وہی ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا۔۔۔۔جبکہ اس سے پہلے اُس کا عرش پانی پر تھا ۔۔۔۔۔۔ تاکہ تم کو آزما کر دیکھے تم میں کون بہتر عمل کرنے والا ہے۔ اب اگر اے محمد ؐ ، تم کہتے ہو کہ لوگو، مرنے کے بعد تم دوبارہ اُٹھائے جاوٴ گے تو منکرین فوراً بول اُٹھتے ہیں کہ یہ تو صریح جادوگری ہے۔ ');
INSERT INTO "ur_maududi" VALUES(11,8,'11:8','اور اگر ہم ایک خاص مدت تک ان کی سزا کو ٹالتے ہیں تو وہ کہنے لگتے ہیں کہ آخر کس چیز نے اُسے روک رکھا ہے؟ سنو! جس روز اُس سزا کا وقت آگیا تو وہ کسی کے پھیرے نہ پھر سکے گا اور وہی چیز ان کو آ گھیرے گی جس کا وہ مذاق اڑا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(11,9,'11:9','اگر کبھی ہم انسان کو اپنی رحمت سے نوازنے کے بعد پھر اس سے محروم کر دیتے ہیں تو وہ مایوس ہوتا ہے اور ناشکری کرنے لگتا ہے');
INSERT INTO "ur_maududi" VALUES(11,10,'11:10','اور اگر اُس مصیبت کے بعد جو اُس پر آئی تھی ہم اُسے نعمت کا مزہ چکھاتے ہیں تو کہتا ہے میرے تو سارے دِلَدّرپار ہو گئے، پھر وہ پھُولا نہیں سماتا اور اکڑنے لگتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(11,11,'11:11','اس عیب سے پاک اگر کوئی ہیں تو بس وہ لوگ جو صبر کرنے والے اور نیکوکار ہیں اور وہی ہیں جن کے لیے درگزر بھی ہے اور بڑا اجر بھی۔ ');
INSERT INTO "ur_maududi" VALUES(11,12,'11:12','تو اے پیغمبر ؐ ، کہیں ایسا نہ ہو کہ تم اُن چیزوں میں سے کسی چیز کو (بیان کرنے سے ) چھوڑ دو جو تمہاری طرف وحی کی جا رہی ہیں۔ اور اس بات پر دل تنگ ہو کہ وہ کہیں گے ”اسِ شخص پر کوئی خزانہ کیوں نہ اُتارا گیا“ یا یہ کہ ”اِس کے ساتھ کوئی فرشتہ کیوں نہ آیا۔“ تم تو محض خبردار کرنے والے ہو، آگے ہر چیز کا حوالہ دار اللہ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(11,13,'11:13','کیا یہ کہتے ہیں کہ پیغمبر نے یہ کتاب خود گھڑ لی ہے؟ کہو، "اچھا یہ بات ہے تو اس جیسی گھڑی ہوئی دس سورتیں تم بنا لاؤ اور اللہ کے سوا اور جو جو (تمہارے معبود) ہیں اُن کو مدد کے لیے بلا سکتے ہو تو بلا لو اگر تم (انہیں معبود سمجھنے میں) سچے ہو');
INSERT INTO "ur_maududi" VALUES(11,14,'11:14','اب اگر وہ(تمہارے معبود ) تمہاری مدد کو نہیں پہنچتے تو جان لو کہ یہ اللہ کے علم سے نازل ہوئی ہے اور یہ کہ اللہ کے سوا کوئی حقیقی معبود نہیں ہے۔ پھر کیا تم ( اِس امرِ حق کے آگے ) سرِ تسلیم خم کرتے ہو؟“ ');
INSERT INTO "ur_maududi" VALUES(11,15,'11:15','جو لوگ بس اِسی دُنیا کی زندگی اور اس کی خوشنمائیوں کے طالب ہوتے ہیں اُن کی کار گزاری کا سارا پھل ہم یہیں اُن کو دے دیتے ہیں اور اس میں ان کے ساتھ کوئی کمی نہیں کی جاتی');
INSERT INTO "ur_maududi" VALUES(11,16,'11:16','مگر آخرت میں ایسے لوگوں کے لیے آگ کے سوا کچھ نہیں ہے۔ ( وہاں معلوم ہو جائے گا کہ) جو کچھ اِنہوں نے دُنیا میں بنایا وہ سب ملیا میٹ ہو گیا اور اب ان کا سارا کِیا دھرا محض باطل ہے');
INSERT INTO "ur_maududi" VALUES(11,17,'11:17','پھر بھلا وہ شخص جو اپنے ربّ کی طرف سے ایک صاف شہادت رکھتا تھا ، اس کے بعد ایک گواہ بھی پروردگار کی طرف سے ( اِس شہادت کی تائید میں) آگیا، اور پہلے موسیٰ ؑ کی کتا ب رہنما اور رحمت کے طور پر آئی ہوئی بھی موجود تھی( کیا وہ بھی دُنیا پرستوں کی طرح اس سے انکار کر سکتا ہے؟) ایسے لوگ تو اس پر ایمان ہی لائیں گے ۔ اور انسانی گروہوں میں سے جو کوئی اِ س کا انکار کرے تو اس کے لیے جس جگہ کا وعدہ ہے وہ دوزخ ہے۔ پس اے پیغمبر ؐ ، تم اس چیز کی طرف سے کسی شک میں نہ پڑنا، یہ حق ہے تمہارے ربّ کی طرف سے مگر اکثر لوگ نہیں مانتے');
INSERT INTO "ur_maududi" VALUES(11,18,'11:18','اور اُس شخص سے بڑھ کر ظالم اور کون ہو گا جو اللہ پر جھوٹ گھڑے۔ ایسے لوگ اپنے ربّ کے حضُور پیش ہونگے اور گواہ شہادت دیں گے کہ یہ ہیں وہ لوگ جنہوں نے اپنے ربّ پر جھوٹ گھڑا تھا۔ سُنو! خُدا کی لعنت ہے ظالموں پر ');
INSERT INTO "ur_maududi" VALUES(11,19,'11:19','اُن ظالموں پر جو خُدا کے راستے سے لوگوں کو روکتے ہیں، اُس کے راستے کو ٹیڑھا کرنا چاہتے ہیں اور آخرت کا انکار کرتے ہیں۔۔۔۔ ');
INSERT INTO "ur_maududi" VALUES(11,20,'11:20','وہ زمین میں اللہ کو بے بس کرنے والے نہ تھے اور نہ اللہ کے مقابلے میں کوئی اُن کا حامی تھا۔ اُنہیں اب دوہرا عذاب دیا جائے گا۔ وہ نہ کسی کی سُن ہی سکتے تھے اور نہ خود ہی اُنہیں کچھ سُو جھتا تھا');
INSERT INTO "ur_maududi" VALUES(11,21,'11:21','یہ وہ لوگ ہیں جنہوں نے اپنے آپ کو خود گھاٹے میں ڈالا اور وہ سب کچھ اُن سے کھویا گیا جو اُنہوں نے گھڑ رکھا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(11,22,'11:22','نا گزیر ہے کہ وہی آخرت میں سب سے بڑھ کر گھاٹے میں رہیں');
INSERT INTO "ur_maududi" VALUES(11,23,'11:23','رہے وہ لوگ جو ایمان لائے اور جنہوں نے نیک عمل کیے اور اپنے ربّ ہی کے ہو کر رہے ، تو یقیناً وہ جنتّی لوگ ہیں اور جنّت میں وہ ہمیشہ رہیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(11,24,'11:24','اِن دونوں فریقوں کی مثال ایسی ہے جیسے ایک آدمی تو ہو اندھا بہرا اور دوسرا ہو دیکھنے اور سُننے والا، کیا یہ دونوں یکساں ہو سکتے ہیں؟ کیا تم ( اِس مثال سے) کوئی سبق نہیں لیتے؟');
INSERT INTO "ur_maududi" VALUES(11,25,'11:25','(اور ایسے ہی حالات تھے جب) ہم نے نوح ؑ کو اُس کی قوم کی طرف بھیجا تھا۔ (اُس نے کہا) ”میں تم لوگوں کو صاف صاف خبردار کرتا ہوں');
INSERT INTO "ur_maududi" VALUES(11,26,'11:26','کہ اللہ کے سوا کسی کی بندگی نہ کرو ورنہ مجھے اندیشہ ہے کہ تم پر ایک روز دردناک عذاب آئے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(11,27,'11:27','جواب میں اُس کی قوم کے سردار، جنہوں نے اس کی بات ماننے سے انکار کیا تھا، بولے ”ہمارے نظر میں تو تم اس کے سوا کچھ نہیں ہو کہ بس ایک انسان ہو ہم جیسے۔ اور ہم دیکھ رہے ہیں کہ ہماری قوم میں سے بس اُن لوگوں نے جو ہمارے ہاں اراذل تھے بے سوچے سمجھے تمہاری پیروی اختیار کر لی ہے۔ اور ہم کوئی چیز بھی ایسی نہیں پاتے جس میں تم لوگ ہم سے کچھ بڑھے ہوئے ہو ، بلکہ ہم تو تمہیں جھوٹا سمجھتے ہیں۔“');
INSERT INTO "ur_maududi" VALUES(11,28,'11:28','اس نے کہا ”اے برادرانِ قوم، ذرا سوچو تو سہی کہ اگر میں اپنے ربّ کی طرف سے ایک کھُلی شہادت پر قائم تھا اور پھر اس نے مجھ کو اپنی خاص رحمت سے بھی نوازدیا مگر وہ تم کو نظر نہ آئی تو آخر ہمارے پاس کیا ذریعہ ہے کہ تم ماننا نہ چاہو اور ہم زبردستی اس کو تمہارے سَر چپیک دیں؟');
INSERT INTO "ur_maududi" VALUES(11,29,'11:29','اور اے برادرانِ قوم ، میں اِس کام پر تم سے کوئی مال نہیں مانگتا، میرا اجر تو اللہ کے ذمّہ ہے۔ اور میں اُن لوگوں کودھکے دینے سے بھی رہا جنہوں نے میری بات مانی ہے، وہ آپ اپنے ربّ کے حضور جانے والے ہیں۔ مگر میں دیکھتا ہوں کہ تم لوگ جہالت برت رہے ہو');
INSERT INTO "ur_maududi" VALUES(11,30,'11:30','اور اے قوم، اگر میں اِن لوگوں کو دھتکار دوں تو خدا کی پکڑ سے کون مجھے بچانے آئے گا؟ تم لوگوں کی سمجھ میں کیا اتنی بات بھی نہیں آتی؟');
INSERT INTO "ur_maududi" VALUES(11,31,'11:31','اور میں تم سے نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں ، نہ یہ کہتا ہوں کہ میں غیب کا علم رکھتا ہوں، نہ یہ میرا دعوٰی ہے کہ میں فرشتہ ہوں۔ اور یہ بھی میں نہیں کہہ سکتا کہ جن لوگوں کو تمہاری آنکھیں حقارت سے دیکھتی ہیں انہیں اللہ نے کوئی بھلائی نہیں دی۔ ان کے نفس کا حال اللہ ہی بہتر جانتا ہے۔ اگر میں ایسا کہوں تو ظالم ہوں گا۔“');
INSERT INTO "ur_maududi" VALUES(11,32,'11:32','آخر کار ان لوگوں نے کہا کہ "اے نوحؑ، تم نے ہم سے جھگڑا کیا اور بہت کر لیا اب تو بس وہ عذاب لے آؤ جس کی تم ہمیں دھمکی دیتے ہو اگر سچے ہو"');
INSERT INTO "ur_maududi" VALUES(11,33,'11:33','نوحؑ نے جواب دیا "وہ تو اللہ ہی لائے گا، اگر چاہے گا، اور تم اتنا بل بوتا نہیں رکھتے کہ اسے روک دو');
INSERT INTO "ur_maududi" VALUES(11,34,'11:34','اب اگر میں تمہاری کچھ خیر خواہی کرنا بھی چاہوں تو میری خیر خواہی تمہیں کوئی فائدہ نہیں دے سکتی جب کہ اللہ ہی نے تمہیں بھٹکا دینے کا ارادہ کر لیا ہو، وہی تمہارا ربّ ہے اور اُسی کی طرف تمہیں پلٹنا ہے۔“');
INSERT INTO "ur_maududi" VALUES(11,35,'11:35','اے محمد ؐ ، کیا یہ لوگ کہتے ہیں کہ اِس شخص نے یہ سب کچھ خود گھڑ لیا ہے؟ ان سے کہو”اگر میں نے یہ خود گھڑا ہے تو مجھ پر اپنے جُرم کی ذمّہ داری ہے ، اور جو جُرم تم کر رہے ہو اس کی ذمّہ داری سے میں بری ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(11,36,'11:36','نوحؑ پر وحی کی گئی کہ تمہاری قوم میں سے جو لوگ ایمان لا چکے بس وہ لا چکے، اب کوئی ماننے والا نہیں ہے ان کے کرتوتوں پر غم کھانا چھوڑو');
INSERT INTO "ur_maududi" VALUES(11,37,'11:37','اور ہماری نگرانی میں ہماری وحی کے مطابق ایک کشتی بنانی شروع کر دو۔ اور دیکھو، جن لوگوں نے ظلم کیا ہے اُن کے حق میں مجھ سے کوئی سفارش نہ کرنا، یہ سارے کے سارے اب ڈوبنے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(11,38,'11:38','نوحؑ کشتی بنا رہا تھا اور اس کی قوم کے سرداروں میں سے جو کوئی اس کے پاس سے گزرتا تھا وہ اس کا مذاق اڑاتا تھا اس نے کہا "اگر تم ہم پر ہنستے ہو تو ہم بھی تم پر ہنس رہے ہیں');
INSERT INTO "ur_maududi" VALUES(11,39,'11:39','عنقریب تمہیں خود معلوم ہو جائے گا کہ کس پر وہ عذاب آتا ہے جو اُسے رُسوا کر دے گا اور کس پر وہ بلا ٹوٹ پڑتی ہے جو ٹالے نہ ٹلے گی۔“ ');
INSERT INTO "ur_maududi" VALUES(11,40,'11:40','یہاں تک کہ جب ہمارا حکم آگیا اور وہ تنور اُبل پڑا تو ہم نے کہا”ہر قسم کے جانوروں کا ایک ایک جوڑا کشتی میں رکھ لو، اپنے گھر والوں کو بھی۔۔۔۔ سوائے اُن اشخاص کے جن کی نشان دہی پہلے کی جا چکی ہے ۔۔۔۔ اس میں سوار کرا دو اور ان لوگوں کو بھی بٹھا لو جو ایمان لائے ہیں۔ “ اور تھوڑے ہی لوگ تھے جو نوح ؑ کے ساتھ ایمان لائے تھے');
INSERT INTO "ur_maududi" VALUES(11,41,'11:41','نوح ؑ نے کہا ”سوار ہو جاوٴ اِس میں، اللہ ہی کے نام سے ہے اس کا چلنا بھی اور اس کا ٹھہرنا بھی ، میرا ربّ بڑا غفور و رحیم ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(11,42,'11:42','کشتی ان لوگوں کو لیے چلی جا رہی تھی اور ایک ایک موج پہاڑ کی طرح اٹھ رہی تھی نوحؑ کا بیٹا دور فاصلے پر تھا نوحؑ نے پکار کر کہا "بیٹا، ہمارے ساتھ سوار ہو جا، کافروں کے ساتھ نہ رہ"');
INSERT INTO "ur_maududi" VALUES(11,43,'11:43','اُس نے پلٹ کر جواب دیا "میں ابھی ایک پہاڑ پر چڑھا جاتا ہوں جو مجھے پانی سے بچا لے گا" نوحؑ نے کہا، آج کوئی چیز اللہ کے حکم سے بچانے والی نہیں ہے سوائے اِس کے کہ اللہ ہی کسی پر رحم فرمائے" اتنے میں ایک موج دونوں کے درمیان حائل ہو گئی اور وہ بھی ڈوبنے والوں میں شامل ہو گیا');
INSERT INTO "ur_maududi" VALUES(11,44,'11:44','حکم ہوا”اے زمین، اپنا سارا پانی نِگل جا اور اے آسمان ، رُک جا۔“ چنانچہ پانی زمین میں بیٹھ گیا، فیصلہ چکا دیا گیا، کشتی جودی پر ٹِک گئی، اور کہہ دیا گیا کہ دُور ہوئی ظالموں کی قوم!');
INSERT INTO "ur_maududi" VALUES(11,45,'11:45','نُوحؑ نے اپنے ربّ کو پکارا۔ کہا ”اے ربّ، میرا بیٹا میرے گھر والوں میں سے ہے اور تیرا وعدہ سچا ہے اور تُو سب حاکموں سے بڑا اور بہتر حاکم ہے۔ “');
INSERT INTO "ur_maududi" VALUES(11,46,'11:46','جواب میں ارشاد ہوا”اے نُوح ؑ ، وہ تیرے گھر والوں میں سے نہیں ہے، وہ تو ایک بگڑا ہُوا کام ہے ، لہٰذا تُو اُس بات کی مجھ سے درخواست نہ کر جس کی حقیقت تُو نہیں جانتا ، میں تجھے نصیحت کر تا ہوں کہ اپنے آپ کو جاہلوں کی طرح نہ بنا لے۔ “');
INSERT INTO "ur_maududi" VALUES(11,47,'11:47','نُوح ؑ نے فوراً عرض کیا”اے میرے ربّ، میں تیری پناہ مانگتا ہوں اِس سے کہ وہ چیز تجھ سے مانگوں جس کا مجھے علم نہیں۔ اگر تُو نے مجھے معاف نہ کیا اور رحم نہ فرمایا تو میں برباد ہو جاوٴں گا۔“ ');
INSERT INTO "ur_maududi" VALUES(11,48,'11:48','حکم ہوا”اے نوح ؑ اُتر جا ، ہماری طرف سے سلامتی اور برکتیں ہیں تُجھ پر اور ان گروہوں پر جو تیرے ساتھ ہیں، اور کچھ گروہ ایسے بھی ہیں جن کو ہم کچھ مدّت سامانِ زندگی بخشیں گے پھر انہیں ہماری طرف سے دردناک عذاب پہنچے گا۔“');
INSERT INTO "ur_maududi" VALUES(11,49,'11:49','اے محمد ؐ ، یہ غیب کی خبریں ہیں جو ہم تمہاری طرف وحی کر رہے ہیں۔ اس سے پہلے نہ تم ان کو جانتے تھے اور نہ تمہاری قوم۔ پس صبر کرو، انجام کار متقیوں ہی کے حق میں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(11,50,'11:50','اور عاد کی طرف ہم نے ان کےبھائی ہُودؑ کو بھیجا۔ اُس نے کہا”اے برادرانِ قوم، اللہ کی بندگی کرو ، تمہارا کوئی خدا اُس کے سوا نہیں ہے۔ تم نے محض جھُوٹ گھڑ رکھے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(11,51,'11:51','اے برادرانِ قوم، اس کام پر میں تم سے کوئی اجر نہیں چاہتا، میرا اجر تو اس کے ذمّہ ہے جس نے مجھے پیدا کیا ہے، کیا تم عقل سے ذرا کام نہیں لیتے؟ ');
INSERT INTO "ur_maududi" VALUES(11,52,'11:52','اور اے میری قوم کے لوگو، اپنے ربّ سے معافی چاہو، پھر اس کی طرف پلٹو، وہ تم پر آسمان کے دہانے کھول دے گا اور تمہاری موجودہ قوت پر مزید قوت کا اضافہ کرے گا۔ مجرموں کی طرح منہ نہ پھیرو۔“');
INSERT INTO "ur_maududi" VALUES(11,53,'11:53','انہوں نے جواب دیا”اے ہُودؑ ، تُو ہمارے پاس کوئی صریح شہادت لے کر نہیں آیا ہے ، اور تیرے کہنے سے ہم اپنے معبودوں کو نہیں چھوڑ سکتے ، اور تُجھ پر ہم ایمان لانے والے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(11,54,'11:54','ہم تو یہ سمجھتے ہیں کہ تیرے اُوپر ہمارے معبودوں میں سے کسی کی مار پڑگئی ہے۔“ ہُود ؑ نے کہا”میں اللہ کی شہادت پیش کرتا ہوں۔ اور تم گواہ رہو کہ یہ جو اللہ کے سوا دُوسروں کو تم نے خدائی میں شریک ٹھہرا رکھا ہے اس سے میں بیزار ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(11,55,'11:55','تم سب کے سب مل کر میرے خلاف اپنی کرنی میں کسر نہ اُٹھا رکھو اور مجھے مہلت نہ دو ');
INSERT INTO "ur_maududi" VALUES(11,56,'11:56','میرا بھروسہ اللہ پر ہے جو میرا ربّ بھی ہے اور تمہارا ربّ بھی۔ کوئی جاندار ایسا نہیں جس کی چوٹی اس کے ہاتھ میں نہ ہو۔ بے شک میرا ربّ سیدھی راہ پر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(11,57,'11:57','اگر تم منہ پھیرتے ہو تو پھیر لو۔ جو پیغام دے کر میں تمہارے پاس بھیجا گیا تھا وہ میں تم کو پہنچا چکا ہوں۔ اب میرا ربّ تمہاریجگہ دُوسری قوم کو اُٹھائے گا اور تم اس کا کچھ نہ بگاڑ سکو گے۔ یقیناً میرا ربّ ہر چیز پر نگراں ہے۔“');
INSERT INTO "ur_maududi" VALUES(11,58,'11:58','پھر جب ہمارا حکم آگیا تو ہم نے اپنی رحمت سے ہودؑ کو اور اُن لوگوں کو جو اُس کے ساتھ ایمان لائے تھے نجات دے دی اور ایک سخت عذاب سے بچا لیا');
INSERT INTO "ur_maududi" VALUES(11,59,'11:59','یہ ہیں عاد ، اپنے ربّ کی آیات سے انہوں نے انکار کیا، اس کے رسُولوں کی بات نہ مانی، اور ہر جبّار دُشمنِ حق کی پیروی کرتے رہے');
INSERT INTO "ur_maududi" VALUES(11,60,'11:60','آخر کار اس دنیا میں بھی ان پر پھٹکار پڑی اور قیامت کے روز بھی سنو! عاد نے اپنے رب سے کفر کیا، سنو! دور پھینک دیے گئے عاد، ہودؑ کی قوم کے لوگ');
INSERT INTO "ur_maududi" VALUES(11,61,'11:61','اور ثمود کی طرف ہم نے اُن کے بھائی صالحؑ کو بھیجا۔ اُس نے کہا”اے میری قوم کے لوگو، اللہ کی بندگی کرو، اُس کے سوا تمہارا کوئی خُدا نہیں ہے۔ وہی ہے جس نے تم کو زمین سے پیدا کیا اور یہاں تم کو بسایا ہے۔ لہٰذا تم اُس سے معافی چاہو اور اُس کی طرف پلٹ آوٴ، یقیناً میرا ربّ قریب ہے اور وہ دعاؤں کا جواب دینے والا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(11,62,'11:62','اُنہوں نے کہا”اے صالح ؑ ، اس سے پہلے تُو ہمارے درمیان ایسا شخص تھا جس سے بڑی توقعات وابستہ تھیں۔ کیا تُو ہمیں اُن معبودوں کی پرستش سے روکنا چاہتا ہے جن کی پرستش ہمارے باپ دادا کرتے تھے؟ تُو جس طریقے کی طرف ہمیں بُلا رہا ہے اُس کے بارے میں ہم کو سخت شُبہ ہے جس نے ہمیں خلجان میں ڈال رکھا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(11,63,'11:63','صالح ؑ نے کہا ”اے برادرانِ قوم، تم نے کچھ اس بات پر بھی غور کیا کہ اگر میں اپنے ربّ کی طرف سے ایک صاف شہادت رکھتا تھا، اور پھر اس نے اپنی رحمت سے بھی مجھ کو نواز دیا تو اِس کے بعد اللہ کی پکڑ سے مجھے کون بچائے گا اگر میں اُس کی نافرمانی کروں؟ تم میرے کس کام آسکتے ہو سوائے اس کے کہ مجھے اَور زیادہ خسارے میں ڈال دو۔ ');
INSERT INTO "ur_maududi" VALUES(11,64,'11:64','اور اے میری قوم کے لوگو، دیکھو یہ اللہ کی اونٹنی تمہارے لیے ایک نشانی ہے اِسے خدا کی زمین میں چرنے کے لیے آزاد چھوڑ دو اِس سے ذرا تعرض نہ کرنا ورنہ کچھ زیادہ دیر نہ گزرے گی کہ تم پر خدا کا عذاب آ جائے گا"');
INSERT INTO "ur_maududi" VALUES(11,65,'11:65','مگر انہوں نے اونٹنی کو مار ڈالا اس پر صالحؑ نے اُن کو خبردار کر دیا کہ "بس اب تین دن اپنے گھروں میں اور رہ بس لو یہ ایسی میعاد ہے جو جھوٹی نہ ثابت ہوگی"');
INSERT INTO "ur_maududi" VALUES(11,66,'11:66','آخرِ کار جب ہمارے فیصلے کا وقت آگیا تو ہم نے اپنی رحمت سے صالح ؑ کو اور اُن لوگوں کو جو اس کے ساتھ ایمان لائے تھے بچا لیا اور اُس دن کی رُسوائی سے ان کو محفوظ رکھا ۔ بے شک تیرا ربّ ہی دراصل طاقتور اور بالا دست ہے');
INSERT INTO "ur_maududi" VALUES(11,67,'11:67','رہے وہ لوگ جنہوں نے ظلم کیا تھا تو ایک سخت دھماکے نے ان کو دھر لیا اور وہ اپنی بستیوں میں اس طرح بے حس و حرکت پڑے کے پڑے رہ گئے');
INSERT INTO "ur_maududi" VALUES(11,68,'11:68','کہ گویا وہ وہاں کبھی بسے ہی نہ تھے سنو! ثمود نے اپنے رب سے کفر کیا سنو! دور پھینک دیے گئے ثمود!');
INSERT INTO "ur_maududi" VALUES(11,69,'11:69','اور دیکھو، ابراہیم ؑ کے پاس ہمارے فرشتے خوشخبری لیے ہوئے پہنچے۔ کہا ، تم پر سلام ہو۔ ابراہیم ؑ نے جواب دیا ، تم پر بھی سلام ہو۔ پھر کچھ دیر نہ گزری کہ ابراہیم ؑ ایک بُھنا ہوا بچھڑا (ان کی ضیافت کے لیے) لے آیا۔ ');
INSERT INTO "ur_maududi" VALUES(11,70,'11:70','مگر جب دیکھا کہ ان کے ہاتھ کھانے پر نہیں بڑھتے تو وہ ان سے مشتبہ ہو گیا اور دل میں ان سے خوف محسوس کرنے لگا۔ اُنہوں نے کہا”ڈرو نہیں، ہم تو لُوط ؑ کی قوم کی طرف بھیجے گئے ہیں ');
INSERT INTO "ur_maududi" VALUES(11,71,'11:71','ابراہیم ؑ کی بیوی بھی کھڑی ہوئی تھی۔وہ یہ سُن کر ہنس دی۔ پھر ہم نے اُس کو اسحاق کی اور اسحاق کے بعد یعقوب ؑ کی خوشخبری دی');
INSERT INTO "ur_maududi" VALUES(11,72,'11:72','وہ بولی ”ہا ئے میری کم بختی! کیا اب میرے ہاں اولاد ہوگی جبکہ میں بڑھیا پُھونس ہو گئی اور یہ میرے میاں بھی بُوڑھے ہو چکے؟ یہ تو بڑے عجیب بات ہے۔“');
INSERT INTO "ur_maududi" VALUES(11,73,'11:73','فرشتوں نے کہا”اللہ کے حکم پر تعجب کرتی ہو؟ ابراہیم ؑ کے گھر والو ، تم لوگوں پر تو اللہ کی رحمت اور اُس کی برکتیں ہیں، اور یقیناً اللہ نہایت قابلِ تعریف اور بڑی شان والا ہے۔“');
INSERT INTO "ur_maududi" VALUES(11,74,'11:74','پھر جب ابراہیم ؑ کی گھبراہٹ دُور ہوگئی اور (اولاد کی بشارت سے) اُس کا دل خوش ہوگیا تو اُس نے قومِ لُوط کے معاملہ میں ہم سے جھگڑا شروع کیا۔ ');
INSERT INTO "ur_maududi" VALUES(11,75,'11:75','حقیقت میں ابراہیمؑ بڑا حلیم اور نرم دل آدمی تھا اور ہر حال میں ہماری طرف رجوع کرتا تھا');
INSERT INTO "ur_maududi" VALUES(11,76,'11:76','(آخرِ کار ہمارے فرشتوں نے اس سے کہا) ”اے ابراہیم ؑ ، اس سے باز آجاوٴ، تمہارے ربّ کا حکم ہو چکا ہے اور اب ان لوگوں پر وہ عذاب آکر رہے گا جو کسی کے پھیرے نہیں پھر سکتا۔“ ');
INSERT INTO "ur_maududi" VALUES(11,77,'11:77','اور جب ہمارے فرشتے لُوط کے پاس پہنچے تو اُن کی آمد سے وہ بہت گھبرایا اور دل تنگ ہوا اور کہنے لگا کہ ”آج بڑی مصیبت کا دن ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(11,78,'11:78','(ان مہمانوں کا آنا تھا کہ) اس کی قوم کے لوگ بے اختیار اس کے گھر کی طرف دَوڑ پڑے۔ پہلے سے وہ ایسی ہی بدکاریوں کے خوگر تھے۔ لُوط ؑ نے ان سے کہا”بھائیو، یہ میری بیٹیاں موجود ہیں، یہ تمہارے لیے پاکیزہ تر ہیں۔ کچھ خدا کا خوف کرو اور میرے مہمانوں کے معاملہ میں مجھے ذلیل نہ کرو۔ کیا تم میں کوئی بھلا آدمی نہیں ؟“');
INSERT INTO "ur_maududi" VALUES(11,79,'11:79','انہوں نے جواب دیا”تجھے تو معلوم ہی ہے کہ تیری بیٹیوں میں ہمارا کوئی حصہ نہیں ہے اور تُو یہ بھی جانتا ہے کہ ہم چاہتے کیا ہیں۔“');
INSERT INTO "ur_maududi" VALUES(11,80,'11:80','لوطؑ نے کہا "کاش میرے پاس اتنی طاقت ہوتی کہ تمہیں سیدھا کر دیتا، یا کوئی مضبوط سہارا ہی ہوتا کہ اس کی پناہ لیتا"');
INSERT INTO "ur_maududi" VALUES(11,81,'11:81','تب فرشتوں نے اس سے کہا کہ ”اے لُوط ؑ ، ہم تیرے ربّ کے بھیجے ہوئے فرشتے ہیں، یہ لوگ تیرا کچھ نہ بگاڑ سکیں گے۔ بس تُو کچھ رات رہے اپنے اہل و عیال کو لے کر نِکل جا۔ اور دیکھو، تم میں سے کوئی شخص پیچھے پلٹ کر نہ دیکھے۔ مگر تیری بیوی ( ساتھ نہیں جائے گی) کیونکہ اس پر بھی وہی کچھ گزرنے والا ہے جو اِن لوگوں پر گزرنا ہے۔ ان کی تباہی کے لیے صبح کا وقت مقرر ہے۔۔۔۔ صبح ہوتے اب دیر ہی کتنی ہے!“');
INSERT INTO "ur_maududi" VALUES(11,82,'11:82','پھر جب ہمارے فیصلے کا وقت آپہنچا تو ہم نے اس بستی کو تل پٹ کر دیا اور اس پر پکی ہوئی مَٹی کے پتھر تابڑ توڑ برسائے ');
INSERT INTO "ur_maududi" VALUES(11,83,'11:83','جن میں سے ہر پتھر تیرے ربّ کے ہاں نشان زدہ تھا۔ اور ظالموں سے یہ سزا کچھ دُور نہیں ہے ');
INSERT INTO "ur_maududi" VALUES(11,84,'11:84','اور مَدیَن والوں کی طرف ہم نے ان کے بھائی شعیب ؑ کو بھیجا۔ اُ س نے کہا”اے میری قوم کے لوگو، اللہ کی بندگی کرو، اس کے سوا تمہارا کوئی خدا نہیں ہے۔ اور ناپ تول میں کمی نہ کرو۔ آج میں تم کو اچھے حال میں دیکھ رہا ہوں، مگر مجھے ڈر ہے کہ کل تم پر ایسا دن آئے گا جس کا عذاب سب کو گھیر لیے گا');
INSERT INTO "ur_maududi" VALUES(11,85,'11:85','اور اے برادران قوم، ٹھیک ٹھیک انصاف کے ساتھ پورا ناپو اور تولو اور لوگوں کو ان کی چیزوں میں گھاٹا نہ دیا کرو اور زمین میں فساد نہ پھیلاتے پھرو');
INSERT INTO "ur_maududi" VALUES(11,86,'11:86','اللہ کی دی ہوئی بچت تمہارے لیے بہتر ہے اگر تم مومن ہو۔ اور بہر حال میں تمہارے اوپر کوئی نگرانِ کار نہیں ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(11,87,'11:87','انہوں نے جواب دیا”اے شعیب ؑ ، کیا تیری نماز تجھے یہ سکھاتی ہے کہ ہم اُن سارے معبودوں کو چھوڑ دیں جن کی پرستش ہمارے باپ دادا کرتے تھے؟ یا یہ کہ ہم کو اپنے مال میں اپنے منشا کے مطابق تصّرف کرنے کا اختیار نہ ہو؟ بس تُو ہی تو ایک عالی ظرف اور راستباز آدمی رہ گیا ہے !“');
INSERT INTO "ur_maududi" VALUES(11,88,'11:88','شعیب ؑ نے کہا ”بھائیو، تم خود ہی سوچو کہ اگر میں اپنے ربّ کی طرف سے ایک کھُلی شہادت پر تھا اور پھر اس نے اپنے ہاں سے مجھ کو اچھا رزق بھی عطا کیا (تو اس کے بعد میں تمہاری گمراہیوں اور حرام خوریوں میں تمہاراشریکِ حال کیسے ہو سکتا ہوں؟)۔اور میں ہر گز یہ نہیں چاہتا کہ جن باتوں سے میں تم کو روکتا ہوں ان کا خود ارتکاب کروں۔ میں تو اصلاح کرنا چاہتا ہوں جہاں تک بھی میرا بس چلے۔ اور یہ جو کچھ میں کرنا چاہتا ہوں اس کا سارا انحصار اللہ کی توفیق پر ہے، اُسی پر میں نے بھروسہ کیا اور ہر معاملہ میں اسی کر طرف رُجوع کرتا ہوں');
INSERT INTO "ur_maududi" VALUES(11,89,'11:89','اور اے برادرانِ قوم، میرے خلاف تمہاری ہٹ دھرمی کہیں یہ نوبت نہ پہنچا دے کہ آخرِ کار تم پر بھی وہی عذاب آکر رہے جو نوح ؑ یا ہُود ؑ یا صالح ؑ کی قوم پر آیا تھا۔ اور ؑ لُوط کی قوم تو تم سے کچھ زیادہ دُور بھی نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(11,90,'11:90','دیکھو! اپنے ربّ سے معافی مانگو اور اس کی طرف پلٹ آوٴ، بے شک میرا ربّ رحیم ہے اور اپنی مخلوق سے محبت رکھتا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(11,91,'11:91','انہوں نے جواب دیا ”اے شعیب ؑ ، تیری بہت سی باتیں تو ہماری سمجھ ہی میں نہیں آتیں۔ اور ہم دیکھتے ہیں کہ تُو ہمارے درمیان ایک بے زور آدمی ہے، تیری برادری نہ ہوتی تو ہم کبھی کا تجھے سنگسار کر چکے ہوتے ، تیرا بل بوتا تو اتنا نہیں ہے کہ ہم پر بھاری ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(11,92,'11:92','شعیبؑ نے کہا "بھائیو، کیا میری برادری تم پر اللہ سے زیادہ بھاری ہے کہ تم نے (برادری کا تو خوف کیا اور) اللہ کو بالکل پس پشت ڈال دیا؟ جان رکھو کہ جو کچھ تم کر رہے ہو وہ اللہ کی گرفت سے باہر نہیں ہے');
INSERT INTO "ur_maududi" VALUES(11,93,'11:93','اے میری قوم کے لوگو، تم اپنے طریقے پر کام کیے جاؤ اور میں اپنے طریقے پر کرتا رہوں گا، جلدی ہی تمہیں معلوم ہو جائے گا کہ کس پر ذلت کا عذاب آتا ہے اور کون جھوٹا ہے تم بھی انتظار کرو اور میں بھی تمہارے ساتھ چشم براہ ہوں"');
INSERT INTO "ur_maududi" VALUES(11,94,'11:94','آخر کار جب ہمارے فیصلے کا وقت آ گیا تو ہم نے اپنی رحمت سے شعیبؑ اور اس کے ساتھی مومنوں کو بچا لیا اور جن لوگوں نے ظلم کیا تھا ان کو ایک سخت دھماکے نے ایسا پکڑا کہ وہ اپنی بستیوں میں بے حس و حرکت پڑے کے پڑے رہ گئے');
INSERT INTO "ur_maududi" VALUES(11,95,'11:95','گویا وہ کبھی وہاں رہے بسے ہی نہ تھے سنو! مدین والے بھی دور پھینک دیے گئے جس طرح ثمود پھینکے گئے تھے');
INSERT INTO "ur_maududi" VALUES(11,96,'11:96','اور موسیٰؑ کو ہم نے اپنی نشانیوں اور کھلی کھلی سند ماموریت کے ساتھ فرعون اور اس کے اعیان سلطنت کی طرف بھیجا');
INSERT INTO "ur_maududi" VALUES(11,97,'11:97','مگر انہوں نے فرعون کے حکم کی پیروی کی حالانکہ فرعون کا حکم راستی پر نہ تھا');
INSERT INTO "ur_maududi" VALUES(11,98,'11:98','قیامت کے روز وہ اپنی قوم کے آگے آگے ہو گا اور اپنی پیشوائی میں انہیں دوزخ کی طرف لے جائے گا۔ کیسی بد تر جائے وُرُود ہے یہ جس پر کوئی پہنچے!');
INSERT INTO "ur_maududi" VALUES(11,99,'11:99','اور ان لوگوں پر دنیا میں بھی لعنت پڑی اور قیامت کے روز بھی پڑے گی کیسا برا صلہ ہے یہ جو کسی کو ملے!');
INSERT INTO "ur_maududi" VALUES(11,100,'11:100','یہ چند بستیوں کی سرگزشت ہے جو ہم تمہیں سنا رہے ہیں ان میں سے بعض اب بھی کھڑی ہیں اور بعض کی فصل کٹ چکی ہے');
INSERT INTO "ur_maududi" VALUES(11,101,'11:101','ہم نے اُن پر ظلم نہیں کیا، انہوں نے آپ ہی اپنے اوپر ستم ڈھایا اور جب اللہ کا حکم آ گیا تو ان کے وہ معبود جنہیں وہ اللہ کو چھوڑ کر پکارا کرتے تھے ان کے کچھ کام نہ آ سکے اور انہوں نے ہلاکت و بربادی کے سوا انہیں کچھ فائدہ نہ دیا');
INSERT INTO "ur_maududi" VALUES(11,102,'11:102','اور تیرا رب جب کسی ظالم بستی کو پکڑتا ہے تو پھر اس کی پکڑ ایسی ہی ہوا کرتی ہے، فی الواقع اس کی پکڑ بڑی سخت اور درد ناک ہوتی ہے');
INSERT INTO "ur_maududi" VALUES(11,103,'11:103','حقیقت یہ ہے کہ اس میں ایک نشانی ہے ہر اُس شخص کے لیے جو عذابِ آخرت کا خوف کرے۔ وہ ایک دن ہو گا جس میں سب لوگ جمع ہوں گے اور پھر جو کچھ بھی اُس روز ہوگا سب کی آنکھوں کے سامنے ہو گا');
INSERT INTO "ur_maududi" VALUES(11,104,'11:104','ہم اس کے لانے میں کچھ بہت زیادہ تاخیر نہیں کر رہے ہیں، بس ایک گنی چنی مدت اس کے لیے مقرر ہے');
INSERT INTO "ur_maududi" VALUES(11,105,'11:105','جب و ہ آئے گا تو کسی کو بات کرنے کی مجال نہ ہو گی، اِلّا یہ کہ خدا کی اجازت سے کچھ عرض کرے۔ پھر کچھ لوگ اس روز بد بخت ہوں گے اور کچھ نیک بخت');
INSERT INTO "ur_maududi" VALUES(11,106,'11:106','جو بد بخت ہوں گے وہ دوزخ میں جائیں گے (جہاں گرمی اور پیاس کی شدت سے) وہ ہانپیں گے اور پھنکارے ماریں گے');
INSERT INTO "ur_maududi" VALUES(11,107,'11:107','اور اسی حالت میں وہ ہمیشہ رہیں گے جب تک کہ زمین و آسمان قائم ہیں ، اِّلا یہ کہ تیرا ربّ کچھ اور چاہے۔ بے شک تیرا ربّ پُورا اختیار رکھتا ہے کہ جو چاہے کرے۔ ');
INSERT INTO "ur_maududi" VALUES(11,108,'11:108','رہے وہ لوگ جو نیک بخت نکلیں گے، تو وہ جنّت میں جائیں گے اور وہاں ہمیشہ رہیں گے جب تک زمین و آسمان قائم ہیں ، اِلّا یہ کہ تیرا ربّ کچھ اور چاہے۔ ایسی بخشش ان کو ملے گی جس کا سلسلہ کبھی منقطع نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(11,109,'11:109','پس اے نبی ؐ ، تُو ان معبودوں کی طرف سے کسی شک میں نہ رہ جن کی یہ لوگ عبادت کر رہے ہیں۔ یہ تو (بس لکیر کے فقیر بنے ہوئے)اُسی طرح پُوجا پاٹ کیے جارہے ہیں جس طرح پہلے ان کے باپ دادا کرتے تھے، اور ہم ان کا حصہ انہیں بھرپور دیں گے بغیر اس کے کہ اس میں کچھ کاٹ کسر ہو');
INSERT INTO "ur_maududi" VALUES(11,110,'11:110','ہم اِس سے پہلے موسیٰ ؑ کو بھی کتا ب دے چکے ہیں اور اس کے بارے میں بھی اختلاف کیا گیا تھا(جس طرح آج اِس کتاب کےبارےمیں کیا جا رہاہے جو تمہیں دی گئی ہے )۔ اگر تیرے ربّ کی طرف سے ایک بات پہلے ہی طے نہ کر دی گئی ہوتی تو اِن اختلاف کرنے والوں کے درمیان کبھی کا فیصلہ چُکا دیا گیا ہوتا۔ یہ واقعہ ہے کہ یہ لوگ ا ِس کی طرف سے شک اور خلجان میں پڑے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(11,111,'11:111','اور یہ بھی واقعہ ہے کہ تیرا رب انہیں ان کے اعمال کا پورا پورا بدلہ دے کر رہے گا، یقیناً وہ ان کی سب حرکتوں سے باخبر ہے');
INSERT INTO "ur_maududi" VALUES(11,112,'11:112','پس اے محمدؐ، تم، اور تمہارے وہ ساتھی جو (کفر و بغاوت سے ایمان و طاعت کی طرف) پلٹ آئے ہیں، ٹھیک ٹھیک راہ راست پر ثابت قدم رہو جیسا کہ تمہیں حکم دیا گیا ہے اور بندگی کی حد سے تجاوز نہ کرو جو کچھ تم کر رہے ہو اس پر تمہارا رب نگاہ رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(11,113,'11:113','اِن ظالموں کی طرف ذرا نہ جھکنا ورنہ جہنم کی لپیٹ میں آ جاؤ گے اور تمہیں کوئی ایسا ولی و سرپرست نہ ملے گا جو خدا سے تمہیں بچا سکے اور کہیں سے تم کو مدد نہ پہنچے گی');
INSERT INTO "ur_maududi" VALUES(11,114,'11:114','اور دیکھو، نماز قائم کرو دن کے دونوں سروں پر اور کچھ رات گزرنے پر۔ در حقیقت نیکیاں برائیوں کو دور کردیتی ہیں، یہ ایک یاد دہانی ہے ان لوگوں کے لئے جو خدا کو یاد رکھنے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(11,115,'11:115','اور صبر کر، اللہ نیکی کرنے والوں کا اجر کبھی ضائع نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(11,116,'11:116','پھر کیوں نہ اُن قوموں میں جو تم سے پہلے گزر چکی ہیں ایسے اہل خیر موجود رہے جو لوگوں کو زمین میں فساد برپا کرنے سے روکتے؟ ایسے لوگ نکلے بھی تو بہت کم، جن کو ہم نے ان قوموں میں سے بچا لیا، ورنہ ظالم لوگ تو انہی مزوں کے پیچھے پڑے رہے جن کے سامان انہیں فراوانی کے ساتھ دیے گئے تھے اور وہ مجرم بن کر رہے');
INSERT INTO "ur_maududi" VALUES(11,117,'11:117','تیرا ربّ ایسا نہیں ہے کہ بستیوں کو ناحق تباہ کر دے حالانکہ ان کے باشندے اصلاح کرنے والے ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(11,118,'11:118','بے شک تیرا رب اگر چاہتا تو تمام انسانوں کو ایک گروہ بنا سکتا تھا، مگر اب تو وہ مختلف طریقوں ہی پر چلتے رہیں گے');
INSERT INTO "ur_maududi" VALUES(11,119,'11:119','اور بے راہ رویوں سے صرف وہ لوگ بچیں گے جن پر تیرے ربّ کی رحمت ہے۔ اِسی (آزادی ِ انتخاب و اختیار)کے لیے ہی تو اس نے انہیں پیدا کیا تھا۔ اور تیرے ربّ کی وہ بات پوری ہوگئی جو اس نے کہی تھی کہ میں جہنّم کو جِن اور انسانوں سے بھر دوں گا');
INSERT INTO "ur_maududi" VALUES(11,120,'11:120','اور اے محمدؐ، یہ پیغمبروں کے قصے جو ہم تمہیں سناتے ہیں، وہ چیزیں ہیں جن کے ذریعے سے ہم تمہارے دل کو مضبوط کرتے ہیں ان کے اندر تم کو حقیقت کا علم ملا اور ایمان لانے والوں کو نصیحت اور بیداری نصیب ہوئی');
INSERT INTO "ur_maududi" VALUES(11,121,'11:121','رہے وہ لوگ جو ایمان نہیں لاتے، تو ان سے کہہ دو کہ تم اپنے طریقے پر کام کرتے رہو اور ہم اپنے طریقے پر کیے جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(11,122,'11:122','انجام کار کا تم بھی انتظار کرو اور ہم بھی منتظر ہیں');
INSERT INTO "ur_maududi" VALUES(11,123,'11:123','آسمانوں اور زمین میں جو کچھ چھُپا ہوا ہے سب اللہ کے قبضہء قدرت میں ہے اور سارا معاملہ اسی کی طرف رُجوع کیا جاتا ہے۔ پس اے نبی ؐ ، تُو اُسی کی بندگی کر اور اُسی پر بھروسا رکھ،جو کچھ تم لوگ کر رہے ہو تیرا ربّ اس سے بے خبر نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(12,1,'12:1','ا، ل، ر یہ اُس کتاب کی آیات ہیں جو اپنا مدعا صاف صاف بیان کرتی ہے');
INSERT INTO "ur_maududi" VALUES(12,2,'12:2','ہم نے اسے نازل کیا ہے قرآن بنا کر عربی زبان میں تا کہ تم (اہلِ عرب)اس کو اچھی طرح سمجھ سکو۔ ');
INSERT INTO "ur_maududi" VALUES(12,3,'12:3','اے محمد ؐ ، ہم اس قرآن کو تمہاری طرف وحی کر کے بہترین پیرایہ میں واقعات اور حقائق تم سے بیان کر تے ہیں، ورنہ اس سے پہلے تو (ان چیزوں سے)تم بالکل ہی بے خبر تھے۔ ');
INSERT INTO "ur_maududi" VALUES(12,4,'12:4','یہ اُس وقت کا ذکر ہے جب یوسفؑ نے اپنے باپ سے کہا "ابا جان، میں نے خواب دیکھا ہے کہ گیارہ ستارے ہیں اور سورج اور چاند ہیں اور وہ مجھے سجدہ کر رہے ہیں"');
INSERT INTO "ur_maududi" VALUES(12,5,'12:5','جواب میں اس کے باپ نے کہا، ”بیٹا، اپنا یہ خواب اپنے بھائیوں کو نہ سُنانا ورنہ وہ تیرے درپے آزار ہو جائیں گے، حقیقت یہ ہے کہ شیطان آدمی کا کُھلا دشمن ہے');
INSERT INTO "ur_maududi" VALUES(12,6,'12:6','اور ایسا ہی ہو گا(جیسا تُو نے خواب میں دیکھا ہے کہ)تیرا ربّ تجھے (اپنے کام کے لیے)منتخب کرے گا اور تجھے باتوں کی تہ تک پہنچنا سکھائے گا اور تیرے اوپر اور آلِ یعقوب ؑ پر اپنی نعمت اسی طرح پوری کرے گا جس طرح اس سے پہلے وہ تیرے بزرگوں ، ابراہیم ؑ اور اسحاق ؑ پر کر چکا ہے، یقیناً تیرا ربّ علیم اور حکیم ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(12,7,'12:7','حقیقت یہ ہے کہ یوسفؑ اور اس کے بھائیوں کے قصہ میں اِن پوچھنے والوں کے لیے بڑی نشانیاں ہیں');
INSERT INTO "ur_maududi" VALUES(12,8,'12:8','یہ قصہ یوں شروع ہوتا ہےکہ اس کے بھائیوں نے آپس میں کہا”یہ یوسف ؑ اور اس کا بھائی، دونوں ہمارے والد کو ہم سب سے زیادہ محبوب ہیں حالانکہ ہم ایک پورا جتھا ہیں، سچی بات یہ ہے کہ ہمارے ابا جان بالکل ہی بہک گئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(12,9,'12:9','چلو یُوسف ؑ کو قتل کر دو یا اسے کہیں پھینک دو تاکہ تمہارے والد کی توجہ صرف تمہاری ہی طرف ہو جائے۔ یہ کام کر لینے کے بعد پھر نیک بن رہنا۔“ ');
INSERT INTO "ur_maududi" VALUES(12,10,'12:10','اس پر ان میں سے ایک بولا "یوسفؑ کو قتل نہ کرو، اگر کچھ کرنا ہی ہے تو اسے کسی اندھے کنویں میں ڈال دو کوئی آتا جاتا قافلہ اسے نکال لے جائے گا"');
INSERT INTO "ur_maududi" VALUES(12,11,'12:11','اس قرارداد پر انہوں نے جا کر اپنے باپ سے کہا "ابا جان، کیا بات ہے کہ آپ یوسفؑ کے معاملہ میں ہم پر بھروسہ نہیں کرتے حالانکہ ہم اس کے سچے خیر خواہ ہیں؟');
INSERT INTO "ur_maududi" VALUES(12,12,'12:12','کل اسے ہمارے ساتھ بھیج دیجیے، کچھ چَر چُگ لے گااور کھیل کُود سے بھی دِل بہلائے گا۔ ہم اس کی حفاظت کو موجود ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(12,13,'12:13','باپ نے کہا "تمہارا اسے لے جانا مجھے شاق گزرتا ہے اور مجھ کو اندیشہ ہے کہ کہیں اسے بھیڑیا نہ پھاڑ کھا ئے جبکہ تم اس سے غافل ہو"');
INSERT INTO "ur_maududi" VALUES(12,14,'12:14','انہوں نے جواب دیا "اگر ہمارے ہوتے اسے بھڑ یے نے کھا لیا، جبکہ ہم ایک جتھا ہیں، تب تو ہم بڑے ہی نکمے ہوں گے"');
INSERT INTO "ur_maududi" VALUES(12,15,'12:15','س طرح اسرار کر کے جب وہ اُسے لے گئےاور انہوں نے طے کر لیا کہ اسے ایک اندھے کنوئیں میں چھوڑ دیں، تو ہم نے یُوسف کو وحی کی کہ”ایک وقت آئے گا جب تُو ان لوگوں کو ان کی یہ حرکت جتائے گا، یہ اپنے فعل کے نتائج سے بے خبر ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(12,16,'12:16','شام کو وہ روتے پیٹتے اپنے باپ کے پاس آئے');
INSERT INTO "ur_maududi" VALUES(12,17,'12:17','اور کہا "ابا جان، ہم دوڑ کا مقابلہ کرنے میں لگ گئے تھے اور یوسفؑ کو ہم نے اپنے سامان کے پاس چھوڑ دیا تھا کہ اتنے میں بھیڑیا آ کر اُسے کھا گیا آپ ہماری بات کا یقین نہ کریں گے چاہے ہم سچے ہی ہوں"');
INSERT INTO "ur_maududi" VALUES(12,18,'12:18','اور و ہ یُوسف کی قمیص پر جھُوٹ مُوٹ کا خون لگا کر لے آئے تھے۔ یہ سُن کر اُن کے باپ نے کہا”بلکہ تمہارے نفس نے تمہارے لیے ایک بڑے کام کو آسان بنا دیا۔ اچھا، صبر کروں گااور بخوبی کروں گا، جو بات تم بنا رہے ہو اس پر اللہ ہی سے مدد مانگی جا سکتی ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(12,19,'12:19','ادھر ایک قافلہ آیا اور اُس نے اپنے سقے کو پانی لانے کے لیے بھیجا سقے نے جو کنویں میں ڈول ڈالا تو (یوسفؑ کو دیکھ کر) پکار اٹھا "مبارک ہو، یہاں تو ایک لڑکا ہے" ان لوگوں نے اس کو مال تجارت سمجھ کر چھپا لیا، حالانکہ جو کچھ وہ کر رہے تھے خدا اس سے باخبر تھا');
INSERT INTO "ur_maududi" VALUES(12,20,'12:20','آخرِ کار انہوں نے اس کو تھوڑی سی قیمت پر چند درہموں کے عوض بیچ ڈالا اور وہ اس کی قیمت کے معاملہ میں کچھ زیادہ کے اُمیدوار نہ تھے');
INSERT INTO "ur_maududi" VALUES(12,21,'12:21','مصر میں جس شخص نے اسے خریدا اُس نے اپنی بیوی سے کہا ”اِس کو اچھی طرح رکھنا، بعید نہیں کہ یہ ہمارے لیے مفید ثابت ہو یا ہم اسے بیٹا بنا لیں۔“ اس طرح ہم نے یُوسف کے لیے اُس سرزمین میں قدم جمانے کی صُورت نکالی اور اُسے معاملہ فہمی کی تعلیم دینے کا انتظام کیا۔ اللہ اپنا کام کر کے رہتا ہے، مگر اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(12,22,'12:22','اور جب وہ اپنی پُوری جوانی کو پہنچا تو ہم نے اُسے قوتِ فیصلہ اور علم عطا کیا، اِس طرح ہم نیک لوگوں کو جزا دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(12,23,'12:23','جس عورت کے گھر میں وہ تھا وہ اُس پر ڈورے ڈالنے لگی اور ایک روز دروازے بند کر کے بولی”آجا۔“ یُوسف نے کہا”خدا کی پناہ، میرے ربّ نے تو مجھے اچھی منزلت بخشی(اور میں یہ کام کروں!)ایسے ظالم کبھی فلاح نہیں پایا کرتے۔“ ');
INSERT INTO "ur_maududi" VALUES(12,24,'12:24','وہ اُس کی طرف بڑھی اور یُوسف بھی اُس کی طرف بڑھتا اگر اپنے ربّ کی بُرہان نہ دیکھ لیتا۔ ایسا ہُوا، تاکہ ہم اُس سے بدی اور بے حیائی کو دُور کر دیں، در حقیقت وہ ہمارے چُنے ہوئے بندوں میں سے تھا');
INSERT INTO "ur_maududi" VALUES(12,25,'12:25','آخر کار یوسفؑ اور وہ آگے پیچھے دروازے کی طرف بھاگے اور اس نے پیچھے سے یوسفؑ کا قمیص (کھینچ کر) پھاڑ دیا دروازے پر دونوں نے اس کے شوہر کو موجود پایا اسے دیکھتے ہی عورت کہنے لگی، "کیا سزا ہے اس شخص کی جو تیری گھر والی پر نیت خراب کرے؟ اِس کے سوا اور کیا سزا ہوسکتی ہے کہ وہ قید کیا جائے یا اسے سخت عذاب دیا جائے"');
INSERT INTO "ur_maududi" VALUES(12,26,'12:26','یُوسُف ؑ نے کہا”یہی مجھے پھانسنے کی کوشش کر رہی تھی۔“ اس عورت کے اپنے کنبہ والوں میں سے ایک شخص نے(قرینے کی)شہادت پیش کی کہ ”اگر یُوسُف ؑ کا قمیص آگے سے پھٹا ہو تو عورت سچی ہے اور یہ جھُوٹا');
INSERT INTO "ur_maududi" VALUES(12,27,'12:27','اور اگر اِس کا قمیص پیچھے سے پھٹا ہو تو عورت جھُوٹی ہے اور یہ سچا۔“ ');
INSERT INTO "ur_maududi" VALUES(12,28,'12:28','جب شوہر نے دیکھا کہ یوسفؑ کا قمیص پیچھے سے پھٹا ہے تو اس نے کہا "یہ تم عورتوں کی چالاکیاں ہیں، واقعی بڑ ے غضب کی ہوتی ہیں تمہاری چالیں');
INSERT INTO "ur_maududi" VALUES(12,29,'12:29','یُوسُف، اس معاملے سے درگزر کر، اور اے عورت، تُو اپنے قصُور کی معافی مانگ، تُو ہی اصل میں خطا کار تھی۔“ ');
INSERT INTO "ur_maududi" VALUES(12,30,'12:30','شہر کی عورتیں آپس میں چرچا کرنے لگیں کہ "عزیز کی بیوی اپنے نوجوان غلام کے پیچھے پڑی ہوئی ہے، محبت نے اس کو بے قابو کر رکھا ہے، ہمارے نزدیک تو وہ صریح غلطی کر رہی ہے"');
INSERT INTO "ur_maududi" VALUES(12,31,'12:31','اس نے جو اُن کی یہ مکّارانہ باتیں سُنیں تو اُن کو بُلاوا بھیج دیا اور ان کے لیے تکیہ دار مجلس آراستہ کی اور ضیافت میں ہر ایک کے آگے ایک ایک چھُری رکھ دی۔ (پھر عین اُس وقت جب کہ وہ پھل کاٹ کاٹ کر کھا رہی تھیں)اس نے یُوسُف کو اشارہ کیا کہ ان کے سامنے نکل آ۔ جب ان عورتوں کی نگاہ اُس پر پڑی تو وہ دنگ رہ گئیں اور اپنے ہاتھ کاٹ بیٹھیں اور بے ساختہ پُکار اُٹھیں”حاشالِلّٰہ، یہ شخص انسان نہیں ہے، یہ تو کوئی بزرگ فرشتہ ہے۔“');
INSERT INTO "ur_maududi" VALUES(12,32,'12:32','عزیز کی بیوی نے کہا”دیکھ لیا! یہ ہے وہ شخص جس کے معاملہ میں تم مجھ پر باتیں بناتی تھیں۔ بےشک میں نے اِسے رجہانے کی کوشش کی تھی مگر یہ بچ نِکلا۔ اگر یہ میرا کہنا نہ مانے گا تو قید کیا جائے گا اور بہت ذلیل و خوار ہوگا۔“ ');
INSERT INTO "ur_maududi" VALUES(12,33,'12:33','یُوسُف ؑ نے کہا ”اے میرے ربّ! قید مجھے منظور ہے بہ نسبت اس کے کہ میں وہ کام کروں جو یہ لوگ مجھ سے چاہتے ہیں۔ اور اگر تُو نے ان کی چالوں کو مجھ سے دفع نہ کیا تو میں ان کے دام میں پھنس جاوٴں گا اور جاہلوں میں شامل ہو رہوں گا“');
INSERT INTO "ur_maududi" VALUES(12,34,'12:34','اس کے ربّ نے اس کی دُعا قبول کی اور اُن عورتوں کی چالیں اس سے دفع کر دیں، بے شک وہی ہے جو سب کی سُنتااور سب کچھ جانتا ہے');
INSERT INTO "ur_maududi" VALUES(12,35,'12:35','پھر ان لوگوں کو یہ سُوجھی کہ ایک مُدّت کے لیے اسے قید کر دیں حالانکہ وہ (اس کی پاکدامنی اور خود اپنی عورتوں کے بُرے اطوار کی)صریح نشانیاں دیکھ چکے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(12,36,'12:36','قید خانہ میں دو غلام اور بھی اس کے ساتھ داخل ہوئے۔ ایک روز اُن میں سے ایک نے اُس سے کہا ”میں نے خواب میں دیکھا ہے کہ میں شراب کشِید کر رہا ہوں۔“ دُوسرے نے کہا”میں نے دیکھا کہ میرے سر پر روٹیاں رکھی ہیں اور پرندے ان کو کھا رہے ہیں۔“ دونوں نے کہا ”ہمیں اس کی تعبیر بتائیے، ہم دیکھتے ہیں کہ آپ ایک نیک آدمی ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(12,37,'12:37','یوسفؑ نے کہا: "یہاں جو کھانا تمہیں ملا کرتا ہے اس کے آنے سے پہلے میں تمہیں اِن خوابوں کی تعبیر بتا دوں گا یہ علم اُن علوم میں سے ہے جو میرے رب نے مجھے عطا کیے ہیں واقعہ یہ ہے کہ میں نے اُن لوگوں کا طریقہ چھوڑ کر جو اللہ پر ایمان نہیں لاتے اور آخرت کا انکار کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(12,38,'12:38','اپنے بزرگوں، ابراہیمؑ، اسحاقؑ اور یعقوبؑ کا طریقہ اختیار کیا ہے ہمارا یہ کام نہیں ہے کہ اللہ کے ساتھ کسی کو شریک ٹھیرائیں در حقیقت یہ اللہ کا فضل ہے ہم پر اور تمام انسانوں پر (کہ اس نے اپنے سوا کسی کا بندہ ہمیں نہیں بنایا) مگر اکثر لوگ شکر نہیں کرتے');
INSERT INTO "ur_maududi" VALUES(12,39,'12:39','اے زنداں کے ساتھیو، تم خود ہی سوچو کہ بہت سے متفرق رب بہتر ہیں یا وہ ایک اللہ جو سب غالب ہے؟');
INSERT INTO "ur_maududi" VALUES(12,40,'12:40','اُس کو چھوڑ کر تم جن کی بندگی کر رہے ہو وہ اس کے سوا کچھ نہیں ہیں کہ بس چند نام ہیں جو تم نے اور تمہارے آباؤ اجداد نے رکھ لیے ہیں، اللہ نے ان کے لیے کوئی سند نازل نہیں کی فرماں روائی کا اقتدار اللہ کے سوا کسی کے لیے نہیں ہے اس کا حکم ہے کہ خود اس کے سوا تم کسی کی بندگی نہ کرو یہی ٹھیٹھ سیدھا طریق زندگی ہے، مگر اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(12,41,'12:41','اے زنداں کے ساتھیو، تمہارے خواب کی تعبیر یہ ہے کہ تم میں سے ایک تو اپنے ربّ (شاہِ مصر)کو شراب پلائے گا، رہا دُوسرا تو اسے سُولی پر چڑھایا جائے گا اورپرندے اس کا سر نوچ نوچ کر کھائیں گے۔ فیصلہ ہوگیا اُس بات کا جو تم پُوچھ رہے تھے۔“ ');
INSERT INTO "ur_maududi" VALUES(12,42,'12:42','پھر اُن میں سے جس کے متعلق خیال تھاکہ وہ رہا ہو جائے گا اس سے یُوسُف ؑ نے کہا کہ ”اپنے ربّ (شاہِ مصر)سے میرا ذکر کرنا۔“ مگر شیطان نے اسے ایسا غفلت میں ڈالا کہ وہ اپنے ربّ (شاہِ مصر)سے اس کا ذکر کرنا بھُول گیا اور یُوسُف ؑ کئی سال قیدخانے میں پڑا رہا۔ ');
INSERT INTO "ur_maududi" VALUES(12,43,'12:43','ایک روز بادشاہ نے کہا ”میں نے خواب میں دیکھا ہے کہ سات موٹی گائیں ہیں جن کو سات دُبلی گائیں کھا رہی ہیں،اور اناج کی سات بالیں ہری ہیں اور دُوسری سات سُوکھی۔ اے اہلِ دربار، مجھے اس خواب کی تعبیر بتاوٴ اگر تم خوابوں کا مطلب سمجھتے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(12,44,'12:44','لوگوں نے کہا "یہ تو پریشان خوابوں کی باتیں ہیں اور ہم اس طرح کے خوابوں کا مطلب نہیں جانتے"');
INSERT INTO "ur_maududi" VALUES(12,45,'12:45','اُن دو قیدیوں میں سے جو شخص بچ گیا تھا اور اُسے ایک مُدّتِ دراز کے بعد اب بات یاد آئی، اُس نے کہا”میں آپ حضرات کو اس کی تاویل بتاتا ہوں، مجھے ذرا (قید خانے میں یُوسُف ؑ کے پاس)بھیج دیجیے۔ “');
INSERT INTO "ur_maududi" VALUES(12,46,'12:46','اُس نے جا کر کہا”یُوسُف ؑ، اے سراپا راستی، مجھے اس خواب کا مطلب بتا کہ سات موٹی گائیں ہیں جن کو سات دُبلی گائیں کھا رہی ہیں اور سات بالیں ہری ہیں اور سات سوکھی۔ شاید کہ میں اُن لوگوں کے پاس واپس جاوٴں اور شاید کہ وہ جان لیں۔“ ');
INSERT INTO "ur_maududi" VALUES(12,47,'12:47','یوسفؑ نے کہا "سات بر س تک لگاتار تم لوگ کھیتی باڑی کرتے رہو گے اس دوران میں جو فصلیں تم کاٹو اُن میں سے بس تھوڑا ساحصہ، جو تمہاری خوراک کے کام آئے، نکالو اور باقی کو اس کی بالوں ہی میں رہنے دو');
INSERT INTO "ur_maududi" VALUES(12,48,'12:48','پھر سات برس بہت سخت آئیں گے اُس زمانے میں وہ سب غلہ کھا لیا جائے گا جو تم اُس وقت کے لیے جمع کرو گے اگر کچھ بچے گا تو بس وہی جو تم نے محفوظ کر رکھا ہو');
INSERT INTO "ur_maududi" VALUES(12,49,'12:49','اس کے بعد پھر ایک سال ایسا آئے گا جس میں بارانِ رحمت سے لوگوں کو فریاد رسی کی جائے گی اور وہ رس نچوڑیں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(12,50,'12:50','بادشاہ نے کہا اسے میرے پاس لاوٴ۔ مگر جب شاہی فرستادہ یوُسف ؑ کے پاس پہنچا تو اس نے کہا” اپنے ربّ کے پاس واپس جا اور اس سے پوچھ کہ اُن عورتوں کا یا معاملہ ہے جنہوں نے اپنے ہاتھ کاٹ لیے تھے؟ میرا ربّ تو ان کی مکّاری سے واقف ہی ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(12,51,'12:51','اس پر بادشاہ نے ان عورتوں سے دریافت کیا”تمہارا کیا تجربہ ہے اُس وقت کا جب تم نے یُوسُف ؑ کو رِجہانے کی کوشش کی تھی؟“ سب نے یک زبان ہو کر کہا”حاشا لِلّٰہ، ہم نے تو اُس میں بدی کا شائبہ تک نہ پایا۔“ عزیز کی بیوی بول اُٹھی”اب حق کُھل چکا ہے، وہ میں ہی تھی جس نے اُس کو پُھسلانے کی کوشش کی تھی، بے شک وہ بالکل سچا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(12,52,'12:52','(یُوسُف ؑ نے کہا)”اِس سے میری غرض یہ تھی کہ (عزیز)یہ جان لے کہ میں نے درپردہ اس کی خیانت نہیں کی تھی۔ اور یہ کہ جو خیانت کرتے ہیں ان کی چالوں کو اللہ کامیابی کی راہ پر نہیں لگاتا');
INSERT INTO "ur_maududi" VALUES(12,53,'12:53','میں کچھ اپنے نفس کی براءَت نہیں کر رہا ہوں، نفس تو بدی پر اکساتا ہی ہے الا یہ کہ کسی پر میرے رب کی رحمت ہو، بے شک میرا رب بڑا غفور و رحیم ہے"');
INSERT INTO "ur_maududi" VALUES(12,54,'12:54','بادشاہ نے کہا”اُنہیں میرے پاس لاوٴ تاکہ میں ان کو اپنے لیے مخصُوص کر لوں۔“جب یُوسُف ؑ نے اس سے گفتگو کی تو اس نے کہا”اب آپ ہمارے ہاں قدر و منزلت رکھتے ہیں اور آپ کی امانت پر پورا بھروسا ہے۔ “');
INSERT INTO "ur_maududi" VALUES(12,55,'12:55','یُوسُف ؑ نے کہا”ملک کے خزانے میرے سپُرد کیجیے، میں حفاظت کرنے والا بھی ہوں اور علم بھی رکھتا ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(12,56,'12:56','اِس طرح ہم نے اُس سرزمین میں یُوسُف ؑ کے لیے اقتدار کی راہ ہموار کی۔ وہ مُختار تھا کہ اس میں جہاں چاہے اپنی جگہ بنائے۔ ہم اپنی رحمت سے جس کو چاہتے ہیں نوازتے ہیں، نیک لوگوں کا اجر ہمارے ہاں مارا نہیں جاتا');
INSERT INTO "ur_maududi" VALUES(12,57,'12:57','اور آخرت کا اجر اُن لوگوں کے لیے زیادہ بہتر ہے جو ایمان لائے اور خدا ترسی کے ساتھ کام کرتے رہے۔ ');
INSERT INTO "ur_maududi" VALUES(12,58,'12:58','یُوسُف ؑ کے بھائی مصر آئے اور اس کے ہاں حاضر ہوئے۔ اس نے انہیں پہچان لیا مگر وہ اس سے نا آشنا تھے۔ ');
INSERT INTO "ur_maududi" VALUES(12,59,'12:59','پھر جب اس نے ان کا سامان تیار کروا دیا تو چلتے وقت ان سے کہا، "اپنے سوتیلے بھائی کو میرے پاس لانا دیکھتے نہیں ہو کہ میں کس طرح پیمانہ بھر کر دیتا ہوں اور کیسا اچھا مہمان نواز ہوں');
INSERT INTO "ur_maududi" VALUES(12,60,'12:60','اگر تم اسے نہ لاوٴ گے تو میرے پاس تمہارے لیے کوئی غلّہ نہیں ہے بلکہ تم میرے قریب بھی نہ پھٹکنا۔ “');
INSERT INTO "ur_maududi" VALUES(12,61,'12:61','انہوں نے کہا، "ہم کوشش کریں گے کہ والد صاحب اسے بھیجنے پر ر اضی ہو جائیں، اور ہم ایسا ضرور کریں گے"');
INSERT INTO "ur_maududi" VALUES(12,62,'12:62','یوسفؑ نے اپنے غلاموں کو اشارہ کیا کہ "اِن لوگوں نے غلے کے عوض جو مال دیا ہے وہ چپکے سے ان کے سامان ہی میں رکھ دو" یہ یوسفؑ نے اِس امید پر کیا کہ گھر پہنچ کر وہ اپنا واپس پایا ہوا مال پہچان جائیں گے (یا اِس فیاضی پر احسان مند ہوں گے) اور عجب نہیں کہ پھر پلٹیں');
INSERT INTO "ur_maududi" VALUES(12,63,'12:63','جب وہ اپنے باپ کے پاس گئے تو کہا "ابا جان، آئندہ ہم کو غلہ دینے سے انکار کر دیا گیا ہے، لہٰذا آپ ہمارے بھائی کو ہمارے ساتھ بھیج دیجیے تاکہ ہم غلہ لے کر آئیں اور اس کی حفاظت کے ہم ذمہ دار ہیں"');
INSERT INTO "ur_maududi" VALUES(12,64,'12:64','باپ نے جواب دیا "کیا میں اُس کے معاملہ میں تم پر ویسا ہی بھروسہ کروں جیسا اِس سے پہلے اُس کے بھائی کے معاملہ میں کر چکا ہوں؟ اللہ ہی بہتر محافظ ہے اور وہ سب سے بڑھ کر رحم فرمانے والا ہے"');
INSERT INTO "ur_maududi" VALUES(12,65,'12:65','پھر جب انہوں نے اپنا سامان کھولا تو دیکھا کہ ان کا مال بھی انہیں واپس کر دیا گیا ہے یہ دیکھ کر وہ پکار اٹھے "ابا جان، اور ہمیں کیا چاہیے، دیکھیے یہ ہمارا مال بھی ہمیں واپس دے دیا گیا ہے بس اب ہم جائیں گے اور اپنے اہل و عیال کے لیے رسد لے آئیں گے، اپنے بھائی کی حفاظت بھی کریں گے اور ایک بارشتر اور زیادہ بھی لے آئیں گے، اتنے غلہ کا اضافہ آسانی کے ساتھ ہو جائے گا"');
INSERT INTO "ur_maududi" VALUES(12,66,'12:66','ان کے باپ نے کہا "میں اس کو ہرگز تمہارے ساتھ نہ بھیجوں گا جب تک کہ تم اللہ کے نام سے مجھ کو پیمان نہ دے دو کہ اِسے میرے پا س ضرور واپس لے کر آؤ گے الا یہ کہ کہیں تم گھیر ہی لیے جاؤ" جب انہوں نے اس کو اپنے اپنے پیمان دے دیے تو اس نے کہا "دیکھو، ہمارے اس قول پر اللہ نگہبان ہے"');
INSERT INTO "ur_maududi" VALUES(12,67,'12:67','پھر اُس نے کہا”میرے بچو، مصر کے دارالسلطنت میں ایک دروازے سے داخل نہ ہونا بلکہ مختلف دروازوں سے جانا۔ مگر میں اللہ کی مشیّت سے تم کو نہیں بچا سکتا، حکم اُس کے سوا کسی کا بھی نہیں چلتا، اسی پر میں نے بھروسہ کیا اور جس کو بھی بھروسہ کرنا ہو اُسی پر کرے۔“');
INSERT INTO "ur_maududi" VALUES(12,68,'12:68','اور واقعہ بھی یہ ہوا کہ جب وہ اپنے باپ کی ہدایت کے مطابق شہر میں (متفرق دروازوں سے)داخل ہوئے تو اُس کی یہ احتیاطی تدبیر اللہ کی مشیّت کے مقابلے میں کچھ بھی کام نہ آسکی۔ ہاں بس یعقوب ؑ کے دل میں جو ایک کھٹک تھی اسے دُور کرنے کے لیے اُس نے اپنی سی کوشش کر لی۔ بے شک وہ ہماری دی ہوئی تعلیم سے صاحبِ علم تھا مگر اکثر لوگ معاملہ کی حقیقت کو جانتے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(12,69,'12:69','یہ لوگ یُوسُف ؑ کے حضُور پہنچے تو اس نے اپنے بھائی کو اپنے پاس الگ بلا لیا اور اسے بتا دیا کہ”میں تیرا وہی بھائی ہوں (جو کھویا گیا تھا)۔ اب تُو اُن باتوں کا غم نہ کر جو یہ لوگ کرتے رہے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(12,70,'12:70','جب یُوسُف ؑ ان بھائیوں کا سامان لدوانے لگا تو اس نے اپنے بھائی کے سامان میں اپنا پیالہ رکھ دیا۔ پھر ایک پُکارنے والے نے پُکار کر کہا”اے قافلے والو، تم لوگ چور ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(12,71,'12:71','انہوں نے پلٹ کر پوچھا "تمہاری کیا چیز کھوئی گئی؟ "');
INSERT INTO "ur_maududi" VALUES(12,72,'12:72','سرکاری ملازموں نے کہا "بادشاہ کا پیمانہ ہم کو نہیں ملتا" (اور ان کے جمعدار نے کہا) "جو شخص لا کر دے گا اُس کے لیے ایک بارشتر انعام ہے، اس کا میں ذمہ لیتا ہوں"');
INSERT INTO "ur_maududi" VALUES(12,73,'12:73','ان بھائیوں نے کہا "خدا کی قسم، تو لوگ خوب جانتے ہو کہ ہم اس ملک میں فساد کرنے نہیں آئے ہیں اور ہم چوریاں کرنے والے لوگ نہیں ہیں"');
INSERT INTO "ur_maududi" VALUES(12,74,'12:74','اُنہوں نے کہا "اچھا، اگر تمہاری بات جھوٹی نکلی تو چور کی کیا سزا ہے؟"');
INSERT INTO "ur_maududi" VALUES(12,75,'12:75','اُنہوں نے کہا”اُس کی سزا؟ جس کے سامان میں سے یہ چیز نکلے وہ آپ ہی اپنی سزا میں رکھ لیا جائے، ہمارے ہاں تو ایسے ظالموں کو سزا دینے کا یہی طریقہ ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(12,76,'12:76','تب یُوسُف ؑ نے اپنے بھائی سے پہلے اُن کی خُرجیوں کی تلاشی لینی شروع کی، پھر اپنے بھائی کی خُرجی سے گم شدہ چیز بر آمد کر لی۔۔۔۔اِس طرح ہم نے یُوسُف ؑ کی تائید اپنی تدبیر سے کی۔ اُس کا یہ کا م نہ تھا کہ بادشاہ کے دین(یعنی مصر کے شاہی قانون)میں اپنے بھائی کو پکڑتا اِلّا یہ کہ اللہ ہی ایسا چاہے۔ ہم جس کے درجے چاہتے ہیں بلند کر دیتے ہیں، اور ایک علم رکھنے والا ایساہے جو ہر صاحبِ علم سے بالاتر ہے');
INSERT INTO "ur_maududi" VALUES(12,77,'12:77','ان بھائیوں نے کہا”یہ چوری کرے تو کچھ تعجّب کی بات نہیں ، اس سے پہلے اس کا بھائی (یُوسُف ؑ)بھی چوری کر چکا ہے۔“ یُوسُف ؑ ان کی یہ بات سُن کر پی گیا، حقیقت ان پر نہ کھولی، بس (زیرِ لب)اتنا کہہ کر رہ گیاکہ”بڑے ہی بُرے ہو تم لوگ،(میرے منہ در منہ مجھ پر)جو الزام تم لگا رہے ہو اس کی حقیقت خدا خوب جانتا ہے۔“');
INSERT INTO "ur_maududi" VALUES(12,78,'12:78','انہوں نے کہا”اے سردار ذی اقتدار(عزیز) ، اس کا باپ بہت بُوڑھا آدمی ہے، اس کی جگہ آپ ہم میں سےکسی کو رکھ لیجیے، ہم آپ کو بڑا ہی نیک نفس انسان پاتے ہیں۔“');
INSERT INTO "ur_maududi" VALUES(12,79,'12:79','یُوسُف ؑ نے کہا”پناہِ خدا، دُوسرے کسی شخص کو ہم کیسے رکھ سکتے ہیں، جس کے پاس ہم نے اپنا مال پایا ہے اُس کو چھوڑ کر دُوسرے کو رکھیں گے تو ہم ظالم ہوں گے۔“');
INSERT INTO "ur_maududi" VALUES(12,80,'12:80','جب وہ یوسفؑ سے مایوس ہو گئے تو ایک گوشے میں جا کر آپس میں مشورہ کرنے لگے ان میں جو سب سے بڑا تھا وہ بولا "تم جانتے نہیں ہو کہ تمہارے والد تم سے خدا کے نام پر کیا عہد و پیمان لے چکے ہیں اور اس سے پہلے یوسفؑ کے معاملہ میں جو زیادتی تم کر چکے ہو وہ بھی تم کو معلوم ہے اب میں تو یہاں سے ہرگز نہ جاؤں گا جب تک کہ میرے والد مجھے اجازت نہ دیں، یا پھر اللہ ہی میرے حق میں کوئی فیصلہ فرما دے کہ وہ سب سے بہتر فیصلہ کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(12,81,'12:81','تم جا کر اپنے والد سے کہو کہ "ابا جان، آپ کے صاحبزادے نے چوری کی ہے ہم نے اسے چوری کرتے ہوئے نہیں دیکھا، جو کچھ ہمیں معلوم ہوا ہے بس وہی ہم بیان کر رہے ہیں، اور غیب کی نگہبانی تو ہم نہ کر سکتے تھے');
INSERT INTO "ur_maududi" VALUES(12,82,'12:82','آپ اُس بستی کے لوگوں سے پوچھ لیجیے جہاں ہم تھے اُس قافلے سے دریافت کر لیجیے جس کے ساتھ ہم آئے ہیں ہم اپنے بیان میں بالکل سچے ہیں"');
INSERT INTO "ur_maududi" VALUES(12,83,'12:83','باپ نے یہ داستان سُن کر کہا”دراصل تمہارے نفس نے تمہارے لیے ایک اور بڑی بات کو سہل بنا دیا۔ اچھا، اس پر بھی صبر کروں گا اور بخوبی کروں گا۔ کیا بعید ہے کہ اللہ ان سب کو مجھ سے لا ملائے، وہ سب کچھ جانتا ہے اور اُس کے سب کا م حکمت پر مبنی ہیں۔“');
INSERT INTO "ur_maududi" VALUES(12,84,'12:84','پھر وہ ان کی طرف سے منہ پھیر کر بیٹھ گیا اور کہنے لگا کہ "ہا ئے یوسف!" وہ دل ہی دل میں غم سے گھٹا جا رہا تھا اور اس کی آنکھیں سفید پڑ گئی تھیں');
INSERT INTO "ur_maududi" VALUES(12,85,'12:85','بیٹوں نے کہا "خدارا! آپ تو بس یوسف ہی کو یاد کیے جاتے ہیں نوبت یہ آ گئی ہے کہ اس کے غم میں اپنے آپ کو گھلا دیں گے یا اپنی جان ہلاک کر ڈالیں گے"');
INSERT INTO "ur_maududi" VALUES(12,86,'12:86','اُس نے کہا "میں اپنی پریشانی اور اپنے غم کی فریاد اللہ کے سوا کسی سے نہیں کرتا، اور اللہ سے جیسا میں واقف ہوں تم نہیں ہو');
INSERT INTO "ur_maududi" VALUES(12,87,'12:87','میرے بچو، جا کر یوسفؑ اور اس کے بھائی کی کچھ ٹوہ لگاؤ، اللہ کی رحمت سے مایوس نہ ہو، اس کی رحمت سے تو بس کافر ہی مایوس ہوا کرتے ہیں"');
INSERT INTO "ur_maududi" VALUES(12,88,'12:88','جب یہ لوگ مصر جا کر یُوسُف ؑ کی پیشی میں داخل ہوئےتو انہوں نے عرض کیا”اے سردار با اقتدار، ہم اور ہمارے اہل و عیال سخت مصیبت میں مُبتلا ہیں، اور ہم کچھ حقیر سی پُونجی لے کر آئے ہیں، آپ ہمیں بھرپور غلّہ عنایت فرمائیں اور ہم کو خیرات دیں ، اللہ خیرات کرنے والوں کو جزا دیتا ہے۔“');
INSERT INTO "ur_maududi" VALUES(12,89,'12:89','(یہ سن کریوسفؑ سے نہ رہا گیا) اُس نے کہا "تمہیں کچھ یہ بھی معلوم ہے کہ تم نے یوسفؑ اور اُس کے بھائی کے ساتھ کیا کیا تھا جبکہ تم نادان تھے"');
INSERT INTO "ur_maududi" VALUES(12,90,'12:90','وہ چونک کر بولے "کیا تم یوسفؑ ہو؟" اُس نے کہا، "ہاں، میں یوسفؑ ہوں اور یہ میرا بھائی ہے اللہ نے ہم پر احسان فرمایا حقیقت یہ ہے کہ اگر کوئی تقویٰ اور صبر سے کام لے تو اللہ کے ہاں ایسے نیک لوگوں کا اجر مارا نہیں جاتا"');
INSERT INTO "ur_maududi" VALUES(12,91,'12:91','انہوں نے کہا "بخدا کہ تم کو اللہ نے ہم پر فضیلت بخشی اور واقعی ہم خطا کار تھے"');
INSERT INTO "ur_maududi" VALUES(12,92,'12:92','اس نے جواب دیا، "آج تم پر کوئی گرفت نہیں، اللہ تمہیں معاف کرے، وہ سب سے بڑھ کر رحم فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(12,93,'12:93','جاؤ، میرا یہ قمیص لے جاؤ اور میرے والد کے منہ پر ڈال دو، اُن کی بینائی پلٹ آئے گی، اور اپنے سب اہل و عیال کو میرے پاس لے آؤ"');
INSERT INTO "ur_maududi" VALUES(12,94,'12:94','جب یہ قافلہ (مصر سے)روانہ ہوا تو ان کے باپ نے (کنعان میں)کہا”میں یُوسُف ؑ کی خوشبو محسُوس کر رہا ہوں، تم لوگ کہیں یہ نہ کہنے لگو کہ میں بڑھاپے میں سٹھیا گیا ہوں۔“');
INSERT INTO "ur_maududi" VALUES(12,95,'12:95','گھر کے لوگ بولے”خدا کی قسم آپ ابھی تک اپنے اسی پُرانے خبط میں پڑے ہوئے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(12,96,'12:96','پھر جب خو ش خبری لانے والا آیا تو اس نے یوسفؑ کا قمیص یعقوبؑ کے منہ پر ڈال دیا اور یکا یک اس کی بینائی عود کر آئی تب اس نے کہا "میں تم سے کہتا نہ تھا؟ میں اللہ کی طرف سے وہ کچھ جانتا ہوں جو تم نہیں جانتے"');
INSERT INTO "ur_maududi" VALUES(12,97,'12:97','سب بول اٹھے "ابا جان، آپ ہمارے گناہوں کی بخشش کے لیے دعا کریں، واقعی ہم خطا کار تھے"');
INSERT INTO "ur_maududi" VALUES(12,98,'12:98','اُس نے کہا "میں اپنے رب سے تمہارے لیے معافی کی درخواست کروں گا، وہ بڑا معاف کرنے والا اور رحیم ہے"');
INSERT INTO "ur_maududi" VALUES(12,99,'12:99','پھر جب یہ لوگ یُوسُف ؑ کے پاس پہنچے تو اُس نے اپنےوالدین کو اپنے ساتھ بٹھا لیا اور (اپنےسب کنبے والوں سے کہا)”چلو، اب شہر میں چلو، اللہ نے چاہا تو امن چین سے رہو گے۔“');
INSERT INTO "ur_maududi" VALUES(12,100,'12:100','(شہر میں داخل ہونے کے بعد)اس نے اپنے والدین کو اُٹھا کر اپنے پاس تخت پر بٹھایا اور سب اس کے آگے بے اختیار سجدے میں جُھک گئے۔ یُوسُف ؑ نے کہا”ابا جان، یہ تعبیر ہے میرے اُس خواب کی جو میں نے پہلے دیکھا تھا، میرے ربّ نے اسے حقیقت بنا دیا۔ اس کا احسان ہے کہ اُس نے مجھے قید خانے سے نکالا، اور آپ لوگوں کو صحرا سے لاکر مجھ سے ملایا حالانکہ شیطان میرے اور میرے بھائیوں کے درمیان فساد ڈال چکا تھا۔ واقعہ یہ ہے کہ میرا ربّ غیر محسُوس تدبیروں سے اپنی مشیّت پُوری کرتا ہے، بے شک وہ علیم اور حکیم ہے');
INSERT INTO "ur_maududi" VALUES(12,101,'12:101','اے میرے ربّ، تُو نے مجھے حکومت بخشی اور مجھے باتوں کی تہہ تک پہنچنا سکھایا۔ زمین و آسمان کے بنانے والے، تُو ہی دُنیا اور آخرت میں میرا سرپرست ہے، میرا خاتمہ اسلام پر کر اور انجامِ کار مجھے صالحین کے ساتھ ملا۔“ ');
INSERT INTO "ur_maududi" VALUES(12,102,'12:102','اے محمدؐ، یہ قصہ غیب کی خبروں میں سے ہے جو ہم تم پر وحی کر رہے ہیں ورنہ تم اُس وقت موجود نہ تھے جب یوسفؑ کے بھائیوں نے آپس میں اتفاق کر کے سازش کی تھی');
INSERT INTO "ur_maududi" VALUES(12,103,'12:103','مگر تم خواہ کتنا ہی چاہو ان میں سے اکثر لوگ مان کر دینے والے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(12,104,'12:104','حالانکہ تم اس خدمت پر ان سے کوئی اُجرت بھی نہیں مانگتے ہو۔ یہ تو ایک نصیحت ہے جو دنیا والوں کے لیے عام ہے۔ ');
INSERT INTO "ur_maududi" VALUES(12,105,'12:105','زمین اور آسمانوں میں کتنی ہی نشانیاں ہیں جن پر سےیہ لوگ گزرتے ہیں اور ذرا توجّہ نہیں کرتے۔ ');
INSERT INTO "ur_maududi" VALUES(12,106,'12:106','ان میں سے اکثر اللہ کو مانتے ہیں مگر اس طرح کہ اُس کے ساتھ دوسروں کو شریک ٹھہراتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(12,107,'12:107','کیا یہ مطمئن ہیں کہ خدا کے عذاب کی کوئی بلا انہیں دبوچ نہ لے گی یا بے خبری میں قیامت کی گھڑی اچانک ان پر نہ آجائے گی؟ ');
INSERT INTO "ur_maududi" VALUES(12,108,'12:108','تم ان سے صاف کہہ دو کہ ”میرا رستہ تو یہ ہے، میں اللہ کی طرف بُلاتا ہوں، میں خود بھی پوری روشنی میں اپنا راستہ دیکھ رہا ہوں اور میرے ساتھی بھی، اور اللہ پاک ہے اور شرک کرنے والوں سے میرا کوئی واسطہ نہیں۔“');
INSERT INTO "ur_maududi" VALUES(12,109,'12:109','اے محمد ؐ ، تم سے پہلے ہم نے جو پیغمبر بھیجے تھے وہ سب بھی انسان ہی تھے، اور اِنہی بستیوں کے رہنے والوں میں تھے، اور اُنہی کی طرف ہم وحی بھیجتے رہے ہیں۔ پھر کیا یہ لوگ زمین میں چلے پھرے نہیں ہیں کہ اُن قوموں کا انجام اِنہیں نظر نہ آیا جو ان سے پہلے گزر چکی ہیں؟ یقیناً آخرت کا گھر اُن لوگوں کے لیے اَور زیادہ بہتر ہے جنہوں نے (پیغمبروں کی بات مان کر)تقوٰی کی روش اختیار کی۔ کیا اب بھی تم لوگ نہ سمجھو گے؟ ');
INSERT INTO "ur_maududi" VALUES(12,110,'12:110','(پہلے پیغمبروں کے ساتھ بھی یہی ہوتا رہا ہے کہ وہ مدتوں نصیحت کرتے رہے اور لوگوں نے سن کر نہ دیا) یہاں تک کہ جب پیغمبر لوگوں سے مایوس ہو گئے اور لوگوں نے بھی سمجھ لیا کہ اُن سے جھوٹ بولا گیا تھا، تو یکا یک ہماری مدد پیغمبروں کو پہنچ گئی پھر جب ایسا موقع آ جاتا ہے تو ہمارا قاعدہ یہ ہے کہ جسے ہم چاہتے ہیں بچا لیتے ہیں اور مجرموں پر سے تو ہمارا عذاب ٹالا ہی نہیں جا سکتا');
INSERT INTO "ur_maududi" VALUES(12,111,'12:111','اگلے لوگوں کے ان قصّوں میں عقل و ہوش رکھنے والوں کے لیے عبرت ہے۔ یہ جو کچھ قرآن میں بیان کیا جا رہا ہے یہ بناوٹی باتیں نہیں ہیں بلکہ جو کتابیں اس سے پہلے آئی ہوئی ہیں انہی کی تصدیق ہے اور ہر چیز کی تفصیل اور ایمان لانے والوں کے لیے ہدایت اور رحمت۔');
INSERT INTO "ur_maududi" VALUES(13,1,'13:1','ا۔ل۔م۔ر، یہ کتاب الہٰی کی آیات ہیں، اور جو کچھ تمہارے ربّ کی طرف سے تم پر نازل کیا گیا ہے وہ عین حق ہے، مگر (تمہاری قوم کے)اکثر لوگ مان نہیں رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(13,2,'13:2','وہ اللہ ہی ہے جس نے آسمانوں کو ایسے سہاروں کے بغیر قائم کیا جو تم کو نظر آتے ہوں ، پھر وہ اپنے تختِ سلطنت پر جلوہ فرما ہوا ، اور اُس نے آفتاب و ماہتاب کو ایک قانون کاپابند بنایا۔ اِس سارے نظام کی ہر چیز ایک وقتِ مقرر تک کے لیے چل رہی ہے اور اللہ ہی اِس سارے کام کی تدبیر فرما رہا ہے۔ وہ نشانیاں کھول کھول کر بیان کرتا ہے ، شاید کہ تم اپنے ربّ کی ملاقات کا یقین کرو۔ ');
INSERT INTO "ur_maududi" VALUES(13,3,'13:3','اور وہی ہے جس نے یہ زمین پھیلا رکھی ہے، اس میں پہاڑوں کے کھونٹے گاڑ رکھے ہیں اور دریا بہا دیے ہیں۔ اُسی نے ہر طرح کے پھلوں کے جوڑے پیدا کیے ہیں، اور وہی دن پر رات طاری کرتا ہے۔ ان ساری چیزوں میں بڑی نشانیاں ہیں اُن لوگوں کے لیےجو غور و فکر سے کام لیتے ہیں۔');
INSERT INTO "ur_maududi" VALUES(13,4,'13:4','اور دیکھو، زمین میں الگ الگ خِطّے پائے جاتے ہیں جو ایک دوسرے سے متصل واقع ہیں۔ انگور کے باغ ہیں،کھیتیاں ہیں، کھجُور کے درخت ہیں جن میں کچھ اکہرے ہیں اور کچھ دوہرے۔ سب کو ایک ہی پانی سیراب کرتا ہے مگر مزے میں ہم کسی کو بہتر بنا دیتے ہیں اور کسی کو کمتر۔ ان سب چیزوں میں بہت سی نشانیاں ہیں اُن لوگوں کے لے جو عقل سے کام لیتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(13,5,'13:5','اب اگر تمہیں تعجّب کرنا ہے تو تعجّب کے قابل لوگوں کا یہ قول ہے کہ”جب ہم مر کر مٹی ہو جائیں گے تو کیا ہم نئے سِروں سے پیدا کیے جائیں گے؟“ یہ وہ لوگ ہیں جنہوں نے اپنے ربّ سے کُفر کیا ہے۔ یہ وہ لوگ ہیں جن کی گردنوں میں طوق پڑے ہوئے ہیں۔ یہ جہنّمی ہیں اورجہنّم میں ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(13,6,'13:6','یہ لوگ بھلائی سے پہلے بُرائی کے لیے جلدی مچا رہے ہیں حالانکہ ان سے پہلے (جو لوگ اس روش پر چلے ہیں ان پر خدا کے عذاب کی)عبرت ناک مثالیں گزر چکی ہیں۔ حقیقت یہ ہے کہ تیرا ربّ لوگوں کی زیادتیوں کے باوجود ان کے ساتھ چشم پوشی سے کام لیتا ہے۔ اور یہ بھی حقیقت ہے کہ تیرا ربّ سخت سزا دینے والا ہے');
INSERT INTO "ur_maududi" VALUES(13,7,'13:7','یہ لوگ جنہوں نے تمہاری بات ماننے سے انکار کر دیا ہے، کہتے ہیں کہ”اِس شخص پر اِ س کے ربّ کی طرف سے کوئی نشانی کیوں نہ اُتری؟“ ۔۔۔۔ تم تو محض خبردار کر دینے والے ہو، اور ہر قوم کے لیے ایک رہنما ہے۔ ');
INSERT INTO "ur_maududi" VALUES(13,8,'13:8','اللہ ایک ایک حاملہ کے پیٹ سے واقف ہے۔ جو کچھ اس میں بنتا ہے اسے بھی وہ جانتا ہے اور جو کچھ اُس میں کمی یا بیشی ہوتی ہے اس سے بھی وہ با خبر رہتا ہے۔ ہر چیز کے لیے اُس کے ہاں ایک مقدار مقرر ہے');
INSERT INTO "ur_maududi" VALUES(13,9,'13:9','وہ پوشیدہ اور ظاہر، ہر چیز کا عالم ہے وہ بزرگ ہے اور ہر حال میں بالا تر رہنے والا ہے');
INSERT INTO "ur_maududi" VALUES(13,10,'13:10','تم میں سے کوئی شخص خواہ زور سے با ت کرے یا آہستہ، اور کوئی رات کی تاریکی میں چھپا ہوا ہو یا دن کی روشنی میں چل رہا ہو، اس کے لیے سب یکساں ہیں');
INSERT INTO "ur_maududi" VALUES(13,11,'13:11','ہر شخص کے آگے اور پیچھے اس کے مقرر کیے ہوئے نگراں لگے ہوئے ہیں جو اللہ کے حکم سے اس کی دیکھ بھال کر رہے ہیں۔ حقیقت یہ ہے کہ اللہ کسی قوم کے حال کو نہیں بدلتا جب تک کہ وہ خود اپنے اوصاف کو نہیں بدل دیتی۔ اور جب اللہ کسی قوم کی شامت لانے کا فیصلہ کر لے تو پھر وہ کسی کے ٹالے نہیں ٹل سکتی، نہ اللہ کے مقابلے میں ایسی قوم کا کوئی حامی و مددگار ہو سکتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(13,12,'13:12','وہی ہے جو تمہارے سامنے بجلیاں چمکاتا ہے جنہیں دیکھ کر تمہیں اندیشے بھی لاحق ہوتے ہیں اور امیدیں بھی بندھتی ہیں وہی ہے جو پانی سے لدے ہوئے بادل اٹھاتا ہے');
INSERT INTO "ur_maududi" VALUES(13,13,'13:13','بادلوں کی گرج اُس کی حمد کے ساتھ اس کی پاکی بیان کرتی ہے اور فرشتے اُس کی ہیبت سے لرزتے ہوئے اُس کی تسبیح کرتے ہیں۔ وہ کڑکتی ہوئی بجلیوں کو بھیجتا ہے اور (بسا اوقات)اُنہیں جس پر چاہتا ہے عین اس حالت میں گرا دیتا ہے جب کہ لوگ اللہ کے بارے میں جھگڑ رہے ہوتے ہیں۔ فی الواقع اس کی چال بڑی زبردست ہے۔ ');
INSERT INTO "ur_maududi" VALUES(13,14,'13:14','اسی کو پکارنا برحق ہے۔ رہیں وہ دُوسری ہستیاں جنہیں اس کو چھوڑ کر یہ لوگ پکارتے ہیں، وہ اُن کی دعاوٴں کا کوئی جواب نہیں دے سکتیں۔ اُنہیں پُکارنا تو ایسا ہے جیسے کوئی شخص پانی کی طرف ہاتھ پھیلا کر اُس سے درخواست کرے کہ تُو میرے منہ تک پہنچ جا، حالانکہ پانی اُس تک پہنچے والا نہیں۔ بس اِسی طرح کافروں کی دعائیں بھی کچھ نہیں ہیں مگر ایک تیرِ بے ہدف!');
INSERT INTO "ur_maududi" VALUES(13,15,'13:15','وہ تو اللہ ہی ہے جس کو زمین و آسمان کی ہر چیز طوعًا و کرہًا سجدہ کر رہی ہے اور سب چیزوں کے سائے صبح و شام اُس کے آگے جُھکتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(13,16,'13:16','اِن سے پوچھو، آسمان و زمین کا ربّ کون ہے؟۔۔۔۔ کہو، اللہ۔ پھر، اِن سے کہو کہ جب حقیقت یہ ہے تو کیا تم نے اُسے چھوڑ کر ایسے معبودوں کو اپنا کارساز ٹھہرا لیا جو خود اپنے لیے بھی کسی نفع و نقصان کا اختیار نہیں رکھتے؟ کہو، کیا اندھا اور آنکھوں والا برابر ہوا کرتا ہے؟ کیا روشنی اور تاریکیاں یکساں ہوتی ہیں؟ اور اگر ایسا نہیں تو کیا اِن کے ٹھہرائے ہوئے شریکوں نے بھی اللہ کی طرح کچھ پیداکیا ہے کہ اُس کی وجہ سے اِن پر تخلیق کا معاملہ مشتبہ ہو گیا؟۔۔۔۔ کہو، ہر چیز کا خالق صرف اللہ ہے اور وہ یکتا ہے ، سب پر غالب! ');
INSERT INTO "ur_maududi" VALUES(13,17,'13:17','اللہ نے آسمان سے پانی برسایا اور ہر ندی نالہ اپنے ظرف کے مطابق اسے لے کر چل نکلا پھر جب سیلاب اُٹھا تو سطح پر جھاگ بھی آگئے۔ اور ایسے ہی جھاگ اُن دھاتوں پر بھی اُٹھتے ہیں جنہیں زیور اور برتن وغیرہ بنانے کے لیے لوگ پگھلایا کرتے ہیں۔ اِسی مثال سے اللہ حق اور باطل کے معاملے کو واضح کرتا ہے۔ جو جھاگ ہے وہ اُڑ جایا کرتا ہے اور جو چیز انسانوں کے لیے نافع ہے وہ زمین میں ٹھہر جاتی ہے۔ اس طرح اللہ مثالوں سے اپنی بات سمجھاتا ہے');
INSERT INTO "ur_maududi" VALUES(13,18,'13:18','جن لوگوں نے اپنے ربّ کی دعوت قبول کر لی اُن کے لیے بھلائی ہے، اور جنہوں نے اسے قبول نہ کیا وہ اگر زمین کی ساری دولت کے بھی مالک ہوں اور اتنی ہی اور فراہم کر لیں تو وہ خُدا کی پکڑ سے بچنے کے لیے اس سب کو فدیہ میں دے ڈالنے کے لیے تیار ہو جائیں گے۔ یہ وہ لوگ ہیں جن سے بُری طرح حساب لیا جائے گا اور اُن کا ٹھکانا جہنّم ہے، بہت ہی بُرا ٹھکانہ');
INSERT INTO "ur_maududi" VALUES(13,19,'13:19','بھلا یہ کس طرح ممکن ہے کہ وہ شخص جو تمہارے ربّ کی اِس کتاب کو جو اس نے تم پر نازل کی ہے حق جانتا ہے، اور وہ شخص جو اس حقیقت کی طرف سے اندھا ہے، دونوں یکساں ہو جائیں؟ نصیحت تو دانشمند لوگ ہی قبول کیا کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(13,20,'13:20','اور ان کا طرز عمل یہ ہوتا ہے کہ اللہ کے ساتھ اپنے عہد کو پورا کرتے ہیں، اسے مضبوط باندھے کے بعد توڑ نہیں ڈالتے۔ ');
INSERT INTO "ur_maududi" VALUES(13,21,'13:21','ان کی روش یہ ہوتی ہے کہ اللہ نے جن جن روابط کو برقرار رکھنے کا حکم دیا ہے انہیں برقرار رکھتے ہیں،اپنے ربّ سے ڈرتے ہیں اور اس بات کا خوف رکھتے ہیں کہ کہیں ان سے بُری طرح حساب نہ لیا جائے');
INSERT INTO "ur_maududi" VALUES(13,22,'13:22','اُن کا حال یہ ہوتا ہے کہ اپنے ربّ کی رضا کے لیے صبر سے کام لیتے ہیں ، نماز قائم کرتے ہیں، ہمارے دیے ہوئے رزق میں سے علانیہ اور پوشیدہ خرچ کرتے ہیں، اور بُرائی کو بھلائی سے دفع کرتے ہیں۔ آخرت کا گھر انہی لوگوں کے لیے ہے');
INSERT INTO "ur_maududi" VALUES(13,23,'13:23','یعنی ایسے باغ جو اُن کی ابدی قیام گاہ ہوں گے وہ خود بھی ان میں داخل ہوں گے اور ان کے آباؤ اجداد اور اُن کی بیویوں اور اُن کی اولاد میں سے جو جو صالح ہیں وہ بھی اُن کے ساتھ وہاں جائیں گے ملائکہ ہر طرف سے اُن کے استقبال کے لیے آئیں گے');
INSERT INTO "ur_maududi" VALUES(13,24,'13:24','اور اُن سے کہیں گے کہ ”تم پر سلامتی ہے، تم نے دُنیا میں جس طرح صبر سے کام لیا اُس کی بدولت آج تم اِ س کے مستحق ہوئے ہو“۔۔۔۔پس کیا ہی خوب ہے یہ آخرت کا گھر!');
INSERT INTO "ur_maududi" VALUES(13,25,'13:25','رہے وہ لوگ جو اللہ کے عہد کو مضبوط باندھ لینے کے بعد توڑ ڈالتے ہیں، جو اُن رابطوں کو کاٹتے ہیں جنہیں اللہ نے جوڑنے کا حکم دیا ہے، اور جو زمین میں فساد پھیلاتے ہیں، وہ لعنت کے مستحق ہیں اور ان کے لیے آخرت میں بہت برا ٹھکانا ہے');
INSERT INTO "ur_maududi" VALUES(13,26,'13:26','اللہ جس کو چاہتا ہے رزق کی فراخی بخشتا ہے اور جسے چاہتا ہے نپا تُلا رزق دیتا ہے۔ یہ لوگ دُنیوی زندگی میں مگن ہیں ، حالانکہ دُنیا کی زندگی آخرت کے مقابلےمیں ایک متاعِ قلیل کے سوا کچھ بھی نہیں');
INSERT INTO "ur_maududi" VALUES(13,27,'13:27','یہ لوگ جنہوں نے (رسالتِ محمدی ؐ کو ماننے سے)انکار کر دیا ہے کہتے ہیں ”اِس شخص پر اِس کے ربّ کی طرف سے کوئی نشانی کیوں نہ اُتری؟“ ۔۔۔۔کہو، اللہ جسے چاہتا ہے گمراہ کر دیتا ہے اور وہ اپنی طرف آنے کا راستہ اُسی کو دکھاتا ہے جو اُس کی طرف رُجوع کرے۔ ');
INSERT INTO "ur_maududi" VALUES(13,28,'13:28','ایسے ہی لوگ ہیں وہ جنہوں نے (اِس نبی کی دعوت کو) مان لیا اور اُن کے دلوں کو اللہ کی یاد سے اطمینان نصیب ہوتا ہے خبردار رہو! اللہ کی یاد ہی وہ چیز ہے جس سے دلوں کو اطمینان نصیب ہوا کرتا ہے');
INSERT INTO "ur_maududi" VALUES(13,29,'13:29','پھر جن لوگوں نے دعوت حق کو مانا اور نیک عمل کیے وہ خوش نصیب ہیں اور ان کے لیے اچھا انجام ہے');
INSERT INTO "ur_maududi" VALUES(13,30,'13:30','اے محمد ؐ ، اِسی شان سے ہم نے تم کو رسُول بنا کر بھیجا ہے ، ایک ایسی قوم میں جس سے پہلے بہت سی قومیں گزر چکی ہیں، تا کہ تم اِن لوگوں کو وہ پیغام سُناوٴ جو ہم نے تم پر نازل کیا ہے، اِس حال میں کہ یہ اپنے نہایت مہربان خدا کے کافر بنے ہوئے ہیں۔ اِن سےکہو کہ وہی میرا ربّ ہے ، اُس کے سوا کوئی معبود نہیں،اُسی پر میں نے بھروسہ کیا اور وہی میرا ملجا و ماویٰ ہے');
INSERT INTO "ur_maududi" VALUES(13,31,'13:31','اور کیا ہو جاتا اگر کوئی ایسا قرآن اُتار دیا جاتا جس کے زور سے پہاڑ چلنے لگتے، یا زمین شق ہو جاتی، یا مُردے قبروں سے نکل بولنے لگتے؟ (اِس طرح کی نشانیاں دِکھا دینا کچھ مشکل نہیں ہے)بلکہ سارا اختیار ہی اللہ کے ہاتھ میں ہے۔ پھر کیا اہلِ ایمان (ابھی تک کفّار کی طلب کے جواب میں کِسی نشانی کے ظہُور کی آس لگائے بیٹھے ہیں اور وہ یہ جان کر)مایوس نہیں ہو گئے کہ اگر اللہ چاہتا تو سارے انسانوں کو ہدایت دے دیتا ؟ جن لوگوں نے خدا کے ساتھ کُفر کا رویّہ اختیار کر رکھا ہے اُن پر ان کے کر تُو توں کی وجہ سے کوئی نہ کوئی آفت آتی ہی رہتی ہے، یا ان کےگھر کے قریب کہیں نازل ہوتی ہے۔ یہ سلسلہ چلتا رہے گا یہاں تک کہ اللہ کا وعدہ آن پُورا ہو۔ یقیناً اللہ اپنے وعدوں کے خلاف ورزی نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(13,32,'13:32','تم سے پہلے بھی بہت سے رسولوں کا مذاق اڑایا جا چکا ہے، مگر میں نے ہمیشہ منکر ین کو ڈھیل دی اور آخر کار ان کو پکڑ لیا، پھر دیکھ لو کہ میری سزا کیسی سخت تھی');
INSERT INTO "ur_maududi" VALUES(13,33,'13:33','پھر کیا وہ جو ایک ایک متنفّس کی کمائی پر نظر رکھتا ہے (اُس کے مقابلے میں یہ جسارتیں کی جارہی ہیں کہ)لوگوں نے اُس کے کچھ شریک ٹھہرا رکھے ہیں؟ اے نبی ؐ ، اِن سےکہو، (اگر واقعی وہ خدا کے اپنے بنائے ہوئے شریک ہیں تو)ذرا اُن کے نام لو کہ وہ کون ہیں؟ کیا تم اللہ کو ایک نئی بات کی خبر دے رہے ہو جسے وہ اپنی زمین میں نہیں جانتا؟ یا تم لوگ بس یونہی جو منہ میں آتا ہے کہہ ڈالتے ہو؟ حقیقت یہ ہے کہ جن لوگوں نے دعوتِ حق کو ماننے سے انکار کیا ہے ان کے لیے اُن کی مکّاریاں خوشنما بنا دی گئی ہیں اور وہ راہِ راست سے روک دیے گئے ہیں ، پھر جس کو اللہ گمراہی میں پھینک دے اُس کوئی راہ دکھانے والا نہیں ہے');
INSERT INTO "ur_maududi" VALUES(13,34,'13:34','ایسے لوگوں کے لیے دنیا کی زندگی ہی میں عذاب ہے، اور آخرت کا عذاب اُس سے بھی زیادہ سخت ہے کوئی ایسا نہیں جو اُنہیں خدا سے بچانے والا ہو');
INSERT INTO "ur_maududi" VALUES(13,35,'13:35','خدا ترس انسانوں کے لیے جس جنت کا وعدہ کیا گیا ہے اس کی شان یہ ہے کہ اس کے نیچے نہریں بہہ رہی ہیں، اس کے پھل دائمی ہیں اور اس کا سایہ لازوال یہ انجام ہے متقی لوگوں کا اور منکرین حق کا انجام یہ ہے کہ ان کے لیے دوزخ کی آگ ہے');
INSERT INTO "ur_maududi" VALUES(13,36,'13:36','اے نبی ؐ ، جن لوگوں کو ہم نے پہلے کتاب دی تھی وہ اِس کتاب سے جو ہم نے تم پر نازل کی ہے ، خوش ہیں اور مختلف گروہوں میں کچھ لوگ ایسے بھی ہیں جو اس کی بعض باتوں کو نہیں مانتے۔ تم صاف کہہ دو کہ”مجھے تو صرف اللہ کی بندگی کا حکم دیا گیا ہے اور اس سے منع کیا گیا ہے کہ کسی کو اس کے ساتھ شریک ٹھہراوٴں۔ لہٰذا میں اسی کی طرف دعوت دیتا ہوں اور اسی کی طرف میرا رجُوع ہے ۔“');
INSERT INTO "ur_maududi" VALUES(13,37,'13:37','اِسی ہدایت کے ساتھ ہم نے یہ فرمان عربی تم پر نازل کیا ہے اب اگر تم نے اِس علم کے باوجود جو تمہارے پاس آ چکا ہے لوگوں کی خواہشات کی پیروی کی تو اللہ کے مقابلے میں نہ کوئی تمہارا حامی و مددگار ہے اور نہ کوئی اس کی پکڑسے تم کو بچا سکتا ہے');
INSERT INTO "ur_maududi" VALUES(13,38,'13:38','تم سے پہلے بھی ہم بہت سے رسُول بھیج چکے ہیں اور اُن کو ہم نے بیوی بچوں والا ہی بنایا تھا۔ اور کسی رسُول کی بھی یہ طاقت نہ تھی کہ اللہ کے اذن کے بغیر کوئی نشانی خود لا دکھاتا۔ ہر دَور کے لیے ایک کتاب ہے');
INSERT INTO "ur_maududi" VALUES(13,39,'13:39','اللہ جو کچھ چاہتا ہے مٹا دیتا ہے اور جس چیز کو چاہتا ہے قائم رکھتا ہے، اُمُّ الکتاب اُسی کے پاس ہے۔ ');
INSERT INTO "ur_maududi" VALUES(13,40,'13:40','اور اے نبی ؐ ، جس بُرے انجام کی دھمکی ہم اِن لوگوں کو دے رہے ہیں اُس کا کوئی حصہ خواہ ہم تمہارے جیتے جی دکھا دیں یا اس کے ظہُور میں آنے سے پہلے ہم تمہیں اُٹھا لیں، بہر حال تمہارا کام صرف پیغام پہنچا دینا ہے اور اُسے حساب لینا ہمارا کام ہے۔ ');
INSERT INTO "ur_maududi" VALUES(13,41,'13:41','کیا یہ لوگ دیکھتے نہیں ہیں کہ ہم اِس سرزمین پر چلے آرہے ہیں اور اس کا دائرہ ہر طرف سے تنگ کرتے چلے آرہے ہیں؟ اللہ حکومت کر رہا ہے، کوئی اس کے فیصلوں پر نظرثانی کرنے والا نہیں ہے، اور حساب لیتے کچھ دیر نہیں لگتی');
INSERT INTO "ur_maududi" VALUES(13,42,'13:42','اِن سے پہلے جو لوگ ہو گزرے ہیں وہ بھی بڑی بڑی چالیں چل چکے ہیں ، مگر اصل فیصلہ کُن چال تو پُوری کی پُوری اللہ ہی کے ہاتھ میں ہے۔ وہ جانتا ہے کہ کون کیا کچھ کمائی کر رہا ہے، اور عنقریب یہ منکرینِ حق دیکھ لیں گے کہ انجام کس کا بخیر ہوتا ہے');
INSERT INTO "ur_maududi" VALUES(13,43,'13:43','یہ منکرین کہتے ہیں کہ تم خدا کے بھیجے ہوئے نہیں ہو۔ کہو، ”میرے اور تمہارے درمیان اللہ کی گواہی کافی ہے اور پھر ہر اُس شخص کی گواہی جو کتابِ آسمانی کا علم رکھتا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(14,1,'14:1','ا۔ل۔ر، اے محمد ؐ ، یہ ایک کتاب ہے جس کو ہم نے تمہاری طرف نازل کیا ہے تا کہ تم لوگوں کو تاریکیوں سے نکال کر روشنی میں لاوٴ، ان کے ربّ کی توفیق سے، اُس خدا کے راستے پر جو زبردست اور اپنی ذات میں آپ محمُود ہے');
INSERT INTO "ur_maududi" VALUES(14,2,'14:2','اور زمین اور آسمانوں کی ساری موجودات کا مالک ہے اور سخت تباہ کن سزا ہے قبول حق سے انکار کرنے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(14,3,'14:3','جو دُنیا کی زندگی کو آخرت پر ترجیح دیتے ہیں، جو اللہ کے راستے سے لوگوں کو روک رہے ہیں اور چاہتے ہیں کہ یہ راستہ (ان کی خواہشات کے مطابق)ٹیڑھا ہو جائے۔ یہ لوگ گمراہی میں بہت دُور نکل گئے ہیں');
INSERT INTO "ur_maududi" VALUES(14,4,'14:4','ہم نے اپنا پیغام دینے کےلیے جب کبھی کوئی رسوُل بھیجا ہے، اُس نے اپنی قوم ہی کی زبان میں پیغام دیا ہے تاکہ وہ اُنہیں اچھی طرح کھول کر بات سمجھائے۔ پھر اللہ جسے چاہتا ہے بھٹکا دیتا ہے اور جسے چاہتا ہے ہدایت بخشتا ہے، وہ بالادست اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(14,5,'14:5','ہم اِس سے پہلے موسیٰ ؑ کو بھی اپنی نشانیوں کے ساتھ بھیج چکے ہیں۔ اُسے بھی ہم نے حکم دیا تھا کہ اپنی قوم کو تاریکیوں سے نکال کر روشنی میں لا اور انہیں تاریخِ الٰہی کے سبق آموز واقعات سُنا کر نصیحت کر۔ اِن واقعات میں بڑی نشانیاں ہیں ہر اُس شخص کے لیے جو صبر اور شکر کرنے والا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(14,6,'14:6','یاد کرو جب موسیٰؑ نے اپنی قوم سے کہا "اللہ کے اُس احسان کو یاد رکھو جو اس نے تم پر کیا ہے اس نے تم کو فرعون والوں سے چھڑایا جو تم کو سخت تکلیفیں دیتے تھے، تمہارے لڑکوں کو قتل کر ڈالتے تھے اور تمہاری عورتوں کو زندہ بچا رکھتے تھے اس میں تمہارے رب کی طرف سے تمہاری بڑی آزمائش تھی');
INSERT INTO "ur_maududi" VALUES(14,7,'14:7','اور یاد رکھو، تمہارے ربّ نے خبردار کر دیا تھا کہ اگر شکر گزار بنو گے تو میں تم کو اور زیادہ نوازوں گا اور اگر کُفرانِ نعمت کرو گے تو میری سزا بہت سخت ہے۔ “');
INSERT INTO "ur_maududi" VALUES(14,8,'14:8','اور موسیٰؑ ”اگر تم کُفر کرو اور زمین کے سارے رہنے والے بھی کافر ہو جائیں تو اللہ بے نیاز اور اپنی ذات میں آپ محمود ہے۔ “');
INSERT INTO "ur_maududi" VALUES(14,9,'14:9','کیا تمہیں اُن قوموں کے حالات نہیں پہنچے جو تم سے پہلے گزر چکی ہیں؟ قومِ نوح ؑ ، عاد، ثمود اور ان کے بعد آنے والی بہت سی قومیں جن کا شمار اللہ ہی کو معلوم ہے؟ اُن کے رسول جب اُن کے پاس صاف صاف باتیں اور کھُلی کھُلی نشانیاں لیے ہوئے آئے تو انہوں نے اپنے منہ میں ہاتھ دبا لیے اور کہا کہ”جس پیغام کے ساتھ تم بھیجے گئے ہو ہم اُس کو نہیں مانتے اور جس چیز کی تم ہمیں دعوت دیتے ہو اُس کی طرف سے ہم سخت خلجان آمیز شک میں پڑے ہوئے ہیں۔ “');
INSERT INTO "ur_maududi" VALUES(14,10,'14:10','اُن کے رسوُلوں نے کہا”کیا خُدا کے بارے میں شک ہے جو آسمانوں اور زمین کا خالق ہے؟ وہ تمہیں بُلا رہا ہے تا کہ تمہارے قصور معاف کرے اور تم کو ایک مدُتِ مقرر تک مہلت دے۔“ انہوں نے جواب دیا ”تم کچھ نہیں ہو مگر ویسے ہی انسان جیسے ہم ہیں۔ تم ہمیں اُن ہستیوں کی بندگی سے روکنا چاہتے ہو جن کی بندگی باپ دادا سے ہوتی چلی آرہی ہے۔ اچھا تو لاوٴ کوئی صریح سَنَد۔“ ');
INSERT INTO "ur_maududi" VALUES(14,11,'14:11','ان کے رسولوں نے ان سے کہا”واقعی ہم کچھ نہیں ہیں مگر تم ہی جیسے انسان لیکن اللہ اپنے بندوں میں سے جس کو چاہتا ہے نوازتا ہے۔ اور یہ ہمارے اختیار میں نہیں ہےکہ تمہیں کوئی سَنَد لا دیں۔ سَنَد تو اللہ ہی کے اِذن سے آسکتی ہے اور اللہ ہی پر اہلِ ایمان کو بھروسہ کرنا چاہیے');
INSERT INTO "ur_maududi" VALUES(14,12,'14:12','اور ہم کیوں نہ اللہ پر بھروسہ کریں جبکہ ہماری زندگی کی راہوں میں اس نے ہماری رہنمائی کی ہے؟ جو اذیتیں تم لوگ ہمیں دے رہے ہو اُن پر ہم صبر کریں گے اور بھروسا کرنے والوں کا بھروسا اللہ ہی پر ہونا چاہیے"');
INSERT INTO "ur_maududi" VALUES(14,13,'14:13','آخرِ کار منکرین نے اپنے رسُولوں سے کہہ دیا کہ ”یا تو تمہیں ہماری ملّت میں واپس آنا ہوگا ورنہ ہم تمہیں اپنے ملک سے نکال دیں گے۔“ تب اُن کے ربّ نے اُن پر وحی بھیجی کہ ”ہم اِن ظالموں کو ہلاک کر دیں گے');
INSERT INTO "ur_maududi" VALUES(14,14,'14:14','اور ان کے بعد تمہیں زمین میں آباد کریں گے۔ یہ انعام ہے اس کا جو میرے حضُور جواب دہی کا خوف رکھتا ہو اور میری وعید سے ڈرتا ہو۔“');
INSERT INTO "ur_maududi" VALUES(14,15,'14:15','اُنہوں نے فیصلہ چاہا تھا (تو یُوں اُن کا فیصلہ ہوا)اور ہر جبّاردشمن ِ حق نے منہ کی کھائی۔ ');
INSERT INTO "ur_maududi" VALUES(14,16,'14:16','پھر اس کے بعد آگے اس کے لیے جہنم ہے وہاں اُسے کچ لہو کا سا پانی پینے کو دیا جائے گا');
INSERT INTO "ur_maududi" VALUES(14,17,'14:17','جسے وہ زبردستی حلق سے اتارنے کی کوشش کرے گا اور مشکل ہی سے اتار سکے گا موت ہر طرف سے اس پر چھائی رہے گی مگر وہ مرنے نہ پائے گا اور آگے ایک سخت عذاب اس کی جان کا لاگو رہے گا');
INSERT INTO "ur_maududi" VALUES(14,18,'14:18','جن لوگوں نے اپنے ربّ سے کُفر کیا ہے اُن کے اعمال کی مثال اُس راکھ کی سی ہے جسے ایک طوفانی دن کی آندھی نے اُڑا دیا ہو۔ وہ اپنے کیے کا کچھ بھی پھل نہ پا سکیں گے۔ یہی پرلے درجے کی گُم گشتگی ہے');
INSERT INTO "ur_maududi" VALUES(14,19,'14:19','کیا تم دیکھتے نہیں ہو کہ اللہ نے آسمان و زمین کی تخلیق کو حق پر قائم کیا ہے؟ وہ چاہے تو تم لوگوں کو لے جائے اور ایک نئی خلقت تمہاری جگہ لے آئے');
INSERT INTO "ur_maududi" VALUES(14,20,'14:20','ایسا کرنا اُس پر کچھ بھی دُشوار نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(14,21,'14:21','اور یہ لوگ جب اکٹھے اللہ کے سامنےبے نقاب ہوں گے تو اُس وقت ان میں جو دُنیا میں کمزور تھے وہ اُن لوگوں سے جو بڑے بنے ہوئے تھے ، کہیں گے”دُنیا میں ہم تمہارے تابع تھے، اب کیا تم اللہ کے عذاب سے ہم کو بچانے کے لیے بھی کچھ کر سکتے ہو؟“ وہ جواب دیں گے”اگر اللہ نے ہمیں نجات کی کوئی راہ دکھائی ہوتی تو ہم ضرور تمہیں بھی دکھا دیتے۔ اب تو یکساں ہے خواہ ہم جزَع فزَع کریں یا صبر، بہر حال ہمارے بچنے کی کوئی صورت نہیں۔ “');
INSERT INTO "ur_maududi" VALUES(14,22,'14:22','اور جب فیصلہ چُکا دیا جائے گا تو شیطان کہے گا ”حقیقت یہ ہے کہ اللہ نے جو وعدے تم سے کیے تھے وہ سب سچے تھے اور میں نے جتنے وعدے کیے اُن میں سے کوئی بھی پُورا نہ کیا ، میرا تم پر کوئی زور تو تھا نہیں، میں نے اس کے سوا کچھ نہیں کیا کہ اپنے راستے کی طرف تمہیں دعوت دی اور تم نے میری دعوت پر لبّیک کہا۔ اب مجھے ملامت نہ کرو، اپنے آپ ہی کو ملامت کرو۔ یہاں نہ میں تمہاری فریاد رسی کر سکتا ہوں اور نہ تم میری۔اس سے پہلے جو تم نے مجھے خدائی میں شریک بنا رکھا تھا میں اُس سے بری الذّمہ ہوں، ایسے ظالموں کے لیے تو دردناک سزا یقینی ہے۔“');
INSERT INTO "ur_maududi" VALUES(14,23,'14:23','بخلاف اس کے جو لوگ دُنیا میں ایمان لائے ہیں اور جنہوں نے نیک عمل کیے ہیں وہ ایسے باغوں میں داخل کیے جائیں گے جن کے نیچے نہریں بہتی ہوں گی۔ وہاں وہ اپنے ربّ کے اِذن سے ہمیشہ رہیں گے، اور وہاں اُن کا استقبال سلامتی کی مبارکباد سے ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(14,24,'14:24','کیا تم دیکھتے نہیں ہو کہ اللہ نے کلمہٴ طیّبہ کو کس چیز سے مثال دی ہے؟ اِس کی مثال ایسی ہے جیسے ایک اچھی ذات کا درخت جس کی جڑ زمین میں گہری جمّی ہوئی ہے اور شاخیں آسمان تک پہنچی ہوئی ہیں ');
INSERT INTO "ur_maududi" VALUES(14,25,'14:25','ہر آن وہ اپنے ربّ کے حکم سے اپنے پھل دے رہا ہے۔ یہ مثالیں اللہ اِس لیے دیتا ہے کہ لوگ اِن سے سبق لیں');
INSERT INTO "ur_maududi" VALUES(14,26,'14:26','اور کلمہ خبیثہ کی مثال ایک بد ذات درخت کی سی ہے جو زمین کی سطح سے اُکھاڑ پھینکا جاتا ہے، اُس کے لیے کوئی استحکام نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(14,27,'14:27','ایمان لانے والوں کو اللہ ایک قولِ ثابت کی بنیاد پر دُنیا اور آخرت دونوں میں ثبات عطا کرتا ہے، اور ظالموں کو اللہ بھٹکا دیتا ہے۔ اللہ کو اختیار ہے جو چاہے کرے');
INSERT INTO "ur_maududi" VALUES(14,28,'14:28','تم نے دیکھا اُن لوگوں کو جنہوں نے اللہ کی نعمت پائی اور اُسے کفران نعمت سے بدل ڈالا اور (اپنے ساتھ) اپنی قوم کو بھی ہلاکت کے گھر میں جھونک دیا');
INSERT INTO "ur_maududi" VALUES(14,29,'14:29','یعنی جہنم، جس میں وہ جھلسے جائیں گے اور وہ بد ترین جائے قرار ہے');
INSERT INTO "ur_maududi" VALUES(14,30,'14:30','اور اللہ کے کچھ ہمسر تجویز کر لیے تاکہ وہ انہیں اللہ کے راستے سے بھٹکا دیں اِن سے کہو، اچھا مزے کر لو، آخرکار تمہیں پلٹ کر جانا دوزخ ہی میں ہے');
INSERT INTO "ur_maududi" VALUES(14,31,'14:31','اے نبی ؐ ، میرے جو بندے ایمان لائے ہیں اُن سے کہہ دو کہ نماز قائم کریں اور جو کچھ ہم نے انہیں دیا ہے اُس میں سے کھُلے اور چھُپے(راہِ خیرمیں)خرچ کریں قبل اِس کے کہ وہ دن آئے جس میں نہ خرید و فروخت ہو گی اور نہ دوست نوازی ہو سکے گی۔ ');
INSERT INTO "ur_maududi" VALUES(14,32,'14:32','اللہ وہی تو ہے جس نے زمین اور آسمانوں کو پیدا کیا اور آسمان سے پانی برسایا، پھر اُس کے ذریعہ سے تمہاری رزق رسانی کے لیے طرح طرح کے پھل پیدا کیے۔ جس نے کشتی کو تمہارے لیے مسخّر کیا کہ سمندر میں اُس کے حکم سے چلے اور دریاوٴں کو تمہارے لیے مسخّر کیا');
INSERT INTO "ur_maududi" VALUES(14,33,'14:33','جس نے سُورج اور چاند کو تمہارے لیے مسخّر کیا کہ لگاتار چلے جا رہے ہیں اور رات اور دن کو تمہارے لیے مسخّر کیا۔ ');
INSERT INTO "ur_maududi" VALUES(14,34,'14:34','جس نے وہ سب کچھ تمہیں دیا جو تم نے مانگا۔ اگر تم اللہ کی نعمتوں کا شمار کرنا چاہو تو کر نہیں سکتے۔ حقیقت یہ ہے کہ انسان بڑا ہی بے انصاف اور ناشکرا ہے');
INSERT INTO "ur_maududi" VALUES(14,35,'14:35','یاد کرو وہ وقت جب ابراہیم ؑ نے دُعا کی تھی کہ”پروردگار، اِس شہر کو امن کا شہر بنا اور مجھے اور میری اولاد کو بُت پرستی سے بچا');
INSERT INTO "ur_maududi" VALUES(14,36,'14:36','پروردگار ، اِن بُتوں نے بہتوں کو گُمراہی میں ڈالا ہے (ممکن ہے کہ میری اولاد کو بھی یہ گُمراہ کر دیں، لہٰذا اُن میں سے)جو میرے طریقے پر چلے وہ میرا ہے اور جو میرے خلاف طریقہ اختیار کرے تو یقیناً تُو درگزر کرنے والا مہربان ہے۔ ');
INSERT INTO "ur_maududi" VALUES(14,37,'14:37','پروردگار، میں نے ایک بے آب و گیاہ وادی میں اپنی اولاد کے ایک حصے کو تیرے محترم گھر کے پاس لا بسایا ہے۔ پروردگار، یہ میں نے اِس لیے کیا ہےکہ لوگ یہاں نماز قائم کریں، لہٰذا تُو لوگوں کے دِلوں کو اِن کا مشتاق بنااور انہیں کھانے کو پھل دے ، شاید کہ یہ شکر گزار بنیں');
INSERT INTO "ur_maududi" VALUES(14,38,'14:38','پروردگار، تُو جانتا ہے جو کچھ ہم چھُپاتے ہیں اور جو کچھ ہم ظاہر کرتے ہیں“ ۔۔۔۔ اور واقعی اللہ سے کچھ بھی چھُپا ہوا نہیں ہے، نہ زمین میں نہ آسمانوں میں');
INSERT INTO "ur_maududi" VALUES(14,39,'14:39','"شکر ہے اُس خدا کا جس نے مجھے اِس بڑھاپے میں اسماعیلؑ اور اسحاقؑ جیسے بیٹے دیے، حقیقت یہ ہے کہ میرا رب ضرور دعا سنتا ہے');
INSERT INTO "ur_maududi" VALUES(14,40,'14:40','ا ے میرے پروردگار، مجھے نماز قائم کرنے والا بنا اور میری اولاد سے بھی (ایسے لوگ اٹھا جو یہ کام کریں) پروردگار، میری دعا قبول کر');
INSERT INTO "ur_maududi" VALUES(14,41,'14:41','پروردگار ، مجھے اور میرے والدین کو اور سب ایمان لانے والوں کو اُس دن معاف کر دیجیو جب کہ حساب قائم ہوگا۔“ ');
INSERT INTO "ur_maududi" VALUES(14,42,'14:42','اب یہ ظالم لوگ جو کچھ کر رہے ہیں، اللہ کو تم اس سے غافل نہ سمجھو اللہ تو اِنہیں ٹال رہا ہے اُس دن کے لیے جب حال یہ ہوگا کہ آنکھیں پھٹی کی پھٹی رہ گئی ہیں');
INSERT INTO "ur_maududi" VALUES(14,43,'14:43','سر اُٹھائے بھاگے چلے جارہے ہیں، نظریں اُوپر جمی ہیں اور دل اُڑے جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(14,44,'14:44','اے محمدؐ، اُس دن سے تم اِنہیں ڈراؤ جبکہ عذاب اِنہیں آلے گا اُس وقت یہ ظالم کہیں گے کہ "اے ہمارے رب، ہمیں تھوڑی سی مہلت اور دے دے، ہم تیری دعوت کو لبیک کہیں گے اور رسولوں کی پیروی کریں گے" (مگر انہیں صاف جواب دے دیا جائے گا کہ) کیا تم وہی لوگ نہیں ہو جو اِس سے پہلے قسمیں کھا کھا کر کہتے تھے کہ ہم پر تو کبھی زوال آنا ہی نہیں ہے؟');
INSERT INTO "ur_maududi" VALUES(14,45,'14:45','حالانکہ تم ان قوموں کی بستیوں میں رہ بس چکے تھے جنہوں نے اپنے اوپر آپ ظلم کیا تھا اور دیکھ چکے تھے کہ ہم نے اُن سے کیا سلوک کیا اور اُن کی مثالیں دے دے کر ہم تمہیں سمجھا بھی چکے تھے');
INSERT INTO "ur_maududi" VALUES(14,46,'14:46','اُنہوں نے اپنی ساری ہی چالیں چل دیکھیں، مگر اُن کی ہر چال کا توڑ اللہ کے پاس تھا اگرچہ اُن کی چالیں ایسی غضب کی تھیں کہ پہاڑ اُن سے ٹل جائیں۔ ');
INSERT INTO "ur_maududi" VALUES(14,47,'14:47','پس اے نبی ؐ ، تم ہر گز یہ گمان نہ کرو کہ اللہ کبھی اپنے رسُولوں سے کیے ہوئے وعدے کے خلاف کرے گا۔ اللہ زبردست ہے اور انتقام لینے والا ہے');
INSERT INTO "ur_maududi" VALUES(14,48,'14:48','ڈراوٴ انہیں اُس دن سے جب کہ زمین اور آسمان بدل کر کچھ سے کچھ کر دیے جائیں گے اور سب کے سب اللہ واحد قہّار کے سامنے بے نقاب حاضر ہو جائیں گے');
INSERT INTO "ur_maududi" VALUES(14,49,'14:49','اُس روز تم مجرموں کو دیکھو گے کہ زنجیروں میں ہاتھ پاؤں جکڑے ہونگے');
INSERT INTO "ur_maududi" VALUES(14,50,'14:50','تارکول کے لباس پہنے ہوئے ہوں گے اور آگ کے شعلے اُن کے چہروں پر چھائے جا رہے ہوں گے');
INSERT INTO "ur_maududi" VALUES(14,51,'14:51','یہ اِس لیے ہوگا کہ اللہ ہر متنفس کو اس کے کیے کا بدلہ دے گا اللہ کوحساب لیتے کچھ دیر نہیں لگتی');
INSERT INTO "ur_maududi" VALUES(14,52,'14:52','یہ ایک پیغام ہے سب انسانوں کے لیے، اور یہ بھیجا گیا ہے اس لیے کہ اُن کو اِس کے ذریعہ سے خبردار کر دیا جائے اور وہ جان لیں کہ حقیقت میں خدا بس ایک ہی ہے اور جو عقل رکھتے ہیں وہ ہوش میں آ جائیں');
INSERT INTO "ur_maududi" VALUES(15,1,'15:1','ا۔ل۔ر، یہ آیات ہیں کتابِ الہٰی اور قرآن ِ مبین کی۔ ');
INSERT INTO "ur_maududi" VALUES(15,2,'15:2','بعید نہیں کہ ایک وقت وہ آ جائے جب وہی لوگ جنہوں نے آج (دعوت اسلام کو قبول کرنے سے) انکار کر دیا ہے پچھتا پچھتا کر کہیں گے کہ کاش ہم نے سر تسلیم خم کر دیا ہوتا');
INSERT INTO "ur_maududi" VALUES(15,3,'15:3','چھوڑو اِنہیں کھائیں، پییں، مزے کریں، اور بھلاوے میں ڈالے رکھے اِن کو جھوٹی امید عنقریب اِنہیں معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(15,4,'15:4','ہم نے اِس سے پہلے جس بستی کو بھی ہلاک کیا ہے اس کے لیے ایک خاص مہلتِ عمل لکھی جا چکی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(15,5,'15:5','کوئی قوم نہ اپنے وقت مقرر سے پہلے ہلاک ہوسکتی ہے، نہ اُس کے بعد چھوٹ سکتی ہے');
INSERT INTO "ur_maududi" VALUES(15,6,'15:6','یہ لوگ کہتے ہیں”اَے وہ شخص جس پر ذکر نازل ہوا ہے، تُو یقیناً دیوانہ ہے');
INSERT INTO "ur_maududi" VALUES(15,7,'15:7','اگر تو سچا ہے تو ہمارے سامنے فرشتوں کو لے کیوں نہیں آتا؟"');
INSERT INTO "ur_maududi" VALUES(15,8,'15:8','ہم فرشتوں کو یوں نہیں اُتار دیا کرتے۔ وہ جب اُترتے ہیں تو حق کے ساتھ اُترتے ہیں اور پھر لوگوں کو مہلت نہیں دی جاتی۔ ');
INSERT INTO "ur_maududi" VALUES(15,9,'15:9','رہا یہ ذکر ، تو اِس کو ہم نے نازل کیا ہے اور ہم خود اِس کےنگہبان ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(15,10,'15:10','اے محمدؐ، ہم تم سے پہلے بہت سی گزری ہوئی قوموں میں رسول بھیج چکے ہیں');
INSERT INTO "ur_maududi" VALUES(15,11,'15:11','کبھی ایسا نہیں ہوا کہ اُن کہ پاس کوئی رسول آیا ہو اور اُنہوں نے اُس کا مذاق نہ اڑایا ہو');
INSERT INTO "ur_maududi" VALUES(15,12,'15:12','مجرمین کے دلوں میں تو ہم اس ذکر کو اِسی طرح (سلاخ کے مانند) گزارتے ہیں');
INSERT INTO "ur_maududi" VALUES(15,13,'15:13','وہ اس پر ایمان نہیں لایا کرتے۔ قدیم سے اس قماش کے لوگوں کا یہی طریقہ چلا آرہا ہے');
INSERT INTO "ur_maududi" VALUES(15,14,'15:14','اگر ہم اُن پر آسمان کا کوئی دروازہ کھول دیتے اور وہ دن دہاڑے اُس میں چڑھنے بھی لگتے');
INSERT INTO "ur_maududi" VALUES(15,15,'15:15','تب بھی وہ یہی کہتے کہ ہماری آنکھوں کو دھوکا ہو رہا ہے، بلکہ ہم پر جادو کر دیا گیا ہے');
INSERT INTO "ur_maududi" VALUES(15,16,'15:16','یہ ہماری کارفرمائی ہے کہ آسمان میں ہم نے بہت سے مضبوط قلعے بنائے ، اُن کو دیکھنے والوں کے لیے مزیَّن کیا، ');
INSERT INTO "ur_maududi" VALUES(15,17,'15:17','اور ہر شیطانِ مردُود سے اُن کو محفوظ کر دیا۔ ');
INSERT INTO "ur_maududi" VALUES(15,18,'15:18','کوئی شیطان اِن میں راہ نہیں پا سکتا، اِلّا یہ کہ کچھ سُن گُن لے لے۔ اور جب وہ سُن گُن لینے کی کوشش کرتا ہے تو ایک شعلہ روشن اُس کا پیچھا کرتا ہے ');
INSERT INTO "ur_maududi" VALUES(15,19,'15:19','ہم نے زمین کو پھیلایا، اُس میں پہاڑ جمائے، اُس میں ہر نوع کی نباتات ٹھیک ٹھیک نپی تُلی مقدار کے ساتھ اُگائی، ');
INSERT INTO "ur_maududi" VALUES(15,20,'15:20','اور اس میں معیشت کے اسباب فراہم کیے، تمہارے لیے بھی اور اُن بہت سی مخلوقات کے لیے بھی جن کے رازق تم نہیں ہو');
INSERT INTO "ur_maududi" VALUES(15,21,'15:21','کوئی چیز ایسی نہیں جس کے خزانے ہمارے پاس نہ ہوں، اور جس چیز کو بھی ہم نازل کرتے ہیں ایک مقرر مقدار میں نازل کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(15,22,'15:22','بار آور ہواؤں کو ہم ہی بھیجتے ہیں، پھر آسمان سے پانی برساتے ہیں، اور اُس پانی سے تمہیں سیراب کرتے ہیں اِس دولت کے خزانہ دار تم نہیں ہو');
INSERT INTO "ur_maududi" VALUES(15,23,'15:23','زندگی اور موت ہم دیتے ہیں، اور ہم ہی سب کے وارث ہونے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(15,24,'15:24','پہلے جو لوگ تم میں سے ہو گزرے ہیں اُن کو بھی ہم نے دیکھ رکھا ہے، اور بعد کے آنے والے بھی ہماری نگاہ میں ہیں');
INSERT INTO "ur_maududi" VALUES(15,25,'15:25','یقیناً تمہارا رب ان سب کو اکٹھا کرے گا، وہ حکیم بھی ہے اور علیم بھی۔ ');
INSERT INTO "ur_maududi" VALUES(15,26,'15:26','ہم نے انسان کو سڑی ہوئی مٹی کے سُوکھے گارے سے بنایا۔ ');
INSERT INTO "ur_maududi" VALUES(15,27,'15:27','اور اس سے پہلے ہم جنّوں کو ہم آگ کی لِپٹ سے پیدا کر چکے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(15,28,'15:28','پھر یاد کرو اُس موقع کو جب تمہارے رب نے فرشتوں سے کہا کہ "میں سڑی ہوئی مٹی کے سوکھے گارے سے ایک بشر پیدا کر رہا ہوں');
INSERT INTO "ur_maududi" VALUES(15,29,'15:29','جب میں اُسے پُورا بنا چکوں اور اس میں اپنی روح سے کچھ پھونک دوں تو تم سب اُس کے آگے سجدے میں گِر جانا۔“');
INSERT INTO "ur_maududi" VALUES(15,30,'15:30','چنانچہ تمام فرشتوں نے سجدہ کیا');
INSERT INTO "ur_maududi" VALUES(15,31,'15:31','سوائے ابلیس کے کہ اُس نے سجدہ کرنے والوں کا ساتھ دینے سے انکار کر دیا۔ ');
INSERT INTO "ur_maududi" VALUES(15,32,'15:32','رب نے پوچھا "اے ابلیس، تجھے کیا ہوا کہ تو نے سجدہ کرنے والوں کا ساتھ نہ دیا؟');
INSERT INTO "ur_maududi" VALUES(15,33,'15:33','اس نے کہا "میرا یہ کام نہیں ہے کہ میں اِس بشر کو سجدہ کروں جسے تو نے سڑی ہوئی مٹی کے سوکھے گارے سے پیدا کیا ہے"');
INSERT INTO "ur_maududi" VALUES(15,34,'15:34','رب نے فرمایا "اچھا تو نکل جا یہاں سے کیونکہ تو مردود ہے');
INSERT INTO "ur_maududi" VALUES(15,35,'15:35','اور اب روزِ جزا تک تجھ پر لعنت ہے۔ “');
INSERT INTO "ur_maududi" VALUES(15,36,'15:36','اُس نے عرض کیا "میرے رب، یہ بات ہے تو پھر مجھے اُس روز تک کے لیے مہلت دے جبکہ سب انسان دوبارہ اٹھائے جائیں گے"');
INSERT INTO "ur_maududi" VALUES(15,37,'15:37','فرمایا "اچھا، تجھے مہلت ہے');
INSERT INTO "ur_maududi" VALUES(15,38,'15:38','اُس دن تک جس کا وقت ہمیں معلوم ہے"');
INSERT INTO "ur_maududi" VALUES(15,39,'15:39','وہ بولا”میرے ربّ، جیسا تُونے مجھے بہکایا اُسی طرح اب میں زمین ان کے لیے دلفریبیاں پیدا کر کے اِن سب کو بہکا دوں گا، ');
INSERT INTO "ur_maududi" VALUES(15,40,'15:40','سوائے تیرے اُن بندوں کے جنہیں تو نے اِن میں سے خالص کر لیا ہو"');
INSERT INTO "ur_maududi" VALUES(15,41,'15:41','فرمایا”یہ راستہ ہے جو سیدھا مجھ تک پہنچتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(15,42,'15:42','بے شک ، جو میرےحقیقی بندے ہیں ان پر تیرا بس نہ چلے گا۔ تیرا بس تو صرف اُن بہکے ہوئے لوگوں ہی پر چلے گا جو تیری پیروی کریں، ');
INSERT INTO "ur_maududi" VALUES(15,43,'15:43','اور ان سب کے لیے جہنّم کی وعید ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(15,44,'15:44','یہ جہنّم(جس کی وعید پَیروانِ ابلیس کے لیے کی گئی ہے)اس کے سات دروازے ہیں۔ ہر دروازے کے لیے ان میں سے ایک حصہ مخصوص کر دیا گیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(15,45,'15:45','بخلاف اِس کے متقی لوگ باغوں اور چشموں میں ہوں گے');
INSERT INTO "ur_maududi" VALUES(15,46,'15:46','اور اُن سے کہا جائے گا کہ داخل ہو جاؤ ان میں سلامتی کے ساتھ بے خوف و خطر');
INSERT INTO "ur_maududi" VALUES(15,47,'15:47','اُن کے دلوں میں جو تھوڑی بہت کھوٹ کپٹ ہو گی اسے ہم نکال دیں گے، وہ آپس میں بھائی بھائی بن کر آمنے سامنے تختوں پر بیٹھیں گے');
INSERT INTO "ur_maududi" VALUES(15,48,'15:48','اُنہیں نہ وہاں کسی مشقّت سے پالا پڑے گا اور نہ وہ وہاں سے نکالے جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(15,49,'15:49','اے نبیؐ، میرے بندوں کو خبر دے دو کہ میں بہت درگزر کرنے والا اور رحیم ہوں');
INSERT INTO "ur_maududi" VALUES(15,50,'15:50','مگر اِس کے ساتھ میرا عذاب بھی نہایت دردناک عذاب ہے');
INSERT INTO "ur_maududi" VALUES(15,51,'15:51','اور انہیں ذرا ابراہیمؑ کے مہمانوں کا قصّہ سُناوٴ۔ ');
INSERT INTO "ur_maududi" VALUES(15,52,'15:52','جب وہ آئے اُس کےہاں اور کہا”سلام ہو تم پر“ تو اُس نے کہا”ہمیں تم سے ڈر لگتا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(15,53,'15:53','اُنہوں نے جواب دیا”ڈرو نہیں، ہم تمہیں ایک بڑے سیانے لڑکے کی بشارت دیتے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(15,54,'15:54','ابراہیمؑ نے کہا "کیا تم اِس بڑھاپے میں مجھے اولاد کی بشارت دیتے ہو؟ ذرا سوچو تو سہی کہ یہ کیسی بشارت تم مجھے دے رہے ہو؟"');
INSERT INTO "ur_maududi" VALUES(15,55,'15:55','اُنہوں نے جواب دیا "ہم تمہیں برحق بشارت دے رہے ہیں، تم مایوس نہ ہو"');
INSERT INTO "ur_maududi" VALUES(15,56,'15:56','ابراہیمؑ نے کہا "اپنے رب کی رحمت سے مایوس تو گمراہ لوگ ہی ہوا کرتے ہیں"');
INSERT INTO "ur_maududi" VALUES(15,57,'15:57','پھر ابراہیم ؑ نے پوچھا”اے فرستادگانِ الٰہی، وہ مہم کیا جس پر آپ حضرات تشریف لائے ہیں؟“ ');
INSERT INTO "ur_maududi" VALUES(15,58,'15:58','وہ بولے”ہم ایک مُجرم قوم کی طرف بھیجے گئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(15,59,'15:59','صرف لوطؑ کے گھر والے مستثنیٰ ہیں، ان سب کو ہم بچا لیں گے');
INSERT INTO "ur_maududi" VALUES(15,60,'15:60','سوائے اُس کی بیوی کے جس کے لیے (اللہ فرماتا ہے کہ) ہم نے مقدر کر دیا ہے کہ وہ پیچھے رہ جانے والوں میں شامل رہے گی"');
INSERT INTO "ur_maududi" VALUES(15,61,'15:61','پھر جب یہ فرستادے لُوط ؑ کے ہاں پہنچے ');
INSERT INTO "ur_maududi" VALUES(15,62,'15:62','تو اُس نے کہا”آپ لوگ اجنبی معلوم ہوتے ہیں“ ');
INSERT INTO "ur_maududi" VALUES(15,63,'15:63','اُنہوں نے جواب دیا "نہیں، بلکہ ہم وہی چیز لے کر آئے ہیں جس کے آنے میں یہ لوگ شک کر رہے تھے');
INSERT INTO "ur_maududi" VALUES(15,64,'15:64','ہم تم سے سچ کہتے ہیں کہ ہم حق کے ساتھ تمہارے پاس آئے ہیں');
INSERT INTO "ur_maududi" VALUES(15,65,'15:65','لہٰذا اب تم کچھ رات رہے اپنے گھر والوں کو لے کر نکل جاوٴ اور خود ان کے پیچھے پیچھے چلو۔ تم میں سے کوئی پلٹ کر نہ دیکھے۔ بس سیدھے چلے جاوٴ جِدھر جانے کا تمہیں حکم دیا جا رہا ہے۔“');
INSERT INTO "ur_maududi" VALUES(15,66,'15:66','اور اُسے ہم نے اپنا یہ فیصلہ پہنچا دیا کہ صبح ہوتے ہوتے اِن لوگوں کی جڑ کاٹ دی جائے گی');
INSERT INTO "ur_maududi" VALUES(15,67,'15:67','اتنے میں شہر کے لوگ خوشی کے مارے بے تاب ہو کر لُوط ؑ کے گھر چڑھ آئے۔ ');
INSERT INTO "ur_maududi" VALUES(15,68,'15:68','لوطؑ نے کہا "بھائیو، یہ میرے مہمان ہیں، میری فضیحت نہ کرو');
INSERT INTO "ur_maududi" VALUES(15,69,'15:69','اللہ سے ڈرو، مجھے رسوا نہ کرو"');
INSERT INTO "ur_maududi" VALUES(15,70,'15:70','وہ بولے "کیا ہم بارہا تمہیں منع نہیں کر چکے ہیں کہ دنیا بھر کے ٹھیکے دار نہ بنو؟"');
INSERT INTO "ur_maududi" VALUES(15,71,'15:71','لوط ؑ نے عاجز ہو کر کہا”اگر تمہیں کچھ کرنا ہی ہے تو یہ میری بیٹیاں موجود ہیں!“ ');
INSERT INTO "ur_maududi" VALUES(15,72,'15:72','تیری جان کی قسم اے نبیؐ، اُس وقت اُن پر ایک نشہ سا چڑھا ہوا تھا جس میں وہ آپے سے باہر ہوئے جاتے تھے');
INSERT INTO "ur_maududi" VALUES(15,73,'15:73','آخرکار پو پھٹتے ہی اُن کو ایک زبردست دھماکے نے آ لیا');
INSERT INTO "ur_maududi" VALUES(15,74,'15:74','اور ہم نے اُس بستی کو تلپٹ کر کے رکھ دیا اور اُن پر پکی ہوئی مٹی کے پتھروں کی بارش برسا دی۔ ');
INSERT INTO "ur_maududi" VALUES(15,75,'15:75','اِس واقعے میں بڑی نشانیاں ہیں اُن لوگوں کے لیے جو صاحب فراست ہیں');
INSERT INTO "ur_maududi" VALUES(15,76,'15:76','اور وہ علاقہ (جہاں یہ واقعہ پیش آیا تھا)گزر گاہ ِعام پر واقع ہے، ');
INSERT INTO "ur_maududi" VALUES(15,77,'15:77','اُس میں سامان عبرت ہے اُن لوگوں کے لیے جو صاحب ایمان ہیں');
INSERT INTO "ur_maududi" VALUES(15,78,'15:78','اور اَیکہ والے ظالم تھے');
INSERT INTO "ur_maududi" VALUES(15,79,'15:79','تو دیکھ لو کہ ہم نے بھی اُن سے انتقام لیا، اور ان دونوں قوموں کے اُجڑے ہوئے علاقے کھُلے راستے پر واقع ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(15,80,'15:80','حِجر کے لوگ بھی رسُولوں کی تکذیب کر چکے ہیں');
INSERT INTO "ur_maududi" VALUES(15,81,'15:81','ہم نے اپنی آیات اُن کے پاس بھیجیں، اپنی نشانیاں اُن کو دکھائیں، مگر وہ سب کو نظر انداز ہی کرتے رہے');
INSERT INTO "ur_maududi" VALUES(15,82,'15:82','وہ پہاڑ تراش تراش کر مکان بناتے تھے اور اپنی جگہ بالکل بے خوف اور مطمئن تھے');
INSERT INTO "ur_maududi" VALUES(15,83,'15:83','آخرکار ایک زبردست دھماکے نے اُن کو صبح ہوتے آ لیا');
INSERT INTO "ur_maududi" VALUES(15,84,'15:84','اور اُن کی کمائی اُن کے کچھ کام نہ آئی۔ ');
INSERT INTO "ur_maududi" VALUES(15,85,'15:85','ہم نے زمین اور آسمان کو اور اُن کی موجودات کو حق کے سوا کسی اور بُنیاد پر خلق نہیں کیا ہے، اور فیصلے کی گھڑی یقیناً آنے والی ہے، پس اے محمد ؐ ، تم (اِن لوگوں کی بے ہودگیوں پر)شریفانہ درگزر سے کام لو');
INSERT INTO "ur_maududi" VALUES(15,86,'15:86','یقیناً تمہارا ربّ سب کا خالق ہے اور سب کچھ جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(15,87,'15:87','ہم نے تم کو سات ایسی آیتیں دے رکھی ہیں جو بار بار دہرائی جانے کے لائق ہیں اور تمہیں قرآنِ عظیم عطا کیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(15,88,'15:88','تم اس متاعِ دُنیا کی طرف آنکھ اُٹھا کر نہ دیکھو جو ہم نے ان میں سے مختلف قسم کے لوگوں کو دے رکھی ہے، اور نہ اِن کے حال پر اپنا دل کُڑھاوٴ۔ انہیں چھوڑ کر ایمان لانے والوں کی طرف جُھکو');
INSERT INTO "ur_maududi" VALUES(15,89,'15:89','اور (نہ ماننے والوں سے) کہہ دو کہ میں تو صاف صاف تنبیہ کرنے والا ہوں');
INSERT INTO "ur_maududi" VALUES(15,90,'15:90','یہ اُسی کی طرح کی تنبیہ ہے جیسی ہم نے اُن تفرقہ پردازوں کی طرف بھیجی تھی');
INSERT INTO "ur_maududi" VALUES(15,91,'15:91','جنہوں نے اپنے قرآن کو ٹکڑے ٹکڑے کر ڈالا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(15,92,'15:92','تو قسم ہے تیرے رب کی، ہم ضرور ان سب سے پوچھیں گے');
INSERT INTO "ur_maududi" VALUES(15,93,'15:93','کہ تم کیا کرتے رہے ہو');
INSERT INTO "ur_maududi" VALUES(15,94,'15:94','پس اے نبیؐ، جس چیز کا تمہیں حکم دیا جا رہا ہے، اُسے ہانکے پکارے کہہ دو اور شرک کرنے والوں کی ذرا پروا نہ کرو');
INSERT INTO "ur_maududi" VALUES(15,95,'15:95','تمہاری طرف سے ہم اُن مذاق اڑانے والوں کی خبر لینے کے لیے کافی ہیں');
INSERT INTO "ur_maududi" VALUES(15,96,'15:96','جو اللہ کے ساتھ کسی اور کو بھی خدا قرار دیتے ہیں عنقریب انہیں معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(15,97,'15:97','ہمیں معلوم ہے کہ جو باتیں یہ لوگ تم پر بناتے ہیں ان سے تمہارے دل کو سخت کوفت ہوتی ہے');
INSERT INTO "ur_maududi" VALUES(15,98,'15:98','اس کا علاج یہ ہے کہ اپنے رب کی حمد کے ساتھ اس کی تسبیح کرو، اس کی جناب میں سجدہ بجا لاؤ');
INSERT INTO "ur_maududi" VALUES(15,99,'15:99','اور اُس آخری گھڑی تک اپنے ربّ کی بندگی کرتے رہو جس کا آنا یقینی ہے ');
INSERT INTO "ur_maududi" VALUES(16,1,'16:1','آگیا اللہ کا فیصلہ، اب اس کے لیے جلدی نہ مچاوٴ، پاک ہے وہ اور بالا و بر تر ہے اُس شرک سے جو یہ لوگ کر رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(16,2,'16:2','وہ اِس روح کو اپنے جس بندے پر چاہتا ہے اپنے حکم سے ملائکہ کے ذریعے نازل فرما دیتا ہے (اِس ہدایت کے ساتھ کے لوگوں کو)”آگاہ کر دو، میرے سوا کوئی تمہارا معبود نہیں ہے، لہٰذا تم مجھی سے ڈرو۔“ ');
INSERT INTO "ur_maududi" VALUES(16,3,'16:3','اُس نے آسمان و زمین کو بر حق پیدا کیا ہے، وہ بہت بالا و برتر ہے اُس شرک سے جو یہ لوگ کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(16,4,'16:4','اُس نے انسان کو ایک ذرا سی بُوند سے پیدا کیا اور دیکھتے دیکھتے صریحاً وہ ایک جھگڑالُو ہستی بن گیا۔ ');
INSERT INTO "ur_maududi" VALUES(16,5,'16:5','اس نے جانور پیدا کیے جن میں تمہارے لیے پوشاک بھی ہے اور خوراک بھی، اور طرح طرح کے دوسرے فائدے بھی');
INSERT INTO "ur_maududi" VALUES(16,6,'16:6','اُن میں تمہارے لیے جمال ہے جب کہ صبح تم انہیں چرنے کے لیے بھیجتے ہو اور جبکہ شام انہیں واپس لاتے ہو');
INSERT INTO "ur_maududi" VALUES(16,7,'16:7','وہ تمہارے لیے بوجھ ڈھو کر ایسے ایسے مقامات تک لے جاتے ہیں جہاں تم سخت جانفشانی کے بغیر نہیں پہنچ سکتے حقیقت یہ ہے کہ تمہارا رب بڑا ہی شفیق اور مہربان ہے');
INSERT INTO "ur_maududi" VALUES(16,8,'16:8','اُس نے گھوڑے اور خچّر اور گدھے پیدا کیے تا کہ تم ان پر سوار ہو اور وہ تمہاری زندگی کی رونق بنیں۔ وہ اور بہت سی چیزیں(تمہارے فائدے کے لیے)پیدا کرتا ہے جن کا تمہیں علم تک نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(16,9,'16:9','اور اللہ ہی کے ذمّہ ہے سیدھا راستہ بتانا جب کہ راستے ٹیڑھے بھی موجود ہیں۔ اگر وہ چاہتا تو تم سب کو ہدایت دے دیتا ');
INSERT INTO "ur_maududi" VALUES(16,10,'16:10','وہی ہے جس نے آسمان سے تمہارے لیے پانی برسایا جس سے تم خود بھی سیراب ہوتے ہو اور تمہارے جانوروں کے لیے بھی چارہ پیدا ہوتا ہے');
INSERT INTO "ur_maududi" VALUES(16,11,'16:11','وہ اس پانی کے ذریعہ سے کھیتیاں اگاتا ہے اور زیتون اور کھجور اور انگور اور طرح طرح کے دوسرے پھل پیدا کرتا ہے اِس میں ایک بڑی نشانی ہے اُن لوگوں کے لیے جو غور و فکر کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(16,12,'16:12','اُس نے تمہاری بھلائی کے لیے رات اور دن کو اور سورج اور چاند کو مسخر کر رکھا ہے اور سب تارے بھی اُسی کے حکم سے مسخر ہیں اِس میں بہت سی نشانیاں ہیں اُن لوگوں کے لیے جو عقل سے کام لیتے ہیں');
INSERT INTO "ur_maududi" VALUES(16,13,'16:13','اور یہ جو بہت سی رنگ برنگ کی چیزیں اس نے تمہارے لیے زمین میں پیدا کر رکھی ہیں، اِن میں بھی ضرور نشانی ہے اُن لوگوں کے لیے جو سبق حاصل کرنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(16,14,'16:14','وہی ہے جس نے تمہارے لیے سمندر کو مسخّر کر رکھا ہے تا کہ تم اس سے تر و تازہ گوشت لے کر کھاوٴ اور اس سے زینت کی وہ چیزیں نکالو جنہیں تم پہنا کرتے ہو۔ تم دیکھتے ہو کہ کشتی سمندر کا سینہ چیرتی ہوئی چلتی ہے۔ یہ سب کچھ اس لیے ہے کہ تم اپنے ربّ کا فضل تلاش کرو اور اس کے شکر گزار بنو');
INSERT INTO "ur_maududi" VALUES(16,15,'16:15','اُس نے زمین میں پہاڑوں کی میخیں گاڑ دیں تاکہ زمین تم کو لے کر ڈُھلک نہ جائے۔ اس نے دریا جاری کیے اور قدرتی راستے بنائے تاکہ تم ہدایت پاو');
INSERT INTO "ur_maududi" VALUES(16,16,'16:16','اس نے زمین میں راستہ بتانے والی علامتیں رکھ دیں، اور تاروں سے بھی لوگ ہدایت پاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(16,17,'16:17','پھر کیا وہ جو پیدا کرتا ہے اور وہ جو کچھ بھی پیدا نہیں کرتے ، دونوں یکساں ہیں؟ کیا تم ہوش میں نہیں آتے؟');
INSERT INTO "ur_maududi" VALUES(16,18,'16:18','اگر تم اللہ کی نعمتوں کو گننا چاہو تو گِن نہیں سکتے، حقیقت یہ ہے کہ وہ بڑا ہی درگزر کرنے والا اور رحیم ہے، ');
INSERT INTO "ur_maududi" VALUES(16,19,'16:19','حالانکہ وہ تمہارے کھُلے سے بھی واقف ہے اور چھُپے سے بھی۔ ');
INSERT INTO "ur_maududi" VALUES(16,20,'16:20','اور وہ دوسری ہستیاں جنہیں اللہ کو چھوڑ کر لوگ پکارتے ہیں، وہ کسی چیز کی بھی خالق نہیں ہیں بلکہ خود مخلوق ہیں');
INSERT INTO "ur_maududi" VALUES(16,21,'16:21','مُردہ ہیں نہ کہ زندہ۔ اور ان کو کچھ معلوم نہیں ہے کہ انہیں کب(دوبارہ زندہ کر کے)اُٹھایا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(16,22,'16:22','تمہارا خدا بس ایک ہی خدا ہے۔ مگر جو لوگ آخرت کو نہیں مانتے اُن کےدلوںمیں انکار بس کر رہ گیا ہے اور وہ گھمنڈ میں پڑ گئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(16,23,'16:23','اللہ یقیناً اِن کے سب کرتوت جانتا ہے چھپے ہوئے بھی اور کھلے ہوئے بھی وہ اُن لوگوں کو ہرگز پسند نہیں کرتا جو غرور نفس میں مبتلا ہوں');
INSERT INTO "ur_maududi" VALUES(16,24,'16:24',' اور جب کوئی ان سے پُوچھتا ہے کہ تمہارے ربّ نےیہ کیا چیز نازل کی ہے ، تو کہتے ہیں”اجی وہ تو اگلے وقتوں کی فرسُودہ کہانیاں ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(16,25,'16:25','یہ باتیں وہ اس لیے کرتے ہیں کہ قیامت کے روز اپنے بوجھ بھی پورے اٹھائیں، اور ساتھ ساتھ کچھ اُن لوگوں کے بوجھ بھی سمیٹیں جنہیں یہ بر بنائے جہالت گمراہ کر رہے ہیں دیکھو! کیسی سخت ذمہ داری ہے جو یہ اپنے سر لے رہے ہیں');
INSERT INTO "ur_maududi" VALUES(16,26,'16:26','اِن سے پہلے بھی بہت سے لوگ (حق کو نیچا دکھانے کے لیے) ایسی ہی مکاریاں کر چکے ہیں، تو دیکھ لو کہ اللہ نے اُن کے مکر کی عمارت جڑ سے اکھاڑ پھینکی اور اس کی چھت اوپر سے ان کے سر پر آ رہی اور ایسے رخ سے ان پر عذاب آیا جدھر سے اس کے آنے کا اُن کو گمان تک نہ تھا');
INSERT INTO "ur_maududi" VALUES(16,27,'16:27','پھر قیامت کے روز اللہ انہیں ذلیل و خوار کرے گا۔ وہ اُن سے کہے گا”بتاوٴ اب کہاں ہیں میرے وہ شریک جن کےلیے تم (اہلِ حق سے)جھگڑے کیا کرتے تھے؟“۔۔۔۔ جن لوگوں کو دنیا میں علم حاصل تھا وہ کہیں گے”آج رُسوائی اور بدبختی ہے کافروں کے لیے۔“');
INSERT INTO "ur_maududi" VALUES(16,28,'16:28','ہاں، اُنہی کافروں کے لیے جو اپنے نفس پر ظلم کرتے ہوئے جب ملائکہ کے ہاتھوں گرفتار ہوتے ہیں تو (سرکشی چھوڑ کر)فوراً ڈَگیں ڈال دیتے ہیں اور کہتے ہیں”ہم تو کوئی قصُور نہیں کر رہے تھے۔“ ملائکہ جواب دیتے ہیں”کر کیسے نہیں رہے تھے!اللہ تمہارے کرتُوتوں سے خوب واقف ہے');
INSERT INTO "ur_maududi" VALUES(16,29,'16:29','اب جاوٴ، جہنّم کے دروازوں میں گھُس جاوٴ۔ وہیں تم کو ہمیشہ رہنا ہے۔“ پس حقیقت یہی ہے کہ بڑا ہی بُرا ٹھکانہ ہے متکبّروں کے لیے');
INSERT INTO "ur_maududi" VALUES(16,30,'16:30','دُوسری طرف جب خدا ترس لوگوں سے پوچھا جاتا ہے کہ یہ کیا چیز ہے جو تمہارے ربّ کی طرف سے نازل کی ہوئی ہے، تو وہ جواب دیتے ہیں کہ”بہترین چیز اُتری ہے۔“ اِس طرح کے نیکوکار لوگوں کے لیے اِس دنیا میں بھی بھلائی ہے اور آخرت کا گھر تو ضرور ہی ان کےحق میں بہتر ہے۔ بڑا اچھا گھر ہے متقیوں کا');
INSERT INTO "ur_maududi" VALUES(16,31,'16:31','دائمی قیام کی جنتیں ، جن میں وہ داخل ہوں گے، نیچے نہریں بہ رہی ہوں گی،اور سب کچھ وہاں عین اُن کی خواہش کے مطابق ہوگا۔ یہ جزا دیتا ہے اللہ متّقیوں کو');
INSERT INTO "ur_maududi" VALUES(16,32,'16:32','اُن متقیوں کو جن کی روحیں پاکیزگی کی حالت میں جب ملائکہ قبض کرتے ہیں تو کہتے ہیں "سلام ہو تم پر، جاؤ جنت میں اپنے اعمال کے بدلے"');
INSERT INTO "ur_maududi" VALUES(16,33,'16:33','اے محمد ؐ ، اب جو یہ لوگ انتظار کر رہے ہیں تو اِس کے سوا اب اور کیا باقی رہ گیا ہے کہ ملائکہ ہی آپہنچیں ، یا تیرے ربّ کا فیصلہ صادر ہو جائے؟ اِس طرح ڈِھٹائی اِن سے پہلے بہت سے لوگ کر چکے ہیں۔ پھر جو کچھ اُن کے ساتھ ہوا وہ اُن پر اللہ کا ظلم نہ تھا بلکہ اُن کا اپنا ظلم تھا جو اُنہوں نے خود اپنے اُوپر کیا');
INSERT INTO "ur_maududi" VALUES(16,34,'16:34','اُن کے کرتوتوں کی خرابیاں آخر اُن کی دامنگیر ہو گئیں اور وہی چیز اُن پر مسلط ہو کر رہی جس کا وہ مذاق اڑایا کرتے تھے');
INSERT INTO "ur_maududi" VALUES(16,35,'16:35','یہ مشرکین کہتے ہیں”اگر اللہ چاہتا تو نہ ہم اور نہ ہمارے باپ دادا اُس کے سوا کسی اَور کی عبادت کرتے اور نہ اُس کے حکم کے بغیر کسی چیز کو حرام ٹھہراتے۔“ ایسے ہی بہانے اِن سے پہلے کے لوگ بھی بناتے رہے ہیں۔ تو کیا رسُولوں پر صاف صاف بات پہنچا دینے کے سوا اور بھی کوئی ذمّہ داری ہے؟');
INSERT INTO "ur_maududi" VALUES(16,36,'16:36','ہم نے ہر اُمّت میں ایک رسُول بھیج دیا، اور اُس کے ذریعہ سے سب کو خبر دار کر دیا کہ ”اللہ کی بندگی کرو اور طاغوت کی بندگی سے بچو۔“ اس کے بعد ان میں سے کسی کو اللہ نے ہدایت بخشی اور کسی پر ضلالت مسلّط ہو گئی۔ پھر ذرا زمین میں چل پھر کر دیکھ لو کہ جھُٹلانے والوں کا کیا انجام ہو چکا ہے ');
INSERT INTO "ur_maududi" VALUES(16,37,'16:37','اے محمدؐ، تم چاہے اِن کی ہدایت کے لیے کتنے ہی حریص ہو، مگر اللہ جس کو بھٹکا دیتا ہے پھر اسے ہدایت نہیں دیا کرتا اور اس طرح کے لوگوں کی مدد کوئی نہیں کر سکتا');
INSERT INTO "ur_maududi" VALUES(16,38,'16:38','یہ لوگ اللہ کے نام سے کڑی کڑی قسمیں کھا کر کہتے ہیں کہ "اللہ کسی مرنے والے کو پھر سے زندہ کر کے نہ اٹھائے گا" اٹھائے گا کیوں نہیں، یہ تو ایک وعدہ ہے جسے پورا کرنا اس نے اپنے اوپر واجب کر لیا ہے، مگر اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(16,39,'16:39','اور ایسا ہونا اس لیے ضروری ہے کہ اللہ اِن کے سامنے اُس حقیقت کو کھول دے جس کے بارے میں یہ اختلاف کر رہے ہیں، اور منکرینِ حق کو معلوم ہو جائے کہ وہ جھُوٹے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(16,40,'16:40','(رہا اس کا امکان ، تو)ہمیں کسی چیز کو وجود میں لانے کے لیے اس سے زیادہ کچھ نہیں کرنا ہوتا کہ اسے حکم دیں”ہو جا“ اور بس وہ ہو جاتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(16,41,'16:41','جو لوگ ظلم سہنے کے بعد اللہ کی خاطر ہجرت کر گئے ہیں ان کو ہم دنیا ہی میں اچھا ٹھکانا دیں گے اور آخرت کا اجر تو بہت بڑا ہے۔ کاش جان لیں وہ مظلوم');
INSERT INTO "ur_maududi" VALUES(16,42,'16:42','جنہوں نے صبر کیا ہے اور جو اپنے رب کے بھروسے پر کام کر رہے ہیں (کہ کیسا اچھا انجام اُن کا منتظر ہے)');
INSERT INTO "ur_maududi" VALUES(16,43,'16:43','اے محمد ؐ ، ہم نے تم سے پہلے بھی جب کبھی رسُول بھیجے ہیں آدمی ہی بھیجے ہیں جن کی طرف ہم اپنے پیغامات وحی کیا کرتے تھے۔ اہل ِذکر سے پُوچھ لو اگر تم لوگ خود نہیں جانتے');
INSERT INTO "ur_maududi" VALUES(16,44,'16:44','پچھلے رسُولوں کو بھی ہم نے روشن نشانیاں اور کتابیں دے کر بھیجا تھا، اور اب یہ ذِکر تم پر نازل کیا ہے تا کہ تم لوگوں کے سامنے اُس تعلیم کی تشریح و توضیح کرتے جاوٴ جو اُن کے لیے اُتاری گئی ہے ، اور تاکہ لوگ (خود بھی)غور و فکر کریں');
INSERT INTO "ur_maududi" VALUES(16,45,'16:45','پھر کیا وہ لوگ جو (دعوت پیغمبر کی مخالفت میں) بدتر سے بدتر چالیں چل رہے ہیں اِس بات سے بالکل ہی بے خوف ہو گئے ہیں کہ اللہ ان کو زمین میں دھنسا دے، یا ایسے گوشے میں ان پر عذاب لے آئے جدھر سے اس کے آنے کا ان کو وہم و گمان تک نہ ہو');
INSERT INTO "ur_maududi" VALUES(16,46,'16:46','یا اچانک چلتے پھرتے ان کو پکڑ لے، یا ایسی حالت میں انہیں پکڑے جبکہ انہیں خود آنے والی مصیبت کا کھٹکا لگا ہوا ہو اور وہ اس سے بچنے کی فکر میں چوکنے ہوں؟');
INSERT INTO "ur_maududi" VALUES(16,47,'16:47','وہ جو کچھ بھی کرنا چاہے یہ لوگ اس کو عاجز کرنے کی طاقت نہیں رکھتے حقیقت یہ ہے کہ تمہارا رب بڑا ہی نرم خو اور رحیم ہے');
INSERT INTO "ur_maududi" VALUES(16,48,'16:48','اور کیا یہ لوگ اللہ کی پیدا کی ہوئی کسی چیز کو بھی نہیں دیکھتے کہ اس کا سایہ کس طرح اللہ کے حضُور سجدہ کرتے ہوئے دائیں اور بائیں گرتا ہے؟ سب کے سب اِس طرح اظہارِ عجز کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(16,49,'16:49','زمین اور آسمانوں میں جس قدر جان دار مخلوقات ہیں اور جتنے ملائکہ ہیں سب اللہ کے آگے سر بسجُود ہیں۔ وہ ہر گز سرکشی نہیں کرتے');
INSERT INTO "ur_maududi" VALUES(16,50,'16:50','اپنے رب سے جو اُن کے اوپر ہے، ڈرتے ہیں اور جو کچھ حکم دیا جاتا ہے اسی کے مطابق کام کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(16,51,'16:51','اللہ کا فرمان ہے کہ ”دو خدا نہ بنا لو، خدا تو بس ایک ہی ہے، لہٰذا تم مجھی سے ڈرو');
INSERT INTO "ur_maududi" VALUES(16,52,'16:52','اُسی کا ہے وہ سب کچھ جو آسمانوں میں ہے اور جو زمین میں ہے، اور خالصًا اُسی کا دین (ساری کائنات میں)چل رہا ہے۔ پھر کیا للہ کو چھوڑ کر تم کسی اور سے تقوٰی کرو گے؟“ ');
INSERT INTO "ur_maududi" VALUES(16,53,'16:53','تم کو جو نعمت بھی حاصل ہے اللہ ہی کی طرف سے ہے۔ پھر جب کوئی سخت وقت تم پر آتا ہے تو تم لوگ خود اپنی فریادیں لے کر اُسی کی طرف دوڑتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(16,54,'16:54','مگر جب اللہ اُس وقت کو ٹال دیتا ہے تو یکایک تم میں سے ایک گروہ اپنے ربّ کے ساتھ دُوسروں کو(اس مہربانی کے شکریے میں)شریک کرنے لگتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(16,55,'16:55','تاکہ اللہ کے احسان کی ناشکری کرے اچھا، مزے کر لو، عنقریب تمہیں معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(16,56,'16:56','یہ لوگ جن کی حقیقت سے واقف نہیں ہیں اُن کے حصے ہمارے دیے ہوئے رزق میں سے مقرر کرتے ہیں۔۔۔۔ خدا کی قسم، ضرور تم سے پوچھا جائے گا کہ یہ جھُوٹ تم نے کیسے گھڑ لیے تھے؟');
INSERT INTO "ur_maududi" VALUES(16,57,'16:57','یہ خدا کے لیے بیٹیاں تجویز کرتے ہیں۔ سُبحان اللہ! اور اِن کے لیے وہ جو یہ خود چاہیں؟ ');
INSERT INTO "ur_maududi" VALUES(16,58,'16:58','جب اِن میں سے کسی کو بیٹی کے پیدا ہونے کی خوش خبری دی جاتی ہے تو اُس کے چہرے پر کلونس چھا جاتی ہے اور وہ بس خون کا سا گھونٹ پی کر رہ جاتا ہے');
INSERT INTO "ur_maududi" VALUES(16,59,'16:59','لوگوں سے چھُپتا پھرتا ہے کہ اِس بُری خبر کے بعد کیا کسی کو منہ دکھائے۔ سوچتا ہے کہ ذلّت کے ساتھ بیٹی کو لیے رہے یا مٹی میں دبا دے؟۔۔۔۔دیکھو ، کیسے بُرے حکم ہیں جو یہ خُدا کے بارے میں لگاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(16,60,'16:60','بری صفات سے متصف کیے جانے کے لائق تو وہ لوگ ہیں جو آخرت کا یقین نہیں رکھتے رہا اللہ، تو اُس کے لیے سب سے برتر صفات ہیں، وہی تو سب پر غالب اور حکمت میں کامل ہے');
INSERT INTO "ur_maududi" VALUES(16,61,'16:61','اگر کہیں اللہ لوگوں کو اُن کی زیادتی پر فوراً ہی پکڑ لیا کر تا تو روئے زمین پر کسی متنفس کو نہ چھوڑتا لیکن وہ سب کو ایک وقت مقرر تک مہلت دیتا ہے، پھر جب وہ وقت آ جاتا ہے تو اس سے کوئی ایک گھڑی بھر بھی آگے پیچھے نہیں ہو سکتا');
INSERT INTO "ur_maududi" VALUES(16,62,'16:62','آج یہ لوگ وہ چیزیں اللہ کے لیے تجویز کر رہے ہیں جو خود اپنے لیے اِنہیں ناپسند ہیں، اور جھوٹ کہتی ہیں اِن کی زبانیں کہ اِن کے لیے بھلا ہی بھلا ہے اِن کے لیے تو ایک ہی چیز ہے، اور وہ ہے دوزخ کی آگ ضرور یہ سب سے پہلے اُس میں پہنچائے جائیں گے');
INSERT INTO "ur_maududi" VALUES(16,63,'16:63','خدا کی قسم، اے محمدؐ، تم سے پہلے بھی بہت سی قوموں میں ہم رسول بھیج چکے ہیں (اور پہلے بھی یہی ہوتا رہا ہے کہ) شیطان نے اُن کے برے کرتوت اُنہیں خوشنما بنا کر دکھائے (اور رسولوں کی بات انہوں نے مان کر نہ دی) وہی شیطان آج اِن لوگوں کا بھی سرپرست بنا ہوا ہے اور یہ دردناک سزا کے مستحق بن رہے ہیں');
INSERT INTO "ur_maududi" VALUES(16,64,'16:64','ہم نے یہ کتاب تم پر اس لیے نازل کی ہےکہ تم اُن اختلافات کی حقیقت اِن پر کھول دو جن میں یہ پڑے ہوئے ہیں۔ یہ کتاب رہنمائی اور رحمت بن کر اُتری ہے اُن لوگوں کے لیے جو اِسے مان لیں۔ ');
INSERT INTO "ur_maududi" VALUES(16,65,'16:65','(تم ہر برسات میں دیکھتے ہو کہ)اللہ نے آسمان سے پانی برسایا اور یکایک مُردہ پڑی ہوئی زمین میں اُس کی بدولت جان ڈال دی۔ یقیناً اس میں ایک نشانی ہے سُننے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(16,66,'16:66','اور تمہارے لیے مویشیوں میں بھی ایک سبق موجود ہے۔ اُن کے پیٹ سے گوبر اور خون کے درمیان ہم ایک چیز تمہیں پِلاتے ہیں، یعنی خالص دُودھ ، جو پینے والوں کے لیے نہایت خوشگوار ہے');
INSERT INTO "ur_maududi" VALUES(16,67,'16:67','(اسی طرح)کھجور کے درختوں اور انگُور کی بیلوں سے بھی ہم ایک چیز تمہیں پلاتے ہیں جسے تم نشہ آور بھی بنا لیتے ہو اور پاک رزق بھی۔ یقیناً اِس میں ایک نشانی ہے عقل سے کام لینے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(16,68,'16:68','اور دیکھو، تمہارے ربّ نے شہد کی مکھی پر یہ بات وحی کر دی کہ پہاڑوں میں، اور درختوں میں، اور ٹٹیوںپر چڑھائی ہوئی بیلوں میں');
INSERT INTO "ur_maududi" VALUES(16,69,'16:69','اپنے چھتّے بنا اور ہر طرح کا رَس چُوس اور اپنے ربّ کی ہموار کی ہوئی راہوں پر چلتی رہ۔ اِس مکھی کے اندر سے رنگ برنگ کا ایک شربت نکلتا ہے جس میں شفا ہے لوگوں کے لیے۔ یقیناً اس میں بھی ایک نشانی ہے اُن لوگوں کے لیے جو غور و فکر کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(16,70,'16:70','اور دیکھو، اللہ نے تم کو پیدا کیا، پھر وہ تم کو موت دیتا ہے ، اور تم میں سے کوئی بدترین عمر کو پہنچا دیا جاتا ہے تاکہ سب کچھ جاننے کے بعد پھر کچھ نہ جانے۔ حق یہ ہے کہ اللہ ہی علم میں بھی کامل ہے اور قدرت میں بھی');
INSERT INTO "ur_maududi" VALUES(16,71,'16:71','اور دیکھو، اللہ نے تم میں سے بعض کو بعض پر رزق میں فضیلت عطا کی ہے ، پھر جن لوگوں کو یہ فضیلت دی گئی ہے وہ ایسے نہیں ہیں کہ اپنا رزق اپنے غلاموں کی طرف پھیر دیا کرتے ہوں تاکہ دونوں اِس رزق میں برابر کے حصہ دار بن جائیں۔ تو کیا اللہ ہی کا اِحسان ماننے سے اِن لوگوں کو اِنکار ہے ؟');
INSERT INTO "ur_maududi" VALUES(16,72,'16:72','اور وہ اللہ ہی جس نے تمہارے لیے تمہاری ہم جنس بیویاں بنائیں اور اسی نے ان بیویوں سے تمہیں بیٹے پوتے عطا کیے اور اچھی اچھی چیزیں تمہیں کھانے کو دیں۔ پھر کیا یہ لوگ (یہ سب کچھ دیکھتے اور جانتے ہوئے بھی)باطل کو مانتے ہیں اور اللہ کے احسان کا انکار کرتے ہیں ');
INSERT INTO "ur_maududi" VALUES(16,73,'16:73','اور اللہ کو چھوڑ کر اُن کو پوجتے ہیں جن کے ہاتھ میں نہ آسمانوں سے انہیں کچھ بھی رزق دینا ہے نہ زمین سے اور نہ یہ کام وہ کر ہی سکتے ہیں؟');
INSERT INTO "ur_maududi" VALUES(16,74,'16:74','پس اللہ کے لیے مثالیں نہ گھڑو ، اللہ جانتا ہے، تم نہیں جانتے');
INSERT INTO "ur_maududi" VALUES(16,75,'16:75','اللہ ایک مثال دیتا ہے ۔ ایک تو ہے غلام، جو دُوسرے کا مملوک ہے اور خود کوئی اختیار نہیں رکھتا۔ دُوسرا شخص ایسا ہے جسے ہم نے اپنی طرف سے اچھا رزق عطا کیا ہے اور وہ اس میں سے کھُلے اور چھُپے خوب خرچ کرتا ہے۔ بتاوٴ ، کیا یہ دونوں برابر ہیں؟۔۔۔۔ الحمدُللہ مگر اکثر لوگ (اس سیدھی بات کو)نہیں جانتے۔ ');
INSERT INTO "ur_maududi" VALUES(16,76,'16:76','اللہ ایک اور مثال دیتا ہے۔ دو آدمی ہیں۔ ایک گونگا بہرا ہے ، کوئی کام نہیں کر سکتا ، اپنے آقا پر بوجھ بنا ہوا ہے، جدھر بھی وہ اسے بھیجے کوئی بھلا کام اُس سے بن نہ آئے۔ دُوسرا شخص ایسا ہے کہ انصاف کا حکم دیتا ہے اور خود راہِ راست پر قائم ہے۔ بتاوٴ کیا یہ دونوں یکساں ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(16,77,'16:77','اور زمین و آسمان کے پوشیدہ حقائق کا علم تو اللہ ہی کو ہے۔ اور قیامت کے برپا ہونے کا معاملہ کچھ دیر نہ لے گا مگر بس اتنی کہ جس میں آدمی کی پلک جھپک جائے، بلکہ اس سے بھی کچھ کم۔ حقیقت یہ ہے کہ اللہ سب کچھ کر سکتا ہے');
INSERT INTO "ur_maududi" VALUES(16,78,'16:78','اللہ نے تم کو تمہاری ماوٴں کے پیٹوں سے نکالا اس حالت میں کہ تم کچھ نہ جانتے تھے۔ اُس نے تمہیں کان دیے ، آنکھیں دیں، اور سوچنےوالے دل دیے ، اس لیے کہ تم شکر گزار بنو۔ ');
INSERT INTO "ur_maududi" VALUES(16,79,'16:79','کیا اِن لوگوں نے کبھی پرندوں کو نہیں دیکھا کہ فضائے آسمانی میں کسی طرح مسخر ہیں؟ اللہ کے سوا کس نے اِن کو تھام رکھا ہے؟ اِس میں بہت نشانیاں ہیں اُن لوگوں کے لیے جو ایمان لاتے ہیں');
INSERT INTO "ur_maududi" VALUES(16,80,'16:80','اللہ نے تمہارے لیے تمہارے گھروں کو جائے سکُون بنایا۔ اس نے جانوروں کی کھالوں سے تمہارے لیے ایسے مکان پیدا کیے جنہیں تم سفر اور قیام، دونوں حالتوں میں ہلکا پاتے ہو۔ اُس نے جانوروں کے صوف اور اُون اور بالوں سے تمہارے لیے پہننے اور برتنے کی بہت سی چیزیں پیدا کر دیں جو زندگی کی مدّتِ مقررہ تک تمہارے کام آتی ہیں');
INSERT INTO "ur_maududi" VALUES(16,81,'16:81','اُس نے اپنی پیدا کی ہوئی بہت سی چیزوں سے تمہارے لیے سائے کا انتظام کیا، پہاڑوں پر تمہارے لیے پناہ گاہیں بنائیں، اور تمہیں ایسی پوشاکیں بخشیں جو تمہیں گرمی سے بچاتی ہیں اور کچھ دُوسری پوشاکیں جو آپس کی جنگ میں تمہاری حفاظت کرتی ہیں۔ اس طرح وہ تم پر اپنی نعمتوں کی تکمیل کرتا ہے شاید کہ تم فرمانبردار بنو');
INSERT INTO "ur_maududi" VALUES(16,82,'16:82','اب اگر یہ لوگ منہ موڑتے ہیں تو اے محمدؐ، تم پر صاف صاف پیغام حق پہنچا دینے کے سوا اور کوئی ذمہ داری نہیں ہے');
INSERT INTO "ur_maududi" VALUES(16,83,'16:83','یہ اللہ کے احسان کے پہچانتے ہیں ، پھر اس کا انکار کرتے ہیں۔ اور اِن میں بیش تر لوگ ایسے ہیں جو حق ماننے کے لیے تیار نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(16,84,'16:84','(اِنہیں کچھ ہوش بھی ہے کہ اُس روز کی بنے گی)جب کہ ہم ہر اُمّت میں سے ایک گواہ کھڑا کریں گے، پھر کافروں کو نہ حُجتیں پیش کرنے کا موقع دیا جائے گا نہ ان سے توبہ و استغفار ہی کا مطالبہ کیا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(16,85,'16:85','ظالم لوگ جب ایک دفعہ عذاب دیکھ لیں گے تو اس کے بعد نہ اُن کے عذاب میں کوئی تخفیف کی جائے گی اور نہ انہیں ایک لمحہ بھر کی مہلت دی جائے گی');
INSERT INTO "ur_maududi" VALUES(16,86,'16:86','اور جب وہ لوگ جنہوں نے دنیا میں شرک کیا تھا اپنے ٹھہرائے ہوئے شریکوں کو دیکھیں گے تو کہیں گے ”اے پروردگار، یہی ہیں ہمارے وہ شریک جنہیں ہم تجھے چھوڑ کر پُکارا کرتے تھے۔“ اس پر اُن کے وہ معبود انہیں صاف جواب دیں گے کہ”تم جھُوٹے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(16,87,'16:87','اُس وقت یہ سب اللہ کے آگے جُھک جائیں گے اور ان کو وہ ساری افترا پردازیاں رفو چکّر ہو جائیں گی جو یہ دنیا میں کرتے رہے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(16,88,'16:88','جن لوگوں نے خود کُفر کی راہ اختیار کی اور دُوسروں کو اللہ کی راہ سے روکا انہیں ہم عذاب پر عذاب دیں گے اُس فساد کے بدلے میں جو وہ دُنیا میں برپا کرتے رہے');
INSERT INTO "ur_maududi" VALUES(16,89,'16:89','(اے محمد ؐ ، اِنہیں اُس دن سے خبردار کر دو)جب کہ ہم ہر اُمّت میں خود اُسی کے اندر سے ایک گواہ اُٹھا کھڑا کریں گے جو اُس کےمقابلہ میں شہادت دے گا، اور اِن لوگوں کے مقابلے میں شہادت دینے کے لیے ہم تمہیں لائیں گے۔ اور (یہ اسی شہادت کی تیاری ہے کہ)ہم نے یہ کتاب تم پر نازل کر دی ہے جو ہر چیز کی صاف صاف وضاحت کرنے والی ہے اور ہدایت و رحمت اور بشارت ہے اُن لوگوں کے لیے جنہوں نے سرِ تسلیم خَم کر دیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(16,90,'16:90','اللہ عدل اور احسان اور صلہء رحمی کا حکم دیتا ہے اور بدی اور بے حیائی اور ظلم و زیادتی سے منع کرتا ہے۔ وہ تمہیں نصیحت کرتا ہے تاکہ تم سبق لو');
INSERT INTO "ur_maududi" VALUES(16,91,'16:91','اللہ کے عہد کو پورا کرو جبکہ تم نے اس سے کوئی عہد باندھا ہو، اور اپنی قسمیں پختہ کرنے کے بعد توڑ نہ ڈالو جبکہ تم اللہ کو اپنے اوپر گواہ بنا چکے ہو اللہ تمہارے سب افعال سے باخبر ہے');
INSERT INTO "ur_maududi" VALUES(16,92,'16:92','تمہاری حالت اُس عورت کی سی نہ ہو جائے جس نے آپ ہی محنت سے سُوت کاتا اور پھر آپ ہی اُسے ٹکڑے ٹکڑے کر ڈالا۔ تم اپنی قسموں کو آپس کے معاملات میں مکر و فریب کا ہتھیار بناتے ہو تاکہ ایک قوم دُوسری قوم سے بڑھ کر فائدے حاصل کرے حالانکہ اللہ اس عہد و پیمان کے ذریعے سے تم کو آزمائش میں ڈالتا ہے ، اور ضرور وہ قیامت کے روز تمہارے تمام اختلافات کی حقیقت تم پر کھول دے گا۔ ');
INSERT INTO "ur_maududi" VALUES(16,93,'16:93','اگر اللہ کی مشیّت یہ ہوتی (کہ تم میں کوئی اختلاف نہ ہو)تو وہ تم سب کو ایک ہی اُمّت بنا دیتا، مگر وہ جسے چاہتا ہے گمراہی میں ڈالتا ہے اور جسے چاہتا ہے راہِ راست دکھا دیتا ہے، اور ضرور تم سے تمہارے اعمال کی باز پُرس ہوکر رہے گی');
INSERT INTO "ur_maududi" VALUES(16,94,'16:94','(اور اے مسلمانو)تم اپنی قسموں کو آپس میں ایک دوسرے کو دھوکہ دینے کا ذریعہ نہ بنا لینا، کہیں ایسا نہ ہو کے کوئی قدم جمنے کے بعد اُکھڑ جائے اور تم اِس جُرم کی پاداش میں کہ تم نے لوگوں کو اللہ کی راہ سے روکا، بُرا نتیجہ دیکھو اور سخت سزا بُھگتو');
INSERT INTO "ur_maududi" VALUES(16,95,'16:95','اللہ کے عہد کو تھوڑے سے فائدے کے بدلے نہ بیچ ڈالو، جو کچھ اللہ کے پاس ہے وہ تمہارے لیے زیادہ بہتر ہے اگر تم جانو');
INSERT INTO "ur_maududi" VALUES(16,96,'16:96','جو کچھ تمہارے پاس ہے وہ خرچ ہو جانے والا ہے اور جو کچھ اللہ کے پاس ہے وہی باقی رہنے والا ہے، اور ہم ضرور صبر سے کام لینے والوں کو اُن کے اجر اُن کے بہترین اعمال کے مطابق دیں گے');
INSERT INTO "ur_maududi" VALUES(16,97,'16:97','جو شخص بھی نیک عمل کرے گا خواہ وہ مرد ہو یا عورت، بشرطیکہ ہو وہ مومن، اُسے ہم دُنیا میں پاکیزہ زندگی بسر کرائیں گے اور (آخرت میں)ایسے لوگوں کو اُں کے اجر اُن کے بہترین اعمال کے مطابق بخشیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(16,98,'16:98','پھر جب تم قرآن پڑھنے لگو تو شیطان رجیم سے خدا کی پناہ مانگ لیا کرو۔ ');
INSERT INTO "ur_maududi" VALUES(16,99,'16:99','اُسے اُن لوگوں پر تسلط حاصل نہیں ہوتا جو ایمان لاتے اور اپنے رب پر بھروسا کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(16,100,'16:100','اس کا زور تو انہی لوگوں پر چلتا ہے جو اس کو اپنا سرپرست بناتے اور اس کے بہکانے سے شرک کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(16,101,'16:101','جب ہم ایک آیت کی جگہ دُوسری آیت نازل کرتے ہیں۔۔۔۔ اور اللہ بہتر جانتا ہے کہ وہ کیا نازل کرے۔۔۔۔ تو یہ لوگ کہتے ہیں کہ تم یہ قرآن خود گھڑتے ہو۔ اصل بات یہ ہے کہ اِن میں سے اکثر لوگ حقیقت سے ناواقف ہیں');
INSERT INTO "ur_maududi" VALUES(16,102,'16:102','اِن سے کہو کہ اسے تو روُح القُدس نے ٹھیک ٹھیک میرے ربّ کی طرف سے بتدریج نازل کیا ہے تاکہ ایمان لانے والوں کے ایمان کو پختہ کرے اور فرماں برداروں کو زندگی کے معاملات میں سیدھی راہ بتائےاور انہیں فلاح و سعادت کی خوشخبری دے۔ ');
INSERT INTO "ur_maududi" VALUES(16,103,'16:103','ہمیں معلوم ہے یہ لوگ تمہارے متعلق کہتے ہیں کہ اِس شخص کو ایک آدمی سِکھاتا پڑھاتا ہے۔ حالانکہ اُن کا اشارہ جس آدمی کی طرف ہے اُس کی زبان عجمی ہے اور یہ صاف عربی زبان ہے');
INSERT INTO "ur_maududi" VALUES(16,104,'16:104','حقیقت یہ ہے کہ جو لوگ اللہ کی آیات کو نہیں مانتے اللہ کبھی ان کو صحیح بات تک پہنچنے کی توفیق نہیں دیتا اور ایسے لوگوں کے لیے دردناک عذاب ہے');
INSERT INTO "ur_maududi" VALUES(16,105,'16:105','(جھوٹی باتیں نبی نہیں گھڑتا بلکہ)جھُوٹ وہ لوگ گھڑ رہے ہیں جو اللہ کی آیات کو نہیں مانتے، وہی حقیقت میں جھوٹے ہیں');
INSERT INTO "ur_maududi" VALUES(16,106,'16:106','جو شخص ایمان لانے کے بعد کُفر کرے (وہ اگر)مجبور کیا گیا ہو اور دل اُس کا ایمان پر مطمئن ہو (تب تو خیر)مگر جس نے دل کی رضا مندی سے کُفر کو قبول کر لیا اس پر اللہ کا غضب ہے اور ایسے سب لوگوں کے لیے بڑا عذاب ہے۔ ');
INSERT INTO "ur_maududi" VALUES(16,107,'16:107','یہ اس لیے کہ اُنہوں نے آخرت کے مقابلہ میں دنیا کی زندگی کو پسند کر لیا، اور اللہ کا قاعدہ ہے کہ وہ اُن لوگوں کو راہ نجات نہیں دکھاتا جو اُس کی نعمت کا کفران کریں');
INSERT INTO "ur_maududi" VALUES(16,108,'16:108','یہ وہ لوگ ہیں جن کے دلوں اور کانوں اور آنکھوں پر اللہ نے مہر لگا دی ہے یہ غفلت میں ڈوب چکے ہیں');
INSERT INTO "ur_maududi" VALUES(16,109,'16:109','ضرور ہے کہ آخرت میں یہی خسارے میں رہیں۔ ');
INSERT INTO "ur_maududi" VALUES(16,110,'16:110','بخلاف اس کے جِن لوگوں کا حال یہ ہے کہ جب(ایمان لانے کی وجہ سے)وہ ستائے گئے تو اُنہوں نے گھر بار چھوڑ دیے، ہجرت کی، راہِ خدا میں سختیاں جھیلیں اور صبر سے کام لیا، اُن کےلیے یقیناً تیرا ربّ غفور و رحیم ہے');
INSERT INTO "ur_maududi" VALUES(16,111,'16:111','(اِن سب کا فیصلہ اُس دن ہوگا) جب کہ ہر متنفس اپنے ہی بچاؤ کی فکر میں لگا ہوا ہوگا اور ہر ایک کو اس کے کیے کا بدلہ پورا پورا دیا جائے گا اور کسی پر ذرہ برابر ظلم نہ ہونے پائے گا');
INSERT INTO "ur_maududi" VALUES(16,112,'16:112','اللہ ایک بستی کی مثال دیتا ہے وہ امن و اطمینان کی زندگی بسر کر رہی تھی اور ہر طرف سے اس کو بفراغت رزق پہنچ رہا تھا کہ اُس نے اللہ کی نعمتوں کا کفران شروع کر دیا تب اللہ نے اس کے باشندوں کو اُن کے کرتوتوں کا یہ مزا چکھایا کہ بھوک اور خوف کی مصیبتیں ان پر چھا گئیں');
INSERT INTO "ur_maududi" VALUES(16,113,'16:113','اُن کے پاس اُن کی اپنی قوم میں سے ایک رسُول آیا۔ مگر اُنہوں نے اس کو جھُٹلادیا۔ آخرِ کار عذاب نے اُن کو آلیا جبکہ وہ ظالم ہو چکے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(16,114,'16:114','پس اے لوگو، اللہ نے جو کچھ حلال اور پاک رزق تم کو بخشا ہے اُسے کھاوٴ اور اللہ کے احسان کا شکر ادا کرو اگر تم واقعی اُسی کی بندگی کرنے والے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(16,115,'16:115','اللہ نے جو کچھ تم پر حرام کیا ہے وہ ہے مُردار اور خُون اور سُوٴر کا گوشت اور وہ جانور جس پر اللہ کے سوا کسی اور کا نام لیا گیا ہو۔ البتہ بھُوک سے مجبور ہو کر اگر کوئی اِن چیزوں کو کھالے، بغیر اس کے کہ وہ قانونِ الٰہی کی خلاف ورزی کا خواہش مند ہو، یا حدِّ ضرورت سے تجاوز کا مرتکب ہو ، تو یقیناً اللہ معاف کرنے اور رحم فرمانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(16,116,'16:116','اور یہ جو تمہاری زبانیں جھُوٹے احکام لگایا کرتی ہیں کہ یہ چیز حلال ہے اور وہ حرام، تو اس طرح کے حکم لگا کر اللہ پر جھُوٹ نہ باندھا کرو۔ جو لوگ اللہ پر جھُوٹے افترا باندھتے ہیں وہ ہر گز فلاح نہیں پایا کرتے');
INSERT INTO "ur_maududi" VALUES(16,117,'16:117','دنیا کا عیش چند روزہ ہے آخرکار اُن کے لیے دردناک سزا ہے');
INSERT INTO "ur_maududi" VALUES(16,118,'16:118',' وہ چیزیں ہم نے خاص طور پر یہودیوں کے لیے حرام کی تھیں جن کا ذکر ہم اس سے پہلے تم سے کر چکے ہیں۔ اور یہ اُن پر ہمارا ظلم نہ تھا بلکہ اُن کا اپنا ہی ظلم تھا جو وہ اپنے اُوپر کر رہے تھے');
INSERT INTO "ur_maududi" VALUES(16,119,'16:119','البتہ جن لوگوں نے جہالت کی بنا پر برا عمل کیا اور پھر توبہ کر کے اپنے عمل کی اصلاح کر لی تو یقیناً توبہ و اصلاح کے بعد تیرا رب اُن کے لیے غفور اور رحیم ہے');
INSERT INTO "ur_maududi" VALUES(16,120,'16:120','واقعہ یہ ہے کہ ابراہیم ؑ اپنی ذات سے ایک پوری اُمّت تھا، اللہ کا مطیعِ فرمان اور یکسُو۔ وہ کبھی مشرک نہ تھا');
INSERT INTO "ur_maududi" VALUES(16,121,'16:121','اللہ کی نعمتوں کا شکر ادا کرنے والا تھا اللہ نے اس کو منتخب کر لیا اور سیدھا راستہ دکھایا');
INSERT INTO "ur_maududi" VALUES(16,122,'16:122','دنیا میں اس کو بھلائی دی اور آخرت میں وہ یقیناً صالحین میں سے ہوگا');
INSERT INTO "ur_maududi" VALUES(16,123,'16:123','پھر ہم نے تمہاری طرف یہ وحی بھیجی کہ یکسُو ہو کر ابراہیم ؑ کے طریقے پر چلو اور وہ مشرکوں میں سے نہ تھا۔ ');
INSERT INTO "ur_maududi" VALUES(16,124,'16:124','رہا سَبت، تو وہ ہم نے اُن لوگوں پر مسلّط کیا تھا جنہوں نے اس کے احکام میں اختلاف کیا، اور یقیناً تیرا ربّ قیامت کے روز ان سب باتوں کا فیصلہ کر دے گا جن میں وہ اختلاف کرتے رہے ہیں');
INSERT INTO "ur_maududi" VALUES(16,125,'16:125','اے نبی ؐ ، اپنے ربّ کے راستے کی طرف دعوت دو حکمت اور عمدہ نصیحت کے ساتھ، اور لوگوں سےمباحثہ کرو ایسے طریقہ پر جو بہترین ہو۔ تمہارا ربّ ہی زیادہ بہتر جانتا ہے کہ کون اُس کی راہ سے بھٹکا ہوا ہے اور کون راہِ راست پر ہے');
INSERT INTO "ur_maududi" VALUES(16,126,'16:126','اور اگر تم لوگ بدلہ لو تو بس اسی قدر لے لو جس قدر تم پر زیادتی کی گئی ہو لیکن اگر تم صبر کرو تو یقیناً یہ صبر کرنے والوں ہی کے حق میں بہتر ہے');
INSERT INTO "ur_maududi" VALUES(16,127,'16:127','اے محمدؐ، صبر سے کام کیے جاؤ اور تمہارا یہ صبر اللہ ہی کی توفیق سے ہے اِن لوگوں کی حرکات پر ر نج نہ کرو اور نہ ان کی چال بازیوں پر دل تنگ ہو');
INSERT INTO "ur_maududi" VALUES(16,128,'16:128','اللہ اُن لوگوں کے ساتھ ہے جو تقوٰی سے کام لیتے ہیں اور احسان پر عمل کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(17,1,'17:1','پاک ہے وہ جو لے گیا ایک رات اپنے بندے کو مسجد حرام سے دُور کی اُس مسجد تک جس کے ماحول کو اس نے برکت دی ہے، تاکہ اسے اپنی کچھ نشانیوں کا مشاہدہ کرائے۔حقیقت میں وہی ہے سب کچھ سُننے اور دیکھنے والا۔');
INSERT INTO "ur_maududi" VALUES(17,2,'17:2','م نے اِس سے پہلے موسیٰ کو کتاب دی تھی اور اُسے بنی اسرائیل کے لیے ذریعہٴ ہدایت بنایا تھا، اِس تاکید کے ساتھ کہ میرے سوا کسی کو اپنا وکیل نہ بنانا۔ ');
INSERT INTO "ur_maududi" VALUES(17,3,'17:3','تم اُن لوگوں کی اولاد ہو جنہیں ہم نے نُوح ؑ کے ساتھ کشتی پر سوار کیا تھا، اور نوحؑ ایک شکر گزار بندہ تھا');
INSERT INTO "ur_maududi" VALUES(17,4,'17:4','پھر ہم نے اپنی کتاب میں بنی اسرائیل کو اِس بات پر بھی متنبہ کر دیا تھا کہ تم دو مرتبہ زمین میں فسادِ عظیم برپا کرو گے اور بڑی سرکشی دکھاوٴ گے۔ ');
INSERT INTO "ur_maududi" VALUES(17,5,'17:5','آخرِ کار جب اُن میں سے پہلی سرکشی کا موقع پیش آیا، تو اے بنی اسرائیل، ہم نے تمہارے مقابلے پر اپنے ایسے بندے اُٹھائے جو نہایت زور آور تھے اور وہ تمہارے ملک میں گھُس کر ہر طرف پھیل گئے۔ یہ ایک وعدہ تھا جسے پُورا ہو کر ہی رہنا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(17,6,'17:6','اِس کے بعد ہم نے تمہیں اُن پر غلبے کا موقع دے دیا اور تمہیں مال اور اولاد سے مدد دی اور تمہاری تعداد پہلے سے بڑھا دی۔ ');
INSERT INTO "ur_maududi" VALUES(17,7,'17:7','دیکھو! تم نے بھلائی کی تو وہ تمہارے اپنے ہی لیے بھلائی تھی، اور بُرائی کی تو وہ تمہاری اپنی ذات کے لیے بُرائی ثابت ہوئی۔ پھر جب دُوسرے وعدے کا وقت آیا تو ہم نے دُوسرے دشمنوں کو تم پر مسلّط کیا تا کہ وہ تمہارے چہرے بگاڑ دیں اور مسجد (بیت المَقدِس)میں اُسی طرح گھُس جائیں جس طرح پہلے دُشمن گھُسے تھے اور جس چیز پر ان کا ہاتھ پڑے اُسے تباہ کر کے رکھ دیں ');
INSERT INTO "ur_maududi" VALUES(17,8,'17:8','ہو سکتا ہے کہ اب تمہارا ربّ تم پر رحم کرے، لیکن اگر تم نے پھر اپنی سابق روش کا اعادہ کیا تو ہم بھی پھر اپنی سزا کا اعادہ کریں گے، اور کافرِ نعمت لوگوں کے لیے ہم ے جہنّم کو قید خانہ بنا رکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,9,'17:9','حقیقت یہ ہے کہ یہ قرآن وہ راہ دکھاتا ہے جو بالکل سیدھی ہے جو لو گ اسے مان کر بھلے کام کرنے لگیں انہیں یہ بشارت دیتا ہے کہ ان کے لیے بڑا اجر ہے');
INSERT INTO "ur_maududi" VALUES(17,10,'17:10','اور جولوگ آخرت کو نہ مانیں انہیں یہ خبر دیتا ہے کہ ان کے لیے ہم نے دردناک عذاب مہیّا کر رکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,11,'17:11','انسان شر اُس طرح مانگتا ہے جس طرح خیر مانگنی چاہیے۔ انسان بڑا ہی جلد باز واقع ہوا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,12,'17:12','دیکھو، ہم نے رات اور دن کو دو نشانیاں بنایا ہے۔ رات کی نشانی کو ہم نے بے نُور بنایا، اور دن کی نشانی کو روشن کر دیا تاکہ تم اپنے ربّ کا فضل تلاش کر سکو اور ماہ و سال کا حساب معلوم کر سکو۔ اِسی طرح ہم نے ہر چیز کو الگ الگ ممیَّز کر کے رکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,13,'17:13','ہر انسان کا شگون ہم نے اُس کے اپنے گلےمیں لٹکا رکھا ہے، اور قیامت کے روز ہم ایک نوشتہ اُس کے لیے نکالیں گے جسے وہ کھُلی کتاب کی طرح پائے گا');
INSERT INTO "ur_maududi" VALUES(17,14,'17:14','پڑھ اپنا نامہ اعمال، آج اپنا حساب لگانے کے لیے تو خود ہی کافی ہے');
INSERT INTO "ur_maududi" VALUES(17,15,'17:15','جو کوئی راہِ راست اختیار کرے اس کی راست روی اُس کے اپنے ہی لیے مفید ہے، اور جو گُمراہ ہو اُس کی گمراہی کا وبال اُسی پر ہے۔ کوئی بوجھ اُٹھانے والا دُوسرے کا بوجھ نہ اُٹھائے گا۔ اور ہم عذاب دینے والے نہیں ہیں جب تک کہ (لوگوں کو حق و باطل کا فرق سمجھانے کے لیے)ایک پیغام بر نہ بھیج دیں۔ ');
INSERT INTO "ur_maududi" VALUES(17,16,'17:16','جب ہم کسی بستی کو ہلاک کرنے کا ارادہ کرتے ہیں تو اس کے خوشحال لوگوں کو حکم دیتے ہیں اور وہ اس میں نافرمانیاں کرنے لگتے ہیں، تب عذاب کا فیصلہ اس بستی پر چسپاں ہو جاتا ہے اور ہم اسے برباد کر کے رکھ دیتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(17,17,'17:17','دیکھ لو، کتنی ہی نسلیں ہیں جو نوحؑ کے بعد ہمارے حکم سے ہلاک ہوئیں تیرا رب اپنے بندوں کے گناہوں سے پوری طرح باخبر ہے اور سب کچھ دیکھ رہا ہے');
INSERT INTO "ur_maududi" VALUES(17,18,'17:18','جو کوئی عاجلہ کا خواہشمند ہو، اسے یہیں ہم دے دیتے ہیں جو کچھ بھی جسے دینا چاہیں، پھر اس کے مقسُوم میں جہنّم لکھ دیتے ہیں جسے وہ تاپے گا ملامت زدہ اور رحمت سے محرُوم ہو کر۔ ');
INSERT INTO "ur_maududi" VALUES(17,19,'17:19','اور جو آخرت کا خواہشمند ہو اور اس کے لیے سعی کرے جیسی کہ اس کے لیے سعی کرنی چاہیے، اور ہو وہ مومن، تو ایسے ہر شخص کی سعی مشکور ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(17,20,'17:20','اِن کو بھی اور اُن کو بھی ، دونوں فریقوں کو ہم (دُنیا میں)سامانِ زیست دیے جارہے ہیں، یہ تیرے ربّ کا عطیہ ہے، اور تیرے ربّ کی عطا کو روکنے والا کوئی نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,21,'17:21','مگر دیکھ لو، دُنیا ہی میں ہم نے ایک گروہ کو دُوسرے پر کیسی فضیلت دے رکھی ہے، اور آخرت میں اُس کے درجے اور بھی زیادہ ہوں گے، اور اس کی فضیلت اور بھی زیادہ بڑھ چڑھ کر ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(17,22,'17:22','تُو اللہ کے ساتھ کوئی دُوسرا معبود نہ بنا ورنہ ملامت زدہ اور بے یار و مددگار بیٹھا رہ جائے گا۔');
INSERT INTO "ur_maududi" VALUES(17,23,'17:23','تیرے ربّ نے فیصلہ کر دیا ہے کہ:(۱)تم لوگ کسی کی عبادت نہ کرو، مگر صرف اُس کی۔ (۲) والدین کے ساتھ نیک سلوک کرو۔ اگر تمہارے پاس اُن میں سے کوئی ایک یا دونوں بُوڑھے ہو کر رہیں تو انہیں اُف تک نہ کہو، نہ انہیں جِھڑک کر جواب دو، بلکہ ان سے احترام کے ساتھ بات کرو');
INSERT INTO "ur_maududi" VALUES(17,24,'17:24','اور نرمی و رحم کے ساتھ ان کے سامنے جھک کر رہو، اور دعا کیا کرو کہ "پروردگار، ان پر رحم فرما جس طرح اِنہوں نے رحمت و شفقت کے ساتھ مجھے بچپن میں پالا تھا"');
INSERT INTO "ur_maududi" VALUES(17,25,'17:25','تمہارا ربّ خُوب جانتا ہے کہ تمہارےدِلوں میں کیا ہے۔ اگر تم صالح بن کر رہو تو وہ ایسے سب لوگوں کے لیے درگزر کرنے والا ہے جو اپنے قصُور پر متنبِہّ ہو کر بندگی کے رویّے کی طرف پلٹ آئیں۔ ');
INSERT INTO "ur_maududi" VALUES(17,26,'17:26','رشتہ دار کو اس کا حق دو اور مسکین اور مسافر کو اس کا حق فضول خرچی نہ کرو');
INSERT INTO "ur_maududi" VALUES(17,27,'17:27','فضول خرچ لوگ شیطان کے بھائی ہیں، اور شیطان اپنے رب کا ناشکرا ہے');
INSERT INTO "ur_maududi" VALUES(17,28,'17:28','اگر اُن سے (یعنی حاجت مند رشتہ داروں ، مسکینوں اور مسافروں سے)تمہیں کترانا ہو، اس بنا پر کہ ابھی تم اللہ کی اُس رحمت کو جس کے تم اُمیدوار ہو تلاش کر رہے ہو، تو انہیں نرم جواب دے دو۔ ');
INSERT INTO "ur_maududi" VALUES(17,29,'17:29','نہ تو اپنا ہاتھ گردن سے باندھ رکھو اور نہ اسے بالکل ہی کُھلا چھوڑ دو کہ ملامت زدہ اور عاجز بن کر رہ جاوٴ۔ ');
INSERT INTO "ur_maududi" VALUES(17,30,'17:30','تیرا ربّ جس کے لیے چاہتا ہے رزق کشادہ کرتا ہے اور جس کے لیے چاہتا ہے تنگ کر دیتا ہے۔ وہ اپنے بندوں کے حال سے باخبر ہے اور انہیں دیکھ رہا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,31,'17:31','اپنی اولاد کو افلاس کے اندیشے سے قتل نہ کرو۔ ہم انہیں بھی رزق دیں گے اور تمہیں بھی۔ درحقیقت اُن کا قتل ایک بڑی خطا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,32,'17:32','زنا کے قریب نہ پھٹکو، وہ بہت بُرا فعل اور بڑا ہی بُرا راستہ۔ ');
INSERT INTO "ur_maududi" VALUES(17,33,'17:33','قتلِ نفس کا ارتکاب نہ کرو جسے اللہ نے حرام کیا ہے مگر حق کے ساتھ۔ اور جو شخص مظلومانہ قتل کیا گیا ہو اس کے ولی کو ہم نے قصاص کے مطالبے کا حق عطا کیا ہے، پس چاہیے کے وہ قتل میں حد سے نہ گزرے، اُس کی مدد کی جائے گی ');
INSERT INTO "ur_maududi" VALUES(17,34,'17:34','مالِ یتیم کے پاس نہ پھٹکو مگر احسن طریقے سے، یہاں تک کہ وہ اپنے شباب کو پہنچ جائے۔ عہد کی پابندی کرو، بے شک عہد کے بارے میں تم کو جواب دہی کرنی ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(17,35,'17:35','پیمانے سے دو تو پُورا بھر کر دو، اور تولو تو ٹھیک ترازو سے تولو۔ یہ اچھا طریقہ ہے اور ہلحاظِ انجام بھی یہی بہتر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,36,'17:36','کسی ایسی چیز کے پیچھے نہ لگو جس کا تمہیں علم نہ ہو، یقیناً آنکھ، کان اور دل سب ہی کی باز پرس ہونی ہے ');
INSERT INTO "ur_maududi" VALUES(17,37,'17:37','زمین میں اکڑ کر نہ چلو، تم نہ زمین کو پھاڑ سکتے ہو، نہ پہاڑوں کی بلندی کو پہنچ سکتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(17,38,'17:38','اِن امور میں سے ہر ایک کا بُرا پہلو تیرے ربّ کے نزدیک ناپسندیدہ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,39,'17:39','یہ وہ حکمت کی باتیں ہیں جو تیرے ربّ نے تجھ پر وحی کی ہیں۔اور دیکھ! اللہ کے ساتھ کو ئی دوسرا معبود نہ بنا بیٹھ ورنہ تُو جہنّم میں ڈال دیا جائے گا ملامت زدہ اور ہر بھلائی سے محروم ہو کر ');
INSERT INTO "ur_maududi" VALUES(17,40,'17:40','کیسی عجیب بات ہے کہ تمہارے ربّ نے تمہیں تو بیٹوں سے نوازا اور خود اپنے لیے ملائکہ کو بیٹیاں بنا لیا؟ بڑی جھوٹی بات ہے جو تم لوگ زبانوں سے نکالتے ہو');
INSERT INTO "ur_maududi" VALUES(17,41,'17:41','ہم نے اِس قرآن میں طرح طرح سے لوگوں کو سمجھایا کہ ہوش میں آئیں، مگر وہ حق سے اور زیادہ دور ہی بھاگے جا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(17,42,'17:42','اے محمد ؐ ، ان سے کہو کہ اگر اللہ کے ساتھ دوسرے خدا بھی ہوتے، جیسا کہ یہ لوگ کہتے ہیں، تو وہ مالکِ عرش کے مقام پر پہنچنے کی ضرور کوشش کرتے۔ ');
INSERT INTO "ur_maududi" VALUES(17,43,'17:43','پاک ہے وہ اور بہت بالا و برتر ہے اُن باتوں سے جو یہ لوگ کہہ رہے ہیں');
INSERT INTO "ur_maududi" VALUES(17,44,'17:44','اُس کی پاکی تو ساتوں آسمان اور زمین اور وہ ساری چیزیں بیان کر رہی ہیں جو آسمان و زمین میں ہیں۔ کوئی چیز ایسی نہیں جو اس کی حمد کے ساتھ اس کی تسبیح نہ کر رہی ہو ، مگر تم ان کی تسبیح سمجھتے نہیں ہو۔ حقیقت یہ ہے کہ وہ بڑا ہی بُردبار اور درگزر کرنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,45,'17:45','جب تم قرآن پڑھتے ہو تو ہم تمہارے اور آخرت پر ایمان نہ لانے والوں کے درمیان ایک پردہ حائل کر دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(17,46,'17:46','اور ان کے دلوں پر ایسا غلاف چڑھادیتے ہیں کہ وہ کچھ نہیں سمجھتے، اور ان کے کانوں میں گرانی پیدا کر دیتے ہیں۔ اور جب تم قرآن میں اپنے ایک ہی ربّ کا ذکر کرتے ہو تو وہ نفرت سے منہ موڑ لیتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(17,47,'17:47','ہمیں معلوم ہے کہ جب وہ کان لگا کر تمہاری بات سُنتے ہیں تو دراصل کیا سُنتے ہیں، اور جب بیٹھ کر باہم سرگوشیاں کرتے ہیں تو کیا کہتے ہیں۔ یہ ظالم آپس میں کہتے ہیں کہ یہ تو ایک سحر زدہ آدمی ہے جس کے پیچھے تم لوگ جارہے ہو ');
INSERT INTO "ur_maududi" VALUES(17,48,'17:48','دیکھو، کیسی باتیں ہیں جو یہ لوگ تم پر چھانٹتے ہیں۔ یہ بھٹک گئے ہیں۔ اِنہیں راستہ نہیں ملتا۔ ');
INSERT INTO "ur_maududi" VALUES(17,49,'17:49','وہ کہتے ہیں "جب ہم صرف ہڈیاں اور خاک ہو کر رہ جائیں گے تو کیا ہم نئے سرے سے پیدا کر کے اٹھائے جائیں گے؟"');
INSERT INTO "ur_maududi" VALUES(17,50,'17:50','ان سے کہو "تم پتھر یا لوہا بھی ہو جاؤ');
INSERT INTO "ur_maududi" VALUES(17,51,'17:51','یا اس سے بھی زیادہ سخت کوئی چیز جو تمہارے ذہن میں قبولِ حیات سے بعید تر ہو“(پھر بھی تم اُٹھ کر رہو گے)۔ وہ ضرور پوچھیں گے”کون ہے وہ جو ہمیں پھر زندگی کی طرف پلٹا کر لائے گا؟“ جواب میں کہو”وہی جس نے پہلی بار تم کو پیدا کیا۔“ وہ سر ہِلا ہِلا کر پوچھیں گے”اچھا، تو یہ ہوگا کب؟“ تم کہو”کیا عجب ، وہ وقت قریب ہی آلگا ہو');
INSERT INTO "ur_maududi" VALUES(17,52,'17:52','جس روز وہ تمہیں پکارے گا تو تم اس کی حمد کرتے ہوئے اس کی پکار کے جواب میں نکل آوٴ گے اور تمہارا گمان اُس وقت یہ ہوگا کہ ہم بس تھوڑی دیر ہی اِس حالت میں پڑے رہے ہیں۔ “');
INSERT INTO "ur_maududi" VALUES(17,53,'17:53','اور اے محمد ؐ ، میرے بندوں سے کہہ دو کہ زبان سے وہ بات نکالا کریں جو بہترین ہو۔ دراصل یہ شیطان ہے جو انسانوں کے درمیان فساد ڈلوانے کی کوشش کرتا ہے۔ حقیقت یہ ہے کہ شیطان انسان کا کُھلا دشمن ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,54,'17:54','تمہارا ربّ تمہارےحال سے زیادہ واقف ہے ، وہ چاہے تو تم پر رحم کرے اور چاہے تو تمہیں عذاب دے دے۔ اور اے نبی ؐ ، ہم نے تم کو لوگوں پر حوالہ دار بنا کر نہیں بھیجا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,55,'17:55','تیرا ربّ زمین اور آسمانوں کی مخلوقات کو زیادہ جانتا ہے۔ ہم نے بعض پیغمبروں کو بعض سے بڑھ کر مرتبے دیے ، اور ہم نے ہی داوٴد ؑ کو زَبور دی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(17,56,'17:56','اِن سے کہو، پکار دیکھو اُن معبودوں کو جن کو تم خدا کے سوا (اپنا کارساز)سمجھتے ہو، وہ کسی تکلیف کو تم سے نہ ہٹا سکتے ہیں نہ بدل سکتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(17,57,'17:57','جن کو یہ لوگ پکارتے ہیں وہ تو خود اپنے ربّ کے حضور رسائی حاصل کرنے کا وسیلہ تلاش کر رہے ہیں کہ کون اُس سے قریب تر ہو جائے اور وہ اُس کی رحمت کے اُمیدوار اور اُس کے عذاب سے خائف ہیں۔ حقیقت یہ ہے کہ تیرے ربّ کا عذاب ہے ہی ڈرنے کے لائق');
INSERT INTO "ur_maududi" VALUES(17,58,'17:58','اور کوئی بستی ایسی نہیں ہے جسے ہم قیامت سے پہلے ہلاک نہ کریں یا سخت عذاب نہ دیں۔ یہ نوشتہء الٰہی میں لکھا ہوا ہے');
INSERT INTO "ur_maududi" VALUES(17,59,'17:59','اور ہم کو نشانیاں بھیجنے سے نہیں روکا مگر اس بات نے کہ اِن سے پہلے کے لوگ اُن کو جُھٹلا چکے ہیں۔ (چنانچہ دیکھ لو)ثمود کو ہم علانیہ اُونٹنی لا کر دی اور اُنہوں نے اُس پر ظلم کیا۔ ہم نشانیاں اسی لیے تو بھیجتے ہیں کہ لوگ انہیں دیکھ کر ڈریں۔ ');
INSERT INTO "ur_maududi" VALUES(17,60,'17:60','یاد کرو اے محمد ؐ ، ہم نے تم سے کہہ دیا تھا کہ تیرے ربّ نے اِن لوگوں کو گھیر رکھا ہے۔ اور یہ جو کچھ ابھی ہم نے تمہیں دکھایا ہے ، اِس کو اور اُس درخت کو جس پر قرآن میں لعنت کی گئی ہے ، ہم نے اِن لوگوں کے لیے بس ایک فتنہ بنا کر رکھ دیا۔ ہم اِنہیں تنبیہ پر تنبیہ کیے جا رہے ہیں، مگر ہر تنبیہ اِن کی سرکشی ہی میں اضافہ کیے جاتی ہے');
INSERT INTO "ur_maududi" VALUES(17,61,'17:61','اور یاد کرو جبکہ ہم نے ملائکہ سے کہا کہ آدم کو سجدہ کرو، تو سب نے سجدہ کیا، مگر ابلیس نے نہ کیا۔ اس نے کہا”کیا میں اُس کو سجدہ کروں جسے تُو نے مٹی سے بنایا ہے؟“');
INSERT INTO "ur_maududi" VALUES(17,62,'17:62','پھر وہ بولا”دیکھ تو سہی، کیا یہ اس قابل تھا کہ تُو نے اِسے مجھ پر فضیلت دی؟ اگر تُو مجھے قیامت کے دن تک مہلت دے تو میں اس کی پُوری نسل کی بیخ کَنی کر ڈالوں ، بس تھوڑے ہی لوگ مجھ سے بچ سکیں گے۔“');
INSERT INTO "ur_maududi" VALUES(17,63,'17:63','اللہ تعالیٰ نے فرمایا "اچھا تو جا، ان میں سے جو بھی تیری پیروی کریں، تجھ سمیت اُن سب کے لیے جہنم ہی بھرپور جزا ہے');
INSERT INTO "ur_maududi" VALUES(17,64,'17:64','تُو جس جس کو اپنی دعوت سے پھِسلا سکتا ہے پِھسلا لے ، ان پر اپنے سوار اور پیادے چڑھا لا، مال اور اولاد میں ان کے ساتھ سا جھا لگا، اور ان کو وعدوں کے جال میں پھانس ۔۔۔۔ اور شیطان کے وعدے ایک دھوکے کے سوا اور کچھ بھی نہیں');
INSERT INTO "ur_maududi" VALUES(17,65,'17:65','یقیناً میرے بندوں پر تجھے کوئی اقتدار حاصل نہ ہوگا، اور توکّل کے لیے تیرا ربّ کافی ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(17,66,'17:66','تمہارا (حقیقی)ربّ تو وہ ہے جو سمندر میں تمہاری کشتی چلاتا ہے تا کہ تم اس کا فضل تلاش کرو۔ حقیقت یہ ہے کہ وہ تمہارے حال پر نہایت مہربان ہے');
INSERT INTO "ur_maududi" VALUES(17,67,'17:67','جب سمندر میں تم پر مصیبت آتی ہے تو اُس ایک کے سوا دُوسرے جن جن کو تم پکارا کرتے ہو وہ سب گُم ہوجاتے ہیں ، مگر جب وہ تم کو بچا کر خشکی پر پہنچا دیتا ہے تو تم اُس سے منہ موڑ جاتے ہو۔ انسان واقعی بڑا ناشکرا ہے');
INSERT INTO "ur_maududi" VALUES(17,68,'17:68','اچھا، تو کیا تم اِس بات سے بالکل بے خوف ہو کہ خدا کبھی خشکی پر ہی تم کو زمین میں دھنسا دے، یا تم پر پتھراؤ کرنے والی آندھی بھیج دے اور تم اس سے بچانے والا کوئی حمایتی نہ پاؤ؟');
INSERT INTO "ur_maududi" VALUES(17,69,'17:69','اور کیا تمہیں اِس کا کوئی اندیشہ نہیں کہ خدا پھر کسی وقت سمندر میں تم کو لے جائے اور تمہاری ناشکری کے بدلے تم پر سخت طوفانی ہوا بھیج کر تمہیں غرق کر دے اور تم کو ایسا کوئی نہ ملے جو اُس سے تمہارے اِس انجام کی پوچھ گچھ کرسکے؟');
INSERT INTO "ur_maududi" VALUES(17,70,'17:70','یہ تو ہماری عنایت ہے کہ ہم نے بنی آدم کو بزرگی دی اور انہیں خشکی و تری میں سواریاں عطا کیں اور ان کو پاکیزہ چیزوں سے رزق دیااور اپنی بہت سی مخلوقات پر نمایاں فوقیت بخشی۔ ');
INSERT INTO "ur_maududi" VALUES(17,71,'17:71','پھر خیال کرو اس دن کا جب کہ ہم ہر انسانی گروہ کو اس کے پیشوا کے ساتھ بلائیں گے۔ اُس وقت جن لوگوں کو ان کا نامہٴ اعمال سیدھے ہاتھ میں دیا گیا وہ اپنا کارنامہ پڑھیں گے اور ان پر ذرّہ برابر ظلم نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(17,72,'17:72','اور جو اِس دنیا میں اندھا بن کر رہا وہ آخرت میں بھی اندھا ہی رہے گا بلکہ راستہ پانے میں اندھے سے بھی زیادہ ناکام');
INSERT INTO "ur_maududi" VALUES(17,73,'17:73','اے محمد ؐ ، ان لوگوں نے اِس کوشش میں کوئی کسر اُٹھا نہیں رکھی کہ تمہیں فتنے میں ڈال کر اُس وحی سے پھیر دیں جو ہم نے تمہاری طرف بھیجی ہے تاکہ تم ہمارے نام پر اپنی طرف سے کوئی بات گھڑو۔ اگر تم ایسا کرتے تو وہ ضرور تمہیں اپنا دوست بنالیتے');
INSERT INTO "ur_maududi" VALUES(17,74,'17:74','اور بعید نہ تھا کہ اگر ہم تمہیں مضبوط نہ رکھتے تو تم ان کی طرف کچھ نہ کچھ جھک جاتے');
INSERT INTO "ur_maududi" VALUES(17,75,'17:75','لیکن اگر تم ایسا کرتے تو ہم تمہیں دُنیا میں بھی دوہرے عذاب کا مزّہ چکھاتے اور آخرت میں بھی دوہرے عذاب کا، پھر ہمارےمقابلے میں تم کوئی مددگار نہ پاتے۔ ');
INSERT INTO "ur_maududi" VALUES(17,76,'17:76','اور یہ لوگ اس بات پر بھی تُلے رہے ہیں کہ تمہارے قدم اِس سرزمین سے اُکھاڑ دیں اور تمہیں یہاں سے نکال باہر کریں۔ لیکن اگر یہ ایسا کریں گے تو تمہارے بعد یہ خود یہاں کچھ زیادہ دیر نہ ٹھہر سکیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(17,77,'17:77','ہ ہمارا مستقل طریقِ کار ہے جو اُن سب رسُولوں کے معاملے میں ہم نے برتا ہے جنہیں تم سے پہلے ہم نے بھیجا تھا،اور ہمارے طریقِ کار میں تم کوئی تغیّر نہ پاوٴگے');
INSERT INTO "ur_maududi" VALUES(17,78,'17:78','نماز قائم کرو زوالِ آفتاب سے لے کر رات کے اندھیرے تک اور فجر کے قرآن کا بھی التزام کرو کیونکہ قرآنِ فجر مشہُود ہوتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,79,'17:79','اور رات کو تہجّد پڑھو، یہ تمہارے لیے نفل ہے ، بعید نہیں کہ تمہارا ربّ تمہیں مقامِ محمُود پر فائز کر دے');
INSERT INTO "ur_maududi" VALUES(17,80,'17:80','اور دُعا کرو کہ پروردگار ، مجھ کو جہاں بھی تُو لے جا سچائی کے ساتھ لے جا اور جہاں سے بھی نکال سچائی کے ساتھ نکال، اور اپنی طرف سے ایک اقتدار کو میرا مددگار بنادے۔ ');
INSERT INTO "ur_maududi" VALUES(17,81,'17:81','اور اعلان کر دو کہ”حق آگیا اور باطل مٹ گیا، باطل تو مٹنے ہی والا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(17,82,'17:82','ہم اِس قرآن کے سلسلہٴ تنزیل میں وہ کچھ نازل کر رہے ہیں جو ماننے والوں کے لیے تو شفا اور رحمت ہے، مگر ظالموں کےلیے خسارے کے سوا اور کسی چیز میں اضافہ نہیں کرتا۔ ');
INSERT INTO "ur_maududi" VALUES(17,83,'17:83','انسان کا حال یہ ہے کہ جب ہم اس کو نعمت عطا کرتے ہیں تو وہ اینٹھتا اور پیٹھ موڑ لیتا ہے، اور جب ذرا مصیبت سے دو چار ہوتا ہے تو مایوس ہونے لگتا ہے');
INSERT INTO "ur_maududi" VALUES(17,84,'17:84','اے نبیؐ، ان لوگوں سے کہہ دو کہ "ہر ایک اپنے طریقے پر عمل کر رہا ہے، اب یہ تمہارا رب ہی بہتر جانتا ہے کہ سیدھی راہ پر کون ہے"');
INSERT INTO "ur_maududi" VALUES(17,85,'17:85','یہ لوگ تم سے رُوح کے متعلق پُوچھتے ہیں۔ کہو”یہ رُوح میرے ربّ کے حکم سے آتی ہے ، مگر تم لوگوں نے علم سے کم ہی بہرہ پایا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(17,86,'17:86','اور اے محمدؐ، ہم چاہیں تو وہ سب کچھ تم سے چھین لیں جو ہم نے وحی کے ذریعہ سے تم کو عطا کیا ہے، پھر تم ہمارے مقابلے میں کوئی حمایتی نہ پاؤ گے جو اسے واپس دلا سکے');
INSERT INTO "ur_maududi" VALUES(17,87,'17:87','یہ تو جو کچھ تمہیں ملا ہے تمہارے ربّ کی رحمت سے مِلا ہے، حقیقت یہ ہے کہ اس کا فضل تم پر بہت بڑا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,88,'17:88','کہہ دو کہ اگر انسان اور جِن سب کے سب مل کر اِس قرآن جیسی کوئی چیزلانے کی کوشش کریں تو نہ لا سکیں گے، چاہے وہ سب ایک دوسرے کے مددگار ہی کیوں نہ ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(17,89,'17:89','ہم نے اس قرآن میں لوگوں کو طرح طرح سے سمجھایا مگر اکثر انکار ہی پر جمے رہے');
INSERT INTO "ur_maududi" VALUES(17,90,'17:90','اور انہوں نے کہا "ہم تیر ی بات نہ مانیں گے جب تک کہ تو ہمارے لیے زمین کو پھاڑ کر ایک چشمہ جاری نہ کر دے');
INSERT INTO "ur_maududi" VALUES(17,91,'17:91','یا تیرے لیے کھجوروں اور انگوروں کا ایک باغ پیدا ہو اور تو اس میں نہریں رواں کر دے');
INSERT INTO "ur_maududi" VALUES(17,92,'17:92','یا تو آسمانوں کو ٹکڑے ٹکڑے کر کے ہمارے اوپر گرا دے جیسا کہ تیرا دعویٰ ہے یا خدا اور فرشتوں کو رُو در رُو ہمارے سامنے لے آئے');
INSERT INTO "ur_maududi" VALUES(17,93,'17:93','یا تیرے لیے سونے کا ایک گھر بن جائے۔ یا تُو آسمان پر چڑھ جائے، اور تیرے چڑھنے کا بھی ہم یقین نہ کریں گے جب تک کہ تُو ہمارے اُوپر ایک ایسی تحریر نہ اُتار لائے جسے ہم پڑھیں“۔۔۔۔اے محمد ؐ ، اِن سے کہو”پاک ہے میرا پروردگار ! کیا میں ایک پیغام لانے والے انسان کے سوا اور بھی کچھ ہوں؟“ ');
INSERT INTO "ur_maududi" VALUES(17,94,'17:94','لوگوں کے سامنے جب کبھی ہدایت آئی تو اس پر ایمان لانے سے اُن کو کسی چیز نے نہیں روکا مگر اُن کے اِسی قول نے کہ”کیا اللہ نے بشر کو پیغمبر بنا کر بھیج دیا؟“ ');
INSERT INTO "ur_maududi" VALUES(17,95,'17:95','ن سے کہو اگر زمین میں فرشتے اطمینان سے چل پھر رہے ہوتے تو ہم ضرور آسمان سے کسی فرشتے ہی کو اُن کے لیے پیغمبر بنا کر بھیجتے۔ ');
INSERT INTO "ur_maududi" VALUES(17,96,'17:96','اے محمد ؐ ، ان سے کہہ دو کہ میرے اور تمہارے درمیان بس ایک اللہ کی گواہی کافی ہے۔ وہ اپنے بندوں کے حال سے باخبر ہے اور سب کچھ دیکھ رہا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,97,'17:97','جس کو اللہ ہدایت دے وہی ہدایت پانے والا ہے، اور جسے وہ گمراہی میں ڈال دے تو اس کے بعد ایسے لوگوں کے لیے تُو کوئی حامی و ناصر نہیں پاسکتا۔ ان لوگوں کو ہم قیامت کے روز اوندھے منہ کھینچ لائیں گے، اندھے، گُونگے اور بہرے۔ اُن کا ٹھکانا جہنّم ہے۔ جب کبھی اس کی آگ دھیمی ہونے لگے گی ہم اسے اور بھڑکا دیں گے');
INSERT INTO "ur_maududi" VALUES(17,98,'17:98','یہ بدلہ ہے ان کی اس حرکت کا کہ انہوں نے ہماری آیات کا انکار کیا اور کہا "کیا جب ہم صرف ہڈیاں اور خاک ہو کر رہ جائیں گے تو نئے سرے سے ہم کو پیدا کر کے اٹھا کھڑا کیا جائے گا؟"');
INSERT INTO "ur_maududi" VALUES(17,99,'17:99','کیا ان کو یہ نہ سوجھا کہ جس خدا نے زمین اور آسمانو ں کو پیدا کیا ہے وہ اِن جیسوں کو پیدا کرنے کی ضرور قدرت رکھتا ہے؟ اس نے اِن کے حشر کے لیے ایک وقت مقرر کر رکھا ہے جس کا آنا یقینی ہے، مگر ظالموں کو اصرار ہے کہ وہ اس کا انکار ہی کریں گے');
INSERT INTO "ur_maududi" VALUES(17,100,'17:100','اے محمد ؐ ، اِن سے کہو، اگر کہیں میرے ربّ کی رحمت کے خزانے تمہارے قبضے میں ہوتے تو تم خرچ ہو جانے کے اندیشے سے ضرور ان کو روک رکھتے۔ واقعی انسان بڑا تنگ دل واقع ہوا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,101,'17:101','ہم نے موسیٰؑ کو نو نشانیاں عطا کی تھیں جو صریح طور پر دکھائی دے رہی تھیں۔ اب یہ تم خود بنی اسرائیل سے پُوچھ لو کہ جب وہ سامنے آئیں تو فرعون نے یہی کہا تھا نہ کہ”اے موسیٰ ، میں سمجھتا ہوں کہ تُو ضرور ایک سحر زدہ آدمی ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(17,102,'17:102','موسیٰؑ نے اس کے جواب میں کہا”تُو خوب جانتا ہے کہ یہ بصیرت افروز نشانیاں ربّ السّماوات و الارض کے سوا کسی نے نازل نہیں کی ہیں ، اور میرا خیال یہ ہےکہ اے فرعون ، تُو ضرور ایک شامت زدہ آدمی ہے۔ “');
INSERT INTO "ur_maududi" VALUES(17,103,'17:103','آخرکار فرعون نے ارادہ کیا کہ موسیٰؑ اور بنی اسرائیل کو زمین سے اکھاڑ پھینکے، مگر ہم نے اس کو اوراس کے ساتھیوں کو اکٹھا غرق کر دیا');
INSERT INTO "ur_maududi" VALUES(17,104,'17:104','اور اس کے بعد بنی اسرائیل سے کہا کہ اب تم زمین میں بسو ، پھر جب آخرت کے وعدے کا وقت آن پُورا ہوگا تو ہم تم سب کو ایک ساتھ لا حاضر کریں گے');
INSERT INTO "ur_maududi" VALUES(17,105,'17:105','س قرآن کو ہم نے حق کے ساتھ نازل کیا ہے اور حق ہی کے ساتھ یہ نازل ہوا ہے، اور اے محمد ؐ ، تمہیں ہم نے اِس کے سوا اور کسی کام کے لیے نہیں بھیجا کہ (جو مان لے اسے)بشارت دے دو اور(جو نہ مانے اُسے)متنبّہ کر دو۔ ');
INSERT INTO "ur_maududi" VALUES(17,106,'17:106','اور اس قرآن کو ہم نے تھوڑا تھوڑا کر کے نازل کیا ہے تاکہ تم ٹھہر ٹھہر کر اسے لوگوں کو سُناوٴ، اور اسے ہم نے(موقع موقع سے)بتدریج اُتارا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,107,'17:107','اے محمدؐ ، اِن لوگوں سے کہہ دو کہ تم اسے مانویا نہ مانو، جن لوگوں کو اس سے پہلے علم دیا گیا ہے انہیں جب یہ سُنایا جاتا ہے تو وہ منہ کے بل سجدے میں گر جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(17,108,'17:108','ور پکار اُٹھتے ہیں”پاک ہے ہمارا ربّ، اُس کا وعدہ تو پورا ہونا ہی تھا۔“ ');
INSERT INTO "ur_maududi" VALUES(17,109,'17:109','اور وہ منہ کے بل روتے ہوئے گِر جاتے ہیں اور اسے سُن کو اُن کا خشوع اور بڑھ جاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(17,110,'17:110','اے نبیؐ ، اِن سے کہو، اللہ کہہ کر پُکارو یا رحمان کہہ کر، جس نام سے بھی پُکارو اُس کے لیے سب اچھے نام ہیں۔ اور اپنی نماز نہ بہت زیادہ بلند آواز سے پڑھو اور نہ بہت پست آواز سے، ان دونوں کے درمیان اوسط درجے کا لہجہ اختیار کرو۔ ');
INSERT INTO "ur_maududi" VALUES(17,111,'17:111','اور کہو”تعریف ہے اُس خدا کے لیے جس نے نہ کسی کو بیٹا بنایا، نہ کوئی بادشاہی میں اُس کا شریک ہے، اور نہ وہ عاجز ہے کہ کوئی اس کا پشتیبان ہو۔“ اور اُس کی بڑائی بیان کرو، کمال درجے کی بڑائی');
INSERT INTO "ur_maududi" VALUES(18,1,'18:1','تعریف اللہ کے لیے ہے جس نے اپنے بندے پر یہ کتاب نازل کی اور اس میں کوئی ٹیڑھ نہ رکھی۔ ');
INSERT INTO "ur_maududi" VALUES(18,2,'18:2','ٹھیک ٹھیک سیدھی بات کہنے والی کتاب، تاکہ وہ لوگوں کو خدا کے سخت عذاب سے خبردار کر دے، اور ایمان لا کر نیک عمل کرنے والوں کو خوشخبری دیدے کہ ان کے لیے اچھا اجر ہے');
INSERT INTO "ur_maududi" VALUES(18,3,'18:3','جس میں وہ ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(18,4,'18:4','اور اُن لوگوں کو ڈرا دے جو کہتے ہیں کہ اللہ نے کسی کو بیٹا بنایا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(18,5,'18:5','اِس بات کا نہ اُنھیں کوئی علم ہے اورنہ ان کے باپ دادا کو تھا۔ بڑی بات ہے جو ان کےمُنہ سے نکلتی ہے۔ وہ محض جھوٹ بکتے ہیں');
INSERT INTO "ur_maududi" VALUES(18,6,'18:6','اچھا، تو اے محمدؐ ،شاید تم ان کے پیچھے غم کے مارے اپنی جان کھو دینے والے ہو اگر یہ اِس تعلیم پر ایمان نہ لائے۔ ');
INSERT INTO "ur_maududi" VALUES(18,7,'18:7','واقعہ یہ ہے کہ یہ جو کچھ سر و سامان بھی زمین پر ہے اس کو ہم نے زمین کی زینت بنایا ہے تاکہ اِن لوگوں کو آزمائیں اِن میں کون بہتر عمل کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(18,8,'18:8','آخرِ کار اِس سب کو ہم ایک چٹیل میدان بنا دینے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(18,9,'18:9','کیا تم سمجھتے ہو کہ غار اور کتبے والے ہماری کوئی بڑی عجیب نشانیوں میں سے تھے؟ ');
INSERT INTO "ur_maududi" VALUES(18,10,'18:10','جب وہ چند نوجوان غار میں پناہ گزیں ہوئے اور انہوں نے کہا کہ "اے پروردگار، ہم کو اپنی رحمت خاص سے نواز اور ہمارا معاملہ درست کر دے،"');
INSERT INTO "ur_maududi" VALUES(18,11,'18:11','تو ہم نے انہیں اُسی غار میں تھپک کر سالہا سال کے لیے گہری نیند سلا دیا');
INSERT INTO "ur_maududi" VALUES(18,12,'18:12','پھر ہم نے انہیں اٹھایا تاکہ دیکھیں اُن کے دو گروہوں میں سے کون اپنی مدت قیام کا ٹھیک شمار کرتا ہے');
INSERT INTO "ur_maududi" VALUES(18,13,'18:13','ہم اِن کا اصل قصہ تم کو سُناتے ہیں۔ وہ چند نوجوان تھے جو اپنے ربّ پر ایمان لے آئے تھے اورہم نے ان کو ہدایت میں ترقی بخشی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(18,14,'18:14','ہم نے ان کے دل اُس وقت مضبوط کر دیے جب وہ اٹھے اور انہوں نے اعلان کر دیا کہ "ہمارا رب تو بس وہی ہے جو آسمانوں اور زمین کا رب ہے ہم اُسے چھوڑ کر کسی دوسرے معبود کو نہ پکاریں گے اگر ہم ایسا کریں تو بالکل بیجا بات کریں گے"');
INSERT INTO "ur_maududi" VALUES(18,15,'18:15','(پھر انہوں نے آپس میں ایک دوسرے سے کہا) "یہ ہماری قوم تو ربِّ کائنات کو چھوڑ کر دوسرے خدا بنا بیٹھی ہے یہ لوگ ان کے معبود ہونے پر کوئی واضح دلیل کیوں نہیں لاتے؟ آخر اُس شخص سے بڑا ظالم اور کون ہو سکتا ہے جو اللہ پر جھوٹ باندھے؟');
INSERT INTO "ur_maududi" VALUES(18,16,'18:16','اب جبکہ تم ان سے اور ان کے معبُودانِ غیراللہ سے بے تعلق ہو چکے ہو تو چلو اب فلاں غار میں چل کر پناہ لو۔ تمہارا ربّ تم پر اپنی رحمت کا دامن وسیع کرے گا اور تمہارے کام کےلیے سروسامان مہیّا کر دے گا۔“');
INSERT INTO "ur_maududi" VALUES(18,17,'18:17','تم انہیں غار میں دیکھتے تو تمہیں یُوں نظر آتا کہ سُورج جب نِکلتا ہے تو ان کے غار کو چھوڑ کر دائیں جانب چڑھ جاتا ہے اور جب غروب ہوتا ہے تو ان سے بچ کر بائیں جانب اُتر جاتا ہے اور وہ ہیں کہ غار کے اندر ایک وسیع جگہ میں پڑے ہیں۔ یہ اللہ کی نشانیوں میں سے ایک ہے۔ جس کو اللہ ہدایت دے وہی ہدایت پانے والا ہے اور جسے اللہ بھٹکا دے اس کے لیے تم کوئی ولیِّ مُرشد نہیں پاسکتے');
INSERT INTO "ur_maududi" VALUES(18,18,'18:18','تم انہیں دیکھ کر یہ سمجھتے کہ وہ جاگ رہے ہیں، حالانکہ وہ سو رہے تھے۔ ہم انہیں دائیں بائیں کروٹ دلواتے رہتے تھے۔ اور ان کا کُتا غار کے دہانے پر ہاتھ پھیلائے بیٹھا تھا۔ اگر تم کہیں جھانک کر اُنہیں دیکھتے تو اُلٹے پاوٴں بھاگ کھڑے ہوتے اور تم پر ان کے نظارے سے دہشت بیٹھ جاتی۔ ');
INSERT INTO "ur_maududi" VALUES(18,19,'18:19','اور اسی عجیب کرشمے سے ہم نے انہیں اُٹھا بٹھایا تاکہ ذرا آپس میں پوچھ گچھ کریں۔ ان میں سے ایک نے پوچھا”کہو، کتنی دیر اس حال میں رہے؟“ دُوسروں نے کہا”شاید دن پھر یا اس سے کچھ کم رہے ہوں گے۔“پھروہ بولے”اللہ ہی بہتر جانتا ہے کہ ہمارا کتنا وقت اس حال میں گزرا۔ چلو، اب اپنے میں سے کسی کو چاندی کا یہ سِکّہ دے کر شہر بھیجیں اور وہ دیکھے کہ سب سے اچھا کھانا کہاں ملتا ہے۔ وہاں سے وہ کچھ کھانے کےلیے لائے۔ اور چاہیے کہ ذرا ہوشیاری سے کام کرے، ایسا نہ ہو کہ کسی کو ہمارے یہاں ہونے سے خبردار کر بیٹھے');
INSERT INTO "ur_maududi" VALUES(18,20,'18:20','اگر کہیں اُن لوگوں کا ہاتھ ہم پر پڑ گیا تو بس سنگسار ہی کر ڈالیں گے، یا پھر زبردستی ہمیں اپنی ملت میں واپس لے جائیں گے، اور ایسا ہوا توہم کبھی فلاح نہ پا سکیں گے"');
INSERT INTO "ur_maududi" VALUES(18,21,'18:21','اِس طرح ہم نے اہلِ شہر کو ان کے حال پر مطلع کیا تاکہ لوگ جان لیں کہ اللہ کا وعدہ سچا ہے اور یہ کہ قیامت کی گھڑی بے شک آکر رہے گی۔ (مگر ذرا خیال کرو کہ جب سوچنے کی اصل بات یہ تھی)اُس وقت وہ آپس میں اِس بات پر جھگڑ رہے تھے کہ اِن (اصحابِ کہف) کے ساتھ کیا کیا جائے۔ کچھ لوگوں نے کہا”اِن پر ایک دیوار چُن دو، اِن کا ربّ ہی اِن کے معاملہ کو بہتر جانتا ہے۔ “مگر جو لوگ اُن کے معاملات پر غالب تھے اُنہوں نے کہا”ہم تو اِن پر ایک عبادت گاہ بنائیں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(18,22,'18:22','کچھ لوگ کہیں گے کہ وہ تین تھے اور چوتھا اُن کا کُتّا تھا۔ اور کچھ دُوسرے کہہ دیں گے کہ پانچ تھے اور چھٹا اُن کا کُتّا تھا۔ یہ سب بے تُکی ہانکتے ہیں۔ کچھ اور لوگ کہتے ہیں کہ سات تھے اور آٹھواں اُن کا کُتّا تھا۔ کہو، میرا ربّ ہی بہتر جانتا ہے کہ وہ کتنے تھے۔ کم ہی لوگ ان کی صحیح تعداد جانتے ہیں۔ پس تم سرسری بات سے بڑھ کر ان کی تعداد کے معاملے میں لوگوں سے بحث نہ کرو، اور نہ ان کے متعلق کسی سے کچھ پُوچھو۔ ');
INSERT INTO "ur_maududi" VALUES(18,23,'18:23','اور دیکھو، کسی چیز کے بارے میں کبھی یہ نہ کہا کرو کہ میں کل یہ کام کر دوں گا');
INSERT INTO "ur_maududi" VALUES(18,24,'18:24','(تم کچھ نہیں کر سکتے)اِلّا یہ کہ اللہ چاہے۔ اگر بھُولے سے ایسی بات زبان سے نکل جائے تو فوراً اپنے ربّ کو یاد کرو اور کہو”اُمید ہے کہ میرا ربّ اِس معاملےمیں رُشد سے قریب تر بات کی طرف میری رہنمائی فرما دے گا۔“');
INSERT INTO "ur_maududi" VALUES(18,25,'18:25','اور وہ اپنے غار میں تین سو سال رہے، اور (کچھ لوگ مدّت کے شمار میں)۹ سال اور بڑھ گئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(18,26,'18:26','تم کہو، اللہ ان کے قیام کی مدّت زیادہ جانتا ہے، آسمانوں اور زمین کے سب پوشیدہ احوال اُسی کو معلوم ہیں، کیا خوب ہے وہ دیکھنے والا اور سننے والا! زمین و آسمان کی مخلوقات کا کوئی خبرگیر اُس کے سوا نہیں، اور وہ اپنی حکومت میں کسی کو شریک نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(18,27,'18:27','اے نبیؐ ، تمہارے ربّ کی کتاب میں سے جو کچھ تم پر وحی کیا گیا ہے اسے (جُوں کا تُوں)سُنا دو، کوئی اُس کے فرمودات کو بدل دینے کا مجاز نہیں ہے، (اور اگر تم کسی کی خاطر اس میں ردّو بدل کرو گے تو)اُس سے بچ کر بھاگنے کے لیے کوئی جائے پناہ نہ پاوٴ گے۔ ');
INSERT INTO "ur_maududi" VALUES(18,28,'18:28','اور اپنے دل کو اُن لوگوں کی معیّت پر مطمئن کرو جو اپنے ربّ کی رضا کے طلب گار بن کر صبح و شام اُسے پکارتے ہیں، اور اُن سے ہر گز نگاہ نہ پھیرو۔ کیا تم دنیا کی زینت پسند کرتے ہو؟ کسی ایسے شخص کی اطاعت نہ کرو جس کے دل کو ہم نے اپنی یاد سے غافل کر دیا ہے اور جس نے اپنی خواہش ِ نفس کی پیروی اختیار کر لی ہے اور جس کا طریقِ کار افراط و تفریط پر مبنی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(18,29,'18:29','صاف کہہ دو کہ یہ حق ہے تمہارے ربّ کی طرف سے ، اب جس کا جی چاہے مان لے اور جس کا جی چاہے انکار کر دے۔ ہم نے (انکار کرنے والے)ظالموں کے لیے ایک آگ تیار کر رکھی ہے جس کی لپٹیں انہیں گھیرے میں لے چکی ہیں۔ وہاں اگر وہ پانی مانگیں گے تو ایسے پانی سے ان کی تواضع کی جائے گی جو تیل کی تلچھٹ جیسا ہوگا اور ان کا منہ بھُون ڈالے گا، بدترین پینے کی چیز اور بہت بُری آرام گاہ!');
INSERT INTO "ur_maududi" VALUES(18,30,'18:30','رہے وہ لوگ جو مان لیں اور نیک عمل کریں، تو یقیناً ہم نیکو کار لوگوں کا اجر ضائع نہیں کیا کرتے');
INSERT INTO "ur_maududi" VALUES(18,31,'18:31','ان کے لیے سدا بہار جنّتیں ہیں جن کے نیچے نہریں بہ رہی ہوں گی، وہاں وہ سونے کے کنگنوں سے آراستہ کیے جائیں گے، باریک ریشم اور اطلس و دیَبا کے سبز کپڑے پہنیں گے، اور اُونچی مسندوں پر تکیے لگا کر بیٹھیں گے۔ بہترین اجر اور اعلیٰ درجے کی جائے قیام!');
INSERT INTO "ur_maududi" VALUES(18,32,'18:32','اے محمدؐ ، اِن کے سامنے ایک مثال پیش کرو۔ دو شخص تھے۔ ان میں سے ایک کو ہم نے انگور کے دو باغ دیے اور اُن کے گرد کھجُور کے درختوں کی باڑھ لگائی اور ان کے درمیان کاشت کی زمین رکھی');
INSERT INTO "ur_maududi" VALUES(18,33,'18:33','دونوں باغ خوب پھلے پھولے اور بار آور ہونے میں انہوں نے ذرا سی کسر بھی نہ چھوڑی اُن باغوں کے اندر ہم نے ایک نہر جاری کر دی');
INSERT INTO "ur_maududi" VALUES(18,34,'18:34','اوراُسے خوب نفع حاصل ہوا یہ کچھ پا کر ایک دن وہ اپنے ہمسائے سے بات کرتے ہوئے بولا "میں تجھ سے زیادہ مالدار ہوں اور تجھ سے زیادہ طاقتور نفری رکھتا ہوں"');
INSERT INTO "ur_maududi" VALUES(18,35,'18:35','پھر وہ اپنی جنّت میں داخل ہوا اور اپنے نفس کے حق میں ظالم بن کر کہنے لگا”میں نہیں سمجھتا کہ یہ دولت کبھی فنا ہو جائے گی');
INSERT INTO "ur_maududi" VALUES(18,36,'18:36','اور مجھے توقع نہیں کہ قیامت کی گھڑی کبھی آئے گی۔ تاہم اگر کبھی مجھے اپنے ربّ کے حضُور پلٹایا بھی گیا تو ضرور اِس سے بھی زیادہ شاندار جگہ پاوٴں گا۔“ ');
INSERT INTO "ur_maududi" VALUES(18,37,'18:37','اُس کے ہمسائے نے گفتگو کرتے ہوئے اُس سے کہا”کیا تُو کُفر کرتا ہے اُس ذات سے جس نے تجھے مٹی سے اور پھر نطفے سے پیدا کیااور تجھے ایک پورا آدمی بنا کر کھڑا کیا؟ ');
INSERT INTO "ur_maududi" VALUES(18,38,'18:38','رہا میں، تو میرا رب تو وہی اللہ ہے اور میں اس کے ساتھ کسی کو شریک نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(18,39,'18:39','اور جب تُو اپنی جنّت میں داخل ہو رہا تھا تو اس وقت تیری زبان سے یہ کیوں نہ نکلا کہ ماشاء اللہ، لاقوة اِلّا باللّٰہ؟ اگر تُو مجھے مال اور اولاد میں اپنے سے کمتر پا رہا ہے');
INSERT INTO "ur_maududi" VALUES(18,40,'18:40','تو بعید نہیں کہ میرا رب مجھے تیری جنت سے بہتر عطا فرما دے اور تیری جنت پر آسمان سے کوئی آفت بھیج دے جس سے وہ صاف میدان بن کر رہ جائے');
INSERT INTO "ur_maududi" VALUES(18,41,'18:41','یا اس کا پانی زمین میں اتر جائے اور پھر تو اسے کسی طرح نہ نکال سکے"');
INSERT INTO "ur_maududi" VALUES(18,42,'18:42','آخرکار ہوا یہ کہ اس کا سارا ثمرہ مارا گیا اور وہ اپنے انگوروں کے باغ کو ٹٹیوں پر الٹا پڑا دیکھ کر اپنی لگائی ہوئی لاگت پر ہاتھ ملتا رہ گیا اور کہنے لگا کہ "کاش! میں نے اپنے رب کے ساتھ کسی کو شریک نہ ٹھیرایا ہوتا"');
INSERT INTO "ur_maududi" VALUES(18,43,'18:43','نہ ہوا اللہ کو چھوڑ کر اس کے پاس کوئی جتھا کہ اس کی مدد کرتا، اور نہ کر سکا وہ آپ ہی اس آفت کا مقابلہ');
INSERT INTO "ur_maududi" VALUES(18,44,'18:44','اُس وقت معلوم ہوا کہ کارسازی کا اختیار خدائے برحق ہی کے لیے ہے، انعام وہی بہتر ہے جو وہ بخشے اور انجام وہی بخیر ہے جو وہ دکھائے');
INSERT INTO "ur_maududi" VALUES(18,45,'18:45','اور اے نبی ؐ ، اِنہیں حیاتِ دُنیا کی حقیقت اِس مثال سے سمجھاوٴ کہ آج ہم نے آسمان سے پانی برسا دیا تو زمین کی پَود خُوب گھنی ہو گئی، اور کل وہی نباتات بھُس بن کر رہ گئی جسے ہوائیں اُڑائے لیے پھرتی ہیں۔ اللہ ہر چیز پر قدرت رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(18,46,'18:46','یہ مال اور یہ اولاد محض دُنیوی زندگی کی ایک ہنگامی آرائش ہے اصل میں تو باقی رہ جانے والی نیکیاں ہی تیرے رب کے نزدیک نتیجے کے لحاظ سے بہتر ہیں اور اُنہی سے اچھی اُمّیدیں وابستہ کی جا سکتی ہیں');
INSERT INTO "ur_maududi" VALUES(18,47,'18:47','فکر اُس دن کی ہونی چاہیے جب کہ ہم پہاڑوں کو چلائیں گے، اور تم زمین کو بالکل برہنہ پاوٴ گے، اور ہم تمام انسانوں کو اس طرح گھیر کر جمع کریں گے کہ (اگلوں پچھلوں میں سے)ایک بھی نہ چھُوٹے گا، ');
INSERT INTO "ur_maududi" VALUES(18,48,'18:48','اور سب کے سب تمہارے ربّ کے حضُور صف در صف پیش کیے جائیں گے۔۔۔۔ لو دیکھ لو، آگئے نا تم ہمارے پاس اُسی طرح جیسا ہم نے تم کو پہلی بار پیدا کیا تھا۔ تم نے تو یہ سمجھا تھا کہ ہم نے تمہارے لیے کوئی وعدے کا وقت مقرر ہی نہیں کیا ہے');
INSERT INTO "ur_maududi" VALUES(18,49,'18:49','اور نامہٴ اعمال سامنے رکھ دیا جائے گا۔ اُس وقت تم دیکھو گے کہ مجرم لوگ اپنی کتابِ زندگی کے اندراجات سے ڈر رہے ہوں گے اور کہہ رہے ہوں گے کہ ہائے ہماری کم بختی، یہ کیسی کتاب ہے کہ ہماری کوئی چھوٹی بڑی حرکت ایسی نہیں رہی جو اس میں درج نہ کی گئی ہو۔ جو جو کچھ اُنہوں نے کیا تھا وہ سب اپنے سامنے حاضر پائیں گے اور تیرا ربّ کسی پر ذرا ظلم نہ کرے گا۔ ');
INSERT INTO "ur_maududi" VALUES(18,50,'18:50','یاد کرو، جب ہم نے فرشتوں سے کہا کہ آدم کو سجدہ کرو تو انہوں نے سجدہ کیا مگر ابلیس نے نہ کیا۔ وہ جِنّوں میں سے تھا اس لیے اپنے ربّ کے حکم کی اطاعت سے نِکل گیا۔ اب کیا تم مجھے چھوڑ کو اُس کو اور اُس کی ذُرّیّت کو اپنا سرپرست بناتے ہو حالانکہ وہ تمہارے دُشمن ہیں؟ بڑا ہی بُرا بدل ہے جسے ظالم لوگ اختیار کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(18,51,'18:51','میں نے آسمان و زمین پیدا کرتے وقت اُن کو نہیں بُلایا تھا اور نہ خود اُن کی اپنی تخلیق میں انہیں شریک کیا تھا۔ میرا یہ کام نہیں ہے کہ گمراہ کرنے والوں کو اپنا مدد گار بنایا کروں');
INSERT INTO "ur_maududi" VALUES(18,52,'18:52','پھر کیا کریں گے یہ لوگ اُس روز جبکہ اِن کا ربّ اِن سے کہے گا کہ پکارو اب اُن ہستیوں کو جنھیں تم میرا شریک سمجھ بیٹھے تھے۔ یہ اُن کو پکاریں گے ، مگر وہ اِن کی مدد کو نہ آئیں گے اور ہم ان کے درمیان ایک ہی ہلاکت کا گڑھا مشترک کر دیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(18,53,'18:53','سارے مجرم اُس روز آگ دیکھیں گے اورسمجھ لیں گے کہ اب انہیں اس میں گرنا ہے اور وہ اس سے بچنے کے لیے کوئی جائے پناہ نہ پائیں گے');
INSERT INTO "ur_maududi" VALUES(18,54,'18:54','ہم نے اِس قرآن میں لوگوں کو طرح طرح سے سمجھایا مگر انسان بڑا ہی جھگڑالو واقع ہوا ہے');
INSERT INTO "ur_maududi" VALUES(18,55,'18:55','اُن کے سامنے جب ہدایت آئی تو اسے ماننے اور اپنے ربّ کے حضور معافی چاہنے سے آخر اُن کو کس چیز نے روک دیا ؟ اِس کے سوا اور کچھ نہیں کہ وہ منتظر ہیں کہ اُن کے ساتھ بھی وہی کچھ ہو جو پچھلی قوموں کےساتھ ہو چکا ہے، یا یہ کہ وہ عذاب کو سامنے آتے دیکھ لیں! ');
INSERT INTO "ur_maududi" VALUES(18,56,'18:56','رسُولوں کو ہم اس کا م کے سوا اور کسی غرض کے لیے نہیں بھیجتے کہ وہ بشارت اور تنبیہ کی خدمت انجام دیں گے۔ مگر کافروں کا حال یہ ہےکہ وہ باطل کے ہتھیار لے کر حق کو نیچا دکھانے کی کوشش کرتے ہیں اور اُنہوں نے میری آیات کو اور اُن تنبیہات کو جو انہیں کی گئیں مذاق بنا لیا ہے');
INSERT INTO "ur_maududi" VALUES(18,57,'18:57','اور اُس شخص سے بڑھ کر ظالم اور کون ہے جسے اُس کے ربّ کی آیات سُناکر نصیحت کی جائے اور وہ اُن سے منہ پھیرے اور اُس بُرے انجام کو بھول جائے جس کا سروسامان اُس نے اپنے لیے خود اپنے ہاتھوں کیا ہے؟(جن لوگوں نے یہ روش اختیار کی ہے)ان کے دلوں پر ہم نے غلاف چڑھا دیے ہیں جو انہیں قرآن کی بات نہیں سمجھنے دیتے، اور اُن کے کانوں میں ہم نے گرانی پیدا کر دی ہے۔ تم انھیں ہدایت کی طرف کتنا ہی بُلاوٴ، وہ اس حالت میں کبھی ہدایت نہ پائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(18,58,'18:58','تیرا ربّ بڑا درگزر کرنے والا اور رحیم ہے۔ وہ ان کے کرتُوتوں پر انھیں پکڑنا چاہتا تو جلدی ہی عذاب بھیج دیتا۔ مگر ان کے لیے وعدے کا ایک وقت مقرر ہے اور اس سے بچ کر بھاگ نکلنے کی یہ کوئی راہ نہ پائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(18,59,'18:59','یہ عذاب رسیدہ بستیاں تمہارے سامنے موجود ہیں۔ انہوں نے جب ظلم کیا تو ہم نے انھیں ہلاک کر دیا، اور ان میں سے ہر ایک کی ہلاکت کے لیے ہم نے وقت مقرر کر رکھا تھا');
INSERT INTO "ur_maududi" VALUES(18,60,'18:60','(ذرا ان کو وہ قصہ سُناوٴ جو موسیٰؑ کو پیش آیا تھا)جب کہ موسیٰؑ نے اپنے خادم سے کہا تھاکہ”میں اپنا سفر ختم نہ کروں گا جب تک کہ دونوں دریاوٴں کے سنگم پر نہ پہنچ جاوٴں، ورنہ میں ایک زمانۂ دراز تک چلتا ہی رہوں گا۔“ ');
INSERT INTO "ur_maududi" VALUES(18,61,'18:61','پس جب وہ ان کے سنگم پر پہنچے تو اپنی مچھلی سے غافل ہو گئے اور وہ نکل کر اس طرح دریا میں چلی گئی جیسے کہ کوئی سرنگ لگی ہو');
INSERT INTO "ur_maududi" VALUES(18,62,'18:62','آگے جا کر موسیٰؑ نے اپنے خادم سے کہا " لاؤ ہمارا ناشتہ، آج کے سفر میں تو ہم بری طرح تھک گئے ہیں"');
INSERT INTO "ur_maududi" VALUES(18,63,'18:63','خادم نے کہا "آپ نے دیکھا! یہ کیا ہوا؟ جب ہم اُس چٹان کے پاس ٹھیرے ہوئے تھے اُس وقت مجھے مچھلی کا خیال نہ رہا اور شیطان نے مجھ کو ایسا غافل کر دیا کہ میں اس کا ذکر (آپ سے کرنا) بھول گیا مچھلی تو عجیب طریقے سے نکل کر دریا میں چلی گئی"');
INSERT INTO "ur_maududi" VALUES(18,64,'18:64','موسیٰؑ نے کہا”اسی کی تو ہمیں تلاش تھی۔“ چنانچہ وہ دونوں اپنے نقشِ قدم پر پھر واپس ہوئے');
INSERT INTO "ur_maududi" VALUES(18,65,'18:65','اور وہاں اُنہوں نے ہمارے بندوں میں سے ایک بندے کو پایا جسے ہم نے اپنی رحمت سے نوازا تھا اور اپنی طرف سے ایک خاص علم عطا کیا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(18,66,'18:66','موسیٰؑ نے اس سے کہا "کیا میں آپ کے ساتھ رہ سکتا ہوں تاکہ آپ مجھے بھی اُس دانش کی تعلیم دیں جو آپ کوسکھائی گئی ہے؟"');
INSERT INTO "ur_maududi" VALUES(18,67,'18:67','اس نے جواب دیا "آپ میرے ساتھ صبر نہیں کر سکتے');
INSERT INTO "ur_maududi" VALUES(18,68,'18:68','اور جس چیز کی آپ کو خبر نہ ہو آخر آپ اس پر صبر کر بھی کیسے سکتے ہیں"');
INSERT INTO "ur_maududi" VALUES(18,69,'18:69','موسیٰؑ نے کہا "انشاءاللہ آپ مجھے صابر پائیں گے اور میں کسی معاملہ میں آپ کی نافرمانی نہ کروں گا"');
INSERT INTO "ur_maududi" VALUES(18,70,'18:70','اس نے کہا "اچھا، اگر آپ میرے ساتھ چلتے ہیں تو مجھ سے کوئی بات نہ پوچھیں جب تک کہ میں خود اس کا آپ سے ذکر نہ کروں"');
INSERT INTO "ur_maududi" VALUES(18,71,'18:71','اب وہ دونوں روانہ ہوئے، یہاں تک کہ جب وہ ایک کشتی میں سوار ہو گئے تو اس شخص نے کشتی میں شگاف ڈال دیا موسیٰؑ نے کہا "آپ نے اس میں شگاف ڈال دیا تاکہ سب کشتی والوں کو ڈبو دیں؟ یہ تو آپ نے ایک سخت حرکت کر ڈالی"');
INSERT INTO "ur_maududi" VALUES(18,72,'18:72','اس نے کہا "میں نے تم سے کہا نہ تھا کہ تم میرے ساتھ صبر نہیں کر سکتے؟"');
INSERT INTO "ur_maududi" VALUES(18,73,'18:73','موسیٰؑ نے کہا "بھول چوک پر مجھے نہ پکڑیے میرے معاملے میں آپ ذرا سختی سے کام نہ لیں"');
INSERT INTO "ur_maududi" VALUES(18,74,'18:74','پھر وہ دونوں چلے، یہاں تک کہ ان کو ایک لڑکا ملا اور اس شخص نے اسے قتل کر دیا موسیٰؑ نے کہا "آپ نے ایک بے گناہ کی جان لے لی حالانکہ اُس نے کسی کا خون نہ کیا تھا؟ یہ کام تو آپ نے بہت ہی برا کیا"');
INSERT INTO "ur_maududi" VALUES(18,75,'18:75','اُس نے کہا " میں نے تم سے کہا نہ تھا کہ تم میرے ساتھ صبر نہیں کر سکتے؟"');
INSERT INTO "ur_maududi" VALUES(18,76,'18:76','موسیٰؑ نے کہا " اس کے بعد اگر میں آپ سے کچھ پوچھوں تو آپ مجھے ساتھ نہ رکھیں لیجیے، اب تو میری طرف سے آپ کو عذر مل گیا"');
INSERT INTO "ur_maududi" VALUES(18,77,'18:77','پھر وہ آگے چلے یہاں تک کہ ایک بستی میں پہنچے اور وہاں کے لوگوں سے کھانا مانگا مگر انہوں نے ان دونوں کی ضیافت سے انکار کر دیا وہاں انہوں نے ایک دیوار دیکھی جوگرا چاہتی تھی اُس شخص نے اس دیوار کو پھر قائم کر دیا موسیٰؑ نے کہا " اگر آپ چاہتے تو اس کام کی اُجرت لے سکتے تھے"');
INSERT INTO "ur_maududi" VALUES(18,78,'18:78','اُس نے کہا "بس میرا تمہارا ساتھ ختم ہوا اب میں تمہیں ان باتوں کی حقیقت بتاتا ہوں جن پر تم صبر نہ کر سکے');
INSERT INTO "ur_maududi" VALUES(18,79,'18:79','اُس کشتی کا معاملہ یہ ہے کہ وہ چند غریب آدمیوں کی تھی جو دریا میں محنت مزدوری کرتے تھے میں نے چاہا کہ اسے عیب دار کر دوں، کیونکہ آگے ایک ایسے بادشاہ کا علاقہ تھا جو ہر کشتی کو زبردستی چھین لیتا تھا');
INSERT INTO "ur_maududi" VALUES(18,80,'18:80','رہا وہ لڑکا، تو اس کے والدین مومن تھے، ہمیں اندیشہ ہوا کہ یہ لڑکا اپنی سرکشی اور کفر سے ان کو تنگ کرے گا');
INSERT INTO "ur_maududi" VALUES(18,81,'18:81','اس لیے ہم نے چاہا کہ ان کا رب اس کے بدلے ان کو ایسی اولاد دے جو اخلاق میں بھی اس سے بہتر ہو اور جس سے صلہ رحمی بھی زیادہ متوقع ہو');
INSERT INTO "ur_maududi" VALUES(18,82,'18:82','اور اس دیوار کا معاملہ یہ ہے کہ یہ دو یتیم لڑکوں کی ہے جو اس شہر میں رہتے ہیں۔ اِس دیوار کے نیچے اِن بچوں کے لیے ایک خزانہ مدفون ہے اور ان کا باپ ایک نیک آدمی تھا۔ اس لیے تمہارے ربّ نے چاہا کہ یہ دونوں بچے بالغ ہوں اور اپنا خزانہ نکال لیں۔ یہ تمہارے ربّ کی رحمت کی بنا پر کیا گیا ہے ، میں نے کچھ اپنے اختیار سے نہیں کر دیا ہے۔ یہ ہے حقیقت اُن باتوں کی جن پر تم صبر نہ کر سکے۔“ ');
INSERT INTO "ur_maududi" VALUES(18,83,'18:83','اور اے محمدؐ ، یہ لوگ تم سے ذُوالقرنین کے بارے میں پُوچھتے ہیں۔ ان سے کہو، میں اس کا کچھ حال تم کو سُناتا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(18,84,'18:84','ہم نے اس کو زمین میں اقتدار عطا کر رکھا تھا اور اسے ہر قسم کے اسباب و وسائل بخشے تھے');
INSERT INTO "ur_maududi" VALUES(18,85,'18:85','اس نے (پہلے مغرب کی طرف ایک مہم کا) سر و سامان کیا');
INSERT INTO "ur_maududi" VALUES(18,86,'18:86','حتیٰ کہ جب وہ غروبِ آفتاب کی حَد تک پہنچ گیا تو اس نے سُورج کو ایک کالے پانی میں ڈوبتے دیکھا اور وہاں اُسے ایک قوم ملی۔ ہم نے کہا”اے ذوالقرنین، تجھے یہ مقدرت بھی حاصل ہے کہ ان کو تکلیف پہنچائے اور یہ بھی کہ اِن کے ساتھ نیک رویّہ اختیار کرے۔“ ');
INSERT INTO "ur_maududi" VALUES(18,87,'18:87','اس نے کہا، "جو ان میں سے ظلم کرے گا ہم اس کو سزا دیں گے، پھر وہ اپنے رب کی طرف پلٹایا جائے گا اور وہ اسے اور زیادہ سخت عذاب دے گا');
INSERT INTO "ur_maududi" VALUES(18,88,'18:88','اور جو ان میں سے ایمان لائے گا اور نیک عمل کرے گا اُس کے لیے اچھی جزا ہے اور ہم اس کو نرم احکام دیں گے"');
INSERT INTO "ur_maududi" VALUES(18,89,'18:89','پھر اُس نے (ایک دُوسری مہم کی) تیاری کی');
INSERT INTO "ur_maududi" VALUES(18,90,'18:90','یہاں تک کہ طلوعِ آفتاب کی حد تک جا پہنچا۔ وہاں اس نے دیکھا کہ سُورج ایک ایسی قوم پر طلوع ہو رہا ہے جس کےلیے دُھوپ سے بچنے کا کوئی سامان ہم نے نہیں کیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(18,91,'18:91','یہ حال تھا اُن کا، اور ذوالقرنین کے پاس جو کچھ تھا اُسے ہم جانتے تھے');
INSERT INTO "ur_maududi" VALUES(18,92,'18:92','پھر اس نے (ایک اور مہم کا) سامان کیا');
INSERT INTO "ur_maududi" VALUES(18,93,'18:93','یہاں تک کہ جب دو پہاڑوں کے درمیان پہنچا تو اسے ان کے پاس ایک قوم ملی جو مشکل ہی سے کوئی بات سجھتی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(18,94,'18:94','اُن لوگوں نے کہا کہ ”اے ذوالقرنین، یاجوج اور ماجوج اس سرزمین میں فساد پھیلاتے ہیں۔ تو کیا ہم تجھے کوئی ٹیکس اس کام لیے دیں کہ تُو ہمارے اور ان کے درمیان ایک بند تعمیر کر دے؟“');
INSERT INTO "ur_maududi" VALUES(18,95,'18:95','اُس نے کہا”جو کچھ میرے ربّ نے مجھے دے رکھا ہے وہ بہت ہے۔ تم بس محنت سے میری مدد کرو، میں تمہارے اور ان کے درمیان بند بنائے دیتا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(18,96,'18:96','مجھے لوہے کی چادریں لا کر دو" آخر جب دونوں پہاڑوں کے درمیانی خلا کو اس نے پاٹ دیا تو لوگوں سے کہا کہ اب آگ دہکاؤ حتیٰ کہ جب (یہ آہنی دیوار) بالکل آگ کی طرح سُرخ ہو گئی تو اس نے کہا "لاؤ، اب میں اس پر پگھلا ہوا تانبا انڈیلوں گا"');
INSERT INTO "ur_maududi" VALUES(18,97,'18:97','(یہ بند ایسا تھا کہ) یاجوج و ماجوج اس پر چڑھ کر بھی نہ آ سکتے تھے اور اس میں نقب لگانا ان کے لیے اور بھی مشکل تھا');
INSERT INTO "ur_maududi" VALUES(18,98,'18:98','ذوالقرنین نے کہا”یہ میرے ربّ کی رحمت ہے۔ مگر جب میرے ربّ کے وعدے کا وقت آئے گا تو وہ اس کو پیوندِ خاک کر دے گا، اور میرے ربّ کا وعدہ برحق ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(18,99,'18:99','اور اُس روز ہم لوگوں کو چھوڑ دیں گے کہ (سمندر کی موجوں کی طرح)ایک دُوسرے سے گتھم گتھا ہوں اور صُور پھُونکا جائے گا اور ہم سب انسانوں کو ایک ساتھ جمع کریں گے');
INSERT INTO "ur_maududi" VALUES(18,100,'18:100','اور وہ دن ہوگا جب ہم جہنم کو کافروں کے سامنے لائیں گے');
INSERT INTO "ur_maududi" VALUES(18,101,'18:101','اُن کافروں کے سامنے جو میری نصیحت کی طرف سے اندھے بنے ہوئے تھے اور کچھ سننے کے لیے تیار ہی نہ تھے');
INSERT INTO "ur_maududi" VALUES(18,102,'18:102','تو کیا یہ لوگ جنہوں نے کُفر کیا ہے، یہ خیال رکھتے ہیں کہ مجھے چھوڑ کر میرے بندوں کو اپنا کارساز بنالیں؟ ہم نے ایسے کافروں کی ضیافت کے یے جہنّم تیار کر رکھی ہے');
INSERT INTO "ur_maududi" VALUES(18,103,'18:103','اے محمدؐ، ان سے کہو، کیا ہم تمہیں بتائیں کہ اپنے اعمال میں سب سے زیادہ ناکام و نامراد لوگ کون ہیں؟');
INSERT INTO "ur_maududi" VALUES(18,104,'18:104','وہ کہ دنیا کہ زندگی میں جن کی ساری سعی و جہد راہِ راست سے بھٹکی رہی اور وہ سمجھتے رہے کہ وہ سب کچھ ٹھیک کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(18,105,'18:105','یہ وہ لوگ ہیں جنہوں نے اپنے ربّ کی آیات کو ماننے سے انکار کیا اور اس کے حضور پیشی کا یقین نہ کیا۔ اس لیے اُن کے سارے اعمال ضائع ہو گئے، قیامت کے روز ہم انہیں کوئی وزن نہ دیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(18,106,'18:106','ان کی جزا جہنم ہے اُس کفر کے بدلے جو انہوں نے کیا اور اُس مذاق کی پاداش میں جو وہ میری آیات اور میرے رسولوں کے ساتھ کرتے رہے');
INSERT INTO "ur_maududi" VALUES(18,107,'18:107','البتہ وہ لوگ جو ایمان لائے اور جنہوں نے نیک عمل کیے، ان کی میزبانی کے لیے فردوس کے باغ ہوں گے');
INSERT INTO "ur_maududi" VALUES(18,108,'18:108','جن میں وہ ہمیشہ رہیں گے اور کبھی اُس جگہ سے نِکل کر کہیں جانے کو اُن کا جی نہ چاہے گا۔ ');
INSERT INTO "ur_maududi" VALUES(18,109,'18:109','اے محمدؐ ، کہو کہ اگر سمندر میرے ربّ کی باتیں لکھنے کے لیے روشنائی بن جائے تو وہ ختم ہو جائے مگر میرے ربّ کی باتیں ختم نہ ہوں، بلکہ اگر اتنی ہی روشنائی ہم اور لے آئیں تو وہ بھی کفایت نہ کرے');
INSERT INTO "ur_maududi" VALUES(18,110,'18:110','اے محمدؐ، کہو کہ میں تو ایک انسان ہوں تم ہی جیسا، میری طرف وحی کی جاتی ہے کہ تمہارا خدا بس ایک ہی خدا ہے، پس جو کوئی اپنے رب کی ملاقات کا امیدوار ہو اسے چاہیے کہ نیک عمل کرے اور بندگی میں اپنے رب کے ساتھ کسی اور کو شریک نہ کرے');
INSERT INTO "ur_maududi" VALUES(19,1,'19:1','ک، ہ، ی، ع، ص');
INSERT INTO "ur_maududi" VALUES(19,2,'19:2','ذکر ہے اُس رحمت کا جو تیرے ربّ نے اپنے بندے زکریا پر کی تھی');
INSERT INTO "ur_maududi" VALUES(19,3,'19:3','جبکہ اُس نے اپنے رب کو چپکے چپکے پکارا');
INSERT INTO "ur_maududi" VALUES(19,4,'19:4','اُس نے عرض کیا "اے پروردگار، میری ہڈیاں تک گھل گئی ہیں اور سر بڑھاپے سے بھڑک اٹھا ہے اے پروردگار، میں کبھی تجھ سے دعا مانگ کر نامراد نہیں رہا');
INSERT INTO "ur_maududi" VALUES(19,5,'19:5','مجھے اپنے پیچھے اپنے بھائی بندوں کی برائیوں کا خوف ہے ، اور میری بیوی بانجھ ہے۔ تو مجھے اپنے فضلِ خاص سے ایک وارث عطا کر دے');
INSERT INTO "ur_maududi" VALUES(19,6,'19:6','جو میرا وارث بھی ہو اور آلِ یعقوبؑ کی میراث بھی پائے، اور اے پروردگار، اس کو ایک پسندیدہ انسان بنا۔“');
INSERT INTO "ur_maududi" VALUES(19,7,'19:7','(جواب دیا گیا)”اے زکریا، ہم تجھے ایک لڑکے کی بشارت دیتے ہیں جس کا نام یحییٰ ہو گا۔ ہم نے اِس نام کا کوئی آدمی اس سے پہلے پیدا نہیں کیا۔“ ');
INSERT INTO "ur_maududi" VALUES(19,8,'19:8','عرض کیا، "پروردگار، بھلا میرے ہاں کیسے بیٹا ہوگا جبکہ میری بیوی بانجھ ہے اور میں بوڑھا ہو کر سوکھ چکا ہوں؟"');
INSERT INTO "ur_maududi" VALUES(19,9,'19:9','جواب مِلا”ایسا ہی ہوگا۔ تیرا ربّ فرماتا ہے کہ یہ تو میرے لیے ایک ذرا سی بات ہے، آخر اس سے پہلے میں تجھے پیدا کر چکا ہوں جب کہ تو کوئی چیز نہ تھا۔“ ');
INSERT INTO "ur_maududi" VALUES(19,10,'19:10','زکریاؑ نے کہا، "پروردگار، میرے لیے کوئی نشانی مقرر کر دے" فرمایا "تیرے لیے نشانی یہ ہے کہ تو پیہم تین دن لوگوں سے بات نہ کر سکے"');
INSERT INTO "ur_maududi" VALUES(19,11,'19:11','چنانچہ وہ محراب سے نکل کر اپنی قوم کے سامنے آیا اور اس نے اشارے سے ان کو ہدایت کی کہ صبح و شام تسبیح کرو۔ ');
INSERT INTO "ur_maududi" VALUES(19,12,'19:12','”اے یحییٰ، کتابِ الہٰی کو مضبُوط تھام لے۔“ ہم نے اسے بچپن ہی میں”حکم“ سے نوازا');
INSERT INTO "ur_maududi" VALUES(19,13,'19:13','اور اپنی طرف سے اس کو نرم دلی اور پاکیزہ عطا کی، اور وہ بڑا پرہیز گار');
INSERT INTO "ur_maududi" VALUES(19,14,'19:14','اور اپنے والدین کا حق شناس تھا وہ جبّار نہ تھا اور نہ نافرمان');
INSERT INTO "ur_maududi" VALUES(19,15,'19:15','سلام اُس پر جس روز کہ وہ پیدا ہوا اور جس دن وہ مرے اور جس روز وہ زندہ کر کے اُٹھا یا جائے۔ ');
INSERT INTO "ur_maududi" VALUES(19,16,'19:16','ور اے محمدؐ ، اس کتاب میں مریم کا حال بیان کرو، جبکہ وہ اپنے لوگوں سے الگ ہو کر شرقی جانب گوشہ نشین ہو گئی تھی');
INSERT INTO "ur_maududi" VALUES(19,17,'19:17','اور پردہ ڈال کر اُن سے چھُپ بیٹھی تھی۔ اس حالت میں ہم نے اس کے پاس اپنی رُوح کو (یعنی فرشتے کو)بھیجا اور وہ اس کے سامنے ایک پورے انسان کی شکل میں نمودار ہو گیا');
INSERT INTO "ur_maududi" VALUES(19,18,'19:18','مریم یکایک بول اٹھی کہ"اگر تو کوئی خدا ترس آدمی ہے تو میں تجھ سے رحمٰن کی پناہ مانگتی ہوں"');
INSERT INTO "ur_maududi" VALUES(19,19,'19:19','اُس نے کہا "میں تو تیرے رب کا فرستادہ ہوں اور اس لیے بھیجا گیا ہوں کہ تجھے ایک پاکیزہ لڑکا دوں"');
INSERT INTO "ur_maududi" VALUES(19,20,'19:20','مریم نے کہا "میرے ہاں کیسے لڑکا ہوگا جبکہ مجھے کسی بشر نے چھوا تک نہیں ہے اور میں کوئی بدکار عورت نہیں ہوں"');
INSERT INTO "ur_maududi" VALUES(19,21,'19:21','فرشتے نے کہا”ایسا ہی ہوگا، تیرا ربّ فرماتا ہے کہ ایسا کرنا میرے لیے بہت آسان ہے اور ہم یہ اس لیے کر یں گے کہ اُس لڑکے کو لوگوں کے لیے ایک نشانی بنائیں اور اپنی طرف سے ایک رحمت اور یہ کام ہو کر رہنا ہے۔“');
INSERT INTO "ur_maududi" VALUES(19,22,'19:22','مریم کو اس بچے کاحمل رہ گیا اور وہ اس حمل کو لیے ہوئے ایک دُور کے مقام پر چلی گئی۔ ');
INSERT INTO "ur_maududi" VALUES(19,23,'19:23','پھر زچگی کی تکلیف نے اُسے ایک کھجُور کے درخت کے نیچے پہنچا دیا۔ وہ کہنے لگی”کاش میں اس سے پہلے ہی مر جاتی اور میرا نام و نشان نہ رہتا۔“ ');
INSERT INTO "ur_maududi" VALUES(19,24,'19:24','فرشتے نے پائنتی سے اس کو پکار کر کہا "غم نے کر تیرے رب نے تیرے نیچے ایک چشمہ رواں کر دیا ہے');
INSERT INTO "ur_maududi" VALUES(19,25,'19:25','اور تو ذرا اِس درخت کے تنے کو ہلا، تیرے اوپر تر و تازہ کھجوریں ٹپک پڑیں گی');
INSERT INTO "ur_maududi" VALUES(19,26,'19:26','پس تُو کھا اور پی اور اپنی آنکھیں ٹھنڈی کر۔ پھر اگر کوئی تجھے نظر آئے تو اس سے کہہ دے کہ میں نے رحمان کے لیے روزے کی نذر مانی ہے، اس لیے آج میں کسی سے نہ بولوں گی۔“ ');
INSERT INTO "ur_maududi" VALUES(19,27,'19:27','پھر وہ اس بچے کو لیے ہوئے اپنی قوم میں آئی لوگ کہنے لگے " اے مریم، یہ تو تُو نے بڑا پاپ کر ڈالا');
INSERT INTO "ur_maududi" VALUES(19,28,'19:28','اے ہارون کی بہن، نہ تیرا باپ کوئی آدمی تھا اور نہ تیری ماں ہی کوئی بدکار عورت تھی۔“ ');
INSERT INTO "ur_maududi" VALUES(19,29,'19:29','مریم نے بچے کی طرف اشارہ کر دیا۔لوگوں نے کہا”ہم اِس سے کیا بات کریں گے جو گہوارے میں پڑا ہوا ایک بچہ ہے؟“ ');
INSERT INTO "ur_maududi" VALUES(19,30,'19:30','بچہ بول اٹھا "میں اللہ کا بندہ ہوں اُس نے مجھے کتاب دی، اور نبی بنایا');
INSERT INTO "ur_maududi" VALUES(19,31,'19:31','اور بابرکت کیا جہاں بھی میں رہوں، اور نماز اور زکوٰۃ کی پابندی کا حکم دیا جب تک میں زندہ رہوں');
INSERT INTO "ur_maududi" VALUES(19,32,'19:32','اور اپنی والدہ کا حق ادا کرنے والا بنایا، اور مجھ کو جبّار اور شقی نہیں بنایا');
INSERT INTO "ur_maududi" VALUES(19,33,'19:33','سلام ہے مجھ پر جبکہ میں پیدا ہوا اور جبکہ میں مروں اور جبکہ زندہ کر کےاُٹھایا جاوٴں۔“ ');
INSERT INTO "ur_maududi" VALUES(19,34,'19:34','یہ ہے عیسٰی ابن مریم اور یہ ہے اُس کے بارے میں وہ سچی بات جس میں لوگ شک کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(19,35,'19:35','اللہ کا یہ کام نہیں ہے کہ وہ کسی کو بیٹا بنائے۔ وہ پاک ذات ہے۔ وہ جب کسی بات کا فیصلہ کرتا ہے تو کہتا ہے کہ ہو جا، اور بس وہ ہو جاتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(19,36,'19:36','(اور عیسیٰ ؑ نے کہا تھا کہ)”اللہ میرا ربّ بھی ہے اور تمہارا ربّ بھی ، پس تم اس کی بندگی کرو، یہی سیدھی راہ ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(19,37,'19:37','مگر پھر مختلف گروہ باہم اختلاف کرنے لگے۔ سو جن لوگوں نے کُفر کیا ان کے لیے وہ وقت بڑی تباہی کا ہوگا');
INSERT INTO "ur_maududi" VALUES(19,38,'19:38','جب کہ وہ ایک بڑا دن دیکھیں گے جب وہ ہمارے سامنے حاضر ہوں گے اُس روز تو اُن کے کان بھی خوب سُن رہے ہوں گے اور اُن کی آنکھیں بھی خوب دیکھتی ہوں گی، مگر آج یہ ظالم کھلی گمراہی میں مبتلا ہیں');
INSERT INTO "ur_maududi" VALUES(19,39,'19:39','اے نبیؐ، اِس حالت میں جبکہ یہ لوگ غافل ہیں اور ایمان نہیں لا رہے ہیں، اِنہیں اس دن سے ڈرا دو جبکہ فیصلہ کر دیا جائے گا اور پچھتاوے کے سوا کوئی چارہ کار نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(19,40,'19:40','آخر کار ہم ہی زمین اور اور اس کی ساری چیزوں کے وارث ہوں گے اور سب ہماری طرف ہی پلٹائے جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(19,41,'19:41','ور اس کتاب میں ابراہیم ؑ کا قصہ بیان کرو ، بے شک وہ ایک راست باز انسان اور ایک نبی تھا');
INSERT INTO "ur_maududi" VALUES(19,42,'19:42','(انہیں ذرا اُس موقع کی یاد دلاؤ) جبکہ اُس نے اپنے باپ سے کہا کہ "ابّا جان، آپ کیوں اُن چیزوں کی عبادت کرتے ہیں جو نہ سنتی ہیں نہ دیکھتی ہیں اور نہ آپ کا کوئی کام بنا سکتی ہیں؟');
INSERT INTO "ur_maududi" VALUES(19,43,'19:43','ابّا جان، میرے پاس ایک ایسا عِلم آیا ہے جو آپ کے پاس نہیں آیا، آپ میرے پیچھے چلیں، میں آپ کو سیدھا راستہ بتاؤں گا');
INSERT INTO "ur_maududi" VALUES(19,44,'19:44','ابّا جان، آپ شیطان کی بندگی نہ کریں، شیطان تو رحمٰن کا نافرمان ہے');
INSERT INTO "ur_maududi" VALUES(19,45,'19:45','ابّا جان، مجھے ڈر ہے کہ کہیں آپ رحمان کے عذاب میں مُبتلا نہ ہو جائیں اور شیطان کے ساتھی بن کر رہیں"');
INSERT INTO "ur_maududi" VALUES(19,46,'19:46','باپ نے کہا "ابراہیمؑ، کیا تو میرے معبُودوں سے پھر گیا ہے؟ اگر تو باز نہ آیا تو میں تجھے سنگسار کر دوں گا بس تو ہمیشہ کے لیے مجھ سے الگ ہو جا"');
INSERT INTO "ur_maududi" VALUES(19,47,'19:47','ابراہیم ؑ نے کہا”سلام ہے آپ کو۔ میں اپنے ربّ سے دُعا کروں گا کہ آپ کو معاف کردے، میرا ربّ مجھ پر بڑا مہربان ہے');
INSERT INTO "ur_maududi" VALUES(19,48,'19:48','میں آپ لوگوں کو بھی چھوڑتا ہوں اور اُن ہستیوں کو بھی جنہیں آپ لوگ خدا کو چھوڑ کر پکارا کرتے ہیں میں تو اپنے رب ہی کو پکاروں گا، امید ہے کہ میں اپنے رب کو پکار کے نامراد نہ رہوں گا"');
INSERT INTO "ur_maududi" VALUES(19,49,'19:49','پس جب وہ اُن لوگوں سے اور اُن کے معبُودانِ غیر اللہ سے جُدا ہو گیا تو ہم نے اُس کو اسحاقؑ اور یعقوبؑ جیسی اولاد دی اور ہر ایک کو نبی بنایا');
INSERT INTO "ur_maududi" VALUES(19,50,'19:50','اور ان کو اپنی رحمت سے نوازا اور ان کو سچی نام وری عطا کی۔ ');
INSERT INTO "ur_maududi" VALUES(19,51,'19:51','اور ذکر کرو اِس کتاب میں موسیٰؑ کا۔ وہ ایک چیدہ شخص تھا اور رسُول نبی تھا');
INSERT INTO "ur_maududi" VALUES(19,52,'19:52','ہم نے اُس کو طُور کے داہنی جانب سے پکارا اور راز کی گفتگو سے اس کو تقرب عطا کیا، ');
INSERT INTO "ur_maududi" VALUES(19,53,'19:53','اور اپنی مہربانی سے اس کے بھائی ہارونؑ کو نبی بنا کر اُسے (مدد گار کے طور پر) دیا');
INSERT INTO "ur_maududi" VALUES(19,54,'19:54','اور اس کتاب میں اسماعیلؑ کا ذکر کرو وہ وعدے کا سچا تھا اور رسُول نبی تھا');
INSERT INTO "ur_maududi" VALUES(19,55,'19:55','وہ اپنے گھر والوں کو نماز اور زکوٰۃ کا حکم دیتا تھا اور اپنے رب کے نزدیک ایک پسندیدہ انسان تھا');
INSERT INTO "ur_maududi" VALUES(19,56,'19:56','اور اِس کتاب میں ادریسؑ کا ذکر کرو۔ وہ ایک راستباز انسان اور ایک نبی تھا');
INSERT INTO "ur_maududi" VALUES(19,57,'19:57','اور اُسے ہم نے بلند مقام پر اُٹھایا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(19,58,'19:58','یہ وہ پیغمبر ہیں جن پر اللہ نے انعام فرمایا آدمؑ کی اولاد میں سے، اور اُن لوگوں کی نسل سے جنہیں ہم نے نوحؑ کے ساتھ کشتی پر سوار کیا تھا، ا ور ابراہیمؑ کی نسل سے اور اسرائیلؑ کی نسل سے اور یہ ان لوگوں میں سے تھے جن کو ہم نے ہدایت بخشی اور برگزیدہ کیا ان کا حال یہ تھا کہ جب رحمان کی آیات ان کو سنائی جاتیں تو روتے ہوئے سجدے میں گر جاتے تھے');
INSERT INTO "ur_maududi" VALUES(19,59,'19:59','پھر ان کے بعد وہ نا خلف لوگ ان کے جانشین ہوئے جنہوں نے نماز کو ضائع کیا اور خواہشاتِ نفس کی پیروی کی، پس قریب ہے کہ وہ گمراہی کے انجام سے دوچار ہوں');
INSERT INTO "ur_maududi" VALUES(19,60,'19:60','البتہ جو توبہ کر لیں اور ایمان لے آئیں اور نیک عملی اختیار کر لیں وہ جنّت میں داخل ہوں گے اور ان کی ذرّہ برابر حق تلفی نہ ہو گی');
INSERT INTO "ur_maududi" VALUES(19,61,'19:61','ان کے لیے ہمیشہ رہنے والی جنّتیں ہیں جن کا رحمٰن نے اپنے بندوں سے درپردہ وعدہ کر رکھا ہے اور یقیناً یہ وعدہ پُورا ہو کر رہنا ہے');
INSERT INTO "ur_maududi" VALUES(19,62,'19:62','وہاں وہ کوئی بےہُودہ بات نہ سُنیں گے،جو کچھ بھی سُنیں گے ٹھیک ہی سُنیں گے۔ اور ان کا رزق انہیں پیہم صبح و شام ملتا رہے گا');
INSERT INTO "ur_maududi" VALUES(19,63,'19:63','یہ ہے وہ جنت جس کا وارث ہم اپنے بندوں میں سے اُس کو بنائیں گے جو پرہیزگار رہا ہے');
INSERT INTO "ur_maududi" VALUES(19,64,'19:64','اے محمدؐ ، ہم تمہارے ربّ کے حکم کے بغیر نہیں اُترا کرتے۔ جو کچھ ہمارے آگے ہے اور جو کچھ پیچھے ہے اور جو کچھ اس کے درمیان ہے ہر چیز کا مالک وہی ہے اور تمہارا ربّ بھُولنے والا نہیں ہے');
INSERT INTO "ur_maududi" VALUES(19,65,'19:65','وہ ربّ ہے آسمانوں کا اور زمین کا اور اُن ساری چیزوں کا جو آسمان و زمین کے درمیان ہیں۔ پس تم اُس کی بندگی کرو اور اُسی کی بندگی پر ثابت قدم رہو۔ کیا ہے کوئی ہستی تمہارے علم میں اُس کی ہم پایہ؟ ');
INSERT INTO "ur_maududi" VALUES(19,66,'19:66','انسان کہتا ہے کیا واقعی جب میں مر چکوں گا تو پھر زندہ کر کے نکال لایا جاؤں گا؟');
INSERT INTO "ur_maududi" VALUES(19,67,'19:67','کیا انسان کو یاد نہیں آتا کہ ہم پہلے اس کو پیدا کر چکے ہیں جبکہ وہ کچھ بھی نہ تھا؟');
INSERT INTO "ur_maududi" VALUES(19,68,'19:68','تیرے ربّ کی قسم ، ہم ضرور اِن سب کو اور ان کے ساتھ شیاطین کو بھی گھیر لائیں گے، پھر جہنّم کے گِرد لا کر انھیں گُھٹنوں کے بل گرا دیں گے');
INSERT INTO "ur_maududi" VALUES(19,69,'19:69','پھر ہر گروہ میں سے ہر اُس شخص کو چھانٹ لیں گے جو رحمٰن کے مقابلے میں زیادہ سرکش بنا ہوا تھا، ');
INSERT INTO "ur_maududi" VALUES(19,70,'19:70','پھر یہ ہم جانتے ہیں کہ ان میں سے کون سب سے بڑھ کر جہنم میں جھونکے جانے کا مستحق ہے');
INSERT INTO "ur_maududi" VALUES(19,71,'19:71','تم میں سے کوئی ایسا نہیں ہے جو جہنّم پر وارد نہ ہو، یہ تو ایک طے شدہ بات ہے جسے پُورا کرنا تیرے ربّ کا ذِمّہ ہے');
INSERT INTO "ur_maududi" VALUES(19,72,'19:72','پھر ہم اُن لوگوں کو بچا لیں گے جو (دنیا میں) متقی تھے اور ظالموں کو اُسی میں گرا ہُوا چھوڑ دیں گے');
INSERT INTO "ur_maududi" VALUES(19,73,'19:73','اِن لوگوں کو جب ہماری کھُلی کھُلی آیات سُنائی جاتی ہیں تو انکار کرنے والے ایمان لانے والوں سے کہتے ہیں”بتاوٴ ہم دونوں گروہوں میں کون بہتر حالت میں ہے اور کس کی مجلسیں زیادہ شاندار ہیں؟“ ');
INSERT INTO "ur_maududi" VALUES(19,74,'19:74','حالانکہ ان سے پہلے ہم کتنی ہی ایسی قوموں کو ہلاک کر چکے ہیں جو اِن سے زیادہ سر و سامان رکھتی تھیں اور ظاہری شان و شوکت میں اِن سے بڑھی ہوئی تھیں');
INSERT INTO "ur_maududi" VALUES(19,75,'19:75','اِن سے کہو، جو شخص گمراہی میں مُبتلا ہوتا ہے اُسے رحمان ڈھیل دیا کرتا ہے یہاں تک کہ جب ایسے لوگ وہ چیز دیکھ لیتے ہیں جس کا اُن سے وعدہ کیا گیا ہے خواہ وہ عذاب الٰہی ہو یا قیامت کی گھڑی تب انہیں معلوم ہو جاتا ہے کہ کس کا حال خراب ہے اور کس کا جتھا کمزور!');
INSERT INTO "ur_maududi" VALUES(19,76,'19:76','اِس کے برعکس جو لوگ راہِ راست اختیار کرتے ہیں اللہ ان کو راست روی میں ترقی عطا فرماتا ہے اور باقی رہ جانے والی نیکیاں ہی تیرے ربّ کے نزدیک جزا اور انجام کے اعتبار سے بہتر ہیں');
INSERT INTO "ur_maududi" VALUES(19,77,'19:77','پھر تُو نے دیکھا اُس شخص کو جو ہماری آیات کو ماننے سے انکار کرتا ہے اور کہتا ہے کہ میں تو مال اور اولاد سے نوازا ہی جاتا رہوں گا؟ ');
INSERT INTO "ur_maududi" VALUES(19,78,'19:78','کیا اسے غیب کا پتہ چل گیا ہے یا اس نے رحمان سے کوئی عہد لے رکھا ہے؟');
INSERT INTO "ur_maududi" VALUES(19,79,'19:79','ہر گز نہیں، جو کچھ یہ بکتا ہے اسے ہم لکھ لیں گے اور اس کے لیے سزا میں اور زیادہ اضافہ کریں گے');
INSERT INTO "ur_maududi" VALUES(19,80,'19:80','جس سر و سامان اور لاؤ لشکر کا یہ ذکر کر رہا ہے وہ سب ہمارے پاس رہ جائے گا اور یہ اکیلا ہمارے سامنے حاضر ہوگا');
INSERT INTO "ur_maududi" VALUES(19,81,'19:81','اِن لوگوں نے اللہ کو چھوڑ کر اپنے کچھ خدا بنا رکھے ہیں تاکہ وہ اِن کے پُشتیبان ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(19,82,'19:82','وہ سب ان کی عبادت کا انکار کریں گے اور اُلٹے اِن کے مخالف بن جائیں گے');
INSERT INTO "ur_maududi" VALUES(19,83,'19:83','کیا تم دیکھتے نہیں ہو کہ ہم نے اِن منکرین حق پر شیاطین چھوڑ رکھے ہیں جو اِنہیں خُوب خُوب (مخالفتِ حق پر) اکسا رہے ہیں؟');
INSERT INTO "ur_maududi" VALUES(19,84,'19:84','اچھا، تو اب اِن نُزوُلِ عذاب کے لیے بےتاب نہ ہو۔ ہم اِن کے دن گِن رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(19,85,'19:85','وہ دن آنے والا ہے جب متقی لوگوں کو ہم مہمانوں کی طرح رحمان کے حضور پیش کریں گے');
INSERT INTO "ur_maududi" VALUES(19,86,'19:86','اور مجرموں کو پیاسے جانوروں کی طرح جہنم کی طرف ہانک لے جائیں گے');
INSERT INTO "ur_maududi" VALUES(19,87,'19:87','اُس وقت لوگ کوئی سفارش لانے پر قادر نہ ہوں گے بجز اُس کے جس نے رحمٰن کے حضور سے پروانہ حاصل کر لیا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(19,88,'19:88','وہ کہتے ہیں کہ رحمان نے کسی کو بیٹا بنایا ہے');
INSERT INTO "ur_maududi" VALUES(19,89,'19:89','سخت بیہودہ بات ہے جوتم لوگ گھڑ لائے ہو');
INSERT INTO "ur_maududi" VALUES(19,90,'19:90','قریب ہے کہ آسمان پھٹ پڑیں، زمین شق ہو جائے اور پہاڑ گر جائیں');
INSERT INTO "ur_maududi" VALUES(19,91,'19:91','اس بات پر کہ لوگوں نے رحمان کے لیے اولاد ہونے کا دعویٰ کیا!');
INSERT INTO "ur_maududi" VALUES(19,92,'19:92','رحمان کی یہ شان نہیں ہے کہ وہ کسی کو بیٹا بنائے');
INSERT INTO "ur_maududi" VALUES(19,93,'19:93','زمین اور آسمان کے اندر جو بھی ہیں سب اس کے حضور بندوں کی حیثیت سے پیش ہونے والے ہیں');
INSERT INTO "ur_maududi" VALUES(19,94,'19:94','سب پر وہ محیط ہے اور اس نے اُن کو شمار کر رکھا ہے');
INSERT INTO "ur_maududi" VALUES(19,95,'19:95','سب قیامت کے روز فرداً فرداً اس کے سامنے حاضر ہوں گے');
INSERT INTO "ur_maududi" VALUES(19,96,'19:96','یقیناً جو لوگ ایمان لے آئے ہیں اور عملِ صالح کر رہے ہیں عنقریب رحمٰن اُن کے لیے دلوں میں محبت پیدا کر دے گا۔ ');
INSERT INTO "ur_maududi" VALUES(19,97,'19:97','پس اے محمدؐ، اِس کلام کو ہم نے آسان کر کے تمہاری زبان میں اسی لیے نازل کیا ہے کہ تم پرہیز گاروں کو خوشخبری دے دو اور ہٹ دھرم لوگوں کو ڈرا دو');
INSERT INTO "ur_maududi" VALUES(19,98,'19:98','ان سے پہلے ہم کتنی ہی قوموں کو ہلاک کر چکے ہیں، پھر آج کہیں تم ان کا نشان پاتے ہو یا اُن کی بھنک بھی کہیں سنائی دیتی ہے؟');
INSERT INTO "ur_maududi" VALUES(20,1,'20:1','طٰہٰ');
INSERT INTO "ur_maududi" VALUES(20,2,'20:2','ہم نے یہ قرآن تم پر اس لیے نازل نہیں کیا ہے کہ تم مصیبت میں پڑ جاؤ');
INSERT INTO "ur_maududi" VALUES(20,3,'20:3','یہ تو ایک یاد دہانی ہے ہر اُس شخص کے لیے جو ڈرے۔ ');
INSERT INTO "ur_maududi" VALUES(20,4,'20:4','نازل کیا گیا ہے اُس ذات کی طرف سے جس نے پیدا کیا ہے زمین کو اور بلند آسمانوں کو');
INSERT INTO "ur_maududi" VALUES(20,5,'20:5','وہ رحمٰن(کائنات کے)تختِ سلطنت پر جلوہ فرما ہے۔ ');
INSERT INTO "ur_maududi" VALUES(20,6,'20:6','مالک ہے اُن سب چیزوں کا جو آسمانوں اور زمین میں ہیں اور جو زمین و آسمان کے درمیان میں ہیں اور جو مٹی کے نیچے ہیں');
INSERT INTO "ur_maududi" VALUES(20,7,'20:7','تم چاہے اپنی بات پُکار کر کہو ، وہ تو چُپکے سے کہی ہوئی بات بلکہ اس سے مخفی بات بھی جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(20,8,'20:8','وہ اللہ ہے، اس کے سوا کوئی خدانہیں، اس کے لیے بہترین نام ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(20,9,'20:9','اور تمہیں کچھ موسیٰؑ کی خبر بھی پہنچی ہے؟');
INSERT INTO "ur_maududi" VALUES(20,10,'20:10','جب کہ اُس نے ایک آگ دیکھی اور اپنے گھر والوں سے کہا کہ”ذرا ٹھہرو، میں نے ایک آگ دیکھی ہے۔ شاید کہ تمہارے لیے ایک آدھ انگارا لے آوٴں، یا اس آگ پر مجھے(راستے کے متعلق)کوئی رہنمائی مِل جائے۔“ ');
INSERT INTO "ur_maududi" VALUES(20,11,'20:11','وہاں پہنچا تو پکارا گیا "اے موسیٰؑ!');
INSERT INTO "ur_maududi" VALUES(20,12,'20:12','میں ہی تیرا ربّ ہوں، جُوتیاں اُتار دے۔ تُو وادی ِ مقدس طُویٰ میں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(20,13,'20:13','اور میں نے تجھ کو چُن لیا ہے، سُن جو کچھ وحی کیا جاتا ہے');
INSERT INTO "ur_maududi" VALUES(20,14,'20:14','میں ہی اللہ ہوں، میرے سوا کوئی خدا نہیں، پس تُو میری بندگی کر اور میری یاد کے لیے نماز قائم کر۔ ');
INSERT INTO "ur_maududi" VALUES(20,15,'20:15','قیامت کی گھڑی ضرور آنے والی ہے۔ میں اُس کا وقت مخفی رکھنا چاہتا ہوں، تاک ہر متنفّس اپنی سعی کے مطابق بدلہ پائے۔ ');
INSERT INTO "ur_maududi" VALUES(20,16,'20:16','پس کوئی ایسا شخص جو اُس پر ایمان نہیں لاتا اور اپنی خواہش نفس کا بندہ بن گیا ہے تجھ کو اُس گھڑی کی فکر سے نہ روک دے، ورنہ تو ہلاکت میں پڑ جائے گا');
INSERT INTO "ur_maududi" VALUES(20,17,'20:17','اور اے موسیٰؑ ، یہ تیرے ہاتھ میں کیا ہے؟“ ');
INSERT INTO "ur_maududi" VALUES(20,18,'20:18','موسیٰؑ نے جواب دیا”یہ میری لاٹھی ہے، اِس پر ٹیک لگا کر چلتا ہوں، اِس سے اپنی بکریوں کے لیے پتّے جھاڑتا ہوں، اور بھی بہت سے کام ہیں جو اِس سے لیتا ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(20,19,'20:19','فرمایا "پھینک دے اس کو موسیٰؑ"');
INSERT INTO "ur_maududi" VALUES(20,20,'20:20','اس نے پھینک دیا اور یکایک وہ ایک سانپ تھی جو دَوڑ رہا تھا');
INSERT INTO "ur_maududi" VALUES(20,21,'20:21','فرمایا "پکڑ لے اس کو اور ڈر نہیں، ہم اسے پھر ویسا ہی کر دیں گے جیسی یہ تھی');
INSERT INTO "ur_maududi" VALUES(20,22,'20:22','اور ذرا اپنا ہاتھ اپنی بغل میں دبا، چمکتا ہوا نکلے گا بغیر کسی تکلیف کے۔ یہ دُوسری نشانی ہے');
INSERT INTO "ur_maududi" VALUES(20,23,'20:23','اس لیے کہ ہم تجھے اپنی بڑی نشانیاں دکھانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(20,24,'20:24','اب تو فرعون کے پاس جا، وہ سرکش ہو گیا ہے"');
INSERT INTO "ur_maududi" VALUES(20,25,'20:25','موسیٰؑ نے عرض کیا”پروردگار، میرا سینہ کھول دے، ');
INSERT INTO "ur_maududi" VALUES(20,26,'20:26','اور میرے کام کو میرے لیے آسان کر دے');
INSERT INTO "ur_maududi" VALUES(20,27,'20:27','اور میری زبان کی گرہ سُلجھا دے');
INSERT INTO "ur_maududi" VALUES(20,28,'20:28','تاکہ لوگ میری بات سمجھ سکیں، ');
INSERT INTO "ur_maududi" VALUES(20,29,'20:29','اور میرے لیے میرے اپنے کنبے سے ایک وزیر مقرر کر دے');
INSERT INTO "ur_maududi" VALUES(20,30,'20:30','ہارون ، جو میرا بھائی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(20,31,'20:31','اُس کے ذریعہ سے میرا ہاتھ مضبُوط کر');
INSERT INTO "ur_maududi" VALUES(20,32,'20:32','اور اس کو میرے کام میں شریک کر دے');
INSERT INTO "ur_maududi" VALUES(20,33,'20:33','تاکہ ہم خوب تیری پاکی بیان کریں');
INSERT INTO "ur_maududi" VALUES(20,34,'20:34','اور خوب تیرا چرچا کریں');
INSERT INTO "ur_maududi" VALUES(20,35,'20:35','تو ہمیشہ ہمارے حال پر نگران رہا ہے"');
INSERT INTO "ur_maududi" VALUES(20,36,'20:36','فرمایا "دیا گیا جو تو نے مانگا اے موسیٰؑ');
INSERT INTO "ur_maududi" VALUES(20,37,'20:37','ہم نے پھر ایک مرتبہ تجھ پر احسان کیا۔ ');
INSERT INTO "ur_maududi" VALUES(20,38,'20:38','یاد کر وہ وقت جبکہ ہم نے تیری ماں کو اشارہ کیا ایسا اشارہ جو وحی کے ذریعہ سے ہی کیا جاتا ہے');
INSERT INTO "ur_maududi" VALUES(20,39,'20:39','کہ اس بچے کو صندوق میں رکھ دے اور صندوق کو دریا میں چھوڑ دے دریا اسے ساحل پر پھینک دے گا اور اسے میرا دشمن اور اس بچے کا دشمن اٹھا لے گا میں نے اپنی طرف سے تجھ پر محبت طاری کر دی اور ایسا انتظام کیا کہ تو میری نگرانی میں پالا جائے');
INSERT INTO "ur_maududi" VALUES(20,40,'20:40','یاد کر جبکہ تیری بہن چل رہی تھی، پھر جا کر کہتی ہے، "میں تمہیں اُس کا پتہ دوں جو اِس بچے کی پرورش اچھی طرح کرے؟" اس طرح ہم نے تجھے پھر تیری ماں کے پاس پہنچا دیا تاکہ اُس کی آنکھ ٹھنڈی رہے اور وہ رنجیدہ نہ ہو اور (یہ بھی یاد کر کہ) تو نے ایک شخص کو قتل کر دیا تھا، ہم نے تجھے اِس پھندے سے نکالا اور تجھے مختلف آزمائشوں سے گزارا اور تو مَدیَن کے لوگوں میں کئی سال ٹھیرا رہا پھر اب ٹھیک اپنے وقت پر تو آ گیا ہے اے موسیٰؑ');
INSERT INTO "ur_maududi" VALUES(20,41,'20:41','میں نے تجھ کو اپنے کام کا بنا لیا ہے');
INSERT INTO "ur_maududi" VALUES(20,42,'20:42','جا، تُو اور تیرا بھائی میری نشانیوں کے ساتھ ا ور دیکھو، تم میری یاد میں تقصیر نہ کرنا');
INSERT INTO "ur_maududi" VALUES(20,43,'20:43','جاؤ تم دونوں فرعون کے پاس کہ وہ سرکش ہو گیا ہے');
INSERT INTO "ur_maududi" VALUES(20,44,'20:44','اس سے نرمی کے ساتھ بات کرنا، شاید کے وہ نصیحت قبول کرے یا ڈر جائے۔“ ');
INSERT INTO "ur_maududi" VALUES(20,45,'20:45','دونوں نے عرض کیا”پروردگار، ہمیں اندیشہ ہے کہ وہ ہم پر زیادتی کرے گا یا پِل پڑے گا۔“');
INSERT INTO "ur_maududi" VALUES(20,46,'20:46','فرمایا " ڈرو مت، میں تمہارے ساتھ ہوں، سب کچھ سُن رہا ہوں اور دیکھ رہا ہوں');
INSERT INTO "ur_maududi" VALUES(20,47,'20:47','جاؤ اس کے پاس اور کہو کہ ہم تیرے رب کے فرستادے ہیں، بنی اسرائیل کو ہمارے ساتھ جانے کے لیے چھوڑ دے اور ان کو تکلیف نہ دے ہم تیرے پاس تیرے رب کی نشانی لے کر آئے ہیں اور سلامتی ہے اُس کے لیے جو راہِ راست کی پیروی کرے');
INSERT INTO "ur_maududi" VALUES(20,48,'20:48','ہم کو وحی سے بتایا گیا ہے کہ عذاب ہے اُس کے لیے جو جُھٹلائے اور منہ موڑے۔“ ');
INSERT INTO "ur_maududi" VALUES(20,49,'20:49','فرعون نے کہا”اچھا، تو پھر تم دونوں کا ربّ کو ن ہے اے موسیٰ؟“ ');
INSERT INTO "ur_maududi" VALUES(20,50,'20:50','موسیٰؑ نے جواب دیا”ہمارا ربّ وہ ہے جس نے ہر چیز کو اُس کی ساخت بخشی، پھر اس کو راستہ بتایا۔“ ');
INSERT INTO "ur_maududi" VALUES(20,51,'20:51','فرعون بولا”اور پہلے جو نسلیں گزر چکیں ہیں ان کی پھر کیا حالت تھی؟“ ');
INSERT INTO "ur_maududi" VALUES(20,52,'20:52','موسیٰؑ نے کہا”اُس کا علم میرے ربّ کے پاس ایک نوشتے میں محفوظ ہے۔ میرا ربّ نہ چُوکتا ہے نہ بھُولتا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(20,53,'20:53',' وہی جس نے تمہارے لیے زمین کا فرش بچھایا، اور اُس میں تمہارے چلنے کو راستےبنائے، اور اوپر سے پانی برسایا، پھر اُس کے ذریعہ سے مختلف اقسام کی پیداوار نکالی');
INSERT INTO "ur_maududi" VALUES(20,54,'20:54','کھاوٴ اور اپنے جانوروں کو بھی چراوٴ۔ یقیناً اِس میں بہت سی نشانیاں ہیں عقل رکھنے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(20,55,'20:55','اِس زمین سے ہم نے تم کو پیدا کیا ہے ، اِسی میں ہم تمہیں واپس لے جائیں گے اور اسی سے تم کو دوبارہ نکالیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(20,56,'20:56','ہم نے فرعون کو اپنی سب ہی نشانیاں دکھائیں مگر وہ جھٹلائے چلا گیا اور نہ مانا');
INSERT INTO "ur_maududi" VALUES(20,57,'20:57','کہنے لگا”اے موسیٰؑ ، کیا تُو ہمارے پاس اِس لیے آیا ہے کہ اپنے جادُو کے زور سے ہم کو ہمارے ملک سے نکال باہر کرے؟ ');
INSERT INTO "ur_maududi" VALUES(20,58,'20:58','اچھا، ہم بھی تیرے مقابلے میں ویساہی جادو لاتے ہیں طے کر لے کب اور کہاں مقابلہ کرنا ہے نہ ہم اِس قرارداد سے پھریں گے نہ تو پھریو کھُلے میدان میں سامنے آ جا"');
INSERT INTO "ur_maududi" VALUES(20,59,'20:59','موسیٰؑ نے کہا”جشن کا دن طے ہوا، اور دن چڑھے لوگ جمع ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(20,60,'20:60','فرعون نے پلٹ کر اپنے سارے ہتھکنڈے جمع کیے اور مقابلے میں آگیا۔ ');
INSERT INTO "ur_maududi" VALUES(20,61,'20:61','موسیٰؑ نے (عین موقع پر گروہِ مقابل کو مخاطب کر کے) کہا”شامت کے مارو، نہ جھُوٹی تہمتیں باندھو اللہ پر، ورنہ وہ ایک سخت عذاب سے تمہارا ستیا ناس کر دے گا۔ جُھوٹ جس نے بھی گھڑا وہ نامراد ہوا۔“');
INSERT INTO "ur_maududi" VALUES(20,62,'20:62','یہ سُن کر اُن کے درمیان اختلافِ رائے ہوگیا اور وہ چُپکے چُپکے باہم مشورہ کرنے لگے۔ ');
INSERT INTO "ur_maududi" VALUES(20,63,'20:63','آخر کار کچھ لوگوں نے کہا کہ” یہ دونوں تو محض جادُو گر ہیں۔ اِن کا مقصد یہ ہے کہ اپنے جادُو کے زور سے تم کو تمہاری زمین سے بے دخل کر دیں اور تمہارے مثالی طریقِ زندگی کا خاتمہ کر دیں۔ ');
INSERT INTO "ur_maududi" VALUES(20,64,'20:64','اپنی ساری تدبیریں آج اکٹھی کر لو اور ایکا کر کے میدان میں آوٴ۔ بس یہ سمجھ لو کہ آج جو غالب رہا وہی جیت گیا۔“');
INSERT INTO "ur_maududi" VALUES(20,65,'20:65','جادُو گر بولے”موسیٰؑ ، تم پھینکتے ہو یا پہلے ہم پھینکیں؟“');
INSERT INTO "ur_maududi" VALUES(20,66,'20:66','موسیٰؑ نے کہا”نہیں، تم ہی پھینکو۔“یکایک اُن کی رسّیاں اور اُن کی لاٹھیاں اُن کے جادُو کے زور سے موسیٰؑ کو دوڑتی ہوئی محسوس ہونے لگیں، ');
INSERT INTO "ur_maududi" VALUES(20,67,'20:67','اور موسیٰؑ اپنے دل میں ڈر گیا۔ ');
INSERT INTO "ur_maududi" VALUES(20,68,'20:68','ہم نے کہا "مت ڈر، تو ہی غالب رہے گا');
INSERT INTO "ur_maududi" VALUES(20,69,'20:69','پھینک جو کچھ تیرے ہاتھ میں ہے، ابھی اِن کی ساری بناوٹی چیزوں کو نِگلے جاتا ہے۔ یہ جو کچھ بنا کر لائے ہیں یہ تو جادُو گر کا فریب ہے، اور جادُوگر کبھی کامیاب نہیں ہو سکتا، خواہ کسی شان سے وہ آئے۔“');
INSERT INTO "ur_maududi" VALUES(20,70,'20:70','آخر کو یہی ہوا کہ سارے جادُو گر سجدے میں گِرا دیے گئے اور پُکار اُٹھے”مان لیا ہم نے ہارونؑ اور موسیٰؑ کے ربّ کو۔“ ');
INSERT INTO "ur_maududi" VALUES(20,71,'20:71','فرعون نے کہا”تم ایمان لے آئے قبل اس کے کہ میں تمہیں اس کی اجازت دیتا؟ معلوم ہوگیا کہ یہ تمہارا گُرو ہے جس نے تمہیں جادُوگری سکھائی تھی۔ اچھا، اب میں تمہارے ہاتھ پاوٴں مخالف سمتوں سے کٹواتا ہوں اور کھجور کے تنوں پر تم کو سُولی دیتا ہوں۔ پھر تمہیں پتہ چل جائے گا کہ ہم دونوں میں سے کس کا عذاب زیادہ سخت اور دیرپا ہے “ (یعنی میں تمہیں زیادہ سخت سزا دے سکتا ہوں یا موسیٰؑ)');
INSERT INTO "ur_maududi" VALUES(20,72,'20:72','جادُوگروں نے جواب دیا”قسم ہے اُس ذات کی جس نے ہمیں پیدا کیا ہے ، یہ ہرگز نہیں ہو سکتا کہ ہم روشن نشانیاں سامنے آجانے کے بعدبھی (صداقت پر)تجھے ترجیح دیں۔ تُوجو کچھ کرنا چاہے کرلے۔ تُو زیادہ سے زیادہ بس اِسی دنیا کی زندگی کا فیصلہ کر سکتا ہے');
INSERT INTO "ur_maududi" VALUES(20,73,'20:73','ہم تو اپنے رب پر ایمان لے آئے تاکہ وہ ہماری خطائیں معاف کر دے اور اس جادوگری سے، جس پر تو نے ہمیں مجبور کیا تھا، درگزر فرمائے اللہ ہی اچھا ہے اور وہی باقی رہنے والا ہے"');
INSERT INTO "ur_maududi" VALUES(20,74,'20:74','حقیقت یہ ہے کہ جو مجرم بن کر اپنے ربّ کے حضور حاضر ہو گا اُس کے لیے جہنّم ہے جس میں وہ نہ جیے گا نہ مرے گا۔ ');
INSERT INTO "ur_maududi" VALUES(20,75,'20:75','اور جو اس کے حضور مومن کی حیثیت سے حاضر ہو گا، جس نے نیک عمل کیے ہوں گے، ایسے سب لوگوں کے لیے بلند درجے ہیں');
INSERT INTO "ur_maududi" VALUES(20,76,'20:76','سدا بہار باغ ہیں جن کے نیچے نہریں بہہ رہی ہوں گی، ان میں وہ ہمیشہ رہیں گے یہ جزا ہے اُس شخص کی جو پاکیزگی اختیار کرے');
INSERT INTO "ur_maududi" VALUES(20,77,'20:77','ہم نے موسیٰؑ پر وحی کی کہ اب راتوں رات میرے بندوں کو لے کر چل پڑ، اور اُن کے لیے سمندر میں سے سُوکھی سڑک بنالے ، تجھے کسی کے تعاقب کا ذرا خوف نہ ہو اور نہ (سمندر کے بیچ سے گزرتے ہوئے)ڈر لگے');
INSERT INTO "ur_maududi" VALUES(20,78,'20:78','پیچھے سے فرعون اپنے لشکر لے کر پہنچا، اور پھر سمندر اُن پر چھا گیا جیسا کہ چھا جانے کا حق تھا۔ ');
INSERT INTO "ur_maududi" VALUES(20,79,'20:79','فرعون نے اپنی قوم کو گمراہ ہی کیا تھا، کوئی صحیح رہنمائی نہیں کی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(20,80,'20:80',' اے بنی اسرائیل، ہم نے تم کو تمہارے دشمن سے نجات دی، اور طور کے دائیں جانب تمہاری حاضری کے لیے وقت مقرر کیا اور تم پر من و سلویٰ اُتارا ۔');
INSERT INTO "ur_maududi" VALUES(20,81,'20:81','کھاؤ ہمارا دیا ہوا پاک رزق اور اسے کھا کر سرکشی نہ کرو، ورنہ تم پر میرا غضب ٹوٹ پڑے گا اور جس پر میرا غضب ٹوٹا وہ پھر گر کر ہی رہا');
INSERT INTO "ur_maududi" VALUES(20,82,'20:82','البتہ جو توبہ کر لےاور ایمان لائے اور نیک عمل کرے، پھر سیدھا چلتا رہے، اُس کے لیے میں بہت درگزر کرنے والا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(20,83,'20:83',' اور کیا چیز تمہیں اپنی قوم سے پہلے لے آئی موسیٰؑ ؟ ');
INSERT INTO "ur_maududi" VALUES(20,84,'20:84','اُس نے عرض کیا " وہ بس میرے پیچھے آ ہی رہے ہیں میں جلدی کر کے تیرے حضور آ گیا ہوں اے میرے رب، تاکہ تو مجھ سے خوش ہو جائے"');
INSERT INTO "ur_maududi" VALUES(20,85,'20:85','فرمایا”اچھا، تو سُنو، ہم نے تمہارے پیچھے تمہاری قوم کو آزمائش میں دال دیا اور سامری نے اُنہیں گمراہ کر ڈالا۔“');
INSERT INTO "ur_maududi" VALUES(20,86,'20:86','موسیٰؑ سخت غصے اور رنج کی حالت میں اپنی قوم کی طرف پلٹا۔ جا کر اُس نے کہا ”اے میری قوم کے لوگو، کیا تمہارے ربّ نے تم سے اچھے وعدے نہیں کیے تھے؟ کیا تمہیں دن لگ گئے ہیں؟ یا تم اپنے ربّ کا غضب ہی اپنے اوپر لانا چاہتے تھے کہ تم نے مجھ سے وعدہ خلافی کی؟“ ');
INSERT INTO "ur_maududi" VALUES(20,87,'20:87','انہوں نے جواب دیا”ہم نے آپ سے وعدہ خلافی کچھ اپنے اختیار سے نہیں کی، معاملہ یہ ہوا کہ لوگوں کے زیورات کے بوجھ سے ہم لَد گئے تھے اور ہم نے بس اُن کو پھینک دیا تھا“ ۔۔۔۔ پھر اِسی طرح سامری نے بھی کچھ ڈالا');
INSERT INTO "ur_maududi" VALUES(20,88,'20:88','اور ان کے لیے ایک بچھڑے کی مورت بنا کر نکال لایا جس میں سے بیل کی سی آواز نکلتی تھی لوگ پکار اٹھے "یہی ہے تمہارا خدا اور موسیٰؑ کا خدا، موسیٰؑ اسے بھول گیا"');
INSERT INTO "ur_maududi" VALUES(20,89,'20:89','کیا وہ نہ دیکھتے تھے کہ نہ وہ اُن کی بات کا جواب دیتا ہے اور نہ ان کے نفع و نقصان کا کچھ اختیار رکھتا ہے؟');
INSERT INTO "ur_maududi" VALUES(20,90,'20:90','ہارونؑ (موسیٰؑ کے آنے سے) پہلے ہی ان سے کہہ چکا تھا کہ "لوگو، تم اِس کی وجہ سے فتنے میں پڑ گئے ہو، تمہارا رب تو رحمٰن ہے، پس تم میری پیروی کرو اور میری بات مانو"');
INSERT INTO "ur_maududi" VALUES(20,91,'20:91','مگر اُنہوں نے اُس سے کہہ دیا کہ”ہم تو اسی کی پرستش کرتے رہیں گے جب تک کہ موسیٰؑ واپس نہ آجائے۔“ ');
INSERT INTO "ur_maududi" VALUES(20,92,'20:92','موسیٰؑ (قوم کو ڈانٹنے کے بعد ہارونؑ کی طرف پلٹا اور) بولا "ہارونؑ، تم نے جب دیکھا تھا کہ یہ گمراہ ہو رہے ہیں');
INSERT INTO "ur_maududi" VALUES(20,93,'20:93','تو کس چیز نے تمہارا ہاتھ پکڑا تھا کہ میرے طریقے پر عمل نہ کرو؟ کیا تم نے میرے حکم کی خلاف ورزی کی؟“ ');
INSERT INTO "ur_maududi" VALUES(20,94,'20:94','ہارونؑ نے جواب دیا”اے میری ماں کے بیٹے، میری ڈاڑھی نہ پکڑ، نہ میرے سر کے بال کھینچ، مجھے اِس بات کا ڈر تھا کہ تُو آکر کہے گا تم نے بنی اسرائیل میں پھُوٹ ڈال دی اور میری بات کا پاس نہ کیا۔“ ');
INSERT INTO "ur_maududi" VALUES(20,95,'20:95','موسیٰؑ نے کہا "اور سامری، تیرا کیا معاملہ ہے؟"');
INSERT INTO "ur_maududi" VALUES(20,96,'20:96','اس نے جواب دیا”میں نے وہ چیز دیکھی جو اِن لوگوں کو نظر نہ آئی، پس میں نے رسُول کے نقشِ قدم سے ایک مٹھی اُٹھا لی اور اُس کو ڈال دیا۔ میرے نفس نے مجھے کچھ ایسا ہی سُجھایا۔“ ');
INSERT INTO "ur_maududi" VALUES(20,97,'20:97','موسیٰؑ نے کہا”اچھا تو جا، اب زندگی بھر تجھے یہی پُکارتے رہنا ہے کہ مجھے نہ چھُونا۔ اور تیرے لیے باز پُرس کا ایک وقت مقرر ہے جو تجھ سے ہر گز نہ ٹلے گا۔ اور دیکھ اپنے اِس خدا کو جس پر تُو ریجھا ہوا تھا، اب ہم اسے جلا ڈالیں گے اور ریزہ ریزہ کر کے دریا میں بہا دیں گے');
INSERT INTO "ur_maududi" VALUES(20,98,'20:98','لوگو، تمہارا خدا تو بس ایک ہی اللہ ہے جس کے سوا کوئی اور خدا نہیں ہے، ہر چیز پر اُس کا علم حاوی ہے"');
INSERT INTO "ur_maududi" VALUES(20,99,'20:99','اے محمدؐ، اس طرح ہم پچھلے گزرے ہوئے حالات کی خبریں تم کو سُناتے ہیں، اور ہم نے خاص اپنے ہاں سے تم کو ایک”ذِکر“ (درسِ نصیحت)عطا کیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(20,100,'20:100','جو کوئی اِس سے منہ موڑے گا وہ قیامت کے دن سخت بار گناہ اٹھائے گا');
INSERT INTO "ur_maududi" VALUES(20,101,'20:101','اور ایسے سب لوگ ہمیشہ اس کے وبال میں گرفتار رہیں گے،اور قیامت کے دن اُن کےلیے (اِس جرم کی ذمّہ داری کا بوجھ)بڑا تکلیف دہ بوجھ ہوگا، ');
INSERT INTO "ur_maududi" VALUES(20,102,'20:102','اُس دن جبکہ صُور پھُونکا جائے گا اور ہم مجرموں کو اِس حال میں گھیر لائیں گے کہ ان کی آنکھیں (دہشت کے مارے)پتھرائی ہوئی ہوں گی، ');
INSERT INTO "ur_maududi" VALUES(20,103,'20:103','آپس میں چُپکےچُپکے کہیں گے کہ دُنیا میں مشکل ہی سے تم نے کوئی دس دن گزارے ہوں گے ');
INSERT INTO "ur_maududi" VALUES(20,104,'20:104',' ہمیں خوب معلوم ہے کہ وہ باتیں کر رہے ہوں گے (ہم یہ بھی جانتے ہیں کہ)اُس وقت ان میں سے جو زیادہ سے زیادہ محتاط اندازہ لگانے والا ہوگا وہ کہے گا کہ نہیں، تمہاری دنیا کی زندگی بس ایک دن کی زندگی تھی');
INSERT INTO "ur_maududi" VALUES(20,105,'20:105',' یہ لوگ تم سے پُوچھتے ہیں کہ آخر اُس دن یہ پہاڑ کہاں چلے جائیں گے؟ کہو کہ میرا ربّ ان کو دُھول بنا کر اُڑا دے گا');
INSERT INTO "ur_maududi" VALUES(20,106,'20:106','اور زمین کو ایسا ہموار چٹیل میدان بنا دے گا');
INSERT INTO "ur_maududi" VALUES(20,107,'20:107','کہ اس میں تم کوئی بَل اور سَلوَٹ نہ دیکھو گے ');
INSERT INTO "ur_maududi" VALUES(20,108,'20:108','اُس روز سب لوگ منادی کی پکار پر سیدھے چلے آئیں گے، کوئی ذرا اکڑ نہ دکھا سکے گا۔ اور آوازیں رحمٰن کے آگے دب جائیں گی، ایک سرسراہٹ کے سوا تم کچھ نہ سُنو گے');
INSERT INTO "ur_maududi" VALUES(20,109,'20:109','اُس روز شفاعت کار گر نہ ہوگی اِلّا یہ کہ کسی کو رحمٰن اس کی اجازت دے اور اس کی بات سُننا پسند کرے ');
INSERT INTO "ur_maududi" VALUES(20,110,'20:110','وہ لوگوں کا اگلا پچھلا سب حال جانتا ہے اور دُوسروں کو اس کا پورا علم نہیں ہے ');
INSERT INTO "ur_maududi" VALUES(20,111,'20:111','لوگوں کے سر اُس حیّ و قیّوم کے آگے جھک جائیں گے نامراد ہوگا جو اُس وقت کسی ظلم کا بار گناہ اٹھائے ہوئے ہو');
INSERT INTO "ur_maududi" VALUES(20,112,'20:112','اور کسی ظلم یا حق تلفی کا خطرہ نہ ہوگا اُس شخص کو جو نیک عمل کرے اور اِس کے ساتھ وہ مومن بھی ہو۔ ');
INSERT INTO "ur_maududi" VALUES(20,113,'20:113','اور اے محمدؐ ، اِسی طرح ہم نے اِسے قرآنِ عربی بنا کر نازل کیا ہے اور اس میں طرح طرح سے تنبیہات کی ہیں شاید کہ یہ لوگ کج روی سے بچیں یا ان میں کچھ ہو کے آثار اِس کی بدولت پیدا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(20,114,'20:114','پس بالا و برتر ہے اللہ ، پادشاہِ حقیقی۔ اور دیکھو، قرآن پڑھنے میں جلدی نہ کیا کرو جب تک کہ تمہاری طرف اُس کی وحی تکمیل کو نہ پہنچ جائے، اور دُعا کرو کہ اے پروردگار مجھے مزید علم عطا کر۔ ');
INSERT INTO "ur_maududi" VALUES(20,115,'20:115',' ہم نے اِس سے پہلے آدمؑ کو ایک حکم دیا تھا، مگر وہ بھُول گیا اور ہم نے اُس میں عزم نہ پایا۔ ');
INSERT INTO "ur_maududi" VALUES(20,116,'20:116','یاد کرو وہ وقت جبکہ ہم نے فرشتوں سے کہا تھا کہ آدمؑ کو سجدہ کرو وہ سب تو سجدہ کر گئے، مگر ایک ابلیس تھا کہ انکار کر بیٹھا');
INSERT INTO "ur_maududi" VALUES(20,117,'20:117','اس پر ہم نے آدمؑ سے کہا کہ ”دیکھو، یہ تمہارا اور تمہاری بیوی کا دشمن ہے ، ایسا نہ ہو کہ یہ تمہیں جنّت سے نکلوا دے اور تم مصیبت میں پڑ جاو');
INSERT INTO "ur_maududi" VALUES(20,118,'20:118','یہاں تو تمہیں یہ آسائشیں حاصل ہیں کہ نہ بھوکے ننگے رہتے ہو');
INSERT INTO "ur_maududi" VALUES(20,119,'20:119','نہ پیاس اور دُھوپ تمہیں ستاتی ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(20,120,'20:120','لیکن شیطان نے اس کو پھُسلایا۔ کہنے لگا ”آدم ، بتاوٴں تمہیں وہ درخت جس سے ابدی زندگی اور لازوال سلطنت حاصل ہوتی ہے؟“ ');
INSERT INTO "ur_maududi" VALUES(20,121,'20:121','آخر کار دونوں(میاں بیوی)اُس درخت کا پھل کھاگئے۔ نتیجہ یہ ہوا کہ فوراً ہی اُن کے ستر ایک دوسرے کے آگے کھُل گئے اور لگے دونوں اپنے آپ کو جنّت کے پتّوں سے ڈھانکنے۔ آدمؑ نے اپنے ربّ کی نافرمانی کی اور راہِ راست سے بھٹک گیا۔ ');
INSERT INTO "ur_maududi" VALUES(20,122,'20:122','پھر اُس کے ربّ نے اُسے برگزیدہ کیا اور اس کی توبہ قبول کر لی اور اسے ہدایت بخشی۔ ');
INSERT INTO "ur_maududi" VALUES(20,123,'20:123','اور فرمایا "تم دونوں (فریق، یعنی انسان اور شیطان) یہاں سے اتر جاؤ تم ایک دُوسرے کے دشمن رہو گے اب اگر میری طرف سے تمہیں کوئی ہدایت پہنچے تو جو کوئی میری اُس ہدایت کی پیروی کرے گا وہ نہ بھٹکے گا نہ بد بختی میں مبتلا ہو گا');
INSERT INTO "ur_maududi" VALUES(20,124,'20:124','اور جو میرے”ذکر“(درسِ نصیحت)سے منہ موڑے گا اُس کے لیے دُنیا میں تنگ زندگی ہوگی اور قیامت کے روز ہم اسے اندھا اُٹھائیں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(20,125,'20:125','وہ کہے گا "پروردگار، دُنیا میں تو میں آنکھوں والا تھا، یہاں مجھے اندھا کیوں اُٹھایا؟"');
INSERT INTO "ur_maududi" VALUES(20,126,'20:126','اللہ تعالیٰ فرمائے گا”ہاں، اِسی طرح تو ہماری آیات کو ، جبکہ وہ تیرے پاس آئی تھیں ، تُو نے بُھلا دیا تھا۔ اُسی طرح آج تُو بھلایا جا رہا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(20,127,'20:127','اِس طرح ہم حد سے گزرنے والے اور اپنے ربّ کی آیات نہ ماننے والے کو (دُنیا میں)بدلہ دیتے ہیں ، اور آخرت کا عذاب زیادہ سخت اور زیادہ دیرپا ہے');
INSERT INTO "ur_maududi" VALUES(20,128,'20:128','پھر کیا اِن لوگوں کو (تاریخ کے اِس سبق سے)کوئی ہدایت نہ ملی کہ اِن سے پہلے کتنی ہی قوموں کو ہم ہلاک کر چکے ہیں جن کی (بربادشدہ)بستیوں میں آج یہ چلتے پھرتے ہیں؟ درحقیقت اِس میں بہت سی نشانیاں ہیں اُن لوگوں کےلیےجو عقلِ سلیم رکھنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(20,129,'20:129','اگر تیرے رب کی طرف سے پہلے ایک بات طے نہ کر دی گئی ہوتی اور مہلت کی ایک مدّت مقرّر نہ کی جا چکی ہوتی تو ضرور اِن کا بھی فیصلہ چکا دیا جاتا');
INSERT INTO "ur_maududi" VALUES(20,130,'20:130','پس اے محمدؐ ، جو باتیں یہ لوگ بناتے ہیں اُن پر صبر کرو، اور اپنےربّ کی حمد و ثنا کے ساتھ اُس کی تسبیح کرو سُورج نکلنے سے پہلے اور غرُوب ہونے سے پہلے، اور رات کے اوقات میں بھی تسبیح کرو اور دن کے کناروں پر بھی، شاید کہ تم راضی ہو جاوٴ۔ ');
INSERT INTO "ur_maududi" VALUES(20,131,'20:131','اور نگاہ اُٹھا کر بھی نہ دیکھو دُنیوی زندگی کی اُس شان و شوکت کو جو ہم نے اِن میں سے مختلف قسم کے لوگوں کو دے رکھی ہے۔وہ تو ہم نے اُنہیں آزمائش میں ڈالنے کے لیے دی ہے، اور تیرے ربّ کا دیا ہوا رزقِ حلال ہی بہتر اور پائندہ تر ہے');
INSERT INTO "ur_maududi" VALUES(20,132,'20:132','اپنے اہل و عیال کو نماز کی تلقین کرو اور خود بھی اُس کے پابند رہو۔ ہم تم سے کوئی رزق نہیں چاہتے، رزق تو ہم ہی تمہیں دے رہے ہیں۔ اور انجام کی بھلائی تقویٰ ہی کے لیے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(20,133,'20:133','وہ کہتے ہیں کہ یہ شخص اپنے ربّ کی طرف سے کوئی نشانی (معجزہ)کیوں نہیں لاتا؟ اور کیا ان کے پاس اگلے صحیفوں کی تمام تعلیمات کا بیانِ واضح نہیں آگیا؟ ');
INSERT INTO "ur_maududi" VALUES(20,134,'20:134','اگر ہم اُس کے آنے سے پہلے اِن کو کسی عذاب سے ہلاک کر دیتے تو پھر یہی لوگ کہتے کہ اے ہمارے پروردگار، تو نے ہمارے پاس کوئی رسول کیوں نہ بھیجا کہ ذلیل و رسوا ہونے سے پہلے ہی ہم تیری آیات کی پیروی اختیار کر لیتے');
INSERT INTO "ur_maududi" VALUES(20,135,'20:135','اے محمدؐ ، اِن سے کہو، ہر ایک انجامِ کار کے انتظار میں ہے، پس اب منتظر رہو، عنقریب تمہیں معلوم ہو جائے گا کہ کون سیدھی راہ چلنے والے ہیں اور کون ہدایت یافتہ ہیں');
INSERT INTO "ur_maududi" VALUES(21,1,'21:1','قریب آگیا ہے لوگوں کے حساب کا وقت ، اور وہ ہیں کہ غفلت میں منہ موڑے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(21,2,'21:2','ان کے پاس جو تازہ نصیحت ہی ان کے ربّ کی طرف سے آتی ہے اُس کو بے تکلف سُنتے ہیں اور کھیل میں پڑے رہتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(21,3,'21:3','دل اُن کے (دوسری ہی فکروں میں)منہمک ہیں۔ اور ظالم آپس میں سرگوشیاں کرتےہیں کہ ”یہ شخص آخر تم جیسا ایک بشر ہی تو ہے، پھر کیا تم آنکھوں دیکھتے جادُو کے پھندے میں پھنس جاوٴ گے؟“ ');
INSERT INTO "ur_maududi" VALUES(21,4,'21:4','رسُولؐ نے کہا ، میرا ربّ ہر اُس بات کو جانتا ہے جو آسمان اور زمین میں کی جائے، وہ سمیع اور علیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(21,5,'21:5','وہ کہتے ہیں ”بلکہ یہ پراگندہ خواب ہیں ، بلکہ یہ اِس کی من گھڑت ہے، بلکہ یہ شخص شاعر ہے۔ ورنہ یہ لائے کوئی نشانی جس طرح پرانے زمانے کے رسُول نشانیوں کے ساتھ بھیجے گئے تھے');
INSERT INTO "ur_maududi" VALUES(21,6,'21:6','حالانکہ ان سے پہلے کوئی بستی بھی جسے ہم نے ہلاک کیا، ایمان نہ لائی۔ اب کیا یہ ایمان لائیں گے؟ ');
INSERT INTO "ur_maududi" VALUES(21,7,'21:7','اور اے محمدؐ ، تم سے پہلے بھی ہم نے انسانوں ہی کو رسُول بنا کر بھیجا تھا جن پر ہم وحی کیا کرتے تھے۔ تم لوگ اگر علم نہیں رکھتے تو اہلِ کتاب سے پوچھ لو۔ ');
INSERT INTO "ur_maududi" VALUES(21,8,'21:8','اُن رسُولوں کو ہم نے کوئی ایسا جسم نہیں دیا تھا کہ وہ کھاتے نہ ہوں، اور نہ وہ سدا جینے والے تھے');
INSERT INTO "ur_maududi" VALUES(21,9,'21:9','پھر دیکھ لو کہ آخر کار ہم نے اُن کے ساتھ اپنے وعدے پُورے کیے، اور اُنہیں اور جس جس کو ہم نے چاہا بچا لیا، اور حد سے گزر جانے والوں کو ہلاک کر دیا۔ ');
INSERT INTO "ur_maududi" VALUES(21,10,'21:10','لوگو، ہم نے تمہاری طرف ایک ایسی کتاب بھیجی ہے جس میں تمہارا ہی ذکر ہے ، کیا تم سمجھتے نہیں ہو؟ ');
INSERT INTO "ur_maududi" VALUES(21,11,'21:11','کتنی ہی ظالم بستیاں ہیں جن کو ہم نے پیس کر رکھ دیا اور اُن کے بعد دُوسری کسی قوم کو اُٹھایا');
INSERT INTO "ur_maududi" VALUES(21,12,'21:12','جب اُن کو ہمارا عذاب محسوس ہوا تو لگے وہاں سے بھاگنے');
INSERT INTO "ur_maududi" VALUES(21,13,'21:13','(کہا گیا)”بھاگو نہیں، جاوٴ اپنے اُنہی گھروں اور عیش کے سامانوں میں جن کے اندر تم چین کر رہے تھے، شاید کہ تم سے پوچھا جائے۔“ ');
INSERT INTO "ur_maududi" VALUES(21,14,'21:14','کہنے لگے "ہائے ہماری کم بختی، بے شک ہم خطا وار تھے"');
INSERT INTO "ur_maududi" VALUES(21,15,'21:15','اور وہ یہی پکارتے رہے یہاں تک کہ ہم نے ان کو کھلیان کر دیا، زندگی کا ایک شرارہ تک ان میں نہ رہا');
INSERT INTO "ur_maududi" VALUES(21,16,'21:16','ہم نے اِس آسمان اور زمین کو اور جو کچھ بھی ان میں ہے کچھ کھیل کے طور پر نہیں بنایا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(21,17,'21:17','اگر ہم کوئی کھلونا بنانا چاہتے اور بس یہی کچھ ہمیں کرنا ہوتا تو اپنے ہی پاس سے کر لیتے۔ ');
INSERT INTO "ur_maududi" VALUES(21,18,'21:18','مگر ہم تو باطل پر حق کی چوٹ لگاتے ہیں جو اس کا سر توڑ دیتی ہے اور وہ دیکھتے دیکھتے مِٹ جا تا ہے اور تمہارے لیے تباہی ہے اُن باتوں کی وجہ سے جو تم بناتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(21,19,'21:19','زمین اور آسمانوں میں جو مخلوق بھی ہے اللہ کی ہے۔ اور جو (فرشتے)اُس کے پاس ہیں وہ نہ اپنے آپ کو بڑا سمجھ کر اُس کی بندگی سے سرتابی کرتے ہیں اور نہ ملُول ہوتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(21,20,'21:20','شب و روز اس کی تسبیح کرتے رہتے ہیں، دَم نہیں لیتے');
INSERT INTO "ur_maududi" VALUES(21,21,'21:21','کیا اِن لوگوں کے بنائے ہوئے ارضی خدا ایسے ہیں کہ (بے جان کو جان بخش کر)اُٹھا کھڑا کرتے ہوں؟ ');
INSERT INTO "ur_maududi" VALUES(21,22,'21:22','اگر آسمان و زمین میں ایک اللہ کے سوا دوسرے خدا بھی ہوتے تو (زمین اور آسمان)دونوں کا نظام بگڑ جاتا۔ پس پاک ہے اللہ ربّ العرش اُن باتوں سے جو یہ لوگ بنا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(21,23,'21:23','وہ اپنے کاموں کے لیے (کسی کے آگے) جواب دہ نہیں ہے اور سب جواب دہ ہیں');
INSERT INTO "ur_maududi" VALUES(21,24,'21:24','کیا اُسے چھوڑ کر انہوں نے دوسرے خدا بنا لیے ہیں؟ اے محمد ؐ ، ان سے کہو کہ ”لاوٴ اپنی دلیل، یہ کتاب بھی موجود ہے جس میں میرے دور کے لوگوں کے لیے نصیحت ہے اور وہ کتابیں بھی موجود ہیں جن میں مجھ سے پہلے لوگوں کے لیے نصیحت تھی۔“ مگر ان میں سے اکثر لوگ حقیقت سے بے خبر ہیں، اس لیے منہ موڑے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(21,25,'21:25','ہم نے تُم سے پہلے جو رسُول بھی بھیجا ہے اُس کو یہی وحی کی ہے کہ میرے سوا کوئی خدا نہیں ہے، پس تم لوگ میری ہی بندگی کرو');
INSERT INTO "ur_maududi" VALUES(21,26,'21:26','یہ کہتے ہیں ”رحمٰن اولاد رکھتا ہے۔“ سُبحان اللہ، وہ تو بندے ہیں جنہیں عزت دی گئی ہے');
INSERT INTO "ur_maududi" VALUES(21,27,'21:27','اُس کے حضور بڑھ کر نہیں بولتے اور بس اُس کے حکم پر عمل کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(21,28,'21:28','جو کچھ اُن کے سامنے ہے اُسے بھی وہ جانتا ہے اور جو کچھ اُن سے اوجھل ہے اُس سے بھی وہ باخبر ہے۔ وہ کسی کی سفارش نہیں کرتے بجز اس کے جس کے حق میں سفارش سُننے پر اللہ راضی ہو، اور وہ اُس کے خوف سے ڈرے رہتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(21,29,'21:29','اور جو اُن میں سے کوئی کہہ دے کہ اللہ کے سوا میں بھی ایک خدا ہوں، تو اُسے ہم جہنّم کی سزا دیں، ہمارے ہاں ظالموں کا یہی بدلہ ہے');
INSERT INTO "ur_maududi" VALUES(21,30,'21:30','کیا وہ لوگ جنہوں نے (نبی کی بات ماننے سے)انکار کر دیا ہے غور نہیں کرتے کہ یہ سب آسمان اور زمین باہم ملے ہوئے تھے، پھر ہم نے انہیں جدا کیا، اور پانی سے ہر زندہ چیز پیدا کی۔ کیا وہ (ہماری اِس خلاقی کو)نہیں مانتے؟');
INSERT INTO "ur_maududi" VALUES(21,31,'21:31','اور ہم نے زمین میں پہاڑ جما دیے تا کہ وہ اِنہیں لے کر ڈھلک نہ جائے، اور اس میں کشادہ راہیں بنا دیں، شاید کہ یہ لوگ اپنا راستہ معلوم کر لیں۔ ');
INSERT INTO "ur_maududi" VALUES(21,32,'21:32','اور ہم نے آسمان کو ایک محفوظ چھت بنا دیا، مگر یہ ہیں کہ اس کی نشانیوں کی طرف توجہ ہی نہیں کرتے');
INSERT INTO "ur_maududi" VALUES(21,33,'21:33','اور وہ اللہ ہی ہے جس نے رات اور دن بنائے اور سُورج اور چاند کو پیدا کیا۔ سب ایک ایک فلک میں تَیر رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(21,34,'21:34',' اور اے محمدؐ ، ہمیشگی تو ہم نے تم سے پہلے بھی کسی انسان کے لیے نہیں رکھی ہے۔ اگر تم مر گئے تو کیا یہ لوگ ہمیشہ جیتے رہیں گے؟');
INSERT INTO "ur_maududi" VALUES(21,35,'21:35','ہر جاندار کو موت کا مزّہ چکھنا ہے، اور ہم اچھے اور بُرے حالات میں ڈال کر تم سب کی آزمائش کر رہے ہیں۔ آخر کار تمہیں ہماری ہی طرف پلٹنا ہے');
INSERT INTO "ur_maududi" VALUES(21,36,'21:36','یہ منکرینِ حق جب تمہیں دیکھتے ہیں تو تمہارا مذاق بنا لیتے ہیں۔ کہتے ہیں”کیا یہ ہے وہ شخص جو تمہارے خداوٴں کا ذکر کیا کرتا ہے؟“ اور ان کا اپنا حال یہ ہے کہ رحمٰن کے ذکر سے منکر ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(21,37,'21:37','انسان جلد باز مخلوق ہے۔ ابھی میں تم کو اپنی نشانیاں دکھائے دیتا ہوں، جلدی نہ مچاوٴ ');
INSERT INTO "ur_maududi" VALUES(21,38,'21:38','یہ لوگ کہتے ہیں "آخر یہ دھمکی پُوری کب ہو گی اگر تم سچے ہو"');
INSERT INTO "ur_maududi" VALUES(21,39,'21:39','کاش اِن کافروں کو اُس وقت کا کچھ علم ہوتا جبکہ یہ نہ اپنے منہ آگ سے بچا سکیں گے نہ اپنی پیٹھیں، اور نہ ان کو کہیں سے مدد پہنچے گی');
INSERT INTO "ur_maududi" VALUES(21,40,'21:40','وہ بلا اچانک آئے گی اور اِنہیں اِس طرح یک لخت دبوچ لے گی کہ یہ نہ اس کو دفع کر سکیں گے اور نہ اِن کو لمحہ بھر مُہلت ہی مل سکے گی');
INSERT INTO "ur_maududi" VALUES(21,41,'21:41','مذاق تم سے پہلے بھی رسُولوں کا اڑایا جا چکا ہے، مگر اُن کا مذاق اڑانے والے اُسی چیز کے پھیر میں آ کر رہے جس کا وہ مذاق اڑاتے تھے');
INSERT INTO "ur_maududi" VALUES(21,42,'21:42','اے محمدؐ ، ان سے کہو ”کون ہے جو رات کو یا دن کو تمہیں رحمٰن سے بچا سکتا ہو؟“ مگر یہ اپنے ربّ کی نصیحت سے منہ موڑ رہے ہیں');
INSERT INTO "ur_maududi" VALUES(21,43,'21:43','کیا یہ کچھ ایسے خدا رکھتے ہیں جو ہمارے مقابلے میں ان کی حمایت کریں؟ وہ نہ تو خود اپنی مدد کر سکتے ہیں اور نہ ہماری ہی تائید اُن کو حاصل ہے');
INSERT INTO "ur_maududi" VALUES(21,44,'21:44','اصل بات یہ ہے کہ ان لوگوں کو اور ان کے آبا و اجداد کو ہم زندگی کا سروسامان دیے چلے گئے یہاں تک کہ ان کو دن لگ گئے۔ مگر کیا انہیں نظر نہیں آتا کہ ہم زمین کو مختلف سمتوں سے گھٹاتے چلے آرہے ہیں؟ پھر کیا یہ غالب آجائیں گے؟ ');
INSERT INTO "ur_maududi" VALUES(21,45,'21:45','اِن سے کہہ دو کہ "میں تو وحی کی بنا پر تمہیں متنبہ کر رہا ہوں" مگر بہرے پکار کو نہیں سنا کرتے جبکہ اُنہیں خبردار کیا جائے');
INSERT INTO "ur_maududi" VALUES(21,46,'21:46','اور اگر تیرے ربّ کا عذاب ذرا سا انہیں چھُو جائے تو ابھی چیخ اُٹھیں کہ ہائے ہماری کم بختی، بے شک ہم خطاوار تھے');
INSERT INTO "ur_maududi" VALUES(21,47,'21:47','قیامت کے روز ہم ٹھیک ٹھیک تولنے والے ترازو رکھ دیں گے ، پھر کسی شخص پر ذرا برابر ظلم نہ ہوگا۔ جس کا رائی کے دانے برابر بھی کچھ کیا دھرا ہو گا ہو ہم سامنے لے آئیں گے۔ اور حساب لگانے کے لیے ہم کافی ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(21,48,'21:48',' پہلے ہم موسیٰؑ اور ہارونؑ کو فرقان اور روشنی اور ”ذکر“ عطا کر چکے ہیں اُن متقی لوگوں کی بھلائی کے لیے ');
INSERT INTO "ur_maududi" VALUES(21,49,'21:49','جو بے دیکھے اپنے ربّ سے ڈریں اور جن کو (حساب کی)اُس گھڑی کا کھٹکا لگا ہوا ہو');
INSERT INTO "ur_maududi" VALUES(21,50,'21:50','اور اب یہ بابرکت "ذکر" ہم نے (تمہارے لیے) نازل کیا ہے پھر کیا تم اِس کو قبول کرنے سے انکاری ہو؟');
INSERT INTO "ur_maududi" VALUES(21,51,'21:51','اُس سے بھی پہلے ہم نے ابراہیمؑ کو اُس کی ہوشمندی بخشی تھی اور ہم اُس کو خوب جانتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(21,52,'21:52',' یاد کرو وہ موقع جب کہ اُس نے اپنے باپ اور اپنی قوم سے کہا تھا کہ ”یہ مورتیں کیسی ہیں جن کے تم لوگ گرویدہ ہو رہے ہو؟“');
INSERT INTO "ur_maududi" VALUES(21,53,'21:53','انہوں نے جواب دیا "ہم نے اپنے باپ دادا کو ان کی عبادت کرتے پایا ہے"');
INSERT INTO "ur_maududi" VALUES(21,54,'21:54','اس نے کہا "تم بھی گمراہ ہو اور تمہارے باپ دادا بھی صریح گمراہی میں پڑے ہوئے تھے؟"');
INSERT INTO "ur_maududi" VALUES(21,55,'21:55','انہوں نے کہا”کیا تُو ہمارے سامنے اپنے اصلی خیالات پیش کر رہا ہے یا مذاق کرتا ہے؟“ ');
INSERT INTO "ur_maududi" VALUES(21,56,'21:56','اُس نے جواب دیا "نہیں، بلکہ فی الواقع تمہارا رب وہی ہے جو زمین اور آسمانوں کا رب اور اُن کا پیدا کرنے والا ہے اِس پر میں تمہارے سامنے گواہی دیتا ہوں');
INSERT INTO "ur_maududi" VALUES(21,57,'21:57','اور خدا کی قسم میں تمہاری غیر موجودگی میں ضرور تمہارے بُتوں کی خبر لوں گا۔“ ');
INSERT INTO "ur_maududi" VALUES(21,58,'21:58','چنانچہ اس نے اُن کو ٹکڑے ٹکڑے کر دیا اور صرف ان کے بڑے کو چھوڑ دیا تاکہ شاید وہ اس کی طرف رُجوع کریں۔ ');
INSERT INTO "ur_maududi" VALUES(21,59,'21:59','(اُنہوں نے آ کر بتوں کا یہ حال دیکھا تو) کہنے لگے "ہمارے خداؤں کا یہ حال کس نے کر دیا؟ بڑا ہی کوئی ظالم تھا وہ"');
INSERT INTO "ur_maududi" VALUES(21,60,'21:60','(بعض لوگ) بولے "ہم نے ایک نوجوان کو ان کا ذکر کرتے سُنا تھا جس کا نام ابراہیمؑ ہے"');
INSERT INTO "ur_maududi" VALUES(21,61,'21:61','اُنہوں نے کہا”تو پکڑ لاوٴ اُسے سب کے سامنے تاکہ لوگ دیکھ لیں(اُس کی کیسی خبر لی جاتی ہے)۔“ ');
INSERT INTO "ur_maududi" VALUES(21,62,'21:62','(ابراہیمؑ کے آنے پر) اُنہوں نے پوچھا "کیوں ابراہیمؑ، تو نے ہمارے خداؤں کے ساتھ یہ حرکت کی ہے؟"');
INSERT INTO "ur_maududi" VALUES(21,63,'21:63','اُس نے جواب دیا”بلکہ یہ سب کچھ اِن کے اس سردار نے کیا ہے، اِن ہی سے پُوچھ لو اگر یہ بولتے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(21,64,'21:64','یہ سُن کر وہ لوگ اپنے ضمیر کی طرف پلٹے اور (اپنے دلوں میں) کہنے لگے "واقعی تم خود ہی ظالم ہو"');
INSERT INTO "ur_maududi" VALUES(21,65,'21:65','مگر پھر اُن کی مت پلٹ گئی اور بولے”تُو جانتا ہے کہ یہ بولتے نہیں ہیں۔“');
INSERT INTO "ur_maududi" VALUES(21,66,'21:66','ابراہیمؑ نے کہا "پھر کیا تم اللہ کو چھوڑ کر اُن چیزوں کو پوج رہے ہو جو نہ تمہیں نفع پہنچانے پر قادر ہیں نہ نقصان');
INSERT INTO "ur_maududi" VALUES(21,67,'21:67','تف ہے تم پر اور تمہارے اِن معبُودوں پر جن کی تم اللہ کو چھوڑ کر پوجا کر رہے ہو کیا تم کچھ بھی عقل نہیں رکھتے؟"');
INSERT INTO "ur_maududi" VALUES(21,68,'21:68','انہوں نے کہا "جلا ڈالو اس کو اور حمایت کرو اپنے خداؤں کی اگر تمہیں کچھ کرنا ہے"');
INSERT INTO "ur_maududi" VALUES(21,69,'21:69','ہم نے کہا”اے آگ، ٹھنڈی ہو جا اور سلامتی بن جا ابراہیمؑ پر۔“ ');
INSERT INTO "ur_maududi" VALUES(21,70,'21:70','وہ چاہتے تھے کہ ابراہیمؑ کے ساتھ بُرائی کریں مگر ہم نے ان کو بُری طرح ناکام کر دیا');
INSERT INTO "ur_maududi" VALUES(21,71,'21:71','اور ہم اُسے اور لوطؑ کو بچا کر اُس سر زمین کی طرف نکال لے گئے جس میں ہم نے دنیا والوں کے لیے برکتیں رکھی ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(21,72,'21:72','اور ہم نے اسے اسحاقؑ عطا کیا اور یعقوبؑ اس پر مزید، اور ہر ایک کو صالح بنایا');
INSERT INTO "ur_maududi" VALUES(21,73,'21:73','اور ہم نے اُن کو امام بنا دیا جو ہمارے حکم سے رہنمائی کرتے تھے۔ اور ہم نے اُنہیں وحی کے ذریعہ نیک کاموں کی اور نماز قائم کرنے اور زکوٰة دینے کی ہدایت کی، اور وہ ہمارے عبادت گزار تھے۔ ');
INSERT INTO "ur_maududi" VALUES(21,74,'21:74','اور لُوطؑ کو ہم نے حکم اور علم بخشا اور اُسے اُس بستی سے بچا کر نکال دیا جو بدکاریاں کرتی تھی۔۔۔۔ درحقیقت وہ بڑی ہی بُری ، فاسق قوم تھی');
INSERT INTO "ur_maududi" VALUES(21,75,'21:75','اور لوطؑ کو ہم نے اپنی رحمت میں داخل کیا، وہ صالح لوگوں میں سے تھا');
INSERT INTO "ur_maududi" VALUES(21,76,'21:76','اور یہی نعمت ہم نے نُوحؑ کو دی۔ یاد کرو جبکہ اِن سب سے پہلے اُس نے ہمیں پکارا تھا۔ ہم نے اس کی دُعا قبول کی اور اسے اور اس کے گھر والوں کو کَربِ عظیم سے نجات دی');
INSERT INTO "ur_maududi" VALUES(21,77,'21:77','اور اُس قوم کے مقابلے میں اُس کی مدد کی جس نے ہماری آیات کو جھٹلا دیا تھا وہ بڑے بُرے لوگ تھے، پس ہم نے ان سب کو غرق کر دیا');
INSERT INTO "ur_maududi" VALUES(21,78,'21:78','اور اِسی نعمت سے ہم نے داؤدؑ اورسلیمانؑ کو سرفراز کیا یاد کرو وہ موقع جبکہ وہ دونوں ایک کھیت کے مقدمے میں فیصلہ کر رہے تھے جس میں رات کے وقت دُوسرے لوگوں کی بکریاں پھیل گئی تھیں، اور ہم اُن کی عدالت خود دیکھ رہے تھے');
INSERT INTO "ur_maududi" VALUES(21,79,'21:79','اُس وقت ہم نے صحیح فیصلہ سلیمان کو سمجھا دیا، حالانکہ حکم اور علم ہم نے دونوں ہی کو عطا کیا تھا۔ داوٴدؑ کے ساتھ ہم نے پہاڑوں اور پرندوں کو مسخر کر دیا تھا جو تسبیح کرتے تھے، اِس فعل کے کرنے والے ہم ہی تھے');
INSERT INTO "ur_maududi" VALUES(21,80,'21:80','اور ہم نے اُس کو تمہارے فائدے کے لیے زرہ بنانے کی صنعت سکھا دی تھی، تاکہ تم کو ایک دُوسرے کی مار سے بچائے، پھر کیا تم شکر گزار ہو؟ ');
INSERT INTO "ur_maududi" VALUES(21,81,'21:81','ور سلیمانؑ کے لیے ہم نے تیز ہوا کو مسخّر کر دیا تھا جو اس کے حکم سے اُس سرزمین کی طرف چلتی تھی جس میں ہم نے برکتیں رکھی ہیں، ہم ہر چیز کا علم رکھنے والے تھے');
INSERT INTO "ur_maududi" VALUES(21,82,'21:82','اور شیاطین میں سے ہم نے ایسے بہت سوں کو اس کا تابع بنا دیا تھا جو اس کے لیے غوطے لگاتے اور اس کے سوا دُوسرے کام کرتے تھے۔ ان سب کے نگراں ہم ہی تھے۔ ');
INSERT INTO "ur_maududi" VALUES(21,83,'21:83','اور یہی (ہوشمندی اور حکم و علم کی نعمت)ہم نے ایوبؑ کو دی تھی۔ یاد کرو، جبکہ اس نے اپنے ربّ کو پکارا کہ ”مجھے بیماری لگ گئی ہے اور تُو ارحم الراحمین ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(21,84,'21:84','ہم نے اس کی دُعا قبول کی اور جو تکلیف اُسے تھی اس کو دُور کر دیا، اور صرف اس کے اہل و عیال ہی اس کو نہیں دیے بلکہ ان کے ساتھ اتنے ہی اور بھی دیے، اپنی خاص رحمت کے طور پر، اور اس لیے کہ یہ ایک سبق ہو عبادت گزاروں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(21,85,'21:85','اور یہی نعمت اسماعیلؑ اور ادریسؑ اور ذوالکفلؑ کو دی کہ یہ سب صابر لوگ تھے');
INSERT INTO "ur_maududi" VALUES(21,86,'21:86','اور ان کو ہم نے اپنی رحمت میں داخل کیا کہ وہ صالحوں میں سے تھے');
INSERT INTO "ur_maududi" VALUES(21,87,'21:87','اور مچھلی والے کو بھی ہم نے نوازا۔ یاد کرو جبکہ وہ بگڑ کر چلا گیا تھا اور سمجھا تھا کہ ہم اس پر گرفت نہ کریں گے۔ آخر کو اُس نے تاریکیوں میں سے پکارا” نہیں ہے کوئی خدا مگر تُو، پاک ہے تیری ذات، بے شک میں نے قُصور کیا۔“');
INSERT INTO "ur_maududi" VALUES(21,88,'21:88','تب ہم نے اس کی دُعا قبول کی اور غم سے اس کو نجات بخشی، اور اِسی طرح ہم مومنوں کو بچا لیا کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(21,89,'21:89','اور زکریّاؑ کو، جبکہ اس نے اپنے رب کو پکارا کہ "اے پروردگار، مجھے اکیلا نہ چھوڑ، اور بہترین وارث تو تُو ہی ہے"');
INSERT INTO "ur_maududi" VALUES(21,90,'21:90','پس ہم نے اس کی دُعا قبول کی اور اسے یحییٰ عطا کیا اور اس کی بیوی کو اس کے لیے درست کر دیا۔ یہ لوگ نیکی کے کاموں میں دوڑ دُھوپ کرتے تھے اور ہمیں رغبت اور خوف کے ساتھ پکارتے تھے، اور ہمارے آگے جُھکے ہوئے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(21,91,'21:91','اور وہ خاتون جس نے اپنی عصمت کی حفاظت کی تھی۔ ہم نے اُس کے اندر اپنی رُوح سے پھُونکا اور اُسے اور اُس کے بیٹے کو دُنیا بھر کے لیے نشانی بنا دیا۔ ');
INSERT INTO "ur_maududi" VALUES(21,92,'21:92','یہ تمہاری امّت حقیقت میں ایک ہی امّت ہے اور میں تمہارا رب ہوں، پس تم میری عبادت کرو');
INSERT INTO "ur_maududi" VALUES(21,93,'21:93','مگر (یہ لوگوں کی کارستانی ہے کہ)انہوں نے آپس میں اپنے دین کو ٹکڑے ٹکڑے کر ڈالا ۔۔۔۔ سب کو ہماری طرف پلٹنا ہے');
INSERT INTO "ur_maududi" VALUES(21,94,'21:94','پھر جو نیک عمل کرے گا، اِس حال میں کہ وہ مومن ہو، تو اس کے کام کی نا قدری نہ ہو گی، اور اُسے ہم لکھ رہے ہیں');
INSERT INTO "ur_maududi" VALUES(21,95,'21:95','اور ممکن نہیں ہے کہ جس بستی کو ہم نے ہلاک کر دیا ہو وہ پلٹ سکے۔ ');
INSERT INTO "ur_maududi" VALUES(21,96,'21:96','یہاں تک کہ جب یاجُوج و ماجُوج کھول دیے جائیں گے اور ہر بلندی سے وہ نکل پڑیں گے');
INSERT INTO "ur_maududi" VALUES(21,97,'21:97','اور وعدہٴِ بر حق کے پُورا ہونے کا وقت قریب آلگے گا تو یکایک اُن لوگوں کے دیدے پھٹے کے پھٹے رہ جائیں گے جنہوں نے کُفر کیا تھا۔ کہیں گے”ہائے ہماری کم بختی، ہم اِس چیز کی طرف سے غفلت میں پڑے ہوئے تھے، بلکہ ہم خطا کار تھے۔“ ');
INSERT INTO "ur_maududi" VALUES(21,98,'21:98','بے شک تم اور تمہارے وہ معبُود جنہیں تم پُوجتے ہو، جہنّم کا ایندھن ہیں، وہیں تم کو جانا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(21,99,'21:99','اگر یہ واقعی خدا ہوتے تو وہاں نہ جاتے۔ اب سب کو ہمیشہ اسی میں رہنا ہے');
INSERT INTO "ur_maududi" VALUES(21,100,'21:100','وہاں وہ پھُنکارے ماریں گے اور حال یہ ہوگا کہ اس میں کان پڑی آواز نہ سُنائی دے گی');
INSERT INTO "ur_maududi" VALUES(21,101,'21:101','رہے وہ لوگ جن کے لیے ہماری طرف سے بھلائی کا پہلے ہی فیصلہ ہو چکا ہو گا، تو وہ یقیناً اُس سے دُور رکھے جائیں گے، ');
INSERT INTO "ur_maududi" VALUES(21,102,'21:102','اس کی سرسراہٹ تک نہ سُنیں گے اور وہ ہمیشہ ہمیشہ اپنی من بھاتی چیزوں کے درمیان رہیں گے');
INSERT INTO "ur_maududi" VALUES(21,103,'21:103','وہ انتہائی گھبراہٹ کا وقت اُن کو ذرا پریشان نہ کرے گا، اور ملائکہ بڑھ کر اُن کو ہاتھوں ہاتھ لیں گے کہ ”یہ تمہارا وہی دن ہے جس کا تم سے وعدہ کیا جاتا تھا۔“');
INSERT INTO "ur_maududi" VALUES(21,104,'21:104','وہ دن جبکہ آسمان کو ہم یوں لپیٹ کر رکھ دیں گے جیسے طومار میں اوراق لپیٹ دیے جاتے ہیں جس طرح پہلے ہم نے تخلیق کی ابتدا کی تھی اُسی طرح ہم پھر اُس کا اعادہ کریں گے یہ ایک وعدہ ہے ہمارے ذمے، اور یہ کام ہمیں بہرحال کرنا ہے');
INSERT INTO "ur_maududi" VALUES(21,105,'21:105','اور زَبور میں ہم نصیحت کے بعد یہ لکھ چکے ہیں کہ زمین کے وارث ہمارے نیک بندے ہوں گے');
INSERT INTO "ur_maududi" VALUES(21,106,'21:106','اِس میں ایک بڑی خبر ہے عبادت گزار لوگوں کےلیے۔ ');
INSERT INTO "ur_maududi" VALUES(21,107,'21:107','اے محمدؐ ، ہم نے جو تم کو بھیجا ہے تو یہ دراصل دنیا والوں کے حق میں ہماری رحمت ہے۔ ');
INSERT INTO "ur_maududi" VALUES(21,108,'21:108','اِن سے کہو "میرے پاس جو وحی آتی ہے وہ یہ ہے کہ تمہارا خدا صرف ایک خدا ہے، پھر کیا تم سرِ اطاعت جھکاتے ہو؟"');
INSERT INTO "ur_maududi" VALUES(21,109,'21:109','اگر ہو منہ پھیریں تو کہہ دو کہ”میں نے علی الاعلان تم کو خبر دار کر دیا ہے۔ اب یہ میں نہیں جانتا کہ وہ چیز جس کا تم سے وعدہ کیا جارہا ہے قریب ہے یا دُور');
INSERT INTO "ur_maududi" VALUES(21,110,'21:110','اللہ وہ باتیں بھی جانتا ہے جو بآواز ِ بلند کہی جاتی ہیں اور وہ بھی جو تم چھُپا کر کرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(21,111,'21:111','میں تو یہ سمجھتا ہوں کہ شاید یہ (دیر)تمہارے لیے ایک فتنہ ہے اور تمہیں ایک وقت ِ خاص تک کے لیے مزے کرنے کا موقع دیا جارہا ہے۔“');
INSERT INTO "ur_maududi" VALUES(21,112,'21:112','(آخرکار) رسولؐ نے کہا کہ "اے میرے رب، حق کے ساتھ فیصلہ کر دے، اور لوگو، تم جو باتیں بناتے ہو اُن کے مقابلے میں ہمارا ربِّ رحمان ہی ہمارے لیے مدد کا سہارا ہے"');
INSERT INTO "ur_maududi" VALUES(22,1,'22:1','لوگو، اپنے ربّ کے غضب سے بچو ، حقیقت یہ ہے کہ قیامت کا زلزلہ بڑی(ہولناک)چیز ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,2,'22:2','جس روز تم اُسے دیکھو گے، حال یہ ہوگا کہ ہر دُودھ پلانے والی اپنے دُودھ پیتے بچے سے غافل ہو جائے گی، ہر حاملہ کا حمل گِر جائے گا، اور لوگ تم کو مدہوش نظر آئیں گے، حالانکہ وہ نشے میں نہ ہوں گے، بلکہ اللہ کا عذاب ہی کچھ ایسا سخت ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(22,3,'22:3','بعض لوگ ایسے ہیں جو عِلم کے بغیر اللہ کے بارے میں بحثیں کرتے ہیں اور ہر شیطان ِ سرکش کی پیروی کرنے لگتے ہیں');
INSERT INTO "ur_maududi" VALUES(22,4,'22:4','حالانکہ اُس کے تو نصیب ہی میں یہ لکھا ہے کہ جو اس کو دوست بنائے گا اسے وہ گمراہ کر کے چھوڑے گا اور عذاب جہنّم کا راستہ دکھائے گا');
INSERT INTO "ur_maududi" VALUES(22,5,'22:5','لوگو، اگر تمہیں زندگی بعدِ موت کے بارے میں کچھ شک ہے تو تمہیں معلوم ہو کہ ہم نے تم کو مٹی سے پیدا کیا ہے، پھر نطفے سے ، پھر خون کے لوتھڑے سے ، پھر گوشت کی بوٹی سے جو شکل والی بھی ہوتی ہے اور بے شکل بھی۔ (یہ ہم اِس لیے بتا رہے ہیں)تاکہ تم پر حقیقت واضح کریں۔ ہم جس (نطفے)کو چاہتے ہیں ایک وقتِ خاص تک رحموں میں ٹھہرائے رکھتے ہیں، پھر تم کو ایک بچے کی صُورت میں نکال لاتے ہیں(پھر تمہیں پرورش کرتے ہیں)تاکہ تم اپنی پُوری جوانی کو پہنچو۔ اور تم میں سے کوئی پہلے ہی واپس بُلا لیا جاتا ہے اور کوئی بدترین عمر کی طرف پھیر دیا جاتا ہے تاکہ سب کچھ جاننے کے بعد پھر کچھ نہ جانے۔ اور تم دیکھتے ہو کہ زمین سُوکھی پڑی ہے، پھر جہاں ہم نے اُس پر مینہ برسایا کہ یکایک وہ پَھبک اُٹھی اور پُھول گئی اور اس نے ہر قسم کی خوش منظر نباتات اُگلنی شروع کر دی');
INSERT INTO "ur_maududi" VALUES(22,6,'22:6','یہ سب کچھ اس وجہ سے ہے کہ اللہ ہی حق ہے، اور وہ مُردوں کو زندہ کرتا ہے ، اور وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_maududi" VALUES(22,7,'22:7','اور یہ (اِس بات کی دلیل ہے)کہ قیامت کی گھڑی آکر رہے گی، اس میں کسی شک کی گنجائش نہیں ، اور اللہ ضرور اُن لوگوں کو اُٹھائے گا جو قبروں میں جاچکے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(22,8,'22:8','بعض اور لوگ ایسے ہیں کہ کسی علم اور ہدایت اور روشنی بخشنے والی کتاب کے بغیر، گردن اکڑائے ہوئے ');
INSERT INTO "ur_maududi" VALUES(22,9,'22:9','خدا کے بارے میں جھگڑتے ہیں تاکہ لوگوں کو راہِ خدا سے بھٹکا دیں۔ ایسے شخص کے لیے دنیا میں رُسوائی ہے اور قیامت کے روز اُس کو ہم آگ کے عذاب کا مزا چکھائیں گے');
INSERT INTO "ur_maududi" VALUES(22,10,'22:10','یہ ہے تیرا وہ مستقبل جو تیرے اپنے ہاتھوں نے تیرے لیے تیار کیا ہے ورنہ اللہ اپنے بندوں پر ظلم کرنے والا نہیں ہے');
INSERT INTO "ur_maududi" VALUES(22,11,'22:11','اور لوگوں میں کوئی ایسا ہے جو کنارے پر رہ کر اللہ کی بندگی کرتا ہے، اگر فائدہ ہوا تو مطمئن ہو گیا اور جو کوئی مصیبت آگئی تو اُلٹا پھر گیا۔ اُس کی دنیا بھی گئی اور آخرت بھی۔ یہ ہے صریح خسارہ۔ ');
INSERT INTO "ur_maududi" VALUES(22,12,'22:12','پھر وہ اللہ کو چھوڑ کر اُن کو پکارتا ہے جو نہ اُس کو نقصان پہنچا سکتے ہیں نہ فائدہ یہ ہے گمراہی کی انتہا');
INSERT INTO "ur_maududi" VALUES(22,13,'22:13','وہ اُن کو پکارتا ہے جن کا نقصان اُن کے نفع سے قریب تر ہے، بدترین ہے اُس کا مولیٰ اور بدترین ہے اُس کا رفیق۔ ');
INSERT INTO "ur_maududi" VALUES(22,14,'22:14','(اِس کے برعکس)اللہ اُن لوگوں کو جو ایمان لائے اور جنہوں نے نیک عمل کیے، یقیناً ایسی جنتّوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہوں گی۔ اللہ کرتا ہے جو کچھ چاہتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,15,'22:15','جو شخص یہ گمان رکھتا ہو کہ اللہ دُنیا اور آخرت میں اُس کی کوئی مدد نہ کر ے گا اُسے چاہیے کہ ایک رسّی کے ذریعے آسمان تک پہنچ کر شگاف لگائے، پھر دیکھ لے کہ آیا اُس کی تدبیر کسی ایسی چیز کو رد کر سکتی ہے جو اس کے ناگوار ہے ');
INSERT INTO "ur_maududi" VALUES(22,16,'22:16','ایسی ہی کھُلی کھُلی باتوں کے ساتھ ہم نے اس قرآن کو نازل کیا ہے، اور ہدایت اللہ جسے چاہتا ہے دیتا ہے');
INSERT INTO "ur_maududi" VALUES(22,17,'22:17','جو لوگ ایمان لائے، اور جو یہوُدی ہوئے، اور صابئی، اور نصاریٰ، اور مجوس ، اور جن لوگوں نے شرک کیا، ان سب کے درمیان اللہ قیامت کے روز فیصلہ کر دے گا، ہر چیز اللہ کی نظر میں ہے');
INSERT INTO "ur_maududi" VALUES(22,18,'22:18','کیا تم دیکھتے نہیں ہو کہ اللہ کے آگے سر بسجُود ہیں وہ سب جو آسمانوں میں ہیں اور جو زمین میں ہیں؟ سُورج اور چاند اور تارے اور پہاڑ اور درخت اور جانور اور بہت سے انسان اور بہت سے وہ لوگ بھی جو عذاب کے مستحق ہو چکے ہیں۔ اور جسے اللہ ذلیل و خوار کردے اُسے پھر کوئی عزّت دینے والا نہیں ہے ، اللہ کرتا ہے جو کچھ چاہتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,19,'22:19','یہ دو فریق ہیں جن کے درمیان اپنے ربّ کے معاملے میں جھگڑا ہے۔ اِن میں سے وہ لوگ جنہوں نے کُفر کیا اُن کے لیے آگ کے لباس کاٹے جا چکے ہیں، اُن کے سروں پر کھَولتا ہوا پانی ڈالا جائے گا');
INSERT INTO "ur_maududi" VALUES(22,20,'22:20','جس سے اُن کی کھالیں ہی نہیں پیٹ کے اندر کے حصے تک گل جائیں گے');
INSERT INTO "ur_maududi" VALUES(22,21,'22:21','اور اُن کی خبر لینے کے لیے لوہے کے گُرز ہوں گے');
INSERT INTO "ur_maududi" VALUES(22,22,'22:22','جب کبھی وہ گھبرا کر جہنّم سے نکلنے کی کوشش کریں گے پھر اُسی میں دھکیل دیے جائیں گے کہ چکھو اب جلنے کی سزا کا مزا');
INSERT INTO "ur_maududi" VALUES(22,23,'22:23','(دُوسری طرف)جو لوگ ایمان لائے اور جنہوں نے نیک عمل کیے اُن کو اللہ ایسی جنتّوں میں داخل کرے گا جِن کے نیچے نہریں بہ رہی ہوں گی۔ وہاں وہ سونے کے کنگنوں اور موتیوں سے آراستہ کیے جائیں گے اور ان کے لباس ریشم کے ہوں گے');
INSERT INTO "ur_maududi" VALUES(22,24,'22:24','ان کو پاکیزہ بات قبول کرنے کی ہدایت بخشی گئی اور انھیں خدائے ستودہ صفات کا راتہ دکھایا گیا۔ ');
INSERT INTO "ur_maududi" VALUES(22,25,'22:25','جن لوگوں نے کُفر کیا اور جو (آج)اللہ کے راستے سے روک رہے ہیں اور اُس مسجدِ حرام کی زیارت میں مانع ہیں جسے ہم نے سب لوگوں کے لیے بنایا ہے، جس میں مقامی باشندوں اور باہر سے آنے والوں کے حقوق برابر ہیں (اُن کی روِش یقیناً سزا کی مستحق ہے)۔ اِس (مسجدِ حرام)میں جو بھی راستی سے ہٹ کر ظلم کا طریقہ اختیار کرے گا اسے ہم دردناک عذاب کا مزا چکھائیں گے');
INSERT INTO "ur_maududi" VALUES(22,26,'22:26','یاد کرو وہ وقت جبکہ ہم نے ابراہیمؑ کے لیے اِس گھر (خانہ کعبہ)کی جگہ تجویز کی تھی اِس ہدایت کے ساتھ)کہ ”میرے ساتھ کسی چیز کو شریک نہ کرو، اور میرے گھر کو طواف کرنے والوں اور قیام و رکوع و سجود کرنے والوں کے لیے پاک رکھو، ');
INSERT INTO "ur_maududi" VALUES(22,27,'22:27','اور لوگوں کو حج کے لیے اِذنِ عام دے دو کہ وہ تمہارے پاس ہر دُور دراز مقام سے پیدل اور اُونٹوں پر سوار آئیں، ');
INSERT INTO "ur_maududi" VALUES(22,28,'22:28','تاکہ وہ فائدے دیکھیں جو یہاں اُن کے لیے رکھے گئے ہیں، اور چند مقرر دنوں میں اُن جانوروں پر اللہ کا نام لیں جو اُس نے انھیں بخشے ہیں، خود بھی کھائیں اور تنگ دست محتاج کو بھی دیں، ');
INSERT INTO "ur_maududi" VALUES(22,29,'22:29','پھر اپنا مَیل کچَیل دُور کریں اور اپنی نذریں پُوری کریں، اور اس قدیم گھر کا طواف کریں۔ ');
INSERT INTO "ur_maududi" VALUES(22,30,'22:30','یہ تھا (تعمیرِ کعبہ کا مقصد)اور جو کوئی اللہ کی قائم کردہ حُرمتوں کا احترام کرے تو یہ اس کے ربّ کے نزدیک خود اسی کے لیے بہتر ہے۔ اور تمہارے لیے مویشی جانور حلال کیے گئے، ماسوا اُن چیزوں کے جو تمہیں بتائی جاچکی ہیں۔ پس بُتوں کی گندگی سے بچو، جھُوٹی باتوں سے پرہیز کرو، ');
INSERT INTO "ur_maududi" VALUES(22,31,'22:31','یکسُو ہو کر اللہ کے بندے بنو، اس کے ساتھ کسی کو شریک نہ کرو۔ اور جو کوئی اللہ کے ساتھ شرک کرے تو گویا وہ آسمان سے گِر گیا، اب یا تو اُسے پرندے اُچک لے جائیں گے یا ہوا اُس کو ایسی جگہ لے جاکر پھینک دے گی جہاں اُس کے چیتھڑے اُڑ جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(22,32,'22:32','یہ ہے اصل معاملہ(اِسے سمجھ لو)اور جو اللہ کے مقرر کردہ شعائر کا احترام کرے تو یہ دِلوں کے تقویٰ سے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,33,'22:33','تمہیں ایک وقتِ مقرر تک اُن (ہدی کے جانوروں)سے فائدہ اُٹھانے کا حق ہے، پھر اُن (کے قربان کرنے)کی جگہ اسی قدیم گھر کے پاس ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,34,'22:34','ہر اُمّت کے لیے ہم نے قربانی کا ایک قاعدہ مقرر کر دیا ہے تاکہ (اُس اُمّت کے)لوگ اُن جانوروں پر اللہ کا نام لیں جو اُس نے ان کو بخشے ہیں۔ (اِن مختلف طریقوں کے اندر مقصد ایک ہی ہے)پس تمہارا خدا ایک ہی خدا ہے اور اُسی کے تم مطیعِ فرمان بنو۔ اور اے نبیؐ ، بشارت دے دےعاجزانہ روِش اختیار کرنے والوں کو، ');
INSERT INTO "ur_maududi" VALUES(22,35,'22:35','جن کا حال یہ ہے کہ اللہ کا ذکر سُنتے ہیں تو ان کے دل کانپ اُٹھتے ہیں، جو مصیبت بھی اُن پر آتی ہے اُس پر صبر کرتے ہیں، نماز قائم کرتے ہیں، اور جو کچھ رزق ہم نے اُن کو دیا ہے اُس میں سے خرچ کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(22,36,'22:36','اور (قربانی کے)اُونٹوں کو ہم نے تمہارے لیے شعائر اللہ میں شامل کیا ہے ، تمہارے لیے اُن میں بھلائی ہے ، پس انھیں کھڑا کر کے ان پر اللہ کا نام لو، اور جب (قربانی کے بعد)ان کی پیٹھیں زمین پر ٹِک جائیں تو اُن میں سے خود بھی کھاوٴ اور اُن کو بھی کھِلاوٴ جو قناعت کیے بیٹھے ہیں اور اُن کو بھی جو اپنی حاجت پیش کریں۔ اِن جانوروں کو ہم نے اِس طرح تمہارے لیے مسخّر کیا تاکہ تم شکریہ ادا کرو۔ ');
INSERT INTO "ur_maududi" VALUES(22,37,'22:37','نہ اُن کے گوشت اللہ کو پہنچتے ہیں نہ خون، مگر اُسے تمہارا تقویٰ پہنچتا ہے۔ اُس نے ان کو تمہارے لیے اِس طرح مسخّر کیا ہے تاکہ اُس کی بخشی ہوئی ہدایت پر تم اُس کی تکبیر کرو۔ اور اے نبیؐ ، بشارت دے دے نیکو کار لوگوں کو');
INSERT INTO "ur_maududi" VALUES(22,38,'22:38',' یقیناً اللہ مدافعت کرتا ہے اُن لوگوں کی طرف سے جو ایمان لائے ہیں۔ یقیناً اللہ کسی خائن کافرِ نعمت کو پسند نہیں کرتا۔ ');
INSERT INTO "ur_maududi" VALUES(22,39,'22:39','اجازت دے دی گئی اُن لوگوں کو جن کے خلاف جنگ کی جارہی ہے، کیونکہ وہ مظلوم ہیں ، اور اللہ یقیناً ان کی مدد پر قادر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,40,'22:40','یہ وہ لوگ ہیں جو اپنے گھروں سے ناحق نکال دیے گئے صرف اِس قصُور پر کہ وہ کہتے تھے”ہمارا ربّ اللہ ہے۔“ اگر اللہ لوگوں کو ایک دوسرے کے ذریعے دفع نہ کرتا رہے تو خانقاہیں اور گرجا اور معبد اور مسجدیں، جن میں اللہ کا کثرت سے نام لیا جاتا ہے ، سب مِسمار کر ڈالی جائیں۔ اللہ ضرور اُن لوگوں کی مدد کرے گا جو اس کی مدد کریں گے۔ اللہ بڑا طاقتور اور زبر دست ہے');
INSERT INTO "ur_maududi" VALUES(22,41,'22:41','یہ وہ لوگ ہیں جنہیں اگر ہم زمین میں اقتدار بخشیں تو وہ نماز قائم کریں گے، زکوٰة دیں گے، معرُوف کا حکم دیں گے اور مُنکَر سے منع کریں گے۔ اور تمام معاملات کا انجامِ کار اللہ کے ہاتھ میں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,42,'22:42','اے نبیؐ ، اگر وہ تمہیں جھُٹلاتے ہیں تو اُن سے پہلے قومِ نوحؐ اور عاد اور ثمود');
INSERT INTO "ur_maududi" VALUES(22,43,'22:43','اور قوم ابراہیمؑ اور قوم لوطؑ');
INSERT INTO "ur_maududi" VALUES(22,44,'22:44','اور اہلِ مَدیَن بھی جھُٹلا چکے ہیں اور موسیٰؑ بھی جھٹلائے جا چکے ہیں۔ ان سب منکرینِ حق کو میں نے پہلے مہلت دی، پھر پکڑ لیا۔ اب دیکھ لو کہ میری عقوبت کیسی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(22,45,'22:45','کتنی ہی خطا کار بستیاں ہیں جن کو ہم نے تباہ کیا ہے اور آج وہ اپنی چھتوں پر اُلٹی پڑی ہیں، کتنے ہی کنوئیں بےکار اور کتنے ہی قصر کھنڈر بنے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(22,46,'22:46','کیا یہ لوگ زمین میں چلے پھرے نہیں ہیں کہ اِن کے دل سمجھنے والے اور اِن کے کان سُننے والے ہوتے؟ حقیقت یہ ہے کہ آنکھیں اندھی نہیں ہوتیں مگر وہ دل اندھے ہو جاتے ہیں جو سینوں میں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(22,47,'22:47','ہ لوگ عذاب کے لیے جلدی مچا رہے ہیں۔ اللہ ہر گز اپنے وعدے کے خلاف نہ کرے گا، مگر تیرے ربّ کے ہاں کا ایک دن تمہارے شمار کے ہزار برس کے برابر ہوا کرتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,48,'22:48','کتنی ہی بستیاں ہیں جو ظالم تھیں، میں نے ان کو پہلے مہلت دی، پھر پکڑ لیا اور سب کو واپس تو میرے پاس ہی آنا ہے');
INSERT INTO "ur_maududi" VALUES(22,49,'22:49','اے محمدؐ ، کہہ دو کہ”لوگو، میں تو تمہارے لیے صرف وہ شخص ہوں جو (بُرا وقت آنے سے پہلے)صاف صاف خبردار کر دینے والا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(22,50,'22:50','پھر جو ایمان لائیں گے اور نیک عمل کریں گے اُں کے لیے مغفرت ہے اور عزّت کی روزی۔ ');
INSERT INTO "ur_maududi" VALUES(22,51,'22:51','اور جو ہماری آیات کو نیچا دکھانے کی کوشش کریں گے وہ دوزخ کے یار ہیں');
INSERT INTO "ur_maududi" VALUES(22,52,'22:52','اور اے محمدؐ ، تم سے پہلےہم نے نہ کوئی رسُول ایسا بھیجا ہے نہ نبی (جس کے ساتھ یہ معاملہ نہ پیش آیا ہو کہ)جب اُس نے تمنا کی ، شیطان اس کی تمنّا میں خلل انداز ہوگیا۔ اِس طرح جو کچھ بھی شیطان خلل اندازیاں کرتا ہے اللہ ان کو مٹا دیتا ہے اور اپنی آیات کو پختہ کر دیتا ہے، اللہ علیم ہے اور حکیم۔ ');
INSERT INTO "ur_maududi" VALUES(22,53,'22:53','(وہ اس لیے ایسا ہونے دیتا ہے) تاکہ شیطان کی ڈالی ہُوئی خرابی کو فتنہ بنا دے اُن لوگوں کے لیے جن کے دلوں کو (نفاق کا) روگ لگا ہُوا ہے اور جن کے دل کھوٹے ہیں حقیقت یہ ہے کہ یہ ظالم لوگ عناد میں بہت دور نکل گئے ہیں');
INSERT INTO "ur_maududi" VALUES(22,54,'22:54','اور علم سے بہرہ مند لوگ جان لیں کہ یہ حق ہے تیرے ربّ کی طرف سے اور وہ اس پر ایمان لے آئیں اور ان کے دل اس کے آگے جُھک جائیں، یقیناً اللہ ایمان لانے والوں کو ہمیشہ سیدھا راستہ دکھا دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,55,'22:55','انکار کرنے والے تو اس کی طرف سے شک ہی میں پڑے رہیں گے یہاں تک کہ یا تو اُن پر قیامت کی گھڑی اچانک آجائے، یا ایک منحوس دن کا عذاب نازل ہوجائے');
INSERT INTO "ur_maududi" VALUES(22,56,'22:56','اُس روز بادشاہی اللہ کی ہو گی، اور وہ ان کے درمیان فیصلہ کر دے گا جو ایمان رکھنے والے اور عمل صالح کرنے والے ہوں گے وہ نعمت بھری جنتوں میں جائیں گے');
INSERT INTO "ur_maududi" VALUES(22,57,'22:57','اور جنہوں نے کفر کیا ہو گا اور ہماری آیات کو جھٹلایا ہوگا اُن کے لیے رسوا کن عذاب ہوگا');
INSERT INTO "ur_maududi" VALUES(22,58,'22:58','اور جن لوگوں نے اللہ کی راہ میں ہجرت کی، پھر قتل کر دیے گئے یا مر گئے، اللہ ان کو اچھّا رزق دے گا اور یقیناً اللہ ہی بہترین رازق ہے');
INSERT INTO "ur_maududi" VALUES(22,59,'22:59','وہ اُنہیں ایسی جگہ پہنچائے گا جس سے وہ خوش ہوجائیں گے۔ بے شک اللہ علیم اور حلیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,60,'22:60','یہ تو ہے اُن کا حال، اور جو کوئی بدلہ لے ، ویسا ہی جیسا اُس کے ساتھ کیا گیا، اور پھر اس پر زیادتی بھی کی گئی ہو، تو اللہ اس کی مدد ضرور کرے گا۔ اللہ معاف کرنے والا اور درگزر کرنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,61,'22:61',' یہ اس لیے کہ رات سے دن اور دن سے رات نکالنے والا اللہ ہی ہے اور وہ سمیع و بصیر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,62,'22:62','یہ اِس لیے کہ اللہ ہی حق ہے اور سب باطل ہیں جنہیں اللہ کو چھوڑ کر یہ لوگ پکارتے ہیں اور اللہ ہی بالادست اور بزرگ ہے');
INSERT INTO "ur_maududi" VALUES(22,63,'22:63','کیا تم دیکھتے نہیں ہو کہ اللہ آسمان سے پانی برساتا ہے اور اس کی بدولت زمین سرسبز ہو جاتی ہے؟ حقیقت یہ ہے کہ وہ لطیف و خبیر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,64,'22:64','اُسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے۔ بے شک وہی غنی و حمید ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,65,'22:65','کیا تم دیکھتے نہیں ہو کہ اُس نے وہ سب کچھ تمہارے لیے مسخّر کر رکھا ہے جو زمین میں ہے ، اور اُسی نے کشتی کو قاعدے کا پابند بنایا ہے کہ وہ اُس کے حکم سے سمندر میں چلتی ہے، اور وہی آسمان کو اس طرح تھامے ہوئے ہے کہ اُس کے اِذن کے بغیر وہ زمین پر نہیں گِر سکتا؟ واقعہ یہ ہے کہ اللہ لوگوں کے حق میں بڑا شفیق اور رحیم ہے');
INSERT INTO "ur_maududi" VALUES(22,66,'22:66','وہی ہے جس نے تمہیں زندگی بخشی ہے ، وہی تم کو موت دیتا ہے اور وہی پھر تم کو زندہ کرے گا۔ سچ یہ ہے کہ انسان بڑا ہی منکرِ حق ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,67,'22:67','ہر اُمّت کے لیے ہم نے ایک طریقِ عبادت مقرر کیا ہے جس کی وہ پیروی کرتی ہے، پس اے محمدؐ ، وہ اِس معاملے میں تم سے جھگڑا نہ کریں۔ تم اپنے ربّ کی طرف دعوت دو، یقیناً تم سیدھے راستے پر ہو۔ ');
INSERT INTO "ur_maududi" VALUES(22,68,'22:68','اور اگر وہ تم سے جھگڑیں تو کہہ دو کہ "جو کچھ تم کر رہے ہو اللہ کو خوب معلوم ہے');
INSERT INTO "ur_maududi" VALUES(22,69,'22:69','اللہ قیامت کے روز تمہارے درمیان ان سب باتوں کا فیصلہ کر دے گا جن میں تم اختلاف کرتے رہے ہو"');
INSERT INTO "ur_maududi" VALUES(22,70,'22:70','کیا تم نہیں جانتے کہ آسمان و زمین کی ہر چیز اللہ کے علم میں ہے؟ سب کچھ ایک کتاب میں درج ہے۔ اللہ کے لیے یہ کچھ بھی مشکل نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,71,'22:71','ہ لوگ اللہ کو چھوڑ کر ان کی عبادت کر رہے ہیں جن کے لیے نہ تو اُس نے کوئی سَنَد نازل کی ہے اور نہ یہ خود اُن کے بارے میں کوئی علم رکھتے ہیں۔ اِن ظالموں کے لیے کوئی مددگار نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(22,72,'22:72','اور جب اِن کو ہماری صاف صاف آیات سُنائی جاتیں ہیں تو تم دیکھتے ہو کہ منکرینِ حق کے چہرے بگڑنے لگتے ہیں، اور ایسا محسوس ہوتا ہے ، کہ ابھی وہ اُن لوگوں پر ٹُوٹ پڑیں گے جو انھیں ہماری آیات سُناتے ہیں۔اِن سے کہو”میں بتاوٴں تمہیں کہ اِس سے بد تر چیز کیا ہے؟ آگ، اللہ نے اسی کا وعدہ ان لوگوں کے حق میں کر رکھا ہے، جو قبولِ حق سے انکار کریں، اور بہت ہی بُرا ٹھکانہ ہے۔“');
INSERT INTO "ur_maududi" VALUES(22,73,'22:73','لوگو، ایک مثال دی جاتی ہے، غور سے سُنو۔ جن معبودوں کو تم خدا کو چھوڑ کر پکارتے ہو وہ سب مل کر ایک مکھی بھی پیدا کرنا چاہیں تو نہیں کر سکتے۔ بلکہ اگر مکھی ان سے کوئی چیز چھین لے جائے تو وہ اسے چھُڑا بھی نہیں سکتے۔ مدد چاہنے والے بھی کمزور اور جن سے مدد چاہی جاتی ہے وہ بھی کمزور۔ ');
INSERT INTO "ur_maududi" VALUES(22,74,'22:74','اِن لوگوں نے اللہ کی قدر ہی نہ پہچانی جیسا کہ اس کے پہچاننے کا حق ہے واقعہ یہ ہے کہ قوت اور عزت والا تو اللہ ہی ہے');
INSERT INTO "ur_maududi" VALUES(22,75,'22:75','حقیقت یہ ہے کہ اللہ (اپنے فرامین کی ترسیل کے لیے)ملائکہ میں سے بھی پیغام رساں منتخب کرتا ہے، اور انسانوں میں سے بھی۔ وہ سمیع اور بصیر ہے');
INSERT INTO "ur_maududi" VALUES(22,76,'22:76','جو کچھ ان کے سامنے ہے اُسے بھی وہ جانتا ہے اور جو کچھ ان سے اوجھل ہے اُس سے بھی وہ واقف ہے ، اور سارے معاملات اُسی کی طرف رجوع ہوتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(22,77,'22:77','اے لوگو جو ایمان لائے ہو، رکوع اورسجدہ کرو، اپنے ربّ کی بندگی کرو، اور نیک کام کرو، شاید کہ تم کو فلاح نصیب ہو۔ ');
INSERT INTO "ur_maududi" VALUES(22,78,'22:78','اللہ کی راہ میں جہاد کرو جیسا کہ جہاد کرنے کا حق ہے۔ اُس نے تمہیں اپنے کام کےلیے چُن لیا ہے اور دین میں تم پر کوئی تنگی نہیں رکھی۔ قائم ہو جاوٴ اپنے باپ ابراہیمؑ کی ملّت پر ۔ اللہ نے پہلے بھی تمہارا نام ”مسلم“ رکھا تھا اور اس (قرآن)میں بھی (تمہارا یہی نام ہے )۔ تاکہ رسولؐ تم پر گواہ ہو اور تم لوگوں پر گواہ۔ پس نماز قائم کرو ، زکوٰة دو، اور اللہ سے وابستہ ہو جاوٴ۔ وہ ہے تمہارا مولیٰ، بہت ہی اچھا ہے وہ مولیٰ اور بہت ہی اچھا ہے وہ مددگار');
INSERT INTO "ur_maududi" VALUES(23,1,'23:1','یقیناً فلاح پائی ہے ایمان لانے والوں نے جو');
INSERT INTO "ur_maududi" VALUES(23,2,'23:2',' اپنی نماز میں خشوع اختیار کرتے ہوں');
INSERT INTO "ur_maududi" VALUES(23,3,'23:3','لغویات سے دُور رہتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(23,4,'23:4','زکوٰة کے طریقے پر عامل ہوتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(23,5,'23:5','اپنی شرمگاہوں کی حفاظت کرتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(23,6,'23:6','سوائے اپنی بیویوں کے اور ان عورتوں کے جو ان کی ملک یمین میں ہوں کہ ان پر (محفوظ نہ رکھنے میں) وہ قابل ملامت نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(23,7,'23:7','البتہ جو اُس کے علاوہ کچھ اور چاہیں وہی زیادتی کرنے والے ہیں، ');
INSERT INTO "ur_maududi" VALUES(23,8,'23:8','اپنی امانتوں اور اپنے عہد و پیمان کا پاس رکھتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(23,9,'23:9','اور اپنی نمازوں کی محافظت کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(23,10,'23:10','یہی لوگ وہ وارث ہیں');
INSERT INTO "ur_maududi" VALUES(23,11,'23:11','جو میراث میں فردوس پائیں گے اور اس میں ہمیشہ رہیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(23,12,'23:12','ہم نے انسان کو مٹی کے ست سے بنایا');
INSERT INTO "ur_maududi" VALUES(23,13,'23:13','پھر اسے ایک محفوظ جگہ ٹپکی ہوئی بوند میں تبدیل کیا');
INSERT INTO "ur_maududi" VALUES(23,14,'23:14',' پھر اس بوند کو لوتھڑے کی شکل دی، پھر لوتھڑے کو بوٹی بنا دیا، پھر بوٹی کو ہڈیاں بنائیں، پھر ہڈیوں پر گوشت چڑھایا، پھر اسے ایک دُوسری ہی مخلوق بنا کھڑا کیا۔ پس بڑا ہی بابرکت ہے اللہ، سب کاریگروں سے اچھا کاریگر ');
INSERT INTO "ur_maududi" VALUES(23,15,'23:15','پھر اس کے بعد تم کو ضرور مرنا ہے،');
INSERT INTO "ur_maududi" VALUES(23,16,'23:16','پھر قیامت کے روز یقیناً تم اٹھائے جاؤ گے');
INSERT INTO "ur_maududi" VALUES(23,17,'23:17','اور تمہارے اُوپر ہم نے سات راستے بنائے، تخلیق کے کام سے ہم کچھ نابلد نہ تھے۔ ');
INSERT INTO "ur_maududi" VALUES(23,18,'23:18','اور آسمان سے ہم نے ٹھیک حساب کے مطابق ایک خاص مقدار میں پانی اُتارا اور اس کو زمین میں ٹھہرا دیا، ہم اُسے جس طرح چاہیں غائب کر سکتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(23,19,'23:19','پھر اس پانی کے ذریعہ سے ہم نے تمہارے لیے کھجُور اور انگور کے باغ پیدا کر دیے، تمہارے لیے ان باغوں میں بہت سے لذیز پھل ہیں اور ان سے تم روزی حاصل کرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(23,20,'23:20','اور وہ درخت بھی ہم نے پیدا کیا جو طوُرِ سیناء سے نکلتا ہے، تیل بھی لیے ہوئے اُگتا ہے اور کھانے والوں کے لیے سالن بھی');
INSERT INTO "ur_maududi" VALUES(23,21,'23:21','اور حقیقت یہ ہے کہ تمہارے لیے مویشیوں میں بھی ایک سبق ہے۔ ان کے پیٹوں میں جو کچھ ہے اسی میں سے ایک چیز ہم تمہیں پلاتے ہیں، اور تمہارے لیے ان میں بہت سے دُوسرے فائدے بھی ہیں');
INSERT INTO "ur_maududi" VALUES(23,22,'23:22','اُن کو تم کھاتے ہو اور اُن پر اور کشتیوں پر سوار بھی کیے جاتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(23,23,'23:23','ہم نے نوحؑ کو اس کی قوم کی طرف بھیجا۔ اس نے کہا”اے میری قوم کے لوگو، اللہ کی بندگی کرو، اس کے سوا تمہارے لیے کوئی معبُود نہیں ہے، کیا تم ڈرتے نہیں ہو؟“ ');
INSERT INTO "ur_maududi" VALUES(23,24,'23:24','اس کی قوم کے جن سرداروں نے ماننے سے انکار کیا وہ کہنے لگو کہ”یہ شخص کچھ نہیں ہے مگر ایک بشر تم ہی جیسا۔ اِس کی غرض یہ ہے کہ تم پر برتری حاصل کرے۔ اللہ کو اگر بھیجنا ہوتا تو فرشتے بھیجتا۔ یہ بات تو ہم نے کبھی اپنے باپ دادا کے وقتوں میں سُنی ہی نہیں (کہ بشر رسُول بن کر آئے)');
INSERT INTO "ur_maududi" VALUES(23,25,'23:25','کچھ نہیں، بس اس آدمی کو ذرا جنون لاحق ہو گیا ہے کچھ مدت اور دیکھ لو (شاید افاقہ ہو جائے)"');
INSERT INTO "ur_maududi" VALUES(23,26,'23:26','نوحؑ نے کہا”پروردگار، اِن لوگوں نے جو میری تکذیب کی ہے اس پر اب تُو ہی میری نُصرت فرما۔“ ');
INSERT INTO "ur_maududi" VALUES(23,27,'23:27','ہم نے اس پر وحی کی کہ”ہماری نگرانی میں اور ہماری وحی کے مطابق کشتی تیار کر۔ پھر جب ہمارا حکم آجائے اور تنُور اُبل پڑے تو ہر قسم کے جانوروں میں سے ایک ایک جوڑا لے کر اس میں سوار ہو جا، اور اپنے اہل و عیال کو بھی ساتھ لے سوائے اُن کے جن کے خلاف پہلے فیصلہ ہو چکا ہے، اور ظالموں کے معاملہ میں مجھ سے کچھ نہ کہنا، یہ اب غرق ہونے والے ہیں');
INSERT INTO "ur_maududi" VALUES(23,28,'23:28','پھر جب تُو اپنے ساتھیوں سمیت کشتی پر سوار ہو جائے تو کہہ، شکر ہے اُس خدا کا جس نے ہمیں ظالم لوگوں سے نجات دی۔ ');
INSERT INTO "ur_maududi" VALUES(23,29,'23:29','اور کہہ، پروردگار ، مجھ کو برکت والی جگہ اُتار اور تُو بہترین جگہ دینے والا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(23,30,'23:30','اِس قصے میں بڑی نشانیاں ہیں ، اور آزمائش تو ہم کر کے ہی رہتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(23,31,'23:31','ان کے بعد ہم نے ایک دُوسرے دور کی قوم اُٹھائی۔ ');
INSERT INTO "ur_maududi" VALUES(23,32,'23:32','پھر اُن میں خود انہی کی قوم کا ایک رسول بھیجا (جس نے انہیں دعوت دی) کہ اللہ کی بندگی کرو، تمہارے لیے اُس کے سوا کوئی اور معبود نہیں ہے، کیا تم ڈرتے نہیں ہو؟');
INSERT INTO "ur_maududi" VALUES(23,33,'23:33','اُس کی قوم کے جن سرداروں نے ماننے سے انکار کیا اور آخرت کی پیشی کو جھُٹلایا، جن کو ہم نے دنیا کی زندگی میں آسُودہ کر رکھا تھا، وہ کہنے لگو”یہ شخص کچھ نہیں ہے مگر ایک بشر تم ہی جیسا۔ جو کچھ تم کھاتے ہو وہی یہ کھاتا ہے اور جو کچھ تم پیتے ہو وہی یہ پیتا ہے');
INSERT INTO "ur_maududi" VALUES(23,34,'23:34','اب اگر تم نے اپنے ہی جیسے ایک بشر کی اطاعت قبول کر لی تو تم گھاٹے ہی میں رہے۔ ');
INSERT INTO "ur_maududi" VALUES(23,35,'23:35','یہ تمہیں اطلاع دیتا ہے کہ جب تم مر کر مٹی ہو جاؤ گے اور ہڈیوں کا پنجر بن کر رہ جاؤ گے اُس وقت تم (قبروں سے) نکالے جاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(23,36,'23:36','بعید، بالکل بعید ہے یہ وعدہ جو تم سے کیا جا رہا ہے');
INSERT INTO "ur_maududi" VALUES(23,37,'23:37','زندگی کچھ نہیں ہے مگر بس یہی دنیا کی زندگی یہیں ہم کو مرنا اور جینا ہے اور ہم ہرگز اٹھائے جانے والے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(23,38,'23:38','یہ شخص خدا کے نام پر محض جھُوٹ گھڑ رہا ہے اور ہم کبھی اس کی ماننے والے نہیں ہیں۔“');
INSERT INTO "ur_maududi" VALUES(23,39,'23:39','رسول نے کہا "پروردگار، اِن لوگوں نے جو میری تکذیب کی ہے، اس پر اب تو ہی میری نصرت فرما"');
INSERT INTO "ur_maududi" VALUES(23,40,'23:40','جواب میں ارشاد ہوا "قریب ہے وہ وقت جب یہ اپنے کیے پر پچھتائیں گے"');
INSERT INTO "ur_maududi" VALUES(23,41,'23:41','آخر کار ٹھیک ٹھیک حق کے مطابق ایک ہنگامہ ٴ عظیم نے ان کو آلیا اور ہم نے ان کو کچرا بنا کر پھینک دیا۔۔۔۔ دُور ہو ظالم قوم!');
INSERT INTO "ur_maududi" VALUES(23,42,'23:42','پھر ہم نے ان کے بعد دوسری قومیں اٹھائیں');
INSERT INTO "ur_maududi" VALUES(23,43,'23:43','کوئی قوم نہ اپنے وقت سے پہلے ختم ہوئی اور نہ اس کے بعد ٹھیر سکی');
INSERT INTO "ur_maududi" VALUES(23,44,'23:44','پھر ہم نے پے درپے رسُول بھیجے۔ جس قوم کے پاس بھی اس کا رسُول آیا، اُس نے اُسے جھُٹلایا، اور ہم ایک کے بعد ایک قوم کو ہلاک کرتے چلے گئے حتٰی کہ ان کو بس افسانہ ہی بنا کر چھوڑا۔۔۔۔ پِھٹکار اُن لوگوں پر جو ایمان نہیں لاتے! ');
INSERT INTO "ur_maududi" VALUES(23,45,'23:45','پھر ہم نے موسیٰؑ اور اس کے بھائی ہارونؑ کو اپنی نشانیوں اور کھُلی سَنَد کے ساتھ فرعون اور اس کے اَعیانِ سلطنت کی طرف بھیجا');
INSERT INTO "ur_maududi" VALUES(23,46,'23:46','مگر انہوں نے تکبّر کیا اور بڑی دوں کی لی۔ ');
INSERT INTO "ur_maududi" VALUES(23,47,'23:47','کہنے لگے”کیا ہم اپنے ہی جیسے دو آدمیوں پر ایمان لے آئیں؟ اور آدمی بھی وہ جن کی قوم ہماری بندی ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(23,48,'23:48','پس اُنہوں نے دونوں کو جھُٹلایا اور ہلاک ہونے والوں میں جا ملے۔ ');
INSERT INTO "ur_maududi" VALUES(23,49,'23:49','اور موسٰیؑ کو ہم نے کتاب عطا فرمائی تاکہ لوگ اس سے رہنمائی حاصل کریں');
INSERT INTO "ur_maududi" VALUES(23,50,'23:50','اور ابنِ مریمؑ اور اس کی ماں کو ہم نے ایک نشان بنایا اور ان کو ایک سطحِ مُرتَفَع پر رکھا جو اطمینان کی جگہ تھی اور چشمے اس میں جاری تھے۔ ');
INSERT INTO "ur_maududi" VALUES(23,51,'23:51','اے پیغمبرو، کھاوٴ پاک چیزیں اور عمل کرو صالح، تم جو کچھ بھی کرتے ہو، میں اس کو خُوب جانتا ہوں');
INSERT INTO "ur_maududi" VALUES(23,52,'23:52','اور یہ تمہاری اُمّت ایک ہی اُمّت ہے اور میں تمہارا ربّ ہوں ، پس مجھی سے ڈرو۔ ');
INSERT INTO "ur_maududi" VALUES(23,53,'23:53','مگر بعد میں لوگوں نے اپنے دین کو آپس میں ٹکڑے ٹکڑے کر لیا۔ ہر گروہ کے پاس جو کچھ ہے اُسی میں وہ مگن ہے ');
INSERT INTO "ur_maududi" VALUES(23,54,'23:54','اچھا، تو چھوڑو انھیں، ڈوبے رہیں اپنی غفلت میں ایک وقتِ خاص تک۔ ');
INSERT INTO "ur_maududi" VALUES(23,55,'23:55','کیا یہ سمجھتے ہیں کہ ہم جو انہیں مال و اولاد سے مدد دیے جا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(23,56,'23:56','تو گویا اِنہیں بھلائیاں دینے میں سرگرم ہیں؟ نہیں ، اصل معاملے کا اِنہیں شعُور نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(23,57,'23:57','جو اپنے ربّ کے خوف سے ڈرے رہتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(23,58,'23:58','جو اپنے ربّ کی آیات پر ایمان لاتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(23,59,'23:59','جو اپنے ربّ کے ساتھ کسی کو شریک نہیں کرتے، ');
INSERT INTO "ur_maududi" VALUES(23,60,'23:60','اور جن کا حال یہ ہے کہ دیتے ہیں جو کچھ بھی دیتے ہیں اور دل اُن کے اِس خیال سے کانپتے رہتے ہیں کہ ہمیں اپنے ربّ کی طرف پلٹنا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(23,61,'23:61','بھلائیوں کی طرف دوڑنے والے اور سبقت کر کے انہیں پالینے والے تو در حقیقت وہ لوگ ہیں');
INSERT INTO "ur_maududi" VALUES(23,62,'23:62','ہم کسی شخص کو اس کی مقدرت سے زیادہ تکلیف نہیں دیتے، اور ہمارے پاس ایک کتاب ہے ، جو (ہر ایک کا حال)ٹھیک ٹھیک بتا دینے والی ہے، اور لوگوں پر ظلم بہرحال نہیں کیا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(23,63,'23:63','مگر یہ لوگ اس معاملے سے بے خبر ہیں۔ اور ان کے اعمال بھی اُس طریقے سے (جس کا اُوپر ذکر کیا گیا ہے)مختلف ہیں۔ وہ اپنے یہ کرتُوت کیے چلے جائیں گے');
INSERT INTO "ur_maududi" VALUES(23,64,'23:64','یہاں تک کہ جب ہم اُن کے عیّاشوں کو عذاب میں پکڑ لیں گے تو پھر وہ ڈکرانا شروع کر دیں گے ');
INSERT INTO "ur_maududi" VALUES(23,65,'23:65',' اب بند کرو اپنی فریاد و فغاں، ہماری طرف سے اب کوئی مدد تمہیں نہیں ملنی');
INSERT INTO "ur_maududi" VALUES(23,66,'23:66','میری آیات سُنائی جاتی تھیں تو تُم (رسُول کی آواز سُنتے ہی)اُلٹے پاوٴں بھاگ نکلتے تھے، ');
INSERT INTO "ur_maududi" VALUES(23,67,'23:67','اپنے گھمنڈ میں اُس کو خاطر ہی میں نہ لاتے تھے، اپنی چوپالوں میں اُس پر باتیں چھانٹتے اور بکواس کیا کرتے تھے');
INSERT INTO "ur_maududi" VALUES(23,68,'23:68','تو کیا اِن لوگوں نے کبھی اِس کلام پر غور نہیں کیا؟ یا وہ کوئی ایسی بات لایا ہے جو کبھی ان کے اسلاف کے پاس نہ آئی تھی؟ ');
INSERT INTO "ur_maududi" VALUES(23,69,'23:69','یا یہ اپنے رسُول سے کبھی کے واقف نہ تھے کہ (اَن جانا آدمی ہو نے کے باعث)اُس سے بِدَکتے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(23,70,'23:70','یا یہ اس بات کے قائل ہیں کہ وہ مجنُون ہے؟ نہیں، بلکہ وہ حق لایا ہے اور حق ہی ان کی اکثریت کو ناگوار ہے');
INSERT INTO "ur_maududi" VALUES(23,71,'23:71','اور حق اگر کہیں اِن کی خواہشات کے پیچھے چلتا تو زمین اور آسمان اور ان کی ساری آبادی کا نظام درہم برہم ہو جاتا ۔۔۔۔ نہیں، بلکہ ہم ان کا اپنا ہی ذکر اُن کے پاس لائے ہیں اور وہ اپنے ذکر سے منہ موڑ رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(23,72,'23:72','کیا تُو ان سے کچھ مانگ رہا ہے؟ تیرے لیے تیرے ربّ کا دیا ہی بہتر ہے اور وہ بہترین رازق ہے۔ ');
INSERT INTO "ur_maududi" VALUES(23,73,'23:73','تُو تو ان کوسیدھے راستے کی طرف بلا رہا ہے');
INSERT INTO "ur_maududi" VALUES(23,74,'23:74','مگر جو لوگ آخرت کو نہیں مانتے وہ راہِ راست سے ہٹ کر چلنا چاہتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(23,75,'23:75','اگر ہم اِن پر رحم کریں اور وہ تکلیف جس میں آج کل یہ مُبتلا ہیں ، دُور کردیں تو یہ اپنی سرکشی میں بالکل ہی بہک جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(23,76,'23:76','اِن کا حال تو یہ ہے کہ ہم نے انہیں تکلیف میں مبتلا کیا، پھر بھی یہ اپنے رب کے آگے نہ جھکے اور نہ عاجزی اختیار کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(23,77,'23:77','البتہ جب نوبت یہاں تک پہنچ جائے گی کہ ہم اِن پر سخت عذاب کا دروازہ کھول دیں تو یکایک تم دیکھو گے کہ اس حالت میں یہ ہر خیر سے مایوس ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(23,78,'23:78','وہ اللہ ہی تو ہے جس نے تمہیں سُننے اور دیکھنے کی قوتیں دیں اور سوچنے کو دل دیے۔ مگر تم لوگ کم ہی شکر گزار ہوتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(23,79,'23:79','وہی ہے جس نے تمہیں زمین میں پھیلایا، اور اسی کی طرف تم سمیٹے جاؤ گے');
INSERT INTO "ur_maududi" VALUES(23,80,'23:80','وہی زندگی بخشتا ہے اور وہی موت دیتا ہے۔ گردشِ لیل و نہار اُسی کے قبضۂ قدرت میں ہے۔ کیا تمہاری سمجھ میں یہ بات نہیں آتی؟ ');
INSERT INTO "ur_maududi" VALUES(23,81,'23:81','مگر یہ لوگ وہی کچھ کہتے ہیں جو ان کے پیش رَو کہہ چکے ہیں');
INSERT INTO "ur_maududi" VALUES(23,82,'23:82','یہ کہتے ہیں "کیا جب ہم مر کر مٹی ہو جائیں گے اور ہڈیوں کا پنجر بن کر رہ جائیں گے تو ہم کو پھر زندہ کر کے اٹھایا جائے گا؟');
INSERT INTO "ur_maududi" VALUES(23,83,'23:83','ہم نے بھی یہ وعدے بہت سُنے ہیں اور ہم سے پہلے ہمارے باپ دادا بھی سُنتے رہے ہیں۔ یہ محض افسانہائے پارینہ ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(23,84,'23:84','ان سے کہو، بتاؤ اگر تم جانتے ہو، کہ یہ زمین اور اس کی ساری آبادی کس کی ہے؟');
INSERT INTO "ur_maududi" VALUES(23,85,'23:85','یہ ضرور کہیں گے ، اللہ کی۔ کہو، پھر تم ہوش میں کیوں نہیں آتے؟ ');
INSERT INTO "ur_maududi" VALUES(23,86,'23:86','ان سے پوچھو، ساتوں آسمانوں اور عرش عظیم کا مالک کون ہے؟');
INSERT INTO "ur_maududi" VALUES(23,87,'23:87','یہ ضرور کہیں گے اللہ ۔ کہو، پھر تم ڈرتے کیوں نہیں؟ ');
INSERT INTO "ur_maududi" VALUES(23,88,'23:88','اِن سے کہو، بتاوٴ اگر تم جانتے ہو کہ ہر چیز پر اقتدار کس کا ہے؟ اور کون ہے جو پناہ دیتا ہے اور اُس کے مقابلے میں کوئی پناہ نہیں دے سکتا؟');
INSERT INTO "ur_maududi" VALUES(23,89,'23:89','یہ ضرور کہیں گے کہ یہ بات تو اللہ ہی کے لیے ہے۔ کہو، پھر کہاں سے تم کو دھوکا لگتا ہے؟ ');
INSERT INTO "ur_maududi" VALUES(23,90,'23:90','جو امرِ حق ہے وہ ہم ان کے سامنے لے آئے ہیں، اور کوئی شک نہیں کہ یہ لوگ جھوٹے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(23,91,'23:91','اللہ نے کسی کو اپنی اولاد نہیں بنایا ہے، اور کوئی دوسرا خدا اُس کے ساتھ نہیں ہے۔ اگر ایسا ہوتا تو ہر خدا اپنی خلق کو لے کر الگ ہو جاتا، اور پھر وہ ایک دوسرے پر چڑھ دوڑتے۔ پاک ہے اللہ اُن باتوں سے جو یہ لوگ بناتے ہیں');
INSERT INTO "ur_maududi" VALUES(23,92,'23:92','کھُلے اور چھُپے کا جاننے والا، وہ بالا تر ہے اُس شرک سے جو یہ لوگ تجویز کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(23,93,'23:93','اے محمدؐ، دعا کرو کہ "پروردگار، جس عذاب کی اِن کو دھمکی دی جا رہی ہے وہ اگر میری موجودگی میں تو لائے');
INSERT INTO "ur_maududi" VALUES(23,94,'23:94','تواے مرے ربّ، مجھے اِن ظالم لوگوں میں شامل نہ کیجیو۔“ ');
INSERT INTO "ur_maududi" VALUES(23,95,'23:95','اور حقیقت یہ ہے کہ ہم تمہاری آنکھوں کے سامنے ہی وہ چیز لے آنے کی پوری قدرت رکھتے ہیں جس کی دھمکی ہم انہیں دے رہے ہیں');
INSERT INTO "ur_maududi" VALUES(23,96,'23:96','اے نبیؐ، برائی کو اس طریقہ سے دفع کرو جو بہترین ہو جو کچھ باتیں وہ تم پر بناتے ہیں وہ ہمیں خوب معلوم ہیں');
INSERT INTO "ur_maududi" VALUES(23,97,'23:97','اور دعا کرو کہ "پروردگار، میں شیاطین کی اکساہٹوں سے تیری پناہ مانگتا ہوں');
INSERT INTO "ur_maududi" VALUES(23,98,'23:98','بلکہ اے میرے ربّ، میں تو اِس سے بھی تیری پناہ مانگتا ہوں کہ وہ میرے پاس آئیں۔“ ');
INSERT INTO "ur_maududi" VALUES(23,99,'23:99','(یہ لوگ اپنی کرنی سے باز نہ آئیں گے)یہاں تک کہ جب اِن میں سے کسی کو موت آجائے گی تو کہنا شروع کرے گا کہ”اے میرے ربّ، مجھے اُسی دنیا میں واپس بھیج دیجیے جسے میں چھوڑ آیا ہوں');
INSERT INTO "ur_maududi" VALUES(23,100,'23:100','اُمید ہے کہ اب میں نیک عمل کروں گا “۔۔۔۔ ہر گز نہیں، یہ تو بس ایک بات ہے جو وہ بَک رہا ہے۔ اب ان سب(مرنے والوں)کے پیچھے ایک برزخ حائل ہے دوسری زندگی کے دن تک۔ ');
INSERT INTO "ur_maududi" VALUES(23,101,'23:101','پھر جونہی کہ صُور پھونک دیا گیا، اِن کے درمیان پھر کوئی رشتہ نہ رہے گا اور نہ وہ ایک دوسرے کو پوچھیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(23,102,'23:102','اُس وقت جن کے پلڑے بھاری ہوں گے وہی فلاح پائیں گے');
INSERT INTO "ur_maududi" VALUES(23,103,'23:103','اور جن کے پلڑے ہلکے ہوں گے وہی لوگ ہوں گے جنہوں نے اپنے آپ کو گھاٹے میں ڈال لیا۔ وہ جہنّم میں ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(23,104,'23:104','آگ اُن کے چہروں کی کھال چاٹ جائے گی اور اُن کے جبڑے باہر نکل آئیں گے۔۔۔۔” ');
INSERT INTO "ur_maududi" VALUES(23,105,'23:105','"کیا تم وہی لوگ نہیں ہو کہ میری آیات تمہیں سنائی جاتی تھیں تو تم انہیں جھٹلاتے تھے؟"');
INSERT INTO "ur_maududi" VALUES(23,106,'23:106','وہ کہیں گے "اے ہمارے رب، ہماری بد بختی ہم پر چھا گئی تھی واقعی ہم گمراہ لوگ تھے');
INSERT INTO "ur_maududi" VALUES(23,107,'23:107','اے پروردگار، اب ہمیں یہاں سے نکال دے پھر ہم ایسا قصور کریں تو ظالم ہوں گے"');
INSERT INTO "ur_maududi" VALUES(23,108,'23:108','اللہ تعالیٰ جواب دے گا”دُور ہو میرے سامنے سے، پڑے رہو اِسی میں اور مجھ سے بات نہ کرو۔ ');
INSERT INTO "ur_maududi" VALUES(23,109,'23:109','تم وہی لوگ تو ہو کہ میرے کچھ بندے جب کہتے تھے کہ اے ہمارے پروردگار، ہم ایمان لائے، ہمیں معاف کر دے، ہم پر رحم کر، تُو سب رحیموں سے اچھا رحیم ہے');
INSERT INTO "ur_maududi" VALUES(23,110,'23:110','تو تم نے ان کا مذاق بنا لیا یہاں تک کہ اُن کی ضد نے تمہیں یہ بھی بھُلا دیا کہ میں بھی کوئی ہوں، اور تم ان پر ہنستے رہے');
INSERT INTO "ur_maududi" VALUES(23,111,'23:111','آج اُن کے اُس صبر کا میں نے یہ پھل دیا ہے کہ وہی کامیاب ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(23,112,'23:112','پھر اللہ تعالی ان سے پوچھے گا "بتاؤ، زمین میں تم کتنے سال رہے؟"');
INSERT INTO "ur_maududi" VALUES(23,113,'23:113','وہ کہیں گے”ایک دن یا دن کا بھی کچھ حصہ ہم وہاں ٹھہرے ہیں، شُمار کرنے والوں سے پوچھ لیجیے۔“');
INSERT INTO "ur_maududi" VALUES(23,114,'23:114','اِرشاد ہو گا”تھوڑی ہی دیر ٹھہرے ہونا۔ کاش تم نے یہ اُس وقت جانا ہوتا۔ ');
INSERT INTO "ur_maududi" VALUES(23,115,'23:115','کیا تم نے یہ سمجھ رکھا تھا کہ ہم نے تمہیں فضُول ہی پیدا کیا ہے اور تمہیں ہماری طرف کبھی پلٹنا ہی نہیں ہے؟“');
INSERT INTO "ur_maududi" VALUES(23,116,'23:116','پس بالا و برتر ہے اللہ، پادشاہِ حقیقی، کوئی خدا اُس کے سوا نہیں، مالک ہے عرشِ بزرگ کا۔');
INSERT INTO "ur_maududi" VALUES(23,117,'23:117','اور جو کوئی اللہ کے ساتھ کسی اور معبُود کو پکارے، جس کے لیے اُس کے پاس کوئی دلیل نہیں تو اُس کا حساب اُس کے ربّ کے پاس ہے۔ ایسے کافر کبھی فلاح نہیں پا سکتے۔ ');
INSERT INTO "ur_maududi" VALUES(23,118,'23:118','اے محمدؐکہو”میرے ربّ درگزر فرما، اور رحم کر ، اور تُو سب رحیموں سے اچھا رحیم ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(24,1,'24:1','یہ ایک سُورت ہے جس کو ہم نے نازل کیا ہے، اور اسے ہم نے فرض کیا ہے، اور اس میں ہم نے صاف صاف ہدایات نازل کی ہیں، شاید کہ تم سبق لو');
INSERT INTO "ur_maududi" VALUES(24,2,'24:2','زانیہ عورت اور زانی مرد، دونوں میں سے ہر ایک کو سو کوڑے مارو۔ اور ان پر ترس کھانے کا جذبہ اللہ کے دین کے معاملے میں تم کو دامنگیر نہ ہو اگر تم اللہ تعالیٰ اور روزِ آخر پر ایمان رکھتے ہو۔ اور ان کو سزا دیتے وقت اہلِ ایمان کا ایک گروہ موجود رہے۔ ');
INSERT INTO "ur_maududi" VALUES(24,3,'24:3','زانی نکاح نہ کرے مگر زانیہ کے ساتھ یا مشرکہ کے ساتھ۔ اور زانیہ کے ساتھ نکاح نہ کرے مگر زانی یہ مشرک۔ اور یہ حرام کر دیا گیا ہے اہلِ ایمان پر۔ ');
INSERT INTO "ur_maududi" VALUES(24,4,'24:4','اور جو لوگ پاک دامن عورتوں پر تہمت لگائیں، پھر چار گواہ لے کر نہ آئیں، ان کو اسی کوڑے مارو اور ان کی شہادت کبھی قبول نہ کرو، اور وہ خود ہی فاسق ہیں');
INSERT INTO "ur_maududi" VALUES(24,5,'24:5','سوائے اُن لوگوں کے جو اس حرکت کے بعد تائب ہو جائیں اور اصلاح کر لیں کہ اللہ ضرور (اُن کے حق میں)غفورو رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(24,6,'24:6','اور جو لوگ اپنی بیویوں پر الزام لگائیں اور ان کے پاس خود ان کے اپنے سوا دوسرے کوئی گواہ نہ ہوں تو اُن میں سے ایک شخص کی شہادت (یہ ہے کہ وہ) چار مرتبہ اللہ کی قسم کھا کر گواہی دے کہ وہ (اپنے الزام میں) سچا ہے');
INSERT INTO "ur_maududi" VALUES(24,7,'24:7','اور پانچویں بار کہے کہ اُس پر اللہ کی لعنت ہو اگر وہ (اپنے الزام میں) جھوٹا ہو');
INSERT INTO "ur_maududi" VALUES(24,8,'24:8','اور عورت سے سزا اس طرح ٹل سکتی ہے کہ وہ چار مرتبہ اللہ کی قسم کھا کر شہادت دے کہ یہ شخص (اپنے الزام میں) جھوٹا ہے');
INSERT INTO "ur_maududi" VALUES(24,9,'24:9','اور پانچویں مرتبہ کہے کہ اُس بندی پر اللہ کا غضب ٹوٹے اگر وہ (اپنے الزام میں)سچا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(24,10,'24:10','تم لوگوں پر اللہ کا فضل اور اس کا رحم نہ ہوتا اور یہ بات نہ ہوتی کہ اللہ بڑا التفات فرمانے والا اور حکیم ہے، تو (بیویوں پر الزام کا معاملہ تمہیں بڑی پیچیدگی میں ڈال دیتا)');
INSERT INTO "ur_maududi" VALUES(24,11,'24:11','جو لوگ یہ بہتان گھڑ لائے ہیں وہ تمہارے ہی اندر کا ایک ٹولہ ہیں۔ اس واقعے کو اپنے حق میں شر نہ سمجھو بلکہ یہ بھی تمہارے لیے خیر ہی ہے۔ جس نے اس میں جتنا حصہ لیا اس نے اتنا ہی گناہ سمیٹا، اور جس شخص نے اِس کی ذمہ داری کا بڑا حصہ اپنے سر لیا اس کے لیے عذابِ عظیم ہے');
INSERT INTO "ur_maududi" VALUES(24,12,'24:12','جس وقت تم لوگوں نے اسے سُنا تھا اُسی وقت کیوں نہ مومن مردوں اور مومن عورتوں نے اپنے آپ سے نیک گمان کیا اور کیوں نہ کہہ دیا کہ یہ صریح بہتان ہے؟ ');
INSERT INTO "ur_maududi" VALUES(24,13,'24:13','وہ لوگ (اپنے الزام کے ثبوت میں)چار گواہ کیوں نہ لائے؟اب کہ وہ گواہ نہیں لائے ہیں، اللہ کے نزدیک وہی جھُوٹے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(24,14,'24:14','اگر تم لوگوں پر دنیا اور آخرت میں اللہ کا فضل اور رحم و کرم نہ ہوتا تو جن باتوں میں تم پڑ گئے تھے ان کی پاداش میں بڑا عذاب تمہیں آ لیتا');
INSERT INTO "ur_maududi" VALUES(24,15,'24:15','(ذرا غور تو کرو، اُس وقت تم کیسی سخت غلطی کر رہے تھے) جبکہ تمہاری ایک زبان سے دوسری زبان اس جھوٹ کو لیتی جا رہی تھی اور تم اپنے منہ سے وہ کچھ کہے جا رہے تھے جس کے متعلق تمہیں کوئی علم نہ تھا تم اسے ایک معمولی بات سمجھ رہے تھے، حالانکہ اللہ کے نزدیک یہ بڑی بات تھی');
INSERT INTO "ur_maududi" VALUES(24,16,'24:16','کیوں نہ اُسے سنتے ہی تم نے کہہ دیا کہ "ہمیں ایسی بات زبان سے نکالنا زیب نہیں دیتا، سبحان اللہ، یہ تو ایک بہتان عظیم ہے"');
INSERT INTO "ur_maududi" VALUES(24,17,'24:17','اللہ تم کو نصیحت کرتا ہے کہ آئندہ کبھی ایسی حرکت نہ کرنا اگر تم مومن ہو');
INSERT INTO "ur_maududi" VALUES(24,18,'24:18','اللہ تمہیں صاف صاف ہدایات دیتا ہے اور رہ علیم و حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(24,19,'24:19','جولوگ چاہتے ہیں کہ ایمان لانے والوں کے گروہ میں فحش پھیلے وہ دنیا اور آخرت میں دردناک سزا کے مستحق ہیں، اللہ جانتا ہے اور تم نہیں جانتے۔ ');
INSERT INTO "ur_maududi" VALUES(24,20,'24:20','اگر اللہ کا فضل اور اس کا رحم و کرم تم پر نہ ہوتا اور یہ بات نہ ہوتی کہ اللہ بڑا شفیق و رحیم ہے (تو یہ چیز جو ابھی تمہارے اندر پھیلائی گئی تھی بدترین نتائج دکھا دیتی)');
INSERT INTO "ur_maududi" VALUES(24,21,'24:21','اے لوگو جو ایمان لائےہو، شیطان کے نقشِ قدم پر نہ چلو۔ اس کی پیروی کوئی کرے گا تو وہ تو اسے فحش اور بدی ہی کا حکم دے گا۔ اگر اللہ کا فضل اور اس کا رحم و کرم تم پر نہ ہوتا تو تم میں سے کو ئی شخص پاک نہ ہو سکتا۔ مگر اللہ ہی جسے چاہتا ہے پاک کر دیتا ہے ، اور اللہ سُننے والا اور جاننے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(24,22,'24:22','تم میں سے جو لوگ صاحبِ فضل اور صاحبِ مقدرت ہیں وہ اس بات کی قسم نہ کھا بیٹھیں کہ اپنے رشتہ دار، مسکین اور مہاجر فی سبیل اللہ لوگوں کی مدد نہ کریں گے۔ انھیں معاف کر دینا چاہیے اور درگزر کرنا چاہیے۔ کیا تم نہیں چاہتے کہ اللہ تمہیں معاف کرے؟ اور اللہ کی صفت یہ ہے کہ وہ غفور و رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(24,23,'24:23','جو لوگ پاک دامن ، بے خبر، مومن عورتوں پر تہمتیں لگاتے ہیں ان پر دُنیا اور آخرت میں لعنت کی گئی اور ان کے لیے بڑا عذاب ہے');
INSERT INTO "ur_maududi" VALUES(24,24,'24:24','وہ اس دن کو بھُول نہ جائیں جبکہ ان کی اپنی زبانیں اور ان کے اپنے ہاتھ پاوٴں ان کے کرتُوتوں کی گواہی دیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(24,25,'24:25','اس دن اللہ وہ بدلہ انہیں بھرپور دے گا جس کے وہ مستحق ہیں اور انہیں معلوم ہو جائے گا کہ اللہ ہی حق ہے سچ کو سچ کر دکھانے والا');
INSERT INTO "ur_maududi" VALUES(24,26,'24:26','خبیث عورتیں خبیث مردوں کے لیے ہیں اور خبیث مرد خبیث عورتوں کے لیے۔ پاکیزہ عورتیں پاکیزہ مردوں کے لیے ہیں اور پاکیزہ مرد پاکیزہ عورتوں کے لیے۔ ان کا دامن پاک ہے اُن باتوں سے جو بنانے والے بناتے ہیں، ان کے لیے مغفرت ہے اور رزقِ کریم');
INSERT INTO "ur_maududi" VALUES(24,27,'24:27',' اے لوگوجو ایمان لائے ہو، اپنے گھروں کے سوا دوسرے گھر میں داخل نہ ہوا کرو جب تک کہ گھر والوں کی رضا نہ لے لو اور گھر والوں پر سلام نہ بھیج لو، یہ طریقہ تمہارے لیے بہتر ہے۔ توقع ہے کہ تم اس کا خیال رکھو گے۔ ');
INSERT INTO "ur_maududi" VALUES(24,28,'24:28','پھر اگر وہاں کسی کو نہ پاوٴ تو داخل نہ ہو جب تک کہ تم کو اجازت نہ دے دی جائے، اور اگر تم سے کہا جائے کہ واپس چلے جاوٴ تو واپس ہو جاوٴ، یہ تمہارے لیے زیادہ پاکیزہ طریقہ ہے ، اور جو کچھ تم کرتے ہو اللہ اسے خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(24,29,'24:29','البتہ تمہارے لیے اس میں کوئی مضائقہ نہیں ہے کہ ایسے گھروں میں داخل ہو جاوٴ جو کسی کے رہنے کی جگہ نہ ہوں اور جن میں تمہارے فائدے (یا کام)کی کوئی چیز ہو، تم جو کچھ ظاہر کرتے ہو اور جو کچھ چھپاتے ہو سب کی اللہ کو خبر ہے');
INSERT INTO "ur_maududi" VALUES(24,30,'24:30','اے نبیؐ ، مومن مردوں سے کہو کہ اپنی نظریں بچا کر رکھیں اور اپنی شرمگاہوں کی حفاظت کریں، یہ اُن کے لیے زیادہ پاکیزہ طریقہ ہے، جو کچھ وہ کرتے ہیں اللہ اُس سے باخبر رہتا ہے');
INSERT INTO "ur_maududi" VALUES(24,31,'24:31','اور اے نبیؐ ، مومن عورتوں سے کہہ دو کہ اپنی نظریں بچا کر رکھیں، اور اپنی شرمگاہوں کی حفاظت کریں، اور اپنا بناوٴ سنگھار نہ دکھائیں بجز اُس کے جو خود ظاہر ہو جائے، اور اپنے سینوں پر اپنی اوڑھنیوں کے آنچل ڈالے رہیں۔ وہ اپنا بناوٴ سنگھار نہ ظاہر کریں مگر اِن لوگوں کے سامنے : شوہر، باپ، شوہروں کے باپ ، اپنے بیٹے، شوہروں کے بیٹے ، بھائی ، بھائیوں کے بیٹے ، بہنوں کے بیٹے ، اپنے میل جول کی عورتیں ، اپنے مملوک ، وہ زیرِ دست مرد جو کسی اور قسم کی غرض نہ رکھتے ہوں ، اور وہ بچے جو عورتوں کی پوشیدہ باتوں سے ابھی واقف نہ ہوئے ہوں۔ وہ اپنے پاوٴں زمین پر مارتی ہوئی نہ چلا کریں کہ اپنی جو زینت انہوں نے چھپا رکھی ہو اس کا لوگوں کو علم ہو جائے۔ اے مومنو، تم سب مِل کر اللہ سے توبہ کرو ، توقع ہے کہ فلاح پاوٴ گے۔ ');
INSERT INTO "ur_maududi" VALUES(24,32,'24:32','تم میں سے جو لوگ مجرد ہوں ، اور تمہارے لونڈی غلاموں میں سے جو صالح ہوں ، ان کے نکاح کر دو۔ اگر وہ غریب ہوں تو اللہ اپنے فضل سے اُن کو غنی کر دے گا ، اللہ بڑی وسعت والا اور علیم ہے');
INSERT INTO "ur_maududi" VALUES(24,33,'24:33','اور جو نکاح کا موقع نہ پائیں انھیں چاہیے کہ عِفّت مآبی اختیار کریں، یہاں تک کہ اللہ اپنے فضل سے اُن کو غنی کر دے۔ اور تمہارےمملوکوں میں سے جو مکاتبت کی درخواست کریں ان سے مکاتبت کر لو اگر تمہیں معلوم ہو کہ ان کے اندر بھلائی ہے ، اور ان کو اُس مال میں سے دو جو اللہ نے تمہیں دیا ہے۔ اور اپنی لونڈیوں کو اپنے دُنیوی فائدوں کی خاطر قحبہ گری پر مجبُور نہ کرو جبکہ وہ خود پاکدامن رہنا چاہتی ہوں ، اور جو کوئی اُن کو مجبور کرے تو اِس جبر کے بعد اللہ اُن کے لیے غفور و رحیم ہے');
INSERT INTO "ur_maududi" VALUES(24,34,'24:34','ہم نے صاف صاف ہدایت دینے والی آیات تمہارے پاس بھیج دی ہیں، اور ان قوموں کی عبرتناک مثالیں بھی ہم تمہارے سامنے پیش کر چکے ہیں جو تم سے پہلے ہو گزری ہیں، اور وہ نصیحتیں ہم نے کر دی ہیں جو ڈرنے والوں کے لیے ہوتی ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(24,35,'24:35',' اللہ آسمانوں اور زمین کا نُور ہے۔ (کائنات میں)اس کے نُور کی مثال ایسی ہے جیسے ایک طاق میں چراغ رکھا ہُوا ہو، چراغ ایک فانُوس میں ہو، فانُوس کا حال یہ ہو کہ جیسے موتی کی طرح چمکتا ہوا تارا، اور وہ چراغ زیتون کے ایک ایسے مبارک درخت کے تیل سے روشن کیا جاتا ہو جو نہ شرقی ہو نہ غربی ، جس کا تیل آپ ہی آپ بھڑکا پڑتا ہو چاہے آگ اس کو نہ لگے، (اِس طرح)روشنی پر روشنی (بڑھنے کے تمام اسباب جمع ہوگئے ہوں )۔ اللہ اپنے نُور کی طرف جس کی چاہتا ہے رہنمائی فرماتا ہے ، وہ لوگوں کو مثالوں سے بات سمجھاتا ہے ، وہ ہر چیز سے خُوب واقف ہے۔ ');
INSERT INTO "ur_maududi" VALUES(24,36,'24:36','(اُس کے نُور کی طرف ہدایت پانے والے)اُن گھروں میں پائے جاتے ہیں جنہیں بلند کرنے کا، اور جن میں اپنے نام کی یاد کا اللہ نے اِذن دیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(24,37,'24:37','اُن میں ایسے لوگ صبح و شام اُس کی تسبیح کرتے ہیں جنہیں تجارت اور خرید و فروخت اللہ کی یاد سے اور اقامت نماز و ادائے زکوٰۃ سے غافل نہیں کر دیتی وہ اُس دن سے ڈرتے رہتے ہیں جس میں دل الٹنے اور دیدے پتھرا جانے کی نوبت آ جائے گی');
INSERT INTO "ur_maududi" VALUES(24,38,'24:38','(اور وہ یہ سب کچھ اس لیے کرتے ہیں)تاکہ ان کے بہترین اعمال کی جزا اُن کو دے اور مزید اپنے فضل سے نوازے، اللہ جسے چاہتا ہے بے حساب دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(24,39,'24:39','(اسِ کے برعکس)جنہوں نے کُفر کیا ان کے اعمال کی مثال ایسی ہے جیسے دشتِ بے آب میں سراب کہ پیاسا اُس کو پانی سمجھے ہوئے تھا، مگر جب وہاں پہنچا تو کچھ نہ پایا، بلکہ وہاں اس نے اللہ کو موجود پایا، جس نے اس کا پورا پورا حساب چکا دیا، اور اللہ کو حساب لیتے دیر نہیں لگتی۔ ');
INSERT INTO "ur_maududi" VALUES(24,40,'24:40','یا پھر اس کی مثال ایسی ہے جیسے ایک گہرے سمندر میں اندھیرا، کہ اُوپر ایک موج چھائی ہوئی ہے، اُس پر ایک اور موج، اور اس کے اُوپر بادل، تاریکی مسلّط ہے، آدمی اپنا ہاتھ نکالے تو اسے بھی نہ دیکھنے پائے۔ جسے اللہ نُور نہ بخشے اُس کے لیے پھر کوئی نُور نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(24,41,'24:41',' کیا تم دیکھتے نہیں ہو کہ اللہ کی تسبیح کر رہے ہیں وہ سب جو آسمانوں اور زمین میں ہیں اور وہ پرندے جو پر پھیلائے اُڑ رہے ہیں؟ ہر ایک اپنی نماز اور تسبیح کا طریقہ جانتا ہے، اور یہ سب جو کچھ کرتے ہیں اللہ اس سے با خبر رہتا ہے');
INSERT INTO "ur_maududi" VALUES(24,42,'24:42','آسمانوں اور زمین کی بادشاہی اللہ ہی کے لیے ہے اور اسی کی طرف سب کو پلٹنا ہے');
INSERT INTO "ur_maududi" VALUES(24,43,'24:43','کیا تم دیکھتے نہیں ہو کہ اللہ بادل کو آہستہ آہستہ چلاتا ہے، پھر اس کے ٹکڑوں کو باہم جوڑتا ہے ، پھر اسے سمیٹ کر ایک کثیف ابر بنا دیتا ہے، پھر تم دیکھتے ہو کہ اس کے خول میں سے بارش کے قطرے ٹپکتے چلے آتے ہیں۔ اور وہ آسمان سے ، اُن پہاڑوں کی بدولت جو اس میں بلند ہیں، اولے برساتا ہے، پھر جسے چاہتا ہے ان کا نقصان پہنچاتا ہے اور جسے چاہتا ہے ان سے بچا لیتا ہے۔ اُس کی بجلی کی چمک نگاہوں کو خیرہ کیے دیتی ہے');
INSERT INTO "ur_maududi" VALUES(24,44,'24:44','رات اور دن کا الٹ پھیر وہی کر رہا ہے اِس میں ایک سبق ہے آنکھوں والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(24,45,'24:45','اور اللہ نے ہر جاندار ایک طرح کے پانی سے پیدا کیا، کوئی پیٹ کے بل چل رہا ہے تو کوئی دو ٹانگوں پر اور کوئی چار ٹانگوں پر جو کچھ وہ چاہتا ہے پیدا کرتا ہے، وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_maududi" VALUES(24,46,'24:46','ہم نے صاف صاف حقیقت بتانے والی آیات نازل کر دی ہیں، آگے صراط مستقیم کی طرف ہدایت اللہ ہی جسے چاہتا ہے دیتا ہے');
INSERT INTO "ur_maududi" VALUES(24,47,'24:47','یہ لوگ کہتے ہیں کہ ہم ایمان لائے اللہ اور رسُولؐ پر اور ہم نے اطاعت قبول کی، مگر اس کے بعد ان میں سے ایک گروہ (اطاعت سے)منہ موڑ جاتا ہے۔ ایسے لوگ ہرگز مومن نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(24,48,'24:48','جب ان کو بُلایا جاتا ہے اللہ اور رسُول کی طرف، تاکہ رسُول ان کے آپس کے مقدمے کا فیصلہ کرے تو ان میں سے ایک فریق کَترا جاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(24,49,'24:49','البتہ اگر حق ان کی موافقت میں ہو تو رسُول کے پاس بڑے اطاعت کیش بن کر آجاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(24,50,'24:50','کیا اِن کے دلوں کو (منافقت کا)روگ لگا ہوا ہے؟ یا یہ شک میں پڑے ہوئے ہیں ؟ یا ان کو یہ خوف ہے کہ اللہ اور اس کا رسُول ان پر ظلم کرے گا؟ اصل بات یہ ہے کہ ظالم تو یہ لوگ خود ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(24,51,'24:51','ایمان لانے والوں کا کام تو یہ ہے کہ جب وہ اللہ اور رسول کی طرف بلا ئے جائیں تاکہ رسول ان کے مقدمے کا فیصلہ کرے تو وہ کہیں کہ ہم نے سنا اور اطاعت کی ایسے ہی لوگ فلاح پانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(24,52,'24:52','اور کامیاب وہی ہیں جو اللہ اور رسول کی فرماں برداری کریں اور اللہ سے ڈریں اور اس کی نافرمانی سے بچیں');
INSERT INTO "ur_maududi" VALUES(24,53,'24:53','ہ (منافق)اللہ کے نام سے کڑی کڑی قسمیں کھا کر کہتے ہیں کہ ”آپ حکم دیں تو ہم گھروں سے نِکل کھڑے ہوں۔“ اِن سے کہو”قسمیں نہ کھاوٴ، تمہاری اطاعت کا حال معلوم ہے ، تمہارے کرتُوتوں سے اللہ بے خبر نہیں ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(24,54,'24:54','کہو، "اللہ کے مطیع بنو اور رسولؐ کے تابع فرمان بن کر رہو لیکن اگر تم منہ پھیرتے ہو تو خوب سمجھ لو کہ رسولؐ پر جس فرض کا بار رکھا گیا ہے اُس کا ذمہ دار وہ ہے اور تم پر جس فرض کا بار ڈالا گیا ہے اُس کے ذمہ دار تم ہو اُس کی اطاعت کرو گے تو خود ہی ہدایت پاؤ گے ورنہ رسول کی ذمہ داری اس سے زیادہ کچھ نہیں ہے کہ صاف صاف حکم پہنچا دے"');
INSERT INTO "ur_maududi" VALUES(24,55,'24:55','اللہ نے وعدہ فرمایا ہے تم میں سے اُں لوگوں کے ساتھ جو ایمان لائیں اور نیک عمل کریں کہ وہ ان کو اُسی طرح زمین میں خلیفہ بنائے گا جس طرح اُن سے پہلے گزرے ہوئے لوگوں کو بنا چکا ہے، اُن کے لیے اُن کے اُس دین کو مضبوط بُنیادوں پر قائم کر دے گا جسے اللہ تعالےٰ نے اُن کے حق میں پسند کیا ہے ، اور اُن کی (موجودہ)حالتِ خوف کو امن سے بدل دے گا، بس وہ میری بندگی کریں اور میرے ساتھ کسی کو شریک نہ کریں۔ اور جو اس کے بعد کُفر کرے تو ایسے ہی لوگ فاسق ہیں');
INSERT INTO "ur_maududi" VALUES(24,56,'24:56','نماز قائم کرو، زکوٰۃ دو، اور رسولؐ کی اطاعت کرو، امید ہے کہ تم پر رحم کیا جائے گا');
INSERT INTO "ur_maududi" VALUES(24,57,'24:57','جو لوگ کفر کر رہے ہیں ا ن کے متعلق اس غلط فہمی میں نہ رہو کہ وہ زمین میں اللہ کو عاجز کر دیں گے ان کا ٹھکانا دوزخ ہے اور وہ بڑا ہی برا ٹھکانا ہے');
INSERT INTO "ur_maududi" VALUES(24,58,'24:58',' اے لوگو جو ایمان لائے ہو، لازم ہے کہ تمہارے مملوک اور تمہارے وہ بچے جو ابھی عقل کی حد کو نہیں پہنچے ہیں ، تین اوقات میں اجازت لے کر تمہارے پاس آیا کریں:صبح کی نماز سے پہلے، اور دوپہر کو جبکہ تم کپڑے اُتار کر رکھ دیتے ہو، اور عشاء کی نماز کے بعد۔ یہ تین وقت تمہارے لیے پردے کے وقت ہیں۔ اِن کے بعد وہ بلا اجازت آئیں تو نہ تم پر کوئی گناہ ہے نہ اُن پر، تمہیں ایک دُوسرے کے پاس بار بار آنا ہی ہوتا ہے۔ اس طرح اللہ تمہارے لیے اپنے ارشادات کی توضیح کرتا ہے، اور وہ علیم و حکیم ہے');
INSERT INTO "ur_maududi" VALUES(24,59,'24:59','اور جب تمہارے بچے عقل کی حد کو پہنچ جائیں تو چاہیے کہ اُسی طرح اجازت لیکر آیا کریں جس طرح اُن کے بڑے اجازت لیتے رہے ہیں۔ اِس طرح اللہ اپنی آیات تمہارے سامنے کھولتا ہے، اور وہ علیم و حکیم ہے');
INSERT INTO "ur_maududi" VALUES(24,60,'24:60','اور جو عورتیں جوانی سے گزری بیٹھی ہوں ، نکاح کی اُمیدوار نہ ہوں، وہ اگر اپنی چادریں اُتار کر رکھ دیں تو اُن پر کوئی گناہ نہیں، بشرطیکہ زینت کی نمائش کرنے والی نہ ہوں۔ تاہم وہ بھی حیاداری ہی برتیں تو اُن کے حق میں اچھا ہے، اور اللہ سب کچھ سُنتا اور جانتا ہے');
INSERT INTO "ur_maududi" VALUES(24,61,'24:61','کوئی حرج نہیں اگر کوئی اندھا ، یا لنگڑا، یا مریض(کسی کے گھر سے کھا لے)اور نہ تمہارے اُوپر اِس میں کوئی مضائقہ ہے کہ اپنے گھروں سے کھاوٴ یا اپنے باپ دادا کے گھروں سے ، یا اپنی ماں نانی کے گھروں سے، یا اپنے بھائیوں کے گھروں سے ، یا اپنی بہنوں کے گھروں سے، یا اپنے چچاوٴں کے گھروں سے، یا اپنی پھُوپھیوں کے گھروں سے ، یا اپنے مامووٴں کے گھروں سے، یا اپنی خالاوٴں کے گھروں سے، یا اُن گھروں سے جن کی کُنجیاں تمہاری سُپردگی میں ہوں، یا اپنے دوستوں کے گھروں سے۔ اس میں بھی کوئی حرج نہیں کہ تم لوگ مل کر کھاوٴ یا الگ الگ۔ البتہ جب گھروں میں داخل ہوا کرو تو اپنے لوگوں کو سلام کیا کرو، دُعائے خیر، اللہ کی طرف سے مقرر فرمائی ہوئی، بڑی با برکت اور پاکیزہ۔ اِس طرح اللہ تعالیٰ تمہارے سامنے آیات بیان کرتا ہے، توقع ہے کہ تم سمجھ بُوجھ سے کام لو گے');
INSERT INTO "ur_maududi" VALUES(24,62,'24:62',' مومن تو اصل میں وہی ہیں جو اللہ اور اُس کے رسُولؐ کو دل سے مانیں اور جب کسی اجتماعی کام کے موقع پر رسُولؐ کے ساتھ ہوں تو اُس سے اجازت لیے بغیر نہ جائیں۔ جو لوگ تم سے اجازت مانگتے ہیں وہی اللہ اور رسُولؐ کے ماننے والے ہیں، پس جب وہ اپنے کسی کام سے اجازت مانگیں تو جسے تم چاہو اجازت دے دیا کرو اور ایسے لوگوں کے حق میں اللہ سے دُعائے مغفرت کیا کرو، اللہ یقیناً غفور و رحیم ہے');
INSERT INTO "ur_maududi" VALUES(24,63,'24:63','مسلمانو، اپنے درمیان رسُولؐ کے بُلانے کو آپس میں ایک دُوسرے کا سا بُلانا نہ سمجھ بیٹھو۔ اللہ اُن لوگوں کو خوب جانتا ہے جو تم میں ایسے ہیں کہ ایک دُوسرے کی آڑ لیتے ہوئے چُپکے سے سَٹک جاتے ہیں۔ رسُولؐ کے حکم کی خلاف ورزی کرنے والوں کو ڈرنا چاہیے کہ وہ کسی فتنے میں گرفتار نہ ہو جائیں یا ان پر دردناک عذاب نہ آجائے');
INSERT INTO "ur_maududi" VALUES(24,64,'24:64','خبردار رہو، آسمان و زمین میں جو کچھ ہے اللہ کا ہے تم جس روش پر بھی ہو اللہ اُس کو جانتا ہے جس روز لوگ اُس کی طرف پلٹیں گے وہ اُنہیں بتا دے گا کہ وہ کیا کچھ کر کے آئے ہیں وہ ہر چیز کا علم رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(25,1,'25:1','نہایت متبرک ہے وہ جس نے یہ فرقان اپنے بندے پر نازل کیا تاکہ سارے جہان والوں کے لیے نذیر ہو ');
INSERT INTO "ur_maududi" VALUES(25,2,'25:2','وہ جو زمین اور آسمانوں کی بادشاہی کا مالک ہے جس نے کسی کو بیٹا نہیں بنایا ہے ، جس کے ساتھ بادشاہی میں کوئی شریک نہیں ہے ، جس نے ہر چیز کو پیدا کیا پھر اس کی ایک تقدیر مقرر کی۔ ');
INSERT INTO "ur_maududi" VALUES(25,3,'25:3','لوگوں نے اُسے چھوڑ کر ایسے معبُود بنا لیے جو کسی چیز کو پیدا نہیں کرتے بلکہ خود پیدا کیے جاتے ہیں ، جو خود اپنے لیے بھی کسی نفع یا نقصان کا اختیار نہیں رکھتے، جو نہ مار سکتے ہیں نہ جِلا سکتے ہیں، نہ مَرے ہوئے کو پھر اُٹھا سکتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(25,4,'25:4','جِن لوگوں نے نبی کی بات ماننے سے انکار کر دیا ہے وہ کہتے ہیں کہ یہ فرقان ایک من گھڑت چیز ہے جسے اِس شخص نے آپ ہی گھڑ لیا ہے اور کچھ دُوسرے لوگوں نے اِس کام میں اس کی مدد کی ہے۔ بڑا ظلم اور سخت جھُوٹ ہے جس پر یہ لوگ اُتر آئے ہیں');
INSERT INTO "ur_maududi" VALUES(25,5,'25:5','کہتے ہیں یہ پرانے لوگوں کی لکھی ہوئی چیزیں ہیں جنہیں یہ شخص نقل کرتا ہے اور وہ اِسے صبح و شام سنائی جاتی ہیں');
INSERT INTO "ur_maududi" VALUES(25,6,'25:6','اے محمدؐ ، ان سے کہو کہ”اِسے نازل کیا ہے اُس نے جو زمین اور آسمانوں کا بھید جانتا ہے۔ “ حقیقت یہ ہے کہ وہ بڑا غفور و رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(25,7,'25:7','کہتے ہیں”یہ کیسا رسُول ہے جو کھانا کھاتا ہے اور بازاروں میں چلتا پھرتا ہے؟ کیوں نہ اس کے پاس کوئی فرشتہ بھیجا گیا جو اس کے ساتھ رہتا اور (نہ ماننے والوں کو)دھمکاتا؟ ');
INSERT INTO "ur_maududi" VALUES(25,8,'25:8','یا اور کچھ نہیں تو اِس کے لیے کوئی خزانہ ہی اتار دیا جاتا، یااس کے پاس کوئی باغ ہی ہوتا جس سے یہ (اطمینان کی)روزی حاصل کرتا۔ “ اور ظالم کہتے ہیں”تم لوگ تو ایک سحر زدہ آدمی کے پیچھے لگ گئے ہو۔“');
INSERT INTO "ur_maududi" VALUES(25,9,'25:9','دیکھو، کیسی کیسی عجیب حجتیں یہ لوگ تمہارے آگے پیش کر رہے ہیں، ایسے بہکے ہیں کہ کوئی ٹھکانے کی بات اِن کو نہیں سُوجھتی۔ ');
INSERT INTO "ur_maududi" VALUES(25,10,'25:10','بڑا بابرکت ہے وہ جو اگر چاہے تو ان کی تجویز کر دہ چیزوں سے بھی زیادہ بڑھ چڑھ کر تم کو دے سکتا ہے ، (ایک نہیں)بہت سے باغ جن کے نیچے سے نہریں بہتی ہوں ، اور بڑے بڑے محل۔');
INSERT INTO "ur_maududi" VALUES(25,11,'25:11','اصل بات یہ ہے کہ یہ لوگ”اُس گھڑی“ کو جھُٹلا چکے ہیں ۔۔۔۔ اور جو اُس گھڑی کو جھُٹلائے اس کے لیے ہم نے بھڑکتی ہوئی آگ مہیّا کر رکھی ہے');
INSERT INTO "ur_maududi" VALUES(25,12,'25:12','وہ جب دُور سے اِن کو دیکھے گی تو یہ اُس کے غضب اور جوش کی آوازیں سُن لیں گے');
INSERT INTO "ur_maududi" VALUES(25,13,'25:13','اور جب یہ دست و پا بستہ اُس میں ایک تنگ جگہ ٹھونسے جائیں گے تو اپنی موت کو پکارنے لگیں گے');
INSERT INTO "ur_maududi" VALUES(25,14,'25:14','(اُس وقت ان سے کہا جائے گا کہ) آج ایک موت کو نہیں بہت سی موتوں کو پکارو');
INSERT INTO "ur_maududi" VALUES(25,15,'25:15','اِن سے پوچھو، یہ انجام اچھا ہے یا وہ اَبدی جنت جس کا وعدہ خداترس پرہیزگاروں سے کیا گیا ہے؟ جو اُن کے اعمال کی جزا اور اُن کے سفر کی آخری منزل ہو گی');
INSERT INTO "ur_maududi" VALUES(25,16,'25:16','جس میں اُن کی ہر خواہش پُوری ہوگی، جس میں وہ ہمیشہ ہمیشہ رہیں گے ، جس کا عطا کرنا تمہارے ربّ کے ذمّے ایک واجب الادا وعدہ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(25,17,'25:17','اور وہی دن ہوگا جب کہ (تمہارا ربّ)اِن لوگوں کو بھی گھیر لائے گا اور ان کے اُن معبُودوں کو بھی بُلالے گا جنہیں آج یہ اللہ کو چھوڑ کر پُوج رہے ہیں، پھر وہ اُن سے پُوچھے گا”کیا تم نے میرے اِن بندوں کو گمراہ کیا تھا؟ یا یہ خود راہِ راست سے بھٹک گئے تھے؟“ ');
INSERT INTO "ur_maududi" VALUES(25,18,'25:18','وہ عرض کریں گے”پاک ہے آپ کی ذات، ہماری تو یہ بھی مجال نہ تھی کہ آپ کے سوا کسی کو اپنا مولیٰ بنائیں۔ مگر آپ نے ان کو اور ان کے باپ دادا کو خوب سامانِ زندگی دیا حتٰی کہ یہ سبق بھُول گئے اور شامت زدہ ہو کر رہے۔“ ');
INSERT INTO "ur_maududi" VALUES(25,19,'25:19','یُوں جھُٹلا دیں گے وہ (تمہارے معبُود)تمہاری اُن باتوں کو جو آج تم کہہ رہے ہو ، پھر تم نہ اپنی شامت کو ٹال سکو گے نہ کہیں سے مدد پا سکو گے اور جو بھی تم میں سے ظلم کرے اُسے ہم سخت عذاب کا مزہ چکھائیں گے');
INSERT INTO "ur_maududi" VALUES(25,20,'25:20','اے محمدؐ ، تم سے پہلے جو رسُول بھی ہم نے بھیجے تھے وہ سب بھی کھانا کھانے والے اور بازاروں میں چلنے پھرنے والے لوگ ہی تھے۔ دراصل ہم نے تم لوگوں کو ایک دُوسرے کے لیے آزمائش کا ذریعہ بنا دیا ہے۔ کیا تم صبر کرتے ہو؟ تمہارا ربّ سب کچھ دیکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(25,21,'25:21','جو لوگ ہمارے حضور پیش ہونے کا اندیشہ نہیں رکھتے وہ کہتے ہیں”کیوں نہ فرشتے ہمارے پاس بھیجے جائیں؟ یا پھر ہم اپنے رب کو دیکھیں۔ “بڑا گھمنڈ لے بیٹھے یہ اپنے نفس میں اور حد سے گزر گئے یہ اپنی سر کشی میں');
INSERT INTO "ur_maududi" VALUES(25,22,'25:22','جس روز یہ فرشتوں کو دیکھیں گےوہ مجرموں کے لیے کسی بشارت کا دن نہ ہو گا ، چیخ اُٹھیں گے کہ پناہ بخدا');
INSERT INTO "ur_maududi" VALUES(25,23,'25:23','اور جو کچھ بھی ان کا کیا دھرا ہے اُسے لے کر ہم غبار کی طرح اُڑا دیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(25,24,'25:24','بس وہی لوگ جو جنّت کے مستحق ہیں اُس دن اچھی جگہ ٹھہریں گے اور دوپہر گزارنے کو عمدہ مقام پائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(25,25,'25:25','آسمان کو چیرتا ہوا ایک بادل اس روز نمودار ہو گا اور فرشتوں کے پرے کے پرے اتار دیے جائیں گے');
INSERT INTO "ur_maududi" VALUES(25,26,'25:26','اُس روز حقیقی بادشاہی صرف رحمٰن کی ہوگی۔ اور وہ منکرین کے لیے بڑا سخت دن ہوگا');
INSERT INTO "ur_maududi" VALUES(25,27,'25:27','ظالم انسان اپنا ہاتھ چبائے گا اور کہے گا "کاش میں نے رسول کا ساتھ دیا ہوتا');
INSERT INTO "ur_maududi" VALUES(25,28,'25:28','ہائے میری کم بختی، کاش میں نے فلاں شخص کو دوست نہ بنایا ہوتا');
INSERT INTO "ur_maududi" VALUES(25,29,'25:29','اُس کے بہکائے میں آکر میں نے وہ نصیحت نہ مانی جو میرے پاس آئی تھی، شیطان انسان کے حق میں بڑا ہی بے وفا نِکلا۔ “');
INSERT INTO "ur_maududi" VALUES(25,30,'25:30','اور رسُولؐ کہے گا کہ ”اے میرے ربّ، میری قوم کے لوگوں نے اس قرآن کو نشانہٴ تضحیک بنا لیا تھا۔“');
INSERT INTO "ur_maududi" VALUES(25,31,'25:31','اے محمد ؐ ، ہم نے تو اِسی طرح مجرموں کو ہر نبی کا دشمن بنایا ہے اور تمہارے لیے تمہارا ربّ ہی رہنمائی اور مدد کو کافی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(25,32,'25:32','منکرین کہتے ہیں”اِس شخص پر سارا قرآن ایک ہی وقت میں کیوں نہ اُتار دیا گیا؟“ ۔۔۔۔ ہاں، ایسا اس لیے کیا گیا ہے کہ اس کو اچھی طرح ہم تمہارے ذہن نشین کرتے رہیں اور (اسی غرض کے لیے)ہم نے اس کو ایک خاص ترتیب کے ساتھ الگ الگ اجزاء کی شکل دی ہے');
INSERT INTO "ur_maududi" VALUES(25,33,'25:33','اور (اس میں یہ مصلحت بھی ہے)کہ جب کبھی وہ تمہارے سامنے کوئی نرالی بات (یا عجیب سوال)لے کر آئے، اُس کا ٹھیک جواب بر وقت ہم نے تمہیں دے دیا اور بہترین طریقے سے بات کھول دی ');
INSERT INTO "ur_maududi" VALUES(25,34,'25:34','جو لوگ اوندھے منہ جہنّم کی طرف دھکیلے جانے والے ہیں ان کا موقف بہت بُرا اور ان کی راہ حد درجہ غلط ہے۔ ');
INSERT INTO "ur_maududi" VALUES(25,35,'25:35','ہم نے موسیٰؑ کو کتاب دی اور اس کے ساتھ اس کے بھائی ہارونؑ کو مددگار کے طور پر لگایا');
INSERT INTO "ur_maududi" VALUES(25,36,'25:36','اور اُن سے کہا کہ جاوٴ اُس قوم کی طرف جس نے ہماری آیات کو جھُٹلا دیا ہے۔ آخر کار اُن لوگوں کو ہم نے تباہ کر کے رکھ دیا');
INSERT INTO "ur_maududi" VALUES(25,37,'25:37','یہی حال قومِ نوح ؑ کا ہوا جب انہوں نے رسُولوں کی تکذیب کی۔ ہم نے اُن کو غرق کر دیا اور دنیا بھر کے لوگوں کے لیے ایک نشانِ عبرت بنا دیا اور ان ظالموں کے لیے ایک دردناک عذاب ہم نے مہیّا کر رکھا ہے');
INSERT INTO "ur_maududi" VALUES(25,38,'25:38','اِسی طرح عاد اور ثمود اور اصحابُ الرس اور بیچ کی صدیوں کے بہت سے لوگ تباہ کیے گئے');
INSERT INTO "ur_maududi" VALUES(25,39,'25:39','اور ان میں سے ہر ایک کو ہم نے (پہلے تباہ ہونے والوں کی) مثالیں دے دے کر سمجھایا اور آخرکار ہر ایک کو غارت کر دیا');
INSERT INTO "ur_maududi" VALUES(25,40,'25:40','اور اُس بستی پر تو اِن کا گزر ہو چکا ہے جس پر بد ترین بارش برسائی گئی تھی۔ کیا اِنہوں نے اس کا حال دیکھا نہ ہوگا؟ مگر یہ موت کے بعد دوسری زندگی کی توقع ہی نہیں رکھتے۔ ');
INSERT INTO "ur_maududi" VALUES(25,41,'25:41','یہ لوگ جب تمہیں دیکھتے ہیں تو تمہارا مذاق بنا لیتے ہیں (کہتے ہیں) "کیا یہ شخص ہے جسے خدا نے رسول بنا کر بھیجا ہے؟');
INSERT INTO "ur_maududi" VALUES(25,42,'25:42','اِس نے تو ہمیں گمراہ کر کے اپنے معبُودوں سے برگشتہ ہی کر دیا ہوتا اگر ہم اُن کی عقیدت پر جم نہ گئے ہوتے ۔“ اچھا، وہ وقت دُور نہیں ہے جب عذاب دیکھ کر اِنہیں خود معلوم ہو جائے گا کہ کون گمراہی میں دُور نکل گیا تھا');
INSERT INTO "ur_maududi" VALUES(25,43,'25:43','کبھی تم نے اُس شخص کے حال پر غور کیا ہے جس نے اپنی خواہش ِ نفس کو اپنا خدا بنا لیا ہو؟ کیا تم ایسے شخص کو راہِ راست پر لانے کا ذمّہ لے سکتے ہو؟');
INSERT INTO "ur_maududi" VALUES(25,44,'25:44','کیا تم سمجھتے ہو کہ ان میں سے اکثر لوگ سُنتے اور سمجھتے ہیں؟ یہ تو جانوروں کی طرح ہیں، بلکہ اُن سے بھی گئے گُزرے۔ ');
INSERT INTO "ur_maududi" VALUES(25,45,'25:45','تم نے دیکھا نہیں کہ تمہارا ربّ کس طرح سایہ پھیلا دیتا ہے؟ اگر وہ چاہتا تو اسے دائمی سایہ بنا دیتا۔ ہم نے سُورج کو اُس پر دلیل بنایا');
INSERT INTO "ur_maududi" VALUES(25,46,'25:46','پھر (جیسے جیسے سُورج اُٹھتا جاتا ہے)ہم اس سائے کو رفتہ رفتہ اپنی طرف سمیٹتے چلے جاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(25,47,'25:47','اور وہ اللہ ہی ہے جس نے رات کو تمہارے لیے لباس ، اور نیند کو سکونِ موت، اور دن کو جی اُٹھنے کا وقت بنایا۔ ');
INSERT INTO "ur_maududi" VALUES(25,48,'25:48','اور وہی ہے جو اپنی رحمت کے آگے آگے ہواوٴں کو بشارت بنا کر بھیجتا ہے۔ پھر آسمان سے پاک پانی نازل کرتا ہے');
INSERT INTO "ur_maududi" VALUES(25,49,'25:49','تاکہ ایک مُردہ علاقے کو اس کے ذریعے زندگی بخشے اور اپنی مخلوق میں سے بہت سے جانوروں اور انسانوں کو سیراب کرے۔ ');
INSERT INTO "ur_maududi" VALUES(25,50,'25:50','اس کرشمے کو ہم بار بار ان کے سامنے لاتے ہیں تاکہ وہ کچھ سبق لیں، مگر اکثر لوگ کُفر اور ناشکری کے سوا کوئی دُوسرا رویّہ اختیار کرنے سے انکار کر دیتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(25,51,'25:51','اگر ہم چاہتے تو ایک ایک بستی میں ایک ایک نذیر اُٹھا کھڑا کرتے ');
INSERT INTO "ur_maududi" VALUES(25,52,'25:52','پس اے نبیؐ ، کافروں کی بات ہر گز نہ مانو اور اس قرآن کو لے کر ان کے ساتھ جہاد کبیر کرو');
INSERT INTO "ur_maududi" VALUES(25,53,'25:53','اور وہی ہے جس نے دوسمندروں کو ملا رکھا ہے۔ ایک لذیذ و شیریں، دُوسرا تلخ و شور۔ اور دونوں کے درمیان ایک پردہ حائل ہے۔ ایک رکاوٹ ہے جو انہیں گڈ مڈ ہونے سے روکے ہوئے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(25,54,'25:54','اور وہی ہے جس نے پانی سے ایک بشر پیدا کیا، پھر اس نے نسب اور سُسرال کے دو الگ سلسلے چلائے۔ تیرا ربّ بڑا ہی قدرت والا ہے');
INSERT INTO "ur_maududi" VALUES(25,55,'25:55','اس خدا کو چھوڑ کر لوگ اُن کو پُوج رہے ہیں جو نہ ان کو نفع پہنچا سکتے ہیں نہ نقصان ، اور اوپر سے مزید یہ کہ کافر اپنے ربّ کے مقابلے میں ہر باغی کا مددگار بنا ہوا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(25,56,'25:56','اے محمد ؐ ، تم کو تو ہم نے بس ایک مبشِّر اور نذیر بنا کر بھیجا ہے');
INSERT INTO "ur_maududi" VALUES(25,57,'25:57','ان سے کہہ دوکہ”میں اس کام پر تم سے کوئی اُجرت نہیں مانگتا، میری اُجرت بس یہی ہے کہ جس کا جی چاہے وہ اپنے ربّ کا راستہ اختیار کر لے۔ “');
INSERT INTO "ur_maududi" VALUES(25,58,'25:58','اور اے محمدؐ، اُس خدا پر بھروسہ رکھو جو زندہ ہے اور کبھی مرنے والا نہیں اس کی حمد کے ساتھ اس کی تسبیح کرو اپنے بندوں کے گناہوں سے بس اُسی کا باخبر ہونا کافی ہے');
INSERT INTO "ur_maududi" VALUES(25,59,'25:59','وہ جس نے چھ دنوں میں زمین اور آسمانوں کو اور اُن ساری چیزوں کو بنا کر رکھ دیا جو آسمان و زمین کے درمیان ہیں، پھر آپ ہی (کائنات کے تختِ سلطنت)”عرش“ پر جلوہ فرما ہوا۔ رحمٰن ، اس کی شان بس کسی جاننے والے سے پوچھو');
INSERT INTO "ur_maududi" VALUES(25,60,'25:60','اِن لوگوں سے جب کہا جاتا ہے کہ اس رحمٰن کو سجدہ کرو تو کہتے ہیں”رحمٰن کیا ہوتا ہے؟ کیا بس جسے تُو کہہ دے اسی کو سجدہ کرتے پھریں؟ “ یہ دعوت ان کی نفرت میں اُلٹا اور اضافہ کر دیتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(25,61,'25:61','بڑا متبرّ ک ہے وہ جس نے آسمان میں بُرج بنائے اور اس میں ایک چراغ اور ایک چمکتا چاند روشن کیا');
INSERT INTO "ur_maududi" VALUES(25,62,'25:62','وہی ہے جس نے رات اور دن کو ایک دُوسرے کا جانشین بنایا، ہر اس شخص کے لیے جو سبق لینا چاہے، یا شکر گزار ہونا چاہے۔ ');
INSERT INTO "ur_maududi" VALUES(25,63,'25:63','رحمٰن کے (اصلی)بندے وہ ہیں جو زمین پر نرم چال چلتے ہیں اور جاہل ان کے مُنہ آئیں تو کہہ دیتے ہیں کہ تم کو سلام۔ ');
INSERT INTO "ur_maududi" VALUES(25,64,'25:64','جو اپنے ربّ کے حضور سجدے اور قیام میں راتیں گزارتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(25,65,'25:65','جو دعائیں کرتے ہیں کہ "اے ہمارے رب، جہنم کے عذاب سے ہم کو بچا لے، اُس کا عذاب تو جان کا لاگو ہے');
INSERT INTO "ur_maududi" VALUES(25,66,'25:66','وہ تو بڑا ہی بُرا مستقر اور مقام ہے۔ ');
INSERT INTO "ur_maududi" VALUES(25,67,'25:67','جو خرچ کرتے ہیں تو نہ فضُول خرچی کرتے ہیں نہ بُخل، بلکہ اُن کا خرچ دونوں انتہاوٴں کے درمیان اعتدال پر قائم رہتا ہے');
INSERT INTO "ur_maududi" VALUES(25,68,'25:68','جو اللہ کے سوا کسی اور معبُود کو نہیں پُکارتے ، اللہ کی حرام کی ہوئی کسی جان کو ناحق ہلاک نہیں کرتے، اور نہ زنا کے مرتکب ہوتے ہیں ۔۔۔۔ یہ کام جو کوئی کرے وہ اپنے گناہ کا بدلہ پائے گا');
INSERT INTO "ur_maududi" VALUES(25,69,'25:69','قیامت کے روز اُس کو مکرّرعذاب دیا جائے گا اور اسی میں وہ ہمیشہ ذلّت کے ساتھ پڑا رہے گا');
INSERT INTO "ur_maududi" VALUES(25,70,'25:70','اِلّا یہ کہ کوئی (ان گناہوں کے بعد)توبہ کر چکا ہو اور ایمان لا کر عملِ صالح کرنے لگا ہو۔ ایسے لوگوں کی بُرائیوں کو اللہ بھلائیوں سے بدل دے گا اور وہ بڑا غفور و رحیم ہے');
INSERT INTO "ur_maududi" VALUES(25,71,'25:71','جو شخص توبہ کر کے نیک عملی اختیار کرتا ہے وہ تو اللہ کی طرف پلٹ آتا ہے جیسا کہ پلٹنے کا حق ہے ');
INSERT INTO "ur_maududi" VALUES(25,72,'25:72','(اور رحمٰن کے بندے وہ ہیں)جو جھُوٹ کے گواہ نہیں بنتے اور کسی لغو چیز پر اُن کا گزر ہو جائے تو شریف آدمیوں کی طرح گُزر جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(25,73,'25:73','جنہیں اگر اُن کے ربّ کی آیات سُنا کر نصیحت کی جاتی ہے تو وہ اس پر اندھے اور بہرے بن کر نہیں رہ جاتے');
INSERT INTO "ur_maududi" VALUES(25,74,'25:74','جو دُعائیں مانگا کرتے ہیں کہ”اے ہمارے ربّ، ہمیں اپنی بیویوں اور اپنی اولاد سے آنکھوں کی ٹھنڈک دے اور ہم کو پرہیزگاروں کا امام بنا“');
INSERT INTO "ur_maududi" VALUES(25,75,'25:75','یہ ہیں وہ لوگ جو اپنے صبر کا پھل منزلِ بلند کی شکل میں پائیں گے۔ آداب و تسلیمات سے اُن کا استقبال ہو گا');
INSERT INTO "ur_maududi" VALUES(25,76,'25:76','وہ ہمیشہ ہمیشہ وہاں رہیں گے۔ کیا ہی اچھا ہے وہ مستقر اور وہ مقام');
INSERT INTO "ur_maududi" VALUES(25,77,'25:77','اے محمدؐ ، لوگوں سے کہو”میرے ربّ کو تمہاری کیا حاجت پڑی ہے اگر تم اُس کو نہ پکارو۔ اب کہ تم نے جھُٹلا دیا ہے، عنقریب وہ سزا پاوٴ گے کہ جان چھُڑانی محال ہو گی۔“');
INSERT INTO "ur_maududi" VALUES(26,1,'26:1','ط س م');
INSERT INTO "ur_maududi" VALUES(26,2,'26:2','یہ کتابِ مبین کی آیات ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,3,'26:3','اے محمدؐ ، شاید تم اس غم میں اپنی جان کھو دو گے کہ یہ لوگ ایمان نہیں لاتے۔ ');
INSERT INTO "ur_maududi" VALUES(26,4,'26:4','ہم چاہیں تو آسمان سے ایسی نشانی نازل کر سکتے ہیں کہ اِن کی گردنیں اس کے آگے جُھک جائیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,5,'26:5','اِن لوگوں کے پاس رحمان کی طرف سے جو نئی نصیحت بھی آتی ہے یہ اس سے منہ موڑ لیتے ہیں');
INSERT INTO "ur_maududi" VALUES(26,6,'26:6','اب کہ یہ جھُٹلا چکے ہیں، عنقریب اِن کو اس چیز کی حقیقت (مختلف طریقوں سے)معلوم ہو جائے گی جس کا یہ مذاق اُڑاتے رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,7,'26:7','اور کیا انہوں نے کبھی زمین پر نگاہ نہیں ڈالی کہ ہم نے کتنی کثیر مقدار میں ہر طرح کی عمدہ نباتات اس میں پیدا کی ہیں؟');
INSERT INTO "ur_maududi" VALUES(26,8,'26:8','یقیناً اس میں ایک نشانی ہے ، مگر ان میں سے اکثر ماننے والے نہیں');
INSERT INTO "ur_maududi" VALUES(26,9,'26:9','اور حقیقت یہ ہے کہ تیرا ربّ زبردست بھی ہے اور رحیم بھی۔ ');
INSERT INTO "ur_maududi" VALUES(26,10,'26:10','اِنہیں اُس وقت کا قصہ سُناوٴ جب کہ تمہارے ربّ نے موسیٰؑ کو پکارا” ظالم قوم کے پاس جا');
INSERT INTO "ur_maududi" VALUES(26,11,'26:11','فرعون کی قوم کے پاس ۔۔۔۔ کیا وہ نہیں ڈرتے؟“ ');
INSERT INTO "ur_maududi" VALUES(26,12,'26:12','اُس نے عرض کیا "اے رب، مجھے خوف ہے کہ وہ مجھے جھٹلا دیں گے');
INSERT INTO "ur_maududi" VALUES(26,13,'26:13','میرا سینہ گھُٹتا ہے اور میری زبان نہیں چلتی۔ آپ ہارونؑ کی طرف رسالت بھیجیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,14,'26:14','اور مجھ پر اُن کےہاں ایک جُرم کا الزام بھی ہے ، اس لیے میں ڈرتا ہوں کہ وہ مجھے قتل کر دیں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(26,15,'26:15','فرمایا”ہر گز نہیں، تم دونوں جاوٴ ہماری نشانیاں لے کر ، ہم تمہارے ساتھ سب کچھ سُنتے رہیں گے');
INSERT INTO "ur_maududi" VALUES(26,16,'26:16','فرعون کے پاس جاؤ اور اس سے کہو، ہم کو رب العٰلمین نے اس لیے بھیجا ہے');
INSERT INTO "ur_maududi" VALUES(26,17,'26:17','کہ تُو بنی اسرائیل کو ہمارے ساتھ جانے دے۔“ ');
INSERT INTO "ur_maududi" VALUES(26,18,'26:18','فرعون نے کہا”کیا ہم نے تجھ کو اپنے ہاں بچہ سا نہیں پالا تھا؟ تُو نے اپنی عمر کے کئی سال ہمارے ہاں گزارے');
INSERT INTO "ur_maududi" VALUES(26,19,'26:19','اور اس کے بعد کر گیا جو کچھ کہ کر گیا ، تُو بڑا احسان فراموش آدمی ہے۔“');
INSERT INTO "ur_maududi" VALUES(26,20,'26:20','موسیٰؑ نے جواب دیا”اُس وقت وہ کام میں نے نا دانستگی میں کر دیا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(26,21,'26:21','پھر میں تمہارے خوف سے بھاگ گیا۔ اس کے بعد میرے ربّ نے مجھ کو حکم عطا کیا اور مجھے رسُولوں میں شامل فرما لیا');
INSERT INTO "ur_maududi" VALUES(26,22,'26:22','رہا تیرا احسان جو تُو نے مجھ پر جتایا ہے تو اس کی حقیقت یہ ہے کہ تُو نے بنی اسرائیل کو غلام بنا لیا تھا۔“ ');
INSERT INTO "ur_maududi" VALUES(26,23,'26:23','فرعون نے کہا ”اور یہ ربّ العالمین کیا ہوتا ہے؟“ ');
INSERT INTO "ur_maududi" VALUES(26,24,'26:24','موسیٰؑ نے جواب دیا”آسمانوں اور زمین کا ربّ، اور اُن سب چیزوں کا ربّ جو آسمان و زمین کے درمیان ہیں، اگر تم یقین لانے والے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(26,25,'26:25','فرعون نے اپنے گرد و پیش کے لوگوں سے کہا "سُنتے ہو؟"');
INSERT INTO "ur_maududi" VALUES(26,26,'26:26','موسیٰؑ نے کہا”تمہارا ربّ بھی اور تمہارے اُن آباوٴ اجداد کا ربّ بھی جو گزر چکے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(26,27,'26:27','فرعون نے (حاضرین سے) کہا "تمہارے یہ رسول صاحب جو تمہاری طرف بھیجے گئے ہیں، بالکل ہی پاگل معلوم ہوتے ہیں"');
INSERT INTO "ur_maududi" VALUES(26,28,'26:28','موسیٰؑ نے کہا”مشرق و مغرب اور جو کچھ ان کے درمیان میں ہے سب کا ربّ، اگر آپ لوگ کچھ عقل رکھتے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(26,29,'26:29','فرعون نے کہا”اگر تُو نے میرے سوا کسی اور کو معبُود مانا تو تجھے بھی اُن لوگوں میں شامل کر دوں گا جو قید خانوں میں پڑے سڑ رہے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(26,30,'26:30','موسیٰؑ نے کہا”اگر چہ میں لے آوٴں تیرے سامنے ایک صریح چیز بھی؟“ ');
INSERT INTO "ur_maududi" VALUES(26,31,'26:31','فرعون نے کہا”اچھا تو لے آ اگر تُو سچا ہے۔ “');
INSERT INTO "ur_maududi" VALUES(26,32,'26:32','(اس کی زبان سے یہ بات نکلتے ہی)موسیٰؑ نے اپنا عصا پھینکا اور یکایک وہ ایک صریح اژدہا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(26,33,'26:33','پھر اُس نے اپنا ہاتھ (بغل سے)کھینچا اور وہ سب دیکھنے والوں کے سامنے چمک رہا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(26,34,'26:34','فرعون اپنے گرد و پیش کے سرداروں سے بولا "یہ شخص یقیناً ایک ماہر جادوگر ہے');
INSERT INTO "ur_maududi" VALUES(26,35,'26:35','چاہتا ہے کہ اپنے جادُو کے زور سے تم کو تمہارے ملک سے نکال دے۔ اب بتاوٴ تم کیا حکم دیتے ہو؟“ ');
INSERT INTO "ur_maududi" VALUES(26,36,'26:36','انہوں نے کہا "اسے اور اس کے بھائی کو روک لیجیے اور شہروں میں ہرکارے بھیج دیجیے');
INSERT INTO "ur_maududi" VALUES(26,37,'26:37','کہ ہر سیانے جادوگر کو آپ کے پاس لے آئیں"');
INSERT INTO "ur_maududi" VALUES(26,38,'26:38','چنانچہ ایک روز مقرر وقت پر جادُو گر اکٹھے کر لیے گئے');
INSERT INTO "ur_maududi" VALUES(26,39,'26:39','اور لوگوں سے کہا گیا”تم اجتماع میں چلو گے؟ ');
INSERT INTO "ur_maududi" VALUES(26,40,'26:40','شاید کہ ہم جادُوگروں کے دین ہی پر رہ جائیں اگر وہ غالب رہے۔“ ');
INSERT INTO "ur_maududi" VALUES(26,41,'26:41','جب جادُو گر میدان میں آئے تو انہوں نے فرعون سے کہا”ہمیں انعام تو ملے گا اگر ہم غالب رہے؟“ ');
INSERT INTO "ur_maududi" VALUES(26,42,'26:42','اس نے کہا”ہاں، اور تم تو اس وقت مقربّین میں شامل ہو جاوٴ گے۔“ ');
INSERT INTO "ur_maududi" VALUES(26,43,'26:43','موسیٰؑ نے کہا "پھینکو جو تمہیں پھینکنا ہے"');
INSERT INTO "ur_maududi" VALUES(26,44,'26:44','انہوں نے فوراً اپنی رسّیاں اور لاٹھیاں پھینک دیں اور بولے”فرعون کے اقبال سے ہم ہی غالب رہیں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(26,45,'26:45','پھر موسیٰؑ نے اپنا عصا پھینکا تو یکایک وہ ان کے جھوٹے کرشموں کو ہڑپ کرتا چلا جا رہا تھا');
INSERT INTO "ur_maududi" VALUES(26,46,'26:46','اس پر سارے جادوگر بے اختیار سجدے میں گر پڑے');
INSERT INTO "ur_maududi" VALUES(26,47,'26:47','اور بول اٹھے کہ "مان گئے ہم رب العالمین کو');
INSERT INTO "ur_maududi" VALUES(26,48,'26:48','موسیٰؑ اور ہارونؑ کے ربّ کو۔“ ');
INSERT INTO "ur_maududi" VALUES(26,49,'26:49','فرعون نے کہا”تم موسیٰؑ کی بات مان گئے قبل اس کے کہ میں تمہیں اجازت دیتا! ضرور یہ تمہارا بڑا ہے جس نے تمہیں جادُو سکھایا ہے۔ اچھا، ابھی تمہیں معلوم ہوا جاتا ہے، میں تمہارے ہاتھ پاوٴں مخالف سمتوں سے کٹواوٴں گا اور تم سب کو سُولی پر چڑھادوں گا۔“ ');
INSERT INTO "ur_maududi" VALUES(26,50,'26:50','انہوں نے جواب دیا "کچھ پرواہ نہیں، ہم اپنے رب کے حضور پہنچ جائیں گے');
INSERT INTO "ur_maududi" VALUES(26,51,'26:51','اور ہمیں توقع ہے کہ ہمارا ربّ ہمارے گناہ معاف کر دے گا کیونکہ سب سے پہلے ہم ایمان لائے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(26,52,'26:52',' ہم نے موسیٰؑ کو وحی بھیجی کہ”راتوں رات میرے بندوں کو لے کر نکل جاوٴ، تمہارا پیچھا کیا جائے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(26,53,'26:53','اس پر فرعون نے (فوجیں جمع کرنے کے لیے) شہروں میں نقیب بھیج دیے');
INSERT INTO "ur_maududi" VALUES(26,54,'26:54','(اور کہلا بھیجا) کہ "یہ کچھ مٹھی بھر لوگ ہیں');
INSERT INTO "ur_maududi" VALUES(26,55,'26:55','اور انہوں نے ہم کو بہت ناراض کیا ہے');
INSERT INTO "ur_maududi" VALUES(26,56,'26:56','اور ہم ایک ایسی جماعت ہیں جس کا شیوہ ہر وقت چوکنّا رہنا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(26,57,'26:57','اِس طرح ہم انہیں ان کے باغوں اور چشموں');
INSERT INTO "ur_maududi" VALUES(26,58,'26:58','اور خزانوں اور ان کی بہترین قیام گاہوں سے نکال لائے۔ ');
INSERT INTO "ur_maududi" VALUES(26,59,'26:59','یہ تو ہوا اُن کے ساتھ، اور (دُوسری طرف)بنی اسرائیل کو ہم نے ان سب چیزوں کا وارث کر دیا۔ ');
INSERT INTO "ur_maududi" VALUES(26,60,'26:60','صبح ہوتے ہی یہ لوگ اُن کے تعاقب میں چل پڑے');
INSERT INTO "ur_maududi" VALUES(26,61,'26:61','جب دونوں گروہوں کا آمنا سامنا ہوا تو موسیٰؑ کے ساتھی چیخ اٹھے کہ "ہم تو پکڑے گئے"');
INSERT INTO "ur_maududi" VALUES(26,62,'26:62','موسیٰؑ نے کہا”ہر گز نہیں۔ میرے ساتھ میرا ربّ ہے۔ وہ ضرور میری رہنمائی فرمائے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(26,63,'26:63','ہم نے موسیٰؑ کو وحی کے ذریعہ سے حکم دیا کہ”ماراپنا عصا سمندر پر۔“ یکایک سمندر پھٹ گیا اور اس کا ہر ٹکڑا ایک عظیم الشان پہاڑ کی طرح ہو گیا۔ ');
INSERT INTO "ur_maududi" VALUES(26,64,'26:64','اُسی جگہ ہم دُوسرے گروہ کو بھی قریب لے آئے۔ ');
INSERT INTO "ur_maududi" VALUES(26,65,'26:65','موسیٰؑ اور اُن سب لوگوں کو جو اس کے ساتھ تھے، ہم نے بچا لیا');
INSERT INTO "ur_maududi" VALUES(26,66,'26:66','اور دوسروں کو غرق کر دیا');
INSERT INTO "ur_maududi" VALUES(26,67,'26:67','اس واقعہ میں ایک نشانی ہے ،مگر اِن لوگوں میں سے اکثر ماننے والے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(26,68,'26:68','اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی');
INSERT INTO "ur_maududi" VALUES(26,69,'26:69','اور اِنہیں ابراہیمؑ کا قصہ سُناوٴ ');
INSERT INTO "ur_maududi" VALUES(26,70,'26:70','جبکہ اس نے اپنے باپ اور اپنی قوم سے پُوچھا تھا کہ”یہ کیا چیزیں ہیں جن کو تم پُوجتے ہو؟“ ');
INSERT INTO "ur_maududi" VALUES(26,71,'26:71','انہوں نے جواب دیا”کچھ بُت ہیں جن کی ہم پُوجا کرتے ہیں اور انہی کی سیوا میں ہم لگے رہتے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(26,72,'26:72','اس نے پوچھا "کیا یہ تمہاری سنتے ہیں جب تم انہیں پکارتے ہو؟');
INSERT INTO "ur_maududi" VALUES(26,73,'26:73','یا یہ تمہیں کچھ نفع یا نقصان پہنچاتے ہیں؟"');
INSERT INTO "ur_maududi" VALUES(26,74,'26:74','انہوں نے جواب دیا”نہیں، بلکہ ہم نے اپنے باپ دادا کو ایسا ہی کرتے پایا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(26,75,'26:75','اس پر ابراہیمؑ نے کہا "کبھی تم نے (آنکھیں کھول کر) اُن چیزوں کو دیکھا بھی جن کی بندگی تم');
INSERT INTO "ur_maududi" VALUES(26,76,'26:76','اور تمہارے پچھلے باپ دادا بجا لاتے رہے؟ ');
INSERT INTO "ur_maududi" VALUES(26,77,'26:77','میرے تو یہ سب دشمن ہیں ، بجز ایک ربّ العالمین کے');
INSERT INTO "ur_maududi" VALUES(26,78,'26:78','جس نے مجھے پیدا کیا ، پھر وہی میری رہنمائی فرماتا ہے');
INSERT INTO "ur_maududi" VALUES(26,79,'26:79','جو مجھے کھلاتا اور پلاتا ہے');
INSERT INTO "ur_maududi" VALUES(26,80,'26:80','اور جب بیمار ہو جاتا ہوں تو وہی مجھے شفا دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(26,81,'26:81','جو مجھے موت دے گا اور پھر دوبارہ مجھ کو زندگی بخشے گا');
INSERT INTO "ur_maududi" VALUES(26,82,'26:82','اور جس سے میں اُمید رکھتا ہوں کہ روزِ جزا میں وہ میری خطا معاف فرما دے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(26,83,'26:83','(اِس کے بعد ابراہیمؑ نے دُعا کی)”اے میرے ربّ، مجھے حکم عطا کر۔ اور مجھ کو صالحوں کے ساتھ مِلا۔ ');
INSERT INTO "ur_maududi" VALUES(26,84,'26:84','اور بعد کے آنے والوں میں مجھ کو سچی ناموری عطا کر۔ ');
INSERT INTO "ur_maududi" VALUES(26,85,'26:85','اور مجھے جنتِ نعیم کے وارثوں میں شامل فرما');
INSERT INTO "ur_maududi" VALUES(26,86,'26:86','اور میرے باپ کو معاف کر دے کہ بے شک وہ گمراہ لوگوں میں سے ہے ');
INSERT INTO "ur_maududi" VALUES(26,87,'26:87','اور مجھے اُس دن رُسوا نہ کر جبکہ سب لوگ زندہ کر کے اُٹھائے جائیں گے ');
INSERT INTO "ur_maududi" VALUES(26,88,'26:88','جبکہ نہ مال کوئی فائدہ دے گا نہ اولاد');
INSERT INTO "ur_maududi" VALUES(26,89,'26:89','بجز اس کے کہ کوئی شخص قلبِ سلیم لیے ہوئے اللہ کے حضور حاضر ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(26,90,'26:90','(اُس روز )جنّت پرہیز گاروں کے قریب لے آئی جائے گی');
INSERT INTO "ur_maududi" VALUES(26,91,'26:91','اور دوزخ بہکے ہوئے لوگوں کے سامنے کھول دی جائے گی ');
INSERT INTO "ur_maududi" VALUES(26,92,'26:92','اور ان سے پوچھا جائے گا کہ "اب کہاں ہیں وہ جن کی تم خدا کو چھوڑ کر عبادت کیا کرتے تھے؟');
INSERT INTO "ur_maududi" VALUES(26,93,'26:93','کیا وہ تمہاری کچھ مدد کر رہے ہیں یا خود اپنا بچاؤ کر سکتے ہیں؟"');
INSERT INTO "ur_maududi" VALUES(26,94,'26:94','پھر وہ معبود اور یہ بہکے ہوئے لوگ');
INSERT INTO "ur_maududi" VALUES(26,95,'26:95','اور ابلیس کے لشکر سب کے سب اس میں اُوپر تلے دھکیل دیے جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(26,96,'26:96','وہاں یہ سب آپس میں جھگڑیں گے اور یہ بہکے ہوئے لوگ (اپنے معبودوں سے) کہیں گے');
INSERT INTO "ur_maududi" VALUES(26,97,'26:97','کہ "خدا کی قسم، ہم تو صریح گمراہی میں مبتلا تھے');
INSERT INTO "ur_maududi" VALUES(26,98,'26:98','جبکہ تم کو رب العالمین کی برابری کا درجہ دے رہے تھے');
INSERT INTO "ur_maududi" VALUES(26,99,'26:99','اور وہ مجرم لوگ ہی تھے جنہوں نے ہم کو اس گمراہی میں ڈالا۔ ');
INSERT INTO "ur_maududi" VALUES(26,100,'26:100','اب نہ ہمارا کوئی سفارشی ہے ');
INSERT INTO "ur_maududi" VALUES(26,101,'26:101','اور نہ کوئی جگری دوست۔ ');
INSERT INTO "ur_maududi" VALUES(26,102,'26:102','کاش ہمیں ایک دفعہ پھر پلٹنے کا موقع مل جائے تو ہم مومن ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(26,103,'26:103','یقیناً اس میں ایک بڑی نشانی ہے ، مگر ان میں سے اکثر لوگ ایمان لانے والے نہیں');
INSERT INTO "ur_maududi" VALUES(26,104,'26:104','اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی');
