INSERT INTO "ur_maududi" VALUES(26,106,'26:106','یاد کرو جبکہ اُن کے بھائی نوحؑ نے ان سے کہا تھا”کیا تم ڈرتے نہیں ہو؟ ');
INSERT INTO "ur_maududi" VALUES(26,107,'26:107','میں تمہارے لیے ایک امانت دار رسُول ہوں ');
INSERT INTO "ur_maududi" VALUES(26,108,'26:108','لہٰذا تم اللہ سے ڈرو اور میری اطاعت کرو۔ ');
INSERT INTO "ur_maududi" VALUES(26,109,'26:109','میں اس کام پر تم سے کسی اجر کا طالب نہیں ہوں۔ میرا اجر تو ربّ العالمین کے ذمّہ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(26,110,'26:110','پس تم اللہ سے ڈرو اور (بے کھٹکے)میری اطاعت کرو۔“ ');
INSERT INTO "ur_maududi" VALUES(26,111,'26:111','انہوں نے جواب دیا”کیا ہم تجھے مان لیں حالانکہ تیری پیروی رذیل ترین لوگوں نے اختیار کی ہے؟“ ');
INSERT INTO "ur_maududi" VALUES(26,112,'26:112','نوحؑ نے کہا "میں کیا جانوں کہ ان کے عمل کیسے ہیں');
INSERT INTO "ur_maududi" VALUES(26,113,'26:113','ان کا حساب تو میرے ربّ کے ذمّہ ہے، کاش تم کچھ شعور سے کام لو۔ ');
INSERT INTO "ur_maududi" VALUES(26,114,'26:114','میرا یہ کام نہیں ہے کہ جو ایمان لائیں ان کو میں دھتکار دوں');
INSERT INTO "ur_maududi" VALUES(26,115,'26:115','میں تو بس ایک صاف صاف متنبّہ کر دینے والا آدمی ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(26,116,'26:116','انہوں نے کہا”اے نُوحؑ ، اگر تُو باز نہ آیا تو پھِٹکارے ہوئے لوگوں میں شامل ہو کر رہے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(26,117,'26:117','نُوحؑ نے دُعا کی”اے میرے ربّ، میری قوم نے مجھے جھُٹلا دیا۔ ');
INSERT INTO "ur_maududi" VALUES(26,118,'26:118','اب میرے اور ان کے درمیان دو ٹوک فیصلہ کر دے اور مجھے اور جو مومن میرے ساتھ ہیں ان کو نجات دے۔“ ');
INSERT INTO "ur_maududi" VALUES(26,119,'26:119','آخر کار ہم نے اس کو اور اس کے ساتھیوں کو ایک بھری ہوئی کشتی میں بچا لیا۔ ');
INSERT INTO "ur_maududi" VALUES(26,120,'26:120','اور اس کے بعد باقی لوگوں کو غرق کر دیا');
INSERT INTO "ur_maududi" VALUES(26,121,'26:121','یقیناً اس میں ایک نشانی ہے، مگر ان میں سے اکثر لوگ ماننے والے نہیں');
