INSERT INTO "ur_maududi" VALUES(26,128,'26:128','یہ تمہارا کیا حال ہے کہ ہر اُونچے مقام پر لاحاصل ایک یادگار عمارت بنا ڈالتے ہو ');
INSERT INTO "ur_maududi" VALUES(26,129,'26:129','اور بڑے بڑے قصر تعمیر کرتے ہو گویا تمہیں ہمیشہ رہنا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(26,130,'26:130','اور جب کسی پر ہاتھ ڈالتے ہو جبّار بن کر ڈالتے ہو۔ ');
