INSERT INTO "ur_maududi" VALUES(26,136,'26:136','انہوں نے جواب دیا "تو نصیحت کر یا نہ کر، ہمارے لیے یکساں ہے');
INSERT INTO "ur_maududi" VALUES(26,137,'26:137','یہ باتیں تو یونہی ہوتی چلی آئی ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,138,'26:138','اور ہم عذاب میں مُبتلا ہونے والے نہیں ہیں"');
INSERT INTO "ur_maududi" VALUES(26,139,'26:139','آخر کار انہوں نے اُسے جھُٹلا دیا اور ہم نے ان کو ہلاک کر دیا۔ یقیناً اس میں ایک نشانی ہے، مگر ان میں سے اکثر لوگ ماننے والے نہیں ہیں');
