INSERT INTO "ur_maududi" VALUES(26,146,'26:146','کیا تم اُن سب چیزوں کے درمیان ، جو یہاں ہیں ، بس یوں ہی اطمینان سے رہنے دیے جاوٴ گے؟ ');
