INSERT INTO "ur_maududi" VALUES(26,148,'26:148','اِن کھیتوں اور نخلستانوں میں جن کے خوشے رس بھرے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(26,149,'26:149','تم پہاڑ کھود کھود کر فخریہ اُن میں عمارتیں بناتے ہو۔ ');
