INSERT INTO "ur_maududi" VALUES(26,153,'26:153','اُنہوں نے جواب دیا”تُو محض ایک سحر زدہ آدمی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(26,154,'26:154','تُو ہم جیسے ایک انسان کے سوا اور کیا ہے۔ لا کوئی نشانی اگر تُو سچا ہے۔ “');
INSERT INTO "ur_maududi" VALUES(26,155,'26:155','صالح نے کہا”یہ اُونٹنی ہے۔ ایک دن اس کے پینے کا ہے اور ایک دن تم سب کے پانی لینے کا۔ ');
INSERT INTO "ur_maududi" VALUES(26,156,'26:156','اس کو ہرگز نہ چھیڑنا ورنہ ایک بڑے دن کا عذاب تم کو آ لے گا"');
