INSERT INTO "ur_maududi" VALUES(26,165,'26:165','کیا تم دنیا کی مخلوق میں سے مردوں کے پاس جاتے ہو ');
INSERT INTO "ur_maududi" VALUES(26,166,'26:166','اور تمہاری بیویوں میں تمہارے ربّ نے تمہارے لیے جو کچھ پیدا کیا ہے اسے چھوڑ دیتے ہو؟ بلکہ تم لوگ تو حد سے ہی گزر گئے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(26,167,'26:167','انہوں نے کہا”اے لُوطؑ ، اگر تُو اِن باتوں سے باز نہ آیا تو جو لوگ ہماری بستیوں سے نکالے گئے ہیں اُن میں تُو بھی شامل ہو کر رہے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(26,168,'26:168','اس نے کہا "تمہارے کرتوتوں پر جو لوگ کُڑھ رہے ہیں میں اُن میں شامل ہوں');
INSERT INTO "ur_maududi" VALUES(26,169,'26:169','اے پروردگار، مجھے اور میرے اہل و عیال کو ان کی بد کرداریوں سے نجات دے۔“ ');
INSERT INTO "ur_maududi" VALUES(26,170,'26:170','آخرکار ہم نے اسے اور اس کے سب اہل و عیال کو بچا لیا');
INSERT INTO "ur_maududi" VALUES(26,171,'26:171','بجز ایک بُڑھیا کے جو پیچھے رہ جانے والوں میں تھی۔ ');
INSERT INTO "ur_maududi" VALUES(26,172,'26:172','پھر باقی ماندہ لوگوں کو ہم نے تباہ کر دیا');
INSERT INTO "ur_maududi" VALUES(26,173,'26:173','اور ان پر برسائی ایک برسات، بڑی ہی بُری بارش تھی جو اُن ڈرائے جانے والوں پر نازل ہوئی۔ ');
INSERT INTO "ur_maududi" VALUES(26,174,'26:174','یقیناً اس میں ایک نشانی ہے، مگر اِن میں سے اکثر ماننے والے نہیں');
