INSERT INTO "ur_maududi" VALUES(26,187,'26:187','اگر تو سچا ہے تو ہم پر آسمان کا کوئی ٹکڑا گرا دے"');
INSERT INTO "ur_maududi" VALUES(26,188,'26:188','شعیبؑ نے کہا”میرا ربّ جانتا ہے جو کچھ تم کر رہے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(26,189,'26:189','انہوں نے اُسے جھُٹلا دیا، آخر کارچھتری والے دن کا عذاب ان پر آگیا ، اور وہ بڑے ہی خوفناک دن کا عذاب تھ');
INSERT INTO "ur_maududi" VALUES(26,190,'26:190','یقیناً اس میں ایک نشانی ہے، مگر ان میں سے اکثر لوگ ماننے والے نہیں');
INSERT INTO "ur_maududi" VALUES(26,191,'26:191','اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی');
INSERT INTO "ur_maududi" VALUES(26,192,'26:192',' یہ ربّ العالمین کی نازل کردہ چیز ہے۔ ');
INSERT INTO "ur_maududi" VALUES(26,193,'26:193','اسے لے کر تیرے دل پر امانت دار رُوح اُتری ہے');
