INSERT INTO "ur_maududi" VALUES(26,198,'26:198','(لیکن اِن کی ہٹ دھرمی کا حال یہ ہے کہ) اگر ہم اسے کسی عجمی پر بھی نازل کر دیتے');
INSERT INTO "ur_maududi" VALUES(26,199,'26:199','اور یہ (فصیح عربی کلام)وہ ان کو پڑھ کر سُناتا تب بھی یہ مان کر نہ دیتے۔ ');
INSERT INTO "ur_maududi" VALUES(26,200,'26:200','اِسی طرح ہم نے اس (ذکر)کو مجرموں کے دلوں میں گزارا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(26,201,'26:201','وہ اس پر ایمان نہیں لاتے جب تک کہ عذاب الِیم نہ دیکھ لیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,202,'26:202','پھر جب وہ بے خبری میں ان پر آ پڑتا ہے');
INSERT INTO "ur_maududi" VALUES(26,203,'26:203','اُس وقت وہ کہتے ہیں کہ”کیا اب ہمیں کچھ مہلت مِل سکتی ہے؟“ ');
INSERT INTO "ur_maududi" VALUES(26,204,'26:204','تو کیا یہ لوگ ہمارے عذاب کے لیے جلدی مچا رہے ہیں؟');
INSERT INTO "ur_maududi" VALUES(26,205,'26:205','تم نے کچھ غور کیا، اگر ہم انہیں برسوں تک عیش کرنے کی مُہلت بھی دے دیں');
INSERT INTO "ur_maududi" VALUES(26,206,'26:206','اور پھر وہی چیز ان پر آ جائے جس سے انہیں ڈرایا جا رہا ہے');
INSERT INTO "ur_maududi" VALUES(26,207,'26:207','تو وہ سامانِ زیست جو ان کو ملا ہوا ہے اِن کے کس کام آئے گا؟ ');
INSERT INTO "ur_maududi" VALUES(26,208,'26:208','(دیکھو) ہم نے کبھی کسی بستی کو اِس کے بغیر ہلاک نہیں کیا کہ اُس کے لیے خبردار کرنے والے حق نصیحت ادا کرنے کو موجود تھے');
INSERT INTO "ur_maududi" VALUES(26,209,'26:209','اور ہم ظالم نہ تھے۔ ');
INSERT INTO "ur_maududi" VALUES(26,210,'26:210','اِس (کتابِ مبین)کو شیاطین لے کر نہیں اُترے ہیں، ');
INSERT INTO "ur_maududi" VALUES(26,211,'26:211','نہ یہ کام ان کو سجتا ہے ، اور نہ وہ ایسا کر ہی سکتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,212,'26:212','وہ تو اس کی سماعت تک سے دُور رکھے گئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,213,'26:213','پس اے محمدؐ ، اللہ کے ساتھ کسی دُوسرے معبُود کو نہ پکارو، ورنہ تم بھی سزا پانے والوں میں شامل ہو جاوٴ گے۔ ');
INSERT INTO "ur_maududi" VALUES(26,214,'26:214','اپنے قریب ترین رشتہ داروں کو ڈراوٴ، ');
INSERT INTO "ur_maududi" VALUES(26,215,'26:215','اور ایمان لانے والوں میں سے جو لوگ تمہاری پیروی اختیار کریں ان کے ساتھ تواضع سے پیش آؤ');
INSERT INTO "ur_maududi" VALUES(26,216,'26:216','لیکن اگر وہ تمہاری نافرمانی کریں تو ان سے کہہ دو کہ جو کچھ تم کرتے ہو اس سے میں بری الذّمّہ ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(26,217,'26:217','اور اُس زبر دست اور رحیم پر توکّل کرو ');
INSERT INTO "ur_maududi" VALUES(26,218,'26:218','جو تمہیں اُس وقت دیکھ رہا ہوتا ہے جب تم اُٹھتے ہو ');
INSERT INTO "ur_maududi" VALUES(26,219,'26:219','اور سجدہ گزار لوگوں میں تمہاری نقل و حرکت پر نگا ہ رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(26,220,'26:220','وہ سب کچھ سننے اور جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(26,221,'26:221','لوگو، کیا میں تمہیں بتاؤں کہ شیاطین کس پر اُترا کرتے ہیں؟');
INSERT INTO "ur_maududi" VALUES(26,222,'26:222','وہ ہر جعل ساز بدکار پر اُترا کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,223,'26:223','سُنی سُنائی باتیں کانوں میں پھُونکتے ہیں اور ان میں سے اکثر جھُوٹے ہوتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,224,'26:224','رہے شعراء، تو ان کے پیچھے بہکے ہوئے لوگ چلا کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(26,225,'26:225','کیا تم دیکھتے نہیں ہو کہ وہ ہر وادی میں بھٹکتے ہیں ');
INSERT INTO "ur_maududi" VALUES(26,226,'26:226','اور ایسی باتیں کہتے ہیں جو کرتے نہیں ہیں ');
INSERT INTO "ur_maududi" VALUES(26,227,'26:227','بجز اُن لوگوں کے جو ایمان لائے اور جنہوں نے نیک عمل کیے اور اللہ کو کثرت سے یاد کیا، اور جب ان پر ظلم کیا گیا تو صرف بدلہ لے لیا ۔۔۔۔ اور ظلم کرنے والوں کو عنقریب معلوم ہو جائے گا کہ وہ کس انجام سے دوچار ہوتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(27,1,'27:1','ط۔س۔ یہ آیات ہیں قرآن اور کتابِ مبین کی ');
INSERT INTO "ur_maududi" VALUES(27,2,'27:2','ہدایت اور بشارت اُن ایمان لانے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(27,3,'27:3','جو نماز قائم کرتے اور زکوٰة دیتے ہیں ، اور پھر وہ ایسے لوگ ہیں جو آخرت پر پُورا یقین رکھتے ہیں ');
INSERT INTO "ur_maududi" VALUES(27,4,'27:4','حقیقت یہ ہے کہ جو لوگ آخرت کو نہیں مانتے ان کے لیے ہم نے اُن کے کرتُوتوں کو خوشنما بنا دیا ہے، اس لیے وہ بھٹکتے پھرتے ہیں ');
INSERT INTO "ur_maududi" VALUES(27,5,'27:5','یہ وہ لوگ ہیں جن کے لیے بُری سزا ہے اور آخرت میں یہی سب سے زیادہ خسارے میں رہنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(27,6,'27:6','اور (اے محمدؐ)بلا شبہہ تم یہ قرآن ایک حکیم و علیم ہستی کی طرف سے پا رہے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(27,7,'27:7','(اِنہیں اُس وقت کا قصہ سُناوٴ)جب موسیٰؑ نے اپنے گھر والوں سے کہا کہ ”مجھے ایک آگ سی نظر آئی ہے، میں ابھی یا تو وہاں سے کوئی خبر لے کر آتا ہوں یا کوئی انگارا چُن لاتا ہوں تاکہ تم لوگ گرم ہو سکو ۔“');
INSERT INTO "ur_maududi" VALUES(27,8,'27:8','وہاں جو پہنچا تو ندا آئی کہ”مبارک ہے وہ جو اس آگ میں ہے اور جو اِس کے ماحول میں ہے۔ پاک ہے اللہ، سب جہان والوں کا پروردگار ');
INSERT INTO "ur_maududi" VALUES(27,9,'27:9','اے موسیٰؑ، یہ میں ہوں اللہ، زبردست اور دانا');
INSERT INTO "ur_maududi" VALUES(27,10,'27:10','اور پھینک تُو ذرا اپنی لاٹھی۔“ جُونہی کہ موسیٰ ؑ نے دیکھا لاٹھی سانپ کی طرح بل کھا رہی ہے تو پیٹھ پھیر کر بھاگا اور پیچھے مُڑ کر بھی نہ دیکھا۔”اے موسیٰؑ ، ڈرو نہیں۔ میرے حضُور رسُول ڈرا نہیں کرتے ');
INSERT INTO "ur_maududi" VALUES(27,11,'27:11','اِلّا یہ کہ کسی نے قصور کیا ہو ۔ پھر اگر بُرائی کے بعد اُس نے بھلائی سے (اپنے فعل کو)بدل لیا تو میں معاف کرنے والا مہربان ہوں ');
INSERT INTO "ur_maududi" VALUES(27,12,'27:12','اور ذرا اپنا ہاتھ اپنے گریبان میں تو ڈالو۔ چمکتا ہوا نکلے گا بغیر کسی تکلیف کے۔ یہ (دونشانیاں)نو نشانیوں میں سے ہیں فرعون اور اُس کی قوم کی طرف (لے جانے کے لیے) ، وہ بڑے بدکردار لوگ ہیں۔“');
INSERT INTO "ur_maududi" VALUES(27,13,'27:13','مگر جب ہماری کھلی کھلی نشانیاں اُن لوگوں کے سامنے آئیں تو انہوں نے کہا یہ تو کھلا جادو ہے');
INSERT INTO "ur_maududi" VALUES(27,14,'27:14','انہوں نے سراسر ظلم اور غرور کی راہ سے ان نشانیوں کا انکار کیا حالانکہ دل ان کے قائل ہو چکے تھے۔ اب دیکھ لو کہ ان مفسدوں کا انجام کیسا ہوا');
INSERT INTO "ur_maududi" VALUES(27,15,'27:15','(دُوسری طرف)ہم نے داوٴدؑ و سلیمانؑ کو علم عطا کیا اور اُنہوں نے کہا کہ شکر ہے اُس خدا کا جس نے ہم کو اپنے بہت سے مومن بندوں پر فضیلت عطا کی۔ ');
INSERT INTO "ur_maududi" VALUES(27,16,'27:16','اور داوٴدؑ کا وارث سلیمانؑ ہوا ۔ اور اس نے کہا”لوگو، ہمیں پرندوں کی بولیاں سکھائی گئی ہیں اور ہمیں ہر طرح کی چیزیں دی گئی ہیں ، بے شک یہ (اللہ کا)نمایاں فضل ہے۔“');
INSERT INTO "ur_maududi" VALUES(27,17,'27:17','سلیمانؑ کے لیے جن اور انسانوں اور پرندوں کے لشکر جمع کیے گئے تھے اور وہ پُورے ضبط میں رکھے جاتے تھے');
INSERT INTO "ur_maududi" VALUES(27,18,'27:18','(ایک مرتبہ وہ ان کے ساتھ کُوچ کر رہا تھا)یہاں تک کہ جب یہ سب چیونٹیوں کی وادی میں پہنچے تو ایک چیونٹی نے کہا”اے چیونٹیو، اپنے بلوں میں گھُس جاوٴ، کہیں ایسا نہ ہو کہ سلیمانؑ اور اس کے لشکر تمہیں کُچل ڈالیں اور انہیں خبر بھی نہ ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(27,19,'27:19','سلیمانؑ اس کی بات پر مُسکراتے ہوئے ہنس پڑا اور بولا۔۔۔۔”اے میرے ربّ، مجھے قابو میں رکھ کہ میں تیرے احسان کا شکر ادا کرتا رہوں جو تُو نے مجھ پر اور میرے والدین پر کیا ہے اور ایسا عملِ صالح کروں جو تجھے پسند آئے اور اپنی رحمت سے مجھ کو اپنے صالح بندوں میں داخل کر۔“ ');
INSERT INTO "ur_maududi" VALUES(27,20,'27:20','(ایک اور موقع پر)سلیمانؑ نے پرندوں کا جائزہ لیا اور کہا”کیا بات ہے کہ میں فلاں ہُد ہُد کو نہیں دیکھ رہا ہوں۔ کیا وہ کہیں غائب ہو گیا ہے؟');
INSERT INTO "ur_maududi" VALUES(27,21,'27:21','میں اسے سخت سزا دوں گا، یا ذبح کر دوں گا، ورنہ اسے میرے سامنے معقول وجہ پیش کرنی ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(27,22,'27:22','کچھ زیادہ دیر نہ گزری تھی کہ اُس نے آکر کہا”میں نے وہ معلومات حاصل کی ہیں جو آپ کے عِلم میں نہیں ہیں۔ میں سَبا کے متعلق یقینی اطلاع لے کر آیا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(27,23,'27:23','میں نے وہاں ایک عورت دیکھی جو اس قوم کی حکمران ہے اُس کو ہر طرح کا ساز و سامان بخشا گیا ہے اور اس کا تخت بڑا عظیم الشان ہے');
INSERT INTO "ur_maududi" VALUES(27,24,'27:24','میں نے دیکھا کہ وہ اور اس کی قوم اللہ کے بجائے سُورج کے آگے سجدہ کرتی ہے “۔۔۔۔ شیطان نے ان کے اعمال ان کے لیے خوشنما بنا دیے اور انہیں شاہراہ سے روک دیا، اس وجہ سے وہ یہ سیدھا راستہ نہیں پاتے');
INSERT INTO "ur_maududi" VALUES(27,25,'27:25','کہ اُس خدا کو سجدہ کریں جو آسمانوں اور زمین کی پوشیدہ چیزیں نکالتا ہے اور سب کچھ جانتا ہے جسے تم لوگ چھُپاتے ہو اور ظاہر کرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(27,26,'27:26','اللہ کہ جس کے سوا کوئی مستحق ِ عبادت نہیں، جو عرشِ عظیم کا مالک ہے۔ ');
INSERT INTO "ur_maududi" VALUES(27,27,'27:27','سلیمانؑ نے کہا "ابھی ہم دیکھے لیتے ہیں کہ تو نے سچ کہا ہے یا تو جھوٹ بولنے والوں میں سے ہے');
INSERT INTO "ur_maududi" VALUES(27,28,'27:28','میرا یہ خط لےجا او ر اسے ان لوگوں کی طرف ڈال دے، پھر الگ ہٹ کر دیکھ کہ وہ کیا ردِ عمل ظاہر کرتے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(27,29,'27:29','ملکہ بولی "اے اہلِ دربار، میری طرف ایک بڑا اہم خط پھینکا گیا ہے');
INSERT INTO "ur_maududi" VALUES(27,30,'27:30','وہ سلیمانؑ کی جانب سے ہے اور اللہ رحمٰن و رحیم کے نام سے شروع کیا گیا ہے');
INSERT INTO "ur_maududi" VALUES(27,31,'27:31','مضمون یہ ہے کہ”میرے مقابلے میں سرکشی نہ کرو اور مسلم ہو کر میرے پاس حاضر ہو جاوٴ۔“ ');
INSERT INTO "ur_maududi" VALUES(27,32,'27:32','(خط سُنا کر)ملکہ نے کہا”اے سردارانِ قوم، میرے اس معاملے میں مجھے مشورہ دو، میں کسی معاملہ کا فیصلہ تمہارے بغیر نہیں کرتی ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(27,33,'27:33','اُنہوں نے جواب دیا "ہم طاقت ور اور لڑنے والے لوگ ہیں آگے فیصلہ آپ کے ہاتھ میں ہے آپ خود دیکھ لیں کہ آپ کو کیا حکم دینا ہے"');
INSERT INTO "ur_maududi" VALUES(27,34,'27:34','ملکہ نے کہا کہ”بادشاہ جب کسی ملک میں گھُس آتے ہیں تو اسے خراب اور اس کے عزت والوں کو ذلیل کردیتے ہیں ۔ یہی کچھ وہ کیا کرتے ہیں ');
INSERT INTO "ur_maududi" VALUES(27,35,'27:35','میں اِن لوگوں کی طرف ایک ہدیہ بھیجتی ہوں، پھر دیکھتی ہوں کہ میرے ایلچی کیا جواب لے کر پلٹتے ہیں"');
INSERT INTO "ur_maududi" VALUES(27,36,'27:36','جب وہ (ملکہ کا سفیر)سلیمانؑ کے ہاں پہنچا تو اس نے کہا”کیا تم لوگ مال سے میری مدد کرنا چاہتے ہو؟ جو کچھ خدا نے مجھے دے رکھا ہے وہ اس سے بہت زیادہ ہے جو تمہیں دیا ہے۔ تمہارا ہدیہ تمہی کو مبارک رہے');
INSERT INTO "ur_maududi" VALUES(27,37,'27:37','(اے سفیر)واپس جا اپنے بھیجنے والوں کی طرف۔ ہم ان پر ایسے لشکر لے کر آئیں گے جن کا مقابلہ وہ نہ کر سکیں گے اور ہم انہیں ایسی ذلّت کے ساتھ وہاں سے نکالیں گے کہ وہ خوار ہو کر رہ جائیں گے۔“');
INSERT INTO "ur_maududi" VALUES(27,38,'27:38',' سلیمانؑ نے کہا”اے اہلِ دربار، تم میں سےکون اس کا تخت میرے پاس لاتا ہے قبل اس کے کہ وہ لوگ مطیع ہو کر میرے پاس حاضر ہوں؟ ');
INSERT INTO "ur_maududi" VALUES(27,39,'27:39','جِنوں میں سے ایک قوی ہیکل نے عرض کیا میں اسے حاضر کر دوں گا قبل اس کے کہ آپ اپنی جگہ سے اُٹھیں۔ میں اس کی طاقت رکھتا ہوں اور امانتدار ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(27,40,'27:40','جس شخص کے پاس کتاب کا ایک علم تھا وہ بولا”میں آپ کی پلک جھپکنے سے پہلے اسے لائے دیتا ہوں۔ “ جُونہی کہ سلیمان ؑ نے وہ تخت اپنے پاس رکھا ہوا دیکھا، وہ پکار اُٹھا”یہ میرے ربّ کا فضل ہے تاکہ وہ مجھے آزمائے کہ میں شکر کرتا ہوں یا کافرِ نعمت بن جاتا ہوں ۔ اور جو کوئی شکر کرتا ہے اس کا شکر اس کے اپنے ہی لیے مفید ہے، ورنہ کوئی نا شکری کرے تو میرا ربّ بے نیاز اور اپنی ذات میں آپ بزرگ ہے ۔“');
INSERT INTO "ur_maududi" VALUES(27,41,'27:41',' سلیمانؑ نے کہا”انجان طریقے سے اس کا تخت اس کے سامنے رکھ دو، دیکھیں وہ صحیح بات تک پہنچتی ہے یا اُن لوگوں میں سے ہے جو راہِ راست نہیں پاتے۔“ ');
INSERT INTO "ur_maududi" VALUES(27,42,'27:42','ملکہ جب حاضر ہوئی تو اس سے کہا گیا کیا تیرا تخت ایسا ہی ہے؟ وہ کہنے لگی”یہ تو گویا وہی ہے ۔ ہم تو پہلے ہی جان گئے تھے اور ہم نے سرِ اطاعت جُھکا دیا تھا۔ (یا ہم مسلم ہو چکے تھے)۔“ ');
INSERT INTO "ur_maududi" VALUES(27,43,'27:43','اُس کو (ایمان لانے سے)جس چیز نے روک رکھا تھا وہ اُن معبُودوں کی عبادت تھی جنہیں وہ اللہ کے سوا پُوجتی تھی، کیونکہ وہ ایک کافر قوم سے تھی۔ ');
INSERT INTO "ur_maududi" VALUES(27,44,'27:44','اس سے کہا گیا کہ محل میں داخل ہو۔ اس نے جو دیکھا تو سمجھی کہ پانی کا حوض ہے اور اُترنے کےلیے اس نےاپنے پائینچے اُٹھا لیے۔ سلیمانؑ نے کہا یہ شیشے کا چکنا فرش ہے۔ اس پروہ پکار اُٹھی”اے میرے ربّ (آج تک)میں اپنے نفس پر بڑا ظلم کرتی رہی، اور اب میں نے سلیمانؑ کے ساتھ اللہ ربّ العالمین کی اطاعت قبول کر لی۔“ ');
INSERT INTO "ur_maududi" VALUES(27,45,'27:45',' اور ثمُود کی طرف ہم نے اُن کے بھائی صالحؑ کو (یہ پیغام دے کر)بھیجا کہ اللہ کی بندگی کرو، تو یکایک وہ دو مُتَخاصِم فریق بن گئے۔ ');
INSERT INTO "ur_maududi" VALUES(27,46,'27:46','صالحؑ نے کہا”اے میری قوم کے لوگو، بھلائی سے پہلے بُرائی کے لیے کیوں جلدی مچاتے ہو؟ کیوں نہیں اللہ سے مغفرت طلب کرتے ؟ شایک کہ تم پر رحم فرمایا جائے؟“');
INSERT INTO "ur_maududi" VALUES(27,47,'27:47','انہوں نےکہا”ہم نے تو تم کو اور تمہارے ساتھیوں کو بدشگونی کا نشان پایا ہے۔“ صالحؑ نے جواب دیا”تمہارے نیک و بدشگون کا سرِشتہ تو اللہ کے پاس ہے۔ اصل بات یہ ہے کہ تم لوگوں کی آزمائش ہو رہی ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(27,48,'27:48','اُس شہر میں نو جتھے دار تھے جو ملک میں فساد پھیلاتے اور کوئی اصلاح کا کام نہ کرتے تھے');
INSERT INTO "ur_maududi" VALUES(27,49,'27:49','انہوں نے آپس میں کہا”خدا کی قسم کھا کر عہد کر لو کہ ہم صالحؑ اور اس کے گھر والوں پر شبخون ماریں گے اور پھر اس کے ولی سے کہہ دیں گے کہ ہم اس کے خاندان کی ہلاکت کے موقع پر موجود نہ تھے، ہم بالکل سچ کہتے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(27,50,'27:50','یہ چال تو وہ چلے اور پھر ایک چال ہم نے چلی جس کی انہیں خبر نہ تھی۔ ');
INSERT INTO "ur_maududi" VALUES(27,51,'27:51','اب دیکھ لو کہ ان کی چال کا انجام کیا ہوا ہم نے تباہ کر کے رکھ دیا اُن کو اور ان کی پوری قوم کو');
INSERT INTO "ur_maududi" VALUES(27,52,'27:52','وہ اُن کے گھر خالی پڑے ہیں اُس ظلم کی پاداش میں جو وہ کرتے تھے، اس میں ایک نشانِ عِبرت ہے اُن لوگوں کے لیے جو علم رکھتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(27,53,'27:53','اور بچا لیا ہم نے اُن لوگوں کو جو ایمان لائے تھے اور نافرمانی سے پرہیز کرتے تھے');
INSERT INTO "ur_maududi" VALUES(27,54,'27:54',' اور لوطؑ کو ہم نے بھیجا۔ یاد کرو وہ وقت جب اس نے اپنی قوم سے کہا”کیا تم آنکھوں دیکھتے بدکاری کرتے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(27,55,'27:55','کیا تمہارا یہی چلن ہے کہ عورتوں کو چھوڑ کر مردوں کے پاس شہوت رانی کے لیے جاتے ہو؟ حقیقت یہ ہے کہ تم لوگ سخت جہالت کا کام کرتے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(27,56,'27:56','مگر اُس کی قوم کا جواب اس کے سوا کچھ نہ تھا کہ انہوں نے کہا "نکال دو لوطؑ کے گھر والوں کو اپنی بستی سے، یہ بڑے پاکباز بنتے ہیں"');
INSERT INTO "ur_maududi" VALUES(27,57,'27:57','آخر کار ہم نے بچا لیا اُس کو اور اُس کے گھر والوں کو ، بجز اُس کی بیوی کے جس کا پیچھے رہ جانا ہم نے طے کر دیا تھا، ');
INSERT INTO "ur_maududi" VALUES(27,58,'27:58','اور برسائی اُن لوگوں پر ایک برسات، بہت ہی بری برسات تھی وہ اُن لوگوں کے حق میں جو متنبہ کیے جا چکے تھے');
INSERT INTO "ur_maududi" VALUES(27,59,'27:59',' (اے نبیؐ)کہو، حمد ہے اللہ کے لیے اور سلام اُس کے اُن بندوں پر جنہیں اس نے برگزیدہ کیا۔(اِن سے پُوچھو)اللہ بہتر ہے یا وہ معبُود جنہیں یہ لوگ اس کا شریک بنا رہے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(27,60,'27:60','بھَلا وہ کون ہے جس نےآسمانوں اور زمین کو پیدا کیا اور تمہارے لیے آسمان سے پانی برسایا پھر اس کے ذریعہ سے وہ خوشنما باغ اُگائے جن کے درختوں کا اُگانا تمہارے بس میں نہ تھا؟ کیا اللہ کے ساتھ کوئی دُوسرا خدا بھی (اِن کاموں میں شریک)ہے؟ (نہیں)، بلکہ یہی لوگ راہِ راست سے ہٹ کر چلے جا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(27,61,'27:61','اور وہ کون ہے جس نے زمین کو جائے قرار بنایا اور اس کے اندر دریا رواں کیے اور اس میں (پہاڑوں کی)میخیں گاڑ دیں اور پانی کے دو ذخیروں کے درمیان پردے حائل کر دیے؟ کیا اللہ کے ساتھ کوئی اور خدا بھی (ان کاموں میں شریک)ہے؟ نہیں، بلکہ ان میں سے اکثر لوگ نادان ہیں');
INSERT INTO "ur_maududi" VALUES(27,62,'27:62','کون ہے جو بے قرار کی دُعا سُنتا ہے جبکہ وہ اُسے پکارے اور کون اس کی تکلیف رفع کرتا ہے؟ اور (کون ہے جو)تمہیں زمین کا خلیفہ بناتا ہے؟ کیا اللہ کے ساتھ کوئی اور خدا بھی (یہ کام کرنے والا)ہے؟ تم لوگ کم ہی سوچتے ہو');
INSERT INTO "ur_maududi" VALUES(27,63,'27:63','اور وہ کون ہے جو خشکی اور سمندر کی تاریکیوں میں تم کو راستہ دکھاتا ہے اور کون اپنی رحمت کے آگے ہواوٴں کو خوشخبری لے کر بھیجتا ہے؟ کیا اللہ کے ساتھ کوئی دُوسرا خدا بھی (یہ کام کرتا)ہے؟ بہت بالا و برتر ہے اللہ اس شرک سے جو یہ لوگ کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(27,64,'27:64','اور وہ کون ہے جو خلق کی ابتدا کرتا ہے اور پھر اس کا اعادہ کرتا ہے؟ اور کون تم کو آسمان اور زمین سے رزق دیتا ہے؟ کیا اللہ کے ساتھ کوئی اور خدا بھی (ان کاموں میں حصہ دار)ہے؟ کہو کہ لاوٴ اپنی دلیل اگر تم سچے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(27,65,'27:65','اِن سے کہو، اللہ کے سوا آسمانوں اور زمین میں کوئی غیب کا عِلم نہیں رکھتا۔ اور وہ نہیں جانتے کہ کب وہ اُٹھائے جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(27,66,'27:66','بلکہ آخرت کا تو علم ہی اِن لوگوں سے گُم ہو گیا ہے، بلکہ یہ اس کی طرف سے شک میں ہیں، بلکہ یہ اُس سے اندھے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(27,67,'27:67','یہ منکرین کہتے ہیں "کیا جب ہم اور ہمارے باپ دادا مٹی ہو چکے ہوں گے تو ہمیں واقعی قبروں سے نکالا جائے گا؟');
INSERT INTO "ur_maududi" VALUES(27,68,'27:68','یہ خبریں ہم کو بھی بہت دی گئی ہیں اور پہلے ہمارے آباء اجداد کو بھی دی جاتی رہی ہیں، مگر یہ بس افسانے ہی افسانے ہیں جو اگلے وقتوں سے سُنتے چلے آ رہے ہیں"');
INSERT INTO "ur_maududi" VALUES(27,69,'27:69','کہو ذرا زمین میں چل پھر کر دیکھو کہ مجرموں کا کیا انجام ہو چکا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(27,70,'27:70','اے نبیؐ ، اِن کےحال پر رنج نہ کرو، اور نہ اِن کی چالوں پر دِل تنگ ہو ');
INSERT INTO "ur_maududi" VALUES(27,71,'27:71','وہ کہتے ہیں کہ ”یہ دھمکی کب پُوری ہوگی اگر تم سچے ہو؟“ ');
INSERT INTO "ur_maududi" VALUES(27,72,'27:72','کہو کیا عجب کہ جس عذاب کے لیے تم جلدی مچا رہے ہو اس کا ایک حصہ تمہارے قریب ہی آلگا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(27,73,'27:73','حقیقت یہ ہے کہ تیرا ربّ تو لوگوں پر بڑا فضل فرمانے والا ہے مگر اکثر لوگ شکر نہیں کرتے۔ ');
INSERT INTO "ur_maududi" VALUES(27,74,'27:74','بلا شُبہ تیرا ربّ خُوب جانتا ہے جو کچھ ان کے سینے اپنے اندر چھُپائے ہوئے ہیں اور جو کچھ وہ ظاہر کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(27,75,'27:75','آسمان و زمین کی کوئی پوشیدہ چیز ایسی نہیں ہے جو ایک واضح کتاب میں لکھی ہوئی موجود نہ ہو۔ ');
INSERT INTO "ur_maududi" VALUES(27,76,'27:76','یہ واقعہ ہے کہ یہ قرآن بنی اسرائیل کو اکثراُن باتوں کی حقیقت بتاتا ہے جن میں وہ اختلاف رکھتے ہیں ');
INSERT INTO "ur_maududi" VALUES(27,77,'27:77','اور یہ ہدایت اور رحمت ہے ایمان لانے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(27,78,'27:78','یقیناً (اِسی طرح)تیرا ربّ اِن لوگوں کے درمیان بھی اپنے حکم سے فیصلہ کر دے گا اور وہ زبردست اور سب کچھ جاننے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(27,79,'27:79','پس اے نبیؐ، اللہ پر بھروسا رکھو، یقیناً تم صریح حق پر ہو');
INSERT INTO "ur_maududi" VALUES(27,80,'27:80','تم مُردوں کو نہیں سُنا سکتے ، نہ اُن بہروں تک اپنی پکار پہنچا سکتے ہو جو پیٹھ پھیر کر بھاگے جا رہے ہوں ');
INSERT INTO "ur_maududi" VALUES(27,81,'27:81','اور نہ اندھوں کو راستہ بتا کر بھٹکنے سے بچا سکتے ہو۔ تم تو اپنی بات اُنہی لوگوں کو سُنا سکتے ہو جو ہماری آیات پر ایمان لاتے ہیں اور پھر فرماں بردار بن جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(27,82,'27:82','اور جب ہماری بات پُوری ہونے کا وقت اُن پر آپہنچے گا تو ہم ان کے لیے ایک جانور زمین سے نکالیں گے جو ان سے کلام کرے گا کہ لوگ ہماری آیات پر یقین نہیں کر تے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(27,83,'27:83','اور ذرا تصور کرو اُس دن کا جب ہم ہر امّت میں سے ایک فوج کی فوج اُن لوگوں کی گھیر لائیں گے جو ہماری آیات کو جھٹلایا کرتے تھے، پھر ان کو (ان کی اقسام کے لحاظ سے درجہ بدرجہ) مرتب کیا جائے گا');
INSERT INTO "ur_maududi" VALUES(27,84,'27:84','یہاں تک کہ جب سب آجائیں گے تو (ان کا ربّ ان سے)پوچھے گاکہ”تم نے میری آیات کو جھُٹلا دیا حالانکہ تم نے ان کا عِلمی احاطہ نہ کیا تھا؟ اگر یہ نہیں تو اور تم کیا کر رہے تھے؟ “');
INSERT INTO "ur_maududi" VALUES(27,85,'27:85','اور ان کے ظلم کی وجہ سے عذاب کا وعدہ ان پر پورا ہو جائے گا، تب وہ کچھ بھی نہ بول سکیں گے');
INSERT INTO "ur_maududi" VALUES(27,86,'27:86','کیا ان کو سُجھائی نہ دیتا تھا کہ ہم نے رات ان کے لیے سکون حاصل کرنے کو بنائی تھی اور دن کو روشن کیا تھا؟ اسی میں بہت نشانیاں تھیں ان لوگوں کے لیے جو ایمان لاتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(27,87,'27:87','اور کیا گُزرے گی اُس روز جب کہ صُور پھُونکا جائے گا اور ہَول کھا جائیں گے وہ سب جو آسمانوں اور زمین میں ہیں ۔۔۔۔ سوائے اُن لوگوں کے جنہیں اللہ اس ہَول سے بچانا چاہے گا۔۔۔۔ اور سب کان دبائے اس کے حضور حاضر ہو جائیں گے');
INSERT INTO "ur_maududi" VALUES(27,88,'27:88','آج تُو پہاڑوں کو دیکھتا ہے اور سمجھتا ہے کہ خوب جمے ہوئے ہیں، مگر اُس وقت یہ بادلوں کی طرح اُڑ رہے ہوں گے، یہ اللہ کی قدرتت کا کرشمہ ہوگا جس نے ہر چیز کو حکمت کے ساتھ استوار کیا ہے۔ وہ خوب جانتا ہے کہ تم لوگ کیا کرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(27,89,'27:89','جو شخص بھلائی لے کر آئے گا اسے اُس سے زیادہ بہتر صلہ ملے گا اور ایسے لوگ اُس دن کے ہَول سے محفوظ ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(27,90,'27:90','اور جو بُرائی لیے ہوئے آئے گا ، ایسے سب لوگ اوندھے منہ آگ میں پھینکے جائیں گے۔ کیا تم لوگ اس کے سوا کوئی اور جزا پا سکتے ہو کہ جیسا کرو ویسا بھرو؟ ');
INSERT INTO "ur_maududi" VALUES(27,91,'27:91','(اے محمدؐ ، اِن سے کہو)”مجھے تو یہی حکم دیا گیا ہے کہ اِس شہر کے ربّ کی بندگی کروں جس نے اِسے حرم بنایا ہے اور جو ہر چیز کا مالک ہے۔ مجھے حکم دیا گیا ہے کہ میں مسلم بن کر رہوں');
INSERT INTO "ur_maududi" VALUES(27,92,'27:92','اور یہ قرآن پڑھ کر سناؤں" اب جو ہدایت اختیار کرے گا وہ اپنے ہی بھلے کے لیے ہدایت اختیار کرے گا اور جو گمراہ ہو اُس سے کہہ دو کہ میں تو بس خبردار کر دینے والا ہوں');
INSERT INTO "ur_maududi" VALUES(27,93,'27:93','اِن سے کہو، تعریف اللہ ہی کے لیے ہے، عنقریب وہ تمہیں اپنی نشانیاں دکھا دے گا اور تم انہیں پہچان لو گے، اور تیرا رب بے خبر نہیں ہے اُن اعمال سے جو تم لوگ کرتے ہو');
INSERT INTO "ur_maududi" VALUES(28,1,'28:1','ط س م');
INSERT INTO "ur_maududi" VALUES(28,2,'28:2','یہ کتاب مبین کی آیات ہیں');
INSERT INTO "ur_maududi" VALUES(28,3,'28:3','ہم موسیٰؑ اور فرعون کا کچھ حال ٹھیک ٹھیک تمہیں سُناتے ہیں، ایسے لوگوں کے فائدے کے لیے جو ایمان لائیں۔ ');
INSERT INTO "ur_maududi" VALUES(28,4,'28:4','واقعہ یہ کہ فرعون نے زمین میں سرکشی کی اور اس کے باشندوں کو گروہوں میں تقسیم کر دیا۔ ان میں سے ایک گروہ کو وہ ذلیل کرتا تھا، اس کے لڑکوں کو قتل کر تا اور اس کی لڑکیوں کو جیتا رہنے دیتا تھا۔ فی الواقع وہ مفسد لوگوں میں سے تھا');
INSERT INTO "ur_maududi" VALUES(28,5,'28:5','اور ہم یہ ارادہ رکھتے تھے کہ مہربانی کریں ان لوگوں پر جو زمین میں ذلیل کر کے رکھے گئے تھے اور انہیں پیشوا بنا دیں اور انہی کو وارث بنائیں ');
INSERT INTO "ur_maududi" VALUES(28,6,'28:6','اور زمین میں ان کو اقتدار بخشیں اور ان سے فرعون و ہامان اور ان کے لشکروں کو وہی کچھ دِکھلا دیں جس کا انہیں ڈر تھا');
INSERT INTO "ur_maududi" VALUES(28,7,'28:7',' ہم نے موسیٰؑ کی ماں کو اشارہ کیا کہ ”اِس کو دُودھ پلا، پھر جب تجھے اُس کی جان کا خطرہ ہو تو اسے دریا میں ڈال دے اور کچھ خوف اور غم نہ کر، ہم اسے تیرے ہی پاس واپس لے آئیں گے اور اس کو پیغمبروں میں شامل کریں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(28,8,'28:8','آخر کار فرعون کے گھر والوں نے اسے (دریا سے)نکال لیا تاکہ وہ ان کا دشمن اور ان کے لیے سببِ رنج بنے، واقعی فرعون اور ہامان اور ان کے لشکر (اپنی تدبیرمیں)بڑے غلط کار تھے');
INSERT INTO "ur_maududi" VALUES(28,9,'28:9','فرعون کی بیوی نے (اس سے)کہا”یہ میرے اور تیرے لیے آنکھوں کی ٹھنڈک ہے، اسے قتل نہ کرو، کیا عجب کہ یہ ہمارے لیے مفید ثابت ہو، یا ہم اسے بیٹا ہی بنا لیں۔“ اور وہ (انجام سے)بے خبر تھے');
INSERT INTO "ur_maududi" VALUES(28,10,'28:10','اُدھر موسیٰؑ کی ماں کا دل اڑا جا رہا تھا وہ اس راز کو فاش کر بیٹھتی اگر ہم اس کی ڈھارس نہ بندھا دیتے تاکہ وہ (ہمارے وعدے پر) ایمان لانے والوں میں سے ہو');
INSERT INTO "ur_maududi" VALUES(28,11,'28:11','اُس نے بچے کی بہن سے کہا اس کے پیچھے پیچھے جا۔ چنانچہ وہ الگ سے اس کو اس طرح دیکھتی رہی کہ (دشمنوں کو)اس کا پتہ نہ چلا۔ ');
INSERT INTO "ur_maududi" VALUES(28,12,'28:12','اور ہم نے بچے پر پہلے ہی دُودھ پِلانے والیوں کی چھاتیاں حرام کر رکھی تھیں۔ (یہ حالت دیکھ کر)اُس لڑکی نے اُن سے کہا”میں تمہیں ایسے گھر کا پتہ بتاوٴں جس کے لوگ اس کی پرورش کا ذمّہ لیں اور خیر خواہی کے ساتھ اسے رکھیں؟ ');
INSERT INTO "ur_maududi" VALUES(28,13,'28:13','اس طرح ہم موسیٰؑ کو اس کی ماں کے پاس پلٹا لائے تاکہ اس کی آنکھیں ٹھنڈی ہوں اور وہ غمگین نہ ہو اور جان لے کہ اللہ کا وعدہ سچا تھا، مگر اکثر لوگ اس بات کو نہیں جانتے۔ “');
INSERT INTO "ur_maududi" VALUES(28,14,'28:14','جب موسیٰؑ اپنی پوری جوانی کو پہنچ گیا اور اُس کا نشوونما مکمل ہوگیا تو ہم نے اُسے حکم اور علم عطا کیا، ہم نے نیک لوگوں کو ایسی ہی جزا دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(28,15,'28:15','(ایک روز) وہ شہر میں ایسے وقت داخل ہوا جبکہ اہلِ شہر غفلت میں تھے۔ وہاں اُس نے دیکھا کہ دو آدمی لڑ رہے ہیں۔ ایک اُس کی اپنی قوم کا تھا اور دُوسرا اُس کی دشمن قوم سے تعلق رکھتا تھا۔ اُس کی قوم کے آدمی نے دشمن قوم والے کے خلاف اُسے مدد کے لیے پکارا۔ موسیٰؑ نے اُس کو ایک گھونسا مارا اور اُس کا کام تمام کر دیا۔ (یہ حرکت سرزَد ہوتے ہی)موسیٰؑ نے کہا”یہ شیطان کی کار فرمائی ہے، وہ سخت دشمن اور کھُلا گمراہ کُن ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(28,16,'28:16','پھر وہ کہنے لگا”اے میرے ربّ، میں نے اپنے نفس پر ظلم کر ڈالا، میری مغفرت فرما دے۔“ چنانچہ اللہ نے اُس کی مغفرت فرما دی، وہ غفورٌ رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(28,17,'28:17','موسیٰؑ نے عہد کیا کہ ”اے میرے ربّ، یہ احسان جو تُو نے مجھ پر کیا ہے اِس کے بعد اب میں کبھی مجرموں کا مددگار نہ بنوں گا۔“ ');
INSERT INTO "ur_maududi" VALUES(28,18,'28:18','دوسرے روز وہ صبح سویرے ڈرتا اور ہر طرف سے خطرہ بھانپتا ہوا شہر میں جا رہا تھا کہ یکایک کیا دیکھتا ہے کہ وہی شخص جس نے کل اُسے مدد کے لیے پکارا تھا آج پھر اُسے پکاررہا ہے۔ موسیٰؑ نے کہا”تُو تو بڑا ہی بہکا ہوا آدمی ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(28,19,'28:19','پھر جب موسیٰؑ نے ارادہ کیا کہ دشمن قوم کے آدمی پر حملہ کرے تو وہ پکار اُٹھا ”اے موسیٰؑ ، کیا آج تُو مجھے اُسی طرح قتل کرنے لگا ہے جس طرح کل ایک شخص کو قتل کر چکا ہے، تُو اس ملک میں جبّار بن کر رہنا چاہتا ہے، اصلاح کرنا نہیں چاہتا۔“');
INSERT INTO "ur_maududi" VALUES(28,20,'28:20','اس کے بعد ایک آدمی شہر کے پَرلے سِرے سے دوڑتا ہوا آیا اور بولا ”موسیٰؑ ، سرداروں میں تیرے قتل کے مشورے ہو رہے ہیں، یہاں سے نکل جا ، میں تیرا خیر خواہ ہوں۔“');
INSERT INTO "ur_maududi" VALUES(28,21,'28:21','یہ خبر سنتے ہی موسیٰؑ ڈرتا اور سہمتا نکل کھڑا ہوا اور اس نے دعا کی کہ "اے میرے رب، مجھے ظالموں سے بچا"');
INSERT INTO "ur_maududi" VALUES(28,22,'28:22','(مصر سے نکل کر)جب موسیٰؑ نے مَدیَن کا رُخ کیا تو اُس نے کہا”اُمید ہے کہ میرا ربّ مجھے ٹھیک راستے پر ڈال دے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(28,23,'28:23','اور جب وہ مَدیَن کے کنوئیں پر پہنچا تو اُس نے دیکھا کہ بہت سے لوگ اپنے جانوروں کو پانی پِلا رہے ہیں اور اُن سے الگ ایک طرف دو عورتیں اپنے جانوروں کو روک رہی ہیں۔موسیٰؑ نے ان عورتوں سے پوچھا”تمہیں کیا پریشانی ہے؟“ اُنہوں نے کہا”ہم اپنے جانوروں کو پانی نہیں پِلا سکتیں جب تک یہ چرواہے اپنے جانور نہ نکال لے جائیں، اور ہمارے والد ایک بہت بُوڑھے آدمی ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(28,24,'28:24','یہ سن کو موسیٰؑ نے ان کے جانوروں کو پانی پلا دیا، پھر ایک سائے کی جگہ جا بیٹھا اور بولا "پروردگار، جو خیر بھی تو مجھ پر نازل کر دے میں اس کا محتاج ہوں"');
INSERT INTO "ur_maududi" VALUES(28,25,'28:25','(کچھ دیر نہ گزری تھی کہ)اُن دونوں عورتوں میں سے ایک شرم و حیا کے ساتھ چلتی ہوئی اُس کے پاس آئی اور کہنے لگی”میرے والد آپ کو بُلا رہے ہیں تاکہ آپ نے ہمارے لیے جانوروں کو پانی جو پلایا ہے اس کا اجر آپ کو دیں۔“ موسیٰؑ جب اُس کے پاس پہنچا اور اپنا سارا قصہ اُسے سُنایا تو اُس نے کہا ”کچھ خوف نہ کرو، اب تم ظالم لوگوں سے بچ نِکلے ہو۔“');
INSERT INTO "ur_maududi" VALUES(28,26,'28:26','ان دونوں عورتوں میں سے ایک نے اپنے باپ سے کہا”ابّا جان، اِس شخص کو نوکر رکھ لیجیے، بہترین آدمی جسے آپ ملازم رکھیں وہی ہو سکتا ہے جو مضبوط اور امانتدار ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(28,27,'28:27','اس کے باپ نے (موسیٰؑ سے)کہا” میں چاہتا ہوں کہ اپنی اِن دو بیٹیوں میں سے ایک کا نکاح تمہارے ساتھ کر دوں بشرطیکہ تم آٹھ سال تک میرے ہاں ملازمت کرو اور اگر دس سال تک پُورے کر دو تو یہ تمہاری مرضی ہے۔ میں تم پر سختی نہیں کرنا چاہتا۔ تم انشاء اللہ مجھے نیک آدمی پاوٴ گے۔“');
INSERT INTO "ur_maududi" VALUES(28,28,'28:28','موسیٰؑ نے جواب دیا”یہ بات میرے اور آپ کے درمیان طے ہوگئی۔ ان دونوں مدّتوں میں سے جو بھی میں پُوری کر دوں اُس کے بعد پھر کوئی زیادتی مجھ پر نہ ہو، اور جو کچھ قول قرار ہم کر رہے ہیں اللہ اُس پر نگہبان ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(28,29,'28:29','جب موسیٰؑ نے مدّت پوری کر دی اور اپنے اہل و عیال کو لے کر چلا تو طُور کی جانب اُس کو ایک آگ نظر آئی۔ اُس نے اپنے گھر والوں سے کہا”ٹھہرو، میں نے ایک آگ دیکھی ہے ، شاید میں وہاں سے کوئی خبر لے آوٴں یا اُس آگ سے کوئی انگارا ہی اُٹھا لاوٴں جس سے تم تاپ سکو۔“');
INSERT INTO "ur_maududi" VALUES(28,30,'28:30','وہاں پہنچا تو وادی کے داہنے کنارے پر مبارک خطّے میں ایک درخت سے پُکارا گیا کہ”اے موسیٰؑ ، میں ہی اللہ ہوں، سارے جہاں والوں کا مالک۔“');
INSERT INTO "ur_maududi" VALUES(28,31,'28:31','اور (حکم دیا گیا کہ) پھینک دے اپنی لاٹھی جونہی کہ موسیٰؑ نے دیکھا کہ وہ لاٹھی سانپ کی طرح بل کھا رہی ہے تو وہ پیٹھ پھیر کر بھاگا اور اس نے مڑ کر بھی نہ دیکھا (ارشاد ہوا) "موسیٰؑ، پلٹ آ اور خوف نہ کر، تو بالکل محفوظ ہے');
INSERT INTO "ur_maududi" VALUES(28,32,'28:32','اپنا ہاتھ گریبان میں ڈال، چمکتا ہوا نکلے گا بغیر کسی تکلیف کے۔ اور خوف سے بچنے کے لیے اپنا بازُو بھینچ لے۔ یہ دوروشن نشانیاں ہیں تیرے ربّ کی طرف سے فرعون اور اُس کے درباریوں کے سامنے پیش کر نے کے لیے ، وہ بڑے نافرمان لوگ ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(28,33,'28:33','موسیٰؑ نے عرض کیا”میرے آقا، میں تو اُن کا ایک آدمی قتل کر چکا ہوں، ڈرتا ہوں کہ وہ مجھے مار ڈالیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(28,34,'28:34','اور میرا بھائی ہارونؑ مجھے سے زیادہ زبان آور ہے، اسے میرے ساتھ مدد گار کے طور پر بھیج تاکہ وہ میری تائید کرے، مجھے اندیشہ ہے کہ وہ لوگ مجھے جھٹلائیں گے"');
INSERT INTO "ur_maududi" VALUES(28,35,'28:35','فرمایا”ہم تیرے بھائی کے ذریعہ سے تیرا ہاتھ مضبوط کریں گے اور تم دونوں کو ایسی سطوت بخشیں گے کہ وہ تمہارا کچھ نہ بگاڑ سکیں گے۔ ہماری نشانیوں کے زور سے غلبہ تمہارا اور تمہارے پیرووں کا ہی ہوگا۔“ ');
INSERT INTO "ur_maududi" VALUES(28,36,'28:36','پھر جب موسیٰؑ اُن لوگوں کے پاس ہماری کھُلی کھُلی نشانیاں لے کر پہنچا تو اُنہوں نے کہا کہ یہ کچھ نہیں ہے مگر بناوٹی جادُو۔ اور یہ باتیں تو ہم نے اپنے باپ دادا کے زمانے میں کبھی سُنی ہی نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(28,37,'28:37','موسیٰؑ نے جواب دیا”میرا ربّ اُس شخص کے حال سے خوب واقف ہے جو اُس کی طرف سے ہدایت لے کر آیا ہے اور وہی بہتر جانتا ہے کہ آخری انجام کِس کا اچھا ہونا ہے، حق یہ ہے کہ ظالم کبھی فلاح نہیں پاتے۔“ ');
INSERT INTO "ur_maududi" VALUES(28,38,'28:38','اور فرعون نے کہا”اے اہلِ دربار، میں تو اپنے سوا تمہارے کسی خدا کو نہیں جانتا۔ ہامان، ذرا اینٹیں پکوا کر میرے لیے ایک اونچی عمارت تو بنوا ، شاید کہ اُس پر چڑھ کر میں موسیٰؑ کے خدا کو دیکھ سکوں، میں تو اسے جھُوٹاسمجھتا ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(28,39,'28:39','اُس نے اور اُس کے لشکروں نے زمین میں بغیر کسی حق کے اپنی بڑائی کا گھمنڈ کیا اور سمجھے کہ اُنہیں کبھی ہماری طرف پلٹنا نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(28,40,'28:40','آخر کار ہم نے اُسے اور اُس کے لشکروں کو پکڑا اور سمندر میں پھینک دیا۔ اب دیکھ لو کہ اُن ظالموں کا کیسا انجام ہوا');
INSERT INTO "ur_maududi" VALUES(28,41,'28:41','ہم نے اُنہیں جہنّم کی طرف دعوت دینے والے پیش رو بنا دیا اور قیامت کے روز وہ کہیں سے کوئی مدد نہ پا سکیں گے');
INSERT INTO "ur_maududi" VALUES(28,42,'28:42','ہم نے اِس دنیا میں ان کے پیچھے لعنت لگا دی اور قیامت کے روز وہ بڑی قباحت میں مبتلا ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(28,43,'28:43','پچھلی نسلوں کو ہلاک کرنے کے بعد ہم نے موسیٰؑ کو کتاب عطا کی، لوگوں کے لیے بصیرتوں کا سامان بنا کر ، ہدایت اور رحمت بنا کر، تاکہ شاید لوگ سبق حاصل کریں۔ ');
INSERT INTO "ur_maududi" VALUES(28,44,'28:44','(اے محمدؐ)تم اُس وقت مغربی گوشے میں موجود نہ تھے جب ہم نے موسیٰؑ کو یہ فرمانِ شریعت عطا کیا، اور نہ تم شاہدین میں شامل تھے، ');
INSERT INTO "ur_maududi" VALUES(28,45,'28:45','بلکہ اُس کے بعد (تمہارے زمانے تک)ہم بہت سی نسلیں اُٹھا چکے ہیں اور اُن پر بہت زمانہ گُزر چکا ہے۔ تم اہلِ مَدیِن کے درمیان بھی موجود نہ تھے کہ اُن کو ہماری آیات سُنا رہے ہوتے، مگر (اُس وقت کی یہ خبریں) بھیجنے والے ہم ہیں');
INSERT INTO "ur_maududi" VALUES(28,46,'28:46','اور تم طُور کے دامن میں بھی اُس وقت موجود نہ تھے جب ہم نے (موسیٰؑ کو پہلی مرتبہ)پکارا تھا، مگر یہ تمہارے ربّ کی رحمت ہے(کہ تم کو یہ معلومات دی جارہی ہیں) تاکہ تم اُن لوگوں کو متنبّہ کرو جن کے پاس ان سے پہلے کوئی متنبّہ کرنے والا نہیں آیا، شاید کہ وہ ہوش میں آئیں');
INSERT INTO "ur_maududi" VALUES(28,47,'28:47','(اور یہ ہم نے اس لیے کیا کہ)کہیں ایسا نہ ہو کہ اُن کے اپنے کیے کرتُوتوں کی بدولت کوئی مصیبت جب اُن پر آئے تو وہ کہیں”اے پروردگار، تُو نے کیوں نہ ہماری طرف کوئی رسُول بھیجا کہ ہم تیری آیات کی پیروی کرتے اور اہلِ ایمان میں سے ہوتے۔“ ');
INSERT INTO "ur_maududi" VALUES(28,48,'28:48','مگر جب ہمارے ہاں سے حق اُن کے پاس آگیا تو وہ کہنے لگے”کیوں نہ دیا گیا اس کو وہی کچھ جو موسیٰؑ کو دیا گیا تھا؟“ کیا یہ لوگ اس کا انکار نہیں کر چکے ہیں جو اس سے پہلے موسیٰؑ کو دیا گیا تھا؟ اُنہوں نے کہا”دونوں جادُو ہیں جو ایک دوسرے کی مدد کرتے ہیں۔“ اور کہا”ہم کسی کو نہیں مانتے۔“');
INSERT INTO "ur_maududi" VALUES(28,49,'28:49','(اے نبیؐ)اِن سے کہو”اچھا، تو لاوٴ اللہ کی طرف سے کوئی کتاب جو اِن دونوں سے زیادہ ہدایت بخشنے والی ہو اگر تم سچے ہو، میں اسی کی پیروی اختیار کروں گا۔“ ');
INSERT INTO "ur_maududi" VALUES(28,50,'28:50','اب اگر وہ تمہارا یہ مطالبہ پورا نہیں کرتے تو سمجھ لو کہ دراصل یہ اپنی خواہشات کے پیرو ہیں، اور اُس شخص سے بڑھ کر کون گمراہ ہو گا جو خدائی ہدایت کے بغیر بس اپنی خواہشات کی پیروی کرے؟ اللہ ایسے ظالموں کو ہرگز ہدایت نہیں بخشتا');
INSERT INTO "ur_maududi" VALUES(28,51,'28:51','اور (نصیحت کی)بات پے در پے ہم انہیں پہنچا چکے ہیں تا کہ وہ غفلت سے بیدار ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(28,52,'28:52','جن لوگوں کو اِس سے پہلے ہم نے کتاب دی تھی وہ اِس (قرآن)پر ایمان لاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(28,53,'28:53','اور جب یہ اُن کو سُنایا جاتا ہے تو وہ کہتے ہیں کہ ”ہم اِس پر ایمان لائے، یہ واقعی حق ہے ہمارے ربّ کی طرف سے، ہم تو پہلے ہی سے مسلم ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(28,54,'28:54','یہ وہ لوگ ہیں جنہیں ان کا اجر دو بار دیا جائے گا اُس ثابت قدمی کے بدلے جو انہوں نے دکھائی۔ وہ بُرائی کو بھلائی سے دفع کرتے ہیں اور جو کچھ رزق ہم نے انہیں دیا ہے اُس میں سے خرچ کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(28,55,'28:55','اور جب انہوں نے بیہودہ بات سُنی تو یہ کہہ کر اُس سے کنارہ کش ہوگئے کہ ”ہمارے اعمال ہمارے لیے اور تمہارے اعمال تمہارے لیے، تم کو سلام ہے، ہم جاہلوں کا سا طریقہ اختیار کرنا نہیں چاہتے۔“');
INSERT INTO "ur_maududi" VALUES(28,56,'28:56','اے نبیؐ ، تم جسے چاہو اُسے ہدایت نہیں دے سکتے، مگر اللہ جسے چاہتا ہے ہدایت دیتا ہے اور وہ اُن لوگوں کو خُوب جانتا ہے جو ہدایت قبول کرنے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(28,57,'28:57','وہ کہتے ہیں ”اگر ہم تمہارے ساتھ اِس ہدایت کی پیروی اختیار کر لیں تو اپنی زمین سے اُچک لیے جائیں گے۔“ کیا یہ واقعہ نہیں ہے کہ ہم نے ایک پُر امن حرم کو ان کے لیے جائے قیام بنا دیا جس کی طرف ہر طرح کے ثمرات کھچے چلے آتے ہیں، ہماری طرف سے رزق کے طور پر؟ مگر ان میں سے اکثر لوگ جانتے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(28,58,'28:58','اور کتنی ہی ایسی بستیاں ہم تباہ کر چکے ہیں جن کے لوگ اپنی معیشت پر اِترا گئے تھے۔ سو دیکھ لو، وہ ان کے مسکن پڑے ہوئے ہیں جن میں ان کے بعد کم ہی کوئی بسا ہے، آخر کار ہم ہی وارث ہو کر رہے۔ ');
INSERT INTO "ur_maududi" VALUES(28,59,'28:59','اور تیرا ربّ بستیوں کو ہلاک کرنے والا نہ تھا جب تک کہ ان کے مرکز میں ایک رسُول نہ بھیج دیتا جو ان کو ہماری آیات سُناتا۔ اور ہم بستیوں کو ہلاک کرنے والے نہ تھے جب تک کہ ان کے رہنے والے ظالم نہ ہو جاتے۔ ');
INSERT INTO "ur_maududi" VALUES(28,60,'28:60','تم لوگوں کو جو کچھ بھی دیا گیا ہے وہ محض دُنیا کی زندگی کا سامان اور اس کی زینت ہے، اور جو کچھ اللہ کے پاس ہے وہ اس سے بہتر اور باقی تر ہے کیا تم لوگ عقل سے کام نہیں لیتے؟');
INSERT INTO "ur_maududi" VALUES(28,61,'28:61','بھلا وہ شخص جس سے ہم نے اچھا وعدہ کیا ہو اور وہ اسے پانے والا ہو کبھی اس شخص کی طرح ہو سکتا ہے جسے ہم نے صرفِ حیات دنیا کا سروسامان دے دیا ہو اور پھر وہ قیامت کے روز سزا کے لیے پیش کیا جانے والا ہو؟ ');
INSERT INTO "ur_maududi" VALUES(28,62,'28:62','اور (بھُول نہ جائیں یہ لوگ)اُس دن کو جب کہ وہ اِن کو پکارے گا اور پُوچھے گا”کہاں ہیں میرے وہ شریک جن کا تم گمان رکھتے تھے؟“ ');
INSERT INTO "ur_maududi" VALUES(28,63,'28:63','یہ قول جن پر چسپاں ہوگا وہ کہیں گے”اے ہمارے ربّ ، بے شک یہی لوگ ہیں جن کو ہم نے گمراہ کیا تھا۔ اِنہیں ہم نے اُسی طرح گمراہ کیا جیسے ہم خود گمراہ ہوئے۔ ہم آپ کے سامنےبراءت کا اظہار کرتے ہیں۔ یہ ہماری تو بندگی نہیں کرتے تھے۔“ ');
INSERT INTO "ur_maududi" VALUES(28,64,'28:64','پھر اِن سے کہا جائے گا کہ پکارو اب اپنے ٹھہرائے ہوئے شریکوں کو۔ یہ انہیں پکاریں گے مگر وہ اِن کو کوئی جواب نہ دیں گے۔ اور یہ لوگ عذاب دیکھ لیں گے۔ کاش یہ ہدایت اختیار کرنے والے ہوتے');
INSERT INTO "ur_maududi" VALUES(28,65,'28:65','اور (فراموش نہ کریں یہ لوگ) وہ دن جبکہ وہ اِن کو پکارے گا اور پوچھے گا کہ "جو رسول بھیجے گئے تھے انہیں تم نے کیا جواب دیا تھا؟"');
INSERT INTO "ur_maududi" VALUES(28,66,'28:66','اُس وقت کوئی جواب اِن کو نہ سُوجھے گا اور نہ یہ آپس میں ایک دُوسرے سے پوچھ ہی سکیں گے');
INSERT INTO "ur_maududi" VALUES(28,67,'28:67','البتہ جس نے آج توبہ کر لی اور ایمان لے آیا اور نیک عمل کیے وہی یہ توقع کر سکتا ہے کہ وہاں فلاح پانے والوں میں سے ہو گا');
INSERT INTO "ur_maududi" VALUES(28,68,'28:68','تیرا ربّ پیدا کرتا ہے جو کچھ چاہتا ہے اور (وہ خود ہی اپنے کام کے لیے جسے چاہتا ہے)منتخب کر لیتا ہے، یہ انتخاب اِن لوگوں کے کرنے کا کام نہیں ہے، اللہ پاک ہے اور بہت بالاتر ہے اُس شرک سے جو یہ لوگ کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(28,69,'28:69','تیرا ربّ جانتا ہے جو کچھ یہ دلوں میں چھپائے ہوئے ہیں اور جو کچھ یہ ظاہر کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(28,70,'28:70','وہی ایک اللہ ہے جس کے سوا کوئی عبادت کا مستحق نہیں اسی کے لیے حمد ہے دنیا میں بھی اور آخرت میں بھی، فرماں روائی اسی کی ہے اور اسی کی طرف تم سب پلٹائے جانے والے ہو');
INSERT INTO "ur_maududi" VALUES(28,71,'28:71','اے نبیؐ، اِن سے کہو کبھی تم لوگوں نے غور کیا کہ اگر اللہ قیامت تک تم پر ہمیشہ کے لیے رات طاری کر دے تو اللہ کے سوا وہ کونسا معبود ہے جو تمہیں روشنی لا دے؟ کیا تم سُنتے نہیں ہو؟');
INSERT INTO "ur_maududi" VALUES(28,72,'28:72','اِن سے پوچھو، کبھی تم نے سوچا کہ اگر اللہ قیامت تک تم پر ہمیشہ کے لیے دن طاری کر دے تو اللہ کے سوا وہ کونسا معبُود ہے جو تمہیں رات لا دے تاکہ تم اس میں سکون حاصل کر سکو؟ کیا تم کو سُوجھتا نہیں؟');
INSERT INTO "ur_maududi" VALUES(28,73,'28:73','یہ اسی کی رحمت ہے کہ اس نے تمہارے لیے رات اور دن بنائے تاکہ تم (رات میں) سکون حاصل کرو اور (دن کو) اپنے رب کا فضل تلاش کرو، شاید کہ تم شکر گزار بنو');
INSERT INTO "ur_maududi" VALUES(28,74,'28:74','(یاد رکھیں یہ لوگ) وہ دن جبکہ وہ انہیں پکارے گا پھر پوچھے گا "کہاں ہیں میرے وہ شریک جن کا تم گمان رکھتے تھے؟"');
INSERT INTO "ur_maududi" VALUES(28,75,'28:75','اور ہم ہر اُمّت میں سے ایک گواہ نکال لائیں گے پھر کہیں گے کہ”لاوٴ اب اپنی دلیل۔“ اس وقت انہیں معلوم ہو جائے گا کہ حق اللہ کی طرف ہے، اور گم ہو جائیں گے ان کے وہ سارے جھُوٹ جو انہوں نے گھڑ رکھے تھے');
INSERT INTO "ur_maududi" VALUES(28,76,'28:76','یہ ایک واقعہ ہے کہ قارون موسیٰؑ کی قوم کا ایک شخص تھا، پھر وہ اپنی قوم کے خلاف سرکش ہوگیا۔ اور ہم نے اس کو اتنے خزانے دے رکھے تھے کہ ان کی کنجیاں طاقت ور آدمیوں کی ایک جماعت مشکل سے اُٹھا سکتی تھی۔ ایک دفعہ جب اس کی قوم کے لوگوں نے اس سے کہا ”پھُول نہ جا، اللہ پھُولنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(28,77,'28:77','جو مال اللہ نے تجھے دیا ہے اس سے آخرت کا گھر بنانے کی فکر کر اور دُنیا میں سے بھی اپنا حصہ فراموش نہ کر احسان کر جس طرح اللہ نے تیرے ساتھ احسان کیا ہے اور زمین میں فساد برپا کرنے کی کوشش نہ کر، اللہ مفسدوں کو پسند نہیں کرتا"');
INSERT INTO "ur_maududi" VALUES(28,78,'28:78','تو اُس نے کہا”یہ سب کچھ تو مجھے اُس علم کی بنا پر دیا گیا ہے جو مجھ کو حاصل ہے“ ۔۔۔۔۔۔۔۔۔ کیا اس کو یہ علم نہ تھا کہ اللہ اس سے پہلے بہت سے ایسے لوگوں کو ہلاک کر چکا ہے جو اس سے زیادہ قوت اور جمعیت رکھتے تھے؟ مجرموں سے تو ان کے گناہ نہیں پُوچھے جاتے۔ ');
INSERT INTO "ur_maududi" VALUES(28,79,'28:79','ایک روز وہ اپنی قوم کے سامنے اپنے پُورے ٹھاٹھ میں نکلا جو لوگ حیات دنیا کے طالب تھے وہ اسے دیکھ کر کہنے لگے "کاش ہمیں بھی وہی کچھ ملتا جو قارون کو دیا گیا ہے، یہ تو بڑا نصیبے والا ہے"');
INSERT INTO "ur_maududi" VALUES(28,80,'28:80','مگر جو لوگ علم رکھنے والے تھے وہ کہنے لگے”افسوس تمہارے حال پر ، اللہ کا ثواب بہتر ہے اُس شخص کے لیے جو ایمان لائے اور نیک عمل کرے، اور یہ دولت نہیں ملتی مگر صبر کرنے والوں کو۔“ ');
INSERT INTO "ur_maududi" VALUES(28,81,'28:81','آخر کار ہم نے اسے اور اس کے گھر کو زمین میں دَھنسا دیا پھر کوئی اس کے حامیوں کا گروہ نہ تھا جو اللہ کے مقابلہ میں اس کی مدد کو آتا اور نہ وہ خود اپنی مدد آپ کر سکا');
INSERT INTO "ur_maududi" VALUES(28,82,'28:82','اب وہی لوگ جو کل اس کی منزلت کی تمنا کر رہے تھے کہنے لگے”افسوس، ہم بھُول گئے تھے کہ اللہ اپنے بندوں میں سے جس کا رزق چاہتا ہے کشادہ کرتا ہے اور جسے چاہتا ہے نَپا تُلا دیتا ہے۔ اگر اللہ نے ہم پر احسان نہ کیا ہوتا تو ہمیں بھی زمین میں دَھنسا دیتا۔ افسوس ہم کو یاد نہ رہا کہ کافر فلاح نہیں پایا کرتے۔“ ');
INSERT INTO "ur_maududi" VALUES(28,83,'28:83','وہ آخرت کا گھر تو ہم اُن لوگوں کے لیے مخصُوص کر دیں گے جو زمین میں اپنی بڑائی نہیں چاہتے اور نہ فساد کرنا چاہتے ہیں۔ اور انجام کی بھلائی متقین ہی کے لیے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(28,84,'28:84','جو کوئی بھَلائی لے کر آئے گا اس کے لیے اس سے بہتر بھَلائی ہے، اور جو بُرائی لے کر آئے تو بُرائیاں کرنے والوں کو ویسا ہی بدلہ ملے گا جیسے عمل وہ کرتے تھے');
INSERT INTO "ur_maududi" VALUES(28,85,'28:85','اے نبیؐ ، یقین جانو کہ جس نے یہ قرآن تم پر فرض کیا ہے وہ تمہیں ایک بہترین انجام کو پہنچانے والا ہے۔ اِن لوگوں سے کہہ دو کہ ”میرا ربّ خُوب جانتا ہے کہ ہدایت لے کر کون آیا ہے اور کھُلی گمراہی میں کون مُبتلا ہے۔“');
INSERT INTO "ur_maududi" VALUES(28,86,'28:86','“ تم اس بات کے ہرگز اُمیدوار نہ تھے کہ تم پر کتاب نازل کی جائے گی، یہ تو محض تمہارے ربّ کی مہربانی سے (تم پر نازل ہوئی ہے)، پس تم کافروں کے مددگار نہ بنو۔ ');
INSERT INTO "ur_maududi" VALUES(28,87,'28:87','اور ایسا کبھی نہ ہونے پائے کہ اللہ کی آیات جب تم پر نازل ہوں تو کُفّار تمہیں اُن سے باز رکھیں۔ اپنے ربّ کی طرف دعوت دو اور ہرگز مشرکوں میں شامل نہ ہو');
INSERT INTO "ur_maududi" VALUES(28,88,'28:88','اور اللہ کے سوا کسی دُوسرے معبُود کو نہ پکارو۔ اُس کے سوا کوئی معبُود نہیں ہے۔ ہر چیز ہلاک ہونے والی ہے سوائے اُس کی ذات کے۔ فرماں روائی اسی کی ہے اور اسی کی طرف تم سب پلٹائے جانے والے ہو');
INSERT INTO "ur_maududi" VALUES(29,1,'29:1','الف۔ل۔م۔');
INSERT INTO "ur_maududi" VALUES(29,2,'29:2','کیا لوگوں نے یہ سمجھ رکھا ہے کہ وہ بس اِتنا کہنے پر چھوڑ دیے جائیں گے کہ ”ہم ایمان لائے“ اور ان کو آزمایا نہ جائے گا؟ ');
INSERT INTO "ur_maududi" VALUES(29,3,'29:3','حالانکہ ہم اُن سب لوگوں کی آزمائش کر چکے ہیں جو اِن سے پہلے گزرے ہیں۔ اللہ کو تو ضرور یہ دیکھنا ہے کہ سچے کون ہیں اور جھُوٹے کون');
INSERT INTO "ur_maududi" VALUES(29,4,'29:4','اور کیا وہ لوگ جو بُری حرکتیں کر رہے ہیں یہ سمجھے بیٹھے ہیں کہ وہ ہم سے بازی لے جائیں گے؟ بڑا غلط حکم ہے جو وہ لگا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(29,5,'29:5','جو کوئی اللہ سے ملنے کی توقع رکھتا ہو (اُسے معلوم ہونا چاہیے کہ)اللہ کا مقرر کیا ہوا وقت آنے ہی والا ہے، اور اللہ سب کچھ سُنتا اور جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(29,6,'29:6','جو شخص بھی مجاہدہ کرے گا اپنے ہی بھلے کے لیے کرے گا، اللہ یقیناً دنیا جہان والوں سے بے نیاز ہے۔ ');
INSERT INTO "ur_maududi" VALUES(29,7,'29:7','اور جو لوگ ایمان لائیں گے اور نیک اعمال کریں گے اُن کی برائیاں ہم ان سے دُور کر دیں گے اور انہیں اُن کے بہترین اعمال کی جزا دیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(29,8,'29:8','ہم نے انسان کو ہدایت کی ہے کہ اپنے والدین کے ساتھ نیک سلوک کرے۔ لیکن اگر وہ تجھ پر زور ڈالیں کہ تُو میرے ساتھ کسی ایسے (معبُود)کو شریک ٹھہرا ئے جسے تُو (میرے شریک کی حیثیت سے)نہیں جانتا تو ان کی اطاعت نہ کر۔ میری ہی طرف تم سب کو پلٹ کر آنا ہے، پھر میں تم کو بتا دوں گا کہ تم کیا کرتے رہے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(29,9,'29:9','اور جو لوگ ایمان لائے ہوں گے اور جنہوں نے نیک اعمال کیے ہوں گے اُن کو ہم ضرور صالحین میں داخل کریں گے');
INSERT INTO "ur_maududi" VALUES(29,10,'29:10','لوگوں میں سے کوئی ایسا ہے جو کہتا ہے کہ ہم ایمان لائے اللہ پر۔ مگر جب وہ اللہ کے معاملہ میں ستایا گیا تو اس نے لوگوں کی ڈالی ہوئی آزمائش کو اللہ کے عذاب کی طرح سمجھ لیا۔ اب اگر تیرے ربّ کی طرف سے فتح و نصرت آگئی تو یہی شخص کہے گا کہ”ہم تو تمہارے ساتھ تھے“ کیا دنیا والوں کےدلوں کا حال اللہ کو بخوبی معلوم نہیں ہے؟');
INSERT INTO "ur_maududi" VALUES(29,11,'29:11','اور اللہ کو تو ضرور یہ دیکھنا ہی ہے کہ ایمان لانے والے کون ہیں اور منافق کون۔ ');
INSERT INTO "ur_maududi" VALUES(29,12,'29:12','یہ کافر لوگ ایمان لانے والوں سے کہتے ہیں کہ تم ہمارے طریقے کی پیروی کرو اور تمہاری خطاوٴں کو ہم اپنے اُوپر لے لیں گے۔ حالانکہ اُن کی خطاوٴں میں سے کچھ بھی وہ اپنے اُوپر لینے والے نہیں ہیں، وہ قطعاً جھُوٹ کہتے ہیں');
INSERT INTO "ur_maududi" VALUES(29,13,'29:13','ہاں ضرور وہ اپنے بوجھ بھی اُٹھائیں گے اور اپنے بوجھوں کے ساتھ دوسرے بہت سے بوجھ بھی۔ اور قیامت کے روز یقیناً ان سے اِن افترا پردازیوں کی باز پرس ہوگی جو وہ کرتے رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(29,14,'29:14','ہم نے نُوحؑ کو اس کی قوم کی طرف بھیجا اور وہ پچاس کم ایک ہزار برس اُن کے درمیان رہا۔ آخر کار اُن لوگوں کو طوفان نے آگھیرا اس حال میں کہ وہ ظالم تھے۔ ');
INSERT INTO "ur_maududi" VALUES(29,15,'29:15','پھر نُوحؑ کو اور کشتی والوں کو ہم نے بچا لیا اور اُسے دنیا والوں کے لیے ایک نشانِ عبرت بنا کر رکھ دیا۔ ');
INSERT INTO "ur_maududi" VALUES(29,16,'29:16','اور ابراہیمؑ کو بھیجا جبکہ اُس نے اپنی قوم سے کہا:”اللہ کی بندگی کرو اور اُس سے ڈرو۔ یہ تمہارے لیے بہتر ہے اگر تم جانو');
INSERT INTO "ur_maududi" VALUES(29,17,'29:17','تم اللہ کو چھوڑ کر جنہیں پُوج رہے ہو وہ تو محض بُت ہیں اور تم ایک جھُوٹ گھڑ رہے ہو۔ درحقیقت اللہ کے سوا جن کی تم پرستش کرتے ہو وہ تمہیں کوئی رزق بھی دینے کا اختیار نہیں رکھتے۔ اللہ سے رزق مانگو اور اسی کی بندگی کرو اور اس کا شکر ادا کرو، اسی کی طرف تم پلٹائے جانے والے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(29,18,'29:18','اور اگر تم جھُٹلا تے ہو تو تم سے پہلے بہت سی قومیں جھُٹلا چکی ہیں، اور رسُولؐ پر صاف صاف پیغام پہنچا دینے کے سوا کوئی ذمّہ داری نہیں ہے۔“');
INSERT INTO "ur_maududi" VALUES(29,19,'29:19',' کیا اِن لوگوں نے کبھی دیکھا ہی نہیں ہے کہ اللہ کس طرح خلق کی ابتدا کرتا ہے، پھر اُس کا اعادہ کرتا ہے؟ یقیناً یہ (اعادہ تو)اللہ کے لیے آسان تر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(29,20,'29:20','ان سے کہو کہ زمین میں چلو پھرو اور دیکھو کہ اُس نے کس طرح خلق کی ابتدا کی ہے ، پھر اللہ بار دیگر بھی زندگی بخشے گا۔ یقیناً اللہ ہر چیز پر قادر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(29,21,'29:21','جسے چاہے سزا دے اور جس پر چاہے رحم فرمائے، اُسی کی طرف تم پھیرے جانے والے ہو');
INSERT INTO "ur_maududi" VALUES(29,22,'29:22','تم نہ زمین میں عاجز کرنے والے ہو نہ آسمان میں، اور اللہ سے بچانے والا کوئی سرپرست اور مددگار تمہارے لیے نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(29,23,'29:23','جن لوگوں نے اللہ کی آیات کا اور اس سے ملاقات کا انکار کیا ہے وہ میری رحمت سے مایوس ہو چکے ہیں اور ان کے لیے دردناک سزا ہے');
INSERT INTO "ur_maududi" VALUES(29,24,'29:24',' پھر اُس کی قوم کا جواب اِس کے سوا کچھ نہ تھا کہ انہوں نے کہا”قتل کردو اِسے یا جلا ڈالو اِس کو۔“ آخر کار اللہ نے اُسے آگ سے بچا لیا، یقیناً اس میں نشانیاں ہیں اُن لوگوں کے لیے جو ایمان لانے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(29,25,'29:25','اور اُس نے کہا ”تم نے دنیا کی زندگی میں تو اللہ کو چھوڑ کر بُتوں کو اپنے درمیان محبّت کا ذریعہ بنالیا ہے مگر قیامت کے روز تم ایک دُوسرے کا انکار اور ایک دُوسرے پر لعنت کرو گے اور آگ تمہارا ٹھکانا ہوگی اور کوئی تمہارا مددگار نہ ہوگا۔“');
INSERT INTO "ur_maududi" VALUES(29,26,'29:26','اُس وقت لُوطؑ نے اُس کو مانا۔ اور ابراہیمؑ نے کہا میں اپنے ربّ کی طرف ہجرت کرتا ہوں، وہ زبردست ہے اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(29,27,'29:27','اور ہم نے اُسے اسحاقؑ اور یعقوبؑ (جیسی اولاد)عنایت فرمائی اور اُس کی نسل میں نبوّت اور کتاب رکھ دی، اور اسے دنیا میں اُس کا اجر عطا کیا اور آخرت میں وہ یقیناً صالحین میں سے ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(29,28,'29:28',' اور ہم نے لُوطؑ کو بھیجا جبکہ اُس نے اپنی قوم سے کہا”تم تو وہ فحش کام کرتے ہو جو تم سے پہلے دنیا والوں میں سے کسی نے نہیں کیا ہے');
INSERT INTO "ur_maududi" VALUES(29,29,'29:29','کیا تمہارا حال یہ ہے کہ مَردوں کے پاس جاتے ہو، اور رہزنی کرتے ہو اور اپنی مجلسوں میں بُرے کام کرتے ہو؟“ پھر کوئی جواب اُس کی قوم کے پاس اس کے سوا نہ تھا کہ انہوں نے کہا”لے آ اللہ کا عذاب اگر تُو سچا ہے۔“');
INSERT INTO "ur_maududi" VALUES(29,30,'29:30','لوطؑ نے کہا "اے میرے رب، اِن مفسد لوگوں کے مقابلے میں میری مدد فرما"');
INSERT INTO "ur_maududi" VALUES(29,31,'29:31','اور جب ہمارے فرستادے ابراہیمؑ کے پاس بشارت لے کر پہنچے تو انہوں نے اُس سے کہا”ہم اِس بستی کے لوگوں کو ہلاک کرنے والے ہیں، اس کے لوگ سخت ظالم ہو چکے ہیں۔“');
INSERT INTO "ur_maududi" VALUES(29,32,'29:32','ابراہیمؑ نے کہا”وہاں تو لُوطؑ موجود ہے۔“ انہوں نے کہا”ہم خوب جانتے ہیں کہ وہاں کون کون ہے۔ ہم اُسے ، اور اُس کی بیوی کے سوا اس کے باقی گھر والوں کو بچا لیں گے۔“ اس کی بیوی پیچھے رہ جانے والوں میں سے تھی۔ ');
INSERT INTO "ur_maududi" VALUES(29,33,'29:33','پھر جب ہمارے فرستادے لُوطؑ کے پاس پہنچے تو ان کی آمد پر وہ سخت پریشان اور دل تنگ ہوا۔ اُنہوں نے کہا”نہ ڈرو اور نہ رنج کرو۔ ہم تمہیں اور تمہارے گھر والوں کو بچا لیں گے ، سوائے تمہاری بیوی کے جو پیچھے رہ جانے والوں میں سے ہے');
INSERT INTO "ur_maududi" VALUES(29,34,'29:34','ہم اس بستی کے لوگوں پر آسمان سے عذاب نازل کرنے والے ہیں اُس فسق کی بدولت جو یہ کرتے رہے ہیں"');
INSERT INTO "ur_maududi" VALUES(29,35,'29:35','اور ہم نے اُس بستی کی ایک کھُلی نشانی چھوڑ دی ہے اُن لوگوں کے لیے جو عقل سے کام لیتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(29,36,'29:36','اور مَدیَن کی طرف ہم نے اُن کے بھائی شعیبؑ کو بھیجا۔ اُس نے کہا”اے میری قوم کے لوگو، اللہ کی بندگی کرو اور روزِ آخر کے اُمیدوار رہو اور زمین میں مفسد بن کر زیادتیاں نہ کرتے پھرو۔“');
INSERT INTO "ur_maududi" VALUES(29,37,'29:37','مگر انہوں نے اسے جھُٹلا دیا۔ آخر کار ایک سخت زلزلے نے انہیں آلیا اور وہ اپنے گھروں میں پڑے کے پڑے رہ گئے');
INSERT INTO "ur_maududi" VALUES(29,38,'29:38','اور عاد و ثمود کو ہم نے ہلاک کیا، تم وہ مقامات دیکھ چکے ہو جہاں وہ رہتے تھے۔ اُن کے اعمال کو شیطان نے اُن کے لیے خوشنما بنا دیا اور انہیں راہِ راست سے برگشتہ کر دیا حالانکہ وہ ہوش گوش رکھتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(29,39,'29:39','اور قارون و فرعون و ہامان کو ہم نے ہلاک کیا۔ موسیٰؑ اُن کے پاس بیّنات لے کر آیا مگر انہوں نے زمین میں اپنی بڑا ئی کا زعم کیا حالانکہ وہ سبقت لے جانے والے نہ تھے۔ ');
INSERT INTO "ur_maududi" VALUES(29,40,'29:40','آخر کار ہر ایک کو ہم نے اُس کے گناہ میں پکڑا، پھر اُن میں سے کسی پر ہم نے پتھراوٴ کرنے والی ہوا بھیجی، اور کسی کو ایک زبردست دھماکے نے آلیا، اور کسی کو ہم نے زمین میں دھنسا دیا، اور کسی کو غرق کر دیا۔ اللہ ان پر ظلم کر نے والا نہ تھا، مگر وہ خود ہی اپنے اُوپر ظلم کر رہے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(29,41,'29:41','جن لوگوں نے اللہ کو چھوڑ کر دوسرے سرپرست بنا لیے ہیں اُن کی مثال مکڑی جیسی ہے جو اپنا ایک گھر بناتی ہے اور سب گھروں سے زیادہ کمزور گھر مکڑی کا گھر ہی ہوتا ہے۔ کاش یہ لوگ علم رکھتے۔ ');
INSERT INTO "ur_maududi" VALUES(29,42,'29:42','یہ لوگ اللہ کو چھوڑ کر جس چیز کو بھی پکارتے ہیں اللہ اُسے خوب جانتا ہے اور وہی زبردست اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(29,43,'29:43','یہ مثالیں ہم لوگوں کی فہمائش کے لیے دیتے ہیں، مگر ان کو وہی لوگ سمجھتے ہیں جو علم رکھنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(29,44,'29:44','اللہ نے آسمانوں اور زمین کو بر حق پیدا کیا ہے، در حقیقت اس میں ایک نشانی ہے اہلِ ایمان کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(29,45,'29:45','(اے نبی ؐ)تلاوت کرو اس کتاب کی جو تمہاری طرف وحی کی ذریعے سے بھیجی گئی ہے۔ اور نماز قائم کرو، یقیناً نماز فحش اور بُرے کاموں سے روکتی ہے اور اللہ کا ذکر اس سے بھی زیادہ بڑی چیز ہے۔ اللہ جانتا ہے جو کچھ تم لوگ کرتے ہو');
INSERT INTO "ur_maududi" VALUES(29,46,'29:46',' اور اہل کتاب سے بحث نہ کرو مگر عمدہ طریقہ سے ۔۔۔۔ سوائے ان لوگوں کے جو ان میں سے ظالم ہوں --۔۔۔۔ اور ان سے کہو کہ”ہم ایمان لائے ہیں اس چیز پر بھی جو ہماری طرف بھیجی گئی ہے اور اس چیز پر بھی جو تمہاری طرف بھیجی گئی تھی، ہمارا خدا اور تمہارا خدا ایک ہی ہے اور ہم اسی کے مسلم(فرمابردار)ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(29,47,'29:47','(اے نبیؐ) ہم نے اسی طرح تمہاری طرف کتاب نازل کی ہے، اس لیے وہ لوگ جن کو ہم نے پہلے کتاب دی تھی وہ اس پر ایمان لاتے ہیں، اور ان لوگوں میں سے بھی بہت سے اس پر ایمان لا رہے ہیں، اور ہماری آیات کا انکار صرف کافر ہی کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(29,48,'29:48','(اے نبیؐ)تم اس سے پہلے کوئی کتاب نہیں پڑھتے تھے اور نہ اپنے ہاتھ سے لکھتے تھے، اگر ایسا ہوتا تو باطل پرست لوگ شک میں پڑ سکتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(29,49,'29:49','دراصل یہ روشن نشانیاں ہیں ان لوگوں کے دلوں میں جنہیں علم بخشا گیا ہے، اور ہماری آیات کا انکا ر نہیں کرتے مگر وہ جو ظالم ہیں');
INSERT INTO "ur_maududi" VALUES(29,50,'29:50','یہ لوگ کہتے ہیں کہ ”کیوں نہ اُتاری گئیں اس شخص پر نشانیاں اس کے رب کی طرف سے“؟کہو،”نشانیاں تو اللہ کے پاس ہیں اور میں صرف خبر دار کرنے والا ہوں کھول کھول کر“');
INSERT INTO "ur_maududi" VALUES(29,51,'29:51','اور کیا ان لوگوں کے لیے یہ (نشانی)کافی نہیں ہے کہ ہم نے تم پر کتاب نازل کی جو انھیں پڑھ کر سنائی جاتی ہے؟ درحقیقت اس میں رحمت ہے اور نصیحت ان لوگوں کے لیے جو ایمان لاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(29,52,'29:52','(اے نبیؐ) کہو کہ "میرے اور تمہارے درمیان اللہ گواہی کے لیے کافی ہے وہ آسمانوں اور زمین میں سب کچھ جانتا ہے جو لوگ باطل کو مانتے ہیں اور اللہ سے کفر کرتے ہیں وہی خسارے میں رہنے والے ہیں"');
INSERT INTO "ur_maududi" VALUES(29,53,'29:53','ہ لوگ تم سے عذاب جلدی لانے کا مطالبہ کرتے ہیں۔ اگر ایک وقت مقرر نہ کر دیا گیا ہوتا تو ان پر عذاب آچکا ہوتا۔ اور یقیناً(اپنے وقت پر)وہ آکر رہے گا اچانک ، اس حال میں کہ انہیں خبر بھی نہ ہو گی');
INSERT INTO "ur_maududi" VALUES(29,54,'29:54','یہ تم سے عذاب جلدی لانے کا مطالبہ کرتے ہیں، حالانکہ جہنم ان کافروں کو گھیرے میں لے چکی ہے');
INSERT INTO "ur_maududi" VALUES(29,55,'29:55','(اور انہیں پتہ چلے گا) اُس روز جبکہ عذاب انہیں اُوپر سے بھی ڈھانک لے گا اور پاؤں کے نیچے سے بھی اور کہے گا کہ اب چکھو مزا ان کرتوتوں کا جو تم کرتے تھے');
INSERT INTO "ur_maududi" VALUES(29,56,'29:56','اے میرے بندو جو ایمان لائے ہو، میری زمین وسیع ہے، پس تم میری ہی بندگی بجا لاؤ۔ ');
INSERT INTO "ur_maududi" VALUES(29,57,'29:57','ہر متنفس کو موت کا مزا چکھنا ہے، پھر تم سب ہماری طرف ہی پلٹا کر لائے جاؤ گے۔ ');
INSERT INTO "ur_maududi" VALUES(29,58,'29:58','جو لوگ ایمان لائے ہیں اور جنہوں نے نیک عمل کیے ہیں ان کو ہم جنت کی بلندو بالا عمارتوں میں رکھیں گے جن کے نیچے نہریں بہتی ہوں گی، جہاں وہ ہمیشہ رہیں گے، کیا ہی عمدہ اجر ہے عمل کرنے والوں کےلیے ');
INSERT INTO "ur_maududi" VALUES(29,59,'29:59','ان لوگوں کے لیے جنہوں نے صبر کیا ہے اور جو اپنے رب پر بھروسہ کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(29,60,'29:60','کتنے ہی جانور ہیں جو اپنا رزق اُٹھائے نہیں پھرتے، اللہ ان کو رزق دیتا ہے اور تمہارا رازق بھی وہی ہے، وہ سب کچھ سنتا اور جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(29,61,'29:61',' اگر تم ان لوگوں سے پوچھوں کہ زمین اور آسمانوں کو کس نے پیدا کیا ہے اور چاند اور سورج کو کس نےمسخر کر رکھا ہے تو ضرور کہیں گے کہ اللہ نے، پھر یہ کدھر سے دھوکا کھا رہے ہیں؟');
INSERT INTO "ur_maududi" VALUES(29,62,'29:62','اللہ ہی ہے جو اپنے بندوں میں سے جس کا چاہتا ہے رزق کشادہ کرتا ہے اور جس کا چاہتا ہے تنگ کرتا ہے، یقیناً اللہ ہر چیز کا جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(29,63,'29:63','اور اگر تم ان سے پوچھو کس نے آسمان سے پانی برسایا اور اس کے ذریعہ سےمردہ پڑی ہوئی زمین کو جِلا اُٹھایا تو وہ ضرور کہیں گے اللہ نے۔ کہو، الحمدللہ، مگر اکثر لوگ سمجھتے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(29,64,'29:64','اور یہ دنیا کی زندگی کچھ نہیں ہے مگر ایک کھیل اور دل کا بہلاوا۔ اصل زندگی کا گھر تو دارِ آخرت ہے، کاش یہ لوگ جانتے۔ ');
INSERT INTO "ur_maududi" VALUES(29,65,'29:65','جب یہ لوگ کشتی پر سوار ہوتے ہیں تو اپنے دین کو اللہ کے لیے خالص کر کے اُس سے دُعا مانگتے ہیں، پھر جب وہ اِنہیں بچا کر خشکی پر لے آتا ہے تو یکایک یہ شرک کرنے لگتے ہیں');
INSERT INTO "ur_maududi" VALUES(29,66,'29:66','تا کہ اللہ کی دی ہوئی نجات پر اس کا کفر ان نعمت کریں اور (حیات دنیا کے) مزے لوٹیں۔ اچھا، عنقریب انہیں معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(29,67,'29:67','کیا یہ دیکھتے نہیں ہیں کہ ہم نے ایک پُر امن حرم بنا دیا ہے حالانکہ ان کے گرد وپیش لوگ اُچک لیے جاتے ہیں؟ کیا پھر بھی یہ لوگ باطل کو مانتے ہیں اور اللہ کی نعمت کا کفران کرتے ہیں؟');
INSERT INTO "ur_maududi" VALUES(29,68,'29:68','اس شخص سے بڑا ظالم کون ہو گا جو اللہ پر جھوٹ باندھے یا حق کو جھٹلائے جب کہ وہ اس کے سامنے آچکا ہو؟ کیا ایسے کافروں کا ٹھکانہ جہنم ہی نہیں ہے؟');
INSERT INTO "ur_maududi" VALUES(29,69,'29:69','جو لوگ ہماری خاطر مجاہدہ کریں گے انہیں ہم اپنے راستے دکھائیں گے، اور یقیناً اللہ نیکو کاروں ہی کے ساتھ ہے');
INSERT INTO "ur_maududi" VALUES(30,1,'30:1','ا ل م');
INSERT INTO "ur_maududi" VALUES(30,2,'30:2','رومی قریب کی سرزمین میں مغلوب ہو گئے ہیں');
INSERT INTO "ur_maududi" VALUES(30,3,'30:3','اور اپنی اس مغلوبیت کے بعد چند سال کے اندر وہ غالب ہو جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(30,4,'30:4','اللہ ہی کا اختیار ہے پہلے بھی اور بعد میں بھی۔ اور وہ دن وہ ہو گا جبکہ اللہ کی بخشی ہوئی فتح پر مسلمان خوشیاں منائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(30,5,'30:5','اللہ نصرت عطا فرماتا ہے جسے چاہتا ہے، اور وہ زبردست اور رحیم ہے');
INSERT INTO "ur_maududi" VALUES(30,6,'30:6','یہ وعدہ اللہ نے کیا ہے، اللہ کبھی اپنے وعدے کی خلاف ورزی نہیں کرتا، مگر اکثر لوگ نہیں جانتے ہیں');
INSERT INTO "ur_maududi" VALUES(30,7,'30:7','لوگ دنیا کی زندگی کا بس ظاہری پہلو جانتے ہیں اور آخرت سے وہ خود ہی غافل ہیں ');
INSERT INTO "ur_maududi" VALUES(30,8,'30:8','کیا انہوں نے کبھی اپنے آپ میں غور و فکر نہیں کیا؟ اللہ نے زمین اور آسمانوں کو اور ان ساری چیزوں کو جو ان کے درمیان ہیں برحق اور ایک مقرر مدت ہی کے لیے پیدا کیا ہے۔ مگر بہت سے لوگ اپنے رب کی ملاقات کے منکر ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(30,9,'30:9','اور کیا یہ لو گ کبھی زمین میں چلے پھرے نہیں ہیں کہ انہیں ان لوگوں کا انجام نظر آتا جو ان سے پہلے گزر چکے ہیں؟ وہ ان سے زیادہ طاقت رکھتے تھے ، انہوں نے زمین کو خوب ادھیڑا تھا اور اسے اتنا آباد کیا تھا جتنا انہوں نے نہیں کیا ہے۔ ان کےپاس ان کے رسول روشن نشانیاں لے کر آئے۔ پھر اللہ ان پر ظلم کرنے والا نہ تھا، مگر وہ خود ہی اپنے اوپر ظلم کر رہے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(30,10,'30:10','آ خرکار جن لوگوں نے بُرائیاں کی تھیں ان کا انجام بہت برا ہوا، اس لیے کہ انہوں نے اللہ کی آیات کو جھٹلایا تھا اور وہ ان کا مذاق اڑاتے تھے');
INSERT INTO "ur_maududi" VALUES(30,11,'30:11','اللہ ہی خلق کی ابتدا کرتا ہے ، پھر وہی اس کا اعادہ کرے گا، پھر اسی کی طرف تم پلٹائے جاؤ گے');
INSERT INTO "ur_maududi" VALUES(30,12,'30:12','اور جب وہ ساعت برپا ہو گی اس دن مجرم ہَک دَک رہ جائیں گے ، ');
INSERT INTO "ur_maududi" VALUES(30,13,'30:13','ان کے ٹھیر ائے ہوئے شریکوں میں کوئی ان کا سفارشی نہ ہو گا اور وہ اپنے شریکوں کےمنکر ہو جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(30,14,'30:14','جس روز ساعت برپا ہوگی، اس دن (سب انسان)الگ گروہوں میں بٹ جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(30,15,'30:15','جو لوگ ایمان لائے ہیں اور جنہوں نے نیک عمل کیے ہیں وہ ایک باغ میں شاداں و فرحاں رکھے جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(30,16,'30:16','اور جنہوں نے کفر کیا ہے اور ہماری آیات کو اور آخرت کی ملاقات کو جھٹلایا ہے وہ عذاب میں حاضررکھے جائیں گے');
INSERT INTO "ur_maududi" VALUES(30,17,'30:17',' پس تسبیح کرو اللہ کی جبکہ تم شام کرتے ہو اور جب صبح کرتے ہو');
INSERT INTO "ur_maududi" VALUES(30,18,'30:18','آسمانوں اور زمین میں اسی کے لیے حمد ہے۔ اور (تسبیح کرو اس کی) تیسرے پہر اور جبکہ تم پر ظہر کا وقت آتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(30,19,'30:19','وہ زندہ میں سے مردے کو نکالتا ہے اور مردے میں سے زندہ کو نکال لاتا ہے اور زمین کو اس کی موت کے بعد زندگی بخشتا ہے۔ اسی طرح تم لوگ بھی (حالتِ موت سے) نکال لیے جاؤ گے');
INSERT INTO "ur_maududi" VALUES(30,20,'30:20',' اس کی نشانیوں میں سے یہ ہے کہ اس نے تم کو مٹی سے پیدا کیا۔ پھر یکایک تم بشر ہو کہ (زمین میں)پھیلتے چلے جا رہے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(30,21,'30:21','اور اس کی نشانیوں میں سے یہ ہے کہ اس نے تمہارے لیے تمہاری ہی جنس سے بیویاں بنائیں تاکہ تم ان کے پاس سکون حاصل کرو اور تمہارے درمیان محبّت اور رحمت پیدا کر دی۔ یقیناً اس میں بہت سی نشانیاں ہیں ان لوگوں کے لیے جو غور وفکر کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(30,22,'30:22','اور اس کی نشانیوں میں سے آسمانوں اور زمین کی پیدائش، اور تمہاری زبانوں اور تمہارے رنگوں کا اختلاف ہے۔ یقیناً اس میں بہت سی نشانیاں ہیں دانشمند لوگوں کے لیے');
INSERT INTO "ur_maududi" VALUES(30,23,'30:23','اور اس کی نشانیوں میں سے تمہارا رات اور دن کو سونا اور تمہارا اس کے فضل کو تلاش کرنا ہے۔ یقیناًاس میں سے بہت سی نشانیاں ہیں ان لوگوں کے لیے جو (غور سے) سنتے ہیں');
INSERT INTO "ur_maududi" VALUES(30,24,'30:24','اور اس کی نشانیوں میں سے یہ ہے کہ وہ تمہیں بجلی کی چمک دکھاتا ہے خوف کے ساتھ بھی اور طمع کے ساتھ بھی۔ اور آسمان سے پانی بر سا تا ہے، پھر اس کے ذریعہ سے زمین کو اس کی موت کے بعد زندگی بخشتا ہے۔ یقیناً اس میں بہت سی نشانیاں ہیں ان لوگوں کے لیے جو عقل سے کام لیتے ہیں');
INSERT INTO "ur_maududi" VALUES(30,25,'30:25','اور اس کی نشانیوں میں سے یہ ہے کہ آسمان اور زمین اس کےحکم سے قائم ہیں۔ پھر جو نہی کہ اس نےتمہیں زمین سے پکارا ، بس ایک ہی پکار میں اچانک تم نکل آؤ گے۔ ');
INSERT INTO "ur_maududi" VALUES(30,26,'30:26','آسمانوں اور زمین میں جو بھی ہیں اُس کے بندے ہیں، سب کے سب اسی کے تابع فرمان ہیں');
INSERT INTO "ur_maududi" VALUES(30,27,'30:27','وہی ہے جو تخلیق کی ابتد کرتا ہے، پھر وہی اس کا اعادہ کرے گا اور یہ اس کے لیے آسان تر ہے۔ آسمانوں اور زمین میں اس کی صفت سب سے برتر ہے اور وہ زبردست اور حکیم ہے');
INSERT INTO "ur_maududi" VALUES(30,28,'30:28','وہ تمہیں خود تمہاری اپنی ہی ذات سےایک مثال دیتا ہے۔ کیا تمہارے اُن غلاموں میں سے جو تمہاری ملکیت میں ہیں کچھ غلام ایسے بھی ہیں جو ہمارے دیے ہوئے مال و دولت میں تمہارے ساتھ برابر کے شریک ہوں اور تم ان سے اس طرح ڈرتے ہو جس طرح آپس میں اپنے ہمسروں سے ڈرتے ہو؟ --۔۔۔۔ اس طرح ہم آیا ت کھول کر پیش کرتے ہیں ان لوگوں کے لیے جو عقل سے کام لیتے ہیں');
INSERT INTO "ur_maududi" VALUES(30,29,'30:29','مگر یہ ظالم بے سمجھے بوجھے اپنے تخیلات کے پیچھے چل پڑے ہیں۔ اب کون اس شخص کو راستہ دکھا سکتا ہے جسے اللہ نے بھٹکا دیا ہو۔ ایسے لوگوں کا تو کوئی مددگار نہیں ہو سکتا');
INSERT INTO "ur_maududi" VALUES(30,30,'30:30',' پس (اے نبیؐ اور نبیؐ کے پیروو) یک سُو ہو کر اپنا رخ اس دین کی سمت میں جمادو، قائم ہو جاؤ اس فطرت پر جس پر اللہ تعالیٰ نےانسانوں کو پیدا کیا ہے، اللہ کی بنائی ہوئی ساخت بدلی نہیں جا سکتی، یہی بالکل راست اور درست دین ہے، مگر اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(30,31,'30:31','(قائم ہو جاؤ اس بات پر) اللہ کی طرف رجوع کرتے ہوئے، اور ڈرو اس سے اور نماز قائم کرو، اور نہ ہو جاؤ ان مشرکین میں سے');
INSERT INTO "ur_maududi" VALUES(30,32,'30:32','جنہوں نے اپنا اپنا دین الگ بنا دیا ہے او گروہوں میں بٹ گئے ہیں،ہر ایک گروہ کے پاس جو کچھ ہے اسی میں وہ مگن ہے۔ ');
INSERT INTO "ur_maududi" VALUES(30,33,'30:33','لوگوں کا حال یہ ہے کہ جب انھیں کوئی تکلیف پہنچتی ہے تو اپنے رب کی طرف رجوع کر کے اسے پکارتے ہیں، پھر جب وہ کچھ اپنی رحمت کا ذائقہ انھیں چکھا دیتا ہے تو یکایک ان میں سے کچھ لوگ شرک کرنے لگتے ہیں ');
INSERT INTO "ur_maududi" VALUES(30,34,'30:34','تاکہ ہمارے کیے ہوئے احسان کی ناشکری کریں اچھا، مزے کر لو، عنقریب تمہیں معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(30,35,'30:35','کیا ہم نے کوئی سند اور دلیل ان پر نازل کی ہے جو شہادت دیتی ہو اس شرک کی صداقت پر جو یہ کر رہے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(30,36,'30:36','جب ہم لوگوں کو رحمت کا ذائقہ چکھاتے ہیں تو وہ اس پر پھول جاتے ہیں اور جب ان کے اپنے کیے کرتوتوں سے ان پر کوئی مصیبت آتی ہے تو یکایک وہ مایوس ہونے لگتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(30,37,'30:37','کیا یہ لوگ دیکھتے نہیں ہیں کہ اللہ ہی رزق کشادہ کرتا ہے جس کا چاہتا ہے اور تنگ کرتاہے(جس کا چاہتا ہے)۔ یقیناً اس میں بہت سی نشانیاں ہیں ان لوگوں کے لیے جو ایمان لاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(30,38,'30:38','پس (اے مومن)رشتہ دار کو اس کا حق دے اور مسکین و مسافر کو (اس کا حق) ۔ یہ طریقہ بہتر ہے ان لوگوں کے لیے جو اللہ کی خوشنودی چاہتے ہوں، اور وہی فلاح پانے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(30,39,'30:39','جو سُود تم دیتے ہو تا کہ لوگوں کے اموال میں شامل ہو کر وہ بڑھ جائے ، اللہ کے نزدیک وہ نہیں بڑھتا، اور جو زکوٰة تم اللہ کی خوشنودی حاصل کر نے کے ارادے سے دیتے ہو، اسی کے دینے والے درحقیقت اپنے مال بڑھاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(30,40,'30:40',' اللہ ہی ہے جس نے تم کو پیدا کیا ، پھر تمہیں رزق دیا، پھر وہ تمہیں موت دیتا ہے، پھر وہ تمہیں زندہ کرے گا۔ کیا تمہارے ٹھیرائے ہوئے شریکوں میں کوئی ایسا ہے جو ان میں سے کوئی کام بھی کرتا ہو؟ پاک ہے وہ اور بہت بالا و بر تر ہے اس شرک سے جو یہ لوگ کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(30,41,'30:41','خشکی اور تری میں فساد برپا ہو گیا ہے لوگوں کے اپنے ہاتھوں کی کمائی سے تاکہ مزّہ چکھائے اُن کو اُن کے بعض اعمال کا، شاید کہ وہ باز آئیں۔ ');
INSERT INTO "ur_maududi" VALUES(30,42,'30:42','(اے نبیؐ)ان سے کہو کہ زمین میں چل پھر کر دیکھو پہلے گُزرے ہوئے لوگوں کا کیا انجام ہو چکا ہے، ان میں اکثر مشرک ہی تھے۔ ');
INSERT INTO "ur_maududi" VALUES(30,43,'30:43','پس (اے نبیؐ)اپنا رُخ مضبوطی کے ساتھ جما دو اِس دینِ راست کی سمت میں قبل اس کے کہ وہ دن آئے جس کے ٹل جانے کی کوئی صُورت اللہ کی طرف سے نہیں ہے۔ اُس دن لوگ پھٹ کر ایک دُوسرے سے الگ ہو جائیں گے');
INSERT INTO "ur_maududi" VALUES(30,44,'30:44','جس نے کُفر کیا ہے اُس کے کُفر کا وبال اُسی پر ہے۔ اور جن لوگوں نے نیک عمل کیا ہے وہ اپنے ہی لیے فلاح کا راستہ صاف کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(30,45,'30:45','تاکہ اللہ ایمان لانے والوں اور عملِ صالح کرنے والوں کو اپنے فضل سے جزا دے یقیناً وہ کافروں کو پسند نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(30,46,'30:46','اُس کی نشانیوں میں سے یہ ہے کہ وہ ہوائیں بھیجتا ہے بشارت دینے کے لیے اور تمہیں اپنی رحمت سے بہرہ مند کرنے کے لیے اور اِس غرض کے لیے کہ کشتیاں اُس کے حکم سے چلیں اور تم اُس کا فضل تلاش کرو اور اُس کے شکر گزار بنو');
INSERT INTO "ur_maududi" VALUES(30,47,'30:47','اور ہم نے تُم سے پہلے رسُولوں کو اُن کی قوم کی طرف بھیجا اور وہ اُن کے پاس روشن نشانیاں لے کر آئے، پھر جنہوں نے جُرم کیا اُن سے ہم نے انتقام لیا اور ہم پر یہ حق تھا کہ ہم مومنوں کی مدد کریں');
INSERT INTO "ur_maududi" VALUES(30,48,'30:48','اللہ ہی ہے جو ہواؤں کو بھیجتا ہے اور وہ بادل اٹھاتی ہیں، پھر وہ اِن بادلوں کو آسمان میں پھیلا دیتا ہے جس طرح چاہتا ہے اور انہیں ٹکڑیوں میں تقسیم کرتا ہے، پھر تُو دیکھتا ہے کہ بارش کے قطرے بادل سے ٹپکے چلے آتے ہیں یہ بارش جب وہ اپنے بندوں میں سے جن پر چاہتا ہے برساتا ہے');
INSERT INTO "ur_maududi" VALUES(30,49,'30:49','تو یکایک وہ خوش و خرم ہو جاتے ہیں حالانکہ اس کے نزول سے پہلے وہ مایوس ہو رہے تھے');
INSERT INTO "ur_maududi" VALUES(30,50,'30:50','دیکھو اللہ کی رحمت کے اثرات کہ مُردہ پڑی ہوئی زمین کو وہ کس طرح جِلا اُٹھاتا ہے، یقیناً وہ مُردوں کو زندگی بخشنے والا ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_maududi" VALUES(30,51,'30:51','اور اگر ہم ایک ایسی ہوا بھیج دیں جس کے اثر سے وہ اپنی کھیتی کو زرد پائیں تو وہ کُفر کرتے رہ جاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(30,52,'30:52','(اے نبیؐ)تم مُردوں کو نہیں سُنا سکتے، نہ اُن بہروں کو اپنی پُکار سُنا سکتے ہو جو پیٹھ پھیرے چلے جارہے ہوں ');
INSERT INTO "ur_maududi" VALUES(30,53,'30:53','اور نہ تم اندھوں کو ان کی گمراہی سے نکال کر راہِ راست دکھا سکتے ہو۔ تم تو صرف اُنہی کو سُنا سکتے ہو جو ہماری آیات پر ایمان لاتے اور سرِ تسلیم خم کر دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(30,54,'30:54','اللہ ہی تو ہے جس نے ضعف کی حالت سے تمہاری پیدائش کی ابتدا کی ، پھر اس ضعف کے بعد تمہیں قوّت بخشی، پھر اس قوّت کے بعد تمہیں ضعیف اور بُوڑھا کر دیا۔ وہ جو کچھ چاہتا ہے پیدا کرتا ہے۔ وہ سب کچھ جاننے والا ، ہر چیز پر قدرت رکھنے والا ہے');
INSERT INTO "ur_maududi" VALUES(30,55,'30:55','اور جب وہ ساعت برپا ہوگی تو مجرم قسمیں کھاکھا کر کہیں گے کہ ہم ایک گھڑی بھر سے زیادہ نہیں ٹھہرے ہیں، اسی طرح وہ دنیا کی زندگی میں دھوکا کھایا کرتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(30,56,'30:56','مگر جو علم اور ایمان سے بہرہ مند کیے گئے تھے وہ کہیں گے کہ خدا کے نوشتے میں تو تم روزِ حشر تک پڑے رہے ہو، سو یہ وہی روزِ حشر ہے، لیکن تم جانتے نہ تھے');
INSERT INTO "ur_maududi" VALUES(30,57,'30:57','پس وہ دن ہوگا جس میں ظالموں کو ان کی معذرت کوئی نفع نہ دے گی اور نہ اُن سے معافی مانگنے کے لیے کہا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(30,58,'30:58','ہم نے اِس قرآن میں لوگوں کو طرح طرح سے سمجھایا ہے تم خواہ کوئی نشانی لے آؤ، جن لوگوں نے ماننے سے انکار کر دیا ہے وہ یہی کہیں گے کہ تم باطل پر ہو');
INSERT INTO "ur_maududi" VALUES(30,59,'30:59','اِس طرح ٹھپہ لگا دیتا ہے اللہ اُن لوگوں کے دلوں پر جو بے علم ہیں');
INSERT INTO "ur_maududi" VALUES(30,60,'30:60','پس (اے نبیؐ)صبر کرو، یقیناً اللہ کا وعدہ سچا ہے، اور ہرگز ہلکا نہ پائیں تم کو وہ لوگ جو یقین نہیں لاتے۔ ');
INSERT INTO "ur_maududi" VALUES(31,1,'31:1','الم');
INSERT INTO "ur_maududi" VALUES(31,2,'31:2','یہ کتاب حکیم کی آیات ہیں، ');
INSERT INTO "ur_maududi" VALUES(31,3,'31:3','ہدایت اور رحمت نیکوکار لوگوں کے لیے ');
INSERT INTO "ur_maududi" VALUES(31,4,'31:4','جو نماز قائم کرتے ہیں، زکوٰة دیتے ہیں اور آخرت پر یقین رکھتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(31,5,'31:5','یہی لوگ اپنے ربّ کی طرف سے راہِ راست پر ہیں اور یہی فلاح پانے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(31,6,'31:6','اور انسانوں ہی میں سے کوئی ایسا بھی ہے جو کلام دلفریب خرید کر لاتا ہے تاکہ لوگوں کو اللہ کے راستہ سے علم کے بغیر بھٹکادے اور اس راستے کی دعوت کو مذاق میں اُڑا دے ۔ ایسے لوگوں کے لیے سخت ذلیل کرنے والا عذاب ہے۔ ');
INSERT INTO "ur_maududi" VALUES(31,7,'31:7','اسے جب ہماری آیات سنائی جاتی ہیں تو وہ بڑے گھمنڈ کے ساتھ اس طرح رخ پھیر لیتا ہے گویا کہ اس نے انہیں سنا ہی نہیں، گویا کہ اس کے کان بہرے ہیں اچھا، مژدہ سُنا دو اسے ایک درد ناک عذاب کا');
INSERT INTO "ur_maududi" VALUES(31,8,'31:8','البتہ جو لوگ ایمان لے آئیں اور نیک عمل کریں، اُن کے لیے نعمت بھری جنتیں ہیں ');
INSERT INTO "ur_maududi" VALUES(31,9,'31:9','جن میں وہ ہمیشہ رہیں گے۔ یہ اللہ کا پختہ وعدہ ہے اور وہ زبردست اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(31,10,'31:10',' اس نے آسمانوں کو پیدا کیا بغیر ستونوں کے جو تم کو نظر آئیں۔ اس نے زمین میں پہاڑ جمادیے تاکہ وہ تمہیں لے کر ڈُھلک نہ جائے۔ اس نے ہر طرح کے جانور زمین میں پھیلادیے اور آسمان سے پانی برسایا اور زمین میں قسم قسم کی عمدہ چیزیں اُگادیں');
INSERT INTO "ur_maududi" VALUES(31,11,'31:11','یہ تو ہے اللہ کی تخلیق ، اب ذرا مجھے دکھاوٴ ، ان دُوسروں نے کیا پیدا کیا ہے؟ ۔۔۔۔اصل بات یہ ہے کہ یہ ظالم لوگ صریح گمراہی میں پڑے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(31,12,'31:12',' ہم نے لقمان کو حکمت عطا کی تھی کہ اللہ کا شکر گزار رہو۔ جو کوئی شکر کرے اُس کا شکر اُس کے اپنے ہی لیے مفید ہے۔ اور جو کفر کرے حقیقت میں اللہ بے نیاز اور آپ سے آپ محمود ہے۔ ');
INSERT INTO "ur_maududi" VALUES(31,13,'31:13','یاد کرو جب لقمان اپنے بیٹے کو نصیحت کر رہا تھا تو اُس نے کہا ”بیٹا !خُدا کے ساتھ کسی کو شریک نہ کرنا، حق یہ ہے کہ شرک بہت بڑا ظلم ہے“ ');
INSERT INTO "ur_maududi" VALUES(31,14,'31:14',' اور یہ حقیقت ہے کہ ہم نے انسان کو اپنے والدین کا حق پہچاننے کی خود تاکید کی ہے۔ اُس کی ماں نے ضعف پر ضعف اُٹھاکر اسے اپنے پیٹ میں رکھا اور دو سال اس کا دودھ چھوٹنے میں لگے۔ (اسی لیے ہم نے ان کو نصیحت کی کہ) میرا شکر کر اور اپنے والدین کا شکر بجا لا، میری ہی طرف تجھے پلٹنا ہے');
INSERT INTO "ur_maududi" VALUES(31,15,'31:15','لیکن اگر وہ تجھ پر دباوٴ ڈالیں کہ میرے ساتھ تو کسی ایسے کو شریک کرے جسے تو نہیں جانتا تو ان کی بات ہر گز نہ مان۔ دُنیا میں ان کے ساتھ نیک برتاوٴ کر تا رہ مگرپیروی اُس شخص کے راستے کی کر جس نے میری طرف رجوع کیا ہے۔ پھر تم سب کو پلٹنا میری ہی طرف ہے ، اُس وقت میں تمہیں بتادوں گا کہ تم کیسے عمل کرتے رہے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(31,16,'31:16','(اور لقمان نے کہا تھا کہ)”کوئی چیز رائی کے دانہ برابر بھی ہو اور چٹان میں یا آسمانوں یا زمین میں کہیں چھُپی ہوئی ہو ، اللہ اُسے نکال لائے گا۔ وہ باریک بین اور باخبر ہے');
INSERT INTO "ur_maududi" VALUES(31,17,'31:17','بیٹا ، نماز قائم کر، نیکی کا حکم دے، بدی سے منع کر ، اور جو مصیبت بھی پڑے اس پر صبر کر ۔ یہ وہ باتیں ہیں جن کی بڑی تاکید کی گئی ہے ');
INSERT INTO "ur_maududi" VALUES(31,18,'31:18','اور لوگوں سے منہ پھیر کر بات نہ کر ، نہ زمین میں اکڑ کر چل ، اللہ کسی خود پسند اور فخر جتانے والے شخص کو پسند نہیں کرتا۔ ');
INSERT INTO "ur_maududi" VALUES(31,19,'31:19','اپنی چال میں اعتدال اختیار کر ، اور اپنی آواز ذراپست رکھ، سب آوازوں سے زیادہ بری آواز گدھوں کی آواز ہوتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(31,20,'31:20','کیا تم لوگ نہیں دیکھتے کہ اللہ نے زمین اور آسمانوں کی ساری چیزیں تمہارے لیے مسخّر کر رکھی ہیں اور اپنی کُھلی اور چھپی نعمتیں تم پر تمام کر دی ہیں ؟ اس پر حال یہ ہے کہ انسانوں میں سے کچھ لوگ ہیں جو اللہ کے بارے میں جھگڑتے ہیں بغیر اس کے کے ان کے پاس کوئی علم ہو ،یا ہدایت ، یا کوئی روشنی دکھانے والی کتاب۔ ');
INSERT INTO "ur_maududi" VALUES(31,21,'31:21','اور جب ان سے کہا جا تا ہے کہ پیروی کرو اس چیز کی جو اللہ نے نازل کی ہے تو کہتے ہیں کہ ہم تو اس چیز کی پیروی کریں گے جس پر ہم نے اپنے باپ دادا کو پایا ہے۔ کیا یہ انہی کی پیروی کریں گےخواہ شیطان ان کو بھڑکتی ہوئی آگ ہی کی طرف کیوں نہ بلاتا رہا ہو؟ ');
INSERT INTO "ur_maududi" VALUES(31,22,'31:22','جو شخص اپنے آپ کو اللہ کے حوالہ کر دے اور عملاً وہ نیک ہو ، اس نے فی الواقع ایک بھروسے کے قابل سہارا تھام لیا ، اور سارے معاملا ت کا آخری فیصلہ اللہ ہی کے ہاتھ ہے');
INSERT INTO "ur_maududi" VALUES(31,23,'31:23','اب جو کفر کرتا ہے اس کو کفر تمہیں غم میں مبتلا نہ کرے ، انھیں پلٹ کر آنا تو ہماری ہی طرف ہے، پھر ہم انھیں بتادیں گے کہ ہو کیا کچھ کرکے آئے ہیں۔ یقیناً اللہ سینوں کے چھپے ہوئے راز تک جانتا ہے');
INSERT INTO "ur_maududi" VALUES(31,24,'31:24','ہم تھوڑی مدت انہیں دُنیا میں مزے کرنے کا موقع دے رہے ہیں، پھر ان کو بے بس کر کے ایک سخت عذاب کی طرف کھینچ لے جائیں گے');
INSERT INTO "ur_maududi" VALUES(31,25,'31:25','اگر تم ان سے پوچھو کے زمین اور آسمانوں کو کس نے پیدا کیا ہے، تو یہ ضرور کہیں گے کہ اللہ نے۔ کہو الحمدللہ۔ مگر ان میں سے اکثر لوگ جانتے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(31,26,'31:26','آسمانوں اور زمین میں جو کچھ ہے اللہ ہی کا ہے ، بے شک اللہ بے نیاز اور آپ سے آپ محمود ہے۔ ');
INSERT INTO "ur_maududi" VALUES(31,27,'31:27','زمین میں جتنے درخت ہیں اگر وہ سب کے سب قلم بن جائیں اور سمندر(دوات بن جائے) جسے سات مزید سمندر روشنائی مہیا کریں تب بھی اللہ کی باتیں لکھنے سے ختم نہ ہوں گی۔ بے شک اللہ زبردست اور حکیم ہے');
INSERT INTO "ur_maududi" VALUES(31,28,'31:28','تم سارے انسانوں کو پیدا کرنا اور پھر دوبارہ جِلا اُٹھانا تو (اس کے لیے) بس ایسا ہے جیسے ایک متنفس کو (پیدا کرنا اور جِلا اٹھانا) حقیقت یہ ہی کہ اللہ سب کچھ سننے اور دیکھنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(31,29,'31:29','کیا تم دیکھتے نہیں ہوکہ اللہ رات کو دن میں پروتا ہؤالے آتا ہے اور دن کو راے میں۔ اس نے سورج اور چاند کو مسخر کر رکھا ہے ، سب ایک وقت مقرر تک چلے جا رہے ہیں ، اور (کیا تم نہیں جانتے) کہ جو کچھ بھی تم کرتے ہو اللہ اس سے باخبر ہے');
INSERT INTO "ur_maududi" VALUES(31,30,'31:30','یہ سب کچھ اس وجہ سے ہے کہ اللہ ہی حق ہے اور اسے چھوڑ کر جن دوسری چیزوں کو یہ لوگ پکارتے ہیں وہ سب باطل ہیں۔ اور (اس وجہ سے کہ) اللہ ہی بزرگ و بر تر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(31,31,'31:31','کیا تم نہیں دیکھتے ہو کہ کشتی سمندر میں اللہ کے فضل سے چلتی ہے تاکہ وہ تمہیں اپنی کچھ نشانیاں دکھائے؟ درحقیقت اس میں بہت سی نشانیاں ہیں ہر اس شخص کے لیے جو صبر اور شکر کرنے والا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(31,32,'31:32','اور جب (سمندر میں) ان لوگوں پر ایک موج سائبانوں کی طرح چھا جا تی ہے تو یہ اللہ کو پکارتے ہیں اپنے دین کو بالکل اسی کے لیے خالص کر کے، پھر جب وہ بچاکر انہیں خشکی تک پہنچا دیتا ہے تو ان میں سے کوئی اقتصاد برتتا ہے ، اور ہماری نشانیوں کا انکار نہیں کرتا مگر ہر وہ شخص جو غدار اور ناشکرا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(31,33,'31:33','لوگو ! بچو اپنے ربّ کے غضب سے اور ڈرو اُس دن سے جبکہ کوئی باپ اپنے بیٹے کی طرف سے بدلہ نہ دے گا اور نہ کوئی بیٹا ہی اپنے باپ کی طرف سے کچھ بدلہ دینے والا ہوگا۔ فی الواقع اللہ کا وعدہ سچّا ہے۔ پس یہ دُنیا کی زندگی تمہیں دھوکے میں نہ ڈالے اور نہ دھوکہ باز تم کو اللہ کے معاملے میں دھوکہ دینے پائے۔ ');
INSERT INTO "ur_maududi" VALUES(31,34,'31:34','اُس گھڑی کا علم اللہ ہی کے پاس ہے ، وہی بارش برساتا ہے، وہی جانتا ہے کہ ماوٴں کے پیٹوں میں کیا پرورش پارہا ہے ، کوئی متنفس نہیں جانتا کہ کل وہ کیا کمائی کرنے والا ہے اور نہ کسی شخص کو یہ خبر ہے کہ کِس سرزمین میں اس کو موت آنی ہے، اللہ ہی سب کچھ جاننے والا اور باخبر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(32,1,'32:1','ا ل م');
INSERT INTO "ur_maududi" VALUES(32,2,'32:2','اس کتاب کی تنزیل بلاشبہ ربّ العالمین کی طرف سے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(32,3,'32:3',' کیا یہ لوگ کہتے ہیں کہ اس شخص نے اِسے خود گھڑ لیا ہے؟ نہیں، بلکہ یہ حق ہے تیری رب کی طرف سے تاکہ تو متنبہ کرے ایک ایسی قوم کو جس کے پاس تجھ سے پہلے کوئی متنبہ کرنے والا نہیں آیا ، شاید کہ وہ ہدایت پا جائیں۔ ');
INSERT INTO "ur_maududi" VALUES(32,4,'32:4',' وہ اللہ ہی ہے جس نے آسمانوں اور زمین کو اور اُن ساری چیزوں کو جو ان کے درمیان ہیں چھ دنوں میں پیدا کیا اور اس کے بعد عرش پر جلوہ فرما ہوا ، اُس کے سوا نہ تمہارا کوئی حامی ومددگار ہے اور نہ کوئی اس کے آگے سفارش کرنے والا ، پھر کیا تم ہوش میں نہ آوٴ گے؟ ');
INSERT INTO "ur_maududi" VALUES(32,5,'32:5','وہ آسمان سے زمین تک دُنیا کے معاملات کی تدبیر کرتا ہے اور اس کی تدبیر روداد اوپر اُس کے حضور جاتی ہے ایک ایسے دن میں جس کی مقدار تمہارے شمار سے ایک ہزار سال ہے۔ ');
INSERT INTO "ur_maududi" VALUES(32,6,'32:6','وہی ہے ہر پوشیدہ اور ظاہر کا جاننے والا ، زبردست اور رحیم ');
INSERT INTO "ur_maududi" VALUES(32,7,'32:7','جو چیز بھی اس نے بنائی خوب ہی بنائی ۔ اُس نے انسان کی تخلیق کی ابتدا گارے سے کی');
INSERT INTO "ur_maududi" VALUES(32,8,'32:8','پھر اُس کی نسل ایک ایسے ست سے چلائی جو حقیر پانی کی طرح کا ہے ');
INSERT INTO "ur_maududi" VALUES(32,9,'32:9','پھر اُس کو نِک سُک سے درست کیا اور اس کے اندر اپنی روح پھونک دی ، اور تم کو کان دیے ، آنکھیں دیں اور دل دیے ۔ تم لوگ کم ہی شکر گزار ہوتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(32,10,'32:10',' اور یہ لوگ کہتے ہیں”جب ہم مٹی میں رَل مِل چکے ہوں گےتوکیا ہم پھر نئے سرے سے پیدا کیے جائیں گے؟“اصل بات یہ ہے کہ یہ اپنے ربّ کی ملاقات کے منکر ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(32,11,'32:11','اِن سے کہو ”موت کا وہ فرشتہ جو تم پر مقرّر کیا گیا ہے تم کو پورا کا پورا اپنے قبضے میں لے لے گا اور پھر تم اپنے ربّ کی طرف پلٹا لائے جاوٴ گے۔“ ');
INSERT INTO "ur_maududi" VALUES(32,12,'32:12',' کاش تم دیکھو وہ وقت جب یہ مُجرم سر جھکائے اپنے ربّ کے حضور کھڑے ہوں گے (اُس وقت یہ کہہ رہے ہوں گے)”اے ہمارے ربّ ، ہم نے خوب دیکھ لیا اور سُن لیا ، اب ہمیں واپس بھج دے تاکہ ہم نیک عمل کرے ، ہمیں اب یقین آگیا ہے۔“');
INSERT INTO "ur_maududi" VALUES(32,13,'32:13','(جواب میں ارشاد ہوگا)”اگر ہم چاہتے تو پہلے ہی ہر نفس کو اس کی ہدایت دے دیتے۔ مگر میری وہ بات پوری ہوگئی جو میں نے کہی تھی کہ میں جہنم کو جنوں اور انسانوں سب سے بھر دوں گا۔ ');
INSERT INTO "ur_maududi" VALUES(32,14,'32:14','پس اب چکھو مزا اپنی اس حرکت کا کہ تم نے اس دن کی ملاقات کو فراموش کردیا ، ہم نے بھی اب تمہیں فراموش کردیا ہے۔ چکھو ہمیشگی کے عذاب کا مزا اپنے کرتوتوں کی پاداش میں۔“');
INSERT INTO "ur_maududi" VALUES(32,15,'32:15','ہماری آیات پر تو وہ لوگ ایمان لاتے ہیں جنہیں یہ آیات سُنا کر جب نصیحت کی جاتی ہے تو سجدے میں گر پڑتے ہیں اور اپنے ربّ کی حمد کے ساتھ اس کی تسبیح کرتے ہیں اور تکبر نہیں کرتے ');
INSERT INTO "ur_maududi" VALUES(32,16,'32:16','اُن کی پیٹھیں بستروں سے الگ رہتی ہیں، اپنے ربّ کو خوف اور طمع کے ساتھ پُکارتے ہیں ، اور جو کچھ رزق ہم نے انہیں دیا ہے اس میں سے خرچ کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(32,17,'32:17','پھر جیسا کچھ آنکھوں کی ٹھنڈک کا سامان ان کے اعمال کی جزاء میں ان کے لیے چھپا رکھا گیا ہے اس کی کسی بھی متنفس کو خبر نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(32,18,'32:18','بھلا کہیں یہ ہوسکتا ہے کہ جو شخص مومن ہو وہ اُس شخص کی طرح ہو جائے جو فاسق ہو؟ یہ دونوں برابر نہیں ہوسکتے۔ ');
INSERT INTO "ur_maududi" VALUES(32,19,'32:19','جو لوگ ایمان لائے ہیں اور جنہوں نے نیک عمل کیے ہیں اُن کے لیے تو جنتوں کی قیام گاہیں ہیں ، ضیافت کے طور پر اُن کے اعمال کے بدلے میں');
INSERT INTO "ur_maududi" VALUES(32,20,'32:20','اور جنہوں نے فسق اختیار کیا ہے اُن کا ٹھکانا دوزخ ہے جب کبھی وہ اس سے نکلنا چاہیں گے اسی میں دھکیل دیے جائیں گے اور ان سے کہا جائے گا کہ چکھو اب اُسی آگ کے عذاب کا مزا جس کو تم جھٹلایا کرتے تھے');
INSERT INTO "ur_maududi" VALUES(32,21,'32:21','اُس بڑے عذاب سے پہلے ہم اسی دُنیا میں(کسی نہ کسی چھوٹے)عذاب کا مزا اِنہیں چکھا تے رہیں گے، شاید کہ یہ (اپنی باغیانہ روش سے)باز آجائیں۔ ');
INSERT INTO "ur_maududi" VALUES(32,22,'32:22','اور اُس سے بڑا ظالم کون ہوگاجسے اس کے ربّ کی آیات کے ذریعہ سے نصیحت کی جائے اور پھر وہ ان سے منہ پھیر لے۔ ایسے مجرموں سے تو ہم انتقام لیکر رہیں گے');
INSERT INTO "ur_maududi" VALUES(32,23,'32:23','اس سے پہلے ہم موسیٰؑ کو کتاب دے چکے ہیں، لہٰذا اُسی چیز کے ملنے پر تمہیں کوئی شک نہیں ہونا چاہیے۔ اُس کتاب کو ہم نے بنی اسرائیل کےلیے ہدایت بنایا تھا ');
INSERT INTO "ur_maududi" VALUES(32,24,'32:24','اور جب انہوں نے صبر کیا اور ہماری آیات پر یقین لاتے رہے تو ان کے اندر ہم نے ایسے پیشوا پیدا کیے جو ہمارے حکم سے رہنمائی کرتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(32,25,'32:25','یقیناً تیرا ربّ ہی قیامت کے روز اُن باتوں کا فیصلہ کریگا جن میں (بنی اسرائیل) باہم اختلاف کرتے رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(32,26,'32:26','اور کیا ان لوگوں کو (اِن تاریخی واقعات میں)کوئی ہدایت نہیں ملی کہ ان سے پہلے کتنی قوموں کو ہم ہلاک کر چکے ہیں جن کے رہنے کی جگہوں میں آج یہ چلتے پھرتے ہیں؟ اس میں بڑی نشانیاں ہیں، کیا یہ سُنتے نہیں ہیں؟');
INSERT INTO "ur_maududi" VALUES(32,27,'32:27','اور کیا ان لوگوں نے یہ منظر کبھی نہیں دیکھاکہ ہم ایک آب و گیاہ زمین کی طرف پانی بہا لاتے ہیں، اور پھر اسی زمین سے وہ فصل اُگاتے ہیں جس سے ان کے جانوروں کو بھی چارہ ملتا ہے اور یہ خود بھی کھاتے ہیں؟تو کیا انھیں کچھ نہیں سُوجھتا؟ ');
INSERT INTO "ur_maududi" VALUES(32,28,'32:28','یہ لوگ کہتے ہیں کہ ”یہ فیصلہ کب ہوگا اگر تم سچے ہو؟ “');
INSERT INTO "ur_maududi" VALUES(32,29,'32:29','ان سے کہو”فیصلے کے دن ایمان لانا اُن لوگوں کےلیے کچھ بھی نافع نہ ہوگا جنہوں نے کفر کیا ہےاور پھر اُن کو کوئی مہلت نہ ملے گی۔“ ');
INSERT INTO "ur_maududi" VALUES(32,30,'32:30','اچھا، اِنہیں ان کے حال پر چھوڑ دو اور انتظار کرو، یہ بھی منتظر ہیں');
INSERT INTO "ur_maududi" VALUES(33,1,'33:1',' اے نبیؐ! اللہ سے ڈرو اور کفّار اور منافقین کی اطاعت نہ کرو، حقیقت میں علیم اور حکیم تو اللہ ہی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,2,'33:2','پیروی کرو اس بات کی جس کا اشارہ تمہارے ربّ کی طرف سے تمہیں کیا جارہا ہے، اللہ ہر اُس بات سے باخبر ہے جو تم لوگ کرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(33,3,'33:3','اللہ پر توکّل کرو، اور اللہ ہی وکیل ہونے کے لیے کافی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,4,'33:4','اللہ نے کسی شخص کے دھڑ میں دو دل نہیں رکھے ہیں ، نہ اس نے تم لوگوں کی اُن بیویوں کو جن سے تم ظہار کرتے ہو تمہاری ماں بنا دیا ہے ، اور نہ اس نے تمہارے منہ بولے بیٹوں کو تمہار حقیقی بیٹا بنایا ہے۔ یہ تو وہ باتیں ہیں جو تم لوگ اپنے منہ سے نکال دیتے ہو،مگر اللہ وہ بات کہتا ہے جو مبنی بر حقیقت ہے، اور وہی صحیح طریقے کی طرف رہنمائی کرتا ہے');
INSERT INTO "ur_maududi" VALUES(33,5,'33:5','منہ بولے بیٹو ں کو ان کے باپوں کی نسبت سے پکارو،یہ اللہ کے نزدیک زیادہ منصفانہ بات ہے۔ اور اگے تمہیں معلوم نہ ہو کہ ان کے باپ کون ہے تو وہ تمہارے دینی بھائی اور رفیق ہیں۔ نادانستہ جو بات تم کہو اس کے لیے تم پر کوئی گرفت نہیں ہے،لیکن اس بات پر ضرور گرفت ہے جس کا تم دل سے ارادہ کرو۔ اللہ درگزر کرنے والا اور رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,6,'33:6','بلاشبہ نبی تو اہلِ ایمان کےلیے اُن کی اپنی ذات پر مقّدم ہے ، اور نبی کی بیویاں اُن کی مائیں ہیں ، مگر کتاب اللہ کی رو عام مومنین ومہاجرین کی بہ بسبت رشتہ دار ایک دوسرے کے زیادہ حقدار ہیں، البتہ اپنے رفیقوں کے ساتھ تم کوئی بھلائی (کرنا چاہو تو) کرسکتے ہو۔ یہ حکم کتابِ الٰہی میں لکھا ہوا ہے');
INSERT INTO "ur_maududi" VALUES(33,7,'33:7','اور (اے نؐبی)یاد رکھو اُس عہد وپیمان کو جو ہم نے سب پیغمبروں سے لیا ہے، تم سے بھی اور اور نوحؑ اور ابراھیمؑ اور موسیٰؑ اور عیسیٰؑ ابن مریم سے بھی۔ سب سے ہم پختہ عہد لے چکے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(33,8,'33:8','تاکہ سچّے لوگوں سے (ان کا ربّ)ان کی سچّائی کے بارے میں سوال کرے ، اور کافروں کےلیے تو اُس نے درد ناک عذاب مہیّا کر ہی رکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,9,'33:9',' اے لوگو جو ایمان لائے ہو ، یاد کرو اللہ کے احسا ن کو جو (ابھی ابھی) اُس نے تم کیا ہے۔ جب لشکر تم پر چڑھ آئے تو ہم نے ان پر ایک سخت آندھی بھیج دی اور ایسی فوجیں روانہ کیں جو تم کو نظر نہ آتی تھیں۔ اللہ وہ سب کچھ دیکھ رہا تھا جو تم لوگ اُس وقت کررہے تھے');
INSERT INTO "ur_maududi" VALUES(33,10,'33:10','جب وہ اُوپر سے اور نیچے سے تم پر چڑھ آئے۔ جب خوف کے مارے آنکھیں پتھراگئیں، کلیجے مُنہ کو آگئے، اور تم لوگ اللہ کے بارے میں طرح طرح کے گمان کرنے لگے');
INSERT INTO "ur_maududi" VALUES(33,11,'33:11','اُس وقت ایمان لانے والےخوب آزمائے گئے اور بُری طرح ہلا مارے گئے۔ ');
INSERT INTO "ur_maududi" VALUES(33,12,'33:12','یاد کرو وہ وقت جب منافقین اور وہ سب لوگ جن کے دلوں میں روگ تھا صاف صاف کہہ رہے تھے کہ اللہ اور اُس کے رسولؐ نے جو وعدے ہم سے کیے تھے وہ فریب کے سِوا کچھ نہ تھے');
INSERT INTO "ur_maududi" VALUES(33,13,'33:13','جب اُن میں سے ایک گروہ نے کہا کہ ”اے یثرب کے لوگو، تمہارے لیے اب ٹھیرنے کا کوئی موقع نہیں ہے، پلٹ چلو۔ “جب ان کا ایک فریق یہ کہہ کر نبیؐ سے رخصت طلب کررہا تھا کہ ”ہمارے گھر خطرے میں ہیں ،“حالانکہ وہ خطرے میں نہ تھے ، دراصل وہ (محاذِجنگ سے) بھاگنا چاہتے تھے');
INSERT INTO "ur_maududi" VALUES(33,14,'33:14','اگر شہر کے اطراف سے دشمن گھُس آئے ہوتے اور اُس وقت اِنہیں فتنے کی طرف دعوت دی جاتی تو یہ اس میں جا پڑتے اور مشکل ہی سے انہیں شریکِ فتنہ ہونے میں کوئی تامّل ہوتا');
INSERT INTO "ur_maududi" VALUES(33,15,'33:15','ن لوگوں نے اس سے پہلے اللہ سے عہد کیا تھا کہ یہ پیٹھ نہ پھیریں گے، اور اور اللہ سے کیے ہوئے عہد کی باز پُرس تو ہونی ہی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(33,16,'33:16','اے نبیؐ ! ان سے کہو، اگر تم موت یا قتل سے بھاگو تو یہ بھاگنا تمہارے لیے کچھ بھی نفع بخش نہ ہوگا۔ اس کے بعد زندگی کے مزے لُوٹنے کا تھوڑا ہی موقع مل سکے گا۔ ');
INSERT INTO "ur_maududi" VALUES(33,17,'33:17','اِن سے کہو، کون ہے جو تمہیں اللہ سے بچا سکتا ہو اگر وہ تمہیں نقصان پہنچانا چاہے؟ اور کون اس کی رحمت کو روک سکتا ہے اگر وہ تم پر مہربانی کرنا چاہے؟ اللہ کے مقابلے میں تو یہ لوگ کوئی حامی و مدد گار نہیں پا سکتے ہیں');
INSERT INTO "ur_maududi" VALUES(33,18,'33:18','اللہ تم میں سے اُن لوگوں کو خوب جانتا ہے جو (جنگ کے کام میں)رُکاوٹیں ڈالنے والے ہیں،جو اپنے بھائیوں سے کہتے ہیں کہ ”آوٴ ہماری طرف “جو لڑائی میں حصہ لیتے بھی ہیں تو بس نام گنانے کو');
INSERT INTO "ur_maududi" VALUES(33,19,'33:19','جوتمہارا ساتھ دینے میں سخت بخیل ہیں۔ خطرے کا وقت آجائے تو اسطرح دِیدے پھراپھراکر تمہاری طرف دیکھتے ہیں جیسے کسی مرنے والے پر غشی طاری ہورہی ہو، مگر جب خطرہ گزر جاتا ہے تو یہی لوگ فائدوں کے حریص بن کر قینچی کی طرح چلتی ہوئی زبانیں لیے تمہارے استقبال کو آجاتے ہیں۔ یہ لوگ ہرگز ایمان نہیں لائے ،اسی لیے اللہ نے ان کے سارے اعمال ضائع کردیے۔ اور ایسا کرنا اللہ کےلیے بہت آسان ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,20,'33:20','یہ سمجھ رہے ہیں کہ حملہ آور گروہ ابھی گئے نہیں ہیں اور اگر وہ پھر حملہ آور ہو جائیں تو ان کا جی چاہتا ہے کہ اُس موقع پر یہ کہیں صحرا میں بدوؤں کے درمیان جا بیٹھیں اور وہیں سے تمہارے حالات پوچھتے رہیں تاہم اگر یہ تمہارے درمیان رہے بھی تو لڑائی میں کم ہی حصہ لیں گے');
INSERT INTO "ur_maududi" VALUES(33,21,'33:21','درحقیقت تم لوگوں کے لیے اللہ کے رسولؐ میں ایک بہترین نمونہ تھا ، ہر اُس شخص کے لیے جو اللہ اور یومِ آخر کا اُمید وار ہو اور کثرت سے اللہ کو یاد کرے۔ ');
INSERT INTO "ur_maududi" VALUES(33,22,'33:22','اور سچّے مومنوں (کا حال اُس وقت یہ تھا کہ )جب تم نے حملہ آور لشکروں کو دیکھا تو پُکار اٹھے کہ”یہ وہی چیز ہے جس کا اللہ اور اُس کے رسولؐ نے ہم سے وعدہ کیا تھا، اللہ اور اُس کے رسولؐ کی بات بالکل سچّی تھی ۔“اِس واقعہ نے اُن کے ایمان اور ان کی سپُردگی کو اور زیادہ بڑھا دیا۔ ');
INSERT INTO "ur_maududi" VALUES(33,23,'33:23','ایمان لانے والوں میں ایسے لوگ موجود ہیں جنہوں نے اللہ سے کیے ہوئے عہد کو سچّا کر دکھایا ہے۔ ان میں سے کوئی اپنی نذر پوری کر چکا اور کوئی وقت آنے کا منتظر ہے۔ انہوں نے اپنے رویےمیں کوئی تبدیلی نہیں کی');
INSERT INTO "ur_maududi" VALUES(33,24,'33:24','(یہ سب کچھ اس لیے ہوا) تاکہ اللہ سچوں کو اُن کی سچائی کی جزا دے اور منافقوں کو چاہے تو سزا دے اور چاہے تو ان کی توبہ قبول کر لے، بے شک اللہ غفور و رحیم ہے');
INSERT INTO "ur_maududi" VALUES(33,25,'33:25','اللہ نے کفار کا منہ پھیر دیا، وہ کوئی فائدہ حاصل کیے بغیر اپنے دل کی جلن لیے یونہی پلٹ گئے، اور مومنین کی طرف سے اللہ ہی لڑنے کے لیے کافی ہو گیا، اللہ بڑی قوت والا اور زبردست ہے');
INSERT INTO "ur_maududi" VALUES(33,26,'33:26','پھر اہلِ کتاب میں سے جن لوگوں نے ان حملہ آوروں کا ساتھ دیا تھا ، اللہ اُن کی گڑھیوں سے انہیں اُتار لایا اور ان کے دلوں میں اُس نے ایسا رُعب ڈال دیا کہ آج ان میں سے ایک گروہ کو تم قتل کر رہے ہو اور دوسرے گروہ کو قید کررہے ہو');
INSERT INTO "ur_maududi" VALUES(33,27,'33:27','اس نے تم کو ان کی زمین اور ان کے گھروں اور ان کے اموال کا وارث بنا دیا اور وہ علاقہ تمہیں دیا جسے تم نے کبھی پامال نہ کیا تھا اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_maududi" VALUES(33,28,'33:28',' اے نبیؐ! اپنی بیویوں سے کہو ، اگر تم دنیا اور اس کی زینت چاہتی ہو تو آؤ، میں تمہیں کچھ دے دلا کر بھلے طریقے سے رخصت کر دوں');
INSERT INTO "ur_maududi" VALUES(33,29,'33:29','اور اگر تم اللہ اور اس کے رسولؐ اور دارِ آخرت کی طالب ہو تو جان لو کہ تم میں سے جو نیکوکار ہیں اللہ نے ان کے لیے بڑا اجر مہیا کر رکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,30,'33:30','نبیؐ کی بیویو، تم میں سے جو کسی صریح فحش حرکت کا ارتکاب کرے گی اسے دوہرا عذاب دیا جائے گا ، اللہ کے لیے یہ بہت آسان کام ہے ۔');
INSERT INTO "ur_maududi" VALUES(33,31,'33:31','اور تم میں سے جو اللہ اور اُس کے رسولؐ کی اطاعت کرے گی اور نیک عمل کرے گی اس کو ہم دوہرا اجر دیں گے اور ہم نے اس کے لیے رزقِ کریم مہیا کر رکھا ہے');
INSERT INTO "ur_maududi" VALUES(33,32,'33:32','نبیؐ کی بیویو، تم عام عورتوں کی طرح نہیں ہو۔ اگر تم اللہ سے ڈرنے والی ہو تو دبی زبان سے بات نہ کیا کرو کہ دل کی خرابی کا مُبتلا کوئی شخص لالچ میں پڑ جائے ، بلکہ صاف سیدھی بات کرو ‘‘');
INSERT INTO "ur_maududi" VALUES(33,33,'33:33','اپنے گھروں میں ٹِک کر رہو۔ اور سابق دَورِ جاہلیت کی سی سج دھج نہ دکھاتی پھرو۔ نماز قائم کرو، زکوٰۃ دو اور اللہ اور اُس کے رسولؐ کی اطاعت کرو۔ اللہ تو یہ چاہتا ہے کہ اہل بیت نبیؐ سے گندگی کو دور کرے اور تمہیں پوری طرح پاک کر دے ');
INSERT INTO "ur_maududi" VALUES(33,34,'33:34','یاد رکھو اللہ کی آیات اور حکمت کی باتوں کو جو تمہارے گھروں میں سنائی جاتی ہیں۔ بے شک اللہ لطیف اور باخبر ہے');
INSERT INTO "ur_maududi" VALUES(33,35,'33:35','بالیقین جو مرد اور جو عورتیں مسلم ہیں ، مومن ہیں ، مطیع فرمان ہیں ، راست باز ہیں ، صابر ہیں ، اللہ کے آگے جھکنے والے ہیں صدقہ دینے والے ہیں ، روزہ رکھنے والے ہیں ، اپنی شرمگاہوں کی حفاظت کرنے والے ہیں ، اور اللہ کو کثرت سے یاد کرنے والے ہیں ، اور اللہ نے ان کے لیے مغفرت اور بڑا اجر مہیا کر رکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,36,'33:36',' کسی مومن مرد اور کسی مومن عورت کو یہ حق نہیں ہے کہ جب اللہ اور اس کا رسولؐ کسی معاملے کا فیصلہ کر دے تو پھر اسے اپنے اس معاملے میں خود فیصلہ کرنے کا اختیار حاصل رہے۔ اور جو کوئی اللہ اور اس کے رسولؐ کی نافرمانی کرے تو وہ صریح گمراہی میں پڑ گیا۔ ');
INSERT INTO "ur_maududi" VALUES(33,37,'33:37',' اے نبیؐ ، یاد کرو وہ موقع جب تم اس شخص سے کہہ رہے تھے جس پر اللہ نے اور تم نے احسان کیا تھا کہ ’’اپنی بیوی کو نہ چھوڑ اور اللہ سے ڈر ‘‘۔ اُس وقت تم اپنے دل میں وہ بات چھپائے ہوئے تھے جسے اللہ کھولنا چاہتا تھا، تم لوگوں سے ڈر رہے تھے ، حالانکہ اللہ اس کا زیادہ حقدار ہے کہ تم اس سے ڈرو پھر جب زیدؓ اس سے اپنی حاجت پوری کر چکا۔ تو ہم نے اس (مطلقہ خاتون) کا تم سے نکاح کر دیا تاکہ مومنوں پر اپنے منہ بولے بیٹوں کی بیویوں کے معاملہ میں کوئی تنگی نہ رہے جبکہ وہ ان سے اپنی حاجت پوری کر چکے ہوں اور اللہ کا حکم تو عمل میں آنا ہی چاہئے تھا');
INSERT INTO "ur_maududi" VALUES(33,38,'33:38','نبی پر کسی ایسے کام میں کوئی رکاوٹ نہیں ہے جو اللہ نے اس کے لیے مقرر کر دیا ہو یہی اللہ کی سنت ان سب انبیاء کے معاملہ میں رہی ہے جو پہلے گزر چکے ہیں اور اللہ کا حکم ایک قطعی طے شدہ فیصلہ ہوتا ہے ');
INSERT INTO "ur_maududi" VALUES(33,39,'33:39','(یہ اللہ کی سنت ہے ان لوگوں کے لیے) جو اللہ کے پیغامات پہنچاتے ہیں اور اُسی سے ڈرتے ہیں اور ایک خدا کے سوا کسی سے نہیں ڈرتے ، اور محاسبہ کے لیے بس اللہ ہی کافی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,40,'33:40','(لوگو) محمدؐ تمہارے مَردوں میں سے کسی کے باپ نہیں ہیں ، مگر وہ اللہ کے رسول اور خاتم النبین ہیں ، اور اللہ ہر چیز کا علم رکھنے والا ہے ');
INSERT INTO "ur_maududi" VALUES(33,41,'33:41','اے لوگو! جو ایمان لائے ہو، اللہ کو کثرت سے یاد کرو');
INSERT INTO "ur_maududi" VALUES(33,42,'33:42','اور صبح و شام اس کی تسبیح کرتے رہو ');
INSERT INTO "ur_maududi" VALUES(33,43,'33:43','وہی ہے جو تم پر رحمت فرماتا ہے اور اس کے ملائکہ تمہارے لے دُعائے رحمت کرتے ہیں تاکہ وہ تمہیں تاریکیوں سے روشنی میں نکال لائے ، وہ مومنوں پر بہت مہربان ہے ');
INSERT INTO "ur_maududi" VALUES(33,44,'33:44','جس روز وہ اس سے ملیں گے اُن کا استقبال سلام سے ہوگا اور اُن کے لیے اللہ نے بڑا با عزت اجر فراہم کر رکھا ہے');
INSERT INTO "ur_maududi" VALUES(33,45,'33:45','اے نبیؐ ،ہم نے تمہیں بھیجا ہے گواہ بنا کر ، بشارت دینے والا اور ڈرانے والا بنا کر ');
INSERT INTO "ur_maududi" VALUES(33,46,'33:46','اللہ کی اجازت سے اس کی طرف دعوت دینے والا بنا کر اور روشن چراغ بناکر');
INSERT INTO "ur_maududi" VALUES(33,47,'33:47','بشارت دے دو اُن لوگوں کو جو (تم پر) ایمان لائے ہیں کہ ان کے لیے اللہ کی طرف سے بڑا فضل ہے');
INSERT INTO "ur_maududi" VALUES(33,48,'33:48','اور ہرگز نہ دبو کفار و منافقین سے، کوئی پرواہ نہ کرو ان کی اذیت رسانی کی اور بھروسہ کر لو اللہ پر، اللہ ہی اس کے لیے کافی ہے کہ آدمی اپنے معاملات اُس کے سپرد کر دے');
INSERT INTO "ur_maududi" VALUES(33,49,'33:49','اَے لوگوں جو ایمان لائے ہو، جب تم مومن عورتوں سے نکاح کرو اور پھر انہیں ہاتھ لگانے سے پہلے طلاق دے دو تو تمہاری طرف سے ان پر کوئی عدت لازم نہیں ہے جس کے پورے ہونے کا تم مطالبہ کر سکو۔ لہذا انہیں کچھ مال دو اور بھلے طریقے سے رخصت کر دو۔ ');
INSERT INTO "ur_maududi" VALUES(33,50,'33:50','اے نبیؐ، ہم نے تمہارے لیے حلال کر دیں تمہاری وہ بیویاں جن کے مہر تم نے ادا کیے ہیں ، اور وہ عورتیں جو اللہ کی عطا کردہ لونڈیوں میں سے تمہاری ملکیت میں آئیں ، اور تمہاری وہ چچازاد اور پھوپھی زاد اور ماموں زاد اور خالہ زاد بہنیں جنہوں نے تمہارے ساتھ ہجرت کی ہے ، اور وہ مومن عورت جس نے اپنے آپ کو نبیؐ کے لیے ہبہ کیا ہو اگر نؐبی اسے نکاح میں لینا چاہے ۔ یہ رعایت خالصتاً تمہارے لیے ہے ، دوسرے مومنوں کے لیے نہیں ہے ہم کو معلوم ہے کہ عام مومنوں پر ان کی بیویوں اور لونڈیوں کے بارے میں ہم نے کیا حدود عائد کیے ہیں۔ (تمہیں ان حدود سے ہم نے اس لیے مستثنیٰ کیا ہے)تاکہ تمہارے اوپر کوئی تنگی نہ رہے ، اور اللہ غفور و رحیم ہے');
INSERT INTO "ur_maududi" VALUES(33,51,'33:51','تم کو اختیار دیا جاتا ہے کہ اپنی بیویوں میں سے جس کو چاہو اپنے سے الگ رکھو، جسے چاہو اپنے ساتھ رکھو اور جسے چاہو الگ رکھنے کے بعد اپنے پاس بلا لو۔ اس معاملے میں تم پر کوئی مضائقہ نہیں ہے۔ اس طرح زیادہ متوقع ہے کہ ان کی آنکھیں ٹھنڈی رہیں گی اور وہ رنجیدہ نہ ہوں گی، اور جو کچھ بھی تم ان کو دو گے اس پر وہ سب راضی رہیں گی اللہ جانتا ہے جو کچھ تم لوگوں کے دلوں میں ہے ، اور اللہ علیم و حلیم ہے ');
INSERT INTO "ur_maududi" VALUES(33,52,'33:52','اس کے بعد تمہارے لیے دوسری عورتیں حلال نہیں ہیں ، اور نہ اس کی اجازت ہے کہ ان کی جگہ اور بیویوں لے آؤ خواہ ان کا حسن تمہیں کتنا ہی پسند ہو ، البتہ لونڈیوں کی تمہیں اجازت ہے اللہ ہر چیز پر نگران ہے');
INSERT INTO "ur_maududi" VALUES(33,53,'33:53','اے لوگو جو ایمان لائے ہو، نبیؐ کے گھروں میں بلا اجازت نہ چلے آیا کرو نہ کھانے کا وقت تاکتے رہو۔ ہاں اگر تمہیں کھانے پر بلایا جائے تو ضرور آؤ مگر جب کھانا کھالو تو منتشر ہو جاؤ۔ باتیں کرنے میں نہ لگے رہو تمہاری یہ حرکتیں نبی کو تکلیف دیتی ہیں ، مگر وہ شرم کی وجہ سے کچھ نہیں کہتے۔اور اللہ حق بات کہنے میں نہیں شرماتا۔ نبیؐ کی بیویوں سے اگر تمہیں کچھ مانگنا ہو تو پردے کے پیچھے سے مانگو کرو، یہ تمہارے اور ان کے دلوں کی پاکیزگی کے لیے زیادہ مناسب طریقہ ہے تمہارے لیے یہ ہرگز جائز نہیں کہ اللہ کے رسولؐ کو تکلیف دو ، اور نہ یہ جائز ہے کہ ان کے بعد ان کی بیویوں سے نکاح کرو ، یہ اللہ کے نزدیک بہت بڑا گناہ ہے');
INSERT INTO "ur_maududi" VALUES(33,54,'33:54','تم خواہ کوئی بات ظاہر کرو یا چھپاؤ، اللہ کو ہر بات کا علم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,55,'33:55','ازواجِ نبیؐ کے لیے اس میں کوئی مضائقہ نہیں ہے کہ ان کے باپ، ان کے بیٹے ، ان کے بھائی ، ان کے بھتیجے ، ان کے بھانجے ، ان کے میل جول کی عورتیں اور ان کے مملوک گھروں میں آئیں۔ (اے عورتو!) تمہیں اللہ کی نافرمانی سے پرہیز کرنا چاہئے۔ اللہ ہر چیز پر نگاہ رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,56,'33:56','اللہ اور اس کے ملائکہ نبیؐ پر درود بھیجتے ہیں ، اے لوگو جو ایمان لائے ہو تم بھی ان پر درود و سلام بھیجو ');
INSERT INTO "ur_maududi" VALUES(33,57,'33:57','جو لوگ اللہ اور اس کے رسولؐ کو اذیت دیتے ہیں ان پر دنیا اور آخرت میں اللہ نے لعنت فرمائی ہے اور ان کے لیے رسوا کن عذاب مہیا کر دیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(33,58,'33:58','ور جو لوگ مومن مردوں اور عورتوں کو بے قصور اذیت دیتے ہیں اُنہوں نے ایک بڑے بہتان اور صریح گناہ کا وبال اپنے سر لے لیا ہے');
INSERT INTO "ur_maududi" VALUES(33,59,'33:59','اے نبیؐ! اپنی بیویوں اور بیٹیوں اور اہلِ ایمان کی عورتوں سے کہہ دو کہ اپنے اوپر اپنی چادروں کے پلو لٹکالیا کریں یہ زیادہ مناسب طریقہ ہے تاکہ وہ پہچان لی جائیں اور نہ ستائی جائیں اللہ تعالیٰ غفور و رحیم ہے ');
INSERT INTO "ur_maududi" VALUES(33,60,'33:60','اگر منافقین ، اور وہ لوگ جن کے دلوں میں خرابی ہے ، اور وہ جو مدینہ میں ہیجان انگیز افواہیں پھیلانے والے ہیں ، اپنی حرکتوں سے باز نہ آئے تو ہم ان کے خلاف کارروائی کرنے کے لیے تمہیں اُٹھا کھڑا کریں گے ، پھر وہ اس شہر میں مشکل ہی سے تمہارے ساتھ رہ سکیں گے');
INSERT INTO "ur_maududi" VALUES(33,61,'33:61','ان پر ہر طرف سے لعنت کی بوچھاڑ ہو گی، جہاں کہیں پائے جائیں گے پکڑے جائیں گے اور بُری طرح مارے جائیں گے');
INSERT INTO "ur_maududi" VALUES(33,62,'33:62','یہ اللہ کی سنت ہے جو ایسے لوگوں کے معاملے میں پہلے سے چلی آ رہی ہے ، اور تم اللہ کی سنت میں کوئی تبدیلی نہ پاؤ گے۔ ');
INSERT INTO "ur_maududi" VALUES(33,63,'33:63','لوگ تم سے پوچھتے ہیں کہ قیامت کی گھڑی کب آئے گی کہو، اس کا علم تو للہ ہی کو ہے۔ تمہیں کیا خبر، شاید کہ وہ قریب ہی آ لگی ہو');
INSERT INTO "ur_maududi" VALUES(33,64,'33:64','بہرحال یہ یقینی امر ہے کہ اللہ نے کافروں پر لعنت کی ہے اور ان کے لیے بھڑکتی ہوئی آگ مہیا کر دی ہے');
INSERT INTO "ur_maududi" VALUES(33,65,'33:65','جس میں وہ ہمیشہ رہیں گے، کوئی حامی و مدد گار نہ پا سکیں گے');
INSERT INTO "ur_maududi" VALUES(33,66,'33:66','جس روز ان کے چہرے آگ پر الٹ پلٹ کیے جائیں گے اُس وقت وہ کہیں گے کہ "کاش ہم نے اللہ اور رسولؐ کی اطاعت کی ہوتی"');
INSERT INTO "ur_maududi" VALUES(33,67,'33:67','اور کہیں گے "اے رب ہمارے، ہم نے اپنے سرداروں اور اپنے بڑوں کی اطاعت کی اور انہوں نے ہمیں راہِ راست سے بے راہ کر دیا');
INSERT INTO "ur_maududi" VALUES(33,68,'33:68','اے رب، ان کو دوہرا عذاب دے اور ان پر سخت لعنت کر‘‘ ');
INSERT INTO "ur_maududi" VALUES(33,69,'33:69','اے لوگو جو ایمان لائے ہو ، ان لوگوں کی طرح نہ بن جاؤ جنہوں نے موسیٰؑ کو اذیتیں دی تھیں ، پھر اللہ نے ان کی بنائی ہوئی باتوں سے اس کی برأت فرمائی اور وہ اللہ کے نزدیک با عزت تھا ');
INSERT INTO "ur_maududi" VALUES(33,70,'33:70','اے ایمان لانے والو، اللہ سے ڈرو اور ٹھیک بات کیا کرو');
INSERT INTO "ur_maududi" VALUES(33,71,'33:71','اللہ تمہارے اعمال درست کر دے گا اور تمہارے قصوروں سے درگزر فرمائے گا جو شخص اللہ اور اس کے رسولؐ کی اطاعت کرے اُس نے بڑی کامیابی حاصل کی');
INSERT INTO "ur_maududi" VALUES(33,72,'33:72','ہم نے اس امانت کو آسمانوں اور زمین اور پہاڑوں کے سامنے پیش کیا تو وہ اسے اٹھانے کے لیے تیار نہ ہوئے اور اس سے ڈر گئے ، مگر انسان نے اسے اٹھا لیا، بے شک وہ بڑا ظالم اور جاہل ہے ');
INSERT INTO "ur_maududi" VALUES(33,73,'33:73','اِس بار امانت کو اٹھانے کا لازمی نتیجہ یہ ہے کہ اللہ منافق مردوں اور عورتوں، اور مشرک مردوں اور عورتوں کو سزا دے اور مومن مَردوں اور عورتوں کی توبہ قبول کرے، اللہ درگزر فرمانے والا اور رحیم ہے');
INSERT INTO "ur_maududi" VALUES(34,1,'34:1','حمد اُس خدا کے لیے ہے جو آسمانوں اور زمین کی ہر چیز کا مالک ہے اور آخرت میں بھی اسی کے لیے حمد ہے ۔ وہ دانا اور باخبر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(34,2,'34:2','جو کچھ زمین میں جاتا ہے اور جو کچھ اُس سے نکلتا ہے اور جو کچھ آسمان سے اُتر تا ہےاور جو کچھ اُس میں چڑھتا ہے، ہر چیز کو وہ جانتا ہے، وہ رحیم اور غفور ہے۔ ');
INSERT INTO "ur_maududi" VALUES(34,3,'34:3','منکرین کہتے ہیں کیا بات ہے کہ قیامت ہم پر نہیں آرہی ہے ! کہو، قسم ہے میرے عالم الغیب پروردگار کی، وہ تم پر آکر رہے گی۔ اُس سے ذرّہ برابر کوئی چیز نہ آسمانوں میں چھُپی ہوئی ہے نہ زمین میں۔ نہ ذرّے سے بڑی اور نہ اُس سے چھوٹی، سب کچھ ایک نمایاں دفتر میں درج ہے۔ ');
INSERT INTO "ur_maududi" VALUES(34,4,'34:4','اور یہ قیامت اس لئے آئے گی کہ جزا دے اللہ اُن لوگوں کو جو ایمان لائے ہیں اور نیک عمل کرتے رہے ہیں اُن کے لیے مغفرت ہے اور رزق کریم');
INSERT INTO "ur_maududi" VALUES(34,5,'34:5','اور جن لوگوں نے ہماری آیات کو نیچا دکھانے کے لیے زور لگایا ہے، ان کے لیے بدترین قسم کا دردناک عذاب ہے۔ ');
INSERT INTO "ur_maududi" VALUES(34,6,'34:6','اے نبیؐ ، علم رکھنے والے خوب جانتے ہیں کہ جو کچھ تمہارے ربّ کی طرف سے تم پر نازل کیا گیا ہے وہ سراسر حق ہے اور خدائے عزیز و حمید کا راستہ دکھاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(34,7,'34:7','منکرین لوگوں سے کہتے ہیں "ہم بتائیں تمہیں ایسا شخص جو خبر دیتا ہے کہ جب تمہارے جسم کا ذرہ ذرہ منتشر ہو چکا ہو گا اس وقت تم نئے سرے سے پیدا کر دیے جاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(34,8,'34:8','نہ معلوم یہ شخص اللہ کے نام سے جھُوٹ گھڑتا ہے یا اسے جنُون لاحق ہے۔“ نہیں، بلکہ جو لوگ آخرت کو نہیں مانتے وہ عذاب میں مبتلا ہونے والے ہیں اور وہی بُری طرح بہکے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(34,9,'34:9','کیا اِنہوں نے کبھی اُس آسمان و زمین کو نہیں دیکھا جو اِنہیں آگے اور پیچھے سے گھیرے ہوئے ہے؟ ہم چاہیں تو اِنہیں زمین میں دَھسا دیں، یا آسمان کے کچھ ٹکڑے اِن پر گِرا دیں۔ در حقیقت اس میں ایک نشانی ہے ہر اُس بندے کے لیے جو خدا کی طرف رجوع کرنے والا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(34,10,'34:10','ہم نے داوٴدؑ کو اپنے ہاں سے بڑا فضل عطا کیا تھا۔ (ہم نے حکم دیا کہ)اے پہاڑو، اس کے ساتھ ہم آہنگی کرو(اور یہی حکم ہم نے)پرندوں کو دیا۔ ہم نے لوہے کو اس کے لیے نرم کر دیا');
INSERT INTO "ur_maududi" VALUES(34,11,'34:11','اس ہدایت کے ساتھ کہ زر ہیں بنا اور ان کے حلقے ٹھیک اندازے پر رکھ۔ (اے آل ِ داوٴدؑ)نیک عمل کرو، جو کچھ تم کرتے ہو اُس کو میں دیکھ رہا ہوں');
INSERT INTO "ur_maududi" VALUES(34,12,'34:12','اور سلیمانؑ کے لیے ہم نے ہوا کو مسخّر کر دیا ، صبح کے وقت اس کا چلنا ایک مہینے کی راہ تک اور شام کے وقت اس کا چلنا ایک مہینے کی راہ تک۔ ہم نے اُس کے لیے پگھلے ہوئے تانبے کا چشمہ بہا دیا اور ایسے جِن اس کے تابع کر دیے جو اپنے ربّ کے حکم سے اس کے آگے کام کرتے تھے۔ اُن میں سے جو ہمارے حکم سے سرتابی کرتا اس کو ہم بھڑکتی ہوئی آگ کا مزہ چکھاتے');
INSERT INTO "ur_maududi" VALUES(34,13,'34:13','وہ اُس کے لیے بناتے تھے جو کچھ وہ چاہتا، اُونچی عمارتیں، تصویریں ، بڑے بڑے حوض جیسے لگن اور اپنی جگہ سے نہ ہٹنے والی بھاری دیگیں ۔۔۔۔ اے آلِ داوٴدؑ ، عمل کرو شکر کے طریقے پر ، میرے بندوں میں کم ہی شکر گزار ہیں');
INSERT INTO "ur_maududi" VALUES(34,14,'34:14','پھر جب سلیمانؑ پر ہم نے موت کا فیصلہ نافذ کیا تو جِنّوں کو اس کی موت کا پتہ دینے والی کوئی چیز اُس گھُن کے سوا نہ تھی جو اس کے عصا کو کھا رہا تھا۔ اس طرح جب سلیمانؑ گر پڑا تو جِنّوں پر یہ بات کھُل گئی کہ اگر وہ غیب کے جاننے والے ہوتے تو اس ذلّت کے عذاب میں مبتلا نہ رہتے۔ ');
INSERT INTO "ur_maududi" VALUES(34,15,'34:15',' سبا کے لیے اُن کے اپنے مسکن ہی میں ایک نشانی موجود تھی ، دو باغ دائیں اور بائیں ۔ کھاوٴ اپنے ربّ کا دیا ہوا رزق اور شکر بجا لاوٴ اُس کا، ملک ہے عمدہ و پاکیزہ اور پروردگار ہے بخشش فرمانے والا');
INSERT INTO "ur_maududi" VALUES(34,16,'34:16','مگر وہ منہ موڑ گئے۔ آخر کار ہم نے اُن پر بند توڑ سیلاب بھیج دیا اور ان کے پچھلے دو باغوں کی جگہ دو اور باغ انہیں دیے جن میں کڑوے کسیلے پھل اور جھاوٴ کے درخت تھے اور کچھ تھوڑی سی بیریاں ');
INSERT INTO "ur_maududi" VALUES(34,17,'34:17','یہ تھا ان کے کفر کا بدلہ جو ہم نے ان کو دیا، اور ناشکرے انسان کے سوا ایسا بدلہ ہم اور کسی کو نہیں دیتے');
INSERT INTO "ur_maududi" VALUES(34,18,'34:18','اور ہم نے اُن کے اور اُن بستیوں کے درمیان ، جن کو ہم نے برکت عطا کی تھی ، نمایاں بستیاں بسا دی تھیں اور اُن میں سفر کی مسافتیں ایک اندازے پر رکھ دی تھیں۔ چلو پھرو اِن راستوں میں رات دن پورے امن کے ساتھ');
INSERT INTO "ur_maududi" VALUES(34,19,'34:19','مگر انہوں نے کہا”اے ہمارے ربّ، ہمارے سفر کی مسافتیں لمبی کردے۔ “ انہوں نے اپنے اوپر آپ ظلم کیا۔ آخر کار ہم نے انہیں افسانہ بنا کر رکھ دیا اور انہیں بالکل تِتّر بِتّر کر ڈالا ۔ یقیناً اس میں نشانیاں ہیں ہر اُس شخص کے لیے جو بڑا صابر و شاکر ہو ');
INSERT INTO "ur_maududi" VALUES(34,20,'34:20','اُن کے معاملہ میں ابلیس نے اپنا گمان صحیح پایا او ر انہوں نے اُسی کی پیروی کی ، بجُز ایک تھوڑے سے گروہ کے جو مومن تھا ');
INSERT INTO "ur_maududi" VALUES(34,21,'34:21','ابلیس کو اُن پر کوئی اقتدار حاصل نہ تھا مگر جو کچھ ہوا وہ اس لیے ہوا کہ ہم یہ دیکھنا چاہتے تھے کہ کون آخرت کا ماننے والا ہے اور کون اس کی طرف سے شک میں پڑا ہوا ہے ۔ تیرا ربّ ہر چیز پر نگران ہے۔ ');
INSERT INTO "ur_maududi" VALUES(34,22,'34:22',' (اے نبیؐ ، اِن مشرکین سے)کہو کہ پکار دیکھو اپنے اُن معبُودوں کو جنہیں تم اللہ کے سوا اپنا معبُود سمجھے بیٹھے ہو ۔ وہ نہ آسمانوں میں کسی ذرّہ برابر چیز کے مالک ہیں نہ زمین میں۔ وہ آسمان و زمین کی ملکیّت میں شریک بھی نہیں ہیں۔ ان میں سے کوئی اللہ کا مددگار بھی نہیں ہے');
INSERT INTO "ur_maududi" VALUES(34,23,'34:23','اور اللہ کے حضور کوئی شفاعت بھی کسی کے لیے نافع نہیں ہو سکتی بجُز اُس شخص کے جس کے لیے اللہ نے سفارش کی اجازت دی ہو ۔ حتٰی کہ جب لوگوں کے دلوں سے گھبراہٹ دُور ہوگی تو وہ (سفارِش کرنے والوں سے)پوچھیں گے کہ تمہارے ربّ نے کیا جواب دیا۔ وہ کہیں گے کہ ٹھیک جواب ملا ہے اور وہ بزرگ و برتر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(34,24,'34:24','(اے نبیؐ)اِن سے پوچھو”کون تم کو آسمانوں اور زمین سے رزق دیتا ہے؟“کہو”اللہ ۔ اب لامحالہ ہم میں اور تم میں سے کوئی ایک ہی ہدایت پر ہے یا کھُلی گمراہی میں پڑا ہوا ہے۔ “');
INSERT INTO "ur_maududi" VALUES(34,25,'34:25','ان سے کہو”جو قصُور ہم نے کیا ہواس کی کوئی باز پرس تم سے نہ ہوگی اور جو کچھ تم کر رہے ہو اس کی کوئی جواب طلبی ہم سے نہیں کی جائے گی ۔“');
INSERT INTO "ur_maududi" VALUES(34,26,'34:26','کہو”ہمارا ربّ ہم کو جمع کرے گا، پھر ہمارے درمیان ٹھیک ٹھیک فیصلہ کر دے گا۔ وہ ایسا زبردست حاکم ہے جو سب کچھ جانتا ہے۔ “');
INSERT INTO "ur_maududi" VALUES(34,27,'34:27','اِن سے کہو”ذرامجھے دکھاوٴ تو سہی وہ کون ہستیاں ہیں جنہیں تم نے اس کے ساتھ شریک لگا رکھا ہے۔ “ ہر گز نہیں، زبردست اور دانا تو بس وہ اللہ ہی ہے');
INSERT INTO "ur_maududi" VALUES(34,28,'34:28','اور (اے نبیؐ)ہم نے تم کو تمام ہی انسانوں کے لیے بشیر و نذیر بنا کر بھیجا ہے ، مگر اکثر لوگ جانتے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(34,29,'34:29','یہ لوگ تم سے کہتے ہیں کہ (قیامت کا)وعدہ کب پورا ہوگا اگر تم سچے ہو ؟');
INSERT INTO "ur_maududi" VALUES(34,30,'34:30','کہو تمہارے لیے ایک ایسے دن کی میعاد مقرر ہے جس کے آنے میں نہ ایک گھڑی بھر کی تاخیر تم کر سکتے ہو اور نہ ایک گھڑی بھر پہلے اُسے لاسکتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(34,31,'34:31','یہ کافر کہتے ہیں کہ ”ہم ہرگز اِس قرآن کو نہ مانیں گے اور نہ اس سے پہلے آئی ہوئی کسی کتاب کو تسلیم کریں گے ۔“ کاش تم دیکھو اِن کا حال اُس وقت جب یہ ظالم اپنے ربّ کے حضُور کھڑے ہوں گے۔ اُس وقت یہ ایک دُوسرے پر الزام دھریں گے۔ جو لوگ دنیا میں دبا کر رکھے گئے تھے وہ بڑے بننے والوں سے کہیں گے کہ ”اگر تم نہ ہوتے تو ہم مومن ہوتے ۔“');
INSERT INTO "ur_maududi" VALUES(34,32,'34:32','وہ بڑے بننے والے ان دبے ہوئے لوگوں کو جواب دیں گے”کیا ہم نے تمہیں اُس ہدایت سے روکا تھا جو تمہارے پاس آئی تھی؟نہیں، بلکہ تم خود مجرم تھے ۔“');
INSERT INTO "ur_maududi" VALUES(34,33,'34:33','وہ دبے ہوئے لوگ ان بڑے بننے والوں سے کہیں گے”نہیں، بلکہ شب و روز کی مکّاری تھی جب تم ہم سے کہتے تھے کہ ہم اللہ سے کُفر کریں اور دُوسروں کی اس کا ہمسر ٹھہرائیں ۔“ آخر کار جب یہ لوگ عذاب دیکھیں گے تو اپنے دلوں میں پچھتائیں گے اور ہم اِن مُنکرین کے گلوں میں طَوق ڈال دیں گے۔ کیا لوگوں کو اِس کے سوا اور کوئی بدلہ دیا جا سکتا ہے کہ جیسے اعمال اُن کے تھے ویسی ہی جزا وہ پائیں؟');
INSERT INTO "ur_maududi" VALUES(34,34,'34:34','کبھی ایسا نہیں ہوا کہ ہم نے کسی بستی میں ایک خبردار کرنے والا بھیجا ہو اور اس بستی کے کھاتے پیتے لوگوں نے یہ نہ کہا ہو جو پیغام تم لے کر آئے ہو اس کو ہم نہیں مانتے ');
INSERT INTO "ur_maududi" VALUES(34,35,'34:35','انہوں نے ہمیشہ یہی کہا کہ ہم تم سے زیادہ مال اولاد رکھتے ہیں اور ہم ہرگز سزا پانے والے نہیں ہیں ');
INSERT INTO "ur_maududi" VALUES(34,36,'34:36','اے نبیؐ ، اِن سے کہو میرا ربّ جسے چاہتا ہے کشادہ رزق دیتا ہے اور جسے چاہتا ہے نَپا تُلا عطا کرتا ہے ، مگر اکثر لوگ اس کی حقیقت نہیں جانتے۔ ');
INSERT INTO "ur_maududi" VALUES(34,37,'34:37','یہ تمہاری دولت اور تمہاری اولاد نہیں ہے جو تمہیں ہم سے قریب کرتی ہو۔ ہاں مگر جو ایمان لائے اور نیک عمل کرے۔ یہی لوگ ہیں جن کے لیے اُن کے عمل کی دُہری جزا ہے ، اور وہ بلند و بالا عمارتوں میں اطمینان سے رہیں گے ');
INSERT INTO "ur_maududi" VALUES(34,38,'34:38','رہے وہ لوگ جو ہماری آیات کو نیچا دکھانے کے لیے دوڑ دھوپ کرتے ہیں، تو وہ عذاب میں مبتلا ہوں گے');
INSERT INTO "ur_maududi" VALUES(34,39,'34:39','اے نبیؐ ، ان سے کہو”میرا ربّ بندوں میں سے جسے چاہتا ہے کُھلا رزق دیتا ہے اور جسے چاہتا ہے نَپا تُلا دیتا ہے۔ جو کچھ تم خرچ کردیتے ہو اُس کی جگہ وہی تم کو اَور دیتا ہے ، وہ سب رازقوں سے بہتر رازق ہے۔ “');
INSERT INTO "ur_maududi" VALUES(34,40,'34:40','اور جس دن وہ تمام انسانوں کو جمع کرے گا پھر فرشتوں سے پوچھے گا”کیا یہ لوگ تمہاری ہی عبادت کیا کرتے تھے ؟“');
INSERT INTO "ur_maududi" VALUES(34,41,'34:41','تو وہ جواب دیں گے”پاک ہے آپ کی ذات، ہمارا تعلق تو آپ سے ہےنہ کہ اِن لوگوں سے ۔ دراصل یہ ہماری نہیں بلکہ جِنّوں کی عبادت کرتے تھے، ان میں سے اکثر انہی پر ایمان لائے ہوئے تھے ۔“');
INSERT INTO "ur_maududi" VALUES(34,42,'34:42','(اُس وقت ہم کہیں گے کہ) آج تم میں سے کوئی نہ کسی کو فائدہ پہنچا سکتا ہے نہ نقصان اور ظالموں سے ہم کہہ دیں گے کہ اب چکھو اس عذاب جہنم کا مزہ جسے تم جھٹلایا کرتے تھے');
INSERT INTO "ur_maududi" VALUES(34,43,'34:43','اِن لوگوں کو جب ہماری صاف صاف آیات سنائی جاتی ہیں تو یہ کہتے ہیں کہ "یہ شخص تو بس یہ چاہتا ہے کہ تم کو اُن معبودوں سے برگشتہ کر دے جن کی عبادت تمہارے باپ دادا کرتے آئے ہیں" اور کہتے ہیں کہ "یہ (قرآن) محض ایک جھوٹ ہے گھڑا ہوا" اِن کافروں کے سامنے جب حق آیا تو انہیں نے کہہ دیا کہ "یہ تو صریح جادو ہے"');
INSERT INTO "ur_maududi" VALUES(34,44,'34:44','حالانکہ نہ ہم نے اِن لوگوں کو پہلے کوئی کتاب دی تھی کہ یہ اسے پڑھتے ہوں اور نہ تم سے پہلے ان کی طرف کوئی متنبّہ کرنے والا بھیجا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(34,45,'34:45','اِن سے پہلے گزرے ہوئے لوگ جھُٹلا چکے ہیں۔ جو کچھ ہم نے اُنہیں دیا تھا اُس کی عُشرِ عَشیر کو بھی یہ نہیں پہنچے ہیں۔ مگر جب انہوں نے میرے رسُولوں کو جھُٹلایا تو دیکھ لو کہ میری سزا کیسی سخت تھی۔ ');
INSERT INTO "ur_maududi" VALUES(34,46,'34:46','اے نبیؐ ، اِن سے کہوکہ”میں تمہیں بس ایک بات کی نصیحت کرتا ہوں، خدا کے لیے تم اکیلے اکیلے اور دو دو مِل کر اپنا دماغ لڑاوٴ اور سوچو، تمہارے صاحب میں آخر ایسی کونسی بات ہے جو جُنون کی ہو ؟ وہ تو ایک سخت عذاب کی آمد سے پہلے تم کو متنبّہ کرنے والا ہے۔ “');
INSERT INTO "ur_maududi" VALUES(34,47,'34:47','اِن سے کہو”اگر میں نے تم سے کوئی اجر مانگا ہے تو وہ تم ہی کو مبارک رہے ۔ میرا اجر تو اللہ کے ذمّہ ہے اور وہ ہر چیز پر گواہ ہے۔ “');
INSERT INTO "ur_maududi" VALUES(34,48,'34:48','اِن سے کہو”میراربّ (مجھ پر)حق کا اِلقا کرتا ہے اور وہ تمام پوشیدہ حقیقتوں کا جاننے والا ہے۔“');
INSERT INTO "ur_maududi" VALUES(34,49,'34:49','کہو "حق آگیا ہے اور اب باطل کے کیے کچھ نہیں ہو سکتا"');
INSERT INTO "ur_maududi" VALUES(34,50,'34:50','کہو”اگر میں گمراہ ہو گیا ہوں تو میری گمراہی کا وبال مجھ پر ہے، اور اگر میں ہدایت پر ہوں تو اُس وحی کی بنا پر ہوں جو میرا ربّ میرے اُوپر نازل کرتا ہے، وہ سب کچھ سُنتا ہے اور قریب ہی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(34,51,'34:51','کاش تم دیکھو انہیں اُس وقت جب یہ لوگ گھبرائے پھر رہے ہوں گے اور کہیں بچ کر نہ جا سکیں گے، بلکہ قریب ہی سے پکڑ لیے جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(34,52,'34:52','اُس وقت یہ کہیں گے کہ ہم اُس پر ایمان لے آئے۔ حالانکہ اب دُور نکلی ہوئی چیز کہاں ہاتھ آسکتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(34,53,'34:53','اِس سے پہلے یہ کُفر کر چکے تھےاور بلا تحقیق دُور دُور کی کوڑیاں لایا کرتے تھے ');
INSERT INTO "ur_maududi" VALUES(34,54,'34:54','اُس وقت جس چیز کی یہ تمنّا کر رہے ہوں گے اس سے محرُوم کر دیے جائیں گے جس طرح اِن کے پیش رَو ہم مشرب محروم ہو ں گے۔ یہ بڑے گمراہ کُن شک میں پڑے ہوئے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(35,1,'35:1','تعریف اللہ ہی کے لیے ہے جو آسمانوں اور زمین کا بنانے والا اور فرشتوں کو پیغام رساں مقرر کرنے والا ہے، (ایسے فرشتے)جن کے دو دو اور تین تین اور چار چار بازو ہیں۔ وہ اپنی مخلوق کی ساخت میں جیسا چاہتا ہے اضافہ کرتا ہے۔ یقیناً اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_maududi" VALUES(35,2,'35:2','اللہ جس رحمت کا دروازہ بھی لوگوں کے لیے کھول دے اسے کوئی روکنے والا نہیں اور جسے وہ بند کر دے اسے اللہ کے بعد پھر کوئی دوسرا کھولنے والا نہیں۔ وہ زبردست اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,3,'35:3','لوگو، تم پر اللہ کے جو احسانات ہیں انہیں یاد رکھو۔ کیا اللہ کے سوا کوئی اور خالق بھی ہے جو تمہیں آسمان اور زمین سے رزق دیتا ہو؟۔۔۔۔ کوئی معبُود اُس کے سوا نہیں، آخر تم کہاں سے دھوکا کھا رہے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(35,4,'35:4','اب اگر (اے نبیؐ)یہ لوگ تمہیں جھُٹلاتے ہیں (تو یہ کوئی نئی بات نہیں)تم سے پہلے بھی بہت سے رسُول جھٹلائے جا چکے ہیں، اور سارے معاملات آخر کار اللہ ہی کی طرف رجوع ہونے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(35,5,'35:5','لوگو، اللہ کا وعدہ یقیناً بر حق ہے ، لہٰذا دنیا کی زندگی تمہیں دھوکے میں نہ ڈالے اور نہ وہ بڑا دھوکے باز تمہیں اللہ کے بارے میں دھوکہ دینے پائے۔ ');
INSERT INTO "ur_maududi" VALUES(35,6,'35:6','در حقیقت شیطان تمہارا دشمن ہے اس لیے تم بھی اسے اپنا دشمن ہی سمجھو وہ تو اپنے پیروؤں کو اپنی راہ پر اس لیے بلا رہا ہے کہ وہ دوزخیوں میں شامل ہو جائیں');
INSERT INTO "ur_maududi" VALUES(35,7,'35:7','جو لوگ کُفر کریں گے اُن کے لیے سخت عذاب ہے اور جو ایمان لائیں گے اور نیک عمل کریں گے اُن کے لیے مغفرت اور بڑا اجر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,8,'35:8',' (بھلا کچھ ٹھکانا ہے اُس شخص کی گمراہی کا)جس کے لیے اُس کا بُرا عمل خوشنما بنا دیا گیا ہو اور وہ اُسے اچھا سمجھ رہا ہو؟ حقیقت یہ ہے کہ اللہ جسے چاہتا ہے گمراہی میں ڈال دیتا ہے اور جسے چاہتا ہے راہِ راست دکھا دیتا ہے۔ پس (اے نبیؐ)خواہ مخواہ تمہاری جان ان لوگوں کی خاطر غم و افسوس میں نہ گھُلے۔ جو کچھ یہ کر رہے ہیں اللہ اُس کو خوب جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,9,'35:9','وہ اللہ ہی تو ہے جو ہواوٴں کو بھیجتا ہے، پھر وہ بادل اُٹھاتی ہیں، پھر ہم اسے ایک اُجاڑ علاقے کی طرف لے جاتے ہیں اور اسی زمین کو جِلا اُٹھاتے ہیں جو مری پڑی تھی۔ مرے ہوئے انسانوں کا جی اُٹھنا بھی اسی طرح ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(35,10,'35:10','جو کوئی عِزت چاہتا ہو اُسے معلوم ہونا چاہیے کہ عزّت ساری کی ساری اللہ کی ہے۔ اُس کے ہاں جو چیز اُوپر چڑھتی ہے وہ صرف پاکیزہ قول ہے ، اور عملِ صالح اس کو اُوپر چڑھاتا ہے۔ رہے وہ لوگ جو بے ہُودہ چالبازیاں کرتے ہیں ، اُن کے لیے سخت عذاب ہے اور اُن کا مکر خود ہی غارت ہونے والا ہے');
INSERT INTO "ur_maududi" VALUES(35,11,'35:11',' اللہ نے تم کو مٹی سے پیدا کیا، پھر نطفہ سے ، پھر تمہارے جوڑے بنا دیے(یعنی مرد اور عورت)۔ کوئی عورت حاملہ نہیں ہوتی اور نہ بچہ جنتی ہے مگر یہ سب کچھ اللہ کے علم میں ہوتا ہے۔ کوئی عمر پانے والا عمر نہیں پاتا اور نہ کسی کی عمر میں کچھ کمی ہوتی ہے مگر یہ سب کچھ ایک کتاب میں لکھا ہوتا ہے۔ اللہ کے لیےیہ بہت آسان کام ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,12,'35:12','ور پانی کے دونوں ذخیرے یکساں نہیں ہیں۔ ایک میٹھا اور پیاس بُجھانے والا ہے، پینے میں خوشگوار ، اور دُوسرا سخت کھاری کہ حلق چھیل دے۔ مگر دونوں سے تم تروتازہ گوشت حاصل کرتے ہو ، پہننے کے لیے زینت کا سامان نکالتے ہو ، اور اسی پانی میں تم دیکھتے ہو کہ کشتیاں اُس کا سینہ چیرتی چلی جارہی ہیں تاکہ تم اللہ کا فضل تلاش کرو اور اُس کے شکر گزار بنو');
INSERT INTO "ur_maududi" VALUES(35,13,'35:13','وہ دن کے اندر رات کو اور رات کے اندر دن کو پِروتا ہوا لے آتا ہے۔ چاند اور سُورج کو اُس نے مسَخّر کر رکھا ہے ۔ یہ سب کچھ ایک وقتِ مقرر تک چلے جارہا ہے۔ وہی اللہ (جس کے یہ سارے کام ہیں)تمہارا ربّ ہے۔ بادشاہی اسی کی ہے۔ اُسے چھوڑ کر جن دُوسروں کو تم پکارتے ہو وہ ایک پرکاہ کے مالک بھی نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(35,14,'35:14','انہیں پکارو تو وہ تمہاری دُعائیں سُن نہیں سکتے اور سُن لیں تو ان کا تمہیں کوئی جواب نہیں دے سکتے۔ اور قیامت کے روز وہ تمہارے شرک کا انکار کر دیں گے۔ حقیقت ِ حال کی ایسی صحیح خبر تمہیں ایک خبر دار کے سوا کوئی نہیں دے سکتا۔ ');
INSERT INTO "ur_maududi" VALUES(35,15,'35:15','لوگو، تم ہی اللہ کے محتاج ہو اور اللہ تو غنی و حمید ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,16,'35:16','وہ چاہے تو تمہیں ہٹا کر کوئی نئی خلقت تمہاری جگہ لے آئے');
INSERT INTO "ur_maududi" VALUES(35,17,'35:17','ایسا کرنا اللہ کے لیے کچھ بھی دُشوار نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(35,18,'35:18','کوئی بوجھ اُٹھانے والا کسی دُوسرے کا بوجھ نہ اُٹھائے گا۔ اور اگر کوئی لدا ہوا نفس اپنا بوجھ اُٹھانے کے لیے پُکارے گا تو اس کے بار ایک ادنیٰ حصّہ بھی بٹا نے کے لیے کوئی نہ آئے گا چاہے وہ قریب ترین رشتہ دار ہی کیوں نہ ہو۔ (اے نبیؐ)تم صرف انہی لوگوں کو متنبّہ کر سکتے ہو جو بے دیکھے اپنے ربّ سے ڈرتے ہیں اور نماز قائم کرتے ہیں۔ جو شخص بھی پاکیزگی اختیار کرتا ہے اپنی ہی بھلائی کے لیے تیار ہے۔ اور پلٹنا سب کو اللہ ہی کی طرف ہے');
INSERT INTO "ur_maududi" VALUES(35,19,'35:19','اندھا اور آنکھوں والا برابر نہیں ہے');
INSERT INTO "ur_maududi" VALUES(35,20,'35:20','نہ تاریکیاں اور روشنی یکساں ہیں');
INSERT INTO "ur_maududi" VALUES(35,21,'35:21','نہ ٹھنڈی چھاؤں اور دھوپ کی تپش ایک جیسی ہے');
INSERT INTO "ur_maududi" VALUES(35,22,'35:22','اور نہ زندہ اور مُردے مساوی ہیں۔ اللہ جسے چاہتا ہے سُنواتا ہے ، مگر (اے نبیؐ)تم اُن لوگوں کو نہیں سُنا سکتے جو قبروں میں مدفون ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(35,23,'35:23','تم تو بس ایک خبر دار کرنے والے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(35,24,'35:24','ہم نے تم کو حق کے ساتھ بھیجا ہے بشارت دینے والا اور ڈرانے والا بنا کر۔ اور کوئی اُمّت ایسی نہیں گزری ہے جس میں کوئی متنبّہ کرنے والا نہ آیا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(35,25,'35:25','اب اگر یہ لوگ تمہیں جھُٹلاتے ہیں تو اِن سے پہلے گزرے ہوئے لوگ بھی جھُٹلا چکے ہیں۔ اُن کے پاس ان کے رسُول کھُلے دلائل اور صحیفے اور روشن ہدایات دینے والی کتاب لے کر آئے تھے');
INSERT INTO "ur_maududi" VALUES(35,26,'35:26','پھر جن لوگوں نے نہ مانا اُن کو میں نے پکڑ لیا اور دیکھ لو کہ میری سزا کیسی سخت تھی');
INSERT INTO "ur_maududi" VALUES(35,27,'35:27','کیا تم دیکھتے نہیں ہو کہ اللہ آسمان سے پانی برساتا ہے اور پھر اس کے ذریعہ سے ہم طرح طرح کے پھل نکال لاتے ہیں جن کے رنگ مختلف ہوتے ہیں پہاڑوں میں بھی سفید، سرخ اور گہری سیاہ دھاریاں پائی جاتی ہیں جن کے رنگ مختلف ہوتے ہیں');
INSERT INTO "ur_maududi" VALUES(35,28,'35:28','اور اسی طرح انسانوں اور جانوروں اور مویشیوں کے رنگ بھی مختلف ہیں۔ حقیقت یہ ہے کہ اللہ کے بندوں میں سے صرف عِلم رکھنے والے لوگ ہی اُس سے ڈرتے ہیں۔ بے شک اللہ زبردست اور درگزر فرمانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,29,'35:29','جو لوگ کتاب اللہ کی تلاوت کرتے ہیں اور نماز قائم کرتے ہیں، اور جو کچھ ہم نے اُنہیں رزق دیا ہے اس میں سے کھلے اور چھپے خرچ کرتے ہیں، یقیناً وہ ایک ایسی تجارت کے متوقع ہیں جس میں ہرگز خسارہ نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(35,30,'35:30','(اس تجارت میں انہوں نے اپنا سب کچھ اِس لیے کھپایا ہے)تاکہ اللہ اُن کے اجر پورے کے پورے اُن کو دے اور مزید اپنے فضل سے ان کو عطا فرمائے۔ بےشک اللہ بخشنے والا اور قدردان ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,31,'35:31','(اے نبیؐ)جو کتاب ہم نے تمہاری طرف وحی کی ذریعہ سے بھیجی ہے وہی حق ہے، تصدیق کرتی ہوئی آئی ہے اُن کتابوں کی جو اِس سے پہلے آئی تھیں۔ بے شک اللہ اپنے بندوں کے حال سے باخبر ہے اور ہر چیز پر نگاہ رکھنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,32,'35:32','پھر ہم نے اس کتاب کا وارث بنا دیا اُن لوگوں کو جنہیں ہم نے (اِس وراثت کے لیے)اپنے بندوں میں سے چُن لیا۔ اب کوئی تو ان میں سے اپنے نفس پر ظلم کرنے والا ہے، اور کوئی بیچ کی راس ہے، اور کوئی اللہ کے اِذن سے نیکیوں میں سبقت کرنے والا ہے، یہی بہت بڑا فضل ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,33,'35:33','ہمیشہ رہنے والی جنّتیں ہیں جن میں یہ لوگ داخل ہوں گے ۔ وہاں انہیں سونے کے کنگنوں اور موتیوں سے آراستہ کیا جائے گا، وہاں ان کا لباس ریشم ہوگا');
INSERT INTO "ur_maududi" VALUES(35,34,'35:34','اور وہ کہیں گے کہ شکر ہے اُس خدا کا جس نے ہم سے غم دُور کر دیا، یقیناً ہمارا ربّ معاف کرنے والا اور قدر فرمانے والا ہے، ');
INSERT INTO "ur_maududi" VALUES(35,35,'35:35','جس نے ہمیں اپنے فضل سے ابدی قیام کی جگہ ٹھہرا دیا، اب یہاں نہ ہمیں کوئی مشقّت پیش آتی ہے اور نہ تکان لاحق ہوتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,36,'35:36','اور جن لوگوں نے کُفر کیا ہے اُن کے لیے جہنّم کی آگ ہے۔ نہ اُن کا قصّہ پاک کر دیا جائے گا کہ مر جائیں اور نہ اُن کے لیے جہنّم کے عذاب میں کوئی کمی کی جائے گی۔ اِس طرح ہم بدلہ دیتے ہیں ہر اُس شخص کو جو کُفر کرنے والا ہو');
INSERT INTO "ur_maududi" VALUES(35,37,'35:37','وہ وہاں چیخ چیخ کر کہیں گے کہ”اے ہمارے ربّ ، ہمیں یہاں سے نکال لے تاکہ ہم نیک عمل کریں اُن اعمال سے مختلف جو پہلے کرتے رہے تھے۔“ (انہیں جواب دیا جائے گا)”کیا ہم نے تم کو اتنی عمر نہ دی تھی جس میں کوئی سبق لینا چاہتا تو سبق لے سکتا تھا؟ اور تمہارے پاس متنبّہ کرنے والا بھی آچکا تھا۔ اب مزا چکھو۔ ظالموں کا یہاں کوئی مددگار نہیں ہے۔“');
INSERT INTO "ur_maududi" VALUES(35,38,'35:38','بے شک اللہ آسمانوں اور زمین کی ہر پوشیدہ چیز سے واقف ہے، وہ تو سینوں کے چھپے ہوئے راز تک جانتا ہے');
INSERT INTO "ur_maududi" VALUES(35,39,'35:39','وہی تو ہے جس نے تم کو زمین میں خلیفہ بنایا ہے۔ اب جو کوئی کُفر کرتا ہے اس کے کُفر کا وبال اُسی پر ہے، اور کافروں کو اُن کا کُفر اِس کےسوا کوئی ترقی نہیں دیتا کہ ان کے ربّ کا غضب اُن پر زیادہ سے زیادہ بھڑکتا چلا جاتا ہے۔ کافروں کے لیے خسارے میں اضافے کے سوا کوئی ترقی نہیں');
INSERT INTO "ur_maududi" VALUES(35,40,'35:40','(اے نبیؐ)ان سے کہو”کبھی تم نے دیکھا بھی ہے اپنے اُن شریکوں کو جنہیں تم خدا کو چھوڑ کر پکارا کرتے ہو ؟ مجھے بتاوٴ، انہوں نے زمین میں کیا پیدا کیا ہے؟ یا آسمانوں میں ان کی کیا شرکت ہے؟“(اگر یہ نہیں بتا سکتے تو ان سے پوچھو)کیا ہم نے کوئی تحریر لکھ کر دی ہے جس کی بنا پر یہ (اپنے اس شرک کے لیے)کوئی صاف سَنَد رکھتے ہوں؟ نہیں، بلکہ یہ ظالم ایک دُوسرے کو محض فریب کے جھانسے دیے جارہے ہیں ');
INSERT INTO "ur_maududi" VALUES(35,41,'35:41','حقیقت یہ ہے کہ اللہ ہی ہے جو آسمانوں اور زمین کو ٹل جانے سے روکے ہوئے ہے، اور اگر وہ ٹل جائیں تو اللہ کے بعد کوئی دُوسرا انہیں تھامنے والا نہیں ہے۔ بے شک اللہ بڑا حلیم اور درگزر فرمانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(35,42,'35:42','یہ لوگ کڑی کڑی قسمیں کھا کر کہا کرتے تھے کہ اگر کوئی خبردار کرنے والا ان کے ہاں آگیا ہوتا تو یہ دنیا کی ہر دُوسری قوم سے بڑھ کر راست رو ہوتے۔ مگر جب خبردار کرنے والا ان کے پاس آگیا تو اس کی آمد نے اِن کے اندر حق سے فرار کے سوا کسی چیز میں اضافہ نہ کیا');
INSERT INTO "ur_maududi" VALUES(35,43,'35:43','یہ زمین میں اور زیادہ استکبار کرنے لگے اور بُری بُری چالیں چلنے لگے، حالانکہ بُری چالیں اپنے چلنے والوں ہی کو لے بیٹھتی ہیں۔ اب کیا یہ لوگ اِس کا انتظار کر رہے ہیں کہ پچھلی قوموں کے ساتھ اللہ کو جو طریقہ رہا ہے وہی اِن کے ساتھ بھی برتا جائے؟ ہی بات ہے تو تم اللہ کے طریقے میں ہر گز کوئی تبدیلی نہ پاوٴ گے اور تم کبھی نہ دیکھو گے کہ اللہ کی سُنّت کو اس کے مقرر راستے سے کوئی طاقت پھیر سکتی ہے');
INSERT INTO "ur_maududi" VALUES(35,44,'35:44','کیا یہ لوگ زمین میں کبھی چلے پھرے نہیں ہیں کہ انہیں اُن لوگوں کا انجام نظر آتا جو اِن سے پہلے گزر چکے ہیں اور ان سے بہت زیادہ طاقت ور تھے؟ اللہ کو کوئی چیز عاجز کرنے والی نہیں ہے، نہ آسمانوں میں اور نہ زمین میں وہ سب کچھ جانتا ہے اور ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(35,45,'35:45','اگر کہیں وہ لوگوں کو اُن کے کیے کرتوتوں پر پکڑتا تو زمین پر کسی متنفس کو جیتا نہ چھوڑتا مگر وہ انہیں ایک مقرر وقت تک کے لیے مہلت دے رہا ہے پھر جب ان کا وقت آن پورا ہوگا تو اللہ اپنے بندوں کو دیکھ لے گا');
INSERT INTO "ur_maududi" VALUES(36,1,'36:1','یٰسٓ۔ ');
INSERT INTO "ur_maududi" VALUES(36,2,'36:2','قسم ہے قرآن حکیم کی');
INSERT INTO "ur_maududi" VALUES(36,3,'36:3','کہ تم یقیناً رسُولوں میں سے ہو ');
INSERT INTO "ur_maududi" VALUES(36,4,'36:4','سیدھے راستے پر ہو');
INSERT INTO "ur_maududi" VALUES(36,5,'36:5','(اور یہ قرآن)غالب اور رحیم ہستی کا نازل کردہ ہے ');
INSERT INTO "ur_maududi" VALUES(36,6,'36:6','تاکہ تم خبردار کرو ایک ایسی قوم کو جس کے باپ دادا خبر دار نہ کیے گئے تھے اور اس وجہ سے وہ غفلت میں پڑے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,7,'36:7','اِن میں سے اکثر لوگ فیصلہٴ عذاب کے مستحق ہو چکے ہیں، اسی لیے وہ ایمان نہیں لاتے۔ ');
INSERT INTO "ur_maududi" VALUES(36,8,'36:8','ہم نے اُن کی گردنوں میں طَوق ڈال دیے ہیں جن سے وہ ٹھوڑیوں تک جکڑے گئے ہیں، اس لیے وہ سر اُٹھا ئے کھڑے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,9,'36:9','ہم نے ایک دیوار اُن کے آگے کھڑی کر دی ہے اور ایک دیوار اُن کے پیچھے۔ ہم نے انہیں ڈھانک دیا ہے، انہیں اب کچھ نہیں سُوجھتا۔ ');
INSERT INTO "ur_maududi" VALUES(36,10,'36:10','ان کے لیے یکساں ہے ، تم انہیں خبردار کرو یا نہ کرو ، یہ نہ مانیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(36,11,'36:11','تم تو اُسی شخص کو خبردار کر سکتے ہی جو نصیحت کی پیروی کرے اور بے دیکھے خدائے رحمان سے ڈرے اُسے مغفرت اور اجر کریم کی بشارت دے دو');
INSERT INTO "ur_maududi" VALUES(36,12,'36:12','ہم یقیناً ایک روز مُردوں کو زندہ کرنے والے ہیں۔ جو کچھ افعال انہوں نے کیے ہیں وہ سب ہم لکھتے جا رہے ہیں ، اور جو کچھ آثار انہوں نے پیچھے چھوڑے ہیں وہ بھی ہم ثبت کر رہے ہیں۔ ہر چیز کو ہم نے ایک کھُلی کتاب میں درج کر رکھا ہے');
INSERT INTO "ur_maududi" VALUES(36,13,'36:13','اِنہیں مثال کے طور پر اُس بستی والوں کا قصّہ سُناوٴ جبکہ اُس میں رسُول آئے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(36,14,'36:14','ہم نے ان کی طرف دو رسول بھیجے اور انہوں نے دونوں کو جھٹلا دیا پھر ہم نے تیسرا مدد کے لیے بھیجا اور ان سب نے کہا "ہم تمہاری طرف رسول کی حیثیت سے بھیجے گئے ہیں"');
INSERT INTO "ur_maududi" VALUES(36,15,'36:15','بستی والوں نے کہا”تم کچھ نہیں ہو مگر ہم جیسے چند انسان ، اور خدائے رحمٰن نے ہرگز کوئی چیز نازل نہیں کی ہے ، تم محض جھُوٹ بولتے ہو۔“');
INSERT INTO "ur_maududi" VALUES(36,16,'36:16','رسولوں نے کہا "ہمارا رب جانتا ہے کہ ہم ضرور تمہاری طرف رسول بنا کر بھیجے گئے ہیں');
INSERT INTO "ur_maududi" VALUES(36,17,'36:17','اور ہم پر صاف صاف پیغام پہنچا دینے کے سوا کوئی ذمّہ داری نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(36,18,'36:18','بستی والے کہنے لگے”ہم تو تمہیں اپنے لیے فالِ بد سمجھتے ہیں۔ اگر تم باز نہ آئے تو ہم تم کو سنگسار کر دیں گے اور ہم سے تم بڑی دردناک سزا پاوٴگے۔“');
INSERT INTO "ur_maududi" VALUES(36,19,'36:19','رسُولوں نے جواب دیا”تمہاری فالِ بد تو تمہارے اپنے ساتھ لگی ہوئی ہے۔ کیا یہ باتیں تم اِس لیے کرتے ہو کہ تمہیں نصیحت کی گئی؟ اصل بات یہ ہے کہ تم حد سے گزرے ہوئے لوگ ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(36,20,'36:20','اتنے میں شہر کے دُور دراز گوشے سے ایک شخص دوڑتا ہوا آیا اور بولا "اے میری قوم کے لوگو، رسولوں کی پیروی اختیار کر لو');
INSERT INTO "ur_maududi" VALUES(36,21,'36:21','پیروی کرو اُن لوگوں کی جو تم سے کوئی اجر نہیں چاہتے اور ٹھیک راستے پر ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,22,'36:22','آخر کیوں نہ میں اُس ہستی کی بندگی کروں جس نے مجھے پیدا کیا ہے اور جس کی طرف تم سب کو پلٹ کر جانا ہے؟ ');
INSERT INTO "ur_maududi" VALUES(36,23,'36:23','کیا میں اُسے چھوڑ کر دُوسرے معبُود بنا لوں؟ حالانکہ اگر خدائے رحمٰن مجھے کوئی نقصان پہنچانا چاہے تو نہ اُن کی شفاعت میرے کسی کام آسکتی ہے اور نہ وہ مجھے چھُڑا ہی سکتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,24,'36:24','اگر میں ایسا کروں تو مین صریح گمراہی میں مُبتلا ہو جاوٴ گا');
INSERT INTO "ur_maududi" VALUES(36,25,'36:25','میں تو تمہارے ربّ پر ایمان لے آیا ، تم بھی میری بات مان لو۔“');
INSERT INTO "ur_maududi" VALUES(36,26,'36:26','(آخر کار ان لوگوں نے اسے قتل کر دیا اور)اس شخص سے کہہ دیا گیا کہ”داخل ہو جاوٴ جنّت میں۔“ اُس نے کہا”کاش میری قوم کو معلوم ہوتا');
INSERT INTO "ur_maududi" VALUES(36,27,'36:27','کہ میرے ربّ نے کس چیز کی بدولت میری مغفرت فرمادی اور مجھے با عزّت لوگوں میں داخل فرمایا۔“ ');
INSERT INTO "ur_maududi" VALUES(36,28,'36:28','اس کے بعد اُس کی قوم پر ہم نے آسمان سے کوئی لشکر نہیں اتارا ہمیں لشکر بھیجنے کی کوئی حاجت نہ تھی');
INSERT INTO "ur_maududi" VALUES(36,29,'36:29','بس ایک دھماکا ہوا اور یکایک وہ سب بجھ کر رہ گئے۔ ');
INSERT INTO "ur_maududi" VALUES(36,30,'36:30','افسوس بندوں کے حال پر، جو رسول بھی ان کے پاس آیا اُس کا وہ مذاق ہی اڑاتے رہے');
INSERT INTO "ur_maududi" VALUES(36,31,'36:31','کیا انہوں نے دیکھا نہیں کہ ان سے پہلے کتنی ہی قوموں کو ہم ہلاک کر چکے ہیں اور اس کے بعد وہ پھر کبھی ان کی طرف پلٹ کر نہ آئے؟ ');
INSERT INTO "ur_maududi" VALUES(36,32,'36:32','ان سب کو ایک روز ہمارے سامنے حاضر کیا جانا ہے');
INSERT INTO "ur_maududi" VALUES(36,33,'36:33',' اِن لوگوں کے لیے بے جان زمین ایک نشانی ہے ۔ ہم نے اس کو زندگی بخشی اور اس سے غلّہ نکالا جسے یہ کھاتے ہیں');
INSERT INTO "ur_maududi" VALUES(36,34,'36:34','ہم نے اس میں کھجوروں اور انگوروں کے باغ پیدا کیے اور اس کے اندر چشمے پھوڑ نکالے');
INSERT INTO "ur_maududi" VALUES(36,35,'36:35','تاکہ یہ اس کے پھل کھائیں۔ یہ سب کچھ ان کے اپنے ہاتھوں کا پیدا کیا ہوا نہیں ہے۔ پھر کیا یہ شکر ادا نہیں کرتے؟ ');
INSERT INTO "ur_maududi" VALUES(36,36,'36:36','پاک وہ ذات جس نے جملہ اقسام کے جوڑے پیدا کیے خواہ وہ زمین کی نباتات میں سے ہوں یا خود اِن کی اپنی جنس (یعنی نوعِ انسانی)میں سے یا اُن اشیاء میں سے جن کو یہ جانتے تک نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,37,'36:37','اِن کے لیے ایک اور نشانی رات ہے، ہم اُس کے اُوپر سے دِن ہٹا دیتے ہیں تو اِن پر اندھیرا چھا جاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(36,38,'36:38','اور سُورج ، وہ اپنے ٹھکانے کی طرف چلا جا رہا ہے۔ یہ زبر دست علیم ہستی کا باندھا ہوا حساب ہے');
INSERT INTO "ur_maududi" VALUES(36,39,'36:39','اور چاند، اُس کے لیے ہم نے منزلیں مقرر کر دی ہیں یہاں تک کہ ان سے گزرتا ہوا وہ پھر کھجور کی سُوکھی شاخ کے مانند رہ جاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(36,40,'36:40','نہ سُورج کے بس میں یہ ہے کہ وہ چاند کو جا پکڑے اور نہ رات دن پر سبقت لے جا سکتی ہے۔ سب ایک ایک فلک میں تیر رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,41,'36:41','اِن کے لیے یہ بھی ایک نشانی ہے کہ ہم نے اِن کی نسل کو بھری ہوئی کشتی میں سوار کر دیا، ');
INSERT INTO "ur_maududi" VALUES(36,42,'36:42','اور پھر اِن کے لیے ویسی ہی کشتیاں اور پیدا کیں جن پر یہ سوار ہوتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,43,'36:43','ہم چاہیں تو اِن کو غرق کر دیں، کوئی اِن کی فریاد سننے والا نہ ہو اور کسی طرح یہ نہ بچائے جا سکیں');
INSERT INTO "ur_maududi" VALUES(36,44,'36:44','بس ہماری رحمت ہی ہے جو انہیں پار لگاتی اور ایک وقت ِ خاص تک زندگی سے متمتع ہونے کا موقع دیتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(36,45,'36:45','اِن لوگوں سے جب کہا جاتا ہے کہ بچو اُس انجام سے جو تمہارے آگے آرہا ہے اور تمہارے پیچھے گزر چکا ہے ، شاید کہ تم پر رحم کیا جائے (تو یہ سُنی اَن سُنی کر جاتے ہیں)');
INSERT INTO "ur_maududi" VALUES(36,46,'36:46','اِن کے سامنے اِن کے رب کی آیات میں سے جو آیت بھی آتی ہے یہ اس کی طرف اِلتفات نہیں کرتے۔ ');
INSERT INTO "ur_maududi" VALUES(36,47,'36:47','اور جب اِن سے کہا جاتا ہے کہ اللہ نے جو رزق تمہیں عطا کیا ہے اُس میں سے کچھ اللہ کی راہ میں خرچ کرو تو یہ لوگ جنہوں نے کُفر کیا ہے ایمان لانے والوں کو جواب دیتے ہیں ”کیا ہم اُن کو کھِلائیں جنہیں اگر اللہ چاہتا تو خود کھِلا دیتا؟ تم تو بالکل ہی بہک گئے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(36,48,'36:48',' یہ لوگ کہتے ہیں کہ”یہ قیامت کی دھمکی آخر کب پُوری ہوگی؟ بتاوٴ اگر تم سچے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(36,49,'36:49','دراصل یہ جس چیز کی راہ تک رہے ہیں وہ بس ایک دھماکا ہے جو یکایک اِنہیں عین اُس حالت میں دھر لے گا جب یہ (اپنے دنیوی معاملات میں) جھگڑ رہے ہوں گے');
INSERT INTO "ur_maududi" VALUES(36,50,'36:50','اور اُس وقت یہ وصیّت تک نہ کر سکیں گے، نہ اپنے گھروں کو پلٹ سکیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(36,51,'36:51','پھر ایک صُور پھونکا جائے گا اور یکایک یہ اپنے ربّ کے حضور پیش ہونے کے لیے اپنی قبروں سے نکل پڑیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(36,52,'36:52','گھبرا کر کہیں گے”ارے ، یہ کس نے ہمیں ہماری خواب گاہوں سے اُٹھا کھڑا کیا؟“ ۔۔۔۔ ”یہ وہی چیز ہے جس کا خدائے رحمٰن نے وعدہ کیا تھا اور رسُولوں کی بات سچی تھی۔“ ');
INSERT INTO "ur_maududi" VALUES(36,53,'36:53','ایک ہی زور کی آواز ہو گی اور سب کے سب ہمارے سامنے حاضر کر دیے جائیں گے');
INSERT INTO "ur_maududi" VALUES(36,54,'36:54',' آج کسی پر ذرّہ برابر ظلم نہ کیا جائے گا اور تمہیں ویسا ہی بدلہ دیا جائے گا جیسے عمل تم کرتے رہے تھے');
INSERT INTO "ur_maududi" VALUES(36,55,'36:55','آج جنّتی لوگ مزے کرنے میں مشغول ہیں، ');
INSERT INTO "ur_maududi" VALUES(36,56,'36:56','وہ اور ان کی بیویاں گھنے سایوں میں ہیں مسندوں پر تکیے لگائے ہوئے');
INSERT INTO "ur_maududi" VALUES(36,57,'36:57','ہر قسم کی لذیذ چیزیں کھانے پینے کو ان کے لیے وہاں موجود ہیں، جو کچھ وہ طلب کریں اُن کے لیے حاضر ہے');
INSERT INTO "ur_maududi" VALUES(36,58,'36:58','رب رحیم کی طرف سے ان کو سلام کہا گیا ہے');
INSERT INTO "ur_maududi" VALUES(36,59,'36:59','اور اے مجرمو، آج تم چھَٹ کر الگ ہو جاوٴ۔ ');
INSERT INTO "ur_maududi" VALUES(36,60,'36:60','آدمؑ کے بچو، کیا میں نے تم کو ہدایت نہ کی تھی کہ شیطان کی بندگی نہ کرو، وہ تمہارا کھلا دشمن ہے');
INSERT INTO "ur_maududi" VALUES(36,61,'36:61','اورمیری ہی بندگی کرو، یہ سیدھا راستہ ہے؟ ');
INSERT INTO "ur_maududi" VALUES(36,62,'36:62','مگر اس کے باوجود اس نے تم میں سے ایک گروہ ِ کثیر کو گمراہ کر دیا۔ کیا تم عقل نہیں رکھتے تھے؟ ');
INSERT INTO "ur_maududi" VALUES(36,63,'36:63','یہ وہی جہنم ہے جس سے تم کو ڈرایا جاتا رہا تھا');
INSERT INTO "ur_maududi" VALUES(36,64,'36:64','جو کفر تم دنیا میں کرتے رہے ہو اُس کی پاداش میں اب اِس کا ایندھن بنو');
INSERT INTO "ur_maududi" VALUES(36,65,'36:65','آج ہم اِن کے منہ بند کیے دیتے ہیں ، اِن کے ہاتھ ہم سے بولیں گے اور ان کے پاوٴں گواہی دیں گے کہ یہ دنیا میں کیا کمائی کرتے رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,66,'36:66','ہم چاہیں تو اِن کی آنکھیں موند دیں، پھر یہ راستے کی طرف لپک کر دیکھیں، کہاں سے اِنہیں راستہ سجھائی دے گا؟');
INSERT INTO "ur_maududi" VALUES(36,67,'36:67','ہم چاہیں تو اِنہیں اِن کی جگہ ہی پر اس طرح مسخ کر کے رکھ دیں کہ یہ نہ آگے چل سکیں نہ پیچھے پلٹ سکیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,68,'36:68','جس شخص کو ہم لمبی عُمر دیتے ہیں اس کی ساخت کو ہم اُلٹ ہی دیتے ہیں ، کیا (یہ حالات دیکھ کر)انہیں عقل نہیں آتی؟');
INSERT INTO "ur_maududi" VALUES(36,69,'36:69','ہم نے اِس (نبیؐ)کو شعر نہیں سکھایا ہے اور نہ شاعری اس کو زیب ہی دیتی ہے۔ یہ تو ایک نصیحت ہے اور صاف پڑھی جانے والی کتاب');
INSERT INTO "ur_maududi" VALUES(36,70,'36:70','تاکہ وہ ہر اُس شخص کو خبردارکر دے جو زندہ ہو اور انکار کرنے والوں پر حجّت قائم ہو جائے');
INSERT INTO "ur_maududi" VALUES(36,71,'36:71','کیا یہ لوگ دیکھتے نہیں ہیں کہ ہم نے اپنے ہاتھوں کی بنائی ہوئی چیزوں میں سے اِن کے لیے مویشی پیدا کیے اور اب یہ ان کےمالک ہیں');
INSERT INTO "ur_maududi" VALUES(36,72,'36:72','ہم نے اُنہیں اس طرح اِن کے بس میں کر دیا ہے کہ اُن میں سے کسی پر یہ سوار ہوتے ہیں، کسی کا یہ گوشت کھاتے ہیں');
INSERT INTO "ur_maududi" VALUES(36,73,'36:73','اور اُن کے اندر اِن کے لیے طرح طرح کے فوائد اور مشروبات ہیں۔ پھر کیا یہ شکر گزار نہیں ہوتے؟ ');
INSERT INTO "ur_maududi" VALUES(36,74,'36:74','یہ سب کچھ ہوتے ہوئے اِنہوں نے اللہ کے سوا دوسرے خدا بنا لیے ہیں اور یہ امید رکھتے ہیں کہ اِن کی مدد کی جائے گی');
INSERT INTO "ur_maududi" VALUES(36,75,'36:75','وہ اِن کی کوئی مدد نہیں کر سکتے بلکہ یہ لوگ اُلٹے اُن کے لیے حاضر باش لشکر بنے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,76,'36:76','اچھا، جو باتیں یہ بنا رہے ہیں وہ تمہیں رنجیدہ نہ کریں، اِن کی چھُپی اور کھُلی سب باتوں کو ہم جانتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(36,77,'36:77',' کیا انسان دیکھتا نہیں ہے کہ ہم نے اسے نطفہ سے پیدا کیا اور پھر وہ صریح جھگڑالُو بن کر کھڑا ہو گیا؟ ');
INSERT INTO "ur_maududi" VALUES(36,78,'36:78','اب وہ ہم پر مثالیں چسپاں کرتا ہے اور اپنی پیدائش کو بھُول جاتا ہے۔ کہتا ہے ”کون اِن ہڈیوں کو زندہ کرے گا جبکہ یہ بوسیدہ ہو چکی ہوں؟“');
INSERT INTO "ur_maududi" VALUES(36,79,'36:79','اس سے کہو، اِنہیں وہی زندہ کرے گا جس نے پہلے انہیں پیدا کیا تھا، اور وہ تخلیق کا ہر کام جانتا ہے');
INSERT INTO "ur_maududi" VALUES(36,80,'36:80','وہی جس نے تمہارے لیے ہرے بھرے درخت سے آگ پیدا کر دی اور تم اس سے اپنے چُولہے روشن کرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(36,81,'36:81','کیا وہ جس نے آسمانوں اور زمین کو پیدا کیا اِس پر قادر نہیں ہے کہ اِن جیسوں کو پیدا کر سکے؟ کیوں نہیں، جبکہ وہ ماہر خلاق ہے');
INSERT INTO "ur_maududi" VALUES(36,82,'36:82','وہ تو جب کسی چیز کا ارادہ کرتا ہے تو اس کا کام بس یہ ہے کہ اسے حکم دے کہ ہو جا اور وہ ہو جاتی ہے');
INSERT INTO "ur_maududi" VALUES(36,83,'36:83','پاک ہے وہ جس کے ہاتھ میں ہر چیز کا مکمل اقتدار ہے، اور اسی کی طرف تم پلٹائے جانے والے');
INSERT INTO "ur_maududi" VALUES(37,1,'37:1','قطار در قطار صف باندھنے والوں کی قسم');
INSERT INTO "ur_maududi" VALUES(37,2,'37:2','پھر اُن کی قسم جو ڈانٹنے پھٹکارنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(37,3,'37:3','پھر اُن کی قسم جو کلامِ نصیحت سُنانے والے ہیں، ');
INSERT INTO "ur_maududi" VALUES(37,4,'37:4','تمہارا معبُودِ حقیقی بس ایک ہی ہے ');
INSERT INTO "ur_maududi" VALUES(37,5,'37:5','وہ جو زمین اور آسمانوں کا اور تمام اُن چیزوں کا مالک ہے جو زمین و آسمان میں ہیں ، اور سارے مشرقوں کا مالک۔ ');
INSERT INTO "ur_maududi" VALUES(37,6,'37:6','ہم نے آسمانِ دُنیا کو تاروں کی زینت سے آراستہ کیا ہے');
INSERT INTO "ur_maududi" VALUES(37,7,'37:7','اور ہر شیطانِ سرکش سے اس کو محفوظ کر دیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(37,8,'37:8','یہ شیاطین ملاء اعلیٰ کی باتیں نہیں سن سکتے، ہر طرف سے مارے اور ہانکے جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(37,9,'37:9','اور ان کے لیے پیہم عذاب ہے');
INSERT INTO "ur_maududi" VALUES(37,10,'37:10','تا ہم اگر کوئی ان میں سے کچھ لے اُڑے تو ایک تیز شُعلہ اس کا پیچھا کرتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(37,11,'37:11','اب اِن سے پوچھو، اِن کی پیدائش زیادہ مشکل ہے یا اُن چیزوں کی جو ہم نے پیدا کر رکھی ہیں؟ اِن کو تو ہم نے لیس دار گارے سے پیدا کیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(37,12,'37:12','تم (اللہ کی قدرت کے کرشموں پر) حیران ہو اور یہ اس کا مذاق اڑا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(37,13,'37:13','سمجھایا جاتا ہے تو سمجھ کر نہیں دیتے');
INSERT INTO "ur_maududi" VALUES(37,14,'37:14','کوئی نشانی دیکھتے ہیں تو اسے ٹھٹھوں میں اڑاتے ہیں');
INSERT INTO "ur_maududi" VALUES(37,15,'37:15','اورکہتے ہیں”یہ تو صریح جادُو ہے، ');
INSERT INTO "ur_maududi" VALUES(37,16,'37:16','بھلا کہیں ایسا ہو سکتا ہے کہ جب ہم مر چکے ہوں اور مٹی بن جائیں اور ہڈیوں کا پنجر رہ جائیں اُس وقت ہم پھر زندہ کر کے اٹھا کھڑے کیے جائیں؟');
INSERT INTO "ur_maududi" VALUES(37,17,'37:17','اور کیا ہمارے اگلے وقتوں کے آبا و اجداد بھی اٹھائے جائیں گے؟"');
INSERT INTO "ur_maududi" VALUES(37,18,'37:18','اِن سے کہوہاں، اور تم (خدا کے مقابلے میں)بے بس ہو۔ ');
INSERT INTO "ur_maududi" VALUES(37,19,'37:19','بس ایک ہی جھِڑکی ہوگی اور یکایک یہ اپنی آنکھوں سے (وہ سب کچھ جس کی خبر دی جارہی ہے)دیکھ رہے ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(37,20,'37:20','اُس وقت یہ کہیں گے "ہائے ہماری کم بختی، یہ تو یوم الجزا ہے"');
INSERT INTO "ur_maududi" VALUES(37,21,'37:21','یہ وہی فیصلے کا دن ہے جسے تم جھُٹلا یا کرتے تھے۔“ ');
INSERT INTO "ur_maududi" VALUES(37,22,'37:22','(حکم ہوگا)گھیر لاوٴ سب ظالموں اور ان کے ساتھیوں اور اُن کے معبُودوں کو جن کی وہ خدا کو چھوڑ کر بندگی کیا کرتے تھے ');
INSERT INTO "ur_maududi" VALUES(37,23,'37:23','پھر ان سب کو جہنم کا راستہ دکھاؤ');
INSERT INTO "ur_maududi" VALUES(37,24,'37:24','اور ذرا اِنہیں ٹھیراؤ، اِن سے کچھ پوچھنا ہے');
INSERT INTO "ur_maududi" VALUES(37,25,'37:25','"کیا ہو گیا تمہیں، اب کیوں ایک دوسرے کی مدد نہیں کرتے؟');
INSERT INTO "ur_maududi" VALUES(37,26,'37:26','ارے، آج تو یہ اپنے آپ کو (اور ایک دُوسرے کو)حوالے کیے دے رہے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(37,27,'37:27','اس کے بعد یہ ایک دوسرے کی طرف مڑیں گے اور باہم تکرار شروع کر دیں گے');
INSERT INTO "ur_maududi" VALUES(37,28,'37:28','(پیروی کرنے والے اپنے پیشواوٴں سے)کہیں گے،”تم ہمارے پاس سیدھے رُخ سے آتے تھے۔“ ');
INSERT INTO "ur_maududi" VALUES(37,29,'37:29','وہ جواب دیں گے، "نہیں بلکہ تم خود ایمان لانے والے نہ تھے');
INSERT INTO "ur_maududi" VALUES(37,30,'37:30','ہمارا تم پر کوئی زور نہ تھا، تم خود ہی سرکش لوگ تھے');
INSERT INTO "ur_maududi" VALUES(37,31,'37:31','آخرکار ہم اپنے رب کے اِس فرمان کے مستحق ہو گئے کہ ہم عذاب کا مزا چکھنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(37,32,'37:32','سو ہم نے تم کو بہکایا، ہم خود بہکے ہوئے تھے۔“ ');
INSERT INTO "ur_maududi" VALUES(37,33,'37:33','اِس طرح وہ سب اُس روز عذاب میں مشترک ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(37,34,'37:34','ہم مجرموں کے ساتھ یہی کچھ کیا کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(37,35,'37:35','یہ وہ لوگ تھے کہ جب ان سے کہا جاتا "اللہ کے سوا کوئی معبود بر حق نہیں ہے" تو یہ گھمنڈ میں آ جاتے تھے');
INSERT INTO "ur_maududi" VALUES(37,36,'37:36','اور کہتے تھے "کیا ہم ایک شاعر مجنون کی خاطر اپنے معبودوں کو چھوڑ دیں؟"');
INSERT INTO "ur_maududi" VALUES(37,37,'37:37','حالانکہ وہ حق لے کر آیا تھا اور اس نے رسُولوں کی تصدیق کی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(37,38,'37:38','(اب ان سے کہا جائے گا کہ) تم لازماً دردناک سزا کا مزا چکھنے والے ہو');
INSERT INTO "ur_maududi" VALUES(37,39,'37:39','اور تمہیں جو بدلہ بھی دیا جا رہا ہے اُنہی اعمال کا دیا جا رہا ہے جو تم کرتے رہے ہو');
INSERT INTO "ur_maududi" VALUES(37,40,'37:40','مگر اللہ کے چیدہ بندے (اس انجام بد سے) محفوظ ہوں گے');
INSERT INTO "ur_maududi" VALUES(37,41,'37:41','ان کے لیے جانا بوجھا رزق ہے، ');
INSERT INTO "ur_maududi" VALUES(37,42,'37:42','ہر طرح کی لذیذ چیزیں۔ ');
INSERT INTO "ur_maududi" VALUES(37,43,'37:43','اور نعمت بھری جنتیں');
INSERT INTO "ur_maududi" VALUES(37,44,'37:44','جن میں وہ عزت کے ساتھ رکھے جائیں گے تختوں پر آمنے سامنے بیٹھیں گے');
INSERT INTO "ur_maududi" VALUES(37,45,'37:45','شراب کے چشموں سے ساغر بھر بھر کر ان کے درمیان پھرائے جائیں گے');
INSERT INTO "ur_maududi" VALUES(37,46,'37:46','چمکتی ہوئی شراب، جو پینے والوں کے لیے لذّت ہو گی');
INSERT INTO "ur_maududi" VALUES(37,47,'37:47','نہ ان کے جسم کو اس سے کوئی ضرر ہوگا اور نہ ان کی عقل اس سے خراب ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(37,48,'37:48','اور ان کے پاس نگاہیں بچانے والی، خوبصورت آنکھوں والی عورتیں ہوں گی، ');
INSERT INTO "ur_maududi" VALUES(37,49,'37:49','ایسی نازک جیسے انڈے کے چھلکے کے نیچے چھُپی ہوئی جھِلی۔ ');
INSERT INTO "ur_maududi" VALUES(37,50,'37:50','پھر وہ ایک دوسرے کی طرف متوجہ ہو کر حالات پوچھیں گے');
INSERT INTO "ur_maududi" VALUES(37,51,'37:51','ان میں سے ایک کہے گا، "دنیا میں میرا ایک ہم نشین تھا');
INSERT INTO "ur_maududi" VALUES(37,52,'37:52','جو مجھ سے کہا کرتا تھا، کیا تم بھی تصدیق کرنے والوں میں سے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(37,53,'37:53','کیا واقعی جب ہم مر چکے ہوں گے اور مٹی ہو جائیں گے اور ہڈیوں کا پنجر بن کر رہ جائیں گے تو ہمیں جزا و سزا دی جائے گی؟');
INSERT INTO "ur_maududi" VALUES(37,54,'37:54','اب کیا آپ لوگ دیکھنا چاہتے ہیں کہ وہ صاحب اب کہاں ہیں؟"');
INSERT INTO "ur_maududi" VALUES(37,55,'37:55','یہ کہہ کر جونہی وہ جھکے گا تو جہنم کی گہرائی میں اس کو دیکھ لے گا');
INSERT INTO "ur_maududi" VALUES(37,56,'37:56','اور اس سے خطاب کر کے کہے گا "خدا کی قسم، تُو تو مجھے تباہ ہی کر دینے والا تھا');
INSERT INTO "ur_maududi" VALUES(37,57,'37:57','میرے ربّ کا فضل شاملِ حال نہ ہوتا تو آج میں بھی اُن لوگوں میں سے ہوتا جو پکڑے ہوئے آئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(37,58,'37:58','اچھا تو کیا اب ہم مرنے والے نہیں ہیں؟');
INSERT INTO "ur_maududi" VALUES(37,59,'37:59','موت جو ہمیں آنی تھی وہ بس پہلے آچکی؟ اب ہمیں کوئی عذاب نہیں ہونا؟“ ');
INSERT INTO "ur_maududi" VALUES(37,60,'37:60','یقیناً یہی عظیم الشان کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(37,61,'37:61','ایسی ہی کامیابی کے لیے عمل کرنے والوں کو عمل کرنا چاہیے');
INSERT INTO "ur_maududi" VALUES(37,62,'37:62','بولو، یہ ضیافت اچھی ہے یا زقوم کا درخت؟');
INSERT INTO "ur_maududi" VALUES(37,63,'37:63','ہم نے اس درخت کو ظالموں کے لیے فتنہ بنا دیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(37,64,'37:64','وہ ایک درخت ہے جو جہنم کی تہ سے نکلتا ہے');
INSERT INTO "ur_maududi" VALUES(37,65,'37:65','اس کے شگوفے ایسے ہیں جیسے شیطانوں کے سر۔ ');
INSERT INTO "ur_maududi" VALUES(37,66,'37:66','جہنم کے لوگ اُسے کھائیں گے اور اسی سے پیٹ بھریں گے');
INSERT INTO "ur_maududi" VALUES(37,67,'37:67','پھر اس پر پینے کے لیے کھولتا ہوا پانی ملے گا');
INSERT INTO "ur_maududi" VALUES(37,68,'37:68','اور اس کے بعد ان کی واپسی اسی آتشِ دوزخ کی طرف ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(37,69,'37:69','یہ وہ لوگ ہیں جنہوں نے اپنے باپ دادا کو گمراہ پایا');
INSERT INTO "ur_maududi" VALUES(37,70,'37:70','اور اُنہی کے نقشِ قدم پر دوڑ چلے۔ ');
INSERT INTO "ur_maududi" VALUES(37,71,'37:71','حالانکہ ان سے پہلے بہت سے لوگ گمراہ ہو چکے تھے');
INSERT INTO "ur_maududi" VALUES(37,72,'37:72','اور اُن میں ہم نے تنبیہ کرنے والے رسول بھیجے تھے');
INSERT INTO "ur_maududi" VALUES(37,73,'37:73','اب دیکھ لو کہ اُن تنبیہ کیے جانے والوں کا کیا انجام ہوا');
INSERT INTO "ur_maududi" VALUES(37,74,'37:74','اس بد انجامی سے بس اللہ کے وہی بندے بچے ہیں جنہیں اس نے اپنے لیے خالص کر لیا ہے');
INSERT INTO "ur_maududi" VALUES(37,75,'37:75','ہم کو (اس سے پہلے) نوحؑ نے پکارا تھا ، تو دیکھو کہ ہم کیسے اچھے جواب دینے والے تھے');
INSERT INTO "ur_maududi" VALUES(37,76,'37:76','ہم نے اس کو اور اس کے گھر والوں کو کرب عظیم سے بچا لیا ');
INSERT INTO "ur_maududi" VALUES(37,77,'37:77','اور اسی کی نسل کو باقی رکھا ');
INSERT INTO "ur_maududi" VALUES(37,78,'37:78','اور بعد کی نسلوں میں اس کی تعریف و توصیف چھوڑ دی');
INSERT INTO "ur_maududi" VALUES(37,79,'37:79','سلام ہے نوحؑ پر تمام دینا والوں میں ');
INSERT INTO "ur_maududi" VALUES(37,80,'37:80','ہم نیکی کرنے والوں کو ایسی ہی جزا دیا کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(37,81,'37:81','در حقیقت وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "ur_maududi" VALUES(37,82,'37:82','پھر دوسرے گروہ کو ہم نے غرق کر دیا');
INSERT INTO "ur_maududi" VALUES(37,83,'37:83','اور نوحؑ ہی کے طریقے پر چلنے والا ابراہیمؑ تھا');
INSERT INTO "ur_maududi" VALUES(37,84,'37:84','جب وہ اپنے رب کے حضور قلب سلیم لے کر آیا ');
INSERT INTO "ur_maududi" VALUES(37,85,'37:85','جب اس نے اپنے باپ اور اپنی قوم سے کہا ۔’’ یہ کیا چیزیں جن کے تم عبادت کر رہے ہو؟');
INSERT INTO "ur_maududi" VALUES(37,86,'37:86','کیا اللہ کو چھوڑ کر جھوٹ گھڑے ہوئے معبود چاہتے ہو؟');
INSERT INTO "ur_maududi" VALUES(37,87,'37:87','آخر اللہ ربّ العالمین کے بارے میں تمہارا کیا گمان ہے ؟‘‘ ');
INSERT INTO "ur_maududi" VALUES(37,88,'37:88','پھر اس نے تاروں پر ایک نگاہ ڈالی ');
INSERT INTO "ur_maududi" VALUES(37,89,'37:89','اور کہا میری طبیعت خراب ہے ');
INSERT INTO "ur_maududi" VALUES(37,90,'37:90','چنانچہ وہ لوگ اسے چھوڑ کر چلے گئے ');
INSERT INTO "ur_maududi" VALUES(37,91,'37:91','ان کے پیچھے وہ چپکے سے ان کے معبودوں کے مندر میں گھس گیا اور بولا ’’ آپ لوگ کھاتے کیوں نہیں ہیں ؟');
INSERT INTO "ur_maududi" VALUES(37,92,'37:92','کیا ہو گیا، آپ لوگ بولتے بھی نہیں؟"');
INSERT INTO "ur_maududi" VALUES(37,93,'37:93','اس کے بعد وہ اُن پر پل پڑا اور سیدھے ہاتھ سے خوب ضربیں لگائیں');
INSERT INTO "ur_maududi" VALUES(37,94,'37:94','(واپس آ کر) وہ لوگ بھاگے بھاگے اس کے پاس آئے ');
INSERT INTO "ur_maududi" VALUES(37,95,'37:95','اس نے کہا "کیا تم اپنی ہی تراشی ہوئی چیزوں کو پوجتے ہو؟');
INSERT INTO "ur_maududi" VALUES(37,96,'37:96','حالانکہ اللہ ہی نے تم کو بھی پیدا کیا ہے اور اُن چیزوں کو بھی جنہیں تم بناتے ہو"');
INSERT INTO "ur_maududi" VALUES(37,97,'37:97','انہوں نے آپس میں کہا "اس کے لیے ایک الاؤ تیار کرو اور اسے دہکتی ہوئی آگ کے ڈھیر میں پھینک دو"');
INSERT INTO "ur_maududi" VALUES(37,98,'37:98','انہوں نے اس کے خلاف ایک کار روائی کرنی چاہی تھی، مگر ہم نے انہی کو نیچا دکھا دیا ');
INSERT INTO "ur_maududi" VALUES(37,99,'37:99','ابراہیمؑ نے کہا ’’میں اپنے رب کی طرف جاتا ہوں ، وہی میری رہنمائی کرے گا');
INSERT INTO "ur_maududi" VALUES(37,100,'37:100','اے پروردگار، مجھے ایک بیٹا عطا کر جو صالحوں میں سے ہو‘‘ ');
INSERT INTO "ur_maududi" VALUES(37,101,'37:101','(اس دعا کے جواب میں) ہم نے اس کو ایک حلیم(بردبار) لڑکے کی بشارت دی ');
INSERT INTO "ur_maududi" VALUES(37,102,'37:102','وہ لڑکا جب اس کے ساتھ دوڑ دھوپ کرنے کی عمر کو پہنچ گیا تو (ایک روز) ابراہیمؑ نے اس سے کہا،’’بیٹا ، میں خواب میں دیکھتا ہوں کہ میں تجھے ذبح کر رہا ہوں ، اب تو بتا تیرا کیا خیال ہے ؟ اس نے کہا، ’’ ابا جان ، جو کچھ آپ کو حکم دیا جا رہا ہے اسے کر ڈالیے ، آپ اِنْشاءَ اللہ مجھے صابروں میں سے پائیں گے ‘‘');
INSERT INTO "ur_maududi" VALUES(37,103,'37:103','آخر کو جب ان دونوں نے سر تسلیم خم کر دیا اور ابراہیمؑ نے بیٹے کو ماتھے کے بل گرا دیا ');
INSERT INTO "ur_maududi" VALUES(37,104,'37:104','اور ہم نے ندا دی کہ ’’ اے ابراہیمؑ');
INSERT INTO "ur_maududi" VALUES(37,105,'37:105','تو نے خواب سچ کر دکھایا ۔ ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں ');
INSERT INTO "ur_maududi" VALUES(37,106,'37:106','یقیناً یہ ایک کھلی آزمائش تھی‘‘ ');
INSERT INTO "ur_maududi" VALUES(37,107,'37:107','اور ہم نے ایک بڑی قربانی فدیے میں دے کر اس بچے کو چھڑا لیا ');
INSERT INTO "ur_maududi" VALUES(37,108,'37:108','اور اس کی تعریف و توصیف ہمیشہ کے لیے بعد کی نسلوں میں چھوڑ دی');
INSERT INTO "ur_maududi" VALUES(37,109,'37:109','سلام ہے ابراہیمؑ پر');
INSERT INTO "ur_maududi" VALUES(37,110,'37:110','ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(37,111,'37:111','یقیناً وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "ur_maududi" VALUES(37,112,'37:112','اور ہم نے اسے اسحاقؑ کی بشارت دی، ایک نبی صالحین میں سے');
INSERT INTO "ur_maududi" VALUES(37,113,'37:113','اور اسے اور اسحاق کو برکت دی ۔اب ان دونوں کی ذریّت میں سے کوئی محسن ہے اور کوئی اپنے نفس پر صریح ظلم کرنے والا ہے ');
INSERT INTO "ur_maududi" VALUES(37,114,'37:114','اور ہم نے موسیٰؑ و ہارونؑ پر احسان کیا');
INSERT INTO "ur_maududi" VALUES(37,115,'37:115','اُن کو اور ان کی قوم کو کربِ عظیم سے نجات دی، ');
INSERT INTO "ur_maududi" VALUES(37,116,'37:116','اُنہیں نصرت بخشی جس کی وجہ سے وہی غالب رہے');
INSERT INTO "ur_maududi" VALUES(37,117,'37:117','ان کو نہایت واضح کتاب عطا کی');
INSERT INTO "ur_maududi" VALUES(37,118,'37:118','انہیں راہ راست دکھائی');
INSERT INTO "ur_maududi" VALUES(37,119,'37:119','اور بعد کی نسلوں میں ان کا ذکر خیر باقی رکھا');
INSERT INTO "ur_maududi" VALUES(37,120,'37:120','سلام ہے موسیٰؑ اور ہارونؑ پر');
INSERT INTO "ur_maududi" VALUES(37,121,'37:121','ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(37,122,'37:122','در حقیقت وہ ہمارے مومن بندوں میں سے تھے');
INSERT INTO "ur_maududi" VALUES(37,123,'37:123','اور الیاسؑ بھی یقیناً مُرسَلین میں سے تھا۔ ');
INSERT INTO "ur_maududi" VALUES(37,124,'37:124','یاد کرو جب اس نے اپنی قوم سے کہا تھا کہ "تم لوگ ڈرتے نہیں ہو؟');
INSERT INTO "ur_maududi" VALUES(37,125,'37:125','کیا تم بعل کو پکارتے ہو اور احسن الخالقین کو چھوڑ دیتےہو');
INSERT INTO "ur_maududi" VALUES(37,126,'37:126','اُس اللہ کو جو تمہارا اور تمہارے اگلے پچھلے آبا و اجداد کا رب ہے؟"');
INSERT INTO "ur_maududi" VALUES(37,127,'37:127','مگر انہوں نے اسے جھٹلا دیا، سو اب یقیناً وہ سزا کے لیے پیش کیے جانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(37,128,'37:128','بجز اُن بندگانِ خدا کے جن کو خالص کر لیا گیا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(37,129,'37:129','اور الیاسؑ کا ذکر خیر ہم نے بعد کی نسلوں میں باقی رکھا ');
INSERT INTO "ur_maududi" VALUES(37,130,'37:130','سلام ہے الیاسؑ پر۔ ');
INSERT INTO "ur_maududi" VALUES(37,131,'37:131','ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(37,132,'37:132','واقعی وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "ur_maududi" VALUES(37,133,'37:133','اور لوطؑ بھی انہی لوگوں میں سے تھا جو رسول بنا کر بھیجے گئے ہیں');
INSERT INTO "ur_maududi" VALUES(37,134,'37:134','یاد کرو جب ہم نے اس کو اور اس کے سب گھر والوں کو نجات دی');
INSERT INTO "ur_maududi" VALUES(37,135,'37:135','سوائے ایک بُڑھیا کے جو پیچھے رہ جانے والوں میں سے تھی۔ ');
INSERT INTO "ur_maududi" VALUES(37,136,'37:136','پھر باقی سب کو تہس نہس کر دیا');
INSERT INTO "ur_maududi" VALUES(37,137,'37:137','آج تم شب و روز اُن کے اُجڑے دیا ر پر سے گزرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(37,138,'37:138','کیا تم کو عقل نہیں آتی؟');
INSERT INTO "ur_maududi" VALUES(37,139,'37:139','اور یقیناً یُونسؑ بھی رسُولوں میں سے تھا۔ ');
INSERT INTO "ur_maududi" VALUES(37,140,'37:140','یاد کرو جب وہ ایک بھر ی کشتی کی طرف بھاگ نکلا، ');
INSERT INTO "ur_maududi" VALUES(37,141,'37:141','پھر قرعہ اندازی میں شریک ہوا اور اس میں مات کھائی');
INSERT INTO "ur_maududi" VALUES(37,142,'37:142','آخرکار مچھلی نے اسے نگل لیا اور وہ ملامت زدہ تھا');
INSERT INTO "ur_maududi" VALUES(37,143,'37:143','اب اگر وہ تسبیح کرنے والوں میں سے نہ ہوتا ');
INSERT INTO "ur_maududi" VALUES(37,144,'37:144','تو روزِ قیامت تک اسی مچھلی کے پیٹ میں رہتا۔ ');
INSERT INTO "ur_maududi" VALUES(37,145,'37:145','آخر کار ہم نے اسے بڑی سقیم حالت میں ایک چٹیل زمین میں پھینک دیا۔ ');
INSERT INTO "ur_maududi" VALUES(37,146,'37:146','اور اُس پر ایک بیلدار درخت اُگا دیا');
INSERT INTO "ur_maududi" VALUES(37,147,'37:147','اس کے بعد ہم نے اُسے ایک لاکھ، یا اس سے زائد لوگوں کی طرف بھیجا، ');
INSERT INTO "ur_maududi" VALUES(37,148,'37:148','وہ ایمان لائے اور ہم نے ایک وقتِ خاص تک انہیں باقی رکھا۔ ');
INSERT INTO "ur_maududi" VALUES(37,149,'37:149','پھر ذرا اِن لوگوں سے پوچھو، کیا (اِن کے دل کو یہ بات لگتی ہے کہ)تمہارے ربّ کے لیے توہوں بیٹیاں اور اِن کے لیے ہوں بیٹے! ');
INSERT INTO "ur_maududi" VALUES(37,150,'37:150','کیا واقعی ہم نے ملائکہ کو عورتیں ہی بنا یا ہے اور یہ آنکھوں دیکھی بات کہہ رہے ہیں؟');
INSERT INTO "ur_maududi" VALUES(37,151,'37:151','خوب سن رکھو، دراصل یہ لوگ اپنی من گھڑت سے یہ بات کہتے ہیں');
INSERT INTO "ur_maududi" VALUES(37,152,'37:152','کہ اللہ اولاد رکھتا ہے، اور فی الواقع یہ جھوٹے ہیں');
INSERT INTO "ur_maududi" VALUES(37,153,'37:153','کیا اللہ نے بیٹوں کے بجائے بیٹیاں اپنے لیے پسند کر لیں؟');
INSERT INTO "ur_maududi" VALUES(37,154,'37:154','تمہیں کیا ہو گیا ہے، کیسے حکم لگا رہے ہو');
INSERT INTO "ur_maududi" VALUES(37,155,'37:155','کیا تمہیں ہوش نہیں آتا');
INSERT INTO "ur_maududi" VALUES(37,156,'37:156','یا پھر تمہارے پاس اپنی ان باتوں کے لیے کوئی صاف سند ہے');
INSERT INTO "ur_maududi" VALUES(37,157,'37:157','تو لاوٴ اپنی وہ کتاب اگر تم سچے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(37,158,'37:158','اِنہوں نے اللہ اور ملائکہ کے درمیان نسب کا رشتہ بنا رکھا ہے، حالانکہ ملائکہ خوب جانتے ہیں کہ یہ لوگ مُجرم کی حیثیت سے پیش ہونے والے ہیں');
INSERT INTO "ur_maududi" VALUES(37,159,'37:159','(اور وہ کہتے ہیں کہ) "اللہ اُن صفات سے پاک ہے');
INSERT INTO "ur_maududi" VALUES(37,160,'37:160','جو اُس کے خالص بندوں کے سوا دوسرے لوگ اس کی طرف منسوب کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(37,161,'37:161','پس تم اور تمہارے یہ معبود');
INSERT INTO "ur_maududi" VALUES(37,162,'37:162','اللہ سے کسی کو پھیر نہیں سکتے');
INSERT INTO "ur_maududi" VALUES(37,163,'37:163','مگر صرف اُس کو جو دوزخ کی بھڑکتی ہوئی آگ میں جھُلسنے والا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(37,164,'37:164','اور ہمارا حال تو یہ ہے کہ ہم میں سے ہر ایک کا ایک مقام مقرر ہے، ');
INSERT INTO "ur_maududi" VALUES(37,165,'37:165','اور ہم صف بستہ خدمت گار ہیں');
INSERT INTO "ur_maududi" VALUES(37,166,'37:166','اور تسبیح کرنے والے ہیں"');
INSERT INTO "ur_maududi" VALUES(37,167,'37:167','یہ لوگ پہلے تو کہا کرتے تھے');
INSERT INTO "ur_maududi" VALUES(37,168,'37:168','کہ کاش ہمارے پاس وہ "ذکر" ہوتا جو پچھلی قوموں کو ملا تھا');
INSERT INTO "ur_maududi" VALUES(37,169,'37:169','تو ہم اللہ کے چیدہ بندے ہوتے۔ ');
INSERT INTO "ur_maududi" VALUES(37,170,'37:170','مگر (جب وہ آ گیا) تو انہوں نے اس کا انکار کر دیا اب عنقریب اِنہیں (اِس روش کا نتیجہ) معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(37,171,'37:171','اپنے بھیجے ہوئے بندوں سے ہم پہلے ہی وعدہ کر چکے ہیں');
INSERT INTO "ur_maududi" VALUES(37,172,'37:172','کہ یقیناً ان کی مدد کی جائے گی');
INSERT INTO "ur_maududi" VALUES(37,173,'37:173','اور ہمارا لشکر ہی غالب ہو کر رہے گا۔ ');
INSERT INTO "ur_maududi" VALUES(37,174,'37:174','پس اے نبیؐ، ذرا کچھ مدّت تک انہیں اِن کے حال پر چھوڑ دو');
INSERT INTO "ur_maududi" VALUES(37,175,'37:175','اور دیکھتے رہو، عنقریب یہ خود بھی دیکھ لیں گے ');
INSERT INTO "ur_maududi" VALUES(37,176,'37:176','کیا یہ ہمارے عذاب کے لیے جلدی مچا رہے ہیں؟');
INSERT INTO "ur_maududi" VALUES(37,177,'37:177','جب وہ اِن کے صحن میں آ اترے گا تو وہ دن اُن لوگوں کے لیے بہت برا ہو گا جنہیں متنبہ کیا جا چکا ہے');
INSERT INTO "ur_maududi" VALUES(37,178,'37:178','بس ذرا اِنہیں کچھ مدت کے لیے چھوڑ دو');
INSERT INTO "ur_maududi" VALUES(37,179,'37:179','اور دیکھتے رہو، عنقریب یہ خود دیکھ لیں گے');
INSERT INTO "ur_maududi" VALUES(37,180,'37:180','پاک ہے تیرا رب، عزت کا مالک، اُن تمام باتوں سے جو یہ لوگ بنا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(37,181,'37:181','اور سلام ہے مرسلین پر');
INSERT INTO "ur_maududi" VALUES(37,182,'37:182','اور ساری تعریف اللہ ربّ العالمین ہی کے لیے ہے');
INSERT INTO "ur_maududi" VALUES(38,1,'38:1','ص، قسم ہے نصیحت بھرے قرآن کی');
INSERT INTO "ur_maududi" VALUES(38,2,'38:2','بلکہ یہی لوگ، جنہوں نے ماننے سے انکار کیا ہے، سخت تکبُّر اور ضِد میں مبتلا ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(38,3,'38:3','اِن سے پہلے ہم ایسی کتنی ہی قوموں کو ہلاک کر چکے ہیں (اور جب اُن کی شامت آئی ہے) تو وہ چیخ اٹھے ہیں، مگر وہ وقت بچنے کا نہیں ہوتا');
INSERT INTO "ur_maududi" VALUES(38,4,'38:4','اِن لوگوں کو اس بات پر بڑا تعجّب ہوا کہ ایک ڈرانے والا خود اِنہی میں سے آگیا۔ منکرین کہنے لگے کہ ”یہ ساحِر ہے ، سخت جھُوٹا ہے');
INSERT INTO "ur_maududi" VALUES(38,5,'38:5','کیا اِس نے سارے خداؤں کی جگہ بس ایک ہی خدا بنا ڈالا؟ یہ تو بڑی عجیب بات ہے"');
INSERT INTO "ur_maududi" VALUES(38,6,'38:6','اور سردارانِ قوم یہ کہتے ہوئے نِکل گئے کہ ”چلو اور ڈٹے رہو اپنے معبُودوں کی عبادت پر۔ یہ بات تو کسی اور ہی غرض سے کہی جارہی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(38,7,'38:7','یہ بات ہم نے زمانہٴ قریب کی مِلّت میں کسی سے نہیں سُنی۔ یہ کچھ نہیں ہے مگر ایک من گھڑت بات');
INSERT INTO "ur_maududi" VALUES(38,8,'38:8','کیا ہمارے درمیان بس یہی ایک شخص رہ گیا تھا جس پر اللہ کا ذکر نازل کر دیا گیا؟“اصل بات یہ ہے کہ یہ میرے ”ذِکر“ پر شک کر رہے ہیں، اور یہ ساری باتیں اس لیے کر رہے ہیں کہ انہوں نےمیرے عذاب کا مزا چکھّا نہیں ہے');
INSERT INTO "ur_maududi" VALUES(38,9,'38:9','کیا تیرے داتا اور غالب پروردگار کی رحمت کے خزانے اِن کے قبضے میں ہیں؟');
INSERT INTO "ur_maududi" VALUES(38,10,'38:10','کیا یہ آسمان و زمین اور ان کے درمیان کی چیزوں کے مالک ہیں؟ اچھا تو یہ عالمِ اسباب کی بلندیوں پر چڑھ کر دیکھیں! ');
INSERT INTO "ur_maududi" VALUES(38,11,'38:11','یہ تو جتھوں میں سے ایک چھوٹا سا جتھا ہےجو اِسی جگہ شکست کھانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(38,12,'38:12','اِن سے پہلے توحؑ کی قوم، اور عاد، اور میخوں والا فرعون ');
INSERT INTO "ur_maududi" VALUES(38,13,'38:13','اور ثمود، اور قوم لوط، اور اَیکہ والے جھٹلا چکے ہیں جتھے وہ تھے');
INSERT INTO "ur_maududi" VALUES(38,14,'38:14','ان میں سے ہر ایک نے رسولوں کو جھٹلایا اور میری عقوبت کا فیصلہ اس پر چسپاں ہو کر رہا');
INSERT INTO "ur_maududi" VALUES(38,15,'38:15','یہ لوگ بھی بس ایک دھماکے کے منتظر ہیں جس کے بعد کوئی دوسرا دھماکا نہ ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(38,16,'38:16','اور یہ کہتے ہیں کہ اے ہمارے ربّ ، یوم الحساب سے پہلے ہی ہمارا حصّہ ہمیں جلدی سے دے دے۔ ');
INSERT INTO "ur_maududi" VALUES(38,17,'38:17','اے نبیؐ ، صبر کرو اُن باتوں پر جو یہ لوگ بناتے ہیں، اور اِن کے سامنے ہمارے بندے داوٴدؑ کا قصّہ بیان کرو جو بڑی قوّتوں کا مالک تھا۔ ہر معاملہ میں اللہ کی طرف رُجوع کرنے والا تھا');
INSERT INTO "ur_maududi" VALUES(38,18,'38:18','ہم نے پہاڑوں کو اس کے ساتھ مسخر کر رکھا تھا کہ صبح و شام وہ اس کے ساتھ تسبیح کرتے تھے');
INSERT INTO "ur_maududi" VALUES(38,19,'38:19','پرندے سِمٹ آتے اور سب کے سب اُس کی تسبیح کی طرف متوجّہ ہو جاتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(38,20,'38:20','ہم نے اس کی سلطنت مضبُوط کر دی تھی، اس کو حکمت عطا کی تھی اور فیصلہ کُن بات کہنے کی صلاحیت بخشی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(38,21,'38:21','پھر تمہیں کچھ خبر پہنچی ہے اُن مقدمے والوں کی جو دیوار چڑھ کر اُس کے بالاخانے میں گھُس آئے تھے؟ ');
INSERT INTO "ur_maududi" VALUES(38,22,'38:22','جب وہ داوٴد ؑ کے پاس پہنچے تو وہ انہیں دیکھ کر گھبرا گیا۔ انہوں نے کہا”ڈریے نہیں، ہم دو فریقِ مقدمہ ہیں جن میں سے ایک نے دُوسرے پر زیادتی کی ہے۔ آپ ہمارے درمیان ٹھیک ٹھیک حق کے ساتھ فیصلہ کر دیجیے، بے انصافی نہ کیجیے اور ہمیں راہِ راست بتائیے');
INSERT INTO "ur_maududi" VALUES(38,23,'38:23','یہ میرا بھائی ہے، اِس کے پاس ننّانوے دُنبیاں ہیں اور میرے پاس صرف ایک ہی دُنبی ہے۔ اِس نے مجھ سے کہا کہ یہ ایک دُنبی بھی میرے حوالے کردے اور اس نے گفتگو میں مجھے دبا لیا۔“ ');
INSERT INTO "ur_maududi" VALUES(38,24,'38:24','داوٴدؑ نے جواب دیا”اِس شخص نے اپنی دُنبیوں کے ساتھ تیری دُنبی ملا لینے کا مطالبہ کر کے یقیناً تجھ پر ظلم کیا ، اور واقعہ یہ ہے کہ مِل جُل کر ساتھ رہنے والے لوگ اکثر ایک دُوسرے پر زیادتیاں کرتے رہتے ہیں ، بس وہی لوگ اس سے بچے ہوئے ہیں جو ایمان رکھتے اور عملِ صالح کرتے ہیں، اور ایسے لوگ کم ہی ہیں۔“ (یہ بات کہتے کہتے)داوٴدؑ سمجھ گیا کہ یہ تو ہم نے دراصل اُس کی آزمائش کی ہے ، چنانچہ اس نے اپنے ربّ سے معافی مانگی اور سجدے میں گِر گیا اور رُجوع کر لیا۔ ');
INSERT INTO "ur_maududi" VALUES(38,25,'38:25','تب ہم نے اس کا وہ قصُور معاف کیا اور یقیناً ہمارے ہاں اس کے لیے تقرُّب کا مقام اور بہتر انجام ہے۔ ');
INSERT INTO "ur_maududi" VALUES(38,26,'38:26','(ہم نے اس سے کہا)”اے داوٴدؑ ، ہم نے تجھے زمین میں خلیفہ بنایا ہے، لہٰذا تُو لوگوں کے درمیان حق کے ساتھ حکومت کر اور خواہشِ نفس کی پیروی نہ کر کہ وہ تجھے اللہ کی راہ سے بھٹکا دے گی۔ جو لوگ اللہ کی راہ سے بھٹکتے ہیں یقیناً اُن کے لیے سخت سزا ہے کہ وہ یوم الحساب کو بھُول گئے۔“ ');
INSERT INTO "ur_maududi" VALUES(38,27,'38:27','ہم نے اس آسمان اور زمین کو اور اس دنیا کو جو ان کی درمیان ہے ، فضول پیدا نہیں کر دیا ہے۔ یہ تو اُن لوگوں کا گمان ہے جنہوں نے کُفر کیا ہے، اور ایسے کافروں کے لیے بربادی ہے جہنّم کی آگ سے');
INSERT INTO "ur_maududi" VALUES(38,28,'38:28','کیا ہم اُن لوگوں کو جو ایمان لاتے اور نیک عمل کرتے ہیں اور اُن کو جو زمین میں فساد کرنے والے ہیں یکساں کر دیں؟ کیا متّقیوں کو ہم فاجروں جیسا کر دیں؟ ');
INSERT INTO "ur_maududi" VALUES(38,29,'38:29','یہ ایک بڑی برکت والی کتاب ہے جو (اے محمدؐ)ہم نے تمہاری طرف نازل کی ہے تاکہ یہ لوگ اس کی آیات پر غور کریں اور عقل و فکر رکھنے والے اس سے سبق لیں');
INSERT INTO "ur_maududi" VALUES(38,30,'38:30','اور داوٴدؑ کو ہم نے سلیمانؑ (جیسا بیٹا)عطا کیا، بہترین بندہ، کثرت سے اپنے ربّ کی طرف رُجوع کرنے والا');
INSERT INTO "ur_maududi" VALUES(38,31,'38:31','قابلِ ذکر ہے وہ موقع جب شام کے وقت اس کے سامنے خوب سدھے ہوئے تیز رَو گھوڑے پیش کیے گئے ');
INSERT INTO "ur_maududi" VALUES(38,32,'38:32','تو اس نے کہا”میں نے اس مال کی محبّت اپنے ربّ کی یاد کی وجہ سے اختیار کی ہے۔“ یہاں تک کہ جب وہ گھوڑے نگاہ سے اوجھل ہوگئے');
INSERT INTO "ur_maududi" VALUES(38,33,'38:33','تو (اس نے حکم دیا کہ)انہیں میرے پاس واپس لاوٴ، پھر لگا ان کی پنڈلیوں اور گردنوں پر ہاتھ پھیرنے۔ ');
INSERT INTO "ur_maududi" VALUES(38,34,'38:34','اور (دیکھو کہ) سلیمانؑ کو بھی ہم نے آزمائش میں ڈالا اور اس کی کرسی پر ایک جسد لا کر ڈال دیا پھر اس نے رجوع کیا');
INSERT INTO "ur_maududi" VALUES(38,35,'38:35','اور کہا کہ”اے میرے ربّ ، مجھے معاف کر دے اور مجھے وہ بادشاہی دے جو میرے بعد کسی کے لیے سزاوار نہ ہو، بے شک تُو ہی اصل داتا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(38,36,'38:36','تب ہم نے اس کے لیے ہوا کو مسخر کر دیا جو اس کے حکم سے نرمی کے ساتھ چلتی تھی جدھر وہ چاہتا تھا');
INSERT INTO "ur_maududi" VALUES(38,37,'38:37','اور شیاطین کو مسخر کر دیا، ہر طرح کے معمار اور غوطہ خور');
INSERT INTO "ur_maududi" VALUES(38,38,'38:38','اور دُوسرے جو پابندِ سلاسل تھے۔ ');
INSERT INTO "ur_maududi" VALUES(38,39,'38:39','(ہم نے اُس سے کہا)”یہ ہماری بخشش ہے ، تجھے اختیار ہے جسے چاہے دے اور جس سے چاہے روک لے، کوئی حساب نہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(38,40,'38:40','یقیناً اُس کے لیے ہمارے ہاں تقرُّب کا مقام اور بہتر انجام ہے۔ ');
INSERT INTO "ur_maududi" VALUES(38,41,'38:41','اور ہمارے بندے ایّوبؑ کا ذکر کرو، جب اس نے اپنے ربّ کو پُکارا کہ شیطان نے مجھے سخت تکلیف اور عذاب میں ڈال دیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(38,42,'38:42','(ہم نے اُسے حکم دیا)اپنا پاوٴں زمین پر مار، یہ ہے ٹھنڈا پانی نہانے کے لیے اور پینے کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(38,43,'38:43','ہم نے اُسے اس کے اہل و عیال واپس دیے اور ان کے ساتھ اُتنے ہی اور ، اپنی طرف سے رحمت کے طور پر ، اور عقل و فکر رکھنے والوں کے لیے درس کے طور پر۔ ');
INSERT INTO "ur_maududi" VALUES(38,44,'38:44','(اور ہم نے اس سے کہا)تِنکوں کا ایک مُٹّھا لے اور اُس سے مار دے، اپنی قسم نہ توڑ۔ ہم نے اُسے صابر پایا، بہترین بندہ، اپنے رب کی طرف بہت رُجوع کرنے والا۔ ');
INSERT INTO "ur_maududi" VALUES(38,45,'38:45','اور ہمارے بندوں ، ابراہیمؑ اور اسحاقؑ اور یعقوبؑ کا ذکر کرو۔ بڑی قوّتِ عمل رکھنے والے اور دیدہ ور لوگ تھے۔ ');
INSERT INTO "ur_maududi" VALUES(38,46,'38:46','ہم نے اُن کو ایک خالص صفت کی بنا پر برگزیدہ کیا تھا، اور وہ دارِ آخرت کی یاد تھی۔ ');
INSERT INTO "ur_maududi" VALUES(38,47,'38:47','یقیناً ہمارے ہاں ان کا شمار چنے ہوئے نیک اشخاص میں ہے');
INSERT INTO "ur_maududi" VALUES(38,48,'38:48','اور اسماعیلؑ اور اَلیَسیع اور ذوالکِفل کا ذکر کرو، یہ سب نیک لوگوں میں سے تھے');
INSERT INTO "ur_maududi" VALUES(38,49,'38:49','یہ ایک ذکر تھا (اب سنو کہ) متقی لوگوں کے لیے یقیناً بہترین ٹھکانا ہے');
INSERT INTO "ur_maududi" VALUES(38,50,'38:50','ہمیشہ رہنے والی جنّتیں جن کے دروازے اُن کے لیے کھُلے ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(38,51,'38:51','ان میں وہ تکیے لگائے بیٹھے ہوں گے، خوب خوب فواکہ اور مشروبات طلب کر رہے ہوں گے');
INSERT INTO "ur_maududi" VALUES(38,52,'38:52','اور ان کے پاس شرمیلی ہم سِن بیویاں ہوں گی');
INSERT INTO "ur_maududi" VALUES(38,53,'38:53','یہ وہ چیزیں ہیں جنہیں حساب کے دن عطا کرنے کا تم سے وعدہ کیا جا رہا ہے');
INSERT INTO "ur_maududi" VALUES(38,54,'38:54','یہ ہمارا رزق ہے جو کبھی ختم ہونے والا نہیں');
INSERT INTO "ur_maududi" VALUES(38,55,'38:55','یہ تو ہے متقیوں کا انجام اور سرکشوں کے لیے بدترین ٹھکانا ہے');
INSERT INTO "ur_maududi" VALUES(38,56,'38:56','جہنم جس میں وہ جھلسے جائیں گے، بہت ہی بری قیام گاہ');
INSERT INTO "ur_maududi" VALUES(38,57,'38:57','یہ ہے اُن کے لیے ، پس وہ مزا چکھیں کھولتے ہوئے پانی اور پیپ لہُو ');
INSERT INTO "ur_maududi" VALUES(38,58,'38:58','اور اسی قسم کی دوسری تلخیوں کا');
INSERT INTO "ur_maududi" VALUES(38,59,'38:59','(وہ جہنم کی طرف اپنے پیروؤں کو آتے دیکھ کر آپس میں کہیں گے) "یہ ایک لشکر تمہارے پاس گھسا چلا آ رہا ہے، کوئی خوش آمدید اِن کے لیے نہیں ہے، یہ آگ میں جھلسنے والے ہیں"');
INSERT INTO "ur_maududi" VALUES(38,60,'38:60','وہ اُن کو جواب دیں گے "نہیں بلکہ تم ہی جھلسے جا رہے ہو، کوئی خیر مقدم تمہارے لیے نہیں تم ہی تو یہ انجام ہمارے آگے لائے ہو، کیسی بری ہے یہ جائے قرار"');
INSERT INTO "ur_maududi" VALUES(38,61,'38:61','پھر وہ کہیں گے "اے ہمارے رب، جس نے ہمیں اس انجام کو پہنچانے کا بندوبست کیا اُس کو دوزخ کا دوہرا عذاب دے"');
INSERT INTO "ur_maududi" VALUES(38,62,'38:62','اور وہ آپس میں کہیں گے ”کیابات ہے ، ہم اُن لوگوں کو کہیں نہیں دیکھتے جنہیں ہم دنیا میں بُرا سمجھتے تھے؟ ');
INSERT INTO "ur_maududi" VALUES(38,63,'38:63','ہم نے یونہی ان کا مذاق بنا لیا تھا، یا وہ کہیں نظروں سے اوجھل ہیں؟"');
INSERT INTO "ur_maududi" VALUES(38,64,'38:64','بے شک یہ بات سچی ہے، اہل دوزخ میں یہی کچھ جھگڑے ہونے والے ہیں');
INSERT INTO "ur_maududi" VALUES(38,65,'38:65',' (اے نبیؐ)اِن سے کہو”میں تو بس خبر دار کردینے والا ہوں۔ کوئی حقیقی معبُود نہیں مگر اللہ ، جو یکتا ہے، سب پر غالب');
INSERT INTO "ur_maududi" VALUES(38,66,'38:66','آسمانوں اور زمین کا مالک اور اُن ساری چیزوں کا مالک جو ان کے درمیان ہیں، زبردست اور درگزر کرنے والا"');
INSERT INTO "ur_maududi" VALUES(38,67,'38:67','اِن سے کہو "یہ ایک بڑی خبر ہے');
INSERT INTO "ur_maududi" VALUES(38,68,'38:68','جس کو سُن کر تم منہ پھیرتے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(38,69,'38:69','(ان سے کہو) "مجھے اُس وقت کی کوئی خبر نہ تھی جب ملاء اعلیٰ میں جھگڑا ہو رہا تھا');
INSERT INTO "ur_maududi" VALUES(38,70,'38:70','مجھ کو تو وحی کے ذریعہ سے یہ باتیں صرف اس لیے بتائی جاتی ہیں کہ میں کھلا کھلا خبردار کرنے والا ہوں"');
INSERT INTO "ur_maududi" VALUES(38,71,'38:71','جب تیرے ربّ نے فرشتوں سے کہا” میں مٹّی سے ایک بشر بنانے والا ہوں، ');
INSERT INTO "ur_maududi" VALUES(38,72,'38:72','پھر جب میں اسے پُوری طرح بناد وں اور اس میں اپنی رُوح پھُونک دوں تو تم اس کے آگے سجدے میں گِر جاوٴ۔“ ');
INSERT INTO "ur_maududi" VALUES(38,73,'38:73','اس حکم کے مطابق فرشتے سب کے سب سجدے میں گر گئے');
INSERT INTO "ur_maududi" VALUES(38,74,'38:74','مگر ابلیس نے اپنی بڑائی کا گھمنڈ کیا اور وہ کافروں میں سے ہوگیا۔ ');
INSERT INTO "ur_maududi" VALUES(38,75,'38:75','ربّ نے فرمایا”اے ابلیس، تجھے کیا چیز اُس کو سجدہ کرنے سے مانع ہوئی جسے میں نے اپنے دونوں ہاتھوں سے بنایا ہے؟ تُو بڑا بن رہا ہے یا تُو ہے ہی کچھ اُونچے درجے کی ہستیوں میں سے؟“');
INSERT INTO "ur_maududi" VALUES(38,76,'38:76','اُس نے جواب دیا "میں اُس سے بہتر ہوں، آپ نے مجھ کو آگ سے پیدا کیا ہے اور اس کو مٹی سے"');
INSERT INTO "ur_maududi" VALUES(38,77,'38:77','فرمایا”اچھا تو یہاں سے نکل جا، تُو مردُود ہے ');
INSERT INTO "ur_maududi" VALUES(38,78,'38:78','اور تیرے اُوپر یوم الجزا ء تک میری لعنت ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(38,79,'38:79','وہ بولا "ا ے میرے رب، یہ بات ہے تو پھر مجھے اُس وقت تک کے لیے مہلت دے دے جب یہ لوگ دوبارہ اٹھائے جائیں گے"');
INSERT INTO "ur_maududi" VALUES(38,80,'38:80','فرمایا، "اچھا، تجھے اُس روز تک کی مہلت ہے');
INSERT INTO "ur_maududi" VALUES(38,81,'38:81','جس کا وقت مجھے معلوم ہے"');
INSERT INTO "ur_maududi" VALUES(38,82,'38:82','اس نے کہا "تیری عزت کی قسم، میں اِن سب لوگو ں کو بہکا کر رہوں گا');
INSERT INTO "ur_maududi" VALUES(38,83,'38:83','بجز تیرے اُن بندوں کے جنہیں تُونے خالص کر لیا ہے۔ “');
INSERT INTO "ur_maududi" VALUES(38,84,'38:84','فرمایا "تو حق یہ ہے، اور میں حق ہی کہا کرتا ہوں');
INSERT INTO "ur_maududi" VALUES(38,85,'38:85','کہ میں جہنّم کو تجھ سے اور اُن سب لوگوں سے بھر دُوں گا جو اِن انسانوں میں سے تیری پیروی کریں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(38,86,'38:86','(ا ے نبیؐ)اِن سے کہہ دو کہ میں اس تبلیغ پر تم سے کوئی اجر نہیں مانگتا، اور نہ میں بناوٹی لوگوں میں سے ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(38,87,'38:87','یہ تو ایک نصیحت ہے تمام جہان والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(38,88,'38:88','اور تھوڑی مدّت ہی گزرے گی کہ تمہیں اس کا حال خود معلوم ہو جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(39,1,'39:1','اِس کتاب کا نزول اللہ زبردست اور دانا کی طرف سے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(39,2,'39:2','(اے محمدؐ)یہ کتاب ہم نے تمہاری طرف برحق نازل کی ہے ، لہٰذا تم اللہ ہی کی بندگی کرو دین کو اُسی کی لیے خالص کرتے ہوئے۔ ');
INSERT INTO "ur_maududi" VALUES(39,3,'39:3','خبردار ، دین خالص اللہ کا حق ہے۔ رہے وہ لوگ جنہوں نے اُس کے سوا دُوسرے سرپرست بنا رکھے ہیں (اور اپنے اِس فعل کی توجیہ یہ کرتے ہیں کہ)ہم تو اُن کی عبادت صرف اس لیے کرتے ہیں کہ وہ اللہ تک ہماری رسائی کرا دیں، اللہ یقیناً اُن کے درمیان اُن تمام باتوں کا فیصلہ کر دے گا جن میں وہ اختلاف کر رہے ہیں۔ اللہ کسی ایسے شخص کو ہدایت نہیں دیتا جو جھُوٹا اور منکرِ حق ہو۔ ');
INSERT INTO "ur_maududi" VALUES(39,4,'39:4','اگر اللہ کسی کو بیٹا بنانا چاہتا تو اپنی مخلوق میں سے جس کو چاہتا برگزیدہ کر لیتا، پاک ہے وہ اس سے (کہ کوئی اُس کا بیٹا ہو)، وہ اللہ ہے اکیلا اور سب پر غالب۔ ');
INSERT INTO "ur_maududi" VALUES(39,5,'39:5','اس نے آسمانوں اور زمین کو بر حق پیدا کیا ہے۔ وہی دن پر رات اور رات پر دن کو لپیٹتا ہے۔ اُسی نے سُورج اور چاند کو اس طرح مسخّر کر رکھا ہے کہ ہر ایک ایک وقتِ مقرر تک چلے جا رہا ہے۔ جان رکھو، وہ زبردست ہے اور درگزر کرنے والا ہے ');
INSERT INTO "ur_maududi" VALUES(39,6,'39:6','اُسی نے تم کو ایک جان سے پیدا کیا، پھر وہی جس نے اُس جان سے اس کا جوڑا بنایا۔ اور اسی نے تمہارے لیے مویشیوں میں سے آٹھ نر و مادہ پیدا کیے۔ وہ تمہاری ماوٴں کے پیٹوں میں تین تین تاریک پردوں کے اندر تمہیں ایک کے بعد ایک شکل دیتا چلا جاتا ہے۔ یہی اللہ (جس کے یہ کام ہیں)تمہارا ربّ ہے، بادشاہی اسی کی ہے، کوئی معبُود اس کے سوا نہیں ہے، پھر تم کدھر سے پھرائے جا رہے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(39,7,'39:7','اگر تم کُفر کرو تو اللہ تم سے بے نیاز ہے ، لیکن وہ اپنے بندوں کے لیے کُفر کو پسند نہیں کرتا، اور اگر تم شکر کرو تو اسے وہ تمہارے لیے پسند کرتا ہے۔ کوئی بوجھ اُٹھانے والا کسی دُوسرے کا بوجھ نہ اُٹھائے گا۔ آخر کار تم سب کو اپنے ربّ کی طرف پلٹنا ہے ، پھر وہ تمہیں بتا دے گا کہ تم کیا کرتے رہے ہو، وہ تو دلوں کا حال تک جانتا ہے');
INSERT INTO "ur_maududi" VALUES(39,8,'39:8','انسان پر جب کوئی آفت آتی ہے تو وہ اپنے ربّ کی طرف رُجوع کر کے اُسے پکارتا ہے ۔ پھر جب اس کا ربّ اسے اپنی نعمت سے نواز دیتا ہے تو وہ اُس مصیبت کو بھُول جاتا ہے جس پر وہ پہلے پکار رہا تھا اور دُوسروں کو اللہ کا ہمسر ٹھہراتا ہے تاکہ اُس کی راہ سے گمراہ کرے۔ (اے نبیؐ)اُس سے کہو کہ تھوڑے دن اپنے کُفر سے لُطف اُٹھا لے، یقیناً تُو دوزخ میں جانے والا ہے');
INSERT INTO "ur_maududi" VALUES(39,9,'39:9','(کیا اِس شخص کی روش بہتر ہے یا اُس شخص کی)جو مطیعِ فرمان ہے ، رات کی گھڑیوں میں کھڑا رہتا اور سجدے کرتا ہے، آخرت سے ڈرتا اور اپنے ربّ کی رحمت سے اُمید لگاتا ہے؟ اِن سے پوچھو ، کیا جاننے والے اور نہ جاننے والے دونوں کبھی یکساں ہو سکتے ہیں؟ نصیحت تو عقل رکھنے والے ہی قبول کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(39,10,'39:10','(اے نبیؐ)کہو کہ اے میرے بندو جو ایمان لائے ہو ، اپنے ربّ سے ڈرو۔ جن لوگوں نے اِس دنیا میں نیک رویّہ اختیار کیا ہے ان کے لیے بھلائی ہے۔ اور خدا کی زمین وسیع ہے، صبر کرنے والوں کو تو ان کا اجر بے حسا ب دیا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(39,11,'39:11','(اے نبیؐ) اِن سے کہو، مجھے حکم دیا گیا ہے کہ دین کو اللہ کے لیے خالص کر کے اُس کی بندگی کروں');
INSERT INTO "ur_maududi" VALUES(39,12,'39:12','اور مجھے حکم دیا گیا ہے کہ سب سے پہلے میں خود مُسلم بنوں۔ ');
INSERT INTO "ur_maududi" VALUES(39,13,'39:13','کہو، اگر میں اپنے رب کی نافرمانی کروں تو مجھے ایک بڑے دن کے عذاب کا خوف ہے');
INSERT INTO "ur_maududi" VALUES(39,14,'39:14','کہہ دو کہ میں تو اپنے دین کو اللہ کے لیے خالص کر کے اُسی کی بندگی کروں گا');
INSERT INTO "ur_maududi" VALUES(39,15,'39:15','تم اُس کے سوا جس جس کی بندگی کرنا چاہو کرتے رہو۔ کہو، اصل دیوالیے تو وہی ہیں جنہوں نے قیامت کے روز اپنے آپ کو اور اپنے اہل و عیال کو گھاٹے میں ڈال دیا۔ خوب سُن رکھو، یہی کھُلا دیوالہ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(39,16,'39:16','اُن پر آگ کی چھتریاں اوپر سے بھی چھائی ہوں گی اور نیچے سے بھی یہ وہ انجام ہے جس سے اللہ اپنے بندوں کو ڈراتا ہے، پس اے میرے بندو، میرے غضب سے بچو');
INSERT INTO "ur_maududi" VALUES(39,17,'39:17','بخلاف اس کے جن لوگوں نے طاغوت کی بندگی سے اجتناب کیا اور اللہ کی طرف رُجوع کر لیا اُن کے لیے خوشخبری ہے۔ پس (اے نبی ؐ)بشارت دے دو میرے اُن بندوں کو');
INSERT INTO "ur_maududi" VALUES(39,18,'39:18','جو بات کو غور سے سُنتے ہیں اور اس کے بہترین پہلو کی پیروی کرتے ہیں۔ یہ وہ لوگ ہیں جن کو اللہ نے ہدایت بخشی ہے اور یہی دانشمند ہیں');
INSERT INTO "ur_maududi" VALUES(39,19,'39:19','(اے نبیؐ)اُس شخص کو کون بچا سکتا ہے جس پر عذاب کا فیصلہ چَسپَاں ہو چکا ہو؟ کیا تم اُسے بچا سکتے ہو جو آگ میں گر چکا ہوِ');
INSERT INTO "ur_maududi" VALUES(39,20,'39:20','البتہ جو لوگ اپنے رب سے ڈر کر رہے اُن کے لیے بلند عمارتیں ہیں منزل پر منزل بنی ہوئی، جن کے نیچے نہریں بہہ رہی ہوں گی یہ اللہ کا وعدہ ہے، اللہ کبھی اپنے وعدے کی خلاف ورزی نہیں کرتا');
INSERT INTO "ur_maududi" VALUES(39,21,'39:21','کیا تم نہیں دیکھتے کہ اللہ نے آسمان سے پانی برسایا ، پھر اس کو سوتوں اور چشموں اور دریاوٴں کی شکل میں زمین کے اندر جاری کیا، پھر اس پانی کے ذریعہ سے وہ طرح طرح کی کھیتیاں نکالتا ہے جن کی قسمیں مختلف ہیں، پھر وہ کھیتیاں پک کر سُوکھ جاتی ہیں، پھر تم دیکھتے ہو کہ وہ زرد پڑ گئیں ، پھر آخر کار اللہ اُن کو بھُس بنا دیتا ہے۔ درحقیقت اِس میں ایک سبق ہے عقل رکھنے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(39,22,'39:22','اب کیا وہ شخص جس کا سینہ اللہ نے اسلام کے لیے کھول دیا اور وہ اپنے ربّ کی طرف سے ایک روشنی پر چل رہا ہے (اُس شخص کی طرح ہو سکتا ہے جس نے اِن باتوں سے کوئی سبق نہ لیا ؟)۔ تباہی ہے اُن لوگوں کے لے جن کے دل اللہ کی نصیحت سے اور زیادہ سخت ہو گئے۔ وہ کھُلی گمراہی میں پڑے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(39,23,'39:23','اللہ نے بہترین کلام اُتارا ہے، ایک ایسی کتاب جس کے تمام اجزا ہمرنگ ہیں اور جس میں بار بار مضامین دہرائے گئے ہیں۔ اُسے سُن کر اُن لوگوں کے رونگٹے کھڑے ہو جاتے ہیں جو اپنے ربّ سے ڈرنے والے ہیں، اور پھر ان کے جسم اور ان کے دل نرم ہو کر اللہ کے ذکر کی طرف راغب ہو جاتے ہیں۔ یہ اللہ کی ہدایت ہے جس سے وہ راہِ راست پر لے آتا ہے جسے چاہتا ہے۔ اور جسے اللہ ہی ہدایت نہ دے اس کے لیے پھر کوئی ہادی نہیں ہے');
INSERT INTO "ur_maududi" VALUES(39,24,'39:24','اب اُس شخص کی بدحالی کا تم کیا اندازہ کر سکتے ہو جو قیامت کے روز عذاب کی سخت مار اپنے منہ پر لے گا؟ ایسے ظالموں سے کہہ دیا جائے گا کہ اب چکھو مزہ اُس کمائی کا جو تم کرتے رہے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(39,25,'39:25','اِن سے پہلے بھی بہت سے لوگ اسی طرح جھٹلا چکے ہیں آخر اُن پر عذاب ایسے رخ سے آیا جدھر ان کا خیال بھی نہ جا سکتا تھا');
INSERT INTO "ur_maududi" VALUES(39,26,'39:26','پھر اللہ نے ان کو دنیا ہی کی زندگی میں رسوائی کا مزہ چکھایا، اور آخرت کا عذاب تو اس سے شدید تر ہے، کاش یہ لوگ جانتے');
INSERT INTO "ur_maududi" VALUES(39,27,'39:27','ہم نے اِس قرآن میں لوگوں کو طرح طرح کی مثالیں دی ہیں کہ یہ ہوش میں آئیں');
INSERT INTO "ur_maududi" VALUES(39,28,'39:28','ایسا قرآن جو عربی زبان میں ہے ، جس میں کوئی ٹیڑھ نہیں ہے ، تاکہ یہ بُرے انجام سے بچیں');
INSERT INTO "ur_maududi" VALUES(39,29,'39:29','اللہ ایک مثال دیتا ہے۔ ایک شخص تو وہ ہے جس کی ملکیّت میں بہت سے کج خُلق آقا شریک ہیں جو اسے اپنی اپنی طرف کھینچتے ہیں اور دُوسرا شخص پُورا کا پُورا ایک ہی آقا کا غلام ہے۔ کیا دونوں کا حال یکساں ہو سکتا ہے؟ ۔۔۔۔ الحمدُ للہ، مگر اکثر لوگ نادانی میں پڑے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(39,30,'39:30','(اے نبیؐ)تمہیں بھی مرنا ہے اور اِن لوگوں کو بھی مرنا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(39,31,'39:31','آخرکار قیامت کے روز تم سب اپنے رب کے حضور اپنا اپنا مقدمہ پیش کرو گے');
INSERT INTO "ur_maududi" VALUES(39,32,'39:32','پھر اُس شخص سے بڑا ظالم اور کون ہو گا جس نے اللہ پر جھوٹ باندھا اور جب سچائی اس کے سامنے آئی تو اُسے جھٹلا دیا کیا ایسے کافروں کے لیے جہنم میں کوئی ٹھکانا نہیں ہے؟');
INSERT INTO "ur_maududi" VALUES(39,33,'39:33','اور جو شخص سچائی لے کر آیا اور جنہوں نے اُس کو سچ مانا، وہی عذاب سے بچنے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(39,34,'39:34','انہیں اپنے ربّ کے ہاں وہ سب کچھ ملے گا جس کی وہ خواہش کریں گے۔ یہ ہے نیکی کرنے والوں کی جزا');
INSERT INTO "ur_maududi" VALUES(39,35,'39:35','تاکہ جو بدترین اعمال انہوں نے کیے تھے انہیں اللہ ان کے حساب سے ساقط کر دے اور جو بہترین اعمال وہ کرتے رہے اُن کے لحاظ سے اُن کو اجر عطا فرمائے۔ ');
INSERT INTO "ur_maududi" VALUES(39,36,'39:36','(اے نبیؐ)کیا اللہ اپنے بندے کے لیے کافی نہیں ہے؟ یہ لوگ اُس کے سوا دُوسروں سے تم کو ڈراتےہیں۔ حالانکہ اللہ جسے گمراہی میں ڈال دے اُسے کوئی راستہ دکھانے والا نہیں');
INSERT INTO "ur_maududi" VALUES(39,37,'39:37','اور جسے وہ ہدایت دے اُسے بھٹکانے والا بھی کوئی نہیں، کیا اللہ زبردست اور انتقام لینے والا نہیں ہے؟ ');
INSERT INTO "ur_maududi" VALUES(39,38,'39:38','اِن لوگوں سے اگر تم پوچھو کہ زمین اور آسمانوں کو کس نے پیدا کیا ہے تو یہ خود کہیں گے کہ اللہ نے۔ اِن سے کہو، جب حقیقت یہ ہے تو تمہارا کیا خیال ہے کہ اگر اللہ مجھے کوئی نقصان پہنچانا چاہے تو یا تمہاری یہ دیویاں ، جنہیں تم اللہ کو چھوڑ کر پکارتے ہو، مجھے اُس کے پہنچائے ہوئے نقصان سے بچا لیں گی؟ یا اللہ مجھ پر مہربانی کرنا چاہے تو کیا یہ اُس کی رحمت کو روک سکیں گی؟ بس ان سے کہہ دو کہ میرے لیے اللہ ہی کافی ہے، بھروسہ کرنے والے اُسی پر بھروسہ کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(39,39,'39:39','ان سے صاف کہو کہ”اے میری قوم کے لوگو، تم اپنی جگہ اپنا کام کیے جاوٴ ، میں اپنا کام کرتا رہوں گا، عنقریب تمہیں معلوم ہوجائے گا');
INSERT INTO "ur_maududi" VALUES(39,40,'39:40','کہ کس پر رسوا کن عذاب آتا ہے اور کسے وہ سزا ملتی ہے جو کبھی ٹلنے والی نہیں"');
INSERT INTO "ur_maududi" VALUES(39,41,'39:41','(اے نبیؐ)ہم نے سب انسانوں کے لیے کتابِ برحق تم پر نازل کر دی ہے۔ اب جو سیدھا راستہ اختیار کرے گا اپنے لیے کرے گا اور جو بھٹکے گا اُس کے بھٹکنے کا وبال اُسی پر ہوگا، تم اُن کے ذمّہ دار نہیں ہو۔ ');
INSERT INTO "ur_maududi" VALUES(39,42,'39:42','وہ اللہ ہی ہے جو موت کے وقت رُوحیں قبض کرتا ہے اور جو ابھی نہیں مرا ہے اُس کی رُوح نیند میں قبض کر لیتا ہے، پھر جس پر وہ موت کا فیصلہ نافذ کرتا ہے اُسے روک لیتا ہے اور دُوسروں کی رُوحیں ایک وقتِ مقرر کے لیے واپس بھیج دیتا ہے۔ اِس میں بڑی نشانیاں ہیں اُن لوگوں کے لیے جو غور و فکر کرنے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(39,43,'39:43','کیا اُس خدا کو چھوڑ کر اِن لوگوں نے دُوسروں کو شفیع بنا رکھا ہے؟ ان سے کہو، کیا وہ شفاعت کریں گے خواہ اُن کے اختیار میں کچھ نہ ہو اور وہ سمجھتے بھی نہ ہوں؟');
INSERT INTO "ur_maududi" VALUES(39,44,'39:44','کہو، شفاعت ساری کی ساری اللہ کے اختیار میں ہے۔ آسمانوں اور زمین کی بادشاہی کا وہی مالک ہے۔ پھر اُسی کی طرف تم پلٹائے جانے والے ہو');
INSERT INTO "ur_maududi" VALUES(39,45,'39:45','جب اکیلے اللہ کا ذکر کیا جاتا ہے تو آخرت پر ایمان نہ رکھنے والوں کے دل کُڑھنے لگتے ہیں، اور جب اُس کے سوا دُوسروں کا ذکر ہوتا ہے تو یکایک وہ خوشی سے کھِل اُٹھتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(39,46,'39:46','کہو، خدایا! آسمانوں اور زمین کے پیدا کرنے والے، حاضر و غائب کے جاننے والے، تو ہی اپنے بندوں کے درمیان اُس چیز کا فیصلہ کرے گا جس میں وہ اختلاف کرتے رہے ہیں');
INSERT INTO "ur_maududi" VALUES(39,47,'39:47','اگر اِن ظالموں کے پاس زمین کی ساری دولت بھی ہو، اور اتنی ہی اور بھی، تو یہ روز قیامت کے برے عذاب سے بچنے کے لیے سب کچھ فدیے میں دینے کے لیے تیار ہو جائیں گے وہاں اللہ کی طرف سے ان کے سامنے وہ کچھ آئے گا جس کا انہوں نے کبھی اندازہ ہی نہیں کیا ہے');
INSERT INTO "ur_maududi" VALUES(39,48,'39:48','وہاں اپنی کمائی کے سارے برے نتائج ان پر کھل جائیں گے اور وہی چیز ان پر مسلط ہو جائے گی جس کا یہ مذاق اڑاتے رہے ہیں');
INSERT INTO "ur_maududi" VALUES(39,49,'39:49','یہی انسان جب ذرا سی مصیبت اِسے چھُوجاتی ہے تو ہمیں پُکارتا ہے، اور جب ہم اسے اپنی طرف سے نعمت دے کر اَپھار دیتے ہیں تو کہتا ہے کہ یہ تو مجھے علم کی بنا پر دیا گیا ہے! نہیں، بلکہ یہ آزمائش ہے ، مگر ان میں سے اکثر لوگ جانتے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(39,50,'39:50','یہی بات اِن سے پہلے گزرے ہوئے لوگ بھی کہہ چکے ہیں ، مگر جو کچھ وہ کماتے تھے وہ ان کے کسی کام نہ آیا۔ ');
INSERT INTO "ur_maududi" VALUES(39,51,'39:51','پھر اپنی کمائی کے برے نتائج انہوں نے بھگتے، اور اِن لوگوں میں سے بھی جو ظالم ہیں وہ عنقریب اپنی کمائی کے برے نتائج بھگتیں گے، یہ ہمیں عاجز کر دینے والے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(39,52,'39:52','اور کیا انہیں معلوم نہیں ہے کہ اللہ جس کا چاہتا ہے رزق کشادہ کر دیتا ہے اور جس کا چاہتا ہے تنگ کر دیتا ہے؟ اس میں نشانیاں ہیں اُن لوگوں کے لیے جو ایمان لاتے ہیں۔');
INSERT INTO "ur_maududi" VALUES(39,53,'39:53','(اے نبیؐ)کہہ دو کہ اے میرے بندو، جنہوں نے اپنی جانوں پر زیادتی کی ہے ، اللہ کی رحمت سے مایوس نہ ہو جاوٴ، یقیناً اللہ سارے گناہ معاف کر دیتا ہے، وہ تو غفورٌ رحیم ہے، ');
INSERT INTO "ur_maududi" VALUES(39,54,'39:54','پلٹ آؤ اپنے رب کی طرف اور مطیع بن جاؤ اُس کے قبل اِس کے کہ تم پر عذاب آ جائے اور پھر کہیں سے تمہیں مدد نہ مل سکے');
INSERT INTO "ur_maududi" VALUES(39,55,'39:55','اور پیروی اختیار کر لو اپنے ربّ کی بھیجی ہوئی کتاب کے بہترین پہلو کی ، قبل اِس کے کہ تم پر اچانک عذاب آئے اور تم کو خبر بھی نہ ہو');
INSERT INTO "ur_maududi" VALUES(39,56,'39:56','کہیں ایسا نہ ہو کہ بعد میں کوئی شخص کہے "افسوس میری اُس تقصیر پر جو میں اللہ کی جناب میں کرتا رہا، بلکہ میں تو الٹا مذاق اڑانے والوں میں شامل تھا"');
INSERT INTO "ur_maududi" VALUES(39,57,'39:57','یا کہے "کاش اللہ نے مجھے ہدایت بخشی ہوتی تو میں بھی متقیوں میں سے ہوتا"');
INSERT INTO "ur_maududi" VALUES(39,58,'39:58','یا عذاب دیکھ کر کہے "کاش مجھے ایک موقع اور مل جائے اور میں بھی نیک عمل کرنے والوں میں شامل ہو جاؤں"');
INSERT INTO "ur_maududi" VALUES(39,59,'39:59','(اور اُس وقت اسے یہ جواب ملے کہ) "کیوں نہیں، میری آیات تیرے پاس آ چکی تھیں، پھر تو نے انہیں جھٹلایا اور تکبر کیا اور تو کافروں میں سے تھا"');
INSERT INTO "ur_maududi" VALUES(39,60,'39:60','آج جن لوگوں نے خدا پر جھوٹ باندھے ہیں قیامت کے روز تم دیکھو گے کہ ان کے منہ کالے ہوں گے کیا جہنم میں متکبروں کے لیے کافی جگہ نہیں ہے؟');
INSERT INTO "ur_maududi" VALUES(39,61,'39:61','اس کے برعکس جن لوگوں نے یہاں تقویٰ کیا ہے ان کے اسباب کامیابی کی وجہ سے اللہ ان کو نجات دے گا، ان کو نہ کوئی گزند پہنچے گا اور نہ وہ غمگین ہوں گے');
INSERT INTO "ur_maududi" VALUES(39,62,'39:62','اللہ ہر چیز کا خالق ہے اور وہی ہر چیز پر نگہبان ہے۔ ');
INSERT INTO "ur_maududi" VALUES(39,63,'39:63','زمین اور آسمانوں کے خزانوں کی کنجیاں اُسی کے پاس ہیں اور جو لوگ اللہ کی آیات سے کفر کرتے ہیں وہی گھاٹے میں رہنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(39,64,'39:64','(اے نبیؐ) اِن سے کہو "پھر کیا اے جاہلو، تم اللہ کے سوا کسی اور کی بندگی کرنے کے لیے مجھ سے کہتے ہو؟"');
INSERT INTO "ur_maududi" VALUES(39,65,'39:65','(یہ بات تمہیں ان سے صاف کہہ دینی چاہیے کیونکہ)تمہاری طرف اور تم سے پہلے گزرے ہوئے تمام انبیاء کی طرف یہ وحی بھیجی جا چکی ہے کہ اگر تم نے شرک کیا تو تمہارا عمل ضائع ہو جائے گا اور تم خسارے میں رہو گے');
INSERT INTO "ur_maududi" VALUES(39,66,'39:66','لہٰذا (اے نبیؐ) تم بس اللہ ہی کی بندگی کرو اور شکر گزار بندوں میں سے ہو جاؤ');
INSERT INTO "ur_maududi" VALUES(39,67,'39:67','اِن لوگوں نے اللہ کی قدر ہی نہ کی جیسا کہ اس کی قدر کرنے کا حق ہے۔ (اس کی قدرتِ کاملہ کا حال تو یہ ہے کہ)قیامت کے روز پُوری زمین اُس کی مٹھی میں ہوگی اور آسمان اس کے دستِ راست میں لپٹے ہوئے ہوں گے۔ پاک اور بالاتر ہے وہ اس شرک سے جو یہ لوگ کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(39,68,'39:68','اور اُس روز صُور پھُونکا جائے گا اور وہ سب مر کر گر جائیں گے جو آسمانوں اور زمین میں ہیں سوائے اُن کے جنہیں اللہ زندہ رکھنا چاہے۔ پھر ایک دُوسرا صُور پھونکا جائے گا اور یکایک سب کے سب اُٹھ کر دیکھنے لگیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(39,69,'39:69','زمین اپنے ربّ کے نُور سے چمک اُٹھے گی ، کتابِ اعمال لا کر رکھ دی جائے گی ، انبیاء اور تمام گواہ حاضر کر دیے جائیں گے ، لوگوں کے درمیان ٹھیک ٹھیک حق کے ساتھ فیصلہ کر دیا جائے گا اور اُن پر کوئی ظلم نہ ہوگا');
INSERT INTO "ur_maududi" VALUES(39,70,'39:70','اور ہر متنفس کو جو کچھ بھی اُس نے عمل کیا تھا اُس کا پورا پورا بدلہ دے دیا جائے گا لوگ جو کچھ بھی کرتے ہیں اللہ اس کو خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(39,71,'39:71','(اِس فیصلہ کے بعد)وہ لوگ جنہوں نے کُفر کیا تھا جہنّم کی طرف گروہ در گروہ ہانکے جائیں گے، یہاں تک کہ جب وہ وہاں پہنچیں گے تو اس کے دروازے کھولے جائیں گے اور اُس کے کارندے ان سے کہیں گے”کیا تمہارے پاس تمہارے اپنے لوگوں میں سے ایسے رسُول نہیں آئے تھے ، جنہوں نے تم کو تمہارے ربّ کی آیات سُنائی ہوں اور تمہیں اِس بات سے ڈرایا ہو کہ ایک وقت تمہیں یہ دن بھی دیکھنا ہوگا؟“ وہ جواب دیں گے”ہاں ، آئے تھے، مگر عذاب کا فیصلہ کافروں پر چِپک گیا۔“');
INSERT INTO "ur_maududi" VALUES(39,72,'39:72','کہا جائے گا، داخل ہو جاؤ جہنم کے دروازوں میں، یہاں اب تمہیں ہمیشہ رہنا ہے، بڑا ہی برا ٹھکانا ہے یہ متکبروں کے لیے');
INSERT INTO "ur_maududi" VALUES(39,73,'39:73','اور جو لوگ اپنے رب کی نافرمانی سے پرہیز کرتے تھے انہیں گروہ در گروہ جنت کی طرف لے جایا جائے گا یہاں تک کہ جب وہ وہاں پہنچیں گے، اور اس کے دروازے پہلے ہی کھولے جا چکے ہوں گے، تو اُس کے منتظمین ان سے کہیں گے کہ "سلام ہو تم پر، بہت اچھے رہے، داخل ہو جاؤ اس میں ہمیشہ کے لیے"');
INSERT INTO "ur_maududi" VALUES(39,74,'39:74','اور وہی کہیں گے”شکر ہے اُس خدا کا جس نے ہمارے ساتھ اپنا وعدہ سچا کر دکھایا اور ہم کو زمین کا وارث بنا دیا، اب ہم جنّت میں جہاں چاہیں اپنی جگہ بنا سکتے ہیں۔“ پس بہترین اجر ہے عمل کرنے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(39,75,'39:75','اور تم دیکھو گے کہ فرشتے عرش کے گر د حلقہ بنائے ہوئے اپنے ربّ کی حمد اور تسبیح کر رہے ہونگے، اور لوگوں کے درمیان ٹھیک ٹھیک حق کے ساتھ فیصلہ چُکا دیا جائے گا ، اور پُکار دیا جائے گا کہ حمد ہے اللہ ربّ العالمین کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(40,1,'40:1','ح م');
INSERT INTO "ur_maududi" VALUES(40,2,'40:2','اِس کتاب کا نزول اللہ کی طرف سے ہے جو زبردست ہے، سب کچھ جاننے والا ہے');
INSERT INTO "ur_maududi" VALUES(40,3,'40:3','گناہ معاف کرنے والا اور توبہ قبول کرنے والا ہے، سخت سزا دینے والا اور بڑا صاحبِ فضل ہے۔ کوئی معبُود اس کے سوا نہیں، اُسی کی طرف سب کو پلٹنا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(40,4,'40:4','اللہ کی آیات میں جھگڑے نہیں کرتے مگر صرف وہ لوگ جنہوں نے کُفر کیا ہے۔ اِس کے بعد دنیا کے ملکوں میں اُن کی چَلَت پھِرَت تمہیں کسی دھوکے میں نہ ڈالے۔ ');
INSERT INTO "ur_maududi" VALUES(40,5,'40:5','اِن سے پہلے نوحؑ کی قوم بھی جھٹلا چکی ہے، اور اُس کے بعد بہت سے دوسرے جتھوں نے بھی یہ کام کیا ہے ہر قوم اپنے رسول پر جھپٹی تاکہ اُسے گرفتار کرے اُن سب نے باطل کے ہتھیاروں سے حق کو نیچا دکھانے کی کوشش کی مگر آخر کار میں نے ان کو پکڑ لیا، پھر دیکھ لو کہ میری سزا کیسی سخت تھی');
INSERT INTO "ur_maududi" VALUES(40,6,'40:6','اِسی طرح تیرے ربّ کا یہ فیصلہ بھی اُن سب لوگوں پر چسپاں ہو چکا ہے جو کُفر کے مرتکب ہوئے ہیں کہ وہ واصلِ جہنّم ہونے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(40,7,'40:7','عرشِ الہٰی کے حامل فرشتے ، اور وہ جو عرش کے گردو پیش حاضر رہتے ہیں، سب اپنے ربّ کی حمد کے ساتھ اس کی تسبیح کر رہے ہیں۔ وہ اس پر ایمان رکھتے ہیں اور ایمان لانے والوں کے حق میں دُعائے مغفرت کرتے ہیں۔ وہ کہتے ہیں”اے ہمارے ربّ ، تُو اپنی رحمت اور اپنے علم کے ساتھ ہر چیز پر چھایا ہوا ہے، پس معاف کردے اور عذابِ دوزخ سے بچالے اُن لوگوں کو جنہوں نے توبہ کی ہے اور تیرا راستہ اختیار کر لیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(40,8,'40:8','اے ہمارے ربّ، اور داخل کر اُن کو ہمیشہ رہنے والی اُن جنّتوں میں جن کا تُو نے اُن سے وعدہ کیا ہے ، اور اُن کے والدین اور بیویوں اور اولاد میں سے جو صالح ہوں (اُن کو بھی وہاں اُن کے ساتھ ہی پہنچا دے )۔ تُو بلاشبہ قادرِ مطلق اور حکیم ہے');
INSERT INTO "ur_maududi" VALUES(40,9,'40:9','اور بچا دے اُن کو برائیوں سے ۔ جس کو تُو نے قیامت کے دن بُرائیوں سے بچا دیا اُس پر تُو نے بڑا رحم کیا ، یہی بڑی کامیابی ہے۔“');
INSERT INTO "ur_maududi" VALUES(40,10,'40:10','جن لوگوں نے کُفر کیا ہے ، قیامت کے روز اُن کو پکار کر کہا جائے گا”آج تمہیں جتنا شدید غصّہ اپنے اُوپر آرہا ہے، اللہ تم پر اُس سے زیادہ غضب ناک اُس وقت ہوتا تھا جب تمہیں ایمان کی طرف بلایا جاتا تھا اور تم کُفر کرتے تھے۔“ ');
INSERT INTO "ur_maududi" VALUES(40,11,'40:11','وہ کہیں گے”اے ہمارے ربّ ، تُو نے واقعی ہمیں دو دفعہ موت اور دو دفعہ زندگی دے دی، اب ہم اپنے قصُوروں کا اعتراف کرتے ہیں، کیا اب یہاں سے نکلنے کی بھی کوئی سبیل ہے؟“ ');
INSERT INTO "ur_maududi" VALUES(40,12,'40:12','(جواب ملے گا)”یہ حالت جس میں تم مبتلا ہو، اس وجہ سے ہے کہ جب اکیلے اللہ کی طرف بُلایا جاتا تھا تو تم ماننے سے انکار کر دیتےتھے اور جب اُس کے ساتھ دُوسروں کو مِلایا جاتا تو تُم مان لیتے تھے۔ اب فیصلہ اللہ بزرگ و برتر کے ہاتھ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(40,13,'40:13','وہی ہے جو تم کو اپنی نشانیاں دکھاتا ہے اور آسمان سے تمہارےلیے رزق نازل کرتا ہے، مگر (اِن نشانیوں کے مشاہدے سے)سبق صرف وہی شخص لیتا ہے جو اللہ کی طرف رجوع کرنے والا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(40,14,'40:14','(پس اے رجوع کرنے والو)اللہ ہی کو پکارو اپنے دین کو اُس کے لیے خالص کر کے، خواہ تمہارا یہ فعل کافروں کو کتنا ہی ناگوار ہو');
INSERT INTO "ur_maududi" VALUES(40,15,'40:15','وہ بلند درجوں والا، مالکِ عرش ہے۔ اپنے بندوں میں سے جس پر چاہتا ہے اپنے حکم سے رُوح نازل کردیتا ہے تاکہ وہ ملاقات کے دن سے خبردار کر دے');
INSERT INTO "ur_maududi" VALUES(40,16,'40:16','وہ دن جبکہ سب لوگ بے پردہ ہوں گے، اللہ سے ان کی کوئی بات بھی چھُپی ہوئی نہ ہوگی۔ (اُس روز پکار کر پُوچھا جائے گا)آج بادشاہی کس کی ہے؟ (سارا عالم پکار اُٹھے گا)اللہ واحد قہّار کی');
INSERT INTO "ur_maududi" VALUES(40,17,'40:17','(کہا جائے گا)آج ہر متنفّس کو اُس کی کمائی کا بدلہ دیا جائے گا جو اس نے کی تھی۔ آج کسی پر کوئی ظلم نہ ہوگا۔ اور اللہ حساب لینے میں بہت تیز ہے۔ ');
INSERT INTO "ur_maududi" VALUES(40,18,'40:18','اے نبیؐ ، ڈرا دو اِن لوگوں کو اُس دن سے جو قریب آلگا ہے۔ جب کلیجے مُنہ کو آرہے ہوں گے اور لوگ چُپ چاپ غم کے گھُونٹ پیے کھڑے ہوں گے۔ ظالموں کا نہ کوئی مشفق دوست ہوگا اور نہ کوئی شفیع جس کی بات مانی جائے۔ ');
INSERT INTO "ur_maududi" VALUES(40,19,'40:19','اللہ نگاہوں کی چوری تک سے واقف ہے اور وہ راز تک جانتا ہے جو سینوں نے چھُپا رکھے ہیں');
INSERT INTO "ur_maududi" VALUES(40,20,'40:20','اور اللہ ٹھیک ٹھیک بےلاگ فیصلہ کر ے گا۔ رہے وہ جن کو (یہ مشرکین)اللہ کو چھوڑ کر پکارتے ہیں ، وہ کسی چیز کا بھی فیصلہ کرنے والے نہیں ہیں۔ بلاشبہ اللہ ہی سب کچھ سُننے اور دیکھنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(40,21,'40:21','کیا یہ لوگ کبھی زمین میں چلے پھرے نہیں ہیں کہ انہیں اُن لوگوں کا انجام نظر آتا جو اِن سے پہلے گزر چکے ہیں؟ وہ اِن زیادہ طاقت ور تھے اور ان سے زیادہ زبردست آثار زمین میں چھوڑ گئے ہیں مگر اللہ نے ان کے گناہوں پر انہیں پکڑ لیا اور اُن کو اللہ سے بچانے والا کوئی نہ تھا');
INSERT INTO "ur_maududi" VALUES(40,22,'40:22','یہ ان کا انجام اس لیے ہوا کہ ان کے پاس اُن کے رسُول بیّنات لے کر آئے اور انہوں نے ماننے سے انکار کر دیا۔ آخر کار اللہ نے ان کو پکڑ لیا، یقیناً وہ بڑی قوّت والا اور سزا دینے میں بہت سخت ہے');
INSERT INTO "ur_maududi" VALUES(40,23,'40:23','ہم نے موسیٰؑ کو اپنی نشانیاں اور نمایاں سند ماموریت کے ساتھ بھیجا');
INSERT INTO "ur_maududi" VALUES(40,24,'40:24','فرعون اور ہامان اور قارُون کی طرف اپنی نشانیوں اور نمایاں سَنَدِ ماموریت کے ساتھ بھیجا، مگر انہوں کہا”ساحر ہے، کذّاب ہے۔“');
INSERT INTO "ur_maududi" VALUES(40,25,'40:25','پھر جب وہ ہماری طرف سے حق ان کے سامنے لے آیا تو انہوں نے کہا”جو لوگ ایمان لا کر اس کے ساتھ شامل ہوئے ہیں ان سب کے لڑکوں کو قتل کرو اور لڑکیوں کو جیتا چھوڑ دو۔“ مگر کافروں کی چال اکارت گئی۔ ');
INSERT INTO "ur_maududi" VALUES(40,26,'40:26',' ایک روز فرعون نے اپنے درباریوں سے کہا”چھوڑو مجھے، میں اِس موسیٰؑ کو قتل کیے دیتا ہوں، اور پکار دیکھے یہ اپنے ربّ کو۔ مجھے اندیشہ ہے کہ یہ تمہارا دین بدل ڈالے گا، یا ملک میں فساد برپا کرے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(40,27,'40:27','موسیٰؑ نے کہا”میں نے تو ہر اُس متکبّر کے مقابلے میں جو یوم الحساب پر ایمان نہیں رکھتا اپنے ربّ اور تمہارے ربّ کی پناہ لے لی ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(40,28,'40:28','اس موقع پر آلِ فرعون میں سے ایک مومن شخص، جو اپنا ایمان چھُپائے ہوئے تھا، بول اُٹھا”کیا تم ایک شخص کو صرف اِس بنا پر قتل کر دو گے کہ وہ کہتا ہے میرا ربّ اللہ ہے؟ حالانکہ وہ تمہارے ربّ کی طرف سے تمہارے پاس بیّنات لے آیا۔ اگر وہ جھُوٹا ہے تو اس کا جھُوٹ خود اسی پرپلٹ پڑے گا۔ لیکن اگر وہ سچا ہے تو جن ہولناک تنائج کا وہ تم کو خوف دلاتا ہے ان میں سے کچھ تو تم پر ضرور ہی آجائیں گے۔ اللہ کسی ایسے شخص کو ہدایت نہیں دیتا جو حد سے گزر جانے والا اور کذّاب ہو۔ ');
INSERT INTO "ur_maududi" VALUES(40,29,'40:29','اے میری قوم کے لوگو! آج تمہیں بادشاہی حاصل ہے اور زمین میں تم غالب ہو، لیکن اگر خدا کا عذاب ہم پر آگیا تو پھر کون ہے جو ہماری مدد کر سکے گا۔“ فرعون نے کہا”میں تو تم لوگوں کو وہی رائے دے رہا ہوں جو مجھے مناسب نظر آتی ہے۔ اور میں اُسی راستے کی طرف تمہاری رہنمائی کرتا ہوں جو ٹھیک ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(40,30,'40:30','وہ شخص جو ایمان لایا تھا اُس نے کہا "اے میری قوم کے لوگو، مجھے خوف ہے کہ کہیں تم پر بھی وہ دن نہ آ جائے جو اس سے پہلے بہت سے جتھوں پر آ چکا ہے');
INSERT INTO "ur_maududi" VALUES(40,31,'40:31','جیسا دن قومِ نوحؑ ، اور عاد اور ثمود اور ان کے بعد والی قوموں پر آیا تھا۔ اور یہ حقیقت ہے کہ اللہ اپنے بندوں پر ظلم کا کوئی ارادہ نہیں رکھتا۔ ');
INSERT INTO "ur_maududi" VALUES(40,32,'40:32','اے قوم، مجھے ڈر ہے کہ کہیں تم پر فریاد و فغاں کا دن نہ آ جائے');
INSERT INTO "ur_maududi" VALUES(40,33,'40:33','جب تم ایک دوسرے کو پکارو گے اور بھاگے بھاگے پھرو گے، مگر اُس وقت اللہ سے بچانے والا کوئی نہ ہو گا سچ یہ ہے کہ جسے اللہ بھٹکا دے اُسے پھر کوئی راستہ دکھانے والا نہیں ہوتا');
INSERT INTO "ur_maududi" VALUES(40,34,'40:34','اس سے پہلے یوسُفؑ تمہارے پاس بیّنات لے کر آئے تھے مگر تم اُن کی لائی ہوئی تعلیم کی طرف سے شک میں پڑے رہے۔ پھر جب اُن کا انتقال ہوگیا تو تم نے کہا اب اُن کے بعد اللہ کوئی رسُول ہر گز نہ بھیجے گا “۔۔۔۔ اِسی طرح اللہ اُن سب لوگوں کو گمراہی میں ڈال دیتا ہے جو حد سے گزرنے والے اور شکّی ہوتے ہیں');
INSERT INTO "ur_maududi" VALUES(40,35,'40:35','اور اللہ کی آیات میں جھگڑے کرتے ہیں بغیر اس کے کہ ان کے پاس کوئی سَنَد یا دلیل آئی ہو۔ یہ رویّہ اللہ اور ایمان لانے والوں کے نزدیک سخت مبغوض ہے۔ اِسی طرح اللہ ہر متکبّر و جبّار کے دل پر ٹھپّہ لگا دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(40,36,'40:36','فرعون نے کہا "اے ہامان، میرے لیے ایک بلند عمارت بنا تاکہ میں راستوں تک پہنچ سکوں');
INSERT INTO "ur_maududi" VALUES(40,37,'40:37','آسمانوں کے راستوں تک ، اور موسیٰؑ کے خدا کو جھانک کر دیکھوں۔ مجھے تو یہ موسیٰؑ جھُوٹا ہی معلوم ہوتا ہے۔“ ۔۔۔۔ اس طرح فرعون کے لیے اس کی بدعملی خوشنما بنادی گئی اور وہ راہِ راست سے روک دیا گیا۔ فرعون کی ساری چال بازی (اُس کی اپنی)تباہی کے راستہ ہی میں صَرف ہوئی');
INSERT INTO "ur_maududi" VALUES(40,38,'40:38','وہ شخص جو ایمان لایا تھا، بولا "اے میری قوم کے لوگو، میری بات مانو، میں تمہیں صحیح راستہ بتاتا ہوں');
INSERT INTO "ur_maududi" VALUES(40,39,'40:39','اے قوم، یہ دُنیا کی زندگی تو چند روزہ ہے، ہمیشہ کے قیام کی جگہ آخرت ہی ہے');
INSERT INTO "ur_maududi" VALUES(40,40,'40:40','جو برائی کرے گا اُس کو اتنا ہی بدلہ ملے گا جتنی اُس نے برائی کی ہو گی اور جو نیک عمل کرے گا، خواہ وہ مرد ہو یا عورت، بشرطیکہ ہو وہ مومن، ایسے سب لوگ جنت میں داخل ہوں گے جہاں اُن کو بے حساب رزق دیا جائے گا');
INSERT INTO "ur_maududi" VALUES(40,41,'40:41','اے قوم، آخر یہ کیا ماجرا ہے کہ میں تو تم لوگوں کو نجات کی طرف بلاتا ہوں اور تم لوگ مجھے آگ کی طرف دعوت دیتے ہو!');
INSERT INTO "ur_maududi" VALUES(40,42,'40:42','تم مجھے اس بات کی دعوت دیتے ہو کہ میں اللہ سے کفر کروں اور اس کے ساتھ اُن ہستیوں کو شریک ٹھہراوٴں جنہیں میں نہیں جانتا ، حالانکہ میں تمہیں اُس زبردست مغفرت کرنے والے خدا کی طرف بُلا رہا ہوں');
INSERT INTO "ur_maududi" VALUES(40,43,'40:43','نہیں، حق یہ ہے اور اِس کے خلاف نہیں ہو سکتا کہ جن کی طرف تم مجھے بُلا رہے ہو اُن کے لیے نہ دنیا میں کوئی دعوت ہے اور نہ آخرت میں ، اور ہم سب کو پلٹنا اللہ ہی کی طرف ہے ، اور حد سے گزرنے والے آگ میں جانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(40,44,'40:44','آج جو کچھ میں کہہ رہا ہوں، عنقریب وہ وقت آئے گا جب تم اُسے یاد کرو گے۔ اور اپنا معاملہ میں اللہ کے سپرد کرتا ہوں، وہ اپنے بندوں کا نگہبان ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(40,45,'40:45','آخر کار اُن لوگوں نے جو بُری سے بُری چالیں اُس مومن کے خلاف چلیں، اللہ نے اُن سب سے اُس کو بچا لیا ، اور فرعون کے ساتھی خود بدترین عذاب کے پھیر میں آگئے۔ ');
INSERT INTO "ur_maududi" VALUES(40,46,'40:46','دوزخ کی آگ ہے جس کے سامنے صبح و شام وہ پیش کیے جاتے ہیں، اور جب قیامت کی گھڑی آجائے گی تو حکم ہوگا کہ آلِ فرعون کو شدید تر عذاب میں داخل کرو۔ ');
INSERT INTO "ur_maududi" VALUES(40,47,'40:47','پھر ذرا خیال کرو اُس وقت کا جب یہ لوگ دوزخ میں ایک دُوسرے سے جھگڑ رہے ہوں گے۔ دنیا میں جو لوگ کمزور تھے وہ بڑے بننے والوں سے کہیں گے کہ ”ہم تمہارے تابع تھے، اب کیا یہاں تم نارِ جہنّم کی تکلیف کے کچھ حصّے سے ہم کو بچا لوگے؟“ ');
INSERT INTO "ur_maududi" VALUES(40,48,'40:48','وہ بڑے بننے والے جواب دیں گے”ہم سب یہاں ایک حال میں ہیں، اور اللہ بندوں کے درمیان فیصلہ کر چکا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(40,49,'40:49','پھر یہ دوزخ میں پڑے ہوئے لوگ جہنم کے اہل کاروں سے کہیں گے "اپنے رب سے دعا کرو کہ ہمارے عذاب میں بس ایک دن کی تخفیف کر دے"');
INSERT INTO "ur_maududi" VALUES(40,50,'40:50','وہ پوچھیں گے”کیا تمہارے پاس تمہارے رسُول بیّنات لے کر نہیں آتے رہے تھے؟“وہ کہیں گے”ہاں۔“ جہنّم کے اہل کار بولیں گے”پھر تو تم ہی دُعا کرو ، اور کافروں کی دُعا اکارت ہی جانے والی ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(40,51,'40:51','یقین جانوکہ ہم اپنے رسولوں اور ایمان لانے والوں کی مدد اِس دُنیا کی زندگی میں بھی لازماً کرتے ہیں، اور اُس روز بھی کریں گے جب گواہ کھڑے ہوں گے، ');
INSERT INTO "ur_maududi" VALUES(40,52,'40:52','جب ظالموں کو ان کی معذرت کچھ بھی فائدہ نہ دے گی اور اُن پر لعنت پڑے گی اور بد ترین ٹھکانا اُن کے حصے میں آئے گا');
INSERT INTO "ur_maududi" VALUES(40,53,'40:53','آخر دیکھ لو کہ موسیٰؑ کی ہم نے رہنمائی کی اور بنی اسرائیل کو اس کتاب کا وارث بنا دیا');
INSERT INTO "ur_maududi" VALUES(40,54,'40:54','جو عقل و دانش رکھنے والوں کے لیے ہدایت و نصیحت تھی۔ ');
INSERT INTO "ur_maududi" VALUES(40,55,'40:55','پس اے نبیؐ ، صبر کرو، اللہ وعدہ بر حق ہے ، اپنے قصُور کی معافی چاہو، اور صبح و شام اپنے ربّ کی حمد کے ساتھ اس کی تسبیح کرتے رہو۔ ');
INSERT INTO "ur_maududi" VALUES(40,56,'40:56','حقیقت یہ ہے کہ جو لوگ کسی سَنَد و حجّت کے بغیر جو اُن کے پاس آئی ہو، اللہ کی آیات میں جھگڑے کر رہے ہیں اُن کے دلوں میں کِبر بھرا ہوا ہے ، مگر وہ اُس بڑائی کو پہنچنے والے نہیں ہیں جس کا وہ گھمنڈ رکھتے ہیں۔ بس اللہ کی پناہ مانگ لو، وہ سب کچھ دیکھتا اور سُنتا ہے');
INSERT INTO "ur_maududi" VALUES(40,57,'40:57',' آسمانوں اور زمین کا پیدا کرنا انسانوں کو پیدا کرنے کی بہ نسبت یقیناً زیادہ بڑا کام ہے ، مگر اکثر لوگ جانتے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(40,58,'40:58','اور یہ نہیں ہو سکتا کہ اندھا اور بینا یکساں ہو جائے اور ایماندار و صالح اور بدکار برابر ٹھہریں۔ مگر تم لوگ کم ہی کچھ سمجھتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(40,59,'40:59','یقیناً قیامت کی گھڑی آنے والی ہے ، اس کے آنے میں کوئی شک نہیں، مگر اکثر لوگ نہیں مانتے۔ ');
INSERT INTO "ur_maududi" VALUES(40,60,'40:60',' تمہارا ربّ کہتا ہے”مجھے پُکارو، میں تمہاری دُعائیں قبول کروں گا، جو لوگ گھمنڈ میں آکر میری عبادت سے مُنہ موڑتے ہیں، ضرور وہ ذلیل و خوار ہو کر جہنّم میں داخل ہوں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(40,61,'40:61','وہ اللہ ہی تو ہے جس نے تمہارے لیے رات بنائی تاکہ تم اس میں سکون حاصل کرو، اور دن کو روشن کیا۔ حقیقت یہ ہے کہ اللہ لوگوں پر بڑا فضل فرمانے والا ہے مگر اکثر لوگ شکر ادا نہیں کرتے۔ ');
INSERT INTO "ur_maududi" VALUES(40,62,'40:62','وہی اللہ (جس نے تمہارے لیے یہ کچھ کیا ہے)تمہارا ربّ ہے۔ ہر چیز کا خالق۔ اس کے سوا کوئی معبُود نہیں ہے۔ پھر تم کدھر سے بہکائے جارہے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(40,63,'40:63','اِسی طرح وہ سب لوگ بہکائے جاتے رہے ہیں جو اللہ کی آیات کا انکار کرتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(40,64,'40:64','وہ اللہ ہی تو ہے جس نے تمہارے لیے زمین کو جائے قرار بنایا، اور اُوپر آسمان کا گُنبد بنا دیا۔ جس نے تمہاری صُورت بنائی اور بڑی ہی عمدہ بنائی۔ جس نے تمہیں پاکیزہ چیزوں کا رزق دیا۔ وہی اللہ (جس کے یہ کام ہیں)تمہارا ربّ ہے۔ بے حساب برکتوں والا ہے وہ کائنات کا ربّ');
INSERT INTO "ur_maududi" VALUES(40,65,'40:65','وہی زندہ ہے۔ اُس کے سوا کوئی معبُود نہیں۔ اُسی کو تم پکارو اپنے دین کو اس کے لیے خالص کر کے۔ ساری تعریف اللہ ربّ العالمین ہے کے لیے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(40,66,'40:66','اے نبی ؐ ، اِن لوگوں سے کہہ دو کہ مجھے تو اُن ہستیوں کی عبادت سے منع کر دیا گیا ہے جنہیں تم اللہ کو چھوڑ کر پکارتے ہو۔ (میں یہ کام کیسے کر سکتا ہوں)جبکہ میرے پاس میرے ربّ کی طرف سے بیّنات آچکی ہیں۔ مجھے حکم دیا گیا ہے کہ میں ربّ العالمین کے آگے سرِ تسلیم خم کر دوں');
INSERT INTO "ur_maududi" VALUES(40,67,'40:67','وہی تو ہے جس نے تم کو مٹی سے پیدا کیا،پھر نطفے سے، پھر خون کے لوتھڑے سے، پھر وہ تمہیں بچے کی شکل میں نکالتا ہے، پھر تمہیں بڑھاتا ہے تاکہ تم اپنی پوری طاقت کو پہنچ جاوٴ، پھر اور بڑھاتا ہے تاکہ تم بُڑھاپے کو پہنچو۔ اور تم میں سے کوئی پہلے ہی واپس بُلا لیا جاتا ہے۔ یہ سب کچھ اس لیے کیا جاتا ہے تاکہ تم اپنے مقرر وقت تک پہنچ جاوٴ، اور اس لیے تم حقیقت کو سمجھو۔ ');
INSERT INTO "ur_maududi" VALUES(40,68,'40:68','وہی ہے زندگی دینے والا، اور وہی موت دینے والا ہے وہ جس بات کا بھی فیصلہ کرتا ہے، بس ایک حکم دیتا ہے کہ وہ ہو جائے اور وہ ہو جاتی ہے');
INSERT INTO "ur_maududi" VALUES(40,69,'40:69','تم نے دیکھا اُن لوگوں کو جو اللہ کی آیات میں جھگڑے کرتے ہیں، کہاں سے وہ پھرائے جا رہے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(40,70,'40:70','یہ لوگ جو اِس کتاب کو اور اُن ساری کتابوں کو جھُٹلاتے ہیں جو ہم نے اپنے رسُولوں کے ساتھ بھیجی تھیں؟ عنقریب انہیں معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(40,71,'40:71','جب طوق ان کی گردنوں میں ہوں گے، اور زنجیریں، جن سے پکڑ کر');
INSERT INTO "ur_maududi" VALUES(40,72,'40:72','وہ کھولتے ہوئے پانی کی طرف کھینچے جائیں گے اور پھر دوزخ کی آگ میں جھونک دیے جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(40,73,'40:73','پھر اُن سے پوچھا جائے گا کہ اب کہاں ہیں اللہ کے سوا وہ دوسرے خدا جن کو تم شریک کرتے تھے؟ ');
INSERT INTO "ur_maududi" VALUES(40,74,'40:74','وہ جواب دیں گے”کھوئے گئے وہ ہم سے، بلکہ ہم اس سے پہلے کسی چیز کو نہ پکارتے تھے۔“ اِس طرح اللہ کافروں کا گمراہ ہونا متحقق کر دے گا');
INSERT INTO "ur_maududi" VALUES(40,75,'40:75','اُن سے کہا جائے گا”یہ تمہارا انجام اِس لیے ہوا ہے کہ تم زمین میں غیر حق پر مگن تھے اور پھر اُس پر اِتراتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(40,76,'40:76','اب جاؤ، جہنم کے دروازوں میں داخل ہو جاؤ ہمیشہ تم کو وہیں رہنا ہے، بہت ہی برا ٹھکانا ہے متکبرین کا"');
INSERT INTO "ur_maududi" VALUES(40,77,'40:77','پس اے نبیؐ ، صبر کرو، اللہ کا وعدہ بر حق ہے۔ اب خواہ ہم تمہارے سامنے ہی اِن کو اُن بُرے نتائج کا کوئی حصّہ دکھا دیں جن سے ہم اِنہیں ڈرا رہے ہیں، یا (اُس سے پہلے)تمہیں دنیا سے اُٹھا لیں ، پلٹ کر آنا تو اِنہیں ہماری ہی طرف ہے۔ ');
INSERT INTO "ur_maududi" VALUES(40,78,'40:78',' اے نبیؐ ، تم سے پہلے ہم بہت سے رسُول بھیج چکے ہیں جن میں سے بعض کے حالات ہم نے تم کو بتائے ہیں اور بعض کے نہیں بتائے۔ کسی رسُول کی بھی یہ طاقت نہ تھی کہ اللہ اِذن کے بغیر خود کوئی نشانی لے آتا۔ پھر جب اللہ کا حکم آگیا تو حق کے مطابق فیصلہ کر دیا گیا اور اُس وقت غلط کار لوگ خسارے میں پڑ گئے۔ ');
INSERT INTO "ur_maududi" VALUES(40,79,'40:79','اللہ ہی نے تمہارے لیے یہ مویشی جانور بنائے ہیں تاکہ ان میں سے کسی پر تم سوار ہو اور کسی کا گوشت کھاؤ');
INSERT INTO "ur_maududi" VALUES(40,80,'40:80','ان کے اندر تمہارے لیے اور بھی بہت سے منافع ہیں وہ اس کام بھی آتے ہیں کہ تمہارے دلوں میں جہاں جانے کی حاجت ہو وہاں تم اُن پر پہنچ سکو اُن پر بھی اور کشتیوں پر بھی تم سوار کیے جاتے ہو');
INSERT INTO "ur_maududi" VALUES(40,81,'40:81','اللہ اپنی یہ نشانیاں تمہیں دکھا رہا ہے ، آخر تم اُس کی کِن کِن نشانیوں کا انکار کرو گے۔ ');
INSERT INTO "ur_maududi" VALUES(40,82,'40:82','پھر کیا یہ زمین مین چلے پھرے نہیں ہیں کہ اِن کو اُن لوگوں کا انجام نظر آتا جو اِن سے پہلے گزر چکے ہیں ؟ وہ اِن سے تعداد میں زیادہ تھے، اِن سے بڑھ کر طاقتور تھے، اور زمین میں اِن سے زیادہ شاندار آثار چھوڑ گئے ہیں۔ جو کچھ کمائی اُنہوں نے کی تھی، آخر وہ اُن کے کس کام آئی؟');
INSERT INTO "ur_maududi" VALUES(40,83,'40:83','جب ان کے رسُول ان کے پاس بیّنات لے کر آئے تو وہ اُسی علم میں مگن رہے جو ان کے اپنے پاس تھا، اور پھر اُسی چیز کے پھیر میں آگئے جس کا وہ مذاق اُڑاتے تھے');
INSERT INTO "ur_maududi" VALUES(40,84,'40:84','جب انہوں نے ہمارا عذاب دیکھ لیا تو پکار اٹھے کہ ہم نے مان لیا اللہ وحدہُ لا شریک کو اور ہم انکار کرتے ہیں اُن سب معبودوں کا جنہیں ہم شریک ٹھیراتے تھے');
INSERT INTO "ur_maududi" VALUES(40,85,'40:85','مگر ہمارا عذاب دیکھ لینے کے بعد ان کا ایمان اُن کے لیے کچھ بھی نافع نہ ہو سکتا تھا، کیونکہ یہی اللہ کا مقرر ضابطہ ہے جو ہمیشہ اس کے بندوں میں جاری رہا ہے، اورا س وقت کافر خسارے میں پڑ گئے');
INSERT INTO "ur_maududi" VALUES(41,1,'41:1','ح م');
INSERT INTO "ur_maududi" VALUES(41,2,'41:2','یہ خدائے رحمان و رحیم کی طرف سے نازل کردہ چیز ہے');
INSERT INTO "ur_maududi" VALUES(41,3,'41:3','ایک ایسی کتاب جس کی آیات خوب کھول کر بیان کی گئی ہیں، عربی زبان کا قرآن، اُن لوگوں کے لیے جو علم رکھتے ہیں');
INSERT INTO "ur_maududi" VALUES(41,4,'41:4','بشارت دینے والا اور ڈرانے والا۔ مگر اِن لوگوں میں سے اکثر نے اس سے رُو گردانی کی اور وہ سُن کر نہیں دیتے');
INSERT INTO "ur_maududi" VALUES(41,5,'41:5','کہتے ہیں”جس چیز کی طرف تُو ہمیں بُلا رہا ہے اس کے لیے ہمارے دلوں پر غلاف چڑھے ہوئے ہیں، ہمارے کان بہرے ہوگئے ہیں، اور ہمارے اور تیرے درمیان ایک حجاب حائل ہو گیا ہے۔ تُو اپنا کام کر ، ہم اپنا کام کیے جائیں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(41,6,'41:6','اے نبی ؐ، اِن سے کہو، میں تو ایک بشر ہوں تم جیسا۔ مجھے وحی کے ذریعہ سے بتایا جاتا ہے کہ تمہارا خدا تو بس ایک ہی خدا ہے ، لہٰذا تم سیدھے اُسی کا رُخ اختیار کرو اور اس سے معافی چاہو۔ تباہی ہے اُن مشرکوں کے لیے');
INSERT INTO "ur_maududi" VALUES(41,7,'41:7','جو زکوٰة نہیں دیتے اور آخرت کے منکر ہیں');
INSERT INTO "ur_maududi" VALUES(41,8,'41:8','رہے وہ لوگ جنہوں نے مان لیا اور نیک اعمال کیے، اُن کے لیے یقیناً ایسا اجر ہے جس کا سلسلہ ٹوٹنے والا نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(41,9,'41:9','اے نبیؐ! اِن سے کہو، کیا تم اُس خدا سے کفر کرتے ہو اور دوسروں کو اُس کا ہمسر ٹھیراتے ہو جس نے زمین کو دو دنوں میں بنا دیا؟ وہی تو سارے جہان والوں کا رب ہے');
INSERT INTO "ur_maududi" VALUES(41,10,'41:10','اُس نے (زمین کو وجود میں لانے کے بعد)اوپر سے اس پر پہاڑ جمادیے اور اس میں برکتیں رکھ دیں اور اس کے اندر سب مانگنے والوں کے لیے ہر ایک کی طلب و حاجت کے مطابق ٹھیک اندازے سے خوراک کا سامان مہیّا کر دیا۔ یہ سب کام چار دن میں ہو گئے۔ ');
INSERT INTO "ur_maududi" VALUES(41,11,'41:11','پھر وہ آسمان کی طرف متوجہ ہوا جو اُس وقت دھواں تھا۔ اُس نے آسمان اور زمین سے کہا”وجود میں آجاوٴ، خواہ تم چاہو یا نہ چاہو۔“ دونوں نے کہا”ہم آگئے فرمانبرداروں کی طرح۔“ ');
INSERT INTO "ur_maududi" VALUES(41,12,'41:12','تب اُس نے دو دن کے اندر سات آسمان بنا دیے، اور ہر آسمان میں اُس کا قانون وحی کردیا۔ اور آسمانِ دنیا کو ہم نے چراغوں سے آراستہ کیا اور اسے خوب محفوظ کر دیا۔ یہ سب کچھ ایک زبردست علیم ہستی کا منصُوبہ ہے');
INSERT INTO "ur_maududi" VALUES(41,13,'41:13','اب اگر یہ لوگ منہ موڑتے ہیں تو اِن سے کہہ دو کہ میں تم کو اُسی طرح کے ایک اچانک ٹوٹ پڑنے والے عذاب سے ڈراتا ہوں جیسا عاد اور ثمود پر نازل ہوا تھا');
INSERT INTO "ur_maududi" VALUES(41,14,'41:14','جب خدا کے رسُول اُن کے پاس آگے اور پیچھے ، ہر طرف سے آئے اور انہیں سمجھایا کہ اللہ کے سوا کسی کی بندگی نہ کرو تو انہوں نے کہا”ہمارا ربّ چاہتا تو فرشتے بھیجتا، لہٰذا ہم اُس بات کو نہیں مانتے جس کے لیے تم بھیجے گئے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(41,15,'41:15','عاد کا حال یہ تھا کہ وہ زمین میں کسی حق کے بغیر بڑے بن بیٹھے اور کہنے لگے "کون ہے ہم سے زیادہ زور آور" اُن کو یہ نہ سوجھا کہ جس خدا نے ان کو پیدا کیا ہے وہ ان سے زیادہ زور آور ہے؟ وہ ہماری آیات کا انکار ہی کرتے رہے');
INSERT INTO "ur_maududi" VALUES(41,16,'41:16','آخر کار ہم نے چند منحوس دنوں میں سخت طوفانی ہوا ان پر بھیج دی۔ تاکہ انہیں دنیا ہی کی زندگی میں ذلّت و رسوائی کے عذاب کا مزا چکھا دیں، اور آخرت کا عذاب تو اس سے بھی زیادہ رُسوا کُن ہے ، وہاں کوئی ان کی مدد کرنے والا نہ ہو گا');
INSERT INTO "ur_maududi" VALUES(41,17,'41:17','رہے ثمود، تو ان کے سامنے ہم نے راہ راست پیش کی مگر انہوں نے راستہ دیکھنے کے بجائے اندھا بنا رہنا پسند کیا آخر اُن کے کرتوتوں کی بدولت ذلت کا عذاب اُن پر ٹوٹ پڑا');
INSERT INTO "ur_maududi" VALUES(41,18,'41:18','اور ہم نے اُن لوگوں کو بچالیا جو ایمان لائے تھے اور گمراہی و بدعملی سے پرہیز کرتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(41,19,'41:19','اور ذرا اُس وقت کا خیال کرو جب اللہ کے دشمن دوزخ کی طرف جانے کے لیے گھیر لائے جائیں گے۔ اُن کے اگلوں کو پچھلوں کے آنے تک روک رکھا جائے گا، ');
INSERT INTO "ur_maududi" VALUES(41,20,'41:20','پھر جب سب وہاں پہنچ جائیں گے تو ان کے کان اور ان کی آنکھیں اور ان کے جسم کی کھالیں ان پر گواہی دیں گی کہ وہ دنیا میں کیا کچھ کرتے رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(41,21,'41:21','وہ اپنے جسم کی کھالوں سے کہیں گے”تم نے ہمارے خلاف کیوں گواہی دی؟“ وہ جواب دیں گی”ہمیں اُسی خدا نے گویائی دی ہے جس نے ہر چیز کو گویا کر دیا ہے۔ اُسی نے تم کو پہلی مرتبہ پیدا کیا تھا اور اب اُسی کی طرف تم واپس لائے جارہے ہو');
INSERT INTO "ur_maududi" VALUES(41,22,'41:22','تم دنیا میں جرائم کرتے وقت جب چھپتے تھے تو تمہیں یہ خیال نہ تھا کہ کبھی تمہارے اپنے کان اور تمہاری آنکھیں اور تمہارے جسم کی کھالیں تم پر گواہی دیں گی بلکہ تم نے تو یہ سمجھا تھا کہ تمہارے بہت سے اعمال کی اللہ کو بھی خبر نہیں ہے');
INSERT INTO "ur_maududi" VALUES(41,23,'41:23','تمہارا یہی گمان جو تم نے اپنے ربّ کے ساتھ کیا تھا، تمہیں لے ڈُوبا اور اسی کی بدولت تم خسارے میں پڑ گئے۔“ ');
INSERT INTO "ur_maududi" VALUES(41,24,'41:24','اس حالت میں وہ صبر کریں (یا نہ کریں)آگ ہی ان کا ٹھکانا ہوگی، اور اگر رجوع کا موقع چاہیں گے کوئی موقع انہیں نہ دیا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(41,25,'41:25','ہم نے ان پر ایسے ساتھی مسلّط کر دیے تھے جو انہیں آگے اور پیچھے ہر چیز خوشنما کر دکھاتے تھے، آخر کا ر اُن پر بھی وہی فیصلہٴ عذاب چسپاں ہو کر رہا جو ان سے پہلے گزرے ہوئے جِنّوں اور انسانوں کے گروہوں پر چسپاں ہو چکا تھا، یقیناًوہ خسارے میں رہ جانے والے تھے');
INSERT INTO "ur_maududi" VALUES(41,26,'41:26','یہ منکرینِ حق کہتے ہیں”اِس قرآن کو ہرگز نہ سُنو اور جب یہ سنایا جائے تو اس میں خلل ڈالو، شاید کہ اس طرح تم غالب آجاوٴ۔“ ');
INSERT INTO "ur_maududi" VALUES(41,27,'41:27','اِن کافروں کو ہم سخت عذاب کا مزا چکھا کر رہیں گے اور جو بدترین حرکات یہ کرتے رہے ہیں ان کا پورا پورا بدلہ اِنہیں دیں گے');
INSERT INTO "ur_maududi" VALUES(41,28,'41:28','وہ دوزخ ہے جو اللہ کے دشمنوں کو بدلے میں ملے گی اُسی میں ہمیشہ ہمیشہ کے لیے اِن کا گھر ہو گا یہ ہے سزا اِس جرم کی کہ وہ ہماری آیات کا انکار کرتے رہے');
INSERT INTO "ur_maududi" VALUES(41,29,'41:29','وہاں یہ کافر کہیں گے کہ ”اے ہمارے ربّ، ذرا ہمیں دکھا دے اُن جِنّوں اور انسانوں کو جنہوں نے ہمیں گمراہ کیا تھا، ہم انہیں پاوٴں تلے روند ڈالیں گے تاکہ وہ خوب ذلیل و خوار ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(41,30,'41:30',' جن لوگوں نے کہا کہ اللہ ہمارا ربّ ہے اور پھر وہ اس پر ثابت قدم رہے، یقیناً اُن پر فرشتے نازل ہوتے ہیں اور ان سے کہتے ہیں کہ”نہ ڈرو، نہ غم کرو، اور خوش ہو جاوٴ اُس جنّت کی بشارت سے جس کا تم سے وعدہ کیا گیا ہے');
INSERT INTO "ur_maududi" VALUES(41,31,'41:31','ہم اِس دنیا کی زندگی میں بھی تمہارے ساتھی ہیں اور آخرت میں بھی، وہاں جو کچھ تم چاہو گے تمہیں ملے گا اور ہر چیز جس کی تم تمنا کرو گے وہ تمہاری ہوگی');
INSERT INTO "ur_maududi" VALUES(41,32,'41:32','یہ ہے سامان ضیافت اُس ہستی کی طرف سے جو غفور اور رحیم ہے"');
INSERT INTO "ur_maududi" VALUES(41,33,'41:33','اور اُس شخص کی بات سے اچھی بات اور کس کی ہو گی جس نے اللہ کی طرف بُلایا اور نیک عمل کیا اور کہا کہ میں مسلمان ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(41,34,'41:34','اور اے نبیؐ ،نیکی اور بدی یکساں نہیں ہیں۔ تم بدی کو اُس نیکی سے دفع کرو جو بہترین ہو۔ تم دیکھو گے کہ تمہارے ساتھ جس کی عداوت پڑی ہوئی تھی وہ جگری دوست بن گیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(41,35,'41:35','یہ صفت نصیب نہیں ہوتی مگر اُن لوگوں کو جو صبر کرتے ہیں، اور یہ مقام حاصل نہیں ہوتا مگر اُن لوگوں کو جو بڑے نصیبے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(41,36,'41:36','اور اگر تم شیطان کی طرف سے کوئی اُکساہٹ محسُوس کرو تو اللہ کی پناہ مانگ لو، وہ سب کچھ سُنتا اور جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(41,37,'41:37',' اللہ کی نشانیوں میں سے ہیں یہ رات اور دن اور سُورج اور چاند۔ سُورج اور چاند کو سجدہ نہ کرو بلکہ اُس خدا کو سجدہ کرو جس نے انہیں پیدا کیا ہے اگر فی الواقع تم اُسی کی عبادت کرنے والے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(41,38,'41:38','لیکن اگر یہ لوگ غرور میں آکر اپنی ہی بات پر اَڑے رہیں تو پروا نہیں، جو فرشتے تیرے ربّ کے مقرب ہیں وہ شب و روز اس کی تسبیح کر رہے ہیں اور کبھی نہیں تھکتے۔ ');
INSERT INTO "ur_maududi" VALUES(41,39,'41:39','اور اللہ کی نشانیوں میں سے ایک یہ ہے کہ تم دیکھتے ہو زمین سُونی پڑی ہوئی ہے، پھر جونہی کہ ہم نے اس پر پانی برسایا، یکایک وہ پَھبَک اُٹھتی ہے اور پھُول جاتی ہے۔ یقیناً جو خدا اس مری ہوئی زمین کو جِلا اُٹھاتا ہے وہ مُردوں کو بھی زندگی بخشنے والا ہے۔ یقیناً وہ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(41,40,'41:40',' جو لوگ ہماری آیات کو اُلٹے معنی پہناتے ہیں وہ ہم سے کچھ چھُپے ہوئے نہیں ہیں۔ خود ہی سوچ لو کہ آیا وہ شخص بہتر ہے جو آگ میں جھونکا جانے والا ہے یا وہ جو قیامت کے روز امن کی حالت میں حاضر ہوگا ؟ کرتے رہو جو کچھ تم چاہو، تمہاری ساری حرکتوں کو اللہ دیکھ رہا ہے');
INSERT INTO "ur_maududi" VALUES(41,41,'41:41','یہ وہ لوگ ہیں جن کے سامنے کلامِ نصیحت آیا تو انہوں نے اسے ماننے سے انکار کردیا۔ مگر حقیقت یہ ہے کہ یہ ایک زبردست کتاب ہے، ');
INSERT INTO "ur_maududi" VALUES(41,42,'41:42','باطل نہ سامنے سے اس پر آسکتا ہے نہ پیچھے سے، یہ ایک حکیم و حمید کی نازل کر دہ چیز ہے');
INSERT INTO "ur_maududi" VALUES(41,43,'41:43','اے نبیؐ ، تم سے جو کچھ کہا جا رہا ہے اس میں کوئی چیز بھی ایسی نہیں ہے جو تم سے پہلے گزرے ہوئے رسُولوں سے نہ کہی جا چکی ہو۔ بے شک تمہارا ربّ بڑا درگزر کرنے والا ہے، اور اس کے ساتھ بڑی دردناک سزا دینے والا بھی ہے');
INSERT INTO "ur_maududi" VALUES(41,44,'41:44','اگر ہم اس کو عجمی قرآن بنا کر بھیجتے تو یہ لوگ کہتے”کیوں نہ اس کی آیات کھول کر بیان کی گئیں؟ کیا عجیب بات ہے کہ کلام عجمی ہے اور مخاطِب عربی۔“ اِن سے کہو ، یہ قرآن ایمان لانے والوں کے لیے تو ہدایت اور شفا ہے، مگر جو لوگ ایمان نہیں لاتےاُن کے لیے یہ کانوں کی ڈاٹ اور آنکھوں کی پٹی ہے۔ ان کا حال تو ایسا ہے جیسے ان کو دُور سے پکارا جا رہا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(41,45,'41:45','اس سے پہلے ہم نے موسیٰؑ کو کتا ب دی تھی اور اس کے معاملے میں بھی یہی اختلاف ہوا تھا۔ اگر تیرے ربّ نے پہلے ہی ایک بات طے نہ کردی ہوتی تو ان اختلاف کرنے والوں کے درمیان فیصلہ چُکا دیا جاتا۔ اور حقیقت یہ ہے کہ یہ لوگ اُس کی طرف سے سخت اضطراب انگیز شک میں پڑے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(41,46,'41:46','جو کوئی نیک عمل کرے گا اپنے ہی لیے اچھا کرے گا، جو بدی کرے گا اُس کا وبال اُسی پر ہوگا، اور تیرا ربّ اپنے بندوں کے حق میں ظالم نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(41,47,'41:47','اُس ساعت کا علم اللہ ہی کی طرف راجع ہوتا ہے، وہی اُن سارے پھَلوں کو جانتا ہے جو اپنے شگوفوں میں سے نکلتے ہیں، اُسی کو معلوم ہے کہ کونسی مادہ حاملہ ہوئی ہے اور کس نے بچہ جنا ہے۔ پھر جس روز وہ ان لوگوں کو پکارے گا کہ کہاں ہیں میرے وہ شریک؟ یہ کہیں گے، ”ہم عرض کر چکے ہیں، آج ہم میں سے کوئی اس کی گواہی دینے والا نہیں ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(41,48,'41:48','اُس وقت وہ سارے معبُود ان سے گم ہو جائیں گے جنہیں یہ اِس سے پہلے پُکارتے تھے، اور یہ لوگ سمجھ لیں گے کہ ان کے لیے اب کوئی جائے پناہ نہیں ہے');
INSERT INTO "ur_maududi" VALUES(41,49,'41:49','انسان کبھی بھلائی کی دُعا مانگتے نہیں تھکتا، اور جب کوئی آفت اس پر آجاتی ہے تو مایوس و دل شکستہ ہوجاتا ہے');
INSERT INTO "ur_maududi" VALUES(41,50,'41:50','مگر جونہی کہ سخت وقت گزر جانے کے بعد ہم اسے اپنی رحمت کا مزا چکھاتے ہیں ، یہ کہتا ہے کہ”میں اِسی کا مستحق ہوں، اور میں نہیں سمجھتا کہ قیامت کبھی آئے گی، لیکن اگر واقعی میں اپنے ربّ کی طرف پلٹایا گیا تو وہاں بھی مزے کروں گا۔“ حالانکہ کُفر کرنے والوں کو لازماً ہم بتا کر رہیں گے کہ وہ کیا کر کے آئے ہیں اور انہیں ہم بڑے گندے عذاب کا مزا چکھائیں گے');
INSERT INTO "ur_maududi" VALUES(41,51,'41:51','انسان کو جب ہم نعمت دیتے ہیں تو وہ منہ پھیرتا ہے اور اکڑ جاتا ہے۔ اور جب اسے کوئی آفت چھُو جاتی ہے تو لمبی چوڑی دُعائیں کرنے لگتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(41,52,'41:52','اے نبیؐ ، اِن سے کہو، کبھی تم نے یہ بھی سوچا کہ اگر واقعی یہ قرآن خدا ہی کی طرف سے ہُوا اور تم اِس کا انکار کرتے رہے تو اُس شخص سے بڑھ کر بھٹکا ہوا اور کون ہوگا جو اِس کی مخالفت میں دُور تک نکل گیا ہو؟ ');
INSERT INTO "ur_maududi" VALUES(41,53,'41:53','عنقریب ہم ان کو اپنی نشانیاں آفاق میں بھی دکھائیں گے اور ان کے اپنے نفس میں بھی یہاں تک کہ ان پر یہ بات کھُل جائے گی کہ یہ قرآن واقعی برحق ہے۔ کیا یہ بات کافی نہیں ہے کہ تیرا ربّ ہر چیز کا شاہد ہے؟ ');
INSERT INTO "ur_maududi" VALUES(41,54,'41:54','آگاہ رہو، یہ لوگ اپنےربّ کی ملاقات میں شک رکھتے ہیں۔ سُن رکھو، وہ ہر چیز پر محیط ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,1,'42:1','ح م');
INSERT INTO "ur_maududi" VALUES(42,2,'42:2','ع س ق');
INSERT INTO "ur_maududi" VALUES(42,3,'42:3','اِسی طرح اللہ غالب و حکیم تمہاری طرف اور تم سے پہلے گزرے ہوئے (رسُولوں)کی طرف وحی کرتا رہا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,4,'42:4','آسمانوں اور زمین میں جو کچھ بھی ہے اُسی کا ہے، وہ برتر اور عظیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,5,'42:5','قریب ہے کہ آسمان اُوپر سے پھٹ پڑیں۔ فرشتے اپنے ربّ کی حمد کے ساتھ اُس کی تسبیح کر رہے ہیں اور زمین والوں کے حق میں در گزر کی درخواستیں کیے جاتے ہیں۔ آگاہ رہو، حقیقت میں اللہ غفور و رحیم ہی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,6,'42:6','جن لوگوں نے اُس کو چھوڑ کر اپنے کچھ دُوسرے سرپرست بنا رکھے ہیں، اللہ ہی اُن پر نگراں ہے، تم ان کے حوالہ دار نہیں ہو');
INSERT INTO "ur_maududi" VALUES(42,7,'42:7','ہاں، اِسی طرح اے نبیؐ ، یہ قرآنِ عربی ہم نے تمہاری طرف وحی کیا ہے تاکہ تم بستیوں کے مرکز (شہرِ مکّہ)اور اُس کے گرد و پیش رہنے والوں کو خبر دار کردو، اور جمع ہونے کے دن سے ڈرا دو جس کے آنے میں کوئی شک نہیں۔ ایک گروہ کو جنّت میں جانا ہے اور دوسرے گروہ کو دوزخ میں');
INSERT INTO "ur_maududi" VALUES(42,8,'42:8','اگر اللہ چاہتا تو اِن سب کو ایک ہی اُمّت بنا دیتا، مگر وہ جسے چاہتا ہے اپنی رحمت میں داخل کرتا ہے، اور ظالموں کا نہ کوئی ولی ہے اور نہ مددگار۔ ');
INSERT INTO "ur_maududi" VALUES(42,9,'42:9','کیا یہ (ایسے نادان ہیں کہ)اِنہوں نے اُسے چھوڑ کر دُوسرے ولی بنا رکھے ہیں؟ ولی تو اللہ ہی ہے، وہی مُردوں کو زندہ کرتا ہے ، اور وہ ہر چیز پر قادر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,10,'42:10',' تمہارے درمیان جس معاملہ میں بھی اختلاف ہو ، اُس کا فیصلہ کرنا اللہ کا کام ہے۔ وہی اللہ میرا ربّ ہے ، اُسی پر میں نے بھروسہ کیا ، اور اُسی کی طرف میں رجوع کرتا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(42,11,'42:11','آسمانوں اور زمین کا بنانے والا ، جس نے تمہاری اپنی جنس سے تمہارے لیے جوڑے پیدا کیے، اور اسی طرح جانوروں میں بھی (اُنہی کے ہم جنس)جوڑے بنائے، اور اِس طریقہ سے وہ تمہاری نسلیں پھیلاتا ہے۔ کائنات کی کوئی چیز اس کے مشابہ نہیں، وہ سب کچھ سُننے اور دیکھنے والا ہے، ');
INSERT INTO "ur_maududi" VALUES(42,12,'42:12','آسمانوں اور زمین کی خزانوں کی کُنجیاں اُسی کے پاس ہیں، جسے چاہتا ہے کھُلا رزق دیتا ہے اور جسے چاہتا ہے نَپا تُلا دیتا ہے، اُسے ہر چیز کا علم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,13,'42:13','اُس نےتمہارے لیے دین کا وہی طریقہ مقرر کیا ہے جس کا حکم اُس نے نُوحؑ کو دیا تھا، اور جسے (اے محمدؐ)اب تمہاری طرف ہم نے وحی کے ذریعہ سے بھیجا ہے، اور جس کی ہدایت ہم ابراہیمؑ اور موسیٰؑ اور عیسیٰؑ کو دے چکے ہیں، اِس تاکید کے ساتھ کہ قائم کرو اِس دین کو اور اُس میں متفرق نہ ہوجاوٴ۔ یہی بات اِن مشرکین کو سخت ناگوار ہوئی ہے جس کی طرف (اے محمدؐ)تم انہیں دعوت دے رہے ہو۔ اللہ جسے چاہتا ہے اپنا کر لیتا ہے، اور وہ اپنی طرف آنے کا راستہ اُسی کو دکھاتا ہے جو اُس کی طرف رجوع کرے۔ ');
INSERT INTO "ur_maududi" VALUES(42,14,'42:14','لوگو ں میں جو تفرقہ رُونما ہوا وہ اِس کے بعد ہوا کہ اُن کے پاس علم آچکا تھا، اور اس بنا پر ہوا کہ وہ آپس میں ایک دُوسرے پر زیادتی کرنا چاہتے تھے۔ اگر تیرا ربّ پہلے ہی یہ نہ فرما چکا ہوتا کہ ایک وقتِ مقرر تک فیصلہ ملتوی رکھا جائے گا تو ان کا قضیہ چُکا دیا گیا ہوتا۔ اور حقیقت یہ ہے کہ اَگلوں کے بعد جو لوگ کتاب کے وارث بنائے گئے وہ اُس کی طرف سے بڑے اضطراب انگیز شک میں پڑے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(42,15,'42:15','چونکہ یہ حالت پیدا ہو چکی ہے اس لیے اے محمدؐ ، اب تم اُسی دین کی طرف دعوت دو، اور جس طرح تمہیں حکم دیا گیا ہے اُس پر مضبوطی کے ساتھ قائم ہو جاوٴ، اور اِن لوگوں کی خواہشات کا اتباع نہ کرو، اور اِن سے کہہ دو کہ”اللہ نے جو کتاب بھی نازل کی ہے میں اُس پر ایمان لایا۔ مجھے حکم دیا گیا ہے کہ میں تمہارے درمیان انصاف کروں۔ اللہ ہی ہمارا ربّ بھی ہے اور تمہارا ربّ بھی۔ ہمارے اعمال ہمارے لیے ہیں اور تمہارے اعمال تمہارے لیے۔ ہمارے اور تمہارے درمیان کوئی جھگڑا نہیں۔ اللہ ایک روز ہم سب کو جمع کرے گا اور اُسی کی طرف سب کو جانا ہے۔“');
INSERT INTO "ur_maududi" VALUES(42,16,'42:16','اللہ کی دعوت پر لبّیک کہے جانے کےبعد جو لوگ (لبّیک کہنے والوں سے)اللہ کے دین کے معاملہ میں جھگڑے کرتے ہیں، اُن کی حجّت بازی اُن کے ربّ کے نزدیک باطل ہے، اور اُن پر اس کا غضب ہے اور اُن کے لیے سخت عذاب ہے');
INSERT INTO "ur_maududi" VALUES(42,17,'42:17','وہ اللہ ہی ہے جس نے حق کے ساتھ یہ کتاب اور میزان نازل کی ہے۔ اور تمہیں کیا خبر، شاید کہ فیصلے کی گھڑی قریب ہی آلگی ہو۔ ');
INSERT INTO "ur_maududi" VALUES(42,18,'42:18','جو لوگ اس کے آنے پر ایمان نہیں رکھتے وہ تو اس کے لیے جلدی مچاتے ہیں، مگر جو اس پر ایمان رکھتے ہیں وہ اس سے ڈرتے ہیں اور جانتے ہیں کہ یقیناً وہ آنے والی ہے خوب سن لو، جو لوگ اُس گھڑی کے آنے میں شک ڈالنے والی بحثیں کرتے ہیں وہ گمراہی میں بہت دور نکل گئے ہیں');
INSERT INTO "ur_maududi" VALUES(42,19,'42:19','اللہ اپنے بندوں پر بہت مہربان ہے۔ جسے جو کچھ چاہتا ہے دیتا ہے، اور وہ بڑی قوت والا اور زبردست ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,20,'42:20','جو کوئی آخرت کی کھیتی چاہتا ہے اُس کی کھیتی کو ہم بڑھاتے ہیں ، اور جو دنیا کی کھیتی چاہتا ہے اُسے دنیا ہی میں سے دیتے ہیں مگر آخرت میں اُس کا کوئی حصّہ نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,21,'42:21','کیا یہ لوگ کچھ ایسے شریکِ خدا رکھتے ہیں جنہوں نے اِن کے لیے دین کی نوعیّت رکھنے والا ایک ایسا طریقہ مقرر کر دیا ہے جس کا اللہ نے اِذن نہیں دیا؟ اگر فیصلے کی بات پہلے طے نہ ہوگئی ہوتی تو ان کا قضّیہ چکا دیا گیا ہوتا۔ یقیناً اِن ظالموں کے لیے دردناک عذاب ہے');
INSERT INTO "ur_maududi" VALUES(42,22,'42:22','تم دیکھو گے کہ یہ ظالم اُس وقت اپنے کیے کے انجام سے ڈر رہے ہوں گے اور وہ اِن پر آ کر رہے گا بخلاف اِس کے جو لوگ ایمان لے آئے ہیں اور جنہوں نے نیک عمل کیے ہیں وہ جنت کے گلستانوں میں ہوں گے، جو کچھ بھی وہ چاہیں گے اپنے رب کے ہاں پائیں گے، یہی بڑا فضل ہے');
INSERT INTO "ur_maududi" VALUES(42,23,'42:23','یہ ہے وہ چیز جس کی خوشخبری اللہ اپنے اُن بندوں کو دیتا ہے جنہوں نے مان لیا اور نیک عمل کیے۔ اے نبیؐ ، اِن لوگوں سے کہہ دو کہ میں اِس کام پر تم سے کسی اجر کا طالب نہیں ہوں، البتہ قرابت کی محبّت ضرور چاہتا ہوں۔ جو کوئی بھلائی کمائے گا ہم اس کے لیے اس بھلائی میں خوبی کا اضافہ کر دیں گے۔ بے شک اللہ بڑا درگزر کرنے والا اور قدر دان ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,24,'42:24','کیا یہ لوگ کہتے ہیں کہ اِس شخص نے اللہ پر جھُوٹا بُہتان گھڑ لیا ہے؟ اگر اللہ چاہے تو تمہارے دل پر مُہر کر دے۔ وہ باطل کو مٹا دیتا ہے اور حق کو اپنے فرمانوں سے حق کر دکھاتا ہے۔ وہ سینوں کے چھُپے ہوئے راز جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,25,'42:25','وہی ہے جو اپنے بندوں سے توبہ قبول کرتا ہے اور بُرائیوں سے درگزر کرتا ہے، حالانکہ تم لوگوں کے سب افعال کا اُسے علم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,26,'42:26','وہ ایمان لانے والوں اور نیک عمل کرنے والوں کی دعا قبول کرتا ہے اور اپنے فضل سے ان کو اور زیادہ دیتا ہے رہے انکار کرنے والے، تو ان کے لیے دردناک سزا ہے');
INSERT INTO "ur_maududi" VALUES(42,27,'42:27','اگر اللہ اپنے سب بندوں کو کھُلا رزق دے دیتا تو وہ زمین میں سرکشی کا طوفان برپا کر دیتے، مگر وہ ایک حساب سے جتنا چاہتا ہے نازل کر تا ہے، یقیناً وہ اپنے بندوں سے باخبر ہے اور اُن پر نگاہ رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,28,'42:28','وہی ہے جو لوگوں کے مایوس ہو جانے کے بعد مینّہ برساتا ہے اور اپنی رحمت پھیلادیتا ہے، اور وہی قابلِ تعریف ولی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,29,'42:29','اُس کی نشانیوں میں سے ہے یہ زمین اور آسمانوں کی پیدائش ، اور یہ جاندار مخلوقات جو اُس نے دونوں جگہ پھیلا رکھی ہیں۔ وہ جب چاہے انہیں اکٹھا کر سکتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,30,'42:30','تم پر جو مصیبت بھی آئی ہے، تمہارے اپنے ہاتھوں کی کمائی سے آئی ہے، اور بہت سے قصُوروں سے وہ ویسے ہی درگزر کر جاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,31,'42:31','تم زمین میں اپنے خدا کو عاجز کر دینے والے نہیں ہو، اور اللہ کے مقابلے میں تم کوئی حامی و ناصر نہیں رکھتے');
INSERT INTO "ur_maududi" VALUES(42,32,'42:32','اُس کی نشانیوں میں سے ہیں یہ جہاز جو سمندر میں پہاڑوں کی طرح نظر آتے ہیں');
INSERT INTO "ur_maududi" VALUES(42,33,'42:33','اللہ جب چاہے ہوا کو ساکن کر دے اور یہ سمندر کی پیٹھ پر کھڑے کے کھڑے رہ جائیں۔۔۔۔ اِس میں بڑی نشانیاں ہیں ہر اُس شخص کے لیے جو کمال درجہ صبر و شکر کرنے والا ہو ');
INSERT INTO "ur_maududi" VALUES(42,34,'42:34','یا (اُن پر سوار ہونے والوں کے) بہت سے گناہوں سے در گزر کرتے ہوئے ان کے چند ہی کرتوتوں کی پاداش میں انہیں ڈبو دے');
INSERT INTO "ur_maududi" VALUES(42,35,'42:35','اور اُس وقت ہماری آیات میں جھگڑے کرنے والوں کو پتہ چل جائے کہ ان کے لیے کوئی جائے پناہ نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,36,'42:36','جو کچھ بھی تم لوگوں کو دیا گیا ہے وہ محض دنیا کی چند روزہ زندگی کا سروسامان ہے، اور جو کچھ اللہ کے ہاں ہے وہ بہتر بھی ہے اور پائیدار بھی۔ وہ اُن لوگوں کے لیے ہے جو ایمان لائے ہیں اور اپنے ربّ پر بھروسہ کرتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(42,37,'42:37','جو بڑے بڑے گناہوں اور بے حیائی کے کاموں سے پرہیز کرتے ہیں اور اگر غصّہ آجائے تو درگزر کر جاتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(42,38,'42:38','جو اپنے ربّ کا حکم مانتے ہیں، نماز قائم کرتے ہیں، اپنے معاملات آپس کے مشورے سے چلاتے ہیں، ہم نے جو کچھ بھی رزق انہیں دیا ہے اُس میں سے خرچ کرتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(42,39,'42:39','اور جب ان پر زیادتی کی جاتی ہے تو اس کا مقابلہ کرتے ہیں ');
INSERT INTO "ur_maududi" VALUES(42,40,'42:40',' بُرائی کا بدلہ ویسی ہی بُرائی ہے ، پھر جو کوئی معاف کردے اور اصلاح کرے اُس کا اجر اللہ کے ذمّہ ہے، اللہ ظالموں کو پسند نہیں کرتا۔ ');
INSERT INTO "ur_maududi" VALUES(42,41,'42:41','اور جو لوگ ظلم ہونے کے بعد بدلہ لیں اُن کو ملامت نہیں کی جا سکتی');
INSERT INTO "ur_maududi" VALUES(42,42,'42:42','ملامت کے مستحق تو وہ ہیں جو دوسروں پر ظلم کرتے ہیں اور زمین میں ناحق زیادتیاں کرتے ہیں ایسے لوگوں کے لیے دردناک عذاب ہے');
INSERT INTO "ur_maududi" VALUES(42,43,'42:43','البتہ جو شخص صبر سے کام لے اور درگزر کرے ، تو یہ بڑی اُولو العزمی کے کاموں میں سے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,44,'42:44','جس کو اللہ ہی گمراہی میں پھینک دے اُس کا کوئی سنبھالنے والا اللہ کے بعد نہیں ہے۔ تم دیکھو گے کہ یہ ظالم جب عذاب دیکھیں گے تو کہیں گے کہ اب پلٹنے کی بھی کوئی سبیل ہے؟ ');
INSERT INTO "ur_maududi" VALUES(42,45,'42:45','اور تم دیکھوں گے کہ یہ جہنّم کے سامنے جب لائے جائیں گے تو ذلّت کے مارے جُھکے جا رہے ہوں گے اور اُس کو نظر بچا بچا کر کن اَنکھیوں سے دیکھیں گے۔ اُس وقت وہ لوگ جو ایمان لائے تھے کہیں گے کہ واقعی اصل زیاں کار وہی ہیں جنہوں نے آج قیامت کے دن اپنے آپ کو اور اپنے متعلّقین کو خسارے میں ڈال دیا ہے۔ خبر دار رہو، ظالم لوگ مستقل عذاب میں ہوں گے');
INSERT INTO "ur_maududi" VALUES(42,46,'42:46','اور ان کے کوئی حامی و سرپرست نہ ہوں گے جو اللہ کے مقابلے میں ان کی مدد کو آئیں جسے اللہ گمراہی میں پھینک دے اس کے لیے بچاؤ کی کوئی سبیل نہیں');
INSERT INTO "ur_maududi" VALUES(42,47,'42:47','مان لو اپنے ربّ کی بات قبل اس کے کہ وہ دن آئے جس کے ٹلنے کی کوئی صُورت اللہ کی طرف سے نہیں ہے۔ اُس دن تمہارے لیے کوئی جائے پناہ نہ ہو گی اور نہ کوئی تمہارے حال کو بدلنے کی کوشش کرنے والا ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(42,48,'42:48','اب اگر یہ لوگ مُنہ موڑتے ہیں تو اے نبیؐ ، ہم نے تم کو ان پر نگہبان بنا کر تو نہیں بھیجا ہے۔ تم پر تو صرف بات پہنچا دینے کی ذمّہ داری ہے۔ انسان کا حال یہ ہے کہ جب ہم اسے اپنی رحمت کا مزا چکھاتے ہیں تو اُس پر پھُول جاتا ہے، اور اگر اس کے اپنے ہاتھوں کا کیا دھرا کسی مصیبت کی شکل میں اُس پر اُلٹ پڑتا ہے تو سخت ناشکرا بن جاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,49,'42:49','اللہ زمین اور آسمانوں کی بادشاہی کا مالک ہے ، جو کچھ چاہتا ہے پیدا کرتا ہے ، جسے چاہتا ہے لڑکیاں دیتا ہے ، جسے چاہتا ہے لڑکے دیتا ہے');
INSERT INTO "ur_maududi" VALUES(42,50,'42:50','جسے چاہتا ہے لڑکے اور لڑکیاں مِلا جُلا کر دیتا ہے، اور جسے چاہتا ہے بانجھ کر دیتا ہے۔ وہ سب کچھ جانتا اور ہر چیز پر قادر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,51,'42:51',' کسی بشر کا یہ مقام نہیں ہے کہ اللہ اُس سے رُوبرُو بات کرے۔ اُس کی بات یا تو وحی (اشارے)کے طور پر ہوتی ہے ، یا پردے کے پیچھے سے ، یا پھر وہ کوئی پیغام بر (فرشتہ)بھیجتا ہے اور وہ اُس کے حکم سے جو کچھ وہ چاہتا ہے، وحی کرتا ہے، وہ برتر اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(42,52,'42:52','اور اِسی طرح (اے محمدؐ)ہم نے اپنے حکم سے ایک روح تمہاری طرف وحی کی ہے۔ تمہیں کچھ پتہ نہ تھا کہ کتاب کیا ہوتی ہے اور ایمان کیا ہوتا ہے، مگر اُس رُوح کو ہم نے ایک روشنی بنا دیا جس سے ہم راہ دکھاتے ہیں اپنے بندوں میں سے جسے چاہتے ہیں۔ یقیناً تم سیدھے راستے کی طرف رہنمائی کر رہے ہو');
INSERT INTO "ur_maududi" VALUES(42,53,'42:53','اُس خدا کے راستے کی طرف جو زمین اور آسمانوں کی ہر چیز کا مالک ہے۔ خبردار رہو، سارے معاملات اللہ ہی کی طرف رجُوع کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(43,1,'43:1','ح م');
INSERT INTO "ur_maududi" VALUES(43,2,'43:2','قسم ہے اِس واضح کتاب کی');
INSERT INTO "ur_maududi" VALUES(43,3,'43:3','کہ ہم نے اِسے عربی زبان کا قرآن بنایا ہے تاکہ تم لوگ اِسے سمجھو۔ ');
INSERT INTO "ur_maududi" VALUES(43,4,'43:4','اور درحقیقت یہ اُمّ الکتاب میں ثبت ہے ، ہمارے ہاں بڑی بلند مرتبہ اور حکمت سے لبریز کتاب۔ ');
INSERT INTO "ur_maududi" VALUES(43,5,'43:5','اب کیا ہم تم سے بےزار ہو کر یہ درسِ نصیحت تمہارے ہاں بھیجنا چھوڑ دیں صرف اِس لیے کہ تم حد سے گزرے ہوئے لوگ ہو؟ ');
INSERT INTO "ur_maududi" VALUES(43,6,'43:6','پہلے گزری ہوئی قوموں میں بھی بارہا ہم نے نبی بھیجے ہیں');
INSERT INTO "ur_maududi" VALUES(43,7,'43:7','کبھی ایسا نہیں ہوا کہ کوئی نبی اُن کے ہاں آیا ہو اور انہوں نے اُس کا مذاق نہ اُڑایا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(43,8,'43:8','پھر جو لوگ اِن سے بدرجہا زیادہ طاقتور تھے اُنہیں ہم نے ہلاک کر دیا ، پچھلی قوموں کی مثالیں گزر چکی ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(43,9,'43:9','اگر تم اِن لوگوں سے پوچھو کہ زمین اور آسمانوں کو کس نے پیدا کیا ہے تو یہ خود کہیں گے کہ انہیں اُسی زبردست علیم ہستی نے پیدا کیا ہے');
INSERT INTO "ur_maududi" VALUES(43,10,'43:10','وہی نا جس نے تمہارے لیے اس زمین کو گہوارہ بنایا اور اس میں تمہاری خاطر راستے بنادیے تاکہ تم لوگ اپنی منزلِ مقصود کی راہ پا سکو۔ ');
INSERT INTO "ur_maududi" VALUES(43,11,'43:11','جس نے ایک خاص مقدار میں آسمان سے پانی اُتارا اور اس کے ذریعہ سے مُردہ زمین کو جِلا اُٹھایا، اِسی طرح ایک روز تم زمین سے برآمد کیے جاوٴ گے۔ ');
INSERT INTO "ur_maududi" VALUES(43,12,'43:12','وہی جس نے یہ تمام جوڑے پیدا کیے، اور جس نے تمہارے لیے کشتیوں اور جانوروں کو سواری بنایا تاکہ تم اُن کی پُشت پر چڑھو');
INSERT INTO "ur_maududi" VALUES(43,13,'43:13','اور جب اُن پر بیٹھو تو اپنے ربّ کا احسان یاد کرو اور کہو کہ ”پاک ہے وہ جس نے ہمارے لیے اِن چیزوں کر مسخّر کر دیا ورنہ ہم انہیں قابُو میں لانے کی طاقت نہ رکھتے تھے ، ');
INSERT INTO "ur_maududi" VALUES(43,14,'43:14','اور ایک روز ہمیں اپنے ربّ کی طرف پلٹنا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(43,15,'43:15','(یہ سب کچھ جانتے اور مانتے ہوئے بھی)اِن لوگوں نے اُس کے بندوں میں سے بعض کو اس کا جُز بنا ڈالا، حقیقت یہ ہے کہ انسان کھُلا احسان فراموش ہے');
INSERT INTO "ur_maududi" VALUES(43,16,'43:16','کیا اللہ نے اپنی مخلوق میں سے اپنے لیے بیٹیاں انتخاب کیں اور تمہیں بیٹوں سے نوازا؟');
INSERT INTO "ur_maududi" VALUES(43,17,'43:17','اور حال یہ ہے کہ جس اولاد کو یہ لوگ اُس خدائے رحمٰن کی طرف منسُوب کرتے ہیں اُس کی ولادت کا مژدہ جب خود اِن میں سے کسی کو دیا جاتا ہے تو اُس کے مُنہ پر سیاہی چھا جاتی ہے اور وہ غم سے بھر جاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(43,18,'43:18','کیا اللہ کے حصّے میں وہ اولاد آئی جو زیوروں میں پالی جاتی ہے اور بحث و حجّت میں اپنا مدّعا پُوری طرح واضح بھی نہیں کر سکتی؟ ');
INSERT INTO "ur_maududi" VALUES(43,19,'43:19','انہوں نے فرشتوں کو، جو خدائے رحمٰن کے خاص بندے ہیں ، عورتیں قرار دے لیا۔ کیا اُن کے جسم کی ساخت اِنہوں نے دیکھی ہے ؟ اِن کی گواہی لکھ لی جائے گی اور انہیں اس کی جواب دہی کرنا ہوگی');
INSERT INTO "ur_maududi" VALUES(43,20,'43:20','یہ کہتے ہیں”اگر خدائے رحمٰن چاہتا (کہ ہم اُن کی عبادت نہ کریں)تو ہم کبھی اُن کو نہ پُوجتے۔“ یہ اس معاملے کی حقیقت کو قطعی نہیں جانتے، محض تیر تُکّے لڑاتے ہیں');
INSERT INTO "ur_maududi" VALUES(43,21,'43:21','کیا ہم نے اِس سے پہلے کوئی کتاب اِن کو دی تھی جس کی سَنَد (اپنی ملائکہ پرستی کے لیے)یہ اپنے پاس رکھتے ہوں؟ ');
INSERT INTO "ur_maududi" VALUES(43,22,'43:22','نہیں ، بلکہ یہ کہتے ہیں کہ ہم نے اپنے باپ دادا کو ایک طریقے پر پایا ہے اور ہم اُنہی کے نقشِ قدم پر چل رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(43,23,'43:23','اِسی طرح تم سے پہلے جس بستی میں بھی ہم نے کوئی نذیر بھیجا، اُس کے کھاتے پیتے لوگوں نے یہی کہا کہ ہم نے اپنے باپ دادا کو ایک طریقے پر پایا ہے اور ہم اُنہی کے نقشِ قدم کی پیروی کررہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(43,24,'43:24','ہر نبی نے ان سے پوچھا، کیا تم اُسی ڈگر پر چلے جاؤ گے خواہ میں اُس راستے سے زیادہ صحیح راستہ تمہیں بتاؤں جس پر تم نے اپنے باپ دادا کو پایا ہے؟ انہوں نے سارے رسولوں کو یہی جواب دیا کہ جس دین کی طرف بلانے کے لیے تم بھیجے گئے ہو ہم اُس کے کافر ہیں');
INSERT INTO "ur_maududi" VALUES(43,25,'43:25','آخر کار ہم نے اُن کی خبر لے ڈالی اور دیکھ لو کہ جھٹلانے والوں کا کیا انجام ہوا');
INSERT INTO "ur_maududi" VALUES(43,26,'43:26','یاد کرو وہ وقت جب ابراہیم ؑ نے اپنے باپ اور اپنی قوم سے کہا تھا کہ”تم جن کی بندگی کرتے ہو میرا اُن سے کوئی تعلق نہیں');
INSERT INTO "ur_maududi" VALUES(43,27,'43:27','میرا تعلق صرف اُس سے ہے جس نے مجھے پیدا کیا، وہی میری رہنمائی کرے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(43,28,'43:28','اور ابراہیم ؑ یہی کلمہ اپنے پیچھے اپنی اولاد میں چھوڑ گیا تاکہ وہ اِس کی طرف رجوع کریں۔ ');
INSERT INTO "ur_maududi" VALUES(43,29,'43:29','(اس کے باوجود جب یہ لوگ دوسروں کی بندگی کرنے لگے تو میں نے ان کو مٹا نہیں دیا) بلکہ میں اِنہیں اور اِن کے باپ دادا کو متاعِ حیات دیتا رہا یہاں تک کہ اِن کے پاس حق ، اور کھول کھول کر بیان کرنے والا رسُول آگیا۔ ');
INSERT INTO "ur_maududi" VALUES(43,30,'43:30','مگر جب وہ حق اِن کے پاس آیا تو اِنہوں نے کہہ دیا کہ یہ تو جادُو ہے اور ہم اس کو ماننے سے انکار کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(43,31,'43:31','کہتے ہیں، یہ قرآن دونوں شہروں کے بڑے آدمیوں میں سے کسی پر کیوں نازل نہ کیا گیا؟ ');
INSERT INTO "ur_maududi" VALUES(43,32,'43:32','کیا تیرے ربّ کی رحمت یہ لوگ تقسیم کرتے ہیں؟ دنیا کی زندگی میں اِن کی گزر بسر کے ذرائع تو ہم نے اِن کے درمیان تقسیم کیے ہیں ، اور اِن میں سے کچھ لوگوں کو کچھ دُوسرے لوگوں پر ہم نے درجہا فوقیت دی ہے تاکہ یہ ایک دُوسرے سے خدمت لیں۔ اور تیرے ربّ کی رحمت اُس دولت سے زیادہ قیمتی ہے جو (اِن کے رئیس)سمیٹ رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(43,33,'43:33','اگر یہ اندیشہ نہ ہوتا کہ سارے لوگ ایک ہی طریقے کے ہو جائیں گے تو خدائے رحمان سے کفر کرنے والوں کے گھروں کی چھتیں، اور ان کی سیڑھیاں جن سے وہ اپنے بالا خانوں پر چڑھتے ہیں');
INSERT INTO "ur_maududi" VALUES(43,34,'43:34','اور اُن کے دروازے، اور ان کے تخت جن پر وہ تکیے لگا کر بیٹھتے ہیں');
INSERT INTO "ur_maududi" VALUES(43,35,'43:35','سب چاندی اور سونے کے بنوا دیتے۔ یہ تو محض حیاتِ دنیا کی متاع ہے، اور آخرت تیرے ربّ کے ہاں صرف متّقین کے لیے ہے');
INSERT INTO "ur_maududi" VALUES(43,36,'43:36','جو شخص رحمٰن کے ذکر سے تغافل برتتا ہے ، ہم اس پر ایک شیطان مسلّط کر دیتے ہیں اور وہ اُس کا رفیق بن جا تا ہے');
INSERT INTO "ur_maududi" VALUES(43,37,'43:37','یہ شیاطین ایسے لوگو ں کو راہ راست پر آنے سے روکتے ہیں، اور وہ اپنی جگہ یہ سمجھتے ہیں کہ ہم ٹھیک جا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(43,38,'43:38','آخر کار جب یہ شخص ہمارے ہاں پہنچے گا تو اپنے شیطان سے کہے گا، "کاش میرے اور تیرے درمیان مشرق و مغرب کا بُعد ہوتا، تُو تو بد ترین ساتھی نکلا"');
INSERT INTO "ur_maududi" VALUES(43,39,'43:39','اُس وقت اِن لوگوں سے کہا جائے گا کہ جب تم ظلم کر چکے تو آج یہ بات تمہارے لیے کچھ بھی نافع نہیں ہے کہ تم اور تمہارے شیاطین عذاب میں مشترک ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(43,40,'43:40','اب کیا اے نبی ؐ ، تم بہروں کو سناوٴ گے؟ یا اندھوں اور صریح گمراہی میں پڑے ہوئے لوگوں کو راہ دکھاوٴ گے؟ ');
INSERT INTO "ur_maududi" VALUES(43,41,'43:41','اب تو ہمیں اِن کو سزا دینی ہے خواہ تمہیں دنیا سے اٹھا لیں');
INSERT INTO "ur_maududi" VALUES(43,42,'43:42','یا تم کو آنکھوں سے اِن کا وہ انجام دکھا دیں جس کا ہم نے اِن سے وعدہ کیا ہے ، ہمیں اِن پر پُوری قدرت حاصل ہے۔ ');
INSERT INTO "ur_maududi" VALUES(43,43,'43:43','تم بہر حال اُس کتاب کو مضبوطی سے تھامے رہو جو وحی کے ذریعہ سے تمہارے پاس بھیجی گئی ہے ، یقیناً تم سیدھے راستے پر ہو۔ ');
INSERT INTO "ur_maududi" VALUES(43,44,'43:44','حقیقت یہ ہے کہ یہ کتاب تمہارے لیے اور تمہاری قوم کے لیے ایک بہت بڑا شرف ہے اور عنقریب تم لوگوں کو اس کی جواب دہی کرنی ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(43,45,'43:45','تم سے پہلے ہم نے جتنے رسُول بھیجے تھے اُن سب سے پوچھ دیکھو، کیا ہم نے خدائے رحمٰن کے سوا کچھ دُوسرے معبُود بھی مقرر کیے تھے کہ اُن کی بندگی کی جائے؟ ');
INSERT INTO "ur_maududi" VALUES(43,46,'43:46',' ہم نے موسیٰ ؑ کو اپنی نشانیوں کے ساتھ فرعون اور اُس کے اعیانِ سلطنت کے پاس بھیجا، اور اس نے جاکر کہا کہ میں ربّ العالمین کا رسُول ہوں');
INSERT INTO "ur_maududi" VALUES(43,47,'43:47','پھر جب اُس نے ہماری نشانیاں ان کے سامنے پیش کیں تو وہ ٹھٹھے مارنے لگے');
INSERT INTO "ur_maududi" VALUES(43,48,'43:48','ہم ایک پر ایک ایسی نشانی اُن کو دکھاتے چلے گئے جو پہلی سے بڑھ چڑھ کر تھی، اور ہم نے اُن کو عذاب میں دھر لیا تاکہ وہ اپنی روش سے باز آئیں۔ ');
INSERT INTO "ur_maududi" VALUES(43,49,'43:49','ہر عذاب کے موقع پر وہ کہتے، اے ساحر، اپنے رب کی طرف سے جو منصب تجھے حاصل ہے اُس کی بنا پر ہمارے لیے اُس سے دعا کر، ہم ضرور راہ راست پر آ جائیں گے');
INSERT INTO "ur_maududi" VALUES(43,50,'43:50','مگر جُوں ہی کہ ہم ان پر سے عذاب ہٹا دیتے وہ اپنی بات سے پھر جاتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(43,51,'43:51','ایک روز فرعون نے اپنی قوم کی درمیان پُکار کر کہا” لوگو، کیا مصر کی بادشاہی میری نہیں ہے، اور یہ نہریں میرے نیچے نہیں بہہ رہی ہیں؟ کیا تم لوگوں کو نظر نہیں آتا ؟ ');
INSERT INTO "ur_maududi" VALUES(43,52,'43:52','میں بہتر ہوں یا یہ شخص جو ذلیل و حقیر ہے اور اپنی بات بھی کھول کر بیان نہیں کر سکتا؟ ');
INSERT INTO "ur_maududi" VALUES(43,53,'43:53','کیوں نہ اس پر سونے کے کنگن اُتارے گئے؟ یا فرشتوں کا ایک دستہ اس کی اردلی میں نہ آیا؟“ ');
INSERT INTO "ur_maududi" VALUES(43,54,'43:54','اُس نے اپنی قوم کو ہلکا سمجھا اور انہوں نے اس کی اطاعت کی، درحقیقت وہ تھے ہی فاسق لوگ۔ ');
INSERT INTO "ur_maududi" VALUES(43,55,'43:55','آخر کار جب انہوں نے ہمیں غضب ناک کر دیا تو ہم نے ان سے انتقام لیا اور ان کو اکٹھا غرق کر دیا');
INSERT INTO "ur_maududi" VALUES(43,56,'43:56','اور بعد والوں کے لیے پیش رو اور نمونہٴ عبرت بنا کر رکھ دیا۔ ');
INSERT INTO "ur_maududi" VALUES(43,57,'43:57','اور جونہی کہ ابن مریم کی مثال دی گئی، تمہاری قوم کے لوگوں نے اس پر غل مچا دیا');
INSERT INTO "ur_maududi" VALUES(43,58,'43:58','اور لگے کہنے کہ ہمارے معبُود اچھے ہیں یا وہ؟ یہ مثال وہ تمہارے سامنے محض کج بحثی کے لیے لائے ہیں ، حقیقت یہ ہے کہ یہ ہیں ہی جھگڑالو لوگ');
INSERT INTO "ur_maududi" VALUES(43,59,'43:59','ابنِ مریم ؑ اِس کے سوا کچھ نہ تھا کہ ایک بندہ تھا جس پر ہم نے انعام کیا اور بنی اسرائیل کے لیے اپنی قدرت کا ایک نمونہ بنا دیا۔ ');
INSERT INTO "ur_maududi" VALUES(43,60,'43:60','ہم چاہیں تو تم سے فرشتے پیدا کر دیں جو زمین میں تمہارے جانشین ہوں');
INSERT INTO "ur_maududi" VALUES(43,61,'43:61','اور وہ دراصل قیامت کی ایک نشانی ہے، پس تم اُس میں شک نہ کرو ، اور میری بات مان لو، یہی سیدھا راستہ ہے');
INSERT INTO "ur_maududi" VALUES(43,62,'43:62','ایسا نہ ہو شیطان تم کو اُس سے روک دے کہ وہ تمہارا کھُلا دشمن ہے');
INSERT INTO "ur_maududi" VALUES(43,63,'43:63','اور جب عیسیٰؑ صریح نشانیاں لیے ہوئے آیا تھا تو اس نے کہا تھا کہ "میں تم لوگوں کے پاس حکمت لے کر آیا ہوں، اور اس لیے آیا ہوں کہ تم پر بعض اُن باتوں کی حقیقت کھول دوں جن میں تم اختلاف کر رہے ہو، لہٰذا تم اللہ سے ڈرو اور میری اطاعت کرو');
INSERT INTO "ur_maududi" VALUES(43,64,'43:64','حقیقت یہ ہے کہ اللہ ہی میرا ربّ بھی اور تمہارا ربّ بھی۔ اُسی کی تم عبادت کرو ، یہی سیدھا راستہ ہے۔ “');
INSERT INTO "ur_maududi" VALUES(43,65,'43:65','مگر(اُس کی اِس صاف تعلیم کے باوجود)گروہوں نے آپس میں اختلاف کیا، پس تباہی ہے اُن لوگوں کے لیے جنہوں نے ظلم کیا ایک دردناک دن کے عذاب سے');
INSERT INTO "ur_maududi" VALUES(43,66,'43:66','کیا یہ لوگ اب بس اِسی چیز کے منتظر ہیں کہ اچانک اِن پر قیامت آ جائے اور انہیں خبر بھی نہ ہو؟');
INSERT INTO "ur_maududi" VALUES(43,67,'43:67','وہ دن جب آئے گا تو متّقین کو چھوڑ کر باقی سب دوست ایک دُوسرے کے دشمن ہو جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(43,68,'43:68','اس روز ان سے کہا جائے گا "اے میرے بندو، آج تمہارے لیے کوئی خوف نہیں اور نہ تمہیں کوئی غم لاحق ہوگا"');
INSERT INTO "ur_maududi" VALUES(43,69,'43:69','جو ہماری آیات پر ایمان لائے تھے اور مطیع فرمان بن کر رہے تھے');
INSERT INTO "ur_maududi" VALUES(43,70,'43:70','داخل ہوجاوٴ جنّت میں تم اور تمہاری بیویاں ، تمہیں خوش کر دیا جائے گا۔“');
INSERT INTO "ur_maududi" VALUES(43,71,'43:71','اُن کے آگے سونے کے تھال اور ساغر گردش کرائے جائیں گے اور ہر من بھاتی اور نگاہوں کو لذت دینے والی چیز وہاں موجود ہو گی ان سے کہا جائے گا، "تم اب یہاں ہمیشہ رہو گے');
INSERT INTO "ur_maududi" VALUES(43,72,'43:72','تم اِس جنت کے وارث اپنے اُن اعمال کی وجہ سے ہوئے ہو جو تم دنیا میں کرتے رہے');
INSERT INTO "ur_maududi" VALUES(43,73,'43:73','تمہارے لیے یہاں بکثرت فواکہ موجود ہیں جنہیں تم کھاؤ گے"');
INSERT INTO "ur_maududi" VALUES(43,74,'43:74','رہے مجرمین، تو وہ ہمیشہ جہنم کے عذاب میں مبتلا رہیں گے');
INSERT INTO "ur_maududi" VALUES(43,75,'43:75','کبھی اُن کے عذاب میں کمی نہ ہو گی، اور وہ اس میں مایوس پڑے ہوں گے');
INSERT INTO "ur_maududi" VALUES(43,76,'43:76','ان پر ہم نے ظلم نہیں کیا بلکہ وہ خود ہی اپنے اوپر ظلم کرتے رہے');
INSERT INTO "ur_maududi" VALUES(43,77,'43:77','وہ پکاریں گے”اے مالک، تیرا ربّ ہمارا کام ہی تمام کر دے تو اچھا ہے۔“ وہ جواب دے گا”تم یوں ہی پڑے رہو گے');
INSERT INTO "ur_maududi" VALUES(43,78,'43:78','ہم تمہارے پاس حق لے کر آئے تھے مگر تم میں سے اکثر کو حق ہی ناگوار تھا۔“ ');
INSERT INTO "ur_maududi" VALUES(43,79,'43:79','کیا اِن لوگوں نے کوئی اِقدام کرنے کا فیصلہ کر لیا ہے؟ اچھا تو ہم بھی پھر ایک فیصلہ کیے لیتے ہیں');
INSERT INTO "ur_maududi" VALUES(43,80,'43:80','کیا اِنہوں نے یہ سمجھ رکھا ہے کہ ہم اِن کی راز کی باتیں اور اِن کی سرگوشیاں سنتے نہیں ہیں؟ ہم سب کچھ سن رہے ہیں اور ہمارے فرشتے اِن کے پاس ہی لکھ رہے ہیں');
INSERT INTO "ur_maududi" VALUES(43,81,'43:81','اِن سے کہو”اگر واقعی رحمٰن کی کوئی اولاد ہوتی تو سب سے پہلے عبادت کرنے والا میں ہوتا۔“ ');
INSERT INTO "ur_maududi" VALUES(43,82,'43:82','پاک ہے آسمانوں اور زمین کا فرماں روا عرش کا مالک، اُن ساری باتوں سے جو یہ لوگ اُس کی طرف منسوب کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(43,83,'43:83','اچھا، اِنہیں اپنے باطل خیالات میں غرق اور اپنے کھیل میں منہمک رہنے دو، یہاں تک کہ یہ اپنا وہ دن دیکھ لیں جس کا اِنہیں خوف دلایا جا رہا ہے');
INSERT INTO "ur_maududi" VALUES(43,84,'43:84','وہی ایک آسمان میں بھی خدا ہے اور زمین میں بھی خدا، اور وہی حکیم و علیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(43,85,'43:85','بہت بالا و برتر ہے وہ جس کے قبضے میں زمین اور آسمانوں اور ہر اُس چیز کی بادشاہی ہے جو زمین و آسمان کے درمیان پائی جاتی ہے۔ اور وہی قیامت کی گھڑی کا علم رکھتا ہے، اور اسی کی طرف تم سب پلٹائے جانے والے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(43,86,'43:86','اُس کو چھوڑ کر یہ لوگ جنہیں پکارتے ہیں وہ کسی شفاعت کا اختیار نہیں رکھتے، اِلّا یہ کہ کوئی علم کی بنا پر حق کی شہادت دے۔ ');
INSERT INTO "ur_maududi" VALUES(43,87,'43:87','اور اگر تم اِن سے پوچھو کہ اِنہیں کس نے پیدا کیا ہے تو یہ خود کہیں گے کہ اللہ نے۔ پھر کہاں سے یہ دھوکا کھا رہے ہیں');
INSERT INTO "ur_maududi" VALUES(43,88,'43:88','قسم ہے رسُول ؐ کے اِس قول کی کہ اے ربّ ، یہ وہ لوگ ہیں جو مان کر نہیں دیتے۔ ');
INSERT INTO "ur_maududi" VALUES(43,89,'43:89','اچھا، اے نبی ؐ ، اِن سے درگزر کرو اور کہہ دو کہ سلام ہے تمہیں ، عنقریب اِنہیں معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(44,1,'44:1','ح م');
INSERT INTO "ur_maududi" VALUES(44,2,'44:2','قسم ہے اِس کتاب مبین کی');
INSERT INTO "ur_maududi" VALUES(44,3,'44:3','کہ ہم نے اِسے ایک بڑی خیر و برکت والی رات میں نازل کیا ہے، کیونکہ ہم لوگوں کو متنبّہ کرنے کا ارادہ رکھتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(44,4,'44:4','یہ وہ رات تھی جس میں ہر معاملہ کا حکیمانہ فیصلہ ');
INSERT INTO "ur_maududi" VALUES(44,5,'44:5','ہمارے حکم سے صادر کیا جاتا ہے۔ ہم ایک رسُول بھیجنے والے تھے');
INSERT INTO "ur_maududi" VALUES(44,6,'44:6','تیرے ربّ کی رحمت کے طو ر پر۔ یقیناً وہی سب کچھ سُننے اور جاننے والا ہے، ');
INSERT INTO "ur_maududi" VALUES(44,7,'44:7','آسمانوں اور زمین کا ربّ اور ہر اُس چیز کا ربّ جو آسمان و زمین کے درمیان ہے اگر تم لوگ واقعی یقین رکھنے والے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(44,8,'44:8','کوئی معبُود اُس کے سوا نہیں ہے۔ وہی زندگی عطا کرتا ہے اور وہی موت دیتا ہے۔ تمہارا ربّ اور تمہارے اُن اسلاف کا ربّ جو پہلے گزر چکے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(44,9,'44:9','(مگر فی الواقع اِن لوگوں کو یقین نہیں ہے)بلکہ یہ اپنے شک میں پڑے کھیل رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(44,10,'44:10','اچھا انتظار کرو اُس دن کا جب آسمان صریح دھواں لیے ہوئے آئے گا');
INSERT INTO "ur_maududi" VALUES(44,11,'44:11','اور وہ لوگوں پر چھا جائے گا، یہ ہے درد ناک سزا');
INSERT INTO "ur_maududi" VALUES(44,12,'44:12','(اب کہتے ہیں کہ) " پروردگار، ہم پر سے یہ عذاب ٹال دے، ہم ایمان لاتے ہیں"');
INSERT INTO "ur_maududi" VALUES(44,13,'44:13','اِن کی غفلت کہاں دُور ہوتی ہے؟ اِن کا حال تو یہ ہے کہ اِن کے پاس رسُولِ مُبین آگیا ');
INSERT INTO "ur_maududi" VALUES(44,14,'44:14','پھر بھی یہ اُس کی طرف مُلتفت نہ ہوئے اور کہا کہ ”یہ تو سکھایا پڑھایا باولا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(44,15,'44:15','ہم ذرا عذاب ہٹائے دیتے ہیں، تم لوگ پھر وہی کچھ کرو گے جو پہلے کر رہے تھے');
INSERT INTO "ur_maududi" VALUES(44,16,'44:16','جس روز ہم بڑی ضرب لگائیں گے وہ دن ہوگا جب ہم تم سے انتقام لیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(44,17,'44:17','ہم اِن سے پہلے فرعون کی قوم کو اِسی آزمائش میں ڈال چکے ہیں۔ اُن کے پاس ایک نہایت شریف رسُول آیا');
INSERT INTO "ur_maududi" VALUES(44,18,'44:18','اور اس نے کہا” اللہ کے بندوں کو میرے حوالے کرو، میں تمہارے لیے ایک امانتدار رسُول ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(44,19,'44:19','اللہ کے مقابلے میں سرکشی نہ کرو۔ میں تمہارے سامنے (اپنی مامُوریّت کی)صریح سَنَد پیش کرتا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(44,20,'44:20','اور میں اپنے رب اور تمہارے رب کی پناہ لے چکا ہوں اِس سے کہ تم مجھ پر حملہ آور ہو');
INSERT INTO "ur_maududi" VALUES(44,21,'44:21','اگر تم میری بات نہیں مانتے تو مجھ پر ہاتھ ڈالنے سے باز رہو۔“ ');
INSERT INTO "ur_maududi" VALUES(44,22,'44:22','آخر کار اس نے اپنے رب کو پکارا کہ یہ لوگ مجرم ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(44,23,'44:23','(جواب دیا گیا)”اچھا تُو راتوں رات میرے بندوں کو لے کر چل پڑ۔ تم لوگوں کا پیچھا کیا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(44,24,'44:24','سمندر کو اس حال پر کھلا چھوڑ دے۔ یہ سارا لشکر غرق ہونے والا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(44,25,'44:25','" کتنے ہی باغ اور چشمے');
INSERT INTO "ur_maududi" VALUES(44,26,'44:26','اور کھیت اور شاندار محل تھے جو وہ چھوڑ گئے');
INSERT INTO "ur_maududi" VALUES(44,27,'44:27','کتنے ہی عیش کے سر و سامان، جن میں وہ مزے کر رہے تھے اُن کے پیچھے دھرے رہ گئے');
INSERT INTO "ur_maududi" VALUES(44,28,'44:28','یہ ہُوا اُن کا انجام، اور ہم نے دُوسروں کو اِن چیزوں کا وارث بنا دیا۔ ');
INSERT INTO "ur_maududi" VALUES(44,29,'44:29','پھر نہ آسمان اُن پر رویا نہ زمین ، اور ذرا سی مہلت بھی اُن کو نہ دی گئی');
INSERT INTO "ur_maududi" VALUES(44,30,'44:30','اِس طرح بنی اسرائیل کو ہم نے سخت ذلّت کے عذاب ، ');
INSERT INTO "ur_maududi" VALUES(44,31,'44:31','فرعون سے نجات دی جو حد سے گزر جانے والوں میں فی الواقع بڑے اُونچے درجے کا آدمی تھا، ');
INSERT INTO "ur_maududi" VALUES(44,32,'44:32','اور اُن کی حالت جانتے ہوئے اُن کو دنیا کی دُوسری قوموں پر ترجیح دی، ');
INSERT INTO "ur_maududi" VALUES(44,33,'44:33','اور اُنہیں ایسی نشانیاں دکھائیں جن میں صریح آزمائش تھی۔ ');
INSERT INTO "ur_maududi" VALUES(44,34,'44:34','یہ لوگ کہتے ہیں');
INSERT INTO "ur_maududi" VALUES(44,35,'44:35','ہماری پہلی موت کے سوا اور کچھ نہیں، اُس کے بعد ہم دوبارہ اُٹھائے جانے والے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(44,36,'44:36','اگر تم سچے ہو تو اُٹھا لاوٴ ہمارے باپ دادا کو۔“ ');
INSERT INTO "ur_maududi" VALUES(44,37,'44:37','یہ بہتر ہیں یا تُبع کی قوم اور اُس سے پہلے کے لوگ؟ ہم نے ان کو اِسی بنا پر تباہ کیا کہ وہ مُجرم ہوگئے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(44,38,'44:38','یہ آسمان و زمین اور اِن کے درمیان کی چیزیں ہم نے کچھ کھیل کے طور پر نہیں بنا دی ہیں');
INSERT INTO "ur_maududi" VALUES(44,39,'44:39','اِن کو ہم نے برحق پیدا کیا ہے، مگر اکثر لوگ جانتے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(44,40,'44:40','اِن سب کے اُٹھائے جانے کے لیے طے شدہ وقت فیصلے کا دن ہے، ');
INSERT INTO "ur_maududi" VALUES(44,41,'44:41','وہ دن جب کوئی عزیزِ قریب اپنے کسی عزیزِ قریب کے کچھ بھی کام نہ آئے گا اور نہ کہیں سے انہیں کوئٰ مدد پہنچے گی');
INSERT INTO "ur_maududi" VALUES(44,42,'44:42','سوائے اِس کے کہ اللہ ہی کسی پر رحم کرے، وہ زبردست اور رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(44,43,'44:43','زَقّوم کا درخت ');
INSERT INTO "ur_maududi" VALUES(44,44,'44:44','گناہ گار کا کھاجا ہوگا');
INSERT INTO "ur_maududi" VALUES(44,45,'44:45','تیل کی تلچھٹ جیسا، پیٹ میں اِس طرح جوش کھائے گا');
INSERT INTO "ur_maududi" VALUES(44,46,'44:46','جیسے کھولتا ہوا پانی جوش کھاتا ہے');
INSERT INTO "ur_maududi" VALUES(44,47,'44:47','" پکڑو اِسے اور رگیدتے ہوئے لے جاؤ اِس کو جہنم کے بیچوں بیچ');
INSERT INTO "ur_maududi" VALUES(44,48,'44:48','اور انڈیل دو اِس کے سر پر کھولتے پانی کا عذاب');
INSERT INTO "ur_maududi" VALUES(44,49,'44:49','چکھ اس کا مزا، بڑا زبردست عزت دار آدمی ہے تُو');
INSERT INTO "ur_maududi" VALUES(44,50,'44:50','یہ وہی چیز ہے جس کے آنے میں تم لوگ شک رکھتے تھے"');
INSERT INTO "ur_maududi" VALUES(44,51,'44:51','خدا ترس لوگ امن کی جگہ میں ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(44,52,'44:52','باغوں اور چشموں میں');
INSERT INTO "ur_maududi" VALUES(44,53,'44:53','حریر و دیبا کے لباس پہنے، آمنے سامنے بیٹھے ہوں گے');
INSERT INTO "ur_maududi" VALUES(44,54,'44:54','یہ ہوگی ان کی شان۔ اور ہم گوری گوری آہو چشم عورتیں ان سے بیاہ دیں گے');
INSERT INTO "ur_maududi" VALUES(44,55,'44:55','وہاں وہ اطمینان سے ہر طرح کی لذیذ چیزیں طلب کریں گے۔ ');
INSERT INTO "ur_maududi" VALUES(44,56,'44:56','وہاں موت کا مزہ وہ کبھی نہ چکھیں گے، بس دنیا میں جو موت آ چکی سو آ چکی اور اللہ اپنے فضل سے،');
INSERT INTO "ur_maududi" VALUES(44,57,'44:57','ان کو جہنّم کے عذاب سے بچا دے گا، یہی بڑی کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(44,58,'44:58','اے نبیؐ، ہم نے اِس کتاب کو تمہاری زبان میں سہل بنا دیا ہے تاکہ یہ لوگ نصیحت حاصل کریں');
INSERT INTO "ur_maududi" VALUES(44,59,'44:59','اب تم بھی انتظارکرو، یہ بھی منتظر ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(45,1,'45:1','ح م');
INSERT INTO "ur_maududi" VALUES(45,2,'45:2','اِس کتاب کا نزول اللہ کی طرف سے ہے جو زبردست اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(45,3,'45:3','حقیقت یہ ہے کہ آسمانوں اور زمین میں بے شمار نشانیاں ہیں ایمان لانے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(45,4,'45:4','اور تمہاری اپنی پیدائش میں، اور اُن حیوانات میں جن کو اللہ (زمین میں)پھیلا رہا ہے، بڑی نشانیاں ہیں اُن لوگوں کے لیے جو یقین لانے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(45,5,'45:5','اور شب و روز کے فرق و اختلاف میں ، اور اُس رزق میں جسے اللہ آسمان سے نازل فرماتا ہے پھر اس کے ذریعہ سے مُردہ زمین کو جِلا اُٹھاتا ہے، اور ہواوٴں کی گردش میں بہت نشانیاں ہیں اُن لوگوں کے لیے جو عقل سے کام لیتے ہیں');
INSERT INTO "ur_maududi" VALUES(45,6,'45:6','یہ اللہ کی نشانیاں ہیں جنہیں ہم تمہارے سامنے ٹھیک ٹھیک بیان کر رہے ہیں۔ اب آخر اللہ اور اس کی آیات کے بعد اور کون سی بات ہے جس پر یہ لوگ ایمان لائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(45,7,'45:7','تباہی ہے ہر اُس جھوٹے بد اعمال شخص کے لیے');
INSERT INTO "ur_maududi" VALUES(45,8,'45:8','جس کے سامنے اللہ کی آیات پڑھی جاتی ہیں ، اور وہ اُن کو سُنتا ہے، پھر پُورے اِستکبار کے ساتھ اپنے کُفر پر اِس طرح اَڑا رہتا ہے کہ گویا اُس نے اُن کو سُنا ہی نہیں۔ ایسے شخص کو دردناک عذاب کا مُژدہ سُنا دو');
INSERT INTO "ur_maududi" VALUES(45,9,'45:9','ہماری آیات میں سے کوئی بات جب اس کے علم میں آتی ہے تو وہ اُن کا مذاق بنا لیتا ہے۔ ایسے سب لوگوں کے لیے ذلّت کا عذاب ہے');
INSERT INTO "ur_maududi" VALUES(45,10,'45:10','اُن کے آگے جہنّم ہے۔ جو کچھ بھی انہوں نے دنیا میں کمایا ہے اس میں سے کوئی چیز اُن کے کسی کام نہ آئے گی، نہ اُن کے وہ سرپرست ہی اُن کے لیے کچھ کر سکیں گے جنہیں اللہ کو چھوڑ کر انہوں نے اپنا ولی بنا رکھا ہے۔ اُن کے لیے بڑا عذاب ہے');
INSERT INTO "ur_maududi" VALUES(45,11,'45:11','یہ قرآن سراسر ہدایت ہے، اور اُن لوگوں کے لیے بلا کا درد ناک عذاب ہے جنہوں نے اپنے رب کی آیات کو ماننے سے انکار کیا');
INSERT INTO "ur_maududi" VALUES(45,12,'45:12','وہ اللہ ہے تو ہے جس نے تمہارے لیے سمندر کو مسخّر کیاتاکہ اس کے حکم سے کشتیاں اُس میں چلیں اور تم اس کا فضل تلاش کرو اور شکر گزار رہو');
INSERT INTO "ur_maududi" VALUES(45,13,'45:13','اس نے زمین اور آسمانوں کی ساری ہی چیزوں کو تمہارے لیے مسخّر کر دیا ہے، سب کچھ اپنے پاس سے۔۔۔۔ اِس میں بڑی نشانیاں ہیں اُن لوگوں کے لیے جو غور و فکر کرنے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(45,14,'45:14','اے نبی ؐ،ایمان لانے والوں سے کہہ دو کہ جو لوگ اللہ کی طرف سے بُرے دن آنے کا کوئی اندیشہ نہیں رکھتے ، اُں کی حرکتوں پر درگزر سے کام لیں تاکہ اللہ خود ایک گروہ کو اس کی کمائی کا بدلہ دے۔ ');
INSERT INTO "ur_maududi" VALUES(45,15,'45:15','جو کوئی نیک عمل کرے گا اپنے ہی لیے کرے گا، اور جو برائی کرے گا وہ آپ ہی اس کا خمیازہ بھگتے گا پھر جانا تو سب کو اپنے رب ہی کی طرف ہے');
INSERT INTO "ur_maududi" VALUES(45,16,'45:16','اِس سے پہلے بنی اسرائیل کو ہم نے کتاب اور حُکم اور نبوّت عطا کی تھی۔ اُن کو ہم نے عمدہ سامانِ زیست سے نوازا ، دنیا بھر کے لوگوں پر انہیں فضیلت عطا کی، ');
INSERT INTO "ur_maududi" VALUES(45,17,'45:17','اور دین کے معاملہ میں اُنہیں واضح ہدیایت دے دیں۔ پھر جو اختلاف اُن کے درمیان رُونما ہوا وہ (ناواقفیت کی وجہ سے نہیں بلکہ)عِلم آجانے کے بعد ہوا اور اس بنا پر ہوا کہ وہ آپس میں ایک دُوسرے پر زیادتی کرنا چاہتے تھے۔ اللہ قیامت کے روز اُن معاملات کا فیصلہ فرما دے گا جن میں وہ اختلاف کرتے رہے ہیں');
INSERT INTO "ur_maududi" VALUES(45,18,'45:18','اس کے بعد اب اے نبی ؐ ، ہم نے تم کو دین کے معاملہ میں ایک صاف شاہراہ (شریعت)پر قائم کیا ہے۔ لہٰذا تم اسی پر چلو اور اُن لوگوں کی خواہشات کا اتباع نہ کرو جو علم نہیں رکھتے');
INSERT INTO "ur_maududi" VALUES(45,19,'45:19','اللہ کے مقابلے میں وہ تمہارے کچھ بھی کام نہیں آسکتے۔ ظالم لوگ ایک دُوسرے کے ساتھی ہیں ، اور متّقیوں کا ساتھی اللہ ہے');
INSERT INTO "ur_maududi" VALUES(45,20,'45:20','یہ بصیرت کی روشنیاں ہیں سب لوگوں کے لیے اور ہدایت اور رحمت اُن لوگوں کے لیے جو یقین لائیں ');
INSERT INTO "ur_maududi" VALUES(45,21,'45:21','کیا وہ لوگ جنہوں نے بُرائیوں کا ارتکاب کیا ہے یہ سمجھے بیٹھے ہیں کہ ہم اُنہیں اور ایمان لانے والوں اور نیک عمل کرنے والوں کو ایک جیسا کر دیں گے کہ ان کا جینا اور مرنا یکساں ہو جائے؟ بہت بُرے حکم ہیں جو یہ لوگ لگاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(45,22,'45:22','اللہ نے تو آسمانوں اور زمین کو برحق پیدا کیا ہے اور اس لیے کیا ہے کہ ہر متنفّس کو اُس کی کمائی کا بدلہ دیا جائے۔ لوگوں پر ظلم ہر گز نہ کیا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(45,23,'45:23','پھر کیا تم نے کبھی اُس شخص کے حال پر بھی غور کیا جس نے اپنی خواہشِ نفس کو اپنا خدا بنا لیا اور اللہ نے علم کے باوجود اُسے گمراہی میں پھینک دیا اور اُس کے دل اور کانوں پر مُہر لگا دی اور اُس کی آنکھوں پر پردہ ڈال دیا؟ اللہ کے بعد اب اور کون ہے جو اُسے ہدایت دے؟ کیا تم لوگ کوئی سبق نہیں لیتے؟ ');
INSERT INTO "ur_maududi" VALUES(45,24,'45:24','یہ لوگ کہتے ہیں کہ ”زندگی بس یہی ہماری دنیا کی زندگی ہے ، یہیں ہمارا مرنا اور جینا ہے اور گردشِ ایام کے سوا کوئی چیز نہیں جو ہمیں ہلاک کرتی ہو۔“ درحقیقت اِس معاملہ میں اِن کے پاس کو ئی علم نہیں ہے۔ یہ محض گمان کی بنا پر یہ باتیں کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(45,25,'45:25','اور جب ہماری واضح آیات انہیں سُنائی جاتی ہیں تو اِن کے پاس کوئی حجّت اس کے سوا نہیں ہوتی کہ اُٹھا لاوٴ ہمارے باپ داد کو اگر تم سچے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(45,26,'45:26','اے نبی ؐ ، اِن سے کہو، اللہ ہی تمہیں زندگی بخشتا ہے، پھر وہی تمہیں موت دیتا ہے، پھر وہی تم کو اُس قیامت کی دن جمع کر ے گا جس کے آنے میں کوئی شک نہیں، مگر اکثر لوگ جانتے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(45,27,'45:27','زمین اور آسمانوں کی بادشاہی اللہ ہی کی ہے ، اور جس روز قیامت کی گھڑی آکھڑی ہوگی اُس دن باطل پرست خسارے میں پڑ جائیں گے');
INSERT INTO "ur_maududi" VALUES(45,28,'45:28','اُس وقت تم ہر گروہ کو گھٹنوں کے بل گِرا دیکھو گے۔ ہر گروہ کو پکارا جائے گا کہ آئے اور اپنا نامہٴ اعمال دیکھے۔ اُن سے کہا جائے گا”آج تم لوگوں کو اُن اعمال کا بدلہ دیا جائے گا جو تم کرتے رہے تھے');
INSERT INTO "ur_maududi" VALUES(45,29,'45:29','یہ ہمارا تیار کرایا ہوا اعمال نامہ ہے جو تمہارے اُوپر ٹھیک ٹھیک شہادت دے رہا ہے ، جو کچھ بھی تم کرتے تھے اُسے ہم لکھواتے جا رہے تھے۔“ ');
INSERT INTO "ur_maududi" VALUES(45,30,'45:30','پھر جو لوگ ایمان لائے تھے اور نیک عمل کرتے رہے تھے انہیں ان کا رب اپنی رحمت میں داخل کرے گا اور یہی صریح کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(45,31,'45:31','اور جن لوگوں نے کُفر کیا تھا اُن سے کہا جائے گا”کیا میری آیات تم کو نہیں سُنائی جاتی تھیں؟ مگر تم نے تکبّر کیا اور مجرم بن کر رہے');
INSERT INTO "ur_maududi" VALUES(45,32,'45:32','اور جب کہا جاتا تھا کہ اللہ کا وعدہ بر حق ہے اور قیامت کے آنے میں کوئی شک نہیں ، تو تم کہتے تھے کہ ہم نہیں جانتے قیامت کیا ہوتی ہے، ہم تو بس ایک گمان سا رکھتے ہیں، یقین ہم کو نہیں ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(45,33,'45:33','اُس وقت اُن پر ان کے اعمال کی بُرائیاں کھُل جائیں گی اور وہ اُسی چیز کے پھیر میں آجائیں گے جِس کا وہ مذاق اُڑایا کرتے تھے');
INSERT INTO "ur_maududi" VALUES(45,34,'45:34','اور ان سے کہہ دیا جائے گا کہ "آج ہم بھی اُسی طرح تمہیں بھلائے دیتے ہیں جس طرح تم اِس دن کی ملاقات کو بھول گئے تھے تمہارا ٹھکانا اب دوزخ ہے اور کوئی تمہاری مدد کرنے والا نہیں ہے');
INSERT INTO "ur_maududi" VALUES(45,35,'45:35','یہ تمہارا انجام اس لیے ہوا ہے کہ تم نے اللہ کو آیات کا مذاق بنا لیا تھا اور تمہیں دنیا کی زندگی نے دھوکے میں ڈال دیا تھا۔ لہٰذا آج نہ یہ لوگ دوزخ سے نکالے جائیں گے اور نہ ان سے کہا جائے گا کہ معافی مانگ کر اپنے ربّ کو راضی کرو۔“ ');
INSERT INTO "ur_maududi" VALUES(45,36,'45:36','پس تعریف اللہ ہی کے لیے ہے جو زمین اور آسمانوں کا مالک اور سارے جہان والوں کا پروردگار ہے');
INSERT INTO "ur_maududi" VALUES(45,37,'45:37','زمین اور آسمانوں میں بڑائی اُسی کے لیے ہے اور وہی زبردست اور دانا ہے');
INSERT INTO "ur_maududi" VALUES(46,1,'46:1','ح م');
INSERT INTO "ur_maududi" VALUES(46,2,'46:2','اِس کتاب کا نزول اللہ زبردست اور دانا کی طرف سے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(46,3,'46:3','ہم نے زمین اور آسمانوں کو اور اُن ساری چیزوں کو جو اُن کے درمیان ہیں برحق، اور ایک مدّتِ خاص کے تعیّن کے ساتھ پیدا کیا ہے۔ مگر یہ کافر لو گ اُس حقیقت سے مُنہ موڑے ہوئے ہیں جس سے ان کو خبر دار کیا گیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(46,4,'46:4','اے نبی ؐ ، اِن سے کہو”کبھی تم نے آنکھیں کھول کر دیکھا بھی کہ وہ ہستیاں ہیں کیا جنہیں تم خدا کو چھوڑ کر پکار تے ہو؟ ذرا مجھے دکھاوٴ تو سہی کہ زمین میں انہوں نے کیا پیدا کیا ہے، یا آسمانوں کی تخلیق و تدبیر میں ان کا کیا حصّہ ہے۔ اِس سے پہلے آئی ہوئی کوئی کتاب یا علم کا کوئی بقیّہ (اِن عقائد کے ثبوت میں)تمہارے پاس ہو تو وہی لے آوٴ اگر تم سچے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(46,5,'46:5','آخر اُس شخص سے زیادہ بہکا ہوا انسان اور کون ہوگا جو اللہ کو چھوڑ کر اُن کو پکارے جو قیامت تک اسے جواب نہیں دے سکتے بلکہ اِس سے بھی بے خبر ہیں کہ پُکارنے والے اُن کو پکار رہے ہیں ، ');
INSERT INTO "ur_maududi" VALUES(46,6,'46:6','اور جب تمام انسان جمع کیے جائیں گے اُس وقت وہ اپنے پکارنے والوں کے دشمن اور ان کی عبادت کے منکر ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(46,7,'46:7','اِن لوگوں کو جب ہماری صاف صاف آیات سُنائی جاتی ہیں اور حق اِن کے سامنے آجاتا ہے تو یہ کافر لوگ اُس کے متعلق کہتے ہیں کہ کہ تو کھُلا جادو ہے۔ ');
INSERT INTO "ur_maududi" VALUES(46,8,'46:8','کیا اُن کا کہنا یہ ہے کہ رسُول نے اِسے خود گھڑ لیا ہے؟ اِن سے کہو ”اگر میں نے اِسے خود گھڑ لیا ہے تو تم مجھے خدا کی پکڑ سے کچھ بھی نہ بچا سکو گے، جو باتیں تم بناتے ہواللہ ان کو خوب جانتا ہے، میرے اور تمہارے درمیان وہی گواہی دینے کے لیے کافی ہے، اور وہ بڑا درگزر کرنے والا اور رحیم ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(46,9,'46:9','اِن سے کہو”،میں کوئی نرالا رسُول تو نہیں ہوں، میں نہیں جانتا کہ کل تمہارے ساتھ کیا ہونا ہے اور میرے ساتھ کیا، میں تو صرف اُس وحی کی پیروی کرتا ہوں جو میرے پاس بھیجی جاتی ہے اور میں ایک صاف صاف خبردار کردینے والے کے سوا اور کچھ بھی نہیں ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(46,10,'46:10','اے نبی ؐ ، ان سے کہو”کبھی تم نے سوچا بھی کہ اگر یہ کلام اللہ ہی کی طرف سے ہوا اور تم نے اِس کا انکار کر دیا(تو تمہارا کیا انجام ہو گا)؟ اور اِس جیسے ایک کلام پر تو بنی اسرائیل کا ایک گواہ شہادت بھی دے چکا ہے۔ وہ ایمان لے آیا اور تم اپنے گھمنڈ میں پڑے رہے۔ ایسے ظالموں کو اللہ ہدایت نہیں دیا کرتا۔“');
INSERT INTO "ur_maududi" VALUES(46,11,'46:11','جن لوگوں نے ماننے سے انکار کر دیا ہے وہ ایمان لانے والوں کے متعلق کہتے ہیں کہ اگر اس کتاب کو مان لینا کوئی اچھا کام ہوتا تو یہ لوگ اس معاملے میں ہم سے سبقت نہ لے جا سکتے تھے۔ چونکہ اِنہوں نے اُس سے ہدایت نہ پائی اس لیے اب یہ ضرور کہیں گے کہ یہ تو پُرانا جھُوٹ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(46,12,'46:12','حالانکہ اِس سے پہلے موسیٰ ؑ کی کتاب رہنما اور رحمت بن کر آچکی ہے، اور یہ کتاب اُس کی تصدیق کرنے والی زباِنِ عربی میں آئی ہے تاکہ ظالموں کو متنبّہ کر دے اور نیک روش اختیار کرنے والوں کو بشارت دے دے');
INSERT INTO "ur_maududi" VALUES(46,13,'46:13','یقیناً جن لوگوں نے کہہ دیا کہ اللہ ہی ہمارا ربّ ہے ، پھر اُس پر جم گئے ، اُن کے لیے نہ کوئی خوف ہے اور نہ وہ غمگین ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(46,14,'46:14','ایسے سب لوگ جنت میں جانے والے ہیں جہاں وہ ہمیشہ رہیں گے اپنے اُن اعمال کے بدلے جو وہ دنیا میں کرتے رہے ہیں');
INSERT INTO "ur_maududi" VALUES(46,15,'46:15','ہم نے انسان کو ہدایت کی کہ وہ اپنے والدین کے ساتھ نیک برتاوٴ کرے۔ اُس کی ماں نے مشقّت اُٹھا کر اسے پیٹ میں رکھا اور مشقُت اُٹھا کر ہی اس کو جنا، اور اس کے حمل اور دُودھ چھڑانے میں تیس مہینے لگ گئے۔ یہاں تک کہ جب وہ اپنی پُوری طاقت کو پہنچا اور چالیس سال کا ہوگیا تو اُس نے کہا”اے میرے ربّ ، مجھے توفیق دے کہ میں تیری اُن نعمتوں کا شکر ادا کروں جو تُو نے مجھے اور میرے والدین کو عطا فرمائیں، اور ایسا نیک عمل کروں جس سے تُو راضی ہو، اور میری اولاد کو بھی نیک بناکر مجھے سُکھ دے، میں تیرے حضور توبہ کرتا ہوں اور تابع فرمان (مسلم)بندوں میں سے ہوں۔“');
INSERT INTO "ur_maududi" VALUES(46,16,'46:16','اِس طرح کے لوگوں سے ہم اُن کے بہترین اعمال کو قبول کرتے ہیں اور ان کی بُرائیوں سے درگزر کر جاتے ہیں۔ یہ جنّتی لوگوں میں شامل ہوں گے اُس سچے وعدے کے مطابق جو ان سے کیا جاتا رہا ہے');
INSERT INTO "ur_maududi" VALUES(46,17,'46:17','اور جس شخص نے اپنے والدین سے کہا "اُف، تنگ کر دیا تم نے، کیا تم مجھے یہ خوف دلاتے ہو کہ میں مرنے کے بعد پھر قبر سے نکالا جاؤں گا حالانکہ مجھ سے پہلے بہت سی نسلیں گزر چکی ہیں (اُن میں سے تو کوئی اٹھ کر نہ آیا)" ماں اور باپ اللہ کی دوہائی دے کر کہتے ہیں "ارے بدنصیب، مان جا، اللہ کا وعدہ سچا ہے" مگر وہ کہتا ہے "یہ سب اگلے وقتوں کی فرسودہ کہانیاں ہیں"');
INSERT INTO "ur_maududi" VALUES(46,18,'46:18','یہ وہ لوگ ہیں جن پر عذاب کا فیصلہ چسپاں ہو چکا ہے۔ اِن سے پہلے جِنوں اور انسانوں کے جو ٹولے (اِسی قماش کے)ہو گزرے ہیں اُنہی میں یہ بھی جا شامل ہوں گے۔ بے شک یہ گھاٹے میں رہ جانے والے لوگ ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(46,19,'46:19','دونوں گروہوں میں سے ہر ایک کے درجے ان کے اعمال کے لحاظ سے ہیں تاکہ اللہ ان کے کیے کا پُورا پُورا بدلہ ان کو دے۔ ان پر ظلم ہرگز نہ کیا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(46,20,'46:20','پھر جب یہ کافر آگ کے سامنے لاکھڑے کیے جائیں گے تو ان سے کہا جائے گا”تم اپنے حصّے کی نعمتیں اپنی دنیا کی زندگی میں ختم کر چکے اور ان کا لُطف تم نے اُٹھا لیا، اب جو تکبّر تم زمین میں کسی حق کے بغیر کرتے رہے اور جو نافرمانیاں تم نے کیں اُں کی پاداش میں آج تم کو ذلّت کا عذاب دیا جائے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(46,21,'46:21','ذرا اِنھیں عاد کے بھائی (ہُود ؑ)کا قصّہ سُناوٴ جبکہ اُس نے اَحْقاف میں اپنی قوم کو خبردار کیا تھا۔۔۔۔اور ایسے خبردار کرنے والا اُس سے پہلے بھی گزر چکے تھے اس کے بعد بھی آتے رہے۔۔۔۔ کہ ”اللہ کے سوا کسی کی بندگی نہ کرو، مجھے تمہارے حق میں ایک بڑے ہولناک دن کے عذاب کا اندیشہ ہے۔“');
INSERT INTO "ur_maududi" VALUES(46,22,'46:22','انہوں نے کہا "کیا تو اِس لیے آیا ہے کہ ہمیں بہکا کر ہمارے معبودوں سے برگشتہ کر دے؟ اچھا تو لے آ اپنا وہ عذاب جس سے تو ہمیں ڈراتا ہے اگر واقعی تو سچا ہے"');
INSERT INTO "ur_maududi" VALUES(46,23,'46:23','اُس نے کہاکہ”اِس کا علم تو اللہ کو ہے، میں صرف وہ پیغام تمہیں پہنچا رہا ہوں جسے دے کر مجھے بھیجا گیا ہے۔ مگر میں دیکھ رہا ہوں کہ تم لوگ جہالت برت رہے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(46,24,'46:24','پھر جب انہوں نے اُس عذاب کو اپنی وادیوں کی طرف آتے دیکھا تو کہنے لگو”یہ بادل ہے جو ہم کو سیراب کر دے گا۔“۔۔۔۔ ”نہیں، بلکہ یہ وہی چیز ہے جس کے لیے تم جلدی مچا رہے تھے۔ یہ ہوا کا طوفان ہے جس میں دردناک عذاب چلا آرہا ہے');
INSERT INTO "ur_maududi" VALUES(46,25,'46:25','اپنے ربّ کے حکم سے ہر چیز کو تباہ کر ڈالے گا۔“ آخر کار اُن کا حال یہ ہوا کہ اُن کے رہنے کی جگہوں کے سوا وہاں کچھ نظر نہ آتا تھا۔ اِس طرح ہم مجرموں کو بدلہ دیا کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(46,26,'46:26','اُن کو ہم نے وہ کچھ دیا تھا جو تم لوگوں کو نہیں دیا ہے۔ اُن کو ہم نے کان، آنکھیں اور دل، سب کچھ دے رکھے تھے، مگر نہ وہ کان اُن کے کسی کام آئے، نہ آنکھیں، نہ دل، کیونکہ وہ اللہ کی آیات کا انکار کرتے تھے، اور اُسی چیز کے پھیر میں وہ آگئے جس کا وہ مذاق اُڑاتے تھے');
INSERT INTO "ur_maududi" VALUES(46,27,'46:27','تمہارے گرد و پیش کے علاقوں میں بہت سی بستیوں کو ہم ہلاک کر چکے ہیں ہم نے اپنی آیات بھیج کر بار بار طرح طرح سے اُن کو سمجھایا، شاید کہ وہ باز آ جائیں');
INSERT INTO "ur_maududi" VALUES(46,28,'46:28','پھر کیوں نہ اُن ہستیوں نے اُن کی مدد کی جنہیں اللہ کو چھوڑ کر انہوں نے تقرّب اِلی اللہ کا ذریعہ سمجھتے ہوئے معبُود بنا لیا تھا؟ بلکہ وہ تو ان سے کھوئے گئے، اور یہ تھا اُں کے جھُوٹ اور اُن بناوٹی عقیدوں کا انجام جو انہوں نے گھڑ رکھے تھے');
INSERT INTO "ur_maududi" VALUES(46,29,'46:29','(اور وہ واقعہ بھی قابلِ ذکر ہے)جب ہم جِنّوں کے ایک گروہ کو تمہاری طرف لے آئے تھے تاکہ قرآن سُنیں۔ جب وہ اُس جگہ پہنچے (جہاں تم قرآن پڑھ رہے تھے)تو انہوں نے آپس میں کہا خاموش ہوجاوٴ۔ پھر جب وہ پڑھا جا چکا تو وہ خبردار کرنے والے بن کر اپنی قوم کی طرف پلٹے');
INSERT INTO "ur_maududi" VALUES(46,30,'46:30','انہوں نے جا کر کہا”اے ہماری قوم کے لوگو، ہم نے ایک کتاب سُنی ہے جو موسیٰ ؑ کے بعد نازل کی گئی ہے، تصدیق کرنے والی ہے اپنے سے پہلے آئی ہوئی کتابوں کی، رہنمائی کرتی ہے حق اور راہِ راست کی طرف۔ ');
INSERT INTO "ur_maududi" VALUES(46,31,'46:31','اے ہماری قوم کے لوگو، اللہ کی طرف بُلانے والے کی دعوت قبول کر لو اور اس پر ایمان لے آوٴ، اللہ تمہارے گناہوں سے درگزر فرمائے گا اور تمہیں عذابِ الیم سے بچا دے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(46,32,'46:32','اور جو کوئی اللہ کے داعی کی بات نہ مانے وہ نہ زمین میں خود کوئی بل بوتا رکھتا ہے کہ اللہ کو زِچ کر دے، اور نہ اس کے کوئی ایسے حامی و سرپرست ہیں کہ اللہ سے اس کو بچالیں۔ ایسے لوگ کھُلی گمراہی میں پڑے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(46,33,'46:33','اور کیا اِن لوگوں کو یہ سجھائی نہیں دیتا کہ جس خدا نے یہ زمین اور آسمان پیدا کیے ہیں اور ان کو بناتے ہوئے جو نہ تھکا، وہ ضرور اس پر قادر ہے کہ مُردوں کو جلا اٹھائے؟ کیوں نہیں، یقیناً وہ ہر چیز کی قدرت رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(46,34,'46:34','جس روز یہ کافر آگ کے سامنے لائے جائیں گے، اُس وقت اِن سے پوچھا جائے گا "کیا یہ حق نہیں ہے؟" یہ کہیں گے "ہاں، ہمارے رب کی قسم (یہ واقعی حق ہے)" اللہ فرمائے گا، "اچھا تو اب عذاب کا مزا چکھو اپنے اُس انکار کی پاداش میں جو تم کرتے رہے تھے"');
INSERT INTO "ur_maududi" VALUES(46,35,'46:35','پس اے نبی ؐ ، صبر کرو جس طرح اُولو العزم رسُولوں نے صبر کیا ہے ، اور ان کے معاملہ میں جلدی نہ کرو۔ جس روز یہ لوگ اُس چیز کو دیکھ لیں گے جس کا اِنہیں خوف دلا یا جا رہا ہے تو اِنہیں یُوں معلوم ہوگا کہ جیسے دنیا میں دِن کی ایک گھڑی بھر سے زیادہ نہیں رہے تھے۔ بات پہنچا دی گئی ، اب کیا نافرمان لوگوں کے سوا اور کوئی ہلاک ہوگا؟');
INSERT INTO "ur_maududi" VALUES(47,1,'47:1','جن لوگوں نے کفر کیا اور اللہ کے راستے سے روکا، اللہ نے ان کے اعمال کو رائیگاں کردیا۔ ');
INSERT INTO "ur_maududi" VALUES(47,2,'47:2','اور جو لوگ ایما ن لائے اور جنہوں نے نیک عمل کیے اور اس چیز کو مان لیا جو محمد ؐ پر نازل ہوئی ہے۔۔۔۔اور ہے وہ سراسر حق ان کے رب کی طرف سے۔۔۔۔اللہ نے ان کی برائیاں ان سے دور کر دیں اور ان کا حال درست کر دیا۔ ');
INSERT INTO "ur_maududi" VALUES(47,3,'47:3','یہ اس لیے کہ کفر کرنے والوں نے باطل کی پیروی کی اور ایمان لانے والوں نے اس حق کی پیروی کی جو ان کے رب کی طرف سے آیا ہے۔ اس طرح اللہ لوگوں کو ان کی ٹھیک ٹھیک حیثیت بتائے دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(47,4,'47:4','پس جب ان کافروں سے تمہاری مُڈ بھیڑ ہو تو پہلا کام گردنیں مارنا ہے، یہاں تک کہ جب تم ان کو اچھی طرح کچل دو تب قیدیوں کو مضبوط باندھو، اس کے بعد(تمہیں اختیار ہے) احسان کر ویا فدیے کا معاملہ کر لو، تا آنکہ لڑائی اپنے ہتھیار ڈال دے۔ یہ ہے تمہارے کرنے کا کام۔ اللہ چاہتا تو خود ہی ان سے نمٹ لیتا، مگر(یہ طریقہ اس نے اس لیے اختیار کیا ہے)تا کہ تم لوگوں کو ایک دوسرے کے ذریعہ سے آزمائے۔ اور جو لوگ اللہ راہ میں مارے جائیں گے اللہ ان کےاعمال کو ہر گز ضائع نہ کرے گا۔ ');
INSERT INTO "ur_maududi" VALUES(47,5,'47:5','وہ ان کی رہنمائی فرمائے گا، ان کا حال درست کر دے گا');
INSERT INTO "ur_maududi" VALUES(47,6,'47:6','اور ان کو اُس جنت میں داخل کرے گا جس سے وہ ان کو واقف کرا چکا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(47,7,'47:7','اے لوگوں جو ایمان لائے ہو، اگر تم اللہ کی مدد کرو گے تو وہ تمہاری مدد کرے گا اور تمہارے قدم مضبوط جما دے گا');
INSERT INTO "ur_maududi" VALUES(47,8,'47:8','رہے وہ لوگ جنہوں نے کفر کیا ہے، تو ان کے لیے ہلاکت ہے اور اللہ نے ان کے اعمال کو بھٹکا دیا ہے');
INSERT INTO "ur_maududi" VALUES(47,9,'47:9','کیونکہ انہوں نے اس چیز کو نا پسند کیا جسے اللہ نے نازل کیا ہے، لہٰذا اللہ نے ان کے اعمال ضائع کر دیے');
INSERT INTO "ur_maududi" VALUES(47,10,'47:10','کیا وہ زمین میں چلے پھرے نہ تھےکہ ان لوگوں کا انجام دیکھتے جو ان سے پہلے گزر چکے ہیں؟ اللہ نے ان کا سب کچھ ان پر الٹ دیا، اور ایسے ہی نتائج ان کافروں کے لیے مقدر ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(47,11,'47:11','یہ اس لیے کہ ایمان لانے والوں کا حامی و ناصر اللہ ہے اور کافروں کا حامی و ناصر کوئی نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(47,12,'47:12','ایما ن لانے والوں اور نیک عمل کرنے والوں کو اللہ ان جنتوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہیں، اور کفر کرنے والے بس دنیا کی چند روزہ زندگی کے مزے لوٹ رہے ہیں، جانوروں کی طرح کھا پی رہے ہیں، اور ان کا آخری ٹھکانہ جہنم ہے');
INSERT INTO "ur_maududi" VALUES(47,13,'47:13','اے نبی ؐ ، کتنی ہی بستیاں ایسی گزر چکی ہیں جو تمہاری اس بستی سے بہت زیادہ زور آور تھیں جس نے تمہیں نکال دیا ہے۔ انہیں ہم نے اس طرح ہلاک کر دیا کہ کوئی ان کا بچانے والا نہ تھا۔ ');
INSERT INTO "ur_maududi" VALUES(47,14,'47:14','بھلا کہیں ایسا ہو سکتا ہے کہ جو اپنے رب کی طرف سے ایک صاف و صریح ہدایت پر ہو، وہ ان لوگوں کی طرح ہو جا ئے جن کے لیے ان کا برا عمل خوشنما بنا دیا گیا ہے اور وہ اپنی خواہشات کے پیروبن گئے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(47,15,'47:15','پرہیز گاروں کے لیے جس جنت کا وعدہ کیا گیا ہے اس کی شان تو یہ ہے کہ اس میں نہریں بہ رہی ہوں گی نِتھرے ہوئے پانی کی ، نہریں بہ رہی ہوں گی ایسے دودھ کی جس کے مزے میں ذرا فرق نہ آیا ہوگا، نہریں بہہ رہی ہوں گی ایسی شراب کی جو پینے والوں کے لیے لذیز ہو گی ، نہریں بہ رہی ہوں گی صاف شفاف شہد کی۔ اس میں ان کے لیے ہر طرح کے پھل ہوں گے اور ان کے رب کی طرف سے بخشش۔ (کیا وہ شخص جس کے حصہ میں یہ جنت آنے والی ہے)ان لوگوں کی طرح ہو سکتا ہے جو جہنم میں ہمیشہ رہیں گے اور جنہیں ایسا گرم پانی پلایا جائے گا جو ان کی آنتیں تک کاٹ دے گا؟');
INSERT INTO "ur_maududi" VALUES(47,16,'47:16','ان میں سے کچھ لوگ ایسے ہیں جو کان لگا کر تمہاری بات سنتے ہیں اور پھر جب تمہارے پاس سے نکلتے ہیں تو ان لوگوں سے جنہیں علم کی نعمت بخشی گئی ہے پوچھتے ہیں کہ ابھی ابھی انہوں نے کیا کہا تھا ؟ یہ وہ لوگ ہیں جن کے دلوں پر اللہ نے ٹھپّہ لگا دیا ہے اور یہ اپنی خواہشات کے پیرو بنے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(47,17,'47:17','رہے وہ لوگ جنہوں نے ہدایت پائی ہے، اللہ ان کو اور زیادہ ہدایت دیتا ہے اور انہیں ان کے حصے کا تقوٰی عطا فرماتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(47,18,'47:18','اب کیا یہ لوگ بس قیامت ہی کے منتظر ہیں کہ وہ اچانک ان پر آجائے؟ اس کی علامات تو آچکی ہیں۔ جب وہ خود آجائے گی تو ان کے لیے نصیحت قبول کرنے کا کونسا موقع باقی رہ جائے گا؟');
INSERT INTO "ur_maududi" VALUES(47,19,'47:19','پس اے نبیؐ، خوب جان لو کہ اللہ کےسوا کوئی عبادت کا مستحق نہیں ہے، اور معافی مانگو اپنے قصور کے لیے بھی اور مومن مردوں اور عورتوں کے لیے بھی۔ اللہ تمہاری سرگرمیوں کو بھی جانتا ہے اور تمہارے ٹھکانے سے بھی واقف ہے');
INSERT INTO "ur_maududi" VALUES(47,20,'47:20','جو لوگ ایمان لائے ہیں وہ کہہ رہے تھے کہ کوئی سورت کیوں نہیں نازل کی جاتی(جس میں جنگ کو حکم دیا جائے)۔ مگر جب ایک محکم سورت نازل کر دی گئی جس میں جنگ کا ذکر تھا تو تم نے دیکھا کہ جن کے دلوں میں بیماری تھی وہ تمہاری طرف اس طرح دیکھ رہے ہیں جیسے کسی پر موت چھاگئ ہو۔ افسوس ان کے حال پر');
INSERT INTO "ur_maududi" VALUES(47,21,'47:21','(اُن کی زبان پر ہے) اطاعت کا اقرار اور اچھی اچھی باتیں مگر جب قطعی حکم دے دیا گیا اُس وقت وہ اللہ سے اپنے عہد میں سچے نکلتے تو انہی کے لئے اچھا تھا');
INSERT INTO "ur_maududi" VALUES(47,22,'47:22','اب کیا تم لوگوں سے اس کے سوا کچھ اور توقع کی جا سکتی ہے کہ اگر تم الٹے منہ پھر گئے تو زمین میں پھر فساد بر پا کرو گے اور آپس میں ایک دوسرے کے گلے کاٹو گے؟ ');
INSERT INTO "ur_maududi" VALUES(47,23,'47:23','یہ لوگ ہیں جن پر اللہ نے لعنت کی اور ان کو اندھا اور بہرا بنا دیا');
INSERT INTO "ur_maududi" VALUES(47,24,'47:24','کیا ان لوگوں نے قرآن پر غور نہیں کیا، یا دلوں پر ان کے قفل چڑھے ہوئے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(47,25,'47:25','حقیقت یہ ہے کہ جو لوگ ہدایت واضح ہو جانے کے بعد اُس سے پھر گئے اُن کے لیے شیطان نے اِس روش کو سہل بنا دیا ہے اور جھوٹی توقعات کا سلسلہ اُن کے لیے دراز کر رکھا ہے');
INSERT INTO "ur_maududi" VALUES(47,26,'47:26','اسی لیے انہوں نے اللہ کے ناز ل کردہ دین کو ناپسند کرنے والوں سے کہہ دیا کہ بعض معاملات میں ہم تمہاری مانیں گے۔ اللہ ان کی یہ خفیہ باتیں خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(47,27,'47:27','پھر اس وقت کیا حال ہو گا جب فرشتے ان کی روحیں قبض کریں گے اور ان کےمنہ اور پیٹھوں پر مارتے ہوئے انھیں لے جائیں گے؟ ');
INSERT INTO "ur_maududi" VALUES(47,28,'47:28','یہ اسی لیے تو ہو گا کہ انہوں نے اس طریقے کی پیروی کی جو اللہ کو ناراض کرنے والا ہے اور کی رضا کا راستہ اختیار کرنا پسند نہ کیا۔ اسی بنا پر اس نے ان کے سب اعمال ضائع کر دیے۔ ');
INSERT INTO "ur_maududi" VALUES(47,29,'47:29','کیا وہ لوگ جن کے دلوں میں بیماری ہے یہ سمجھے بیٹھے ہیں کہ اللہ ان کے دلوں کے کھوٹ ظاہر نہیں کرے گا؟');
INSERT INTO "ur_maududi" VALUES(47,30,'47:30','ہم چاہیں تو انہیں تم کو آنکھوں سے دکھا دیں اور اُن کے چہروں سے تم ان کو پہچان لو مگر ان کے انداز کلام سے تو تم ان کو جان ہی لو گے اللہ تم سب کے اعمال سے خوب واقف ہے');
INSERT INTO "ur_maududi" VALUES(47,31,'47:31','ہم ضرور تم لوگوں کو آزمائش میں ڈالیں گے تاکہ تمہارے حالات کی جانچ کریں اور دیکھ لیں کہ تم میں مجاہد اور ثابت قدم کون ہیں');
INSERT INTO "ur_maududi" VALUES(47,32,'47:32','جن لوگوں نے کفر کیا اور اللہ کی راہ سے روکا اور رسول سے جھگڑا کیا جبکہ ان پر راہ راست واضح ہو چکی تھی، درحقیقت وہ اللہ کا کوئی نقصان بھی نہیں کر سکتے ، بلکہ اللہ ہی ان کا سب کیا کرایا غارت کر دے گا۔ ');
INSERT INTO "ur_maududi" VALUES(47,33,'47:33','اے لوگو جو ایمان لائے ہو، تم اللہ کی اطاعت کرو اوررسول کی اطاعت کرو اور اپنے اعمال کو بر باد نہ کر لو۔ ');
INSERT INTO "ur_maududi" VALUES(47,34,'47:34','کفر کرنے والوں اور راہ خدا سے روکنے والوں اور مرتے دم تک کفر پر جمے رہنے والوں کو تو اللہ ہرگز معاف نہ کرے گا');
INSERT INTO "ur_maududi" VALUES(47,35,'47:35','پس تم بودے نہ بنو اور صلح کی درخواست نہ کرو۔ تم ہی غالب رہنے والے ہو۔ اللہ تمہارے ساتھ ہے اور تمہارے اعمال کو وہ ہر گز ضائع نہ کریگا');
INSERT INTO "ur_maududi" VALUES(47,36,'47:36','یہ دنیا کی زندگی تو ایک کھیل اور تماشا ہے۔ اگر تم ایمان رکھو اور تقوٰی کی روش پر چلتے رہو تو اللہ تمہارے اجر تم کو دے گا اور وہ تمہارے مال تم سے نہ مانگے گا۔ ');
INSERT INTO "ur_maududi" VALUES(47,37,'47:37','اگر کہیں وہ تمہارے مال تم سے مانگ لے اور سب کے سب تم سے طلب کر لے تو تم بخل کرو گے اور وہ تمہارے کھوٹ اُبھار لائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(47,38,'47:38','دیکھو، تم لوگوں کو دعوت دی جا رہی ہے کہ اللہ کی راہ میں مال خرچ کرو اِس پر تم میں سے کچھ لوگ ہیں جو بخل کر رہے ہیں، حالانکہ جو بخل کرتا ہے وہ در حقیقت اپنے آپ ہی سے بخل کر رہا ہے اللہ تو غنی ہے، تم ہی اس کے محتاج ہو اگر تم منہ موڑو گے تو اللہ تمہاری جگہ کسی اور قوم کو لے آئے گا اور وہ تم جیسے نہ ہوں گے');
INSERT INTO "ur_maududi" VALUES(48,1,'48:1','اے نبی ؐ ، ہم نے تم کو کھلی فتح عطا کر دی ');
INSERT INTO "ur_maududi" VALUES(48,2,'48:2','تاکہ اللہ تمہاری اگلی پچھلی ہر کوتاہی سے درگزر فرمائے اور تم پر اپنی نعمت کی تکمیل کر دے اور تمھیں سیدھا راستہ دکھائے ');
INSERT INTO "ur_maududi" VALUES(48,3,'48:3','اور تم کو زبردست نصرت بخشے۔ ');
INSERT INTO "ur_maududi" VALUES(48,4,'48:4','وہی ہے جس نے مومنوں کے دلوں میں سکینت نازل فرمائی تاکہ اپنے ایمان کے ساتھ وہ ایک ایمان اور بڑھا لیں۔ زمین اور آسمانوں کے سب لشکر اللہ کے قبضہٴ قدرت میں ہیں اور وہ علیم و حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(48,5,'48:5','(اس نے یہ کام اس لیے کیا ہے)تاکہ مومن مردوں اور عورتوں کو ہمیشہ رہنے کے لیے ایسی جنّتوں میں داخل فرمائے جن کے نیچے نہریں بہ رہی ہو ں گی اور ان کی برائیاں ان سے دور کر دے۔۔۔۔ اللہ کے نزدیک یہ بڑی کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(48,6,'48:6','اور ان منافق مردوں اور عورتوں اور مشرک مردوں اور عورتوں کو سزا دے جو اللہ کے متعلق بُرے گمان رکھتے ہیں۔ بُرائی کے پھیر میں وہ خودہی آگئے، اللہ کا غضب ان پر ہوا اور اس نے ان پر لعنت کی اور ان کے لیے جہنم مہیّا کر دی جو بہت ہی بُرا ٹھکانا ہے');
INSERT INTO "ur_maududi" VALUES(48,7,'48:7','زمین اور آسمان کے لشکر اللہ ہی کے قبضہٴ قدرت میں ہیں اور وہ زبردست اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(48,8,'48:8','اے نبیؐ ، ہم نے تم کو شہادت دینے والا، بشارت دینے والا اور خبر دار کردینے والا بنا کر بھیجا ہے');
INSERT INTO "ur_maududi" VALUES(48,9,'48:9','تاکہ اے لوگو، تم اللہ اور اس کے رسول پر ایمان لاؤ اور اس کا ساتھ دو، اس کی تعظیم و توقیر کرو اور صبح و شام اس کی تسبیح کرتے رہو۔ ');
INSERT INTO "ur_maududi" VALUES(48,10,'48:10','اے نبیؐ ، جو لوگ تم سے بیعت کر رہے تھے وہ دراصل اللہ سے بیعت کر رہے تھے۔ ان کے ہاتھ پر اللہ کا ہاتھ تھا۔ اب جو اس عہد کو توڑے گا اس کی عہد شکنی کا وبال اس کی اپنی ہی ذات پر ہو گا ، اور جو اس عہد کو وفا کرے گا جو اس نے اللہ سے کیا ہے، اللہ عنقریب اس کوبڑا اجر عطا فرمائے گا');
INSERT INTO "ur_maududi" VALUES(48,11,'48:11','اے نبیؐ ، بدوی عربوں میں سے جو لوگ پیچھے چھوڑ دیے گئے تھے اب وہ آکر ضرور تم سے کہیں گے کہ ”ہمیں اپنے اموال اور بال بچّوں کی فکر نے مشغول کر رکھا تھا، آپ ہمارے لیے مغفرت کی دُعا فرمائیں۔“ یہ لوگ اپنی زبانوں سے وہ باتیں کہتے ہیں جو ان کے دلوں میں نہیں ہوتیں۔ ان سے کہنا”اچھا ، یہی بات ہے تو کون تمہارے معاملہ میں اللہ کے فیصلے کو روک دینے کا کچھ بھی اختیار رکھتا ہے اگر وہ تمہیں کوئی نقصان پہنچانا چاہے یا نفع بخشنا چاہے؟ تمہارے اعمال سے تو اللہ ہی باخبر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(48,12,'48:12','(مگر اصلہ بات وہ نہیں ہے جو تم کہہ رہے ہو) بلکہ تم نے یوں سمجھا کہ رسول اور مومنین اپنے گھر والوں میں ہر گز پلٹ کر نہ آسکیں گے اور یہ خیال تمہارے دلوں کو بہت بھلا لگا اور تم نے بہت بُرے گمان کیے اور تم سخت بد باطن لوگ ہو۔ “');
INSERT INTO "ur_maududi" VALUES(48,13,'48:13','اللہ اور اس کے رسول پر جو لوگ ایمان نہ رکھتے ہوں ایسے کافروں کے لیے ہم نے بھڑکتی ہوئی آگ مہیّا کر رکھی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(48,14,'48:14','آسمانوں اور زمین کی بادشاہی کا مالک اللہ ہی ہے۔ جسے چاہے معاف کرے اور جسے چاہے سزا دے، اور وہ غفورورحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(48,15,'48:15','جب تم مالِ غنیمت حاصل کرنے کے لیے جانے لگو گے تو یہ پیچھے چھوڑے جانے والے لوگ تم سے ضرور کہیں گے کہ ہمیں بھی اپنے ساتھ چلنے دو۔ یہ چاہتے ہیں کہ اللہ کے فرمان کو بدل دیں۔ ان سے صاف کہہ دینا کہ ”تم ہر گز ہمارے ساتھ نہیں چل سکتے ،اللہ پہلے ہی یہ فرما چکا ہے۔“ یہ کہیں گے کہ”نہیں، بلکہ تم لوگ ہم سے حسد کر رہے ہو“۔(حالانکہ بات حسد کی نہیں ہے)بلکہ یہ لوگ صحیح بات کو کم ہی سمجھتے ہیں');
INSERT INTO "ur_maududi" VALUES(48,16,'48:16','ان پیچھے چھوڑے جانے والے بدوی عربوں سے کہنا کہ”عنقریب تمہیں ایسے لوگوں سے لڑنے کے لیے بلایا جائے گا جو بڑے زور آور ہیں۔ تم کو ان سے جنگ کرنی ہوگی یا وہ مطیع ہوجائیں گے۔ اس وقت اگر تم نے حکم جہاد کی اطاعت کی تو اللہ تمہیں اچھا اجر دیگا، اور اگر تم پھر اسی طرح منہ موڑ گئے جس طرح پہلے موڑ چکے ہو تو اللہ تم کو درد ناک سزا دے گا');
INSERT INTO "ur_maududi" VALUES(48,17,'48:17','اگر اندھا اور لنگڑا اور مریض جہاد کے لیے نہ آئے تو کوئی حرج نہیں۔ جو کوئی اللہ اور اس کے رسول کی اطاعت کرے گا اللہ اسے ان جنّتوں میں داخل کرے گا جن کے نیچے نہریں بہ رہی ہو ں گی اور جو منہ پھیرے گا اسے وہ درد ناک عذاب دے گا“');
INSERT INTO "ur_maududi" VALUES(48,18,'48:18','اللہ مومنوں سے خوش ہوگیا جب وہ درخت کے نیچے تم سےبیعت کر رہے تھے۔ ان کے دلوں کا حال اس کو معلوم تھا، اس لیے اس نے ان پر سکینت نازل فرمائی، ان کو انعام میں قریبی فتح بخشی');
INSERT INTO "ur_maududi" VALUES(48,19,'48:19','اور بہت سا مالِ غنیمت انہیں عطا کر دیا جسے وہ (عنقریب) حاصل کریں گے۔ اللہ زبردست اور حکیم ہے');
INSERT INTO "ur_maududi" VALUES(48,20,'48:20','اللہ تم سے بکثرت اموالِ غنیمت کاوعدہ کرتا ہے جنہیں تم حاصل کرو گے۔ فوری طور پر تو یہ فتح اس نے تمہیں عطا کر دی اور لوگوں کے ہاتھ تمہارے خلاف اٹھنے سے روک دیے، تاکہ یہ مومنوں کےلیے ایک نشانی بن جائے اور اللہ سیدھے راستے کی طرف تمہیں ہدایت بخشے۔ ');
INSERT INTO "ur_maududi" VALUES(48,21,'48:21','اِس کے علاوہ دوسری اور غنیمتوں کا بھی وہ تم سے وعدہ کرتا ہے جن پر تم ابھی تک قادر نہیں ہوئے ہو اور اللہ نے ان کو گھیر رکھا ہے، اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_maududi" VALUES(48,22,'48:22','یہ کافر لوگ اگر اس وقت تم سے لڑگئے ہو تے تو یقیناً پیٹھ پھیر جاتے اور کوئی حامی و مدد گار نہ پاتے۔ ');
INSERT INTO "ur_maududi" VALUES(48,23,'48:23','یہ اللہ کی سنّت ہے جو پہلے سے چلی آرہی ہے اور تم اللہ کی سنّت میں کوئی تبدیلی نہ پاؤ گے');
INSERT INTO "ur_maududi" VALUES(48,24,'48:24','وہی ہے جس نے مکہ کی وادی میں اُن کے ہاتھ تم سے اور تمہارے ہاتھ اُن سے روک دیے، حالانکہ وہ اُن پر تمہیں غلبہ عطا کر چکا تھا اور جو کچھ تم کر رہے تھے اللہ اسے دیکھ رہا تھا');
INSERT INTO "ur_maududi" VALUES(48,25,'48:25','وہی لوگ تو ہیں جنھوں نے کفر کیا اور تم کو مسجد حرام سے روکا اور ہدی کے اونٹوں کو ان کی قربانی کی جگہ نہ پہنچنے دیا۔ اگر (مکّہ میں) ایسے مومن مرد و عورت موجود نہ ہو تے جنھیں تم نہیں جانتے، اور یہ خطرہ نہ ہوتا کہ نادانستگی میں تم انھیں پامال کر دوگے اور اس سے تم پر حرف آئے گا(تو جنگ نہ روکی جاتی۔ روکی وہ اس لیے گئی)تا کہ اللہ اپنی رحمت میں جس کو چاہے داخل کر لے۔ وہ مومن الگ ہو گئے ہوتے تو (اہل مکّہ میں سے) جو کافر تھے ان کو ہم ضرور سخت سزا دیتے۔ ');
INSERT INTO "ur_maududi" VALUES(48,26,'48:26','(یہی وجہ ہے کہ) جب ان کافروں نے اپنے دلوں میں جاہلانہ حمیّت بٹھالی تو اللہ نے اپنے رسول اور مومنوں پر سکینت نازل فرمائی اور مومنوں کو تقوٰی کی بات کا پابند رکھا کہ وہی اس کے زیادہ حق دار اور اس کے اہل تھے۔ اللہ ہر چیز کا علم رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(48,27,'48:27','فی الواقع اللہ نے اپنے رسول کو سچا خواب دکھا یا تھا جو ٹھیک ٹھیک حق کے مطابق تھا۔ اِنشاءاللہ تم ضرور مسجد حرام میں پورے امن کے ساتھ داخل ہوگے، اپنے سر منڈواؤ گے اور بال ترشواؤ گے، اور تمہیں کوئی خوف نہ ہو گا۔ وہ اس بات کو جانتا تھا جسے تم نہ جانتے تھے اس لیے وہ خواب پورا ہونے سے پہلے اس نے یہ قریبی فتح تم کو عطا فرمادی');
INSERT INTO "ur_maududi" VALUES(48,28,'48:28','وہ اللہ ہی ہے جس نے اپنے رسول کو ہدایت اور دین حق کے ساتھ بھیجاہے تاکہ اس کو پوری جنسِ دین پر غالب کر دے اور اس حقیقت پر اللہ کی گواہی کافی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(48,29,'48:29','محمدؐ اللہ کے رسول ہیں، اور جو لوگ ان کے ساتھ ہیں وہ کفار پر سخت اور آپس میں رحیم ہیں۔ تم جب دیکھو گے انہیں رکوع وسجود، اور اللہ کے فضل اور اس کی خوشنودی کی طلب میں مشغول پاؤ گے۔ سجود کے اثرات ان کے چہروں پرموجود ہیں جن سے وہ الگ پہچانے جاتے ہیں۔ یہ ہے ان کی صفت توراة میں۔ اور انجیل میں ان کی مثال یوں دی گئی ہے کہ گویا ایک کھیتی ہے جس نے پہلے کونپل نکالی، پھر اس کو تقویت دی ، پھر وہ گدرائی ، پھر اپنے تنے پر کھڑی ہو گئی۔ کاشت کرنے والوں کو وہ خوش کر تی ہے تا کہ کفار ان کے پھلنے پھولنے پر جلیں۔ اِس گروہ کےلوگ جو ایمان لائے ہیں اور جنہوں نے نیک عمل کیے ہیں اللہ نےان سے مغفرت اور بڑے اجر کا وعدہ فرمایا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(49,1,'49:1','اے لوگوں جو ایمان لائے ہو، اللہ اور اس کےرسول کے آگے پیش قدمی نہ کرو اور اللہ سے ڈرو، اللہ سب کچھ سننے اور جاننے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(49,2,'49:2','اے لوگوں جو ایمان لائے ہو ، اپنی آواز نبی کی آواز سے بلند نہ کرو، اور نہ نبی ؐ کے ساتھ اونچی آواز سے بات کیا کرو جس طرح تم آپس میں ایک دوسرے سے کرتے ہو، کہیں ایسا نہ ہو کہ تمہارا کیا کرایا سب غارت ہو جائے اور تمہیں خبر بھی نہ ہو۔ ');
INSERT INTO "ur_maududi" VALUES(49,3,'49:3','جو لوگ رسولِ خدا کے حضور بات کرتے ہوئے اپنی آواز پست رکھتے ہیں وہ درحقیقت وہی لوگ ہیں جن کے دلوں کو اللہ نے تقوٰی کے لیے جانچ لیا ہے، ان کے لیے مغفرت ہے اور اجرِعظیم');
INSERT INTO "ur_maududi" VALUES(49,4,'49:4','اے نبیؐ، جو لوگ تمہیں حجروں کے باہر سے پکارتے ہیں ان میں سے اکثر بے عقل ہیں');
INSERT INTO "ur_maududi" VALUES(49,5,'49:5','اگر وہ تمہارے برآمد ہونے تک صبر کرتے تو انہی کے لیے بہتر تھا، اللہ درگزر کرنے والا اور رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(49,6,'49:6','اے لوگو جو ایمان لائے ہو، اگر کوئی فاسق تمہارے پاس کوئی خبر لے کر آئے تو تحقیق کر لیا کرو، کہیں ایسا نہ ہو کہ تم کسی گروہ کو نادانستہ نقصان پہنچا بیٹھو اور پھر اپنے کیے پر پشیمان ہو۔ ');
INSERT INTO "ur_maududi" VALUES(49,7,'49:7','خوب جان رکھو کے تمہارے درمیان اللہ کا رسول موجود ہے۔ اگر وہ بہت سے معاملات میں تمہاری بات مان لیا کرے تو تم خود ہی مشکلات میں مبتلا ہو جاؤ۔ مگر اللہ نے تم کو ایمان کی محبت دی اور اس کو تمہارے لیے دل پسند بنا دیا، اور کفر وفسق اور نافرمانی سے تم کو متنفر کردیا');
INSERT INTO "ur_maududi" VALUES(49,8,'49:8','ایسے ہی لوگ اللہ کے فضل و احسان سے راست رو ہیں اور اللہ علیم و حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(49,9,'49:9','اور اگر اہل ایمان میں سے دو گروہ آپس میں لڑجائیں تو ان کے درمیان صلح کراؤ۔ پھر اگر ان میں سے ایک گروہ دوسرے گروہ سے زیادتی کرے تو زیادتی کرنے والے سے لڑو یہاں تک کہ وہ اللہ کے حکم کی طرف پلٹ آئے۔ پھر اگر وہ پلٹ آئے تو ان کے درمیان عدل کے ساتھ صلح کرادو۔ اور انصاف کرو کہ اللہ انصاف کرنے والوں کو پسند کرتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(49,10,'49:10','مومن تو ایک دوسرے کے بھائی ہیں، لہٰذا اپنے بھائیوں کے درمیان تعلقات کو درست کرو اور اللہ سے ڈرو، امید ہے کہ تم پر رحم کیا جائے گا');
INSERT INTO "ur_maududi" VALUES(49,11,'49:11',' اے لوگو جو ایمان لائے ہو ، نہ مرد دوسرے مردوں کا مذاق اڑائیں، ہوسکتا ہے کہ وہ ان سے بہتر ہوں، اور نہ عورتیں دوسری عورتوں کا مذاق اڑائیں ، ہوسکتا ہے کہ وہ ان سے بہتر ہوں۔ آپس میں ایک دوسرے پر طعن نہ کرو اور نہ ایک دوسرے کو بُرے القاب سے یاد کرو۔ ایمان لانے کے بعد فسق میں نام پیدا کرنا بہت بری بات ہے۔ جو لوگ اس روش سے باز نہ آئیں وہی ظالم ہیں');
INSERT INTO "ur_maududi" VALUES(49,12,'49:12','اے لوگوں جو ایمان لائے ہو ، بہت گمان کرنے سے پرہیز کرو کہ بعض گمان گناہ ہوتے ہیں۔ تجسُّس نہ کرو۔ اور تم میں سے کوئی کسی کی غیبت نہ کرے۔ کیا تمہارے اندر کوئی ایسا ہے جو اپنے مرے ہوئے بھائی کا گوشت کھانا پسند کرے گا؟ دیکھو، تم خود اس سے گِھن کھاتے ہو۔ اللہ سے ڈرو ، اللہ بڑا توبہ قبول کرنے والا اور رحیم ہے');
INSERT INTO "ur_maududi" VALUES(49,13,'49:13','لوگو، ہم نے تم کو ایک مرد اور ایک عورت سے پیدا کیا اور پھر تمہاری قومیں اور برادریاں بنا دیں تاکہ تم ایک دوسرے کو پہچانو۔ درحقیقت اللہ کے نزدیک تم میں سب سے زیادہ عزّت والا وہ ہے جو تمہارے اندر سب سے زیادہ پرہیزگار ہے۔ یقیناً اللہ سب کچھ جاننے والا اور باخبر ہے');
INSERT INTO "ur_maududi" VALUES(49,14,'49:14',' یہ بدوی کہتے ہیں کہ ”ہم ایمان لائے“۔ ان سے کہو، تم ایمان نہیں لائے، بلکہ یوں کہو کہ ”ہم مطیع ہوگئے“ ۔ ایمان ابھی تمہارے دلوں میں داخل نہیں ہوا ہے۔ اگر تم اللہ اور اس کے رسول کی فرمانبرداری اختیار کر لو تو وہ تمہارے اعمال کے اجر میں کوئی کمی نہ کرے گا،یقیناً اللہ بڑا درگزر کرنے والا اور رحیم ہے');
INSERT INTO "ur_maududi" VALUES(49,15,'49:15','حقیقت میں تو مومن وہ ہیں جو اللہ اور اُس کے رسول پر ایمان لائے پھر انہوں نے کوئی شک نہ کیا اور اپنی جانوں اور مالوں سے اللہ کی راہ میں جہاد کیا وہی سچے لوگ ہیں');
INSERT INTO "ur_maududi" VALUES(49,16,'49:16','اے نبیؐ، اِن (مدعیان ایمان) سے کہو، کیا تم اللہ کو اپنے دین کی اطلاع دے رہے ہو؟ حالانکہ اللہ زمین اور آسمانوں کی ہر چیز کو جانتا ہے اور وہ ہر شے کا علم رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(49,17,'49:17','یہ لوگ تم پر احسان جتاتے ہیں کہ اِنہوں نے اسلام قبول کر لیا اِن سے کہو اپنے اسلام کا احسان مجھ پر نہ رکھو، بلکہ اللہ تم پر اپنا احسان رکھتا ہے کہ اس نے تمہیں ایمان کی ہدایت دی اگر تم واقعی اپنے دعوائے ایمان میں سچے ہو');
INSERT INTO "ur_maududi" VALUES(49,18,'49:18','اللہ زمین اور آسمانوں کی ہر پوشیدہ چیز کا علم رکھتا ہے اور جو کچھ تم کرتے ہو وہ سب اس کی نگاہ میں ہے');
INSERT INTO "ur_maududi" VALUES(50,1,'50:1','ق، قسم ہےقرآن مجید کی ');
INSERT INTO "ur_maududi" VALUES(50,2,'50:2','بلکہ ان لوگوں کو تعجب اس بات پر ہوا کہ ایک خبردار کرنے والا خود انہی میں سے ان کے پاس آگیا۔ پھر منکرین کہنے لگے”یہ تو عجیب بات ہے');
INSERT INTO "ur_maududi" VALUES(50,3,'50:3','کیا جب ہم مر جائیں گے اور خاک ہو جائیں گے (تو دوبارہ اٹھا ئے جائیں گے)؟ یہ واپسی تو عقل سے بعید ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(50,4,'50:4','(حالانکہ) زمین ان کے جسم میں سے کچھ کھاتی ہے وہ سب ہمارے علم میں ہے اور ہمارے پاس ایک کتاب ہے جس میں سب کچھ محفوظ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(50,5,'50:5','بلکہ ان لوگوں نے تو جس وقت حق اِن کے پاس آیا اُسی وقت اُسے صاف جھٹلادیا۔ اسی وجہ سے اب یہ الجھن میں پڑے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(50,6,'50:6',' اچھا، تو کیا انہوں نے کبھی اپنے اوپر آسمان کی طرف نہیں دیکھا؟ کس طرح ہم نے اسے بنایا اور آراستہ کیا، اور اس میں کوئی رخنہ نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(50,7,'50:7','اور زمین کو ہم نے بچھا یا اور اس میں پہاڑ جمائے اور اس کے اندر ہر طرح کی خوش منظر نباتات اُگادیں۔ ');
INSERT INTO "ur_maududi" VALUES(50,8,'50:8','یہ ساری چیزیں آنکھیں کھولنے والی اور سبق دینے والی ہیں ہر اُس بندے کے لیے جو (حق کی طرف) رجوع کرنے والا ہو');
INSERT INTO "ur_maududi" VALUES(50,9,'50:9','اور آسمان سے ہم نے برکت والا پانی نازل کیا، پھر اس سے باغ اور فصل کے غلے');
INSERT INTO "ur_maududi" VALUES(50,10,'50:10','اور بلند و بالا کھجور کے درخت پیدا کر دیے جن پر پھلوں سے لدے ہوئے خوشے تہ بر تہ لگتے ہیں');
INSERT INTO "ur_maududi" VALUES(50,11,'50:11','یہ انتظام ہے بندوں کی رزق دینے کا۔ اس پانی سے ہم ایک مُردہ زمین کو زندگی بخش دیتے ہیں۔ (مَرے ہوئے انسانوں کا زمین سے) نکلنا بھی اسی طرح ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(50,12,'50:12','ان سے پہلے نوحؑ کی قوم ، اور اصحاب الرّس، اور ثمُود');
INSERT INTO "ur_maududi" VALUES(50,13,'50:13','اور عاد، اور فرعون، اور لوُط کے بھائی');
INSERT INTO "ur_maududi" VALUES(50,14,'50:14','اور ایکہ والے، اور تُبَّع کی قوم کے لوگ بھی جھٹلاچکے ہیں۔ ہر ایک نے رسولوں کو جھٹلایا، اور آخر کار میری وعید اُن پرچسپاں ہوگئی۔ ');
INSERT INTO "ur_maududi" VALUES(50,15,'50:15','کیا پہلی بار کی تخلیق سے ہم عاجز تھے؟ مگر ایک نئی تخلیق کی طرف سے یہ لوگ شک میں پڑے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(50,16,'50:16',' ہم نے انسان کو پیدا کیا ہے اور اس کے دل میں ابھرنے والے وسوسوں تک کو ہم جانتے ہیں۔ ہم اس کی رگِ گردن سے بھی زیادہ اس سے قریب ہیں، ');
INSERT INTO "ur_maududi" VALUES(50,17,'50:17','(اور ہمارے اس براہ راست علم کے علاوہ) دو کاتب اس کے دائیں اور بائیں بیٹھے ہر چیز ثبت کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(50,18,'50:18','کوئی لفظ اس کی زبان سے نہیں نکلتا جسے محفوظ کرنے کے لیے ایک حاضر باش نگراں موجود نہ ہو۔ ');
INSERT INTO "ur_maududi" VALUES(50,19,'50:19','پھر دیکھو، وہ موت کی جاں کنی حق لے کر آ پہنچی، یہ وہی چیز ہے جس سے تُو بھاگتا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(50,20,'50:20','اور پھر صُور پھونکا گیا، یہ ہے وہ دن جس کا تجھے خوف دلایا جاتا تھا۔');
INSERT INTO "ur_maududi" VALUES(50,21,'50:21','ہر شخص اِس حال میں آگیا کہ اس کے ساتھ ایک ہانک کرلانے والا ہے اور ایک گواہی دینے والا۔ ');
INSERT INTO "ur_maududi" VALUES(50,22,'50:22','اِس چیز کی طرف سے تُو غفلت میں تھا ، ہم نے وہ پردہ ہٹا دیا جو تیرے آگے پڑا ہوا تھا اور آج تیری نگاہ خوب تیز ہے۔ ');
INSERT INTO "ur_maududi" VALUES(50,23,'50:23','اُس کے ساتھی نے عرض کیا یہ جو میری سپردگی میں تھا حاضر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(50,24,'50:24','حکم دیا گیا ”پھینک دو جہنم میں ہر کَٹّے کافر کو جو حق سے عناد رکھتا تھا');
INSERT INTO "ur_maududi" VALUES(50,25,'50:25','خیر کو روکنے والا اور حد سے تجاوز کرنے والا تھا، شک میں پڑا ہوا تھا ');
INSERT INTO "ur_maududi" VALUES(50,26,'50:26','اور اللہ کے ساتھ کسی دوسرے کو خدا بنائے بیٹھا تھا۔ ڈال دو اُسے سخت عذاب میں۔“ ');
INSERT INTO "ur_maududi" VALUES(50,27,'50:27','اس کے ساتھی نے عرض کیا ”خداوند ، میں نے اِس کو سرکش نہیں بنایا بلکہ یہ خود ہی پرلے درجے کی گمراہی میں پڑا ہوا تھا۔“ ');
INSERT INTO "ur_maududi" VALUES(50,28,'50:28','جواب میں ارشاد ہوا”میرے حضور جھگڑا نہ کرو، میں تم کو پہلے ہی انجام ِبد سے خبر دار کر چکا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(50,29,'50:29','میرے ہاں بات پلٹی نہیں جاتی اور میں اپنے بندوں پر ظلم توڑنے والا نہیں ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(50,30,'50:30','وہ دن جبکہ ہم جہنم سے پوچھیں گے کیا تُو بھر گئی؟ اور وہ کہے گی کیا اور کچھ ہے؟ ');
INSERT INTO "ur_maududi" VALUES(50,31,'50:31','اور جنت متقین کے قریب لے آئی جائے گی، کچھ بھی دور نہ ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(50,32,'50:32','ارشاد ہوگا”یہ ہے وہ چیز جس کا تم سے وعدہ کیا جاتا تھا، ہر اس شخص کے لیے جو بہت رجوع کرنے والا اور بڑی نگہداشت کرنے والا تھا، ');
INSERT INTO "ur_maududi" VALUES(50,33,'50:33','جو بے دیکھے رحمٰن سے ڈرتا تھا، اور جو دلِ گرویدہ لیے ہوئے آیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(50,34,'50:34','داخل ہو جاؤ جنت میں سلامتی کے ساتھ۔“ وہ دن حیاتِ ابدی کا دن ہو گا');
INSERT INTO "ur_maududi" VALUES(50,35,'50:35','وہاں ان کے لیے وہ سب کچھ ہو گا جو وہ چاہیں گے، اور ہمارے پاس اس سے زیادہ بھی بہت کچھ ان کے لیے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(50,36,'50:36','ہم ان سے پہلے بہت سی قوموں کو ہلاک کر چکے ہیں جو ان سے بہت زیادہ طاقتور تھیں اور دنیا کے ملکوں کو انہوں نے چھان مارا تھا۔ پھر کیا وہ کوئی جائے پناہ پا سکے؟ ');
INSERT INTO "ur_maududi" VALUES(50,37,'50:37','اس تاریخ میں عبرت کا سبق ہے ہر اس شخص کے لیے جو دل رکھتا ہو، یا جو توجہ سے بات کو سنے۔ ');
INSERT INTO "ur_maududi" VALUES(50,38,'50:38','ہم نے زمین اور آسمانوں کو اور ان کے درمیان کی ساری چیزوں کو چھ دنوں میں پیدا کردیا اور ہمیں کوئی تکان لاحق نہ ہوئی');
INSERT INTO "ur_maududi" VALUES(50,39,'50:39','پس اے نبیؐ، جو باتیں یہ لوگ بناتے ہیں ان پر صبر کرو، اور اپنے رب کی حمد کے ساتھ اس کی تسبیح کرتے رہو، طلوع آفتاب اور غروب آفتاب سے پہلے');
INSERT INTO "ur_maududi" VALUES(50,40,'50:40','اور رات کے وقت پھر اس کی تسبیح کرو اور سجدہ ریزیوں سے فارغ ہونے کے بعد بھی۔ ');
INSERT INTO "ur_maududi" VALUES(50,41,'50:41','اور سنو، جس دن منادی کرنے والا (ہر شخص کے) قریب ہی سے پکارے گا، ');
INSERT INTO "ur_maududi" VALUES(50,42,'50:42','جس دن سب لوگ آوازہٴ حشر کو ٹھیک ٹھیک سن رہے ہوں گے، وہ زمین سے مُردوں کے نکلنے کا دن ہو گا');
INSERT INTO "ur_maududi" VALUES(50,43,'50:43','ہم ہی زندگی بخشتے ہیں اور ہم ہی موت دیتے ہیں، اور ہماری طرف ہی اُس دن سب کو پلٹنا ہے');
INSERT INTO "ur_maududi" VALUES(50,44,'50:44','جب زمین پھٹے گی اور لوگ اس کے اندر سے نکل کر تیز تیز بھاگے جا رہے ہوں گے۔ یہ حشر ہمارے لیے بہت آسان ہے۔ ');
INSERT INTO "ur_maududi" VALUES(50,45,'50:45','اے نبیؐ ، جو باتیں یہ لوگ بنا رہےہیں انہیں ہم خوب جانتے ہیں، اور تمہارا کام ان سے جبراًبات منوانا نہیں ہے۔ بس تم اس قرآن کےذریعہ سے ہر اُس شخص کو نصیحت کر دو جو میری تنبیہ سے ڈرے۔ ');
INSERT INTO "ur_maududi" VALUES(51,1,'51:1','قسم ہے اُن ہواؤں کی جو گرد اڑانے والی ہیں');
INSERT INTO "ur_maududi" VALUES(51,2,'51:2','پھر پانی سے لدے ہوئے بادل اٹھانے والی ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(51,3,'51:3','پھر سبک رفتاری کے ساتھ چلنے والی ہیں');
INSERT INTO "ur_maududi" VALUES(51,4,'51:4','پھر ایک بڑے کام (بارش) کی تقسیم کرنے والی ہیں، ');
INSERT INTO "ur_maududi" VALUES(51,5,'51:5','حق یہ ہے کہ جس چیز کا تمہیں خوف دلایا جارہا ہے وہ سچی ہے');
INSERT INTO "ur_maududi" VALUES(51,6,'51:6','اور جزائے اعمال ضرور پیش آنی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(51,7,'51:7','قسم ہے متفرق شکلوں والے آسمان کی، ');
INSERT INTO "ur_maududi" VALUES(51,8,'51:8','(آخرت کے بارے میں) تمہاری بات ایک دوسرے سےمختلف ہے۔ ');
INSERT INTO "ur_maududi" VALUES(51,9,'51:9','اس سے وہی بر گشتہ ہوتا ہے جو حق سے پِھرا ہوا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(51,10,'51:10','مارے گئے قیاس و گمان سے حکم لگانے والے، ');
INSERT INTO "ur_maududi" VALUES(51,11,'51:11','جو جہالت میں غرق اور غفلت میں مدہوش ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(51,12,'51:12','پوچھتے ہیں آخر وہ روز جزاء کب آئے گا؟');
INSERT INTO "ur_maududi" VALUES(51,13,'51:13','وہ اُس روز آئے گا جب یہ لوگ آگ پر تپائے جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(51,14,'51:14','(اِن سے کہا جائےگا) اب چکھو مزا اپنے فتنے کا۔ یہ وہی چیز ہےجس کے لیے تم جلدی مچارہے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(51,15,'51:15','البتہ متّقی لوگ اُس روز باغوں اور چشموں میں ہوں گے');
INSERT INTO "ur_maududi" VALUES(51,16,'51:16','جو کچھ ان کا رب انہیں دے گا اسے خوشی خوشی لے رہے ہوں گے۔ وہ اُس دن کے آنے سے پہلے نیکو کار تھے');
INSERT INTO "ur_maududi" VALUES(51,17,'51:17','راتوں کو کم ہی سوتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(51,18,'51:18','پھر وہی رات کے پچھلے پہروں میں معافی مانگتے تھے، ');
INSERT INTO "ur_maududi" VALUES(51,19,'51:19','اور ان کے مالوں میں حق تھا سائل اور محروم کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(51,20,'51:20','زمین میں بہت سی نشانیاں ہیں یقین لانے والوں کے لیے، ');
INSERT INTO "ur_maududi" VALUES(51,21,'51:21','اور خود تمہارے اپنے وجود میں ہیں۔ کیا تم کو سُوجھتا نہیں؟');
INSERT INTO "ur_maududi" VALUES(51,22,'51:22','آسمان ہی میں ہے تمہارا رزق بھی اور وہ چیز بھی جس کاتم سے وعدہ کیا جا رہا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(51,23,'51:23','پس قسم ہے آسمان اور زمین کے مالک کی، یہ بات حق ہے، ایسی ہی یقینی جیسے تم بول رہے ہو');
INSERT INTO "ur_maududi" VALUES(51,24,'51:24',' اے نبی ؐ ، ابراہیم ؑ کے معزز مہمانوں کی حکایت بھی تمہیں پہنچی ہے؟ ');
INSERT INTO "ur_maududi" VALUES(51,25,'51:25','جب وہ اُس کے ہاں آئے تو کہا آپ کو سلام ہے۔ اُس نے کہا ”آپ لوگوں کو بھی سلام ہے ۔۔۔۔ کچھ ناآشنا سے لوگ ہیں۔“');
INSERT INTO "ur_maududi" VALUES(51,26,'51:26','پھر وہ چپکے سے اپنے گھر والوں کے پاس گیا ، اور ایک موٹا تازہ بچھڑا لاکر');
INSERT INTO "ur_maududi" VALUES(51,27,'51:27','مہمانوں کے آگے پیش کیا اُس نے کہا آپ حضرات کھاتے نہیں؟');
INSERT INTO "ur_maududi" VALUES(51,28,'51:28','پھر وہ اپنے دل میں ان سے ڈرا۔ انہوں نےکہا ڈریے نہیں، اور اُسے ایک ذی علم لڑکے کی پیدائش کا مژدہ سنایا۔ ');
INSERT INTO "ur_maududi" VALUES(51,29,'51:29','یہ سُن کر اُس کی بیوی چیختی ہوئی آگے بڑھی اور اس نے اپنا منہ پِیٹ لیا اور کہنے لگی ، ”بوڑھی، بانجھ!“ ');
INSERT INTO "ur_maududi" VALUES(51,30,'51:30','انہوں نے کہا”یہی کچھ فرمایا ہے تیرے رب نے، وہ حکیم ہے اور سب کچھ جانتا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(51,31,'51:31','ابراہیم ؑ نے کہا ، اے فرستادگانِ الہٰی ، کیا مہم آپ کو درپیش ہے؟ ');
INSERT INTO "ur_maududi" VALUES(51,32,'51:32','انہوں نے کہا”ہم ایک مجرم قوم کی طرف بھیجے گئے ہیں ');
INSERT INTO "ur_maududi" VALUES(51,33,'51:33','تاکہ اُس پر پکی ہوئی مٹی کے پتھر برسا دیں');
INSERT INTO "ur_maududi" VALUES(51,34,'51:34','جو آپ کے رب کے ہاں حد سے گزر جانے والوں کے لیے نشان زدہ ہیں“ ');
INSERT INTO "ur_maududi" VALUES(51,35,'51:35',' پھر ہم نے ان سب لوگوں کو نکال لیا جو اس بستی میں مومن تھے');
INSERT INTO "ur_maududi" VALUES(51,36,'51:36','اور وہاں ہم نے ایک گھر کےسوا مسلمانوں کا کوئی گھر نہ پایا۔ ');
INSERT INTO "ur_maududi" VALUES(51,37,'51:37','اس کے بعد ہم نے وہاں بس ایک نشانی ان لوگوں کے لیے چھوڑ دی جو درد ناک عذاب سے ڈرتے ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(51,38,'51:38','اور(تمہارے لیے نشانی ہے) موسیٰؑ کے قصے ہیں۔ جب ہم نے اسے صریح سَنَد کے ساتھ فرعون کے پاس بھیجا ');
INSERT INTO "ur_maududi" VALUES(51,39,'51:39','تو وہ اپنے بل بوتے پر اکڑ گیا اور بولا یہ جادوگر ہے یا مجنون ہے۔ ');
INSERT INTO "ur_maududi" VALUES(51,40,'51:40','آخر کار ہم نے اُسے اور اس کے لشکروں کو پکڑا اور سب کو سمندر میں پھینک دیا اور وہ ملامت زدہ ہو کر رہ گیا۔ ');
INSERT INTO "ur_maududi" VALUES(51,41,'51:41','اور (تمہارے لیے نشانی ہے) عاد میں، جبکہ ہم نے ان پر ایک ایسی بے خیر ہوا بھیج دی');
INSERT INTO "ur_maududi" VALUES(51,42,'51:42','کہ جس چیز پر بھی وہ گزر گئی اسے بوسیدہ کر کے رکھ دیا۔ ');
INSERT INTO "ur_maududi" VALUES(51,43,'51:43','اور (تمہارے لیے نشانی ہے) ثمود میں ، جب ان سے کہا گیا تھا کہ ایک خاص وقت تک مزے کر لو۔ ');
INSERT INTO "ur_maududi" VALUES(51,44,'51:44','مگر اس تنبیہ پر بھی انہوں نے اپنے رب کےحکم سے سرتابی کی۔ آخر کار ان کے دیکھتے دیکھتےایک اچانک ٹوٹ پڑنے والے عذاب نے ان کو آلیا۔');
INSERT INTO "ur_maududi" VALUES(51,45,'51:45','پھر نہ ان میں اٹھنے کی سکت تھی اور نہ وہ اپنا بچاؤ کر سکتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(51,46,'51:46','اور ان سب سے پہلے ہم نے نوحؑ کی قوم کو ہلاک کیا کیونکہ وہ فاسق لوگ تھے');
INSERT INTO "ur_maududi" VALUES(51,47,'51:47',' آسمان کو ہم نے اپنے زور سے بنایا ہے اور ہم اس کی قدرت رکھتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(51,48,'51:48','زمین کو ہم نے بچھایا ہے اور ہم بڑے اچھے ہموار کرنے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(51,49,'51:49','اور ہر چیز کے ہم نے جوڑے بنائے ہیں، شاید کہ تم اس سے سبق لو۔ ');
INSERT INTO "ur_maududi" VALUES(51,50,'51:50','تو دوڑو اللہ کی طرف، میں تمہارے لیے اس کی طرف سے صاف صاف خبردار کرنے والا ہوں');
INSERT INTO "ur_maududi" VALUES(51,51,'51:51','اور نہ بناؤ اللہ کے ساتھ کوئی دوسرا معبود، میں تمہارے لیے اس کی طرف سے صاف صاف خبردار کرنے والا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(51,52,'51:52','یونہی ہو تا رہا ہے، ان سے پہلے کی قوموں کے پاس بھی کوئی رسول ایسا نہیں آیا جسے انہوں نے یہ نہ کہا ہو کہ یہ ساحر ہے یا مجنون ');
INSERT INTO "ur_maududi" VALUES(51,53,'51:53','کیا ان سب نے آپس میں اس پر کوئی سمجھوتہ کر لیا ہے؟ نہیں، بلکہ یہ سب سر کش لوگ ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(51,54,'51:54','پس اے نبی ؐ ، ان سے رخ پھیر لو، تم پر کچھ ملامت نہیں ');
INSERT INTO "ur_maududi" VALUES(51,55,'51:55','البتہ نصیحت کرے رہو، کیونکہ نصیحت ایمان لانے والوں کے لیے نافع ہے۔ ');
INSERT INTO "ur_maududi" VALUES(51,56,'51:56','میں نے جِنّ اور انسانوں کو اس کے سوا کسی کام کے لیے پیدا نہیں کیا ہے کہ وہ میری بندگی کریں ');
INSERT INTO "ur_maududi" VALUES(51,57,'51:57','میں ان سے کوئی رزق نہیں چاہتا اور نہ یہ چاہتا ہوں کہ وہ مجھے کھلائیں۔ ');
INSERT INTO "ur_maududi" VALUES(51,58,'51:58','اللہ تو خود ہی رزّاق ہےبڑی قوت والا اور زبردست۔ ');
INSERT INTO "ur_maududi" VALUES(51,59,'51:59','پس جن لوگوں نے ظلم کیا ہے ان کے حصے کا بھی ویسا ہی عذاب تیار ہے جیسا اِنہی جیسے لوگوں کو اُن کے حصے کا مل چکا ہے، اس کے لیے یہ لوگ جلدی نہ مچائیں۔ ');
INSERT INTO "ur_maududi" VALUES(51,60,'51:60','آخر کو تباہی ہے کفر کرنے والوں کے لیے اُس روز جس کا انہیں خوف دلایا جا رہا ہے');
INSERT INTO "ur_maududi" VALUES(52,1,'52:1','قسم ہے ُطور کی، ');
INSERT INTO "ur_maududi" VALUES(52,2,'52:2','اور ایک ایسی کھلی کتاب کی');
INSERT INTO "ur_maududi" VALUES(52,3,'52:3','جو رقیق جِلد میں لکھی ہوئی ہے، ');
INSERT INTO "ur_maududi" VALUES(52,4,'52:4','اور آباد گھر کی، ');
INSERT INTO "ur_maududi" VALUES(52,5,'52:5','اور اُونچی چھت کی، ');
INSERT INTO "ur_maududi" VALUES(52,6,'52:6','اور مَوجْزَن سمندر کی، ');
INSERT INTO "ur_maududi" VALUES(52,7,'52:7','کہ تیرے رب کا عذاب ضرور واقع ہونے والا ہے');
INSERT INTO "ur_maududi" VALUES(52,8,'52:8','جسے کوئی دفع کرنے والا نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(52,9,'52:9','وہ اُس روز واقع ہو گا جب آسمان بُری طرح ڈگمگائے گا ');
INSERT INTO "ur_maududi" VALUES(52,10,'52:10','اور پہاڑ اُڑے اُڑے پھریں گے۔ ');
INSERT INTO "ur_maududi" VALUES(52,11,'52:11','تباہی ہے اُس روز اُن جھٹلانے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(52,12,'52:12','جو آج کھیل کے طور پر اپنی حجّت بازیوں میں لگے ہوئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(52,13,'52:13','جس دن انہیں دھکے مار مار کر نار جہنم کی طرف لے چلا جائے گا');
INSERT INTO "ur_maududi" VALUES(52,14,'52:14','اُس وقت اُن سے کہا جائے گا کہ "یہ وہی آگ ہے جسے تم جھٹلایا کرتے تھے');
INSERT INTO "ur_maududi" VALUES(52,15,'52:15','اب بتاؤیہ جادُو ہے یا تمھیں سُوجھ نہیں رہا ہے؟ ');
INSERT INTO "ur_maududi" VALUES(52,16,'52:16','جاؤ اب جھلسو اِس کے اندر، تم خواہ صبر کرو یا نہ کرو، تمہارے لیے یکساں ہے، تمہیں ویسا ہی بدلہ دیا جا رہا ہے جیسے تم عمل کر رہے تھے');
INSERT INTO "ur_maududi" VALUES(52,17,'52:17','متقی لوگ وہاں باغوں اور نعمتوں میں ہوں گے');
INSERT INTO "ur_maududi" VALUES(52,18,'52:18','لُطف لے رہے ہوں گے اُن چیزوں سے جو اُن کا رب انہیں دے گا، اور اُن کا رب اُنہیں دوزخ کے عذاب سے بچالے گا۔ ');
INSERT INTO "ur_maududi" VALUES(52,19,'52:19','(ان سے کہا جائے گا) کھاؤ اور پیو مزے سے اپنے ان اعمال کےصلے میں جو تم کرتے رہے ہو');
INSERT INTO "ur_maududi" VALUES(52,20,'52:20','وہ آمنے سامنے بچھے ہوئےتختوں پر تکیے لگائے بیٹھے ہوں گے اور ہم خوبصورت آنکھوں والی حوریں اُن سے بیاہ دیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(52,21,'52:21','جو لوگ ایمان لائے ہیں اور ان کی اولاد بھی کسی درجہٴ ایمان میں ان کے نقشِ قدم پر چلی ہے ان کی اُس اولاد کو بھی ہم (جنت میں) اُن کے ساتھ ملادیں گے اور اُن کے عمل میں کوئی گھاٹا ان کو نہ دیں گے۔ ہر شخص اپنے کسب کے عوض رہن ہے۔ ');
INSERT INTO "ur_maududi" VALUES(52,22,'52:22','ہم اُن کو ہر طرح کے پھل اور گوشت، جس چیز کو بھی ان کا جی چاہےگا، خوب دیے چلے جائیں گے');
INSERT INTO "ur_maududi" VALUES(52,23,'52:23','وہ ایک دوسرے سے جامِ شراب لپک لپک کر لے رہے ہوں گے جس میں نہ یادہ گوئی ہوگی نہ بدکرداری۔ ');
INSERT INTO "ur_maududi" VALUES(52,24,'52:24','اور اُن کی خدمت میں وہ لڑکے دوڑتے پھر رہے ہوں گے جو انہی کیلیے مخصوص ہوں گے، ایسے خوبصورت جیسے چھپا کر رکھے ہوئے موتی');
INSERT INTO "ur_maududi" VALUES(52,25,'52:25','یہ لوگ آپس میں ایک دوسرے سے (دنیا میں گزرے ہوئے) حالات پوچھیں گے');
INSERT INTO "ur_maududi" VALUES(52,26,'52:26','یہ کہیں گے کہ ہم پہلے اپنے گھروالوں میں ڈرتے ہوئے زندگی بسر کرتے تھے، ');
INSERT INTO "ur_maududi" VALUES(52,27,'52:27','آخر کار اللہ نے ہم پر فضل فرمایا اور ہمیں جھلسادینے والی ہوا کے عذاب سے بچالیا');
INSERT INTO "ur_maududi" VALUES(52,28,'52:28','ہم پچھلی زندگی میں اُسی سے دعائیں مانگتے تھے، وہ واقعی بڑا ہی محسن اور رحیم ہے');
INSERT INTO "ur_maududi" VALUES(52,29,'52:29','پس اے نبیؐ، تم نصیحت کیے جاؤ، اپنے رب کے فضل سے نہ تم کاہن ہو اور نہ مجنون');
INSERT INTO "ur_maududi" VALUES(52,30,'52:30','کیا یہ لوگ کہتے ہیں کہ یہ شخص شاعر ہے جس کےحق میں گردشِ ایّام کا انتظار کر رہے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(52,31,'52:31','ان سے کہو اچھا انتظار کرو، میں بھی تمہارے ساتھ انتظار کرتا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(52,32,'52:32','کیا ان کی عقلیں انہیں ایسی ہی باتیں کرنے کے لیے کہتی ہیں؟ یا درحقیقت یہ عناد میں حد سے گزرے ہوئے لوگ ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(52,33,'52:33','کیا یہ کہتے ہیں کہ اس شخص نے یہ قرآن خود گھڑ لیا ہے؟ اصل بات یہ ہے کہ یہ ایمان نہیں لانا چاہتے۔ ');
INSERT INTO "ur_maududi" VALUES(52,34,'52:34','اگر یہ اپنے اس قول میں سچے ہیں تو اسی شان کا ایک کلام بنا لائیں۔ ');
INSERT INTO "ur_maududi" VALUES(52,35,'52:35','کیا یہ کسی خالق کے بغیر خود پیدا ہو گئے ہیں؟ یا یہ خود اپنے خالق ہیں؟');
INSERT INTO "ur_maududi" VALUES(52,36,'52:36','یا زمین اور آسمانوں کو انہوں نے پیدا کیا ہے؟ اصل بات یہ ہے کہ یہ یقین نہیں رکھتے۔ ');
INSERT INTO "ur_maududi" VALUES(52,37,'52:37','کیا تیرے رب کے خزانے ان کے قبضے میں ہیں؟ یا ان پرانہی کا حکم چلتا ہے؟ ');
INSERT INTO "ur_maududi" VALUES(52,38,'52:38','کیا اِن کے پاس کوئی سیڑھی ہے جس پر چڑھ کر یہ عالم بالا کی سن گن لیتے ہیں؟ اِن میں سے جس نے سن گن لی ہو وہ لائے کوئی کھلی دلیل');
INSERT INTO "ur_maududi" VALUES(52,39,'52:39','کیا اللہ کے لیے تو ہیں بیٹیاں اور تم لوگوں کے لیے یہ ہیں بیٹے؟ ');
INSERT INTO "ur_maududi" VALUES(52,40,'52:40','کیا تم ان سے کوئی اجر مانگتے ہو کہ یہ زبر دستی پڑی ہوئی چٹّی کے بوجھ تلے دبے جاتے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(52,41,'52:41','کیا ان کے پاس غیب کے حقائق کا علم ہے کہ اس کی بناء پر یہ لکھ رہے ہوں؟ ');
INSERT INTO "ur_maududi" VALUES(52,42,'52:42','کیا یہ کوئی چال چلنا چاہتے ہیں؟ (اگر یہ بات ہے) تو کفر کرنے والوں پر ان کی چال الٹی ہی پڑے گی۔ ');
INSERT INTO "ur_maududi" VALUES(52,43,'52:43','کیا اللہ کے سوا یہ کوئی اور معبود رکھتے ہیں؟ اللہ پاک ہے اس شرک سے جو یہ لوگ کر رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(52,44,'52:44','یہ لوگ آسمان کے ٹکڑے بھی گرتے ہوئے دیکھ لیں تو کہیں گے یہ بادل ہیں جو اُمڈے چلے آرہے ہیں ');
INSERT INTO "ur_maududi" VALUES(52,45,'52:45','پس اے نبیؐ، اِنہیں اِن کے حال پر چھوڑ دو یہاں تک کہ یہ اپنے اُس دن کو پہنچ جائیں جس میں یہ مار گرائے جائیں گے');
INSERT INTO "ur_maududi" VALUES(52,46,'52:46','جس دن نہ اِن کی اپنی کوئی چال اِن کے کسی کام آئے گی نہ کوئی اِن کی مدد کو آئے گا');
INSERT INTO "ur_maududi" VALUES(52,47,'52:47','اور اس وقت کے آنے سے پہلے بھی ظالموں کے لیے ایک عذاب ہے ،مگر ان میں سے اکثر جانتے نہیں ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(52,48,'52:48','اے نبیؐ، اپنے رب کا فیصلہ آنے تک صبر کرو، تم ہماری نگاہ میں ہو۔ تم جب اٹھو تو اپنے رب کی حمد کے ساتھ اس کی تسبیح کرو، ');
INSERT INTO "ur_maududi" VALUES(52,49,'52:49','رات کو بھی اس کی تسبیح کیا کرو اور ستارے جب پلٹتے ہیں اس وقت بھی۔ ');
INSERT INTO "ur_maududi" VALUES(53,1,'53:1','قسم ہے تارے کی جبکہ وہ غروب ہوا');
INSERT INTO "ur_maududi" VALUES(53,2,'53:2','تمہارا رفیق نہ بھٹکا ہے نہ بہکا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(53,3,'53:3','وہ اپنی خواہش نفس سے نہیں بولتا');
INSERT INTO "ur_maududi" VALUES(53,4,'53:4','یہ تو ایک وحی ہے جو اس پر نازل کی جاتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(53,5,'53:5','اُسے زبر دست قوت والے نے تعلیم دی ہے ');
INSERT INTO "ur_maududi" VALUES(53,6,'53:6','جو بڑا صاحبِ حکمت ہے۔ ');
INSERT INTO "ur_maududi" VALUES(53,7,'53:7','وہ سامنے آ کھڑا ہوا جبکہ وہ بالائی افق پر تھا ');
INSERT INTO "ur_maududi" VALUES(53,8,'53:8','پھر قریب آیا اور اوپر معلق ہو گیا');
INSERT INTO "ur_maududi" VALUES(53,9,'53:9','یہاں تک کہ دو کمانوں کے برابر یا اس سے کچھ کم فاصلہ رہ گیا۔ ');
INSERT INTO "ur_maududi" VALUES(53,10,'53:10','تب اس نے اللہ کے بندے کو وحی پہنچائی جو وحی بھی اسے پہنچانی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(53,11,'53:11','نظر نے جو کچھ دیکھا، دل نےاس میں جھوٹ نہ ملایا۔ ');
INSERT INTO "ur_maududi" VALUES(53,12,'53:12','اب کیا تم اُس چیز پر اُس سے جھگڑتے ہو جسے وہ آنکھوں سے دیکھتا ہے؟');
INSERT INTO "ur_maududi" VALUES(53,13,'53:13','اور ایک مرتبہ پھر اُس نے');
INSERT INTO "ur_maududi" VALUES(53,14,'53:14','سدرۃالمنتہیٰ کے پاس اُس کو دیکھا');
INSERT INTO "ur_maududi" VALUES(53,15,'53:15','جہاں پاس ہی جنت الماوٰی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(53,16,'53:16','اُس وقت سِدرہ پر چھا رہا تھا جو کچھ کہ چھا رہا تھا');
INSERT INTO "ur_maududi" VALUES(53,17,'53:17','نگاہ نہ چُوندھیائی نہ حد سے متجاوز ہوئی ');
INSERT INTO "ur_maududi" VALUES(53,18,'53:18','اور اس نے اپنے رب کی بڑی بڑی نشانیاں دیکھیں۔ ');
INSERT INTO "ur_maududi" VALUES(53,19,'53:19','اب ذرا بتاؤ، تم نے کبھی اِس لات، اور اِس عزیٰ');
INSERT INTO "ur_maududi" VALUES(53,20,'53:20','اور تیسری ایک اور دیوی مَنات کی حقیقت پر کچھ غور بھی کیا؟ ');
INSERT INTO "ur_maududi" VALUES(53,21,'53:21','کیا بیٹے تمہارے لیے ہیں اور بیٹیاں خدا کے لیے؟ ');
INSERT INTO "ur_maududi" VALUES(53,22,'53:22','یہ تو بڑی دھاندلی کی تقسیم ہوئی!');
INSERT INTO "ur_maududi" VALUES(53,23,'53:23','در اصل یہ کچھ نہیں ہیں مگر بس چند نام جو تم نے اور تمہارے باپ دادا نے رکھ لیے ہیں۔اللہ نے اِن کے لیے کوئی سند نازل نہیں کی۔ حقیقت یہ ہے کہ لوگ محض وہم و گمان کی پیروی کر رہے ہیں اور خواہشاتِ نفس کے مُرید بنے ہوئے ہیں۔ حالانکہ اُن کے رب کی طرف سے اُن کے پاس ہدایت آچکی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(53,24,'53:24','کیا انسان جو کچھ چاہے اس کے لیے وہی حق ہے؟ ');
INSERT INTO "ur_maududi" VALUES(53,25,'53:25','دنیا اور آخرت کا مالک تو اللہ ہی ہے');
INSERT INTO "ur_maududi" VALUES(53,26,'53:26','آسمانوں میں کتنے ہی فرشتے موجود ہیں، اُن کی شفاعت کچھ بھی کام نہیں آسکتی جب تک کہ اللہ کسی ایسے شخص کے حق میں اس کی اجازت نہ دے جس کے لیے وہ کوئی عرضداشت سننا چاہے اور اس کو پسند کرے۔ ');
INSERT INTO "ur_maududi" VALUES(53,27,'53:27','مگر جو لوگ آخرت کو نہیں مانتے وہ فرشتوں کو دیویوں کے ناموں سے موسوم کرتے ہیں ، ');
INSERT INTO "ur_maududi" VALUES(53,28,'53:28','حالانکہ اس معاملہ کا کوئی علم انہیں حاصل نہیں ہے، وہ محض گمان کی پیروی کر رہے ہیں، اور گمان حق کی جگہ کچھ بھی کام نہیں دے سکتا');
INSERT INTO "ur_maududi" VALUES(53,29,'53:29','پس اے نبیؐ ، جو شخص ہمارے ذکر سےمنہ پھیرتا ہے، اور دنیا کی زندگی کے سوا جسے کچھ مطلوب نہیں ہے، اسے اس کے حال پر چھوڑدو ');
INSERT INTO "ur_maududi" VALUES(53,30,'53:30',' اِن لوگوں کا مبلغ علم بس یہی کچھ ہے، یہ بات تیرا رب ہی زیادہ جانتا ہے کہ اُس کے راستے سے کون بھٹک گیا ہے اور کون سیدھے راستے پر ہے');
INSERT INTO "ur_maududi" VALUES(53,31,'53:31','اور زمین اور آسمانوں کی ہر چیز کا مالک اللہ ہی ہے ۔۔۔۔ تاکہ اللہ بُرائی کرنے والوں کو اُن کے عمل کا بدلہ دے اور اُن لوگوں کو اچھی جزا سے نوازے جنہوں نے نیک رویّہ اختیار کیا ہے');
INSERT INTO "ur_maududi" VALUES(53,32,'53:32','جو بڑے بڑے گناہوں اور کھلے کھلے قبیح افعال سے پرہیز کرتے ہیں، اِلّا یہ کہ کچھ قصور اُن سے سرزد ہوجائے۔ بِلاشبہ تیرے رب کا دامنِ مغفرت بہت وسیع ہے۔ وہ تمہیں اُس وقت سےخوب جانتا ہے جب اس نے زمین سے تمہیں پیدا کیا اور جب تم اپنی ماؤں کے پیٹوں میں ابھی جَنین ہی تھے۔ پس اپنے نفس کی پاکی کے دعوے نہ کرو، وہی بہتر جانتا ہے کہ واقعی متقی کون ہے');
INSERT INTO "ur_maududi" VALUES(53,33,'53:33','پھر اے نبیؐ، تم نے اُس شخص کو بھی دیکھا جو راہ خدا سے پھر گیا');
INSERT INTO "ur_maududi" VALUES(53,34,'53:34','اور تھوڑا سا دے کر رُک گیا۔ ');
INSERT INTO "ur_maududi" VALUES(53,35,'53:35','کیا اس کے پاس غیب کا علم ہے کہ وہ حقیقت کو دیکھ رہا ہے؟ ');
INSERT INTO "ur_maududi" VALUES(53,36,'53:36','کیا اُسے اُن باتوں کی کوئی خبر نہیں پہنچی جو موسیٰؑ کے صحیفوں');
INSERT INTO "ur_maududi" VALUES(53,37,'53:37','اور اُس ابراہیمؑ کے صحیفوں میں بیان ہوئی ہیں جس نے وفا کا حق ادا کر دیا؟ ');
INSERT INTO "ur_maududi" VALUES(53,38,'53:38','”یہ کہ کوئی بوجھ اٹھا نے والا دوسرے کا بوجھ نہیں اٹھائے گا، ');
INSERT INTO "ur_maududi" VALUES(53,39,'53:39','اور یہ کہ انسان کے لیے کچھ نہیں ہے مگر وہ جس کی اس نے سعی کی ہے، ');
INSERT INTO "ur_maududi" VALUES(53,40,'53:40','اور یہ کہ اس کی سعی عنقریب دیکھی جائے گی ');
INSERT INTO "ur_maududi" VALUES(53,41,'53:41','اور اس کی پوری جزا اسے دی جائے گی');
INSERT INTO "ur_maududi" VALUES(53,42,'53:42','اور یہ کہ آخر کار پہنچنا تیرے رب ہی کے پاس ہے');
INSERT INTO "ur_maududi" VALUES(53,43,'53:43','اور یہ کہ اُسی نے ہنسایا اور اُسی نے رلایا، ');
INSERT INTO "ur_maududi" VALUES(53,44,'53:44','اور یہ کہ اُسی نے موت دی اور اُسی نے زندگی بخشی');
INSERT INTO "ur_maududi" VALUES(53,45,'53:45','اور یہ کہ اُسی نے نر اور مادہ کا جوڑا پیدا کیا');
INSERT INTO "ur_maududi" VALUES(53,46,'53:46','ایک بُوند سے جب وہ ٹپکائی جاتی ہے، ');
INSERT INTO "ur_maududi" VALUES(53,47,'53:47','اور یہ کہ دوسری زندگی بخشنا بھی اُسی کے ذمّہ ہے، ');
INSERT INTO "ur_maududi" VALUES(53,48,'53:48','اور یہ کہ اُسی نے غنی کیا اور جائیداد بخشی، ');
INSERT INTO "ur_maududi" VALUES(53,49,'53:49','اور یہ کہ وہی شِعریٰ کا ربّ ہے، ');
INSERT INTO "ur_maududi" VALUES(53,50,'53:50','اور یہ کہ اُسی نے عادِ اولیٰ کو ہلاک کیا');
INSERT INTO "ur_maududi" VALUES(53,51,'53:51','اور ثمود کو ایسا مٹایا کہ ان میں سے کسی کو باقی نہ چھوڑا');
INSERT INTO "ur_maududi" VALUES(53,52,'53:52','اور اُن سے پہلے قوم نوحؑ کو تباہ کیا کیونکہ وہ تھے ہی سخت ظالم و سرکش لوگ');
INSERT INTO "ur_maududi" VALUES(53,53,'53:53','اور اوندھی گرنے والی بستیوں کو اٹھا پھینکا');
INSERT INTO "ur_maududi" VALUES(53,54,'53:54','پھر چھا دیا اُن پر وہ کچھ جو (تم جانتے ہی ہو کہ) کیا چھا دیا۔ ');
INSERT INTO "ur_maududi" VALUES(53,55,'53:55',' پس اے مخاطب ، اپنے رب کی کن کن نعمتوں میں تُو شک کرے گا“؟ ');
INSERT INTO "ur_maududi" VALUES(53,56,'53:56','یہ ایک تنبیہ ہے پہلے آئی ہوئی تنبیہات میں سے');
INSERT INTO "ur_maududi" VALUES(53,57,'53:57','آنے والی گھڑی قریب آلگی ہے، ');
INSERT INTO "ur_maududi" VALUES(53,58,'53:58','اللہ کے سوا کوئی اُس کو ہٹانے والا نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(53,59,'53:59','اب کیا یہی وہ باتیں ہیں جن پر تم اظہارِ تعجّب کرتے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(53,60,'53:60','ہنستے ہو اور روتے نہیں ہو؟');
INSERT INTO "ur_maududi" VALUES(53,61,'53:61','اور گا بجا کر انہیں ٹالتے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(53,62,'53:62','جُھک جاؤ اللہ کے آگے اور بندگی بجا لاؤ۔ ');
INSERT INTO "ur_maududi" VALUES(54,1,'54:1','قیامت کی گھڑی قریب آگئی اور چاند پھٹ گیا۔ ');
INSERT INTO "ur_maududi" VALUES(54,2,'54:2','مگر اِن لوگوں کا حال یہ ہے خواہ کوئی نشانی دیکھ لیں منہ موڑ جاتے ہیں اور کہتے ہیں یہ تو چلتا ہوا جادو ہے۔ ');
INSERT INTO "ur_maududi" VALUES(54,3,'54:3','اِنہوں نے (اس کو بھی) جھٹلادیا اور اپنی خواہشاتِ نفس ہی کی پیروی کی۔ ہر معاملہ کو آخر کار ایک انجام پر پہنچ کر رہنا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(54,4,'54:4','اِن لوگوں کے سامنے (پچھلی قوموں کے) وہ حالات آ چکے ہیں جن میں سرکشی سے باز رکھنے کے لیے کافی سامان عبرت ہے');
INSERT INTO "ur_maududi" VALUES(54,5,'54:5','اور ایسی حکمت جو نصیحت کے مقصد کو بدرجہ اتم پورا کرتی ہے مگر تنبیہات اِن پر کارگر نہیں ہوتیں');
INSERT INTO "ur_maududi" VALUES(54,6,'54:6','پس اے نبیؐ ، اِن سے رُخ پھیر لو۔ جس روز پکارنے والا ایک سخت ناگوار چیز کی طرف پکارے گا');
INSERT INTO "ur_maududi" VALUES(54,7,'54:7','لوگ سہمی ہوئی نگاہوں کے ساتھ اپنی قبروں سے اِس طرح نکلیں گے گویا وہ بکھری ہوئی ٹِڈیاں ہیں');
INSERT INTO "ur_maududi" VALUES(54,8,'54:8','پکارنے والے کی طرف دوڑے جا رہے ہوں گے اور وہی منکرین (جو دنیا میں اس کا انکار کرتے تھے) اُس وقت کہیں گے کہ یہ دن تو بڑا کٹھن ہے');
INSERT INTO "ur_maududi" VALUES(54,9,'54:9','اِن سے پہلے نوحؑ کی قوم جھٹلا چکی ہے۔ اُنہوں نے ہمارے بندے کو جھوٹا قرار دیا اور کہا کہ یہ دیوانہ ہے، اور وہ بری طرح جِھڑکا گیا۔ ');
INSERT INTO "ur_maududi" VALUES(54,10,'54:10','آخر کار اُس نے اپنے رب کو پکارا کہ "میں مغلوب ہو چکا، اب تو اِن سے انتقام لے"');
INSERT INTO "ur_maududi" VALUES(54,11,'54:11','تب ہم نے موسلادھار بارش سے آسمان کے دروازے کھول دیے اور زمین کو پھاڑ کر چشموں میں تبدیل کر دیا، ');
INSERT INTO "ur_maududi" VALUES(54,12,'54:12','اور یہ سارا پانی اُس کام کو پورا کرنے لیے مل گیا جو مقدر ہو چکا تھا');
INSERT INTO "ur_maududi" VALUES(54,13,'54:13','اور نوح ؑ کو ہم نے ایک تختوں اور کیلوں والی پر سوار کر دیا');
INSERT INTO "ur_maududi" VALUES(54,14,'54:14','جو ہماری نگرانی میں چل رہی تھی۔ یہ تھا بدلہ اُس شخص کی خاطر جس کی ناقدری کی گئی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(54,15,'54:15','اُس کشتی کو ہم نے ایک نشانی بنا کر چھوڑ دی ا، پھر کوئی ہے نصیحت قبول کرنے والا؟');
INSERT INTO "ur_maududi" VALUES(54,16,'54:16','دیکھ لو، کیسا تھا میرا عذاب اور کیسی تھیں میری تنبیہات');
INSERT INTO "ur_maududi" VALUES(54,17,'54:17','ہم نےاِس قرآن کو نصیحت کے لیے آسان ذریعہ بنا دیا ہے، پھر کیا ہے کوئی نصیحت قبول کرنے والا؟');
INSERT INTO "ur_maududi" VALUES(54,18,'54:18','عاد نے جھٹلایا، تو دیکھ لو کہ کیسا تھا میرا عذاب اور کیسی تھیں میری تنبیہات');
INSERT INTO "ur_maududi" VALUES(54,19,'54:19','ہم نے ایک پیہم نحوست کے دن سخت طوفانی ہوا اُن پر بھیج دی جو لوگوں کو اُٹھا اُٹھا کر اِس طرح پھینک رہی تھی');
INSERT INTO "ur_maududi" VALUES(54,20,'54:20','جیسے وہ جڑ سے اکھڑے ہوئے کھجور کے تنے ہوں');
INSERT INTO "ur_maududi" VALUES(54,21,'54:21','پس دیکھ لو کہ کیسا تھا میرا عذاب اور کیسی تھیں میری تنبیہات');
INSERT INTO "ur_maududi" VALUES(54,22,'54:22','ہم نے اِس قرآن کو نصیحت کے لیے آسان ذریعہ بنا دیا ہے، پھر کیا ہے کوئی نصیحت قبول کرنے والا؟');
INSERT INTO "ur_maududi" VALUES(54,23,'54:23','ثمود نے تنبیہات کو جھٹلایا');
INSERT INTO "ur_maududi" VALUES(54,24,'54:24','اور کہنے لگے”ایک اکیلا آدمی جو ہم ہی میں سے ہے کیا اب ہم اُس کے پیچھے چلیں؟ اِس کا اتباع ہم قبول کر لیں تو اِس کے معنی یہ ہوں گے کہ ہم بہک گئے ہیں اور ہماری عقل ماری گئی ہے');
INSERT INTO "ur_maududi" VALUES(54,25,'54:25','کیا ہمارے درمیان بس یہی ایک شخص تھا جس پر خدا کا ذکر نازل کیا گیا؟ نہیں، بلکہ یہ پرلے درجے کا جھوٹا اور بر خود غلط ہے"');
INSERT INTO "ur_maududi" VALUES(54,26,'54:26','(ہم نے اپنے پیغمبر سے کہا) "کل ہی اِنہیں معلوم ہوا جاتا ہے کہ کون پرلے درجے کا جھوٹا اور بر خود غلط ہے');
INSERT INTO "ur_maududi" VALUES(54,27,'54:27','ہم اونٹنی کو اِن کے لیے فتنہ بنا کر بھیج رہے ہیں اب ذرا صبر کے ساتھ دیکھ کہ اِن کا کیا انجام ہوتا ہے');
INSERT INTO "ur_maududi" VALUES(54,28,'54:28','اِن کو جتا دے کہ پانی اِن کے اور اُونٹنی کے درمیان تقسیم ہوگا اور ہر ایک اپنی باری کے دن پانی پر آئے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(54,29,'54:29','آخر کار اُن لوگوں نے اپنے آدمی کو پکارا اور اُس نے اِس کا م کا بیڑا اُٹھایا اور اونٹنی کو مار ڈالا۔ ');
INSERT INTO "ur_maududi" VALUES(54,30,'54:30','پھر دیکھ لو کہ کیسا تھا میرا عذاب اور کیسی تھیں میری تنبیہات');
INSERT INTO "ur_maududi" VALUES(54,31,'54:31','ہم نےاُن پر بس ایک ہی دھماکہ چھوڑا اور وہ باڑے والے کی روندی ہوئی باڑھ کی طرح بُھس ہو کر رہ گئے۔ ');
INSERT INTO "ur_maududi" VALUES(54,32,'54:32','ہم نے اِس قرآن کو نصیحت کے لیے آسان ذریعہ بنا دیا ہے، اب ہے کوئی نصیحت قبول کرنے والا؟');
INSERT INTO "ur_maududi" VALUES(54,33,'54:33','لوطؑ کی قوم نے تنبیہات کو جھٹلایا');
INSERT INTO "ur_maududi" VALUES(54,34,'54:34','اور ہم نے پتھراؤ کرنے والی ہوا اس پر بھیج دی صرف لوطؑ کے گھر والے اُس سے محفوظ رہے');
INSERT INTO "ur_maududi" VALUES(54,35,'54:35','اُن کو ہم نے اپنے فضل سے رات کے پچھلے پہر بچا کر نکال دیا یہ جزا دیتے ہیں ہم ہر اُس شخص کو جو شکر گزار ہوتا ہے');
INSERT INTO "ur_maududi" VALUES(54,36,'54:36','لوطؑ نے اپنی قوم کے لوگوں کو ہماری پکڑ سے خبردار کیا مگر وہ ساری تنبیہات کو مشکوک سمجھ کر باتوں میں اڑاتے رہے');
INSERT INTO "ur_maududi" VALUES(54,37,'54:37','پھر انہوں نے اُسے اپنے مہمانوں کی حفاظت سے باز رکھنے کی کوشش کی۔ آخر کار ہم نے اُن کی آنکھیں مونددیں کہ چکھو اب میرے عذاب اور میری تنبیہات کا مزا۔ ');
INSERT INTO "ur_maududi" VALUES(54,38,'54:38','صبح سویرے ہی ایک اٹل عذاب نے ان کو آ لیا');
INSERT INTO "ur_maududi" VALUES(54,39,'54:39','چکھو مزا اب میرے عذاب کا اور میری تنبیہات کا');
INSERT INTO "ur_maududi" VALUES(54,40,'54:40','ہم نے اِس قرآن کو نصیحت کے لیے آسان ذریعہ بنا دیا ہے، پس ہے کوئی نصیحت قبول کرنے والا؟');
INSERT INTO "ur_maududi" VALUES(54,41,'54:41','اور آل فرعون کے پاس بھی تنبیہات آئی تھیں');
INSERT INTO "ur_maududi" VALUES(54,42,'54:42','مگر انہوں نے ہماری ساری نشانیوں کو جھٹلا دیا آخر کو ہم نے انہیں پکڑا جس طرح کوئی زبردست قدرت والا پکڑتا ہے');
INSERT INTO "ur_maududi" VALUES(54,43,'54:43','کیا تمہارے کُفّار کچھ اُن لوگوں سے بہتر ہیں؟ یا آسمانی کتابوں میں تمہارے لیے کوئی معافی لکھی ہوئی ہے؟');
INSERT INTO "ur_maududi" VALUES(54,44,'54:44','یا اِن لوگوں کا کہنا یہ ہے کہ ہم ایک مضبوط جتھا ہیں، اپنا بچاؤ کر لیں گے؟');
INSERT INTO "ur_maududi" VALUES(54,45,'54:45','عنقریب یہ جتّھا شکست کھا جائے گا اور یہ سب پیٹھ پھیر کر بھاگتے نظر آئیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(54,46,'54:46','بلکہ اِن سے نمٹنے کے لیے اصل وعدے کا وقت تو قیامت ہے اور وہ بڑی آفت اور زیادہ تلخ ساعت ہے');
INSERT INTO "ur_maududi" VALUES(54,47,'54:47','یہ مجرم لوگ در حقیقت غلط فہمی میں میں مبتلا ہیں اور اِن کی عقل ماری گئی ہے');
INSERT INTO "ur_maududi" VALUES(54,48,'54:48','جس روز یہ منہ کے بل آگ میں گھسیٹے جائیں گے اُس روز اِن سے کہا جائے گا کہ اب چکھو جہنم کی لپٹ کا مزا');
INSERT INTO "ur_maududi" VALUES(54,49,'54:49','ہم نے ہر چیز ایک تقدیر کے ساتھ پیدا کی ہے، ');
INSERT INTO "ur_maududi" VALUES(54,50,'54:50','اور ہمارا حکم بس ایک ہی حکم ہوتا ہے اور پلک جھپکاتے وہ عمل میں آجاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(54,51,'54:51','تم جیسے بہت سوں کو ہم ہلاک کر چکے ہیں، پھر ہے کوئی نصیحت قبول کرنے والا؟');
INSERT INTO "ur_maududi" VALUES(54,52,'54:52','جو کچھ اِنہوں نے کیا ہے وہ سب دفتروں میں درج ہے');
INSERT INTO "ur_maududi" VALUES(54,53,'54:53','اور ہر چھوٹی بڑی بات لکھی ہوئی موجود ہے۔ ');
INSERT INTO "ur_maududi" VALUES(54,54,'54:54','نافرمانی سے پرہیز کرنے والے یقیناً باغوں اور نہروں میں ہوں گے');
INSERT INTO "ur_maududi" VALUES(54,55,'54:55','سچی عزت کی جگہ، بڑے ذی اقتدار بادشاہ کے قریب');
INSERT INTO "ur_maududi" VALUES(55,1,'55:1','رحمٰن نے');
INSERT INTO "ur_maududi" VALUES(55,2,'55:2','اِس قرآن کی تعلیم دی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(55,3,'55:3','اُسی نے انسان کو پیدا کیا ');
INSERT INTO "ur_maududi" VALUES(55,4,'55:4','اور اسے بولنا سکھایا۔ ');
INSERT INTO "ur_maududi" VALUES(55,5,'55:5','سورج اور چاند ایک حساب کے پابند ہیں ');
INSERT INTO "ur_maududi" VALUES(55,6,'55:6','اور تارے اور درخت سب سجدہ ریز ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(55,7,'55:7','آسمان کو اُس نے بلند کیا اور میزان قائم کر دی۔ ');
INSERT INTO "ur_maududi" VALUES(55,8,'55:8','اِس کا تقاضا یہ ہے کہ تم میزان میں خلل نہ ڈالو');
INSERT INTO "ur_maududi" VALUES(55,9,'55:9','انصاف کے ساتھ ٹھیک ٹھیک تولو اور ترازو میں ڈنڈی نہ مارو۔ ');
INSERT INTO "ur_maududi" VALUES(55,10,'55:10',' زمین کو اس نے سب مخلوقات کے لیے بنایا۔ ');
INSERT INTO "ur_maududi" VALUES(55,11,'55:11','اس میں ہر طرح کے بکثرت لذیذ پھل ہیں کھجور کے درخت ہیں جن کے پھل غلافوں میں لپٹے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(55,12,'55:12','طرح طرح کے غلّے ہیں جن میں بُھوسا بھی ہوتا ہے اور دانہ بھی۔ ');
INSERT INTO "ur_maududi" VALUES(55,13,'55:13','پس اے جِنّ وانس، تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے؟ ');
INSERT INTO "ur_maududi" VALUES(55,14,'55:14','انسان کو اُس نے ٹھیکری جیسے سُوکھے سڑے ہوئے گارے سے بنایا ');
INSERT INTO "ur_maududi" VALUES(55,15,'55:15','اور جِن کو آگ کی لَپٹ سے پیدا کیا۔ ');
INSERT INTO "ur_maududi" VALUES(55,16,'55:16','پس اے جِنّ و اِنس ، تم اپنے رب کی کن کن عجائبِ قدرت کو جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,17,'55:17','دونوں مشرق اور دونوں مغرب، سب کا مالک و پروردگار وہی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(55,18,'55:18','پس اے جِنّ و اِنس تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,19,'55:19','دو سمندروں کو اس نے چھوڑ دیا کہ باہم مل جائیں');
INSERT INTO "ur_maududi" VALUES(55,20,'55:20','پھر بھی ان کے درمیا ن ایک پر دہ حائل ہے جس سے وہ تجاوز نہیں کرتے۔ ');
INSERT INTO "ur_maududi" VALUES(55,21,'55:21','پس اے جن و انس، تم اپنے رب کی قدرت کے کن کن کرشموں کو جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,22,'55:22','اِن سمندروں سے موتی اور مونگے نکلتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(55,23,'55:23','پس اے جِنّ و اِنس، تم اپنے رب کی قدرت کے کن کن کمالات کو جھٹلاؤ گے؟ ');
INSERT INTO "ur_maududi" VALUES(55,24,'55:24','اور یہ جہاز اُسی کے ہیں جو سمندر میں پہاڑوں کی طرح اونچے اُٹھے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(55,25,'55:25','پس اے جِنّ و اِنس تم اپنے رب کے کن کن احسانات کو جھٹلاؤ گے؟ ');
INSERT INTO "ur_maududi" VALUES(55,26,'55:26',' ہر چیز جو اس زمین پر ہے فنا ہو جانے والی ہے');
INSERT INTO "ur_maududi" VALUES(55,27,'55:27','اور صرف تیرے رب کی جلیل و کریم ذات ہی باقی رہنے والی ہے');
INSERT INTO "ur_maududi" VALUES(55,28,'55:28','پس اے جِنّ و اِنس، تم اپنے رب کے کن کن کمالات کو جھٹلاؤ گے؟ ');
INSERT INTO "ur_maududi" VALUES(55,29,'55:29','زمین اور آسمانوں میں جو بھی ہیں سب اپنی حا جتیں اُسی سے مانگ رہے ہیں۔ ہر آن وہ نئی شان میں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(55,30,'55:30','پس اے جِنّ و اِنس، تم اپنے رب کی کن کن صفاتِ حمیدہ کو جھٹلاؤ گے؟ ');
INSERT INTO "ur_maududi" VALUES(55,31,'55:31','اے زمین کے بوجھو، عنقریب ہم تم سے باز پُرس کرنے کے لیے فارغ ہوئے جاتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(55,32,'55:32','پھر دیکھ لیں گے کہ ، تم اپنے رب کے کن کن احسانات کو جھٹلاتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(55,33,'55:33','اے گروہ جِنّ و اِنس، اگر تم زمین اور آسمانوں کی سرحدوں سے نکل کر بھاگ سکتے ہو تو بھاگ دیکھو۔ نہیں بھاگ سکتے۔ اس کے لیے بڑا زور چاہیے۔ ');
INSERT INTO "ur_maududi" VALUES(55,34,'55:34','اپنے رب کی کن کن قدرتوں کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,35,'55:35','(بھاگنے کی کوشش کرو گے تو) تم پر آگ کا شعلہ اور دھواں چھوڑ دیا جائیگا جس کا تم مقابلہ نہ کر سکو گے');
INSERT INTO "ur_maududi" VALUES(55,36,'55:36','اے جن و انس، تم اپنے رب کی کن کن قدرتوں کا انکار کرو گے؟');
INSERT INTO "ur_maududi" VALUES(55,37,'55:37','پھر (کیا بنے گی اُس وقت) جب آسمان پھٹے گا اور لال چمڑے کی طرح سُرخ ہو جائے گا؟ ');
INSERT INTO "ur_maududi" VALUES(55,38,'55:38','اےجِنّ و اِنس (اُس وقت) تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے؟ ');
INSERT INTO "ur_maududi" VALUES(55,39,'55:39','اُس روز کسی انسان اور کسی جِن سے اُس کا گناہ پوچھنے کی ضرورت نہ ہوگی، ');
INSERT INTO "ur_maududi" VALUES(55,40,'55:40','پھر(دیکھ لیاجائے گا کہ) تم دونوں گروہ اپنے رب کے کن کن احسانات کا انکار کرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(55,41,'55:41','مجرم وہاں اپنے چہروں سے پہچان لیے جائیں گے اور انہیں پیشانی کے بال اور پاؤں پکڑ پکڑ کر گھسیٹا جائے گا');
INSERT INTO "ur_maududi" VALUES(55,42,'55:42','اُس وقت تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,43,'55:43','(اُس وقت کہا جائے گا) یہ وہی جہنم ہے جس کو مجرمین جھوٹ قرار دیا کرتے تھے');
INSERT INTO "ur_maududi" VALUES(55,44,'55:44','اسی جہنم اور کھولتے ہوئے پانی کے درمیان وہ گردش کرتے رہیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(55,45,'55:45','پھر اپنے رب کی کن کن قدرتوں کو تم جھٹلاؤ گے؟ ');
INSERT INTO "ur_maududi" VALUES(55,46,'55:46','اور ہر اُس شخص کے لیے جو اپنے رب کے حضور پیش ہونے کا خوف رکھتا ہو، دو باغ ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(55,47,'55:47','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟ ');
INSERT INTO "ur_maududi" VALUES(55,48,'55:48','ہری بھری ڈالیوں سے بھرپور');
INSERT INTO "ur_maududi" VALUES(55,49,'55:49','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,50,'55:50','دونوں باغوں میں دو چشمے رواں');
INSERT INTO "ur_maududi" VALUES(55,51,'55:51','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,52,'55:52','دونوں باغوں میں ہر پھل کی دو قسمیں۔ ');
INSERT INTO "ur_maududi" VALUES(55,53,'55:53','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,54,'55:54','جنّتی لوگ ایسے فرشو ں پر تکیے لگا کے بیٹھیں گے جن کے اَستر دبیز ریشم کے ہوں گے، اور باغوں کی ڈالیاں پھلوں سے جھکی پڑرہی ہوں گی');
INSERT INTO "ur_maududi" VALUES(55,55,'55:55','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,56,'55:56','اِن نعمتوں کے درمیان شرمیلی نگاہوں والیاں ہوں گی جنہیں اِن جنتیوں سے پہلے کسی انسان یا جِن نے چُھوا نہ ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(55,57,'55:57','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,58,'55:58','ایسی خوبصورت جیسے ہیرے اور موتی');
INSERT INTO "ur_maududi" VALUES(55,59,'55:59','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,60,'55:60','نیکی کا بدلہ نیکی کے سوا اور کیا ہوسکتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(55,61,'55:61','پھر اے جِنّ و اِنس۔ اپنے رب کے کن کن اوصاف ِحمیدہ کا تم انکار کرو گے؟ ');
INSERT INTO "ur_maududi" VALUES(55,62,'55:62','اور ان دو باغوں کے علاوہ دوباغ اور ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(55,63,'55:63','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,64,'55:64','گھَنے سرسبز و شاداب باغ۔ ');
INSERT INTO "ur_maududi" VALUES(55,65,'55:65','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,66,'55:66','دونوں باغوں میں دو چشمے فواروں کی طرح ابلتے ہوئے');
INSERT INTO "ur_maududi" VALUES(55,67,'55:67','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,68,'55:68','اُن میں بکثرت پھل اور کھجوریں اور انار');
INSERT INTO "ur_maududi" VALUES(55,69,'55:69','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,70,'55:70','اِن نعمتوں کے درمیان خوب سیرت اور خوبصورت بیویاں');
INSERT INTO "ur_maududi" VALUES(55,71,'55:71','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,72,'55:72','خیموں میں ٹھیرائی ہوئی حُوریں۔ ');
INSERT INTO "ur_maududi" VALUES(55,73,'55:73','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,74,'55:74','اِن جنتیوں سے پہلے کبھی انسان یا جن نے اُن کو نہ چھوا ہوگا');
INSERT INTO "ur_maududi" VALUES(55,75,'55:75','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,76,'55:76','وہ جنّتی سبز قالینوں اور نفیس و نادر فرشوں پر تکیے لگا کے بیٹھیں گے');
INSERT INTO "ur_maududi" VALUES(55,77,'55:77','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے؟');
INSERT INTO "ur_maududi" VALUES(55,78,'55:78','بڑی برکت والا ہے تیرے رب جلیل و کریم کا نام');
INSERT INTO "ur_maududi" VALUES(56,1,'56:1','جب وہ ہونے والا واقعہ پیش آ جائے گا');
INSERT INTO "ur_maududi" VALUES(56,2,'56:2','تو کوئی اس کے وقوع کو جُھٹلانے والا نہ ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(56,3,'56:3','وہ تہ و بالا کر دینے والی آفت ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(56,4,'56:4','زمین اس وقت یکبارگی ہلا ڈالی جائے گی ');
INSERT INTO "ur_maududi" VALUES(56,5,'56:5','اور پہاڑ اس طرح ریزہ ریزہ کر دیے جائیں گے');
INSERT INTO "ur_maududi" VALUES(56,6,'56:6','کہ پراگندہ غبار بن کر رہ جائیں گے');
INSERT INTO "ur_maududi" VALUES(56,7,'56:7','تم لوگ اُس وقت تین گروہوں میں تقسیم ہو جاؤ گے ');
INSERT INTO "ur_maududi" VALUES(56,8,'56:8','دائیں بازو والے، سودائیں بازو والوں (کی خوش نصیبی)کا کیا کہنا');
INSERT INTO "ur_maududi" VALUES(56,9,'56:9','اور بائیں بازو والے، تو بائیں بازو والوں(کی بد نصیبی)کا کیا ٹھکانا');
INSERT INTO "ur_maududi" VALUES(56,10,'56:10','اور آگے والے تو پھر آگے والے ہی ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(56,11,'56:11','وہی تو مقرب لوگ ہیں');
INSERT INTO "ur_maududi" VALUES(56,12,'56:12','نعمت بھری جنتوں میں رہیں گے');
INSERT INTO "ur_maududi" VALUES(56,13,'56:13','اگلوں میں سے بہت ہوں گے');
INSERT INTO "ur_maududi" VALUES(56,14,'56:14','اور پچھلوں میں سے کم۔ ');
INSERT INTO "ur_maududi" VALUES(56,15,'56:15','مرصع تختوں پر');
INSERT INTO "ur_maududi" VALUES(56,16,'56:16','تکیے لگا ئے آمنے سامنے بیٹھیں گے');
INSERT INTO "ur_maududi" VALUES(56,17,'56:17','اُن کی مجلسوں میں اَبَدی لڑکے شرابِ چشمہٴ جاری سے');
INSERT INTO "ur_maududi" VALUES(56,18,'56:18','لبریز پیالے کنٹر اور ساغر لیے دوڑتے پھرتے ہونگے');
INSERT INTO "ur_maududi" VALUES(56,19,'56:19','جسے پی کر نہ ان کا سر چکرائے گا نہ ان کی عقل میں فتور آئے گا۔ ');
INSERT INTO "ur_maududi" VALUES(56,20,'56:20','اور وہ اُن کے سامنے طرح طرح کے لذیذ پھل پیش کریں گے جسے چاہیں چن لیں');
INSERT INTO "ur_maududi" VALUES(56,21,'56:21','اور پرندوں کا گوشت پیش کریں گے کہ جس پرندےکا چاہیں استعمال کریں۔ ');
INSERT INTO "ur_maududi" VALUES(56,22,'56:22','اور ان کے لیے خوبصورت آنکھوں والی حوریں ہونگی');
INSERT INTO "ur_maududi" VALUES(56,23,'56:23','ایسی حسین جیسے چُھپا کر رکھے ہوئے موتی۔ ');
INSERT INTO "ur_maududi" VALUES(56,24,'56:24','یہ سب کچھ اُن اعمال کی جزا کے طور پر انہیں ملے گا جو وہ دنیا میں کرتے رہے تھے');
INSERT INTO "ur_maududi" VALUES(56,25,'56:25','وہاں وہ کوئی بیہودہ کلام یا گناہ کی بات نہ سنیں گے ');
INSERT INTO "ur_maududi" VALUES(56,26,'56:26','جو بات بھی ہوگی ٹھیک ٹھیک ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(56,27,'56:27','اور دائیں بازو والے، دائیں بازو والوں کی خوش نصیبی کا کیا کہنا');
INSERT INTO "ur_maududi" VALUES(56,28,'56:28','وہ بے خار بیریوں، ');
INSERT INTO "ur_maududi" VALUES(56,29,'56:29','اور تہ بر تہ چڑھے ہوئے کیلوں');
INSERT INTO "ur_maududi" VALUES(56,30,'56:30','اور دور تک پھیلی ہوئی چھاؤں');
INSERT INTO "ur_maududi" VALUES(56,31,'56:31','اور ہر دم رواں پانی');
INSERT INTO "ur_maududi" VALUES(56,32,'56:32','اور کبھی ختم نہ ہونے والے');
INSERT INTO "ur_maududi" VALUES(56,33,'56:33','اور بے روک ٹوک ملنے والے بکثرت پھلوں، ');
INSERT INTO "ur_maududi" VALUES(56,34,'56:34','اور اونچی نشست گاہوں میں ہوں گے');
INSERT INTO "ur_maududi" VALUES(56,35,'56:35','ان کی بیویوں کو ہم خاص طور پر نئے سرے سے پیدا کریں گے');
INSERT INTO "ur_maududi" VALUES(56,36,'56:36','اور انہیں باکرہ بنا دیں گے، ');
INSERT INTO "ur_maududi" VALUES(56,37,'56:37','اپنے شوہروں کی عاشق اور عمر میں ہم سِن۔ ');
INSERT INTO "ur_maududi" VALUES(56,38,'56:38','یہ کچھ دائیں بازو والوں کے لیے ہے');
INSERT INTO "ur_maududi" VALUES(56,39,'56:39','وہ اگلوں میں سے بہت ہوں گے');
INSERT INTO "ur_maududi" VALUES(56,40,'56:40','اور پچھلوں میں سے بھی بہت');
INSERT INTO "ur_maududi" VALUES(56,41,'56:41','اور بائیں بازو والے، بائیں بازو والوں کی بد نصیبی کا کیا پوچھنا');
INSERT INTO "ur_maududi" VALUES(56,42,'56:42','وہ لو کی لپٹ اور کھولتے ہوئے پانی');
INSERT INTO "ur_maududi" VALUES(56,43,'56:43','اور کالے دھوئیں کے سائے میں ہوں گے');
INSERT INTO "ur_maududi" VALUES(56,44,'56:44','جو نہ ٹھنڈا ہوگا نہ آرام دہ');
INSERT INTO "ur_maududi" VALUES(56,45,'56:45','یہ وہ لوگ ہوں گے جو اِس انجام کو پہنچنے سے پہلے خوشحال تھے');
INSERT INTO "ur_maududi" VALUES(56,46,'56:46','اور گناہِ عظیم پر اصرار کرتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(56,47,'56:47','کہتے تھے "کیا جب ہم مر کر خاک ہو جائیں گے اور ہڈیوں کا پنجر رہ جائیں گے تو پھر اٹھا کھڑے کیے جائیں گے؟');
INSERT INTO "ur_maududi" VALUES(56,48,'56:48','اور کیا ہمارے وہ باپ دادا بھی اٹھائے جائیں گے جو پہلے گزر چکے ہیں؟"');
INSERT INTO "ur_maududi" VALUES(56,49,'56:49','اے نبیؐ، اِن لوگوں سے کہو، یقیناً اگلے اور پچھلے سب');
INSERT INTO "ur_maududi" VALUES(56,50,'56:50','ایک دن ضرور جمع کیے جانے والے ہیں جس کا وقت مقرر کیا جا چکا ہے');
INSERT INTO "ur_maududi" VALUES(56,51,'56:51','پھر اے گمراہو اور جھٹلانے والو!');
INSERT INTO "ur_maududi" VALUES(56,52,'56:52','تم شجرِ زَقّوم کی غذا کھانے والے ہو');
INSERT INTO "ur_maududi" VALUES(56,53,'56:53','اُسی سے تم پیٹ بھرو گے');
INSERT INTO "ur_maududi" VALUES(56,54,'56:54','اور اوپر سے کھولتا ہوا پانی');
INSERT INTO "ur_maududi" VALUES(56,55,'56:55','تونس لگے ہوئے اونٹ کی طرح پیو گے');
INSERT INTO "ur_maududi" VALUES(56,56,'56:56','یہ ہے بائیں والوں کی ضیافت کا سامان روز جزا میں');
INSERT INTO "ur_maududi" VALUES(56,57,'56:57',' ہم نے تمہیں پیدا کیا ہے پھر کیوں تصدیق نہیں کرتے؟ ');
INSERT INTO "ur_maududi" VALUES(56,58,'56:58','کبھی تم نے غور کیا، یہ نطفہ جو تم ڈالتے ہو');
INSERT INTO "ur_maududi" VALUES(56,59,'56:59','اس سے بچّہ تم بناتے ہو یا اس کے بنانے والے ہم ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(56,60,'56:60','ہم نے تمہارے درمیان موت کو تقسیم کیا ہے، اور ہم اِس سے عاجز نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(56,61,'56:61','کہ تمہاری شکلیں بدل دیں اور کسی ایسی شکل میں تمہیں پیدا کر دیں جس کو تم نہیں جانتے۔ ');
INSERT INTO "ur_maududi" VALUES(56,62,'56:62','اپنی پہلی پیدائش کو تو تم جانتے ہو، پھر کیوں سبق نہیں لیتے؟ ');
INSERT INTO "ur_maududi" VALUES(56,63,'56:63','کبھی تم نے سوچا، یہ بیج جو تم بوتے ہو');
INSERT INTO "ur_maududi" VALUES(56,64,'56:64','ان سے کھیتیاں تم اُگا تے ہو یا اُن کے اُگانے والے ہم ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(56,65,'56:65','ہم چاہیں تو ان کھیتیوں کو بھس بنا کر رکھ دیں اور تم طرح طرح کی باتیں بناتے رہ جاؤ');
INSERT INTO "ur_maududi" VALUES(56,66,'56:66','کہ ہم پر تو الٹی چٹی پڑ گئی');
INSERT INTO "ur_maududi" VALUES(56,67,'56:67','بلکہ ہمارے تو نصیب ہی پھوٹے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(56,68,'56:68','کبھی تم نے آنکھیں کھول کر دیکھا، یہ پانی جو تم پیتے ہو');
INSERT INTO "ur_maududi" VALUES(56,69,'56:69','اِسے تم نے بادل سے بر سا یا ہےیا اِ س کے برسانے والے ہم ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(56,70,'56:70','ہم چاہیں تو اُسے سخت کھاری بنا کر رکھ دیں، پھر کیوں تم شکر گزار نہیں ہوتے؟ ');
INSERT INTO "ur_maududi" VALUES(56,71,'56:71','کبھی تم نے خیال کیا، یہ آگ جو تم سلگاتے ہو');
INSERT INTO "ur_maududi" VALUES(56,72,'56:72','اِس کا درخت تم نے پیدا کیا ہے، یا اس کے پیدا کرنے والے ہم ہیں؟');
INSERT INTO "ur_maududi" VALUES(56,73,'56:73','ہم نے اُس کو یاد دہانی کا ذریعہ اور حاجت مندوں کے لیےسامان ِ زیست بنا یا ہے');
INSERT INTO "ur_maududi" VALUES(56,74,'56:74','پس اے نبیؐ ، اپنے رَبِّ عظیم کے نام کی تسبیح کرو۔ ');
INSERT INTO "ur_maududi" VALUES(56,75,'56:75',' پس نہیں، میں قسم کھاتا ہوں تاروں کے مواقع کی');
INSERT INTO "ur_maududi" VALUES(56,76,'56:76','اور اگر تم سمجھو تو یہ بہت بڑی قسم ہے');
INSERT INTO "ur_maududi" VALUES(56,77,'56:77','کہ یہ ایک بلند پایہ قرآن ہے، ');
INSERT INTO "ur_maududi" VALUES(56,78,'56:78','ایک محفوظ کتاب میں ثَبَت، ');
INSERT INTO "ur_maududi" VALUES(56,79,'56:79','جسے مُطَہَّرین کےسوا کوئی چُھو نہیں سکتا۔ ');
INSERT INTO "ur_maududi" VALUES(56,80,'56:80','یہ رب العالمین کا نازل کردہ ہے');
INSERT INTO "ur_maududi" VALUES(56,81,'56:81','پھر کیا اِس کلام کے ساتھ تم بے اعتنائی برتتے ہو، ');
INSERT INTO "ur_maududi" VALUES(56,82,'56:82','اور اِس نعمت میں اپنا حصّہ تم نے یہ رکھا ہے کہ اِسے جُھٹلاتے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(56,83,'56:83','تو جب مرنے والے کی جان حلق تک پہنچ چکی ہوتی ہے');
INSERT INTO "ur_maududi" VALUES(56,84,'56:84','اور تم آنکھوں سے دیکھ رہے ہوتے ہو کہ وہ مر رہا ہے،');
INSERT INTO "ur_maududi" VALUES(56,85,'56:85','اُس وقت تمہاری بہ نسبت ہم اُس کے زیادہ قریب ہوتے ہیں مگر تم کو نظر نہیں آتے');
INSERT INTO "ur_maududi" VALUES(56,86,'56:86','اب اگر تم کسی کے محکوم نہیں ہو اور اپنے اِس خیال میں سچے ہو،');
INSERT INTO "ur_maududi" VALUES(56,87,'56:87','اُس وقت اُس کی نکلتی ہوئی جان کو واپس کیوں نہیں لے آتے؟');
INSERT INTO "ur_maududi" VALUES(56,88,'56:88','پھر وہ مرنے والا اگر مقربین میں سے ہو');
INSERT INTO "ur_maududi" VALUES(56,89,'56:89','تو اس کے لیے راحت اور عمدہ رزق اور نعمت بھری جنت ہے');
INSERT INTO "ur_maududi" VALUES(56,90,'56:90','اور اگر وہ اصحاب یمین میں سے ہو');
INSERT INTO "ur_maududi" VALUES(56,91,'56:91','تو اس کا استقبال یوں ہوتا ہے کہ سلام ہے تجھے، تو اصحاب الیمین میں سے ہے');
INSERT INTO "ur_maududi" VALUES(56,92,'56:92','اور اگر وہ جھٹلانے والے گمراہ لوگوں میں سے ہو');
INSERT INTO "ur_maududi" VALUES(56,93,'56:93','تو اس کی تواضع کے لیے کھولتا ہوا پانی ہے');
INSERT INTO "ur_maududi" VALUES(56,94,'56:94','اور جہنم میں جھونکا جانا');
INSERT INTO "ur_maududi" VALUES(56,95,'56:95','یہ سب کچھ قطعی حق ہے');
INSERT INTO "ur_maududi" VALUES(56,96,'56:96','پس اے نبیؐ ،اپنے رَبِّ عظیم کے نام کی تسبیح کرو۔ ');
INSERT INTO "ur_maududi" VALUES(57,1,'57:1','اللہ کی تسبیح کی ہے ہر اُس چیز نے جو زمین اور آسمانوں میں ہے، اور وہی زبر دست دانا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(57,2,'57:2','زمین اور آسمانوں کی سلطنت کا مالک وہی ہے، زندگی بخشتا ہے اور موت دیتا ہے، اور ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(57,3,'57:3','وہی اوّل بھی ہے اور آخر بھی ، اور ظاہر بھی ہے اور مخفی بھی، اور وہ ہر چیز کا علم رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(57,4,'57:4','وہی ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا اور پھر عرش پر جلوہ فرما ہوا۔ اُس کے علم میں ہے جو کچھ زمین میں جاتا ہے اور جو کچھ اس سے نکلتا ہے اور جو کچھ آسمان سے اُترتاہے اور جو کچھ اُس میں چڑھتا ہے۔ وہ تمہارے ساتھ ہے جہاں بھی تم ہو۔ جو کام بھی تم کرتے ہو اسے وہ دیکھ رہا ہے');
INSERT INTO "ur_maududi" VALUES(57,5,'57:5','وہی زمین اور آسمانوں کی بادشاہی کا مالک ہے اور تمام معاملات فیصلے کے لیے اُسی کی طرف رجوع کیے جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(57,6,'57:6','وہی رات کو دن میں اور دن کو رات میں داخل کرتا ہے، اور دلوں کے چھپے ہوئے راز تک جانتا ہے');
INSERT INTO "ur_maududi" VALUES(57,7,'57:7','ایمان لاؤ اللہ اور اُس کے رسول پر اور خرچ کرو اُن چیزوں میں سے جن پر اُس نے تم کو خلیفہ بنایا ہے۔ جو لوگ تم میں سے ایمان لائیں گے اور مال خرچ کریں گے ان کے لیے بڑا اجر ہے');
INSERT INTO "ur_maududi" VALUES(57,8,'57:8','تمہیں کیا ہو گیا ہے کہ تم اللہ پر ایمان نہیں لاتےحالانکہ رسول تمہیں اپنے رب پر ایمان لانے کی دعوت دے رہا ہے اور وہ تم سے عہد لے چکا ہے اگر تم واقعی ماننے والے ہو');
INSERT INTO "ur_maududi" VALUES(57,9,'57:9','وہ اللہ ہی تو ہے جو اپنے بندے پر صاف صاف آیتیں نازل کر رہا ہے تاکہ تمہیں تاریکیوں سے نکال کر روشنی میں لے آئے، اور حقیقت یہ ہے کہ اللہ تم پر نہایت شفیق اور مہربان ہے');
INSERT INTO "ur_maududi" VALUES(57,10,'57:10','آخر کیا وجہ ہےکہ تم اللہ کی راہ میں خرچ نہیں کرتے حالانکہ زمین اور آسمانوں کی میراث اللہ ہی کے لیے ہے۔ تم میں سے جو لوگ فتح کے بعد خرچ اور جہاد کریں گے وہ کبھی اُن لوگوں کے برابر نہیں ہو سکتے جنہوں نے فتح سے پہلے خرچ اور جہاد کیا ہے۔ اُن کا درجہ بعد میں خرچ اور جہاد کرنے والوں سے بڑھ کر ہے اگرچہ اللہ نے دونوں ہی سے اچھے وعدے فرمائے ہیں۔ جو کچھ تم کرتے ہو اللہ اُس سے باخبر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(57,11,'57:11','کون ہے جو اللہ کو قرض دے؟ اچھا قرض، تاکہ اللہ اسے کئی گُنا بڑھا کر واپس دے، اور اُس کے لیے بہترین اجر ہے ');
INSERT INTO "ur_maududi" VALUES(57,12,'57:12','اُس دن جبکہ تم مومن مردوں اور عورتوں کو دیکھوگے کہ ان کا نور اُن کے آگے آگے اور ان کے دائیں جانب دوڑ رہا ہوگا۔ (ان سے کہا جائے گاکہ) ”آج بشارت ہے تمہارے لیے“۔ جنتیں ہونگی جن کے نیچے نہریں بہ رہی ہونگی، جن میں وہ ہمیشہ رہیں گے یہی ہے بڑی کامیابی');
INSERT INTO "ur_maududi" VALUES(57,13,'57:13','اُس روز منافق مردوں اور عورتوں کا حال یہ ہوگا کہ وہ مومنوں سے کہیں گے ذرا ہماری طرف دیکھو تاکہ ہم تمہارے نُور سے کچھ فائدہ اُٹھائیں، مگر ان سے کہا جائے گا پیچھے ہٹ جاؤ، اپنا نور کہیں اور تلاش کرو۔ پھر ان کے درمیان ایک دیوار حائل کردی جائے گی جس میں ایک دروازہ ہوگا۔ اُس دروازے کے اندر رحمت ہوگی اور باہر عذاب۔ ');
INSERT INTO "ur_maududi" VALUES(57,14,'57:14','وہ مومنوں سے پکار پکار کر کہیں گے کیا ہم تمہارے ساتھ نہ تھے؟ مومن جواب دیں گے ہاں، مگر تم نے اپنے آپ کو خود فتنے میں ڈالا، موقع پرستی کی، شک میں پڑے رہے، اور جُھوٹی توقعات تمہیں فریب دیتی رہیں، یہاں تک کہ اللہ کا فیصلہ آگیا، اور آخر وقت تک وہ بڑا دھوکے باز تمہیں اللہ کے معاملہ میں دھوکا دیتارہا');
INSERT INTO "ur_maududi" VALUES(57,15,'57:15','لہذا آج نہ تم سے کوئی فدیہ قبول کیا جائے گا اور نہ اُن لوگو ں سے جنہوں نے کُھلا کُھلا کُفر کیا تھا۔ تمہارا ٹھکانا جہنم ہے، وہی تمہاری خبر گیری کرنے والی ہے اور یہ بدترین انجام ہے');
INSERT INTO "ur_maududi" VALUES(57,16,'57:16','کیا ایما ن لانے والوں کے لیے ابھی وہ وقت نہیں آیا کہ اُن کے دل اللہ کے ذکر سے پگھلیں اور اُس کے نازل کردہ حق کے آگے جُھکیں اور وہ اُن لوگوں کی طرح نہ ہو جائیں جنہیں پہلے کتاب دی گئی تھی، پھر ایک لمبی مدّت اُن پر گزر گئی تو اُن کے دل سخت ہو گئے اور آج ان میں سے اکثر فاسق بنے ہوئے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(57,17,'57:17','خوب جان لو کہ اللہ زمین کو اُس کی موت کے بعد زندگی بخشتاہے، ہم نے نشانیاں تم کو صاف صاف دکھا دی ہیں، شاید کہ تم عقل سے کام لو۔ ');
INSERT INTO "ur_maududi" VALUES(57,18,'57:18','مَردوں اور عورتوں میں سے جو لوگ صدقات دینے والے ہیں اور جنہوں نے اللہ کو قرضِ حَسَن دیا ہے، اُن کو یقیناً کئی گُنا بڑھا کر دیا جائے گا اور ان کے لیےبہترین اجر ہے');
INSERT INTO "ur_maududi" VALUES(57,19,'57:19','اور جو لوگ اللہ اور اُس کے رسُولوں پر ایمان لائے ہیں وہی اپنے رب کے نزدیک صِدّیق اور شہید ہیں،اُن کے لیے اُن کا اجر اور اُن کا نُور ہے۔ اور جن لوگوں نے کُفر کیا ہے اور ہماری آیات کوجُھٹلایا ہے وہ دوزخی ہیں');
INSERT INTO "ur_maududi" VALUES(57,20,'57:20','خُوب جان لو کہ یہ دنیا کی زندگی اس کے سوا کچھ نہیں کہ ایک کھیل اور دل لگی اور ظاہری ٹیپ ٹاپ اور تمہارا آپس میں ایک دوسرے پر فخر جتانا اور مال و اولاد میں ایک دُوسرے سے بڑھ جانے کی کوشش کرنا ہے۔ اس کی مثال ایسی ہے جیسے ایک بارش ہوگئی تو اس سے پیدا ہونے والی نباتات کو دیکھ کر کاشت کار خوش ہوگئے۔ پھر وہی کھیتی پک جاتی ہے اور تم دیکھتے ہو کہ وہ زرد ہوگئی۔ پھر وہ بُھس بن کر رہ جاتی ہے۔ اِس کے برعکس آخرت وہ جگہ ہے جہاں سخت عذاب ہے اور اللہ کی مغفرت اور اس کی خوشنودی ہے۔ دنیا کی زندگی ایک دھوکے کی ٹٹّی کے سوا کچھ نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(57,21,'57:21','دَوڑو اور ایک دُوسرے سے آگے بڑھنے کی کوشش کرو اپنے رب کی مغفرت اور اُس جنّت کی طرف جس کی وسعت آسمان و زمین جیسی ہے، جو مہیّا کی گئی ہے اُن لوگوں کے لیےجو اللہ اوراُس کے رسُولوں پر ایمان لائے ہوں۔یہ اللہ کا فضل ہے، جسےچاہتا ہے عطافرماتا ہے، اور اللہ بڑے فضل والا ہے');
INSERT INTO "ur_maududi" VALUES(57,22,'57:22','کوئی مصیبت ایسی نہیں ہے جو زمین میں یا تمہارے اپنے نفس پر نازل ہوتی ہو اور ہم نے اس کو پیدا کرنے سے پہلے ایک کتاب میں لکھ نہ رکھا ہو۔ ایسا کرنا اللہ کے لیے بہت آسان کام ہے۔ ');
INSERT INTO "ur_maududi" VALUES(57,23,'57:23','(یہ سب کچھ اس لیے ہے) تاکہ جو کچھ بھی نقصان تمہیں ہو اس پر تم دل شکستہ نہ ہو اور جو کچھ اللہ تمہیں عطا فرمائے اس پر پھول نہ جاؤ ۔ اللہ ایسے لوگوں کو پسند نہیں کرتا جو اپنے آپ کو بڑی چیز سمجھتے ہیں اور فخر جتاتے ہیں');
INSERT INTO "ur_maududi" VALUES(57,24,'57:24','جو خود بُخل کرتے ہیں اور دوسروں کوبُخل کرنے پر اُکساتے ہیں۔ اب اگر کوئی رُوگردانی کرتا ہے تو اللہ بےنیاز اور ستودہ صفات ہے۔ ');
INSERT INTO "ur_maududi" VALUES(57,25,'57:25','ہم نے اپنے رسُولوں کو صاف صاف نشانیوں اور ہدایات کے ساتھ بھیجا، اور اُن کے ساتھ کتاب اور میزان نازل کی تاکہ لوگ انصاف پر قائم ہوں، اور لوہا اتارا جس میں بڑا زور ہے اور لوگوں کے لیے منافع ہیں۔ یہ اس لیے کیا گیا ہے کہ اللہ کو معلوم ہو جائےکہ کون اُس کو دیکھے بغیر اُس کی اور اُس کے رسُولوں کی مدد کرتا ہے۔ یقیناً اللہ بڑی قوّت والا اور زبردست ہے۔ ');
INSERT INTO "ur_maududi" VALUES(57,26,'57:26',' ہم نے نوحؑ اور ابراہیمؑ کو بھیجا اور اُن دونوں کی نسل میں نبوّت اور کتاب رکھ دی۔ پھر ان کی اولاد میں سے کسی نے ہدایت اختیار کی اور بہت سے فاسق ہوگئے۔ ');
INSERT INTO "ur_maududi" VALUES(57,27,'57:27','اُن کے بعد ہم نے پے در پےاپنے رسُول بھیجے، اور ان سب کے بعد عیسٰیؑ ابن مریم کو مبعوث کیا اور اُس کو انجیل عطا کی، اور جن لوگوں نے اس کی پیروی اختیار کی اُن کے دلوں میں ہم نے ترس اور رحم ڈال دیا۔ اور رہبانیت انہوں نے خود ایجاد کر لی، ہم نے اُسے اُن پر فرض نہیں کیا تھا، مگر اللہ کی خوشنودی کی طلب میں انہوں نے آپ ہی یہ بدعت نکالی اور پھر اس کی پابندی کرنے کا جو حق تھا اسے ادا نہ کیا۔ اُن میں سے جو لوگ ایمان لائے ہوئے تھے اُن کا اجر ہم نے عطا کیا، مگران میں سے اکثر لوگ فاسق ہیں');
INSERT INTO "ur_maududi" VALUES(57,28,'57:28','اے لوگوں جو ایمان لائے ہو، اللہ سے ڈرو اور اس کے رسُول (محمد صلى الله عليه وسلم) پر ایمان لاؤ، اللہ تمہیں اپنی رحمت کا دوہرا حصّہ عطا فرمائے گا اور تمہیں وہ نور بخشے گا جس کی روشنی میں تم چلو گے، اور تمہارے قصُور معاف کر دے گا، اللہ بڑا معاف کرنے والا اور مہربان ہے');
INSERT INTO "ur_maududi" VALUES(57,29,'57:29','(تم کو یہ روش اختیار کرنی چاہیے) تاکہ اہل کتاب کو معلوم ہو جائے کہ اللہ کے فضل پر اُن کا کوئی اجارہ نہیں ہے، اور یہ کہ اللہ کا فضل اس کے اپنے ہی ہاتھ میں ہے، جسے چاہتا ہے عطا فرماتا ہے، اور وہ بڑے فضل والا ہے');
INSERT INTO "ur_maududi" VALUES(58,1,'58:1','اللہ نے سُن لی اُس عورت کی بات جو اپنے شوہر کے معاملہ میں تم سے تکرار کر رہی ہے اور اللہ سے فریاد کیے جاتی ہے۔ اللہ تم دونوں کی گفتگو سُن رہا ہے، وہ سب کچھ سُننے اور دیکھنے والا ہے');
INSERT INTO "ur_maududi" VALUES(58,2,'58:2','تم میں سے جو لوگ اپنی بیویوں سے ظہار کرتے ہیں ان کی بیویاں ان کی مائیں نہیں ہیں،ا ن کی مائیں تو وہی ہیں جنہوں نے ان کو جنا ہے۔ یہ لوگ ایک سخت ناپسندیدہ اور جُھوٹی بات کہتے ہیں، اور حقیقت یہ ہے کہ اللہ بڑا معاف کرنے والا اور درگزر فرمانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(58,3,'58:3',' جو لوگ اپنی بیویوں سے ظِہار کریں پھر اپنی اُس بات سے رجُوع کریں جو انہوں نے کہی تھی، تو قبل اس کے کہ دونوں ایک دوسرے کو ہاتھ لگائیں، ایک غلام آزاد کرنا ہوگا۔ اِس سے تم کو نصیحت کی جاتی ہے، اور جو کچھ تم کرتے ہو اللہ اس سے باخبر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(58,4,'58:4','اور جو شخص غلام نہ پائے وہ دو مہینے کے پے درپے روزے رکھے قبل اس کے کہ دونوں ایک دوسرے کو ہاتھ لگائیں۔ اور جو اس پر بھی قادر نہ ہو وہ ساٹھ (۶۰) مسکینوں کو کھانا کھلائے۔ یہ حکم اِس لیے دیا جارہا ہے کہ تم اللہ اور اُس کے رسُول پر ایمان لاؤ۔ یہ اللہ کی مقرر کی ہوئی حدیں ہیں، اور کافروں کے لیے دردناک سزا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(58,5,'58:5','جو لوگ اللہ اور اُس کے رسُول کی مخالفت کرتے ہیں وہ اسی طرح ذلیل و خوار کر دیے جائے گے جس طرح ان سے پہلے کے لوگ ذلیل و خوار کیے جا چکے ہیں۔ ہم نے صاف صاف آیات نازل کر دی ہیں، اور کافروں کے لیے ذلّت کا عذاب ہے۔ ');
INSERT INTO "ur_maududi" VALUES(58,6,'58:6','اُس دن (یہ ذلّت کا عذاب ہونا ہے) جب اللہ ان سب کو پھر سے زندہ کرکے اُٹھائے گا اور انہیں بتادےگا کہ وہ کیا کچھ کرکے آئیں ہیں۔ وہ بُھول گئے ہیں مگر اللہ نے ان کا سب کیا دھرا گِن گِن کر محفوظ کر رکھا ہے اور اللہ ایک ایک چیز پر شاہد ہے');
INSERT INTO "ur_maududi" VALUES(58,7,'58:7',' کیا تم کو خبر نہیں ہے کہ زمین اور آسمانوں کی ہر چیز کا اللہ کو علم ہے؟ کبھی ایسا نہیں ہوتا کہ تین آدمیوں میں کوئی سرگوشی ہو اور ان کے درمیان چوتھا اللہ نہ ہو، یا پانچ آدمیوں میں سرگوشی ہو اور ان کے اندر چھٹا اللہ نہ ہو۔ خفیہ بات کرنے والے خواہ اِس سے کم ہوں یا زیادہ ، جہا ں کہیں بھی وہ ہوں ، اللہ ان کے ساتھ ہوتا ہے۔ پھر قیامت کے روز وہ ان کو بتا دے گا کہ انہوں نے کیا کچھ کیا ہے۔ اللہ ہر چیز کا علم رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(58,8,'58:8','کیا تم نے دیکھا نہیں اُن لوگوں کو جنہیں سرگوشیاں کرنے سے منع کر دیا گیا تھا پھر بھی وہ وہی حرکت کیے جاتے ہیں جس سے انہیں منع کیا گیا تھا؟ یہ لوگ چُھپ چُھپ کر آپس میں گناہ اور زیادتی اور رسُول کی نافرمانی کی باتیں کرتے ہیں، اور جب تمہارے پاس آتے ہیں تو تمہیں اُس طریقے سے سلام کرتے ہیں جس طرح اللہ نے تم پر سلام نہیں کیا ہے اور اپنے دلوں میں کہتے ہیں کہ ہماری اِن باتوں پر اللہ ہمیں عذاب کیوں نہیں دیتا۔ اُن کے لیے جہنم ہی کافی ہے۔ اُسی کا وہ ایندھن بنیں گے۔ بڑا ہی بُرا انجام ہے اُن کا');
INSERT INTO "ur_maududi" VALUES(58,9,'58:9','اے لوگو جو ایمان لائے ہو، جب تم آپس میں پوشیدہ بات کر و تو گناہ اور زیادتی اور رسول کی نافرمانی کی باتیں نہیں بلکہ نیکی اور تقوٰی کی باتیں کرو اور اُس خدا سے ڈرتے رہوجس کے حضور تمہیں حشر میں پیش ہونا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(58,10,'58:10','کانا پھوسی تو ایک شیطانی کام ہے، اور وہ اس لیے کی جاتی ہے کہ ایمان لانے والے لوگ اُس سے رنجیدہ ہوں ، حالانکہ بےاذنِ خدا وہ انہیں کچھ بھی نقصان نہیں پہنچا سکتی،اور مومنوں کو اللہ ہی بھروسہ رکھنا چاہیے۔ ');
INSERT INTO "ur_maududi" VALUES(58,11,'58:11','اے لوگو جو ایمان لائے ہو، جب تم سے کہا جائے کہ اپنی مجلسوں میں کشادگی پیدا کرو تو جگہ کشادہ کر دیا کرو، اللہ تمہیں کشادگی بخشے گا۔ اور جب تم سے کہا جائے کہ اُٹھ جاؤ تو اُٹھ جایا کرو۔ تم میں سے جو لوگ ایمان رکھنے والےہیں اور جن کو علم بخشا گیا ہے، اللہ ان کو بلند درجے عطا فرمائے گا، اور جو کچھ تم کرتے ہواللہ کو اس کی خبر ہے');
INSERT INTO "ur_maududi" VALUES(58,12,'58:12','اے لوگو جو ایمان لائے ہو، جب تم رسول سے تخلیہ میں بات کرو تو بات کرنے سے پہلے کچھ صدقہ دو۔ یہ تمہارے لیے بہتر اور پاکیزہ تر ہے۔ البتہ اگر تم صدقہ دینے کے لیے کچھ نہ پاؤ تو اللہ غفورورحیم ہے');
INSERT INTO "ur_maududi" VALUES(58,13,'58:13','کیا تم ڈرگئے اس بات سےکہ تخلیہ میں گفتگو کرنے سے پہلے تمہیں صدقات دینے ہونگے؟ اچھا، اگر تم ایسا نہ کرو۔۔۔۔ اور اللہ نے تم کو اس سے معاف کر دیا۔۔۔۔ تو نماز قائم کرتے رہو، زکوٰة دیتے رہو اور اللہ اور اُس کے رسُول کی اطاعت کرتے رہو۔ تم جو کچھ کرتے ہو اللہ اس سے باخبر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(58,14,'58:14','کیا تم نے دیکھا نہیں اُن لوگوں کو جنہوں نے دوست بنایا ہے ایک ایسے گروہ کو جواللہ کا مغضوب ہے؟ وہ نہ تمہارے ہیں نہ اُن کے، اور وہ جان بُوجھ کر جُھوٹی بات پر قسمیں کھاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(58,15,'58:15','اللہ نے ان کے لیے سخت عذاب مہیا کر رکھا ہے، بڑے ہی برے کرتوت ہیں جو وہ کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(58,16,'58:16','اُنہوں نے اپنی قسموں کر ڈھال بنا رکھا ہے جس کی آڑ میں وہ اللہ کی راہ سے لوگوں کو روکتے ہیں، اِس پر ان کے لیے ذلّت کا عذاب ہے');
INSERT INTO "ur_maududi" VALUES(58,17,'58:17','اللہ سے بچانے کے لیے نہ ان کے مال کچھ کام آئیں گے نہ ان کی اولاد وہ دوزخ کے یار ہیں، اسی میں وہ ہمیشہ رہیں گے');
INSERT INTO "ur_maududi" VALUES(58,18,'58:18','سی طرح قسمیں کھائیں گے جس طرح تمہارے سامنے کھاتے ہیں اور اپنے نزدیک یہ سمجھیں گے کہ اس سے ان کا کچھ کام بن جائے گا۔خوب جان لو، وہ پرلے درجے کے جُھوٹے ہیں');
INSERT INTO "ur_maududi" VALUES(58,19,'58:19','شیطان اُن پر مسلط ہو چکا ہے اور ا ُس نے خدا کی یاد اُن کے دل سے بھلا دی ہے وہ شیطان کی پارٹی کے لوگ ہیں خبردار ہو، شیطان کی پارٹی والے ہی خسارے میں رہنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(58,20,'58:20','یقیناً ذلیل ترین مخلوقات میں سے ہیں وہ لوگ جو اللہ اور اس کے رسول کا مقابلہ کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(58,21,'58:21','اللہ نے لکھ دیا ہے کہ میں اور میرے رسُول غالب ہو کر رہیں گے۔ فی الواقع اللہ زبردست اور زورآور ہے');
INSERT INTO "ur_maududi" VALUES(58,22,'58:22','تم کبھی یہ نہ پاؤ گے کہ جو لوگ اللہ اور آخرت پر ایمان رکھنے والے ہیں وہ اُن لوگوں سے محبت کرتے ہوں جنہوں نے اللہ اور اُس کے رسُول کی مخالفت کی ہے، خواہ وہ اُن کے باپ ہوں ، یا اُن کے بیٹے، یا اُن کے بھائی یا اُن کے اہلِ خاندان۔ یہ وہ لوگ ہیں جن کے دلوں میں اللہ نے ایمان ثبت کر دیا ہے اور اپنی طرف سے ایک رُوح عطا کر کے ان کو قوّت بخشی ہے۔ وہ ان کو ایسی جنّتوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی۔ ان میں وہ ہمیشہ رہیں گے۔ اللہ ان سے راضی ہوا اور وہ اللہ سے راضی ہوئے۔ وہ اللہ کی پارٹی کے لوگ ہیں۔ خبردار رہو، اللہ کی پارٹی والےہی فلاح پانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(59,1,'59:1','اللہ ہی کی تسبیح کی ہے ہر اُس چیز نے جو آسمانوں اور زمین میں ہے، اور وہی غالب اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(59,2,'59:2','وہی ہے جس نے اہل کتاب کافروں کو پہلے ہی حملے میں اُن کے گھروں سے نکال باہر کیا۔ تمہیں ہر گز یہ گمان نہ تھا کہ وہ نکل جائیں گے، او ر وہ بھی یہ سمجھے بیٹھے تھے کہ اُن کی گڑھیاں انہیں اللہ سے بچا لیں گی۔ مگر اللہ ایسے رُخ سے اُن پر آیا جدھر اُن کا خیال بھی نہ گیا تھا۔ اُس نے اُن کے دلوں میں رُعب ڈال دیا۔ نتیجہ یہ ہوا کہ وہ خود اپنے ہاتھوں سے بھی اپنے گھروں کو برباد کر رہے تھے اور مومنوں کے ہاتھوں بھی برباد کروا رہے تھے۔ پس عبرت حاصل کرواے دیدہٴ بینا رکھنے والو! ');
INSERT INTO "ur_maududi" VALUES(59,3,'59:3','اگر اللہ نے اُن کے حق میں جلا وطنی نہ لکھ دی ہوتی تو دنیا ہی میں وہ انہیں عذاب دے ڈالتا، اور آخرت میں تو ان کے لیے دوزخ کا عذاب ہے ہی');
INSERT INTO "ur_maududi" VALUES(59,4,'59:4','یہ سب کچھ اس لیے ہوا کہ انہوں نے اللہ اور اس کے رسول کا مقابلہ کیا، اور جو بھی اللہ کا مقابلہ کرے اللہ اس کو سزا دینے میں بہت سخت ہے');
INSERT INTO "ur_maududi" VALUES(59,5,'59:5','تم لوگوں نے کھجوروں کے جو درخت کاٹے یاجن کو اپنی جڑوں پر کھڑا رہنے دیا، یہ سب اللہ ہی کے اذن سے تھا۔ اور (اللہ نے یہ اذن اس لیے دیا) تاکہ فاسقوں کو ذلیل و خوار کرے۔ ');
INSERT INTO "ur_maududi" VALUES(59,6,'59:6','اور جو مال اللہ نے اُن کے قبضے سے نکال کر اپنے رسُول کی طرف پلٹا دیے، وہ ایسے مال نہیں ہیں جن پر تم نے اپنے گھوڑے اور اُونٹ دوڑائے ہوں، بلکہ اللہ اپنے رسُولوں کو جس پر چاہتا ہےتسلّط فرما دیتا ہے، اور اللہ ہر چیز پر قادر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(59,7,'59:7','جو کچھ بھی اللہ اِن بستیوں کے لوگوں سے اپنے رسُول کی طرف پلٹا دے وہ اللہ اور رسُول اور رشتہ داروں اور یتامٰی اور مساکین اور مسافروں کے لیے ہے تاکہ وہ تمہارے مالداروں ہی کے درمیان گردش نہ کرتا رہے، جو کچھ رسُولؐ تمہیں دے وہ لے لو اور جس چیز سے وہ تم کو روک دے اس سے رُک جاؤ۔ اللہ سے ڈرو، اللہ سخت سزا دینے والا ہے');
INSERT INTO "ur_maududi" VALUES(59,8,'59:8','(نیز وہ مال) اُن غریب مہا جرین کےلیے ہے جو اپنے گھروں اور جائیدادوں سے نکال باہر کیے گئے ہیں۔ یہ لوگ اللہ کا فضل اور اس کی خوشنودی چاہتے ہیں اور اللہ اور اُس کے رسُولؐ کی حمایت پر کمر بستہ رہتے ہیں۔ یہی راستباز لوگ ہیں۔');
INSERT INTO "ur_maududi" VALUES(59,9,'59:9','(اور وہ اُن لوگوں کے لیے بھی ہے)جو اِن مہاجرین کی آمد سے پہلےہی ایمان لاکر دارالہجرت میں مقیم تھے۔ یہ اُن لوگوں سےمحبت کرتے ہیں جو ہجرت کرکے اُن کے پاس آئے ہیں اور جو کچھ بھی اُن کو دیدیا جائے اُس کی کوئی حاجت تک یہ اپنے دلوں میں محسوس نہیں کرتے اور اپنی ذات پر دُوسروں کو تر جیح دیتے ہیں خواہ اپنی جگہ خود محتاج ہوں۔ حقیقت یہ ہے کہ جو لوگ اپنے دل کی تنگی سے بچالیے گئے۔ وہی فلاح پانے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(59,10,'59:10','(اور وہ اُن لوگوں کے لیے بھی ہے) جو اِن اَگلوں کے بعد آئے ہیں، جو کہتے ہیں کہ”اے ہمارے رب ، ہمیں اور ہمارے اُن سب بھائیوں کو بخش دے جو ہم سےپہلے ایمان لائے ہیں اورہمارے دلوں میں اہلِ ایمان کے لیےکوئی بغض نہ رکھ، اے ہمارے رب تُو بڑا مہربان اور رحیم ہے“۔ ');
INSERT INTO "ur_maududi" VALUES(59,11,'59:11',' تم نے دیکھا نہیں اُن لوگوں کو جنہوں نے منافقت کی روش اختیار کی ہے؟ یہ اپنے کافر اہلِ کتاب بھائیوں سے کہتے ہیں”اگر تمہیں نکالا گیا تو ہم تمہارےساتھ نکلیں گے، اور تمہارے معاملہ میں ہم کسی کی بات ہرگز نہ مانیں گے، اور اگر تم سے جنگ کی گئی تو ہم تمہاری مدد کریں گے۔“ مگر اللہ گواہ ہے کہ یہ لوگ قطعی جُھوٹے ہیں');
INSERT INTO "ur_maududi" VALUES(59,12,'59:12','اگر وہ نکالے گئے تو یہ ان کے ساتھ ہرگز نہ نکلیں گے، اور اگر اُن سے جنگ کی گئی تو یہ اُن کی ہرگز مدد نہ کریں گے، اور اگر یہ ان کی مدد کریں بھی تو پیٹھ پھیر جائیں گے اور پھر کہیں سے کوئی مدد نہ پائیں گے');
INSERT INTO "ur_maududi" VALUES(59,13,'59:13','اِن کے دلوں میں اللہ سے بڑھ کر تمہارا خوف ہے، اس لیے کہ یہ ایسے لوگ ہیں جو سمجھ بُوجھ نہیں رکھتے۔ ');
INSERT INTO "ur_maududi" VALUES(59,14,'59:14','یہ کبھی اکٹھے ہو کر (کُھلے میدان میں) تمہارا مقابلہ نہ کریں گے، لڑیں گے بھی تو قلعہ بند بستیوں میں بیٹھ کر یا دیواروں کےپیچھے چُھپ کر۔ یہ آپس کی مخالفت میں بڑے سخت ہیں۔ تم اِنہیں اکٹھا سمجھتے ہو مگر ان کے دل ایک دُوسرے سے پھٹے ہوئے ہیں۔ اِن کا یہ حال اِس لیے ہے کہ یہ بے عقل لوگ ہیں');
INSERT INTO "ur_maududi" VALUES(59,15,'59:15','یہ اُنہی لوگوں کے مانند ہیں جو اِن سے تھوڑی ہی مدّت پہلے اپنے کیے کا مزا چکھ چکے ہیں اور اِن کے لیے درد ناک عذاب ہے');
INSERT INTO "ur_maududi" VALUES(59,16,'59:16','اِن کی مثال شیطان کی سی ہے کہ پہلے وہ انسان سے کہتا ہے کہ کفر کر ، اور جب انسان کفر کر بیٹھتا ہے تو وہ کہتا ہے کہ میں تجھ سے بَرِیُّ الذِّمّہ ہوں، مجھے توا للہ ربّ العالمین سے ڈر لگتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(59,17,'59:17','پھر دونوں کا انجام یہ ہونا ہے کہ ہمیشہ کے لیے جہنم میں جائیں، اور ظالموں کی یہی جزا ہے');
INSERT INTO "ur_maududi" VALUES(59,18,'59:18',' اے لوگو جو ایمان لائے ہو، اللہ سے ڈرو، اور ہر شخص یہ دیکھے کہ اُس نے کل کے لیے کیا سامان کیا ہے۔ اللہ سے ڈرتے رہو، اللہ یقیناً تمہارے اُن سب اعمال سے باخبر ہے جو تم کرتے ہو');
INSERT INTO "ur_maududi" VALUES(59,19,'59:19','اُن لوگوں کی طرح نہ ہو جاؤ جو اللہ کو بُھول گئے تو اللہ نے اُنہیں خود اپنا نفس بُھلا دیا، یہی لوگ فاسق ہیں');
INSERT INTO "ur_maududi" VALUES(59,20,'59:20','دوزخ میں جانے والے اور جنت میں جانے والے کبھی یکساں نہیں ہو سکتے جنت میں جانے والے ہی اصل میں کامیاب ہیں');
INSERT INTO "ur_maududi" VALUES(59,21,'59:21','اگر ہم نے یہ قرآن کسی پہاڑ پر بھی اُتار دیا ہوتا تو تم دیکھتے کہ وہ اللہ کے خوف سے دبا جارہا ہے اور پھٹا پڑتا ہے۔ یہ مثالیں ہم لوگوں کے سامنے اس لیے بیان کرتے ہیں کہ وہ (اپنی حالت پر)غور کریں۔');
INSERT INTO "ur_maududi" VALUES(59,22,'59:22',' وہ اللہ ہی ہے جس کے سوا کوئی معبود نہیں، غائب اور ظاہر ہر چیز کا جاننے والا، وہی رحمٰن اور رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(59,23,'59:23','وہ اللہ ہی ہے جس کے سوا کوئی معبود نہیں۔ وہ بادشاہ ہے نہایت مقدّس، سراسر سلامتی، امن دینے والا، نگہبان، سب پر غالب، اپنا حکم بزور نافذ کرنے والا اور وہ بڑا ہی ہو کر رہنے والا۔ پاک ہے اللہ اُس شرک سے جو لوگ کر رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(59,24,'59:24','وہ اللہ ہی ہے جو تخلیق کا منصوبہ بنانے والا اور اس کو نافذ کرنے والا اور اس کے مطابق صورت گری کرنے والا ہے۔ اُس کے لیے بہترین نام ہیں۔ ہر چیز جو آسمانوں اور زمین میں ہے اُس کی تسبیح کر رہی ہے، اور وہ زبردست اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(60,1,'60:1',' اے لوگو جو ایمان لائے ہو، اگر تم میری راہ میں جہاد کرنے کے لیے اور میری رضا جوئی کی خاطر (وطن چھوڑ کر گھروں سے)نکلے ہو تو میرے اور اپنے دشمنوں کو دوست نہ بناؤ۔تم اُن کے ساتھ دوستی کی طرح ڈالتے ہو، حالانکہ جو حق تمہارے پاس آیا ہے اُس کو ماننے سے وہ انکار کر چکے ہیں اور اُن کی روش یہ ہے کہ رسول کو اور خود تم کو صرف اِس قصُور پر جلا وطن کرتے ہیں کہ تم اپنے رب، اللہ پر ایمان لائے ہو۔ تم چُھپا کر اُن کو دوستانہ پیغام بھیجتے ہو، حالانکہ جو کچھ تم چھپا کر کرتے ہو اور جو اعلانیہ کرتے ہو، ہر چیز کو میں خوب جانتا ہوں۔ جو شخص بھی تم میں سے ایساکرے وہ یقیناً راہِ راست سے بھٹک گیا');
INSERT INTO "ur_maududi" VALUES(60,2,'60:2','اُن کا رویّہ تو یہ ہے کہ اگر تم پر قابو پا جائیں تو تمہارے ساتھ دشمنی کریں اور ہاتھ اور زبان سے تمہیں آزار دیں۔ وہ تو یہ چاہتے ہیں کہ تم کسی طرح کافر ہو جاؤ۔ ');
INSERT INTO "ur_maududi" VALUES(60,3,'60:3','قیامت کے دن نہ تمہاری رشتہ داریاں کسی کام آئیں گی نہ تمہاری اولاد۔ اُس روز اللہ تمہارے درمیان جدائی ڈال دے گا، اور وہی تمہارے اعمال کا دیکھنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(60,4,'60:4','تم لوگوں کے لیے ابراہیمؑ اور اُس کے ساتھیوں میں ایک اچھا نمونہ ہے کہ انہوں نے اپنی قوم سے صاف کہہ دیا”ہم تم سے اور تمہارے اِن معبودوں سے جن کو تم خدا کو چھوڑ کر پُوجتے ہو قطعی بیزار ہیں، ہم نے تم سے کفر کیا اور ہمارے اور تمہارے درمیان ہمیشہ کے لیے عداوت ہوگئی اور بیر پڑگیا جب تک تم اللہ واحد پر ایمان نہ لاؤ۔“مگر ابراہیمؑ کا اپنے باپ سے یہ کہنا (اِس سے مستثنٰی ہے) کہ ”میں آپ کے لیے مغفرت کی درخواست ضرور کروں گا، اور اللہ سے آپ کے لیے کچھ حاصل کر لینا میرے بس میں نہیں ہے“ (اور ابراہیمؑ واصحابِ ابراہیمؑ کی دعا یہ تھی کہ)”اے ہمارے رب، تیرے ہی اوپر ہم نے بھروسہ کیا اور تیری ہی طرف ہم نے رجوع کر لیا اور تیرے ہی حضور ہمیں پلٹنا ہے');
INSERT INTO "ur_maududi" VALUES(60,5,'60:5','اے ہمارے رب، ہمیں کافروں کے لیے فتنہ نہ بنادے۔ اور اے ہمارے رب، ہمارے قصُوروں سے درگزر فرما، بے شک تُو ہی زبردست اور دانا ہے۔“');
INSERT INTO "ur_maududi" VALUES(60,6,'60:6','انہی لوگوں کے طرز عمل میں تمہارے لیے اور ہر اُس شخص کے لیے اچّھا نمونہ ہے جواللہ اور روزِآخر کا امید وار ہو۔ اِس سےکوئی منحرف ہو تو اللہ بے نیاز اور اپنی ذات میں آپ محمُود ہے۔ ');
INSERT INTO "ur_maududi" VALUES(60,7,'60:7','بعید نہیں کہ اللہ کبھی تمہارے اور اُن لوگوں کے درمیان محبت ڈال دے جن سے آج تم نے دشمنی مول لی ہے۔ اللہ بڑی قدرت رکھتا ہے اور وہ غفور و رحیم ہے');
INSERT INTO "ur_maududi" VALUES(60,8,'60:8','اللہ تمہیں اس بات سے نہیں روکتا کہ تم ان لوگوں کے ساتھ نیکی اور انصاف کا برتاؤ کرو جنہوں نے دین کے معاملہ میں تم سے جنگ نہیں کی ہےاور تمہیں تمہارے گھروں سے نہیں نکالا ہے۔ اللہ انصاف کرنے والوں کو پسند کرتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(60,9,'60:9','وہ تمہیں جس بات سے روکتا ہے وہ تو یہ ہے کہ تم اُن لوگوں سے دوستی کر و جنہوں نے تم سے دین کے معاملہ میں جنگ کی ہے اور تمہیں تمہارے گھروں سے نکالا ہے اور تمہارے اِخراج میں ایک دُوسرے کی مدد کی ہے۔ اُن سے جو لوگ دوستی کریں وہی ظالم ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(60,10,'60:10','اے لوگو جو ایمان لائے ہو ، جب عورتیں ہجرت کرکے تمہارے پاس آئیں تو (ان کےمومن ہونے کی) جانچ پڑتا ل کرلو، اور ان کےایمان کی حقیقت اللہ ہی بہترجانتا ہے۔ پھر جب تمہیں معلوم ہو جائے کہ وہ مومن ہیں تو انہیں کفار کی طرف واپس نہ کرو۔ نہ وہ کفار کے لیے حلال ہیں اور نہ کفّار ان کے لیے حلال۔ ان کے کافر شوہروں نے جو مہر اُن کو دیے تھے وہ انہیں پھیردو۔اور ان سے نکاح کر لینے میں تم پر کوئی گناہ نہیں جبکہ تم اُن کےمہر اُن کو ادا کردو۔ اور تم خود بھی کافر عورتوں کو اپنے نکاح میں روکے نہ رہو۔جو مہر تم نے اپنی کافر بیویوں کو دیے تھے وہ تم واپس مانگ لو اور جو مہر کافروں نے اپنی مسلمان بیویوں کو دیے تھے انہیں وہ واپس مانگ لیں۔ یہ اللہ کاحکم ہے، وہ تمہارے درمیان فیصلہ کرتا ہے اور اللہ علیم وحکیم ہے');
INSERT INTO "ur_maududi" VALUES(60,11,'60:11','اور اگر تمہاری کافر بیویوں کےمہروں میں سے کچھ تمہیں کفّار سے واپس نہ ملے اور پھر تمہاری نوبت آئےتو جن لوگوں کی بیویاں اُدھررہ گئی ہیں اُن کو اتنی رقم ادا کرو جو اُن سےدیےہوئے مہروں کے برابر ہو۔ اُس خدا سے ڈرتے رہو جس پر تم ایمان لائے ہو');
INSERT INTO "ur_maududi" VALUES(60,12,'60:12','اے نبی ؐ ، جب تمہارے پاس مومن عورتیں بیعت کرنے کے لیے آئیں اور اس بات کا عہد کریں کہ وہ اللہ کے ساتھ کسی کو شریک نہ کریں گی ، چوری نہ کریں گی، زنا نہ کریں گی، اپنی اولادکو قتل نہ کریں گی، اپنے ہاتھ پاؤں کے آگے کوئی بہتان گھڑ کر نہ لائیں گی، اور کسی امرِ معروف میں تمہاری نافرمانی نہ کریں گی، تو ان سے بیعت لے لو اور اُن کے حق میں اللہ سے دعا ئے مغفرت کرو، یقیناً اللہ درگزر فرمانے والا اور رحم کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(60,13,'60:13','اے لوگو جو ایمان لائے ہو ، اُن لوگوں کو دوست نہ بناؤ جن پر اللہ نے غضب فرمایا ہے، جو آخرت سے اسی طرح مایوس ہیں جس طرح قبروں میں پڑے ہوئے کافر مایوس ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(61,1,'61:1','اللہ کی تسبیح کی ہے ہراُس چیز نے جو آسمانوں اور زمین میں ہے، اور وہ غالب اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(61,2,'61:2','اے لوگو جو ایمان لائے ہو، تم کیوں وہ بات کہتے ہو جو کرتے نہیں ہو؟');
INSERT INTO "ur_maududi" VALUES(61,3,'61:3','اللہ کے نزدیک یہ سخت ناپسندیدہ حرکت ہے کہ تم کہو وہ بات جو کرتے نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(61,4,'61:4','اللہ کو تو پسند وہ لوگ ہیں جو اُس کی راہ میں اس طرح صف بستہ ہو کر لڑتے ہیں گویا کہ وہ ایک سیسہ پلائی ہوئی دیوار ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(61,5,'61:5','اور یاد کرو موسٰیؑ کی وہ بات جو اس نے اپنی قوم سے کہی تھی کہ”اے میری قوم کے لوگو ،تم کیوں مجھے اذیّت دیتے ہو حالانکہ تم خوب جانتے ہو کہ میں تمہاری طرف اللہ کا بھیجا ہوا رسول ہوں؟“ پھر جب انہوں نے ٹیڑھ اختیار کی تو اللہ نے ان کے دل ٹیڑھے کر دیے ، اللہ فاسقوں کو ہدایت نہیں دیتا۔ ');
INSERT INTO "ur_maududi" VALUES(61,6,'61:6',' اور یاد کرو عیسٰیؑ ابن مریمؑ کی وہ بات جو اس نے کہی تھی کہ ”اے بنی اسرائیل ، میں تمہاری طرف اللہ کا بھیجا ہوا رسول ہوں، تصدیق کرنے والا ہوں اُس توراة کی جو مجھ سے پہلے آئی ہوئی موجود ہے، اور بشارت دینے والا ہوں ایک رسول کی جو میرے بعد آئے گا جس کا نام احمد ہوگا۔ مگر جب وہ اُن کے پاس کھلی کھلی نشانیاں لے کر آیا تو انہوں نے کہا یہ تو صریح دھوکا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(61,7,'61:7','اب بھلا اُس شخص سے بڑا ظالم اور کون ہوگا جو اللہ پر جُھوٹے بہتان باندھے حالانکہ اسے اسلام (اللہ کے آگے سرِ اطاعت جھکا دینے) کی دعوت دی جا رہی ہو؟ ایسے ظالموں کو اللہ ہدایت نہیں دیا کرتا');
INSERT INTO "ur_maududi" VALUES(61,8,'61:8','یہ لوگ اپنے مُنہ کی پُھونکوں سے اللہ کے نُور کو بجھانا چاہتے ہیں، اور اللہ کا فیصلہ یہ ہے کہ وہ اپنے نُور کو پُورا پھیلا کر رہے گا خواہ کافروں کو یہ کتنا ہی ناگوار ہو۔ ');
INSERT INTO "ur_maududi" VALUES(61,9,'61:9','وہی تو ہے جس نے اپنے رسول کو ہدایت اور دینِ حق کے ساتھ بھیجا ہے تا کہ اسے پُورے کے پُورے دین پر غالب کر دے خواہ مشرکین کو یہ کتنا ہی ناگوار ہو۔ ');
INSERT INTO "ur_maududi" VALUES(61,10,'61:10','اے لوگو جو ایمان لائے ہو، میں بتاؤں تم کو وہ تجارت جو تمہیں عذابِ الیم سے بچا دے؟');
INSERT INTO "ur_maududi" VALUES(61,11,'61:11','ایمان لاؤ اللہ اور اُس کے رسُول پر، اور جہاد کرو اللہ کی راہ میں اپنے مالوں سے اور اپنی جانوں سے۔ یہی تمہارے لیے بہتر ہے اگر تم جانو۔ ');
INSERT INTO "ur_maududi" VALUES(61,12,'61:12','اللہ تمہارے گناہ معاف کردے گا، اور تم کو ایسے باغوں میں داخل کرےگا جن کے نیچے نہریں بہتی ہوں گی، اور ابدی قیام کی جنّتوں میں بہترین گھر تمہیں عطا فرمائے گا۔ یہ ہے بڑی کامیابی۔ ');
INSERT INTO "ur_maududi" VALUES(61,13,'61:13','اور وہ دُوسری چیز جو تم چاہتے ہو وہ بھی تمہیں دے گا، اللہ کی طرف سے نُصرت اور قریب ہی میں حاصل ہو جانے والی فتح۔ اے نبیؐ ، اہل ایمان کو اِس کی بشارت دے دو');
INSERT INTO "ur_maududi" VALUES(61,14,'61:14','اے لوگو جو ایمان لائے ہو، اللہ کے مددگار بنو، جس طرح عیسٰیؑ ابنِ مریمؑ نے حواریوں کو خطاب کرکے کہا تھا: ”کون ہے اللہ کی طرف (بلانے) میں میرا مددگار“؟ اور حواریوں نے جواب دیا تھا:”ہم ہیں اللہ کے مددگار“ ۔ اُس وقت بنی اسرائیل کا ایک گروہ ایمان لایا اور دُوسرے گروہ نے انکار کیا۔ پھر ہم نے ایمان لانے والوں کی اُن کے دُشمنوں کےمقابلے میں تائید کی اور وہی غالب ہو کر رہے۔ ');
INSERT INTO "ur_maududi" VALUES(62,1,'62:1','اللہ کی تسبیح کر رہی ہے ہر وہ چیز جو آسمانوں میں ہے اور ہر وہ چیز جو زمین میں ہے۔۔۔۔ بادشاہ ہے، قُدُّوس ہے، زبردست اور حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(62,2,'62:2','وُہی ہےجس نے اُمّیوں کے اندر ایک رسُول خود اُنہی میں سے اُٹھایا ، جو اُنہیں اُس کی آیات سناتا ہے، اُن کی زندگی سنوارتا ہے، اور اُن کو کتاب اور حکمت کی تعلیم دیتا ہے۔ حالانکہ اِس سے پہلے وہ کُھلی گمراہی میں پڑے ہوئے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(62,3,'62:3','اور (اس رسُول کی بعثت) اُن دوسرے لوگوں کے لیے بھی ہے جو ابھی اُن سے نہیں ملے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(62,4,'62:4','اللہ زبردست اور حکیم ہے۔ یہ اس کا فضل ہے ، جسے چاہتا ہے دیتا ہے، اور وہ بڑا فضل فرمانے والا ہے');
INSERT INTO "ur_maududi" VALUES(62,5,'62:5','جن لوگوں کو توراة کا حامل بنایا گیا تھا مگر انہوں نے اس کا بار نہ اُٹھایا، اُن کی مثال اُس گدھے کی سی ہے جس پر کتابیں لدی ہوئی ہوں۔ اِس سے بھی زیادہ بُری مثال ہےاُن لوگوں کی جنہوں نے اللہ کی آیات کو جُھٹلا دیا ہے۔ ایسے ظالموں کو اللہ ہدایت نہیں دیا کرتا');
INSERT INTO "ur_maududi" VALUES(62,6,'62:6','اِن سےکہو، ”اے لوگو جو یہودی بن گئے ہو، اگر تمہیں یہ گھمنڈ ہے کہ باقی سب لوگوں کو چھوڑکر بس تم ہی اللہ کے چہیتے ہو تو موت کے تمنّا کرو اگر تم اپنے اِس زَعم میں سچے ہو“۔ ');
INSERT INTO "ur_maududi" VALUES(62,7,'62:7','لیکن یہ ہر گز اس کی تمنّا نہ کریں گے اپنے کرتُوتوں کی وجہ سے جو یہ کر چکے ہیں، اور اللہ اِن ظالموں کوخُوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(62,8,'62:8','اِن سے کہو، "جس موت سے تم بھاگتے ہو وہ تو تمہیں آ کر رہے گی پھر تم اس کے سامنے پیش کیے جاؤ گے جو پوشیدہ و ظاہر کا جاننے والا ہے، اور وہ تمہیں بتا دے گا کہ تم کیا کچھ کرتے رہے ہو');
INSERT INTO "ur_maududi" VALUES(62,9,'62:9','اے لوگو جو ایمان لائے ہو، جب پکارا جائے نماز کے لیے جمعہ کے دن تو اللہ کے ذکر کی طرف دوڑو اور خریدو فروخت چھوڑ دو، یہ تمہارے لیے زیادہ بہتر ہے اگر تم جانو');
INSERT INTO "ur_maududi" VALUES(62,10,'62:10','پھر جب نماز پوری ہو جائے تو زمین میں پھیل جاؤ اور اللہ کا فضل تلاش کرو۔ اور اللہ کو کثرت سے یاد کرتے رہو، شاید کہ تمہیں فلاح نصیب ہو جائے۔ ');
INSERT INTO "ur_maududi" VALUES(62,11,'62:11','اور جب انہوں نے تجارت اور کھیل تماشا ہوتے دیکھا تو اس کی طرف لپک گئے اور تمہیں کھڑا چھوڑ دیا۔ ان سے کہو، جو کچھ اللہ کے پاس ہے وہ کھیل تماشے اور تجارت سے بہتر ہے۔ اور اللہ سب سے بہتر رزق دینے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(63,1,'63:1','اے نبیؐ ، جب یہ منافق تمہارے پاس آتے ہیں تو کہتے ہیں،”ہم گواہی دیتے ہیں کہ آپ یقیناً اللہ کے رسُول ہیں“۔ ہاں، اللہ جانتا ہے کہ تم ضرور اُس کے رسُول ہو، مگر اللہ گواہی دیتا ہے کہ یہ منافق قطعی جُھوٹے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(63,2,'63:2','انہوں نے اپنی قَسموں کو ڈھال بنا رکھا ہے اور اِس طرح یہ اللہ کے راستے سےخود رُکتے اور دنیا کو روکتے ہیں۔ کیسی بُری حرکتیں ہیں جو یہ لوگ کر رہے ہیں');
INSERT INTO "ur_maududi" VALUES(63,3,'63:3','کیسی بُری حرکتیں ہیں جو یہ لوگ کر رہے ہیں۔ یہ سب کچھ اس وجہ سے ہے کہ ان لوگوں نے ایمان لا کر پھر کفر کیا اس لیے ان کے دلوں پر مہر لگا دی گئی، اب یہ کچھ نہیں سمجھتے۔ ');
INSERT INTO "ur_maududi" VALUES(63,4,'63:4','اِنہیں دیکھو تو اِن کے جُثّے تمہیں بڑے شاندار نظر آئیں۔ بولیں تو تم ان کی باتیں سُنتے رہ جاؤ۔ مگر اصل میں یہ گویا لکڑی کے کُندے ہیں جو دیوار کے ساتھ چُن کر رکھ دیے گئے ہوں۔ ہر روز کی آواز کو یہ اپنے خلاف سمجھتے ہیں۔ یہ پکّے دشمن ہیں، ان سے بچ کر رہو، اللہ کی مار ان پر، یہ کدھر اُلٹے پِھر ائے جا رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(63,5,'63:5','اور جب اِن سے کہا جاتا ہے کہ آؤ تاکہ اللہ کا رسُول تمہارے لیے مغفرت کی دُعا کرے ، تو سر جھٹکتے ہیں اور تم دیکھتے ہو کہ وہ بڑے گھمنڈ کے ساتھ آنے سے رُکتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(63,6,'63:6','اے نبیؐ ، تم چاہے ان کے لیے مغفرت کی دُعا کرو یا نہ کرو، ان کے لیے یکساں ہے، اللہ ہر گز انہیں معاف نہ کرے گا، اللہ فاسق لوگوں کو ہر گز ہدایت نہیں دیتا۔ ');
INSERT INTO "ur_maududi" VALUES(63,7,'63:7','یہ وہی لوگ ہیں جو کہتے ہیں کہ رسول کے ساتھیوں پر خرچ کرنا بند کر دو تاکہ یہ منتشر ہو جائیں حالانکہ زمین اور آسمانوں کے خزانوں کا مالک اللہ ہی ہے، مگر یہ منافق سمجھتے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(63,8,'63:8','یہ کہتے ہیں کہ ہم مدینے واپس پہنچ جائیں تو جو عزّت والا ہے وہ ذلیل کو وہاں سے نکال باہر کرے گا۔ حالانکہ عزّت تو اللہ اور اُس کے رسُولؐ اور مومنین کے لیے ہے، مگر یہ منافق جانتے نہیں ہیں');
INSERT INTO "ur_maududi" VALUES(63,9,'63:9',' اے لوگو جو ایمان لائے ہو، تمہارے مال اور تمہاری اولادیں تم کو اللہ کی یاد سے غافل نہ کردیں۔ جو لوگ ایسا کریں وہی خسارے میں رہنے والے ہیں');
INSERT INTO "ur_maududi" VALUES(63,10,'63:10','جو رزق ہم نے تمہیں دیا ہے اس میں سے خرچ کرو قبل اس کے کہ تم میں سے کسی کی موت کا وقت آ جائے اور اُس وقت وہ کہے کہ "اے میرے رب، کیوں نہ تو نے مجھے تھوڑی سی مہلت اور دے دی کہ میں صدقہ دیتا اور صالح لوگوں میں شامل ہو جاتا"');
INSERT INTO "ur_maududi" VALUES(63,11,'63:11','حالانکہ جب کسی کی مہلت عمل پوری ہونے کا وقت آ جاتا ہے تو اللہ اُس کو ہرگز مزید مہلت نہیں دیتا، اور جو کچھ تم کرتے ہو، اللہ اس سے باخبر ہے');
INSERT INTO "ur_maududi" VALUES(64,1,'64:1','اللہ کی تسبیح کر رہی ہے ہر وہ چیز جو آسمانوں میں ہے اور ہر وہ چیز جو زمین میں ہے۔ اُسی کی بادشاہی ہے اور اُسی کے لیے تعریف ہے اور وہ ہر چیز پر قادر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(64,2,'64:2','وہی ہے جس نے تم کو پیدا کیا ،پھر تم میں سے کوئی کافر ہے اور کوئی مومن، اور اللہ وہ سب کچھ دیکھ رہا ہے جو تم کرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(64,3,'64:3','اُس نے زمین اور آسمانوں کو برحق پیدا کیا ہے، اور تمہاری صورت بنائی اور بڑی عمدہ بنائی ہے، اور اُسی کی طرف آخر کار تمہیں پلٹنا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(64,4,'64:4','زمین اور آسمانوں کی ہر چیز کا اُسے علم ہے۔ جو کچھ تُم چُھپاتے ہو اور جو کچھ تم ظاہر کرتے ہو سب اُس کو معلوم ہے، اور وہ دلوں کا حال تک جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(64,5,'64:5','کیا تمہیں اُن لوگوں کی کوئی خبر نہیں پہنچی جنہوں نے اِس سے پہلے کُفر کیا اور پھر اپنی شامتِ اعمال کا مزہ چکھ لیا؟ اور آگے اُن کے لیے ایک درد ناک عذاب ہے۔ ');
INSERT INTO "ur_maududi" VALUES(64,6,'64:6','اِس انجام کے مستحق وہ اس لیے ہوئے کہ اُن کے پاس اُن کے رسوُل کُھلی کُھلی دلیلیں اور نشانیاں لے کر آتے رہے، مگر اُنہوں نے کہا”کیا انسان ہمیں ہدایت دیں گے“؟ اس طرح انہوں نے ماننے سے انکار کر دیا اور منہ پھیر لیا، تب اللہ بھی ان سے بے پرواہ ہو گیا اور اللہ تو ہے ہی بے نیاز اور اپنی ذات میں آپ محمود۔ ');
INSERT INTO "ur_maududi" VALUES(64,7,'64:7','مُنکرین نے بڑے دعوے سے کہا ہے کہ وہ مرنے کے بعد ہر گز دوبارہ نہ اُٹھائے جائیں گے۔ ان سے کہو”نہیں، میرےرب کی قسم تم ضرور اُٹھائے جاؤ گے، پھر ضرور تمہیں بتایا جائے گا کہ تم نے (دنیا میں)کیا کچھ کیا ہے، اور ایسا کرنا اللہ کے لیے بہت آسان ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(64,8,'64:8','پس ایمان لاؤ اللہ پر، اور اُس کے رسُول پر ، اور اُس روشنی پر جو ہم نے نازل کی ہے۔ جو کچھ تم کرتے ہو اللہ اُس سے باخبر ہے۔');
INSERT INTO "ur_maududi" VALUES(64,9,'64:9','(اِس کا پتہ تمہیں اُس روز چل جائے گا) جب اجتماع کے دن وہ تم سب کواکٹھا کرے گا۔ وہ دن ہوگا ایک دُوسرے کے مقابلے میں لوگوں کی ہار جیت کا۔ جو اللہ پر ایمان لایاہے اور نیک عمل کرتا ہے، اللہ اُس کے گناہ جھاڑ دے گا اور اُسے ایسی جنّتوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی۔ یہ لوگ ہمیشہ ہمیشہ ان میں رہیں گے۔ یہی بڑی کامیابی ہے');
INSERT INTO "ur_maududi" VALUES(64,10,'64:10','اور جن لوگوں نے کُفر کیا ہے اور ہماری آیات کو جُھٹلایا ہے وہ دوزخ کے باشندے ہوں گے جس میں وہ ہمیشہ رہیں گے اور وہ بد ترین ٹھکانہ ہے');
INSERT INTO "ur_maududi" VALUES(64,11,'64:11',' کوئی مصیبت کبھی نہیں آتی مگر اللہ کے اِذن ہی سے آتی ہے۔ جو شخص اللہ پر ایمان رکھتا ہو اللہ اس کے دل کو ہدایت بخشتا ہے، اور اللہ کو ہر چیز کا علم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(64,12,'64:12','اللہ کی اطاعت کرو اوررسُول کی طاعت کرو۔ لیکن اگر تم اطاعت سےمُنہ موڑتے ہو تو ہمارے رسُول پر صاف صاف حق پہنچادینے کے سوا کوئی ذمہ داری نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(64,13,'64:13','اللہ وہ ہے جس کے سوا کوئی خدا نہیں، لہٰذا ایمان لانے والوں کو اللہ ہی پر بھروسہ رکھنا چاہیے۔ ');
INSERT INTO "ur_maududi" VALUES(64,14,'64:14','اے لوگو جو ایمان لائے ہو، تمہاری بیویوں اور تمہاری اولاد میں سے بعض تمہارے دشمن ہیں، ان سے ہوشیار رہو۔ اور اگر تم عفوودرگزر سے کام لو اور معاف کردو تو اللہ غفور و رحیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(64,15,'64:15','تمہارے مال اور تمہاری اولاد تو ایک آزمائش ہیں، اور اللہ ہی ہے جس کے پاس بڑا اجر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(64,16,'64:16','لہٰذا جہاں تک تمہارے بس میں ہو اللہ سے ڈرتے رہو، اور سنو اور اطاعت کرو، اوراپنے مال خرچ کرو، یہ تمہارے ہی لیے بہتر ہے۔ جو اپنے دل کی تنگی سے محفوظ رہ گئے بس وہی فلاح پانے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(64,17,'64:17','اگر تم اللہ کو قرضِ حَسَن دو تو وہ تمہیں کئی گُنا بڑھا کر دے گا اور تمہارے قصوروں سے در گزر فرمائے گا، اللہ بڑا قدردان اور بردبار ہے، ');
INSERT INTO "ur_maududi" VALUES(64,18,'64:18','حاضر اور غائب ہر چیز کو جانتا ہے، زبردست اور دانا ہے');
INSERT INTO "ur_maududi" VALUES(65,1,'65:1','اے نبیؐ ، جب تم لوگ عورتوں کو طلاق دو تو اُنہیں اُن کی عدّت کے لیے طلاق دیا کرو۔ اور عدّت کے زمانے کا ٹھیک ٹھیک شمار کرو، اور اللہ سے ڈرو جو تمہارا رب ہے۔ (زمانہ عدّت میں) نہ تم اُنہیں اُن کے گھروں سے نکالو اور نہ وہ خود نکلیں، الّا یہ کہ وہ کسی صریح بُرائی کی مرتکب ہوں۔ یہ اللہ کی مقرر کر دہ حدیں ہیں، اور جو کوئی اللہ کی حدوں سے تجاوز کریگا وہ اپنے اوپر خود ظلم کریگا۔ تم نہیں جانتے، شاید اس کے بعد اللہ (موافقت کی) کوئی صُورت پیدا کر دے۔ ');
INSERT INTO "ur_maududi" VALUES(65,2,'65:2','پھر جب وہ اپنی (عدّت کی) مدّت کے خاتمے پر پہنچیں تو یا انہیں بھلے طریقے سے (اپنے نکاح میں) روک رکھو، یا بَھلے طریقے پر اُن سے جُدا ہو جاؤ۔ اور دو ایسے آدمیوں کو گواہ بنالو جو تم میں سے صاحبِ عدل ہوں۔ اور (اے گواہ بننے والو) گواہی ٹھیک ٹھیک اللہ کے لیے ادا کرو۔یہ باتیں ہیں جن کی تم لوگوں کو نصیحت کی جاتی ہے، ہر اُس شخص کو جو اللہ اور آخرت کےدن پر ایمان رکھتا ہو۔ جو کوئی اللہ سے ڈرتے ہوئے کام کرے گا اللہ اُس کے لیے مشکلات سے نکلنے کا کوئی راستہ پیدا کر دے گا ');
INSERT INTO "ur_maududi" VALUES(65,3,'65:3','اور اُسے ایسے راستے سے رزق دے گا جدھر اُس کا گمان بھی نہ جاتا ہو۔ جو اللہ پر بھروسہ کرے اُس کےلیے وہ کافی ہے۔ اللہ اپنا کام پورا کرکے رہتا ہے۔ اللہ نے ہر چیز کے لیے ایک تقدیر مقرر کر رکھی ہے');
INSERT INTO "ur_maududi" VALUES(65,4,'65:4','اور تمہاری عورتوں میں سے جو حیض سے مایوس ہو چکی ہوں ان کے معاملہ میں اگر تم لوگوں کو کوئی شک لاحق ہے تو (تمہیں معلوم ہو کہ) ان کی عدّت تین مہینے ہے۔ اور یہی حکم اُن کا ہے جنہیں ابھی حیض نہ آیا ہو۔ اور حاملہ عورتوں کی عدّت کی حد یہ ہے کہ اُن کا وضعِ حمل ہو جائے۔ جو شخص اللہ سے ڈرے اُس کے معاملہ میں وہ سہولت پیدا کردیتاہے');
INSERT INTO "ur_maududi" VALUES(65,5,'65:5','یہ اللہ کا حکم ہے جواُس نے تمہاری طرف نازل کیا ہے۔ جو اللہ سے ڈرے گا اللہ اُس کی بُرائیوں کو اُس سے دُور کر دے گا اور اُس کو بڑا اجر دے گا۔ ');
INSERT INTO "ur_maududi" VALUES(65,6,'65:6','اُن کو (زمانہ عدّت میں) اُسی جگہ رکھو جہاں تم رہتے ہو، جیسی کچھ بھی جگہ تمہیں میسّر ہو،اور انہیں تنگ کرنے کے لیے ان کو نہ ستاؤ۔ اور اگر وہ حاملہ ہوں تو ان پر اُس وقت تک خرچ کرتے رہو جب تک ان کا وضع حمل نہ ہوجائے۔ پھر اگر وہ تمہارے لیے (بچےکو) دُودھ پلائیں تو ان کی اُجرت انہیں دو، اور بھلے طریقےسے (اُجرت کا معاملہ) باہمی گفت وشنید سے طے کر لو۔ لیکن اگر تم نے (اُجرت طے کرنے میں) ایک دُوسرے کو تنگ کیا تو بچے کو کوئی اور عورت دُودھ پلالے گی۔ ');
INSERT INTO "ur_maududi" VALUES(65,7,'65:7','خوشحال آدمی اپنی خوشحالی کے مطابق نفقہ دے، اور جس کو رزق کم دیا گیا ہو وہ اُسی مال میں سے خرچ کرے جو اللہ نے اسے دیا ہے اللہ نے جس کو جتنا کچھ دیا ہے اس سے زیادہ کا وہ اُسے مکلف نہیں کرتا بعید نہیں کہ اللہ تنگ دستی کے بعد فراخ دستی بھی عطا فرما دے');
INSERT INTO "ur_maududi" VALUES(65,8,'65:8',' کتنی ہی بستیاں ہیں جنہوں نے اپنے رب اور اُس کے رسُولوں کے حکم سے سرتابی کی تو ہم نے اُن سے سخت محاسبہ کیا اور اُن کو بُری طرح سزا دی۔');
INSERT INTO "ur_maududi" VALUES(65,9,'65:9','انہوں نے اپنے کیے کا مزا چکھ لیا اور اُن کا انجام کار گھاٹا ہی گھاٹا ہے');
INSERT INTO "ur_maududi" VALUES(65,10,'65:10','اللہ نے (آخرت میں) ان کے لیے سخت عذاب مہیا کر رکھا ہے پس اللہ سے ڈرو اے صاحب عقل لوگو جو ایمان لائے ہو اللہ نے تمہاری طرف ایک نصیحت نازل کر دی ہے');
INSERT INTO "ur_maududi" VALUES(65,11,'65:11','ایک ایسا رسُول جو تم کو اللہ کی صاف صاف ہدایت دینے والی آیات سناتا ہے تاکہ ایمان لانے والوں اور نیک عمل کرنے والوں کو تاریکیوں سے نکال کر روشنی میں لے آئے۔ جو کوئی اللہ پر ایمان لائے اور نیک عمل کرے، اللہ اُسے ایسی جنّتوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی۔ یہ لوگ اُن میں ہمیشہ رہیں گے۔ اللہ نے ایسے شخص کے لیے بہترین رزق رکھا ہے');
INSERT INTO "ur_maududi" VALUES(65,12,'65:12','اللہ وہ ہے جس نے سات آسمان بنائے اور زمین کی قسم سے بھی اُنہی کے مانند۔ ان کے درمیان حکم نازل ہوتا رہتا ہے۔ (یہ بات تمہیں اس لیے بتائی جارہی ہے) تاکہ تم جان لو کہ اللہ ہر چیز پر قدرت رکھتا ہے، اور یہ کہ اللہ کا علم ہر چیز پر محیط ہے');
INSERT INTO "ur_maududi" VALUES(66,1,'66:1','اے نبیؐ ، تم کیوں اُس چیز کو حرام کرتے ہو جو اللہ نے تمہارے لیے حلال کی ہے؟ (کیا اس لیے کہ) تم اپنی بیویوں کی خوشی چاہتے ہو؟ ۔۔۔۔ اللہ معاف کرنے والا اوررحم فرمانے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(66,2,'66:2','اللہ نے تم لوگوں کے لیے اپنی قسموں کی پابندی سے نکلنے کا طریقہ مقرر کر دیا ہے۔ اللہ تمہارا مولیٰ ہے ، اور وہی علیم و حکیم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(66,3,'66:3','(اور یہ معاملہ بھی قابلِ توجہ ہے کہ) نبی نے ایک بات اپنی ایک بیوی سے راز میں کی تھی۔ پھر جب اُس بیوی نے (کسی اور پر) وہ راز ظاہر کر دیا، اور اللہ نے نبی کو اِس (افشائے راز) کی اطلاع دے دی ، تو نبی نے اس پر کسی حد تک (اُس بیوی کو) خبردار کیا اور کسی حد تک اس سے درگزر کیا۔ پھر جب نبی نے اُسے (افشائے راز کی) یہ بات بتائی تو اُس نے پوچھا آپ کو اِس کی کس نے خبر دی ؟ نبی نے کہا”مجھے اُس نے خبر دی جو سب کچھ جانتا ہے اور خوب باخبر ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(66,4,'66:4','اگر تم دونوں اللہ سے توبہ کرتی ہو (تو یہ تمہارے لیے بہتر ہے) کیونکہ تمہارے دل سیدھی راہ سے ہٹ گئے ہیں، اور اگر نبی کے مقابلہ میں تم نے باہم جتھہ بندی کی تو جان رکھو کہ اللہ اُس کا مولیٰ ہے اور اُس کے بعد جبریل اور تمام صالح اہلِ ایمان اور سب ملائکہ اس کے ساتھی اورمدد گار ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(66,5,'66:5','بعید نہیں کہ اگر نبی تم سب بیویوں کو طلاق دے دے تو اللہ اسے ایسی بیویاں تمہارے بدلے میں عطا فرما دے جو تم سے بہتر ہوں، سچی مسلمان، باایمان، اطاعت گزار، توبہ گزار، عبادت گزار، اور روزہ دار، خواہ شوہر دیدہ ہوں یا باکرہ۔');
INSERT INTO "ur_maududi" VALUES(66,6,'66:6','اے لوگو جو ایمان لائے ہو، بچاوٴ اپنے آپ کو اور اپنے اہل و عیال کو اُس آگ سے جس کا ایندھن انسان اور پتھر ہوں گے، جس پر نہایت تند خُو اور سخت گیر فرشتے مقرر ہوں گے جو کبھی اللہ کے حکم کی نافرمانی نہیں کرتے اور جو حکم انہیں دیا جاتا ہے اسے بجا لاتے ہیں ');
INSERT INTO "ur_maududi" VALUES(66,7,'66:7','(اُس وقت کہا جائے گا کہ) اے کافرو، آج معذرتیں پیش نہ کرو، تمہیں تو ویسا ہی بدلہ دیا جارہا ہے جیسے تم عمل کر رہے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(66,8,'66:8','اے لوگو جو ایمان لائے ہو ، اللہ سے توبہ کرو، خالص توبہ ، بعید نہیں کہ اللہ تمہاری برائیاں تم سے دُور کر دے اور تمہیں ایسی جنّتوں میں داخل فرما دے جن کے نیچے نہریں بہ رہی ہوں گی۔ یہ وہ دن ہوگا جب اللہ اپنے نبی کو اور اُن لوگوں کو جو اس کے ساتھ ایمان لائے ہیں رُسوا نہ کرے گا۔ اُن کا نُور اُن کے آگے آگے اور ان کے دائیں جانب دوڑ رہا ہوگا اور وہ کہہ رہے ہوں گے کہ اے ہمارے ربّ، ہمارا نُور ہمارے لیے مکمل کر دے اور ہم سے درگزر فرما، تُو ہر چیز پر قدرت رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(66,9,'66:9','اے نبی ؐ ، کفار اور منافقین سے جہاد کرو اور ان کے ساتھ سختی سے پیش آوٴ۔ ان کا ٹھکانا جہنّم ہے اور وہ بہت بُرا ٹھکانا ہے');
INSERT INTO "ur_maududi" VALUES(66,10,'66:10','اللہ کافروں کے معاملہ میں نوح ؑ اور لُوط ؑ کی بیویوں کو بطور مثال پیش کرتا ہے۔ وہ ہمارے دو صالح بندوں کی زوجیّت میں تھیں ، مگر انہوں نے اپنے ان شوہروں سے خیانت کی اور وہ اللہ کے مقابلہ میں ان کے کچھ بھی نہ کام آسکے۔ دونوں سے کہہ دیا گیا ہے کہ جاوٴ آگ میں جانے والوں کے ساتھ تم بھی چلی جاو');
INSERT INTO "ur_maududi" VALUES(66,11,'66:11','اور اہلِ ایمان کے معاملہ میں اللہ فرعون کی بیوی کی مثال پیش کرتا ہے جبکہ اس نے دعا کی”اے میرے ربّ ، میرے لیے اپنے ہاں جنّت میں ایک گھر بنادے اور مجھے فرعون اور اس کے عمل سے بچا لے اور ظالم قوم سے مجھ کو نجات دے۔“');
INSERT INTO "ur_maududi" VALUES(66,12,'66:12','اور عمران کی بیٹی مریم کی مثال دیتا ہے جس نے اپنی شرمگاہ کی حفاظت کی تھی، پھر ہم نے اس کے اندر اپنی طرف سے رُوح پھُونک دی، اور اُس نے اپنے ربّ کے ارشادات اور اس کی کتابوں کی تصدیق کی اور وہ اطاعت گزار لوگوں میں سے تھی۔ ');
INSERT INTO "ur_maududi" VALUES(67,1,'67:1','نہایت بزرگ و برتر ہے وہ جس کے ہاتھ میں کائنات کی سلطنت ہے، اور وہ ہر چیز پر قدرت رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(67,2,'67:2','جس نے موت اور زندگی کو ایجاد کیا تاکہ تم لوگوں کو آزما کر دیکھے تم میں سے کون بہتر عمل کرنے والا ہے، اور وہ زبردست بھی ہے اور درگزر فرمانے والا بھی۔ ');
INSERT INTO "ur_maududi" VALUES(67,3,'67:3','س نے تہ بر تہ سات آسمان بنائے۔ تم رحمٰن کی تخلیق میں کسی قسم کی بے ربطی نہ پاوٴ گے۔ پھر پلٹ کر دیکھو، کہیں تمہیں کوئی خلل نظر آتا ہے؟ ');
INSERT INTO "ur_maududi" VALUES(67,4,'67:4','بار بار نگاہ دوڑاؤ تمہاری نگاہ تھک کر نامراد پلٹ آئے گی');
INSERT INTO "ur_maududi" VALUES(67,5,'67:5','ہم نے تمہارے قریب کے آسمان کو عظیم الشان چراغوں سے آراستہ کیا ہے اور اُنہیں شیاطین کو مار بھگانے کا ذریعہ بنا دیا ہے۔ اِن شیطانوں کے لیے بھڑکتی ہوئی آگ ہم نے مہیّا کر رکھی ہے');
INSERT INTO "ur_maududi" VALUES(67,6,'67:6','جن لوگوں نے اپنے ربّ سے کفر کیا ہے ان کے لیے جہنّم کا عذاب ہے اور وہ بہت ہی بُرا ٹھکانا ہے');
INSERT INTO "ur_maududi" VALUES(67,7,'67:7','جب وہ اُس میں پھینکے جائیں گے تو اس کے دَھاڑنے کی ہولناک آوازیں سُنیں گے اور وہ جوش کھا رہی ہوگی');
INSERT INTO "ur_maududi" VALUES(67,8,'67:8','شِدّتِ غضب سے پھٹی جاتی ہو گی۔ ہر بار جب کوئی انبوہ اس میں ڈالا جائے گا، اُس کے کارندے اُن لوگوں سے پوچھیں گے”کیا تمہارے پاس کوئی خبردار کرنے والا نہیں آیا تھا؟“ ');
INSERT INTO "ur_maududi" VALUES(67,9,'67:9','وہ جواب دیں گے”ہاں، خبردار کرنے والا ہمارے پاس آیا تھا ، مگر ہم نے اسے جھٹلا دیا اور کہا اللہ نے کچھ بھی نازل نہیں کیا ہے، تم بڑی گمراہی میں پڑے ہوئے ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(67,10,'67:10','اور وہ کہیں گے ”کاش ہم سُنتے اور سمجھتے تو آج اِس بھَڑکتی ہوئی آگ کے سزا واروں میں نہ شامل ہوتے۔“');
INSERT INTO "ur_maududi" VALUES(67,11,'67:11','اس طرح وہ اپنے قصورکا خود اعتراف کر لیں گے، لعنت ہے ان دوزخیوں پر');
INSERT INTO "ur_maududi" VALUES(67,12,'67:12','جو لوگ بے دیکھے اپنے ربّ سے ڈرتے ہیں، یقیناً اُن کے لیے مغفرت ہے اور بڑا اجر۔ ');
INSERT INTO "ur_maududi" VALUES(67,13,'67:13','تم خواہ چُپکے سے بات کرو یا اُونچی آواز سے (اللہ کے لیے یکساں ہے)، وہ تو دلوں کا حال تک جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(67,14,'67:14','کیا وہی نہ جانے گا جس نے پیدا کیا ہے؟ حالانکہ وہ باریک بیں اور باخبر ہے');
INSERT INTO "ur_maududi" VALUES(67,15,'67:15','وہی تو ہے جس نے تمہارے لیے زمین کو تابع کر رکھا ہے ، چلو اُس کی چھاتی پر اور کھاوٴ خدا کا رزق، اُسی کے حضور تمہیں دوبارہ زندہ ہو کر جانا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(67,16,'67:16','کیا تم اِس سے بے خوف ہو کہ وہ جو آسمان میں ہے تمہیں زمین میں دھنسا دے اور یکایک یہ زمین جَھکو لے کھانے لگے؟');
INSERT INTO "ur_maududi" VALUES(67,17,'67:17','کیا تم اِس سے بے خوف ہو کہ وہ جو آسمان میں ہے تم پر پتھراوٴ کرنے والی ہوا بھیج دے؟ پھر تمہیں معلوم ہو جائے گا کہ میری تنبیہ کیسی ہوتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(67,18,'67:18','ن سے پہلے گُزرے ہوئے لوگ جُھٹلا چکے ہیں۔ پھر دیکھ لو میری گرفت کیسی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(67,19,'67:19','کیا یہ لوگ اپنے اُوپر اُڑنے والے پرندوں کو پر پھیلائے اور سکیڑتے نہیں دیکھتے؟ رحمٰن کے سوا کوئی نہیں جو انہیں تھامے ہوئے ہو۔ وہی ہر چیز کا نگہبان ہے۔ ');
INSERT INTO "ur_maududi" VALUES(67,20,'67:20','بتاوٴ، آخر وہ کون سا لشکر تمہارے پاس ہے جو رحمٰن کے مقابلے میں تمہارے مدد کر سکتا ہے؟ حقیقت یہ ہے کہ یہ منکرین دھوکے میں پڑے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(67,21,'67:21','یا پھر بتاؤ، کون ہے جو تمہیں رزق دے سکتا ہے اگر رحمان اپنا رزق روک لے؟ دراصل یہ لوگ سر کشی اور حق سے گریز پر اڑے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(67,22,'67:22','بھلا سوچو، جو شخص منہ اوندھائے چل رہا ہو وہ زیادہ صحیح راہ پانے والا ہے یا وہ جو سر اُٹھائے سیدھا ایک ہموار سڑک پر چل رہا ہو؟');
INSERT INTO "ur_maududi" VALUES(67,23,'67:23','اِن سے کہو اللہ ہی ہے جس نے تمہیں پیدا کیا، تم کو سُننے اور دیکھنے کی طاقتیں دیں اور سوچنے سمجھنے والے دل دیے، مگر تم کم ہی شکر ادا کرتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(67,24,'67:24','اِن سے کہو، اللہ ہی ہے جس نے تمہیں زمین میں پھیلا یا ہے اور اسی کی طرف تم سمیٹے جاوٴ گے۔ ');
INSERT INTO "ur_maududi" VALUES(67,25,'67:25','یہ کہتے ہیں”اگر تم سچے ہو تو بتاوٴ یہ وعدہ کب پُورا ہوگا؟ ');
INSERT INTO "ur_maududi" VALUES(67,26,'67:26','کہو”اِس کا علم تو اللہ کے پاس ہے، میں تو بس صاف صاف خبردار کر دینے والا ہوں۔“ ');
INSERT INTO "ur_maududi" VALUES(67,27,'67:27','پھر جب یہ اُس چیز کو قریب دیکھ لیں گے تو اُن سب لوگوں کے چہرے بگڑ جائیں گے جنہوں نے انکار کیا ہے، اور اُس وقت ان سے کہا جائے گا کہ یہی ہے وہ چیز جس کے لیے تم تقاضے کر رہے تھے');
INSERT INTO "ur_maududi" VALUES(67,28,'67:28','اِن سے کہو، کبھی تم نے یہ بھی سوچا کہ اللہ خواہ مجھے اور میرے ساتھیوں کو ہلاک کر دے یا ہم پر رحم کرے، کافروں کو دردناک عذاب سے کون بچا لے گا؟ ');
INSERT INTO "ur_maududi" VALUES(67,29,'67:29','اِن سے کہو، وہ بڑا رحیم ہے، اسی پر ہم ایمان لائے ہیں، اور اُسی پر ہمارا بھروسا ہے، عنقریب تمہیں معلوم ہوجائے گا کہ صریح گمراہی میں پڑا ہوا کون ہے');
INSERT INTO "ur_maududi" VALUES(67,30,'67:30','اِن سےکہو، کبھی تم نے یہ بھی سوچا کہ اگر تمہارے کنووٴں کا پانی زمین میں اُتر جائے تو کون ہے جو اِس پانی کی بہتی ہوئی سوتیں تمہیں نکال کر لادے گا؟ ');
INSERT INTO "ur_maududi" VALUES(68,1,'68:1','ن۔ قسم ہے قلم کی اور اُس چیز کی جسے لکھنے والے لکھ رہے ہیں، ');
INSERT INTO "ur_maududi" VALUES(68,2,'68:2','تم اپنے ربّ کے فضل سے مجنون نہیں ہو۔ ');
INSERT INTO "ur_maududi" VALUES(68,3,'68:3','اور یقیناً تمہارے لیے ایسا اجر ہے جس کا سلسلہ کبھی ختم ہونے والا نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(68,4,'68:4','اور بےشک تم اخلاق کے بڑے مرتبے پر ہو۔ ');
INSERT INTO "ur_maududi" VALUES(68,5,'68:5','عنقریب تم بھی دیکھ لو گے اور وہ بھی دیکھ لیں گے');
INSERT INTO "ur_maududi" VALUES(68,6,'68:6','کہ تم میں سے کون جنون میں مبتلا ہے');
INSERT INTO "ur_maududi" VALUES(68,7,'68:7','تمہارا رب اُن لوگوں کو بھی خوب جانتا ہے جو اس کی راہ سے بھٹکے ہوئے ہیں، اور وہی ان کو بھی اچھی طرح جانتا ہے جو راہ راست پر ہیں');
INSERT INTO "ur_maududi" VALUES(68,8,'68:8','لہٰذا تم اِن جھٹلانے والوں کے دباؤ میں ہرگز نہ آؤ');
INSERT INTO "ur_maududi" VALUES(68,9,'68:9','یہ تو چاہتے ہیں کہ کچھ تم مداہنت کرو تو یہ بھی مداہنت کریں۔ ');
INSERT INTO "ur_maududi" VALUES(68,10,'68:10','ہر گز نہ دبو کسی ایسے شخص سے جو بہت قسمیں کھانے والا بے وقعت آدمی ہے، ');
INSERT INTO "ur_maududi" VALUES(68,11,'68:11','طعنے دیتا ہے، چغلیاں کھاتا پھرتا ہے');
INSERT INTO "ur_maududi" VALUES(68,12,'68:12','بھلائی سے روکتا ہے، ظلم و زیادتی میں حد سے گزر جانے والا ہے، سخت بد اعمال ہے، جفا کار ہے، ');
INSERT INTO "ur_maududi" VALUES(68,13,'68:13','اور اِن سب عیوب کے ساتھ بد اصل ہے، ');
INSERT INTO "ur_maududi" VALUES(68,14,'68:14','اِس بنا پر کہ وہ بہت مال و اولاد رکھتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(68,15,'68:15','جب ہماری آیات اُس کو سنائی جاتی ہیں تو کہتا ہے یہ تو اگلے وقتوں کے افسانے ہیں');
INSERT INTO "ur_maududi" VALUES(68,16,'68:16','عنقریب ہم اُس کی سُونڈ پر داغ لگائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(68,17,'68:17','ہم نے اِن (اہلِ مکّہ)کو اُسی طرح آزمائش میں ڈالا ہے جس طرح ایک باغ کے مالکوں کو آزمائش میں ڈالا تھا، جب اُنہوں نے قسم کھائی کے صبح و سویرے ضرور اپنے باغ کے پھل توڑیں گے');
INSERT INTO "ur_maududi" VALUES(68,18,'68:18','اور وہ کوئی استثناء نہیں کر رہے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(68,19,'68:19','رات کو وہ سوئے پڑے تھے کہ تمہارے رب کی طرف سے ایک بلا اس باغ پر پھر گئی');
INSERT INTO "ur_maududi" VALUES(68,20,'68:20','اور اُس کا حال ایسا ہو گیا جیسے کٹی ہوئی فصل ہو');
INSERT INTO "ur_maududi" VALUES(68,21,'68:21','صبح اُن لوگوں نے ایک دوسرے کو پکارا');
INSERT INTO "ur_maududi" VALUES(68,22,'68:22','کہ اگر پھل توڑنے ہیں تو سویرے سویرے اپنی کھیتی کی طرف نکل چلو۔ ');
INSERT INTO "ur_maududi" VALUES(68,23,'68:23','چنانچہ وہ چل پڑے اور آپس میں چپکے چپکے کہتے جاتے تھے');
INSERT INTO "ur_maududi" VALUES(68,24,'68:24','کہ آج کوئی مسکین تمہارے پاس باغ میں نہ آنے پائے');
INSERT INTO "ur_maududi" VALUES(68,25,'68:25','وہ کچھ نہ دینے کا فیصلہ کیے ہوئے صبح سویرے جلدی جلدی اس طرح وہاں گئے جیسے کہ وہ (پھل توڑنے پر)قادر ہیں');
INSERT INTO "ur_maududi" VALUES(68,26,'68:26','مگر جب باغ کو دیکھا تو کہنے لگے "ہم راستہ بھول گئے ہیں');
INSERT INTO "ur_maududi" VALUES(68,27,'68:27','نہیں، بلکہ ہم محروم رہ گئے۔“ ');
INSERT INTO "ur_maududi" VALUES(68,28,'68:28','اُن میں جو سب سے بہتر آدمی تھا اُس نے کہا”میں نے تم سے کہا نہ تھا کہ تم تسبیح کیوں نہیں کرتے؟“ ');
INSERT INTO "ur_maududi" VALUES(68,29,'68:29','و ہ پکار اٹھے پاک ہے ہمارا رب، واقعی ہم گناہ گار تھے');
INSERT INTO "ur_maududi" VALUES(68,30,'68:30','پھر اُن میں سے ہر ایک دوسرے کو ملامت کرنے لگا۔ ');
INSERT INTO "ur_maududi" VALUES(68,31,'68:31','آخر کو انہوں نے کہا "افسوس ہمارے حال پر، بے شک ہم سرکش ہو گئے تھے');
INSERT INTO "ur_maududi" VALUES(68,32,'68:32','بعید نہیں کہ ہمارا رب ہمیں بدلے میں اِس سے بہتر باغ عطا فرمائے، ہم اپنے رب کی طرف رجوع کرتے ہیں"');
INSERT INTO "ur_maududi" VALUES(68,33,'68:33','ایسا ہوتا ہے عذاب اور آخرت کا عذاب اِس سے بھی بڑا ہے، کاش یہ لوگ اِس کو جانتے');
INSERT INTO "ur_maududi" VALUES(68,34,'68:34',' یقیناً خدا ترس لوگوں کے لیے اُن کے ربّ کے ہاں نعمت بھری جنّتیں ہیں');
INSERT INTO "ur_maududi" VALUES(68,35,'68:35','کیا ہم فرماں برداروں کا حال مجرموں کا سا کر دیں؟');
INSERT INTO "ur_maududi" VALUES(68,36,'68:36','تم لوگوں کو کیا ہو گیا ہے، تم کیسے حکم لگاتے ہو؟ ');
INSERT INTO "ur_maududi" VALUES(68,37,'68:37','کیا تمہارے پاس کوئی کتاب ہے جس میں تم یہ پڑھتے ہو');
INSERT INTO "ur_maududi" VALUES(68,38,'68:38','کہ تمہارے لیے ضرور وہاں وہی کچھ ہے جو تم اپنے لیے پسند کرتے ہو؟');
INSERT INTO "ur_maududi" VALUES(68,39,'68:39','یا پھر کیا تمہارے لیے روز قیامت تک ہم پر کچھ عہد و پیمان ثابت ہیں کہ تمہیں وہی کچھ ملے گا جس کا تم حکم لگاؤ؟');
INSERT INTO "ur_maududi" VALUES(68,40,'68:40','اِن سے پوچھو تم میں سے کون اِس کا ضامن ہے؟ ');
INSERT INTO "ur_maududi" VALUES(68,41,'68:41','یا پھر اِن کے ٹھہرائے ہوئے کچھ شریک ہیں(جنہوں نے اِس کا ذمّہ لیا ہو)؟ یہ بات ہے تو لائیں اپنے شریکوں کو اگر یہ سچے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(68,42,'68:42','جس روز سخت وقت آپڑے گا اور لوگوں کو سجدہ کرنے کے لیے بلایا جائے گا تو یہ لوگ سجدہ نہ کر سکیں گے');
INSERT INTO "ur_maududi" VALUES(68,43,'68:43','اِن کی نگاہیں نیچی ہوں گی، ذِلّت اِن پر چھا رہی ہوگی۔ یہ جب صحیح و سالم تھے اُس وقت اِنہیں سجدے کے لیے بلایا جاتا تھا(اور یہ انکار کرتے تھے)۔ ');
INSERT INTO "ur_maududi" VALUES(68,44,'68:44','پس اے نبیؐ ، تم اِس کلام کے جھٹلانے والوں کا معاملہ مجھ پر چھوڑ دو۔ ہم ایسے طریقہ سے اِن کو بتدریج تباہی کی طرف لے جائیں گے کہ اِن کو خبر بھی نہ ہو گی۔ ');
INSERT INTO "ur_maududi" VALUES(68,45,'68:45','میں اِن کی رسّی دراز کر رہا ہوں ، میری چال بڑی زبردست ہے');
INSERT INTO "ur_maududi" VALUES(68,46,'68:46','کیا تم اِن سے کوئی اجر طلب کر رہے ہو کہ یہ اس چَٹّی کے بوجھ تلے دبے جارہے ہوں؟ ');
INSERT INTO "ur_maududi" VALUES(68,47,'68:47','کیا اِن کے پاس غیب کا علم ہے جسے یہ لکھ رہے ہوں؟ ');
INSERT INTO "ur_maududi" VALUES(68,48,'68:48','پس اپنے ربّ کا فیصلہ صادر ہونے تک صبر کرو اور مچھلی والے (یونسؑ)کی طرح نہ ہو جاوٴ، جب اُس نے پکارا تھا اور وہ غم سے بھرا ہو۱ تھا۔ ');
INSERT INTO "ur_maududi" VALUES(68,49,'68:49','اگر اس کے ربّ کی مہربانی اُس کے شاملِ حال نہ ہو جاتی تو وہ مذموُم ہو کر چَٹیل میدان میں پھینک دیا جاتا۔ ');
INSERT INTO "ur_maududi" VALUES(68,50,'68:50','آخرکار اُس کے رب نے اسے برگزیدہ فرما لیا اور اِسے صالح بندوں میں شامل کر دیا');
INSERT INTO "ur_maududi" VALUES(68,51,'68:51','جب یہ کافر لوگ کلامِ نصیحت (قرآن)سُنتے ہیں تو تمہیں ایسی نظروں سے دیکھتے ہیں کہ گویا تمہارے قدم اُکھاڑ دیں گے، اور کہتے ہیں کہ یہ ضرور دیوانہ ہے');
INSERT INTO "ur_maududi" VALUES(68,52,'68:52','حالانکہ یہ تو سارے جہان والوں کے لیے ایک نصیحت ہے');
INSERT INTO "ur_maududi" VALUES(69,1,'69:1','ہونی شُدنی! ');
INSERT INTO "ur_maududi" VALUES(69,2,'69:2','کیا ہے وہ ہونی شدنی؟');
INSERT INTO "ur_maududi" VALUES(69,3,'69:3','اور تم کیا جانو کہ وہ کیا ہے ہونی شُدنی؟ ');
INSERT INTO "ur_maududi" VALUES(69,4,'69:4','ثمود اور عاد نے اُس اچانک ٹوٹ پڑنے والی آفت کو جھُٹلایا');
INSERT INTO "ur_maududi" VALUES(69,5,'69:5','تو ثمود ایک سخت حادثہ سے ہلاک کیے گئے');
INSERT INTO "ur_maududi" VALUES(69,6,'69:6','اور عاد ایک بڑی شدید طوفانی آندھی سے تباہ کر دیے گئے');
INSERT INTO "ur_maududi" VALUES(69,7,'69:7','اللہ تعالیٰ نے اُس کو مسلسل سات رات اور آٹھ دن اُن پر مسلط رکھا (تم وہاں ہوتے تو) دیکھتے کہ وہ وہاں اِس طرح پچھڑے پڑے ہیں جیسے وہ کھجور کے بوسیدہ تنے ہوں');
INSERT INTO "ur_maududi" VALUES(69,8,'69:8','اب کیا اُن میں سے کوئی تمہیں باقی بچا نظر آتا ہے؟');
INSERT INTO "ur_maududi" VALUES(69,9,'69:9','اور اِسی خطائے عظیم کا ارتکاب فرعون اور اُس سے پہلے کے لوگوں نے اور تَل پٹ ہوجانے والی بستیوں نے کیا۔ ');
INSERT INTO "ur_maududi" VALUES(69,10,'69:10','ان سب نے اپنے رب کے رسول کی بات نہ مانی تو اُس نے اُن کو بڑی سختی کے ساتھ پکڑا');
INSERT INTO "ur_maududi" VALUES(69,11,'69:11','جب پانی کا طوفان حد سے گزر گیا تو ہم نے تم کو کشتی میں سوار کر دیا تھا ');
INSERT INTO "ur_maududi" VALUES(69,12,'69:12','کہ اِس واقعہ کو تمہارے لیے ایک سبق آموز یادگار بنادیں اور یاد رکھنے والے کان اس کی یاد محفوظ رکھیں۔ ');
INSERT INTO "ur_maududi" VALUES(69,13,'69:13',' پھر جب ایک دفعہ صُور میں پھونک مار دی جائے گی');
INSERT INTO "ur_maududi" VALUES(69,14,'69:14','اور زمین اور پہاڑوں کو اٹھا کر ایک ہی چوٹ میں ریزہ ریزہ کر دیا جائے گا');
INSERT INTO "ur_maududi" VALUES(69,15,'69:15','اُس روز وہ ہونے والا واقعہ پیش آ جائے گا');
INSERT INTO "ur_maududi" VALUES(69,16,'69:16','اُس دن آسمان پھٹے گا اور اس کی بندش ڈھیلی پڑ جائے گی');
INSERT INTO "ur_maududi" VALUES(69,17,'69:17','فرشتے اس کے اطراف و جوانب میں ہوں گے اور آٹھ فرشتے اُس روز تیرے ربّ کا عرش اپنے اُوپر اُٹھائے ہوئے ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(69,18,'69:18','وہ دن ہوگا جب تم لوگ پیش کیے جاؤ گے، تمہارا کوئی راز بھی چھپا نہ رہ جائے گا');
INSERT INTO "ur_maududi" VALUES(69,19,'69:19','اُس وقت جس کا نامہٴ اعمال اُس کے سیدھے ہاتھ میں دیا جائے گا وہ کہے گا”لو دیکھو، پڑھو میرا نامہٴ اعمال، ');
INSERT INTO "ur_maududi" VALUES(69,20,'69:20','میں سمجھتا تھا کہ مجھے ضرور اپنا حساب ملنے والا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(69,21,'69:21','پس وہ دل پسند عیش میں ہوگا');
INSERT INTO "ur_maududi" VALUES(69,22,'69:22','عالی مقام جنت میں');
INSERT INTO "ur_maududi" VALUES(69,23,'69:23','جس کے پھلوں کے گچھے جھکے پڑ رہے ہوں گے');
INSERT INTO "ur_maududi" VALUES(69,24,'69:24','(ایسے لوگوں سے کہا جائے گا) مزے سے کھاؤ اور پیو اپنے اُن اعمال کے بدلے جو تم نے گزرے ہوئے دنوں میں کیے ہیں');
INSERT INTO "ur_maududi" VALUES(69,25,'69:25','اور جس کا نامہٴ اعمال اُس کے بائیں ہاتھ میں دیا جائے گا وہ کہے گا”کاش میرا اعمال نامہ مجھے نہ دیا گیا ہوتا ');
INSERT INTO "ur_maududi" VALUES(69,26,'69:26','اور میں نہ جانتا کہ میرا حساب کیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(69,27,'69:27','کاش میری وہی موت (جو دُنیا میں آئی تھی) فیصلہ کُن ہوتی۔ ');
INSERT INTO "ur_maududi" VALUES(69,28,'69:28','آج میرا مال میرے کچھ کام نہ آیا');
INSERT INTO "ur_maududi" VALUES(69,29,'69:29','میرا سارا اقتدار ختم ہوگیا۔“ ');
INSERT INTO "ur_maududi" VALUES(69,30,'69:30','(حکم ہو گا) پکڑو اِسے اور اِس کی گردن میں طوق ڈال دو');
INSERT INTO "ur_maududi" VALUES(69,31,'69:31','پھر اِسے جہنم میں جھونک دو');
INSERT INTO "ur_maududi" VALUES(69,32,'69:32','پھر اِس کو ستر ہاتھ لمبی زنجیر میں جکڑ دو');
INSERT INTO "ur_maududi" VALUES(69,33,'69:33','یہ نہ اللہ بزرگ و برتر پر ایمان لاتا تھا');
INSERT INTO "ur_maududi" VALUES(69,34,'69:34','اور نہ مسکین کو کھانا کھلانے کی ترغیب دیتا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(69,35,'69:35','لہٰذا آج نہ یہاں اِس کا کوئی یار غم خوار ہے');
INSERT INTO "ur_maududi" VALUES(69,36,'69:36','اور نہ زخموں کے دھوون کے سوا اِس کے لیے کوئی کھانا');
INSERT INTO "ur_maududi" VALUES(69,37,'69:37','جسے خطا کاروں کے سوا کوئی نہیں کھاتا');
INSERT INTO "ur_maududi" VALUES(69,38,'69:38','پس نہیں، میں قسم کھاتا ہوں اُن چیزوں کی بھی جو تم دیکھتے ہو');
INSERT INTO "ur_maududi" VALUES(69,39,'69:39','اور اُن کی بھی جنہیں تم نہیں دیکھتے');
INSERT INTO "ur_maududi" VALUES(69,40,'69:40','یہ ایک رسولِ کریم کا قول ہے، ');
INSERT INTO "ur_maududi" VALUES(69,41,'69:41','کسی شاعر کا قول نہیں ہے ، تم لوگ کم ہی ایمان لاتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(69,42,'69:42','اور نہ یہ کسی کاہن کا قول ہے، تم لوگ کم ہی غور کرتے ہو');
INSERT INTO "ur_maududi" VALUES(69,43,'69:43','یہ ربّ العالمین کی طرف سے نازل ہوا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(69,44,'69:44','اور اگر اس (نبی) نے خود گھڑ کر کوئی بات ہماری طرف منسوب کی ہوتی');
INSERT INTO "ur_maududi" VALUES(69,45,'69:45','تو ہم اِس کا دایاں ہاتھ پکڑ لیتے');
INSERT INTO "ur_maududi" VALUES(69,46,'69:46','اور اِس کی رگ گردن کاٹ ڈالتے');
INSERT INTO "ur_maududi" VALUES(69,47,'69:47','پھر تم میں سے کوئی (ہمیں) اِس کام سے روکنے والا نہ ہوتا۔ ');
INSERT INTO "ur_maududi" VALUES(69,48,'69:48','درحقیقت یہ پرہیز گار لوگوں کے لیے ایک نصیحت ہے۔ ');
INSERT INTO "ur_maududi" VALUES(69,49,'69:49','اور ہم جانتے ہیں کہ تم میں سے کچھ لوگ جھٹلانے والے ہیں');
INSERT INTO "ur_maududi" VALUES(69,50,'69:50','ایسے کافروں کے لیے یقیناً یہ موجبِ حسرت ہے۔ ');
INSERT INTO "ur_maududi" VALUES(69,51,'69:51','اور یہ بالکل یقینی حق ہے');
INSERT INTO "ur_maududi" VALUES(69,52,'69:52','پس اے نبیؐ، اپنے رب عظیم کے نام کی تسبیح کرو');
INSERT INTO "ur_maududi" VALUES(70,1,'70:1','مانگنے والےنے عذاب مانگا ہے، (وہ عذاب)جو ضرور واقع ہونے والا ہے');
INSERT INTO "ur_maududi" VALUES(70,2,'70:2','کافروں کے لیے ہے، کوئی اُسے دفع کرنے والا نہیں');
INSERT INTO "ur_maududi" VALUES(70,3,'70:3','اُس خدا کی طرف سے ہے جو عروج کے زینوں کا مالک ہے۔ ');
INSERT INTO "ur_maududi" VALUES(70,4,'70:4','ملائکہ اور رُوح اُس کے حضور چڑھ کر جاتے ہیں ایک ایسے دن میں جس کی مقدار پچاس ہزار سال ہے۔ ');
INSERT INTO "ur_maududi" VALUES(70,5,'70:5','پس اے نبیؐ ، صبر کرو، شائستہ صبر۔ ');
INSERT INTO "ur_maududi" VALUES(70,6,'70:6','یہ لوگ اُسے دور سمجھتے ہیں');
INSERT INTO "ur_maududi" VALUES(70,7,'70:7','اور ہم اسے قریب دیکھ رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(70,8,'70:8','(وہ عذاب اُس روز ہو گا)جس روز آسمان پگھلی ہوئی چاندی کی طرح ہو جائے گا ');
INSERT INTO "ur_maududi" VALUES(70,9,'70:9','اور پہاڑ رنگ برنگ کے دُھنکے ہوئے اُون جیسے ہوجائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(70,10,'70:10','اور کوئی جگری دوست اپنے جگری دوست کو نہ پوچھے گا');
INSERT INTO "ur_maududi" VALUES(70,11,'70:11','حالانکہ وہ ایک دُوسرے کو دکھائے جائیں گے۔ مجرم چاہے گا کہ اس دن کے عذاب سے بچنے کے لیے اپنی اولاد کو');
INSERT INTO "ur_maududi" VALUES(70,12,'70:12','اپنی بیوی کو، اپنے بھائی کو');
INSERT INTO "ur_maududi" VALUES(70,13,'70:13','اپنے قریب ترین خاندان کو جو اسے پناہ دینے والا تھا');
INSERT INTO "ur_maududi" VALUES(70,14,'70:14','اور روئے زمین کے سب لوگوں کو فدیہ میں دیدے اور یہ تدبیر اُسے نجات دلا دے');
INSERT INTO "ur_maududi" VALUES(70,15,'70:15','ہرگز نہیں وہ تو بھڑکتی ہوئی آگ کی لپٹ ہوگی');
INSERT INTO "ur_maududi" VALUES(70,16,'70:16','جو گوشت پوست کو چاٹ جائے گی');
INSERT INTO "ur_maududi" VALUES(70,17,'70:17','پکار پکار کر اپنی طرف بلائے گی ہر اُس شخص کو جس نے حق سے منہ موڑا اور پیٹھ پھیری');
INSERT INTO "ur_maududi" VALUES(70,18,'70:18','اور مال جمع کیا اور سینت سینت کر رکھا۔ ');
INSERT INTO "ur_maududi" VALUES(70,19,'70:19','انسان تھُڑ دِلا پیدا کیا گیا ہے، ');
INSERT INTO "ur_maududi" VALUES(70,20,'70:20','جب اس پر مصیبت آتی ہے تو گھبرا اٹھتا ہے');
INSERT INTO "ur_maududi" VALUES(70,21,'70:21','اور جب اسے خوشحالی نصیب ہوتی ہے تو بخل کرنے لگتا ہے');
INSERT INTO "ur_maududi" VALUES(70,22,'70:22','مگر وہ لوگ (اِس عیب سے بچے ہوئے ہیں)جو نماز پڑھنے والے ہیں، ');
INSERT INTO "ur_maududi" VALUES(70,23,'70:23','جو اپنی نماز کی ہمیشہ پابندی کرتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(70,24,'70:24','جن کے مالوں میں،');
INSERT INTO "ur_maududi" VALUES(70,25,'70:25','سائل اور محروم کا ایک مقرر حق ہے، ');
INSERT INTO "ur_maududi" VALUES(70,26,'70:26','جو روزِ جزا کو بر حق مانتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(70,27,'70:27','جو اپنے ربّ کے عذاب سے ڈرتے ہیں ');
INSERT INTO "ur_maududi" VALUES(70,28,'70:28','کیونکہ اُن کے رب کا عذاب ایسی چیز نہیں ہے جس سے کوئی بے خوف ہو');
INSERT INTO "ur_maududi" VALUES(70,29,'70:29','جو اپنی شرمگاہوں کی حفاظت کرتے ہیں ');
INSERT INTO "ur_maududi" VALUES(70,30,'70:30','بجز اپنی بیویوں یا اپنی مملوکہ عورتوں کے جن سے محفوظ نہ رکھنے میں ان پر کوئی ملامت نہیں');
INSERT INTO "ur_maududi" VALUES(70,31,'70:31','البتہ جو اس کے علاوہ کچھ اور چاہیں وہی حد سے تجاوز کرنے والے ہیں ');
INSERT INTO "ur_maududi" VALUES(70,32,'70:32','جو اپنی امانتوں کی حفاظت اور اپنے عہد کا پاس کرتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(70,33,'70:33','جو اپنی گواہیوں میں راست بازی پر قائم رہتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(70,34,'70:34','اور جو اپنی نماز کی حفاظت کرتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(70,35,'70:35','یہ لوگ عزت کے ساتھ جنت کے باغوں میں رہیں گے');
INSERT INTO "ur_maududi" VALUES(70,36,'70:36','پس اے نبیؐ، کیا بات ہے کہ یہ منکرین دائیں اور بائیں سے،');
INSERT INTO "ur_maududi" VALUES(70,37,'70:37','گروہ در گروہ تمہاری طرف دوڑے چلے آرہے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(70,38,'70:38','کیا اِن میں سے ہر ایک یہ لالچ رکھتا ہے کہ وہ نعمت بھری جنت میں داخل کر دیا جائے گا؟ ');
INSERT INTO "ur_maududi" VALUES(70,39,'70:39','ہر گز نہیں۔ ہم نے جس چیز سے اِن کو پیدا کیا اُسے یہ خود جانتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(70,40,'70:40','پس نہیں، میں قسم کھاتا ہوں مشرقوں اور مغربوں کے مالک کی، ہم اِس پر قادر ہیں');
INSERT INTO "ur_maududi" VALUES(70,41,'70:41','کہ اِن کی جگہ اِن سے بہتر لوگ لے آئیں اور کوئی ہم سے بازی لے جانے والا نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(70,42,'70:42','لہٰذا اِنہیں اپنی بیہودہ باتوں اور اپنے کھیل میں پڑا رہنے دو یہاں تک کہ یہ اپنے اُس دن تک پہنچ جائیں جس کا ان سے وعدہ کیا جا رہا ہے');
INSERT INTO "ur_maududi" VALUES(70,43,'70:43','جب یہ اپنی قبروں سے نِکل کر اِس طرح دوڑے جارہے ہوں گے جیسے اپنے بُتوں کے استھانوں کی طرف دوڑے جارہے ہوں، ');
INSERT INTO "ur_maududi" VALUES(70,44,'70:44','اِن کی نگاہیں جھکی ہوئی ہوں گی، ذلت اِن پر چھا رہی ہوگی وہ دن ہے جس کا اِن سے وعدہ کیا جا رہا ہے');
INSERT INTO "ur_maududi" VALUES(71,1,'71:1','ہم نے نوحؑ کو اس کی قوم کی طرف بھیجا (اس ہدایت کے ساتھ)کہ اپنی قوم کے لوگوں کو خبر دارکر دے قبل اس کے کہ ان پر ایک دردناک عذاب آئے۔ ');
INSERT INTO "ur_maududi" VALUES(71,2,'71:2','ا س نے کہا "اے میری قوم کے لوگو، میں تمہارے لیے ایک صاف صاف خبردار کردینے والا (پیغمبر) ہوں');
INSERT INTO "ur_maududi" VALUES(71,3,'71:3','(تم کو آگاہ کرتا ہوں)کہ اللہ کی بندگی کرو اور اس سے ڈرو اور میری اطاعت کرو، ');
INSERT INTO "ur_maududi" VALUES(71,4,'71:4','اللہ تمہارے گناہوں سے درگزر فرمائے گا اور تمہیں ایک وقتِ مقرر تک باقی رکھے گا۔ حقیقت یہ ہے کہ اللہ کا مقرر کیا ہوا وقت جب آجاتا ہے تو پھر ٹالا نہیں جاتا۔ کاش تمہیں اِس کا علم ہو۔“ ');
INSERT INTO "ur_maududi" VALUES(71,5,'71:5',' اُس نے عرض کیا”اے میرے ربّ، میں نے اپنی قوم کے لوگوں کو شب و روز پکارا');
INSERT INTO "ur_maududi" VALUES(71,6,'71:6','مگر میری پُکار نے اُن کے فرار ہی میں اضافہ کیا۔ ');
INSERT INTO "ur_maududi" VALUES(71,7,'71:7','اور جب بھی میں نے اُن کو بلایا تاکہ تُو انہیں معاف کر دے، انہوں نے کانوں میں اُنگلیاں ٹھونس لیں اور اپنے کپڑوں سے منہ ڈھانک لیے اور اپنی روش پر اَڑ گئے اور بڑا تکبّر کیا۔ ');
INSERT INTO "ur_maududi" VALUES(71,8,'71:8','پھر میں نے ان کو ہانکے پکارے دعوت دی');
INSERT INTO "ur_maududi" VALUES(71,9,'71:9','پھر میں نے علانیہ بھی ان کو تبلیغ کی اور چپکے چپکے بھی سمجھایا');
INSERT INTO "ur_maududi" VALUES(71,10,'71:10','میں نے کہا اپنے رب سے معافی مانگو، بے شک وہ بڑا معاف کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(71,11,'71:11','وہ تم پر آسمان سے خوب بارشیں برسائے گا');
INSERT INTO "ur_maududi" VALUES(71,12,'71:12','تمہیں مال اور اولاد سے نوازے گا، تمہارے لیے باغ پیدا کرے گا اور تمہارے لیے نہریں جاری کردے گا۔ ');
INSERT INTO "ur_maududi" VALUES(71,13,'71:13','تمہیں کیا ہوگیا کہ اللہ کے لیے تم کسی وقار کی توقع نہیں رکھتے؟ ');
INSERT INTO "ur_maududi" VALUES(71,14,'71:14','حالانکہ اُس نے طرح طرح سے تمہیں بنایا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(71,15,'71:15','کیا دیکھتے نہیں ہو کہ اللہ نے کس طرح سات آسمان تہ بر تہ بنائے');
INSERT INTO "ur_maududi" VALUES(71,16,'71:16','اور اُن میں چاند کو نور اور سورج کو چراغ بنایا؟');
INSERT INTO "ur_maududi" VALUES(71,17,'71:17','اور اللہ نے تم کو زمین سے عجیب طرح اُگایا، ');
INSERT INTO "ur_maududi" VALUES(71,18,'71:18','پھر وہ تمہیں اِسی زمین میں واپس لے جائے گا اور اِس سے یکایک تم کو نکال کھڑا کرے گا');
INSERT INTO "ur_maududi" VALUES(71,19,'71:19','اور اللہ نے زمین کو تمہارے لیے فرش کی طرح بچھا دیا');
INSERT INTO "ur_maududi" VALUES(71,20,'71:20','تاکہ تم اس کے اندر کھلے راستوں میں چلو"');
INSERT INTO "ur_maududi" VALUES(71,21,'71:21','نوحؑ نے کہا، "میرے رب، اُنہوں نے میری بات رد کر دی اور اُن (رئیسوں) کی پیروی کی جو مال اور اولاد پا کر اور زیادہ نامراد ہو گئے ہیں');
INSERT INTO "ur_maududi" VALUES(71,22,'71:22','اِن لوگوں نے بڑا بھاری مکر کا جال پھیلا رکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(71,23,'71:23','اِنہوں نے کہا ہر گز نہ چھوڑو اپنے معبُودوں کو ، اور نہ چھوڑو وَدّ اور سُواع کو، اور نہ یَغُوث اور یَعُوق اور نَسر کو۔ ');
INSERT INTO "ur_maududi" VALUES(71,24,'71:24','انہوں نے بہت لوگوں کو گمراہ کیا ہے، اور تُو بھی اِن ظالموں کو گمراہی کے سوا کسی چیز میں ترقی نہ دے۔“ ');
INSERT INTO "ur_maududi" VALUES(71,25,'71:25','اپنی خطاوٴں کی بنا پر ہی وہ غرق کیے گئے اور آگ میں جھونک دیے گئے، پھر انہوں نے اپنے لیے اللہ سے بچانے والا کوئی مددگار نہ پایا۔ ');
INSERT INTO "ur_maududi" VALUES(71,26,'71:26','اور نوحؑ نے کہا، "میرے رب، اِن کافروں میں سے کوئی زمین پر بسنے والا نہ چھوڑ');
INSERT INTO "ur_maududi" VALUES(71,27,'71:27','اگر تو نے اِن کو چھوڑ دیا تو یہ تیرے بندوں کو گمراہ کریں گے اور اِن کی نسل سے جو بھی پیدا ہوگا بدکار اور سخت کافر ہی ہوگا');
INSERT INTO "ur_maududi" VALUES(71,28,'71:28','میرے رب، مجھے اور میرے والدین کو اور ہر اس شخص کو جو میرے گھر میں مومن کی حیثیت سے داخل ہوا ہے، اور سب مومن مردوں اور عورتوں کو معاف فرما دے، اور ظالموں کے لیے ہلاکت کے سوا کسی چیز میں اضافہ نہ کر"');
INSERT INTO "ur_maududi" VALUES(72,1,'72:1','اے نبیؐ ، کہو، میری طرف وحی بھیجی گئی ہے کہ جِنّوں کے ایک گروہ نے غور سے سُنا پھر (جا کر اپنی قوم کے لوگوں سے) کہا:”ہم نے ایک بڑا ہی عجیب قرآن سُنا ہے ');
INSERT INTO "ur_maududi" VALUES(72,2,'72:2','راہِ راست کی طرف رہنمائی کرتا ہے اِس لیے ہم اُس پر ایمان لے آئے ہیں اور اب ہم ہرگز اپنے ربّ کے ساتھ کسی کو شریک نہیں کریں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(72,3,'72:3','اور یہ کہ”ہمارے ربّ کی شان بہت اعلیٰ و ارفع ہے ، اُس نے کسی کو بیوی یا بیٹا نہیں بنایا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(72,4,'72:4','اور یہ کہ”ہمارےنادان لوگ اللہ کے بارے میں بہت خلافِ حق باتیں کہتے رہے ہیں۔“');
INSERT INTO "ur_maududi" VALUES(72,5,'72:5','اور یہ کہ ”ہم نے سمجھا تھا کہ انسان اور جِن کبھی خدا کے بارے میں جھوٹ نہیں بول سکتے۔“ ');
INSERT INTO "ur_maududi" VALUES(72,6,'72:6','اور یہ کہ”انسانوں میں سے کچھ لوگ جِنوں میں سے کچھ لوگوں کی پناہ مانگا کرتے تھے، اِس طرح اُنہوں نے جِنّوں کا غرور اور زیادہ بڑھا دیا۔“ ');
INSERT INTO "ur_maududi" VALUES(72,7,'72:7','اور یہ کہ”انسانوں نے بھی وہی گمان کیا جیسا تمھارا گمان تھا کہ اللہ کسی کو رسُول بنا کر نہ بھیجے گا۔“ ');
INSERT INTO "ur_maududi" VALUES(72,8,'72:8','اور یہ کہ "ہم نے آسمان کو ٹٹولا تو دیکھا کہ وہ پہرے داروں سے پٹا پڑا ہے اور شہابوں کی بارش ہو رہی ہے"');
INSERT INTO "ur_maududi" VALUES(72,9,'72:9','اور یہ کہ”پہلے ہم سُن گُن لینے کے لیے آسمان میں بیٹھنے کی جگہ پالیتے تھے ، مگر اب جو چوری چھُپے سُننے کی کوشش کرتا ہے وہ اپنے لیے گھات میں ایک شہابِ ثاقب لگا ہوا پاتا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(72,10,'72:10','اور یہ کہ”ہماری سمجھ میں نہ آتا تھا کہ آیا زمین والوں کے ساتھ کوئی بُرا معاملہ کرنے کا ارادہ کیا گیا ہے یا اُن کا ربّ اُنہیں راہِ راست دکھانا چاہتا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(72,11,'72:11','اور یہ کہ”ہم میں سے کچھ لوگ صالح ہیں اور کچھ اس سے فرو تر ہیں، ہم مختلف طریقوں میں بٹے ہوئے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(72,12,'72:12','اور یہ کہ”ہم سمجھتے تھے کہ نہ زمین میں ہم اللہ کو عاجز کر سکتے ہیں اور نہ بھاگ کر اُسے ہرا سکتے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(72,13,'72:13','اور یہ کہ”ہم نے جب ہدایت کی تعلیم سُنی تو ہم اس پر ایمان لے آئے۔ اب جو کوئی بھی اپنے ربّ پر ایمان لے آئے گا اسے کسی حق تلفی یا ظلم کا خوف نہ ہوگا۔“ ');
INSERT INTO "ur_maududi" VALUES(72,14,'72:14','اور یہ کہ "ہم میں سے کچھ مسلم (اللہ کے اطاعت گزار) ہیں اور کچھ حق سے منحرف تو جنہوں نے اسلام (اطاعت کا راستہ) اختیار کر لیا انہوں نے نجات کی راہ ڈھونڈ لی');
INSERT INTO "ur_maududi" VALUES(72,15,'72:15','جو حق سے منحرف ہیں وہ جہنّم کا ایندھن بننے والے ہیں۔“ ');
INSERT INTO "ur_maududi" VALUES(72,16,'72:16',' اور (اے نبیؐ) کہو، مجھ پر یہ وحی بھی کی گئی ہے کہ) لوگ اگر راہِ راست پر ثابت قدمی سے چلتے تو ہم اُنہیں خوب سیراب کرتے، ');
INSERT INTO "ur_maududi" VALUES(72,17,'72:17','تاکہ اس نعمت سے ان کی آزمائش کریں۔ اور جو اپنے ربّ کے ذکر سے منہ موڑے گا اس کا ربّ اسے سخت عذاب میں مبتلا کر دے گا');
INSERT INTO "ur_maududi" VALUES(72,18,'72:18','اور یہ کہ مسجدیں اللہ کے لیے ہیں، لہٰذا اُن میں اللہ کے ساتھ کسی اور کو نہ پکارو۔ ');
INSERT INTO "ur_maududi" VALUES(72,19,'72:19','اور یہ کہ جب اللہ بندہ اُس کو پکارنے کے لیے کھڑا ہوا تو لوگ اُس پر ٹُوٹ پڑنے کے لیے تیار ہوگئے');
INSERT INTO "ur_maududi" VALUES(72,20,'72:20','اے نبیؐ ، کہو کہ”میں تو اپنے ربّ کو پکارتا ہوں اور اس کے ساتھ کسی کو شریک نہیں کرتا۔“ ');
INSERT INTO "ur_maududi" VALUES(72,21,'72:21','کہو، "میں تم لوگوں کے لئے نہ کسی نقصان کا اختیار رکھتا ہوں نہ کسی بھلائی کا"');
INSERT INTO "ur_maududi" VALUES(72,22,'72:22','کہو، "مجھے اللہ کی گرفت سے کوئی نہیں بچا سکتا اور نہ میں اُس کے دامن کے سوا کوئی جائے پناہ پا سکتا ہوں');
INSERT INTO "ur_maududi" VALUES(72,23,'72:23','میرا کام اس کے سوا کچھ نہیں ہے کہ اللہ کی بات اور اس کے پیغامات پہنچا دوں۔ اب جو بھی اللہ اور اس کے رسُول کی بات نہ مانے گا اس کے لیے جہنّم کی آگ ہے اور ویسے لوگ اس میں ہمیشہ رہیں گے۔“ ');
INSERT INTO "ur_maududi" VALUES(72,24,'72:24','(یہ لوگ اپنی اِس روش سے باز نہ آئیں گے) یہاں تک کہ جب اُس چیز کو دیکھ لیں گے جس کا اِن سے وعدہ کیا جا رہا ہے تو انہیں معلوم ہو جائے گا کہ کس کے مددگار کمزور ہیں اور کس کا جتھا تعداد میں کم ہے۔ ');
INSERT INTO "ur_maududi" VALUES(72,25,'72:25','کہو”میں نہیں جانتا کہ جس چیز کا وعدہ تم سےکیا جارہا ہے وہ قریب ہے یا میرا ربّ اس کے لیے کوئی لمبی مدّت مقرر فرماتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(72,26,'72:26','وہ عالم الغیب ہے، اپنے غیب پر کسی کو مطلّع نہیں کرتا، ');
INSERT INTO "ur_maududi" VALUES(72,27,'72:27','سوائے اُس رسُول کے جسے اُس نے (غیب کا کوئی علم دینے کے لیے) پسند کر لیا ہو، تو اُس کے آگے اور پیچھے وہ محافظ لگا دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(72,28,'72:28','تاکہ وہ جان لے کہ انہوں نے اپنے ربّ کے پیغامات پہنچا دیے، اور اُن کے پُورے ماحول کا احاطہ کیے ہوئے ہے اور ایک ایک چیز کو اس نے گِن رکھا ہے۔“ ');
INSERT INTO "ur_maududi" VALUES(73,1,'73:1','اے اوڑھ لپیٹ کر سونے والے، ');
INSERT INTO "ur_maududi" VALUES(73,2,'73:2','رات کو نماز میں کھڑے رہا کرو مگر کم، ');
INSERT INTO "ur_maududi" VALUES(73,3,'73:3','آدھی رات، یا اس سے تھوڑا کم');
INSERT INTO "ur_maududi" VALUES(73,4,'73:4','یا اس سے کچھ کم کر لو، یا اس سے کچھ زیادہ بڑھا دو، اور قرآن کو خوب ٹھہر ٹھہر کر پڑھو۔ ');
INSERT INTO "ur_maududi" VALUES(73,5,'73:5','ہم تم پر ایک بھاری کلام نازل کرنے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(73,6,'73:6','درحقیقت رات کا اُٹھنا نفس پر قابو پانے کے لیے بہت کارگر اور قرآن ٹھیک پڑھنے کے لیے زیادہ موزوں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(73,7,'73:7','دن کے اوقات میں تو تمہارے لیے بہت مصروفیات ہیں');
INSERT INTO "ur_maududi" VALUES(73,8,'73:8','اپنے ربّ کے نام کا ذکر کیا کرو اور سب سے کٹ کر اسی کے ہو رہو');
INSERT INTO "ur_maududi" VALUES(73,9,'73:9','وہ مشرق و مغرب کا مالک ہے، اُس کے سوا کوئی خدا نہیں ہے، لہٰذا اُسی کو اپنا وکیل بنا لو۔ ');
INSERT INTO "ur_maududi" VALUES(73,10,'73:10','اور جو باتیں لوگ بنا رہے ہیں ان پر صبر کرو اور شرافت کے ساتھ اُن سے الگ ہو جاوٴ۔ ');
INSERT INTO "ur_maududi" VALUES(73,11,'73:11','اِن جُھٹلانے والے خوشحال لوگوں سے نمٹنے کا کام تم مجھ پر چھوڑ دو اور اِنہیں ذرا کچھ دیر اِسی حالت میں رہنے دو');
INSERT INTO "ur_maududi" VALUES(73,12,'73:12','ہمارے پاس (اِن کے لیے) بھاری بیڑیاں ہیں اور بھڑکتی ہوئی آگ');
INSERT INTO "ur_maududi" VALUES(73,13,'73:13','اور حلق میں پھنسنے والا کھانا اور دردناک عذاب');
INSERT INTO "ur_maududi" VALUES(73,14,'73:14','یہ اُس دن ہو گا جب زمین اور پہاڑ لرز اُٹھیں گے اور پہاڑوں کا حال ایسا ہو جائے گا جیسے ریت کے ڈھیر جو بکھرے جا رہے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(73,15,'73:15',' تم لوگوں کے پاس ہم نے اُسی طرح ایک رسول تم پر گواہ بنا کر بھیجا ہے جس طرح ہم نے فرعون کی طرف ایک رسُول بھیجا تھا');
INSERT INTO "ur_maududi" VALUES(73,16,'73:16','(پھر دیکھ لو کہ جب) فرعون نے اُس رسول کی بات نہ مانی تو ہم نے اس کو بڑی سختی کے ساتھ پکڑ لیا');
INSERT INTO "ur_maududi" VALUES(73,17,'73:17','اگر تم ماننے سے انکار کرو گے تو اُس دن کیسے بچ جاوٴ گے جو بچوں کو بوڑھا کر دے گا ');
INSERT INTO "ur_maududi" VALUES(73,18,'73:18','اور جس کی سختی سے آسمان پھٹا جا رہا ہوگا؟ اللہ کا وعدہ تو پورا ہو کر ہی رہنا ہے');
INSERT INTO "ur_maududi" VALUES(73,19,'73:19','یہ ایک نصیحت ہے، اب جس کا جی چاہے اپنے رب کی طرف جانے کا راستہ اختیار کر لے');
INSERT INTO "ur_maududi" VALUES(73,20,'73:20',' اے نبیؐ ، تمہارا ربّ جانتا ہے کہ تم کبھی دو تہائی رات کے قریب اور کبھی آدھی رات اور کبھی ایک تہائی رات عبادت میں کھڑے رہتے ہو، اور تمہارے ساتھیوں میں سے بھی ایک گروہ یہ عمل کرتا ہے۔ اللہ ہی رات اور دن کے اوقات کا حساب رکھتا ہے ، اُسے معلوم ہے کہ تم لوگ اوقات کا ٹھیک شمار نہیں کر سکتے، لہٰذا اس نے تم پر مہربانی فرمائی، اب جتنا قرآن آسانی سے پڑھ سکتے ہو پڑھ لیا کرو۔ اُسے معلوم ہے کہ تم میں کچھ مریض ہوں گے، کچھ دُوسرے لوگ اللہ کے فضل کی تلاش میں سفر کرتے ہیں، اور کچھ اور لوگ اللہ کی راہ میں جنگ کرتے ہیں۔ پس جتنا قرآن بآسانی پڑھا جاسکے پڑھ لیا کرو، نماز قائم کرو، زکوٰة دو اور اللہ کو اچھا قرض دیتے رہو۔ جو کچھ بھلائی تم اپنے لیے آگے بھیجو گے اسے اللہ کے ہاں موجود پاوٴ گے، وہی زیادہ بہتر ہے اور اس کا اجر بہت بڑا ہے۔ اللہ سے مغفرت مانگتے رہو، بے شک اللہ بڑا غفور و رحیم ہے');
INSERT INTO "ur_maududi" VALUES(74,1,'74:1','اے اوڑھ لپیٹ کر لیٹنے والے، ');
INSERT INTO "ur_maududi" VALUES(74,2,'74:2','اُٹھو اور خبر دار کرو۔ ');
INSERT INTO "ur_maududi" VALUES(74,3,'74:3','اور اپنے ربّ کی بڑائی کا اعلان کرو۔ ');
INSERT INTO "ur_maududi" VALUES(74,4,'74:4','اور اپنے کپڑے پاک رکھو۔ ');
INSERT INTO "ur_maududi" VALUES(74,5,'74:5','اور گندگی سے دُور رہو۔ ');
INSERT INTO "ur_maududi" VALUES(74,6,'74:6','اور احسان نہ کرو زیادہ حاصل کرنے کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(74,7,'74:7','اور اپنے ربّ کی خاطر صبر کرو۔ ');
INSERT INTO "ur_maududi" VALUES(74,8,'74:8','اچھا، جب صور میں پھونک ماری جائے گی');
INSERT INTO "ur_maududi" VALUES(74,9,'74:9','وہ دن بڑا ہی سخت دن ہوگا، ');
INSERT INTO "ur_maududi" VALUES(74,10,'74:10','کافروں کے لیے ہلکا نہ ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(74,11,'74:11','چھوڑ دو مجھے اور اُس شخص کو جسے میں نے اکیلا پیدا کیا، ');
INSERT INTO "ur_maududi" VALUES(74,12,'74:12','بہت سا مال اُس کو دیا');
INSERT INTO "ur_maududi" VALUES(74,13,'74:13','اس کے ساتھ حاضر رہنے والے بیٹے دیے، ');
INSERT INTO "ur_maududi" VALUES(74,14,'74:14','اور اس کے لیے ریاست کی راہ ہموار کی');
INSERT INTO "ur_maududi" VALUES(74,15,'74:15','پھر وہ طمع رکھتا ہے کہ میں اسے اور زیادہ دُوں۔ ');
INSERT INTO "ur_maududi" VALUES(74,16,'74:16','ہرگز نہیں، وہ ہماری آیات سے عناد رکھتا ہے');
INSERT INTO "ur_maududi" VALUES(74,17,'74:17','میں تو اسے عنقریب ایک کٹھن چڑھائی چڑھواؤں گا');
INSERT INTO "ur_maududi" VALUES(74,18,'74:18','اس نے سوچا اور کچھ بات بنانے کی کوشش کی');
INSERT INTO "ur_maududi" VALUES(74,19,'74:19','تو خدا کی مار اس پر، کیسی بات بنانے کی کوشش کی');
INSERT INTO "ur_maududi" VALUES(74,20,'74:20','ہاں، خدا کی مار اُس پر، کیسی بات بنانے کی کوشش کی');
INSERT INTO "ur_maududi" VALUES(74,21,'74:21','پھر (لوگوں کی طرف) دیکھا');
INSERT INTO "ur_maududi" VALUES(74,22,'74:22','پھر پیشانی سیکڑی اور منہ بنایا');
INSERT INTO "ur_maududi" VALUES(74,23,'74:23','پھر پلٹا اور تکبر میں پڑ گیا');
INSERT INTO "ur_maududi" VALUES(74,24,'74:24','آخرکار بولا کہ یہ کچھ نہیں ہے مگر ایک جادو جو پہلے سے چلا آ رہا ہے');
INSERT INTO "ur_maududi" VALUES(74,25,'74:25','یہ تو ایک انسانی کلام ہے۔ ');
INSERT INTO "ur_maududi" VALUES(74,26,'74:26','عنقریب میں اسے دوزخ میں جھونک دوں گا');
INSERT INTO "ur_maududi" VALUES(74,27,'74:27','اور تم کیا جانو کہ کیا ہے وہ دوزخ؟');
INSERT INTO "ur_maududi" VALUES(74,28,'74:28','نہ باقی رکھے نہ چھوڑے۔ ');
INSERT INTO "ur_maududi" VALUES(74,29,'74:29','کھال جھُلس دینے والی۔ ');
INSERT INTO "ur_maududi" VALUES(74,30,'74:30','اُنّیس کارکن اُس پر مقرر ہیں۔۔۔۔ ');
INSERT INTO "ur_maududi" VALUES(74,31,'74:31','ہم نے دوزخ کے یہ کارکن فرشتے بنائے ہیں، اور ان کی تعداد کو کافروں کے لیے فتنہ بنا دیا ہے، تاکہ اہلِ کتاب کو یقین آجائے اور ایمان لانے والوں کا ایمان بڑھے، اور اہلِ کتاب اور مومنین کسی شک میں نہ رہیں، اور دل کے بیمار اور کفّار یہ کہیں کہ بھلا اللہ کا اِس عجیب بات سے کیا مطلب ہوسکتا ہے۔ اِس طرح اللہ جسے چاہتا ہے گمراہ کر دیتا ہے اور جسے چاہتا ہے ہدایت بخش دیتا ہے۔ اور تیرے ربّ کے لشکروں کو خود اُس کے سوا کوئی نہیں جانتا ۔۔۔۔ اور اس دوزخ کا ذکر اِس کے سوا کسی غرض کے لیےنہیں کیا گیا کہ لوگوں کو اس سے نصیحت ہو۔ ');
INSERT INTO "ur_maududi" VALUES(74,32,'74:32',' ہرگز نہیں، قسم ہے چاند کی');
INSERT INTO "ur_maududi" VALUES(74,33,'74:33','اور رات کی جبکہ وہ پلٹتی ہے');
INSERT INTO "ur_maududi" VALUES(74,34,'74:34','اور صبح کی جبکہ وہ روشن ہوتی ہے');
INSERT INTO "ur_maududi" VALUES(74,35,'74:35','یہ دوزخ بھی بڑی چیزوں میں سے ایک ہے، ');
INSERT INTO "ur_maududi" VALUES(74,36,'74:36','انسانوں کے لیے ڈراوا');
INSERT INTO "ur_maududi" VALUES(74,37,'74:37','تم میں سے ہر اُس شخص کے لیے ڈراوا جو آگے بڑھنا چاہے یا پیچھے رہ جانا چاہے۔ ');
INSERT INTO "ur_maududi" VALUES(74,38,'74:38','ہر متنفّس اپنے کسب کے بدلے رہن ہے، ');
INSERT INTO "ur_maududi" VALUES(74,39,'74:39','دائیں بازو والوں کے سوا، ');
INSERT INTO "ur_maududi" VALUES(74,40,'74:40','جو جنتوں میں ہوں گے وہاں وہ،');
INSERT INTO "ur_maududi" VALUES(74,41,'74:41','مجرموں سے پوچھیں گے ');
INSERT INTO "ur_maududi" VALUES(74,42,'74:42','"تمہیں کیا چیز دوزخ میں لے گئی؟"');
INSERT INTO "ur_maududi" VALUES(74,43,'74:43','وہ کہیں گے”ہم نماز پڑھنے والوں میں سے نہ تھے، ');
INSERT INTO "ur_maududi" VALUES(74,44,'74:44','اور مسکین کو کھانا نہیں کھلاتے تھے، ');
INSERT INTO "ur_maududi" VALUES(74,45,'74:45','اور حق کے خلاف باتیں بنانے والوں کے ساتھ مل کر ہم بھی باتیں بنانے لگتے تھے');
INSERT INTO "ur_maududi" VALUES(74,46,'74:46','اور روز جزا کو جھوٹ قرار دیتے تھے');
INSERT INTO "ur_maududi" VALUES(74,47,'74:47','یہاں تک کہ ہمیں اُس یقینی چیز سے سابقہ پیش آگیا۔“ ');
INSERT INTO "ur_maududi" VALUES(74,48,'74:48','اُس وقت سفارش کرنے والوں کی سفارش ان کے کسی کام نہ آئے گی۔ ');
INSERT INTO "ur_maududi" VALUES(74,49,'74:49','آخر اِن لوگوں کو کیا ہو گیا ہے کہ یہ اِس نصیحت سے منہ موڑ رہے ہیں');
INSERT INTO "ur_maududi" VALUES(74,50,'74:50','گویا یہ جنگلی گدھے ہیں');
INSERT INTO "ur_maududi" VALUES(74,51,'74:51','جو شیر سے ڈر کر بھاگ پڑے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(74,52,'74:52','بلکہ اِن میں سے تو ہر ایک یہ چاہتا ہے کہ اُس کے نام کُھلے خط بھیجے جائیں۔ ');
INSERT INTO "ur_maududi" VALUES(74,53,'74:53','ہر گز نہیں، اصل بات یہ ہے کہ یہ آخرت کا خوف نہیں رکھتے۔ ');
INSERT INTO "ur_maududi" VALUES(74,54,'74:54','ہر گز نہیں، یہ تو ایک نصیحت ہے');
INSERT INTO "ur_maududi" VALUES(74,55,'74:55','اب جس کا جی چاہے اس سے سبق حاصل کر لے');
INSERT INTO "ur_maududi" VALUES(74,56,'74:56','اور یہ کوئی سبق حاصل نہ کریں گے اِلّا یہ کہ اللہ ہی ایسا چاہے۔ وہ اس کا حق دار ہے کہ اُس سے تقویٰ کیا جائے اور وہ اس کا اہل ہے کہ (تقویٰ کرنے والوں کو) بخش دے۔ ');
INSERT INTO "ur_maududi" VALUES(75,1,'75:1','نہیں، میں قسم کھاتا ہوں قیامت کے دن کی');
INSERT INTO "ur_maududi" VALUES(75,2,'75:2','اور نہیں ، میں قسم کھاتا ہوں ملامت کرنے والے نفس کی، ');
INSERT INTO "ur_maududi" VALUES(75,3,'75:3','کیا انسان یہ سمجھ رہا ہے کہ ہم اُس کی ہڈیوں کو جمع نہ کر سکیں گے؟ ');
INSERT INTO "ur_maududi" VALUES(75,4,'75:4','کیوں نہیں؟ ہم تو اس کی انگلیوں کی پور پور تک ٹھیک بنادینے پر قادر ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(75,5,'75:5','مگر انسان چاہتا یہ ہے کہ آگے بھی بداعمالیاں کرتا رہے۔ ');
INSERT INTO "ur_maududi" VALUES(75,6,'75:6','پوچھتا ہے”آخر کب آنا ہے وہ قیامت کا دن؟“ ');
INSERT INTO "ur_maududi" VALUES(75,7,'75:7','پھر جب دِیدے پتھرا جائیں گے ');
INSERT INTO "ur_maududi" VALUES(75,8,'75:8','اور چاند بے نور ہو جائیگا');
INSERT INTO "ur_maududi" VALUES(75,9,'75:9','اور چاند سُورج ملا کر ایک کر دیے جائیں گے ');
INSERT INTO "ur_maududi" VALUES(75,10,'75:10','اُس وقت یہی انسان کہے گا "کہاں بھاگ کر جاؤں؟"');
INSERT INTO "ur_maududi" VALUES(75,11,'75:11','ہرگز نہیں، وہاں کوئی جائے پناہ نہ ہوگی');
INSERT INTO "ur_maududi" VALUES(75,12,'75:12','اُس روز تیرے رب ہی کے سامنے جا کر ٹھیرنا ہوگا');
INSERT INTO "ur_maududi" VALUES(75,13,'75:13','اُس روز انسان کو اس کا سب اگلا پچھلا کیا کرایا بتا دیا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(75,14,'75:14','بلکہ انسان خود ہی اپنے آپ کو خوب جانتا ہے');
INSERT INTO "ur_maududi" VALUES(75,15,'75:15','چاہے وہ کتنی ہی معذرتیں پیش کرے ');
INSERT INTO "ur_maududi" VALUES(75,16,'75:16',' اے نبیؐ ، اِس وحی کو جلدی جلدی یاد کرنے کے لیے اپنی زبان کو حرکت نہ دو');
INSERT INTO "ur_maududi" VALUES(75,17,'75:17','اِس کو یاد کرا دینا اور پڑھوا دینا ہمارے ذمہ ہے');
INSERT INTO "ur_maududi" VALUES(75,18,'75:18','لہٰذا جب ہم اِسے پڑھ رہے ہوں اُس وقت تم اِس کی قراٴت کو غور سے سُنتے رہو');
INSERT INTO "ur_maududi" VALUES(75,19,'75:19','پھر اس کا مطلب سمجھا دینا بھی ہمارے ہی ذمّہ ہے ');
INSERT INTO "ur_maududi" VALUES(75,20,'75:20','ہر گز نہیں، اصل بات یہ ہے کہ تم لوگ جلدی حاصل ہونے والی چیز (یعنی دنیا)سے محبت رکھتے ہو');
INSERT INTO "ur_maududi" VALUES(75,21,'75:21','اور آخرت کو چھوڑ دیتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(75,22,'75:22','اُس روز کچھ چہرے ترو تازہ ہونگے، ');
INSERT INTO "ur_maududi" VALUES(75,23,'75:23','اپنے ربّ کی طرف دیکھ رہے ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(75,24,'75:24','اور کچھ چہرے اداس ہوں گے');
INSERT INTO "ur_maududi" VALUES(75,25,'75:25','اور سمجھ رہے ہوں گے کہ اُن کے ساتھ کمر توڑ برتاؤ ہونے والا ہے');
INSERT INTO "ur_maududi" VALUES(75,26,'75:26','ہر گز نہیں، جب جان حلق تک پہنچ جائے گی');
INSERT INTO "ur_maududi" VALUES(75,27,'75:27','اور کہا جائے گا کہ ہے کوئی جھاڑ پھُونک کر نے والا، ');
INSERT INTO "ur_maududi" VALUES(75,28,'75:28','اور آدمی سمجھ لے گا کہ یہ دنیا سے جدائی کا وقت ہے');
INSERT INTO "ur_maududi" VALUES(75,29,'75:29','اور پنڈلی سے پنڈلی جُڑ جائے گی، ');
INSERT INTO "ur_maududi" VALUES(75,30,'75:30','وہ دن ہوگا تیرے رب کی طرف روانگی کا');
INSERT INTO "ur_maududi" VALUES(75,31,'75:31','مگر اُس نے نہ سچ مانا، اور نہ نماز پڑھی');
INSERT INTO "ur_maududi" VALUES(75,32,'75:32','بلکہ جھٹلایا اور پلٹ گیا');
INSERT INTO "ur_maududi" VALUES(75,33,'75:33','پھر اکڑتا ہوا اپنے گھر والوں کی طرف چل دیا۔ ');
INSERT INTO "ur_maududi" VALUES(75,34,'75:34','یہ روش تیرے ہی لیے سزاوار ہے اور تجھی کو زیب دیتی ہے');
INSERT INTO "ur_maududi" VALUES(75,35,'75:35','ہاں یہ روش تیرے ہی لیے سزاوار ہے اور تجھی کو زیب دیتی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(75,36,'75:36',' کیا انسان نے یہ سمجھ رکھا ہے کہ وہ یُونہی مہمل چھوڑ دیا جائے گا؟ ');
INSERT INTO "ur_maududi" VALUES(75,37,'75:37','کیا وہ ایک حقیر پانی کا نطفہ نہ تھا جو (رحم مادر میں) ٹپکایا جاتا ہے؟');
INSERT INTO "ur_maududi" VALUES(75,38,'75:38','پھر وہ ایک لوتھڑا بنا، پھر اللہ نے اس کا جسم بنایا اور اس کے اعضا درست کیے');
INSERT INTO "ur_maududi" VALUES(75,39,'75:39','پھر اس سے مرد اور عورت کی دو قسمیں بنائیں');
INSERT INTO "ur_maududi" VALUES(75,40,'75:40','کیا وہ اِس پر قادر نہیں ہے کہ مرنے والوں کو پھر سے زندہ کردے؟ ');
INSERT INTO "ur_maududi" VALUES(76,1,'76:1','کیا انسان پر لامتناہی زمانے کا ایک وقت ایسا بھی گزرا ہے جب وہ کوئی قابلِ ذکر چیز نہ تھا؟ ');
INSERT INTO "ur_maududi" VALUES(76,2,'76:2','ہم نے انسان کو ایک مخلُوط نُطفے سے پیدا کیا تاکہ اس کا امتحان لیں اور اِس غرض کے لیے ہم نے اسے سُننے اور دیکھنے والا بنایا۔ ');
INSERT INTO "ur_maududi" VALUES(76,3,'76:3','ہم نے اُسے راستہ دکھایا ، خواہ شُکر کرنے والا بنے یا کُفر کرنے والا۔ ');
INSERT INTO "ur_maududi" VALUES(76,4,'76:4','کفر کرنے والوں کے لیے ہم نے زنجیریں اور طوق اور بھڑکتی ہوئی آگ مہیا کر رکھی ہے');
INSERT INTO "ur_maududi" VALUES(76,5,'76:5','نیک لوگ (جنّت میں)شراب کے ایسے ساغر پئیں گے جن میں آبِ کافور کی آمیزش ہوگی');
INSERT INTO "ur_maududi" VALUES(76,6,'76:6','یہ ایک بہتا چشمہ ہوگا جس کے پانی کے ساتھ اللہ کے بندے شراب پئیں گے اور جہاں چاہیں گے بسہُولت اس کی شاخیں نکال لیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(76,7,'76:7','یہ وہ لوگ ہونگے جو (دُنیا میں)نذر پُوری کرتے ہیں، اور اُس دن سے ڈرتے ہیں جس کی آفت ہر طرف پھیلی ہوئی ہو گی');
INSERT INTO "ur_maududi" VALUES(76,8,'76:8','اور اللہ کی محبت میں مسکین اور یتیم اور قیدی کو کھانا کھلاتے ہیں ');
INSERT INTO "ur_maududi" VALUES(76,9,'76:9','(اور اُن سے کہتے ہیں کہ)ہم تمہیں صرف اللہ کی خاطر کِھلا رہے ہیں، ہم تم سے نہ کوئی بدلہ چاہتے ہیں نہ شکریہ، ');
INSERT INTO "ur_maududi" VALUES(76,10,'76:10','ہمیں تو اپنے رب سے اُس دن کے عذاب کا خوف لاحق ہے جو سخت مصیبت کا انتہائی طویل دن ہوگا');
INSERT INTO "ur_maududi" VALUES(76,11,'76:11','پس اللہ تعالیٰ انہیں اُس دن کے شر سے بچالے گا اور انہیں تازگی اور سُرور بخشے گا ');
INSERT INTO "ur_maududi" VALUES(76,12,'76:12','اور اُن کے صبر کے بدلے میں اُنہیں جنّت اور ریشمی لباس عطا کرے گا');
INSERT INTO "ur_maududi" VALUES(76,13,'76:13','وہاں وہ اونچی مسندوں پر تکیے لگائے بیٹھے ہونگے نہ اُنہیں دھوپ کی گرمی ستائے گی نہ جاڑے کی ٹھر');
INSERT INTO "ur_maududi" VALUES(76,14,'76:14','جنت کی چھاؤں ان پر جھکی ہوئی سایہ کر رہی ہوگی، اور اُس کے پھل ہر وقت ان کے بس میں ہوں گے (کہ جس طرح چاہیں انہیں توڑ لیں)');
INSERT INTO "ur_maududi" VALUES(76,15,'76:15','اُن کے آگے چاندی کے برتن اور شیشے کے پیالے گردش کرائے جارہے ہوں گے');
INSERT INTO "ur_maududi" VALUES(76,16,'76:16','شیشے بھی وہ جو چاندی کی قِسم کے ہونگے، اور ان کو(منتظمینِ جنّت نے)ٹھیک اندازے کے مطابق بھرا ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(76,17,'76:17','ان کو وہاں ایسی شراب کے جام پلائے جائیں گے جس میں سونٹھ کی آمیزش ہوگی');
INSERT INTO "ur_maududi" VALUES(76,18,'76:18','یہ جنّت کا ایک چشمہ ہوگا جسے سلسبیل کہا جاتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(76,19,'76:19','ان کے خدمت کے لیے ایسے لڑکے دوڑتے پھر رہے ہوں گے جو ہمیشہ لڑکے ہی رہیں گے۔ تم انہیں دیکھو تو سمجھو کہ موتی ہیں جو بکھیر دیے گئے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(76,20,'76:20','وہاں جِدھر بھی تم نگاہ ڈالو گے نعمتیں ہی نعمتیں اور ایک بڑی سلطنت کا سروسامان تمہیں نظر آئے گا۔ ');
INSERT INTO "ur_maududi" VALUES(76,21,'76:21','اُن کے اوپر باریک ریشم کےسبز لباس اور اطلس و دیبا کے کپڑے ہوں گے، ان کو چاندی کے کنگن پہنائے جائیں گے، اور ان کا ربّ ان کو نہایت پاکیزہ شراب پلائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(76,22,'76:22','یہ ہے تمہاری جزا اور تمہاری کارگزاری قابلِ قدر ٹھہری ہے۔ ');
INSERT INTO "ur_maududi" VALUES(76,23,'76:23','اے نبی ؐ ، ہم نے ہی تم پر یہ قرآن تھوڑا تھوڑا کر کے نازل کیا ہے، ');
INSERT INTO "ur_maududi" VALUES(76,24,'76:24','لہٰذا تم اپنے ربّ کے حکم پر صبر کرو، اور اِن میں سے کسی بد عمل یا منکرِ حق کی بات نہ مانو۔ ');
INSERT INTO "ur_maududi" VALUES(76,25,'76:25','اپنے رب کا نام صبح و شام یاد کرو');
INSERT INTO "ur_maududi" VALUES(76,26,'76:26','رات کو بھی اس کے حضور سجدہ ریز ہو، اور رات کے طویل اوقات میں اُس کی تسبیح کرتے رہو۔ ');
INSERT INTO "ur_maududi" VALUES(76,27,'76:27','یہ لوگ تو جلدی حاصل ہونے والی چیز (دُنیا)سے محبت رکھتے ہیں اور آگے جو بھاری دن آنے والا ہے اسے نظر انداز کر دیتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(76,28,'76:28','ہم نے ہی اِن کو پیدا کیا ہے اور ان کے جوڑ بند مضبوط کیے ہیں، اور ہم جب چاہیں اِن کی شکلوں کو بدل کر رکھ دیں۔ ');
INSERT INTO "ur_maududi" VALUES(76,29,'76:29','یہ ایک نصیحت ہے، اب جس کا جی چاہے اپنے رب کی طرف جانے کا راستہ اختیار کر لے');
INSERT INTO "ur_maududi" VALUES(76,30,'76:30','اور تمہارے چاہنے سے کچھ نہیں ہوتا جب تک کہ اللہ نہ چاہے۔ یقیناً اللہ بڑا علیم و حکیم ہے');
INSERT INTO "ur_maududi" VALUES(76,31,'76:31','اپنی رحمت میں جس کو چاہتا ہے داخل کرتا ہے، اور ظالموں کے لیے اس نے دردناک عذاب تیار کر رکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(77,1,'77:1','قسم ہے اُن (ہواؤں) کی جو پے در پے بھیجی جاتی ہیں');
INSERT INTO "ur_maududi" VALUES(77,2,'77:2','پھر طوفانی رفتار سے چلتی ہیں');
INSERT INTO "ur_maududi" VALUES(77,3,'77:3','اور (بادلوں کو) اٹھا کر پھیلاتی ہیں');
INSERT INTO "ur_maududi" VALUES(77,4,'77:4','پھر (اُن کو) پھاڑ کر جدا کرتی ہیں');
INSERT INTO "ur_maududi" VALUES(77,5,'77:5','پھر (دلوں میں خدا کی) یاد ڈالتی ہیں');
INSERT INTO "ur_maududi" VALUES(77,6,'77:6','عُذر کے طور پر یا ڈراوے کے طور پر، ');
INSERT INTO "ur_maududi" VALUES(77,7,'77:7','جس چیز کا تم سے وعدہ کیا جا رہا ہے وہ ضرور واقع ہونے والی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(77,8,'77:8','پھر جب ستارے ماند پڑ جائیں گے، ');
INSERT INTO "ur_maududi" VALUES(77,9,'77:9','اور آسمان پھاڑ دیا جائے گا، ');
INSERT INTO "ur_maududi" VALUES(77,10,'77:10','اور پہاڑ دھنک ڈالے جائیں گے');
INSERT INTO "ur_maududi" VALUES(77,11,'77:11','اور رسُولوں کی حاضری کا وقت آپہنچے گا (اس روز وہ چیز واقع ہوجائے گی)');
INSERT INTO "ur_maududi" VALUES(77,12,'77:12','کس روز کے لیے یہ کام اٹھا رکھا گیا ہے؟');
INSERT INTO "ur_maududi" VALUES(77,13,'77:13','فیصلے کے روز کے لیے');
INSERT INTO "ur_maududi" VALUES(77,14,'77:14','اور تمہیں کیا خبر کہ وہ فیصلے کا دن کیا ہے؟');
INSERT INTO "ur_maududi" VALUES(77,15,'77:15','تباہی ہے اُس دن جُھٹلانے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(77,16,'77:16','کیا ہم نے اگلوں کو ہلاک نہیں کیا؟ ');
INSERT INTO "ur_maududi" VALUES(77,17,'77:17','پھر اُنہی کے پیچھے ہم بعد والوں کو چلتا کریں گے۔ ');
INSERT INTO "ur_maududi" VALUES(77,18,'77:18','مجرموں کے ساتھ ہم یہی کچھ کیا کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(77,19,'77:19','تباہی ہے اُس دن جھُٹلانے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(77,20,'77:20','کیا ہم نے ایک حقیر پانی سے تمہیں پیدا نہیں کیا');
INSERT INTO "ur_maududi" VALUES(77,21,'77:21','اور ایک مقرر مدت تک ');
INSERT INTO "ur_maududi" VALUES(77,22,'77:22','اُسے ایک محفوظ جگہ ٹھیرائے رکھا؟');
INSERT INTO "ur_maududi" VALUES(77,23,'77:23','تو دیکھو، ہم اِس پر قادر تھے ، پس ہم بہت اچھی قدرت رکھنے والے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(77,24,'77:24','تباہی ہے اُس روز جھُٹلانے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(77,25,'77:25','کیا ہم نے زمین کو سمیٹ کر رکھنے والی نہیں بنایا');
INSERT INTO "ur_maududi" VALUES(77,26,'77:26','زندوں کے لیے بھی اور مُردوں کے لیے بھی');
INSERT INTO "ur_maududi" VALUES(77,27,'77:27','اور اس میں بلند و بالا پہاڑ جمائے، اور تمہیں میٹھا پانی پلایا؟ ');
INSERT INTO "ur_maududi" VALUES(77,28,'77:28','تباہی ہے اُس روز جھُٹلانے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(77,29,'77:29',' چلو اب اُسی چیز کی طرف جسے تم جھُٹلایا کرتے تھے');
INSERT INTO "ur_maududi" VALUES(77,30,'77:30','چلو اُس سائے کی طرف جو تین شاخوں والا ہے، ');
INSERT INTO "ur_maududi" VALUES(77,31,'77:31','نہ ٹھنڈک پہنچانے والا اور نہ آگ کی لپٹ سے بچانے والا');
INSERT INTO "ur_maududi" VALUES(77,32,'77:32','وہ آگ محل جیسی بڑی بڑی چنگاریاں پھینکے گی');
INSERT INTO "ur_maududi" VALUES(77,33,'77:33','(جو اُچھلتی ہوئی یُوں محسُوس ہوں گی) گویا کہ وہ زرد اُونٹ ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(77,34,'77:34','تباہی ہے اُس روز جھٹلانے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(77,35,'77:35','یہ وہ دن ہے جس میں وہ نہ کچھ بولیں گے');
INSERT INTO "ur_maududi" VALUES(77,36,'77:36','اور نہ اُنہیں موقع دیا جائے گا کہ کوئی عُذر پیش کریں۔ ');
INSERT INTO "ur_maududi" VALUES(77,37,'77:37','تباہی ہے اُس دن جھٹلانے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(77,38,'77:38','یہ فیصلے کا دن ہے ہم نے تمہیں اور تم سے پہلے گزرے ہوئے لوگوں کو جمع کر دیا ہے');
INSERT INTO "ur_maududi" VALUES(77,39,'77:39','اب اگر کوئی چال تم چل سکتے ہو تو میرے مقابلہ میں چل دیکھو۔ ');
INSERT INTO "ur_maududi" VALUES(77,40,'77:40','تباہی ہے اُس دن جھٹلانے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(77,41,'77:41',' متقی لوگ آج سایوں اور چشموں میں ہیں');
INSERT INTO "ur_maududi" VALUES(77,42,'77:42','اور جو پھل وہ چاہیں (اُن کے لیے حاضر ہیں)');
INSERT INTO "ur_maududi" VALUES(77,43,'77:43','کھاؤ اور پیو مزے سے اپنے اُن اعمال کے صلے میں جو تم کرتے رہے ہو');
INSERT INTO "ur_maududi" VALUES(77,44,'77:44','ہم نیک لوگوں کو ایسی ہی جزا دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(77,45,'77:45','تباہی ہے اُس روز جھُٹلانے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(77,46,'77:46',' کھالو اور مزے کر لو تھوڑے دن۔ حقیقت میں تم لوگ مجرم ہو');
INSERT INTO "ur_maududi" VALUES(77,47,'77:47','تباہی ہے اُس روز جھٹلانے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(77,48,'77:48','جب اِن سے کہا جاتا ہے کہ (اللہ کے آگے) جُھکو تو نہیں جُھکتے۔ ');
INSERT INTO "ur_maududi" VALUES(77,49,'77:49','تباہی ہے اُس روز جھٹلانے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(77,50,'77:50','اب اِس (قرآن)کے بعد اورکونسا کلام ایسا ہو سکتا ہے جس پر یہ ایمان لائیں؟ ');
INSERT INTO "ur_maududi" VALUES(78,1,'78:1','یہ لوگ کس چیز کے بارے میں پوچھ گچھ کر رہے ہیں؟');
INSERT INTO "ur_maududi" VALUES(78,2,'78:2','کیا اُس بڑی خبر کے بارے میں');
INSERT INTO "ur_maududi" VALUES(78,3,'78:3','جس کے متعلق یہ مختلف چہ میگوئیاں کرنے میں لگے ہوئے ہیں؟ ');
INSERT INTO "ur_maududi" VALUES(78,4,'78:4','ہر گز نہیں، عنقریب اِنہیں معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(78,5,'78:5','ہاں، ہر گز نہیں، عنقریب انہیں معلوم ہو جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(78,6,'78:6','کیا یہ واقعہ نہیں ہے کہ ہم نے زمین کو فرش بنایا، ');
INSERT INTO "ur_maududi" VALUES(78,7,'78:7','اور پہاڑوں کو میخوں کی طرح گاڑ دیا، ');
INSERT INTO "ur_maududi" VALUES(78,8,'78:8','اور تمہیں(مَردوں اور عورتوں کے) جوڑوں کی شکل میں پیدا کیا، ');
INSERT INTO "ur_maududi" VALUES(78,9,'78:9','اور تمہاری نیند کو باعثِ سکون بنایا، ');
INSERT INTO "ur_maududi" VALUES(78,10,'78:10','اور رات کو پردہ پوش');
INSERT INTO "ur_maududi" VALUES(78,11,'78:11','اور دن کو معاش کا وقت بنایا، ');
INSERT INTO "ur_maududi" VALUES(78,12,'78:12','اور تمہارے اوپر سات مضبُوط آسمان قائم کیے، ');
INSERT INTO "ur_maududi" VALUES(78,13,'78:13','اور ایک نہایت روشن اور گرم چراغ پیدا کیا، ');
INSERT INTO "ur_maududi" VALUES(78,14,'78:14','اور بادلوں سے لگاتار بارش برسائی');
INSERT INTO "ur_maududi" VALUES(78,15,'78:15','تاکہ اس کے ذریعہ سے غلہ اور سبزی');
INSERT INTO "ur_maududi" VALUES(78,16,'78:16','اور گھنے باغ اُگائیں؟ ');
INSERT INTO "ur_maududi" VALUES(78,17,'78:17','بے شک فیصلے کا دن ایک مقرر وقت ہے');
INSERT INTO "ur_maududi" VALUES(78,18,'78:18','جس روز صور میں پھُونک ما ر دی جائے گی، تم فوج در فوج نکل آوٴ گے۔ ');
INSERT INTO "ur_maududi" VALUES(78,19,'78:19','اور آسمان کھول دیا جائے گا حتیٰ کہ وہ دروازے ہی دروازے بن کر رہ جائے گا');
INSERT INTO "ur_maududi" VALUES(78,20,'78:20','اور پہاڑ چلائے جائیں گے یہاں تک کہ وہ سراب ہو جائیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(78,21,'78:21','در حقیقت جہنّم ایک گھات ہے، ');
INSERT INTO "ur_maududi" VALUES(78,22,'78:22','سرکشوں کا ٹھکانا');
INSERT INTO "ur_maududi" VALUES(78,23,'78:23','جس میں وہ مُدّتوں پڑے رہیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(78,24,'78:24','اُس کے اندر کسی ٹھنڈک اور پینے کے قابل کسی چیز کا مزہ وہ نہ چکھیں گے');
INSERT INTO "ur_maududi" VALUES(78,25,'78:25','کچھ ملے گا تو بس گرم پانی اور زخموں کا دھوون، ');
INSERT INTO "ur_maududi" VALUES(78,26,'78:26','(اُن کے کرتوتوں) کا بھرپور بدلہ');
INSERT INTO "ur_maududi" VALUES(78,27,'78:27','وہ کسی حساب کی توقع نہ رکھتے تھے');
INSERT INTO "ur_maududi" VALUES(78,28,'78:28','اور ہماری آیات کو انہوں نے بالکل جھُٹلا دیا تھا، ');
INSERT INTO "ur_maududi" VALUES(78,29,'78:29','اور حال یہ تھا کہ ہم نے ہر چیز گِن گِن کر لکھ رکھی تھی۔ ');
INSERT INTO "ur_maududi" VALUES(78,30,'78:30','اب چکھو مزہ، ہم تمہارے لیے عذاب کے سوا کسی چیز میں ہرگز اضافہ نہ کریں گے');
INSERT INTO "ur_maududi" VALUES(78,31,'78:31','یقیناً متّقیوں کے لیے کامرانی کا ایک مقام ہے');
INSERT INTO "ur_maududi" VALUES(78,32,'78:32','باغ اور انگور');
INSERT INTO "ur_maududi" VALUES(78,33,'78:33','اور نوخیز ہم سن لڑکیاں، ');
INSERT INTO "ur_maududi" VALUES(78,34,'78:34','اور چھلکتے ہوئے جام');
INSERT INTO "ur_maududi" VALUES(78,35,'78:35','وہاں کوئی لغو اور جھُوٹی بات وہ نہ سُنیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(78,36,'78:36','جزا ء اور کافی انعام تمہارے ربّ کی طرف سے');
INSERT INTO "ur_maududi" VALUES(78,37,'78:37','اُس نہایت مہربان خدا کی طرف سے جو زمین اور آسمانوں کا اور ان کے درمیان کی ہر چیز کا مالک ہے جس کے سامنے کسی کو بولنے کا یارا نہیں۔ ');
INSERT INTO "ur_maududi" VALUES(78,38,'78:38','جس روز رُوح اور ملائکہ صف بستہ کھڑے ہوں گے، کوئی نہ بولے گا سوائے اُس کے جسے رحمٰن اجازت دے اور جو ٹھیک بات کہے۔ ');
INSERT INTO "ur_maududi" VALUES(78,39,'78:39','وہ دن برحق ہے، اب جس کا جی چاہے اپنے رب کی طرف پلٹنے کا راستہ اختیار کر لے');
INSERT INTO "ur_maududi" VALUES(78,40,'78:40','ہم نے تم لوگوں کو اُس عذاب سے ڈرا دیا ہے جو قریب آلگا ہے۔ جس روز آدمی وہ سب کچھ دیکھ لے گا جو اس کے ہاتھوں نے آگے بھیجا ہے ، اور کافر پکار اُٹھے گا کہ کاش میں خاک ہوتا۔ ');
INSERT INTO "ur_maududi" VALUES(79,1,'79:1','قسم ہے اُن (فرشتوں کی) جو ڈوب کر کھینچتے ہیں');
INSERT INTO "ur_maududi" VALUES(79,2,'79:2','اور آہستگی سے نکال لے جاتے ہیں');
INSERT INTO "ur_maududi" VALUES(79,3,'79:3','اور (اُن فرشتوں کی جو کائنات میں) تیزی سے تیرتے پھرتے ہیں');
INSERT INTO "ur_maududi" VALUES(79,4,'79:4','پھر (حکم بجا لانے میں) سبقت کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(79,5,'79:5','پھر (احکامِ الٰہی کے مطابق)معاملات کا انتظام چلاتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(79,6,'79:6','جس روز ہلا مارے گا زلزلے کا جھٹکا');
INSERT INTO "ur_maududi" VALUES(79,7,'79:7','اور اس کے پیچھے ایک اور جھٹکا پڑے گا، ');
INSERT INTO "ur_maududi" VALUES(79,8,'79:8','کچھ دل ہوں گے جو اُس روز خوف سے کانپ رہے ہوں گے، ');
INSERT INTO "ur_maududi" VALUES(79,9,'79:9','نگاہیں اُن کی سہمی ہوئی ہوں گی');
INSERT INTO "ur_maududi" VALUES(79,10,'79:10','یہ لوگ کہتے ہیں "کیا واقعی ہم پلٹا کر پھر واپس لائے جائیں گے؟');
INSERT INTO "ur_maududi" VALUES(79,11,'79:11','کیا جب ہم کھوکھلی بوسیدہ ہڈیاں بن چکے ہوں گے؟"');
INSERT INTO "ur_maududi" VALUES(79,12,'79:12','کہنے لگے”یہ واپسی تو پھر بڑے گھاٹے کی ہوگی!“ ');
INSERT INTO "ur_maududi" VALUES(79,13,'79:13','حالانکہ یہ بس اتنا کام ہے کہ ایک زور کی ڈانٹ پڑے گی');
INSERT INTO "ur_maududi" VALUES(79,14,'79:14','اور یکایک یہ کُھلے میدان میں موجود ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(79,15,'79:15',' کیا تمہیں مُوسیٰؑ کے قصّے کی خبر پہنچی ہے؟');
INSERT INTO "ur_maududi" VALUES(79,16,'79:16','جب اس کے ربّ نے اُسے طُویٰ کی مقدّس وادی میں پُکارا تھا');
INSERT INTO "ur_maududi" VALUES(79,17,'79:17','کہ "فرعون کے پاس جا، وہ سرکش ہو گیا ہے');
INSERT INTO "ur_maududi" VALUES(79,18,'79:18','اور اس سے کہہ کیا تو اِس کے لیے تیار ہے کہ پاکیزگی اختیار کرے');
INSERT INTO "ur_maududi" VALUES(79,19,'79:19','اور میں تیرے ربّ کی طرف تیری رہنمائی کروں تو (اُس کا)خوف تیرے اندر پیدا ہو؟“ ');
INSERT INTO "ur_maududi" VALUES(79,20,'79:20','پھر موسیٰؑ نے (فرعون کے پاس جا کر)اُس کی بڑی نشانی دکھائی، ');
INSERT INTO "ur_maududi" VALUES(79,21,'79:21','مگر اُس نے جھٹلا دیا اور نہ مانا');
INSERT INTO "ur_maududi" VALUES(79,22,'79:22','پھر چالبازیاں کرنے کے لیے پلٹا ');
INSERT INTO "ur_maududi" VALUES(79,23,'79:23','اور لوگوں کو جمع کر کے اس نے پکار کر کہا');
INSERT INTO "ur_maududi" VALUES(79,24,'79:24','میں تمہارا سب سے بڑا ربّ ہوں“ ');
INSERT INTO "ur_maududi" VALUES(79,25,'79:25','آخرکار اللہ نے اسے آخرت اور دنیا کے عذاب میں پکڑ لیا');
INSERT INTO "ur_maududi" VALUES(79,26,'79:26','در حقیقت اِس میں بڑی عبرت ہے ہر اُس شخص کے لیے جو ڈرے۔ ');
INSERT INTO "ur_maududi" VALUES(79,27,'79:27',' کیا تم لوگوں کی تخلیق زیادہ سخت کام ہے یا آسمان کی؟ اللہ نے اُس کو بنایا');
INSERT INTO "ur_maududi" VALUES(79,28,'79:28','اُس کی چھت خوب اونچی اٹھائی پھر اُس کا توازن قائم کیا');
INSERT INTO "ur_maududi" VALUES(79,29,'79:29','اور اُس کی رات ڈھانکی اور اُس کا دن نکالا۔ ');
INSERT INTO "ur_maududi" VALUES(79,30,'79:30','اِس کے بعد زمین کو اس نے بچھایا، ');
INSERT INTO "ur_maududi" VALUES(79,31,'79:31','اُس کے اندر اُس کا پانی اور چارہ نکالا، ');
INSERT INTO "ur_maududi" VALUES(79,32,'79:32','اور پہاڑ اس میں گاڑ دیے');
INSERT INTO "ur_maududi" VALUES(79,33,'79:33','سامانِ زیست کے طور پر تمہارے لیے اور تمہارے مویشیوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(79,34,'79:34','پھر جب وہ ہنگامہٴِ عظیم برپا ہوگا، ');
INSERT INTO "ur_maududi" VALUES(79,35,'79:35','جس روز انسان اپنا سب کیا دھرا یاد کرے گا، ');
INSERT INTO "ur_maududi" VALUES(79,36,'79:36','اور ہر دیکھنے والے کے سامنے دوزخ کھول کر رکھ دی جائے گی');
INSERT INTO "ur_maududi" VALUES(79,37,'79:37','تو جس نے سرکشی کی تھی');
INSERT INTO "ur_maududi" VALUES(79,38,'79:38','اور دنیا کی زندگی کو ترجیح دی تھی');
INSERT INTO "ur_maududi" VALUES(79,39,'79:39','دوزخ ہی اس کا ٹھکانا ہوگی');
INSERT INTO "ur_maududi" VALUES(79,40,'79:40','اور جس نے اپنے رب کے سامنے کھڑے ہونے کا خوف کیا تھا اور نفس کو بری خواہشات سے باز رکھا تھا');
INSERT INTO "ur_maududi" VALUES(79,41,'79:41','جنّت اس کا ٹھکانا ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(79,42,'79:42','یہ لوگ تم سے پوچھتے ہیں کہ”آخر وہ گھڑی کب آکر ٹھہرے گی؟“ ');
INSERT INTO "ur_maududi" VALUES(79,43,'79:43','تمہارا کیا کام کہ اس کا وقت بتاؤ');
INSERT INTO "ur_maududi" VALUES(79,44,'79:44','اس کا علم تو اللہ پر ختم ہے');
INSERT INTO "ur_maududi" VALUES(79,45,'79:45','تم صرف خبردار کرنے والے ہو ہر اُس شخص کو جو اُس کا خوف کرے');
INSERT INTO "ur_maududi" VALUES(79,46,'79:46','جس روز یہ لوگ اُسے دیکھ لیں گے تو انہیں یوں محسوس ہو گا کہ (یہ دنیا میں یا حالتِ موت میں) بس ایک دن کے پچھلے پہر یا اگلے پہر تک ٹھہرے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(80,1,'80:1','ترش رو ہوا، اور بے رخی برتی');
INSERT INTO "ur_maududi" VALUES(80,2,'80:2','اِس با ت پر کہ وہ اندھا اُس کے پاس آگیا۔ ');
INSERT INTO "ur_maududi" VALUES(80,3,'80:3','تمہیں کیا خبر، شاید وہ سدھر جائے');
INSERT INTO "ur_maududi" VALUES(80,4,'80:4','یا نصیحت پر دھیان دے، اور نصیحت کرنا اس کے لیے نافع ہو؟');
INSERT INTO "ur_maududi" VALUES(80,5,'80:5','جو شخص بے پروائی برتتا ہے');
INSERT INTO "ur_maududi" VALUES(80,6,'80:6','اس کی طرف تو تم توجہ کرتے ہو');
INSERT INTO "ur_maududi" VALUES(80,7,'80:7','حالانکہ اگر وہ نہ سدھرے تو تم پر اس کی کیا ذمہ داری ہے؟');
INSERT INTO "ur_maududi" VALUES(80,8,'80:8','اور جو خود تمہارے پاس دوڑا آتا ہے');
INSERT INTO "ur_maududi" VALUES(80,9,'80:9','اور وہ ڈر رہا ہوتا ہے');
INSERT INTO "ur_maududi" VALUES(80,10,'80:10','اس سے تم بے رُخی برتتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(80,11,'80:11','ہر گز نہیں، یہ تو ایک نصیحت ہے، ');
INSERT INTO "ur_maududi" VALUES(80,12,'80:12','جس کا جی چاہے اِسے قبول کرے');
INSERT INTO "ur_maududi" VALUES(80,13,'80:13','یہ ایسے صحیفوں میں درج ہے جو مکرم ہیں');
INSERT INTO "ur_maududi" VALUES(80,14,'80:14','بلند مرتبہ ہیں، پاکیزہ ہیں، ');
INSERT INTO "ur_maududi" VALUES(80,15,'80:15','معزّز اور نیک کاتبوں کے');
INSERT INTO "ur_maududi" VALUES(80,16,'80:16','ہاتھوں میں رہتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(80,17,'80:17','لعنت ہو انسان پر، کیسا سخت مُنکرِ حق ہے یہ۔ ');
INSERT INTO "ur_maududi" VALUES(80,18,'80:18','کس چیز سے اللہ نے اِسے پیدا کیا ہے؟');
INSERT INTO "ur_maududi" VALUES(80,19,'80:19','نُطفہ کی ایک بُوند سے۔ اللہ نے اِسے پیدا کیا، پھر اِس کی تقدیر مقرر کی، ');
INSERT INTO "ur_maududi" VALUES(80,20,'80:20','پھر اِس کے لیے زندگی کی راہ آسان کی، ');
INSERT INTO "ur_maududi" VALUES(80,21,'80:21','پھر اِسے موت دی اور قبر میں پہنچایا۔ ');
INSERT INTO "ur_maududi" VALUES(80,22,'80:22','پھر جب چاہے وہ اِسے دوبارہ اُٹھا کھڑا کرے۔ ');
INSERT INTO "ur_maududi" VALUES(80,23,'80:23','ہر گز نہیں، اِس نے وہ فرض ادا نہیں کیا جس کا اللہ نے اِسے حکم دیا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(80,24,'80:24','پھر ذرا انسان اپنی خوراک کو دیکھے۔ ');
INSERT INTO "ur_maududi" VALUES(80,25,'80:25','ہم نے خُوب پانی لُنڈھایا، ');
INSERT INTO "ur_maududi" VALUES(80,26,'80:26','پھر زمین کو عجیب طرح پھاڑا، ');
INSERT INTO "ur_maududi" VALUES(80,27,'80:27','پھر اُس کے اندر اگائے غلے');
INSERT INTO "ur_maududi" VALUES(80,28,'80:28','اور انگور اور ترکاریاں');
INSERT INTO "ur_maududi" VALUES(80,29,'80:29','اور زیتون اور کھجوریں');
INSERT INTO "ur_maududi" VALUES(80,30,'80:30','اور گھنے باغ');
INSERT INTO "ur_maududi" VALUES(80,31,'80:31','اور طرح طرح کے پھل، اور چارے');
INSERT INTO "ur_maududi" VALUES(80,32,'80:32','تمہارے لیے اور تمہارے مویشیوں کے لیے سامانِ زیست کے طور پر۔ ');
INSERT INTO "ur_maududi" VALUES(80,33,'80:33','آخر کار جب وہ کان بہرے کر دینے والی آواز بلند ہوگی ');
INSERT INTO "ur_maududi" VALUES(80,34,'80:34','اُس روز آدمی اپنے بھائی سے بھاگے گا');
INSERT INTO "ur_maududi" VALUES(80,35,'80:35','اور اپنی ماں اور اپنے باپ');
INSERT INTO "ur_maududi" VALUES(80,36,'80:36','اور اپنی بیوی اور اپنی اولاد سے بھاگے گا۔ ');
INSERT INTO "ur_maududi" VALUES(80,37,'80:37','ان میں سے ہر شخص پر اس دن ایسا وقت آپڑے گا کہ اسے اپنے سوا کسی کا ہوش نہ ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(80,38,'80:38','کچھ چہرے اُس روز دمک رہے ہوں گے');
INSERT INTO "ur_maududi" VALUES(80,39,'80:39','ہشاش بشاش اور خوش و خرم ہوں گے');
INSERT INTO "ur_maududi" VALUES(80,40,'80:40','اور کچھ چہروں پر اس روز خاک اڑ رہی ہوگی');
INSERT INTO "ur_maududi" VALUES(80,41,'80:41','اور کلونس چھائی ہوئی ہوگی');
INSERT INTO "ur_maududi" VALUES(80,42,'80:42','یہی کافر و فاجر لوگ ہوں گے');
INSERT INTO "ur_maududi" VALUES(81,1,'81:1','جب سورج لپیٹ دیا جائے گا، ');
INSERT INTO "ur_maududi" VALUES(81,2,'81:2','اور جب تارے بکھر جائیں گے، ');
INSERT INTO "ur_maududi" VALUES(81,3,'81:3','اور جب پہاڑ چلائے جائیں گے، ');
INSERT INTO "ur_maududi" VALUES(81,4,'81:4','اور جب دس مہینے کی حاملہ اُونٹنیاں اپنے حال پر چھوڑ دی جائیں گی، ');
INSERT INTO "ur_maududi" VALUES(81,5,'81:5','اور جب جنگلی جانور سمیٹ کر اکٹھے کر دیے جائیں گے، ');
INSERT INTO "ur_maududi" VALUES(81,6,'81:6','اور جب سمندر بھڑکا دیے جائیں گے، ');
INSERT INTO "ur_maududi" VALUES(81,7,'81:7','اور جب جانیں (جسموں سے) جوڑ دی جائیں گی، ');
INSERT INTO "ur_maududi" VALUES(81,8,'81:8','اور جب زندہ گاڑی ہوئی لڑکی سے پوچھا جائے گا');
INSERT INTO "ur_maududi" VALUES(81,9,'81:9','کہ وہ کس قُصور میں مار ی گئی؟ ');
INSERT INTO "ur_maududi" VALUES(81,10,'81:10','اور جب اعمال نامے کھولے جائیں گے');
INSERT INTO "ur_maududi" VALUES(81,11,'81:11','اور جب آسمان کا پردہ ہٹا دیا جائے گا، ');
INSERT INTO "ur_maududi" VALUES(81,12,'81:12','اور جب جہنم دہکائی جائے گی');
INSERT INTO "ur_maududi" VALUES(81,13,'81:13','اور جب جنّت قریب لے آئی جائے گی، ');
INSERT INTO "ur_maududi" VALUES(81,14,'81:14','اُس وقت ہر شخص کو معلوم ہو جائے گا کہ وہ کیا لے کر آیا ہے');
INSERT INTO "ur_maududi" VALUES(81,15,'81:15','پس نہیں، میں قسم کھاتا ہوں');
INSERT INTO "ur_maududi" VALUES(81,16,'81:16','پلٹنے اور چھپ جانے والے تاروں کی');
INSERT INTO "ur_maududi" VALUES(81,17,'81:17','اور رات کی جبکہ وہ رخصت ہوئی');
INSERT INTO "ur_maududi" VALUES(81,18,'81:18','اور صبح کی جبکہ اس نے سانس لیا، ');
INSERT INTO "ur_maududi" VALUES(81,19,'81:19','یہ فی الواقع ایک بزرگ پیغام بر کا قول ہے ');
INSERT INTO "ur_maududi" VALUES(81,20,'81:20','جو بڑی توانائی رکھتا ہے، عرش والے کے ہاں بلند مرتبہ ہے');
INSERT INTO "ur_maududi" VALUES(81,21,'81:21','وہاں اُس کا حکم مانا جاتا ہے، وہ بااعتماد ہے۔ ');
INSERT INTO "ur_maududi" VALUES(81,22,'81:22','اور (اے اہلِ مکّہ) تمہارا رفیق مجنون نہیں ہے');
INSERT INTO "ur_maududi" VALUES(81,23,'81:23','اُس نے اُس پیغام بر کو روشن اُفق پر دیکھا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(81,24,'81:24','اور وہ غَیب (کے اِس علم کو لوگوں تک پہنچانے) کے معاملہ میں بخیل نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(81,25,'81:25','اور یہ کسی شیطان ِ مردُود کا قول نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(81,26,'81:26','پھر تم لوگ کدھر چلے جا رہے ہو؟');
INSERT INTO "ur_maududi" VALUES(81,27,'81:27','یہ تو سارے جہان والوں کے لیے ایک نصیحت ہے');
INSERT INTO "ur_maududi" VALUES(81,28,'81:28','تم میں سے ہر اُس شخص کے لیے جو راہِ راست پر چلنا چاہتا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(81,29,'81:29','اور تمہارے چاہنے سے کچھ نہیں ہوتا جب تک اللہ ربّ العالمین نہ چاہے۔ ');
INSERT INTO "ur_maududi" VALUES(82,1,'82:1','جب آسمان پھٹ جائے گا');
INSERT INTO "ur_maududi" VALUES(82,2,'82:2','اور جب تارے بکھر جائیں گے');
INSERT INTO "ur_maududi" VALUES(82,3,'82:3','اور جب سمندر پھاڑ دیے جائیں گے، ');
INSERT INTO "ur_maududi" VALUES(82,4,'82:4','اور جب قبریں کھول دی جائیں گی، ');
INSERT INTO "ur_maududi" VALUES(82,5,'82:5','اُس وقت ہر شخص کو اُس کا اگلا پچھلا سب کیا دھرا معلوم ہو جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(82,6,'82:6','اے انسان، کس چیز نے تجھے اپنے اُس رب کریم کی طرف سے دھوکے میں ڈال دیا');
INSERT INTO "ur_maududi" VALUES(82,7,'82:7','جس نے تجھے پیدا کیا، تجھے نک سک سے درست کیا، تجھے متناسب بنایا');
INSERT INTO "ur_maududi" VALUES(82,8,'82:8','اور جس صورت میں چاہا تجھ کو جوڑ کر تیار کیا؟ ');
INSERT INTO "ur_maududi" VALUES(82,9,'82:9','ہر گز نہیں، بلکہ (اصل بات یہ ہے کہ) تم لوگ جزا و سزا کو جھُٹلاتے ہو، ');
INSERT INTO "ur_maududi" VALUES(82,10,'82:10','حالانکہ تم پر نگراں مقرر ہیں');
INSERT INTO "ur_maududi" VALUES(82,11,'82:11','ایسے معزز کاتب');
INSERT INTO "ur_maududi" VALUES(82,12,'82:12','جو تمہارے ہر فعل کو جانتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(82,13,'82:13','یقیناً نیک لوگ مزے میں ہوں گے');
INSERT INTO "ur_maududi" VALUES(82,14,'82:14','اور بے شک بدکار لوگ جہنم میں جائیں گے');
INSERT INTO "ur_maududi" VALUES(82,15,'82:15','جزا کے دن وہ اس میں داخل ہوں گے');
INSERT INTO "ur_maududi" VALUES(82,16,'82:16','اور اُس سے ہرگز غائب نہ ہو سکیں گے');
INSERT INTO "ur_maududi" VALUES(82,17,'82:17','اور تم کیا جانتے ہو کہ وہ جزا کا دن کیا ہے؟');
INSERT INTO "ur_maududi" VALUES(82,18,'82:18','ہاں، تمہیں کیا خبر کہ وہ جزا کا دن کیا ہے؟');
INSERT INTO "ur_maududi" VALUES(82,19,'82:19','یہ وہ دن ہے جب کسی شخص کے لیے کچھ کرنا کسی کے بس میں نہ ہوگا، فیصلہ اُس دن بالکل اللہ کے اختیار میں ہوگا');
INSERT INTO "ur_maududi" VALUES(83,1,'83:1','تباہی ہے ڈنڈی مارنے والوں کے لیے۔ ');
INSERT INTO "ur_maududi" VALUES(83,2,'83:2','جن کا حال یہ ہے کہ جب لوگوں سے لیتے ہیں تو پورا پورا لیتے ہیں');
INSERT INTO "ur_maududi" VALUES(83,3,'83:3','اور جب ان کو ناپ کو یا تول کر دیتے ہیں تو انہیں گھاٹا دیتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(83,4,'83:4','کیا یہ لوگ نہیں سمجھتے کہ ایک بڑے دن ');
INSERT INTO "ur_maududi" VALUES(83,5,'83:5','یہ اٹھا کر لائے جانے والے ہیں؟');
INSERT INTO "ur_maududi" VALUES(83,6,'83:6','اُس دن جبکہ سب لوگ رب العالمین کے سامنے کھڑے ہوں گے');
INSERT INTO "ur_maududi" VALUES(83,7,'83:7','ہر گز نہیں، یقیناً بدکاروں کا نامہٴ اعمال قیدخانے کے دفتر میں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(83,8,'83:8','اور تمہیں کیا معلوم کہ وہ قید خانے کا دفتر کیا ہے؟');
INSERT INTO "ur_maududi" VALUES(83,9,'83:9','ایک کتاب ہے لکھی ہوئی');
INSERT INTO "ur_maududi" VALUES(83,10,'83:10','تباہی ہے اُس روز جھٹلانے والوں کے لیے');
INSERT INTO "ur_maududi" VALUES(83,11,'83:11','جو روز جزا کو جھٹلاتے ہیں');
INSERT INTO "ur_maududi" VALUES(83,12,'83:12','اور اُسے نہیں جھٹلاتا مگر ہر وہ شخص جو حد سے گزر جانے والا بد عمل ہے');
INSERT INTO "ur_maududi" VALUES(83,13,'83:13','اُسے جب ہماری آیات سُنائی جاتی ہیں تو کہتا ہے یہ تو اگلے وقتوں کی کہانیاں ہیں');
INSERT INTO "ur_maududi" VALUES(83,14,'83:14','ہر گز نہیں، بلکہ دراصل اِن لوگوں کے دلوں پر اِن کے بُرے اعمال کا زنگ چڑھ گیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(83,15,'83:15','ہر گز نہیں، بالیقین اُس روز یہ اپنے ربّ کی دید سے محروم رکھے جائیں گے، ');
INSERT INTO "ur_maududi" VALUES(83,16,'83:16','پھر یہ جہنم میں جا پڑیں گے');
INSERT INTO "ur_maududi" VALUES(83,17,'83:17','پھر اِن سے کہا جائے گا کہ یہ وہی چیز ہے جسے تم جھٹلایا کرتے تھے');
INSERT INTO "ur_maududi" VALUES(83,18,'83:18','ہر گز نہیں، بے شک نیک آدمیوں کا نامہٴ اعمال بلند پایہ لوگوں کے دفتر میں ہے');
INSERT INTO "ur_maududi" VALUES(83,19,'83:19','اور تمہیں کیا خبر کہ کیا ہے وہ بلند پایہ لوگوں کا دفتر؟');
INSERT INTO "ur_maududi" VALUES(83,20,'83:20','ایک لکھی ہوئی کتاب ہے');
INSERT INTO "ur_maududi" VALUES(83,21,'83:21','جس کی نگہداشت مقرب فرشتے کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(83,22,'83:22','بے شک نیک لوگ بڑے مزے میں ہوں گے');
INSERT INTO "ur_maududi" VALUES(83,23,'83:23','اونچی مسندوں پر بیٹھے نظارے کر رہے ہوں گے');
INSERT INTO "ur_maududi" VALUES(83,24,'83:24','ان کے چہروں پر تم خوشحالی کی رونق محسوس کرو گے');
INSERT INTO "ur_maududi" VALUES(83,25,'83:25','ان کو نفیس ترین سر بند شراب پلائی جائے گی جس پر مُشک کی مُہر لگی ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(83,26,'83:26','جو لوگ دوسروں پر بازی لے جانا چاہتے ہوں وہ اِس چیز کو حاصل کرنے میں بازی لے جانے کی کوشش کریں');
INSERT INTO "ur_maududi" VALUES(83,27,'83:27','ں۔ اُس شراب میں تسنیم کی آمیزش ہوگی، ');
INSERT INTO "ur_maududi" VALUES(83,28,'83:28','یہ ایک چشمہ ہے جس کے پانی کے ساتھ مقرب لوگ شراب پئیں گے');
INSERT INTO "ur_maududi" VALUES(83,29,'83:29','مجرم لوگ دنیا میں ایمان لانے والوں کا مذاق اڑاتے تھے');
INSERT INTO "ur_maududi" VALUES(83,30,'83:30','جب اُن کے پاس سے گزرتے تو آنکھیں مار مار کر اُن کی طرف اشارے کرتے تھے');
INSERT INTO "ur_maududi" VALUES(83,31,'83:31','پنے گھروں کی طرف پلٹتے تو مزے لیتے ہوئے پلٹتے تھے، ');
INSERT INTO "ur_maududi" VALUES(83,32,'83:32','اور جب انہیں دیکھتے تو کہتے تھے کہ یہ بہکے ہوئے لوگ ہیں، ');
INSERT INTO "ur_maududi" VALUES(83,33,'83:33','حالانکہ وہ اُن پر نگراں بنا کر نہیں بھیجے گئے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(83,34,'83:34','آج ایمان لانے والے کفار پر ہنس رہے ہیں');
INSERT INTO "ur_maududi" VALUES(83,35,'83:35','مسندوں پر بیٹھے ہوئے ان کا حال دیکھ رہے ہیں');
INSERT INTO "ur_maududi" VALUES(83,36,'83:36','مِل گیا نا کافروں کو اُن حرکتوں کا ثواب جو وہ کیا کرتے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(84,1,'84:1','جب آسمان پھٹ جائے گا');
INSERT INTO "ur_maududi" VALUES(84,2,'84:2','اور اپنے ربّ کے فرمان کی تعمیل کرے گا اور اُس کے لیے حق یہی ہے (کہ اپنے ربّ کا حکم مانے)');
INSERT INTO "ur_maududi" VALUES(84,3,'84:3','اور جب زمین پھیلا دی جائے گی ');
INSERT INTO "ur_maududi" VALUES(84,4,'84:4','اور جو کچھ اس کے اندر ہے اُسے باہر پھینک کر خالی ہو جائے گی ');
INSERT INTO "ur_maududi" VALUES(84,5,'84:5','اور اپنے ربّ کے حکم کی تعمیل کرے گی اور اُس کے لیے حق یہی ہے (کہ اس کی تعمیل کرے)۔ ');
INSERT INTO "ur_maududi" VALUES(84,6,'84:6','اے انسان، تُو کشاں کشاں اپنے ربّ کی طرف چلا جا رہا ہے اور اُس سے ملنے والا ہے');
INSERT INTO "ur_maududi" VALUES(84,7,'84:7','پھر جس کا نامہ اعمال اُس کے سیدھے ہاتھ میں دیا گیا');
INSERT INTO "ur_maududi" VALUES(84,8,'84:8','اُس سے ہلکا حساب لیا جائے گا ');
INSERT INTO "ur_maududi" VALUES(84,9,'84:9','اور وہ اپنے لوگوں کی طرف خوش خوش پلٹے گا۔ ');
INSERT INTO "ur_maududi" VALUES(84,10,'84:10','رہا وہ شخص جس کا نامہٴ اعمال اُس کی پیٹھ کے پیچھے دیا جائے گا ');
INSERT INTO "ur_maududi" VALUES(84,11,'84:11','تو وہ موت کو پکارے گا');
INSERT INTO "ur_maududi" VALUES(84,12,'84:12','اور بھڑکتی ہوئی آگ میں جا پڑے گا');
INSERT INTO "ur_maududi" VALUES(84,13,'84:13','وہ اپنے گھر والوں میں مگن تھا۔ ');
INSERT INTO "ur_maududi" VALUES(84,14,'84:14','اُس نے سمجھا تھا کہ اسے کبھی پلٹنا نہیں ہے');
INSERT INTO "ur_maududi" VALUES(84,15,'84:15','پلٹناکیسے نہ تھا، اُس کا ربّ اُس کے کرتُوت دیکھ رہا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(84,16,'84:16','پس نہیں، میں قسم کھاتا ہوں شفق کی');
INSERT INTO "ur_maududi" VALUES(84,17,'84:17','اور رات کی اور جو کچھ وہ سمیٹ لیتی ہے');
INSERT INTO "ur_maududi" VALUES(84,18,'84:18','اور چاند کی جب کہ وہ ماہ کامل ہو جاتا ہے');
INSERT INTO "ur_maududi" VALUES(84,19,'84:19','تم کو ضرور درجہ بدرجہ ایک حالت سے دوسری حالت کی طرف گزرتے چلے جانا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(84,20,'84:20','پھر اِن لوگوں کو کیا ہو گیا ہے کہ یہ ایمان نہیں لاتے');
INSERT INTO "ur_maududi" VALUES(84,21,'84:21','اور جب قرآن اِن کے سامنے پڑ ھا جاتا ہے تو سجدہ نہیں کرتے؟ ');
INSERT INTO "ur_maududi" VALUES(84,22,'84:22','بلکہ یہ منکرین تو الٹا جھٹلاتے ہیں');
INSERT INTO "ur_maududi" VALUES(84,23,'84:23','حالانکہ جو کچھ یہ (اپنے نامہٴ اعمال میں) جمع کر رہے ہیں اللہ اُسے خوب جانتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(84,24,'84:24','لہٰذا اِن کو دردناک عذاب کی بشارت دے دو');
INSERT INTO "ur_maududi" VALUES(84,25,'84:25','البتہ جو لوگ ایمان لے آئے ہیں اور جنہوں نے نیک عمل کیے ہیں ان کے لیے کبھی ختم نہ ہونے والا اجر ہے');
INSERT INTO "ur_maududi" VALUES(85,1,'85:1','قسم ہے مضبُوط قلعوں والے آسمان کی، ');
INSERT INTO "ur_maududi" VALUES(85,2,'85:2','اور اُس دن کی جس کا وعدہ کیا گیا ہے، ');
INSERT INTO "ur_maududi" VALUES(85,3,'85:3','اور دیکھنے والے کی اور دیکھی جانے والی چیز کی ');
INSERT INTO "ur_maududi" VALUES(85,4,'85:4','کہ مارے گئے گڑھے والے');
INSERT INTO "ur_maududi" VALUES(85,5,'85:5','(اُس گڑھے والے) جس میں خوب بھڑکتے ہوئے ایندھن کی آگ تھی');
INSERT INTO "ur_maududi" VALUES(85,6,'85:6','جبکہ وہ اُس گڑھے کے کنارے بیٹھے ہوئے تھے');
INSERT INTO "ur_maududi" VALUES(85,7,'85:7','اور جو کچھ وہ ایمان لانے والوں کے ساتھ کر رہے تھے اُسے دیکھ رہے تھے۔ ');
INSERT INTO "ur_maududi" VALUES(85,8,'85:8','اور اُن اہل ایمان سے اُن کی دشمنی اِس کے سوا کسی وجہ سے نہ تھی کہ وہ اُس خدا پر ایمان لے آئے تھے جو زبردست اور اپنی ذات میں آپ محمود ہے');
INSERT INTO "ur_maududi" VALUES(85,9,'85:9','جو آسمانوں اور زمین کی سلطنت کا مالک ہے، اور وہ خدا سب کچھ دیکھ رہا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(85,10,'85:10','جن لوگوں نے مومن مردوں اور عورتوں پر ظلم و ستم توڑا اور پھر اس سے تائب نہ ہوئے، یقیناً اُن کے لیے جہنّم کا عذاب ہے اور ان کے لیے جَلائے جانے کی سزا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(85,11,'85:11','جو لوگ ایمان لائے اور جنہوں نے نیک عمل کیے، یقیناً اُن کے لیے جنت کے باغ ہیں جن کے نیچے نہریں بہتی ہوں گی، یہ ہے بڑی کامیابی');
INSERT INTO "ur_maududi" VALUES(85,12,'85:12','درحقیقت تمہارے رب کی پکڑ بڑی سخت ہے');
INSERT INTO "ur_maududi" VALUES(85,13,'85:13','وہی پہلی بار پیدا کرتا ہے اور وہی دوبارہ پیدا کرے گا');
INSERT INTO "ur_maududi" VALUES(85,14,'85:14','اور وہ بخشنے والا ہے، محبت کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(85,15,'85:15','عرش کا مالک ہے، بزرگ و برتر ہے');
INSERT INTO "ur_maududi" VALUES(85,16,'85:16','اور جو کچھ چاہے کر ڈالنے والا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(85,17,'85:17','کیا تمہیں لشکروں کی خبر پہنچی ہے؟');
INSERT INTO "ur_maududi" VALUES(85,18,'85:18','فرعون اور ثمود (کے لشکروں)کی؟ ');
INSERT INTO "ur_maududi" VALUES(85,19,'85:19','مگر جنہوں نے کفر کیا ہے وہ جھٹلانے میں لگے ہوئے ہیں');
INSERT INTO "ur_maududi" VALUES(85,20,'85:20','حالانکہ اللہ نے ان کو گھیرے میں لے رکھا ہے');
INSERT INTO "ur_maududi" VALUES(85,21,'85:21','(اُن کے جھٹلانے سے اِس قرآن کا کچھ نہیں بگڑتا) بلکہ یہ قرآن بلند پایہ ہے');
INSERT INTO "ur_maududi" VALUES(85,22,'85:22','اُس لوح میں (نقش ہے) جو محفوظ ہے۔ ');
INSERT INTO "ur_maududi" VALUES(86,1,'86:1','قسم ہے آسمان کی اور رات کو نمودار ہونے والے کی');
INSERT INTO "ur_maududi" VALUES(86,2,'86:2','اور تم کیا جانو کہ وہ رات کو نمودار ہونے والا کیا ہے؟');
INSERT INTO "ur_maududi" VALUES(86,3,'86:3','چمکتا ہوا تارا');
INSERT INTO "ur_maududi" VALUES(86,4,'86:4','کوئی جان ایسی نہیں ہے جس کے اوپر کوئی نگہبان نہ ہو۔ ');
INSERT INTO "ur_maududi" VALUES(86,5,'86:5','پھر ذرا انسان یہی دیکھ لے کہ وہ کِس چیز سے پیدا کیا گیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(86,6,'86:6','ایک اچھلنے والے پانی سے پیدا کیا گیا ہے');
INSERT INTO "ur_maududi" VALUES(86,7,'86:7','جو پیٹھ اور سینے کی ہڈیوں کے درمیان سے نکلتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(86,8,'86:8','یقیناً وہ (خالق) اُسے دوبارہ پیدا کرنے پر قادر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(86,9,'86:9','جس روز پوشیدہ اسرار کی جانچ پڑتال ہوگی ');
INSERT INTO "ur_maududi" VALUES(86,10,'86:10','اُس وقت انسان کے پاس نہ خود اپنا کوئی زور ہوگا اور نہ کوئی اس کی مدد کرنے والا ہوگا');
INSERT INTO "ur_maududi" VALUES(86,11,'86:11','قسم ہے بارش برسانے والے آسمان کی ');
INSERT INTO "ur_maududi" VALUES(86,12,'86:12','اور (نباتات اگتے وقت) پھٹ جانے والی زمین کی');
INSERT INTO "ur_maududi" VALUES(86,13,'86:13','یہ ایک جچی تلی بات ہے');
INSERT INTO "ur_maududi" VALUES(86,14,'86:14','ہنسی مذاق نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(86,15,'86:15','یہ لوگ کچھ چالیں چل رہے ہیں ');
INSERT INTO "ur_maududi" VALUES(86,16,'86:16','اور میں بھی ایک چال چل رہا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(86,17,'86:17','پس چھوڑ دو اے نبیؐ ، اِن کافروں کو اِک ذرا کی ذرا اِن کے حال پر چھوڑ دو۔ ');
INSERT INTO "ur_maududi" VALUES(87,1,'87:1','(اے نبیؐ)اپنے ربِّ برتر کے نام کی تسبیح کرو ');
INSERT INTO "ur_maududi" VALUES(87,2,'87:2','جس نے پیدا کیا اور تناسُب قائم کیا، ');
INSERT INTO "ur_maududi" VALUES(87,3,'87:3','جس نے تقدیر بنائی پھر راہ دکھائی، ');
INSERT INTO "ur_maududi" VALUES(87,4,'87:4','جس نے نباتات اُگائیں ');
INSERT INTO "ur_maududi" VALUES(87,5,'87:5','پھر اُن کو سیاہ کُوڑا کرکٹ بنا دیا۔ ');
INSERT INTO "ur_maududi" VALUES(87,6,'87:6','ہم تمہیں پڑھوا دیں گے، پھر تم نہیں بھولو گے ');
INSERT INTO "ur_maududi" VALUES(87,7,'87:7','سوائے اُس کے جو اللہ چاہے، وہ ظاہر کو بھی جانتا ہے اور جوکچھ پوشیدہ ہے اُس کو بھی۔ ');
INSERT INTO "ur_maududi" VALUES(87,8,'87:8','اور ہم تمہیں آسان طریقے کی سہولت دیتے ہیں');
INSERT INTO "ur_maududi" VALUES(87,9,'87:9','لہٰذا تم نصیحت کرو اگر نصیحت نافع ہو۔ ');
INSERT INTO "ur_maududi" VALUES(87,10,'87:10','جو شخص ڈرتا ہے وہ نصیحت قبول کر لے گا، ');
INSERT INTO "ur_maududi" VALUES(87,11,'87:11','اور اس سے گریز کریگا وہ انتہائی بد بخت');
INSERT INTO "ur_maududi" VALUES(87,12,'87:12','جو بڑی آگ میں جائے گا');
INSERT INTO "ur_maududi" VALUES(87,13,'87:13',' پھر نہ اس میں مرے گا اور نہ جیے گا۔ ');
INSERT INTO "ur_maududi" VALUES(87,14,'87:14','فلاح پا گیا وہ جس نے پاکیزگی اختیار کی ');
INSERT INTO "ur_maududi" VALUES(87,15,'87:15','اور اپنے ربّ کا نام یاد کیا پھر نماز پڑھی۔ ');
INSERT INTO "ur_maududi" VALUES(87,16,'87:16','مگر تم لوگ دنیا کی زندگی کو ترجیح دیتے ہو، ');
INSERT INTO "ur_maududi" VALUES(87,17,'87:17','حالانکہ آخرت بہتر ہے اور باقی رہنے والی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(87,18,'87:18','یہی بات پہلے آئے ہوئے صحیفوں میں بھی کہی گئی تھی');
INSERT INTO "ur_maududi" VALUES(87,19,'87:19','ابراہیمؑ اور موسیٰؑ کے صحیفوں میں۔ ');
INSERT INTO "ur_maududi" VALUES(88,1,'88:1','کیا تمہیں اُس چھا جانے والی آفت کی خبر پہنچی ہے؟ ');
INSERT INTO "ur_maududi" VALUES(88,2,'88:2','کچھ چہرے اُس روز خوف زدہ ہوں گے');
INSERT INTO "ur_maududi" VALUES(88,3,'88:3','سخت مشقت کر رہے ہونگے');
INSERT INTO "ur_maududi" VALUES(88,4,'88:4','تھکے جاتے ہونگے، شدید آگ میں جھلس رہے ہونگے');
INSERT INTO "ur_maududi" VALUES(88,5,'88:5','کھولتے ہوئے چشمے کا پانی انہیں پینے کو دیا جائے گا');
INSERT INTO "ur_maududi" VALUES(88,6,'88:6','خاردار سُوکھی گھاس کے سوا کوئی کھانا اُن کے لیے نہ ہوگا، ');
INSERT INTO "ur_maududi" VALUES(88,7,'88:7','جو نہ موٹا کرے نہ بھوک مٹائے');
INSERT INTO "ur_maududi" VALUES(88,8,'88:8','کچھ چہرے اُس روز با رونق ہوں گے');
INSERT INTO "ur_maududi" VALUES(88,9,'88:9','اپنی کار گزاری پر خوش ہوں گے، ');
INSERT INTO "ur_maududi" VALUES(88,10,'88:10','عالی مقام جنت میں ہوں گے');
INSERT INTO "ur_maududi" VALUES(88,11,'88:11','کوئی بےہودہ بات وہاں نہ سُنیں گے، ');
INSERT INTO "ur_maududi" VALUES(88,12,'88:12','اُس میں چشمے رواں ہونگے');
INSERT INTO "ur_maududi" VALUES(88,13,'88:13','اُس کے اندر اونچی مسندیں ہوں گی');
INSERT INTO "ur_maududi" VALUES(88,14,'88:14','ساغر رکھے ہوئے ہوں گے، ');
INSERT INTO "ur_maududi" VALUES(88,15,'88:15','گاؤ تکیوں کی قطاریں لگی ہوں گی');
INSERT INTO "ur_maududi" VALUES(88,16,'88:16','اور نفیس فرش بچھے ہوئے ہوں گے');
INSERT INTO "ur_maududi" VALUES(88,17,'88:17','(یہ لوگ نہیں مانتے) تو کیا یہ اونٹوں کو نہیں دیکھتے کہ کیسے بنائے گئے؟');
INSERT INTO "ur_maududi" VALUES(88,18,'88:18','آسمان کو نہیں دیکھتے کہ کیسے اٹھایا گیا؟');
INSERT INTO "ur_maududi" VALUES(88,19,'88:19','پہاڑوں کو نہیں دیکھتے کہ کیسے جمائے گئے؟');
INSERT INTO "ur_maududi" VALUES(88,20,'88:20','اور زمین کو نہیں دیکھتے کہ کیسے بچھائی گئی؟ ');
INSERT INTO "ur_maududi" VALUES(88,21,'88:21','اچھا تو (اے نبیؐ) نصیحت کیے جاؤ، تم بس نصیحت ہی کرنے والے ہو');
INSERT INTO "ur_maududi" VALUES(88,22,'88:22','کچھ ان پر جبر کرنے والے نہیں ہو۔ ');
INSERT INTO "ur_maududi" VALUES(88,23,'88:23','البتہ جو شخص منہ موڑے گا اور انکار کرے گا');
INSERT INTO "ur_maududi" VALUES(88,24,'88:24','تو اللہ اس کو بھاری سزا دے گا');
INSERT INTO "ur_maududi" VALUES(88,25,'88:25','اِن لوگوں کو پلٹنا ہماری طرف ہی ہے');
INSERT INTO "ur_maududi" VALUES(88,26,'88:26','پھر اِن کا حساب لینا ہمارے ہی ذمہ ہے');
INSERT INTO "ur_maududi" VALUES(89,1,'89:1','قسم ہے فجر کی');
INSERT INTO "ur_maududi" VALUES(89,2,'89:2','اور دس راتوں کی');
INSERT INTO "ur_maududi" VALUES(89,3,'89:3','اور جفت اور طاق کی');
INSERT INTO "ur_maududi" VALUES(89,4,'89:4','اور رات کی جبکہ وہ رخصت ہو رہی ہو');
INSERT INTO "ur_maududi" VALUES(89,5,'89:5','کیا اِس میں کسی صاحب ِ عقل کے لیے کوئی قسم ہے؟ ');
INSERT INTO "ur_maududi" VALUES(89,6,'89:6',' تم نے دیکھا نہیں کہ تمہارے ربّ نے کیا برتاوٴ کیا');
INSERT INTO "ur_maududi" VALUES(89,7,'89:7','اُونچے ستونوں والے عادِ اِرَم کے ساتھ، ');
INSERT INTO "ur_maududi" VALUES(89,8,'89:8','جن کے مانند کوئی قوم دنیا کے ملکوں میں پیدا نہیں کی گئی تھی؟ ');
INSERT INTO "ur_maududi" VALUES(89,9,'89:9','اور ثمود کے ساتھ جنہوں نے وادی میں چٹانیں تراشی تھیں؟ ');
INSERT INTO "ur_maududi" VALUES(89,10,'89:10','اور میخوں والے فرعون کے ساتھ؟');
INSERT INTO "ur_maududi" VALUES(89,11,'89:11','یہ وہ لوگ تھے جنہوں نے دنیا کے ملکوں میں بڑی سرکشی کی تھی');
INSERT INTO "ur_maududi" VALUES(89,12,'89:12','اور ان میں بہت فساد پھیلایا تھا');
INSERT INTO "ur_maududi" VALUES(89,13,'89:13','آخرکار تمہارے رب نے ان پر عذاب کا کوڑا برسا دیا');
INSERT INTO "ur_maududi" VALUES(89,14,'89:14','حقیقت یہ ہے کہ تمہارا ربّ گھات لگائے ہوئے ہے۔ ');
INSERT INTO "ur_maududi" VALUES(89,15,'89:15',' مگر انسان کا حال یہ ہے کہ اس کا ربّ جب اس کو آزمائش میں ڈالتا ہے اور اُسے عزت اور نعمت دیتا ہے تو وہ کہتا ہے کہ میرے ربّ نے مجھے عزت دار بنا دیا');
INSERT INTO "ur_maududi" VALUES(89,16,'89:16','اور جب وہ اُس کو آزمائش میں ڈالتا ہے اور اُس کا رزق اُس پر تنگ کر دیتا ہے تو وہ کہتا ہے میرے ربّ نے مجھے ذلیل کر دیا۔ ');
INSERT INTO "ur_maududi" VALUES(89,17,'89:17','ہرگز نہیں، بلکہ تم یتیم سے عزّت کا سلوک نہیں کرتے ');
INSERT INTO "ur_maududi" VALUES(89,18,'89:18','اور مسکین کو کھانا کھلانے پر ایک دُوسرے کو نہیں اُکساتے، ');
INSERT INTO "ur_maududi" VALUES(89,19,'89:19','اور میراث کا سارا مال سمیٹ کر کھا جاتے ہو، ');
INSERT INTO "ur_maududi" VALUES(89,20,'89:20','اور مال کی محبت میں بُری طرح گرفتار ہو۔ ');
INSERT INTO "ur_maududi" VALUES(89,21,'89:21','ہرگز نہیں، جب زمین پے در پے کوُٹ کوُٹ کر ریگ زار بنا دی جائے گی');
INSERT INTO "ur_maududi" VALUES(89,22,'89:22','اور تمہارا ربّ جلوہ فرما ہوگا اِس حال میں کہ فرشتے صف در صف کھڑے ہوں گے');
INSERT INTO "ur_maududi" VALUES(89,23,'89:23','اور جہنّم اُس روز سامنے لے آئی جائے گی، اُس دن انسان کو سمجھ آئے گی اور اس وقت اُس کے سمجھنے کا کیا حاصل ؟ ');
INSERT INTO "ur_maududi" VALUES(89,24,'89:24','وہ کہے گا کہ کاش میں نے اپنی اِس زندگی کے لیے کچھ پیشگی سامان کیا ہوتا!');
INSERT INTO "ur_maududi" VALUES(89,25,'89:25','پھر اُس دن اللہ جو عذاب دے گا ویسا عذاب دینے والا کوئی نہیں');
INSERT INTO "ur_maududi" VALUES(89,26,'89:26','اور اللہ جیسا باندھے گا ویسا باندھنے والا کوئی نہیں');
INSERT INTO "ur_maududi" VALUES(89,27,'89:27','(دُوسری طرف ارشاد ہوگا)اے نفسِ مطمئن، ');
INSERT INTO "ur_maududi" VALUES(89,28,'89:28','چل اپنے ربّ کی طرف اِس حال میں کہ تُو (اپنے انجامِ نیک سے) خوش (اور اپنے ربّ کے نزدیک) پسندیدہ ہے');
INSERT INTO "ur_maududi" VALUES(89,29,'89:29','شامل ہو جا میرے (نیک) بندوں میں');
INSERT INTO "ur_maududi" VALUES(89,30,'89:30','اور داخل ہو جا میری جنت میں');
INSERT INTO "ur_maududi" VALUES(90,1,'90:1','نہیں، میں قسم کھاتا ہوں اِس شہر کی ');
INSERT INTO "ur_maududi" VALUES(90,2,'90:2','اور حال یہ ہے کہ (اے نبیؐ) اِس شہر میں تم کو حلال کر لیا گیا ہے، ');
INSERT INTO "ur_maududi" VALUES(90,3,'90:3','اور قسم کھاتا ہوں باپ کی اور اس اولاد کی جو اس سے پیدا ہوئی، ');
INSERT INTO "ur_maududi" VALUES(90,4,'90:4','درحقیقت ہم نے انسان کو مشقّت میں پیدا کیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(90,5,'90:5','کیا اُس نے یہ سمجھ رکھا ہے کہ اُس پر کوئی قابو نہ پا سکے گا؟ ');
INSERT INTO "ur_maududi" VALUES(90,6,'90:6','کہتا ہے کہ میں نے ڈھیروں مال اُڑا دیا۔ ');
INSERT INTO "ur_maududi" VALUES(90,7,'90:7','کیا وہ سمجھتا ہے کہ کسی نے اُس کو نہیں دیکھا؟ ');
INSERT INTO "ur_maududi" VALUES(90,8,'90:8','کیا ہم نے اُسے دو آنکھیں');
INSERT INTO "ur_maududi" VALUES(90,9,'90:9','اور ایک زبان اور دو ہونٹ نہیں دیے؟ ');
INSERT INTO "ur_maududi" VALUES(90,10,'90:10','اور دونوں نمایاں راستے اُسے (نہیں) دکھا دیے؟ ');
INSERT INTO "ur_maududi" VALUES(90,11,'90:11','مگر اس نے دشوار گزار گھاٹی سے گزرنے کی ہمّت نہ کی۔ ');
INSERT INTO "ur_maududi" VALUES(90,12,'90:12','اور تم کیا جانو کہ کیا ہے وہ دشوار گزار گھاٹی؟');
INSERT INTO "ur_maududi" VALUES(90,13,'90:13','کسی گردن کو غلامی سے چھڑانا');
INSERT INTO "ur_maududi" VALUES(90,14,'90:14','یا فاقے کے دن');
INSERT INTO "ur_maududi" VALUES(90,15,'90:15','کسی قریبی یتیم');
INSERT INTO "ur_maududi" VALUES(90,16,'90:16','یا خاک نشین مسکین کو کھانا کھلانا۔ ');
INSERT INTO "ur_maududi" VALUES(90,17,'90:17','پھر (اِس کے ساتھ یہ کہ) آدمی اُن لوگوں میں شامل ہو جو ایمان لائے اور جنہوں نے ایک دُوسرے کو صبر اور (خلقِ خدا پر) رحم کی تلقین کی۔ ');
INSERT INTO "ur_maududi" VALUES(90,18,'90:18','یہ لوگ ہیں دائیں بازو والے');
INSERT INTO "ur_maududi" VALUES(90,19,'90:19','اور جنہوں نے ہماری آیات کو ماننے سے انکار کیا وہ بائیں بازو والے ہیں، ');
INSERT INTO "ur_maududi" VALUES(90,20,'90:20','ان پر آگ چھائی ہوئی ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(91,1,'91:1','سُورج اور اُس کی دُھوپ کی قسم');
INSERT INTO "ur_maududi" VALUES(91,2,'91:2','اور چاند کی قسم جبکہ وہ اُس کے پیچھے آتا ہے');
INSERT INTO "ur_maududi" VALUES(91,3,'91:3','اور دن کی قسم جبکہ وہ (سورج کو) نمایاں کر دیتا ہے');
INSERT INTO "ur_maududi" VALUES(91,4,'91:4','اور رات کی قسم جبکہ وہ (سُورج کو) ڈھانک لیتی ہے، ');
INSERT INTO "ur_maududi" VALUES(91,5,'91:5','اور آسمان کی اور اُس ذات کی قسم جس نے اُسے قائم کیا، ');
INSERT INTO "ur_maududi" VALUES(91,6,'91:6','اور زمین کی اور اُس ذات کی قسم جس نے اُسے بچھایا');
INSERT INTO "ur_maududi" VALUES(91,7,'91:7','اور نفسِ انسانی کی اور اُس ذات کی قسم جس نے اُسے ہموار کیا ');
INSERT INTO "ur_maududi" VALUES(91,8,'91:8','پھر اُس کی بدی اوراُس کی پرہیزگاری اس پر الہام کر دی، ');
INSERT INTO "ur_maududi" VALUES(91,9,'91:9','یقیناً فلاح پا گیا وہ جس نے نفس کا تزکیہ کیا');
INSERT INTO "ur_maududi" VALUES(91,10,'91:10','اور نامراد ہوا وہ جس نے اُس کو دبا دیا۔ ');
INSERT INTO "ur_maududi" VALUES(91,11,'91:11',' ثمُود نے اپنی سرکشی کی بنا پر جھُٹلایا۔ ');
INSERT INTO "ur_maududi" VALUES(91,12,'91:12','جب اُس قوم کا سب سے زیادہ شقی آدمی بپھر کر اٹھا');
INSERT INTO "ur_maududi" VALUES(91,13,'91:13','تو اللہ کے رسُول نے اُن لوگوں سے کہا کہ خبردار، اللہ کی اُونٹنی کو (ہاتھ نہ لگانا) اور اُس کے پانی پینے (میں مانع نہ ہونا)۔ ');
INSERT INTO "ur_maududi" VALUES(91,14,'91:14','مگر انہوں نے اُس کی بات کو جھُوٹا قرار دیا ااور اُونٹنی کو مار ڈالا۔ آخر کار اُن کے گناہ کی پاداش میں ان کے ربّ نے ان پر ایسی آفت توڑی کہ ایک ساتھ سب کو پیوندِ خاک کر دیا');
INSERT INTO "ur_maududi" VALUES(91,15,'91:15','اور اسے (اپنے اس فعل کے) کسی بُرے نتیجے کا کوئی خوف نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(92,1,'92:1','قسم ہے رات کی جبکہ وہ چھا جائے');
INSERT INTO "ur_maududi" VALUES(92,2,'92:2','اور دن کی جبکہ وہ روشن ہو');
INSERT INTO "ur_maududi" VALUES(92,3,'92:3','اور اُس ذات کی جس نے نر اور مادہ کو پیدا کیا');
INSERT INTO "ur_maududi" VALUES(92,4,'92:4','درحقیقت تم لوگوں کی کوششیں مختلف قسم کی ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(92,5,'92:5','تو جس نے (راہ خدا میں) مال دیا اور (خدا کی نافرمانی سے) پرہیز کیا');
INSERT INTO "ur_maududi" VALUES(92,6,'92:6','اور بھلائی کو سچ مانا، ');
INSERT INTO "ur_maududi" VALUES(92,7,'92:7','اس کو ہم آسان راستے کے لیے سہُولت دیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(92,8,'92:8','اور جس نے بخل کیا اور (اپنے خدا سے) بے نیازی برتی');
INSERT INTO "ur_maududi" VALUES(92,9,'92:9','اور بَھلائی کو جھُٹلایا، ');
INSERT INTO "ur_maududi" VALUES(92,10,'92:10','اس کو ہم سخت راستے کے لیے سہُولت دیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(92,11,'92:11','اور اُس کا مال آخر اُس کے کس کام آئے گا جبکہ وہ ہلاک ہو جائے؟ ');
INSERT INTO "ur_maududi" VALUES(92,12,'92:12','بے شک راستہ بتانا ہمارے ذمّہ ہے، ');
INSERT INTO "ur_maududi" VALUES(92,13,'92:13','اور درحقیقت آخرت اور دنیا، دونوں کے ہم ہی مالک ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(92,14,'92:14','پس میں نے تم کو خبردار کر دیا ہے بھڑکتی ہوئی آگ سے');
INSERT INTO "ur_maududi" VALUES(92,15,'92:15','اُس میں نہیں جھلسے گا مگر وہ انتہائی بد بخت');
INSERT INTO "ur_maududi" VALUES(92,16,'92:16','جس نے جھٹلایا اور منہ پھیرا');
INSERT INTO "ur_maududi" VALUES(92,17,'92:17','اور اُس سے دور رکھا جائیگا وہ نہایت پرہیزگار');
INSERT INTO "ur_maududi" VALUES(92,18,'92:18','جو پاکیزہ ہونے کی خاطر اپنا مال دیتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(92,19,'92:19','اُس پر کسی کا کوئی احسان نہیں ہے جس کا بدلہ اُسے دینا ہو');
INSERT INTO "ur_maududi" VALUES(92,20,'92:20','وہ تو صرف اپنے ربِّ برتر کی رضا جوئی کے لیے یہ کام کرتا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(92,21,'92:21','اور ضرور وہ (اُس سے) خوش ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(93,1,'93:1','قسم ہے روزِ روشن کی ');
INSERT INTO "ur_maududi" VALUES(93,2,'93:2','اور رات کی جبکہ وہ سکون کے ساتھ طاری ہو جائے ');
INSERT INTO "ur_maududi" VALUES(93,3,'93:3','(اے نبیؐ) تمہارے ربّ نے تم کو ہر گز نہیں چھوڑا اور نہ وہ ناراض ہوا۔ ');
INSERT INTO "ur_maududi" VALUES(93,4,'93:4','اور یقیناً تمہارے لیے بعد کا دور پہلے دور سے بہتر ہے، ');
INSERT INTO "ur_maududi" VALUES(93,5,'93:5','اور عنقریب تمہارا ربّ تم کو اتنا دے گا کہ تم خوش ہو جاوٴ گے۔ ');
INSERT INTO "ur_maududi" VALUES(93,6,'93:6','کیا اُس نے تم کو یتیم نہیں پایا اور پھر ٹھِکانا فراہم کیا؟ ');
INSERT INTO "ur_maududi" VALUES(93,7,'93:7','اور تمہیں ناواقفِ راہ پایا اور پھر ہدایت بخشی۔ ');
INSERT INTO "ur_maududi" VALUES(93,8,'93:8','اور تمہیں نادار پایا اور پھر مالدار کر دیا۔ ');
INSERT INTO "ur_maududi" VALUES(93,9,'93:9','لہٰذا یتیم پر سختی نہ کرو، ');
INSERT INTO "ur_maududi" VALUES(93,10,'93:10','اور سائل کو نہ جھِڑکو، ');
INSERT INTO "ur_maududi" VALUES(93,11,'93:11','اور اپنے ربّ کی نعمت کا اظہار کرو۔ ');
INSERT INTO "ur_maududi" VALUES(94,1,'94:1','(اے نبیؐ) کیا ہم نے تمہارا سینہ تمہارے لیے کھول نہیں دیا؟ ');
INSERT INTO "ur_maududi" VALUES(94,2,'94:2','اور تم پر سے وہ بھاری بوجھ اتار دیا');
INSERT INTO "ur_maududi" VALUES(94,3,'94:3','جو تمہاری کمر توڑ ڈال رہا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(94,4,'94:4','اور تمہاری خاطر تمہارے ذِکر کا آوازہ بلند کردیا۔ ');
INSERT INTO "ur_maududi" VALUES(94,5,'94:5','پس حقیقت یہ ہے کہ تنگی کے ساتھ فراخی بھی ہے');
INSERT INTO "ur_maududi" VALUES(94,6,'94:6','بے شک تنگی کے ساتھ فراخی بھی ہے۔ ');
INSERT INTO "ur_maududi" VALUES(94,7,'94:7','لہٰذا جب تم فارغ ہو تو عبادت کی مشقت میں لگ جاؤ');
INSERT INTO "ur_maududi" VALUES(94,8,'94:8','اور اپنے ربّ ہی کی طرف راغب ہو۔ ');
INSERT INTO "ur_maududi" VALUES(95,1,'95:1','قسم ہے انجیر اور زیتون کی ');
INSERT INTO "ur_maududi" VALUES(95,2,'95:2','اور طورِ سینا ');
INSERT INTO "ur_maududi" VALUES(95,3,'95:3','اور اِس پرامن شہر (مکہ) کی');
INSERT INTO "ur_maududi" VALUES(95,4,'95:4','ہم نے انسان کو بہترین ساخت پر پیدا کیا، ');
INSERT INTO "ur_maududi" VALUES(95,5,'95:5','پھر اُسے اُلٹا پھیر کر ہم نے سب نِیچوں سے نیچ کر دیا، ');
INSERT INTO "ur_maududi" VALUES(95,6,'95:6','سوائے اُن لوگوں کے جو ایمان لائے اور نیک عمل کرتے رہے کہ ان کے لیے کبھی ختم نہ ہونے والا اجر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(95,7,'95:7','پس (اے نبیؐ)اِس کے بعد کون جزا و سزا کے معاملہ میں تم کو جھُٹلا سکتا ہے؟ ');
INSERT INTO "ur_maududi" VALUES(95,8,'95:8','کیا اللہ سب حاکموں سے بڑا حاکم نہیں ہے؟ ');
INSERT INTO "ur_maududi" VALUES(96,1,'96:1','پڑھو (اے نبیؐ) اپنے ربّ کے نام کے ساتھ جس نے پیدا کیا، ');
INSERT INTO "ur_maududi" VALUES(96,2,'96:2','جمے ہوئے خون کے ایک لوتھڑے سے انسان کی تخلیق کی۔ ');
INSERT INTO "ur_maududi" VALUES(96,3,'96:3','پڑھو، اور تمہارا رب بڑا کریم ہے');
INSERT INTO "ur_maududi" VALUES(96,4,'96:4','جس نے قلم کے ذریعہ سے علم سکھایا، ');
INSERT INTO "ur_maududi" VALUES(96,5,'96:5','انسان کو وہ علم دیا جسے وہ نہ جانتا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(96,6,'96:6','ہرگز نہیں، انسان سرکشی کرتا ہے');
INSERT INTO "ur_maududi" VALUES(96,7,'96:7','اِس بنا پر کہ وہ اپنے آپ کو بے نیاز دیکھتا ہے ');
INSERT INTO "ur_maududi" VALUES(96,8,'96:8','(حالانکہ) پلٹنا یقیناً تیرے ربّ ہی کی طرف ہے۔ ');
INSERT INTO "ur_maududi" VALUES(96,9,'96:9','تم نے دیکھا اُس شخص کو');
INSERT INTO "ur_maududi" VALUES(96,10,'96:10','جو ایک بندے کو منع کرتا ہے جبکہ وہ نماز پڑھتا ہو؟ ');
INSERT INTO "ur_maududi" VALUES(96,11,'96:11','تمہارا کیا خیال ہے اگر (وہ بندہ) راہ راست پر ہو');
INSERT INTO "ur_maududi" VALUES(96,12,'96:12','یا پرہیزگاری کی تلقین کرتا ہو؟');
INSERT INTO "ur_maududi" VALUES(96,13,'96:13','تمہارا کیا خیال ہے اگر (یہ منع کرنے والا شخص حق کو) جھٹلاتا اور منہ موڑتا ہو؟');
INSERT INTO "ur_maududi" VALUES(96,14,'96:14','کیا وہ نہیں جانتا کہ اللہ دیکھ رہا ہے؟ ');
INSERT INTO "ur_maududi" VALUES(96,15,'96:15','ہرگز نہیں، اگر وہ باز نہ آیا تو ہم اس کی پیشانی کے بال پکڑ کر اُسے کھینچیں گے');
INSERT INTO "ur_maududi" VALUES(96,16,'96:16','اُس پیشانی کو جو جھُوٹی اور سخت خطاکار ہے۔ ');
INSERT INTO "ur_maududi" VALUES(96,17,'96:17','وہ بُلالے اپنے حامیوں کی ٹولی کو، ');
INSERT INTO "ur_maududi" VALUES(96,18,'96:18','ہم بھی عذاب کے فرشتوں کو بُلا لیں گے۔ ');
INSERT INTO "ur_maududi" VALUES(96,19,'96:19','ہرگزنہیں، اُس کی بات نہ مانو اور سجدہ کرو اور (اپنے ربّ کا) قرب حاصل کرو۔ ');
INSERT INTO "ur_maududi" VALUES(97,1,'97:1','ہم نے اِس (قرآن)کو شبِ قدر میں نازل کیا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(97,2,'97:2','اور تم کیا جانو کہ شب قدر کیا ہے؟');
INSERT INTO "ur_maududi" VALUES(97,3,'97:3','شبِ قدر ہزار مہینوں سے زیادہ بہتر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(97,4,'97:4','فرشتے اور رُوح اُس میں اپنے ربّ کے اِذن سے ہر حکم لے کر اُترتے ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(97,5,'97:5','وہ رات سراسر سلامتی ہے طلوعِ فجر تک۔ ');
INSERT INTO "ur_maududi" VALUES(98,1,'98:1','اہلِ کتاب اور مشرکین میں سے جو لوگ کافر تھے (وہ اپنے کُفر سے) باز آنے والے نہ تھے جب تک کہ ان کے پاس دلیلِ روشن نہ آجائے ');
INSERT INTO "ur_maududi" VALUES(98,2,'98:2','(یعنی) اللہ کی طرف سے ایک رسُول جو پاک صحیفے پڑھ کر سُنائے ');
INSERT INTO "ur_maududi" VALUES(98,3,'98:3','جن میں بالکل راست اور درست تحریریں لکھی ہوئی ہوں');
INSERT INTO "ur_maududi" VALUES(98,4,'98:4','پہلے جن لوگوں کو کتاب دی گئی تھی اُن میں تفرقہ برپا نہیں ہوا مگر اِس کے بعد کہ اُن کے پاس (راہِ راست کا) بیانِ واضح آچکا تھا۔ ');
INSERT INTO "ur_maududi" VALUES(98,5,'98:5','اور اُن کو اِس کے سوا کوئی حکم نہیں دیا گیا تھا کہ اللہ کی بندگی کریں اپنے دین کو اُس کے لیے خالص کر کے، بالکل یَکسُو ہو کر، اور نماز قائم کریں اور زکوٰة دیں۔ یہی نہایت صحیح و درست دین ہے۔ ');
INSERT INTO "ur_maududi" VALUES(98,6,'98:6','اہلِ کتاب اور مشرکین میں سے جن لوگوں نے کُفر کیا ہے وہ یقیناً جہنّم کی آگ میں جائیں گے اور ہمیشہ اس میں رہیں گے ، یہ لوگ بدترینِ خلائق ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(98,7,'98:7','جو لوگ ایمان لے آئے اور جنہوں نے نیک عمل کیے ، وہ یقیناً بہترین خلائق ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(98,8,'98:8','ن کی جزا اُن کے ربّ کے ہاں دائمی قیام کی جنّتیں ہیں جن کے نیچے نہریں بہ رہی ہوں گی، وہ ان میں ہمیشہ ہمیشہ رہیں گے۔ اللہ ان سے راضی ہوا اور وہ اللہ سے راضی ہوئے۔ یہ کچھ ہے اُس شخص کے لیے جس نے اپنے ربّ کا خوف کیا ہو۔ ');
INSERT INTO "ur_maududi" VALUES(99,1,'99:1','جب زمین اپنی پُوری شدّت کے ساتھ ہلا ڈالی جائے گی، ');
INSERT INTO "ur_maududi" VALUES(99,2,'99:2','اور زمین اپنے اندر کے سارے بوجھ نکال کر باہر ڈال دے گی، ');
INSERT INTO "ur_maududi" VALUES(99,3,'99:3','اور انسان کہے گا کہ یہ اِس کو کیا ہو رہا ہے، ');
INSERT INTO "ur_maududi" VALUES(99,4,'99:4','اُس روز وہ اپنے (اُوپر گزرے ہوئے) حالات بیان کرے گی، ');
INSERT INTO "ur_maududi" VALUES(99,5,'99:5','کیونکہ تیرے رب نے اُسے (ایساکرنے کا) حکم دیا ہوگا');
INSERT INTO "ur_maududi" VALUES(99,6,'99:6','اُس روز لوگ متفرق حالت میں پلٹیں گے تاکہ اُن کے اعمال اُن کو دکھائے جائیں۔ ');
INSERT INTO "ur_maududi" VALUES(99,7,'99:7','پھر جس نے ذرہ برابر نیکی کی ہوگی وہ اس کو دیکھ لے گا');
INSERT INTO "ur_maududi" VALUES(99,8,'99:8','اور جس نے ذرّہ برابر بدی کی ہوگی وہ اس کو دیکھ لے گا۔ ');
INSERT INTO "ur_maududi" VALUES(100,1,'100:1','قسم ہے اُن (گھوڑوں) کی جو پھُنکارے مارتے ہوئے دَوڑتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(100,2,'100:2','پھر (اپنی ٹاپوں سے) چِنگاریاں جھاڑتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(100,3,'100:3','پھر صبح سویرے چھاپہ مارتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(100,4,'100:4','پھر اس موقع پر گرد و غبار اڑاتے ہیں');
INSERT INTO "ur_maududi" VALUES(100,5,'100:5','پھر اِسی حالت میں کسی مجمع کے اندر جا گھستے ہیں');
INSERT INTO "ur_maududi" VALUES(100,6,'100:6','حقیقت یہ ہے کہ انسان اپنے ربّ کا بڑا ناشکرا ہے، ');
INSERT INTO "ur_maududi" VALUES(100,7,'100:7','اور وہ خود اِس پر گواہ ہے، ');
INSERT INTO "ur_maududi" VALUES(100,8,'100:8','اور وہ مال و دولت کی محبت میں بُری طرح مُبتلا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(100,9,'100:9','تو کیا وہ اُس وقت کو نہیں جانتا جب قبروں میں جو کچھ (مدفون) ہے اُسے نکال لیا جائے گا، ');
INSERT INTO "ur_maududi" VALUES(100,10,'100:10','اور سینوں میں جو کچھ (مخفی) ہے اُسے بر آمد کر کے اس کی جانچ پڑتال کی جائے گی؟ ');
INSERT INTO "ur_maududi" VALUES(100,11,'100:11','یقیناً اُن کا ربّ اُس روز اُن سے خوب باخبر ہوگا۔ ');
INSERT INTO "ur_maududi" VALUES(101,1,'101:1','عظیم حادثہ! ');
INSERT INTO "ur_maududi" VALUES(101,2,'101:2','کیا ہے وہ عظیم حادثہ؟');
INSERT INTO "ur_maududi" VALUES(101,3,'101:3','تم کیا جانو کہ وہ عظیم حادثہ کیا ہے؟');
INSERT INTO "ur_maududi" VALUES(101,4,'101:4','وہ دن جب لوگ بکھرے ہوئے پروانوں کی طرح');
INSERT INTO "ur_maududi" VALUES(101,5,'101:5','اور پہاڑ رنگ برنگ کے دُھنکے ہوئے اُون کی طرح ہوں گے۔ ');
INSERT INTO "ur_maududi" VALUES(101,6,'101:6','پھر جس کے پلڑے بھاری ہوں گے');
INSERT INTO "ur_maududi" VALUES(101,7,'101:7','وہ دل پسند عیش میں ہوگا');
INSERT INTO "ur_maududi" VALUES(101,8,'101:8','اور جس کے پلڑے ہلکے ہوں گے ');
INSERT INTO "ur_maududi" VALUES(101,9,'101:9','اس کی جائے قرار گہری کھائی ہوگی۔ ');
INSERT INTO "ur_maududi" VALUES(101,10,'101:10','اور تمہیں کیا خبر کہ وہ کیا چیز ہے؟');
INSERT INTO "ur_maududi" VALUES(101,11,'101:11','بھڑکتی ہوئی آگ۔ ');
INSERT INTO "ur_maududi" VALUES(102,1,'102:1','تم لوگوں کو زیادہ سے زیادہ اور ایک دُوسرے سے بڑھ کر دنیا حاصل کرنے کی دُھن نے غفلت میں ڈال رکھا ہے ');
INSERT INTO "ur_maududi" VALUES(102,2,'102:2','یہاں تک کہ (اِسی فکر میں) تم لبِ گور تک پہنچ جاتے ہو۔ ');
INSERT INTO "ur_maududi" VALUES(102,3,'102:3','ہرگز نہیں، عنقریب تم کو معلوم ہو جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(102,4,'102:4','پھر (سن لو کہ) ہرگز نہیں، عنقریب تم کو معلوم ہو جائے گا');
INSERT INTO "ur_maududi" VALUES(102,5,'102:5','ہرگز نہیں، اگر تم یقینی علم کی حیثیت سے (اِس روش کے انجام کو) جانتے ہوتے (تو تمہارا یہ طرز عمل نہ ہوتا)');
INSERT INTO "ur_maududi" VALUES(102,6,'102:6','تم دوزخ دیکھ کر رہو گے');
INSERT INTO "ur_maududi" VALUES(102,7,'102:7','پھر (سن لو کہ) تم بالکل یقین کے ساتھ اُسے دیکھ لو گے');
INSERT INTO "ur_maududi" VALUES(102,8,'102:8','پھر ضرور اُس روز تم سے اِن نعمتوں کے بارے میں جواب طلبی کی جائے گی۔ ');
INSERT INTO "ur_maududi" VALUES(103,1,'103:1','زمانے کی قسم');
INSERT INTO "ur_maududi" VALUES(103,2,'103:2','انسان درحقیقت بڑے خسارے میں ہے');
INSERT INTO "ur_maududi" VALUES(103,3,'103:3','سوائے اُن لوگوں کے جو ایمان لائے، اور نیک اعمال کرتے رہے، اور ایک دوسرے کو حق کی نصیحت اور صبر کی تلقین کرتے رہے۔ ');
INSERT INTO "ur_maududi" VALUES(104,1,'104:1','تباہی ہے ہر اُس شخص کے لیے جو (مُنہ در مُنہ) لوگوں پر طعن اور (پیٹھ پیچھے) بُرائیاں کرنے کا خوگر ہے۔ ');
INSERT INTO "ur_maududi" VALUES(104,2,'104:2','جس نے مال جمع کیا اور اُسے گِن گِن کر رکھا۔ ');
INSERT INTO "ur_maududi" VALUES(104,3,'104:3','وہ سمجھتا ہے کہ اُس کا مال ہمیشہ اُس کے پاس رہے گا۔ ');
INSERT INTO "ur_maududi" VALUES(104,4,'104:4','ہرگز نہیں، وہ شخص تو چَکنا چُور کر دینے والی جگہ میں پھینک دیا جائے گا۔ ');
INSERT INTO "ur_maududi" VALUES(104,5,'104:5','اور تم کیا جانو کہ کیا ہے وہ چکنا چور کر دینے والی جگہ؟');
INSERT INTO "ur_maududi" VALUES(104,6,'104:6','اللہ کی آگ، خُوب بھڑکائی ہوئی');
INSERT INTO "ur_maududi" VALUES(104,7,'104:7','جو دلوں تک پہنچے گی۔ ');
INSERT INTO "ur_maududi" VALUES(104,8,'104:8','وہ اُن پر ڈھانک کر بند کر دی جائے گی ');
INSERT INTO "ur_maududi" VALUES(104,9,'104:9','(اِس حالت میں کہ وہ) اُونچے اُونچے ستونوں میں (گھِرے ہوئے ہوں گے)۔ ');
INSERT INTO "ur_maududi" VALUES(105,1,'105:1','تم نے دیکھا نہیں کہ تمہارے ربّ نے ہاتھی والوں کے ساتھ کیا کیا؟ ');
INSERT INTO "ur_maududi" VALUES(105,2,'105:2','کیا اُس نے اُن کی تدبیر کو اَکارَت نہیں کر دیا؟ ');
INSERT INTO "ur_maududi" VALUES(105,3,'105:3','اور اُن پر پرندوں کے جھُنڈ کے جھُنڈ بھیج دیے ');
INSERT INTO "ur_maududi" VALUES(105,4,'105:4','جو اُن پر پکّی ہوئی مَٹّی کے پتھر پھینک رہے تھے، ');
INSERT INTO "ur_maududi" VALUES(105,5,'105:5','پھر اُن کا یہ حال کر دیا جیسے جانوروں کا کھایا ہوا بھوسا۔ ');
INSERT INTO "ur_maududi" VALUES(106,1,'106:1','چونکہ قریش مانوس ہوئے ');
INSERT INTO "ur_maududi" VALUES(106,2,'106:2','(یعنی) جاڑے اور گرمی کے سفروں سے مانوس، ');
INSERT INTO "ur_maududi" VALUES(106,3,'106:3','لہٰذا اُن کو چاہیے کہ اِس گھر کے ربّ کی عبادت کریں ');
INSERT INTO "ur_maududi" VALUES(106,4,'106:4','جس نے اُنہیں بھُوک سے بچا کر کھانے کو دیا اور خوف سے بچا کر امن عطا کیا۔ ');
INSERT INTO "ur_maududi" VALUES(107,1,'107:1','تم نے دیکھا اُس شخص کو جو آخرت کی جزا و سزا کو جھُٹلاتا ہے؟ ');
INSERT INTO "ur_maududi" VALUES(107,2,'107:2','وہی تو ہے جو یتیم کو دھکّے دیتا ہے، ');
INSERT INTO "ur_maududi" VALUES(107,3,'107:3','اور مسکین کا کھانا دینے پر نہیں اُکساتا۔ ');
INSERT INTO "ur_maududi" VALUES(107,4,'107:4','پھر تباہی ہے اُن نماز پڑھنے والوں کے لیے ');
INSERT INTO "ur_maududi" VALUES(107,5,'107:5','جو اپنی نماز سے غفلت برتتے ہیں، ');
INSERT INTO "ur_maududi" VALUES(107,6,'107:6','جو ریا کاری کرتے ہیں ');
INSERT INTO "ur_maududi" VALUES(107,7,'107:7','اور معمولی ضرورت کی چیزیں (لوگوں کو)دینے سے گریز کرتے ہیں');
INSERT INTO "ur_maududi" VALUES(108,1,'108:1','(اے نبیؐ) ہم نے تمہیں کوثر عطا کر دیا۔ ');
INSERT INTO "ur_maududi" VALUES(108,2,'108:2','پس تم اپنے ربّ ہی کے لیے نماز پڑھو اور قربانی کرو۔ ');
INSERT INTO "ur_maududi" VALUES(108,3,'108:3','تمہارا دُشمن ہی جَڑ کٹا ہے۔ ');
INSERT INTO "ur_maududi" VALUES(109,1,'109:1','کہہ دو کہ اے کافرو، ');
INSERT INTO "ur_maududi" VALUES(109,2,'109:2','میں اُن کی عبادت نہیں کرتا جن کی عبادت تم کرتے ہو، ');
INSERT INTO "ur_maududi" VALUES(109,3,'109:3','اور نہ تم اُس کی عبادت کرنے والے ہو جس کی عبادت میں کرتا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(109,4,'109:4','اور نہ میں اُن کی عبادت کرنے والا ہوں جن کی عبادت تم نے کی ہے');
INSERT INTO "ur_maududi" VALUES(109,5,'109:5','اور نہ تم اُس کی عبادت کرنے والے ہو جس کی عبادت میں کرتا ہوں۔ ');
INSERT INTO "ur_maududi" VALUES(109,6,'109:6','تمہارے لیے تمہارا دین ہے اور میرے لیے میرا دین۔ ');
INSERT INTO "ur_maududi" VALUES(110,1,'110:1','جب اللہ کی مدد آجائے اور فتح نصیب ہو جائے ');
INSERT INTO "ur_maududi" VALUES(110,2,'110:2','اور (اے نبیؐ) تم دیکھ لو کہ لوگ فوج در فوج اللہ کے دین میں داخل ہو رہے ہیں ');
INSERT INTO "ur_maududi" VALUES(110,3,'110:3','تو اپنے رب کی حمد کے ساتھ اُس کی تسبیح کرو، اور اُس سے مغفرت کی دُعا مانگو، بے شک وہ بڑا توبہ قبول کرنے والا ہے');
INSERT INTO "ur_maududi" VALUES(111,1,'111:1','ٹوٹ گئے ابو لہب کے ہاتھ اور نامراد ہوگیا وہ۔ ');
INSERT INTO "ur_maududi" VALUES(111,2,'111:2','اُس کا مال اور جو کچھ اس نے کمایا وہ اُس کے کسی کام نہ آیا۔ ');
INSERT INTO "ur_maududi" VALUES(111,3,'111:3','ضرور وہ شعلہ زن آگ میں ڈالا جائے گا');
INSERT INTO "ur_maududi" VALUES(111,4,'111:4','اور (اُس کے ساتھ) اُس کی جورُو بھی، لگائی بُجھائی کرنے والی، ');
INSERT INTO "ur_maududi" VALUES(111,5,'111:5','اُس کی گردن میں مونجھ کی رسّی ہو گی۔ ');
INSERT INTO "ur_maududi" VALUES(112,1,'112:1','کہو، وہ اللہ ہے، یَکتا۔ ');
INSERT INTO "ur_maududi" VALUES(112,2,'112:2','اللہ سب سے بے نیاز ہے اور سب اس کے محتاج ہیں۔ ');
INSERT INTO "ur_maududi" VALUES(112,3,'112:3','نہ اس کی کوئی اولاد ہے اور نہ وہ کسی کی اولاد۔ ');
INSERT INTO "ur_maududi" VALUES(112,4,'112:4','اور کوئی اُس کا ہمسر نہیں ہے۔ ');
INSERT INTO "ur_maududi" VALUES(113,1,'113:1','کہو، میں پناہ مانگتا ہوں صبح کے ربّ کی');
INSERT INTO "ur_maududi" VALUES(113,2,'113:2','ہر اُس چیز کے شر سے جو اُس نے پیدا کی ہے ');
INSERT INTO "ur_maududi" VALUES(113,3,'113:3','اور رات کی تاریکی کے شر سے جب کہ وہ چھا جائے، ');
INSERT INTO "ur_maududi" VALUES(113,4,'113:4','اور گِرہوں میں پُھونکنے والوں (یا والیوں)کے شر سے، ');
INSERT INTO "ur_maududi" VALUES(113,5,'113:5','اور حاسد کے شر سے جب کہ وہ حسد کرے۔ ');
INSERT INTO "ur_maududi" VALUES(114,1,'114:1','کہو، میں پناہ مانگتا ہوں انسانوں کے رب');
INSERT INTO "ur_maududi" VALUES(114,2,'114:2','انسانوں کے بادشاہ');
INSERT INTO "ur_maududi" VALUES(114,3,'114:3','انسانوں کے حقیقی معبُود کی ');
INSERT INTO "ur_maududi" VALUES(114,4,'114:4','اُس وسوسہ ڈالنے والے کے شر سے جو بار بار پلٹ کر آتا ہے، ');
INSERT INTO "ur_maududi" VALUES(114,5,'114:5','جو لوگوں کے دلوں میں وسوسے ڈالتا ہے');
INSERT INTO "ur_maududi" VALUES(114,6,'114:6','خواہ وہ جِنّوں میں سے ہو یا انسانوں میں سے۔ ');
