INSERT INTO "ur_wahid_uddin" VALUES(1,1,'1:1','شروع اللہ کے نام سے جو بڑامہربان،نہایت رحم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(1,2,'1:2','سب تعریف اللہ کے لئے ہے جو سارے جہان کا مالک ہے');
INSERT INTO "ur_wahid_uddin" VALUES(1,3,'1:3','بہت مہربان، نہایت رحم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(1,4,'1:4','انصاف کے دن کا مالک ہے');
INSERT INTO "ur_wahid_uddin" VALUES(1,5,'1:5','ہم تیری ہی عبادت کرتے ہیں اور تجھی سے مدد چاہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(1,6,'1:6','ہم کو سیدھا راستہ دکھا');
INSERT INTO "ur_wahid_uddin" VALUES(1,7,'1:7','ان لوگوں کا راستہ جن پر تو نے فضل کیا ان کا راستہ نہیں جن پر تیرا غضب ہوا ،اور نہ ان لوگوں کا راستہ جو راستے سے بھٹک گئے');
INSERT INTO "ur_wahid_uddin" VALUES(2,1,'2:1','الف لام میم');
INSERT INTO "ur_wahid_uddin" VALUES(2,2,'2:2','یہ اللہ کی کتاب ہے اس میں کوئی شک نہیں راہ دکھاتی ہے ڈر رکھنے والوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(2,3,'2:3','جو یقین کرتے ہیں بن دیکھے، اور نماز قائم کرتے ہیں ، اور جو کچھ ہم نے ان کو دیا ہے وہ اس میں سے خرچ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,4,'2:4','اور جو ایمان لاتے ہیں اس پر جو تمھارے اوپر اترا اور جو تم سے پہلے اتارا گیا، اور وہ آخرت پر یقین رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,5,'2:5','انھیں لوگوں نے اپنے رب کی راہ پائی ہے اور وہی کامیابی کو پہنچنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,6,'2:6','جن لوگوں نے انکار کیا، ان کے لیے یکساں ہے ڈراؤ یا نہ ڈراؤ، وہ ماننے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,7,'2:7','اللہ نے ان کے دلوں پر اور ان کے کانوں پر مہر لگادی ہے، اور ان کی آنکھوں پر پردہ ہے، اور ان کے لیے بڑا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,8,'2:8','اور لوگوں میں کچھ ایسے بھی ہیں جو کہتے ہیں کہ ہم ایمان لائے اللہ پر اور آخرت کے دن پر، حالاں کہ وہ ایمان والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,9,'2:9','وہ اللہ کو اور مومنین کو دھوکا دینا چاہتے ہیں مگر وہ صرف اپنے آپ کو دھوکا دے رہے ہیں اور وہ اس کا شعور نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,10,'2:10','ان کے دلوں میں روگ ہے تو اللہ نے ان کے روگ کو بڑھا دیا اور ان کے ليے درد ناک عذاب ہے، اس وجہ سے کہ وہ جھوٹ کہتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(2,11,'2:11','اور جب ان سے کہا جاتا ہے کہ زمین میں فساد نہ کرو تو وہ جواب دیتے ہیں کہ ہم تو اصلاح کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,12,'2:12','آگاہ، یہی لوگ فساد کرنے والے ہیں ، مگر وہ نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,13,'2:13','اور جب ان سے کہا جاتا ہے کہ تم بھی اسی طرح ایمان لے آؤ جس طرح اور لوگ ایمان لائے ہیں تو کہتے ہیں کہ کیا ہم اس طرح ایمان لائیں جس طرح بے وقوف لوگ ایمان لائے ہیں آگاہ، کہ بے وقوف یہی لوگ ہیں ، مگر وہ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,14,'2:14','اور جب وہ ایمان والوں سے ملتے ہیں تو کہتے ہیں کہ ہم ایمان لائے ہیں ، اور جب اپنے شیطانوں کی مجلس میں پہنچتے ہیں تو کہتے ہیں کہ ہم تمھارے ساتھ ہیں ، ہم تو ان سے صرف ہنسی کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,15,'2:15','اللہ ان سے هنسي کر رہا ہے اوروہ ان کو ان کی سرکشی میں ڈھیل دے رہا ہے، وہ بھٹکتے پھر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,16,'2:16','یہ وہ لوگ ہیں جنھوں نے راہ کے بدلے گمراہی خریدی تو ان کی تجارت مفید ثابت نہ ہوئی، اور وہ نہ ہوئے راہ پانے والے');
INSERT INTO "ur_wahid_uddin" VALUES(2,17,'2:17','ان کی مثال ایسی ہے جیسے ایک شخص نے آگ جلائی جب آگ نے اس کے ارد گرد کو روشن کردیا تو اللہ نے ان کی آنکھ کی روشنی چھین لی اور ان کو اندھیرے میں چھوڑ دیا کہ ان کو کچھ نظر نہیں آتا');
INSERT INTO "ur_wahid_uddin" VALUES(2,18,'2:18','وہ بہرے ہیں ، گونگے ہیں ، اندھے ہیں اب یہ لوٹنے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,19,'2:19','یا ان کی مثال ایسی ہے جیسے آسمان سے بارش ہو رہی ہو، اس میں تاریکی بھی ہو اور گرج چمک بھی وہ کڑک سے ڈر کر موت سے بچنے کے ليے اپنی انگلیاں اپنے کانوں میں ٹھونس رہے ہوں حالاں کہ اللہ منکروں کو اپنے گھیرے میں ليے ہوئے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,20,'2:20','قریب ہے کہ بجلی ان کی نگاہوں کو اچک لے جب بھی ان پر بجلی چمکتی ہے، اس میں وہ چل پڑتے ہیں اور جب ان پر اندھیرا چھا جاتا ہے تو وہ رک جاتے ہیں اور اگر اللہ چاہے تو ان کے کان اور ان کی آنکھوں کو چھین لے اللہ یقیناً ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,21,'2:21','اے لوگو، اپنے رب کی عبادت کرو جس نے تم کو پیدا کیا اور ان لوگوں کو بھی جو تم سے پہلے گزر چکے ہیں ، تاکہ تم دوزخ سے بچ جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(2,22,'2:22','وہ ذات جس نے زمین کو تمھارے ليے بچھونا بنایا اور آسمان کو چھت بنایا، اور اتارا آسمان سے پانی اور اس سے پیدا کيے پھل تمھاری غذا کے ليے پس تم کسی کو اللہ کے برابر نہ ٹھہراؤ حالاں کہ تم جانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,23,'2:23','اگر تم اس کلام کے بارے میں شک میں ہو جو ہم نے اپنے بندے کے اوپر اتارا ہے تو لاؤ اس کے جیسی ایک سورہ اور بلالو اپنے مدد گاروں کو بھی اللہ کے سوا، اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,24,'2:24','پس اگر تم یہ نہ کرسکو اور ہرگز نہ کرسکو گے تو ڈرو اس آگ سے جس کا ایندھن بنیں گے آدمی اور پتھر وہ تیار کی گئی ہے منکروں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(2,25,'2:25','اور خوش خبری دے دو، ان لوگوں کو جو ایمان لائے اور جنھوں نے نیک کام کيے، اس بات کی کہ ان کے ليے ایسے باغ ہوں گے جن کے نیچے نہریں جاری ہوں گی جب بھی ان کو ان باغوں میں سے کوئی پھل کھانے کو ملے گا تو وہ کہیں گے یہ وہی ہے جو اس سے پہلے ہم کو دیا گیا تھا اور ملے گا ان کو ایک دوسرے سے ملتا جلتا اور ان کے ليے وہاں پاکیزہ جوڑے ہوں گے اور وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,26,'2:26','اللہ اس سے نہیں شرماتا کہ بیان کرے مثال مچھر کی یا اس سے بھی کسی چھوٹی چیز کی پھر جو ایمان والے ہیں ، وہ جانتے ہیں کہ وہ حق ہے ان کے رب کی جانب سے اور جو منکر ہیں وہ کہتے ہیں کہ اس مثال کو بیان کرکے اللہ نے کیا چاہا ہے اللہ اس کے ذریعہ بہت سے لوگوں کو گمراہ کرتاہے اور بہت سے لوگوں کو اس سے راہ دکھاتا ہے اور وہ گمراہ کرتا ہے ان لوگوں کو جو نافرمانی کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,27,'2:27','جو اللہ کے عہد کواس کے باندھنے کے بعد توڑتے ہیں اور اس چیز کو توڑتے ہیں جس کو اللہ نے جوڑنے کا حکم دیا ہے اور زمین میں بگاڑ پیدا کرتے ہیں یہی لوگ ہیں نقصان اٹھانے والے');
INSERT INTO "ur_wahid_uddin" VALUES(2,28,'2:28','تم کس طرح اللہ کا انکار کرتے ہو، حالاں کہ تم بے جان تھے تو اس نے تم کو زندگی عطا کی پھر وہ تم کو موت دے گا پھر زندہ کرے گا پھرتم اسی کی طرف لوٹائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,29,'2:29','وہی ہے جس نے تمھارے ليے وہ سب کچھ پیدا کیا جو زمین میں ہے پھر آسمان کی طرف توجہ کی اور سات آسمان درست کيے اور وہ ہرچیز کو جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,30,'2:30','اور جب تیرے رب نے فرشتوں سے کہا کہ میں زمین میں ایک جانشین بنانے والا ہوں فرشتوں نے کہا کیا تو زمین میں ایسے لوگوں کو بسائے گا جو اس میں فساد کریں اور خون بہائیں اور ہم تیری حمد کرتے ہیں اور تیری پاکی بیان کرتے ہیں اللہ نے کہا میں جانتا ہوں جو تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,31,'2:31','اور اللہ نے سکھادئے آدم کو سارے نام، پھر ان کو فرشتوں کے سامنے پیش کیا اور کہا کہ اگر تم سچے ہو تو مجھے ان لوگوں کے نام بتاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(2,32,'2:32','فرشتوں نے کہا کہ تو پاک ہے ہم تو وہی جانتے ہیں جو تو نے ہم کو بتایا بے شک تو ہی علم والا اور حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,33,'2:33','اللہ نے کہا اے آدم، ان کو بتاؤ ان لوگوں کے نام تو جب آدم نے بتائے ان کو ان لوگوں کے نام تو اللہ نے کہا— کیا میں نے تم سے نہیں کہا تھا کہ آسمانوں اور زمین کے بھید کو میں ہی جانتا ہوں اور مجھ کو معلوم ہے جو تم ظاہر کرتے ہو اور جو تم چھپاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,34,'2:34','اور جب ہم نے فرشتوں سے کہا کہ آدم کو سجدہ کرو تو انھوں نے سجدہ کیا، مگر ابلیس نے نہ کیا اس نے انکار کیا اور گھمنڈ کیا اور منکروں میں سے ہوگیا');
INSERT INTO "ur_wahid_uddin" VALUES(2,35,'2:35','اور ہم نے کہا اے آدم! تم اور تمھاری بیوی دونوں جنت میں رہو اور اس میں سے کھاؤ آسودگی کے ساتھ جہاں سے چاہو اور اس درخت کے قریب مت جانا ورنہ تم ظالموں میں سے ہوجاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,36,'2:36','پھر شیطان نے اس درخت کے ذریعہ دونوں کو لغزش میں مبتلا کردیا اور ان کو اس عیش سے نکال دیا جس میں وہ تھے اور ہم نے کہا تم سب اترو یہاں سے تم ایک دوسرے کے دشمن ہوگے اور تمھارے ليے زمین میں ٹھہرنا اور کام چلانا ہے ایک مدت تک');
INSERT INTO "ur_wahid_uddin" VALUES(2,37,'2:37','پھر آدم نے سیکھ ليے اپنے رب سے چند بول تو اللہ اس پر متوجہ ہوا بے شک وہ توبہ قبول کرنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,38,'2:38','ہم نے کہا تم سب یہاں سے اترو پھر جب آئے تمھارے پاس میری طرف سے کوئی ہدایت تو جو لوگ میری ہدایت کی پیروی کریں گے، ان کے ليے نہ کوئی ڈر ہوگا اور نہ وہ غمگین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,39,'2:39','اور جو لوگ انکار کریں گے اور ہماری نشانیوں کو جھٹلائیں گے تو وہی لوگ دوزخ والے ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,40,'2:40','اے بنی اسرائیل! یاد کرو میرے اس احسان کو جو میں نے تمھارے اوپر کیا اور میرے عہد کو پورا کرو، میں تمھارے عہد کو پورا کروں گا، اور میرا ہی ڈر رکھو');
INSERT INTO "ur_wahid_uddin" VALUES(2,41,'2:41','اور ایمان لاؤ اس چیز پر جو میں نے اتاری ہے تصدیق کرتی ہوئی اس چیز کی جو تمھارے پاس ہے اور تم سب سے پہلے اس کا انکار کرنے والے نہ بنو اور نہ لو میری آیتوں پر مول تھوڑا اور مجھ سے ڈرو');
INSERT INTO "ur_wahid_uddin" VALUES(2,42,'2:42','اور صحیح میں غلط کو نہ ملاؤ اور سچ کو نہ چھپاؤ، حالاں کہ تم جانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,43,'2:43','اور نماز قائم کرو اور زکوٰۃ ادا کرو اور جھکنے والوں کے ساتھ جھک جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(2,44,'2:44','تم، لوگوں سے نیک کام کرنے کو کہتے ہو اور اپنے آپ کو بھول جاتے ہو حالاں کہ تم کتاب کو پڑھتے ہو، کیا تم سمجھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,45,'2:45','اور مدد چاہو صبر اور نماز سے اور بے شک وہ بھاری ہے، مگر ان لوگوں پر نہیں جو ڈرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,46,'2:46','جو گمان رکھتے ہیں کہ ان کو اپنے رب سے ملنا ہے اور وہ اسی کی طرف لوٹنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,47,'2:47','اے بنی اسرائیل، میرے اس احسان کو یاد کرو جو میں نے تمھارے اوپر کیا اور اس بات کو کہ میں نے تم کو دنیا والوں پر فضلیت دی');
INSERT INTO "ur_wahid_uddin" VALUES(2,48,'2:48','اور ڈرو اس دن سے کہ کوئی جان کسی دوسری جان کے کچھ کام نہ آئے گی نہ اس کی طرف سے کوئی سفارش قبول ہوگی اور نہ اس سے بدلے میں کچھ لیا جائے گا اور نہ ان کی کوئی مدد کی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(2,49,'2:49','اور جب ہم نے تم کو فرعون کے لوگوں سے چھڑایا وہ تم کوبڑی تکلیف دیتے تھے تمھارے بیٹوں کو ذبح کرتے اور تمھاری عورتوں کو زنده رکھتے اور اس میں تمھارے رب کی طرف سے بھاری آزمائش تھی');
INSERT INTO "ur_wahid_uddin" VALUES(2,50,'2:50','اور جب ہم نے دریا کو پھاڑ کر تمہیں پار کرایا پھر بچایا تم کو، اور ڈبادیا فرعون کے لوگوں کو اور تم دیکھتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,51,'2:51','اور جب ہم نے موسیٰ سے وعدہ کیا چالیس رات کا پھر تم نے اس کے بعد بچھڑے کو معبود بنالیا اور تم ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(2,52,'2:52','پھر ہم نے اس کے بعد تم کو معاف کردیا، تاکہ تم شکر گزار بنو');
INSERT INTO "ur_wahid_uddin" VALUES(2,53,'2:53','اور جب ہم نے موسیٰ کو کتاب دی اور فیصلہ کرنے والی چیز تاکہ تم راہ پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(2,54,'2:54','اور جب موسیٰ نے اپنی قوم سے کہا کہ اے میری قوم! تم نے بچھڑے کو معبود بناکر اپنی جانوں پر ظلم کیا ہے اب اپنے پیدا کرنے والے کی طرف متوجہ ہو اور اپنے مجرموں کو اپنے ہاتھوں سے قتل کرو یہ تمھارے ليے تمھارے پیدا کرنے والے کے نزدیک بہتر ہے تو اللہ نے تمھاری توبہ قبول فرمائی بے شک وہی توبہ قبول کرنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,55,'2:55','اور جب تم نے کہا کہ اے موسیٰ، ہم تمھارا یقین نہیں کریں گے، جب تک ہم اللہ کو سامنے نہ دیکھ لیں تو تم کو بجلی نے پکڑ لیا اور تم دیکھ رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(2,56,'2:56','پھر ہم نے تمھاری موت کے بعد تم کو اٹھایا تاکہ تم شکر گزار بنو');
INSERT INTO "ur_wahid_uddin" VALUES(2,57,'2:57','اور ہم نے تمھارے اوپر بدلیوں کا سایہ کیا اور تم پر من و سلویٰ اتارا کھاؤ پاکیزہ چیزوں میں سے جو ہم نے تم کو دی ہیں اور انھوں نے ہمارا کچھ نقصان نہیں کیا، وہ اپنا ہی نقصان کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,58,'2:58','اور جب ہم نے کہا کہ داخل ہوجاؤ اس شہر میں اور کھاؤ اس میں سے جہاں سے چاہو فراغت کے ساتھ، اور داخل ہو دروازہ میں سرجھکائے ہوئے اور کہو کہ اے رب! ہماری خطاؤں کو بخش دے ہم تمھاری خطاؤں کو بخش دیں گے اور نیکی کرنے والوں کو زیادہ بھی دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,59,'2:59','تو انھوں نے بدل دیا اس بات کو جو ان سے کہی گئی تھی دوسری بات سے اس پر ہم نے ان لوگوں کے اوپر جنھوں نے ظلم کیا، ان کی نافرمانی کے سبب سے، آسمان سے عذاب اُتارا');
INSERT INTO "ur_wahid_uddin" VALUES(2,60,'2:60','اور جب موسیٰ نے اپنی قوم کے ليے پانی مانگا تو ہم نے کہا اپنا عصا پتھر پر مارو تو اس سے پھوٹ نکلے بارہ چشمے ہر گروہ نے اپنا اپنا گھاٹ پہچان لیا کھاؤ اور پیو اللہ کے رزق سے اور نہ پھرو زمین میں فساد مچانے والے بن کر');
INSERT INTO "ur_wahid_uddin" VALUES(2,61,'2:61','اور جب تم نے کہا اے موسیٰ، ہم ایک ہی قسم کے کھانے پر ہرگز صبر نہیں کرسکتے اپنے رب کو ہمارے ليے پکارو کہ وہ نکالے همارے ليے جو اگتا ہے زمین سے، ساگ اور ککڑی اور گیہوں اور مسور اور پیاز موسیٰ نے کہا کیا تم ایک بہتر چیز کے بدلے ایک ادنیٰ چیز لینا چاہتے ہو کسی شہر میں اتر و تو تم کو ملے گی وہ چیز جو تم مانگتے ہو اور ڈال دی گئی ان پر ذلت اور محتاجی اور وہ غضب ِالٰہی کے مستحق ہوگئے یہ اس وجہ سے ہوا کہ وہ اللہ کی نشانیوں کا انکار کرتے تھے اور نبیوں کو ناحق قتل کرتے تھے یہ اس وجہ سے کہ انھوں نے نافرمانی کی اور وہ حد پر نہ رہتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(2,62,'2:62','یوں ہے کہ جو لوگ ایمان لائے اور جو لوگ یہودی ہوئے اور نصاریٰ اور صابی، ان میں سے جو شخص ایمان لایا اللہ پر اور آخرت کے دن پر اور اس نے نیک کام کیا تو اس کے ليے اس کے رب کے پاس اجر ہے اور ان کے ليے نہ کوئی ڈر ہے اور نہ وہ غم گین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,63,'2:63','اور جب ہم نے تم سے تمھارا عہد لیا اور طور پہاڑ کو تمھارے اوپر اٹھایا پکڑو اس چیزکو جو ہم نے تم کو دی ہے مضبوطی کے ساتھ، اور جو کچھ اس میں ہے اس کو یاد رکھو تاکہ تم بچو');
INSERT INTO "ur_wahid_uddin" VALUES(2,64,'2:64','اس کے بعد تم اس سے پھر گئے اگر اللہ کا فضل اور اس کی رحمت نہ ہوتی تو ضرور تم ہلاک ہوجاتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,65,'2:65','اور ان لوگوں کا حال تم جانتے ہو جو سبت (سنیچر) کے معاملہ میں اللہ کے حکم سے نکل گئے تو ہم نے ان کو کہا کہ تم لوگ ذلیل بندر بن جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(2,66,'2:66','پھر ہم نے اس کو عبرت بنادیا ان لوگوں کے ليے جو اس کے روبرو تھے اور ان لوگوں کے ليے جو اس کے بعد آئے اور اس میں ہم نے نصیحت رکھ دی ڈر والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(2,67,'2:67','اور جب موسیٰ نے اپنی قوم سے کہا کہ اللہ تم کو حکم ديتا ہے کہ تم ایک بقرہ (گائے) ذبح کرو انھوں نے کہا کیا تم ہم سے ہنسی کر رہے ہو موسیٰ نے کہا کہ میں اللہ کی پناہ مانگتا ہوں کہ میں ایسا نادان بنوں');
INSERT INTO "ur_wahid_uddin" VALUES(2,68,'2:68','انھوں نے کہا، اپنے رب سے درخواست کرو کہ وہ ہم سے بیان کرے کہ وہ بقرہ کیسی ہوموسیٰ نے کہا، اللہ فرماتا ہے کہ وہ بقرہ نہ بوڑھی ہو نہ بچہ، ان کے بیچ کی ہو اب کر ڈالو جو حکم تم کو ملا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,69,'2:69','انھوں نے کہا، اپنے رب سے درخواست کروکہ وہ بیان کرے کہ اس کا رنگ کیا ہو موسیٰ نے کہا،اللہ فرماتا ہے کہ وہ گہرے زرد رنگ کی ہودیکھنے والوں کو اچھی معلوم ہوتی ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,70,'2:70','انھوں نے کہا، اپنے رب سے درخواست کرو کہ وہ ہم سے بیان کردے کہ وہ کیسی ہو کیوں کہ بقرہ میں ہم کو شبہ پڑ گیا ہے اور اللہ نے چاہا تو ہم راہ پالیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,71,'2:71','موسیٰ نے کہا، اللہ فرماتا ہے کہ وہ ایسی بقرہ ہو کہ محنت کرنے والی نہ ہو، زمین کو جوتنے والی اور کھیتوں کو پانی دینے والی نہ ہو وہ صحیح سالم ہو، اس میں کوئی داغ نہ ہوانھوں نے کہا اب تم واضح بات لائے پھر انھوں نے اس کو ذبح کیا اور وہ ذبح کرتے نظر نہ آتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(2,72,'2:72','اور جب تم نے ایک شخص کو مار ڈالا، پھر ایک دوسرے پر اس کا الزام ڈالنے لگے حالاں کہ اللہ کو ظاہر کرنا منظور تھا جو کچھ تم چھپانا چاہتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(2,73,'2:73','پس ہم نے حکم دیاکہ مارو اس مُردے کو اس کے ایک ٹکڑے سے اس طرح زندہ کرتا ہے اللہ مُردوں کو اور وہ تم کو اپنی نشانیاں دکھاتا ہے تاکہ تم سمجھو');
INSERT INTO "ur_wahid_uddin" VALUES(2,74,'2:74','پھر اس کے بعد تمھارے دل سخت ہوگئے پس وہ پتھر کی مانند ہوگئے یا اس سے بھی زیادہ سخت پتھروں میں بعض ایسے بھی ہوتے ہیں جن سے نہریں پھوٹ نکلتی ہیں بعض پتھر پھٹ جاتے ہیں اور ان سے پانی نکل آتا ہے اور بعض پتھر ایسے بھی ہوتے ہیں جو اللہ کے ڈر سے گر پڑتے ہیں اور اللہ اس سے بے خبر نہیں جو تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,75,'2:75','کیا تم یہ امید رکھتے ہو کہ یہ یہود تمھارے کہنے سے ایمان لے آئیں گے حالاں کہ ان میں سے کچھ لوگ ایسے ہیں کہ وہ اللہ کا کلام سنتے تھے اور پھر اس کو بدل ڈالتے تھے سمجھنے کے بعد،اور وہ جانتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,76,'2:76','جب وہ اہلِ ایمان سے ملتے ہیں توکہتے ہیں کہ ہم ایمان لائے ہوئے ہیں اور جب وہ آپس میں ایک دوسرے سے ملتے ہیں تو کہتے ہیں  کیا تم ان کو وہ باتیں بتاتے ہو جو اللہ نے تم پر کھولی ہیں کہ وہ تمھارے رب کے پاس تم سے حجت کریں کیا تم سمجھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,77,'2:77','کیا وہ نہیں جانتے کہ اللہ کو معلوم ہے جو وہ چھپاتے ہیں اور جو وہ ظاہر کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,78,'2:78','اور ان میں اَن پڑھ ہیں جونہیں جانتے کتاب کو مگر آرزوئیں ان کے پاس گمان کے سوا اور کچھ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,79,'2:79','پس خرابی ہے ان لوگوں کے ليے جو اپنے ہاتھ سے کتاب لکھتے ہیں ، پھر کہتے ہیں کہ یہ اللہ کی جانب سے ہے، تاکہ اس کے ذریعہ تھوڑی سی پونجی حاصل کرلیں پس خرابی ہے اس چیز کی بدولت جو ان کے ہاتھوں نے لکھی اور ان کے ليے خرابی ہے اپنی اس کمائی سے');
INSERT INTO "ur_wahid_uddin" VALUES(2,80,'2:80','اور وہ کہتے ہیں ہم کو دوزخ کی آگ نہیں چھوئے گی مگر گنتی کے چند دن کہو، کیا تم نے اللہ کے پاس سے کوئی وعدہ لے لیا ہے کہ اللہ اپنے وعدہ کے خلاف نہیں کرے گا، یا تم اللہ کے اوپرایسی بات کہتے ہو جو تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,81,'2:81','ہاں جس نے کوئی برائی کی اور اس کے گناہ نے اس کو اپنے گھیرے میں لے لیا، تو وہی لوگ دوزخ والے ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,82,'2:82','اور جو ایمان لائے اور جنھوں نے نیک عمل کيے، وہ جنت والے لوگ ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,83,'2:83','اور جب ہم نے بنی اسرائیل سے عہد لیا کہ تم اللہ کے سوا کسی کی عبادت نہ کرو گے اور نیک سلوک کرو گے ماں باپ کے ساتھ، قرابت داروں کے ساتھ، یتیموں اور مسکینوں کے ساتھ اور یہ کہ لوگوں سے اچھی بات کہو اور نماز قائم کرو اور زکوٰۃ ادا کرو پھر تم اس سے پھر گئے سوا تھوڑے لوگوں کے اور تم اقرار کرکے اس سے ہٹ جانے والے لوگ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,84,'2:84','اور جب ہم نے تم سے یہ عہد لیا کہ تم اپنوں کا خون نہ بہاؤ گے اور اپنے لوگوں کو اپنی بستیوں سے نہ نکالو گے پھر تم نے اقرار کیا اور تم اس کے گواہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,85,'2:85','پھر تم ہی وہ ہو کہ اپنوں کو قتل کرتے ہو اور اپنے ہی ایک گروہ کو ان کی بستیوں سے نکالتے ہو ان کے مقابلہ میں ان کے دشمنوں کی مدد کرتے ہو گناہ اور ظلم کے ساتھ پھر اگر وہ تمھارے پاس قید ہوکر آتے ہیں تو تم فدیہ دے کر ان کو چھڑاتے ہو حالاں کہ خود ان کا نکالنا تمھارے اوپر حرام تھا کیا تم کتاب الٰہی کے ایک حصہ کو مانتے ہو اور ایک حصے کا انکار کرتے ہو پس تم میں سے جو لوگ ایسا کریں ، ان کی سزا اس کے سوا کیا ہے کہ ان کو دنیا کی زندگی میں رسوائی ہو اور قیامت کے دن ان کو سخت عذاب میں ڈال دیا جائے اور اللہ اس چیز سے بے خبر نہیں جو تم کر رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,86,'2:86','یہی لوگ ہیں جنھوں نے آخرت کے بدلے دنیا کی زندگی خریدی پس نہ ان کا عذاب ہلکا کیا جائے گا اور نہ ان کو مدد پہنچے گی');
INSERT INTO "ur_wahid_uddin" VALUES(2,87,'2:87','اور ہم نے موسی کو کتاب دی اور اس کے بعد پے در پے رسول بھیجے اور عیسیٰ بن مریم کو کھلی کھلی نشانیاں دیں اور روحِ پاک سے اس کی تائید کی تو جب بھی کوئی رسول تمھارے پاس وہ چیز لے کر آیا جس کو تمھارا دل نہیں چاہتا تھا تو تم نے تکبر کیا پھر ایک جماعت کوجھٹلایا اور ایک جماعت کو مار ڈالا');
INSERT INTO "ur_wahid_uddin" VALUES(2,88,'2:88','اوریہود کہتے ہیں کہ ہمارے دل بند ہیں نہیں ، بلکہ اللہ نے ان کے انکار کی وجہ سے ان پر لعنت کردی ہے اس ليے وہ بہت کم ایمان لاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,89,'2:89','اور جب آئی اللہ کی طرف سے ان کے پاس ایک کتاب جو سچا کرنے والی ہے اس کو جو ان کے پاس ہے اور وہ پہلے سے منکروں پر فتح مانگا کرتے تھے پھر جب آئی ان کے پاس وہ چیز جس کو انھوں نے پہچان رکھا تھا تو انھوں نے اس کا انکار کردیا پس اللہ کی لعنت ہے انکار کرنے والوں پر');
INSERT INTO "ur_wahid_uddin" VALUES(2,90,'2:90','کیسی بری ہے وہ چیز جس سے انھوں نے اپنی جانوں کا سوداکیا کہ وہ انکار کر رہے ہیں اللہ کے اتارے ہوئے کلام کا اس ضد کی بنا پر کہ اللہ اپنے فضل سے اپنے بندوں میں سے جس پر چاہے اُتارے پس وہ غصہ پر غصہ کماکرلائے اور انکار کرنے والوں کے ليے ذلت کا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,91,'2:91','اور جب ان سے کہا جاتا ہے کہ اس کلام پر ایمان لاؤ جو اللہ نے اتارا ہے تو وہ کہتے ہیں کہ ہم اس پر ایمان رکھتے ہیں جو ہمارے اوپر اترا ہے اور وہ اس کلام کا انکار کرتے ہیں جو اس کے پیچھے آیا ہے حالاں کہ وہ حق ہے اور سچا کرنے والا ہے اس کا جوان کے پاس ہے کہو، اگر تم ایمان والے ہو تو تم اللہ کے پیغمبروں کو اس سے پہلے کیوں قتل کرتے رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,92,'2:92','اور موسیٰ تمھارے پاس کھلی نشانیاں لے کر آیا پھر تم نے اس کے پیچھے بچھڑے کو معبود بنالیا اور تم ظلم کرنے والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,93,'2:93','اور جب ہم نے تم سے عہد لیا اور طور پہاڑ کو تمھارے اوپر کھڑا کیا جو حکم ہم نے تم کو دیا ہے اس کو مضبوطی کے ساتھ پکڑو اور سنو انھوں نے کہا ہم نے سنا اور ہم نے نہیں مانا اور ان کے کفر کے سبب سے بچھڑا ان کے دلوں میں رچ بس گیا کہو، اگر تم ایمان والے ہو تو کیسی بری ہے وہ چیز جو تمھارا ایمان تم کو سکھاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,94,'2:94','کہو، اگر اللہ کے یہاں آخرت کا گھر خاص تمھارے ليے ہے، دوسروں کو چھوڑ کر، تو تم مرنے کی آرزو کرو اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,95,'2:95','مگر وہ کبھی اس کی آرزو نہ کریں گے بہ سبب اس کے جو وہ اپنے آگے بھیج چکے ہیں اور اللہ خوب جانتا ہے ظالموں کو');
INSERT INTO "ur_wahid_uddin" VALUES(2,96,'2:96','اور تم ان کو زندگی کا سب سے زیادہ حریص پاؤگے،ان لوگوں سے بھی زیادہ جو مشرک ہیں ان میں سے ہر ایک یہ چاہتا ہے کہ ہزار برس کی عمر پائےحالاں کہ اتنا جینا بھی اس کوعذاب سے بچا نہیں سکتااور اللہ دیکھتا ہے جو کچھ وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,97,'2:97','کہو کہ جو کوئی جبریل کا مخالف ہے تو اس نے اس کلام کو تمھارے دل پر اللہ کے حکم سے اتارا ہے، وہ سچا کرنے والا ہے اس کا جو اس کے آگے ہے اور وہ ہدایت اور خوش خبری ہے ایمان والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(2,98,'2:98','جو کوئی دشمن ہو اللہ کا اور اس کے فرشتوں کا اور اس کے رسولوں کا اور جبریل اور میکائیل کا تو اللہ ایسے منکروں کا دشمن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,99,'2:99','اور ہم نے تمھارے اوپر واضح نشانیاں اتاریں اور کوئی ان کا انکار نہیں کرتا مگر وہی لوگ جو حد سے گزرجانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,100,'2:100','کیا جب بھی وہ کوئی وعدہ کریں گے تو ان کا ایک گروہ اس کو توڑ پھینکے گا بلکہ ان میں سے اکثر ایمان نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,101,'2:101','اور جب ان کے پاس اللہ کی طرف سے ایک رسول آیا جو سچا کرنے والا تھا اس چیز کا جو ان کے پاس ہے تو ان لوگوں نے جن کو کتاب دی گئی تھی، اللہ کی کتاب کو اس طرح پیٹھ پیچھے پھینک دیا گویا وہ اس کو جانتے ہی نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,102,'2:102','اور وہ اس چیز کے پیچھے پڑگئے جس کو شیاطین، سلیمان کی سلطنت پر لگاکر پڑھتے تھے حالاں کہ سلیمان نے کفر نہیں کیا، بلکہ یہ شیاطین تھے جنھو ں نے کفر کیا وہ لوگوں کو جادو سکھاتے تھے اور وہ اس چیز میں پڑگئے جو بابل میں دو فرشتوں ، ہاروت اور ماروت پر اتاری گئی، جب کہ ان کا حال یہ تھا کہ جب بھی وہ کسی کو اپنا یہ فن سکھاتے تو اس سے کہہ دیتے کہ ہم تو آزمائش کے ليے ہیں پس تم منکرنہ بنو مگر وہ ان سے وہ چیز سیکھتے جس سے مرد اور اس کی عورت کے درمیان جدائی ڈال دیں حالاں کہ وہ اللہ کے اِذن کے بغیر اس سے کسی کا کچھ بگاڑ نہیں سکتے تھے اور وہ ایسی چیز سیکھتے جوان کو نقصان پہنچائے اور نفع نہ دے اور وہ جانتے تھے کہ جو کوئی اس چیز کا خریدار ہو، آخرت میں اس کا کوئی حصہ نہیں کیسی بری چیز ہے جس کے بدلے انھوں نے اپنی جانوں کو بیچ ڈالا کاش، وہ اس کو سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,103,'2:103','اور اگر وہ مومن بنتے اور تقویٰ اختیار کرتے تو اللہ کا بدلہ ان کے ليے بہتر تھا، کاش وہ اس کو سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,104,'2:104','اے ایمان والو، تم راعِنا نہ کہو بلکہ اُنظرنا کہو اور سنو اور انکار کرنے والوں کے ليے درد ناک سزا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,105,'2:105','جن لوگوں نے انکار کیا، چاہے اہل کتاب ہوں یا مشرکین، وہ نہیں چاہتے کہ تمھارے اوپر تمھارے رب کی طرف سے کوئی بھلائی اترے اور اللہ جس کو چاہتا ہے اپنی رحمت کے ليے چن لیتا ہے اللہ بڑے فضل و الا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,106,'2:106','ہم جس آیت کو ملتوی کرتے ہیں یا بھلا دیتے ہیں تو اس سے بہتر یا اس کے مثل دوسری لاتے ہیں کیا تم نہیں جانتے کہ اللہ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,107,'2:107','کیا تم نہیں جانتے کہ اللہ ہی کے ليے آسمانوں اور زمین کی بادشاہی ہے، اور تمھارے ليے اللہ کے سوا نہ کوئی دوست ہے اور نہ کوئی مدد گار');
INSERT INTO "ur_wahid_uddin" VALUES(2,108,'2:108','کیا تم چاہتے ہو کہ اپنے رسول سے سوالات کرو جس طرح اس سے پہلے موسیٰ سے سوالات کيے گئے اور جس شخص نے ایمان کو انکار سے بدل لیا، وہ یقینا سیدھی راہ سے بھٹک گیا');
INSERT INTO "ur_wahid_uddin" VALUES(2,109,'2:109','بہت سے اہل کتاب دل سے چاہتے ہیں کہ تمھارے مومن ہوجانے کے بعد وہ کسی طرح پھر تم کو منکربنادیں ، اپنے حسد کی وجہ سے، باوجود یہ کہ حق ان کے سامنے واضح ہوچکا ہے پس معاف کرو اور درگزر کرو یہاں تک کہ اللہ کا فیصلہ آجائے بے شک اللہ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,110,'2:110','اور نماز قائم کرو اور زکوٰۃ ادا کرو اور جو بھلائی تم اپنے ليے آگے بھیجو گے، اس کو تم اللہ کے پاس پاؤ گے جو کچھ تم کرتے ہو، اللہ یقیناًاس کو دیکھ رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,111,'2:111','اور وہ کہتے ہیں کہ جنت میں صرف وہی لوگ جائیں گے جو یہودی ہوں یا عیسائی ہوں ، یہ محض ان کی آرزوئیں ہیں کہو کہ لاؤ اپنی دلیل اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,112,'2:112','بلکہ جس نے اپنے آپ کو اللہ کے حوالے کردیا اور وہ مخلص بھی ہے تو ایسے شخص کے ليے اجر ہے اس کے رب کے پاس، ان کے ليے نہ کوئی ڈر ہے اور نہ کوئی غم');
INSERT INTO "ur_wahid_uddin" VALUES(2,113,'2:113','اور یہود نے کہا کہ نصاریٰ کسی چیز پر نہیں ، اور نصاریٰ نے کہا کہ یہود کسی چیز پر نہیں اور وہ سب آسمانی کتاب پڑھتے ہیں اسی طرح ان لوگوں نے بھي کہا جن کے پاس علم نہیں ، انھیں کا ساقول پس اللہ قیامت کے دن اس بات کا فیصلہ کرے گا جس میں یہ جھگڑ رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(2,114,'2:114','اور اس سے بڑھ کر ظالم اور کون ہوگا جو اللہ کی مسجدوں کو اس سے روکے کہ وہاں اللہ کے نام کی یاد کی جائے اور ان کو اجاڑنے کی کوشش کرے ان کا حال تو یہ ہونا چاهيے تھا کہ مسجدوں میں اللہ سے ڈرتے ہوئے داخل ہوں ان کے ليے دنیا میں رسوائی ہے اور آخرت میں ان کے ليے بھاری سزا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,115,'2:115','اور پورب اور پچھم اللہ ہی کے ليے ہے تم جدھر رخ کر و، اسی طرف اللہ ہے یقینا اللہ وسعت والا ہے، علم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,116,'2:116','اور وہ کہتے ہیں کہ اللہ نے بیٹا بنایا ہے وہ اس سے پاک ہے، بلکہ آسمانوں اور زمین میں جو کچھ ہے سب اسی کا ہے اسی کے حکم بردار ہیں سارے');
INSERT INTO "ur_wahid_uddin" VALUES(2,117,'2:117','وہ آسمانوں اورزمین کو وجود میں لانے والا ہے وہ جب کسی کام کا کرنا ٹھہرالیتا ہے تو بس اس کے ليے فرمادیتا ہے کہ ہوجا، تو وہ ہوجاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,118,'2:118','اور جو لوگ علم نہیں رکھتے، انھوں نے کہا اللہ کیوں نہیں کلام کرتا ہم سے یا ہمارے پاس کوئی نشانی کیوں نہیں آتی اسی طرح ان کے اگلے بھی انھیں کی سی بات کہہ چکے ہیں ، ان سب کے دل ایک جیسے ہیں ، ہم نے پیش کردی ہیں نشانیاں ان لوگوں کے ليے جو یقین کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,119,'2:119','ہم نے تم کو حق كے ساتھ بھیجا ہے، خو ش خبری سنانے والا اور ڈرانے والا بناکر اور تم سے دوزخ میں جانے والوں کی بابت کوئی پوچھ نہیں ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(2,120,'2:120','اور یہود اور نصاریٰ ہرگز تم سے راضی نہ ہوں گے، جب تک تم ان کی ملت کے پیرو نہ بن جاؤ تم کہو کہ جو راہ اللہ دکھاتا ہے، وہی اصل راہ ہے اور اگر بعد اس علم کے جو تم کو پہنچ چکا ہے، تم نے ان کی خواہشوں کی پیروی کی تو اللہ کے مقابلہ میں نہ تمھارا کوئی دوست ہوگا اور نہ کوئی مدد گار');
INSERT INTO "ur_wahid_uddin" VALUES(2,121,'2:121','جن لوگوں کو ہم نے کتاب دی ہے وہ اس کو پڑھتے ہیں جیسا کہ حق ہے پڑھنے کا یہی لوگ ایمان لاتے ہیں اس پر اور جو اس کا انکار کرے تو وہی گھاٹے میں رہنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,122,'2:122','اے بنی اسرائیل، میرے اس احسان کو یاد کرو جو میں نے تمھارے اوپر کیا اور اس بات کو کہ میں نے تم کو تمام دنیا والوں پر فضیلت دی');
INSERT INTO "ur_wahid_uddin" VALUES(2,123,'2:123','اور اس دن سے ڈرو جس میں کوئی شخص کسی شخص کے کچھ کام نہ آئے گا اور نہ کسی کی طرف سے کوئی معاوضہ قبول کیا جائے گا اور نہ کسی کو کوئی سفارش فائدہ دے گی اور نہ کہیں سے ان کو کوئی مدد پہنچے گی');
INSERT INTO "ur_wahid_uddin" VALUES(2,124,'2:124','اور جب ابراہیم کو اس کے رب نے کئی باتوں میں آزمایا تو اس نے پورا کردکھایا اللہ نے کہا میں تم کو سب لوگوں کا امام بناؤں گا ابراہیم نے کہا اور میری اولاد میں سے بھی اللہ نے کہا میرا وعدہ ظالموں تک نہیں پہنچتا');
INSERT INTO "ur_wahid_uddin" VALUES(2,125,'2:125','اور جب ہم نے کعبہ کو لوگوں کے اكٹھا ہونے کی جگہ اور امن کا مقام ٹھیرایا اور حکم دیا کہ مقامِ ابراہیم کو نماز پڑھنے کی جگہ بنالو اور ابراہیم اور اسماعیل کو تاکید کی کہ میرے گھر کو طواف کرنے والوں ، اعتکاف کرنے والوں اور رکوع اور سجدہ کرنے والوں کے ليے پاک رکھو');
INSERT INTO "ur_wahid_uddin" VALUES(2,126,'2:126','اور جب ابراہیم نے کہا کہ اے میرے رب، اس شہر کو امن کا شہر بنادے اور اس کے باشندوں کو، جو ان میں سے اللہ اور آخرت کے دن پر ایمان رکھیں ، پھلوں کی روزی عطا فرما اللہ نے کہا جو انکار کرے گا، میں اس کو بھی تھوڑے دنوں فائدہ دوں گا پھر اس کو آگ کے عذاب کی طرف دھکیل دوں گا اور وہ بہت برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,127,'2:127','اور جب ابراہیم اور اسماعیل بیت اللہ کی دیواریں اٹھا رہے تھے اور یہ کہتے جاتے تھے اے ہمارے رب، قبول کر ہم سے، یقینا تو ہی سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,128,'2:128','اے ہمارے رب، ہم کو اپنا فرماں بردار بنا اور ہماری نسل میں سے اپنی ایک فرماں بردار امت اٹھا اور ہم کو ہماری عبادت کے طریقے بتا اور ہم کو معاف فرما، تو معاف کرنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,129,'2:129','اے ہمارے رب، اور ان میں ان ہی میں کا ایک رسول اٹھا جو ان کو تیری آیتیں سنائے اور ان کو کتاب اور حکمت کی تعلیم دے اور ان کا تزکیہ کرے بے شک تو زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,130,'2:130','اور کون ہے جو ابراہیم کے دین کو پسند نہ کرے، مگر وہ جس نے اپنے آپ کو احمق بنالیا ہو حالاں کہ ہم نے اس کو دنیا میں چن لیا تھا اور آخرت میں وہ نیک لوگوں میں سے ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(2,131,'2:131','جب اس کے رب نے کہا کہ اپنے آپ کو حوالے کردو تو اس نے کہا میں نے اپنے آپ کو رب العالمین کے حوالے کیا');
INSERT INTO "ur_wahid_uddin" VALUES(2,132,'2:132','اور اسی کی نصیحت کی ابراہیم نے اپنی اولاد کو اور اسی کی نصیحت کی یعقوب نے اپنی اولاد کو اے میرے بیٹو! اللہ نے تمھارے ليے اسی دین کو چن لیا ہےپس ایمان کے سوا کسی اور حالت پر تم کو موت نہ آئے');
INSERT INTO "ur_wahid_uddin" VALUES(2,133,'2:133','کیا تم موجود تھے جب یعقوب کی موت کا وقت آیا جب اس نے اپنے بیٹوں سے کہا کہ میرے بعد تم کس کی عبادت کرو گےانھوں نے کہا ہم اسی خدا کی عبادت کریں گے جس کی عبادت آپ اور آپ کے آباءابراہیم، اسماعیل اور اسحاق کرتے آئے ہیں ، وہی ایک معبود ہے اور ہم اس کے فرماں بردار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,134,'2:134','یہ ایک جماعت تھی جو گزر گئی اس کو ملے گا جو اس نے کمایا اور تم کو ملے گا جو تم نے کمایا اور تم سے ان کے اعمال کی پوچھ نہ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(2,135,'2:135','اور وہ کہتے ہیں کہ یہودی یا نصرانی بن جاؤ تو ہدایت پاؤ گے کہو کہ نہیں ، بلکہ ہم تو پیروی کرتے ہیں ابراہیم کے دین کی جو اللہ کی طرف یکسو تھا اور وہ شرک کرنے والوں میں نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(2,136,'2:136','کہو ہم اللہ پر ایمان لائے اور اس چیز پر ایمان لائے جو ہماری طرف اتاری گئی ہے اور اس پر بھی جو ابراہیم اوراسماعیل اور اسحاق اور یعقوب اور اس کی اولاد پر اتاری گئی اورجو ملا موسیٰ اور عیسیٰ کو اور جو ملا سب نبیوں کو ان کے رب کی طرف سے ہم ان میں سے کسی کے درمیان فرق نہیں کرتے اور ہم اللہ ہی کے فرماں بردار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,137,'2:137','پھر اگر وہ ایمان لائیں جس طرح تم ایمان لائے ہو تو بے شک وہ راہ پاگئے اور اگر وہ پھر جائیں تو اب وہ ضد پر ہیں پس تمھاری طرف سے اللہ ان کے ليے کافی ہے اوروہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,138,'2:138','کہو ہم نے لیا اللہ کا رنگ اور اللہ کے رنگ سے کس کا رنگ اچھا ہے اور ہم اسی کی عبادت کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,139,'2:139','کہو، کیا تم اللہ کے بارے میں ہم سے جھگڑتے ہو، حالاں کہ وہ ہمارا رب بھی ہے اور تمھارا رب بھی ہمارے ليے ہمارے اعمال ہیں اور تمھارے ليے تمھارے اعمال ہیں اور ہم خالص اس کے ليے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,140,'2:140','کیا تم کہتے ہو کہ ابراہیم اور اسماعیل اور اسحاق اوریعقوب اور اس کی اولاد سب یہودی یا نصرانی تھے کہو کہ تم زیادہ جانتے ہو یا اللہ اور اس سے بڑا ظالم اور کون ہوگا جو اس گواہی کو چھپائے جو اللہ کی طرف سے اس کے پاس آئی ہوئی ہے اور جو کچھ تم کرتے ہو اللہ اس سے بے خبر نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,141,'2:141','یہ ایک جماعت تھی جو گزر گئی اس کو ملے گا جو اس نے کمایا اور تم کو ملے گا جو تم نے کمایا اور تم سے ان کے کيے ہوئے کی پوچھ نہ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(2,142,'2:142','اب بے وقوف لوگ کہیں گے کہ مسلمانوں کو کس چیز نے ان کے قبلہ سے پھیر دیا کہو کہ مشرق اور مغرب اللہ ہی کے ہیں وہ جس کو چاہتا ہے سیدھا راستہ دکھاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,143,'2:143','اور اس طرح ہم نے تم کو بیچ کی امت بنادیا تا کہ تم ہو بتانے والے لوگوں پر، اور رسول ہو تم پر بتانے والا اور جس قبلہ پر تم تھے، ہم نے اس کو صرف اس ليے ٹھہرایا تھا کہ ہم جان لیں کہ کون رسول کی پیروی کرتا ہے اور کون اس سے الٹے پاؤں پھر جاتا ہے اور بیشک یہ بات بھاری ہے، مگر ان لوگوں پر جن کو اللہ نے ہدایت دی ہے اور اللہ ایسا نہیں کہ تمھارے ایمان کو ضائع کردے بے شک اللہ لوگوں کے ساتھ شفقت کرنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,144,'2:144','ہم تمھارے منھ کا بار بار آسمان کی طرف اٹھنا دیکھ رہے ہیں پس ہم تم کو اسی قبلہ کی طرف پھیر دیں گے جس کو تم پسند کرتے ہو، اب اپنا رُخ مسجد حرام کی طرف پھیر دو اور تم جہاں کہیں بھی ہو، اپنے رُخ کو اسی کی طرف کرو اور اہل کتاب خوب جانتے ہیں کہ یہ حق ہے اور ان کے رب کی جانب سے ہے اور اللہ بے خبر نہیں اس سے جو وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,145,'2:145','اور اگر تم ان اہل کتاب کے سامنے تمام دلیلیں پیش کردو تب بھی وہ تمھارے قبلہ کو نہ مانیں گے اور نہ تم ان کے قبلہ کی پیروی کرسکتے ہو اور نہ وہ خود ایک دوسرے کے قبلہ کو مانتے ہیں اور اس علم کے بعد جو تمھارے پاس آچکا ہے،اگر تم ان کی خواہشوں کی پیروی کرو گے تو یقیناًتم ظالموں میں ہوجاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,146,'2:146','جن کو ہم نے کتاب دی ہے، وہ اس کو اسی طرح پہچانتے ہیں جس طرح وہ اپنے بیٹوں کو پہچانتے ہیں اور ان میں سے ایک گروہ حق کو چھپارہا ہے، حالاں کہ وہ اس کو جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,147,'2:147','حق وہ ہے جو تیرا رب کہے پس تم ہرگز شک کرنے والوں میں سے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(2,148,'2:148','ہر ایک کے لیے ایک رخ ہے، جدھر وہ منہ کرتا ہے پس تم بھلائیوں کی طرف دوڑو تم جہاں کہیں ہوگے، اللہ تم سب کو لے آئے گا بے شک اللہ سب کچھ کرسکتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,149,'2:149','اور تم جہاں سے بھی نکلو، اپنا رخ مسجد حرام کی طرف کرو بے شک یہ حق ہے، تمھارے رب کی طرف سے ہے اور جو کچھ تم کرتے ہو، اللہ اس سے بے خبر نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,150,'2:150','اور تم جہاں سے بھی نکلو، اپنا رخ مسجد حرام کی طرف کرو اور تم جہاں بھی ہو، اپنے رخ كو اسی کی طرف رکھو تاکہ لوگو ں کو تمھارے اوپر کوئی حجت باقی نہ رہے، سوا ان لوگوں کے جو ان میں بے انصاف ہیں پس تم ان سے نہ ڈرو اور مجھ سے ڈرو اور تاکہ میں اپنی نعمت تمھارے اوپر پوری کردوں اور تاکہ تم راہ پاجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(2,151,'2:151','جس طرح ہم نے تمھارے درمیان ایک رسول تم ہی میں سے بھیجا جو تم کو ہماری آیتیں پڑھ کر سناتا ہے اور تم کو پاک کرتا ہے اور تم کو کتاب کی اور حکمت کی تعلیم دیتا ہے اور تم کو وہ چیزیں سکھا رہا ہے جن کو تم نہیں جانتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(2,152,'2:152','پس تم مجھ کو یاد رکھو، میں تم کو یاد رکھوں گا اور میرا احسان مانو، میری ناشکری مت کرو');
INSERT INTO "ur_wahid_uddin" VALUES(2,153,'2:153','اے ایمان والو، صبر اور نماز سے مدد حاصل کرو یقیناًاللہ صبر کرنے والوں کے ساتھ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,154,'2:154','اور جو لوگ اللہ کی راہ میں مارے جائیں ، ان کو مردہ مت کہوبلکہ وہ زندہ ہیں ، مگر تم کو خبر نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,155,'2:155','اور ہم ضرور تم کو آزمائیں گے کچھ ڈر اور بھوک سے اور مالوں اور جانوں اور پھلوں کی کمی سے اور ثابت قدم رہنے والوں کو خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(2,156,'2:156','جن کا حال یہ ہے کہ جب ان کو کوئی مصیبت پہنچتی ہے تو وہ کہتے ہیں  ہم اللہ کے ہیں اور ہم اسی کی طرف لوٹنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,157,'2:157','یہی لوگ ہیں جن کے اوپر ان کے رب کی شاباشیاں ہیں اور رحمت ہے اور یہی لوگ ہیں جو راہ پرہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,158,'2:158','صفا اور مروہ، بے شک اللہ کی یاد گاروں میں سے ہیں پس جو شخص بیت اللہ کا حج کرے یا عمرہ کرے تو اس پر کوئی حرج نہیں کہ وہ ان کا طواف کرے اور جو کوئی شوق سے کچھ نیکی کرے تو اللہ قدردان ہے، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,159,'2:159','جو لوگ چھپاتے ہیں ہماری اتاری ہوئی کھلی نشانیوں کو اور ہماری ہدایت کو، بعد اس کے کہ ہم اس کو لوگوں کے ليے کتاب میں کھول چکے ہیں تو وہی لوگ ہیں جن پر اللہ لعنت کرتا ہے اور ان پر لعنت کرنے والے لعنت کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,160,'2:160','البتہ جنھوں نے توبہ کی اور اصلاح کرلی اور واضح طور پر بیان کردیا تو ان کو میں معاف کردوں گا اور میں ہوں معاف کرنے والا، مہربان');
INSERT INTO "ur_wahid_uddin" VALUES(2,161,'2:161','بے شک جن لوگوں نے انکار کیا اور وہ اسی حال میں مرگئے تو وہی لوگ ہیں کہ ان پر اللہ کی اور فرشتوں کی اور تمام انسانوں کی لعنت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,162,'2:162','اسی حال میں وہ ہمیشہ رہیں گے ان پر سے عذاب ہلکا نہ کیا جائے گا اور نہ ان کو ڈھیل دی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(2,163,'2:163','اور تمھارامعبود ایک ہی معبود ہے اس کے سوا کوئی معبود نہیں وہ بڑا مہربان ہے، نہایت رحم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,164,'2:164','بے شک آسمانوں اور زمین کی بناوٹ میں اور رات اور دن کے آنے جانے میں اور ان کشتیوں میں جو انسانوں کے کام آنے والی چیزیں لے کر سمندر میں چلتی ہیں ، اور اس پانی میں جس کو اللہ نے آسمان سے اتارا پھر اس سے مردہ زمین کو زندگی بخشی اور اس نے زمین میں سب قسم کے جانور پھیلادئے اور ہواؤں کی گردش میں اور بادلوں میں جو آسمان اور زمین کے درمیان حکم کے تابع ہیں ، ان لوگوں کے ليے نشانیاں ہیں جو عقل سے کام لیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,165,'2:165','اور کچھ لوگ ایسے ہیں جو اللہ کے سوا دوسروں کو اس کا برابر ٹھہراتے ہیں ان سے ایسی محبت رکھتے ہیں جیسی محبت اللہ سے رکھنا چاہيے اور جو ایمان والے ہیں ، وہ سب سے زیادہ اللہ سے محبت رکھنے والے ہیں اور اگر یہ ظالم اس وقت کو دیکھ لیں جب کہ وہ عذاب سے دوچار ہوں گے کہ زور سارا کا سارا اللہ کا ہے اور اللہ بڑا سخت عذاب دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,166,'2:166','جب کہ وہ لوگ جن کے کہنے پر دوسرے چلتے تھے ان لوگوں سے الگ ہوجائیں گے جو ان کے کہنے پر چلتے تھے عذاب ان کے سامنے ہوگا اور ان کے سب طرف کے رشتے بالکل ٹوٹ چکے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,167,'2:167','وہ لوگ جو پیچھے چلے تھے، کہیں گے کاش ہم کو دنیا کی طرف لوٹنے كا موقع مل جاتا تو ہم بھی ان سے الگ ہوجاتے جیسے یہ ہم سے الگ ہوگئے اس طرح اللہ ان کے اعمال کو انھیں حسرت بناکر دکھائے گا اور وہ آگ سے نکل نہ سکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,168,'2:168','لوگو! زمین کی چیزوں میں سے حلال اور پاکیزہ چیزیں کھاؤ اور شیطان کے قدموں پر مت چلو، بے شک وہ تمھارا کھلا ہوا دشمن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,169,'2:169','وہ تم کو صرف برے کام اور بے حیائی کی تلقین کرتا ہے اور اس بات کی کہ تم اللہ کی طرف وہ باتیں منسوب کرو جن کے بارے میں تم کو کوئی علم نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,170,'2:170','اورجب ان سے کہا جاتا ہے کہ اس پر چلو جو اللہ نے اتارا ہے تو وہ کہتے ہیں کہ ہم اس پر چلیں گے جس پر ہم نے اپنے باپ دادا کو پایا ہے کیا اس صورت میں بھی کہ ان کے باپ دادا نہ عقل رکھتے ہوں اور نہ سیدھی راہ جانتے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(2,171,'2:171','اور ان منکروں کی مثال ایسی ہے جیسے کوئی شخص ایسے جانور کے پیچھے چلّا رہا ہو جو بلانے اور پکارنے کے سوا اور کچھ نہیں سنتا یہ بہرے ہیں ، گونگے ہیں ، اندھے ہیں وہ کچھ نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,172,'2:172','اے ایمان والو، ہماری دی ہوئی پاک چیزوں کو کھاؤ اور اللہ کا شکر ادا کرو اگر تم اس کی عبادت کرنے والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,173,'2:173','اللہ نے تم پر حرام کیا ہے صرف مردار کو اور خون کو اور سور کے گوشت کو اور جس پر اللہ کے سوا کسی اور کا نام لیا گیا ہو پھر جو شخص مجبور ہوجائے، وہ نہ خواہش مند ہو اور نہ حد سے آگے بڑھنے والا ہو، تو اس پر کوئی گناہ نہیں بے شک اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,174,'2:174','جو لوگ اس چیز کو چھپاتے ہیں جو اللہ نے اپنی کتاب میں اتاری ہے اور اس کے بدلے میں تھوڑا مول لیتے ہیں ، وہ اپنے پیٹ میں صرف آگ بھر رہے ہیں قیامت کے دن اللہ نہ ان سے ہم کلام ہوگا اور نہ ان کو پاک کرے گا اور ان کے ليے درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,175,'2:175','یہ وہ لوگ ہیں جنھوں نے ہدایت کے بدلے گمراہی کا سودا کیا اوربخشش کے بدلے عذاب کا، تو کیسی سہار ہے ان کو آگ کی');
INSERT INTO "ur_wahid_uddin" VALUES(2,176,'2:176','یہ اس ليے کہ اللہ نے اپنی کتاب کو ٹھیک ٹھیک اتارا، مگر جن لوگوں نے کتاب میں کئی راہیں نکال لیں ، وہ ضد میں دور جا پڑے');
INSERT INTO "ur_wahid_uddin" VALUES(2,177,'2:177','نیکی یہ نہیں کہ تم اپنے منہ پورب اورپچھم کی طرف کرلو، بلکہ نیکی یہ ہے کہ آدمی ایمان لائے اللہ پر اور آخرت کے دن پر اور فرشتوں پر اور کتاب پر اور پیغمبروں پر اورمال دے اللہ کی محبت میں رشتہ داروں کو اور یتیموں کو اور محتاجوں کو اور مسافروں کو اور مانگنے والوں کو اور گردنیں چھڑانے میں اور نماز قائم کرے اور زکوٰۃ ادا کرے اور جب عہد کرلیں تو اس کو پورا کریں اور صبر کرنے والے سختی اور تکلیف میں اور لڑائی کے وقت یہی لوگ ہیں جو سچے نکلے اور یہی ہیں ڈر رکھنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(2,178,'2:178','اے ایمان والو، تم پر مقتولوں کا قصاص لینا فرض کیا جاتا ہے آزاد کے بدلے آزاد، غلام کے بدلے غلام،عورت کے بدلے عورت پھر جس کو اس کے بھائی کی طرف سے کچھ معافی ہوجائے تو اس کو چاہئے کہ وہ معروف کی پیروی کرے اور خوبی کے ساتھ اس کو ادا کرےیہ تمھارے رب کی طرف سے ایک آسانی اور مہربانی ہے اب اس کے بعد بھی جو شخص زیادتی کرے، اس کے ليے درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,179,'2:179','اور اے عقل والو، قصاص میں تمھارے ليے زندگی ہے تاکہ تم بچو');
INSERT INTO "ur_wahid_uddin" VALUES(2,180,'2:180','تم پر فرض کیا جاتا ہے کہ جب تم میں سے کسی کی موت کا وقت آجائے اور وہ اپنے پیچھے، مال چھوڑ رہا ہو تو وہ معروف کے مطابق وصیت کردے اپنے ماں باپ کے ليے اور اپنے قرابت داروں کے ليے یہ ضروری ہے خدا سے ڈرنے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(2,181,'2:181','پھر جو کوئی وصیت کو سننے کے بعد اس کو بدل ڈالے تو اس کا گناہ اسی پر ہوگا جس نے اس کو بدلا، یقینا اللہ سننے والا، جاننے والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,182,'2:182','البتہ جس کو وصیت کرنے والے کی بابت یہ اندیشہ ہو کہ اس نے جانب داری یا حق تلفی کی ہے اور وہ آپس میں صلح کرادے تو اس پر کوئی گناہ نہیں اللہ معاف کرنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,183,'2:183','اے ایمان والو، تم پر روزہ فرض کیا گیا جس طرح تم سے اگلوں پر فرض کیا گیا تھا، تاکہ تم پرہیز گار بنو');
INSERT INTO "ur_wahid_uddin" VALUES(2,184,'2:184','گنتی کے چند دن پھر جو کوئی تم میں بیمار ہو یا سفر میں ہو تو وہ دوسرے دنوں میں تعداد پوری کرلے اور جو اس کو بہ مشقت برداشت کرسکیں ، تو ایک روزہ کا بدلہ ایک مسکین کا کھانا ہے جو کوئی مزید نیکی کرے تو وہ اس کے ليے بہتر ہے اور تم روزہ رکھو تو یہ تمھارے ليے زیادہ بہتر ہے، اگر تم جانو');
INSERT INTO "ur_wahid_uddin" VALUES(2,185,'2:185','رمضان کا مہینہ جس میں قرآن اتارا گیا، ہدایت ہے لوگوں کے ليے اور کھلی نشانیاں راستہ کی اور حق اور باطل کے درمیان فیصلہ کرنے والا پس تم میں سے جو کوئی اس مہینہ کو پائے، وہ اس کے روزے رکھے اور جو بیمار ہو یا سفر پر ہو تو وہ دوسرے دنوں میں گنتی پوری کرلے اللہ تمھارے ليے آسانی چاہتا ہے، وہ تمھارے ساتھ سختی کرنا نہیں چاہتا اور اس ليے کہ تم گنتی پوری کرلو اور اللہ کی بڑائی کرو اس پر کہ اس نے تم کو راہ بتائی اور تاکہ تم اس کے شکر گزار بنو');
INSERT INTO "ur_wahid_uddin" VALUES(2,186,'2:186','اور جب میرے بندے تم سے میری بابت پوچھیں تو میں نزدیک ہوں ،پکارنے والے کی پکار کا جواب دیتا ہوں جب کہ وہ مجھے پکارتا ہے، تو چاہيے کہ وہ میرا حکم مانیں اور مجھ پر یقین رکھیں ، تاکہ وہ ہدایت پائیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,187,'2:187','تمھارے ليے روزہ کی رات میں اپنی بیویوں کے پاس جانا جائز کیا گیا وہ تمھارے ليے لباس ہیں اور تم ان کے ليے لباس ہو اللہ نے جانا کہ تم اپنے آپ سے خیانت کر رہے تھے تو اس نے تم پر عنایت کی اور تم کو معاف کردیا تو اب تم ان سے ملو اور چاہو جو اللہ نے تمھارے ليے لکھ دیا ہے اور کھاؤ اور پیو، یہاں تک کہ صبح کی سفید دھاری کالی دھاری سے الگ ظاہر ہوجائے پھر پورا کرو روزہ رات تک اور جب تم مسجد میں اعتکاف میں ہو تو بیویوں سے خلوت نہ کرویہ اللہ کی حدیں ہیں تو ان کے نزدیک نہ جاؤ اس طرح اللہ اپنی آیتیں لوگوں کے ليے بیان کرتا ہے تاکہ وہ بچیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,188,'2:188','اور تم آپس میں ایک دوسرے کے مال کو ناحق طور پر نہ کھاؤ اور ان کو حاکموں تک نہ پہنچاؤ، تاکہ دوسروں کے مال کا کوئی حصہ بطریقِ گناہ کھاجاؤ حالاں کہ تم اس کو جانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,189,'2:189','وہ تم سے چاندوں کی بابت پوچھتے ہیں کہہ دو کہ وہ اوقات ہیں لوگوں کے ليے اور حج کے ليے اور نیکی یہ نہیں کہ تم گھروں میں آؤ چھت پر سے، بلکہ نیکی یہ ہے کہ آدمی پرہیز گاری کرے اور گھروں میں ان کے دروازوں سے آؤ اور اللہ سے ڈرو تاکہ تم کامیاب ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,190,'2:190','اور اللہ کی راہ میں ان لوگوں سے لڑو جو لڑتے ہیں تم سے اور زیادتی نہ کرو اللہ زیادتی کرنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(2,191,'2:191','اور قتل کرو ان کو جس جگہ پاؤ اور نکال دو ان کو جہاں سے انھوں نے تم کو نکالا ہےاور فتنہ سخت تر ہے قتل سے اور ان سے مسجد حرام کے پاس نہ لڑو جب تک کہ وہ تم سے اس میں جنگ نہ چھیڑیں پس اگر وہ تم سے جنگ چھیڑیں تو ان کو قتل کرو یہی سزا ہے منکروں کی');
INSERT INTO "ur_wahid_uddin" VALUES(2,192,'2:192','پھر اگر وہ بازآ جائیں تو اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,193,'2:193','اور ان سے جنگ کرو یہاں تک کہ فتنہ باقی نہ رہے اور دین اللہ کا ہوجائے، پھر اگر وہ باز آجائیں تو اس کے بعد سختی نہیں ہے مگر ظالموں پر');
INSERT INTO "ur_wahid_uddin" VALUES(2,194,'2:194','حرمت والا مہینہ حرمت والے مہینہ کا بدلہ ہے اور حرمتوں کا بھی قصاص ہے پس جس نے تم پر زیادتی کی تم بھی اس پر زیادتی کرو جیسی اس نے تم پر زیادتی کی ہے اور اللہ سے ڈرو اور جان لو کہ اللہ پرہیزگاروں کے ساتھ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,195,'2:195','اور اللہ کی راہ میں خرچ کرو اور اپنے آپ کو ہلاکت میں نہ ڈالو اور کام اچھی طرح کرو بے شک اللہ پسند کرتا ہے اچھی طرح کام کرنے والوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(2,196,'2:196','اور پورا کروحج اور عمرہ اللہ کے ليے پھر اگر تم گھر جاؤ تو جو قربانی کا جانور میسر ہو، وہ پیش کردو اور اپنے سروں کو نہ منڈاؤ جب تک کہ قربانی اپنے ٹھکانے پر نہ پہنچ جائے تم میں سے جو بیمار ہو یااس کے سر میں کوئی تکلیف ہو تو وہ فدیہ دے روزہ یا صدقہ یا قربانی کا جب امن کی حالت ہو اور کوئی حج تک عمرہ کا فائدہ حاصل کرنا چاہے تو وہ قربانی پیش کرے جو اس کو میسر آئے پھر جس کو میسر نہ آئے تو وہ حج کے ایام میں تین دن کے روزے رکھے اور سات دن کے روزے جب کہ تم گھروں کو لوٹو یہ پورے دس دن ہوئے یہ اس شخص کے ليے ہے جس کا خاندان مسجد حرام کے پاس آباد نہ ہو اللہ سے ڈرو اورجان لو کہ اللہ سخت عذاب دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,197,'2:197','حج کے متعین مہینے ہیں پس جس نے حج کا عزم کرلیا تو پھر اس کو حج کے دوران نہ کوئی فحش بات کرنی ہے اور نہ گناہ کی اور نہ لڑائی جھگڑے کی اور جو نیک کام تم کرو گے، اللہ اس کو جان لے گا اور تم زادِ راہ لو بہترین زادِراہ تقویٰ کا زادِ راہ ہے اور اے عقل والو، مجھ سے ڈرو');
INSERT INTO "ur_wahid_uddin" VALUES(2,198,'2:198','تم پر اس میں کوئی گناہ نہیں کہ تم اپنے رب کا فضل تلاش کرو پھر جب تم لوگ عرفات سے واپس ہو تو اللہ کو یاد کرو مشعرِ حرام کے نزدیک اور اس کو یاد کرو جس طرح اللہ نے بتایا ہے اس سے پہلے یقینا تم راہ بھٹکے ہوئے لوگوں میں تھے');
INSERT INTO "ur_wahid_uddin" VALUES(2,199,'2:199','پھر طواف کو چلو جہاں سے سب لوگ چلیں اور اللہ سے معافی مانگو یقینا اللہ بخشنے والا، رحم کرنے والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,200,'2:200','پھر جب تم اپنے حج کے اعمال پورے کرلو تو اللہ کو یاد کرو جس طرح تم پہلے اپنے باپ دادا کو یاد کرتے تھے، بلکہ اس سے بھی زیادہ پس کوئی آدمی کہتا ہے اے ہمارے رب، ہم کو اسی دنیا میں دے دے، اور آخرت میں اس کا کچھ حصہ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,201,'2:201','اور کوئی آدمی ہے جو کہتا ہے کہ اے ہمارے رب، ہم کو دنیا میں بھلائی دے اور آخرت میں بھی بھلائی دے اور ہم کو آگ کے عذاب سے بچا');
INSERT INTO "ur_wahid_uddin" VALUES(2,202,'2:202','انھیں لوگوں کے لیے حصہ ہے ان کے کیے کا، اور اللہ جلد حساب لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,203,'2:203','اور اللہ کو یاد کرو مقرر دنوں میں پھر جو شخص جلدی کرکے دو دن میں مکہ واپس آجائے اس پر کوئی گناہ نہیں اور جو شخص ٹھہر جائے، اس پر بھی کوئی گناہ نہیں یہ اس کے لیے ہے جو اللہ سے ڈرے اور تم اللہ سے ڈرتے رہو اور خوب جان لو کہ تم اسی کے پاس اكٹھا کیے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(2,204,'2:204','اور لوگوں میں سے کوئی ہے کہ اس کی بات دنیا کی زندگی میں تم کو خوش كن لگتی ہے اور وہ اپنے دل کی بات پر اللہ کو گواہ بناتا ہے حالاں کہ وہ سخت جھگڑالو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,205,'2:205','اور جب وہ پیٹھ پھیرتا ہے تو وہ اس کوشش میں رہتا ہے کہ زمین میں فساد پھیلائے اور کھیتیوں اور جانوں کو ہلاک کرے حالاں کہ اللہ فساد کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(2,206,'2:206','اور جب اس سے کہاجاتا ہے کہ اللہ سے ڈر تو وقار اس کو گناہ پر جمادیتاہے پس ایسے شخص کے ليے جہنم کافی ہے اور وہ بہت برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,207,'2:207','اور لوگوں میں کوئی ہے کہ اللہ کی خوشی کی تلاش میں اپنی جان کو بیچ دیتا ہے اور اللہ اپنے بندوں پر نہایت مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,208,'2:208','اے ایمان والو، اسلام میں پورے پورے داخل ہوجاؤ اور شیطان کے قدموں پر مت چلو، وہ تمھارا کھلا ہوا دشمن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,209,'2:209','اگر تم پھسل جاؤ بعد اس کے کہ تمھارے پاس واضح دلیلیں آچکی ہیں تو جان لو کہ اللہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,210,'2:210','کیا لوگ اس انتظار میں ہیں کہ اللہ بادل کے سائبانوں میں آئے اور فرشتے بھی آجائیں اور معاملہ کا فیصلہ کردیا جائے اور سارے معاملات اللہ ہی کی طرف پھیرے جاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,211,'2:211','بنی اسرائیل سے پوچھو، ہم نے ان کو کتنی کھلی کھلی نشانیاں دیں اور جو شخص اللہ کی نعمت کو بدل ڈالے جب کہ وہ اس کے پاس آچکی ہو تو اللہ یقینا سخت سزا دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,212,'2:212','خوش نما کردی گئی ہے دنیا کی زندگی ان لوگوں کی نظر میں جو منکر ہیں اور وہ ایمان والوں پر ہنستے ہیں حالاں کہ جو پرہیز گار ہیں ، وہ قیامت کے دن ان کے مقابلہ میں اونچے ہوں گے اور اللہ جس کو چاہتا ہے، بے حساب روزی دے دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,213,'2:213','لوگ ایک امت تھے انھوں نے اختلاف کیا تو اللہ نے پیغمبروں کو بھیجا خوش خبری دینے والے اور ڈرانے والے اور ان کے ساتھ اتاری کتاب حق کے ساتھ، تاکہ وہ فیصلہ کردے ان باتوں کا جن میں لوگ اختلاف کر رہے ہیں اور یہ اختلافات انھیں لوگوں نے کيے جن کو حق دیا گیا تھا، بعد اس کے کہ ان کے پاس کھلی کھلی ہدایات آچکی تھیں ، آپس کی ضد کی وجہ سے پس اللہ نے اپنی توفیق سے حق کے معاملہ میں ایمان والوں کو راہ دکھائی جس میں وہ جھگڑ رہے تھے اور اللہ جس کو چاہتا ہے سیدھی راہ دکھا دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,214,'2:214','کیا تم نے یہ سمجھ رکھا ہے کہ تم جنت میں داخل ہوجاؤ گے، حالاں کہ ابھی تم پر وہ حالات گزرے ہی نہیں جو تمھارے اگلوں پر گزرے تھے ان کو سختی اور تکلیف پہنچی اور وہ ہلا مارے گئے یہاں تک کہ رسول اور ان کے ساتھ ایمان لانے والے پکار اٹھے کہ اللہ کی مدد کب آئے گی یاد رکھو، اللہ کی مدد قریب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,215,'2:215','لوگ تم سے پوچھتے ہیں کہ وہ کیا خرچ کریں کہہ دو کہ جو مال تم خرچ کرو تو اس میں حق ہے تمھارے ماں باپ کا اور رشتہ داروں کا اور یتیموں کا اور محتاجوں کا اور مسافروں کا اور جو بھلائی تم کرو گے، وہ اللہ کومعلوم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,216,'2:216','تم پر لڑائی کا حکم ہوا ہے اور وہ تم کو گراں معلوم ہوتی ہے ہوسکتا ہے کہ تم ایک چیز کوناگوار سمجھو اور وہ تمھارے ليے بھلی ہو اور ہوسکتا ہے کہ تم ایک چیز کو پسند کرو اور وہ تمھارے ليے بری ہو اور اللہ جانتاہے تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,217,'2:217','لوگ تم سے حرمت والے مہینہ کی بابت پوچھتے ہیں کہ اس میں لڑنا کیسا ہے کہہ دو کہ اس میں لڑنا بہت برا ہے مگر اللہ کے راستہ سے روکنا اور اس کاانکار کرنا اور مسجد حرام سے روکنا اور اس کے لوگوں کو اس سے نکالنا، اللہ کے نزدیک اس سے بھی زیادہ برا ہے اور فتنہ قتل سے بھی زیادہ بڑی برائی ہے اور یہ لوگ تم سے برابر لڑتے رہیں گے یہاں تک کہ وہ تم کو تمھارے دین سے پھیر دیں اگر قابو پائیں اور تم میں سے جو کوئی اپنے دین سے پھرے گا اورکفر کی حالت میں مرجائے تو ایسے لوگوں کے عمل ضائع ہوگئے دنیا میں اور آخرت میں اور وہ آگ میں پڑنے والے ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,218,'2:218','وہ لوگ جو ایمان لائے اور جنھوں نے ہجرت کی اور اللہ کی راہ میں جہادکیا، وہ اللہ کی رحمت کے امیدوار ہیں اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,219,'2:219','لوگ تم سے شراب اور جوئے کی بابت پوچھتے ہیں کہہ دو کہ ان دونوں چیزوں میں بڑا گناہ ہے اور لوگوں کے ليے کچھ فائدے بھی ہیں اور ان کا گناہ بہت زیادہ ہے، ان کے فائدے سے اور وہ تم سے پوچھتے ہیں کہ وہ کیا خرچ کریں کہہ دو کہ جو حاجت سے زیادہ ہو اس طرح اللہ تمھارے ليے احکام کو بیان کرتا ہے تاکہ تم دھیان کرو');
INSERT INTO "ur_wahid_uddin" VALUES(2,220,'2:220','دنیا اور آخرت کے معاملات میں اور وہ تم سے یتیموں کی بابت پوچھتے ہیں کہہ دو کہ جس میں ان کی بہبود ہو وہ بہتر ہے اور اگر تم ان کو اپنے ساتھ شامل کرلو تو وہ تمھارے بھائی ہیں اور اللہ کو معلوم ہے کہ کون خرابی پیدا کرنے والا ہے اور کون درستگی پیدا کرنے والا اور اگر اللہ چاہتا تو تم کو مشکل میں ڈال دیتا اللہ زبردست ہے، تدبیر والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,221,'2:221','اور مشرک عورتوں سے نکاح نہ کرو جب تک وہ ایمان نہ لائیں ،اور مومن کنیز بہتر ہے ایک مشرک عورت سے، اگرچہ وہ تم کو اچھی معلوم ہو اور اپنی عورتوں کو مشرک مردوں کے نکاح میں نہ دو جب تک وہ ایمان نہ لائیں ، مومن غلام بہتر ہے ایک آزاد مشرک سے، اگرچہ وہ تم کواچھا معلوم ہو یہ لوگ آگ کی طرف بلاتے ہیں اور اللہ جنت کی طرف اور اپنی بخشش کی طرف بلاتا ہےاپنے حكم سے وہ اپنے احکام لوگوں کے ليے کھول کر بیان کرتا ہے تاکہ وہ نصیحت پکڑیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,222,'2:222','اور وہ تم سے حیض کا حکم پوچھتے ہیں کہہ دو کہ وہ ایک گندگی ہے، اس میں عورتوں سے الگ رہو اور جب تک وہ پاک نہ ہوجائیں ، ان کے قریب نہ جاؤ پھر جب وہ اچھی طرح پاک ہوجائیں تو ا س طریقہ سے ان کے پاس جاؤ جس کا اللہ نے تم کو حکم دیا ہے اللہ دوست رکھتا ہے تو بہ کرنے والوں کو اور وہ دوست رکھتا ہے پاک رہنے والوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(2,223,'2:223','تمھاری عورتیں تمھاری کھیتیاں ہیں پس اپنی کھیتی میں جس طرح چاہو جاؤ اور اپنے ليے آگے بھیجو اور اللہ سے ڈرو اور جان لو کہ تمھیں ضرور اس سے ملناہے اور ایمان والوں کو خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(2,224,'2:224','اور اللہ کو اپنی قسموں کا نشانہ نہ بناؤ کہ تم بھلائی نہ کرو اور پرہیز گاری نہ کرو اور لوگوں کے درمیان صلح نہ کرو اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,225,'2:225','اللہ تمھاری بے ارادہ قسموں پر تم کو نہیں پکڑتا، مگر وہ اس کام پر پکڑتا ہے جو تمھارے دل کرتے ہیں اور اللہ بخشنے والا، تحمل والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,226,'2:226','جو لوگ اپنی بیویوں سے نہ ملنے کی قسم کھالیں ، ان کے ليے چار مہینے تک کی مہلت ہے پھر اگر وہ رجوع کرلیں تو اللہ معاف کردینے والا، مہر بان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,227,'2:227','اور اگر وہ طلاق کا فیصلہ کریں تو یقینا اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,228,'2:228','اورطلاق دی ہوئی عورتیں اپنے آپ کو تین حیض تک روکے رکھیں ، اور اگر وہ اللہ پر اور آخرت کے دن پر ایمان رکھتی ہیں تو ان کے ليے جائز نہیں کہ وہ اس چیز کو چھپائیں جو اللہ نے پیدا کیا ہے ان کے پیٹ میں اور اس دوران میں ان کے شوہر ان کو پھر لوٹا لینے کا حق رکھتے ہیں اگر وہ صلح کرنا چاہیں اور ان عورتوں کے ليے دستور کے مطابق اسی طرح حقوق ہیں جس طرح دستور کے مطابق ان پر ذمہ داریاں ہیں اور مردوں کا ان کے مقابلہ میں کچھ درجہ بڑھا ہوا ہے اور اللہ زبردست ہے، تدبیر والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,229,'2:229','طلاق دوبار ہے پھر یا تو قاعدہ کے مطابق رکھ لینا ہے یا خوش اسلوبی کے ساتھ رخصت کردینا اور تمھارے ليے یہ بات جائز نہیں کہ تم نے جو کچھ ان عورتوں کو دیا ہے، اس میں سے کچھ لے لو مگر یہ کہ دونوں کو ڈر ہو کہ وہ اللہ کی حدوں پر قائم نہ رہ سکیں گے، پھر اگر تم کو یہ ڈر ہو کہ دونوں اللہ کی حدوں پر قائم نہ رہ سکیں گے تو دونوں پر گناہ نہیں اس مال میں جس کو عورت فدیہ میں دے یہ اللہ کی حدیں ہیں تو ان سے باہر نہ نکلو اور جو شخص اللہ کی حدوں سے نکل جائے تو وہی لوگ ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,230,'2:230','پھر اگر وہ اس کو طلاق دیدے تو اس کے بعد وہ عورت اس کے ليے حلال نہیں جب تک کہ وہ کسی دوسرے مرد سے نکاح نہ کرے پھر اگر وہ مرد اس کو طلاق دیدے تب گناہ نہیں ان دونوں پر کہ پھر مل جائیں بشرطیکہ انھیں اللہ کی حدوں پر قائم رہنے کی توقع ہو یہ خداوندی ضابطے ہیں جن کو وہ بیان کر رہا ہے ان لوگوں کے ليے جو دانش مند ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,231,'2:231','اور جب تم عورتوں کو طلاق دے دو اور وہ اپنی عدت تک پہنچ جائیں تو ان کو یاقاعدہ کے مطابق رکھ لو یا قاعدہ کے مطابق رخصت کردو اور تکلیف پہنچانے کی غرض سے نہ روکو تاکہ ان پر زیادتی کرو اور جو ایسا کرے گا، اس نے اپنا ہی بُرا کیااور اللہ کی آیتوں کو کھیل نہ بناؤ اور یاد کرو اپنے اوپر اللہ کی نعمت کواور اس کتاب و حکمت کو جو اس نے تمھاری نصیحت کے ليے اتاری ہے اور اللہ سے ڈرو اور جان لو کہ اللہ ہر چیزکوجاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,232,'2:232','اور جب تم اپنی عورتوں کو طلاق دے دو اور وہ اپنی عدت پوری کرلیں تو ان کو نہ روکو کہ وہ اپنے شوہروں سے نکاح کرلیں جب کہ وہ دستور کے موافق آپس میں راضی ہوجائیں یہ نصیحت کی جاتی ہے اس شخص کو جو تم میں سے اللہ پر اور آخرت کے دن پر یقین رکھتا ہو یہ تمھارے ليے زیادہ پاکیزہ اور ستھرا طریقہ ہے اور اللہ جانتا ہے، تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,233,'2:233','اور مائیں اپنے بچوں کو پورے دو سال تک دودھ پلائیں ، ان لوگوں کے ليے جو پوری مدت تک دودھ پلانا چاہتے ہوں اور جس کا بچہ ہے اس کے ذمہ ہے ان ماؤں کا کھانا اور کپڑا دستور کے مطابق کسی کو حکم نہیں دیاجاتا مگر اس کی برداشت کے موافق نہ کسی ماں کو اس کے بچہ کے سبب سے تکلیف دی جائے، اور نہ کسی باپ کو اس کے بچہ کے سبب سے اور یہی ذمہ داری وارث پر بھی ہے پھر اگر دونوں باہمی رضا مندی اور مشورہ سے دودھ چھڑانا چاہیں تو دونوں پر کوئی گناہ نہیں ، اور اگر تم چاہو کہ اپنے بچوں کو کسی اور سے دودھ پلواؤ تب بھی تم پر کوئی گناہ نہیں بشرطیکہ تم قاعدہ کے مطابق وہ ادا کر دو جو تم نے ان کو دینا ٹھہرایا تھا اور اللہ سے ڈرو اور جان لو کہ جو کچھ تم کرتے ہو، اللہ اس کو دیکھ رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,234,'2:234','اور تم میں سے جو لوگ مرجائیں اور بیویاں چھوڑ جائیں ، وہ بیویاں اپنے آپ کو چار مہینے دس دن تک انتظار میں رکھیں پھر جب وہ اپنی مدت کو پہنچیں تو جو کچھ وہ اپنی ذات کے بارے میں قاعدہ کے موافق کریں اس کا تم پر کوئی گناہ نہیں اور اللہ تمھارے کاموں سے پوری طرح باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,235,'2:235','اور تمھارے ليے اس بات میں کوئی گناہ نہیں کہ ان عورتوں کوپیغام دینے میں کوئی بات اشارۃً کہو یا اپنے دل میں چھپائے رکھو اللہ کو معلوم ہے کہ تم ضرور ان کا دھیان کرو گے مگر چھپ کر ان سے وعدہ نہ کرو، تم ان سے صرف دستور کے مطابق کوئی بات کہہ سکتے ہو اور عقد ِ نکاح کا ارادہ اس وقت تک نہ کرو جب تک مقررہ مدت اپنی ختم کو نہ پہنچ جائے اور جان لو کہ اللہ جانتا ہے جو کچھ تمھارے دلوں میں ہے پس اس سے ڈرو اورجان لو کہ اللہ بخشنے والا، تحمل والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,236,'2:236','اگر تم عورتوں کو ایسی حالت میں طلاق دو کہ نہ ان کو تم نے ہاتھ لگایا ہے اور نہ ان کے ليے کچھ مہر مقرر کیا ہے تو ان کے مہر کا تم پر کچھ مواخذہ نہیں البتہ ان کو دستور کے مطابق کچھ سامان دے دو، وسعت والے پر اپنی حیثیت کے مطابق ہے اور تنگی والے پر اپنی حیثیت کے مطابق، یہ نیکی کرنے والوں پر لازم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,237,'2:237','اور اگر تم ان کو طلاق دو قبل اس کے کہ ان کو ہاتھ لگاؤ اور تم ان کے ليے کچھ مہر بھی مقرر کرچکے تھے تو جتنامہر تم نے مقرر کیا ہو اس کا آدھا ادا کردو، الا یہ کہ وہ معاف کردیں ، یا وہ مرد معاف کردے جس کے ہاتھ میں نکاح کی گرہ ہے اور تمھارا معاف کردینا زیادہ قریب ہے تقویٰ سے اور آپس میں احسان کرنے سے غفلت مت کرو جو کچھ تم کرتے ہو اللہ اس کو دیکھ رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,238,'2:238','پابندی کرو نمازوں کی اور پابندی کرو بیچ کی نماز کی اور کھڑے ہو اللہ کے سامنے عاجز بنے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(2,239,'2:239','اگر تم کو اندیشہ ہو تو پیدل یا سواری پر پڑھ لو پھر جب حالتِ امن آجائے تو اللہ کو اس طریقہ پر یاد کرو جو اس نے تم کو سکھایا ہے، جس کو تم نہیں جانتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(2,240,'2:240','اور تم میں سے جو لوگ وفات پاجائیں اور بیویاں چھوڑ رہے ہوں ، وہ اپنی بیویوں کے بارے میں وصیت کردیں کہ ایک سال تک ان کو گھر میں رکھ کر خرچ دیا جائے پھر اگر وہ خود سے گھر چھوڑدیں تو جو کچھ وہ اپنی ذات کے معاملہ میں دستور کے مطابق کریں ، اس کا تم پر کوئی الزام نہیں اللہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,241,'2:241','اور طلاق دی ہوئی عورتوں کو بھی دستور کے مطابق خرچ دیناہے، یہ لازم ہے پرہیز گاروں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(2,242,'2:242','اس طرح اللہ تمھارے ليے اپنے احکام کھول کربیان کرتا ہے تاکہ تم سمجھو');
INSERT INTO "ur_wahid_uddin" VALUES(2,243,'2:243','کیا تم نے ان لوگوں کو نہیں دیکھا جو اپنے گھروں سے بھاگ کھڑے ہوئے موت کے ڈر سے، اور وہ ہزاروں کی تعداد میں تھے تو اللہ نے ان سے کہا کہ مرجاؤ پھر اللہ نے ان کو زندہ کیا بے شک اللہ لوگوں پر فضل کرنے والا ہےمگر اکثر لوگ شکر نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(2,244,'2:244','اور اللہ کی راہ میں لڑو اورجان لو کہ اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,245,'2:245','کون ہے جو اللہ کو قرضِ حسن دے کہ اللہ اس کو بڑھا کر اس کے ليے کئی گنا کردے اور اللہ ہی تنگی بھی پیدا کرتا ہے اور کشادگی بھی اور تم سب اسی کی طرف لوٹائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,246,'2:246','کیا تم نے بنی اسرائیل کے سرداروں کو نہیں دیکھا موسیٰ کے بعد، جب کہ انھوں نے اپنے نبی سے کہا کہ ہمارے ليے ایک بادشاہ مقرر کردیجئے تاکہ ہم اللہ کی راہ میں لڑیں نبی نے جواب دیا ایسا نہ ہو کہ تم کو لڑائی کا حکم دیا جائے تب تم نہ لڑو انھوں نے کہا یہ کیسے ہوسکتا ہے کہ ہم نہ لڑیں اللہ کی راہ میں حالاں کہ ہم کو اپنے گھروں سے نکالا گیا ہے اور اپنے بچوں سے جدا کیا گیا ہے پھر جب ان کو لڑائی کا حکم ہوا تو تھوڑے لوگوں کے سوا سب پھر گئے اور اللہ ظالموں کو خوب جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,247,'2:247','اور ان کے نبی نے ان سے کہا اللہ نے طالوت کو تمھارے ليے بادشاہ مقرر کیا ہے انھوں نے کہا کہ اس کو ہمارے اوپر بادشاہی کیسے مل سکتی ہے، حالاں کہ اس کے مقابلہ میں ہم بادشاہی کے زیادہ حقدار ہیں اور اس کو زیادہ دولت بھی حاصل نہیں نبی نے کہا اللہ نے تمھارے مقابلہ میں اس کو چنا ہے اور علم اور جسم میں اس کو زیادتی دی ہے اور اللہ اپنی سلطنت جس کو چاہتا ہے دیتاہے اللہ بڑی وسعت والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,248,'2:248','اور ان کے نبی نے ان سے کہاکہ طالوت کے بادشاہ ہونے کی نشانی یہ ہے کہ تمھارے پاس وہ صندوق آجائے گا جس میں تمھارے رب کی طرف سے تمھارے ليے تسکین ہے اور آلِ موسیٰ اور آلِ ہارون کی چھوڑی ہوئی یادگاریں ہیں اس صندوق کو فرشتے لے آئیں گے اس میں تمھارے ليے بڑی نشانی ہے، اگر تم یقین رکھنے والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,249,'2:249','پھر جب طالوت فوجوں کو لے کر چلا تو اس نے کہا اللہ تم کو ایک ندی کے ذریعہ آزمانے والا ہے پس جس نے اس کا پانی پیاوہ میرا ساتھی نہیں اور جس نے اس کو نہ چکھا، وہ میرا ساتھی ہے، مگر یہ کہ کوئی اپنے ہاتھ سے ایک چلو بھرلے تو انھوں نے اس میں سے خوب پیابجز تھوڑے آدمیوں کے پھر جب طالوت اور جو اس کے ساتھ ایمان پر قائم رہے تھے، دریا پار کرچکے تو وہ لوگ بولے کہ آج ہم کوجالوت اور اس کی فوجوں سے لڑنے کی طاقت نہیں جو لوگ یہ جانتے تھے کہ وہ اللہ سے ملنے والے ہیں ، انھوں نے کہا کہ کتنی ہی چھوٹی جماعتیں اللہ کے حکم سے بڑی جماعتوں پر غالب آئی ہیں اور اللہ صبر کرنے والوں کے ساتھ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,250,'2:250','اور جب جالوت اور اس کی فوجوں سے ان کا سامنا ہوا تو انھوں نے کہا اے ہمارے رب، ہمارے اوپر صبر ڈال دے اور ہمارے قدموں کو جمادے اور ان منکروں کے مقابلہ میں ہماری مدد کر');
INSERT INTO "ur_wahid_uddin" VALUES(2,251,'2:251','پھر انھوں نے اللہ کے حکم سے ان کو شکست دی اور داؤد نے جالوت کو قتل کردیا اور اللہ نے داؤد کو بادشاہت اور دانائی عطا کی اور جن چیزوں کا چاہا علم بخشا، اور اگر اللہ بعض لوگوں کو بعض لوگوں سے دفع نہ کرتا رہے تو زمین فساد سے بھرجائےمگر اللہ دنیا والوں پر بڑا فضل فرمانے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,252,'2:252','یہ اللہ کی آیتیں ہیں جو ہم تم کو سناتے ہیں ٹھیک ٹھیک اور بے شک تو پیغمبروں میں سے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,253,'2:253','ان پیغمبروں میں سے بعض کوہم نے بعض پر فضیلت دی ان میں سے بعض سے اللہ نے کلام کیا اور بعض کے درجے بلندکيے اور ہم نے عیسیٰ بن مریم کوکھلی نشانیاں دیں اور ہم نے اس کی مدد کی روح القدس سے اور اللہ اگر چاہتا توان کے بعد والے صاف حکم آجانے کے بعد نہ لڑتے، مگر انھوں نے اختلاف کیا پھر ان میں سے کوئی ایمان لایا اور کسی نے انکار کیا اور اگر اللہ چاہتا تو وہ نہ لڑتے، مگر اللہ کرتا ہے جو وہ چاہتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,254,'2:254','اے ایمان والو، خرچ کرو ان چیزوں ميں سے جو ہم نے تم کو دیا ہے، اس دن کے آنے سے پہلے جس میں نہ خرید و فروخت ہے اور نہ دوستی ہے اور نہ سفارش اور جو منکر ہیں وہی ہیں ظلم کرنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(2,255,'2:255','اللہ، اس کے سوا کوئی معبود نہیں وہ زندہ ہے، سب کا تھامنے والا اس کو نہ اونگھ آتی ہے اور نہ نیند اسی کا ہے جو کچھ آسمانوں اور زمین میں ہے کون ہے جو اس کے پاس اس کی اجازت کے بغیر سفارش کرے وہ جانتا ہے جو کچھ ان کے آگے ہے اور جو کچھ ان کے پیچھے ہے، اور وہ اس کے علم میں سے کسی چیز کا احاطہ نہیں کرسکتے مگر جو وہ چاہے اس کی حکومت آسمانوں اور زمین پر چھائی ہوئی ہےوہ تھکتا نہیں ان کے تھامنے سے اور وہی ہے بلند مرتبہ، بڑا');
INSERT INTO "ur_wahid_uddin" VALUES(2,256,'2:256','دین کے معاملہ میں کوئی زبردستی نہیں ہدایت، گمراہی سے الگ ہوچکی ہے پس جو شخص شیطان کا انکار کرے اور اللہ پر ایمان لائے اس نے مضبوط حلقہ پکڑ لیا جو ٹوٹنے والا نہیں اور اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,257,'2:257','اللہ کام بنانے والا ہے ایمان والوں کا، وہ ان کو اندھیروں سے نکال کر اجالے کی طرف لاتا ہے، اور جن لوگوں نے انکار کیا ان کے دوست شیطان ہیں ، وہ ان کو اجالے سے نکال کر اندھیروں کی طرف لے جاتے ہیں یہ آگ میں جانے والے لوگ ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,258,'2:258','کیا تم نے اس کو نہیں دیکھا جس نے ابراہیم سے اس کے رب کے بارے میں حجت کیکیوں کہ اللہ نے اس کو سلطنت دی تھی جب ابراہیم نے کہا کہ میرا رب وہ ہے جو جلاتا اور مارتا ہے وہ بولا کہ میں بھی جلاتا ہوں اور مارتا ہوں ابراہیم نے کہا کہ اللہ سورج کو پورب سے نکالتاہے تم اس کو پچھم سے نکال دو تب وہ منکر حیران رہ گیا اور اللہ ظالموں کو راہ نہیں دکھاتا');
INSERT INTO "ur_wahid_uddin" VALUES(2,259,'2:259','یا جیسے وہ شخص جس کا گزر ایک بستی پر سے ہوا اور وہ اپنی چھتوں پر گری ہوئی تھی اس نے کہا ہلاک ہوجانے کے بعد اللہ اس بستی کو دوبارہ کیسے زندہ کرے گا پھر اللہ نے اس پرسو برس تک کے ليے موت طاری کردی پھر اس کو اٹھایا اللہ نے پوچھا تم کتنی دیر اس حالت میں رہے اس نے کہا ایک دن یا ایک دن سے کچھ کم اللہ نے کہا نہیں ، بلکہ تم سو برس اس حال ميں رہے اب تم اپنے کھانے پینے کی چیزوں کودیکھو کہ وہ سڑی نہیں ہیں اور اپنے گدھے کو دیکھو اور تاکہ ہم تم کو لوگوں کے ليے ایک نشانی بنادیں اور ہڈیوں کی طرف دیکھو، کس طرح ہم ان کا ڈھانچہ کھڑا کرتے ہیں پھر ان پر گوشت چڑھاتے ہیں پس جب اس پر واضح ہوگیا تو کہا میں جانتا ہوں کہ بے شک اللہ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,260,'2:260','اور جب ابراہیم نے کہا کہ اے میرے رب، مجھ کو دکھادے کہ تو مردوں کو کس طرح زندہ کرے گا اللہ نے کہاکیا تم نے یقین نہیں کیا ابراہیم نے کہا کیوں نہیں ، مگر اس ليے کہ میرے دل کو تسکین ہوجائے فرمایا، تم چار پرندے لو اور ان کو اپنے سے ہلالو پھر ان میں سے ہر ایک کو الگ الگ پہاڑی پر رکھ دو، پھر ان کو بلاؤ وہ تمھارے پاس دوڑتے ہوئے چلے آئیں گے اورجان لو کہ اللہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,261,'2:261','جو لوگ اپنے مال کواللہ کی راہ میں خرچ کرتے ہیں ان کی مثال ایسی ہے جیسے ایک دانہ ہو جس سے سات بالیں پیدا ہوں ، ہر بالی میں سودانے ہوں اور اللہ بڑھاتا ہے جس کے ليے چاہتا ہے اور اللہ وسعت والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,262,'2:262','جو لوگ اپنے مال کو اللہ کی راہ میں خرچ کرتے ہیں پھر خرچ کرنے کے بعد نہ احسان رکھتے ہیں اور نہ تکلیف پہنچاتے ہیں ، ان کے ليے ان کے رب کے پاس ان کا اجر ہے اور ان کے ليے نہ کوئی ڈر ہے اور نہ وہ غم گین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,263,'2:263','مناسب بات کہہ دینا اور درگزر کرنا اس صدقہ سے بہتر ہے جس کے پیچھے ستانا ہو اور اللہ بے نیاز ہے، تحمل والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,264,'2:264','اے ایمان والو، احسان رکھ کر اور ستاکر اپنے صدقہ کو ضائع نہ کرو، جس طرح وہ شخص جو اپنا مال دکھاوے کے ليے خرچ کرتا ہے اور وہ اللہ پر اور آخرت کے دن پر ایمان نہیں رکھتا پس اس کی مثال ایسی ہے جیسے ایک چٹان ہو جس پر کچھ مٹی ہو، پھر اس پر زور کا مینھ پڑے اور وہ اس کو بالکل صاف کردے ایسے لوگوں کو اپنی کمائی کچھ بھی ہاتھ نہ لگے گی اور اللہ منکروں کو راہ نہیں دکھاتا');
INSERT INTO "ur_wahid_uddin" VALUES(2,265,'2:265','اور ان لوگوں کی مثال جو اپنے مال کو اللہ کی رضا چاہنے کے لیے اور اپنے نفس میں پختگی کے لیے خرچ کرتے ہیں ، ایک باغ کی طرح ہے جو بلندی پر ہو اس پر زور کا مینھ پڑا تو وہ دوگنا پھل لایا اور اگر زور کا مینھ نہ پڑے تو ہلکی پھوار بھی کافی ہے اور جو کچھ تم کرتے ہو اللہ اس کو دیکھ رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,266,'2:266','کیا تم میں سے کوئی یہ پسند کرتا ہے کہ اس کے پاس کھجوروں اور انگوروں کا ایک باغ ہو، اس کے نیچے نہریں بہہ رہی ہوں اس میں اس کے واسطے ہر قسم کے پھل ہوں اور وہ بوڑھا ہوجائے اور اس کے بچے ابھی کمزور ہوں تب اس باغ پر ایک بگولہ آئے جس میں آگ ہو پھر وہ باغ جل جائے اللہ اس طرح تمھارے ليے کھول کر نشانیاں بیان کرتا ہے تاکہ تم غور کرو');
INSERT INTO "ur_wahid_uddin" VALUES(2,267,'2:267','اے ایمان والو، خرچ کرو عمدہ چیز کو اپنی کمائی میں سے اور اس میں سے جو ہم نے تمھارے ليے زمین میں سے پیدا کیا ہے اور ردی چیز کا قصد نہ کرو کہ اس میں سے خرچ کرو حالاں کہ تم کبھی اس کو لینے والے نہیں ، الا یہ کہ چشم پوشی کرجاؤ اور جان لو کہ اللہ بے نیاز ہے، خوبیوں والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,268,'2:268','شیطان تم کو محتاجی سے ڈراتا ہے اوربری بات کی تلقین کرتا ہے اور اللہ وعدہ دیتا ہے اپنی بخشش کا اور فضل کا اور اللہ وسعت والا ہے، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,269,'2:269','وہ جس کو چاہتا ہے حکمت دے دیتا ہے اور جس کو حکمت ملی اس کو بڑی دولت مل گئی اور نصیحت وہی حاصل کرتے ہیں جو عقل والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,270,'2:270','اور تم جو خرچ کرتے ہو یا جو نذر مانتے ہو اس کو اللہ جانتا ہے اور ظالموں کا کوئی مدد گار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(2,271,'2:271','اگر تم اپنے صدقات ظاہر کرکے دو تب بھی اچھا ہے اور اگر تم انھیں چھپاکر محتاجوں کو دو تو یہ تمھارے ليے زیادہ بہتر ہے اور اللہ تمھارے گناہوں کو دور کردے گا اور اللہ تمھارے کاموں سے واقف ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,272,'2:272','ان کو ہدایت پر لانا تمھارا ذمہ نہیں بلکہ اللہ جس کو چاہتا ہے ہدایت دیتا ہے اور جو مال تم خرچ کرو گے اپنے ہی ليے کرو گے اور تم نہ خرچ کرو مگر اللہ کی رضا چاہنے کے ليےاور تم جو مال خرچ کرو گے، وہ تم کو پورا كر دیا جائے گا اور تمھارے ليے اس میں کمی نہ کی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(2,273,'2:273','صدقات ان حاجت مندوں کے ليے ہیں جو اللہ کی راہ میں گھر گئے ہوں ، زمین میں دوڑ دھوپ نہیں کرسکتے ناواقف آدمی ان کو غنی خیال کرتا ہے، ان کے نہ مانگنے کی وجہ سے تم ان کو ان کی صورت سے پہچان سکتے ہو وہ لوگوں سے لپٹ کر نہیں مانگتے اور جو مال تم خرچ کرو گے وہ اللہ کو معلوم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,274,'2:274','جو لوگ اپنے مالوں کو رات اور دن، چھپے اور کھلے خرچ کرتے ہیں ، ان کے ليے ان کے رب کے پاس اجر ہے اور ان کے ليے نہ خوف ہے اور نہ وہ غم گین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,275,'2:275','جو لوگ سو دکھاتے ہیں ، وہ قیامت میں نہ اٹھیں گے مگر اس شخص کی مانند جس کو شیطان نے چھوکر خبطی بنادیا ہو یہ اس ليے کہ انھوں نے کہا کہ تجارت کرنا بھی ویسا ہی ہے جیسا سود لینا حالاں کہ اللہ نے تجارت کو حلال ٹھہرایا ہے اور سود کو حرام کیا ہے پھر جس شخص کے پاس اس کے رب کی طرف سے نصیحت پہنچی اور وہ باز آگیا تو جو کچھ وہ لے چکا وہ اس کے ليے ہے اور اس کا معاملہ اللہ کے حوالے ہے اور جو شخص پھر وہی کرے تو وہی لوگ دوزخی ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,276,'2:276','اللہ سود کو گھٹاتا ہے اور صدقات کو بڑھاتا ہے اور اللہ پسند نہیں کرتا نا شکروں کو، گنہ گاروں کو');
INSERT INTO "ur_wahid_uddin" VALUES(2,277,'2:277','بے شک جو لوگ ایمان لائے اور نیک عمل کيے اور نماز کی پابندی کی اور زکوٰۃ ادا کی، ان کے ليے ان کا اجر ہے ان کے رب کے پاس ان کے ليے نہ کوئی اندیشہ ہے اور نہ وہ غم گین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(2,278,'2:278','اے ایمان والو، اللہ سے ڈرو اور جو سود باقی رہ گیا ہے اس کو چھوڑ دو، اگر تم مومن ہو');
INSERT INTO "ur_wahid_uddin" VALUES(2,279,'2:279','اگر تم ایسا نہیں کرتے تو اللہ اور اس کے رسول کی طرف سے لڑائی کے ليے خبر دار ہوجاؤ اور اگر تم توبہ کرلو تو اصل رقم کے تم حق دار ہو، نہ تم کسی پر ظلم کرو اور نہ تم پر ظلم کیا جائے');
INSERT INTO "ur_wahid_uddin" VALUES(2,280,'2:280','اور اگر ایک شخص تنگی والا ہے تو اس کی فراخی تک مہلت دو اور اگر تم معاف کردو تو یہ تمھارے ليے زیادہ بہتر ہے، اگر تم سمجھو');
INSERT INTO "ur_wahid_uddin" VALUES(2,281,'2:281','اور اس دن سے ڈرو جس دن تم اللہ کی طرف لوٹائے جاؤ گے پھر ہر شخص کو اس کا کیا ہوا پورا پورا مل جائے گا اور ان پر ظلم نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(2,282,'2:282','اے ایمان والو، جب تم کسی مقرر مدت کے ليے ادھار کا لین دین کرو تو اس کو لکھ لیا کرو اور اس کو لکھے تمھارے درمیان کوئی لکھنے والا، انصاف کے ساتھ اور لکھنے والا لکھنے سے انکار نہ کرے، جیسا اللہ نے اس کو سکھایا، اسی طرح اس کو چاہئے کہ لکھ دےاور وہ شخص لکھوائے جس پر حق آتا ہے اور وہ ڈرے اللہ سے جو اس کا رب ہے اور اس میں کوئی کمی نہ کرے اور اگر وہ شخص جس پر حق آتا ہے بے سمجھ ہو یا کمزور ہو یا خود لکھوانے کی قدرت نہ رکھتا ہو تو چاہئے کہ اس کا ولی انصاف کے ساتھ لکھوادے اور اپنے مَردوں میں سے دو آدمیوں کو گواہ بنا لو اور اگر دو مرد نہ ہوں تو پھر ایک مرد اور دو عورتیں ، ان لوگوں میں سے جن کو تم پسند کرتے ہو، تاکہ اگر ایک عورت بھول جائے تو دوسری عورت اس کو یاد دلا دے اور گواہ انکار نہ کریں جب وہ بلائے جائیں اور معاملہ چھوٹا ہو یا بڑا، میعاد کے تعین کے ساتھ اس کو لکھنے میں کاہلی نہ کرو یہ لکھ لینا اللہ کے نزدیک زیادہ انصاف کا طریقہ ہے اور گواہی کو زیادہ درست رکھنے والا ہے اور زیادہ قرینِ قیاس ہے کہ تم شبہ میں نہ پڑو لیکن اگر کوئی سودا دست بدست ہو جس کا تم آپس میں لین دین کیا کرتے ہو تو تم پر کوئی الزام نہیں کہ تم اس کو نہ لکھو مگر جب یہ سودا کرو تو گواہ بنالیا کرو اور کسی لکھنے والے کو یا گواہ کو تکلیف نہ پہنچائی جائے اور اگر ایسا کرو گے تو یہ تمھارے ليے گناہ کی بات ہوگی اور اللہ سے ڈرو، اللہ تم کو سکھاتا ہے اور اللہ ہر چیز کا جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,283,'2:283','اور اگر تم سفر میں ہو اور کوئی لکھنے والا نہ پاؤ تو رہن رکھنے کی چیزیں قبضہ میں دے دی جائیں اور اگر تم میں سے ایک شخص دوسرے شخص کا اعتبار کرتا ہو تو چاہئے کہ جس شخص پر اعتبار کیا گیا، وہ اعتبار کو پورا کرے اور اللہ سے ڈرے جو اس کا رب ہے اور گواہی کو نہ چھپاؤ اور جو شخص چھپائے گا اس کا دل گنہ گار ہوگا اور جو کچھ تم کرتے ہو، اللہ اس کو جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,284,'2:284','اللہ کا ہے جو کچھ آسمانوں میں ہے اور جو زمین میں ہے تم اپنے دل کی باتوں کو ظاہر کرو یا چھپاؤ، اللہ تم سے اس کا حساب لے گا پھر جس کو چاہے گا بخشے گا اور جس کو چاہے گا سزا دے گا اور اللہ ہر چیز پر قدرت رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,285,'2:285','رسول ایمان لایا ہے اس پر جو اس کے رب کی طرف سے اس پر اترا ہے اور مسلمان بھی اس پر ایمان لائے ہیں سب ایمان لائے ہیں اللہ پر اور اس کے فرشتوں پر اوراس کی کتابوں پر اوراس کے رسولوں پر ہم اس کے رسولوں میں سے کسی کے درمیان فرق نہیں کرتے اور وہ کہتے ہیں کہ ہم نے سنا اور مانا ہم تیری بخشش چاہتے ہیں اے ہمارے رب، اور تیری ہی طرف لوٹنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(2,286,'2:286','اللہ کسی پر ذمہ داری نہیں ڈالتا مگر اس کی طاقت کے مطابق اس کو ملے گا وہی جو اس نے کمایا اور اس پر پڑے گا وہی جو اس نے کیا اے ہمارے رب، ہم کو نہ پکڑاگر ہم بھولیں یا ہم غلطی کرجائیں اے ہمارے رب، ہم پر بوجھ نہ ڈال جیسا بوجھ تو نے ڈالا تھا ہم سے اگلوں پر اے ہمارے رب، ہم سے وہ نہ اٹھوا جس کی طاقت ہم کو نہیں اور درگزر کر ہم سے اور ہم کو بخش دے اورہم پر رحم کر تو ہمارا کار ساز ہے پس انکار کرنے والوں کے مقابل میں ہماری مدد کر');
INSERT INTO "ur_wahid_uddin" VALUES(3,1,'3:1','الم');
INSERT INTO "ur_wahid_uddin" VALUES(3,2,'3:2','اللہ، اس کے سوا کوئی معبود نہیں ، زندہ اور سب کا تھامنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(3,3,'3:3','اس نے تم پرکتاب اتاری حق کے ساتھ، تصدیق کرنے والی اس چیز کی جواس کے آگے ہے، اور اس نے تورات اور انجیل اتاری');
INSERT INTO "ur_wahid_uddin" VALUES(3,4,'3:4','اس سے پہلے لوگوں کی ہدایت کے لیے اوراللہ نے فرقان اتارا بے شک جن لوگوں نے اللہ کی نشانیوں کا انکار کیا، ان کے لیے سخت عذاب ہے اور اللہ زبردست ہے، بدلہ لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,5,'3:5','بے شک اللہ سے کوئی چیز چھپی ہوئی نہیں ، نہ زمین میں اور نہ آسمان میں');
INSERT INTO "ur_wahid_uddin" VALUES(3,6,'3:6','وہی تمھاری صورت بناتا ہے ماں کے پیٹ میں جس طرح چاہتا ہے اس کے سواکوئی معبود نہیں ، وہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,7,'3:7','وہی ہے جس نے تمھارے اوپر کتاب اتاری اس میں بعض آیتیں محکم ہیں ، وہ کتاب کی اصل ہیں اور دوسری آیتیں متشابہ ہیں پس جن کے دلوں میں ٹیڑھ ہے وہ متشابہ آیتوں کے پیچھے پڑجاتے ہیں فتنہ کی تلاش میں اور اس کے مطلب کی تلاش میں حالاں کہ ان کا مطلب اللہ کے سواکوئی نہیں جانتا اور جو لوگ پختہ علم والے ہیں ، وہ کہتے ہیں کہ ہم ان پر ایمان لائے سب ہمارے رب کی طرف سے ہے اور نصیحت وہی لوگ قبول کرتے ہیں جو عقل والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,8,'3:8','اے ہمارے رب، ہمارے دلوں کو نہ پھیر جب کہ توہم کو ہدایت دے چکا اور ہم کو اپنے پاس سے رحمت دے بے شک تو ہی سب کچھ دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,9,'3:9','اے ہمارے رب، تو جمع کرنے والا ہے لوگوں کو ایک دن جس میں کوئی شبہ نہیں بے شک اللہ وعدہ کے خلاف نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(3,10,'3:10','بے شک جن لوگوں نے انکار کیا، ان کے مال اور ان کی اولاد اللہ کے مقابلہ میں ان کے کچھ کام نہ آئیں گے اور یہی لوگ آگ کے ایندھن ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,11,'3:11','ان کا انجام ویسا ہی ہوگا جیسا فرعون والوں کا اور ان سے پہلے والوں کا ہوا انھوں نے ہماری نشانیوں کو جھٹلایا اس پر اللہ نے ان کے گناہوں کے باعث ان کو پکڑ لیا اور اللہ سخت سزا دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,12,'3:12','انکار کرنے والوں سے کہہ دو کہ اب تم مغلوب کيے جاؤ گے اورجہنم کی طرف جمع کرکے لے جائے جاؤ گے اور جہنم بہت برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,13,'3:13','بے شک تمھارے ليے نشانی ہے ان دو گروہوں میں جن میں (بدر میں ) مڈبھیڑ ہوئی ایک گروہ اللہ کی راہ میں لڑ رہا تھا اور دوسرا منکر تھا یہ منکر کھلی آنکھوں سے ان کو دوگنا دیکھتے تھے اور اللہ جس کو چاہتا ہے اپنی مدد کا زور دے دیتا ہے اس میں آنکھ والوں کے ليے بڑا سبق ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,14,'3:14','لوگوں کے ليے خوش نما کردی گئی ہے محبت خواہشوں کی— عورتیں ، بیٹے، سونے چاندی کے ڈھیر، نشان لگے ہوئے گھوڑے، مویشی اور کھیتی یہ دنیوی زندگی کے سامان ہیں اور اللہ کے پاس اچھا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,15,'3:15','کہو، کیا میں تم کو بتاؤں اس سے بہتر چیز ان لوگوں کے ليے جو ڈرتے ہیں ان کے رب کے پاس باغ ہیں جن کے نیچے نہریں جاری ہوں گی وہ ان میں ہمیشہ رہیں گے اور ستھری بیویاں ہوں گی اور اللہ کی رضا مندی ہوگی اور اللہ کی نگاہ میں ہیں اس کے بندے');
INSERT INTO "ur_wahid_uddin" VALUES(3,16,'3:16','جو کہتے ہیں اے ہمارے رب، ہم ایمان لے آئے پس تو ہمارے گناہوں کو معاف کردے اورہم کو آگ کے عذاب سے بچا');
INSERT INTO "ur_wahid_uddin" VALUES(3,17,'3:17','وہ صبر کرنے والے ہیں اور سچے ہیں فرماں بردار ہیں اور خرچ کرنے والے ہیں اورپچھلی رات کو مغفرت مانگنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,18,'3:18','اللہ کی گواہی ہے اور فرشتوں کی اور اہل علم کی کہ اللہ کے سواکوئی معبود نہیں وہ قائم رکھنے والا ہے انصاف کا اس کے سوا کوئی معبود نہیں وہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,19,'3:19','دین اللہ کے نزدیک صرف اسلام ہے اوراہل کتاب نے اس میں جو اختلاف کیا وہ آپس کی ضد کی وجہ سے کیا، بعد اس کے کہ ان کو صحیح علم پہنچ چکا تھا اور جو اللہ کی آیتوں کا انکار کرے تو اللہ یقیناً جلد حساب لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,20,'3:20','پھر اگر وہ تم سے اس بارے میں جھگڑیں تو ان سے کہہ دوکہ میں اپنا رخ اللہ کی طرف کرچکا اور جومیرے پیرو ہیں وہ بھی اور اہل کتاب سے اور اَن پڑھوں سے پوچھو کیا تم بھی اسی طرح اسلام لاتے ہو اگر وہ اسلام لائیں تو انھوں نے راہ پالی اور اگر وہ پھر جائیں تو تمھارے اوپر صرف پہنچادینا ہے اور اللہ کی نگاہ میں ہیں اس کے بندے');
INSERT INTO "ur_wahid_uddin" VALUES(3,21,'3:21','جو لوگ اللہ کی نشانیوں کا انکار کرتے ہیں اورپیغمبروں کو ناحق قتل کرتے ہیں اوران لوگوں کومار ڈالتے ہیں جو لوگوں میں سے انصاف کی دعوت لے کر اٹھتے ہیں ، ان کو ایک درد ناک سزا کی خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(3,22,'3:22','یہی وہ لوگ ہیں جن کے اعمال دنیا اور آخرت میں ضائع ہوگئے اور ان کا مدد گار کوئی نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,23,'3:23','کیا تم نے ان لوگوں کو نہیں دیکھا جن کو اللہ کی کتاب کا ایک حصہ دیا گیا تھا ان کو اللہ کی کتاب کی طرف بلایا جارہا ہے کہ وہ ان کے درمیان فیصلہ کرے پھر ان کا ایک گروہ منھ پھیرلیتا ہے بے رخی کرتے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(3,24,'3:24','یہ اس سبب سے کہ وہ لوگ کہتے ہیں کہ ہم کو ہر گز آگ نہ چھوئے گی بجز گنے ہوئے چند دنوں کے اور ان کی بنائی ہوئی باتوں نے ان کو ان کے دین کے بارے میں دھوکے میں ڈال دیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,25,'3:25','پھر اس وقت کیا ہوگا جب ہم ان کو جمع کریں گے ایک دن جس کے آنے میں کوئی شک نہیں اور ہر شخص کو جو کچھ اس نے کیا ہے، اس کا پورا پورا بدلہ دیا جائے گا اوران پر ظلم نہ کیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(3,26,'3:26','تم کہو، اے اللہ، سلطنت کے مالک تو جس کو چاہے سلطنت دے اور جس سے چاہے سلطنت چھین لے اور تو جس کو چاہے عزت دے اور جس کو چاہے ذلیل کرے تیرے ہاتھ میں ہے سب خوبی بے شک تو ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,27,'3:27','تو رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے اور تو بے جان سے جان دار کو نکالتا ہے اور تو جان دار سے بے جان کو نکالتا ہے اور تو جس کو چاہتا ہے بے حساب رزق دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,28,'3:28','مومنین کو چاہئے کہ وہ اہلِ ایمان کو چھوڑ کر منکروں کو دوست نہ بنائیں اور جو شخص ایسا کرے گا تو اللہ سے اس کا کوئی تعلق نہیں مگر ایسی حالت میں کہ تم ان سے بچاؤ کرنا چاہو اور اللہ تم کو ڈراتا ہے اپنی ذات سے اور اللہ ہی کی طرف لوٹنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,29,'3:29','کہہ دو کہ جو کچھ تمھارے سینوں میں ہے اس کو چھپاؤ یا ظاہرکرو، اللہ اس کو جانتاہے اور وہ جانتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور اللہ ہر چیز پر قادرہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,30,'3:30','جس دن ہرشخص اپنی کی ہوئی نیکی کواپنے سامنے موجود پائے گا، اورجو برائی کی ہوگی اس کو بھی اس دن ہر آدمی یہ چاہے گا کہ کاش، ابھی یہ دن اس سے بہت دور ہوتا اوراللہ تم کو ڈراتا ہے اپنی ذات سے اور اللہ اپنے بندوں پر بہت مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,31,'3:31','کہو، اگر تم اللہ سے محبت کرتے ہو تو میری پیروی کرو،اللہ تم سے محبت کرے گا اور تمھارے گناہوں کو معاف کردے گا اللہ بڑا معاف کرنے والا، بڑا مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,32,'3:32','کہو کہ اللہ کی اطاعت کرو اور رسول کی پھر اگر وہ اعراض کریں تو اللہ منکروں کو دوست نہیں رکھتا');
INSERT INTO "ur_wahid_uddin" VALUES(3,33,'3:33','بے شک اللہ نے آدم کو اور نوح کو اور آل ابراہیم کواور آل عمران کو سارے عالم کے اوپر منتخب کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,34,'3:34','یہ ایک دوسرے کی اولاد ہیں اور اللہ سننے والاجاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,35,'3:35','جب عمران کی بیوی نے کہا اے میرے رب، میں نے نذر کیا تیرے ليے جو میرے پیٹ میں ہے وہ آزاد رکھا جائے گا پس تو مجھ سے قبول کر بے شک تو سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,36,'3:36','پھر جب اس نے جنا تو اس نے کہا اے میرے رب، میں نے تو لڑکی کو جنا ہے اور اللہ خوب جانتا ہے کہ اس نے کیا جنا ہے اور لڑکا نہیں ہوتا لڑکی کی مانند اورمیں نے اس کا نام مریم رکھاہے اورمیں اس کو اور اس کی اولاد کو شیطان مردود سے تیری پناہ میں دیتی ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(3,37,'3:37','پس اس کے رب نے اس کو اچھی طرح قبول کیا اور اس کو عمدہ طریقہ سے پروان چڑھایا اور زکریا کواس کا سرپرست بنایا جب کبھی زکریا ان کے پاس حجرہ میں آتا تو وہ وہاں رزق پاتااس نے پوچھا اے مریم، یہ چیز تمھیں کہا ں سے ملتی ہے مریم نے کہا یہ اللہ کے پاس سے ہے بے شک اللہ جس کو چاہتا ہے بے حساب رزق دے دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,38,'3:38','اس وقت زکریا نے اپنے رب کو پکارا اس نے کہا اے میرے رب، مجھ کو اپنے پاس سے پاکیزہ اولاد عطا کر، بے شک تو دعا کا سننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,39,'3:39','پھر فرشتوں نے اس کو آواز دی جب کہ وہ حجرہ میں کھڑا ہوا نماز پڑھ رہا تھا کہ اللہ تجھ کو یحییٰ کی خوش خبری دیتا ہے جو کلمۃ اللہ کی تصدیق کرنے والا ہوگا اور سردار ہوگا اور اپنے نفس کو روکنے والا ہوگا اور نبی ہوگا نیکوں میں سے');
INSERT INTO "ur_wahid_uddin" VALUES(3,40,'3:40','زکریا نے کہا اے میرے رب، میرے يهاں لڑکا کس طرح ہوگا، حالاں کہ میں بوڑھا ہوچکا اور میری عورت بانجھ ہے فرمایا اسی طرح اللہ کردیتا ہے جو وہ چاہتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,41,'3:41','زکریانے کہا اے میرے رب، میرے ليے کوئی نشانی مقرر کردے کہا تمھارے ليے نشانی یہ ہے کہ تم تین دن تک لوگوں سے بات نہ کرسکو گے، مگر اشارہ سے اور اپنے رب کو کثرت سے یاد کرتے رہو اور شام اور صبح اس کی تسبیح کرو');
INSERT INTO "ur_wahid_uddin" VALUES(3,42,'3:42','اور جب فرشتوں نے کہا اے مریم، اللہ نے تم کو منتخب کیا اور تم کو پاک کیا اور تم کو دنیا بھر کی عورتوں کے مقابلہ میں منتخب کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,43,'3:43','اے مریم، اپنے رب کی فرماں برداری کرو اور سجدہ کرو اور رکوع کرنے والوں کے ساتھ رکوع کرو');
INSERT INTO "ur_wahid_uddin" VALUES(3,44,'3:44','یہ غیب کی خبریں ہیں جو ہم تم کو وحی کر رہے ہیں اور تم ان کے پاس موجود نہ تھے جب وہ اپنے قرعے ڈال رہے تھے کہ کون مریم کی سرپرستی کرے اورنہ تم اس وقت ان کے پاس موجود تھے جب وہ آپس میں جھگڑ رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(3,45,'3:45','جب فرشتوں نے کہا اے مریم، اللہ تم کو خوش خبری دیتا ہے اپنی طرف سے ایک کلمہ کی اس کا نام مسیح عیسیٰ بن مریم ہوگا وہ دنیا اور آخرت میں مرتبہ والا ہوگا اور اللہ کے مقرب بندوں میں ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(3,46,'3:46','وہ لوگوں سے باتیں کرے گاجب ماں کی گود میں ہوگا اور جب پوری عمر کا ہوگا اور وہ صالحین میں سے ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(3,47,'3:47','مریم نے کہا اے میرے رب، مجھے کس طرح لڑکا ہوگا، جب کہ کسی مرد نے مجھ کو ہاتھ نہیں لگایا فرمایا اسی طرح اللہ پیدا کرتا ہے جو چاہتا ہے جب وہ کسی کام کا فیصلہ کرتا ہے تو اس کوکہتا ہے کہ ہوجااور وہ ہوجاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,48,'3:48','اور اللہ اس کوکتاب اور حکمت اور تورات اور انجیل سکھائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(3,49,'3:49','اور وہ رسول ہوگا بنی اسرائیل کی طرف کہ میں تمھارے پاس تمھارے رب کی نشانی لے کر آیا ہوں میں تمھارے ليے مٹی سے پرندہ کی مانند صورت بناتا ہوں ،پھر اس میں پھونک مارتا ہوں تو وہ اللہ کے حکم سے واقعی پرندہ بن جاتی ہے اور میں اللہ کے حکم سے مادر زاد اندھے اور كوڑھي کو اچھا کرتا ہوں اور میں اللہ کے حکم سے مُردے کوزندہ کرتا ہوں اورمیں تم کو بتاتا ہوں کہ تم کیا کھاتے ہو اور اپنے گھروں میں کیا ذخیرہ کرتے ہو بے شک اس میں تمھارے ليے نشانی ہے اگر تم ایمان رکھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,50,'3:50','اور میں تصدیق کرنے والا ہوں تو رات کی جو مجھ سے پہلے کی ہے اور میں اس ليے آیا ہوں کہ بعض ان چیزوں کو تمھارے ليے حلال ٹھہراؤں جو تم پر حرام کردی گئی ہیں اور میں تمھارے پاس تمھارے رب کی طرف سے نشانی لے کر آیا ہوں پس تم اللہ سے ڈرو اور میری اطاعت کرو');
INSERT INTO "ur_wahid_uddin" VALUES(3,51,'3:51','بے شک اللہ میرا رب ہے اور تمھارا بھی پس اس کی عبادت کرو، یہی سیدھی راہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,52,'3:52','پھر جب عیسی ٰ نے ان کاانکار دیکھا تو کہا کہ کون میرا مدد گار بنتا ہے اللہ کی راہ میں حواریوں نے کہا کہ ہم ہیں اللہ کے مدد گار ہم ایمان لائے ہیں اللہ پر اور آپ گواہ رہيے کہ ہم فرماں بردار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,53,'3:53','اے ہمارے رب، ہم ایمان لائے اس پر جو تو نے اتارا، اور ہم نے رسول کی پیروی کی پس تو لکھ لے ہم کو گواہی دینے والوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(3,54,'3:54','اور انھوں نے خفیہ تدبیر کی اور اللہ نے بھی خفیہ تدبیر کی اور اللہ سب سے بہتر تدبیر کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,55,'3:55','جب اللہ نے کہا کہ اے عیسیٰ، میں تم کو واپس لینے والا ہوں اور تم کو اپنی طرف اٹھالینے والا ہوں اور جن لوگوں نے انکار کیا ہے ان سے تمھیں پاک کرنے والا ہوں اور جو تمھارے پیرو ہیں ان کو قیامت تک ان لوگوں پر غالب کرنے والا ہوں جنھوں نے تمھارا انکار کیا ہے پھر میری طرف ہوگی سب کی واپسی پس میں تمھارے درمیان ان چیزوں کے بارے میں فیصلہ کروں گا جن میں تم جھگڑتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(3,56,'3:56','پھر جو لوگ منکر ہوئے ان کو سخت عذاب دوں گا دنیا میں اور آخرت میں اور ان کا کوئی مدد گار نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(3,57,'3:57','اور جو لوگ ایمان لائے اور نیک کام کيے ان کو اللہ ان کا پورا اجر دے گا اور اللہ ظالموں کو دوست نہیں رکھتا');
INSERT INTO "ur_wahid_uddin" VALUES(3,58,'3:58','یہ ہم تم کو سناتے ہیں اپنی آیتیں اور پُرحکمت مضامین');
INSERT INTO "ur_wahid_uddin" VALUES(3,59,'3:59','بے شک عیسیٰ کی مثال اللہ کے نزدیک آدم کی سی ہے اللہ نے اس کو مٹی سے بنایا پھر اس کو کہا کہ ہوجا، تو وہ ہوگیا');
INSERT INTO "ur_wahid_uddin" VALUES(3,60,'3:60','حق بات ہے تیرے رب کی طرف سے پس تم نہ ہو شک کرنے والوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(3,61,'3:61','پھر جو تم سے اس بارے میں حجت کرے بعد اس کے کہ تمھارے پاس علم آچکا ہے تو ان سے کہو کہ آؤ، ہم بلائیں اپنے بیٹوں کو اور تمھارے بیٹوں کو، اپنی عورتوں کواور تمھاری عورتوں کو اور ہم اور تم خود بھی جمع ہوں پھر ہم مل کر دعا کریں کہ جو جھوٹا ہو اس پر اللہ کی لعنت ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,62,'3:62','بے شک یہ سچا بیان ہے اور اللہ کے سوا کوئی معبود نہیں اور اللہ ہی زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,63,'3:63','پھر اگر وہ قبول نہ کریں تو اللہ مفسدوں کوجاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,64,'3:64','کہو اے اہل کتاب، آؤ ایک ایسی بات کی طرف جوہمارے اور تمھارے درمیان مسلّم ہے کہ ہم اللہ کے سوا کسی کی عبادت نہ کریں اور اللہ کے ساتھ کسی کو شریک نہ ٹھہرائیں اور ہم میں سے کوئی کسی دوسرے کو اللہ کے سوا رب نہ بنائے پھر اگر وہ اس سے اعراض کریں تو کہہ دو کہ تم گواہ رہو، ہم فرماں بردار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,65,'3:65','اے اہل کتاب، تم ابراہیم کے بارے میں کیوں جھگڑتے ہو حالاں کہ تورات اور انجیل تو اس کے بعد اتری ہیں کیا تم اس کو نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(3,66,'3:66','تم وہ لوگ ہو کہ تم اُس بات کے بارے میں جھگڑتےهو جس کا تمھیں کچھ علم تھا اب تم ایسی بات میں کیوں جھگڑتے ہو جس کا تمھیں کوئی علم نہیں اور اللہ جانتا ہے، تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(3,67,'3:67','ابراہیم نہ یہودی تھا اور نہ نصرانی بلکہ وہ حنیف مسلم تھا اور وہ شرک کرنے والوں میں سے نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(3,68,'3:68','لوگوں میں زیادہ مناسبت ابراہیم سے ان کو ہے جنھوں نے اس کی پیروی کی اور یہ پیغمبر اور جو اس پر ایمان لائے اور اللہ ایمان والوں کا ساتھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,69,'3:69','اہل کتاب میں سے ایک گروہ چاہتا ہے کہ وہ کسی طرح تم کو گمراہ کردے حالاں کہ وہ نہیں گمراہ کرتے مگر خود اپنے آپ کو مگر وہ اس کا احساس نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(3,70,'3:70','اے اہل کتاب، تم اللہ کی نشانیوں کا کیوں انکار کرتے ہو حالاں کہ تم گواہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,71,'3:71','اے اہل کتاب، تم کیوں صحیح میں غلط کو ملاتے ہو اور حق کو چھپاتے ہو حالاں کہ تم جانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,72,'3:72','اور اہل کتاب کے ایک گروہ نے کہا کہ مومنوں پر جو چیز اتاری گئی ہے اس پر صبح کوایمان لاؤ اور شام کو اس کا انکار کردو، شاید کہ مومنین بھی اس سے پھر جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,73,'3:73','اور یقین نہ کرو مگر صرف اس کا جو چلے تمھارے دین پر کہو، ہدایت وہی ہے جو اللہ ہدایت کرے اور یہ اسی کی دین ہے کہ کسی کو وہی کچھ دے دیا جائے جو تم کو دیا گیا تھا یا وہ تم سے تمھارے رب کے یہاں حجت کریں کہو، بڑائی اللہ کے ہاتھ میں ہے وہ جس کو چاہتا ہے دیتا ہے اور اللہ بڑا وسعت والا ہے، علم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,74,'3:74','وہ جس کو چاہتا ہے اپنی رحمت کے ليے خاص کر لیتا ہے اور اللہ بڑا فضل والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,75,'3:75','اور اہل کتاب میں کوئی ایسا بھی ہے کہ اگر تم اس کے پاس امانت کا ڈھیر رکھو تو وہ اس کو تمھیں ادا کردے اور ان میں کوئی ایسا ہے کہ اگر تم اس کے پاس ایک دینار امانت رکھ دو تو وہ تم کو ادا نہ کرے، اِلاّ یہ کہ تم اس کے سر پر کھڑے ہوجاؤ، یہ اس سبب سے کہ وہ کہتے ہیں کہ غیر اہل کتاب کے بارے میں ہم پر کوئی الزام نہیں اور وہ اللہ کے اوپر جھوٹ لگاتے ہیں حالاں کہ وہ جانتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,76,'3:76','بلکہ جو شخص اپنے عہد کو پورا کرے اور اللہ سے ڈرے تو بے شک اللہ ایسے متقیوں کو دوست رکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,77,'3:77','جو لوگ اللہ کے عہد اور اپنی قسموں کو تھوڑی قیمت پر بیچتے ہیں ، ان کے ليے آخرت میں کوئی حصہ نہیں اللہ نہ ان سے بات کرے گا، نہ ان کی طر ف دیکھے گا قیامت کے دن، اور نہ ان کو پاک کرے گا اور ان کے ليے دردناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,78,'3:78','اور ان میں کچھ لوگ ایسے بھی ہیں جو اپنی زبانوں کو کتاب میں موڑتے ہیں تاکہ تم اس کو کتاب میں سے سمجھو، حالاں کہ وہ کتاب میں سے نہیں اور وہ کہتے ہیں کہ یہ اللہ کی جانب سے ہے، حالاں کہ وہ اللہ کی جانب سے نہیں اور وہ جان کر اللہ پر جھوٹ بولتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,79,'3:79','کسی انسان کا یہ کام نہیں کہ اللہ اس کو کتاب اورحکمت اور نبوت دے اور وہ لوگوں سے یہ کہے کہ تم اللہ کو چھوڑ کر میرے بندے بن جاؤ بلکہ وہ تو کہے گا کہ تم اللہ والے بنو، اس واسطے کہ تم دوسروں کو کتاب کی تعلیم دیتے ہو اور خود بھی اس کو پڑھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,80,'3:80','اور نہ وہ تمھیں یہ حکم دے گا کہ تم فرشتوں اور پیغمبروں کو رب بناؤ کیا وہ تمھیں کفر کا حکم دے گا، بعد اس کے کہ تم اسلام لاچکے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,81,'3:81','اور جب اللہ نے پیغمبروں سے عہد لیا کہ جو کچھ میں نے تم کوکتاب اور حکمت دی، پھر تمھارے پاس پیغمبر آئے جو سچا ثابت کرے ان پیشین گوئیوں کو جو تمھارے پاس ہیں تو تم اس پر ایمان لاؤ گے اوراس کی مدد کرو گے اللہ نے کہا کیا تم نے اقرار کیا اور اس پر میرا عہد قبول کیا انھوں نے کہا ہم اقرار کرتے ہیں فرمایا اب گواہ رہو اور میں بھی تمھارے ساتھ گواہ ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(3,82,'3:82','پس جو شخص اس كے بعد پھر جائے تو ایسے ہی لوگ نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,83,'3:83','کیا یہ لوگ اللہ کے دین کے سواکوئی اور دین چاہتے ہیں حالاں کہ اسی کے فرماں بردار هيں جو آسمانوں اور زمین میں هيں ، خوشی سے یا ناخوشی سے، اور سب اسی کی طرف لوٹائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,84,'3:84','کہو ہم اللہ پر ایمان لائے اوراس پر جو ہمارے اوپر اتارا گیا اور جو اتارا گیا ابراہیم پر اسمٰعیل پر اسحاق پر اور یعقوب پر اور اولادِ یعقوب پر اورجو دیا گیا موسیٰ اور عیسیٰ اور دوسرے نبیوں کو ان کے رب کی طرف سے ہم ان کے درمیان فرق نہیں کرتے اور ہم اسی کے فرماں بردار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,85,'3:85','اور جو شخص اسلام کے سوا کسی دوسرے دین کو چاہے گا تو وہ اس سے ہرگز قبول نہ کیا جائے گا اور وہ آخرت میں نامرادوں میں سے ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(3,86,'3:86','اللہ کیوں کر ایسے لوگوں کو ہدایت دے گاجو ایمان لانے کے بعد منکر ہوگئے حالاں کہ وہ گواہی دے چکے کہ یہ رسول برحق ہے اور ان کے پاس روشن نشانیاں آچکی ہیں اور اللہ ظالموں کو ہدایت نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(3,87,'3:87','ایسے لوگوں کی سزا یہ ہے کہ ان پر اللہ کی، اس کے فرشتوں کی اور سارے انسانوں کی لعنت ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(3,88,'3:88','وہ ا س میں ہمیشہ رہیں گے، نہ ان کا عذاب ہلکا کیاجائے گا اور نہ ان کو مہلت دی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(3,89,'3:89','البتہ جو لوگ اس کے بعد توبہ کرلیں اور اپنی اصلاح کرلیں تو بے شک اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,90,'3:90','بے شک جو لوگ ایمان لانے کے بعد منکر ہوگئے پھر وہ کفر میں بڑھتے رہے، ان کی توبہ ہرگز قبول نہ کی جائے گی اوریہی لوگ گمراہ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,91,'3:91','بے شک جن لوگوں نے انکار کیا اور انکار کی حالت میں مرگئے، اگر وہ زمین بھر سونا بھی فدیہ میں دیں تو قبول نہ کیا جائے گا ان کے ليے درد ناک عذاب ہے اور ان کا کوئی مدد گار نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(3,92,'3:92','تم ہر گز نیکی کے مرتبہ کو نہیں پہنچ سکتے جب تک تم ان چیزوں میں سے نہ خرچ کرو جن کو تم محبوب رکھتے ہو اور جو چیز بھی تم خرچ کرو گے اس سے اللہ باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,93,'3:93','سب کھانے کی چیزیں بنی اسرائیل کے ليے حلال تھیں بجز اس کے جو اسرائیل نے اپنے اوپر حرام کرلیا تھا قبل اس کے کہ تورات اترے کہو کہ تورات لاؤ اوراس کو پڑھو، اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,94,'3:94','اس کے بعد بھی جو لوگ اللہ پر جھوٹ باندھیں وہی ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,95,'3:95','کہو، اللہ نے سچ کہا اب ابراہیم کے دین کی پیروی کرو جو حنیف تھا اور وہ شرک کرنے والا نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(3,96,'3:96','بے شک پہلا گھر جو لوگوں کے ليے بنایا گیا وہ وہی ہے جو مکہ میں ہے، برکت والا اور سارے جہان کے ليے ہدایت کا مرکز');
INSERT INTO "ur_wahid_uddin" VALUES(3,97,'3:97','اس میں کھلی ہوئی نشانیاں ہیں ، مقام ابراہیم ہے، جو اس میں داخل ہوجائے وہ مامون ہے اور لوگوں پر اللہ کا یہ حق ہے کہ جو اس گھر تک پہنچنے کی طاقت رکھتا ہو، وہ اس کا حج کرے اورجو کوئی منکر ہوا تواللہ تمام دنیا والوں سے بے نیازہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,98,'3:98','کہو اے اہل کتاب، تم کیوں اللہ کی نشانیوں کا انکار کرتے ہو حالاں کہ اللہ دیکھ رہاہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,99,'3:99','کہو، اے اہل کتاب، تم ایمان لانے والوں کو اللہ کی راہ سے کیوں روکتے ہو تم اس میں عیب ڈھونڈتے ہو حالاں کہ تم گواہ بنائے گئے ہو اور اللہ تمھارے کاموں سے بے خبر نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,100,'3:100','اے ایمان والو، اگر تم اہل کتاب میں سے ایک گروہ کی بات مان لو گے تو وہ تم کو ایمان کے بعد پھر منکر بنادیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,101,'3:101','اور تم کس طرح انکار کرو گے، حالاں کہ تم کو اللہ کی آیتیں سنائی جارہی ہیں اور تمھارے درمیان اس کا رسول موجود ہےاور جو شخص اللہ کو مضبوطی سے پکڑے گا تو وہ پہنچ گیا سیدھی راہ پر');
INSERT INTO "ur_wahid_uddin" VALUES(3,102,'3:102','اے ایمان والو، اللہ سے ڈرو جیسا کہ اس سے ڈرنا چاہيے اور تم کو موت نہ آئے مگر اس حال میں کہ تم مسلم ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,103,'3:103','اور سب مل کر اللہ کی رسی کو مضبوط پکڑ لو اور پھوٹ نہ ڈالو اور اللہ کا یہ انعام اپنے اوپر یاد رکھو کہ تم ایک دوسرے کے دشمن تھے پھر اس نے تمھارے دلوں میں الفت ڈال دی پس تم اس کے فضل سے بھائی بھائی بن گئے اور تم آگ کے گڑھے کے کنارے کھڑے تھے تو اللہ نے تم کو اس سے بچالیا اس طرح اللہ تمھارے ليے اپنی نشانیاں بیان کرتا ہے تاکہ تم راہ پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(3,104,'3:104','اور ضرور ہے کہ تم میں ایک گروہ ہو جو نیکی کی طرف بلائے، وہ بھلائی کا حکم دے اور برائی سے روکے اور ایسے ہی لوگ کامیاب ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,105,'3:105','اور تم ان لوگوں کی طرح نہ ہوجانا جو فرقوں میں بٹ گئے اور باہم اختلاف کرلیا بعد اس کے کہ ان کے پاس واضح احکام آچکے تھے اور ان کے ليے بڑا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,106,'3:106','جس دن کچھ چہرے روشن ہوں گے اور کچھ چہرے کالے ہوں گے، تو جن کے چہرے کالے ہوں گے ان سے کہا جائے گا کیا تم اپنے ایمان کے بعد منکرہوگئے، تو اب چکھو عذاب اپنے کفر کے سبب سے');
INSERT INTO "ur_wahid_uddin" VALUES(3,107,'3:107','اور جن کے چہرے روشن ہوں گے وہ اللہ کی رحمت میں ہوں گے، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,108,'3:108','یہ اللہ کی آیتیں ہیں جو ہم تم کو حق کے ساتھ سنا رہے ہیں اور اللہ جہان والوں پر ظلم نہیں چاہتا');
INSERT INTO "ur_wahid_uddin" VALUES(3,109,'3:109','اور جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے سب اللہ کے ليے ہے اور سارے معاملات اللہ ہی کی طرف لوٹائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,110,'3:110','اب تم بہترین گروہ ہو جس کو لوگوں کے واسطے نکالا گیا ہے تم بھلائی کا حکم دیتے ہو اور برائی سے روکتے ہو اور اللہ پر ایمان رکھتے ہو، اور اگر اہل کتاب بھی ایمان لاتے تو ان کے ليے بہتر ہوتا ان میں سے کچھ ایمان والے ہیں اور ان میں اکثر نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,111,'3:111','وہ تمھارا کچھ بگاڑ نہیں سکتے مگر کچھ ستانا اور اگر وہ تم سے مقابلہ کریں گے تو تم کو پیٹھ دکھائیں گے پھر ان کو مدد بھی نہ پہنچے گی');
INSERT INTO "ur_wahid_uddin" VALUES(3,112,'3:112','اور ان پر مسلط کردی گئی ذلت، خواہ وہ کہیں بھی پائے جائیں ، سوا اس کے کہ اللہ کی طرف سے کوئی عہد ہو یا لوگوں کی طرف سے کوئی عہد ہو اور وہ اللہ کے غضب کے مستحق ہوگئے اور ان پر مسلط کردی گئی پستی یہ اس واسطے کہ وہ اللہ کی نشانیوں کا انکار کرتے رہے اور انھوں نے پیغمبروں کو ناحق قتل کیا یہ اس سبب سے ہوا کہ انھوں نے نافرمانی کی اور وہ حد سے نکل جاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(3,113,'3:113','سب اہل کتاب یکساں نہیں ان میں ایک گروہ عہد پر قائم ہے وہ راتوں کو اللہ کی آیتیں پڑھتے ہیں اور وہ سجدہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,114,'3:114','وہ اللہ پر اور آخرت کے دن پر ایمان رکھتے ہیں اور بھلائی کا حکم دیتے ہیں اور برائی سے روکتے ہیں اور نیک کاموں میں دوڑتے ہیں یہ صالح لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,115,'3:115','جو نیکی بھی وہ کریں گے اس کی ناقدری نہ کی جائے گی اور اللہ پرہیز گاروں کو خوب جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,116,'3:116','بے شک جن لوگوں نے انکار کیا تو اللہ کے مقابلہ میں ان کے مال اور اولاد ان کے کچھ کام نہ آئیں گے اور وہ لوگ دوزخ والے ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,117,'3:117','وہ اس دنیا کی زندگی میں جو کچھ خرچ کرتے ہیں ، اس کی مثال اس ہواکی سی ہے جس میں پالا ہو اور وہ ان لوگوں کی کھیتی پر چلے جنھوں نے اپنے اوپر ظلم کیا ہے پھر وہ اس کو برباد کردے اللہ نے ان پرظلم نہیں کیا بلکہ وہ خود اپنی جانوں پر ظلم کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,118,'3:118','اے ایمان والو، اپنے غیر کو اپنا راز دار نہ بناؤ، وہ تمھیں نقصان پہنچانے میں کوئی کمی نہیں کرتےان کو خوشی ہوتی ہے تم جس قدر تکلیف پاؤ ان کی عداوت ان کی زبان سے نکلی پڑتی ہے اور جو ان کے دلوں میں ہے وہ اس سے بھی زیادہ سخت ہے، ہم نے تمھارے ليے نشانیاں کھول کر ظاہر کردی ہیں اگر تم عقل رکھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,119,'3:119','تم ان سے محبت رکھتے ہو مگر وہ تم سے محبت نہیں رکھتے حالاں کہ تم سب آسمانی کتابوں کو مانتے ہو اور جب وہ تم سے ملتے ہیں تو کہتے ہیں کہ ہم ایمان لائے اور جب وہ آپس میں ملتے ہیں توتم پر غصہ سے انگلیاں کاٹتے ہیں کہو، تم اپنے غصہ میں مرجاؤ بے شک اللہ دلوں کی بات کو جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,120,'3:120','اگر تم کو کوئی اچھی حالت پیش آتی ہے تو ان کو رنج ہوتا ہے اور اگر تم پر کوئی مصیبت آتی ہے تو وہ اس سے خوش ہوتے ہیں اگر تم صبر کرو اور اللہ سے ڈرو تو ان کی کوئی تدبیر تم کو نقصان نہ پہنچا سکے گی جو کچھ وہ کر رہے ہیں سب اللہ کے بس میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,121,'3:121','جب تم صبح کو اپنے گھر سے نکلے اور مسلمانوں کو جنگ کے مقامات پر متعین کیا اور اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,122,'3:122','جب تم میں سے دو جماعتوں نے ارادہ کیا کہ ہمت ہاردیں اور اللہ ان دونوں جماعتوں کا مدد گار تھا اور اللہ ہی پر چاہئے کہ مومنین بھروسہ کریں');
INSERT INTO "ur_wahid_uddin" VALUES(3,123,'3:123','اور اللہ تمھاری مدد کرچکا ہے بدر میں جب کہ تم کمزور تھےپس اللہ سے ڈرو تاکہ تم شکر گزار رہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,124,'3:124','جب تم مومنین سے کہہ رہے تھے کہ کیا تمھارے ليے کافی نہیں کہ تمھارا رب تین ہزار فرشتے اتار کر تمھاری مدد کرے');
INSERT INTO "ur_wahid_uddin" VALUES(3,125,'3:125','اگر تم صبر کرو اور اللہ سے ڈرو اور دشمن تمھارے اوپر یک دم آپہنچے تو تمھارا رب پانچ ہزارنشان کيے ہوئے فرشتوں سے تمھاری مدد کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(3,126,'3:126','اور یہ اللہ نے اس ليے کیا تاکہ تمھارے ليے خوش خبری ہو اور تمھارے دل اس سے مطمئن ہوجائیں اور مدد صرف اللہ ہی کی طرف سے ہے جو زبردست ہے،حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,127,'3:127','تاکہ اللہ منکروں کے ایک حصہ کو کاٹ دے یا ان کو ذلیل کردے کہ وہ ناکام لوٹ جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,128,'3:128','تم کو اس امر میں کوئی دخل نہیں اللہ ان کی توبہ قبول کرے یا ان کو عذاب دے، کیوں کہ وہ ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,129,'3:129','اور اللہ ہی کے اختیار میں ہے جوکچھ آسمانوں میں ہے اورجو کچھ زمین میں ہے وہ جس کو چاہے بخش دے اور جس کو چاہے عذاب دے اور اللہ غفور اور رحیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,130,'3:130','اے ایمان والو، سود کئی کئی گنا بڑھا کر نہ کھاؤ اوراللہ سے ڈرو تاکہ تم کامیاب ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,131,'3:131','اور ڈرو اس آگ سے جو منکروں کے ليے تیار کی گئی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,132,'3:132','اور اللہ اور رسول کی اطاعت کرو تاکہ تم پر رحم کیاجائے');
INSERT INTO "ur_wahid_uddin" VALUES(3,133,'3:133','اور دوڑو اپنے رب کی بخشش کی طرف اور اس جنت کی طر ف جس کی وسعت آسمانوں اور زمین جیسی ہے وہ تیار کی گئی ہے اللہ سے ڈرنے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(3,134,'3:134','جو لوگ کہ خرچ کرتے ہیں فراغت میں اور تنگی میں وہ غصہ کو پی جانے والے ہیں اور لوگوں سے درگزر کرنے والے ہیں اور اللہ نیکی کرنے والوں کو دوست رکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,135,'3:135','اور ایسے لوگ کہ جب وہ کوئی کھلی برائی کربیٹھیں یا اپنی جان پر کوئی ظلم کرڈالیں تو وہ اللہ کو یاد کرکے اپنے گناہوں کی معافی مانگیں اللہ کے سوا کون ہے جو گنا ہوں کو معاف کرے اور وہ جانتے بوجھتے اپنے کيے پر اصرارنہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(3,136,'3:136','یہ لوگ ہیں کہ ان کا بدلہ ان کے رب کی طرف سے مغفرت ہے اورایسے باغ ہیں جن کے نیچے نہریں بہتی ہوں گی ان میں وہ ہمیشہ رہیں گے کیسا اچھا بدلہ ہے کام کرنے والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(3,137,'3:137','تم سے پہلے بہت سی مثالیں گزر چکی ہیں تو زمین میں چل پھر کر دیکھو کہ کیا انجام ہوا جھٹلانے والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(3,138,'3:138','یہ بیان ہے لوگوں کے ليے اور ہدایت اور نصیحت ہے ڈرنے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(3,139,'3:139','اور ہمت نہ ہارو اور غم نہ کرو، تم ہی غالب رہو گے اگر تم مومن ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,140,'3:140','اگر تم کو کوئی زخم پہنچے تو دشمن کوبھی ویسا ہی زخم پہنچا ہے اور ہم ان ایام کو لوگوں کے درمیان بدلتے رہتے ہیں ، تاکہ اللہ ایمان والوں کو جان لے اور تم میں سے کچھ لوگوں کو گواہ بنائے اور اللہ ظالموں کو دوست نہیں رکھتا');
INSERT INTO "ur_wahid_uddin" VALUES(3,141,'3:141','اور تاکہ اللہ ایمان والوں کو چھانٹ لے اورانکار کرنے والوں کو مٹادے');
INSERT INTO "ur_wahid_uddin" VALUES(3,142,'3:142','کیا تم خیال کرتے ہو کہ تم جنت میں داخل ہوجاؤ گے، حالاں کہ ابھی اللہ نے تم میں سے ان لوگوں کوجانا نہیں جنھوں نے جہاد کیا اور نہ ان کو جو ثابت قدم رہنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,143,'3:143','اور تم موت کی تمنا کر رہے تھے اس سے ملنے سے پہلے، سو اب تم نے اس کو کھلی آنکھوں سے دیکھ لیا');
INSERT INTO "ur_wahid_uddin" VALUES(3,144,'3:144','محمد بس ایک رسول ہیں ان سے پہلے بھی رسول گزر چکے ہیں پھر کیا اگر وہ مرجائیں یا قتل کردئے جائیں تو تم الٹے پاؤں پھر جاؤ گے اور جو شخص پھر جائے وہ اللہ کا کچھ نہیں بگاڑے گا اور اللہ شکر گزاروں کو بدلہ دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(3,145,'3:145','اور کوئی جان مر نہیں سکتی بغیر اللہ کے حکم کے اللہ کالکھا ہوا وعدہ ہے اور جو شخص دنیا کا فائدہ چاہتا ہے، اس کو ہم دنیا میں سے دے دیتے ہیں اور جو آخرت کا فائدہ چاہتاہے، اس کو ہم آخرت میں سے دے دیتے ہیں اور شکر کرنے والوں کو ہم ان کا بدلہ ضرور عطا کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,146,'3:146','اور کتنے نبی ہیں جن کے ساتھ ہوکر بہت سے اللہ والوں نے جنگ کی اللہ کی راہ میں جو مصیبتیں ان پر پڑیں ، ان سے نہ وہ پست ہمت ہوئے ،نہ انھوں نے کمزوری دکھائی اور نہ وہ دبے اور اللہ صبر کرنے والوں کو دوست رکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,147,'3:147','ان کی زبان سے اس کے سوا کچھ اور نہ نکلا کہ اے ہمارے رب، ہمارے گناہوں کو بخش دے اور ہمارے کام میں ہم سے جو زیادتی ہوئی اس کو معاف فرما اور ہم کو ثابت قدم رکھ اورمنکر قوم کے مقابلہ میں ہماری مدد فرما');
INSERT INTO "ur_wahid_uddin" VALUES(3,148,'3:148','پس اللہ نے ان کو دنیا کا بدلہ بھی دیا اور آخرت کااچھا بدلہ بھی اور اللہ نیکی کرنے والوں کو دوست رکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,149,'3:149','اے ایمان والو، اگر تم منکروں کی بات مانو گے تو وہ تم کو الٹے پاؤں پھیر دیں گے، پھر تم ناکام ہوکر رہ جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,150,'3:150','بلکہ اللہ تمھارا مدد گار ہے اور وہ سب سے بہتر مدد کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,151,'3:151','ہم منکروں کے دلوں میں تمھارا رعب ڈال دیں گے کیوں کہ انھوں نے ایسی چیز کو اللہ کا شریک ٹھہرایا جس کے حق میں اللہ نے کوئی دلیل نہیں اتاری ان کا ٹھکانا جہنم ہے اور وہ بری جگہ ہے ظالموں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(3,152,'3:152','اور اللہ نے تم سے اپنے وعدہ کو سچا کر دکھایا جب کہ تم ان کو اللہ کے حکم سے قتل کرر ہے تھے یہاں تک کہ جب تم خود کمزور پڑگئے اور تم نے کام میں جھگڑا کیا اور تم کہنے پر نہ چلے جب کہ اللہ نے تم کو وہ چیزدکھادی تھی جو کہ تم چاہتے تھے تم میں سے بعض دنیا چاہتے تھے اور تم میں سے بعض آخرت چاہتے تھے پھر اللہ نے تمھارا رخ ان سے پھیر دیا، تاکہ وہ تمھاری آزمائش کرے اور اللہ نے تم کو معاف کردیا اور اللہ ایمان والوں کے حق میں بڑا فضل والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,153,'3:153','جب تم چڑھے جارہے تھے اور مڑ کر بھی کسی کو نہ دیکھتے تھے اور رسول تم کو تمھارے پیچھے سے پکار رہا تھا پھر اللہ نے تم کو غم پر غم دیاتاکہ تم رنجیدہ نہ ہو اس چیز پر جو تمھارے ہاتھ سے چوک گئی اور نہ اس مصیبت پر جوتم پر پڑے اور اللہ خبردار ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,154,'3:154','پھر اللہ نے تمھارے اوپر غم کے بعد اطمینان اتارا یعنی اونگھ، جس کا تم میں سے ایک جماعت پر غلبہ ہو رہا تھا اورایک جماعت وہ تھی کہ اس کو اپنی جانوں کی فکر پڑی ہوئی تھی وہ اللہ کے بارے میں خلافِ حقیقت خیالات، جاہلیت کے خیالات قائم کرر ہے تھےوہ کہتے تھے کیا ہمارا بھی کچھ اختیار ہے کہو، سارا معاملہ اللہ کے اختیار میں ہے وہ اپنے دلوں میں ایسی بات چھپائے ہوئے ہیں جو وہ تم پر ظاہر نہیں کرتے وہ کہتے ہیں کہ اگر اس معاملہ میں کچھ ہمارا بھی دخل ہوتا تو ہم یہاں نہ مارے جاتے کہو، اگر تم اپنے گھروں میں ہوتے تب بھی جن کا قتل ہونا لکھ گیا تھا، وہ اپنی قتل گاہوں کی طر ف نکل پڑتے یہ اس ليے ہوا کہ اللہ کو آزمانا تھا جو کچھ تمھارے سینوں میں ہے اور نکھارنا تھا جو کچھ تمھارے دلوں میں ہے اور اللہ جانتا ہے سینوں والی بات کو');
INSERT INTO "ur_wahid_uddin" VALUES(3,155,'3:155','تم میں سے جو لوگ پھر گئے تھے اس دن کہ دونوں گروہوں میں مڈبھیڑ ہوئی، ان کو شیطان نے ان کے بعض اعمال کے سبب سے پھسلادیا تھا اللہ نے ان کو معاف کردیا بے شک اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,156,'3:156','اے ایمان والو، تم ان لوگوں کے مانند نہ ہوجانا جنھوں نے انکار کیا وہ اپنے بھائیوں کی نسبت کہتے ہیں ، جب کہ وہ سفر یا جہاد میں نکلتے ہیں اور ان کو موت آجاتی ہے، کہ اگر وہ ہمارے پاس رہتے تو نہ مرتے اور نہ مارے جاتے تاکہ اللہ اس کو ان کے دلوں میں سببِ حسرت بنادے اور اللہ ہی جلاتا ہے اورمارتا ہے، اورجو کچھ تم کرتے ہو اللہ اس کو دیکھ رہاہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,157,'3:157','اور اگر تم اللہ کی راہ میں مارے جاؤ یا مرجاؤ تو اللہ کی مغفرت اور رحمت اس سے بہتر ہے جس کو وہ جمع کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,158,'3:158','اور تم مرگئے یا مارے گئے بہرحال تم اللہ ہی کے پاس اكٹھاکيے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,159,'3:159','یہ اللہ کی بڑی رحمت ہے کہ تم ان کے ليے نرم ہو اگر تم تند خو اور سخت دل ہوتے تو یہ لوگ تمھارے پاس سے بھاگ جاتے پس ان کو معاف کردو اوران کے ليے مغفرت مانگو اور معاملات میں ان سے مشورہ لو پھر جب فیصلہ کرلو تو اللہ پر بھروسہ کرو بیشک اللہ ان سے محبت کرتا ہے جواس پر بھروسہ رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,160,'3:160','اگر اللہ تمھارا ساتھ دے تو کوئی تم پر غالب نہیں آسکتا اور اگر وہ تمھارا ساتھ چھوڑ دے تو اس کے بعد کون ہے جو تمھاری مدد کرے اور اللہ ہی کے اوپر بھروسہ کرناچاہئے ایمان والوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(3,161,'3:161','اور نبی کا یہ کام نہیں کہ وہ کچھ چھپا رکھے اور جوکوئی چھپائے گا وہ اپنی چھپائی ہوئی چیز کو قیامت کے دن حاضر کرے گا پھر ہرجان کو اس کے کيے ہوئے کا پورا بدلہ ملے گا اور ان پر کچھ ظلم نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(3,162,'3:162','کیا وہ شخص جو اللہ کی مرضی کا تابع ہے، وہ اس شخص کے مانند ہوجائے گا جو اللہ کاغضب لے کرلوٹا اور اس کا ٹھکاناجہنم ہے اور وہ کیسا براٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,163,'3:163','اللہ کے یہاں ان کے درجے الگ الگ ہوں گےاور اللہ دیکھ رہا ہے جو وہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,164,'3:164','اللہ نے ایمان والو ں پر احسان کیا کہ ان میں انھیں میں سے ایک رسول بھیجا جو ان کو اللہ کی آیتیں سناتا ہے اور ان کو پاک کرتا ہے اور ان کو کتاب اور حکمت کی تعلیم دیتا ہے بے شک یہ اس سے پہلے کھلی ہوئی گمراہی میں تھے');
INSERT INTO "ur_wahid_uddin" VALUES(3,165,'3:165','اور جب تم کو ایسی مصیبت پہنچی جس کی دوگنی مصیبت تم پہنچاچکے تھے تو تم نے کہا کہ یہ کہاں سے آگئی کہو یہ تمھارے اپنے پاس سے ہے بے شک اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,166,'3:166','اور دونوں جماعتوں کی مڈبھیڑ کے دن تم کو جو مصیبت پہنچی، وہ اللہ کے حکم سے پہنچی اور اس واسطے کہ اللہ مومنین کو جان لے');
INSERT INTO "ur_wahid_uddin" VALUES(3,167,'3:167','اور ان کو بھی جان لے جو منافق تھے جن سے کہا گیا کہ آؤ اللہ کی راہ میں لڑو یا دشمن کو دفع کرو انھوں نے کہا اگر ہم جانتے کہ جنگ ہونا ہے تو ہم ضرور تمھارے ساتھ چلتے یہ لوگ اس دن ایمان سے زیادہ کفر کے قریب تھے وہ اپنے منہ سے وہ بات کہتے ہیں جو ان کے دلوں میں نہیں ہے اور اللہ اس چیزکو خوب جانتا ہے جس کو وہ چھپاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,168,'3:168','یہ لوگ جو خود بیٹھے رہے، اپنے بھائیوں کی نسبت کہتے ہیں کہ اگر وہ ہماری بات مانتے تو مارے نہ جاتے کہو تم اپنے اوپر سے موت کو ہٹادو اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,169,'3:169','اور جو لوگ اللہ کی راہ میں مارے گئے ان کو مردہ نہ سمجھو، بلکہ وہ زندہ ہیں اپنے رب کے پاس، ان کو روزی مل رہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,170,'3:170','وہ خوش ہیں اس پر جو اللہ نے اپنے فضل میں سے ان کو دیا ہے اورخوش خبری لے رہے ہیں کہ جو لوگ ان کے پیچھے ہیں اور ابھی وہاں نہیں پہنچے ہیں ، ان کے ليے بھی نہ کوئی خوف ہے اور نہ وہ غمگین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(3,171,'3:171','وہ خوش ہو رہے ہیں اللہ کے انعام اور فضل پر اور اس پر کہ اللہ ایمان والوں کا اجر ضائع نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(3,172,'3:172','جن لوگوں نے اللہ اور رسول کے حکم کو مانا بعد اس کے کہ ان کو زخم لگ چکا تھا، ان میں سے جو نیک اور متقی ہیں ان کے ليے بڑا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,173,'3:173','جن سے لوگوں نے کہا کہ دشمن نے تمھارے خلاف بڑی طاقت جمع کرلی ہے اس سے ڈرو ،لیکن اس چیز نے ان کے ایمان میں اور اضافہ کردیا اور وہ بولے کہ اللہ ہمارے ليے کافی ہے اور وہ بہترین کارساز ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,174,'3:174','پس وہ اللہ کی نعمت اور اس کے فضل کے ساتھ واپس آئے ان لوگوں کوکوئی برائی پیش نہ آئی اور وہ اللہ کی رضا مندی پر چلے اور اللہ بڑا فضل والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,175,'3:175','یہ شیطان ہے جو تم کو اپنے دوستوں کے ذریعہ ڈراتا ہے تم ان سے نہ ڈرو، بلکہ مجھ سے ڈرو اگر تم مومن ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,176,'3:176','اور وہ لوگ تمھارے ليے باعثِ غم نہ بنیں جو انکار میں سبقت کر رہے ہیں وہ اللہ کو ہر گز کوئی نقصان نہ پہنچاسکیں گے اللہ چاہتا ہے کہ ان کے ليے آخرت میں کوئی حصہ نہ رکھے ان کے ليے بڑا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,177,'3:177','جن لوگوں نے ایمان کے بدلے کفر کو خریدا ہے، وہ اللہ کا کچھ بگاڑ نہیں سکتے اوران کے ليے درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,178,'3:178','اور جو لوگ کفر کر رہے ہیں ، وہ یہ خیال نہ کریں کہ ہم جو ان کو مہلت دے رہے ہیں یہ ان کے حق میں بہتر ہے ہم تو بس اس ليے مہلت دے رہے ہیں کہ وہ جرم میں اور بڑھ جائیں اور ان کے ليے ذلیل کرنے والا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,179,'3:179','اللہ ايسا نہیں کہ وه مومنوں کواس حالت پر چھوڑ دے جس پر کہ تم اب ہو جب تک کہ وہ ناپاک کو پاک سے جدا نہ کرلے اور اللہ ايسا نہیں کہ وه تم کو غیب سے باخبرکردےبلکہ اللہ چن لیتا ہے اپنے رسولوں میں سے جس کو چاہتا ہے پس تم ایمان لاؤ اللہ پر اور اس کے رسولوں پر اور اگر تم ایمان لاؤ اور پرہیزگاری اختیار کرو تو تمھارے ليے بڑا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,180,'3:180','اور جو لوگ بخل کرتے ہیں اس چیز میں جو اللہ نے ان کو اپنے فضل میں سے دیا ہے، وہ ہرگز یہ نہ سمجھیں کہ یہ ان کے حق میں اچھا ہے، بلکہ یہ ان کے حق میں بہت برا ہے جس چیز میں وہ بخل کر رہے ہیں اس کا قیامت کے دن ان کو طوق پہنایا جائے گا اور اللہ ہی وارث ہے زمین اور آسمانوں کا اور جو کچھ تم کرتے ہو، اللہ اس سے باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,181,'3:181','اللہ نے ان لوگوں کا قول سنا جنھو ں نے کہا کہ اللہ محتاج ہے اور ہم غنی ہیں ہم لکھ لیں گے ان کے اس قول کو اور ان کے پیغمبروں کو ناحق مار ڈالنے کو بھی اور ہم کہیں گے کہ اب آگ کا عذاب چکھو');
INSERT INTO "ur_wahid_uddin" VALUES(3,182,'3:182','یہ تمھارے اپنے ہاتھوں کی کمائی ہے اور اللہ اپنے بندوں کے ساتھ نا انصافی کرنے والانہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,183,'3:183','جولوگ کہتے ہیں کہ اللہ نے ہم کو حکم دیا ہے کہ ہم کسی رسول کو تسلیم نہ کریں جب تک وہ ہمارے سامنے ایسی قربانی پیش نہ کرے جس کو آگ کھالے، ان سے کہو کہ مجھ سے پہلے تمھارے پاس رسول آئے کھلی نشانیاں لے کر اور وہ چیز لے کر جس کو تم کہہ رہے ہو، پھر تم نے کیوں ان کو مارڈالا، اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(3,184,'3:184','پس اگر یہ تم کو جھٹلاتے ہیں تو تم سے پہلے بھی بہت سے رسول جھٹلائے جاچکے ہیں جوکھلی نشانیاں اور صحیفے اور روشن کتاب لے کر آئے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(3,185,'3:185','ہر شخص کوموت کا مزہ چکھنا ہے اور تم کو پورا اجر تو بس قیامت کے دن ملے گا پس جو شخص آگ سے بچ جائے اور جنت میں داخل کیا جائے، وہی کامیاب رہا اور دنیا کی زندگی تو بس دھوکے کا سودا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,186,'3:186','یقینا تم اپنے جان اور مال میں آزمائے جاؤ گےاور تم بہت سی تکلیف دہ باتیں سنو گے ان سے جن کو تم سے پہلے کتاب ملی اور ان سے بھی جنھوں نے شرک کیا اور اگر تم صبر کرو اور تقویٰ اختیار کرو تو یہ بڑے حوصلہ کا کام ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,187,'3:187','اور جب اللہ نے اہل کتاب سے عہد لیا کہ تم خدا کی کتاب کو پوری طرح لوگوں کے لیے ظاہر کرو گے اور اس کو نہیں چھپاؤ گے مگر انھوں نے اس کو پس پشت ڈال دیا اوراس کو تھوڑی قیمت پر بیچ ڈالا کیسی بری چیز ہے جس کو وہ خرید رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,188,'3:188','جو لوگ اپنے ان کرتوتوں پر خوش ہیں اور چاہتے ہیں کہ جو کام انھوں نے نہیں کيے اس پر ان کی تعریف ہو، ان کو عذاب سے بری نہ سمجھو ان کے ليے درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,189,'3:189','اور اللہ ہی کے ليے ہے زمین اور آسمان کی بادشاہی، اور اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,190,'3:190','آسمانوں اور زمین کی پیدائش میں اوررات دن کے باری باری آنے میں عقل والوں کے ليے بہت نشانیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,191,'3:191','جو کھڑے اوربیٹھے اور اپنی کروٹوں پر اللہ کو یاد کرتے ہیں اور آسمانوں اور زمین کی پیدائش میں غور کرتے رہتے ہیں وہ کہہ اٹھتے ہیں اے ہمارے رب، تو نے یہ سب بے مقصد نہیں بنایا تو پاک ہے، پس ہم کو آگ کے عذاب سے بچا');
INSERT INTO "ur_wahid_uddin" VALUES(3,192,'3:192','اے ہمارے رب، تو نے جس کو آگ میں ڈالا، اس کو تونے واقعی رسوا کردیا اور ظالموں کاکوئی مدد گار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,193,'3:193','اے ہمارے رب، ہم نے ایک پکارنے والے کو سنا جوایمان کی طرف پکار رہا تھا کہ اپنے رب پر ایمان لاؤ، پس ہم ایمان لائے اے ہمارے رب، ہمارے گناہو ں کو بخش دے اور ہماری برائیوں کو ہم سے دور کردے اور ہمارا خاتمہ نیک لوگوں کے ساتھ کر');
INSERT INTO "ur_wahid_uddin" VALUES(3,194,'3:194','اے ہمارے رب، تونے جو وعدے اپنے رسولوں کی معرفت ہم سے کيے ہیں ،ان کو ہمارے ساتھ پورا کر اور قیامت کے دن ہم کو رسوائی میں نہ ڈال بے شک تو اپنے وعدے کے خلاف کرنے والا نہیں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,195,'3:195','ان کے رب نے ان کی دعا قبول فرمائی کہ میں تم میں سے کسی کا عمل ضائع کرنے والا نہیں ، خواہ وہ مرد ہو یا عورت، تم سب ایک دوسرے سے ہو پس جن لوگوں نے ہجرت کی اور جو اپنے گھروں سے نکالے گئے اور میری راہ میں ستائے گئے اور وہ لڑے اور مارے گئے، میں ان کی خطائیں ضرور ان سے دور کردوں گا اور ان کو ایسے باغوں میں داخل کروں گاجن کے نیچے نہریں بہتی ہوں گی یہ ان کا بدلہ ہے اللہ کے یہاں اور بہترین بدلہ اللہ ہی کے پاس ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,196,'3:196','اور ملک کے اند رمنکروں کی سرگرمیاں تم کو دھوکے میں نہ ڈالیں');
INSERT INTO "ur_wahid_uddin" VALUES(3,197,'3:197','یہ تھوڑا سا فائدہ ہے پھر ان کا ٹھکانا جہنم ہے اور وہ کیسابرا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,198,'3:198','البتہ جو لوگ اپنے رب سے ڈرتے ہیں ، ان کے ليے باغ ہوں گے جن کے نیچے نہریں بہتی ہوں گی وہ اس میں ہمیشہ رہیں گے یہ اللہ کی طرف سے ان کی میزبانی ہوگی اور جو کچھ اللہ کے پاس نیک لوگوں کے ليے ہے وہی سب سے بہتر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,199,'3:199','اور بے شک اہل کتاب میں کچھ ایسے بھی ہیں جو اللہ پر ایمان رکھتے ہیں اور اس کتاب کو بھی مانتے ہیں جو تمھاری طرف بھیجی گئی ہے اور اس کتاب کو بھی مانتے ہیں جو اس سے پہلے خود ان کی طرف بھیجی گئی تھی، وہ اللہ کے آگے جھکے ہوئے ہیں اور وہ اللہ کی آیتوں کو تھوڑی قیمت پر بیچ نہیں دیتے ان کا اجر ان کے رب کے پاس ہے اور اللہ جلدحساب لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(3,200,'3:200','اے ایمان والو، صبر کرو اور مقابلہ میں مضبوط رہو اور لگے رہو اور اللہ سے ڈرو، امید ہے کہ تم کامیاب ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(4,1,'4:1','اے لوگو، اپنے رب سے ڈرو جس نے تم کو ایک جان سے پیدا کیا اور اسی سے اس کا جوڑا پیدا کیا اور ان دونوں سے بہت سے مرد اور عورتیں پھیلادیں اور اللہ سے ڈرو جس کے واسطے سے تم ایک دوسرے سے سوال کرتے ہو اور خبردار رہو قرابت والوں سے بے شک اللہ تمھاری نگرانی کر رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,2,'4:2','اور یتیموں کا مال ان کے حوالے کرو اور برے مال کو اچھے مال سے نہ بدلو اور ان کے مال اپنے مال کے ساتھ ملاکر نہ کھاؤیہ بہت بڑا گناہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,3,'4:3','اور اگر تم کو اندیشہ ہو کہ تم یتیموں کے معاملہ میں انصاف نہ کرسکو گے تو عورتوں میں سے جو تم کو پسند ہوں ان سے دو دو، تین تین، چار چار تک نکاح کر لو اور اگر تم کو اندیشہ ہو کہ تم عدل نہ کرسکو گے تو ایک ہی نکاح کرو یا جو کنیز تمھاری مِلک میں ہو اس میں امید ہے کہ تم انصاف سے نہ ہٹو گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,4,'4:4','اور عورتوں کو ان کے مہر خوش دلی کے ساتھ ادا کرو پھر اگر وہ اس میں سے کچھ تمھارے ليے چھوڑ دیں اپنی خوشی سے تو تم اس کو ہنسی خوشی سے کھاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(4,5,'4:5','اور نادانوں کو اپنا وہ مال نہ دو جس کو اللہ نے تمھارے ليے قیام کا ذریعہ بنایا ہے اور اس مال میں سے ان کو کھلاؤ اور پہناؤ اوران سے بھلائی کی بات کہو');
INSERT INTO "ur_wahid_uddin" VALUES(4,6,'4:6','اور یتیموں کو جانچتے رہو، یہاں تک کہ جب وہ نکاح کی عمر کو پہنچ جائیں تو اگر تم ان میں ہوشیاری دیکھو تو ان کا مال ان کے حوالے کردو اور ان کا مال اسراف سے اور اس خیال سے کہ وہ بڑے ہوجائیں گے نہ کھا جاؤ اور جس کو حاجت نہ ہو وہ یتیم کے مال سے پرہیز کرے اور جو شخص محتاج ہو، وہ دستور کے موافق کھائے پھر جب تم ان کا مال ان کے حوالے کرو تو ان پر گواہ ٹھہرالو اور اللہ حساب لینے کے ليے کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,7,'4:7','ماں باپ اور قرابت داروں کے ترکہ میں سے مردوں کا بھی حصہ ہے اور ماں باپ اورقرابت داروں کے ترکہ میں سے عورتوں کا بھی حصہ ہے، تھوڑا ہو یا زیادہ ہو، ایک مقرر کیا ہوا حصہ');
INSERT INTO "ur_wahid_uddin" VALUES(4,8,'4:8','اور اگر تقسیم کے وقت رشتہ دار اور یتیم اور محتاج موجود ہوں تو اس میں سے ان کو بھی کچھ دو اور ان سے ہمدردی کی بات کہو');
INSERT INTO "ur_wahid_uddin" VALUES(4,9,'4:9','اور ایسے لوگوں کو ڈرنا چاہيے کہ اگر وہ اپنے پیچھے ناتواں بچے چھو ڑ جاتے تو انھیں ان کی بہت فکر رہتی پس ان کو چاہيے کہ اللہ سے ڈریں اور بات پکی کہیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,10,'4:10','جو لوگ یتیموں کا مال ناحق کھاتے ہیں ، وہ لوگ اپنے پیٹوں میں آگ بھر رہے ہیں اور وہ عنقریب بھڑکتی ہوئی آگ میں ڈالے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,11,'4:11','اللہ تم کو تمھاری اولاد کے بارے میں حکم دیتاہے کہ مرد کا حصہ دو عورتوں کے برابر ہے اگر عورتیں دو سے زائد ہیں تو ان کے ليے دو تہائی ہے اس مال سے جو مورث چھوڑ گیا ہے، اور اگر وہ اکیلی ہے تو اس کے ليے آدھا ہے اور میت کے ماں باپ کو دونوں میں سے ہر ایک کے ليے چھٹا حصہ ہے اس مال کا جو وہ چھوڑ گیاہے بشرطیکہ مورث کے اولاد ہو اور اگر مورث کے اولاد نہ ہو اور اس کے ماں باپ اس کے وارث ہوں تو اس کی ماں کا تہائی حصہ ہے اور اگر اس کے بھائی بہن ہوں تو اس کی ماں کے ليے چھٹا حصہ ہے یہ حصے وصیت نکالنے کے بعد یا ادائے قرض کے بعد ہیں جو وہ کرجاتا ہے تمھارے باپ ہوں یا تمھارے بیٹے ہوں ، تم نہیں جانتے کہ ان میں تمھارے ليے سب سے زیادہ نافع کون ہے یہ اللہ کا ٹھہرایا ہوا فریضہ ہے بے شک اللہ علم والا، حکمت والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,12,'4:12','اور تمھارے ليے اس مال کا آدھا حصہ ہے جو تمھاری بیویاں چھوڑیں ، بشرطیکہ ان کے اولادنہ ہواور اگر ان کے اولاد ہو تو تمھارے ليے بیویوں کے ترکہ کا چوتھائی حصہ ہے وصیت نکالنے کے بعد جس کی وہ وصیت کرجائیں یا ادائے قرض کے بعد اوران بیویوں کے ليے چوتھائی حصہ ہے تمھارے ترکہ کا اگر تمھارے اولادنہیں ہے، اور اگر تمھارے اولاد ہے تو ان کے ليے آٹھواں حصہ ہے تمھارے ترکہ کا بعد وصیت نکالنے کے جس کی تم وصیت کرجاؤ یا ادائے قرض کے بعد اور اگر کوئی مورث مردیا عورت ایسا ہو جس کے نہ اصول ہوں اور نہ فروع، اور اس کے ایک بھائی یا ایک بہن ہو تو دونوں میں سے ہر ایک کے ليے چھٹا حصہ ہے اور اگر وہ اس سے زائد ہوں ، تو وہ ایک تہائی میں شریک ہوں گے بعد وصیت نکالنے کے جس کی وصیت کی گئی ہو یا ادائے قرض کے بعد، بغیر کسی کو نقصان پہنچائے یہ حکم اللہ کی طرف سے ہے اور اللہ علیم اور حلیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,13,'4:13','یہ اللہ کی ٹھہرائی ہوئی حدیں ہیں اور جو شخص اللہ اور اس کے رسول کی اطاعت کرے گا، اللہ اس کو ایسے باغوں میں داخل کرے گاجن کے نیچے نہریں بہتی ہوں گی، ان میں وہ ہمیشہ رہیں گے اور یہی بڑی کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,14,'4:14','اور جو شخص اللہ اور اس کے رسول کی نافرمانی کرے گا اور اس کے مقرر کيے ہوئے ضابطوں سے باہر نکل جائے گا، اس کو وہ آگ میں داخل کرے گاجس میں وہ ہمیشہ رہے گا اور اس کے ليے ذلت و الا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,15,'4:15','اور تمھاری عورتوں میں سے جو کوئی بدکاری کرے تو ان پر اپنوں میں سے چار مرد گواہ کرو پھر اگر وہ گواہی دے دیں تو ان عورتوں کو گھروں کے اندربند رکھو، یہاں تک کہ ان کوموت اٹھالے یا اللہ ان کے ليے کوئی راہ نکال دے');
INSERT INTO "ur_wahid_uddin" VALUES(4,16,'4:16','اور تم میں سے دو مرد جو وہی بدکاری کریں تو ان کو اذیت پہنچاؤپھر اگر وہ دونوں توبہ کریں اور اپنی اصلاح کرلیں تو ان کا خیال چھوڑ دو بے شک اللہ توبہ قبول کرنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,17,'4:17','تو بہ، جس کو قبول کرنا اللہ کے ذمہ ہے، وہ ان لوگوں کی ہے جوبری حرکت نادانی سے کربیٹھتے ہیں ، پھر جلد ہی توبہ کرلیتے ہیں وہی ہیں جن کی توبہ اللہ قبول کرتا ہے اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,18,'4:18','اور ایسے لوگوں کی توبہ نہیں ہے جو برابر گناہ کرتے رہیں ، یہاں تک کہ جب موت ان میں سے کسی کے سامنے آجائے تب وہ کہے کہ اب میں توبہ کرتا ہوں اور نہ ان لوگوں کی توبہ ہے جو اس حال میں مرتے ہیں کہ وہ منکرہیں ، ان کے ليے تو ہم نے درد ناک عذاب تیار کر رکھاہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,19,'4:19','اے ایمان والو، تمھارے ليے جائز نہیں کہ تم عورتوں کو زبردستی اپنی میراث میں لے لو اور نہ ان کو اس غرض سے روکے رکھوکہ تم نے جو کچھ ان کو دیا ہے اس کا کچھ حصہ ان سے لے لو، مگر اس صورت میں کہ وہ کھلی ہوئی بے حیائی کریں اور ان کے ساتھ اچھی طرح گزر بسر کرو اگر وہ تم کوناپسند ہوں تو ہوسکتا ہے کہ ایک چیز تم کو پسند نہ ہو مگر اللہ نے اس میں تمھارے ليے بہت بڑی بھلائی رکھ دی ہو');
INSERT INTO "ur_wahid_uddin" VALUES(4,20,'4:20','اور اگر تم ایک بیوی کی جگہ دوسری بیوی بدلناچاہو اور تم اس کو بہت سا مال دے چکے ہو تو تم اس میں سے کچھ واپس نہ لو کیا تم اس کو بہتان لگاکر اور صریح ظلم کرکے واپس لو گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,21,'4:21','اور تم کس طرح اس کو لو گے جب کہ ایک، دوسرے سے خلوت کرچکا ہے اور وہ تم سے پختہ عہد لے چکی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,22,'4:22','اور ان عورتوں سے نکاح مت کرو جن سے تمھارے باپ نکاح کرچکے ہیں ، مگر جو پہلے ہوچکا بے شک یہ بے حیائی ہے اور نفرت کی بات ہے اور بہت برا طریقہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,23,'4:23','تمھارے اوپر حرام کی گئیں تمھاری مائیں ، تمھاری بیٹیاں ، تمھاری بہنیں ، تمھاری پھوپھیاں ، تمھاری خالائیں ، تمھاری بھتیجیاں اور بھانجیاں اور تمھاری وہ مائیں جنھوں نے تم کو دودھ پلایا، تمھاری دودھ شریک بہنیں ، تمھاری عورتوں کی مائیں اور ان کی بیٹیاں جو تمھاری پرورش میں ہیں جو تمھاری ان بیویوں سے ہوں جن سے تم نے صحبت کی ہے، لیکن اگر ابھی تم نے ان سے صحبت نہ کی ہو تو تم پر کوئی گناہ نہیں اور تمھارے صلبی بیٹوں کی بیویاں اوریہ کہ تم اکھٹا کرو دو بہنوں کو مگر جو پہلے ہوچکا بے شک اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,24,'4:24','اور وہ عورتیں بھی حرام ہیں جو کسی دوسرے کے نکاح میں ہوں ، مگر یہ کہ وہ جنگ میں تمھارے ہاتھ آئیں یہ اللہ کا حکم ہے تمھارے اوپر ان کے علاوہ جو عورتیں ہیں ، وہ سب تمھارے ليے حلال ہیں بشرطیکہ تم اپنے مال کے ذریعہ سے ان کے طالب بنو، ان کو قید ِ نکاح میں لے کر، نہ کہ بد کاری کے طور پر پھر ان عورتوں میں سے جن کو تم کام میں لائے، ان کو ان کا طے شدہ مہر دے دو اورمہر کے ٹھہرانے کے بعد جو تم نے آپس میں راضی نامہ کیا ہو توا س میں کوئی گناہ نہیں بے شک اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,25,'4:25','اور تم میں سے جو شخص قدرت نہ رکھتا ہو کہ آزاد مسلمان عورتوں سے نکاح کرسکے تو اس کو چاهيے کہ وہ تمھاری ان کنیزوں میں سے کسی کے ساتھ نکاح کرلے جو تمھارے قبضہ میں ہوں اور مو منہ ہوں اللہ تمھارے ایمان کو خوب جانتا ہے، تم آپس میں ایک ہو پس ان کے مالکوں کی اجازت سے ان سے نکاح کرلو اور معروف طریقہ سے ان کے مہر ادا کردو، اس طرح کہ وہ قید نکاح میں لائی جائیں ، نہ کہ آزاد شہوت رانی کریں اور چوری چھپے آشنائیاں کریں پھر جب وہ قید نکاح میں آجائیں اور اس کے بعد وہ بدکاری کی مرتکب ہوں تو آزاد عورتوں کے ليے جو سزا ہے اس کی نصف سزا ان پر ہے یہ اس کے ليے ہے جو تم میں سے بدکاری کا اندیشہ رکھتا ہو اور اگر تم ضبط سے کام لو تو یہ تمھارے ليے زیادہ بہتر ہے، اور اللہ بخشنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,26,'4:26','اللہ چاہتا ہے کہ وہ تمھارے واسطے بیان کرے اور تمھیں ان لوگوں کے طریقوں کی ہدایت دے جو تم سے پہلے گزر چکے ہیں اور تم پر توجہ کرے، اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,27,'4:27','اور اللہ چاہتا ہے کہ وہ تمھارے اوپر توجہ کرے اور جو لوگ اپنی خواہشات کی پیروی کر رہے ہیں ، وہ چاہتے ہیں کہ تم راہ راست سے بہت دور نکل جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(4,28,'4:28','اللہ چاہتا ہے کہ تم سے بوجھ کو ہلکاکرے، اور انسان کمزور بنایا گیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,29,'4:29','اے ایمان والو، آپس میں ایک دوسرے کا مال ناحق طور پر نہ کھاؤ مگر یہ کہ تجارت ہو آپس کی خوشی سےاور خون نہ کرو آپس میں بے شک اللہ تمھارے اوپر بڑا مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,30,'4:30','اور جو شخص سرکشی اور ظلم سے ایسا کرے گا، اس کو ہم ضرور آگ میں ڈالیں گے اور یہ اللہ کے ليے آسان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,31,'4:31','اگر تم ان بڑے گناہوں سے بچتے رہے جن سے تمھیں منع کیا گیا ہے تو ہم تمھاری چھوٹی برائیوں کو معاف کردیں گے اور تم کو عزت کی جگہ داخل کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,32,'4:32','اور تم ایسی چیز کی تمنا نہ کرو جس میں اللہ نے تم میں سے ایک کو دوسرے پر بڑائی دی ہےمردوں کے ليے حصہ ہے اپنی کمائی کا اور عورتوں کے ليے حصہ ہے اپنی کمائی کا اور اللہ سے اس کا فضل مانگوبے شک اللہ ہر چیز کا علم رکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,33,'4:33','اور ہم نے والدین اور قرابت مندوں کے چھوڑے ہوئے میں سے ہر ایک کے ليے وارث ٹھہرادئے ہیں اور جن سے تم نے عہد باندھ رکھا ہو تو ان کو ان کاحصہ دے دو، بے شک اللہ کے روبرو ہے ہرچیز');
INSERT INTO "ur_wahid_uddin" VALUES(4,34,'4:34','مرد، عورتوں کے اوپر قوام ہیں اس بنا پر کہ اللہ نے ایک کو دوسرے پر بڑائی دی ہے اور اس بنا پر کہ مرد نے اپنے مال خرچ كيے پس جو نیک عورتیں ہیں وہ فرماں برداری کرنے والی، پیٹھ پیچھے نگہبانی کرتی ہیں اللہ کی حفاظت سے اور جن عورتوں سے تم کو سرکشی کا اندیشہ ہو، ان کو سمجھاؤ اور ان کو ان کے بستر میں تنہا چھوڑ دو اور ان کو سزا دو پس اگر وہ تمھاری اطاعت کریں تو ان کے خلاف الزام کی راہ نہ تلاش کرو بے شک اللہ سب سے اوپر ہے، بہت بڑا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,35,'4:35','اور اگرتمھیں میاں بیوی کے درمیان تعلقات بگڑنے کااندیشہ ہو تو ایک منصف، مرد کے رشتہ داروں میں سے کھڑا کرو اورایک منصف، عورت کے رشتہ داروں میں سے کھڑا کرو اگر دونوں اصلاح چاہیں گے تو اللہ ان کے درمیان موافقت کر دے گا بے شک اللہ سب کچھ جاننے والا، خبردار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,36,'4:36','اور اللہ کی عبادت کرو اور کسی چیز کو اس کا شریک نہ بناؤ اور اچھا سلوک کرو ماں باپ کے ساتھ اور قرابت داروں کے ساتھ اور یتیموں اور مسکینوں اور قرابت دار پڑوسی اور اجنبی پڑوسی اور پاس بیٹھنے والے اور مسافر کے ساتھ اور مملوک کے ساتھ بے شک اللہ پسند نہیں کرتا اترانے والے، بڑائی کرنے والے کو');
INSERT INTO "ur_wahid_uddin" VALUES(4,37,'4:37','جو کہ بخل کرتے ہیں اور دوسروں کو بھی بخل سکھاتے ہیں اور جو کچھ انھیں اللہ نے اپنے فضل سے دے رکھا ہے اس کو چھپاتے ہیں اور ہم نے منکروں کے ليے ذلت کا عذاب تیار کر رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,38,'4:38','اور جو لوگ اپنا مال لوگوں کو دکھانے کے ليے خرچ کرتے ہیں اور اللہ پر اور آخرت کے دن پر ایمان نہیں رکھتے، اور جس کا ساتھی شیطا ن بن جائے تو وہ بہت برا ساتھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,39,'4:39','ان کا کیا نقصان تھا اگر وہ اللہ پر اور آخرت کے دن پر ایمان لاتے اور اللہ نے جو کچھ انھیں دے رکھا ہے اس میں سے خرچ کرتے اور اللہ ان سے اچھی طرح باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,40,'4:40','بے شک اللہ ذرا بھی کسی کی حق تلفی نہیں کرے گا اگر نیکی ہو تو وہ اس کو دگنا بڑھا دیتا ہے اوراپنے پاس سے بہت بڑا ثواب دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,41,'4:41','پھر اس وقت کیا حال ہوگا جب ہم ہر امت میں سے ایک گواہ لائیں گے اور تم کو ان لوگوں کے اوپر گواہ بناکر کھڑا کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,42,'4:42','وہ لوگ جنھوں نے انکار کیا اور پیغمبر کی نافرمانی کی، وہ اس روز تمنا کریں گے کہ کاش زمین ان پر برابر کردی جائے، اور وہ اللہ سے کوئی بات نہ چھپاسکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,43,'4:43','اے ایمان والو، نزدیک نہ جاؤ نماز کے جس وقت کہ تم نشہ میں ہو یہاں تک کہ سمجھنے لگو جو تم کہتے ہو، اورنہ اس وقت جب کہ غسل کی حاجت ہو مگر سفر كرتے ہوئے، یہاں تک کہ غسل کرلو اور اگر تم مریض ہو یا سفر میں ہو یا تم میں سے کوئی جائے حاجت سے آيا هو یا تم عورتوں کے پاس گئے ہو پھر تم کو پانی نہ ملے تو تم پاک مٹی سے تیمم کرلو اور اپنے چہرہ اور ہاتھوں کا مسح کرلو، بے شک اللہ معاف کرنے والا، بخشنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,44,'4:44','کیا تم نے ان لوگوں کو نہیں دیکھا جن کو کتاب سے حصہ ملا تھا وہ گمراہی کومول لے رہے ہیں اور چاہتے ہیں کہ تم بھی راہ سے بھٹک جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(4,45,'4:45','اللہ تمھارے دشمنوں کو خوب جانتا ہے اور اللہ کافی ہے حمایت کے ليے اور اللہ کافی ہے مدد کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(4,46,'4:46','یہود میں سے ایک گروہ بات کو اس کے محل سے ہٹادیتا ہے اور کہتا ہے کہ ہم نے سنا اور نہ مانا اور کہتے ہیں کہ سنو اور تمہیں سنوایا نہ جائے وہ اپنی زبان کوموڑ کر کہتے ہیں راعنا، دین میں عیب لگانے کے ليے اور اگر وہ کہتے کہ ہم نے سنا اور مانا، اور سنو اور ہم پر نظر کرو تو یہ ان کے حق میں زیادہ بہتر اور درست ہوتا، مگر اللہ نے ان کے انکار کے سبب سے ان پر لعنت کردی ہے پس وہ ایمان نہ لائیں گے مگر بہت کم');
INSERT INTO "ur_wahid_uddin" VALUES(4,47,'4:47','اے وہ لوگو جن کو کتاب دی گئی اس پر ایمان لاؤ جو ہم نے اتارا ہے، تصدیق کرنے والی اس کتاب کی جو تمھارے پاس ہے، اس سے پہلے کہ ہم چہروں کو مٹادیں پھر ان کو الٹ دیں پیٹھ کی طرف یا ان پر لعنت کریں جیسے ہم نے لعنت کی سبت والوں پر اور اللہ کا حکم پورا ہوکر رہتاہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,48,'4:48','بے شک اللہ اس کو نہیں بخشے گا کہ اس کے ساتھ شرک کیا جائے لیکن اس کے علاوہ جو کچھ ہے، اس کو وہ جس کے ليے چاہے گا بخش دے گا اور جس نے اللہ کا شریک ٹھہرایا، اس نے بڑا طوفان باندھا');
INSERT INTO "ur_wahid_uddin" VALUES(4,49,'4:49','کیا تم نے دیکھا ان کو جو اپنے آپ کو پاکیزہ کہتے ہیں بلکہ اللہ ہی پاک کرتا ہے جس کو چاہتا ہے، اور ان پر ذرا بھی ظلم نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(4,50,'4:50','دیکھو، یہ اللہ پر کیسا جھوٹ باندھ رہے ہیں اور صریح گناہ ہونے کے ليے یہی کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,51,'4:51','کیا تم نے ان لوگوں کو نہیں دیکھاجنھیں کتاب سے حصہ ملا تھا، وہ جبت اور طاغوت کومانتے ہیں اور منکروں کے متعلق کہتے ہیں کہ وہ مسلمانوں سے زیادہ صحیح راستہ پر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,52,'4:52','یہی لوگ ہیں جن پر اللہ نے لعنت کی ہے اور جس پر اللہ لعنت کرے ،تم اس کا کوئی مدد گار نہ پاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,53,'4:53','کیا خدا کے اقتدار میں کچھ ان کا بھی دخل ہے، پھر تو یہ لوگوں کو ایک تِل برابر بھی نہ دیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,54,'4:54','کیا یہ لوگوں پر حسد کر رہے ہیں اس بنا پر جو اللہ نے ان کواپنے فضل سے دیا ہے پس ہم نے آل ابراہیم کو کتاب اور حکمت دی ہے اور ہم نے ان کو ایک بڑی سلطنت بھی دے دی');
INSERT INTO "ur_wahid_uddin" VALUES(4,55,'4:55','ان میں سے کسی نے اس کو مانا اور کوئی اس سے رکا رہا اور ایسوں کے ليے جہنم کی بھڑکتی ہوئی آگ کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,56,'4:56','بے شک جن لوگوں نے ہماری نشانیوں کا انکار کیا، ان کو ہم سخت آگ میں ڈالیں گے جب ان کے جسم کی کھال جل جائے گی تو ہم ان کی کھال کو بدل کر دوسری کردیں گے تاکہ وہ عذاب چکھتے رہیں بے شک اللہ زبردست ہے، حکمت والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,57,'4:57','اور جو لوگ ایمان لائے اور نیک کام کيے، ان کو ہم باغوں میں داخل کریں گے جس کے نیچے نہریں بہتی ہوں گی، اس میں وہ ہمیشہ رہیں گے، وہاں ان کے ليے ستھری بیویاں ہوں گی اور ان کوہم گھنی چھاؤں میں رکھیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,58,'4:58','اللہ تم کو حکم دیتا ہے کہ امانتیں ان کے حق داروں کو پہنچادو اور جب لوگوں کے درمیان فیصلہ کرو تو انصاف کے ساتھ فیصلہ کرو اللہ اچھی نصیحت کرتا ہے تم کو، بے شک اللہ سننے والا، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,59,'4:59','اے ایمان والو، اللہ کی اطاعت کرو اور رسول کی اطاعت کرو اور اپنے میں اہلِ اختیار کی اطاعت کرو پھر اگر تمھارے درمیان کسی چیزمیں اختلاف ہوجائے تو اس کو اللہ اور رسول کی طرف لوٹاؤ، اگر تم اللہ پر اور آخرت کے دن پر ایمان رکھتے ہو یہ بات اچھی ہے اور اس کا انجام بہتر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,60,'4:60','کیا تم نے ان لوگوں کونہیں دیکھا جو دعویٰ کرتے ہیں کہ وہ ایمان لائے ہیں اس پر جو اتارا گیا ہے تمھاری طرف اور جو اتارا گیا ہے تم سے پہلے، وہ چاہتے ہیں کہ مقدمه كو لے جائیں شیطان کی طرف، حالاں کہ ان کو حکم ہوچکا ہے کہ اس کو نہ مانیں اور شیطان چاہتا ہے کہ ان کو بہکاکر بہت دور ڈال دے');
INSERT INTO "ur_wahid_uddin" VALUES(4,61,'4:61','اور جب ان سے کہا جاتا ہے کہ آؤ اللہ کی اتاری ہوئی کتاب کی طرف اور رسول کی طرف تو تم دیکھو گے کہ منافقین تم سے کتراجاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,62,'4:62','پھر اس وقت کیا ہوگا جب ان کے اپنے ہاتھوں کی لائی ہوئی مصیبت ان پر پہنچے گی، اس وقت یہ تمھارے پاس قسمیں کھاتے ہوے آئیں گے کہ خدا کی قسم، ہم کو تو صرف بھلائی اور ملاپ سے غرض تھی');
INSERT INTO "ur_wahid_uddin" VALUES(4,63,'4:63','ان کے دلوں میں جو کچھ ہے اللہ اس سے خوب واقف ہے پس تم ان سے اعراض کرو اور ان کو نصیحت کرو اور ان سے ایسی بات کہو جو ان کے دلوں میں اترجائے');
INSERT INTO "ur_wahid_uddin" VALUES(4,64,'4:64','اور ہم نے جو رسول بھیجا، اسی ليے بھیجا کہ اللہ کے حکم سے اس کی اطاعت کی جائے اور اگر وہ جب کہ انھوں نے اپنا براکیا تھا، وہ تمھارے پاس آتے اور اللہ سے معافی چاہتے اور رسول بھی ان کے ليے معافی چاہتا تو یقینا وہ اللہ کو بخشنے والا، رحم کرنے والا پاتے');
INSERT INTO "ur_wahid_uddin" VALUES(4,65,'4:65','پس تیرے رب کی قسم، وہ کبھی ایمان والے نہیں ہوسکتے جب تک وہ اپنے باہمی جھگڑے میں تم کو فیصلہ کرنے والا نہ مان لیں پھر جو فیصلہ تم کرو، اس پر وہ اپنے دلوں میں کوئی تنگی نہ پائیں اور اس کو خوشی سے قبول کرلیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,66,'4:66','اور اگر ہم ان کو حکم دیتے کہ اپنے آپ کو ہلاک کرو یا اپنے گھروں سے نکلو تو ان میں سے تھوڑے ہی اس پر عمل کرتےاور یہ لوگ وہ کرتے جس کی انھیں نصیحت کی جاتی ہے تو ان کے ليے یہ بات بہتر اور ایمان پر ثابت رکھنے والی ہوتی');
INSERT INTO "ur_wahid_uddin" VALUES(4,67,'4:67','اور اس وقت ہم ان کو اپنے پاس سے بڑا اجر دیتے');
INSERT INTO "ur_wahid_uddin" VALUES(4,68,'4:68','اور ان کو سیدھا راستہ دکھاتے');
INSERT INTO "ur_wahid_uddin" VALUES(4,69,'4:69','اور جو اللہ اور رسول کی اطاعت کرے گا، وہ ان لوگوں کے ساتھ ہوگا جن پر اللہ نے انعام کیا، یعنی پیغمبر اور صدیق اور شہید اور صالح کیسی اچھی ہے ان کی رفاقت');
INSERT INTO "ur_wahid_uddin" VALUES(4,70,'4:70','یہ فضل ہے اللہ کی طرف سے اور اللہ کا علم کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,71,'4:71','اے ایمان والو، اپنی احتیاط کرلو پھر نکلو جدا جدا یا اکھٹے ہوکر');
INSERT INTO "ur_wahid_uddin" VALUES(4,72,'4:72','اور تم میں کوئی ایسا بھی ہے جو دیر لگا دیتاہے پھر اگر تم کو کوئی مصیبت پہنچے تو وہ کہتا ہے کہ اللہ نے مجھ پر انعام کیا کہ میں ان کے ساتھ نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(4,73,'4:73','اور اگر تم کو اللہ کا کوئی فضل حاصل ہو تو کہتا ہے، گویا تمھارے اور اس کے درمیان کوئی رشتۂ محبت ہی نہیں ، کہ کاش میں بھی ان کے ساتھ ہوتا تو بڑی کامیابی حاصل کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(4,74,'4:74','پس چاہيے کہ لڑیں اللہ کی راہ میں وہ لوگ جو آخرت کے بدلے دنیا کی زندگی کو بیچ دیتے ہیں اور جو شخص اللہ کی راہ میں لڑے، پھر مارا جائے یا غالب ہو تو ہم اس کو بڑا اجر دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,75,'4:75','اور تم کو کیا ہوا کہ تم نہیں لڑتے اللہ کی راہ میں اور ان کمزور مردوں اور عورتوں اور بچوں کے ليے جوکہتے ہیں کہ خدا یا، ہم کو اس بستی سے نکال جس کے باشندے ظالم ہیں اور ہمارے ليے اپنے پاس سے کوئی حمایتی پیدا کردے اور ہمارے ليے اپنے پاس سے کوئی مدد گار کھڑا کردے');
INSERT INTO "ur_wahid_uddin" VALUES(4,76,'4:76','جو لوگ ایمان والے ہیں ، وہ اللہ کی راہ میں لڑتے ہیں اور جو منکر ہیں ، وہ شیطان کی راہ میں لڑتے ہیں پس تم شیطان کے ساتھیوں سے لڑو بے شک شیطان کی چال بہت کمزور ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,77,'4:77','کیا تم نے ان لوگوں کو نہیں دیکھا جن سے کہا گیا تھا کہ اپنے ہاتھ روکے رکھو اور نماز قائم کرو اور زکوٰۃ دو پھر جب ان کو لڑائی کا حکم دیا گیا تو ان میں سے ایک گروہ انسانوں سے ایسا ڈرنے لگا جیسے اللہ سے ڈرنا چاہيے یا اس سے بھی زیادہ وہ کہتے ہیں اے ہمارے رب، تو نے ہم پر لڑائی کیوں فرض کردی کیوں نہ چھوڑے رکھا ہم کو تھوڑی مدت تک کہہ دو کہ دنیا کا فائدہ تھوڑا ہے اور آخرت بہتر ہے اس کے ليے جو پرہیز گاری کرے، اور تمھارے ساتھ ذرا بھی ظلم نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(4,78,'4:78','اور تم جہاں بھی ہوگے، موت تم کو پالے گی، اگرچہ تم مضبوط قلعوں میں ہو اگر ان کو کوئی بھلائی پہنچتی ہے تووہ کہتے ہیں کہ یہ خدا کی طرف سے ہے اور اگر ان کو کوئی برائی پہنچتی ہے تو وہ کہتے ہیں کہ یہ تمھارے سبب سے ہے کہہ دو کہ سب کچھ اللہ کی طرف سے ہے ان لوگوں کا کیا حا ل ہے کہ لگتا ہے کہ کوئی بات ہی نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(4,79,'4:79','تم کو جو بھلائی بھی پہنچتی ہے، وہ خدا کی طرف سے پہنچتی ہے اور تم کو جو برائی پہنچتی ہے، وہ تمھارے اپنے ہی سبب سے ہے اور ہم نے تم کوانسانوں کی طرف پیغمبر بناکر بھیجا ہے اور اللہ کی گواہی کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,80,'4:80','جس نے رسول کی اطاعت کی، اس نے اللہ کی اطاعت کی اور جو الٹا پھرا تو ہم نے ان پر تم کو نگراں بناکر نہیں بھیجا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,81,'4:81','اور یہ لوگ کہتے ہیں کہ ہم کو قبول ہے پھر جب تمھارے پاس سے نکلتے ہیں تو ان میں سے ایک گروہ اس کے خلاف مشورہ کرتا ہے جو وہ کہہ چکا تھا اور اللہ ان کی سرگوشیوں کو لکھ رہا ہے پس تم ان سے اعراض کرو اور اللہ پر بھروسہ رکھو، اور اللہ بھروسہ کے ليے کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,82,'4:82','کیا یہ لوگ قرآن پر غور نہیں کرتے، اگر یہ اللہ کے سوا کسی اور کی طرف سے ہوتا تو وہ اس کے اندر بڑا اختلاف پاتے');
INSERT INTO "ur_wahid_uddin" VALUES(4,83,'4:83','اور جب ان کو کوئی بات امن یا خوف کی پہنچتی ہے تو وہ اس کو پھیلادیتے ہیں اور اگر وہ اس کو رسول تک یا اپنے ذمہ دار اصحاب تک پہنچاتے تو ان میں سے جو لوگ تحقیق کرنے والے ہیں ، وہ اس کی حقیقت جان لیتے اور اگر تم پر اللہ کا فضل اور اس کی رحمت نہ ہوتی تو تھوڑے لوگوں کے سوا تم سب شیطان کے پیچھے لگ جاتے');
INSERT INTO "ur_wahid_uddin" VALUES(4,84,'4:84','پس لڑو اللہ کی راہ میں تم پر اپنی جان کے سواکسی کی ذمہ داری نہیں اور اہلِ ایمان کو ابھارو امید ہے کہ اللہ منکروں کا زور توڑ دے اور اللہ بڑا زور والا اور بہت سخت سزا دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,85,'4:85','جو شخص کسی اچھی بات کے حق میں کہے گا، اس کے ليے اس میں سے حصہ ہے، اور جو اس کی مخالفت میں کہے گا، اس کے ليے اس میں حصہ ہے اوراللہ ہر چیز پر قدرت رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,86,'4:86','اور جب کوئی تم کو دعا دے تو تم بھی دعا دو اس سے بہتر یا الٹ کر وہی کہہ دو، بے شک اللہ ہر چیز کا حساب لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,87,'4:87','اللہ ہی معبود ہے اس کے سوا کوئی معبود نہیں وہ تم سب کو قیامت کے دن جمع کرے گاجس کے آنے میں کوئی شبہ نہیں اور اللہ کی بات سے بڑھ کر سچی بات اور کس کی ہوسکتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,88,'4:88','پھر تم کو کیا ہوا ہے کہ تم منافقوں کے معاملہ میں دو گروہ ہو رہے ہو حالاں کہ اللہ نے ان کے اعمال کے سبب سے ان کو الٹا پھیر دیاہے کیا تم چاہتے ہو کہ ان کو راہ پر لاؤ جن کواللہ نے گمراہ کردیا ہے اور جس کو اللہ گمراہ کردے تم ہر گز اس کے ليے کوئی راہ نہیں پاسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(4,89,'4:89','وہ چاہتے ہیں کہ جس طرح انھوں نے انکارکیا ہے، تم بھی انکار کرو تاکہ تم سب برابر ہوجاؤ پس تم ان میں سے کسی کو دوست نہ بناؤ جب تک وہ اللہ کی راہ میں ہجرت نہ کریں پھر اگر وہ اس کو قبول نہ کریں تو ان کو پکڑو اور جہاں کہیں ان کو پاؤ، انھیں قتل کرو اور ان میں سے کسی کو ساتھی اور مدد گار نہ بناؤ');
INSERT INTO "ur_wahid_uddin" VALUES(4,90,'4:90','مگر وہ لوگ جن کا تعلق کسی ایسی قوم سے ہو جن کے ساتھ تمھارا معاہدہ ہے، یا وہ لوگ جو تمھارے پاس اس حال میں آئیں کہ ان کے سینے تنگ ہورہے ہیں تمھاری لڑائی سے اور اپنی قوم کی لڑائی سے اور اگر اللہ چاہتا تو ان کو تم پر زور دے دیتا تو وہ ضرور تم سے لڑتے پس اگر وہ تم کو چھوڑے رہیں اور تم سے جنگ نہ کریں اور تمھارے ساتھ صلح کا رویہ رکھیں تو اللہ تم کو بھی ان کے خلاف کسی اقدام کی اجازت نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(4,91,'4:91','دوسرے کچھ ایسے لوگوں کو بھی تم پاؤ گے جو چاہتے ہیں کہ تم سے بھی امن میں رہیں اور اپنی قوم سے بھی امن میں رہیں جب کبھی وہ فتنہ کا موقع پائیں ، وہ اس میں کود پڑتے ہیں ایسے لوگ اگر تم سے یکسو نہ رہیں اور تمھارے ساتھ صلح کا رویہ نہ رکھیں اور اپنے ہاتھ نہ روکیں تو تم ان کو پکڑو اور ان کو قتل کرو جہاں کہیں پاؤ یہ لوگ ہیں جن کے خلاف ہم نے تم کوکھلی حجت دی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,92,'4:92','اور مومن کا کام نہیں کہ وہ مومن کو قتل کرے مگر یہ کہ غلطی سے ایسا ہوجائےاور جو شخص کسی مومن کو غلطی سے قتل کردے تو وہ ایک مومن غلام کو آزاد کرے اور مقتول کے وارثوں کو خوں بہادے، الاّ یہ کہ وہ معاف کردیں پھر مقتول اگر ایسی قوم میں سے تھا جو تمھاری دشمن ہے اور وہ خود مومن تھا تو وہ ایک مومن غلام کو آزاد کرے اور اگر وہ ایسی قوم سے تھا کہ تمھارے اور اس کے درمیان عہد ہے تو وہ اس کے وارثوں کو خوں بہادے اورایک مومن کو آزاد کرے پھر جس کو میسر نہ ہو تو وہ لگاتار دو مہینے کے روزے رکھے یہ توبہ ہے اللہ کی طرف سے اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,93,'4:93','اور جو شخص کسی مومن کوجان کر قتل کرے تو اس کی سزاجہنم ہے جس میں وہ ہمیشہ رہے گا اور اس پر اللہ کا غضب اوراس کی لعنت ہے اور اللہ نے اس کے ليے بڑا عذاب تیار کر رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,94,'4:94','اے ایمان والو، جب تم سفر کرو اللہ کی راہ میں تو خوب تحقیق کرلیا کرو اورجو شخص تم کو سلام کرے اس کو یہ نہ کہو کہ تو مومن نہیں تم دینوی زندگی کا سامان چاہتے ہو تو اللہ کے پاس بہت سامانِ غنیمت ہے تم بھی پہلے ایسے ہی تھے پھر اللہ نے تم پر فضل کیا تو تحقیق کرلیا کرو جو کچھ تم کرتے ہو اللہ اس سے خبردار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,95,'4:95','برابر نہیں ہوسکتے بیٹھے رہنے والے اہلِ ایمان جن کو کوئی عذر نہیں اور وہ اہلِ ایمان جواللہ کی راہ میں لڑنے والے ہیں اپنے مال اور اپنی جان سے مال و جان سے جہاد کرنے والوں کا درجہ اللہ نے بیٹھے رہنے والوں کی نسبت بڑا رکھا ہے، اور ہرایک سے اللہ نے بھلائی کا وعدہ کیا ہے اور اللہ نے جہاد کرنے والوں کو بیٹھے رہنے والوں پر اجر عظیم میں برتری دی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,96,'4:96','ان کے ليے اللہ کی طرف سے بڑے درجے ہیں اور مغفرت اور رحمت ہے اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,97,'4:97','جو لوگ اپنا برا کر رہے ہیں ، جب ان کی جان فرشتے نکالیں گے تو وہ ان سے پوچھیں گے کہ تم کس حال میں تھے وہ کہیں گے کہ ہم زمین میں بے بس تھے فرشتے کہیں گے کیا خدا کی زمین کشادہ نہ تھی کہ تم وطن چھوڑ کر وہاں چلے جاتے یہ وہ لوگ ہیں جن کا ٹھکانا جہنم ہے اور وہ بہت برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,98,'4:98','مگر وہ بے بس مرد اور عورتیں اور بچے جوکوئی تدبیر نہیں کرسکتے اور نہ کوئی راہ پا رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,99,'4:99','یہ لوگ، اميد ہے کہ اللہ انھیں معاف کردے گا اور اللہ معاف کرنے والا، بخشنے والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,100,'4:100','اور جوکوئی اللہ کی راہ میں وطن چھوڑے گا، وہ زمین میں بڑے ٹھکانے اور بڑی وسعت پائے گا اور جو شخص اپنے گھر سے اللہ اور اس کے رسول کی طرف ہجرت کرکے نکلے، پھر اس کو موت آجائے تو اس کا اجر اللہ کے یہاں مقرر ہوچکا اور اللہ بخشنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,101,'4:101','اور جب تم زمین میں سفر کرو تو تم پر کوئی گناہ نہیں کہ تم نماز میں قصرکرو، اگر تم کو ڈر ہو کہ منکر تم کو ستائیں گے بے شک منکر لوگ تمھارے کھلے ہوئے دشمن ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,102,'4:102','اور جب تم اہلِ ایمان کے درمیان ہو اور ان کے ليے نماز قائم کرو تو چاہيے کہ ان کی ایک جماعت تمھارے ساتھ کھڑی ہو اور وہ اپنے ہتھیارليے ہوئے ہو پس جب وہ سجدہ کرچکیں تو وہ تمھارے پاس سے ہٹ جائیں اور دوسری جماعت آئے جس نے ابھی نماز نہیں پڑھی ہے اور وہ تمھارے ساتھ نماز پڑھیں اور وہ بھی اپنے بچاؤ کا سامان اور ہتھیار ليے رہیں منکر لوگ چاہتے ہیں کہ تم اپنے ہتھیاروں اور سامان سے کسی طرح غافل ہوجاؤ تو وہ تم پر یکبارگی ٹوٹ پڑیں اور تمھارے اوپر کوئی گناہ نہیں اگر تم کو بارش کے سبب سے تکلیف ہو یا تم بیمار ہو تو اپنے ہتھیار اتار دو اور اپنے بچاؤ کا سامان ليے رہو بے شک اللہ نے منکروں کے ليے رسوا کرنے والا عذاب تیار کر رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,103,'4:103','پس جب تم نماز ادا کرلو تو اللہ کو یاد کرو کھڑے اور بیٹھے اور لیٹے پھر جب اطمینان ہوجائے تو نماز کی اقامت کرو بے شک نماز اہل ایمان پر مقرر وقتوں کے ساتھ فرض ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,104,'4:104','اور قوم کا پیچھا کرنے سے ہمت نہ ہارو اگر تم دکھ اٹھاتے ہو تو وہ بھی تمھاری طرح دکھ اٹھاتے ہیں اور تم اللہ سے وہ امید رکھتے ہو جو امید وہ نہیں رکھتے اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,105,'4:105','بے شک ہم نے یہ کتاب تمھاری طرف حق کے ساتھ اتاری ہے، تاکہ تم لوگوں کے درمیان اس کے مطابق فیصلہ کرو جواللہ نے تم کو دکھایا ہے اور بد دیانت لوگوں کی طرف سے جھگڑنے والے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(4,106,'4:106','اور اللہ سے بخشش مانگو بے شک اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,107,'4:107','اور تم ان لوگوں کی طرف سے نہ جھگڑو جو اپنے آپ سے خیانت کر رہے ہیں اللہ ایسے شخص کو پسند نہیں کرتا جو خیانت والا اور گنہ گارہو');
INSERT INTO "ur_wahid_uddin" VALUES(4,108,'4:108','وہ انسانوں سے شرماتے ہیں اور اللہ سے نہیں شرماتے حالاں کہ وہ ان کے ساتھ ہوتا ہے جب کہ وہ سرگوشیاں کرتے ہیں اس بات کی جس سے اللہ راضی نہیں اور جو کچھ وہ کرتے ہیں اللہ اس کا احاطہ کيے ہوئے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,109,'4:109','تم لوگوں نے دنیا کی زندگی میں تو ان کی طرف سے جھگڑا کرلیامگر قیامت کے دن کون ان کے بدلے اللہ سے جھگڑا کرے گا یا کون ہوگا ان کا کام بنانے والا');
INSERT INTO "ur_wahid_uddin" VALUES(4,110,'4:110','اورجو شخص برائی کرے یا اپنے آپ پر ظلم کرے پھر اللہ سے بخشش مانگے تو وہ اللہ کو بخشنے والا، رحم کرنے والا پائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(4,111,'4:111','اور جو شخص کوئی گناہ کرتا ہے تو وہ اپنے ہی حق میں کرتا ہے اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,112,'4:112','اور جو شخص کوئی غلطی یا گناہ کرے پھر اس کی تہمت کسی بے گناہ پر لگادے تو اس نے ایک بڑا بہتان اور کھلا ہوا گناہ اپنے سر لے لیا');
INSERT INTO "ur_wahid_uddin" VALUES(4,113,'4:113','اور اگر تم پر اللہ کا فضل اور اس کی رحمت نہ ہوتی تو ان میں سے ایک گروہ نے تو یہ ٹھان ہی لیا تھا کہ وہ تم کو بہکا کر رہے گا حالاں کہ وہ اپنے آپ کو بہکا رہے ہیں وہ تمھارا کچھ بگاڑ نہیں سکتے اور اللہ نے تم پرکتاب اور حکمت اتاری ہے اور تم کو وہ چیز سکھائی ہے جس کو تم نہیں جانتے تھے اوراللہ کا فضل ہے تم پر بہت بڑا');
INSERT INTO "ur_wahid_uddin" VALUES(4,114,'4:114','ان کی اکثر سرگوشیوں میں کوئی بھلائی نہیں بھلائی والی سرگوشی صرف اس کی ہے جو صدقہ کرنے کو کہے یا کسی نیک کام کے ليے کہے یا لوگوں میں صلح کرانے کے ليے کہے جو شخص اللہ کی خوشی کے ليے ایساکرے تو ہم اس کوبڑا اجر عطا کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,115,'4:115','مگر جو شخص رسول کی مخالفت کرے گا اور مومنین کے راستہ کے سوا کسی اور راستہ پر چلے گا، حالاں کہ اس پر راہ واضح ہوچکی تو اس کو ہم اسی طرف چلائیں گے جدھر وہ خود پھر گیا اور اس کو جہنم میں داخل کریں گے اور وہ برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,116,'4:116','بے شک اللہ اس کو نہیں بخشے گا کہ اس کا شریک ٹھہرایا جائے اور اس کے سوا دوسرے گنا ہوں کو بخش دے گا جس کے ليے چاہے گا اور جس نے اللہ کا شریک ٹھہرایا وہ بہک کر بہت دور جا پڑا');
INSERT INTO "ur_wahid_uddin" VALUES(4,117,'4:117','وہ اللہ کو چھوڑ کر پکارتے ہیں دیویوں کو اور وہ پکارتے ہیں سرکش شیطان کو');
INSERT INTO "ur_wahid_uddin" VALUES(4,118,'4:118','اس پر اللہ نے لعنت کی ہے اور شیطان نے کہا تھا کہ میں تیرے بندوں سے ایک مقرر حصہ لے کر رہوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(4,119,'4:119','میں ان کو بہکاؤں گا اور ان کو جھوٹي امیدیں دلاؤں گااور ان کو سجھاؤں گا تو وہ چوپایوں کے کان کاٹیں گے اور ان کو سجھاؤں گا تو وہ اللہ کی بناوٹ کو بدلیں گے اور جو شخص اللہ کے سوا شیطان کو اپنا دوست بنائے تو وہ کھلے ہوئے نقصان میں پڑگیا');
INSERT INTO "ur_wahid_uddin" VALUES(4,120,'4:120','وہ ان کو وعدہ دیتا ہے اور ان کو امیدیں دلاتا ہے اور شیطان کے تمام وعدے فریب کے سوا اور کچھ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,121,'4:121','ایسے لوگوں کا ٹھکانا جہنم ہے اور وہ اس سے بچنے کی کوئی راہ نہ پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,122,'4:122','اور جو لوگ ایمان لائے اور انھوں نے نیک کام کيے ان کو ہم ایسے باغوں میں داخل کریں گے جن کے نیچے نہریں جاری ہوں گی،جن ميں وه هميشه رهيں گے اللہ کاو عدہ سچا ہے اور اللہ سے بڑھ کر کون اپنی بات میں سچا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(4,123,'4:123','نہ تمھاری آرزؤوں پر هےاور نہ اہل کتاب کی آرزؤوں پر جو کوئی بھی برا کرے گا، اس کا بدلہ پائے گا اور وہ نہ پائے گا اللہ کے سوا اپنا کوئی حمایتی اور نہ مدد گار');
INSERT INTO "ur_wahid_uddin" VALUES(4,124,'4:124','اور جو شخص کوئی نیک کام کرے گا، خواہ وہ مرد ہو یا عورت بشرطیکہ وہ مومن ہو، تو ایسے لوگ جنت میں داخل ہوں گے اور ان پر ذرا بھی ظلم نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(4,125,'4:125','اور اس سے بہتر کس کا دین ہے جو اپنا چہرہ اللہ کی طرف جھکادے اور وہ نیکی کرنے والا ہو اور وہ چلے دین ابراہیم پر جو يكسو تھا اور اللہ نے ابراہیم کو اپنا دوست بنالیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(4,126,'4:126','اور اللہ کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور اللہ ہر چیز کا احاطہ کيے ہوئے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,127,'4:127','اور لوگ تم سے عورتوں کے بارے میں حکم پوچھتے ہیں کہہ دو اللہ تمھیں ان کے بارے میں حکم دیتا ہے اور وہ آیات بھی جو تمھیں کتاب میں ان یتیم عورتوں کے بارے میں پڑھ کر سنائی جاتی ہیں جن کو تم وہ نہیں دیتے جو ان کے ليے لکھا گیا ہے اور چاہتے ہو کہ ان کو نکاح میں لے آؤ اور جو آیات کمزور بچوں کے بارے میں ہیں ، اور یتیموں کے ساتھ انصاف کرو اور جو بھلائی تم کرو گے وہ اللہ کو خوب معلوم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,128,'4:128','اور اگر کسی عورت کو اپنے شوہر کی طرف سے بدسلوکی یا بے رخی کا اندیشہ ہو تو اس میں کوئی حرج نہیں کہ دونوں آپس میں کوئی صلح کرلیں اور صلح بہتر ہے اورحرص انسان کی طبیعت میں بسی ہوئی ہے اور اگر تم اچھا سلوک کرو اور خدا ترسی سے کام لو تو جو کچھ تم کرو گے، اللہ اس سے باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,129,'4:129','اور تم ہرگز عورتوں کو برابر نہیں رکھ سکتے اگرچہ تم ایسا کرنا چاہو پس بالکل ایک ہی طرف نہ جھک پڑو کہ دوسری کو لٹکی ہوئی کی طرح چھوڑ دو اور اگر تم اصلاح کرلو اور ڈرو تو اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,130,'4:130','اور اگر دونوں جدا ہوجائیں تو اللہ ہر ایک کو اپنی وسعت سے بے احتیاج کردے گا اور اللہ بڑی وسعت والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,131,'4:131','اور اللہ کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور ہم نے حکم دیا ہے ان لوگوں کو جنھیں تم سے پہلے کتاب دی گئی اور تم کو بھی کہ اللہ سے ڈرو اور اگر تم نے نہ مانا تو اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور اللہ بے نیاز ہے، سب خوبیوں والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,132,'4:132','اور اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور بھروسہ کے ليے اللہ کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,133,'4:133','اگر وہ چاہے تو تم سب کو لے جائے اے لوگو، اور دوسرو ں کو لے آئے اور اللہ اس پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,134,'4:134','جو شخص دنیا کا ثواب چاہتا ہو تو اللہ کے پاس دنیا کا ثواب بھی ہے اور آخرت کا ثواب بھی اور اللہ سننے والا، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,135,'4:135','اے ایمان والو، انصاف پر خوب قائم رہنے والے اور اللہ کے ليے گواہی دینے والے بنو، چاہے وہ تمھارے یا تمھارے ماں باپ یا عزیزوں کے خلاف ہواگر کوئی مال دار ہے یا محتاج تو اللہ تم سے زیادہ دونوں کا خیر خواہ ہے پس تم خواہش کی پیروی نہ کرو کہ حق سے ہٹ جاؤ اور اگر تم کجی کرو گے یا پہلو تہی کرو گے تو جو کچھ تم کر رہے ہو، اللہ اس سے باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,136,'4:136','اے ایمان والو، ایمان لاؤ اللہ پر اور اس کے رسول پر اور اس کتاب پر جو اس نے اپنے رسول پر اتاری اور اس کتاب پر جو اس نے پہلے نازل کی اور جو شخص انکار کرے اللہ کا اور اس کے فرشتوں کا اور اس کی کتابوں کا اور اس کے رسولوں کا اور آخرت کے دن کا تووہ بہک کر دور جاپڑا');
INSERT INTO "ur_wahid_uddin" VALUES(4,137,'4:137','بے شک جو لوگ ایمان لائے پھر انکار کیا، پھر ایمان لائے پھر انکار کیا، پھر وہ انکار میں بڑھتے گئے تو اللہ ان کو ہرگز نہیں بخشے گا اور نہ ان کو راہ دکھائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(4,138,'4:138','منافقوں کو خوش خبری دے دو کہ ان کے ليے ایک درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,139,'4:139','وہ لوگ جو مومنوں کو چھوڑ کر منکروں کو دوست بناتے ہیں ، کیا وہ ان کے پاس عزت کی تلاش کر رہے ہیں ، تو عزت ساری اللہ کے ليے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,140,'4:140','اور اللہ کتاب میں تم پر یہ حکم اتار چکا ہے کہ جب تم سنو کہ اللہ کی نشانیوں کا انکار کیا جارہا ہے اور ان کا مذاق کیا جارہا ہے تو تم ان کے ساتھ نہ بیٹھو، یہاں تک کہ وہ دوسری بات میں مشغول ہوجائیں ورنہ تم بھی انھیں جیسے ہوجاؤ گے اللہ منافقوں کو اور منکروں کو جہنم میں ایک جگہ اکھٹا کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,141,'4:141','وہ منافق تمھارے ليے انتظار میں رہتے ہیں اگر تم کو اللہ کی طرف سے کوئی فتح حاصل ہوتی ہے تو وہ کہتے ہیں کہ کیا ہم تمھارے ساتھ نہ تھے اور اگر منکروں کو کوئی حصہ مل جائے تو ان سے کہیں گے کہ کیا ہم تمھارے خلاف لڑنے پر قادر نہ تھے اور پھر بھی ہم نے تم کو مسلمانوں سے بچایا تو اللہ ہی تم لوگوں کے درمیان قیامت کے دن فیصلہ کرے گا اور اللہ ہرگز منکروں کو مومنوں پر کوئی راہ نہیں دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(4,142,'4:142','منافقین اللہ کے ساتھ دھوکہ بازی کر رہے ہیں حالاں کہ اللہ ہی نے ان کو دھوکہ میں ڈال رکھا ہے اور جب وہ نماز کے ليے کھڑے ہوتے ہیں تو کاہلی کے ساتھ کھڑے ہوتے ہیں محض لوگوں کو دکھانے کے ليے اور وہ اللہ کو کم ہی یاد کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,143,'4:143','وہ دونوں کے بیچ لٹک رہے ہیں ، نہ اِدھر ہیں اور نہ اُدھر اور جس کو اللہ بھٹکادے تم اس کے ليے کوئی راہ نہیں پاسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(4,144,'4:144','اے ایمان والو، مومنوں کو چھوڑ کر منکروں کو اپنا دوست نہ بناؤ کیا تم چاہتے ہو کہ اپنے اوپر اللہ کی کھلی حجت قائم کرلو');
INSERT INTO "ur_wahid_uddin" VALUES(4,145,'4:145','بے شک منافقین دوزخ کے سب سے نیچے کے طبقہ میں ہوں گے اور تم ان کا کوئی مدد گار نہ پاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,146,'4:146','البتہ جو لوگ تو بہ کریں اور اپنی اصلاح کرلیں اور اللہ کو مضبوطی سے پکڑلیں اور اپنے دین کو اللہ کے ليے خالص کرلیں تو یہ لوگ ایمان والوں کے ساتھ ہوں گے اور اللہ ایمان والوں کو بڑا ثواب دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(4,147,'4:147','اللہ تم کو عذاب دے کر کیا کرے گا، اگر تم شکر گزاری کرو اور ایمان لاؤ اللہ بڑا قدر داں ہے، سب کچھ جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,148,'4:148','اللہ بد گوئی کو پسند نہیں کرتا مگر یہ کہ کسی پر ظلم ہوا ہو اور اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,149,'4:149','اگر تم بھلائی کو ظاہر کرو یاتم اس کو چھپاؤ یا کسی برائی سے درگزر کرو تو اللہ معاف کرنے والا، قدرت رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,150,'4:150','جو لوگ اللہ اور اس کے رسولوں کا انکار کر رہے ہیں اور چاہتے ہیں کہ اللہ اور اس کے رسولوں کے درمیان تفریق کریں اور کہتے ہیں کہ ہم کسی کو مانیں گے اور کسی کو نہ مانیں گے اور وہ چاہتے ہیں کہ اس کے بیچ میں ایک راہ نکالیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,151,'4:151','ایسے لوگ پکے منکرہیں اور ہم نے منکروں کے ليے ذلت کا عذاب تیار کر رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,152,'4:152','اور جو لوگ اللہ اور اس کے رسولوں پر ایمان لائے اور ان میں سے کسی كے درميان فرق نہ کیا، ان کو اللہ ان کا اجر دے گا اور اللہ غفور اور رحیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,153,'4:153','اہل کتاب تم سے یہ مطالبہ کرتے ہیں کہ تم ان پر آسمان سے ایک کتاب اتار لاؤ پس موسیٰ سے وہ اس سے بھی بڑی چیز کا مطالبہ کرچکے ہیں انھوں نے کہا کہ ہمیں اللہ کو بالکل سامنے دکھادو پس ان کی اس زیادتی کے باعث ان پر بجلی آپڑی پھر کھلی نشانی آجانے کے بعد انھوں نے بچھڑے کو معبود بنالیا پھر ہم نے اس سے درگزر کیا اور موسیٰ کو ہم نے کھلی حجت عطا کی');
INSERT INTO "ur_wahid_uddin" VALUES(4,154,'4:154','اور ہم نے ان کے اوپر کوہِ طور کو اٹھایا ان سے عہد لینے کے واسطے اور ہم نے ان سے کہا کہ دروازے میں داخل ہو سرجھکائے ہوئے اور ان سے کہا کہ َسبْت کے معاملہ میں زیادتی نہ کرنا اور ہم نے ان سے مضبوط عہد لیا');
INSERT INTO "ur_wahid_uddin" VALUES(4,155,'4:155','ان کو جو سزا ملی وہ اس پر کہ انھوں نے اپنے عہد کو توڑا اور اس پر کہ انھوں نے اللہ کی نشانیوں کا انکار کیا اور اس پر کہ انھوں نے پیغمبروں کو ناحق قتل کیا اور ان کے کہنے پر کہ ہمارے دل تو بند ہیں بلکہ اللہ نے ان کے انکار کے سبب سے ان کے دلوں پر مہر کردی ہے تو وہ کم ہی ایمان لاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(4,156,'4:156','اور ان کے انکا رپر اور مریم پر بڑا بهتان باندھنے پر');
INSERT INTO "ur_wahid_uddin" VALUES(4,157,'4:157','اور ان کے اس کہنے پر کہ ہم نے مسیح بن مریم، اللہ کے رسول کو قتل کردیا حالاں کہ انھوں نے نہ اس کو قتل کیا اور نہ سولی دی، بلکہ معاملہ ان کے ليے مشتبہ کردیا گیا اور جو لوگ اس میں اختلاف کر رہے ہیں وہ اس کے بارے میں شک میں پڑے ہوئے ہیں ان کو اس کا کوئی علم نہیں ، وہ صرف اٹکل پر چل رہے ہیں اور بے شک انھوں نے اس کو قتل نہیں کیا');
INSERT INTO "ur_wahid_uddin" VALUES(4,158,'4:158','بلکہ اللہ نے اس کو اپنی طرف اٹھالیا اور اللہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,159,'4:159','اور اہل کتاب میں سے کوئی ایسا نہیں جو اس کی موت سے پہلے اس پر ایمان نہ لے آئے اور قیامت کے دن وہ ان پر گواہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(4,160,'4:160','پس یہود کے ظلم کی وجہ سے ہم نے وہ پاک چیزیں ان پر حرام کردیں جو ان کے ليے حلال تھیں اور اس وجہ سے کہ وہ اللہ کی راہ سے بہت روکتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(4,161,'4:161','اور اس وجہ سے کہ وہ سود لیتے تھے، حالاں کہ اس سے انھیں منع کیا گیا تھا اور اس وجہ سے کہ وہ لوگوں کا مال باطل طریقہ سے کھاتے تھے اور ہم نے ان میں سے منکروں کے ليے درد ناک عذاب تیار کر رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,162,'4:162','مگر ان میں جو لوگ علم میں پختہ اور ایمان والے ہیں ، وہ ایمان لائے ہیں اس پر جو تمھارے اوپر اتاری گئی اور جو تم سے پہلے اتاری گئی اور وہ نماز کے پابند ہیں اور زکوٰۃ ادا کرنے والے ہیں اور اللہ پر اور قیامت کے دن پر ایمان رکھنے والے ہیں ایسے لوگوں کو ہم ضرور بڑا اجر دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(4,163,'4:163','ہم نے تمھاری طرف وحی بھیجی ہے جس طرح ہم نے نوح اور اس کے بعد کے نبیوں کی طرف وحی بھیجی تھی اور ہم نے ابراہیم اور اسماعیل اور اسحاق اور یعقوب اور اولادِ یعقوب اور عیسیٰ اور ایوب اور یونس اور ہارون اور سلیمان کی طرف وحی بھیجی تھی اور ہم نے داؤد کو زبور دی');
INSERT INTO "ur_wahid_uddin" VALUES(4,164,'4:164','اور ہم نے ایسے رسول بھیجے جن کا حال ہم تم کو پہلے سناچکے ہیں اور ایسے رسول بھی جن کا حال ہم نے تم کو نہیں سنایا اور موسیٰ سے اللہ نے کلام کیا');
INSERT INTO "ur_wahid_uddin" VALUES(4,165,'4:165','اللہ نے رسولوں کو خوش خبری دینے والے اور ڈرانے والے بناکر بھیجا، تاکہ رسولوں کے بعد لوگوں کے پاس اللہ کے مقابلہ میں کوئی حجت باقی نہ رہے اوراللہ زبردست ہے، حکمت والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,166,'4:166','مگر اللہ گواہ ہے اس پر جو اس نے تمھارے اوپر اتارا ہے کہ اس نے اس کو اپنے علم کے ساتھ اتارا ہے اور فرشتے بھی گواہی دیتے ہیں اوراللہ گواہی کے ليے کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,167,'4:167','جن لوگوں نے انکار کیا اوراللہ کے راستہ سے روکا، وہ بہک کر بہت دور نکل گئے');
INSERT INTO "ur_wahid_uddin" VALUES(4,168,'4:168','جن لوگوں نے انکار کیااور ظلم کیا، ان کو اللہ ہرگز نہیں بخشے گا اور نہ ہی ان کو کوئی راستہ دکھائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(4,169,'4:169','جہنم کے راستے کے سوا، جس میں وہ ہمیشہ رہیں گے اور اللہ کے ليے یہ آسان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,170,'4:170','اے لوگو، تمھارے پاس رسول آچکا تمھارے رب کی ٹھیک بات لے کر پس مان لو تاکہ تمھارا بھلا ہو اور اگر نہ مانو گے تو اللہ کا ہے جو کچھ آسمانوں میں اور زمین میں ہے اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,171,'4:171','اے اہل کتاب، اپنے دین میں غلو نہ کرو اور اللہ کے بارے میں کوئی بات حق کے سوا نہ کہو مسیح عیسیٰ ابن مریم تو بس اللہ کے ایک رسول اور اس کا ایک کلمہ ہیں جس کو اس نے مریم کی طرف اِلقا فرمایا اور اس کی جانب سے ایک روح ہیں پس اللہ اور اس کے رسولوں پر ایمان لاؤ اور یہ نہ کہو کہ خدا تین ہیں باز آجاؤ، یہی تمھارے حق میں بہتر ہے معبود تو بس ایک اللہ ہی ہے وہ اِس سے پاک ہے کہ اس کے اولاد ہو اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور اللہ ہی کا کارساز ہونا کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(4,172,'4:172','مسیح کو ہرگز اللہ کا بندہ بننے سے عارنہ ہوگا اورنہ مقرب فرشتوں کو عار ہوگا اور جو اللہ کی بندگی سے عار کرے گا اور تکبر کرے گا تو اللہ ضرور سب کو اپنے پاس جمع کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(4,173,'4:173','پھر جو لوگ ایمان لائے اور جنھوں نے نیک کام کيے تو ان کو وہ پورا پورا اجر دے گا اور اپنے فضل سے ان کو مزید بھی دے گا اور جن لوگوں نے عار اور تکبر کیا ہوگا ان کو دردناک عذاب دے گا ور وہ اللہ کے مقابلہ میں نہ کسی کو اپنا دوست پائیں گے اور نہ مدد گار');
INSERT INTO "ur_wahid_uddin" VALUES(4,174,'4:174','اے لوگو، تمھارے پاس تمھارے رب کی طرف سے ایک دلیل آچکی ہے اور ہم نے تمھارے اوپر ایک واضح روشنی اتار دی');
INSERT INTO "ur_wahid_uddin" VALUES(4,175,'4:175','پس جو لوگ اللہ پر ایمان لائے اوراس کو انھوں نے مضبوط پکڑ لیا، ان کو ضرور اللہ اپنی رحمت اور فضل میں داخل کرے گا اور ان کو اپنی طرف سیدھا راستہ دکھائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(4,176,'4:176','لوگ تم سے حکم پوچھتے ہیں کہہ دو اللہ تم کو کَلالَہ (وہ مُورث جس کے بھائی بہن کے علاوہ کوئی اور وارث موجود نہ ہو) کے بارے میں حکم بتاتا ہے اگر کوئی شخص مرجائے اوراس کے کوئی اولاد نہ ہو اور اس کے ایک بہن ہو تو اس کے ليے اس کے ترکہ کا نصف ہے اوروہ مرد اس بہن کا وارث ہوگا، اگر اس بہن کے کوئی اولاد نہ ہو اور اگر دو بہنیں ہوں تو ان کے ليے اس کے ترکہ کا دوتہائی ہوگا اور اگر کئی بھائی بہن مرد عورتیں ہوں تو ایک مرد کے ليے دو عورتوں کے برابر حصہ ہےاللہ تمھارے ليے بیان کرتا ہے، تاکہ تم گمراہ نہ ہو اور اللہ ہر چیزکا جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,1,'5:1','اے ایمان والو، عہد و پیمان کو پورا کرو تمھارے ليے مویشی کی قسم کے سب جانو رحلال کيے گئے، سوا ان کے جن کا ذکر آگے کیاجارہا ہے مگر احرام کی حالت میں شکار کو حلال نہ جانو اللہ حکم دیتا ہے جو چاہتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,2,'5:2','اے ایمان والو، بے حرمتی نہ کرو اللہ کی نشانیوں کی اور نہ حرمت والے مہینوں کی اور نہ حرم میں قربانی والے جانوروں کی اور نہ پٹے بندھے ہوئے نیاز کے جانوروں کی اور نہ حرمت والے گھر کی طرف آنے والوں کی جو اپنے رب کا فضل اور اس کی خوشنودي ڈھونڈنے نکلے ہیں اور جب تم احرام کی حالت سے باہر آجاؤ تو شکار کرو اور کسی قوم کی دشمنی کہ اس نے تم کو مسجد حرام سے روکا ہے، تم کو اس پر نہ ابھارے کہ تم زیادتی کرنے لگو تم نیکی اور تقوی میں ایک دوسرے کی مدد کرو اور گناہ اور زیادتی میں ایک دوسرے کی مدد نہ کرو اللہ سے ڈرو بے شک اللہ سخت عذاب دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,3,'5:3','تم پر حرام کیا گیا مردار اور خون اور سور کا گوشت اور وہ جانور جو خدا کے سوا کسی اور نام پر ذبح کیا گیا ہو اور وہ جو مرگیا ہو گلا گھونٹنے سے یا چوٹ سے یا اونچے سے گر کر یا سینگ مارنے سے اور وہ جس کو درندے نے کھایا ہو مگر جس کو تم نے ذبح کرلیا اور وہ جو کسی تھان پر ذبح کیا گیا ہو اور یہ کہ تقسیم کرو جوئے کے تیروں سے یہ گناہ کا کام ہے آج منکر تمھارے دین کی طرف سے مایوس ہوگئے پس تم ان سے نہ ڈرو، صرف مجھ سے ڈرو آج میں نے تمھارے ليے تمھارے دین کو پورا کردیا اور تم پر اپنی نعمت تمام کردی اور تمھارے ليے اسلام کو دین کی حیثیت سے پسندکرلیا پس جو بھوک سے مجبور ہوجائے، لیکن وہ گناہ پر مائل نہ ہو تو اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,4,'5:4','وہ پوچھتے ہیں کہ ان کے ليے کیا چیز حلال کی گئی ہے کہو کہ تمھارے ليے ستھری چیزیں حلال ہیں اور شکاری جانوروں میں سے جن کو تم نے سدھایا ہے، تم ان کو سکھاتے ہو اس میں سے جو اللہ نے تم کو سکھایا پس تم ان کے شکار میں سے کھاؤ جو وہ تمھارے ليے پکڑ رکھیں اور ان پر اللہ کا نام لو اور اللہ سے ڈرو، اللہ بے شک جلد حساب لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,5,'5:5','آج تمھارے ليے سب ستھری چیزیں حلال کردی گئیں اور اہل کتاب کا کھانا تمھارے ليے حلال ہے اور تمھارا کھانا ان کے ليے حلال ہے اور حلال ہیں تمھارے ليے پاک دامن عورتیں مومن عورتوں میں سے اور پاک دامن عورتیں ان میں سے جن کو تم سے پہلے کتاب دی گئی جب تم انھیں ان کے مہر دے دو اس طرح کہ تم نکاح میں لانے والے ہو، نہ اعلانیہ بدکاری کرو اور نہ خفیہ آشنائی کرو اور جو شخص ایمان کے ساتھ کفر کرے گا تو اس کا عمل ضائع ہوجائے گا اور وہ آخرت میں نقصان اٹھانے والوں میں سے ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(5,6,'5:6','اے ایمان والو، جب تم نماز کے ليے اٹھو تو اپنے چہروں اور اپنے ہاتھوں کو کہنیوں تک دھوؤ اور اپنے سروں کا مسح کرو اوراپنے پیروں کو ٹخنوں تک دھوؤ اور اگر تم حالتِ جنابت میں ہو تو غسل کرلو اور اگر تم مریض ہو یا سفر میں ہو یا تم میں سے کوئی استنجا سے آئے یا تم نے عورت سے صحبت کی ہو پھر تم کو پانی نہ ملے تو پاک مٹی سے تیمم کرلو اور اپنے چہروں اور ہاتھوں پر اس سے مسح کرلو اللہ نہیں چاہتا کہ وہ تم پر کوئی تنگی ڈالےبلکہ وہ چاہتا ہے کہ تم کو پاک کرے اور تم پر اپنی نعمت تمام کرے تاکہ تم شکر گزار بنو');
INSERT INTO "ur_wahid_uddin" VALUES(5,7,'5:7','اور اپنے اوپر اللہ کی نعمت کو یاد کرو اور اس کے اس عہد کو یاد کرو جو اس نے تم سے لیا ہے جب تم نے کہا کہ ہم نے سنا اور ہم نے مانااور اللہ سے ڈرو بے شک اللہ دلوں کی بات تک جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,8,'5:8','اے ایمان والو، اللہ کے ليے انصاف کے ساتھ قائم رہنے والے گواہی دینے والے بنو اور کسی گروہ کی دشمنی تم کو اس پر نہ ابھارے کہ تم انصاف نہ کرو، انصاف کرو یہی تقویٰ سے زیادہ قریب ہے اور اللہ سے ڈرو بے شک اللہ کو خبر ہے جو تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(5,9,'5:9','جو لوگ ایمان لائے اور انھوں نے نیک عمل کیا، ان سے اللہ کا وعدہ ہے کہ ان کے ليے بخشش ہے اور بڑا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,10,'5:10','اور جنھوں نے انکار کیا اور ہماری نشانیوں کو جھٹلایا، ایسے لوگ دوزح والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,11,'5:11','اے ایمان والو، اپنے اوپر اللہ کے احسان کو یاد کرو جب ایک قوم نے ارادہ کیا کہ وہ تم پر دست درازی کرے تو اللہ نے تم سے ان کے ہاتھ کو روک دیا اور اللہ سے ڈرو اور ایمان والوں کو اللہ ہی پر بھروسہ کرنا چاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(5,12,'5:12','اور اللہ نے بنی اسرائیل سے عہد لیا اور ہم نے ان میں بارہ سر دار مقرر کيے اور اللہ نے کہا کہ میں تمھارے ساتھ ہوں اگر تم نماز قائم کرو گے اور زکوٰۃ ادا کرو گے اور میرے پیغمبروں پر ایمان لاؤ گے اور ان کی مدد کرو گے اور اللہ کو قرضِ حسن دو گے تو میں تم سے تمھارے گناہ ضرور دور کردوں گا اور تم کو ضرور ایسے باغوں میں داخل کرو ں گا جن کے نیچے نہریں بہتی ہوں گی پس تم میں سے جو شخص اس کے بعد انکار کرے گا تو وہ سیدھے راستے سے بھٹک گیا');
INSERT INTO "ur_wahid_uddin" VALUES(5,13,'5:13','پس ان کی عہد شکنی کی بنا پر ہم نے ان پر لعنت کردی اور ہم نے ان کے دلوں کو سخت کردیا وہ کلام کو اس کی جگہ سے بدل دیتے ہیں اور جو کچھ ان کو نصیحت کی گئی تھی، اس کا بڑا حصہ وہ بھلا بیٹھے اور تم برابر ان کی کسی نہ کسی خیانت سے آگاہ ہوتے رہتے ہو، بجز تھوڑے لوگوں کے ان کو معاف کرو اور ان سے درگزر کرو، اللہ نیکی کرنے والوں کو پسند کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,14,'5:14','اور جو لوگ کہتے ہیں کہ ہم نصرانی ہیں ،ان سے ہم نے عہد لیاتھا پس جو کچھ ان کو نصیحت کی گئی تھی، اس کا بڑا حصہ وہ بھلا بیٹھے پھر ہم نے قیامت تک کے ليے ان کے درمیان دشمنی اور بغض ڈال دیا اور آخر اللہ ان کو آگاہ کردے گا اس سے جو کچھ وہ کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(5,15,'5:15','اے اہل کتاب، تمھارے پاس ہمارا رسول آیا ہے وہ کتاب الٰہی کی بہت سی ان باتوں کو تمھارے سامنے کھول رہا ہے جن کو تم چھپاتے تھے اور وہ درگزر کرتا ہے بہت سی چیزوں سے بے شک تمھارے پاس اللہ کی طرف سے ایک روشنی اورایک واضح کرنے والی کتاب آچکی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,16,'5:16','اس کے ذریعہ سے اللہ ان لوگوں کو سلامتی کی راہیں دکھاتا ہے جو اس کی رضا کے طالب ہیں اوروہ اپنی توفیق سے ان کو اندھیروں سے نکال کر روشنی میں لارہا ہے اور سیدھی راہ کی طرف ان کی رہنمائی کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,17,'5:17','بے شک ان لوگوں نے کفر کیا جنھوں نے کہا کہ خدا ہی تو مسیح ابن مریم ہےکہو پھر کون اختیار رکھتا ہے اللہ کے آگے اگر وہ چاہے کہ ہلاک کردے مسیح ابن مریم کو اور اس کی ماں کو اور جتنے لوگ زمین میں ہیں سب کو اور اللہ ہی کے ليے ہے بادشاہی آسمانوں اور زمین کی اور جو کچھ ان کے درمیان ہے وہ پیدا کرتا ہے جو چاہتا ہے اور اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,18,'5:18','اور یہود و نصاریٰ کہتے ہیں کہ ہم خدا کے بیٹے اور اس کے محبوب ہیں تم کہو کہ پھر وہ تمھارے گناہوں پر تم کو سزا کیوں دیتاہے نہیں ، بلکہ تم بھی اس کی پیدا کی ہوئی مخلوق میں سے ایک آدمی ہو وہ جس کو چاہے گابخشے گا اور جس کو چاہے گا عذاب دے گا اور اللہ ہی کے ليے ہے بادشاہی آسمانوں اور زمین کی اور جو کچھ ان کے درمیان ہے اور اسی کی طرف لوٹ کر جانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,19,'5:19','اے اہلِ کتاب، تمھارے پا س ہمارا رسول آیا ہے، وہ تم کو صاف صاف بتارہا ہے، رسولوں کے ایک وقفے کے بعد تاکہ تم یہ نہ کہو کہ ہمارے پاس کوئی خوش خبری دینے والا اور آگاه كرنے والا نہیں آیا پس اب تمھارے پاس خوش خبری دینے والا اور ڈرانے والا آگیا ہے اور اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,20,'5:20','اور جب موسیٰ نے اپنی قوم سے کہا کہ اے میری قوم، اپنے اوپر اللہ کے احسان کو یاد کرو کہ اس نے تمھارے اندر نبی پیدا کيے اور تم کو بادشاہ بنایا اور تم کو وہ دیا جو دنیا میں کسی کو نہیں دیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(5,21,'5:21','اے میری قوم، اس پاک زمین میں داخل ہوجاؤ جو اللہ نے تمھارے ليے لکھ دی ہے اور اپنی پیٹھ کی طرف نہ لو ٹو ورنہ نقصان میں پڑجاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(5,22,'5:22','انھوں نے کہا کہ اے موسیٰ،وہاں ایک زبردست قوم ہے ہم ہر گز وہاں نہ جائیں گے جب تک وہ وہاں سے نکل نہ جائیں اگر وہ وہاں سے نکل جائیں تو ہم داخل ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(5,23,'5:23','دو آدمی جو اللہ سے ڈرنے والوں میں سے تھے اوران دونوں پر اللہ نے انعام کیا تھا، انھوں نے کہا کہ تم ان پر حملہ کرکے شہر کے پھاٹک میں داخل ہوجاؤ جب تم اس میں داخل ہوجاؤ گے تو تم ہی غالب ہوگے اور اللہ پر بھروسہ کرو اگر تم مومن ہو');
INSERT INTO "ur_wahid_uddin" VALUES(5,24,'5:24','انھوں نے کہا کہ اے موسیٰ، ہم کبھی وہاں داخل نہ ہوں گے جب تک وہ لوگ وہاں ہیں پس تم اورتمھارا خداوند دونوں جاکر لڑو، ہم یہاں بیٹھے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,25,'5:25','موسیٰ نے کہا کہ اے میرے رب، اپنے اور اپنے بھائی کے سوا کسی پر میرا اختیار نہیں پس تو ہمارے اور اس نافرمان قوم کے درمیان جدائی کردے');
INSERT INTO "ur_wahid_uddin" VALUES(5,26,'5:26','اللہ نے کہا وہ ملک ان پر چالیس سال کے ليے حرام کردیا گیا یہ لوگ زمین میں بھٹکتے پھریں گے پس تم اس نافرمان قوم پر افسوس نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(5,27,'5:27','اور ان کو آدم کے دو بیٹوں کا قصہ حق کے ساتھ سناؤ جب کہ ان دونوں نے قربانی پیش کی تو ان میں سے ایک کی قربانی قبول ہوئی اور دوسرے کی قربانی قبول نہ ہوئی اس نے کہا میں تجھ کو مار ڈالوں گا اس نے جواب دیا کہ اللہ تو صرف متقیوں سے قبول کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,28,'5:28','اگر تم مجھے قتل کرنے کے ليے ہاتھ اٹھاؤ گے تو میں تم کو قتل کرنے کے ليے تم پر ہاتھ نہیں اٹھاؤں گا میں ڈرتا ہوں اللہ سے جو سارے جہان کا رب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,29,'5:29','میں چاہتاہوں کہ میرا اور اپنا گناہ توہی لے لے پھر تو آگ والوں میں شامل ہوجائے اور یہی سزا ہے ظلم کرنے والوں کی');
INSERT INTO "ur_wahid_uddin" VALUES(5,30,'5:30','پھر اس کے نفس نے اس کو اپنے بھائی کے قتل پر راضی کرلیا اوراس نے اس کو قتل کر ڈالاپھر وہ نقصان اٹھانے والوں میں شامل ہوگیا');
INSERT INTO "ur_wahid_uddin" VALUES(5,31,'5:31','پھر خدا نے ایک کوّے کو بھیجا جو زمین میں کریدتا تھا، تاکہ وہ اس کو دکھائے کہ وہ اپنے بھائی کی لاش کو کس طرح چھپائے اس نے کہا افسوس میری حالت پر کہ میں اس کوّے جیسا بھی نہ ہوسکا کہ اپنے بھائی کی لاش کو چھپادیتا پس وہ بہت شرمندہ ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(5,32,'5:32','اسی سبب سے ہم نے بنی اسرائیل پر یہ لکھ دیا کہ جو شخص کسی کو قتل کرے، بغیر اس کے کہ اس نے کسی کو قتل کیا ہو یا زمین میں فساد برپا کیا ہو تو گویا اس نے سارے آدمیوں کو قتل کر ڈالا اورجس نے ایک شخص کو بچایا تو گویا اس نے سارے آدمیوں کو بچالیا اور ہمارے پیغمبر ان کے پاس کھلے ہوئے احکام لے کر آئے اس کے باوجود ان میں سے بہت سے لوگ زمین میں زیادتیاں کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,33,'5:33','جو لوگ اللہ اوراس کے رسول سے لڑتے ہیں اور زمین میں فساد کرنے کے ليے دوڑتے ہیں ، ان کی سزا یہی ہے کہ ان کو قتل کیا جائے یا وہ سولی پر چڑھائے جائیں یا ان کے ہاتھ اور پاؤں مخالف سمت سے کاٹے جائیں یا ان کو ملک سے باہر نکال دیا جائے یہ ان کی رسوائی دنیا میں ہے اور آخرت میں ان کے ليے بڑا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,34,'5:34','مگر جو لوگ توبہ کرلیں تمھارے قابو پانے سے پہلے تو جان لو کہ اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,35,'5:35','اے ایمان والو، اللہ سے ڈرو اوراس کا قرب تلاش کرو اور اس کی راہ میں جدوجہد کرو تاکہ تم فلاح پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(5,36,'5:36','بے شک جن لوگوں نے کفر کیا ہے کہ اگر ان کے پاس وہ سب کچھ ہو جو زمین میں ہے اور اتنا ہی اور ہو،تاکہ وہ اس کو فدیہ میں دے کر قیامت کے دن کے عذاب سے چھوٹ جائیں ، تب بھی وہ ان سے قبول نہ کی جائے گی اور ان کے ليے درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,37,'5:37','وہ چاہیں گے کہ آگ سے نکل جائیں مگر وہ اس سے نکل نہ سکیں گے اور ان کے ليے ایک مستقل عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,38,'5:38','اور چور مرد اور چور عورت دونوں کے ہاتھ کاٹ دو یہ ان کے كئے کا بدلہ ہے اور اللہ کی طرف سے عبرت ناک سزا اور اللہ غالب اور حکیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,39,'5:39','پھر جس نے اپنے ظلم کے بعد توبہ کی اور اصلاح کرلی تو اللہ بے شک اس پر توجہ کرے گا اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,40,'5:40','کیا تم نہیں جانتے کہ اللہ زمین اور آسمانوں کی سلطنت کا مالک ہے وہ جس کو چاہے سزا دے اورجس کو چاہے معاف کردے اور اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,41,'5:41','اے پیغمبر، تم کو وہ لوگ رنج میں نہ ڈالیں جو کفر کی راہ میں بڑی تیزی دکھا رہے ہیں خواہ وہ ان میں سے ہوں جو اپنے منہ سے کہتے ہیں کہ ہم ایمان لائے، حالاں کہ ان کے دل ایمان نہیں لائے یا ان میں سے ہوں جو یہودی ہیں ، جھوٹ کے بڑے سننے والے، سننے والے دوسرے لوگوں کی خاطر جو تمھارے پاس نہیں آئے وہ کلام کو اس کے مقام سے ہٹادیتے ہیں وہ لوگوں سے کہتے ہیں کہ اگر تم کو یہ حکم ملے تو قبول کرلینااور اگر یہ حکم نہ ملے تو اس سے بچ کر رہنا اور جس کو اللہ فتنہ میں ڈالنا چاہے تو تم اللہ کے مقابل اس کے معاملہ میں کچھ نہیں کرسکتے یہی وہ لوگ ہیں کہ اللہ نے نہ چاہا کہ وہ ان کے دلوں کو پاک کرے ان کے ليے دنیا میں رسوائی ہے اور آخرت میں ان کے ليے بڑا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,42,'5:42','وہ جھوٹ کے بڑے سننے والے ہیں ، حرام کے بڑے کھانے والے ہیں اگر وہ تمھارے پاس آئیں تو خواہ ان کے درمیان فیصلہ کرو یا ان کو ٹال دو اگر تم ان کو ٹال دو گے تو وہ تمھارا کچھ بگاڑ نہیں سکتے اوراگر تم فیصلہ کرو تو اِن کے درمیان انصاف کے مطابق فیصلہ کرو اللہ انصاف کرنے والوں کو پسند کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,43,'5:43','اور وہ کیسے تم کو حکَم بناتے ہیں ، حالاں کہ ان کے پاس تورات ہے جس میں اللہ کا حکم موجود ہے، اور پھر وہ اس سے منھ موڑ رہے ہیں اور یہ لوگ ہر گز ایمان والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,44,'5:44','بے شک ہم نے تورات اتاری ہے جس میں ہدایت اورروشنی ہے اسی کے مطابق، خدا کے فرماں بردار انبیاء یہودی لوگوں کا فیصلہ کرتے تھے اور ان کے درویش اور علماء بھیاس ليے کہ وہ خدا کی کتاب پر نگہبان ٹھہرائے گئے تھےاور وہ اس کے گواہ تھے پس تم انسانوں سے نہ ڈرو، مجھ سے ڈرو اور میری آیتوں کومتاعِ حقیر کے عوض نہ بیچو اور جو کوئی اس کے موافق فيصله نہ کرے جو اللہ نے اتارا ہے تو وہی لوگ منکرہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,45,'5:45','اور ہم نے اس کتاب میں ان پر لکھ دیا کہ جان کے بدلے جان اور آنکھ کے بدلے آنکھ اور ناک کے بدلے ناک اور کان کے بدلے کان اور دانت کے بدلے دانت اور زخموں کا بدلا ان کے برابر پھر جس نے اس کو معاف کردیا تو وہ اس کے ليے کفارہ ہے اور جو شخص اس کے موافق فیصلہ نہ کرے جو اللہ نے اتارا تو وہی لوگ ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,46,'5:46','اور ہم نے ان کے پیچھے عیسیٰ ابن مریم کو بھیجا تصدیق کرتے ہوئے اپنے سے قبل کی کتاب تورات کی اور ہم نے اس کو انجیل دی جس میں ہدایت اور نور ہے اوروہ تصدیق کرنے والی تھی اپنے سے اگلی کتاب تورات کی اور ہدایت اورنصیحت ڈرنے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(5,47,'5:47','اور چاہیے کہ انجیل والے اس کے موافق فیصلہ کریں جو اللہ نے اس میں اتاراہے اور جو کوئی اس کے موافق فیصلہ نہ کرے جو اللہ نے اتارا تو وہی لوگ نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,48,'5:48','اور ہم نے تمھاری طرف کتاب اتاری حق کے ساتھ، تصدیق کرنے والی پچھلی کتاب کی اوراس کے مضامین پر نگہبان پس تم ان کے درمیان فیصلہ کرو اس کے مطابق جو اللہ نے اتارا اور جو حق تمھارے پاس آیا ہے، اس کو چھوڑ کر ان کی خواہشوں کی پیروی نہ کرو ہم نے تم میں سے ہر ایک کے ليے ایک شریعت اور ایک طریقہ ٹھہرایا اور اگر خدا چاہتا تو تم کو ایک ہی امت بنادیتا مگر اللہ نے چاہا کہ وہ اپنے دئے ہوئے حکموں میں تمھاری آزمائش کرے پس تم بھلائیوں کی طرف دوڑو آخر کار تم سب کو خدا کی طرف پلٹ کرجانا ہے پھر وہ تم کو آگاہ کردے گا، اُس چیز سے جس میں تم اختلاف کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(5,49,'5:49','اور ان کے درمیان اس کے مطابق فیصلہ کرو جو اللہ نے اتارا ہے اوران کی خواہشوں کی پیروی نہ کرو اوران لوگوں سے بچو کہ کہیں وہ تم کو پھسلادیں تمھارے اوپر اللہ کے اتارے ہوئے کسی حکم سے پس اگر وہ پھر جائیں تو جان لو کہ اللہ ان کو اِن کے بعض گناہوں کی سزا دینا چاہتا ہے اور یقیناً لوگوں میں سے زیادہ آدمی نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,50,'5:50','کیا یہ لوگ جاہلیت کا فیصلہ چاہتے ہیں اور اللہ سے بهتر کس کا فیصلہ ہوسکتا ہے، ان لوگوں کے ليے جو یقین کرنا چاہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,51,'5:51','اے ایمان والو، یہود اور نصاریٰ کو دوست نہ بناؤ وہ ایک دوسرے کے دوست ہیں اور تم میں سے جو شخص ان کواپنا دوست بنائے گا تو وہ ان ہی میں سے ہوگا اللہ ظالم لوگوں کو راہ نہیں دکھاتا');
INSERT INTO "ur_wahid_uddin" VALUES(5,52,'5:52','تم دیکھتے ہو کہ جن کے دلوں میں روگ ہے، وہ ان ہی کی طر ف دوڑ رہے ہیں وہ کہتے ہیں کہ ہم کو یہ اندیشہ ہے کہ ہم کسی مصیبت میں نہ پھنس جائیں تو ممکن ہے کہ اللہ فتح دیدے یا اپنی طرف سے کوئی خاص بات ظاہر کرے تو یہ لوگ اس چیز پر جس کو یہ اپنے دلوں میں چھپائے ہوئے ہیں نادم ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(5,53,'5:53','اور اس وقت اہل ایمان کہیں گے کیا یہ وہی لوگ ہیں جو زور وشور سے اللہ کی قسمیں کھاکر یقین دلاتے تھے کہ ہم تمھارے ساتھ ہیں ان کے سارے اعمال ضائع ہوگئے اور وہ گھاٹے میں رہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,54,'5:54','اے ایمان والو، تم میں سے جو شخص اپنے دین سے پھر جائے تو اللہ جلد ایسے لوگوں کو اٹھائے گا جو اللہ کو محبوب ہوں گے اوراللہ ان کو محبوب ہوگا وہ مومنوں کے ليے نرم اورمنکروں کے اوپر سخت ہوں گے وہ اللہ کی راہ میں جہاد کریں گے اور کسی ملامت کرنے والے کی ملامت سے نہ ڈریں گے یہ اللہ کا فضل ہے وہ جس کو چاہتا ہے عطا کرتا ہے اور اللہ وسعت والا اور علم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,55,'5:55','تمھارے دوست تو بس اللہ اور اس کا رسول اور وہ ایمان والے ہیں جو نماز قائم کرتے ہیں اور زکوٰۃ ادا کرتے ہیں اور وہ اللہ کے آگے جھکنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,56,'5:56','اور جو شخص اللہ اور اس کے رسول اور ایمان والوں کو دوست بنائے تو بے شک اللہ کی جماعت ہی غالب رہنے والی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,57,'5:57','اے ایمان والو، ان لوگوں کو اپنا دوست نہ بناؤ جنھوں نے تمھارے دین کو مذاق اور کھیل بنالیا ہے، ان لوگوں میں سے جن کو تم سے پہلے کتاب دی گئی اورنہ منکروں کو اور اللہ سے ڈرتے رہو اگر تم ایمان والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(5,58,'5:58','اور جب تم نماز کے ليے پکارتے ہو تو وہ لوگ اس کو مذاق اور کھیل بنالیتے ہیں اس کی وجہ یہ ہے کہ وہ عقل نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(5,59,'5:59','کہو کہ اے اہلِ کتاب، تم ہم سے صرف اس ليے ضد رکھتے ہو کہ ہم ایمان لائے اللہ پر اور اس پر جو ہماری طرف اتارا گیا اور اس پر جو ہم سے پہلے اترا اور تم میں سے اکثر لوگ نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,60,'5:60','کہو کیا میں تم کو بتاؤ ں وہ جو اللہ کے یہاں انجام کے اعتبار سے اس سے بھی زیادہ برا ہے وہ جس پر خدا نے لعنت کی اور جس پر اس کا غضب ہوا اور جن میں سے بندر اور سور بنادئے اور انھوں نے شیطان کی پرستش کی ایسے لوگ مقام کے اعتبار سے بدتر اور راہِ راست سے بہت دور ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,61,'5:61','اور جب وہ تمھارے پاس آتے ہیں تو کہتے ہیں کہ ہم ایمان لائے، حالاں کہ وہ منکرآئے تھے اور منکر ہی چلے گئے اور اللہ خوب جانتا ہے اس چیزکو جسے وہ چھپارہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,62,'5:62','اور تم ان میں سے اکثر کو دیکھو گے کہ وہ گناہ اور ظلم اور حرام کھانے پر دوڑتے ہیں کیسے برے کام ہیں جو وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,63,'5:63','ان کے مشائخ اور علماء ان کو کیوں نہیں روکتے گناہ کی بات کہنے سے اور حرام کھانے سے کیسے برے کام ہیں جو وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,64,'5:64','اور یہود کہتے ہیں کہ خدا کے ہاتھ بندھے ہوئے ہیں انھیں کے ہاتھ بندھ جائیں اور لعنت ہو ان کو اس کہنے پر بلکہ خدا کے دونوں ہاتھ کھلے ہوئے ہیں وہ جس طرح چاہتا ہے خرچ کرتا ہے اور تمھارے اوپر تمھارے پروردگار کی طرف سے جو کچھ اترا ہے، وہ ان میں سے اکثر لوگوں کی سرکشی اور انکار كو بڑھا رہا ہے اور ہم نے ان کے درمیان دشمنی اورکینہ قیامت تک کے ليے ڈال دیا ہے جب کبھی وہ لڑائی کی آگ بھڑکاتے ہیں تو اللہ اس کو بجھادیتا ہے اور وہ زمین میں فساد پھیلانے میں سرگرم ہیں حالاں کہ اللہ فساد برپا کرنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(5,65,'5:65','اور اگر اہل کتاب ایمان لاتے اور اللہ سے ڈرتے تو ہم ضرور ان کی برائیاں ان سے دور کردیتے اور ان کو نعمت کے باغوں میں داخل کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(5,66,'5:66','اور اگر وہ تورات اور انجیل کی پابندی کرتے اور اس کی جوان پر ان کے رب کی طرف سے اتارا گیا ہے تو وہ کھاتے اپنے اوپر سے اور اپنے قدموں کے نیچے سے کچھ لوگ ان میں سیدھی راہ پر ہیں لیکن زیادہ ان میں ایسے ہیں جو بہت برا کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,67,'5:67','اے پیغمبر، جو کچھ تمھارے اوپر تمھارے رب کی طرف سے اترا ہے اس کو پہنچادو اور اگر تم نے ایسا نہ کیا تو تم نے اللہ کے پیغام کونہیں پہنچایا اور اللہ تم کو لوگوں سے بچا ئے گا اللہ یقیناً منکر لوگوں کو راہ نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(5,68,'5:68','کہہ دو، اے اہل کتاب تم کسی چیز پر نہیں جب تک تم قائم نہ کرو تورات اور انجیل کو اور اس کوجو تمھارے اوپر اترا ہے تمھارے رب کی طرف سے اور جو کچھ تمھارے اوپر تمھارے رب کی طرف سے اتارا گیا ہے، وہ یقیناً ان میں سے اکثر کی سرکشی اور انکار کو بڑھائے گاپس تم انکار کرنے والوں کے اوپرافسوس نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(5,69,'5:69','بے شک جو لوگ ایمان لائے اور جو لوگ یہودی ہوئے اور صابی اور نصرانی، جو شخص بھی ایمان لائے اللہ پر اور آخرت کے دن پر اور نیک عمل کرے تو ان کے ليے نہ کوئی اندیشہ ہے اور نہ وہ غمگین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(5,70,'5:70','ہم نے بنی اسرائیل سے عہد لیا اور ان کی طرف بہت سے رسول بھیجے جب کوئی رسول ان کے پاس ایسی بات لے کر آیا جس کو ان کا جی نہ چاہتا تھا تو بعضوں کو انھوں نے جھٹلایا اور بعضوں کو قتل کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(5,71,'5:71','اور خیال کیا کہ کچھ خرابی نہ ہوگی پس وہ اندھے اور بہرے بن گئے پھر اللہ نے ان پر توجہ کی پھر ان میں سے بہت سے اندھے اور بہرے بن گئےاور اللہ دیکھتا ہے جو کچھ وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,72,'5:72','یقیناً ان لوگوں نے کفر کیا جنھوں نے کہا کہ خدا تو هی مسیح ابن مریم ہے حالاں کہ مسیح نے کہا تھا کہ اے بنی اسرائیل، اللہ کی عبادت کرو جو میرا رب ہے اور تمھارا رب بھی جو شخص اللہ کا شریک ٹھہرائے گا تو اللہ نے حرام کی اس پر جنت اور اس کا ٹھکانا آگ ہے اور ظالموں کا کوئی مددگار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,73,'5:73','یقینا ان لوگوں نے کفر کیا جنھوں نے کہا کہ خدا تین میں کا تیسرا ہے حالاں کہ کوئی معبود نہیں بجز ایک معبود کے اور اگر وہ باز نہ آئے اس سے جو وہ کہتے ہیں تو ان میں سے کفر پر قائم رہنے والوں کو ایک درد ناک عذاب پکڑے گا');
INSERT INTO "ur_wahid_uddin" VALUES(5,74,'5:74','یہ لوگ اللہ کے آگے توبہ کیوں نہیں کرتے اور اس سے معافی کیوں نہیں چاہتے اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,75,'5:75','مسیح ابن مریم تو صرف ایک رسول ہیں ان سے پہلے بھی بہت سے رسول گزر چکے ہیں اور ان کی ماں ایک راست باز خاتون تھیں دونوں کھانا کھاتے تھے دیکھو ہم کس طرح ان کے سامنے دلیلیں بیان کر رہے ہیں پھر دیکھو وہ کدھر الٹے چلے جارہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,76,'5:76','کہو، کیا تم اللہ کو چھوڑ کر ایسی چیز کی عبادت کرتے ہو جو نہ تمھارے نقصان کا اختیار رکھتی ہے اور نہ نفع کا اور سننے والا اور جاننے والا صرف اللہ ہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,77,'5:77','کہو، اے اہل کتاب، اپنے دین میں ناحق غلونہ کرو اور ان لوگوں کے خیالات کی پیروی نہ کرو جو اس سے پہلے گمراه ہوئے اور جنھوں نے بہت سے لوگوں کو گمراہ کیا اور وہ سیدھی راہ سے بھٹک گئے');
INSERT INTO "ur_wahid_uddin" VALUES(5,78,'5:78','بنی اسرائیل میں سے جن لوگوں نے کفر کیا، ان پر لعنت کی گئی داؤد اور عیسیٰ ابن مریم کی زبان سے اس ليے کہ انھوں نے نافرمانی کی اور وہ حد سے آگے بڑھ جاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(5,79,'5:79','وہ ایک دوسرے کو منع نہیں کرتے تھے اس برائی سے جو وہ کرتے تھے نہایت برا کام تھا جو وہ کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(5,80,'5:80','تم ان میں بہت آدمی دیکھو گے کہ کفر کرنے والوں سے دوستی رکھتے ہیں کیسی بری چیز ہے جو انھوں نے اپنے ليے آگے بھیجی ہے کہ خدا کا غضب ہوا ان پر اور وہ ہمیشہ عذاب میں پڑے رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(5,81,'5:81','اگروہ ایمان رکھنے والے ہوتے اللہ پر اور نبی پر اور اس پر جو اس کی طرف اترا تو وہ منکروں کو دوست نہ بناتے مگر ان میں اکثر نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,82,'5:82','ایمان والوں کے ساتھ عداوت میں تم سب سے بڑھ کر یہود اور مشرکین کو پاؤ گے اور ایمان والوں کے ساتھ دوستی میں تم سب سے زیادہ ان لوگوں کو پاؤ گے جو اپنے کو نصاریٰ کہتے ہیں یہ اس ليے کہ ان میں عالم اور راہب ہیں اور اس ليے کہ وہ تکبر نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(5,83,'5:83','اور جب وہ اس کلام کو سنتے ہیں جو رسول پر اتارا گیا ہے تو تم دیکھو گے کہ ان کی آنکھوں سے آنسو جاری ہیں ، اس سبب سے کہ انھوں نے حق کو پہچان لیا وہ پکار اٹھتے ہیں کہ اے ہمارے رب ہم ایمان لائے پس تو ہم کو گواہی دینے والوں میں لکھ لے');
INSERT INTO "ur_wahid_uddin" VALUES(5,84,'5:84','اور ہم کیوں نہ ایمان لائیں اللہ پر اور اس حق پر جو ہمیں پہنچا ہے جب کہ ہم یہ آرزو رکھتے ہیں کہ ہمارا رب ہم کو صالح لوگوں کے ساتھ شامل کرے');
INSERT INTO "ur_wahid_uddin" VALUES(5,85,'5:85','پس اللہ ان کو اس قول کے بدلہ میں ایسے باغ دے گاجن کے نیچے نہریں بہتی ہوں گی، وہ ان میں ہمیشہ رہیں گے اور یہی بدلہ ہے نیک عمل کرنے والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(5,86,'5:86','اور جنھوں نے انکار کیا اور ہماری نشانیو ں کو جھٹلایا تو وہی لوگ دوزخ والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,87,'5:87','اے ایمان والو،ان ستھری چیزو ں کو حرام نہ ٹھہراؤجو اللہ نے تمھاری ليے حلال کی ہیں اور حد سے نہ بڑھو اللہ حد سے بڑھنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(5,88,'5:88','اور اللہ نے تم کو جو حلال چیزیں دی ہیں ، ان میں سے کھاؤ اور اللہ سے ڈرو جس پر تم ایمان لائے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(5,89,'5:89','اللہ تم سے تمھاری بے معنی قسموں پر گرفت نہیں کرتامگر جن قسموں کو تم نے مضبوط باندھا، ان پر وہ ضرور تمھاری گرفت کرے گا ایسی قسم کا کفارہ ہے دس مسکینوں کو اوسط درجہ کا کھانا کھلانا جو تم اپنے گھر والوں کو کھلاتے ہو یا کپڑا پہنا دینا یا ایک گردن آزاد کرنا اور جس کو میسر نہ ہو وہ تین دن کے روزے رکھے یہ کفارہ ہے تمھاری قسمو ں کا جب کہ تم قسم کھا بیٹھو اور اپنی قسموں کی حفاظت کرو اس طرح اللہ تمھارے لیے اپنے احکام بیان کرتا ہے تاکہ تم شکر ادا کرو');
INSERT INTO "ur_wahid_uddin" VALUES(5,90,'5:90','اے ایمان والو، شراب اور جوا اور تھان اور پانسے سب گندے کام ہیں ، شیطان کے پس تم ان سے بچو تاکہ تم فلاح پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(5,91,'5:91','شیطان تو یہی چاہتا ہے کہ شراب اور جوئے کے ذریعہ وہ تمھارے درمیان دشمنی اور بغض ڈال دے اور تم کو اللہ کی یاد اور نماز سے روک دے تو کیا تم ان سے باز آؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(5,92,'5:92','اور اطاعت کرو اللہ کی اور اطاعت کرو رسول کی اور بچو اگر تم اعراض کرو گے تو جان لو کہ ہمارے رسول کے ذمے صرف کھول کر پہنچا دینا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,93,'5:93','جو لوگ ایمان لائے اور نیک کام کيے ان پر اس چیز میں کوئی گناہ نہیں جو وہ کھاچکے جب کہ وہ ڈرے اور ایمان لائے اور نیک کام کیاپھر ڈرے اور ایمان لائے پھر ڈرے اور نیک کام کیااور اللہ نیک کام کرنے والوں کے ساتھ محبت رکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,94,'5:94','اے ایمان والو، اللہ تمھیں اس شکار کے ذریعہ سے آزمائش میں ڈالے گا جو بالکل تمھارے ہاتھوں اور تمھارے نیزوں کی زد میں ہوگا، تاکہ اللہ جانے کہ کون شخص اس سے بن دیکھے ڈرتا ہے پھر جس نے اس کے بعد زیادتی کی تو اس کے ليے ددرد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,95,'5:95','اے ایمان والو، شکار کونہ مارو جب کہ تم حالتِ احرام میں ہو اور تم میں سے جو شخص اس کو جان بوجھ کر مارے تو اس کا بدلہ اسی طرح کا جانو رہے جیسا کہ اس نے مارا ہے جس کا فیصلہ تم میں سے دو عادل آدمی کریں گے اور یہ نذرانہ کعبہ پہنچایا جائے یا اس کے کفارہ میں چند محتاجوں کو کھانا کھلانا ہوگا یا اس کے برابر روزے رکھنے ہوں گے، تاکہ وہ اپنے کيے کی سزا چکھے اللہ نے معاف کیا جو کچھ ہوچکا اور جو شخص پھرے گا تو اللہ اس سے بدلہ لے گا اور اللہ زبردست ہے، بدلہ لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,96,'5:96','تمھارے ليے دریا کا شکار اور اس کا کھانا جائز کیا گیا، تمھارے فائدہ کے ليے اور قافلوں کے ليے اور جب تک تم احرام میں ہو خشکی کا شکار تمھارے اوپر حرام کیاگیا اور اللہ سے ڈرو جس کے پاس تم حاضر کيے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(5,97,'5:97','اللہ نے کعبہ، حرمت والے گھر، کولوگوں کے ليے قیام کا باعث بنایا اور حرمت والے مہینوں کو اور قربانی کے جانوروں کو اور گلے میں پٹّے پڑے ہوئے جانوروں کو بھی، یہ اس ليے کہ تم جانو کہ اللہ کو معلوم ہے جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے اور اللہ ہر چیز سے واقف ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,98,'5:98','جان لو کہ اللہ کا عذاب سخت ہے اور بے شک اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,99,'5:99','رسول پر صرف پہنچادینے کی ذمہ داری ہے اللہ جانتا ہے جو کچھ تم ظاہر کرتے ہو اور جو کچھ تم چھپاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(5,100,'5:100','کہو کہ ناپاک اور پاک برابر نہیں ہوسکتے، اگرچہ ناپاک کی کثرت تم کو بھلی لگے پس اللہ سے ڈرو، اے عقل والو، تاکہ تم فلاح پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(5,101,'5:101','اے ایمان والو، ایسی باتوں کے متعلق سوال نہ کرو کہ اگر وہ تم پر ظاہر کردی جائیں تو وہ تم کو گراں گزریں اور اگر تم ان کے متعلق سوال کروگے ایسے وقت میں جب کہ قرآن اتر رہا ہے تو وہ تم پر ظاہر کردی جائیں گی اللہ نے ان سے درگزر کیا اور اللہ بخشنے والا، تحمل والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,102,'5:102','ایسی ہی باتیں تم سے پہلے ایک جماعت نے پوچھیں پھر وہ ان کے منکر ہوکر رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(5,103,'5:103','اللہ نے بَحیرہ اور سائبہ اور وصیلہ اور حام (بتوں کے نام پر چھوڑے ہوئے جانور) مقرر نہیں کيے مگر جن لوگوں نے کفر کیا وہ اللہ پر جھوٹ باندھتے ہیں اور ان میں سے اکثر عقل سے کام نہیں لیتے');
INSERT INTO "ur_wahid_uddin" VALUES(5,104,'5:104','اور جب ان سے کہاجاتا ہے کہ اللہ نے جو کچھ اتارا ہے اس کی طرف آؤ اور رسول کی طرف آؤ تو وہ کہتے ہیں کہ ہمارے ليے وہی کافی ہے جس پر ہم نے اپنے بڑو ں کو پایا ہے کیا اگرچہ ان کے بڑے نہ کچھ جانتے ہوں اور نہ ہدایت پر ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(5,105,'5:105','اے ایمان والو، تم اپنی فکر رکھو کوئی گمراہ ہو تو اس سے تمھارا کچھ نقصان نہیں اگر تم ہدایت پر ہو تم سب کواللہ کے پاس لوٹ کر جاناہے پھر وہ تم کو بتادے گا جو کچھ تم کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(5,106,'5:106','اے ایمان والو، تمھارے درمیان گواہی وصیت کے وقت، جب کہ تم میں سے کسی کی موت کا وقت آجائے، اس طرح ہے کہ دو معتبر آدمی تم میں سے گواہ ہوں یا اگر تم سفر کی حالت میں ہو اور وہاں موت کی مصیبت پیش آجائے تو تمھارے غیروں میں سے دو گواہ لے ليے جائیں پھر اگر تم کو شبہ ہوجائے تو دونوں گواہوں کو نماز کے بعد روک لو اور وہ دونوں خدا کی قسم کھاکر کہیں کہ ہم کسی قیمت کے عوض اس کونہ بیچیں گے، خواہ کوئی قرابت دار ہی کیوں نہ ہو اور نہ ہم اللہ کی گواہی کو چھپائیں گے اگر ہم ایسا کریں تو بے شک ہم گنہ گار ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(5,107,'5:107','پھر اگر پتہ چلے کہ ان دونوں نے کوئی حق تلفی کی ہے تو ان کی جگہ دو اور شخص ان لوگوں میں سے کھڑے ہوں جن کا حق پچھلے دو گواہوں نے مارنا چاہاتھا وہ خدا کی قسم کھائیں کہ ہماری گواہی ان دونوں کی گواہی سے زیادہ برحق ہے اور ہم نے کوئی زیادتی نہیں کی ہے اگر ہم ایسا کریں تو ہم ظالموں میں سے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(5,108,'5:108','یہ قریب ترین طریقہ ہے کہ لوگ گواہی ٹھیک دیں ، یا اس سے ڈریں کہ ہماری قسم ان کی قسم کے بعد الٹی پڑے گی اور اللہ سے ڈرو اور سنو اللہ نافرمانوں کو سیدھی راہ نہیں چلاتا');
INSERT INTO "ur_wahid_uddin" VALUES(5,109,'5:109','جس دن اللہ پیغمبروں کو جمع کرے گا، پھر پوچھے گا تم کو کیا جواب ملا تھا وہ کہیں گے ہمیں کچھ علم نہیں ، چھپی ہوئی باتوں کو جاننے والا تو ہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,110,'5:110','جب اللہ کہے گا اے عیسیٰ ابن مریم، میری اس نعمت کو یاد کرو جو میں نے تم پر اور تمھاری ماں پر کیا جب کہ میں نے روح پاک سے تمھاری مدد کی تم لوگوں سے کلام کرتے تھے گود میں بھی اور بڑی عمر میں بھیاور جب میں نے تم کو کتاب اور حکمت اور تورات اور انجیل کی تعلیم دی اور جب تم مٹی سے پرندہ جیسی صورت میرے حکم سے بناتے تھے پھر اس میں پھونک مارتے تھے تو وہ میرے حکم سے پرندہ بن جاتی تھیاور تم اندھے اور جُذامی کومیرے حکم سے اچھا کردیتے تھے اور جب تم مُردوں کومیرے حکم سے نکال کھڑا کرتے تھے اور جب میں نے بنی اسرائیل کو تم سے روکا جب کہ تم ان کے پاس کھلی نشانیاں لے کر آئے تو ان کے منکروں نے کہا یہ تو بس ایک کھلا ہوا جادو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,111,'5:111','اور جب میں نے حواریوں کے دل میں ڈال دیا کہ مجھ پر ایمان لاؤ اور میرے رسول پر ایمان لاؤ تو انھوں نے کہا کہ ہم ایمان لائے اور تو گواہ رہ کہ ہم فرماں بردار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,112,'5:112','جب حواریوں نے کہا کہ اے عیسیٰ ابن مریم، کیا تمھارا رب یہ کرسکتا ہے کہ ہم پر آسمان سے ایک خوان اتارے عیسیٰ نے کہا اللہ سے ڈرو اگر تم ایمان والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(5,113,'5:113','انھوں نے کہا کہ ہم چاہتے ہیں کہ ہم اس میں سے کھائیں اور ہمارے دل مطمئن ہوں اور ہم یہ جان لیں کہ تو نے ہم سے سچ کہا اور ہم اس پر گواہی دینے واے بن جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(5,114,'5:114','عیسیٰ ابن مریم نے دعا کی کہ اے اللہ، ہمارے رب، توآسمان سے ہم پر ایک خوان اتار جو ہمارے ليے ایک عید بن جائے، ہمارے اگلوں کے ليے اور ہمارے پچھلوں کے ليے اور تیری طرف سے ایک نشانی ہو اور ہم کو عطا کر، تو ہی بہترین عطا کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,115,'5:115','اللہ نے کہا میں یہ خوان ضرور تم پر اتاروں گا پھر اس کے بعد تم میں سے جو شخص منکر ہوگا، اس کو میں ایسی سزا دوں گاجو دنیا میں کسی کو نہ دی ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(5,116,'5:116','اور جب اللہ پوچھے گاکہ اے عیسیٰ ابن مریم، کیا تم نے لوگوں سے کہا تھا کہ مجھ کو اور میری ماں کو خدا کے سوا معبود بنالو وہ جواب دیں گے کہ تو پاک ہے، میرا یہ کام نہ تھا کہ میں وہ بات کہوں جس کا مجھے کوئی حق نہیں اگر میں نے یہ کہا ہوگا تو تجھ کو ضرور معلوم ہوگا تو جانتا ہے جو میرے جی میں ہے اور میں نہیں جانتا جو تیرے جی میں ہے، بے شک تو ہی ہے چھپی باتوں کا جاننے والا');
INSERT INTO "ur_wahid_uddin" VALUES(5,117,'5:117','میں نے ان سے وہی بات کہی جس کا تونے مجھے حکم دیا تھا یہ کہ اللہ کی عبادت کرو جو میرا رب ہے اور تمھارا بھی اور میں ان پر گواہ تھا جب تک میں ان میں رہا پھر جب تو نے مجھ کو وفات دے دی تو ان پر تو ہی نگراں تھا اور تو ہر چیز پر گواہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,118,'5:118','اگر تو ان کو سزا دے تو وہ تیرے بندے ہیں اور اگر تو ان کو معاف کردے تو تو ہی زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(5,119,'5:119','اللہ کہے گا آج وہ دن ہے کہ سچوں کو ان کا سچ کام آئے گا ان کے ليے باغ ہیں جن کے نیچے نہریں بہہ رہی ہیں ان میں وہ ہمیشہ رہیں گے اللہ ان سے راضی ہوا اور وہ اللہ سے راضی ہوئے یہی ہے بڑی کامیابی');
INSERT INTO "ur_wahid_uddin" VALUES(5,120,'5:120','آسمانوں اور زمین اور جو کچھ ان میں ہے سب کی بادشاہی اللہ ہی کے ليے ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,1,'6:1','تعریف اللہ کے ليے ہے جس نے آسمانوں اور زمین کو پیداکیا اور تاریکیوں اور روشنی کو بنایاپھر بھی منکر لوگ دوسرو ں کو اپنے رب کا ہمسر ٹھہراتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,2,'6:2','وہی ہے جس نے تم کو مٹی سے پیدا کیا پھر ایک مدت مقرر کی اور مقررہ مدت اسی کے علم میں ہےپھر بھی تم شک کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,3,'6:3','اور وہی اللہ آسمانوں میں ہے اور وہی زمین میں وہ تمھارے چھپے اور کھلے کو جانتا ہے اور وہ جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,4,'6:4','اور ان کے رب کی نشانیوں میں سے جو نشانی بھی ان کے پاس آتی ہے، وہ اس سے اعراض کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,5,'6:5','چنانچہ جو حق ان کے پاس آیا ہے، اس کو بھی انھوں نے جھٹلادیا پس عنقریب ان کے پاس اس چیز کی خبریں آئیں گی جس کا وہ مذاق اڑاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,6,'6:6','کیا انھوں نے نہیں دیکھا کہ ہم نے ان سے پہلے کتنی قوموں کو ہلاک کردیا ان کوہم نے زمین میں جمادیا تھا جتنا تم کو نہیں جمایااور ہم نے ان پر آسمان سے خوب بارش برسائی اور ہم نے نہریں جاری کیں جو ان کے نیچے بہتی تھیں ، پھر ہم نے ان کو ان کے گناہوں کے باعث ہلاک کر ڈالا اور ان کے بعد ہم نے دوسری قوموں کواٹھایا');
INSERT INTO "ur_wahid_uddin" VALUES(6,7,'6:7','اور اگر ہم تم پر ایسی کتاب اتارتے جو کاغذ میں لکھی ہوئی ہوتی اور وہ اس کو اپنے ہاتھوں سے چھو بھی لیتے، تب بھی انکار کرنے والے یہ کہتے کہ یہ تو ایک کھلا ہوا جادو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,8,'6:8','اور وہ کہتے ہیں کہ اس پر کوئی فرشتہ کیوں نہیں اتا راگیااور اگر ہم کوئی فرشتہ اتارتے تو معاملہ کا فیصلہ ہوجاتا،پھر انھیں کوئی مہلت نہ ملتی');
INSERT INTO "ur_wahid_uddin" VALUES(6,9,'6:9','اور اگر ہم کسی فرشتہ کو رسول بناکر بھیجتے تو اس کو بھی آدمی بناتے اور ان کو اسی شبہ میں ڈال دیتے جس میں وہ اب پڑے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,10,'6:10','اور تم سے پہلے بھی رسولوں کا مذاق اڑایا گیا تو ان میں سے جن لوگو ں نے مذاق اڑایا، ان کو اس چیز نے آگھیرا جس کا وہ مذاق اڑاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,11,'6:11','کہو، زمین میں چلو پھرو اور دیکھو کہ جھٹلانے والوں کا انجام کیا ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(6,12,'6:12','پوچھو کہ کس کا ہے جو کچھ آسمانوں اور زمین میں ہے کہو سب کچھ اللہ کا ہے اس نے اپنے اوپر رحمت لکھ لی ہے وہ ضرور تم کو جمع کرے گاقیامت کے دن، اس میں کوئی شک نہیں جن لوگوں نے اپنے آپ کو گھاٹے میں ڈالا، وہی ہیں جو اس پر ایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,13,'6:13','اور اللہ ہی کا ہے جو کچھ ٹھیرتا ہے رات میں اور جو کچھ دن میں اور وہ سب کچھ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,14,'6:14','کہو، کیا میں اللہ کے سوا کسی اور کو مدد گار بناؤں جو بنانے والا ہے آسمانوں اور زمین کا اور وہ سب کو کھلاتا ہے اور اس کو کوئی نہیں کھلاتاکہو مجھ کو حکم ملا ہے کہ میں سب سے پہلے اسلام لانے والا بنوں اور تم ہر گز مشرکوں میں سے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(6,15,'6:15','کہو، اگر میں اپنے رب کی نافرمانی کروں تو میں ایک بڑے دن کے عذاب سے ڈرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(6,16,'6:16','جس شخص سے وہ اس روز ہٹالیا گیا، اس پر اللہ نے بڑا رحم فرمایا اور یہی کھلی ہوئی کامیا بی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,17,'6:17','اور اگر اللہ تجھ کو کوئی دکھ پہنچائے تو اس کے سوا کوئی اس کا دور کرنے والا نہیں اور اگر اللہ تجھ کوکوئی بھلائی پہنچائے تو وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,18,'6:18','اور اسی کا زور ہے اپنے بندوں پر اور وہ حکمت والا، سب کی خبر رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,19,'6:19','تم پوچھو کہ سب سے بڑا گواہ کون ہے کہو اللہ، وہ میرے اور تمھارے درمیان گواہ ہے اور مجھ پر یہ قرآن اترا ہے، تاکہ میں تم کو اس سے خبردار کردوں اور اس کو جسے یہ پہنچے کیا تم اس کی گواہی دیتے ہو کہ خدا کے ساتھ کچھ اور معبود بھی ہیں کہو، میں اس کی گواہی نہیں دیتا کہو، وہ تو بس ایک ہی معبود ہے اور میں بَری ہوں تمھارے شرک سے');
INSERT INTO "ur_wahid_uddin" VALUES(6,20,'6:20','جن لوگوں کوہم نے کتاب دی ہے، وہ اس کو پہچانتے ہیں جیسا اپنے بیٹوں کوپہچانتے ہیں جن لوگوں نے اپنے کو گھاٹے میں ڈالا وہ اس کو نہیں مانتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,21,'6:21','اوراس شخص سے زیادہ ظالم کون ہوگا جواللہ پر بہتان باندھے یا اللہ کی نشانیوں کو جھٹلائےیقیناً ظالموں کو فلاح نہیں ملتی');
INSERT INTO "ur_wahid_uddin" VALUES(6,22,'6:22','اور جس دن ہم ان سب کو جمع کریں گے، پھر ہم کہیں گے ان شریک ٹھہرانے والوں سے کہ تمھارے وہ شریک کہاں ہیں جن کا تم کو دعویٰ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(6,23,'6:23','پھر ان کے پاس کوئی فریب نہ رہے گا مگر یہ کہ وہ کہیں گے کہ اللہ اپنے رب کی قسم، ہم شرک کرنے والے نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,24,'6:24','دیکھو یہ کس طرح اپنے آپ پر جھوٹ بولے اور کھوئی گئیں ان سے وہ باتیں جو وہ بنایا کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,25,'6:25','اور ان میں بعض لوگ ایسے ہیں جو تمھاری طرف کان لگاتے ہیں اور ہم نے ان کے دلوں پر پردے ڈال دئے ہیں کہ وہ اس کو نہ سمجھیں اور ان کے کانوں میں بوجھ ہے اگر وہ تمام نشانیاں دیکھ لیں تب بھی وہ ان پر ایمان نہ لائیں گے یہاں تک کہ جب وہ تمھارے پاس تم سے جھگڑنے آتے ہیں تو وہ منکر کہتے ہیں کہ یہ تو بس پہلے لوگوں کی کہانیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,26,'6:26','وہ لوگوں کو روکتے ہیں اور خود بھی اس سے الگ رہتے ہیں وہ خود اپنے آپ کو ہلاک کر رہے ہیں مگر وہ نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,27,'6:27','اور اگر تم ان کو اس وقت دیکھو جب وہ آگ پر کھڑے کيے جائیں گے اور کہیں گے کہ کاش ہم پھر بھیج دیے جائیں تو ہم اپنے رب کی نشانیوں کونہ جھٹلائیں اور ہم ایمان والوں میں سے ہوجائیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,28,'6:28','اب ان پر وہ چیز کھل گئی جس کو وہ اس سے پہلے چھپاتے تھے اور اگر وہ واپس بھیج دئے جائیں تو وہ پھر وہی کریں گے جس سے وہ روکے گئے تھے اور بے شک وہ جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,29,'6:29','اور وہ کہتے ہیں کہ زندگی تو بس یہی ہماری دنیا کی زندگی ہے اور ہم پھر اٹھائے جانے والے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,30,'6:30','اور اگر تم اس وقت دیکھتے جب کہ وہ اپنے رب کے سامنے کھڑے کيے جائیں گے وہ ان سے پوچھے گا کیا یہ حقیقت نہیں ہے، وہ جواب دیں گے ہاں ، ہمارے رب کی قسم، یہ حقیقت ہے خدا فرمائے گااچھاتو عذاب چکھو اس انکار کے بدلے جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,31,'6:31','یقیناً وہ لوگ گھاٹے میں رہے جنھوں نے اللہ سے ملنے کو جھٹلایا یہاں تک کہ جب وہ گھڑی ان پر اچانک آئے گی تو وہ کہیں گے ہائے افسوس، اس باب میں ہم نے کیسی کوتاہی کی اور وہ اپنے بوجھ اپنی پیٹھوں پر اٹھائے ہوئے ہوں گے دیکھو،کیسا برا بوجھ ہے جس کو وہ اٹھائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,32,'6:32','اور دنیا کی زندگی تو بس کھیل تماشا ہے اور آخرت کا گھر بہتر ہے ان لوگوں کے ليے جو تقویٰ رکھتے ہیں ، کیا تم نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,33,'6:33','ہم کو معلوم ہے کہ وہ جو کچھ کہتے ہیں اس سے تم کو رنج ہوتا ہے یہ لوگ تم کونہیں جھٹلاتے بلکہ یہ ظالم دراصل اللہ کی نشانیوں کا انکار کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,34,'6:34','اور تم سے پہلے بھی رسولوں کو جھٹلایا گیا تو انھوں نے جھٹلائے جانے اور تکلیف پہنچانے پر صبر کیا یہاں تک کہ ان کو ہماری مدد پہنچ گئی اور اللہ کی باتوں کو کوئی بدلنے والا نہیں اور پیغمبروں کی کچھ خبریں تم کو پہنچ ہی چکی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,35,'6:35','اور اگر ان کی بے رخی تم پر گراں گزر رہی ہے تو اگر تم میں کچھ زور ہے تو زمین میں کوئی سرنگ ڈھونڈو یا آسمان میں سیڑھی لگاؤ اور ان کے ليے کوئی نشانی لے آؤ اور اگر اللہ چاہتا تو ان سب کو ہدایت پر جمع کردیتا پس تم نادانوں میں سے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(6,36,'6:36','قبول تو وہی لوگ کرتے ہیں جو سنتے ہیں اور مُردوں کو اللہ اٹھائے گا پھر وہ اس کی طرف لوٹائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,37,'6:37','اور وہ کہتے ہیں کہ رسول پر کوئی نشانی اس کے رب کی طرف سے کیوں نہیں اتری کہو، اللہ بے شک قادر ہے کہ کوئی نشانی اتارے، مگر اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,38,'6:38','اور جو بھی جانور زمین پر چلتا ہے اور جو بھی پرندہ اپنے دونو ں بازوؤں سے اڑتا ہے، وہ سب تمھاری ہی طرح کے انواع ہیں ہم نے لکھنے میں کوئی چیزنہیں چھوڑی ہے پھر سب اپنے رب کے پاس اكٹھے کيے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,39,'6:39','اور جنھوں نے ہماری نشانیوں کو جھٹلایا، وہ بہرے اور گونگے ہیں ،تاریکیوں میں پڑے ہوئے ہیں اللہ جس کو چاہتا ہے بھٹکادیتا ہے اور جس کو چاہتا ہے سیدھی راہ پر لگادیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,40,'6:40','کہو، یہ بتاؤ کہ اگر تم پر اللہ کا عذاب آئے یا قیامت آجائے تو کیا تم اللہ کے سوا کسی اور کو پکارو گے بتاؤ اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,41,'6:41','بلکہ تم اسی کو پکارو گے پھر وہ دور کردیتاہے اس مصیبت کو جس کے ليے تم اس کو پکارتے ہو، اگر وہ چاہتا ہے اور تم بھول جاتے ہو ان کو جنھیں تم شریک ٹھہراتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,42,'6:42','اور تم سے پہلے بہت سی قوموں کی طرف ہم نے رسول بھیجے پھر ہم نے ان کو پکڑا سختی میں اور تکلیف میں تاکہ وہ گڑگڑائیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,43,'6:43','پس جب ہماری طرف سے ان پر سختی آئی تو کیوں نہ وہ گڑگڑائے،بلکہ ان کے دل سخت ہوگئے اور شیطان ان کے عمل کو ان کی نظر میں خوش نما کرکے دکھاتا رہا');
INSERT INTO "ur_wahid_uddin" VALUES(6,44,'6:44','پھر جب انھوں نے اس نصیحت کو بھلادیا جو ان کو کی گئی تھی تو ہم نے ان پر ہر چیز کے دروازے کھول دئے یہاں تک کہ جب وہ اس چیز پر خوش ہوگئے جو انھیں دی گئی تھی تو ہم نے اچانک ان کو پکڑلیا اس وقت وہ نا امید ہوکر رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(6,45,'6:45','پس ان لوگو ں کی جڑ کاٹ دی گئی جنھوں نے ظلم کیا تھا اور ساری تعریف اللہ کے ليے ہے، تمام جہانوں کا رب');
INSERT INTO "ur_wahid_uddin" VALUES(6,46,'6:46','کہو، یہ بتاؤ کہ اللہ اگر چھین لے تمھارے کان اور تمھاری آنکھیں اور تمھارے دلوں پر مہر کردے تو اللہ کے سوا کون معبود ہے جو اس کوواپس لائے دیکھو ہم کیوں کر طرح طرح سے نشانیاں بیان کرتے ہیں ، پھر بھی وہ اعراض کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,47,'6:47','کہو، یہ بتاؤ اگر اللہ کا عذاب تمھارے اوپر اچانک یااعلانیہ آجائے تو ظالموں کے سوا اور کون ہلاک ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(6,48,'6:48','اور رسولوں کو ہم صرف خوش خبری دینے والے یا ڈرانے والے کی حیثیت سے بھیجتے ہیں پھر جو ایمان لایا اور اپنی اصلاح کی تو ان کے ليے نہ کوئی اندیشہ ہے اور نہ وہ غمگین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,49,'6:49','اور جنھوں نے ہماری نشانیوں کو جھٹلایا تو ان کو عذاب پکڑلے گا اس ليے کہ وہ نافرمانی کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,50,'6:50','کہو، میں تم سے یہ نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں اور نہ میں غیب کو جانتا ہوں اور نہ میں تم سے کہتا ہوں کہ میں فرشتہ ہوں میں تو بس اس وحی کی پیروی کرتا ہوں جو میرے پاس آتی ہے کہو، کیا اندھا اور آنکھوں والا دونوں برابر ہوسکتے ہیں کیا تم غور نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,51,'6:51','اور تم اس وحی کے ذریعہ سے ڈراؤ ان لوگوں کو جو اندیشہ رکھتے ہیں اس بات کا کہ وہ اپنے رب کے پاس جمع کيے جائیں گے اس حال میں کہ اللہ کے سوا نہ ان کا کوئی حمایتی ہوگا اور نہ کوئی سفارش کرنے والا، شایدکہ وہ اللہ سے ڈریں');
INSERT INTO "ur_wahid_uddin" VALUES(6,52,'6:52','اور تم ان لوگوں کو اپنے سے دور نہ کرو جوصبح و شام اپنے رب کو پکارتے ہیں اس کی خوشنودی چاہتے ہوئے ان کے حساب میں سے کسی چیزکا بوجھ تم پر نہیں اور تمھارے حساب میں سے کسی چیز کا بوجھ ان پر نہیں کہ تم ان کو اپنے سے دور کر کے بے انصافوں میں سے ہو جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(6,53,'6:53','اور اس طرح ہم نے ان میں سے ایک کو دوسرے سے آزمایا ہے، تاکہ وہ کہیں کہ کیا یہی وہ لوگ ہیں جن پر ہمارے درمیان اللہ کا فضل ہوا ہے کیا اللہ شکر گزاروں سے خوب واقف نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,54,'6:54','اور جب تمھارے پاس وہ لوگ آئیں جو ہماری آیات پر ایمان لائے ہیں تو ان سے کہو کہ تم پر سلامتی ہو تمھارے رب نے اپنے اوپر رحمت لکھ لی ہے بے شک تم میں سے جو کوئی نادانی سے برائی کر بیٹھے پھر اس کے بعد وہ توبہ کرے اور اصلاح کرلے تو اللہ بخشنے والا مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,55,'6:55','اور اس طرح ہم اپنی نشانیاں کھول کر بیان کرتے ہیں ، اور تاکہ مجرمین کا طریقہ ظاہر ہوجائے');
INSERT INTO "ur_wahid_uddin" VALUES(6,56,'6:56','کہو، مجھے اس سے روکا گیا ہے کہ میں ان کی عبادت کروں جن کو تم اللہ کے سوا پکارتے ہو کہو، میں تمھاری خواہشوں کی پیروی نہیں کرسکتا اگر میں ایسا کروں تو میں بے راہ ہو جاؤں گا اور میں راہ پانے والوں میں سے نہ رہوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(6,57,'6:57','کہو میں اپنے رب کی طرف سے ایک روشن دلیل پر ہوں اور تم نے اس کو جھٹلادیا ہے وہ چیز میرے پاس نہیں ہے جس کے لیے تم جلدی کر رہے ہو فیصلہ کا اختیار صرف اللہ کو ہے وہی حق کو بیان کرتا ہے اور وہ بہترین فیصلہ کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,58,'6:58','کہو، اگر وہ چیز میرے پاس ہوتی جس کے ليے تم جلدی کر رہے ہو تو میرے اور تمھارے درمیان معاملے کا فیصلہ ہوچکا ہوتا، اور اللہ خوب جانتا ہے ظالموں کو');
INSERT INTO "ur_wahid_uddin" VALUES(6,59,'6:59','اور اسی کے پاس غیب کی کنجیاں ہیں ، اس کے سوا اس کو کوئی نہیں جانتا اللہ جانتا ہے جو کچھ خشکی میں اور سمندرمیں ہے اور درخت سے گرنے والا کوئی پتہ نہیں جس کا اس کو علم نہ ہو اور زمین کی تاریکیوں میں کوئی دانہ نہیں گرتا اور نہ کوئی تر اور خشک چیزمگر سب ایک کھلی ہوئی کتاب میں درج ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,60,'6:60','اور وہی ہے جو رات میں تم کو وفات دیتا ہے اور دن کو جو کچھ تم کرتے ہو اس کو جانتا ہے پھر تم کو اٹھادیتا ہے اس میں تاکہ مقررہ مدت پوری ہوجائے پھر اسی کی طرف تمھاری واپسی ہے پھر وہ تم کو باخبر کردے گا اس سے جو تم کرتے رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,61,'6:61','اور وہ غالب ہے اپنے بندوں کے اوپر اور وہ تمھارے اوپر نگراں بھیجتا ہے یہاں تک کہ جب تم میں سے کسی کی موت کا وقت آجاتا ہے تو ہمارے بھیجے ہوئے فرشتے اس کی روح قبض کرلیتے ہیں اور وہ کوتاہی نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,62,'6:62','پھر سب، اللہ، اپنے مالکِ حقیقی کی طرف واپس لائے جائیں گے سن لو، حکم اسی کاہے اور وہ بہت جلد حساب لینے والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,63,'6:63','کہو، کون تم کو نجات دیتا ہے خشکی اور سمندر کی تاریکیوں سے، تم اس کو پکارتے ہو عاجزی سے اور چپکے چپکے کہ اگر خدا نے ہم کو نجات دے دی اس مصیبت سے تو ہم اس کے شکر گزار بندوں میں سے بن جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,64,'6:64','کہو، خدا ہی تم کو نجات دیتا ہے اس سے اور ہر تکلیف سے، پھر بھی تم شرک کرنے لگتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,65,'6:65','کہو، خدا قادر ہے اس پر کہ وہ تم پر کوئی عذاب بھیج دے تمھارے اوپر سے یا تمھارے پیروں کے نیچے سے یا تم کو گروہ گروہ کر کے ایک کو دوسرے کی طاقت کا مزہ چکھا دے دیکھو، ہم کس طرح دلائل مختلف پہلوؤں سے بیان کرتے ہیں تاکہ وہ سمجھیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,66,'6:66','اور تمھاری قوم نے اس کو جھٹلادیا ہے حالاں کہ وہ حق ہے کہو، میں تمھارے اوپر داروغہ نہیں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(6,67,'6:67','ہر خبر کے ليے ایک وقت مقرر ہے اور تم جلد ہی جان لو گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,68,'6:68','اور جب تم ان لوگوں کو دیکھو جو ہماری آیتوں میں عیب نکالتے ہیں تو ان سے الگ ہوجاؤ، یہاں تک کہ وہ کسی اور بات میں لگ جائیں اور اگر کبھی شیطان تم کو بھلادے تو یاد آنے کے بعد ایسے بے انصاف لوگوں کے پاس نہ بیٹھو');
INSERT INTO "ur_wahid_uddin" VALUES(6,69,'6:69','اور جولوگ اللہ سے ڈرتے ہیں ، ان پر ان کے حساب میں سے کسی چیزکی ذمہ داری نہیں ، البتہ یاد دلانا ہے شاید کہ وہ بھی ڈریں');
INSERT INTO "ur_wahid_uddin" VALUES(6,70,'6:70','ان لوگوں کو چھوڑو جنھوں نے اپنے دین کو کھیل تماشا بنا رکھا ہے اور جن کو دنیا کی زندگی نے دھوکے میں ڈال رکھا ہے اور قرآن کے ذریعہ نصیحت کرتے رہو تاکہ کوئی شخص اپنے کيے میں گرفتار نہ ہوجائے، اس حال میں کہ اللہ سے بچانے والا کوئی مدد گار اور سفارشی اس کے ليے نہ ہو اگر وہ دنیا بھر کا معاوضہ دے تب بھی وہ قبول نہ کیا جائے یہی لوگ ہیں جو اپنے کيے میں گرفتار ہوگئے ان کے ليے کھولتا ہواپانی پینے کے ليے ہوگا اور درد ناک سزا ہوگی اس ليے کہ وہ کفر کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,71,'6:71','کہو، کیا ہم اللہ کو چھوڑ کر ان کو پکاریں جو نہ ہم کو نفع دے سکتے اور نہ ہم کو نقصان پہنچاسکتے اور کیا ہم الٹے پاؤں پھر جائیں بعد اس کے کہ اللہ ہم کو سیدھا راستہ دکھاچکا ہے، اس شخص کی مانند جس کو شیطانوں نے بیابان میں بھٹکادیا ہو اور وہ حیران پھر رہا ہو، اس کے ساتھی اس کو سیدھے راستہ کی طرف بلارہے ہوں کہ ہمارے پاس آجاؤ کہو کہ رہنمائی تو صرف اللہ کی رہنمائی ہے اور ہم کو حکم ملا ہے کہ ہم اپنے آپ کو عالَم کے رب کے حوالے کردیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,72,'6:72','اور یہ کہ نماز قائم کرو اور اللہ سے ڈرو اور وہی ہے جس کی طرف تم سمیٹے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,73,'6:73','اور وہی ہے جس نے آسمانوں اور زمین کو حق کے ساتھ پیدا کیا ہے اور جس دن وہ کہے گا کہ ہوجا تو وہ ہوجائے گا اس کی بات حق ہے اور اسی کی حکومت ہوگی اس روز جب صور پھونکا جائے گا وہ غائب و حاضر کا عالم اور حکیم ، خبیر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,74,'6:74','اور جب ابراہیم نے اپنے باپ آزر سے کہا کہ کیا تم بتوں کو معبود مانتے ہو میں تم کو اور تمھاری قوم کوکھلی ہوئی گمراہی میں دیکھتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(6,75,'6:75','اور اسی طرح ہم نے ابراہیم کو دکھادی آسمانوں اور زمین کی حکومت، اور تاکہ اس کو یقین آجائے');
INSERT INTO "ur_wahid_uddin" VALUES(6,76,'6:76','پھر جب رات نے اس پر اندھیرا کرلیا، اس نے ایک تارہ کو دیکھا کہا یہ میرا رب ہےپھر جب وہ ڈوب گیا تو اس نے کہا میں ڈوب جانے والوں کو دوست نہیں رکھتا');
INSERT INTO "ur_wahid_uddin" VALUES(6,77,'6:77','پھر جب اس نے چاند کو چمکتے ہوئے دیکھا تو کہا کہ یہ میرا رب ہے، پھر جب وہ ڈوب گیا تو اس نے کہا اگر میرا رب مجھ کو ہدایت نہ کرے تو میں گمراہ لوگوں میں سے ہوجاؤں');
INSERT INTO "ur_wahid_uddin" VALUES(6,78,'6:78','پھر جب سورج کو چمکتے ہوئے دیکھا تو کہا کہ یہ میرا رب ہےیہ سب سے بڑا ہے پھر جب وہ ڈوب گیا تو اس نے اپنی قوم سے کہا کہ اے لوگو، میں اس شرک سے بَری ہوں جو تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,79,'6:79','میں نے اپنا رخ یک سو ہوکر ا س کی طرف کرلیا جس نے آسمانوں اور زمین کو پیدا کیا ہے اور میں شرک کرنے والوں میں سے نہیں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(6,80,'6:80','اور اس کی قوم اس سے جھگڑنے لگی اس نے کہا کیا تم اللہ کے معاملہ میں مجھ سے جھگڑتے ہو، حالاں کہ اس نے مجھے راہ دکھادی ہے اور میں ان سے نہیں ڈرتا جن کو تم اللہ کا شریک ٹھہراتے ہو مگر یہ کہ کوئی بات میرا رب ہی چاہے میرے رب کا علم ہر چیز پر چھایا ہوا ہے، کیا تم نہیں سوچتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,81,'6:81','اور میں کیوں کر ڈروں تمھارے شریکوں سے جب کہ تم اللہ کے ساتھ ان چیزوں کو خدائی میں شریک ٹھہراتے ہوئے نہیں ڈرتے جن کے ليے اس نے تم پر کوئی سند نہیں اتاری اب دونوں فریقوں میں سے امن کا زیادہ مستحق کون ہے، اگر تم جانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,82,'6:82','جو لوگ ایمان لائے اور نہیں ملایا انھوں نے اپنے ایمان میں کوئی نقصان، انھیں کے ليے امن ہے اور وہی سیدھی راہ پر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,83,'6:83','یہ ہے ہماری دلیل جو ہم نے ابراہیم کو اس کی قوم کے مقابلہ میں دی ہم جس کے درجے چاہتے ہیں بلند کردیتے ہیں بے شک تمھارارب حکیم اور علیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,84,'6:84','اور ہم نے ابراہیم کو اسحاق اور یعقوب عطا کيے، ہر ایک کوہم نے ہدایت دی اور نوح کو بھی ہم نے ہدایت دی اس سے پہلے اور اس کی نسل میں سے داؤد اور سلیمان اور ایوب اور یوسف اور موسیٰ اور ہارون کو بھی اور ہم نیکوں کو اسی طرح بدلہ دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,85,'6:85','اور زکریا اور یحییٰ اور عیسیٰ اور الیاس کو بھی، ان میں سے ہر ایک صالح تھا');
INSERT INTO "ur_wahid_uddin" VALUES(6,86,'6:86','اور اسماعیل اور الیسع اور یونس اور لوط کو بھی اور ان میں سے ہر ایک کو ہم نے دنیا والوں پر فضیلت عطا کی');
INSERT INTO "ur_wahid_uddin" VALUES(6,87,'6:87','اور ان کے باپ دادوں اور ان کی اولاد اور ان کے بھائیوں میں سے بھی، اور ان کوہم نے چن لیا اور ہم نے سیدھے راستہ کی طرف ان کی رہنمائی کی');
INSERT INTO "ur_wahid_uddin" VALUES(6,88,'6:88','یہ اللہ کی ہدایت ہے، وہ اس سے سرفراز کرتا ہے اپنے بندوں میں سے جس کو چاہتا ہے اور اگر وہ شرک کرتے تو ضائع ہوجاتا جو کچھ انھوں نے کیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(6,89,'6:89','یہ لوگ ہیں جن کو ہم نے کتاب اور حکمت اور نبوت عطا کی پس اگر یہ مکہ والے اس کا انکار کردیں تو ہم نے اس کے ليے ایسے لوگ مقرر کردئے ہیں جو اس کے منکر نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,90,'6:90','یہی لوگ ہیں جن کو اللہ نے ہدایت بخشی، پس تم بھی ان کے طریقہ پر چلوکہہ دو، میں اس پر تم سے کوئی معاوضہ نہیں مانگتا یہ تو بس ایک نصیحت ہے دنیا والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(6,91,'6:91','اور انھوں نے اللہ کا بہت غلط اندازہ لگایا جب انھوں نے کہاکہ اللہ نے کسی انسان پر کوئی چیز نہیں اتاری کہو کہ وہ کتاب کس نے اتاری تھی جس کو لے کر موسیٰ آئے تھے، وہ روشن تھی اور رہنمائی تھی لوگوں کے واسطے، جس کو تم نے ورق ورق کر رکھا ہے کچھ کو ظاہر کرتے ہو اور بہت کچھ چھپا جاتے ہو اور تم کو وہ باتیں سکھائیں جن کو نہ جانتے تھے تم اور نہ تمھارے باپ دادا کہو کہ اللہ نے اتاری پھر ان کو چھوڑ دو کہ اپنی کج بحثیوں میں کھیلتے رہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,92,'6:92','اور یہ ایک کتاب ہے جو ہم نے اتاری ہے، برکت والی ہے، تصدیق کرنے والی ان کي جو اس سے پہلے ہیں اور تاکہ توڈرائے مکہ والوں کو اور اس کے آس پاس والوں کو اور جو آخرت پر یقین رکھتے ہیں ، وہی اس پر ایمان لائیں گے اور وہ اپنی نماز کی حفاظت کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,93,'6:93','اور اس سے بڑھ کر ظالم کون ہوگا جو اللہ پر جھوٹی تہمت باندھے یا کہے کہ مجھ پر وحی آئی ہے، حالاں کہ اس پر کوئی وحی نازل نہ کی گئی ہو اور کہے کہ جیسا کلام خدا نے اتارا ہے میں بھی اتاروں گا اور کاش تم اس وقت دیکھو جب کہ یہ ظالم موت کی سختیوں میں ہوں گے اور فرشتے ہاتھ بڑھا رہے ہوں گے کہ لاؤ اپنی جانیں نکالو آج تم کو ذلت کا عذاب دیا جائے گا اس سبب سے کہ تم اللہ پر جھوٹی باتیں کہتے تھے اور تم اللہ کی نشانیوں سے تکبر کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,94,'6:94','اور تم ہمارے پاس اکیلے اکیلے آگئے جیسا کہ ہم نے تم کو پہلی مرتبہ پیدا کیا تھا اور جو کچھ اسباب ہم نے تم کو دیا تھا سب تم پیچھے چھوڑ آئے اور ہم تمھارے ساتھ ان سفارش والوں کو بھی نہیں دیکھتے جن کے متعلق تم سمجھتے تھے کہ تمھارا کام بنانے میں ان کا بھی حصہ ہے تمھارا رشتہ ٹوٹ گیا اور تم سے جاتے رہے وہ دعوے جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,95,'6:95','بے شک اللہ دانے اور گٹھلی کو پھاڑنے والا ہے وہ جان دار کو بے جان سے نکالتا ہے اور وہی بے جان کو جان دار سے نکالنے والا ہے وہی تمھارا اللہ ہے، پھر تم کدھر بہکے چلے جارہے رہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,96,'6:96','وہی برآمد کرنے والا ہے صبح کا اور اس نے رات کو سکون کا وقت بنایا اور سورج اور چاند کو حساب سے رکھا ہے یہ ٹھہرایا ہوا ہے بڑے غلبہ والے کا، بڑے علم والے کا');
INSERT INTO "ur_wahid_uddin" VALUES(6,97,'6:97','اور وہی ہے جس نے تمھارے ليے ستارے بنائے تاکہ تم ان کے ذریعہ سے خشکی اور تری کے اندھیروں میں راہ پاؤ بے شک ہم نے دلائل کھول کر بیان کردئے ہیں ان لوگوں کے ليے جو جاننا چاہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,98,'6:98','اور وہی ہے جس نے تم کو پیدا کیا ایک جان سے، پھر ہر ایک کے ليے ایک ٹھکانہ ہے اور ہر ایک کے ليے اس کے سونپے جانے کی جگہ ہم نے دلائل کھول کر بیان کردئے ہیں ان لوگوں کے ليے جو سمجھیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,99,'6:99','اور وہی ہے جس نے آسمان سے پانی برسایا، پھر ہم نے اس سے نکالی اگنے والی ہر چیزپھر ہم نے اس سے سرسبز شاخ نکالی جس سے ہم تہ بہ تہ دانے پیدا کردیتے ہیں اور کھجور کے گابھے میں سے پھل کے گچھے جھکے ہوئے اور باغ انگو رکے اور زیتون کے اور انار کے، آپس میں ملتے جلتے اور جدا جدابھی ہر ایک کے پھل کو دیکھو جب وہ پھلتا ہے اور اس کے پکنے کو دیکھو جب وہ پکتاہے بے شک ان کے اندر نشانیاں ہیں ان لوگوں کے ليے جو ایمان کی طلب رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,100,'6:100','اور انھوں نے جنات کو اللہ کا شریک قرار دیا حالاں کہ اسی نے ان کو پیدا کیا ہے اور بے جانے بوجھے اس کے ليے بیٹے اور بیٹیاں تراشیں پاک اور برتر ہے وہ ان باتوں سے جو یہ بیان کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,101,'6:101','وہ آسمانوں اور زمین کا موجد ہے اس کا کوئی بیٹا کیسے ہوسکتا ہے جب کہ اس کی کوئی بیوی نہیں اور اس نے ہر چیزکو پیدا کیا ہے اور وہ ہر چیز سے باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,102,'6:102','یہ ہے اللہ تمھارا رب اس کے سوا کوئی معبود نہیں وہی ہر چیز کا خالق ہے، پس تم اسی کی عبادت کرو اور وہ ہر چیزکا کارساز ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,103,'6:103','اس کو نگاہیں نہیں پاتیں مگر وہ نگاہوں کو پالیتا ہے وہ بڑا باریک بیں اور بڑا باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,104,'6:104','اب تمھارے پاس تمھارے رب کی طرف سے بصیرت کی روشنیاں آچکی ہیں پس جو بینائی سے کام لے گا وہ اپنے ہی ليے، اور جو اندھا بنے گا وہ خود نقصان اٹھائے گا اور میں تمھارے اوپر کوئی نگراں نہیں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(6,105,'6:105','اور اس طرح ہم اپنی دلیلیں مختلف طریقوں سے بیان کرتے ہیں اور تاکہ وہ کہیں کہ تم نے پڑھ دیا اور تاکہ ہم اچھی طرح کھول دیں ان لوگوں کے ليے جوجاننا چاہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,106,'6:106','تم بس اس چیزکی پیروی کرو جو تمھارے رب کی طرف سے تم پر وحی کی جارہی ہے اس کے سوا کوئی معبود نہیں اور مشرکوں سے اعراض کرو');
INSERT INTO "ur_wahid_uddin" VALUES(6,107,'6:107','اور اگر اللہ چاہتا تو یہ لوگ شرک نہ کرتے اور ہم نے تم کو ان کے اوپر نگراں نہیں بنایا ہے اور نہ تم ان پر مختار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,108,'6:108','اور اللہ کے سوا جن کو یہ لوگ پکارتے ہیں ، ان کو گالی نہ دو، ورنہ یہ لوگ حد سے گزر کر جہالت کی بنا پر اللہ کو گالیاں دینے لگیں گےاسی طرح ہم نے ہر گروہ کی نظر میں اس کے عمل کو خوش نما بنادیا ہے پھر ان سب کو اپنے رب کی طرف پلٹنا ہے اس وقت اللہ انھیں بتادے گا جووہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,109,'6:109','اور یہ لوگ اللہ کی قسم بڑے زور سے کھاکر کہتے ہیں کہ اگر ان کے پاس کوئی نشانی آجائے تو وہ ضرور اس پر ایمان لے آئیں کہہ دو کہ نشانیاں تو اللہ کے پاس ہیں اور تمھیں کیاخبر کہ اگر نشانیاں آجائیں تب بھی یہ ایمان نہیں لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,110,'6:110','اور ہم ان کے دلوں اور ان کی نگاہوں کو پھیر دیں گے جیسا کہ یہ لوگ اس کے اوپر پہلی بار ایمان نہیں لائے اور ہم ان کو ان کی سرکشی میں بھٹکتا ہوا چھوڑدیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,111,'6:111','اور اگر ہم ان پر فرشتے اتار دیتے اور مردے ان سے باتیں کرتے اور ہم ساری چیزیں ان کے سامنے اکٹھا کردیتے تب بھی یہ لوگ ایمان لانے والے نہ تھے، الا یہ کہ اللہ چاہے مگر ان میں سے اکثر لوگ نادانی کی باتیں کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,112,'6:112','اور اسی طرح ہم نے شریر آدمیوں اور شریر جنوں کو ہر نبی کا دشمن بنادیا وہ ایک دوسرے کو پر فریب باتیں سکھاتے ہیں دھوکا دینے کے ليے اور اگر تیرا رب چاہتا تو وہ ایسا نہ کرسکتے پس تم انھیں چھوڑ دو کہ وہ جھوٹ باندھتے رہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,113,'6:113','اور ایسا اس ليے ہے کہ اس کی طرف ان لوگوں کے دل مائل ہوں جو آخرت پر یقین نہیں رکھتے اور تاکہ وہ اس کو پسند کریں اور تاکہ جو کمائی انھیں کرنی ہے وہ کرلیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,114,'6:114','کیا میں اللہ کے سوا کسی اور کو منصف بناؤں حالاں کہ اس نے تمھاری طرف واضح کتاب اتاری ہے اور جن لوگوں کو ہم نے پہلے کتاب دی تھی، وہ جانتے ہیں کہ یہ تیرے رب کی طرف سے اتاری گئی ہے واقعیت کے ساتھ پس تم نہ ہو شک کرنے والوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(6,115,'6:115','اور تمھارے رب کی بات پوری سچی ہے اور انصاف کی کوئی بدلنے والا نہیں اس کی بات کو اور وہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,116,'6:116','اور اگر تم لوگوں کی اکثریت کے کہنے پر چلو جو زمین میں ہیں تو وہ تم کو خداکے راستہ سے بھٹکادیں گے وہ محض گمان کی پیروی کرتے ہیں اور قیاس آرائیاں کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,117,'6:117','بے شک تمھارا رب خوب جانتا ہے ان کو جو اُس کے راستہ سے بھٹکے ہوئے ہیں اور خوب جانتا ہے ان کو جو راہ پائے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,118,'6:118','پس کھاؤ اس جانور میں سے جس پر اللہ کا نام لیا جائے، اگر تم اس کی آیات پر ایمان رکھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,119,'6:119','اور کیا وجہ ہے کہ تم اس جانور میں سے نہ کھاؤ جس پر اللہ کا نام لیا گیا ہے، حالاں کہ خدا نے تفصیل سے بیان کردی ہیں وہ چیزیں جن کو اس نے تم پر حرام کیا ہے، سوا اس کے کہ اس کے ليے تم مجبور ہوجاؤ اور یقیناً بہت سے لوگ اپنی خواہشات کی بنا پر گم راہ کرتے ہیں بغیر کسی علم کے بے شک تمھارا رب خوب جانتا ہے حد سے نکل جانے والوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(6,120,'6:120','اور تم گناہ کے ظاہر کو بھی چھوڑ دو اور اس کے باطن کو بھی جو لوگ گناہ کما رہے ہیں ، ان کو جلد بدلہ مل جائے گا اس کا جو وہ کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,121,'6:121','اور تم اس جانور میں سے نہ کھاؤ جس پر اللہ کا نام نہ لیا گیا ہو یقیناً یہ بے حکمی ہے اور شیاطین القا کر رہے ہیں اپنے ساتھیوں کو تاکہ وہ تم سے جھگڑیں اور اگر تم ان کا کہا مانوگے تو تم بھی مشرک ہوجاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,122,'6:122','کیا وہ شخص جو مردہ تھا پھر ہم نے اس کو زندگی دی اور ہم نے اس کو ایک روشنی دی کہ اس کے ساتھ وہ لوگوں میں چلتا ہے، وہ اس شخص کی طرح ہوسکتا ہے جو تاریکیوں میں پڑا ہے، وہ اس سے نکلنے والا نہیں اس طرح منکروں کی نظر میں ان کے اعمال خوش نما بنادئے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,123,'6:123','اور اس طرح ہر بستی میں ہم نے گنہ گاروں کے سردار رکھ دئے ہیں کہ وہ وہاں حیلے کریں حالاں کہ وہ جو حیلہ کرتے ہیں اپنے ہی خلاف کرتے ہیں ، مگر وہ اس کو نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,124,'6:124','اور جب ان کے پاس کوئی نشاني آتي ہے تو وہ کہتے ہیں کہ ہم ہر گز نہ مانیں گے جب تک ہم کو بھی وہی نہ دیا جائے جو خدا کے پیغمبروں کو دیا گیا اللہ ہی بہتر جانتا ہے کہ وہ اپنی پیغمبری کس کو بخشے جو لوگ مجرم ہیں ضرور ان کو اللہ کے یہاں ذلت نصیب ہوگی اور سخت عذاب بھی، اس وجہ سے کہ وہ مکر کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,125,'6:125','اللہ جس کو چاہتا ہے کہ ہدایت دے تو اس کا سینہ اسلام کے ليے کھول دیتا ہے اور جس کو چاہتا ہے کہ گمراہ کرے تو اس کے سینہ کو بالکل تنگ کردیتا ہے، جیسے اس کو آسمان میں چڑھنا پڑ رہا ہو اس طرح اللہ گندگی ڈال دیتا ہے ان لوگوں پر جو ایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,126,'6:126','اور یہی تمھارے رب کا سیدھا راستہ ہے ہم نے واضح کردی ہیں نشانیاں غور کرنے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(6,127,'6:127','انھیں کے ليے سلامتی کا گھر ہے ان کے رب کے پاس اور وہ ان کا مدد گار ہے اس عمل کے سبب سے جو وہ کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,128,'6:128','اور جس دن اللہ ان سب کو جمع کرے گا، اے جنوں کے گروہ، تم نے بہت سے لے ليے انسانوں میں سے اور انسانوں میں سے ان کے ساتھی کہیں گے اے ہمارے رب، ہم نے ایک دوسرے کو استعمال کیا اور ہم پہنچ گئے اپنے اس وعدہ کو جو تو نے ہمارے ليے مقرر کیا تھا خدا کہے گا اب تمھارا ٹھکانا آگ ہے، ہمیشہ اس میں رہو گے مگر جو اللہ چاہے بے شک تمھارا رب حکمت والا، علم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,129,'6:129','اور اسی طرح ہم ساتھ ملادیں گے گنہ گاروں کو ایک دوسرے سے، بہ سبب ان اعمال کے جو وہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,130,'6:130','اے جنوں اور انسانوں کے گروہ، کیا تمھارے پاس تمھیں میں سے پیغمبر نہیں آئے جو تم کو میری آیتیں سناتے اور تم کو اس دن کے پیش آنے سے ڈراتے تھے وہ کہیں گے ہم خود اپنے خلاف گواہ ہیں اور ان کو دنیا کی زندگی نے دھوکے میں رکھا اور وہ اپنے خلاف خود گواہی دیں گے کہ بے شک ہم منکر تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,131,'6:131','یہ اس وجہ سے کہ تمھارا رب بستیوں کو ان کے ظلم پر اس حال میں ہلاک کرنے والا نہیں کہ وہا ں کے لوگ بے خبر ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(6,132,'6:132','اور ہر شخص کا درجہ ہے اس کے عمل کے لحاظ سے، اور تمھارا رب لوگوں کے اعمال سے بے خبر نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,133,'6:133','اور تمھارا رب بے نیاز، رحمت والا ہے اگر وہ چاہے تو تم سب کو اٹھالے اور تمھارے بعد جس کو چاہے تمھاری جگہ لے آئے جس طرح اس نے تم کو پیدا کیا دوسروں کی نسل سے');
INSERT INTO "ur_wahid_uddin" VALUES(6,134,'6:134','جس چیز کا تم سے وعدہ کیا جارہا ہے، وہ آکر رہے گی اور تم خدا کو عاجز نہیں کرسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,135,'6:135','کہو، اے لوگو تم عمل کرتے رہو اپنی جگہ پر، میں بھی عمل کر رہا ہوں تم جلد ہی جان لو گے کہ انجام کار کس کے حق میں بہتر ہوتا ہے یقیناً ظالم کبھی فلاح نہیں پاسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(6,136,'6:136','اور خدا نے جو کھیتی اور چوپائے پیدا کيے، اس میں سے انھوں نے خدا کا کچھ حصہ مقرر کیا ہے پس وہ کہتے ہیں کہ یہ حصّہ اللہ کا ہے، ان کے گمان کے مطابق، اور یہ حصہ ہمارے شریکوں کا ہے پھر جو حصہ ان کے شریکوں کا ہوتا ہے وہ تو اللہ کو نہیں پہنچتا اور جو حصہ اللہ کے ليے ہے وہ ان کے شریکوں کو پہنچ جاتا ہے کیسا برا فیصلہ ہے جو یہ لوگ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,137,'6:137','اور اس طرح بہت سے مشرکوں کی نظر میں ان کے شریکوں نے اپنی اولاد کے قتل کو خوش نما بنادیا ہے، تاکہ ان کو برباد کریں اور ان پر ان کے دین کو مشتبہ بنادیں اور اگر اللہ چاہتا تو وہ ایسا نہ کرتے پس ان کو چھوڑ دو کہ وہ اپنی افترا میں لگے رہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,138,'6:138','اور کہتے ہیں کہ یہ جانور اور یہ کھیتی ممنوع ہے، انھیں کوئی نہیں کھاسکتاسوا اس کے جس کو ہم چاہیں ، اپنے گمان کے مطابق اور فلاں چوپائے ہیں کہ ان کی پیٹھ حرام کردی گئی ہے اور کچھ چوپائے ہیں جن پر وہ اللہ کا نام نہیں لیتے یہ سب انھوں نے اللہ پر افترا کیا ہے اللہ جلد ان کو اس افترا کا بدلہ دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(6,139,'6:139','اور کہتے ہیں کہ جو فلاں قسم کے جانوروں کے پیٹ میں ہے، وہ ہمارے مردوں کے ليے خاص ہے اور وہ ہماری عورتوں کے ليے حرام ہے اگر وہ مردہ ہو تو اس میں سب شریک ہیں اللہ جلد ان کو اس کہنے کی سزا دے گا بے شک اللہ حکمت والا، علم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,140,'6:140','وہ لوگ گھاٹے میں پڑگئے جنھوں نے اپنی اولاد کو قتل کیا نادانی سے بغیر کسی علم کے اور انھوں نے اس رزق کو حرام کرلیا جو اللہ نے ان کو دیا تھا، اللہ پر بہتان باندھتے ہوئے وہ گمراہ ہوگئے اور وہ ہدایت پانے والے نہ بنے');
INSERT INTO "ur_wahid_uddin" VALUES(6,141,'6:141','اور وہ اللہ ہی ہے جس نے باغ پیدا كيے، کچھ ٹٹیوں پر چڑھائے جاتے ہیں اور کچھ نہیں چڑھائے جاتے اور کھجور کے درخت اور کھیتی کہ اس کے کھانے کی چیزیں مختلف ہوتی ہیں اور زیتون اور انار، باہم ملتے جلتے بھی اور ایک دوسرے سے مختلف بھی کھاؤ ان کی پیداوار جب کہ وہ پھلیں اور اللہ کا حق ادا کرو اس کے کاٹنے کے دن اور اسراف نہ کرو، بے شک اللہ اسراف کرنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(6,142,'6:142','اور اس نے مویشیوں میں بوجھ اٹھانے والے پیدا كيے اور زمین سے لگے ہوئے بھی کھاؤ ان چیزوں میں سے جو اللہ نے تمھیں دی ہیں اور شیطان کی پیروی نہ کرو، وہ تمھارا کھلا ہوا دشمن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,143,'6:143','اللہ نے آٹھ جوڑے پیدا کيے دو بھیڑ کی قسم سے اور دوبکری کی قسم سے پوچھو کہ دونوں نر اللہ نے حرام کيے ہیں یا دونوں مادہ یا وہ بچے جو بھیڑوں اور بکریوں کے پیٹ میں ہوں مجھے دلیل کے ساتھ بتاؤ اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,144,'6:144','اور اسی طرح دو اونٹ کی قسم سے ہیں اور دو گائے کی قسم سے پوچھو کہ دونوں نر اللہ نے حرام کيے ہیں یا دونوں مادہ یا وہ بچے جو اونٹنی اور گائے کے پیٹ میں ہوں کیا تم اس وقت حاضر تھے جب اللہ نے تم کو اس کا حکم دیا تھا پھر اس سے زیادہ ظالم کون ہے جو اللہ پر جھوٹ بہتان باندھے تاکہ وہ لوگوں کو بہکادے بغیر علم کے بے شک اللہ ظالموں کو راہ نہیں دکھاتا');
INSERT INTO "ur_wahid_uddin" VALUES(6,145,'6:145','کہو، مجھ پر جو وحی آئی ہے، اس میں تو میں کوئی چیز نہیں پاتا جو حرام ہو کسی کھانے والے پر سوا اس کے کہ وہ مردار ہو یا بہایا ہوا خون ہو یا سور کا گوشت ہو کہ وہ ناپاک ہے، یا ناجائز ذبیحہ جس پر اللہ کے سوا کسی اور کا نام پکارا گیا ہو لیکن جو شخص بھوک سے بے اختیار ہوجائے، نہ نافرمانی کرے اور نہ زیادتی کرے، تو تیرا رب بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(6,146,'6:146','اور یہود پر ہم نے سارے ناخن والے جانور حرام کيے تھے اور گائے اور بکری کی چربی حرام کی سوا اس کے جوان کی پیٹھ یا انتڑیوں سے لگی ہو یا کسی ہڈی سے ملی ہوئی ہو یہ سزا دی تھی ہم نے ان کو ان کی سرکشی پر اور یقیناً ہم سچے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,147,'6:147','پس اگر وہ تم کو جھٹلائیں تو کہہ دو کہ تمھارا رب بڑی وسیع رحمت والا ہے اور اس کا عذاب مجرم لوگوں سے ٹل نہیں سکتا');
INSERT INTO "ur_wahid_uddin" VALUES(6,148,'6:148','جنھوں نے شرک کیا، وہ کہیں گے کہ اگر اللہ چاہتا تو ہم شرک نہ کرتے اور نہ ہمارے باپ دادا کرتے اور نہ ہم کسی چیز کو حرام کرلیتے اسی طرح جھٹلایا ان لوگوں نے بھی جو ان سے قبل ہوئے ہیں یہاں تک کہ انھوں نے ہمارا عذاب چکھا کہو کیا تمھارے پاس کوئی علم ہے جس کو تم ہمارے سامنے پیش کرو تم تو صرف گمان کی پیروی کر رہے ہو اور محض اٹکل سے کام لیتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,149,'6:149','کہو کہ پوری حجت تو اللہ کی ہے اور اگر اللہ چاہتا تو وہ تم سب کو ہدایت دے دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(6,150,'6:150','کہو کہ اپنے گواہوں کو لاؤ جو اس پر گواہی دیں کہ اللہ نے ان چیزوں کو حرام ٹھہرایا ہے اگر وہ جھوٹی گواہی دے بھی دیں تو تم ان کے ساتھ گواہی نہ دینا، اور تم ان لوگوں کی خواہشوں کی پیروی نہ کرو جنھوں نے ہماری نشانیوں کو جھٹلایا اور جو آخرت پر ایمان نہیں رکھتے اور دوسروں کو اپنے رب کا ہم سر ٹھہراتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,151,'6:151','کہو، آؤ میں سناؤں وہ چیزیں جو تمھارے رب نے تم پر حرام کی ہیں یہ کہ تم اس کے ساتھ کسی چیز کو شریک نہ کرو اور ماں باپ کے ساتھ نیک سلوک کرو اور اپنی اولاد کو مفلسی كي وجه سے قتل نہ کرو ہم تم کو بھی روزی دیتے ہیں اور ان کو بھی اور بے حیائی کے کام کے پاس نہ جاؤ خواہ وہ ظاہر ہو یا پوشیدہ اور جس جان کو اللہ نے حرام ٹھہرایا اس کو نہ مارو مگر حق پر یہ باتیں ہیں جن کی خدا نے تمھیں ہدایت فرمائی ہے تاکہ تم عقل سے کام لو');
INSERT INTO "ur_wahid_uddin" VALUES(6,152,'6:152','اور یتیم کے مال کے پاس نہ جاؤ مگر ایسے طریقے سے جو بہتر ہو یہاں تک کہ وہ اپنی جوانی کو پہنچ جائے اور ناپ تول میں پورا انصاف کرو ہم کسی کے ذمہ وہی چیز لازم کرتے ہیں جس کی اسے طاقت ہو اور جب بولو تو انصاف کی بات بولو خواہ معاملہ اپنے رشتہ دار ہی کا ہو اور اللہ کے عہد کو پورا کرو یہ چیزیں ہیں جن کا اللہ نے تمھیں حکم دیا ہے تاکہ تم نصیحت پکڑو');
INSERT INTO "ur_wahid_uddin" VALUES(6,153,'6:153','اور اللہ نے حکم دیا کہ یہی میری سیدھی شاہراہ ہے پس اسی پر چلو اور دوسرے راستوں پر نہ چلو کہ وہ تم کو اللہ کے راستہ سے جدا کردیں گے یہ اللہ نے تم کو حکم دیا ہے تاکہ تم بچتے رہو');
INSERT INTO "ur_wahid_uddin" VALUES(6,154,'6:154','پھر ہم نے موسیٰ کو کتاب دی نیک کام کرنے والوں پر اپنی نعمت پوری کرنے کے ليے اور ہر بات کی تفصیل اور ہدایت اور رحمت، تاکہ وہ اپنے رب کے ملنے کا یقین کریں');
INSERT INTO "ur_wahid_uddin" VALUES(6,155,'6:155','اور اسی طرح ہم نے یہ کتاب اتاری ہے، ایک برکت والی کتاب پس اس پر چلو اور اللہ سے ڈرو تاکہ تم پر رحمت کی جائے');
INSERT INTO "ur_wahid_uddin" VALUES(6,156,'6:156','اس ليے کہ تم یہ نہ کہنے لگو کہ کتاب تو ہم سے پہلے کے دو گروہوں کو دی گئی تھی اور ہم ان کو پڑھنے پڑھانے سے بے خبر تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,157,'6:157','یا کہو کہ اگر ہم پر کتاب اتاری جاتی تو ہم ان سے بہتر راہ پر چلنے والے ہوتے پس آچکی تمھارے پاس تمھارے رب کی طرف سے ایک روشن دلیل اور ہدایت اور رحمت تو اس سے زیادہ ظالم کون ہوگا جو اللہ کی نشانیوں کو جھٹلائے اور ان سے منہ موڑے جو لوگ ہماری نشانیوں سے اعراض کرتے ہیں ہم ان کو ان کے اعراض کی پاداش میں بہت برا عذاب دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(6,158,'6:158','یہ لوگ کیا اس کے منتظر ہیں کہ ان کے پاس فرشتے آئیں یا تمھارا رب آئے یا تمھارے رب کی نشانیوں میں سے کوئی نشانی ظاہر ہو جس دن تمھارے رب کی نشانیوں میں سے کوئی نشانی آپہنچے گی تو کسی شخص کو اس کا ایمان نفع نہ دے گا جو پہلے ایمان نہ لاچکا ہو یا اپنے ایمان میں کچھ نیکی نہ کی ہو کہو تم راہ دیکھو، ہم بھی راہ دیکھ رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(6,159,'6:159','جنھوں نے اپنے دین میں راہیں نکالیں اور گروہ گروہ بن گئے تم کو ان سے کچھ سروکار نہیں ان کا معاملہ اللہ کے حوالے ہے پھر وہی ان کو بتادے گا جو وہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,160,'6:160','جو شخص نیکی لے کر آئے گا تو اس کے ليے اس کا دس گنا ہے اور جو شخص برائی لے کر آئے گا تو اس کو بس اس کے برابر بدلہ ملے گا اور ان پر ظلم نہیں کیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(6,161,'6:161','کہو، میرے رب نے مجھ کو سیدھا راستہ بتادیا ہے دینِ صحیح ابراہیم کی ملت کی طرف جو یکسو تھے اور مشرکین میں سے نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,162,'6:162','کہو میری نماز اور میری قربانی، میرا جینا اور میرا مرنا اللہ کے ليے ہے جو رب ہے سارے جہان کا');
INSERT INTO "ur_wahid_uddin" VALUES(6,163,'6:163','کوئی اس کا شریک نہیں اور مجھے اسی کا حکم ملا ہے اور میں سب سے پہلے فرماں بردار ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(6,164,'6:164','کہو، کیا میں اللہ کے سوا کوئی اور رب تلاش کروں جب کہ وہی ہر چیز کا رب ہے اور جو شخص بھی کوئی کمائی کرتا ہے وہ اسی پر رہتا ہے اور کوئی بوجھ اٹھانے والا دوسرے کا بوجھ نہ اٹھائے گا پھر تمھارے رب ہی کی طرف تمھارا لوٹنا ہے پس وہ تمھیں بتادے گا وہ چیز جس میں تم اختلاف کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(6,165,'6:165','اور وہی ہے جس نے تمھیں زمین میں ایک دوسرے کا جانشین بنایا اور تم میں سے ایک کا رتبہ دوسرے پر بلند کیا، تاکہ وہ آزمائے تم کو ان چیزوں میں جو اس نے تم دی ہیں تمھارا رب جلد سزا دینے والا ہے اور بے شک وہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,1,'7:1','الٓمٓصٓ');
INSERT INTO "ur_wahid_uddin" VALUES(7,2,'7:2','یہ کتاب ہے جو تمھاری طرف اتاری گئی ہے پس تمھارا دل اس کے باعث تنگ نہ ہو،تا کہ تم اس کے ذریعہ سے لوگوں کو ڈراؤ، اور وہ ایمان والوں کے ليے یاد دہانی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,3,'7:3','جو اترا ہے تمھاری جانب تمھارے رب کی طرف سے اس کی پیروی کرو اور اس کے سوا دوسرے سرپرستوں کی پیروی نہ کرو تم بہت کم نصیحت مانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,4,'7:4','اور کتنی ہی بستیاں ہیں جن کو ہم نے ہلاک کردیا ان پر ہمارا عذاب رات کو آپہنچا، یا دوپہر کو جب کہ وہ آرام کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,5,'7:5','پھر جب ہمارا عذاب ان پر آیا تو وہ اس کے سوا کچھ نہ کہہ سکے کہ واقعی ہم ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,6,'7:6','پس ہم کو ضرور پوچھنا ہے ان لوگوں سے جن کے پاس رسول بھیجے گئے اور ہم کو ضرور پوچھنا ہے رسولوں سے');
INSERT INTO "ur_wahid_uddin" VALUES(7,7,'7:7','پھر ہم ان کے سامنے سب بیان کردیں گے علم کے ساتھ اور ہم کہیں غائب نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,8,'7:8','اس دن وزن دار صرف حق ہوگا پس جن كے پلڑے بھاری ہوں گے وہی لوگ کامیاب ٹھہریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,9,'7:9','اور جن كے پلڑے هلكے هوں گے وہی لوگ ہیں جنھوں نے اپنے آپ کو گھاٹے میں ڈالا، کیوں کہ وہ ہماری نشانیوں کے ساتھ ناانصافی کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,10,'7:10','اور ہم نے تم کو زمین میں جگہ دی اور ہم نے تمھارے ليے اس میں زندگی کا سامان فراہم کیا، مگر تم بہت کم شکر کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,11,'7:11','اور ہم نے تم کو پیدا کیا، پھر ہم نے تمھاری صورت بنائی پھر فرشتوں سے کہا کہ آدم کو سجدہ کرو پس انھوں نے سجدہ کیا مگر ابلیس سجدہ کرنے والوں میں شامل نہ ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(7,12,'7:12','خدا نے کہا کہ تجھے کس چیز نے سجدہ کرنے سے روکا، جب کہ میں نے تجھ کو حکم دیا تھا ابلیس نے کہا کہ میں اس سے بہتر ہوں تو نے مجھ کو آگ سے بنایا ہے اور آدم کو مٹی سے');
INSERT INTO "ur_wahid_uddin" VALUES(7,13,'7:13','خدا نے کہا کہ تو اتر یہاں سے تجھے یہ حق نہیں کہ تو اس میں گھمنڈ کرے پس نکل جا، یقینا تو ذلیل ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,14,'7:14','ابلیس نے کہا کہ اس دن تک کے ليے تو مجھے مہلت دے جب کہ سب لوگ اٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,15,'7:15','خدا نے کہا کہ تجھ کو مہلت دی گئی');
INSERT INTO "ur_wahid_uddin" VALUES(7,16,'7:16','ابلیس نے کہا کہ چوں کہ تو نے مجھے گمراہ کیا ہے، میں بھی لوگوں کے ليے تیری سیدھی راہ پر بیٹھوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(7,17,'7:17','پھر ان پر آؤں گا ان کے آگے سے اور ان کے پیچھے سے اور ان کے دائیں سے اور ان کے بائیں سے، اور تو ان میں سے اکثر کو شکر گزار نہ پائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(7,18,'7:18','خدا نے کہا کہ نکل یہاں سے ذلیل اور ٹھکرایا ہوا جو کوئی ان میں سے تیری راہ پر چلے گا تو میں تم سب سے جہنم کو بھردوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(7,19,'7:19','اور اے آدم، تم اور تمھاری بیوی جنت میں رہو اور کھاؤ جہاں سے چاہو، مگر اس درخت کے پاس نہ جانا ورنہ تم نقصان اٹھانے والوں میں سے ہوجاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,20,'7:20','پھر شیطان نے دونوں کو بہکایا تاکہ وہ کھول دے ان کی وہ شرم کی جگہیں جو ان سے چھپائی گئی تھیں اس نے ان سے کہا کہ تمھارے رب نے تم کو اس درخت سے صرف اس ليے روکا ہے کہ کہیں تم دونوں فرشتہ نہ بن جاؤ یا تم کو ہمیشہ کی زندگی حاصل ہوجائے');
INSERT INTO "ur_wahid_uddin" VALUES(7,21,'7:21','اور اس نے قسم کھاکر کہا کہ میں تم دونوں کا خیر خواہ ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(7,22,'7:22','پس مائل کرلیا ان کو فریب سے پھر جب دونوں نے درخت کا پھل چکھا تو ان کی شرم گاہیں ان پر کھل گئیں اور وہ اپنے کو باغ کے پتوں سے ڈھانکنے لگے اور ان کے رب نے ان کو پکارا کہ کیا میں نے تمھیں اس درخت سے منع نہیں کیا تھا اور یہ نہیں کہا تھا کہ شیطان تمھارا کھلا ہوا دشمن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,23,'7:23','انھوں نے کہا، اے ہمارے رب ہم نے اپنی جانوں پر ظلم کیا اور اگر تو ہم کو معاف نہ کرے اور ہم پر رحم نہ کرے تو ہم گھاٹا اٹھانے والوں میں سے ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,24,'7:24','خدا نے کہا، اترو، تم ایک دوسرے کے دشمن ہوگے، اور تمھارے ليے زمین میں ایک خاص مدت تک ٹھہرنا اور نفع اٹھانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,25,'7:25','خدا نے کہا، اسی میں تم جیوگے اور اسی میں تم مروگے اور اسی سے تم نکالے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,26,'7:26','اے بنی آدم، ہم نے تم پر لباس اتارا جو تمھارے بدن کے قابلِ شرم حصوں کو ڈھانکے اور زینت بھی اور تقویٰ کا لباس اس سے بھی بہتر ہے یہ اللہ کی نشانیوں میں سے ہے تاکہ لوگ غور کریں');
INSERT INTO "ur_wahid_uddin" VALUES(7,27,'7:27','اے آدم کی اولاد، شیطان تم کو بہکا نہ دے جس طرح اس نے تمھارے ماں باپ کو جنت سے نکلوادیا، اس نے ان کے لباس اتروائے تاکہ ان کو ان کے سامنے بے پردہ کردے وہ اور اس کے ساتھی تم کو ایسی جگہ سے دیکھتے ہیں جہاں سے تم انھیں نہیں دیکھتے ہم نے شیطانوں کو ان لوگوں کا دوست بنادیا ہے جو ایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,28,'7:28','اور جب وہ کوئی فحش (کھلی برائی) کرتے ہیں تو کہتے ہیں کہ ہم نے اپنے باپ دادا کو اسی طرح کرتے ہوئے پایا ہے اور خدا نے ہم کو اسی کا حکم دیا ہے کہو، اللہ کبھی برے کام کا حکم نہیں دیتا کیا تم اللہ کے ذمہ وہ بات لگاتے ہو جس کا تمھیں کوئی علم نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,29,'7:29','کہوکہ میرے رب نے قِسط کا حکم دیا ہے اور یہ کہ ہر نماز کے وقت اپنا رخ سیدھا رکھو اور اسی کو پکارو اسی کے ليے دین کو خالص کرتے ہوئے جس طرح اس نے تم کو پہلے پیدا کیا، اسی طرح تم دوسری بار بھی پیدا ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(7,30,'7:30','ایک گروہ کو اس نے راہ دکھادی اور ایک گروہ ہے کہ اس پر گمراہی ثابت ہوچکی انھوں نے اللہ کو چھوڑ کر شیطانوں کو اپنا رفیق بنایا اور گمان یہ رکھتے ہیں کہ وہ ہدایت پر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,31,'7:31','اے اولادِ آدم، ہر نماز کے وقت اپنا لباس پہنو اور کھاؤ پیو اور حد سے تجاوز نہ کرو بے شک اللہ حد سے تجاوز کرنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(7,32,'7:32','کہو اللہ کی زینت کو کس نے حرام کیا جو اس نے اپنے بندوں کے ليے نکالا تھا اور کھانے کی پاک چیزوں کو کہو وہ دنیا کی زندگی میں بھی ایمان والوں کے ليے ہیں اور آخرت میں تو وہ خاص انھیں کے ليے ہوں گی اسی طرح ہم اپنی آیتیں کھول کر بیان کرتے ہیں ان لوگوں کے ليے جو جاننا چاہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,33,'7:33','کہو، میرے رب نے تو بس فحش باتوں کو حرام ٹھہرایا ہے وہ کھلی ہوں یا چھپی ہوں ، اور گناہ کو اور ناحق کی زیادتی کو اور اس بات کو کہ تم اللہ کے ساتھ کسی کو شریک کرو جس کی اس نے کوئی دلیل نہیں اتاری اور یہ کہ تم اللہ کے ذمہ ایسی بات لگاؤ جس کا تم علم نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,34,'7:34','اور ہر قوم کے ليے ایک مقررہ مدت ہے پھر جب ان کی مدت آجائے گی تو وہ نہ ایک ساعت پیچھے ہٹ سکیں گے اور نہ آگے بڑھ سکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,35,'7:35','اے بنی آدم، اگر تمھارے پاس تمھیں میں سے رسول آئیں جو تم کو میری آیات سنائیں تو جو شخص ڈرا اور جس نے اصلاح کرلی، ان کے ليے نہ کوئی خوف ہوگا اور نہ وہ غمگین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,36,'7:36','اور جو لوگ میری آیات کو جھٹلائیں اور ان سے تکبر کریں ، وہی لوگ دوزح والے ہیں وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,37,'7:37','پھر اس سے زیادہ ظالم کون ہوگا جو اللہ پر بہتان باندھے یا اس کی نشانیوں کو جھٹلائے، ان کے نصیب کا جو حصہ لکھا ہوا ہے وہ انھیں مل کر رہے گا یہاں تک کہ جب ہمارے بھیجے ہوئے ان کی جان لینے کے ليے ان کے پاس پہنچیں گے تو ان سے پوچھیں گے کہ اللہ کے سوا جن کو تم پکارتے تھے کہاں ہیں وہ کہیں گے کہ وہ سب ہم سے کھوئے گئے اور وہ اپنے اوپر اقرار کریں گے کہ بے شک وہ انکار کرنے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,38,'7:38','خدا کہے گا، داخل ہوجاؤ آگ میں جنوں اور انسانوں کے ان گروہوں کے ساتھ جو تم سے پہلے گزر چکے ہیں جب بھی کوئی گروہ جہنم میں داخل ہوگا وہ اپنے ساتھی گروہ پر لعنت کرے گا یہاں تک کہ جب وہ اس میں جمع ہوجائیں گے تو ان کے پچھلے اپنے اگلوں کے بارے میں کہیں گے، اے ہمارے رب، یہی لوگ ہیں جنھوں نے ہم کو گمراہ کیا پس تو ان کو آگ کا دہرا عذاب دے خدا کہے گا کہ سب کے ليے دہرا ہے مگر تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,39,'7:39','اور ان کے اگلے اپنے پچھلوں سے کہیں گے، تم کو ہم پر کوئی فضیلت حاصل نہیں پس اپنی کمائی کے نتیجہ میں عذاب کا مزہ چکھو');
INSERT INTO "ur_wahid_uddin" VALUES(7,40,'7:40','بے شک جن لوگوں نے ہماری نشانیوں کو جھٹلایا اور ان سے تکبر کیا، ان کے ليے آسمان کے دروازے نہیں کھولے جائیں گے اور وہ جنت میں داخل نہ ہوں گے جب تک کہ اونٹ سوئی کے ناکے میں نہ گھس جائے اور ہم مجرموں کو ایسی ہی سزا دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,41,'7:41','ان کے ليے دوزخ کا بچھونا ہوگا اور ان کے اوپر اسی کا اوڑھنا ہوگا اور ہم ظالموں کو اسی طرح سزا دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,42,'7:42','اور جو لوگ ایمان لائے اور انھوں نے نیک کام کيے ہم کسی شخص پر اس کی طاقت کے موافق ہی بوجھ ڈالتے ہیں یہی لوگ جنت والے ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,43,'7:43','اور ان کے سینہ کی ہر خلش کو ہم نکال دیں گے ان کے نیچے نہریں بہہ رہی ہوں گی اور وہ کہیں گے کہ ساری تعریف اللہ کے ليے ہے جس نے ہم کو یہاں تک پہنچایا اور ہم راہ پانے والے نہ تھے اگر اللہ ہم کو ہدایت نہ کرتا ہمارے رب کے رسول سچی بات لے کر آئے تھے اور آواز آئے گی کہ یہ جنت ہے جس کے تم وارث ٹھہرائے گئے ہو اپنے اعمال کے بدلے');
INSERT INTO "ur_wahid_uddin" VALUES(7,44,'7:44','اور جنت والے دوزح والوں کو پکاریں گے کہ ہم سے ہمارے رب نے جو وعدہ کیا تھا ہم نے اس کو سچاپایا، کیا تم نے بھی اپنے رب کے وعدہ کو سچا پایا وہ کہیں گے ہاں پھر ایک پکارنے والا دونوں کے درمیان پکارے گا کہ اللہ کی لعنت ہو ظالموں پر');
INSERT INTO "ur_wahid_uddin" VALUES(7,45,'7:45','جو اللہ کی راہ سے روکتے تھے اور اس میں کجی ڈھونڈتے تھے اور وہ آخرت کے منکر تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,46,'7:46','اور دونوں کے درمیان ایک آڑ ہوگی اور اعراف کے اوپر کچھ لوگ ہوں گے جو ہر ایک کو ان کی علامت سے پہچانیں گے اور وہ جنت والوں کو پکار کر کہیں گے کہ تم پر سلامتی ہو، وہ ابھی جنت میں داخل نہیں ہوئے ہوں گے مگر وہ امید وار ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,47,'7:47','اور جب دوزخ والوں کی طرف ان کی نگاہ پھیری جائے گی تو وہ کہیں گے کہ اے ہمارے رب، ہم کو شامل نہ کرنا ان ظالم لوگوں کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(7,48,'7:48','اور اعراف والے ان لوگوں کو پکاریں گے جنھیں وہ ان کی علامت سے پہچانتے ہوں گے وہ کہیں گے کہ تمھارے کام نہ آئی تمھاری جماعت اور تمھارا اپنے کو بڑا سمجھنا');
INSERT INTO "ur_wahid_uddin" VALUES(7,49,'7:49','کیا یہی وہ لوگ ہیں جن کی نسبت تم قسم کھاکر کہتے تھے کہ ان کو کبھی اللہ کی رحمت نہ پہنچے گی جنت میں داخل ہوجاؤ، اب نہ تم پر کوئی ڈر ہے اور نہ تم غم گین ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(7,50,'7:50','اور دوزخ کے لوگ جنت والوں کو پکاریں گے کہ کچھ پانی ہم پر ڈال دو یا اس میں سے جو اللہ نے تمھیں کھانے کو دے رکھا ہے وہ کہیں گے کہ اللہ نے ان دونوں چیزوں کو منکروں کے ليے حرام کردیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,51,'7:51','وہ جنھوں نے اپنے دین کو کھیل اور تماشا بنالیا تھا اور جن کو دنیا کی زندگی نے دھوکے میں ڈال رکھا تھا پس آج ہم ان کو بھلادیں گے جس طرح انھوں نے اپنے اس دن کی ملاقات کو بھلادیا تھا اور جیسا کہ وہ ہماری نشانیوں کا انکار کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,52,'7:52','اور ہم ان لوگوں کے پاس ایک ایسی کتاب لے آئے ہیں جس کو ہم نے علم کی بنیاد پر مفصل کیا ہے، ہدایت اور رحمت بناکر ان لوگوں کے ليے جو ایمان لائیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,53,'7:53','کیا اب وہ اسی کے منتظر ہیں کہ اس کا مضمون ظاہر ہوجائے جس دن اس کا مضمون ظاہر ہوجائے گا تو وہ لوگ جو اس کو پہلے بھولے ہوئے تھے بول اٹھیں گے کہ بے شک ہمارے رب کے پیغمبر حق لے کر آئے تھے پس اب کیا کوئی ہماری سفارش کرنے والے ہیں کہ وہ ہماری سفارش کریں یا ہم کو دوبارہ واپس ہی بھیج دیا جائے تاکہ ہم اس سے مختلف عمل کریں جو ہم پہلے کر رہے تھے انھوں نے اپنے آپ کو گھاٹے میں ڈالا اور ان سے گم ہوگیا وہ جو وہ گھڑتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,54,'7:54','بے شک تمھارا رب وہی اللہ ہے جس نے آسمانوں اورزمین کو چھ دنوں میں پیدا کیا پھر وہ عرش پر متمکن ہوا وہ اڑھاتا ہے رات کو دن پر، دن اس کے پیچھے لگا آتا ہے دوڑتا ہوا اور اس نے پیدا کيے سورج اور چاند اور ستارے، سب تا بع دار ہیں اس کے حکم کے یاد رکھو، اسی کا کام ہے پیدا کرنا اور حکم کرنا بڑی برکت والا ہے اللہ جو رب ہے سارے جہان کا');
INSERT INTO "ur_wahid_uddin" VALUES(7,55,'7:55','اپنے رب کو پکارو گڑگڑاتے ہوئے اور چپکے چپکے یقیناوہ حد سے گزرنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(7,56,'7:56','اور زمین میں فساد نہ کرو اس کی اصلاح کے بعد اور اسی کو پکارو خوف کے ساتھ اور طمع کے ساتھ یقینا اللہ کی رحمت نیک کام کرنے والوں سے قریب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,57,'7:57','اور وہ اللہ ہی ہے جو ہواؤں کو اپنی رحمت کے آگے خوش خبری بناکر بھیجتا ہے پھر جب وہ بوجھل بادلوں کو اٹھالیتی ہیں تو ہم اس کو کسی خشک سر زمین کی طرف ہانک دیتے ہیں پھر ہم اس کے ذریعہ پانی اتارتے ہیں پھر ہم اس کے ذریعہ سے ہر قسم کے پھل نکالتے ہیں اسی طرح ہم مُردوں کو نکالیں گے، تاکہ تم غور کرو');
INSERT INTO "ur_wahid_uddin" VALUES(7,58,'7:58','اور جو زمین اچھی ہے اس کی پیداوار نکلتی ہے اس کے رب کے حکم سے اور جو زمین خراب ہے اس کی پیداوار کم ہی ہوتی ہے اسی طرح ہم اپنی نشانیاں مختلف پہلوؤں سے دکھاتے ہیں ان کے ليے جو شکر کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,59,'7:59','ہم نے نوح کو اس کی قوم کی طرف بھیجا نوح نے کہا اے میری قوم، اللہ کی عبادت کرو اس کے سوا تمھارا کوئی معبود نہیں میں تم پر ایک بڑے دن کے عذاب سے ڈرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(7,60,'7:60','اس کی قوم کے بڑوں نے کہا کہ ہم کو تو یہ نظر آتا ہے کہ تم ایک کھلی ہوئی گمراہی میں مبتلا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,61,'7:61','نوح نے کہا کہ اے میری قوم، مجھ میں کوئی گمراہی نہیں ہے، بلکہ میں بھیجا ہوا ہوں سارے عالم کے پروردگار کا');
INSERT INTO "ur_wahid_uddin" VALUES(7,62,'7:62','تم کو اپنے رب کے پیغامات پہنچا رہا ہوں اور تمھاری خیر خواہی کر رہا ہوں اور میں اللہ کی طرف سے وہ بات جانتا ہوں جو تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,63,'7:63','کیا تم کو اس پر تعجب ہوا کہ تمھارے رب کی نصیحت تمھارے پاس تمھیں میں سے ایک شخص کے ذریعہ آئی تاکہ وہ تم کو ڈرائے اور تاکہ تم بچو اور تاکہ تم پر رحم کیا جائے');
INSERT INTO "ur_wahid_uddin" VALUES(7,64,'7:64','پس انھوں نے اس کو جھٹلادیا پھر ہم نے نوح کو بچالیا اور ان لوگوں کو بھی جو اس کے ساتھ کشتی میں تھے اور ہم نے ان لوگوں کو ڈبو دیا جنھوں نے ہماری نشانیوں کو جھٹلایا تھا بے شک وہ لوگ اندھے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,65,'7:65','اور عاد کی طرف ہم نے ان کے بھائی ہود کو بھیجا انھوں نے کہا اے میری قوم، اللہ کی عبادت کرو، اس کے سوا تمھارا کوئی معبود نہیں سو کیا تم ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,66,'7:66','اس کی قوم کے بڑے جو انکار کر رہے تھے بولے، ہم تو تم کو بے عقلی میں مبتلا دیکھتے ہیں اور ہم کو گمان ہے کہ تم جھوٹے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,67,'7:67','ہود نے کہا کہ اے میری قوم، مجھے کچھ بے عقلی نہیں بلکہ میں خداوند عالم کا رسول ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(7,68,'7:68','تم کو اپنے رب کے پیغامات پہنچا رہا ہوں اور تمھارا خیر خواہ اور امین ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(7,69,'7:69','کیا تم کو اس پر تعجب ہے کہ تمھارے پاس تمہیں میں سے ایک شخص کے ذریعہ تمھارے رب کی نصیحت آئی تاکہ وہ تم کو ڈرائے اور یاد کرو جب کہ اس نے قوم نوح کے بعد تم کو اس کا جانشین بنایا اور ڈیل ڈول میں تم کو پھیلاؤ بھی زیادہ دیا پس اللہ کی نعمتوں کو یاد کرو تاکہ تم فلاح پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(7,70,'7:70','ہود کی قوم نے کہا، کیا تم ہمارے پاس اس ليے آئے ہو کہ ہم تنہا اللہ کی عبادت کریں اور ان کو چھوڑ دیں جن کی عبادت ہمارے باپ دادا کرتے آئے ہیں پس تم جس عذاب کی دھمکی ہم کو دیتے ہو، اس کو لے آؤ اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,71,'7:71','ہود نے کہا، تم پر تمھارے رب کی طرف سے ناپاکی اور غصہ واقع ہوچکا ہے کیا تم مجھ سے ان ناموں پر جھگڑتے ہو جو تم نے اور تمھارے باپ دادا نے رکھ ليے ہیں جن کی خدا نے کوئی سند نہیں اتاری پس انتظار کرو، میں بھی تمھارے ساتھ انتظار کرنے والوں میں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(7,72,'7:72','پھر ہم نے بچالیا اس کو اور جو اس کے ساتھ تھے اپنی رحمت سے اور ان لوگوں کی جڑ کاٹ دی جو ہماری نشانیوں کو جھٹلاتے تھے اور مانتے نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,73,'7:73','اور ثمود کی طرف ہم نے ان کے بھائی صالح کو بھیجا انھوں نے کہا اے میری قوم، اللہ کی عبادت کرو اس کے سوا تمھارا کوئی معبود نہیں تمھارے پاس تمھارے رب کی طرف سے ایک کھلا ہوا نشان آگیا ہے یہ اللہ کی اونٹنی تمھارے ليے ایک نشانی کے طور پر ہے پس اس کو چھوڑ دو کہ وہ کھائے اللہ کی زمین میں اور اس کو کوئی گزند نہ پہنچانا ور نہ تم کو ایک درد ناک عذاب پکڑلے گا');
INSERT INTO "ur_wahid_uddin" VALUES(7,74,'7:74','اور یاد کرو جب کہ خدا نے عاد کے بعد تم کو ان کا جانشین بنایا اور تم کو زمین میں ٹھکانا دیا، تم اس کے میدانوں میں محل بناتے ہو اور پہاڑوں کو تراش کر گھر بناتے ہو پس اللہ کی نعمتوں کو یاد کرو اور زمین میں فسا دکرتے نہ پھرو');
INSERT INTO "ur_wahid_uddin" VALUES(7,75,'7:75','ان کی قوم کے بڑے جنھوں نے گھمنڈ کیا، وہ ان مومنین سے بولے جو ناتوان گنے جاتے تھے، کیا تم کو یقین ہے کہ صالح اپنے رب کے بھیجے ہوئے ہیں انھوں نے جواب دیا کہ ہم تو جو وہ لے کر آئے ہیں اس پر ایمان رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,76,'7:76','وہ متکبر لوگ کہنے لگے کہ ہم تو اس چیز کے منکر ہیں جس پر تم ایمان لائے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,77,'7:77','پھر انھوں نے اونٹنی کو کاٹ ڈالا اور اپنے رب کے حکم سے پھر گئے اور انھوں نے کہا، اے صالح، اگر تم پیغمبر ہو تو وہ عذاب ہم پر لے آؤ جس سے تم ہم کو ڈراتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,78,'7:78','پھر انھیں زلزلہ نے آپکڑا اور وہ اپنے گھر میں اوندھے منہ پڑے رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(7,79,'7:79','اور صالح یہ کہتا ہو اان کی بستیوں سے نکل گیا کہ اے میری قوم، میں نے تم کو اپنے رب کا پیغام پہنچادیا اور میں نے تمھاری خیر خواہی کی مگر تم خیر خواہوں کو پسند نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,80,'7:80','اور ہم نے لوط کو بھیجا جب اس نے اپنی قوم سے کہا کیا تم کھلی بے حیائی کا کام کرتے ہو جو تم سے پہلے دنیا میں کسی نے نہیں کیا');
INSERT INTO "ur_wahid_uddin" VALUES(7,81,'7:81','تم عورتوں کو چھوڑ کر مردوں سے اپنی خواہش پوری کرتے ہو بلکہ تم حد سے گزر جانے والے لوگ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,82,'7:82','مگر اس کی قوم کا جواب اس کے سوا کچھ نہ تھا کہ انھیں اپنی بستی سے نکال دو یہ لوگ بڑے پاک باز بنتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,83,'7:83','پھر ہم نے بچالیا لوط کو اور اس کے گھر والوں کو، اس کی بیوی کے سوا جو پیچھے رہ جانے والوں میں سے بنی');
INSERT INTO "ur_wahid_uddin" VALUES(7,84,'7:84','اور ہم نے ان پر بارش برسائی پتھروں کی، پھر دیکھو کہ کیسا انجام ہوا مجرموں کا');
INSERT INTO "ur_wahid_uddin" VALUES(7,85,'7:85','اور مدین کی طرف ہم نے ان کے بھائی شعیب کو بھیجا اس نے کہا اے میری قوم، اللہ کی عبادت کرو، اس کے سوا کوئی تمھارا معبود نہیں تمھارے پاس تمھارے رب کی طرف سے دلیل پہنچ چکی ہے پس ناپ اور تول پوری کرو اور مت گھٹا کر دو لوگوں کو ان کی چیزیں اور فساد نہ ڈالو زمین میں اس کی اصلاح کے بعد یہ تمھارے حق میں بہتر ہے اگر تم مومن ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,86,'7:86','اور راستوں پر مت بیٹھو کہ ڈراؤ اور اللہ کی راہ سے ان لوگوں کو روکو جو اس پر ایمان لاچکے ہیں اور اس راہ میں کجی تلاش کرو اور یاد کرو جب کہ تم بہت تھوڑے تھے پھر تم کو بڑھادیا اور دیکھو فساد کرنے والوں کا انجام کیا ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(7,87,'7:87','اور اگر تم میں سے ایک گروہ اس پر ایمان لایا ہے جو دے کر میں بھیجا گیا ہوں اور ایک گروہ ایمان نہیں لایا ہے تو انتظار کرو یہاں تک کہ اللہ ہمارے درمیان فیصلہ کردے اور وہ بہتر فیصلہ کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,88,'7:88','قوم کے بڑے جو متکبر تھے انھوں نے کہا کہ اے شعیب ہم تم کو اور ان لوگوں کو جو تمھارے ساتھ ایمان لائے ہیں اپنی بستی سے نکال دیں گے یا تم ہماری ملّت میں پھر آجاؤ شعیب نے کہا، کیا ہم بیزار ہوں تب بھی');
INSERT INTO "ur_wahid_uddin" VALUES(7,89,'7:89','ہم اللہ پر جھوٹ گھڑنے والے ہوں گے اگر ہم تمھاری ملت میں لوٹ آئیں بعد اس کے کہ اللہ نے ہم کو اس سے نجات دی اور ہم سے یہ ممکن نہیں کہ ہم اس ملت میں لوٹ آئیں مگر یہ کہ خدا ہمارا رب ہی ایسا چاہے ہمارا رب ہر چیز کو اپنے علم سے گھیرے ہوئے ہے ہم نے اپنے رب پر بھروسہ کیا اے ہمارے رب، ہمارے اور ہماری قوم کے درمیان حق کے ساتھ فیصلہ کردے، تو بہترین فیصلہ کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,90,'7:90','اور ان بڑوں نے جنھوں نے اس کی قوم میں سے انکار کیا تھا کہا کہ اگر تم شعیب کی پیروی کرو گے تو تم برباد ہوجاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,91,'7:91','پھر ان کو زلزلہ نے پکڑلیا پس وہ اپنے گھر میں اوندھے منہ پڑے رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(7,92,'7:92','جنھوں نے شعیب کو جھٹلایا تھا گویا وہ کبھی اس بستی میں بسے ہی نہ تھے، جنھوں نے شعیب کو جھٹلایا وہی گھاٹے میں رہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,93,'7:93','اس وقت شعیب ان سے منہ موڑ کر چلا اور کہا، اے میری قوم، میں تم کو اپنے رب کے پیغامات پہنچا چکا اور تمھاری خیر خواہی کرچکا اب میں کیا افسوس کروں منکروں پر');
INSERT INTO "ur_wahid_uddin" VALUES(7,94,'7:94','اور ہم نے جس بستی میں بھی کوئی نبی بھیجا، اس کے باشندوں کو ہم نے سختی اور تکلیف میں مبتلا کیا تاکہ وہ گڑگڑائیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,95,'7:95','پھر ہم نے دکھ کو سکھ سے بدل دیا یہاں تک کہ انھیں خوب ترقی ہوئی اور وہ کہنے لگے کہ تکلیف اور خوشی تو ہمارے باپ دادوں کو بھی پہنچتی رہی ہے پھر ہم نے ان کو اچانک پکڑ لیا اور وہ اس کا گمان بھی نہ رکھتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,96,'7:96','اور اگر بستیوں والے ایمان لاتے اور ڈرتے تو ہم ان پر آسمان اور زمین کی نعمتیں کھول دیتے مگر انھوں نے جھٹلایا تو ہم نے ان کو پکڑ لیا ان کے اعمال کے بدلے');
INSERT INTO "ur_wahid_uddin" VALUES(7,97,'7:97','پھر کیا بستی والے اس سے بے خوف ہوگئے ہیں کہ ان پر ہمارا عذاب رات کے وقت آپڑے جب کہ وہ سوتے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(7,98,'7:98','یا کیا بستی والے اس سے بے خوف ہوگئے ہیں کہ ہمارا عذاب آپہنچے ان پر دن چڑھے جب وہ کھیلتے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(7,99,'7:99','کیا یہ لوگ اللہ کی تدبیروں سے بے خوف ہوگئے ہیں پس اللہ کی تدبیروں سے وہی لوگ بے خوف ہوتے ہیں جو تباہ ہونے والے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(7,100,'7:100','کیا سبق نہیں ملا ان کو جو زمین کے وارث ہوئے ہیں اس کے اگلے باشندوں کے بعد کہ اگر ہم چاہیں تو ان کو پکڑ لیں ان کے گناہوں پر اور ہم نے ان کے دلوں پر مہر کردی ہے پس وہ نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,101,'7:101','یہ وہ بستیاں ہیں جن کے کچھ حالات ہم تم کو سنا رہے ہیں ان کے پاس ہمارے رسول نشانیاں لے کر آئے تو ہرگز نہ ہوا کہ وہ ایمان لائیں اس بات پر جس کو وہ پہلے جھٹلاچکے تھے اس طرح اللہ منکرین کے دلوں پر مہر کردیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,102,'7:102','اور ہم نے ان کے اکثر لوگوں میں عہد کا نباہ نہ پایا اور ہم نے ان میں سے اکثر کو نافرمان پایا');
INSERT INTO "ur_wahid_uddin" VALUES(7,103,'7:103','پھر ان کے بعد ہم نے موسیٰ کو اپنی نشانیوں کے ساتھ بھیجا فرعون اور اس کی قوم کے سرداروں کے پاس، مگر انھوں نے ہماری نشانیوں کے ساتھ ظلم کیا پس دیکھو کہ مفسدوں کا کیا انجام ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(7,104,'7:104','اور موسیٰ نے کہا اے فرعون، میں پروردگار عالم کی طرف سے بھیجا ہوا آیا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(7,105,'7:105','سزاوار ہوں کہ اللہ کے نام پر کوئی بات حق کے سوا نہ کہوں میں تمھارے پاس تمھارے رب کی طرف سے کھلی ہوئی نشانی لے کر آیا ہوں پس تو میرے ساتھ بنی اسرائیل کو جانے دے');
INSERT INTO "ur_wahid_uddin" VALUES(7,106,'7:106','فرعون نے کہا، اگر تم کوئی نشانی لے کر آئے ہو تو اس کو پیش کرو اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,107,'7:107','تب موسیٰ نے اپنا عصا ڈال دیا تو یکایک وہ ایک صاف اژدہا بن گیا');
INSERT INTO "ur_wahid_uddin" VALUES(7,108,'7:108','اور اس نے اپنا ہاتھ نکالا تو اچانک وہ دیکھنے والوں کے سامنے چمک رہا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(7,109,'7:109','فرعون کی قوم کے سرداروں نے کہا یہ شخص بڑا ماہر جادو گر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,110,'7:110','وہ چاہتا ہے کہ تم کو تمھاری زمین سے نکال دے');
INSERT INTO "ur_wahid_uddin" VALUES(7,111,'7:111','اب تمھاری کیا صلاح ہے انھوں نے کہا، موسیٰ کو اور اس کے بھائی کو مہلت دو اور شہروں میں ہرکارے بھیجو');
INSERT INTO "ur_wahid_uddin" VALUES(7,112,'7:112','وہ تمھارے پاس سارے ماہر جادو گر لے آئیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,113,'7:113','اور جادو گر فرعون کے پاس آئے انھوں نے کہا، ہم کو انعام تو ضرور ملے گا اگر ہم غالب رہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,114,'7:114','فرعون نے کہا، ہاں اور یقیناً تم ہمارے مقربین میں داخل ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(7,115,'7:115','جادو گروں نے کہا، یا تو تم ڈالو یا ہم ڈالنے والے بنتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,116,'7:116','موسیٰ نے کہا، تم ہی ڈالو پھر جب انھوں نے ڈالا تو لوگوں کی آنکھوں پر جادو کردیا اور ان پر دہشت طاری کردی اور بہت بڑا کرتب دکھایا');
INSERT INTO "ur_wahid_uddin" VALUES(7,117,'7:117','اور ہم نے موسیٰ کو حکم بھیجا کہ اپنا عصا ڈال دو تو اچانک وہ نگلنے لگا اس کو جو انھوں نے گھڑا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(7,118,'7:118','پس حق ظاہر ہوگیا اور جو کچھ انھوں نے بنایا تھا، وہ باطل ہو کر رہ گیا');
INSERT INTO "ur_wahid_uddin" VALUES(7,119,'7:119','پس وہ لوگ وہیں ہار گئے اور ذلیل ہوکر رہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,120,'7:120','اور جادو گر سجدہ میں گر پڑے');
INSERT INTO "ur_wahid_uddin" VALUES(7,121,'7:121','انھوں نے کہا، ہم ایمان لائے رب العالمین پر');
INSERT INTO "ur_wahid_uddin" VALUES(7,122,'7:122','جو رب ہے موسیٰ اور ہارون کا');
INSERT INTO "ur_wahid_uddin" VALUES(7,123,'7:123','فرعون نے کہا، تم لوگ موسیٰ پر ایمان لے آئے قبل اس کے کہ میں تمھیں اجازت دوں یقیناً یہ ایک سازش ہے جو تم لوگوں نے شہر میں اس غرض سے کی ہے کہ تم اس کے باشندوں کو یہاں سے نکال دو، تو تم بہت جلد جان لو گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,124,'7:124','میں تمھارے ہاتھ اور پاؤ ں مخالف سمتوں سے کاٹوں گا پھر تم سب کو سولی پر چڑھادوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(7,125,'7:125','انھوں نے کہا، ہم کو اپنے رب ہی کی طرف لوٹنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,126,'7:126','تو ہم کو صرف اس بات کی سزا دینا چاہتا ہے کہ ہمارے رب کی نشانیاں جب ہمارے سامنے آگئیں تو ہم ان پر ایمان لے آئے اے رب، ہم پر صبر انڈیل دے اور ہم کو وفات دے اسلام پر');
INSERT INTO "ur_wahid_uddin" VALUES(7,127,'7:127','قوم فرعون کے سرداروں نے کہا، کیا تو موسیٰ اور اس کی قوم کو چھوڑ دے گا کہ وہ ملک میں فساد پھیلائیں اور تجھ کو اور تیرے معبودوں کو چھوڑیں فرعون نے کہا کہ ہم ان کے بیٹوں کو قتل کریں گے اور ان کی عورتوں کو زندہ رکھیں گے اور ہم ان پر پوری طرح قادر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,128,'7:128','موسیٰ نے اپنی قوم سے کہا کہ اللہ سے مدد چاہو اور صبر کرو زمین اللہ کی ہے، وہ اپنے بندوں میں سے جس کو چاہتا ہے اس کا وارث بنادیتا ہے اور آخری کامیابی اللہ سے ڈرنے والوں ہی کے ليے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,129,'7:129','موسیٰ کی قوم نے کہا، ہم تمھارے آنے سے پہلے بھی ستائے گئے اور تمھارے آنے کے بعد بھی موسیٰ نے کہا قریب ہے کہ تمھارا رب تمھارے دشمن کو ہلاک کردے اور بجائے ان کے تم کو اس سرزمین کا مالک بنادے، پھر دیکھے کہ تم کیسا عمل کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,130,'7:130','اور ہم نے فرعون والوں کو قحط اور پیداوار کی کمی میں مبتلا کیا تاکہ ان کو نصیحت ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,131,'7:131','لیکن جب ان پر خوش حالی آتی تو کہتے کہ یہ ہمارے ليے ہے اور اگر ان پر کوئی آفت آتی تو اس کو موسیٰ اور اس کے ساتھیوں کی نحوست بتاتے سنو، ان کی بدبختی تو اللہ کے پاس ہے مگر ان میں سے اکثر نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,132,'7:132','اور انھوں نے موسیٰ سے کہا، ہم کو مسحور کرنے کے ليے تم خواہ کوئی بھی نشانی لاؤ ہم تم پر ایمان لانے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,133,'7:133','پھر ہم نے ان کے اوپر طوفان بھیجا اور ٹڈی اور جوئیں اور مینڈک اور خون یہ سب نشانیاں الگ الگ دکھائیں پھر بھی انھوں نے تکبر کیا اور وہ مجرم لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,134,'7:134','اور جب ان پر کوئی عذاب پڑتا تو کہتے اے موسیٰ، اپنے رب سے ہمارے ليے دعا کرو جس کا اس نے تم سے وعدہ کر رکھا ہے اگر تم ہم پر سے اس عذاب کو ہٹادو تو ہم ضرور تم پر ایمان لائیں گے اور تمھارے ساتھ بنی اسرائیل کو جانے دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,135,'7:135','پھر جب ہم ان سے دور کردیتے آفت کو کچھ مدت کے ليے جہاں بہرحال انھیں پہنچنا تھا تو اسی وقت وہ عہد کو توڑ دیتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,136,'7:136','پھر ہم نے ان کو سزا دی اور ان کو سمندر میں غرق کردیا کیوں کہ انھوں نے ہماری نشانیوں کو جھٹلایا اور ان سے بے پروا ہوگئے');
INSERT INTO "ur_wahid_uddin" VALUES(7,137,'7:137','اور جو لوگ کمزور سمجھے جاتے تھے ان کو ہم نے اس سرزمین کے مشرق و مغرب کا وارث بنادیا جس میں ہم نے برکت رکھی تھی اور بنی اسرائیل پر تیرے رب کا نیک وعدہ پورا ہوگیا بہ سبب اس کے کہ انھوں نے صبر کیا اور ہم نے فرعون اور اس کی قوم کا وہ سب کچھ برباد کردیا جو وہ بناتے تھے اور جو وہ چڑھاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,138,'7:138','اور ہم نے بنی اسرائیل کو سمندر کے پار اتار دیا پھر ان کا گزر ایک ایسی قوم پر ہوا جو اپنے بتوں كو پوجنے میں لگے هوئے تھے انھوں نے کہا اے موسیٰ، ہماری عبادت کے ليے بھی ایک بت بنادے جیسے ان کے بت ہیں موسیٰ نے کہا، تم بڑے جاہل لوگ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,139,'7:139','یہ لوگ جس کام میں لگے ہوئے ہیں ، وہ برباد ہونے والا ہے اور یہ جو کچھ کر رہے ہیں وہ باطل ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,140,'7:140','اس نے کہا، کیا میں اللہ کے سوا کوئی اور معبود تمھارے ليے تلاش کروں ، حالاں کہ اس نے تم کو تمام اہل عالم پر فضیلت دی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,141,'7:141','اور جب ہم نے فرعون والوں سے تم کو نجات دی جو تم کو سخت عذاب میں ڈالے ہوئے تھے تمھارے بیٹوں کو قتل کرتے اور تمھاری عورتوں کو زندہ رہنے دیتے اور اس میں تمھارے رب کی طرف سے تمھاری بڑی آزمائش تھی');
INSERT INTO "ur_wahid_uddin" VALUES(7,142,'7:142','اور ہم نے موسیٰ سے تیس راتوں کا وعدہ کیا اور اس کو پورا کیا دس مزید راتوں سے تو اس کے رب کی مدت چالیس راتوں میں پوری ہوئی اور موسیٰ نے اپنے بھائی ہارون سے کہا، میرے پیچھے تم میری قوم میں میری جانشینی کرنا، اصلاح کرتے رہنا اور بگاڑ پیدا کرنے والوں کے طریقے پر نہ چلنا');
INSERT INTO "ur_wahid_uddin" VALUES(7,143,'7:143','اور جب موسی ٰ ہمارے وقت پر آگیا اور اس کے رب نے اس سے کلام کیا تو اس نے کہا، مجھے اپنے کو دکھادے کہ میں تجھ کو دیکھوں فرمایا، تم مجھ کو ہرگز نہیں دیکھ سکتے البتہ پہاڑ کی طرف دیکھو، اگر وہ اپنی جگہ قائم رہ جائے تو تم بھی مجھ کو دیکھ سکو گے پھر جب اس کے رب نے پہاڑ پر اپنی تجلی ڈالی تو اس کو ریزہ ریزہ کردیا اور موسیٰ بے ہوش ہوکر گر پڑا پھر جب ہوش آیا تو بولا، تو پاک ہے، میں نے تیری طرف رجوع کیا اور میں سب سے پہلے ایمان لانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(7,144,'7:144','اللہ نے فرمایا، اے موسیٰ، میں نے تم کو لوگوں پر اپنی پیغمبری اور اپنے کلام کے ذریعہ سے سرفراز کیا پس اب لو جو کچھ میں نے تم کو عطا کیا ہے اور شکر گزاروں میں سے بنو');
INSERT INTO "ur_wahid_uddin" VALUES(7,145,'7:145','اور ہم نے اس کے ليے تختیوں پر ہر قسم کی نصیحت اور ہر چیز کی تفصیل لکھ دی پس اس کو مضبوطی سے پکڑو اور اپنی قوم کو حکم دو کہ ان کے بہتر مفہوم کی پیروی کریں عنقریب میں تم کو نافرمانوں کا گھر دکھاؤں گا');
INSERT INTO "ur_wahid_uddin" VALUES(7,146,'7:146','میں اپنی نشانیوں سے ان لوگوں کو پھیر دوں گا جو زمین میں ناحق گھمنڈ کرتے ہیں اور اگر وہ ہر قسم کی نشانیاں دیکھ لیں تب بھی ان پر ایمان نہ لائيں اور اگر وہ ہدایت کا راستہ دیکھیں تو اس کو نہ اپنائیں گے اور اگر گمراہی کا راستہ دیکھیں تو اس کو اپنالیں گے یہ اس سبب سے ہے کہ انھوں نے ہماری نشانیوں کو جھٹلایا اور ان کی طرف سے اپنے کو غافل رکھا');
INSERT INTO "ur_wahid_uddin" VALUES(7,147,'7:147','اور جنھوں نے ہماری نشانیوں کو اور آخرت کی ملاقات کو جھٹلایا، ان کے اعمال اکارت ہوگئے اور وہ بدلے میں وہی پائیں گے جو وہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,148,'7:148','اور موسیٰ کی قوم نے اس کے پیچھے اپنے زیوروں سے ایک بچھڑا بنایا ایک دھڑ جس سے بیل کی سی آواز نکلتی تھی کیا انھوں نے نہیں دیکھا کہ وہ نہ ان سے بولتا ہے اور نہ کوئی راہ دکھاتا ہے اس کو انھوں نے معبود بنالیا اور وہ بڑے ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,149,'7:149','اور جب وہ پچھتائے اور انھوں نے محسوس کیا کہ وہ گمراہی میں پڑگئے تھے تو انھوں نے کہا، اگر ہمارے رب نے ہم پر رحم نہ کیا اور ہم کو نہ بخشا تو یقینا ہم برباد ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,150,'7:150','اور جب موسیٰ رنج اور غصہ میں بھرا ہوا اپنی قوم کی طرف لوٹا تو اس نے کہا، تم نے میرے بعد میری بہت بری جانشینی کی کیا تم نے اپنے رب کے حکم سے پہلے ہی جلدی کرلی اور اس نے تختیاں ڈال دیں اور اپنے بھائی کا سر پکڑ کر اس کو اپنی طرف کھینچنے لگا ہارون نے کہا، اے میری ماں کے بیٹے، لوگوں نے مجھے دبالیا اور قریب تھا کہ مجھ کو مار ڈالیں پس تو دشمنوں کو میرے اوپر ہنسنے کا موقع نہ دے اور مجھ کو ظالموں کے ساتھ شامل نہ کر');
INSERT INTO "ur_wahid_uddin" VALUES(7,151,'7:151','موسیٰ نے کہا، اے میرے رب معاف کردے مجھ کو اور میرے بھائی کو اور ہم کو اپنی رحمت میں داخل فرما اور تو سب سے زیادہ رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,152,'7:152','بے شک جن لوگوں نے بچھڑے کو معبود بنایا، ان کو ان کے رب کا غضب پہنچے گا اور ذلت دنیا کی زندگی میں اور ہم ایسا ہی بدلہ دیتے ہیں جھوٹ باندھنے والوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(7,153,'7:153','اور جن لوگوں نے برے کام کيے پھر اس کے بعد توبہ کی اور ایمان لائے تو بے شک اس کے بعد تیرا رب بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,154,'7:154','اور جب موسیٰ کا غصہ تھما تو اس نے تختیاں اٹھائیں اور جو ان میں لکھا ہوا تھا، اس میں ہدایت اور رحمت تھی ان لوگوں کے ليے جو اپنے رب سے ڈرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,155,'7:155','اور موسیٰ نے اپنی قوم میں سے ستر آدمی چنے ہمارے مقرر کيے ہوئے وقت کے ليے پھر جب ان کو زلزلہ نے پکڑا تو موسی نے کہا اے رب، اگر تو چاہتا تو پہلے ہی تو ان کو ہلاک کردیتا اور مجھ کو بھی کیا تو ہم کو ایسے کام پر ہلاک کرے گا جو ہمارے اندر کے بیوقوفوں نے کیا یہ سب تیری آزمائش ہے، تو اس سے جس کو چاہے گمراہ کردے اور جس کو چاہے ہدایت دے تو ہی ہمارا تھامنے والا ہے پس ہم کو بخش دے اور ہم پر رحم فرما، تو سب سے بہتر بخشنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,156,'7:156','اور ہمارے ليے اس دنیا میں بھی بھلائی لکھ دے اور آخرت میں بھی ہم نے تیری طرف رجوع کیااللہ نے کہا، میں اپنا عذاب اسی پر ڈالتا ہوں جس کو چاہتا ہوں اور میری رحمت شامل ہے ہر چیز کو پس میں اس کو لکھ دوں گا ان کے ليے جو ڈر رکھتے ہیں اور زکوٰۃ ادا کرتے ہیں اور ہماری نشانیوں پر ایمان لاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,157,'7:157','جو لوگ پیروی کریں گے اس رسول کی جو نبی ٔ امی ہے، جس کو وہ اپنے یہاں تورات اور انجیل میں لکھا ہوا پاتے ہیں وہ ان کو نیکی کا حکم دیتا ہے اور ان کو برائی سے روکتا ہے اور ان کے ليے پاکیزہ چیزیں جائز ٹھہراتا ہے اور ناپاک چیزیں حرام کرتا ہے اور ان پر سے وہ بوجھ اور قیدیں اتار تا ہے جو ان پر تھیں پس جو لوگ اس پر ایمان لائے اور جنھوں نے اس کی عزت کی اور اس کی مدد کی اور اس نور کی پیروی کی جو اس کے ساتھ اتارا گیا ہے تو وہی لوگ فلاح پانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,158,'7:158','کہو، اے لوگو، بے شک میں اللہ کا رسول ہوں تم سب کی طرف جس کی حکومت ہے آسمانوں اور زمین میں ، اس كے سوا كوئي معبود نهيں ، وہی جلاتا ہے اور وہی مارتا ہے پس ایمان لاؤ اللہ پر اور اس کے امی رسول و نبی پر جو ایمان رکھتا ہے اللہ اور اس کے کلمات پر اور اس کی پیروی کرو تاکہ تم ہدایت پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(7,159,'7:159','اور موسیٰ کی قوم میں ایک گروہ ایسا بھی ہے جو حق کے مطابق رہنمائی کرتا ہے اور اسی کے مطابق انصاف کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,160,'7:160','اور ہم نے ان کو بارہ گھرانوں میں تقسیم کرکے انھیں الگ الگ گروہ بنادیا اور جب موسیٰ کی قوم نے پانی مانگا تو ہم نے موسیٰ کو حکم بھیجا کہ فلاں چٹان پر اپنی لاٹھی مارو تو اس سے بارہ چشمے پھوٹ نکلے ہر گروہ نے اپنا پانی پینے کا مقام معلوم کرلیا اور ہم نے ان پر بدلیوں کا سایہ کیا اور ان پر من وسلویٰ اتارا کھاؤ پاکیزہ چیزوں میں سے جو ہم نے تم کو دی ہیں اور انھوں نے ہمارا کچھ نہیں بگاڑا بلکہ خود اپنا ہی نقصان کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,161,'7:161','اورجب ان سے کہا گیا کہ اس بستی میں جاکر بس جاؤ اس میں جہاں سے چاہو کھاؤ اور کہو ہم کو بخش دے اور دروازہ میں جھکے ہوئے داخل ہو، ہم تمھاری خطائیں معاف کردیں گے ہم نیکی کرنے والوں کو اور زیادہ دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,162,'7:162','پھر ان میں سے ظالموں نے بدل ڈالا دوسرا لفظ اس کے سوا جو ان سے کہا گیا تھا پھر ہم نے ان پر آسمان سے عذاب بھیجا اس ليے کہ وہ ظلم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,163,'7:163','اور ان سے اس بستی کا حال پوچھو جو دریا کے کنارے تھی جب وہ سبت (سنیچر) کے بارے میں تجاوز کرتے تھے جب ان کے سبت کے دن ان کی مچھلیاں پانی کے اوپر آتیں اور جس دن سبت نہ ہوتا تو نہ آتیں ان کی آزمائش ہم نے اس طرح کی، اس ليے کہ وہ نافرمانی کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,164,'7:164','اور جب ان میں سے ایک گروہ نے کہا کہ تم ایسے لوگوں کو کیوں نصیحت کرتے ہو جنھیں اللہ ہلاک کرنے والا ہے یا ان کو سخت عذاب دینے والا ہے انھوں نے کہا،تمھارے رب کے سامنے الزام اتارنے کے ليے اور اس ليے کہ شاید وہ ڈریں');
INSERT INTO "ur_wahid_uddin" VALUES(7,165,'7:165','پھر جب انھوں نے بھلادی وہ چیز جو ان کو یاد دلائی گئی تھی تو ہم نے ان لوگوں کو بچالیا جو برائی سے روکتے تھے اور ان لوگوں کو جنھوں نے اپنی جانوں پر ظلم کیا ایک سخت عذاب میں پکڑ لیا اس ليے کہ وہ نافرمانی کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(7,166,'7:166','پھر جب وہ بڑھنے لگے اس کام میں جس سے وہ روکے گئے تھے تو ہم نے ان سے کہا کہ ذلیل بندر بن جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(7,167,'7:167','اور جب تمھارے رب نے اعلان کردیا کہ وہ ان پر قیامت کے دن تک ضرور ایسے لوگ بھیجتا رہے گا جو ان کو نہایت برا عذاب ديں بے شک تیرا رب جلد سزا دینے والا ہے اور بے شک وہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,168,'7:168','اور ہم نے ان کو گروہ گروہ کرکے زمین میں متفرق کردیا ان میں کچھ نیک ہیں اور ان میں کچھ اس سے مختلف اور ہم نے ان کی آزمائش کی اچھے حالات سے اور برے حالات سے تاکہ وہ باز آئیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,169,'7:169','پھر ان کے پیچھے ناخلف لوگ آئے جو کتاب کے وارث بنے، وہ اسی دنیاکی متاع لیتے ہیں اور کہتے ہیں کہ ہم یقیناً بخش دئے جائیں گے اور اگر ایسی ہی متاع ان کے سامنے پھر آئے تو اس کو لے لیں گے کیا ان سے کتاب میں اس کا عہد نہیں لیا گیا ہے کہ وہ اللہ کے نام پر حق کے سوا کوئی اور بات نہ کہیں اور انھوں نے پڑھاہے جو کچھ اس میں لکھا ہے اور آخرت کا گھر بہتر ہے ڈرنے والوں کے ليے، کیا تم سمجھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,170,'7:170','اور جو لوگ خداکی کتاب کو مضبوطی سے پکڑتے ہیں اور نماز قائم کرتے ہیں ، بے شک ہم مصلحین کا اجر ضائع نہیں کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,171,'7:171','اور جب ہم نے پہاڑ کو ان کے اوپر اٹھایا گویا کہ وہ سائبان ہے اور انھوں نے گمان کیا کہ وہ ان پر آپڑے گا پکڑو اس چیز کو جو ہم نے تم کو دی ہے مضبوطی سے، اور یاد رکھو جو اس میں ہے تاکہ تم بچو');
INSERT INTO "ur_wahid_uddin" VALUES(7,172,'7:172','اور جب تیرے رب نے بنی آدم کی پیٹھوں سے ان کی اولاد کو نکالا اور ان کو گواہ ٹھہرایا خود ان کے اوپر کیا میں تمھارا رب نہیں ہوں انھوں نے کہا ہاں ، ہم اقرار کرتے ہیں یہ اس ليے ہوا کہ کہیں تم قیامت کے دن کہنے لگو کہ ہم کو تو اس کی خبر نہ تھی');
INSERT INTO "ur_wahid_uddin" VALUES(7,173,'7:173','یا کہو کہ ہمارے باپ دادا نے پہلے سے شرک کیا تھا اور ہم ان کے بعد ان کی نسل میں ہوئے تو کیا تو ہم کو ہلاک کرے گا اس کام پر جو غلط کار لوگوں نے کیا');
INSERT INTO "ur_wahid_uddin" VALUES(7,174,'7:174','اور اس طرح ہم اپنی نشانیاں کھول کر بیان کرتے ہیں تاکہ وہ رجوع کریں');
INSERT INTO "ur_wahid_uddin" VALUES(7,175,'7:175','اور ان کو اس شخص کا حال سناؤ جس کو ہم نے اپنی آیتیں دی تھیں تو وہ ان سے نکل بھاگا پس شیطان اس کے پیچھے لگ گیا اور وہ گمراہوں میں سے ہوگیا');
INSERT INTO "ur_wahid_uddin" VALUES(7,176,'7:176','اور اگر ہم چاہتے تو اس کو ان آیتوں کے ذریعہ سے بلندی عطا کرتے مگر وہ تو زمین کا ہو رہا اور اپنی خواہشوں کی پیروی کرنے لگا پس اس کی مثال کتے کی سی ہے کہ اگر تو اس کا تعاقب کرےتب بھی ہانپے اور اگر چھوڑ دے تب بھی ہانپے یہ مثال ان لوگوں کی ہے جنھوں نے ہماری نشانیوں کو جھٹلایا پس تم یہ احوال ان کو سناؤ تاکہ وہ سوچیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,177,'7:177','کیسی بری مثال ہے ان لوگوں کی جو ہماری نشانیوں کو جھٹلاتے ہیں اور وہ اپنا ہی نقصان کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,178,'7:178','اللہ جس کو راہ دکھائے، وہی راہ پانے والا ہوتا ہے اور جس کو وہ بے راہ کردے تو وہی گھاٹا اٹھانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,179,'7:179','اور ہم نے جنات اور انسان میں سے بہتوں کو دوزخ کے ليے پیدا کیا ہے ان کے دل ہیں جن سے وہ سمجھتے نہیں ، ان کی آنکھیں ہیں جن سے وہ دیکھتے نہیں ، ان کے کان ہیں جن سے وہ سنتے نہیں وہ ایسے ہیں جیسے چوپائے بلکہ ان سے بھی زیادہ بے راہ یہی لوگ ہیں غافل');
INSERT INTO "ur_wahid_uddin" VALUES(7,180,'7:180','اور اللہ کے ليے ہیں سب اچھے نام پس انھیں سے اس کو پکارو اور ان لوگوں کو چھوڑ دو، جو اس کے ناموں میں کج روی کرتے ہیں وہ بدلہ پا کر رہیں گے اپنے کاموں کا');
INSERT INTO "ur_wahid_uddin" VALUES(7,181,'7:181','اور ہم نے جن کو پیدا کیا ہے ان میں سے ایک گروہ ایسا ہے جو حق کے مطابق رہنمائی کرتا ہے، اور حق کے مطابق فیصلہ کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,182,'7:182','اور جن لوگوں نے ہماری نشانیوں کو جھٹلایا ہم ان کو آہستہ آہستہ پکڑیں گے ایسی جگہ سے جہاں سے ان کو خبر بھی نہ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(7,183,'7:183','اور میں ان کو ڈھیل دیتا ہوں ، بے شک میرا داؤ بڑا مضبوط ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,184,'7:184','کیا ان لوگوں نے غور نہیں کیاکہ ان کے ساتھی کو کوئی جنون نہیں ہے وہ تو ایک صاف ڈرانے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,185,'7:185','کیا انھوں نے آسمانوں اور زمین کے نظام پر نظر نہیں کی اور جو کچھ اللہ نے پیدا کیا ہے ہر چیز سے، اور اس بات پر کہ شاید ان کی مدت قریب آگئی ہو پس اس کے بعد وہ کس بات پر ایمان لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,186,'7:186','جس کو اللہ بے راہ کردے اس کو کوئی راہ دکھانے والا نہیں اور وہ ان کو سرکشی ہی میں بھٹکتا ہوا چھوڑ دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,187,'7:187','وہ تم سے قیامت کی بابت پوچھتے ہیں کہ اس کا وقوع کب ہوگا کہو، اس کا علم تو میرے رب ہی کے پاس ہے وہی اس کے وقت پر اس کو ظاہر کرے گا وہ بھاری ہو رہی ہے آسمانوں میں اور زمین میں وہ جب تم پر آئے گی تو اچانک آجائے گی وہ تم سے پوچھتے ہیں گویا کہ تم اس کی تحقیق کرچکے ہو کہو، اس کا علم تو بس اللہ ہی کے پاس ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,188,'7:188','کہو، میں مالک نہیں اپنی جان کے بھلے کا اور نہ برے کا مگر جو اللہ چاہے اور اگر میں غیب کو جانتا تو میں بہت سے فائدے اپنے ليے حاصل کرلیتااور مجھے کوئی نقصان نہ پہنچتا میں تو محض ایک ڈرانے والا، اور خوش خبری سنانے والا ہوں ان لوگوں کے ليے جو میری بات مانیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,189,'7:189','وہی ہے جس نے تم کو پیدا کیا ایک جان سے اور اسی سے بنایا اس کا جوڑا تاکہ اس کے پاس سکون حاصل کرے پھر جب مرد نے عورت کو ڈھانک لیا تو اس کو ایک ہلکا سا حمل رہ گیا پھر وہ اس کو ليے پھرتی رہی پھر جب وہ بوجھل ہوگئی تو دونوں نے مل کر اپنے رب اللہ سے دعا کی— اگر تو نے ہمیں تندرست اولاد دی تو ہم تیرے شکر گزار رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(7,190,'7:190','مگر جب اللہ نے ان کو تندرست اولاد دے دی تو وہ اس کی بخشی ہوئی چیز میں دوسروں کو اس کا شریک ٹھہرانے لگے اللہ برتر ہے ان مشر کانہ باتوں سے جو یہ لوگ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,191,'7:191','کیا وہ شریک بناتے ہیں ایسوں کو جو کسی چیز کو پیدا نہیں کرتے بلکہ وہ خود مخلوق ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,192,'7:192','اور وہ نہ ان کی کسی قسم کی مدد کرسکتے ہیں اور نہ اپنی ہی مدد کرسکتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,193,'7:193','اور اگر تم ان کو رہنمائی کے ليے پکارو تو وہ تمھاری پکار پر نہ چلیں گے برابر ہے خواہ تم ان کو پکارو یا تم خاموش رہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,194,'7:194','جن کو تم اللہ کے سوا پکارتے ہو، وہ تمھارے ہی جیسے بندے ہیں پس تم ان کو پکارو، وہ تمھیں جواب دیں اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(7,195,'7:195','کیا ان کے پاؤں ہیں کہ ان سے چلیں کیا ان کے ہاتھ ہیں کہ ان سے پکڑیں کیا ان کے آنکھیں ہیں کہ ان سے دیکھیں کیا ان کے کان ہیں کہ ان سے سنیں کہو، تم اپنے شریکوں کو بلاؤ پھر تم لوگ میرے خلاف تدبیریں کرو اور مجھے مہلت نہ دو');
INSERT INTO "ur_wahid_uddin" VALUES(7,196,'7:196','یقیناً میرا کار ساز اللہ ہے جس نے کتاب اتاری ہے اور وہ کار سازی کرتا ہے نیک بندوں کی');
INSERT INTO "ur_wahid_uddin" VALUES(7,197,'7:197','اور جن کو تم پکارتے ہو اس کے سوا، وہ نہ تمھاری مدد کرسکتے ہیں اور نہ اپنی ہی مدد کرسکتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,198,'7:198','اور اگر تم ان کو راستہ کی طرف پکارو تو وہ تمھاری بات نہ سنیں گے اور تم کو نظر آتا ہے کہ وہ تمھاری طرف دیکھ رہے ہیں مگر وہ کچھ نہیں دیکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,199,'7:199','در گزر کرو، نیکی کا حکم دو اور جاہلوں سے نہ الجھو');
INSERT INTO "ur_wahid_uddin" VALUES(7,200,'7:200','اور اگر تم کو کوئی وسوسہ شیطان کی طرف سے آئے تو اللہ کی پناہ چاہو بے شک وہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,201,'7:201','جو لوگ ڈر رکھتے ہیں جب کبھی شیطان کے اثر سے کوئی برا خیال انھیں چھوجاتا ہے تو وہ فوراً چونک پڑتے ہیں اور پھر اسی وقت ان کو سوجھ آجاتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(7,202,'7:202','اور جو شیطان کے بھائی ہیں وہ ان کو گمراہی میں کھینچے چلے جاتے ہیں ، پھر وہ کمی نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(7,203,'7:203','اور جب تم ان کے سامنے کوئی نشانی (معجزہ) نہیں لائے تو وہ کہتے ہیں کہ کیوں نہ تم چھانٹ لائے کچھ اپنی طرف سے کہو، میں تو اسی کی پیروی کرتا ہوں جو میرے رب کی طرف سے مجھ پر وحی کی جاتی ہے یہ سوجھ کی باتیں ہیں تمھارے رب کی طرف سے اور ہدایت اور رحمت ہے ان لوگوں کے ليے جو ایمان رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(7,204,'7:204','اور جب قرآن پڑھا جائے تو اس کو توجہ سے سنو اور خاموش رہو، تاکہ تم پر رحمت کی جائے');
INSERT INTO "ur_wahid_uddin" VALUES(7,205,'7:205','اور اپنے رب کو صبح و شام یا دکرو اپنے دل میں ، عاجزی اور خوف کے ساتھ اور پست آواز سے، اور غافلوں میں سے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(7,206,'7:206','جو (فرشتے) تیرے رب کے پاس ہیں ، وہ اس کی عبادت سے تکبر نہیں کرتے اور وہ اس کی پاک ذات کو یاد کرتے ہیں اور اسی کو سجدہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(8,1,'8:1','وہ تم سے انفال (مال غنیمت) کے بارے میں پوچھتے ہیں کہو کہ انفال اللہ اور اس کے رسول کے ہیں پس تم لوگ اللہ سے ڈرو اور اپنے آپس کے تعلقات کی اصلاح کرو اور اللہ اور اس کے رسول کی اطاعت کرو، اگر تم ایمان رکھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(8,2,'8:2','ایمان والے تو وہ ہیں کہ جب اللہ کاذکر کیا جائے تو ان کے دل دہل جائیں اور جب اللہ کی آیتیں ان کے سامنے پڑھی جائیں تو وہ ان کا ایمان بڑھادیتی ہیں اور وہ اپنے رب پر بھروسہ رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(8,3,'8:3','وہ نماز قائم کرتے ہیں اور جو کچھ ہم نے انھیں دیا ہے، اس میں سے خرچ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(8,4,'8:4','یہی لوگ حقیقی مومن ہیں ان کے ليے ان کے رب کے پاس درجے اور مغفرت ہیں اور ان کے ليے عزت کی روزی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,5,'8:5','جیسا کہ تمھارے رب نے تم کو حق کے ساتھ تمھارے گھر سے نکالا اور مومنوں میں سے ایک گروہ کو یہ ناگوار تھا');
INSERT INTO "ur_wahid_uddin" VALUES(8,6,'8:6','وہ اس حق کے معاملہ میں تم سے جھگڑ رہے تھے باوجود یہ کہ وہ ظاہر ہوچکا تھا، گویا کہ وہ موت کی طرف ہانکے جارہے ہیں آنکھوں دیکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(8,7,'8:7','اور جب خدا تم سے وعدہ کر رہا تھا کہ دو جماعتوں میں سے ایک تم کو مل جائے گی اور تم چاہتے تھے کہ جس میں کانٹا نہ لگے وہ تم کو ملے اور اللہ چاہتا تھا کہ وہ حق کاحق ہونا ثابت کردے اپنے کلمات سے اور منکروں کی جڑ کاٹ دے');
INSERT INTO "ur_wahid_uddin" VALUES(8,8,'8:8','تاکہ حق حق ہو کر رہے اور باطل باطل ہوکر رہ جائے خواہ مجرموں کو وہ کتنا ہی ناگوار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(8,9,'8:9','جب تم اپنے رب سے فریاد کر رہے تھے تو اس نے تمھاری فریاد سنی کہ میں تمھاری مدد کے ليے ایک ہزار فرشتے لگاتار بھیج رہا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(8,10,'8:10','اور یہ اللہ نے صرف اس ليے کیا تاکہ تمھارے ليے خوش خبری ہو اور تاکہ تمھارے دل اس سے مطمئن ہوجائیں اور مدد تو اللہ ہی کے پاس سے آتی ہے یقینا اللہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,11,'8:11','جب اللہ نے تم پر اونگھ ڈال دی اپنی طرف سے تمھاری تسکین کے ليے اور آسمان سے تمھارے اوپر پانی اتارا تاکہ اس کے ذریعہ سے وہ تمھیں پاک کرے اور تم سے شیطان کی نجاست کو دور کردے اور تمھارے دلوں کو مضبوط کردے اور اس سے قدموں کو جمادے');
INSERT INTO "ur_wahid_uddin" VALUES(8,12,'8:12','جب تیرے رب نے فرشتوں کو حکم بھیجا کہ میں تمھارے ساتھ ہوں ، تم ایمان والوں کو جمائے رکھو میں منکروں کے دل میں رعب ڈال دوں گا پس تم ان کی گردن کے اوپر مارو اور ان کے پور پور پر ضرب لگاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(8,13,'8:13','یہ اس سبب سے کہ انھوں نے اللہ اور اس کے رسول کی مخالفت کی اور جو کوئی اللہ اور اس کے رسول کی مخالفت کرتا ہے تو اللہ سزا دینے میں سخت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,14,'8:14','یہ تو اب چکھو اور جان لو کہ منکروں کے ليے آگ کا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,15,'8:15','اے ایمان والو، جب تمھارا مقابلہ منکرین سے میدانِ جنگ میں ہو تو ان سے پیٹھ مت پھیرو');
INSERT INTO "ur_wahid_uddin" VALUES(8,16,'8:16','اور جس نے ایسے موقع پر پیٹھ پھیری، سوا اس کے کہ جنگی چال کے طور پر ہو یا دوسری فوج سے جا ملنے کے ليے، تو وہ اللہ کے غضب میں آجائے گا اور اس کا ٹھکانا جہنم ہے اور وہ بہت ہی برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,17,'8:17','پس ان کو تم نے قتل نہیں کیا بلکہ اللہ نے قتل کیا اور جب تو نے ان پر خاک پھینکی تو تم نے نہیں پھینکی بلکہ اللہ نے پھینکی تاکہ اللہ اپنی طرف سے ایمان والوں پر خوب احسان کرے بے شک اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,18,'8:18','یہ تو ہوچکا اور بے شک اللہ منکرین کی تمام تدبیریں بے کار کر کے رہے گا');
INSERT INTO "ur_wahid_uddin" VALUES(8,19,'8:19','اگر تم فیصلہ چاہتے تھے تو فیصلہ تمھارے سامنے آگیا اور اگر تم باز آجاؤ تو یہ تمھارے حق میں بہتر ہے اور اگر تم پھر وہی کرو گے تو ہم بھی پھر وہی کریں گے اور تمھارا جتھا تمھارے کچھ کام نہ آئے گا خواہ وہ کتنا ہی زیادہ ہو اور بے شک اللہ ایمان والوں کے ساتھ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,20,'8:20','اے ایمان والو، اللہ اور اس کے رسول کی اطاعت کرو اور اس سے روگردانی نہ کرو حالاں کہ تم سن رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(8,21,'8:21','اور ان لوگوں کی طرح نہ ہوجاؤ جنھوں نے کہا کہ ہم نے سنا، حالاں کہ وہ نہیں سنتے');
INSERT INTO "ur_wahid_uddin" VALUES(8,22,'8:22','یقیناً اللہ کے نزدیک بدترین جانور وہ بہرے گونگے لوگ ہیں جو عقل سے کام نہیں لیتے');
INSERT INTO "ur_wahid_uddin" VALUES(8,23,'8:23','اور اگر ان میں کسی بھلائی کا علم اللہ کو ہوتا تووہ ضرور انھیں سننے کی توفیق دیتا اور اگر اب وہ انھیں سنوادے تو وہ ضرور روگردانی کریں گے بے رخی کرتے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(8,24,'8:24','اے ایمان والو، اللہ اور رسول کی پکار پر لبیک کہو جب کہ رسول تم کو اس چیز کی طرف بلا رہا ہے جو تم کو زندگی دینے والی ہے اور جان لو کہ اللہ آدمی اور اس کے دل کے درمیان حائل ہوجاتا ہے اور یہ کہ اسی کی طرف تمھارا اكٹھا ہونا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,25,'8:25','اور ڈرو اس فتنہ سے جو خاص انھیں لوگوں پر واقع نہ ہوگا جو تم میں سے ظلم کے مرتکب ہوئے ہیں اور جان لو کہ اللہ سخت سزا دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,26,'8:26','اور یاد کرو جب کہ تم تھوڑے تھے اور زمین میں کمزور سمجھے جاتے تھے ڈرتے تھے کہ لوگ اچانک تم کو اچک نہ لیں پھر اللہ نے تم کو رہنے کی جگہ دی اور اپنی نصرت سے تمھاری تائید کی اور تم کو پاکیزہ روزی دی تاکہ تم شکر گزار بنو');
INSERT INTO "ur_wahid_uddin" VALUES(8,27,'8:27','اے ایمان والو، خیانت نہ کرو اللہ اور رسول کی اور خیانت نہ کرو اپنی امانتوں میں حالاں کہ تم جانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(8,28,'8:28','اور جان لو کہ تمھارے مال اور تمھاری اولاد ایک آزمائش ہے اور یہ کہ اللہ ہی کے پاس ہے بڑا اجر');
INSERT INTO "ur_wahid_uddin" VALUES(8,29,'8:29','اے ایمان والو، اگر تم اللہ سے ڈرو گے تو وہ تمھارے ليے فرقان بہم پہنچائے گا اور تم سے تمھارے گناہوں کو دور کردے گا اور تم کو بخش دے گا اور اللہ بڑے فضل والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,30,'8:30','اور جب منکرتمھاری نسبت تدبیریں سوچ رہے تھے کہ وہ تم کو قید کردیں یا قتل کر ڈالیں یا جلا وطن کردیں وہ اپنی تدبیریں کر رہے تھے اور اللہ اپنی تدبیریں کر رہا تھا اور اللہ بہترین تدبیر والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,31,'8:31','اور جب ان کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو کہتے ہیں ہم نے سن لیا اگر ہم چاہیں تو ہم بھی ایسا ہی کلام پیش کردیں یہ تو بس اگلوں کی کہانیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(8,32,'8:32','اور جب انھوں نے کہا کہ اے اللہ، اگر یہی حق ہے تیرے پاس سے تو ہم پر آسمان سے پتھر برسادے یا اور کوئی درد ناک عذاب ہم پر لے آ');
INSERT INTO "ur_wahid_uddin" VALUES(8,33,'8:33','اور اللہ ایسا کرنے والا نہیں کہ ان کو عذاب دے اس حال میں کہ تم ان میں موجود ہو اور اللہ ان پر عذاب لانے والا نہیں جب کہ وہ استغفار کر رہے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(8,34,'8:34','اور اللہ ان کو کیوں نہ عذاب دے گا حالاں کہ وہ مسجد حرام سے روکتے ہیں جب کہ وہ اس کے متولی نہیں اس کے متولی تو صرف اللہ سے ڈرنے والے ہوسکتے ہیں مگر ان میں سے اکثر اس کو نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(8,35,'8:35','اور بیت اللہ کے پاس ان کی نماز سیٹی بجانے اورتالی پیٹنے کے سوا اور کچھ نہیں پس اب چکھو عذاب اپنے کفر کا');
INSERT INTO "ur_wahid_uddin" VALUES(8,36,'8:36','جن لوگوں نے انکار کیا وہ اپنے مال کو اس ليے خرچ کرتے ہیں کہ لوگوں کو اللہ کی راہ سے روکیں وہ اس کو خرچ کرتے رہیں گے پھر یہ ان کے ليے حسرت بنے گا پھر وہ مغلوب کيے جائیں گے اور جنھوں نے انکار کیا، ان کو جہنم کی طرف اکھٹا کیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(8,37,'8:37','تاکہ اللہ ناپاک کو الگ کردے پاک سے اور ناپاک کو ایک پر ایک رکھے پھر اس ڈھیر کو جہنم میں ڈال دے، یہی لوگ ہیں خسارہ میں پڑنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(8,38,'8:38','انکار کرنے والوں سے کہو کہ اگر وہ باز آجائیں تو جو کچھ ہوچکا ہے وہ انھیں معاف کردیا جائے گا اور اگر وہ پھر وہی کریں گے تو ہمارا معاملہ اگلوں کے ساتھ گزر چکا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,39,'8:39','اور ان سے لڑو یہاں تک کہ فتنہ باقی نہ رہے اور دین سب اللہ کے ليے ہوجائے پھر اگر وہ باز آجائیں تو اللہ دیکھنے والا ہے ان کے عمل کا');
INSERT INTO "ur_wahid_uddin" VALUES(8,40,'8:40','اور اگر انھوں نے اعراض کیا تو جان لو کہ اللہ تمھارا مولیٰ ہے اور کیا ہی اچھا مولیٰ ہے اور کیا ہی اچھا مددگار');
INSERT INTO "ur_wahid_uddin" VALUES(8,41,'8:41','اور جان لو کہ جو کچھ مال غنیمت تمھیں حاصل ہو، اس کا پانچواں حصہ اللہ اور رسول کے ليے اور رشتہ داروں اور یتیموں اور مسکینوں اور مسافروں کے ليے ہے، اگر تم ایمان رکھتے ہو اللہ پر اور اس چیز پر جو ہم نے اپنے بندے (محمد) پر اتاری فیصلہ کے دن، جس دن کہ دونوں جماعتوں میں مڈبھیڑ ہوئی اور اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,42,'8:42','اور جب کہ تم وادی کے قریبی کنارے پر تھے اور وہ دور کے کنارے پر اور قافلہ تم سے نیچے کی طرف تھااور اگر تم اور وہ وقت مقرر کرتے تو ضرور اس تقرر کے بارے میں تم میں اختلاف ہوجاتا لیکن جو ہوا وہ اس ليے ہوا تاکہ اللہ اس امر کا فیصلہ کردے جس کو ہوکر رہنا تھا، تاکہ جس کو ہلاک ہونا ہے، وہ روشن دلیل کے ساتھ ہلاک ہو اور جس کو زندگی حاصل کرنا ہے، وہ روشن دلیل کے ساتھ زندہ رہے یقیناً اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,43,'8:43','جب اللہ تمھارے خواب میں ان کو تھوڑا دکھاتا رہا اگر وہ ان کو زیادہ دکھادیتا تو تم لوگ ہمت ہارجاتے اور آپس میں جھگڑنے لگتے اس معاملہ میں لیکن اللہ نے تم کو بچالیا یقیناً وہ دلوں تک کا حال جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,44,'8:44','اور جب اللہ نے ان لوگوں کو تمھاری نظر میں کم کرکے دکھایا اور تم کو ان کی نظر میں کم کرکے دکھایا تاکہ اللہ اس امر کا فیصلہ کردے جس کا ہونا طے تھا اور سارے معاملات اللہ ہی کی طرف لوٹتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(8,45,'8:45','اے ایمان والو، جب کسی گروہ سے تمھارا مقابلہ ہو تو تم ثابت قدم رہو اور اللہ کو بہت یاد کرو تاکہ تم کامیاب ہو');
INSERT INTO "ur_wahid_uddin" VALUES(8,46,'8:46','اور اطاعت کرو اللہ کی اور اس کے رسول کی اور آپس میں جھگڑا نہ کرو، ورنہ تمھارے اندر کمزوری آجائے گی اور تمھاری ہوا اکھڑ جائے گی اور صبر کرو، بے شک اللہ صبر کرنے والوں کے ساتھ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,47,'8:47','اور ان لوگوں کی طرح نہ بنو جو اپنے گھروں سے اکڑتے ہوئے اور لوگوں کو دکھلاتے ہوئے نکلے اور جو اللہ کی راہ سے روکتے ہیں حالاں کہ وہ جو کچھ کر رہے ہیں اللہ ان کا احاطہ کيے ہوئے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,48,'8:48','اور جب شیطان نے انھیں ان کے اعمال خوش نما بناکر دکھائے اور کہا کہ لوگوں میں سے آج کوئی تم پر غالب آنے والانہیں اور میں تمھارے ساتھ ہوں مگر جب دونوں گروہ آمنے سامنے ہوئے تو وہ الٹے پاؤں بھاگا اور کہا کہ میں تم سے بری ہوں ، میں وہ کچھ دیکھ رہا ہوں جو تم لوگ نہیں دیکھتے میں اللہ سے ڈرتا ہوں اور اللہ سخت سزا دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,49,'8:49','جب منافق اور جن کے دلوں میں روگ ہے، وہ کہتے تھے کہ ان لوگوں کو ان کے دین نے دھوکے میں ڈال دیا ہے، اور جو اللہ پر بھروسہ کرے تو اللہ بڑا زبردست اور حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,50,'8:50','اور اگر تم دیکھتے جب کہ فرشتے ان منکرین کی جان قبض کرتے ہیں ، مارتے ہوئے ان کے چہروں اور ان کی پیٹھوں پر، اور یہ کہتے ہوئے کہ اب جلنے کا عذاب چکھو');
INSERT INTO "ur_wahid_uddin" VALUES(8,51,'8:51','یہ بدلہ ہے اس کا جو تم نے اپنے ہاتھوں آگے بھیجا تھا اور اللہ ہرگز بندوں پر ظلم کرنے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(8,52,'8:52','فرعون والوں کی طرح اور جو ان سے پہلے تھے کہ انھوں نے اللہ کی نشانیوں کا انکار کیا، پس اللہ نے ان کے گناہوں پر ان کو پکڑ لیا بے شک اللہ قوت والا ہے، سخت سزا دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,53,'8:53','یہ اس وجہ سے ہوا کہ اللہ اس انعام کو جوو ہ کسی قوم پر کرتا ہے، اس وقت تک نہیں بدلتا جب تک وہ اس کو نہ بدل دیں جو ان کے نفسوں میں ہے اور بے شک اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,54,'8:54','فرعون والوں کی طرح اور جو ان سے پہلے تھے کہ انھوں نے اپنے رب کی نشانیوں کو جھٹلایا پھر ہم نے ان کے گناہوں کے سبب سے ان کو ہلاک کردیا اور ہم نے فرعون والوں کو غرق کردیا اور یہ سب لو گ ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(8,55,'8:55','بے شک سب جانداروں میں بدترین اللہ کے نزدیک وہ لوگ ہیں جنھوں نے انکار کیا اور وہ ایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(8,56,'8:56','جن سے تم نے عہد لیا، پھر وہ اپنا عہد ہر بار توڑ دیتے ہیں اور وہ ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(8,57,'8:57','پس اگر تم ان کو لڑائی میں پاؤ تو ان کو ایسی سزا دو کہ جو ان کے پیچھے ہیں وہ بھی دیکھ کر بھاگ جائیں ، تاکہ انھیں عبرت ہو');
INSERT INTO "ur_wahid_uddin" VALUES(8,58,'8:58','اور اگر تم کو کسی قوم سے بد عہدی کا ڈر ہو تو ان کا عہد ان کی طرف پھینک دو، ایسی طرح کہ تم اور وہ برابر ہوجائیں بے شک اللہ بد عہدوں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(8,59,'8:59','اور انکار کرنے والے یہ نہ سمجھیں کہ وہ نکل بھاگیں گے، وہ ہرگز اللہ کو عاجز نہیں کرسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(8,60,'8:60','اور ان کے ليے جس قدر تم سے ہو سکے تیار رکھو قوت اور پلے ہوئے گھوڑے کہ اس سے تمھاری ہیبت رہے اللہ کے دشمنوں پر اور تمھارے دشمنوں پر اور ان کے علاوہ دوسروں پر بھی جن کو تم نہیں جانتے اللہ ان کو جانتا ہے اور جو کچھ تم اللہ کی راہ میں خرچ کرو گے، وہ تمھیں پورا کردیا جائے گا اور تمھارے ساتھ کوئی کمی نہ کی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(8,61,'8:61','اور اگر وہ صلح کی طرف جھکیں تو تم بھی اس کے ليے جھک جاؤ اور اللہ پر بھروسہ رکھو بے شک وہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,62,'8:62','اور اگر وہ تم کو دھوکا دینا چاہیں گے تو اللہ تمھارے ليے کافی ہے وہی ہے جس نے اپنی نصرت اور مومنین کے ذریعہ تم کو قوت دی');
INSERT INTO "ur_wahid_uddin" VALUES(8,63,'8:63','اور ان کے دلوں میں اتفاق پیدا کردیا اگر تم زمین میں جو کچھ ہے سب خرچ کر ڈالتے، تب بھی تم ان کے دلوں میں اتفاق پیدا نہ کرسکتے لیکن اللہ نے ان میں اتفاق پیدا کردیا، بے شک وہ زور آور ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,64,'8:64','اے نبی، تمھارے ليے اللہ کافی ہے اور وہ مومنین جنھوں نے تمھارا ساتھ دیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,65,'8:65','اے نبی، مومنین کو لڑائی پر ابھارو اگر تم میں بیس آدمی ثابت قدم ہوں گے تو وہ دو سو پر غالب آئیں گے اور اگر تم میں سوہوں گے تو وہ ہزار منکروں پر غالب آئیں گے، اس واسطے کہ وہ لوگ سمجھ نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(8,66,'8:66','اب اللہ نے تم پر سے بوجھ ہلکا کردیا اور اس نے جان لیا کہ تم میں کچھ کمزوری ہے پس اگر تم میں سو ثابت قدم ہوں گے تو وہ دو سو پر غالب آئیں گے اور اگر ہزار ہوں گے تو وہ اللہ کے حکم سے دو ہزار پر غالب آئیں گے، اور اللہ ثابت قدم رہنے والوں کے ساتھ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,67,'8:67','کسی نبی کے ليے لائق نہیں کہ اس کے پاس قیدی ہوں جب تک وہ زمین میں اچھی طرح خوں ریزی نہ کرلے تم دنیا کے اسباب چاہتے ہو اور اللہ آخرت کو چاہتا ہے اور اللہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,68,'8:68','اور اگر اللہ کا ایک لکھا ہوا پہلے سے موجود نہ ہوتا تو جو طریقہ تم نے اختیار کیا، اس کے باعث تم کو سخت عذاب پہنچ جاتا');
INSERT INTO "ur_wahid_uddin" VALUES(8,69,'8:69','پس جو مال تم نے لیا ہے اس کو کھاؤ،وه تمھارے ليے حلال اور پاک ہے اور اللہ سے ڈرو، بے شک اللہ بخشنے والا مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,70,'8:70','اے نبی، تمھارے ہاتھ میں جو قیدی ہیں ، ان سے کہہ دو کہ اگر اللہ تمھارے دلوں میں کوئی بھلائی پائے گا تو جو کچھ تم سے لیا گیا ہے، اس سے بہتر وہ تمھیں دے دے گا اور تم کو بخش دے گا اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,71,'8:71','اور اگر یہ تم سے بد عہدی کریں گے تو اس سے پہلے انھوں نے خدا سے بدعہدی کی تو خدا نے تم کو ان پر قابو دے دیا اور اللہ علم والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,72,'8:72','جو لوگ ایمان لائے اور جنھوں نے ہجرت کی اور اللہ کی راہ میں اپنے جان و مال سے جہاد کیا اور وہ لوگ جنھوں نے پناہ دی اور مدد کی، وہ لوگ ایک دوسرے کے رفیق ہیں اور جو لوگ ایمان لائے مگر انھوں نے ہجرت نہیں کی تو ان سے تمھارا رفاقت کا کوئی تعلق نہیں جب تک کہ وہ ہجرت کرکے نہ آجائیں اور وہ تم سے دین کے معاملہ میں مدد مانگیں تو تم پر ان کی مد دکرنا واجب ہے، الا یہ کہ مدد کسی ایسی قوم کے خلاف ہو جس کے ساتھ تمھارا معاہدہ ہے اور جو کچھ تم کرتے ہو اللہ اس کو دیکھ رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,73,'8:73','اور جو لوگ منکر ہیں ، وہ ایک دوسرے کے رفیق ہیں اگر تم ایسا نہ کرو گے تو زمین میں فتنہ پھیلے گا اور بڑا فساد ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(8,74,'8:74','اور جو لوگ ایمان لائے اور انھوں نے ہجرت کی اور اللہ کی راہ میں جہاد کیا اور جن لوگوں نے پناہ دی اور مدد کی، یہی لوگ سچے مومن ہیں ان کے ليے بخشش ہے اور بہترین رزق ہے');
INSERT INTO "ur_wahid_uddin" VALUES(8,75,'8:75','اور جو لوگ بعد میں ایمان لائے اور ہجرت کی اور تمھارے ساتھ مل کر جہاد کیا وہ بھی تم میں سے ہیں اور خون کے رشتہ دار ایک دوسرے کے زیادہ حق دار ہیں اللہ كي كتاب میں بے شک اللہ ہر چیز کا جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,1,'9:1','اعلانِ براء ت ہے اللہ اور اس کے رسول کی طرف سے ان مشرکین کو جن سے تم نے معاہدہ کيے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(9,2,'9:2','پس تم لوگ ملک میں چار مہینے چل پھر لو اور جان لو کہ تم اللہ کو عاجز نہیں کرسکتے اور یہ کہ اللہ منکروں کو رسوا کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,3,'9:3','اعلان ہے اللہ اور رسول کی طرف سے بڑے حج کے دن لوگوں کے ليے کہ اللہ اور اس کا رسول مشرکوں سے بری الذمہ ہیں اب اگر تم لوگ توبہ کرو تو تمھارے حق میں بہتر ہے اور اگر تم منہ پھیرو گے تو جان لو کہ تم اللہ کو عاجز کرنے والے نہیں ہو اور انکار کرنے والوں کو سخت عذاب کی خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(9,4,'9:4','مگر جن مشرکوں سے تم نے معاہدہ کیا تھا، پھر انھوں نے تمھارے ساتھ کوئی کمی نہیں کی اور نہ تمھارے خلاف کسی کی مدد کی تو ان کا معاہدہ ان کی مدت تک پورا کرو بے شک اللہ پرہیز گارو ں کو پسند کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,5,'9:5','پھر جب حرمت والے مہینے گز رجائیں تو مشرکین کو قتل کرو جہاں پاؤ اور ان کو پکڑو اور ان کو گھیرو اور بیٹھو ہر جگہ ان کی گھات میں پھر اگر وہ توبہ کرلیں اور نماز قائم کریں اور زکوٰۃ ادا کریں تو انھیں چھوڑ دو اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,6,'9:6','اور اگر مشرکین میں سے کوئی شخص تم سے پناہ مانگے تو تم اس کو پنا ہ دے دو تاکہ وہ اللہ کا کلام سنے پھر اس کو اس کے امان کی جگہ پہنچادو یہ اس ليے کہ وہ لوگ علم نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(9,7,'9:7','ان مشرکوں کے ليے اللہ اور اس کے رسول کے ذمہ کوئی عہد کیسے رہ سکتا ہے، مگر جن لوگوں سے تم نے عہد کیا تھا مسجد حرام کے پاس، پس جب تک وہ تم سے سیدھے رہیں تم بھی ان سے سیدھے رہو، بے شک اللہ پرہیز گاروں کو پسند کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,8,'9:8','کیسے عہد رہے گا جب کہ یہ حال ہے کہ اگر وہ تمھارے اوپر قابو پائیں تو تمھارے بارے میں نہ قرابت کا لحاظ کریں اور نہ عہد کا وہ تم کو اپنے منہ کی بات سے راضی کرنا چاہتے ہیں مگر ان کے دل انکار کرتے ہیں اور ان میں اکثر بدعہدہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,9,'9:9','انھوں نے اللہ کی آیتوں کو تھوڑی قیمت پر بیچ دیا، پھر انھوں نے اللہ کے راستہ سے روکا بہت برا ہے جو وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,10,'9:10','کسی مومن کے معاملہ میں وہ نہ قرابت کا لحاظ کرتے ہیں اور نہ عہد کا، یہی لوگ ہیں زیادتی کرنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(9,11,'9:11','پس اگر وہ توبہ کریں اور نماز قائم کریں اور زکوٰۃ ادا کریں تو وہ تمھارے دینی بھائی ہیں اور ہم کھول کر بیان کرتے ہیں آیات کو جاننے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(9,12,'9:12','اور اگر عہد کے بعد یہ اپنی قسموں کو توڑ ڈالیں اور تمھارے دین میں عیب لگائیں تو کفر کے ان سرداروں سے لڑو بے شک ان کی قسمیں کچھ نہیں ، تاکہ وہ باز آئیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,13,'9:13','کیا تم نہ لڑو گے ایسے لوگوں سے جنھوں نے اپنے عہد توڑ دئے اور رسول کو نکالنے کی جسارت کی اور وہی ہیں جنھوں نے تم سے جنگ میں پہل کی کیا تم ان سے ڈرو گے اللہ زیادہ مستحق ہے کہ تم اس سے ڈرو اگر تم مومن ہو');
INSERT INTO "ur_wahid_uddin" VALUES(9,14,'9:14','ان سے لڑو اللہ تمھارے ہاتھوں ان کو سزا دے گا اور ان کو رُسوا کرے گا اور تم کو ان پر غلبہ دے گا اور اہلِ ایمان کے سینہ کو ٹھنڈا کرے گا اور ان کے دل کی جلن کو دور کردے گا');
INSERT INTO "ur_wahid_uddin" VALUES(9,15,'9:15','اور اللہ توبہ نصیب کرے گا جس کو چاہے گا اور اللہ جاننے والا ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,16,'9:16','کیا تمھارا یہ گمان ہے کہ تم چھوڑ دئے جاؤ گے حالاں کہ ابھی اللہ نے تم میں سے ان لوگوں کو جانا ہی نہیں جنھوں نے جہاد کیا اور جنھوں نے اللہ اور رسول اور مومنین کے سوا کسی کو دوست نہیں بنایا اور اللہ جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(9,17,'9:17','مشرکوں کا کام نہیں کہ وہ اللہ کی مسجدوں کو آباد کریں حالاں کہ وہ خود اپنے اوپر کفر کے گواہ ہیں ان لوگوں کے اعمال اکارت گئے اور وہ ہمیشہ آگ میں رہنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,18,'9:18','اللہ کی مسجدوں کو تو وہ آباد کرتا ہے جو اللہ اور آخرت کے دن پر ایمان لائے اور نماز قائم کرے اور زکوٰۃ ادا کرے اور اللہ کے سوا کسی سے نہ ڈرے ایسے لوگ امید ہے کہ ہدایت پانے والوں میں سے بنیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,19,'9:19','کیا تم نے حاجیوں کے پانی پلانے اور مسجد حرام کے بسانے کو برابر کردیا اس شخص کے جو اللہ اور آخرت پر ایمان لایا اور اللہ کی راہ میں جہاد کیا، اللہ کے نزدیک یہ دونوں برابر نہیں ہوسکتے اور اللہ ظالم لوگوں کو راہ نہیں دکھاتا');
INSERT INTO "ur_wahid_uddin" VALUES(9,20,'9:20','جولوگ ایمان لائے اور انھوں نے ہجرت کی اور اللہ کے راستہ میں اپنے جان و مال سے جہاد کیا، ان کا درجہ اللہ کے یہاں بڑا ہے اور یہی لوگ کامیاب ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,21,'9:21','ان کا رب ان کو خوش خبری دیتا ہے اپنی رحمت اور خوشنودی کی اور ایسے باغو ں کی جن میں ان کے ليے دائمی نعمت ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(9,22,'9:22','ان میں وہ ہمیشہ رہیں گے بے شک اللہ ہی کے پاس بڑا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,23,'9:23','اے ایمان والو، اپنے باپوں اور اپنے بھائیوں کو دوست نہ بناؤ اگر وہ ایمان کے مقابلہ میں کفر کو عزیز رکھیں اور تم میں سے جو ان کو اپنا دوست بنائیں گے تو ایسے ہی لوگ ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,24,'9:24','کہو کہ اگر تمھارے باپ اور تمھارے لڑکے اور تمھارے بھائی اور تمھاری بیویاں اور تمھارا خاندان اور وہ مال جو تم نے کمائے ہیں اور وہ تجارت جس کے بند ہونے سے تم ڈرتے ہو اور وہ گھر جن کو تم پسند کرتے ہو، یہ سب تم کو اللہ اور اس کے رسول اور اس کی راہ میں جہاد کرنے سے زیادہ محبوب ہیں تو انتظار کرو یہاں تک کہ اللہ اپنا حکم بھیج دے اور اللہ نافرمان لوگوں کو راستہ نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(9,25,'9:25','بے شک اللہ نے بہت سے موقعوں پر تمھاری مدد کی ہے اور حنین کے دن بھی جب تمھاری کثرت نے تم کو ناز میں مبتلا کردیا تھا پھر وہ تمھارے کچھ کام نہ آئی اور زمین اپنی وسعت کے باوجود تم پر تنگ ہوگئی، پھر تم پیٹھ پھیر کر بھاگے');
INSERT INTO "ur_wahid_uddin" VALUES(9,26,'9:26','اس کے بعد اللہ نے اپنے رسول اور مومنین پر اپنی سکینت اتاری اور ایسے لشکر اتارے جن کو تم نے نہیں دیکھا اور اللہ نے منکروں کو سزا دی اور یہی منکروں کا بدلہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,27,'9:27','پھر اس کے بعد اللہ جس کو چاہے توبہ نصیب کردے اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,28,'9:28','اے ایمان والو، مشرکین بالکل ناپاک ہیں پس وہ اس سال کے بعد مسجد حرام کے پاس نہ آئیں اور اگر تم کو مفلسی کا اندیشہ ہو تو اللہ اگر چاہے گا تو اپنے فضل سے تم کو بے نیاز کردے گا، اللہ علیم اور حکیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,29,'9:29','ان اہل کتاب سے لڑو جو نہ اللہ پر ایمان رکھتے ہیں اور نہ آخرت کے دن پر اور نہ اللہ اور اس کے رسول کے حرام ٹھہرائے ہوئے کو حرام ٹھہراتے ہیں اور نہ دین حق کو اپنا دین بناتے ہیں ،یہاں تک کہ وہ اپنے ہاتھ سے جزیہ دیں اور چھوٹے بن کر رہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,30,'9:30','اور یہود نے کہا کہ عزیر اللہ کے بیٹے ہیں اور نصاریٰ نے کہا کہ مسیح اللہ کے بیٹے ہیں یہ ان کے اپنے منہ کی باتیں ہیں وہ ان لوگوں کی بات کی نقل کر رہے ہیں جنھوں نے ان سے پہلے کفر کیا اللہ ان کو ہلاک کرے، وہ کدھر بہکے جارہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,31,'9:31','انھوں نے اللہ کے سوا اپنے علماء اور مشائخ کو رب بنا ڈالا اور مسیح ابن مریم کو بھی حالاں کہ ان کو صرف یہ حکم تھا کہ وہ ایک معبود کی عبادت کریں اس كے سوا كوئي معبود نهيں ، وہ پاک ہے اس سے جو وہ شریک کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,32,'9:32','وہ چاہتے ہیں کہ اللہ کی روشنی کو اپنے منہ سے بجھادیں اور اللہ اپنی روشنی کو پورا کيے بغیر ماننے والا نہیں ، خواہ منکروں کو یہ کتنا ہی ناگوار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(9,33,'9:33','اسی نے اپنے رسول کو بھیجا ہے ہدایت اور دین حق کے ساتھ تاکہ اس کو سارے دین پر غالب کردے خواہ یہ مشرکوں کو کتنا ہی ناگوار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(9,34,'9:34','اے ایمان والو، اہل کتاب کے اکثر علماء اور مشائخ لوگوں کے مال باطل طریقوں سے کھاتے ہیں اور لوگوں کو اللہ کے راستے سے روکتے ہیں اور جو لوگ سونا او چاندی جمع کر کے رکھتے ہیں اور ان کو اللہ کی راہ میں خرچ نہیں کرتے، ان کو ایک درد ناک عذاب کی خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(9,35,'9:35','اس دن اس مال پر دوزخ کی آگ دہکائی جائے گیپھر اس سے ان کی پیشانیاں اور ان کے پہلو اور ان کی پیٹھیں داغی جائیں گی یہی ہے وہ جس کو تم نے اپنے واسطے جمع کیا تھا پس اب چکھو جو تم جمع کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,36,'9:36','مہینوں کی گنتی اللہ کے نزدیک بارہ مہینے ہیں اللہ کی کتاب میں ، جس دن سے اس نے آسمانوں اور زمین کو پیدا کیا، ان میں سے چار حرمت والے ہیں یہی ہے سیدھا دین پس ان میں تم اپنے اوپر ظلم نہ کرو اور مشرکوں سے سب مل کر لڑو جس طرح وہ سب مل کر تم سے لڑتے ہیں اور جان لو کہ اللہ متقیوں کے ساتھ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,37,'9:37','مہینوں کا ہٹادینا کفر میں ایک اضافہ ہے اس سے کفر کرنے والے گمراہی میں پڑتے ہیں ، وہ کسی سال حرام مہینہ کو حلال کرلیتے ہیں اور کسی سال اس کو حرام کردیتے ہیں تاکہ خدا کے حرام کيے ہوئے کی گنتی پوری کرکے اس کے حرام کيے ہوئے کو حلال کرلیں ان کے برے اعمال ان کے ليے خوش نما بنادئے گئے ہیں اور اللہ انکار کرنے والوں کو راستہ نہیں دکھاتا');
INSERT INTO "ur_wahid_uddin" VALUES(9,38,'9:38','اے ایمان والو، تم کو کیا ہوگیا ہے کہ جب تم سے کہا جاتا ہے کہ اللہ کی راہ میں نکلو تو تم زمین سے لگے جاتے ہو کیا تم آخرت کے مقابلہ میں دنیا کی زندگی پر راضی ہوگئے آخرت کے مقابلہ میں دنیا کی زندگی کا سامان تو بہت تھوڑا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,39,'9:39','اگر تم نہ نکلو گے تو خدا تم کو درد ناک سزا دے گا اور تمھاری جگہ وہ دوسری قوم کو لے آئے گا اور تم خدا کا کچھ بھی نہ بگاڑ سکو گے اور خدا ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,40,'9:40','اگر تم رسول کی مدد نہ کرو گے تو اللہ خود اس کی مددکرچکا ہے جب کہ منکروں نے اس کو نکال دیا تھا، وہ صرف دو میں کا دوسرا تھا جب وہ دونوں غار میں تھے جب وہ اپنے ساتھی سے کہہ رہا تھا کہ غم نہ کرو، اللہ ہمارے ساتھ ہے پس اللہ نے اس پر اپنی سکینت نازل فرمائی اور اس کی مدد ایسے لشکروں سے کی جو تم کو نظر نہ آتے تھے اور اللہ نے منکروں کی بات نیچی کردی اور اللہ ہی کی بات تو اونچی ہے اور اللہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,41,'9:41','ہلکے اور بوجھل اور اپنے مال اور اپنی جان سے اللہ کی راہ میں جہاد کرو، یہ تمھارے ليے بہتر ہے اگر تم جانو');
INSERT INTO "ur_wahid_uddin" VALUES(9,42,'9:42','اگر نفع قریب ہوتا اور سفر ہلکا ہوتا تو وہ ضرو رتمھارے پیچھے ہولیتے، مگر یہ منزل ان پر کٹھن ہوگئی اب وہ قسمیں کھائیں گے کہ اگر ہم سے ہوسکتا تو ہم ضرو رتمھارے ساتھ چلتے وہ اپنے آپ کو ہلاکت میں ڈال رہے ہیں اور اللہ جانتا ہے کہ یہ لوگ یقیناً جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,43,'9:43','اللہ تم کو معاف کرے، تم نے کیوں انھیں اجازت دے دی یہاں تک کہ تم پر کھل جاتا کہ کون لوگ سچے ہیں اور جھوٹوں کو بھی تم جان لیتے');
INSERT INTO "ur_wahid_uddin" VALUES(9,44,'9:44','جو لوگ اللہ پر اور آخرت کے دن پر ایمان رکھتے ہیں ، وہ کبھی تم سے یہ درخواست نہ کریں گے کہ وہ اپنے مال اور اپنی جان سے جہاد نہ کریں ، اور اللہ ڈرنے والوں کو خوب جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,45,'9:45','تم سے اجازت تو وہی لوگ مانگتے ہیں جو اللہ پر اور آخرت کے دن پر ایمان نہیں رکھتے اور ان کے دل شک میں پڑے ہوئے ہیں پس وہ اپنے شک میں بھٹک رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,46,'9:46','اور اگر وہ نکلنا چاہتے تو ضرور وہ اس کا کچھ سامان کرلیتے مگر اللہ نے ان کا اٹھنا پسند نہ کیا اس ليے انھیں جما رہنے دیا اور کہہ دیا گیا کہ بیٹھنے والوں کے ساتھ بیٹھے رہو');
INSERT INTO "ur_wahid_uddin" VALUES(9,47,'9:47','اگر یہ لوگ تمھارے ساتھ نکلتے تو وہ تمھارے ليے خرابی ہی بڑھانے کا باعث بنتے اور وہ تمھارے درمیان فتنہ پردازی کے ليے دوڑ دھوپ کرتے اور تم میں ان کی سننے والے ہیں اور اللہ ظالموں سے خوب واقف ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,48,'9:48','یہ پہلے بھی فتنہ کی کوشش کرچکے ہیں اور وہ تمھارے ليے کاموں کا الٹ پھیر کرتے رہے ہیں یہاں تک کہ حق آگیا اور اللہ کا حکم ظاہر ہوگیا اور وہ ناخوش ہی رہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,49,'9:49','اور ان میں وہ بھی ہیں جو کہتے ہیں کہ مجھے رخصت دے دیجيے اور مجھ کو فتنہ میں نہ ڈاليے سن لو، وہ تو فتنہ میں پڑچکے اور بے شک جہنم منکروں کو گھیرے ہوئے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,50,'9:50','اگر تمھیں کوئی اچھائی پیش آتی ہے تو ان کو دکھ ہوتا ہے اور اگر تم کوکوئی مصیبت پہنچتی ہے تو وہ کہتے ہیں ہم نے پہلے ہی اپنا بچاؤ کرلیا تھا اور وہ خوش ہوکر لوٹتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,51,'9:51','کہو، ہمیں صرف وہی چیز پہنچے گی جو اللہ نے ہمارے ليے لکھ دی ہے وہ ہمارا کار ساز ہے اور اہل ایمان کو اللہ ہی پر بھروسہ کرنا چاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(9,52,'9:52','کہو، تم ہمارے ليے صرف دو بھلائیوں میں سے ایک بھلائی کے منتظر ہو مگر ہم تمھارے حق میں اس کے منتظر ہیں کہ اللہ تم پر عذاب بھیجے اپنی طرف سے یا ہمارے ہاتھوں سے پس تم انتظار کرو ہم بھی تمھارے ساتھ انتظار کرنے والوں میں سے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,53,'9:53','کہو، تم خوشی سے خرچ کرو یا ناخوشی سے، تم سے ہرگز قبول نہ کیا جائے گا بے شک تم نافرمان لوگ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(9,54,'9:54','اور وہ اپنے خرچ کی قبولیت سے صرف اس ليے محروم ہوئے کہ انھوں نے اللہ اور اس کے رسول کا انکار کیا اور یہ لوگ نماز کے ليے آتے ہیں تو گرانی کے ساتھ آتے ہیں اور خرچ کرتے ہیں تو ناگواری کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(9,55,'9:55','تم ان کے مال اور اولاد کو کچھ وقعت نہ دو اللہ تو یہ چاہتا ہے کہ ان کے ذریعے سے انھیں دنیا کی زندگی میں عذاب دے اور ان کی جانیں اس حالت میں نکلیں کہ وہ منکرہوں');
INSERT INTO "ur_wahid_uddin" VALUES(9,56,'9:56','وہ خداکی قسم کھاکر کہتے ہیں کہ وہ تم میں سے ہیں حالاں کہ وہ تم میں سے نہیں بلکہ وہ ایسے لوگ ہیں جو تم سے ڈرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,57,'9:57','اگر وہ کوئی پناہ کی جگہ پائیں یا کوئی کھوہ یا گھس بیٹھنے کی جگہ تو وہ بھاگ کر اس میں جا چھپیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,58,'9:58','اور ان میں ایسے بھی ہیں جو تم پر صدقات کے بارے میں عیب لگاتے ہیں اگر اس میں سے انھیں دے دیا جائے تو راضی رہتے ہیں اور اگر نہ دیا جائے تو ناراض ہوجاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,59,'9:59','کیا اچھا ہوتا کہ اللہ اور رسول نے جو کچھ انھیں دیا تھا اس پر وہ راضی رہتے اور کہتے کہ اللہ ہمارے ليے کافی ہے اللہ اپنے فضل سے ہم کو اور بھی دے گا اور اس کا رسول بھی، ہم کو تو اللہ ہی چاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(9,60,'9:60','صدقات (زکوٰۃ) تو دراصل فقیروں اور مسکینوں کے ليے ہیں اور ان کا رکنوں کے ليے جو صدقات کے کام پر مقرر ہیں اور ان کے ليے جن کی تالیفِ قلب مطلوب ہے نیز گردنوں کے چھڑانے میں اور جو تاوان بھریں اور اللہ کے راستہ میں اور مسافر کی امداد میں یہ ایک فریضہ ہے اللہ کی طرف سے اور اللہ علم والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,61,'9:61','اور ان میں وہ لوگ بھی ہیں جو نبی کو دکھ دیتے ہیں اور کہتے ہیں کہ یہ شخص تو کان ہے کہو کہ وہ تمھاری بھلائی کے ليے کان ہے وہ اللہ پر ایمان رکھتا ہے اور اہل ایمان پر اعتماد کرتا ہے اور وہ رحمت ہے ان کے ليے جو تم میں اہل ایمان ہیں اور جو لوگ اللہ کے رسول کو دکھ دیتے ہیں ان کے ليے درد ناک سزا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,62,'9:62','وہ تمھارے سامنے اللہ کی قسمیں کھاتے ہیں تاکہ تم کو راضی کریں حالاں کہ اللہ اور اس کا رسول زیادہ حق دار ہیں کہ وہ اس کو راضی کریں ، اگر وہ مومن ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,63,'9:63','کیا ان کو معلوم نہیں کہ جو اللہ اور اس کے رسول کی مخالفت کرے اس کے ليے جہنم کی آگ ہے جس میں وہ ہمیشہ رہے گا یہ بہت بڑی رسوائی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,64,'9:64','منافقین ڈرتے ہیں کہ کہیں مومنوں پر ایسی سورہ نازل نہ ہوجائے جو ان کو ان کے دلوں کے بھیدوں سے آگاہ کردے کہو کہ تم مذاق اڑالو، اللہ یقیناً اس کو ظاہر کردے گا جس سے تم ڈرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(9,65,'9:65','اور اگر تم ان سے پوچھو تو وہ کہیں گے کہ ہم تو ہنسی اور دل لگی کر رہے تھے کہو، کیا تم اللہ سے اور اس کی آیات سے اور اس کے رسول سے ہنسی ،دل لگی کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(9,66,'9:66','بہانے مت بناؤ، تم نے ایمان لانے کے بعد کفر کیا ہے اور ہم تم میں سے ایک گروہ کو معاف کردیں تو دوسرے گروہ کو تو ضرور سزا دیں گے کیوں کہ وہ مجرم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,67,'9:67','منافق مرد اور منافق عورتیں سب ایک ہی طرح کے ہیں وہ برائی کا حکم دیتے ہیں اور بھلائی سے منع کرتے ہیں اور اپنے ہاتھوں کو بند رکھتے ہیں انھوں نے اللہ کو بھلادیا تو اللہ نے بھی ان کو بھلادیا بے شک منافقین بہت نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,68,'9:68','منافق مردوں اور منافق عورتوں اور منکروں سے اللہ نے جہنم کی آگ کا وعدہ کر رکھا ہے جس میں وہ ہمیشہ رہیں گے یہی ان کے ليے بس ہے ان پر اللہ کی لعنت ہے اور ان کے ليے قائم رہنے والا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,69,'9:69','جس طرح تم سے اگلے لوگ، وہ تم سے زور میں زیادہ تھے اور مال و اولاد کی کثرت میں تم سے بڑھے ہوئے تھے تو انھوں نے اپنے حصہ سے فائدہ اٹھایا اور تم نے بھی اپنے حصہ سے فائدہ اٹھایا، جیسا کہ تمھارے اگلوں نے اپنے حصہ سے فائدہ اٹھایا تھااور تم نے بھی وہی بحثیں کیں جیسی بحثیں انھوں نے کی تھیں یہی وہ لوگ ہیں جن کے اعمال دنیا اور آخرت میں ضائع ہوگئے اور یہی لوگ گھاٹے میں پڑنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,70,'9:70','کیا انھیں ان لوگوں کی خبر نہیں پہنچی جو ان سے پہلے گزرے قومِ نوح اور عاد اور ثمود اور قومِ ابراہیم اور اصحاب مدین اور الٹی ہوئی بستیوں کی ان کے پاس ان کے رسول دلیلوں کے ساتھ آئے، تو ایسا نہ تھا کہ اللہ ان پر ظلم کرتا، مگر وہ خود اپنی جانوں پر ظلم کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,71,'9:71','اور مومن مرد اور مومن عورتیں ایک دوسرے کے مدد گار ہیں وہ بھلائی کا حکم دیتے ہیں اور برائی سے روکتے ہیں اور نماز قائم کرتے ہیں اور زکوٰۃ ادا کرتے ہیں اور اللہ اور اس کے رسول کی اطاعت کرتے ہیں یہی وہ لوگ ہیں جن پر اللہ رحم کرے گا بے شک اللہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,72,'9:72','مومن مردوں اور مومن عورتوں سے اللہ کا وعدہ ہے باغوں کا کہ ان کے نیچے نہریں جاری ہوں گی، ان میں وہ ہمیشہ رہیں گے اور وعدہ ہے، ستھرے مکانوں کا ہمیشگی کے باغوں میں اور اللہ کی رضامندی جو سب سے بڑھ کر ہے یہی بڑی کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,73,'9:73','اے نبی، منکروں اور منافقوں سے جہاد کرو اور ان پر کڑے بن جاؤ اور ان کا ٹھکانا جہنم ہے اور وہ بہت برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,74,'9:74','وہ خدا کی قسم کھاتے ہیں کہ انھوں نے نہیں کہا حالاں کہ انھوں نے کفر کی بات کہی اور وہ اسلام کے بعد منکرہوگئے اور انھوں نے وہ چاہا جو انھیں حاصل نہ ہوسکی اور یہ صرف اس کا بدلہ تھا کہ ان کو اللہ اوررسول نے اپنے فضل سے غنی کردیا اگر وہ توبہ کریں تو ان کے حق میں بہتر ہے اور اگر وہ اعراض کریں تو خدا ان کو درد ناک عذاب دے گا دنیا میں بھی اور آخرت میں بھی اور زمین میں ان کا نہ کوئی حمایتی ہوگا اور نہ مدد گار');
INSERT INTO "ur_wahid_uddin" VALUES(9,75,'9:75','اور ان میں وہ بھی ہیں جنھوں نے اللہ سے عہد کیا کہ اگر اس نے ہم کو اپنے فضل سے عطا کیا تو ہم ضرور صدقہ کریں گے اور ہم صالح بن کر رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(9,76,'9:76','پھر جب اللہ نے ان کو اپنے فضل سے عطا کیا تو وہ بخل کرنے لگے اور برگشتہ ہوکر انھوں نے منہ پھیرلیا');
INSERT INTO "ur_wahid_uddin" VALUES(9,77,'9:77','پس اللہ نے ان کے دلوں میں نفاق بٹھا دیا اس دن تک کے ليے جب کہ وہ اس سے ملیں گے، اس سبب سے کہ انھوں نے اللہ سے کيے ہوئے وعدہ کی خلاف ورزی کی اور اس سبب سے کہ وہ جھوٹ بولتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,78,'9:78','کیا انھیں خبر نہیں کہ اللہ ان کے راز اور ان کی سرگوشی کو جانتا ہے اور اللہ تمام چھپی ہوئی باتوں کو جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,79,'9:79','وہ لوگ جو طعن کرتے ہیں ان مومنوں پر جو دل کھول کر صدقات دیتے ہیں اور جو صرف اپنی محنت مزدوری سے انفاق کرتے ہیں ، وہ ان کا مذاق اڑاتے ہیں اللہ ان مذاق اڑانے والوں کا مذاق اڑاتا ہے اور ان کے ليے درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,80,'9:80','تم ان کے ليے معافی کی درخواست کرو یا نہ کرو، اگر تم ستر مرتبہ انھیں معاف کرنے کی درخواست کرو گے تو اللہ ان کو معاف کرنے والا نہیں یہ اس ليے کہ انھوں نے اللہ اور رسول کا انکار کیا اور اللہ نافرمانوں کو راہ نہیں دکھاتا');
INSERT INTO "ur_wahid_uddin" VALUES(9,81,'9:81','پیچھے رہ جانے والے اللہ کے رسول سے پیچھے بیٹھ رہنے پر بہت خوش ہوئے اور ان کو گراں گزرا کہ وہ اپنے مال اور جان سے اللہ کی راہ میں جہاد کریں اور انھوں نے کہا کہ گرمی میں نہ نکلو کہہ دو کہ دوزخ کی آگ اس سے زیادہ گرم ہے، کاش انھیں سمجھ ہوتی');
INSERT INTO "ur_wahid_uddin" VALUES(9,82,'9:82','پس وہ ہنسیں کم اور روئیں زیادہ، اس کے بدلے میں جو وہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(9,83,'9:83','پس اگر اللہ تم کو ان میں سے کسی گروہ کی طرف واپس لائے اور وہ تم سے جہاد کے ليے نکلنے کی اجازت مانگیں تو کہہ دینا کہ تم میرے ساتھ کبھی نہیں چلو گے اور نہ میرے ساتھ ہوکر کسی دشمن سے لڑو گے تم نے پہلی بار بھی بیٹھ رہنے کو پسند کیا تھا، پس پیچھے رہنے والوں کے ساتھ بیٹھے رہو');
INSERT INTO "ur_wahid_uddin" VALUES(9,84,'9:84','اور ان میں سے جو کوئی مرجائے، اس پر تم کبھی نماز نہ پڑھو اور نہ اس کی قبر پر کھڑے ہو بے شک انھوں نے اللہ اور اس کے رسول کا انکار کیا اور وہ اس حال میں مرے کہ وہ نافرمان تھے');
INSERT INTO "ur_wahid_uddin" VALUES(9,85,'9:85','اور ان کے مال اور ان کی اولاد تم کو تعجب میں نہ ڈالیں اللہ تو بس یہ چاہتا ہے کہ ان کے ذریعے سے ان کو دنیا میں عذاب دے اور ان کی جانیں اس حال میں نکلیں کہ وہ منکر ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(9,86,'9:86','اور جب کوئی سورہ اترتی ہے کہ اللہ پر ایمان لاؤ اور اس کے رسول کے ساتھ جہاد کرو تو ان کے مقدور والے تم سے رخصت مانگنے لگتے ہیں اور کہتے ہیں کہ ہم کو چھوڑ دیجيے کہ ہم یہاں ٹھہرنے والوں کے ساتھ رہ جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,87,'9:87','انھوں نے اس کو پسند کیا کہ پیچھے رہنے والی عورتوں کے ساتھ رہ جائیں اور ان کے دلو ں پر مہر کردی گئی پس وہ کچھ نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(9,88,'9:88','لیکن رسول اور جو لوگ اس کے ساتھ ایمان لائے ہیں ، انھوں نے اپنے مال اور جان سے جہاد کیا اور انھیں کے ليے ہیں خوبیاں اور وہی فلاح پانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,89,'9:89','ان کے ليے اللہ نے ایسے باغ تیار کر رکھے ہیں جن کے نیچے نہریں بہتی ہیں ان میں وہ ہمیشہ رہیں گے یہی بڑی کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,90,'9:90','بدوی عربوں میں سے بھی بہانہ کرنے والے آئے کہ انھیں اجازت مل جائے اور جو اللہ اور اس کے رسول سے جھوٹ بولے، وہ بیٹھ رہے ان میں سے جنھوں نے انکار کیا ان کو ایک درد ناک عذاب پکڑے گا');
INSERT INTO "ur_wahid_uddin" VALUES(9,91,'9:91','کوئی گناہ کمزوروں پر نہیں ہے اور نہ بیماروں پر اور نہ ان پر جو خرچ کرنے کو کچھ نہیں پاتے جب کہ وہ اللہ اور اس کے رسول کے ساتھ خیر خواہی کریں نیک کاروں پر کوئی الزام نہیں اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,92,'9:92','اور نہ ان لوگوں پر کوئی الزام ہے کہ جب وہ تمھارے پاس آئے کہ تم ان کو سواری دو تم نے کہا کہ میرے پاس کوئی چیز نہیں کہ تم کو اس پر سوار کردوں تو وہ اس حال میں واپس ہوئے کہ ان کی آنکھوں سے آنسو جاری تھے اس غم میں کہ انھیں کچھ میسر نہیں جو وہ خرچ کریں');
INSERT INTO "ur_wahid_uddin" VALUES(9,93,'9:93','الزام تو بس ان لوگوں پر ہے جو تم سے اجازت مانگتے ہیں حالاں کہ وہ مال دار ہیں وہ اس پر راضی ہوگئے کہ پیچھے رہنے والی عورتوں کے ساتھ رہ جائیں اور اللہ نے ان کے دلو ں پر مہر کردی، پس وہ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(9,94,'9:94','تم جب ان کی طرف پلٹو گے تو وہ تمھارے سامنے عذرات پیش کریں گے کہہ دو کہ بہانے نہ بناؤ ہم ہر گز تمھاری بات نہ مانیں گے بے شک اللہ نے ہم کو تمھارے حالات بتادئے ہیں اب اللہ اور رسول تمھارے عمل کو دیکھیں گے پھر تم اس کی طرف لوٹائے جاؤ گے جو کھلے اور چھپے کا جاننے والا ہے، وہ تم کو بتادے گا جو کچھ تم کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(9,95,'9:95','یہ لوگ تمھاری واپسی پر تمھارے سامنے اللہ کی قسمیں کھائیں گے تاکہ تم ان سے درگزر کرو پس تم ان سے درگزر کرو بے شک وہ ناپاک ہیں اور ان کا ٹھکانا جہنم ہے بدلہ میں اس کے جو وہ کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,96,'9:96','وہ تمھارے سامنے قسمیں کھائیں گے کہ تم ان سے راضی ہوجاؤ اگر تم ان سے راضی بھی ہوجاؤ تو اللہ نافرمان لوگوں سے راضی ہونے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,97,'9:97','دیہات والے کفر و نفاق میں زیادہ سخت ہیں اور وہ اسی لائق ہیں کہ اللہ نے اپنے رسول پر جو کچھ اتارا ہے اس کے حدود سے بے خبر رہیں اور اللہ سب کچھ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,98,'9:98','اور دیہاتیوں میں ایسے بھی ہیں جو خدا کی راہ میں خرچ کو ایک تاوان سمجھتے ہیں اور تمھارے ليے زمانہ کی گردشوں کے منتظر ہیں بری گردش خود انھیں پر ہے اور اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,99,'9:99','اور دیہاتیوں میں وہ بھی ہیں جو اللہ پر اور آخرت کے دن پر ایمان رکھتے ہیں اور جو کچھ خرچ کرتے ہیں ، وہ اس کو اللہ کے یہاں قرب کا اور رسول کی دعائیں لینے کا ذریعہ بناتے ہیں ہا ں بے شک وہ ان کے ليے قرب کا ذریعہ ہے اللہ ان کو اپنی رحمت میں داخل کرے گا یقیناً اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,100,'9:100','اور مہاجرین و انصار میں جو لوگ سابق اور مقدم ہیں اور جنھوں نے خوبی کے ساتھ ان کی پیروی کی، اللہ ان سے راضی ہوا اور وہ اس سے راضی ہوئے اور اللہ نے ان کے ليے ایسے باغ تیار کر رکھے ہیں جن کے نیچے نہریں بہتی ہوں گی وہ ان میں ہمیشہ رہیں گے یہی ہے بڑی کامیابی');
INSERT INTO "ur_wahid_uddin" VALUES(9,101,'9:101','اور تمھارے گرد و پیش جو دیہاتی ہیں ان میں منافق ہیں اور مدینہ والوں میں بھی منافق ہیں وہ نفاق پر جم گئے ہیں تم ان کو نہیں جانتے، ہم ان کو جانتے ہیں ہم ان کو دہرا عذاب دیں گے پھر وہ ایک عذاب عظیم کی طرف بھیجے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(9,102,'9:102','کچھ اور لوگ ہیں جنھوں نے اپنے قصوروں کا اعتراف کرلیا ہے انھوں نے ملے جلے عمل کيے تھے، کچھ بھلے اور کچھ برے امید ہے کہ اللہ ان پر توجہ کرےبے شک اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,103,'9:103','تم ان کے مالوں میں سے صدقہ لو، اس سے تم ان کو پاک کرو گے اور ان کا تزکیہ کرو گے اور تم ان کے ليے دعا کرو بے شک تمھاری دعا ان کے ليے وجہِ تسکین ہوگی اللہ سب کچھ سننے والا، جاننے والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,104,'9:104','کیا وہ نہیں جانتے کہ اللہ ہی اپنے بندوں کی تو بہ قبول کرتا ہے اور وہی صدقات کو قبول کرتا ہے اور اللہ توبہ قبول کرنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,105,'9:105','کہو کہ عمل کرو، اللہ اور اس کا رسول اور اہلِ ایمان تمھارے عمل کو دیکھیں گے اور تم جلد اس کے پاس لوٹائے جاؤ گے جو تمام کھلے اور چھپے کو جانتا ہے وہ تم کو بتادے گا جو کچھ تم کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(9,106,'9:106','کچھ دوسرے لوگ ہیں جن کا معاملہ ابھی خداکا حکم آنے تک ٹھہرا ہوا ہے، یا وہ ان کو سزا دے گا یا ان کی توبہ قبول کرے گا، اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,107,'9:107','اور ان میں ایسے بھی ہیں جنھوں نے ایک مسجد بنائی نقصان پہنچانے کے ليے اور کفر کے ليے اور اہل ایمان میں پھوٹ ڈالنے کے ليے اور اس ليے تاکہ کمین گاہ فراہم کریں اس شخص کے ليے جو پہلے سے اللہ اور اس کے رسول سے لڑ رہا ہے اور یہ لوگ قسمیں کھائیں گے کہ ہم نے تو صرف بھلائی چاہی تھی اور اللہ گواہ ہے کہ وہ جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,108,'9:108','تم اس عمارت میں کبھی کھڑے نہ ہونا البتہ جس مسجد کی بنیاد اول دن سے تقوی پر پڑی ہے، وہ اس لائق ہے کہ تم اس میں کھڑے ہو اس میں ایسے لوگ ہیں جو پاک رہنے کو پسند کرتے ہیں اور اللہ پاک رہنے والوں کو پسند کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,109,'9:109','کیا وہ شخص بہتر ہے جس نے اپنی عمارت کی بنیاد خدا سے ڈر پر اور خدا کی خوشنودی پر رکھی، یا وہ شخص بہتر ہے جس نے اپنی عمارت کی بنیاد ایک کھائی کے کنارے پر رکھی جو گرنے کو ہے پھر وہ عمارت اس کو لے کر جہنم کی آگ میں گر پڑی اور اللہ ظالم لوگوں کو راہ نہیں دکھاتا');
INSERT INTO "ur_wahid_uddin" VALUES(9,110,'9:110','اور یہ عمارت جو انھوں نے بنائی ہمیشہ ان کے دلوں میں شک کی بنیاد بنی رہے گی بجز اس کے کہ ان کے دل ہی ٹکڑے ہوجائیں اور اللہ علیم ، حکیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,111,'9:111','بلاشبہ اللہ نے مومنوں سے ان کی جان اور ان کے مال کو خرید لیا ہے جنت کے بدلے وہ اللہ کی راہ میں لڑتے ہیں پھر مارتے ہیں اور مارے جاتے ہیں یہ اللہ کے ذمہ ایک سچا وعدہ ہے، تورات میں اور انجیل میں اور قرآن میں اور اللہ سے بڑھ کر اپنے عہد کو پورا کرنے والا کون ہے پس تم خوشیاں کرو اس معاملہ پر جو تم نے اللہ سے کیا ہے اور یہی ہے سب سے بڑی کامیابی');
INSERT INTO "ur_wahid_uddin" VALUES(9,112,'9:112','وہ توبہ کرنے والے ہیں عبادت کرنے والے ہیں حمد کرنے والے ہیں خدا کی راہ میں پھرنے والے ہیں رکوع کرنے والے ہیں سجدہ کرنے والے ہیں بھلائی کا حکم کرنے والے ہیں برائی سے روکنے والے ہیں اللہ کی حدوں کا خیال رکھنے والے ہیں اور مومنوں کو خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(9,113,'9:113','نبی کو اور ان لوگوں کو جو ایمان لائے ہیں روا نہیں کہ وہ مشرکوں کے ليے مغفرت کی دعا کریں ، چاہے وہ ان کے رشتہ دار ہی ہوں جب کہ ان پر کھل چکا کہ یہ جہنم میں جانے والے لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,114,'9:114','اور ابراہیم کا اپنے باپ کے ليے مغفرت کی دعا مانگنا صرف اس وعدہ کے سبب سے تھا جو اس نے اس سے کرلیاتھا پھر جب اس پر کھل گیا کہ وہ اللہ کا دشمن ہے تو وہ اس سے بے تعلق ہوگیا بے شک ابراہیم بڑا نرم دل اور بردبار تھا');
INSERT INTO "ur_wahid_uddin" VALUES(9,115,'9:115','اور اللہ کسی قوم کو ہدایت دینے کے بعد گمراہ نہیں کرتا جب تک ان کو صاف صاف وہ چیزیں بتا نہ دے جن سے انھیں بچنا ہے، بے شک اللہ ہر چیز کا علم رکھتاہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,116,'9:116','اللہ ہی کی سلطنت ہے آسمانوں میں اور زمین میں ، وہ جلاتا ہے ا ور وہی مارتا ہے اور اللہ کے سوا نہ تمھارا کوئی دوست ہے اور نہ مدد گار');
INSERT INTO "ur_wahid_uddin" VALUES(9,117,'9:117','اللہ نے نبی پر اور مہاجرین و انصار پر توجہ فرمائی جنھوں نے تنگی کے وقت میں نبی کا ساتھ دیا، بعد اس کے کہ ان میں سے کچھ لوگوں کے دل کجی کی طرف مائل ہوچکے تھے پھر اللہ نے ان پر توجہ فرمائی بے شک اللہ ان پر مہربان ہے، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,118,'9:118','اور ان تینوں پر بھی اس نے توجہ فرمائی جن کا معاملہ اٹھا رکھا گیا تھا یہاں تک کہ جب زمین اپنی وسعت کے باوجود ان پر تنگ ہوگئی اور وہ خود اپنی جانوں سے تنگ آگئے اور انھوں نے سمجھ لیا کہ اللہ سے بچنے کے ليے خود اللہ کے سوا کوئی جائے پناہ نہیں پھر اللہ ان کی طرف پلٹا تاکہ وہ اس کی طرف پلٹ آئیں بے شک اللہ توبہ قبول کرنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,119,'9:119','اے ایمان والو، اللہ سے ڈرو اور سچے لوگوں کے ساتھ رہو');
INSERT INTO "ur_wahid_uddin" VALUES(9,120,'9:120','مدینہ والوں اور اطراف کے بدویوں کے ليے زیبا نہ تھا کہ وہ اللہ کے رسول کو چھوڑ کر پیچھے بیٹھ رہیں اور نہ یہ کہ اپنی جان کو اس کی جان سے عزیز رکھیں یہ اس ليے کہ جو پیاس اور تھکان اور بھوک بھی ان کو خدا کی راہ میں لاحق ہوتی ہے اور جو قدم بھی وہ منکروں کو رنج پہنچانے والا اٹھاتے ہیں اور جو چیز بھی وہ دشمن سے چھینتے ہیں ، ان کے بدلے میں ان کے ليے ایک نیکی لکھ دی جاتی ہے اللہ نیکی کرنے والوں کا اجر ضائع نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(9,121,'9:121','اور جو چھوٹا یا بڑا خرچ انھوں نے کیا اور جو میدان انھوں نے طے کيے، وہ سب ان کے ليے لکھا گیا تاکہ اللہ ان کے عمل کا اچھے سے اچھا بدلہ دے');
INSERT INTO "ur_wahid_uddin" VALUES(9,122,'9:122','اور یہ ممکن نہ تھا کہ اہلِ ایمان سب کے سب نکل کھڑے ہوں تو ایسا کیوں نہ ہوا کہ ان کے ہر گروہ میں سے ایک حصہ نکل کر آتا، تاکہ وہ دین میں گہری سمجھ پیدا کرتا اور واپس جاکر اپنی قوم کے لوگوں کو آگاہ کرتا تاکہ وہ بھی پرہیز کرنے والے بنتے');
INSERT INTO "ur_wahid_uddin" VALUES(9,123,'9:123','اے ایمان والو، ان منکروں سے جنگ کرو جو تمھارے آس پاس ہیں اور چاہيے کہ وہ تمھارے اندر سختی پائیں اور جان لو کہ اللہ ڈرنے والوں کے ساتھ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,124,'9:124','اور جب کوئی سورہ اترتی ہے تو ان میں سے بعض کہتے ہیں کہ اس نے تم میں سے کس کا ایمان زیادہ کردیا پس جو ایمان والے ہیں ، ان کا اس نے ایمان زیادہ کردیا اور وہ خوش ہو رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,125,'9:125','اور جن لوگوں کے دلوں میں روگ ہے تو اس نے بڑھادی ان کی گندگی پر گندگی اور وہ مرنے تک منکرہی رہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,126,'9:126','کیا یہ لوگ دیکھتے نہیں کہ وہ ہر سال ایک بار یا دو بار آزمائش میں ڈالے جاتے ہیں ، پھر بھی نہ تو بہ کرتے ہیں اور نہ سبق حاصل کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,127,'9:127','اور جب کوئی سورہ اتاری جاتی ہے تو یہ لوگ ایک دوسرے کو دیکھتے ہیں کہ کوئی دیکھتا تو نہیں ، پھر چل دیتے ہیں اللہ نے ان کے دلوں کو پھیردیا اس وجہ سے کہ یہ سمجھ سے کام لینے والے لوگ نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(9,128,'9:128','تمھارے پاس ایک رسول آیا ہے جو خود تم میں سے ہے تمھارا نقصان میں پڑنا اس پرشاق ہے وہ تمھاری بھلائی کا حریص ہے ایمان والوں پر نہایت شفیق اور مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(9,129,'9:129','پھر بھی اگر وہ منہ پھیریں تو کہہ دو کہ اللہ میرے ليے کافی ہے اس کے سوا کوئی معبود نہیں اسی پر میں نے بھروسہ کیا اور وہی مالک ہے عرش عظیم کا');
INSERT INTO "ur_wahid_uddin" VALUES(10,1,'10:1','الر، یہ پُر حکمت کتاب کی آیتیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,2,'10:2','کیا لوگوں کو اس پر حیرت ہے کہ ہم نے انھیں میں سے ایک شخص پر وحی کی کہ لوگوں کو ڈراؤ اور جو ایمان لائیں ان کو خوش خبری سنا دو کہ ان کے ليے ان کے رب کے پاس سچا مرتبہ ہے منکروں نے کہا کہ یہ شخص تو کھلا جادو گر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,3,'10:3','بے شک تمھارا رب اللہ ہے جس نے آسمانوں اور زمین کو چھ دنوں (ادوار) میں پیدا کیا، پھر وہ عرش پر قائم ہوا وہی معاملات کا انتظام کرتا ہے اس کی اجازت کے بغیر کوئی سفارش کرنے والا نہیں یہی اللہ تمھارا رب ہے پس تم اسی کی عبادت کرو، کیا تم سوچتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,4,'10:4','اسی کی طرف تم سب کو لوٹ کر جانا ہے، یہ اللہ کا پکا وعدہ ہے بے شک وہ پیدائش کی ابتدا کرتا ہے، پھر وہ دوبارہ پیدا کرے گا تاکہ جو لوگ ایمان لائے اور انھوں نے نیک کام کيے ان کو انصاف کے ساتھ بدلہ دے اور جنھوں نے انکار کیا ان کے انکار کے بدلے ان کے ليے کھولتا ہوا پانی اور درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,5,'10:5','اللہ ہی ہے جس نے سورج کو چمکتا بنایا اور چاند کو روشنی دی اور اس کی منزلیں مقرر کردیں تاکہ تم برسوں کا شمار اور حساب معلوم کرو اللہ نے یہ سب کچھ بے مقصد نہیں بنایا ہے وہ نشانیاں کھول کر بیان کرتا ہے ان کے ليے جو سمجھ رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,6,'10:6','یقیناً رات اور دن کے الٹ پھیر میں اور اللہ نے جو کچھ آسمانوں اور زمین میں پیدا کیاہے، ان میں ان لوگوں کے ليے نشانیاں ہیں جو ڈرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,7,'10:7','بے شک جو لوگ ہماری ملاقات کی امید نہیں رکھتے اور دنیا کی زندگی پر راضی اور مطمئن ہیں اور جو ہماری نشانیوں سے بے پروا ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,8,'10:8','ان کا ٹھکانا جہنم ہوگا بہ سبب اس کے جو وہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(10,9,'10:9','بے شک جو لوگ ایمان لائے اور نیک کام کيے، اللہ ان کے ایمان کی بدولت ان کو (جنت ميں ) پہنچادے گا، ان کے نیچے نہریں بہتی ہوں گی، نعمت کے باغوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(10,10,'10:10','اس میں ان کا قول ہوگا کہ اے اللہ تو پاک ہے اور ملاقات ان کی سلام سے ہوگی اور ان کی آخری بات یہ ہوگی کہ ساری تعریف اللہ کے ليے ہے جو رب ہے سارے جہان کا');
INSERT INTO "ur_wahid_uddin" VALUES(10,11,'10:11','اگر اللہ لوگوں کے ليے عذاب اسی طرح جلد پہنچادے جس طرح وہ ان کے ساتھ رحمت میں جلدی کرتا ہے تو ان کی مدت ختم کردی گئی ہوتی لیکن ہم ان لوگوں کو جو ہماری ملاقات کی امید نہیں رکھتے ان کی سرکشی میں بھٹکنے کے ليے چھوڑ دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,12,'10:12','اور انسان کو جب کوئی تکلیف پہنچتی ہے تو وہ کھڑے اور بیٹھے اور لیٹے ہم کو پکارتا ہے پھر جب ہم اس سے اس کی تکلیف کو دور کردیتے ہیں تو وہ ایسا ہوجاتا ہے گویا اس نے کبھی اپنے کسی برے وقت پر ہم کو پکارا ہی نہ تھا اس طرح حد سے گزرجانے والوں کے ليے ان کے اعمال خوش نما بنادئے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,13,'10:13','اور ہم نے تم سے پہلے قوموں کو ہلاک کیا جب کہ انھوں نے ظلم کیااور ان کے پیغمبران کے پاس کھلی ہوئی دلیلوں کے ساتھ آئے اور وہ ایمان لانے والے نہ بنے ہم ایسا ہی بدلہ دیتے ہیں مجرم لوگوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(10,14,'10:14','پھر ہم نے ان کے بعد تم کو ملک میں جانشین بنایا تاکہ ہم دیکھیں کہ تم کیسا عمل کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(10,15,'10:15','اور جب ان کو ہماری کھلی ہوئی آیتیں پڑھ کر سنائی جاتی ہیں تو جن لوگوں کو ہمارے پاس آنے کاکھٹکا نہیں ہے، وہ کہتے ہیں کہ اس کے سوا کوئی اور قرآن لاؤ یا اس کو بدل دو کہو کہ میرا یہ کام نہیں کہ میں اپنے جی سے اس کو بدل دوں میں تو صرف اس وحی کی پیروی کرتا ہوں جو میرے پاس آتی ہے اگر میں اپنے رب کی نافرمانی کروں تو میں ایک بڑے دن کے عذاب سے ڈرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(10,16,'10:16','کہو کہ اللہ اگر چاہتا تو میں اس کوتمھیں نہ سناتا اور نہ اللہ اس سے تمھیں باخبر کرتا میں اس سے پہلے تمھارے درمیان ایک عمر گزارچکا ہوں ، پھر کیا تم عقل سے کام نہیں لیتے');
INSERT INTO "ur_wahid_uddin" VALUES(10,17,'10:17','اس سے بڑھ کر ظالم اور کون ہوگا جو اللہ پر جھوٹ بہتان باندھے یا اس کی نشانیوں کو جھٹلائے یقینا مجرموں کو فلاح حاصل نہیں ہوتی');
INSERT INTO "ur_wahid_uddin" VALUES(10,18,'10:18','اور وہ اللہ کے سوا ایسی چیزوں کی عبادت کرتے ہیں جو ان کو نہ نقصان پہنچاسکیں اور نہ نفع پہنچاسکیں اور وہ کہتے ہیں کہ یہ اللہ کے یہاں ہمارے سفارشی ہیں کہو، کیا تم اللہ کو ایسی چیز کی خبر دیتے ہو جو اس کو آسمانوں اور زمین میں معلوم نہیں وہ پاک اور برتر ہے اس سے جس کو وہ شریک کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,19,'10:19','اور لوگ ایک ہی امت تھےپھر انھوں نے اختلاف کیا اور اگر تمھارے رب کی طرف سے ایک بات پہلے سے نہ ٹھہر چکی ہوتی تو ان کے درمیان اس امر کا فیصلہ کردیا جاتا جس میں وہ اختلاف کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,20,'10:20','اور وہ کہتے ہیں کہ نبی پر اس کے رب کی طرف سے کوئی نشانی کیوں نہیں اتاری گئی، کہو کہ غیب کی خبر تو اللہ ہی کو ہے تم لوگ انتظار کرو، میں بھی تمھارے ساتھ انتظار کرنے والوں میں سے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(10,21,'10:21','اور جب کوئی تکلیف پڑنے کے بعد ہم لوگوں کو اپنی رحمت کا مزا چکھاتے ہیں تو وہ فوراً ہماری نشانیوں کے معاملہ میں حیلے بنانے لگتے ہیں کہو کہ خدا اپنے حیلوں میں ان سے بھی زیادہ تیز ہے یقیناً ہمارے فرشتے تمھاری حیلہ بازیوں کو لکھ رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,22,'10:22','وہ اللہ ہی ہے جو تم کو خشکی اور تری میں چلاتا ہے چنانچہ جب تم کشتی میں ہوتے ہو اور کشتیاں لوگوں کو لے کر موافق ہوا سے چل رہی ہوتی ہیں اور لوگ اس سے خوش ہوتے ہیں کہ یکایک تند ہوا آتی ہے اور ان پر ہر جانب سے موجیں اٹھنے لگتی ہیں اور وہ گمان کرلیتے ہیں کہ ہم گِھر گئے اس وقت وہ اپنے دین کو اللہ ہی کے ليے خالص کرکے اس کو پکارنے لگتے ہیں کہ اگر تو نے ہمیں اس سے نجات دے دی تو یقیناً ہم شکر گزار بندے بنیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,23,'10:23','پھر جب وہ ان کو نجات دے دیتا ہے تو فوراً ہی وہ زمین میں ناحق کی سرکشی کرنے لگتے ہیں اے لوگو، تمھاری سرکشی تمھارے اپنے ہی خلاف ہے، دنیا کی زندگی کا نفع اٹھالو، پھر تم کو ہماری طرف لوٹ کر آنا ہے، پھر ہم بتادیں گے جو کچھ تم کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(10,24,'10:24','دنیا کی زندگی کی مثال ایسی ہے جیسے پانی، ہم نے اس کو آسمان سے برسایا تو زمین کا سبزہ خوب نکلا جس کو آدمی کھاتے ہیں اور جس کو جانور کھاتے ہیں یہاں تک کہ جب زمین پوری رونق پر آگئی اور سنور اٹھی اور زمین والوں نے گمان کرلیا کہ اب یہ ہمارے قابو میں ہے تو اچانک اس پر ہمارا حکم رات کو یا دن کو آگیا، پھر ہم نے اس کو کاٹ کر ڈھیر کردیا گویا کل یہاں کچھ تھا ہی نہیں اس طرح ہم نشانیاں کھول کر بیان کرتے ہیں ان لوگوں کے ليے جو غور کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,25,'10:25','اور اللہ سلامتی کے گھر کی طرف بلاتا ہے اور وہ جس کو چاہتا ہے سیدھا راستہ دکھادیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,26,'10:26','جن لوگوں نے بھلائی کی، ان کے ليے بھلائی ہے اور اس پر مزید بھی اور ان کے چہروں پر نہ سیاہی چھائے گی اور نہ ذلت یہی جنت والے لوگ ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,27,'10:27','اور جنھوں نے برائیاں کمائیں تو برائی کا بدلہ اس کے برابر ہے اور ان پر رسوائی چھائی ہوئی ہوگی کوئی ان کو اللہ سے بچانے والا نہ ہوگا گویا کہ ان کے چہرے اندھیری رات کے ٹکڑوں سے ڈھانک دئے گئے ہیں یہی لوگ دوزخ والے ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,28,'10:28','اور جس دن ہم ان سب کو جمع کریں گے، پھر ہم شرک کرنے والوں سے کہیں گے کہ ٹھہرو تم بھی اور تمھارے بنائے ہوئے شریک بھی پھر ہم ان کے درمیان تفریق کردیں گے اور ان کے شریک کہیں گے کہ تم ہماری عبادت تو نہیں کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(10,29,'10:29','اللہ ہمارے درمیان گواہی کے ليے کافی ہے ہم تمھاری عبادت سے بالکل بے خبر تھے');
INSERT INTO "ur_wahid_uddin" VALUES(10,30,'10:30','اس وقت ہر شخص اپنے اس عمل سے دو چار ہوگا جو اس نے کیا تھا اور لوگ اللہ اپنے مالک حقیقی کی طرف لوٹائے جائیں گے اور جو جھوٹ انھوں نے گھڑے تھے وہ سب ان سے جاتے رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,31,'10:31','کہو کہ کون تم کو آسمان اور زمین سے روزی دیتا ہے، یا کون ہے جو کان پر اور آنکھوں پر اختیار رکھتا ہے اور کون بے جان میں سے جاندار کو اور جاندار میں سے بے جان کو نکالتا ہے اور کون معاملات کا انتظا م کر رہا ہے وہ کہیں گے کہ اللہ کہو کہ پھر کیا تم ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,32,'10:32','پس وہی اللہ تمھارا پروردگارِ حقیقی ہے سچائي کے بعد بھٹکنے کے سوا اور کیا ہے، تم کدھر پھرے جاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(10,33,'10:33','اسی طرح تیرے رب کی بات سرکشی کرنے والوں کے حق میں پوری ہوچکی ہے کہ وہ ایمان نہ لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,34,'10:34','کہو، کیا تمھارے ٹھہرائے ہوئے شریکوں میں کوئی ہے جو پہلی بار پیدا کرتا ہو، پھر وہ دوبارہ بھی پیدا کرے کہو، اللہ ہی پہلی بار بھی پیدا کرتا ہے پھر وہی دوبارہ بھی پیدا کرے گا پھر تم کہاں بھٹکے جاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(10,35,'10:35','کہو، کیا تمھارے شرکاء میں کوئی ہے جو حق کی طرف رہنمائی کرتا ہو، کہہ دو کہ اللہ ہی حق کی طرف رہنمائی کرتا ہے پھر جو حق کی طرف رہنمائی کرتا ہے، وہ پیروی کيے جانے کا مستحق ہے یا وہ جس کو خود ہی راستہ نہ ملتا ہو بلکہ اسے راستہ بتایا جائے تم کو کیا ہوگیا ہے، تم کیسا فیصلہ کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(10,36,'10:36','ان میں سے اکثر صرف گمان کی پیروی کر رہے ہیں اور گمان حق بات میں کچھ بھی کام نہیں دیتا اللہ کو خوب معلوم ہے جو کچھ وہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,37,'10:37','اور یہ قرآن ایسا نہیں کہ اللہ کے سوا کوئی اس کو بنالے بلکہ یہ تصدیق ہے ان پیشین گوئیوں کی جو اس کے پہلے سے موجو دہیں اور کتاب کی تفصیل ہے، اس میں کوئی شک نہیں کہ وہ خداوند عالم کی طرف سے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,38,'10:38','کیا لوگ کہتے ہیں کہ اس شخص نے اس کو گڑھ لیا ہے کہو کہ تم اس کی مانند کوئی سورہ لے آؤ اور اللہ کے سوا تم جن کو بلاسکو بلالو، اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(10,39,'10:39','بلکہ یہ لوگ اس چیز کو جھٹلا رہے ہیں جو ان کے علم کے احاطے میں نہیں آئی اور جس کی حقیقت ابھی ان پر نہیں کھلی اسی طرح ان لوگوں نے بھی جھٹلایا جو ان سے پہلے گزرے ہیں ، پس دیکھو کہ ظالموں کا انجام کیا ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(10,40,'10:40','اور ان میں سے وہ بھی ہیں جو قرآن پر ایمان لے آئیں گے اور وہ بھی ہیں جو اس پر ایمان نہیں لائیں گے اور تیرا رب مفسدوں کو خوب جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,41,'10:41','اور اگر وہ تم کو جھٹلاتے ہیں تو کہہ دو کہ میرا عمل میرے ليے ہے اور تمھارا عمل تمھارے ليے تم اس سے بری ہو جو میں کرتا ہوں اور میں اس سے بری ہوں جو تم کر رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(10,42,'10:42','اور ان میں بعض ایسے بھی ہیں جو تمھاری طرف کان لگاتے ہیں ، تو کیا تم بہروں کو سناؤ گے جب کہ وہ سمجھ سے کام نہ لے رہے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(10,43,'10:43','اور ان میں سے کچھ ایسے ہیں جو تمھاری طرف دیکھتے ہیں ، تو کیا تم اندھوں کو راستہ دکھاؤ گے اگرچہ وہ دیکھ نہ رہے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(10,44,'10:44','اللہ لوگوں پر کچھ بھی ظلم نہیں کرتا، مگر لوگ خود ہی اپنی جانو ں پر ظلم کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,45,'10:45','اور جس دن اللہ ان کو جمع کرے گا، گویا کہ وہ بس دن کی ایک گھڑی دنیا میں تھے وہ ایک دوسرے کو پہچانیں گے بے شک سخت گھاٹے میں رہے وہ لوگ جنھوں نے اللہ سے ملنے کو جھٹلایا اور وہ راہِ راست پر نہ آئے');
INSERT INTO "ur_wahid_uddin" VALUES(10,46,'10:46','ہم تم کو اس کا کوئی حصہ دکھادیں جس کا ہم ان سے وعدہ کر رہے ہیں یا تمھیں وفات دے دیں ، بہرحال ان کو ہماری ہی طرف لوٹنا ہے، پھر اللہ گواہ ہے اس پر جو کچھ وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,47,'10:47','اور ہر امت کے ليے ایک رسول ہے پھر جب ان کا رسول آجاتا ہے تو ان کے درمیان انصاف کے ساتھ فیصلہ کردیا جاتا ہے اور ان پر کوئی ظلم نہیں ہوتا');
INSERT INTO "ur_wahid_uddin" VALUES(10,48,'10:48','اور وہ کہتے ہیں کہ یہ وعدہ کب پورا ہوگا اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(10,49,'10:49','کہو میں اپنے واسطے بھی برے اور بھلے کا مالک نہیں ، مگر جو اللہ چاہے ہر امت کے ليے ایک وقت ہے جب ان کا وقت آجاتا ہے تو پھر نہ وہ ایک گھڑی پیچھے ہوتے اور نہ آگے');
INSERT INTO "ur_wahid_uddin" VALUES(10,50,'10:50','کہو کہ بتاؤ، اگر اللہ کا عذاب تم پر رات کو آپڑے یا دن کو آجائے تو مجرم لوگ اس سے پہلے کیا کرلیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,51,'10:51','پھر کیا جب عذاب واقع ہوچکے گا تب اس پر یقین کرو گے اب قائل ہوئے اور تم اسی کا تقاضا کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(10,52,'10:52','پھر ظالموں سے کہا جائے گاکہ اب ہمیشہ کا عذاب چکھو یہ اسی کا بدلہ مل رہا ہے جو کچھ تم کماتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(10,53,'10:53','اور وہ تم سے پوچھتے ہیں کہ کیا یہ بات سچ ہے کہو کہ ہاں میرے رب کی قسم، یہ سچ ہے اور تم اس کو تھکا نہ سکو گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,54,'10:54','اور اگر ہر ظالم کے پاس وہ سب کچھ ہو جو زمین میں ہے تو وہ اس کوفدیہ میں دے دینا چاہے گا اور جب وہ عذاب کو دیکھیں گے تو اپنے دل میں پچھتائیں گے اور ان کے درمیان انصاف سے فیصلہ کردیا جائے گا اور ان پر ظلم نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(10,55,'10:55','یا درکھو، جو کچھ آسمانوں اور زمین میں ہے سب اللہ کا ہے، یاد رکھو، اللہ کا وعدہ سچا ہے مگر اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(10,56,'10:56','وہی زندہ کرتا ہے اور وہی مارتا ہے اور اسی کی طرف تم لوٹائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,57,'10:57','اے لوگو، تمھارے پاس تمھارے رب کی جانب سے نصیحت آگئی اور اس کے ليے شفاء جو سینوں میں ہوتی ہے اور اہل ایمان کے ليے ہدایت اور رحمت');
INSERT INTO "ur_wahid_uddin" VALUES(10,58,'10:58','کہو کہ یہ اللہ کے فضل اور اس کی رحمت سے ہے اب چاہيے کہ لوگ خوش ہوں ، یہ اس سے بہتر ہے جس کو وہ جمع کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,59,'10:59','کہو، یہ بتاؤ کہ اللہ نے تمھارے ليے جو رزق اتارا تھا، پھر تم نے اس میں سے کچھ کو حرام ٹھہرایا اور کچھ کو حلال کہو، کیا اللہ نے تم کو اس کا حکم دیا ہے یا تم اللہ پر جھوٹ لگا رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(10,60,'10:60','اور قیامت کے دن کے بارے میں ان لوگوں کا کیا خیال ہے جو اللہ پر جھوٹ لگا رہے ہیں بے شک اللہ لوگوں پر بڑا فضل فرمانے والا ہے، مگر اکثر لوگ شکر ادا نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(10,61,'10:61','اور تم جس حال میں بھی ہو اور قرآن میں سے جو حصہ بھی سنا رہے ہو اور تم لوگ جو کام بھی کرتے ہو، ہم تمھارے اوپر گواہ رہتے ہیں جس وقت تم اس میں مشغول ہوتے ہو اور تیرے رب سے ذرہ برابر بھی کوئی چیز غائب نہیں ، نہ زمین میں اور نہ آسمان میں اور نہ اس سے چھوٹی اور نہ بڑی، مگر وہ ایک واضح کتاب میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,62,'10:62','سن لو، اللہ کے دوستوں کے ليے نہ کوئی خوف ہوگا اور نہ وہ غمگین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,63,'10:63','یہ وہ لوگ ہیں جو ایمان لائے اور ڈرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,64,'10:64','ان کے ليے خوش خبری ہے دنیا کی زندگی میں بھی اور آخرت میں بھی اللہ کی باتوں میں کوئی تبدیلی نہیں ، یہی بڑی کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,65,'10:65','اور تم کو ان کی بات غم میں نہ ڈالےزور سب اللہ ہی کے ليے ہے، وہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,66,'10:66','سنو، جو آسمانوں میں ہیں اور جو زمین میں ہیں سب اللہ ہی کے ہیں اور جو لوگ اللہ کے سوا شریکوں کو پکارتے ہیں وہ کس چیز کی پیروی کر رہے ہیں ، وہ صرف گمان کی پیروی کر رہے ہیں اور وہ محض اٹکل دوڑا رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,67,'10:67','وہ اللہ ہی ہے جس نے تمھارے ليے رات بنائی تاکہ تم سکون حاصل کرو اور دن کو روشن بنایا بے شک اس میں نشانیاں ہیں ان لوگوں کے ليے جو سنتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,68,'10:68','کہتے ہیں کہ اللہ نے بیٹا بنایا ہے وہ پاک ہے، بے نیاز ہے اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے تمھارے پاس اس کی کوئی دلیل نہیں کیا تم اللہ پر ایسی بات گھڑتے ہو جس کا تم علم نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(10,69,'10:69','کہو،جو لوگ اللہ پر جھوٹ باندھتے ہیں وہ فلاح نہیں پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,70,'10:70','ان کے ليے بس دنیا میں تھوڑا فائدہ اٹھالینا ہے پھر ہماری ہی طرف ان کا لوٹنا ہے پھر ان کو ہم اس انکار کے بدلے سخت عذاب کا مزہ چکھائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,71,'10:71','اور ان کو نوح کا حال سناؤ جب کہ اس نے اپنی قوم سے کہا کہ اے میری قوم، اگر میرا کھڑا ہونا اور اللہ کی آیتوں سے نصیحت کرنا تم پر گراں ہوگیا ہے تو میں نے اللہ پر بھروسہ کیا تم اپنا متفقہ فیصلہ کرلو اور اپنے شریکوں کو بھی ساتھ لے لو، تم کو اپنے فیصلہ میں کوئی شبہ باقی نہ رہے پھر تم لوگ میرے ساتھ جو کچھ کرنا چاہتے ہو، وہ کرگزرو اور مجھ کو مہلت نہ دو');
INSERT INTO "ur_wahid_uddin" VALUES(10,72,'10:72','اگر تم اعراض کرو گے تو میں نے تم سے کوئی مزدوری نہیں مانگی ہے میری مزدوری تو اللہ کے ذمہ ہے اور مجھ کو حکم دیا گیا ہے کہ میں فرماں برداروں میں سے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(10,73,'10:73','پھر انھوں نے اس کو جھٹلادیا تو ہم نے نوح کو اور جو لوگ اس کے ساتھ کشتی میں تھے نجات دی اور ان کو جانشین بنایا اور ان لوگوں کو غرق کردیا جنھوں نے ہماری نشانیوں کو جھٹلایا تھا دیکھو کہ کیا انجام ہوا ان کا جن کو ڈرایا گیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(10,74,'10:74','پھر ہم نے نوح کے بعد کتنے رسول بھیجے وہ ان کے پاس کھلی کھلی دلیلیں لے کر آئے، مگر وہ اس پر ایمان لانے والے نہ بنے جس کو وه پہلے جھٹلاچکے تھے اسی طرح ہم حد سے نکل جانے والوں کے دلوں پر مہر لگادیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,75,'10:75','پھر ہم نے ان کے بعد موسیٰ اور ہارون کو فرعون اور اس کے سرداروں کے پاس اپنی نشانیاں دے کر بھیجا، مگر انھوں نے گھمنڈ کیا اور وہ مجرم لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(10,76,'10:76','پھر جب ان کے پاس ہماری طرف سے سچی بات پہنچی تو انھوں نے کہا یہ تو کھلا ہوا جادو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,77,'10:77','موسیٰ نے کہا کہ کیا تم حق کو جادو کہتے ہو جب کہ وہ تمھارے پاس آچکا ہے کیا یہ جادو ہے، حالاں کہ جادو والے کبھی فلاح نہیں پاتے');
INSERT INTO "ur_wahid_uddin" VALUES(10,78,'10:78','انھوں نے کہا کہ کیا تم ہمارے پاس اس ليے آئے ہو کہ ہم کو اس راستے سے پھیر دو جس پر ہم نے اپنے باپ دادا کو پایا ہے، اور اس ملک میں تم دونوں کی بڑائی قائم ہوجائے، اور ہم کبھی تم دونوں کی بات ماننے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,79,'10:79','اور فرعون نے کہا کہ تمام ماہر جادو گروں کو میرے پاس لے آؤ');
INSERT INTO "ur_wahid_uddin" VALUES(10,80,'10:80','جب جادو گر آئے تو موسیٰ نے ان سے کہا کہ جو کچھ تمھیں ڈالنا ہے ڈالو');
INSERT INTO "ur_wahid_uddin" VALUES(10,81,'10:81','پھر جب جادو گروں نے ڈالا تو موسیٰ نے کہا کہ جو کچھ تم لائے ہو وہ جادو ہے بے شک اللہ اس کو باطل کردے گا، اللہ یقینا مفسدوں کے کام کو سدھرنے نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(10,82,'10:82','اور اللہ اپنے حکم سے حق کو حق کر دکھاتا ہے، خواہ مجرموں کو وہ کتنا ہی ناگوار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(10,83,'10:83','پھر موسیٰ کو اس کی قوم میں سے چند نوجوانوں کے سوا کسی نے نہ مانا، فرعون کے ڈر سے اور خود اپنی قوم کے بڑے لوگوں کے ڈر سے کہ کہیں وہ ان کو کسی فتنہ میں نہ ڈال دے، بے شک فرعون زمین میں غلبہ رکھتا تھا اور وہ ان لوگوں میں سے تھا جو حد سے گزرجاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,84,'10:84','اور موسیٰ نے کہا اے میری قوم، اگر تم اللہ پر ایمان رکھتے ہو تو اسی پر بھروسہ کرو، اگر تم واقعی فرماں بردار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(10,85,'10:85','انھوں نے کہا، ہم نے اللہ پر بھروسہ کیا، اے ہمارے رب، ہمیں ظالم لوگوں کے ليے فتنہ نہ بنا');
INSERT INTO "ur_wahid_uddin" VALUES(10,86,'10:86','اور اپنی رحمت سے ہم کو منکر لوگوں سے نجات دے');
INSERT INTO "ur_wahid_uddin" VALUES(10,87,'10:87','اور ہم نے موسیٰ اور اس کے بھائی کی طرف وحی کی کہ اپنی قوم کے ليے مصر میں کچھ گھر مقرر کرلو اور اپنے ان گھرو ں کو قبلہ بناؤ اور نماز قائم کرو اور اہل ایمان کو خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(10,88,'10:88','اور موسیٰ نے کہا، اے ہمارے رب، تو نے فرعون کو اور اس کے سرداروں کو دنیا کی زندگی میں رونق اور مال دیا ہے اے ہمارے رب، اس ليے کہ وہ تیری راہ سے لوگوں کو بھٹکائیں اے ہمارے رب، ان کے مال کو غارت کردے اور ان کے دلوں کو سخت کردے کہ وہ ایمان نہ لائیں یہاں تک کہ دردناک عذاب کو دیکھ لیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,89,'10:89','فرمایا، تم دونوں کی دعا قبول کی گئی اب تم دونوں جمے رہو اور ان لوگوں کی راہ کی پیروی نہ کرو جو علم نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(10,90,'10:90','اور ہم نے بنی اسرائیل کو سمندر پار کرا دیا تو فرعون اور اس کے لشکر نے ان کا پیچھا کیا، سرکشی اور زیادتی کی غرض سے یہاں تک کہ جب فرعون ڈوبنے لگا تو اس نے کہا کہ میں ایمان لایا کہ کوئی معبود نہیں ، مگر وہ جس پر بنی اسرائیل ایمان لائے اور میں اس کے فرماں بردارو ں میں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(10,91,'10:91','کیا اب، اور اس سے پہلے تو نافرمانی کرتا رہا اور تو فساد برپا کرنے والوں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(10,92,'10:92','پس آج ہم تیرے بدن کو بچائیں گے تاکہ تو اپنے بعد والوں کے ليے نشانی بنے، اور بیشک بہت سے لوگ ہماری نشانیوں سے غافل رہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,93,'10:93','اور ہم نے بنی اسرائیل کو اچھا ٹھکانا دیا اور ان کو ستھری چیزیں کھانے کے ليے دیں پھر انھوں نے اختلاف نہیں کیا مگر اس وقت جب کہ علم ان کے پاس آچکا تھا یقینا تیرا رب قیامت کے دن ان کے درمیان اس چیز کا فیصلہ کردے گا جس میں وہ اختلاف کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,94,'10:94','پس اگر تم کو اس چیز کے بارے میں شک ہے جو ہم نے تمھاری طرف اتاری ہے تو ان لوگوں سے پوچھ لو جو تم سے پہلے سے کتاب پڑھ رہے ہیں بے شک یہ تم پر حق آیا ہے تمھارے رب کی طرف سے، پس تم شک کرنے والوں میں سے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(10,95,'10:95','اور تم ان لوگوں میں شامل نہ ہو جنھوں نے اللہ کی آیتوں کو جھٹلایا ہے، ور نہ تم نقصان اٹھانے والوں میں سے ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(10,96,'10:96','بے شک جن لوگوں پر تیرے رب کی بات پوری ہوچکی ہے، وہ ایمان نہیں لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,97,'10:97','خواہ ان کے پاس ساری نشانیاں آجائیں جب تک کہ وہ درد ناک عذاب کو سامنے آتا نہ دیکھ لیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,98,'10:98','پس کیوں نہ ہوا کہ کوئی بستی ایمان لاتی کہ اس کا ایمان اس کو نفع دیتا، یونس کی قوم کے سوا جب وہ ایمان لائے تو ہم نے ان سے دنیا کی زندگی میں رسوائی کا عذاب ٹال دیا اور ان کو ایک مدت تک بہرہ مند ہونے کا موقع دیا');
INSERT INTO "ur_wahid_uddin" VALUES(10,99,'10:99','اور اگر تیرا رب چاہتا تو زمین پر جتنے لوگ ہیں سب کے سب ایمان لے آتے پھر کیا تم لوگوں کو مجبور کروگے کہ وہ مومن ہوجائیں');
INSERT INTO "ur_wahid_uddin" VALUES(10,100,'10:100','اور کسی شخص کے ليے ممکن نہیں کہ وہ اللہ کی اجازت کے بغیر ایمان لاسکے اور اللہ ان لوگوں پر گندگی ڈال دیتا ہے جو عقل سے کام نہیں لیتے');
INSERT INTO "ur_wahid_uddin" VALUES(10,101,'10:101','کہو کہ آسمانوں اور زمین میں جو کچھ ہے، اسے دیکھواور نشانیاں اور ڈراوے ان لوگوں کو فائدہ نہیں پہنچاتے جوایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(10,102,'10:102','وہ تو بس اس طرح کے دن کا انتظار کر رہے ہیں جس طرح کے دن ان سے پہلے گزرے ہوئے لوگوں کو پیش آئے کہو، انتظار کرو میں بھی تمھارے ساتھ انتظار کرنے والوں میں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(10,103,'10:103','پھر ہم بچالیتے ہیں اپنے رسولوں کو اور ان کو جو ایمان لائے اسی طرح ہمارا ذمہ ہے کہ ہم ایمان والوں کو بچالیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,104,'10:104','کہو، اے لوگو! اگر تم میرے دین کے متعلق شک میں ہو تو میں ان کی عبادت نہیں کرتا جن کی عبادت تم کرتے ہو اللہ کے سوا بلکہ میں اس اللہ کی عبادت کرتا ہوں جو تم کو وفات دیتا ہے اور مجھ کوحکم ملا ہے کہ میں ایمان والوں میں سے بنوں');
INSERT INTO "ur_wahid_uddin" VALUES(10,105,'10:105','اور یہ کہ اپنا رخ یکسو ہوکر دین کی طرف کروں اور مشرکوں میں سے نہ بنوں');
INSERT INTO "ur_wahid_uddin" VALUES(10,106,'10:106','اور اللہ کے علاوہ ان کو نہ پکارو جو تم کو نہ نفع پہنچاسکتے ہیں اور نہ نقصان پھر اگر تم ایسا کرو گے تو یقینا تم ظالموں میں سے ہوجاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(10,107,'10:107','اور اگر اللہ تم کو کسی تکلیف میں پکڑلے تو اس کے سوا کوئی نہیں جو اس کو دور کرسکے اور اگر وہ تم کو کوئی بھلائی پہنچانا چاہے تو اس کے فضل کو کوئی روکنے والا نہیں وہ اپنا فضل اپنے بندوں میں سے جس کو چاہتا ہے دیتا ہے اور وہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(10,108,'10:108','کہو، اے لوگو، تمھارے رب کی طرف سے تمھارے پاس حق آگیا ہے جو ہدایت قبول کرے گا، وہ اپنے ہی ليے کرے گا اور جو بھٹکے گا تو اس کا وبال اسی پر آئے گا، اور میں تمھارے اوپر ذمہ دار نہیں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(10,109,'10:109','اور تم اس کی پیروی کرو جو تم پر وحی کی جاتی ہے اور صبر کرو یہاں تک کہ اللہ فیصلہ کردے اور وہ بہترین فیصلہ کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,1,'11:1','الر یہ کتاب ہے جس کی آیتیں پہلے محکم کی گئیں پھر ایک دانا اور خبیر ہستی کی طرف سے ان کی تفصیل کی گئی');
INSERT INTO "ur_wahid_uddin" VALUES(11,2,'11:2','کہ تم اللہ کے سوا کسی اور کی عبادت نہ کرو میں تم کو اس کی طرف سے ڈرانے والا اور خوش خبری دینے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(11,3,'11:3','اور یہ کہ تم اپنے رب سے معافی چاہو اور اس کی طرف پلٹ آؤ، وہ تم کو ایک مدت تک عطا كرے گا اچھا سامانِ زندگي، اور وه زياده بهتر عمل كرنے والے كو زياده عطا كرتا هےاور اگر تم پھر جاؤ تو میں تمھارے حق میں ایک بڑے دن کے عذاب سے ڈرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(11,4,'11:4','تم سب کو اللہ کی طرف پلٹنا ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,5,'11:5','دیکھو، یہ لوگ اپنے سینوں کو لپیٹتے ہیں تاکہ اس سے چھپ جائیں خبردار، جب وہ کپڑوں سے اپنے آپ کو ڈھانپتے ہیں ، اللہ جانتا ہے جو کچھ وہ چھپاتے ہیں اور جو وہ ظاہر کرتے ہیں وہ دلوں کی بات تک جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,6,'11:6','اور زمین پر کوئی چلنے والا ایسا نہیں جس کی روزی اللہ کے ذمہ نہ ہو اور وہ جانتا ہے جہاں کوئی ٹھہرتا ہے اور جہاں وہ سونپا جاتا ہے سب کچھ ایک کھلی ہوئی کتاب میں موجود ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,7,'11:7','اور وہی ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا اور اس کا عرش پانی پر تھا، تاکہ تم کو آزمائے کہ کون تم میں اچھا کام کرتا ہے اور اگر تم کہو کہ مرنے کے بعد تم لوگ اٹھائے جاؤ گے تو منکرین کہتے ہیں یہ تو کھلا ہوا جادو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,8,'11:8','اور اگر ہم کچھ مدت تک ان کی سزا کو روک دیں تو کہتے ہیں کہ کیا چیز اس کو روکے ہوئے ہے آگاہ، جس دن وہ ان پر آپڑے گا تو وہ ان سے پھیرا نہ جاسکے گا اور ان کو گھیرے گی وہ چیز جس کا وہ مذاق اڑا رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(11,9,'11:9','اور اگر ہم انسان کو اپنی کسی رحمت سے نوازتے ہیں پھر اس سے اس کو محروم کردیتے ہیں تو وہ مایوس اور ناشکرا بن جاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,10,'11:10','اور اگر کسی تکلیف کے بعد جو اس کو پہنچی تھی، اس کو ہم نعمت سے نوازتے ہیں تو وہ کہتا ہے کہ ساری مصیبتیں مجھ سے دور ہوگئیں ، وہ اترانے والا اور اکڑنے والا بن جاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,11,'11:11','مگر جو لوگ صبر کرنے والے اور نیک عمل کرنے والے ہیں ، ان کے ليے بخشش ہے اور بڑا اجر');
INSERT INTO "ur_wahid_uddin" VALUES(11,12,'11:12','کہیں ایسا نہ ہو کہ تم اس چیز کا کچھ حصہ چھوڑ دو جو تمھاری طرف وحی کی گئی ہے اور تم اس بات پر تنگ دل ہوکہ وہ کہتے ہیں کہ اس پر کوئی خزانہ کیوں نہیں اتارا گیایا اس کے ساتھ کوئی فرشتہ کیوں نہیں آیا تم تو صرف ڈرانے والے ہو اور اللہ ہر چیز کا ذمہ دار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,13,'11:13','کیا وہ کہتے ہیں کہ پیغمبر نے اس کتاب کو گھڑ لیا ہے کہو، تم بھی ایسی ہی دس سورتیں بناکر لے آؤ اور اللہ کے سوا جس کو بلا سکو بلالو، اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(11,14,'11:14','پس اگر وہ تمھارا کہا پورا نہ کرسکیں تو جان لو کہ یہ اللہ کے علم سے اترا ہے اور یہ کہ اس کے سوا کوئی معبود نہیں ، پھر کیا تم حکم مانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(11,15,'11:15','جو لوگ دنیا کی زندگی اور اس کی زینت چاہتے ہیں ، ہم ان کے اعمال کا بدلہ دنیا ہی میں دے دیتے ہیں اور اس میں ان کے ساتھ کوئی کمی نہیں کی جاتی');
INSERT INTO "ur_wahid_uddin" VALUES(11,16,'11:16','یہی لوگ ہیں جن کے ليے آخرت میں آگ کے سوا کچھ نہیں ہے انھوں نے دنیا میں جوکچھ بنایا تھا وہ برباد ہوا اور خراب گیا جو انھوں نے کمایا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(11,17,'11:17','بھلا ایک شخص جو اپنے رب کی طرف سے ایک دلیل پر ہے، اس کے بعداللہ کی طرف سے اس کے ليے ایک گواہ بھی آگیا، اور اس سے پہلے موسیٰ کی کتاب رہنما اور رحمت کی حیثیت سے موجود تھی، ایسے ہی لوگ اس پر ایمان لاتے ہیں اور جماعتوں میں سے جو کوئی اس کا انکار کرے تو اس کے وعدہ کی جگہ آگ ہے پس تم اس کے بارے میں کسی شک میں نہ پڑو یہ حق ہے تمھارے رب کی طرف سے مگر اکثر لوگ نہیں مانتے');
INSERT INTO "ur_wahid_uddin" VALUES(11,18,'11:18','اور اس سے بڑھ کر ظالم کون ہے جو اللہ پر جھوٹ گھڑے ایسے لوگ اپنے رب کے سامنے پیش ہوں گے اور گواہی دینے والے کہیں گے کہ یہ وہ لوگ ہیں جنھوں نے اپنے رب پر جھوٹ گھڑا تھا سنو، اللہ کی لعنت ہے ظالموں کے اوپر');
INSERT INTO "ur_wahid_uddin" VALUES(11,19,'11:19','ان لوگوں کے اوپر جو اللہ کے راستہ سے لوگوں کو روکتے ہیں اور اس میں کجی ڈھونڈتے ہیں یہی لوگ آخرت کے منکر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,20,'11:20','وہ لوگ زمین میں اللہ کو بے بس کرنے والے نہیں اور نہ اللہ کے سواان کا کوئی مدد گار ہے، ان پر دہرا عذاب ہوگا وہ نہ سن سکتے تھے اور نہ دیکھتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(11,21,'11:21','یہ وہ لوگ ہیں جنھوں نے اپنے آپ کو گھاٹے میں ڈالا اور وہ سب کچھ ان سے کھویا گیا جو انھوں نے گھڑ رکھا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(11,22,'11:22','اس میں شک نہیں کہ یہی لوگ آخرت میں سب سے زیادہ گھاٹے میں رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(11,23,'11:23','جو لوگ ایمان لائے اور جنھوں نے نیک عمل کيے اور اپنے رب کے سامنے عاجزی کی، وہی لوگ جنت والے ہیں وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(11,24,'11:24','ان دونوں فریقوں کی مثال ایسی ہے جیسے ایک اندھا اور بہرا ہو اور دوسرا دیکھنے اور سننے والا کیا یہ دونوں یکساں ہوجائیں گے کیا تم غور نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(11,25,'11:25','اور ہم نے نوح کو اس کی قوم کی طرف بھیجا کہ میں تم کو کھلا ہوا ڈرانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(11,26,'11:26','یہ کہ تم اللہ کے سوا کسی کی عبادت نہ کرو میں تم پر ایک درد ناک عذاب کے دن کا اندیشہ رکھتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(11,27,'11:27','اس کی قوم کے سرداروں نے کہا، جنھوں نے انکار کیا تھا کہ ہم تو تم کو بس اپنے جیسا ایک آدمی دیکھتے ہیں اور ہم نہیں دیکھتے کہ کوئی تمھارا تابع ہوا ہو، سوائے ان کے جو ہم میں پست لوگ ہیں ، بے سمجھے بوجھے اور ہم نہیں دیکھتے کہ تم کو ہمارے اوپر کچھ بڑائی حاصل ہے، بلکہ ہم تو تم کو جھوٹا خیال کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,28,'11:28','نوح نے کہا اے میری قوم، بتاؤ اگر میں اپنے رب کی طرف سے ایک روشن دلیل پر ہوں اور اس نے مجھ پر اپنے پاس سے رحمت بھیجی ہے، مگر وہ تم کو نظر نہ آئی تو کیا ہم اس کو تم پر چپکاسکتے ہیں جب کہ تم اس سے بیزار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(11,29,'11:29','اور اے میری قوم، میں اس پر تم سے کچھ مال نہیں مانگتا میرا اجر تو بس اللہ کے ذمہ ہے اور میں ہر گز ان کو اپنے سے دور کرنے والا نہیں جو ایمان لائے ہیں ان لوگوں کو اپنے رب سے ملنا ہے مگر میں دیکھتا ہوں تم لوگ جہالت میں مبتلا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(11,30,'11:30','اور اے میری قوم، اگر میں ان لوگوں کو دھتکار دوں تو خدا کے مقابلہ میں کون میری مدد کرے گا کیا تم غور نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(11,31,'11:31','اور میں تم سے نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں اور نہ میں غیب کی خبر رکھتا ہوں اور نہ یہ کہتا ہوں کہ میں فرشتہ ہوں اور میں یہ بھی نہیں کہہ سکتا کہ جو لوگ تمھاری نگاہوں میں حقیر ہیں ، ان کو اللہ کوئی بھلائی نہیں دے گا اللہ خوب جانتا ہے جو کچھ ان کے دلوں میں ہے اگر میں ایسا کہوں تو میں ہی ظالم ہوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(11,32,'11:32','انھوں نے کہا کہ اے نوح، تم نے ہم سے جھگڑا کیا اور بہت جھگڑا کرلیا اب وہ چیز لے آؤ جس کا تم ہم سے وعدہ کرتے رہے ہو، اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(11,33,'11:33','نوح نے کہا اس کو تو تمھارے اوپر اللہ ہی لائے گا اگر وہ چاہے گا اور تم اس کے قابو سے باہر نہ جاسکو گے');
INSERT INTO "ur_wahid_uddin" VALUES(11,34,'11:34','اور میری نصیحت تم کو فائدہ نہیں دے گی اگر میں تم کو نصیحت کرنا چاہوں جب کہ اللہ یہ چاہتا ہو کہ وہ تم کو گمراہ کرے وہی تمھارارب ہے اور اسی کی طرف تم کو لوٹ کر جانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,35,'11:35','کیا وہ کہتے ہیں کہ پیغمبر نے اس کو گھڑلیا ہے کہوکہ اگر میں نے اس کو گھڑا ہے تو میرا جرم میرے اوپر ہے اور جو جرم تم کر رہے ہو، اس سے میں بری ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(11,36,'11:36','اور نوح کی طرف وحی کی گئی کہ اب تمھاری قوم میں سے کوئی ایمان نہیں لائے گا، سوا اس کے جو ایمان لاچکا ہے پس تم ان کاموں پر غمگین نہ ہو جو وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,37,'11:37','اور ہمارے رو برو اور ہمارے حکم سے تم کشتی بناؤ اور ظالموں کے حق میں مجھ سے بات نہ کرو، بے شک یہ لوگ غرق ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(11,38,'11:38','اور نوح کشتی بنانے لگا اور جب اس کی قوم کا کوئی سردار اس پر گزرتا تو وہ اس کی ہنسی اڑاتا، انھوں نے کہا اگر تم ہم پر ہنستے ہو تو ہم بھی تم پر ہنسيں گے');
INSERT INTO "ur_wahid_uddin" VALUES(11,39,'11:39','تم جلد جان لوگے کہ وہ کون ہیں جن پر وہ عذاب آتا ہے جو اس کو رسوا کردے اور اس پر وہ عذاب اترتا ہے جو دائمی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,40,'11:40','یہاں تک کہ جب ہمارا حکم آپہنچا اور طوفان ابل پڑا، ہم نے نوح سے کہا کہ ہر قسم کے جانوروں کا ایک ایک جوڑا کشتی میں رکھ لو اور اپنے گھر والوں کو بھی، سوا ان اشخاص کے جن کی بابت پہلے کہا جاچکا ہے اور سب ایمان والوں کو بھی اور تھوڑے ہی لوگ تھے جو نوح کے ساتھ ایمان لائے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(11,41,'11:41','اور نوح نے کہا کہ کشتی میں سوار ہوجاؤ، اللہ کے نام سے اس کا چلنا ہے اور اس کا ٹھہرنا بھی بیشک میرا رب بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,42,'11:42','اور کشتی پہاڑ جیسی موجوں کے درمیان ان کو لے کر چلنے لگی اور نوح نے اپنے بیٹے کو پکارا جو اس سے الگ تھا اے میرے بیٹے، ہمارے ساتھ سوار ہوجا اور منکروں کے ساتھ مت رہ');
INSERT INTO "ur_wahid_uddin" VALUES(11,43,'11:43','اس نے کہا میں کسی پہاڑ کی پناہ لے لوں گا جو مجھ کو پانی سے بچا لے گا نوح نے کہا کہ آج کوئی اللہ کے حکم سے بچانے والا نہیں ، مگر وہ جس پر اللہ رحم کرے اور دونوں کے درمیان موج حائل ہوگئی اور وہ ڈوبنے والوں میں شامل ہوگیا');
INSERT INTO "ur_wahid_uddin" VALUES(11,44,'11:44','اور کہا گیا کہ اے زمین، اپنا پانی نگل لے اور اے آسمان تھم جا اور پانی سکھادیا گیا اور معاملہ کا فیصلہ ہوگیا اور کشتی جو دی پہاڑ پر ٹھہر گئی اور کہہ دیا گیا کہ دور ہو ظالموں کی قوم');
INSERT INTO "ur_wahid_uddin" VALUES(11,45,'11:45','اور نوح نے اپنے رب کو پکارا اور کہا کہ اے میرے رب، میرا بیٹا میرے گھر والوں میں سے ہے، اور بے شک تیرا وعدہ سچا ہے اور تو سب سے بڑا حاکم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,46,'11:46','خدا نے کہا اے نوح، وہ تیرے گھر والوں میں نہیں اس کے کام خراب ہیں پس مجھ سے اس چیز کے ليے سوال نہ کرو جس کا تمھیں علم نہیں میں تم کو نصیحت کرتا ہوں کہ تم جاہلوں میں سے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(11,47,'11:47','نوح نے کہا کہ اے میرے رب، میں تیری پناہ چاہتا ہوں کہ تجھ سے وہ چیز مانگوں جس کا مجھے علم نہیں اور اگر تو مجھے معاف نہ کرے اور مجھ پر رحم نہ فرمائے تو میں برباد ہوجاؤں گا');
INSERT INTO "ur_wahid_uddin" VALUES(11,48,'11:48','کہا گیا کہ اے نوح، اترو، ہماری طرف سے سلامتی کے ساتھ اور برکتوں کے ساتھ، تم پر اور ان گروہوں پر جو تمھارے ساتھ ہیں اور (ان سے ظہور میں آنے والے) گروہ کہ ہم ان کو فائدہ دیں گے، پھر ان کو ہماری طرف سے ایک درد ناک عذاب پکڑلے گا');
INSERT INTO "ur_wahid_uddin" VALUES(11,49,'11:49','یہ غیب کی خبریں ہیں جن کو ہم تمھاری طرف وحی کر رہے ہیں اس سے پہلے نہ تم ان کو جانتے تھے اور نہ تمھاری قوم پس صبر کرو بے شک آخری انجام ڈرنے والوں کے ليے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,50,'11:50','اور عاد کی طرف ہم نے ان کے بھائی ہود کو بھیجا اس نے کہا کہ اے میری قوم، اللہ کی عبادت کرو اس کے سوا تمھارا کوئی معبود نہیں تم نے محض جھوٹ گھڑ رکھے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,51,'11:51','اے میری قوم، میں اس پر تم سے کوئی اجر نہیں مانگتا میرا اجر تو اس پر ہے جس نے مجھے پیدا کیا ہے کیا تم نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(11,52,'11:52','اور اے میری قوم، اپنے رب سے معافی چاہو، پھر اس کی طرف پلٹو وہ تمھارے اوپر خوب بارشیں برسائے گا اور تمھاری قوت پر مزید قوت کا اضافہ کرے گا اور تم مجرم ہوکر روگردانی نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(11,53,'11:53','انھوں نے کہا کہ اے ہود، تم ہمارے پاس کوئی کھلی ہوئی نشانی لے کر نہیں آئے ہو، اور ہم تمھارے کہنے سے اپنے معبودوں کو چھوڑنے والے نہیں ہیں اور ہم ہرگز تم کو ماننے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,54,'11:54','ہم تو یہی کہیں گے کہ تمھارے اوپر ہمارے معبودوں میں سے کسی کی مار پڑ گئی ہے ہود نے کہا، میں اللہ کو گواہ ٹھہراتا ہوں اور تم بھی گواہ رہو کہ میں بری ہوں ان سے جن کو تم شریک کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(11,55,'11:55','اس کے سوا پس تم سب مل کر میرے خلاف تدبیر کرو، پھر مجھ کومہلت نہ دو');
INSERT INTO "ur_wahid_uddin" VALUES(11,56,'11:56','میں نے اللہ پر بھروسہ کیا جو میرا رب ہے اور تمھارا رب بھی کوئی جاندار ایسا نہیں جس کی چوٹی اس کے ہاتھ میں نہ ہو بے شک میرا رب سیدھی راہ پر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,57,'11:57','اگر تم اعراض کرتے ہو تو میں نے تم کو وہ پیغام پہنچا دیا جس کو دے کر مجھے تمھاری طرف بھیجا گیا تھا اور میرا رب تمھاری جگہ تمھارے سوا کسی اور گروہ کو جانشین (خلیفہ) بنائے گا تم اس کا کچھ نہ بگاڑ سکو گے بے شک میرا رب ہر چیز پر نگہبان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,58,'11:58','اور جب ہمارا حکم آپہنچا، ہم نے اپنی رحمت سے بچا دیا ہود کو اور ان لوگوں کو جو اس کے ساتھ ایمان لائے تھے اور ہم نے ان کو ایک سخت عذاب سے بچادیا');
INSERT INTO "ur_wahid_uddin" VALUES(11,59,'11:59','اور یہ عاد تھے کہ انھوں نے اپنے رب کی نشانیوں کا انکار کیا اور اس کے رسولوں کو نہ مانا اور ہر سرکش اور مخالف کی بات کی اتباع کی');
INSERT INTO "ur_wahid_uddin" VALUES(11,60,'11:60','اور ان کے پیچھے لعنت لگادی گئی اس دنیا میں اور قیامت کے دن سن لو، عاد نے اپنے رب کا انکار کیا سن لو، دوری ہے عاد کے ليے جو ہود کی قوم تھی');
INSERT INTO "ur_wahid_uddin" VALUES(11,61,'11:61','اور ثمود کی طرف ہم نے ان کے بھائی صالح کو بھیجا اس نے کہا، اے میری قوم، اللہ کی عبادت کرو اس کے سوا تمھارا ا کوئی معبود نہیں اسی نے تم کو زمین سے بنایا، اور اس میں تم کو آباد کیا پس معافی چاہو، پھر اس کی طرف رجوع کرو بے شک میرا رب قریب ہے، قبول کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,62,'11:62','انھوں نے کہا کہ اے صالح اس سے پہلے ہم کو تم سے امید تھی کیا تم ہم کو ان کی عبادت سے روکتے ہو جن کی عبادت ہمارے باپ دادا کرتے تھے اور جس چیز کی طرف تم ہم کو بلاتے ہو، اس کے بارے میں ہم کو سخت شبہ ہے اور ہم بڑے خلجان میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,63,'11:63','اس نے کہا کہ اے میری قوم بتاؤ اگر میں اپنے رب کی طرف سے ایک واضح دلیل پر ہوں اور اس نے مجھ کو اپنے پاس سے رحمت دی ہے تو مجھ کو خدا سے کون بچائے گا اگر میں اس کی نافرمانی کروں پس تم کچھ نہیں بڑھاؤ گے میرا سوائے نقصان کے');
INSERT INTO "ur_wahid_uddin" VALUES(11,64,'11:64','اور اے میری قوم، یہ اللہ کی اونٹنی تمھارے ليے ایک نشانی ہے پس اس کو چھوڑ دو کہ وہ اللہ کی زمین میں کھائے اور اس کوکوئی تکلیف نہ پہنچاؤ ورنہ بہت جلد تم کو عذاب پکڑ لے گا');
INSERT INTO "ur_wahid_uddin" VALUES(11,65,'11:65','پھر انھوں نے اس کے پاؤں کاٹ ڈالے تب صالح نے کہا کہ تین دن اور اپنے گھروں میں فائدہ اٹھالو یہ ایک وعدہ ہے جو جھوٹا نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(11,66,'11:66','پھر جب ہمارا حکم آگیا تو ہم نے اپنی رحمت سے صالح کو اور ان لوگوں کو جو اس کے ساتھ ایمان لائے تھے بچالیا اور اس دن کی رسوائی سے (محفوظ رکھا) بیشک تیرا رب ہی قوی اور زبردست ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,67,'11:67','اور جن لوگوں نے ظلم کیا تھا، ان کو ایک ہولناک آواز نے پکڑ لیا، پھر صبح کو وہ اپنے گھروں میں اوندھے پڑے رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(11,68,'11:68','جیسے کہ وہ کبھی ان میں بسے ہی نہیں سنو، ثمود نے اپنے رب سے کفر کیا سنو، پھٹکار ہے ثمود کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(11,69,'11:69','اور ابراہیم کے پاس ہمارے فرشتے خوش خبری لے کر آئے کہا تم پر سلامتی ہو ابراہیم نے کہا تم پر بھی سلامتی ہو پھر دیر نہ گزری کہ ابراہیم ایک بھنا ہوا بچھڑا لے آیا');
INSERT INTO "ur_wahid_uddin" VALUES(11,70,'11:70','پھر جب دیکھا کہ ان کے ہاتھ کھانے کی طرف نہیں بڑھ رہے ہیں تو وہ کھٹک گیا اور دل میں ان سے ڈرا انھوں نے کہا کہ ڈرو نہیں ، ہم لوط کی قوم کی طرف بھیجے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,71,'11:71','اور ابراہیم کی بیوی کھڑی تھی، وہ ہنس پڑی پس ہم نے اس کو اسحاق کی خوش خبری دی اور اسحاق کے آگے یعقوب کی');
INSERT INTO "ur_wahid_uddin" VALUES(11,72,'11:72','اس نے کہا، اے خرابی، کیا میں بچہ جنوں گی، حالاں کہ میں بوڑھی ہوں اور یہ میرا خاوند بھی بوڑھا ہے یہ تو ایک عجیب بات ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,73,'11:73','فرشتوں نے کہا، کیا تم اللہ کے حکم پر تعجب کرتی ہو ابراہیم کے گھر والو، تم پر اللہ کی رحمتیں اور برکتیں ہیں بے شک اللہ نہایت قابل تعریف اور بڑی شان والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,74,'11:74','پھر جب ابراہیم کا خوف دور ہوا اور اس کو خوش خبری ملی تو وہ ہم سے قوم لوط کے بارے میں جھگڑنے لگا');
INSERT INTO "ur_wahid_uddin" VALUES(11,75,'11:75','بے شک ابراہیم بڑا حلیم اور نرم دل تھا اور رجوع کرنے والا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(11,76,'11:76','اے ابراہیم اس کو چھوڑو تمھارے رب کا حکم آچکا ہے اور ان پر ایک ایسا عذاب آنے والا ہے جو لوٹایا نہیں جاتا');
INSERT INTO "ur_wahid_uddin" VALUES(11,77,'11:77','اور جب ہمارے فرشتے لوط کے پاس پہنچے تو وہ گھبرایا اور ان کے آنے سے دل تنگ ہوا اس نے کہا آج کا دن بڑا سخت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,78,'11:78','اور اس کی قوم کے لوگ دوڑتے ہوئے اس کے پاس آئے اور وہ پہلے سے برے کام کر رہے تھے لوط نے کہا اے میری قوم، یہ میری بیٹیاں ہیں ، وہ تمھارے ليے زیادہ پاکیزہ ہیں پس تم اللہ سے ڈرو اور مجھ کو میرے مہمانوں کے سامنے رسوا نہ کرو کیا تم میں کوئی بھلا آدمی نہیں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,79,'11:79','انھوں نے کہا، تم جانتے ہو کہ ہم کو تمھاری بیٹیوں سے کچھ غرض نہیں ، اور تم جانتے ہو کہ ہم کیا چاہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,80,'11:80','لوط نے کہا، کاش میرے پاس تم سے مقابلہ کی قوت ہوتی یا میں جا بیٹھتا کسی مستحکم پناہ میں');
INSERT INTO "ur_wahid_uddin" VALUES(11,81,'11:81','فرشتوں نے کہا کہ اے لوط، ہم تیرے رب کے بھیجے ہوئے ہیں وہ ہرگز تم تک نہ پہنچ سکیں گے پس تم اپنے لوگوں کو لے کر کچھ رات رہے نکل جاؤ اور تم میں سے کوئی مڑ کر نہ دیکھے مگر تمھاری عورت، اس پروہی کچھ گزرنے والا ہے جو ان لوگوں پر گزرے گا ان کے ليے صبح کا وقت مقرر ہے، کیا صبح قریب نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,82,'11:82','پھر جب ہمارا حکم آیا تو ہم نے اس بستی کو تلپٹ کردیا اور اس پر پتھر برسائے کنکر کے، تہہ بہ تہہ');
INSERT INTO "ur_wahid_uddin" VALUES(11,83,'11:83','تمھارے رب کے پاس سے نشان لگائے ہوئے اور وہ بستی ان ظالموں سے کچھ دور نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,84,'11:84','اور مدین کی طرف ان کے بھائی شعیب کو بھیجا اس نے کہا کہ اے میری قوم، اللہ کی عبادت کرو، اس کے سوا تمھارا کوئی معبود نہیں اور ناپ اور تول میں کمی نہ کرو میں تم کو اچھے حال میں دیکھ رہا ہوں ، اور میں تم پر ایک گھیر لینے والے دن کے عذاب سے ڈرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(11,85,'11:85','اور اے میری قوم، ناپ اور تول کو پورا کرو انصاف کے ساتھ اور لوگوں کو ان کی چیزیں گھٹاکر نہ دو اور زمین پر فساد نہ مچاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(11,86,'11:86','جو اللہ کا دیا ہوا بچا رہے، وہ تمھارے ليے بہتر ہے اگر تم مومن ہو اور میں تمھارے اوپر نگہبان نہیں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(11,87,'11:87','انھوں نے کہا کہ اے شعیب، کیا تمھاری نماز تم کو یہ سکھاتی ہے کہ ہم ان چیزوں کو چھوڑ دیں جن کی عبادت ہمارے باپ دادا کرتے تھے یا اپنے مال میں اپنی مرضی کے مطابق تصرف کرنا چھوڑ دیں بس تم ہی تو ایک دانش مند اور نیک چلن آدمی ہو');
INSERT INTO "ur_wahid_uddin" VALUES(11,88,'11:88','شعیب نے کہا کہ اے میری قوم، بتاؤ ، اگر میں اپنے رب کی طرف سے ایک واضح دلیل پر ہوں اور اس نے اپنی جانب سے مجھ کو اچھا رزق بھی دیا اور میں نہیں چاہتا کہ میں خود وہی کام کروں جس سے میں تم کو روک رہا ہوں میں تو صرف اصلاح چاہتا ہوں ، جہاں تک ہوسکے اور مجھے توفیق تو اللہ ہی سے ملے گی اسی پر میں نے بھروسہ کیا ہے اور اسی کی طرف میں رجوع کرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(11,89,'11:89','اور اے میری قوم، ایسا نہ ہو کہ میری مخالفت کرکے تم پر وہ آفت آپڑے جو قوم نوح یا قوم ہود یا قوم صالح پر آئی تھی، اور لوط کی قوم تو تم سے دور بھی نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,90,'11:90','اور اپنے رب سے معافی مانگو پھر اس کی طرف پلٹ آؤبے شک میرا رب مہربان اور محبت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,91,'11:91','انھوں نے کہا کہ اے شعیب، جو تم کہتے ہو، اس کا بہت سا حصہ ہماری سمجھ میں نہیں آتا اور ہم تو دیکھتے ہیں کہ تو ہم میں کمزور ہے اور اگر تیری برادری نہ ہوتی تو ہم تم کو سنگسار کردیتے اور تم ہم پر کچھ بھاری نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,92,'11:92','شعیب نے کہا کہ اے میری قوم، کیا میری برادری تم پر اللہ سے زیادہ بھاری ہے اور اللہ کو تم نے پس پشت ڈال دیا بے شک میرے رب کے قابو میں ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(11,93,'11:93','اور اے میری قوم، تم اپنے طریقہ پر کام کيے جاؤ اور میں اپنے طریقہ پر کام کرتا رہوں گا جلد ہی تم کو معلوم ہوجائے گا کہ کس کے اوپر رسوا کرنے والا عذاب آتا ہے اور کون جھوٹا ہے اور انتظا رکرو، میں بھی تمھارے ساتھ انتظار کرنے والوں میں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(11,94,'11:94','اور جب ہمارا حکم آیا ہم نے شعیب کو اور جو اس کے ساتھ ایمان لائے تھے اپنی رحمت سے بچالیا اور جن لوگو ں نے ظلم کیا تھا، ان کو کڑک نے پکڑلیا پس وہ اپنے گھروں میں اوندھے پڑے رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(11,95,'11:95','گویا کہ وہ کبھی ان میں بسے ہی نہ تھے سنو، پھٹکار ہے مدین کو جیسے پھٹکار ہوئی تھی ثمود کو');
INSERT INTO "ur_wahid_uddin" VALUES(11,96,'11:96','اور ہم نے موسیٰ کو اپنی نشانیوں اور واضح سند کے ساتھ بھیجا');
INSERT INTO "ur_wahid_uddin" VALUES(11,97,'11:97','فرعون اور اس کے سرداروں کی طرف پھر وہ فرعون کے حکم پر چلے حالاں کہ فرعون کا حکم راستی پر نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(11,98,'11:98','قیامت کے دن وہ اپنی قوم کے آگے ہوگا اور ان کو آگ پر پہنچائے گا اور کیسا برا گھاٹ ہے جس پر وہ پہنچیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(11,99,'11:99','اور اس دنیا میں ان کے پیچھے لعنت لگادی گئی اور قیامت کے دن بھی کیسا برا انعام ہے جو ان کو ملا');
INSERT INTO "ur_wahid_uddin" VALUES(11,100,'11:100','یہ بستیوں کے کچھ حالات ہیں جو ہم تم کو سنارہے ہیں ان میں سے بعض بستیاں اب تک قائم ہیں اور بعـض مٹ گئیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,101,'11:101','اور ہم نے ان پر ظلم نہیں کیا بلکہ انھوں نے خو داپنے اوپر ظلم کیا پھر جب تیرے رب کا حکم آگیا تو ان کے معبود ان کے کچھ کام نہ آئے جن کو وہ اللہ کے سوا پکارتے تھے اور انھوں نے ان کے حق میں بربادی کے سوا اور کچھ نہیں بڑھایا');
INSERT INTO "ur_wahid_uddin" VALUES(11,102,'11:102','اور تیرے رب کی پکڑ ایسی ہی ہے جب کہ وہ بستیوں کو ان کے ظلم پر پکڑتا ہے بے شک اس کی پکڑ بڑی درد ناک اور سخت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,103,'11:103','اس میں ان لوگوں کے ليے نشانی ہے جو آخرت کے عذاب سے ڈریں وہ ایک ایسا دن ہے جس میں سب لوگ جمع ہوں گے اور وہ حاضری کا دن ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(11,104,'11:104','اور ہم اس کو ایک مدت کے ليے ٹال رہے ہیں جو مقرر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,105,'11:105','جب وہ دن آئے گا تو کوئی جان اس کی اجازت کے بغیر کلام نہ کرسکے گی پس ان میں کچھ بدبخت ہوں گے، اور کچھ نیک بخت');
INSERT INTO "ur_wahid_uddin" VALUES(11,106,'11:106','پس جو لوگ بدبخت ہیں وہ آگ میں ہوں گے ان کو وہاں چیخنا ہے اور دھاڑنا');
INSERT INTO "ur_wahid_uddin" VALUES(11,107,'11:107','وہ اس میں رہیں گے جب تک آسمان اور زمین قائم ہیں ، مگر جو تیرا رب چاہے بے شک تیرا رب کر ڈالتا ہے جو چاہتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(11,108,'11:108','اور جو لوگ نیک بخت ہیں وہ جنت میں ہوں گے، وہ اس میں رہیں گے جب تک آسمان اور زمین قائم ہیں ، مگر جو تیرا رب چاہے بخشش ہے بے انتہا');
INSERT INTO "ur_wahid_uddin" VALUES(11,109,'11:109','پس تو ان چیزوں سے شک میں نہ رہ جن کی یہ لوگ عبادت کر رہے ہیں یہ تو بس اسی طرح عبادت کر رہے ہیں جس طرح ان سے پہلے ان کے باپ دادا عبادت کر رہے تھے اور ہم ان کاحصہ انھیں پورا پورا دیں گے بغیر کسی کمی کے');
INSERT INTO "ur_wahid_uddin" VALUES(11,110,'11:110','اور ہم نے موسیٰ کو کتاب دی پھر اس میں پھوٹ پڑگئی اور اگر تیرے رب کی طرف سے پہلے ہی ایک بات نہ آچکی ہوتی تو ان کے درمیان فیصلہ کردیا جاتا اور ان کو اس میں شبہ ہے جو مطمئن نہیں ہونے دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(11,111,'11:111','اور یقینا ًتیرا رب ہر ایک کو اس کے اعمال کا پورا بدلہ دے گا وہ باخبر ہے اس سے جو وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,112,'11:112','پس تم جمے رہو جیسا کہ تم کو حکم ہوا ہے اور وہ بھی جنھوں نے تمھارے ساتھ توبہ کی ہے اور حد سے نہ بڑھو بیشک وہ دیکھ رہا ہے جو تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(11,113,'11:113','اور ان کی طرف نہ جھکو جنھوں نے ظلم کیا، ورنہ تم کو آگ پکڑ لے گی اور اللہ کے سوا تمھارا کوئی مدد گار نہیں ، پھر تم کہیں مدد نہ پاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(11,114,'11:114','اور نماز قائم کرو دن کے دونو ں حصوں میں اور رات کے کچھ حصہ میں بے شک نیکیاں دور کرتی ہیں برائیوں کو یہ یاد دہانی ہے یاد دہانی حاصل کرنے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(11,115,'11:115','اور صبر کرو، اللہ نیکی کرنے والوں کا اجر ضائع نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(11,116,'11:116','پس کیوں نہ ایسا ہوا کہ تم سے پہلے کی قوموں میں ایسے اہلِ خیر ہوتے جو لوگوں کو زمین میں فساد کرنے سے روکتے ایسے تھوڑے لوگ نکلے جن کو ہم نے ان میں سے بچالیا اور ظالم لوگ تو اسی عیش میں پڑے رہے جو انھیں ملا تھا اور وہ مجرم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(11,117,'11:117','اور تیرا رب ایسا نہیں کہ وہ بستیوں کو ناحق تباہ کردے، حالاں کہ اس کے باشندے اصلاح کرنے والے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(11,118,'11:118','اور اگر تیرا رب چاہتا تو لوگوں کو ایک ہی امت بنادیتا، مگر وہ ہمیشہ اختلاف میں رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(11,119,'11:119','سوا ان کے جن پر تیرا رب رحم فرمائے اور اس نے اسی ليے ان کو پیدا کیا ہے اور تیرے رب کی بات پوری ہوئی کہ میں جہنم کو جنوں اور انسانوں سے اكٹھے بھردوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(11,120,'11:120','اور ہم رسولوں کے احوال سے سب چیز تمہیں سنا رہے ہیں جس سے تمھارے دل کو مضبوط کریں اور اس میں تمھارے پاس حق آیا ہے اور مومنوں کے ليے نصیحت اور یاد دہانی');
INSERT INTO "ur_wahid_uddin" VALUES(11,121,'11:121','اور جو لوگ ایمان نہیں لائے، ان سے کہو کہ تم اپنے طریقے پرعمل کرتے رہو اور ہم اپنے طریقہ پرعمل کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,122,'11:122','اور انتظار کرو ہم بھی منتظر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(11,123,'11:123','اور آسمانوں اور زمین کی چھپی بات اللہ کے پاس ہے اور وہی تمام امور کا مرجع ہے پس تم اس کی عبادت کرو اور اسی پر بھروسہ رکھو اور تمھارا رب اس سے بے خبر نہیں جو تم کر رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(12,1,'12:1','الر، یہ واضح کتاب کی آیتیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,2,'12:2','ہم نے اس کو عربی قرآن بناکر اتارا ہے تاکہ تم سمجھو');
INSERT INTO "ur_wahid_uddin" VALUES(12,3,'12:3','ہم تم کو بہترین سرگزشت سناتے ہیں اس قرآن کی بدولت جو ہم نے تمھاری طرف وحی کیا اس سے پہلے بے شک تو بے خبروں میں تھا');
INSERT INTO "ur_wahid_uddin" VALUES(12,4,'12:4','جب یوسف نے اپنے باپ سے کہا کہ ابا جان، میں نے خواب میں گیارہ ستارے اور سورج اور چاند دیکھے ہیں میں نے ان کو دیکھا کہ وہ مجھ کو سجدہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,5,'12:5','اس کے باپ نے کہا کہ اے میرے بیٹے، تم اپنا یہ خواب اپنے بھائیوں کو نہ سنانا کہ وہ تمھارے خلاف کوئی سازش کرنے لگیں بے شک شیطان آدمی کا کھلا ہوا دشمن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,6,'12:6','اور اسی طرح تیرا رب تجھ کو منتخب کرے گا اور تم کو باتوں کی حقیقت تک پہنچنا سکھائے گا اور تم پر اور آل یعقوب پر اپنی نعمت پوری کرے گا جس طرح وہ اس سے پہلے تمھارے اجداد— ابراہیم اور اسحاق پر اپنی نعمت پوری کرچکا ہے یقیناً تیرا رب علیم اور حکیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,7,'12:7','حقیقت یہ ہے کہ یوسف اور اس کے بھائیوں میں پوچھنے والوں کے ليے بڑی نشانیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,8,'12:8','جب اس کے بھائیوں نے آپس میں کہا کہ یوسف اور اس کا بھائی ہمارے باپ کو ہم سے زیادہ محبوب ہیں حالاں کہ ہم ایک پورا جتھا ہیں یقیناً ہمارا باپ ایک کھلی ہوئی غلطی میں مبتلا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,9,'12:9','یوسف کو قتل کردو یا اس کو کسی جگہ پھینک دو تاکہ تمھارے باپ کی توجہ صرف تمھاری طرف ہوجائے اور اس کے بعد تم بالکل ٹھیک ہوجانا');
INSERT INTO "ur_wahid_uddin" VALUES(12,10,'12:10','ان میں سے ایک کہنے والے نے کہا کہ یوسف کو قتل نہ کرو اگر تم کچھ کرنے ہی والے ہو تو اس کو کسی اندھے کنوئیں میں ڈال دو کوئی راہ چلتا قافلہ اس کو نکال لے جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(12,11,'12:11','انھوں نے اپنے باپ سے کہا، اے ہمارے باپ، کیا بات ہے کہ آپ یوسف کے معاملہ میں ہم پر بھروسہ نہیں کرتے حالاں کہ ہم تو اس کے خیر خواہ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,12,'12:12','کل اس کو ہمارے ساتھ بھیج دیجيے، کھائے اور کھیلے، اور ہم اس کے نگہبان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,13,'12:13','باپ نے کہا، میں اس سے غمگین ہوتا ہوں کہ تم اس کو لے جاؤ اور مجھ کو اندیشہ ہے کہ اس کو کوئی بھیڑیا کھا جائے جب کہ تم اس سے غافل ہو');
INSERT INTO "ur_wahid_uddin" VALUES(12,14,'12:14','انھوں نے کہا کہ اگر اس کو بھیڑ یا کھا گیا جب کہ ہم ایک پوری جماعت ہیں ، تو ہم بڑے خسارے والے ثابت ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(12,15,'12:15','پھر جب وہ اس کو لے گئے اور یہ طے کرلیا کہ اس کو ایک اندھے کنویں میں ڈال دیں اور ہم نے یوسف کو وحی کی کہ تو ان کوان کا یہ کام جتائے گااور وہ تجھ کو نہ جانیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(12,16,'12:16','اور وہ شام کو اپنے باپ کے پاس روتے ہوئے آئے');
INSERT INTO "ur_wahid_uddin" VALUES(12,17,'12:17','انھوں نے کہا کہ اے ہمارے باپ، ہم دوڑ کا مقابلہ کرنے لگے اور یوسف کو ہم نے اپنے سامان کے پاس چھوڑ دیا پھر اس کو بھیڑیا کھا گیا اور آپ ہماری بات کا یقین نہ کریں گے، چاہے ہم سچے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(12,18,'12:18','اور وہ یوسف کی قمیص پر جھوٹا خون لگاکر لے آئے باپ نے کہا نہیں ، بلکہ تمھارے نفس نے تمھارے ليے ایک بات بنادی ہے اب صبر ہی بہتر ہے اور جو بات تم ظاہر کر رہے ہو، اس پر اللہ ہی سے مدد مانگتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(12,19,'12:19','اور ایک قافلہ آیا تو انھوں نے اپنا پانی بھرنے والا بھیجا اس نے اپنا ڈول لٹکا دیا اس نے کہا، خوش خبری ہو، یہ تو ایک لڑکا ہے اور اس کو تجارت کا مال سمجھ کر محفوظ کرلیا اور اللہ خوب جانتا تھا جو وہ کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(12,20,'12:20','اور انھوں نے اس کو تھوڑی سی قیمت، چند درہم کے عوض بیچ دیا اور وہ اس سے بے رغبت تھے');
INSERT INTO "ur_wahid_uddin" VALUES(12,21,'12:21','اور اہل مصر میں سے جس شخص نے اس کو خریدا، اس نے اپنی بیوی سے کہا کہ اس کو اچھی طرح رکھو امید ہے کہ وہ ہمارے ليے مفید ہو یا ہم اس کو بیٹا بنالیں اوراس طرح ہم نے یوسف کو اس ملک میں جگہ دی اور تاکہ ہم اس کو باتوں کی تاویل سکھائیں اور اللہ اپنے کام پر غالب رہتا ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(12,22,'12:22','اور جب وہ اپنی پختگی کو پہنچا ہم نے اس کو حکم اور علم عطا کیا اور نیکی کرنے والوں کو ہم ایسا ہی بدلہ دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,23,'12:23','اور یوسف جس عورت کے گھر میں تھا وہ اس کو پھسلانے لگی اور ایک روز اس نے دروازے بند کردئے اور بولی کہ آجا یوسف نے کہا خدا کی پناہ وہ میرا آقا ہے، اس نے مجھ کو اچھی طرح رکھا ہے بے شک ظالم لوگ کبھی فلاح نہیں پاتے');
INSERT INTO "ur_wahid_uddin" VALUES(12,24,'12:24','اور عورت نے اس کا ارادہ کرلیا اور وہ بھی اس کا ارادہ کرتا اگر وہ اپنے رب کی برہان نہ دیکھ لیتا ایسا ہوا تاکہ ہم اس سے برائی اور بے حیائی کو دور کردیں بے شک وہ ہمارے چنے ہوئے بندوں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(12,25,'12:25','اور دونوں دروازے کی طرف بھاگے اور عورت نے یوسف کا کرتا پیچھے سے پھاڑ دیا اور دونوں نے اس کے شوہر کو دروازے پر پایا عورت بولی کہ جو تیری گھر والی کے ساتھ برائی کا ارادہ کرے، اس کی سزا اس کے سوا کیا ہے کہ اسے قید کیا جائے یا اسے سخت عذاب دیا جائے');
INSERT INTO "ur_wahid_uddin" VALUES(12,26,'12:26','یوسف بولاکہ اسی نے مجھے پھسلانے کی کوشش کی اور عورت کے کنبہ والوں میں سے ایک شخص نے گواہی دی کہ اگر اس کا کرتا آگے سے پھٹا ہوا ہو تو عورت سچی ہے اور وہ جھوٹا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,27,'12:27','اور اگر اس کا کرتا پیچھے سے پھٹا ہوا ہو تو عورت جھوٹی ہے اور وہ سچا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,28,'12:28','پھر جب عزیز نے دیکھا کہ اس کا کرتا پیچھے سے پھٹا ہوا ہے تو اس نے کہا کہ بے شک یہ تم عورتوں کی چال ہے اور تمھاری چالیں بہت بڑی ہوتی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,29,'12:29','یوسف، اس سے درگزر کرو اور اے عورت تو اپنی غلطی کی معافی مانگ بے شک تو ہی خطا کار تھی');
INSERT INTO "ur_wahid_uddin" VALUES(12,30,'12:30','اور شہر کی عورتیں کہنے لگیں کہ عزیز کی بیوی اپنے نوجوان غلام کے پیچھے پڑی ہوئی ہے وہ اس کی محبت میں فریفتہ ہے ہم دیکھتے ہیں کہ وہ کھلی ہوئی غلطی پر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,31,'12:31','پھر جب اس نے ان کا فریب سنا تو اس نے ان کو بلابھیجا اور ان کے ليے ایک مجلس تیار کی اور ان میں سے ہر ایک کو ایک ایک چھری دی اور یوسف سے کہا کہ تم ان کے سامنے آؤ پھر جب عورتوں نے اس کو دیکھا تو وہ دنگ رہ گئیں اور انھوں نے اپنے ہاتھ کاٹ ڈالے اور انھوں نے کہا حاشاللہ، یہ آدمی نہیں ہے، یہ تو کوئی بزرگ فرشتہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,32,'12:32','اس نے کہا یہ وہی ہے جس کے بارے میں تم مجھ کو ملامت کر رہی تھیں اور میں نے اس کو رجھانے کی کوشش کی تھی مگر وہ بچ گیا اور اگر اس نے وہ نہیں کیا جو میں اس سے کہہ رہی ہوں تو وہ قید میں پڑے گا اور ضرور بے عزت ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(12,33,'12:33','یوسف نے کہا، اے میرے رب، قید خانہ مجھ کو اس چیز سے زیادہ پسند ہے جس کی طرف یہ مجھے بلارہی ہیں اور اگر تو نے ان کے فریب کو مجھ سے دفع نہ کیا تو میں ان کی طرف مائل ہوجاؤں گا اور جاہلوں میں سے ہوجاؤں گا');
INSERT INTO "ur_wahid_uddin" VALUES(12,34,'12:34','پس اس کے رب نے اس کی دعا قبول کرلی اور ان کے فریب کو اس سے دفع کردیا بے شک وہ سننے والا اور جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,35,'12:35','پھر نشانیاں دیکھ لینے کے بعد ان لوگوں کی سمجھ میں آیا کہ ایک مدت کے ليے اس کو قید کردیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,36,'12:36','اور قید خانہ میں اس کے ساتھ دو اور جو ان داخل ہوئے ان میں سے ایک نے (ایک روز) کہا کہ میں خواب میں دیکھتا ہوں کہ میں شراب نچوڑ رہا ہوں اور دوسرے نے کہا کہ میں خواب میں دیکھتا ہوں کہ میں اپنے سر پر روٹی اٹھائے ہوئے ہوں جس میں سے چڑیاں کھارہی ہیں ہم کو اس کی تعبیر بتاؤ ہم دیکھتے ہیں کہ تم نیک لوگوں میں سے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(12,37,'12:37','یوسف نے کہا، جو کھانا تم کو ملتا ہے، اس کے آنے سے پہلے میں تمھیں ان خوابوں کی تعبیر بتادوں گا یہ اس علم میں سے ہے جو میرے رب نے مجھے سکھایا ہے میں نے ان لوگوں کے مذہب کو چھوڑا جو اللہ پر ایمان نہیں لاتے اور وہ لوگ آخرت کے منکر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,38,'12:38','اور میں نے اپنے بزرگوں ابراہیم اور اسحاق اور یعقوب کے مذہب کی پیروی کی ہم کو یہ حق نہیں کہ ہم کسی چیز کو اللہ کا شریک ٹھہرائیں یہ اللہ کا فضل ہے ہمارے اوپر اور سب لوگوں کے اوپر مگر اکثر لوگ شکر نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(12,39,'12:39','اے میرے جیل کے ساتھیو، کیا جدا جدا کئی معبود بہتر ہیں یا اللہ اکیلا زبردست');
INSERT INTO "ur_wahid_uddin" VALUES(12,40,'12:40','تم اس کے سوا نہیں پوجتے ہو مگر کچھ ناموں کو جو تم نے اور تمھارے باپ دادا نے رکھ ليے ہیں اللہ نے اس کی کوئی سند نہیں اتاری اقتدار صرف اللہ کے ليے ہے اس نے حکم دیا ہے کہ اس کے سوا کسی کی عبادت نہ کرو یہی سیدھا دین ہے مگر بہت لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(12,41,'12:41','اے میرے قید خانہ کے ساتھیو، تم میں سے ایک اپنے آقا کو شراب پلائے گا اور جو دوسرا ہے اس کو سولی دی جائے گی پھر پرندے اس کے سر میں سے کھائیں گے اس امر کا فیصلہ ہوگیا جس کے بارے میں تم پوچھ رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(12,42,'12:42','اور یوسف نے اس شخص سے کہا جس کے بارے میں اس نے گمان کیا تھا کہ وہ بچ جائے گا کہ اپنے آقا کے پاس میرا ذکر کرنا پھر شیطان نے اس کو اپنے آقا سے ذکر کرنا بھلادیا پس وہ قید خانہ میں کئی سال پڑا رہا');
INSERT INTO "ur_wahid_uddin" VALUES(12,43,'12:43','اور بادشاہ نے کہا کہ میں خواب میں دیکھتا ہوں کہ سات موٹی گائیں ہیں جن کو سات دبلی گائیں کھا رہی ہیں اور سات ہری بالیاں ہیں اور دوسری سات سوکھی بالیاں ، اے دربار والو، میرے خواب کی تعبیر مجھے بتاؤ اگر تم خواب کی تعبیر دیتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(12,44,'12:44','وہ بولے یہ خیالی خواب ہیں اور ہم کو ایسے خوابوں کی تعبیر معلوم نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,45,'12:45','ان دو قیدیوں میں سے جو شخص بچ گیا تھا اور اس کو ایک مدت کے بعد یاد آیا، اس نے کہا کہ میں تم لوگوں کو اس کی تعبیر بتاؤں گا، پس مجھ کو (یوسف کے پاس) جانے دو');
INSERT INTO "ur_wahid_uddin" VALUES(12,46,'12:46','یوسف اے سچے، مجھے اس خواب کا مطلب بتاکہ سات موٹی گائیں ہیں جن کو سات دبلی گائیں کھا رہی ہیں اور سات بالیں ہری ہیں اور دوسری سات سوکھی تاکہ میں ان لوگوں کے پاس جاؤں تاکہ وہ جان لیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,47,'12:47','یوسف نے کہا کہ تم سات سال تک برابر کھیتی کرو گے پس جو فصل تم کاٹو، اس کو اس کی بالیوں میں چھوڑ دو مگر تھوڑا سا جو تم کھاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(12,48,'12:48','پر اس کے بعد سات سخت سال آئیں گے اس زمانہ میں وہ غلہ کھالیا جائے گا جو تم اس وقت کے ليے جمع کرو گے، بجز تھوڑا سا جو تم محفوظ کر لو گے');
INSERT INTO "ur_wahid_uddin" VALUES(12,49,'12:49','پھر اس کے بعد ایک سال آئے گا جس میں لوگوں پر مینھ برسے گا اور وہ اس میں رس نچوڑیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(12,50,'12:50','اور بادشاہ نے کہا کہ اس کو میرے پاس لاؤ پھر جب قاصد اس کے پاس آیا تو اس نے کہا کہ تم اپنے آقا کے پاس واپس جاؤ اور اس سے پوچھو کہ ان عورتوں کا کیا معاملہ ہے جنھوں نے اپنے ہاتھ کاٹ ليے تھے میرا رب تو ان کے فریب سے خوب واقف ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,51,'12:51','بادشاہ نے پوچھا، تمھارا کیا ماجرا ہے جب تم نے یوسف کو پھسلانے کی کوشش کی تھی انھوں نے کہا کہ حاشاللہ، ہم نے اس میں کچھ برائی نہیں پائی عزیز کی بیوی نے کہا اب حق کھل گیا میں نے ہی اس کو پھسلانے کی کوشش کی تھی اور بلاشبہ وہ سچا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,52,'12:52','یہ اس ليے کہ (عزیز مصر) یہ جان لے کہ میں نے در پردہ اس کی خیانت نہیں کی اور بے شک اللہ خیانت کرنے والوں کی چال کو چلنے نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(12,53,'12:53','اور میں اپنے نفس کی برأت نہیں کرتا نفس تو بدی ہی سکھاتا ہے، الا یہ کہ میرا رب رحم فرمائے بے شک میرا رب بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,54,'12:54','اور بادشاہ نے کہا، اس کو میرے پاس لاؤ میں اس کو خاص اپنے ليے رکھوں گاپھر جب یوسف نے اس سے بات کی تو بادشاہ نے کہا، آج سے تم ہمارے یہاں معزز اور معتمد ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(12,55,'12:55','یوسف نے کہا کہ مجھ کو ملک کے خزانوں پر مقرر کردو میں نگہبان ہوں اور جاننے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(12,56,'12:56','اور اس طرح ہم نے یوسف کو ملک میں با اختیار بنادیا وہ اس میں جہاں چاہے جگہ بنائے ہم جس پر چاہیں اپنی عنایت متوجہ کردیں اور ہم نیکی کرنے والوں کا اجر ضائع نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(12,57,'12:57','اور آخرت کا اجر کہیں زیادہ بڑھ کر ہے، ایمان اور تقویٰ والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(12,58,'12:58','اور یوسف کے بھائی مصر آئے پھر وہ اس کے پاس پہنچے، پس یوسف نے ان کو پہچان لیا اور انھوں نے یوسف کو نہیں پہچانا');
INSERT INTO "ur_wahid_uddin" VALUES(12,59,'12:59','اور جب اس نے ان کا سامان تیار کردیا تو کہا کہ اپنے سوتیلے بھائی کو بھی میرے پا س لے آنا تم دیکھتے نہیں ہو کہ میں غلہ بھی پورا ناپ کردیتا ہوں اور بہترین میزبانی کرنے والا بھی ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(12,60,'12:60','اور اگر تم اس کو میرے پاس نہ لائے تو نہ میرے پاس تمھارے ليے غلہ ہے اور نہ تم میرے پاس آنا');
INSERT INTO "ur_wahid_uddin" VALUES(12,61,'12:61','انھوں نے کہا کہ ہم اس کے بارے میں اس کے باپ کو راضی کرنے کی کوشش کریں گے اور ہم کو یہ کام کرنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,62,'12:62','اور اس نے اپنے کارندوں سے کہا کہ ان کا مال ان کے اسباب میں رکھ دو، تاکہ جب وہ اپنے گھر پہنچیں تو اس کو پہچان لیں ، شاید وہ پھر آئیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,63,'12:63','پھر جب وہ اپنے باپ کے پاس لوٹے تو کہا کہ اے باپ، ہم سے غلہ روک دیا گیا، پس ہمارے بھائی (بن یامین) کو ہمارے ساتھ جانے دے کہ ہم غلہ لائیں اور ہم اس کے نگہبان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,64,'12:64','یعقوب نے کہا، کیا میں اس کے بارے میں تمھارا ویسا ہی اعتبار کروں جیساکہ اس سے پہلے اس کے بھائی کے بارے میں تمھارا اعتبار کرچکا ہوں پس اللہ بہتر نگہبان ہے اور وہ سب مہربانوں سے زیادہ مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,65,'12:65','اور جب انھوں نے اپنا سامان کھولا تو دیکھا کہ ان کی پونجی بھی ان کو لوٹادی گئی ہے انھوں نے کہا، اے ہمارے باپ، اور ہم کو کیا چاہيے یہ ہماری پونجی بھی ہم کو لوٹادی گئی ہے اب ہم جائیں گے اور اپنے اہل و عیال کے ليے رسد لائیں گے اور اپنے بھائی کی حفاظت کریں گے اور ایک اونٹ کا بوجھ غلہ اور زیادہ لائیں گے یہ غلہ تو تھوڑا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,66,'12:66','یعقوب نے کہا، میں اس کو تمھارے ساتھ ہرگز نہ بھیجوں گا جب تک تم مجھ سے خدا کے نام پر یہ عہد نہ کرو کہ تم اس کو ضرور میرے پاس لے آؤ گے، الا یہ کہ تم سب گھِر جاؤ پھر جب انھوں نے اس کو اپنا پکا قول دے دیا، اس نے کہا کہ جو ہم کہہ رہے ہیں اس پر اللہ نگہبان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,67,'12:67','اور یعقوب نے کہا کہ اے میرے بیٹو، تم سب ایک ہی دروازے سے داخل نہ ہونا بلکہ الگ الگ دروازوں سے داخل ہونا اور میں تم کو اللہ کی کسی بات سے نہیں بچاسکتا حکم تو بس اللہ کا ہے میں اسی پر بھروسہ رکھتا ہوں اور بھروسہ کرنے والوں کو اسی پر بھروسہ کرنا چاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(12,68,'12:68','اور جب وہ داخل ہوئے جہاں سے ان کے باپ نے ان کو ہدایت کی تھی، وہ ان کو نہیں بچا سکتا تھا اللہ کی کسی بات سے وہ بس یعقوب کے دل میں ایک خیال تھا جو اس نے پورا کیا بے شک وہ ہماری دی ہوئی تعلیم سے صاحب علم تھا مگر اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(12,69,'12:69','اور جب وہ یوسف کے پاس پہنچے تو اس نے اپنے بھائی کو اپنے پاس رکھا کہا کہ میں تمھارا بھائی (یوسف) ہوں پس غمگین نہ ہو اس سے جو وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,70,'12:70','پھر جب ان کا سامان تیار کرادیا تو پینے کا پیالہ اپنے بھائی کے اسباب میں رکھ دیا پھر ایک پکارنے والے نے پکارا کہ اے قافلہ والو، تم لوگ چور ہو');
INSERT INTO "ur_wahid_uddin" VALUES(12,71,'12:71','انھوں نے ان کی طرف متوجہ ہوکر کہا، تمھاری کیا چیز کھوئی گئی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,72,'12:72','انھوں نے کہا، ہم شاہی پیمانہ نہیں پارہے ہیں اور جو اس کو لائے گا اس کے ليے ایک بار ِشُتر غلہ ہے اور میں اس کا ذمہ دار ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(12,73,'12:73','انھوں نے کہا، خدا کی قسم تم کو معلوم ہے کہ ہم لوگ اس ملک میں فساد کرنے کے ليے نہیں آئے اور نہ ہم کبھی چور تھے');
INSERT INTO "ur_wahid_uddin" VALUES(12,74,'12:74','انھوں نے کہا اگر تم جھوٹے نکلے تو اس چوری کرنے والے کی سزا کیاہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,75,'12:75','انھوں نے کہا، اس کی سزا یہ ہے کہ جس شخص کے اسباب میں وہ ملے پس وہی شخص اپنی سزا ہے ہم لوگ ظالموں کو ایسی ہی سزا دیا کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,76,'12:76','پھر اس نے اس کے (چھوٹے) بھائی سے پہلے ان کے تھیلوں کی تلاشی لینا شروع کیا پھر اس کے بھائی کے تھیلے سے اس کو برآمد کرلیا اس طرح ہم نے یوسف کے ليے تدبیر کی وہ بادشاہ کے قانون کی رو سے اپنے بھائی کو نہیں لے سکتا تھا مگر یہ کہ اللہ چاہے ہم جس کے درجے چاہتے ہیں بلند کردیتے ہیں اور ہر علم والے سے بالا تر ایک علم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,77,'12:77','انھوں نے کہا اگر یہ چوری کرے تو اس سے پہلے اس کا ایک بھائی بھی چوری کرچکا ہے پس یوسف نے اس بات کو اپنے دل میں رکھا، اور اس کو ان پر ظاہر نہیں کیا اس نے اپنے جی میں کہا، تم خود ہی برے لوگ ہو، اور جو کچھ تم بیان کر رہے ہو اللہ اس کو خوب جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,78,'12:78','انھوں نے کہا کہ اے عزیز، اس کا ایک بہت بوڑھا باپ ہے سو تو اس کی جگہ ہم میں سے کسی کو رکھ لےہم تجھ کو بہت نیک دیکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,79,'12:79','اس نے کہا، اللہ کی پناہ کہ ہم اس کے سوا کسی کو پکڑیں جس کے پاس ہم نے اپنی چیز پائی ہے اس صورت میں ہم ضرور ظالم ٹھہریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(12,80,'12:80','جب وہ اس سے نا امید ہوگئے تووہ الگ ہوکر باہم مشورہ کرنے لگے ان کے بڑے نے کہا کیا تم کو معلوم نہیں کہ تمھارے باپ نے اللہ کے نام پر پکا اقرار لیا اور اس سے پہلے یوسف کے معاملہ میں جو زیادتی تم کرچکے ہو، وہ بھی تم کو معلوم ہے پس میں اس زمین سے ہرگز نہیں ٹلوں گا جب تک میرا باپ مجھے اجازت نہ دے یا اللہ میرے ليے کوئی فیصلہ فرمادے اور وہ سب سے بہتر فیصلہ کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,81,'12:81','تم لوگ اپنے باپ کے پاس جاؤ اور کہو کہ اے ہمارے باپ، تیرے بیٹے نے چوری کی اور ہم وہی بات کہہ رہے ہیں جو ہم کو معلوم ہوئی اور ہم غیب کے نگہبان نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,82,'12:82','اور تو اس بستی کے لوگوں سے پوچھ لے جہاں ہم تھے اور اس قافلہ سے پوچھ لے جس کے ساتھ ہم آئے ہیں اور ہم بالکل سچے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,83,'12:83','باپ نے کہا، بلکہ تم نے اپنے دل سے ایک بات بنالی ہے، پس میں صبر کروں گا امید ہے کہ اللہ ان سب کو میرے پاس لائے گا وہ جاننے والا، حکیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,84,'12:84','اور اس نے رخ پھیرلیا اور کہا، ہائے یوسف، اور غم سے اس کی آنکھیں سفید پڑگئیں وہ گھُٹاگھُٹا رہنے لگا');
INSERT INTO "ur_wahid_uddin" VALUES(12,85,'12:85','انھوں نے کہا، خدا کی قسم، تو یوسف ہی کی یاد میں رہے گا یہاں تک کہ گھل جائے یا ہلاک ہوجائے');
INSERT INTO "ur_wahid_uddin" VALUES(12,86,'12:86','اس نے کہا، میں اپنی پریشانی اور اپنے غم کا شکوہ صرف اللہ سے کرتا ہوں اور میں اللہ کی طرف سے وہ باتیں جانتا ہوں جو تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(12,87,'12:87','اے میرے بیٹو، جاؤ یوسف اور اس کے بھائی کی تلاش کرو اور اللہ کی رحمت سے نا امید نہ ہو اللہ کی رحمت سے صرف منکر ہی ناامید ہوتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,88,'12:88','پھر جب وہ یوسف کے پاس پہنچے، انھوں نے کہا، اے عزیز، ہم کو اور ہمارے گھر والوں کو بڑی تکلیف پہنچ رہی ہے اور ہم تھوڑی پونجی لے کر آئے ہیں ، تو ہم کو پورا غلہ دے اور ہم کو صدقہ بھی دے بے شک اللہ صدقہ کرنے والوں کو اس کا بدلہ دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,89,'12:89','اس نے کہا، کیا تم کو خبر ہے کہ تم نے یوسف اور اس کے بھائی کے ساتھ کیا کیا جب کہ تم کو سمجھ نہ تھی');
INSERT INTO "ur_wahid_uddin" VALUES(12,90,'12:90','انھوں نے کہا، کیا سچ مچ تم ہی یوسف ہو اس نے کہا ہاں ، میں یوسف ہوں اور یہ میرا بھائی ہے اللہ نے ہم پر فضل فرمایا جو شخص ڈرتا ہے اور صبر کرتا ہے تو اللہ نیک کام کرنے والوں کا اجر ضائع نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(12,91,'12:91','بھائیوں نے کہا، خدا کی قسم، اللہ نے تم کو ہمارے اوپر فضیلت دی، اور بے شک ہم غلطی پر تھے');
INSERT INTO "ur_wahid_uddin" VALUES(12,92,'12:92','یوسف نے کہا، آج تم پر کوئی الزام نہیں ، اللہ تم کو معاف کرے اور وہ سب مہربانوں سے زیادہ مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,93,'12:93','تم میرا یہ کرتا لے جاؤ اور اس کو میرے باپ کے چہرے پر ڈال دو، اس کی بینائی پلٹ آئے گی اور تم اپنے گھر والوں کے ساتھ میرے پاس آجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(12,94,'12:94','اور جب قافلہ (مصر سے) چلا تو اس کے باپ نے (کنعان میں ) کہا کہ اگر تم مجھ کو بڑھاپے میں بہکی باتیں کرنے والا نہ سمجھو تو میں یوسف کی خوشبو پارہا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(12,95,'12:95','لوگوں نے کہا، خدا کی قسم، تم تو ابھی تک اپنے پرانے غلط خیال میں مبتلا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(12,96,'12:96','پس جب خوش خبری دینے والا آیا، اس نے کرتا یعقوب کے چہرے پر ڈال دیا، پس اس کی بینائی لوٹ آئی اس نے کہا، کیا میں نے تم سے نہیں کہا تھا کہ میں اللہ کی جانب سے وہ باتیں جانتا ہوں جو تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(12,97,'12:97','برادرانِ یوسف نے کہا، اے ہمارے باپ، ہمارے گناہوں کی معافی کی دعا کیجئے بے شک ہم خطاوار تھے');
INSERT INTO "ur_wahid_uddin" VALUES(12,98,'12:98','یعقوب نے کہا، میں اپنے رب سے تمھارے ليے مغفرت کی دعا کروں گا بے شک وہ بخشنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,99,'12:99','پس جب وہ سب یوسف کے پاس پہنچے تو اس نے اپنے والدین کو اپنے پاس بٹھایا اور کہا کہ مصر میں ان شاء اللہ امن چین سے رہو');
INSERT INTO "ur_wahid_uddin" VALUES(12,100,'12:100','اور اس نے اپنے والدین کو تخت پر بٹھایا اور سب اس کے ليے سجدے میں جھک گئے اور یوسف نے کہا اے باپ، یہ ہے میرے خواب کی تعبیر جو میں نے پہلے دیکھا تھا میرے رب نے اس کو سچا کردیا اور اس نے میرے ساتھ احسان کیا کہ اس نے مجھے قید سے نکالا اور تم سب کو دیہات سے یہاں لایا بعد اس کے کہ شیطان نے میرے اور میرے بھائیوں کے درمیان فساد ڈال دیا تھا بے شک میرا رب جو کچھ چاہتا ہے اس کی عمدہ تدبیر کرلیتا ہے، وہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(12,101,'12:101','اے میرے رب، تو نے مجھ کو حکومت میں سے حصہ دیا اور مجھ کو باتوں کی تعبیر کرنا سکھایا اے آسمانوں اور زمین کے پیدا کرنے والے، تو میرا کار ساز ہے، دنیامیں بھی اور آخرت میں بھی مجھ کو فرماں برداری کی حالت میں وفات دے اور مجھ کو نیک بندوں میں شامل فرما');
INSERT INTO "ur_wahid_uddin" VALUES(12,102,'12:102','یہ غیب کی خبروں میں سے ہے جو ہم تم پر وحی کر رہے ہیں اور تم اس وقت ان کے پاس موجود نہ تھے، جب یوسف کے بھائیوں نے اپنی رائے پختہ کی اور وہ تدبیریں کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(12,103,'12:103','اور تم خواہ کتنا ہی چاہو، اکثر لوگ ایمان لانے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,104,'12:104','اور تم اس پر ان سے کوئی معاوضہ نہیں مانگتے یہ تو صرف ایک نصیحت ہے تمام جہان والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(12,105,'12:105','اور آسمانوں اور زمین میں کتنی ہی نشانیاں ہیں جن پر ان کا گزر ہوتا رہتا ہے اور وہ ان پر دھیان نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(12,106,'12:106','اور اکثر لوگ جو خدا کو مانتے ہیں وہ اس کے ساتھ دوسروں کو شریک بھی ٹھہراتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,107,'12:107','کیا یہ لوگ اس بات سے مطمئن ہیں کہ ان پر عذاب الٰہی کی کوئی آفت آپڑے یا اچانک ان پر قیامت آجائے اور وہ اس سے بے خبر ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(12,108,'12:108','کہو، یہ میرا راستہ ہے، میں اللہ کی طرف بلاتا ہوں بصیرت کے ساتھ، میں بھی اور وہ لوگ بھی جنھوں نے میری پیروی کی ہے اور اللہ پاک ہے اور میں مشرکوں میں سے نہیں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(12,109,'12:109','اور ہم نے تم سے پہلے مختلف بستی والوں میں سے جتنے رسول بھیجے سب آدمی ہی تھے ہم ان کی طرف وحی کرتے تھے کیا یہ لوگ زمین میں چلے پھرے نہیں کہ دیکھتے کہ ان لوگوں کا انجام کیا ہوا جو ان سے پہلے تھے اور آخرت کا گھر ان لوگوں کے ليے بہتر ہے جو ڈرتے ہیں ، کیا تم سمجھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(12,110,'12:110','یہاں تک کہ جب پیغمبر مایوس ہوگئے اور وہ خیال کرنے لگے کہ ان سے جھوٹ کہا گیا تھا تو ان کو ہماری مدد آپہنچی پس نجات ملی جس کو ہم نے چاہا اور مجرم لوگوں سے ہمارا عذاب ٹالا نہیں جاسکتا');
INSERT INTO "ur_wahid_uddin" VALUES(12,111,'12:111','ان کے قصوں میں سمجھ دار لوگوں کے ليے بڑی عبرت ہے یہ کوئی گھڑی ہوئی بات نہیں ، بلکہ تصدیق ہے اس چیز کی جو اس سے پہلے موجود ہے اور تفصیل ہے ہر چیز کی اور ہدایت اور رحمت ہے ایمان والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(13,1,'13:1','المر، یہ کتاب الٰہی کی آیتیں ہیں اور جو کچھ تمھارے اوپر تمھارے رب کی طرف سے اترا ہے وہ حق ہے، مگر اکثر لوگ نہیں مانتے');
INSERT INTO "ur_wahid_uddin" VALUES(13,2,'13:2','اللہ ہی ہے جس نے آسمان کو بلند کیا بغیر ایسے ستون کے جو تمھیں نظر آئیں پھر وہ اپنے تخت پر متمکن ہوا اور اس نے سورج اور چاند کو ایک قانون کا پابند بنایا، ہر ایک ایک مقررہ وقت پر چلتاہے اللہ ہی ہر کام کا انتظام کرتا ہے وہ نشانیوں کو کھول کھول کر بیان کرتا ہے تاکہ تم اپنے رب سے ملنے کا یقین کرو');
INSERT INTO "ur_wahid_uddin" VALUES(13,3,'13:3','اور وہی ہے جس نے زمین کو پھیلایا اور اس میں پہاڑ اور ندیاں رکھ دیں اور ہر قسم کے پھلوں کے جوڑے اس میں پیدا کيے وہ رات کو دن پر اڑھا دیتا ہے بے شک ان چیزوں میں نشانیاں ہیں ان لوگوں کے ليے جو غور کریں');
INSERT INTO "ur_wahid_uddin" VALUES(13,4,'13:4','اور زمین میں پاس پاس مختلف قطعے ہیں اور انگوروں کے باغ ہیں اور کھیتی ہے اور کھجوریں ہیں ، ان میں سے کچھ اکہرے ہیں اور کچھ دہرے سب ایک ہی پانی سے سیراب ہوتے ہیں اور ہم ایک کو دوسرے پر پیداوار میں فوقیت دیتے ہیں بے شک ان میں نشانیاں ہیں ان لوگوں کے ليے جو غور کریں');
INSERT INTO "ur_wahid_uddin" VALUES(13,5,'13:5','اور اگر تم تعجب کرو تو تعجب کے قابل ان کا یہ قول ہے کہ— جب هم مٹی ہوجائیں گے تو کیا ہم نئے سرے سے پیدا کيے جائیں گے یہ وہ لوگ ہیں جنھوں نے اپنے رب کا انکار کیا اور یہ وہ لوگ ہیں جن کی گردنوں میں طوق پڑے ہوئے ہیں وہ آگ والے لوگ ہیں ، وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(13,6,'13:6','وہ بھلائی سے پہلے برائی کے ليے جلدی کر رہے ہیں حالاں کہ ان سے پہلے مثالیں گزر چکی ہیں اور تمھارا رب لوگوں کے ظلم کے باوجود ان کو معاف کرنے والا ہے اور بے شک تمھارا رب سخت سزا دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,7,'13:7','اور جن لوگوں نے انکار کیا، وہ کہتے ہیں کہ اس شخص پر اس کے رب کی طرف سے کوئی نشانی کیوں نہیں اتری تم تو صرف خبردار کردینے والے ہو اور ہر قوم کے ليے ایک راہ بتانے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,8,'13:8','اللہ جانتا ہے ہر مادہ کے حمل کو اور جو کچھ رحموں میں گھٹتا اور بڑھتا ہے اس کو بھی اور ہر چیز کا اس کے یہاں ایک اندازہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,9,'13:9','وہ پوشیدہ اور ظاہر کو جاننے والاہے، سب سے بڑا ہے، سب سے برتر');
INSERT INTO "ur_wahid_uddin" VALUES(13,10,'13:10','تم میں سے کوئی شخص چپکے سے بات کہے اور جو پکار کر کہے اور جو رات میں چھپا ہواہو اور جو دن میں چل رہا ہو، خدا کے ليے سب یکساں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(13,11,'13:11','ہر شخص کے آگے اور پیچھے اس کے نگراں ہیں جو اللہ کے حکم سے اس کی دیکھ بھال کر رہے ہیں بے شک اللہ کسی قوم کی حالت کو نہیں بدلتا جب تک کہ وہ اس کو نہ بدل ڈالیں جو ان کے جی میں ہے اور جب اللہ کسی قوم پر کوئی آفت لانا چاہتا ہے تو پھر اس کے ہٹنے کی کوئی صورت نہیں اور اللہ کے سوا اس کے مقابلہ میں کوئی ان کا مدد گار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(13,12,'13:12','وہی ہے جو تم کو بجلی دکھاتا ہے جس سے ڈر بھی پیدا ہوتا ہے اور امید بھیاور وہی ہے جو پانی سے لدے ہوئے بادل اٹھاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,13,'13:13','اور بجلی کی گرج اس کی حمد کے ساتھ اس کی پاکی بیان کرتی ہے اور فرشتے بھی اس کے خوف سے اور وہ بجلیاں بھیجتا ہے، پھر جس پر چاہے انھیں گرادیتا ہے اور وہ لوگ خدا کے باب میں جھگڑتے ہیں ، حالاں کہ وہ زبردست ہے، قوت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,14,'13:14','سچاپکارنا صرف خدا کے ليے ہے اور اس کے سوا جن کو لوگ پکارتے ہیں ، وہ ان کی اس سے زیادہ داد رسی نہیں کرسکتے جتنا پانی اس شخص کی کرتا ہے جو اپنے دونوں ہاتھ پانی کی طرف پھیلائے ہوئے ہو تاکہ وہ اس کے منہ تک پہنچ جائے اور وہ اس کے منہ تک پہنچنے والا نہیں اور منکرین کی پکار سب بے فائدہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,15,'13:15','اور آسمانوں اور زمین میں جو بھی ہیں سب خدا ہی کو سجدہ کرتے ہیں ، خوشی سے یا مجبوری سے اور ان کے سائے بھی صبح اور شام');
INSERT INTO "ur_wahid_uddin" VALUES(13,16,'13:16','کہو، آسمانوں اور زمین کا رب کون ہے کہہ دو کہ اللہ کہو کیا پھر بھی تم نے اس کے سوا ایسے مدد گار بنا رکھے ہیں جو خود اپنی ذات کے نفع اور نقصان کا بھی اختیا رنہیں رکھتے کہو، کیا اندھا اور آنکھوں والا دونوں برابر ہوسکتے ہیں ، یا کیا اندھیرا اور اجالا دونوں برابر ہوجائیں گے کیا انھوں نے خدا کے ایسے شریک ٹھہرائے ہیں جنھوں نے بھی پیدا کیا ہے جیسا کہ اللہ نے پیدا کیا، پھر پیدائش ان کی نظر میں مشتبہ ہوگئی کہو، اللہ ہر چیز کا پیدا کرنے والا ہے اور وہی ہے اکیلا، زبردست');
INSERT INTO "ur_wahid_uddin" VALUES(13,17,'13:17','اللہ نے آسمان سے پانی اتارا پھر نالے اپنی اپنی مقدار کے موافق بہہ نکلے پھر سیلاب نے ابھرتے جھاگ کو اٹھالیا اور اسی طرح کا جھاگ ان چیزوں میں بھی ابھر آتا ہے جن کو لوگ زیور یا اسباب بنانے کے ليے آگ میں پگھلاتے ہیں اس طرح اللہ حق اور باطل کی مثال بیان کرتا ہے پس جھاگ تو سوکھ کر جاتا رہتا ہے اور جو چیز انسانوں کو نفع پہنچانے والی ہے، وہ زمین میں ٹھہر جاتی ہے اللہ اسی طرح مثالیں بیان کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,18,'13:18','جن لوگوں نے اپنے رب کی پکار کو لبیک کہا، ان کے ليے بھلائی ہے اور جن لوگوں نے اس کی پکار کو نہ مانا، اگر ان کے پاس وہ سب کچھ ہو جو زمین میں ہے، اور اس کے برابر اور بھی تو وہ سب اپنی رہائی کے ليے دے ڈالیں ان لوگوں کاحساب سخت ہوگا اور ان کا ٹھکانا جہنم ہوگا اور وہ کیسا برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,19,'13:19','جو شخص یہ جانتا ہے کہ جو کچھ تمھارے رب کی طرف سے اتارا گیا ہے وہ حق ہے، کیا وہ اس کے مانند ہوسکتا ہے جو اندھا ہے نصیحت تو عقل والے لوگ ہی قبول کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(13,20,'13:20','وہ لوگ جو اللہ کے عہد کو پورا کرتے ہیں اور اس کے عہد کو نہیں توڑتے');
INSERT INTO "ur_wahid_uddin" VALUES(13,21,'13:21','اور جو اس کو جوڑتے ہیں جس کو اللہ نے جوڑنے کا حکم دیا ہے اور وہ اپنے رب سے ڈرتے ہیں اور وہ برے حساب کا اندیشہ رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(13,22,'13:22','اور جنھوں نے اپنے رب کی رضا کے ليے صبر کیا اور نماز قائم کی اور ہمارے دئے میں سے پوشیدہ اور علانیہ خرچ کیا اور جو برائی کو بھلائی سے دفع کرتے ہیں آخرت کا گھر انھیں لوگوں کے ليے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,23,'13:23','ابدی باغ جن میں وہ داخل ہوں گے اور وہ بھی جو اس کے اہل بنیں ، ان کے آباء و اجداد اور ان کی بیویوں اور ان کی اولاد میں سے اور فرشتے ہر دروازے سے ان کے پاس آئیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(13,24,'13:24','کہیں گے تم لوگوں پر سلامتی ہو اس صبر کے بدلے جو تم نے کیا پس کیا ہی خوب ہے یہ آخرت کا گھر');
INSERT INTO "ur_wahid_uddin" VALUES(13,25,'13:25','اور جو لوگ اللہ کے عہد کو مضبوط کرنے کے بعد توڑتے ہیں اور اس کو کاٹتے ہیں جس کو اللہ نے جوڑنے کا حکم دیا ہے اور زمین میں فساد کرتے ہیں ، ایسے لوگوں پر لعنت ہے اور ان کے ليے برا گھر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,26,'13:26','اللہ جس کو چاہتا ہے روزی زیادہ دیتا ہے اور جس کے ليے چاہتا ہے تنگ کردیتا ہے اور وہ دنیا کی زندگی پر خوش ہیں اور دنیا کی زندگی آخرت کے مقابلہ میں ایک متاعِ قلیل کے سوا اور کچھ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(13,27,'13:27','اور جنھوں نے انکار کیا، وہ کہتے ہیں کہ اس شخص پر اس کے رب کی طرف سے کوئی نشانی کیوں نہیں اتاری گئی کہو کہ اللہ جس کو چاہتا ہے گمراہ کرتا ہے اور وہ اپنا راستہ اس کو دکھاتا ہے جو اس کی طرف متوجہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(13,28,'13:28','وہ لوگ جو ایمان لائے اور جن کے دل اللہ کی یاد سے مطمئن ہوتے ہیں سنو، اللہ کی یاد ہی سے دلوں کو اطمینان حاصل ہوتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,29,'13:29','جو لوگ ایمان لائے اور جنھوں نے اچھے کام کيے، ان کے ليے خوش خبری ہے اور اچھا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,30,'13:30','اسی طرح ہم نے تم کو بھیجا ہے، ایک امت میں جس سے پہلے بہت سی امتیں گزر چکی ہیں ، تاکہ تم لوگوں کو وہ پیغام سنا دو جو ہم نے تمھاری طرف بھیجا ہے اور وہ مہربان خدا کا انکار کر رہے ہیں کہو کہ وہی میرا رب ہے، اس کے سوا کوئی معبود نہیں ، اسی پر میں نے بھروسہ کیا اور اسی کی طرف لوٹنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,31,'13:31','اور اگر ایسا قرآن اترتا جس سے پہاڑ چلنے لگتے، یا اس سے زمین ٹکڑے ہوجاتی یا اس سے مردے بولنے لگتے بلکہ سارا اختیار اللہ ہی کے ليے ہے کیا ایمان لانے والوں کو اس سے اطمینان نہیں کہ اگر اللہ چاہتا تو سارے لوگوں کو ہدایت دے دیتا اور انکار کرنے والوں پر کوئی نہ کوئی آفت آتی رہتی ہے، ان کے اعمال کے سبب سے، یا ان کی بستی کے قریب کہیں نازل ہوتی رهتي هے، یہاں تک کہ اللہ کا وعدہ آجائے یقیناً اللہ وعدہ کے خلاف نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(13,32,'13:32','اور تم سے پہلے بھی رسولوں کا مذاق اڑایا گیا تو میں نے انکار کرنے والوں کو ڈھیل دی، پھر میں نے ان کو پکڑ لیا تو دیکھو کیسی تھی میری سزا');
INSERT INTO "ur_wahid_uddin" VALUES(13,33,'13:33','پھر کیا جوہر شخص سے اس کے عمل کا حساب کرنے والا ہے (اور وہ جو کسی چیز پر قدرت نہیں رکھتے، یکساں ہیں )، اور لوگوں نے اللہ کے شریک بناليے ہیں کہو کہ ان کا نام لو کیا تم اللہ کو ایسی چیز کی خبر دے رہے ہو جس کو وہ زمین میں نہیں جانتا یا تم اوپر ہی اوپر باتیں کر رہے ہو، بلکہ انکار کرنے والوں کو ان کا فریب خوشنما بنادیا گیا ہے اور وہ راستہ سے روک دئے گئے ہیں اور اللہ جس کوگمراہ کرے اس کو کوئی راہ بتانے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(13,34,'13:34','ان کے ليے دنیا کی زندگی میں بھی عذاب ہے اور آخرت کا عذاب تو بہت سخت ہے کوئی ان کو اللہ سے بچانے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(13,35,'13:35','اور جنت کی مثال جس کا متقیوں سے وعدہ کیا گیا ہے، یہ ہے کہ اس کے نیچے نہرے بہتی ہوں گی اس کا پھل اور سایہ ہمیشہ رہے گا یہ انجام ان لوگوں کا ہے جو خدا سے ڈرے اور منکروں کا انجام آگ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,36,'13:36','اور جن لوگوں کو ہم نے کتاب دی تھی، وہ اس چیز پر خوش ہیں جو تم پر اتاری گئی ہے اور ان گروہوں میں ایسے بھی ہیں جو اس کے بعض حصہ کا انکار کرتے ہیں کہو کہ مجھے حکم دیا گیا ہے کہ میں اللہ کی عبادت کروں اور کسی کو اس کا شریک نہ ٹھہراؤں میں اسی کی طرف بلاتا ہوں اور اسی کی طرف میرا لوٹنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,37,'13:37','اور اسی طرح ہم نے اس کو ایک حکم کی حیثیت سے عربی میں اتارا ہے اور اگر تم ان کی خواہشوں کی پیروی کرو بعد اس کے کہ تمھارے پاس علم آچکا ہے تو خدا کے مقابلہ میں تمھارا نہ کوئی مدد گار ہو گا اور نہ کوئی بچانے والا');
INSERT INTO "ur_wahid_uddin" VALUES(13,38,'13:38','اور ہم نے تم سے پہلے کتنے رسول بھیجے اور ہم نے ان کو بیویاں اور اولاد عطا کیا اور کسی رسول کے ليے یہ ممکن نہیں کہ وہ اللہ کی اِذن کے بغیر کوئی نشانی لے آئے ہر ایک وعدہ لکھا ہوا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,39,'13:39','اللہ جس کو چاہے مٹاتا ہے اور جس کو چاہے باقی رکھتا ہے اور اسی کے پاس ہے اصل کتاب');
INSERT INTO "ur_wahid_uddin" VALUES(13,40,'13:40','اور جس کا ہم ان سے عہد کر رہے ہیں اس کا کچھ حصہ ہم تم کو دکھادیں یا ہم تم کو وفات دے دیں ، پس تمھارے اوپر صرف پہنچا دینا ہے اور ہمارے اوپر ہے حساب لینا');
INSERT INTO "ur_wahid_uddin" VALUES(13,41,'13:41','کیا وہ دیکھتے نہیں کہ ہم زمین کو اس کے اطراف سے کم کرتے چلے آرہے ہیں اور اللہ فیصلہ کرتا ہے، کوئی اس کے فیصلہ کو ہٹانے والا نہیں اور وہ جلد حساب لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,42,'13:42','جو ان سے پہلے تھے انھوں نے بھی تدبیریں کیں مگر تمام تدبیریں اللہ کے اختیار میں ہیں وہ جانتا ہے کہ ہر ایک کیا کر رہا ہے اور منکرین جلد جان لیں گے کہ آخرت کا گھر کس کے ليے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(13,43,'13:43','اور منکرین کہتے ہیں کہ تم خدا کے بھیجے ہوئے نہیں ہو، کہو کہ میرے اور تمھارے درمیان اللہ کی گواہی کافی ہے اور اس کی گواہی جس کے پاس کتاب کاعلم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,1,'14:1','الرٰ یہ کتاب ہے جس کو ہم نے تمھاری طرف نازل کیا ہے، تاکہ تم لوگوں کو اندھیروں سے نکال کر اجالے کی طرف لاؤ، ان کے رب کے حکم سے خدائے عزیز اور حمید کے راستے کی طرف');
INSERT INTO "ur_wahid_uddin" VALUES(14,2,'14:2','اس اللہ کی طرف کہ آسمانوں اور زمین میں جو کچھ ہے سب اسی کا ہے اور منکروں کے ليے ایک عذاب شدید کی تباہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,3,'14:3','جو کہ آخرت کے مقابلہ میں دنیا کی زندگی کو پسند کرتے ہیں اور اللہ کے راستے سے روکتے ہیں اور اس میں کجی نکالنا چاہتے ہیں یہ لوگ راستہ سے بھٹک کر دور جا پڑے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(14,4,'14:4','اور ہم نے جو پیغمبر بھی بھیجا اس کی قوم کی زبان میں بھیجا، تاکہ وہ ان سے بیان کردے پھر اللہ جس کو چاہتا ہے بھٹکادیتا ہے اور جس کو چاہتا ہے ہدایت دیتا ہے وہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,5,'14:5','اور ہم نے موسیٰ کو اپنی نشانیوں کے ساتھ بھیجا کہ اپنی قوم کو اندھیروں سے نکال کر اجالے میں لاؤ اور ان کو اللہ کے دنوں کی یاد دلاؤ بے شک ان کے اندر بڑی نشانیاں ہیں ہر اس شخص کے ليے جو صبر اور شکر کرنے والا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(14,6,'14:6','اور جب موسیٰ نے اپنی قوم سے کہا کہ اپنے اوپر اللہ کے اس انعام کو یاد کرو جب کہ اس نے تم کو فرعون کی قوم سے چھڑایا جو تم کو سخت تکلیفیں پہنچاتے تھے اور جو تمھارے لڑکوں کو مارڈالتے تھے اور تمھاری عورتوں کو زندہ رکھتے تھے اور ا س میں تمھارے رب کی طرف سے بڑا امتحان تھا');
INSERT INTO "ur_wahid_uddin" VALUES(14,7,'14:7','اور جب تمھارے رب نے تم کو آگاہ کردیا کہ اگر تم شکر کرو گے تو میں تم کو زیادہ دوں گا اور اگر تم ناشکری کرو گے تو میراعذاب بڑا سخت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,8,'14:8','اور موسیٰ نے کہا کہ اگر تم انکار کرو اور زمین کے سارے لوگ بھی منکر ہوجائیں تو اللہ بے پروا ہے، خوبیوں والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,9,'14:9','کیا تم کو ان لوگوں کی خبر نہیں پہنچی جو تم سے پہلے گزر چکے ہیں ، قوم نوح اور عاد اور ثمود اور جو لوگ ان کے بعدہوئے ہیں ، جن کو خدا کے سوا کوئی نہیں جانتا ان کے پیغمبر ان کے پاس دلائل لے کر آئے تو انھوں نے اپنے ہاتھ اپنے منه پر ركھ ديے اور کہا کہ جو تم کو دے کر بھیجا گیا ہے ہم اس کو نہیں مانتے اور جس چیزکی طرف تم ہم کو بلاتے ہو ہم اس کے بارے میں سخت الجھن والے شک میں پڑے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(14,10,'14:10','ان کے پیغمبروں نے کہا، کیا خدا کے بارے میں شک ہے جو آسمانوں اور زمین کو وجود میں لانے والا ہے وہ تم کو بلا رہا ہے کہ تمھارے گناہ معاف کردے اور تم کو ایک مقرر مدت تک مہلت دے انھوں نے کہا کہ تم اس کے سوا کچھ نہیں کہ ہمارے جیسے ایک آدمی ہو تم چاہتے ہو کہ ہم کو ان چیزوں کی عبادت سے روک دو جن کی عبادت ہمارے باپ دادا کرتے تھے تم ہمارے سامنے کوئی کھلی سند لے آؤ');
INSERT INTO "ur_wahid_uddin" VALUES(14,11,'14:11','ان کے رسولوں نے ان سے کہا، ہم اس کے سوا کچھ نہیں کہ تمھارے ہی جیسے انسان ہیں مگر اللہ اپنے بندوں میں سے جس پر چاہتا ہے اپنا انعام فرماتا ہے اور یہ ہمارے اختیار میں نہیں کہ ہم تم کو کوئی معجزہ دکھائیں بغیر خدا کے حکم کے اور ایمان والوں کو اللہ ہی پر بھروسہ کرنا چاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(14,12,'14:12','اور ہم کیوں نہ اللہ پر بھروسہ کریں جب کہ اس نے ہم کو ہمارے راستے بتائے اور جو تکلیف تم ہمیں دو گے ہم اس پر صبرہی کریں گے اور بھروسہ کرنے والوں کو اللہ ہی پر بھروسہ کرنا چاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(14,13,'14:13','اور انکار کرنے والوں نے اپنے پیغمبروں سے کہا کہ یا تو ہم تم کو اپنی زمین سے نکال دیں گے یا تم کو ہماری ملت میں واپس آنا ہوگا تو پیغمبروں کے رب نے ان پر وحی بھیجی کہ ہم ان ظالموں کو ہلاک کردیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(14,14,'14:14','اور ان کے بعد تم کو زمین پر بسائیں گے یہ اس شخص کے ليے ہے جو میرے سامنے کھڑا ہونے سے ڈرے اور جو میری وعید سے ڈرے');
INSERT INTO "ur_wahid_uddin" VALUES(14,15,'14:15','اور انھوں نے فیصلہ چاہا اور ہر سرکش، ضدی، نامراد ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(14,16,'14:16','اس کے آگے دوزح ہے اور اس کو پیپ کا پانی پینے کو ملے گا');
INSERT INTO "ur_wahid_uddin" VALUES(14,17,'14:17','وہ اس کو گھونٹ گھونٹ پئے گا اور اس کو حلق سے مشکل سے اتار سکے گا موت ہر طرف سے اس پر چھائی ہوئی ہوگی، مگر وہ کسی طرح نہیں مرے گا اور اس کے آگے سخت عذاب ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(14,18,'14:18','جن لوگوں نے اپنے رب کاانکار کیا، ان کے اعمال اس راکھ کی طرح ہیں جس کو ایک طوفانی دن کی آندھی نے اڑادیا ہو وہ اپنے کيے میں سے کچھ بھی نہ پاسکیں گے یہی دور کی گمراہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,19,'14:19','کیا تم نے نہیں دیکھا کہ اللہ نے آسمانوں اور زمین کو بالکل ٹھیک ٹھیک پیدا کیا ہے اگر وہ چاہے تو تم لوگوں کو لے جائے اور ایک نئی مخلوق لے آئے');
INSERT INTO "ur_wahid_uddin" VALUES(14,20,'14:20','اور یہ خدا پر کچھ بھي دشوار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(14,21,'14:21','اور خدا کے سامنے سب پیش ہوں گے پھر کمزور لوگ ان لوگوں سے کہیں گے جو بڑائی والے تھے، ہم تمھارے تابع تھے تو کیا تم اللہ کے عذاب سے کچھ ہم کو بچاؤ گے وہ کہیں گے کہ اگر اللہ ہم کو کوئی راہ دکھاتا تو ہم تم کو بھی ضرور وہ راہ دکھادیتے اب ہمارے ليے یکساں ہے کہ ہم بے قرار ہوں یا صبر کریں ، ہمارے بچنے کی کوئی صورت نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(14,22,'14:22','اور جب معاملہ کا فیصلہ ہوجائے گا تو شیطان کہے گا کہ اللہ نے تم سے سچا وعدہ کیا تھا اور میں نے تم سے وعدہ کیا تو میں نے اس کی خلاف ورزی کی اور میرا تمھارے اوپر کوئی زور نہ تھا، مگر یہ کہ میں نے تم کو بلایا تو تم نے میری بات کو مان لیا، پس تم مجھ کو الزام نہ دو، اور تم اپنے آپ کو الزام دو نہ میں تمھارا مدد گار ہوسکتا ہوں اور نہ تم میرے مدد گار ہوسکتے ہو میں خود اِس سے بیزار ہوں کہ تم اس سے پہلے مجھ کو شریک ٹھہراتے تھے بے شک ظالموں کے ليے درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,23,'14:23','اور جو لوگ ایمان لائے اور جنھوں نے نیک عمل کيے وہ ایسے باغوں میں داخل کيے جائیں گے جن کے نیچے نہریں بہتی ہوں گی ان میں وہ اپنے رب کے حکم سے ہمیشہ رہیں گے اس میں ان کی ملاقات ایک دوسرے پر سلامتی ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(14,24,'14:24','کیا تم نے نہیں دیکھا، کس طرح مثال بیان فرمائی اللہ نے کلمۂ طیبہ کی وہ ایک پاکیزہ درخت کی مانند ہے، جس کی جڑ زمین میں جمی ہوئی ہے اور جس کی شاخیں آسمان تک پہنچی ہوئی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(14,25,'14:25','وہ ہر وقت پراپنا پھل دیتا ہے اپنے رب کے حکم سے اور اللہ لوگوں کے ليے مثال بیان کرتا ہے تاکہ وہ نصیحت حاصل کریں');
INSERT INTO "ur_wahid_uddin" VALUES(14,26,'14:26','اور کلمۂ خبیثہ کی مثال ایک خراب درخت کی ہے جو زمین کے اوپر ہی سے اکھاڑ لیا جائے اس کو کوئی ثبات نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(14,27,'14:27','اللہ ایمان والوں کو ایک پکی بات سے دنیا اور آخرت میں مضبوط کرتا ہے اور اللہ ظالموں کو بھٹکا دیتاہے اور اللہ کرتا ہے جو وہ چاہتاہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,28,'14:28','کیا تم نے ان لوگوں کو نہیں دیکھا جنھوں نے اللہ کی نعمت کے بدلے کفرکیا اور جنھوں نے اپنی قوم کو ہلاکت کے گھر میں پہنچادیا');
INSERT INTO "ur_wahid_uddin" VALUES(14,29,'14:29','جهنم، وہ اس میں داخل ہوں گے اور وہ کیسا برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,30,'14:30','اور انھوں نے اللہ کے مقابل ٹھہرائے، تاکہ وہ لوگوں کو اللہ کے راستے سے بھٹکادیں کہو کہ چند دن فائدہ اٹھالو، آخر کار تمھارا ٹھکانا دوزخ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,31,'14:31','میرے جو بندے ایمان لائے ہیں ، ان سے کہہ دو کہ وہ نماز قائم کریں اور جو کچھ ہم نے ان کو دیا ہے اس میں سے کھلے اور چھپے خرچ کریں ، قبل اس کے کہ وہ دن آئے جس میں نہ خرید و فروخت ہوگی اور نہ دوستی کام آئے گی');
INSERT INTO "ur_wahid_uddin" VALUES(14,32,'14:32','اللہ وہ ہے جس نے آسمان اور زمین بنائے اور آسمان سے پانی اتارا پھر اس سے مختلف پھل نکالے تمھاری روزی کے ليے اور کشتی کو تمھارے ليے مسخر کردیا کہ سمندر میں اس کے حکم سے چلے اور اس نے دریاؤں کو تمھارے ليے مسخر کیا');
INSERT INTO "ur_wahid_uddin" VALUES(14,33,'14:33','اور اس نے سورج اور چاند کو تمھارے ليے مسخر کردیا كه وہ برابر چلے جارہے ہیں اور اس نے رات اور دن کو تمھارے ليے مسخر کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(14,34,'14:34','اور اس نے تم کو ہر چیز میں سے دیا جو تم نے مانگا اگر تم اللہ کی نعمتوں کو گنو تو تم گن نہیں سکتے بے شک انسان بہت بے انصاف اور بڑا ناشکرا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,35,'14:35','اور جب ابراہیم نے کہا، اے میرے رب، اس شہر کو امن والا بنا اور مجھ کو اور میری اولاد کو اس سے دور رکھ کہ ہم بتوں کی عبادت کریں');
INSERT INTO "ur_wahid_uddin" VALUES(14,36,'14:36','اے میرے رب، ان بتوں نے بہت لوگوں کو گمراہ کردیا پس جس نے میری پیروی کی وہ میرا ہے اور جس نے میرا کہا نہ مانا تو تو بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,37,'14:37','اے ہمارے رب، میں نے اپنی اولاد کو ایک بے کھیتی کی وادی میں تیرے محترم گھر کے پاس بسایا ہے اے ہمارے رب، تاکہ وہ نماز قائم کریں پس تو لوگوں کے دل ان کی طرف مائل کردے اور ان کو پھلوں کی روزی عطا فرما، تاکہ وہ شکر کریں');
INSERT INTO "ur_wahid_uddin" VALUES(14,38,'14:38','اے ہمارے رب، تو جانتا ہے جو کچھ ہم چھپاتے ہیں اور جو کچھ ہم ظاہر کرتے ہیں اور اللہ سے کوئی چیز مخفی نہیں ، نہ زمین میں اور نہ آسمان میں');
INSERT INTO "ur_wahid_uddin" VALUES(14,39,'14:39','شکر ہے اس خدا کا جس نے مجھ کو بڑھاپے میں اسماعیل اور اسحاق دئے بے شک میرا رب دعا کا سننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,40,'14:40','اے میرے رب، مجھے نماز قائم کرنے والا بنا، اور میری اولاد میں بھی اے میرے رب، میری دعا قبول کر');
INSERT INTO "ur_wahid_uddin" VALUES(14,41,'14:41','اے ہمارے رب، مجھے معاف فرما اور میرے والدین کو اور مومنین کو، اس روز جب کہ حساب قائم ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(14,42,'14:42','اور ہرگز مت خیال کرو کہ اللہ اس سے بے خبر ہے جو ظالم لوگ کر رہے ہیں وہ ان کو اس دن کے ليے ڈھیل دے رہا ہے جس دن آنکھیں پتھرا جائیں گی');
INSERT INTO "ur_wahid_uddin" VALUES(14,43,'14:43','وہ سر اٹھائے ہوئے بھاگ رہے ہوں گے ان کی نظر ان کی طرف ہٹ کر نہ آئے گی اور ان کے دل بدحواس ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(14,44,'14:44','اور لوگوں کو اس دن سے ڈرادو جس دن ان پر عذاب آجائے گا اس وقت ظالم لوگ کہیں گے کہ اے ہمارے رب، ہم کو تھوڑی مہلت اور دے دے، ہم تیری دعوت قبول کرلیں گے اور رسولوں کی پیروی کریں گے کیا تم نے اس سے پہلے قسمیں نہیں کھائی تھیں کہ تم پر کچھ زوال آنا نہیں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,45,'14:45','اور تم ان لوگوں کی بستیوں میں آباد تھے جنھوں نے اپنی جانوں پر ظلم کیا اور تم پر کھل چکا تھا کہ ہم نے ان کے ساتھ کیا کیا اور ہم نے تم سے مثالیں بیان کیں');
INSERT INTO "ur_wahid_uddin" VALUES(14,46,'14:46','اور انھوں نے اپنی ساری تدبیریں کیں اوران کی تدبیریں اللہ کے سامنے تھیں اگرچہ ان کی تدبیریں ایسی تھیں کہ ان سے پہاڑبھی ٹل جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(14,47,'14:47','پس تم اللہ کو اپنے پیغمبروں سے وعدہ خلافی کرنے والا نہ سمجھو بے شک اللہ زبردست ہے، بدلہ لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,48,'14:48','جس دن یہ زمین دوسری زمین سے بدل جائے گی اور آسمان بھی اور سب ایک زبردست اللہ کے سامنے پیش ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(14,49,'14:49','اور تم اس دن مجرموں کو زنجیروں میں جکڑا ہوا دیکھو گے');
INSERT INTO "ur_wahid_uddin" VALUES(14,50,'14:50','ان کے لباس تارکول کے ہوں گے اور ان کے چہروں پر آگ چھائی ہوئی ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(14,51,'14:51','تاکہ اللہ ہر شخص کو اس کے کيے کا بدلہ دے بے شک اللہ جلد حساب لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(14,52,'14:52','یہ لوگوں کے ليے ایک اعلان ہے اور تاکہ اس کے ذریعہ سے وہ ڈرادئے جائیں اور تاکہ وہ جان لیں کہ وہی ایک معبود ہے اور تاکہ دانش مند لوگ نصیحت حاصل کریں');
INSERT INTO "ur_wahid_uddin" VALUES(15,1,'15:1','الریہ آیتیں ہیں کتاب کی اور ایک واضح قرآن کی');
INSERT INTO "ur_wahid_uddin" VALUES(15,2,'15:2','وہ وقت آئے گا جب انکار کرنے والے لوگ تمنا کریں گے کہ کاش وہ ماننے والے بنے ہوتے');
INSERT INTO "ur_wahid_uddin" VALUES(15,3,'15:3','ان کو چھوڑو کہ وہ کھائیں اور فائدہ اٹھائیں اور خیالی امید ان کو بُھلاوے میں ڈالے رکھے، پس آئندہ وہ جان لیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(15,4,'15:4','اور ہم نے اس سے پہلے جس بستی کو بھی ہلاک کیا ہے اس کا ایک مقرر وقت لکھا ہوا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(15,5,'15:5','کوئی قوم نہ اپنے مقرر وقت سے آگے بڑھتی اور نہ پیچھے ہٹتی هے');
INSERT INTO "ur_wahid_uddin" VALUES(15,6,'15:6','اور یہ لوگ کہتے ہیں کہ اے وہ شخص جس پر نصیحت اتری ہے تو بے شک دیوانہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,7,'15:7','اگر تو سچا ہے تو ہمارے پاس فرشتوں کوکیوں نہیں لے آتا');
INSERT INTO "ur_wahid_uddin" VALUES(15,8,'15:8','ہم فرشتوں کو صرف فیصلہ کے ليے اتارتے ہیں اور اس وقت لوگوں کو مہلت نہیں دی جاتی');
INSERT INTO "ur_wahid_uddin" VALUES(15,9,'15:9','بے شك یہ یاد دہانی (کتاب) ہم ہی نے اتاری ہے اور ہم ہی اس کے محافظ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,10,'15:10','اور ہم تم سے پہلے گزری ہوئی قوموں میں رسول بھیج چکے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,11,'15:11','اور جو رسول بھی ان کے پاس آیا وہ اس کا استہزاء کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,12,'15:12','اسی طرح ہم یہ (استہزاء) مجرمین کے دلوں میں ڈال دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,13,'15:13','وہ اس پر ایمان نہیں لائیں گے اور یہ دستور اگلوں سے ہوتا آیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,14,'15:14','اور اگر ہم ان پر آسمان کا کوئی دروازہ کھول دیتے جس پر وہ چڑھنے لگتے');
INSERT INTO "ur_wahid_uddin" VALUES(15,15,'15:15','تب بھی وہ کہہ دیتے کہ ہماری آنکھوں کو دھوکہ ہو رہا ہے، بلکہ ہم پر جادو کردیا گیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,16,'15:16','اور ہم نے آسمان میں برج بنائے اور دیکھنے والو ں کے ليے اسے رونق دی');
INSERT INTO "ur_wahid_uddin" VALUES(15,17,'15:17','اور اس کو ہر شیطان مردود سے محفوظ کیا');
INSERT INTO "ur_wahid_uddin" VALUES(15,18,'15:18','اگر کوئی چوری چھپے سننے کے ليے کان لگاتا ہے تو ایک روشن شعلہ اس کا پیچھا کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,19,'15:19','اور ہم نے زمین کو پھیلایا اور اس پر ہم نے پہاڑ رکھ دئے اور اس میں ہر چیز ایک اندازے سے اگائی');
INSERT INTO "ur_wahid_uddin" VALUES(15,20,'15:20','اور ہم نے تمھارے ليے اس میں معیشت کے اسباب بنائے اور وہ چیزیں جن کو تم روزی نہیں دیتے');
INSERT INTO "ur_wahid_uddin" VALUES(15,21,'15:21','اور کوئی چیز ایسی نہیں جس کے خزانے ہمارے پاس نہ ہوں اور ہم اس کو ایک متعین اندازے کے ساتھ ہی اتارتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,22,'15:22','اور ہم ہی ہواؤں کو بار آور بناکر چلاتے ہیں پھر ہم آسمان سے پانی برساتے ہیں پھر اس پانی سے تم کو سیراب کرتے ہیں اور تمھارے بس میں نہ تھا کہ تم اس کا ذخیرہ جمع کرکے رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(15,23,'15:23','اور بے شک ہم ہی زندہ کرتے ہیں اور ہم ہی مارتے ہیں اور ہم ہی باقی رہ جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(15,24,'15:24','اور ہم تمھارے اگلوں کو بھی جانتے ہیں اور تمھارے پچھلوں کو بھی جانتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,25,'15:25','اور بے شک تمھارا رب ان سب کو اكٹھاکرے گا وہ علم والا ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,26,'15:26','اور ہم نے انسان کو سنے ہوئے گارے کی کھنکھناتی مٹی سے پیدا کیا');
INSERT INTO "ur_wahid_uddin" VALUES(15,27,'15:27','اور اس سے پہلے جنوں کو ہم نے آگ کی لپٹ سے پیدا کیا');
INSERT INTO "ur_wahid_uddin" VALUES(15,28,'15:28','اور جب تیرے رب نے فرشتوں سے کہا کہ میں سنے ہوئے گارے کی سوکھی مٹی سے ایک بشر پیدا کرنے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(15,29,'15:29','جب میں اس کو پورا بنالوں اور اس میں اپنی روح میں سے پھونک دوں تو تم اس کے ليے سجدہ میں گر پڑنا');
INSERT INTO "ur_wahid_uddin" VALUES(15,30,'15:30','پس تمام فرشتوں نے سجدہ کیا');
INSERT INTO "ur_wahid_uddin" VALUES(15,31,'15:31','مگر ابلیس، کہ اس نے سجدہ کرنے والوں کا ساتھ دینے سے انکار کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(15,32,'15:32','خدا نے کہا اے ابلیس، تجھ کو کیا ہوا کہ تو سجدہ کرنے والوں میں شامل نہ ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(15,33,'15:33','ابلیس نے کہا کہ میں ایسا نہیں کہ بشر کو سجدہ کروں جس کو تونے سنے ہوئے گارے کی سوکھی مٹی سے پیدا کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,34,'15:34','خدا نے کہا تو یہاں سے نکل جا، کیوں کہ تو مردود ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,35,'15:35','اور بے شک تجھ پر روز جزا تک لعنت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,36,'15:36','ابلیس نے کہا اے میرے رب، تو مجھے اس دن تک کے ليے مہلت دے جس دن لوگ اٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(15,37,'15:37','خدا نے کہا، تجھ کو مہلت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,38,'15:38','اس مقرر وقت کے دن تک');
INSERT INTO "ur_wahid_uddin" VALUES(15,39,'15:39','ابلیس نے کہا، اے میرے رب، جیسا تو نے مجھ کو گمراہ کیا ہے اسی طرح میں زمین میں ان کے ليے مزین کروں گا اور سب کو گمراہ کردوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(15,40,'15:40','سوائے ان کے جو تیرے چنے ہوئے بندے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,41,'15:41','اللہ نے فرمایا، یہ ایک سیدھا راستہ ہے جو مجھ تک پہنچتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,42,'15:42','بے شک جو میرے بندے ہیں ، ان پر تیرا زور نہیں چلے گا سوا ان کے جو گمراہوں میں سے تیری پیروی کریں');
INSERT INTO "ur_wahid_uddin" VALUES(15,43,'15:43','اور ان سب کے ليے جہنم کا وعدہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,44,'15:44','اس کے سات دروازے ہیں ہر دروازہ کے ليے ان لوگوں کے الگ الگ حصے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,45,'15:45','بے شک ڈرنے والے باغوں اور چشموں میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(15,46,'15:46','داخل ہوجاؤ ان میں سلامتی اور امن کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(15,47,'15:47','اور ان کے سینوں کی کدورتیں ہم نکال دیں گے، سب بھائی بھائی کی طرح رہیں گے، تختوں پر آمنے سامنے');
INSERT INTO "ur_wahid_uddin" VALUES(15,48,'15:48','وہاں ان کو کوئی تکلیف نہیں پہنچے گی اور نہ وہ وہاں سے نکالے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(15,49,'15:49','میرے بندوں کو خبر دے دو کہ میں بخشنے والا، رحمت والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(15,50,'15:50','اور میری سزا درد ناک سزا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,51,'15:51','اور ان کو ابراہیم کے مہمانوں سے آگاہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(15,52,'15:52','جب وہ اس کے پاس آئے پھر انھوں نے سلام کیا ابراہیم نے کہا کہ ہم تم لوگوں سے اندیشہ ناک ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,53,'15:53','انھوں نے کہا کہ اندیشہ نہ کرو ہم تم کو ایک لڑکے کی بشارت دیتے ہیں جو بڑا عالم ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(15,54,'15:54','ابراہیم نے کہا ،کیا تم اس بڑھاپے میں مجھ کو اولاد کی بشارت دیتے ہو پس تم کس چیز کی بشارت مجھ کو دے رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(15,55,'15:55','انھوں نے کہا کہ ہم تمھیں حق کے ساتھ بشارت دیتے ہیں پس تونا امید ہونے والوں میں سے نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(15,56,'15:56','ابراہیم نے کہا کہ اپنے رب کی رحمت سے گمراہوں کے سوا اور کون نا امید ہوسکتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,57,'15:57','کہا، اے بھیجے ہوئے فرشتو، اب تمھاری مہم کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,58,'15:58','انھوں نے کہا کہ ہم ایک مجرم قوم کی طرف بھیجے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,59,'15:59','مگر لوط کے گھر والے کہ ہم ان سب کو بچالیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(15,60,'15:60','بجز اس کی بیوی کے کہ ہم نے ٹھہرا لیا ہے کہ وہ ضرور مجرم لوگوں میں رہ جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(15,61,'15:61','پھر جب بھیجے ہوئے فرشتے خاندانِ لوط کے پاس آئے');
INSERT INTO "ur_wahid_uddin" VALUES(15,62,'15:62','انھوں نے کہا کہ تم لوگ اجنبی معلوم ہوتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(15,63,'15:63','انھوں نے کہا کہ نہیں ، بلکہ ہم تمھارے پاس وہ چیز لے کر آئے ہیں جس میں یہ لوگ شک کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,64,'15:64','اور ہم تمھارے پاس حق کے ساتھ آئے ہیں ، اور ہم بالکل سچے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,65,'15:65','پس تم کچھ رات رہے اپنے گھر والوں کے ساتھ نکل جاؤ اور تم ان کے پیچھے چلو اور تم میں سے کوئی پیچھے مڑ کر نہ دیکھے اور وہاں چلے جاؤ جہاں تم کو جانے کا حکم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,66,'15:66','اور ہم نے لوط کے پاس حکم بھیجا کہ صبح ہوتے ہی ان لوگوں کی جڑ کٹ جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(15,67,'15:67','اور شہر کے لوگ خوش ہوکر آئے');
INSERT INTO "ur_wahid_uddin" VALUES(15,68,'15:68','اس نے کہا یہ لوگ میرے مہمان ہیں ، تم لوگ مجھ کو رسوا نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(15,69,'15:69','اور تم اللہ سے ڈرو اور مجھ کو ذلیل نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(15,70,'15:70','انھوں نے کہا کیا ہم نے تم کو دنیا بھر کے لوگوں سے منع نہیں کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(15,71,'15:71','اس نے کہا یہ میری بیٹیاں ہیں اگر تم کو کرنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,72,'15:72','تیری جان کی قسم، وہ اپنی سرمستی میں مدہوش تھے');
INSERT INTO "ur_wahid_uddin" VALUES(15,73,'15:73','پس دن نکلتے ہی ان کو چنگھاڑنے پکڑلیا');
INSERT INTO "ur_wahid_uddin" VALUES(15,74,'15:74','پھر ہم نے اس بستی کو تلپٹ کردیااور ان لوگوں پر کنکر کے پتھر کی بارش کردی');
INSERT INTO "ur_wahid_uddin" VALUES(15,75,'15:75','بے شک اس میں نشانیاں ہیں دھیان کرنے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(15,76,'15:76','اور یہ بستی ایک سیدھی راہ پر واقع ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,77,'15:77','بے شک اس میں نشانی ہے ایمان والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(15,78,'15:78','اور ایکہ والے یقیناً ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(15,79,'15:79','پس ہم نے ان سے انتقام لیا اور یہ دونوں بستیاں کھلے راستے پر واقع ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,80,'15:80','اور حجر والوں نے بھی رسولوں کو جھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(15,81,'15:81','اور ہم نے ان کو اپنی نشانیاں دیں مگر وہ اس سے منہ پھیرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,82,'15:82','اور وہ پہاڑوں کو تراش کر ان میں گھر بناتے تھے کہ امن میں رہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,83,'15:83','پس ان کو صبح کے وقت سخت آواز نے پکڑلیا');
INSERT INTO "ur_wahid_uddin" VALUES(15,84,'15:84','پس ان کا کیا ہوا ان کے کچھ کام نہ آیا');
INSERT INTO "ur_wahid_uddin" VALUES(15,85,'15:85','اور ہم نے آسمانوں اور زمین کو اور جو کچھ ان کے درمیان ہے، حکمت کے بغیر نہیں بنایا اور بلاشبہ قیامت آنے والی ہے پس تم خوبی کے ساتھ درگزر کرو');
INSERT INTO "ur_wahid_uddin" VALUES(15,86,'15:86','بے شک تمھارا رب سب کا خالق ہے، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,87,'15:87','اور ہم نے تم کو سات مثانی اور قرآن عظیم عطا کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,88,'15:88','تم اس متاعِ دنیا کی طرف آنکھ اٹھاکر نہ دیکھو جو ہم نے ان میں سے مختلف لوگوں کو دی ہیں اور ان پر غم نہ کرو اور ایمان والوں پر اپنے شفقت کے بازو جھکادو');
INSERT INTO "ur_wahid_uddin" VALUES(15,89,'15:89','اور کہو کہ میں ایک کھلا ہوا ڈرانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(15,90,'15:90','اسی طرح ہم نے تقسیم کرنے والوں پر بھی اتارا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(15,91,'15:91','جنھوں نے اپنے قرآن کے ٹکڑے ٹکڑے کردئے');
INSERT INTO "ur_wahid_uddin" VALUES(15,92,'15:92','پس تیرے رب کی قسم، ہم ان سب سے ضرور پوچھیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(15,93,'15:93','جو کچھ وہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(15,94,'15:94','پس جس چیز کا تم کو حکم ملا ہے، اس کو کھول کر سنا دو اور مشرکوں سے اعراض کرو');
INSERT INTO "ur_wahid_uddin" VALUES(15,95,'15:95','ہم تمھاری طرف سے ان مذاق اڑانے والوں کے ليے کافی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(15,96,'15:96','جو اللہ کے ساتھ دوسرے معبود کو شریک کرتے ہیں پس عنقریب وہ جان لیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(15,97,'15:97','اور ہم جانتے ہیں کہ جو کچھ وہ کہتے ہیں ، اس سے تمھارا دل تنگ ہوتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(15,98,'15:98','پس تم اپنے رب کی حمد کے ساتھ اس کی تسبیح کرو اور سجدہ کرنے والوں میں سے بنو');
INSERT INTO "ur_wahid_uddin" VALUES(15,99,'15:99','اور اپنے رب کی عبادت کرو یہاں تک کہ تمھارے پاس یقینی بات آجائے');
INSERT INTO "ur_wahid_uddin" VALUES(16,1,'16:1','آگیا اللہ کا فیصلہ، پس اس کی جلدی نہ کرو وہ پاک ہے اور برتر ہے اس سے جس کو وہ شریک ٹھہراتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,2,'16:2','وہ فرشتوں کو اپنے حکم کی روح کے ساتھ اتارتا ہے اپنے بندوں میں سے جس پر چاہتا ہے کہ لوگوں کو خبر دار کردو کہ میرے سوا کوئی لائق عبادت نہیں پس تم مجھ سے ڈرو');
INSERT INTO "ur_wahid_uddin" VALUES(16,3,'16:3','اس نے آسمانوں اور زمین کو حق کے ساتھ پیدا کیا ہے وہ برتر ہے اس شرک سے جو وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,4,'16:4','اس نے انسان کو ایک بوند سے بنایا پھر وہ یکایک کھلم کھلا جھگڑنے لگا');
INSERT INTO "ur_wahid_uddin" VALUES(16,5,'16:5','اور اس نے چوپایوں کو بنایا، ان میں تمھارے ليے پوشاک ہے اور دوسرے فائدے بھی، اور ان میں سے تم کھاتے بھی ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,6,'16:6','اور ان میں تمھارے ليے رونق ہے، جب کہ شام کے وقت ان کو لاتے ہو اور جب صبح کے وقت چھوڑتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,7,'16:7','اور وہ تمھارے بوجھ ایسے مقامات تک پہنچاتے ہیں جہاں تم سخت محنت کے بغیر نہیں پہنچ سکتے تھے بے شک تمھارا رب بڑا شفیق، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,8,'16:8','اور اس نے گھوڑے اور خچر اور گدھے پیدا کيے تاکہ تم ان پر سوار ہو اور زینت کے ليے بھی اور وہ ایسی چیزیں پیدا کرتا ہے جو تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(16,9,'16:9','اور اللہ تک پہنچتی ہے سیدھی راہ اور بعض راستے ٹیڑھے بھی ہیں اور اگر اللہ چاہتا تو تم سب کو ہدایت دے دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(16,10,'16:10','وہی ہے جس نے آسمان سے پانی اتارا، تم اس میں سے پیتے ہو اور اسی سے درخت ہوتے ہیں جن میں تم چَراتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,11,'16:11','وہ اسی سے تمھارے ليے کھیتی اور زیتون اور کھجور اور انگور اور ہر قسم کے پھل اگاتا ہے بے شک اس کے اندر نشانی ہے ان لوگوں کے ليے جو غور کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,12,'16:12','اور اس نے تمھارے کام میں لگا دیا رات کو اور دن کو اور سورج کو اور چاند کو اور ستارے بھی اس کے حکم سے مسخر ہیں بے شک اس میں نشانیاں ہیں عقل مند لوگوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(16,13,'16:13','اور زمین میں جو چیزیں مختلف قسم کی تمھارے ليے پھیلائیں ، بے شک اس میں نشانی ہے ان لوگوں کے ليے جو سبق حاصل کریں');
INSERT INTO "ur_wahid_uddin" VALUES(16,14,'16:14','اور وہی ہے جس نے سمندر کو تمھارے کام میں لگادیا، تاکہ تم اس میں سے تازہ گوشت کھاؤ اور اس سے زیور نکالو جس کو تم پہنتے ہو اور تم کشتیوں کو دیکھتے ہو کہ اس میں چیرتی ہوئی چلتی ہیں اور تاکہ تم اس کا فضل تلاش کرو اور تاکہ تم شکر کرو');
INSERT INTO "ur_wahid_uddin" VALUES(16,15,'16:15','اور اس نے زمین میں پہاڑ رکھ دئے تاکہ وہ تم کو لے کر ڈگمگانے نہ لگے اور اس نے نہریں اور راستے بنائے تاکہ تم راہ پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(16,16,'16:16','اور بہت سی دوسری علامتیں بھی ہیں ، اور لوگ تاروں سے بھی راستہ معلوم کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,17,'16:17','پھر کیا جو پیداکرتا ہے وہ برابر ہے اس کے جو کچھ پیدا نہیں کرسکتا، کیا تم سوچتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,18,'16:18','اگر تم اللہ کی نعمتوں کو گنو تو تم ان کو گن نہ سکو گے، بے شک اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,19,'16:19','اور اللہ جانتا ہے جو کچھ تم چھپاتے ہو اور جو تم ظاہر کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,20,'16:20','اور جن کو لوگ اللہ کے سوا پکارتے ہیں ، وہ کسی چیز کو پیدا نہیں کرسکتے اور وہ خود پیداکيے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,21,'16:21','وہ مردہ ہیں جن میں جان نہیں اور وہ نہیں جانتے کہ وہ کب اٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(16,22,'16:22','تمھارا معبود ایک ہی معبود ہے، مگر جو لوگ آخرت پر ایمان نہیں رکھتے ان کے دل منکر ہیں اور وہ تکبر کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,23,'16:23','اللہ یقینا جانتا ہے جو کچھ وہ چھپاتے ہیں اور جو کچھ وہ ظاہر کرتے ہیں بے شک وہ تکبر کرنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(16,24,'16:24','اور جب ان سے کہا جائے کہ تمھارے رب نے کیا چیز اتاری ہے تووہ کہتے ہیں کہ اگلے لوگوں کی کہانیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,25,'16:25','تاکہ وہ قیامت کے دن اپنے بوجھ بھی پورے اٹھائیں اور ان لوگوں کے بوجھ میں سے بھی جن کو وہ بغیر کسی علم کے گمراہ کر رہے ہیں یاد رکھو، بہت برا ہے وہ بوجھ جس کو وہ اٹھا رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,26,'16:26','ان سے پہلے والوں نے بھی تدبیریں کیں پھر اللہ ان کی عمارت پر بنیادوں سے آگیا پس چھت اوپر سے ان کے اوپر گر پڑی اور ان پر عذاب وہاں سے آگیا جہاں سے ان کو گمان بھی نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(16,27,'16:27','پھر قیامت کے دن اللہ ان کو رسوا کرے گا اور کہے گا کہ وہ میرے شریک کہاں ہیں جن کے ليے تم جھگڑا کیا کرتے تھے جن کو علم دیا گیاتھا وہ کہیں گے کہ آج رسوائی اور عذاب منکروں پر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,28,'16:28','جن لوگوں کو فرشتے اس حال میں وفات دیں گے کہ وہ اپنی جانوں پر ظلم کر رہے ہوں گے تو اس وقت وہ سپر ڈال دیں گے کہ ہم تو کوئی برا کام نہ کرتے تھے، ہاں بے شک اللہ جانتا ہے جو کچھ تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(16,29,'16:29','اب جہنم کے دروازوں میں داخل ہوجاؤ اس میں ہمیشہ ہمیشہ رہوپس کیسا برا ٹھکانا ہے تکبر کرنے والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(16,30,'16:30','اورجو تقویٰ والے ہیں ان سے کہا گیا کہ تمھارے رب نے کیا چیز اتاری ہے تو انھوں نے کہا کہ نیک بات جن لوگوں نے بھلائی کی ان کے ليے اس دنیا میں بھی بھلائی ہے اور آخرت کا گھر بہتر ہے اور کیا خوب گھر ہے تقویٰ والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(16,31,'16:31','ہمیشہ رہنے کے باغ ہیں جن میں وہ داخل ہوں گے، ان کے نیچے سے نہریں جاری ہوں گی ان کے ليے وہاں سب کچھ ہوگا جو وہ چاہیں ، اللہ پرہیز گاروں کو ایسا ہی بدلہ دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(16,32,'16:32','جن کی روح فرشتے اس حالت میں قبض کرتے ہیں کہ وہ پاک ہیں فرشتے کہتے ہیں تم پر سلامتی ہو، جنت میں داخل ہوجاؤ اپنے اعمال کے بدلے میں');
INSERT INTO "ur_wahid_uddin" VALUES(16,33,'16:33','کیا یہ لوگ اس کے منتظر ہیں کہ ان کے پاس فرشتے آئیں یا تمھارے رب کا حکم آجائے ایسا ہی ان سے پہلے والوں نے کیا اور اللہ نے ان پر ظلم نہیں کیا بلکہ وہ خود ہی اپنے اوپر ظلم کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(16,34,'16:34','پھر ان کو ان کے برے کام کی سزائیں ملیں اور جس چیز کا وہ مذاق اڑاتے تھے اس نے ان کو گھیر لیا');
INSERT INTO "ur_wahid_uddin" VALUES(16,35,'16:35','اور جن لوگوں نے شرک کیا وہ کہتے ہیں ، اگر اللہ چاہتاتو ہم اس کے سوا کسی چیز کی عبادت نہ کرتے، نہ ہم اور نہ ہمارے باپ دادا، اور نہ ہم اس کے بغیر کسی چیزکو حرام ٹھہراتے ایسا ہی ان سے پہلے والوں نے کیا تھا، پس رسولوں کے ذمہ تو صرف صاف صاف پہنچا دینا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,36,'16:36','اورہم نے ہر امت میں ایک رسول بھیجا کہ اللہ کی عبادت کرو اور طاغوت سے بچو، پس ان میں سے کچھ کو اللہ نے ہدایت دی اور کسی پر گمراہی ثابت ہوئی پس زمین میں چل پھر کر دیکھو کہ جھٹلانے والوں کا انجام کیا ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(16,37,'16:37','اگر تم اس کی ہدایت کے حریص ہو تو اللہ اس کو ہدایت نہیں دیتا جس کو وہ گمراہ کردیتا ہے اور ان کا کوئی مدد گار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,38,'16:38','اور یہ لوگ اللہ کی قسمیں کھاتے ہیں ، سخت قسمیں کہ جو شخص مرجائے گا اللہ اس کو نہیں اٹھائے گا ہاں ، یہ اس کے اوپر ایک پکا وعدہ ہے، لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(16,39,'16:39','تاکہ ان کے سامنے اس چیزکو کھول دے جس میں وہ اختلاف کر رہے ہیں اور انکار کرنے والے لوگ جان لیں کہ وہ جھوٹے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(16,40,'16:40','جب ہم کسی چیز کا ارادہ کرتے ہیں تو اتنا ہی ہمارا کہنا ہوتا ہے کہ ہم اس کو کہتے ہیں کہ ہوجا تو وہ ہوجاتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,41,'16:41','اور جن لوگوں نے اللہ کے ليے اپنا وطن چھوڑا، بعد اس کے کہ ان پر ظلم کیا گیا،ہم ان کو دنیامیں ضرور اچھا ٹھکانہ دیں گے اور آخرت کا ثواب تو بہت بڑا ہے، کاش وہ جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(16,42,'16:42','وہ ایسے ہیں جو صبر کرتے ہیں اور اپنے رب پر بھروسہ رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,43,'16:43','اور ہم نے تم سے پہلے بھی آدمیوں ہی کو رسول بناکر بھیجا، جن کی طرف ہم وحی کرتے تھے، پس اہل علم سے پوچھ لو اگر تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(16,44,'16:44','ہم نے بھیجا تھا ان کو دلائل اور کتابوں کے ساتھ اور ہم نے تم پر بھی یاد دہانی اتاری تاکہ تم لوگوں پر اس چیز کو واضح کردو جو ان کی طرف اتاری گئی ہے اور تاکہ وہ غور کریں');
INSERT INTO "ur_wahid_uddin" VALUES(16,45,'16:45','کیا وہ لوگ جو بری تدبیریں کر رہے ہیں ، وہ اس بات سے بے فکر ہیں کہ اللہ ان کو زمین میں دھنسا دے یا ان پر عذاب وہاں سے آجائے جہاں سے ان کو گمان بھی نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,46,'16:46','یا ان کو چلتے پھرتے پکڑلے تو وہ لوگ خدا کو عاجز نہیں کرسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(16,47,'16:47','یاان کو اندیشہ کی حالت میں پکڑ لے پس تمھارا رب شفیق اور مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,48,'16:48','کیا وہ نہیں دیکھتے کہ اللہ نے جو چیز بھی پیدا کی ہے اس کے سائے دائیں طرف اور بائیں طرف جھک جاتے ہیں ، اللہ کو سجدہ کرتے ہوئے، اور وہ سب عاجز ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,49,'16:49','اور اللہ ہی کو سجدہ کرتی ہیں جتنی چیزیں چلنے والی آسمانوں اور زمین میں ہیں اور فرشتے بھی اور وہ تکبر نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(16,50,'16:50','وہ اپنے اوپر اپنے رب سے ڈرتے ہیں اور وہی کرتے ہیں جس کا ان کو حکم ملتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,51,'16:51','اور اللہ نے فرمایا کہ دو معبود مت بناؤ وہ ایک ہی معبود ہے، تو مجھ ہی سے ڈرو');
INSERT INTO "ur_wahid_uddin" VALUES(16,52,'16:52','اور اسی کا ہے جو کچھ آسمانوں اور زمین میں ہے اور اسی کی اطاعت ہے ہمیشہ تو کیا تم اللہ کے سوا اوروں سے ڈرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,53,'16:53','اور تمھارے پاس جو نعمت بھی ہے، وہ اللہ ہی کی طرف سے ہے پھر جب تم کو تکلیف پہنچتی ہے تو اس سے فریاد کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,54,'16:54','پھر جب وہ تم سے تکلیف دور کردیتا ہے تو تم میں سے ایک گروہ اپنے رب کا شریک ٹھہرانے لگتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,55,'16:55','تاکہ وہ منکر ہوجائیں اس چیز سے جو ہم نے ان کو دی ہے پس چند روزہ فائدے اٹھالو جلد ہی تم جان لوگے');
INSERT INTO "ur_wahid_uddin" VALUES(16,56,'16:56','اور یہ لوگ ہماری دی ہوئی چیزوں میں سے ان کا حصہ لگاتے ہیں جن کے متعلق ان کو کچھ علم نہیں خدا کی قسم، جو افترا تم کر رہے ہو، اس کی تم سے ضرور باز پرس ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(16,57,'16:57','اور وہ اللہ کے ليے بیٹیاں ٹھہراتے ہیں ، وہ اس سے پاک ہے، اور اپنے ليے وہ جو دل چاہتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,58,'16:58','اور جب ان میں سے کسی کو بیٹی کی خوش خبری دی جائے تو اس کا چہرہ سیاہ پڑجاتا ہےاور وہ جی میں گھٹتا رہتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,59,'16:59','جس چیز کی اس کو خوش خبری دی گئی ہے اس کے عار سے لوگوں سے چھپا پھرتا ہے اس کو ذلت کے ساتھ رکھ چھوڑے یا اس کو مٹی میں گاڑ دے کیا ہی برا فیصلہ ہے جو وہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,60,'16:60','بری مثال ہے ان لوگوں کے ليے جو آخرت پر ایمان نہیں رکھتے اور اللہ کے ليے اعلیٰ مثالیں ہیں وہ غالب اور حکیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,61,'16:61','اور اگر اللہ لوگوں کو ان کے ظلم پر پکڑتا تو زمین پر کسی جاندار کو نہ چھوڑتا لیکن وہ ایک مقرر وقت تک لوگوں کو مہلت دیتا ہے پھر جب ان کا مقرر وقت آجائے گا تو وہ نہ ایک گھڑی پیچھے ہٹ سکیں گے اور نہ آگے بڑھ سکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(16,62,'16:62','اور وہ اللہ کے ليے وہ چیز ٹھہراتے ہیں جس کو اپنے ليے ناپسند کرتے ہیں اور ان کی زبانیں جھوٹ بیان کرتی ہیں کہ ان کے ليے بھلائی ہے لازماً ان کے ليے دوزخ ہے اور وہ ضرور اس میں پہنچادئے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(16,63,'16:63','خدا کی قسم، ہم نے تم سے پہلے مختلف قوموں کی طرف رسول بھیجے پھر شیطان نے ان کے کام ان کو اچھے کرکے دکھائے پس وہی آج ان کا ساتھی ہے اور ان کے ليے ایک درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,64,'16:64','اور ہم نے تم پر کتاب صرف اس ليے اتاری ہے کہ تم ان کو وہ چیز کھول کر سنا دو جس میں وہ اختلاف کر رہے ہیں ، اور وہ ہدایت اور رحمت ہے ان لوگوں کے ليے جو ایمان لائیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,65,'16:65','اور اللہ نے آسمان سے پانی اتارا پھر اس سے زمین کو اس کے مردہ ہونے کے بعد زندہ کردیا بے شک اس میں نشانی ہے ان لوگوں کے ليے جو سنتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,66,'16:66','اور بے شک تمھارے ليے چوپایوں میں سبق ہے ہم ان کے پیٹوں کے اندر کے گوبر اور خون کے درمیان سے تم کو خالص دودھ پلاتے ہیں ، خوش گوار پینے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(16,67,'16:67','اور کھجور اور انگور کے پھلوں سے بھی تم ان سے نشہ کی چیزیں بھی بناتے ہو اور کھانے کی اچھی چیزیں بھی بے شک اس میں نشانی ہے ان لوگوں کے ليے جو عقل رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,68,'16:68','اور تمھارے رب نے شہد کی مکھی پر وحی کیا کہ پہاڑوں اور درختوں اور جہاں ٹٹیاں باندھتے ہیں ان میں گھر بنا');
INSERT INTO "ur_wahid_uddin" VALUES(16,69,'16:69','پھر ہر قسم کے پھلوں کا رس چوس اور اپنے رب کی ہموار کی ہوئی راہوں پر چل اس کے پیٹ سے پینے کی چیز نکلتی ہے، اس کے رنگ مختلف ہیں ، اس میں لوگوں کے ليے شفا ہے بے شک اس میں نشانی ہے ان لوگوں کے ليے جو غور کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,70,'16:70','اور اللہ نے تم کو پیدا کیا، پھر وہی تم کو وفات دیتا ہے اور تم میں سے بعض وہ ہیں جو ناکارہ عمر تک پہنچائے جاتے ہیں کہ جاننے کے بعد وہ کچھ نہ جانیں بے شک اللہ علم والا ہے، قدرت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,71,'16:71','اور اللہ نے تم میں سے بعض کو بعض پر روزی میں بڑائی دے دی پس جن کو بڑائی دی گئی ہے وہ اپنی روزی اپنے غلاموں کو نہیں دے دیتے کہ وہ اس میں برابر ہوجائیں پھر کیا وہ اللہ کی نعمت کا انکار کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,72,'16:72','اور اللہ نے تمھارے ليے تم ہی میں سے بیویاں بنائیں اور تمھاری بیویوں سے تمھارے ليے بیٹے اور پوتے پیدا کيے اور تم کو ستھری چیزیں کھانے کے ليے دیں پھر کیا یہ باطل پر ایمان لاتے ہیں اور اللہ کی نعمت کا انکار کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,73,'16:73','اور وہ اللہ کے سوا ان چیزوں کی عبادت کرتے ہیں جو نہ ان کے ليے آسمان سے کسی روزی پر اختیار رکھتے ہیں اور نہ زمین سے، اور نہ وہ قدرت رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,74,'16:74','پس تم اللہ کے ليے مثالیں نہ بیان کرو بے شک اللہ جانتا ہے اور تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(16,75,'16:75','اور اللہ مثال بیان کرتا ہے ایک غلام مملوک کی جو کسی چیز پر اختیار نہیں رکھتا، اور ایک شخص ہے جس کو ہم نے اپنے پاس سے اچھا رزق دیا ہے، وہ اس میں سے پوشیدہ اور علانیہ خرچ کرتا ہے کیا یہ یکساں ہوں گے ساری تعریف اللہ کے ليے ہے، لیکن ان میں اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(16,76,'16:76','اور اللہ ایک اور مثال بیان کرتا ہے کہ دو شخص ہیں ، جن میں سے ایک گونگا ہے، کوئی کام نہیں کرسکتا اور وہ اپنے مالک پر ایک بوجھ ہے وہ اس کو جہاں بھیجتا ہے وہ کوئی کام درست کرکے نہیں لاتا کیا وہ اور ایسا شخص برابر ہوسکتے ہیں جو انصاف کی تعلیم دیتا ہے اور وہ ایک سیدھی راہ پر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,77,'16:77','اور آسمانوں اور زمین کی پوشیدہ باتیں اللہ ہی کے ليے ہیں اور قیامت کا معاملہ بس ایسا ہوگا جیسے آنکھ جھپکنا بلکہ اس سے بھی جلد بے شک اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,78,'16:78','اور اللہ نے تم کو تمھاری ماؤں کے پیٹ سے نکالا، تم کسی چیز کو نہ جانتے تھے اور اس نے تمھارے ليے کان اور آنکھ اور دل بنائے تاکہ تم شکر کرو');
INSERT INTO "ur_wahid_uddin" VALUES(16,79,'16:79','کیا لوگوں نے پرندوں کو نہیں دیکھا کہ وہ آسمان کی فضا میں مسخر ہو رہے ہیں ان کو صرف اللہ تھامے ہوئے ہے بے شک اس میں نشانیاں ہیں ان لوگوں کے ليے جو ایمان لائیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,80,'16:80','اور اللہ نے تمھارے ليے تمھارے گھروں کو مقام سکون بنایا اور تمھارے ليے جانوروں کی کھال کے گھر بنائے جن کو تم اپنے کوچ کے دن اور قیام کے دن ہلکا پاتے ہو اور ان کے اون اور ان کے روئیں اور ان کے بالوں سے گھر کا سامان اور فائدے کی چیزیں ایک مدت تک کے ليے بنائیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,81,'16:81','اور اللہ نے تمھارے ليے اپنی پیدا کی ہوئی چیزوں کے سائے بنائے اور تمھارے ليے پہاڑوں میں چھپنے کی جگہ بنائی اور تمھارے ليے ایسے لباس بنائے جو تم کو گرمی سے بچاتے ہیں اور ایسے لباس بنائے جو لڑائی میں تم کو بچاتے ہیں اسی طرح اللہ تم پر اپنی نعمتیں پوری کرتا ہے تاکہ تم فرماں بردار بنو');
INSERT INTO "ur_wahid_uddin" VALUES(16,82,'16:82','پس اگر وہ اعراض کریں تو تمھارے اوپر صرف صاف صاف پہنچادینے کی ذمہ داری ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,83,'16:83','وہ لوگ خدا کی نعمت کو پہچانتے ہیں پھر وہ اس کے منکرہوجاتے ہیں اور ان میں اکثر لوگ ناشکر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,84,'16:84','اور جس دن ہم ہر امت میں ایک گواہ اٹھائیں گے پھر انکار کرنے والوں کو ہدایت نہ دی جائے گی اور نہ ان سے توبہ لی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(16,85,'16:85','اور جب ظالم لوگ عذاب کو دیکھیں گے تو وہ عذاب نہ ان سے ہلکا کیا جائے گا اور نہ انھیں مہلت دی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(16,86,'16:86','اور جب مشرک لوگ اپنے شریکوں کو دیکھیں گے تو کہیں گے کہ اے ہمارے رب، یہی ہمارے وہ شرکاء ہیں جن کو ہم تجھے چھوڑ کر پکارتے تھے تب وہ بات ان کے اوپر ڈال دیں گے کہ تم جھوٹے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,87,'16:87','اور اس دن وہ اللہ کے آگے جھک جائیں گے اور ان کی افترا پردازیاں ان سے گم ہوجائیں گی');
INSERT INTO "ur_wahid_uddin" VALUES(16,88,'16:88','جنھوں نے انکار کیا اور لوگوں کو اللہ کے راستہ سے روکا، ہم ان کے عذاب پر عذاب کا اضافہ کریں گے، بوجہ اس فساد کے جو وہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(16,89,'16:89','اور جس دن ہم ہر امت میں ایک گواہ انھیں میں سے ان پر اٹھائیں گے اور تم کو ان لوگوں پر گواہ بناکر لائیں گے اور ہم نے تم پر کتاب اتاری ہے ہر چیز کو کھول دینے کے ليے وہ ہدایت اور رحمت اور بشارت ہے فرماں برداروں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(16,90,'16:90','بے شک اللہ حکم دیتا ہے عدل کا اور احسان کا اور قرابت داروں کو دینے کا اور اللہ روکتا ہے فحشاء سے اور منکر سے اور سرکشی سے اللہ تم کو نصیحت کرتا ہے، تاکہ تم یاد دہانی حاصل کرو');
INSERT INTO "ur_wahid_uddin" VALUES(16,91,'16:91','اور تم اللہ کے عہد کو پورا کرو جب کہ تم آپس میں عہد کرلو اور قسموں کو پکاکرنے کے بعد نہ توڑو اور تم اللہ کو ضامن بھی بناچکے ہو بے شک اللہ جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,92,'16:92','اور تم اس عورت کی مانند نہ بنو جس نے اپنا محنت سے کاتا ہوا سوت ٹکڑے ٹکڑے کرکے توڑ دیا تم اپنی قسمو ں کو آپس میں فساد ڈالنے کا ذریعہ بناتے ہو، محض اس وجہ سے کہ ایک گروہ دوسرے گروہ سے بڑھ جائے اور اللہ اس کے ذریعے سے تمھاری آزمائش کرتا ہے اوروہ قیامت کے دن اس چیز کو اچھی طرح تم پر ظاہر کردے گا جس میں تم اختلاف کر رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,93,'16:93','اور اگر اللہ چاہتا تو تم سب کو ایک ہی امت بنادیتا، لیکن وہ بے راہ کردیتا ہے جس کو چاہتا ہے اور ہدایت دے دیتا ہے جس کو چاہتا ہے، اور ضرور تم سے تمھارے اعمال کی پوچھ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(16,94,'16:94','اور تم اپنی قسموں کو آپس میں فریب کا ذریعہ نہ بناؤ کہ کوئی قدم جمنے کے بعدپھسل جائے اور تم اس بات کی سزا چکھو کہ تم نے اللہ کی راہ سے روکا اور تمھارے ليے ایک بڑا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,95,'16:95','اور اللہ کے عہد کو تھوڑے فائدے کے ليے نہ بیچو جو کچھ اللہ کے پاس ہے، وہ تمھارے ليے بہتر ہے اگر تم جانو');
INSERT INTO "ur_wahid_uddin" VALUES(16,96,'16:96','جو کچھ تمھارے پاس ہے وہ ختم ہوجائے گا اور جو کچھ اللہ کے پاس ہے وہ باقی رہنے والاہے اور جولوگ صبر کریں گے، ہم ان کے اچھے کاموں کا اجر ان کو ضرور دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(16,97,'16:97','جو شخص کوئی نیک کام کرے گا، خواہ وہ مرد ہو یا عورت، بشرطیکہ وہ مومن ہو تو ہم اس کو زندگی دیں گے، ایک اچھی زندگی، اور جو کچھ وہ کرتے رہے اس کا ہم ان کو بہترین بدلہ دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(16,98,'16:98','پس جب تم قرآن کو پڑھو تو شیطان مردود سے اللہ کی پناہ مانگو');
INSERT INTO "ur_wahid_uddin" VALUES(16,99,'16:99','اس کا زوران لوگوں پر نہیں چلتا جو ایمان والے ہیں اور اپنے رب پر بھروسہ رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,100,'16:100','اس کا زور صرف ان لوگوں پر چلتا ہے جو اس سے تعلق رکھتے ہیں ، اور جو اللہ کے ساتھ شرک کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,101,'16:101','اور جب ہم ایک آیت کی جگہ دوسری آیت بدلتے ہیں ، اور اللہ خوب جانتا ہے جو کچھ وہ اتارتا ہے، تو وہ کہتے ہیں کہ تم گھڑ لائے ہو بلکہ ان میں اکثر لوگ علم نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(16,102,'16:102','کہو کہ اس کو روح القدس نے تمھارے رب کی طرف سے حق کے ساتھ اتارا ہے تاکہ وہ ایمان والوں کو ثابت قدم رکھے اور وہ ہدایت اور خوش خبری ہو فرماں برداروں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(16,103,'16:103','اور ہم کو معلوم ہے کہ یہ لوگ کہتے ہیں کہ اس کو تو ایک آدمی سکھاتا ہے جس شخص کی طرف وہ منسوب کرتے ہیں ، اس کی زبان عجمی ہے اور یہ قرآن صاف عربی زبان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,104,'16:104','بے شک جو لوگ اللہ کی آیتوں پر ایمان نہیں لاتے، اللہ ان کو کبھی راہ نہیں دکھائے گا اور ان کے ليے درد ناک سزا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,105,'16:105','جھوٹ تو وہ لوگ گھڑتے ہیں جو اللہ کی آیتوں پر ایمان نہیں رکھتے اوریہی لوگ جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,106,'16:106','جو شخص ایمان لانے کے بعد اللہ سے منکر ہوگا، سوائے اس کے جس پر زبردستی کی گئی ہو بشرطیکہ اس کا دل ایمان پر جما ہوا ہو، لیکن جو شخص دل کھول کر منکر ہوجائے تو ایسے لوگوں پر اللہ کا غضب ہوگا اور ان کو بڑی سزا ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(16,107,'16:107','یہ اس واسطے کہ انھوں نے آخرت کے مقابلہ میں دنیا کی زندگی کو پسند کیا اور اللہ منکروں کو راستہ نہیں دکھاتا');
INSERT INTO "ur_wahid_uddin" VALUES(16,108,'16:108','یہ وہ لوگ ہیں کہ اللہ نے ان کے دلوں پر اور ان کے کانوں پر اور ان کی آنکھوں پر مہر کردی اور یہ لوگ بالکل غافل ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,109,'16:109','لازمی بات ہے کہ آخرت میں یہ لوگ گھاٹے میں رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(16,110,'16:110','پھر تیرا رب ان لوگوں کے ليے جنھوں نے آزمائش میں ڈالے جانے کے بعدہجرت کی، پھر جہاد کیا اور قائم رہے تو ان باتوں کے بعد بے شک تیرا رب بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,111,'16:111','جس دن ہر شخص اپنی ہی طرف داری میں بولتا ہوا آئے گا اور ہر شخص کو اس کے کيے کا پورا بدلہ ملے گا اور ان پر ظلم نہ کیا جاے گا');
INSERT INTO "ur_wahid_uddin" VALUES(16,112,'16:112','اور اللہ ایک بستی والوں کی مثال بیان کرتا ہے کہ وہ امن و اطمینان میں تھے ان کو ان کا رزق فراغت کے ساتھ ہر طرف سے پہنچ رہا تھا پھر انھوں نے خدا کی نعمتوں کی ناشکری کی تو اللہ نے ان کو ان کے اعمال کے سبب سے بھوک اور خوف کا مزا چکھایا');
INSERT INTO "ur_wahid_uddin" VALUES(16,113,'16:113','اور ان کے پاس ایک رسول انھیں میں سے آیا تو اس کو انھوں نے جھوٹا بتایا، پھر ان کو عذاب نے پکڑ لیا اور وہ ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(16,114,'16:114','سو جو چیزیں اللہ نے تم کو حلال اور پاک دی ہیں ان میں سے کھاؤ اور اللہ کی نعمت کا شکر کرو، اگر تم اس کی عبادت کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,115,'16:115','اس نے تو تم پر صرف مردار کو حرام کیا ہے اور خون کو اور سور کے گوشت کو اور جس پر غیر اللہ کا نام لیا گیا ہو پھر جو شخص مجبور ہوجائے بشرطیکہ وہ نہ طالب ہو اور نہ حد سے بڑھنے والاہو، تو اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,116,'16:116','اور اپنی زبانوں کے گھڑے ہوئے جھوٹ کی بنا پر یہ نہ کہو کہ یہ حلال ہے، اور یہ حرام ہے کہ تم اللہ پر جھوٹی تہمت لگاؤ جو لوگ اللہ پر جھوٹی تہمت لگائیں گے وہ فلاح نہیں پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(16,117,'16:117','وہ تھوڑا سا فائدہ اٹھالیں ، اور ان کے ليے درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,118,'16:118','اور یہودیوں پر ہم نے وہ چیزیں حرام کر دی تھیں جو ہم اس سے پہلے تم کو بتاچکے ہیں کہ ہم نے ان پر کوئی ظلم نہیں کیا بلکہ وہ خود اپنے اوپر ظلم کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,119,'16:119','پھر تمھارا رب ان لوگوں کے ليے جنھوں نے جہالت سے برائی کرلی، اس کے بعد توبہ کیا اور اپنی اصلاح کی تو تمھارا رب اس کے بعد بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,120,'16:120','بے شک ابراہیم ایک الگ امت تھا، اللہ کا فرماں بردار، اور اس کی طرف یکسو، اور وہ شرک کرنے والوں میں سے نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(16,121,'16:121','وہ اس کی نعمتوں کا شکر کرنے والا تھا خدا نے اس کو چن لیا اور سیدھے راستے کی طرف اس کی رہنمائی کی');
INSERT INTO "ur_wahid_uddin" VALUES(16,122,'16:122','اور ہم نے اس کو دنیا میں بھی بھلائی دی اور آخرت میں بھی وہ اچھے لوگوں میں سے ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(16,123,'16:123','پھر ہم نے تمھاری طرف وحی کی کہ ابراہیم کے طریقہ کی پیروی کرو جو یکسو تھا اور وہ شرک کرنے والوں میں سے نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(16,124,'16:124','سبت انھیں لوگوں پر عائد کیا گیا تھاجنھوں نے اس میں اختلاف کیا تھا اور بے شک تمھارا رب قیامت کے دن ان کے درمیان فیصلہ کردے گا جس بات میں وہ اختلاف کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(16,125,'16:125','اپنے رب کے راستے کی طرف حکمت اور اچھی نصیحت کے ساتھ بلاؤ اور ان سے اچھے طریقہ سے بحث کرو بے شک تمھارا رب خوب جانتا ہے کہ کون اس کی راہ سے بھٹکا ہوا ہے اور وہ ان کو بھی خوب جانتا ہے جو راہ پر چلنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(16,126,'16:126','اور اگر تم بدلہ لو تو اتنا ہی بدلہ لو جتنا تمھارے ساتھ کیا گیا ہے اور اگر تم صبر کرو تو وہ صبر کرنے والوں کے ليے بہت بہتر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(16,127,'16:127','اور صبر کرو اور تمھارا صبر خدا ہی کی توفیق سے ہے اور تم ان پر غم نہ کرو اور جو کچھ تدبیریں وہ کر رہے ہیں اس سے تنگ دل نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(16,128,'16:128','بے شک اللہ ان لوگوں کے ساتھ ہے جو پرہیز گار ہیں اور جو نیکی کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,1,'17:1','پاک ہے وہ جو لے گیا ایک رات اپنے بندے کو مسجد حرام سے دور کی اس مسجد تک جس کے ماحول کو ہم نے بابرکت بنایا ہے ، تاکہ ہم اس کو اپنی کچھ نشانیاں دکھائیں بے شک وہ سننے والا، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,2,'17:2','اور ہم نے موسیٰ کو کتاب دی اور اس کو بنی اسرائیل کے ليے ہدایت بنایا کہ میرے سوا کسی کو اپنا کارساز نہ بناؤ');
INSERT INTO "ur_wahid_uddin" VALUES(17,3,'17:3','تم ان لوگوں کی اولاد ہو جن کو ہم نے نوح کے ساتھ سوار کیا تھا، بے شک وہ ایک شکر گزار بندہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(17,4,'17:4','اور ہم نے بنی اسرائیل کو کتاب میں بتادیا تھا کہ تم دو مرتبہ زمین (شام) میں خرابی کروگے اور بڑی سرکشی دکھاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(17,5,'17:5','پھر جب ان میں سے پہلا وعدہ آیا تو ہم نے تم پر اپنے بندے بھیجے، نہایت زور والے وہ گھروں میں گھس پڑے اور وعدہ پورا ہوکر رہا');
INSERT INTO "ur_wahid_uddin" VALUES(17,6,'17:6','پھر ہم نے تمھاری باری ان پر لوٹادی اور مال اور اولاد سے تمھاری مدد کی اور تم کو زیادہ بڑی جماعت بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(17,7,'17:7','اگر تم اچھا کام کرو گے تو تم اپنے ليے اچھا کرو گے اور اگر تم برا کام کرو گے تب بھی اپنے ليے برا کرو گے پھر جب دوسرے وعدے کا وقت آیا تو ہم نے اوربندے بھیجے کہ وہ تمھارے چہرے کو بگاڑ دیں اورمسجد (بیت المقدس) میں گھس جائیں جس طرح وہ اس میں پہلی بار گھسے تھے اور جس چیز پر ان کا زور چلے اس کو برباد کردیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,8,'17:8','بعید نہیں کہ تمھارا رب تمھارے اوپر رحم کرے اور اگر تم پھر وہی کرو گے تو ہم بھی وہی کریں گے اور ہم نے جہنم کو منکرین کے ليے قید خانہ بنادیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,9,'17:9','بلاشبہ یہ قرآن وہ راہ دکھاتا ہے جو بالکل سیدھی ہے اور وہ بشارت دیتا ہے ایمان والوں کو جو اچھے عمل کرتے ہیں کہ ان کے ليے بڑا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,10,'17:10','اور یہ کہ جو لوگ آخرت کو نہیں مانتے، ان کے ليے ہم نے ایک درد ناک عذاب تیار کر رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,11,'17:11','اور انسان برائی مانگتا ہے جس طرح اس کو بھلائی مانگنا چاہيے اور انسان بڑا جلد باز ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,12,'17:12','اور ہم نے رات اور دن کو دو نشانیاں بنایا پھر ہم نے رات کی نشانی کو مٹادیا اور دن کی نشانی کو ہم نے روشن کردیا، تاکہ تم اپنے رب کا فضل تلاش کرو اور تاکہ تم برسوں کی گنتی اور حساب معلوم کرو اور ہم نے ہر چیز کو خوب کھول کر بیان کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,13,'17:13','اور ہم نے ہر انسان کی قسمت اس کے گلے کے ساتھ باندھ دی ہے اور ہم قیامت کے دن اس کے ليے ایک کتاب نکالیں گے جس کو وہ کھلا ہوا پائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(17,14,'17:14','پڑھ اپنی کتاب آج اپنا حساب لینے کے ليے تو خود ہی کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,15,'17:15','جو شخص ہدایت کی راہ چلتا ہے تو وہ اپنے ہی ليے چلتا ہے اور جو شخص بے راہی کرتا ہے وہ بھی اپنے ہی نقصان کے ليے بے راہ ہوتا ہے اور کوئی بوجھ اٹھانے والا دوسرے کا بوجھ نہ اٹھائے گا اور ہم کبھی سزا نہیں دیتے جب تک ہم کسی رسول کو نہ بھیجیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,16,'17:16','اور جب ہم کسی بستی کو ہلاک کرنا چاہتے ہیں تو اس کے خوش عیش لوگوں کو حکم دیتے ہیں ، پھر وہ اس میں نافرمانی کرتے ہیں تب ان پر بات ثابت ہوجاتی ہے پھر ہم اس بستی کو تباہ و برباد کردیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,17,'17:17','اور نوح کے بعدہم نے کتنی ہی قومیں ہلاک کردیں اور تیرا رب کافی ہے اپنے بندوں کے گناہوں کو جاننے کے ليے اور ان کو دیکھنے کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(17,18,'17:18','جو شخص عاجلہ(دنیا) کو چاہتا ہو، اس کو ہم اس میں سے دے دیتے ہیں ، جتنا بھی ہم جس کو دینا چاہیں پھر ہم نے اس کے ليے جہنم ٹھہرادی ہے، وہ اس میں داخل ہوگا بد حال اور راندہ ہوکر');
INSERT INTO "ur_wahid_uddin" VALUES(17,19,'17:19','اور جس نے آخرت کو چاہا اور اس کے ليے دوڑ کی جو کہ اس کی دوڑ ہے اور وہ مومن ہو تو ایسے لوگوں کی کوشش مقبول ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(17,20,'17:20','ہم ہر ایک کو تیرے رب کی بخشش میں سے پہنچاتے ہیں ، ان کو بھی اور ان کو بھی اور تیرے رب کی بخشش کسی کے اوپر بند نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,21,'17:21','دیکھو ہم نے ان کے ایک کو دوسرے پر کس طرح فوقیت دی ہے اور یقینا آخرت اور بھی زیادہ بڑی ہے، درجہ کے اعتبار سے اور فضیلت کے اعتبار سے');
INSERT INTO "ur_wahid_uddin" VALUES(17,22,'17:22','تو اللہ کے ساتھ کسی اور کو معبود نہ بنا، ورنہ تو مذموم اوربے کس ہوکر رہ جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(17,23,'17:23','اور تیرے رب نے فیصلہ کردیا ہے کہ تم اس کے سوا کسی اور کی عبادت نہ کرو اور ماں باپ کے ساتھ اچھا سلوک کرو اگر وہ تیرے سامنے بڑھاپے کو پہنچ جائیں ، ان میں سے ایک یا دونوں ، تو ان کو اُف نہ کہو اور نہ ان کو جھڑکو، اور ان سے احترام کے ساتھ بات کرو');
INSERT INTO "ur_wahid_uddin" VALUES(17,24,'17:24','اور ان کے سامنے نرمی سے عجز کے بازو جھکادو اور کہو کہ اے رب، ان دونوں پر رحم فرما جیسا کہ انھوں نے مجھے بچپن میں پالا');
INSERT INTO "ur_wahid_uddin" VALUES(17,25,'17:25','تمھارا رب خوب جانتا ہے کہ تمھارے دلوں میں کیا ہے اگر تم نیک رہو گے تو وہ توبہ کرنے والوں کو معاف کردینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,26,'17:26','اور رشتہ دار کو اس کا حق دو اور مسکین کو اور مسافر کو اور فضول خرچی نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(17,27,'17:27','بے شک فضول خرچی کرنے والے شیطان کے بھائی ہیں ، اور شیطان اپنے رب کا بڑاناشکرا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,28,'17:28','اور اگر تم کو اپنے رب کے فضل کے انتظار میں جس کی تمھیں امید ہے، ان سے اعراض کرنا پڑے تو تم ان سے نرمی کی بات کہو');
INSERT INTO "ur_wahid_uddin" VALUES(17,29,'17:29','اور نہ تو اپنا ہاتھ گردن سے باندھ لو اور نہ اس کو بالکل کھلا چھوڑ دو کہ تم ملامت زدہ اور عاجز بن کر رہ جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(17,30,'17:30','بے شک تیرا رب جس کو چاہتا ہے زیادہ رزق دیتا ہے اور جس کے ليے چاہتا ہے تنگ کردیتا ہے بے شک وہ اپنے بندوں کو جاننے والا، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,31,'17:31','اور اپنی اولاد کو مفلسی کے اندیشے سے قتل نہ کرو، ہم ان کو بھی رزق دیتے ہیں اور تم کو بھی بے شک ان کو قتل کرنا بڑا گناہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,32,'17:32','اور زنا کے قریب نہ جاؤ، وہ بے حیائی ہے اور برا راستہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,33,'17:33','اور جس جان کو خدا نے محترم ٹھہرایا ہے اس کو قتل مت کرو مگر حق پر اور جو شخص ناحق قتل کیا جائے تو ہم نے اس کے وارث کو اختیار دیا ہے پس وہ قتل میں حد سے نہ گزرے، اس کی مدد کی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(17,34,'17:34','اور تم یتیم کے مال کے پاس نہ جاؤ مگر جس طرح کہ بہتر ہو یہاں تک کہ وہ اپنی جوانی کی عمر کو پہنچ جائے اور عہد کو پورا کرو بے شک عہد کی پوچھ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(17,35,'17:35','اور جب ناپ کر دو تو پورا ناپو اور ٹھیک ترازو سے تول کردو یہ بہتر طریقہ ہے اور اس کا انجام بھی اچھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,36,'17:36','اور ایسی چیز کے پیچھے نہ لگو جس کی تم کو خبر نہیں بے شک کان اور آنکھ اور دل سب کی آدمی سے پوچھ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(17,37,'17:37','اور زمین میں اکڑ کر نہ چلو تم زمین کو پھاڑ نہیں سکتے اورنہ تم پہاڑوں کی لمبائی کو پہنچ سکتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(17,38,'17:38','یہ سارے بُرے کام تیرے رب کے نزدیک ناپسندیدہ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,39,'17:39','یہ وہ باتیں ہیں جو تمھارے رب نے حکمت میں سے تمھاری طرف وحی کی ہے اور اللہ کے ساتھ کوئی اور معبود نہ بنانا، ورنہ تم جہنم میں ڈال دئے جاؤ گے، ملامت زدہ اور راندہ ہوکر');
INSERT INTO "ur_wahid_uddin" VALUES(17,40,'17:40','کیا تمھارے رب نے تم کو بیٹے چن کر دئے اور اپنے ليے فرشتوں میں سے بیٹیاں بنالیں بے شک تم بڑی سخت بات کہتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(17,41,'17:41','اور ہم نے اس قرآن میں طرح طرح سے بیان کیا ہے تاکہ وہ یاد دہانی حاصل کریں لیکن ان کی بیزاری بڑھتی ہی جاتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,42,'17:42','کہو کہ اگر اللہ کے ساتھ اور بھی معبود ہوتے جیسا کہ یہ لوگ کہتے ہیں تو وہ عرش والے کی طرف ضرور راستہ نکالتے');
INSERT INTO "ur_wahid_uddin" VALUES(17,43,'17:43','اللہ پاک اور برتر ہے اس سے جو یہ لوگ کہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,44,'17:44','ساتوں آسمان اور زمین اور جو ان میں ہے سب اس کی پاکی بیان کرتے ہیں اور کوئی چیز ایسی نہیں جو تعریف کے ساتھ اس کی پاکی بیان نہ کرتی ہو مگر تم ان کی تسبیح کو نہیں سمجھتے بلاشبہ وہ حلم والا، بخشنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,45,'17:45','اور جب تم قرآن پڑھتے ہو تو ہم تمھارے اور ان لوگوں کے درمیان ایک چھپا ہوا پردہ حائل کردیتے ہیں جو آخرت کو نہیں مانتے');
INSERT INTO "ur_wahid_uddin" VALUES(17,46,'17:46','اور ہم ان کے دلوں پر پردہ رکھ دیتے ہیں کہ وہ اس کو نہ سمجھیں اور ان کے کانوں میں گرانی پیدا کردیتے ہیں اور جب تم قرآن میں تنہا اپنے رب کا ذکرکرتے ہو تو وہ نفرت کے ساتھ پیٹھ پھیرلیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,47,'17:47','اور ہم جانتے ہیں کہ جب وہ تمھاری طرف کان لگاتے ہیں تو وہ کس ليے سنتے ہیں اور جب کہ وہ آپس میں سرگوشیاں کرتے ہیں یہ ظالم کہتے ہیں کہ تم لوگ تو بس ایک سحر زدہ آدمی کے پیچھے چل رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(17,48,'17:48','دیکھو تمھارے اوپر وہ کیسی کیسی مثالیں چست کر رہے ہیں یہ لوگ کھوئے گئے، وہ راستہ نہیں پاسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(17,49,'17:49','اور وہ کہتے ہیں کہ کیا جب ہم ہڈی اور ریزہ ہوجائیں گے تو کیا ہم از سر نو اٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(17,50,'17:50','کہو کہ تم پتھر یا لوہا ہوجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(17,51,'17:51','یااور کوئی چیز جو تمھارے خیال میں ان سے بھی زیادہ سخت ہو پھر وہ کہیں گے کہ وہ کون ہے جو ہم کو دوبارہ زندہ کرے گا تم کہو کہ وہی جس نے تم کو پہلی بار پیدا کیا ہے پھر وہ تمھارے آگے اپنا سرہلائیں گے اور کہیں گے کہ یہ کب ہوگا، کہو کہ عجب نہیں کہ اس کا وقت قریب آپہنچا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(17,52,'17:52','جس دن خدا تم کو پکارے گا تو تم اس کی حمد کرتے ہوئے اس کی پکار پر چلے آؤ گے اور تم یہ خیال کرو گے کہ تم بہت تھوڑی مدت رہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,53,'17:53','اور میرے بندوں سے کہو کہ وہی بات کہیں جو بہتر ہو شیطان ان کے درمیان فساد ڈالتا ہے بے شک شیطان انسان کا کھلا ہوا دشمن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,54,'17:54','تمھارا رب تم کو خوب جانتا ہے، اگر وہ چاہے تو تم پر رحم کرے یا اگر وہ چاہے تو تم کو عذاب دے اور ہم نے تم کو ان کا ذمہ دار بناکر نہیں بھیجا');
INSERT INTO "ur_wahid_uddin" VALUES(17,55,'17:55','اور تمھارا رب خوب جانتا ہے ان کو جو آسمانوں اور زمین میں ہیں اور ہم نے بعض نبیوں کو بعض پر فضیلت دی ہے اور ہم نے داؤد کو زبور دی');
INSERT INTO "ur_wahid_uddin" VALUES(17,56,'17:56','کہو کہ ان کو پکارو جن کو تم نے خدا کے سوا معبود سمجھ رکھا ہے وہ نہ تم سے کسی مصیبت کو دور کرنے کا اختیار رکھتے ہیں اور نہ وہ اس کو بدل سکتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,57,'17:57','جن کو یہ لوگ پکارتے ہیں وہ خود اپنے رب کا قرب ڈھونڈتے ہیں کہ ان میں سے کون سب سے زیادہ قریب ہوجائے اور وہ اپنے رب کی رحمت کے امیدوار ہیں اور وہ اس کے عذاب سے ڈرتے ہیں واقعی تمھارے رب کا عذاب ڈرنے ہی کی چیز ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,58,'17:58','اور کوئی بستی ایسی نہیں جس کو ہم قیامت سے پہلے ہلاک نہ کریں یا سخت عذاب نہ دیں یہ بات کتاب میں لکھی ہوئی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,59,'17:59','اور ہم کو نشانیاں بھیجنے سے نہیں روکا مگر اس چیز نے کہ اگلوں نے ان کو جھٹلایا اور ہم نے ثمود کو اونٹنی دی ان کو سمجھانے کے ليے پھر انھوں نے اس پر ظلم کیا اور نشانیاں ہم صرف ڈرانے کے ليے بھیجتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,60,'17:60','اور جب ہم نے تم سے کہا کہ تمھارے رب نے لوگوں کو گھیرے میں لے لیا ہے اور وہ رؤیا جو ہم نے تم کود کھایا وہ صرف لوگوں کی جانچ کے ليے تھا، اور اس درخت کو بھی جس کی قرآن میں مذمت کی گئی ہے اور ہم ان کو ڈراتے ہیں ، لیکن ان کی غایت سرکشی بڑھتی ہی جارہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,61,'17:61','اور جب ہم نے فرشتوں سے کہا کہ آدم کو سجدہ کرو تو انھوں نے سجدہ کیا مگر ابلیس نے نہیں کیا اس نے کہا کیا میں ایسے شخص کو سجدہ کروں جس کو تونے مٹی سے بنایا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,62,'17:62','اس نے کہا، ذرا دیکھ، یہ شخص جس کو تونے مجھ پر عزت دی ہے اگر تو مجھ کو قیامت کے دن تک مہلت دے تو میں تھوڑے لوگوں کے سوا اس کی تمام اولاد کو کھاجاؤں گا');
INSERT INTO "ur_wahid_uddin" VALUES(17,63,'17:63','خدا نے کہا کہ جا، ان میں سے جو بھی تیرا ساتھی بنا تو جہنم تم سب کا پورا پورا بدلہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,64,'17:64','اور ان میں سے جس پر تیرا بس چلے، تو اپنی آواز سے ان کا قدم اکھاڑ دے اور ان پر اپنے سوار اور پیادے چڑھا لا اور ان کے مال اور اولاد میں ان کا ساجھی بن جا اور ان سے وعدہ کر اور شیطان کا وعدہ ایک دھوکہ کے سوا اور کچھ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,65,'17:65','بے شک جو میرے بندے ہیں ان پر تیرا زور نہیں چلے گا اور تیرا رب کار سازی کے ليے کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,66,'17:66','تمھارا رب وہ ہے جو تمھارے ليے سمندر میں کشتی چلاتا ہے تاکہ تم اس کا فضل تلاش کرو بے شک وہ تمھارے اوپر مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,67,'17:67','اور جب سمندر میں تم پر کوئی آفت آتی ہے تو تم ان معبودوں کو بھول جاتے ہو جن کو تم اللہ کے سوا پکارتے تھے پھر جب وہ تم کو خشکی کی طرف بچالاتا ہے تو تم دوبارہ پھر جاتے ہو اور انسان بڑا ہی ناشکرا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,68,'17:68','کیا تم اس سے بے ڈر ہوگئے کہ خدا تم کو خشکی کی طرف لاکر زمین میں دھنسا دے یا تم پر پتھر برسانے والی آندھی بھیج دے، پھر تم کسی کو اپنا کارساز نہ پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(17,69,'17:69','یا تم اس سے بے ڈر ہوگئے کہ وہ تم کو دوبارہ سمندر میں لے جائے پھر تم پر ہوا کا سخت طوفان بھیج دے اور تم کو تمھارے انکار کے سبب سے غرق کردے پھر تم اس پر کوئی ہمارا پیچھا کرنے والا نہ پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(17,70,'17:70','اور ہم نے آدم کی اولاد کو عزت دی اور ہم نے ان کو خشکی اور تری میں سوار کیا اور ان کو پاکیزہ چیزوں کا رزق دیا اور ہم نے ان کو اپنی بہت سی مخلوقات پر فوقیت دی');
INSERT INTO "ur_wahid_uddin" VALUES(17,71,'17:71','جس دن ہم ہر گروہ کو اس کے رہنما کے ساتھ بلائیں گے پس جس کا اعمال نامہ اس کے دائیں ہاتھ میں دیاجائے گا وہ لوگ اپنا اعمال نامہ پڑھیں گے اور ان کے ساتھ ذرا بھی ناانصافی نہیں کی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(17,72,'17:72','اور جو شخص اس دنیا میں اندھا رہا، وہ آخرت میں بھی اندھا رہے گا اور بہت دور پڑا ہوگا راستے سے');
INSERT INTO "ur_wahid_uddin" VALUES(17,73,'17:73','اور قریب تھا کہ یہ لوگ فتنہ میں ڈال کر تم کو اس سے ہٹادیں جو ہم نے تم پر وحی کی ہے، تاکہ تم اس کے سوا ہماری طرف غلط بات منسوب کرو اور تب وہ تم کو اپنا دوست بنالیتے');
INSERT INTO "ur_wahid_uddin" VALUES(17,74,'17:74','اور اگر ہم نے تم کو جمائے نہ رکھا ہوتا تو قریب تھا کہ تم ان کی طرف کچھ جھک پڑو');
INSERT INTO "ur_wahid_uddin" VALUES(17,75,'17:75','پھر ہم تم کو زندگی اور موت دونوں کا دہرا (عذاب) چکھاتے اس کے بعد تم ہمارے مقابلہ میں اپنا کوئی مدد گار نہ پاتے');
INSERT INTO "ur_wahid_uddin" VALUES(17,76,'17:76','اور یہ لوگ اس سرزمین سے تمھارے قدم اکھاڑنے لگے تھے تاکہ تم کو اس سے نکال دیں اور اگر ایسا ہوتا تو تمھارے بعد یہ بھی بہت کم ٹھہرنے پاتے');
INSERT INTO "ur_wahid_uddin" VALUES(17,77,'17:77','جیسا کہ ان رسولوں کے بارے میں ہمارا طریقہ رہا ہے جن کو ہم نے تم سے پہلے بھیجا تھا اور تم ہمارے طریقے میں تبدیلی نہ پاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(17,78,'17:78','نماز قائم کرو سورج ڈھلنے کے بعد سے رات کے اندھیرے تک اور خاص کر فجر کی قرأت بے شک فجر کی قرأت مشہود ہوتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,79,'17:79','اور رات کو تہجد پڑھو، یہ نفل ہے تمھارے ليے امید ہے کہ تمھارا رب تم کو مقام محمود پر کھڑا کرے');
INSERT INTO "ur_wahid_uddin" VALUES(17,80,'17:80','اور کہو کہ اے میرے رب، مجھ کو داخل کر سچا داخل کرنا اور مجھ کو نکال سچا نکالنا اور مجھ کو اپنے پاس سے مددگار قوت عطا کر');
INSERT INTO "ur_wahid_uddin" VALUES(17,81,'17:81','اور کہہ کہ حق آیا اور باطل مٹ گیا بے شک باطل مٹنے ہی والا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(17,82,'17:82','اور ہم قرآن میں سے اتارتے ہیں جس میں شفا اور رحمت ہے ایمان والوں کے ليے، اور ظالموں کے ليے اس سے نقصان کے سوا اور کچھ نہیں بڑھتا');
INSERT INTO "ur_wahid_uddin" VALUES(17,83,'17:83','اور آدمی پر جب ہم انعام کرتے ہیں تو وہ اعراض کرتا ہے اور پیٹھ موڑ لیتا ہے اور جب اس کو تکلیف پہنچتی ہے تو وہ نا امید ہوجاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,84,'17:84','کہوکہ ہر ایک اپنے طریقہ پر عمل کر رہا ہے اب تمھارا رب ہی بہتر جانتا ہے کہ کون زیادہ ٹھیک راستہ پر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,85,'17:85','اور وہ تم سے روح کے متعلق پوچھتے ہیں کہو کہ روح میرے رب کے حکم سے ہے اور تم کو بہت تھوڑا علم دیا گیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,86,'17:86','اور اگر ہم چاہیں تو وہ سب کچھ تم سے چھین لیں جو ہم نے وحی کے ذریعہ تم کو دیا ہے، پھر تم اس کے ليے ہمارے مقابلہ میں کوئی حمایتی نہ پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(17,87,'17:87','مگریہ صرف تمھارے رب کی رحمت ہے، بے شک تمھارے اوپر اس کا بڑا فضل ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,88,'17:88','کہو کہ اگر تمام انسان اور جنات جمع ہوجائیں کہ ایسا قرآن بنا لائیں تب بھی وہ اس کے جیسا نہ لاسکیں گے، اگرچہ وہ ایک دوسرے کے مد گار بن جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,89,'17:89','اور ہم نے لوگوں کے ليے اس قرآن میں ہر قسم کا مضمون طرح طرح سے بیان کیا ہے، پھر بھی اکثر لوگ انکار ہی پر جمے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,90,'17:90','اور وہ کہتے ہیں کہ ہم ہرگز تم پر ایمان نہ لائیں گے جب تک تم ہمارے ليے زمین سے کوئی چشمہ جاری نہ کردو');
INSERT INTO "ur_wahid_uddin" VALUES(17,91,'17:91','یا تمھارے پاس کھجوروں اور انگوروں کاکوئی باغ ہوجائے، پھر تم اس باغ کے بیچ میں بہت سی نہریں جاری کردو');
INSERT INTO "ur_wahid_uddin" VALUES(17,92,'17:92','یا جیسا کہ تم کہتے ہو، ہمارے اوپر آسمان سے ٹکڑے گرادو یااللہ اور فرشتوں کولاکر ہمارے سامنے کھڑا کردو');
INSERT INTO "ur_wahid_uddin" VALUES(17,93,'17:93','یا تمھارے پاس سونے کاکوئی گھر ہوجائے یا تم آسمان پر چڑھ جاؤ اور ہم تمھارے چڑھنے کو بھی نہ مانیں گے جب تک تم وہاں سے ہم پر کوئی کتاب نہ اتار دو جسے ہم پڑھیں کہو کہ میرا رب پاک ہے، میں تو صرف ایک بشر ہوں ، اللہ کا رسول');
INSERT INTO "ur_wahid_uddin" VALUES(17,94,'17:94','اور جب ان کے پاس ہدایت آگئی تو ان کو ایمان لانے سے اس کے سوا اور کوئی چیز مانع نہیں ہوئی کہ انھوں نے کہاکہ کیا اللہ نے بشر کو رسول بناکربھیجاہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,95,'17:95','کہو کہ اگر زمین میں فرشتے ہوتے کہ اس میں چلتے پھرتے تو البتہ ہم ان پر آسمان سے فرشتہ کو رسول بناکر بھیجتے');
INSERT INTO "ur_wahid_uddin" VALUES(17,96,'17:96','کہو کہ اللہ میرے اور تمھارے درمیان گواہی کے لیے کافی ہے بے شک وہ اپنے بندوں کو جاننے والا، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,97,'17:97','اللہ جس کو راہ دکھائے وہی راہ پانے والاہے اور جس کو وہ بے راہ کردے تو تم ان کے ليے اللہ کے سوا کسی کو مدد گار نہ پاؤ گے اور ہم قیامت کے دن ان کو ان کے منہ کے بل اندھے اور گونگے اور بہرے اكھٹا کریں گے ان کا ٹھکانہ جہنم ہے جب اس کی آگ دھیمی ہوگی ہم اس کو مزید بھڑ کادیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(17,98,'17:98','یہ ہے ان کا بدلہ اس سبب سے کہ انھوں نے ہماری نشانیوں کا انکار کیا اور کہا کہ جب ہم ہڈی اور ریزہ ہوجائیں گے تو کیاہم از سر نو پیدا کرکے اٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(17,99,'17:99','کیا ان لوگوں نے نہیں دیکھاکہ جس اللہ نے آسمانوں اور زمین کو پیدا کیا وہ اس پر قادر ہے کہ ان کے مانند دوبارہ پیدا کردے اور اس نے ان کے لیے ایک مدت مقرر کر رکھی ہے، اس میں کوئی شک نہیں اس پر بھی ظالم لوگ انکار کيے بغیر نہ رہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,100,'17:100','کہو کہ اگر تم لوگ میرے رب کی رحمت کے خزانوں کے مالک ہوتے تو اس صورت میں تم خرچ ہوجانے کے اندیشے سے ضرو رہاتھ روک لیتے اور انسان بڑا ہی تنگ دل ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,101,'17:101','اور ہم نے موسیٰ کو نونشانیاں کھلی ہوئی دیں تو بنی اسرائیل سے پوچھ لو جب کہ وہ ان کے پاس آیا تو فرعون نے اس سے کہاکہ اے موسیٰ، میرے خیال میں توضرورتم پر کسی نے جادو کردیاہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,102,'17:102','موسیٰ نے کہا کہ تو خوب جانتا ہے کہ ان کو آسمانوں اور زمین کے رب ہی نے اتارا ہے، آنکھیں کھول دینے کے ليے اور میرا خیال ہے کہ اے فرعون، توضرور شامت زدہ آدمی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,103,'17:103','پھر فرعون نے چاہا کہ ان کو اس سرزمین سے اکھاڑ دے پس ہم نے اس کو اور جو اس کے ساتھ تھے سب کو غرق کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(17,104,'17:104','اور ہم نے بنی اسرائیل سے کہا کہ تم زمین میں رہو پھر جب آخرت کا وعدہ آجائے گا تو ہم تم سب کو اکھٹا کرکے لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(17,105,'17:105','اور ہم نے قرآن کو حق کے ساتھ اتارا ہے اور وہ حق ہی کے ساتھ اترا ہے اور ہم نے تم کو صرف خوش خبری دینے والا اور ڈرانے والا بناکربھیجا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,106,'17:106','اور ہم نے قرآن کو تھوڑا تھوڑا کرکے اتارا تاکہ تم اس کو لوگوں کے سامنے ٹھہر ٹھہر کر پڑھو اور اس کو ہم نے بتدریج اتارا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,107,'17:107','کہو کہ تم اس پر ایمان لاؤ یا ایمان نہ لاؤ، وہ لوگ جن کو اس سے پہلے علم دیا گیا تھا جب وہ ان کے سامنے پڑھا جاتا ہے تو وہ ٹھوڑیوں کے بل سجدہ میں گر پڑتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(17,108,'17:108','اور کہتے ہیں کہ ہمارا رب پاک ہے بے شک ہمارے رب کا وعدہ ضرور پورا ہوتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,109,'17:109','اور وہ ٹھوڑیوں کے بل روتے ہوئے گرتے ہیں اور قرآن ان کا خشوع بڑھادیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(17,110,'17:110','کہو کہ خواہ اللہ کہہ کر پکارو یا رحمن کہہ کر پکارو، اس کے ليے سب اچھے نام ہیں اور تم اپنی نماز نہ بہت پکار کر پڑھو اور نہ بالکل چپکے چپکے پڑھو اور دونوں کے درمیان کا طریقہ اختیار کرو');
INSERT INTO "ur_wahid_uddin" VALUES(17,111,'17:111','اور کہو کہ تمام خوبیاں اس اللہ کے ليے ہیں جو نہ اولاد رکھتا ہے اور نہ بادشاہی میں کوئی اس کا شریک ہے اور نہ کمزوری کی وجہ سے اس کا کوئی مد گار ہے اور تم اس کی خوب بڑائی بیان کرو');
INSERT INTO "ur_wahid_uddin" VALUES(18,1,'18:1','تعریف اللہ کے ليے ہے جس نے اپنے بندہ پر کتاب اتاری، اور اس میں کوئی کجی نہیں رکھی');
INSERT INTO "ur_wahid_uddin" VALUES(18,2,'18:2','بالکل ٹھیک، تاکہ وہ اللہ کی طرف سے ایک سخت عذاب سے آگاہ کردے اور ایمان والوں کو خوش خبری دے دے جو نیک اعمال کرتے ہیں کہ ان کے ليے اچھا بدلہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,3,'18:3','وہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,4,'18:4','اور ان لوگوں کو ڈرا دے جو کہتے ہیں کہ اللہ نے بیٹا بنایا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,5,'18:5','ان کو اس بات کاکوئی علم نہیں اور نہ ان کے باپ دادا کو یہ بڑی بھاری بات ہے جو ان کے منہ سے نکل رہی ہے، وہ صرف جھوٹ کہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(18,6,'18:6','شاید تم ان کے پیچھے غم سے اپنے آپ کو ہلاک کر ڈالو گے، اگر وہ اس بات پر ایمان نہ لائے');
INSERT INTO "ur_wahid_uddin" VALUES(18,7,'18:7','جو کچھ زمین پر ہے اس کو ہم نے زمین کی رونق بنایا ہے، تاکہ ہم لوگوں کو جانچیں کہ ان میں کون اچھا عمل کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,8,'18:8','اور ہم زمین کی تمام چیزوں کوایک صاف میدان بنادیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,9,'18:9','کیا تم خیال کرتے ہو کہ کہف اور رقیم والے ہماری نشانیوں میں سے بہت عجیب نشانی تھے');
INSERT INTO "ur_wahid_uddin" VALUES(18,10,'18:10','جب ان نوجوانوں نے غار میں پناہ لی، پھر انھوں نے کہا کہ اے ہمارے رب، ہم کو اپنے پاس سے رحمت دے اورہمارے معاملے کو درست کردے');
INSERT INTO "ur_wahid_uddin" VALUES(18,11,'18:11','پس ہم نے غار میں ان کے کانوں پر سالہا سال کے ليے (نیند کا پردہ) ڈال دیا');
INSERT INTO "ur_wahid_uddin" VALUES(18,12,'18:12','پھر ہم نے ان کو اٹھایا، تاکہ ہم معلوم کریں کہ دونوں گروہوں میں سے کون مدتِ قیام کا زیادہ ٹھیک شمار کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,13,'18:13','ہم تم کو ان کااصل قصہ سناتے ہیں وہ کچھ نوجوان تھے جو اپنے رب پر ایمان لائے اور ہم نے ان کی ہدایت میں مزید ترقی دی');
INSERT INTO "ur_wahid_uddin" VALUES(18,14,'18:14','اور ہم نے ان کے دلوں کو مضبوط کر دیا جب کہ وہ اٹھے اور کہا کہ ہمارا رب وہی ہے جو آسمانوں اور زمین کا رب ہے ہم اس کے سوا کسی دوسرے معبود کو نہ پکاریں گے اگر ہم ایساکریں تو ہم بہت بے جا بات کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,15,'18:15','یہ ہماری قوم کے لوگوں نے اس کے سوا دوسرے معبود بنا رکھے ہیں یہ ان کے حق میں واضح دلیل کیوں نہیں لاتے پھر اس شخص سے بڑا ظالم اور کون ہوگا جو اللہ پر جھوٹ باندھے');
INSERT INTO "ur_wahid_uddin" VALUES(18,16,'18:16','اور جب تم ان لوگوں سے الگ ہوگئے ہو اور ان کے معبودوں سے جن کی وہ خدا کے سوا عبادت کرتے ہیں تو اب چل کر غار میں پناہ لو، تمھارا رب تمھارے اوپراپنی رحمت پھیلائے گا اور تمھارے کام کے لیے سروسامان مہیا کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(18,17,'18:17','اور تم سورج کو دیکھتے کہ جب وہ طلوع ہوتا ہے تو ان کے غار سے دائیں جانب کو بچا رہتا ہے اور جب ڈوبتا ہے تو ان سے بائیں طرف کو کترا جاتا ہے اور وہ غار کے اندر ایک وسیع جگہ میں ہیں یہ اللہ کی نشانیوں میں سے ہے جس کو اللہ ہدایت دے، وہی ہدایت پانے والا ہے اور جس کو اللہ بے راہ کردے تو تم اس کے ليے کوئی مدد گار، راہ بتانے والا نہ پاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,18,'18:18','اور تم انھیں دیکھ کر یہ سمجھتے کہ وہ جاگ رہے ہیں ، حالاں کہ وہ سو رہے تھے ہم ان کو دائیں اور بائیں کروٹ بدلواتے رہتے تھے اور ان کا کتا غار کے دہانے پر دونوں ہاتھ پھیلائے ہوئے بیٹھا تھا اگر تم ان کو جھانک کر دیکھتے تو ان سے پیٹھ پھیر کر بھاگ کھڑے ہوتے اور تمھارے اند ران کی دہشت بیٹھ جاتی');
INSERT INTO "ur_wahid_uddin" VALUES(18,19,'18:19','اور اسی طرح ہم نے ان کو جگایا تاکہ وہ آپس میں پوچھ گچھ کریں ان میں سے ایک کہنے والے نے کہا، تم کتنی دیر یہاں ٹھہرے انھوں نے کہا کہ ہم ایک دن یا ایک دن سے بھی کم ٹھہرے ہوں گے وہ بولے کہ اللہ ہی بہتر جانتا ہے کہ تم کتنی دیر یہاں رہے پس اپنے میں سے کسی کو یہ چاندی کا سکہ دے کر شہر بھیجو، پس وہ دیکھے کہ پاکیزہ کھانا کہاں ملتا ہے، اور تمھارے ليے اس میں سے کچھ کھانا لائے اور وہ نرمی سے جائے اور کسی کو تمھاری خبر نہ ہونے دے');
INSERT INTO "ur_wahid_uddin" VALUES(18,20,'18:20','اگر وہ تمھاری خبر پا جائیں گے تو تم کو پتھروں سے مار ڈالیں گے یا تم کو اپنے دین میں لوٹالیں گے اور پھر تم کبھی فلاح نہ پاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,21,'18:21','اور اس طرح ہم نے ان پر لوگوں کو مطلع کردیا، تاکہ لوگ جان لیں کہ اللہ کا وعدہ سچا ہے اور یہ کہ قیامت میں کوئی شک نہیں جب لوگ آپس میں ان کے معاملے میں جھگڑ رہے تھے پھر کہنے لگے کہ ان کے غار پر ایک عمارت بنادو ان کا رب ان کو خوب جانتا ہے جو لوگ ان کے معاملہ میں غالب آئے، انھوں نے کہا کہ ہم ان کے غار پر ایک عبادت گاہ بنائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,22,'18:22','کچھ لوگ کہیں گے کہ وہ تین تھے، اور چوتھا ان کا کتا تھا اور کچھ لوگ کہیں گے وہ پانچ تھے اور چھٹا ان کا کتا تھا، یہ لوگ بے تحقیق بات کہہ رہے ہیں ، اور کچھ لوگ کہیں گے کہ وہ سات تھے اور آٹھواں ان کا کتا تھا کہو کہ میرا رب بہتر جانتا ہے کہ وہ کتنے تھے تھوڑے ہی لوگ ان کو جانتے ہیں پس تم سرسری بات سے زیادہ ان کے معاملہ میں بحث نہ کرو اور نہ ان کے بارے میں ان میں سے کسی سے پوچھو');
INSERT INTO "ur_wahid_uddin" VALUES(18,23,'18:23','اور تم کسی کام کی نسبت یوں نہ کہو کہ میں اس کو کل کردوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(18,24,'18:24','مگر یہ کہ اللہ چاہے اور جب تم بھول جاؤ تو اپنے رب کو یاد کرو اور کہوکہ امید ہے کہ میرا رب مجھ کو بھلائی کی اس سے زیادہ قریب راہ دکھادے');
INSERT INTO "ur_wahid_uddin" VALUES(18,25,'18:25','اور وہ لوگ اپنے غار میں تین سو سال رہے (کچھ لوگ مدت کے شمار میں ) سال اور بڑھ گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(18,26,'18:26','کہوکہ اللہ ان کے رہنے کی مدت کو زیادہ جانتا ہے آسمانوں اور زمین کا غیب اس کے علم میں ہے، کیا خوب ہے وہ دیکھنے والا اور سننے والا خدا کے سوا ان کا کوئی مدد گار نہیں اور نہ اللہ کسی کو اپنے اختیار میں شریک کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,27,'18:27','اور تمھارے رب کی جو کتاب تم پر وحی کی جارہی ہے اس کو سناؤ، خدا کی باتوں کو کوئی بدلنے والا نہیں اور اس کے سوا تم کوئی پناہ نہیں پاسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(18,28,'18:28','اور اپنے آپ کو ان لوگوں کے ساتھ جمائے رکھو جو صبح و شام اپنے رب کو پکارتے ہیں ، وہ اس کی رضا کے طالب ہیں اور تمھاری آنکھیں حیاتِ دنیا کی رونق کی خاطر ان سے ہٹنے نہ پائیں اور تم ایسے شخص کا کہنا نہ مانو جس کے قلب کو ہم نے اپنی یاد سے غافل کردیا اور وہ اپنی خواہش پر چلتا ہے اور اس کا معاملہ حد سے گزر گیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,29,'18:29','اورکہو کہ یہ حق ہے تمھارے رب کی طرف سے، پس جو شخص چاہے اسے مانے اور جو شخص چاہے نہ مانے ہم نے ظالموں کے ليے ایسی آگ تیار کر رکھی ہے جس کی قناتیں ان کو اپنے گھیرے میں لے لیں گی اور اگر وہ پانی کے ليے فریاد کریں گے تو ان کی فریاد رسی ایسے پانی سے کی جائے گی جو تیل کی تلچھٹ کی طرح ہوگا وہ چہروں کو بھون ڈالے گا کیا برا پانی ہوگااور کیسا برا ٹھکانا');
INSERT INTO "ur_wahid_uddin" VALUES(18,30,'18:30','بے شک جو لوگ ایمان لائے اور انھوں نے اچھے کام کيے تو ہم ایسے لوگوں کا اجر ضائع نہیں کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,31,'18:31','جو اچھی طرح کام کریں ، ان کے ليے ہمیشہ رہنے والے باغ ہیں جن کے نیچے نہریں بہتی ہوں گی وہاں ان کو سونے کے کنگن پہنائے جائیں گے اور وہ باریک اور دبیز ریشم کے سبز کپڑے پہنیں گے، تختوں پر ٹیک لگائے ہوئے کیسا اچھا بدلہ ہے اور کیسی اچھی جگہ');
INSERT INTO "ur_wahid_uddin" VALUES(18,32,'18:32','تم ان کے سامنے ایک مثال پیش کرو دو شخص تھے ان میں سے ایک کو ہم نے انگوروں کے دو باغ دئے اور ان کے گرد کھجور کے درختوں کا احاطہ بنایااور دونوں کے درمیان کھیتی رکھ دی');
INSERT INTO "ur_wahid_uddin" VALUES(18,33,'18:33','دونوں باغ اپنا پورا پھل لائے، ان میں کچھ کمی نہیں کی اور دونوں باغوں کے بیچ ہم نے نہر جاری کردی');
INSERT INTO "ur_wahid_uddin" VALUES(18,34,'18:34','اور اس کو خوب پھل ملا تو اس نے اپنے ساتھی سے بات کرتے ہوئے کہا کہ میں تجھ سے مال میں زیادہ ہوں اور تعداد میں بھی زیادہ طاقت ور ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(18,35,'18:35','وہ اپنے باغ میں داخل ہوا اور وہ اپنے آپ پر ظلم کر رہا تھا اس نے کہا کہ میں نہیں سمجھتا کہ یہ کبھی برباد ہوجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(18,36,'18:36','اور میں نہیں سمجھتا کہ قیامت کبھی آئے گی اور اگر میں اپنے رب کی طرف لوٹا دیا گیا تو ضرور اس سے زیادہ اچھی جگہ مجھ کو ملے گی');
INSERT INTO "ur_wahid_uddin" VALUES(18,37,'18:37','اس کے ساتھی نے بات کرتے ہوئے کہا، کیا تم اس ذات سے انکارکر رہے ہو جس نے تم کو مٹی سے بنایا، پھر پانی کی ایک بوند سے پھر تم کو پورا آدمی بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(18,38,'18:38','لیکن میرا رب تو وہی اللہ ہے اور میں اپنے رب کے ساتھ کسی کو شریک نہیں ٹھہراتا');
INSERT INTO "ur_wahid_uddin" VALUES(18,39,'18:39','اور جب تم اپنے باغ میں داخل ہوئے تو تم نے کیوں نہ کہا کہ جو اللہ چاہتا ہے وہی ہوتا ہے، اللہ کے بغیر کسی میں کوئی قوت نہیں اگر تم دیکھتے ہو کہ میں مال اور اولاد میں تم سے کم ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(18,40,'18:40','تو امید ہے کہ میرا رب مجھ کو تمھارے باغ سے بہتر باغ دے دے اور تمھارے باغ پر آسمان سے کوئی آفت بھیج دے جس سے وہ باغ صاف میدا ن ہوکر رہ جائے');
INSERT INTO "ur_wahid_uddin" VALUES(18,41,'18:41','یا اس کا پانی خشک ہوجائے، پھر تم اس کو کسی طرح نہ پاسکو');
INSERT INTO "ur_wahid_uddin" VALUES(18,42,'18:42','اور اس کے پھل پر آفت آئی تو جو کچھ اس نے اس پر خرچ کیا تھا اس پر وہ ہاتھ ملتا رہ گیا اور وہ باغ اپنی ٹٹیوں پر گرا ہوا پڑا تھا اور وہ کہنے لگا کہ اے کاش، میں اپنے رب کے ساتھ کسی کو شریک نہ ٹھہراتا');
INSERT INTO "ur_wahid_uddin" VALUES(18,43,'18:43','اور اس کے پاس کوئی جتھا نہ تھا جو خدا کے سوا اس کی مدد کرتا اورنہ وہ خود بدلہ لینے والا بن سکا');
INSERT INTO "ur_wahid_uddin" VALUES(18,44,'18:44','یہاں سارا اختیار صرف خدائے برحق کا ہے وہ بہترین اجر اور بہترین انجام والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,45,'18:45','اور ان کو دنیا کی زندگی کی مثال سناؤ جیسے کہ پانی جس کو ہم نے آسمان سے اتارا پھر اس سے زمین کی نباتات خوب گھنی ہوگئیں پھر وہ ریزہ ریزہ ہوگئیں جس کو ہوائیں اڑاتی پھرتی ہیں اور اللہ ہر چیز پر قدرت رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,46,'18:46','مال اور اولاد دنیوی زندگی کی رونق ہیں اور باقی رہنے والی نیکیاں تمھارے رب کے نزدیک ثواب اور توقع کے اعتبار سے بہتر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(18,47,'18:47','اور جس دن ہم پہاڑوں کو چلائیں گے اور تم دیکھو گے زمین کو بالکل کھلی ہوئی اور ہم ان سب کو جمع کریں گے پھر ہم ان میں سے کسی کو نہ چھوڑیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,48,'18:48','اور سب لوگ تیرے رب کے سامنے صف باندھ کر پیش کيے جائیں گے تم ہمارے پاس آگئے جس طرح ہم نے تم کو پہلی بار پیدا کیا تھا، بلکہ تم نے یہ گمان کیا کہ ہم تمھارے ليے کوئی وعدہ کا وقت مقرر نہیں کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,49,'18:49','اور رجسٹر رکھا جائے گا تو تم مجرموں کو دیکھو گے کہ اس میں جو کچھ ہے وہ اس سے ڈرتے ہوں گے اور کہیں گے کہ ہائے خرابی کیسی ہے یہ کتاب کہ اس نے نہ کوئی چھوٹی بات درج کرنے سے چھوڑی ہے اور نہ کوئی بڑی بات اور جو کچھ انھوں نے کیا ہے وہ سب سامنے پائیں گے اور تیرا رب کسی کے اوپر ظلم نہ کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(18,50,'18:50','اور جب ہم نے فرشتوں سے کہا کہ آدم کو سجدہ کرو تو انھوں نے سجدہ کیا مگر ابلیس نے سجدہ نہ کیا، وہ جنوں میں سے تھا پس اس نے اپنے رب کے حکم کی نافرمانی کی اب کیا تم اس کو اور اس کی اولاد کو میرے سوا اپنا دوست بناتے ہو، حالاں کہ وہ تمھارے دشمن ہیں یہ ظالموں کے لیے بہت برا بدل ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,51,'18:51','میں نے ان کو نہ آسمانوں اور زمین پیداکرنے کے وقت بلایا اور نہ خود ان کے پیدا کرنے کے وقت بلایا اور میں ایسا نہیں کہ گمراہ کرنے والوں کواپنا مدد گار بناؤں');
INSERT INTO "ur_wahid_uddin" VALUES(18,52,'18:52','اور جس دن خدا کہے گاکہ جن کو تم میرا شریک سمجھتے تھے ان کو پکارو پس وہ ان کو پکاریں گے مگر وہ ان کو کوئی جواب نہ دیں گے اور ہم ان کے درمیان (عداوت کی) آڑ کردیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,53,'18:53','اور مجرم لوگ آگ کو دیکھیں گے اورسمجھ لیں گے کہ وہ اس میں گرنے والے ہیں اور وہ اس سے بچنے کی کوئی راہ نہ پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,54,'18:54','اور ہم نے اس قرآن میں لوگوں کی ہدایت کے ليے ہر قسم کی مثال بیان کی ہے اورانسان سب سے زیادہ جھگڑالو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,55,'18:55','اور لوگوں کو بعد اس کے کہ ان کو ہدایت پہنچ چکی، ایمان لانے سے اور اپنے رب سے بخشش مانگنے سے نہیں روکا مگر اس چیز نے کہ اگلوں کا معاملہ ان کے ليے بھی ظاہر ہوجائے، یا عذاب ان کے سامنے آکھڑا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(18,56,'18:56','اور رسولوں کو ہم صرف خوش خبری دینے والا اور ڈرانے والابناکر بھیجتے ہیں اور منکر لوگ ناحق کی باتیں لے کر جھوٹا جھگڑا کرتے ہیں ، تاکہ اس کے ذریعہ سے حق کو نیچا کردیں اور انھوں نے میری نشانیوں کو اور جو ڈر سنائے گئے ان کو مذاق بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(18,57,'18:57','اس سے بڑا ظالم کون ہوگا جس کواس کے رب کی آیات کے ذریعہ یاد دہانی کی جائے تو وہ اس سے منہ پھیر لے اور اپنے ہاتھوں کے عمل کو بھول جائے ہم نے ان کے دلوں پر پردے ڈال دئے ہیں کہ وہ اس کو نہ سمجھیں اور ان کے کانوں میں ڈاٹ ہے اور اگر تم ان کو ہدایت کی طرف بلاؤ تو وہ کبھی راہ پر آنے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(18,58,'18:58','اور تمھارا رب بخشنے والا، رحمت والا ہے اگر وہ ان کے کيے پر انھیں پکڑے تو فوراً ان پر عذاب بھیج دے، مگر ان کے ليے ایک مقرر وقت ہے اور وہ اس کے مقابلہ میں کوئی پناہ کی جگہ نہ پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,59,'18:59','اور یہ بستیاں ہیں جن کوہم نے ہلاک کردیاجب کہ وہ ظالم ہوگئے اور ہم نے ان کی ہلاکت کا ایک وقت مقرر کیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(18,60,'18:60','اور جب موسیٰ نے اپنے شاگرد سے کہا کہ میں چلتا رہوں گا، یہاں تک کہ یا تو دو دریاؤں کے ملنے کی جگہ پرپہنچ جاؤں یا اسی طرح برسوں تک چلتا رہوں');
INSERT INTO "ur_wahid_uddin" VALUES(18,61,'18:61','پس جب وہ دریاؤں کے ملنے کی جگہ پہنچے تو وہ اپنی مچھلی کو بھول گئے اور مچھلی نے دریا میں اپنی راہ لی');
INSERT INTO "ur_wahid_uddin" VALUES(18,62,'18:62','پھر جب وہ آگے بڑھے تو موسیٰ نے اپنے شاگرد سے کہا کہ ہمارا کھانا لاؤ، ہمارے اس سفر سے ہم کو بڑی تکان ہوگئی');
INSERT INTO "ur_wahid_uddin" VALUES(18,63,'18:63','شاگرد نے کہا، کیا آپ نے دیکھا، جب ہم اس پتھر کے پاس ٹھہرے تھے تو میں مچھلی کو بھول گیا اور مجھ کو شیطان نے بھلادیاکہ میں اس کا ذکر کرتا اور مچھلی عجیب طریقے سے نکل کر دریا میں چلی گئی');
INSERT INTO "ur_wahid_uddin" VALUES(18,64,'18:64','موسیٰ نے کہا، اسی موقع کی توہمیں تلاش تھی پس دونوں اپنے قدموں کے نشان دیکھتے ہوئے واپس لوٹے');
INSERT INTO "ur_wahid_uddin" VALUES(18,65,'18:65','تو انھوں نے وہاں ہمارے بندوں میں سے ایک بندے کو پایا جس کو ہم نے اپنے پاس سے رحمت دی تھی اور جس کو اپنے پاس سے ایک علم سکھایا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(18,66,'18:66','موسیٰ نے اس سے کہا، کیا میں آپ کے ساتھ رہ سکتا ہوں ، تاکہ آپ مجھے اس علم میں سے سکھادیں جو آپ کو سکھایا گیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,67,'18:67','اس نے کہا کہ تم میرے ساتھ صبر نہیں کرسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(18,68,'18:68','اور تم اس چیز پر کیسے صبر کرسکتے ہو جو تمھاری واقفیت کے دائرے سے باہرہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,69,'18:69','موسیٰ نے کہا، ان شاء اللہ، آپ مجھ کو صبر کرنے والا پائیں گے اور میں کسی بات میں آپ کی نافرمانی نہیں کروں گا');
INSERT INTO "ur_wahid_uddin" VALUES(18,70,'18:70','اس نے کہا کہ اگر تم میرے ساتھ چلتے ہو تو مجھ سے کوئی بات نہ پوچھنا جب تک کہ میں خود تم سے اس کا ذکر نہ کروں');
INSERT INTO "ur_wahid_uddin" VALUES(18,71,'18:71','پھر دونوں چلے یہاں تک کہ جب وہ کشتی میں سوار ہوئے تو اس شخص نے کشتی میں چھید کردیا موسیٰ نے کہا، کیا آپ نے اس کشتی میں اس ليے چھید کیا ہے کہ کشتی والوں کو غرق کردیں یہ تو آپ نے بڑی سخت چیز کر ڈالی');
INSERT INTO "ur_wahid_uddin" VALUES(18,72,'18:72','اس نے کہا، میں نے تم سے نہیں کہا تھا کہ تم میرے ساتھ صبر نہ کرسکو گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,73,'18:73','موسیٰ نے کہا، میری بھول پر مجھ کو نہ پکڑئیے اور میرے معاملہ میں سختی سے کام نہ لیجيے');
INSERT INTO "ur_wahid_uddin" VALUES(18,74,'18:74','پھر وہ دونوں چلے، یہاں تک کہ وہ ایک لڑکے سے ملے تو اس شخص نے اس کو مار ڈالا موسیٰ نے کہا، کیاآپ نے ایک معصوم جان کو مار ڈالا حالاں کہ اس نے کسی کا خون نہیں کیا تھا یہ تو آپ نے ایک نامعقول بات کی');
INSERT INTO "ur_wahid_uddin" VALUES(18,75,'18:75','اس شخص نے کہا کہ کیا میں نے تم سے نہیں کہا تھا کہ تم میرے ساتھ صبر نہ کرسکو گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,76,'18:76','موسیٰ نے کہا کہ اس کے بعد اگر میں آپ سے کسی چیز کے متعلق پوچھوں تو آپ مجھ کو ساتھ نہ رکھیں آپ میری طرف سے عذر کی حد کو پہنچ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(18,77,'18:77','پھر دونوں چلے، یہاں تک کہ جب وہ ایک بستی والوں کے پاس پہنچے تو وہاں كے لوگوں سے کھانے کو مانگا انھوں نے ان کی میزبانی سے انکار کردیا پھر ان کو وہاں ایک دیوار ملی جو گرا چاہتی تھی تو اس نے اس کو سیدھاکردیا موسیٰ نے کہااگر آپ چاہتے تو اس پر کچھ اجرت لے لیتے');
INSERT INTO "ur_wahid_uddin" VALUES(18,78,'18:78','اس نے کہاکہ اب یہ میرے اور تمھارے درمیان جدائی ہے میں تم کو ان چیزوں کی حقیقت بتاؤں گا جن پر تم صبر نہ کرسکے');
INSERT INTO "ur_wahid_uddin" VALUES(18,79,'18:79','کشتی کا معاملہ یہ ہے کہ وہ چند مسکینوں کی تھی جو دریا میں محنت کرتے تھے، تو میں نے چاہا کہ اس کو عیب دار کردوں ، اور ان کے آگے ایک بادشاہ تھا جو ہر کشتی کو زبردستی چھین کر لے لیتا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(18,80,'18:80','اور لڑکے کا معاملہ یہ ہے کہ اس کے ماں باپ ایمان دار تھے ہم کو اندیشہ ہوا کہ وہ بڑا ہوکر اپنی سرکشی اور کفر سے ان کو تنگ کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(18,81,'18:81','پس ہم نے چاہا کہ ان کا رب ان کو اس کی جگہ ایسی اولاد دے جو پاکیزگی میں اس سے بہتر ہو اور شفقت کرنے والی ہو');
INSERT INTO "ur_wahid_uddin" VALUES(18,82,'18:82','اور دیوار کا معاملہ یہ ہے کہ وہ شہر کے دو یتیم لڑکوں کی تھی اور اس دیوار کے نیچے ان کا ایک خزانہ دفن تھا اور ان کا باپ ایک نیک آدمی تھا، پس تمھارے رب نے چاہا کہ وہ دونوں اپنی جوانی کی عمر کو پہنچیں اور اپنا خزانہ نکالیں یہ تمھارے رب کی رحمت سے ہوا اور میں نے اس کو اپنی رائے سے نہیں کیا یہ ہے حقیقت ان باتوں کی جن پر تم صبر نہ کرسکے');
INSERT INTO "ur_wahid_uddin" VALUES(18,83,'18:83','اور وہ تم سے ذوالقرنین کا حال پوچھتے ہیں کہو کہ میں اس کا کچھ حال تمھارے سامنے بیان کروں گا');
INSERT INTO "ur_wahid_uddin" VALUES(18,84,'18:84','ہم نے اس کو زمین میں اقتدار دیا تھا اور ہم نے اس کو ہر چیز کا سامان دیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(18,85,'18:85','پھر ذوالقرنین ایک راہ کے پیچھے چلا');
INSERT INTO "ur_wahid_uddin" VALUES(18,86,'18:86','یہاں تک کہ وہ سورج کے غروب ہونے کے مقام تک پہنچ گیا اس نے سورج کو دیکھا کہ وہ ایک کالے پانی میں ڈوب رہا ہے اور وہاں اس کوایک قوم ملی ہم نے کہاکہ اے ذوالقرنین، تم چاہو تو ان کو سزا دو اور چاہو تو ان کے ساتھ اچھا سلوک کرو');
INSERT INTO "ur_wahid_uddin" VALUES(18,87,'18:87','اس نے کہا کہ جوان میں سے ظلم کرے گا، ہم اس کو سزا دیں گے پھر وہ اپنے رب کے پاس پہنچایا جائے گا، پھر وہ اس کو سخت سزا دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(18,88,'18:88','اور جو شخص ایمان لائے گا اور نیک عمل کرے گا اس کے ليے اچھی جزا ہے اور ہم بھی اس کے ساتھ آسان معاملہ کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,89,'18:89','پھر وہ ایک راہ پر چلا');
INSERT INTO "ur_wahid_uddin" VALUES(18,90,'18:90','یہاں تک کہ جب وہ سورج نکلنے کی جگہ پہنچا تو اس نے سورج کو ایک ایسی قوم پر طلوع ہوتے ہوئے پایا جن کے ليے ہم نے آفتاب کے اوپر کوئی آڑ نہیں رکھی تھی');
INSERT INTO "ur_wahid_uddin" VALUES(18,91,'18:91','یہ اسی طرح ہے اور ہم ذوالقرنین کے احوال سے باخبر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(18,92,'18:92','پھر وہ ایک راہ پر چلا');
INSERT INTO "ur_wahid_uddin" VALUES(18,93,'18:93','یہاں تک کہ جب وہ دو پہاڑوں کے درمیان پہنچا تو ان کے پاس اس نے ایک قوم کو پایا جو کوئی بات سمجھ نہیں پاتی تھی');
INSERT INTO "ur_wahid_uddin" VALUES(18,94,'18:94','انھوں نے کہا کہ اے ذوالقرنین، یا جوج اور ماجوج ہمارے ملک میں فساد پھیلاتے ہیں تو کیا ہم تم کو کوئی محصول اس کے ليے مقرر کردیں کہ تم ہمارے اور ان کے درمیان کوئی روک بنادو');
INSERT INTO "ur_wahid_uddin" VALUES(18,95,'18:95','ذوالقرنین نے جواب دیا کہ جو کچھ میرے رب نے مجھے دیا ہے وہ بہت ہے تم محنت سے میری مدد کرو میں تمھارے اور ان کے درمیان ایک دیوار بنادوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(18,96,'18:96','تم لوہے کے تختے لاکر مجھے دو یہاں تک کہ جب اس نے دونوں کے درمیانی خلا کو بھر دیا تو لوگوں سے کہا کہ آگ دہکاؤ، یہاں تک کہ جب اس کو آگ کردیا تو کہا کہ لاؤ اب میں اس پر پگھلا ہواتانباڈال دوں');
INSERT INTO "ur_wahid_uddin" VALUES(18,97,'18:97','پس یاجوج اور ماجوج نہ اس پر چڑھ سکتے تھے اور نہ وہ اس میں سوراخ کرسکتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(18,98,'18:98','ذوالقرنین نے کہا کہ یہ میرے رب کی رحمت ہے، پھر جب میرے رب کا وعدہ آئے گا تو وہ اس کو ڈھاکر برابر کردے گا اور میرے رب کا وعدہ سچا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,99,'18:99','اور اس دن ہم لوگوں کو چھوڑ دیں گے وہ موجوں کی طرح ایک دوسرے میں گھسیں گے اور صور پھونکا جائے گاپس ہم سب کو ایک ساتھ جمع کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,100,'18:100','اور اس دن ہم جہنم کو منکروں کے سامنے لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,101,'18:101','جن کی آنکھوں پر ہماری یاد دہانی سے پردہ پڑا رہا اور وہ کچھ سننے کے ليے تیار نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(18,102,'18:102','کیا انکار کرنے والے یہ سمجھتے ہیں کہ وہ میرے سوا میرے بندوں کو اپنا کار ساز بنائیں ہم نے منکروں کی مہمانی کے ليے جہنم تیارکر رکھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,103,'18:103','کہو، کیا میں تم کو بتادوں کہ اپنے اعمال کے اعتبار سے سب سے زیادہ گھاٹے میں کون لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(18,104,'18:104','وہ لوگ جن کی کوشش دنیا کی زندگی میں اکارت ہوگئی اور وہ سمجھتے رہے کہ وہ بہت اچھاکام کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(18,105,'18:105','یہی لوگ ہیں جنھوں نے اپنے رب کی نشانیوں کا اور اس سے ملنے کا انکارکیا پس ان کاکیا ہوا برباد ہوگیا پھر قیامت کے دن ہم ان کو کوئی وزن نہ دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,106,'18:106','جہنم ان کا بدلہ ہے اس ليے کہ انھوں نے انکار کیااورمیری نشانیوں اور میرے رسولوں کا مذاق اڑایا');
INSERT INTO "ur_wahid_uddin" VALUES(18,107,'18:107','بے شک جولوگ ایمان لائے اور انھوں نے نیک عمل کیاان کے ليے فردوس کے باغوں کی مہمانی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(18,108,'18:108','اس میں وہ ہمیشہ رہیں گے وہ وہاں سے کبھی نکلنا نہ چاہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(18,109,'18:109','کہو کہ اگر سمندر میرے رب کی نشانیوں کو لکھنے کے ليے روشنائی ہوجائے تو سمندر ختم ہوجائے گا اس سے پہلے کہ میرے رب کی باتیں ختم ہوں ، اگرچہ ہم اس کے ساتھ اسی کے مانند اور سمندر ملادیں');
INSERT INTO "ur_wahid_uddin" VALUES(18,110,'18:110','کہو کہ میں تمھاری ہی طرح ایک آدمی ہوں مجھ پر وحی آتی ہے کہ تمھارا معبود صرف ایک ہی معبود ہے پس جس کو اپنے رب سے ملنے کی امید ہو، اس کو چاہيے کہ نیک عمل کرے اور اپنے رب کی عبادت میں کسی کو شریک نہ ٹھہرائے');
INSERT INTO "ur_wahid_uddin" VALUES(19,1,'19:1','کھیعص');
INSERT INTO "ur_wahid_uddin" VALUES(19,2,'19:2','یہ اس رحمت کا ذکر ہے جو تیرے رب نے اپنے بندے زکریا پر کی');
INSERT INTO "ur_wahid_uddin" VALUES(19,3,'19:3','جب اس نے اپنے رب کو چھپی آواز سے پکارا');
INSERT INTO "ur_wahid_uddin" VALUES(19,4,'19:4','زکریا نے کہا، اے میرے رب، میری ہڈیاں کمزور ہوگئی ہیں اور سر میں بالوں کی سفیدی پھیل گئی ہے اور اے میرے رب، تجھ سے مانگ کر میں کبھی محروم نہیں رہا');
INSERT INTO "ur_wahid_uddin" VALUES(19,5,'19:5','اور میں اپنے بعد رشتہ داروں کی طرف سے اندیشہ رکھتا ہوں اور میری بیوی بانجھ ہے، پس مجھ کو اپنے پاس سے ایک وارث دے');
INSERT INTO "ur_wahid_uddin" VALUES(19,6,'19:6','جو میری جگہ لے اور آل یعقوب کی بھی اوراے میرے رب اس کو اپنا پسندیدہ بنا');
INSERT INTO "ur_wahid_uddin" VALUES(19,7,'19:7','اے زکریا، ہم تم کوایک لڑکے کی بشارت دیتے ہیں جس کا نام یحییٰ ہوگا ہم نے اس سے پہلے اس نام کا کوئی آدمی نہیں بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(19,8,'19:8','اس نے کہا، اے میرے رب، میرے یہاں لڑکا کیسے ہوگا جب کہ میری بیوی بانجھ ہے اور میں بڑھاپے کے انتہائی درجہ کو پہنچ چکا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(19,9,'19:9','جواب ملا کہ ایسا ہی ہوگا تیرا رب فرماتا ہے کہ یہ میرے ليے آسان ہے میں نے اس سے پہلے تم کو پیدا کیا، حالاں کہ تم کچھ بھی نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(19,10,'19:10','زکریا نے کہا کہ اے میرے رب، میرے ليے کوئی نشانی مقرر کردے فرمایا کہ تمھاری نشانی یہ ہے کہ تم تین شب و روز لوگوں سے بات نہ کرسکو گے حالاں کہ تم تندرست ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(19,11,'19:11','پھر زکریا محرابِ عبادت سے نکل کر لوگوں کے پاس آیا اور ان سے اشارے سے کہا کہ تم صبح و شام خدا کی پاکی بیان کرو');
INSERT INTO "ur_wahid_uddin" VALUES(19,12,'19:12','اے یحییٰ، کتاب کو مضبوطی سے پکڑو اور ہم نے اس کو بچپن ہی میں دین کی سمجھ عطا کی');
INSERT INTO "ur_wahid_uddin" VALUES(19,13,'19:13','اور اپنی طرف سے اس کو نرم دلی اور پاکیزگی عطا کی');
INSERT INTO "ur_wahid_uddin" VALUES(19,14,'19:14','اور وہ پرہیز گار اور اپنے والدین کا خدمت گزار تھا اور وہ سرکش اور نافرمان نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(19,15,'19:15','اور اس پر سلامتی ہے جس دن وہ پیداہوا اور جس دن وہ مرے گا اور جس دن وہ زندہ کرکے اٹھایا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(19,16,'19:16','اور کتاب میں مریم کا ذکر کرو جب کہ وہ اپنے لوگوں سے الگ ہوکر شرقی مکان میں چلی گئی');
INSERT INTO "ur_wahid_uddin" VALUES(19,17,'19:17','پھر اس نے اپنے آپ کو ان سے پردے میں کرلیا پھر ہم نے اس کے پاس اپنا فرشتہ بھیجا جو اس کے سامنے ایک پورا آدمی بن کر ظاہر ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(19,18,'19:18','مریم نے کہا، میں تجھ سے خدائے رحمان کی پناہ مانگتی ہوں اگر تو خدا سے ڈرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,19,'19:19','اس نے کہا، میں تمھارے رب کا بھیجا ہوا ہوں ، تاکہ تم کو ایک پاکیزہ لڑکا دوں');
INSERT INTO "ur_wahid_uddin" VALUES(19,20,'19:20','مریم نے کہا میرے یہاں کیسے لڑکا ہوگا، جب کہ مجھ کو کسی آدمی نے نہیں چھوا اور نہ میں بدکار ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(19,21,'19:21','فرشتے نے کہا کہ ایسا ہی ہوگا تیرا رب فرماتا ہے کہ یہ میرے ليے آسان ہے اور تاکہ ہم اس کولوگوں کے ليے نشانی بنادیں اور اپنی جانب سے ایک رحمت اور یہ ایک طے شدہ بات ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,22,'19:22','پس مریم نے اس کا حمل اٹھالیا اور وہ اس کو لے کر ایک دور کی جگہ چلی گئی');
INSERT INTO "ur_wahid_uddin" VALUES(19,23,'19:23','پھر دردِ زہ اس کو کھجور کے درخت کی طرف لے گیااس نے کہا، کاش میں اس سے پہلے مرجاتی اور بھولی بسری چیز ہوجاتی');
INSERT INTO "ur_wahid_uddin" VALUES(19,24,'19:24','پھر مریم کو اس نے اس کے نیچے سے آواز دی کہ غمگین نہ ہو تیرے رب نے تیرے نیچے ایک چشمہ جاری کردیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,25,'19:25','اور تم کھجور کے تنے کو اپنی طرف ہلاؤ اس سے تمھارے اوپر پکی کھجوریں گریں گی');
INSERT INTO "ur_wahid_uddin" VALUES(19,26,'19:26','پس کھاؤ اور پیو اور آنکھیں ٹھنڈی کرو پھر اگر تم کوئی آدمی دیکھو تو اس سے کہہ دو کہ میں نے رحمان کا روزہ مان رکھاہے تو آج میں کسی انسان سے نہیں بولوں گی');
INSERT INTO "ur_wahid_uddin" VALUES(19,27,'19:27','پھر وہ اس کو گود میں ليے ہوئے اپنی قوم کے پاس آئی لوگوں نے کہا، اے مریم، تم نے بڑا طوفان کر ڈالا');
INSERT INTO "ur_wahid_uddin" VALUES(19,28,'19:28','اے ہارون کی بہن، نہ تمھارا باپ کوئی برا آدمی تھا اور نہ تمھاری ماں بد کار تھی');
INSERT INTO "ur_wahid_uddin" VALUES(19,29,'19:29','پھر مریم نے اس کی طرف اشارہ کیا لوگوں نے کہا، ہم اس سے کس طرح بات کریں جو کہ گود میں بچہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,30,'19:30','بچہ بولا، میں اللہ کا بندہ ہوں اس نے مجھ کو کتاب دی اور مجھ کو نبی بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(19,31,'19:31','اورمیں جہاں کہیں بھی ہوں اس نے مجھ کو برکت والا بنایا ہے اور اس نے مجھ کو نماز اور زکوٰۃ کی تاکید کی ہے جب تک میں زندہ رہوں');
INSERT INTO "ur_wahid_uddin" VALUES(19,32,'19:32','اور مجھ کو میری ماں کا خدمت گزار بنایا ہے اور مجھ کو سرکش، بدبخت نہیں بنایا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,33,'19:33','اور مجھ پر سلامتی ہے جس دن میں پیدا ہوا اور جس دن میں مروں گا اور جس دن میں زندہ کرکے اٹھایا جاؤں گا');
INSERT INTO "ur_wahid_uddin" VALUES(19,34,'19:34','یہ ہے عیسیٰ ابن مریم، سچی بات جس میں لوگ جھگڑ رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,35,'19:35','اللہ ایسا نہیں کہ وہ کوئی اولاد بنائے وہ پاک ہے جب وہ کسی کام کا فیصلہ کرتا ہے تو کہتا ہے کہ ہو جا تو وہ ہوجاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,36,'19:36','اور بے شک اللہ میرا رب ہے اور تمھارا رب بھی، پس تم اسی کی عبات کرو، یہی سیدھا راستہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,37,'19:37','پھر ان کے فرقوں نے باہم اختلاف کیا پس انکار کرنے والوں کے ليے ایک بڑے دن کے آنے سے خرابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,38,'19:38','جس دن یہ لوگ ہمارے پاس آئیں گے وہ خوب سنتے اور خوب دیکھتے ہوں گے، مگر آج یہ ظالم کھلی ہوئی گمراہی میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,39,'19:39','اور ان لوگوں کو اس حسرت کے دن سے ڈرا دو جب معاملہ کا فیصلہ کردیا جائے گا، اور وہ غفلت میں ہیں اور وہ ایمان نہیں لا رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,40,'19:40','بے شک ہم ہی زمین اور زمین کے رہنے والوں کے وارث ہوں گے اور لوگ ہماری ہی طرف لوٹائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(19,41,'19:41','اورکتاب میں ابراہیم کا ذکر کروبے شک وہ سچا تھا اور نبی تھا');
INSERT INTO "ur_wahid_uddin" VALUES(19,42,'19:42','جب اس نے اپنے باپ سے کہا کہ اے میرے باپ، ایسی چیز کی عبادت کیوں کرتے ہو جو نہ سنے اور نہ دیکھے، اور نہ تمھارے کچھ کام آسکے');
INSERT INTO "ur_wahid_uddin" VALUES(19,43,'19:43','اے میرے باپ، میرے پاس ایسا علم آیا ہے جو تمھارے پاس نہیں ہے تو تم میرے کہنے پر چلو میں تم کو سیدھا راستہ دکھاؤں گا');
INSERT INTO "ur_wahid_uddin" VALUES(19,44,'19:44','اے میرے باپ، شیطان کی عبادت نہ کر، بے شک شیطان خدائے رحمان کی نافرمانی کرنے والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,45,'19:45','اے میرے باپ، مجھ کو ڈر ہے کہ تم کو خدائے رحمان کاکوئی عذاب پکڑ لے اور تم شیطان کے ساتھی بن کر رہ جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(19,46,'19:46','باپ نے کہا کہ اے ابرہیم، کیا تم میرے معبودوں سے پھر گئے ہو اگر تم باز نہ آئے تو میں تم کو سنگ سار کردوں گا اور تم مجھ سے ہمیشہ کے لیے دور ہوجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(19,47,'19:47','ابراہیم نے کہا، تم پر سلامتی ہو میں اپنے رب سے تمھارے لیے بخشش کی دعا کروں گا، بے شک وہ مجھ پر مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,48,'19:48','اور میں تم لوگوں کو چھوڑ تا ہوں اور ان کو بھی جنھیں تم اللہ کے سوا پکارتے ہو اور میں اپنے رب ہی کو پکاروں گا امید ہے کہ میں اپنے رب کو پکار کر محروم نہیں رہوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(19,49,'19:49','پس جب وہ لوگوں سے جدا ہوگیا اور ان سے جن کو وہ اللہ کے سوا پوجتے تھے تو ہم نے اس کو اسحاق اور یعقوب عطا کيے اور ہم نے ان میں سے ہر ایک کو نبی بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(19,50,'19:50','اور ان کو اپنی رحمت کا حصہ دیا اور ہم نے ان کا نام نیک اور بلند کیا');
INSERT INTO "ur_wahid_uddin" VALUES(19,51,'19:51','اور کتاب میں موسیٰ کا ذکر کرو بے شک وہ چنا ہوا تھا اور رسول نبی تھا');
INSERT INTO "ur_wahid_uddin" VALUES(19,52,'19:52','اور ہم نے اس کو کوہِ طور کے داہنی جانب سے پکارا اور اس کو ہم نے راز کی باتیں کرنے کے ليے قریب کیا');
INSERT INTO "ur_wahid_uddin" VALUES(19,53,'19:53','اور اپنی رحمت سے ہم نے اس کے بھائی ہارون کو نبی بناکر اسے دیا');
INSERT INTO "ur_wahid_uddin" VALUES(19,54,'19:54','اور کتاب میں اسماعیل کا ذکر کرو وہ وعدہ کا سچا تھا اور رسول نبی تھا');
INSERT INTO "ur_wahid_uddin" VALUES(19,55,'19:55','وہ اپنے لوگوں کو نمازاور زکوٰۃ کا حکم دیتا تھا اور اپنے رب کے نزدیک پسندیدہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(19,56,'19:56','اور کتاب میں ادریس کا ذکر کروبے شک وہ سچا تھا اور نبی تھا');
INSERT INTO "ur_wahid_uddin" VALUES(19,57,'19:57','اور ہم نے اس کو بلند رتبہ تک پہنچایا');
INSERT INTO "ur_wahid_uddin" VALUES(19,58,'19:58','یہ وہ لوگ ہیں جن پر اللہ نے پیغمبروں میں سے اپنا فضل فرمایا آدم کی اولاد میں سے اور ان لوگوں میں سے جن کو ہم نے نوح کے ساتھ سوار کیا تھا اور ابراہیم اور اسرائیل کی نسل سے اور ان لوگوں میں سے جن کو ہم نے ہدایت بخشی اور ان کو مقبول بنایا جب ان کو خدائے رحمان کی آیتیں سنائی جاتیں تو وہ سجدہ کرتے ہوئے اور روتے ہوئے گر پڑتے');
INSERT INTO "ur_wahid_uddin" VALUES(19,59,'19:59','پھر ان کے بعد ایسے ناخلف جانشیں ہوئے جنھوں نے نماز کو کھو دیا اور وہ خواہشوں کے پیچھے پڑگئے پس عنقریب وہ اپنی خرابی کو دیکھیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(19,60,'19:60','البتہ جس نے توبہ کی اور ایمان لے آیا اور نیک کام کیا تو یہی لوگ جنت میں داخل ہوں گے اوران کی ذرا بھی حق تلفی نہیں کی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(19,61,'19:61','ان کے ليے ہمیشہ رہنے والے باغ ہیں جن کا رحمان نے اپنے بندوں سے غائبانہ وعدہ کر رکھا ہے اور یہ وعدہ پورا ہوکر رہنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,62,'19:62','اس میں وہ لوگ کوئی فضول بات نہیں سنیں گے بجز سلام کے اور اس میں ان کا رزق صبح و شام ملے گا');
INSERT INTO "ur_wahid_uddin" VALUES(19,63,'19:63','یہ وہ جنت ہے جس کا وارث ہم اپنے بندوں میں سے ان کو بنائیں گے جو خدا سے ڈرنے والے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(19,64,'19:64','اور ہم (ملائکہ) نہیں اترتے مگر تمھارے رب کے حکم سے اسی کا ہے جو ہمارے آگے ہے اور جو ہمارے پیچھے ہے اور جو اس کے بیچ میں ہے اور تمھارا رب بھولنے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,65,'19:65','وہ رب ہے آسمانوں کا اور زمین کااور جو ان کے بیچ میں ہے، پس تم اسی کی عبادت کرو اور اس کی عبادت پر قائم رہو کیا تم اس کا کوئی ہم صفت جانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(19,66,'19:66','اور انسان کہتا ہے، کیا جب میں مرجاؤں گا تو پھر زندہ کرکے نکالا جاؤں گا');
INSERT INTO "ur_wahid_uddin" VALUES(19,67,'19:67','کیا انسان کو یاد نہیں آتا کہ ہم نے اس کو اس سے پہلے پیدا کیا اور وہ کچھ بھی نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(19,68,'19:68','پس تیرے رب کی قسم، ہم ان کو جمع کریں گے اور شیطانوں کو بھی، پھر ان کو جہنم کے گرد اس طرح حاضر کریں گے کہ وہ گھٹنوں کے بل گرے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(19,69,'19:69','پھر ہم ہر گروہ میں سے ان لوگوں کو جدا کریں گے جو رحمان کے مقابلہ میں سب سے زیادہ سرکش بنے ہوئے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(19,70,'19:70','پھر ہم ایسے لوگوں کو خوب جانتے ہیں جوجہنم میں داخل ہونے کے زیادہ مستحق ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,71,'19:71','اور تم میں سے کوئی نہیں جس کااس پر سے گزر نہ ہو، یہ تیرے رب کے اوپر لازم ہے جو پورا ہوکر رہے گا');
INSERT INTO "ur_wahid_uddin" VALUES(19,72,'19:72','پھر ہم ان لوگوں کو بچالیں گے جو ڈرتے تھے اور ظالموں کو اس میں گرا ہوا چھوڑ دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(19,73,'19:73','اور جب ان کو ہماری کھلی کھلی آیتیں سنائی جاتی ہیں تو انکارکرنے والے ایمان لانے والوں سے کہتے ہیں کہ دونوں گروہوں میں سے کون بہتر حالت میں ہے اور کس کی مجلس زیادہ اچھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,74,'19:74','اور ان سے پہلے ہم نے کتنی ہی قومیں ہلاک کردیں جو ان سے زیادہ اسباب والی اور ان سے زیادہ شان والی تھیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,75,'19:75','کہوکہ جو شخص گمراہی میں ہوتا ہے تو رحمان اس کو ڈھیل دیا کرتا ہے، یہاں تک کہ جب وہ دیکھ لیں گے اس چیز کو جس کا ان سے وعدہ کیا جارہا ہے، عذاب یا قیامت، تو ان کو معلوم ہوجائے گا کہ کس کا حال برا ہے اور کس کا جتھا کمزور');
INSERT INTO "ur_wahid_uddin" VALUES(19,76,'19:76','اوراللہ ہدایت پکڑنے والوں کی ہدایت میں اضافہ کرتا ہے، اور باقی رہنے والی نیکیاں تمھارے رب کے نزدیک اجر کے اعتبار سے بہترین ہیں اور انجام کے اعتبار سے بھی بہتر');
INSERT INTO "ur_wahid_uddin" VALUES(19,77,'19:77','کیا تم نے اس کو دیکھا جس نے ہماری آیتوں کا انکار کیا اورکہا کہ مجھ کو مال اور اولاد مل کر رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(19,78,'19:78','کیا اس نے غیب میں جھانک کر دیکھا ہے یا اس نے اللہ سے کوئی عہد لے لیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,79,'19:79','ہر گز نہیں ، جو کچھ وہ کہتا ہے اس کو ہم لکھ لیں گے اور اس کی سزا میں اضافہ کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(19,80,'19:80','اور جن چیزوں کا وہ مدعی ہے، اس کے وارث ہم بنیں گے اور وہ ہمارے پاس اکیلا آئے گا');
INSERT INTO "ur_wahid_uddin" VALUES(19,81,'19:81','اور انھوں نے اللہ کے سوا معبود بنائے ہیں تاکہ وہ ان کے ليے مد دگاربنیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,82,'19:82','ہرگز نہیں ، وہ ان کی عبادت کا انکار کریں گے اور ان کے مخالف بن جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(19,83,'19:83','کیا تم نے نہیں دیکھا کہ ہم نے منکروں پر شیطانوں کو چھوڑ دیاہے، وہ ان کو خوب ابھار رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,84,'19:84','پس تم ان کے ليے جلدی نہ کرو ہم ان کی گنتی پوری کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,85,'19:85','جس دن ہم ڈرنے والوں کو رحمان کی طرف مہمان بناکر جمع کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(19,86,'19:86','اور مجرموں کو جہنم کی طرف پیاسا ہانکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(19,87,'19:87','کسی کو شفاعت کا اختیار نہ ہوگا،مگر اس کو جس نے رحمان کے پاس سے اجازت لی ہو');
INSERT INTO "ur_wahid_uddin" VALUES(19,88,'19:88','اور یہ لوگ کہتے ہیں کہ رحمان نے کسی کو بیٹا بنایا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,89,'19:89','یہ تم نے بڑی سنگین بات کہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,90,'19:90','قریب ہے کہ اس سے آسمان پھٹ پڑیں اور زمین ٹکڑے ہوجائے اور پہاڑ ٹوٹ کر گر پڑیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,91,'19:91','اس پر کہ لوگ رحمان کی طرف اولاد کی نسبت کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(19,92,'19:92','حالاں کہ رحمان کی یہ شان نہیں کہ وہ اولاد اختیار کرے');
INSERT INTO "ur_wahid_uddin" VALUES(19,93,'19:93','آسمانوں اور زمین میں کوئی نہیں جو رحمان کا بندہ ہوکر نہ آئے');
INSERT INTO "ur_wahid_uddin" VALUES(19,94,'19:94','اس کے پاس ان کا شمار ہے اور اس نے ان کو اچھی طرح گن رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(19,95,'19:95','اور ان میں سے ہر ایک قیامت کے دن اس کے سامنے اکیلا آئے گا');
INSERT INTO "ur_wahid_uddin" VALUES(19,96,'19:96','البتہ جو لوگ ایمان لائے اور جنھوں نے نیک عمل کئے، ان کے ليے خدا محبت پیدا کردے گا');
INSERT INTO "ur_wahid_uddin" VALUES(19,97,'19:97','پس ہم نے اس قرآن کو تمھاری زبان میں اس ليے آسان کردیا ہے کہ تم متقیوں کو خوش خبری سنا دو اور ہٹ دھرم لوگوں کو ڈرا دو');
INSERT INTO "ur_wahid_uddin" VALUES(19,98,'19:98','اور ان سے پہلے ہم کتنی ہی قوموں کو ہلاک کرچکے ہیں کیا تم ان میں سے کسی کو دیکھتے ہو یا ان کی کوئی آہٹ سنتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(20,1,'20:1','طٰہٰ');
INSERT INTO "ur_wahid_uddin" VALUES(20,2,'20:2','ہم نے قرآن تم پر اس ليے نہیں اتارا کہ تم مصیبت میں پڑجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(20,3,'20:3','بلکہ ایسے شخص کی نصیحت کے ليے جو ڈرتا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(20,4,'20:4','یہ اس کی طرف سے اتارا گیا ہے جس نے زمین کو اور اونچے آسمانوں کو پیدا کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,5,'20:5','وہ رحمت والا ہے، عرش پر قائم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,6,'20:6','اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور جو ان دونوں کے درمیان ہے اور جو کچھ زمین کے نیچے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,7,'20:7','اور تم چاہے اپنی بات پکار کر کہو، وہ چپکے سے کہی ہوئی بات کو جانتا ہے اور اس سے زیادہ خفی بات کو بھی');
INSERT INTO "ur_wahid_uddin" VALUES(20,8,'20:8','وہ اللہ ہے اس کے سوا کوئی معبود نہیں تمام اچھے نام اسی کے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,9,'20:9','اور کیا تم کو موسیٰ کی بات پہنچی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,10,'20:10','جب کہ اس نے ایک آگ دیکھی تو اپنے گھر والوں سے کہا کہ ٹھہرو، میں نے ایک آگ دیکھی ہے، شاید میں اس میں سے تمھارے ليے ایک انگارہ لاؤں یا اس آگ پر مجھے راستہ کا پتہ مل جائے');
INSERT INTO "ur_wahid_uddin" VALUES(20,11,'20:11','پھر جب وہ اس کے پاس پہنچا تو آواز دی گئی کہ اے موسیٰ');
INSERT INTO "ur_wahid_uddin" VALUES(20,12,'20:12','میں ہی تمھارا رب ہوں ، پس تم اپنے جوتے اتار دو، کیوں کہ تم طُویٰ کی مقدس وادی میں ہو');
INSERT INTO "ur_wahid_uddin" VALUES(20,13,'20:13','اور میں نے تم کو چن لیا ہے پس جو وحی کی جارہی ہے اس کو سنو');
INSERT INTO "ur_wahid_uddin" VALUES(20,14,'20:14','میں ہی اللہ ہوں میرے سوا کوئی معبود نہیں پس تم میری ہی عبادت کرو اورمیری یاد کے ليے نماز قائم کرو');
INSERT INTO "ur_wahid_uddin" VALUES(20,15,'20:15','بے شک قیامت آنے والی هے میں اس کو چھپائے رکھنا چاہتا ہوں ، تاکہ ہر شخص کو اس کے کيے کا بدلہ ملے');
INSERT INTO "ur_wahid_uddin" VALUES(20,16,'20:16','پس اس سے تم کو وہ شخص غافل نہ کرے جو اس پر ایمان نہیں رکھتا اور اپنی خواہشوں پر چلتا ہے کہ تم ہلاک ہوجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(20,17,'20:17','اور یہ تمھارے ہاتھ میں کیا ہے اے موسیٰ');
INSERT INTO "ur_wahid_uddin" VALUES(20,18,'20:18','اس نے کہا، یہ میری لاٹھی ہے میں اس پر ٹیک لگاتا ہوں اور اس سے اپنی بکریوں کے ليے پتے جھاڑتا ہوں اس میں میرے ليے دوسرے کام بھی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,19,'20:19','فرمایا کہ اے موسیٰ، اس کو زمین پر ڈال دو');
INSERT INTO "ur_wahid_uddin" VALUES(20,20,'20:20','اس نے اس کو ڈال دیا تو یکایک وہ ایک دوڑتا ہوا سانپ بن گیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,21,'20:21','فرمایا کہ اس کو پکڑ لو اور مت ڈرو، ہم پھراس کو اس کی پہلی حالت پر لوٹا دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(20,22,'20:22','اورتم اپنا ہاتھ اپنی بغل سے ملالو، وہ چمکتا ہوا نکلے گا بغیرکسی عیب کے یہ دوسری نشانی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,23,'20:23','تاکہ ہم اپنی بڑی نشانیوں میں سے بعض نشانیاں تمہیں دکھائیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,24,'20:24','تم فرعون کے پاس جاؤ وہ حد سے نکل گیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,25,'20:25','موسیٰ نے کہا کہ اے میرے رب، میرے سینہ کو میرے لیے کھول دے');
INSERT INTO "ur_wahid_uddin" VALUES(20,26,'20:26','اور میرے کام کو میرے ليے آسان کردے');
INSERT INTO "ur_wahid_uddin" VALUES(20,27,'20:27','اور میری زبان کی گرہ کھول دے');
INSERT INTO "ur_wahid_uddin" VALUES(20,28,'20:28','تاکہ لوگ میری بات سمجھیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,29,'20:29','اورمیرے خاندان سے میرے ليے ایک معاون مقرر کردے');
INSERT INTO "ur_wahid_uddin" VALUES(20,30,'20:30','ہارون کو جو میرا بھائی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,31,'20:31','اس کے ذریعہ سے میری کمر کو مضبوط کردے');
INSERT INTO "ur_wahid_uddin" VALUES(20,32,'20:32','اور اس کو میرے کام میں شریک کردے');
INSERT INTO "ur_wahid_uddin" VALUES(20,33,'20:33','تاکہ ہم دونوں کثرت سے تیری پاکی بیان کریں');
INSERT INTO "ur_wahid_uddin" VALUES(20,34,'20:34','اور کثرت سے تیرا چرچا کریں');
INSERT INTO "ur_wahid_uddin" VALUES(20,35,'20:35','بے شک تو ہم کو دیکھ رہاہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,36,'20:36','فرمایا کہ دے دیا گیا تم کو اے موسیٰ تمھارا سوال');
INSERT INTO "ur_wahid_uddin" VALUES(20,37,'20:37','اور ہم نے تمھارے اوپر ایک باراور احسان کیاہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,38,'20:38','جب کہ ہم نے تمھاری ماں کی طرف وحی کی جو وحی کی جارہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,39,'20:39','کہ اس کو صندوق میں رکھو، پھر اس کو دریا میں ڈال دو، پھر دریا اس کو کنارے پر ڈال دے اس کوایک شخص اٹھالے گا جو میرا بھی دشمن ہے اور اس کا بھی دشمن ہے اور میں نے اپنی طرف سے تم پر ایک محبت ڈال دی اور تاکہ تم میری نگرانی میں پرورش پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(20,40,'20:40','جب کہ تمھاری بہن چلتی ہوئی آئی، پھر وہ کہنے لگی، کیا میں تم لوگوں کو اس کا پتہ دوں جو اس بچے کی پرورش اچھی طرح کرے پس ہم نے تم کو تمھاری ماں کی طرف لوٹا دیا، تاکہ اس کی آنکھ ٹھنڈی ہو اور اس کو غم نہ رہے اور تم نے ایک شخص کو قتل کردیاپھر، ہم نے تم کو اس غم سے نجات دی اور ہم نے تم کو خوب جانچا پھر تم کئی سال مدین والوں میں رہے پھر تم ایک اندازہ پر آگئے، اے موسیٰ');
INSERT INTO "ur_wahid_uddin" VALUES(20,41,'20:41','اورمیں نے تم کو اپنے ليے منتخب کیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,42,'20:42','جاؤ تم اورتمھارے بھائی میری نشانیوں کے ساتھ اور تم دونوں میری یاد میں سستی نہ کرنا');
INSERT INTO "ur_wahid_uddin" VALUES(20,43,'20:43','تم دونوں فرعون کے پاس جاؤ کہ وہ سرکش ہوگیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,44,'20:44','پس اس سے نرمی کے ساتھ بات کرنا، شاید وہ نصیحت قبول کرے یا ڈر جائے');
INSERT INTO "ur_wahid_uddin" VALUES(20,45,'20:45','دونوں نے کہا کہ اے ہمارے رب، ہم کو اندیشہ ہے کہ وہ ہم پر زیادتی کرے یا سرکشی کرنے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(20,46,'20:46','فرمایا کہ تم اندیشہ نہ کرو میں تم دونوں کے ساتھ ہوں ، سن رہا ہوں اور دیکھ رہا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(20,47,'20:47','پس تم اس کے پاس جاؤ اور کہو کہ ہم دونوں تیرے رب کے بھیجے ہوئے ہیں ، پس تو بنی اسرائیل کو ہمارے ساتھ جانے دے اور ان کو نہ ستا ہم تیرے رب کے پاس سے ایک نشانی بھی لائے ہیں اورسلامتی اس شخص کے ليے ہے جو ہدایت کی پیروی کرے');
INSERT INTO "ur_wahid_uddin" VALUES(20,48,'20:48','ہم پر یہ وحی کی گئی ہے کہ اس شخص پر عذاب ہوگا جو جھٹلائے اور اعراض کرے');
INSERT INTO "ur_wahid_uddin" VALUES(20,49,'20:49','فرعون نے کہا، پھر تم دونوں کا رب کون ہے، اے موسیٰ');
INSERT INTO "ur_wahid_uddin" VALUES(20,50,'20:50','موسیٰ نے کہا، ہمارا رب وہ ہے جس نے ہر چیز کو اس کی صورت عطا کی، پھر رہنمائی فرمائی');
INSERT INTO "ur_wahid_uddin" VALUES(20,51,'20:51','فرعون نے کہا، پھراگلی قوموں کا کیاحال ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,52,'20:52','موسیٰ نے کہا، اس کا علم میرے رب کے پاس ایک دفتر میں ہے میرا رب نہ غلطی کرتا ہے اور نہ بھولتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,53,'20:53','وہی ہے جس نے تمھارے ليے زمین کا فرش بنایا اور اس میں تمھارے ليے راہیں نکالیں اور آسمان سے پانی اتارا پھر ہم نے اس کے ذریعہ سے مختلف قسم کی نباتات پیدا کیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,54,'20:54','کھاؤ اور اپنے مویشیوں کو چراؤ اس کے اندر اہل عقل کے ليے نشانیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,55,'20:55','اسی سے ہم نے تم کو پیدا کیاہے اور اسی میں ہم تم کو لوٹائیں گے اور اسی سے ہم تم کو دوبارہ نکالیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(20,56,'20:56','اور ہم نے فرعون کواپنی سب نشانیاں دکھائیں تواس نے جھٹلایااور انکار کیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,57,'20:57','اس نے کہا کہ اے موسیٰ، کیا تم اس ليے ہمارے پاس آئے ہو کہ اپنے جادو سے ہم کو ہمارے ملک سے نکال دو');
INSERT INTO "ur_wahid_uddin" VALUES(20,58,'20:58','تو ہم تمھارے مقابلہ میں ایسا ہی جادو لائیں گے پس تم ہمارے اور اپنے درمیان ایک وعدہ مقررکرلو، نہ ہم اس کے خلاف کریں اور نہ تم یہ مقابلہ ایک ہموار میدان میں ہو');
INSERT INTO "ur_wahid_uddin" VALUES(20,59,'20:59','موسیٰ نے کہا، تمھارے ليے وعدہ کا دن میلے والا دن ہے اور یہ کہ لوگ دن چڑھتے تک جمع کيے جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,60,'20:60','فرعون وہاں سے ہٹا، پھر اپنے سارے داؤ جمع کئے، اس کے بعد وہ مقابلہ پر آیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,61,'20:61','موسیٰ نے کہا کہ تمھارا برا ہو، اللہ پر جھوٹ نہ باندھو کہ وہ تم کو کسی آفت سے غارت کردے اور جس نے خدا پر جھوٹ باندھا وہ ناکام ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(20,62,'20:62','پھر انھوں نے اپنے معاملہ میں اختلاف کیا اور انھوں نے چپکے چپکے باہم مشورہ کیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,63,'20:63','انھوں نے کہا یہ دونوں یقیناً جادو گر ہیں ، وہ چاہتے ہیں کہ اپنے جادو کے زور سے تم کو تمھارے ملک سے نکال دیں اور تمھارے عمدہ طریقہ کا خاتمہ کردیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,64,'20:64','پس تم اپنی تدبیریں اکھٹا کرو پھر متحد ہوکر آؤ اور وہی جیت گیا جو آج غالب رہا');
INSERT INTO "ur_wahid_uddin" VALUES(20,65,'20:65','انھوں نے کہاکہ اے موسیٰ، یا تو تم ڈالو یا ہم پہلے ڈالنے والے بنیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,66,'20:66','موسیٰ نے کہا کہ تم ہی پہلے ڈالو، تو یکایک ان کی رسیاں اور ان کی لاٹھیاں ان کے جادو کے زور سے اس کو اس طرح دکھائی دیں گویا کہ وہ دوڑ رہی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,67,'20:67','پس موسیٰ اپنے دل میں کچھ ڈر گیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,68,'20:68','ہم نے کہا کہ تم ڈرو نہیں ، تم ہی غالب رہو گے');
INSERT INTO "ur_wahid_uddin" VALUES(20,69,'20:69','اورجو تمھارے داہنے ہاتھ میں ہے اس کو ڈال دو، وہ اس کو نگل جائے گا جو انھوں نے بنایا ہے یہ جو کچھ انھوں نے بنایا ہے یہ جادو گر کا فریب ہے اور جادو گر کبھی کامیاب نہیں ہوتا، خواہ وہ کیسے آئے');
INSERT INTO "ur_wahid_uddin" VALUES(20,70,'20:70','پس جادو گر سجدہ میں گر پڑے انھوں نے کہا کہ ہم ہارون اور موسیٰ کے رب پر ایمان لائے');
INSERT INTO "ur_wahid_uddin" VALUES(20,71,'20:71','فرعون نے کہا کہ تم نے اس کو مان لیا اس سے پہلے کہ میں تم کو اجازت دیتا وہی تمھارا بڑا ہے جس نے تم کو جادو سکھایا ہے تو اب میں تمھارے ہاتھ اور پاؤں مخالف سمتوں سے کٹواؤں گا اورمیں تم کو کھجور کے تنوں پر سولی دوں گا اور تم جان لوگے کہ ہم میں سے کس کا عذاب زیادہ سخت ہے اور زیادہ دیر تک رہنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,72,'20:72','جادو گروں نے کہا کہ ہم تجھ کو ہرگز ان دلائل پر ترجیح نہیں دیں گے جو ہمارے پاس آئے ہیں اور اس ذات پر جس نے ہم کو پیدا کیاہے، پس تجھ کو جو کچھ کرنا ہے اسے کر ڈال تم جو کچھ کرسکـتے ہو، اسی دنیاکی زندگی کا کرسکتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(20,73,'20:73','ہم اپنے رب پر ایمان لائے تاکہ وہ ہمارے گناہوں کو بخش دے اور اس جادو کو بھی جس پر تم نے ہمیں مجبور کیا اور اللہ بہتر ہے اور باقی رہنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,74,'20:74','بے شک جو شخص مجرم بن کر اپنے رب کے سامنے حاضرہوگا تو اس کے ليے جہنم ہے، اس میں وہ نہ مرے گا اورنہ جئے گا');
INSERT INTO "ur_wahid_uddin" VALUES(20,75,'20:75','اور جو شخص اپنے رب کے پاس مومن ہوکر آئے گاجس نے نیک عمل کيے ہوں ، تو ایسے لوگوں کے ليے بڑے اونچے درجے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,76,'20:76','ان کے ليے ہمیشہ رہنے والے باغ ہیں جن کے نیچے نہریں جاری ہوں گی وہ ان میں ہمیشہ رہیں گے اور یہ بدلہ ہے اس شخص کا جو پاکیزگی اختیارکرے');
INSERT INTO "ur_wahid_uddin" VALUES(20,77,'20:77','اور ہم نے موسیٰ کو وحی کی کہ رات کے وقت میرے بندوں کو لے کر نکلو پھر ان کے ليے سمندر میں سوکھا راستہ بنالو، تم نہ تعاقب سے ڈرو اور نہ کسی اور چیز سے ڈرو');
INSERT INTO "ur_wahid_uddin" VALUES(20,78,'20:78','پھر فرعون نے اپنے لشکروں کے ساتھ ان کا پیچھا کیا پھر ان کو سمندر کے پانی نے ڈھانپ لیا، جیسا کہ ڈھانپ لیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,79,'20:79','اور فرعون نے اپنی قوم کو گمراہ کیا اور اس کو صحیح راہ نہ دکھائی');
INSERT INTO "ur_wahid_uddin" VALUES(20,80,'20:80','اے بنی اسرائیل، ہم نے تم کو تمھارے دشمن سے نجات دی اور تم سے طور کے دائیں جانب وعدہ ٹھہرایا اور ہم نے تمھارے اوپر من و سلوی اتارا');
INSERT INTO "ur_wahid_uddin" VALUES(20,81,'20:81','کھاؤ ہماری دی ہوئی پاک روزی اور اس میں سرکشی نہ کرو کہ تمھارے اوپر میرا غضب نازل ہو اور جس پر میرا غضب اترا وہ تباہ ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(20,82,'20:82','البتہ جو توبہ کرے اور ایمان لائے اور نیک عمل کرے اور سیدھی راہ پر رہے تو اس کے ليے میں بہت زیادہ بخشنے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(20,83,'20:83','اور اے موسیٰ، اپنی قوم کو چھوڑ کر جلد آنے پر تم کو کس چیز نے ابھارا');
INSERT INTO "ur_wahid_uddin" VALUES(20,84,'20:84','موسیٰ نے کہا، وہ لوگ بھی میرے پیچھے ہی ہیں اور میں اے میرے رب، تیری طرف جلد آگیاتاکہ تو راضی ہو');
INSERT INTO "ur_wahid_uddin" VALUES(20,85,'20:85','فرمایا، تو ہم نے تمھاری قوم کو تمھارے بعد ایک فتنہ میں ڈال دیا اور سامری نے اس کوگمراہ کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,86,'20:86','پھر موسیٰ اپنی قوم کی طرف غصہ اور رنج میں بھرے ہوئے لوٹے انھوں نے کہا کہ اے میری قوم، کیا تم سے تمھارے رب نے ایک اچھا وعدہ نہیں کیا تھا کیا تم پر زیادہ زمانہ گزر گیا یا تم نے چاہا کہ تمھارے اوپر تمھارے رب کا غضب نازل ہو، اس ليے تم نے مجھ سے وعدہ خلافی کی');
INSERT INTO "ur_wahid_uddin" VALUES(20,87,'20:87','انھوں نے کہا کہ ہم نے اپنے اختیار سے آپ کے ساتھ وعدہ خلافی نہیں کی، بلکہ قوم کے زیورات کا بوجھ ہم سے اٹھوایا گیا تھا تو ہم نے اس کو پھینک دیا پھر اس طرح سامری نے ڈھال لیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,88,'20:88','پس اس نے ان کے ليے ایک بچھڑا بر آمد کردیا ایک مورت جس سے بیل کی سی آواز نکلتی تھی پھر اس نے کہا کہ یہ تمھارا معبود ہے اور موسیٰ کا معبود بھی، موسیٰ اسے بھول گئے');
INSERT INTO "ur_wahid_uddin" VALUES(20,89,'20:89','کیا وہ دیکھتے نہ تھے کہ نہ وہ کسی بات کا جواب دیتا ہے اور نہ کوئی نفع یا نقصان پہنچا سکتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,90,'20:90','اور ہارون نے ان سے پہلے ہی کہا تھا کہ اے میری قوم، تم اس بچھڑے کے ذریعہ سے بہک گئے ہو اور تمھارا رب تو رحمان ہے پس میری پیروی کرو اور میری بات مانو');
INSERT INTO "ur_wahid_uddin" VALUES(20,91,'20:91','انھوں نے کہا کہ ہم تو اسی کی پرستش میں لگے رہیں گے جب تک کہ موسیٰ ہمارے پاس لوٹ نہ آئے');
INSERT INTO "ur_wahid_uddin" VALUES(20,92,'20:92','موسیٰ نے کہا کہ اے ہارون، جب تو نے دیکھا کہ وہ بہک گئے ہیں تو تم کو کس چیز نے روکا کہ تم میری پیروی کرو');
INSERT INTO "ur_wahid_uddin" VALUES(20,93,'20:93','کیا تم نے میرے کہنے کے خلاف کیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,94,'20:94','ہارون نے کہا کہ اے میری ماں کے بیٹے، تم میری داڑھی نہ پکڑو اور نہ میرا سر مجھے یہ ڈر تھا کہ تم کہو گے کہ تم نے بنی اسرائیل کے درمیان پھوٹ ڈال دی اور میری بات کا لحاظ نہ کیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,95,'20:95','موسیٰ نے کہا کہ اے سامری، تمھارا کیا معاملہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,96,'20:96','اس نے کہا کہ مجھ کو وہ چیز نظر آئی جو دوسروں کو نظر نہیں آئی تو میں نے رسول کے نقشِ قدم سے ایک مٹھی اٹھائی اور وہ اس میں ڈال دی میرے نفس نے مجھ کو ایسا ہی سمجھایا');
INSERT INTO "ur_wahid_uddin" VALUES(20,97,'20:97','موسیٰ نے کہا کہ دور ہو اب تیرے ليے زندگی بھر یہ ہے کہ تو کہے کہ مجھ کو نہ چھونا اور تیرے ليے ایک اور وعدہ یہ ہے جو تجھ سے ٹلنے والا نہیں اور تو اپنے اس معبود کو دیکھ جس پر تو برابر معتکف رہتا تھا، ہم اس کو جلائیں گے پھر اس کو دریا میں بکھیر کر بہادیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(20,98,'20:98','تمھارا معبود تو صرف اللہ ہے اس کے سوا کوئی معبود نہیں اس کا علم ہر چیز پر حاوی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,99,'20:99','اسی طرح ہم تم کو ان کے احوال سناتے ہیں جو پہلے گزر چکے اور ہم نے تم کو اپنے پاس سے ایک نصیحت نامہ دیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,100,'20:100','جو اس سے اعراض کرے گا، وہ قیامت کے دن ایک بھاری بوجھ اٹھائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(20,101,'20:101','وہ اس میں ہمیشہ رہیں گے اور یہ بوجھ قیامت کے دن ان کے ليے بہت برا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(20,102,'20:102','جس دن صور میں پھونک ماری جائے گی اور مجرموں کو اس دن ہم اس حال میں جمع کریں گے کہ خوف سے ان کی آنکھیں نیلی ہوں گی');
INSERT INTO "ur_wahid_uddin" VALUES(20,103,'20:103','آپس میں چپکے چپکے کہتے ہوں گے کہ تم صرف دس دن رہے ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(20,104,'20:104','ہم خوب جانتے ہیں جو کچھ وہ کہیں گے جب کہ ان کا سب سے زیادہ واقف کار کہے گا کہ تم صرف ایک دن ٹھہرے');
INSERT INTO "ur_wahid_uddin" VALUES(20,105,'20:105','اور لوگ تم سے پہاڑوں کی بابت پوچھتے ہیں کہو کہ میرا رب ان کو اڑا کر بکھیر دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(20,106,'20:106','پھر زمین کو صاف میدان بناکر چھوڑ دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(20,107,'20:107','تم اس میں نہ کوئی کجی دیکھو گے اور نہ کوئی بلندي');
INSERT INTO "ur_wahid_uddin" VALUES(20,108,'20:108','اس دن سب پکارنے والے کے پیچھے چل پڑیں گے ذرا بھی کوئی کجی نہ ہوگی تمام آوازیں رحمان کے آگے دب جائیں گی تم ایک سرسراہٹ کے سوا کچھ نہ سنو گے');
INSERT INTO "ur_wahid_uddin" VALUES(20,109,'20:109','اس دن سفارش نفع نہ دے گی، مگر ایسا شخص جس کو رحمان نے اجازت دی ہو اور اس کے ليے بولنا پسند کیا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(20,110,'20:110','وہ سب کے اگلے اور پچھلے احوال کو جانتا ہے اور ان کا علم اس کا احاطہ نہیں کرسکتا');
INSERT INTO "ur_wahid_uddin" VALUES(20,111,'20:111','اور تمام چہرے اس حی و قیوم کے سامنے جھکے ہوں گے اور ایسا شخص ناکام رہے گا جو ظلم لے کر آیا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(20,112,'20:112','اور جس نے نیک کام کيے ہوں گے اور وہ ایمان بھی رکھتا ہوگا تو اس کو نہ کسی زیادتی کا اندیشہ ہوگا اور نہ کسی کمی کا');
INSERT INTO "ur_wahid_uddin" VALUES(20,113,'20:113','اور اسی طرح ہم نے عربی کا قرآن اتارا ہے اور اس میں ہم نے طرح طرح سے وعید بیان کی ہے تاکہ لوگ ڈریں یا وہ ان کے دل میں کچھ سوچ ڈال دے');
INSERT INTO "ur_wahid_uddin" VALUES(20,114,'20:114','پس برتر ہے اللہ، بادشاہ حقیقی اور تم قرآن کے لینے میں جلدی نہ کرو جب تک اس کی وحی تکمیل کو نہ پہنچ جائے اور کہوکہ اے میرے رب، میرا علم زیادہ کردے');
INSERT INTO "ur_wahid_uddin" VALUES(20,115,'20:115','اور ہم نے آدم کو اس سے پہلے حکم دیا تھا تو وہ بھول گیا اور ہم نے اس میں عزم نہ پایا');
INSERT INTO "ur_wahid_uddin" VALUES(20,116,'20:116','اور جب ہم نے فرشتوں سے کہا کہ آدم کو سجدہ کرو تو انھوں نے سجدہ کیا مگر ابلیس کہ اس نے انکار کیا');
INSERT INTO "ur_wahid_uddin" VALUES(20,117,'20:117','پھر ہم نے کہا کہ اے آدم، یہ بلاشبہ تمھارا اور تمھاری بیوی کا دشمن ہے تو کہیں وہ تم دونوں کو جنت سے نکلوا نہ دے، پھر تم محروم ہوکر رہ جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(20,118,'20:118','یہاں تمھارے ليے یہ ہے کہ تم نہ بھوکے رہو گے اور نہ تم ننگے ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(20,119,'20:119','اور تم یہاں نہ پیاسے ہوگے، اور نہ تم کو دھوپ لگے گی');
INSERT INTO "ur_wahid_uddin" VALUES(20,120,'20:120','پھر شیطان نے ان کو بہکایا اس نے کہا کہ کیا میں تم کو ہمیشگی کا درخت بتاؤں اور ایسی بادشاہی جس میں کبھی کمزوری نہ آئے');
INSERT INTO "ur_wahid_uddin" VALUES(20,121,'20:121','پس ان دونوں نے اس درخت کا پھل کھالیا تو ان دونوں کے ستر ایک دوسرے کے سامنے کھل گئے اور دونوں اپنے آپ کو جنت کے پتوں سے ڈھانکنے لگے اور آدم نے اپنے رب کے حکم کی خلاف ورزی کی تو بھٹک گئے');
INSERT INTO "ur_wahid_uddin" VALUES(20,122,'20:122','پھر اس کے رب نے اس کو نوازا پس اس کی توبہ قبول کی اور اس کو ہدایت دی');
INSERT INTO "ur_wahid_uddin" VALUES(20,123,'20:123','خدا نے کہا کہ تم دونوں یہاں سے اترو تم ایک دوسرے کے دشمن ہوگے پھر اگر تمھارے پاس میری طرف سے ہدایت آئے تو جو شخص میری ہدایت کی پیروی کرے گا، وہ نہ گمراہ ہوگا اور نہ محروم رہے گا');
INSERT INTO "ur_wahid_uddin" VALUES(20,124,'20:124','اور جو شخص میری نصیحت سے اعراض کرے گا تو اس کے ليے تنگی کا جینا ہوگا اور قیامت کے دن ہم اس کو اندھا اٹھائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(20,125,'20:125','وہ کہے گا کہ اے میرے رب، تو نے مجھ کو اندھا کیو ں اٹھایا، میں تو آنکھوں والا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(20,126,'20:126','ارشاد ہوگا کہ اسی طرح تمھارے پاس ہماری نشانیاں آئیں تو تم نے ان کا کچھ خیال نہ کیا تو اسی طرح آج تمھارا کچھ خیال نہ کیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(20,127,'20:127','اور اسی طرح ہم بدلہ دیں گے اس کو جو حد سے گزر جائے اور اپنے رب کی نشانیوں پر ایمان نہ لائے اور آخرت کا عذاب بڑا سخت ہے اور بہت باقی رہنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(20,128,'20:128','کیا لوگوں کو اس بات سے سمجھ نہ آئی کہ ان سے پہلے ہم نے کتنے گروہ ہلاک کردئے یہ ان کی بستیوں میں چلتے ہیں بے شک اس میں اہل عقل کے ليے بڑی نشانیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(20,129,'20:129','اور اگر تمھارے رب کی طرف سے ایک بات پہلے طے نہ ہوچکی ہوتی اور مہلت کی ایک مدت مقرر نہ ہوتی تو ضرور ان کا فیصلہ چکا دیا جاتا');
INSERT INTO "ur_wahid_uddin" VALUES(20,130,'20:130','پس جو یہ کہتے ہیں اس پر صبر کرو اور اپنے رب کی حمد کے ساتھ اس کی تسبیح کرو، سورج نکلنے سے پہلے اوراس کے ڈوبنے سے پہلے، اور رات کے اوقات میں بھی تسبیح کرو اور دن کے کناروں پر بھی، تاکہ تم راضی ہوجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(20,131,'20:131','اور ہرگز ان چیزوں کی طرف آنکھ اٹھاکر بھی نہ دیکھو جن کو ہم نے ان کے کچھ گروہوں کو ان کی آزمائش کے ليے دے رکھا ہے اورتمھارے رب کا رزق زیادہ بہتر ہے اور باقی رہنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,132,'20:132','اور اپنے لوگوں کو نماز کا حکم دو اور اس کے پابند رہو ہم تم سے کوئی رزق نہیں مانگتے رزق تو تم کو ہم دیں گے اور بہتر انجام تو تقویٰ ہی کے ليے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(20,133,'20:133','اور لوگ کہتے ہیں کہ یہ اپنے رب کے پاس سے ہمارے ليے کوئی نشانی کیوں نہیں لاتے کیا ان کو اگلی کتابوں کی دلیل نہیں پہنچی');
INSERT INTO "ur_wahid_uddin" VALUES(20,134,'20:134','اور اگر ہم ان کو اس سے پہلے کسی عذاب سے ہلاک کردیتے تو وہ کہتے کہ اے ہمارے رب، تو نے ہمارے پاس رسول کیوں نہ بھیجا کہ ہم ذلیل اور رسوا ہونے سے پہلے تیری نشانیوں کی پیروی کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(20,135,'20:135','کہو کہ ہر ایک منتظر ہے تو تم بھی انتظار کرو آئندہ تم جان لو گے کہ کون سیدھی راہ والا ہے اور کون منزل تک پہنچا');
INSERT INTO "ur_wahid_uddin" VALUES(21,1,'21:1','لوگوں کے ليے ان کا حساب نزدیک آپہنچا اور وہ غفلت میں پڑے ہوئے اعراض کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,2,'21:2','ان کے رب کی طرف سے جو بھی نئی نصیحت ان کے پاس آتی ہے، وہ اس کو ہنسی کرتے ہوئے سنتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,3,'21:3','ان کے دل غفلت میں پڑے ہوئے ہیں اور ظالموں نے آپس میں یہ سرگوشی کی کہ یہ شخص تو تمھارے ہی جیسا ایک آدمی ہے پھر تم کیوں آنکھوں دیکھے اس کے جادو میں پھنستے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,4,'21:4','رسول نے کہا کہ میرا رب ہر بات کو جانتا ہے، خواہ وہ آسمان میں ہو یا زمین میں اور وہ سننے والا ، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,5,'21:5','بلکہ وہ کہتے ہیں ، یہ پرا گندہ خواب ہیں بلکہ اس کو اس نے گھڑ لیا ہے بلکہ وہ ایک شاعر هے اس کو چاہيے کہ ہمارے پاس اس طرح کی کوئی نشانی لائے، جس طرح کی نشانیوں کے ساتھ پچھلے رسول بھیجے گئے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,6,'21:6','ان سے پہلے کسی بستی کے لوگ بھی جن کو ہم نے ہلاک کیا، ایمان نہیں لائے تو کیا یہ لوگ ایمان لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(21,7,'21:7','اور تم سے پہلے بھی جس کو ہم نے رسول بناکر بھیجا، آدمیوں ہی میں سے بھیجا ہم ان کی طرف وحی بھیجتے تھے پس تم اہل کتاب سے پوچھ لو، اگر تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(21,8,'21:8','اور ہم نے ان رسولوں کو ایسے جسم نہیں دئے کہ وہ کھانا نہ کھاتے ہوں اور وہ ہمیشہ رہنے والے نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,9,'21:9','پھر ہم نے ان سے وعدہ پورا کیا پس ان کو اور جس جس کو ہم نے چاہا، بچالیا اور ہم نے حد سے گزرنے والوں کو ہلاک کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(21,10,'21:10','ہم نے تمھاری طرف ایک کتاب اتاری ہے جس میں تمھاری یاد دہانی ہے، پھر کیا تم سمجھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,11,'21:11','اورکتنی ہی ظالم بستیاں ہیں جن کو ہم نے پیس ڈالا اور ان کے بعد دوسری قوم کو اٹھایا');
INSERT INTO "ur_wahid_uddin" VALUES(21,12,'21:12','پس جب انھوں نے ہمارا عذاب آتے دیکھا تو وہ اس سے بھاگنے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(21,13,'21:13','بھاگو مت اور اپنے سامان عیش کی طرف اور اپنے مکانوں کی طرف واپس چلو، تاکہ تم سے پوچھا جائے');
INSERT INTO "ur_wahid_uddin" VALUES(21,14,'21:14','انھوں نے کہا، ہائے ہماری کم بختی، بے شک ہم لوگ ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,15,'21:15','پس وہ یہی پکارتے رہے یہاں تک کہ ہم نے ان کو ایسا کردیا جیسے کھیتی کٹ گئی ہو اور آگ بجھ گئی ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,16,'21:16','اور ہم نے آسمان اور زمین کو اور جو کچھ ان کے درمیان ہے کھیل کے طور پر نہیں بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(21,17,'21:17','اگر ہم کوئی کھیل بنانا چاہتے تو اس کو ہم اپنے پاس سے بنالیتے، اگر ہم کو یہ کرنا ہوتا');
INSERT INTO "ur_wahid_uddin" VALUES(21,18,'21:18','بلکہ ہم حق کو باطل پر ماریں گے تو وہ اس کا سر توڑ دے گا تو وہ دفعةًجاتا رہے گا اور تمھارے ليے ان باتوں سے بڑی خرابی ہے جو تم بیان کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,19,'21:19','اور اسی کے ہیں جو آسمانوں اور زمین میں ہیں اور جو اس کے پاس ہیں وہ اس کی عبادت سے سرتابی نہیں کرتے اورنہ کاہلی کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,20,'21:20','وہ رات دن اس کو یاد کرتے ہیں ، وہ کبھی نہیں تھکتے');
INSERT INTO "ur_wahid_uddin" VALUES(21,21,'21:21','کیا انھوں نے زمین میں سے معبود ٹھہرائے ہیں جو کسی کو زندہ کرتے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(21,22,'21:22','اگر ان دونوں میں اللہ کے سوا معبود ہوتے تو دونوں درہم برہم ہوجاتے پس اللہ، عرش کا مالک، ان باتوں سے پاک ہے جو یہ لوگ بیان کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,23,'21:23','وہ جو کچھ کرتا ہے اس پر وہ پوچھا نہ جائے گا اور ان سے پوچھ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(21,24,'21:24','کیا انھوں نے خدا کے سوا اور معبود بنائے ہیں ان سے کہو کہ تم اپنی دلیل لاؤ یہی بات ان لوگوں کی ہے جو میرے ساتھ ہیں اور یہی بات ان لوگوں کی ہے جو مجھ سے پہلے ہوئے بلکہ ان میں سے اکثر حق کو نہیں جانتے پس وہ اعراض کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,25,'21:25','اور ہم نے تم سے پہلے کوئی ایسا پیغمبر نہیں بھیجا جس کی طرف ہم نے یہ وحی نہ کی ہو کہ میرے سوا کوئی معبود نہیں ، پس تم میری عبادت کرو');
INSERT INTO "ur_wahid_uddin" VALUES(21,26,'21:26','اور وہ کہتے ہیں کہ رحمن نے اولاد بنائی ہے، وہ اس سے پاک ہے، بلکہ (فرشتے) تو معزز بندے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,27,'21:27','وہ اس سے آگے بڑھ کر بات نہیں کرتے اور وہ اسی کے حکم کے مطابق عمل کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,28,'21:28','اللہ ان کے اگلے اور پچھلے احوال کو جانتا ہے اور وہ سفارش نہیں کرسکتے، مگر اس کے ليے جس کو اللہ پسند کرے اور وہ اس کی ہیبت سے ڈرتے رہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,29,'21:29','اور ان میں سے جو شخص کہے گا کہ اس کے سوا میں معبود ہوں تو ہم اس کو جہنم کی سزا دیں گے ہم ظالموں کو ایسی ہی سزا دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,30,'21:30','کیا انکار کرنے والوں نے نہیں دیکھا کہ آسمان اور زمین دونوں بند تھے، پھر ہم نے ان کو کھول دیا اور ہم نے پانی سے ہر جاندار چیز کو بنایا کیا پھر بھی وہ ایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(21,31,'21:31','اور ہم نے زمین میں پہاڑ بنائے کہ وہ ان کو لے کر جھک نہ جائے اور اس میں ہم نے کشادہ راستے بنائے تاکہ لوگ راہ پائیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,32,'21:32','اور ہم نے آسمان کو ایک محفوظ چھت بنایا اور وہ اس کی نشانیوں سے اعراض کيے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,33,'21:33','اور وہی ہے جس نے رات اور دن اور سورج اور چاند بنائے سب ایک ایک مدار میں تیر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,34,'21:34','اور ہم نے تم سے پہلے بھی کسی انسان کو ہمیشہ کی زندگی نہیں دی تو کیا اگر تم کو موت آجائے تو وہ ہمیشہ رہنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,35,'21:35','ہرجان کو موت کا مزہ چکھنا ہے اور ہم تم کو بری حالت اور اچھی حالت سے آزماتے ہیں پرکھنے کے ليے اور تم سب ہماری طرف لوٹائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(21,36,'21:36','اور منکر لوگ جب تم کو دیکھتے ہیں تو وہ سب تم کو مذاق بنالیتے ہیں کیا یہی ہے جو تمھارے معبودوں کا ذکر کیا کرتا ہے اور خود یہ لوگ رحمان کے ذکر کا انکار کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,37,'21:37','انسان عجلت کے خمیر سے پیدا ہوا ہے میں تم کو عنقریب اپنی نشانیاں دکھاؤں گا، پس تم مجھ سے جلدی نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(21,38,'21:38','اور لوگ کہتے ہیں کہ یہ وعدہ کب آئے گا، اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,39,'21:39','کاش، ان منکروں کو اس وقت کی خبر ہوتی جب کہ وہ آگ کو نہ اپنے سامنے سے روک سکیں گے اور نہ اپنے پیچھے سے اور نہ ان کو مدد پہنچے گی');
INSERT INTO "ur_wahid_uddin" VALUES(21,40,'21:40','بلکہ وہ اچانک ان پر آجائے گی، پس وہ ان کو بدحواس کردے گی پھر وہ نہ اس کو دفع کرسکیں گے اور نہ ان کو مہلت دی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(21,41,'21:41','اور تم سے پہلے بھی رسولوں کا مذاق اڑایا گیا پھر جن لوگوں نے ان میں سے مذاق اڑایا تھا ان کو اس چیز نے گھیر لیا جس کا وہ مذاق اڑاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,42,'21:42','کہو کہ کون ہے جو رات اور دن میں رحمان سے تمھاری حفاظت کرتا ہے بلکہ وہ لوگ اپنے رب کی یاد دہانی سے اعراض کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,43,'21:43','کیا ان کے ليے ہمارے سوا کچھ معبود ہیں جو ان کو بچالیتے ہیں وہ خود اپنی حفاظت کی قدرت نہیں رکھتے اور نہ ہمارے مقابلہ میں کوئی ان کا ساتھ دے سکتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,44,'21:44','بلکہ ہم نے ان کو اور ان کے باپ دادا کو دنیا کا سامان دیا یہاں تک کہ اسی حال میں ان پر لمبی مدت گزر گئی کیا وہ نہیں دیکھتے کہ ہم زمین کو اس کے اطراف سے گھٹاتے چلے جارہے ہیں پھر کیا یہی لوگ غالب رہنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,45,'21:45','کہو کہ میں بس وحی کے ذریعہ سے تم کو ڈراتا ہوں اور بہرے پکار کو نہیں سنتے جب کہ انھیں ڈرایا جائے');
INSERT INTO "ur_wahid_uddin" VALUES(21,46,'21:46','اور اگر تیرے رب کے عذاب کا جھونکاانھیں لگ جائے تو وہ کہنے لگیں گے کہ ہائے ہماری بد بختی، بے شک ہم ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,47,'21:47','اور ہم قیامت کے دن انصاف کی ترازو رکھیں گے پس کسی جان پر ذرا بھی ظلم نہ ہوگا اور اگر رائی کے دانہ کے برابر بھی کسی کا عمل ہوگا تو ہم اس کو حاضر کردیں گے اور ہم حساب لینے کے ليے کافی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,48,'21:48','اور ہم نے موسیٰ اور ہارون کو فرقان اور روشنی اور نصیحت عطا کی خدا ترسوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(21,49,'21:49','جو بن دیکھے اپنے رب سے ڈرتے ہیں اور وہ قیامت کا خوف رکھنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,50,'21:50','اور یہ ایک بابرکت یاد دہانی ہے جو ہم نے اتاری ہے، تو کیا تم اس کے منکر ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,51,'21:51','اور ہم نے اس سے پہلے ابراہیم کو اس کی ہدایت عطا کی اور ہم اس کو خوب جانتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,52,'21:52','جب اس نے اپنے باپ اور اپنی قوم سے کہا کہ یہ کیا مورتیں ہیں جن پر تم جمے بیٹھے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,53,'21:53','انھوں نے کہا کہ ہم نے اپنے باپ داداکو ان کی عبادت کرتے ہوئے پایا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,54,'21:54','ابرہیم نے کہا کہ بے شک تم اور تمھارے باپ دادا ایک کھلی ہوئی گمراہی میں مبتلا رہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,55,'21:55','انھوں نے کہا، کیا تم ہمارے پاس سچی بات لائے ہو یا تم مذاق کر رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,56,'21:56','ابراہیم نے کہا، بلکہ تمھارا رب وہ ہے جو آسمانوں اور زمین کا رب ہے جس نے ان کو پیدا کیا اور میں اس بات کی گواہی دینے والوں میں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(21,57,'21:57','اور خدا کی قسم میں تمھارے بتوں کے ساتھ ایک تدبیر کروں گا جب کہ تم پیٹھ پھیر کر چلے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(21,58,'21:58','پس اس نے ان کو ٹکڑے ٹکڑے کردیا سوا ان کے ایک بڑے کے تاکہ وہ اس کی طرف رجوع کریں');
INSERT INTO "ur_wahid_uddin" VALUES(21,59,'21:59','انھوں نے کہا کہ کس نے ہمارے بتوں کے ساتھ ایسا کیا ہے بے شک وہ بڑا ظالم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,60,'21:60','لوگوں نے کہا کہ ہم نے ایک جوان کو ان کا تذکرہ کرتے ہوئے سنا تھا جس کو ابرہیم کہا جاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,61,'21:61','انھوں نے کہا کہ اس کو سب آدمیوں کے سامنے حاضر کرو، تاکہ وہ دیکھیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,62,'21:62','انھوں نے کہا کہ اے ابراہیم، کیا ہمارے معبودوں کے ساتھ تم نے ایسا کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,63,'21:63','ابراہیم نے کہا، بلکہ ان کے اس بڑے نے ایسا کیا ہے تو ان سے پوچھ لو اگر یہ بولتے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(21,64,'21:64','پھر انھوں نے اپنے جی میں سوچا، پھر وہ کہنے لگے کہ حقیقت میں تم ہی ناحق پر ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,65,'21:65','پھر اپنے سروں کو جھکالیا اے ابراہیم، تم جانتے ہو کہ یہ بولتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,66,'21:66','ابرہیم نے کہا، کیا تم خدا کے سوا ایسی چیزوں کی عبادت کرتے ہو جو تم کو نہ کوئی فائدہ پہنچاسکیں اور نہ کوئی نقصان');
INSERT INTO "ur_wahid_uddin" VALUES(21,67,'21:67','افسوس ہے تم پر بھی اور ان چیزوں پر بھی جن کی تم اللہ کے سوا عبادت کرتے ہو کیا تم سمجھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,68,'21:68','انھوں نے کہا کہ اس کو آگ میں جلا دو اور اپنے معبودوں کی مدد کرو، اگر تم کو کچھ کرنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,69,'21:69','ہم نے کہا کہ اے آگ، تو ابراہیم کے ليے ٹھنڈک اور سلامتی بن جا');
INSERT INTO "ur_wahid_uddin" VALUES(21,70,'21:70','اور انھوں نے اس کے ساتھ برائی کرنا چاہا تو ہم نے انھیں لوگوں کو ناکام بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(21,71,'21:71','اور ہم نے اس کو اور لوط کو اس زمین کی طرف نجات دے دی جس میں ہم نے دنیا والوں کے ليے برکتیں رکھی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,72,'21:72','اور ہم نے اس کو اسحاق دیا اور مزید برآں یعقوب اور ہم نے ان سب کو نیک بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(21,73,'21:73','اور ہم نے ان کو امام بنایا جو ہمارے حکم سے رہنمائی کرتے تھے اور ہم نے ان کو نیک عملی اور نماز کی اقامت اور زکوٰۃ کی ادائیگی کا حکم بھیجا اور وہ ہماری عبادت کرنے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,74,'21:74','اور لوط کو ہم نے حکمت اور علم عطا کیا اور اس کو اس بستی سے نجات دی جو گندے کام کرتی تھی بلاشبہ وہ بہت برے، فاسق لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,75,'21:75','اور ہم نے اس کو اپنی رحمت میں داخل کیا بے شک وہ نیکوں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(21,76,'21:76','اور نوح کو جب کہ اس سے پہلے اس نے پکارا تو ہم نے اس کی دعا قبول کی پس ہم نے اس کو اور اس کے لوگوں کو بہت بڑے غم سے نجات دی');
INSERT INTO "ur_wahid_uddin" VALUES(21,77,'21:77','اور ان لوگوں کے مقابلہ میں اس کی مدد کی جنھوں نے ہماری نشانیوں کو جھٹلایا بے شک وہ بہت برے لوگ تھے پس ہم نے ان سب کو غرق کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(21,78,'21:78','اور داؤد اور سلیمان کو جب وہ دونوں کھیت کے بارے میں فیصلہ کر رہے تھے، جب کہ اس میں کچھ لوگوں کی بکریاں رات کے وقت جا پڑیں اور ہم ان کے اس فیصلہ کو دیکھ رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,79,'21:79','پس ہم نے سلیمان کو اس کی سمجھ دے دی اور ہم نے دونوں کو حکمت اور علم عطا کیا تھا اور ہم نے داؤد کے ساتھ تابع کردیا تھا پہاڑوں کو کہ وہ اس کے ساتھ تسبیح کرتے تھے اور پرندوں کو بھی اور ہم ہی کرنے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,80,'21:80','اور ہم نے اس کو تمھارے ليے ایک جنگی لباس کی صنعت سکھائی، تاکہ وہ تم کو لڑائی میں محفوظ رکھے تو کیا تم شکر کرنے والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,81,'21:81','اور ہم نے سلیمان کے ليے تیز ہوا کو مسخر کردیا جو اس کے حکم سے اس سرزمین کی طرف چلتی تھی جس میں ہم نے برکتیں رکھی ہیں اور ہم ہر چیز کو جاننے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,82,'21:82','اور شیاطین میں سے بھی ہم نے اس کے تابع کردیا تھا جو اس کے ليے غوطہ لگاتے تھے اور اس کے سوا دوسرے کام کرتے تھے اور ہم ان کو سنبھالنے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,83,'21:83','اور ایوب کو جب کہ اس نے اپنے رب کو پکارا کہ مجھ کو بیماری لگ گئی ہے اور تو سب رحم کرنے والوں سے زیادہ رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,84,'21:84','تو ہم نے اس کی دعا قبول کی اور اس کو جو تکلیف تھی اس کو دور کردیا اور ہم نے اس کو اس کا کنبہ عطا کیا اور اسی کے ساتھ اس کے برابر اور بھی، اپنی طرف سے رحمت اور نصیحت، عبادت کرنے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(21,85,'21:85','اور اسماعیل اور ادریس اور ذوالکفل کو، یہ سب صبر کرنے والوں میں سے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,86,'21:86','اور ہم نے ان کو اپنی رحمت میں داخل کیا بے شک وہ نیک عمل کرنے والوں میں سے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,87,'21:87','اور مچھلی والے (یونس) کو، جب کہ وہ اپنی قوم سے برہم ہوکر چلا گیا پھر اس نے یہ سمجھا کہ ہم اس کو نہ پکڑیں گے، پھر اس نے اندھیرے میں پکارا کہ تیرے سوا کوئی معبود نہیں ، تو پاک ہے بے شک میں قصوروار ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(21,88,'21:88','تو ہم نے اس کی دعا قبول کی اور اس کو غم سے نجات دی اور اسی طرح ہم ایمان والوں کو نجات دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,89,'21:89','اور زکریا کو، جب کہ اس نے اپنے رب کو پکارا کہ اے میرے رب، تو مجھ کو اکیلا نہ چھوڑ اور تو بہترین وارث ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,90,'21:90','تو ہم نے اس کی دعا قبول کی اور اس کو یحییٰ عطا کیا اور اس کی بیوی کو اس کے ليے درست کردیا یہ لوگ نیک کاموں میں دوڑتے تھے اور ہم کو امید اور خوف کے ساتھ پکارتے تھے اور ہمارے آگے جھکے ہوئے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,91,'21:91','اور وہ خاتون جس نے اپنی ناموس کو بچایا تو ہم نے اس کے اندر اپنی روح پھونک دی اور اس کو اور اس کے بیٹے کو دنیا والوں کے ليے ایک نشانی بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(21,92,'21:92','اور یہ تمھاری امت ایک ہی امت ہے اور میں ہی تمھارا رب ہوں تو تم میری عبادت کرو');
INSERT INTO "ur_wahid_uddin" VALUES(21,93,'21:93','اور انھوں نے اپنا دین اپنے اندر ٹکڑے ٹکڑے کر ڈالا سب ہمارے پاس آنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,94,'21:94','پس جو شخص نیک عمل کرے گا اور وہ ایمان والا ہوگا تو اس کی محنت کی ناقدری نہ ہوگی، اور ہم اس کو لکھ لیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(21,95,'21:95','اور جس بستی والوں کے ليے ہم نے ہلاکت مقرر کردی ہے، ان کے ليے حرام ہے کہ وہ رجوع کریں');
INSERT INTO "ur_wahid_uddin" VALUES(21,96,'21:96','یہاں تک کہ جب یاجوج اور ماجوج کھول دئے جائیں گے اور وہ ہر بلندی سے نکل پڑیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(21,97,'21:97','اور سچا وعدہ نزدیک آلگے گا تو ان لوگوں کی نگاہیں پھٹی رہ جائیں گی جنھوں نے انکار کیا تھا ہائے ہماری کم بختی، ہم اس سے غفلت میں پڑے رہے بلکہ ہم ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(21,98,'21:98','بے شک تم اور جن کو تم خدا کے سوا پوجتے تھے، سب جہنم کا ایندھن ہیں وہیں تم کو جانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,99,'21:99','اگر یہ واقعی معبود ہوتے تو وہ اس میں نہ پڑتے اور سب اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(21,100,'21:100','اس میں ان کے ليے چلّانا ہے اور وہ اس میں کچھ نہ سنیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(21,101,'21:101','بے شک جن کے ليے ہماری طرف سے بھلائی کا پہلے فیصلہ ہوچکا ہے، وہ اس سے دور رکھے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(21,102,'21:102','وہ اس کی آہٹ بھی نہ سنیں گے اور وہ اپنی پسندیدہ چیزوں میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(21,103,'21:103','ان کو بڑی گھبراہٹ غم میں نہ ڈالے گی اور فرشتے ان کا استقبال کریں گے یہ ہے تمھارا وہ دن جس کا تم سے وعدہ کیا گیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(21,104,'21:104','جس دن ہم آسمان کو لپیٹ دیں گے جس طرح طومار میں اوراق لپیٹ دئے جاتے ہیں جس طرح پہلے ہم نے تخلیق کی ابتدا کی تھی، اسی طرح ہم پھر اس کا اعادہ کریں گے یہ ہمارے ذمہ وعدہ ہے اور ہم اس کو کرکے رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(21,105,'21:105','اور زبور میں ہم نصیحت کے بعد لکھ چکے ہیں کہ زمین کے وارث ہمارے نیک بندے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(21,106,'21:106','اس میں ایک بڑی خبر ہے عبادت گزار لوگوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(21,107,'21:107','اور ہم نے تم کو سارے جهان کے ليے رحمت هي بناکر بھیجا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(21,108,'21:108','کہو کہ میرے پاس جو وحی آتی ہے وہ یہ ہے کہ تمھارا معبود صرف ایک معبود ہے، تو کیا تم اطاعت گزار بنتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,109,'21:109','پس اگر وہ اعراض کریں تو کہہ دو کہ میں تم کو صاف طور پر اطلاع کرچکا ہوں اور میں نہیں جانتا کہ وہ چیز جس کا تم سے وعدہ کیا جارہا ہے، قریب ہے یا دور');
INSERT INTO "ur_wahid_uddin" VALUES(21,110,'21:110','بے شک وہ کھلی بات کو بھی جانتا ہے اور اس بات کو بھی جس کو تم چھپاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,111,'21:111','اور مجھ کو نہیں معلوم شاید وہ تمھارے ليے امتحان ہو اور فائدہ اٹھالینے کی ایک مہلت ہو');
INSERT INTO "ur_wahid_uddin" VALUES(21,112,'21:112','پیغمبر نے کہا کہ اے میرے رب، حق کے ساتھ فیصلہ کردے اور ہمارا رب رحمان ہے، اسی سے ہم ان باتوں پر مدد مانگتے ہیں جو تم بیان کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(22,1,'22:1','اے لوگو، اپنے رب سے ڈرو بے شک قیامت کا بھونچال بڑی بھاری چیز ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,2,'22:2','جس دن تم اسے دیکھو گے، ہر دودھ پلانے والی اپنے دودھ پیتے بچے کو بھول جائے گی اورہر حمل والی اپنا حمل ڈال دے گی اور لوگ تم کو مدہوش نظر آئیں گے حالاں کہ وہ مدہوش نہ ہوں گے، بلکہ اللہ کا عذاب بڑا ہی سخت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,3,'22:3','اور لوگوں میں کوئی ایسا بھی ہے جو علم کے بغیر اللہ کے بارے میں جھگڑتا ہے اور ہر سرکش شیطان کی پیروی کرنے لگتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,4,'22:4','اس کی نسبت یہ لکھ دیا گیا ہے کہ جو شخص اس کو دوست بنائے گا، وہ اس کو بے راہ کردے گا اور اس کو عذاب جہنم کا راستہ دکھائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(22,5,'22:5','اے لوگو! اگر تم دوبارہ جی اٹھنے کے متعلق شک میں ہو تو ہم نے تم کو مٹی سے پیدا کیا ہے، پھر نطفہ سے، پھر خون کے لوتھڑے سے، پھر گوشت کی بوٹی سے،شکل والی اور بغیر شکل والی بھی، تاکہ ہم تم پر واضح کریں اور ہم رحموں میں ٹھہرادیتے ہیں جو چاہتے ہیں ایک معین مدت تک پھر ہم تم کو بچہ بناکر باہر لاتے ہیں پھر تاکہ تم اپنی پوری جوانی تک پہنچ جاؤ اور تم میں سے کوئی شخص پہلے ہی مرجاتا ہے اور کوئی شخص بد ترین عمر تک پہنچادیا جاتا ہے تاکہ وہ جان لینے کے بعدپھر کچھ نہ جانے اور تم زمین کو دیکھتے ہو کہ خشک پڑی ہے، پھر جب ہم اس پر پانی برساتے ہیں تو وہ تازہ ہوگئی اور ابھر آئی اور وہ طرح طرح کی خوشنما چیزیں اگاتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,6,'22:6','یہ اس ليے کہ اللہ ہی حق ہے اور وہ بے جانوں میں جان ڈالتا ہے، اور وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,7,'22:7','اور یہ کہ قیامت آنے والی ہے، اس میں کوئی شک نہیں اور اللہ ضرور ان لوگوں کو اٹھائے گا جو قبروں میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(22,8,'22:8','اور لوگوں میں کوئی شخص ہے جو اللہ کی بات میں جھگڑتا ہے، علم اور ہدایت اور روشن کتاب کے بغیر');
INSERT INTO "ur_wahid_uddin" VALUES(22,9,'22:9','تکبر کرتے ہوئے تاکہ وہ اللہ کی ر اہ سے بے راہ کردے اس کے ليے دنیا میں رسوائی ہے اور قیامت کے دن ہم اس کو جلتی ہوئی آگ کا عذاب چکھائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(22,10,'22:10','یہ تمھارے ہاتھ کے کيے ہوئے کاموں کا بدلہ ہے اور اللہ اپنے بندوں پر ظلم کرنے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(22,11,'22:11','اور لوگوں میں کوئی ہے جو کنارے پر رہ کر اللہ کی عبات کرتا ہے پس اگر اس کو کوئی فائدہ پہنچا تو وہ اس عبادت پر قائم ہوگیا اور اگر کوئی آزمائش پیش آئی تو الٹا پھر گیا اس نے دنیا بھی کھودی اور آخرت بھی، یہی کھلا ہوا خسارہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,12,'22:12','وہ خدا کے سوا ایسی چیز کو پکارتا ہے جو نہ اس کو نقصان پہنچاسکتی اور نہ اس کو نفع پہنچا سکتی یہ انتہا درجہ کی گمراہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,13,'22:13','وہ ایسی چیز کو پکارتا ہے جس کا نقصان اس کے نفع سے قریب تر ہے کیسا برا کار ساز ہے اور کیسا برا رفیق');
INSERT INTO "ur_wahid_uddin" VALUES(22,14,'22:14','بے شک اللہ ان لوگوں کو جو ایمان لائے اور نیک عمل کئے، ایسی جنتوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی اللہ کرتا ہے جو وہ چاہتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,15,'22:15','جو شخص یہ گمان رکھتا ہو کہ خدا دنیا اور آخرت میں اس کی مد دنہیں کرے گا تو اس کو چاہيے کہ وہ ایک رسی آسمان تک تانے پھر اس کو کاٹ ڈالے اور دیکھے کہ کیا اس کی تدبیر اس کے غصہ کو دور کرنے والی بنتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,16,'22:16','اور اس طرح ہم نے قرآن کو کھلی کھلی دلیلوں کے ساتھ اتارا ہے اور بے شک اللہ جسے چاہتا ہے ہدایت دے دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,17,'22:17','اس میں کوئی شک نہیں کہ جو لوگ ایمان لائے اور جنھوں نے یہودیت اختیار کی اور صابی اور نصاریٰ اور مجوس اور جنھوں نے شرك کیا، اللہ ان سب کے درمیان قیامت کے روز فیصلہ فرمائے گا بے شک اللہ ہر چیز سے واقف ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,18,'22:18','کیا تم نہیں دیکھتے کہ اللہ ہی کے آگے سجدہ کرتے ہیں جو آسمانوں میں ہیں اور جو زمین میں ہیں اور سورج اور چاند اور ستارے اور پہاڑ اور درخت اور چوپائے اور بہت سے انساناور بہت سے ایسے ہیں جن پر عذاب ثابت ہوچکا ہے اور جس کو خدا ذلیل کردے تو اس کو کوئی عزت دینے والا نہیں بے شک اللہ کرتا ہے جو وہ چاہتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,19,'22:19','یہ دو فریق ہیں جنھوں نے اپنے رب کے بارے میں جھگڑا کیا پس جنھوں نے انکار کیا، ان کے ليے آگ کے کپڑے قطع کيے جائیں گے ان کے سروں کے اوپرسے کھولتا ہوا پانی ڈالا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(22,20,'22:20','اس سے ان کے پیٹ کی چیزیں تک گل جائیں گی اور کھالیں بھی');
INSERT INTO "ur_wahid_uddin" VALUES(22,21,'22:21','اور ان کے ليے وہاں لوہے کے ہتھوڑے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(22,22,'22:22','جب بھی وہ گھبراکر اس سے باہر نکلنا چاہیں گے تو وہ پھر اس میں ڈھکیل دئے جائیں گے، اور چکھتے رہو جلنے کا عذاب');
INSERT INTO "ur_wahid_uddin" VALUES(22,23,'22:23','بے شک جو لوگ ایمان لائے اور نیک عمل کیا، اللہ ان کو ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی ان کو وہاں سونے کے گنگن اور موتی پہنائے جائیں گے اور وہاں ان کی پوشاک ریشم ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(22,24,'22:24','اور ان کو پاکیزہ قول کی ہدایت بخشی گئی تھی اور ان کو خدائے حمید کا راستہ دکھایا گیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(22,25,'22:25','بے شک جن لوگوں نے انکار کیا اور وہ لوگوں کو اللہ کی راہ سے اور مسجد حرام سے روکتے ہیں ، جس کو ہم نے لوگوں کے ليے بنایا ہے، جس میں مقامی باشندے اور باہر سے آنے والے برابر ہیں اور جو اس مسجد میں راستی سے ہٹ کر ظلم کا طریقہ اختیا رکرے گا، اس کو ہم درد ناک عذاب کا مزہ چکھائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(22,26,'22:26','اور جب ہم نے ابراہیم کو بیت اللہ کی جگہ بتادی کہ میرے ساتھ کسی چیز کو شریک نہ کرنا اور میرے گھر کو پاک رکھنا، طواف کرنے والوں کے ليے اور قیام کرنے والوں کے ليے اور رکوع اور سجدہ کرنے والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(22,27,'22:27','اور لوگوں میں حج کا اعلان کردو، وہ تمھارے پاس آئیں گے پیروں پر چل کر اور دبلے اونٹوں پر سوار ہوکر جو کہ دور دراز راستوں سے آئیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(22,28,'22:28','تاکہ وہ اپنے فائدہ کی جگہوں پر پہنچیں اور چند معلوم دنوں میں ان چوپایوں پر اللہ کا نام لیں جو اس نے انھیں بخشے ہیں پس اس میں سے کھاؤ اور مصیبت زدہ محتاج کو کھلاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(22,29,'22:29','تو چاہيے کہ وہ اپنا میل کچیل ختم کردیں اور اپنی نذریں پوری کریں اور اس قدیم گھر کا طواف کریں');
INSERT INTO "ur_wahid_uddin" VALUES(22,30,'22:30','یہ بات ہوچکی اور جو شخص اللہ کی حرمتوں کی تعظیم کرے گا تو وہ اس کے حق میں اس کے رب کے نزدیک بہتر ہے اور تمھارے ليے چوپائے حلال کر دئے گئے ہیں ، سوا ان کے جو تم کو پڑھ کر سنائے جاچکے ہیں ، تو تم بتوں کی گندگی سے بچو اور جھوٹی بات سے بچو');
INSERT INTO "ur_wahid_uddin" VALUES(22,31,'22:31','اللہ کی طرف یکسو رہو، اس کے ساتھ شریک نہ ٹھہراؤ اور جو شخص اللہ کے ساتھ شرک کرتا ہے تو گویا وہ آسمان سے گر پڑا پھر چڑیاں اس کو اچک لیں یا ہوا اس کو کسی دور دراز مقام پر لے جاکر ڈال دے');
INSERT INTO "ur_wahid_uddin" VALUES(22,32,'22:32','یہ بات ہوچکی اور جو شخص اللہ کے شعائر کا پورا لحاظ رکھے گا تو یہ دل کے تقویٰ کی بات ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,33,'22:33','تم کو ان سے ایک مقرر وقت تک فائدہ اٹھانا ہے پھر ان کو قربانی کے ليے قدیم گھر کی طرف لے جانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,34,'22:34','اور ہم نے ہر امت کے ليے قربانی کرنا مقرر کیا تاکہ وہ ان چوپایوں پر اللہ کا نام لیں جو اس نے ان کو عطا کيے ہیں پس تمھارا معبودایک ہی معبودہے تو تم اسی کے ہوکر رہو اور عاجزی کرنے والوں کو بشارت دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(22,35,'22:35','جن کا حال یہ ہے کہ جب اللہ کا ذکر کیا جاتا ہے تو ان کے دل کانپ اٹھتے ہیں اور جو اُن پر پڑے، اس کو وہ سہنے والے اور نماز کی پابندی کرنے والے اور جو کچھ ہم نے ان کو دیا ہے وہ اس میں سے خرچ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(22,36,'22:36','اور قربانی کے اونٹوں کو ہم نے تمھارے ليے اللہ کی یاد گار بنایا ہے ان میں تمھارے ليے بھلائی ہے پس ان کو کھڑا کرکے ان پر اللہ کا نام لو پھر جب وہ کروٹ کے بل گر پڑیں تو ان میں سے کھاؤ اوربے سوال محتاج اور سائل کو کھلاؤ اس طرح ہم نے ان جانوروں کو تمھارے ليے مسخر کردیا تاکہ تم شکر ادا کرو');
INSERT INTO "ur_wahid_uddin" VALUES(22,37,'22:37','اور اللہ کو نہ ان کا گوشت پہنچتا ہے اور نہ ان کا خون بلکہ اللہ کو صرف تمھارا تقویٰ پہنچتا ہے اس طرح اللہ نے ان کو تمھارے ليے مسخر کردیا ہے، تاکہ تم اللہ کی بخشی ہوئی ہدایت پر اس کی بڑائی بیان کرو اور نیکی کرنے والوں کو خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(22,38,'22:38','بے شک اللہ ان لوگوں کی مدافعت کرتا ہے جو ایمان لائے بے شک اللہ بد عہدوں اور ناشکروں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(22,39,'22:39','اجازت دے دی گئی ان لوگوں کو جن سے لڑائی کی جارہی ہے اس وجہ سے کہ ان پر ظلم کیا گیا ہے اور بے شک اللہ ان کی مدد پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,40,'22:40','وہ لوگ جو اپنے گھروں سے بے وجہ نکالے گئے صرف اس ليے کہ و ہ کہتے ہیں کہ ہمارا رب اللہ ہے اور اگر اللہ لوگوں کو ایک دوسرے کے ذریعے دفع نہ کرتا رہے تو خانقاہیں اور گرجا اور عبادت خانے اور مسجدیں جن میں اللہ کا نام کثرت سے لیا جاتا ہے، ڈھادئے جاتے اور اللہ ضرور اس کی مدد کرے گا جو اللہ کی مدد کرے بے شک اللہ زبردست ہے، زور والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,41,'22:41','یہ وہ لوگ ہیں جن کو اگر ہم زمین میں غلبہ دیں تو وہ نماز کا اہتمام کریں گے اور زکوٰۃ ادا کریں گے اور معروف کا حکم دیں گے اور منکر سے روکیں گے اور سب کاموں کا انجام خدا ہی کے اختیار میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,42,'22:42','اور اگر وہ تمھیں جھٹلائیں تو ان سے پہلے قوم نوح اور عاد اور ثمود جھٹلا چکے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(22,43,'22:43','اور قوم ابراہیم اور قوم لوط');
INSERT INTO "ur_wahid_uddin" VALUES(22,44,'22:44','اور مدین کے لوگ بھی اور موسیٰ کو جھٹلایا گیا پھر میں نے منکروں کو ڈھیل دی پھر میں نے ان کو پکڑ لیا پس کیسا ہوا میرا عذاب');
INSERT INTO "ur_wahid_uddin" VALUES(22,45,'22:45','پس کتنی ہی بستیاں ہیں جن کو ہم نے ہلاک کردیا اور وہ ظالم تھیں پس اب وہ اپنی چھتوں پر الٹی پڑی ہیں اور کتنے ہی بیکار کنوئیں اور کتنے پختہ محل جو ویران پڑے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(22,46,'22:46','کیا یہ لوگ زمین میں چلے پھرے نہیں کہ ان کے دل ایسے ہوجاتے کہ وہ ان سے سمجھتے یا ان کے کان ایسے ہوجاتے کہ وہ ان سے سنتے کیوں کہ آنکھیں اندھی نہیں ہوتیں بلکہ وہ دل اندھے ہوجاتے ہیں جو سینوں میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(22,47,'22:47','اور یہ لوگ تم سے عذاب کے ليے جلدی كيے ہوئے ہیں اور اللہ ہرگز اپنے وعدہ کے خلاف کرنے والا نہیں ہے اور تیرے رب کے یہاں کا ایک دن تمھارے شمار کے اعتبار سے ایک ہزار سال کے برابر ہوتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,48,'22:48','اور کتنی ہی بستیاں ہیں جن کو میں نے ڈھیل دی اور وہ ظالم تھیں پھر میں نے ان کو پکڑ لیا اور میری ہی طرف لوٹ کر آنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,49,'22:49','کہو کہ اے لوگو، میں تمھارے ليے ایک کھلا ہوا ڈرانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(22,50,'22:50','پس جو لوگ ایمان لائے اور اچھے کام کئے، ان کے ليے مغفرت ہے اور عزت کی روزی');
INSERT INTO "ur_wahid_uddin" VALUES(22,51,'22:51','اور جو لوگ ہماری آیتوں کو نیچا دکھانے کے ليے دوڑے، وہی دوزخ والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(22,52,'22:52','اور ہم نے تم سے پہلے جو بھی رسول اور نبی بھیجا تو جب اس نے کچھ پڑھا تو شیطان نے اس کے پڑھنے میں ملا دیا پھر اللہ شیطان کے ڈالے ہوئے کو مٹا دیتا ہے پھر اللہ اپنی آیتوں کو پختہ کردیتا ہے اور اللہ علم والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,53,'22:53','تاکہ جو کچھ شیطان نے ملایا ہے، اس سے وہ ان لوگوں کو جانچے جن کے دلوں میں روگ ہے اور جن کے دل سخت ہیں اور ظالم لوگ مخالفت میں بہت دور نکل گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(22,54,'22:54','اور تاکہ وہ لوگ جن کو علم ملا ہے، وہ جان لیں کہ یہ فی الواقع تیرے رب کی طرف سے ہے، پھر وہ اس پر یقین لائیں اور ان کے دل اس کے آگے جھک جائیں اور اللہ ایمان لانے والوں کو ضرور سیدھا راستہ دکھاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,55,'22:55','اور انکار کرنے والے لوگ ہمیشہ اس کی طرف سے شک میں پڑے رہیں گے، یہاں تک کہ اچانک ان پر قیامت آجائے، یا ایک منحوس دن کا عذاب آجائے');
INSERT INTO "ur_wahid_uddin" VALUES(22,56,'22:56','اس دن سارا اختیار صرف اللہ کو ہوگا وہ ان کے درمیان فیصلہ فرمائے گا پس جو لوگ ایمان لائے اور اچھے کام کيے وہ نعمت کے باغوں میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(22,57,'22:57','اور جنھوں نے انکار کیا اور ہماری آیتوں کو جھٹلایا تو ان کے ليے ذلت کا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,58,'22:58','اور جن لوگوں نے اللہ کی راہ میں اپنا وطن چھوڑا، پھر وہ قتل کردئے گئے یا وہ مرگئے، اللہ ضرور ان کو اچھا رزق دے گا اور بے شک اللہ ہی سب سے بہتر رزق دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,59,'22:59','وہ ان کو ایسی جگہ پہنچائے گا جس سے وہ راضی ہوں گے اور بے شک اللہ جاننے والا، حلم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,60,'22:60','یہ ہوچکا اور جو شخص بدلہ لے ویسا ہی جیسا اس کے ساتھ کیا گیا تھا، اور پھر اس پر زیادتی کی جائے تو اللہ ضرور اس کی مدد کرے گا بے شک اللہ معاف کرنے والا، درگزر کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,61,'22:61','یہ اس ليے کہ اللہ رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے اور اللہ سننے والا، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,62,'22:62','یہ اس ليے کہ اللہ ہی حق ہے اور وہ سب باطل ہیں جنھیں اللہ کو چھوڑ کر لوگ پکارتے ہیں اور بے شک اللہ ہی سب سے اوپر ہے، سب سے بڑا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,63,'22:63','کیا تم نہیں دیکھتے کہ اللہ نے آسمان سے پانی برسایا پھر زمین سر سبز ہوگئی بے شک اللہ باریک بیں ہے، خبر رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,64,'22:64','اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے بے شک اللہ ہی ہے جو بے نیاز ہے، تعریفوں والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,65,'22:65','کیا تم دیکھتے نہیں کہ اللہ نے زمین کی چیزوں کو تمھارے کام میں لگا رکھا ہے اورکشتی کو بھی، وہ اس کے حکم سے سمندر میں چلتی ہے اور اللہ آسمان کو زمین پر گرنے سے تھامے ہوئے ہے، مگر یہ کہ اس کے حکم سے بے شک اللہ لوگوں پر نرمی کرنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,66,'22:66','اور وہی ہے جس نے تم کو زندگی دی، پھر وہ تم کو موت دیتا ہے پھر وہ تم کو زندہ کرے گا بے شک انسان بڑا ہی ناشکرا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,67,'22:67','اور ہم نے ہر امت کے ليے ایک طریقہ مقرر کیا کہ وہ اس کی پیروی کرتے تھے پس وہ اس معاملہ میں تم سے جھگڑا نہ کریں اور تم اپنے رب کی طرف بلاؤ یقیناً تم سیدھے راستہ پر ہو');
INSERT INTO "ur_wahid_uddin" VALUES(22,68,'22:68','اگر وہ تم سے جھگڑا کریں تو کہو کہ اللہ خوب جانتا ہے جو کچھ تم کر رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(22,69,'22:69','اللہ قیامت کے دن تمھارے درمیان اس چیز کا فیصلہ کردے گا جس میں تم اختلاف کر رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(22,70,'22:70','کیا تم نہیں جانتے کہ آسمان اور زمین کی ہر چیز اللہ کے علم میں ہے سب کچھ ایک کتاب میں ہے بے شک یہ اللہ کے ليے آسان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,71,'22:71','اور وہ اللہ کے سوا ان کی عبات کرتے ہیں جن کے حق میں اللہ نے کوئی دلیل نہیں اتاری اور نہ ان کے بارے میں ان کو کوئی علم ہے اور ظالموں کا کوئی مدد گار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(22,72,'22:72','اور جب ان کو ہماری واضح آیتیں پڑھ کر سنائی جاتی ہیں تو تم منکروں کے چہرے پر برے آثار دیکھتے ہو گویا کہ وہ ان لوگوں پر حملہ کردیں گے جو ان کو ہماری آیتیں پڑھ کر سنا رہے ہیں کہو کہ کیا میں تم کو بتاؤں کہ اس سے بد تر چیز کیا ہے وہ آگ ہے اس کا اللہ نے ان لوگوں سے وعدہ کیا ہے جنھوں نے انکار کیا اور وہ بہت بُرا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,73,'22:73','اے لوگو، ایک مثال بیان کی جاتی ہے تو اس کو غور سے سنو، تم لوگ خدا کے سوا جس چیزکو پکارتے ہو، وہ ایک مکھی بھی پیدا نہیں کرسکتے اگرچہ سب کے سب اس کے ليے جمع ہوجائیں اور اگر مکھی ان سے کچھ چھین لے تو وہ اس کو اس سے چھڑا نہیں سکتے مدد چاہنے والے بھی کمزور اور جن سے مدد چاہی گئی وہ بھی کمزور');
INSERT INTO "ur_wahid_uddin" VALUES(22,74,'22:74','انھوں نے اللہ کی قدر نہ پہچانی جیسا کہ اس کے پہچاننے کا حق ہے بے شک اللہ طاقت ور ہے، غالب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,75,'22:75','اللہ فرشتوں میں سے اپنا پیغام پہنچانے والا چنتا ہے، اور انسانوں میں سے بھی بے شک اللہ سننے والا، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(22,76,'22:76','وہ جانتا ہے جو کچھ ان کے آگے ہے اور جو کچھ ان کے پیچھے ہے اور اللہ ہی کی طرف لوٹتے ہیں سارے معاملات');
INSERT INTO "ur_wahid_uddin" VALUES(22,77,'22:77','اے ایمان والو، رکوع اور سجدہ کرو اور اپنے رب کی عبادت کرو اور بھلائی کے کام کرو تاکہ تم کامیاب ہو');
INSERT INTO "ur_wahid_uddin" VALUES(22,78,'22:78','اور اللہ کی راہ میں کوشش کرو جیسا کہ کوشش کرنے کا حق ہے اسی نے تم کو چنا ہے اور اس نے دین کے معاملہ میں تم پر کوئی تنگی نہیں رکھی، تمھارے باپ ابراہیم کا دین اسی نے تمھارا نام مسلم رکھا اس سے پہلے اور اس قرآن میں بھی، تاکہ رسول تم پر گواہ ہو اور تم لوگوں پر گواہ بنو پس نماز قائم کرو اور زکوٰۃ ادا کرو اور اللہ کو مضبوط پکڑو، وہی تمھارا مالک ہے پس کیسا اچھا مالک ہے اورکیسا اچھا مدد گار');
INSERT INTO "ur_wahid_uddin" VALUES(23,1,'23:1','یقیناً فلاح پائی ایمان والوں نے');
INSERT INTO "ur_wahid_uddin" VALUES(23,2,'23:2','جو اپنی نماز میں جھکنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,3,'23:3','اور جو لغو باتوں سے اعراض کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,4,'23:4','اور جو زکوٰۃ ادا کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,5,'23:5','اور جو اپنی شرمگاہوں کی حفاظت کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,6,'23:6','سوا اپنی بیویوں کے اور ان عورتوں کے جو ان کی ملکِ یمین میں ہوں کہ ان پر وہ قابل ملامت نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,7,'23:7','البتہ جو اس کے علاوہ چاہیں تو وہی لوگ زیادتی کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,8,'23:8','اور جو اپنی امانتوں اور اپنے عہد کا خیال رکھنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,9,'23:9','اور جو اپنی نمازوں کی حفاظت کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,10,'23:10','یہی لوگ وارث ہونے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,11,'23:11','جو فردوس کی وراثت پائیں گےوہ اس میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,12,'23:12','اور ہم نے انسان کو مٹی کے خلاصہ سے پیدا کیا');
INSERT INTO "ur_wahid_uddin" VALUES(23,13,'23:13','پھر ہم نے پانی کی ایک بوند کی شکل میں اس کو ایک محفوظ ٹھکانے میں رکھا');
INSERT INTO "ur_wahid_uddin" VALUES(23,14,'23:14','پھر ہم نے پانی کی بوند کو ایک جنین کی شکل دیپھر جنین کو گوشت کا ایک لوتھڑا بنایا پس لوتھڑے کے اندر ہڈیاں پیدا کیں پھر ہم نے ہڈیوں پرگوشت چڑھا دیا پھر ہم نے اس کو ایک نئی صورت میں بنا کھڑا کیا پس بڑا ہی بابرکت ہے اللہ، بہترین پیدا کرنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(23,15,'23:15','پھر اس کے بعد تم کو ضرور مرنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,16,'23:16','پھر تم قیامت کے دن اٹھائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,17,'23:17','اور ہم نے تمھارے اوپر سات راستے بنائے اور ہم مخلوق سے بے خبر نہیں ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(23,18,'23:18','اور ہم نے آسمان سے پانی برسایا ایک اندازے کے ساتھ پھر ہم نے اس کو زمین میں ٹھہرادیا اور ہم اس کو واپس لینے پر قادر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,19,'23:19','پھر ہم نے اس سے تمھارے ليے کھجور اور انگور کے باغ پیدا کئے تمھارے ليے ان میں بہت سے پھل ہیں اور تم ان میں سے کھاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,20,'23:20','اور ہم نے وہ درخت پیدا کیا جو طورِ سینا سے نکلتا ہے، وہ تیل ليے ہوئے اگتا ہے اور کھانے والوں کے لیے سالن بھی');
INSERT INTO "ur_wahid_uddin" VALUES(23,21,'23:21','اورتمھارے ليے مویشیوں میں سبق ہے ہم تم کو ان کے پیٹ کی چیز سے پلاتے ہیں اور تمھارے ليے ان میں بہت فائدے ہیں اور تم ان کو کھاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,22,'23:22','اور تم ان پر اور کشتیوں پر سواری کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,23,'23:23','اور ہم نے نوح کو اس کی قوم کی طرف بھیجا تو اس نے کہا کہ اے میری قوم، تم اللہ کی عبادت کرو اس کے سوا تمھارا کوئی معبود نہیں کیا تم ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,24,'23:24','تو اس کی قوم کے سردار جنھوں نے انکار کیا تھا، انھوں نے کہا کہ یہ تو بس تمھارے جیسا ایک آدمی ہے وہ چاہتا ہے کہ تمھارے اوپر برتری حاصل کرے اور اگر اللہ چاہتا تو وہ فرشتے بھیجتا ہم نے یہ بات اپنے پچھلے بڑوں میں نہیں سنی');
INSERT INTO "ur_wahid_uddin" VALUES(23,25,'23:25','یہ تو بس ایک شخص ہے جس کو جنون ہوگیا ہے پس ایک وقت تک اس کا انتظار کرو');
INSERT INTO "ur_wahid_uddin" VALUES(23,26,'23:26','نوح نے کہا کہ اے میرے رب تو میری مدد فرما کہ انھوں نے مجھ کو جھٹلادیا');
INSERT INTO "ur_wahid_uddin" VALUES(23,27,'23:27','تو ہم نے اس کو وحی کی کہ تم کشتی تیار کرو ہماری نگرانی میں اور ہماری ہدایت کے مطابق تو جب ہمارا حکم آجائے اور زمین سے پانی ابل پڑے تو ہر قسم کے جانوروں میں سے ایک ایک جوڑا لے کر اس میں سوار ہوجاؤ اور اپنے گھر والوں کو بھی، سوا ان کے جن کے بارے میں پہلے فیصلہ ہوچکا ہے اور جنھوں نے ظلم کیا ہے، ان کے معاملہ میں مجھ سے بات نہ کرنا بے شک ان کو ڈوبنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,28,'23:28','پھر جب تم اور تمھارے ساتھی کشتی میں بیٹھ جائیں تو کہو کہ شکر ہے اللہ کا جس نے ہم کو ظالم لوگوں سے نجات دی');
INSERT INTO "ur_wahid_uddin" VALUES(23,29,'23:29','اور کہو کہ اے میرے رب، تو مجھے اتار برکت کا اتارنا اور تو بہتر اتارنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,30,'23:30','بے شک اس میں نشانیاں ہیں اور بے شک ہم بندوں کو آزماتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,31,'23:31','پھر ہم نے ان کے بعد دوسرا گروہ پیدا کیا');
INSERT INTO "ur_wahid_uddin" VALUES(23,32,'23:32','پھر ان میں سے ایک رسول انھیں میں سے بھیجا کہ تم اللہ کی عبادت کرو اس کے سوا تمھارا کوئی معبود نہیں کیا تم ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,33,'23:33','اور اس کی قوم کے سرداروں نے، جنھوں نے انکار کیا اور آخرت کی ملاقات کو جھٹلایا، اور ان کو ہم نے دنیا کی زندگی میں آسودگی دی تھی، کہا یہ تو تمھارے ہی جیسا ایک آدمی ہے وہی کھاتا ہے جو تم کھاتے ہو، اور وہی پیتا ہے جو تم پیتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,34,'23:34','اور اگر تم نے اپنے ہی جیسے ایک آدمی کی بات مانی تو تم بڑے گھاٹے میں رہو گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,35,'23:35','کیا یہ شخص تم سے کہتا ہے کہ جب تم مرجاؤ گے اور مٹی اور ہڈیاں ہوجاؤ گے تو پھر تم نکالے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,36,'23:36','بہت ہی بعید اور بہت ہی بعید ہے جو بات تم سے کہی جارہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,37,'23:37','زندگی تو یہی ہماری دنیا کی زندگی ہے یہیں ہم مرتے ہیں اور جیتے ہیں اور ہم دوبارہ اٹھائے جانے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,38,'23:38','یہ تو بس ایک ایسا شخص ہے جس نے اللہ پر جھوٹ باندھا ہے اور ہم اس کو ماننے والے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,39,'23:39','رسول نے کہا، اے میرے رب، میری مدد فرما کہ انھوں نے مجھ کو جھٹلادیا');
INSERT INTO "ur_wahid_uddin" VALUES(23,40,'23:40','فرمایا کہ یہ لوگ جلد ہی پچھتائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,41,'23:41','پس ان کو ایک سخت آواز نے حق کے مطابق پکڑلیا پھر ہم نے ان کو خس و خاشاک کردیا پس دور ہو ظالم قوم');
INSERT INTO "ur_wahid_uddin" VALUES(23,42,'23:42','پھر ہم نے ان کے بعد دوسری قومیں پیدا کیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,43,'23:43','کوئی قوم نہ اپنے وعدہ سے آگے جاتی اور نہ اس سے پیچھے رہتی');
INSERT INTO "ur_wahid_uddin" VALUES(23,44,'23:44','پھر ہم نے لگاتار اپنے رسول بھیجے جب بھی کسی قوم کے پاس اس کا رسول آیا تو انھوں نے اس کو جھٹلایا تو ہم نے ایک کے بعدایک کو لگا دیا اور ہم نے ان کو کہانیاں بنادیا پس دور ہوں وہ لوگ جو ایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(23,45,'23:45','پھر ہم نے موسیٰ اور اس کے بھائی ہارون کو بھیجا اپنی نشانیوں اور کھلی دلیل کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(23,46,'23:46','فرعون اور اس کے درباریوں کے پاس تو انھوں نے تکبر کیا اور وہ مغرور لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(23,47,'23:47','پس انھوں نے کہا کیا ہم اپنے جیسے دو آدمیوں کی بات مان لیں ، حالاں کہ ان کی قوم کے لوگ ہمارے تابع دار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,48,'23:48','پس انھوں نے ان کو جھٹلادیا، پھر وہ ہلاک کردئے گئے');
INSERT INTO "ur_wahid_uddin" VALUES(23,49,'23:49','اور ہم نے موسیٰ کو کتاب دی تاکہ وہ راہ پائیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,50,'23:50','اور ہم نے مریم کے بیٹے کو اور اس کی ماں کو ایک نشانی بنایا اورہم نے ان کو ایک اونچی زمین پر ٹھکانا دیا جو سکون کی جگہ تھی اور وہاں چشمہ جاری تھا');
INSERT INTO "ur_wahid_uddin" VALUES(23,51,'23:51','اے پیغمبر و، ستھری چیزیں کھاؤ اور نیک کام کرو میں جانتا ہوں جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,52,'23:52','اور یہ تمھارا دین ایک ہی دین ہے اور میں تمھارا رب ہوں ، تو تم مجھ سے ڈرو');
INSERT INTO "ur_wahid_uddin" VALUES(23,53,'23:53','پھر لوگوں نے اپنے دین کو آپس میں ٹکڑے ٹکڑے کرلیا ہر گروہ کے پاس جو کچھ ہے اسی پر وہ نازاں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,54,'23:54','پس ان کو ان کی بے ہوشی میں کچھ دن چھوڑ دو');
INSERT INTO "ur_wahid_uddin" VALUES(23,55,'23:55','کیا وہ سمجھتے ہیں کہ ہم ان کو جو مال اور اولاد دئے جارہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,56,'23:56','تو ہم ان کو فائدہ پہنچانے میں سرگرم ہیں ، بلکہ وہ بات کو نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(23,57,'23:57','بے شک جو لوگ اپنے رب کی ہیبت سے ڈرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,58,'23:58','اور جو لوگ اپنے رب کی آیتوں پر یقین رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,59,'23:59','اور جولوگ اپنے رب کے ساتھ کسی کو شریک نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(23,60,'23:60','اور جو لوگ دیتے ہیں جو کچھ وہ دیتے ہیں اور ان کے دل کانپتے ہیں کہ وہ اپنے رب کی طرف لوٹنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,61,'23:61','یہ لوگ بھلائیوں کی ر اہ میں سبقت کر رہے ہیں اور وہ ان پر پہنچنے والے ہیں سب سے آگے');
INSERT INTO "ur_wahid_uddin" VALUES(23,62,'23:62','اور ہم کسی پر اس کی طاقت سے زیادہ بوجھ نہیں ڈالتے اور ہمارے پاس ایک کتاب ہے جو بالکل ٹھیک بولتی ہے اور ان پر ظلم نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(23,63,'23:63','بلکہ ان کے دل اس کی طرف سے غفلت میں ہیں اور ان کے کچھ کام اس کے علاوہ ہیں وہ ان کو کرتے رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,64,'23:64','یہاں تک کہ جب ہم ان کے آسودہ لوگوں کو عذاب میں پکڑیں گے تو وہ فریاد کرنے لگیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,65,'23:65','اب فریاد نہ کرو اب ہماری طرف سے تمھاری کوئی مد دنہ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(23,66,'23:66','تم کو میری آیتیں سنائی جاتی تھیں تو تم پیٹھ پھیر کربھاگتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(23,67,'23:67','اس سے تکبر کرکے گویا کسی قصہ گو کو چھوڑ رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,68,'23:68','پھر کیا انھوں نے اس کلام پر غور نہیں کیا یا ان کے پاس ایسی چیز آئی ہے جو ان کے اگلے باپ دادا کے پاس نہیں آئی');
INSERT INTO "ur_wahid_uddin" VALUES(23,69,'23:69','یا انھوں نے اپنے رسول کو پہچانا نہیں اس وجہ سے وہ اس کو نہیں مانتے');
INSERT INTO "ur_wahid_uddin" VALUES(23,70,'23:70','یا وہ کہتے ہیں کہ اس کو جنون ہے بلکہ وہ ان کے پاس حق لے کر آیا ہے اور ان میں سے اکثر کو حق بات بری لگتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,71,'23:71','اور اگر حق ان کی خواہشوں کے تابع ہوتا تو آسمان اور زمین اور جوان میں ہیں سب تباہ ہوجاتے بلکہ ہم نے ان کے پاس ان کی نصیحت بھیجی ہے تو وہ اپنی نصیحت سے اعراض کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,72,'23:72','کیا تم ان سے کوئی مال مانگ رہے ہو تو تمھارے رب کا مال تمھارے ليے بہتر ہے اور وہ بہترین روزی دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,73,'23:73','اور یقیناً تم ان کو ایک سیدھے راستے کی طرف بلاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,74,'23:74','اور جو لوگ آخرت پر یقین نہیں رکھتے وہ راستہ سے ہٹ گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,75,'23:75','اور اگر ہم ان پر رحم کریں اور ان پر جو تکلیف ہے،اس کو دور کردیں تب بھی وہ اپنی سر کشی میں لگے رہیں گے بہکے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(23,76,'23:76','اور ہم نے ان کو عذاب میں پکڑا، لیکن نہ وہ اپنے رب کے آگے جھکے اور نہ انھوں نے عاجزی کی');
INSERT INTO "ur_wahid_uddin" VALUES(23,77,'23:77','یہاں تک کہ جب ہم ان پر سخت عذاب کا دروازہ کھول دیں گے تو اس وقت وہ حیرت زدہ رہ جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,78,'23:78','اور وہی ہے جس نے تمھارے ليے کان اور آنکھیں اور دل بنائے تم بہت کم شکر ادا کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,79,'23:79','اور وہی ہے جس نے تم کو زمین میں پھیلایا اور تم اسی کی طرف جمع کيے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,80,'23:80','اور وہی ہے جو جلاتا ہے اور مارتا ہے اور اسی کے اختیارمیں ہے رات اور دن کا بدلنا تو کیا تم سمجھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,81,'23:81','بلکہ انھوں نے وہی بات کہی جو اگلوں نے کہی تھی');
INSERT INTO "ur_wahid_uddin" VALUES(23,82,'23:82','انھوں نے کہا کہ کیا جب ہم مرجائیں گے اور ہم مٹی اور ہڈیاں ہوجائیں گے تو کیا ہم دوبارہ اٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,83,'23:83','اس کا وعدہ ہم کو اور اس سے پہلے ہمارے باپ دادا کو بھی دیا گیا یہ محض اگلوں کے افسانے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,84,'23:84','کہو کہ زمین اور جو کوئی اس میں ہے یہ کس کا ہے، اگر تم جانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,85,'23:85','وہ کہیں گے کہ اللہ کا ہے کہو کہ پھر تم سوچتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,86,'23:86','کہو کہ کون مالک ہے سات آسمانوں کا اور کون مالک ہے عرش عظیم کا');
INSERT INTO "ur_wahid_uddin" VALUES(23,87,'23:87','وہ کہیں گے کہ سب اللہ کا ہے کہو، پھر کیا تم ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,88,'23:88','کہو کہ کون ہے جس کے ہاتھ میں ہر چیز کا اختیار ہے اور وہ پناہ دیتا ہے اور اس کے مقابلہ میں کوئی پناہ نہیں دے سکتا، اگر تم جانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,89,'23:89','وہ کہیں گے کہ یہ اللہ کے ليے ہے کہو کہ پھر کہاں سے تم مسحور کيے جاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(23,90,'23:90','بلکہ ہم ان کے پاس حق لائے ہیں اور بے شک وہ جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,91,'23:91','اللہ نے کوئی بیٹا نہیں بنایا اور اس کے ساتھ کوئی اور معبود نہیں ایسا ہوتا تو ہر معبود اپنی مخلوق کو لے کر الگ ہوجاتا اور ایک دوسرے پر چڑھائی کرتا اللہ پاک ہے اس سے جو وہ بیان کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,92,'23:92','وہ کھلے اور چھپے کا جاننے والا ہے وہ بہت اوپر ہے اس سے جس کو یہ شریک بتاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,93,'23:93','کہو کہ اے میرے ر ب، اگر تو مجھ کو وہ دکھا دے جس کا ان سے وعدہ کیا جارہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,94,'23:94','تو اے میرے رب، مجھ کو ظالم لوگوں میں شامل نہ کر');
INSERT INTO "ur_wahid_uddin" VALUES(23,95,'23:95','اوربے شک ہم قادر ہیں کہ ہم ان سے جو وعدہ کر رہے ہیں وہ تم کو دکھادیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,96,'23:96','تم برائی کو اس طریقے سے دفع کرو جو بہتر ہو ہم خوب جانتے ہیں جو یہ لوگ کہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,97,'23:97','اور کہو کہ اے میرے رب، میں پناہ مانگتا ہوں شیطانوں کے وسوسوں سے');
INSERT INTO "ur_wahid_uddin" VALUES(23,98,'23:98','اور اے میرے رب، میں تجھ سے پناہ مانگتا ہوں کہ وہ میرے پاس آئیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,99,'23:99','یہاں تک کہ جب ان میں سے کسی پر موت آتی ہے تو وہ کہتا ہے کہ اے میرے رب، مجھ کو واپس بھیج دے');
INSERT INTO "ur_wahid_uddin" VALUES(23,100,'23:100','تاکہ جس کو میں چھوڑ آیا ہوں اس میں کچھ نیکی کماؤں ہر گز نہیں ، یہ محض ایک بات ہے جس کووہ کہتا ہے اور ان کے آگے ایک پردہ ہے اس دن تک کے ليے جب کہ وہ اٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,101,'23:101','پھر جب صور پھونکا جائے گا تو پھر ان کے درمیان نہ کوئی رشتہ رہے گا اور نہ کوئی کسی کو پوچھے گا');
INSERT INTO "ur_wahid_uddin" VALUES(23,102,'23:102','پس جن کے پلے بھاری ہوں گے وہی لوگ کامیاب ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,103,'23:103','اور جن کے پلے ہلکے ہوں گے تو یہی لوگ ہیں جنھوں نے اپنے آپ کو گھاٹے میں ڈالا، وہ جہنم میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,104,'23:104','ان کے چہروں کو آگ جھلس دے گی اور وہ اس میں بد شکل ہو رہے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,105,'23:105','کیا تم کو میری آیتیں پڑھ کر نہیں سنائی جاتی تھیں تو تم ان کو جھٹلاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(23,106,'23:106','وہ کہیں گے کہ اے ہمارے رب، ہماری بدبختی نے ہم کو گھیر لیا تھا اور ہم گمراہ لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(23,107,'23:107','اے ہمارے رب ہم کو اس سے نکال لے، پھر اگر ہم دوبارہ ایسا کریں تو بے شک ہم ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(23,108,'23:108','خدا کہے گا کہ دور ہو، اسی میں پڑے رہو اور مجھ سے بات نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(23,109,'23:109','میرے بندوں میں ایک گروہ تھا جو کہتا تھا کہ اے ہمارے رب، ہم ایمان لائے، پس تو ہم کو بخش دے اور ہم پر رحم فرما اور تو بہترین رحم فرمانے والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,110,'23:110','پس تم نے ان کو مذاق بنالیا یہاں تک کہ ان کے پیچھے تم نے ہماری یاد بھلادی اور تم ان پر ہنستے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,111,'23:111','میں نے ان کو آج ان کے صبر کا بدلہ دیا کہ وہی ہیں کامیاب ہونے والے');
INSERT INTO "ur_wahid_uddin" VALUES(23,112,'23:112','ارشاد ہوگا کہ برسوں کے شمار سے تم کتنی دیر زمین میں رہے');
INSERT INTO "ur_wahid_uddin" VALUES(23,113,'23:113','وہ کہیں گے ہم ایک دن رہے یا ایک دن سے بھی کم توگنتی والوں سے پوچھ لیجيے');
INSERT INTO "ur_wahid_uddin" VALUES(23,114,'23:114','ارشاد ہوگا کہ تم تھوڑی ہی مدت رہے کاش تم جانتے ہوتے');
INSERT INTO "ur_wahid_uddin" VALUES(23,115,'23:115','پس کیا تم یہ خیال کرتے ہو کہ ہم نے تم کو بے مقصد پیدا کیا ہے اور تم ہمارے پاس نہیں لائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(23,116,'23:116','پس بہت برتر ہے اللہ، بادشاہِ حقیقی، اس کے سوا کوئی معبود نہیں وہ مالک ہے عرش عظیم کا');
INSERT INTO "ur_wahid_uddin" VALUES(23,117,'23:117','اور جو شخص اللہ کے ساتھ کسی اور معبود کو پکارے، جس کے حق میں اس کے پاس کوئی دلیل نہیں ، تو اس کا حساب اس کے رب کے پاس ہے بے شک منکروں کو فلاح نہ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(23,118,'23:118','اور کہو کہ اے میرے رب، مجھے بخش دے اور مجھ پر رحم فرما، تو بہترین رحم فرمانے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,1,'24:1','یہ ایک سورہ ہے جس کو ہم نے اتارا ہے اور اس کو ہم نے فرض کیا ہے اور اس میں ہم نے صاف صاف آیتیں اتاری ہیں تاكه تم ياد ركھو');
INSERT INTO "ur_wahid_uddin" VALUES(24,2,'24:2','زانی عورت اور زانی مرد دونوں میں سے ہر ایک کو سو کوڑے مارو اور تم کو ان دونوں پر اللہ کے دین کے معاملہ میں رحم نہ آنا چاہيے، اگر تم اللہ پر اور آخرت کے دن پر ایمان رکھتے ہو اور چاہيے کہ دونوں کی سزا کے وقت مسلمانوں کا ایک گروہ موجود رہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,3,'24:3','زانی نکاح نہ کرے مگر زانیہ کے ساتھ یا مشرکہ کے ساتھ اور زانیہ کے ساتھ نکاح نہ کرے مگر زانی یا مشرک اور یہ حرام کردیا گیا اہل ایمان پر');
INSERT INTO "ur_wahid_uddin" VALUES(24,4,'24:4','اور جو لوگ پاک دامن عورتوں پر عیب لگائیں ، پھر چار گواہ نہ لے آئیں ، ان کو اسّی کوڑے مارو اور ان کی گواہی کبھی قبول نہ کرو یہی لوگ نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,5,'24:5','لیکن جو لوگ اس کے بعد توبہ کریں اور اصلاح کرلیں تو اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,6,'24:6','اور جو لوگ اپنی بیویوں پر عیب لگائیں اور ان کے پاس ان کے اپنے سوا اور گواہ نہ ہوں تو ایسے شخص کی گواہی کی صورت یہ ہے کہ وہ چار بار اللہ کی قسم کھاکر کہے کہ بے شک وہ سچا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,7,'24:7','اور پانچویں بار یہ کہے کہ اس پر اللہ کی لعنت ہو اگر وہ جھوٹا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(24,8,'24:8','اورعورت سے سزا اس طرح ٹل جائے گی کہ وہ چار باراللہ کی قسم کھاکر کہے کہ یہ شخص جھوٹا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,9,'24:9','اور پانچویں بار یہ کہے کہ مجھ پر اللہ کا غضب ہو اگر یہ شخص سچا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(24,10,'24:10','اور اگر تم لوگوں پر اللہ کا فضل اور اس کی رحمت نہ ہوتی اور یہ کہ اللہ توبہ قبول کرنے والا حکمت والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,11,'24:11','جن لوگوں نے یہ طوفان برپا کیا وہ تمھارے اندر ہی کی ایک جماعت ہے تم اس کو اپنے حق میں برا نہ سمجھو بلکہ یہ تمھارے ليے بہتر ہے ان میں سے ہر آدمی کے ليے وہ ہے جتنا اس نے گناہ کمایا اور جس نے اس میں سب سے بڑا حصہ لیا اس کے ليے بڑا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,12,'24:12','جب تم لوگوں نے اس کو سنا تو مسلمان مردوں اور مسلمان عورتوں نے ایک دوسرے کی بابت نیک گمان کیوں نہ کیا اور کیو ں نہ کہا کہ یہ کھلا ہوا بہتان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,13,'24:13','یہ لوگ اس پر چار گواہ کیوں نہ لائے پس جب وہ گواہ نہیں لائے تو اللہ کے نزدیک وہی جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,14,'24:14','اور اگر تم لوگوں پر دنیا اور آخرت میں اللہ کا فضل اور اس کی رحمت نہ ہوتی تو جن باتوں میں تم پڑگئے تھے، اس کے باعث تم پر کوئی بڑی آفت آجاتی');
INSERT INTO "ur_wahid_uddin" VALUES(24,15,'24:15','جب کہ تم اس کو اپنی زبانوں سے نقل کر رہے تھے اور اپنے منہ سے ایسی بات کہہ رہے تھے جس کا تمھیں کوئی علم نہ تھا اور تم اس کو ایک معمولی بات سمجھ رہے تھے حالاں کہ وہ اللہ کے نزدیک بہت بھاری بات ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,16,'24:16','اور جب تم نے اس کو سنا تو یوں کیوں نہ کہا کہ ہم کو زیبا نہیں کہ ہم ایسی بات منہ سے نکالیں معاذ اللہ، یہ بہت بڑا بہتان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,17,'24:17','اللہ تم کو نصیحت کرتا ہے کہ پھر کبھی ایسا نہ کرنا اگر تم مومن ہو');
INSERT INTO "ur_wahid_uddin" VALUES(24,18,'24:18','اللہ تم سے صاف صاف احکام بیان کرتا ہے اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,19,'24:19','بے شک جو لوگ یہ چاہتے ہیں کہ مسلمانوں میں بے حیائی کا چرچا ہو، ان کے ليے دنیا اور آخرت میں درد ناک سزا ہے اور اللہ جانتا ہے اور تم نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(24,20,'24:20','اور اگر تم پر اللہ کا فضل اور اس کی رحمت نہ ہوتی اور یہ کہ اللہ نرمی کرنے والا، رحمت کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,21,'24:21','اے ایمان والو، تم شیطان کے قدموں پر نہ چلو اور جو شخص شیطان کے قدموں پر چلے گا تو وہ اس کو بے حیائی اور بدی ہی کا کام کرنے کو کہے گا اور اگر تم پر اللہ کا فضل اور اس کی رحمت نہ ہوتی تو تم میں سے کوئی شخص پاک نہ ہوسکتا لیکن اللہ ہی جسے چاہتا ہے پاک کردیتا ہے اور اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,22,'24:22','اور تم میں سے جو لوگ فضل والے اور وسعت والے ہیں ، وہ اس بات کی قسم نہ کھائیں کہ وہ اپنے رشتے داروں اور مسکینوں اور خدا کی راہ میں ہجرت کرنے والوں کو نہ دیں گے اور چاہيے کہ وہ معاف کردیں اور درگزر کریں کیا تم نہیں چاہتے کہ اللہ تم کو معاف کرے اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,23,'24:23','بے شک جو لوگ پاک دامن، بے خبر، ایمان والی عورتوں پر تہمت لگاتے ہیں ، ان پر دنیا اور آخرت میں لعنت کی گئی اور ان کے ليے بڑا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,24,'24:24','اس دن جب کہ ان کی زبانیں ان کے خلاف گواہی دیں گی اور ان کے ہاتھ اور ان کے پاؤں بھی ان کاموں کی جو کہ یہ لوگ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(24,25,'24:25','اس دن اللہ ان کو واجبی بدلہ پورا پورا دے گا اور وہ جان لیں گے کہ اللہ ہی حق ہے، کھولنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,26,'24:26','خبیث عورتیں خبیث مردوں کے ليے ہیں اور خبیث مرد خبیث عورتوں کے لیے ہیں اور پاکیزہ عورتیں پاکیزہ مردوں کے ليے ہیں اور پاکیزہ مرد پاکیزہ عورتوں کے ليے وہ لوگ بَری ہیں ان باتوں سے جو یہ کہتے ہیں ان کے ليے بخشش ہے اور عزت کی روزی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,27,'24:27','اے ایمان والو، تم اپنے گھروں کے سوا دوسرے گھروں میں داخل نہ ہو جب تک اجازت حاصل نہ کرلو اور گھر والوں کو سلام نہ کرلو یہ تمھارے ليے بہتر ہے تاکہ تم یاد رکھو');
INSERT INTO "ur_wahid_uddin" VALUES(24,28,'24:28','پھر اگر وہاں کسی کو نہ پاؤ تو ان میں داخل نہ ہو جب تک تم کو اجازت نہ دے دی جائے اور اگر تم سے کہا جائے کہ لوٹ جاؤ تو تم لوٹ جاؤ یہ تمھارے ليے بہتر ہے اور اللہ جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(24,29,'24:29','تم پر اس میں کچھ گناہ نہیں کہ تم ان گھروں میں داخل ہو جن میں کوئی نہ رہتاہو ان میں تمھارے فائدے کی کوئی چیز ہو اور اللہ جانتا ہے جو کچھ تم ظاہر کرتے ہو اور جو کچھ تم چھپاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(24,30,'24:30','مومن مردوں سے کہو کہ وہ اپنی نگاہیں نیچی رکھیں اور اپنی شرم گاہوں کی حفاظت کریں یہ ان کے ليے پاکیزہ ہے بے شک اللہ باخبر ہے اس سے جو وہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,31,'24:31','اور مومن عورتوں سے کہو کہ وہ اپنی نگاہیں نیچی رکھیں اور اپنی شرم گاہوں کی حفاظت کریں اور اپنی زینت کو ظاہر نہ کریں ، مگر جواس میں سے ظاہر ہوجائے اور اپنے دوپٹے اپنے سینوں پر ڈالے رہیں اور اپنی زینت کو ظاہر نہ کریں مگر اپنے شوہروں پر یا اپنے باپ پر یا اپنے شوہر کے باپ پر یا اپنے بیٹوں یا اپنے شوہر کے بیٹوں پر یا اپنے بھائیوں پر یا اپنے بھائیوں کے بیٹوں پر یا اپنی بہنوں کے بیٹو ں پر یا اپنی عورتوں پر یا اپنے مملوک پر یا زیردست مردوں پر جو کچھ غرض نہیں رکھتے یا ایسے لڑکوں پر جو عورتوں کے پردے کی باتوں سے ابھی ناواقف ہوں وہ اپنے پاؤں زور سے نہ ماریں کہ ان کی مخفی زینت معلوم ہوجائے، اور اے ایمان والو، تم سب مل کر اللہ کی طرف رجوع کرو تاکہ تم فلاح پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(24,32,'24:32','اور تم میں جو بے نکاح ہوں ان کا نکاح کردو اور تمھارے غلاموں اور لونڈیوں میں سے جو نکاح کے لائق ہوں ان کا بھی اگر وہ غریب ہوں گے تو اللہ ان کو اپنے فضل سے غنی کردے گا اور اللہ وسعت والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,33,'24:33','اور جو نکاح کا موقع نہ پائیں ، ان کو چاہيے کہ وہ ضبط کریں یہاں تک کہ اللہ اپنے فضل سے ان کو غنی کردے اور تمھارے مملوکوں میں سے جو مُکاتَب ہونے کے طالب ہوں تو ان کو مکاتب بنا لو اگر تم ان میں صلاحیت پاؤ اور ان کو اس مال میں سے دو جو اللہ نے تمھیں دیا ہے اور اپنی لونڈیوں کو پیشہ پر مجبور نہ کرو جب کہ وہ پاک دامن رہنا چاہتی ہوں ، محض اس ليے کہ دنیوی زندگی کا کچھ فائدہ تم کو حاصل ہوجائے اور جو شخص ان کو مجبور کرے گا تو اللہ اس جبر کے بعد بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,34,'24:34','اور بےشک ہم نے تمھاری طرف روشن آیتیں اتاری ہیں اور ان لوگوں کی مثالیں بھی جو تم سے پہلے گزرچکے ہیں اور ڈرنے والوں کے ليے نصیحت بھی');
INSERT INTO "ur_wahid_uddin" VALUES(24,35,'24:35','اللہ آسمانوں اور زمین کی روشنی ہے اس کی روشنی کی مثال ایسی ہے جیسے ایک طاق اس میں ایک چراغ ہے چراغ ایک شیشہ کے اندر ہے شیشہ ایسا ہے جیسے ایک چمک دار تارہ وہ زیتون کے ایک ایسے مبارک درخت کے تیل سے روشن کیا جاتا ہے جو نہ شرقی ہے اور نہ غربی اس کا تیل ایسا ہے گویا آگ کے چھوئے بغیر ہی وہ خود بخود جل اٹھے گاروشنی کے اوپر روشنی اللہ اپنی روشنی کی راہ دکھاتا ہے جس کو چاہتا ہے اور اللہ لوگوں کے ليے مثالیں بیان کرتا ہے اور اللہ ہر چیز کو جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,36,'24:36','ایسے گھروں میں جن کی نسبت اللہ نے حکم دیا ہے کہ وہ بلند کيے جائیں اور ان میں اس کے نام کا ذکر کیا جائے ان میں صبح و شام اللہ کی یاد کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,37,'24:37','وہ لوگ جن کو تجارت اور خریدو فروخت اللہ کی یاد سے غافل نہیں کرتی اور نہ نماز کی اقامت سے اور زکوٰۃ کی ادائیگی سے وہ اس دن سے ڈرتے ہیں جس میں دل اور آنکھیں الٹ جائیں گی');
INSERT INTO "ur_wahid_uddin" VALUES(24,38,'24:38','کہ اللہ انھیں ان کے عمل کا بہترین بدلہ دے اور ان کو مزید اپنے فضل سے نوازے اور اللہ جس کو چاہتا ہے بے حساب دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,39,'24:39','اور جن لوگوں نے انکار کیا ان کے اعمال ایسے ہیں جیسے چٹیل میدان میں سراب پیاسا اس کو پانی خیال کرتا ہے یہاں تک کہ جب وہ اس کے پاس آیا تو اس کو کچھ نہ پایا اور اس نے وہاں اللہ کو موجود پایا، پس اس نے اس کا حساب چکادیا اور اللہ جلد حساب چکانے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,40,'24:40','یا جیسے ایک گہرے سمندر میں اندھیرا ہو، موج کے اوپر موج اٹھ رہی ہو،اوپر سے بادل چھائے ہوئے ہوں ، اوپر تلے بہت سے اندھیرے، اگر کوئی اپنا ہاتھ نکالے تو اس کو بھی نہ دیکھ پائے اور جس کو اللہ روشنی نہ دے تو اس کے ليے کوئی روشنی نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,41,'24:41','کیا تم نے نہیں دیکھا کہ اللہ کی پاکی بیان کرتے ہیں جو آسمانوں اور زمین میں ہے اور چڑیاں بھی پرکو پھیلائے ہوئے ہر ایک اپنی نماز کو اور اپنی تسبیح کو جانتا ہے اور اللہ کو معلوم ہے جو کچھ وہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,42,'24:42','اور اللہ ہی کی حکومت ہے آسمانوں اور زمین میں اوراللہ ہی کی طرف ہے سب کی واپسی');
INSERT INTO "ur_wahid_uddin" VALUES(24,43,'24:43','کیا تم نے دیکھا نہیں کہ اللہ بادلوں کو چلاتا ہے پھر ان کو آپس میں ملادیتا ہے پھر ان کو تہہ بہ تہہ کردیتا ہے پھر تم بارش کو دیکھتے ہو کہ اس کے بیچ سے نکلتی ہے اور وہ آسمان سے— اس کے اندر کے پہاڑوں سے— اولے برساتا ہے پھر ان کو جس پر چاہتا ہے گراتا ہے اور جس سے چاہتا ہے ان کو ہٹادیتا ہے اس کی بجلی کی چمک معلوم ہوتا ہے کہ نگاہوں کو اچک لے جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(24,44,'24:44','اللہ رات اور دن کو بدلتا رہتا ہے بے شک اس میں سبق ہے آنکھ والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(24,45,'24:45','اور اللہ نے ہر جاندار کو پانی سے پیدا کیا پھر ان میں سے کوئی اپنے پیٹ کے بل چلتا ہے اور ان میں سے کوئی دو پاؤں پر چلتا ہے اور ان میں سے کوئی چار پیروں پر چلتا ہے اللہ پیدا کرتا ہے جو وہ چاہتا ہے بے شک اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,46,'24:46','ہم نے کھول کر بتانے والی آیتیں اتار دی ہیں اور اللہ جس کو چاہتا ہے سیدھی راہ کی ہدایت دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,47,'24:47','اور وہ کہتے ہیں کہ ہم اللہ اور رسول پر ایمان لائے اور ہم نے اطاعت کی مگر ان میں سے ایک گروہ اس کے بعد پھر جاتا ہے اور یہ لوگ ایمان لانے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,48,'24:48','اور جب ان کو اللہ اور رسول کی طرف بلایا جاتا ہے، تاکہ خدا کا رسول ان کے درمیان فیصلہ کرے تو ان میں سے ایک گروہ رو گردانی کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,49,'24:49','اور اگر حق ان کو ملنے والا ہو تووہ اس کی طرف فرماں بردار بن کر آجاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,50,'24:50','کیا ان کے دلوں میں بیماری ہے یا وہ شک میں پڑے ہوئے ہیں یا ان کو یہ اندیشہ ہے کہ اللہ اور اس کا رسول ان کے ساتھ ظلم کریں گے بلکہ یہی لوگ ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,51,'24:51','ایمان والوں کا قول تو یہ ہے کہ جب وہ اللہ اور اس کے رسول کی طرف بلائے جائیں تاکہ رسول ان کے درمیان فیصلہ کرے تو وہ کہیں کہ ہم نے سنا اور ہم نے مانا اور یہی لوگ فلاح پانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,52,'24:52','اور جو شخص اللہ اور اس کے رسول کی طاعت کرے اور وہ اللہ سے ڈرے اور اس کی مخالفت سے بچے تو یہی لوگ ہیں جو کامیاب ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(24,53,'24:53','اور وہ اللہ کی قسمیں کھاتے ہیں ، بڑی سخت قسمیں ، کہ اگر تم ان کو حکم دو تو وہ ضرور نکلیں گے کہو کہ قسمیں نہ کھاؤ، دستور کے مطابق اطاعت چاہيے بے شک اللہ کو معلوم ہے جو تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(24,54,'24:54','کہو کہ اللہ کی اطاعت کرو اور رسول کی اطاعت کرو پھر اگر تم روگردانی کرو گے تو رسول پر وہ بوجھ ہے جو اس پر ڈالا گیا ہے اور تم پر وہ بوجھ ہے جو تم پر ڈالا گیا ہے اور اگر تم اس کی اطاعت کرو گے تو ہدایت پاؤ گے اور رسول کے ذمہ صرف صاف صاف پہنچادینا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,55,'24:55','اللہ نے وعدہ فرمایا ہے تم میں سے ان لوگوں کے ساتھ جو ایمان لائیں اور نیک عمل کریں کہ وہ ان کو زمین میں اقتدار دے گا، جیسا کہ ان سے پہلے لوگوں کو اقتدار دیا تھا اور ان کے ليے ان کے دین کو جما دے گا جس کو ان کے ليے پسند کیا ہے اور ان کی خوف کی حالت کے بعد اس کو امن سے بدل دے گا وہ صرف میری عبادت کریں گے اور کسی چیز کو میرا شریک نہ بنائیں گے اور جو اس کے بعد انکار کرے تو ایسے ہی لوگ نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(24,56,'24:56','اور نماز قائم کرو اور زکوٰۃ ادا کرو اور رسول کی اطاعت کرو تاکہ تم پر رحم کیا جائے');
INSERT INTO "ur_wahid_uddin" VALUES(24,57,'24:57','جو لوگ انکار کرر ہے ہیں ان کی نسبت یہ گمان نہ کرو کہ وہ زمین میں اللہ کو عاجز کردیں گے اور ان کا ٹھکانا آگ ہے اور وہ نہایت برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,58,'24:58','اے ایمان والو، تمھارے مملوکوں کو اور تم میں جو بلوغ کو نہیں پہنچے ان کو تین وقتوں میں اجازت لینا چاہیے— فجر کی نماز سے پہلے، اور دوپہر کو جب تم اپنے کپڑے اتارتے ہو، اور عشاء کی نماز کے بعد یہ تین وقت تمھارے ليے پردے کے ہیں ان کے بعد نہ تم پر کوئی گناہ ہے اور نہ ان پر تم ایک دوسرے کے پاس بکثرت آتے جاتے رہتے ہو اس طرح اللہ تمھارے ليے اپنی آیتوں کی وضاحت کرتا ہے اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,59,'24:59','اور جب تمھارے بچے عقل کی حد کو پہنچ جائیں تو وہ بھی اسی طرح اجازت لیں جس طرح ان کے اگلے اجازت لیتے رہے ہیں اس طرح اللہ تمھارے ليے اپنی آیتوں کی وضاحت کرتا ہے اور اللہ علیم اور حکیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,60,'24:60','اور بڑی بوڑھی عورتیں جو نکاح کی امید نہیں رکھتیں ، ان پر کوئی گناہ نہیں اگر وہ اپنی چادریں اتار کر رکھ دیں ، بشرطیکہ وہ زینت کی نمائش کرنے والی نہ ہوں اور اگر وہ بھی احتیاط کریں تو ان کے لیے بہتر ہے اور اللہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,61,'24:61','اندھے پر کوئی تنگی نہیں اور لنگڑے پر کوئی تنگی نہیں اور بیمار پر کوئی تنگی نہیں اور نہ تم لوگوں پر کوئی تنگی ہے کہ تم اپنے گھروں سے کھاؤ یا اپنے باپ دادا کے گھروں سے، یا اپنی ماؤں کے گھروں سے، یا اپنے بھائیوں کے گھروں سے، یا اپني بهنوں کے گھروں سے،یا اپنے چچاؤں کے گھروں سے، یا اپنی پھوپھیوں کے گھروں سے، یا اپنے ماموؤں کے گھروں سے یا اپنی خالاؤں کے گھروں سے یا جس گھر کی کنجیوں کے تم مالک ہو یا اپنے دوستوں کے گھروں سے، تم پر کوئی گناہ نہیں کہ تم لوگ مل کر کھاؤ یا الگ الگ پھر جب تم گھروں میں داخل ہو تو اپنے لوگوں کو سلام کرو جو بابرکت دعا ہے اللہ کی طرف سے اس طرح اللہ تمھارے ليے آیتوں کی وضاحت کرتا ہے تاکہ تم سمجھو');
INSERT INTO "ur_wahid_uddin" VALUES(24,62,'24:62','ایمان والے وہ ہیں جو اللہ اور اس کے رسول پر یقین لائیں اور جب وہ کسی اجتماعی کام کے موقع پر رسول کے ساتھ ہوں تو جب تک تم سے اجازت نہ لے لیں وہاں سے نہ جائیں جو لوگ تم سے اجازت لیتے ہیں وہی اللہ اور اس کے رسول پر ایمان رکھتے ہیں پس جب وہ اپنے کسی کام کے ليے تم سے اجازت مانگیں تو ان کو اجازت دے دو اور ان کے ليے اللہ سے معافی مانگو بے شک اللہ معاف کرنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(24,63,'24:63','تم لوگ رسول کے بلانے کو اس طرح کا بلانا نہ سمجھو جس طرح تم آپس میں ایک دوسرے کو بلاتے ہو اللہ تم میں سے ان لوگوں کو جانتا ہے جو ایک دوسرے کی آڑ لیتے ہوئے چپکے سے چلے جاتے ہیں پس جو لوگ اس کے حکم کی خلاف ورزی کرتے ہیں ، ان کو ڈرنا چاہیے کہ ان پر کوئی آزمائش آجائے یا ان کو ایک درد ناک عذاب پکڑ لے');
INSERT INTO "ur_wahid_uddin" VALUES(24,64,'24:64','یاد رکھو کہ جو کچھ آسمانوں اور زمین میں ہے سب اللہ کا ہے اللہ اس حالت کو جانتا ہے جس پر تم ہو اور جس دن لوگ اس کی طرف لائے جائیں گے تو جو کچھ انھوں نے کیا تھا، وہ اس سے ان کو باخبر کردے گا اور اللہ ہر چیز کو جاننے و الا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,1,'25:1','بڑی بابرکت ہے وہ ذات جس نے اپنے بندے پر فرقان اتارا تاکہ وہ جہان والوں کے ليے ڈرانے والا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(25,2,'25:2','وہ جس کے ليے آسمانوں اور زمین کی بادشاہی ہے اور اس نے کوئی بیٹا نہیں بنایا اور بادشاہی میں کوئی اس کا شریک نہیں اور اس نے ہر چیزکو پیدا کیا اور اس کا ایک اندازہ مقرر کیا');
INSERT INTO "ur_wahid_uddin" VALUES(25,3,'25:3','اور لوگوں نے اس کے سوا ایسے معبود بنائے جو کسی چیز کو پیدا نہیں کرتے، وہ خود پیدا کيے جاتے ہیں اور وہ خود اپنے ليے نہ کسی نقصان کا اختیار رکھتے ہیں اور نہ کسی نفع کا اور نہ وہ کسی کے مرنے کا اختیار رکھتے ہیں اور نہ کسی کے جینے کا اور نه مرنے كے بعد دوباره زنده كرنے كا');
INSERT INTO "ur_wahid_uddin" VALUES(25,4,'25:4','اور منکر لوگ کہتے ہیں کہ یہ صرف ایک جھوٹ ہے جس کو اس نے گھڑا ہے اور کچھ دوسرے لوگوں نے اس میں اس کی مدد کی ہے پس یہ لوگ ظلم اور جھوٹ کے مرتکب ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(25,5,'25:5','اور وہ کہتے ہیں کہ یہ اگلوں کی بے سند باتیں ہیں جن کو اس نے لکھوالیا ہے پس وہ اس کو صبح و شام سنائی جاتی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(25,6,'25:6','کہو کہ اس کو اس نے اتارا ہے جو آسمانوں اور زمین کے بھید کو جانتا ہے بے شک وہ بخشنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,7,'25:7','اور وہ کہتے ہیں کہ یہ کیسا رسول ہے جو کھانا کھاتا ہے اور بازاروں میں چلتا پھرتا ہے کیوں نہ اس کے پاس کوئی فرشتہ بھیجا گیا کہ وہ اس کے ساتھ رہ کر ڈراتا');
INSERT INTO "ur_wahid_uddin" VALUES(25,8,'25:8','یااس کے ليے کوئی خزانہ اتارا جاتا یا اس کے ليے کوئی باغ ہوتا جس سے وہ کھاتا اور ظالموں نے کہا کہ تم لوگ ایک سحر زدہ آدمی کی پیروی کر رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(25,9,'25:9','دیکھو وہ کیسی کیسی مثلیں تمھارے ليے بیان کر رہے ہیں پس وہ بہک گئے ہیں ، پھر وہ راہ نہیں پاسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(25,10,'25:10','بڑا بابرکت ہے وہ اگر وہ چاہے تو تم کو اس سے بھی بہتر چیز دے دے ایسے باغات جن کے نیچے نہریں جاری ہوں ، اور تم کو بہت سے محل دے دے');
INSERT INTO "ur_wahid_uddin" VALUES(25,11,'25:11','بلکہ انھوں نے قیامت کو جھٹلادیا ہے اور ہم نے ایسے شخص کے ليے جو قیامت کو جھٹلائے دوزح تیار کر رکھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,12,'25:12','جب وہ ان کو دور سے دیکھے گی تو وہ اس کا بپھرنا اور دھاڑنا سنیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(25,13,'25:13','اور جب وہ اس کی کسی تنگ جگہ میں باندھ کر ڈال دئے جائیں گے تو وہ وہاں موت کو پکاریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(25,14,'25:14','آج ایک موت کو نہ پکارو، اور بہت سی موت کو پکارو');
INSERT INTO "ur_wahid_uddin" VALUES(25,15,'25:15','کہو کیا یہ بہتر ہے، یا ہمیشہ کی جنت جس کا وعدہ خدا سے ڈرنے والوں سے کیا گیا ہے، وہ ان کے ليے بدلہ اور ٹھکانا ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(25,16,'25:16','اس میں ان کے ليے وہ سب ہوگا جو وہ چاہیں گے، وہ اس میں ہمیشہ رہیں گے یہ تیرے رب کے ذمہ ایک وعدہ ہے واجب الادا');
INSERT INTO "ur_wahid_uddin" VALUES(25,17,'25:17','اور جس دن وہ ان کو جمع کرے گا اور ان کو بھی جن کی وہ اللہ کے سوا عبادت کرتے ہیں ، پھر وہ کہے گا، کیا تم نے میرے ان بندوں کو گمراہ کیا یا وہ خود راستہ سے بھٹک گئے');
INSERT INTO "ur_wahid_uddin" VALUES(25,18,'25:18','وہ کہیں گے کہ پاک ہے تیری ذات ہمیں یہ سزا وار نہ تھا کہ ہم تیرے سوا دوسروں کو کار ساز تجویز کریں مگر تو نے ان کو اور ان کے باپ دادا کو دنیا کا سامان دیا، یہاں تک کہ وہ نصیحت کو بھول گئے اور ہلاک ہونے والے بنے');
INSERT INTO "ur_wahid_uddin" VALUES(25,19,'25:19','پس انھوں نے تم کو تمھاری باتوں میں جھوٹا ٹھہرادیا اب نہ تم خود ٹال سکتے ہو اور نہ کوئی مدد پاسکتے ہو اور تم میں سے جو شخص ظلم کرے گا ہم اس کو ایک بڑا عذاب چکھائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(25,20,'25:20','اور ہم نے تم سے پہلے جتنے پیغمبر بھیجے سب کھانا کھاتے تھے اور بازاروں میں چلتے پھرتے تھے اور ہم نے تم کو ایک دوسرے کے ليے آزمائش بنایا ہے کیا تم صبر کرتے ہو اور تمھارا رب سب کچھ دیکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,21,'25:21','اور جو لوگ ہمارے سامنے پیش ہونے کااندیشہ نہیں رکھتے، وہ کہتے ہیں کہ ہمارے اوپر فرشتے کیوں نہیں اتارے گئے یا ہم اپنے رب کو دیکھ لیتے انھوں نے اپنے جی میں اپنے کو بہت بڑا سمجھا اور وہ حد سے گزر گئے ہیں سرکشی میں');
INSERT INTO "ur_wahid_uddin" VALUES(25,22,'25:22','جس دن وہ فرشتوں کو دیکھیں گے اس دن مجرموں کے ليے کوئی خوش خبری نہ ہو گی اور وہ کہیں گے کہ پناہ، پناہ');
INSERT INTO "ur_wahid_uddin" VALUES(25,23,'25:23','اور ہم ان کے ہر عمل کی طرف بڑھیں گے جو انھوں نے کیا تھا اور پھر اس کو اڑتی ہوئی خاک بنادیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(25,24,'25:24','جنت والے اس دن بہترین ٹھکانے میں ہوں گے اور نہایت اچھی آرام گاہ میں');
INSERT INTO "ur_wahid_uddin" VALUES(25,25,'25:25','اور جس دن آسمان بادل سے پھٹ جائے گا اور فرشتے لگاتار اتارے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(25,26,'25:26','اس دن حقیقی بادشاہی صرف رحمن کی ہوگی اور وہ دن منکروں پر بڑا سخت ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(25,27,'25:27','اور جس دن ظالم اپنے ہاتھوں کو کاٹے گا، وہ کہے گا کاش، میں نے رسول کی معیت میں راہ اختیار کی ہوتی');
INSERT INTO "ur_wahid_uddin" VALUES(25,28,'25:28','ہائےمیری شامت، کاش میں فلاں شخص کو دوست نہ بناتا');
INSERT INTO "ur_wahid_uddin" VALUES(25,29,'25:29','اس نے مجھ کو نصیحت سے بہکادیا بعد اس کے کہ وہ میرے پاس آچکی تھی اور شیطان ہے ہی انسان کو دغا دینے والا');
INSERT INTO "ur_wahid_uddin" VALUES(25,30,'25:30','اور رسول کہے گا کہ اے میرے رب، میری قوم نے اس قرآن کو بالکل نظر انداز کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(25,31,'25:31','اور اسی طرح ہم نے مجرموں میں سے ہر نبی کے دشمن بنائے اور تمھارا رب کافی ہے رہنمائی کے ليے اور مدد کرنے کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(25,32,'25:32','اور انکار کرنے والوں نے کہا کہ اس کے اوپر پورا قرآن کیوں نہیں اتارا گیا ایسا اس ليے ہے تاکہ اس کے ذریعہ سے ہم تمھارے دل کو مضبوط کریں اور ہم نے اس کو ٹھہر ٹھہر کر اتارا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,33,'25:33','اور یہ لوگ کیسا ہی عجیب سوال تمھارے سامنے لائیں ، مگر ہم اس کا ٹھیک جواب اور بہترین وضاحت تمھیں بتادیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(25,34,'25:34','جو لوگ اپنے منہ کے بل جہنم کی طرف لے جائے جائیں گے، انھیں کا برا ٹھکانا ہے اور وہی ہیں راہ سے بہت بھٹکے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(25,35,'25:35','اور ہم نے موسیٰ کو کتاب دی اور اس کے ساتھ اس کے بھائی ہارون کو مدد گار بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(25,36,'25:36','پھر ہم نے ان سے کہا کہ تم دونوں ان لوگوں کے پاس جاؤ جنھوں نے ہماری آیتوں کو جھٹلادیا ہے پھر ہم نے ان کو بالکل تباہ کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(25,37,'25:37','اور نوح کی قوم کو بھی ہم نے غرق کردیا جب کہ انھوں نے رسولوں کو جھٹلایا اور ہم نے ان کو لوگوں کے ليے ایک نشانی بنادیا اور ہم نے ظالموں کے ليے درد ناک عذاب تیار کر رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,38,'25:38','اور عاد اور ثمود کو اور اصحاب الرس کو اور ان کے درمیان بہت سی قوموں کو');
INSERT INTO "ur_wahid_uddin" VALUES(25,39,'25:39','اور ہم نے ان میں سے ہر ایک کو مثالیں سنائیں اورہم نے ہر ایک کو بالکل برباد کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(25,40,'25:40','اور یہ لوگ اس بستی پر سے گزرے ہیں جس پر بری طرح پتھر برسائے گئے کیا وہ اس کو دیکھتے نہیں رہے ہیں بلکہ وہ لوگ دوبارہ اٹھائے جانے کی امید نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(25,41,'25:41','اور وہ جب تم کو دیکھتے ہیں تو وہ تمھارا مذاق بنالیتے ہیں کیا یہی ہے جس کو خدا نے رسول بناکر بھیجا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,42,'25:42','اس نے تو ہم کو ہمارے معبودوں سے ہٹا ہی دیا ہوتا، اگر ہم ان پر جمے نہ رہتے اور جلد ہی ان کو معلوم ہوجائے گا جب وہ عذاب کو دیکھیں گے کہ سب سے زیادہ بے راہ کون ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,43,'25:43','کیا تم نے اس شخص کو دیکھا جس نے اپنی خواہش کو اپنا معبود بنا رکھا ہے پس کیا تم اس کا ذمہ لے سکتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(25,44,'25:44','یا تم خیال کرتے ہو کہ ان میں سے اکثر سنتے اور سمجھتے ہیں وہ تو محض جانوروں کی طرح ہیں بلکہ وہ ان سے بھی زیادہ بے راہ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(25,45,'25:45','کیا تم نے اپنے رب کی طرف نہیں دیکھا کہ وہ کس طرح سائے کو پھیلادیتا ہے اور اگر وہ چاہتا تو وہ اس کو ٹھہرا دیتا پھر ہم نے سورج کو اس پر دلیل بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(25,46,'25:46','پھر ہم نے آہستہ آہستہ اس کو اپنی طرف سمیٹ لیا');
INSERT INTO "ur_wahid_uddin" VALUES(25,47,'25:47','اور وہی ہے جس نے تمھارے ليے رات کو پردہ اور نیند کو راحت بنایا اور دن کو جی اٹھنے کا وقت بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(25,48,'25:48','اور وہی ہے جو اپنی رحمت سے پہلے ہواؤں کو خوش خبری بناکر بھیجتا ہے اور ہم آسمان سے پاک پانی اتارتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(25,49,'25:49','تاکہ اس کے ذریعہ سے مردہ زمین میں جان ڈال دیں اور اس کو پلائیں اپنی مخلوقات میں سے بہت سے جانوروں اور انسانوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(25,50,'25:50','اور ہم نے اس کو ان کے درمیان طرح طرح سے بیان کیا ہے تاکہ وہ سوچیں پھر بھی اکثر لوگ ناشکری کيے بغیر نہیں رہتے');
INSERT INTO "ur_wahid_uddin" VALUES(25,51,'25:51','اور اگر ہم چاہتے تو ہر بستی میں ایک ڈرانے والا بھیج دیتے');
INSERT INTO "ur_wahid_uddin" VALUES(25,52,'25:52','پس تم منکروں کی بات نہ مانو اور اس کے ذریعہ سے ان کے ساتھ بڑا جہادکرو');
INSERT INTO "ur_wahid_uddin" VALUES(25,53,'25:53','اور وہی ہے جس نے دو سمندروں کو ملایا یہ میٹھا ہے پیاس بجھانے والا اور یہ کھاری ہے کڑوا اور اس نے ان کے درمیان ایک پردہ رکھ دیا اور ایک مضبوط آڑ');
INSERT INTO "ur_wahid_uddin" VALUES(25,54,'25:54','اور وہی ہے جس نے انسان کو پانی سے پیدا کیا پھر اس کو خاندان والا اور سسرال والا بنایا اور تمھارا رب بڑی قدرت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,55,'25:55','اور وہ اللہ کو چھوڑ کر ان چیزوں کی عبادت کرتے ہیں جو ان کو نہ نفع پہنچاسکتی ہیں اور نہ نقصان اور منکرتو اپنے رب کے خلاف مدد گار بنا ہوا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,56,'25:56','اور ہم نے تم کو صرف خوش خبری دینے والا اور ڈرانے والا بناکر بھیجا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,57,'25:57','تم کہو کہ میں تم سے اس پر کوئی اجرت نہیں مانگتا، مگر یہ کہ جو چاہے وہ اپنے رب کا راستہ پکڑلے');
INSERT INTO "ur_wahid_uddin" VALUES(25,58,'25:58','اور زندہ خدا پر، جو کبھی مرنے والا نہیں ، بھروسہ رکھو اور اس کی حمد کے ساتھ اس کی تسبیح کرو اور وہ اپنے بندوں کے گناہوں سے باخبر رہنے کے ليے کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,59,'25:59','جس نے پیدا کیا آسمانوں اور زمین کو اور جو کچھ ان کے درمیان ہے، چھ دن میں پھر وہ تخت پر متمکن ہوا رحمٰن، پس اس کو کسی جاننے والے سے پوچھو');
INSERT INTO "ur_wahid_uddin" VALUES(25,60,'25:60','اور جب ان سے کہا جاتا ہے کہ رحمن کو سجدہ کرو تووہ کہتے ہیں کہ رحمٰن کیا ہے کیا ہم اس کو سجدہ کریں جس کو تو ہم سے کہے اور ان کا بدکنا اور بڑھ جاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,61,'25:61','بڑی بابرکت ہے وہ ذات جس نے آسمان میں برج بنائے اور اس میں ایک چراغ (سورج) اور ایک چمکتا چاند رکھا');
INSERT INTO "ur_wahid_uddin" VALUES(25,62,'25:62','اور وہی ہے جس نے رات اور دن کو یکے بعد دیگرے آنے والا بنایا، اس شخص کے ليے جو سبق لینا چاہے اور شکر گزار بننا چاہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,63,'25:63','اور رحمن کے بندے وہ ہیں جو زمین میں عاجزی کے ساتھ چلتے ہیں اور جب جاہل لوگ ان سے بات کرتے ہیں تو وہ کہہ دیتے ہیں کہ تم کو سلام');
INSERT INTO "ur_wahid_uddin" VALUES(25,64,'25:64','اور جو اپنے رب کے آگے سجدہ اور قیام میں راتیں گزارتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(25,65,'25:65','اور جو کہتے ہیں کہ اے ہمارے رب، جہنم کے عذاب کو ہم سے دور رکھ بے شک اس کا عذاب پوری تباہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,66,'25:66','بے شک وہ برا ٹھکانا ہے اور برا مقام ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,67,'25:67','اور وہ لوگ کہ جب وہ خرچ کرتے ہیں تو نہ فضول خرچی کرتے ہیں اور نہ تنگی کرتے ہیں اور ان کا خرچ اس کے درمیان اعتدال پر ہوتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,68,'25:68','اور جو لوگ اللہ کے سوا کسی دوسرے معبود کو نہیں پکارتے ہیں اور وہ اللہ کی حرام کی ہوئی کسی جان کو قتل نہیں کرتے مگر حق پر اور وہ بدکاری نہیں کرتے اور جو شخص ایسے کام کرے گا تو وہ سزا سے دو چار ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(25,69,'25:69','قیامت کے دن اس کا عذاب بڑھتا چلا جائے گا اور وہ اس میں ہمیشہ ذلیل ہوکر رہے گا');
INSERT INTO "ur_wahid_uddin" VALUES(25,70,'25:70','مگر جو شخص توبہ کرے اور ایمان لائے اور نیک کام کرے تو اللہ ایسے لوگوں کی برائیوں کو بھلائیوں سے بدل دے گا اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,71,'25:71','اور جو شخص توبہ کرے اور نیک کام کرے تو وہ درحقیقت اللہ کی طرف رجوع کر رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(25,72,'25:72','اور جو لوگ جھوٹے کام میں شامل نہیں ہوتے اور جب کسی بیہودہ چیز سے ان کا گزر ہوتا ہے تو وہ سنجیدگی کے ساتھ گزر جاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(25,73,'25:73','اور وہ ایسے ہیں کہ جب ان کو ان کے رب کی آیتوں کے ذریعہ نصیحت کی جاتی ہے تو وہ ان پر بہرے اور اندھے ہوکر نہیں گرتے');
INSERT INTO "ur_wahid_uddin" VALUES(25,74,'25:74','اور جو کہتے ہیں کہ اے ہمارے رب، ہم کو ہماری بیوی اور ہماری اولاد کی طرف سے آنکھوں کی ٹھنڈک عطا فرما اور ہم کو پرہیزگاروں کا امام بنا');
INSERT INTO "ur_wahid_uddin" VALUES(25,75,'25:75','یہ لوگ ہیں کہ ان کو بالا خانے ملیں گے اس ليے کہ انھوں نے صبر کیا اور ان میں ان کا استقبال دعا اور سلام کے ساتھ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(25,76,'25:76','وہ ان میں ہمیشہ رہیں گے وہ خوب جگہ ہے ٹھہرنے کی اور خوب جگہ ہے رہنے کی');
INSERT INTO "ur_wahid_uddin" VALUES(25,77,'25:77','کہوکہ میرا رب تمھاری پروا نہیں رکھتا اگر تم اس کو نہ پکارو پس تم جھٹلاچکے تو وہ چیز عنقریب ہوکر رہے گی');
INSERT INTO "ur_wahid_uddin" VALUES(26,1,'26:1','طٰسم');
INSERT INTO "ur_wahid_uddin" VALUES(26,2,'26:2','یہ واضح کتاب کی آیتیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,3,'26:3','شاید تم اپنے کو ہلاک کر ڈالو گے اس پر کہ وہ ایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(26,4,'26:4','اگر ہم چاہیں تو ان پر آسمان سے نشانی اتاردیں پھر ان کی گردنیں اس کے آگے جھک جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,5,'26:5','ان کے پاس رحمان کی طرف سے کوئی بھی نئی نصیحت ایسی نہیں آتی جس سے وہ بے رخی نہ کرتے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,6,'26:6','پس انھوں نے جھٹلادیا تواب عنقریب ان کو اس چیز کی حقیقت معلوم ہوجائے گی جس کا وہ مذاق اڑاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(26,7,'26:7','کیا انھوں نے زمین کو نہیں دیکھا کہ ہم نے اس میں کس قدر طرح طرح کی عمدہ چیزیں اگائی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,8,'26:8','بے شک اس میں نشانی ہے اوران میں سے اکثر لوگ ایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(26,9,'26:9','اور بے شک تمھارا رب غالب ہے، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,10,'26:10','اور جب تمھارے رب نے موسیٰ کو پکارا کہ تم ظالم قوم کے پاس جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(26,11,'26:11','فرعون کی قوم کے پاس، کیا وہ نہیں ڈرتے');
INSERT INTO "ur_wahid_uddin" VALUES(26,12,'26:12','موسیٰ نے کہا اے میرے رب، مجھ کو اندیشہ ہے کہ وہ مجھے جھٹلادیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(26,13,'26:13','اور میرا سینہ تنگ ہوتا ہے اور میری زبان نہیں چلتی پس تو ہارون کے پاس پیغام بھیج دے');
INSERT INTO "ur_wahid_uddin" VALUES(26,14,'26:14','اور میرے اوپر ان کا ایک جرم بھی ہے پس میں ڈرتا ہوں کہ وہ مجھے قتل کردیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(26,15,'26:15','فرمایا کبھی نہیں پس تم دونوں ہماری نشانیوں کے ساتھ جاؤ، ہم تمھارے ساتھ سننے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,16,'26:16','پس تم دونوں فرعون کے پاس جاؤ اورکہو کہ ہم خداوندِ عالم کے رسول ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,17,'26:17','کہ تو بنی اسرائیل کو ہمارے ساتھ جانے دے');
INSERT INTO "ur_wahid_uddin" VALUES(26,18,'26:18','فرعون نے کہا، کیا ہم نے تم کو بچپن میں اپنے اندر نہیں پالا اور تم نے اپنی عمر کے کئی سال ہمارے یہاں گزارے');
INSERT INTO "ur_wahid_uddin" VALUES(26,19,'26:19','اورتم نے اپنا وہ فعل کیا جو کیا اور تم ناشکروں میں سے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,20,'26:20','موسیٰ نے کہا اس وقت میں نے کیا تھا اور مجھ سے غلطی ہوگئی');
INSERT INTO "ur_wahid_uddin" VALUES(26,21,'26:21','پھر مجھے تم لوگوں سے ڈرلگا تو میں تم سے بھاگ گیا، پھر مجھ کو میرے رب نے دانش مندی عطا فرمائی اور مجھ کو رسولوں میں سے بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,22,'26:22','اور یہ احسان ہے جو تم مجھ کو جتا رہے ہو جب کہ تم نے بنی اسرائیل کو غلام بنالیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,23,'26:23','فرعون نے کہا کہ رب العالمین کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,24,'26:24','موسیٰ نے کہا، آسمانوں اور زمین کا رب اوران سب کا رب جو ان کے درمیان ہیں ، اگر تم یقین لانے والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,25,'26:25','فرعون نے اپنے ارد گرد والوں سے کہا، کیا تم سنتے نہیں ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,26,'26:26','موسیٰ نے کہا وہ تمھارا بھی رب ہے اور تمھارے اگلے بزرگوں کا بھی');
INSERT INTO "ur_wahid_uddin" VALUES(26,27,'26:27','فرعون نے کہا تمھارا یہ رسول جو تمھاری طرف بھیجا گیا ہے مجنون ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,28,'26:28','موسیٰ نے کہا، مشرق و مغرب کا رب اور جو کچھ ان کے درمیان ہے، اگر تم عقل رکھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,29,'26:29','فرعون نے کہا، اگر تم نے میرے سوا کسی کو معبود بنایا تو میں تم کو قید کردوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(26,30,'26:30','موسیٰ نے کہا کیا اگر میں کوئی واضح دلیل پیش کروں تب بھی');
INSERT INTO "ur_wahid_uddin" VALUES(26,31,'26:31','فرعون نے کہا پھر اس کو پیش کرو اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,32,'26:32','پھر موسیٰ نے اپنا عصا ڈال دیا تو یکایک وہ ایک كھلا هوا اژدھا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(26,33,'26:33','اور اس نے اپنا ہاتھ کھینچا تو یکایک وہ دیکھنے والوں کے ليے چمک رہا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(26,34,'26:34','فرعون نے اپنے ارد گرد کے سرداروں سے کہا، یقیناً یہ شخص ایک ماہر جادو گر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,35,'26:35','وہ چاہتا ہے کہ وہ اپنے جادو سے تم کو تمھارے ملک سے نکال دے پس تم کیا مشورہ دیتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,36,'26:36','درباریوں نے کہا کہ اس کو اور اس کے بھائی کو مہلت دیجيے اور شہروں میں ہرکارے بھیجئے');
INSERT INTO "ur_wahid_uddin" VALUES(26,37,'26:37','کہ وہ آپ کے پاس تمام ماہر جادو گروں کو لائیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,38,'26:38','پس جادو گر ایک دن مقرر وقت پر اکھٹا کئے گئے');
INSERT INTO "ur_wahid_uddin" VALUES(26,39,'26:39','اور لوگوں سے کہا گیا کہ کیا تم لوگ جمع ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(26,40,'26:40','تاکہ ہم جادوگروں کا ساتھ دیں اگر وہ غالب رہنے والے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,41,'26:41','پھر جب جادو گر آئے تو انھوں نے فرعون سے کہا، کیا ہمارے ليے کوئی انعام ہے اگر ہم غالب رہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,42,'26:42','اس نے کہا ہاں ، اور تم اس صورت میں مقرب لوگوں میں شامل ہوجاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(26,43,'26:43','موسیٰ نے ان سے کہا کہ تم کو جو کچھ ڈالنا ہو ڈالو');
INSERT INTO "ur_wahid_uddin" VALUES(26,44,'26:44','پس انھوں نے اپنی رسیاں اور لاٹھیاں ڈالیں اور کہا کہ فرعون کے اقبال کی قسم، ہم ہی غالب رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(26,45,'26:45','پھر موسیٰ نے اپنا عصا ڈالا تو اچانک وہ اس فريب كو نگلنے لگا جو انھوں نے بنایا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(26,46,'26:46','پھر جادو گر سجدے میں گر پڑے');
INSERT INTO "ur_wahid_uddin" VALUES(26,47,'26:47','انھوں نے کہا ہم ایمان لائے رب العالمین پر');
INSERT INTO "ur_wahid_uddin" VALUES(26,48,'26:48','جو موسیٰ اور ہارون کا رب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,49,'26:49','فرعون نے کہا، تم نے اس کو مان لیا اس سے پہلے کہ میں تم کو اجازت دوں بے شک وہی تمھارا استاد ہے جس نے تم کو جادو سکھایا ہے پس اب تم کو معلوم ہوجائے گا میں تمھارے ایک طرف کے ہاتھ اور دوسری طرف کے پاؤں کا ٹوں گا اور تم سب کو سولی پر چڑھاؤں گا');
INSERT INTO "ur_wahid_uddin" VALUES(26,50,'26:50','انھوں نے کہا کہ کچھ حرج نہیں ہم اپنے مالک کے پاس پہنچ جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(26,51,'26:51','ہم امید رکھتے ہیں کہ ہمارا رب ہماری خطاؤ ں کو معاف کردے گا اس ليے کہ ہم پہلے ایمان لانے والے بنے');
INSERT INTO "ur_wahid_uddin" VALUES(26,52,'26:52','اور ہم نے موسیٰ کو وحی بھیجی کہ میرے بندوں کو لے کر رات کو نکل جاؤ بے شک تمھارا پیچھا کیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(26,53,'26:53','پس فرعون نے شہروں میں ہر کارے بھیجے');
INSERT INTO "ur_wahid_uddin" VALUES(26,54,'26:54','یہ لوگ تھوڑی سی جماعت ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,55,'26:55','اور انھوں نے ہم کو غصہ دلایا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,56,'26:56','اور ہم ایک مستعد جماعت ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,57,'26:57','پس ہم نے ان کو باغوں اور چشموں سے نکالا');
INSERT INTO "ur_wahid_uddin" VALUES(26,58,'26:58','اور خزانوں اور عمدہ مکانات سے');
INSERT INTO "ur_wahid_uddin" VALUES(26,59,'26:59','یہ ہوا اور ہم نے بنی اسرائیل کو ان چیزوں کا وارث بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,60,'26:60','پس انھوں نے سورج نکلنے کے وقت ان کا پیچھا کیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,61,'26:61','پھر جب دونوں جماعتیں آمنے سامنے ہوئیں تو موسیٰ کے ساتھیوں نے کہا کہ ہم تو پکڑے گئے');
INSERT INTO "ur_wahid_uddin" VALUES(26,62,'26:62','موسیٰ نے کہا کہ ہرگز نہیں ، بے شک میرا رب میرے ساتھ ہے وہ مجھ کو راہ بتائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(26,63,'26:63','پھر ہم نے موسیٰ کو وحی کی کہ اپنا عصا دریا پر مارو پس وہ پھٹ گیا اور ہر حصہ ایسا ہوگیا جیسے بڑا پہاڑ');
INSERT INTO "ur_wahid_uddin" VALUES(26,64,'26:64','اور ہم نے دوسرے فریق کو بھی اس کے قریب پہنچادیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,65,'26:65','اور ہم نے موسیٰ کواور ان سب کو جو اس کے ساتھ تھے بچالیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,66,'26:66','پھر دوسروں کو غرق کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,67,'26:67','بے شک اس کے اندر نشانی ہے اور ان میں سے اکثر ماننے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,68,'26:68','اور بے شک تیرا رب زبردست ہے، رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,69,'26:69','اور ان کو ابراہیم کا قصہ سناؤ');
INSERT INTO "ur_wahid_uddin" VALUES(26,70,'26:70','جب کہ اس نے اپنے باپ سے اور اپنی قوم سے کہا کہ تم کس چیز کی عبادت کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,71,'26:71','انھوں نے کہا کہ ہم بتوں کی عبادت کرتے ہیں اور ہم برابر اس پر جمے رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(26,72,'26:72','ابراہیم نے کہا، کیا یہ تمھاری سنتے ہیں جب تم ان کو پکارتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,73,'26:73','یا وہ تم کو نفع نقصان پہنچاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,74,'26:74','انھوں نے کہا بلکہ ہم نے اپنے باپ دادا کو ایسا ہی کرتے ہوئے پایا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,75,'26:75','ابراہیم نے کہا، کیا تم نے ان چیزوں کو دیکھا بھی جن کی عبادت کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,76,'26:76','تم بھی اور تمھارے بڑے بھی');
INSERT INTO "ur_wahid_uddin" VALUES(26,77,'26:77','یہ سب میرے دشمن ہیں سوا ایک خداوند عالم کے');
INSERT INTO "ur_wahid_uddin" VALUES(26,78,'26:78','جس نے مجھے پیدا کیا، پھر وہی میری رہنمائی فرماتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,79,'26:79','اور جو مجھ کو کھلاتا ہے اور پلاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,80,'26:80','اور جب میں بیمار ہوتا ہوں تو وہی مجھ کو شفا دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,81,'26:81','اور جو مجھ کو موت دے گا پھر مجھ کو زندہ کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(26,82,'26:82','اور وہ جس سے میں امید رکھتا ہوں کہ بدلے کے دن میری خطا معاف کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(26,83,'26:83','اے میرے رب، مجھ کو حکمت عطا فرما اور مجھ کو نیک لوگوں میں شامل فرما');
INSERT INTO "ur_wahid_uddin" VALUES(26,84,'26:84','اور میرا بول سچا رکھ بعد کے آنے والوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(26,85,'26:85','اور مجھے باغ نعمت کے وارثوں میں سے بنا');
INSERT INTO "ur_wahid_uddin" VALUES(26,86,'26:86','اور میرے باپ کو معاف فرما، بے شک وہ گمراہوں میں سے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,87,'26:87','اور مجھ کو اس دن رسوا نہ کر جب کہ لوگ اٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(26,88,'26:88','جس دن نہ مال کام آئے گا اور نہ اولاد');
INSERT INTO "ur_wahid_uddin" VALUES(26,89,'26:89','مگر وہ جو اللہ کے پاس قلب سلیم لے کر آئے');
INSERT INTO "ur_wahid_uddin" VALUES(26,90,'26:90','اور جنت ڈرنے والوں کے قریب لائی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(26,91,'26:91','اور جہنم گمراہوں کے ليے ظاہر کی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(26,92,'26:92','اور ان سے کہا جائے گا، کہاں ہیں وہ جن کی تم عبادت کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(26,93,'26:93','اللہ کے سوا کیا وہ تمھاری مدد کریں گے، یا وہ اپنا بچاؤ کرسکتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,94,'26:94','پھر اس میں اوندھے منہ ڈال دئے جائیں گےوہ اور گمراہ لوگ');
INSERT INTO "ur_wahid_uddin" VALUES(26,95,'26:95','اور ابلیس کا لشکر، سب کے سب');
INSERT INTO "ur_wahid_uddin" VALUES(26,96,'26:96','وہ اس میں باہم جھگڑتے ہوئے کہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(26,97,'26:97','خدا کی قسم، ہم کھلی ہوئی گمراہی میں تھے');
INSERT INTO "ur_wahid_uddin" VALUES(26,98,'26:98','جب کہ ہم تم کو خداوند عالم کے برابر کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(26,99,'26:99','اور ہم کو تو بس مجرموں نے راستہ سے بھٹکایا');
INSERT INTO "ur_wahid_uddin" VALUES(26,100,'26:100','پس اب ہمارا کوئی سفارشی نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,101,'26:101','اور نہ کوئی مخلص دوست');
INSERT INTO "ur_wahid_uddin" VALUES(26,102,'26:102','پس کاش ہم کو پھر واپس جانا ہو کہ ہم ایمان والوں میں سے بنیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,103,'26:103','بے شک اس میں نشانی ہے اور ان میں اکثر لوگ ایمان لانے والے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,104,'26:104','اور بے شک تیرا رب زبردست ہے، رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,105,'26:105','نوح کی قوم نے رسولوں کوجھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(26,106,'26:106','جب کہ ان کے بھائی نوح نے ان سے کہا، کیا تم ڈرتے نہیں ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,107,'26:107','میں تمھارے ليے ایک امانت دار رسول ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,108,'26:108','پس تم لوگ اللہ سے ڈرو، اور میری بات مانو');
INSERT INTO "ur_wahid_uddin" VALUES(26,109,'26:109','اور میں اس پر تم سے کوئی اجر نہیں مانگتا میرا اجر تو صرف اللہ رب العالمین کے ذمہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,110,'26:110','پس تم اللہ سے ڈرو اور میری بات مانو');
INSERT INTO "ur_wahid_uddin" VALUES(26,111,'26:111','انھوں نے کہا کیا ہم تم کو مان لیں ، حالاں کہ تمھاری پیروی رذیل لوگوں نے کی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,112,'26:112','نوح نے کہا کہ مجھ کو کیا خبر جو وہ کرتے رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,113,'26:113','ان کا حساب تو میرے رب کے ذمہ ہے، اگر تم سمجھو');
INSERT INTO "ur_wahid_uddin" VALUES(26,114,'26:114','اور میں مومنوں کو دور کرنے والا نہیں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,115,'26:115','میں تو بس ایک کھلا ہوا ڈرانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,116,'26:116','انھوں نے کہا کہ اے نوح اگر تم باز نہ آئے تو ضرور سنگسار کردئے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(26,117,'26:117','نوح نے کہا کہ اے میرے رب ، میری قوم نے مجھے جھٹلادیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,118,'26:118','پس تو میرے اور ان کے درمیان واضح فیصلہ فرمادے اور مجھ کو اور جو مومن میرے ساتھ ہیں ان کو نجات دے');
INSERT INTO "ur_wahid_uddin" VALUES(26,119,'26:119','پھر ہم نے اس کو اور اس کے ساتھیوں کو ایک بھری ہوئی کشتی میں بچالیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,120,'26:120','پھر اس کے بعد ہم نے باقی لوگوں کو غرق کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,121,'26:121','یقیناً اس کے اندر نشانی ہے، اور ان میں سے اکثر لوگ ماننے والے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,122,'26:122','اور بے شک تیرا رب وہی زبردست ہے، رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,123,'26:123','عاد نے رسولوں کو جھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(26,124,'26:124','جب کہ ان کے بھائی ہود نے ان سے کہا کہ کیا تم لوگ ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,125,'26:125','میں تمھارے ليے ایک معتبر رسول ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,126,'26:126','پس اللہ سے ڈرو اور میری بات مانو');
INSERT INTO "ur_wahid_uddin" VALUES(26,127,'26:127','اور میں اس پر تم سے کوئی بدلہ نہیں مانگتا میرا بدلہ صرف خداوند عالم کے ذمہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,128,'26:128','کیا تم ہر اونچی زمین پر لاحاصل ایک یاد گار عمارت بناتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,129,'26:129','اور بڑے بڑے محل تعمیر کرتے ہو گویا تمھیں ہمیشہ رہنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,130,'26:130','اور جب کسی پر ہاتھ ڈالتے ہو تو جباربن کر ڈالتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,131,'26:131','پس تم اللہ سے ڈرو اور میری بات مانو');
INSERT INTO "ur_wahid_uddin" VALUES(26,132,'26:132','اور اس اللہ سے ڈرو جس نے ان چیزوں سے تمھیں مدد پہنچائی جن کو تم جانتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,133,'26:133','اس نے تمھاری مدد کی چوپایوں اور اولاد سے');
INSERT INTO "ur_wahid_uddin" VALUES(26,134,'26:134','اور باغوں اور چشموں سے');
INSERT INTO "ur_wahid_uddin" VALUES(26,135,'26:135','میں تمھارے اوپر ایک بڑے دن کے عذاب سے ڈرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,136,'26:136','انھوں نے کہا ہمارے ليے برابر ہے، خواہ تم نصیحت کرو یا نصیحت کرنے والوں میں سے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(26,137,'26:137','یہ تو بس اگلے لوگوں کی ایک عادت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,138,'26:138','اور ہم پر ہر گز عذاب آنے والا نہیں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,139,'26:139','پس انھوں نے اس کو جھٹلادیا، پھر ہم نے ان کو ہلاک کردیا، بے شک اس کے اندر نشانی ہے اور ان میں سے اکثر لوگ ماننے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,140,'26:140','اور بے شک تمھارا رب وہ زبردست ہے، رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,141,'26:141','ثمود نے رسولوں کو جھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(26,142,'26:142','جب ان کے بھائی صالح نے ان سے کہا کیا تم ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,143,'26:143','میں تمھارے ليے ایک معتبر رسول ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,144,'26:144','پس تم اللہ سے ڈرو اور میری بات مانو');
INSERT INTO "ur_wahid_uddin" VALUES(26,145,'26:145','اور میں تم سے اس پر کوئی بدلہ نہیں مانگتا میرا بدلہ صرف خدا وند عالم کے ذمہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,146,'26:146','کیا تم کو ان چیزوں میں بے فکری سے رہنے دیا جائے گا جو یہاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,147,'26:147','باغوں اور چشموں میں');
INSERT INTO "ur_wahid_uddin" VALUES(26,148,'26:148','اور کھیتوں اور رس بھرے خوشوں والی کھجوروں میں');
INSERT INTO "ur_wahid_uddin" VALUES(26,149,'26:149','اور تم پہاڑ کھود کر فخر کرتے ہوئے مکان بناتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,150,'26:150','پس اللہ سے ڈرو اور میری بات مانو');
INSERT INTO "ur_wahid_uddin" VALUES(26,151,'26:151','اور حد سے گزر جانے والوں کی بات نہ مانو');
INSERT INTO "ur_wahid_uddin" VALUES(26,152,'26:152','جو زمین میں خرابی کرتے ہیں اور اصلاح نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(26,153,'26:153','انھوں نے کہا، تم پر تو کسی نے جادو کردیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,154,'26:154','تم صرف ہمارے جیسے ایک آدمی ہو، پس تم کوئی نشانی لاؤ اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,155,'26:155','صالح نے کہا یہ ایک اونٹنی ہے اس کے ليے پانی پینے کی ایک باری ہے اور ایک مقرر دن کی باری تمھارے ليے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,156,'26:156','اور اس کو برائی کے ساتھ مت چھیڑنا ورنہ ایک بڑے دن کا عذاب تم کو پکڑلے گا');
INSERT INTO "ur_wahid_uddin" VALUES(26,157,'26:157','پھر انھوں نے اس اونٹنی کو مار ڈالا، پھر وہ پشیمان ہوکر رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(26,158,'26:158','پھر ان کو عذاب نے پکڑلیا بے شک اس میں نشانی ہے اور ان میں سے اکثر ماننے والے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,159,'26:159','اور بے شک تمھارا رب وہ زبردست ہے، رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,160,'26:160','لوط کی قوم نے رسولوں کو جھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(26,161,'26:161','جب ان کے بھائی لوط نے ان سے کہا، کیا تم ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,162,'26:162','میں تمھارے ليے ایک معتبر رسول ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,163,'26:163','پس اللہ سے ڈرو اور میری بات مانو');
INSERT INTO "ur_wahid_uddin" VALUES(26,164,'26:164','میں اس پر تم سے کوئی بدلہ نہیں مانگتا، میرا بدلہ تو خداوند عالم کے ذمہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,165,'26:165','کیا تم دنیا والوں میں سے مردوں کے پاس جاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,166,'26:166','اور تمھارے رب نے تمھارے ليے جو بیویاں پیدا کی ہیں ان کو چھوڑتے ہو، بلکہ تم حد سے گزر جانے والے لوگ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,167,'26:167','انھوں نے کہا کہ اے لوط، اگر تم باز نہ آئے تو ضرور تم نکال دئے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(26,168,'26:168','اس نے کہا میں تمھارے عمل سے سخت بیزار ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,169,'26:169','اے میرے رب، تو مجھ کو اور میرے گھر والوں کو ان کے عمل سے نجات دے');
INSERT INTO "ur_wahid_uddin" VALUES(26,170,'26:170','پس ہم نے اس کو اور اس کے سب گھر والوں کو بچالیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,171,'26:171','مگر ایک بڑھیا کہ وہ رہنے والوں میں رہ گئی');
INSERT INTO "ur_wahid_uddin" VALUES(26,172,'26:172','پھر ہم نے دوسروں کو ہلاک کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,173,'26:173','اور ہم نے ان پر برسایا ایک مینھ پس كيسا برا مينھ تھا جو ان پر برسا جن کو ڈرایا گیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(26,174,'26:174','بے شک اس میں نشانی ہے اور ان میں سے اکثر ماننے والے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,175,'26:175','اور بے شک تیرا رب وہ زبردست ہے، رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,176,'26:176','اصحابِ ایکہ نے رسولوں کو جھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(26,177,'26:177','جب شعیب نے ان سے کہا کیا تم ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,178,'26:178','میں تمھارے ليے ایک معتبر رسول ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,179,'26:179','پس اللہ سے ڈرو اور میری بات مانو');
INSERT INTO "ur_wahid_uddin" VALUES(26,180,'26:180','اور میں اس پر تم سے کوئی بدلہ نہیں مانگتا میرا بدلہ خداوند عالم کے ذمہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,181,'26:181','تم لوگ پورا پورا ناپو اور نقصان دینے والوں میں سے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(26,182,'26:182','اور سیدھی ترازو سے تولو');
INSERT INTO "ur_wahid_uddin" VALUES(26,183,'26:183','اور لوگوں کو ان کی چیزیں گھٹاکر نہ دو اور زمین میں فساد نہ پھیلاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(26,184,'26:184','اور اس ذات سے ڈرو جس نے تم کو پیدا کیا ہے اور گزشتہ نسلوں کو بھی');
INSERT INTO "ur_wahid_uddin" VALUES(26,185,'26:185','انھوں نے کہا تم پر تو کسی نے جادو کردیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,186,'26:186','اور تم ہمارے ہی جیسے ایک آدمی ہو اور ہم تو تم کو جھوٹے لوگوں میں سے خیال کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,187,'26:187','پس ہمارے اوپر آسمان سے کوئی ٹکڑا گراؤ اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,188,'26:188','شعیب نے کہا میرا رب خوب جانتا ہے جو کچھ تم کر رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,189,'26:189','پس انھوں نے اس کو جھٹلادیا پھر ان کو بادل والے دن کے عذاب نے پکڑ لیا');
INSERT INTO "ur_wahid_uddin" VALUES(26,190,'26:190','بے شک وہ ایک بڑے دن کا عذاب تھا بے شک اس میں نشانی ہے اور ان میں سے اکثر ماننے والے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,191,'26:191','اور بے شک تمھارا رب زبردست ہے، رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,192,'26:192','اور بے شک یہ خدا وند عالم کا اتارا ہوا کلام ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,193,'26:193','اس کو امانت دار فرشتہ لے کر اترا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,194,'26:194','تمھارے دل پر تاکہ تم ڈرانے والوں میں سے بنو');
INSERT INTO "ur_wahid_uddin" VALUES(26,195,'26:195','صاف عربی زبان میں');
INSERT INTO "ur_wahid_uddin" VALUES(26,196,'26:196','اور اس کا ذکر اگلے لوگوں کی کتابوں میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,197,'26:197','اور کیا ان کے ليے یہ نشانی نہیں ہے کہ اس کو بنی اسرائیل کے علماء جانتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,198,'26:198','اور اگر ہم اس کو کسی عجمی پر اتارتے');
INSERT INTO "ur_wahid_uddin" VALUES(26,199,'26:199','پھر وہ ان کو پڑھ کر سناتا تو وہ اس پر ایمان لانے والے نہ بنتے');
INSERT INTO "ur_wahid_uddin" VALUES(26,200,'26:200','اسی طرح ہم نے ایمان نہ لانے کو مجرموں کے دلوں میں ڈال رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,201,'26:201','یہ لوگ ایمان نہ لائیں گے جب تک سخت عذاب نہ دیکھ لیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,202,'26:202','پس وہ ان پر اچانک آجائے گا اور ان کو خبر بھی نہ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(26,203,'26:203','پھر وہ کہیں گے کہ کیا ہم کو کچھ مہلت مل سکتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,204,'26:204','کیا وہ ہمارے عذاب کو جلد مانگ رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,205,'26:205','بتاؤ کہ اگر ہم ان کو چند سال تک فائدہ پہنچاتے رہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,206,'26:206','پھر ان پر وہ چیز آجائے جس سے انھیں ڈرایا جارہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,207,'26:207','تو یہ فائدہ مندی ان کے کس کام آئے گی');
INSERT INTO "ur_wahid_uddin" VALUES(26,208,'26:208','اور ہم نے کسی بستی کو بھی ہلاک نہیں کیا مگر اس کے ليے ڈرانے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(26,209,'26:209','یاد دلانے کے ليے، اور ہم ظالم نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,210,'26:210','اور اس کو شیطان لے کر نہیں اترے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,211,'26:211','نہ یہ ان کے ليے لائق ہے اور نہ وہ ایسا کرسکتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,212,'26:212','وہ اس کو سننے سے روک دئے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,213,'26:213','پس تم اللہ کے ساتھ کسی دوسرے معبود کو نہ پکارو کہ تم بھی سزا پانے والوں میں سے ہوجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(26,214,'26:214','اور اپنے قریبی رشتہ داروں کو ڈراؤ');
INSERT INTO "ur_wahid_uddin" VALUES(26,215,'26:215','اور ان لوگوں کے ليے اپنے بازو جھکائے رکھو جو مومنین میں داخل ہوکر تمھاری پیروی کریں');
INSERT INTO "ur_wahid_uddin" VALUES(26,216,'26:216','پس اگر وہ تمھاری نافرمانی کریں تو کہو کہ جو کچھ تم کر رہے ہو، میں اس سے بری ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(26,217,'26:217','اور زبردست اور مہربان خدا پر بھروسہ رکھو');
INSERT INTO "ur_wahid_uddin" VALUES(26,218,'26:218','جو دیکھتا ہے تم کو جب کہ تم اٹھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(26,219,'26:219','اور تمھاری نقل و حرکت نمازیوں کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(26,220,'26:220','بے شک وہ سننے والا، جاننے والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(26,221,'26:221','کیا میں تمھیں بتاؤں کہ شیطان کس پر اترتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,222,'26:222','وہ ہر جھوٹے گنہ گار پر اترتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,223,'26:223','وہ کان لگاتے ہیں اور ان میں سے اکثر جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,224,'26:224','اور شاعروں کے پیچھے بے راہ لوگ چلتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,225,'26:225','کیا تم نہیں دیکھتے کہ وہ ہروادی میں بھٹکتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,226,'26:226','اور وہ کہتے ہیں جو وہ کرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(26,227,'26:227','مگر جو لوگ ایمان لائے اور اچھے کام کئے اور انھوں نے اللہ کو بہت یاد کیا اور انھوں نے بدلہ لیا بعد اس کے کہ ان پر ظلم ہوا اور ظلم کرنے والوں کو جلد معلوم ہوجائے گا کہ ان کو کیسی جگہ لوٹ کر جانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(27,1,'27:1','طٰسٓیہ آیتیں ہیں قران کی اور ایک واضح کتاب کی');
INSERT INTO "ur_wahid_uddin" VALUES(27,2,'27:2','رہنمائی اور خوش خبری ایمان والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(27,3,'27:3','جو نماز قائم کرتے ہیں اور زکوٰۃ دیتے ہیں اور وہ آخرت پر یقین رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,4,'27:4','جو لوگ آخرت پر ایمان نہیں رکھتے ان کے کاموں کو ہم نے ان کے ليے خوش نما بنادیا ہے، پس وہ بھٹک رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,5,'27:5','یہ لوگ ہیں جن کے ليے بری سزا ہے اور وہ آخرت میں سخت خسارے میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(27,6,'27:6','اور بے شک قرآن تم کو ایک حکیم اور علیم کی طرف سے دیا جارہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(27,7,'27:7','جب موسیٰ نے اپنے گھر والوں سے کہا کہ میں نے ایک آگ دیکھی ہے میں وہاں سے کوئی خبر لاتا ہوں یا آگ کا کوئی انگارہ لاتا ہوں تاکہ تم تاپو');
INSERT INTO "ur_wahid_uddin" VALUES(27,8,'27:8','پھر جب وہ اس کے پاس پہنچا تو آواز دی گئی کہ مبارک ہے وہ جو آگ میں ہے اورجو اس کے پاس ہے اور پاک ہے اللہ جو رب ہے سارے جہان کا');
INSERT INTO "ur_wahid_uddin" VALUES(27,9,'27:9','اے موسیٰ، یہ میں ہوں اللہ، زبردست حکیم');
INSERT INTO "ur_wahid_uddin" VALUES(27,10,'27:10','اور تم اپنا عصا ڈال دو پھر جب اس نے اس کو اس طرح حرکت کرتے ہوئے دیکھا جیسے وہ سانپ ہو تو وہ پیچھے کو مڑا اور پلٹ کر نہ دیکھا اے موسیٰ، ڈرو نہیں ، میرے حضور پیغمبر ڈرا نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(27,11,'27:11','مگر جس نے زیادتی کی پھر اس نے برائی کے بعد اس کو بھلائی سے بدل دیا، تو میں بخشنے والا، مہربان ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(27,12,'27:12','اور تم اپنا ہاتھ اپنے گریبان میں ڈالو، وہ کسی عیب کے بغیر سفید نکلے گا یہ دونوں مل کر نونشانیوں کے ساتھ فرعون اور اس کی قوم کے پاس جاؤبے شک وہ نافرمان لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,13,'27:13','پس جب ان کے پاس ہماری واضح نشانیاں آئیں ، انھوں نے کہا یہ کھلا ہوا جادو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(27,14,'27:14','اور انھوں نے ان کا انکار کیا، حالاں کہ ان کے دلوں نے ان کا یقین کرلیا تھا، ظلم اور گھمنڈ کی وجہ سے پس دیکھو کیسا برا انجام ہوا مفسدوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(27,15,'27:15','اور ہم نے داؤد اور سلیمان کو علم عطا کیا اور ان دونوں نے کہا کہ شکر ہے اللہ کے ليے جس نے ہم کو اپنے بہت سے ایمان والے بندوں پر فضیلت عطا فرمائی');
INSERT INTO "ur_wahid_uddin" VALUES(27,16,'27:16','اور داؤد کا وارث سلیمان ہوا اور کہا کہ اے لوگو، ہم کو پرندوں کی بولی سکھائی گئی ہے، اور ہم کو ہر قسم کی چیز دی گئی بے شک یہ کھلا ہوا فضل ہے');
INSERT INTO "ur_wahid_uddin" VALUES(27,17,'27:17','اور سلیمان کے ليے اس کا لشکر جمع کیا گیا، جن اور انسان اور پرندے، پھر ان کی جماعتیں بنائی جاتیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,18,'27:18','یہاں تک کہ جب وہ چونٹیوں کی وادی پر پہنچے ایک چیونٹی نے کہا، اے چیونٹیو، اپنے سوراخوں میں داخل ہوجاؤ، کہیں سلیمان اور اس کا لشکر تم کوکچل ڈالیں اور ان کو خبر بھی نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,19,'27:19','پس سلیمان اس کی بات پر مسکراتے ہوئے ہنس پڑا اور کہا، اے میرے رب مجھے توفیق دے کہ میں تیری نعمت کا شکر ادا کروں جو تو نے مجھ پر اورمیرے والدین پر کیا ہے اور یہ کہ میں نیک کام کروں جو تجھ کو پسند ہو اور اپنی رحمت سے تو مجھ کو اپنے نیک بندوں میں داخل کر');
INSERT INTO "ur_wahid_uddin" VALUES(27,20,'27:20','اور سلیمان نے پرندوں کا جائزہ لیا تو کہا، کیا بات ہے کہ میں ہدہد کو نہیں دیکھ رہا ہوں کیا وہ کہیں غائب ہوگیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(27,21,'27:21','میں اس کو سخت سزا دوں گا یا اس کو ذبح کردوں گا، یا وہ میرے سامنے کوئی صاف حجت لائے');
INSERT INTO "ur_wahid_uddin" VALUES(27,22,'27:22','زیادہ دیر نہیں گزری تھی کہ اس نے آکر کہا، کہ میں ایک چیز کی خبر لایا ہوں جس کی آپ کو خبر نہ تھی اور میں سبا سے ایک یقینی خبر لے کر آیا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(27,23,'27:23','میں نے پایا کہ ایک عورت ان پر بادشاہی کرتی ہے اور اس کو سب چیز ملی ہے اور اس کا ایک بڑا تخت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(27,24,'27:24','میں نے اس کو اور اس کی قوم کو پایا کہ وہ سورج کو سجدہ کرتے ہیں اللہ کے سوا اور شیطان نے ان کے اعمال ان کے ليے خوش نما بنادئے، پھر ان کو راستے سے روک دیا، پس وہ راہ نہیں پاتے');
INSERT INTO "ur_wahid_uddin" VALUES(27,25,'27:25','کہ وہ اللہ کو سجدہ نہ کریں جو آسمانوں اور زمین کی چھپی ہوئی چیز کونکالتا ہے اور وہ جانتا ہے جو کچھ تم چھپاتے ہو اور جو کچھ تم ظاہر کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,26,'27:26','اللہ، اس کے سوا کوئی معبود نہیں ، مالک عرش عظیم کا');
INSERT INTO "ur_wahid_uddin" VALUES(27,27,'27:27','سلیمان نے کہا، ہم دیکھیں گے کہ تم نے سچ کہا یا تم جھوٹوں میں سے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,28,'27:28','میرا یہ خط لے کر جاؤ پھر اس کو ان لوگوں کی طرف ڈال دو پھر ان سے ہٹ جانا پھر دیکھنا کہ وہ کیا رد عمل ظاہر کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,29,'27:29','ملکہ سبا نے کہا کہ اے دربار والو، میری طرف ایک باوقعت خط ڈالا گیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(27,30,'27:30','وہ سلیمان کی طرف سے ہے اور وہ ہے— شروع اللہ کے نام سے جو بڑا مہربان، نہایت رحم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(27,31,'27:31','کہ تم میرے مقابلہ میں سرکشی نہ کرو اور مطیع ہوکر میرے پاس آجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(27,32,'27:32','ملکہ نے کہا کہ اے درباریو، میرے معاملہ میں مجھے رائے دو میں کسی معاملہ کا فیصلہ نہیں کرتی جب تک تم لوگ موجود نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,33,'27:33','انھوں نے کہا، ہم لوگ زور آور ہیں اور سخت لڑائی والے ہیں اور فیصلہ آپ کے اختیار میں ہے پس آپ دیکھ لیں کہ آپ کیا حکم دیتی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,34,'27:34','ملکہ نے کہا کہ بادشاہ لوگ جب کسی بستی میں داخل ہوتے ہیں تو اس کو خراب کردیتے ہیں اور اس کے عزت والوں کو ذلیل کردیتے ہیں اور یہی یہ لوگ کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(27,35,'27:35','اور میں ان کی طرف ایک ہدیہ بھیجتی ہوں ، پھر دیکھتی ہوں کہ سفیر کیا جواب لاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,36,'27:36','پھر جب سفیر سلیمان کے پاس پہنچا، اس نے کہا کیا تم لوگ مال سے میری مدد کرنا چاہتے ہو پس اللہ نے جو کچھ مجھے دیا ہے وہ اس سے بہتر ہے جو اس نے تم کو دیا ہے بلکہ تم ہی اپنے تحفہ سے خوش ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,37,'27:37','ان کے پاس واپس جاؤ ہم ان پر ایسے لشکر لے کر آئیں گے جن کا مقابلہ وہ نہ کرسکیں گے اور ہم ان کو وہاں سے بے عزت کرکے نکال دیں گے اور وہ خوار ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(27,38,'27:38','سلیمان نے کہا کہ اے دربار والو، تم میں سے کون اس کا تخت میرے پاس لاتا ہے اس سے پہلے کہ وہ لوگ مطیع ہوکر میرے پاس آئیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,39,'27:39','جنوں میں سے ایک دیو نے کہا، میں اس کو آپ کے پاس لے آؤں گا اس سے پہلے کہ آپ اپنی جگہ سے اٹھیں ، اور میں اس پر قدرت رکھنے والا، امانت دار ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(27,40,'27:40','جس کے پاس کتاب کا ایک علم تھا اس نے کہا، میں آپ کے پلک جھپکنے سے پہلے اس کو لادوں گا پھر جب اس نے تخت کو اپنے پاس رکھا ہوا دیکھا تو اس نے کہا، یہ میرے رب کا فضل ہے تاکہ وہ مجھے جانچے کہ میں شکر کرتا ہوں یاناشکری اور جو شخص شکر کرے تو وہ اپنے ہی ليے شکر کرتا ہے اور جو شخص ناشکری کرے تو میرا رب بے نیاز ہے، کرم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(27,41,'27:41','سلیمان نے کہا کہ اس کے تخت کا روپ بدل دو، دیکھیں وہ سمجھ پاتی ہے یاوہ ان لوگوں میں سے ہوجاتی ہے جن کو سمجھ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,42,'27:42','پس جب وہ آئی تو کہا گیا کیا تمھارا تخت ایسا ہی ہے اس نے کہا، گویا کہ یہ وہی ہے اور ہم کو اس سے پہلے معلوم ہوچکا تھا اور ہم فرماں برداروں میں تھے');
INSERT INTO "ur_wahid_uddin" VALUES(27,43,'27:43','اور اس کو روک رکھا تھاان چیزوں نے جن کو وہ اللہ کے سوا پوجتی تھی وہ منکر لوگوں میں سے تھی');
INSERT INTO "ur_wahid_uddin" VALUES(27,44,'27:44','اس سے کہا گیا کہ محل میں داخل ہو پس جب اس نے اس کو دیکھا تو اس کو خیال کیا کہ وہ گہرا پانی ہے اور اپنی دونوں پنڈلیاں کھول دیں سلیمان نے کہا، یہ تو ایک محل ہے جو شیشوں سے بنایا گیا ہے اس نے کہا کہ اے میرے رب، میں نے اپنی جان پر ظلم کیا اور میں سلیمان کے ساتھ ہوکر اللہ رب العالمین پر ایمان لائی');
INSERT INTO "ur_wahid_uddin" VALUES(27,45,'27:45','اور ہم نے ثمود کی طرف ان کے بھائی صالح کو بھیجا، کہ اللہ کی عبادت کرو، پھر وہ دو فریق بن کر آپس میں جھگڑنے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(27,46,'27:46','اس نے کہا کہ اے میری قوم کے لوگو، تم بھلائی سے پہلے برائی کے ليے کیوں جلدی کر رہے ہو تم اللہ سے معافی کیوں نہیں چاہتے کہ تم پر رحم کیا جائے');
INSERT INTO "ur_wahid_uddin" VALUES(27,47,'27:47','انھوں نے کہا، ہم تو تم کو اور تمھارے ساتھ والوں کو منحوس سمجھتے ہیں اس نے کہا کہ تمھاری بری قسمت اللہ کے پاس ہے بلکہ تم تو آزمائے جارہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,48,'27:48','اور شہر میں نو شخص تھے جو زمین میں فساد کرتے تھے اور وہ اصلاح کا کام نہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(27,49,'27:49','انھوں نے کہا کہ تم لوگ اللہ کی قسم کھاؤ کہ ہم اس کو اور اس کے لوگوں کو چپکے سے ہلاک کردیں گے پھر اس کے ولی سے کہہ دیں گے کہ ہم اس کے گھر والوں کی ہلاکت کے وقت موجود نہ تھے اور بے شک ہم سچے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,50,'27:50','اور انھوں نے ایک تدبیر کی اور ہم نے بھی ایک تدبیر کی اور ان کو خبر بھی نہ ہوئی');
INSERT INTO "ur_wahid_uddin" VALUES(27,51,'27:51','پس دیکھو کیسا ہوا ان کی تدبیر کا انجام ہم نے ان کو اور ان کی پوری قوم کو ہلاک کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(27,52,'27:52','پس یہ ہیں ان کے گھر ویران پڑے ہوئے ان کے ظلم کے سبب سےبے شک اس میں سبق ہے ان لوگوں کے ليے جو جانیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,53,'27:53','اور ہم نے ان لوگوں کو بچالیا جو ایمان لائے اور جو ڈرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(27,54,'27:54','اور لوط کو جب اس نے اپنی قوم سے کہا، کیا تم بے حیائی کرتے ہو اور تم دیکھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,55,'27:55','کیا تم مردوں کے ساتھ شہوت رانی کرتے ہو، عورتوں کو چھوڑ کر، بلکہ تم لوگ بے سمجھ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,56,'27:56','پھر اس کی قوم کا جواب اس کے سوا کچھ نہ تھا کہ انھوں نے کہا، لوط کے گھر والوں کو اپنی بستی سے نکال دو، یہ لوگ پاک صاف بنتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,57,'27:57','پھر ہم نے اس کو اور اس کے لوگوں کو نجات دی سوا اس کی بیوی کے جس کا پیچھے رہ جانا ہم نے طے کردیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(27,58,'27:58','اور ہم نے ان پر برسایا ایک ہولناک برسانا پس کیسا برابر ساؤتھا ان پر جن کو آگاہ کیا جاچکا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(27,59,'27:59','کہو حمد ہے اللہ کے ليے اور سلام اس کے ان بندوں پر جن کو اس نے منتخب فرمایا کیا اللہ بہتر ہے یا وہ جن کو وہ شریک کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,60,'27:60','بھلا وہ کون ہے جس نے آسمانوں اور زمین کو پیدا کیا اور تمھارے ليے آسمان سے پانی اتارا پھر ہم نے اس سے رونق والے باغ اگائے تمھارے بس میں نہ تھا کہ تم ان درختوں کو اگاسکتے کیا اللہ کے ساتھ کوئی اور معبود ہے بلکہ وہ راہ سے انحراف کرنے والے لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,61,'27:61','بھلا کس نے زمین کو ٹھہرنے کے لائق بنایا اور اس کے درمیان ندیاں جاری کیں اور اس کے ليے اس نے پہاڑ بنائے اور دو سمندروں کے درمیان پردہ ڈال دیا کیا اللہ کے ساتھ کوئی اور معبود ہے، بلکہ ان کے اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(27,62,'27:62','کون ہے جو بے بس کی پکار کو سنتا ہے اور اس کے دکھ کو دور کردیتا ہے اور تم کو زمین کا جانشین بناتا ہے کیا اللہ کے سوا کوئی اور معبود ہے تم بہت کم نصیحت پکڑتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,63,'27:63','کون ہے جو تم کو خشکی اور سمندر کے اندھیروں میں راستہ دکھاتا ہے اور کون اپنی رحمت کے آگے ہواؤں کو خوش خبری بناکر بھیجتا ہے کیا اللہ کے ساتھ کوئی اور معبود ہے اللہ بہت برتر ہے اس سے جن کو وہ شریک ٹھہراتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,64,'27:64','کون ہے جو خلق کی ابتدا کرتا ہے اور پھر اس کا اعادہ کرتا ہے اور کون تم کو آسمان اور زمین سے روزی دیتا ہے کیا اللہ کے ساتھ کوئی اور معبود ہے کہو کہ اپنی دلیل لاؤ، اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,65,'27:65','کہو کہ اللہ کے سوا، آسمانوں اور زمین میں کوئی غیب کا علم نہیں رکھتا اور وہ نہیں جانتے کہ وہ کب اٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(27,66,'27:66','بلکہ آخرت کے باب میں ان کا علم الجھ گیا ہے بلکہ وہ اس کی طرف سے شک میں ہیں بلکہ وہ اس سے اندھے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,67,'27:67','اور انکار کرنے والوں نے کہا، کیا جب ہم مٹی ہوجائیں گے اورہمارے باپ دادا بھی، تو کیا ہم زمین سے نکالے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(27,68,'27:68','اس کا وعدہ ہمیں بھی دیا گیا اور اس سے پہلے ہمارے باپ دادا کو بھی یہ محض اگلوں کی کہانیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,69,'27:69','کہو کہ زمین میں چلو پھرو، پس دیکھو کہ مجرموں کا انجام کیا ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(27,70,'27:70','اور ان پر غم نہ کرو اور دل تنگ نہ ہو ان تدبیروں پر جو وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,71,'27:71','اور وہ کہتے ہیں کہ یہ وعدہ کب ہے اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,72,'27:72','کہو کہ جس چیز کی تم جلدی کر رہے ہو شاید اس میں سے کچھ تمھارے پاس آلگا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,73,'27:73','اور بے شک تمھارا رب لوگوں پر بڑے فضل والا ہے مگر ان میں سے اکثر شکر نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(27,74,'27:74','اور بے شک تمھارا رب خوب جانتا ہے جو ان کے سینے چھپائے ہوئے ہیں اور جو وہ ظاہر کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,75,'27:75','اور آسمانوں اور زمین کی کوئی پوشیدہ چیز نہیں ہے جو ایک واضح کتاب میں درج نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,76,'27:76','بے شک یہ قرآن بنی اسرائیل پر بہت سی چیزوں کو واضح کر رہا ہے جن میں وہ اختلاف رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,77,'27:77','اور وہ ہدایت اور رحمت ہے ایمان والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(27,78,'27:78','بے شک تمھارا رب اپنے حکم کے ذریعہ ان کے درمیان فیصلہ کرے گا اور وہ زبردست ہے، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(27,79,'27:79','پس اللہ پر بھروسہ کرو بے شک تم صریح حق پر ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,80,'27:80','تم مُردوں کو نہیں سناسکتے اورنہ تم بہروں کو اپنی پکار سناسکتے ہو جب کہ وہ پیٹھ پھیر کر چلے جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,81,'27:81','اور نہ تم اندھوں کو ان کی گمراہی سے بچا کر راستہ دکھانے والے ہو تم تو صرف ان کو سناسکتے ہو جو ہماری آیتوں پر ایمان لاتے ہیں ، پھر فرماں بردار بن جاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,82,'27:82','اور جب ان پر بات آپڑے گی تو ہم ان کے ليے زمین سے ایک دابّہ (غیر انسانی مخلوق) نکالیں گے جو ان سے کلام کرے گا کہ لوگ ہماری آیتوں پر یقین نہیں رکھتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(27,83,'27:83','اور جس دن ہم ہر امت میں سے ایک گروہ ان لوگوں کا جمع کریں گے جو ہماری آیتوں کو جھٹلاتے تھے، پھر ان کی جماعت بندی کی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(27,84,'27:84','یہاں تک کہ جب وہ آجائیں گے تو خدا کہے گا کہ تم نے میری آیتوں کو جھٹلایا حالاں کہ تمھارا علم ان کا احاطہ نہ کرسکا، یا بولو کہ تم کیا کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(27,85,'27:85','اور ان پر بات پوری ہوجائے گی اس سبب سے کہ انھوں نے ظلم کیا، پس وہ کچھ نہ بول سکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(27,86,'27:86','کیا انھوں نے نہیں دیکھا کہ ہم نے رات بنائی تاکہ لوگ اس میں آرام کریں اور دن کہ وہ اس میں دیکھیں بے شک اس میں نشانیاں ہیں ان لوگوں کے ليے جو یقین کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(27,87,'27:87','اور جس دن صور پھونکا جائے گا تو گھبرا اٹھیں گے جو آسمانوں میں ہیں اور جو زمین میں ہیں مگر وہ جس کو اللہ چاہے اور سب چلے آئیں گے اس کے آگے عاجزی سے');
INSERT INTO "ur_wahid_uddin" VALUES(27,88,'27:88','اور تم پہاڑوں کو دیکھ کر گمان کرتے ہو کہ وہ جمے ہوئے ہیں ، اور وہ چلیں گے جیسے بادل چلیں یہ اللہ کی کاری گری ہے جس نے ہر چیز کو محکم کیا ہے بے شک وہ جانتا ہے جو تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(27,89,'27:89','جو شخص بھلائی لے کر آئے گا تو اس کے ليے اس سے بہتر ہے، اور وہ اس دن گھبراہٹ سے محفوظ ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(27,90,'27:90','اور جو شخص برائی لے کر آیا تو ایسے لوگ اوندھے منہ آگ میں ڈال دئے جائیں گے تم وہی بدلہ پارہے ہو جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(27,91,'27:91','مجھ کو یہی حکم دیا گیا ہے کہ میں اس شہر کے رب کی عبادت کروں جس نے اس کو محترم ٹھہرایا اور ہر چیز اسی کی ہے اور مجھے حکم دیا گیا ہے کہ میں فرماں برداری کرنے والوں میں سے بنوں');
INSERT INTO "ur_wahid_uddin" VALUES(27,92,'27:92','اور یہ کہ قرآن کو سناؤں پھر جو شخص راہ پر آئے گا تو وہ اپنے ليے راہ پر آئے گا اورجو گمراہ ہوا تو کہہ دو کہ میں تو صرف ڈرانے والوں میں سے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(27,93,'27:93','اور کہو کہ سب تعریف اللہ کے ليے ہے، وہ تم کو اپنی نشانیاں دکھائے گا تو تم ان کو پہچان لوگے، اور تمھارا رب اس سے بے خبر نہیں جو تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(28,1,'28:1','طسم');
INSERT INTO "ur_wahid_uddin" VALUES(28,2,'28:2','یہ واضح کتاب کی آیتیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,3,'28:3','ہم موسیٰ اور فرعون کا کچھ حال تم کو ٹھیک ٹھیک سناتے ہیں ، ان لوگوں کے ليے جو ایمان لائیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,4,'28:4','بے شک فرعون نے زمین میں سرکشی کی اور اس نے اس کے باشندوں کو گروہوں میں تقسیم کردیا ان میں سے ایک گروہ کو اس نے کمزور کر رکھا تھا وہ ان کے لڑکوں کو ذبح کرتا تھا اور ان کی عورتوں کو زندہ رکھتا تھا بے شک وہ فساد کرنے والوں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(28,5,'28:5','اور ہم چاہتے تھے کہ ان لوگوں پر احسان کریں جو زمین میں کمزور کردئے گئے تھے اور ان کو پیشوا بنائیں اور ان کو وارث بنادیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,6,'28:6','اور ان کو زمین میں اقتدار عطا کریں اور فرعون اور ہامان اور ان کی فوجوں کو ان كے ذريعه وہی دکھادیں جس سے وہ ڈرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(28,7,'28:7','اور ہم نے موسیٰ کی ماں کو الہام کیا کہ اس کو دودھ پلاؤ پھر جب تم کو اس کی بابت ڈر ہو تو اس کو دریا میں ڈال دو اور نہ اندیشہ کرو اور نہ غمگین ہو ہم اس کو تمھارے پاس لوٹا کر لائیں گے اور اس کو پیغمبروں میں سے بنائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(28,8,'28:8','پھر اس کو فرعون کے گھر والوں نے اٹھالیا، تاکہ وہ ان کے ليے دشمن ہو اور غم کا باعث بنے بے شک فرعون اور ہامان اور ان کے لشکر خطا کار تھے');
INSERT INTO "ur_wahid_uddin" VALUES(28,9,'28:9','اور فرعون کی بیوی نے کہا کہ یہ آنکھ کی ٹھنڈک ہے، میرے ليے اور تمھارے ليے اس کو قتل نہ کرو کیا عجب کہ یہ ہم کو نفع دے یا ہم اس کو بیٹا بنالیں اور وہ سمجھتے نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(28,10,'28:10','اور موسیٰ کی ماں کا دل بے چین ہوگیا قریب تھاکہ وہ اس کو ظاہر کردے اگر ہم اس کے دل کو نہ سنبھالتے کہ وہ یقین کرنے والوں میں سے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(28,11,'28:11','اور اس نے اس کی بہن سے کہا کہ تو اس کے پیچھے پیچھے جا تو وہ اس کو اجنبی بن کر دیکھتی رہی اور ان لوگوں کو خبر نہیں ہوئی');
INSERT INTO "ur_wahid_uddin" VALUES(28,12,'28:12','اور ہم نے پہلے ہی موسیٰ سے دائیوں کو روک رکھا تھا تو لڑکی نے کہا، کیا میں تم کو ایسے گھر والوں کا پتہ دوں جو اس کو تمھارے ليے پالیں اور وہ اس کی خیر خواہی کریں');
INSERT INTO "ur_wahid_uddin" VALUES(28,13,'28:13','پس ہم نے اس کو اس کی ماں کی طرف لوٹادیا تاکہ اس کی آنکھیں ٹھنڈی ہوں اور وہ غمگین نہ ہو اور تاکہ وہ جان لے کہ اللہ کا وعدہ سچا ہے، مگر اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(28,14,'28:14','اور جب موسیٰ اپنی جوانی کو پہنچا اور پورا ہوگیا تو ہم نے اس کو حکمت اور علم عطا کیا اور ہم اسی طرح بدلہ دیتے ہیں نیکی کرنے والوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(28,15,'28:15','اور شہر میں وہ ایسے وقت داخل ہوا جب کہ شہر والے غفلت میں تھے تو اس نے وہاں دو آدمیوں کو لڑتے ہوئے پایا ایک اس کی اپنی قوم کا تھا اور دوسرا دشمنوں میں سے تھا تو جو اس کی قوم میں سے تھا اس نے اس کے خلاف مدد طلب کی جو اس کے دشمنوں میں سے تھا پس موسیٰ نے اس کو گھونسہ مارا پھر اس کا کام تمام کردیا موسیٰ نے کہا کہ یہ شیطان کے کام سے ہے بے شک وہ دشمن ہے، کھلا گمراہ کرنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(28,16,'28:16','اس نے کہا کہ اے میرے رب، میں نے اپنی جان پر ظلم کیا ہے پس تو مجھ کو بخش دے تو خدا نے اس کو بخش دیا بے شک وہ بخشنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(28,17,'28:17','اس نے کہا کہ اے میرے رب، جیسا تو نے میرے اوپر فضل کیا تو میں کبھی مجرموں کا مدد گار نہیں بنوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(28,18,'28:18','پھر صبح کو وہ شہر میں اٹھا ڈرتا ہوا، خبر لیتا ہوا تو دیکھتا کہ وہی شخص جس نے کل مدد مانگی تھی وہی آج پھر اس کو مدد کے ليے پکار رہا ہے موسیٰ نے اس سے کہا، بے شک تم صریح گمراہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(28,19,'28:19','پھر جب اس نے چاہا کہ اس کو پکڑے جو ان دونوں کا دشمن تھا تو اس نے کہا کہ اے موسیٰ، کیا تم مجھ کو قتل کرنا چاہتے ہو جس طرح تم نے کل ایک شخص کو قتل کیا تم تو زمین میں سرکش بن کر رہنا چاہتے ہو تم صلح کرنے والوں میں سے بننا نہیں چاہتے');
INSERT INTO "ur_wahid_uddin" VALUES(28,20,'28:20','اور ایک شخص شہر کے کنارے سے دوڑتا ہوا آیا اس نے کہا اے موسیٰ، دربار والے مشورہ کر رہے ہیں کہ وہ تم کو مار ڈالیں پس تم نکل جاؤ، میں تمھارے خیر خواہوں میں سے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(28,21,'28:21','پھر وہ وہاں سے نکلا ڈرتا ہوا، خبر لیتا ہوا اس نے کہا کہ اے میرے رب، مجھے ظالم لوگوں سے نجات دے');
INSERT INTO "ur_wahid_uddin" VALUES(28,22,'28:22','اور جب اس نے مدین کا رخ کیا تو اس نے کہا، امید ہے کہ میرا رب مجھ کو سیدھا راستہ دکھادے');
INSERT INTO "ur_wahid_uddin" VALUES(28,23,'28:23','اور جب وہ مدین کے پانی پر پہنچا تو وہاں اُس نے لوگوں کی ایک جماعت کو پانی پلاتے ہوئے پایا اور ان سے الگ ایک طرف دو عورتوں کو دیکھا کہ وہ اپنی بکریوں کو روکے ہوئے کھڑی ہیں موسیٰ نے ان سے پوچھا کہ تمھارا کیا ماجرا ہے انھوں نے کہا کہ ہم پانی نہیں پلاتے جب تک چرواہے اپنی بکریاں ہٹا نہ لیں اور ہمارا باپ بہت بوڑھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(28,24,'28:24','تو اس نے ان کے جانوروں کو پانی پلایا پھر سائے کی طرف ہٹ گیا پھر کہا کہ اے میرے رب، تو جو چیز میری طرف خير ميں سے اتارے، میں اس کا محتاج ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(28,25,'28:25','پھر ان دونوں میں سے ایک لڑکی آئی شرم سے چلتی ہوئی اس نے کہا کہ میرا باپ آپ کو بلا رہا ہے کہ آپ نے ہماری خاطر جو پانی پلایا اس کا آپ کو بدلہ دے پھر جب وہ اس کے پاس آیا اور اس سے سارا قصہ بیان کیا تو اس نے کہا کہ اندیشہ نہ کرو تم نے ظالموں سے نجات پائی');
INSERT INTO "ur_wahid_uddin" VALUES(28,26,'28:26','ان میں سے ایک نے کہا کہ اے باپ اس کو ملازم رکھ لیجيے بہترین آدمی جسے آپ ملازم رکھیں وہی ہے جو مضبوط اور امانت دار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(28,27,'28:27','اس نے کہا کہ میں چاہتا ہوں کہ اپنی ان دو لڑکیوں میں سے ایک کا نکاح تمھارے ساتھ کردوں اس شرط پر کہ تم آٹھ سال میری ملازمت کرو پھر اگر تم دس سال پورے کردو تو وہ تمھاری طرف سے ہے اور میں تم پر مشقت ڈالنا نہیں چاہتا ان شاء اللہ تم مجھ کو بھلا آدمی پاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(28,28,'28:28','موسیٰ نے کہا کہ یہ بات میرے اور آپ کے درمیان طے ہے ان دونوں مدتوں میں سے جو بھی میں پوری کروں تو مجھ پر کوئی جبر نہ ہوگا اور اللہ ہمارے قول و قرار پر گواہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(28,29,'28:29','پھر جب موسیٰ نے مدت پوری کردی اور وہ اپنے گھر والوں کے ساتھ روانہ ہوا تو اس نے طور کی طرف سے ایک آگ دیکھی اس نے اپنے گھر والوں سے کہا کہ تم ٹھہرو، میں نے ایک آگ دیکھی ہے شاید میں وہاں سے کوئی خبر لے آؤں یا آگ کا انگارہ تاکہ تم تاپو');
INSERT INTO "ur_wahid_uddin" VALUES(28,30,'28:30','پھر جب وہ وہاں پہنچا تو وادی کے داہنے کنارے سے برکت والے خطہ میں درخت سے پکارا گیا کہ اے موسیٰ، میں اللہ ہوں ، سارے جہان کا مالک');
INSERT INTO "ur_wahid_uddin" VALUES(28,31,'28:31','اور یہ کہ تم اپنا عصا ڈال دو تو جب اس نے اس کو حرکت کرتے ہوئے دیکھا کہ گویا سانپ ہو، تو وہ پیٹھ پھیر کر بھاگا اور اس نے مڑ کر نہ دیکھا اے موسیٰ ،آگے آؤ اور نہ ڈرو تم بالکل محفوظ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(28,32,'28:32','اپنا ہاتھ گریبان میں ڈالو، وہ چمکتا ہوا نکلے گا بغیر کسی مرض کے، اور خوف کے واسطے اپنا بازواپنی طرف ملالو پس یہ تمھارے رب کی طرف سے دو سندیں ہیں فرعون اور اس کے درباریوں کے پاس جانے کے ليے بے شک وہ نافرمان لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,33,'28:33','موسیٰ نے کہا اے میرے رب، میں نے ان میں سے ایک شخص کو قتل کیا ہے تو میں ڈرتا ہوں کہ وہ مجھے مار ڈالیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(28,34,'28:34','اور میرا بھائی ہارون وہ مجھ سے زیادہ فصیح ہے زبان میں ، پس تو اس کو میرے ساتھ مدد گار کی حیثیت سے بھیج کہ وہ میری تائید کرے میں ڈرتا ہوں کہ وہ لوگ مجھے جھٹلادیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(28,35,'28:35','فرمایا کہ ہم تمھارے بھائی کے ذریعہ تمھارے بازو کو مضبوط کردیں گے اور ہم تم دونوں کو غلبہ دیں گے تو وہ تم لوگوں تک نہ پہنچ سکیں گے ہماری نشانیوں کے ساتھ، تم دونوں اور تمھاری پیروی کرنے والے ہی غالب رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(28,36,'28:36','پھر جب موسیٰ ان لوگوں کے پاس ہماری واضح نشانیوں کے ساتھ پہنچا، انھوں نے کہا کہ یہ محض گھڑا ہوا جادو ہے اور یہ بات ہم نے اپنے اگلے باپ دادا میں نہیں سنی');
INSERT INTO "ur_wahid_uddin" VALUES(28,37,'28:37','اور موسیٰ نے کہا میرا رب خوب جانتا ہے اس کو جو اس کی طرف سے ہدایت لے کر آیا ہے اور جس کو آخرت کا گھر ملے گا بے شک ظالم فلاح نہ پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(28,38,'28:38','اور فرعون نے کہا کہ اے دربار والو، میں تمھارے ليے اپنے سوا کسی معبود کو نہیں جانتا تو اے ہامان، میرے ليے مٹی کو آگ دے، پھر میرے ليے ایک اونچی عمارت بنا تاکہ میں موسیٰ کے رب کو جھانک کر دیکھوں ، اور میں تو اس کو ایک جھوٹا آدمی سمجھتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(28,39,'28:39','اور اس نے اور اس کی فوجوں نے زمین میں ناحق گھمنڈ کیا اور انھوں نے سمجھا کہ ان کو ہماری طرف لوٹ کر آنا نہیں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(28,40,'28:40','تو ہم نے اس کو اور اس کی فوجوں کو پکڑا پھر ان کو سمندر میں پھینک دیا تو دیکھو کہ ظالموں کا انجام کیا ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(28,41,'28:41','اور ہم نے ان کو سردار بنایا کہ وہ آگ کی طرف بلاتے ہیں اور قیامت کے دن ان کو مدد نہیں ملے گی');
INSERT INTO "ur_wahid_uddin" VALUES(28,42,'28:42','اور ہم نے اس دنیا میں ان کے پیچھے لعنت لگادی اور قیامت کے دن وہ بدحال لوگوں میں سے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(28,43,'28:43','اور ہم نے اگلی امتوں کو ہلاک کرنے کے بعد موسیٰ کو کتاب دی لوگوں کے ليے بصیرت کا سامان، اور ہدایت اور رحمت تاکہ وہ نصیحت پکڑیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,44,'28:44','اور تم پہاڑ کے مغربی جانب موجود نہ تھے جب کہ ہم نے موسیٰ کو احکام دئے اور نہ تم شاہدین میں شامل تھے');
INSERT INTO "ur_wahid_uddin" VALUES(28,45,'28:45','لیکن ہم نے بہت سی نسلیں پیدا کیں پھر ان پر بہت زمانہ گزر گیا اور تم مدین والوں میں بھی نہ رہتے تھے کہ ان کو ہماری آیتیں سناتے مگر ہم ہیں پیغمبر بھیجنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(28,46,'28:46','اور تم طور کے کنارے نہ تھے جب ہم نے موسیٰ کو پکارا، لیکن یہ تمھارے رب کا انعام ہے، تاکہ تم ایک ایسی قوم کو ڈراؤ جن کے پاس تم سے پہلے کوئی ڈرانے والانہیں آیا تاکہ وہ نصیحت پکڑیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,47,'28:47','اور اگر ایسا نہ ہوتا کہ ان پر ان کے اعمال کے سبب سے کوئی آفت آئی تو وہ کہیں گے کہ اے ہمارے رب، تو نے ہماری طرف کوئی رسول کیوں نہیں بھیجا کہ ہم تیری آیتوں کی پیروی کرتے اورہم ایمان والوں میں سے ہوتے');
INSERT INTO "ur_wahid_uddin" VALUES(28,48,'28:48','پھر جب ان کے پاس ہماری طرف سے حق آیا تو انھوں نے کہا کہ کیوں نہ اس کو ویسا ملا جیسا کہ موسیٰ کو ملاتھا، كيا لوگوں نے اس كا انكار نهيں كيا جو اس سے پهلے موسيٰ كو دياگيا تھا، انھوں نے کہا کہ دونوں جادوگر ہیں ایک د وسرے کے مدد گار، اور انھوں نے کہا کہ ہم دونوں کا انکار کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,49,'28:49','کہو کہ تم اللہ کے پاس سے کوئی کتاب لے آؤ جو ہدایت کرنے میں ان دونوں سے بہتر ہو، میں اس کی پیروی کروں گا اگرتم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(28,50,'28:50','پس اگر یہ لوگ تمھارا کہا نہ کرسکیں تو جان لو کہ وہ صرف اپنی خواہش کی پیروی کر رہے ہیں اور اس سے زیادہ گمراہ کون ہوگا جو اللہ کی ہدایت کے بغیر اپنی خواہش کی پیروی کرے بے شک اللہ ظالم لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(28,51,'28:51','اور ہم نے ان لوگوں کے ليے پے درپے اپنا کلام بھیجا تاکہ وہ نصیحت پکڑیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,52,'28:52','جن لوگوں کو ہم نے اس سے پہلے کتاب دی ہے وہ اس (قرآن) پر ایمان لاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,53,'28:53','اور جب وہ ان کو سنایا جاتا ہے تو وہ کہتے ہیں کہ ہم اس پر ایمان لائے بے شک یہ حق ہے ہمارے رب کی طرف سے، ہم تو پہلے ہی سے اس کو ماننے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,54,'28:54','یہ لوگ ہیں کہ ان کو ان کا اجر دہرا دیا جائے گا، اس پر کہ انھوں نے صبر کیا اور وہ برائی کو بھلائی سے دفع کرتے ہیں اورہم نے جو کچھ ان کو دیا ہے اس میں سے وہ خرچ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,55,'28:55','اور جب وہ لغوبات سنتے ہیں تو وہ اس سے اعراض کرتے ہیں اور کہتے ہیں کہ ہمارے ليے ہمارے اعمال ہیں اور تمھارے ليے تمھارے اعمال تم کو سلام، ہم بے سمجھ لوگوں سے الجھنا نہیں چاہتے');
INSERT INTO "ur_wahid_uddin" VALUES(28,56,'28:56','تم جس کو چاہو ہدایت نہیں دے سکتے بلکہ اللہ جس کو چاہتا ہے ہدایت دیتا ہے اور وہی خوب جانتا ہے جو ہدایت قبول کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,57,'28:57','اور وہ کہتے ہیں کہ اگر ہم تمھارے ساتھ ہو کر اس ہدایت پر چلنے لگیں تو ہم اپنی زمین سے اچک ليے جائیں گے کیا ہم نے ان کو امن و امان والے حرم میں جگہ نہیں دی جہاں ہر قسم کے پھل کھنچے چلے آتے ہیں ، ہماری طرف سے رزق کے طور پر، لیکن ان میں سے اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(28,58,'28:58','اور ہم نے کتنی ہی بستیاں ہلاک کردیں جو اپنے سامانِ معیشت پر نازاں تھیں پس یہ ہیں ان کی بستیاں جو ان کے بعد آباد نہیں ہوئیں مگر بہت کم، اور ہم ہی ان کے وارث ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(28,59,'28:59','اور تیرا رب بستیوں کو ہلاک کرنے والا نہ تھا جب تک ان کی بڑی بستی میں کسی پیغمبر کو نہ بھیج دے جو ان کو ہماری آیتیں پڑھ کر سنائے اور ہم ہرگز بستیوں کو ہلاک کرنے والے نہیں مگر جب کہ وہاں کے لوگ ظالم ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(28,60,'28:60','اور جو چیز بھی تم کو دی گئی ہے تو وہ بس دنیا کی زندگی کا سامان اور اس کی رونق ہے اور جو کچھ اللہ کے پاس ہے وہ بہترہے اور باقی رہنے والا ہے پھر کیا تم سمجھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,61,'28:61','بھلا وہ شخص جس سے ہم نے اچھا وعدہ کیا ہے پھر وہ اس کو پانے والا ہے، کیا وه اس شخص جیسا ہوسکتا ہے جس کو ہم نے صرف دنیوی زندگی کا فائدہ دیا ہے، پھر قیامت کے دن وہ حاضر کيے جانے والوں میں سے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(28,62,'28:62','اور جس دن خدا ان کو پکارے گا پھر کہے گا کہ کہاں ہیں میرے وہ شریک جن کا تم دعوی کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(28,63,'28:63','جن پر بات ثابت ہوچکی ہوگی وہ کہیں گے کہ اے ہمارے رب، یہ لوگ ہیں جنھوں نے ہم کو بہکایا ہم نے ان کو اسی طرح بہکایا جس طرح ہم خود بہکے تھے ہم ان سے برأت کرتے ہیں یہ لوگ ہماری عبادت نہیں کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(28,64,'28:64','اور کہا جائے گا کہ اپنے شریکوں کو بلاؤ تو وہ ان کو پکاریں گے تو وہ ان کو جواب نہ دیں گے اور وہ عذاب کو دیکھیں گے کاش، وہ ہدایت اختیار کرنے والے ہوتے');
INSERT INTO "ur_wahid_uddin" VALUES(28,65,'28:65','اور جس دن خدا ان کو پکارے گا اور فرمائے گا کہ تم نے پیغام پہنچانے والوں کو کیا جواب دیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(28,66,'28:66','پھر اس دن ان کی تمام باتیں گم ہوجائیں گی، تو وہ آپس میں بھی نہ پوچھ سکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(28,67,'28:67','البتہ جس نے توبہ کی اور ایمان لایا اور نیک عمل کیا تو امید ہے کہ وہ فلاح پانے والوں میں سے ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(28,68,'28:68','اور تیرا رب پیدا کرتا ہے جو چاہے اور وہ پسند کرتا ہے جس کو چاہے ان کے ہاتھ میں نہیں ہے پسند کرنا اللہ پاک اور برتر ہے ا س سے جس کو وہ شریک ٹھہراتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,69,'28:69','اور تیرا رب جانتا ہے جو کچھ ان کے سینے چھپاتے ہیں اور جو کچھ وہ ظاہر کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,70,'28:70','اور وہی اللہ ہے، اس کے سوا کوئی معبود نہیں اسی کے ليے حمد ہے دنیا میں اور آخرت میں اور اسی کے ليے فیصلہ ہے اور اسی کی طرف تم لوٹائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(28,71,'28:71','کہو کہ بتاؤ، اگر اللہ قیامت کے دن تک تم پر ہمیشہ کے ليے رات کردے تو اللہ کے سوا کون معبود ہے جو تمھارے ليے روشنی لے آئے تو کیا تم لوگ سنتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,72,'28:72','کہو کہ بتاؤ اگر اللہ قیامت تک تم پر ہمیشہ کے ليے دن کردے تو اللہ کے سوا کون معبود ہے جو تمھارے ليے رات کو لے آئے جس میں تم سکون حاصل کرتے ہو کیا تم لوگ دیکھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,73,'28:73','اور اس نے اپنی رحمت سے تمھارے ليے رات اور دن کو بنایا تاکہ تم اس میں سکون حاصل کرو اور تاکہ تم اس کا فضل تلاش کرو اور تاکہ تم شکر کرو');
INSERT INTO "ur_wahid_uddin" VALUES(28,74,'28:74','اور جس دن اللہ ان کو پکارے گا پھر کہے گاکہ کہاں ہیں میرے شریک جن کا تم گمان رکھتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(28,75,'28:75','اور ہم ہر امت میں سے ایک گواہ نکال کر لائیں گے پھر لوگوں سے کہیں گے کہ اپنی دلیل لاؤ، تب وہ جان لیں گے کہ حق اللہ کی طرف ہے اور وہ باتیں ان سے گم ہوجائیں گی جو وہ گھڑتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(28,76,'28:76','قارون، موسٰی کی قوم میں سے تھا پھر وہ ان کے خلاف سرکش ہوگیا اور ہم نے اس کو اتنے خزانے دئے تھے کہ ان کی کنجیاں اٹھانے سے کئی طاقت ور مرد تھک جاتے تھے جب اس کی قوم نے اس سے کہا کہ اتراؤ مت، اللہ اترانے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(28,77,'28:77','اور جو کچھ اللہ نے تم کو دیا ہے اس میں آخرت کے طالب بنو اور دنیا میں سے اپنے حصے کو نہ بھولو اور لوگوں کے ساتھ بھلائی کرو جس طرح اللہ نے تمھارے ساتھ بھلائی کی ہے اور زمین میں فساد کے طالب نہ بنو، اللہ فساد کرنے والوں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(28,78,'28:78','اس نے کہا، یہ مال مجھ کو ایک علم کی بنا پر ملا ہے جو میرے پاس ہے کیا اس نے یہ نہیں جانا کہ اللہ اس سے پہلے کتنی جماعتوں کو ہلاک کرچکا ہے جو اس سے زیادہ قوت اور جمعیت رکھتی تھیں اور مجرموں سے ان کے گناہ پوچھے نہیں جاتے');
INSERT INTO "ur_wahid_uddin" VALUES(28,79,'28:79','پس وہ اپنی قوم کے سامنے اپنی پوری آرائش کے ساتھ نکلا جو لوگ حیاتِ دنیا کے طالب تھے انھوں نے کہا، کاش ہم کو بھی وہی ملتا جو قارون کو دیا گیا ہے، بے شک وہ بڑی قسمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(28,80,'28:80','اور جن لوگوں کو علم ملا تھا انھوں نے کہا، تمھارا برا ہو اللہ کا ثواب بہتر ہے اس شخص کے ليے جو ایمان لائے اور نیک عمل کرے اور یہ انھیں کو ملتا ہے جو صبر کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(28,81,'28:81','پھر ہم نے اس کو اور اس کے گھر کو زمین میں دھنسا دیا پھر اس کے ليے کوئی جماعت نہ اٹھی جو اللہ کے مقابلہ میں اس کی مدد کرتی اور نہ وہ خود ہی اپنے کو بچا سکا');
INSERT INTO "ur_wahid_uddin" VALUES(28,82,'28:82','اور جو لوگ کل اس کے جیسا ہونے کی تمنا کر رہے تھے وہ کہنے لگے کہ افسوس، بے شک اللہ اپنے بندوں میں سے جس کے ليے چاہتا ہے رزق کشادہ کردیتا ہے اور جس کے ليے چاہتا ہے تنگ کردیتا ہے اگر اللہ نے ہم پر احسان نہ کیا ہوتا تو ہم کو بھی زمین میں دھنسا دیتا افسوس، بے شک انکار کرنے والے فلاح نہیں پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(28,83,'28:83','یہ آخرت کا گھر ہم ان لوگوں کو دیں گے جو زمین میں نہ بڑا بننا چاہتے ہیں اور نہ فساد کرنا اور آخری انجام ڈرنے والوں کے ليے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(28,84,'28:84','جو شخص نیکی لے کر آئے گا، اس کے ليے اس سے بہتر ہے اور جو شخص برائی لے کر آئے گا تو جو لوگ برائی کرتے ہیں ان کو وہی ملے گا جو انھوں نے کیا');
INSERT INTO "ur_wahid_uddin" VALUES(28,85,'28:85','بے شک جس نے تم پر قرآن کو فرض کیا ہے وہ تم کو ایک اچھے انجام تک پہنچا کر رہے گا کہو کہ میرا رب خوب جانتا ہے کہ کون ہدایت لے کر آیا ہے اور کون کھلی ہوئی گمراہی میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(28,86,'28:86','اور تم کو یہ امید نہ تھی کہ تم پر کتاب اتاری جائے گی، مگر تمھارے رب کی مہربانی سے پس تم منکروں کے مدد گار نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(28,87,'28:87','اور وہ تم کو اللہ کی آیتوں سے روک نہ دیں جب کہ وہ تمھاری طرف اتاری جاچکی ہیں اور تم اپنے رب کی طرف بلاؤ اور مشرکوں میں سے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(28,88,'28:88','اور اللہ کے ساتھ کسی دوسرے معبود کو نہ پکارو اس کے سوا کوئی معبود نہیں ہر چیز ہلاک ہونے والی ہے سوا اس کی ذات کے فیصلہ اسی کے ليے ہے اور تم لوگ اسی کی طرف لوٹائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(29,1,'29:1','الم');
INSERT INTO "ur_wahid_uddin" VALUES(29,2,'29:2','کیا لوگ یہ سمجھتے ہیں کہ وہ محض یہ کہنے پر چھوڑ دئے جائیں گے کہ ہم ایمان لائے اور ان کو جانچا نہ جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(29,3,'29:3','اور ہم نے ان لوگوں کو جانچا ہے جو ان سے پہلے تھے، پس اللہ ان لوگوں کو جان کر رہے گا جو سچے ہیں اور وہ جھوٹوں کو بھی ضرور معلوم کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(29,4,'29:4','کیا جو لوگ برائیاں کر رہے ہیں وہ سمجھتے ہے کہ وہ ہم سے بچ جائیں گے بہت برا فیصلہ ہے جو وہ کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,5,'29:5','جو شخص اللہ سے ملنے کی امید رکھتا ہے تو اللہ کا وعدہ ضرور آنے والا ہے اور وہ سننے والا ہے، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,6,'29:6','اور جو شخص محنت کرے تو وہ اپنے ہی ليے محنت کرتا ہے بے شک اللہ جہان والوں سے بے نیاز ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,7,'29:7','اور جو لوگ ایمان لائے اور انھوں نے نیک کام کیا تو ہم ان کی برائیاں ان سے دور کردیں گے اور ان کو ان کے عمل کا بہترین بدلہ دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(29,8,'29:8','اور ہم نے انسان کو تاکید کی کہ وہ اپنے ماں باپ کے ساتھ نیک سلوک کرے اور اگر وہ تجھ پر دباؤ ڈالیں کہ تو ایسی چیز کو میرا شریک ٹھہرائے جس کا تجھ کو کوئی علم نہیں تو ان کی اطاعت نہ کر تم سب کو میرے پاس لوٹ کر آنا ہے، پھر میں تم کو بتادوں گا جو کچھ تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(29,9,'29:9','اور جو لوگ ایمان لائے اور انھوں نے نیک کام کیا تو ہم ان کو نیک بندوں میں داخل کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(29,10,'29:10','اور لوگوں میں کوئی ایسا ہے جو کہتا ہے کہ ہم اللہ پر ایمان لائے پھر جب اللہ کی راہ میں اس کو ستایا جاتا ہے تو وہ لوگوں کے ستانے کو اللہ کے عذاب کی طرح سمجھ لیتاہے اور اگر تمھارے رب کی طرف سے کوئی مدد آجائے تو وہ کہیں گے کہ ہم تو تمھارے ساتھ تھے کیا اللہ اس سے اچھی طرح باخبر نہیں جو لوگوں کے دلوں میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,11,'29:11','اور اللہ ضرو رمعلوم کرے گا ان لوگوں کو جو ایمان لائے اور وہ ضرور معلوم کرے گامنافقین کو');
INSERT INTO "ur_wahid_uddin" VALUES(29,12,'29:12','اور منکر لوگ ایمان والوں سے کہتے ہیں کہ تم ہمارے راستہ پر چلو اور ہم تمھارے گناہوں کو اٹھالیں گے اور وہ ان کے گناہوں میں سے کچھ بھی اٹھانے والے نہیں ہیں بے شک وہ جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,13,'29:13','اور وہ اپنے بوجھ اٹھائیں گے، اور اپنے بوجھ کے ساتھ کچھ اوربوجھ بھی اور یہ لوگ جو جھوٹی باتیں بناتے ہیں قیامت کے دن اس کی بابت ان سے پوچھ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(29,14,'29:14','اور ہم نے نوح کو اس کی قوم کی طرف بھیجا تو وہ ان کے اندر پچاس سال کم ایک ہزار سال رہا پھر ان کو طوفان نے پکڑ لیا اور وہ ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(29,15,'29:15','پھر ہم نے نوح کو اور کشتی والوں کو بچالیا اور ہم نے اس واقعہ کو دنیا والوں کے ليے ایک نشانی بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(29,16,'29:16','اور ابراہیم کو جب کہ اس نے اپنی قوم سے کہا کہ اللہ کی عبادت کرو اور اس سے ڈرو یہ تمھارے ليے بہتر ہے اگر تم جانو');
INSERT INTO "ur_wahid_uddin" VALUES(29,17,'29:17','تم لوگ اللہ کو چھوڑ کر محض بتوں کو پوجتے ہو اور تم جھوٹی باتیں گھڑتے ہو اللہ کے سوا تم جن کی عبادت کرتے ہو وہ تم کو رزق دینے کا اختیار نہیں رکھتے پس تم اللہ کے پاس رزق تلاش کرو اور اس کی عبادت کرو اور اس کا شکر ادا کرو اسی کی طرف تم لوٹائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(29,18,'29:18','اور اگر تم جھٹلاؤ گے تو تم سے پہلے بہت سی قومیں جھٹلاچکی ہیں اور رسول پر صاف صاف پہنچا دینے کے سوا کوئی ذمہ داری نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,19,'29:19','کیا لوگوں نے نہیں دیکھا کہ اللہ کس طرح خلق کو شروع کرتا ہے، پھر وہ اس کو دہرائے گا بے شک یہ اللہ پر آسان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,20,'29:20','کہو کہ زمین میں چلو پھرو،پھر دیکھو کہ اللہ نے کس طرح خلق کو شروع کیا، پھر وہ اس کو دوبارہ اٹھائے گا بے شک اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,21,'29:21','وہ جس کو چاہے گا عذاب دے گا اور جس پر چاہے گا رحم کرے گا اور اسی کی طرف تم لوٹائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(29,22,'29:22','اور تم نہ زمین میں عاجز کرنے والے ہو اور نہ آسمان میں ، اور تمھارے ليے اللہ کے سوا نہ کوئی کارساز ہے اورنہ کوئی مدد گار');
INSERT INTO "ur_wahid_uddin" VALUES(29,23,'29:23','اور جن لوگوں نے اللہ کی آیتوں کا اور اس سے ملنے کا انکار کیا تو وہی میری رحمت سے محروم ہوئے اور ان کے ليے درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,24,'29:24','پھر اس کی قوم کا جواب اس کے سواکچھ نہ تھا کہ انھوں نے کہا کہ اس کو قتل کر دویا اس کو جلا دو، تو اللہ نے اس کو آگ سے بچالیا بے شک اس کے اندر نشانیاں ہیں ان لوگوں کے ليے جو ایمان لائے');
INSERT INTO "ur_wahid_uddin" VALUES(29,25,'29:25','اور اس نے کہا کہ تم نے اللہ کے سواجو بت بنائے ہیں ، بس وہ تمھارے باہمی دنیا کے تعلقات کی وجہ سے ہے، پھر قیامت کے دن تم میں سے ہر ایک دوسرے کا انکار کرے گا اور ایک دوسرے پر لعنت کرے گا اور آگ تمھارا ٹھکانا ہوگی اور کوئی تمھارا مدد گار نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(29,26,'29:26','پھر لوط نے اس کو مانا اور کہا کہ میں اپنے رب کی طرف ہجرت کرتا ہوں بے شک وہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,27,'29:27','اور ہم نے عطا کيے اس کو اسحاق اور یعقوب اور اس کی نسل میں نبوت اور کتاب رکھ دی اور ہم نے دنیا میں اس کو اجر عطا کیا اور آخرت میں یقیناً وہ صالحین میں سے ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(29,28,'29:28','اور لوط کو جب کہ اس نے اپنی قوم سے کہا کہ تم ایسی بے حیائی کا کام کرتے ہو کہ تم سے پہلے دنیا والوں میں سے کسی نے نہیں کیا');
INSERT INTO "ur_wahid_uddin" VALUES(29,29,'29:29','کیا تم مردوں کے پاس جاتے ہو اور راسته روكتے ہو اور اپنی مجلس میں برا کام کرتے ہو پس اس کی قوم کا جواب اس کے سوا کچھ نہ تھا کہ اس نے کہا کہ اگر تم سچے ہو تو ہمارے اوپر اللہ کا عذاب لاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(29,30,'29:30','لوط نے کہا کہ اے میرے رب، مفسد لوگوں کے مقابلہ میں میری مدد فرما');
INSERT INTO "ur_wahid_uddin" VALUES(29,31,'29:31','اور جب ہمارے بھیجے ہوئے ابراہیم کے پاس بشارت لے کر پہنچے، انھوں نے کہا کہ ہم اس بستی کے لوگوں کو ہلاک کرنے والے ہیں بے شک اس کے لوگ سخت ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,32,'29:32','ابراہیم نے کہا کہ اس میں تو لوط بھی ہے انھوں نے کہا کہ ہم خوب جانتے ہیں کہ وہاں کون ہے ہم اس کو اور اس کے گھر والوں کو بچالیں گے مگر اس کی بیوی کہ وہ پیچھے رہ جانے والوں میں سے ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(29,33,'29:33','پھر جب ہمارے بھیجے ہوئے لوط کے پاس آئے تو وہ ان سے پریشان ہوا اور دل تنگ ہوا اور انھوں نے کہا کہ تم نہ ڈرو اور نہ غم کرو ہم تم کو اور تمھارے گھر والوں کو بچالیں گے، مگر تمھاری بیوی کہ وہ پیچھے رہ جانے والوں میں سے ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(29,34,'29:34','ہم اس بستی کے باشندوں پر ایک آسمانی عذاب ان کی بدکاریوں کی سزا میں نازل کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,35,'29:35','اور ہم نے اس بستی کے کچھ نشان رہنے دیئے ہیں ان لوگوں کی عبرت کے ليے جو عقل رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,36,'29:36','اور مدین کی طرف ان کے بھائی شعیب کو اس نے کہا کہ اے میری قوم، اللہ کی عبادت کرو اور آخرت کے دن کی امید رکھو اور زمین میں فساد پھیلانے والے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(29,37,'29:37','تو انھوں نے اس کو جھٹلادیا پس زلزلہ نے ان کو آ پکڑا پھر وہ اپنے گھرو ں میں اوندھے پڑے رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(29,38,'29:38','اور عاد اور ثمود کو، اور تم پر حال کھل چکا ہے ان کے گھروں سے اور ان کے اعمال کو شیطان نے ان کے ليے خوش نما بنا دیا پھر ان کو راستے سے روک دیا اور وہ ہوشیار لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(29,39,'29:39','اور قارون کو اور فرعون کو اور ہامان کو اور موسیٰ ان کے پاس کھلی نشانیاں لے کر آیا تو انھوں نے زمین میں گھمنڈ کیا اور وہ ہم سے بھاگ جانے والے نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(29,40,'29:40','پس ہم نے ہر ایک کو اس کے گناہ میں پکڑا پھر ان میں سے بعض پر ہم نے پتھراؤ کرنے والی ہوا بھیجی اور ان میں سے بعض کو کڑک نے آپکڑا اور ان میں سے بعض کو ہم نے زمین میں دھنسادیا اور ان میں سے بعض کو ہم نے غرق کردیا اور اللہ ان پر ظلم کرنے والا نہ تھا مگر وہ خود اپنی جانوں پر ظلم کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(29,41,'29:41','جن لوگوں نے اللہ کے سوا دوسرے کار ساز بنائے ہیں ان کی مثال مکڑی کی سی ہے اس نے ایک گھر بنایا اور بے شک تمام گھروں سے زیادہ کمزور گھر مکڑی کا گھر ہے کاش کہ لوگ جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(29,42,'29:42','بے شک اللہ جانتا ہے ان چیزوں کو جن کو وہ اس کے سوا پکارتے ہیں اور وہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,43,'29:43','اور یہ مثالیں ہیں جن کو ہم لوگوں کے ليے بیان کرتے رہے ہیں اور ان کو وہی لوگ سمجھتے ہیں جو علم والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,44,'29:44','اللہ نے آسمانوں اور زمین کو برحق پیدا کیا ہے بے شک اس میں نشانی ہے ایمان والوں کے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(29,45,'29:45','تم اس کتاب کو پڑھو جو تم پر وحی کی گئی ہے اور نماز قائم کرو بے شک نماز بے حیائی سے اور برے کاموں سے روکتی ہے اور اللہ کی یاد بہت بڑی چیز ہے اور اللہ جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(29,46,'29:46','اور تم اہل کتاب سے بحث نہ کرو مگر اس طریقہ پر جو بہتر ہے، مگر جو ان میں بے انصاف ہیں اور کہو کہ ہم ایمان لائے اس چیز پر جو ہماری طرف بھیجی گئی ہے اور اس پر جو تمھاری طرف بھیجی گئی ہے ہمارا معبود اور تمھارا معبود ایک ہے اور ہم اسی کی فرماں برداری کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,47,'29:47','اور اسی طرح ہم نے تمھارے اوپر کتاب اتاری تو جن لوگوں کو ہم نے کتاب دی ہے وہ اس پر ایمان لاتے ہیں اور ان لوگوں میں سے بھی بعض ایمان لاتے ہیں اور ہماری آیتوں کا انکار صرف منکرہی کرتے ہيں');
INSERT INTO "ur_wahid_uddin" VALUES(29,48,'29:48','اور تم اس سے پہلے کوئی کتاب نہیں پڑھتے تھے اورنہ اس کو اپنے ہاتھ سے لکھتے تھے ایسی حالت میں باطل پرست لوگ شبہ میں پڑتے');
INSERT INTO "ur_wahid_uddin" VALUES(29,49,'29:49','بلکہ یہ کھلی ہوئی آیتیں ہیں ان لوگوں کے سینوں میں جن کو علم عطا ہوا ہے اور ہماری آیتوں کا انکار نہیں کرتے مگر وہ جو ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,50,'29:50','اور وہ کہتے ہیں کہ اس پر اس کے رب کی طرف سے نشانیاں کیوں نہیں اتاری گئیں کہو کہ نشانیاں تو اللہ کے پاس ہیں اور میں صرف کھلا ہوا ڈرانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(29,51,'29:51','کیا ان کے ليے یہ کافی نہیں ہے کہ ہم نے تم پر کتاب اتاری جو ان کو پڑھ کر سنائی جاتی ہےبے شک اس میں رحمت اور یاد دہانی ہے ان لوگوں کے ليے جو ایمان لائے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,52,'29:52','کہو کہ اللہ میرے اور تمھارے درمیان گواہی کے ليے کافی ہے وہ جانتا ہے جو کچھ آسمانوں اور زمین میں ہے اور جو لوگ باطل پر ایمان لائے اور جنھوں نے اللہ کا انکار کیا، وہی خسارے میں رہنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,53,'29:53','اور یہ لوگ تم سے عذاب جلد مانگ رہے ہیں اور اگر ایک وقت مقرر نہ ہوتا تو ان پر عذاب آجاتا اور یقیناً وہ ان پر اچانک آئے گا اور ان کو خبر بھی نہ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(29,54,'29:54','وہ تم سے عذاب جلد مانگ رہےهيں اور جہنم منکروں کو گھیرے ہوئے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,55,'29:55','جس دن عذاب ان کو اوپر سے ڈھانک لے گا اور پاؤں کے نیچے سے بھی، اور کہے گا کہ چکھو اس کو جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(29,56,'29:56','اے میرے بندو جو ایمان لائے ہو، بے شک میری زمین وسیع ہے تو تم میری ہی عبادت کرو');
INSERT INTO "ur_wahid_uddin" VALUES(29,57,'29:57','ہر جان کو موت کا مزہ چکھنا ہے پھر تم ہماری طرف لوٹائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(29,58,'29:58','اور جو لوگ ایمان لائے اور انھوں نے نیک عمل کيے ان کو ہم جنت کے بالاخانوں میں جگہ دیں گے جن کے نیچے نہریں جاری ہوں گی وہ ان میں ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(29,59,'29:59','کیا ہی اچھا اجر ہے عمل کرنے والوں کا جنھوں نے صبر کیا اور جو اپنے رب پر بھروسہ رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,60,'29:60','اور کتنے جانور ہیں جو اپنا رزق اٹھائے نہیں پھرتے اللہ ان کو رزق دیتا ہے اور تم کو بھی اور وہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,61,'29:61','اور اگر تم ان سے پوچھو کہ کس نے پیدا کیا آسمانوں اور زمین کو، اور مسخر کیا سورج کو اور چاند کو، تو وہ ضرور کہیں گے کہ اللہ نے پھر وہ کہاں سے پھیر دئے جاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,62,'29:62','اللہ ہی اپنے بندوں میں سے جس کا چاہتا ہے رزق کشادہ کرتا ہے اور جس کا چاہتا ہے تنگ کردیتا ہے بے شک اللہ ہر چیز کا جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(29,63,'29:63','اور اگر تم ان سے پوچھو کہ کس نے آسمان سے پانی اتارا، پھر اس سے زمین کو زندہ کیا اس کے مرجانے کے بعد، تو ضرور وہ کہیں گے کہ اللہ نے کہو کہ ساری تعریف اللہ کے ليے ہے بلکہ ان میں سے اکثر لوگ نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(29,64,'29:64','اور یہ دنیا کی زندگی کچھ نہیں ہے مگر ایک کھیل اور دل کا بہلاوا اور آخرت کا گھر ہی اصل زندگی کی جگہ ہے، کاش کہ وہ جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(29,65,'29:65','پس جب وہ کشتی میں سوار ہوتے ہیں تو اللہ کو پکارتے ہیں ، اسی کے ليے دین کو خالص کرتے ہوئے، پھر جب وہ ان کو نجات دے کر خشکی کی طرف لے جاتا ہے تو وہ فوراً شرک کرنے لگتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,66,'29:66','تاکہ ہم نے جو نعمت ان کو دی ہے اس کی ناشکری کریں اور چند دن فائدہ اٹھائیں پس وہ عنقریب جان لیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(29,67,'29:67','کیا وہ دیکھتے نہیں کہ ہم نے ایک پر امن حرم بنایا اور ان کے گرد و پیش كے لوگ اچک ليے جاتے ہیں تو کیا وہ باطل کو مانتے ہیں اور اللہ کی نعمت کی ناشکری کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(29,68,'29:68','اور اس شخص سے بڑا ظالم کون ہوگا جو اللہ پر جھوٹ باندھے یا حق کو جھٹلائے جب کہ وہ اس کے پاس آچکا کیا منکروں کا ٹھکانا جہنم میں نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(29,69,'29:69','اور جو لوگ ہماری خاطر مشقت اٹھائیں گے، ان کو ہم اپنے راستے دکھائیں گے اور یقیناً اللہ نیکی کرنے والوں کے ساتھ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(30,1,'30:1','الم');
INSERT INTO "ur_wahid_uddin" VALUES(30,2,'30:2','رومی پاس کے علاقہ میں مغلوب ہوگئے');
INSERT INTO "ur_wahid_uddin" VALUES(30,3,'30:3','اور وہ اپنی مغلوبیت کے بعد عنقریب غالب ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,4,'30:4','چند برسوں میں اللہ ہی کے ہاتھ میں سب کام ہے، پہلے بھی اور پیچھے بھی اور اس دن ایمان والے خوش ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,5,'30:5','اللہ کی مدد سے وہ جس کی چاہتا ہے مدد کرتا ہے اور وہ زبردست ہے، رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(30,6,'30:6','اللہ کا وعدہ ہے اللہ اپنے وعدہ کے خلاف نہیں کرتا لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(30,7,'30:7','وہ دنیا کی زندگی کے صرف ظاہر کو جانتے ہیں ، اور وہ آخرت سے بے خبر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,8,'30:8','کیا انھوں نے اپنے جی میں غور نہیں کیا، اللہ نے آسمانوں اور زمین کو اور جو کچھ ان کے درمیان ہے برحق پیدا کیا ہے اور صرف ایک مقرر مدت کے ليے اور لوگوں میں بہت سے ہیں جو اپنے رب سے ملاقات کے منکر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,9,'30:9','کیا وہ زمین میں چلے پھرے نہیں کہ وہ دیکھتے کہ کیسا انجام ہوا ان لوگوں کا جو ان سے پہلے تھے وہ ان سے زیادہ طاقت رکھتے تھے اور انھوں نے زمین کو جوتا اور اس کو اس سے زیادہ آباد کیا جتنا انھوں نے آباد کیاہے اور ان کے پاس ان کے رسول واضح نشانیاں لے کر آئے پس اللہ ان پر ظلم کرنے والا نہ تھا مگر وہ خود ہی اپنی جانوں پر ظلم کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(30,10,'30:10','پھر جن لوگوں نے برا کام کیا تھا ان کا انجام برا ہوا، اس وجہ سے کہ انھوں نے اللہ کی آیتوں کو جھٹلایا اور وہ ان کی ہنسی اڑاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(30,11,'30:11','اللہ خلق کو پہلی بار پیدا کرتا ہے، پھر وہی دوبارہ اس کو پیدا کرے گا پھر تم اسی کی طرف لوٹائے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,12,'30:12','اور جس دن قیامت برپا ہوگی اس دن مجرم لوگ حیرت زدہ رہ جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,13,'30:13','اور ان کے شریکوں میں سے ان کا کوئی سفارشی نہ ہوگا اور وہ اپنے شریکوں کے منکر ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,14,'30:14','اور جس دن قیامت برپا ہوگی اس دن سب لوگ جدا جدا ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,15,'30:15','پس جو ایمان لائے اور جنھوں نے نیک عمل کیا وہ ایک باغ میں مسرور ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,16,'30:16','اور جن لوگوں نے انکار کیا اور ہماری آیتوں کو اور آخرت کے پیش آنے کو جھٹلایا تو وہ عذاب میں پڑے ہوئے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,17,'30:17','پس تم پاک اللہ کی یاد کرو جب تم شام کرتے ہو اور جب تم صبح کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(30,18,'30:18','اور آسمانوں اور زمین میں اسی کے ليے حمد ہے اور تیسرے پہر اور جب تم ظہر کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(30,19,'30:19','وہ زندہ کو مردہ سے نکالتا ہے اور مردہ کو زندہ سے نکالتا ہے اور وہ زمین کو اس کے مردہ ہوجانے کے بعد زندہ کرتاہے اور اسی طرح تم لوگ نکالے جاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,20,'30:20','اور اس کی نشانیوں میں سے یہ ہے کہ اس نے تم کومٹی سے پیدا کیا ہے، پھر یکایک تم بشر بن کر پھیل جاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(30,21,'30:21','اور اس کی نشانیوں میں سے یہ ہے کہ اس نے تمھاری جنس سے تمھارے ليے جوڑے پیدا کيے، تاکہ تم ان سے سکون حاصل کرو اور اس نے تمھارے درمیان محبت اور رحمت رکھ دی بے شک اس میں بہت سی نشانیاں ہیں ان لوگوں کے ليے جو غور کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,22,'30:22','اور اس کی نشانیوں میں سے آسمانوں اورزمین کی پیدائش اور تمھاری بولیوں اور تمھارے رنگوں کا فرق ہے بے شک اس میں بہت سی نشانیاں ہیں علم والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(30,23,'30:23','اور اس کی نشانیوں میں سے تمھارا رات اور دن میں سونا اور تمھارا اس کے فضل کو تلاش کرنا ہے بے شک اس میں بہت سی نشانیاں ہیں ان لوگوں کے لیے جو سنتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,24,'30:24','اور اس کی نشانیوں میں سے یہ ہے کہ وہ تم کو بجلی دکھاتا ہے، خوف کے ساتھ اور امید کے ساتھاور وہ آسمان سے پانی اُتارتا ہے پھر اس سے زمین کو زندہ کرتا ہے اس کے مُردہ ہوجانے کے بعد بے شک اس میں نشانیاں ہیں ان لوگوں کے لیے جوعقل سے کام لیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,25,'30:25','اور اس کی نشانیوں میں سے یہ ہے کہ آسمان اور زمین اس کے حکم سے قائم ہيں پھر جب وہ تم کو ایک بار پکارے گا تو تم اسی وقت زمین سے نکل پڑوگے');
INSERT INTO "ur_wahid_uddin" VALUES(30,26,'30:26','اور آسمانوں اور زمین میں جو بھی ہے اسی کا ہے سب اسی کے تابع ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,27,'30:27','اور وہی ہے جو اول بار پیدا کرتا ہے پھر وہی دوبارہ پیدا کرے گااور یہ اس کے لیے زیادہ آسان ہے اور آسمانوں اور زمین میں اسی کے لیے سب سے برتر صفت ہےاور وہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(30,28,'30:28','وہ تمھارے لیے خود تمھاری ذات سے ایک مثال بیان کرتا ہےکیا تمھارے غلاموں میں کوئی تمھارے اس مال میں شریک ہے جو ہم نے تم کو دیا ہے کہ تم اور وہ اس میں برابر ہوں اور جس طرح تم اپنوں کا لحاظ کرتے ہو، اسی طرح ان کا بھی لحاظ کرتے ہو اس طرح ہم آیتیں کھول کر بیان کرتے ہیں ان لوگوں کے لیے جو عقل سے کام لیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,29,'30:29','بلکہ اپنی جانوں پر ظلم کرنے والوں نے بلادلیل اپنے خیالات کی پیروی کر رکھی ہے تو اس کوکون ہدایت دے سکتا ہے جس کو اللہ نے بھٹکا دیا ہواور کوئی ان کامددگار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,30,'30:30','پس تم یکسو ہوکر اپنا رخ اس دین کی طرف رکھو، اللہ کی فطرت جس پر اس نے لوگوں کو بنایا ہےاس کے بنائے ہوئے کو بدلنا نہیں یہی سیدھا دین ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(30,31,'30:31','اسی کی طرف متوجہ ہوکر اور اسی سے ڈرو اور نماز قائم کرو اور مشرکین میں سے نہ بنو');
INSERT INTO "ur_wahid_uddin" VALUES(30,32,'30:32','جنھوں نے اپنے دین کو ٹکڑے ٹکڑے کرلیااور بہت سے گروہ ہوگئے ہر گروہ اپنے طریقے پر نازاں ہے جو اس کے پاس ہے');
INSERT INTO "ur_wahid_uddin" VALUES(30,33,'30:33','اور جب لوگوں کو کوئی تکلیف پہنچتی ہے تووہ اپنے رب کو پکارتے ہیں اسی کی طرف متوجہ ہوکرپھر جب وہ اپنی طرف سے ان کو مہربانی چکھاتا ہے تو ان میں سے ایک گروہ اپنے رب کا شریک ٹھہرانے لگتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(30,34,'30:34','کہ جو کچھ ہم نے ان کو دیا ہے اس کے منکر ہوجائیں تو چند دن فائدہ اٹھالو، عنقریب تم کو معلوم ہوجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(30,35,'30:35','کیا ہم نے ان پرکوئی سند اتاری ہے کہ وہ ان کو خدا کے ساتھ شرک کرنے کو کہہ رہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(30,36,'30:36','اور جب ہم لوگوں کو مہربانی چکھاتے ہیں تو وہ اس سے خوش ہوجاتے ہیں اور اگر ان کے اعمال کے سبب سے ان کو کوئی تکلیف پہنچتی ہے تو یکایک وہ مایوس ہوجاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,37,'30:37','کیا وہ دیکھتے نہیں کہ اللہ جس کو چاہے زیادہ روزی دیتا ہے اور جس کو چاہے کمبے شک اس میں نشانیاں ہیں ان لوگوں کے لیے جو ایمان رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,38,'30:38','پس رشتہ دار کو اس کاحق دو اور مسکین کو اور مسافرکویہ بہتر ہے ان لوگوں کے لیے جواللہ کی رضا چاہتے ہیں اوروہی لوگ فلاح پانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,39,'30:39','اور جو سود تم دیتے ہو تاکہ لوگوں کے مال میں شامل ہوکر وہ بڑھ جائے ، تو اللہ کے نزدیک وہ نہیں بڑھتا اور جوزکوٰۃ تم دوگے، اللہ کی رضا حاصل کرنے کے لیے تو یہی لوگ ہیں جو اللہ کے یہاں اپنے مال کو بڑھانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,40,'30:40','اللہ ہی ہے جس نے تم کو پیدا کیاپھر اس نے تم کو روزی دی،پھر وہ تم کو موت دیتا ہے پھر وہ تم کو زندہ کرے گاکیا تمھارے شریکوں میں سے کوئی ایسا ہے جو ان میں سے کوئی کام کرتا ہووہ پاک ہے اور برتر ہے اس شرک سے جو یہ لوگ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,41,'30:41','خشکی اور تری میں فساد پھیل گیا لوگوں کے اپنے ہاتھوں کی کمائی سے، تاکہ اللہ مزه چکھا ئے ان کو ان کے بعض اعمال کا، شاید کہ وہ باز آئیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,42,'30:42','کہو کہ زمین میں چلو پھرو، پھردیکھو کہ ان لوگوں کا انجام کیا ہوا جو اس سے پہلے گزرے ہیں ان میں سے اکثر مشرک تھے');
INSERT INTO "ur_wahid_uddin" VALUES(30,43,'30:43','پس اپنا رُخ دین ِقیّم کی طرف سیدھا رکھو،قبل اس کے کہ اللہ کی طرف سے ایسا دن آجائے جس کے لیے واپسی نہیں ہےاس دن لوگ جدا جدا ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,44,'30:44','جس نے انکار کیاتو اس کا انکار اسی پر پڑے گا اور جس نے نیک عمل کیا تو یہ لوگ اپنے ہی لیے سامان کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,45,'30:45','تاکہ اللہ ایمان لانے والوں کو اور نیک عمل کرنے والوں کو اپنے فضل سے جزا دے بے شک اللہ منکروں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(30,46,'30:46','اور اس کی نشانیوں میں سے یہ ہے کہ وہ ہوائیں بھیجتا ہے خوش خبری دینے کے لیے، اور تاکہ وہ تم کو اپنی رحمت سے نوازے اور تاکہ کشتیاں اس کے حکم سے چلیں ،اور تاکہ تم اس کا فضل تلاش کرو،اور تاکہ تم اس کاشکر ادا کرو');
INSERT INTO "ur_wahid_uddin" VALUES(30,47,'30:47','اور ہم نے تم سے پہلے رسولوں کو بھیجا ان کی قوم کی طرف پس وہ ان کے پاس کھلی ہوئی نشانیاں لے کر آئے تو ہم نے ان لوگوں سے انتقام لیا جنھوں نے جرم کیا تھا اور ہم پر یہ حق تھا کہ ہم مومنوں کی مددکریں');
INSERT INTO "ur_wahid_uddin" VALUES(30,48,'30:48','اللہ ہی ہے جو ہواؤں کو بھیجتا ہے پس وہ بادل کواٹھاتی ہیں پھر اللہ ان کو آسمان میں پھیلا دیتا ہے جس طرح چاہتا ہے اور وہ ان کو تہہ بہ تہہ کرتا ہے پھر تم مینھ کو دیکھتے ہو کہ اس کے اندر سے نکلتا ہےپھر جب وہ اپنے بندوں میں سے جس کو چاہتا ہے اسے پہنچادیتا ہے تو یکایک وہ خوش ہوجاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,49,'30:49','اور وہ اس کے نازل کيے جانے سے قبل، خوشی سے پہلے ناامید تھے');
INSERT INTO "ur_wahid_uddin" VALUES(30,50,'30:50','پس اللہ کی رحمت کے آثار کو دیکھو وہ کس طرح زمین کو زندہ کردیتا ہے اس کے مُردہ ہو جانے کے بعد بے شک وہی مردوں کو زندہ کرنے والا ہےاور وہ ہر چیز پرقادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(30,51,'30:51','اور اگر ہم ایک ہوا بھیج دیں ،پھر وہ کھیتی کو زرد ہوئی دیکھیں تو اس کے بعد وہ انکار کرنے لگیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(30,52,'30:52','تو تم مُردوں کو نہیں سناسکتے اور نہ تم بہروں کو اپنی پکار سناسکتے جب کہ وہ پیٹھ پھیر کر چلے جارہے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(30,53,'30:53','اور نہ تم اندھوں کو ان کی گمراہی سے نکال کر راہ پرلاسکتے ہوتم صرف اس کو سناسکتے ہو جوہماری آیتوں پر ایمان لانے والا ہو پس یہی لوگ اطاعت کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(30,54,'30:54','اللہ ہی ہے جس نے تم کو ناتوانی سے پیدا کیا، پھر ناتوانی کے بعد قوت دی، پھر قوت کے بعد ضعف اور بڑھاپا طاری کردیا وہ جو چاہتا ہے پیدا کرتا ہےاور وہ علم والا، قدرت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(30,55,'30:55','اور جس دن قیامت برپاہوگی،مجرم لوگ قسم کھاکر کہیں گے کہ وہ ایک گھڑی سے زیادہ نہیں رہےا س طرح وہ پھیرے جاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(30,56,'30:56','اور جن لوگوں کو علم اور ایمان عطا ہوا تھا وہ کہیں گے کہ اللہ کی کتاب میں تو تم روزِحشر تک پڑے رہےپس یہ حشر کادن ہے، لیکن تم جانتے نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(30,57,'30:57','پس اس دن ظالموں کو ان کی معذرت کچھ نفع نہ دے گی اور نہ ان سے معافی مانگنے کے لیے کہاجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(30,58,'30:58','اور ہم نے اس قرآن میں لوگوں کے لیے ہرقسم کی مثالیں بیان کی ہیں اور اگر تم ان کے پاس کوئی نشانی لے آؤ تو جن لوگوں نے انکار کیا ہے وہ یہی کہیں گے کہ تم سب باطل پرہو');
INSERT INTO "ur_wahid_uddin" VALUES(30,59,'30:59','اس طرح اللہ مہر کردیتا ہے ان لوگوں کے دلوں پر جو نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(30,60,'30:60','پس تم صبر کرو بے شک اللہ کا وعدہ سچا ہےاور تم کو بے برداشت نہ کردیں وہ لوگ جویقین نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(31,1,'31:1','الم');
INSERT INTO "ur_wahid_uddin" VALUES(31,2,'31:2','یہ پُر حکمت کتاب کی آیتیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(31,3,'31:3','ہدایت اور رحمت نیکی کرنے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(31,4,'31:4','جو کہ نماز قائم کرتے ہیں اورزکوٰۃ اداکرتے ہیں اور وہ آخرت پر یقین رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(31,5,'31:5','یہ لوگ اپنے رب کے سیدھے راستے پر ہیں اور یہی لوگ فلاح پانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(31,6,'31:6','اور لوگوں میں کوئی ایسا ہے جوان باتوں کاخریدار بنتا ہے جوغافل کرنے والی ہیں ، تاکہ اللہ کی راہ سے گمراہ کرے، بغیر کسی علم کے اوروہ اس کی ہنسی اڑائےایسے لوگوں کے لیے ذلیل کرنے والا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,7,'31:7','اور جب ان کو ہماری آیتیں سنائی جاتی ہیں تو وہ تکبر کرتا ہوا منہ موڑلیتا ہے، جیسے اس نے سنا ہی نہیں ،جیسے اس کے کانوں میں بہراپن ہےتو اس کو ایک دردناک عذاب کی خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(31,8,'31:8','بے شک جو لوگ ایمان لائے اور انھوں نے نیک کام کیا، ان کے لیے نعمت کے باغ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(31,9,'31:9','ان میں وہ ہمیشہ رہیں گے یہ اللہ کا پختہ وعدہ ہے اور وہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,10,'31:10','اللہ نے آسمانوں کو پیدا کیا، ایسے ستونوں کے بغیر جو تم کو نظر آئیں اور اس نے زمین میں پہاڑ رکھ دئے کہ وہ تم کو لے کر جھک نہ جائےاور اس میں ہر قسم کے جاندار پھیلادئےاور ہم نے آسمان سے پانی اتار ا، پھر زمین میں ہر قسم کی عمدہ چیزیں اگائیں');
INSERT INTO "ur_wahid_uddin" VALUES(31,11,'31:11','یہ ہے اللہ کی تخلیق، تو تم مجھ کودکھاؤ کہ اس کے سوا جو ہیں انھوں نے کیا پیداکیا ہے بلکہ ظالم لوگ کھلی گمراہی میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(31,12,'31:12','اور ہم نے لقمان کو حکمت عطافرمائی کہ اللہ کا شکر کرواور جو شخص شکر کرے گا تو وہ اپنے ہی لیے شکر کرے گا اور جو ناشکری کرے گا تو اللہ بے نیاز ہے، خوبیوں والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,13,'31:13','اور جب لقمان نے اپنے بیٹے کو نصیحت کرتے ہوئے کہا کہ اے میرے بیٹے، اللہ کے ساتھ شریک نہ ٹھہرانا،بے شک شرک بہت بڑا ظلم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,14,'31:14','اور ہم نے انسان کو اس کے ماں باپ کے معاملہ میں تاکید کیاس کی ماں نے دکھ پر دکھ اٹھاکر اس کو پیٹ میں رکھا اور دو برس میں اس کادودھ چھڑانا ہوا کہ تو میرا شکر کر اور اپنے والدین کا میری ہی طرف لوٹ کر آنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,15,'31:15','اور اگر وہ دونوں تجھ پر زور ڈالیں کہ تو میرے ساتھ ایسی چیز کو شریک ٹھہرائے جو تم کومعلوم نہیں تو ان کی بات نہ ماننااور دنیامیں ان کے ساتھ نیک برتاؤ کرنااور تم اس شخص کے راستے کی پیروی کرنا جس نے میری طرف رجوع کیا ہے پھر تم سب کو میرے پاس آنا ہے پھر میں تم کو بتادوں گا جو کچھ تم کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,16,'31:16','اے میرے بیٹے ، کوئی عمل اگر رائی کے دانے کے برابر ہو پھر وہ کسی پتھر کے اندر ہویا آسمانوں میں ہو یازمین میں ہو، اللہ اس کو حاضر کردے گابے شک اللہ باریک بیں ہے، باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,17,'31:17','اے میرے بیٹے، نماز قائم کرو،اچھے کام کی نصیحت کرو اور برائی سے روکو اورجو مصیبت تم کو پہنچے اس پر صبر کرو بے شک یہ ہمت کے کاموں میں سے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,18,'31:18','اور لوگوں سے بے رخی نہ کر اور زمین میں اکڑ کر نہ چلبے شک اللہ کسی اکڑنے والے اور فخر کرنے والے کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(31,19,'31:19','اور اپنی چال میں میانہ روی اختیار کر اور اپنی آواز کوپست کر بے شک سب سے بری آواز گدھے کی آواز ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,20,'31:20','کیا تم نہیں دیکھتے کہ اللہ نے تمھارے کام میں لگادیا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہےاوراس نے اپنی کھلی اور چھپی نعمتیں تم پر تمام کردیں اور لوگوں میں ایسے بھی ہیں جو اللہ کے بارے میں جھگڑتے ہیں ، کسی علم اور کسی ہدایت اور کسی روشن کتاب کے بغیر');
INSERT INTO "ur_wahid_uddin" VALUES(31,21,'31:21','اور جب ان سے کہاجاتا ہے کہ تم پیروی کرو اس چیز کی جو اللہ نے اتاری ہے تو وہ کہتے ہیں کہ نہیں ہم اس چیز کی پیروی کریں گے جس پر ہم نے اپنے باپ دادا کو پایا ہےکیا اگر شیطان ان کو آگ کے عذاب کی طرف بلارہا ہو تب بھی');
INSERT INTO "ur_wahid_uddin" VALUES(31,22,'31:22','اور جوشخص اپنا رُخ اللہ کی طرف جھکادے اور وہ نیک عمل بھی ہو تو اس نے مضبوط رسی پکڑلیاور اللہ ہی کی طرف ہے تمام معاملات کا انجامِ کار');
INSERT INTO "ur_wahid_uddin" VALUES(31,23,'31:23','اور جس نے انکار کیا تو اس کاانکار تم کو غمگین نہ کرے ہماری ہی طرف ہے ان کی واپسی تو ہم ان کو بتادیں گے جو کچھ انھوں نے کیا بے شک اللہ دلوں کی بات سے بھی واقف ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,24,'31:24','ان کو ہم تھوڑی مدت تک فائدہ دیں گے پھر ان کو ایک سخت عذاب کی طرف کھینچ لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(31,25,'31:25','اور اگر تم ان سے پوچھو کہ آسمانوں اور زمین کو کس نے پیدا کیا، تو وہ ضرور کہیں گے کہ اللہ نےکہو کہ سب تعریف اللہ کے لیے ہےبلکہ ان میں سے اکثر نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(31,26,'31:26','اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور زمین میں ، بے شک اللہ بے نیاز ہے، خوبیوں والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,27,'31:27','اور اگر زمین میں جودرخت ہیں وہ قلم بن جائیں اور سمندر ،سات مزید سمندروں کے ساتھ، روشنائی بن جائیں ، تب بھی اللہ کی باتیں ختم نہ ہوں بے شک اللہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,28,'31:28','تم سب کا پیداکرنا اور زندہ کرنا بس ایسا ہی ہے جیسا ایک شخص کا بے شک اللہ سننے والا، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,29,'31:29','کیا تم نے نہیں دیکھا کہ اللہ رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہےاور اس نے سورج اور چاند کو کام میں لگادیا ہےہر ایک چلتا ہے ایک مقرر وقت تکاور یہ کہ جو کچھ تم کرتے ہو، اللہ اس سے باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,30,'31:30','یہ اس وجہ سے ہے کہ اللہ ہی حق ہے اور اس کے سوا جن چیزوں کو وہ پکارتے ہیں وہ باطل ہیں اور بے شک اللہ برتر ہے، بڑا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(31,31,'31:31','کیا تم نے دیکھا نہیں کہ کشتی سمندر میں اللہ کے فضل سے چلتی ہے، تاکہ وہ تم کو اپنی نشانیاں دکھائے بے شک اس میں نشانیاں ہیں ہر صبر کرنے والے، شکر کرنے والے کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(31,32,'31:32','اور جب موت ان کے سر پر بادل کی طرح چھاجاتی ہے، وہ اللہ کو پکارتے ہیں اس کے لیے دین کو خالص کرتے ہوئے پھر جب وہ ان کو نجات دے کر خشکی کی طرف لے آتا ہے تو ان میں کچھ اعتدال پر رہتے ہیں اور ہماری نشانیوں کاانکار وہی لوگ کرتے ہیں جو بدعہد اور ناشکر گزار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(31,33,'31:33','اے لوگو، اپنے رب سے ڈرو اور اس دن سے ڈرو جب کہ کوئی باپ اپنے بیٹے کی طرف سے بدلہ نہ دے گا اور نہ کوئی بیٹا اپنے باپ کی طرف سے کچھ بدلہ دینے والاہوگا بے شک اللہ کا وعدہ سچا ہے تو دنیا کی زندگی تمھیں دھوکے میں نہ ڈالے اور نہ دھوکہ باز تم کو اللہ کے بارے میں دھوکہ دینے پائے');
INSERT INTO "ur_wahid_uddin" VALUES(31,34,'31:34','بے شک اللہ ہی کو قیامت کا علم ہے اور وہی بارش برساتا ہے اور وہ جانتا ہے جو کچھ رحم میں ہے اور کوئی شخص نہیں جانتا کہ کل وہ کیا کمائی کرے گا اور کوئی شخص نہیں جانتا کہ وہ کس زمین میں مرے گا بے شک اللہ جاننے والا، باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(32,1,'32:1','الم');
INSERT INTO "ur_wahid_uddin" VALUES(32,2,'32:2','یہ نازل کی ہوئی کتاب ہے، اس میں کچھ شبہ نہیں ، خداوندعالم کی طرف سے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(32,3,'32:3','کیا وہ کہتے ہیں کہ اس شخص نے اس کو خود گھڑلیا ہے بلکہ یہ حق ہے تمھارے رب کی طرف سے، تاکہ تم ان لوگوں کو ڈر ادو جن کے پاس تم سے پہلے کوئی ڈرانے والا نہیں آیا، تاکہ وہ راہ پر آجائیں');
INSERT INTO "ur_wahid_uddin" VALUES(32,4,'32:4','اللہ ہی ہے جس نے پیدا کیا آسمانوں اور زمین کو اور جوا ن کے درمیان ہے چھ دنوں میں ، پھر وہ عرش پر قائم ہوا اس کے سوا نہ تمھارا کوئی مددگار ہے اور نہ کوئی سفارش کرنے والا تو کیا تم دھیان نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(32,5,'32:5','وہ آسمان سے زمین تک تمام معاملات کی تدبیر کرتا ہےپھر وہ اس کی طرف لوٹتے ہیں ایک ایسے دن میں جس کی مقدار تمھاری گنتی سے ہزار سال کے برابر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(32,6,'32:6','وہی ہے پوشیدہ اور ظاہر کو جاننے والا زبردست ہے، رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(32,7,'32:7','اس نے جو چیز بھی بنائی خوب بنائیاور اس نے انسان کی تخلیق کی ابتدا مٹی سے کی');
INSERT INTO "ur_wahid_uddin" VALUES(32,8,'32:8','پھر اس کی نسل حقیر پانی کے خلاصہ سے چلائی');
INSERT INTO "ur_wahid_uddin" VALUES(32,9,'32:9','پھر اس کے اعضاء درست کيے اور اس میں اپنی روح پھونکیتمھارے لیے کان اور آنکھیں اور دل بنائے تم لوگ بہت کم شکر کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(32,10,'32:10','اور انھوں نے کہا کہ کیا جب ہم زمین میں گم ہوجائیں گے تو ہم پھر نئے سرے سے پیدا کيے جائیں گے بلکہ وہ اپنے رب کی ملاقات کے منکر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(32,11,'32:11','کہو کہ موت کافرشتہ تمھاری جان قبض کرتا ہے جو تم پر مقرر کیا گیا ہےپھر تم اپنے رب کی طرف لوٹائے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(32,12,'32:12','اور کاش تم دیکھو جب کہ یہ مجرم لوگ اپنے رب کے سامنے سرجھکائے ہوں گے اے ہمارے رب، ہم نے دیکھ لیا اور ہم نے سن لیا تو ہم کو واپس بھیج دے کہ ہم نیک کام کریں ہم یقین کرنے والے بن گئے');
INSERT INTO "ur_wahid_uddin" VALUES(32,13,'32:13','اور اگر ہم چاہتے تو ہرشخص کو اس کی ہدایت دے دیتے لیکن میری بات ثابت ہوچکی کہ میں جہنم کو جنوں اور انسانوں سے بھروں گا');
INSERT INTO "ur_wahid_uddin" VALUES(32,14,'32:14','تو اب مزہ چکھو اس بات کا کہ تم نے اس دن کی ملاقات کو بھلا دیا ہم نے بھی تم کو بھلادیااور اپنے کيے کی بدولت ہمیشہ کا عذاب چکھو');
INSERT INTO "ur_wahid_uddin" VALUES(32,15,'32:15','ہماری آیتوں پر وہی لوگ ایمان لاتے ہیں کہ جب ان کو ان کے ذریعہ سے یاد دہانی کی جاتی ہے تو وہ سجدے میں گر پڑتے ہیں اور اپنے رب کی حمد کے ساتھ اس کی تسبیح کرتے ہیں اور وہ تکبر نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(32,16,'32:16','ان کے پہلو بستروں سے الگ رہتے ہیں وہ اپنے رب کوپکارتے ہیں ڈر سے اور امید سے اور جو کچھ ہم نے ان کو دیا ہے، وہ اس میں سے خرچ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(32,17,'32:17','تو کسی کو خبر نہیں کہ ان لوگوں کے لیے ان کے اعمال کے صلہ میں آنکھوں کی کیا ٹھنڈک چھپا رکھی گئی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(32,18,'32:18','تو کیا جو مومن ہے وہ اس شخص جیسا ہوگا جو نافرمان ہے دونوں برابر نہیں ہوسکتے');
INSERT INTO "ur_wahid_uddin" VALUES(32,19,'32:19','جولوگ ایمان لائے اور انھوں نے اچھے کام کيے تو ان کے ليے جنت کی قیام گاہیں ہیں ، ضیافت ان کاموں کی وجہ سے جو وہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(32,20,'32:20','اور جن لوگوں نے نافرمانی کی تو ان کا ٹھکانا آگ ہے، وہ لوگ جب اس سے نکلنا چاہیں گے تو پھر وہ اسی میں دھکیل دئے جائیں گے اور ان سے کہا جائے گا کہ آگ کاعذاب چکھو جس کو تم جھٹلاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(32,21,'32:21','اور ہم ان کو بڑے عذاب سے پہلے چھوٹا عذاب چکھائیں گے، شاید کہ وہ باز آجائیں');
INSERT INTO "ur_wahid_uddin" VALUES(32,22,'32:22','اور اس شخص سے زیادہ ظالم کون ہوگا جس کو اس کے رب کی آیتوں کے ذریعہ نصیحت کی جائے، پھر وہ ان سے اعراض کرےہم ایسے مجرموں سے ضرور بدلہ لیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(32,23,'32:23','اور ہم نے موسیٰ کو کتاب دی تو تم اس کے ملنے میں کچھ شک نہ کرواور ہم نے اس کو بنی اسرائیل کے لیے ہدایت بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(32,24,'32:24','اور ہم نے ان میں پیشوا بنائے جو ہمارے حکم سے لوگوں کی رہنمائی کرتے تھے، جب کہ انھوں نے صبر کیا اور وہ ہماری آیتوں پریقین رکھتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(32,25,'32:25','بے شک تیرا رب قیامت کے دن ان کے درمیان ان امور میں فیصلہ کردے گا جن میں وہ باہم اختلاف کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(32,26,'32:26','کیا ان کے لیے یہ چیز ہدایت دینے والی نہ بنی کہ ان سے پہلے ہم نے کتنی قوموں کو ہلاک کردیاجن کی بستیوں میں یہ لوگ آتے جاتے ہیں بے شک اس میں نشانیاں ہیں ، کیا یہ لوگ سنتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(32,27,'32:27','کیاانھوں نے نہیں دیکھا کہ ہم پانی کو چٹیل زمین کی طرف ہانک کرلے جاتے ہیں پھر ہم اس سے کھیتی نکالتے ہیں جس سے ان کے چوپائے کھاتے ہیں اور وہ خود بھی پھر کیاوہ دیکھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(32,28,'32:28','اور وہ کہتے ہیں کہ یہ فیصلہ کب ہوگااگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(32,29,'32:29','کہو کہ فیصلہ کے دن ان لوگوں کاایمان نفع نہ دے گا جنھوں نے انکار کیا اور نہ ان کو مہلت دی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(32,30,'32:30','تو ان سے اعراض کرو اور منتظر رہو، یہ بھی منتظر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(33,1,'33:1','اے نبی ، اللہ سے ڈرو اور منکروں اور منافقوں کی اطاعت نہ کرو، بے شک اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,2,'33:2','اور پیروی کرو اس چیز کی جو تمھارے رب کی طرف سے تم پر وحی کی جارہی ہے، بے شک اللہ باخبر ہے اس سے جو تم لوگ کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(33,3,'33:3','اور اللہ پر بھروسہ رکھو، اوروہ اللہ کارساز ہونے کے لیے کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,4,'33:4','اللہ نے کسی آدمی کے سینے میں دو دل نہیں رکھےاور نہ تمھاری بیویوں کو جن سے تم ظہار کرتے ہو تمھاری ماں بنایا اور نہ تمھارے منہ بولے بیٹوں کو تمھارا بیٹا بنادیا یہ سب تمھارے اپنے منہ کی باتیں ہیں اور اللہ حق بات کہتا ہے اور وہ سیدھا راستہ دکھاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,5,'33:5','منہ بولے بیٹوں کو ان کے باپوں کی نسبت سے پکارو، یہ اللہ کے نزدیک زیادہ منصفانہ بات ہےپھر اگر تم ان کے باپ کو نہ جانو تو وہ تمھارے دینی بھائی ہیں اور تمھارے رفیق ہیں اور جس چیز میں تم سے بھول چوک ہوجائے تو اس کا تم پر کچھ گناہ نہیں مگر جو تم دل سے ارادہ کرکے کرواور اللہ معاف کرنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,6,'33:6','اور نبی کا حق مومنوں پر ان کی اپنی جان سے بھی زیادہ ہے، اور نبی کی بیویاں ان کی مائیں ہیں اور رشتہ دار خدا کی کتاب میں ، دوسرے مومنین اور مہاجرین کی بہ نسبت، ایک دوسرے سے زیادہ تعلق رکھتے ہیں مگر یہ کہ تم اپنے دوستوں سے کچھ سلوک کرنا چاہو یہ کتاب میں لکھا ہوا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,7,'33:7','اور جب ہم نے پیغمبروں سے ان کا عہد لیا اور تم سے اور نوح سے اور ابراہیم اور موسیٰ اور عیسیٰ بن مریم سے اور ہم نے ان سے پختہ عہدلیا');
INSERT INTO "ur_wahid_uddin" VALUES(33,8,'33:8','تاکہ اللہ سچے لوگوں سے ان کی سچائی کے بارے میں سوال کرے، اور منکروں کے لیے اس نے دردناک عذاب تیار کررکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,9,'33:9','اے لوگو، جوایمان لائے ہو، اپنے اوپر اللہ کے احسان کو یاد کرو، جب تم پر فوجیں چڑھ آئیں تو ہم نے ان پر ایک آندھی بھیجی اور ایسی فوج جو تم کودکھائی نہ دیتی تھیاور اللہ دیکھنے والا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(33,10,'33:10','جب کہ وہ تم پر چڑھ آئے، تمھارے اوپر کی طرف سے اور تمھارے نیچے کی طرف سےاور جب آنکھیں پتھرا گئیں اور دل گلوں تک پہنچ گئے اور تم اللہ کے ساتھ طرح طرح کے گمان کرنے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(33,11,'33:11','اس وقت ایمان والے امتحان میں ڈالے گئے اور بالکل ہلادئے گئے');
INSERT INTO "ur_wahid_uddin" VALUES(33,12,'33:12','اور جب منافقین اور وہ لوگ جن کے دلوں میں روگ ہے، وہ کہتے تھے کہ اللہ اور اس کے رسول نے جو وعدہ ہم سے کیا تھا وہ صرف فریب تھا');
INSERT INTO "ur_wahid_uddin" VALUES(33,13,'33:13','اور جب ان میں سے ایک گروہ نے کہا کہ اے یثرب والو، تمھارے لیے ٹھہرنے کاموقع نہیں ، تو تم لوٹ چلواور ان میں سے ایک گروہ پیغمبر سے اجازت مانگتا تھا، وہ کہتا تھا کہ ہمارے گھر غیرمحفوظ ہیں ، اور وہ غیرمحفوظ نہیں وہ صرف بھاگنا چاہتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(33,14,'33:14','اور اگر مدینہ کے اطراف سے ان پر کوئی گھس آتا اور ان کو فتنہ کی دعوت دیتا تو وہ مان لیتے اور وہ اس میں بہت کم دیر کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(33,15,'33:15','اور انھوں نے اس سے پہلے اللہ سے عہد کیا تھا کہ وہ پیٹھ نہ پھیریں گےاور اللہ سے کيے ہوئے عہد کی پوچھ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(33,16,'33:16','کہو کہ اگر تم موت سے یاقتل سے بھاگو تو یہ بھاگنا تمھارے کچھ کام نہ آئے گا اور اس حالت میں تم کوصرف تھوڑے دنوں فائدہ اٹھانے کا موقع ملے گا');
INSERT INTO "ur_wahid_uddin" VALUES(33,17,'33:17','کہو، کون ہے جو تم کو اللہ سے بچائے اگر وہ تم کو نقصان پہنچانا چاہے، یاوہ تم پررحمت کرنا چاہے اور وہ اپنے لیے اللہ کے مقابلہ میں کوئی حمایتی اور مددرگار نہ پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(33,18,'33:18','اللہ تم میں سے ان لوگوں کو جانتا ہے جو تم میں سے روکنے والے ہیں اور جو اپنے بھائیوں سے کہتے ہیں کہ ہمارے پاس آجاؤاور وہ لڑائی میں کم ہی آتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(33,19,'33:19','وہ تم سے بخل کرتے ہیں پس جب خوف پیش آتا ہے تو تم دیکھتے ہو کہ وہ تمھاری طرف اس طرح دیکھنے لگتے ہیں کہ ان کی آنکھیں اس شخص کی آنکھوں کی طرح گردش کررہی ہیں جس پر موت کی بے ہوشی طاری ہوپھر جب خطرہ دور ہوجاتا ہے تو وہ مال کی حرص میں تم سے تیز زبانی کے ساتھ ملتے ہیں یہ لوگ یقین نہیں لائے تو اللہ نے ان کے اعمال اکارت کردئےاور یہ اللہ کے لیے آسان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,20,'33:20','وہ سمجھتے ہیں کہ فوجیں ابھی گئی نہیں ہیں اور اگر فوجیں آجائیں تو یہ لوگ یہی پسند کریں گے کہ کاش ہم بدؤوں کے ساتھ دیہات میں ہوں ، تمھاری خبریں پوچھتے رہیں اور اگر وہ تمھارے ساتھ ہوتے تو لڑائی میں کم ہی حصہ لیتے');
INSERT INTO "ur_wahid_uddin" VALUES(33,21,'33:21','تمھارے لیے اللہ کے رسول میں بہترین نمونہ تھا،اس شخص کے لیے جو اللہ کااور آخرت کے دن کا امیدوار ہو اور کثرت سے اللہ کو یاد کرے');
INSERT INTO "ur_wahid_uddin" VALUES(33,22,'33:22','اور جب ایمان والوں نے فوجوں کودیکھا ، وہ بولے یہ وہی ہے جس کااللہ اور اس کے رسول نے ہم سے وعدہ کیا تھا اور اللہ اور اس کے رسول نے سچ کہااور اس نے ان کے ایمان اور اطاعت میں اضافہ کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(33,23,'33:23','ایمان والوں میں ایسے لوگ بھی ہیں جنھوں نے اللہ سے کيے ہوئے عہد کو پورا کردکھایاپس ان میں سے کوئی اپنا ذمہ پورا کرچکا اور ان میں سے کوئی منتظر ہےاور انھوں نے ذرا بھی تبدیلی نہیں کی');
INSERT INTO "ur_wahid_uddin" VALUES(33,24,'33:24','تاکہ اللہ سچوں کو ان کی سچائی کا بدلہ دے اور منافقوں کو عذاب دے اگر چاہے یا ان کی توبہ قبول کرے بے شک اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,25,'33:25','اور اللہ نے منکروں کوان کے غصے کے ساتھ پھیردیا کہ ان کی کچھ بھی مراد پوری نہ ہوئی اور مومنین کی طرف سے لڑنے کے لیے اللہ کافی ہوگیا اللہ قوت والا، زبردست ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,26,'33:26','اور اللہ نے ان اہل کتاب کو جنھوں نے حملہ آوروں کا ساتھ دیا ان کے قلعوں سے اتارااور ان کے دلوں میں اس نے رعب ڈال دیا تم ان کے ایک گروہ کو قتل کررہے ہو اور ایک گروہ کو قید کررہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(33,27,'33:27','اور اس نے ان کی زمین اور ان کے گھروں اور ان کے مالوں کا تم کو وارث بنادیا اور ایسی زمین کا بھی جس پر تم نے قدم نہیں رکھااور اللہ ہر چیز پرقدرت رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,28,'33:28','اے نبی، اپنی بیویوں سے کہو کہ اگر تم دنیا کی زندگی اور اس کی زینت چاہتی ہو تو آؤ، میں تم کو کچھ مال و متاع دے کر خوبی کے ساتھ رخصت کردوں');
INSERT INTO "ur_wahid_uddin" VALUES(33,29,'33:29','اور اگر تم اللہ اور اس کے رسول اور آخرت کے گھر کو چاہتی ہو تو اللہ نے تم میں سے نیک کر داروں کے لیے بڑا اجر مہیا کررکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,30,'33:30','اے نبی کی بیویو، تم میں سے جوکسی کھلی بے حیائی کاارتکاب کرے گی،اس کو دہرا عذاب دیا جائے گااور یہ اللہ کے لیے آسان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,31,'33:31','اور تم میں سے جو اللہ اور اس کے رسول کی فرماں برداری کرے گی اور نیک عمل کرے گی تو ہم اس کو اس کا دہرا اجر دیں گے اور ہم نے اس کے لیے باعزت روزی تیار کررکھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,32,'33:32','اے نبی کی بیویو ، تم عام عورتوں کی طرح نہیں ہو اگر تم اللہ سے ڈرو تو تم لہجہ میں نرمی نہ اختیار کرو کہ جس کے دل میں بیماری ہے وہ لالچ میں پڑجائے اور معروف کے مطابق بات کہو');
INSERT INTO "ur_wahid_uddin" VALUES(33,33,'33:33','اور تم اپنے گھر میں قرار سے رہو اور سابقہ جاہلیت کی طرح دکھلاتی نہ پھرواور نماز قائم کرو اور زکوٰۃ اداکرو اور اللہ اور اس کے رسول کی اطاعت کرو اللہ تو چاہتا ہے کہ تم اہل بیت سے آلودگی کودور کرے اور تم کو پوری طرح پاک کردے');
INSERT INTO "ur_wahid_uddin" VALUES(33,34,'33:34','اور تمھارے گھروں میں اللہ کی آیات اور حکمت کی جو تعلیم ہوتی ہے اس کو یاد رکھو بے شک اللہ باریک بیں ہے، خبر رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,35,'33:35','بے شک اطاعت کرنے والے مرد اور اطاعت کرنے والی عورتیں اور ایمان لانے والے مرد اور ایمان لانے والی عورتیں اور فرماں برداری کرنے والے مرد اور فرماں برداری کرنے والی عورتیں اورر است باز مرد اور راست باز عورتیں اور صبر کرنے والے مرد اور صبر کرنے والی عورتیں اور خشوع کرنے والے مرد اور خشوع کرنے والی عورتیں اور صدقہ دینے والے مرد اور صدقہ دینے والی عورتیں اور روزہ رکھنے والے مرد اور روزہ رکھنے والی عورتیں اور اپنی شرمگاہوں کی حفاظت کرنے والے مرد اور حفاظت کرنے والی عورتیں اوراللہ کو کثرت سے یاد کرنے والے مرد اور یادکرنے والی عورتیں — ان کے لیے اللہ نے مغفرت اور بڑا اجر مہیا کررکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,36,'33:36','کسی مومن مرد یا کسی مومن عورت کے لیے گنجائش نہیں ہے کہ جب اللہ اور اس کارسول کسی معاملہ کا فیصلہ کردیں تو پھر ان کے لیے اس میں اختیار باقی رہے اور جو شخص اللہ اور اس کے رسول کی نافرمانی کرے گا تو وہ صریح گمراہی میں پڑگیا');
INSERT INTO "ur_wahid_uddin" VALUES(33,37,'33:37','اور جب تم اس شخص سے کہہ رہے تھے جس پر اللہ نے انعام کیا اور تم نے انعام کیا کہ اپنی بیوی کو روکے رکھو اور اللہ سے ڈرواور تم اپنے دل میں وہ بات چھپائے ہوئے تھے جس کو اللہ ظاہر کرنے والا تھااور تم لوگوں سے ڈر رہے تھے اور اللہ زیادہ حقدارہے کہ تم اس سے ڈروپھر جب زید اس سے اپنی غرض تمام کرچکا ، ہم نے تم سے اس کا نکاح کردیا تا کہ مسلمانوں پر اپنے منہ بولے بیٹوں کی بیویوں کے بارے میں کچھ تنگی نہ رہے جب کہ وہ ان سے اپنی غرض تمام کرلیں اور اللہ کا حکم ہونے والا ہی تھا');
INSERT INTO "ur_wahid_uddin" VALUES(33,38,'33:38','پیغمبر کے لیے اس میں کوئی مضائقہ نہیں جو اللہ نے اس کے لیے مقرر کردیا ہو یہی اللہ کی سنت ان پیغمبروں کے معاملہ میں رہی ہے جو پہلے گزر چکے ہیں اور اللہ کاحکم ایک قطعی فیصلہ ہوتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,39,'33:39','وہ اللہ کے پیغاموں کو پہنچاتے تھے اور اسی سے ڈرتے تھے اور اللہ کے سوا کسی سے نہیں ڈرتے تھے اور اللہ حساب لینے کے لیے کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,40,'33:40','محمد تمھارے مَردوں میں سے کسی کے باپ نہیں ہیں ، لیکن وہ اللہ کے رسول اور نبیوں کے خاتم ہیں اور اللہ ہر چیز کا علم رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,41,'33:41','اے ایمان والو، اللہ کو بہت زیادہ یاد کرو');
INSERT INTO "ur_wahid_uddin" VALUES(33,42,'33:42','اور اس کی تسبیح کرو صبح اور شام');
INSERT INTO "ur_wahid_uddin" VALUES(33,43,'33:43','وہی ہے جو تم پر رحمت بھیجتا ہے اور اس کے فرشتے بھی تاکہ وه تم کو تاریکیوں سے نکال کر روشنی میں لائے اور وہ مومنوں پر بہت مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,44,'33:44','جس روز وہ اس سے ملیں گے، ان کا استقبال سلام سے ہوگا اور اس نے ان کے لیے باعزت صلہ تیار کررکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,45,'33:45','اے نبی، ہم نے تم کو گواہی دینے والا اور خوش خبری دینے والا اور ڈرانے والا بناکر بھیجا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,46,'33:46','اور اللہ کی طرف، اس کے اذن سے، دعوت دینے والا اور ایک روشن چراغ');
INSERT INTO "ur_wahid_uddin" VALUES(33,47,'33:47','اور مومنوں کو بشارت دے دو کہ ان کے لیے اللہ کی طرف سے بہت بڑا فضل ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,48,'33:48','اور تم منکروں اور منافقوں کی بات نہ مانو اور ان کے ستانے کو نظر انداز کرو اور اللہ پر بھروسہ رکھواور اللہ بھروسہ کے لیے کافی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,49,'33:49','اے ایمان والو، جب تم مومن عورتوں سے نکاح کرو، پھر ان کو ہاتھ لگانے سے پہلے طلاق دے دو تو ان کے بارے میں تم پر کوئی عدت لازم نہیں ہے جس کا تم شمار کرو پس ان کو کچھ متاع دے دو اور خوبی کے ساتھ ان کو رخصت کردو');
INSERT INTO "ur_wahid_uddin" VALUES(33,50,'33:50','اے نبی، ہم نے تمھارے لیے حلال کردیں تمھاری وہ بیویاں جن کی مہر تم دے چکے ہو اور وہ عورتیں بھی جو تمھاری مملوکہ ہیں جو اللہ نے غنیمت میں تم کو دی ہیں اور تمھارے چچا کی بیٹیاں اور تمھاری پھوپھیوں کی بیٹیاں اور تمھارے ماموؤں کی بیٹیاں اور تمھاری خالاؤں کی بیٹیاں جنھوں نے تمھارے ساتھ ہجرت کی ہواور اس مسلمان عورت کو بھی جواپنے آپ کو پیغمبر کو دے دے،بشرطیکہ پیغمبر اس کو نکاح میں لانا چاہے، یہ خاص تمھارے لیے ہے، مسلمانوں سے الگ ہم کو معلوم ہے جو ہم نے ان پران کی بیویوں اور ان کی باندیوں کے بارے میں فرض کیا ہے، تاکہ تم پر کوئی تنگی نہ رہے اور اللہ بخشنے والا،مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,51,'33:51','تم ان میں سے جس جس کو چاہو دور رکھو اور جس کو چاہو اپنے پاس رکھواور جن کو دور کیا تھا ان میں سے پھر کسی کو طلب کرو تب بھی تم پر کوئی گناہ نہیں اس میں زیادہ توقع ہے کہ ان کی آنکھیں ٹھنڈی رہیں گی، اور وہ رنجیدہ نہ ہوں گیاور وہ اس پر راضی رہیں جو تم ان سب کو دو اور اللہ جانتا ہے جو تمھارے دلوں میں ہےاور اللہ جاننے والا ہے،بردبار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,52,'33:52','ان کے علاوہ اور عورتیں تمھارے لیے حلال نہیں ہیں اور نہ یہ درست ہے کہ تم ان کی جگہ دوسری بیویاں کرلو، اگرچہ ان کی صورت تم کو اچھی لگے،مگر جو تمھاری مملوکہ ہواور اللہ ہر چیز پر نگراں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,53,'33:53','اے ایمان والو، نبی کے گھروں میں مت جایا کرو مگر جس وقت تم کو کھانے کے لیے اجازت دی جائے، ایسے طور پر کہ اس کی تیاری کے منتظر نہ رہولیکن جب تم کو بلایا جائے تو داخل ہوپھر جب تم کھاچکو تو اُٹھ کر چلے جاؤ اور باتوں میں لگے ہوئے بیٹھے نہ رہواس بات سے نبی کو ناگواری ہوتی ہےمگر وہ تمھارا لحاظ کرتے ہیں اور اللہ حق بات کہنے میں کسی کا لحاظ نہیں کرتااور جب تم ازواجِ رسول سے کوئی چیز مانگو تو پردے کی اوٹ سے مانگویہ طریقہ تمھارے دلوں کے لیے زیادہ پاکیزہ ہے اور ان کے دلوں کے لیے بھیاور تمھارے لیے جائز نہیں کہ تم اللہ کے رسول کو تکلیف دو اور نہ یہ جائز ہے کہ تم ان کے بعد ان کی بیویوں سے کبھی نکاح کرو یہ اللہ کے نزدیک بڑی سنگین بات ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,54,'33:54','تم کسی چیز کو ظاہر کرو یا اس کو چھپاؤ تو اللہ ہر چیز کو جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,55,'33:55','پیغمبر کی بیویوں پر اپنے باپوں کے بارے میں کوئی گناہ نہیں ہےاور نہ اپنے بیٹوں کے بارے میں اور نہ اپنے بھائیوں کے بارے میں اور نہ اپنے بھتیجوں کے بارے میں اور نہ اپنے بھانجوں کے بارے میں اور نہ اپنی عورتوں کے بارے میں اور نہ اپنی لونڈیوں کے بارے میں اور تم اللہ سے ڈرتی رہو، بے شک اللہ ہر چیز پر نگاہ رکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,56,'33:56','اللہ اور اس کے فرشتے نبی پر رحمت بھیجتے ہیں اے ایمان والو، تم بھی اس پر درود وسلام بھیجو');
INSERT INTO "ur_wahid_uddin" VALUES(33,57,'33:57','جولوگ اللہ اور اس کے رسول کو اذیت دیتے ہیں اللہ نے ان پر دنیا اور آخرت میں لعنت کی اور ان کے لیے ذلیل کرنے والا عذاب تیار کررکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,58,'33:58','اور جو لوگ مومن مردوں اور مومن عورتوں کو اذیت دیتے ہیں بغیر اس کے کہ انھوں نے کچھ کیا ہو تو انھوں نے بہتان کا اور صریح گناہ کا بوجھ اٹھایا');
INSERT INTO "ur_wahid_uddin" VALUES(33,59,'33:59','اے نبی، اپنی بیویوں سے کہو اور اپنی بیٹیوں سے اور مسلمانوں کی عورتوں سے کہ نیچے کرلیا کریں اپنے اوپر تھوڑی سی اپنی چادریں ، اس سے جلدی پہچان ہوجائے گی تو وہ ستائی نہ جائیں گیاور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,60,'33:60','منافقین اور وہ لوگ جن کے دلوں میں روگ ہے اور جومدینہ میں جھوٹی خبریں پھیلانے والے ہیں ،اگر وہ باز نہ آئے تو ہم تم کو ان کے پیچھے لگادیں گےپھر وہ تمھارے ساتھ مدینہ میں بہت کم رہنے پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(33,61,'33:61','پھٹکارے ہوئے،جہاں پائے جائیں گے پکڑے جائیں گے اور بری طرح مارے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(33,62,'33:62','یہ اللہ کا دستور ہے ان لوگوں کے بارے میں جو پہلے گزرچکے ہیں اور تم اللہ کے دستور میں کوئی تبدیلی نہ پاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(33,63,'33:63','لوگ تم سے قیامت کے بارے میں پوچھتے ہیں کہو کہ اس کا علم تو صرف اللہ کے پاس ہےاور تم کو کیا خبر، شاید قیامت قریب آگئی ہو');
INSERT INTO "ur_wahid_uddin" VALUES(33,64,'33:64','بے شک اللہ نے منکروں کو رحمت سے دور کردیا ہےاور ان کے لیے بھڑکتی ہوئی آگ تیار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(33,65,'33:65','اس میں وہ ہمیشہ رہیں گے وہ نہ کوئی حامی پائیں گے اور نہ کوئی مددگار');
INSERT INTO "ur_wahid_uddin" VALUES(33,66,'33:66','جس دن ان کے چہرے آگ میں الٹ پلٹ کيے جائیں گےوہ کہیں گے، اے کاش، ہم نے اللہ کی اطاعت کی ہوتی اور ہم نے رسول کی اطاعت کی ہوتی');
INSERT INTO "ur_wahid_uddin" VALUES(33,67,'33:67','اور وہ کہیں گے اے ہمارے رب ، ہم نے اپنے سرداروں اور اپنے بڑوں کا کہنا مانا تو انھوں نے ہم کو راستے سے بھٹکادیا');
INSERT INTO "ur_wahid_uddin" VALUES(33,68,'33:68','اے ہمارے رب ، ان کودہرا عذاب دے اور ان پر بھاری لعنت کر');
INSERT INTO "ur_wahid_uddin" VALUES(33,69,'33:69','اے ایمان والو، تم ان لوگوں کی طرح نہ بنو جنھوں نے موسیٰ کو اذیت پہنچائی تو اللہ نے اس کو ان لوگوں کی باتوں سے بری ثابت کیااور وہ اللہ کے نزدیک باعزت تھا');
INSERT INTO "ur_wahid_uddin" VALUES(33,70,'33:70','اے ایمان والو، اللہ سے ڈرو اور درست بات کہو');
INSERT INTO "ur_wahid_uddin" VALUES(33,71,'33:71','وہ تمھارے اعمال سدھارے گا اور تمھارے گناہوں کو بخش دے گااور جو شخص اللہ اور اس کے رسول کی اطاعت کرے اس نے بڑی کامیابی حاصل کی');
INSERT INTO "ur_wahid_uddin" VALUES(33,72,'33:72','ہم نے امانت کو آسمانوں اور زمین اور پہاڑوں کے سامنے پیش کیا تو انھوں نے اس کو اٹھانے سے انکار کیا اور وہ اس سے ڈرگئے، اور انسان نے اس کو اٹھالیابے شک وہ ظالم اور جاہل تھا');
INSERT INTO "ur_wahid_uddin" VALUES(33,73,'33:73','تاکہ اللہ منافق مردوں اور منافق عورتوں کواور مشرک مردوں اور مشرک عورتوں کو سزادے اور مومن مردوں اور مومن عورتوں پرتوجہ فرمائےاور اللہ بخشنے والا ،مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,1,'34:1','تعریف خدا کے لیے ہے جس کاوہ سب کچھ ہے جو آسمانوں میں ہے اور جو کچھ زمین میں ہے اوراسی کی تعریف ہے آخرت میں اور وہ حکمت والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,2,'34:2','وہ جانتا ہے جوکچھ زمین کے اندر داخل ہوتا ہے اور جو کچھ اس سے نکلتا ہے،اور جو آسمان سے اترتا ہے اور جو اس میں چڑھتا ہےاوروہ رحمت والا، بخشنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,3,'34:3','اور جنھوں نے انکار کیا وہ کہتے ہیں کہ ہم پر قیامت نہیں آئے گیکہو کہ کیوں نہیں قسم ہے میرے پروردگار عالم الغیب کی، وہ ضرور تم پر آئے گیاس سے ذرہ برابر کوئی چیز مخفی نہیں ، نہ آسمانوں میں اور نہ زمین میں اور نہ کوئی چیز اس سے چھوٹی اور نہ بڑی، مگر وہ ایک کھلی کتاب میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,4,'34:4','تاکہ وہ ان لوگوں کو بدلہ دے جو ایمان لائے اور نیک کام کیایہی لوگ ہیں جن کے لیے معافی ہے اورعزت کی روزی');
INSERT INTO "ur_wahid_uddin" VALUES(34,5,'34:5','اور جن لوگوں نے ہماری آیتوں کو عاجز کرنے کی کوشش کی، ان کے لیے سختی کادردناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,6,'34:6','اور جن کو علم دیا گیا ہے وہ، اس چیز کو جو تمھارے رب کی طرف سے تمھارے پاس بھیجا گیا ہے، جانتے ہیں کہ وہ حق ہے اور وہ خدائے عزیزا ورحمید کاراستہ دکھاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,7,'34:7','اور جنھوں نے انکار کیا وہ کہتے ہیں ، کیا ہم تم کو ایک ایسا آدمی بتائیں جو تم کو خبر دیتا ہے کہ جب تم بالکل ریزہ ریزہ ہوجاؤگے تو پھر تم کو نئے سرے سے بننا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,8,'34:8','کیا اس نے اللہ پر جھوٹ باندھا ہے یا اس کو کسی طرح کاجنون ہے بلکہ جو لوگ آخرت پر یقین نہیں رکھتے وہی عذاب میں اور دور کی گمراہی میں مبتلا ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(34,9,'34:9','تو کیا انھوں نے آسمان اورزمین کی طرف نظر نہیں کی جو ان کے آگے ہے اور ان کے پیچھے بھی اگر ہم چاہیں تو ان کو زمین میں دھنسا دیں یا ان پر آسمان سے ٹکڑاگرادیں بے شک اس میں نشانی ہے ہر اس بندے کے لیے جو متوجہ ہونے والا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(34,10,'34:10','اور ہم نے داؤد کو اپنی طرف سے بڑی نعمت دی اے پہاڑو، تم بھی اس کے ساتھ تسبیح میں شرکت کرواور اسی طرح پرندوں کو حکم دیااور ہم نے لوہے کو اس کے لیے نرم کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(34,11,'34:11','کہ تم کشادہ زرہیں بناؤ اور کڑیوں کو اندازے سے جوڑواور نیک عمل کروجو کچھ تم کرتے ہو اس کو میں دیکھ رہا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(34,12,'34:12','اور سلیمان کے لیے ہم نے ہوا کو مسخر کردیا، اس کی صبح کی منزل ایک مہینہ کی ہوتی اور اس کی شام کی منزل ایک مہینہ کیاور ہم نے اس کے لیے تانبے کاچشمہ بہادیااور جنّات میں سے ایسے تھے جو اس کے رب کے حکم سے اس کے آگے کام کرتے تھےاور ان میں سے جوکوئی ہمارے حکم سے پھرے تو ہم اس کوآگ کاعذاب چکھائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(34,13,'34:13','وہ اس کے لیے بناتے جو وہ چاہتا، عمارتیں اور تصویریں اور حوض جیسے لگن اور جمی ہوئی دیگیں — اے آل داؤد، شکر گزاری کے ساتھ عمل کرو اور میرے بندوں میں کم ہی شکرگزار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(34,14,'34:14','پھر جب ہم نے اس پر موت کافیصلہ نافذ کیا تو کسی چیز نے ان کو اس کے مرنے کاپتہ نہیں دیا مگر زمین کے کیڑے نے، وہ اس کے عصا کو کھاتا تھاپس جب وہ گرپڑا تب جنوں پر کھلا کہ اگر وہ غیب کو جانتے تو اس ذلت کی مصیبت میں نہ رہتے');
INSERT INTO "ur_wahid_uddin" VALUES(34,15,'34:15','سبا کے ليے ان کے اپنے مسکن میں نشانی تھی دو باغ دائیں اور بائیں ، اپنے رب کے رزق سے کھاؤ اور اس کا شکر کرو عمدہ شہر اور بخشنے والا رب');
INSERT INTO "ur_wahid_uddin" VALUES(34,16,'34:16','پس انھوں نے سر تابی کی تو ہم نے ان پر بند کا سیلاب بھیج دیا اور ان کے باغوں کو دو ایسے باغوں سے بدل دیا جن میں بدمزہ پھل اور جھاؤ کے درخت اور کچھ تھوڑے سے بیر');
INSERT INTO "ur_wahid_uddin" VALUES(34,17,'34:17','یہ ہم نے ان کی ناشکری کا بدلہ دیا اور ایسا بدلہ ہم اسی کو دیتے ہیں جو ناشکر ہو');
INSERT INTO "ur_wahid_uddin" VALUES(34,18,'34:18','اور ہم نے ان کے اور ان کی بستیوں کے درمیان ، جہاں ہم نے برکت رکھی تھی،ایسی بستیاں آبادکیں جو نظر آتی تھیں اور ہم نے ان کے درمیان سفر کی منزلیں ٹھہرادیں ان میں رات دن امن کے ساتھ چلو');
INSERT INTO "ur_wahid_uddin" VALUES(34,19,'34:19','پھر انھوں نے کہا کہ اے ہمارے رب، ہمارے سفروں کے درمیان دوری ڈال دےاور انھوں نے اپنی جانوں پر ظلم کیا تو ہم نے ان کو افسانہ بنادیا اور ہم نے ان کو بالکل تتر بتر کردیابے شک اس میں نشانی ہے ہر صبر کرنے والے،شکر کرنے والے کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(34,20,'34:20','اور ابلیس نے ان کے اوپر اپنا گمان سچ کردکھایاپس انھوں نے اس کی پیروی کی، مگر ایمان والوں کا ایک گروہ');
INSERT INTO "ur_wahid_uddin" VALUES(34,21,'34:21','اور ابلیس کو ان کے اوپر کوئی اختیار نہ تھا،مگر یہ کہ ہم معلوم کرلیں ان لوگوں کو جو آخرت پر ایمان رکھتے ہیں ان لوگوں سے (الگ کرکے) جو اس کی طرف سے شک میں ہیں اور تمھارا رب ہر چیز پر نگراں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,22,'34:22','کہو کہ ان کو پکارو جن کو تم نے خدا کے سوا معبود سمجھ رکھا ہے، وہ نہ آسمانوں میں ذرہ برابر اختیار رکھتے ہیں اور نہ زمین میں اور نہ ان دونوں میں ان کی کوئی شرکت ہے اور نہ ان میں سے کوئی اس کامددگار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,23,'34:23','اور اس کے سامنے کوئی شفاعت کام نہیں آتی مگر اس کے لیے جس کے لیے وہ اجازت دےیہاں تک کہ جب ان کے دلوں سے گھبراہٹ دور ہوگی تو وہ پوچھیں گے کہ تمھارے رب نے کیا فرمایا وہ کہیں گے کہ حق بات کا حکم فرمایا اور وہ سب سے اوپر ہے، سب سے بڑا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,24,'34:24','کہو کہ کون تم کو آسمانوں اور زمین سے رزق دیتا ہےکہو کہ اللہاور ہم میں اور تم میں سے کوئی ایک ہدایت پر ہے یا کھلی ہوئی گمراہی میں');
INSERT INTO "ur_wahid_uddin" VALUES(34,25,'34:25','کہو کہ جو قصور ہم نے کیا اس کی کوئی پوچھ تم سے نہ ہو گیاور جو کچھ تم کررہے ہو اس کی بابت ہم سے نہیں پوچھا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(34,26,'34:26','کہوکہ ہمارا رب ہم کو جمع کرے گا، پھر ہمارے درمیان وہ حق کے مطابق، فیصلہ فرمائے گااور وہ فیصلہ كرنے والا هے، علم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,27,'34:27','کہو، مجھے ان کو دکھاؤ جن کو تم نے شریک بناکر خدا کے ساتھ ملا رکھا ہےہرگز نہیں ، بلکہ وہ اللہ زبردست ہے،حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,28,'34:28','اور ہم نے تم کو تمام انسانوں کے لیے خوش خبری دینے والا اور ڈرانے والا بناکر بھیجا ہے، مگر اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(34,29,'34:29','اور وہ کہتے ہیں کہ یہ وعدہ کب ہوگا اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(34,30,'34:30','کہو کہ تمھارے لیے ایک خاص دن کا وعدہ ہے کہ اس سے نہ ایک ساعت پیچھے ہٹ سکتے ہو اور نہ آگے بڑھ سکتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(34,31,'34:31','اور جن لوگوں نے انکار کیا وہ کہتے ہیں کہ ہم ہرگز نہ اس قرآن کو مانیں گے اور نہ اس کو جو اس کے آگے ہے اور اگر تم اس وقت کو دیکھو جب کہ یہ ظالم اپنے رب کے سامنے کھڑے کيے جائیں گے ایک دوسرے پر بات ڈالتا ہوگا جولوگ کمزور سمجھے جاتے تھے، وہ بڑا بننے والوں سے کہیں گے کہ اگر تم نہ ہوتے تو ہم ضرور ایمان والے ہوتے');
INSERT INTO "ur_wahid_uddin" VALUES(34,32,'34:32','بڑا بننے والے کمزور لوگوں کو جواب دیں گے کیا ہم نے تم کو ہدایت سے روکا تھا جب کہ وہ تم کو پہنچ چکی تھی، بلکہ تم خود مجرم ہو');
INSERT INTO "ur_wahid_uddin" VALUES(34,33,'34:33','اور کمزور لوگ بڑے لوگوں سے کہیں گے، نہیں بلکہ تمھاری رات دن کی تدبیروں سے، جب کہ تم ہم سے کہتے تھے کہ ہم اللہ کے ساتھ کفر کریں اور اس کے ساتھ شریک ٹھہرائیں اور وہ اپنی پشیمانی کوچھپائیں گے جب کہ وہ عذاب دیکھیں گےاور ہم منکروں کی گردن میں طوق ڈالیں گے وہ وہی بدلہ پائیں گے جو وہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(34,34,'34:34','اور ہم نے جس بستی میں بھی کوئی ڈرانے والا بھیجا تو اس کے خوش حال لوگوں نے یہی کہا کہ ہم تو اس کے منکر ہیں جو دے کر تم بھیجے گئے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(34,35,'34:35','اور انھوں نے کہا کہ ہم مال اور اولاد میں زیادہ ہیں اور ہم کبھی سزا پانے والے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(34,36,'34:36','کہوکہ میرا رب جس کو چاہتا ہے زیادہ روزی دیتا ہےاور جس کو چاہتا ہے کم کردیتا ہے،لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(34,37,'34:37','اور تمھارے مال اور تمھاری اولاد وہ چیز نہیں جو درجہ میں تم کو ہمارا مقرب بنادے، البتہ جوایمان لایا اور اس نے نیک عمل کیا، ایسے لوگوں کے لیے ان کے عمل کا دونا بدلہ ہے اور وہ بالا خانوں میں اطمینان سے رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(34,38,'34:38','اور جولوگ ہماری آیتوں کو نیچا دکھانے کے لیے سرگرم ہیں وہ عذاب میں داخل کيے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(34,39,'34:39','کہو کہ میرارب اپنے بندوں میں سے جس کو چاہتا ہے کشادہ روزی دیتا ہے اور جس کو چاہتا ہے تنگ کردیتا ہےاور جو چیز بھی تم خرچ کروگے تو وہ اس کا بدلہ دے گااور وہ بہتر رزق دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,40,'34:40','اور جس دن وہ ان سب کوجمع کرے گا پھر وہ فرشتوں سے پوچھے گا، کیا یہ لوگ تمھاری عبادت کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(34,41,'34:41','وہ کہیں گے پاک ہے تیری ذات، ہمارا تعلق تجھ سے ہے، نہ کہ ان لوگوں سے،بلکہ یہ جنوں کی عبادت کرتے تھے ان میں سے اکثر لوگ انھیں کے مومن تھے');
INSERT INTO "ur_wahid_uddin" VALUES(34,42,'34:42','پس آج تم ميں سے کوئی ایک دوسرے کو نہ فائدہ پہنچاسکتا ہے اور نہ نقصاناور ہم ظالموں سے کہیں گے کہ آگ کا عذاب چکھو جس کو تم جھٹلاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(34,43,'34:43','اور جب ان کو ہماری کھلی كھلي آیتیں سنائی جاتی ہیں تو وہ کہتے ہیں کہ یہ تو بس ایک شخص ہے جو چاہتا ہے کہ تم کو ان سے روک دے جن کی تمھارے باپ دادا عبادت کرتے تھےاور انھوں نے کہا، یہ تو محض ایک جھوٹ ہے گھڑا ہوا اور ان منکروں کے سامنے جب حق آیا تو انھوں نے کہا کہ یہ تو بس کھلا ہوا جادو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,44,'34:44','اور ہم نے ان کو کتابیں نہیں دی تھیں جن کو وہ پڑھتے ہوں اور ہم نے تم سے پہلے ان کے پاس کوئی ڈرانے والا نہیں بھیجا');
INSERT INTO "ur_wahid_uddin" VALUES(34,45,'34:45','اور ان سے پہلے والوں نے بھی جھٹلایا اور یہ اس کے دسویں حصہ کو بھی نہیں پہنچے جو ہم نے ان کو دیا تھاپس انھوں نے میرے رسولوں کو جھٹلایا ، تو کیسا تھا ان پر میرا عذاب');
INSERT INTO "ur_wahid_uddin" VALUES(34,46,'34:46','کہو، میں تم کو ایک بات کی نصیحت کرتا ہوں ، یہ کہ تم خداکے واسطے کھڑے ہوجاؤ، دو دو اور ایک ایک ، پھر سوچو تمھارے ساتھی کو جنون نہیں ہے، وہ تو بس ایک سخت عذاب سے پہلے تم کو ڈرانے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,47,'34:47','کہو کہ میں نے تم سے کچھ معاوضہ مانگا ہو تو وہ تمھارا ہی ہےمیرا معاوضہ تو بس اللہ کے اوپر ہے اور وہ ہر چیز پر گواہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,48,'34:48','کہو کہ میرا رب حق کو (باطل پر) مارے گا، وہ چھپی چیزوں کوجاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,49,'34:49','کہو کہ حق آگیا اور باطل نہ آغاز کرتا ہے اور نہ اعادہ');
INSERT INTO "ur_wahid_uddin" VALUES(34,50,'34:50','کہوکہ اگر میں گمراہی پر ہوں تو میری گمراہی کا وبال مجھ پر ہے اور اگر میں ہدایت پر ہوں تو یہ اس وحی کی بدولت ہے جو میرا رب میری طرف بھیج رہا ہےبے شک وہ سننے والا ہے، قریب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,51,'34:51','اور اگر تم دیکھو ، جب یہ گھبرائے ہوئے ہوں گے پس وہ بھاگ نہ سکیں گے اور قریب ہی سے پکڑ لیے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(34,52,'34:52','اور وہ کہیں گے کہ ہم اس پر ایمان لائے اور اتنی دور سے ان کے لیے اس کا پاناکہاں');
INSERT INTO "ur_wahid_uddin" VALUES(34,53,'34:53','اور اس سے پہلے انھوں نے اس کا انکار کیا اور بِن دیکھے دور جگہ سے باتیں پھینکتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(34,54,'34:54','اور ان کے اور ان کی آرزو میں آڑکردی جائے گی، جیسا کہ اس سے پہلے ان کے ہم مشربوں کے ساتھ کیا گیاوہ بڑے دھوکے والے شک میں پڑے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,1,'35:1','تعریف اللہ کے لیے ہے، آسمانوں اور زمین کا پیدا کرنے والا، فرشتوں کو پیغام رساں بنانے والا جن کے پر ہیں دو دو اور تین تین اور چار چاروہ پیدائش میں جو چاہے زیادہ کردیتا ہےبے شک اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,2,'35:2','اللہ جو رحمت لوگوں کے لیے کھولے تو کوئی اس کا روکنے والا نہیں اور جس کو وہ روک لے تو کوئی اس کو کھولنے والا نہیں اور وہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,3,'35:3','اے لوگو، اپنے اوپر اللہ کے احسان کو یاد کرو کیا اللہ کے سوا کوئی اور خالق ہے جو تم کو آسمان اورزمین سے رزق دیتا ہےاس کے سوا کوئی معبود نہیں تو تم کہاں سے دھوکا کھارہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(35,4,'35:4','اوراگریہ لوگ تم کو جھٹلائیں تو تم سے پہلے بھی بہت سے پیغمبر جھٹلائے جاچکے ہیں اور سارے امور اللہ ہی کی طرف رجوع ہونے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(35,5,'35:5','اے لوگو، بے شک اللہ کا وعدہ برحق ہے تو دنیا کی زندگی تمھیں دھوکے میں نہ ڈالے اور نہ وہ بڑا دھوکہ باز تم کو اللہ کے باب میں دھوکہ دینے پائے');
INSERT INTO "ur_wahid_uddin" VALUES(35,6,'35:6','بے شک شیطان تمھارا دشمن ہے تو تم اس کودشمن ہی سمجھو وہ تو اپنے گروہ کو اسی لیے بلاتا ہے کہ وہ دوزخ والوں میں سے ہوجائیں');
INSERT INTO "ur_wahid_uddin" VALUES(35,7,'35:7','جن لوگوں نے انکار کیا، ان کے لیے سخت عذاب ہےاور جوایمان لائے اور نیک عمل کیا، ان کے لیے معافی ہے اور بڑا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,8,'35:8','کیا ایسا شخص جس کو اس کا برا عمل اچھا کرکے دکھایا گیا، پھر وہ اس کواچھا سمجھنے لگا، پس اللہ جس کو چاہتا ہے بھٹکادیتا ہے اور جس کو چاہتا ہے ہدایت دیتا ہے پس ان پر افسوس کرکے تم اپنے کو ہلکان نہ کرو اللہ کو معلوم ہے جو کچھ وہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(35,9,'35:9','اور اللہ ہی ہے جو ہواؤں کو بھیجتا ہےپھر وہ بادل کو اٹھاتی ہیں پھر ہم اس کوایک مردہ دیس کی طرف لے جاتے ہیں پس ہم نے اس سے زمین کو اس کے مردہ ہوجانے کے بعد پھر زندہ کردیااسی طرح ہوگا دوبارہ جی اٹھنا');
INSERT INTO "ur_wahid_uddin" VALUES(35,10,'35:10','جو شخص عزت چاہتا ہو تو عزت تمام تر اللہ کے لیے ہےاسي کی طرف پاکیزہ کلام چڑھتا ہے اور عملِ صالح اس کو اوپر اٹھاتا ہے اور جولوگ بری تدبیریں کررہے ہیں ان کے لیے سخت عذاب ہے اور ان کی تدبیریں نابود ہوکر رہیں گی');
INSERT INTO "ur_wahid_uddin" VALUES(35,11,'35:11','اوراللہ نے تم کو مٹی سے پیدا کیاپھر پانی کی بوندسے، پھر تم کو جوڑے جوڑے بنایااور کوئی عورت نہ حاملہ ہوتی ہے اور نہ جنتی ہے مگر اس کے علم سے اور نہ کوئی عمر والا بڑی عمر پاتا ہے اور نہ کسی کی عمر گھٹتی ہے مگر وہ ایک کتاب میں درج ہےبے شک یہ اللہ پر آسان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,12,'35:12','اور دونوں دریایکساں نہیں یہ میٹھا ہے، پیاس بجھانے والا،پینے کے لیے خوش گواراور یہ کھاری کڑوا ہے اور تم دونوں سے تازہ گوشت کھاتے ہو اور زینت کی چیز نکالتے ہوجس کو تم پہنتے ہواور تم دیکھتے ہو جہازوں کو کہ وہ اس میں پھاڑتے ہوئے چلتے ہیں تاکہ تم اس کا فضل تلاش کرو اور تاکہ تم شکر ادا کرو');
INSERT INTO "ur_wahid_uddin" VALUES(35,13,'35:13','وہ داخل کرتا ہے رات کو دن میں اور وہ داخل کرتا ہے دن کو رات میں اور اس نے سورج اور چاند کو مسخر کردیا ہے ہر ایک چلتا ہے ایک مقرر وقت کے لیے یہ اللہ ہی تمھارا رب ہے، اسی کے لیے بادشاہی ہے اور اس کے سوا تم جنھیں پکارتے ہو وہ کھجور کی گٹھلی کے ایک چھلکے کے بھی مالک نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(35,14,'35:14','اگر تم ان کوپکارو تو وہ تمھاری پکار نہیں سنیں گے اور اگر وہ سنیں تو وہ تمھاری فریاد رسی نہیں کرسکتے اور وہ قیامت کے دن تمھارے شرک کاانکار کریں گے اور ایک باخبر کی طرح کوئی تم کو نہیں بتاسکتا');
INSERT INTO "ur_wahid_uddin" VALUES(35,15,'35:15','اے لوگو، تم اللہ کے محتاج ہو اور اللہ تو بے نیاز ہے، تعریف والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,16,'35:16','اگروہ چاہے تو تم کو لے جائے اور ایک نئی مخلوق لے آئے');
INSERT INTO "ur_wahid_uddin" VALUES(35,17,'35:17','اور یہ اللہ کے لیے کچھ مشکل نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(35,18,'35:18','اور کوئی اٹھانے والا دوسرے کا بوجھ نہ اٹھائے گااور اگر کوئی بھاری بوجھ والا اپنا بوجھ اٹھانے کے لیے پکارے تو اس میں سے ذرا بھی نہ اٹھایاجائے گا، اگرچہ و ہ قرابت والا کیوں نہ ہو تم تو صرف انھیں لوگوں کو ڈراسکتے ہو جو بن دیکھے اپنے رب سے ڈرتے ہیں اور نماز قائم کرتے ہیں اور جوشخص پاک ہوتا ہے وہ اپنے لیے پاک ہوتا ہے اور اللہ ہی کی طرف لوٹ کرجانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,19,'35:19','اور اندھا اور آنکھوں والا برابر نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(35,20,'35:20','اور نہ اندھیرا اور نہ اجالا');
INSERT INTO "ur_wahid_uddin" VALUES(35,21,'35:21','اور نہ سایہ اور نہ دھوپ');
INSERT INTO "ur_wahid_uddin" VALUES(35,22,'35:22','اور زندہ اور مردہ برابر نہیں ہوسکتےبے شک اللہ سناتا ہے جس کو وہ چاہتا ہے اور تم ان کو سنانے والے نہیں بن سکتے جو قبروں میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(35,23,'35:23','تم تو بس ایک خبردار کرنے والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(35,24,'35:24','ہم نے تم کو حق کے ساتھ بھیجا ہےخوش خبری دینے والا اور ڈرانے والا بناکراور کوئی امت ایسی نہیں جس میں کوئی ڈرانے والا نہ آیا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(35,25,'35:25','اور اگر یہ لوگ تم کو جھٹلاتے ہیں تو ان سے پہلے جو لوگ ہوئے ہیں ، انھوں نے بھی جھٹلایا ان کے پاس ان کے پیغمبر کھلے ہوئے دلائل اور صحیفے اور روشن کتاب لے کر آئے');
INSERT INTO "ur_wahid_uddin" VALUES(35,26,'35:26','پھر جن لوگوں نے نہ مانا ان کو میں نے پکڑلیا، تو دیکھو کہ کیسا ہوا ان کے اوپر میراعذاب');
INSERT INTO "ur_wahid_uddin" VALUES(35,27,'35:27','کیا تم نہیں دیکھتے کہ اللہ نے آسمان سے پانی اتارا پھر ہم نے اس سے مختلف رنگوں کے پھل پیدا کردئے اور پہاڑوں میں بھی سفید اورسرخ، مختلف رنگوں کے ٹکڑے ہیں اور گہرے سیاہ بھی');
INSERT INTO "ur_wahid_uddin" VALUES(35,28,'35:28','اور اسی طرح انسانوں اور جانوروں اور چوپایوں میں بھی مختلف رنگ کے ہیں اللہ سے اس کے بندوں میں سے صرف وہی لوگ ڈرتے ہیں جو علم والے ہیں بے شک اللہ زبردست ہے، بخشنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,29,'35:29','جو لوگ اللہ کی کتاب پڑھتے ہیں اور نماز قائم کرتے ہیں اور جو کچھ ہم نے ان کو عطاکیا ہے اس میں سے چھپے اور کھلے خرچ کرتے ہیں ، وہ ایسی تجارت کے امیدوار ہیں جو کبھی ماند نہ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(35,30,'35:30','تاکہ اللہ ان کو ان کا پورا اجر دےاور ان کے لیے اپنے فضل سے اور زیادہ کردے بے شک وہ بخشنے والا ہے، قدر داں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,31,'35:31','اور ہم نے تمھاری طرف جو کتاب وحی کی ہے وہ حق ہے، اس کی تصدیق کرنے والی ہے جو اس کے پہلے سے موجود ہےبے شک اللہ اپنے بندوں کی خبر رکھنے والا ہے، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,32,'35:32','پھر ہم نے کتاب کا وارث بنایا ان لوگوں کو جن کو ہم نے اپنے بندوں میں سے چن لیاپس ان میں سے کچھ اپنی جانوں پر ظلم کرنے والے ہیں اور ان میں سے کچھ بیچ کی چال پر ہیں اور ان میں سے کچھ اللہ کی توفیق سے بھلائیوں میں سبقت کرنے والے ہیں یہی سب سے بڑا فضل ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,33,'35:33','ہمیشہ رہنے والے باغ ہیں جن میں یہ لوگ داخل ہوں گے، وہاں ان کوسونے کے کنگن اور موتی پہنائے جائیں گے، اور وہاں ان کا لباس ریشم ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(35,34,'35:34','اور وہ کہیں گے، شکر ہے اللہ کاجس نے ہم سے غم کو دور کیا بے شک ہمارا رب معاف کرنے والا، قدر کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,35,'35:35','جس نے ہم کو اپنے فضل سے آباد رہنے کے گھر میں اُتارا ،اس میں ہم کو نہ کوئی مشقت پہنچے گی اور نہ کبھی تکان لاحق ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(35,36,'35:36','اور جنھوں نے انکار کیا ان کے لیے جہنم کی آگ ہے، نہ ان کی قضا آئے گی کہ وہ مرجائیں اور نہ دوزخ کا عذاب ہی ان سے ہلکا کیا جائے گا ہم ہر منکر کو ایسی ہی سزا دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(35,37,'35:37','اور وہ لوگ اس میں چلائیں گے اے ہمارے رب، ہم کو نکال لے ہم نیک عمل کریں گے ،اس سے مختلف جو ہم کیا کرتے تھے کیا ہم نے تم کو اتنی عمر نہ دی کہ جس کو سمجھنا ہوتا وہ سمجھ سکتااور تمھارے پاس ڈرانے والا آیا اب چکھو کہ ظالموں کا کوئی مدد گار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(35,38,'35:38','اللہ آسمانوں اور زمین کے غیب کو جاننے والا ہے بے شک وہ دل کی باتوں سے بھی باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,39,'35:39','وہی ہے جس نے تم کو زمین میں آباد کیا تو جوشخص انکار کرے گا اس کا انکار اسی پرپڑے گااور منکروں کے لیے ان کاانکار، ان کے رب کے نزدیک،ناراضی ہی کے بڑھنے کاباعث ہوتا ہےاور منکروں کے لیے ان کاانکار اُن کے خسارہ ہی میں اضافہ کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(35,40,'35:40','کہو، ذرا تم دیکھو اپنے ان شریکوں کو جن کو تم خدا کے سوا پکارتے ہو، مجھ کودکھاؤ کہ انھوں نے زمین میں سے کیا بنایا ہے یا ان کی آسمانوں میں کوئی حصہ داری ہےیا ہم نے ان کو کوئی کتاب دی ہے تو وہ اس کی کسی دلیل پر ہیں ، بلکہ یہ ظالم ایک دوسرے سے صرف دھوکہ کی باتوں کاوعدہ کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(35,41,'35:41','بے شک اللہ ہی آسمانوں اور زمینوں کو تھامے ہوئے ہے کہ وہ ٹل نہ جائیں اور اگر وہ ٹل جائیں تو اس کے سوا کوئی اور ان کو تھام نہیں سکتابے شک وہ تحمل والا ہے، بخشنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,42,'35:42','اور انھوں نے اللہ کی تاکیدی قسمیں کھائی تھیں کہ اگر ان کے پاس کوئی ڈرانے والا آیا تو وہ ہر ایک امت سے زیادہ ہدایت قبول کرنے والے ہوں گےپھر جب ان کے پاس ایک ڈرانے والاآیا تو صرف ان کی بیزاری ہی کو ترقی ہوئی');
INSERT INTO "ur_wahid_uddin" VALUES(35,43,'35:43','زمین میں اپنے کو بڑا سمجھنے كي وجه سے،اور ان كي بري تدبيروں كو اور بری تدبیروں کاوبال تو بری تدبیر کرنے والوں ہی پر پڑتا ہے تو کیا یہ اسی دستور کے منتظر ہیں جو اگلے لوگوں کے باب میں ظاہر ہوا پس تم خدا کے دستور میں نہ کوئی تبدیلی پاؤگے اور نہ خدا کے دستور کوٹلتا ہوا پاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(35,44,'35:44','کیا یہ لوگ زمین میں چلے پھرے نہیں کہ وہ دیکھتے کہ کیسا ہواانجام ان لوگوں کا جو ان سے پہلے گزرے ہیں ، اور وہ قوت میں ان سے بڑھے ہوئے تھے اور خدا ایسا نہیں کہ کوئی چیز اس کو عاجز کردے، نہ آسمانوں میں اور نہ زمین میں بے شک وہ علم والا ہے، قدرت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(35,45,'35:45','اور اگر لوگوں کے اعمال پر اللہ ان کو پکڑتا تو زمین پر وہ ایک جاندار کو بھی نہ چھوڑتا، لیکن وہ ان کو ایک مقرر مدت تک مہلت دیتا ہےپھر جب ان کی مدت پوری ہوجائے گی تو اللہ اپنے بندوں کو خود دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,1,'36:1','یٰسٓ');
INSERT INTO "ur_wahid_uddin" VALUES(36,2,'36:2','قسم ہے با حکمت قرآن کی');
INSERT INTO "ur_wahid_uddin" VALUES(36,3,'36:3','بے شک تم رسولوں میں سے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(36,4,'36:4','نہایت سیدھے راستے پر');
INSERT INTO "ur_wahid_uddin" VALUES(36,5,'36:5','یہ خدائے عزیز اور رحیم کی طرف سے اتارا گیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,6,'36:6','تاکہ تم ان لوگوں کو ڈرا دو جن کے اگلوں کو نہیں ڈرایاگیاپس وہ بے خبر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,7,'36:7','ان میں سے اکثر لوگوں پر بات ثابت ہوچکی ہے تو وہ ایمان نہیں لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,8,'36:8','ہم نے ان کی گردنوں میں طوق ڈال دئے ہیں سو وہ ٹھوڑیوں تک ہیں ، پس ان کے سراونچے ہورہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,9,'36:9','اور ہم نے ایک آڑ ان کے سامنے کردی ہے اور ایک آڑ ان کے پیچھے کردی ہےپھر ہم نے ان کو ڈھانک دیا تو ان کودکھائی نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(36,10,'36:10','اور ان کے لیے یکساں ہے، تم ان کو ڈراؤیا نہ ڈراؤ،وہ ایمان نہیں لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,11,'36:11','تم تو صرف اس شخص کو ڈراسکتے ہو جو نصیحت پر چلے اور خدا سے ڈرے بن دیکھے تو ایسے شخص کومعافی کی اور باعزت ثو اب کی بشارت دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(36,12,'36:12','یقیناً ہم مُردوں کو زندہ کریں گے اور ہم لکھ رہے ہیں جو انھوں نے آگے بھیجا اور جوانھوں نے پیچھے چھوڑااور ہر چیز ہم نے درج کرلی ہے ایک کھلی ہوئی کتاب میں');
INSERT INTO "ur_wahid_uddin" VALUES(36,13,'36:13','اور ان کو بستی والوں کی مثال سناؤ، جب کہ اس میں رسول آئے');
INSERT INTO "ur_wahid_uddin" VALUES(36,14,'36:14','جب کہ ہم نے ان کے پاس دو رسول بھیجے تو انھوں نے دونوں کو جھٹلایا، پھر ہم نے تیسرے سے ان کی تائید کی، انھوں نے کہا کہ ہم تمھارے پاس بھیجے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,15,'36:15','لوگوں نے کہا کہ تم تو ہمارے ہی جیسے بشر ہو اور رحمان نے کوئی چیز نہیں اتاری ہے، تم محض جھوٹ بولتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(36,16,'36:16','انھوں نے کہا کہ ہمارا رب جانتا ہے کہ ہم بے شک تمھارے پاس بھیجے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,17,'36:17','اور ہمارے ذمہ تو صرف واضح طور پر پہنچادینا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,18,'36:18','لوگوں نے کہا کہ ہم تو تم کو منحوس سمجھتے ہیں ، اگر تم لوگ باز نہ آئے تو ہم تم کو سنگسار کریں گے اور تم کو ہماری طرف سے سخت تکلیف پہنچے گی');
INSERT INTO "ur_wahid_uddin" VALUES(36,19,'36:19','انھوں نے کہا کہ تمھاری نحوست تمھارے ساتھ ہے، کیا اتنی بات پر کہ تم کو نصیحت کی گئیبلکہ تم حد سے نکل جانے والے لوگ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(36,20,'36:20','اور شہر کے دور مقام سے ایک شخص دوڑتاہوا آیااس نے کہا، اے میری قوم، رسولوں کی پیروی کرو');
INSERT INTO "ur_wahid_uddin" VALUES(36,21,'36:21','ان لوگوں کی پیروی کرو جو تم سے کوئی بدلہ نہیں مانگتے اور وہ ٹھیک راستے پر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,22,'36:22','اور میں کیوں نہ عبادت کروں اس ذات کی جس نے مجھ کو پیدا کیا اور اسی کی طرف تم لوٹائے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(36,23,'36:23','کیا میں اس کے سوا دوسروں کومعبود بناؤں اگر رحمٰن مجھ کو کوئی تکلیف پہنچاناچاہے تو ان کی سفارش میرے کچھ کام نہ آئے گی اور نہ وہ مجھ کو چھڑا سکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,24,'36:24','بے شک اس وقت میں ایک کھلی ہوئی گمراہی میں ہوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(36,25,'36:25','میں تمھارے رب پر ایمان لایا تو تم بھی میری بات سن لو');
INSERT INTO "ur_wahid_uddin" VALUES(36,26,'36:26','ارشاد ہوا کہ جنت میں داخل ہوجاؤاس نے کہا کاش، میری قوم جانتی');
INSERT INTO "ur_wahid_uddin" VALUES(36,27,'36:27','کہ میرے رب نے مجھ کو بخش دیا اور مجھ کو عزت والوں میں شامل کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(36,28,'36:28','اور اس کے بعد اس کی قوم پر ہم نے آسمان سے کوئی فوج نہیں اتاری ، اور ہم فوج نہیں اتارا کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(36,29,'36:29','بس ایک دھماکہ ہو اتو یکایک وہ سب بجھ کر رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(36,30,'36:30','افسوس ہے بندوں کے اوپر، جورسول بھی ان کے پاس آیا وہ اس کامذاق ہی اڑاتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,31,'36:31','کیا انھوں نے نہیں دیکھا کہ ہم نے ان سے پہلے کتنی ہی قومیں ہلاک کردیں اب وہ ان کی طرف واپس آنے والی نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,32,'36:32','اور ان میں کوئی ایسا نہیں جواكٹھا ہوکر ہمارے پاس حاضر نہ کیا جائے');
INSERT INTO "ur_wahid_uddin" VALUES(36,33,'36:33','اور ایک نشانی ان کے لیے مردہ زمین ہےاس کو ہم نے زندہ کیا اور اس سے ہم نے غلہ نکالا پس وہ اس میں سے کھاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,34,'36:34','اور اس میں ہم نے کھجور کے اور انگور کے باغ بنائے اور اس میں ہم نے چشمے جاری کيے');
INSERT INTO "ur_wahid_uddin" VALUES(36,35,'36:35','تاکہ لوگ اس کے پھل کھائیں اور اس کو ان کے ہاتھوں نے نہیں بنایاتو کیا وہ شکر نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(36,36,'36:36','پاک ہے وہ ذات جس نے سب چیز کے جوڑے بنائے ان میں سے بھی جن کو زمین اگاتی ہے اورخود ان کے اندر سے بھی اور ان میں سے بھی جن کو وہ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(36,37,'36:37','اور ایک نشانی ان کے لیے رات ہے، ہم اس سے دن کو کھینچ لیتے ہیں تو وہ اندھیرے میں رہ جاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,38,'36:38','اور سورج، وہ اپنی ٹھہرائی ہوئی راہ پرچلتا رہتا ہے یہ عزیز اورعلیم کاباندھا ہوا اندازہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,39,'36:39','اور چاند کے لیے ہم نے منزلیں مقررکردیں ، یہاں تک کہ وہ ایسا رہ جاتا ہے جیسے کھجور کی پرانی شاخ');
INSERT INTO "ur_wahid_uddin" VALUES(36,40,'36:40','نہ سورج کے بس میں ہے کہ وہ چاند کو پکڑلے اور نہ رات، دن سے پہلے آسکتی ہے اور سب ایک ایک دائرہ میں تیر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,41,'36:41','اور ایک نشانی ان کے لیے یہ ہے کہ ہم نے ان کی نسل کو بھری ہوئی کشتی میں سوار کیا');
INSERT INTO "ur_wahid_uddin" VALUES(36,42,'36:42','اور ہم نے ان کے لیے اسی کے مانند اورچیزیں پیداکیں جن پر وہ سوار ہوتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,43,'36:43','اور اگر ہم چاہیں تو ان کوغرق کردیں ، پھر نہ کوئی ان کی فریاد سننے والا ہو اور نہ وہ بچائے جاسکیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,44,'36:44','مگر یہ ہماری رحمت ہے اور ان کو ایک وقتِ معین تک فائدہ دینا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,45,'36:45','اور جب ان سے کہا جاتا ہے کہ اس سے ڈرو جو تمھارے آگے ہے اور جو تمھارے پیچھے ہے تاکہ تم پر رحم کیاجائے');
INSERT INTO "ur_wahid_uddin" VALUES(36,46,'36:46','اور ان کے رب کی نشانیوں میں سے کوئی نشانی بھی ان کے پاس ایسی نہیں آتی جس سے وہ اعراض نہ کرتے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(36,47,'36:47','اور جب ان سے کہا جاتا ہے کہ اللہ نے جوکچھ تم کو دیا ہے، اس میں سے خرچ کرو تو جن لوگوں نے انکار کیا وہ ایمان لانے والوں سے کہتے ہیں کہ ہم ایسے لوگوں کو کھلائیں جن کو اللہ چاہتا تو وہ ان کو کھلادیتا تم لوگ تو کھلی ہوئی گمراہی میں ہو');
INSERT INTO "ur_wahid_uddin" VALUES(36,48,'36:48','اور وہ کہتے ہیں کہ یہ وعدہ کب ہوگا اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(36,49,'36:49','یہ لوگ بس ایک چنگھاڑ کی راہ دیکھ رہے ہیں جو ان کو آپکڑے گی اور وہ جھگڑتے ہی رہ جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,50,'36:50','پھر وہ نہ کوئی وصیت کرپائیں گے اور نہ اپنے لوگوں کی طرف لوٹ سکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,51,'36:51','اور صور پھونکا جائے گا تو یکایک وہ قبروں سے اپنے رب کی طرف چل پڑیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,52,'36:52','وہ کہیں گے، ہائے ہماری بدبختی، ہماری قبر سے کس نے ہم کو اٹھایا— یہ وہی ہے جس کارحمن نے وعدہ کیا تھا اور پیغمبروں نے سچ کہا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(36,53,'36:53','بس وہ ایک چنگھاڑ ہوگی، پھر یکایک سب جمع ہوکر ہمارے پاس حاضر کردئے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,54,'36:54','پس آج کے دن کسی شخص پر کوئی ظلم نہ ہوگا، اور تم کو وہی بدلہ ملے گا جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(36,55,'36:55','بے شک جنت کے لوگ آج اپنے مشغلوں میں خوش ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,56,'36:56','وہ اور ان کی بیویاں ، سایوں میں مسہریوں پر تکیہ لگائے ہوئے بیٹھے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,57,'36:57','ان کے لیے وہاں میوے ہوں گے اور ان کے لیے وہ سب کچھ ہوگا جو وہ مانگیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,58,'36:58','ان کو سلام کہلایاجائے گا مہربان رب کی طرف سے');
INSERT INTO "ur_wahid_uddin" VALUES(36,59,'36:59','اور اے مجرمو، آج تم الگ ہوجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(36,60,'36:60','اے اولاد آدم، کیا میں نے تم کو تاکید نہیں کردی تھی کہ تم شیطان کی عبادت نہ کرنابے شک وہ تمھارا کھلا ہوا دشمن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,61,'36:61','اور یہ کہ تم میری ہی عبادت کرنا، یہی سیدھا راستہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,62,'36:62','اور اس نے تم میں سے ایک کثیر گروہ کو گمراہ کردیاتو کیاتم سمجھتے نہیں تھے');
INSERT INTO "ur_wahid_uddin" VALUES(36,63,'36:63','یہ ہے جہنم، جس کا تم سے وعدہ کیا جاتا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(36,64,'36:64','اب اپنے کفر کے بدلے میں اس میں داخل ہوجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(36,65,'36:65','آج ہم ان کے منہ پرمہر لگادیں گے اور ان کے ہاتھ ہم سے بولیں گے اور ان کے پاؤں گواہی دیں گے جوکچھ یہ لوگ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(36,66,'36:66','اور اگر ہم چاہتے تو ان کی آنکھوں کو مٹادیتے پھر وہ راستہ کی طرف دوڑتے تو ان کو کہاں نظر آتا');
INSERT INTO "ur_wahid_uddin" VALUES(36,67,'36:67','اور اگر ہم چاہتے تو ان کی جگہ ہی پر ان کی صورتیں بدل دیتے تو وہ نہ آگے بڑھ سکتے اور نہ پیچھے لوٹ سکتے');
INSERT INTO "ur_wahid_uddin" VALUES(36,68,'36:68','اور ہم جس کی عمر زیادہ کردیتے ہیں ، تو اس کو اس کی پیدائش میں پیچھے لوٹا دیتے ہیں ، تو کیا وہ سمجھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,69,'36:69','اور ہم نے اس کو شعر نہیں سکھایا اور نہ یہ اس کے لائق ہے یہ تو صرف ایک نصیحت ہے اور واضح قرآن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,70,'36:70','تاکہ وہ اس شخص کو خبردار کردے جو زندہ ہو اور انکار کرنے والوں پر حجت قائم ہوجائے');
INSERT INTO "ur_wahid_uddin" VALUES(36,71,'36:71','کیا انھوں نے نہیں دیکھا کہ ہم نے اپنے ہاتھ کی بنائی ہوئی چیزوں میں سے ان کے لیے مویشی پیدا کيے، تو وہ ان کے مالک ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,72,'36:72','اور ہم نے ان کو ان کاتابع بنادیا، تو ان میں سے کوئی ان کی سواری ہے اور کسی کو وہ کھاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,73,'36:73','اور ان کے لیے ان میں فائدے ہیں اور پینے کی چیزیں بھی، تو کیا وہ شکر نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(36,74,'36:74','اور انھوں نے اللہ کے سوا دوسرے معبود بنائے کہ شاید ان کی مدد کی جائے');
INSERT INTO "ur_wahid_uddin" VALUES(36,75,'36:75','وہ ان کی مدد نہ کرسکیں گے، اور وہ ان کی فوج ہوکر حاضر کيے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(36,76,'36:76','تو ان کی بات تم کو غمگین نہ کرے، ہم جانتے ہیں جو کچھ وہ چھپاتے ہیں اور جو کچھ وہ ظاہر کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(36,77,'36:77','کیا انسان نے نہیں دیکھا کہ ہم نے اس کو ایک بوند سے پیدا کیا، پھر وہ صریح جھگڑالو بن گیا');
INSERT INTO "ur_wahid_uddin" VALUES(36,78,'36:78','اور وہ ہم پر مثال چسپاں کرتا ہے اور وہ اپنی پیدائش کو بھول گیا وہ کہتا ہے کہ ہڈیوں کو کون زندہ کرے گا جب کہ وہ بوسیدہ ہوگئی ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(36,79,'36:79','کہو، ان کو وہی زندہ کرے گا جس نے ان کو پہلی مرتبہ پیدا کیا اور وہ سب طرح پیدا کرنا جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,80,'36:80','وہی ہے جس نے تمھارے لیے ہرے بھرے درخت سے آگ پیدا کردیپھر تم اس سے آگ جلاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(36,81,'36:81','کیا جس نے آسمانوں اور زمین کو پیدا کیا، وہ اس پر قادر نہیں کہ ان جیسوں کو پیدا کردےہاں وہ قادر ہےاوروہی ہے اصل پیدا کرنے والا،جاننے والا');
INSERT INTO "ur_wahid_uddin" VALUES(36,82,'36:82','اس کا معاملہ تو بس یہ ہے کہ جب وہ کسی چیز کاارادہ کرتا ہے تو کہتا ہے کہ ہوجا تو وہ ہوجاتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(36,83,'36:83','پس، پاک ہے وہ ذات جس کے ہاتھ میں ہر چیز کا اختیار ہے اور اسی کی طرف تم لوٹائے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(37,1,'37:1','قسم ہے قطار درقطار صف باندھنے والے فرشتوں کی');
INSERT INTO "ur_wahid_uddin" VALUES(37,2,'37:2','پھر ڈانٹنے والوں کی جھڑک کر');
INSERT INTO "ur_wahid_uddin" VALUES(37,3,'37:3','پھر ان کی جو نصیحت سنانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,4,'37:4','کہ تمھارا معبود ایک ہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,5,'37:5','آسمانوں اور زمین کا رب اور جو کچھ ان کے درمیان ہے اور سارے مشرقوں کا رب');
INSERT INTO "ur_wahid_uddin" VALUES(37,6,'37:6','ہم نے آسمانِ دنیا کو ستاروں کی زینت سے سجایا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,7,'37:7','اور ہر شیطانِ سرکش سے اس کو محفوظ کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,8,'37:8','وہ ملاءِ اعلیٰ کی طرف کان نہیں لگاسکتے اور وہ ہر طرف سے مارے جاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,9,'37:9','بھگانے کے لیے ، اور ان کے لیے ایک دائمی عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,10,'37:10','مگر جو شیطان کوئی بات اچک لے تو ایک دہکتا ہوا شعلہ اس کا پیچھا کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,11,'37:11','پس ان سے پوچھو کہ ان کی پیدائش زیادہ مشکل ہے یا ان چیزوں کی جو ہم نے پیداکی ہیں ہم نے ان کو چپکتی ہوئی مٹی سے پیدا کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,12,'37:12','بلکہ تم تعجب کرتے ہو اور وہ مذاق اُڑارہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,13,'37:13','اور جب ان کو سمجھایا جاتا ہے تو وہ سمجھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,14,'37:14','اور جب وہ کوئی نشانی دیکھتے ہیں تو وہ اس کو ہنسی میں ڈال دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,15,'37:15','اور کہتے ہیں کہ یہ تو بس کھلا ہوا جادو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,16,'37:16','کیا جب ہم مرجائیں گے اور مٹی اور ہڈیاں بن جائیں گے تو پھر ہم اُٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,17,'37:17','اور کیا ہمارے اگلے باپ دادا بھی');
INSERT INTO "ur_wahid_uddin" VALUES(37,18,'37:18','کہو کہ ہاں ، اور تم ذلیل بھی ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(37,19,'37:19','پس وہ تو ایک جھڑکی ہوگی، پھر اسی وقت وہ دیکھنے لگیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,20,'37:20','اور وہ کہیں گے کہ ہائے ہماری کم بختی، یہ تو جزاء کا دن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,21,'37:21','یہ وہی فیصلہ کا دن ہے جس کو تم جھٹلاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,22,'37:22','جمع کرو ان کو جنھوں نے ظلم کیااور ان کے ساتھیوں کو اور ان معبودوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(37,23,'37:23','جن کی وہ اللہ کے سوا عبادت کرتے تھے؛ پھر ان سب کودوزخ کاراستہ دکھاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(37,24,'37:24','اور ان کوٹھہراؤ، ان سے کچھ پوچھنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,25,'37:25','تم کو کیا ہوا کہ تم ایک دوسرے کی مدد نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(37,26,'37:26','بلکہ آج تو وہ فرماں بردار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,27,'37:27','اور وہ ایک دوسرے کی طرف متوجہ ہوکر سوال وجواب کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,28,'37:28','کہیں گے، تم ہمارے پاس دائیں طرف سے آتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,29,'37:29','وہ جواب دیں گے، بلکہ تم خود ایمان لانے والے نہیں تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,30,'37:30','اور ہمارا تمھارے اوپر کوئی زور نہ تھا، بلکہ تم خود ہی سرکش لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,31,'37:31','پس ہم سب پر ہمارے رب کی بات پوری ہوکر رہی ، ہم کو اس کا مزہ چکھنا ہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,32,'37:32','ہم نے تم کو گمراہ کیا، ہم خود بھی گمراہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,33,'37:33','پس وہ سب اس دن عذاب میں مشترک ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,34,'37:34','ہم مجرموں کے ساتھ ایسا ہی کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,35,'37:35','یہ وہ لوگ تھے کہ جب ان سے کہا جاتا کہ اللہ کے سوا کوئی معبود نہیں تو وہ تکبر کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,36,'37:36','اور وہ کہتے تھے کہ کیا ہم ایک شاعر دیوانہ کے کہنے سے اپنے معبودوں کو چھوڑدیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,37,'37:37','بلکہ وہ حق لے کر آیا ہے اور وہ رسولوں کی پیشین گوئیوں کا مصداق ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,38,'37:38','بے شک تم کو دردناک عذاب چکھنا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(37,39,'37:39','او ر تم کو اسی کا بدلہ دیا جارہا ہے جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,40,'37:40','مگر جواللہ کے چنے ہوئے بندے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,41,'37:41','ان کے لیے معلوم رزق ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(37,42,'37:42','میوے، اور وہ نہایت عزت سے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,43,'37:43','آرام کے باغوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(37,44,'37:44','تختوں پر آمنے سامنے بیٹھے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,45,'37:45','ان کے پاس ایسا پیالہ لایا جائے گا جو بہتی ہوئی شراب سے بھرا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(37,46,'37:46','صاف شفاف پینے والوں کے لیے لذت');
INSERT INTO "ur_wahid_uddin" VALUES(37,47,'37:47','نہ اس میں کوئی ضرر ہوگا اور نہ اس سے عقل خراب ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(37,48,'37:48','اور ان کے پاس نیچی نگاہ والی، بڑی آنکھوں والی عورتیں ہوں گی');
INSERT INTO "ur_wahid_uddin" VALUES(37,49,'37:49','گویا کہ وہ انڈے ہیں جو چھپے ہوئے رکھے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(37,50,'37:50','پھروہ ایک دوسرے کی طرف متوجہ ہوکر بات کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,51,'37:51','ان میں سے ایک کہنے والا کہے گا کہ میرا ایک ملاقاتی تھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,52,'37:52','وہ کہا کرتا تھا کہ کیا تم بھی تصدیق کرنے والوں میں سے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(37,53,'37:53','کیا جب ہم مرجائیں گے اور مٹی اور ہڈیاں ہوجائیں گے تو کیا ہم کو جزاملے گی');
INSERT INTO "ur_wahid_uddin" VALUES(37,54,'37:54','کہے گا، کیا تم جھانک کردیکھو گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,55,'37:55','تو وہ جھانکے گا اور وہ اس کو جہنم کے بیچ میں دیکھے گا');
INSERT INTO "ur_wahid_uddin" VALUES(37,56,'37:56','کہے گا کہ خدا کی قسم، تم تو مجھ کوتباہ کردینے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,57,'37:57','اور اگر میرے رب کافضل نہ ہوتا تو میں بھی انھیں لوگوں میں ہوتا جوپکڑے ہوئے آئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,58,'37:58','کیا اب ہم کو مرنا نہیں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,59,'37:59','مگر پہلی بار جو ہم مرچکے اور اب ہم کو عذاب نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(37,60,'37:60','بے شک یہی بڑی کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,61,'37:61','ایسی ہی کامیابی کے لیے عمل کرنے والوں کو عمل کرناچاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(37,62,'37:62','یہ ضیافت اچھی ہے، یا زقوم کادرخت');
INSERT INTO "ur_wahid_uddin" VALUES(37,63,'37:63','ہم نے اس کو ظالموں کے لیے فتنہ بنایا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,64,'37:64','وہ ایک درخت ہے جو دوزخ کی تہ سے نکلتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,65,'37:65','اس کاخوشہ ایسا ہے جیسے شیطان کاسر');
INSERT INTO "ur_wahid_uddin" VALUES(37,66,'37:66','تو وہ لوگ اس سے کھائیں گے پھر اسی سے پیٹ بھریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,67,'37:67','پھر ان کو کھولتا ہوا پانی ملاکر دیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(37,68,'37:68','پھر ان کی واپسی دوزخ ہی کی طرف ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(37,69,'37:69','انھوں نے اپنے باپ دادا کو گمراہی میں پایا');
INSERT INTO "ur_wahid_uddin" VALUES(37,70,'37:70','پھر وہ بھی انھیں کے قدم بقدم دوڑتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,71,'37:71','اور ان سے پہلے بھی اگلے لوگوں میں اکثر گمراہ ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(37,72,'37:72','اور ہم نے ان میں بھی ڈرانے والے بھیجے');
INSERT INTO "ur_wahid_uddin" VALUES(37,73,'37:73','تو دیکھو، ان لوگوں کاانجام کیا ہوا جن کو ڈرایا گیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,74,'37:74','مگر وہ جو اللہ کے چنے ہوئے بندے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,75,'37:75','اور ہم کو نوح نے پکارا تو ہم کیا خوب پکار سننے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,76,'37:76','اور ہم نے اس کو اور اس کے لوگوں کو بہت بڑے غم سے بچالیا');
INSERT INTO "ur_wahid_uddin" VALUES(37,77,'37:77','اور ہم نے اس کی نسل کو باقی رہنے والا بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(37,78,'37:78','اور ہم نے اس کے طریقہ پر پچھلوں میں ایک گروہ کو چھوڑا');
INSERT INTO "ur_wahid_uddin" VALUES(37,79,'37:79','سلام ہے نوح پر تمام دنیا والوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(37,80,'37:80','ہم نیکی کرنے والوں کو ایسا ہی بدلہ دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,81,'37:81','بے شک وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,82,'37:82','پھر ہم نے دوسروں کوغرق کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(37,83,'37:83','اور اسی کے طریقہ والوں میں سے ابراہیم بھی تھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,84,'37:84','جب کہ وہ آیا اپنے رب کے پاس قلبِ سلیم کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(37,85,'37:85','جب اس نے اپنے باپ سے اور اپنی قوم سے کہا کہ تم کس چیز کی عبادت کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(37,86,'37:86','کیا تم اللہ کے سوا من گھڑت معبودوں کوچاہتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(37,87,'37:87','تو خدا وند ِ عالم کے باب میں تمھارا کیا خیال ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,88,'37:88','پھر ابراہیم نے ستاروں پر ایک نظر ڈالی');
INSERT INTO "ur_wahid_uddin" VALUES(37,89,'37:89','پس کہا کہ میں بیمار ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(37,90,'37:90','پھر وہ لوگ اس کو چھوڑ کر چلے گئے');
INSERT INTO "ur_wahid_uddin" VALUES(37,91,'37:91','تو وہ ان کے بتوں میں گھس گیا، کہا کہ کیا تم کھاتے نہیں ہو');
INSERT INTO "ur_wahid_uddin" VALUES(37,92,'37:92','تم کو کیا ہوا کہ تم کچھ بولتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,93,'37:93','پھران کو مارا پوری قوت کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(37,94,'37:94','پھر لوگ اس کے پاس دوڑے ہوئے آئے');
INSERT INTO "ur_wahid_uddin" VALUES(37,95,'37:95','ابراہیم نے کہا، کیاتم لوگ ان چیزوں کوپوجتے ہو جن کو خود تراشتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(37,96,'37:96','اور اللہ ہی نے پیدا کیا ہے تم کو بھی اور ان چیزوں کو بھی جن کو تم بناتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(37,97,'37:97','انھوں نے کہا، اس کے لیے ایک مکان بناؤ پھر اس کو دہکتی ہوئی آگ میں ڈال دو');
INSERT INTO "ur_wahid_uddin" VALUES(37,98,'37:98','پس انھوں نے اس کے خلاف ایک کاروائی کرنی چاہی تو ہم نے انھیں کو نیچا کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(37,99,'37:99','اور اس نے کہا کہ میں اپنے رب کی طرف جارہاہوں ، وہ میری رہنمائی فرمائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(37,100,'37:100','اے میرے رب ، مجھ کو اولادِ صالح عطا فرما');
INSERT INTO "ur_wahid_uddin" VALUES(37,101,'37:101','تو ہم نے اس کو ایک بردبار لڑکے کی بشارت دی');
INSERT INTO "ur_wahid_uddin" VALUES(37,102,'37:102','پس جب وہ اس کے ساتھ چلنے پھرنے کی عمر کو پہنچا، اس نے کہا کہ اے میرے بیٹے، میں خواب میں دیکھتا ہوں کہ تم کو ذبح کررہا ہوں پس تم سوچ لو کہ تمھاری کیا رائے ہے اس نے کہا کہ اے میرے باپ، آپ کوجو حکم دیا جارہا ہے آپ اس کو کرڈالیے، ان شاء اللہ آپ مجھ کو صابروں میں سے پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,103,'37:103','پس جب دونوں مطیع ہوگئے اور ابراہیم نے اس کو ماتھے کے بل گرادیا');
INSERT INTO "ur_wahid_uddin" VALUES(37,104,'37:104','اور ہم نے اس کو آواز دی کہ اے ابراہیم');
INSERT INTO "ur_wahid_uddin" VALUES(37,105,'37:105','تم نے خواب کو سچ کر دکھایا بے شک ہم نیکی کرنے والوں کو ایسا ہی بدلہ دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,106,'37:106','یقیناً یہ ایک کھلی ہوئی آزمائش تھی');
INSERT INTO "ur_wahid_uddin" VALUES(37,107,'37:107','اور ہم نے ایک بڑی قربانی کے عوض اس کو چھڑالیا');
INSERT INTO "ur_wahid_uddin" VALUES(37,108,'37:108','اور ہم نے اس پر پچھلوں میں ایک گروہ کوچھوڑا');
INSERT INTO "ur_wahid_uddin" VALUES(37,109,'37:109','سلامتی ہو ابراہیم پر');
INSERT INTO "ur_wahid_uddin" VALUES(37,110,'37:110','ہم نیکی کرنے والوں کو اسی طرح بدلہ دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,111,'37:111','بے شک وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,112,'37:112','اور ہم نے اس کو اسحاق کی خوش خبری دی، ایک نبی صالحین میں سے');
INSERT INTO "ur_wahid_uddin" VALUES(37,113,'37:113','اور ہم نے اس کو اور اسحاق کو برکت دی اور ان دونوں کی نسل میں اچھے بھی ہیں اور ایسے بھی جواپنے نفس پرصریح ظلم کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,114,'37:114','اور ہم نے موسیٰ اور ہارون پراحسان کیا');
INSERT INTO "ur_wahid_uddin" VALUES(37,115,'37:115','اور ان کو اور ان کی قوم کو ایک بڑی مصیبت سے نجات دی');
INSERT INTO "ur_wahid_uddin" VALUES(37,116,'37:116','اور ہم نے ان کی مدد کی تو وہی غالب آنے والے بنے');
INSERT INTO "ur_wahid_uddin" VALUES(37,117,'37:117','اورہم نے ان دونوں کو واضح کتاب دی');
INSERT INTO "ur_wahid_uddin" VALUES(37,118,'37:118','اور ہم نے ان دونوں کو سیدھا راستہ دکھایا');
INSERT INTO "ur_wahid_uddin" VALUES(37,119,'37:119','اور ہم نے ان کے طریقہ پر پیچھے والوں میں ایک گروہ کوچھوڑا');
INSERT INTO "ur_wahid_uddin" VALUES(37,120,'37:120','سلامتی ہو موسیٰ اور ہارون پر');
INSERT INTO "ur_wahid_uddin" VALUES(37,121,'37:121','ہم نیکی کرنے والوں کو ایسا ہی بدلہ دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,122,'37:122','بے شک وہ دونوں ہمارے مومن بندوں میں سے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,123,'37:123','اور الیاس بھی پیغمبروں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,124,'37:124','جب کہ اس نے اپنی قوم سے کہا کیا تم ڈرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,125,'37:125','کیا تم لوگ بعل کو پکارتے ہو اور بہترین خالق کو چھوڑ دیتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(37,126,'37:126','اللہ کو، جوتمھارا بھی رب ہے اور تمھارے اگلے باپ دادا کا بھی');
INSERT INTO "ur_wahid_uddin" VALUES(37,127,'37:127','پس انھوں نے اس کو جھٹلایا تو وہ پکڑے جانے والوں میں سے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,128,'37:128','مگر جو اللہ کے خاص بندے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,129,'37:129','اور ہم نے اس کے طریقہ پر پچھلوں میں ایک گروہ کو چھوڑا');
INSERT INTO "ur_wahid_uddin" VALUES(37,130,'37:130','سلامتی ہوالیاس پر');
INSERT INTO "ur_wahid_uddin" VALUES(37,131,'37:131','ہم نیکی کرنے والوں کو ایسا ہی بدلہ دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,132,'37:132','بے شک وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,133,'37:133','اور بے شک لوط بھی پیغمبروں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,134,'37:134','جب کہ ہم نے اس کو اور اس کے لوگوں کو نجات دی');
INSERT INTO "ur_wahid_uddin" VALUES(37,135,'37:135','مگر ایک بڑھیا جو پیچھے رہ جانے والوں میں سے تھی');
INSERT INTO "ur_wahid_uddin" VALUES(37,136,'37:136','پھر ہم نے دوسروں کو ہلاک کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(37,137,'37:137','اور تم ان کی بستیوں پرگزرتے ہو صبح کو بھی');
INSERT INTO "ur_wahid_uddin" VALUES(37,138,'37:138','اور رات کو بھی ، تو کیا تم نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(37,139,'37:139','اور بے شک یونس بھی رسولوں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,140,'37:140','جب کہ وہ بھاگ کر بھری ہوئی کشتی پر پہنچا');
INSERT INTO "ur_wahid_uddin" VALUES(37,141,'37:141','پھر قرعہ ڈالا تو وہی خطا وار نکلا');
INSERT INTO "ur_wahid_uddin" VALUES(37,142,'37:142','پھر اس کو مچھلی نے نگل لیااور وہ اپنے کو ملامت کررہاتھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,143,'37:143','پس اگر وہ تسبیح کرنے والوں میں سے نہ ہوتا');
INSERT INTO "ur_wahid_uddin" VALUES(37,144,'37:144','تو لوگوں کے اٹھائے جانے کے دن تک اس کے پیٹ ہی میں رہتا');
INSERT INTO "ur_wahid_uddin" VALUES(37,145,'37:145','پھر ہم نے اس کوایک میدان میں ڈال دیا اور وہ نڈھال تھا');
INSERT INTO "ur_wahid_uddin" VALUES(37,146,'37:146','اور ہم نے اس پر ایک بیل دار درخت اگادیا');
INSERT INTO "ur_wahid_uddin" VALUES(37,147,'37:147','اور ہم نے اس کو ایک لاکھ یا اس سے زیادہ لوگوں کی طرف بھیجا');
INSERT INTO "ur_wahid_uddin" VALUES(37,148,'37:148','پھر وہ لوگ ایمان لائے تو ہم نے ان کو فائدہ اٹھانے دیا ایک مدت تک');
INSERT INTO "ur_wahid_uddin" VALUES(37,149,'37:149','پس ان سے پوچھو، کیا تمھارے رب کے لیے بیٹیاں ہیں اور ان کے لیے بیٹے');
INSERT INTO "ur_wahid_uddin" VALUES(37,150,'37:150','کیا ہم نے فرشتوں کو عورت بنایا ہے اور وہ دیکھ رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,151,'37:151','سن لو، یہ لوگ صرف من گھڑت کے طور پر ایسا کہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,152,'37:152','کہ اللہ اولاد رکھتا ہے اور یقیناً وہ جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,153,'37:153','کیا اللہ نے بیٹوں کے مقابلہ میں بیٹیاں پسند کی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,154,'37:154','تم کو کیا ہوگیا ہے، تم کیسا حکم لگا رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(37,155,'37:155','پھر کیا تم سوچ سے کام نہیں لیتے');
INSERT INTO "ur_wahid_uddin" VALUES(37,156,'37:156','کیا تمھارے پاس کوئی واضح دلیل ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,157,'37:157','تو اپنی کتاب لاؤ اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(37,158,'37:158','اور انھوں نے خدا اور جنات میں بھی رشتہ داری قرار دی ہے اور جنوں کو معلوم ہے کہ یقیناً وہ پکڑے ہوئے آئیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,159,'37:159','اللہ پاک ہے ان باتوں سے جو یہ بیان کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,160,'37:160','مگر وہ جو اللہ کے چنے ہوئے بندے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,161,'37:161','پس تم اور جن کی تم عبادت کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(37,162,'37:162','خدا سے کسی کوپھیر نہیں سکتے');
INSERT INTO "ur_wahid_uddin" VALUES(37,163,'37:163','مگر اس کو جو جہنم میں پڑنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,164,'37:164','اور ہم میں سے ہر ایک کا ایک متعین مقام ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,165,'37:165','اور ہم خدا کے حضور بس صف بستہ رہنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,166,'37:166','اور ہم اس کی تسبیح کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,167,'37:167','اور یہ لوگ کہا کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(37,168,'37:168','کہ اگر ہمارے پاس پہلوں کی کوئی تعلیم ہوتی');
INSERT INTO "ur_wahid_uddin" VALUES(37,169,'37:169','توہم اللہ کے خاص بندے ہوتے');
INSERT INTO "ur_wahid_uddin" VALUES(37,170,'37:170','پھر انھوں نے اس کا انکار کردیاتو عنقریب وہ جان لیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,171,'37:171','اور اپنے بھیجے ہوئے بندوں کے لیے ہمارا یہ فیصلہ پہلے ہی ہوچکا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,172,'37:172','کہ بے شک وہی غالب کيے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,173,'37:173','اور ہمارا لشکر ہی غالب رہنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,174,'37:174','تو کچھ مدت تک ان سے اعراض کرو');
INSERT INTO "ur_wahid_uddin" VALUES(37,175,'37:175','اوردیکھتے رہو، عنقریب وہ بھی دیکھ لیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,176,'37:176','کیا وہ ہمارے عذاب کے لیے جلدی کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,177,'37:177','پس جب وہ ان کے صحن میں اترے گا تو بڑی ہی بری ہوگی ان لوگوں کی صبح جن کو اس سے ڈرایاجاچکا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(37,178,'37:178','تو کچھ مدت کے لیے ان سے اعراض کرو');
INSERT INTO "ur_wahid_uddin" VALUES(37,179,'37:179','اور دیکھتے رہو، عنقریب وہ خود دیکھ لیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(37,180,'37:180','پاک ہے تیرا رب، عزت کا مالک ، ان باتوں سے جو یہ لوگ بیان کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(37,181,'37:181','اور سلام ہے پیغمبروں پر');
INSERT INTO "ur_wahid_uddin" VALUES(37,182,'37:182','اور ساری تعریف اللہ کے لیے ہے جو رب ہے سارے جہان کا');
INSERT INTO "ur_wahid_uddin" VALUES(38,1,'38:1','صٓ قسم ہے نصیحت والے قرآن کی');
INSERT INTO "ur_wahid_uddin" VALUES(38,2,'38:2','بلکہ جن لوگوں نے انکار کیاوہ گھمنڈ اور ضد میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(38,3,'38:3','ان سے پہلے ہم نے کتنی ہی قومیں ہلاک کردیں ، تو وہ پکارنے لگے اور وہ وقت بچنے کا نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(38,4,'38:4','اور ان لوگوں نے تعجب کیا کہ ان کے پاس ان میں سے ایک ڈرانے والا آیا اور انکار کرنے والوں نے کہا کہ یہ جادوگر ہے، جھوٹا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,5,'38:5','کیا اس نے اتنے معبودوں کی جگہ ایک معبود کردیا، یہ تو بڑی عجیب بات ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,6,'38:6','اور ان کے سرداراُٹھ کھڑے ہوئے کہ چلو اور اپنے معبودوں پرجمے رہو، یہ کوئی مطلب کی بات ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,7,'38:7','ہم نے یہ بات پچھلے مذہب میں نہیں سنی، یہ صرف ایک بنائی ہوئی بات ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,8,'38:8','کیا ہم سب میں سے اسی شخص پر کلام الٰہی نازل کیاگیا بلکہ یہ لوگ میری یاد دہانی کی طرف سے شک میں ہیں بلکہ انھوں نے اب تک میرے عذاب کا مزہ نہیں چکھا');
INSERT INTO "ur_wahid_uddin" VALUES(38,9,'38:9','کیا تیرے رب کی رحمت کے خزانے ان کے پاس ہیں جو زبردست ہے، فیاض ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,10,'38:10','کیا آسمانوں اور زمین اور ان کے درمیان کی چیزوں کی بادشاہی ان کے اختیار میں ہےپھر وہ سیڑھیاں لگاکر چڑھ جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(38,11,'38:11','ایک لشکر یہ بھی یہاں تباہ ہوگا سب لشکروں میں سے');
INSERT INTO "ur_wahid_uddin" VALUES(38,12,'38:12','ان سے پہلے قوم نوح اور عاد اور میخوں والا فرعون');
INSERT INTO "ur_wahid_uddin" VALUES(38,13,'38:13','اور ثمود اور قوم لوط اور اصحاب ایکہ نے جھٹلایا یہ لوگ بڑی بڑی جماعتیں تھے');
INSERT INTO "ur_wahid_uddin" VALUES(38,14,'38:14','ان سب نے رسولوں کو جھٹلایا تو میرا عذاب نازل ہوکر رہا');
INSERT INTO "ur_wahid_uddin" VALUES(38,15,'38:15','اور یہ لوگ صرف ایک چنگھاڑکے منتظر ہیں ،جس کے بعد کوئی ڈھیل نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(38,16,'38:16','اور انھوں نے کہا کہ اے ہمارے رب، ہمارا حصہ ہم کو حساب کے دن سے پہلے دے دے');
INSERT INTO "ur_wahid_uddin" VALUES(38,17,'38:17','جو کچھ وہ کہتے ہیں اس پر صبر کرو، اور ہمارے بندے داؤد کو یاد کرو جو قوت والا، رجوع کرنے والا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(38,18,'38:18','ہم نے پہاڑوں کو اس کے ساتھ مسخر کردیا کہ وہ اس کے ساتھ صبح اور شام تسبیح کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(38,19,'38:19','اور پرندوں کو بھی جمع ہوکرسب اللہ کی طرف رجوع کرنے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(38,20,'38:20','اور ہم نے اس کی سلطنت مضبوط کی، اور اس کو حکمت عطا کیاور معاملات کا فیصلہ کرنے کی صلاحیت دی');
INSERT INTO "ur_wahid_uddin" VALUES(38,21,'38:21','اور کیا تم کو خبر پہنچی ہے مقدمہ والوں کی جب کہ وہ دیوار پھاند کرعبادت خانہ میں داخل ہوگئے');
INSERT INTO "ur_wahid_uddin" VALUES(38,22,'38:22','جب وہ داؤد کے پاس پہنچے تو وہ ان سے گھبرا گیاانھوں نے کہا کہ آپ ڈریں نہیں ، ہم دو فریقِ معاملہ ہیں ، ایک نے دوسرے پر زیادتی کی ہے تو آپ ہمارے درمیان حق کے ساتھ فیصلہ کیجئے، بے انصافی نہ کیجئے اور ہم کو راہ راست بتائیے');
INSERT INTO "ur_wahid_uddin" VALUES(38,23,'38:23','یہ میرا بھائی ہے، اس کے پاس ننانوے دنبیاں ہیں اور میرے پاس صرف ایک دنبی ہے تو وہ کہتا ہے کہ وہ بھی میرے حوالے کردےاور اس نے گفتگو میں مجھ کو دبالیا');
INSERT INTO "ur_wahid_uddin" VALUES(38,24,'38:24','داؤد نے کہا، اس نے تمھاری دنبی کواپنی دنبیوں میں ملانے کا مطالبہ کرکے واقعی تم پر ظلم کیاہے اور اکثر شرکاء ایک دوسرے پر زیادتی کیا کرتے ہیں مگر وہ جو ایمان رکھتے ہیں اور نیک عمل کرتے ہیں ،اور ایسے لوگ بہت کم ہیں اور داؤد کو خیال آیا کہ ہم نے اس کاامتحان کیا ہے ، تو اس نے اپنے رب سے معافی مانگی اور سجدے میں گرگیااور رجوع ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(38,25,'38:25','پھر ہم نے اس کی وہ (غلطی) معاف کردیاور بے شک ہمارے یہاں اس کے لیے تقرب ہے اور اچھا انجام');
INSERT INTO "ur_wahid_uddin" VALUES(38,26,'38:26','اے داؤد،ہم نے تم کو زمین میں خلیفہ (حاکم) بنایا ہے تو لوگوں کے درمیان انصاف کے ساتھ فیصلہ کرو اور خواہش کی پیروی نہ کرو، وہ تم کو اللہ کی راہ سے بھٹکادے گی جولوگ اللہ کی راہ سے بھٹکتے ہیں ان کے لیے سخت عذاب ہے اس وجہ سے کہ وہ روز حساب کو بھولے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,27,'38:27','اور ہم نے زمین اور آسمان اور جو ان کے درمیان ہے عبث نہیں پیدا کیا، یہ ان لوگوں کاگمان ہے جنھوں نے انکار کیا، تو جن لوگوں نے انکار کیا ان کے لیے بربادی ہے آگ سے');
INSERT INTO "ur_wahid_uddin" VALUES(38,28,'38:28','کیا ہم ان لوگوں کو جو ایمان لائے اور اچھے کام کيے ان کی مانند کردیں گے جوزمین میں فساد کرنے والے ہیں ،یا ہم پرہیزگاروں کو بدکاروں جیسا کردیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(38,29,'38:29','یہ ایک بابرکت کتاب ہے جو ہم نے تمھاری طرف اتاری ہے، تاکہ لوگ اس کی آیتوں پر غور کریں اور تاکہ عقل والے اس سے نصیحت حاصل کریں');
INSERT INTO "ur_wahid_uddin" VALUES(38,30,'38:30','اور ہم نے داؤد کو سلیمان عطا کیابہترین بندہ ، اپنے رب کی طرف بہت رجوع کرنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(38,31,'38:31','جب شام کے وقت اس کے سامنے تیزرو، عمدہ گھوڑے پیش کيے گئے');
INSERT INTO "ur_wahid_uddin" VALUES(38,32,'38:32','تو اس نے کہا، میں نے دوست رکھا مال کی محبت کو اپنے رب کی یاد سے، یہاں تک کہ چھپ گیا اوٹ میں');
INSERT INTO "ur_wahid_uddin" VALUES(38,33,'38:33','ان کو میرے پاس واپس لاؤپھر وہ جھاڑنے لگا پنڈلیاں اور گردنیں');
INSERT INTO "ur_wahid_uddin" VALUES(38,34,'38:34','اور ہم نے سلیمان کوآزمایااور ہم نے اس کے تخت پر ایک دھڑ ڈال دیاپھر اس نے رجوع کیا');
INSERT INTO "ur_wahid_uddin" VALUES(38,35,'38:35','اس نے کہا کہ اے میرے رب، مجھ کو معاف کردے اور مجھ کو ایسی سلطنت دے جو میرے بعد کسی کے لیے سزاوار نہ ہو،بے شک توبڑا دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,36,'38:36','تو ہم نے ہوا کواس کے تابع کردیاوہ اس کے حکم سے نرمی کے ساتھ چلتی تھی جدھر وہ چاہتا');
INSERT INTO "ur_wahid_uddin" VALUES(38,37,'38:37','اور جنات کو بھی اس کا تابع کردیاہر طرح کے معمار اور غوطہ خور');
INSERT INTO "ur_wahid_uddin" VALUES(38,38,'38:38','اور دوسرے جو زنجیروں میں جکڑے ہوئے رہتے');
INSERT INTO "ur_wahid_uddin" VALUES(38,39,'38:39','یہ ہمارا عطیہ ہے توخواہ اس کو دویاروکو، بے حساب');
INSERT INTO "ur_wahid_uddin" VALUES(38,40,'38:40','اور اس کے لیے ہمارے یہاں قرب ہے اور بہتر انجام');
INSERT INTO "ur_wahid_uddin" VALUES(38,41,'38:41','اور ہمارے بندے ایوب کو یاد کروجب اس نے اپنے رب کو پکارا کہ شیطان نے مجھ کو تکلیف اور عذاب میں ڈال دیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,42,'38:42','اپنا پاؤں مارویہ ٹھنڈا پانی ہے، نہانے کے لیے اور پینے کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(38,43,'38:43','اور ہم نے اس کو اس کا کنبہ عطا کیا اور ان کے ساتھ ان کے برابر اور بھی،اپنی طرف سے رحمت کے طور پر اور عقل والوں کے لیے نصیحت کے طور پر');
INSERT INTO "ur_wahid_uddin" VALUES(38,44,'38:44','اور اپنے ہاتھ میں سینکوں کا ایک مُٹھا لو اور اس سے مارواور قسم نہ توڑو بے شک ہم نے اس کو صابر پایا بہترین بندہ، اپنے رب کی طرف بہت رجوع کرنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(38,45,'38:45','اور ہمارے بندوں ، ابراہیم اور اسحاق اور یعقوب کو یاد کرو، وہ ہاتھوں والے اور آنکھوں والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(38,46,'38:46','ہم نے ان کو ایک خاص بات کے ساتھ مخصوص کیا تھا کہ وہ آخرت کی یاد دہانی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,47,'38:47','اور وہ ہمارے یہاں چنے ہوئے نیک لوگوں میں سے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(38,48,'38:48','اور اسماعیل اور الیسع اور ذوالکفل کو یاد کرو،سب نیک لوگوں میں سے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(38,49,'38:49','یہ نصیحت ہے، اور بے شک اللہ سے ڈرنے والوں کے لیے اچھا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,50,'38:50','ہمیشہ کے باغ جن کے دروازے ان کے لیے کھلے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(38,51,'38:51','وہ ان میں تکیہ لگائے بیٹھے ہوں گےاور بہت سے میوے اور مشروبات طلب کرتے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(38,52,'38:52','اور ان کے پاس شرمیلی ہم سن بیویاں ہوں گی');
INSERT INTO "ur_wahid_uddin" VALUES(38,53,'38:53','یہ ہے وہ چیز جس کا تم سے روز حساب آنے پر وعدہ کیا جاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,54,'38:54','یہ ہمارا رزق ہے جو کبھی ختم ہونے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(38,55,'38:55','یہ بات ہوچکی، اور سرکشوں کے لیے براٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,56,'38:56','جہنم ، اس میں وہ داخل ہوں گےپس کیا ہی بری جگہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,57,'38:57','یہ کھولتا ہوا پانی اور پیپ ہے، تو یہ لوگ ان کو چکھیں');
INSERT INTO "ur_wahid_uddin" VALUES(38,58,'38:58','اور اس قسم کی دوسری اور بھی چیزیں ہوں گی');
INSERT INTO "ur_wahid_uddin" VALUES(38,59,'38:59','یہ ایک فوج تمھارے پاس گھسی چلی آرہی ہے، ان کے لیے کوئی خوش آمدید نہیں وہ آگ میں پڑنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(38,60,'38:60','وہ کہیں گے بلکہ تم ، تمھارے لیے کوئی خوش آمدید نہیں تمھیں تو یہ ہمارے آگے لائے ہو، پس کیسا برا ہے یہ ٹھکانا');
INSERT INTO "ur_wahid_uddin" VALUES(38,61,'38:61','وہ کہیں گے کہ اے ہمارے رب، جوشخص اس کو ہمارے آگے لایا اس کو تو دو نا عذاب دے، جہنم میں');
INSERT INTO "ur_wahid_uddin" VALUES(38,62,'38:62','اور وہ کہیں گے، کیا بات ہے کہ ہم ان لوگوں کو یہاں نہیں دیکھ رہے ہیں جن کو ہم برے لوگوں میں شمار کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(38,63,'38:63','کیا ہم نے ان کو مذاق بنالیاتھا یا ان سے نگاہیں چوک رہی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(38,64,'38:64','بے شک یہ بات سچی ہے، اہل دوزخ کا باہم جھگڑنا');
INSERT INTO "ur_wahid_uddin" VALUES(38,65,'38:65','کہو کہ میں تو صرف ایک ڈرانے والا ہوں اور کوئی معبود نہیں مگر اللہ، یکتا اور غالب');
INSERT INTO "ur_wahid_uddin" VALUES(38,66,'38:66','وہ رب ہے آسمانوں اور زمین کا اور ان چیزوں کا جو ان کے درمیان ہیں ، وہ زبردست ہے، بخشنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,67,'38:67','کہو کہ یہ ایک بڑی خبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,68,'38:68','جس سے تم بے پرواہ ہورہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(38,69,'38:69','مجھ کو عالمِ بالا کی کچھ خبر نہ تھی جب کہ وہ آپس میں تکرار کر رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(38,70,'38:70','میرے پاس تو وحی بس اس لیے آتی ہے کہ میں ایک کھلاہواڈرانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(38,71,'38:71','جب تمھارے رب نے فرشتوں سے کہا کہ میں مٹی سے ایک بشر بنانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(38,72,'38:72','پھر جب میں اس کو درست کرلوں اور اس میں اپنی روح پھونک دوں تو تم اس کے آگے سجدے میں گرپڑنا');
INSERT INTO "ur_wahid_uddin" VALUES(38,73,'38:73','پس تمام فرشتوں نے سجدہ کیا');
INSERT INTO "ur_wahid_uddin" VALUES(38,74,'38:74','مگر ابلیس کہ اس نے گھمنڈ کیا اور وہ انکار کرنے والوں میں سے ہوگیا');
INSERT INTO "ur_wahid_uddin" VALUES(38,75,'38:75','فرمایا کہ اے ابلیس ، کس چیز نے تجھ کو روک دیا کہ تو اس کوسجدہ کرے جس کو میں نے اپنے دونوں ہاتھوں سے بنایایہ تو نے تکبر کیا یا تو بڑے درجہ والوں میں سے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,76,'38:76','اس نے کہا کہ میں آدم سے بہتر ہوں تونے مجھ کو آگ سے پیدا کیا ہے اور اس کو مٹی سے');
INSERT INTO "ur_wahid_uddin" VALUES(38,77,'38:77','فرمایا کہ تو یہاں سے نکل جا،کیوں کہ تو مردود ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,78,'38:78','اور تجھ پر میری لعنت ہے جزاء کے دن تک');
INSERT INTO "ur_wahid_uddin" VALUES(38,79,'38:79','ابلیس نے کہا کہ اے میرے رب، مجھ کو مہلت دے اس دن تک کے لیے جب لوگ دوبارہ اٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(38,80,'38:80','فرمایا کہ تجھ کو مہلت دی گئی');
INSERT INTO "ur_wahid_uddin" VALUES(38,81,'38:81','وقت معین تک کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(38,82,'38:82','اس نے کہا کہ تیری عزت کی قسم، میں ان سب کو گمراہ کرکے رہوں گا');
INSERT INTO "ur_wahid_uddin" VALUES(38,83,'38:83','بجز تیرے ان بندوں کے جنھیں تونے خالص کرلیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(38,84,'38:84','فرمایا، توحق یہ ہے اور میں حق ہی کہتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(38,85,'38:85','کہ میں جہنم کو تجھ سے اوران تمام لوگوں سے بھردوں گا جو ان میں سے تیری پیروی کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(38,86,'38:86','کہو کہ میں اس پر تم سے کوئی اجر نہیں مانگتا اور نہ میں تکلف کرنے والوں میں سے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(38,87,'38:87','یہ تو بس ایک نصیحت ہے دنیا والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(38,88,'38:88','اور تم جلد اس کی دی ہوئی خبر کو جان لوگے');
INSERT INTO "ur_wahid_uddin" VALUES(39,1,'39:1','یہ کتاب اللہ کی طرف سے اُتاری گئی ہے جو زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(39,2,'39:2','بے شک ہم نے یہ کتاب تمھاری طرف حق کے ساتھ اتاری ہے ، پس تم اللہ ہی کی عبادت کرو، اسی کے لیے دین کو خالص کرتے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(39,3,'39:3','آگاہ ،دینِ خالص صرف اللہ کے لیے ہے اور جن لوگوں نے اس کے سوا دوسرے حمایتی بنا رکھے ہیں ، کہ ہم تو ان کی عبادت صرف اس لیے کرتے ہیں کہ وہ ہم کو خدا سے قریب کردیں بے شک اللہ ان کے درمیان اس بات کا فیصلہ کردے گا جس میں وہ اختلاف کررہے ہیں اللہ ایسے شخص کو ہدایت نہیں دیتا جو جھوٹا،حق کو نہ ماننے والا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(39,4,'39:4','اگر اللہ چاہتا کہ وہ بیٹا بنائے تواپنی مخلوق میں سے جس کو چاہتا چن لیتا،وہ پاک ہےوہ اللہ ہے، اکیلا،سب پرغالب');
INSERT INTO "ur_wahid_uddin" VALUES(39,5,'39:5','اس نے آسمانوں اور زمین کوحق کے ساتھ پیدا کیاوہ رات کو دن پرلپیٹتا ہے اور دن کو رات پرلپیٹتا ہےاور اس نے سورج اور چاند کومسخر کررکھا ہےہر ایک ٹھہری ہوئی مدت پر چلتا ہے سن لو کہ وہ زبردست ہے، بخشنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(39,6,'39:6','اللہ نے تم کوایک جان سے پیدا کیا،پھر اس نے اسی سے اس کا جوڑا بنایااور اسی نے تمھارے لیے نرومادہ چوپایوں کی آٹھ قسمیں اُتاریں وہ تم کوتمھاری ماؤں کے پیٹ میں بناتا ہے، ایک خلقت کے بعد دوسری خلقت، تین تاریکیوں کے اندر یہی اللہ تمھارا رب ہےبادشاہی اسی کی ہے اس کے سوا کوئی معبود نہیں پھر تم کہاں سے پھیرے جاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(39,7,'39:7','اگر تم انکار کرو تو اللہ تم سے بے نیاز ہے اور وہ اپنے بندوں کے لیے انکار کو پسند نہیں کرتااور اگر تم شکر کرو تو وہ اس کو تمھارے لیے پسند کرتا ہے اور کوئی بوجھ اٹھانے والا کسی دوسرے کابوجھ نہ اُٹھائے گا پھر تمھارے رب ہی کی طرف تمھاری واپسی ہےتو وہ تم کوبتادے گا جوتم کرتے تھے بے شک وہ دلوں کی بات کو جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(39,8,'39:8','اور جب انسان کو کوئی تکلیف پہنچتی ہے تو وہ اپنے رب کو پکارتا ہے، اس کی طرف رجوع ہوکرپھر جب وہ اس کو اپنے پاس سے نعمت دے دیتا ہے تو وہ اس چیز کو بھول جاتا ہے جس کے لیے وہ پہلے پکاررہا تھا اور وہ دوسروں کو اللہ کا برابر ٹھہرانے لگتا ہے تاکہ وہ اس کی راہ سے گمراہ کردےکہو کہ تو اپنے کفر سے تھوڑے دن فائدہ اٹھالے،بے شک تو آگ والوں میں سے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(39,9,'39:9','بھلا کوئی شخص رات کی گھڑیوں میں سجدہ اور قیام کی حالت میں عاجزی کررہا ہو، آخرت سے ڈرتا ہواور اپنے رب کی رحمت کاامید وار ہو کہو ، کیا جاننے والے اور نہ جاننے والے دونوں برابر ہوسکتے ہیں نصیحت تو وہی لوگ پکڑتے ہیں جو عقل والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,10,'39:10','کہو کہ اے میرے بندو جوایمان لائے ہو، اپنے رب سے ڈروجو لوگ اس دنیا میں نیکی کریں گے ان کے لیے نیک صلہ ہے اور اللہ کی زمین وسیع ہے بے شک صبر کرنے والوں کو ان کااجر بے حساب دیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(39,11,'39:11','کہو، مجھ کو حکم دیا گیا ہے کہ میں اللہ ہی کی عبادت کروں ، اسی کے لیے دین کو خالص کرتے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(39,12,'39:12','اور مجھ کو حکم دیا گیا ہے کہ سب سے پہلے خود مسلم بنوں');
INSERT INTO "ur_wahid_uddin" VALUES(39,13,'39:13','کہو کہ اگر میں اپنے رب کی نافرمانی کروں تو میں ایک ہولناک دن کے عذاب سے ڈرتاہوں');
INSERT INTO "ur_wahid_uddin" VALUES(39,14,'39:14','کہو کہ میں اللہ کی عبادت کرتا ہوں اسی کے لیے دین کو خالص کرتے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(39,15,'39:15','پس تم اس کے سوا جس کی چاہے عبادت کروکہو کہ اصلی گھاٹے والے تو وہ ہیں ،جنھوں نے اپنے آپ کو اور اپنے گھر والوں کو قیامت کے دن گھاٹے میں ڈالاسن لو یہی کھلا ہوا گھاٹا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(39,16,'39:16','ان کے لیے ان کے اوپر سے بھی آگ کے سائبان ہوں گے اور ان کے نیچے سے بھی یہ چیز ہے جس سے اللہ اپنے بندوں کو ڈراتا ہے اے میرے بندو، پس مجھ سے ڈرو');
INSERT INTO "ur_wahid_uddin" VALUES(39,17,'39:17','اور جو لوگ شیطان سے بچے کہ وہ اس کی عبادت کریں اور وہ اللہ کی طرف رجوع ہوئے، ان کے لیے خوش خبری ہے، تو میرے بندوں کو خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(39,18,'39:18','جو بات کو غور سے سنتے ہیں پھر اس کے بہتر کی پیروی کرتے ہیں یہی وہ لوگ ہیں جن کو اللہ نے ہدایت بخشی ہے اور یہی ہیں جو عقل والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,19,'39:19','کیا جس پر عذاب کی بات ثابت ہوچکی، پس کیا تم ایسے شخص کو بچاسکتے ہو جو کہ آگ میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(39,20,'39:20','لیکن جو لوگ اپنے رب سے ڈرے، ان کے ليے بالا خانے ہیں جن کے اوپر اور بالا خانے ہیں ، بنے ہوئے ان کے نیچے نہریں بہتی ہیں یہ اللہ کا وعدہ ہے اللہ اپنے وعدہ کے خلاف نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(39,21,'39:21','کیا تم نے نہیں دیکھا کہ اللہ نے آسمان سے پانی اتارا پھر اس کو زمین کے چشموں میں جاری کردیاپھر وہ اس سے مختلف قسم کی کھیتیاں نکالتا ہے، پھر وہ خشک ہوجاتی ہے، تو تم اس کو زرد دیکھتے ہوپھروہ اس کوریزہ ریزہ کردیتا ہے بے شک اس میں نصیحت ہے عقل والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(39,22,'39:22','کیا وہ شخص جس کا سینہ اللہ نے اسلام کے لیے کھول دیا، پس وہ اپنے رب کی طرف سے ایک روشنی پر ہےتو خرابی ہے ان کے لیے جن کے دل اللہ کی نصیحت کے معاملہ میں سخت ہوگئے یہ لوگ کھلی ہوئی گمراہی میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,23,'39:23','اللہ نے بہترین کلام اتارا ہےایک ایسی کتاب آپس میں ملتی جلتی،بار بار دہرائی ہوئی،اس سے ان لوگوں کے رونگٹے کھڑے ہوجاتے ہیں جو اپنے رب سے ڈرنے والے ہیں پھر ان کے بدن اور ان کے دل نرم ہوکر اللہ کی یاد کی طرف متوجہ ہوجاتے ہیں یہ اللہ کی ہدایت ہےاس سے وہ ہدایت دیتا ہے جس کو وہ چاہتا ہےاور جس کو اللہ گمراہ کردے تو اس کو کوئی ہدایت دینے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,24,'39:24','کیا وہ شخص جو قیامت کے دن اپنے چہرے کو برے عذاب کی سپربنائے گا، اور ظالموں سے کہا جائے گا کہ چکھو مزہ اس کمائی کا جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(39,25,'39:25','ان سے پہلے والوں نے بھی جھٹلایا تو ان پر عذاب وہاں سے آگیا جدھر سے ان کا خیال بھی نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(39,26,'39:26','تو اللہ نے ان کو دنیا کی زندگی میں رسوائی کامزہ چکھایا اور آخرت کاعذاب اور بھی بڑا ہے، کاش یہ لوگ جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(39,27,'39:27','اور ہم نے اس قرآن میں ہر قسم کی مثاليں بیان کی ہیں تاکہ وہ نصیحت حاصل کریں');
INSERT INTO "ur_wahid_uddin" VALUES(39,28,'39:28','یہ عربی قرآن ہے، اس میں کوئی ٹیڑھ نہیں ، تاکہ لوگ ڈریں');
INSERT INTO "ur_wahid_uddin" VALUES(39,29,'39:29','اللہ مثال بیان کرتا ہے ایک شخص کی جس کی ملکیت میں کئی ضدی آقا شریک ہیں اور دوسراشخص پورا کا پورا ایک ہی آقا کا غلام ہےکیا ان دونوں کا حال یکساں ہوگا سب تعریف اللہ کے لیے ہے لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(39,30,'39:30','تم کو بھی مرنا ہے اور وہ بھی مرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,31,'39:31','پھر تم لوگ قیامت کے دن اپنے رب کے سامنے اپنا مقدمہ پیش کرو گے');
INSERT INTO "ur_wahid_uddin" VALUES(39,32,'39:32','اس شخص سے زیادہ ظالم کون ہوگا جس نے اللہ پر جھوٹ باندھااور سچائی کو جھٹلادیا جب کہ وہ اس کے پاس آئیکیا ایسے منکروں کاٹھکانا جہنم میں نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(39,33,'39:33','اورجوشخص سچائی لے کر آیا اور جس نے اس کی تصدیق کی، یہی لوگ اللہ سے ڈرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,34,'39:34','ان کے لیے ان کے رب کے پاس وہ سب ہے جو وہ چاہیں گے، یہ بدلہ ہے نیکی کرنے والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(39,35,'39:35','تاکہ اللہ ان سے ان کے برے عملوں کو دور کردے اور ان کے نیک کاموں کے عوض ان کو ان کا ثواب دے');
INSERT INTO "ur_wahid_uddin" VALUES(39,36,'39:36','کیا اللہ اپنے بندے کے لیے کافی نہیں اور یہ لوگ اس کے سوا دوسروں سے تم کو ڈراتے ہیں ، اوراللہ جس کو گمراہ کردے اس کو کوئی راستہ دکھانے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,37,'39:37','اوراللہ جس کو ہدایت دے اس کو کوئی گمراہ کرنے والا نہیں ، کیا اللہ زبردست، انتقام لینے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,38,'39:38','اور اگر تم ان سے پوچھو کہ آسمانوں کو اور زمین کو کس نے پیدا کیا تو وہ کہیں گے کہ اللہ نے کہو ،تمھارا کیا خیال ہے، اللہ کے سوا تم جن کو پکارتے ہو،اگر اللہ مجھ کو کوئی تکلیف پہنچانا چاہے تو کیا یہ اس کی دی ہوئی تکلیف کو دور کرسکتے ہیں ، یا اللہ مجھ پر کوئی مہربانی کرنا چاہے تو کیایہ اس کی مہربانی کو روکنے والے بن سکتے ہیں کہو کہ اللہ میرے لیے کافی ہے، بھروسہ کرنے والے اسی پر بھروسہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,39,'39:39','کہو کہ اے میری قوم، تم اپنی جگہ عمل کرو، میں بھی عمل کررہا ہوں ، تو تم جلد جان لوگے');
INSERT INTO "ur_wahid_uddin" VALUES(39,40,'39:40','کہ کس پر رسوا کرنے والا عذاب آتا ہے اور کس پر وہ عذاب آتا ہے جو کبھی ٹلنے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,41,'39:41','ہم نے لوگوں کی ہدایت کے لیے یہ کتاب تم پر حق کے ساتھ اُتاری ہے پس جوشخص ہدایت حاصل کرے گا وہ اپنے ہی لیے حاصل کرے گااورجو شخص بے راہ ہوگا تو اس کا بے راہ ہونا اسی پر پڑے گااور تم ان کے اوپر ذمہ دار نہیں ہو');
INSERT INTO "ur_wahid_uddin" VALUES(39,42,'39:42','اللہ ہی وفات دیتا ہے جانوں کو ان کی موت کے وقت، اور جن کی موت نہیں آئی ان کے سونے کے وقتپھر وہ ان کو روک لیتا ہے جن کی موت کافیصلہ کرچکا ہے اور دوسروں کو ایک وقت مقرر تک کے لیے رِہا کردیتا ہے بے شک اس میں نشانیاں ہیں ان لوگوں کے لیے جو غور کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,43,'39:43','کیا انھوں نے اللہ کو چھوڑکر دوسروں کو سفارشی بنا رکھا ہے کہو، اگرچہ وہ نہ کچھ اختیار رکھتے ہوں اور نہ کچھ سمجھتے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(39,44,'39:44','کہو، سفارش ساری کی ساری اللہ کے اختیار میں ہے آسمانوں اور زمین کی بادشاہی اسی کی ہےپھر تم اسی کی طرف لوٹائے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(39,45,'39:45','اور جب اکیلے اللہ کا ذکر کیا جاتا ہے تو ان لوگوں کے دل کڑھتے ہیں جو آخرت پر ایمان نہیں رکھتےاور جب اس کے سوا دوسروں کا ذکر ہوتاہے تو اس وقت وہ خوش ہوجاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,46,'39:46','کہو کہ اے اللہ،آسمانوں اور زمین کے پیدا کرنے والے، غائب اور حاضر کے جاننے والے، تو اپنے بندوں کے درمیان اس چیز کا فیصلہ کرے گا جس میں وہ اختلاف کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,47,'39:47','اور اگر ظلم کرنے والوں کے پاس وہ سب کچھ ہو جو زمین میں ہے اور اسی کے برابر اور بھی ، تو وہ قیامت کے دن سخت عذاب سے بچنے کے لیے ان کو فدیہ میں دے دیں اور اللہ کی طرف سے ان کو وہ معاملہ پیش آئے گا جس کا ان کو گمان بھی نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(39,48,'39:48','اور ان کے سامنے آجائیں گے ان کے برے اعمال اور وہ چیز ان کو گھیرلے گی جس کا وہ مذاق اُڑاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(39,49,'39:49','پس انسان کو کوئی تکلیف پہنچتی ہے تو وہ ہم کو پکارتا ہے، پھر جب ہم اپنی طرف سے اس کو نعمت دے دیتے ہیں تو وہ کہتا ہے کہ یہ تو مجھ کو علم کی بنا پر دیا گیا ہے،بلکہ یہ آزمائش ہے مگر ان میں سے اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(39,50,'39:50','ان سے پہلے والوں نے بھی یہ بات کہی تو جو کچھ وہ کماتے تھے وہ ان کے کام نہ آیا');
INSERT INTO "ur_wahid_uddin" VALUES(39,51,'39:51','پس ان پر وہ برائیاں آپڑیں جو انھوں نے کمائی تھیں اور ان لوگوں میں سے جو ظالم ہیں ان کے سامنے بھی ان کی کمائی کے برے نتائج جلد آئیں گےوہ ہم کو عاجز کردینے والے نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,52,'39:52','کیا ان کو معلوم نہیں کہ اللہ جس کو چاہتا ہے رزق کشادہ کردیتا ہے اور وہی تنگ کردیتا ہے بے شک اس کے اندر نشانیاں ہیں ان لوگوں کے لیے جو ایمان لانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,53,'39:53','کہو کہ اے میرے بندو جنھوں نے اپنی جانوں پر زیادتی کی ہے، اللہ کی رحمت سے مایوس نہ ہو بے شک اللہ تمام گناہوں کو معاف کردیتا ہے، وہ بڑا بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(39,54,'39:54','اور تم اپنے رب کی طرف رجوع کرو اور اس کے فرماں بردار بن جاؤ اس سے پہلے کہ تم پر عذاب آجائے، پھر تمھاری کوئی مدد نہ کی جائے');
INSERT INTO "ur_wahid_uddin" VALUES(39,55,'39:55','اور تم پیروی کرو اپنے رب کی بھیجی ہوئی کتاب کے بہتر پہلو کی، قبل اس کے کہ تم پر اچانک عذاب آجائے اور تم کو خبر بھی نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(39,56,'39:56','کہیں کوئی شخص یہ کہے کہ افسوس میری کوتاہی پر جو میں نے خداکی جناب میں کی ،اور میں تو مذاق اُڑانے والوں میں شامل رہا');
INSERT INTO "ur_wahid_uddin" VALUES(39,57,'39:57','یا کوئی یہ کہے کہ اگر اللہ مجھ کو ہدایت دیتا تو میں بھی ڈرنے والوں میں سے ہوتا');
INSERT INTO "ur_wahid_uddin" VALUES(39,58,'39:58','یا عذاب کو دیکھ کر کوئی شخص یہ کہے کہ کاش، مجھے دنیا میں دوبارہ جانا ہو تو میں نیک بندوں میں سے ہوجاؤں');
INSERT INTO "ur_wahid_uddin" VALUES(39,59,'39:59','ہاں تمھارے پاس میری آیتیں آئیں پھر تونے ان کوجھٹلایا اور تکبر کیا اور تو منکروں میں شامل رہا');
INSERT INTO "ur_wahid_uddin" VALUES(39,60,'39:60','اور تم قیامت کے دن ان لوگوں کے چہرے سیاہ دیکھو گے جنھوں نے اللہ پر جھوٹ بولا تھا کیا متکبرین کاٹھکانا جہنم میں نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(39,61,'39:61','اور جولوگ ڈرتے رہے، اللہ ان لوگوں کو کامیابی کے ساتھ نجات دے گا، اوران کو کوئی تکلیف نہ پہنچے گی اور نہ وہ غمگین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(39,62,'39:62','اللہ ہر چیز کاخالق ہے اور وہی ہر چیز پرنگہبان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(39,63,'39:63','آسمانوں اور زمین کی کنجیاں اسی کے پاس ہیں اور جن لوگوں نے اللہ کی آیتوں کا انکار کیا وہی گھاٹے میں رہنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,64,'39:64','کہوکہ اے نادانو، کیا تم مجھ کو غیر اللہ کی عبادت کرنے کے لیے کہتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(39,65,'39:65','اور تمھاري طرف اور تم سے پہلے والوں کی طرف بھی وحی بھیجی جاچکی ہے کہ اگر تم نے شرک کیا تو تمھارا عمل ضائع ہوجائے گااور تم خسارہ میں رہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(39,66,'39:66','بلکہ صرف اللہ کی عبادت کرو اور شکر کرنے والوں میں سے بنو');
INSERT INTO "ur_wahid_uddin" VALUES(39,67,'39:67','اور لوگوں نے اللہ کی قدر نہ کی جیسا کہ اس کی قدر کرنے کا حق ہےاور زمین ساری اس کی مٹھی میں ہوگی قیامت کے دن اور تمام آسمان لپٹے ہوں گے اس کے داہنے ہاتھ میں و ہ پاک اور برتر ہے اس شرک سے جو یہ لوگ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,68,'39:68','اور صورپھونکاجائے گا تو آسمانوں اور زمین میں جو بھی ہیں سب بے ہوش ہوکر گرپڑیں گے،مگر جس کو اللہ چاہےپھردوبارہ اس میں پھونکا جائے گا تو یکایک سب کے سب اُٹھ کر دیکھنے لگیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(39,69,'39:69','اور زمین اپنے رب کے نور سے چمک اُٹھے گی اور کتاب رکھ دی جائے گی اور پیغمبر اور گواہ حاضر کيے جائیں گے اور لوگوں کے درمیان ٹھیک ٹھیک فیصلہ کردیا جائے گا اور ان پر کوئی ظلم نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(39,70,'39:70','اور ہر شخص کو اس کے اعمال کا پورا بدلہ دیا جائے گااور وہ خوب جانتا ہے جو کچھ وہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(39,71,'39:71','اور جن لوگوں نے انکار کیا وہ گروہ گروہ بناکر جہنم کی طرف ہانکے جائیں گےیہاں تک کہ جب وہ اس کے پاس پہنچیں گے اس کے دروازے کھول دئے جائیں گے اور اس کے محافظ ان سے کہیں گے، کیا تمھارے پاس تم ہی لوگوں میں سے پیغمبر نہیں آئے جو تم کو تمھارے رب کی آیتیں سناتے تھے اور تم کو تمھارے اس دن کی ملاقات سے ڈراتے تھےوہ کہیں گے کہ ہاں ، لیکن عذاب کا وعدہ منکروں پر پورا ہوکر رہا');
INSERT INTO "ur_wahid_uddin" VALUES(39,72,'39:72','کہا جائے گا کہ جہنم کے دروازوں میں داخل ہوجاؤ، اس میں ہمیشہ رہنے کے لیے پس کیسا برا ٹھکانا ہے تکبر کرنے والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(39,73,'39:73','اور جو لوگ اپنے رب سے ڈرے وہ گروہ درگروہ جنت کی طرف لے جائے جائیں گے یہاں تک کہ جب وہ وہاں پہنچیں گے اور اس کے دروازے کھول دئے جائیں گے اور اس کے محافظ ان سے کہیں گے کہ سلام ہو تم پر،خوش حال رہو، پس اس میں داخل ہوجاؤ ہمیشہ کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(39,74,'39:74','اور وہ کہیں گے کہ شکرہے اس اللہ کا جس نے ہمارے ساتھ اپنا وعدہ سچ کردکھایا اور ہم کو اس زمین کاوارث بنادیا ہم جنت میں جہاں چاہیں مقام کریں پس کیاخوب بدلہ ہے عمل کرنے والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(39,75,'39:75','اور تم فرشتوں کو دیکھو گے کہ عرش کے گرد حلقہ بنائے ہوئے اپنے رب کی حمد اور تسبیح کرتے ہوں گے اور لوگوں کے درمیان ٹھیک ٹھیک فیصلہ کردیا جائے گا اور کہا جائے گا کہ ساری حمد اللہ کے لیے ہے، عالم کا خداوند');
INSERT INTO "ur_wahid_uddin" VALUES(40,1,'40:1','حم');
INSERT INTO "ur_wahid_uddin" VALUES(40,2,'40:2','یہ کتاب اتاری گئی ہے اللہ کی طرف سے جو زبردست ہے، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,3,'40:3','معاف کرنے والا اور توبہ قبول کرنے والا ہے سخت سزا دینے والا ،بڑی قدرت والا ، اس کے سوا کوئی معبود نہیں اسی کی طرف لوٹنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,4,'40:4','اللہ کی آیتوں میں وہی لوگ جھگڑے نکالتے ہیں جو منکر ہیں ، تو ان لوگوں کا شہروں میں چلنا پھرنا تم کو دھوکے میں نہ ڈالے');
INSERT INTO "ur_wahid_uddin" VALUES(40,5,'40:5','ان سے پہلے نوح کی قوم نے جھٹلایا ،اور ان کے بعد کے گروہ نے بھی اور ہر امت نے ارادہ کیا کہ اپنے رسول کو پکڑلیں اور انھوں نے ناحق کے جھگڑے نکالے، تاکہ وہ اس سے حق کو پسپا کردیں تو میں نے ان کو پکڑلیا پھر کیسی تھی میری سزا');
INSERT INTO "ur_wahid_uddin" VALUES(40,6,'40:6','اور اسی طرح تیرے رب کی بات ان لوگوں پر پوری ہو چکی ہے جنھوں نے انکار کیا کہ وہ آگ والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(40,7,'40:7','جو عرش کو اُٹھائے ہوئے ہیں اور جو اس کے ارد گرد ہیں وہ اپنے رب کی تسبیح کرتے ہیں ، اس کی حمد کے ساتھاور وہ اس پر ایمان رکھتے ہیں اور و ہ ایمان والوں کے لیے مغفرت کی دعا کرتے ہیں اے ہمارے رب، تیری رحمت اور تیرا علم ہر چیز کا احاطہ کيے ہوئے ہے پس تو معاف کردے ان لوگوں کو جو توبہ کریں اور تیرے راستے کی پیروی کریں اور تو ان کو جہنم کے عذاب سے بچا');
INSERT INTO "ur_wahid_uddin" VALUES(40,8,'40:8','اے ہمارے رب، اور تو ان کو ہمیشہ رہنے والے باغوں میں داخل کر جن کا تونے ان سے وعدہ کیا ہے اور ان کو بھی جوصالح ہوں ، ان کے والدین اور ان کی بیویوں اور ان کی اولاد میں سے، بے شک تو زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,9,'40:9','اور ان کو برائیوں سے بچالےاور جس کو تونے اس دن برائیوں سے بچایا تو ان پر تونے رحم کیااور یہی بڑی کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,10,'40:10','جن لوگوں نے انکار کیا، ان کو پکار کر کہا جائے گا، خدا کی بیزاری تم سے اس سے زیادہ ہے جتنی بیزاری تم کو اپنے آپ پرہےجب تم کو ایمان کی طرف بلایا جاتا تھا تو تم انکار کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(40,11,'40:11','وہ کہیں گے کہ اے ہمارے رب، تونے ہم کو دوبارموت دی اور دوبارہم کو زندگی دیپس ہم نے اپنے گناہوں کا اقرار کیا، تو کیا نکلنے کی کوئی صورت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,12,'40:12','یہ تم پر اس لیے ہے کہ جب اکیلے اللہ کی طرف بلایا جاتا تھا تو تم انکار کرتے تھےاور جب اس کے ساتھ شریک کیا جاتا تو تم مان لیتے پس فیصلہ اللہ کے اختیار میں ہے جوعظیم ہے، بڑے مرتبہ والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,13,'40:13','وہی ہے جو تم کو اپنی نشانیاں دکھاتا ہے اور آسمان سے تمھارے لیے رزق اُتارتا ہے اور نصیحت صرف وہی شخص قبول کرتا ہے جو اللہ کی طرف رجوع کرنے والا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(40,14,'40:14','پس اللہ ہی کو پکارو، دین کو اسی کے لیے خالص کرکے ،خواہ منکروں کو ناگوار کیوں نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(40,15,'40:15','وہ بلند درجوں والا، عرش کامالک ہےوہ اپنے بندوں میں سے جس پر چاہتا ہے وحی بھیجتا ہے، تاکہ وہ ملاقات کے دن سے ڈرائے');
INSERT INTO "ur_wahid_uddin" VALUES(40,16,'40:16','جس دن کہ وہ ظاہر ہوں گے اللہ سے ان کی کوئی چیز چھپی ہوئی نہ ہوگیآج بادشاہی کس کی ہےاللہ واحد قہار کی');
INSERT INTO "ur_wahid_uddin" VALUES(40,17,'40:17','آج ہر شخص کو اس کے کيے کا بدلہ ملے گاآج کوئی ظلم نہ ہوگابے شک اللہ جلد حساب لینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,18,'40:18','اور ان کو قر یب آنے والی مصیبت کے دن سے ڈراؤ جب کہ دل حلق تک آپہنچیں گے،وہ غم سے بھرے ہوئے ہوں گے ظالموں کا نہ کوئی دوست ہوگا اور نہ کوئی سفارشی جس کی بات مانی جائے');
INSERT INTO "ur_wahid_uddin" VALUES(40,19,'40:19','وہ نگاہوں کی خیانت کو جانتا ہے اور ان باتوں کو بھی جن کو سینے چھپائے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(40,20,'40:20','اور اللہ حق کے ساتھ فیصلہ کرے گااور جن کو وہ اللہ کے سوا پکارتے ہیں ، وہ کسی چیز کا فیصلہ نہیں کرتےبے شک اللہ سننے والا ہے،دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,21,'40:21','کیا وہ زمین میں چلے پھرے نہیں کہ وہ دیکھتے کہ کیا انجام ہوا ان لوگوں کا جو ان سے پہلے گزرچکے ہیں وہ ان سے بہت زیادہ تھے قوت میں اور ان آثار کے اعتبار سے بھی جو انھوں نے زمین میں چھوڑےپھر اللہ نے ان کے گناہوں پر ان کو پکڑلیا اور کوئی ان کو اللہ سے بچانے والا نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(40,22,'40:22','یہ اس لیے ہوا کہ ان کے پاس ان کے رسول کھلی ہوئی نشانیاں لے کر آئے تو انھوں نے انکار کیا تو اللہ نے ان کو پکڑ لیا یقیناً وہ طاقت ور ہے سخت سزادینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,23,'40:23','اور ہم نے موسیٰ کو اپنی نشانیوں کے ساتھ اور کھلی ہوئی دلیل کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(40,24,'40:24','فرعون اور ہامان اور قارون کے پاس بھیجا، تو انھوں نے کہا کہ یہ ایک جادوگر ہے، جھوٹا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,25,'40:25','پھر جب وہ ہماری طرف سے حق لے کر ان کے پاس پہنچا،انھوں نے کہا کہ ان لوگوں کے بیٹوں کو قتل کرڈالو جو اس کے ساتھ ایمان لائیں اور ان کی عورتوں کو زندہ رکھو اور ان منکروں کی تدبیر محض بے اثر رہی');
INSERT INTO "ur_wahid_uddin" VALUES(40,26,'40:26','اور فرعون نے کہا، مجھ کو چھوڑو، میں موسیٰ کو قتل کرڈالوں اور وہ اپنے رب کو پکارے، مجھ کو اندیشہ ہے کہ کہیں وہ تمھارا دین بدل ڈالے یا ملک میں فساد پھیلادے');
INSERT INTO "ur_wahid_uddin" VALUES(40,27,'40:27','اور موسیٰ نے کہا کہ میں نے اپنے اور تمھارے رب کی پناہ لی ہر اس متکبر سے جو حساب کے دن پر ایمان نہیں رکھتا');
INSERT INTO "ur_wahid_uddin" VALUES(40,28,'40:28','اور آلِ فرعون میں سے ایک مومن شخص، جو اپنے ایمان کو چھپائے ہوئے تھا، وہ بولا کیاتم لوگ ایک شخص کو صرف اس بات پر قتل کردوگے کہ وہ کہتا ہے کہ میرا رب اللہ ہے،حالاں کہ وہ تمھارے رب کی طرف سے کھلی ہوئی دلیلیں بھی لے کر آیا ہےاور اگر وہ جھوٹا ہے تو اس کا جھوٹ اسی پر پڑے گااور اگر وہ سچا ہے تو اس کا کوئی حصہ تم کو پہنچ کررہے گا جس کا وعدہ وہ تم سے کرتاہے بے شک اللہ ایسے شخص کو ہدایت نہیں دیتا جو حد سے گزرنے والا ہو، جھوٹا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(40,29,'40:29','اے میری قوم، آج تمھاری سلطنت ہے کہ تم زمین میں غالب ہو پھر اللہ کے عذاب کے مقابل ہماری کون مدد کرے گا، اگر وہ ہم پر آگیافرعون نے کہا، میں تم کو وہی رائے دیتاہوں جس کو میں سمجھ رہا ہوں ، اور میں تمھاری رہنمائی ٹھیک بھلائی کے راستہ کی طرف کررہا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(40,30,'40:30','اور جوشخص ایمان لایا تھا اس نے کہا کہ اے میری قوم ، میں ڈرتا ہوں کہ تم پراور گروہوں جیسا دن آجائے');
INSERT INTO "ur_wahid_uddin" VALUES(40,31,'40:31','جیسا دن قوم نوح اور عاد اور ثمود اور ان کے بعد والوں پر آیااور اللہ اپنے بندوں پر کوئی ظلم کرنا نہیں چاہتا');
INSERT INTO "ur_wahid_uddin" VALUES(40,32,'40:32','اور اے میری قوم، میں ڈرتاہوں کہ تم پر چیخ پکار کادن آجائے');
INSERT INTO "ur_wahid_uddin" VALUES(40,33,'40:33','جس دن تم پیٹھ پھیر کر بھاگو گےاور تم کو خدا سے بچانے والا کوئی نہ ہوگااور جس کو خدا گمراہ کردے اس کو کوئی ہدایت دینے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(40,34,'40:34','اور اس سے پہلے یوسف تمھارے پاس کھلے ہوئے دلائل کے ساتھ آئے تو تم ان کی لائی ہوئی باتوں کی طرف سے شک ہی میں پڑے رہے،یہاں تک کہ جب ان کی وفات ہوگئی تو تم نے کہا کہ اللہ ان کے بعد ہرگز کوئی رسول نہ بھیجے گا اسی طرح اللہ ان لوگوں کو گمراہ کردیتا ہے جو حد سے گزرنے والے اور شک کرنے والے ہوتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(40,35,'40:35','جو اللہ کی آیتوں میں جھگڑا کرتے ہیں بغیر کسی دلیل کے جو ان کے پاس آئی ہو اللہ اور ایمان والوں کے نزدیک یہ سخت مبغوض ہے اسی طرح اللہ مہر کردیتا ہے ہر مغرور،سرکش کے دل پر');
INSERT INTO "ur_wahid_uddin" VALUES(40,36,'40:36','اور فرعون نے کہا کہ اے ہامان، میرے لیے ایک اونچی عمارت بنا،تاکہ میں راستوں پر پہنچوں');
INSERT INTO "ur_wahid_uddin" VALUES(40,37,'40:37','آسمانوں کے راستوں تک، پس موسیٰ کے معبود کو جھانک کردیکھوں ، اور میں تو اس کو جھوٹا خیال کرتا ہوں اور اس طرح فرعون کے لیے اس کی بدعملی خوشنما بنادی گئی اور وہ سیدھے راستے سے روک دیاگیااور فرعون کی تدبیر غارت ہوکر رہی');
INSERT INTO "ur_wahid_uddin" VALUES(40,38,'40:38','اور جوشخص ایما ن لایا تھا اس نے کہا کہ اے میری قوم، تم میری پیروی کرو، میں تم کو صحیح راستہ بتارہا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(40,39,'40:39','اے میری قوم، یہ دنیا کی زندگی محض چند روزہ ہے اور اصل ٹھہرنے کا مقام آخرت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,40,'40:40','جو شخص برائی کرے گا تو وہ اس کے برابر بدلہ پائے گااور جو شخص نیک کام کرے گا، خواہ وہ مرد ہو یا عورت،بشرطیکہ وہ مومن ہو تو یہی لوگ جنت میں داخل ہوں گے، وہاں وہ بے حساب رزق پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(40,41,'40:41','اور اے میری قوم، کیا بات ہے کہ میں تو تم کو نجات کی طرف بلاتا ہوں اور تم مجھ کو آگ کی طرف بلارہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(40,42,'40:42','تم مجھ کو بلارہے ہوکہ میں خدا کے ساتھ کفر کروں اور ایسی چیز کو اس کا شریک بناؤں جس کا مجھے کوئی علم نہیں اور میں تم کو زبردست مغفرت کرنے والے خدا کی طرف بلارہاہوں');
INSERT INTO "ur_wahid_uddin" VALUES(40,43,'40:43','یقینی بات ہے کہ تم جس چیزکی طرف مجھ کو بلاتے ہو اس کی کوئی آواز نہ دنیا میں ہے اور نہ آخرت میں اور بے شک ہم سب کی واپسی اللہ ہی کی طرف ہے اور حدسے گزرنے والے ہی آگ میں جانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(40,44,'40:44','پس تم آگے چل کر میری بات کو یاد کروگےاور میں اپنا معاملہ اللہ کے سپرد کرتا ہوں بے شک اللہ تمام بندوں کا نگراں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,45,'40:45','پھر اللہ نے اس کو ان کی بری تدبیروں سے بچالیااور فرعون والوں کو برے عذاب نے گھیرلیا');
INSERT INTO "ur_wahid_uddin" VALUES(40,46,'40:46','آگ ، جس پر وہ صبح و شام پیش کيے جاتے ہیں اور جس دن قیامت قائم ہوگی، فرعون والوں کو سخت ترین عذاب میں داخل کرو');
INSERT INTO "ur_wahid_uddin" VALUES(40,47,'40:47','اور جب وہ دوزخ میں ایک دوسرے سے جھگڑیں گے تو کمزور لوگ بڑا بننے والوں سے کہیں گے کہ ہم تمھارے تابع تھے، تو کیا تم ہم سے آگ کا کوئی حصہ ہٹاسکتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(40,48,'40:48','بڑے لوگ کہیں گے کہ ہم سب ہی اس میں ہیں اللہ نے بندوں کے درمیان فیصلہ کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(40,49,'40:49','اور جو لوگ آگ میں ہوں گے وہ جہنم کے نگہبانوں سے کہیں گے کہ تم اپنے رب سے درخواست کرو کہ وہ ہمارے عذاب میں سے ایک دن کی تخفیف کردے');
INSERT INTO "ur_wahid_uddin" VALUES(40,50,'40:50','وہ کہیں گے، کیا تمھارے پاس تمھارے رسول واضح دلیلیں لے کر نہیں آئے وہ کہیں گے کہ ہاں ،نگہبان کہیں گے پھر تم ہی درخواست کرواور منکروں کی پکار اکارت ہی جانے والی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,51,'40:51','بے شک ہم مدد کرتے ہیں اپنے رسولوں کی اور ایمان والوں کی دنیا کی زندگی میں ، اور اس دن بھی جب کہ گواہ کھڑے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(40,52,'40:52','جس دن ظالموں کو ان کی معذرت کچھ فائدہ نہ دے گی اور ان کے لیے لعنت ہوگی اور ان کے لیے برا ٹھکانا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(40,53,'40:53','اور ہم نے موسیٰ کو ہدایت عطا کی اور بنی اسرائیل کو کتاب کا وارث بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(40,54,'40:54','رہنمائی اور نصیحت عقل والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(40,55,'40:55','پس تم صبر کرو،بے شک اللہ کاوعدہ برحق ہے اور اپنے قصور کی معافی چاہواور صبح وشام اپنے رب کی تسبیح کرو اس کی حمد کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(40,56,'40:56','جو لوگ کسی سند کے بغیر جو ان کے پاس آئی ہو، اللہ کی آیتوں میں جھگڑے نکالتے ہیں ، ان کے دلوں میں صرف بڑائی ہے کہ وہ اس تک کبھی پہنچنے والے نہیں ، پس تم اللہ کی پناہ مانگو، بے شک وہ سُننے والا ہے، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,57,'40:57','یقیناً آسمانوں اور زمین کا پیدا کرنا انسانوں کو پیدا کرنے کی نسبت زیادہ بڑا کام ہے، لیکن اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(40,58,'40:58','اور اندھا اور آنکھوں والا یکساں نہیں ہوسکتا، اور نہ ایمان دار اور نیکو کار اور وہ جوبرائی کرنے والے ہیں تم لوگ بہت کم سوچتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(40,59,'40:59','بے شک قیامت آکر رہے گی اس میں کوئی شک نہیں ، مگر اکثر لوگ نہیں مانتے');
INSERT INTO "ur_wahid_uddin" VALUES(40,60,'40:60','اور تمھارے رب نے فرما دیا ہے کہ مجھ کو پکارو، میں تمھاری درخواست قبول کروں گا جو لوگ میری عبادت سے سرتابی کرتے ہیں وہ عنقریب ذلیل ہوکر جہنم میں داخل ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(40,61,'40:61','اللہ ہی ہے جس نے تمھارے لیے رات بنائی تاکہ تم اس میں آرام کرو،اور دن کو روشن کیا بے شک اللہ لوگوں پر بڑا فضل کرنے والا ہے مگر اکثر لوگ شکر نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(40,62,'40:62','یہی اللہ تمھارا رب ہے، ہر چیز کا پیدا کرنے والا، اس کے سوا کوئی معبود نہیں پھر تم کہاں سے بہکائے جاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(40,63,'40:63','اسی طرح وہ لوگ بہکائے جاتے رہے ہیں جو اللہ کی آیتوں کاانکار کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(40,64,'40:64','اللہ ہی ہے جس نے تمھارے لیے زمین کو ٹھہرنے کی جگہ بنایا اور آسمان کو چھت بنایا اور تمھارا نقشہ بنایا پس عمدہ نقشہ بنایااور اس نے تم کو عمدہ چیزوں کا رزق دیایہ اللہ ہے تمھارا رب، پس بڑا ہی بابرکت ہے اللہ جو رب ہے سارے جہان کا');
INSERT INTO "ur_wahid_uddin" VALUES(40,65,'40:65','وہی زندہ ہے اس کے سوا کوئی معبود نہیں پس تم اسی کو پکارو، دین کو اسی کے لیے خالص کرتے ہوئےساری تعریف اللہ کے لیے جورب ہے سارے جہان کا');
INSERT INTO "ur_wahid_uddin" VALUES(40,66,'40:66','کہو، مجھے اس سے منع کردیا گیا ہے کہ میں ان کی عبادت کروں جن کو تم اللہ کے سوا پکارتے ہو، جب کہ میرے پاس کھلی ہوئی دلیلیں آچکیں اور مجھ کو حکم دیا گیا ہے کہ میں اپنے آپ کو رب العالمین کے حوالے کردوں');
INSERT INTO "ur_wahid_uddin" VALUES(40,67,'40:67','وہی ہے جس نے تم کو مٹی سے پیداکیا،، پھر نطفہ سے، پھر خون کے لوتھڑے سے، پھر وہ تم کو بچے کی شکل میں نکالتا ہے، پھر وہ تم کو بڑھاتا ہے تاکہ تم اپنی پوری طاقت کو پہنچو، پھر تاکہ تم بوڑھے ہوجاؤاور تم میں سے کوئی پہلے ہی مرجاتا ہےاور تاکہ تم مقرر وقت تک پہنچ جاؤ اور تاکہ تم سوچو');
INSERT INTO "ur_wahid_uddin" VALUES(40,68,'40:68','وہی ہے جو جِلاتا ہے اورمارتا ہے پس جب وہ کسی امرکا فیصلہ کرلیتا ہے تو بس وہ اس کو کہتا ہے کہ ہوجا،بس وہ ہوجاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,69,'40:69','کیاتم نے ان لوگوں کو نہیں دیکھا جواللہ کی آیتوں میں جھگڑے نکالتے ہیں وہ کہاں سے پھیرے جاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(40,70,'40:70','جنھوں نے کتاب کو جھٹلایا اور اس چیز کو بھی جس کے ساتھ ہم نے اپنے رسولوں کوبھیجا، تو عنقریب وہ جانیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(40,71,'40:71','جب کہ ان کی گردنوں میں طوق ہوں گے اور زنجیریں ، وہ گھسیٹے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(40,72,'40:72','جلتے ہوئے پانی میں پھر وہ آگ میں جھونک دئے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(40,73,'40:73','پھر ان سے کہا جائے گا، کہاں ہیں وہ جن کو تم شریک کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(40,74,'40:74','اللہ کے سوا وہ کہیں گے— وہ ہم سے کھوئے گئے بلکہ ہم اس سے پہلے کسی چیز کو پکارتے نہ تھےاس طرح اللہ گمراہ کرتا ہے منکروں کو');
INSERT INTO "ur_wahid_uddin" VALUES(40,75,'40:75','یہ اس سبب سے کہ تم زمین میں ناحق خوش ہوتے تھے اور اس سبب سے کہ تم گھمنڈ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(40,76,'40:76','جہنم کے دروازوں میں داخل ہوجاؤ ، اس میں ہمیشہ رہنے کے لیےپس کیسا برا ٹھکانا ہے گھمنڈ کرنے والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(40,77,'40:77','پس صبر کرو، بے شک اللہ کاوعدہ برحق ہےپھر جس کا ہم ان سے وعدہ کررہے ہیں اس کاکچھ حصہ ہم تم کو دکھا دیں گے،یا تم کووفات دیں گے، پس ان کی واپسی ہماری ہی طرف ہے');
INSERT INTO "ur_wahid_uddin" VALUES(40,78,'40:78','اور ہم نے تم سے پہلے بہت سے رسول بھیجے، ان میں سے کچھ کے حالات ہم نے تم کوسنائے ہیں اور ان میں کچھ ایسے بھی ہیں جن کے حالات ہم نے تم کو نہیں سنائےاور کسی رسول کا یہ مقدور نہ تھا کہ وہ اللہ کے اذن کے بغیر کوئی نشانی لے آئے پھر جب اللہ کا حکم آگیا تو حق کے مطابق فیصلہ کردیا گیااور غلط کار لوگ اس وقت خسارے میں رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(40,79,'40:79','اللہ ہی ہے جس نے تمھارے لیے مویشی بنائے تاکہ تم بعض سے سواری کا کام لو اور ان میں سے بعض کو تم کھاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(40,80,'40:80','اور تمھارے لیے ان میں اور بھی فائدے ہیں اور تاکہ تم ان کے ذریعہ سے اپنی حاجت تک پہنچو جو تمھارے دلوں میں ہو اور ان پراور کشتی پر تم سوار کيے جاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(40,81,'40:81','اوروہ تم کواور بھی نشانیاں دکھاتا ہے تو تم اللہ کی کن کن نشانیوں کاانکار کروگے');
INSERT INTO "ur_wahid_uddin" VALUES(40,82,'40:82','کیا وہ زمین میں چلے پھرے نہیں کہ وہ دیکھتے کہ کیا انجام ہوا ان لوگوں کا جو ان سے پہلے گزرے ہیں وہ ان سے زیادہ تھے، اور قوت میں اور نشانیوں میں ، جو کہ وہ زمین پر چھوڑگئے، وہ ان سے بڑھے ہوئے تھےپس ان کی کمائی ان کے کچھ کام نہ آئی');
INSERT INTO "ur_wahid_uddin" VALUES(40,83,'40:83','پس جب ان کے پیغمبران کے پاس کھلی ہوئی دلیلیں لے کر آئے تو وہ اپنے اس علم پر نازاں رہے جو ان کے پاس تھا، اوران پروہ عذاب آپڑا جس کا وہ مذاق اُڑاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(40,84,'40:84','پھر جب انھوں نے ہمارا عذاب دیکھا،وہ کہنے لگے کہ ہم اللہ واحد پر ایمان لائے اور ہم انکار کرتے ہیں جن کو ہم اس کے ساتھ شریک کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(40,85,'40:85','پس ان کا ایمان ان کے کام نہ آیا جب کہ انھوں نے ہمارا عذاب دیکھ لیا یہی اللہ کی سنت ہے جو اس کے بندوں میں جاری رہی ہے، اور اس وقت انکار کرنے والے خسارے میں رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(41,1,'41:1','حٰمٓ');
INSERT INTO "ur_wahid_uddin" VALUES(41,2,'41:2','یہ بڑے مہربان،نہایت رحم والے کی طرف سے اُتارا ہوا کلام ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,3,'41:3','یہ ایک کتاب ہے جس کی آیتیں کھول کھول کربیان کی گئی ہیں ، عربی زبان کاقرآن ،ان لوگوں کے لیے جوعلم رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(41,4,'41:4','خوش خبری دینے والا اور ڈرانے والاپس ان لوگوں میں سے اکثر نے اس سے اعراض کیاپس وہ نہیں سن رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(41,5,'41:5','اورانھوں نے کہاہمارے دل اس سے پردے میں ہیں جن کی طرف تم ہم کو بلاتے ہو اور ہمارے کانوں میں ڈاٹ ہےاور ہمارے اور تمھارے درمیان میں ایک حجاب ہے پس تم اپنا کام کرو، ہم بھی اپنا کام کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(41,6,'41:6','کہو، میں تو ایک بشرہوں تم جیسامیرے پاس یہ وحی آتی ہے کہ تمھارا معبود بس ایک ہی معبود ہے، پس تم سیدھے رہو اسی کی طرف اور اس سے معافی چاہواور خرابی ہے مشرکوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(41,7,'41:7','جو زکوٰۃ نہیں دیتے اوروہ آخرت کے منکر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(41,8,'41:8','بے شک جولوگ ایمان لائے اور انھوں نے نیک عمل کیاان کے لیے ایسا اجر ہے جو ختم ہونے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(41,9,'41:9','کہو کیا تم لوگ اس ہستی کا انکار کرتے ہو جس نے زمین کو دودن میں بنایا، اور تم اس کے ہم سر ٹھہراتے ہو، وہ رب ہے تمام جہان والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(41,10,'41:10','اور اس نے زمین میں اس کے اوپر پہاڑ بنائےاور اس میں فائدے کی چیزیں رکھ دیں اور اس میں اس کی غذائیں ٹھہرادیں چار دن میں ، پورا ہوا پوچھنے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(41,11,'41:11','پھر وہ آسمان کی طرف متوجہ ہوااور وہ دھواں تھاپھر اس نے آسمان اور زمین سے کہا کہ تم دونوں آؤ خوشی سے یا ناخوشی سے دونوں نے کہا کہ ہم خوشی سے حاضر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(41,12,'41:12','پھر اس نے دو دن میں اس کے سات آسمان بنائے اور ہر آسمان میں اُس کاحکم بھیج دیااور ہم نے آسمانِ دنیا کوچراغوں سے زینت دی، اور اس کو محفوظ کردیا یہ عزیز وعلیم کی منصوبہ بندی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,13,'41:13','پس اگر وہ اعراض کرتے ہیں تو کہوکہ تم کو اسی طرح کے عذاب سے ڈراتا ہوں جیسا عذاب عاداورثمود پرنازل ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(41,14,'41:14','جب کہ ان کے پاس رسول آئے، ان کے آگے سے اور ان کے پیچھے سے کہ اللہ کے سوا تم کسی کی عبادت نہ کروانھوں نے کہا کہ اگرہمارا رب چاہتا تو وہ فرشتہ اُتارتا، پس ہم اس چیزکے منکر ہیں جس کو دے کر تم بھیجے گئے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(41,15,'41:15','عاد کا یہ حال تھا کہ انھوں نے زمین میں بغیر کسی حق کے گھمنڈ کیا، اورانھوں نے کہا، کون ہے جوقوت میں ہم سے زیادہ ہے کیا انھوں نے نہیں دیکھا کہ جس خدا نے ان کو پیدا کیا ہے وہ قوت میں ان سے زیادہ ہے اور وہ ہماری نشانیوں کاانکار کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,16,'41:16','تو ہم نے چند منحوس دنوں میں ان پر سخت طوفانی ہوا بھیج دی تاکہ ان کو دنیا کی زندگی میں رسوائی کاعذاب چکھائیں ، اور آخرت کاعذاب اس سے بھی زیادہ رسوا کن ہے اور ان کوکوئی مددنہ پہنچے گی');
INSERT INTO "ur_wahid_uddin" VALUES(41,17,'41:17','اوروہ جوثمود تھے، تو ہم نے ان کو ہدایت کاراستہ دکھایا مگر انھوں نے ہدایت کے مقابلہ میں اندھے پن کو پسند کیا، تو ان کو عذاب ذلت کے کڑکے نے پکڑلیا ان کی بدکرداریوں کی وجہ سے');
INSERT INTO "ur_wahid_uddin" VALUES(41,18,'41:18','اور ہم نے ان لوگوں کو نجات دی جو ایمان لائے اور ڈرنے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(41,19,'41:19','اور جس دن اللہ کے دشمن آ گ کی طرف جمع کيے جائیں گے، پھروہ جدا جدا کيے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(41,20,'41:20','یہاں تک کہ جب وہ اس کے پاس آجائیں گے،ان کے کان اور ان کی آنکھیں اوران کی کھالیں ان پر ان کے اعمال کی گواہی دیں گی');
INSERT INTO "ur_wahid_uddin" VALUES(41,21,'41:21','اور وہ اپنی کھالوں سے کہیں گے، تم نے ہمارے خلاف کیوں گواہی دی وہ کہیں گی کہ ہم کو اسی اللہ نے گویائی دی ہے جس نے ہر چیز کو بولنے والا بناديا ہے اوراسی نے تم کو پہلی مرتبہ پیداکیا اور اسی کے پاس تم لائے گئے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(41,22,'41:22','اور تم اپنے کو اس سے چھپانہ سکتے تھے کہ تمھارے کان اور تمھاری آنکھیں اور تمھاری کھالیں تمھارے خلاف گواہی دیں ، لیکن تم اس گمان میں رہے کہ اللہ تمھارے بہت سے ان اعما ل کو نہیں جانتا جوتم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(41,23,'41:23','اور تمھارے اسی گمان نے جو کہ تم نے اپنے رب کے ساتھ کیا تھا تم کو برباد کیا، پس تم خسارہ اٹھانے والوں میں سے ہوگئے');
INSERT INTO "ur_wahid_uddin" VALUES(41,24,'41:24','پس اگر وہ صبر کریں تو آگ ہی ان کا ٹھکانا ہے،اور اگر وہ معافی مانگیں تو ان کو معافی نہیں ملے گی');
INSERT INTO "ur_wahid_uddin" VALUES(41,25,'41:25','اور ہم نے ان پر کچھ ساتھی مسلط کردئے تو انھوں نے ان کے آگے اور پیچھے کی ہر چیز ان کو خوش نما بناکر دکھایااور ان پر وہی بات پوری ہوکر رہی جو جنوں اورانسانوں کے ان گرو ہوں پر پوری ہوئی جو ان سے پہلے گزرچکے تھے بے شک وہ خسارے میں رہ جانے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(41,26,'41:26','اور کفر کرنے والوں نے کہا اس قرآن کونہ سنواور اس میں خلل ڈالو، تاکہ تم غالب رہو');
INSERT INTO "ur_wahid_uddin" VALUES(41,27,'41:27','پس ہم انکار کرنے والوں کو سخت عذاب چکھائیں گے اور ان کو ان کے عمل کا بدترین بدلہ دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(41,28,'41:28','یہ اللہ کے دشمنوں کا بدلہ ہے، یعنی آگ ان کے لیے اس میں ہمیشگی کاٹھکانا ہوگا،اس بات کے بدلے میں کہ وہ ہماری آیتوں کاانکار کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(41,29,'41:29','اور کفر کرنے والے کہیں گے کہ اے ہمارے رب، ہمیں ان لوگوں کودکھا جنھوں نے جنوں اور انسانوں میں سے ہم کو گمراہ کیا، ہم ان کو اپنے پاؤں کے نیچے ڈالیں گے،تاکہ وہ ذلیل ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(41,30,'41:30','جن لوگوں نے کہا کہ اللہ ہمارا رب ہے،پھر وہ ثابت قدم رہے، یقیناً ان پر فرشتے اترتے ہیں اور ان سے کہتے ہیں کہ تم نہ اندیشہ کرو اور نہ رنج کرو اور اس جنت کی بشارت سے خوش ہوجاؤ جس کا تم سے وعدہ کیاگیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,31,'41:31','ہم دنیا کی زندگی میں تمھارے ساتھی ہیں اور آخرت میں بھی اور تمھارے لیے وہاں ہر چیز ہے جس کا تمھارا دل چاہے اور تمھارے لیے اس میں ہروہ چیز ہے جو تم طلب کروگے');
INSERT INTO "ur_wahid_uddin" VALUES(41,32,'41:32','غفور رحیم کی طرف سے مہمانی کے طور پر');
INSERT INTO "ur_wahid_uddin" VALUES(41,33,'41:33','اوراس سے بہتر کس کی بات ہوگی جس نے اللہ کی طرف بلایا اور نیک عمل کیا اور کہا کہ میں فرماں برداروں میں سے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(41,34,'41:34','اور بھلائی اوربرائی دونوں برابر نہیں ،تم جواب میں وہ کہو جو اس سے بہتر ہو پھر تم دیکھو گے کہ تم میں اور جس میں دشمنی تھی، وہ ایسا ہوگیا جیسے کوئی دوست قرابت والا');
INSERT INTO "ur_wahid_uddin" VALUES(41,35,'41:35','اور یہ بات اسی کوملتی ہے جو صبر کرنے والے ہیں ، اور یہ بات اسی کو ملتی ہے جو بڑا نصیب والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,36,'41:36','اور اگر شیطان تمھارے دل میں کچھ وسوسہ ڈالے تو اللہ کی پناہ مانگوبے شک وہ سننے والا، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,37,'41:37','اوراس کی نشانیوں میں سے ہے رات اور دن اور سورج اور چاندتم سورج اور چاند کو سجدہ نہ کرو بلکہ اس اللہ کو سجدہ کرو جس نے ان سب کو پیدا کیا، اگر تم اسی کی عبادت کرنے والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(41,38,'41:38','پس اگر وہ تکبرکریں تو جو لوگ تیرے رب کے پاس ہیں وہ شب و روز اسی کی تسبیح کرتے ہیں اور وہ کبھی نہیں تھکتے');
INSERT INTO "ur_wahid_uddin" VALUES(41,39,'41:39','اوراس کی نشانیوں میں سے یہ ہے کہ تم زمین کوفرسودہ حالت میں دیکھتے ہو،پھر جب ہم اس پر پانی برساتے ہیں تو وہ ابھرتی ہے اور پھول جاتی ہےبے شک جس نے اس کوزندہ کردیا، وہ مُردوں کو بھی زندہ کردینے والا ہے بے شک وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,40,'41:40','جولوگ ہماری آیتوں کو الٹے معنی پہناتے ہیں وہ ہم سے چھپے ہوئے نہیں ہیں کیاجو آگ میں ڈالا جائے گا وہ اچھا ہے یا وہ شخص جو قیامت کے دن امن کے ساتھ آئے گا جو کچھ چاہے کرلو، بے شک وہ دیکھتا ہے جو تم کررہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(41,41,'41:41','جن لوگوں نے اللہ کی نصیحت کا انکار کیا جب کہ وہ ان کے پاس آگئی،اور بے شک یہ ایک زبردست کتاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,42,'41:42','اس میں باطل نہ اس کے آگے سے آسکتا ہے اور نہ اس کے پیچھے سے، یہ حکیم اورحمید کی طرف سے اتاری گئی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,43,'41:43','تم کو وہی باتیں کہی جارہی ہیں جو تم سے پہلے رسولوں کو کہی گئی ہیں بے شک تمھارا رب مغفرت والا ہے اور دردناک سزادینے والا بھی');
INSERT INTO "ur_wahid_uddin" VALUES(41,44,'41:44','اور اگر ہم اس کو عجمی قرآن بناتے تو وہ کہتے کہ اس کی آیتیں صاف صاف کیوں نہیں بیان کی گئیں کیا عجمی کتاب اور عربی لوگکہو کہ وہ ایمان لانے والوں کے لیے تو ہدایت اور شفا ہے،اور وہ لوگ جو ایمان نہیں لاتے ان کے کانوں میں ڈاٹ ہے اور وہ ان کے حق میں اندھا پن ہے یہ لوگ گویا کہ دور کی جگہ سے پکارے جارہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(41,45,'41:45','اور ہم نے موسیٰ کو کتاب دی تھی تو اس میں اختلاف پیدا کیا گیااور اگر تیرے رب کی طرف سے ایک بات پہلے طے نہ ہوچکی ہوتی تو ان کے درمیان فیصلہ کردیا جاتااور یہ لوگ اس کی طرف سے ایسے شک میں ہیں جس نے ان کوتردد میں ڈال رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,46,'41:46','جوشخص نیک عمل کرے گا تو اپنے ہی لیے کرے گا اور جو شخص برائی کرے گا تو اس کاوبال اسی پر آئے گااور تیرا رب بندوں پر ظلم کرنے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(41,47,'41:47','قیامت کاعلم اللہ ہی سے متعلق ہےاور کوئی پھل اپنے خول سے نہیں نکلتا اور نہ کوئی عورت حاملہ ہوتی اور نہ جنتی ہے، مگر یہ سب اس کی اطلاع سے ہوتا ہےاور جس دن اللہ ان کو پکارے گا کہ میرے شریک کہاں ہیں ، وہ کہیں گے کہ ہم آپ سے یہی عرض کرتے ہیں کہ ہم میں کوئی اس کامدعی نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(41,48,'41:48','اور جن کو وہ پہلے پکارتے تھے وہ سب ان سے گم ہوجائیں گے،اور وہ سمجھ لیں گے کہ ان کے لیے کوئی بچاؤ کی صورت نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(41,49,'41:49','اور انسان بھلائی مانگنے سے نہیں تھکتا،اور اگر اس کوکوئی تکلیف پہنچ جائے تو مایوس اوردل شکستہ ہوجاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,50,'41:50','اور اگر ہم اس کو تکلیف کے بعد جو کہ اسے پہنچی تھی، اپنی مہربانی کامزہ چکھادیتے ہیں تو وہ کہتا ہے یہ تو میرا حق ہی ہے،اور میں نہیں سمجھتا کہ قیامت کبھی آئے گیاور اگر میں اپنے رب کی طرف لوٹا یا گیا تو اس کے پاس بھی میرے لیے بہتری ہی ہےپس ہم ان منکروں کو ان کے اعمال سے ضرور آگاہ کریں گےاور ان کو سخت عذاب کامزہ چکھائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(41,51,'41:51','اور جب ہم انسان پرفضل کرتے ہیں تو وہ اعراض کرتا ہے اور اپنی کروٹ پھیرلیتا ہےاور جب اس کو تکلیف پہنچتی ہے تو وہ لمبی لمبی دعائیں کرنے والا بن جاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,52,'41:52','کہو کہ بتاؤ، اگر یہ قرآن اللہ کی طرف سے آیا ہو،پھر تم نے اس کا انکار کیا تو اس شخص سے زیادہ گمراہ اور کون ہوگا جو مخالفت میں بہت دور چلاجائے');
INSERT INTO "ur_wahid_uddin" VALUES(41,53,'41:53','ہم ان کواپنی نشانیاں دکھائیں گے آفاق میں بھی اور خود ان کے اندر بھییہاں تک کہ ان پر ظاہر ہوجائے گا کہ یہ حق ہےاور کیا یہ بات کافی نہیں کہ تیرا رب ہر چیز کاگواہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(41,54,'41:54','سن لو،یہ لوگ اپنے رب کی ملاقات میں شک رکھتے ہیں سن لو، وہ ہر چیز کااحاطہ کيے ہوئے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,1,'42:1','حم');
INSERT INTO "ur_wahid_uddin" VALUES(42,2,'42:2','عسق');
INSERT INTO "ur_wahid_uddin" VALUES(42,3,'42:3','اسی طرح اللہ غالب وحکیم وحی کرتا ہے تمھاری طرف اور ان کی طرف جو تم سے پہلے گزرے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,4,'42:4','اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے، وہ سب سے اوپر ہے، سب سے بڑا');
INSERT INTO "ur_wahid_uddin" VALUES(42,5,'42:5','قریب ہے کہ آسمان اپنے اوپر سے پھٹ پڑے، اور فرشتے اپنے رب کی تسبیح کرتے ہیں اسی کی حمد کے ساتھ اور زمین والوں کے لیے معافی مانگتے ہیں سن لو کہ اللہ ہی معاف کرنے والا،رحمت کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,6,'42:6','اور جن لوگوں نے اس کے سوا دوسرے کارساز بنائے ہیں ،اللہ ان کے اوپر نگہبان ہے اور تم ان کے اوپر ذمہ دار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,7,'42:7','اور ہم نے اسی طرح تمھاری طرف عربی قرآن اُتارا ہے تاکہ تم مکہ والوں کو اور اس کے آس پاس والوں کوڈرادو، اور ان کو جمع ہونے کے دن سے ڈرا دو جس کے آنے میں کوئی شک نہیں ایک گروہ جنت میں ہوگا اور ایک گروہ آگ میں');
INSERT INTO "ur_wahid_uddin" VALUES(42,8,'42:8','اور اگر اللہ چاہتا تو ان سب کو ایک ہی اُمت بنادیتا، لیکن وہ جس کوچاہتا ہے اپنی رحمت میں داخل کرتا ہے اور ظالموں کاکوئی حامی ومددگار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,9,'42:9','کیا انھوں نے اس کے سوا دوسرے کارساز بنارکھے ہیں ،پس اللہ ہی کارساز ہے اور وہی مُردوں کوزندہ کرتا ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,10,'42:10','اور جس کسی بات میں تم اختلاف کرتے ہو اس کا فیصلہ اللہ ہی کے سپرد ہےوہی اللہ میرا رب ہے، اسی پر میں نے بھروسہ کیااوراسی کی طرف میں رجوع کرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(42,11,'42:11','وہ آسمانوں کا اور زمین کا پیداکرنے والا ہےاس نے تمھاری جنس سے تمھارے جوڑے پیدا کيے اور جانوروں کے بھی جوڑے بنائےاس کے ذریعہ وہ تمھاری نسل چلاتا ہےکوئی چیز اس کے مثل نہیں اوروہ سننے والا، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,12,'42:12','اسی کے اختیار میں آسمانوں اور زمین کی کنجیاں ہیں وہ جس کے لیے چاہتا ہے زیادہ روزی دیتا ہے اور جس کو چاہتا ہے کم کردیتا ہےبے شک وہ ہر چیز کاعلم رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,13,'42:13','اللہ نے تمھارے لیے وہی دین مقرر کیا ہے جس کااس نے نوح کوحکم دیا تھا اور جس کی وحی ہم نے تمھاری طرف کی ہے اور جس کا حکم ہم نے ابراہیم کواور موسیٰ کو اور عیسیٰ کودیا تھا کہ دین کو قائم رکھو اور اس میں اختلاف نہ ڈالومشرکین پر وہ بات بہت گراں ہے جس کی طرف تم ان کو بلارہے ہواللہ جس کو چاہتا ہے اپنی طرف چن لیتا ہےاور وہ اپنی طرف ان کی رہنمائی کرتا ہے جو اس کی طرف متوجہ ہوتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,14,'42:14','اور جو لوگ متفرق ہوئے وہ علم آنے کے بعد ہوئے،صرف آپس کی ضد کی وجہ سےاوراگر تمھارے رب کی طرف سے ایک وقت معین تک کی بات طے نہ ہوچکی ہوتی تو ان کے درمیان فیصلہ کردیا جاتااور جن لوگوں کو ان کے بعد کتاب دی گئی وہ اس کی طرف سے شک میں پڑے ہوئے ہیں جس نے ان کوتردد میں ڈال دیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,15,'42:15','پس تم اسی کی طرف بلاؤاور اس پر جمے رہو جس طرح تم کو حکم ہوا ہے اور ان کی خواہشوں کی پیروی نہ کرواور کہو کہ اللہ نے جو کتاب اُتاری ہے میں اس پر ایمان لاتا ہوں اور مجھ کو یہ حکم ہوا ہے کہ میں تمھارے درمیان انصاف کروں اللہ ہمارا رب ہے اور تمھارا رب بھیہمارا عمل ہمارے لیے اور تمھارا عمل تمھارے لیے ہم میں اور تم میں کچھ جھگڑا نہیں اللہ ہم سب کو جمع کرے گا اور اسی کے پاس جانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,16,'42:16','اور جو لوگ اللہ کے بارے میں حجت کررہے ہیں ، بعد اس کے کہ وہ مان لیا گیا،ان کی حجت ان کے رب کے نزدیک باطل ہے اور ان پرغضب ہے اور ان کے لیے سخت عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,17,'42:17','اللہ ہی ہے جس نے حق کے ساتھ کتاب اُتاری اورترازو بھیاور تم کو کیاخبر شاید وہ گھڑی قریب ہو');
INSERT INTO "ur_wahid_uddin" VALUES(42,18,'42:18','جولوگ اس کا یقین نہیں رکھتے وہ اس کی جلدی کررہے ہیں اور جو لوگ یقین رکھنے والے ہیں وہ اس سے ڈرتے ہیں اور وہ جانتے ہیں کہ وہ برحق ہےیادرکھو کہ جولوگ اس گھڑی کے بارے میں جھگڑتے ہیں وہ گمراہی میں بہت دور نکل گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,19,'42:19','اللہ اپنے بندوں پر مہربان ہےوہ جس کوچاہتا ہے روزی دیتا ہےاوروہ قوت والا زبردست ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,20,'42:20','جوشخص آخرت کی کھیتی چاہے ہم اس کو اس کی کھیتی میں ترقی دیں گےاور جوشخص دنیا کی کھیتی چاہے ہم اس کو اس میں سے کچھ دے دیتے ہیں اور آخرت میں اس کا کوئی حصہ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,21,'42:21','کیا ان کے کچھ شریک ہیں جنھوں نے ان کے لیے ایسا دین مقرر کیا ہے جس کی اللہ نے اجازت نہیں دی اور اگر فیصلے کی بات طے نہ پاچکی ہوتی تو ان کا فیصلہ کردیا جاتااوربے شک ظالموں کے لیے دردناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,22,'42:22','تم ظالموں کو دیکھو گے کہ وہ ڈر رہے ہوں گے اس سے جو انھوں نے کمایااوروہ ان پر ضرور پڑنے والا ہے اور جو لوگ ایمان لائے اور انھوں نے اچھے کام کيے وہ جنت کے باغوں میں ہوں گےان کے لیے ان کے رب کے پاس وہ سب کچھ ہوگا جو وہ چاہیں گے، یہی بڑا انعام ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,23,'42:23','یہ چیز ہے جس کی خوش خبری اللہ اپنے ان بندوں کو دیتا ہے جو ایمان لائے اور جنھوں نے نیک عمل کیاکہو کہ میں اس پرتم سے کوئی بدلہ نہیں چاہتا مگر قرابت داری کی محبت اور جو شخص کوئی نیکی کرے گا ہم اس کے لیے اس میں بھلائی بڑھادیں گے بے شک اللہ معاف کرنے والا ہے،قدرداں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,24,'42:24','کیا وہ کہتے ہیں کہ اس نے اللہ پر جھوٹ باندھا ہےپس اگر اللہ چاہے تو وہ تمھارے دل پر مہر لگادے اور اللہ باطل کو مٹاتا ہے اور حق کو ثابت کرتا ہے اپنی باتوں سے بے شک وہ دلو ں کی باتیں جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,25,'42:25','اور وہی ہے جو اپنے بندوں کی توبہ قبول کرتا ہے اوربرائیوں کو معاف کرتا ہے اور وہ جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(42,26,'42:26','اور وہ ان لوگوں کی دعائیں قبول کرتا ہے جوایمان لائے اور جنھوں نے نیک عمل کیااور وہ ان کو اپنے فضل سے، زیادہ دے دیتا ہےاورجو انکار کرنے والے ہیں ان کے لیے سخت عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,27,'42:27','اور اگر اللہ اپنے بندوں کے لیے روزی کو کھول دیتا تو وہ زمین میں فساد کرتے لیکن وہ اندازے کے ساتھ اتارتا ہے جتنا چاہتا ہےبے شک وہ اپنے بندوں کو جاننے والا ہے، دیکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,28,'42:28','اور وہی ہے جو لوگوں کے مایوس ہوجانے کے بعد بارش برساتا ہے اور اپنی رحمت پھیلا دیتاہے اوروہ کام بنانے والاہے،قابلِ تعریف ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,29,'42:29','اور اُسی کی نشانیوں میں سے ہے آسمانوں اورزمین کا پیدا کرنااور وہ جاندار جو اس نے ان کے درمیان پھیلائے ہیں اور وہ ان کو جمع کرنے پر قادر ہے جب وہ ان کو جمع کرنا چاہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,30,'42:30','اور جومصیبت تم کو پہنچتی ہے تو وہ تمھارے ہاتھوں کے کيے ہوئے کاموں ہی سے پہنچتی ہے، اور بہت سے قصوروں کو وہ معاف کردیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,31,'42:31','اور تم زمین میں خدا کے قابوسے نکل نہیں سکتے اور اللہ کے سوا نہ تمھارا کوئی کام بنانے والا ہے اور نہ کوئی مددگار');
INSERT INTO "ur_wahid_uddin" VALUES(42,32,'42:32','اور اس کی نشانیوں میں سے یہ ہے کہ جہاز سمندر میں چلتے ہیں جیسے پہاڑ');
INSERT INTO "ur_wahid_uddin" VALUES(42,33,'42:33','اگر وہ چاہے تو وہ ہوا کو روک دے پھر وہ سمندر کی سطح پرٹھہرے ہوئے رہ جائیں بے شک اس کے اندر نشانیاں ہیں ہر اس شخص کے لیے جو صبر کرنے والا، شکر کرنے والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,34,'42:34','یا وہ ان کوتباہ کردے ان کے اعمال کے سبب سے اور معاف کردے بہت سے لوگوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(42,35,'42:35','اورتاکہ جان لیں وہ لوگ جو ہماری نشانیوں میں جھگڑتے ہیں کہ ان کے لیے بھاگنے کی کوئی جگہ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,36,'42:36','پس جوکچھ تم کو ملا ہے وہ محض دنیوی زندگی کے برتنے کے لیے ہےاور جو کچھ اللہ کے پاس ہے، وہ زیادہ بہتر ہے اور باقی رہنے والا ہے ان لوگوں کے لیے جوایمان لائے اور وہ اللہ پر بھروسہ رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,37,'42:37','اور وہ لوگ جو بڑے گناہوں سے اور بے حیائی سے بچتے ہیں اور جب ان کوغصہ آتا ہے تو وہ معاف کردیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,38,'42:38','اور وہ جنھوں نے اپنے رب کی دعوت کوقبول کیا اور نماز قائم کیااور وہ اپنا کام آپس کے مشورے سے کرتے ہیں اور ہم نے جو کچھ انھیں دیا ہے وہ اس میں سے خرچ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,39,'42:39','اور وہ لوگ کہ جب ان پرچڑھائی ہوتی ہے تو وہ بدلہ لیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,40,'42:40','اور برائی کابدلہ ہے ویسی ہی برائیپھر جس نے معاف کردیا اور اصلاح کی تو اس کا اجر اللہ کے ذمّے ہے بے شک وہ ظالموں کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(42,41,'42:41','اور جو شخص اپنے مظلوم ہونے کے بعد بدلہ لے تو ایسے لوگوں کے اوپر کچھ الزام نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,42,'42:42','الزام صرف ان پر ہے جو لوگوں کے اوپر ظلم کرتے ہیں اور زمین میں ناحق سرکشی کرتے ہیں یہی لوگ ہیں جن کے لیے دردناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,43,'42:43','اور جس شخص نے صبر کیا اور معاف کردیا تو بے شک یہ ہمت کے کام ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,44,'42:44','اور جس شخص کو اللہ بھٹکادے تو اس کے بعد اس کا کوئی کارساز نہیں اور تم ظالموں کو دیکھو گے کہ جب وہ عذاب کو دیکھیں گے تو وہ کہیں گے کہ کیا واپس جانے کی کوئی صورت ہےاور تم ان کو دیکھو گے کہ وہ دوزخ کے سامنے لائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(42,45,'42:45','وہ ذلت سے جھکے ہوئے ہوں گےچھپی نگاہ سے ديکھتے ہوں گےاور ایمان والے کہیں گے کہ خسارے والے وہی لوگ ہیں جنھوں نے قیامت کے دن اپنے آپ کو اور اپنے متعلقین کوخسارے میں ڈال دیا سُن لو، ظالم لوگ دائمی عذاب میں رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(42,46,'42:46','اور ان کے لیے کوئی مددگار نہ ہوں گے جو اللہ کے مقابلے میں ان کی مدد کریں اور خدا جس کو بھٹکادے تو اس کے لیے کوئی راستہ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(42,47,'42:47','تم اپنے رب کی دعوت قبول کرو اس سے پہلے کہ ایسا دن آجائے جس کے لیے خدا کی طرف سے ہٹنا نہ ہوگا اس دن تمھارے لیے کوئی پناہ نہ ہوگی اور نہ تم کسی چیز کو رد کر سکو گے');
INSERT INTO "ur_wahid_uddin" VALUES(42,48,'42:48','پس اگر وہ اعراض کریں تو ہم نے تم کو ان کے اوپر نگراں بناکرنہیں بھیجا ہے تمھارا ذمہ صرف پہنچادینا ہے اور انسان کو جب ہم اپنی رحمت سے نوازتے ہیں تو وہ اس پر خوش ہوجاتا ہے اور اگر ان کے اعمال کے بدلے میں ان پر کوئی مصیبت آپڑتی ہے تو آدمی ناشکری کرنے لگتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,49,'42:49','آسمانوں اور زمین کی بادشاہی اللہ کے لیے ہے، وہ جو چاہتا ہے پیدا کرتا ہےوہ جس کو چاہتا ہے بیٹیاں عطا کرتا اور جس کو چاہتا ہے بیٹے عطا کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,50,'42:50','یا ان کو جمع کردیتا ہے بیٹے بھی اور بیٹیاں بھیاور جس کو چاہتا ہے بے اولاد رکھتا ہےبے شک وہ جاننے والا ہے، قدرت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,51,'42:51','اور کسی آدمی کی یہ طاقت نہیں کہ اللہ اس سے کلام کرے، مگر وحی کے ذریعہ سے یا پردے کے پیچھے سے یا وہ کسی فرشتے کو بھیجے کہ وہ وحی کردے اس کے اذن سے جو وہ چاہے بے شک وہ سب سے اوپر ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(42,52,'42:52','اور اسی طرح ہم نے تمھاری طرف بھی وحی کی ہے، ایک روح اپنے حکم سےتم نہ جانتے تھے کہ کتاب کیا ہے اور نہ یہ جانتے تھے کہ ایمان کیا ہےلیکن ہم نے اس کو ایک نور بنایا، اس سے ہم ہدایت دیتے ہیں اپنے بندوں میں سے جس کو چاہتے ہیں اور بے شک تم ایک سیدھے راستے کی طرف رہنمائی کررہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(42,53,'42:53','اس اللہ کے راستے کی طرف جس کا وہ سب کچھ ہے جو آسمانوں اور زمین میں ہےسن لو، سارے معاملات اللہ ہی کی طرف لوٹنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,1,'43:1','حٰم');
INSERT INTO "ur_wahid_uddin" VALUES(43,2,'43:2','قسم ہے اس واضح کتاب کی');
INSERT INTO "ur_wahid_uddin" VALUES(43,3,'43:3','ہم نے اس کو عربی زبان کاقرآن بنایا ہے تاکہ تم سمجھو');
INSERT INTO "ur_wahid_uddin" VALUES(43,4,'43:4','اور بے شک یہ اصل کتاب میں ہمارے پاس ہے،بلند اور پرحکمت');
INSERT INTO "ur_wahid_uddin" VALUES(43,5,'43:5','کیا ہم تمھاری نصیحت سے اس لیے صرف نظر کرلیں گے کہ تم حد سے گزرنے والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(43,6,'43:6','اور ہم نے اگلے لوگوں میں کتنے ہی نبی بھیجے');
INSERT INTO "ur_wahid_uddin" VALUES(43,7,'43:7','اور ان لوگوں کے پاس کوئی نبی نہیں آیا جس کا انھوں نے مذاق نہ اُڑایا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(43,8,'43:8','پھر جو لوگ ان سے زیادہ طاقت ورتھے ان کو ہم نے ہلاک کردیااور اگلے لوگوں کی مثالیں گزرچکیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,9,'43:9','اور اگر تم ان سے پوچھو کہ آسمانوں اور زمین کو کس نے پیدا کیا تو وہ ضرور کہیں گے کہ ان کو زبردست، جاننے والے نے پیدا کیا');
INSERT INTO "ur_wahid_uddin" VALUES(43,10,'43:10','جس نے تمھارے لیے زمین کو فرش بنایااور اس میں تمھارے لیے راستے بنائے تاکہ تم راہ پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(43,11,'43:11','اور جس نے آسمان سے پانی اُتارا ایک اندازے کے ساتھ پھر ہم نے اس سے مُردہ زمین کو زندہ کردیا، اسی طرح تم نکالے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(43,12,'43:12','اور جس نے تمام قسمیں بنائیں اور تمھارے لیے وہ کشتیاں اور چوپائے بنائے جن پر تم سوار ہوتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(43,13,'43:13','تاکہ تم ان کی پیٹھ پر جم کر بیٹھوپھر تم اپنے رب کی نعمت کویاد کرو جب کہ تم ان پر بیٹھو اور کہو کہ پاک ہے وہ جس نے ان چیزوں کو ہمارے بس میں کردیااور ہم ایسے نہ تھے کہ ان کو قابو میں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(43,14,'43:14','اور بے شک ہم اپنے رب کی طرف لوٹنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,15,'43:15','اور ان لوگوں نے خدا کے بندوں میں سے خدا کا جزء ٹھہرایابے شک انسان کھلاہوا ناشکرا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,16,'43:16','کیا خدا نے اپنی مخلوقات میں سے بیٹیاں پسند کیں اور تم کو بیٹوں سے نوازا');
INSERT INTO "ur_wahid_uddin" VALUES(43,17,'43:17','اور جب ان میں سے کسی کو اس چیز کی خبردی جاتی ہے جس کو وہ رحمٰن کی طرف منسوب کرتا ہے تو اس کا چہرہ سیاہ پڑجاتا ہے اور وہ غم سے بھر جاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,18,'43:18','کیا وہ جو آرائش میں پرورش پائے اور جھگڑے میں بات نہ کہہ سکے');
INSERT INTO "ur_wahid_uddin" VALUES(43,19,'43:19','اور فرشتے جو رحمان کے بندے ہیں ان کو انھوں نے عورت قرار دے رکھا ہےکیاوہ ان کی پیدائش کے وقت موجود تھےان کا یہ دعویٰ لکھ لیا جائے گا اور ان سے پوچھ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(43,20,'43:20','اور وہ کہتے ہیں کہ اگر رحمٰن چاہتا تو ہم ان کی عبادت نہ کرتےان کو اس کاکوئی علم نہیں وہ محض بے تحقیق بات کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,21,'43:21','کیا ہم نے ان کو اس سے پہلے کوئی کتاب دی ہے تو انھوں نے اس کو مضبوط پکڑرکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,22,'43:22','بلکہ وہ کہتے ہیں کہ ہم نے اپنے باپ دادا کو ایک طریقہ پرپایا ہے اور ہم ان کے پیچھے چل رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,23,'43:23','اور اسی طرح ہم نے تم سے پہلے جس بستی میں کوئی نذیر بھیجا تو اس کے خوش حال لوگوں نے کہا کہ ہم نے اپنے باپ دادا کو ایک طریقہ پرپایا ہے اور ہم ان کے پیچھے چلے جارہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,24,'43:24','نذیر نے کہا،اگرچہ میں اس سے زیادہ صحیح راستہ تمھیں بتاؤں جس پر تم نے اپنے باپ دادا کو پایا ہے انھوں نے کہا کہ ہم اس کے منکر ہیں جو دے کر تم بھیجے گئے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(43,25,'43:25','تو ہم نے ان سے انتقام لیا، پس دیکھو کہ کیسا انجام ہوا جھٹلانے والوں کا');
INSERT INTO "ur_wahid_uddin" VALUES(43,26,'43:26','اور جب ابراہیم نے اپنے باپ سے اور اپنی قوم سے کہا کہ میں ان چیزوں سے بری ہوں جن کی تم عبادت کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(43,27,'43:27','مگر وہ جس نے مجھ کو پیداکیا، پس بے شک وہ میری رہنمائی کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(43,28,'43:28','اور ابراہیم یہی کلمہ اپنے پیچھے اپنی اولاد میں چھوڑگیا تاکہ وہ اس کی طرف رجوع کریں');
INSERT INTO "ur_wahid_uddin" VALUES(43,29,'43:29','بلکہ میں نے ان کو اور ان کے باپ دادا کو دنیا کا سامان دیا یہاں تک کہ ان کے پاس حق آیا اور رسول کھول کرسنادینے والا');
INSERT INTO "ur_wahid_uddin" VALUES(43,30,'43:30','اور جب ان کے پاس حق آگیا، انھوں نے کہا کہ یہ جادو ہے اور ہم اس کے منکر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,31,'43:31','اور انھوں نے کہا کہ یہ قرآن دونوں بستیوں میں سے کسی بڑے آدمی پر کیوں نہیں اُتاراگیا');
INSERT INTO "ur_wahid_uddin" VALUES(43,32,'43:32','کیا یہ لوگ تیرے رب کی رحمت کو تقسیم کرتے ہیں دنیا کی زندگی میں ان کی روزی کو تو ہم نے تقسیم کیا ہے اور ہم نے ایک کو دوسرے پر فوقیت دی ہے تاکہ وہ ایک دوسرے سے کام لیں اور تیرے رب کی رحمت اس سے بہتر ہے جو یہ جمع کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,33,'43:33','اوراگر یہ بات نہ ہوتی کہ سب لوگ ایک ہی طریقے کے ہوجائیں گے تو جو لوگ رحمن کاانکار کرتے ہیں ان کے لیے ہم ان کے گھروں کی چھتیں چاندی کی بنادیتے اور زینے بھی جن پروہ چڑھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,34,'43:34','اور ان کے گھروں کے کواڑبھی اور تخت بھی جن پر وہ تکیہ لگاکر بیٹھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,35,'43:35','اور سونے کے بھی، اور یہ چیزیں تو صرف دنیا کی زندگی کاسامان ہیں اور آخرت تیرے رب کے پاس متقیوں کے لیے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,36,'43:36','اور جو شخص رحمن کی نصیحت سے اعراض کرتا ہے تو ہم اس پر ایک شیطان مسلط کردیتے ہیں ، پس وہ اس کا ساتھی بن جاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,37,'43:37','اور وہ ان کوراہِ حق سے روکتے رہتے ہیں اور یہ لوگ سمجھتے ہیں کہ وہ ہدایت پر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,38,'43:38','یہاں تک کہ جب وہ ہمارے پاس آئے گا کہ کاش میرے اور تیرے درمیان مشرق اور مغرب کی دوری ہوتیپس کیا ہی براساتھی تھا');
INSERT INTO "ur_wahid_uddin" VALUES(43,39,'43:39','اور جب کہ تم ظلم کرچکے تو آج یہ بات تم کو کچھ بھی فائدہ نہیں دے گی کہ تم عذاب میں ایک دوسرے کے شریک ہو');
INSERT INTO "ur_wahid_uddin" VALUES(43,40,'43:40','پس کیاتم بہروں کو سناؤگے یا تم اندھوں کو راہ دکھاؤگے اور ان کو جو کھلی ہوئی گمراہی میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,41,'43:41','پس اگر ہم تم کو اٹھالیں تو ہم ان سے بدلہ لینے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,42,'43:42','یا تم کو دکھادیں گے وہ چیز جس کا ہم نے ان سے وعدہ کیا ہے پس ہم ان پر پوری طرح قادر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,43,'43:43','پس تم اس کو مضبوطی سے تھامے رہو جو تمھارے اوپر وحی کیا گیا ہےبے شک تم ایک سیدھے راستے پر ہو');
INSERT INTO "ur_wahid_uddin" VALUES(43,44,'43:44','اور یہ تمھارے لیے اور تمھاری قوم کے لیے نصیحت ہے اور عنقریب تم سے پوچھ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(43,45,'43:45','اور جن کو ہم نے تم سے پہلے بھیجا ہے ان سے پوچھ لو کہ کیا ہم نے رحمن کے سوا دوسرے، معبود ٹھہرائے تھے کہ ان کی عبادت کی جائے');
INSERT INTO "ur_wahid_uddin" VALUES(43,46,'43:46','اور ہم نے موسیٰ کو اپنی نشانیوں کے ساتھ فرعون اور اس کے امراء کے پاس بھیجا تو اس نے کہا کہ میں خداوند عالم کا رسول ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(43,47,'43:47','پس جب وہ ان کے پاس ہماری نشانیوں کے ساتھ آیا تو وہ اس پر ہنسنے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(43,48,'43:48','اور ہم ان کو جو نشانیاں دکھاتے تھے وہ پہلی نشانی سے بڑھ کر ہوتی تھیں اور ہم نے ان کو عذاب میں پکڑا تاکہ وہ رجوع کریں');
INSERT INTO "ur_wahid_uddin" VALUES(43,49,'43:49','اور انھوں نے کہا کہ اے جادوگر، ہمارے لیے اپنے رب سے دعا کرو، اس عہد کی بنا پر جو اس نے تم سے کیا ہے، ہم ضرور راہ پر آجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(43,50,'43:50','پھر جب ہم نے وہ عذاب ان سے ہٹادیا تو انھوں نے اپنا عہد توڑدیا');
INSERT INTO "ur_wahid_uddin" VALUES(43,51,'43:51','اور فرعون نے اپنی قوم کے درمیان پکار کر کہا کہ اے میری قوم، کیا مصر کی بادشاہی میری نہیں ہے، اور یہ نہریں جو میرے نیچے بہہ رہی ہیں کیا تم لوگ دیکھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,52,'43:52','بلکہ میں بہتر ہوں اس شخص سے جو کہ حقیر ہے اور صاف بول نہیں سکتا');
INSERT INTO "ur_wahid_uddin" VALUES(43,53,'43:53','پھر کیوں نہ اس پر سونے کے کنگن آپڑے یافرشتے اس کے ساتھ جمع هوکر آتے');
INSERT INTO "ur_wahid_uddin" VALUES(43,54,'43:54','پس اس نے اپنی قوم کو بے عقل کردیا پھر انھوں نے اس کی بات مان لی یہ نافرمان قسم کے لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(43,55,'43:55','پھر جب انھوں نے ہم کو غصہ دلایا تو ہم نے ان سے بدلہ لیا اور ہم نے ان سب کو غرق کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(43,56,'43:56','پھر ہم نے ان کوماضی کی داستان بنادیا اور دوسروں کے لیے ایک نمونۂ عبرت');
INSERT INTO "ur_wahid_uddin" VALUES(43,57,'43:57','اور جب ابن مریم کی مثال دی گئی تو تمھاری قوم کے لوگ اس پر چلا اُٹھے');
INSERT INTO "ur_wahid_uddin" VALUES(43,58,'43:58','اور انھوں نے کہا کہ ہمارے معبود اچھے ہیں یا وہ یہ مثال وہ تم سے صرف جھگڑنے کے لیے بیان کرتے ہیں بلکہ یہ لوگ جھگڑالو ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,59,'43:59','عیسیٰ تو بس ہمارا ایک بندہ تھا جس پر ہم نے فضل فرمایا اور اس کو بنی اسرائيل کے لیے ایک مثال بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(43,60,'43:60','اور اگر ہم چاہیں تو تمھارے اندر سے فرشتے بنادیں جو زمین میں تمھارے جانشیں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(43,61,'43:61','اور بے شک عیسیٰ قیامت کا ایک نشان ہیں ، تو تم اس میں شک نہ کرو اور میری پیروی کرویہی سیدھا راستہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,62,'43:62','اور شیطان تم کو اس سے روکنے نہ پائے، بے شک وہ تمھارا کھلاہوا دشمن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,63,'43:63','اور جب عیسیٰ کھلی ہوئی نشانیوں کے ساتھ آیا، اس نے کہا کہ میں تمھارے پاس حکمت لے کر آیا ہوں اورتاکہ میں تم پر واضح کردوں بعض باتیں جن میں تم اختلاف کررہے ہو پس تم اللہ سے ڈرو اور میری اطاعت کرو');
INSERT INTO "ur_wahid_uddin" VALUES(43,64,'43:64','بے شک اللہ ہی میرا رب ہے اور تمھارا رب بھی، تو تم اسی کی عبادت کرو، یہی سیدھا راستہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,65,'43:65','پھر گروہوں نے آپس میں اختلاف کیا پس تباہی ہے ان لوگوں کے لیے جنھوں نے ظلم کیا، ایک دردناک دن کے عذاب سے');
INSERT INTO "ur_wahid_uddin" VALUES(43,66,'43:66','یہ لوگ بس قیامت کاانتظار کررہے ہیں کہ وہ ان پر اچانک آپڑے اور ان کو خبر بھی نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(43,67,'43:67','تمام دوست اس دن ایک دوسرے کے دشمن ہوں گے، بجز ڈرنے والوں کے');
INSERT INTO "ur_wahid_uddin" VALUES(43,68,'43:68','اے میرے بندو، آج تم پر نہ کوئی خوف ہے اور نہ تم غمگین ہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(43,69,'43:69','جولوگ ایمان لائے اور فرماں بردار رہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,70,'43:70','جنت میں داخل ہوجاؤ تم اور تمھاری بیویاں ، تم شاد کيے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(43,71,'43:71','ان کے سامنے سونے کی رکابیاں اور پیالے پیش کيے جائیں گے اور وہاں وہ چیزیں ہوں گی جن کو جی چاہے گا اور جن سے آنکھوں کو لذت ہوگی اور تم یہاں ہمیشہ رہوگے');
INSERT INTO "ur_wahid_uddin" VALUES(43,72,'43:72','اور یہ وہ جنت ہے جس کے تم مالک بنادئے گئے اس کی وجہ سے جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(43,73,'43:73','تمھارے لیے اس میں بہت سے میوے ہیں جن میں سے تم کھاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(43,74,'43:74','بے شک مجرم لوگ ہمیشہ دوزخ کے عذاب میں رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(43,75,'43:75','وہ ان سے ہلکا نہ کیا جائے گا اور وہ اس میں مایوس پڑے رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(43,76,'43:76','اور ہم نے ان پر ظلم نہیں کیا بلکہ وہ خود ہی ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(43,77,'43:77','اور وہ پکاریں گے کہ اے مالک، تمھارا رب ہمارا خاتمہ ہی کردے فرشتہ کہے گا تم کواسی طرح پڑے رہنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,78,'43:78','ہم تمھارے پاس حق لے کر آئے مگر تم میں سے اکثر لوگ حق سے بیزار رہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,79,'43:79','کیا انھوں نے کوئی بات ٹھہرالی ہے تو ہم بھی ایک بات ٹھہرالیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(43,80,'43:80','کیا ان کا گمان ہے کہ ہم ان کے رازوں کواور ان کے مشوروں کو نہیں سن رہے ہیں ہاں ،اور ہمارے بھیجے ہوئے ان کے پاس لکھتے رہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,81,'43:81','کہو کہ اگر رحمن کے کوئی اولاد ہو تو میں سب سے پہلے اس کی عبادت کرنے والاہوں');
INSERT INTO "ur_wahid_uddin" VALUES(43,82,'43:82','آسمانو ں اور زمین کا خداوند، عرش کا مالک وہ ان باتوں سے پاک ہے جس کو لوگ بیان کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,83,'43:83','پس ان کو چھوڑ دو کہ وہ بحث کریں اور کھیلیں ، یہاں تک کہ وہ اس دن سے دوچار ہوں جس کا ان سے وعدہ کیا جارہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,84,'43:84','اور وہی ہے جو آسمان میں خداوند ہے اور وہی زمین میں خداوند ہے اور وہ حکمت والا، علم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(43,85,'43:85','اور بڑی بابرکت ہے وہ ذات جس کی بادشاہی آسمانوں اور زمین میں ہے اور جو کچھ ان کے درمیان ہے اور اسی کے پاس قیامت کی خبر ہے اوراسی کی طرف تم لوٹائے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(43,86,'43:86','اور اللہ کے سوا جن کو یہ لوگ پکارتے ہیں وہ سفارش کا اختیار نہیں رکھتے، مگر وہ جو حق کی گواہی دیں گے اور وہ جانتے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(43,87,'43:87','اور اگر تم ان سے پوچھو کہ ان کو کس نے پیدا کیا ہے تو وہ یہی کہیں گے کہ اللہ نے،پھر وہ کہاں بھٹک جاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(43,88,'43:88','اور اس کورسول کے اس کہنے کی خبر ہے کہ اے میرے رب، یہ ایسے لوگ ہیں کہ ایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(43,89,'43:89','پس ان سے درگزر کرو اور کہو کہ سلام ہے تم کو، عنقریب ان کومعلوم ہوجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(44,1,'44:1','حٰمٓ');
INSERT INTO "ur_wahid_uddin" VALUES(44,2,'44:2','قسم ہے اس واضح کتاب کی');
INSERT INTO "ur_wahid_uddin" VALUES(44,3,'44:3','ہم نے اس کوایک برکت والی رات میں اُتارا ہے، بے شک ہم آگاہ کرنے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(44,4,'44:4','اس رات میں ہر حکمت والامعاملہ طے کیاجاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(44,5,'44:5','ہمارے حکم سے،بے شک ہم تھے بھیجنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(44,6,'44:6','تیرے رب کی رحمت سے، وہی سننے والا ہے، جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(44,7,'44:7','آسمانوں اورزمین کارب اور جو کچھ ان کے درمیان ہے،اگر تم یقین کرنے والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(44,8,'44:8','اس کے سوا کوئی معبود نہیں وہی زندہ کرتا ہے اورمارتاہے تمھارابھی رب اور تمھارے اگلے باپ دادا کا بھی رب');
INSERT INTO "ur_wahid_uddin" VALUES(44,9,'44:9','بلکہ وہ شک میں پڑے ہوئے کھیل رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(44,10,'44:10','پس انتظار کرو اس دن کا جب آسمان ایک کھلے ہوئے دھوئیں کے ساتھ ظاہر ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(44,11,'44:11','وہ لوگوں کو گھیرلے گایہ ایک دردناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(44,12,'44:12','اے ہمارے رب، ہم پر سے عذاب ٹال دے، ہم ایمان لاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(44,13,'44:13','ان کے لیے نصیحت کہاں ، اور ان کے پاس رسول آچکا تھا کھول کر سنانے والا');
INSERT INTO "ur_wahid_uddin" VALUES(44,14,'44:14','پھر انھوں نے اس سے پیٹھ پھیری اور کہا کہ یہ تو ایک سکھایا ہوا دیوانہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(44,15,'44:15','ہم کچھ وقت کے لیے عذاب کو ہٹادیں ،تم پھراپنی اسی حالت پرآجاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(44,16,'44:16','جس دن ہم پکڑیں گے بڑی پکڑ، اس دن ہم پورابدلہ لیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(44,17,'44:17','اور ان سے پہلے ہم نے فرعون کی قوم کوآزمایااوران کے پاس ایک معززرسول آیا');
INSERT INTO "ur_wahid_uddin" VALUES(44,18,'44:18','کہ اللہ کے بندوں کو میرے حوالے کرومیں تمھارے لیے ایک معتبررسول ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(44,19,'44:19','اور یہ کہ اللہ کے مقابلہ میں سرکشی نہ کرو میں تمھارے سامنے ایک واضح دلیل پیش کرتاہوں');
INSERT INTO "ur_wahid_uddin" VALUES(44,20,'44:20','اور میں اپنے اور تمھارے رب کی پناہ لے چکا ہوں اس بات سے کہ تم مجھے سنگسار کرو');
INSERT INTO "ur_wahid_uddin" VALUES(44,21,'44:21','اور اگر تم مجھ پر ایمان نہیں لاتے تو تم مجھ سے الگ رہو');
INSERT INTO "ur_wahid_uddin" VALUES(44,22,'44:22','پس موسیٰ نے اپنے رب کوپکارا کہ یہ لوگ مجرم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(44,23,'44:23','تو اب تم میرے بندوں کورات ہی رات میں لے کر چلے جاؤ،تمھارا پیچھا کیاجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(44,24,'44:24','اور تم دریاکو تھماہوا چھوڑدو، ان کالشکر ڈوبنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(44,25,'44:25','انھوں نے کتنے ہی باغ اورچشمے');
INSERT INTO "ur_wahid_uddin" VALUES(44,26,'44:26','اورکھیتیاں اور عمدہ مکانات');
INSERT INTO "ur_wahid_uddin" VALUES(44,27,'44:27','اور آرام کے سامان جن میں وہ خوش رہتے تھے، سب چھوڑدئے');
INSERT INTO "ur_wahid_uddin" VALUES(44,28,'44:28','اسی طرح ہوااور ہم نے د وسری قوم کو ان کامالک بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(44,29,'44:29','پس نہ ان پر آسمان رویا اور نہ زمین، اورنہ ان کو مہلت دی گئی');
INSERT INTO "ur_wahid_uddin" VALUES(44,30,'44:30','اور ہم نے بنی اسرائیل کو ذلت والے عذاب سے نجات دی');
INSERT INTO "ur_wahid_uddin" VALUES(44,31,'44:31','یعنی فرعون سے، بے شک وہ سرکش اور حدسے نکل جانے والوں میں سے تھا');
INSERT INTO "ur_wahid_uddin" VALUES(44,32,'44:32','اور ہم نے ان کو اپنے علم کی رو سے دنیا والوں پرترجیح دی');
INSERT INTO "ur_wahid_uddin" VALUES(44,33,'44:33','اور ہم نے ان کو ایسی نشانیاں دیں جن میں کھلاہوا انعام تھا');
INSERT INTO "ur_wahid_uddin" VALUES(44,34,'44:34','یہ لوگ کہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(44,35,'44:35','بس یہی ہمارا پہلا مرنا ہے اور ہم پھر اٹھائے نہیں جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(44,36,'44:36','اگر تم سچے ہو تو لے آؤ ہمارے باپ دادا کو');
INSERT INTO "ur_wahid_uddin" VALUES(44,37,'44:37','کیا یہ بہتر ہیں یا تبع کی قوم اور جو ان سے پہلے تھے، ہم نے ان کو ہلاک کردیا، بے شک وہ نافرمان تھے');
INSERT INTO "ur_wahid_uddin" VALUES(44,38,'44:38','اور ہم نے آسمانوں اور زمین کواور جوکچھ ان کے درمیان ہے کھیل کے طور پر نہیں بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(44,39,'44:39','ان کو ہم نے حق کے ساتھ بنایا ہے لیکن ان کے اکثر لوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(44,40,'44:40','بے شک فیصلے کا دن ان سب کا طے شدہ وقت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(44,41,'44:41','جس دن کوئی رشتہ دار کسی رشتہ دار کے کام نہیں آئے گا اور نہ ان کی کچھ حمایت کی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(44,42,'44:42','ہاں مگر وہ جس پر اللہ رحم فرمائے بے شک وہ زبردست ہے، رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(44,43,'44:43','زقوم کادرخت');
INSERT INTO "ur_wahid_uddin" VALUES(44,44,'44:44','گنہگار کاکھانا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(44,45,'44:45','تیل کی تلچھٹ جیسا، وہ پیٹ میں کھولے گا');
INSERT INTO "ur_wahid_uddin" VALUES(44,46,'44:46','جس طرح گرم پانی کھولتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(44,47,'44:47','اس کو پکڑو اور اس کو گھسیٹتے ہوئے جہنم کے بیچ تک لے جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(44,48,'44:48','پھر اس کے سرپر کھولتے ہوئے پانی کاعذاب انڈیل دو');
INSERT INTO "ur_wahid_uddin" VALUES(44,49,'44:49','چکھ اس کو ، تو بڑا معزز،مکرم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(44,50,'44:50','یہ وہی چیز ہے جس میں تم شک کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(44,51,'44:51','بے شک خداسے ڈرنے والے امن کی جگہ میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(44,52,'44:52','باغوں اورچشموں میں');
INSERT INTO "ur_wahid_uddin" VALUES(44,53,'44:53','باریک ریشم اوردبیزریشم کے لباس پہنے ہوئے آمنے سامنے بیٹھے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(44,54,'44:54','یہ بات اسی طرح ہے، اور ہم ان سے بیاہ دیں گے حوریں بڑی بڑی آنکھ والی');
INSERT INTO "ur_wahid_uddin" VALUES(44,55,'44:55','وہ اس میں طلب کریں گے ہرقسم کے میوے نہایت اطمینان سے');
INSERT INTO "ur_wahid_uddin" VALUES(44,56,'44:56','وہ وہاں موت کونہ چکھیں گے مگر وہ موت جو پہلے آچکی ہے اور اللہ نے ان کو جہنم کے عذاب سے بچالیا');
INSERT INTO "ur_wahid_uddin" VALUES(44,57,'44:57','یہ تیرے رب کے فضل سے ہوگا، یہی ہے بڑی کامیابی');
INSERT INTO "ur_wahid_uddin" VALUES(44,58,'44:58','پس ہم نے اس کتاب کو تمھاری زبان میں آسان بنادیا ہے تاکہ لوگ نصیحت حاصل کریں');
INSERT INTO "ur_wahid_uddin" VALUES(44,59,'44:59','پس تم بھی انتظار کرو، وہ بھی انتظار کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(45,1,'45:1','حٰمٓ');
INSERT INTO "ur_wahid_uddin" VALUES(45,2,'45:2','یہ نازل کی ہوئی کتاب ہے اللہ غالب، حکمت والے کی طرف سے');
INSERT INTO "ur_wahid_uddin" VALUES(45,3,'45:3','بے شک آسمانوں اور زمین میں نشانیاں ہیں ایمان والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(45,4,'45:4','اور تمھارے بنانے میں اور ان حیوانات میں جو اس نے زمین میں پھیلارکھے ہیں نشانیاں ہیں ان لوگوں کے لیے جو یقین رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(45,5,'45:5','اور رات اور دن کے آنے جانے میں اور اس رزق میں جس کو اللہ نے آسمان سے اُتارا،پھراس سے زمین کو زندہ کردیا اس کے مرجانے کے بعد،اور ہواؤں کی گردش میں بھی نشانیاں ہیں ان لوگوں کے لیے جو عقل رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(45,6,'45:6','یہ اللہ کی آیتیں ہیں جن کو ہم حق کے ساتھ تمھیں سنارہے ہیں پھر اللہ اور اس کی آیتوں کے بعد کون سی بات ہے جس پر وہ ایمان لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(45,7,'45:7','خرابی ہے ہر شخص کے لیے جوجھوٹا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(45,8,'45:8','جو خدا کی آیتوں کو سنتا ہے جب کہ وہ اس کے سامنے پڑھی جاتی ہیں ، پھروہ تکبر کے ساتھ اَڑا رہتا ہے، گویا اس نے ان کو سناہی نہیں پس تم اس کو ایک دردناک عذاب کی خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(45,9,'45:9','اور جب وہ ہماری آیتوں میں سے کسی چیز کی خبر پاتا ہے تو وہ اس کو مذاق بنالیتا ہےایسے لوگوں کے لیے ذلت کاعذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(45,10,'45:10','ان کے آگے جہنم ہےاور جو کچھ انھوں نے کمایا وہ ان کے کچھ کام آنے والانہیں اور نہ وہ جن کوانھوں نے اللہ کے سوا کارسازبنایا اوران کے لیے بڑا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(45,11,'45:11','یہ ہدایت ہے،اور جنھوں نے اپنے رب کی آیتوں کا انکار کیا، ان کے لیے سختی کا درد ناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(45,12,'45:12','اللہ ہی ہے جس نے تمھارے لیے سمندر کومسخر کردیا تاکہ اس کے حکم سے اس میں کشتیاں چلیں اور تاکہ تم اس کا فضل تلاش کرو اور تاکہ تم شکر کرو');
INSERT INTO "ur_wahid_uddin" VALUES(45,13,'45:13','اور اس نے آسمانوں اور زمین کی تمام چیزوں کو تمھارے لیے مسخر کردیا، سب کواپنی طرف سے بے شک اس میں نشانیاں ہیں ان لوگوں کے لیے جو غورکرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(45,14,'45:14','ایمان والوں سے کہو کہ ان لوگوں سے درگزر کریں جو خدا کے دنوں کی امید نہیں رکھتے، تاکہ اللہ ایک قوم کو اس کی کمائی کا بدلہ دے');
INSERT INTO "ur_wahid_uddin" VALUES(45,15,'45:15','جو شخص نیک عمل کرے گاتو اس کا فائدہ اسی کے لیے ہےاور جس شخص نے براکیا تو اس کاوبال اسی پر پڑے گاپھر تم اپنے رب کی طرف لوٹائے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(45,16,'45:16','اور ہم نے بنی اسرائیل کو کتاب اور حکم اور نبوت دی اور ان کو پاکیزہ رزق عطا کیا اور ہم نے ان کو دنیا والوں پر فضیلت بخشی');
INSERT INTO "ur_wahid_uddin" VALUES(45,17,'45:17','اور ہم نے ان کو دین کے بارے میں کھلی کھلی دلیلیں دیں پھر انھوں نے اختلاف نہیں کیا مگر اس کے بعد کہ ان کے پاس علم آچکا تھا، آپس کی ضد کی وجہ سےبے شک تیرا رب قیامت کے دن ان کے درمیان فیصلہ کردے گاان چیزوں کے بارے میں جن میں وہ باہم اختلاف کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(45,18,'45:18','پھر ہم نے تم کو دین کے ایک واضح طریقہ پر قائم کیا پس تم اسی پر چلو اور ان لوگوں کی خواہشوں کی پیروی نہ کرو جو علم نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(45,19,'45:19','یہ لوگ اللہ کے مقابلہ میں تمھارے کچھ کام نہیں آسکتے اور ظالم لوگ ایک دوسرے کے ساتھی ہیں اور ڈرنے والوں کا ساتھی اللہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(45,20,'45:20','یہ لوگوں کے لیے بصیرت کی باتیں ہیں اور ہدایت اور رحمت ان لوگوں کے لیے جو یقین کریں');
INSERT INTO "ur_wahid_uddin" VALUES(45,21,'45:21','کیا وہ لوگ جنھوں نے برائیوں کاارتکاب کیاہے، وہ یہ خیال کرتے ہیں کہ ہم ان کو ان لوگوں کی مانند کردیں گے جو ایمان لائے اور نیک عمل کیا ان سب کا جینااور مرنایکساں ہوجائے بہت برافیصلہ ہے جووہ کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(45,22,'45:22','اور اللہ نے آسمانوں اور زمین کو حکمت کے ساتھ پیدا کیا اور تاکہ ہر شخص کو اس کے کيے کا بدلہ دیا جائے اور ان پر کوئی ظلم نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(45,23,'45:23','کیاتم نے اس شخص کو دیکھاجس نے اپنی خواہش کو اپنا معبود بنارکھاہے اور اللہ نے اس کے علم کے باوجود اس کو گمراہی میں ڈال دیااور اس کے کان اور اس کے دل پر مہرلگادی اور اس کی آنکھ پر پردہ ڈال دیاپس ایسے شخص کو کون ہدایت دے سکتا ہے،اس کے بعد کہ اللہ نے اس کو گمراہ کردیا ہو، کیا تم دھیان نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(45,24,'45:24','اور وہ کہتے ہیں کہ ہماری اِس دنیا کی زندگی کے سوا کوئی اور زندگی نہیں ہم مرتے ہیں اور جیتے ہیں اور ہم کو صرف زمانہ کی گردش ہلاک کرتی ہے اور ان کو اس باب میں کوئی علم نہیں وہ محض گمان کی بنا پر ایساکہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(45,25,'45:25','اور جب ان کو ہماری کھلی کھلی آیتیں سنائی جاتی ہیں تو ان کے پاس کوئی حجت اس کے سوا نہیں ہوتی کہ ہمارے باپ دادا کوزندہ کرکے لاؤاگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(45,26,'45:26','کہو کہ اللہ ہی تم کو زندہ کرتا ہے پھر وہ تم کو مارتا ہے پھر وہ قیامت کے دن تم کو جمع کرے گا،اس میں کوئی شک نہیں ، لیکن اکثرلوگ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(45,27,'45:27','اور اللہ ہی کی بادشاہی ہے آسمانوں میں اور زمین میں اور جس دن قیامت قائم ہوگی اس دن اہلِ باطل خسارے میں پڑجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(45,28,'45:28','اور تم دیکھوگے کہ ہر گروہ گھٹنوں کے بَل گر پڑے گا ہر گروہ اپنے نامۂ اعمال کی طرف بلایاجائے گاآج تم کو اس عمل کا بدلہ دیا جائے گا جوتم کررہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(45,29,'45:29','یہ ہمارا دفتر ہے جوتمھارے اوپر ٹھیک ٹھیک گواہی دے رہاہےہم لکھواتے جارہے تھے جو کچھ تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(45,30,'45:30','پس جو لوگ ایمان لائے اور انھوں نے اچھے کام کيے تو ان کا رب ان کو اپنی رحمت میں داخل کرے گا یہی کھلی ہوئی کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(45,31,'45:31','اور جنھوں نے انکار کیا، کیا تم کومیری آیتیں پڑھ کر نہیں سنائی جاتی تھیں پس تم نے تکبر کیا اور تم مجرم لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(45,32,'45:32','اور جب کہا جاتا تھا کہ اللہ کا وعدہ حق ہے اور قیامت میں کوئی شک نہیں تو تم کہتے تھے کہ ہم نہیں جانتے کہ قیامت کیا ہے، ہم تو بس ایک گمان سارکھتے ہیں ،اور ہم اس پر یقین کرنے والے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(45,33,'45:33','اوران پر ان کے اعمال کی برائیاں کھل جائیں گی اور وہ چیزان کو گھیر لے گی جس کا وہ مذاق اُڑاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(45,34,'45:34','اور کہا جائے گا کہ آج ہم تم کو بھلادیں گے جس طرح تم نے اپنے اس دن کے آنے کوبھلائے رکھااور تمھارا ٹھکانا آگ ہے اور کوئی تمھارا مددگار نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(45,35,'45:35','یہ اس وجہ سے کہ تم نے اللہ کی آیتوں کا مذاق اُڑایااور دنیا کی زندگی نے تم کو دھوکہ میں رکھا پس آج وہ اس سے نکالے جائیں گے اور نہ ان کا عذر قبول کیاجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(45,36,'45:36','پس ساری تعریف اللہ کے لیے ہے جو رب ہے آسمانوں کا اور رب ہے زمین کا،رب ہے تمام عالم کا');
INSERT INTO "ur_wahid_uddin" VALUES(45,37,'45:37','اوراسی کے لیے بڑائی ہے آسمانوں اور زمین میں اور وہ زبردست ہے،حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(46,1,'46:1','حٰمٓ');
INSERT INTO "ur_wahid_uddin" VALUES(46,2,'46:2','یہ کتاب اللہ زبردست، حکمت والے کی طرف سے اُتاری گئی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(46,3,'46:3','ہم نے آسمانوں اورزمین کواور ان کے درمیان کی چیزوں کو نہیں پیدا کیا مگر حق کے ساتھ اور معین مدت کے لیےاور جو لوگ منکر ہیں وہ اس سے بے رخی کرتے ہیں جس سے ان کو ڈرایاگیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(46,4,'46:4','کہو کہ تم نے غور بھی کیا ان چیزوں پر جن کو تم اللہ کے سواپکارتے ہو، مجھے دکھاؤکہ انھوں نے زمین میں کیا بنایا ہے، یا ان کاآسمان میں کچھ ساجھا ہے، میرے پاس اس سے پہلے کی کوئی کتاب لے آؤ یا کوئی علم جو چلاآتا ہو، اگر تم سچّے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(46,5,'46:5','اور اس شخص سے زیادہ گمراہ کون ہوگا جو اللہ کو چھوڑ کران کو پکارے جو قیامت تک اس کا جواب نہیں دے سکتے، اور ان کو ان کے پکارنے کی بھی خبر نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(46,6,'46:6','اور جب لوگ اكٹھا کيے جائیں گے تو وہ ان کے دشمن ہوں گے اور ان کی عبادت کے منکر بن جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(46,7,'46:7','اور جب ہماری کھلی کھلی آیتیں ان کو پڑھ کر سنائی جاتی ہیں تومنکرلوگ اس حق کی بابت، جب کہ وہ ان کے پاس پہنچتا ہے، کہتے ہیں کہ یہ کھلا ہوا جادو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(46,8,'46:8','کیا یہ لوگ کہتے ہیں کہ اس نے اس کو اپنی طرف سے بنالیا ہے کہو کہ اگر میں نے اس کو اپنی طرف سے بنایا ہے تو تم لوگ مجھ کو ذرا بھی اللہ سے بچانہیں سکتے جو باتیں تم بناتے ہو،اللہ ان کوخوب جانتا ہے وہ میرے اور تمھارے درمیان گواہی کے لیے کافی ہےاور وہ بخشنے والا اور رحمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(46,9,'46:9','کہوکہ میں کوئی انوکھا رسول نہیں ہوں اور میں نہیں جانتا کہ میرے ساتھ کیاکیا جائے گا اور تمھارے ساتھ کیا میں تو صرف اسی کا اتباع کرتا ہوں جو میری طرف وحی کے ذریعہ آتا ہے اور میں توصرف ایک کھلاہوا آگاہ کرنے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(46,10,'46:10','کہو، کیا تم نے کبھی سوچا کہ اگر یہ قرآن اللہ کی جانب سے ہو اور تم نے اس کو نہیں مانا، اور بنی اسرائیل میں سے ایک گواہ نے اس جیسی کتاب پر گواہی دی ہے پس وہ ایمان لایا اور تم نے تکبر کیا بے شک اللہ ظالموں کو ہدایت نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(46,11,'46:11','اور انکار کرنے والے، ایمان لانے والوں کی نسبت کہتے ہیں کہ اگر یہ کوئی اچھی چیز ہوتی تو وہ اس پر ہم سے پہلے نہ دوڑتےاور چوں کہ انھوں نے اس سے ہدایت نہیں پائی تو اب وہ کہیں گے کہ یہ تو پرانا جھوٹ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(46,12,'46:12','اور اس سے پہلے موسیٰ کی کتاب تھی رہنما اور رحمتاور یہ ایک کتاب ہے جو اس کی تصدیق کرتی ہے، عربی زبان میں ، تاکہ ان لوگوں کو ڈرائے جنھوں نے ظلم کیا اور وہ خوش خبری ہے نیک لوگوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(46,13,'46:13','بے شک جن لوگوں نے کہا کہ ہمارا رب اللہ ہے، پھر وہ اس پر جمے رہے توان لوگوں پرکوئی خوف نہیں اور نہ وہ غمگین ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(46,14,'46:14','یہی لوگ جنت والے ہیں جواس میں ہمیشہ رہیں گے،ان اعمال کے بدلے جو وہ دنیا میں کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(46,15,'46:15','اور ہم نے انسان کو حکم دیا کہ وہ اپنے ماں باپ کے ساتھ بھلائی کرےاس کی ماں نے تکلیف کے ساتھ اس کو پیٹ میں رکھااور تکلیف کے ساتھ اس کو جَنااور اس کا حمل ميں رهنا اور دودھ چھڑانا تیس مہینے میں ہوایہاں تک کہ جب وہ اپنی پختگی کوپہنچا اور چالیس برس کو پہنچ گیا تو وہ کہنے لگاکہ اے میرے رب، مجھے توفیق دے کہ میں تیرے احسان کا شکر کروں جو تونے مجھ پر کیا اور میرے ماں باپ پر کیا اور یہ کہ میں وہ نیک عمل کروں جس سے تو راضی ہو اور میری اولاد میں بھی مجھ کو نیک اولاد دے میں نے تیری طرف رجوع کیااور میں فرماں برداروں میں سے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(46,16,'46:16','یہ لوگ ہیں جن کے اچھے اعمال کو ہم قبول کریں گے اور ان کی برائیوں سے ہم درگزر کریں گے، وہ اہل جنت میں سے ہوں گے،سچاوعدہ جو ان سے کیا جاتا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(46,17,'46:17','اور جس نے اپنے ماں باپ سے کہا کہ میں بیزار ہوں تم سےکیا تم مجھ کو یہ خوف دلاتے ہوکہ میں قبر سے نکالاجاؤں گا، حالاں کہ مجھ سے پہلے بہت سی قومیں گزرچکی ہیں اور وہ دونوں اللہ سے فریاد کرتے ہیں کہ تیری خرابی ہو،تو ایمان لا، بے شک اللہ کا وعد ہ سچاہےپس وہ کہتا ہے کہ یہ سب اگلوں کی کہانیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(46,18,'46:18','یہ وہ لوگ جن پر اللہ کا قول پورا ہوا ان گروہوں کے ساتھ جوان سے پہلے گزرے، جنوں اور انسانوں میں سے بےشک وہ خسارے میں رہے');
INSERT INTO "ur_wahid_uddin" VALUES(46,19,'46:19','اور ہر ایک کے لیے ان کے اعمال کے اعتبار سے درجے ہوں گےاورتاکہ اللہ سب کو ان کے اعمال پورے کردے اور ان پر ظلم نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(46,20,'46:20','اور جس دن انکار کرنے والے آگ کے سامنے لائے جائیں گے، تم اپنی اچھی چیزیں دنیا کی زندگی میں لے چکے اوران کوبرت چکے تو آج تم کو ذلت کی سزادی جائے گی،اس وجہ سے کہ تم دنیا میں ناحق تکبر کرتے تھے اور اس وجہ سے کہ تم نافرمانی کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(46,21,'46:21','اور عادکے بھائی(ہود) کویادکروجب کہ اس نے اپنی قوم کو احقاف میں ڈرایا— اور ڈرانے والے اس سے پہلے بھی گزرچکے تھے اوراس کے بعد بھی آئے— کہ اللہ کے سوا کسی کی عبادت نہ کرومیں تم پر ایک ہولناک دن کے عذاب سے ڈرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(46,22,'46:22','انھوں نے کہا کہ کیا تم ہمارے پاس اس لیے آئے ہو کہ ہم کو ہمارے معبودوں سے پھیردوپس اگر تم سچے ہوتووہ چیز ہم پرلاؤجس کا تم ہم سے وعدہ کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(46,23,'46:23','اس نے کہاکہ اس کاعلم تو اللہ کو ہے، اور میں توتم کو وہ پیغام پہنچارہا ہوں جس کے ساتھ مجھے بھیجاگیا ہے، لیکن میں تم کو دیکھتا ہوں کہ تم لوگ نادانی کی باتیں کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(46,24,'46:24','پس جب انھوں نے اس کو بادل کی شکل میں اپنی وادیوں کی طرف آتے ہوئے دیکھا تو انھوں نے کہا کہ یہ تو بادل ہے جو ہم پر برسے گا نہیں بلکہ یہ وہ چیز ہے جس کی تم جلدی کررہے تھےایک آندھی ہے جس میں دردناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(46,25,'46:25','وہ ہر چیزکو اپنے رب کے حکم سے اکھاڑ پھینکے گیپس وہ ایسے ہوگئے کہ ان کے گھروں کے سوا وہاں کچھ نظرنہ آتا تھامجرموں کو ہم اسی طرح سزادیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(46,26,'46:26','اور ہم نے ان لوگوں کوان باتوں میں قدرت دی تھی کہ تم کو ان باتوں میں قدرت نہیں دی اور ہم نے ان کو کان اور آنکھ اور دل دئے،مگر وہ کان ان کے کچھ کام نہ آئے،اور نہ آنکھیں اور نہ دلکیوں کہ وہ اللہ کی آیتوں کا انکار کرتے تھے اور ان کو اس چیز نے گھیرلیاجس کاوہ مذاق اُڑاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(46,27,'46:27','اور ہم نے تمھارے آس پاس کی بستیاں بھی تباہ کردیں اور ہم نے بار باراپنی نشانیاں بتائیں تاکہ وہ باز آئیں');
INSERT INTO "ur_wahid_uddin" VALUES(46,28,'46:28','پس کیوں نہ ان کی مدد کی انھوں نے جن کوانھوں نے خداکے تقرب کے لیے معبود بنا رکھا تھابلکہ وہ سب ان سے کھوئے گئے اور یہ ان کا جھوٹ تھا اور ان کی گھڑی ہوئی بات تھی');
INSERT INTO "ur_wahid_uddin" VALUES(46,29,'46:29','اور جب ہم جنات کے ایک گروہ کو تمھاری طرف لے آئے، وہ قرآن سننے لگےپس جب وہ اس کے پاس آئے تو کہنے لگے کہ چپ رہوپھر جب قرآن پڑھاجاچکاتو وہ لوگ ڈرانے والے بن کر اپنی قوم کی طرف واپس گئے');
INSERT INTO "ur_wahid_uddin" VALUES(46,30,'46:30','انھوں نے کہاکہ اے ہماری قوم،ہم نے ایک کتاب سنی ہے جو موسیٰ کے بعد اُتاری گئی ہے، ان پیشین گوئیوں کی تصدیق کرتی ہوئی جواس کے پہلے سے موجود ہیں وہ حق کی طرف اور ایک سیدھے راستے کی طرف رہنمائی کرتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(46,31,'46:31','اے ہماری قوم،اللہ کی طرف بلانے والے کی دعوت قبول کرو اور اس پر ایمان لے آؤ،اللہ تمھارے گناہوں کو معاف کردے گا اور تم کو دردناک عذاب سے بچائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(46,32,'46:32','اور جو شخص اللہ کے داعی کی دعوت پرلبیک نہیں کہے گا تو وہ زمین میں ہرانہیں سکتا اور اللہ کے سوا اس کا کوئی مددگار نہ ہوگا ایسے لوگ کھلی ہوئی گمراہی میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(46,33,'46:33','کیا ان لوگوں نے نہیں دیکھا کہ جس خدانے آسمانوں اور زمین کو پیدا کیا اوروہ ان کے پیدا کرنے سے نہیں تھکا،اس پر قدرت رکھتا ہے کہ وہ مُردوں کوزندہ کردے،ہاں وہ ہر چیز پرقادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(46,34,'46:34','اورجس دن یہ انکار کرنے والے آگ کے سامنے لائے جائیں گے،کیا یہ حقیقت نہیں ہے وہ کہیں گے کہ ہاں ، ہمارے رب کی قسمارشاد ہوگا پھر چکھو عذاب اس انکار کے بدلے جوتم کررہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(46,35,'46:35','پس تم صبر کروجس طرح ہمت والے پیغمبروں نے صبر کیا اور ان کے لیے جلدی نہ کرو جس دن یہ لوگ اس چیز کودیکھیں گے جس کا ان سے وعدہ کیا جارہا ہے تو گویا کہ وہ دن کی ایک گھڑی سے زیادہ نہیں رہے یہ پہنچا دینا ہے پس وہی لوگ برباد ہوں گے جو نافرمانی کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(47,1,'47:1','جن لوگوں نے انکار کیا اور اللہ کے راستہ سے روکا، اللہ نے ان کے اعمال کو رائیگاں کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(47,2,'47:2','اور جو لوگ ایمان لائے اور انھوں نے نیک عمل کيے اور اس چیزکو مانا جو محمد پراُتارا گیاہےاور وہ حق ہے ان کے رب کی طرف سے، اللہ نے ان کی برائیاں ان سے دور کردیں اور ان کا حال درست کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(47,3,'47:3','یہ اس لیے کہ جن لوگوں نے انکار کیا انھوں نے باطل کی پیروی کیاور جو لوگ ایمان لائے انھوں نے حق کی پیروی کی جو ان کے رب کی طرف سے ہےاس طرح اللہ لوگوں کے لیے ان کی مثالیں بیان کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(47,4,'47:4','پس جب منکروں سے تمھارا مقابلہ ہو تو ان کی گردنیں مارویہاں تک کہ جب خوب قتل کرچکو تو ان کو مضبوط باندھ لو پھر اس کے بعد یا تو احسان کرکے چھوڑناہے یامعاوضہ لے کر، یہاں تک کہ جنگ اپنے ہتھیار رکھ دےیہ ہے کاماور اگر اللہ چاہتا تو وہ ان سے بدلہ لے لیتا،مگر تاکہ وہ تم لوگوں کو ایک دوسرے سے آزمائے اور جولوگ اللہ کی راہ میں مارے جائیں گے، اللہ ان کے اعمال کو ہرگز ضائع نہیں کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,5,'47:5','وہ ان کی رہنمائی فرمائے گااور ان کا حال درست کردے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,6,'47:6','اور ان کو جنت میں داخل کرے گا جس کی اُس نے انھیں پہچان کرادی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(47,7,'47:7','اے ایمان والو، اگر تم اللہ کی مدد کروگے تووہ تمھاری مددکرے گا اور تمھارے قدموں کو جمادے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,8,'47:8','اورجن لوگوں نے انکار کیاان کے لیے تباہی ہے اور اللہ ان کے اعمال کو ضائع کردے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,9,'47:9','یہ اس سبب سے کہ انھوں نے اس چیزکو ناپسند کیا جو اللہ نے اُتاری ہےپس اللہ نے ان کے اعمال کو اکارت کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(47,10,'47:10','کیا یہ لوگ ملک میں چلے پھرے نہیں کہ وہ اُن لوگوں کا انجام دیکھتے جو ان سے پہلے گزرچکے ہیں ،اللہ نے ا ن کو اُکھاڑپھینکا اور منکروں کے سامنے انھیں کی مثالیں آنی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(47,11,'47:11','یہ اس سبب سے کہ اللہ ایمان والوں کا کارساز ہے اور منکروں کا کوئی کارساز نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(47,12,'47:12','بے شک اللہ ان لوگوں کو جو ایمان لائے اور جنھوں نے نیک عمل کیاایسے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گیاور جن لوگوں نے انکار کیا، وہ فائده اٹھا رہے ہیں اور کھارہے ہیں جیسے کہ چوپائے کھائیں ،اور آگ ان لوگوں کاٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(47,13,'47:13','اور کتنی ہی بستیاں ہیں جو قوت میں تمھاری اس بستی سے زیادہ تھیں جس نے تم کو نکالا ہےہم نے ان کو ہلاک کردیاپس کوئی ان کامددگار نہ ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(47,14,'47:14','کیاوہ جواپنے رب کی طرف سے ایک واضح دلیل پر ہے، وہ اس کی طرح ہوجائے گا جس کی بدعملی اس کے لیے خوش نمابنادی گئی ہے اور وہ اپنی خواہشات پرچل رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(47,15,'47:15','جنت کی مثال، جس کاوعدہ ڈرنے والوں سے کیاگیا ہے، اس کی کیفیت یہ ہے کہ اس میں نہریں هيں ایسے پانی کی جس میں تغیرنہ ہوگا اور نہریں ہوں گی دودھ کی جس کا مزہ نہیں بدلا ہوگا اور نہریں ہوں گی شراب کی جو پینے والوں کے لیے لذیذ ہوگی اور نہریں ہوں گی شہد کی جو بالکل صاف ہوگااور ان کے لیے وہاں ہر قسم کے پھل ہوں گے اور ان کے رب کی طرف سے بخشش ہوگیکیایہ لوگ ان جیسے ہوسکتے ہیں جو ہمیشہ آگ میں رہیں گے اور ان کوکھولتا ہوا پانی پینے کے لیے دیاجائے گاپس وہ ان کی آنتوں کو ٹکڑے ٹکڑے کردے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,16,'47:16','اور ان میں کچھ لوگ ایسے ہیں جو تمھاری طرف کان لگاتے ہیں یہاں تک کہ جب وہ تمھارے پاس سے باہر جاتے ہیں توعلم والوں سے پوچھتے ہیں کہ انھوں نے ابھی کیا کہایہی لوگ ہیں جن کے دلوں پر اللہ نے مہر لگادی اوروہ اپنی خواہشوں پرچلتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(47,17,'47:17','اور جن لوگوں نے ہدایت کی راہ اختیار کی تو اللہ ان کواور زیادہ ہدایت دیتا ہے اور ان کو ان کی پرہیزگاری عطاکرتاہے');
INSERT INTO "ur_wahid_uddin" VALUES(47,18,'47:18','یہ لوگ تو بس اس کے منتظر ہیں کہ قیامت ان پر اچانک آجائے تو اس کی علامتیں ظاہر ہوچکی ہیں پس جب وہ آجائے گی تو ان کے لیے نصیحت حاصل کرنے کاموقع کہاں رہے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,19,'47:19','پس جان لوکہ اللہ کے سوا کوئی معبود نہیں اور معافی مانگو اپنے قصور کے لیے اور مومن مردوں اور مومن عورتوں کے لیےاور اللہ جانتا ہے تمھارے چلنے پھرنے کو اور تمھارے ٹھکانوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(47,20,'47:20','اور جو لوگ ایما ن لائے ہیں وہ کہتے ہیں کہ کوئی سورہ کیوں نہیں اُتاری جاتی پس جب ایک واضح سورہ اُتار دی گئی اور اس میں جنگ کابھی ذکر تھا تو تم نے دیکھا کہ جن کے دلوں میں بیماری ہے، وہ تمھاری طرف اس طرح دیکھ رہے ہیں جیسے کسی پر موت چھاگئی ہوپس خرابی ہے ان کی');
INSERT INTO "ur_wahid_uddin" VALUES(47,21,'47:21','حکم ماننا ہے اور بھلی بات کہنا ہےپس جب معاملہ کاقطعی فیصلہ ہوجائے تو اگر وہ اللہ سے سچے رہتے تو ان کے لیے بہت بہتر ہوتا');
INSERT INTO "ur_wahid_uddin" VALUES(47,22,'47:22','پس اگر تم پھرگئے تو اس کے سوا تم سے کچھ متوقع نہیں کہ تم زمین میں فساد کرو اور آپس کی قرابتوں کوقطع کرو');
INSERT INTO "ur_wahid_uddin" VALUES(47,23,'47:23','یہی لوگ ہیں جن کو اللہ نے اپنی رحمت سے دور کیا، پس ان کو بہرا کردیا اور ان کی آنکھوں کو اندھا کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(47,24,'47:24','کیایہ لوگ قرآن میں غور نہیں کرتے یا دلوں پر ان کے تالے لگے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(47,25,'47:25','جو لوگ پیٹھ پھیر کرہٹ گئے، بعد اس کے کہ ہدایت ان پر واضح ہوگئی،شیطان نے ان کوفریب دیااور اللہ نے ان کو ڈھیل دے دی');
INSERT INTO "ur_wahid_uddin" VALUES(47,26,'47:26','یہ اس سبب سے ہوا کہ انھوں نے ان لوگوں سے جوکہ خداکی اُتاری ہوئی چیز کوناپسند کرتے ہیں ، کہا کہ بعض باتوں میں ہم تمھارا کہنامان لیں گےاور اللہ ان کی رازداری کوجانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(47,27,'47:27','پس اس وقت کیاہوگا جب کہ فرشتے ان کی روحیں قبض کرتے ہوں گے، ان کے منہ اور ان کی پیٹھوں پر مارتے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(47,28,'47:28','یہ اس سبب سے کہ انھوں نے اس چیزکی پیروی کی جو اللہ کوغصہ دلانے والی تھی اورانھوں نے اس کی رضا کوناپسند کیاپس اللہ نے ان کے اعمال اکارت کردئے');
INSERT INTO "ur_wahid_uddin" VALUES(47,29,'47:29','جن لوگوں کے دلوں میں بیماری ہے، کیاوہ خیال کرتے ہیں کہ اللہ ان کے کینے کوکبھی ظاہر نہ کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,30,'47:30','اور اگرہم چاہتے تو ہم ان کو تمھیں دکھادیتے، پس تم ان کی علامتوں سے ان کو پہچان لیتے اور تم ان کے اندازِکلام سے ضرور ان کوپہچان لوگےاور اللہ تمھارے اعمال کو جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(47,31,'47:31','اور ہم ضرور تم کوآزمائیں گے تاکہ ہم ان لوگوں کو جان لیں جوتم میں جہاد کرنے والے ہیں اور ثابت قدم رہنے والے ہیں اورہم تمھارے حالات کی جانچ کرلیں');
INSERT INTO "ur_wahid_uddin" VALUES(47,32,'47:32','بے شک جن لوگوں نے انکار کیا اور اللہ کے راستے سے روکااور رسول کی مخالفت کی جب کہ ہدایت ان پر واضح ہوچکی تھی، وہ اللہ کو کچھ نقصان نہ پہنچاسکیں گےاور اللہ ان کے اعمال کوڈھادے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,33,'47:33','اے ایمان والو، اللہ کی اطاعت کرو اور رسول کی اطاعت کرو اور اپنے اعمال کو برباد نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(47,34,'47:34','بے شک جن لوگوں نے انکار کیا اوراللہ کے راستے سے روکا، پھر وہ منکر ہی مرگئے، اللہ ان کوکبھی نہ بخشے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,35,'47:35','پس تم ہمت نہ ہارواور صلح کی درخواست نہ کرواور تم ہی غالب رہوگےاوراللہ تمھارے ساتھ ہےاور وہ ہرگز تمھارے اعمال میں کمی نہ کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,36,'47:36','دنیا کی زندگی تو محض ایک کھیل تماشا ہے اور اگر تم ایمان لاؤ اور تقویٰ اختیار کرو تو اللہ تم کو تمھارے اجرعطاکرے گا اور وہ تمھارے مال تم سے نہ مانگے گا');
INSERT INTO "ur_wahid_uddin" VALUES(47,37,'47:37','اگروہ تم سے تمھارے مال طلب کرے پھر آخر تک طلب کرتا رہے تو تم بخل کرنے لگو اور اللہ تمھارے کینے کوظاہر کردے');
INSERT INTO "ur_wahid_uddin" VALUES(47,38,'47:38','ہاں ، تم وہ لوگ ہو کہ تم کو اللہ کی راہ میں خرچ کرنے کے لیے بلایا جاتا ہے، پس تم میں سے کچھ لوگ ہیں جو بخل کرتے ہیں اور جو شخص بخل کرتا ہے تو وہ اپنے ہی سے بخل کرتا ہےاور اللہ بے نیاز ہے، تم محتاج ہواوراگر تم پھر جاؤ تو اللہ تمھاری جگہ دوسری قوم لے آئے گا،پھر وہ تم جیسے نہ ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(48,1,'48:1','بے شک ہم نے تم کو کھلی ہوئی فتح دے دی');
INSERT INTO "ur_wahid_uddin" VALUES(48,2,'48:2','تاکہ اللہ تمھاری اگلی اور پچھلی خطائیں معاف کردےاور تمھارے اوپر اپنی نعمت کی تکمیل کردے اور تم کوسیدھا راستہ دکھائے');
INSERT INTO "ur_wahid_uddin" VALUES(48,3,'48:3','اور الله تم کوزبردست مددعطا کرے');
INSERT INTO "ur_wahid_uddin" VALUES(48,4,'48:4','وہی ہے جس نے مومنوں کے دل میں اطمینان اُتارا، تاکہ ان کے ایمان کے ساتھ ان کاایمان اور بڑھ جائےاور آسمانوں اورزمین کی فوجیں اللہ ہی کی ہیں اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,5,'48:5','تاکہ اللہ مومن مردوں اور مومن عورتوں کو ایسے باغوں میں داخل کرے جن کے نیچے نہریں جاری ہوں گی، وہ ان میں ہمیشہ رہیں گےاور تاکہ الله ان کی برائیاں ان سے دور کردےاور یہ اللہ کے نزدیک بڑی کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,6,'48:6','اور تاکہ اللہ منافق مردوں اور منافق عورتوں کو اور مشرک مردوں اور مشرک عورتوں کوعذاب دے جو اللہ کے ساتھ برے گمان رکھتے ہیں برائی کی گردش انھیں پر ہےاوران پر اللہ کا غضب ہوا اور ان پراس نے لعنت کی اور ان کے لیے اس نے جہنم تیار کررکھی ہے اوروہ بہت براٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,7,'48:7','اور آسمانوں اورزمین کی فوجیں اللہ ہی کی ہیں اور اللہ زبردست ہے،حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,8,'48:8','بے شک ہم نے تم کو گواہی دینے والا اور بشارت دینے والااورڈرانے والابناکر بھیجا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,9,'48:9','تاکہ تم لوگ اللہ پر اور اس کے رسول پر ایمان لاؤ اور اس کی مدد کرو اور اس کی تعظیم کرواور تم اللہ کی تسبیح کرو صبح وشام');
INSERT INTO "ur_wahid_uddin" VALUES(48,10,'48:10','جولوگ تم سے بیعت کرتے ہیں وہ درحقیقت اللہ سے بیعت کرتے ہیں اللہ کا ہاتھ ان کے ہاتھوں کے اوپر ہے پھر جو شخص اس کوتوڑے گا، اس کے توڑنے کاوبال اسی پر پڑے گا اورجو شخص اس عہد کو پورا کرے گا جو اس نے اللہ سے کیاہے تو اللہ اس کو بڑا اجر عطافرمائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(48,11,'48:11','جودیہاتی پیچھے رہ گئے وہ اب تم سے کہیں گے کہ ہم کو ہمارے اموال اور ہمارے بال بچوں نے مشغول رکھا، پس آپ ہمارے لیے معافی کی دعا فرمائیں یہ اپنی زبانوں سے وہ بات کہتے ہیں جو ان کے دلوں میں نہیں ہے تم کہوکہ کون ہے جواللہ کے سامنے تمھارے لیے کچھ اختیاررکھتا ہو، اگر وہ تم کو کوئی نقصان یاکوئی نفع پہنچانا چاہے، بلکہ اللہ اس سے باخبر ہے جو تم کررہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(48,12,'48:12','بلکہ تم نے یہ گمان کیا کہ رسول اور مومنین کبھی اپنے گھروالوں کی طرف لوٹ کر نہ آئیں گےاور یہ خیال تمھارے دلوں کوبہت بھلانظر آیا اور تم نے بہت برے گمان کيےاور تم بربادہونے والے لوگ ہوگئے');
INSERT INTO "ur_wahid_uddin" VALUES(48,13,'48:13','اور جو ایمان نہ لایااللہ پر اور اس کے رسول پر توہم نے ایسے منکروں کے لیے دہکتی ہوئی آگ تیار کررکھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,14,'48:14','اور آسمانوں اور زمین کی بادشاہی اللہ ہی کی ہے، وہ جس کو چاہے بخشے اور جس کو چاہے عذاب دے اور اللہ بخشنے والا،رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,15,'48:15','جب تم غنیمتیں لینے کے لیے چلوگے توپیچھے رہ جانے والے لوگ کہیں گے کہ ہمیں بھی اپنے ساتھ چلنے دو وہ چاہتے ہیں کہ اللہ کی بات کو بدل دیں کہو کہ تم ہرگز ہمارے ساتھ نہیں چل سکتےاللہ پہلے ہی یہ فرماچکا ہےتو وہ کہیں گے بلکہ تم لوگ ہم سے حسد کرتے ہو، بلکہ یہی لوگ بہت کم سمجھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(48,16,'48:16','پیچھے رہنے والے دیہاتیوں سے کہو کہ عنقریب تم ایسے لوگوں کی طرف بلائے جاؤ گے جو بڑے زور آور ہیں ، تم ان سے لڑوگے یاوہ اسلام لائیں گے پس اگر تم حکم مانو گے تو اللہ تم کو اچھا اجر دے گا اور اگر تم روگردانی کروگے، جیسا کہ تم اس سے پہلے روگردانی کرچکے ہو تو وہ تم کو دردناک عذاب دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(48,17,'48:17','نہ اندھے پر کوئی گناہ ہے اور نہ لنگڑے پر کوئی گناہ ہے اور نہ بیمار پر کوئی گناہ ہے اور جو شخص اللہ اور اس کے رسول کی اطاعت کرے گا، اس کو اللہ ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گیاور جوشخص روگردانی کرے گا اس کو وہ دردناک عذاب دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(48,18,'48:18','اللہ ایمان والوں سے راضی ہوگیاجب کہ وہ تم سے درخت کے نیچے بیعت کررہے تھے، اللہ نے جان لیا جو کچھ ان کے دلوں میں تھاپس اس نے ان پر سکینت نازل فرمائی اور ان کو انعام میں ایک قریبی فتح دے دی');
INSERT INTO "ur_wahid_uddin" VALUES(48,19,'48:19','اوربہت سی غنیمتیں بھی جن کو وہ حاصل کریں گےاور اللہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,20,'48:20','اور اللہ نے تم سے بہت سی غنیمتوں کا وعدہ کیا ہے جن کو تم لوگے،پس یہ اس نے تم کوفوری طور پر دے دیا اور لوگوں کے ہاتھ تم سے روک دئے اور تاکہ اہل ایمان کے لیے یہ ایک نشانی بن جائےاور تاکہ وہ تم کو سیدھے راستے پر چلائے');
INSERT INTO "ur_wahid_uddin" VALUES(48,21,'48:21','اورایک فتح اور بھی ہے جس پر تم ابھی قادر نہیں ہوئےاللہ نے اس کا احاطہ کررکھاہے اور اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,22,'48:22','اور اگر یہ منکر لوگ تم سے لڑتے تو وہ ضرورپیٹھ پھیر کر بھاگتے، پھر وہ نہ کوئی حمایتی پاتے اور نہ مددگار');
INSERT INTO "ur_wahid_uddin" VALUES(48,23,'48:23','یہ اللہ کی سنت ہے جو پہلے سے چلی آرہی ہےاور تم اللہ کی سنت میں کوئی تبدیلی نہ پاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(48,24,'48:24','اوروہی ہے جس نے مکہ کی وادی میں ان کے ہاتھ تم سے اورتمھارے ہاتھ ان سے روک دئے، بعد اس کے کہ تم کو ان پر قابو دے دیاتھااور اللہ تمھارے کاموں کو دیکھ رہاتھا');
INSERT INTO "ur_wahid_uddin" VALUES(48,25,'48:25','وہی لوگ ہیں جنھوں نے کفر کیا اور تم کو مسجد حرام سے روکا اور قربانی کے جانوروں کوبھی روکے رکھا کہ وہ اپنی جگہ پر نہ پہنچیں اوراگر (مکہ میں ) بہت سے مومن مرد اور مومن عورتیں نہ ہوتیں جن کو تم لاعلمی میں پیس ڈالتے، پھر ان کے باعث تم پر بے خبری میں الزام آتا، تاکہ اللہ جس کو چاہے اپنی رحمت میں داخل کرے، اوراگر وہ لوگ الگ ہوگئے ہوتے تو ان میں جو منکر تھے ان کو ہم دردناک سزادیتے');
INSERT INTO "ur_wahid_uddin" VALUES(48,26,'48:26','جب انکار کرنے والوں نے اپنے دلوں میں حمیت پیدا کی،جاہلیت کی حمیت،پھر اللہ نے اپنی طرف سے سکینت نازل فرمائی اپنے رسول پر اور ایمان والوں پر، اور اللہ نے ان کوتقویٰ کی بات پر جمائے رکھا اور وہ اس کے زیادہ حق دار اور اس کے اہل تھےاور اللہ ہر چیز کا جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,27,'48:27','بے شک اللہ نے اپنے رسول کو سچا خواب دکھایا جو مطابقِ واقعہ ہےبے شک اللہ نے چاہاتو تم مسجد حرام میں ضرور داخل ہوگے، امن کے ساتھ ، بال مونڈتے ہوئے اپنے سروں كے اور کترتے ہوئے، تم کو کوئی اندیشہ نہ ہوگاپس اللہ نے وہ بات جانی جو تم نے نہیں جانی،پس اس سے پہلے اس نے ایک فتح دے دی');
INSERT INTO "ur_wahid_uddin" VALUES(48,28,'48:28','اور اللہ ہی ہے جس نے اپنے رسول کو ہدایت اور دین حق کے ساتھ بھیجا، تاکہ وہ اس کو تمام دینوں پر غالب کردےاور اللہ کافی گواہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(48,29,'48:29','محمد اللہ کے رسول ہیں اور جو لوگ ان کے ساتھ ہیں وہ منکروں پرسخت ہیں اور آپس میں مہربان ہیں تم ان کورکوع میں اورسجدہ میں دیکھوگے،وہ اللہ کافضل اور اس کی رضامندی کی طلب میں لگے رہتے ہیں ان کی نشانی ان کے چہروں پر ہے سجدہ کے اثر سے،ان کی یہ مثال تورات میں ہےاور انجیل میں ان کی مثال یہ ہے کہ جیسے کھیتی، اس نے اپنااَنکھوانکالا،پھر اس کو مضبوط کیا،پھر وہ اور موٹا ہوا،پھر اپنے تنے پر کھڑا ہوگا، وہ کسانوں کو بھلالگتا ہے تاکہ ان سے منکروں کو جلائےان میں سے جو لوگ ایمان لائے اور نیک عمل کیا، اللہ نے ان سے معافی کا اور بڑے ثواب کا وعدہ کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(49,1,'49:1','اے ایمان والو، تم اللہ اور اس كے رسول سے آگے نہ بڑھو،اور اللہ سے ڈرو،بے شک اللہ سننے والا،جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(49,2,'49:2','اے ایمان والو،تم اپنی آوازیں پیغمبر کی آواز سے اوپر مت کرو اور نہ اس کو اس طرح آواز دے کر پکارو جس طرح تم آپس میں ایک دوسرے کو پکارتے ہوکہیں ایسا نہ ہو کہ تمھارے اعمال برباد ہوجائیں اور تم کو خبر بھی نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(49,3,'49:3','جولوگ اللہ کے رسول کے آگے اپنی آوازیں پست رکھتے ہیں ، وہی وہ لوگ ہیں جن کے دلوں کو اللہ نے تقویٰ کے لیے جانچ لیا ہے ان کے لیے معافی ہے اور بڑا ثواب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(49,4,'49:4','جولوگ تم کو حجروں کے باہر سے پکارتے ہیں ان میں سے اکثر سمجھ نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(49,5,'49:5','اور اگر وہ صبر کرتے یہاں تک کہ تم خود ان کے پاس نکل کر آجاؤ تو یہ ان کے لیے بہتر ہوتااور اللہ بخشنے والا،مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(49,6,'49:6','اے ایمان والو،اگر کوئی فاسق تمھارے پاس خبر لائے تو تم اچھی طرح تحقیق کرلیاکرو،کہیں ایسانہ ہو کہ تم کسی گروہ کونادانی سے کوئی نقصان پہنچادو،پھرتم کواپنے کيے پر پچھتاناپڑے');
INSERT INTO "ur_wahid_uddin" VALUES(49,7,'49:7','اور جان لو کہ تمہارے درمیان اللہ کا رسول ہے اگر وہ بہت سے معاملات میں تمہاری بات مان لے تو تم بڑی مشکل میں پڑجاؤ لیکن اللہ نے تم کو ایمان کی محبت دی اور اس کو تمہارے دلوں میں مرغوب بنا دیا، اور کفر اور فسق اور نافرمانی سے تم کو متنفر کردیا ایسے ہی لوگ راہ راست پر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(49,8,'49:8','اللہ کے فضل اور انعام سے، اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(49,9,'49:9','اور اگر مسلمانوں کے دو گروہ آپس میں لڑجائیں تو ان کے درمیان صلح کراؤ پھراگر ان میں کا ایک گروہ دوسرے گروہ پر زیادتی کرے تو اس گروہ سے لڑو جو زیادتی کرتا ہے،یہاں تک کہ وہ اللہ کے حکم کی طرف لوٹ آئےپھر اگر وہ لوٹ آئے تو ان کے درمیان عدل کے ساتھ صلح کراؤ اور انصاف کرو، بے شک اللہ انصاف کرنے والوں کوپسند کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(49,10,'49:10','اہلِ ایمان سب بھائی ہیں ، پس اپنے بھائیوں کے درمیان ملاپ کراؤ اور اللہ سے ڈرو،تاکہ تم پر رحم کیا جائے');
INSERT INTO "ur_wahid_uddin" VALUES(49,11,'49:11','اے ایمان والو، نہ مرد دوسرے مردوں کا مذاق اُڑائیں ، ہوسکتا ہے کہ وہ ان سے بہتر ہوں اور نہ عورتیں دوسری عورتوں کامذاق اُڑائیں ،ہوسکتا ہے کہ وہ ان سے بہتر ہوں اور نہ ایک دوسرے کوطعنہ دو اور نہ ایک دوسرے کوبرے لقب سے پکاروایمان لانے کے بعد گناہ کانام دينا برا ہےاور جوباز نہ آئیں تو وہی لوگ ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(49,12,'49:12','اے ایمان والو، بہت سے گمانوں سے بچو، کیوں کہ بعض گمان گناہ ہوتے ہیں اور ٹوہ میں نہ لگو اور تم میں سے کوئی کسی کی غیبت نہ کرےکیا تم میں سے کوئی اس بات کوپسند کرے گا کہ وہ اپنے مرے ہوئے بھائی کاگوشت کھائے، اس کوتم خود ناگوار سمجھتے ہواور اللہ سے ڈروبے شک اللہ معاف کرنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(49,13,'49:13','اے لوگو،ہم نے تم کو ایک مرداور ایک عورت سے پیدا کیا اور تم کو قوموں اور خاندانوں میں تقسیم کردیا،تاکہ تم ایک دوسرے کو پہچانو بے شک اللہ کے نزدیک تم میں سب سے زیادہ عزت والاوہ ہے جو سب سے زیادہ پرہیزگار ہےبے شک اللہ جاننے والا، خبر رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(49,14,'49:14','اعراب کہتے ہیں کہ ہم ایمان لائے، کہوکہ تم ایمان نہیں لائےبلکہ یوں کہو کہ ہم نے اسلام قبول کیا،اور ابھی تک ایمان تمھارے دلوں میں داخل نہیں ہوااور اگر تم اللہ اور اس کے رسول کی اطاعت کرو تو اللہ تمھارے اعمال میں سے کچھ کمی نہیں کرے گابے شک اللہ بخشنے والا، رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(49,15,'49:15','مومن تو بس وہ ہیں جو اللہ اور اس کے رسول پر ایمان لائے پھر انھوں نے شک نہیں کیا اوراپنے مال اور اپنی جان سے اللہ کے راستے میں جہاد کیا، یہی سچے لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(49,16,'49:16','کہو، کیا تم اللہ کو اپنے دین سے آگاہ کررہے ہو حالاں کہ اللہ جانتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہےاور اللہ ہر چیز سے باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(49,17,'49:17','یہ لوگ تم پر احسان رکھتے ہیں کہ انھوں نے اسلام قبول کیا ہے کہو کہ اپنے اسلام کا احسان مجھ پر نہ رکھو،بلکہ اللہ کاتم پراحسان ہے کہ اس نے تم کو ایمان کی ہدایت دیاگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(49,18,'49:18','بےشک اللہ آسمانوں اور زمین کی چھپی ہوئی باتوں کوجانتا ہےاور اللہ دیکھتا ہے جوکچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(50,1,'50:1','قٓقسم ہے باعظمت قرآن کی');
INSERT INTO "ur_wahid_uddin" VALUES(50,2,'50:2','بلکہ ان کوتعجب ہوا کہ ان کے پاس انھیں میں سے ایک ڈرانے والا آیا،پس منکروں نے کہاکہ یہ تعجب کی چیزہے');
INSERT INTO "ur_wahid_uddin" VALUES(50,3,'50:3','کیاجب ہم مرجائیں گے اور مٹی ہوجائیں گے ،یہ دوبارہ زندہ ہونا بہت بعید ہے');
INSERT INTO "ur_wahid_uddin" VALUES(50,4,'50:4','ہم کومعلوم ہے جتنا زمین ان کے اندر سے گھٹاتی ہے اور ہمارے پاس کتاب ہے جس میں سب کچھ محفوظ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(50,5,'50:5','بلکہ انھوں نے حق کوجھٹلایا ہے جب کہ وہ ان کے پاس آچکا ہے،پس وہ الجھن میں پڑے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(50,6,'50:6','کیاان لوگوں نے اپنے اوپر آسمان کو نہیں دیکھا، ہم نے کیسااس کو بنایا اور اس کورونق دی اور اس میں کوئی رخنہ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(50,7,'50:7','اور زمین کوہم نے پھیلایا اور اس میں پہاڑڈا ل دئے اور اس میں ہر قسم کی رونق کی چیز اُگائی');
INSERT INTO "ur_wahid_uddin" VALUES(50,8,'50:8','سمجھانے کواور یاددلانے کو ہر اس بندے کے لیے جورجوع کرے');
INSERT INTO "ur_wahid_uddin" VALUES(50,9,'50:9','اور ہم نے آسمان سے برکت والا پانی اُتارا،پھراس سے ہم نے باغ اُگائے اور کاٹی جانے والی فصلیں');
INSERT INTO "ur_wahid_uddin" VALUES(50,10,'50:10','اورکھجوروں کے لمبے درخت جن میں تہہ بہ تہہ خوشے لگتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(50,11,'50:11','بندوں کی روزی کے لیےاور ہم نے اس کے ذریعہ سے مُردہ زمین کوزندہ کیااسی طرح زمین سے نکلناہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(50,12,'50:12','ان سے پہلے نوح کی قوم اور اصحاب الرس اور ثمود');
INSERT INTO "ur_wahid_uddin" VALUES(50,13,'50:13','اور عاد اور فرعون اور لوط کے بھائی');
INSERT INTO "ur_wahid_uddin" VALUES(50,14,'50:14','اور ایکہ والے اور تبع کی قوم نے بھی جھٹلایا، سب نے پیغمبروں کو جھٹلایا پس میراڈرانا ان پرواقع ہوکر رہا');
INSERT INTO "ur_wahid_uddin" VALUES(50,15,'50:15','کیا ہم پہلی بارپیدا کرنے سے عاجز رہےبلکہ یہ لوگ ازسرِ نو پیداکرنے کی طرف سے شبہ میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(50,16,'50:16','اور ہم نے انسان کوپیدا کیا اور ہم جانتے ہیں ان باتوں کو جو اس کے دل میں آتی ہیں اور ہم رگِ گردن سے بھی زیادہ اس سے قریب ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(50,17,'50:17','جب دو لینے والے لیتے رہتے ہیں جو کہ دائیں اور بائیں طرف بیٹھے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(50,18,'50:18','کوئی لفظ وہ نہیں بولتا مگر اس کے پاس ایک مستعد نگراں موجود ہے');
INSERT INTO "ur_wahid_uddin" VALUES(50,19,'50:19','اور موت کی بے ہوشی حق کے ساتھ آپہنچی یہ وہی چیزہے جس سے تو بھاگتا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(50,20,'50:20','اور صور پھونکا جائے گا، یہ ڈرانے کادن ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(50,21,'50:21','ہرشخص اس طرح آگیا کہ اس کے ساتھ ایک ہانکنے والا ہے اور ایک گواہی دینے والا');
INSERT INTO "ur_wahid_uddin" VALUES(50,22,'50:22','تم اس سے غفلت میں رہےپس ہم نے تمھارے اوپر سے پردہ ہٹا دیا پس آج تمھاری نگاہ تیز ہے');
INSERT INTO "ur_wahid_uddin" VALUES(50,23,'50:23','اور اس کے ساتھ کافرشتہ کہے گا،یہ جومیرے پاس تھا حاضر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(50,24,'50:24','جہنم میں ڈال دو ناشکر،مخالف کو');
INSERT INTO "ur_wahid_uddin" VALUES(50,25,'50:25','نیکی سے روکنے والا،حدسے بڑھنے والا،شبہ ڈالنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(50,26,'50:26','جس نے اللہ کے ساتھ دوسرے معبود بنائے،پس اس کو ڈال دو سخت عذاب میں');
INSERT INTO "ur_wahid_uddin" VALUES(50,27,'50:27','اس کا ساتھی شیطان کہے گا کہ اے ہمارے رب، میں نے اسے سرکش نہیں بنایا بلکہ وہ خود راہ بھولا ہوا، دور پڑا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(50,28,'50:28','ارشاد ہوگا، میرے سامنے جھگڑا نہ کرو اور میں نے پہلے ہی تم کو عذاب سے ڈرادیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(50,29,'50:29','میرے یہاں بات بدلی نہیں جاتی اور میں بندوں پرظلم کرنے والا نہیں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(50,30,'50:30','جس دن ہم جہنم سے کہیں گے، کیا تو بھر گئیاور وہ کہے گی کہ کچھ اور بھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(50,31,'50:31','اور جنت ڈرنے والوں کے قریب لائی جائے گی، کچھ دور نہ رہے گی');
INSERT INTO "ur_wahid_uddin" VALUES(50,32,'50:32','یہ ہے وہ چیز جس کاتم سے وعدہ کیا جاتا تھا، ہر رجوع کرنے والے اور یاد رکھنے والے کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(50,33,'50:33','جو شخص رحمان سے ڈرا بن دیکھے اور رجوع ہونے والا دل لے کر آیا');
INSERT INTO "ur_wahid_uddin" VALUES(50,34,'50:34','داخل ہوجاؤ اس میں سلامتی کے ساتھ، یہ دن ہمیشہ رہے گا');
INSERT INTO "ur_wahid_uddin" VALUES(50,35,'50:35','وہاں ان کے لیے وہ سب ہوگا جو وہ چاہیں اور ہمارے پاس مزید ہے');
INSERT INTO "ur_wahid_uddin" VALUES(50,36,'50:36','اور ہم ان سے پہلے کتنی ہی قوموں کوہلاک کرچکے ہیں ،وہ قوت میں ان سے زیادہ تھیں ، پس انھوں نے ملکوں کوچھان مارا کہ ہے کوئی پناہ کی جگہ');
INSERT INTO "ur_wahid_uddin" VALUES(50,37,'50:37','اس میں یاد دہانی ہے اس شخص کے لیے جس کے پاس دل ہو یا وہ کان لگائے متوجہ ہوکر');
INSERT INTO "ur_wahid_uddin" VALUES(50,38,'50:38','اور ہم نے آسمانوں اور زمین کواور جو کچھ ان کے درمیان ہے چھ دن میں بنایا اور ہم کو کچھ تکان نہیں ہوئی');
INSERT INTO "ur_wahid_uddin" VALUES(50,39,'50:39','پس جوکچھ وہ کہتے ہیں اس پر صبر کرو اور اپنے رب کی تسبیح کرو حمد کے ساتھ،سورج نکلنے سے پہلے اور اس کے ڈوبنے سے پہلے');
INSERT INTO "ur_wahid_uddin" VALUES(50,40,'50:40','اور رات میں اس کی تسبیح کرو اورسجدوں کے پیچھے');
INSERT INTO "ur_wahid_uddin" VALUES(50,41,'50:41','اور کان لگائے رکھو کہ جس دن پکارنے والا بہت قریب سے پکارے گا');
INSERT INTO "ur_wahid_uddin" VALUES(50,42,'50:42','جس دن لوگ بالیقین چنگھاڑ کو سنیں گے وہ نکلنے کادن ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(50,43,'50:43','بے شک ہم ہی جِلاتے ہیں اور ہم ہی مارتے ہیں اور ہماری ہی طرف لوٹنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(50,44,'50:44','جس دن زمین ان پرسے کھل جائے گی، وہ سب دوڑتے ہوں گے،یہ اكٹھا کرناہمارے لیے آسان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(50,45,'50:45','ہم جانتے ہیں جوکچھ یہ لوگ کہہ رہے ہیں اور تم ان پرجبرکرنے والے نہیں ہو پس تم قرآن کے ذریعہ اُس شخص کونصیحت کروجو میرے ڈرانے سے ڈرے');
INSERT INTO "ur_wahid_uddin" VALUES(51,1,'51:1','قسم ہے ان ہواؤں کی جوگرداُڑانے والی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,2,'51:2','پھر وہ اٹھالیتی ہیں بوجھ');
INSERT INTO "ur_wahid_uddin" VALUES(51,3,'51:3','پھر وہ چلنے لگتی ہیں آہستہ');
INSERT INTO "ur_wahid_uddin" VALUES(51,4,'51:4','پھرالگ الگ کرتی ہیں معاملہ');
INSERT INTO "ur_wahid_uddin" VALUES(51,5,'51:5','بے شک تم سے جو وعدہ کیاجارہا ہے وہ سچ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(51,6,'51:6','اور بے شک انصاف ہوناضرورہے');
INSERT INTO "ur_wahid_uddin" VALUES(51,7,'51:7','قسم ہے جال دار آسمان کی');
INSERT INTO "ur_wahid_uddin" VALUES(51,8,'51:8','بے شک تم ایک اختلاف میں پڑے ہوئے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(51,9,'51:9','اس سے وہی پھرتا ہے جوپھیرا گیا');
INSERT INTO "ur_wahid_uddin" VALUES(51,10,'51:10','مارے گئے اٹکل سے باتیں کرنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(51,11,'51:11','جو غفلت میں بھولے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,12,'51:12','وہ پوچھتے ہیں کہ کب ہے بدلہ کادن');
INSERT INTO "ur_wahid_uddin" VALUES(51,13,'51:13','جس دن وہ آگ پررکھے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(51,14,'51:14','چکھومزہ اپنی شرارت کا، یہ ہے وہ چیز جس کی تم جلدی کررہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(51,15,'51:15','بے شک ڈرنے والے لوگ باغوں میں اور چشموں میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(51,16,'51:16','لےرہے ہوں گے جو کچھ ان کے رب نے ان کو دیا، وہ اس سے پہلے نیکی کرنے والے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(51,17,'51:17','وہ راتوں کو کم سوتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(51,18,'51:18','اور صبح کے وقتوں میں وہ معافی مانگتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(51,19,'51:19','اور ان کے مال میں سائل اور محروم کاحصہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(51,20,'51:20','اور زمین میں نشانیاں ہیں یقین کرنے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(51,21,'51:21','اور خود تمھارے اندر بھی،کیا تم دیکھتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,22,'51:22','اور آسمان میں تمھاری روزی ہے اور وہ چیز بھی جس کاتم سے وعدہ کیا جارہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(51,23,'51:23','پس آسمان اور زمین کے رب کی قسم، وہ یقینی ہے جیسا کہ تم بولتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(51,24,'51:24','کیا تم کو ابراہیم کے معزز مہمانوں کی بات پہنچی');
INSERT INTO "ur_wahid_uddin" VALUES(51,25,'51:25','جب وہ اس کے پاس آئےپھر ان کوسلام کیا اس نے کہا تم لوگوں کو بھی سلام ہے— کچھ اجنبی لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,26,'51:26','پھروہ اپنے گھر کی طرف چلا اور ایک بچھڑا بھنا ہوالے آیا');
INSERT INTO "ur_wahid_uddin" VALUES(51,27,'51:27','پھر اس کو ان کے پاس رکھا،اس نے کہا، آپ لوگ کھاتے کیوں نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,28,'51:28','پھر وہ دل میں ان سے ڈرا انھوں نے کہا کہ ڈرو متاور ان کو ذی علم لڑکے کی بشارت دی');
INSERT INTO "ur_wahid_uddin" VALUES(51,29,'51:29','پھر اس کی بیوی بولتی ہوئی آئی،پھر ماتھے پر ہاتھ مارا اور کہنے لگی کہ بوڑھی،بانجھ');
INSERT INTO "ur_wahid_uddin" VALUES(51,30,'51:30','انھوں نے کہا کہ ایسا ہی فرمایا ہے تیرے رب نے بے شک وہ حکمت والا جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(51,31,'51:31','ابراہیم نے کہا کہ اے فرشتو، تم کو کیا مہم درپیش ہے');
INSERT INTO "ur_wahid_uddin" VALUES(51,32,'51:32','انھوں نے کہا کہ ہم ایک مجرم قوم (قوم لوط)کی طرف بھیجے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,33,'51:33','تاکہ هم اس پرپکی ہوئی مٹی کے پتھر برسائیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,34,'51:34','جو نشان لگائے ہوئے ہیں تمھارے رب کے پاس ان لوگوں کے لیے جو حد سے گزرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,35,'51:35','پھر وہاں جتنے ایمان والے تھے ان کو ہم نے نکال لیا');
INSERT INTO "ur_wahid_uddin" VALUES(51,36,'51:36','پس وہاں ہم نے ایک گھر کے سوا کوئی مسلم گھر نہ پایا');
INSERT INTO "ur_wahid_uddin" VALUES(51,37,'51:37','اور ہم نے اس میں ایک نشانی چھوڑی ان لوگوں کے لیے جو دردناک عذاب سے ڈرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,38,'51:38','اور موسیٰ میں بھی نشانی ہے جب کہ ہم نے اس کو فرعون کے پاس ایک کھلی ہوئی دلیل کے ساتھ بھیجا');
INSERT INTO "ur_wahid_uddin" VALUES(51,39,'51:39','تو وہ اپنی قوت کے ساتھ پھر گیااور کہا کہ یہ جادو گر هے يا مجنون هے');
INSERT INTO "ur_wahid_uddin" VALUES(51,40,'51:40','پس ہم نے اس کو اور اس کی فوج کو پکڑا،پھر ان کو سمندر میں پھینک دیا اور وہ سزاوارِ ملامت تھا');
INSERT INTO "ur_wahid_uddin" VALUES(51,41,'51:41','اور عاد میں بھی نشانی ہے جب کہ ہم نے ان پر ایک بے نفع ہوا بھیج دی');
INSERT INTO "ur_wahid_uddin" VALUES(51,42,'51:42','وہ جس چیز پر سے بھی گزری، اس کو ریزہ ریزہ کرکے چھوڑدیا');
INSERT INTO "ur_wahid_uddin" VALUES(51,43,'51:43','اور ثمود میں بھی نشانی ہے جب کہ ان سے کہا گیا کہ تھوڑی مدت تک کے لیے فائدہ اُٹھالو');
INSERT INTO "ur_wahid_uddin" VALUES(51,44,'51:44','پس انھوں نے اپنے رب کے حکم سے سرکشی کی، پس ان کو کڑک نے پکڑلیا اور وہ دیکھ رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(51,45,'51:45','پھر وہ نہ اُٹھ سکے اور نہ اپنا بچاؤ کرسکے');
INSERT INTO "ur_wahid_uddin" VALUES(51,46,'51:46','اور نوح کی قوم کو بھی اس سے پہلے، بے شک وہ نافرمان لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(51,47,'51:47','اور ہم نے آسمان کو اپنی قدرت سے بنایا اور ہم کشادہ کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,48,'51:48','اورزمین کوہم نے بچھایا، پس کیاہی ہم خوب بچھانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,49,'51:49','اور ہم نے ہر چیز کو جوڑا جوڑا بنایا ہے تاکہ تم دھیان کرو');
INSERT INTO "ur_wahid_uddin" VALUES(51,50,'51:50','پس دوڑو اللہ کی طرف، میں ا س کی طرف سے ایک کھلا ہوا ڈرانے والاہوں');
INSERT INTO "ur_wahid_uddin" VALUES(51,51,'51:51','اور اللہ کے ساتھ کوئی اور معبود نہ بناؤ، میں اس کی طرف سے تمھارے لیے کھلا ہواڈرانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(51,52,'51:52','اسی طرح ان کے اگلوں کے پاس کوئی پیغمبر ایسا نہیں آیا جس کو انھوں نے ساحریا مجنون نہ کہاہو');
INSERT INTO "ur_wahid_uddin" VALUES(51,53,'51:53','کیا یہ ایک دوسرے کو اس کی وصیت کرتے چلے آرہے ہیں ، بلکہ یہ سب سرکش لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,54,'51:54','پس تم ان سے اعراض کرو،تم پر کچھ الزام نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,55,'51:55','اور سمجھاتے رہو کیوں کہ سمجھاناایمان والوں کونفع دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(51,56,'51:56','اور میں نے جن اور انسان کوصرف اسی لیے پیدا کیا ہے کہ وہ میری عبادت کریں');
INSERT INTO "ur_wahid_uddin" VALUES(51,57,'51:57','میں ان سے رزق نہیں چاہتا اور نہ یہ چاہتا ہوں کہ وہ مجھ کو کھلائیں');
INSERT INTO "ur_wahid_uddin" VALUES(51,58,'51:58','بے شک اللہ ہی روزی دینے والا،زور آور،زبردست ہے');
INSERT INTO "ur_wahid_uddin" VALUES(51,59,'51:59','پس جن لوگوں نے ظلم کیا، ان کا ڈول بھر چکا ہے جیسے ان کے ساتھیوں کے ڈول بھرے تھے،پس وہ جلدی نہ کریں');
INSERT INTO "ur_wahid_uddin" VALUES(51,60,'51:60','پس منکروں کے لیے خرابی ہے ان کے اس دن سے جس کا ان سے وعدہ کیا جارہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(52,1,'52:1','قسم ہے طور کی');
INSERT INTO "ur_wahid_uddin" VALUES(52,2,'52:2','اور لکھی ہوئی کتاب کی');
INSERT INTO "ur_wahid_uddin" VALUES(52,3,'52:3','کشادہ ورق میں');
INSERT INTO "ur_wahid_uddin" VALUES(52,4,'52:4','اور آباد گھر کی');
INSERT INTO "ur_wahid_uddin" VALUES(52,5,'52:5','اور اونچی چھت کی');
INSERT INTO "ur_wahid_uddin" VALUES(52,6,'52:6','اور ابلتے ہوئے سمندر کی');
INSERT INTO "ur_wahid_uddin" VALUES(52,7,'52:7','بے شک تمھارے رب کاعذاب واقع ہوکر رہے گا');
INSERT INTO "ur_wahid_uddin" VALUES(52,8,'52:8','اس کو کوئی ٹالنے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(52,9,'52:9','جس دن آسمان ڈگمگائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(52,10,'52:10','اور پہاڑ چلنے لگیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(52,11,'52:11','پس خرابی ہے اس دن جھٹلانے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(52,12,'52:12','جوباتیں بناتے ہیں کھیلتے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(52,13,'52:13','جس دن وہ جہنم کی آگ کی طرف ڈھکیلے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(52,14,'52:14','یہ ہے وہ آگ جس کوتم جھٹلاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(52,15,'52:15','کیایہ جادوہے یا تم کو نظر نہیں آتا');
INSERT INTO "ur_wahid_uddin" VALUES(52,16,'52:16','اس میں داخل ہوجاؤ پھر تم صبرکرو یا صبر نہ کرو،تمھارے لیے یکساں ہے تم وہی بدلہ پارہے ہو جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(52,17,'52:17','بے شک متقی لوگ باغوں اور نعمتوں میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(52,18,'52:18','وہ خوش دل ہوں گے ان چیزوں سے جو ان کے رب نے انھیں دی ہوں گی،اور ان کے رب نے ان کو دوزخ کے عذاب سے بچالیا');
INSERT INTO "ur_wahid_uddin" VALUES(52,19,'52:19','کھاؤ اور پیو مزہ کے ساتھ اپنے اعمال کے بدلہ میں');
INSERT INTO "ur_wahid_uddin" VALUES(52,20,'52:20','تکیہ لگائے ہوئے صف بہ صف تختوں کے اوپراورہم بڑی بڑی آنکھوں والی حوریں ان سے بیاہ دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(52,21,'52:21','اور جولوگ ایمان لائے اور ان کی اولاد بھی ان کی راہ پر ایمان کے ساتھ چلی،ان کے ساتھ ہم ان کی اولاد کو بھی جمع کریں گے،اور ان کے عمل میں سے کوئی چیزکم نہیں کریں گےہرآدمی اپنی کمائی میں پھنسا ہوا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(52,22,'52:22','اورہم ان کی پسند کے میوے اورگوشت ان کو برابر دیتے رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(52,23,'52:23','ان کے درمیان شراب کے پیالوں کے تبادلے ہورہے ہوں گے جو لغویت اور گناہ سے پاک ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(52,24,'52:24','اور ان کی خدمت میں بچے دوڑتے پھررہے ہوں گے،گویا کہ وہ حفاظت سے رکھے ہوئے موتی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(52,25,'52:25','وہ ایک دوسرے کی طرف متوجہ ہوکر بات کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(52,26,'52:26','وہ کہیں گے کہ ہم اس سے پہلے اپنےگھروالوں میں ڈرتے رہتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(52,27,'52:27','پس اللہ نے ہم پرفضل فرمایا اور ہم کو لُوکے عذاب سے بچالیا');
INSERT INTO "ur_wahid_uddin" VALUES(52,28,'52:28','ہم اس سے پہلے اسی کو پکارتے تھے،بے شک وہ نیک سلوک والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(52,29,'52:29','پس تم نصیحت کرتے رہو، اپنے رب کے فضل سے تم نہ کاہن ہو اور نہ مجنون');
INSERT INTO "ur_wahid_uddin" VALUES(52,30,'52:30','کیا وہ کہتے ہیں کہ یہ ایک شاعر ہےہم اس پرگردشِ زمانہ کے منتظر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(52,31,'52:31','کہوکہ انتظار کرو،میں بھی تمھارے ساتھ انتظار کرنے والوں میں ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(52,32,'52:32','کیا ان کی عقلیں ان کو یہی سکھاتی ہیں یا یہ سرکش لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(52,33,'52:33','کیا وہ کہتے ہیں کہ یہ قرآن کوخود بنالایا ہےبلکہ وہ ایمان نہیں لانا چاہتے');
INSERT INTO "ur_wahid_uddin" VALUES(52,34,'52:34','پس وہ اس کے مانند کوئی کلام لے آئیں ،اگر وہ سچے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(52,35,'52:35','کیاوہ کسی خالق کے بغیر پیدا ہوگئے یا وہ خود ہی خالق ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(52,36,'52:36','کیازمین اور آسمان کو انھوں نے پیدا کیاہے،بلکہ وہ یقین نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(52,37,'52:37','کیا ان کے پاس تمھارے رب کے خزانے ہیں یا وہ داروغہ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(52,38,'52:38','کیاان کے پاس کوئی سیڑھی ہے جس پر وہ باتیں سُن لیا کرتے ہیں ، تو ان کا سُننے والا کوئی کھلی ہوئی دلیل لے آئے');
INSERT INTO "ur_wahid_uddin" VALUES(52,39,'52:39','کیا اللہ کے لیے بیٹیاں ہیں اور تمھارے لیے بیٹے');
INSERT INTO "ur_wahid_uddin" VALUES(52,40,'52:40','کیا تم ان سے معاوضہ مانگتے ہو کہ وہ تاوان کے بوجھ سے دبے جارہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(52,41,'52:41','کیاان کے پاس غیب ہے کہ وہ لکھ لیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(52,42,'52:42','کیاوہ کوئی تدبیر کرناچاہتے ہیں ، پس انکار کرنے والے خودہی اس تدبیرمیں گرفتار ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(52,43,'52:43','کیا اللہ کے سواان کااور کوئی معبود ہےاللہ پاک ہے ان کے شریک بنانے سے');
INSERT INTO "ur_wahid_uddin" VALUES(52,44,'52:44','اور اگر وہ آسمان سے کوئی ٹکڑا گرتا ہوادیکھیں تو وہ کہیں گے کہ یہ تہ بہ تہ بادل ہے');
INSERT INTO "ur_wahid_uddin" VALUES(52,45,'52:45','پس ان کوچھوڑو،یہاں تک کو وہ اپنے اس دن سے دوچار ہوں جس میں ان کے ہوش جاتے رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(52,46,'52:46','جس دن ان کی تدبیریں ان کے کچھ کام نہ آئیں گی اور نہ ان کو کوئی مدد ملے گی');
INSERT INTO "ur_wahid_uddin" VALUES(52,47,'52:47','اور ان ظالموں کے لیے اس کے سوا بھی عذاب ہے،لیکن ان میں سے اکثر نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(52,48,'52:48','اور تم صبر کے ساتھ اپنے رب کے فیصلہ کاانتظار کروبے شک تم ہماری نگاہ میں ہواور اپنے رب کی تسبیح کرو اس کی حمد کے ساتھ، جس وقت تم اٹھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(52,49,'52:49','اوررات کوبھی اس کی تسبیح کرو،اور ستاروں کے پیچھے ہٹنے کے وقت بھی');
INSERT INTO "ur_wahid_uddin" VALUES(53,1,'53:1','قسم ہے ستارے کی جب کہ وہ غروب ہو');
INSERT INTO "ur_wahid_uddin" VALUES(53,2,'53:2','تمھارا ساتھی نہ بھٹکاہے اور نہ گمراہ ہوا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,3,'53:3','اور وہ اپنے جی سے نہیں بولتا');
INSERT INTO "ur_wahid_uddin" VALUES(53,4,'53:4','یہ ایک وحی ہے جو اس پر بھیجی جاتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,5,'53:5','اس کو زبردست قوت والے نے تعلیم دی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,6,'53:6','عاقل اوردانانے پھر وہ نمودارہوا');
INSERT INTO "ur_wahid_uddin" VALUES(53,7,'53:7','اور وہ آسمان کے اونچے کنارے پر تھا');
INSERT INTO "ur_wahid_uddin" VALUES(53,8,'53:8','پھر وہ نزدیک ہواپس وہ اتر آیا');
INSERT INTO "ur_wahid_uddin" VALUES(53,9,'53:9','پھر دوکمانوں کے برابر یا اس سے بھی کم فاصلہ رہ گیا');
INSERT INTO "ur_wahid_uddin" VALUES(53,10,'53:10','پھر اللہ نے وحی کی اپنے بندہ کی طرف جو وحی کی');
INSERT INTO "ur_wahid_uddin" VALUES(53,11,'53:11','جھوٹ نہیں کہا رسول کے دل نے جو اس نے دیکھا');
INSERT INTO "ur_wahid_uddin" VALUES(53,12,'53:12','اب کیا تم اس چیز پر اس سے جھگڑتے ہو جو اس نے دیکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,13,'53:13','اور اس نے ایک بار اور بھی اس کو اترتے ہوئے دیکھا');
INSERT INTO "ur_wahid_uddin" VALUES(53,14,'53:14','سدر ۃ المنتہیٰ کے پاس');
INSERT INTO "ur_wahid_uddin" VALUES(53,15,'53:15','اس کے پاس ہی بہشت ہے آرام سے رہنے کی');
INSERT INTO "ur_wahid_uddin" VALUES(53,16,'53:16','جب کہ سدرہ پرچھارہا تھا جو کچھ کہ چھارہا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(53,17,'53:17','نگاہ بہکی نہیں اور نہ حد سے بڑھی');
INSERT INTO "ur_wahid_uddin" VALUES(53,18,'53:18','اس نے اپنے رب کی بڑی بڑی نشانیاں دیکھیں');
INSERT INTO "ur_wahid_uddin" VALUES(53,19,'53:19','بھلا تم نے لات اورعُزّی پر غور کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,20,'53:20','اور تیسرے ایک اور منات پر');
INSERT INTO "ur_wahid_uddin" VALUES(53,21,'53:21','کیا تمھارے لیے بیٹے ہیں اور خدا کے لیے بیٹیاں');
INSERT INTO "ur_wahid_uddin" VALUES(53,22,'53:22','یہ تو بہت بے ڈھنگی تقسیم ہوئی');
INSERT INTO "ur_wahid_uddin" VALUES(53,23,'53:23','یہ محض نام ہیں جوتم نے اور تمھارے باپ دادا نے رکھ لیے ہیں اللہ نے ان کے حق میں کوئی دلیل نہیں اُتاریوہ محض گمان کی پیروی کررہے ہیں اور نفس کی خواہش کی،حالاں کہ ان کے پاس ان کے رب کی جانب سے ہدایت آچکی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,24,'53:24','کیا انسان وہ سب پالیتا ہے جووہ چاہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,25,'53:25','پس اللہ کے اختیار میں ہے آخرت اور دنیا');
INSERT INTO "ur_wahid_uddin" VALUES(53,26,'53:26','اور آسمانوں میں کتنے فرشتے ہیں جن کی سفارش کچھ بھی کام نہیں آسکتی،مگر بعد اس کے کہ اللہ اجازت دے جس کو وہ چاہے اور پسند کرے');
INSERT INTO "ur_wahid_uddin" VALUES(53,27,'53:27','بے شک جولوگ آخرت پر ایمان نہیں رکھتے، وہ فرشتوں کوعورتوں کے نام سے موسوم کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(53,28,'53:28','حالاں کہ ان کے پاس اس پرکوئی دلیل نہیں وہ محض گمان پرچل رہے ہیں اور گمان امر ِحق میں ذرا بھی مفید نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(53,29,'53:29','پس تم ایسے شخص سے اعراض کرو جوہماری نصیحت سے منہ موڑےاور وہ دنیا کی زندگی کے سوا اور کچھ نہ چاہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,30,'53:30','ان کی سمجھ بس یہیں تک پہنچی ہے تمھارا رب خوب جانتا ہے کہ کون اس کے راستے سے بھٹکا ہوا ہے اور وہ اس کو بھی خوب جانتا ہے جو راہ ِراست پر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,31,'53:31','اوراللہ ہی کاہے جوکچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے،تاکہ وہ بدلہ دے برا کام کرنے والوں کو ان کے کيے کا اور بدلہ دے بھلائی والوں کو بھلائی سے');
INSERT INTO "ur_wahid_uddin" VALUES(53,32,'53:32','جوکہ بڑے گناہوں سے اور بے حیائی سے بچتے ہیں ، مگر کچھ آلودگیبے شک تمھارے رب کی بخشش کی بڑی سمائی ہےوہ تم کوخوب جانتا ہے جب کہ اس نے تم کو زمین سے پیداکیااور جب تم اپنی ماؤں کے پیٹ میں جنین کی شکل میں تھےتو تم اپنے کو مقدس نہ سمجھو وہ تقویٰ والوں کوخوب جانتاہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,33,'53:33','بھلا تم نے اس شخص کودیکھا جس نے اعراض کیا');
INSERT INTO "ur_wahid_uddin" VALUES(53,34,'53:34','تھوڑا سادیااور رک گیا');
INSERT INTO "ur_wahid_uddin" VALUES(53,35,'53:35','کیا اس کے پاس غیب کاعلم ہےپس وہ دیکھ رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,36,'53:36','کیااس کو خبر نہیں پہنچی اس بات کی جوموسیٰ کے صحیفوں میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,37,'53:37','اور ابراہیم کي، جس نے اپنا قول پورا کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(53,38,'53:38','کہ کوئی اٹھانے والاکسی دوسرے کا بوجھ نہیں اٹھائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(53,39,'53:39','اور یہ کہ انسان کے لیے وہی ہے جو اس نے کمایا');
INSERT INTO "ur_wahid_uddin" VALUES(53,40,'53:40','اور یہ کہ اس کی کمائی عنقریب دیکھی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(53,41,'53:41','پھر اس کو پورا بدلہ دیاجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(53,42,'53:42','اور یہ کہ سب کو تمھارے رب تک پہنچنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,43,'53:43','بے شک وہی ہنساتا ہے اوررلاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,44,'53:44','اور وہی مارتا ہے اور جِلاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,45,'53:45','اور اسی نے دونوں قسم،نراور مادہ کو پیدا کیا');
INSERT INTO "ur_wahid_uddin" VALUES(53,46,'53:46','ایک بوندسے جب کہ وہ ٹپکائی جائے');
INSERT INTO "ur_wahid_uddin" VALUES(53,47,'53:47','اور اسی کے ذمہ ہے دوسری بار اٹھانا');
INSERT INTO "ur_wahid_uddin" VALUES(53,48,'53:48','اور اسی نے دولت دی اورسرمایہ دار بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(53,49,'53:49','اور وہی شعریٰ کارب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,50,'53:50','اور اللہ ہی نے ہلاک کیاعادِ اول کو');
INSERT INTO "ur_wahid_uddin" VALUES(53,51,'53:51','اورثمود کو پھر کسی کوباقی نہ چھوڑا');
INSERT INTO "ur_wahid_uddin" VALUES(53,52,'53:52','اور قوم نوح کو اس سے پہلے ،بے شک وہ نہایت ظالم اور سرکش لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(53,53,'53:53','اورالٹی ہوئی بستیوں کوبھی پھینک دیا');
INSERT INTO "ur_wahid_uddin" VALUES(53,54,'53:54','پس ان کو ڈھانک لیا جس چیز نے ڈھانک لیا');
INSERT INTO "ur_wahid_uddin" VALUES(53,55,'53:55','پس تم اپنے رب کے کن کن کرشموں کوجھٹلاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(53,56,'53:56','یہ ایک ڈرانے والا ہے پہلے ڈرانے والوں کی طرح');
INSERT INTO "ur_wahid_uddin" VALUES(53,57,'53:57','قریب آنے والی قریب آگئی');
INSERT INTO "ur_wahid_uddin" VALUES(53,58,'53:58','اللہ کے سوا کوئی اس کو ہٹانے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(53,59,'53:59','کیاتم کو اس بات سے تعجب ہوتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(53,60,'53:60','اور تم ہنستے ہواور تم روتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(53,61,'53:61','اور تم تکبر کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(53,62,'53:62','پس اللہ کے لیے سجدہ کرو اوراسی کی عبادت کرو');
INSERT INTO "ur_wahid_uddin" VALUES(54,1,'54:1','قیامت قریب آگئی اور چاندپھٹ گیا');
INSERT INTO "ur_wahid_uddin" VALUES(54,2,'54:2','اور وہ کوئی بھی نشانی دیکھیں تو وہ اعراض ہی کریں گے اور کہیں گے کہ یہ تو جادو ہے جوپہلے سے چلا آرہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(54,3,'54:3','اور انھوں نے جھٹلادیااور اپنی خواہشوں کی پیروی کی اور ہر کام کا وقت مقرر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(54,4,'54:4','اور ان کو وہ خبریں پہنچ چکی ہیں جس میں کافی عبرت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(54,5,'54:5','نہایت درجہ کی حکمت، مگر تنبیہات ان کو فائدہ نہیں دیتیں');
INSERT INTO "ur_wahid_uddin" VALUES(54,6,'54:6','پس ان سے اعراض کرو، جس دن پکارنے والا ایک ناگوار چیزکی طرف پکارے گا');
INSERT INTO "ur_wahid_uddin" VALUES(54,7,'54:7','آنکھیں جھکائے ہوئے قبروں سے نکل پڑیں گے گویا کہ وہ بکھری ہوئی ٹڈیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(54,8,'54:8','بھاگتے ہوئے پکارنے والے کی طرف،منکر کہیں گے کہ یہ دن بڑا سخت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(54,9,'54:9','ان سے پہلے نوح کی قوم نے جھٹلایا، انھوں نے ہمارے بندے کی تکذیب کی اور کہا کہ دیوانہ ہے اور جھڑک دیا');
INSERT INTO "ur_wahid_uddin" VALUES(54,10,'54:10','پس اس نے اپنے رب کوپکارا کہ میں مغلوب ہوں ، تو بدلہ لے');
INSERT INTO "ur_wahid_uddin" VALUES(54,11,'54:11','پس ہم نے آسمان کے دروازے موسلادھار بارش سے کھول دئے');
INSERT INTO "ur_wahid_uddin" VALUES(54,12,'54:12','اور زمین سے چشمے بہادئےپس سب پانی ایک کام پر مل گیا جو مقدر ہوچکا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(54,13,'54:13','اور ہم نے اس کوایک تختوں اور کیلوں والی (کشتی) پراُٹھالیا');
INSERT INTO "ur_wahid_uddin" VALUES(54,14,'54:14','وہ ہماری آنکھوں کے سامنے چلتی رہی اس شخص کا بدلہ لینے کے لیے جس کی ناقدری کی گئی تھی');
INSERT INTO "ur_wahid_uddin" VALUES(54,15,'54:15','اور اس کو ہم نے نشانی کے لیے چھوڑدیاپھر کوئی ہے سوچنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(54,16,'54:16','پھر کیسا تھا میرا عذاب اور میرا ڈرانا');
INSERT INTO "ur_wahid_uddin" VALUES(54,17,'54:17','اورہم نے قرآن کو نصیحت کے لیے آسان کردیا ہے، تو کیا کوئی ہے نصیحت حاصل کرنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(54,18,'54:18','عادنے جھٹلایاتو کیسا تھا میرا عذاب اور میرا ڈرانا');
INSERT INTO "ur_wahid_uddin" VALUES(54,19,'54:19','ہم نے ان پر ایک سخت ہوا بھیجی مسلسل نحوست (سخت سردی) کے دن میں');
INSERT INTO "ur_wahid_uddin" VALUES(54,20,'54:20','وہ لوگوں کو اُکھاڑ پھینکتی تھی جیسے کہ وہ اکھڑے ہوئے کھجوروں کے تنے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(54,21,'54:21','پھر کیسا تھا میرا عذاب اور میرا ڈرانا');
INSERT INTO "ur_wahid_uddin" VALUES(54,22,'54:22','اور ہم نے قرآن کو نصیحت کے لیے آسان کردیا، تو کیاکوئی ہے نصیحت حاصل کرنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(54,23,'54:23','ثمود نے ڈرسنانےوالوں کوجھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(54,24,'54:24','پس انھوں نے کہاکیا ہم اپنے ہی اندر کے ایک آدمی کے کہے پر چلیں گے،اس صورت میں توہم غلطی اور جنون میں پڑجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(54,25,'54:25','کیاہم سب میں سے اسی پرنصیحت اتری ہے ،بلکہ وہ جھوٹا ہے، بڑا بننے والا');
INSERT INTO "ur_wahid_uddin" VALUES(54,26,'54:26','اب وہ کل کے دن جان لیں گے کہ کون جھوٹا ہے اور بڑا بننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(54,27,'54:27','ہم اونٹنی کو بھیجنے والے ہیں ان کے لیے آزمائش بناکر،پس تم ان کاانتظار کرو، اور صبر کرو');
INSERT INTO "ur_wahid_uddin" VALUES(54,28,'54:28','اور ان کو آگاہ کردو کہ پانی ان میں بانٹ دیا گیا ہےہر ایک باری پرحاضر ہو');
INSERT INTO "ur_wahid_uddin" VALUES(54,29,'54:29','پھر انھوں نے اپنے آدمی کو پکارا،پس اس نے وار کیا اور اونٹنی کو کاٹ ڈالا');
INSERT INTO "ur_wahid_uddin" VALUES(54,30,'54:30','پھر کیسا تھا میراعذاب اور میرا ڈرانا');
INSERT INTO "ur_wahid_uddin" VALUES(54,31,'54:31','ہم نے ان پر ایک چنگھاڑ بھیجی ، تو وہ باڑھ والے کی روندی ہوئی باڑھ کی طرح ہوکر رہ گئے');
INSERT INTO "ur_wahid_uddin" VALUES(54,32,'54:32','اورہم نے قرآن کو نصیحت کے لیے آسان کردیا،تو کیا کوئی ہے نصیحت حاصل کرنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(54,33,'54:33','لوط کی قوم نے ڈرسنا نے والوں کو جھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(54,34,'54:34','ہم نے ان پر پتھر برسانے والی ہوا بھیجی صرف لوط کے گھروالے اس سے بچے،ان کو ہم نے بچالیا سحر کے وقت');
INSERT INTO "ur_wahid_uddin" VALUES(54,35,'54:35','اپنی جانب سے فضل کرکےہم اسی طرح بدلہ دیتے ہیں اس کو جو شکر کرے');
INSERT INTO "ur_wahid_uddin" VALUES(54,36,'54:36','اور لوط نے ان کوہماری پکڑسے ڈرایا، پھر انھوں نے اس ڈرانے میں جھگڑے پیدا کيے');
INSERT INTO "ur_wahid_uddin" VALUES(54,37,'54:37','اور وہ اس کے مہمانوں کواس سے لینے لگےپس ہم نے ان کی آنکھیں مٹادیں اب چکھو میرا عذاب اور میرا ڈرانا');
INSERT INTO "ur_wahid_uddin" VALUES(54,38,'54:38','اور صبح سویرے ان پر عذاب آپڑا جو ٹھہرچکا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(54,39,'54:39','اب چکھو میراعذاب اور میرا ڈرانا');
INSERT INTO "ur_wahid_uddin" VALUES(54,40,'54:40','اور ہم نے قرآن کونصیحت کے لیے آسان کردیا ہے، تو کیا کوئی ہے نصیحت حاصل کرنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(54,41,'54:41','اور فرعون والوں کے پاس پہنچے ڈرانے والے');
INSERT INTO "ur_wahid_uddin" VALUES(54,42,'54:42','انھوں نے ہماری تمام نشانیوں کو جھٹلایا تو ہم نے ان کو ایک غالب اور قوت والے کے پکڑنے کی طرح پکڑا');
INSERT INTO "ur_wahid_uddin" VALUES(54,43,'54:43','کیا تمھارے منکر ان لوگوں سے بہتر ہیں یاتمھارے لیے آسمانی کتابوں میں معافی لکھ دی گئی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(54,44,'54:44','کیا وہ کہتے ہیں کہ ہم ایسی جماعت ہیں جو غالب رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(54,45,'54:45','عنقریب یہ جماعت شکست کھائے گی اور پیٹھ پھیر کر بھاگے گی');
INSERT INTO "ur_wahid_uddin" VALUES(54,46,'54:46','بلکہ قیامت ان کے وعدہ کاوقت ہے اورقیامت بڑی سخت اوربڑی کڑوی چیز ہے');
INSERT INTO "ur_wahid_uddin" VALUES(54,47,'54:47','بے شک مجرم لوگ گمراہی میں اور بے عقلی میں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(54,48,'54:48','جس دن وہ منہ کے بل آگ میں گھسیٹے جائیں گےچکھو مزا آگ کا');
INSERT INTO "ur_wahid_uddin" VALUES(54,49,'54:49','ہم نے ہر چیز کوپیدا کیا ہے اندازہ سے');
INSERT INTO "ur_wahid_uddin" VALUES(54,50,'54:50','اور ہمارا حکم بس یکبارگی آجائے گا جیسے آنکھ کاجھپکنا');
INSERT INTO "ur_wahid_uddin" VALUES(54,51,'54:51','اور ہم ہلاک کرچکے ہیں تمھارے ساتھ والوں کو، پھر کیاکوئی ہے سوچنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(54,52,'54:52','اور جو کچھ انھوں نے کیا، سب کتابوں میں درج ہے');
INSERT INTO "ur_wahid_uddin" VALUES(54,53,'54:53','اور ہر چھوٹی اور بڑی بات لکھی ہوئی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(54,54,'54:54','بے شک ڈرنے والے باغوں میں اور نہروں میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(54,55,'54:55','وہ بیٹھے ہوں گے سچی بیٹھک میں ، قدرت والے بادشاہ کے پاس');
INSERT INTO "ur_wahid_uddin" VALUES(55,1,'55:1','رحمان نے');
INSERT INTO "ur_wahid_uddin" VALUES(55,2,'55:2','قرآن کی تعلیم دی');
INSERT INTO "ur_wahid_uddin" VALUES(55,3,'55:3','اس نے انسان کو پیدا کیا');
INSERT INTO "ur_wahid_uddin" VALUES(55,4,'55:4','اس کو بولنا سکھایا');
INSERT INTO "ur_wahid_uddin" VALUES(55,5,'55:5','سورج اور چاند کے لیے ایک حساب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(55,6,'55:6','اور ستارے اوردرخت سجدہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(55,7,'55:7','اوراس نے آسمان کواونچا کیا اور اس نے ترازو رکھ دی');
INSERT INTO "ur_wahid_uddin" VALUES(55,8,'55:8','کہ تم تولنے میں زیادتی نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(55,9,'55:9','اور انصاف کے ساتھ سیدھی ترازو تولو اور تول میں نہ گھٹاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(55,10,'55:10','اور زمین کو اس نے خلق کے لیے رکھ دیا');
INSERT INTO "ur_wahid_uddin" VALUES(55,11,'55:11','اس میں میوے ہیں اور کھجور ہیں جن کے اوپرغلاف ہوتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(55,12,'55:12','اور بھس والے اناج بھی ہیں اور خوشبودار پھول بھی');
INSERT INTO "ur_wahid_uddin" VALUES(55,13,'55:13','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(55,14,'55:14','اس نے پیدا کیا انسان کو ٹھیکرے کی طرح کھنکھناتی ہوئی مٹی سے');
INSERT INTO "ur_wahid_uddin" VALUES(55,15,'55:15','اوراس نے جنات کو آگ کی لپٹ سے پیداکیا');
INSERT INTO "ur_wahid_uddin" VALUES(55,16,'55:16','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(55,17,'55:17','اور وہ مالک ہے دونوں مشرق کااور دونوں مغرب کا');
INSERT INTO "ur_wahid_uddin" VALUES(55,18,'55:18','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(55,19,'55:19','اس نے چلائے دو دریا، مل کر چلنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(55,20,'55:20','دونوں کے درمیان ایک پردہ ہے جس سے وہ تجاوز نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(55,21,'55:21','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,22,'55:22','ان دونوں سے موتی اور مونگا نکلتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(55,23,'55:23','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(55,24,'55:24','اور اسی کی ہیں جہاز، سمندر میں اونچے کھڑے ہوئے جیسے پہاڑ');
INSERT INTO "ur_wahid_uddin" VALUES(55,25,'55:25','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,26,'55:26','جو بھی زمین پر ہے، وہ فنا ہونے والا');
INSERT INTO "ur_wahid_uddin" VALUES(55,27,'55:27','اور تیرے رب کی ذات باقی رہے گی، عظمت والی اور عزت والی');
INSERT INTO "ur_wahid_uddin" VALUES(55,28,'55:28','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(55,29,'55:29','اسی سے مانگتے ہیں جو آسمانوں اورزمین میں ہیں ہر روز اس کا ایک کام ہے');
INSERT INTO "ur_wahid_uddin" VALUES(55,30,'55:30','پھر تم اپنے رب کی کن کن نعمتوں کوجھٹلاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(55,31,'55:31','ہم جلدی فارغ ہونے والے ہیں تمھاری طرف ، اے دو بھاری قافلو');
INSERT INTO "ur_wahid_uddin" VALUES(55,32,'55:32','پھر تم اپنے رب کی کن کن نعمتوں کوجھٹلاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(55,33,'55:33','اے جنوں اور انسانوں کے گروہاگر تم سے ہوسکے کہ تم آسمانوں اورزمین کی حدود سے نکل جاؤ تو نکل جاؤ،تم نہیں نکل سکتے بغیر سند کے');
INSERT INTO "ur_wahid_uddin" VALUES(55,34,'55:34','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,35,'55:35','تم پرچھوڑے جائیں گے آگ کے شعلے اور دھواں تو تم بچاؤ نہ کرسکو گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,36,'55:36','پھر تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,37,'55:37','پھر جب آسمان پھٹ کر کھال کے مانند سرخ ہوجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(55,38,'55:38','پھر تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,39,'55:39','پس اس دن کسی انسان یا جن سے اس کے گناہ کی بابت پوچھ نہ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(55,40,'55:40','پھر تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,41,'55:41','مجرم پہچان لیے جائیں گے اپنی علامتوں سے، پھر پکڑا جائے گاپیشانی کے بال سے اور پاؤں سے');
INSERT INTO "ur_wahid_uddin" VALUES(55,42,'55:42','پھر تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,43,'55:43','یہ جہنم ہے جس کو مجرم لوگ جھوٹ بتاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(55,44,'55:44','وہ پھریں گے اس کے درمیان اورکھولتے پانی کے درمیان');
INSERT INTO "ur_wahid_uddin" VALUES(55,45,'55:45','پھر تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,46,'55:46','اور جو شخص اپنے رب کے سامنے کھڑا ہونے سے ڈرے، اس کے لیے دوباغ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(55,47,'55:47','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,48,'55:48','دونوں بہت شاخوں والے');
INSERT INTO "ur_wahid_uddin" VALUES(55,49,'55:49','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,50,'55:50','ان کے درمیان دوچشمے جاری ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,51,'55:51','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,52,'55:52','دونوں باغوں میں ہر پھل کی دو قسمیں');
INSERT INTO "ur_wahid_uddin" VALUES(55,53,'55:53','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,54,'55:54','وہ تکیہ لگائے ایسے بچھونوں پر بیٹھے ہوں گے جن کے استر دبیزریشم کے ہوں گےاور پھل ان باغوں کا جھک رہا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(55,55,'55:55','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,56,'55:56','ان میں نیچی نگاہ والی عورتیں ہوں گی، جنھیں ان لوگوں سے پہلے نہ کسی انسان نے چھوا ہوگا نہ کسی جن نے');
INSERT INTO "ur_wahid_uddin" VALUES(55,57,'55:57','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,58,'55:58','وہ ایسی ہوں گی جیسے کہ یاقوت اورمرجان');
INSERT INTO "ur_wahid_uddin" VALUES(55,59,'55:59','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,60,'55:60','نیکی کا بدلہ نیکی کے سوا اور کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(55,61,'55:61','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(55,62,'55:62','اور ان کے سوا دو باغ اور ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(55,63,'55:63','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,64,'55:64','دونوں گہرے سبز سیاہی مائل');
INSERT INTO "ur_wahid_uddin" VALUES(55,65,'55:65','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,66,'55:66','ان میں دوچشمے ہوں گے ابلتے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(55,67,'55:67','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(55,68,'55:68','ان میں پھل اور کھجور اور انارہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,69,'55:69','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,70,'55:70','ان میں خوب سیرت، خوب صورت عورتیں ہوں گی');
INSERT INTO "ur_wahid_uddin" VALUES(55,71,'55:71','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,72,'55:72','حوریں خیموں میں رہنے والیاں');
INSERT INTO "ur_wahid_uddin" VALUES(55,73,'55:73','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,74,'55:74','ان سے پہلے ان کو نہ کسی انسان نے ہاتھ لگایا ہوگا اور نہ کسی جن نے');
INSERT INTO "ur_wahid_uddin" VALUES(55,75,'55:75','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,76,'55:76','تکیہ لگائے سبز مسندوں پر اور قیمتی نفیس بچھونے پر');
INSERT INTO "ur_wahid_uddin" VALUES(55,77,'55:77','پھر تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے');
INSERT INTO "ur_wahid_uddin" VALUES(55,78,'55:78','بڑا بابرکت ہے تیرے رب کا نام بڑائی والااور عظمت والا');
INSERT INTO "ur_wahid_uddin" VALUES(56,1,'56:1','جب واقع ہونے والی واقع ہوجائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(56,2,'56:2','اس کے واقع ہونے میں کچھ جھوٹ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(56,3,'56:3','وہ پست کرنے والی،بلند کرنے والی ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(56,4,'56:4','جب کہ زمین ہلا ڈالی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(56,5,'56:5','اور پہاڑ ٹوٹ کرریزہ ریزہ ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(56,6,'56:6','پھر وہ پراگندہ غبار بن جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(56,7,'56:7','اور تم لوگ تین قسم کے ہوجاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(56,8,'56:8','پھردائیں والے،پس کیا خوب ہیں دائیں والے');
INSERT INTO "ur_wahid_uddin" VALUES(56,9,'56:9','اور بائیں والے، کیسے برے لوگ ہیں بائیں والے');
INSERT INTO "ur_wahid_uddin" VALUES(56,10,'56:10','اور آگے والے تو آگے ہی والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(56,11,'56:11','وہ مقرب لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(56,12,'56:12','نعمت کے باغوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(56,13,'56:13','ان کی بڑی تعداد اگلوں میں سے ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(56,14,'56:14','اورتھوڑے پچھلوں میں سے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(56,15,'56:15','جڑاؤ تختوں پر');
INSERT INTO "ur_wahid_uddin" VALUES(56,16,'56:16','تکیہ لگائے آمنے سامنے بیٹھے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(56,17,'56:17','پھررہے ہوں گے ان کے پاس بچے ہمیشہ رہنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(56,18,'56:18','آب خورے اور کوزے لیے ہوئے اور پیالہ صاف شراب کا');
INSERT INTO "ur_wahid_uddin" VALUES(56,19,'56:19','اس سے نہ درد سر ہوگا اور نہ عقل میں فتور آئے گا');
INSERT INTO "ur_wahid_uddin" VALUES(56,20,'56:20','اور میوے کہ جوچاہیں چُن لیں');
INSERT INTO "ur_wahid_uddin" VALUES(56,21,'56:21','اور پرندوں کاگوشت جو ان کو مرغوب ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,22,'56:22','اور بڑی آنکھوں والی حوریں');
INSERT INTO "ur_wahid_uddin" VALUES(56,23,'56:23','جیسے موتی کے دانے اپنے غلاف کے اندر');
INSERT INTO "ur_wahid_uddin" VALUES(56,24,'56:24','بدلہ ان کاموں کا جو وہ کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(56,25,'56:25','اس میں وہ کوئی لغو اور گناہ کی بات نہیں سنیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(56,26,'56:26','مگر صرف سلام سلام کابول');
INSERT INTO "ur_wahid_uddin" VALUES(56,27,'56:27','اور داہنے والے، کیاخوب ہیں داہنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(56,28,'56:28','بیری کے درختوں میں جن میں کانٹا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(56,29,'56:29','اور کیلے تہہ بہ تہہ');
INSERT INTO "ur_wahid_uddin" VALUES(56,30,'56:30','اور پھیلے ہوئے سائے');
INSERT INTO "ur_wahid_uddin" VALUES(56,31,'56:31','اور بہتا ہواپانی');
INSERT INTO "ur_wahid_uddin" VALUES(56,32,'56:32','اور کثرت سے میوے');
INSERT INTO "ur_wahid_uddin" VALUES(56,33,'56:33','جو نہ ختم ہوں گے اور نہ کوئی روک ٹوک ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(56,34,'56:34','اور اونچے بچھونے');
INSERT INTO "ur_wahid_uddin" VALUES(56,35,'56:35','ہم نے ان عورتوں کو خاص طور پر بنایا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(56,36,'56:36','پھر ان کو کنواری رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(56,37,'56:37','دل ربا اور ہم عمر');
INSERT INTO "ur_wahid_uddin" VALUES(56,38,'56:38','داہنے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(56,39,'56:39','اگلوں میں سے ایک بڑا گروہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(56,40,'56:40','اور پچھلوں میں سے بھی ایک بڑا گروہ');
INSERT INTO "ur_wahid_uddin" VALUES(56,41,'56:41','اور بائیں والے، کیسے برے ہیں بائیں والے');
INSERT INTO "ur_wahid_uddin" VALUES(56,42,'56:42','آگ میں اور کھولتے ہوئے پانی میں');
INSERT INTO "ur_wahid_uddin" VALUES(56,43,'56:43','اور سیاہ دھوئیں کے سایہ میں');
INSERT INTO "ur_wahid_uddin" VALUES(56,44,'56:44','نہ ٹھنڈا اور نہ عزت کا');
INSERT INTO "ur_wahid_uddin" VALUES(56,45,'56:45','یہ لوگ اس سے پہلے خوش حال تھے');
INSERT INTO "ur_wahid_uddin" VALUES(56,46,'56:46','اور وہ بھاری گناہ پر اصرار کرتے رہے');
INSERT INTO "ur_wahid_uddin" VALUES(56,47,'56:47','اور وہ کہتے تھے، کیا جب ہم مرجائیں گے اور ہم مٹی اور ہڈیاں ہوجائیں گے تو کیا ہم پھر اُٹھائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(56,48,'56:48','اور کیا ہمارے اگلے باپ دادا بھی');
INSERT INTO "ur_wahid_uddin" VALUES(56,49,'56:49','کہو کہ اگلے اور پچھلے');
INSERT INTO "ur_wahid_uddin" VALUES(56,50,'56:50','سب، جمع کيے جائیں گے ایک مقرر دن کے وقت پر');
INSERT INTO "ur_wahid_uddin" VALUES(56,51,'56:51','پھر تم لوگ، اے بہکے ہوئے اور جھٹلانے والے');
INSERT INTO "ur_wahid_uddin" VALUES(56,52,'56:52','زقوم کے درخت میں سے کھاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(56,53,'56:53','پھر اس سے اپنا پیٹ بھروگے');
INSERT INTO "ur_wahid_uddin" VALUES(56,54,'56:54','پھر اس پرکھولتا ہوا پانی پیوگے');
INSERT INTO "ur_wahid_uddin" VALUES(56,55,'56:55','پھرپیاسے اونٹوں کی طرح پیوگے');
INSERT INTO "ur_wahid_uddin" VALUES(56,56,'56:56','یہ ان کی مہمانی ہوگی انصاف کے دن');
INSERT INTO "ur_wahid_uddin" VALUES(56,57,'56:57','ہم نے تم کو پیدا کیا ہے، پھر تم تصدیق کیوں نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(56,58,'56:58','کیا تم نے غور کیا اس چیز پر جو تم ٹپکاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,59,'56:59','کیا تم اس کو بناتے ہو یا ہم ہیں بنانے والے');
INSERT INTO "ur_wahid_uddin" VALUES(56,60,'56:60','ہم نے تمھارے درمیان موت مقدر کی ہے اور ہم اس سے عاجز نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(56,61,'56:61','کہ تمھاری جگہ تمھارے جیسے پیداکردیں اور تم کوایسی صورت میں بنادیں جن کو تم جانتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(56,62,'56:62','اور تم پہلی پیدائش کو جانتے ہو، پھر کیوں سبق نہیں لیتے');
INSERT INTO "ur_wahid_uddin" VALUES(56,63,'56:63','کیا تم نے غور کیا اس چیز پر جو تم بوتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,64,'56:64','کیا تم اس کو اُگاتے ہو یا ہم ہیں اُگانے والے');
INSERT INTO "ur_wahid_uddin" VALUES(56,65,'56:65','اگر ہم چاہیں تو اس کو ریزہ ریزہ کردیں ، پھر تم باتیں بناتے رہ جاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(56,66,'56:66','ہم تو تاوان میں پڑگئے');
INSERT INTO "ur_wahid_uddin" VALUES(56,67,'56:67','بلکہ ہم بالکل محروم ہوگئے');
INSERT INTO "ur_wahid_uddin" VALUES(56,68,'56:68','کیا تم نے غور کیا اس پانی پر جو تم پیتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,69,'56:69','کیا تم نے اس کوبادل سے اُتارا ہے یا ہم ہیں اُتارنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(56,70,'56:70','اگر ہم چاہیں تو اس کو سخت کھاری بنادیں پھر تم شکر کیوں نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(56,71,'56:71','کیا تم نے غور کیا اس آگ پر جس کو تم جلاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,72,'56:72','کیا تم نے پیداکیا ہے اس کے درخت کو یا ہم ہیں اس کے پیدا کرنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(56,73,'56:73','ہم نے اس کو یاد دہانی بنایا ہے، اور مسافروں کے لیے فائدہ کی چیز');
INSERT INTO "ur_wahid_uddin" VALUES(56,74,'56:74','پس تم اپنے عظیم رب کے نام کی تسبیح کرو');
INSERT INTO "ur_wahid_uddin" VALUES(56,75,'56:75','پس نہیں ، میں قسم کھاتا ہوں ستاروں کے مواقع کی');
INSERT INTO "ur_wahid_uddin" VALUES(56,76,'56:76','اور اگر تم غور کرو تو یہ بہت بڑی قسم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(56,77,'56:77','بے شک یہ ایک عزت والا قرآن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(56,78,'56:78','ایک محفوظ کتاب میں');
INSERT INTO "ur_wahid_uddin" VALUES(56,79,'56:79','اس کو وہی چھوتے ہیں جوپاک بنائے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(56,80,'56:80','اترا ہوا ہے پروردگار عالم کی طرف سے');
INSERT INTO "ur_wahid_uddin" VALUES(56,81,'56:81','پھر کیا تم اس کلام کے ساتھ بے اعتنائی برتتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,82,'56:82','اور تم اپنا حصہ یہی لیتے ہو کہ تم اس کو جھٹلاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,83,'56:83','پھر کیوں نہیں ، جب کہ جان حلق میں پہنچتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(56,84,'56:84','اور تم اس وقت دیکھ رہے ہوتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,85,'56:85','اور ہم تم سے زیادہ اس شخص سے قریب ہوتے ہیں مگر تم نہیں دیکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(56,86,'56:86','پھر کیوں نہیں ، اگر تم محکوم نہیں ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,87,'56:87','تو تم اس جان کو کیوں نہیں لوٹالاتے، اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,88,'56:88','پس اگر وہ مقربین میں سے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,89,'56:89','تو راحت ہے اورعمدہ روزی ہے اور نعمت کاباغ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(56,90,'56:90','اوراگر وہ اصحابِ یمین میں سے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,91,'56:91','تو تمھارے لیے سلامتی، تو اصحاب یمین میں سے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(56,92,'56:92','اور اگر وہ جھٹلانے والے گمراہ لوگوں میں سے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(56,93,'56:93','تو گرم پانی کی ضیافت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(56,94,'56:94','اورجہنم میں داخل ہونا');
INSERT INTO "ur_wahid_uddin" VALUES(56,95,'56:95','بے شک یہ قطعی حق ہے');
INSERT INTO "ur_wahid_uddin" VALUES(56,96,'56:96','پس تم اپنے عظیم رب کے نام کی تسبیح کرو');
INSERT INTO "ur_wahid_uddin" VALUES(57,1,'57:1','اللہ کی تسبیح کرتی ہے ہر چیز جو آسمانوں اور زمین میں ہے اور وہ زبردست ہے حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,2,'57:2','آسمانوں اور زمین کی سلطنت اسی کی ہے وہ جِلاتا ہے اورمارتا ہے اور وہ ہر چیز پرقادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,3,'57:3','وہی اول بھی ہے اور آخر بھی اور ظاہر بھی ہے اور باطن بھیاور وہ ہر چیز کا جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,4,'57:4','وہی ہے جس نے آسمانوں اور زمین کو پیداکیا چھ دنوں میں ، پھر وہ عرش پر متمکن ہوا وہ جانتا ہے جو کچھ زمین کے اندر جاتا ہے اور جو کچھ اس سے نکلتا ہے اور جو کچھ آسمان سے اُترتا ہے اور جو کچھ اس میں چڑھتا ہے، اور وہ تمھارے ساتھ ہے جہاں بھی تم ہو،اور اللہ دیکھتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(57,5,'57:5','آسمانوں اور زمین کی سلطنت اسی کی ہے، اور اللہ ہی کی طرف لوٹتے ہیں سارے امور');
INSERT INTO "ur_wahid_uddin" VALUES(57,6,'57:6','وہ رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے، اور وہ دل کی باتوں کو جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,7,'57:7','ایمان لاؤ اللہ اور اس کے رسول پر اورخرچ کرو اس چیز میں سے جس میں اس نے تم کو امین بنایا ہے، پس جو لوگ تم میں سے ایمان لائیں اور خرچ کریں ان کے لیے بڑا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,8,'57:8','اورتم کو کیا ہوا کہ تم اللہ پر ایمان نہیں لاتےحالاں کہ رسول تم کو بلارہا ہے کہ اپنے رب پر ایمان لاؤ اوروہ تم سے عہد لے چکا ہے، اگر تم مومن ہو');
INSERT INTO "ur_wahid_uddin" VALUES(57,9,'57:9','وہی ہے جو اپنے بندے پر واضح آیتیں اُتارتا ہے تاکہ تم کو تاریکیوں سے روشنی کی طرف لے آئے، اور اللہ تمھارے اوپرنرمی کرنے والا ہے، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,10,'57:10','اور تم کو کیا ہوا کہ تم اللہ کے راستہ میں خرچ نہیں کرتے، حالاں کہ سب آسمان اور زمین آخر میں اللہ ہی کارہ جائے گاتم میں سے جو لوگ فتح کے بعد خرچ کریں اور لڑیں وہ ان لوگوں کے برابر نہیں ہوسکتے جنھوں نے فتح سے پہلے خرچ کیا اور لڑے،اوراللہ نے سب سے بھلائی کاوعدہ کیا ہے،اللہ جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(57,11,'57:11','کون ہے جو اللہ کو قرض دے،اچھا قرض،کہ وہ اس کو اس کے لیے بڑھائے، اوراس کے لیے باعزت اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,12,'57:12','جس دن تم مومن مردوں اور مومن عورتوں کو دیکھوگے کہ ان کی روشنی ان کے آگے اور ان کے دائیں چل رہی ہوگی— آج کے دن تم کو خوش خبری ہے باغوں کی جن کے نیچے نہریں جاری ہوں گی، یہ بڑی کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,13,'57:13','جس دن منافق مرد اور منافق عورتیں ایمان والوں سے کہیں گے کہ ہمیں موقع دو کہ ہم بھی تمھاری روشنی سے کچھ فائدہ اٹھالیں کہا جائے گا کہ تم اپنے پیچھے لوٹ جاؤ،پھر روشنی تلاش کروپھر ان کے درمیان ایک دیوار کھڑی کردی جائے گی جس میں ایک دروازہ ہوگااس کے اندر کی طرف رحمت ہوگی اور اس کے باہر کی طرف عذاب ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(57,14,'57:14','وہ ان کو پکاریں گے کہ کیا ہم تمھارے ساتھ نہ تھےوہ کہیں گے کہ ہاں ، مگر تم نے اپنے آپ کو فتنہ میں ڈالا اور راہ دیکھتے رہے اور شک میں پڑے رہے اور جھوٹی امیدوں نے تم کو دھوکہ میں رکھا، یہاں تک کہ اللہ کافیصلہ آگیا اور دھوکے باز نے تم کواللہ کے معاملہ میں دھوکہ دیا');
INSERT INTO "ur_wahid_uddin" VALUES(57,15,'57:15','پس آج نہ تم سے کوئی فدیہ قبول کیا جائے گا اور نہ ان لوگوں سے جنھوں نے کفر کیا تمھارا ٹھکانا آگ ہے وہی تمھاری رفیق ہےاوروہ برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,16,'57:16','کیا ایمان والوں کے لیے وہ وقت نہیں آیا کہ ان کے دل اللہ کی نصیحت کے آگے جھک جائیں اور اس حق کے آگے جو نازل ہوچکا ہے اور وہ ان لوگوں کی طرح نہ ہوجائیں جن کو پہلے کتاب دی گئی تھی، پھر ان پر لمبی مدت گزرگئی تو ان کے دل سخت ہوگئےاور ان میں سے اکثر لوگ نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(57,17,'57:17','جان لوکہ اللہ زمین کو زندگی دیتا ہے اس کی موت کے بعد، ہم نے تمھارے لیے نشانیاں بیان کردی ہیں ، تاکہ تم سمجھو');
INSERT INTO "ur_wahid_uddin" VALUES(57,18,'57:18','بے شک صدقہ دینے والے مرد اور صدقہ دینے والی عورتیں اوروہ لوگ جنھوں نے اللہ کوقرض دیا، اچھا قرض، تو ان کے لیے بڑھایا جائے گا اور ان کے لیے باعزت اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,19,'57:19','اور جو لوگ ایمان لائے اللہ پر اور اس کے رسولوں پر، وہی لوگ اپنے رب کے نزدیک صدیق اور شہید ہیں ان کے لیے ان کااجر اور ان کی روشنی ہے، اور جن لوگوں نے انکار کیااور ہماری آیتوں کوجھٹلایا وہ دوزخ کے لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(57,20,'57:20','جان لو کہ دنیا کی زندگی اس کے سوا کچھ نہیں کہ کھیل اور تماشہ ہے اور زینت اور باہمی فخر اور مال اور اولاد میں ایک دوسرے سے بڑھنے کی کوشش کرناهے جیسے کہ بارش کہ اس کی پیداوار کسانوں کو اچھی معلوم ہوتی ہے، پھر وہ خشک ہوجاتی ہے، پھر تو اس کو زرد دیکھتا ہے، پھر وہ ریزہ ریزہ ہوجاتی ہے اور آخرت میں سخت عذاب ہے اور اللہ کی طرف سے معافی اور رضا مندی بھی اور دنیا کی زندگی دھوکے کی پونجی کے سوا اور کچھ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(57,21,'57:21','دوڑواپنے رب کی معافی کی طرف اور ایسی جنت کی طرف جس کی وسعت آسمان اور زمین کی وسعت کے برابر ہے وہ ان لوگوں کے لیے تیار کی گئی ہے جو اللہ اور اس کے رسول پر ایمان لائیں ، یہ اللہ کا فضل ہےوہ اس کو دیتا ہے جسے وہ چاہتا ہے اور اللہ بڑا فضل والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,22,'57:22','کوئی مصیبت نہ زمین میں آتی ہے اورنہ تمھاری جانوں میں مگر وہ ایک کتاب میں لکھی ہوئی ہے اس سے پہلے کہ ہم اس کو پیدا کریں ، بے شک یہ اللہ کے لیے آسان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,23,'57:23','تاکہ تم غم نہ کرو اس پر جو تم سے کھویا گیااور نہ اس چیز پر فخر کرو جو اس نے تم کو دیا، اور اللہ اترانے والے فخر کرنے والے کو پسند نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(57,24,'57:24','جو کہ بخل کرتے ہیں اوردوسروں کو بھی بخل کی تعلیم دیتے ہیں اور جو شخص اعراض کرے گا تو اللہ بے نیاز ہے، خوبیوں والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,25,'57:25','ہم نے اپنے رسولوں کونشانیوں کے ساتھ بھیجا اور ان کے ساتھ اُتارا کتاب اور ترازو، تاکہ لوگ انصاف پر قائم ہوں اور ہم نے لوہا اُتارا جس میں بڑی قوت ہے اور لوگوں کے لیے فائدے ہیں اورتاکہ اللہ جان لے کہ کون اس کی اوراس کے رسولوں کی مدد کرتا ہے بِن دیکھے، بے شک اللہ طاقت والا، زبردست ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,26,'57:26','اور ہم نے نوح کو اور ابراہیم کو بھیجااوران کی اولاد میں ہم نے پیغمبری اور کتاب رکھ دیپھر ان میں سے کوئی راہ پر ہے اور ان میں سے بہت سے نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(57,27,'57:27','پھر انھیں کے نقش قدم پر ہم نے اپنے رسول بھیجے اور انھیں کے نقش قدم پر عیسیٰ بن مریم کو بھیجا اور ہم نے اس کو انجیل دی اور جن لوگوں نے اس کی پیروی کی ہم نے ان کے دلوں میں شفقت اوررحمت رکھ دیاور رہبانیت کو انھوں نے خود ایجاد کیا ہے، ہم نے اس کو ان پر نہیں لکھا تھامگر انھوں نے اللہ کی رضا مندی کے لیے اس کو اختیار کرلیا، پھر انھوں نے اس کی پوری رعایت نہ کی، پس ان میں سے جولوگ ایمان لائے ان کو ہم نے ان کااجردیااور ان میں سے اکثر لوگ نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(57,28,'57:28','اے ایمان والو، اللہ سے ڈرو اور اس کے رسول پر ایمان لاؤ اللہ تم کو اپنی رحمت سے دو حصے عطا کرے گااور تم کو روشنی عطا کرے گا جس کو لے کر تم چلوگےاور تم کو بخش دے گا اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(57,29,'57:29','تاکہ اہل کتاب جان لیں کہ وہ اللہ کے فضل میں سے کسی چیز پراختیار نہیں رکھتے اور یہ کہ فضل اللہ کے ہاتھ میں ہے وہ جسے چاہتا ہے عطا فرماتا ہے اور اللہ بڑے فضل والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,1,'58:1','اللہ نے سن لی اس عورت کی بات جواپنے شوہر کے معاملہ میں تم سے جھگڑتی تھی اور اللہ سے شکوہ کررہی تھی، اور اللہ تم دونوں کی گفتگو سن رہا تھا، بے شک اللہ سننے والا ،جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,2,'58:2','تم میں سے جو لوگ اپنی بیویوں سے ظہار کرتے ہیں وہ ان کی مائیں نہیں ہیں ان کی مائیں تو وہی ہیں جنھوں نے ان کو جنااور یہ لوگ بے شک ایک نامعقول اور جھوٹ بات کہتے ہیں ، اور اللہ معاف کرنے والا، بخشنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,3,'58:3','اور جو لوگ اپنی بیویوں سے ظہار کریں پھر اس سے رجوع کریں جو انھوں نے کہا تھا تو ایک گردن کو آزاد کرنا ہے،اس سے پہلے کہ وہ آپس میں ہاتھ لگائیں اس سے تم کو نصیحت کی جاتی ہے، اور اللہ جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(58,4,'58:4','پھر جو شخص نہ پائے تو روزے ہیں دو مہینے کے لگاتار، اس سے پہلے کہ وہ آپس میں ہاتھ لگائیں پھر جو شخص نہ کرسکے تو ساٹھ مسکینوں کو کھانا کھلانا ہےیہ اس لیے کہ تم اللہ اور اس کے رسول پر ایمان لاؤاوریہ اللہ کی حدیں ہیں اور منکروں کے لیے دردناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,5,'58:5','جو لوگ اللہ اوراس كے رسول کی مخالفت کرتے ہیں ، وہ ذلیل ہوں گے جس طرح وہ لوگ ذلیل ہوئے جو ان سے پہلے تھے اور ہم نے واضح آیتیں اُتاردی ہیں ، اور منکروں کے لیے ذلت کا عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,6,'58:6','جس دن اللہ ان سب کو اُٹھائے گا اور ان کے کيے ہوئے کام ان کو بتائے گااللہ نے اس کو گن رکھا ہےاور وہ لوگ اس کو بھول گئے، اور اللہ کے سامنے ہے ہر چیز');
INSERT INTO "ur_wahid_uddin" VALUES(58,7,'58:7','تم نے نہیں دیکھا کہ اللہ جانتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہےکوئی سرگوشی تین آدمیوں کی نہیں ہوتی جس میں چوتھا اللہ نہ ہواورنہ پانچ کی سر گوشی ہوتی ہے جس میں چھٹا وہ نہ ہو اور نہ اس سے کم کی یازیادہ کی مگر وہ ان کے ساتھ ہوتا ہے جہاں بھی وہ ہوں ،پھر وہ ان کو ان کے کیے سے آگاہ کرے گا قیامت کے دنبے شک اللہ ہربات کاعلم رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,8,'58:8','کیا تم نے نہیں دیکھا جن کو سرگوشیوں سے روکاگیا تھا، پھر بھی وہ وہی کررہے ہیں جس سے وہ روکے گئے تھےاور وہ گناہ اور زیادتی اور رسول کی نافرمانی کی سرگوشیاں کرتے ہیں ، اور جب وہ تمھارے پاس آتے ہیں تو تم کو ایسے طریقہ سے سلام کرتے ہیں جس سے اللہ نے تم کو سلام نہیں کیا اوراپنے دلوں میں کہتے ہیں کہ ہماری ان باتوں پر اللہ ہم کوعذاب کیوں نہیں دیتا ان کے لیے جہنم ہی کافی ہے، وہ اس میں پڑیں گے، پس وہ براٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,9,'58:9','اے ایمان والو، جب تم سرگوشی کرو تو گناہ اور زیادتی اوررسول کی نافرمانی کی سرگوشی نہ کرو اور تم نیکی اور پرہیزگاری کی سرگوشی کرواور اللہ سے ڈرو جس کے پاس تم جمع کيے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(58,10,'58:10','یہ سرگوشی شیطان کی طرف سے ہے تاکہ وہ ایمان والوں کو رنج پہنچائے، اور وہ ان کو کچھ بھی رنج نہیں پہنچاسکتا مگر اللہ کے حکم سےاور ایمان والوں کو اللہ ہی پر بھروسہ رکھنا چاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(58,11,'58:11','اے ایمان والو، جب تم کو کہا جائے کہ مجلسوں میں کھل کر بیٹھو تو تم کھل کر بیٹھو، اللہ تم کو کشادگی دے گا اور جب کہا جائے کہ اُٹھ جاؤ تو تم اُٹھ جاؤتم میں سے جولوگ ایمان والے ہیں اور جن کو علم دیا گیا ہے، اللہ ان کے درجے بلند کرے گااورجوکچھ تم کرتے ہو، اللہ اس سے باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,12,'58:12','اے ایمان والو، جب تم رسول سے رازدارانہ بات کرو تو اپنی رازدارانہ بات سے پہلے کچھ صدقہ دویہ تمھارے لیے بہتر ہے اور زیادہ پاکیزہ ہےپھر اگر تم نہ پاؤ تو اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,13,'58:13','کیا تم ڈرگئے اس بات سے کہ تم اپنی رازدارانہ گفتگو سے پہلے صدقہ دوپس اگر تم ایسانہ کرو، اور اللہ نے تم کو معاف کردیا، تو تم نماز قائم کرو اور زکوٰۃ اداکرو اور اللہ اور اس کے رسول کی اطاعت کرواور جو کچھ تم کرتے ہو، اللہ اس سے باخبر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,14,'58:14','کیا تم نے ان لوگوں کو نہیں دیکھا جو ایسے لوگوں سے دوستی کرتے ہیں جن پر اللہ کاغضب ہوا وہ نہ تم میں سے ہیں اور نہ ان میں سے ہیں ، اور وہ جھوٹی بات پرقسم کھاتے ہیں حالاں کہ وہ جانتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(58,15,'58:15','اللہ نے ان لوگوں کے لیے سخت عذاب تیار کر رکھا ہے، بے شک وہ برے کام ہیں جو وہ کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(58,16,'58:16','انھوں نے اپنی قسموں کوڈھال بنا رکھا ہے، پھر وہ روکتے ہیں اللہ کی راہ سے، پس ان کے لیے ذلت کاعذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,17,'58:17','ان کے مال اور ان کی اولاد ان کو ذرا بھی اللہ سے نہ بچاسکیں گے یہ لوگ دوزخ والے ہیں وہ اس میں هميشه رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(58,18,'58:18','جس دن اللہ ان سب کو اُٹھائے گا تو وہ اس سے بھی اسی طرح قسم کھائیں گے جس طرح تم سے قسم کھاتے ہیں اور وہ سمجھتے ہیں کہ وہ کسی چیز پر ہیں ، سن لوکہ یہی لوگ جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(58,19,'58:19','شیطان نے ان پرقابو حاصل کرلیا ہے،پھر اس نے ان کو خدا کی یاد بھلادی ہے یہ لوگ شیطان کاگروہ ہیں سن لو کہ شیطان کا گروہ ضرور برباد ہونے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,20,'58:20','جولوگ اللہ اور اس کے رسول کی مخالفت کرتے ہیں ، وہی لوگ سب سے زیادہ ذلیل لوگ هيں');
INSERT INTO "ur_wahid_uddin" VALUES(58,21,'58:21','اللہ نے لکھ دیا ہے کہ میں اور میرے رسول ہی غالب رہیں گےبے شک اللہ قوت والا ،زبردست ہے');
INSERT INTO "ur_wahid_uddin" VALUES(58,22,'58:22','تم ایسی قوم نہیں پاسکتے جو اللہ پر اور آخرت کے دن پر ایمان رکھتی ہو اور وہ ایسے لوگوں سے دوستی رکھے جو اللہ اور اس کے رسول کے مخالف ہیں اگرچہ وہ ان کے باپ یاان کے بیٹے یاان کے بھائی یاان کے خاندان والے کیوں نہ ہوں یہی لوگ ہیں جن کے دلوں میں اللہ نے ایمان لکھ دیا ہے اوران کواپنے فیض سے قوت دی ہےاور وہ ان کوایسے باغوں میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی، ان میں وہ ہمیشہ رہیں گےاللہ ان سے راضی ہوا اوروہ اللہ سے راضی ہوئےیہی لوگ اللہ کاگروہ ہیں ، اور اللہ کاگروہ ہی فلاح پانے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,1,'59:1','اللہ کی پاکی بیان کرتی ہیں سب چیزیں جو آسمانوں اورزمین میں ہیں ،اور وہ زبردست ہے،حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,2,'59:2','وہی ہے جس نے اہل کتاب منکروں کو ان کے گھروں سے پہلی ہی بار اكٹھا کرکے نکال دیاتمھارا گمان نہ تھا کہ وہ نکلیں گے اور وہ خیال کرتے تھے کہ ان کے قلعے ان کو اللہ سے بچالیں گے،پھر اللہ ان پر وہاں سے پہنچا جہاں سے ان کوخیال بھی نہ تھا اور ان کے دلوں میں رعب ڈال دیا،وہ اپنے گھروں کوخود اپنے ہاتھوں سے اُجاڑ رہے تھے اور مسلمانوں کے ہاتھوں سے بھیپس اے آنکھ والو، عبرت حاصل کرو');
INSERT INTO "ur_wahid_uddin" VALUES(59,3,'59:3','اوراگر اللہ نے ان پرجلاوطنی نہ لکھ دی ہوتی تو وہ دنیا ہی میں ان کو عذاب دیتا، اورآخرت میں ان کے لیے آگ کاعذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,4,'59:4','یہ اس لیے کہ انھوں نے اللہ اور اس کے رسول کی مخالفت کیاور جو شخص اللہ کی مخالفت کرتا ہے تو اللہ سخت عذاب والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,5,'59:5','کھجوروں کے جودرخت تم نے کاٹ ڈالے یاان کو ان کی جڑوں پرکھڑا رہنے دیاتو یہ اللہ کے حکم سے،اور تاکہ وہ نافرمانوں کو رسوا کرے');
INSERT INTO "ur_wahid_uddin" VALUES(59,6,'59:6','اور اللہ نے ان سے جو کچھ اپنے رسول کی طرف لوٹا یا تو تم نے اس پر نہ گھوڑے دوڑائے اور نہ اونٹ اور لیکن اللہ اپنے رسولوں کو جس پر چاہتا ہے تسلط دے دیتا ہےاور اللہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,7,'59:7','جو کچھ اللہ اپنے رسول کو بستیوں والوں کی طرف سے لوٹائے تو وہ اللہ کے لیے ہے اور رسول کے لیے ہے اور رشتہ داروں اور یتیموں اورمسکینوں اورمسافروں کے لیے ہے، تاکہ وہ تمھارے مالداروں ہی کے درمیان گردش نہ کرتا رہےاور رسول تم کو جو کچھ دے اس کو تم لے لو اور وہ جس چیز سے تم کو روکے اس سے تم رُک جاؤ اور اللہ سے ڈرو،اللہ سخت سزاد ینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,8,'59:8','ان مفلس مہاجروں کے لیے جواپنے گھروں اور اپنے مالوں سے نکالے گئے ہیں وہ اللہ کافضل اوررضامندی چاہتے ہیں اور وہ اللہ اور اس کے رسول کی مددکرتے ہیں ، یہی لوگ سچے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(59,9,'59:9','اور جولوگ پہلے سے مدینہ میں قرار پکڑے ہوئے ہیں اورایمان استوار کيے ہوئے ہیں ، جو ان کے پاس ہجرت کرکے آتا ہے اس سے وہ محبت کرتے ہیں اوروہ اپنے دلوں میں اس سے تنگی نہیں پاتےجو مہاجرین کودیاجاتا ہے اوروہ ان کواپنے اوپر مقدم رکھتے ہیں اگرچہ ان کے اوپر فاقہ ہو اورجوشخص اپنے جی کے لالچ سے بچالیاگیا تو وہی لوگ فلاح پانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(59,10,'59:10','اور جو ان کے بعد آئے وہ کہتے ہیں کہ اے ہمارے رب، ہم کو بخش دے اورہمارے ان بھائیوں کو جو ہم سے پہلے ایمان لاچکے ہیں اور ہمارے دلوں میں ایمان والوں کے لیے کینہ نہ رکھ، اے ہمارے رب، تو بڑا شفیق اور بڑامہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,11,'59:11','کیا تم نے ان لوگوں کو نہیں دیکھا جونفاق میں مبتلا ہیں وہ اپنے بھائیوں سے کہتے ہیں جنھوں نے اہل کتاب میں سے کفر کیا ہے،اگر تم نکالے گئے توہم بھی تمھارے ساتھ نکل جائیں گے اور تمھارے معاملہ میں ہم کسی کی بات نہ مانیں گےاور اگر تم سے لڑائی ہوئی تو ہم تمھاری مدد کریں گے اور اللہ گواہی دیتا ہے کہ وہ جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(59,12,'59:12','اگر وہ نکالے گئے تو یہ ان کے ساتھ نہیں نکلیں گےاور اگر ان سے لڑائی ہوئی تو یہ ان کی مدد نہیں کریں گے اور اگر ان کی مدد کریں گے تو ضرور وہ پیٹھ پھیر کر بھاگیں گے، پھروہ کہیں مدد نہ پائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(59,13,'59:13','بے شک تم لوگوں کاڈر ان کے دلوں میں اللہ سے زیادہ ہے یہ اس لیے کہ وہ لوگ سمجھ نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(59,14,'59:14','یہ لوگ سب مل کر تم سے کبھی نہیں لڑیں گے،مگر حفاظت والی بستیوں میں یا دیواروں کی آڑ میں ان کی لڑائی آپس میں سخت ہے تم ان کو متحدخیال کرتے ہو اور ان کے دل جداجدا ہورہے ہیں یہ اس لیے کہ وہ لوگ عقل نہیں رکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(59,15,'59:15','یہ ان لوگوں کی مانند ہیں جوان سے کچھ ہی پہلے اپنے کيے کامزہ چکھ چکے ہیں ،اور ان کے لیے دردناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,16,'59:16','جیسے شیطان جو انسان سے کہتا ہے کہ منکر ہوجا، پھرجب وہ منکر ہوجاتا ہے تو وہ کہتا ہے کہ میں تم سے بَری ہوں میں اللہ سے ڈرتا ہوں جوسارے جہان کارب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,17,'59:17','پھرانجام دونوں کا یہ ہوا کہ دونوں دوزخ میں گئے جہاں وہ ہمیشہ رہیں گے، اور ظالموں کی سزا یہی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,18,'59:18','اے ایمان والو، اللہ سے ڈرو،اور ہرشخص دیکھے کہ اس نے کل کے لیے کیا بھیجا ہےاور اللہ سے ڈرو،بے شک اللہ باخبر ہے اس سے جو تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(59,19,'59:19','اور تم ان لوگوں کی طرح نہ بن جاؤ جواللہ کو بھول گئے تواللہ نے ان کو خود ان کی جانوں سے غافل کردیا، یہی لوگ نافرمان ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(59,20,'59:20','دوزخ والے اور جنت والے برابر نہیں ہوسکتے جنت والے ہی اصل میں کامیاب ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(59,21,'59:21','اگرہم اس قرآن کوپہاڑ پراتارتے تو تم دیکھتے کہ وہ خدا کے خوف سے دب جاتا اور پھٹ جاتا،اور یہ مثالیں ہم لوگوں کے لیے بیان کرتے ہیں تاکہ وہ سوچیں');
INSERT INTO "ur_wahid_uddin" VALUES(59,22,'59:22','وہی اللہ ہے جس کے سوا کوئی معبود نہیں ، پوشیدہ اور ظاہر کو جاننے والا، وہ بڑا مہربان ہے،نہایت رحم والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(59,23,'59:23','وہی اللہ ہے جس کے سوا کوئی معبود نہیں ، بادشاہ سب عیبوں سے پاک، سراسر سلامتی، امن دینے والا، نگہبان، غالب، زور آور، عظمت والا، اللہ اس شرک سے پاک ہے جو لوگ کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(59,24,'59:24','وہی اللہ ہے پیداکرنے والا، وجود میں لانے والا، صورت گری کرنے والا،اسی کے لیے ہیں سار ے اچھے نامہر وہ چیز جوآسمانوں اورزمین میں ہے اس کی تسبیح کررہی ہے، اور وہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(60,1,'60:1','اے ایمان والو، تم میرے دشمنوں اوراپنے دشمنوں کودوست نہ بناؤ،تم ان سے دوستی کااظہار کرتے ہو حالاں کہ انھوں نے اس حق کاانکار کیا جوتمھارے پاس آیا، وہ رسول کواور تم کو اس بنا پر جلاوطن کرتے ہیں کہ تم اپنے رب ، اللہ پر ایمان لائے— اگر تم میری راہ میں جہاد اور میری رضا مندی کی طلب کے لیے نکلے ہو تم چھپا کر انھیں دوستی کاپیغام بھيجتے ہواورمیں جانتا ہوں جو کچھ تم چھپاتے ہواور جو کچھ تم ظاہر کرتے ہواور جو شخص تم میں سے ایسا کرے گا وہ راہ راست سے بھٹک گیا');
INSERT INTO "ur_wahid_uddin" VALUES(60,2,'60:2','اگر وہ تم پر قابو پاجائیں تو وہ تمھارے دشمن بن جائیں گے اور وہ اپنے ہاتھ اور اپنی زبان سے تم کو آزار پہنچائیں گےاور چاہیں گے کہ تم بھی کسی طرح منکر ہوجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(60,3,'60:3','تمھارے رشتہ دار اور تمھاری اولاد قیامت کے دن تمھارے کام نہ آئیں گے، وہ تمھارے درمیان فیصلہ کرے گا،اور اللہ دیکھنے والا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(60,4,'60:4','تمھارے لیے ابراہیم اور اس کے ساتھیوں میں اچھا نمونہ ہے، جب کہ انھوں نے اپنی قوم سے کہا کہ ہم الگ ہیں تم سے اور ان چیزوں سے جن کی تم اللہ کے سوا عبادت کرتے ہو، ہم تمھارے منکر ہیں اور ہمارے اور تمھارے درمیان ہمیشہ کے لیے عداوت اور بیزاری ظاہر ہوگئی، یہاں تک کہ تم اللہ واحد پر ایمان لاؤمگر ابراہیم کا اپنے باپ سے یہ کہنا کہ میں آپ کے لیے معافی مانگوں گا، اور میں آپ کے لیے اللہ کے آگے کسی بات کا اختیار نہیں رکھتااے ہمارے رب، ہم نے تیرے اوپر بھروسہ کیااورہم تیری طرف رجوع ہوئے اور تیری ہی طرف لوٹنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(60,5,'60:5','اے ہمارے رب، ہم کو منکروں کے لیے فتنہ نہ بنا، اور اے ہمارے رب ہم کو بخش دے، بے شک تو زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(60,6,'60:6','بے شک تمھارے لیے ان کے اندر اچھا نمونہ ہے، اس شخص کے لیے جواللہ کا اور آخرت کے دن کاامید وار ہو اور جو شخص روگردانی کرے گا تو اللہ بے نیاز ہے، تعریفوں والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(60,7,'60:7','امید ہے کہ اللہ تمھارے اور ان لوگوں کے درمیان دوستی پیدا کردے جن سے تم نے دشمنی کی اور اللہ سب کچھ کرسکتا ہے، اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(60,8,'60:8','اللہ تم کو ان لوگوں سے نہیں روکتا جنھوں نے دین کے معاملہ میں تم سے جنگ نہیں کیاور تم کو تمھارے گھروں سے نہیں نکالا کہ تم ان سے بھلائی کرو اور تم ان کے ساتھ انصاف کروبے شک اللہ انصاف کرنے والوں کو پسند کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(60,9,'60:9','اللہ بس ان لوگوں سے تم کو منع کرتا ہے جو دین کے معاملہ میں تم سے لڑے اور تم کو تمھارے گھروں سے نکالا اور تمھارے نکالنے میں مدد کی کہ تم ان سے دوستی کرو، اور جو ان سے دوستی کرے تو وہی لوگ ظالم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(60,10,'60:10','اے ایمان والو، جب تمھارے پاس مسلمان عورتیں ہجرت کرکے آئیں تو تم ان کو جانچ لو اللہ ان کے ایمان کو خوب جانتا ہےپس اگر تم جان لوکہ وہ مومن ہیں تو ان کو منکروں کی طرف نہ لوٹاؤنہ وہ عورتیں ان کے لیے حلال ہیں اور نہ وہ ان عورتوں کے لیے حلال ہیں اور منکر شوہروں نے جو کچھ خرچ کیا وہ ان کو ادا کر دو اور تم پر کوئی گناہ نہیں اگر تم ان سے نکاح کرلو جب کہ تم ان کے مہران کوادا کردو اور تم منکر عورتوں کو اپنے نکاح میں نہ روکے رہواور جو کچھ تم نے خرچ کیا ہے اس کو مانگ لو اور جو کچھ منکروں نے خرچ کیا وہ بھی تم سے مانگ لیں یہ اللہ کا حکم ہے، وہ تمھارے درمیان فیصلہ کرتا ہے،اور اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(60,11,'60:11','اور اگر تمھاری بیویوں کے مہر میں سے کچھ منکروں کی طرف رہ جائے ، پھر تمھاری نوبت آئے تو جن کی بیویاں گئی ہیں ان کو ادا کردو جو کچھ انھوں نے خرچ کیا اور اللہ سے ڈرو جس پر تم ایمان لائے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(60,12,'60:12','اے نبی، جب تمھارے پاس مومن عورتیں اس بات پر بیعت کے لیے آئیں کہ وہ اللہ کے ساتھ کسی چیز کو شریک نہ کریں گیاور وہ چوری نہ کریں گیاور وہ بدکاری نہ کریں گیاور وہ اپنی اولاد کو قتل نہ کریں گےاور وہ اپنے ہاتھ اور پاؤں کے آگے کوئی بہتان گھڑکر نہ لائیں گیاور وہ کسی معروف میں تمھاری نافرمانی نہ کریں گی تو تم ان سے بیعت لے لو اور ان کے لیے اللہ سے بخشش کی دعا کرو، بے شک اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(60,13,'60:13','اے ایمان والو، تم ان لوگوں کو دوست نہ بناؤ جن کے اوپر اللہ کا غضب ہوا، وہ آخرت سے ناامید ہوگئے ہیں جس طرح قبروں میں پڑے ہوئے منکر ناامید ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(61,1,'61:1','اللہ کی تسبیح کرتی ہے ہر چیز جو آسمانوں اور زمین میں ہےاور وہ غالب ہے حکیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(61,2,'61:2','اے ایمان والو،تم ایسی بات کیوں کہتے ہو جو تم کرتے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(61,3,'61:3','اللہ کے نزدیک یہ بات بہت ناراضی کی ہے کہ تم ایسی بات کہو جو تم کرو نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(61,4,'61:4','اللہ تو ان لوگوں کو پسند کرتا ہے جو اس کے راستہ میں اس طرح مل کر لڑتے ہیں گویا وہ ایک سیسہ پلائی ہوئی دیوار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(61,5,'61:5','اور جب موسیٰ نے اپنی قوم سے کہا کہ اے میری قوم، تم لوگ کیوں مجھ کو ستاتے ہو، حالاں کہ تم کو معلوم ہے کہ میں تمھاری طرف اللہ کا بھيجاہوا رسول ہوں پس جب وہ پھرگئے تو اللہ نے ان کے دلوں کو پھیردیااور اللہ نافرمان لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(61,6,'61:6','اور جب عیسیٰ بن مریم نے کہا کہ اے بنی اسرائیل، میں تمھاری طرف اللہ کا بھیجا ہوا رسول ہوں ، تصدیق کرنے والا ہوں اس تورات کا جومجھ سے پہلے سے موجود ہے، اور خوش خبری دینے والا ہوں ایک رسول کی جو میرے بعد آئے گا، اس کا نام احمد ہوگاپھر جب وہ ان کے پاس کھلی نشانیاں لے کر آیا تو انھوں نے کہا، یہ تو کھلا ہوا جادو ہے');
INSERT INTO "ur_wahid_uddin" VALUES(61,7,'61:7','اور اس سے بڑھ کر ظالم کون ہوگا جو اللہ پر جھوٹ باندھے حالاں کہ وہ اسلام کی طرف بلایا جارہا ہو، اور اللہ ظالم لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(61,8,'61:8','وہ چاہتے ہیں کہ اللہ کی روشنی کو اپنے منہ سے بجھادیں ، حالاں کہ اللہ اپنی روشنی کو پورا کرکے رہے گا، خواہ منکروں کو یہ کتنا ہی ناگوار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(61,9,'61:9','وہی ہے جس نے بھیجا اپنے رسول کو ہدایت اور دین حق کے ساتھ، تاکہ وہ اس کو سب دینوں پر غالب کردے خواہ مشرکوں کو یہ کتنا ہی ناگوار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(61,10,'61:10','اے ایمان والو، کیا میں تم کو ایک ایسی تجارت بتاؤں جو تم کو ایک دردناک عذاب سے بچالے');
INSERT INTO "ur_wahid_uddin" VALUES(61,11,'61:11','تم اللہ اور اس کے رسول پر ایمان لاؤ اور اللہ کی راہ میں اپنے مال اور اپني جان سے جہاد کرو، یہ تمھارے لیے بہتر ہے اگر تم جانو');
INSERT INTO "ur_wahid_uddin" VALUES(61,12,'61:12','اللہ تمھارے گناہ معاف کردے گا اور تم کو ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی اور عمدہ مکانوں میں جو ہمیشہ رہنے کے باغوں میں ہوں گے، یہ ہے بڑی کامیابی');
INSERT INTO "ur_wahid_uddin" VALUES(61,13,'61:13','اور ایک اور چیز بھی جس کی تم تمنا رکھتے ہو، اللہ کی مدد اور فتح جلدی،ا ور مومنوں کو بشارت دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(61,14,'61:14','اے ایمان والو، تم اللہ کے مددگار بنو جیسا کہ عیسیٰ بن مریم نے حواریوں سے کہا، کون اللہ کے واسطے میرا مددگار ہوتا ہے حواریوں نے کہا ہم ہیں اللہ کے مددگار، پس بنی اسرائیل میں سے کچھ لوگ ایمان لائے اور کچھ لوگوں نے انکار کیاپھر ہم نے ایمان لانے والوں کی ان دشمنوں کے مقابلہ میں مدد کی،پس وہ غالب ہوگئے');
INSERT INTO "ur_wahid_uddin" VALUES(62,1,'62:1','اللہ کی تسبیح کررہی ہے وہ چیز جو آسمانوں میں ہے اور جوزمین میں ہے— جو بادشاہ ہے، پاک ہے، زبردست ہے،حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(62,2,'62:2','وہی ہے جس نے امیوں کے اندر ایک رسول انھیں میں سے اُٹھایا، وہ ان کو اس کی آیتیں پڑھ کرسناتا ہےاور ان کو پاک کرتا ہے اور ان کو کتاب اور حکمت کی تعلیم دیتا ہے،اور وہ اس سے پہلے کھلی ہوئی گمراہی میں تھے');
INSERT INTO "ur_wahid_uddin" VALUES(62,3,'62:3','اور دوسروں کے لیے بھی ان میں سے جو ابھی ان میں شامل نہیں ہوئےاور وہ زبردست ہے، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(62,4,'62:4','یہ اللہ کا فضل ہےوہ دیتا ہے جس کو چاہتا ہے، اوراللہ بڑے فضل والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(62,5,'62:5','جن لوگوں کو تورات کاحامل بنایاگیا پھر انھوں نے اس کو نہ اُٹھایا، ان کی مثال اس گدھے کی سی ہے جو کتابوں کا بوجھ اُٹھائے ہوئے ہو کیا ہی بری مثال ہے ان لوگوں کی جنھوں نے اللہ کی آیتوں کو جھٹلایا اور اللہ ظالم لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(62,6,'62:6','کہوکہ اے یہودیو،اگر تمھارا گمان ہے کہ تم دوسروں کے مقابل میں اللہ کے محبوب ہو تو تم موت کی تمنا کرو،اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(62,7,'62:7','اور وہ کبھی اس کی تمنا نہ کریں گے، ان کاموں کی وجہ سے جن کو ان کے ہاتھ آگے بھیج چکے ہیں اور اللہ ظالموں کوخوب جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(62,8,'62:8','کہو کہ جس موت سے تم بھاگتے ہو وہ تمھیں آکر رہے گی، پھر تم پوشیدہ اور ظاہر کے جاننے والے کے پاس لے جائے جاؤگے ، پھر وہ تم کو بتادے گا جو تم کرتے رہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(62,9,'62:9','اے ایمان والو، جب جمعہ کے دن کی نماز کے لیے پکارا جائے تو اللہ کی یا د کی طرف چل پڑو اور خرید و فروخت چھوڑدو، یہ تمھارے لیے بہتر ہے اگر تم جانو');
INSERT INTO "ur_wahid_uddin" VALUES(62,10,'62:10','پھر جب نماز پوری ہوجائے تو زمین میں پھیل جاؤ اور اللہ کا فضل تلاش کرو،اور اللہ کو کثرت سے یاد کرو، تاکہ تم فلاح پاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(62,11,'62:11','اور جب وہ کوئی تجارت یا کھیل تماشہ دیکھتے ہیں تو اس کی طرف دوڑ پڑتے ہیں اور تم کو کھڑا ہوا چھوڑدیتے ہیں ، کہو کہ جو اللہ کے پاس ہے وہ کھیل تماشے اور تجارت سے بہتر ہے، اور اللہ بہترین رزق دینے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(63,1,'63:1','جب منافق تمھارے پاس آتے ہیں تو وہ کہتے ہیں کہ ہم گواہی دیتے ہیں کہ آپ بے شک اللہ کے رسول ہیں ، اور اللہ جانتا ہے کہ بے شک تم اس کے رسول ہو، اور اللہ گواہی دیتا ہے کہ یہ منافقین جھوٹے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(63,2,'63:2','انھوں نے اپنی قسموں کو ڈھال بنارکھا ہے، پھر وہ روکتے ہیں اللہ کی راہ سے،بے شک نہایت براہے جو وہ کررہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(63,3,'63:3','یہ اس سبب سے ہے کہ وہ ایمان لائے پھر انھوں نے کفر کیا، پھر ان کے دلوں پر مہرکردی گئی، پس وہ نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(63,4,'63:4','اور جب تم انھیں دیکھو تو ان کے جسم تم کو اچھے لگتے ہیں ،اوراگر وہ بات کرتے ہیں تو تم ان کی بات سنتے ہو،گویا کہ وہ لکڑیاں ہیں ٹیک لگائی ہوئیوہ ہرزورکی آواز کو اپنے خلاف سمجھتے ہیں یہی لوگ دشمن ہیں ، پس ان سے بچو اللہ ان کو ہلاک کرے ،وہ کہاں پھرے جاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(63,5,'63:5','اور جب ان سے کہا جاتا ہے کہ آؤ،اللہ کارسول تمھارے لیے استغفار کرے تو وہ اپنا سر پھیرلیتے ہیں اورتم ان کو دیکھو گے کہ وہ تکبر کرتے ہوئے بے رخی کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(63,6,'63:6','ان کے لیے یکساں ہے، تم ان کے لیے مغفرت کی دعا کرویا مغفرت کی دعا نہ کرو اللہ ہرگز ان کو معاف نہ کرے گا اللہ نافرمان لوگوں کو ہدایت نہیں دیتا');
INSERT INTO "ur_wahid_uddin" VALUES(63,7,'63:7','یہی ہیں جوکہتے ہیں کہ جو لوگ رسول اللہ کے پاس ہیں ان پرخرچ مت کرو یہاں تک کہ وہ منتشر ہوجائیں اور آسمانوں اور زمین کے خزانے اللہ ہی کے ہیں لیکن منافقین نہیں سمجھتے');
INSERT INTO "ur_wahid_uddin" VALUES(63,8,'63:8','وہ کہتے ہیں کہ اگر ہم مدینہ لوٹے تو عزت والا وہاں سے ذلت والے کو نکال دے گاحالاں کہ عزت اللہ کے لیے اور اس کے رسول کے لیے اور مومنین کے لیے ہے،مگر منافقین نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(63,9,'63:9','اے ایمان والو، تمھارے مال اور تمھاری اولاد تم کو اللہ کی یاد سے غافل نہ کرنے پائیں ،اور جوایسا کرے گا تو وہی گھاٹے میں پڑنے والے لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(63,10,'63:10','اور ہم نے جو کچھ تم کودیا ہے اس میں سے خرچ کرو اس سے پہلے کہ تم میں سے کسی کی موت آجائے، پھر وہ کہے کہ اے میرے رب، تونے مجھے کچھ اور مہلت کیوں نہ دی کہ میں صدقہ کرتا اور نیک لوگوں میں شامل ہوجاتا');
INSERT INTO "ur_wahid_uddin" VALUES(63,11,'63:11','اور اللہ ہرگز کسی جان کو مہلت نہیں دیتا جب کہ اس کی میعاد آجائے،اور اللہ جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(64,1,'64:1','اللہ کی تسبیح کررہی ہے ہر چیز جو آسمانوں میں ہے اورہرچیز جو زمین میں ہے اسی کی بادشاہی ہے اور اسی کے لیے تعریف ہے اور وہ ہرچیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,2,'64:2','وہی ہے جس نے تم کو پیدا کیا،پھر تم میں سے کوئی منکرہے اور کوئی مومن،اور اللہ دیکھ رہا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(64,3,'64:3','اس نے آسمانوں اورزمین کوٹھیک طور پر پیدا کیا اور اس نے تمھاری صورت بنائی تو نہایت اچھی صورت بنائی،اور اسی کی طرف ہے لوٹنا');
INSERT INTO "ur_wahid_uddin" VALUES(64,4,'64:4','وہ جانتا ہے جو کچھ آسمانوں اور زمین میں ہےاور وہ جانتا ہے جو تم چھپاتے ہو اور جو کچھ تم ظاہر کرتے ہواور اللہ دلوں تک کی باتوں کا جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,5,'64:5','کیاتم کو ان لوگوں کی خبر نہیں پہنچی جنھوں نے اس سے پہلے انکار کیا، پھر انھوں نے اپنے کيے کا وبال چکھا اور ان کے لیے ایک دردناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,6,'64:6','یہ اس لیے کہ ان کے پاس ان کے رسول کھلی ہوئی دلیلوں کے ساتھ آئے،تو انھوں نے کہا کہ كيا انسان ہماری رہنمائی کریں گے پس انھوں نے انکار کیااور منہ پھیرلیا،اور اللہ ان سے بے پرواہ ہوگیا،اوراللہ بے نیاز ہے، تعریف والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,7,'64:7','انکار کرنے والوں نے دعویٰ کیا کہ وہ ہرگز دوبارہ اٹھائے نہ جائیں گےکہو کہ ہاں ، میرے رب کی قسم، تم ضرور اُٹھائے جاؤگے،پھر تم کو بتایاجائے گا جو کچھ تم نے کیا ہے،اور یہ اللہ کے لیے بہت آسان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,8,'64:8','پس اللہ پر ایمان لاؤ اور اس کے رسول پر اور اس نور پر جو ہم نے اُتارا ہےاور اللہ جانتا ہے جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(64,9,'64:9','جس دن وہ تم سب کو ایک جمع ہونے کے دن جمع کرے گا، یہی دن ہار جیت کادن ہوگااور جوشخص اللہ پر ایمان لایا ہوگا اوراس نے نیک عمل کیا ہوگا،اللہ اس کے گناہ اس سے دور کردے گا اور اس کوایسے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی، وہ ہمیشہ اس میں رہیں گےیہی ہے بہت بڑی کامیابی');
INSERT INTO "ur_wahid_uddin" VALUES(64,10,'64:10','اور جن لوگوں نے انکار کیا اورہماری آیتوں کو جھٹلایا وہی لوگ آگ والے ہیں ،اس میں وہ ہمیشہ رہیں گے ،اور وہ برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,11,'64:11','جو مصیبت بھی آتی ہے اللہ کے اذن سے آتی ہےاور جو شخص اللہ پر ایمان رکھتا ہے اللہ اس کے دل کو راہ دکھاتا ہے،اور اللہ ہر چیز کو جاننے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,12,'64:12','اور تم اللہ کی اطاعت کرو اور رسول کی اطاعت کرو پھر اگر تم اعراض کروگے تو ہمارے رسول پربس صاف صاف پہنچادینا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,13,'64:13','اللہ، اس کے سوا کوئی الٰہ نہیں ، اور ایمان لانے والوں کو اللہ ہی پر بھروسہ رکھنا چاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(64,14,'64:14','اے ایمان والو، تمھاری بعض بیویاں اور بعض اولاد تمھارے دشمن ہیں ،پس تم ان سے ہوشیار رہو، اور اگر تم معاف کردو اور دردگزر کرو اور بخش دو تو اللہ بخشنے والا،رحم کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,15,'64:15','تمھارے مال اور تمھاری اولاد آزمائش کی چیز ہیں ،اور اللہ کے پاس بہت بڑا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,16,'64:16','پس تم اللہ سے ڈرو جہاں تک ہوسکےاور سنو اور مانو اور خرچ کرو،یہ تمھارے لیے بہتر ہے،اور جو شخص دل کی تنگی سے محفوظ رہاتو ایسے ہی لوگ فلاح پانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(64,17,'64:17','اگر تم اللہ کو اچھا قرض دوگے تو وہ اس کو تمھارے لیے کئی گنا بڑھادے گا اور تم کو بخش دے گا،اور اللہ قدرداں ہے، بُردبار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(64,18,'64:18','غائب اور حاضر کو جاننے والا ہے،زبردست ہے،حکیم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(65,1,'65:1','اے پیغمبر، جب تم لوگ عورتوں کوطلاق دو تو ان کی عدت پرطلاق دو اور عدت کوگنتے رہو، اور اللہ سے ڈرو جو تمھارا رب ہےان عورتوں کو ان کے گھروں سے نہ نکالو اور نہ وہ خود نکلیں ،اِلاّ یہ کہ وہ کوئی کھلی ہوئی بے حیائی کریں ، اور یہ اللہ کی حدیں ہیں ،اور جو شخص اللہ کی حدوں سے تجاوز کرے گا تو اس نے اپنے اوپر ظلم کیا،تم نہیں جانتے شاید اللہ اس طلاق کے بعد کوئی نئی صورت پیدا کردے');
INSERT INTO "ur_wahid_uddin" VALUES(65,2,'65:2','پھر جب وہ اپنی مدت کو پہنچ جائیں تو ان کو یاتو معروف کے مطابق رکھ لو یا معروف کے مطابق ان کوچھوڑدو اور اپنے میں سے دومعتبر گواہ بنالو اور ٹھیک ٹھیک اللہ کے لیے گواہی دویہ اس شخص کو نصیحت کی جاتی ہے جو اللہ پراور آخرت کے دن پر ایمان رکھتا ہواور جو شخص اللہ سے ڈرے گا، اللہ اس کے لیے راہ نکالے گا');
INSERT INTO "ur_wahid_uddin" VALUES(65,3,'65:3','اور اس کو وہاں سے رزق دے گا جہاں اس کا گمان بھی نہ گیا ہو،اور جو شخص اللہ پر بھروسہ کرے گا تو اللہ اس کے لیے کافی ہے، بے شک اللہ اپنا کام پورا کرکے رہتا ہےاللہ نے ہرچیز کے لیے ایک اندازہ ٹھہرارکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(65,4,'65:4','اور تمھاری عورتوں میں سے جو حیض سے مایوس ہوچکی ہیں ، اگر تم کو شبہ ہو تو ان کی عدت تین مہینہ ہےاور اسی طرح ان کی بھی عدت جن کو حیض نہیں آیا،اور حاملہ عورتوں کی عدت اس حمل کا پیدا ہوجانا ہے،اور جو شخص اللہ سے ڈرے گا،اللہ اس کے لیے اس کے کام میں آسانی کردے گا');
INSERT INTO "ur_wahid_uddin" VALUES(65,5,'65:5','یہ اللہ کا حکم ہے جو اس نے تمھاری طرف اُتارا ہے اور جو شخص اللہ سے ڈرے گا، اللہ اس کے گناہ اس سے دور کردے گا اور اس کوبڑااجر دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(65,6,'65:6','تم ان عورتوں کو اپنی وسعت کے مطابق رہنے کامکان دو جہاں تم رہتے ہو اور ان کو تنگ کرنے کے لیے انھیں تکلیف نہ پہنچاؤ،اور اگر وہ حمل والیاں ہوں تو ان پرخرچ کرو یہاں تک کہ ان کا وضعِ حمل ہوجائےپھر اگر وہ تمھارے لیے دودھ پلائیں تو ان کی اجرت انھیں دواورتم آپس میں ایک دوسرے کو نیکی سکھاؤاور اگر تم آپس میں ضدکرو تو کوئی او ر عورت دودھ پلائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(65,7,'65:7','چاہیے کہ وسعت والا اپنی وسعت کے مطابق خرچ کرے اور جس کی آمدنی کم ہو اس کوچاہيے کہ اللہ نے جتنا اس کو دیا ہے وہ اس میں سے خرچ کرےاللہ کسی پر بوجھ نہیں ڈالتا مگر اتنا ہی جتنا اس کو دیا ہے، اللہ سختی کے بعد جلد ہی آسانی پیدا کردے گا');
INSERT INTO "ur_wahid_uddin" VALUES(65,8,'65:8','اور بہت سی بستیاں ہیں جنھوں نے اپنے رب کے حکم سے سرتابی کی اور اس کے رسولوں كي،پس ہم نے ان کا سخت حساب کیا اور ہم نے ان کو ہولناک سزادی');
INSERT INTO "ur_wahid_uddin" VALUES(65,9,'65:9','پس انھوں نے اپنے کيے کاوبال چکھا اور ان کا انجام کار خسارہ ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(65,10,'65:10','اللہ نے ان کے لیے ایک سخت عذاب تیار کررکھا ہےپس اللہ سے ڈرو، اے عقل والو جو کہ ایمان لائے ہواللہ نے تمھاری طرف ایک نصیحت اُتاری ہے');
INSERT INTO "ur_wahid_uddin" VALUES(65,11,'65:11','ایک رسول جو تم کو اللہ کی کھلی ہوئی آیتیں پڑھ کر سناتا ہے،تاکہ ان لوگوں کو تاریکیوں سے روشنی کی طرف نکالے جو ایمان لائے اور انھوں نے نیک عمل کیااور جو شخص اللہ پر ایمان لایا اور نیک عمل کیا اس کو وہ ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی وہ اس میں ہمیشہ رہیں گے ،اللہ نے اس کو بہت اچھی روزی دی');
INSERT INTO "ur_wahid_uddin" VALUES(65,12,'65:12','اللہ ہی ہے جس نے بنائے سات آسمان اور انھیں کی طرح زمین بھیان کے اندر اس کا حکم اترتا ہے،تاکہ تم جان لوکہ اللہ ہر چیز پر قادر ہےاور اللہ نے اپنے علم سے ہر چیز کا احاطہ کررکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(66,1,'66:1','اے نبی، تم کیوں اس چیز کو حرام کرتے ہو جو اللہ نے تمھارے لیے حلال کی ہے، اپنی بیویوں کی رضامندی چاہنے کے لیے ،اور اللہ بخشنے والا، مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(66,2,'66:2','اللہ نے تم لوگوں کے لیے تمھاری قسموں کا کھولنا مقرر کردیا ہے،اور اللہ تمھارا کارساز ہے، اور وہ جاننے والا،حکمت والا ہےبیویوں کے پیدا کردہ بعض اندرونی مسائل کی وجہ سے رسول اللہ صلی اللہ علیہ وسلم نے اپنے گھر میں یہ قسم کھالی کہ میں شہد نہیں کھاؤں گا مگر پیغمبر کا عمل اس کی امت کے لیے نمونہ بن جاتا ہے، اس لیے اللہ تعالیٰ نے حکم دیا کہ آپ شرعی طریقہ کے مطابق کفارہ ادا کرکے اپنی قسم کو توڑ دیں اور شہد نہ کھانے کے عہد سے اپنے آپ کو آزاد کرلیں تاکہ ایسا نہ ہو کہ آئندہ آپ کے امتی اس کو تقوی کا معیار سمجھ کر شہد کھانے سے پرہیز کرنے لگیں');
INSERT INTO "ur_wahid_uddin" VALUES(66,3,'66:3','اور جب نبی نے اپنی کسی بیوی سے ایک بات چھپاکر کہی،تو جب اس نے اس کو بتادیا اور اللہ نے نبی کو اس سے آگاہ کردیا تو نبی نے کچھ بات بتائی اور کچھ ٹال دی،پھر جب نبی نے اس کو یہ بات بتائی تو اس نے کہا کہ آپ کو کس نے اس کی خبر دینبی نے کہا کہ مجھ کوبتایا جاننے والے نے، باخبر نے');
INSERT INTO "ur_wahid_uddin" VALUES(66,4,'66:4','اگر تم دونوں اللہ کی طرف رجوع کرو تو تمھارے دل جھک پڑے ہیں ، اور اگر تم دونوں نبی کے مقابلہ میں کاروائیاں کروگی تو اس کا رفیق اللہ ہے اور جبریل اور صالح اہل ایمان اور ان کے علاوہ فرشتے اس کے مددگار ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(66,5,'66:5','اگر نبی تم سب کو طلاق دے دے تو اس کارب تمھارے بدلے میں تم سے بہتر بیویاں اس کو دیدے،مسلمہ، باایمان، فرماں بردار، توبہ کرنے والی، عبادت کرنے والی،روزہ دار،بیوہ اور کنواری');
INSERT INTO "ur_wahid_uddin" VALUES(66,6,'66:6','اے ایمان والو، اپنے آپ کو اور اپنے گھروالوں کو اس آگ سے بچاؤ جس کاایندھن آدمی اور پتھرہوں گے،اس پر تندخو اور زبردست فرشتے مقرر ہیں ،اللہ ان کو جو حکم دے اس میں وہ اس کی نافرمانی نہیں کرتے، اور وہ وہی کرتے ہیں جس کا ان کو حکم ملتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(66,7,'66:7','اے لوگو جنھوں نے انکار کیا،آج عذر نہ پیش کرو، تم وہی بدلہ میں پارہے ہو جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(66,8,'66:8','اے ایمان والو، اللہ کے آگے سچی توبہ کروامید ہے کہ تمھارا رب تمھارے گناہ معاف کردے اور تم کو ایسے باغوں میں داخل کرے جن کے نیچے نہریں بہتی ہوں گی، جس دن اللہ نبی کو اور اس کے ساتھ ایمان لانے والوں کو رسوا نہیں کرے گاان کی روشنی ان کے آگے اور ان کے دائیں طرف دوڑ رہی ہوگی، وہ کہہ رہے ہوں گے کہ اے ہمارے رب، ہمارے لیے ہماری روشنی کو کامل کردے اور ہماری مغفرت فرما، بے شک تو ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(66,9,'66:9','اے نبی، منکروں اور منافقوں سے جہاد کرو اور ان پرسختی کرو،اور ان کاٹھکانا جہنم ہے اور وہ برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(66,10,'66:10','اللہ منکروں کے لیے مثال بیان کرتا ہے نوح کی بیوی کی اور لوط کی بیوی کی، دونوں ہمارے بندوں میں سے دو نیک بندوں کے نکاح میں تھیں ، پھر انھوں نے ان کے ساتھ خیانت کی تو وہ دونوں اللہ کے مقابلہ میں ان کے کچھ کام نہ آسکے،اوردونوں کو کہہ دیاگیا کہ آگ میں داخل ہوجاؤ، داخل ہونے والوں کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(66,11,'66:11','اور اللہ ایمان والوں کے لیے مثال بیان کرتا ہے فرعون کی بیوی کی، جب کہ اس نے کہا کہ اے میرے رب، میرے لیے اپنے پاس جنت میں ایک گھر بنادے اور مجھ کو فرعون اور اس کے عمل سے بچالے اور مجھ کو ظالم قوم سے نجات دے');
INSERT INTO "ur_wahid_uddin" VALUES(66,12,'66:12','او رعمران کی بیٹی مریم، جس نے اپنی عصمت کی حفاظت کی، پھر ہم نے اس میں اپنی روح پھونک دی اور اس نے اپنے رب کے کلمات کی اور اس کی کتابوں کی تصدیق کی،اور وہ فرماں برداروں میں سے تھی');
INSERT INTO "ur_wahid_uddin" VALUES(67,1,'67:1','بڑا بابرکت ہے وہ جس کے ہاتھ میں بادشاہی ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,2,'67:2','جس نے موت اور زندگی کو پیدا کیا تاکہ وہ تم کو جانچے کہ تم میں سے کون اچھا کام کرتا ہےاور وہ زبردست ہے،بخشنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,3,'67:3','جس نے بنائے سات آسمان اوپر تلے، تم رحمن کے بنانے میں کوئی خلل نہیں دیکھوگے،پھر نگاہ ڈال کر دیکھ لو، کہیں تم کو کوئی خلل نظر آتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,4,'67:4','پھر بار بارنگاہ ڈال کر دیکھو، نگاہ ناکام تھک کر تمھاری طرف واپس آجائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(67,5,'67:5','اور ہم نے قریب کے آسمان کو چراغوں سے سجایا ہےاور ہم نے ان کوشیطانوں کے مارنے کا ذریعہ بنایا ہےاور ہم نے ان کے لیے دوزخ کاعذاب تیار کر رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,6,'67:6','اور جن لوگوں نے اپنے رب کا انکار کیا، ان کے لیے جہنم کا عذاب ہے اوروہ برا ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,7,'67:7','جب وہ اس میں ڈالے جائیں گے، وہ اس کا دھاڑنا سنیں گےاور وہ جوش مارتی ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(67,8,'67:8','معلوم ہوگا کہ وہ غصہ میں پھٹ پڑے گیجب اس میں کوئی گروہ ڈالا جائے گا، اس کے داروغہ اس سے پوچھیں گے، کیاتمھارے پاس کوئی ڈرانے والا نہیں آیا');
INSERT INTO "ur_wahid_uddin" VALUES(67,9,'67:9','وہ کہیں گے کہ ہاں ،ہمارے پاس ڈرانے والا آیاپھر ہم نے اس کو جھٹلادیا اور ہم نے کہا کہ اللہ نے کوئی چیز نہیں اُتاری،تم لوگ بڑی گمراہی میں پڑے ہوئے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(67,10,'67:10','اور وہ کہیں گے کہ اگر ہم سنتے یاسمجھتے تو ہم دوزخ والوں میں سے نہ ہوتے');
INSERT INTO "ur_wahid_uddin" VALUES(67,11,'67:11','پس وہ اپنے گناہ کا اقرار کریں گے، پس لعنت ہو دوزخ والوں پر');
INSERT INTO "ur_wahid_uddin" VALUES(67,12,'67:12','جولوگ اپنے رب سے بن دیکھے ڈرتے ہیں ، ان کے لیے مغفرت اور بڑا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,13,'67:13','اورتم اپنی بات چھپا کر کہو یا پکار کر کہو،وہ دلوں تک کی باتوں کو جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,14,'67:14','کیا وہ نہ جانے گا جس نے پیدا کیا ہے،اور وہ باریک بیں ہے، خبر رکھنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,15,'67:15','وہی ہے جس نے زمین کو تمھارے لیے پست کردیا تو تم اس کے راستوں میں چلو اور اس کے رزق میں سے کھاؤ اور اسی کی طرف ہے اُٹھنا');
INSERT INTO "ur_wahid_uddin" VALUES(67,16,'67:16','کیا تم اس سے بے خوف ہوگئے جو آسمان میں ہے کہ وہ تم کو زمین میں دھنسا دے،پھر وہ لرزنے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(67,17,'67:17','کیا تم اس سے جو آسمان میں ہے بے خوف ہوگئے کہ وہ تم پر پتھراؤ کرنے والی ہوا بھیج دے، پھر تم جان لو کہ کیسا ہے میرا ڈرانا');
INSERT INTO "ur_wahid_uddin" VALUES(67,18,'67:18','اور انھوں نے جھٹلایا جو ان سے پہلے تھے تو كيسا هوا ميرا انكار');
INSERT INTO "ur_wahid_uddin" VALUES(67,19,'67:19','کیا وہ پرندوں کو اپنے اوپر نہیں دیکھتے پر پھیلائے ہوئے اور وہ ان کو سمیٹ بھی لیتے ہیں رحمن کے سوا کوئی نہیں جو ان کو تھامے ہوئے ہوبے شک وہ ہرچیز کو دیکھ رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,20,'67:20','آخر کون ہے کہ وہ تمھارالشکر بن کر رحمن کے مقابلہ میں تمھاری مدد کرسکے انکار کرنے والے دھوکے میں پڑے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(67,21,'67:21','آخرکون ہے جو تم کو روزی دے اگر اللہ اپنی روزی روک لے، بلکہ وہ سرکشی پراور بدکنے پر اَڑ گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(67,22,'67:22','کیاجو شخص اوندھے منہ چل رہا ہے وہ زیادہ صحیح راہ پانے والا ہے یا وہ شخص جو سیدھا ایک صراطِ مستقیم پر چل رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,23,'67:23','کہو کہ وہی ہے جس نے تم کو پیداکیااور تمھارے لیے کان اور آنکھ اور دل بنائےتم لوگ بہت کم شکرادا کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(67,24,'67:24','کہو کہ وہی ہے جس نے تم کو زمین میں پھیلایا اور تم اسی کی طرف اکھٹا کيے جاؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(67,25,'67:25','اور وہ کہتے ہیں کہ یہ وعدہ کب ہوگا اگر تم سچے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(67,26,'67:26','کہو کہ یہ علم اللہ کے پاس ہے اور میں صرف کھلا ہوا ڈرانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(67,27,'67:27','پس جب وہ اس کو قریب آتا ہوادیکھیں گے تو ان کے چہرے بگڑ جائیں گے جنھوں نے انکار کیا، اور کہا جائے گا کہ یہی ہے وہ چیز جس کو تم مانگا کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(67,28,'67:28','کہو کہ اگر اللہ مجھ کو ہلاک کردے اوران لوگوں کو جو میرے ساتھ ہیں ، یا ہم پررحم فرمائے تو منکروں کو دردناک عذاب سے کون بچائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(67,29,'67:29','کہو، وہ رحمن ہے، ہم اس پر ایمان لائے اور اسی پر ہم نے بھروسہ کیا پس عنقریب تم جان لوگے کہ کھلی ہوئی گمراہی میں کون ہے');
INSERT INTO "ur_wahid_uddin" VALUES(67,30,'67:30','کہو کہ بتاؤ، اگر تمھارا پانی نیچے اتر جائے تو کون ہے جوتمھارے لیے صاف پانی لے آئے');
INSERT INTO "ur_wahid_uddin" VALUES(68,1,'68:1','نٓ، قسم ہے قلم کی اور جو کچھ لوگ لکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(68,2,'68:2','تم اپنے رب کے فضل سے دیوانے نہیں ہو');
INSERT INTO "ur_wahid_uddin" VALUES(68,3,'68:3','اور بے شک تمھارے لیے اجر ہے کبھی ختم نہ ہونے والا');
INSERT INTO "ur_wahid_uddin" VALUES(68,4,'68:4','اور بے شک تم ایک اعلیٰ اخلاق پر ہو');
INSERT INTO "ur_wahid_uddin" VALUES(68,5,'68:5','پس عنقریب تم دیکھو گے اور وہ بھی دیکھیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(68,6,'68:6','کہ تم میں سے کس کو جنون تھا');
INSERT INTO "ur_wahid_uddin" VALUES(68,7,'68:7','تمھارارب ہی خوب جانتا ہے ، جو اس کی راہ سے بھٹکاہوا ہے،اور وہ راہ پر چلنے والوں کو بھی خوب جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(68,8,'68:8','پس تم ان جھٹلانے والوں کا کہنا نہ مانو');
INSERT INTO "ur_wahid_uddin" VALUES(68,9,'68:9','وہ چاہتے ہیں کہ تم نرم پڑجاؤ تو وہ بھی نرم پڑجائیں');
INSERT INTO "ur_wahid_uddin" VALUES(68,10,'68:10','اور تم ایسے شخص کا کہنا نہ مانو جو بہت قسمیں کھانے والاہو');
INSERT INTO "ur_wahid_uddin" VALUES(68,11,'68:11','بےوقعت ہو، طعنہ دینے والا ہو،چغلی لگاتا پھرتا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(68,12,'68:12','نیک کام سے روکنے والا ہو،حد سے گزرجانے والاہو، حق مارنے والا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(68,13,'68:13','سنگ دل ہو، مزید برآں بے نسب ہو');
INSERT INTO "ur_wahid_uddin" VALUES(68,14,'68:14','اس سبب سے کہ وہ مال و اولاد والاہے');
INSERT INTO "ur_wahid_uddin" VALUES(68,15,'68:15','جب اس کو ہماری آیتیں پڑھ کر سنائی جاتی ہیں تو وہ کہتا ہے کہ یہ اگلوں کی بے سند باتیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(68,16,'68:16','عنقریب ہم اس کی ناک پر داغ لگائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(68,17,'68:17','ہم نے ان کو آزمائش میں ڈالا ہے، جس طرح ہم نے باغ والوں کو آزمائش میں ڈالا تھا جب کہ انھوں نے قسم کھائی کہ وہ صبح سویرے ضرور اس کا پھل توڑیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(68,18,'68:18','اور انھوں نے ان شاء اللہ نہیں کہا');
INSERT INTO "ur_wahid_uddin" VALUES(68,19,'68:19','پس اس باغ پر تیرے رب کی طرف سے ایک پھرنے والا پھر گیا اور وہ سورہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(68,20,'68:20','پھر صبح کو وہ ایسا رہ گیا جیسے کٹی ہوئی فصل');
INSERT INTO "ur_wahid_uddin" VALUES(68,21,'68:21','پس صبح کو انھوں نے ایک دوسرے کوپکارا');
INSERT INTO "ur_wahid_uddin" VALUES(68,22,'68:22','کہ اپنے کھیت پر سویرے چلو اگر تم کو پھل توڑنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(68,23,'68:23','پھر وہ چل پڑے اور وہ آپس میں چپکے چپکے کہہ رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(68,24,'68:24','کہ آج کوئی محتاج تمھارے پاس باغ میں نہ آنے پائے');
INSERT INTO "ur_wahid_uddin" VALUES(68,25,'68:25','اور وہ اپنے کو نہ دینے پر قادر سمجھ کر چلے');
INSERT INTO "ur_wahid_uddin" VALUES(68,26,'68:26','پھر جب باغ کو دیکھا تو کہا کہ ہم راستہ بھول گئے');
INSERT INTO "ur_wahid_uddin" VALUES(68,27,'68:27','بلکہ ہم محروم ہوگئے');
INSERT INTO "ur_wahid_uddin" VALUES(68,28,'68:28','ان میں جو بہتر آدمی تھا اس نے کہا،میں نے تم سے نہیں کہاتھا کہ تم لوگ تسبیح کیوں نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(68,29,'68:29','انھوں نے کہا کہ ہمارا رب پاک ہےبے شک ہم ظالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(68,30,'68:30','پھر وہ آپس میں ایک دوسرے کو الزام دینے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(68,31,'68:31','انھوں نے کہا، افسوس ہے ہم پر، بے شک ہم حد سے نکلنے والے لوگ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(68,32,'68:32','شاید ہمارا رب ہم کو اس سے اچھا باغ اس کے بدلے میں دے دے، ہم اسی کی طرف رجوع ہوتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(68,33,'68:33','اسی طرح آتا ہے عذاب ،اور آخرت کا عذاب اس سے بھی بڑا ہے،کاش یہ لوگ جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(68,34,'68:34','بے شک ڈرنے والوں کے لیے ان کے رب کے پاس نعمت کے باغ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(68,35,'68:35','کیا ہم فرماں برداروں کو نافرمانوں کے برابر کردیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(68,36,'68:36','تم کو کیا ہوا،تم کیسا فیصلہ کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(68,37,'68:37','کیا تمھارے پاس کوئی کتاب ہے جس میں تم پڑھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(68,38,'68:38','اس میں تمھارے لیے وہ ہے جس کو تم پسند کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(68,39,'68:39','کیا تمھارے لیے ہمارے اوپر قسمیں ہیں قیامت تک باقی رہنے والی کہ تمھارے لیے وہی کچھ ہے جو تم فیصلہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(68,40,'68:40','ان سے پوچھو کہ ان میں سے کون اس کا ذمہ دار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(68,41,'68:41','کیا ان کے کچھ شریک ہیں ،تو وہ اپنے شریکوں کو لائیں اگر وہ سچے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(68,42,'68:42','جس دن حقیقت سے پردہ اُٹھایا جائے گا اور لوگ سجدہ کے لیے بلائے جائیں گے تو وہ سجدہ نہ کرسکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(68,43,'68:43','ان کی نگاہیں جھکی ہوئی ہوں گی،ان پر ذلت چھائی ہوگی،اور وہ سجدہ کے لیے بلائے جاتے تھے اور صحیح سالم تھے');
INSERT INTO "ur_wahid_uddin" VALUES(68,44,'68:44','پس چھوڑو مجھ کو اور ان کو جواس کلام کو جھٹلاتے ہیں ،ہم ان کو آہستہ آہستہ لارہے ہیں جہاں سے وہ نہیں جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(68,45,'68:45','اور میں ان کو مہلت دے رہا ہوں ، بے شک میری تدبیر مضبوط ہے');
INSERT INTO "ur_wahid_uddin" VALUES(68,46,'68:46','کیا تم ان سے معاوضہ مانگتے ہو کہ وہ اس کے تاوان سے دبے جارہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(68,47,'68:47','یا ان کے پاس غیب ہے پس وہ لکھ رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(68,48,'68:48','پس اپنے رب کے فیصلہ تک صبر کرو اور مچھلی والے کی طرح نہ بن جاؤ،جب اس نے پکارا اور وہ غم سے بھرا ہوا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(68,49,'68:49','اگر اس کے رب کی مہربانی اس کے شاملِ حال نہ ہوتی تو وہ مذموم ہوکر چٹیل میدان میں پھینک دیا جاتا');
INSERT INTO "ur_wahid_uddin" VALUES(68,50,'68:50','پھر اس کے رب نے اس کو نوازا،پس اس کونیکوں میں شامل کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(68,51,'68:51','اور یہ منکر لوگ جب نصیحت کو سنتے ہیں تو اس طرح تم کو دیکھتے ہیں گویا اپنی نگاہوں سے تم کو پھسلادیں گے،اور کہتے ہیں کہ یہ ضرور دیوانہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(68,52,'68:52','اوروہ جہان والوں کے لیے صرف ایک نصیحت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(69,1,'69:1','وہ ہونے والی');
INSERT INTO "ur_wahid_uddin" VALUES(69,2,'69:2','کیا ہے وہ ہونے والی');
INSERT INTO "ur_wahid_uddin" VALUES(69,3,'69:3','اور تم کیا جانو کہ کیا ہے وہ ہونے والی');
INSERT INTO "ur_wahid_uddin" VALUES(69,4,'69:4','ثمود اور عاد نے اس کھڑا کھڑانے والی چیز کو جھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(69,5,'69:5','پس ثمود، تو وہ ایک سخت حادثہ سے ہلاک کردئے گئے');
INSERT INTO "ur_wahid_uddin" VALUES(69,6,'69:6','اورعاد، تو وہ ایک تیز وتند ہوا سے ہلاک کيے گئے');
INSERT INTO "ur_wahid_uddin" VALUES(69,7,'69:7','اس کو اللہ نے مسلسل سات رات اور آٹھ دن ان پر مسلط رکھا، پس تم دیکھتے ہو کہ وہاں وہ اس طرح گرے ہوئے پڑے ہیں گویا کہ وہ کھجوروں کے کھوکھلے تنے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(69,8,'69:8','تو کیا تم کو ان میں سے کوئی بچا ہوا نظر آتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(69,9,'69:9','اور فرعون اور اس سے پہلے والوں نے اور الٹی ہوئی بستیوں نے جرم کیا');
INSERT INTO "ur_wahid_uddin" VALUES(69,10,'69:10','انھوں نے اپنے رب کے رسول کی نافرمانی کی تو اللہ نے ان کو بہت سخت پکڑا');
INSERT INTO "ur_wahid_uddin" VALUES(69,11,'69:11','اور جب پانی حد سے گزرگیا تو ہم نے تم کو کشتی میں سوار کرایا');
INSERT INTO "ur_wahid_uddin" VALUES(69,12,'69:12','تاکہ ہم اس کو تمھارے لیے یادگار بنادیں اوریادرکھنے والے کان اس کو یاد رکھیں');
INSERT INTO "ur_wahid_uddin" VALUES(69,13,'69:13','پس جب صور میں یکبارگی پھونک ماری جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(69,14,'69:14','اورزمین اور پہاڑوں کو اُٹھا کر ایک ہی بار میں ریزہ ریزہ کردیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(69,15,'69:15','تو اس دن واقع ہونے والی واقع ہوجائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(69,16,'69:16','اورآسمان پھٹ جائے گاتو وہ اس روز بالکل بوداہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(69,17,'69:17','اور فرشتے اس کے کناروں پر ہوں گے، اور تیرے رب کے عرش کو اس دن آٹھ فرشتے اپنے اوپر اٹھائے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(69,18,'69:18','اس دن تم پیش کيے جاؤگے تمھاری کوئی بات پوشیدہ نہ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(69,19,'69:19','پس جس شخص کو اس کااعمال نامہ اس کے دائیں ہاتھ میں دیاجائے گا تو وہ کہے گا کہ لو،میرا اعمال نامہ پڑھو');
INSERT INTO "ur_wahid_uddin" VALUES(69,20,'69:20','میں نے گمان رکھا تھا کہ مجھ کو میرا حساب پیش آنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(69,21,'69:21','پس وہ ایک پسندیدہ عیش میں ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(69,22,'69:22','اونچے باغ میں');
INSERT INTO "ur_wahid_uddin" VALUES(69,23,'69:23','اس کے پھل جھکے پڑ رہے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(69,24,'69:24','کھاؤ اور پیو مزے کے ساتھ، ان اعمال کے بدلے میں جوتم نے گزرے ہوئے دنوں میں کيے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(69,25,'69:25','اور جس شخص کا اعمال نامہ اس کے بائیں ہاتھ میں دیا جائے گا،تو وہ کہے گا— کاش میرااعمال نامہ مجھے نہ دیا جاتا');
INSERT INTO "ur_wahid_uddin" VALUES(69,26,'69:26','اورمیں نہ جانتا کہ میراحساب کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(69,27,'69:27','کاش وہی موت فیصلہ کن ہوتی');
INSERT INTO "ur_wahid_uddin" VALUES(69,28,'69:28','میرا مال میرے کام نہ آیا');
INSERT INTO "ur_wahid_uddin" VALUES(69,29,'69:29','میرا اقتدار ختم ہوگیا');
INSERT INTO "ur_wahid_uddin" VALUES(69,30,'69:30','اس شخص کو پکڑو، پھر اس کو طوق پہناؤ');
INSERT INTO "ur_wahid_uddin" VALUES(69,31,'69:31','پھر اس کو جہنم میں داخل کردو');
INSERT INTO "ur_wahid_uddin" VALUES(69,32,'69:32','پھر ایک زنجیر میں جس کی پیمائش سترہاتھ ہے اس کو جکڑدو');
INSERT INTO "ur_wahid_uddin" VALUES(69,33,'69:33','یہ شخص خدائے عظیم پر ایمان نہ رکھتا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(69,34,'69:34','اور وہ غریبوں کو کھانا کھلانے پر نہیں اُبھارتا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(69,35,'69:35','پس آج یہاں اس کا کوئی ہمدرد نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(69,36,'69:36','اور زخموں کے دھوون کے سوا اس کے لیے کوئی کھانا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(69,37,'69:37','اس کو گناہ گاروں کے سوا کوئی نہ کھائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(69,38,'69:38','پس نہیں ، میں قسم کھاتا ہوں ان چیزوں کی جن کو تم دیکھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(69,39,'69:39','اور جن کو تم نہیں دیکھتے');
INSERT INTO "ur_wahid_uddin" VALUES(69,40,'69:40','بے شک یہ ایک باعزت رسول کا کلام ہے');
INSERT INTO "ur_wahid_uddin" VALUES(69,41,'69:41','اور وہ کسی شاعر کا کلام نہیں ،تم بہت کم ایمان لاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(69,42,'69:42','اور یہ کسی کا ہن کا کلام نہیں ، تم بہت کم غور کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(69,43,'69:43','خدا وند عالم کی طرف سے اُتارا ہوا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(69,44,'69:44','اور اگر وہ کوئی بات گھڑ کر ہمارے اوپر لگاتا');
INSERT INTO "ur_wahid_uddin" VALUES(69,45,'69:45','تو ہم اس کا دایاں ہاتھ پکڑتے');
INSERT INTO "ur_wahid_uddin" VALUES(69,46,'69:46','پھر ہم اس کی رگِ دل کاٹ دیتے');
INSERT INTO "ur_wahid_uddin" VALUES(69,47,'69:47','پھر تم میں سے کوئی اس سے ہم کو روکنے والا نہ ہوتا');
INSERT INTO "ur_wahid_uddin" VALUES(69,48,'69:48','اور بلاشبہ یہ یاد دہانی ہے ڈرنے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(69,49,'69:49','اور ہم جانتے ہیں کہ تم میں اس کے جھٹلانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(69,50,'69:50','اور وہ منکروں کے لیے پچھتاوا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(69,51,'69:51','اور یہ یقینی حق ہے');
INSERT INTO "ur_wahid_uddin" VALUES(69,52,'69:52','پس تم اپنے ربِ عظیم کے نام کی تسبیح کرو');
INSERT INTO "ur_wahid_uddin" VALUES(70,1,'70:1','مانگنے والے نے عذاب مانگا واقع ہونے والا');
INSERT INTO "ur_wahid_uddin" VALUES(70,2,'70:2','منکروں کے لیے کوئی اس کو ہٹانے والا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,3,'70:3','اللہ کی طرف سے جو سیڑھیوں کا مالک ہے');
INSERT INTO "ur_wahid_uddin" VALUES(70,4,'70:4','اس کی طرف فرشتے اور جبریل چڑھ کر جاتے ہیں ، ایک ایسے دن میں جس کی مقدار پچاس ہزار سال ہے');
INSERT INTO "ur_wahid_uddin" VALUES(70,5,'70:5','پس تم صبر کرو،بھلی طرح کا صبر');
INSERT INTO "ur_wahid_uddin" VALUES(70,6,'70:6','وہ اس کو دور دیکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,7,'70:7','اور ہم اس کو قریب دیکھ رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,8,'70:8','جس دن آسمان تیل کی تلچھٹ کی طرح ہوجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(70,9,'70:9','اور پہاڑ دھنے ہوئے اون کی طرح');
INSERT INTO "ur_wahid_uddin" VALUES(70,10,'70:10','اور کوئی دوست کسی دوست کو نہ پوچھے گا');
INSERT INTO "ur_wahid_uddin" VALUES(70,11,'70:11','وہ ان کو دکھائے جائیں گےمجرم چاہے گا کہ کاش، اس دن کے عذاب سے بچنے کے لیے اپنے بیٹوں');
INSERT INTO "ur_wahid_uddin" VALUES(70,12,'70:12','اور اپنی بیوی اور اپنے بھائی');
INSERT INTO "ur_wahid_uddin" VALUES(70,13,'70:13','اور اپنے کنبہ کو جو اسے پناہ دینے والا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(70,14,'70:14','اور تمام اہل زمین کو فدیہ میں دے کر اپنے کو بچالے');
INSERT INTO "ur_wahid_uddin" VALUES(70,15,'70:15','ہر گز نہیں ،وہ تو بھڑکتی ہوئی آگ کی لپٹ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(70,16,'70:16','جو کھال اُتاردے گی');
INSERT INTO "ur_wahid_uddin" VALUES(70,17,'70:17','وہ ہر اس شخص کوبلائے گی جس نے پیٹھ پھیری اور اعراض کیا');
INSERT INTO "ur_wahid_uddin" VALUES(70,18,'70:18','جمع کیا اور سینت کررکھا');
INSERT INTO "ur_wahid_uddin" VALUES(70,19,'70:19','بے شک انسان کم ہمت پیدا ہوا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(70,20,'70:20','جب اس کو تکلیف پہنچتی ہے تو وہ گھبرا اُٹھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(70,21,'70:21','اور جب اس کو فارغ البالی ہوتی ہے تو وہ بخل کرنے لگتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(70,22,'70:22','مگر وہ نمازی');
INSERT INTO "ur_wahid_uddin" VALUES(70,23,'70:23','جو اپنی نماز کی پابندی کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,24,'70:24','اور جن کے مالوں میں متعین حق ہے');
INSERT INTO "ur_wahid_uddin" VALUES(70,25,'70:25','سائل اور محروم کا');
INSERT INTO "ur_wahid_uddin" VALUES(70,26,'70:26','اور جو انصاف کے دن پر یقین رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,27,'70:27','اور جو اپنے رب کے عذاب سے ڈرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,28,'70:28','بے شک ان کے رب کے عذاب سے کسی کو نڈر نہ ہونا چاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(70,29,'70:29','اور جو اپنی شرم گاہوں کی حفاظت کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,30,'70:30','مگر اپنی بیویوں سے یا اپنی مملوکہ عورتوں سے،پس ان پر ان کوکوئی ملامت نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,31,'70:31','پھر جو شخص اس کے علاوہ کچھ اور چاہے تو وہی لوگ حدسے تجاوز کرنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,32,'70:32','اور جو اپنی امانتوں اور اپنے عہد کو نباہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,33,'70:33','اور جو اپنی گواہیوں پر قائم رہتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,34,'70:34','اور جو اپنی نماز کی حفاظت کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,35,'70:35','یہی لوگ جنتوں میں عزت کے ساتھ ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(70,36,'70:36','پھر ان منکروں کو کیا ہوگیا ہے کہ وہ تمھاری طرف دوڑے چلے آرہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,37,'70:37','دائیں سے اور بائیں سے گروہ درگروہ');
INSERT INTO "ur_wahid_uddin" VALUES(70,38,'70:38','کیاان میں سے ہر شخص یہ لالچ رکھتا ہے کہ وہ نعمت کے باغ میں داخل کرلیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(70,39,'70:39','ہر گز نہیں ، ہم نے ان کو پیدا کیا ہے اس چیز سے جس کو وہ جانتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,40,'70:40','پس نہیں ، میں قسم کھاتا ہوں مشرقوں اور مغربوں کے رب کی، ہم اس پر قادر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,41,'70:41','کہ بدل کر ان سے بہتر لے آئیں ، اور ہم عاجز نہیں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(70,42,'70:42','پس ان کو چھوڑ دو کہ وہ باتیں بنائیں اور کھیل کریں ،یہاں تک کہ وہ اپنے اس دن سے دوچار ہوں جس کا ان سے وعدہ کیا جارہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(70,43,'70:43','جس دن قبروں سے نکل پڑیں گے دوڑتے ہوئے ،جیسے وہ کسی نشانہ کی طرف بھاگ رہے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(70,44,'70:44','ان کی نگاہیں جھکی ہوں گیان پر ذلت چھائی ہوگی، یہ ہے وہ دن جس کا ان سے وعدہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(71,1,'71:1','ہم نے نوح کو اس کی قوم کی طرف رسول بناکر بھیجا کہ اپنی قوم کے لوگوں کو خبردار کردو، اس سے پہلے کہ ان پر ایک دردناک عذاب آجائے');
INSERT INTO "ur_wahid_uddin" VALUES(71,2,'71:2','اس نے کہا کہ اے میری قوم کے لوگو، میں تمھارے لیے ایک کھلا ہوا ڈرانے والا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(71,3,'71:3','کہ تم اللہ کی عبادت کرو اور اس سے ڈرو اور میری اطاعت کرو');
INSERT INTO "ur_wahid_uddin" VALUES(71,4,'71:4','اللہ تمھارے گناہوں سے درگزر کرے گا اور تم کو ایک متعین وقت تک باقی رکھے گابے شک جب اللہ کا مقرر کیا ہوا وقت آجاتا ہے تو پھر وہ ٹالا نہیں جاتاکاش ، تم اس کو جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(71,5,'71:5','نوح نے کہا کہ اے میرے رب، میں نے اپنی قوم کو شب و روز پکارا');
INSERT INTO "ur_wahid_uddin" VALUES(71,6,'71:6','مگر میری پکار نے ان کی دوری ہی میں اضافہ کیا');
INSERT INTO "ur_wahid_uddin" VALUES(71,7,'71:7','اور میں نے جب بھی ان کو بلایا کہ تو انھیں معاف کردے تو انھوں نے اپنے کانوں میں انگلیاں ڈال لیں اور اپنے اوپر اپنے کپڑے لپیٹ لیے اور ضد پر اَڑ گئے اور بڑا گھمنڈکیا');
INSERT INTO "ur_wahid_uddin" VALUES(71,8,'71:8','پھر میں نے ان کو بر ملا پکارا');
INSERT INTO "ur_wahid_uddin" VALUES(71,9,'71:9','پھر میں نے ان کو کھلی تبلیغ کی اور ان کو چپکے سے سمجھایا');
INSERT INTO "ur_wahid_uddin" VALUES(71,10,'71:10','میں نے کہا کہ اپنے رب سے معافی مانگو، بے شک وہ بڑا معاف کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(71,11,'71:11','وہ تم پر آسمان سے خوب بارش برسائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(71,12,'71:12','اور تمھارے مال اور اولاد میں ترقی دے گااور تمھارے لیے باغ پیدا کرے گااور تمھارے لیے نہریں جاری کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(71,13,'71:13','تم کو کیا ہوگیا ہے کہ تم اللہ کے لیے عظمت کے متوقع نہیں ہو');
INSERT INTO "ur_wahid_uddin" VALUES(71,14,'71:14','حالاں کہ اس نے تم کو طرح طرح سے بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(71,15,'71:15','کیا تم نے دیکھا نہیں کہ اللہ نے کس طرح سات آسمان تہ بہ تہ بنائے');
INSERT INTO "ur_wahid_uddin" VALUES(71,16,'71:16','اور ان میں چاند کو نور اورسورج کو چراغ بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(71,17,'71:17','اور اللہ نے تم کو زمین سے خاص اہتمام سے اگایا');
INSERT INTO "ur_wahid_uddin" VALUES(71,18,'71:18','پھروہ تم کو زمین میں واپس لے جائے گااور پھر اس سے تم کو باہر لے آئے گا');
INSERT INTO "ur_wahid_uddin" VALUES(71,19,'71:19','اوراللہ نے تمھارے لیے زمین کو ہموار بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(71,20,'71:20','تاکہ تم اس کے کھلے راستوں میں چلو');
INSERT INTO "ur_wahid_uddin" VALUES(71,21,'71:21','نوح نے کہا کہ اے میرے رب،انھوں نے میرا کہا نہ مانا اور ایسے آدمیوں کی پیروی کی جن کے مال اور اولاد نے ان کے گھاٹے ہی میں اضافہ کیا');
INSERT INTO "ur_wahid_uddin" VALUES(71,22,'71:22','اور انھوں نے بڑی تدبیریں کیں');
INSERT INTO "ur_wahid_uddin" VALUES(71,23,'71:23','اور انھوں نے کہا کہ تم اپنے معبودوں کو ہرگز نہ چھوڑنااور تم ہر گز نہ چھوڑنا وَدکو اور سواع کو اور یَغوث کو اوريعوق كو اور نَسرکو');
INSERT INTO "ur_wahid_uddin" VALUES(71,24,'71:24','اور انھوں نے بہت لوگوں کو بہکادیا اور اب تو ان گمراہوں کی گمراہی میں ہی اضافہ کر');
INSERT INTO "ur_wahid_uddin" VALUES(71,25,'71:25','اپنے گناہوں کے سبب سے وہ غرق کيے گئے، پھر وہ آگ میں داخل کردئے گئےپس انھوں نے اپنے لیے اللہ سے بچانے والا کوئی مددگار نہ پایا');
INSERT INTO "ur_wahid_uddin" VALUES(71,26,'71:26','اور نوح نے کہا کہ اے میرے رب، تو ان منکروں میں سے کوئی زمین پربسنے والا نہ چھوڑ');
INSERT INTO "ur_wahid_uddin" VALUES(71,27,'71:27','اگر تونے ان کو چھوڑدیاتو یہ تیرے بندوں کو گمراہ کریں گے اور ان کی نسل سے جو بھی پیدا ہوگا، وہ بدکار اور سخت منکر ہی ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(71,28,'71:28','اے میرے رب، میری مغفرت فرمااور میرے ماں باپ کی مغفرت فرمااور جو میرے گھر میں مومن ہوکر داخل ہو، تو اس کی مغفرت فرمااور سب مومن مردوں اور مومن عورتوں کو معاف فرمادے اور ظالموں کے لیے ہلاکت کے سوا کسی چیز میں اضافہ نہ کر');
INSERT INTO "ur_wahid_uddin" VALUES(72,1,'72:1','کہو کہ مجھے وحی کی گئی ہے کہ جنات کی ایک جماعت نے قرآن سنا تو انھوں نے کہا کہ ہم نے ایک عجیب قرآن سنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(72,2,'72:2','جو ہدایت کی راہ بتاتا ہے تو ہم اس پر ایمان لائے او ر ہم اپنے رب کے ساتھ کسی کو شریک نہ بنائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(72,3,'72:3','اور یہ کہ ہمارے رب کی شان بہت بلند ہےاس نے نہ کوئی بیوی بنائی ہے اور نہ اولاد');
INSERT INTO "ur_wahid_uddin" VALUES(72,4,'72:4','اور یہ کہ ہمارا نادان شخص اللہ کے بارے میں بہت خلافِ حق باتیں کہتا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(72,5,'72:5','اور ہم نے گمان کیاتھا کہ انسان اور جن، خدا کی شان میں کبھی جھوٹ بات نہ کہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(72,6,'72:6','اور یہ کہ انسانوں میں کچھ ایسے تھے جو جنات میں سے بعض کی پناہ لیتے تھے،تو انھوں نے جنوں کا غرور اور بڑھا دیا');
INSERT INTO "ur_wahid_uddin" VALUES(72,7,'72:7','اوریہ کہ انھوں نے بھی گمان کیا، جیسا تمھارا گمان تھا کہ اللہ کسی کو نہ اُٹھائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(72,8,'72:8','اور ہم نے آسمان کا جائزہ لیا تو ہم نے پایا کہ وہ سخت پہرے دار وں اور شعلوں سے بھرا ہوا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(72,9,'72:9','اور ہم اس کے بعض ٹھکانوں میں سننے کے لیے بیٹھا کرتے تھے، سو اب جو کوئی سننا چاہتا ہے تو وہ اپنے لیے ایک تیار شعلہ پاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(72,10,'72:10','اور ہم نہیں جانتے کہ یہ زمین والوں کے لیے کوئی برائی چاہی گئی ہے یا ان کے رب نے ان کے ساتھ بھلائی کاارادہ کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(72,11,'72:11','اور یہ کہ ہم میں بعض نیک ہیں اور بعض اور طرح کےہم مختلف طریقوں پر ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(72,12,'72:12','اور یہ کہ ہم نے سمجھ لیا کہ ہم زمین میں اللہ کو ہرا نہیں سکتےاور نہ بھاگ کر اس کو ہراسکتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(72,13,'72:13','اور یہ کہ ہم نے جب ہدایت کی بات سنی تو ہم اس پر ایمان لائےپس جو شخص اپنے رب پر ایمان لائے گا تو اس کو نہ کسی کمی کااندیشہ ہوگا، اور نہ زیادتی کا');
INSERT INTO "ur_wahid_uddin" VALUES(72,14,'72:14','اور یہ کہ ہم میں بعض فرماں بردار ہیں اور ہم میں بعض بے راہ ہیں ، پس جس نے فرماں برداری کی تو انھوں نے بھلائی کا راستہ ڈھونڈ لیا');
INSERT INTO "ur_wahid_uddin" VALUES(72,15,'72:15','اور جولوگ بے راہ ہیں تو وہ دوزخ کے ایندھن ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(72,16,'72:16','اور (مجھے وحی کی گئی ہے کہ) یہ لوگ اگر راستہ پر قائم ہوجاتے تو ہم ان کو خوب سیراب کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(72,17,'72:17','تاکہ اس میں ان کو آزمائیں ، اور جو شخص اپنے رب کی یاد سے اعراض کرے گا تو وہ اس کو سخت عذاب میں مبتلا کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(72,18,'72:18','اور یہ کہ مسجدیں اللہ کے لیے ہیں پس تم اللہ کے ساتھ کسی اور کو نہ پکارو');
INSERT INTO "ur_wahid_uddin" VALUES(72,19,'72:19','اور یہ کہ جب اللہ کا بندہ اس کو پکارنے کے لیے کھڑا ہو تو لوگ اس پر ٹوٹ پڑنے کے لیے تیار ہوگئے');
INSERT INTO "ur_wahid_uddin" VALUES(72,20,'72:20','كهوکہ میں صرف اپنے رب کو پکارتا ہوں اور اس کے ساتھ کسی کو شریک نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(72,21,'72:21','کہو کہ میں تم لوگوں کے لیے نہ کسی نقصان کااختیار رکھتا ہوں اور نہ کسی بھلائی کا');
INSERT INTO "ur_wahid_uddin" VALUES(72,22,'72:22','کہو کہ مجھ کو اللہ سے کوئی بچانہیں سکتااور نہ میں اس کے سوا کوئی پناہ پاسکتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(72,23,'72:23','بس اللہ ہی کی طرف سے پہنچادینا اور اس کے پیغاموں کی ادائیگی ہے اور جو شخص اللہ اور اس کے رسول کی نافرمانی کرے گا تو اس کے لیے جہنم کی آگ ہے جس میں وہ ہمیشہ رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(72,24,'72:24','یہاں تک کہ جب وہ دیکھیں گے اس چیز کو جس کا ان سے وعدہ کیا جارہا ہے تو وہ جان لیں گے کہ کس کے مددگار کمزور ہیں او رکون تعداد میں کم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(72,25,'72:25','کہو کہ میں نہیں جانتا کہ جس چیز کاتم سے وعدہ کیا جارہا ہے وہ قریب ہے یا میرے رب نے اس کے لیے لمبی مدت مقرر کررکھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(72,26,'72:26','غیب کا جاننے والا وہی ہے وہ اپنے غیب پر کسی کو مطلع نہیں کرتا');
INSERT INTO "ur_wahid_uddin" VALUES(72,27,'72:27','سوااس رسول کے جس کو اس نے پسند کیا ہو،تو وہ اس کے آگے اور پیچھے محافظ لگادیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(72,28,'72:28','تاکہ اللہ جان لے کے انھوں نے اپنے رب کے پیغامات پہنچادئے ہیں اور وہ ان کے ماحول کا احاطہ کيے ہوئے ہے اور اس نے ہر چیز کو گن رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(73,1,'73:1','اے کپڑے میں لپٹنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(73,2,'73:2','رات میں قیام کر مگر تھوڑا حصہ');
INSERT INTO "ur_wahid_uddin" VALUES(73,3,'73:3','آدھی رات یا اس سے کچھ کم کردو');
INSERT INTO "ur_wahid_uddin" VALUES(73,4,'73:4','یا اس سے کچھ بڑھا دو، اورقرآن کو ٹھہرٹھہر کر پڑھو');
INSERT INTO "ur_wahid_uddin" VALUES(73,5,'73:5','ہم تم پر ایک بھاری بات ڈالنے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(73,6,'73:6','بے شک رات کا اٹھنا سخت روندتا ہے اور بات ٹھیک نکلتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(73,7,'73:7','بے شک تم کو دن میں بہت کا م رہتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(73,8,'73:8','اور اپنے رب کا نام یاد کرو اور اس کی طرف متوجہ ہوجاؤ سب سے الگ ہوکر');
INSERT INTO "ur_wahid_uddin" VALUES(73,9,'73:9','وہ مشرق اور مغرب کا مالک ہے، اس کے سوا کوئی الہٰ نہیں ، پس تم اس کو اپنا کارساز بنالو');
INSERT INTO "ur_wahid_uddin" VALUES(73,10,'73:10','اور لوگ جو کچھ کہتے ہیں اس پر صبر کرواور بھلی طرح ان سے الگ ہوجاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(73,11,'73:11','اور جھٹلانے والے خوش حال لوگوں کا معاملہ مجھ پر چھوڑ دو اور ان کو تھوڑی ڈھیل دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(73,12,'73:12','ہمارے پاس،بیڑیاں ہیں اور دوزخ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(73,13,'73:13','اور گلے میں پھنس جانے والا کھانا ہے اوردردناک عذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(73,14,'73:14','جس دن زمین اور پہاڑ ہلنے لگیں گے اور پہاڑ ریت کے پھسلتے ہوئے تودے ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(73,15,'73:15','ہم نے تمھاری طرف ایک رسول بھیجا ہے،تم پرگواہ بناکر ،جس طرح ہم نے فرعون کی طرف ایک رسول بھیجا');
INSERT INTO "ur_wahid_uddin" VALUES(73,16,'73:16','پھر فرعون نے رسول کا کہا نہ مانا تو ہم نے اس کو پکڑا سخت پکڑنا');
INSERT INTO "ur_wahid_uddin" VALUES(73,17,'73:17','پس اگر تم نے انکار کیاتو تم اس دن کے عذاب سے کیسے بچوگے جو بچوں کو بوڑھا کردے گا');
INSERT INTO "ur_wahid_uddin" VALUES(73,18,'73:18','جس میں آسمان پھٹ جائے گا، بے شک اس کاوعدہ پورا ہوکر رہے گا');
INSERT INTO "ur_wahid_uddin" VALUES(73,19,'73:19','یہ ایک نصیحت ہے ،پس جوچاہے اپنے رب کی طرف راہ اختیار کرلے');
INSERT INTO "ur_wahid_uddin" VALUES(73,20,'73:20','بے شک تمھارا رب جانتا ہے کہ تم دوتہائی رات کے قریب یا آدھی رات یا ایک تہائی رات قیام کرتے ہو، اور ایک گروہ تمھارے ساتھیوں میں سے بھیاور اللہ ہی رات اور دن کا اندازہ ٹھہراتا ہے، اس نے جانا کہ تم اس کو پورا نہ کرسکو گے پس اس نے تم پر مہربانی فرمائی،اب قرآن سے پڑھو جتنا تم کو آسان ہواس نے جانا کہ تم میں بیمارہوں گے اور کتنے لوگ اللہ کے فضل کی تلاش میں زمین میں سفر کریں گے،اور دوسرے ایسے لوگ بھی ہوں گے جو اللہ کی راہ میں جہاد کریں گےپس اس میں سے پڑھو جتنا تم کو آسان ہو،اور نماز قائم کرو اور زکوٰۃ ادا کرو اور اللہ کو قرض دو اچھا قرضاور جو بھلائی تم اپنے لیے آگے بھیجو گے اس کو اللہ کے یہاں موجود پاؤگے وہ بہتر ہے اور ثواب میں زیادہ،اور اللہ سے معافی مانگو،بے شک اللہ بخشنے والا،مہربان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(74,1,'74:1','اے کپڑے میں لپٹنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(74,2,'74:2','اُٹھ اور لوگوں کو ڈرا');
INSERT INTO "ur_wahid_uddin" VALUES(74,3,'74:3','اور اپنے رب کی بڑائی بیان کر');
INSERT INTO "ur_wahid_uddin" VALUES(74,4,'74:4','اور اپنے کپڑے کو پاک رکھ');
INSERT INTO "ur_wahid_uddin" VALUES(74,5,'74:5','اور گندگی کو چھوڑدے');
INSERT INTO "ur_wahid_uddin" VALUES(74,6,'74:6','اور ایسا نہ کرو کہ احسان کرو اور بہت بدلہ چاہو');
INSERT INTO "ur_wahid_uddin" VALUES(74,7,'74:7','اور اپنے رب کے لیے صبر کرو');
INSERT INTO "ur_wahid_uddin" VALUES(74,8,'74:8','پھر جب صور پھونکا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(74,9,'74:9','تو وہ بڑا سخت دن ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(74,10,'74:10','منکروں پر آسان نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(74,11,'74:11','چھوڑدو مجھ کو اور اس شخص کو جس کو میں نے پیدا کیا اکیلا');
INSERT INTO "ur_wahid_uddin" VALUES(74,12,'74:12','اور اس کو بہت سامال دیا');
INSERT INTO "ur_wahid_uddin" VALUES(74,13,'74:13','اور پاس رہنے والے بیٹے');
INSERT INTO "ur_wahid_uddin" VALUES(74,14,'74:14','اور سب طرح کاسامان اس کے لیے مہیا کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(74,15,'74:15','پھر وہ طمع رکھتا ہے کہ میں اس کو اور زیادہ دوں');
INSERT INTO "ur_wahid_uddin" VALUES(74,16,'74:16','ہر گز نہیں ، وہ ہماری آیتوں کا مخالف ہے');
INSERT INTO "ur_wahid_uddin" VALUES(74,17,'74:17','عنقریب میں اس کو ایک سخت چڑھائی چڑھاؤں گا');
INSERT INTO "ur_wahid_uddin" VALUES(74,18,'74:18','اس نے سوچا اور بات بنائی');
INSERT INTO "ur_wahid_uddin" VALUES(74,19,'74:19','پس وہ ہلاک ہوا، اس نے کیسی بات بنائی');
INSERT INTO "ur_wahid_uddin" VALUES(74,20,'74:20','پھر وہ ہلاک ہو، اس نے کیسی بات بنائی');
INSERT INTO "ur_wahid_uddin" VALUES(74,21,'74:21','پھر اس نے دیکھا');
INSERT INTO "ur_wahid_uddin" VALUES(74,22,'74:22','پھر اس نے تیور چڑھائے اور منہ بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(74,23,'74:23','پھر پیٹھ پھیری اور تکبر کیا');
INSERT INTO "ur_wahid_uddin" VALUES(74,24,'74:24','پھر بولا یہ تو محض ایک جادو ہے جو پہلے سے چلا آرہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(74,25,'74:25','یہ تو بس آدمی کا کلام ہے');
INSERT INTO "ur_wahid_uddin" VALUES(74,26,'74:26','میں اس کو عنقریب دوزخ میں داخل کروں گا');
INSERT INTO "ur_wahid_uddin" VALUES(74,27,'74:27','اور تم کیاجانو کہ کیا ہے دوزخ');
INSERT INTO "ur_wahid_uddin" VALUES(74,28,'74:28','نہ باقی رہنے دے گی اور نہ چھوڑے گی');
INSERT INTO "ur_wahid_uddin" VALUES(74,29,'74:29','کھال جھلس دینے والی');
INSERT INTO "ur_wahid_uddin" VALUES(74,30,'74:30','اس پر فرشتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(74,31,'74:31','اور ہم نے دوزخ کے کارکن صرف فرشتے بنائے ہیں اور ہم نے ان کی جو گنتی رکھی ہے وہ صرف منکروں کو جانچنے کے لیے، تاکہ یقین حاصل کریں وہ لوگ جن کو کتاب عطا ہوئیاور ایمان والے اپنے ایمان کو بڑھائیں اور اہل کتاب اور مومنین شک نہ کریں ،اور تاکہ جن لوگوں کے دلوں میں مرض ہے اور منکر لوگ کہیں کہ اس سے اللہ کی کیا مراد ہےاس طرح اللہ گمراہ کرتا ہے جس کو چاہتا ہے اور ہدایت دیتا ہے جس کو چاہتا ہے، اور تیرے رب کے لشکر کو صرف وہی جانتا ہے، اور یہ تو صرف سمجھانا ہے لوگوں کے واسطے');
INSERT INTO "ur_wahid_uddin" VALUES(74,32,'74:32','ہرگز نہیں ، قسم ہے چاند کی');
INSERT INTO "ur_wahid_uddin" VALUES(74,33,'74:33','اور رات کی جب کہ وہ جانے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(74,34,'74:34','اور صبح کی جب وہ روشن ہوجائے');
INSERT INTO "ur_wahid_uddin" VALUES(74,35,'74:35','یقیناًدوزخ بڑی چیزوں میں سے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(74,36,'74:36','انسان کے لیے ڈراوا');
INSERT INTO "ur_wahid_uddin" VALUES(74,37,'74:37','ان کے لیے جو تم میں سے آگے کی طرف بڑھے یا پیچھے کی طرف ہٹے');
INSERT INTO "ur_wahid_uddin" VALUES(74,38,'74:38','وہ شخص اپنے اعمال کے بدلے میں رہن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(74,39,'74:39','دائیں والوں کے سوا');
INSERT INTO "ur_wahid_uddin" VALUES(74,40,'74:40','وہ باغوں میں ہوں گے، پوچھتے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(74,41,'74:41','مجرموں سے');
INSERT INTO "ur_wahid_uddin" VALUES(74,42,'74:42','تم کو کیا چیز دوزخ میں لے گئی');
INSERT INTO "ur_wahid_uddin" VALUES(74,43,'74:43','وہ کہیں گے،ہم نماز پڑھنے والوں میں سے نہ تھے');
INSERT INTO "ur_wahid_uddin" VALUES(74,44,'74:44','اور ہم غریبوں کو کھانا نہیں کھلاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(74,45,'74:45','اور ہم بحث کرنے والوں کے ساتھ بحث کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(74,46,'74:46','اور ہم انصاف کے دن کو جھٹلاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(74,47,'74:47','یہاں تک کہ وہ یقینی بات ہم پر آگئی');
INSERT INTO "ur_wahid_uddin" VALUES(74,48,'74:48','تو ان کو شفاعت کرنے والوں کی شفاعت کچھ فائدہ نہ دے گی');
INSERT INTO "ur_wahid_uddin" VALUES(74,49,'74:49','پھر ان کو کیا ہوگیا ہے کہ وہ نصیحت سے روگردانی کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(74,50,'74:50','گویا کہ وہ وحشی گدھے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(74,51,'74:51','جوشیر سے بھاگے جارہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(74,52,'74:52','بلکہ ان میں سے ہر شخص یہ چاہتا ہے کہ اس کو کھلے ہوئے نوشتے دئے جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(74,53,'74:53','ہرگز نہیں ،بلکہ یہ لوگ آخرت سے نہیں ڈرتے');
INSERT INTO "ur_wahid_uddin" VALUES(74,54,'74:54','ہرگز نہیں یہ تو ایک نصیحت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(74,55,'74:55','پس جس کا جی چاہے ،اس سے نصیحت حاصل کرے');
INSERT INTO "ur_wahid_uddin" VALUES(74,56,'74:56','اور وہ اس سے نصیحت حاصل نہیں کریں گے مگر یہ کہ اللہ چاہے وہی ہے جس سے ڈرنا چاہيے اور وہی ہے بخشنے کے لائق');
INSERT INTO "ur_wahid_uddin" VALUES(75,1,'75:1','نہیں ، میں قسم کھاتا ہوں قیامت کے دن کی');
INSERT INTO "ur_wahid_uddin" VALUES(75,2,'75:2','اور نہیں ، میں قسم کھاتا ہوں ملامت کرنے والے نفس کی');
INSERT INTO "ur_wahid_uddin" VALUES(75,3,'75:3','کیاانسان خیال کرتا ہے کہ ہم اس کی ہڈیوں کو جمع نہ کریں گے');
INSERT INTO "ur_wahid_uddin" VALUES(75,4,'75:4','کیوں نہیں ، ہم اس پر قادر ہیں کہ اس کی انگلیوں کے پور پور تک درست کردیں');
INSERT INTO "ur_wahid_uddin" VALUES(75,5,'75:5','بلکہ انسان چاہتا ہے کہ وہ ڈھٹائی کرے اس کے سامنے');
INSERT INTO "ur_wahid_uddin" VALUES(75,6,'75:6','وہ پوچھتا ہے کہ قیامت کا دن کب آئے گا');
INSERT INTO "ur_wahid_uddin" VALUES(75,7,'75:7','پس جب آنکھیں خیرہ ہوجائیں گی');
INSERT INTO "ur_wahid_uddin" VALUES(75,8,'75:8','اور چاند بے نور ہوجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(75,9,'75:9','اور سورج اور چاند اکھٹا کردئے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(75,10,'75:10','اس دن انسان کہے گا کہ کہاں بھاگوں');
INSERT INTO "ur_wahid_uddin" VALUES(75,11,'75:11','ہرگز نہیں ،کہیں پناہ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(75,12,'75:12','اس دن تیرے رب ہی کے پاس ٹھکانا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(75,13,'75:13','اس دن انسان کو بتایا جائے گا کہ اس نے کیا آگے بھیجا اور کیا پیچھے چھوڑا');
INSERT INTO "ur_wahid_uddin" VALUES(75,14,'75:14','بلکہ انسان خود اپنے آپ کو جانتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(75,15,'75:15','چاہے وہ کتنے ہی بہانے پیش کرے');
INSERT INTO "ur_wahid_uddin" VALUES(75,16,'75:16','تم اس کے پڑھنے پراپنی زبان نہ چلاؤ تاکہ تم اس کوجلدی سیکھ لو');
INSERT INTO "ur_wahid_uddin" VALUES(75,17,'75:17','ہمارے اوپر ہے اس کوجمع کرنا اوراس کوسنانا');
INSERT INTO "ur_wahid_uddin" VALUES(75,18,'75:18','پس جب ہم اس کوسنائیں تو تم اس سنانے کی پیروی کرو');
INSERT INTO "ur_wahid_uddin" VALUES(75,19,'75:19','پھر ہمارے اوپر ہے اس کو بیان کردینا');
INSERT INTO "ur_wahid_uddin" VALUES(75,20,'75:20','ہر گز نہیں ،بلکہ تم چاہتے ہو جو جلد آئے');
INSERT INTO "ur_wahid_uddin" VALUES(75,21,'75:21','اور تم چھوڑتے ہو جو دیر میں آئے');
INSERT INTO "ur_wahid_uddin" VALUES(75,22,'75:22','کچھ چہرے اس دن بارونق ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(75,23,'75:23','اپنے رب کی طرف دیکھ رہے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(75,24,'75:24','اور کچھ چہرے اس دن اداس ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(75,25,'75:25','گمان کررہے ہوں گے کہ ان کے ساتھ کمر توڑدینے والا معاملہ کیاجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(75,26,'75:26','ہرگز نہیں ،جب جان حلق تک پہنچ جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(75,27,'75:27','اور کہاجائے گا کہ کون ہے جھاڑ پھونک کرنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(75,28,'75:28','اور وہ گمان کرے گا کہ یہ جدائی کا وقت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(75,29,'75:29','اور پنڈلی سے پنڈلی لپٹ جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(75,30,'75:30','وہ دن ہوگا تیرے رب کی طرف جانے کا');
INSERT INTO "ur_wahid_uddin" VALUES(75,31,'75:31','تو اس نے نہ سچ مانا اور نہ نماز پڑھی');
INSERT INTO "ur_wahid_uddin" VALUES(75,32,'75:32','بلکہ جھٹلایا اور منہ موڑا');
INSERT INTO "ur_wahid_uddin" VALUES(75,33,'75:33','پھر اکڑتا ہوا اپنے لوگوں کی طرف چلاگیا');
INSERT INTO "ur_wahid_uddin" VALUES(75,34,'75:34','افسوس ہے تجھ پر افسوس ہے');
INSERT INTO "ur_wahid_uddin" VALUES(75,35,'75:35','پھرافسوس ہے تجھ پر افسوس ہے');
INSERT INTO "ur_wahid_uddin" VALUES(75,36,'75:36','کیا انسان خیال کرتا ہے کہ وہ بس یوں ہی چھوڑدیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(75,37,'75:37','کیا وہ ٹپکائی ہوئی منی کی ایک بوند نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(75,38,'75:38','پھر وہ علقہ ہوگیا،پھر اللہ نے بنایا، پھر اعضا درست کيے');
INSERT INTO "ur_wahid_uddin" VALUES(75,39,'75:39','پھر اس کی دو قسمیں کردیں ، مرد اور عورت');
INSERT INTO "ur_wahid_uddin" VALUES(75,40,'75:40','کیا وہ اس پر قادر نہیں کہ مُردوں کو زندہ کردے');
INSERT INTO "ur_wahid_uddin" VALUES(76,1,'76:1','کبھی انسان پرزمانہ میں ایک وقت گزرا ہے کہ وہ کوئی قابلِ ذکر چیز نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(76,2,'76:2','ہم نے انسان کو ایک مخلوط بوند سے پیدا کیا،ہم اس کو پلٹتے رہےپھر ہم نے اس کو سننے والا،دیکھنے والا بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(76,3,'76:3','ہم نے اس کو راہ سمجھائی،چاہے وہ شکر کرنے والا بنے یا انکار کرنے والا');
INSERT INTO "ur_wahid_uddin" VALUES(76,4,'76:4','ہم نے منکروں کے لیے زنجیریں اور طوق اور بھڑکتی ہوئی آگ تیار کر رکھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(76,5,'76:5','نیک لوگ ایسے پیالے سے پئیں گے جس میں کافور کی آمیزش ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(76,6,'76:6','اس چشمہ سے اللہ کے بندے پئیں گےوہ اس کی شاخیں نکالیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(76,7,'76:7','وہ لوگ واجبات کو پورا کرتے ہیں اور ایسے دن سے ڈرتے ہیں جس کی سختی عام ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(76,8,'76:8','اور اس کی محبت پر کھانا کھلاتے ہیں محتاج کو اور یتیم کواور قیدی کو');
INSERT INTO "ur_wahid_uddin" VALUES(76,9,'76:9','ہم جوتم کو کھلاتے ہیں تو اللہ کی خوشی چاہنے کے لیےہم نہ تم سے بدلہ چاہتے ہیں اور نہ شکرگزاری');
INSERT INTO "ur_wahid_uddin" VALUES(76,10,'76:10','ہم اپنے رب کی طرف سے ایک سخت اور تلخ دن کا اندیشہ رکھتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(76,11,'76:11','پس اللہ نے ان کواس دن کی سختی سے بچالیااور ان کو تازگی اور خوشی عطا فرمائی');
INSERT INTO "ur_wahid_uddin" VALUES(76,12,'76:12','اور ان کے صبر کے بدلہ میں ان کوجنت اور ریشمی لباس عطاکیا');
INSERT INTO "ur_wahid_uddin" VALUES(76,13,'76:13','ٹیک لگائے ہوں گے وہ اس میں تختوں پر، اس میں نہ وہ گرمی سے دوچار ہوں گے اور نہ سردی سے');
INSERT INTO "ur_wahid_uddin" VALUES(76,14,'76:14','جنت کے سائے ان پر جھکے ہوئے ہوں گے اور اس کے پھل ان کے بس میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(76,15,'76:15','اور ان کے آگے چاندی کے برتن اور شیشے کے پیالے گردش میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(76,16,'76:16','شیشے چاندی کے ہوں گے،جن کو بھرنے والوں نے مناسب انداز سے بھرا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(76,17,'76:17','اور وہاں ان کو ایک اور جام پلایا جائے گا جس میں سونٹھ کی آمیزش ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(76,18,'76:18','یہ اس میں ایک چشمہ ہے جس کو سلسبیل کہا جاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(76,19,'76:19','اور ان کے پاس پھر رہے ہوں گے ایسے بچے جو ہمیشہ بچے ہی رہیں گےتم انھیں دیکھو تو سمجھو کہ موتی ہیں جو بکھیردئے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(76,20,'76:20','اور تم جہاں دیکھوگے، وہیں عظیم نعمت اورعظیم بادشاہی دیکھوگے');
INSERT INTO "ur_wahid_uddin" VALUES(76,21,'76:21','ان کے اوپر باریک ریشم کے سبز کپڑے ہوں گے اور دبیز ریشم کے کپڑے بھیاور ان کو چاندی کے کنگن پہنائے جائیں گےاور ان کا رب ان کو پاکیزہ مشروب پلائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(76,22,'76:22','بے شک یہ تمھارا صلہ ہے اور تمھاری کوشش مقبول ہوئی');
INSERT INTO "ur_wahid_uddin" VALUES(76,23,'76:23','ہم نے تم پرقرآن تھوڑا تھوڑا کرکے اُتارا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(76,24,'76:24','پس تم اپنے رب کے حکم پر صبر کرو اور ان میں سے کسی گناہ گار یا ناشکر کی بات نہ مانو');
INSERT INTO "ur_wahid_uddin" VALUES(76,25,'76:25','اور اپنے رب کانام صبح وشام یادکرو');
INSERT INTO "ur_wahid_uddin" VALUES(76,26,'76:26','اور رات کوبھی اس کوسجدہ کرواور اس کی تسبیح کرو رات کے طویل حصہ میں');
INSERT INTO "ur_wahid_uddin" VALUES(76,27,'76:27','یہ لوگ جلدی ملنے والی چیز کو چاہتے ہیں اور انھوں نے چھوڑ رکھا ہے اپنے پیچھے ایک بھاری دن کو');
INSERT INTO "ur_wahid_uddin" VALUES(76,28,'76:28','ہم ہی نے ان کو پیداکیا اور ہم ہی نے ان کے جوڑ بند مضبوط کيے،اور جب ہم چاہیں گے انھیں جیسے لوگ ان کی جگہ بدل لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(76,29,'76:29','یہ ایک نصیحت ہے پس جو شخص چاہے، اپنے رب کی طرف راستہ اختیار کرلے');
INSERT INTO "ur_wahid_uddin" VALUES(76,30,'76:30','اور تم نہیں چاہ سکتے مگر یہ کہ اللہ چاہےبے شک اللہ جاننے والا، حکمت والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(76,31,'76:31','وہ جس کو چاہتا ہے اپنی رحمت میں داخل کرتا ہے، اور ظالموں کے لیے اس نے دردناک عذاب تیار کررکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(77,1,'77:1','قسم ہے ہواؤں کی جو چھوڑدی جاتی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(77,2,'77:2','پھر وہ طوفانی رفتار سے چلتی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(77,3,'77:3','اور بادلوں کو اُٹھاکر پھیلاتی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(77,4,'77:4','پھر معاملہ کو جدا کرتی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(77,5,'77:5','پھر یاد دہانی ڈالتی ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(77,6,'77:6','عذر کے طور پر یا ڈراوے کے طور پر');
INSERT INTO "ur_wahid_uddin" VALUES(77,7,'77:7','جو وعدہ تم سے کیا جارہا ہے وہ ضرور واقع ہونے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(77,8,'77:8','پس جب ستارے بے نور ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(77,9,'77:9','اور جب آسمان پھٹ جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(77,10,'77:10','اورجب پہاڑ ریزہ ریزہ کردئے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(77,11,'77:11','اورجب پیغمبر وقتِ معین پر جمع کيے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(77,12,'77:12','کس دن کے لیے وہ ٹالے گئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(77,13,'77:13','فیصلہ کے دن کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,14,'77:14','اور تم کو کیا خبر کہ فیصلہ کادن کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(77,15,'77:15','تباہی ہے اس دن جھٹلانے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,16,'77:16','کیا ہم نے اگلوں کو ہلاک نہیں کیا');
INSERT INTO "ur_wahid_uddin" VALUES(77,17,'77:17','پھر ہم ان کے پیچھے بھیجتے ہیں پچھلوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(77,18,'77:18','ہم مجرموں کے ساتھ ایسا ہی کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(77,19,'77:19','خرابی ہے اس دن جھٹلانے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,20,'77:20','کیا ہم نے تم کو ایک حقیر پانی سے پیدا نہیں کیا');
INSERT INTO "ur_wahid_uddin" VALUES(77,21,'77:21','پھر اس کو ایک محفوظ جگہ رکھا');
INSERT INTO "ur_wahid_uddin" VALUES(77,22,'77:22','ایک مقرر مدت تک');
INSERT INTO "ur_wahid_uddin" VALUES(77,23,'77:23','پھرہم نے ایک اندازہ ٹھہرایا، ہم کیسا اچھا اندازہ ٹھہرانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(77,24,'77:24','خرابی ہے اس دن جھٹلانے والوں کی');
INSERT INTO "ur_wahid_uddin" VALUES(77,25,'77:25','کیا ہم نے زمین کو سمیٹنے والا نہیں بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(77,26,'77:26','زندوں کے لیے اورمردوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,27,'77:27','اورہم نے اس میں اونچے پہاڑ بنائے اور تم کو میٹھا پانی پلایا');
INSERT INTO "ur_wahid_uddin" VALUES(77,28,'77:28','اس روز خرابی ہے جھٹلانے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,29,'77:29','چلو اس چیز کی طرف جس کو تم جھٹلاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(77,30,'77:30','چلو تین شاخوں والے سایہ کی طرف');
INSERT INTO "ur_wahid_uddin" VALUES(77,31,'77:31','جس میں نہ سایہ ہے اور نہ وہ گرمی سے بچاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(77,32,'77:32','وہ انگارے برسائے گا جیسے کہ اونچا محل');
INSERT INTO "ur_wahid_uddin" VALUES(77,33,'77:33','زرد اونٹوں کی مانند');
INSERT INTO "ur_wahid_uddin" VALUES(77,34,'77:34','اس دن خرابی ہے جھٹلانے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,35,'77:35','یہ وہ دن ہے جس میں لوگ بول نہ سکیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(77,36,'77:36','اور نہ ان کو اجازت ہوگی کہ وہ عذر پیش کریں');
INSERT INTO "ur_wahid_uddin" VALUES(77,37,'77:37','خرابی ہے اس دن جھٹلانے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,38,'77:38','یہ فیصلہ کا دن ہےہم نے تم کواور اگلے لوگوں کو جمع کرلیا');
INSERT INTO "ur_wahid_uddin" VALUES(77,39,'77:39','پس اگر کوئی تدبیر ہو تو مجھ پر تدبیر چلاؤ');
INSERT INTO "ur_wahid_uddin" VALUES(77,40,'77:40','خرابی ہے اس دن جھٹلانے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,41,'77:41','بے شک ڈرنے والے سایہ میں اور چشموں میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(77,42,'77:42','اور پھلوں میں جو وہ چاہیں');
INSERT INTO "ur_wahid_uddin" VALUES(77,43,'77:43','مزے کے ساتھ کھاؤ اور پیواس عمل کے بدلے میں جو تم کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(77,44,'77:44','ہم نیک لوگوں کو ایسا ہی بدلہ دیتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(77,45,'77:45','خرابی ہے اس دن جھٹلانے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,46,'77:46','کھاؤ اور برت لو تھوڑے دن، بے شک تم گناہ گار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(77,47,'77:47','خرابی ہے اس دن جھٹلانے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,48,'77:48','اور جب ان سے کہا جاتا ہے کہ جھکو تو وہ نہیں جھکتے');
INSERT INTO "ur_wahid_uddin" VALUES(77,49,'77:49','خرابی ہے اس دن جھٹلانے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(77,50,'77:50','اب اس کے بعد وہ کس بات پر ایمان لائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(78,1,'78:1','لوگ کس چیز کے بارے میں پوچھ رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(78,2,'78:2','اس بڑی خبر کے بارے میں');
INSERT INTO "ur_wahid_uddin" VALUES(78,3,'78:3','جس میں وہ لوگ مختلف ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(78,4,'78:4','ہرگز نہیں ،عنقریب وہ جان لیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(78,5,'78:5','ہر گز نہیں عنقریب وہ جان لیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(78,6,'78:6','کیا ہم نے زمین کو فرش نہیں بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(78,7,'78:7','اور پہاڑوں کو میخیں');
INSERT INTO "ur_wahid_uddin" VALUES(78,8,'78:8','اور تم کو ہم نے بنایا جوڑے جوڑے');
INSERT INTO "ur_wahid_uddin" VALUES(78,9,'78:9','اور نیند کو بنایا تمھاری تکان دور کرنے کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(78,10,'78:10','اور ہم نے رات کو پردہ بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(78,11,'78:11','اور ہم نے دن کو معاش کا وقت بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(78,12,'78:12','اور ہم نے تمھارے اوپر سات مضبوط آسمان بنائے');
INSERT INTO "ur_wahid_uddin" VALUES(78,13,'78:13','اورہم نے اس میں ایک چمکتا ہوا چراغ رکھ دیا');
INSERT INTO "ur_wahid_uddin" VALUES(78,14,'78:14','اور ہم نے پانی بھرے بادلوں سے موسلا دھار پانی برسایا');
INSERT INTO "ur_wahid_uddin" VALUES(78,15,'78:15','تاکہ ہم اس کے ذریعہ سے اُگائیں غلہ اور سبزی');
INSERT INTO "ur_wahid_uddin" VALUES(78,16,'78:16','اور گھنے باغ');
INSERT INTO "ur_wahid_uddin" VALUES(78,17,'78:17','بے شک فیصلہ کا دن ایک مقرر وقت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(78,18,'78:18','جس دن صور پھونکا جائے گا، پھر تم فوج درفوج آؤگے');
INSERT INTO "ur_wahid_uddin" VALUES(78,19,'78:19','اور آسمان کھول دیاجاے گا، پھر اس میں دروازے ہی دروازے ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(78,20,'78:20','اور پہاڑ چلادئے جائیں گے تو وہ ریت کی طرح ہو جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(78,21,'78:21','بے شک جہنم گھات میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(78,22,'78:22','سرکشوں کاٹھکانا');
INSERT INTO "ur_wahid_uddin" VALUES(78,23,'78:23','اس میں وہ مدتوں پڑے رہیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(78,24,'78:24','اس میں نہ وہ کسی ٹھنڈک کو چکھیں گے اور نہ پینے کی چیزکو');
INSERT INTO "ur_wahid_uddin" VALUES(78,25,'78:25','مگر گرم پانی اورپیپ');
INSERT INTO "ur_wahid_uddin" VALUES(78,26,'78:26','بدلہ ان کے عمل کے موافق');
INSERT INTO "ur_wahid_uddin" VALUES(78,27,'78:27','وہ حساب کااندیشہ نہیں رکھتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(78,28,'78:28','اور انھوں نے ہماری آیتوں کو بالکل جھٹلادیا');
INSERT INTO "ur_wahid_uddin" VALUES(78,29,'78:29','اور ہم نے ہرچیز کو لکھ کر شمار کررکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(78,30,'78:30','پس چکھو کہ ہم تمھاری سزا ہی بڑھاتے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(78,31,'78:31','بے شک ڈرنے والوں کے لیے کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(78,32,'78:32','باغ اور انگور');
INSERT INTO "ur_wahid_uddin" VALUES(78,33,'78:33','اور نوخیز ہم سن لڑکیاں');
INSERT INTO "ur_wahid_uddin" VALUES(78,34,'78:34','اور بھرے ہوئے جام');
INSERT INTO "ur_wahid_uddin" VALUES(78,35,'78:35','وہاں وہ لغو اور جھوٹی بات نہ سنیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(78,36,'78:36','بدلہ تیرے رب کی طرف سے ہوگا، ان کے عمل کے حساب سے');
INSERT INTO "ur_wahid_uddin" VALUES(78,37,'78:37','رحمٰن کی طرف سے جو آسمانوں اور زمین اور ان کے درمیان کی چیزوں کارب ہے، کوئی قدرت نہیں رکھتا کہ اس سے بات کرے');
INSERT INTO "ur_wahid_uddin" VALUES(78,38,'78:38','جس دن روح اور فرشتے صف بستہ کھڑے ہوں گے،کوئی نہ بولے گا مگر جس کو رحمن اجازت دے،اوروہ ٹھیک بات کہے گا');
INSERT INTO "ur_wahid_uddin" VALUES(78,39,'78:39','یہ دن برحق ہے، پس جو چاہے اپنے رب کی طرف ٹھکانا بنالے');
INSERT INTO "ur_wahid_uddin" VALUES(78,40,'78:40','ہم نے تم کو قریب آجانے والے عذاب سے ڈرا دیا ہے جس دن آدمی اس کو دیکھ لے گا جو اس کے ہاتھوں نے آگے بھیجا ہے،اور منکر کہے گا، کاش میں مٹی ہوتا');
INSERT INTO "ur_wahid_uddin" VALUES(79,1,'79:1','قسم ہے جڑسے اُکھاڑنے والی ہواؤں کی');
INSERT INTO "ur_wahid_uddin" VALUES(79,2,'79:2','اور قسم ہے آہستہ چلنے والی ہواؤں کی');
INSERT INTO "ur_wahid_uddin" VALUES(79,3,'79:3','اورقسم ہے تیرنے والے بادلوں کی');
INSERT INTO "ur_wahid_uddin" VALUES(79,4,'79:4','پھر سبقت کرکے بڑھنے والوں کی');
INSERT INTO "ur_wahid_uddin" VALUES(79,5,'79:5','پھر معاملہ کی تدبیر کرنے والوں کی');
INSERT INTO "ur_wahid_uddin" VALUES(79,6,'79:6','جس دن ہلادینے والی ہلاڈالے گی');
INSERT INTO "ur_wahid_uddin" VALUES(79,7,'79:7','اس کے پیچھے ایک اور آنے والی چیز آئے گی');
INSERT INTO "ur_wahid_uddin" VALUES(79,8,'79:8','کتنے دل اس دن دھڑکتے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(79,9,'79:9','ان کی آنکھیں جھک رہی ہوں گی');
INSERT INTO "ur_wahid_uddin" VALUES(79,10,'79:10','وہ کہتے ہیں کیا ہم پہلی حالت میں پھر واپس ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(79,11,'79:11','کیا جب ہم بوسیدہ ہڈیاں ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(79,12,'79:12','انھوں نے کہا کہ یہ واپسی تو بڑے گھاٹے کی ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(79,13,'79:13','وہ تو بس ایک ڈانٹ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(79,14,'79:14','پھر یکایک وہ میدان میں موجود ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(79,15,'79:15','کیا تم کو موسیٰ کی بات پہنچی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(79,16,'79:16','جب کہ اس کے رب نے اس کو طُویٰ کی مقدس وادی میں پکارا');
INSERT INTO "ur_wahid_uddin" VALUES(79,17,'79:17','فرعون کے پاس جاؤ، وہ سرکش ہوگیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(79,18,'79:18','پھر اس سے کہو، کیا تجھ کو اس بات کی خواہش ہے کہ تو درست ہوجائے');
INSERT INTO "ur_wahid_uddin" VALUES(79,19,'79:19','اورمیں تجھ کوتیرے رب کی راہ دکھاؤں پھر تو ڈرے');
INSERT INTO "ur_wahid_uddin" VALUES(79,20,'79:20','پس موسیٰ نے اس کو بڑی نشانی دکھائی');
INSERT INTO "ur_wahid_uddin" VALUES(79,21,'79:21','پھر اس نے جھٹلایا اور نہ مانا');
INSERT INTO "ur_wahid_uddin" VALUES(79,22,'79:22','پھر وہ پلٹا کوشش کرتے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(79,23,'79:23','پھر اس نے جمع کیا، پھر اس نے پکارا');
INSERT INTO "ur_wahid_uddin" VALUES(79,24,'79:24','پس اس نے کہا کہ میں تمھارا سب سے بڑا رب ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(79,25,'79:25','پس اللہ نے اس کو آخرت اور دنیا کے عذاب میں پکڑا');
INSERT INTO "ur_wahid_uddin" VALUES(79,26,'79:26','بے شک اس میں نصیحت ہے ہر اس شخص کے لیے جوڈرے');
INSERT INTO "ur_wahid_uddin" VALUES(79,27,'79:27','کیاتمھارا بنانازیادہ مشکل ہے یا آسمان کا، اللہ نے اس کو بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(79,28,'79:28','اس کی چھت کوبلند کیا پھر اس کو درست بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(79,29,'79:29','اور اس کی رات کو تاریک بنایا اور اس کے دن کو ظاہر کیا');
INSERT INTO "ur_wahid_uddin" VALUES(79,30,'79:30','اور زمین کو اس کے بعد پھیلایا');
INSERT INTO "ur_wahid_uddin" VALUES(79,31,'79:31','اس سے اس کا پانی اور چارہ نکالا');
INSERT INTO "ur_wahid_uddin" VALUES(79,32,'79:32','اور پہاڑوں کو قائم کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(79,33,'79:33','سامانِ حیات کے طور پر تمھارے لیے اور تمھارے مویشیوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(79,34,'79:34','پھر جب وہ بڑا ہنگامہ آئے گا');
INSERT INTO "ur_wahid_uddin" VALUES(79,35,'79:35','جس دن انسان اپنے کيے کو یاد کرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(79,36,'79:36','اور دیکھنے والوں کے سامنے دوزخ ظاہر کردی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(79,37,'79:37','پس جس نے سرکشی کی');
INSERT INTO "ur_wahid_uddin" VALUES(79,38,'79:38','اور دنیا کی زندگی کو ترجیح دی');
INSERT INTO "ur_wahid_uddin" VALUES(79,39,'79:39','تو دوزخ اس کا ٹھکانا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(79,40,'79:40','اور جو شخص اپنے رب کے سامنے کھڑا ہونے سے ڈرا اور نفس کو خواہش سے روکا');
INSERT INTO "ur_wahid_uddin" VALUES(79,41,'79:41','تو جنت اس کا ٹھکانا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(79,42,'79:42','وہ قیامت کے بارے میں پوچھتے ہیں کہ وہ کب کھڑی ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(79,43,'79:43','تم کو کیا کام اس کے ذکر سے');
INSERT INTO "ur_wahid_uddin" VALUES(79,44,'79:44','یہ معاملہ تیرے رب کے حوالے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(79,45,'79:45','تم تو بس ڈرانے والے ہو اس شخص کو جو ڈرے');
INSERT INTO "ur_wahid_uddin" VALUES(79,46,'79:46','جس روز یہ اس کو دیکھیں گے تو گویا وہ دنیا میں نہیں ٹھہرے، مگر ایک شام یا اس کی صبح');
INSERT INTO "ur_wahid_uddin" VALUES(80,1,'80:1','ترش رو ہوا اور بے رخی برتی');
INSERT INTO "ur_wahid_uddin" VALUES(80,2,'80:2','اس بات پر کہ اندھا اس کے پاس آیا');
INSERT INTO "ur_wahid_uddin" VALUES(80,3,'80:3','اور تم کو کیا خبر کہ وہ سدھر جائے');
INSERT INTO "ur_wahid_uddin" VALUES(80,4,'80:4','یا نصیحت کو سنے تو نصیحت اس کے کام آئے');
INSERT INTO "ur_wahid_uddin" VALUES(80,5,'80:5','جوشخص بے پروائی برتتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(80,6,'80:6','تم اس کی فکرمیں پڑتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(80,7,'80:7','حالاں کہ تم پر کوئی ذمہ داری نہیں اگر وہ نہ سدھرے');
INSERT INTO "ur_wahid_uddin" VALUES(80,8,'80:8','اور جو شخص تمھارے پاس دوڑتا ہوا آتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(80,9,'80:9','اور وہ ڈرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(80,10,'80:10','تو تم اس سے بے پروائی برتتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(80,11,'80:11','ہرگز نہیں ، یہ تو ایک نصیحت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(80,12,'80:12','پس جو چاہے یا ددہانی حاصل کرے');
INSERT INTO "ur_wahid_uddin" VALUES(80,13,'80:13','وہ ایسے صحیفوں میں ہے جومکرم ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(80,14,'80:14','بلندمرتبہ ہیں ،پاکیزہ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(80,15,'80:15','کاتبوں کے ہاتھوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(80,16,'80:16','معزز، نیک');
INSERT INTO "ur_wahid_uddin" VALUES(80,17,'80:17','برا ہو آدمی کا، وہ کیسا ناشکرہے');
INSERT INTO "ur_wahid_uddin" VALUES(80,18,'80:18','اس کو اللہ نے کس چیز سے پیداکیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(80,19,'80:19','ایک بوند سے اس کو پیداکیاپھر اس کے لیے اندازہ ٹھہرایا');
INSERT INTO "ur_wahid_uddin" VALUES(80,20,'80:20','پھر اس کے لیے راہ آسان کردی');
INSERT INTO "ur_wahid_uddin" VALUES(80,21,'80:21','پھر اس کو موت دی، پھر اس کو قبر میں لے گیا');
INSERT INTO "ur_wahid_uddin" VALUES(80,22,'80:22','پھر جب وہ چاہے گا اس کودوبارہ زندہ کردے گا');
INSERT INTO "ur_wahid_uddin" VALUES(80,23,'80:23','ہرگز نہیں ،اس نے پورا نہیں کیا جس کااللہ نے اسے حکم دیا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(80,24,'80:24','پس انسان کو چاہيے کہ وہ اپنے کھانے کو دیکھے');
INSERT INTO "ur_wahid_uddin" VALUES(80,25,'80:25','ہم نے پانی برسایا اچھی طرح');
INSERT INTO "ur_wahid_uddin" VALUES(80,26,'80:26','پھر ہم نے زمین کو اچھی طرح پھاڑا');
INSERT INTO "ur_wahid_uddin" VALUES(80,27,'80:27','پھر اگائے اس میں غلے');
INSERT INTO "ur_wahid_uddin" VALUES(80,28,'80:28','اور انگور اور ترکاریاں');
INSERT INTO "ur_wahid_uddin" VALUES(80,29,'80:29','اور زیتون اور کھجور');
INSERT INTO "ur_wahid_uddin" VALUES(80,30,'80:30','اور گھنے باغ');
INSERT INTO "ur_wahid_uddin" VALUES(80,31,'80:31','اور پھل اورسبزہ');
INSERT INTO "ur_wahid_uddin" VALUES(80,32,'80:32','تمھارے لیے اور تمھارے مویشیوں کے لیے سامانِ حیات کے طور پر');
INSERT INTO "ur_wahid_uddin" VALUES(80,33,'80:33','پس جب وہ کانوں کو بہرہ کردینے والا شور برپاہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(80,34,'80:34','جس دن آدمی بھاگے گا اپنے بھائی سے');
INSERT INTO "ur_wahid_uddin" VALUES(80,35,'80:35','اور اپنی ماں سے اور اپنے باپ سے');
INSERT INTO "ur_wahid_uddin" VALUES(80,36,'80:36','او ر اپنی بیوی سے اور اپنے بیٹوں سے');
INSERT INTO "ur_wahid_uddin" VALUES(80,37,'80:37','ان میں سے ہر شخص کو اس دن ایسا فکر ہوگا جو اس کو کسی اور طرف متوجہ نہ ہونے دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(80,38,'80:38','کچھ چہرے اس دن روشن ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(80,39,'80:39','ہنستے ہوئے،خوشی کرتے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(80,40,'80:40','اور کچھ چہروں پر اس دن خاک اُڑ رہی ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(80,41,'80:41','ان پرسیاہی چھائی ہوئی ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(80,42,'80:42','یہی لوگ منکر ہیں ،ڈھیٹ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(81,1,'81:1','جب سورج لپیٹ دیاجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(81,2,'81:2','اور جب ستارے بے نور ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(81,3,'81:3','اور جب پہاڑ چلائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(81,4,'81:4','اور جب دس مہینہ کی حاملہ اونٹنیاں آوارہ پھریں گی');
INSERT INTO "ur_wahid_uddin" VALUES(81,5,'81:5','اور جب وحشی جانور اكٹھا ہو جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(81,6,'81:6','اور جب سمندر بھڑکادئے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(81,7,'81:7','اور جب ایک ایک قسم کے لوگ اكٹھا کيے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(81,8,'81:8','اور جب زندہ گاڑی ہوئی لڑکی سے پوچھا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(81,9,'81:9','کہ وہ کس قصور میں ماری گئی');
INSERT INTO "ur_wahid_uddin" VALUES(81,10,'81:10','اور جب اعمال نامے کھولے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(81,11,'81:11','اور جب آسمان کھل جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(81,12,'81:12','اور دوزخ بھڑکائی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(81,13,'81:13','اور جب جنت قریب لائی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(81,14,'81:14','ہر شخص جان لے گا کہ وہ کیا لے کر آیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(81,15,'81:15','پس نہیں ، میں قسم کھاتا ہوں پیچھے ہٹنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(81,16,'81:16','چلنے والے اور چھپ جانے والے ستاروں کی');
INSERT INTO "ur_wahid_uddin" VALUES(81,17,'81:17','اور رات کی جب وہ جانے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(81,18,'81:18','اور صبح کی جب وہ آنے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(81,19,'81:19','کہ یہ ایک باعزت رسول کا لایا ہو ا کلام ہے');
INSERT INTO "ur_wahid_uddin" VALUES(81,20,'81:20','قوت والا، عرش والے کے نزدیک بلند مرتبہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(81,21,'81:21','اس کی بات مانی جاتی ہے،وہ امانت دار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(81,22,'81:22','اور تمھارا ساتھی دیوانہ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(81,23,'81:23','اور اس نے اس کو کھلے افق میں دیکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(81,24,'81:24','اور وہ غیب کی باتوں کا حریص نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(81,25,'81:25','اوروہ شیطان مردود کاقول نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(81,26,'81:26','پھر تم کدھر جارہے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(81,27,'81:27','یہ تو بس عالم والوں کے لیے ایک نصیحت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(81,28,'81:28','اس کے لیے جو تم میں سے سیدھا چلنا چاہے');
INSERT INTO "ur_wahid_uddin" VALUES(81,29,'81:29','اور تم نہیں چاہ سکتے، مگر یہ کہ اللہ رب االعالمین چاہے');
INSERT INTO "ur_wahid_uddin" VALUES(82,1,'82:1','جب آسمان پھٹ جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(82,2,'82:2','اور جب ستارے بکھرجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(82,3,'82:3','اور جب سمندر بہہ پڑیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(82,4,'82:4','اور جب قبریں کھول دی جائیں گی');
INSERT INTO "ur_wahid_uddin" VALUES(82,5,'82:5','ہر شخص جان لے گا کہ اس نے کیا آگے بھیجا اور کیا پیچھے چھوڑا');
INSERT INTO "ur_wahid_uddin" VALUES(82,6,'82:6','اے انسان، تجھ کو کس چیز نے اپنے ربِ کریم کی طرف سے دھوکے میں ڈال رکھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(82,7,'82:7','جس نے تجھ کو پیدا کیا، پھر تیرے اعضاء کو درست کیا،پھر تجھ کو متناسب بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(82,8,'82:8','اُس نے جس صورت میں چاہا، تم کو ترتیب دے دیا');
INSERT INTO "ur_wahid_uddin" VALUES(82,9,'82:9','ہرگز نہیں ،بلکہ تم انصاف کے دن کو جھٹلاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(82,10,'82:10','حالاں کہ تم پرنگہبان مقررہیں');
INSERT INTO "ur_wahid_uddin" VALUES(82,11,'82:11','معزز لکھنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(82,12,'82:12','وہ جانتے ہیں جو کچھ تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(82,13,'82:13','بے شک نیک لوگ عیش میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(82,14,'82:14','اور بے شک گنہ گار دوزخ میں');
INSERT INTO "ur_wahid_uddin" VALUES(82,15,'82:15','انصاف کے دن وہ اس میں ڈالے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(82,16,'82:16','وہ اس سے جدا ہونے والے نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(82,17,'82:17','اور تم کو کیا خبر کہ انصاف کا دن کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(82,18,'82:18','پھرتم کوکیا خبر کہ انصاف کادن کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(82,19,'82:19','اس دن کوئی جان کسی دوسری جان کے لیے کچھ نہ کرسکے گیاور معاملہ اس دن اللہ ہی کے اختیار میں ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(83,1,'83:1','خرابی ہے ناپ تول میں کمی کرنے والوں کی');
INSERT INTO "ur_wahid_uddin" VALUES(83,2,'83:2','جو لوگوں سے ناپ کرلیں تو پورا لیں');
INSERT INTO "ur_wahid_uddin" VALUES(83,3,'83:3','اور جب ان کو ناپ کریا تول کردیں تو گھٹا کردیں');
INSERT INTO "ur_wahid_uddin" VALUES(83,4,'83:4','کیا یہ لوگ نہیں سمجھتے کہ وہ اُٹھائے جانے والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(83,5,'83:5','ایک بڑے دن کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(83,6,'83:6','جس دن تمام لوگ خدا وندِ عالم کے سامنے کھڑے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(83,7,'83:7','ہرگز نہیں ، بے شک گنہ گاروں کا اعمال نامہ سجین میں ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(83,8,'83:8','اور تم کیا جانو کہ سجین کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(83,9,'83:9','وہ ایک لکھا ہوا دفتر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(83,10,'83:10','خرابی ہے اس دن جھٹلانے والوں کی');
INSERT INTO "ur_wahid_uddin" VALUES(83,11,'83:11','جو انصاف کے دن کوجھٹلاتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(83,12,'83:12','اور اس کو وہی شخص جھٹلاتا ہے جو حد سے گزرنے والا ہو،گناہ گار ہو');
INSERT INTO "ur_wahid_uddin" VALUES(83,13,'83:13','جب اس کو ہماری آیتیں سنائی جاتی ہیں تو وہ کہتا ہے کہ یہ اگلوں کی کہانیاں ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(83,14,'83:14','ہرگز نہیں ، بلکہ ان کے دلوں پر ان کے اعمال کا زنگ چڑھ گیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(83,15,'83:15','ہر گز نہیں ، بلکہ اس دن وہ اپنے رب سے اوٹ میں رکھے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(83,16,'83:16','پھروہ دوزخ میں داخل ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(83,17,'83:17','پھر کہا جائے گا کہ یہی وہ چیز ہے جس کو تم جھٹلاتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(83,18,'83:18','ہرگز نہیں ،بے شک نیک لوگوں کا اعمال نامہ علّیّین میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(83,19,'83:19','اور تم کیا جانو علّیّین کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(83,20,'83:20','لکھا ہوا دفتر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(83,21,'83:21','مقرب فرشتوں کی نگرانی میں');
INSERT INTO "ur_wahid_uddin" VALUES(83,22,'83:22','بے شک نیک لوگ آرام میں ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(83,23,'83:23','وہ تختوں پر بیٹھے دیکھتے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(83,24,'83:24','ان کے چہروں میں تم آرام کی تازگی محسوس کروگے');
INSERT INTO "ur_wahid_uddin" VALUES(83,25,'83:25','ان کو شراب خالص، مہرلگی ہوئی پلائی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(83,26,'83:26','جس پر مشک کی مہر ہوگیاور یہ چیز ہے جس کی حرص کرنے والوں کو حرص کرنا چاہيے');
INSERT INTO "ur_wahid_uddin" VALUES(83,27,'83:27','اور اس شراب میں تسنیم کی آمیزش ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(83,28,'83:28','ایک ایسا چشمہ جس سے مقرب لوگ پئیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(83,29,'83:29','بے شک جولوگ مجرم تھے وہ ایمان والوں پرہنستے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(83,30,'83:30','اور جب وہ ان کے سامنے سے گزرتے تو وہ آپس میں آنکھوں سے اشارے کرتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(83,31,'83:31','اور جب وہ اپنے لوگوں میں لوٹتے تو دل لگی کرتے ہوئے لوٹتے');
INSERT INTO "ur_wahid_uddin" VALUES(83,32,'83:32','اور جب وہ ان کو دیکھتے تو کہتے کہ یہ بہکے ہوئے لوگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(83,33,'83:33','حالاں کہ وہ ان پرنگراں بناکر نہیں بھیجے گئے');
INSERT INTO "ur_wahid_uddin" VALUES(83,34,'83:34','پس آج ایمان والے منکروں پرہنستے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(83,35,'83:35','وہ تختوں پر بیٹھے دیکھ رہے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(83,36,'83:36','واقعی منکروں کو ان کے کيے کاخوب بدلہ ملا');
INSERT INTO "ur_wahid_uddin" VALUES(84,1,'84:1','جب آسمان پھٹ جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(84,2,'84:2','اوروہ اپنے رب کا حکم سن لے گا اور وہ اسی لائق ہے');
INSERT INTO "ur_wahid_uddin" VALUES(84,3,'84:3','اورجب زمین پھیلادی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(84,4,'84:4','اور وہ اپنے اندر کی چیزوں کواگل دے گی اور خالی ہوجائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(84,5,'84:5','اور وہ اپنے رب کاحکم سن لے گی اور وہ اسی لائق ہے');
INSERT INTO "ur_wahid_uddin" VALUES(84,6,'84:6','اے انسان، تو کشاں کشاں اپنے رب کی طرف جارہا ہے،پھر اس سے ملنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(84,7,'84:7','توجس کو اس کا اعمال نامہ اس کے داہنے ہاتھ میں دیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(84,8,'84:8','اس سے آسان حساب لیاجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(84,9,'84:9','اور وہ اپنے لوگوں کے پاس خوش خوش آئے گا');
INSERT INTO "ur_wahid_uddin" VALUES(84,10,'84:10','اور جس کا اعمال نامہ اس کی پیٹھ کے پیچھے سے دیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(84,11,'84:11','وہ موت کو پکارے گا');
INSERT INTO "ur_wahid_uddin" VALUES(84,12,'84:12','اور جہنم میں داخل ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(84,13,'84:13','وہ اپنے لوگوں میں بے غم رہتا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(84,14,'84:14','اس نے خیال کیا تھا کہ اس کو لوٹنا نہیں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(84,15,'84:15','کیوں نہیں ، اس کا رب اس کو دیکھ رہا تھا');
INSERT INTO "ur_wahid_uddin" VALUES(84,16,'84:16','پس نہیں ، میں قسم کھاتا ہوں شفق کی');
INSERT INTO "ur_wahid_uddin" VALUES(84,17,'84:17','اور رات کی اور ان چیزوں کی جن کو وہ سمیٹ لیتی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(84,18,'84:18','اور چاند کی جب وہ پورا ہوجائے');
INSERT INTO "ur_wahid_uddin" VALUES(84,19,'84:19','کہ تم کو ضرور ایک حالت کے بعد دوسری حالت پر پہنچنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(84,20,'84:20','تو انھیں کیا ہوگیا کہ وہ ایمان نہیں لاتے');
INSERT INTO "ur_wahid_uddin" VALUES(84,21,'84:21','اور جب ان کے سامنے قرآن پڑھا جاتا ہے تو وہ خدا کی طرف نہیں جھکتے');
INSERT INTO "ur_wahid_uddin" VALUES(84,22,'84:22','بلکہ منکرین جھٹلا رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(84,23,'84:23','اور اللہ جانتا ہے جو کچھ وہ جمع کر رہے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(84,24,'84:24','پس ان کو ایک درد ناک عذاب کی خوش خبری دے دو');
INSERT INTO "ur_wahid_uddin" VALUES(84,25,'84:25','لیکن جو لوگ ایمان لائے اور انھوں نے اچھے عمل کيے، ان کے ليے کبھی نہ ختم ہونے والا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,1,'85:1','قسم ہے برجوں والے آسمان کی');
INSERT INTO "ur_wahid_uddin" VALUES(85,2,'85:2','اور وعدہ کيے ہوئے دن کی');
INSERT INTO "ur_wahid_uddin" VALUES(85,3,'85:3','اور دیکھنے والے کی اور دیکھی ہوئی کی');
INSERT INTO "ur_wahid_uddin" VALUES(85,4,'85:4','ہلاک ہوئے خندق والے');
INSERT INTO "ur_wahid_uddin" VALUES(85,5,'85:5','جس میں بھڑکتے ہوئے ایندھن کی آگ تھی');
INSERT INTO "ur_wahid_uddin" VALUES(85,6,'85:6','جب کہ وہ اس پربیٹھے ہوئے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(85,7,'85:7','اورجو کچھ وہ ایمان والوں کے ساتھ کررہے تھے، وہ اس کو دیکھ رہے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(85,8,'85:8','اور ان سے ان کی دشمنی اس کے سوا کسی وجہ سے نہ تھی کہ وہ ایمان لائے اللہ پر جو زبردست ہے، تعریف والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,9,'85:9','اسی کی بادشاہی آسمانوں اور زمین میں ہے،اور اللہ ہر چیز کو دیکھ رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,10,'85:10','جن لوگوں نے مومن مردوں اورمومن عورتوں کو ستایا، پھر توبہ نہ کی تو ان کے لیے جہنم کاعذاب ہےاور ان کے لیے جلنے کاعذاب ہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,11,'85:11','بے شک جولوگ ایمان لائے اور انھوں نے نیک عمل کیا ان کے لیے باغ ہیں جن کے نیچے نہریں جاری ہوں گی، یہ بڑی کامیابی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,12,'85:12','بے شک تیرے رب کی پکڑ بڑی سخت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,13,'85:13','وہی آغاز کرتا ہے اوروہی لوٹائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(85,14,'85:14','اوروہ بخشنے والا ہے، محبت کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,15,'85:15','عرشِ بریں کا مالک');
INSERT INTO "ur_wahid_uddin" VALUES(85,16,'85:16','کرڈالنے والا جوچاہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,17,'85:17','کیا تم کو لشکروں کی خبر پہنچی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,18,'85:18','فرعون اورثمود کی');
INSERT INTO "ur_wahid_uddin" VALUES(85,19,'85:19','بلکہ یہ منکر جھٹلانے پرلگے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(85,20,'85:20','اوراللہ ان کو ہر طرف سے گھیرے ہوئے ہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,21,'85:21','بلکہ وہ ایک باعظمت قرآن ہے');
INSERT INTO "ur_wahid_uddin" VALUES(85,22,'85:22','لوح محفوظ میں لکھا ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(86,1,'86:1','قسم ہے آسمان کی اور رات کو نمودار ہونے والے کی');
INSERT INTO "ur_wahid_uddin" VALUES(86,2,'86:2','اور تم کیاجانو کہ وہ رات کو نمودار ہونے والا کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(86,3,'86:3','چمکتا ہوا تارہ');
INSERT INTO "ur_wahid_uddin" VALUES(86,4,'86:4','کوئی جان ایسی نہیں ہے جس کے اوپر نگہبان نہ ہو');
INSERT INTO "ur_wahid_uddin" VALUES(86,5,'86:5','توانسان کودیکھنا چاہيے کہ وہ کس چیز سے پیدا کیاگیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(86,6,'86:6','وہ ایک اچھلتے ہوئے پانی سے پیدا کیاگیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(86,7,'86:7','جونکلتا ہے پیٹھ اورسینے کے درمیان سے');
INSERT INTO "ur_wahid_uddin" VALUES(86,8,'86:8','بے شک وہ اس کے دوبارہ پیداکرنے پر قادر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(86,9,'86:9','جس دن چھپی ہوئی باتیں پرکھی جائیں گی');
INSERT INTO "ur_wahid_uddin" VALUES(86,10,'86:10','اس وقت انسان کے پاس کوئی زور نہ ہوگا او رنہ کوئی مددگار');
INSERT INTO "ur_wahid_uddin" VALUES(86,11,'86:11','قسم ہے آسمان چکرمارنے والے کی');
INSERT INTO "ur_wahid_uddin" VALUES(86,12,'86:12','اور پھوٹ نکلنے والی زمین کی');
INSERT INTO "ur_wahid_uddin" VALUES(86,13,'86:13','بے شک یہ دوٹوک بات ہے');
INSERT INTO "ur_wahid_uddin" VALUES(86,14,'86:14','اور وہ ہنسی کی بات نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(86,15,'86:15','وہ تدبیر کرنے میں لگے ہوئے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(86,16,'86:16','اور میں بھی تدبیر کرنے میں لگا ہوا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(86,17,'86:17','پس منکروں کو ڈھیل دے،ان کو ڈھیل دے تھوڑے دنوں');
INSERT INTO "ur_wahid_uddin" VALUES(87,1,'87:1','اپنے رب کے نام کی پاکی بیان کر جو سب سے اوپر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(87,2,'87:2','جس نے بنایا پھر ٹھیک کیا');
INSERT INTO "ur_wahid_uddin" VALUES(87,3,'87:3','اور جس نے ٹھہرایا، پھر راہ بتائی');
INSERT INTO "ur_wahid_uddin" VALUES(87,4,'87:4','اور جس نے چارہ نکالا');
INSERT INTO "ur_wahid_uddin" VALUES(87,5,'87:5','پھر اس کوسیاہ کوڑا بنادیا');
INSERT INTO "ur_wahid_uddin" VALUES(87,6,'87:6','ہم تم کو پڑھائیں گے پھرتم نہیں بھولوگے');
INSERT INTO "ur_wahid_uddin" VALUES(87,7,'87:7','مگر جواللہ چاہے،وہ جانتا ہے کھلے کو بھی اور اس کو بھی جوچھپا ہوا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(87,8,'87:8','اور ہم تم کو لے چلیں گے آسان راہ');
INSERT INTO "ur_wahid_uddin" VALUES(87,9,'87:9','پس نصیحت کرو اگر نصیحت فائدہ پہنچائے');
INSERT INTO "ur_wahid_uddin" VALUES(87,10,'87:10','وہ شخص نصیحت قبول کرے گا جو ڈرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(87,11,'87:11','اور اس سے گریز کرے گا، وہ جو بدبخت ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(87,12,'87:12','وہ پڑے گا بڑی آگ میں');
INSERT INTO "ur_wahid_uddin" VALUES(87,13,'87:13','پھر نہ وہ اس میں مرے گا اور نہ جئے گا');
INSERT INTO "ur_wahid_uddin" VALUES(87,14,'87:14','کامیاب ہوا جس نے اپنے کو پاک کیا');
INSERT INTO "ur_wahid_uddin" VALUES(87,15,'87:15','اور اپنے رب کانام لیا،پھر نماز پڑھی');
INSERT INTO "ur_wahid_uddin" VALUES(87,16,'87:16','بلکہ تم دنیوی زندگی کو مقدم رکھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(87,17,'87:17','اور آخرت بہتر ہے اور پائيدار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(87,18,'87:18','یہی اگلے صحیفوں میں بھی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(87,19,'87:19','موسیٰ اورابراہیم کے صحیفوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(88,1,'88:1','کیاتم کو اس چھاجانے والی کی خبر پہنچی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(88,2,'88:2','کچھ چہرے اس دن ذلیل ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(88,3,'88:3','محنت کرنے والے تھکے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(88,4,'88:4','وہ دہکتی ہوئی آگ میں پڑیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(88,5,'88:5','کھولتے ہوئے چشمے سے پانی پلائے جائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(88,6,'88:6','ان کے لیے کانٹوں والے جھاڑ کے سوا اور کوئی کھانا نہ ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(88,7,'88:7','جو نہ موٹا کرے اور نہ بھوک مٹائے');
INSERT INTO "ur_wahid_uddin" VALUES(88,8,'88:8','کچھ چہرے اس دن بارونق ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(88,9,'88:9','اپنی کمائی پرخوش ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(88,10,'88:10','اونچے باغ میں');
INSERT INTO "ur_wahid_uddin" VALUES(88,11,'88:11','وہ اس میں کوئی لغوبات نہیں سنیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(88,12,'88:12','اس میں بہتے ہوئے چشمے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(88,13,'88:13','اس میں تخت ہوں گے اونچے بچھے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(88,14,'88:14','اور آب خورے، سامنے چنے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(88,15,'88:15','اور برابر بچھے ہوئے گدے');
INSERT INTO "ur_wahid_uddin" VALUES(88,16,'88:16','اور قالین ہرطرف بچھے ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(88,17,'88:17','تو کیا وہ اونٹ کو نہیں دیکھتے کہ وہ کیسے پیدا کیا گیا');
INSERT INTO "ur_wahid_uddin" VALUES(88,18,'88:18','اور آسمان کو کہ وہ کس طرح بلند کیاگیا');
INSERT INTO "ur_wahid_uddin" VALUES(88,19,'88:19','اور پہاڑوں کو کہ وہ کس طرح کھڑا کیاگیا');
INSERT INTO "ur_wahid_uddin" VALUES(88,20,'88:20','اورزمین کو کہ وہ کس طرح بچھائی گئی');
INSERT INTO "ur_wahid_uddin" VALUES(88,21,'88:21','پس تم یاد دہانی کردو، تم بس یاد دہانی کرنے والے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(88,22,'88:22','تم ان پر داروغہ نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(88,23,'88:23','مگر جس نے روگردانی کی اور انکار کیا');
INSERT INTO "ur_wahid_uddin" VALUES(88,24,'88:24','تو اللہ اس کو بڑا عذاب دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(88,25,'88:25','ہماری ہی طرف ان کی واپسی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(88,26,'88:26','پھر ہمارے ذمہ ہے ان سے حساب لینا');
INSERT INTO "ur_wahid_uddin" VALUES(89,1,'89:1','قسم ہے فجر کی');
INSERT INTO "ur_wahid_uddin" VALUES(89,2,'89:2','اور دس راتوں کی');
INSERT INTO "ur_wahid_uddin" VALUES(89,3,'89:3','اور جفت اور طاق کی');
INSERT INTO "ur_wahid_uddin" VALUES(89,4,'89:4','اور رات کی جب وہ چلنے لگے');
INSERT INTO "ur_wahid_uddin" VALUES(89,5,'89:5','کیوں ، اس میں توعقل مند کے لیے کافی قسم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(89,6,'89:6','تم نے نہیں دیکھا، تمھارے رب نے عاد کے ساتھ کیا معاملہ کیا');
INSERT INTO "ur_wahid_uddin" VALUES(89,7,'89:7','ستونوں والے ارم کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(89,8,'89:8','جن کے برابر کوئی قوم ملکوں میں پیدا نہیں کی گئی');
INSERT INTO "ur_wahid_uddin" VALUES(89,9,'89:9','اورثمود کے ساتھ جنھوں نے وادی میں چٹانیں تراشیں');
INSERT INTO "ur_wahid_uddin" VALUES(89,10,'89:10','اور میخوں والے فرعون کے ساتھ');
INSERT INTO "ur_wahid_uddin" VALUES(89,11,'89:11','جنھوں نے ملکوں میں سرکشی کی');
INSERT INTO "ur_wahid_uddin" VALUES(89,12,'89:12','پھر ان میں بہت فساد پھیلایا');
INSERT INTO "ur_wahid_uddin" VALUES(89,13,'89:13','تو تمھارے رب نے ان پرعذاب کا کوڑا برسایا');
INSERT INTO "ur_wahid_uddin" VALUES(89,14,'89:14','بے شک تمھارا رب گھات میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(89,15,'89:15','پس انسان کا حال یہ ہے کہ جب اس کا رب اس کو آزماتا ہے اور اس کو عزت اور نعمت دیتا ہے تو وہ کہتا ہے کہ میرے رب نے مجھ کو عزت دی');
INSERT INTO "ur_wahid_uddin" VALUES(89,16,'89:16','اور جب وہ اس کو آزماتا ہے اور اس کارزق اس پرتنگ کردیتا ہے تو وہ کہتا ہے کہ میرے رب نے مجھ کوذلیل کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(89,17,'89:17','ہرگز نہیں ، بلکہ تم یتیم کی عزت نہیں کرتے');
INSERT INTO "ur_wahid_uddin" VALUES(89,18,'89:18','اور تم مسکین کو کھانا کھلانے پر ایک دوسرے کو نہیں ابھارتے');
INSERT INTO "ur_wahid_uddin" VALUES(89,19,'89:19','اور تم وراثت کو سمیٹ کر کھا جاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(89,20,'89:20','اور تم مال سے بہت زیادہ محبت رکھتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(89,21,'89:21','ہرگز نہیں ، جب زمین کوتوڑ کر ریزہ ریزہ کردیا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(89,22,'89:22','اور تمھارا رب آئے گا اورفرشتے آئیں گے قطار در قطار');
INSERT INTO "ur_wahid_uddin" VALUES(89,23,'89:23','اور اس دن جہنم لائی جائے گی،اس دن انسان کوسمجھ آئے گی، اور اب سمجھ آنے کا موقع کہاں');
INSERT INTO "ur_wahid_uddin" VALUES(89,24,'89:24','وہ کہے گا، کاش میں اپنی زندگی میں کچھ آگے بھیجتا');
INSERT INTO "ur_wahid_uddin" VALUES(89,25,'89:25','پس اس دن نہ تو خدا کے برابر کوئی عذاب دے گا');
INSERT INTO "ur_wahid_uddin" VALUES(89,26,'89:26','اور نہ اس کے باندھنے کے برابر کوئی باندھے گا');
INSERT INTO "ur_wahid_uddin" VALUES(89,27,'89:27','اے نفسِ مطمئن');
INSERT INTO "ur_wahid_uddin" VALUES(89,28,'89:28','چل اپنے رب کی طرف، تو اس سے راضی ، وہ تجھ سے راضی');
INSERT INTO "ur_wahid_uddin" VALUES(89,29,'89:29','پھر شامل ہو میرے بندوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(89,30,'89:30','اور داخل ہومیری جنت میں');
INSERT INTO "ur_wahid_uddin" VALUES(90,1,'90:1','نہیں ، میں قسم کھاتا ہوں اس شہر کی');
INSERT INTO "ur_wahid_uddin" VALUES(90,2,'90:2','اور تم اس میں مقیم ہو');
INSERT INTO "ur_wahid_uddin" VALUES(90,3,'90:3','اور قسم ہے باپ کی اور اس کی اولاد کی');
INSERT INTO "ur_wahid_uddin" VALUES(90,4,'90:4','ہم نے انسان کو مشقت میں پیدا کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(90,5,'90:5','کیا وہ خیال کرتا ہے کہ اس پرکسی کازور نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(90,6,'90:6','کہتا ہے کہ میں نے بہت سامال خرچ کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(90,7,'90:7','کیا وہ سمجھتا ہے کہ کسی نے اس کو نہیں دیکھا');
INSERT INTO "ur_wahid_uddin" VALUES(90,8,'90:8','کیا ہم نے اس کو دو آنکھیں نہیں دیں');
INSERT INTO "ur_wahid_uddin" VALUES(90,9,'90:9','اور ایک زبان اور دو ہونٹ');
INSERT INTO "ur_wahid_uddin" VALUES(90,10,'90:10','اور ہم نے اس کو دونوں راستے بتادئے');
INSERT INTO "ur_wahid_uddin" VALUES(90,11,'90:11','پھر وہ گھاٹی پر نہیں چڑھا');
INSERT INTO "ur_wahid_uddin" VALUES(90,12,'90:12','اور تم کیا جانو کہ کیا ہے وہ گھاٹی');
INSERT INTO "ur_wahid_uddin" VALUES(90,13,'90:13','گردن کو چھڑانا');
INSERT INTO "ur_wahid_uddin" VALUES(90,14,'90:14','یا بھوک کے زمانے میں کھلانا');
INSERT INTO "ur_wahid_uddin" VALUES(90,15,'90:15','قرابت دار یتیم کو');
INSERT INTO "ur_wahid_uddin" VALUES(90,16,'90:16','یا خاک نشیں محتاج کو');
INSERT INTO "ur_wahid_uddin" VALUES(90,17,'90:17','پھر وہ ان لوگوں میں سے ہو جو ایمان لائے اور ایک دوسرے کو صبر کی اور ہمدردی کی نصیحت کی');
INSERT INTO "ur_wahid_uddin" VALUES(90,18,'90:18','یہی لوگ نصیب والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(90,19,'90:19','اور جو ہماری آیتوں کے منکر ہوئے، وہ بدبختی والے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(90,20,'90:20','ان پر آگ چھائی ہوئی ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(91,1,'91:1','قسم ہے سورج کی اور اس کی دھوپ چڑھنے کی');
INSERT INTO "ur_wahid_uddin" VALUES(91,2,'91:2','اور چاند کی جب کہ وہ سورج کے پیچھے آئے');
INSERT INTO "ur_wahid_uddin" VALUES(91,3,'91:3','اور دن کی جب کہ وہ اس کو روشن کردے');
INSERT INTO "ur_wahid_uddin" VALUES(91,4,'91:4','اور رات کی جب وہ اس کو چھپائے');
INSERT INTO "ur_wahid_uddin" VALUES(91,5,'91:5','اور آسمان کی اور جیسا کہ اس کو بنایا');
INSERT INTO "ur_wahid_uddin" VALUES(91,6,'91:6','اور زمین کی اور جیسا کہ اس کو پھیلایا');
INSERT INTO "ur_wahid_uddin" VALUES(91,7,'91:7','اور جان کی جیسا کہ اس کو ٹھیک کیا');
INSERT INTO "ur_wahid_uddin" VALUES(91,8,'91:8','پھر اس کو سمجھ دی،اس کی بدی کی اور اس کی نیکی کی');
INSERT INTO "ur_wahid_uddin" VALUES(91,9,'91:9','کامیاب ہوا جس نے اس کو پاک کیا');
INSERT INTO "ur_wahid_uddin" VALUES(91,10,'91:10','اور نامراد ہو ا جس نے اس کو آلودہ کیا');
INSERT INTO "ur_wahid_uddin" VALUES(91,11,'91:11','ثمود نے اپنی سرکشی کی بنا پر جھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(91,12,'91:12','جب کہ اٹھ کھڑا ہوا ان کا سب سے بڑا بدبخت');
INSERT INTO "ur_wahid_uddin" VALUES(91,13,'91:13','تو اللہ کے رسول نے ان سے کہا کہ اللہ کی اونٹنی اور اس کے پانی پینے سے خبردار');
INSERT INTO "ur_wahid_uddin" VALUES(91,14,'91:14','تو انھوں نے اس کو جھٹلایا، پھر اونٹنی کو مار ڈالا،پھر ان کے رب نے ان پر ہلاکت نازل کی ان كے گناه كے سبب، پھر سب کو برابر کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(91,15,'91:15','وہ نہیں ڈرتا کہ اس کے پیچھے کیا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(92,1,'92:1','قسم ہے رات کی جب کہ وہ چھاجائے');
INSERT INTO "ur_wahid_uddin" VALUES(92,2,'92:2','اور دن کی جب کہ وہ روشن ہو');
INSERT INTO "ur_wahid_uddin" VALUES(92,3,'92:3','اوراس کی جو اس نے پیدا کيے نر اور مادہ');
INSERT INTO "ur_wahid_uddin" VALUES(92,4,'92:4','کہ تمھاری کوششیں الگ الگ ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(92,5,'92:5','پس جس نے دیا اور وہ ڈرا');
INSERT INTO "ur_wahid_uddin" VALUES(92,6,'92:6','اور اس نے بھلائی کو سچ مانا');
INSERT INTO "ur_wahid_uddin" VALUES(92,7,'92:7','تو اس کو ہم آسان راستہ کے لیے سہولت دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(92,8,'92:8','اور جس نے بخل کیا اور بے پروارہا');
INSERT INTO "ur_wahid_uddin" VALUES(92,9,'92:9','اور بھلائی کو جھٹلایا');
INSERT INTO "ur_wahid_uddin" VALUES(92,10,'92:10','تو ہم اس کو سخت راستہ کے لیے سہولت دیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(92,11,'92:11','اور اس کا مال اس کے کام نہ آئے گا جب وہ گڑھے میں گرے گا');
INSERT INTO "ur_wahid_uddin" VALUES(92,12,'92:12','بے شک ہمارے ذمہ ہے راہ بتانا');
INSERT INTO "ur_wahid_uddin" VALUES(92,13,'92:13','اور بے شک ہمارے اختیار میں ہے آخرت اور دنیا');
INSERT INTO "ur_wahid_uddin" VALUES(92,14,'92:14','پس میں نے تم کو ڈرا دیا بھڑکتی ہوئی آگ سے');
INSERT INTO "ur_wahid_uddin" VALUES(92,15,'92:15','اس میں وہی پڑے گا جو بڑا بدبخت ہے');
INSERT INTO "ur_wahid_uddin" VALUES(92,16,'92:16','جس نے جھٹلایا اور روگردانی کی');
INSERT INTO "ur_wahid_uddin" VALUES(92,17,'92:17','اور ہم اس سے بچادیں گے زیادہ ڈرنے والے کو');
INSERT INTO "ur_wahid_uddin" VALUES(92,18,'92:18','جو اپنا مال دیتا ہے پاکی حاصل کرنے کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(92,19,'92:19','اور اس پر کسی کا احسان نہیں جس کا بدلہ اسے دینا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(92,20,'92:20','مگر صرف اپنے خدائے برترکی خوشنودی کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(92,21,'92:21','اور عنقریب وہ خوش ہوجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(93,1,'93:1','قسم ہے روزِ روشن کی');
INSERT INTO "ur_wahid_uddin" VALUES(93,2,'93:2','اور رات کی جب وہ چھاجائے');
INSERT INTO "ur_wahid_uddin" VALUES(93,3,'93:3','تمھارے رب نے تم کو نہیں چھوڑا،اور نہ وہ تم سے بیزار ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(93,4,'93:4','اور یقیناً آخرت تمھارے لیے دنیا سے بہترہے');
INSERT INTO "ur_wahid_uddin" VALUES(93,5,'93:5','اور عنقریب اللہ تجھ کو دے گا،پھر تو راضی ہوجائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(93,6,'93:6','کیا اللہ نے تم کو یتیم نہیں پایا، پھر اُس نے تم کو ٹھکانا دیا');
INSERT INTO "ur_wahid_uddin" VALUES(93,7,'93:7','اور تم کو متلاشی پایا تو تم کو راہ دکھائی');
INSERT INTO "ur_wahid_uddin" VALUES(93,8,'93:8','اور تم کو نادار پایا تو تم کو غنی کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(93,9,'93:9','پس تم یتیم پر سختی نہ کرو');
INSERT INTO "ur_wahid_uddin" VALUES(93,10,'93:10','اور تم سائل کو نہ جھڑکو');
INSERT INTO "ur_wahid_uddin" VALUES(93,11,'93:11','اور تم اپنے رب کی نعمت بیان کرو');
INSERT INTO "ur_wahid_uddin" VALUES(94,1,'94:1','کیا ہم نے تمھارا سینہ تمھارے لیے کھول نہیں دیا');
INSERT INTO "ur_wahid_uddin" VALUES(94,2,'94:2','اور تمھارا وہ بوجھ اُتار دیا');
INSERT INTO "ur_wahid_uddin" VALUES(94,3,'94:3','جس نے تمھاری پیٹھ جھکادی تھی');
INSERT INTO "ur_wahid_uddin" VALUES(94,4,'94:4','اور ہم نے تمھارا ذكر بلند کیا');
INSERT INTO "ur_wahid_uddin" VALUES(94,5,'94:5','پس مشکل کے ساتھ آسانی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(94,6,'94:6','بے شک مشکل کے ساتھ آسانی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(94,7,'94:7','پھر جب تم فارغ ہوجاؤ تو محنت کرو');
INSERT INTO "ur_wahid_uddin" VALUES(94,8,'94:8','اور اپنے رب کی طرف توجہ رکھو');
INSERT INTO "ur_wahid_uddin" VALUES(95,1,'95:1','قسم ہے تین کی اور زیتون کی');
INSERT INTO "ur_wahid_uddin" VALUES(95,2,'95:2','اور طورِسینا کی');
INSERT INTO "ur_wahid_uddin" VALUES(95,3,'95:3','اور اس امن والے شہر کی');
INSERT INTO "ur_wahid_uddin" VALUES(95,4,'95:4','ہم نے انسان کو بہترین ساخت پر پیدا کیا');
INSERT INTO "ur_wahid_uddin" VALUES(95,5,'95:5','پھر اس کو سب سے نیچے پھینک دیا');
INSERT INTO "ur_wahid_uddin" VALUES(95,6,'95:6','لیکن جولوگ ایمان لائے اور اچھے کام کيے تو ان کے لیے کبھی ختم نہ ہونے والا اجر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(95,7,'95:7','تو اب کیا ہے جس سے تم بدلہ ملنے کو جھٹلاتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(95,8,'95:8','کیا اللہ سب حاکموں سے بڑا حاکم نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(96,1,'96:1','پڑھ اپنے رب کے نام سے جس نے پیدا کیا');
INSERT INTO "ur_wahid_uddin" VALUES(96,2,'96:2','پیدا کیا انسان کو علق سے');
INSERT INTO "ur_wahid_uddin" VALUES(96,3,'96:3','پڑھ اور تیرا رب بڑا کریم ہے');
INSERT INTO "ur_wahid_uddin" VALUES(96,4,'96:4','جس نے علم سکھایا قلم سے');
INSERT INTO "ur_wahid_uddin" VALUES(96,5,'96:5','انسان کو وہ کچھ سکھایا جووہ جانتا نہ تھا');
INSERT INTO "ur_wahid_uddin" VALUES(96,6,'96:6','ہرگز نہیں ،انسان سرکشی کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(96,7,'96:7','اس بنا پر کہ وہ اپنے کو بے نیاز دیکھتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(96,8,'96:8','بے شک تیرے رب ہی کی طرف لوٹنا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(96,9,'96:9','کیا تم نے دیکھا اس شخص کو جو منع کرتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(96,10,'96:10','ایک بندہ کو جب وہ نماز پڑھتا ہو');
INSERT INTO "ur_wahid_uddin" VALUES(96,11,'96:11','تمھارا کیا خیال ہے، اگر وہ ہدایت پر ہو');
INSERT INTO "ur_wahid_uddin" VALUES(96,12,'96:12','یا ڈر کی بات سکھاتاہو');
INSERT INTO "ur_wahid_uddin" VALUES(96,13,'96:13','تمھارا کیا خیال ہے،اگر اس نے جھٹلایا اور روگردانی کی');
INSERT INTO "ur_wahid_uddin" VALUES(96,14,'96:14','کیا اس نے نہیں جانا کہ اللہ دیکھ رہا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(96,15,'96:15','ہرگز نہیں ، اگر وہ باز نہ آیا تو ہم پیشانی کے بال پکڑکر اس کوکھینچیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(96,16,'96:16','اس پیشانی کو جو جھوٹی، گناہ گار ہے');
INSERT INTO "ur_wahid_uddin" VALUES(96,17,'96:17','اب وہ بلالے اپنے حامیوں کو');
INSERT INTO "ur_wahid_uddin" VALUES(96,18,'96:18','ہم بھی دوزخ کے فرشتوں کو بلائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(96,19,'96:19','ہرگز نہیں ، اس کی بات نہ مان اور سجدہ کر اور قریب ہوجا');
INSERT INTO "ur_wahid_uddin" VALUES(97,1,'97:1','ہم نے اس کو اُتارا ہے شب قدر میں');
INSERT INTO "ur_wahid_uddin" VALUES(97,2,'97:2','اور تم کیاجانو کہ شب قدر کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(97,3,'97:3','شب قدر ہزار مہینوں سے بہتر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(97,4,'97:4','فرشتے اور روح اس میں اپنے رب کی اجازت سے اُترتے ہیں ،ہر حکم لے کر');
INSERT INTO "ur_wahid_uddin" VALUES(97,5,'97:5','وہ رات سراسر سلامتی ہے،صبح نکلنے تک');
INSERT INTO "ur_wahid_uddin" VALUES(98,1,'98:1','اہل کتاب اور مشرکین میں سے جن لوگوں نے انکار کیا، وہ باز آنے والے نہیں جب تک ان کے پاس واضح دلیل نہ آجائے');
INSERT INTO "ur_wahid_uddin" VALUES(98,2,'98:2','اللہ کی طرف سے ایک رسول جو پاک صحیفے پڑھ کر سنائے');
INSERT INTO "ur_wahid_uddin" VALUES(98,3,'98:3','جن میں درست مضامین لکھے ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(98,4,'98:4','اور جو لوگ اہل کتاب تھے وہ واضح دلیل آجانے کے بعد ہی مختلف ہوگئے');
INSERT INTO "ur_wahid_uddin" VALUES(98,5,'98:5','حالاں کہ ان کو یہی حکم دیا گیا تھا کہ وہ اللہ کی عبادت کریں اس کے لیے دین کو خالص کردیں ،یکسو ہوکر اور نماز قائم کریں اور زکوٰۃ دیں ، اور یہی درست دین ہے');
INSERT INTO "ur_wahid_uddin" VALUES(98,6,'98:6','بے شک اہل کتاب اور مشرکین میں سے جن لوگوں نے کفر کیا وہ جہنم کی آگ میں پڑیں گے، وہ ہمیشہ اس میں رہیں گے، یہ لوگ بدترین خلائق ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(98,7,'98:7','جو لوگ ایمان لائے اور انھوں نے اچھے کام کيے ،وہ لوگ بہترین ِ خلائق ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(98,8,'98:8','ان کا بدلہ ان کے رب کے پاس ہمیشہ رہنے والے باغ ہیں جن کے نیچے نہریں جاری ہوں گی،ان میں وہ ہمیشہ ہمیشہ رہیں گےاللہ ان سے راضی اور وہ اللہ سے راضی،یہ اس شخص کے لیے ہے جو اپنے رب سے ڈرے');
INSERT INTO "ur_wahid_uddin" VALUES(99,1,'99:1','جب زمین شدت سے ہلادی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(99,2,'99:2','اور زمین اپنا بوجھ نکال کر باہر ڈال دے گی');
INSERT INTO "ur_wahid_uddin" VALUES(99,3,'99:3','اور انسان کہے گا کہ اس کو کیا ہوا');
INSERT INTO "ur_wahid_uddin" VALUES(99,4,'99:4','اس دن وہ اپنے حالات بیان کرے گی');
INSERT INTO "ur_wahid_uddin" VALUES(99,5,'99:5','کیوں کہ تمھارے رب کا اس کو یہی حکم ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(99,6,'99:6','اس دن لوگ الگ الگ نکلیں گے، تاکہ ان کے اعمال انھیں دکھائے جائیں');
INSERT INTO "ur_wahid_uddin" VALUES(99,7,'99:7','پس جس شخص نے ذرہ برابر نیکی کی ہوگی، وہ اس کودیکھ لے گا');
INSERT INTO "ur_wahid_uddin" VALUES(99,8,'99:8','اور جس شخص نے ذرہ برابر بدی کی ہوگی، وہ اس کو دیکھ لے گا');
INSERT INTO "ur_wahid_uddin" VALUES(100,1,'100:1','قسم ہے ان گھوڑوں کی جو ہانپتے ہوئے دوڑتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(100,2,'100:2','پھر ٹاپ مارکر چنگاری نکالنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(100,3,'100:3','پھر صبح کے وقت چھاپہ مارنے والے');
INSERT INTO "ur_wahid_uddin" VALUES(100,4,'100:4','پھر اس میں غبار اُڑانے والے');
INSERT INTO "ur_wahid_uddin" VALUES(100,5,'100:5','پھر اس وقت فوج میں گھس جانے والے');
INSERT INTO "ur_wahid_uddin" VALUES(100,6,'100:6','بے شک انسان اپنے رب کا ناشکر ہے');
INSERT INTO "ur_wahid_uddin" VALUES(100,7,'100:7','اور وہ خود اس پر گواہ ہے');
INSERT INTO "ur_wahid_uddin" VALUES(100,8,'100:8','اور وہ مال کی محبت میں بہت شدید ہے');
INSERT INTO "ur_wahid_uddin" VALUES(100,9,'100:9','کیا وہ اس وقت کو نہیں جانتا جب وہ قبروں سے نکالا جائے گا');
INSERT INTO "ur_wahid_uddin" VALUES(100,10,'100:10','اور نکالا جائے گا جو کچھ دلوں میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(100,11,'100:11','بے شک اس دن ان کا رب ان سے خوب باخبر ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(101,1,'101:1','کھڑکھڑانے والی');
INSERT INTO "ur_wahid_uddin" VALUES(101,2,'101:2','کیا ہے کھڑکھڑا نے والی');
INSERT INTO "ur_wahid_uddin" VALUES(101,3,'101:3','اور تم کیا جانو کہ کیا ہے وہ کھڑ کھڑانے والی');
INSERT INTO "ur_wahid_uddin" VALUES(101,4,'101:4','جس دن لوگ پتنگوں کی طرح بکھرے ہوئے ہوں گے');
INSERT INTO "ur_wahid_uddin" VALUES(101,5,'101:5','اور پہاڑ دھنکے ہوئے رنگین اون کی طرح ہوجائیں گے');
INSERT INTO "ur_wahid_uddin" VALUES(101,6,'101:6','پھر جس شخص کا پلہ بھاری ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(101,7,'101:7','وہ دل پسند آرام میں ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(101,8,'101:8','اور جس شخص کا پلہ ہلکا ہوگا');
INSERT INTO "ur_wahid_uddin" VALUES(101,9,'101:9','تو اس کا ٹھکانا گڑھا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(101,10,'101:10','اور تم کیا جانو کہ وہ کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(101,11,'101:11','بھڑکتی ہوئی آگ');
INSERT INTO "ur_wahid_uddin" VALUES(102,1,'102:1','بہتات کی حرص نے تم کو غفلت میں رکھا');
INSERT INTO "ur_wahid_uddin" VALUES(102,2,'102:2','یہاں تک کہ تم قبروں میں جاپہنچے');
INSERT INTO "ur_wahid_uddin" VALUES(102,3,'102:3','ہرگز نہیں ، تم بہت جلد جان لوگے');
INSERT INTO "ur_wahid_uddin" VALUES(102,4,'102:4','پھر ہر گز نہیں ، تم بہت جلد جان لوگے');
INSERT INTO "ur_wahid_uddin" VALUES(102,5,'102:5','ہرگز نہیں ، اگر تم یقین کے ساتھ جانتے');
INSERT INTO "ur_wahid_uddin" VALUES(102,6,'102:6','کہ تم ضرور دوزخ کودیکھو گے');
INSERT INTO "ur_wahid_uddin" VALUES(102,7,'102:7','پھر تم اس کویقین کی آنکھ سے دیکھو گے');
INSERT INTO "ur_wahid_uddin" VALUES(102,8,'102:8','پھر اس دن یقینا تم سے نعمتوں کے بارے میں ضرور پوچھ ہوگی');
INSERT INTO "ur_wahid_uddin" VALUES(103,1,'103:1','قسم ہے زمانے کی');
INSERT INTO "ur_wahid_uddin" VALUES(103,2,'103:2','بے شک انسان گھاٹے میں ہے');
INSERT INTO "ur_wahid_uddin" VALUES(103,3,'103:3','مگر جولوگ کہ ایمان لائے اور نیک عمل کیا اور انھوں نے ایک دوسرے کو حق کی نصیحت کی اور انھوں نے ایک دوسرے کو صبر کی نصیحت کی');
INSERT INTO "ur_wahid_uddin" VALUES(104,1,'104:1','تباہی ہے ہر طعنہ دینے والے ،عیب نکالنے والے كے ليے');
INSERT INTO "ur_wahid_uddin" VALUES(104,2,'104:2','جس نے مال کو سمیٹا اور گن گن کر رکھا');
INSERT INTO "ur_wahid_uddin" VALUES(104,3,'104:3','وہ خیال کرتا ہے کہ اس کامال ہمیشہ اس کے ساتھ رہے گا');
INSERT INTO "ur_wahid_uddin" VALUES(104,4,'104:4','ہرگز نہیں ، وہ پھینکا جائے گا روندنے والی جگہ میں');
INSERT INTO "ur_wahid_uddin" VALUES(104,5,'104:5','اور تم کیاجانو کہ وہ روندنے والی جگہ کیا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(104,6,'104:6','اللہ کی بھڑکائی ہوئی آگ');
INSERT INTO "ur_wahid_uddin" VALUES(104,7,'104:7','جودلوں تک پہنچے گی');
INSERT INTO "ur_wahid_uddin" VALUES(104,8,'104:8','وہ ان پر بند کردی جائے گی');
INSERT INTO "ur_wahid_uddin" VALUES(104,9,'104:9','اونچے اونچے ستونوں میں');
INSERT INTO "ur_wahid_uddin" VALUES(105,1,'105:1','کیا تم نے نہیں دیکھا کہ تمھارے رب نے ہاتھی والوں کے ساتھ کیاکیا');
INSERT INTO "ur_wahid_uddin" VALUES(105,2,'105:2','کیا اس نے ان کی تدبیر کواکارت نہیں کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(105,3,'105:3','اور ان پر چڑیاں بھیجیں جھنڈ کی جھنڈ');
INSERT INTO "ur_wahid_uddin" VALUES(105,4,'105:4','جو ان پر کنکر کی پتھریاں پھینکتے تھے');
INSERT INTO "ur_wahid_uddin" VALUES(105,5,'105:5','پھر اللہ نے اس کو کھائے ہوئے بھس کی طرح کردیا');
INSERT INTO "ur_wahid_uddin" VALUES(106,1,'106:1','اس واسطے کہ قریش مانوس ہوئے');
INSERT INTO "ur_wahid_uddin" VALUES(106,2,'106:2','جاڑے او رگرمی کے سفر سے مانوس');
INSERT INTO "ur_wahid_uddin" VALUES(106,3,'106:3','تو ان کوچاہيے کہ وہ اس گھر کے رب کی عبادت کریں');
INSERT INTO "ur_wahid_uddin" VALUES(106,4,'106:4','جس نے ان کو بھوک میں کھانا دیا اور خوف سے ان کو امن دیا');
INSERT INTO "ur_wahid_uddin" VALUES(107,1,'107:1','کیاتم نے نہیں دیکھا اس شخص کو جوانصاف کے دن کو جھٹلاتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(107,2,'107:2','وہی ہے جو یتیم کو دھکے دیتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(107,3,'107:3','اورمسکین کو کھانا دینے پر نہیں ابھارتا');
INSERT INTO "ur_wahid_uddin" VALUES(107,4,'107:4','پس تباہی ہے ان نماز پڑھنے والوں کے لیے');
INSERT INTO "ur_wahid_uddin" VALUES(107,5,'107:5','جو اپنی نماز سے غافل ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(107,6,'107:6','وہ جو دکھلاوا کرتے ہیں');
INSERT INTO "ur_wahid_uddin" VALUES(107,7,'107:7','اور معمولی ضرورت کی چیزیں بھی نہیں دیتے');
INSERT INTO "ur_wahid_uddin" VALUES(108,1,'108:1','ہم نے تم کو کوثردے دیا');
INSERT INTO "ur_wahid_uddin" VALUES(108,2,'108:2','پس اپنے رب کے لیے نماز پڑھو اور قربانی کرو');
INSERT INTO "ur_wahid_uddin" VALUES(108,3,'108:3','بے شک تمھارا دشمن ہی بے نام ونشان ہے');
INSERT INTO "ur_wahid_uddin" VALUES(109,1,'109:1','کہو کے اے منکرو');
INSERT INTO "ur_wahid_uddin" VALUES(109,2,'109:2','میں ان کی عبادت نہیں کروں گا جن کی عبادت تم کرتے ہو');
INSERT INTO "ur_wahid_uddin" VALUES(109,3,'109:3','اور نہ تم اس کی عبادت کرنے والے ہو جس کی عبادت میں کرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(109,4,'109:4','اور میں ان کی عبادت کرنے والا نہیں جن کی عبادت تم نے کی ہے');
INSERT INTO "ur_wahid_uddin" VALUES(109,5,'109:5','اور نہ تم اس کی عبادت کرنے والے ہو جس کی عبادت میں کرتا ہوں');
INSERT INTO "ur_wahid_uddin" VALUES(109,6,'109:6','تمھارے ليے تمھارا دین، اور میرے لیے میرا دین');
INSERT INTO "ur_wahid_uddin" VALUES(110,1,'110:1','جب اللہ کی مدد آجائے اور فتح');
INSERT INTO "ur_wahid_uddin" VALUES(110,2,'110:2','اور تم دیکھو گے کہ لوگ خدا کے دین میں داخل ہورہے ہیں فوج در فوج');
INSERT INTO "ur_wahid_uddin" VALUES(110,3,'110:3','تو اپنے رب کی تسبیح کرو اس کی حمد کے ساتھ اور اس سے بخشش مانگو،بے شک وہ بہت معاف کرنے والا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(111,1,'111:1','ابولہب کے ہاتھ ٹوٹ جائیں اور وہ برباد ہوجائے');
INSERT INTO "ur_wahid_uddin" VALUES(111,2,'111:2','نہ اس کامال اس کے کام آیا اور نہ وہ جو اس نے کمایا');
INSERT INTO "ur_wahid_uddin" VALUES(111,3,'111:3','وہ عنقریب بھڑکتی ہوئی آگ میں پڑے گا');
INSERT INTO "ur_wahid_uddin" VALUES(111,4,'111:4','اور اس کی بیوی بھی جو ایندھن لیے پھرتی ہے سر پر');
INSERT INTO "ur_wahid_uddin" VALUES(111,5,'111:5','اس کی گردن میں رسی ہے بٹی ہوئی');
INSERT INTO "ur_wahid_uddin" VALUES(112,1,'112:1','کہو، وہ اللہ ایک ہے');
INSERT INTO "ur_wahid_uddin" VALUES(112,2,'112:2','اللہ بے نیاز ہے');
INSERT INTO "ur_wahid_uddin" VALUES(112,3,'112:3','نہ اس کی کوئی اولاد ہے اور نہ وہ کسی کی اولاد');
INSERT INTO "ur_wahid_uddin" VALUES(112,4,'112:4','اور کوئی اس کے برابر کا نہیں');
INSERT INTO "ur_wahid_uddin" VALUES(113,1,'113:1','کہو، میں پناہ مانگتا ہوں صبح کے رب کی');
INSERT INTO "ur_wahid_uddin" VALUES(113,2,'113:2','ہر چیز کے شر سے جو اس نے پیدا کی');
INSERT INTO "ur_wahid_uddin" VALUES(113,3,'113:3','اور تاریکی کے شر سے جب کہ وہ چھاجائے');
INSERT INTO "ur_wahid_uddin" VALUES(113,4,'113:4','اور گرہوں میں پھونک مارنے والوں کے شر سے');
INSERT INTO "ur_wahid_uddin" VALUES(113,5,'113:5','اور حاسد کے شر سے، جب کہ وہ حسد کرے');
INSERT INTO "ur_wahid_uddin" VALUES(114,1,'114:1','کہو، میں پناہ مانگتا ہوں لوگوں کے رب کی');
INSERT INTO "ur_wahid_uddin" VALUES(114,2,'114:2','لوگوں کے بادشاہ کی');
INSERT INTO "ur_wahid_uddin" VALUES(114,3,'114:3','لوگوں کے معبود کی');
INSERT INTO "ur_wahid_uddin" VALUES(114,4,'114:4','اس کے شر سے جو وسوسہ ڈالے اور چھپ جائے');
INSERT INTO "ur_wahid_uddin" VALUES(114,5,'114:5','جو لوگوں کے دلوں میں وسوسہ ڈالتا ہے');
INSERT INTO "ur_wahid_uddin" VALUES(114,6,'114:6','جن میں سے، اور انسان میں سے');
