INSERT INTO "uthmani_simple" VALUES(1,1,'1:1','بسم الله الرحمن الرحيم');
INSERT INTO "uthmani_simple" VALUES(1,2,'1:2','الحمد لله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(1,3,'1:3','الرحمن الرحيم');
INSERT INTO "uthmani_simple" VALUES(1,4,'1:4','ملك يوم الدين');
INSERT INTO "uthmani_simple" VALUES(1,5,'1:5','اياك نعبد واياك نستعين');
INSERT INTO "uthmani_simple" VALUES(1,6,'1:6','اهدنا الصرط المستقيم');
INSERT INTO "uthmani_simple" VALUES(1,7,'1:7','صرط الذين انعمت عليهم غير المغضوب عليهم ولا الضالين');
INSERT INTO "uthmani_simple" VALUES(2,1,'2:1','الم');
INSERT INTO "uthmani_simple" VALUES(2,2,'2:2','ذلك الكتب لا ريب فيه هدى للمتقين');
INSERT INTO "uthmani_simple" VALUES(2,3,'2:3','الذين يؤمنون بالغيب ويقيمون الصلوة ومما رزقنهم ينفقون');
INSERT INTO "uthmani_simple" VALUES(2,4,'2:4','والذين يؤمنون بما انزل اليك وما انزل من قبلك وبالءاخرة هم يوقنون');
INSERT INTO "uthmani_simple" VALUES(2,5,'2:5','او۟لئك على هدى من ربهم واو۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(2,6,'2:6','ان الذين كفروا۟ سواء عليهم ءانذرتهم ام لم تنذرهم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(2,7,'2:7','ختم الله على قلوبهم وعلى سمعهم وعلى ابصرهم غشوة ولهم عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(2,8,'2:8','ومن الناس من يقول ءامنا بالله وباليوم الءاخر وما هم بمؤمنين');
INSERT INTO "uthmani_simple" VALUES(2,9,'2:9','يخدعون الله والذين ءامنوا۟ وما يخدعون الا انفسهم وما يشعرون');
INSERT INTO "uthmani_simple" VALUES(2,10,'2:10','فى قلوبهم مرض فزادهم الله مرضا ولهم عذاب اليمۢ بما كانوا۟ يكذبون');
INSERT INTO "uthmani_simple" VALUES(2,11,'2:11','واذا قيل لهم لا تفسدوا۟ فى الارض قالوا۟ انما نحن مصلحون');
INSERT INTO "uthmani_simple" VALUES(2,12,'2:12','الا انهم هم المفسدون ولكن لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(2,13,'2:13','واذا قيل لهم ءامنوا۟ كما ءامن الناس قالوا۟ انؤمن كما ءامن السفهاء الا انهم هم السفهاء ولكن لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(2,14,'2:14','واذا لقوا۟ الذين ءامنوا۟ قالوا۟ ءامنا واذا خلوا۟ الى شيطينهم قالوا۟ انا معكم انما نحن مستهزءون');
INSERT INTO "uthmani_simple" VALUES(2,15,'2:15','الله يستهزئ بهم ويمدهم فى طغينهم يعمهون');
INSERT INTO "uthmani_simple" VALUES(2,16,'2:16','او۟لئك الذين اشتروا۟ الضللة بالهدى فما ربحت تجرتهم وما كانوا۟ مهتدين');
INSERT INTO "uthmani_simple" VALUES(2,17,'2:17','مثلهم كمثل الذى استوقد نارا فلما اضاءت ما حوله ذهب الله بنورهم وتركهم فى ظلمت لا يبصرون');
INSERT INTO "uthmani_simple" VALUES(2,18,'2:18','صمۢ بكم عمى فهم لا يرجعون');
INSERT INTO "uthmani_simple" VALUES(2,19,'2:19','او كصيب من السماء فيه ظلمت ورعد وبرق يجعلون اصبعهم فى ءاذانهم من الصوعق حذر الموت والله محيطۢ بالكفرين');
INSERT INTO "uthmani_simple" VALUES(2,20,'2:20','يكاد البرق يخطف ابصرهم كلما اضاء لهم مشوا۟ فيه واذا اظلم عليهم قاموا۟ ولو شاء الله لذهب بسمعهم وابصرهم ان الله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(2,21,'2:21','يايها الناس اعبدوا۟ ربكم الذى خلقكم والذين من قبلكم لعلكم تتقون');
INSERT INTO "uthmani_simple" VALUES(2,22,'2:22','الذى جعل لكم الارض فرشا والسماء بناء وانزل من السماء ماء فاخرج به من الثمرت رزقا لكم فلا تجعلوا۟ لله اندادا وانتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,23,'2:23','وان كنتم فى ريب مما نزلنا على عبدنا فاتوا۟ بسورة من مثله وادعوا۟ شهداءكم من دون الله ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(2,24,'2:24','فان لم تفعلوا۟ ولن تفعلوا۟ فاتقوا۟ النار التى وقودها الناس والحجارة اعدت للكفرين');
INSERT INTO "uthmani_simple" VALUES(2,25,'2:25','وبشر الذين ءامنوا۟ وعملوا۟ الصلحت ان لهم جنت تجرى من تحتها الانهر كلما رزقوا۟ منها من ثمرة رزقا قالوا۟ هذا الذى رزقنا من قبل واتوا۟ به متشبها ولهم فيها ازوج مطهرة وهم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(2,26,'2:26','ان الله لا يستحى ان يضرب مثلا ما بعوضة فما فوقها فاما الذين ءامنوا۟ فيعلمون انه الحق من ربهم واما الذين كفروا۟ فيقولون ماذا اراد الله بهذا مثلا يضل به كثيرا ويهدى به كثيرا وما يضل به الا الفسقين');
INSERT INTO "uthmani_simple" VALUES(2,27,'2:27','الذين ينقضون عهد الله منۢ بعد ميثقه ويقطعون ما امر الله به ان يوصل ويفسدون فى الارض او۟لئك هم الخسرون');
INSERT INTO "uthmani_simple" VALUES(2,28,'2:28','كيف تكفرون بالله وكنتم اموتا فاحيكم ثم يميتكم ثم يحييكم ثم اليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(2,29,'2:29','هو الذى خلق لكم ما فى الارض جميعا ثم استوى الى السماء فسوىهن سبع سموت وهو بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(2,30,'2:30','واذ قال ربك للملئكة انى جاعل فى الارض خليفة قالوا۟ اتجعل فيها من يفسد فيها ويسفك الدماء ونحن نسبح بحمدك ونقدس لك قال انى اعلم ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,31,'2:31','وعلم ءادم الاسماء كلها ثم عرضهم على الملئكة فقال انۢبـونى باسماء هؤلاء ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(2,32,'2:32','قالوا۟ سبحنك لا علم لنا الا ما علمتنا انك انت العليم الحكيم');
INSERT INTO "uthmani_simple" VALUES(2,33,'2:33','قال يـادم انۢبئهم باسمائهم فلما انۢباهم باسمائهم قال الم اقل لكم انى اعلم غيب السموت والارض واعلم ما تبدون وما كنتم تكتمون');
INSERT INTO "uthmani_simple" VALUES(2,34,'2:34','واذ قلنا للملئكة اسجدوا۟ لءادم فسجدوا۟ الا ابليس ابى واستكبر وكان من الكفرين');
INSERT INTO "uthmani_simple" VALUES(2,35,'2:35','وقلنا يـادم اسكن انت وزوجك الجنة وكلا منها رغدا حيث شئتما ولا تقربا هذه الشجرة فتكونا من الظلمين');
INSERT INTO "uthmani_simple" VALUES(2,36,'2:36','فازلهما الشيطن عنها فاخرجهما مما كانا فيه وقلنا اهبطوا۟ بعضكم لبعض عدو ولكم فى الارض مستقر ومتع الى حين');
INSERT INTO "uthmani_simple" VALUES(2,37,'2:37','فتلقى ءادم من ربه كلمت فتاب عليه انه هو التواب الرحيم');
INSERT INTO "uthmani_simple" VALUES(2,38,'2:38','قلنا اهبطوا۟ منها جميعا فاما ياتينكم منى هدى فمن تبع هداى فلا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(2,39,'2:39','والذين كفروا۟ وكذبوا۟ بـايتنا او۟لئك اصحب النار هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(2,40,'2:40','يبنى اسرءيل اذكروا۟ نعمتى التى انعمت عليكم واوفوا۟ بعهدى اوف بعهدكم وايى فارهبون');
INSERT INTO "uthmani_simple" VALUES(2,41,'2:41','وءامنوا۟ بما انزلت مصدقا لما معكم ولا تكونوا۟ اول كافرۭ به ولا تشتروا۟ بـايتى ثمنا قليلا وايى فاتقون');
INSERT INTO "uthmani_simple" VALUES(2,42,'2:42','ولا تلبسوا۟ الحق بالبطل وتكتموا۟ الحق وانتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,43,'2:43','واقيموا۟ الصلوة وءاتوا۟ الزكوة واركعوا۟ مع الركعين');
INSERT INTO "uthmani_simple" VALUES(2,44,'2:44','اتامرون الناس بالبر وتنسون انفسكم وانتم تتلون الكتب افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(2,45,'2:45','واستعينوا۟ بالصبر والصلوة وانها لكبيرة الا على الخشعين');
INSERT INTO "uthmani_simple" VALUES(2,46,'2:46','الذين يظنون انهم ملقوا۟ ربهم وانهم اليه رجعون');
INSERT INTO "uthmani_simple" VALUES(2,47,'2:47','يبنى اسرءيل اذكروا۟ نعمتى التى انعمت عليكم وانى فضلتكم على العلمين');
INSERT INTO "uthmani_simple" VALUES(2,48,'2:48','واتقوا۟ يوما لا تجزى نفس عن نفس شيـا ولا يقبل منها شفعة ولا يؤخذ منها عدل ولا هم ينصرون');
INSERT INTO "uthmani_simple" VALUES(2,49,'2:49','واذ نجينكم من ءال فرعون يسومونكم سوء العذاب يذبحون ابناءكم ويستحيون نساءكم وفى ذلكم بلاء من ربكم عظيم');
INSERT INTO "uthmani_simple" VALUES(2,50,'2:50','واذ فرقنا بكم البحر فانجينكم واغرقنا ءال فرعون وانتم تنظرون');
INSERT INTO "uthmani_simple" VALUES(2,51,'2:51','واذ وعدنا موسى اربعين ليلة ثم اتخذتم العجل منۢ بعده وانتم ظلمون');
INSERT INTO "uthmani_simple" VALUES(2,52,'2:52','ثم عفونا عنكم منۢ بعد ذلك لعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(2,53,'2:53','واذ ءاتينا موسى الكتب والفرقان لعلكم تهتدون');
INSERT INTO "uthmani_simple" VALUES(2,54,'2:54','واذ قال موسى لقومه يقوم انكم ظلمتم انفسكم باتخاذكم العجل فتوبوا۟ الى بارئكم فاقتلوا۟ انفسكم ذلكم خير لكم عند بارئكم فتاب عليكم انه هو التواب الرحيم');
INSERT INTO "uthmani_simple" VALUES(2,55,'2:55','واذ قلتم يموسى لن نؤمن لك حتى نرى الله جهرة فاخذتكم الصعقة وانتم تنظرون');
INSERT INTO "uthmani_simple" VALUES(2,56,'2:56','ثم بعثنكم منۢ بعد موتكم لعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(2,57,'2:57','وظللنا عليكم الغمام وانزلنا عليكم المن والسلوى كلوا۟ من طيبت ما رزقنكم وما ظلمونا ولكن كانوا۟ انفسهم يظلمون');
INSERT INTO "uthmani_simple" VALUES(2,58,'2:58','واذ قلنا ادخلوا۟ هذه القرية فكلوا۟ منها حيث شئتم رغدا وادخلوا۟ الباب سجدا وقولوا۟ حطة نغفر لكم خطيكم وسنزيد المحسنين');
INSERT INTO "uthmani_simple" VALUES(2,59,'2:59','فبدل الذين ظلموا۟ قولا غير الذى قيل لهم فانزلنا على الذين ظلموا۟ رجزا من السماء بما كانوا۟ يفسقون');
INSERT INTO "uthmani_simple" VALUES(2,60,'2:60','واذ استسقى موسى لقومه فقلنا اضرب بعصاك الحجر فانفجرت منه اثنتا عشرة عينا قد علم كل اناس مشربهم كلوا۟ واشربوا۟ من رزق الله ولا تعثوا۟ فى الارض مفسدين');
INSERT INTO "uthmani_simple" VALUES(2,61,'2:61','واذ قلتم يموسى لن نصبر على طعام وحد فادع لنا ربك يخرج لنا مما تنۢبت الارض منۢ بقلها وقثائها وفومها وعدسها وبصلها قال اتستبدلون الذى هو ادنى بالذى هو خير اهبطوا۟ مصرا فان لكم ما سالتم وضربت عليهم الذلة والمسكنة وباءو بغضب من الله ذلك بانهم كانوا۟ يكفرون بـايت الله ويقتلون النبين بغير الحق ذلك بما عصوا۟ وكانوا۟ يعتدون');
INSERT INTO "uthmani_simple" VALUES(2,62,'2:62','ان الذين ءامنوا۟ والذين هادوا۟ والنصرى والصبـين من ءامن بالله واليوم الءاخر وعمل صلحا فلهم اجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(2,63,'2:63','واذ اخذنا ميثقكم ورفعنا فوقكم الطور خذوا۟ ما ءاتينكم بقوة واذكروا۟ ما فيه لعلكم تتقون');
INSERT INTO "uthmani_simple" VALUES(2,64,'2:64','ثم توليتم منۢ بعد ذلك فلولا فضل الله عليكم ورحمته لكنتم من الخسرين');
INSERT INTO "uthmani_simple" VALUES(2,65,'2:65','ولقد علمتم الذين اعتدوا۟ منكم فى السبت فقلنا لهم كونوا۟ قردة خسـين');
INSERT INTO "uthmani_simple" VALUES(2,66,'2:66','فجعلنها نكلا لما بين يديها وما خلفها وموعظة للمتقين');
INSERT INTO "uthmani_simple" VALUES(2,67,'2:67','واذ قال موسى لقومه ان الله يامركم ان تذبحوا۟ بقرة قالوا۟ اتتخذنا هزوا قال اعوذ بالله ان اكون من الجهلين');
INSERT INTO "uthmani_simple" VALUES(2,68,'2:68','قالوا۟ ادع لنا ربك يبين لنا ما هى قال انه يقول انها بقرة لا فارض ولا بكر عوانۢ بين ذلك فافعلوا۟ ما تؤمرون');
INSERT INTO "uthmani_simple" VALUES(2,69,'2:69','قالوا۟ ادع لنا ربك يبين لنا ما لونها قال انه يقول انها بقرة صفراء فاقع لونها تسر النظرين');
INSERT INTO "uthmani_simple" VALUES(2,70,'2:70','قالوا۟ ادع لنا ربك يبين لنا ما هى ان البقر تشبه علينا وانا ان شاء الله لمهتدون');
INSERT INTO "uthmani_simple" VALUES(2,71,'2:71','قال انه يقول انها بقرة لا ذلول تثير الارض ولا تسقى الحرث مسلمة لا شية فيها قالوا۟ الـن جئت بالحق فذبحوها وما كادوا۟ يفعلون');
INSERT INTO "uthmani_simple" VALUES(2,72,'2:72','واذ قتلتم نفسا فادرتم فيها والله مخرج ما كنتم تكتمون');
INSERT INTO "uthmani_simple" VALUES(2,73,'2:73','فقلنا اضربوه ببعضها كذلك يحى الله الموتى ويريكم ءايته لعلكم تعقلون');
INSERT INTO "uthmani_simple" VALUES(2,74,'2:74','ثم قست قلوبكم منۢ بعد ذلك فهى كالحجارة او اشد قسوة وان من الحجارة لما يتفجر منه الانهر وان منها لما يشقق فيخرج منه الماء وان منها لما يهبط من خشية الله وما الله بغفل عما تعملون');
INSERT INTO "uthmani_simple" VALUES(2,75,'2:75','افتطمعون ان يؤمنوا۟ لكم وقد كان فريق منهم يسمعون كلم الله ثم يحرفونه منۢ بعد ما عقلوه وهم يعلمون');
INSERT INTO "uthmani_simple" VALUES(2,76,'2:76','واذا لقوا۟ الذين ءامنوا۟ قالوا۟ ءامنا واذا خلا بعضهم الى بعض قالوا۟ اتحدثونهم بما فتح الله عليكم ليحاجوكم به عند ربكم افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(2,77,'2:77','اولا يعلمون ان الله يعلم ما يسرون وما يعلنون');
INSERT INTO "uthmani_simple" VALUES(2,78,'2:78','ومنهم اميون لا يعلمون الكتب الا امانى وان هم الا يظنون');
INSERT INTO "uthmani_simple" VALUES(2,79,'2:79','فويل للذين يكتبون الكتب بايديهم ثم يقولون هذا من عند الله ليشتروا۟ به ثمنا قليلا فويل لهم مما كتبت ايديهم وويل لهم مما يكسبون');
INSERT INTO "uthmani_simple" VALUES(2,80,'2:80','وقالوا۟ لن تمسنا النار الا اياما معدودة قل اتخذتم عند الله عهدا فلن يخلف الله عهده ام تقولون على الله ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,81,'2:81','بلى من كسب سيئة واحطت به خطيـته فاو۟لئك اصحب النار هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(2,82,'2:82','والذين ءامنوا۟ وعملوا۟ الصلحت او۟لئك اصحب الجنة هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(2,83,'2:83','واذ اخذنا ميثق بنى اسرءيل لا تعبدون الا الله وبالولدين احسانا وذى القربى واليتمى والمسكين وقولوا۟ للناس حسنا واقيموا۟ الصلوة وءاتوا۟ الزكوة ثم توليتم الا قليلا منكم وانتم معرضون');
INSERT INTO "uthmani_simple" VALUES(2,84,'2:84','واذ اخذنا ميثقكم لا تسفكون دماءكم ولا تخرجون انفسكم من ديركم ثم اقررتم وانتم تشهدون');
INSERT INTO "uthmani_simple" VALUES(2,85,'2:85','ثم انتم هؤلاء تقتلون انفسكم وتخرجون فريقا منكم من ديرهم تظهرون عليهم بالاثم والعدون وان ياتوكم اسرى تفدوهم وهو محرم عليكم اخراجهم افتؤمنون ببعض الكتب وتكفرون ببعض فما جزاء من يفعل ذلك منكم الا خزى فى الحيوة الدنيا ويوم القيمة يردون الى اشد العذاب وما الله بغفل عما تعملون');
INSERT INTO "uthmani_simple" VALUES(2,86,'2:86','او۟لئك الذين اشتروا۟ الحيوة الدنيا بالءاخرة فلا يخفف عنهم العذاب ولا هم ينصرون');
INSERT INTO "uthmani_simple" VALUES(2,87,'2:87','ولقد ءاتينا موسى الكتب وقفينا منۢ بعده بالرسل وءاتينا عيسى ابن مريم البينت وايدنه بروح القدس افكلما جاءكم رسولۢ بما لا تهوى انفسكم استكبرتم ففريقا كذبتم وفريقا تقتلون');
INSERT INTO "uthmani_simple" VALUES(2,88,'2:88','وقالوا۟ قلوبنا غلفۢ بل لعنهم الله بكفرهم فقليلا ما يؤمنون');
INSERT INTO "uthmani_simple" VALUES(2,89,'2:89','ولما جاءهم كتب من عند الله مصدق لما معهم وكانوا۟ من قبل يستفتحون على الذين كفروا۟ فلما جاءهم ما عرفوا۟ كفروا۟ به فلعنة الله على الكفرين');
INSERT INTO "uthmani_simple" VALUES(2,90,'2:90','بئسما اشتروا۟ به انفسهم ان يكفروا۟ بما انزل الله بغيا ان ينزل الله من فضله على من يشاء من عباده فباءو بغضب على غضب وللكفرين عذاب مهين');
INSERT INTO "uthmani_simple" VALUES(2,91,'2:91','واذا قيل لهم ءامنوا۟ بما انزل الله قالوا۟ نؤمن بما انزل علينا ويكفرون بما وراءه وهو الحق مصدقا لما معهم قل فلم تقتلون انۢبياء الله من قبل ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(2,92,'2:92','ولقد جاءكم موسى بالبينت ثم اتخذتم العجل منۢ بعده وانتم ظلمون');
INSERT INTO "uthmani_simple" VALUES(2,93,'2:93','واذ اخذنا ميثقكم ورفعنا فوقكم الطور خذوا۟ ما ءاتينكم بقوة واسمعوا۟ قالوا۟ سمعنا وعصينا واشربوا۟ فى قلوبهم العجل بكفرهم قل بئسما يامركم به ايمنكم ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(2,94,'2:94','قل ان كانت لكم الدار الءاخرة عند الله خالصة من دون الناس فتمنوا۟ الموت ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(2,95,'2:95','ولن يتمنوه ابدۢا بما قدمت ايديهم والله عليمۢ بالظلمين');
INSERT INTO "uthmani_simple" VALUES(2,96,'2:96','ولتجدنهم احرص الناس على حيوة ومن الذين اشركوا۟ يود احدهم لو يعمر الف سنة وما هو بمزحزحه من العذاب ان يعمر والله بصيرۢ بما يعملون');
INSERT INTO "uthmani_simple" VALUES(2,97,'2:97','قل من كان عدوا لجبريل فانه نزله على قلبك باذن الله مصدقا لما بين يديه وهدى وبشرى للمؤمنين');
INSERT INTO "uthmani_simple" VALUES(2,98,'2:98','من كان عدوا لله وملئكته ورسله وجبريل وميكىل فان الله عدو للكفرين');
INSERT INTO "uthmani_simple" VALUES(2,99,'2:99','ولقد انزلنا اليك ءايتۭ بينت وما يكفر بها الا الفسقون');
INSERT INTO "uthmani_simple" VALUES(2,100,'2:100','اوكلما عهدوا۟ عهدا نبذه فريق منهم بل اكثرهم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(2,101,'2:101','ولما جاءهم رسول من عند الله مصدق لما معهم نبذ فريق من الذين اوتوا۟ الكتب كتب الله وراء ظهورهم كانهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(2,102,'2:102','واتبعوا۟ ما تتلوا۟ الشيطين على ملك سليمن وما كفر سليمن ولكن الشيطين كفروا۟ يعلمون الناس السحر وما انزل على الملكين ببابل هروت ومروت وما يعلمان من احد حتى يقولا انما نحن فتنة فلا تكفر فيتعلمون منهما ما يفرقون به بين المرء وزوجه وما هم بضارين به من احد الا باذن الله ويتعلمون ما يضرهم ولا ينفعهم ولقد علموا۟ لمن اشترىه ما له فى الءاخرة من خلق ولبئس ما شروا۟ به انفسهم لو كانوا۟ يعلمون');
INSERT INTO "uthmani_simple" VALUES(2,103,'2:103','ولو انهم ءامنوا۟ واتقوا۟ لمثوبة من عند الله خير لو كانوا۟ يعلمون');
INSERT INTO "uthmani_simple" VALUES(2,104,'2:104','يايها الذين ءامنوا۟ لا تقولوا۟ رعنا وقولوا۟ انظرنا واسمعوا۟ وللكفرين عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(2,105,'2:105','ما يود الذين كفروا۟ من اهل الكتب ولا المشركين ان ينزل عليكم من خير من ربكم والله يختص برحمته من يشاء والله ذو الفضل العظيم');
INSERT INTO "uthmani_simple" VALUES(2,106,'2:106','ما ننسخ من ءاية او ننسها نات بخير منها او مثلها الم تعلم ان الله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(2,107,'2:107','الم تعلم ان الله له ملك السموت والارض وما لكم من دون الله من ولى ولا نصير');
INSERT INTO "uthmani_simple" VALUES(2,108,'2:108','ام تريدون ان تسـلوا۟ رسولكم كما سئل موسى من قبل ومن يتبدل الكفر بالايمن فقد ضل سواء السبيل');
INSERT INTO "uthmani_simple" VALUES(2,109,'2:109','ود كثير من اهل الكتب لو يردونكم منۢ بعد ايمنكم كفارا حسدا من عند انفسهم منۢ بعد ما تبين لهم الحق فاعفوا۟ واصفحوا۟ حتى ياتى الله بامره ان الله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(2,110,'2:110','واقيموا۟ الصلوة وءاتوا۟ الزكوة وما تقدموا۟ لانفسكم من خير تجدوه عند الله ان الله بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(2,111,'2:111','وقالوا۟ لن يدخل الجنة الا من كان هودا او نصرى تلك امانيهم قل هاتوا۟ برهنكم ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(2,112,'2:112','بلى من اسلم وجهه لله وهو محسن فله اجره عند ربه ولا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(2,113,'2:113','وقالت اليهود ليست النصرى على شىء وقالت النصرى ليست اليهود على شىء وهم يتلون الكتب كذلك قال الذين لا يعلمون مثل قولهم فالله يحكم بينهم يوم القيمة فيما كانوا۟ فيه يختلفون');
INSERT INTO "uthmani_simple" VALUES(2,114,'2:114','ومن اظلم ممن منع مسجد الله ان يذكر فيها اسمه وسعى فى خرابها او۟لئك ما كان لهم ان يدخلوها الا خائفين لهم فى الدنيا خزى ولهم فى الءاخرة عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(2,115,'2:115','ولله المشرق والمغرب فاينما تولوا۟ فثم وجه الله ان الله وسع عليم');
INSERT INTO "uthmani_simple" VALUES(2,116,'2:116','وقالوا۟ اتخذ الله ولدا سبحنه بل له ما فى السموت والارض كل له قنتون');
INSERT INTO "uthmani_simple" VALUES(2,117,'2:117','بديع السموت والارض واذا قضى امرا فانما يقول له كن فيكون');
INSERT INTO "uthmani_simple" VALUES(2,118,'2:118','وقال الذين لا يعلمون لولا يكلمنا الله او تاتينا ءاية كذلك قال الذين من قبلهم مثل قولهم تشبهت قلوبهم قد بينا الءايت لقوم يوقنون');
INSERT INTO "uthmani_simple" VALUES(2,119,'2:119','انا ارسلنك بالحق بشيرا ونذيرا ولا تسـل عن اصحب الجحيم');
INSERT INTO "uthmani_simple" VALUES(2,120,'2:120','ولن ترضى عنك اليهود ولا النصرى حتى تتبع ملتهم قل ان هدى الله هو الهدى ولئن اتبعت اهواءهم بعد الذى جاءك من العلم ما لك من الله من ولى ولا نصير');
INSERT INTO "uthmani_simple" VALUES(2,121,'2:121','الذين ءاتينهم الكتب يتلونه حق تلاوته او۟لئك يؤمنون به ومن يكفر به فاو۟لئك هم الخسرون');
INSERT INTO "uthmani_simple" VALUES(2,122,'2:122','يبنى اسرءيل اذكروا۟ نعمتى التى انعمت عليكم وانى فضلتكم على العلمين');
INSERT INTO "uthmani_simple" VALUES(2,123,'2:123','واتقوا۟ يوما لا تجزى نفس عن نفس شيـا ولا يقبل منها عدل ولا تنفعها شفعة ولا هم ينصرون');
INSERT INTO "uthmani_simple" VALUES(2,124,'2:124','واذ ابتلى ابرهم ربه بكلمت فاتمهن قال انى جاعلك للناس اماما قال ومن ذريتى قال لا ينال عهدى الظلمين');
INSERT INTO "uthmani_simple" VALUES(2,125,'2:125','واذ جعلنا البيت مثابة للناس وامنا واتخذوا۟ من مقام ابرهم مصلى وعهدنا الى ابرهم واسمعيل ان طهرا بيتى للطائفين والعكفين والركع السجود');
INSERT INTO "uthmani_simple" VALUES(2,126,'2:126','واذ قال ابرهم رب اجعل هذا بلدا ءامنا وارزق اهله من الثمرت من ءامن منهم بالله واليوم الءاخر قال ومن كفر فامتعه قليلا ثم اضطره الى عذاب النار وبئس المصير');
INSERT INTO "uthmani_simple" VALUES(2,127,'2:127','واذ يرفع ابرهم القواعد من البيت واسمعيل ربنا تقبل منا انك انت السميع العليم');
INSERT INTO "uthmani_simple" VALUES(2,128,'2:128','ربنا واجعلنا مسلمين لك ومن ذريتنا امة مسلمة لك وارنا مناسكنا وتب علينا انك انت التواب الرحيم');
INSERT INTO "uthmani_simple" VALUES(2,129,'2:129','ربنا وابعث فيهم رسولا منهم يتلوا۟ عليهم ءايتك ويعلمهم الكتب والحكمة ويزكيهم انك انت العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(2,130,'2:130','ومن يرغب عن ملة ابرهم الا من سفه نفسه ولقد اصطفينه فى الدنيا وانه فى الءاخرة لمن الصلحين');
INSERT INTO "uthmani_simple" VALUES(2,131,'2:131','اذ قال له ربه اسلم قال اسلمت لرب العلمين');
INSERT INTO "uthmani_simple" VALUES(2,132,'2:132','ووصى بها ابرهم بنيه ويعقوب يبنى ان الله اصطفى لكم الدين فلا تموتن الا وانتم مسلمون');
INSERT INTO "uthmani_simple" VALUES(2,133,'2:133','ام كنتم شهداء اذ حضر يعقوب الموت اذ قال لبنيه ما تعبدون منۢ بعدى قالوا۟ نعبد الهك واله ءابائك ابرهم واسمعيل واسحق الها وحدا ونحن له مسلمون');
INSERT INTO "uthmani_simple" VALUES(2,134,'2:134','تلك امة قد خلت لها ما كسبت ولكم ما كسبتم ولا تسـلون عما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(2,135,'2:135','وقالوا۟ كونوا۟ هودا او نصرى تهتدوا۟ قل بل ملة ابرهم حنيفا وما كان من المشركين');
INSERT INTO "uthmani_simple" VALUES(2,136,'2:136','قولوا۟ ءامنا بالله وما انزل الينا وما انزل الى ابرهم واسمعيل واسحق ويعقوب والاسباط وما اوتى موسى وعيسى وما اوتى النبيون من ربهم لا نفرق بين احد منهم ونحن له مسلمون');
INSERT INTO "uthmani_simple" VALUES(2,137,'2:137','فان ءامنوا۟ بمثل ما ءامنتم به فقد اهتدوا۟ وان تولوا۟ فانما هم فى شقاق فسيكفيكهم الله وهو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(2,138,'2:138','صبغة الله ومن احسن من الله صبغة ونحن له عبدون');
INSERT INTO "uthmani_simple" VALUES(2,139,'2:139','قل اتحاجوننا فى الله وهو ربنا وربكم ولنا اعملنا ولكم اعملكم ونحن له مخلصون');
INSERT INTO "uthmani_simple" VALUES(2,140,'2:140','ام تقولون ان ابرهم واسمعيل واسحق ويعقوب والاسباط كانوا۟ هودا او نصرى قل ءانتم اعلم ام الله ومن اظلم ممن كتم شهدة عنده من الله وما الله بغفل عما تعملون');
INSERT INTO "uthmani_simple" VALUES(2,141,'2:141','تلك امة قد خلت لها ما كسبت ولكم ما كسبتم ولا تسـلون عما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(2,142,'2:142','سيقول السفهاء من الناس ما ولىهم عن قبلتهم التى كانوا۟ عليها قل لله المشرق والمغرب يهدى من يشاء الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(2,143,'2:143','وكذلك جعلنكم امة وسطا لتكونوا۟ شهداء على الناس ويكون الرسول عليكم شهيدا وما جعلنا القبلة التى كنت عليها الا لنعلم من يتبع الرسول ممن ينقلب على عقبيه وان كانت لكبيرة الا على الذين هدى الله وما كان الله ليضيع ايمنكم ان الله بالناس لرءوف رحيم');
INSERT INTO "uthmani_simple" VALUES(2,144,'2:144','قد نرى تقلب وجهك فى السماء فلنولينك قبلة ترضىها فول وجهك شطر المسجد الحرام وحيث ما كنتم فولوا۟ وجوهكم شطره وان الذين اوتوا۟ الكتب ليعلمون انه الحق من ربهم وما الله بغفل عما يعملون');
INSERT INTO "uthmani_simple" VALUES(2,145,'2:145','ولئن اتيت الذين اوتوا۟ الكتب بكل ءاية ما تبعوا۟ قبلتك وما انت بتابع قبلتهم وما بعضهم بتابع قبلة بعض ولئن اتبعت اهواءهم منۢ بعد ما جاءك من العلم انك اذا لمن الظلمين');
INSERT INTO "uthmani_simple" VALUES(2,146,'2:146','الذين ءاتينهم الكتب يعرفونه كما يعرفون ابناءهم وان فريقا منهم ليكتمون الحق وهم يعلمون');
INSERT INTO "uthmani_simple" VALUES(2,147,'2:147','الحق من ربك فلا تكونن من الممترين');
INSERT INTO "uthmani_simple" VALUES(2,148,'2:148','ولكل وجهة هو موليها فاستبقوا۟ الخيرت اين ما تكونوا۟ يات بكم الله جميعا ان الله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(2,149,'2:149','ومن حيث خرجت فول وجهك شطر المسجد الحرام وانه للحق من ربك وما الله بغفل عما تعملون');
INSERT INTO "uthmani_simple" VALUES(2,150,'2:150','ومن حيث خرجت فول وجهك شطر المسجد الحرام وحيث ما كنتم فولوا۟ وجوهكم شطره لئلا يكون للناس عليكم حجة الا الذين ظلموا۟ منهم فلا تخشوهم واخشونى ولاتم نعمتى عليكم ولعلكم تهتدون');
INSERT INTO "uthmani_simple" VALUES(2,151,'2:151','كما ارسلنا فيكم رسولا منكم يتلوا۟ عليكم ءايتنا ويزكيكم ويعلمكم الكتب والحكمة ويعلمكم ما لم تكونوا۟ تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,152,'2:152','فاذكرونى اذكركم واشكروا۟ لى ولا تكفرون');
INSERT INTO "uthmani_simple" VALUES(2,153,'2:153','يايها الذين ءامنوا۟ استعينوا۟ بالصبر والصلوة ان الله مع الصبرين');
INSERT INTO "uthmani_simple" VALUES(2,154,'2:154','ولا تقولوا۟ لمن يقتل فى سبيل الله اموتۢ بل احياء ولكن لا تشعرون');
INSERT INTO "uthmani_simple" VALUES(2,155,'2:155','ولنبلونكم بشىء من الخوف والجوع ونقص من الامول والانفس والثمرت وبشر الصبرين');
INSERT INTO "uthmani_simple" VALUES(2,156,'2:156','الذين اذا اصبتهم مصيبة قالوا۟ انا لله وانا اليه رجعون');
INSERT INTO "uthmani_simple" VALUES(2,157,'2:157','او۟لئك عليهم صلوت من ربهم ورحمة واو۟لئك هم المهتدون');
INSERT INTO "uthmani_simple" VALUES(2,158,'2:158','ان الصفا والمروة من شعائر الله فمن حج البيت او اعتمر فلا جناح عليه ان يطوف بهما ومن تطوع خيرا فان الله شاكر عليم');
INSERT INTO "uthmani_simple" VALUES(2,159,'2:159','ان الذين يكتمون ما انزلنا من البينت والهدى منۢ بعد ما بينه للناس فى الكتب او۟لئك يلعنهم الله ويلعنهم اللعنون');
INSERT INTO "uthmani_simple" VALUES(2,160,'2:160','الا الذين تابوا۟ واصلحوا۟ وبينوا۟ فاو۟لئك اتوب عليهم وانا التواب الرحيم');
INSERT INTO "uthmani_simple" VALUES(2,161,'2:161','ان الذين كفروا۟ وماتوا۟ وهم كفار او۟لئك عليهم لعنة الله والملئكة والناس اجمعين');
INSERT INTO "uthmani_simple" VALUES(2,162,'2:162','خلدين فيها لا يخفف عنهم العذاب ولا هم ينظرون');
INSERT INTO "uthmani_simple" VALUES(2,163,'2:163','والهكم اله وحد لا اله الا هو الرحمن الرحيم');
INSERT INTO "uthmani_simple" VALUES(2,164,'2:164','ان فى خلق السموت والارض واختلف اليل والنهار والفلك التى تجرى فى البحر بما ينفع الناس وما انزل الله من السماء من ماء فاحيا به الارض بعد موتها وبث فيها من كل دابة وتصريف الريح والسحاب المسخر بين السماء والارض لءايت لقوم يعقلون');
INSERT INTO "uthmani_simple" VALUES(2,165,'2:165','ومن الناس من يتخذ من دون الله اندادا يحبونهم كحب الله والذين ءامنوا۟ اشد حبا لله ولو يرى الذين ظلموا۟ اذ يرون العذاب ان القوة لله جميعا وان الله شديد العذاب');
INSERT INTO "uthmani_simple" VALUES(2,166,'2:166','اذ تبرا الذين اتبعوا۟ من الذين اتبعوا۟ وراوا۟ العذاب وتقطعت بهم الاسباب');
INSERT INTO "uthmani_simple" VALUES(2,167,'2:167','وقال الذين اتبعوا۟ لو ان لنا كرة فنتبرا منهم كما تبرءوا۟ منا كذلك يريهم الله اعملهم حسرت عليهم وما هم بخرجين من النار');
INSERT INTO "uthmani_simple" VALUES(2,168,'2:168','يايها الناس كلوا۟ مما فى الارض حللا طيبا ولا تتبعوا۟ خطوت الشيطن انه لكم عدو مبين');
INSERT INTO "uthmani_simple" VALUES(2,169,'2:169','انما يامركم بالسوء والفحشاء وان تقولوا۟ على الله ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,170,'2:170','واذا قيل لهم اتبعوا۟ ما انزل الله قالوا۟ بل نتبع ما الفينا عليه ءاباءنا اولو كان ءاباؤهم لا يعقلون شيـا ولا يهتدون');
INSERT INTO "uthmani_simple" VALUES(2,171,'2:171','ومثل الذين كفروا۟ كمثل الذى ينعق بما لا يسمع الا دعاء ونداء صمۢ بكم عمى فهم لا يعقلون');
INSERT INTO "uthmani_simple" VALUES(2,172,'2:172','يايها الذين ءامنوا۟ كلوا۟ من طيبت ما رزقنكم واشكروا۟ لله ان كنتم اياه تعبدون');
INSERT INTO "uthmani_simple" VALUES(2,173,'2:173','انما حرم عليكم الميتة والدم ولحم الخنزير وما اهل به لغير الله فمن اضطر غير باغ ولا عاد فلا اثم عليه ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(2,174,'2:174','ان الذين يكتمون ما انزل الله من الكتب ويشترون به ثمنا قليلا او۟لئك ما ياكلون فى بطونهم الا النار ولا يكلمهم الله يوم القيمة ولا يزكيهم ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(2,175,'2:175','او۟لئك الذين اشتروا۟ الضللة بالهدى والعذاب بالمغفرة فما اصبرهم على النار');
INSERT INTO "uthmani_simple" VALUES(2,176,'2:176','ذلك بان الله نزل الكتب بالحق وان الذين اختلفوا۟ فى الكتب لفى شقاقۭ بعيد');
INSERT INTO "uthmani_simple" VALUES(2,177,'2:177','ليس البر ان تولوا۟ وجوهكم قبل المشرق والمغرب ولكن البر من ءامن بالله واليوم الءاخر والملئكة والكتب والنبين وءاتى المال على حبه ذوى القربى واليتمى والمسكين وابن السبيل والسائلين وفى الرقاب واقام الصلوة وءاتى الزكوة والموفون بعهدهم اذا عهدوا۟ والصبرين فى الباساء والضراء وحين الباس او۟لئك الذين صدقوا۟ واو۟لئك هم المتقون');
INSERT INTO "uthmani_simple" VALUES(2,178,'2:178','يايها الذين ءامنوا۟ كتب عليكم القصاص فى القتلى الحر بالحر والعبد بالعبد والانثى بالانثى فمن عفى له من اخيه شىء فاتباعۢ بالمعروف واداء اليه باحسن ذلك تخفيف من ربكم ورحمة فمن اعتدى بعد ذلك فله عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(2,179,'2:179','ولكم فى القصاص حيوة ياو۟لى الالبب لعلكم تتقون');
INSERT INTO "uthmani_simple" VALUES(2,180,'2:180','كتب عليكم اذا حضر احدكم الموت ان ترك خيرا الوصية للولدين والاقربين بالمعروف حقا على المتقين');
INSERT INTO "uthmani_simple" VALUES(2,181,'2:181','فمنۢ بدله بعدما سمعه فانما اثمه على الذين يبدلونه ان الله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(2,182,'2:182','فمن خاف من موص جنفا او اثما فاصلح بينهم فلا اثم عليه ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(2,183,'2:183','يايها الذين ءامنوا۟ كتب عليكم الصيام كما كتب على الذين من قبلكم لعلكم تتقون');
INSERT INTO "uthmani_simple" VALUES(2,184,'2:184','اياما معدودت فمن كان منكم مريضا او على سفر فعدة من ايام اخر وعلى الذين يطيقونه فدية طعام مسكين فمن تطوع خيرا فهو خير له وان تصوموا۟ خير لكم ان كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,185,'2:185','شهر رمضان الذى انزل فيه القرءان هدى للناس وبينت من الهدى والفرقان فمن شهد منكم الشهر فليصمه ومن كان مريضا او على سفر فعدة من ايام اخر يريد الله بكم اليسر ولا يريد بكم العسر ولتكملوا۟ العدة ولتكبروا۟ الله على ما هدىكم ولعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(2,186,'2:186','واذا سالك عبادى عنى فانى قريب اجيب دعوة الداع اذا دعان فليستجيبوا۟ لى وليؤمنوا۟ بى لعلهم يرشدون');
INSERT INTO "uthmani_simple" VALUES(2,187,'2:187','احل لكم ليلة الصيام الرفث الى نسائكم هن لباس لكم وانتم لباس لهن علم الله انكم كنتم تختانون انفسكم فتاب عليكم وعفا عنكم فالـن بشروهن وابتغوا۟ ما كتب الله لكم وكلوا۟ واشربوا۟ حتى يتبين لكم الخيط الابيض من الخيط الاسود من الفجر ثم اتموا۟ الصيام الى اليل ولا تبشروهن وانتم عكفون فى المسجد تلك حدود الله فلا تقربوها كذلك يبين الله ءايته للناس لعلهم يتقون');
INSERT INTO "uthmani_simple" VALUES(2,188,'2:188','ولا تاكلوا۟ امولكم بينكم بالبطل وتدلوا۟ بها الى الحكام لتاكلوا۟ فريقا من امول الناس بالاثم وانتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,189,'2:189','يسـلونك عن الاهلة قل هى موقيت للناس والحج وليس البر بان تاتوا۟ البيوت من ظهورها ولكن البر من اتقى واتوا۟ البيوت من ابوبها واتقوا۟ الله لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(2,190,'2:190','وقتلوا۟ فى سبيل الله الذين يقتلونكم ولا تعتدوا۟ ان الله لا يحب المعتدين');
INSERT INTO "uthmani_simple" VALUES(2,191,'2:191','واقتلوهم حيث ثقفتموهم واخرجوهم من حيث اخرجوكم والفتنة اشد من القتل ولا تقتلوهم عند المسجد الحرام حتى يقتلوكم فيه فان قتلوكم فاقتلوهم كذلك جزاء الكفرين');
INSERT INTO "uthmani_simple" VALUES(2,192,'2:192','فان انتهوا۟ فان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(2,193,'2:193','وقتلوهم حتى لا تكون فتنة ويكون الدين لله فان انتهوا۟ فلا عدون الا على الظلمين');
INSERT INTO "uthmani_simple" VALUES(2,194,'2:194','الشهر الحرام بالشهر الحرام والحرمت قصاص فمن اعتدى عليكم فاعتدوا۟ عليه بمثل ما اعتدى عليكم واتقوا۟ الله واعلموا۟ ان الله مع المتقين');
INSERT INTO "uthmani_simple" VALUES(2,195,'2:195','وانفقوا۟ فى سبيل الله ولا تلقوا۟ بايديكم الى التهلكة واحسنوا۟ ان الله يحب المحسنين');
INSERT INTO "uthmani_simple" VALUES(2,196,'2:196','واتموا۟ الحج والعمرة لله فان احصرتم فما استيسر من الهدى ولا تحلقوا۟ رءوسكم حتى يبلغ الهدى محله فمن كان منكم مريضا او به اذى من راسه ففدية من صيام او صدقة او نسك فاذا امنتم فمن تمتع بالعمرة الى الحج فما استيسر من الهدى فمن لم يجد فصيام ثلثة ايام فى الحج وسبعة اذا رجعتم تلك عشرة كاملة ذلك لمن لم يكن اهله حاضرى المسجد الحرام واتقوا۟ الله واعلموا۟ ان الله شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(2,197,'2:197','الحج اشهر معلومت فمن فرض فيهن الحج فلا رفث ولا فسوق ولا جدال فى الحج وما تفعلوا۟ من خير يعلمه الله وتزودوا۟ فان خير الزاد التقوى واتقون ياو۟لى الالبب');
INSERT INTO "uthmani_simple" VALUES(2,198,'2:198','ليس عليكم جناح ان تبتغوا۟ فضلا من ربكم فاذا افضتم من عرفت فاذكروا۟ الله عند المشعر الحرام واذكروه كما هدىكم وان كنتم من قبله لمن الضالين');
INSERT INTO "uthmani_simple" VALUES(2,199,'2:199','ثم افيضوا۟ من حيث افاض الناس واستغفروا۟ الله ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(2,200,'2:200','فاذا قضيتم منسككم فاذكروا۟ الله كذكركم ءاباءكم او اشد ذكرا فمن الناس من يقول ربنا ءاتنا فى الدنيا وما له فى الءاخرة من خلق');
INSERT INTO "uthmani_simple" VALUES(2,201,'2:201','ومنهم من يقول ربنا ءاتنا فى الدنيا حسنة وفى الءاخرة حسنة وقنا عذاب النار');
INSERT INTO "uthmani_simple" VALUES(2,202,'2:202','او۟لئك لهم نصيب مما كسبوا۟ والله سريع الحساب');
INSERT INTO "uthmani_simple" VALUES(2,203,'2:203','واذكروا۟ الله فى ايام معدودت فمن تعجل فى يومين فلا اثم عليه ومن تاخر فلا اثم عليه لمن اتقى واتقوا۟ الله واعلموا۟ انكم اليه تحشرون');
INSERT INTO "uthmani_simple" VALUES(2,204,'2:204','ومن الناس من يعجبك قوله فى الحيوة الدنيا ويشهد الله على ما فى قلبه وهو الد الخصام');
INSERT INTO "uthmani_simple" VALUES(2,205,'2:205','واذا تولى سعى فى الارض ليفسد فيها ويهلك الحرث والنسل والله لا يحب الفساد');
INSERT INTO "uthmani_simple" VALUES(2,206,'2:206','واذا قيل له اتق الله اخذته العزة بالاثم فحسبه جهنم ولبئس المهاد');
INSERT INTO "uthmani_simple" VALUES(2,207,'2:207','ومن الناس من يشرى نفسه ابتغاء مرضات الله والله رءوفۢ بالعباد');
INSERT INTO "uthmani_simple" VALUES(2,208,'2:208','يايها الذين ءامنوا۟ ادخلوا۟ فى السلم كافة ولا تتبعوا۟ خطوت الشيطن انه لكم عدو مبين');
INSERT INTO "uthmani_simple" VALUES(2,209,'2:209','فان زللتم منۢ بعد ما جاءتكم البينت فاعلموا۟ ان الله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(2,210,'2:210','هل ينظرون الا ان ياتيهم الله فى ظلل من الغمام والملئكة وقضى الامر والى الله ترجع الامور');
INSERT INTO "uthmani_simple" VALUES(2,211,'2:211','سل بنى اسرءيل كم ءاتينهم من ءايةۭ بينة ومن يبدل نعمة الله منۢ بعد ما جاءته فان الله شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(2,212,'2:212','زين للذين كفروا۟ الحيوة الدنيا ويسخرون من الذين ءامنوا۟ والذين اتقوا۟ فوقهم يوم القيمة والله يرزق من يشاء بغير حساب');
INSERT INTO "uthmani_simple" VALUES(2,213,'2:213','كان الناس امة وحدة فبعث الله النبين مبشرين ومنذرين وانزل معهم الكتب بالحق ليحكم بين الناس فيما اختلفوا۟ فيه وما اختلف فيه الا الذين اوتوه منۢ بعد ما جاءتهم البينت بغيۢا بينهم فهدى الله الذين ءامنوا۟ لما اختلفوا۟ فيه من الحق باذنه والله يهدى من يشاء الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(2,214,'2:214','ام حسبتم ان تدخلوا۟ الجنة ولما ياتكم مثل الذين خلوا۟ من قبلكم مستهم الباساء والضراء وزلزلوا۟ حتى يقول الرسول والذين ءامنوا۟ معه متى نصر الله الا ان نصر الله قريب');
INSERT INTO "uthmani_simple" VALUES(2,215,'2:215','يسـلونك ماذا ينفقون قل ما انفقتم من خير فللولدين والاقربين واليتمى والمسكين وابن السبيل وما تفعلوا۟ من خير فان الله به عليم');
INSERT INTO "uthmani_simple" VALUES(2,216,'2:216','كتب عليكم القتال وهو كره لكم وعسى ان تكرهوا۟ شيـا وهو خير لكم وعسى ان تحبوا۟ شيـا وهو شر لكم والله يعلم وانتم لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,217,'2:217','يسـلونك عن الشهر الحرام قتال فيه قل قتال فيه كبير وصد عن سبيل الله وكفرۢ به والمسجد الحرام واخراج اهله منه اكبر عند الله والفتنة اكبر من القتل ولا يزالون يقتلونكم حتى يردوكم عن دينكم ان استطعوا۟ ومن يرتدد منكم عن دينه فيمت وهو كافر فاو۟لئك حبطت اعملهم فى الدنيا والءاخرة واو۟لئك اصحب النار هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(2,218,'2:218','ان الذين ءامنوا۟ والذين هاجروا۟ وجهدوا۟ فى سبيل الله او۟لئك يرجون رحمت الله والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(2,219,'2:219','يسـلونك عن الخمر والميسر قل فيهما اثم كبير ومنفع للناس واثمهما اكبر من نفعهما ويسـلونك ماذا ينفقون قل العفو كذلك يبين الله لكم الءايت لعلكم تتفكرون');
INSERT INTO "uthmani_simple" VALUES(2,220,'2:220','فى الدنيا والءاخرة ويسـلونك عن اليتمى قل اصلاح لهم خير وان تخالطوهم فاخونكم والله يعلم المفسد من المصلح ولو شاء الله لاعنتكم ان الله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(2,221,'2:221','ولا تنكحوا۟ المشركت حتى يؤمن ولامة مؤمنة خير من مشركة ولو اعجبتكم ولا تنكحوا۟ المشركين حتى يؤمنوا۟ ولعبد مؤمن خير من مشرك ولو اعجبكم او۟لئك يدعون الى النار والله يدعوا۟ الى الجنة والمغفرة باذنه ويبين ءايته للناس لعلهم يتذكرون');
INSERT INTO "uthmani_simple" VALUES(2,222,'2:222','ويسـلونك عن المحيض قل هو اذى فاعتزلوا۟ النساء فى المحيض ولا تقربوهن حتى يطهرن فاذا تطهرن فاتوهن من حيث امركم الله ان الله يحب التوبين ويحب المتطهرين');
INSERT INTO "uthmani_simple" VALUES(2,223,'2:223','نساؤكم حرث لكم فاتوا۟ حرثكم انى شئتم وقدموا۟ لانفسكم واتقوا۟ الله واعلموا۟ انكم ملقوه وبشر المؤمنين');
INSERT INTO "uthmani_simple" VALUES(2,224,'2:224','ولا تجعلوا۟ الله عرضة لايمنكم ان تبروا۟ وتتقوا۟ وتصلحوا۟ بين الناس والله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(2,225,'2:225','لا يؤاخذكم الله باللغو فى ايمنكم ولكن يؤاخذكم بما كسبت قلوبكم والله غفور حليم');
INSERT INTO "uthmani_simple" VALUES(2,226,'2:226','للذين يؤلون من نسائهم تربص اربعة اشهر فان فاءو فان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(2,227,'2:227','وان عزموا۟ الطلق فان الله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(2,228,'2:228','والمطلقت يتربصن بانفسهن ثلثة قروء ولا يحل لهن ان يكتمن ما خلق الله فى ارحامهن ان كن يؤمن بالله واليوم الءاخر وبعولتهن احق بردهن فى ذلك ان ارادوا۟ اصلحا ولهن مثل الذى عليهن بالمعروف وللرجال عليهن درجة والله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(2,229,'2:229','الطلق مرتان فامساكۢ بمعروف او تسريحۢ باحسن ولا يحل لكم ان تاخذوا۟ مما ءاتيتموهن شيـا الا ان يخافا الا يقيما حدود الله فان خفتم الا يقيما حدود الله فلا جناح عليهما فيما افتدت به تلك حدود الله فلا تعتدوها ومن يتعد حدود الله فاو۟لئك هم الظلمون');
INSERT INTO "uthmani_simple" VALUES(2,230,'2:230','فان طلقها فلا تحل له منۢ بعد حتى تنكح زوجا غيره فان طلقها فلا جناح عليهما ان يتراجعا ان ظنا ان يقيما حدود الله وتلك حدود الله يبينها لقوم يعلمون');
INSERT INTO "uthmani_simple" VALUES(2,231,'2:231','واذا طلقتم النساء فبلغن اجلهن فامسكوهن بمعروف او سرحوهن بمعروف ولا تمسكوهن ضرارا لتعتدوا۟ ومن يفعل ذلك فقد ظلم نفسه ولا تتخذوا۟ ءايت الله هزوا واذكروا۟ نعمت الله عليكم وما انزل عليكم من الكتب والحكمة يعظكم به واتقوا۟ الله واعلموا۟ ان الله بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(2,232,'2:232','واذا طلقتم النساء فبلغن اجلهن فلا تعضلوهن ان ينكحن ازوجهن اذا ترضوا۟ بينهم بالمعروف ذلك يوعظ به من كان منكم يؤمن بالله واليوم الءاخر ذلكم ازكى لكم واطهر والله يعلم وانتم لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,233,'2:233','والولدت يرضعن اولدهن حولين كاملين لمن اراد ان يتم الرضاعة وعلى المولود له رزقهن وكسوتهن بالمعروف لا تكلف نفس الا وسعها لا تضار ولدةۢ بولدها ولا مولود له بولده وعلى الوارث مثل ذلك فان ارادا فصالا عن تراض منهما وتشاور فلا جناح عليهما وان اردتم ان تسترضعوا۟ اولدكم فلا جناح عليكم اذا سلمتم ما ءاتيتم بالمعروف واتقوا۟ الله واعلموا۟ ان الله بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(2,234,'2:234','والذين يتوفون منكم ويذرون ازوجا يتربصن بانفسهن اربعة اشهر وعشرا فاذا بلغن اجلهن فلا جناح عليكم فيما فعلن فى انفسهن بالمعروف والله بما تعملون خبير');
INSERT INTO "uthmani_simple" VALUES(2,235,'2:235','ولا جناح عليكم فيما عرضتم به من خطبة النساء او اكننتم فى انفسكم علم الله انكم ستذكرونهن ولكن لا تواعدوهن سرا الا ان تقولوا۟ قولا معروفا ولا تعزموا۟ عقدة النكاح حتى يبلغ الكتب اجله واعلموا۟ ان الله يعلم ما فى انفسكم فاحذروه واعلموا۟ ان الله غفور حليم');
INSERT INTO "uthmani_simple" VALUES(2,236,'2:236','لا جناح عليكم ان طلقتم النساء ما لم تمسوهن او تفرضوا۟ لهن فريضة ومتعوهن على الموسع قدره وعلى المقتر قدره متعۢا بالمعروف حقا على المحسنين');
INSERT INTO "uthmani_simple" VALUES(2,237,'2:237','وان طلقتموهن من قبل ان تمسوهن وقد فرضتم لهن فريضة فنصف ما فرضتم الا ان يعفون او يعفوا۟ الذى بيده عقدة النكاح وان تعفوا۟ اقرب للتقوى ولا تنسوا۟ الفضل بينكم ان الله بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(2,238,'2:238','حفظوا۟ على الصلوت والصلوة الوسطى وقوموا۟ لله قنتين');
INSERT INTO "uthmani_simple" VALUES(2,239,'2:239','فان خفتم فرجالا او ركبانا فاذا امنتم فاذكروا۟ الله كما علمكم ما لم تكونوا۟ تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,240,'2:240','والذين يتوفون منكم ويذرون ازوجا وصية لازوجهم متعا الى الحول غير اخراج فان خرجن فلا جناح عليكم فى ما فعلن فى انفسهن من معروف والله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(2,241,'2:241','وللمطلقت متعۢ بالمعروف حقا على المتقين');
INSERT INTO "uthmani_simple" VALUES(2,242,'2:242','كذلك يبين الله لكم ءايته لعلكم تعقلون');
INSERT INTO "uthmani_simple" VALUES(2,243,'2:243','الم تر الى الذين خرجوا۟ من ديرهم وهم الوف حذر الموت فقال لهم الله موتوا۟ ثم احيهم ان الله لذو فضل على الناس ولكن اكثر الناس لا يشكرون');
INSERT INTO "uthmani_simple" VALUES(2,244,'2:244','وقتلوا۟ فى سبيل الله واعلموا۟ ان الله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(2,245,'2:245','من ذا الذى يقرض الله قرضا حسنا فيضعفه له اضعافا كثيرة والله يقبض ويبصۜط واليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(2,246,'2:246','الم تر الى الملا منۢ بنى اسرءيل منۢ بعد موسى اذ قالوا۟ لنبى لهم ابعث لنا ملكا نقتل فى سبيل الله قال هل عسيتم ان كتب عليكم القتال الا تقتلوا۟ قالوا۟ وما لنا الا نقتل فى سبيل الله وقد اخرجنا من ديرنا وابنائنا فلما كتب عليهم القتال تولوا۟ الا قليلا منهم والله عليمۢ بالظلمين');
INSERT INTO "uthmani_simple" VALUES(2,247,'2:247','وقال لهم نبيهم ان الله قد بعث لكم طالوت ملكا قالوا۟ انى يكون له الملك علينا ونحن احق بالملك منه ولم يؤت سعة من المال قال ان الله اصطفىه عليكم وزاده بسطة فى العلم والجسم والله يؤتى ملكه من يشاء والله وسع عليم');
INSERT INTO "uthmani_simple" VALUES(2,248,'2:248','وقال لهم نبيهم ان ءاية ملكه ان ياتيكم التابوت فيه سكينة من ربكم وبقية مما ترك ءال موسى وءال هرون تحمله الملئكة ان فى ذلك لءاية لكم ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(2,249,'2:249','فلما فصل طالوت بالجنود قال ان الله مبتليكم بنهر فمن شرب منه فليس منى ومن لم يطعمه فانه منى الا من اغترف غرفةۢ بيده فشربوا۟ منه الا قليلا منهم فلما جاوزه هو والذين ءامنوا۟ معه قالوا۟ لا طاقة لنا اليوم بجالوت وجنوده قال الذين يظنون انهم ملقوا۟ الله كم من فئة قليلة غلبت فئة كثيرةۢ باذن الله والله مع الصبرين');
INSERT INTO "uthmani_simple" VALUES(2,250,'2:250','ولما برزوا۟ لجالوت وجنوده قالوا۟ ربنا افرغ علينا صبرا وثبت اقدامنا وانصرنا على القوم الكفرين');
INSERT INTO "uthmani_simple" VALUES(2,251,'2:251','فهزموهم باذن الله وقتل داود جالوت وءاتىه الله الملك والحكمة وعلمه مما يشاء ولولا دفع الله الناس بعضهم ببعض لفسدت الارض ولكن الله ذو فضل على العلمين');
INSERT INTO "uthmani_simple" VALUES(2,252,'2:252','تلك ءايت الله نتلوها عليك بالحق وانك لمن المرسلين');
INSERT INTO "uthmani_simple" VALUES(2,253,'2:253','تلك الرسل فضلنا بعضهم على بعض منهم من كلم الله ورفع بعضهم درجت وءاتينا عيسى ابن مريم البينت وايدنه بروح القدس ولو شاء الله ما اقتتل الذين منۢ بعدهم منۢ بعد ما جاءتهم البينت ولكن اختلفوا۟ فمنهم من ءامن ومنهم من كفر ولو شاء الله ما اقتتلوا۟ ولكن الله يفعل ما يريد');
INSERT INTO "uthmani_simple" VALUES(2,254,'2:254','يايها الذين ءامنوا۟ انفقوا۟ مما رزقنكم من قبل ان ياتى يوم لا بيع فيه ولا خلة ولا شفعة والكفرون هم الظلمون');
INSERT INTO "uthmani_simple" VALUES(2,255,'2:255','الله لا اله الا هو الحى القيوم لا تاخذه سنة ولا نوم له ما فى السموت وما فى الارض من ذا الذى يشفع عنده الا باذنه يعلم ما بين ايديهم وما خلفهم ولا يحيطون بشىء من علمه الا بما شاء وسع كرسيه السموت والارض ولا يـوده حفظهما وهو العلى العظيم');
INSERT INTO "uthmani_simple" VALUES(2,256,'2:256','لا اكراه فى الدين قد تبين الرشد من الغى فمن يكفر بالطغوت ويؤمنۢ بالله فقد استمسك بالعروة الوثقى لا انفصام لها والله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(2,257,'2:257','الله ولى الذين ءامنوا۟ يخرجهم من الظلمت الى النور والذين كفروا۟ اولياؤهم الطغوت يخرجونهم من النور الى الظلمت او۟لئك اصحب النار هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(2,258,'2:258','الم تر الى الذى حاج ابرهم فى ربه ان ءاتىه الله الملك اذ قال ابرهم ربى الذى يحى ويميت قال انا۠ احى واميت قال ابرهم فان الله ياتى بالشمس من المشرق فات بها من المغرب فبهت الذى كفر والله لا يهدى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(2,259,'2:259','او كالذى مر على قرية وهى خاوية على عروشها قال انى يحى هذه الله بعد موتها فاماته الله ما۟ئة عام ثم بعثه قال كم لبثت قال لبثت يوما او بعض يوم قال بل لبثت ما۟ئة عام فانظر الى طعامك وشرابك لم يتسنه وانظر الى حمارك ولنجعلك ءاية للناس وانظر الى العظام كيف ننشزها ثم نكسوها لحما فلما تبين له قال اعلم ان الله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(2,260,'2:260','واذ قال ابرهم رب ارنى كيف تحى الموتى قال اولم تؤمن قال بلى ولكن ليطمئن قلبى قال فخذ اربعة من الطير فصرهن اليك ثم اجعل على كل جبل منهن جزءا ثم ادعهن ياتينك سعيا واعلم ان الله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(2,261,'2:261','مثل الذين ينفقون امولهم فى سبيل الله كمثل حبة انۢبتت سبع سنابل فى كل سنۢبلة ما۟ئة حبة والله يضعف لمن يشاء والله وسع عليم');
INSERT INTO "uthmani_simple" VALUES(2,262,'2:262','الذين ينفقون امولهم فى سبيل الله ثم لا يتبعون ما انفقوا۟ منا ولا اذى لهم اجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(2,263,'2:263','قول معروف ومغفرة خير من صدقة يتبعها اذى والله غنى حليم');
INSERT INTO "uthmani_simple" VALUES(2,264,'2:264','يايها الذين ءامنوا۟ لا تبطلوا۟ صدقتكم بالمن والاذى كالذى ينفق ماله رئاء الناس ولا يؤمن بالله واليوم الءاخر فمثله كمثل صفوان عليه تراب فاصابه وابل فتركه صلدا لا يقدرون على شىء مما كسبوا۟ والله لا يهدى القوم الكفرين');
INSERT INTO "uthmani_simple" VALUES(2,265,'2:265','ومثل الذين ينفقون امولهم ابتغاء مرضات الله وتثبيتا من انفسهم كمثل جنةۭ بربوة اصابها وابل فـاتت اكلها ضعفين فان لم يصبها وابل فطل والله بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(2,266,'2:266','ايود احدكم ان تكون له جنة من نخيل واعناب تجرى من تحتها الانهر له فيها من كل الثمرت واصابه الكبر وله ذرية ضعفاء فاصابها اعصار فيه نار فاحترقت كذلك يبين الله لكم الءايت لعلكم تتفكرون');
INSERT INTO "uthmani_simple" VALUES(2,267,'2:267','يايها الذين ءامنوا۟ انفقوا۟ من طيبت ما كسبتم ومما اخرجنا لكم من الارض ولا تيمموا۟ الخبيث منه تنفقون ولستم بـاخذيه الا ان تغمضوا۟ فيه واعلموا۟ ان الله غنى حميد');
INSERT INTO "uthmani_simple" VALUES(2,268,'2:268','الشيطن يعدكم الفقر ويامركم بالفحشاء والله يعدكم مغفرة منه وفضلا والله وسع عليم');
INSERT INTO "uthmani_simple" VALUES(2,269,'2:269','يؤتى الحكمة من يشاء ومن يؤت الحكمة فقد اوتى خيرا كثيرا وما يذكر الا او۟لوا۟ الالبب');
INSERT INTO "uthmani_simple" VALUES(2,270,'2:270','وما انفقتم من نفقة او نذرتم من نذر فان الله يعلمه وما للظلمين من انصار');
INSERT INTO "uthmani_simple" VALUES(2,271,'2:271','ان تبدوا۟ الصدقت فنعما هى وان تخفوها وتؤتوها الفقراء فهو خير لكم ويكفر عنكم من سيـاتكم والله بما تعملون خبير');
INSERT INTO "uthmani_simple" VALUES(2,272,'2:272','ليس عليك هدىهم ولكن الله يهدى من يشاء وما تنفقوا۟ من خير فلانفسكم وما تنفقون الا ابتغاء وجه الله وما تنفقوا۟ من خير يوف اليكم وانتم لا تظلمون');
INSERT INTO "uthmani_simple" VALUES(2,273,'2:273','للفقراء الذين احصروا۟ فى سبيل الله لا يستطيعون ضربا فى الارض يحسبهم الجاهل اغنياء من التعفف تعرفهم بسيمهم لا يسـلون الناس الحافا وما تنفقوا۟ من خير فان الله به عليم');
INSERT INTO "uthmani_simple" VALUES(2,274,'2:274','الذين ينفقون امولهم باليل والنهار سرا وعلانية فلهم اجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(2,275,'2:275','الذين ياكلون الربوا۟ لا يقومون الا كما يقوم الذى يتخبطه الشيطن من المس ذلك بانهم قالوا۟ انما البيع مثل الربوا۟ واحل الله البيع وحرم الربوا۟ فمن جاءه موعظة من ربه فانتهى فله ما سلف وامره الى الله ومن عاد فاو۟لئك اصحب النار هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(2,276,'2:276','يمحق الله الربوا۟ ويربى الصدقت والله لا يحب كل كفار اثيم');
INSERT INTO "uthmani_simple" VALUES(2,277,'2:277','ان الذين ءامنوا۟ وعملوا۟ الصلحت واقاموا۟ الصلوة وءاتوا۟ الزكوة لهم اجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(2,278,'2:278','يايها الذين ءامنوا۟ اتقوا۟ الله وذروا۟ ما بقى من الربوا۟ ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(2,279,'2:279','فان لم تفعلوا۟ فاذنوا۟ بحرب من الله ورسوله وان تبتم فلكم رءوس امولكم لا تظلمون ولا تظلمون');
INSERT INTO "uthmani_simple" VALUES(2,280,'2:280','وان كان ذو عسرة فنظرة الى ميسرة وان تصدقوا۟ خير لكم ان كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(2,281,'2:281','واتقوا۟ يوما ترجعون فيه الى الله ثم توفى كل نفس ما كسبت وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(2,282,'2:282','يايها الذين ءامنوا۟ اذا تداينتم بدين الى اجل مسمى فاكتبوه وليكتب بينكم كاتبۢ بالعدل ولا ياب كاتب ان يكتب كما علمه الله فليكتب وليملل الذى عليه الحق وليتق الله ربه ولا يبخس منه شيـا فان كان الذى عليه الحق سفيها او ضعيفا او لا يستطيع ان يمل هو فليملل وليه بالعدل واستشهدوا۟ شهيدين من رجالكم فان لم يكونا رجلين فرجل وامراتان ممن ترضون من الشهداء ان تضل احدىهما فتذكر احدىهما الاخرى ولا ياب الشهداء اذا ما دعوا۟ ولا تسـموا۟ ان تكتبوه صغيرا او كبيرا الى اجله ذلكم اقسط عند الله واقوم للشهدة وادنى الا ترتابوا۟ الا ان تكون تجرة حاضرة تديرونها بينكم فليس عليكم جناح الا تكتبوها واشهدوا۟ اذا تبايعتم ولا يضار كاتب ولا شهيد وان تفعلوا۟ فانه فسوقۢ بكم واتقوا۟ الله ويعلمكم الله والله بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(2,283,'2:283','وان كنتم على سفر ولم تجدوا۟ كاتبا فرهن مقبوضة فان امن بعضكم بعضا فليؤد الذى اؤتمن امنته وليتق الله ربه ولا تكتموا۟ الشهدة ومن يكتمها فانه ءاثم قلبه والله بما تعملون عليم');
INSERT INTO "uthmani_simple" VALUES(2,284,'2:284','لله ما فى السموت وما فى الارض وان تبدوا۟ ما فى انفسكم او تخفوه يحاسبكم به الله فيغفر لمن يشاء ويعذب من يشاء والله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(2,285,'2:285','ءامن الرسول بما انزل اليه من ربه والمؤمنون كل ءامن بالله وملئكته وكتبه ورسله لا نفرق بين احد من رسله وقالوا۟ سمعنا واطعنا غفرانك ربنا واليك المصير');
INSERT INTO "uthmani_simple" VALUES(2,286,'2:286','لا يكلف الله نفسا الا وسعها لها ما كسبت وعليها ما اكتسبت ربنا لا تؤاخذنا ان نسينا او اخطانا ربنا ولا تحمل علينا اصرا كما حملته على الذين من قبلنا ربنا ولا تحملنا ما لا طاقة لنا به واعف عنا واغفر لنا وارحمنا انت مولىنا فانصرنا على القوم الكفرين');
INSERT INTO "uthmani_simple" VALUES(3,1,'3:1','الم');
INSERT INTO "uthmani_simple" VALUES(3,2,'3:2','الله لا اله الا هو الحى القيوم');
INSERT INTO "uthmani_simple" VALUES(3,3,'3:3','نزل عليك الكتب بالحق مصدقا لما بين يديه وانزل التورىة والانجيل');
INSERT INTO "uthmani_simple" VALUES(3,4,'3:4','من قبل هدى للناس وانزل الفرقان ان الذين كفروا۟ بـايت الله لهم عذاب شديد والله عزيز ذو انتقام');
INSERT INTO "uthmani_simple" VALUES(3,5,'3:5','ان الله لا يخفى عليه شىء فى الارض ولا فى السماء');
INSERT INTO "uthmani_simple" VALUES(3,6,'3:6','هو الذى يصوركم فى الارحام كيف يشاء لا اله الا هو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(3,7,'3:7','هو الذى انزل عليك الكتب منه ءايت محكمت هن ام الكتب واخر متشبهت فاما الذين فى قلوبهم زيغ فيتبعون ما تشبه منه ابتغاء الفتنة وابتغاء تاويله وما يعلم تاويله الا الله والرسخون فى العلم يقولون ءامنا به كل من عند ربنا وما يذكر الا او۟لوا۟ الالبب');
INSERT INTO "uthmani_simple" VALUES(3,8,'3:8','ربنا لا تزغ قلوبنا بعد اذ هديتنا وهب لنا من لدنك رحمة انك انت الوهاب');
INSERT INTO "uthmani_simple" VALUES(3,9,'3:9','ربنا انك جامع الناس ليوم لا ريب فيه ان الله لا يخلف الميعاد');
INSERT INTO "uthmani_simple" VALUES(3,10,'3:10','ان الذين كفروا۟ لن تغنى عنهم امولهم ولا اولدهم من الله شيـا واو۟لئك هم وقود النار');
INSERT INTO "uthmani_simple" VALUES(3,11,'3:11','كداب ءال فرعون والذين من قبلهم كذبوا۟ بـايتنا فاخذهم الله بذنوبهم والله شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(3,12,'3:12','قل للذين كفروا۟ ستغلبون وتحشرون الى جهنم وبئس المهاد');
INSERT INTO "uthmani_simple" VALUES(3,13,'3:13','قد كان لكم ءاية فى فئتين التقتا فئة تقتل فى سبيل الله واخرى كافرة يرونهم مثليهم راى العين والله يؤيد بنصره من يشاء ان فى ذلك لعبرة لاو۟لى الابصر');
INSERT INTO "uthmani_simple" VALUES(3,14,'3:14','زين للناس حب الشهوت من النساء والبنين والقنطير المقنطرة من الذهب والفضة والخيل المسومة والانعم والحرث ذلك متع الحيوة الدنيا والله عنده حسن المـاب');
INSERT INTO "uthmani_simple" VALUES(3,15,'3:15','قل اؤنبئكم بخير من ذلكم للذين اتقوا۟ عند ربهم جنت تجرى من تحتها الانهر خلدين فيها وازوج مطهرة ورضون من الله والله بصيرۢ بالعباد');
INSERT INTO "uthmani_simple" VALUES(3,16,'3:16','الذين يقولون ربنا اننا ءامنا فاغفر لنا ذنوبنا وقنا عذاب النار');
INSERT INTO "uthmani_simple" VALUES(3,17,'3:17','الصبرين والصدقين والقنتين والمنفقين والمستغفرين بالاسحار');
INSERT INTO "uthmani_simple" VALUES(3,18,'3:18','شهد الله انه لا اله الا هو والملئكة واو۟لوا۟ العلم قائمۢا بالقسط لا اله الا هو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(3,19,'3:19','ان الدين عند الله الاسلم وما اختلف الذين اوتوا۟ الكتب الا منۢ بعد ما جاءهم العلم بغيۢا بينهم ومن يكفر بـايت الله فان الله سريع الحساب');
INSERT INTO "uthmani_simple" VALUES(3,20,'3:20','فان حاجوك فقل اسلمت وجهى لله ومن اتبعن وقل للذين اوتوا۟ الكتب والامين ءاسلمتم فان اسلموا۟ فقد اهتدوا۟ وان تولوا۟ فانما عليك البلغ والله بصيرۢ بالعباد');
INSERT INTO "uthmani_simple" VALUES(3,21,'3:21','ان الذين يكفرون بـايت الله ويقتلون النبين بغير حق ويقتلون الذين يامرون بالقسط من الناس فبشرهم بعذاب اليم');
INSERT INTO "uthmani_simple" VALUES(3,22,'3:22','او۟لئك الذين حبطت اعملهم فى الدنيا والءاخرة وما لهم من نصرين');
INSERT INTO "uthmani_simple" VALUES(3,23,'3:23','الم تر الى الذين اوتوا۟ نصيبا من الكتب يدعون الى كتب الله ليحكم بينهم ثم يتولى فريق منهم وهم معرضون');
INSERT INTO "uthmani_simple" VALUES(3,24,'3:24','ذلك بانهم قالوا۟ لن تمسنا النار الا اياما معدودت وغرهم فى دينهم ما كانوا۟ يفترون');
INSERT INTO "uthmani_simple" VALUES(3,25,'3:25','فكيف اذا جمعنهم ليوم لا ريب فيه ووفيت كل نفس ما كسبت وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(3,26,'3:26','قل اللهم ملك الملك تؤتى الملك من تشاء وتنزع الملك ممن تشاء وتعز من تشاء وتذل من تشاء بيدك الخير انك على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(3,27,'3:27','تولج اليل فى النهار وتولج النهار فى اليل وتخرج الحى من الميت وتخرج الميت من الحى وترزق من تشاء بغير حساب');
INSERT INTO "uthmani_simple" VALUES(3,28,'3:28','لا يتخذ المؤمنون الكفرين اولياء من دون المؤمنين ومن يفعل ذلك فليس من الله فى شىء الا ان تتقوا۟ منهم تقىة ويحذركم الله نفسه والى الله المصير');
INSERT INTO "uthmani_simple" VALUES(3,29,'3:29','قل ان تخفوا۟ ما فى صدوركم او تبدوه يعلمه الله ويعلم ما فى السموت وما فى الارض والله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(3,30,'3:30','يوم تجد كل نفس ما عملت من خير محضرا وما عملت من سوء تود لو ان بينها وبينه امدۢا بعيدا ويحذركم الله نفسه والله رءوفۢ بالعباد');
INSERT INTO "uthmani_simple" VALUES(3,31,'3:31','قل ان كنتم تحبون الله فاتبعونى يحببكم الله ويغفر لكم ذنوبكم والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(3,32,'3:32','قل اطيعوا۟ الله والرسول فان تولوا۟ فان الله لا يحب الكفرين');
INSERT INTO "uthmani_simple" VALUES(3,33,'3:33','ان الله اصطفى ءادم ونوحا وءال ابرهيم وءال عمرن على العلمين');
INSERT INTO "uthmani_simple" VALUES(3,34,'3:34','ذريةۢ بعضها منۢ بعض والله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(3,35,'3:35','اذ قالت امرات عمرن رب انى نذرت لك ما فى بطنى محررا فتقبل منى انك انت السميع العليم');
INSERT INTO "uthmani_simple" VALUES(3,36,'3:36','فلما وضعتها قالت رب انى وضعتها انثى والله اعلم بما وضعت وليس الذكر كالانثى وانى سميتها مريم وانى اعيذها بك وذريتها من الشيطن الرجيم');
INSERT INTO "uthmani_simple" VALUES(3,37,'3:37','فتقبلها ربها بقبول حسن وانۢبتها نباتا حسنا وكفلها زكريا كلما دخل عليها زكريا المحراب وجد عندها رزقا قال يمريم انى لك هذا قالت هو من عند الله ان الله يرزق من يشاء بغير حساب');
INSERT INTO "uthmani_simple" VALUES(3,38,'3:38','هنالك دعا زكريا ربه قال رب هب لى من لدنك ذرية طيبة انك سميع الدعاء');
INSERT INTO "uthmani_simple" VALUES(3,39,'3:39','فنادته الملئكة وهو قائم يصلى فى المحراب ان الله يبشرك بيحيى مصدقۢا بكلمة من الله وسيدا وحصورا ونبيا من الصلحين');
INSERT INTO "uthmani_simple" VALUES(3,40,'3:40','قال رب انى يكون لى غلم وقد بلغنى الكبر وامراتى عاقر قال كذلك الله يفعل ما يشاء');
INSERT INTO "uthmani_simple" VALUES(3,41,'3:41','قال رب اجعل لى ءاية قال ءايتك الا تكلم الناس ثلثة ايام الا رمزا واذكر ربك كثيرا وسبح بالعشى والابكر');
INSERT INTO "uthmani_simple" VALUES(3,42,'3:42','واذ قالت الملئكة يمريم ان الله اصطفىك وطهرك واصطفىك على نساء العلمين');
INSERT INTO "uthmani_simple" VALUES(3,43,'3:43','يمريم اقنتى لربك واسجدى واركعى مع الركعين');
INSERT INTO "uthmani_simple" VALUES(3,44,'3:44','ذلك من انۢباء الغيب نوحيه اليك وما كنت لديهم اذ يلقون اقلمهم ايهم يكفل مريم وما كنت لديهم اذ يختصمون');
INSERT INTO "uthmani_simple" VALUES(3,45,'3:45','اذ قالت الملئكة يمريم ان الله يبشرك بكلمة منه اسمه المسيح عيسى ابن مريم وجيها فى الدنيا والءاخرة ومن المقربين');
INSERT INTO "uthmani_simple" VALUES(3,46,'3:46','ويكلم الناس فى المهد وكهلا ومن الصلحين');
INSERT INTO "uthmani_simple" VALUES(3,47,'3:47','قالت رب انى يكون لى ولد ولم يمسسنى بشر قال كذلك الله يخلق ما يشاء اذا قضى امرا فانما يقول له كن فيكون');
INSERT INTO "uthmani_simple" VALUES(3,48,'3:48','ويعلمه الكتب والحكمة والتورىة والانجيل');
INSERT INTO "uthmani_simple" VALUES(3,49,'3:49','ورسولا الى بنى اسرءيل انى قد جئتكم بـاية من ربكم انى اخلق لكم من الطين كهيـة الطير فانفخ فيه فيكون طيرۢا باذن الله وابرئ الاكمه والابرص واحى الموتى باذن الله وانبئكم بما تاكلون وما تدخرون فى بيوتكم ان فى ذلك لءاية لكم ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(3,50,'3:50','ومصدقا لما بين يدى من التورىة ولاحل لكم بعض الذى حرم عليكم وجئتكم بـاية من ربكم فاتقوا۟ الله واطيعون');
INSERT INTO "uthmani_simple" VALUES(3,51,'3:51','ان الله ربى وربكم فاعبدوه هذا صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(3,52,'3:52','فلما احس عيسى منهم الكفر قال من انصارى الى الله قال الحواريون نحن انصار الله ءامنا بالله واشهد بانا مسلمون');
INSERT INTO "uthmani_simple" VALUES(3,53,'3:53','ربنا ءامنا بما انزلت واتبعنا الرسول فاكتبنا مع الشهدين');
INSERT INTO "uthmani_simple" VALUES(3,54,'3:54','ومكروا۟ ومكر الله والله خير المكرين');
INSERT INTO "uthmani_simple" VALUES(3,55,'3:55','اذ قال الله يعيسى انى متوفيك ورافعك الى ومطهرك من الذين كفروا۟ وجاعل الذين اتبعوك فوق الذين كفروا۟ الى يوم القيمة ثم الى مرجعكم فاحكم بينكم فيما كنتم فيه تختلفون');
INSERT INTO "uthmani_simple" VALUES(3,56,'3:56','فاما الذين كفروا۟ فاعذبهم عذابا شديدا فى الدنيا والءاخرة وما لهم من نصرين');
INSERT INTO "uthmani_simple" VALUES(3,57,'3:57','واما الذين ءامنوا۟ وعملوا۟ الصلحت فيوفيهم اجورهم والله لا يحب الظلمين');
INSERT INTO "uthmani_simple" VALUES(3,58,'3:58','ذلك نتلوه عليك من الءايت والذكر الحكيم');
INSERT INTO "uthmani_simple" VALUES(3,59,'3:59','ان مثل عيسى عند الله كمثل ءادم خلقه من تراب ثم قال له كن فيكون');
INSERT INTO "uthmani_simple" VALUES(3,60,'3:60','الحق من ربك فلا تكن من الممترين');
INSERT INTO "uthmani_simple" VALUES(3,61,'3:61','فمن حاجك فيه منۢ بعد ما جاءك من العلم فقل تعالوا۟ ندع ابناءنا وابناءكم ونساءنا ونساءكم وانفسنا وانفسكم ثم نبتهل فنجعل لعنت الله على الكذبين');
INSERT INTO "uthmani_simple" VALUES(3,62,'3:62','ان هذا لهو القصص الحق وما من اله الا الله وان الله لهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(3,63,'3:63','فان تولوا۟ فان الله عليمۢ بالمفسدين');
INSERT INTO "uthmani_simple" VALUES(3,64,'3:64','قل ياهل الكتب تعالوا۟ الى كلمة سواءۭ بيننا وبينكم الا نعبد الا الله ولا نشرك به شيـا ولا يتخذ بعضنا بعضا اربابا من دون الله فان تولوا۟ فقولوا۟ اشهدوا۟ بانا مسلمون');
INSERT INTO "uthmani_simple" VALUES(3,65,'3:65','ياهل الكتب لم تحاجون فى ابرهيم وما انزلت التورىة والانجيل الا منۢ بعده افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(3,66,'3:66','هانتم هؤلاء حججتم فيما لكم به علم فلم تحاجون فيما ليس لكم به علم والله يعلم وانتم لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(3,67,'3:67','ما كان ابرهيم يهوديا ولا نصرانيا ولكن كان حنيفا مسلما وما كان من المشركين');
INSERT INTO "uthmani_simple" VALUES(3,68,'3:68','ان اولى الناس بابرهيم للذين اتبعوه وهذا النبى والذين ءامنوا۟ والله ولى المؤمنين');
INSERT INTO "uthmani_simple" VALUES(3,69,'3:69','ودت طائفة من اهل الكتب لو يضلونكم وما يضلون الا انفسهم وما يشعرون');
INSERT INTO "uthmani_simple" VALUES(3,70,'3:70','ياهل الكتب لم تكفرون بـايت الله وانتم تشهدون');
INSERT INTO "uthmani_simple" VALUES(3,71,'3:71','ياهل الكتب لم تلبسون الحق بالبطل وتكتمون الحق وانتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(3,72,'3:72','وقالت طائفة من اهل الكتب ءامنوا۟ بالذى انزل على الذين ءامنوا۟ وجه النهار واكفروا۟ ءاخره لعلهم يرجعون');
INSERT INTO "uthmani_simple" VALUES(3,73,'3:73','ولا تؤمنوا۟ الا لمن تبع دينكم قل ان الهدى هدى الله ان يؤتى احد مثل ما اوتيتم او يحاجوكم عند ربكم قل ان الفضل بيد الله يؤتيه من يشاء والله وسع عليم');
INSERT INTO "uthmani_simple" VALUES(3,74,'3:74','يختص برحمته من يشاء والله ذو الفضل العظيم');
INSERT INTO "uthmani_simple" VALUES(3,75,'3:75','ومن اهل الكتب من ان تامنه بقنطار يؤده اليك ومنهم من ان تامنه بدينار لا يؤده اليك الا ما دمت عليه قائما ذلك بانهم قالوا۟ ليس علينا فى الامين سبيل ويقولون على الله الكذب وهم يعلمون');
INSERT INTO "uthmani_simple" VALUES(3,76,'3:76','بلى من اوفى بعهده واتقى فان الله يحب المتقين');
INSERT INTO "uthmani_simple" VALUES(3,77,'3:77','ان الذين يشترون بعهد الله وايمنهم ثمنا قليلا او۟لئك لا خلق لهم فى الءاخرة ولا يكلمهم الله ولا ينظر اليهم يوم القيمة ولا يزكيهم ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(3,78,'3:78','وان منهم لفريقا يلون السنتهم بالكتب لتحسبوه من الكتب وما هو من الكتب ويقولون هو من عند الله وما هو من عند الله ويقولون على الله الكذب وهم يعلمون');
INSERT INTO "uthmani_simple" VALUES(3,79,'3:79','ما كان لبشر ان يؤتيه الله الكتب والحكم والنبوة ثم يقول للناس كونوا۟ عبادا لى من دون الله ولكن كونوا۟ ربنين بما كنتم تعلمون الكتب وبما كنتم تدرسون');
INSERT INTO "uthmani_simple" VALUES(3,80,'3:80','ولا يامركم ان تتخذوا۟ الملئكة والنبين اربابا ايامركم بالكفر بعد اذ انتم مسلمون');
INSERT INTO "uthmani_simple" VALUES(3,81,'3:81','واذ اخذ الله ميثق النبين لما ءاتيتكم من كتب وحكمة ثم جاءكم رسول مصدق لما معكم لتؤمنن به ولتنصرنه قال ءاقررتم واخذتم على ذلكم اصرى قالوا۟ اقررنا قال فاشهدوا۟ وانا۠ معكم من الشهدين');
INSERT INTO "uthmani_simple" VALUES(3,82,'3:82','فمن تولى بعد ذلك فاو۟لئك هم الفسقون');
INSERT INTO "uthmani_simple" VALUES(3,83,'3:83','افغير دين الله يبغون وله اسلم من فى السموت والارض طوعا وكرها واليه يرجعون');
INSERT INTO "uthmani_simple" VALUES(3,84,'3:84','قل ءامنا بالله وما انزل علينا وما انزل على ابرهيم واسمعيل واسحق ويعقوب والاسباط وما اوتى موسى وعيسى والنبيون من ربهم لا نفرق بين احد منهم ونحن له مسلمون');
INSERT INTO "uthmani_simple" VALUES(3,85,'3:85','ومن يبتغ غير الاسلم دينا فلن يقبل منه وهو فى الءاخرة من الخسرين');
INSERT INTO "uthmani_simple" VALUES(3,86,'3:86','كيف يهدى الله قوما كفروا۟ بعد ايمنهم وشهدوا۟ ان الرسول حق وجاءهم البينت والله لا يهدى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(3,87,'3:87','او۟لئك جزاؤهم ان عليهم لعنة الله والملئكة والناس اجمعين');
INSERT INTO "uthmani_simple" VALUES(3,88,'3:88','خلدين فيها لا يخفف عنهم العذاب ولا هم ينظرون');
INSERT INTO "uthmani_simple" VALUES(3,89,'3:89','الا الذين تابوا۟ منۢ بعد ذلك واصلحوا۟ فان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(3,90,'3:90','ان الذين كفروا۟ بعد ايمنهم ثم ازدادوا۟ كفرا لن تقبل توبتهم واو۟لئك هم الضالون');
INSERT INTO "uthmani_simple" VALUES(3,91,'3:91','ان الذين كفروا۟ وماتوا۟ وهم كفار فلن يقبل من احدهم ملء الارض ذهبا ولو افتدى به او۟لئك لهم عذاب اليم وما لهم من نصرين');
INSERT INTO "uthmani_simple" VALUES(3,92,'3:92','لن تنالوا۟ البر حتى تنفقوا۟ مما تحبون وما تنفقوا۟ من شىء فان الله به عليم');
INSERT INTO "uthmani_simple" VALUES(3,93,'3:93','كل الطعام كان حلا لبنى اسرءيل الا ما حرم اسرءيل على نفسه من قبل ان تنزل التورىة قل فاتوا۟ بالتورىة فاتلوها ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(3,94,'3:94','فمن افترى على الله الكذب منۢ بعد ذلك فاو۟لئك هم الظلمون');
INSERT INTO "uthmani_simple" VALUES(3,95,'3:95','قل صدق الله فاتبعوا۟ ملة ابرهيم حنيفا وما كان من المشركين');
INSERT INTO "uthmani_simple" VALUES(3,96,'3:96','ان اول بيت وضع للناس للذى ببكة مباركا وهدى للعلمين');
INSERT INTO "uthmani_simple" VALUES(3,97,'3:97','فيه ءايتۢ بينت مقام ابرهيم ومن دخله كان ءامنا ولله على الناس حج البيت من استطاع اليه سبيلا ومن كفر فان الله غنى عن العلمين');
INSERT INTO "uthmani_simple" VALUES(3,98,'3:98','قل ياهل الكتب لم تكفرون بـايت الله والله شهيد على ما تعملون');
INSERT INTO "uthmani_simple" VALUES(3,99,'3:99','قل ياهل الكتب لم تصدون عن سبيل الله من ءامن تبغونها عوجا وانتم شهداء وما الله بغفل عما تعملون');
INSERT INTO "uthmani_simple" VALUES(3,100,'3:100','يايها الذين ءامنوا۟ ان تطيعوا۟ فريقا من الذين اوتوا۟ الكتب يردوكم بعد ايمنكم كفرين');
INSERT INTO "uthmani_simple" VALUES(3,101,'3:101','وكيف تكفرون وانتم تتلى عليكم ءايت الله وفيكم رسوله ومن يعتصم بالله فقد هدى الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(3,102,'3:102','يايها الذين ءامنوا۟ اتقوا۟ الله حق تقاته ولا تموتن الا وانتم مسلمون');
INSERT INTO "uthmani_simple" VALUES(3,103,'3:103','واعتصموا۟ بحبل الله جميعا ولا تفرقوا۟ واذكروا۟ نعمت الله عليكم اذ كنتم اعداء فالف بين قلوبكم فاصبحتم بنعمته اخونا وكنتم على شفا حفرة من النار فانقذكم منها كذلك يبين الله لكم ءايته لعلكم تهتدون');
INSERT INTO "uthmani_simple" VALUES(3,104,'3:104','ولتكن منكم امة يدعون الى الخير ويامرون بالمعروف وينهون عن المنكر واو۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(3,105,'3:105','ولا تكونوا۟ كالذين تفرقوا۟ واختلفوا۟ منۢ بعد ما جاءهم البينت واو۟لئك لهم عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(3,106,'3:106','يوم تبيض وجوه وتسود وجوه فاما الذين اسودت وجوههم اكفرتم بعد ايمنكم فذوقوا۟ العذاب بما كنتم تكفرون');
INSERT INTO "uthmani_simple" VALUES(3,107,'3:107','واما الذين ابيضت وجوههم ففى رحمة الله هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(3,108,'3:108','تلك ءايت الله نتلوها عليك بالحق وما الله يريد ظلما للعلمين');
INSERT INTO "uthmani_simple" VALUES(3,109,'3:109','ولله ما فى السموت وما فى الارض والى الله ترجع الامور');
INSERT INTO "uthmani_simple" VALUES(3,110,'3:110','كنتم خير امة اخرجت للناس تامرون بالمعروف وتنهون عن المنكر وتؤمنون بالله ولو ءامن اهل الكتب لكان خيرا لهم منهم المؤمنون واكثرهم الفسقون');
INSERT INTO "uthmani_simple" VALUES(3,111,'3:111','لن يضروكم الا اذى وان يقتلوكم يولوكم الادبار ثم لا ينصرون');
INSERT INTO "uthmani_simple" VALUES(3,112,'3:112','ضربت عليهم الذلة اين ما ثقفوا۟ الا بحبل من الله وحبل من الناس وباءو بغضب من الله وضربت عليهم المسكنة ذلك بانهم كانوا۟ يكفرون بـايت الله ويقتلون الانۢبياء بغير حق ذلك بما عصوا۟ وكانوا۟ يعتدون');
INSERT INTO "uthmani_simple" VALUES(3,113,'3:113','ليسوا۟ سواء من اهل الكتب امة قائمة يتلون ءايت الله ءاناء اليل وهم يسجدون');
INSERT INTO "uthmani_simple" VALUES(3,114,'3:114','يؤمنون بالله واليوم الءاخر ويامرون بالمعروف وينهون عن المنكر ويسرعون فى الخيرت واو۟لئك من الصلحين');
INSERT INTO "uthmani_simple" VALUES(3,115,'3:115','وما يفعلوا۟ من خير فلن يكفروه والله عليمۢ بالمتقين');
INSERT INTO "uthmani_simple" VALUES(3,116,'3:116','ان الذين كفروا۟ لن تغنى عنهم امولهم ولا اولدهم من الله شيـا واو۟لئك اصحب النار هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(3,117,'3:117','مثل ما ينفقون فى هذه الحيوة الدنيا كمثل ريح فيها صر اصابت حرث قوم ظلموا۟ انفسهم فاهلكته وما ظلمهم الله ولكن انفسهم يظلمون');
INSERT INTO "uthmani_simple" VALUES(3,118,'3:118','يايها الذين ءامنوا۟ لا تتخذوا۟ بطانة من دونكم لا يالونكم خبالا ودوا۟ ما عنتم قد بدت البغضاء من افوههم وما تخفى صدورهم اكبر قد بينا لكم الءايت ان كنتم تعقلون');
INSERT INTO "uthmani_simple" VALUES(3,119,'3:119','هانتم او۟لاء تحبونهم ولا يحبونكم وتؤمنون بالكتب كله واذا لقوكم قالوا۟ ءامنا واذا خلوا۟ عضوا۟ عليكم الانامل من الغيظ قل موتوا۟ بغيظكم ان الله عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(3,120,'3:120','ان تمسسكم حسنة تسؤهم وان تصبكم سيئة يفرحوا۟ بها وان تصبروا۟ وتتقوا۟ لا يضركم كيدهم شيـا ان الله بما يعملون محيط');
INSERT INTO "uthmani_simple" VALUES(3,121,'3:121','واذ غدوت من اهلك تبوئ المؤمنين مقعد للقتال والله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(3,122,'3:122','اذ همت طائفتان منكم ان تفشلا والله وليهما وعلى الله فليتوكل المؤمنون');
INSERT INTO "uthmani_simple" VALUES(3,123,'3:123','ولقد نصركم الله ببدر وانتم اذلة فاتقوا۟ الله لعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(3,124,'3:124','اذ تقول للمؤمنين الن يكفيكم ان يمدكم ربكم بثلثة ءالف من الملئكة منزلين');
INSERT INTO "uthmani_simple" VALUES(3,125,'3:125','بلى ان تصبروا۟ وتتقوا۟ وياتوكم من فورهم هذا يمددكم ربكم بخمسة ءالف من الملئكة مسومين');
INSERT INTO "uthmani_simple" VALUES(3,126,'3:126','وما جعله الله الا بشرى لكم ولتطمئن قلوبكم به وما النصر الا من عند الله العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(3,127,'3:127','ليقطع طرفا من الذين كفروا۟ او يكبتهم فينقلبوا۟ خائبين');
INSERT INTO "uthmani_simple" VALUES(3,128,'3:128','ليس لك من الامر شىء او يتوب عليهم او يعذبهم فانهم ظلمون');
INSERT INTO "uthmani_simple" VALUES(3,129,'3:129','ولله ما فى السموت وما فى الارض يغفر لمن يشاء ويعذب من يشاء والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(3,130,'3:130','يايها الذين ءامنوا۟ لا تاكلوا۟ الربوا۟ اضعفا مضعفة واتقوا۟ الله لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(3,131,'3:131','واتقوا۟ النار التى اعدت للكفرين');
INSERT INTO "uthmani_simple" VALUES(3,132,'3:132','واطيعوا۟ الله والرسول لعلكم ترحمون');
INSERT INTO "uthmani_simple" VALUES(3,133,'3:133','وسارعوا۟ الى مغفرة من ربكم وجنة عرضها السموت والارض اعدت للمتقين');
INSERT INTO "uthmani_simple" VALUES(3,134,'3:134','الذين ينفقون فى السراء والضراء والكظمين الغيظ والعافين عن الناس والله يحب المحسنين');
INSERT INTO "uthmani_simple" VALUES(3,135,'3:135','والذين اذا فعلوا۟ فحشة او ظلموا۟ انفسهم ذكروا۟ الله فاستغفروا۟ لذنوبهم ومن يغفر الذنوب الا الله ولم يصروا۟ على ما فعلوا۟ وهم يعلمون');
INSERT INTO "uthmani_simple" VALUES(3,136,'3:136','او۟لئك جزاؤهم مغفرة من ربهم وجنت تجرى من تحتها الانهر خلدين فيها ونعم اجر العملين');
INSERT INTO "uthmani_simple" VALUES(3,137,'3:137','قد خلت من قبلكم سنن فسيروا۟ فى الارض فانظروا۟ كيف كان عقبة المكذبين');
INSERT INTO "uthmani_simple" VALUES(3,138,'3:138','هذا بيان للناس وهدى وموعظة للمتقين');
INSERT INTO "uthmani_simple" VALUES(3,139,'3:139','ولا تهنوا۟ ولا تحزنوا۟ وانتم الاعلون ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(3,140,'3:140','ان يمسسكم قرح فقد مس القوم قرح مثله وتلك الايام نداولها بين الناس وليعلم الله الذين ءامنوا۟ ويتخذ منكم شهداء والله لا يحب الظلمين');
INSERT INTO "uthmani_simple" VALUES(3,141,'3:141','وليمحص الله الذين ءامنوا۟ ويمحق الكفرين');
INSERT INTO "uthmani_simple" VALUES(3,142,'3:142','ام حسبتم ان تدخلوا۟ الجنة ولما يعلم الله الذين جهدوا۟ منكم ويعلم الصبرين');
INSERT INTO "uthmani_simple" VALUES(3,143,'3:143','ولقد كنتم تمنون الموت من قبل ان تلقوه فقد رايتموه وانتم تنظرون');
INSERT INTO "uthmani_simple" VALUES(3,144,'3:144','وما محمد الا رسول قد خلت من قبله الرسل افاي۟ن مات او قتل انقلبتم على اعقبكم ومن ينقلب على عقبيه فلن يضر الله شيـا وسيجزى الله الشكرين');
INSERT INTO "uthmani_simple" VALUES(3,145,'3:145','وما كان لنفس ان تموت الا باذن الله كتبا مؤجلا ومن يرد ثواب الدنيا نؤته منها ومن يرد ثواب الءاخرة نؤته منها وسنجزى الشكرين');
INSERT INTO "uthmani_simple" VALUES(3,146,'3:146','وكاين من نبى قتل معه ربيون كثير فما وهنوا۟ لما اصابهم فى سبيل الله وما ضعفوا۟ وما استكانوا۟ والله يحب الصبرين');
INSERT INTO "uthmani_simple" VALUES(3,147,'3:147','وما كان قولهم الا ان قالوا۟ ربنا اغفر لنا ذنوبنا واسرافنا فى امرنا وثبت اقدامنا وانصرنا على القوم الكفرين');
INSERT INTO "uthmani_simple" VALUES(3,148,'3:148','فـاتىهم الله ثواب الدنيا وحسن ثواب الءاخرة والله يحب المحسنين');
INSERT INTO "uthmani_simple" VALUES(3,149,'3:149','يايها الذين ءامنوا۟ ان تطيعوا۟ الذين كفروا۟ يردوكم على اعقبكم فتنقلبوا۟ خسرين');
INSERT INTO "uthmani_simple" VALUES(3,150,'3:150','بل الله مولىكم وهو خير النصرين');
INSERT INTO "uthmani_simple" VALUES(3,151,'3:151','سنلقى فى قلوب الذين كفروا۟ الرعب بما اشركوا۟ بالله ما لم ينزل به سلطنا وماوىهم النار وبئس مثوى الظلمين');
INSERT INTO "uthmani_simple" VALUES(3,152,'3:152','ولقد صدقكم الله وعده اذ تحسونهم باذنه حتى اذا فشلتم وتنزعتم فى الامر وعصيتم منۢ بعد ما ارىكم ما تحبون منكم من يريد الدنيا ومنكم من يريد الءاخرة ثم صرفكم عنهم ليبتليكم ولقد عفا عنكم والله ذو فضل على المؤمنين');
INSERT INTO "uthmani_simple" VALUES(3,153,'3:153','اذ تصعدون ولا تلون على احد والرسول يدعوكم فى اخرىكم فاثبكم غمۢا بغم لكيلا تحزنوا۟ على ما فاتكم ولا ما اصبكم والله خبيرۢ بما تعملون');
INSERT INTO "uthmani_simple" VALUES(3,154,'3:154','ثم انزل عليكم منۢ بعد الغم امنة نعاسا يغشى طائفة منكم وطائفة قد اهمتهم انفسهم يظنون بالله غير الحق ظن الجهلية يقولون هل لنا من الامر من شىء قل ان الامر كله لله يخفون فى انفسهم ما لا يبدون لك يقولون لو كان لنا من الامر شىء ما قتلنا ههنا قل لو كنتم فى بيوتكم لبرز الذين كتب عليهم القتل الى مضاجعهم وليبتلى الله ما فى صدوركم وليمحص ما فى قلوبكم والله عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(3,155,'3:155','ان الذين تولوا۟ منكم يوم التقى الجمعان انما استزلهم الشيطن ببعض ما كسبوا۟ ولقد عفا الله عنهم ان الله غفور حليم');
INSERT INTO "uthmani_simple" VALUES(3,156,'3:156','يايها الذين ءامنوا۟ لا تكونوا۟ كالذين كفروا۟ وقالوا۟ لاخونهم اذا ضربوا۟ فى الارض او كانوا۟ غزى لو كانوا۟ عندنا ما ماتوا۟ وما قتلوا۟ ليجعل الله ذلك حسرة فى قلوبهم والله يحى ويميت والله بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(3,157,'3:157','ولئن قتلتم فى سبيل الله او متم لمغفرة من الله ورحمة خير مما يجمعون');
INSERT INTO "uthmani_simple" VALUES(3,158,'3:158','ولئن متم او قتلتم لالى الله تحشرون');
INSERT INTO "uthmani_simple" VALUES(3,159,'3:159','فبما رحمة من الله لنت لهم ولو كنت فظا غليظ القلب لانفضوا۟ من حولك فاعف عنهم واستغفر لهم وشاورهم فى الامر فاذا عزمت فتوكل على الله ان الله يحب المتوكلين');
INSERT INTO "uthmani_simple" VALUES(3,160,'3:160','ان ينصركم الله فلا غالب لكم وان يخذلكم فمن ذا الذى ينصركم منۢ بعده وعلى الله فليتوكل المؤمنون');
INSERT INTO "uthmani_simple" VALUES(3,161,'3:161','وما كان لنبى ان يغل ومن يغلل يات بما غل يوم القيمة ثم توفى كل نفس ما كسبت وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(3,162,'3:162','افمن اتبع رضون الله كمنۢ باء بسخط من الله وماوىه جهنم وبئس المصير');
INSERT INTO "uthmani_simple" VALUES(3,163,'3:163','هم درجت عند الله والله بصيرۢ بما يعملون');
INSERT INTO "uthmani_simple" VALUES(3,164,'3:164','لقد من الله على المؤمنين اذ بعث فيهم رسولا من انفسهم يتلوا۟ عليهم ءايته ويزكيهم ويعلمهم الكتب والحكمة وان كانوا۟ من قبل لفى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(3,165,'3:165','اولما اصبتكم مصيبة قد اصبتم مثليها قلتم انى هذا قل هو من عند انفسكم ان الله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(3,166,'3:166','وما اصبكم يوم التقى الجمعان فباذن الله وليعلم المؤمنين');
INSERT INTO "uthmani_simple" VALUES(3,167,'3:167','وليعلم الذين نافقوا۟ وقيل لهم تعالوا۟ قتلوا۟ فى سبيل الله او ادفعوا۟ قالوا۟ لو نعلم قتالا لاتبعنكم هم للكفر يومئذ اقرب منهم للايمن يقولون بافوههم ما ليس فى قلوبهم والله اعلم بما يكتمون');
INSERT INTO "uthmani_simple" VALUES(3,168,'3:168','الذين قالوا۟ لاخونهم وقعدوا۟ لو اطاعونا ما قتلوا۟ قل فادرءوا۟ عن انفسكم الموت ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(3,169,'3:169','ولا تحسبن الذين قتلوا۟ فى سبيل الله اموتۢا بل احياء عند ربهم يرزقون');
INSERT INTO "uthmani_simple" VALUES(3,170,'3:170','فرحين بما ءاتىهم الله من فضله ويستبشرون بالذين لم يلحقوا۟ بهم من خلفهم الا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(3,171,'3:171','يستبشرون بنعمة من الله وفضل وان الله لا يضيع اجر المؤمنين');
INSERT INTO "uthmani_simple" VALUES(3,172,'3:172','الذين استجابوا۟ لله والرسول منۢ بعد ما اصابهم القرح للذين احسنوا۟ منهم واتقوا۟ اجر عظيم');
INSERT INTO "uthmani_simple" VALUES(3,173,'3:173','الذين قال لهم الناس ان الناس قد جمعوا۟ لكم فاخشوهم فزادهم ايمنا وقالوا۟ حسبنا الله ونعم الوكيل');
INSERT INTO "uthmani_simple" VALUES(3,174,'3:174','فانقلبوا۟ بنعمة من الله وفضل لم يمسسهم سوء واتبعوا۟ رضون الله والله ذو فضل عظيم');
INSERT INTO "uthmani_simple" VALUES(3,175,'3:175','انما ذلكم الشيطن يخوف اولياءه فلا تخافوهم وخافون ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(3,176,'3:176','ولا يحزنك الذين يسرعون فى الكفر انهم لن يضروا۟ الله شيـا يريد الله الا يجعل لهم حظا فى الءاخرة ولهم عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(3,177,'3:177','ان الذين اشتروا۟ الكفر بالايمن لن يضروا۟ الله شيـا ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(3,178,'3:178','ولا يحسبن الذين كفروا۟ انما نملى لهم خير لانفسهم انما نملى لهم ليزدادوا۟ اثما ولهم عذاب مهين');
INSERT INTO "uthmani_simple" VALUES(3,179,'3:179','ما كان الله ليذر المؤمنين على ما انتم عليه حتى يميز الخبيث من الطيب وما كان الله ليطلعكم على الغيب ولكن الله يجتبى من رسله من يشاء فـامنوا۟ بالله ورسله وان تؤمنوا۟ وتتقوا۟ فلكم اجر عظيم');
INSERT INTO "uthmani_simple" VALUES(3,180,'3:180','ولا يحسبن الذين يبخلون بما ءاتىهم الله من فضله هو خيرا لهم بل هو شر لهم سيطوقون ما بخلوا۟ به يوم القيمة ولله ميرث السموت والارض والله بما تعملون خبير');
INSERT INTO "uthmani_simple" VALUES(3,181,'3:181','لقد سمع الله قول الذين قالوا۟ ان الله فقير ونحن اغنياء سنكتب ما قالوا۟ وقتلهم الانۢبياء بغير حق ونقول ذوقوا۟ عذاب الحريق');
INSERT INTO "uthmani_simple" VALUES(3,182,'3:182','ذلك بما قدمت ايديكم وان الله ليس بظلام للعبيد');
INSERT INTO "uthmani_simple" VALUES(3,183,'3:183','الذين قالوا۟ ان الله عهد الينا الا نؤمن لرسول حتى ياتينا بقربان تاكله النار قل قد جاءكم رسل من قبلى بالبينت وبالذى قلتم فلم قتلتموهم ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(3,184,'3:184','فان كذبوك فقد كذب رسل من قبلك جاءو بالبينت والزبر والكتب المنير');
INSERT INTO "uthmani_simple" VALUES(3,185,'3:185','كل نفس ذائقة الموت وانما توفون اجوركم يوم القيمة فمن زحزح عن النار وادخل الجنة فقد فاز وما الحيوة الدنيا الا متع الغرور');
INSERT INTO "uthmani_simple" VALUES(3,186,'3:186','لتبلون فى امولكم وانفسكم ولتسمعن من الذين اوتوا۟ الكتب من قبلكم ومن الذين اشركوا۟ اذى كثيرا وان تصبروا۟ وتتقوا۟ فان ذلك من عزم الامور');
INSERT INTO "uthmani_simple" VALUES(3,187,'3:187','واذ اخذ الله ميثق الذين اوتوا۟ الكتب لتبيننه للناس ولا تكتمونه فنبذوه وراء ظهورهم واشتروا۟ به ثمنا قليلا فبئس ما يشترون');
INSERT INTO "uthmani_simple" VALUES(3,188,'3:188','لا تحسبن الذين يفرحون بما اتوا۟ ويحبون ان يحمدوا۟ بما لم يفعلوا۟ فلا تحسبنهم بمفازة من العذاب ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(3,189,'3:189','ولله ملك السموت والارض والله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(3,190,'3:190','ان فى خلق السموت والارض واختلف اليل والنهار لءايت لاو۟لى الالبب');
INSERT INTO "uthmani_simple" VALUES(3,191,'3:191','الذين يذكرون الله قيما وقعودا وعلى جنوبهم ويتفكرون فى خلق السموت والارض ربنا ما خلقت هذا بطلا سبحنك فقنا عذاب النار');
INSERT INTO "uthmani_simple" VALUES(3,192,'3:192','ربنا انك من تدخل النار فقد اخزيته وما للظلمين من انصار');
INSERT INTO "uthmani_simple" VALUES(3,193,'3:193','ربنا اننا سمعنا مناديا ينادى للايمن ان ءامنوا۟ بربكم فـامنا ربنا فاغفر لنا ذنوبنا وكفر عنا سيـاتنا وتوفنا مع الابرار');
INSERT INTO "uthmani_simple" VALUES(3,194,'3:194','ربنا وءاتنا ما وعدتنا على رسلك ولا تخزنا يوم القيمة انك لا تخلف الميعاد');
INSERT INTO "uthmani_simple" VALUES(3,195,'3:195','فاستجاب لهم ربهم انى لا اضيع عمل عمل منكم من ذكر او انثى بعضكم منۢ بعض فالذين هاجروا۟ واخرجوا۟ من ديرهم واوذوا۟ فى سبيلى وقتلوا۟ وقتلوا۟ لاكفرن عنهم سيـاتهم ولادخلنهم جنت تجرى من تحتها الانهر ثوابا من عند الله والله عنده حسن الثواب');
INSERT INTO "uthmani_simple" VALUES(3,196,'3:196','لا يغرنك تقلب الذين كفروا۟ فى البلد');
INSERT INTO "uthmani_simple" VALUES(3,197,'3:197','متع قليل ثم ماوىهم جهنم وبئس المهاد');
INSERT INTO "uthmani_simple" VALUES(3,198,'3:198','لكن الذين اتقوا۟ ربهم لهم جنت تجرى من تحتها الانهر خلدين فيها نزلا من عند الله وما عند الله خير للابرار');
INSERT INTO "uthmani_simple" VALUES(3,199,'3:199','وان من اهل الكتب لمن يؤمن بالله وما انزل اليكم وما انزل اليهم خشعين لله لا يشترون بـايت الله ثمنا قليلا او۟لئك لهم اجرهم عند ربهم ان الله سريع الحساب');
INSERT INTO "uthmani_simple" VALUES(3,200,'3:200','يايها الذين ءامنوا۟ اصبروا۟ وصابروا۟ ورابطوا۟ واتقوا۟ الله لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(4,1,'4:1','يايها الناس اتقوا۟ ربكم الذى خلقكم من نفس وحدة وخلق منها زوجها وبث منهما رجالا كثيرا ونساء واتقوا۟ الله الذى تساءلون به والارحام ان الله كان عليكم رقيبا');
INSERT INTO "uthmani_simple" VALUES(4,2,'4:2','وءاتوا۟ اليتمى امولهم ولا تتبدلوا۟ الخبيث بالطيب ولا تاكلوا۟ امولهم الى امولكم انه كان حوبا كبيرا');
INSERT INTO "uthmani_simple" VALUES(4,3,'4:3','وان خفتم الا تقسطوا۟ فى اليتمى فانكحوا۟ ما طاب لكم من النساء مثنى وثلث وربع فان خفتم الا تعدلوا۟ فوحدة او ما ملكت ايمنكم ذلك ادنى الا تعولوا۟');
INSERT INTO "uthmani_simple" VALUES(4,4,'4:4','وءاتوا۟ النساء صدقتهن نحلة فان طبن لكم عن شىء منه نفسا فكلوه هنيـا مريـا');
INSERT INTO "uthmani_simple" VALUES(4,5,'4:5','ولا تؤتوا۟ السفهاء امولكم التى جعل الله لكم قيما وارزقوهم فيها واكسوهم وقولوا۟ لهم قولا معروفا');
INSERT INTO "uthmani_simple" VALUES(4,6,'4:6','وابتلوا۟ اليتمى حتى اذا بلغوا۟ النكاح فان ءانستم منهم رشدا فادفعوا۟ اليهم امولهم ولا تاكلوها اسرافا وبدارا ان يكبروا۟ ومن كان غنيا فليستعفف ومن كان فقيرا فلياكل بالمعروف فاذا دفعتم اليهم امولهم فاشهدوا۟ عليهم وكفى بالله حسيبا');
INSERT INTO "uthmani_simple" VALUES(4,7,'4:7','للرجال نصيب مما ترك الولدان والاقربون وللنساء نصيب مما ترك الولدان والاقربون مما قل منه او كثر نصيبا مفروضا');
INSERT INTO "uthmani_simple" VALUES(4,8,'4:8','واذا حضر القسمة او۟لوا۟ القربى واليتمى والمسكين فارزقوهم منه وقولوا۟ لهم قولا معروفا');
INSERT INTO "uthmani_simple" VALUES(4,9,'4:9','وليخش الذين لو تركوا۟ من خلفهم ذرية ضعفا خافوا۟ عليهم فليتقوا۟ الله وليقولوا۟ قولا سديدا');
INSERT INTO "uthmani_simple" VALUES(4,10,'4:10','ان الذين ياكلون امول اليتمى ظلما انما ياكلون فى بطونهم نارا وسيصلون سعيرا');
INSERT INTO "uthmani_simple" VALUES(4,11,'4:11','يوصيكم الله فى اولدكم للذكر مثل حظ الانثيين فان كن نساء فوق اثنتين فلهن ثلثا ما ترك وان كانت وحدة فلها النصف ولابويه لكل وحد منهما السدس مما ترك ان كان له ولد فان لم يكن له ولد وورثه ابواه فلامه الثلث فان كان له اخوة فلامه السدس منۢ بعد وصية يوصى بها او دين ءاباؤكم وابناؤكم لا تدرون ايهم اقرب لكم نفعا فريضة من الله ان الله كان عليما حكيما');
INSERT INTO "uthmani_simple" VALUES(4,12,'4:12','ولكم نصف ما ترك ازوجكم ان لم يكن لهن ولد فان كان لهن ولد فلكم الربع مما تركن منۢ بعد وصية يوصين بها او دين ولهن الربع مما تركتم ان لم يكن لكم ولد فان كان لكم ولد فلهن الثمن مما تركتم منۢ بعد وصية توصون بها او دين وان كان رجل يورث كللة او امراة وله اخ او اخت فلكل وحد منهما السدس فان كانوا۟ اكثر من ذلك فهم شركاء فى الثلث منۢ بعد وصية يوصى بها او دين غير مضار وصية من الله والله عليم حليم');
INSERT INTO "uthmani_simple" VALUES(4,13,'4:13','تلك حدود الله ومن يطع الله ورسوله يدخله جنت تجرى من تحتها الانهر خلدين فيها وذلك الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(4,14,'4:14','ومن يعص الله ورسوله ويتعد حدوده يدخله نارا خلدا فيها وله عذاب مهين');
INSERT INTO "uthmani_simple" VALUES(4,15,'4:15','والتى ياتين الفحشة من نسائكم فاستشهدوا۟ عليهن اربعة منكم فان شهدوا۟ فامسكوهن فى البيوت حتى يتوفىهن الموت او يجعل الله لهن سبيلا');
INSERT INTO "uthmani_simple" VALUES(4,16,'4:16','والذان ياتينها منكم فـاذوهما فان تابا واصلحا فاعرضوا۟ عنهما ان الله كان توابا رحيما');
INSERT INTO "uthmani_simple" VALUES(4,17,'4:17','انما التوبة على الله للذين يعملون السوء بجهلة ثم يتوبون من قريب فاو۟لئك يتوب الله عليهم وكان الله عليما حكيما');
INSERT INTO "uthmani_simple" VALUES(4,18,'4:18','وليست التوبة للذين يعملون السيـات حتى اذا حضر احدهم الموت قال انى تبت الـن ولا الذين يموتون وهم كفار او۟لئك اعتدنا لهم عذابا اليما');
INSERT INTO "uthmani_simple" VALUES(4,19,'4:19','يايها الذين ءامنوا۟ لا يحل لكم ان ترثوا۟ النساء كرها ولا تعضلوهن لتذهبوا۟ ببعض ما ءاتيتموهن الا ان ياتين بفحشة مبينة وعاشروهن بالمعروف فان كرهتموهن فعسى ان تكرهوا۟ شيـا ويجعل الله فيه خيرا كثيرا');
INSERT INTO "uthmani_simple" VALUES(4,20,'4:20','وان اردتم استبدال زوج مكان زوج وءاتيتم احدىهن قنطارا فلا تاخذوا۟ منه شيـا اتاخذونه بهتنا واثما مبينا');
INSERT INTO "uthmani_simple" VALUES(4,21,'4:21','وكيف تاخذونه وقد افضى بعضكم الى بعض واخذن منكم ميثقا غليظا');
INSERT INTO "uthmani_simple" VALUES(4,22,'4:22','ولا تنكحوا۟ ما نكح ءاباؤكم من النساء الا ما قد سلف انه كان فحشة ومقتا وساء سبيلا');
INSERT INTO "uthmani_simple" VALUES(4,23,'4:23','حرمت عليكم امهتكم وبناتكم واخوتكم وعمتكم وخلتكم وبنات الاخ وبنات الاخت وامهتكم التى ارضعنكم واخوتكم من الرضعة وامهت نسائكم وربئبكم التى فى حجوركم من نسائكم التى دخلتم بهن فان لم تكونوا۟ دخلتم بهن فلا جناح عليكم وحلئل ابنائكم الذين من اصلبكم وان تجمعوا۟ بين الاختين الا ما قد سلف ان الله كان غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(4,24,'4:24','والمحصنت من النساء الا ما ملكت ايمنكم كتب الله عليكم واحل لكم ما وراء ذلكم ان تبتغوا۟ بامولكم محصنين غير مسفحين فما استمتعتم به منهن فـاتوهن اجورهن فريضة ولا جناح عليكم فيما ترضيتم به منۢ بعد الفريضة ان الله كان عليما حكيما');
INSERT INTO "uthmani_simple" VALUES(4,25,'4:25','ومن لم يستطع منكم طولا ان ينكح المحصنت المؤمنت فمن ما ملكت ايمنكم من فتيتكم المؤمنت والله اعلم بايمنكم بعضكم منۢ بعض فانكحوهن باذن اهلهن وءاتوهن اجورهن بالمعروف محصنت غير مسفحت ولا متخذت اخدان فاذا احصن فان اتين بفحشة فعليهن نصف ما على المحصنت من العذاب ذلك لمن خشى العنت منكم وان تصبروا۟ خير لكم والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(4,26,'4:26','يريد الله ليبين لكم ويهديكم سنن الذين من قبلكم ويتوب عليكم والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(4,27,'4:27','والله يريد ان يتوب عليكم ويريد الذين يتبعون الشهوت ان تميلوا۟ ميلا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,28,'4:28','يريد الله ان يخفف عنكم وخلق الانسن ضعيفا');
INSERT INTO "uthmani_simple" VALUES(4,29,'4:29','يايها الذين ءامنوا۟ لا تاكلوا۟ امولكم بينكم بالبطل الا ان تكون تجرة عن تراض منكم ولا تقتلوا۟ انفسكم ان الله كان بكم رحيما');
INSERT INTO "uthmani_simple" VALUES(4,30,'4:30','ومن يفعل ذلك عدونا وظلما فسوف نصليه نارا وكان ذلك على الله يسيرا');
INSERT INTO "uthmani_simple" VALUES(4,31,'4:31','ان تجتنبوا۟ كبائر ما تنهون عنه نكفر عنكم سيـاتكم وندخلكم مدخلا كريما');
INSERT INTO "uthmani_simple" VALUES(4,32,'4:32','ولا تتمنوا۟ ما فضل الله به بعضكم على بعض للرجال نصيب مما اكتسبوا۟ وللنساء نصيب مما اكتسبن وسـلوا۟ الله من فضله ان الله كان بكل شىء عليما');
INSERT INTO "uthmani_simple" VALUES(4,33,'4:33','ولكل جعلنا مولى مما ترك الولدان والاقربون والذين عقدت ايمنكم فـاتوهم نصيبهم ان الله كان على كل شىء شهيدا');
INSERT INTO "uthmani_simple" VALUES(4,34,'4:34','الرجال قومون على النساء بما فضل الله بعضهم على بعض وبما انفقوا۟ من امولهم فالصلحت قنتت حفظت للغيب بما حفظ الله والتى تخافون نشوزهن فعظوهن واهجروهن فى المضاجع واضربوهن فان اطعنكم فلا تبغوا۟ عليهن سبيلا ان الله كان عليا كبيرا');
INSERT INTO "uthmani_simple" VALUES(4,35,'4:35','وان خفتم شقاق بينهما فابعثوا۟ حكما من اهله وحكما من اهلها ان يريدا اصلحا يوفق الله بينهما ان الله كان عليما خبيرا');
INSERT INTO "uthmani_simple" VALUES(4,36,'4:36','واعبدوا۟ الله ولا تشركوا۟ به شيـا وبالولدين احسنا وبذى القربى واليتمى والمسكين والجار ذى القربى والجار الجنب والصاحب بالجنۢب وابن السبيل وما ملكت ايمنكم ان الله لا يحب من كان مختالا فخورا');
INSERT INTO "uthmani_simple" VALUES(4,37,'4:37','الذين يبخلون ويامرون الناس بالبخل ويكتمون ما ءاتىهم الله من فضله واعتدنا للكفرين عذابا مهينا');
INSERT INTO "uthmani_simple" VALUES(4,38,'4:38','والذين ينفقون امولهم رئاء الناس ولا يؤمنون بالله ولا باليوم الءاخر ومن يكن الشيطن له قرينا فساء قرينا');
INSERT INTO "uthmani_simple" VALUES(4,39,'4:39','وماذا عليهم لو ءامنوا۟ بالله واليوم الءاخر وانفقوا۟ مما رزقهم الله وكان الله بهم عليما');
INSERT INTO "uthmani_simple" VALUES(4,40,'4:40','ان الله لا يظلم مثقال ذرة وان تك حسنة يضعفها ويؤت من لدنه اجرا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,41,'4:41','فكيف اذا جئنا من كل امةۭ بشهيد وجئنا بك على هؤلاء شهيدا');
INSERT INTO "uthmani_simple" VALUES(4,42,'4:42','يومئذ يود الذين كفروا۟ وعصوا۟ الرسول لو تسوى بهم الارض ولا يكتمون الله حديثا');
INSERT INTO "uthmani_simple" VALUES(4,43,'4:43','يايها الذين ءامنوا۟ لا تقربوا۟ الصلوة وانتم سكرى حتى تعلموا۟ ما تقولون ولا جنبا الا عابرى سبيل حتى تغتسلوا۟ وان كنتم مرضى او على سفر او جاء احد منكم من الغائط او لمستم النساء فلم تجدوا۟ ماء فتيمموا۟ صعيدا طيبا فامسحوا۟ بوجوهكم وايديكم ان الله كان عفوا غفورا');
INSERT INTO "uthmani_simple" VALUES(4,44,'4:44','الم تر الى الذين اوتوا۟ نصيبا من الكتب يشترون الضللة ويريدون ان تضلوا۟ السبيل');
INSERT INTO "uthmani_simple" VALUES(4,45,'4:45','والله اعلم باعدائكم وكفى بالله وليا وكفى بالله نصيرا');
INSERT INTO "uthmani_simple" VALUES(4,46,'4:46','من الذين هادوا۟ يحرفون الكلم عن مواضعه ويقولون سمعنا وعصينا واسمع غير مسمع ورعنا ليۢا بالسنتهم وطعنا فى الدين ولو انهم قالوا۟ سمعنا واطعنا واسمع وانظرنا لكان خيرا لهم واقوم ولكن لعنهم الله بكفرهم فلا يؤمنون الا قليلا');
INSERT INTO "uthmani_simple" VALUES(4,47,'4:47','يايها الذين اوتوا۟ الكتب ءامنوا۟ بما نزلنا مصدقا لما معكم من قبل ان نطمس وجوها فنردها على ادبارها او نلعنهم كما لعنا اصحب السبت وكان امر الله مفعولا');
INSERT INTO "uthmani_simple" VALUES(4,48,'4:48','ان الله لا يغفر ان يشرك به ويغفر ما دون ذلك لمن يشاء ومن يشرك بالله فقد افترى اثما عظيما');
INSERT INTO "uthmani_simple" VALUES(4,49,'4:49','الم تر الى الذين يزكون انفسهم بل الله يزكى من يشاء ولا يظلمون فتيلا');
INSERT INTO "uthmani_simple" VALUES(4,50,'4:50','انظر كيف يفترون على الله الكذب وكفى به اثما مبينا');
INSERT INTO "uthmani_simple" VALUES(4,51,'4:51','الم تر الى الذين اوتوا۟ نصيبا من الكتب يؤمنون بالجبت والطغوت ويقولون للذين كفروا۟ هؤلاء اهدى من الذين ءامنوا۟ سبيلا');
INSERT INTO "uthmani_simple" VALUES(4,52,'4:52','او۟لئك الذين لعنهم الله ومن يلعن الله فلن تجد له نصيرا');
INSERT INTO "uthmani_simple" VALUES(4,53,'4:53','ام لهم نصيب من الملك فاذا لا يؤتون الناس نقيرا');
INSERT INTO "uthmani_simple" VALUES(4,54,'4:54','ام يحسدون الناس على ما ءاتىهم الله من فضله فقد ءاتينا ءال ابرهيم الكتب والحكمة وءاتينهم ملكا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,55,'4:55','فمنهم من ءامن به ومنهم من صد عنه وكفى بجهنم سعيرا');
INSERT INTO "uthmani_simple" VALUES(4,56,'4:56','ان الذين كفروا۟ بـايتنا سوف نصليهم نارا كلما نضجت جلودهم بدلنهم جلودا غيرها ليذوقوا۟ العذاب ان الله كان عزيزا حكيما');
INSERT INTO "uthmani_simple" VALUES(4,57,'4:57','والذين ءامنوا۟ وعملوا۟ الصلحت سندخلهم جنت تجرى من تحتها الانهر خلدين فيها ابدا لهم فيها ازوج مطهرة وندخلهم ظلا ظليلا');
INSERT INTO "uthmani_simple" VALUES(4,58,'4:58','ان الله يامركم ان تؤدوا۟ الامنت الى اهلها واذا حكمتم بين الناس ان تحكموا۟ بالعدل ان الله نعما يعظكم به ان الله كان سميعۢا بصيرا');
INSERT INTO "uthmani_simple" VALUES(4,59,'4:59','يايها الذين ءامنوا۟ اطيعوا۟ الله واطيعوا۟ الرسول واو۟لى الامر منكم فان تنزعتم فى شىء فردوه الى الله والرسول ان كنتم تؤمنون بالله واليوم الءاخر ذلك خير واحسن تاويلا');
INSERT INTO "uthmani_simple" VALUES(4,60,'4:60','الم تر الى الذين يزعمون انهم ءامنوا۟ بما انزل اليك وما انزل من قبلك يريدون ان يتحاكموا۟ الى الطغوت وقد امروا۟ ان يكفروا۟ به ويريد الشيطن ان يضلهم ضللۢا بعيدا');
INSERT INTO "uthmani_simple" VALUES(4,61,'4:61','واذا قيل لهم تعالوا۟ الى ما انزل الله والى الرسول رايت المنفقين يصدون عنك صدودا');
INSERT INTO "uthmani_simple" VALUES(4,62,'4:62','فكيف اذا اصبتهم مصيبةۢ بما قدمت ايديهم ثم جاءوك يحلفون بالله ان اردنا الا احسنا وتوفيقا');
INSERT INTO "uthmani_simple" VALUES(4,63,'4:63','او۟لئك الذين يعلم الله ما فى قلوبهم فاعرض عنهم وعظهم وقل لهم فى انفسهم قولۢا بليغا');
INSERT INTO "uthmani_simple" VALUES(4,64,'4:64','وما ارسلنا من رسول الا ليطاع باذن الله ولو انهم اذ ظلموا۟ انفسهم جاءوك فاستغفروا۟ الله واستغفر لهم الرسول لوجدوا۟ الله توابا رحيما');
INSERT INTO "uthmani_simple" VALUES(4,65,'4:65','فلا وربك لا يؤمنون حتى يحكموك فيما شجر بينهم ثم لا يجدوا۟ فى انفسهم حرجا مما قضيت ويسلموا۟ تسليما');
INSERT INTO "uthmani_simple" VALUES(4,66,'4:66','ولو انا كتبنا عليهم ان اقتلوا۟ انفسكم او اخرجوا۟ من ديركم ما فعلوه الا قليل منهم ولو انهم فعلوا۟ ما يوعظون به لكان خيرا لهم واشد تثبيتا');
INSERT INTO "uthmani_simple" VALUES(4,67,'4:67','واذا لءاتينهم من لدنا اجرا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,68,'4:68','ولهدينهم صرطا مستقيما');
INSERT INTO "uthmani_simple" VALUES(4,69,'4:69','ومن يطع الله والرسول فاو۟لئك مع الذين انعم الله عليهم من النبين والصديقين والشهداء والصلحين وحسن او۟لئك رفيقا');
INSERT INTO "uthmani_simple" VALUES(4,70,'4:70','ذلك الفضل من الله وكفى بالله عليما');
INSERT INTO "uthmani_simple" VALUES(4,71,'4:71','يايها الذين ءامنوا۟ خذوا۟ حذركم فانفروا۟ ثبات او انفروا۟ جميعا');
INSERT INTO "uthmani_simple" VALUES(4,72,'4:72','وان منكم لمن ليبطئن فان اصبتكم مصيبة قال قد انعم الله على اذ لم اكن معهم شهيدا');
INSERT INTO "uthmani_simple" VALUES(4,73,'4:73','ولئن اصبكم فضل من الله ليقولن كان لم تكنۢ بينكم وبينه مودة يليتنى كنت معهم فافوز فوزا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,74,'4:74','فليقتل فى سبيل الله الذين يشرون الحيوة الدنيا بالءاخرة ومن يقتل فى سبيل الله فيقتل او يغلب فسوف نؤتيه اجرا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,75,'4:75','وما لكم لا تقتلون فى سبيل الله والمستضعفين من الرجال والنساء والولدن الذين يقولون ربنا اخرجنا من هذه القرية الظالم اهلها واجعل لنا من لدنك وليا واجعل لنا من لدنك نصيرا');
INSERT INTO "uthmani_simple" VALUES(4,76,'4:76','الذين ءامنوا۟ يقتلون فى سبيل الله والذين كفروا۟ يقتلون فى سبيل الطغوت فقتلوا۟ اولياء الشيطن ان كيد الشيطن كان ضعيفا');
INSERT INTO "uthmani_simple" VALUES(4,77,'4:77','الم تر الى الذين قيل لهم كفوا۟ ايديكم واقيموا۟ الصلوة وءاتوا۟ الزكوة فلما كتب عليهم القتال اذا فريق منهم يخشون الناس كخشية الله او اشد خشية وقالوا۟ ربنا لم كتبت علينا القتال لولا اخرتنا الى اجل قريب قل متع الدنيا قليل والءاخرة خير لمن اتقى ولا تظلمون فتيلا');
INSERT INTO "uthmani_simple" VALUES(4,78,'4:78','اينما تكونوا۟ يدرككم الموت ولو كنتم فى بروج مشيدة وان تصبهم حسنة يقولوا۟ هذه من عند الله وان تصبهم سيئة يقولوا۟ هذه من عندك قل كل من عند الله فمال هؤلاء القوم لا يكادون يفقهون حديثا');
INSERT INTO "uthmani_simple" VALUES(4,79,'4:79','ما اصابك من حسنة فمن الله وما اصابك من سيئة فمن نفسك وارسلنك للناس رسولا وكفى بالله شهيدا');
INSERT INTO "uthmani_simple" VALUES(4,80,'4:80','من يطع الرسول فقد اطاع الله ومن تولى فما ارسلنك عليهم حفيظا');
INSERT INTO "uthmani_simple" VALUES(4,81,'4:81','ويقولون طاعة فاذا برزوا۟ من عندك بيت طائفة منهم غير الذى تقول والله يكتب ما يبيتون فاعرض عنهم وتوكل على الله وكفى بالله وكيلا');
INSERT INTO "uthmani_simple" VALUES(4,82,'4:82','افلا يتدبرون القرءان ولو كان من عند غير الله لوجدوا۟ فيه اختلفا كثيرا');
INSERT INTO "uthmani_simple" VALUES(4,83,'4:83','واذا جاءهم امر من الامن او الخوف اذاعوا۟ به ولو ردوه الى الرسول والى او۟لى الامر منهم لعلمه الذين يستنۢبطونه منهم ولولا فضل الله عليكم ورحمته لاتبعتم الشيطن الا قليلا');
INSERT INTO "uthmani_simple" VALUES(4,84,'4:84','فقتل فى سبيل الله لا تكلف الا نفسك وحرض المؤمنين عسى الله ان يكف باس الذين كفروا۟ والله اشد باسا واشد تنكيلا');
INSERT INTO "uthmani_simple" VALUES(4,85,'4:85','من يشفع شفعة حسنة يكن له نصيب منها ومن يشفع شفعة سيئة يكن له كفل منها وكان الله على كل شىء مقيتا');
INSERT INTO "uthmani_simple" VALUES(4,86,'4:86','واذا حييتم بتحية فحيوا۟ باحسن منها او ردوها ان الله كان على كل شىء حسيبا');
INSERT INTO "uthmani_simple" VALUES(4,87,'4:87','الله لا اله الا هو ليجمعنكم الى يوم القيمة لا ريب فيه ومن اصدق من الله حديثا');
INSERT INTO "uthmani_simple" VALUES(4,88,'4:88','فما لكم فى المنفقين فئتين والله اركسهم بما كسبوا۟ اتريدون ان تهدوا۟ من اضل الله ومن يضلل الله فلن تجد له سبيلا');
INSERT INTO "uthmani_simple" VALUES(4,89,'4:89','ودوا۟ لو تكفرون كما كفروا۟ فتكونون سواء فلا تتخذوا۟ منهم اولياء حتى يهاجروا۟ فى سبيل الله فان تولوا۟ فخذوهم واقتلوهم حيث وجدتموهم ولا تتخذوا۟ منهم وليا ولا نصيرا');
INSERT INTO "uthmani_simple" VALUES(4,90,'4:90','الا الذين يصلون الى قومۭ بينكم وبينهم ميثق او جاءوكم حصرت صدورهم ان يقتلوكم او يقتلوا۟ قومهم ولو شاء الله لسلطهم عليكم فلقتلوكم فان اعتزلوكم فلم يقتلوكم والقوا۟ اليكم السلم فما جعل الله لكم عليهم سبيلا');
INSERT INTO "uthmani_simple" VALUES(4,91,'4:91','ستجدون ءاخرين يريدون ان يامنوكم ويامنوا۟ قومهم كل ما ردوا۟ الى الفتنة اركسوا۟ فيها فان لم يعتزلوكم ويلقوا۟ اليكم السلم ويكفوا۟ ايديهم فخذوهم واقتلوهم حيث ثقفتموهم واو۟لئكم جعلنا لكم عليهم سلطنا مبينا');
INSERT INTO "uthmani_simple" VALUES(4,92,'4:92','وما كان لمؤمن ان يقتل مؤمنا الا خطـا ومن قتل مؤمنا خطـا فتحرير رقبة مؤمنة ودية مسلمة الى اهله الا ان يصدقوا۟ فان كان من قوم عدو لكم وهو مؤمن فتحرير رقبة مؤمنة وان كان من قومۭ بينكم وبينهم ميثق فدية مسلمة الى اهله وتحرير رقبة مؤمنة فمن لم يجد فصيام شهرين متتابعين توبة من الله وكان الله عليما حكيما');
INSERT INTO "uthmani_simple" VALUES(4,93,'4:93','ومن يقتل مؤمنا متعمدا فجزاؤه جهنم خلدا فيها وغضب الله عليه ولعنه واعد له عذابا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,94,'4:94','يايها الذين ءامنوا۟ اذا ضربتم فى سبيل الله فتبينوا۟ ولا تقولوا۟ لمن القى اليكم السلم لست مؤمنا تبتغون عرض الحيوة الدنيا فعند الله مغانم كثيرة كذلك كنتم من قبل فمن الله عليكم فتبينوا۟ ان الله كان بما تعملون خبيرا');
INSERT INTO "uthmani_simple" VALUES(4,95,'4:95','لا يستوى القعدون من المؤمنين غير او۟لى الضرر والمجهدون فى سبيل الله بامولهم وانفسهم فضل الله المجهدين بامولهم وانفسهم على القعدين درجة وكلا وعد الله الحسنى وفضل الله المجهدين على القعدين اجرا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,96,'4:96','درجت منه ومغفرة ورحمة وكان الله غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(4,97,'4:97','ان الذين توفىهم الملئكة ظالمى انفسهم قالوا۟ فيم كنتم قالوا۟ كنا مستضعفين فى الارض قالوا۟ الم تكن ارض الله وسعة فتهاجروا۟ فيها فاو۟لئك ماوىهم جهنم وساءت مصيرا');
INSERT INTO "uthmani_simple" VALUES(4,98,'4:98','الا المستضعفين من الرجال والنساء والولدن لا يستطيعون حيلة ولا يهتدون سبيلا');
INSERT INTO "uthmani_simple" VALUES(4,99,'4:99','فاو۟لئك عسى الله ان يعفو عنهم وكان الله عفوا غفورا');
INSERT INTO "uthmani_simple" VALUES(4,100,'4:100','ومن يهاجر فى سبيل الله يجد فى الارض مرغما كثيرا وسعة ومن يخرج منۢ بيته مهاجرا الى الله ورسوله ثم يدركه الموت فقد وقع اجره على الله وكان الله غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(4,101,'4:101','واذا ضربتم فى الارض فليس عليكم جناح ان تقصروا۟ من الصلوة ان خفتم ان يفتنكم الذين كفروا۟ ان الكفرين كانوا۟ لكم عدوا مبينا');
INSERT INTO "uthmani_simple" VALUES(4,102,'4:102','واذا كنت فيهم فاقمت لهم الصلوة فلتقم طائفة منهم معك ولياخذوا۟ اسلحتهم فاذا سجدوا۟ فليكونوا۟ من ورائكم ولتات طائفة اخرى لم يصلوا۟ فليصلوا۟ معك ولياخذوا۟ حذرهم واسلحتهم ود الذين كفروا۟ لو تغفلون عن اسلحتكم وامتعتكم فيميلون عليكم ميلة وحدة ولا جناح عليكم ان كان بكم اذى من مطر او كنتم مرضى ان تضعوا۟ اسلحتكم وخذوا۟ حذركم ان الله اعد للكفرين عذابا مهينا');
INSERT INTO "uthmani_simple" VALUES(4,103,'4:103','فاذا قضيتم الصلوة فاذكروا۟ الله قيما وقعودا وعلى جنوبكم فاذا اطماننتم فاقيموا۟ الصلوة ان الصلوة كانت على المؤمنين كتبا موقوتا');
INSERT INTO "uthmani_simple" VALUES(4,104,'4:104','ولا تهنوا۟ فى ابتغاء القوم ان تكونوا۟ تالمون فانهم يالمون كما تالمون وترجون من الله ما لا يرجون وكان الله عليما حكيما');
INSERT INTO "uthmani_simple" VALUES(4,105,'4:105','انا انزلنا اليك الكتب بالحق لتحكم بين الناس بما ارىك الله ولا تكن للخائنين خصيما');
INSERT INTO "uthmani_simple" VALUES(4,106,'4:106','واستغفر الله ان الله كان غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(4,107,'4:107','ولا تجدل عن الذين يختانون انفسهم ان الله لا يحب من كان خوانا اثيما');
INSERT INTO "uthmani_simple" VALUES(4,108,'4:108','يستخفون من الناس ولا يستخفون من الله وهو معهم اذ يبيتون ما لا يرضى من القول وكان الله بما يعملون محيطا');
INSERT INTO "uthmani_simple" VALUES(4,109,'4:109','هانتم هؤلاء جدلتم عنهم فى الحيوة الدنيا فمن يجدل الله عنهم يوم القيمة ام من يكون عليهم وكيلا');
INSERT INTO "uthmani_simple" VALUES(4,110,'4:110','ومن يعمل سوءا او يظلم نفسه ثم يستغفر الله يجد الله غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(4,111,'4:111','ومن يكسب اثما فانما يكسبه على نفسه وكان الله عليما حكيما');
INSERT INTO "uthmani_simple" VALUES(4,112,'4:112','ومن يكسب خطيـة او اثما ثم يرم به بريـا فقد احتمل بهتنا واثما مبينا');
INSERT INTO "uthmani_simple" VALUES(4,113,'4:113','ولولا فضل الله عليك ورحمته لهمت طائفة منهم ان يضلوك وما يضلون الا انفسهم وما يضرونك من شىء وانزل الله عليك الكتب والحكمة وعلمك ما لم تكن تعلم وكان فضل الله عليك عظيما');
INSERT INTO "uthmani_simple" VALUES(4,114,'4:114','لا خير فى كثير من نجوىهم الا من امر بصدقة او معروف او اصلحۭ بين الناس ومن يفعل ذلك ابتغاء مرضات الله فسوف نؤتيه اجرا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,115,'4:115','ومن يشاقق الرسول منۢ بعد ما تبين له الهدى ويتبع غير سبيل المؤمنين نوله ما تولى ونصله جهنم وساءت مصيرا');
INSERT INTO "uthmani_simple" VALUES(4,116,'4:116','ان الله لا يغفر ان يشرك به ويغفر ما دون ذلك لمن يشاء ومن يشرك بالله فقد ضل ضللۢا بعيدا');
INSERT INTO "uthmani_simple" VALUES(4,117,'4:117','ان يدعون من دونه الا انثا وان يدعون الا شيطنا مريدا');
INSERT INTO "uthmani_simple" VALUES(4,118,'4:118','لعنه الله وقال لاتخذن من عبادك نصيبا مفروضا');
INSERT INTO "uthmani_simple" VALUES(4,119,'4:119','ولاضلنهم ولامنينهم ولءامرنهم فليبتكن ءاذان الانعم ولءامرنهم فليغيرن خلق الله ومن يتخذ الشيطن وليا من دون الله فقد خسر خسرانا مبينا');
INSERT INTO "uthmani_simple" VALUES(4,120,'4:120','يعدهم ويمنيهم وما يعدهم الشيطن الا غرورا');
INSERT INTO "uthmani_simple" VALUES(4,121,'4:121','او۟لئك ماوىهم جهنم ولا يجدون عنها محيصا');
INSERT INTO "uthmani_simple" VALUES(4,122,'4:122','والذين ءامنوا۟ وعملوا۟ الصلحت سندخلهم جنت تجرى من تحتها الانهر خلدين فيها ابدا وعد الله حقا ومن اصدق من الله قيلا');
INSERT INTO "uthmani_simple" VALUES(4,123,'4:123','ليس بامانيكم ولا امانى اهل الكتب من يعمل سوءا يجز به ولا يجد له من دون الله وليا ولا نصيرا');
INSERT INTO "uthmani_simple" VALUES(4,124,'4:124','ومن يعمل من الصلحت من ذكر او انثى وهو مؤمن فاو۟لئك يدخلون الجنة ولا يظلمون نقيرا');
INSERT INTO "uthmani_simple" VALUES(4,125,'4:125','ومن احسن دينا ممن اسلم وجهه لله وهو محسن واتبع ملة ابرهيم حنيفا واتخذ الله ابرهيم خليلا');
INSERT INTO "uthmani_simple" VALUES(4,126,'4:126','ولله ما فى السموت وما فى الارض وكان الله بكل شىء محيطا');
INSERT INTO "uthmani_simple" VALUES(4,127,'4:127','ويستفتونك فى النساء قل الله يفتيكم فيهن وما يتلى عليكم فى الكتب فى يتمى النساء التى لا تؤتونهن ما كتب لهن وترغبون ان تنكحوهن والمستضعفين من الولدن وان تقوموا۟ لليتمى بالقسط وما تفعلوا۟ من خير فان الله كان به عليما');
INSERT INTO "uthmani_simple" VALUES(4,128,'4:128','وان امراة خافت منۢ بعلها نشوزا او اعراضا فلا جناح عليهما ان يصلحا بينهما صلحا والصلح خير واحضرت الانفس الشح وان تحسنوا۟ وتتقوا۟ فان الله كان بما تعملون خبيرا');
INSERT INTO "uthmani_simple" VALUES(4,129,'4:129','ولن تستطيعوا۟ ان تعدلوا۟ بين النساء ولو حرصتم فلا تميلوا۟ كل الميل فتذروها كالمعلقة وان تصلحوا۟ وتتقوا۟ فان الله كان غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(4,130,'4:130','وان يتفرقا يغن الله كلا من سعته وكان الله وسعا حكيما');
INSERT INTO "uthmani_simple" VALUES(4,131,'4:131','ولله ما فى السموت وما فى الارض ولقد وصينا الذين اوتوا۟ الكتب من قبلكم واياكم ان اتقوا۟ الله وان تكفروا۟ فان لله ما فى السموت وما فى الارض وكان الله غنيا حميدا');
INSERT INTO "uthmani_simple" VALUES(4,132,'4:132','ولله ما فى السموت وما فى الارض وكفى بالله وكيلا');
INSERT INTO "uthmani_simple" VALUES(4,133,'4:133','ان يشا يذهبكم ايها الناس ويات بـاخرين وكان الله على ذلك قديرا');
INSERT INTO "uthmani_simple" VALUES(4,134,'4:134','من كان يريد ثواب الدنيا فعند الله ثواب الدنيا والءاخرة وكان الله سميعۢا بصيرا');
INSERT INTO "uthmani_simple" VALUES(4,135,'4:135','يايها الذين ءامنوا۟ كونوا۟ قومين بالقسط شهداء لله ولو على انفسكم او الولدين والاقربين ان يكن غنيا او فقيرا فالله اولى بهما فلا تتبعوا۟ الهوى ان تعدلوا۟ وان تلوا۟ او تعرضوا۟ فان الله كان بما تعملون خبيرا');
INSERT INTO "uthmani_simple" VALUES(4,136,'4:136','يايها الذين ءامنوا۟ ءامنوا۟ بالله ورسوله والكتب الذى نزل على رسوله والكتب الذى انزل من قبل ومن يكفر بالله وملئكته وكتبه ورسله واليوم الءاخر فقد ضل ضللۢا بعيدا');
INSERT INTO "uthmani_simple" VALUES(4,137,'4:137','ان الذين ءامنوا۟ ثم كفروا۟ ثم ءامنوا۟ ثم كفروا۟ ثم ازدادوا۟ كفرا لم يكن الله ليغفر لهم ولا ليهديهم سبيلۢا');
INSERT INTO "uthmani_simple" VALUES(4,138,'4:138','بشر المنفقين بان لهم عذابا اليما');
INSERT INTO "uthmani_simple" VALUES(4,139,'4:139','الذين يتخذون الكفرين اولياء من دون المؤمنين ايبتغون عندهم العزة فان العزة لله جميعا');
INSERT INTO "uthmani_simple" VALUES(4,140,'4:140','وقد نزل عليكم فى الكتب ان اذا سمعتم ءايت الله يكفر بها ويستهزا بها فلا تقعدوا۟ معهم حتى يخوضوا۟ فى حديث غيره انكم اذا مثلهم ان الله جامع المنفقين والكفرين فى جهنم جميعا');
INSERT INTO "uthmani_simple" VALUES(4,141,'4:141','الذين يتربصون بكم فان كان لكم فتح من الله قالوا۟ الم نكن معكم وان كان للكفرين نصيب قالوا۟ الم نستحوذ عليكم ونمنعكم من المؤمنين فالله يحكم بينكم يوم القيمة ولن يجعل الله للكفرين على المؤمنين سبيلا');
INSERT INTO "uthmani_simple" VALUES(4,142,'4:142','ان المنفقين يخدعون الله وهو خدعهم واذا قاموا۟ الى الصلوة قاموا۟ كسالى يراءون الناس ولا يذكرون الله الا قليلا');
INSERT INTO "uthmani_simple" VALUES(4,143,'4:143','مذبذبين بين ذلك لا الى هؤلاء ولا الى هؤلاء ومن يضلل الله فلن تجد له سبيلا');
INSERT INTO "uthmani_simple" VALUES(4,144,'4:144','يايها الذين ءامنوا۟ لا تتخذوا۟ الكفرين اولياء من دون المؤمنين اتريدون ان تجعلوا۟ لله عليكم سلطنا مبينا');
INSERT INTO "uthmani_simple" VALUES(4,145,'4:145','ان المنفقين فى الدرك الاسفل من النار ولن تجد لهم نصيرا');
INSERT INTO "uthmani_simple" VALUES(4,146,'4:146','الا الذين تابوا۟ واصلحوا۟ واعتصموا۟ بالله واخلصوا۟ دينهم لله فاو۟لئك مع المؤمنين وسوف يؤت الله المؤمنين اجرا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,147,'4:147','ما يفعل الله بعذابكم ان شكرتم وءامنتم وكان الله شاكرا عليما');
INSERT INTO "uthmani_simple" VALUES(4,148,'4:148','لا يحب الله الجهر بالسوء من القول الا من ظلم وكان الله سميعا عليما');
INSERT INTO "uthmani_simple" VALUES(4,149,'4:149','ان تبدوا۟ خيرا او تخفوه او تعفوا۟ عن سوء فان الله كان عفوا قديرا');
INSERT INTO "uthmani_simple" VALUES(4,150,'4:150','ان الذين يكفرون بالله ورسله ويريدون ان يفرقوا۟ بين الله ورسله ويقولون نؤمن ببعض ونكفر ببعض ويريدون ان يتخذوا۟ بين ذلك سبيلا');
INSERT INTO "uthmani_simple" VALUES(4,151,'4:151','او۟لئك هم الكفرون حقا واعتدنا للكفرين عذابا مهينا');
INSERT INTO "uthmani_simple" VALUES(4,152,'4:152','والذين ءامنوا۟ بالله ورسله ولم يفرقوا۟ بين احد منهم او۟لئك سوف يؤتيهم اجورهم وكان الله غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(4,153,'4:153','يسـلك اهل الكتب ان تنزل عليهم كتبا من السماء فقد سالوا۟ موسى اكبر من ذلك فقالوا۟ ارنا الله جهرة فاخذتهم الصعقة بظلمهم ثم اتخذوا۟ العجل منۢ بعد ما جاءتهم البينت فعفونا عن ذلك وءاتينا موسى سلطنا مبينا');
INSERT INTO "uthmani_simple" VALUES(4,154,'4:154','ورفعنا فوقهم الطور بميثقهم وقلنا لهم ادخلوا۟ الباب سجدا وقلنا لهم لا تعدوا۟ فى السبت واخذنا منهم ميثقا غليظا');
INSERT INTO "uthmani_simple" VALUES(4,155,'4:155','فبما نقضهم ميثقهم وكفرهم بـايت الله وقتلهم الانۢبياء بغير حق وقولهم قلوبنا غلفۢ بل طبع الله عليها بكفرهم فلا يؤمنون الا قليلا');
INSERT INTO "uthmani_simple" VALUES(4,156,'4:156','وبكفرهم وقولهم على مريم بهتنا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,157,'4:157','وقولهم انا قتلنا المسيح عيسى ابن مريم رسول الله وما قتلوه وما صلبوه ولكن شبه لهم وان الذين اختلفوا۟ فيه لفى شك منه ما لهم به من علم الا اتباع الظن وما قتلوه يقينۢا');
INSERT INTO "uthmani_simple" VALUES(4,158,'4:158','بل رفعه الله اليه وكان الله عزيزا حكيما');
INSERT INTO "uthmani_simple" VALUES(4,159,'4:159','وان من اهل الكتب الا ليؤمنن به قبل موته ويوم القيمة يكون عليهم شهيدا');
INSERT INTO "uthmani_simple" VALUES(4,160,'4:160','فبظلم من الذين هادوا۟ حرمنا عليهم طيبت احلت لهم وبصدهم عن سبيل الله كثيرا');
INSERT INTO "uthmani_simple" VALUES(4,161,'4:161','واخذهم الربوا۟ وقد نهوا۟ عنه واكلهم امول الناس بالبطل واعتدنا للكفرين منهم عذابا اليما');
INSERT INTO "uthmani_simple" VALUES(4,162,'4:162','لكن الرسخون فى العلم منهم والمؤمنون يؤمنون بما انزل اليك وما انزل من قبلك والمقيمين الصلوة والمؤتون الزكوة والمؤمنون بالله واليوم الءاخر او۟لئك سنؤتيهم اجرا عظيما');
INSERT INTO "uthmani_simple" VALUES(4,163,'4:163','انا اوحينا اليك كما اوحينا الى نوح والنبين منۢ بعده واوحينا الى ابرهيم واسمعيل واسحق ويعقوب والاسباط وعيسى وايوب ويونس وهرون وسليمن وءاتينا داود زبورا');
INSERT INTO "uthmani_simple" VALUES(4,164,'4:164','ورسلا قد قصصنهم عليك من قبل ورسلا لم نقصصهم عليك وكلم الله موسى تكليما');
INSERT INTO "uthmani_simple" VALUES(4,165,'4:165','رسلا مبشرين ومنذرين لئلا يكون للناس على الله حجةۢ بعد الرسل وكان الله عزيزا حكيما');
INSERT INTO "uthmani_simple" VALUES(4,166,'4:166','لكن الله يشهد بما انزل اليك انزله بعلمه والملئكة يشهدون وكفى بالله شهيدا');
INSERT INTO "uthmani_simple" VALUES(4,167,'4:167','ان الذين كفروا۟ وصدوا۟ عن سبيل الله قد ضلوا۟ ضللۢا بعيدا');
INSERT INTO "uthmani_simple" VALUES(4,168,'4:168','ان الذين كفروا۟ وظلموا۟ لم يكن الله ليغفر لهم ولا ليهديهم طريقا');
INSERT INTO "uthmani_simple" VALUES(4,169,'4:169','الا طريق جهنم خلدين فيها ابدا وكان ذلك على الله يسيرا');
INSERT INTO "uthmani_simple" VALUES(4,170,'4:170','يايها الناس قد جاءكم الرسول بالحق من ربكم فـامنوا۟ خيرا لكم وان تكفروا۟ فان لله ما فى السموت والارض وكان الله عليما حكيما');
INSERT INTO "uthmani_simple" VALUES(4,171,'4:171','ياهل الكتب لا تغلوا۟ فى دينكم ولا تقولوا۟ على الله الا الحق انما المسيح عيسى ابن مريم رسول الله وكلمته القىها الى مريم وروح منه فـامنوا۟ بالله ورسله ولا تقولوا۟ ثلثة انتهوا۟ خيرا لكم انما الله اله وحد سبحنه ان يكون له ولد له ما فى السموت وما فى الارض وكفى بالله وكيلا');
INSERT INTO "uthmani_simple" VALUES(4,172,'4:172','لن يستنكف المسيح ان يكون عبدا لله ولا الملئكة المقربون ومن يستنكف عن عبادته ويستكبر فسيحشرهم اليه جميعا');
INSERT INTO "uthmani_simple" VALUES(4,173,'4:173','فاما الذين ءامنوا۟ وعملوا۟ الصلحت فيوفيهم اجورهم ويزيدهم من فضله واما الذين استنكفوا۟ واستكبروا۟ فيعذبهم عذابا اليما ولا يجدون لهم من دون الله وليا ولا نصيرا');
INSERT INTO "uthmani_simple" VALUES(4,174,'4:174','يايها الناس قد جاءكم برهن من ربكم وانزلنا اليكم نورا مبينا');
INSERT INTO "uthmani_simple" VALUES(4,175,'4:175','فاما الذين ءامنوا۟ بالله واعتصموا۟ به فسيدخلهم فى رحمة منه وفضل ويهديهم اليه صرطا مستقيما');
INSERT INTO "uthmani_simple" VALUES(4,176,'4:176','يستفتونك قل الله يفتيكم فى الكللة ان امرؤا۟ هلك ليس له ولد وله اخت فلها نصف ما ترك وهو يرثها ان لم يكن لها ولد فان كانتا اثنتين فلهما الثلثان مما ترك وان كانوا۟ اخوة رجالا ونساء فللذكر مثل حظ الانثيين يبين الله لكم ان تضلوا۟ والله بكل شىء عليمۢ');
INSERT INTO "uthmani_simple" VALUES(5,1,'5:1','يايها الذين ءامنوا۟ اوفوا۟ بالعقود احلت لكم بهيمة الانعم الا ما يتلى عليكم غير محلى الصيد وانتم حرم ان الله يحكم ما يريد');
INSERT INTO "uthmani_simple" VALUES(5,2,'5:2','يايها الذين ءامنوا۟ لا تحلوا۟ شعئر الله ولا الشهر الحرام ولا الهدى ولا القلئد ولا ءامين البيت الحرام يبتغون فضلا من ربهم ورضونا واذا حللتم فاصطادوا۟ ولا يجرمنكم شنـان قوم ان صدوكم عن المسجد الحرام ان تعتدوا۟ وتعاونوا۟ على البر والتقوى ولا تعاونوا۟ على الاثم والعدون واتقوا۟ الله ان الله شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(5,3,'5:3','حرمت عليكم الميتة والدم ولحم الخنزير وما اهل لغير الله به والمنخنقة والموقوذة والمتردية والنطيحة وما اكل السبع الا ما ذكيتم وما ذبح على النصب وان تستقسموا۟ بالازلم ذلكم فسق اليوم يئس الذين كفروا۟ من دينكم فلا تخشوهم واخشون اليوم اكملت لكم دينكم واتممت عليكم نعمتى ورضيت لكم الاسلم دينا فمن اضطر فى مخمصة غير متجانف لاثم فان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(5,4,'5:4','يسـلونك ماذا احل لهم قل احل لكم الطيبت وما علمتم من الجوارح مكلبين تعلمونهن مما علمكم الله فكلوا۟ مما امسكن عليكم واذكروا۟ اسم الله عليه واتقوا۟ الله ان الله سريع الحساب');
INSERT INTO "uthmani_simple" VALUES(5,5,'5:5','اليوم احل لكم الطيبت وطعام الذين اوتوا۟ الكتب حل لكم وطعامكم حل لهم والمحصنت من المؤمنت والمحصنت من الذين اوتوا۟ الكتب من قبلكم اذا ءاتيتموهن اجورهن محصنين غير مسفحين ولا متخذى اخدان ومن يكفر بالايمن فقد حبط عمله وهو فى الءاخرة من الخسرين');
INSERT INTO "uthmani_simple" VALUES(5,6,'5:6','يايها الذين ءامنوا۟ اذا قمتم الى الصلوة فاغسلوا۟ وجوهكم وايديكم الى المرافق وامسحوا۟ برءوسكم وارجلكم الى الكعبين وان كنتم جنبا فاطهروا۟ وان كنتم مرضى او على سفر او جاء احد منكم من الغائط او لمستم النساء فلم تجدوا۟ ماء فتيمموا۟ صعيدا طيبا فامسحوا۟ بوجوهكم وايديكم منه ما يريد الله ليجعل عليكم من حرج ولكن يريد ليطهركم وليتم نعمته عليكم لعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(5,7,'5:7','واذكروا۟ نعمة الله عليكم وميثقه الذى واثقكم به اذ قلتم سمعنا واطعنا واتقوا۟ الله ان الله عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(5,8,'5:8','يايها الذين ءامنوا۟ كونوا۟ قومين لله شهداء بالقسط ولا يجرمنكم شنـان قوم على الا تعدلوا۟ اعدلوا۟ هو اقرب للتقوى واتقوا۟ الله ان الله خبيرۢ بما تعملون');
INSERT INTO "uthmani_simple" VALUES(5,9,'5:9','وعد الله الذين ءامنوا۟ وعملوا۟ الصلحت لهم مغفرة واجر عظيم');
INSERT INTO "uthmani_simple" VALUES(5,10,'5:10','والذين كفروا۟ وكذبوا۟ بـايتنا او۟لئك اصحب الجحيم');
INSERT INTO "uthmani_simple" VALUES(5,11,'5:11','يايها الذين ءامنوا۟ اذكروا۟ نعمت الله عليكم اذ هم قوم ان يبسطوا۟ اليكم ايديهم فكف ايديهم عنكم واتقوا۟ الله وعلى الله فليتوكل المؤمنون');
INSERT INTO "uthmani_simple" VALUES(5,12,'5:12','ولقد اخذ الله ميثق بنى اسرءيل وبعثنا منهم اثنى عشر نقيبا وقال الله انى معكم لئن اقمتم الصلوة وءاتيتم الزكوة وءامنتم برسلى وعزرتموهم واقرضتم الله قرضا حسنا لاكفرن عنكم سيـاتكم ولادخلنكم جنت تجرى من تحتها الانهر فمن كفر بعد ذلك منكم فقد ضل سواء السبيل');
INSERT INTO "uthmani_simple" VALUES(5,13,'5:13','فبما نقضهم ميثقهم لعنهم وجعلنا قلوبهم قسية يحرفون الكلم عن مواضعه ونسوا۟ حظا مما ذكروا۟ به ولا تزال تطلع على خائنة منهم الا قليلا منهم فاعف عنهم واصفح ان الله يحب المحسنين');
INSERT INTO "uthmani_simple" VALUES(5,14,'5:14','ومن الذين قالوا۟ انا نصرى اخذنا ميثقهم فنسوا۟ حظا مما ذكروا۟ به فاغرينا بينهم العداوة والبغضاء الى يوم القيمة وسوف ينبئهم الله بما كانوا۟ يصنعون');
INSERT INTO "uthmani_simple" VALUES(5,15,'5:15','ياهل الكتب قد جاءكم رسولنا يبين لكم كثيرا مما كنتم تخفون من الكتب ويعفوا۟ عن كثير قد جاءكم من الله نور وكتب مبين');
INSERT INTO "uthmani_simple" VALUES(5,16,'5:16','يهدى به الله من اتبع رضونه سبل السلم ويخرجهم من الظلمت الى النور باذنه ويهديهم الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(5,17,'5:17','لقد كفر الذين قالوا۟ ان الله هو المسيح ابن مريم قل فمن يملك من الله شيـا ان اراد ان يهلك المسيح ابن مريم وامه ومن فى الارض جميعا ولله ملك السموت والارض وما بينهما يخلق ما يشاء والله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(5,18,'5:18','وقالت اليهود والنصرى نحن ابنؤا۟ الله واحبؤه قل فلم يعذبكم بذنوبكم بل انتم بشر ممن خلق يغفر لمن يشاء ويعذب من يشاء ولله ملك السموت والارض وما بينهما واليه المصير');
INSERT INTO "uthmani_simple" VALUES(5,19,'5:19','ياهل الكتب قد جاءكم رسولنا يبين لكم على فترة من الرسل ان تقولوا۟ ما جاءنا منۢ بشير ولا نذير فقد جاءكم بشير ونذير والله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(5,20,'5:20','واذ قال موسى لقومه يقوم اذكروا۟ نعمة الله عليكم اذ جعل فيكم انۢبياء وجعلكم ملوكا وءاتىكم ما لم يؤت احدا من العلمين');
INSERT INTO "uthmani_simple" VALUES(5,21,'5:21','يقوم ادخلوا۟ الارض المقدسة التى كتب الله لكم ولا ترتدوا۟ على ادباركم فتنقلبوا۟ خسرين');
INSERT INTO "uthmani_simple" VALUES(5,22,'5:22','قالوا۟ يموسى ان فيها قوما جبارين وانا لن ندخلها حتى يخرجوا۟ منها فان يخرجوا۟ منها فانا دخلون');
INSERT INTO "uthmani_simple" VALUES(5,23,'5:23','قال رجلان من الذين يخافون انعم الله عليهما ادخلوا۟ عليهم الباب فاذا دخلتموه فانكم غلبون وعلى الله فتوكلوا۟ ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(5,24,'5:24','قالوا۟ يموسى انا لن ندخلها ابدا ما داموا۟ فيها فاذهب انت وربك فقتلا انا ههنا قعدون');
INSERT INTO "uthmani_simple" VALUES(5,25,'5:25','قال رب انى لا املك الا نفسى واخى فافرق بيننا وبين القوم الفسقين');
INSERT INTO "uthmani_simple" VALUES(5,26,'5:26','قال فانها محرمة عليهم اربعين سنة يتيهون فى الارض فلا تاس على القوم الفسقين');
INSERT INTO "uthmani_simple" VALUES(5,27,'5:27','واتل عليهم نبا ابنى ءادم بالحق اذ قربا قربانا فتقبل من احدهما ولم يتقبل من الءاخر قال لاقتلنك قال انما يتقبل الله من المتقين');
INSERT INTO "uthmani_simple" VALUES(5,28,'5:28','لئنۢ بسطت الى يدك لتقتلنى ما انا۠ بباسط يدى اليك لاقتلك انى اخاف الله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(5,29,'5:29','انى اريد ان تبوا باثمى واثمك فتكون من اصحب النار وذلك جزؤا۟ الظلمين');
INSERT INTO "uthmani_simple" VALUES(5,30,'5:30','فطوعت له نفسه قتل اخيه فقتله فاصبح من الخسرين');
INSERT INTO "uthmani_simple" VALUES(5,31,'5:31','فبعث الله غرابا يبحث فى الارض ليريه كيف يورى سوءة اخيه قال يويلتى اعجزت ان اكون مثل هذا الغراب فاورى سوءة اخى فاصبح من الندمين');
INSERT INTO "uthmani_simple" VALUES(5,32,'5:32','من اجل ذلك كتبنا على بنى اسرءيل انه من قتل نفسۢا بغير نفس او فساد فى الارض فكانما قتل الناس جميعا ومن احياها فكانما احيا الناس جميعا ولقد جاءتهم رسلنا بالبينت ثم ان كثيرا منهم بعد ذلك فى الارض لمسرفون');
INSERT INTO "uthmani_simple" VALUES(5,33,'5:33','انما جزؤا۟ الذين يحاربون الله ورسوله ويسعون فى الارض فسادا ان يقتلوا۟ او يصلبوا۟ او تقطع ايديهم وارجلهم من خلف او ينفوا۟ من الارض ذلك لهم خزى فى الدنيا ولهم فى الءاخرة عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(5,34,'5:34','الا الذين تابوا۟ من قبل ان تقدروا۟ عليهم فاعلموا۟ ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(5,35,'5:35','يايها الذين ءامنوا۟ اتقوا۟ الله وابتغوا۟ اليه الوسيلة وجهدوا۟ فى سبيله لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(5,36,'5:36','ان الذين كفروا۟ لو ان لهم ما فى الارض جميعا ومثله معه ليفتدوا۟ به من عذاب يوم القيمة ما تقبل منهم ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(5,37,'5:37','يريدون ان يخرجوا۟ من النار وما هم بخرجين منها ولهم عذاب مقيم');
INSERT INTO "uthmani_simple" VALUES(5,38,'5:38','والسارق والسارقة فاقطعوا۟ ايديهما جزاءۢ بما كسبا نكلا من الله والله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(5,39,'5:39','فمن تاب منۢ بعد ظلمه واصلح فان الله يتوب عليه ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(5,40,'5:40','الم تعلم ان الله له ملك السموت والارض يعذب من يشاء ويغفر لمن يشاء والله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(5,41,'5:41','يايها الرسول لا يحزنك الذين يسرعون فى الكفر من الذين قالوا۟ ءامنا بافوههم ولم تؤمن قلوبهم ومن الذين هادوا۟ سمعون للكذب سمعون لقوم ءاخرين لم ياتوك يحرفون الكلم منۢ بعد مواضعه يقولون ان اوتيتم هذا فخذوه وان لم تؤتوه فاحذروا۟ ومن يرد الله فتنته فلن تملك له من الله شيـا او۟لئك الذين لم يرد الله ان يطهر قلوبهم لهم فى الدنيا خزى ولهم فى الءاخرة عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(5,42,'5:42','سمعون للكذب اكلون للسحت فان جاءوك فاحكم بينهم او اعرض عنهم وان تعرض عنهم فلن يضروك شيـا وان حكمت فاحكم بينهم بالقسط ان الله يحب المقسطين');
INSERT INTO "uthmani_simple" VALUES(5,43,'5:43','وكيف يحكمونك وعندهم التورىة فيها حكم الله ثم يتولون منۢ بعد ذلك وما او۟لئك بالمؤمنين');
INSERT INTO "uthmani_simple" VALUES(5,44,'5:44','انا انزلنا التورىة فيها هدى ونور يحكم بها النبيون الذين اسلموا۟ للذين هادوا۟ والربنيون والاحبار بما استحفظوا۟ من كتب الله وكانوا۟ عليه شهداء فلا تخشوا۟ الناس واخشون ولا تشتروا۟ بـايتى ثمنا قليلا ومن لم يحكم بما انزل الله فاو۟لئك هم الكفرون');
INSERT INTO "uthmani_simple" VALUES(5,45,'5:45','وكتبنا عليهم فيها ان النفس بالنفس والعين بالعين والانف بالانف والاذن بالاذن والسن بالسن والجروح قصاص فمن تصدق به فهو كفارة له ومن لم يحكم بما انزل الله فاو۟لئك هم الظلمون');
INSERT INTO "uthmani_simple" VALUES(5,46,'5:46','وقفينا على ءاثرهم بعيسى ابن مريم مصدقا لما بين يديه من التورىة وءاتينه الانجيل فيه هدى ونور ومصدقا لما بين يديه من التورىة وهدى وموعظة للمتقين');
INSERT INTO "uthmani_simple" VALUES(5,47,'5:47','وليحكم اهل الانجيل بما انزل الله فيه ومن لم يحكم بما انزل الله فاو۟لئك هم الفسقون');
INSERT INTO "uthmani_simple" VALUES(5,48,'5:48','وانزلنا اليك الكتب بالحق مصدقا لما بين يديه من الكتب ومهيمنا عليه فاحكم بينهم بما انزل الله ولا تتبع اهواءهم عما جاءك من الحق لكل جعلنا منكم شرعة ومنهاجا ولو شاء الله لجعلكم امة وحدة ولكن ليبلوكم فى ما ءاتىكم فاستبقوا۟ الخيرت الى الله مرجعكم جميعا فينبئكم بما كنتم فيه تختلفون');
INSERT INTO "uthmani_simple" VALUES(5,49,'5:49','وان احكم بينهم بما انزل الله ولا تتبع اهواءهم واحذرهم ان يفتنوك عنۢ بعض ما انزل الله اليك فان تولوا۟ فاعلم انما يريد الله ان يصيبهم ببعض ذنوبهم وان كثيرا من الناس لفسقون');
INSERT INTO "uthmani_simple" VALUES(5,50,'5:50','افحكم الجهلية يبغون ومن احسن من الله حكما لقوم يوقنون');
INSERT INTO "uthmani_simple" VALUES(5,51,'5:51','يايها الذين ءامنوا۟ لا تتخذوا۟ اليهود والنصرى اولياء بعضهم اولياء بعض ومن يتولهم منكم فانه منهم ان الله لا يهدى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(5,52,'5:52','فترى الذين فى قلوبهم مرض يسرعون فيهم يقولون نخشى ان تصيبنا دائرة فعسى الله ان ياتى بالفتح او امر من عنده فيصبحوا۟ على ما اسروا۟ فى انفسهم ندمين');
INSERT INTO "uthmani_simple" VALUES(5,53,'5:53','ويقول الذين ءامنوا۟ اهؤلاء الذين اقسموا۟ بالله جهد ايمنهم انهم لمعكم حبطت اعملهم فاصبحوا۟ خسرين');
INSERT INTO "uthmani_simple" VALUES(5,54,'5:54','يايها الذين ءامنوا۟ من يرتد منكم عن دينه فسوف ياتى الله بقوم يحبهم ويحبونه اذلة على المؤمنين اعزة على الكفرين يجهدون فى سبيل الله ولا يخافون لومة لائم ذلك فضل الله يؤتيه من يشاء والله وسع عليم');
INSERT INTO "uthmani_simple" VALUES(5,55,'5:55','انما وليكم الله ورسوله والذين ءامنوا۟ الذين يقيمون الصلوة ويؤتون الزكوة وهم ركعون');
INSERT INTO "uthmani_simple" VALUES(5,56,'5:56','ومن يتول الله ورسوله والذين ءامنوا۟ فان حزب الله هم الغلبون');
INSERT INTO "uthmani_simple" VALUES(5,57,'5:57','يايها الذين ءامنوا۟ لا تتخذوا۟ الذين اتخذوا۟ دينكم هزوا ولعبا من الذين اوتوا۟ الكتب من قبلكم والكفار اولياء واتقوا۟ الله ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(5,58,'5:58','واذا ناديتم الى الصلوة اتخذوها هزوا ولعبا ذلك بانهم قوم لا يعقلون');
INSERT INTO "uthmani_simple" VALUES(5,59,'5:59','قل ياهل الكتب هل تنقمون منا الا ان ءامنا بالله وما انزل الينا وما انزل من قبل وان اكثركم فسقون');
INSERT INTO "uthmani_simple" VALUES(5,60,'5:60','قل هل انبئكم بشر من ذلك مثوبة عند الله من لعنه الله وغضب عليه وجعل منهم القردة والخنازير وعبد الطغوت او۟لئك شر مكانا واضل عن سواء السبيل');
INSERT INTO "uthmani_simple" VALUES(5,61,'5:61','واذا جاءوكم قالوا۟ ءامنا وقد دخلوا۟ بالكفر وهم قد خرجوا۟ به والله اعلم بما كانوا۟ يكتمون');
INSERT INTO "uthmani_simple" VALUES(5,62,'5:62','وترى كثيرا منهم يسرعون فى الاثم والعدون واكلهم السحت لبئس ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(5,63,'5:63','لولا ينهىهم الربنيون والاحبار عن قولهم الاثم واكلهم السحت لبئس ما كانوا۟ يصنعون');
INSERT INTO "uthmani_simple" VALUES(5,64,'5:64','وقالت اليهود يد الله مغلولة غلت ايديهم ولعنوا۟ بما قالوا۟ بل يداه مبسوطتان ينفق كيف يشاء وليزيدن كثيرا منهم ما انزل اليك من ربك طغينا وكفرا والقينا بينهم العدوة والبغضاء الى يوم القيمة كلما اوقدوا۟ نارا للحرب اطفاها الله ويسعون فى الارض فسادا والله لا يحب المفسدين');
INSERT INTO "uthmani_simple" VALUES(5,65,'5:65','ولو ان اهل الكتب ءامنوا۟ واتقوا۟ لكفرنا عنهم سيـاتهم ولادخلنهم جنت النعيم');
INSERT INTO "uthmani_simple" VALUES(5,66,'5:66','ولو انهم اقاموا۟ التورىة والانجيل وما انزل اليهم من ربهم لاكلوا۟ من فوقهم ومن تحت ارجلهم منهم امة مقتصدة وكثير منهم ساء ما يعملون');
INSERT INTO "uthmani_simple" VALUES(5,67,'5:67','يايها الرسول بلغ ما انزل اليك من ربك وان لم تفعل فما بلغت رسالته والله يعصمك من الناس ان الله لا يهدى القوم الكفرين');
INSERT INTO "uthmani_simple" VALUES(5,68,'5:68','قل ياهل الكتب لستم على شىء حتى تقيموا۟ التورىة والانجيل وما انزل اليكم من ربكم وليزيدن كثيرا منهم ما انزل اليك من ربك طغينا وكفرا فلا تاس على القوم الكفرين');
INSERT INTO "uthmani_simple" VALUES(5,69,'5:69','ان الذين ءامنوا۟ والذين هادوا۟ والصبـون والنصرى من ءامن بالله واليوم الءاخر وعمل صلحا فلا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(5,70,'5:70','لقد اخذنا ميثق بنى اسرءيل وارسلنا اليهم رسلا كلما جاءهم رسولۢ بما لا تهوى انفسهم فريقا كذبوا۟ وفريقا يقتلون');
INSERT INTO "uthmani_simple" VALUES(5,71,'5:71','وحسبوا۟ الا تكون فتنة فعموا۟ وصموا۟ ثم تاب الله عليهم ثم عموا۟ وصموا۟ كثير منهم والله بصيرۢ بما يعملون');
INSERT INTO "uthmani_simple" VALUES(5,72,'5:72','لقد كفر الذين قالوا۟ ان الله هو المسيح ابن مريم وقال المسيح يبنى اسرءيل اعبدوا۟ الله ربى وربكم انه من يشرك بالله فقد حرم الله عليه الجنة وماوىه النار وما للظلمين من انصار');
INSERT INTO "uthmani_simple" VALUES(5,73,'5:73','لقد كفر الذين قالوا۟ ان الله ثالث ثلثة وما من اله الا اله وحد وان لم ينتهوا۟ عما يقولون ليمسن الذين كفروا۟ منهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(5,74,'5:74','افلا يتوبون الى الله ويستغفرونه والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(5,75,'5:75','ما المسيح ابن مريم الا رسول قد خلت من قبله الرسل وامه صديقة كانا ياكلان الطعام انظر كيف نبين لهم الءايت ثم انظر انى يؤفكون');
INSERT INTO "uthmani_simple" VALUES(5,76,'5:76','قل اتعبدون من دون الله ما لا يملك لكم ضرا ولا نفعا والله هو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(5,77,'5:77','قل ياهل الكتب لا تغلوا۟ فى دينكم غير الحق ولا تتبعوا۟ اهواء قوم قد ضلوا۟ من قبل واضلوا۟ كثيرا وضلوا۟ عن سواء السبيل');
INSERT INTO "uthmani_simple" VALUES(5,78,'5:78','لعن الذين كفروا۟ منۢ بنى اسرءيل على لسان داود وعيسى ابن مريم ذلك بما عصوا۟ وكانوا۟ يعتدون');
INSERT INTO "uthmani_simple" VALUES(5,79,'5:79','كانوا۟ لا يتناهون عن منكر فعلوه لبئس ما كانوا۟ يفعلون');
INSERT INTO "uthmani_simple" VALUES(5,80,'5:80','ترى كثيرا منهم يتولون الذين كفروا۟ لبئس ما قدمت لهم انفسهم ان سخط الله عليهم وفى العذاب هم خلدون');
INSERT INTO "uthmani_simple" VALUES(5,81,'5:81','ولو كانوا۟ يؤمنون بالله والنبى وما انزل اليه ما اتخذوهم اولياء ولكن كثيرا منهم فسقون');
INSERT INTO "uthmani_simple" VALUES(5,82,'5:82','لتجدن اشد الناس عدوة للذين ءامنوا۟ اليهود والذين اشركوا۟ ولتجدن اقربهم مودة للذين ءامنوا۟ الذين قالوا۟ انا نصرى ذلك بان منهم قسيسين ورهبانا وانهم لا يستكبرون');
INSERT INTO "uthmani_simple" VALUES(5,83,'5:83','واذا سمعوا۟ ما انزل الى الرسول ترى اعينهم تفيض من الدمع مما عرفوا۟ من الحق يقولون ربنا ءامنا فاكتبنا مع الشهدين');
INSERT INTO "uthmani_simple" VALUES(5,84,'5:84','وما لنا لا نؤمن بالله وما جاءنا من الحق ونطمع ان يدخلنا ربنا مع القوم الصلحين');
INSERT INTO "uthmani_simple" VALUES(5,85,'5:85','فاثبهم الله بما قالوا۟ جنت تجرى من تحتها الانهر خلدين فيها وذلك جزاء المحسنين');
INSERT INTO "uthmani_simple" VALUES(5,86,'5:86','والذين كفروا۟ وكذبوا۟ بـايتنا او۟لئك اصحب الجحيم');
INSERT INTO "uthmani_simple" VALUES(5,87,'5:87','يايها الذين ءامنوا۟ لا تحرموا۟ طيبت ما احل الله لكم ولا تعتدوا۟ ان الله لا يحب المعتدين');
INSERT INTO "uthmani_simple" VALUES(5,88,'5:88','وكلوا۟ مما رزقكم الله حللا طيبا واتقوا۟ الله الذى انتم به مؤمنون');
INSERT INTO "uthmani_simple" VALUES(5,89,'5:89','لا يؤاخذكم الله باللغو فى ايمنكم ولكن يؤاخذكم بما عقدتم الايمن فكفرته اطعام عشرة مسكين من اوسط ما تطعمون اهليكم او كسوتهم او تحرير رقبة فمن لم يجد فصيام ثلثة ايام ذلك كفرة ايمنكم اذا حلفتم واحفظوا۟ ايمنكم كذلك يبين الله لكم ءايته لعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(5,90,'5:90','يايها الذين ءامنوا۟ انما الخمر والميسر والانصاب والازلم رجس من عمل الشيطن فاجتنبوه لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(5,91,'5:91','انما يريد الشيطن ان يوقع بينكم العدوة والبغضاء فى الخمر والميسر ويصدكم عن ذكر الله وعن الصلوة فهل انتم منتهون');
INSERT INTO "uthmani_simple" VALUES(5,92,'5:92','واطيعوا۟ الله واطيعوا۟ الرسول واحذروا۟ فان توليتم فاعلموا۟ انما على رسولنا البلغ المبين');
INSERT INTO "uthmani_simple" VALUES(5,93,'5:93','ليس على الذين ءامنوا۟ وعملوا۟ الصلحت جناح فيما طعموا۟ اذا ما اتقوا۟ وءامنوا۟ وعملوا۟ الصلحت ثم اتقوا۟ وءامنوا۟ ثم اتقوا۟ واحسنوا۟ والله يحب المحسنين');
INSERT INTO "uthmani_simple" VALUES(5,94,'5:94','يايها الذين ءامنوا۟ ليبلونكم الله بشىء من الصيد تناله ايديكم ورماحكم ليعلم الله من يخافه بالغيب فمن اعتدى بعد ذلك فله عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(5,95,'5:95','يايها الذين ءامنوا۟ لا تقتلوا۟ الصيد وانتم حرم ومن قتله منكم متعمدا فجزاء مثل ما قتل من النعم يحكم به ذوا عدل منكم هديۢا بلغ الكعبة او كفرة طعام مسكين او عدل ذلك صياما ليذوق وبال امره عفا الله عما سلف ومن عاد فينتقم الله منه والله عزيز ذو انتقام');
INSERT INTO "uthmani_simple" VALUES(5,96,'5:96','احل لكم صيد البحر وطعامه متعا لكم وللسيارة وحرم عليكم صيد البر ما دمتم حرما واتقوا۟ الله الذى اليه تحشرون');
INSERT INTO "uthmani_simple" VALUES(5,97,'5:97','جعل الله الكعبة البيت الحرام قيما للناس والشهر الحرام والهدى والقلئد ذلك لتعلموا۟ ان الله يعلم ما فى السموت وما فى الارض وان الله بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(5,98,'5:98','اعلموا۟ ان الله شديد العقاب وان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(5,99,'5:99','ما على الرسول الا البلغ والله يعلم ما تبدون وما تكتمون');
INSERT INTO "uthmani_simple" VALUES(5,100,'5:100','قل لا يستوى الخبيث والطيب ولو اعجبك كثرة الخبيث فاتقوا۟ الله ياو۟لى الالبب لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(5,101,'5:101','يايها الذين ءامنوا۟ لا تسـلوا۟ عن اشياء ان تبد لكم تسؤكم وان تسـلوا۟ عنها حين ينزل القرءان تبد لكم عفا الله عنها والله غفور حليم');
INSERT INTO "uthmani_simple" VALUES(5,102,'5:102','قد سالها قوم من قبلكم ثم اصبحوا۟ بها كفرين');
INSERT INTO "uthmani_simple" VALUES(5,103,'5:103','ما جعل الله منۢ بحيرة ولا سائبة ولا وصيلة ولا حام ولكن الذين كفروا۟ يفترون على الله الكذب واكثرهم لا يعقلون');
INSERT INTO "uthmani_simple" VALUES(5,104,'5:104','واذا قيل لهم تعالوا۟ الى ما انزل الله والى الرسول قالوا۟ حسبنا ما وجدنا عليه ءاباءنا اولو كان ءاباؤهم لا يعلمون شيـا ولا يهتدون');
INSERT INTO "uthmani_simple" VALUES(5,105,'5:105','يايها الذين ءامنوا۟ عليكم انفسكم لا يضركم من ضل اذا اهتديتم الى الله مرجعكم جميعا فينبئكم بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(5,106,'5:106','يايها الذين ءامنوا۟ شهدة بينكم اذا حضر احدكم الموت حين الوصية اثنان ذوا عدل منكم او ءاخران من غيركم ان انتم ضربتم فى الارض فاصبتكم مصيبة الموت تحبسونهما منۢ بعد الصلوة فيقسمان بالله ان ارتبتم لا نشترى به ثمنا ولو كان ذا قربى ولا نكتم شهدة الله انا اذا لمن الءاثمين');
INSERT INTO "uthmani_simple" VALUES(5,107,'5:107','فان عثر على انهما استحقا اثما فـاخران يقومان مقامهما من الذين استحق عليهم الاولين فيقسمان بالله لشهدتنا احق من شهدتهما وما اعتدينا انا اذا لمن الظلمين');
INSERT INTO "uthmani_simple" VALUES(5,108,'5:108','ذلك ادنى ان ياتوا۟ بالشهدة على وجهها او يخافوا۟ ان ترد ايمنۢ بعد ايمنهم واتقوا۟ الله واسمعوا۟ والله لا يهدى القوم الفسقين');
INSERT INTO "uthmani_simple" VALUES(5,109,'5:109','يوم يجمع الله الرسل فيقول ماذا اجبتم قالوا۟ لا علم لنا انك انت علم الغيوب');
INSERT INTO "uthmani_simple" VALUES(5,110,'5:110','اذ قال الله يعيسى ابن مريم اذكر نعمتى عليك وعلى ولدتك اذ ايدتك بروح القدس تكلم الناس فى المهد وكهلا واذ علمتك الكتب والحكمة والتورىة والانجيل واذ تخلق من الطين كهيـة الطير باذنى فتنفخ فيها فتكون طيرۢا باذنى وتبرئ الاكمه والابرص باذنى واذ تخرج الموتى باذنى واذ كففت بنى اسرءيل عنك اذ جئتهم بالبينت فقال الذين كفروا۟ منهم ان هذا الا سحر مبين');
INSERT INTO "uthmani_simple" VALUES(5,111,'5:111','واذ اوحيت الى الحوارين ان ءامنوا۟ بى وبرسولى قالوا۟ ءامنا واشهد باننا مسلمون');
INSERT INTO "uthmani_simple" VALUES(5,112,'5:112','اذ قال الحواريون يعيسى ابن مريم هل يستطيع ربك ان ينزل علينا مائدة من السماء قال اتقوا۟ الله ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(5,113,'5:113','قالوا۟ نريد ان ناكل منها وتطمئن قلوبنا ونعلم ان قد صدقتنا ونكون عليها من الشهدين');
INSERT INTO "uthmani_simple" VALUES(5,114,'5:114','قال عيسى ابن مريم اللهم ربنا انزل علينا مائدة من السماء تكون لنا عيدا لاولنا وءاخرنا وءاية منك وارزقنا وانت خير الرزقين');
INSERT INTO "uthmani_simple" VALUES(5,115,'5:115','قال الله انى منزلها عليكم فمن يكفر بعد منكم فانى اعذبه عذابا لا اعذبه احدا من العلمين');
INSERT INTO "uthmani_simple" VALUES(5,116,'5:116','واذ قال الله يعيسى ابن مريم ءانت قلت للناس اتخذونى وامى الهين من دون الله قال سبحنك ما يكون لى ان اقول ما ليس لى بحق ان كنت قلته فقد علمته تعلم ما فى نفسى ولا اعلم ما فى نفسك انك انت علم الغيوب');
INSERT INTO "uthmani_simple" VALUES(5,117,'5:117','ما قلت لهم الا ما امرتنى به ان اعبدوا۟ الله ربى وربكم وكنت عليهم شهيدا ما دمت فيهم فلما توفيتنى كنت انت الرقيب عليهم وانت على كل شىء شهيد');
INSERT INTO "uthmani_simple" VALUES(5,118,'5:118','ان تعذبهم فانهم عبادك وان تغفر لهم فانك انت العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(5,119,'5:119','قال الله هذا يوم ينفع الصدقين صدقهم لهم جنت تجرى من تحتها الانهر خلدين فيها ابدا رضى الله عنهم ورضوا۟ عنه ذلك الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(5,120,'5:120','لله ملك السموت والارض وما فيهن وهو على كل شىء قديرۢ');
INSERT INTO "uthmani_simple" VALUES(6,1,'6:1','الحمد لله الذى خلق السموت والارض وجعل الظلمت والنور ثم الذين كفروا۟ بربهم يعدلون');
INSERT INTO "uthmani_simple" VALUES(6,2,'6:2','هو الذى خلقكم من طين ثم قضى اجلا واجل مسمى عنده ثم انتم تمترون');
INSERT INTO "uthmani_simple" VALUES(6,3,'6:3','وهو الله فى السموت وفى الارض يعلم سركم وجهركم ويعلم ما تكسبون');
INSERT INTO "uthmani_simple" VALUES(6,4,'6:4','وما تاتيهم من ءاية من ءايت ربهم الا كانوا۟ عنها معرضين');
INSERT INTO "uthmani_simple" VALUES(6,5,'6:5','فقد كذبوا۟ بالحق لما جاءهم فسوف ياتيهم انۢبؤا۟ ما كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(6,6,'6:6','الم يروا۟ كم اهلكنا من قبلهم من قرن مكنهم فى الارض ما لم نمكن لكم وارسلنا السماء عليهم مدرارا وجعلنا الانهر تجرى من تحتهم فاهلكنهم بذنوبهم وانشانا منۢ بعدهم قرنا ءاخرين');
INSERT INTO "uthmani_simple" VALUES(6,7,'6:7','ولو نزلنا عليك كتبا فى قرطاس فلمسوه بايديهم لقال الذين كفروا۟ ان هذا الا سحر مبين');
INSERT INTO "uthmani_simple" VALUES(6,8,'6:8','وقالوا۟ لولا انزل عليه ملك ولو انزلنا ملكا لقضى الامر ثم لا ينظرون');
INSERT INTO "uthmani_simple" VALUES(6,9,'6:9','ولو جعلنه ملكا لجعلنه رجلا وللبسنا عليهم ما يلبسون');
INSERT INTO "uthmani_simple" VALUES(6,10,'6:10','ولقد استهزئ برسل من قبلك فحاق بالذين سخروا۟ منهم ما كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(6,11,'6:11','قل سيروا۟ فى الارض ثم انظروا۟ كيف كان عقبة المكذبين');
INSERT INTO "uthmani_simple" VALUES(6,12,'6:12','قل لمن ما فى السموت والارض قل لله كتب على نفسه الرحمة ليجمعنكم الى يوم القيمة لا ريب فيه الذين خسروا۟ انفسهم فهم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(6,13,'6:13','وله ما سكن فى اليل والنهار وهو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(6,14,'6:14','قل اغير الله اتخذ وليا فاطر السموت والارض وهو يطعم ولا يطعم قل انى امرت ان اكون اول من اسلم ولا تكونن من المشركين');
INSERT INTO "uthmani_simple" VALUES(6,15,'6:15','قل انى اخاف ان عصيت ربى عذاب يوم عظيم');
INSERT INTO "uthmani_simple" VALUES(6,16,'6:16','من يصرف عنه يومئذ فقد رحمه وذلك الفوز المبين');
INSERT INTO "uthmani_simple" VALUES(6,17,'6:17','وان يمسسك الله بضر فلا كاشف له الا هو وان يمسسك بخير فهو على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(6,18,'6:18','وهو القاهر فوق عباده وهو الحكيم الخبير');
INSERT INTO "uthmani_simple" VALUES(6,19,'6:19','قل اى شىء اكبر شهدة قل الله شهيدۢ بينى وبينكم واوحى الى هذا القرءان لانذركم به ومنۢ بلغ ائنكم لتشهدون ان مع الله ءالهة اخرى قل لا اشهد قل انما هو اله وحد واننى برىء مما تشركون');
INSERT INTO "uthmani_simple" VALUES(6,20,'6:20','الذين ءاتينهم الكتب يعرفونه كما يعرفون ابناءهم الذين خسروا۟ انفسهم فهم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(6,21,'6:21','ومن اظلم ممن افترى على الله كذبا او كذب بـايته انه لا يفلح الظلمون');
INSERT INTO "uthmani_simple" VALUES(6,22,'6:22','ويوم نحشرهم جميعا ثم نقول للذين اشركوا۟ اين شركاؤكم الذين كنتم تزعمون');
INSERT INTO "uthmani_simple" VALUES(6,23,'6:23','ثم لم تكن فتنتهم الا ان قالوا۟ والله ربنا ما كنا مشركين');
INSERT INTO "uthmani_simple" VALUES(6,24,'6:24','انظر كيف كذبوا۟ على انفسهم وضل عنهم ما كانوا۟ يفترون');
INSERT INTO "uthmani_simple" VALUES(6,25,'6:25','ومنهم من يستمع اليك وجعلنا على قلوبهم اكنة ان يفقهوه وفى ءاذانهم وقرا وان يروا۟ كل ءاية لا يؤمنوا۟ بها حتى اذا جاءوك يجدلونك يقول الذين كفروا۟ ان هذا الا اسطير الاولين');
INSERT INTO "uthmani_simple" VALUES(6,26,'6:26','وهم ينهون عنه وينـون عنه وان يهلكون الا انفسهم وما يشعرون');
INSERT INTO "uthmani_simple" VALUES(6,27,'6:27','ولو ترى اذ وقفوا۟ على النار فقالوا۟ يليتنا نرد ولا نكذب بـايت ربنا ونكون من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(6,28,'6:28','بل بدا لهم ما كانوا۟ يخفون من قبل ولو ردوا۟ لعادوا۟ لما نهوا۟ عنه وانهم لكذبون');
INSERT INTO "uthmani_simple" VALUES(6,29,'6:29','وقالوا۟ ان هى الا حياتنا الدنيا وما نحن بمبعوثين');
INSERT INTO "uthmani_simple" VALUES(6,30,'6:30','ولو ترى اذ وقفوا۟ على ربهم قال اليس هذا بالحق قالوا۟ بلى وربنا قال فذوقوا۟ العذاب بما كنتم تكفرون');
INSERT INTO "uthmani_simple" VALUES(6,31,'6:31','قد خسر الذين كذبوا۟ بلقاء الله حتى اذا جاءتهم الساعة بغتة قالوا۟ يحسرتنا على ما فرطنا فيها وهم يحملون اوزارهم على ظهورهم الا ساء ما يزرون');
INSERT INTO "uthmani_simple" VALUES(6,32,'6:32','وما الحيوة الدنيا الا لعب ولهو وللدار الءاخرة خير للذين يتقون افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(6,33,'6:33','قد نعلم انه ليحزنك الذى يقولون فانهم لا يكذبونك ولكن الظلمين بـايت الله يجحدون');
INSERT INTO "uthmani_simple" VALUES(6,34,'6:34','ولقد كذبت رسل من قبلك فصبروا۟ على ما كذبوا۟ واوذوا۟ حتى اتىهم نصرنا ولا مبدل لكلمت الله ولقد جاءك من نباى۟ المرسلين');
INSERT INTO "uthmani_simple" VALUES(6,35,'6:35','وان كان كبر عليك اعراضهم فان استطعت ان تبتغى نفقا فى الارض او سلما فى السماء فتاتيهم بـاية ولو شاء الله لجمعهم على الهدى فلا تكونن من الجهلين');
INSERT INTO "uthmani_simple" VALUES(6,36,'6:36','انما يستجيب الذين يسمعون والموتى يبعثهم الله ثم اليه يرجعون');
INSERT INTO "uthmani_simple" VALUES(6,37,'6:37','وقالوا۟ لولا نزل عليه ءاية من ربه قل ان الله قادر على ان ينزل ءاية ولكن اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(6,38,'6:38','وما من دابة فى الارض ولا طئر يطير بجناحيه الا امم امثالكم ما فرطنا فى الكتب من شىء ثم الى ربهم يحشرون');
INSERT INTO "uthmani_simple" VALUES(6,39,'6:39','والذين كذبوا۟ بـايتنا صم وبكم فى الظلمت من يشا الله يضلله ومن يشا يجعله على صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(6,40,'6:40','قل ارءيتكم ان اتىكم عذاب الله او اتتكم الساعة اغير الله تدعون ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(6,41,'6:41','بل اياه تدعون فيكشف ما تدعون اليه ان شاء وتنسون ما تشركون');
INSERT INTO "uthmani_simple" VALUES(6,42,'6:42','ولقد ارسلنا الى امم من قبلك فاخذنهم بالباساء والضراء لعلهم يتضرعون');
INSERT INTO "uthmani_simple" VALUES(6,43,'6:43','فلولا اذ جاءهم باسنا تضرعوا۟ ولكن قست قلوبهم وزين لهم الشيطن ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(6,44,'6:44','فلما نسوا۟ ما ذكروا۟ به فتحنا عليهم ابوب كل شىء حتى اذا فرحوا۟ بما اوتوا۟ اخذنهم بغتة فاذا هم مبلسون');
INSERT INTO "uthmani_simple" VALUES(6,45,'6:45','فقطع دابر القوم الذين ظلموا۟ والحمد لله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(6,46,'6:46','قل ارءيتم ان اخذ الله سمعكم وابصركم وختم على قلوبكم من اله غير الله ياتيكم به انظر كيف نصرف الءايت ثم هم يصدفون');
INSERT INTO "uthmani_simple" VALUES(6,47,'6:47','قل ارءيتكم ان اتىكم عذاب الله بغتة او جهرة هل يهلك الا القوم الظلمون');
INSERT INTO "uthmani_simple" VALUES(6,48,'6:48','وما نرسل المرسلين الا مبشرين ومنذرين فمن ءامن واصلح فلا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(6,49,'6:49','والذين كذبوا۟ بـايتنا يمسهم العذاب بما كانوا۟ يفسقون');
INSERT INTO "uthmani_simple" VALUES(6,50,'6:50','قل لا اقول لكم عندى خزائن الله ولا اعلم الغيب ولا اقول لكم انى ملك ان اتبع الا ما يوحى الى قل هل يستوى الاعمى والبصير افلا تتفكرون');
INSERT INTO "uthmani_simple" VALUES(6,51,'6:51','وانذر به الذين يخافون ان يحشروا۟ الى ربهم ليس لهم من دونه ولى ولا شفيع لعلهم يتقون');
INSERT INTO "uthmani_simple" VALUES(6,52,'6:52','ولا تطرد الذين يدعون ربهم بالغدوة والعشى يريدون وجهه ما عليك من حسابهم من شىء وما من حسابك عليهم من شىء فتطردهم فتكون من الظلمين');
INSERT INTO "uthmani_simple" VALUES(6,53,'6:53','وكذلك فتنا بعضهم ببعض ليقولوا۟ اهؤلاء من الله عليهم منۢ بيننا اليس الله باعلم بالشكرين');
INSERT INTO "uthmani_simple" VALUES(6,54,'6:54','واذا جاءك الذين يؤمنون بـايتنا فقل سلم عليكم كتب ربكم على نفسه الرحمة انه من عمل منكم سوءۢا بجهلة ثم تاب منۢ بعده واصلح فانه غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(6,55,'6:55','وكذلك نفصل الءايت ولتستبين سبيل المجرمين');
INSERT INTO "uthmani_simple" VALUES(6,56,'6:56','قل انى نهيت ان اعبد الذين تدعون من دون الله قل لا اتبع اهواءكم قد ضللت اذا وما انا۠ من المهتدين');
INSERT INTO "uthmani_simple" VALUES(6,57,'6:57','قل انى على بينة من ربى وكذبتم به ما عندى ما تستعجلون به ان الحكم الا لله يقص الحق وهو خير الفصلين');
INSERT INTO "uthmani_simple" VALUES(6,58,'6:58','قل لو ان عندى ما تستعجلون به لقضى الامر بينى وبينكم والله اعلم بالظلمين');
INSERT INTO "uthmani_simple" VALUES(6,59,'6:59','وعنده مفاتح الغيب لا يعلمها الا هو ويعلم ما فى البر والبحر وما تسقط من ورقة الا يعلمها ولا حبة فى ظلمت الارض ولا رطب ولا يابس الا فى كتب مبين');
INSERT INTO "uthmani_simple" VALUES(6,60,'6:60','وهو الذى يتوفىكم باليل ويعلم ما جرحتم بالنهار ثم يبعثكم فيه ليقضى اجل مسمى ثم اليه مرجعكم ثم ينبئكم بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(6,61,'6:61','وهو القاهر فوق عباده ويرسل عليكم حفظة حتى اذا جاء احدكم الموت توفته رسلنا وهم لا يفرطون');
INSERT INTO "uthmani_simple" VALUES(6,62,'6:62','ثم ردوا۟ الى الله مولىهم الحق الا له الحكم وهو اسرع الحسبين');
INSERT INTO "uthmani_simple" VALUES(6,63,'6:63','قل من ينجيكم من ظلمت البر والبحر تدعونه تضرعا وخفية لئن انجىنا من هذه لنكونن من الشكرين');
INSERT INTO "uthmani_simple" VALUES(6,64,'6:64','قل الله ينجيكم منها ومن كل كرب ثم انتم تشركون');
INSERT INTO "uthmani_simple" VALUES(6,65,'6:65','قل هو القادر على ان يبعث عليكم عذابا من فوقكم او من تحت ارجلكم او يلبسكم شيعا ويذيق بعضكم باس بعض انظر كيف نصرف الءايت لعلهم يفقهون');
INSERT INTO "uthmani_simple" VALUES(6,66,'6:66','وكذب به قومك وهو الحق قل لست عليكم بوكيل');
INSERT INTO "uthmani_simple" VALUES(6,67,'6:67','لكل نبا مستقر وسوف تعلمون');
INSERT INTO "uthmani_simple" VALUES(6,68,'6:68','واذا رايت الذين يخوضون فى ءايتنا فاعرض عنهم حتى يخوضوا۟ فى حديث غيره واما ينسينك الشيطن فلا تقعد بعد الذكرى مع القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(6,69,'6:69','وما على الذين يتقون من حسابهم من شىء ولكن ذكرى لعلهم يتقون');
INSERT INTO "uthmani_simple" VALUES(6,70,'6:70','وذر الذين اتخذوا۟ دينهم لعبا ولهوا وغرتهم الحيوة الدنيا وذكر به ان تبسل نفسۢ بما كسبت ليس لها من دون الله ولى ولا شفيع وان تعدل كل عدل لا يؤخذ منها او۟لئك الذين ابسلوا۟ بما كسبوا۟ لهم شراب من حميم وعذاب اليمۢ بما كانوا۟ يكفرون');
INSERT INTO "uthmani_simple" VALUES(6,71,'6:71','قل اندعوا۟ من دون الله ما لا ينفعنا ولا يضرنا ونرد على اعقابنا بعد اذ هدىنا الله كالذى استهوته الشيطين فى الارض حيران له اصحب يدعونه الى الهدى ائتنا قل ان هدى الله هو الهدى وامرنا لنسلم لرب العلمين');
INSERT INTO "uthmani_simple" VALUES(6,72,'6:72','وان اقيموا۟ الصلوة واتقوه وهو الذى اليه تحشرون');
INSERT INTO "uthmani_simple" VALUES(6,73,'6:73','وهو الذى خلق السموت والارض بالحق ويوم يقول كن فيكون قوله الحق وله الملك يوم ينفخ فى الصور علم الغيب والشهدة وهو الحكيم الخبير');
INSERT INTO "uthmani_simple" VALUES(6,74,'6:74','واذ قال ابرهيم لابيه ءازر اتتخذ اصناما ءالهة انى ارىك وقومك فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(6,75,'6:75','وكذلك نرى ابرهيم ملكوت السموت والارض وليكون من الموقنين');
INSERT INTO "uthmani_simple" VALUES(6,76,'6:76','فلما جن عليه اليل رءا كوكبا قال هذا ربى فلما افل قال لا احب الءافلين');
INSERT INTO "uthmani_simple" VALUES(6,77,'6:77','فلما رءا القمر بازغا قال هذا ربى فلما افل قال لئن لم يهدنى ربى لاكونن من القوم الضالين');
INSERT INTO "uthmani_simple" VALUES(6,78,'6:78','فلما رءا الشمس بازغة قال هذا ربى هذا اكبر فلما افلت قال يقوم انى برىء مما تشركون');
INSERT INTO "uthmani_simple" VALUES(6,79,'6:79','انى وجهت وجهى للذى فطر السموت والارض حنيفا وما انا۠ من المشركين');
INSERT INTO "uthmani_simple" VALUES(6,80,'6:80','وحاجه قومه قال اتحجونى فى الله وقد هدىن ولا اخاف ما تشركون به الا ان يشاء ربى شيـا وسع ربى كل شىء علما افلا تتذكرون');
INSERT INTO "uthmani_simple" VALUES(6,81,'6:81','وكيف اخاف ما اشركتم ولا تخافون انكم اشركتم بالله ما لم ينزل به عليكم سلطنا فاى الفريقين احق بالامن ان كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(6,82,'6:82','الذين ءامنوا۟ ولم يلبسوا۟ ايمنهم بظلم او۟لئك لهم الامن وهم مهتدون');
INSERT INTO "uthmani_simple" VALUES(6,83,'6:83','وتلك حجتنا ءاتينها ابرهيم على قومه نرفع درجت من نشاء ان ربك حكيم عليم');
INSERT INTO "uthmani_simple" VALUES(6,84,'6:84','ووهبنا له اسحق ويعقوب كلا هدينا ونوحا هدينا من قبل ومن ذريته داود وسليمن وايوب ويوسف وموسى وهرون وكذلك نجزى المحسنين');
INSERT INTO "uthmani_simple" VALUES(6,85,'6:85','وزكريا ويحيى وعيسى والياس كل من الصلحين');
INSERT INTO "uthmani_simple" VALUES(6,86,'6:86','واسمعيل واليسع ويونس ولوطا وكلا فضلنا على العلمين');
INSERT INTO "uthmani_simple" VALUES(6,87,'6:87','ومن ءابائهم وذريتهم واخونهم واجتبينهم وهدينهم الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(6,88,'6:88','ذلك هدى الله يهدى به من يشاء من عباده ولو اشركوا۟ لحبط عنهم ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(6,89,'6:89','او۟لئك الذين ءاتينهم الكتب والحكم والنبوة فان يكفر بها هؤلاء فقد وكلنا بها قوما ليسوا۟ بها بكفرين');
INSERT INTO "uthmani_simple" VALUES(6,90,'6:90','او۟لئك الذين هدى الله فبهدىهم اقتده قل لا اسـلكم عليه اجرا ان هو الا ذكرى للعلمين');
INSERT INTO "uthmani_simple" VALUES(6,91,'6:91','وما قدروا۟ الله حق قدره اذ قالوا۟ ما انزل الله على بشر من شىء قل من انزل الكتب الذى جاء به موسى نورا وهدى للناس تجعلونه قراطيس تبدونها وتخفون كثيرا وعلمتم ما لم تعلموا۟ انتم ولا ءاباؤكم قل الله ثم ذرهم فى خوضهم يلعبون');
INSERT INTO "uthmani_simple" VALUES(6,92,'6:92','وهذا كتب انزلنه مبارك مصدق الذى بين يديه ولتنذر ام القرى ومن حولها والذين يؤمنون بالءاخرة يؤمنون به وهم على صلاتهم يحافظون');
INSERT INTO "uthmani_simple" VALUES(6,93,'6:93','ومن اظلم ممن افترى على الله كذبا او قال اوحى الى ولم يوح اليه شىء ومن قال سانزل مثل ما انزل الله ولو ترى اذ الظلمون فى غمرت الموت والملئكة باسطوا۟ ايديهم اخرجوا۟ انفسكم اليوم تجزون عذاب الهون بما كنتم تقولون على الله غير الحق وكنتم عن ءايته تستكبرون');
INSERT INTO "uthmani_simple" VALUES(6,94,'6:94','ولقد جئتمونا فردى كما خلقنكم اول مرة وتركتم ما خولنكم وراء ظهوركم وما نرى معكم شفعاءكم الذين زعمتم انهم فيكم شركؤا۟ لقد تقطع بينكم وضل عنكم ما كنتم تزعمون');
INSERT INTO "uthmani_simple" VALUES(6,95,'6:95','ان الله فالق الحب والنوى يخرج الحى من الميت ومخرج الميت من الحى ذلكم الله فانى تؤفكون');
INSERT INTO "uthmani_simple" VALUES(6,96,'6:96','فالق الاصباح وجعل اليل سكنا والشمس والقمر حسبانا ذلك تقدير العزيز العليم');
INSERT INTO "uthmani_simple" VALUES(6,97,'6:97','وهو الذى جعل لكم النجوم لتهتدوا۟ بها فى ظلمت البر والبحر قد فصلنا الءايت لقوم يعلمون');
INSERT INTO "uthmani_simple" VALUES(6,98,'6:98','وهو الذى انشاكم من نفس وحدة فمستقر ومستودع قد فصلنا الءايت لقوم يفقهون');
INSERT INTO "uthmani_simple" VALUES(6,99,'6:99','وهو الذى انزل من السماء ماء فاخرجنا به نبات كل شىء فاخرجنا منه خضرا نخرج منه حبا متراكبا ومن النخل من طلعها قنوان دانية وجنت من اعناب والزيتون والرمان مشتبها وغير متشبه انظروا۟ الى ثمره اذا اثمر وينعه ان فى ذلكم لءايت لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(6,100,'6:100','وجعلوا۟ لله شركاء الجن وخلقهم وخرقوا۟ له بنين وبنتۭ بغير علم سبحنه وتعلى عما يصفون');
INSERT INTO "uthmani_simple" VALUES(6,101,'6:101','بديع السموت والارض انى يكون له ولد ولم تكن له صحبة وخلق كل شىء وهو بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(6,102,'6:102','ذلكم الله ربكم لا اله الا هو خلق كل شىء فاعبدوه وهو على كل شىء وكيل');
INSERT INTO "uthmani_simple" VALUES(6,103,'6:103','لا تدركه الابصر وهو يدرك الابصر وهو اللطيف الخبير');
INSERT INTO "uthmani_simple" VALUES(6,104,'6:104','قد جاءكم بصائر من ربكم فمن ابصر فلنفسه ومن عمى فعليها وما انا۠ عليكم بحفيظ');
INSERT INTO "uthmani_simple" VALUES(6,105,'6:105','وكذلك نصرف الءايت وليقولوا۟ درست ولنبينه لقوم يعلمون');
INSERT INTO "uthmani_simple" VALUES(6,106,'6:106','اتبع ما اوحى اليك من ربك لا اله الا هو واعرض عن المشركين');
INSERT INTO "uthmani_simple" VALUES(6,107,'6:107','ولو شاء الله ما اشركوا۟ وما جعلنك عليهم حفيظا وما انت عليهم بوكيل');
INSERT INTO "uthmani_simple" VALUES(6,108,'6:108','ولا تسبوا۟ الذين يدعون من دون الله فيسبوا۟ الله عدوۢا بغير علم كذلك زينا لكل امة عملهم ثم الى ربهم مرجعهم فينبئهم بما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(6,109,'6:109','واقسموا۟ بالله جهد ايمنهم لئن جاءتهم ءاية ليؤمنن بها قل انما الءايت عند الله وما يشعركم انها اذا جاءت لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(6,110,'6:110','ونقلب افـدتهم وابصرهم كما لم يؤمنوا۟ به اول مرة ونذرهم فى طغينهم يعمهون');
INSERT INTO "uthmani_simple" VALUES(6,111,'6:111','ولو اننا نزلنا اليهم الملئكة وكلمهم الموتى وحشرنا عليهم كل شىء قبلا ما كانوا۟ ليؤمنوا۟ الا ان يشاء الله ولكن اكثرهم يجهلون');
INSERT INTO "uthmani_simple" VALUES(6,112,'6:112','وكذلك جعلنا لكل نبى عدوا شيطين الانس والجن يوحى بعضهم الى بعض زخرف القول غرورا ولو شاء ربك ما فعلوه فذرهم وما يفترون');
INSERT INTO "uthmani_simple" VALUES(6,113,'6:113','ولتصغى اليه افـدة الذين لا يؤمنون بالءاخرة وليرضوه وليقترفوا۟ ما هم مقترفون');
INSERT INTO "uthmani_simple" VALUES(6,114,'6:114','افغير الله ابتغى حكما وهو الذى انزل اليكم الكتب مفصلا والذين ءاتينهم الكتب يعلمون انه منزل من ربك بالحق فلا تكونن من الممترين');
INSERT INTO "uthmani_simple" VALUES(6,115,'6:115','وتمت كلمت ربك صدقا وعدلا لا مبدل لكلمته وهو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(6,116,'6:116','وان تطع اكثر من فى الارض يضلوك عن سبيل الله ان يتبعون الا الظن وان هم الا يخرصون');
INSERT INTO "uthmani_simple" VALUES(6,117,'6:117','ان ربك هو اعلم من يضل عن سبيله وهو اعلم بالمهتدين');
INSERT INTO "uthmani_simple" VALUES(6,118,'6:118','فكلوا۟ مما ذكر اسم الله عليه ان كنتم بـايته مؤمنين');
INSERT INTO "uthmani_simple" VALUES(6,119,'6:119','وما لكم الا تاكلوا۟ مما ذكر اسم الله عليه وقد فصل لكم ما حرم عليكم الا ما اضطررتم اليه وان كثيرا ليضلون باهوائهم بغير علم ان ربك هو اعلم بالمعتدين');
INSERT INTO "uthmani_simple" VALUES(6,120,'6:120','وذروا۟ ظهر الاثم وباطنه ان الذين يكسبون الاثم سيجزون بما كانوا۟ يقترفون');
INSERT INTO "uthmani_simple" VALUES(6,121,'6:121','ولا تاكلوا۟ مما لم يذكر اسم الله عليه وانه لفسق وان الشيطين ليوحون الى اوليائهم ليجدلوكم وان اطعتموهم انكم لمشركون');
INSERT INTO "uthmani_simple" VALUES(6,122,'6:122','اومن كان ميتا فاحيينه وجعلنا له نورا يمشى به فى الناس كمن مثله فى الظلمت ليس بخارج منها كذلك زين للكفرين ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(6,123,'6:123','وكذلك جعلنا فى كل قرية اكبر مجرميها ليمكروا۟ فيها وما يمكرون الا بانفسهم وما يشعرون');
INSERT INTO "uthmani_simple" VALUES(6,124,'6:124','واذا جاءتهم ءاية قالوا۟ لن نؤمن حتى نؤتى مثل ما اوتى رسل الله الله اعلم حيث يجعل رسالته سيصيب الذين اجرموا۟ صغار عند الله وعذاب شديدۢ بما كانوا۟ يمكرون');
INSERT INTO "uthmani_simple" VALUES(6,125,'6:125','فمن يرد الله ان يهديه يشرح صدره للاسلم ومن يرد ان يضله يجعل صدره ضيقا حرجا كانما يصعد فى السماء كذلك يجعل الله الرجس على الذين لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(6,126,'6:126','وهذا صرط ربك مستقيما قد فصلنا الءايت لقوم يذكرون');
INSERT INTO "uthmani_simple" VALUES(6,127,'6:127','لهم دار السلم عند ربهم وهو وليهم بما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(6,128,'6:128','ويوم يحشرهم جميعا يمعشر الجن قد استكثرتم من الانس وقال اولياؤهم من الانس ربنا استمتع بعضنا ببعض وبلغنا اجلنا الذى اجلت لنا قال النار مثوىكم خلدين فيها الا ما شاء الله ان ربك حكيم عليم');
INSERT INTO "uthmani_simple" VALUES(6,129,'6:129','وكذلك نولى بعض الظلمين بعضۢا بما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(6,130,'6:130','يمعشر الجن والانس الم ياتكم رسل منكم يقصون عليكم ءايتى وينذرونكم لقاء يومكم هذا قالوا۟ شهدنا على انفسنا وغرتهم الحيوة الدنيا وشهدوا۟ على انفسهم انهم كانوا۟ كفرين');
INSERT INTO "uthmani_simple" VALUES(6,131,'6:131','ذلك ان لم يكن ربك مهلك القرى بظلم واهلها غفلون');
INSERT INTO "uthmani_simple" VALUES(6,132,'6:132','ولكل درجت مما عملوا۟ وما ربك بغفل عما يعملون');
INSERT INTO "uthmani_simple" VALUES(6,133,'6:133','وربك الغنى ذو الرحمة ان يشا يذهبكم ويستخلف منۢ بعدكم ما يشاء كما انشاكم من ذرية قوم ءاخرين');
INSERT INTO "uthmani_simple" VALUES(6,134,'6:134','ان ما توعدون لءات وما انتم بمعجزين');
INSERT INTO "uthmani_simple" VALUES(6,135,'6:135','قل يقوم اعملوا۟ على مكانتكم انى عامل فسوف تعلمون من تكون له عقبة الدار انه لا يفلح الظلمون');
INSERT INTO "uthmani_simple" VALUES(6,136,'6:136','وجعلوا۟ لله مما ذرا من الحرث والانعم نصيبا فقالوا۟ هذا لله بزعمهم وهذا لشركائنا فما كان لشركائهم فلا يصل الى الله وما كان لله فهو يصل الى شركائهم ساء ما يحكمون');
INSERT INTO "uthmani_simple" VALUES(6,137,'6:137','وكذلك زين لكثير من المشركين قتل اولدهم شركاؤهم ليردوهم وليلبسوا۟ عليهم دينهم ولو شاء الله ما فعلوه فذرهم وما يفترون');
INSERT INTO "uthmani_simple" VALUES(6,138,'6:138','وقالوا۟ هذه انعم وحرث حجر لا يطعمها الا من نشاء بزعمهم وانعم حرمت ظهورها وانعم لا يذكرون اسم الله عليها افتراء عليه سيجزيهم بما كانوا۟ يفترون');
INSERT INTO "uthmani_simple" VALUES(6,139,'6:139','وقالوا۟ ما فى بطون هذه الانعم خالصة لذكورنا ومحرم على ازوجنا وان يكن ميتة فهم فيه شركاء سيجزيهم وصفهم انه حكيم عليم');
INSERT INTO "uthmani_simple" VALUES(6,140,'6:140','قد خسر الذين قتلوا۟ اولدهم سفهۢا بغير علم وحرموا۟ ما رزقهم الله افتراء على الله قد ضلوا۟ وما كانوا۟ مهتدين');
INSERT INTO "uthmani_simple" VALUES(6,141,'6:141','وهو الذى انشا جنت معروشت وغير معروشت والنخل والزرع مختلفا اكله والزيتون والرمان متشبها وغير متشبه كلوا۟ من ثمره اذا اثمر وءاتوا۟ حقه يوم حصاده ولا تسرفوا۟ انه لا يحب المسرفين');
INSERT INTO "uthmani_simple" VALUES(6,142,'6:142','ومن الانعم حمولة وفرشا كلوا۟ مما رزقكم الله ولا تتبعوا۟ خطوت الشيطن انه لكم عدو مبين');
INSERT INTO "uthmani_simple" VALUES(6,143,'6:143','ثمنية ازوج من الضان اثنين ومن المعز اثنين قل ءالذكرين حرم ام الانثيين اما اشتملت عليه ارحام الانثيين نبـونى بعلم ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(6,144,'6:144','ومن الابل اثنين ومن البقر اثنين قل ءالذكرين حرم ام الانثيين اما اشتملت عليه ارحام الانثيين ام كنتم شهداء اذ وصىكم الله بهذا فمن اظلم ممن افترى على الله كذبا ليضل الناس بغير علم ان الله لا يهدى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(6,145,'6:145','قل لا اجد فى ما اوحى الى محرما على طاعم يطعمه الا ان يكون ميتة او دما مسفوحا او لحم خنزير فانه رجس او فسقا اهل لغير الله به فمن اضطر غير باغ ولا عاد فان ربك غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(6,146,'6:146','وعلى الذين هادوا۟ حرمنا كل ذى ظفر ومن البقر والغنم حرمنا عليهم شحومهما الا ما حملت ظهورهما او الحوايا او ما اختلط بعظم ذلك جزينهم ببغيهم وانا لصدقون');
INSERT INTO "uthmani_simple" VALUES(6,147,'6:147','فان كذبوك فقل ربكم ذو رحمة وسعة ولا يرد باسه عن القوم المجرمين');
INSERT INTO "uthmani_simple" VALUES(6,148,'6:148','سيقول الذين اشركوا۟ لو شاء الله ما اشركنا ولا ءاباؤنا ولا حرمنا من شىء كذلك كذب الذين من قبلهم حتى ذاقوا۟ باسنا قل هل عندكم من علم فتخرجوه لنا ان تتبعون الا الظن وان انتم الا تخرصون');
INSERT INTO "uthmani_simple" VALUES(6,149,'6:149','قل فلله الحجة البلغة فلو شاء لهدىكم اجمعين');
INSERT INTO "uthmani_simple" VALUES(6,150,'6:150','قل هلم شهداءكم الذين يشهدون ان الله حرم هذا فان شهدوا۟ فلا تشهد معهم ولا تتبع اهواء الذين كذبوا۟ بـايتنا والذين لا يؤمنون بالءاخرة وهم بربهم يعدلون');
INSERT INTO "uthmani_simple" VALUES(6,151,'6:151','قل تعالوا۟ اتل ما حرم ربكم عليكم الا تشركوا۟ به شيـا وبالولدين احسنا ولا تقتلوا۟ اولدكم من املق نحن نرزقكم واياهم ولا تقربوا۟ الفوحش ما ظهر منها وما بطن ولا تقتلوا۟ النفس التى حرم الله الا بالحق ذلكم وصىكم به لعلكم تعقلون');
INSERT INTO "uthmani_simple" VALUES(6,152,'6:152','ولا تقربوا۟ مال اليتيم الا بالتى هى احسن حتى يبلغ اشده واوفوا۟ الكيل والميزان بالقسط لا نكلف نفسا الا وسعها واذا قلتم فاعدلوا۟ ولو كان ذا قربى وبعهد الله اوفوا۟ ذلكم وصىكم به لعلكم تذكرون');
INSERT INTO "uthmani_simple" VALUES(6,153,'6:153','وان هذا صرطى مستقيما فاتبعوه ولا تتبعوا۟ السبل فتفرق بكم عن سبيله ذلكم وصىكم به لعلكم تتقون');
INSERT INTO "uthmani_simple" VALUES(6,154,'6:154','ثم ءاتينا موسى الكتب تماما على الذى احسن وتفصيلا لكل شىء وهدى ورحمة لعلهم بلقاء ربهم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(6,155,'6:155','وهذا كتب انزلنه مبارك فاتبعوه واتقوا۟ لعلكم ترحمون');
INSERT INTO "uthmani_simple" VALUES(6,156,'6:156','ان تقولوا۟ انما انزل الكتب على طائفتين من قبلنا وان كنا عن دراستهم لغفلين');
INSERT INTO "uthmani_simple" VALUES(6,157,'6:157','او تقولوا۟ لو انا انزل علينا الكتب لكنا اهدى منهم فقد جاءكم بينة من ربكم وهدى ورحمة فمن اظلم ممن كذب بـايت الله وصدف عنها سنجزى الذين يصدفون عن ءايتنا سوء العذاب بما كانوا۟ يصدفون');
INSERT INTO "uthmani_simple" VALUES(6,158,'6:158','هل ينظرون الا ان تاتيهم الملئكة او ياتى ربك او ياتى بعض ءايت ربك يوم ياتى بعض ءايت ربك لا ينفع نفسا ايمنها لم تكن ءامنت من قبل او كسبت فى ايمنها خيرا قل انتظروا۟ انا منتظرون');
INSERT INTO "uthmani_simple" VALUES(6,159,'6:159','ان الذين فرقوا۟ دينهم وكانوا۟ شيعا لست منهم فى شىء انما امرهم الى الله ثم ينبئهم بما كانوا۟ يفعلون');
INSERT INTO "uthmani_simple" VALUES(6,160,'6:160','من جاء بالحسنة فله عشر امثالها ومن جاء بالسيئة فلا يجزى الا مثلها وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(6,161,'6:161','قل اننى هدىنى ربى الى صرط مستقيم دينا قيما ملة ابرهيم حنيفا وما كان من المشركين');
INSERT INTO "uthmani_simple" VALUES(6,162,'6:162','قل ان صلاتى ونسكى ومحياى ومماتى لله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(6,163,'6:163','لا شريك له وبذلك امرت وانا۠ اول المسلمين');
INSERT INTO "uthmani_simple" VALUES(6,164,'6:164','قل اغير الله ابغى ربا وهو رب كل شىء ولا تكسب كل نفس الا عليها ولا تزر وازرة وزر اخرى ثم الى ربكم مرجعكم فينبئكم بما كنتم فيه تختلفون');
INSERT INTO "uthmani_simple" VALUES(6,165,'6:165','وهو الذى جعلكم خلئف الارض ورفع بعضكم فوق بعض درجت ليبلوكم فى ما ءاتىكم ان ربك سريع العقاب وانه لغفور رحيمۢ');
INSERT INTO "uthmani_simple" VALUES(7,1,'7:1','المص');
INSERT INTO "uthmani_simple" VALUES(7,2,'7:2','كتب انزل اليك فلا يكن فى صدرك حرج منه لتنذر به وذكرى للمؤمنين');
INSERT INTO "uthmani_simple" VALUES(7,3,'7:3','اتبعوا۟ ما انزل اليكم من ربكم ولا تتبعوا۟ من دونه اولياء قليلا ما تذكرون');
INSERT INTO "uthmani_simple" VALUES(7,4,'7:4','وكم من قرية اهلكنها فجاءها باسنا بيتا او هم قائلون');
INSERT INTO "uthmani_simple" VALUES(7,5,'7:5','فما كان دعوىهم اذ جاءهم باسنا الا ان قالوا۟ انا كنا ظلمين');
INSERT INTO "uthmani_simple" VALUES(7,6,'7:6','فلنسـلن الذين ارسل اليهم ولنسـلن المرسلين');
INSERT INTO "uthmani_simple" VALUES(7,7,'7:7','فلنقصن عليهم بعلم وما كنا غائبين');
INSERT INTO "uthmani_simple" VALUES(7,8,'7:8','والوزن يومئذ الحق فمن ثقلت موزينه فاو۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(7,9,'7:9','ومن خفت موزينه فاو۟لئك الذين خسروا۟ انفسهم بما كانوا۟ بـايتنا يظلمون');
INSERT INTO "uthmani_simple" VALUES(7,10,'7:10','ولقد مكنكم فى الارض وجعلنا لكم فيها معيش قليلا ما تشكرون');
INSERT INTO "uthmani_simple" VALUES(7,11,'7:11','ولقد خلقنكم ثم صورنكم ثم قلنا للملئكة اسجدوا۟ لءادم فسجدوا۟ الا ابليس لم يكن من السجدين');
INSERT INTO "uthmani_simple" VALUES(7,12,'7:12','قال ما منعك الا تسجد اذ امرتك قال انا۠ خير منه خلقتنى من نار وخلقته من طين');
INSERT INTO "uthmani_simple" VALUES(7,13,'7:13','قال فاهبط منها فما يكون لك ان تتكبر فيها فاخرج انك من الصغرين');
INSERT INTO "uthmani_simple" VALUES(7,14,'7:14','قال انظرنى الى يوم يبعثون');
INSERT INTO "uthmani_simple" VALUES(7,15,'7:15','قال انك من المنظرين');
INSERT INTO "uthmani_simple" VALUES(7,16,'7:16','قال فبما اغويتنى لاقعدن لهم صرطك المستقيم');
INSERT INTO "uthmani_simple" VALUES(7,17,'7:17','ثم لءاتينهم منۢ بين ايديهم ومن خلفهم وعن ايمنهم وعن شمائلهم ولا تجد اكثرهم شكرين');
INSERT INTO "uthmani_simple" VALUES(7,18,'7:18','قال اخرج منها مذءوما مدحورا لمن تبعك منهم لاملان جهنم منكم اجمعين');
INSERT INTO "uthmani_simple" VALUES(7,19,'7:19','ويـادم اسكن انت وزوجك الجنة فكلا من حيث شئتما ولا تقربا هذه الشجرة فتكونا من الظلمين');
INSERT INTO "uthmani_simple" VALUES(7,20,'7:20','فوسوس لهما الشيطن ليبدى لهما ما ورى عنهما من سوءتهما وقال ما نهىكما ربكما عن هذه الشجرة الا ان تكونا ملكين او تكونا من الخلدين');
INSERT INTO "uthmani_simple" VALUES(7,21,'7:21','وقاسمهما انى لكما لمن النصحين');
INSERT INTO "uthmani_simple" VALUES(7,22,'7:22','فدلىهما بغرور فلما ذاقا الشجرة بدت لهما سوءتهما وطفقا يخصفان عليهما من ورق الجنة ونادىهما ربهما الم انهكما عن تلكما الشجرة واقل لكما ان الشيطن لكما عدو مبين');
INSERT INTO "uthmani_simple" VALUES(7,23,'7:23','قالا ربنا ظلمنا انفسنا وان لم تغفر لنا وترحمنا لنكونن من الخسرين');
INSERT INTO "uthmani_simple" VALUES(7,24,'7:24','قال اهبطوا۟ بعضكم لبعض عدو ولكم فى الارض مستقر ومتع الى حين');
INSERT INTO "uthmani_simple" VALUES(7,25,'7:25','قال فيها تحيون وفيها تموتون ومنها تخرجون');
INSERT INTO "uthmani_simple" VALUES(7,26,'7:26','يبنى ءادم قد انزلنا عليكم لباسا يورى سوءتكم وريشا ولباس التقوى ذلك خير ذلك من ءايت الله لعلهم يذكرون');
INSERT INTO "uthmani_simple" VALUES(7,27,'7:27','يبنى ءادم لا يفتننكم الشيطن كما اخرج ابويكم من الجنة ينزع عنهما لباسهما ليريهما سوءتهما انه يرىكم هو وقبيله من حيث لا ترونهم انا جعلنا الشيطين اولياء للذين لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(7,28,'7:28','واذا فعلوا۟ فحشة قالوا۟ وجدنا عليها ءاباءنا والله امرنا بها قل ان الله لا يامر بالفحشاء اتقولون على الله ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(7,29,'7:29','قل امر ربى بالقسط واقيموا۟ وجوهكم عند كل مسجد وادعوه مخلصين له الدين كما بداكم تعودون');
INSERT INTO "uthmani_simple" VALUES(7,30,'7:30','فريقا هدى وفريقا حق عليهم الضللة انهم اتخذوا۟ الشيطين اولياء من دون الله ويحسبون انهم مهتدون');
INSERT INTO "uthmani_simple" VALUES(7,31,'7:31','يبنى ءادم خذوا۟ زينتكم عند كل مسجد وكلوا۟ واشربوا۟ ولا تسرفوا۟ انه لا يحب المسرفين');
INSERT INTO "uthmani_simple" VALUES(7,32,'7:32','قل من حرم زينة الله التى اخرج لعباده والطيبت من الرزق قل هى للذين ءامنوا۟ فى الحيوة الدنيا خالصة يوم القيمة كذلك نفصل الءايت لقوم يعلمون');
INSERT INTO "uthmani_simple" VALUES(7,33,'7:33','قل انما حرم ربى الفوحش ما ظهر منها وما بطن والاثم والبغى بغير الحق وان تشركوا۟ بالله ما لم ينزل به سلطنا وان تقولوا۟ على الله ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(7,34,'7:34','ولكل امة اجل فاذا جاء اجلهم لا يستاخرون ساعة ولا يستقدمون');
INSERT INTO "uthmani_simple" VALUES(7,35,'7:35','يبنى ءادم اما ياتينكم رسل منكم يقصون عليكم ءايتى فمن اتقى واصلح فلا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(7,36,'7:36','والذين كذبوا۟ بـايتنا واستكبروا۟ عنها او۟لئك اصحب النار هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(7,37,'7:37','فمن اظلم ممن افترى على الله كذبا او كذب بـايته او۟لئك ينالهم نصيبهم من الكتب حتى اذا جاءتهم رسلنا يتوفونهم قالوا۟ اين ما كنتم تدعون من دون الله قالوا۟ ضلوا۟ عنا وشهدوا۟ على انفسهم انهم كانوا۟ كفرين');
INSERT INTO "uthmani_simple" VALUES(7,38,'7:38','قال ادخلوا۟ فى امم قد خلت من قبلكم من الجن والانس فى النار كلما دخلت امة لعنت اختها حتى اذا اداركوا۟ فيها جميعا قالت اخرىهم لاولىهم ربنا هؤلاء اضلونا فـاتهم عذابا ضعفا من النار قال لكل ضعف ولكن لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(7,39,'7:39','وقالت اولىهم لاخرىهم فما كان لكم علينا من فضل فذوقوا۟ العذاب بما كنتم تكسبون');
INSERT INTO "uthmani_simple" VALUES(7,40,'7:40','ان الذين كذبوا۟ بـايتنا واستكبروا۟ عنها لا تفتح لهم ابوب السماء ولا يدخلون الجنة حتى يلج الجمل فى سم الخياط وكذلك نجزى المجرمين');
INSERT INTO "uthmani_simple" VALUES(7,41,'7:41','لهم من جهنم مهاد ومن فوقهم غواش وكذلك نجزى الظلمين');
INSERT INTO "uthmani_simple" VALUES(7,42,'7:42','والذين ءامنوا۟ وعملوا۟ الصلحت لا نكلف نفسا الا وسعها او۟لئك اصحب الجنة هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(7,43,'7:43','ونزعنا ما فى صدورهم من غل تجرى من تحتهم الانهر وقالوا۟ الحمد لله الذى هدىنا لهذا وما كنا لنهتدى لولا ان هدىنا الله لقد جاءت رسل ربنا بالحق ونودوا۟ ان تلكم الجنة اورثتموها بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(7,44,'7:44','ونادى اصحب الجنة اصحب النار ان قد وجدنا ما وعدنا ربنا حقا فهل وجدتم ما وعد ربكم حقا قالوا۟ نعم فاذن مؤذنۢ بينهم ان لعنة الله على الظلمين');
INSERT INTO "uthmani_simple" VALUES(7,45,'7:45','الذين يصدون عن سبيل الله ويبغونها عوجا وهم بالءاخرة كفرون');
INSERT INTO "uthmani_simple" VALUES(7,46,'7:46','وبينهما حجاب وعلى الاعراف رجال يعرفون كلۢا بسيمىهم ونادوا۟ اصحب الجنة ان سلم عليكم لم يدخلوها وهم يطمعون');
INSERT INTO "uthmani_simple" VALUES(7,47,'7:47','واذا صرفت ابصرهم تلقاء اصحب النار قالوا۟ ربنا لا تجعلنا مع القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(7,48,'7:48','ونادى اصحب الاعراف رجالا يعرفونهم بسيمىهم قالوا۟ ما اغنى عنكم جمعكم وما كنتم تستكبرون');
INSERT INTO "uthmani_simple" VALUES(7,49,'7:49','اهؤلاء الذين اقسمتم لا ينالهم الله برحمة ادخلوا۟ الجنة لا خوف عليكم ولا انتم تحزنون');
INSERT INTO "uthmani_simple" VALUES(7,50,'7:50','ونادى اصحب النار اصحب الجنة ان افيضوا۟ علينا من الماء او مما رزقكم الله قالوا۟ ان الله حرمهما على الكفرين');
INSERT INTO "uthmani_simple" VALUES(7,51,'7:51','الذين اتخذوا۟ دينهم لهوا ولعبا وغرتهم الحيوة الدنيا فاليوم ننسىهم كما نسوا۟ لقاء يومهم هذا وما كانوا۟ بـايتنا يجحدون');
INSERT INTO "uthmani_simple" VALUES(7,52,'7:52','ولقد جئنهم بكتب فصلنه على علم هدى ورحمة لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(7,53,'7:53','هل ينظرون الا تاويله يوم ياتى تاويله يقول الذين نسوه من قبل قد جاءت رسل ربنا بالحق فهل لنا من شفعاء فيشفعوا۟ لنا او نرد فنعمل غير الذى كنا نعمل قد خسروا۟ انفسهم وضل عنهم ما كانوا۟ يفترون');
INSERT INTO "uthmani_simple" VALUES(7,54,'7:54','ان ربكم الله الذى خلق السموت والارض فى ستة ايام ثم استوى على العرش يغشى اليل النهار يطلبه حثيثا والشمس والقمر والنجوم مسخرتۭ بامره الا له الخلق والامر تبارك الله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(7,55,'7:55','ادعوا۟ ربكم تضرعا وخفية انه لا يحب المعتدين');
INSERT INTO "uthmani_simple" VALUES(7,56,'7:56','ولا تفسدوا۟ فى الارض بعد اصلحها وادعوه خوفا وطمعا ان رحمت الله قريب من المحسنين');
INSERT INTO "uthmani_simple" VALUES(7,57,'7:57','وهو الذى يرسل الريح بشرۢا بين يدى رحمته حتى اذا اقلت سحابا ثقالا سقنه لبلد ميت فانزلنا به الماء فاخرجنا به من كل الثمرت كذلك نخرج الموتى لعلكم تذكرون');
INSERT INTO "uthmani_simple" VALUES(7,58,'7:58','والبلد الطيب يخرج نباته باذن ربه والذى خبث لا يخرج الا نكدا كذلك نصرف الءايت لقوم يشكرون');
INSERT INTO "uthmani_simple" VALUES(7,59,'7:59','لقد ارسلنا نوحا الى قومه فقال يقوم اعبدوا۟ الله ما لكم من اله غيره انى اخاف عليكم عذاب يوم عظيم');
INSERT INTO "uthmani_simple" VALUES(7,60,'7:60','قال الملا من قومه انا لنرىك فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(7,61,'7:61','قال يقوم ليس بى ضللة ولكنى رسول من رب العلمين');
INSERT INTO "uthmani_simple" VALUES(7,62,'7:62','ابلغكم رسلت ربى وانصح لكم واعلم من الله ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(7,63,'7:63','اوعجبتم ان جاءكم ذكر من ربكم على رجل منكم لينذركم ولتتقوا۟ ولعلكم ترحمون');
INSERT INTO "uthmani_simple" VALUES(7,64,'7:64','فكذبوه فانجينه والذين معه فى الفلك واغرقنا الذين كذبوا۟ بـايتنا انهم كانوا۟ قوما عمين');
INSERT INTO "uthmani_simple" VALUES(7,65,'7:65','والى عاد اخاهم هودا قال يقوم اعبدوا۟ الله ما لكم من اله غيره افلا تتقون');
INSERT INTO "uthmani_simple" VALUES(7,66,'7:66','قال الملا الذين كفروا۟ من قومه انا لنرىك فى سفاهة وانا لنظنك من الكذبين');
INSERT INTO "uthmani_simple" VALUES(7,67,'7:67','قال يقوم ليس بى سفاهة ولكنى رسول من رب العلمين');
INSERT INTO "uthmani_simple" VALUES(7,68,'7:68','ابلغكم رسلت ربى وانا۠ لكم ناصح امين');
INSERT INTO "uthmani_simple" VALUES(7,69,'7:69','اوعجبتم ان جاءكم ذكر من ربكم على رجل منكم لينذركم واذكروا۟ اذ جعلكم خلفاء منۢ بعد قوم نوح وزادكم فى الخلق بصۜطة فاذكروا۟ ءالاء الله لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(7,70,'7:70','قالوا۟ اجئتنا لنعبد الله وحده ونذر ما كان يعبد ءاباؤنا فاتنا بما تعدنا ان كنت من الصدقين');
INSERT INTO "uthmani_simple" VALUES(7,71,'7:71','قال قد وقع عليكم من ربكم رجس وغضب اتجدلوننى فى اسماء سميتموها انتم وءاباؤكم ما نزل الله بها من سلطن فانتظروا۟ انى معكم من المنتظرين');
INSERT INTO "uthmani_simple" VALUES(7,72,'7:72','فانجينه والذين معه برحمة منا وقطعنا دابر الذين كذبوا۟ بـايتنا وما كانوا۟ مؤمنين');
INSERT INTO "uthmani_simple" VALUES(7,73,'7:73','والى ثمود اخاهم صلحا قال يقوم اعبدوا۟ الله ما لكم من اله غيره قد جاءتكم بينة من ربكم هذه ناقة الله لكم ءاية فذروها تاكل فى ارض الله ولا تمسوها بسوء فياخذكم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(7,74,'7:74','واذكروا۟ اذ جعلكم خلفاء منۢ بعد عاد وبواكم فى الارض تتخذون من سهولها قصورا وتنحتون الجبال بيوتا فاذكروا۟ ءالاء الله ولا تعثوا۟ فى الارض مفسدين');
INSERT INTO "uthmani_simple" VALUES(7,75,'7:75','قال الملا الذين استكبروا۟ من قومه للذين استضعفوا۟ لمن ءامن منهم اتعلمون ان صلحا مرسل من ربه قالوا۟ انا بما ارسل به مؤمنون');
INSERT INTO "uthmani_simple" VALUES(7,76,'7:76','قال الذين استكبروا۟ انا بالذى ءامنتم به كفرون');
INSERT INTO "uthmani_simple" VALUES(7,77,'7:77','فعقروا۟ الناقة وعتوا۟ عن امر ربهم وقالوا۟ يصلح ائتنا بما تعدنا ان كنت من المرسلين');
INSERT INTO "uthmani_simple" VALUES(7,78,'7:78','فاخذتهم الرجفة فاصبحوا۟ فى دارهم جثمين');
INSERT INTO "uthmani_simple" VALUES(7,79,'7:79','فتولى عنهم وقال يقوم لقد ابلغتكم رسالة ربى ونصحت لكم ولكن لا تحبون النصحين');
INSERT INTO "uthmani_simple" VALUES(7,80,'7:80','ولوطا اذ قال لقومه اتاتون الفحشة ما سبقكم بها من احد من العلمين');
INSERT INTO "uthmani_simple" VALUES(7,81,'7:81','انكم لتاتون الرجال شهوة من دون النساء بل انتم قوم مسرفون');
INSERT INTO "uthmani_simple" VALUES(7,82,'7:82','وما كان جواب قومه الا ان قالوا۟ اخرجوهم من قريتكم انهم اناس يتطهرون');
INSERT INTO "uthmani_simple" VALUES(7,83,'7:83','فانجينه واهله الا امراته كانت من الغبرين');
INSERT INTO "uthmani_simple" VALUES(7,84,'7:84','وامطرنا عليهم مطرا فانظر كيف كان عقبة المجرمين');
INSERT INTO "uthmani_simple" VALUES(7,85,'7:85','والى مدين اخاهم شعيبا قال يقوم اعبدوا۟ الله ما لكم من اله غيره قد جاءتكم بينة من ربكم فاوفوا۟ الكيل والميزان ولا تبخسوا۟ الناس اشياءهم ولا تفسدوا۟ فى الارض بعد اصلحها ذلكم خير لكم ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(7,86,'7:86','ولا تقعدوا۟ بكل صرط توعدون وتصدون عن سبيل الله من ءامن به وتبغونها عوجا واذكروا۟ اذ كنتم قليلا فكثركم وانظروا۟ كيف كان عقبة المفسدين');
INSERT INTO "uthmani_simple" VALUES(7,87,'7:87','وان كان طائفة منكم ءامنوا۟ بالذى ارسلت به وطائفة لم يؤمنوا۟ فاصبروا۟ حتى يحكم الله بيننا وهو خير الحكمين');
INSERT INTO "uthmani_simple" VALUES(7,88,'7:88','قال الملا الذين استكبروا۟ من قومه لنخرجنك يشعيب والذين ءامنوا۟ معك من قريتنا او لتعودن فى ملتنا قال اولو كنا كرهين');
INSERT INTO "uthmani_simple" VALUES(7,89,'7:89','قد افترينا على الله كذبا ان عدنا فى ملتكم بعد اذ نجىنا الله منها وما يكون لنا ان نعود فيها الا ان يشاء الله ربنا وسع ربنا كل شىء علما على الله توكلنا ربنا افتح بيننا وبين قومنا بالحق وانت خير الفتحين');
INSERT INTO "uthmani_simple" VALUES(7,90,'7:90','وقال الملا الذين كفروا۟ من قومه لئن اتبعتم شعيبا انكم اذا لخسرون');
INSERT INTO "uthmani_simple" VALUES(7,91,'7:91','فاخذتهم الرجفة فاصبحوا۟ فى دارهم جثمين');
INSERT INTO "uthmani_simple" VALUES(7,92,'7:92','الذين كذبوا۟ شعيبا كان لم يغنوا۟ فيها الذين كذبوا۟ شعيبا كانوا۟ هم الخسرين');
INSERT INTO "uthmani_simple" VALUES(7,93,'7:93','فتولى عنهم وقال يقوم لقد ابلغتكم رسلت ربى ونصحت لكم فكيف ءاسى على قوم كفرين');
INSERT INTO "uthmani_simple" VALUES(7,94,'7:94','وما ارسلنا فى قرية من نبى الا اخذنا اهلها بالباساء والضراء لعلهم يضرعون');
INSERT INTO "uthmani_simple" VALUES(7,95,'7:95','ثم بدلنا مكان السيئة الحسنة حتى عفوا۟ وقالوا۟ قد مس ءاباءنا الضراء والسراء فاخذنهم بغتة وهم لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(7,96,'7:96','ولو ان اهل القرى ءامنوا۟ واتقوا۟ لفتحنا عليهم بركت من السماء والارض ولكن كذبوا۟ فاخذنهم بما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(7,97,'7:97','افامن اهل القرى ان ياتيهم باسنا بيتا وهم نائمون');
INSERT INTO "uthmani_simple" VALUES(7,98,'7:98','اوامن اهل القرى ان ياتيهم باسنا ضحى وهم يلعبون');
INSERT INTO "uthmani_simple" VALUES(7,99,'7:99','افامنوا۟ مكر الله فلا يامن مكر الله الا القوم الخسرون');
INSERT INTO "uthmani_simple" VALUES(7,100,'7:100','اولم يهد للذين يرثون الارض منۢ بعد اهلها ان لو نشاء اصبنهم بذنوبهم ونطبع على قلوبهم فهم لا يسمعون');
INSERT INTO "uthmani_simple" VALUES(7,101,'7:101','تلك القرى نقص عليك من انۢبائها ولقد جاءتهم رسلهم بالبينت فما كانوا۟ ليؤمنوا۟ بما كذبوا۟ من قبل كذلك يطبع الله على قلوب الكفرين');
INSERT INTO "uthmani_simple" VALUES(7,102,'7:102','وما وجدنا لاكثرهم من عهد وان وجدنا اكثرهم لفسقين');
INSERT INTO "uthmani_simple" VALUES(7,103,'7:103','ثم بعثنا منۢ بعدهم موسى بـايتنا الى فرعون وملاي۟ه فظلموا۟ بها فانظر كيف كان عقبة المفسدين');
INSERT INTO "uthmani_simple" VALUES(7,104,'7:104','وقال موسى يفرعون انى رسول من رب العلمين');
INSERT INTO "uthmani_simple" VALUES(7,105,'7:105','حقيق على ان لا اقول على الله الا الحق قد جئتكم ببينة من ربكم فارسل معى بنى اسرءيل');
INSERT INTO "uthmani_simple" VALUES(7,106,'7:106','قال ان كنت جئت بـاية فات بها ان كنت من الصدقين');
INSERT INTO "uthmani_simple" VALUES(7,107,'7:107','فالقى عصاه فاذا هى ثعبان مبين');
INSERT INTO "uthmani_simple" VALUES(7,108,'7:108','ونزع يده فاذا هى بيضاء للنظرين');
INSERT INTO "uthmani_simple" VALUES(7,109,'7:109','قال الملا من قوم فرعون ان هذا لسحر عليم');
INSERT INTO "uthmani_simple" VALUES(7,110,'7:110','يريد ان يخرجكم من ارضكم فماذا تامرون');
INSERT INTO "uthmani_simple" VALUES(7,111,'7:111','قالوا۟ ارجه واخاه وارسل فى المدائن حشرين');
INSERT INTO "uthmani_simple" VALUES(7,112,'7:112','ياتوك بكل سحر عليم');
INSERT INTO "uthmani_simple" VALUES(7,113,'7:113','وجاء السحرة فرعون قالوا۟ ان لنا لاجرا ان كنا نحن الغلبين');
INSERT INTO "uthmani_simple" VALUES(7,114,'7:114','قال نعم وانكم لمن المقربين');
INSERT INTO "uthmani_simple" VALUES(7,115,'7:115','قالوا۟ يموسى اما ان تلقى واما ان نكون نحن الملقين');
INSERT INTO "uthmani_simple" VALUES(7,116,'7:116','قال القوا۟ فلما القوا۟ سحروا۟ اعين الناس واسترهبوهم وجاءو بسحر عظيم');
INSERT INTO "uthmani_simple" VALUES(7,117,'7:117','واوحينا الى موسى ان الق عصاك فاذا هى تلقف ما يافكون');
INSERT INTO "uthmani_simple" VALUES(7,118,'7:118','فوقع الحق وبطل ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(7,119,'7:119','فغلبوا۟ هنالك وانقلبوا۟ صغرين');
INSERT INTO "uthmani_simple" VALUES(7,120,'7:120','والقى السحرة سجدين');
INSERT INTO "uthmani_simple" VALUES(7,121,'7:121','قالوا۟ ءامنا برب العلمين');
INSERT INTO "uthmani_simple" VALUES(7,122,'7:122','رب موسى وهرون');
INSERT INTO "uthmani_simple" VALUES(7,123,'7:123','قال فرعون ءامنتم به قبل ان ءاذن لكم ان هذا لمكر مكرتموه فى المدينة لتخرجوا۟ منها اهلها فسوف تعلمون');
INSERT INTO "uthmani_simple" VALUES(7,124,'7:124','لاقطعن ايديكم وارجلكم من خلف ثم لاصلبنكم اجمعين');
INSERT INTO "uthmani_simple" VALUES(7,125,'7:125','قالوا۟ انا الى ربنا منقلبون');
INSERT INTO "uthmani_simple" VALUES(7,126,'7:126','وما تنقم منا الا ان ءامنا بـايت ربنا لما جاءتنا ربنا افرغ علينا صبرا وتوفنا مسلمين');
INSERT INTO "uthmani_simple" VALUES(7,127,'7:127','وقال الملا من قوم فرعون اتذر موسى وقومه ليفسدوا۟ فى الارض ويذرك وءالهتك قال سنقتل ابناءهم ونستحى نساءهم وانا فوقهم قهرون');
INSERT INTO "uthmani_simple" VALUES(7,128,'7:128','قال موسى لقومه استعينوا۟ بالله واصبروا۟ ان الارض لله يورثها من يشاء من عباده والعقبة للمتقين');
INSERT INTO "uthmani_simple" VALUES(7,129,'7:129','قالوا۟ اوذينا من قبل ان تاتينا ومنۢ بعد ما جئتنا قال عسى ربكم ان يهلك عدوكم ويستخلفكم فى الارض فينظر كيف تعملون');
INSERT INTO "uthmani_simple" VALUES(7,130,'7:130','ولقد اخذنا ءال فرعون بالسنين ونقص من الثمرت لعلهم يذكرون');
INSERT INTO "uthmani_simple" VALUES(7,131,'7:131','فاذا جاءتهم الحسنة قالوا۟ لنا هذه وان تصبهم سيئة يطيروا۟ بموسى ومن معه الا انما طئرهم عند الله ولكن اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(7,132,'7:132','وقالوا۟ مهما تاتنا به من ءاية لتسحرنا بها فما نحن لك بمؤمنين');
INSERT INTO "uthmani_simple" VALUES(7,133,'7:133','فارسلنا عليهم الطوفان والجراد والقمل والضفادع والدم ءايت مفصلت فاستكبروا۟ وكانوا۟ قوما مجرمين');
INSERT INTO "uthmani_simple" VALUES(7,134,'7:134','ولما وقع عليهم الرجز قالوا۟ يموسى ادع لنا ربك بما عهد عندك لئن كشفت عنا الرجز لنؤمنن لك ولنرسلن معك بنى اسرءيل');
INSERT INTO "uthmani_simple" VALUES(7,135,'7:135','فلما كشفنا عنهم الرجز الى اجل هم بلغوه اذا هم ينكثون');
INSERT INTO "uthmani_simple" VALUES(7,136,'7:136','فانتقمنا منهم فاغرقنهم فى اليم بانهم كذبوا۟ بـايتنا وكانوا۟ عنها غفلين');
INSERT INTO "uthmani_simple" VALUES(7,137,'7:137','واورثنا القوم الذين كانوا۟ يستضعفون مشرق الارض ومغربها التى بركنا فيها وتمت كلمت ربك الحسنى على بنى اسرءيل بما صبروا۟ ودمرنا ما كان يصنع فرعون وقومه وما كانوا۟ يعرشون');
INSERT INTO "uthmani_simple" VALUES(7,138,'7:138','وجوزنا ببنى اسرءيل البحر فاتوا۟ على قوم يعكفون على اصنام لهم قالوا۟ يموسى اجعل لنا الها كما لهم ءالهة قال انكم قوم تجهلون');
INSERT INTO "uthmani_simple" VALUES(7,139,'7:139','ان هؤلاء متبر ما هم فيه وبطل ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(7,140,'7:140','قال اغير الله ابغيكم الها وهو فضلكم على العلمين');
INSERT INTO "uthmani_simple" VALUES(7,141,'7:141','واذ انجينكم من ءال فرعون يسومونكم سوء العذاب يقتلون ابناءكم ويستحيون نساءكم وفى ذلكم بلاء من ربكم عظيم');
INSERT INTO "uthmani_simple" VALUES(7,142,'7:142','ووعدنا موسى ثلثين ليلة واتممنها بعشر فتم ميقت ربه اربعين ليلة وقال موسى لاخيه هرون اخلفنى فى قومى واصلح ولا تتبع سبيل المفسدين');
INSERT INTO "uthmani_simple" VALUES(7,143,'7:143','ولما جاء موسى لميقتنا وكلمه ربه قال رب ارنى انظر اليك قال لن ترىنى ولكن انظر الى الجبل فان استقر مكانه فسوف ترىنى فلما تجلى ربه للجبل جعله دكا وخر موسى صعقا فلما افاق قال سبحنك تبت اليك وانا۠ اول المؤمنين');
INSERT INTO "uthmani_simple" VALUES(7,144,'7:144','قال يموسى انى اصطفيتك على الناس برسلتى وبكلمى فخذ ما ءاتيتك وكن من الشكرين');
INSERT INTO "uthmani_simple" VALUES(7,145,'7:145','وكتبنا له فى الالواح من كل شىء موعظة وتفصيلا لكل شىء فخذها بقوة وامر قومك ياخذوا۟ باحسنها ساو۟ريكم دار الفسقين');
INSERT INTO "uthmani_simple" VALUES(7,146,'7:146','ساصرف عن ءايتى الذين يتكبرون فى الارض بغير الحق وان يروا۟ كل ءاية لا يؤمنوا۟ بها وان يروا۟ سبيل الرشد لا يتخذوه سبيلا وان يروا۟ سبيل الغى يتخذوه سبيلا ذلك بانهم كذبوا۟ بـايتنا وكانوا۟ عنها غفلين');
INSERT INTO "uthmani_simple" VALUES(7,147,'7:147','والذين كذبوا۟ بـايتنا ولقاء الءاخرة حبطت اعملهم هل يجزون الا ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(7,148,'7:148','واتخذ قوم موسى منۢ بعده من حليهم عجلا جسدا له خوار الم يروا۟ انه لا يكلمهم ولا يهديهم سبيلا اتخذوه وكانوا۟ ظلمين');
INSERT INTO "uthmani_simple" VALUES(7,149,'7:149','ولما سقط فى ايديهم وراوا۟ انهم قد ضلوا۟ قالوا۟ لئن لم يرحمنا ربنا ويغفر لنا لنكونن من الخسرين');
INSERT INTO "uthmani_simple" VALUES(7,150,'7:150','ولما رجع موسى الى قومه غضبن اسفا قال بئسما خلفتمونى منۢ بعدى اعجلتم امر ربكم والقى الالواح واخذ براس اخيه يجره اليه قال ابن ام ان القوم استضعفونى وكادوا۟ يقتلوننى فلا تشمت بى الاعداء ولا تجعلنى مع القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(7,151,'7:151','قال رب اغفر لى ولاخى وادخلنا فى رحمتك وانت ارحم الرحمين');
INSERT INTO "uthmani_simple" VALUES(7,152,'7:152','ان الذين اتخذوا۟ العجل سينالهم غضب من ربهم وذلة فى الحيوة الدنيا وكذلك نجزى المفترين');
INSERT INTO "uthmani_simple" VALUES(7,153,'7:153','والذين عملوا۟ السيـات ثم تابوا۟ منۢ بعدها وءامنوا۟ ان ربك منۢ بعدها لغفور رحيم');
INSERT INTO "uthmani_simple" VALUES(7,154,'7:154','ولما سكت عن موسى الغضب اخذ الالواح وفى نسختها هدى ورحمة للذين هم لربهم يرهبون');
INSERT INTO "uthmani_simple" VALUES(7,155,'7:155','واختار موسى قومه سبعين رجلا لميقتنا فلما اخذتهم الرجفة قال رب لو شئت اهلكتهم من قبل وايى اتهلكنا بما فعل السفهاء منا ان هى الا فتنتك تضل بها من تشاء وتهدى من تشاء انت ولينا فاغفر لنا وارحمنا وانت خير الغفرين');
INSERT INTO "uthmani_simple" VALUES(7,156,'7:156','واكتب لنا فى هذه الدنيا حسنة وفى الءاخرة انا هدنا اليك قال عذابى اصيب به من اشاء ورحمتى وسعت كل شىء فساكتبها للذين يتقون ويؤتون الزكوة والذين هم بـايتنا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(7,157,'7:157','الذين يتبعون الرسول النبى الامى الذى يجدونه مكتوبا عندهم فى التورىة والانجيل يامرهم بالمعروف وينهىهم عن المنكر ويحل لهم الطيبت ويحرم عليهم الخبئث ويضع عنهم اصرهم والاغلل التى كانت عليهم فالذين ءامنوا۟ به وعزروه ونصروه واتبعوا۟ النور الذى انزل معه او۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(7,158,'7:158','قل يايها الناس انى رسول الله اليكم جميعا الذى له ملك السموت والارض لا اله الا هو يحى ويميت فـامنوا۟ بالله ورسوله النبى الامى الذى يؤمن بالله وكلمته واتبعوه لعلكم تهتدون');
INSERT INTO "uthmani_simple" VALUES(7,159,'7:159','ومن قوم موسى امة يهدون بالحق وبه يعدلون');
INSERT INTO "uthmani_simple" VALUES(7,160,'7:160','وقطعنهم اثنتى عشرة اسباطا امما واوحينا الى موسى اذ استسقىه قومه ان اضرب بعصاك الحجر فانۢبجست منه اثنتا عشرة عينا قد علم كل اناس مشربهم وظللنا عليهم الغمم وانزلنا عليهم المن والسلوى كلوا۟ من طيبت ما رزقنكم وما ظلمونا ولكن كانوا۟ انفسهم يظلمون');
INSERT INTO "uthmani_simple" VALUES(7,161,'7:161','واذ قيل لهم اسكنوا۟ هذه القرية وكلوا۟ منها حيث شئتم وقولوا۟ حطة وادخلوا۟ الباب سجدا نغفر لكم خطيـتكم سنزيد المحسنين');
INSERT INTO "uthmani_simple" VALUES(7,162,'7:162','فبدل الذين ظلموا۟ منهم قولا غير الذى قيل لهم فارسلنا عليهم رجزا من السماء بما كانوا۟ يظلمون');
INSERT INTO "uthmani_simple" VALUES(7,163,'7:163','وسـلهم عن القرية التى كانت حاضرة البحر اذ يعدون فى السبت اذ تاتيهم حيتانهم يوم سبتهم شرعا ويوم لا يسبتون لا تاتيهم كذلك نبلوهم بما كانوا۟ يفسقون');
INSERT INTO "uthmani_simple" VALUES(7,164,'7:164','واذ قالت امة منهم لم تعظون قوما الله مهلكهم او معذبهم عذابا شديدا قالوا۟ معذرة الى ربكم ولعلهم يتقون');
INSERT INTO "uthmani_simple" VALUES(7,165,'7:165','فلما نسوا۟ ما ذكروا۟ به انجينا الذين ينهون عن السوء واخذنا الذين ظلموا۟ بعذابۭ بـيسۭ بما كانوا۟ يفسقون');
INSERT INTO "uthmani_simple" VALUES(7,166,'7:166','فلما عتوا۟ عن ما نهوا۟ عنه قلنا لهم كونوا۟ قردة خسـين');
INSERT INTO "uthmani_simple" VALUES(7,167,'7:167','واذ تاذن ربك ليبعثن عليهم الى يوم القيمة من يسومهم سوء العذاب ان ربك لسريع العقاب وانه لغفور رحيم');
INSERT INTO "uthmani_simple" VALUES(7,168,'7:168','وقطعنهم فى الارض امما منهم الصلحون ومنهم دون ذلك وبلونهم بالحسنت والسيـات لعلهم يرجعون');
INSERT INTO "uthmani_simple" VALUES(7,169,'7:169','فخلف منۢ بعدهم خلف ورثوا۟ الكتب ياخذون عرض هذا الادنى ويقولون سيغفر لنا وان ياتهم عرض مثله ياخذوه الم يؤخذ عليهم ميثق الكتب ان لا يقولوا۟ على الله الا الحق ودرسوا۟ ما فيه والدار الءاخرة خير للذين يتقون افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(7,170,'7:170','والذين يمسكون بالكتب واقاموا۟ الصلوة انا لا نضيع اجر المصلحين');
INSERT INTO "uthmani_simple" VALUES(7,171,'7:171','واذ نتقنا الجبل فوقهم كانه ظلة وظنوا۟ انه واقعۢ بهم خذوا۟ ما ءاتينكم بقوة واذكروا۟ ما فيه لعلكم تتقون');
INSERT INTO "uthmani_simple" VALUES(7,172,'7:172','واذ اخذ ربك منۢ بنى ءادم من ظهورهم ذريتهم واشهدهم على انفسهم الست بربكم قالوا۟ بلى شهدنا ان تقولوا۟ يوم القيمة انا كنا عن هذا غفلين');
INSERT INTO "uthmani_simple" VALUES(7,173,'7:173','او تقولوا۟ انما اشرك ءاباؤنا من قبل وكنا ذرية منۢ بعدهم افتهلكنا بما فعل المبطلون');
INSERT INTO "uthmani_simple" VALUES(7,174,'7:174','وكذلك نفصل الءايت ولعلهم يرجعون');
INSERT INTO "uthmani_simple" VALUES(7,175,'7:175','واتل عليهم نبا الذى ءاتينه ءايتنا فانسلخ منها فاتبعه الشيطن فكان من الغاوين');
INSERT INTO "uthmani_simple" VALUES(7,176,'7:176','ولو شئنا لرفعنه بها ولكنه اخلد الى الارض واتبع هوىه فمثله كمثل الكلب ان تحمل عليه يلهث او تتركه يلهث ذلك مثل القوم الذين كذبوا۟ بـايتنا فاقصص القصص لعلهم يتفكرون');
INSERT INTO "uthmani_simple" VALUES(7,177,'7:177','ساء مثلا القوم الذين كذبوا۟ بـايتنا وانفسهم كانوا۟ يظلمون');
INSERT INTO "uthmani_simple" VALUES(7,178,'7:178','من يهد الله فهو المهتدى ومن يضلل فاو۟لئك هم الخسرون');
INSERT INTO "uthmani_simple" VALUES(7,179,'7:179','ولقد ذرانا لجهنم كثيرا من الجن والانس لهم قلوب لا يفقهون بها ولهم اعين لا يبصرون بها ولهم ءاذان لا يسمعون بها او۟لئك كالانعم بل هم اضل او۟لئك هم الغفلون');
INSERT INTO "uthmani_simple" VALUES(7,180,'7:180','ولله الاسماء الحسنى فادعوه بها وذروا۟ الذين يلحدون فى اسمئه سيجزون ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(7,181,'7:181','وممن خلقنا امة يهدون بالحق وبه يعدلون');
INSERT INTO "uthmani_simple" VALUES(7,182,'7:182','والذين كذبوا۟ بـايتنا سنستدرجهم من حيث لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(7,183,'7:183','واملى لهم ان كيدى متين');
INSERT INTO "uthmani_simple" VALUES(7,184,'7:184','اولم يتفكروا۟ ما بصاحبهم من جنة ان هو الا نذير مبين');
INSERT INTO "uthmani_simple" VALUES(7,185,'7:185','اولم ينظروا۟ فى ملكوت السموت والارض وما خلق الله من شىء وان عسى ان يكون قد اقترب اجلهم فباى حديثۭ بعده يؤمنون');
INSERT INTO "uthmani_simple" VALUES(7,186,'7:186','من يضلل الله فلا هادى له ويذرهم فى طغينهم يعمهون');
INSERT INTO "uthmani_simple" VALUES(7,187,'7:187','يسـلونك عن الساعة ايان مرسىها قل انما علمها عند ربى لا يجليها لوقتها الا هو ثقلت فى السموت والارض لا تاتيكم الا بغتة يسـلونك كانك حفى عنها قل انما علمها عند الله ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(7,188,'7:188','قل لا املك لنفسى نفعا ولا ضرا الا ما شاء الله ولو كنت اعلم الغيب لاستكثرت من الخير وما مسنى السوء ان انا۠ الا نذير وبشير لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(7,189,'7:189','هو الذى خلقكم من نفس وحدة وجعل منها زوجها ليسكن اليها فلما تغشىها حملت حملا خفيفا فمرت به فلما اثقلت دعوا الله ربهما لئن ءاتيتنا صلحا لنكونن من الشكرين');
INSERT INTO "uthmani_simple" VALUES(7,190,'7:190','فلما ءاتىهما صلحا جعلا له شركاء فيما ءاتىهما فتعلى الله عما يشركون');
INSERT INTO "uthmani_simple" VALUES(7,191,'7:191','ايشركون ما لا يخلق شيـا وهم يخلقون');
INSERT INTO "uthmani_simple" VALUES(7,192,'7:192','ولا يستطيعون لهم نصرا ولا انفسهم ينصرون');
INSERT INTO "uthmani_simple" VALUES(7,193,'7:193','وان تدعوهم الى الهدى لا يتبعوكم سواء عليكم ادعوتموهم ام انتم صمتون');
INSERT INTO "uthmani_simple" VALUES(7,194,'7:194','ان الذين تدعون من دون الله عباد امثالكم فادعوهم فليستجيبوا۟ لكم ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(7,195,'7:195','الهم ارجل يمشون بها ام لهم ايد يبطشون بها ام لهم اعين يبصرون بها ام لهم ءاذان يسمعون بها قل ادعوا۟ شركاءكم ثم كيدون فلا تنظرون');
INSERT INTO "uthmani_simple" VALUES(7,196,'7:196','ان ولى الله الذى نزل الكتب وهو يتولى الصلحين');
INSERT INTO "uthmani_simple" VALUES(7,197,'7:197','والذين تدعون من دونه لا يستطيعون نصركم ولا انفسهم ينصرون');
INSERT INTO "uthmani_simple" VALUES(7,198,'7:198','وان تدعوهم الى الهدى لا يسمعوا۟ وترىهم ينظرون اليك وهم لا يبصرون');
INSERT INTO "uthmani_simple" VALUES(7,199,'7:199','خذ العفو وامر بالعرف واعرض عن الجهلين');
INSERT INTO "uthmani_simple" VALUES(7,200,'7:200','واما ينزغنك من الشيطن نزغ فاستعذ بالله انه سميع عليم');
INSERT INTO "uthmani_simple" VALUES(7,201,'7:201','ان الذين اتقوا۟ اذا مسهم طئف من الشيطن تذكروا۟ فاذا هم مبصرون');
INSERT INTO "uthmani_simple" VALUES(7,202,'7:202','واخونهم يمدونهم فى الغى ثم لا يقصرون');
INSERT INTO "uthmani_simple" VALUES(7,203,'7:203','واذا لم تاتهم بـاية قالوا۟ لولا اجتبيتها قل انما اتبع ما يوحى الى من ربى هذا بصائر من ربكم وهدى ورحمة لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(7,204,'7:204','واذا قرئ القرءان فاستمعوا۟ له وانصتوا۟ لعلكم ترحمون');
INSERT INTO "uthmani_simple" VALUES(7,205,'7:205','واذكر ربك فى نفسك تضرعا وخيفة ودون الجهر من القول بالغدو والءاصال ولا تكن من الغفلين');
INSERT INTO "uthmani_simple" VALUES(7,206,'7:206','ان الذين عند ربك لا يستكبرون عن عبادته ويسبحونه وله يسجدون');
INSERT INTO "uthmani_simple" VALUES(8,1,'8:1','يسـلونك عن الانفال قل الانفال لله والرسول فاتقوا۟ الله واصلحوا۟ ذات بينكم واطيعوا۟ الله ورسوله ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(8,2,'8:2','انما المؤمنون الذين اذا ذكر الله وجلت قلوبهم واذا تليت عليهم ءايته زادتهم ايمنا وعلى ربهم يتوكلون');
INSERT INTO "uthmani_simple" VALUES(8,3,'8:3','الذين يقيمون الصلوة ومما رزقنهم ينفقون');
INSERT INTO "uthmani_simple" VALUES(8,4,'8:4','او۟لئك هم المؤمنون حقا لهم درجت عند ربهم ومغفرة ورزق كريم');
INSERT INTO "uthmani_simple" VALUES(8,5,'8:5','كما اخرجك ربك منۢ بيتك بالحق وان فريقا من المؤمنين لكرهون');
INSERT INTO "uthmani_simple" VALUES(8,6,'8:6','يجدلونك فى الحق بعدما تبين كانما يساقون الى الموت وهم ينظرون');
INSERT INTO "uthmani_simple" VALUES(8,7,'8:7','واذ يعدكم الله احدى الطائفتين انها لكم وتودون ان غير ذات الشوكة تكون لكم ويريد الله ان يحق الحق بكلمته ويقطع دابر الكفرين');
INSERT INTO "uthmani_simple" VALUES(8,8,'8:8','ليحق الحق ويبطل البطل ولو كره المجرمون');
INSERT INTO "uthmani_simple" VALUES(8,9,'8:9','اذ تستغيثون ربكم فاستجاب لكم انى ممدكم بالف من الملئكة مردفين');
INSERT INTO "uthmani_simple" VALUES(8,10,'8:10','وما جعله الله الا بشرى ولتطمئن به قلوبكم وما النصر الا من عند الله ان الله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(8,11,'8:11','اذ يغشيكم النعاس امنة منه وينزل عليكم من السماء ماء ليطهركم به ويذهب عنكم رجز الشيطن وليربط على قلوبكم ويثبت به الاقدام');
INSERT INTO "uthmani_simple" VALUES(8,12,'8:12','اذ يوحى ربك الى الملئكة انى معكم فثبتوا۟ الذين ءامنوا۟ سالقى فى قلوب الذين كفروا۟ الرعب فاضربوا۟ فوق الاعناق واضربوا۟ منهم كل بنان');
INSERT INTO "uthmani_simple" VALUES(8,13,'8:13','ذلك بانهم شاقوا۟ الله ورسوله ومن يشاقق الله ورسوله فان الله شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(8,14,'8:14','ذلكم فذوقوه وان للكفرين عذاب النار');
INSERT INTO "uthmani_simple" VALUES(8,15,'8:15','يايها الذين ءامنوا۟ اذا لقيتم الذين كفروا۟ زحفا فلا تولوهم الادبار');
INSERT INTO "uthmani_simple" VALUES(8,16,'8:16','ومن يولهم يومئذ دبره الا متحرفا لقتال او متحيزا الى فئة فقد باء بغضب من الله وماوىه جهنم وبئس المصير');
INSERT INTO "uthmani_simple" VALUES(8,17,'8:17','فلم تقتلوهم ولكن الله قتلهم وما رميت اذ رميت ولكن الله رمى وليبلى المؤمنين منه بلاء حسنا ان الله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(8,18,'8:18','ذلكم وان الله موهن كيد الكفرين');
INSERT INTO "uthmani_simple" VALUES(8,19,'8:19','ان تستفتحوا۟ فقد جاءكم الفتح وان تنتهوا۟ فهو خير لكم وان تعودوا۟ نعد ولن تغنى عنكم فئتكم شيـا ولو كثرت وان الله مع المؤمنين');
INSERT INTO "uthmani_simple" VALUES(8,20,'8:20','يايها الذين ءامنوا۟ اطيعوا۟ الله ورسوله ولا تولوا۟ عنه وانتم تسمعون');
INSERT INTO "uthmani_simple" VALUES(8,21,'8:21','ولا تكونوا۟ كالذين قالوا۟ سمعنا وهم لا يسمعون');
INSERT INTO "uthmani_simple" VALUES(8,22,'8:22','ان شر الدواب عند الله الصم البكم الذين لا يعقلون');
INSERT INTO "uthmani_simple" VALUES(8,23,'8:23','ولو علم الله فيهم خيرا لاسمعهم ولو اسمعهم لتولوا۟ وهم معرضون');
INSERT INTO "uthmani_simple" VALUES(8,24,'8:24','يايها الذين ءامنوا۟ استجيبوا۟ لله وللرسول اذا دعاكم لما يحييكم واعلموا۟ ان الله يحول بين المرء وقلبه وانه اليه تحشرون');
INSERT INTO "uthmani_simple" VALUES(8,25,'8:25','واتقوا۟ فتنة لا تصيبن الذين ظلموا۟ منكم خاصة واعلموا۟ ان الله شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(8,26,'8:26','واذكروا۟ اذ انتم قليل مستضعفون فى الارض تخافون ان يتخطفكم الناس فـاوىكم وايدكم بنصره ورزقكم من الطيبت لعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(8,27,'8:27','يايها الذين ءامنوا۟ لا تخونوا۟ الله والرسول وتخونوا۟ امنتكم وانتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(8,28,'8:28','واعلموا۟ انما امولكم واولدكم فتنة وان الله عنده اجر عظيم');
INSERT INTO "uthmani_simple" VALUES(8,29,'8:29','يايها الذين ءامنوا۟ ان تتقوا۟ الله يجعل لكم فرقانا ويكفر عنكم سيـاتكم ويغفر لكم والله ذو الفضل العظيم');
INSERT INTO "uthmani_simple" VALUES(8,30,'8:30','واذ يمكر بك الذين كفروا۟ ليثبتوك او يقتلوك او يخرجوك ويمكرون ويمكر الله والله خير المكرين');
INSERT INTO "uthmani_simple" VALUES(8,31,'8:31','واذا تتلى عليهم ءايتنا قالوا۟ قد سمعنا لو نشاء لقلنا مثل هذا ان هذا الا اسطير الاولين');
INSERT INTO "uthmani_simple" VALUES(8,32,'8:32','واذ قالوا۟ اللهم ان كان هذا هو الحق من عندك فامطر علينا حجارة من السماء او ائتنا بعذاب اليم');
INSERT INTO "uthmani_simple" VALUES(8,33,'8:33','وما كان الله ليعذبهم وانت فيهم وما كان الله معذبهم وهم يستغفرون');
INSERT INTO "uthmani_simple" VALUES(8,34,'8:34','وما لهم الا يعذبهم الله وهم يصدون عن المسجد الحرام وما كانوا۟ اولياءه ان اولياؤه الا المتقون ولكن اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(8,35,'8:35','وما كان صلاتهم عند البيت الا مكاء وتصدية فذوقوا۟ العذاب بما كنتم تكفرون');
INSERT INTO "uthmani_simple" VALUES(8,36,'8:36','ان الذين كفروا۟ ينفقون امولهم ليصدوا۟ عن سبيل الله فسينفقونها ثم تكون عليهم حسرة ثم يغلبون والذين كفروا۟ الى جهنم يحشرون');
INSERT INTO "uthmani_simple" VALUES(8,37,'8:37','ليميز الله الخبيث من الطيب ويجعل الخبيث بعضه على بعض فيركمه جميعا فيجعله فى جهنم او۟لئك هم الخسرون');
INSERT INTO "uthmani_simple" VALUES(8,38,'8:38','قل للذين كفروا۟ ان ينتهوا۟ يغفر لهم ما قد سلف وان يعودوا۟ فقد مضت سنت الاولين');
INSERT INTO "uthmani_simple" VALUES(8,39,'8:39','وقتلوهم حتى لا تكون فتنة ويكون الدين كله لله فان انتهوا۟ فان الله بما يعملون بصير');
INSERT INTO "uthmani_simple" VALUES(8,40,'8:40','وان تولوا۟ فاعلموا۟ ان الله مولىكم نعم المولى ونعم النصير');
INSERT INTO "uthmani_simple" VALUES(8,41,'8:41','واعلموا۟ انما غنمتم من شىء فان لله خمسه وللرسول ولذى القربى واليتمى والمسكين وابن السبيل ان كنتم ءامنتم بالله وما انزلنا على عبدنا يوم الفرقان يوم التقى الجمعان والله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(8,42,'8:42','اذ انتم بالعدوة الدنيا وهم بالعدوة القصوى والركب اسفل منكم ولو تواعدتم لاختلفتم فى الميعد ولكن ليقضى الله امرا كان مفعولا ليهلك من هلك عنۢ بينة ويحيى من حى عنۢ بينة وان الله لسميع عليم');
INSERT INTO "uthmani_simple" VALUES(8,43,'8:43','اذ يريكهم الله فى منامك قليلا ولو ارىكهم كثيرا لفشلتم ولتنزعتم فى الامر ولكن الله سلم انه عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(8,44,'8:44','واذ يريكموهم اذ التقيتم فى اعينكم قليلا ويقللكم فى اعينهم ليقضى الله امرا كان مفعولا والى الله ترجع الامور');
INSERT INTO "uthmani_simple" VALUES(8,45,'8:45','يايها الذين ءامنوا۟ اذا لقيتم فئة فاثبتوا۟ واذكروا۟ الله كثيرا لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(8,46,'8:46','واطيعوا۟ الله ورسوله ولا تنزعوا۟ فتفشلوا۟ وتذهب ريحكم واصبروا۟ ان الله مع الصبرين');
INSERT INTO "uthmani_simple" VALUES(8,47,'8:47','ولا تكونوا۟ كالذين خرجوا۟ من ديرهم بطرا ورئاء الناس ويصدون عن سبيل الله والله بما يعملون محيط');
INSERT INTO "uthmani_simple" VALUES(8,48,'8:48','واذ زين لهم الشيطن اعملهم وقال لا غالب لكم اليوم من الناس وانى جار لكم فلما تراءت الفئتان نكص على عقبيه وقال انى برىء منكم انى ارى ما لا ترون انى اخاف الله والله شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(8,49,'8:49','اذ يقول المنفقون والذين فى قلوبهم مرض غر هؤلاء دينهم ومن يتوكل على الله فان الله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(8,50,'8:50','ولو ترى اذ يتوفى الذين كفروا۟ الملئكة يضربون وجوههم وادبرهم وذوقوا۟ عذاب الحريق');
INSERT INTO "uthmani_simple" VALUES(8,51,'8:51','ذلك بما قدمت ايديكم وان الله ليس بظلم للعبيد');
INSERT INTO "uthmani_simple" VALUES(8,52,'8:52','كداب ءال فرعون والذين من قبلهم كفروا۟ بـايت الله فاخذهم الله بذنوبهم ان الله قوى شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(8,53,'8:53','ذلك بان الله لم يك مغيرا نعمة انعمها على قوم حتى يغيروا۟ ما بانفسهم وان الله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(8,54,'8:54','كداب ءال فرعون والذين من قبلهم كذبوا۟ بـايت ربهم فاهلكنهم بذنوبهم واغرقنا ءال فرعون وكل كانوا۟ ظلمين');
INSERT INTO "uthmani_simple" VALUES(8,55,'8:55','ان شر الدواب عند الله الذين كفروا۟ فهم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(8,56,'8:56','الذين عهدت منهم ثم ينقضون عهدهم فى كل مرة وهم لا يتقون');
INSERT INTO "uthmani_simple" VALUES(8,57,'8:57','فاما تثقفنهم فى الحرب فشرد بهم من خلفهم لعلهم يذكرون');
INSERT INTO "uthmani_simple" VALUES(8,58,'8:58','واما تخافن من قوم خيانة فانۢبذ اليهم على سواء ان الله لا يحب الخائنين');
INSERT INTO "uthmani_simple" VALUES(8,59,'8:59','ولا يحسبن الذين كفروا۟ سبقوا۟ انهم لا يعجزون');
INSERT INTO "uthmani_simple" VALUES(8,60,'8:60','واعدوا۟ لهم ما استطعتم من قوة ومن رباط الخيل ترهبون به عدو الله وعدوكم وءاخرين من دونهم لا تعلمونهم الله يعلمهم وما تنفقوا۟ من شىء فى سبيل الله يوف اليكم وانتم لا تظلمون');
INSERT INTO "uthmani_simple" VALUES(8,61,'8:61','وان جنحوا۟ للسلم فاجنح لها وتوكل على الله انه هو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(8,62,'8:62','وان يريدوا۟ ان يخدعوك فان حسبك الله هو الذى ايدك بنصره وبالمؤمنين');
INSERT INTO "uthmani_simple" VALUES(8,63,'8:63','والف بين قلوبهم لو انفقت ما فى الارض جميعا ما الفت بين قلوبهم ولكن الله الف بينهم انه عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(8,64,'8:64','يايها النبى حسبك الله ومن اتبعك من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(8,65,'8:65','يايها النبى حرض المؤمنين على القتال ان يكن منكم عشرون صبرون يغلبوا۟ ما۟ئتين وان يكن منكم ما۟ئة يغلبوا۟ الفا من الذين كفروا۟ بانهم قوم لا يفقهون');
INSERT INTO "uthmani_simple" VALUES(8,66,'8:66','الـن خفف الله عنكم وعلم ان فيكم ضعفا فان يكن منكم ما۟ئة صابرة يغلبوا۟ ما۟ئتين وان يكن منكم الف يغلبوا۟ الفين باذن الله والله مع الصبرين');
INSERT INTO "uthmani_simple" VALUES(8,67,'8:67','ما كان لنبى ان يكون له اسرى حتى يثخن فى الارض تريدون عرض الدنيا والله يريد الءاخرة والله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(8,68,'8:68','لولا كتب من الله سبق لمسكم فيما اخذتم عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(8,69,'8:69','فكلوا۟ مما غنمتم حللا طيبا واتقوا۟ الله ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(8,70,'8:70','يايها النبى قل لمن فى ايديكم من الاسرى ان يعلم الله فى قلوبكم خيرا يؤتكم خيرا مما اخذ منكم ويغفر لكم والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(8,71,'8:71','وان يريدوا۟ خيانتك فقد خانوا۟ الله من قبل فامكن منهم والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(8,72,'8:72','ان الذين ءامنوا۟ وهاجروا۟ وجهدوا۟ بامولهم وانفسهم فى سبيل الله والذين ءاووا۟ ونصروا۟ او۟لئك بعضهم اولياء بعض والذين ءامنوا۟ ولم يهاجروا۟ ما لكم من وليتهم من شىء حتى يهاجروا۟ وان استنصروكم فى الدين فعليكم النصر الا على قومۭ بينكم وبينهم ميثق والله بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(8,73,'8:73','والذين كفروا۟ بعضهم اولياء بعض الا تفعلوه تكن فتنة فى الارض وفساد كبير');
INSERT INTO "uthmani_simple" VALUES(8,74,'8:74','والذين ءامنوا۟ وهاجروا۟ وجهدوا۟ فى سبيل الله والذين ءاووا۟ ونصروا۟ او۟لئك هم المؤمنون حقا لهم مغفرة ورزق كريم');
INSERT INTO "uthmani_simple" VALUES(8,75,'8:75','والذين ءامنوا۟ منۢ بعد وهاجروا۟ وجهدوا۟ معكم فاو۟لئك منكم واو۟لوا۟ الارحام بعضهم اولى ببعض فى كتب الله ان الله بكل شىء عليمۢ');
INSERT INTO "uthmani_simple" VALUES(9,1,'9:1','براءة من الله ورسوله الى الذين عهدتم من المشركين');
INSERT INTO "uthmani_simple" VALUES(9,2,'9:2','فسيحوا۟ فى الارض اربعة اشهر واعلموا۟ انكم غير معجزى الله وان الله مخزى الكفرين');
INSERT INTO "uthmani_simple" VALUES(9,3,'9:3','واذن من الله ورسوله الى الناس يوم الحج الاكبر ان الله برىء من المشركين ورسوله فان تبتم فهو خير لكم وان توليتم فاعلموا۟ انكم غير معجزى الله وبشر الذين كفروا۟ بعذاب اليم');
INSERT INTO "uthmani_simple" VALUES(9,4,'9:4','الا الذين عهدتم من المشركين ثم لم ينقصوكم شيـا ولم يظهروا۟ عليكم احدا فاتموا۟ اليهم عهدهم الى مدتهم ان الله يحب المتقين');
INSERT INTO "uthmani_simple" VALUES(9,5,'9:5','فاذا انسلخ الاشهر الحرم فاقتلوا۟ المشركين حيث وجدتموهم وخذوهم واحصروهم واقعدوا۟ لهم كل مرصد فان تابوا۟ واقاموا۟ الصلوة وءاتوا۟ الزكوة فخلوا۟ سبيلهم ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(9,6,'9:6','وان احد من المشركين استجارك فاجره حتى يسمع كلم الله ثم ابلغه مامنه ذلك بانهم قوم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(9,7,'9:7','كيف يكون للمشركين عهد عند الله وعند رسوله الا الذين عهدتم عند المسجد الحرام فما استقموا۟ لكم فاستقيموا۟ لهم ان الله يحب المتقين');
INSERT INTO "uthmani_simple" VALUES(9,8,'9:8','كيف وان يظهروا۟ عليكم لا يرقبوا۟ فيكم الا ولا ذمة يرضونكم بافوههم وتابى قلوبهم واكثرهم فسقون');
INSERT INTO "uthmani_simple" VALUES(9,9,'9:9','اشتروا۟ بـايت الله ثمنا قليلا فصدوا۟ عن سبيله انهم ساء ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(9,10,'9:10','لا يرقبون فى مؤمن الا ولا ذمة واو۟لئك هم المعتدون');
INSERT INTO "uthmani_simple" VALUES(9,11,'9:11','فان تابوا۟ واقاموا۟ الصلوة وءاتوا۟ الزكوة فاخونكم فى الدين ونفصل الءايت لقوم يعلمون');
INSERT INTO "uthmani_simple" VALUES(9,12,'9:12','وان نكثوا۟ ايمنهم منۢ بعد عهدهم وطعنوا۟ فى دينكم فقتلوا۟ ائمة الكفر انهم لا ايمن لهم لعلهم ينتهون');
INSERT INTO "uthmani_simple" VALUES(9,13,'9:13','الا تقتلون قوما نكثوا۟ ايمنهم وهموا۟ باخراج الرسول وهم بدءوكم اول مرة اتخشونهم فالله احق ان تخشوه ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(9,14,'9:14','قتلوهم يعذبهم الله بايديكم ويخزهم وينصركم عليهم ويشف صدور قوم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(9,15,'9:15','ويذهب غيظ قلوبهم ويتوب الله على من يشاء والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(9,16,'9:16','ام حسبتم ان تتركوا۟ ولما يعلم الله الذين جهدوا۟ منكم ولم يتخذوا۟ من دون الله ولا رسوله ولا المؤمنين وليجة والله خبيرۢ بما تعملون');
INSERT INTO "uthmani_simple" VALUES(9,17,'9:17','ما كان للمشركين ان يعمروا۟ مسجد الله شهدين على انفسهم بالكفر او۟لئك حبطت اعملهم وفى النار هم خلدون');
INSERT INTO "uthmani_simple" VALUES(9,18,'9:18','انما يعمر مسجد الله من ءامن بالله واليوم الءاخر واقام الصلوة وءاتى الزكوة ولم يخش الا الله فعسى او۟لئك ان يكونوا۟ من المهتدين');
INSERT INTO "uthmani_simple" VALUES(9,19,'9:19','اجعلتم سقاية الحاج وعمارة المسجد الحرام كمن ءامن بالله واليوم الءاخر وجهد فى سبيل الله لا يستون عند الله والله لا يهدى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(9,20,'9:20','الذين ءامنوا۟ وهاجروا۟ وجهدوا۟ فى سبيل الله بامولهم وانفسهم اعظم درجة عند الله واو۟لئك هم الفائزون');
INSERT INTO "uthmani_simple" VALUES(9,21,'9:21','يبشرهم ربهم برحمة منه ورضون وجنت لهم فيها نعيم مقيم');
INSERT INTO "uthmani_simple" VALUES(9,22,'9:22','خلدين فيها ابدا ان الله عنده اجر عظيم');
INSERT INTO "uthmani_simple" VALUES(9,23,'9:23','يايها الذين ءامنوا۟ لا تتخذوا۟ ءاباءكم واخونكم اولياء ان استحبوا۟ الكفر على الايمن ومن يتولهم منكم فاو۟لئك هم الظلمون');
INSERT INTO "uthmani_simple" VALUES(9,24,'9:24','قل ان كان ءاباؤكم وابناؤكم واخونكم وازوجكم وعشيرتكم وامول اقترفتموها وتجرة تخشون كسادها ومسكن ترضونها احب اليكم من الله ورسوله وجهاد فى سبيله فتربصوا۟ حتى ياتى الله بامره والله لا يهدى القوم الفسقين');
INSERT INTO "uthmani_simple" VALUES(9,25,'9:25','لقد نصركم الله فى مواطن كثيرة ويوم حنين اذ اعجبتكم كثرتكم فلم تغن عنكم شيـا وضاقت عليكم الارض بما رحبت ثم وليتم مدبرين');
INSERT INTO "uthmani_simple" VALUES(9,26,'9:26','ثم انزل الله سكينته على رسوله وعلى المؤمنين وانزل جنودا لم تروها وعذب الذين كفروا۟ وذلك جزاء الكفرين');
INSERT INTO "uthmani_simple" VALUES(9,27,'9:27','ثم يتوب الله منۢ بعد ذلك على من يشاء والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(9,28,'9:28','يايها الذين ءامنوا۟ انما المشركون نجس فلا يقربوا۟ المسجد الحرام بعد عامهم هذا وان خفتم عيلة فسوف يغنيكم الله من فضله ان شاء ان الله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(9,29,'9:29','قتلوا۟ الذين لا يؤمنون بالله ولا باليوم الءاخر ولا يحرمون ما حرم الله ورسوله ولا يدينون دين الحق من الذين اوتوا۟ الكتب حتى يعطوا۟ الجزية عن يد وهم صغرون');
INSERT INTO "uthmani_simple" VALUES(9,30,'9:30','وقالت اليهود عزير ابن الله وقالت النصرى المسيح ابن الله ذلك قولهم بافوههم يضهـون قول الذين كفروا۟ من قبل قتلهم الله انى يؤفكون');
INSERT INTO "uthmani_simple" VALUES(9,31,'9:31','اتخذوا۟ احبارهم ورهبنهم اربابا من دون الله والمسيح ابن مريم وما امروا۟ الا ليعبدوا۟ الها وحدا لا اله الا هو سبحنه عما يشركون');
INSERT INTO "uthmani_simple" VALUES(9,32,'9:32','يريدون ان يطفـوا۟ نور الله بافوههم ويابى الله الا ان يتم نوره ولو كره الكفرون');
INSERT INTO "uthmani_simple" VALUES(9,33,'9:33','هو الذى ارسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون');
INSERT INTO "uthmani_simple" VALUES(9,34,'9:34','يايها الذين ءامنوا۟ ان كثيرا من الاحبار والرهبان لياكلون امول الناس بالبطل ويصدون عن سبيل الله والذين يكنزون الذهب والفضة ولا ينفقونها فى سبيل الله فبشرهم بعذاب اليم');
INSERT INTO "uthmani_simple" VALUES(9,35,'9:35','يوم يحمى عليها فى نار جهنم فتكوى بها جباههم وجنوبهم وظهورهم هذا ما كنزتم لانفسكم فذوقوا۟ ما كنتم تكنزون');
INSERT INTO "uthmani_simple" VALUES(9,36,'9:36','ان عدة الشهور عند الله اثنا عشر شهرا فى كتب الله يوم خلق السموت والارض منها اربعة حرم ذلك الدين القيم فلا تظلموا۟ فيهن انفسكم وقتلوا۟ المشركين كافة كما يقتلونكم كافة واعلموا۟ ان الله مع المتقين');
INSERT INTO "uthmani_simple" VALUES(9,37,'9:37','انما النسىء زيادة فى الكفر يضل به الذين كفروا۟ يحلونه عاما ويحرمونه عاما ليواطـوا۟ عدة ما حرم الله فيحلوا۟ ما حرم الله زين لهم سوء اعملهم والله لا يهدى القوم الكفرين');
INSERT INTO "uthmani_simple" VALUES(9,38,'9:38','يايها الذين ءامنوا۟ ما لكم اذا قيل لكم انفروا۟ فى سبيل الله اثاقلتم الى الارض ارضيتم بالحيوة الدنيا من الءاخرة فما متع الحيوة الدنيا فى الءاخرة الا قليل');
INSERT INTO "uthmani_simple" VALUES(9,39,'9:39','الا تنفروا۟ يعذبكم عذابا اليما ويستبدل قوما غيركم ولا تضروه شيـا والله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(9,40,'9:40','الا تنصروه فقد نصره الله اذ اخرجه الذين كفروا۟ ثانى اثنين اذ هما فى الغار اذ يقول لصحبه لا تحزن ان الله معنا فانزل الله سكينته عليه وايده بجنود لم تروها وجعل كلمة الذين كفروا۟ السفلى وكلمة الله هى العليا والله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(9,41,'9:41','انفروا۟ خفافا وثقالا وجهدوا۟ بامولكم وانفسكم فى سبيل الله ذلكم خير لكم ان كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(9,42,'9:42','لو كان عرضا قريبا وسفرا قاصدا لاتبعوك ولكنۢ بعدت عليهم الشقة وسيحلفون بالله لو استطعنا لخرجنا معكم يهلكون انفسهم والله يعلم انهم لكذبون');
INSERT INTO "uthmani_simple" VALUES(9,43,'9:43','عفا الله عنك لم اذنت لهم حتى يتبين لك الذين صدقوا۟ وتعلم الكذبين');
INSERT INTO "uthmani_simple" VALUES(9,44,'9:44','لا يستـذنك الذين يؤمنون بالله واليوم الءاخر ان يجهدوا۟ بامولهم وانفسهم والله عليمۢ بالمتقين');
INSERT INTO "uthmani_simple" VALUES(9,45,'9:45','انما يستـذنك الذين لا يؤمنون بالله واليوم الءاخر وارتابت قلوبهم فهم فى ريبهم يترددون');
INSERT INTO "uthmani_simple" VALUES(9,46,'9:46','ولو ارادوا۟ الخروج لاعدوا۟ له عدة ولكن كره الله انۢبعاثهم فثبطهم وقيل اقعدوا۟ مع القعدين');
INSERT INTO "uthmani_simple" VALUES(9,47,'9:47','لو خرجوا۟ فيكم ما زادوكم الا خبالا ولاوضعوا۟ خللكم يبغونكم الفتنة وفيكم سمعون لهم والله عليمۢ بالظلمين');
INSERT INTO "uthmani_simple" VALUES(9,48,'9:48','لقد ابتغوا۟ الفتنة من قبل وقلبوا۟ لك الامور حتى جاء الحق وظهر امر الله وهم كرهون');
INSERT INTO "uthmani_simple" VALUES(9,49,'9:49','ومنهم من يقول ائذن لى ولا تفتنى الا فى الفتنة سقطوا۟ وان جهنم لمحيطةۢ بالكفرين');
INSERT INTO "uthmani_simple" VALUES(9,50,'9:50','ان تصبك حسنة تسؤهم وان تصبك مصيبة يقولوا۟ قد اخذنا امرنا من قبل ويتولوا۟ وهم فرحون');
INSERT INTO "uthmani_simple" VALUES(9,51,'9:51','قل لن يصيبنا الا ما كتب الله لنا هو مولىنا وعلى الله فليتوكل المؤمنون');
INSERT INTO "uthmani_simple" VALUES(9,52,'9:52','قل هل تربصون بنا الا احدى الحسنيين ونحن نتربص بكم ان يصيبكم الله بعذاب من عنده او بايدينا فتربصوا۟ انا معكم متربصون');
INSERT INTO "uthmani_simple" VALUES(9,53,'9:53','قل انفقوا۟ طوعا او كرها لن يتقبل منكم انكم كنتم قوما فسقين');
INSERT INTO "uthmani_simple" VALUES(9,54,'9:54','وما منعهم ان تقبل منهم نفقتهم الا انهم كفروا۟ بالله وبرسوله ولا ياتون الصلوة الا وهم كسالى ولا ينفقون الا وهم كرهون');
INSERT INTO "uthmani_simple" VALUES(9,55,'9:55','فلا تعجبك امولهم ولا اولدهم انما يريد الله ليعذبهم بها فى الحيوة الدنيا وتزهق انفسهم وهم كفرون');
INSERT INTO "uthmani_simple" VALUES(9,56,'9:56','ويحلفون بالله انهم لمنكم وما هم منكم ولكنهم قوم يفرقون');
INSERT INTO "uthmani_simple" VALUES(9,57,'9:57','لو يجدون ملجـا او مغرت او مدخلا لولوا۟ اليه وهم يجمحون');
INSERT INTO "uthmani_simple" VALUES(9,58,'9:58','ومنهم من يلمزك فى الصدقت فان اعطوا۟ منها رضوا۟ وان لم يعطوا۟ منها اذا هم يسخطون');
INSERT INTO "uthmani_simple" VALUES(9,59,'9:59','ولو انهم رضوا۟ ما ءاتىهم الله ورسوله وقالوا۟ حسبنا الله سيؤتينا الله من فضله ورسوله انا الى الله رغبون');
INSERT INTO "uthmani_simple" VALUES(9,60,'9:60','انما الصدقت للفقراء والمسكين والعملين عليها والمؤلفة قلوبهم وفى الرقاب والغرمين وفى سبيل الله وابن السبيل فريضة من الله والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(9,61,'9:61','ومنهم الذين يؤذون النبى ويقولون هو اذن قل اذن خير لكم يؤمن بالله ويؤمن للمؤمنين ورحمة للذين ءامنوا۟ منكم والذين يؤذون رسول الله لهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(9,62,'9:62','يحلفون بالله لكم ليرضوكم والله ورسوله احق ان يرضوه ان كانوا۟ مؤمنين');
INSERT INTO "uthmani_simple" VALUES(9,63,'9:63','الم يعلموا۟ انه من يحادد الله ورسوله فان له نار جهنم خلدا فيها ذلك الخزى العظيم');
INSERT INTO "uthmani_simple" VALUES(9,64,'9:64','يحذر المنفقون ان تنزل عليهم سورة تنبئهم بما فى قلوبهم قل استهزءوا۟ ان الله مخرج ما تحذرون');
INSERT INTO "uthmani_simple" VALUES(9,65,'9:65','ولئن سالتهم ليقولن انما كنا نخوض ونلعب قل ابالله وءايته ورسوله كنتم تستهزءون');
INSERT INTO "uthmani_simple" VALUES(9,66,'9:66','لا تعتذروا۟ قد كفرتم بعد ايمنكم ان نعف عن طائفة منكم نعذب طائفةۢ بانهم كانوا۟ مجرمين');
INSERT INTO "uthmani_simple" VALUES(9,67,'9:67','المنفقون والمنفقت بعضهم منۢ بعض يامرون بالمنكر وينهون عن المعروف ويقبضون ايديهم نسوا۟ الله فنسيهم ان المنفقين هم الفسقون');
INSERT INTO "uthmani_simple" VALUES(9,68,'9:68','وعد الله المنفقين والمنفقت والكفار نار جهنم خلدين فيها هى حسبهم ولعنهم الله ولهم عذاب مقيم');
INSERT INTO "uthmani_simple" VALUES(9,69,'9:69','كالذين من قبلكم كانوا۟ اشد منكم قوة واكثر امولا واولدا فاستمتعوا۟ بخلقهم فاستمتعتم بخلقكم كما استمتع الذين من قبلكم بخلقهم وخضتم كالذى خاضوا۟ او۟لئك حبطت اعملهم فى الدنيا والءاخرة واو۟لئك هم الخسرون');
INSERT INTO "uthmani_simple" VALUES(9,70,'9:70','الم ياتهم نبا الذين من قبلهم قوم نوح وعاد وثمود وقوم ابرهيم واصحب مدين والمؤتفكت اتتهم رسلهم بالبينت فما كان الله ليظلمهم ولكن كانوا۟ انفسهم يظلمون');
INSERT INTO "uthmani_simple" VALUES(9,71,'9:71','والمؤمنون والمؤمنت بعضهم اولياء بعض يامرون بالمعروف وينهون عن المنكر ويقيمون الصلوة ويؤتون الزكوة ويطيعون الله ورسوله او۟لئك سيرحمهم الله ان الله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(9,72,'9:72','وعد الله المؤمنين والمؤمنت جنت تجرى من تحتها الانهر خلدين فيها ومسكن طيبة فى جنت عدن ورضون من الله اكبر ذلك هو الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(9,73,'9:73','يايها النبى جهد الكفار والمنفقين واغلظ عليهم وماوىهم جهنم وبئس المصير');
INSERT INTO "uthmani_simple" VALUES(9,74,'9:74','يحلفون بالله ما قالوا۟ ولقد قالوا۟ كلمة الكفر وكفروا۟ بعد اسلمهم وهموا۟ بما لم ينالوا۟ وما نقموا۟ الا ان اغنىهم الله ورسوله من فضله فان يتوبوا۟ يك خيرا لهم وان يتولوا۟ يعذبهم الله عذابا اليما فى الدنيا والءاخرة وما لهم فى الارض من ولى ولا نصير');
INSERT INTO "uthmani_simple" VALUES(9,75,'9:75','ومنهم من عهد الله لئن ءاتىنا من فضله لنصدقن ولنكونن من الصلحين');
INSERT INTO "uthmani_simple" VALUES(9,76,'9:76','فلما ءاتىهم من فضله بخلوا۟ به وتولوا۟ وهم معرضون');
INSERT INTO "uthmani_simple" VALUES(9,77,'9:77','فاعقبهم نفاقا فى قلوبهم الى يوم يلقونه بما اخلفوا۟ الله ما وعدوه وبما كانوا۟ يكذبون');
INSERT INTO "uthmani_simple" VALUES(9,78,'9:78','الم يعلموا۟ ان الله يعلم سرهم ونجوىهم وان الله علم الغيوب');
INSERT INTO "uthmani_simple" VALUES(9,79,'9:79','الذين يلمزون المطوعين من المؤمنين فى الصدقت والذين لا يجدون الا جهدهم فيسخرون منهم سخر الله منهم ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(9,80,'9:80','استغفر لهم او لا تستغفر لهم ان تستغفر لهم سبعين مرة فلن يغفر الله لهم ذلك بانهم كفروا۟ بالله ورسوله والله لا يهدى القوم الفسقين');
INSERT INTO "uthmani_simple" VALUES(9,81,'9:81','فرح المخلفون بمقعدهم خلف رسول الله وكرهوا۟ ان يجهدوا۟ بامولهم وانفسهم فى سبيل الله وقالوا۟ لا تنفروا۟ فى الحر قل نار جهنم اشد حرا لو كانوا۟ يفقهون');
INSERT INTO "uthmani_simple" VALUES(9,82,'9:82','فليضحكوا۟ قليلا وليبكوا۟ كثيرا جزاءۢ بما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(9,83,'9:83','فان رجعك الله الى طائفة منهم فاستـذنوك للخروج فقل لن تخرجوا۟ معى ابدا ولن تقتلوا۟ معى عدوا انكم رضيتم بالقعود اول مرة فاقعدوا۟ مع الخلفين');
INSERT INTO "uthmani_simple" VALUES(9,84,'9:84','ولا تصل على احد منهم مات ابدا ولا تقم على قبره انهم كفروا۟ بالله ورسوله وماتوا۟ وهم فسقون');
INSERT INTO "uthmani_simple" VALUES(9,85,'9:85','ولا تعجبك امولهم واولدهم انما يريد الله ان يعذبهم بها فى الدنيا وتزهق انفسهم وهم كفرون');
INSERT INTO "uthmani_simple" VALUES(9,86,'9:86','واذا انزلت سورة ان ءامنوا۟ بالله وجهدوا۟ مع رسوله استـذنك او۟لوا۟ الطول منهم وقالوا۟ ذرنا نكن مع القعدين');
INSERT INTO "uthmani_simple" VALUES(9,87,'9:87','رضوا۟ بان يكونوا۟ مع الخوالف وطبع على قلوبهم فهم لا يفقهون');
INSERT INTO "uthmani_simple" VALUES(9,88,'9:88','لكن الرسول والذين ءامنوا۟ معه جهدوا۟ بامولهم وانفسهم واو۟لئك لهم الخيرت واو۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(9,89,'9:89','اعد الله لهم جنت تجرى من تحتها الانهر خلدين فيها ذلك الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(9,90,'9:90','وجاء المعذرون من الاعراب ليؤذن لهم وقعد الذين كذبوا۟ الله ورسوله سيصيب الذين كفروا۟ منهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(9,91,'9:91','ليس على الضعفاء ولا على المرضى ولا على الذين لا يجدون ما ينفقون حرج اذا نصحوا۟ لله ورسوله ما على المحسنين من سبيل والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(9,92,'9:92','ولا على الذين اذا ما اتوك لتحملهم قلت لا اجد ما احملكم عليه تولوا۟ واعينهم تفيض من الدمع حزنا الا يجدوا۟ ما ينفقون');
INSERT INTO "uthmani_simple" VALUES(9,93,'9:93','انما السبيل على الذين يستـذنونك وهم اغنياء رضوا۟ بان يكونوا۟ مع الخوالف وطبع الله على قلوبهم فهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(9,94,'9:94','يعتذرون اليكم اذا رجعتم اليهم قل لا تعتذروا۟ لن نؤمن لكم قد نبانا الله من اخباركم وسيرى الله عملكم ورسوله ثم تردون الى علم الغيب والشهدة فينبئكم بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(9,95,'9:95','سيحلفون بالله لكم اذا انقلبتم اليهم لتعرضوا۟ عنهم فاعرضوا۟ عنهم انهم رجس وماوىهم جهنم جزاءۢ بما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(9,96,'9:96','يحلفون لكم لترضوا۟ عنهم فان ترضوا۟ عنهم فان الله لا يرضى عن القوم الفسقين');
INSERT INTO "uthmani_simple" VALUES(9,97,'9:97','الاعراب اشد كفرا ونفاقا واجدر الا يعلموا۟ حدود ما انزل الله على رسوله والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(9,98,'9:98','ومن الاعراب من يتخذ ما ينفق مغرما ويتربص بكم الدوائر عليهم دائرة السوء والله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(9,99,'9:99','ومن الاعراب من يؤمن بالله واليوم الءاخر ويتخذ ما ينفق قربت عند الله وصلوت الرسول الا انها قربة لهم سيدخلهم الله فى رحمته ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(9,100,'9:100','والسبقون الاولون من المهجرين والانصار والذين اتبعوهم باحسن رضى الله عنهم ورضوا۟ عنه واعد لهم جنت تجرى تحتها الانهر خلدين فيها ابدا ذلك الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(9,101,'9:101','وممن حولكم من الاعراب منفقون ومن اهل المدينة مردوا۟ على النفاق لا تعلمهم نحن نعلمهم سنعذبهم مرتين ثم يردون الى عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(9,102,'9:102','وءاخرون اعترفوا۟ بذنوبهم خلطوا۟ عملا صلحا وءاخر سيئا عسى الله ان يتوب عليهم ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(9,103,'9:103','خذ من امولهم صدقة تطهرهم وتزكيهم بها وصل عليهم ان صلوتك سكن لهم والله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(9,104,'9:104','الم يعلموا۟ ان الله هو يقبل التوبة عن عباده وياخذ الصدقت وان الله هو التواب الرحيم');
INSERT INTO "uthmani_simple" VALUES(9,105,'9:105','وقل اعملوا۟ فسيرى الله عملكم ورسوله والمؤمنون وستردون الى علم الغيب والشهدة فينبئكم بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(9,106,'9:106','وءاخرون مرجون لامر الله اما يعذبهم واما يتوب عليهم والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(9,107,'9:107','والذين اتخذوا۟ مسجدا ضرارا وكفرا وتفريقۢا بين المؤمنين وارصادا لمن حارب الله ورسوله من قبل وليحلفن ان اردنا الا الحسنى والله يشهد انهم لكذبون');
INSERT INTO "uthmani_simple" VALUES(9,108,'9:108','لا تقم فيه ابدا لمسجد اسس على التقوى من اول يوم احق ان تقوم فيه فيه رجال يحبون ان يتطهروا۟ والله يحب المطهرين');
INSERT INTO "uthmani_simple" VALUES(9,109,'9:109','افمن اسس بنينه على تقوى من الله ورضون خير ام من اسس بنينه على شفا جرف هار فانهار به فى نار جهنم والله لا يهدى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(9,110,'9:110','لا يزال بنينهم الذى بنوا۟ ريبة فى قلوبهم الا ان تقطع قلوبهم والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(9,111,'9:111','ان الله اشترى من المؤمنين انفسهم وامولهم بان لهم الجنة يقتلون فى سبيل الله فيقتلون ويقتلون وعدا عليه حقا فى التورىة والانجيل والقرءان ومن اوفى بعهده من الله فاستبشروا۟ ببيعكم الذى بايعتم به وذلك هو الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(9,112,'9:112','التئبون العبدون الحمدون السئحون الركعون السجدون الءامرون بالمعروف والناهون عن المنكر والحفظون لحدود الله وبشر المؤمنين');
INSERT INTO "uthmani_simple" VALUES(9,113,'9:113','ما كان للنبى والذين ءامنوا۟ ان يستغفروا۟ للمشركين ولو كانوا۟ او۟لى قربى منۢ بعد ما تبين لهم انهم اصحب الجحيم');
INSERT INTO "uthmani_simple" VALUES(9,114,'9:114','وما كان استغفار ابرهيم لابيه الا عن موعدة وعدها اياه فلما تبين له انه عدو لله تبرا منه ان ابرهيم لاوه حليم');
INSERT INTO "uthmani_simple" VALUES(9,115,'9:115','وما كان الله ليضل قومۢا بعد اذ هدىهم حتى يبين لهم ما يتقون ان الله بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(9,116,'9:116','ان الله له ملك السموت والارض يحى ويميت وما لكم من دون الله من ولى ولا نصير');
INSERT INTO "uthmani_simple" VALUES(9,117,'9:117','لقد تاب الله على النبى والمهجرين والانصار الذين اتبعوه فى ساعة العسرة منۢ بعد ما كاد يزيغ قلوب فريق منهم ثم تاب عليهم انه بهم رءوف رحيم');
INSERT INTO "uthmani_simple" VALUES(9,118,'9:118','وعلى الثلثة الذين خلفوا۟ حتى اذا ضاقت عليهم الارض بما رحبت وضاقت عليهم انفسهم وظنوا۟ ان لا ملجا من الله الا اليه ثم تاب عليهم ليتوبوا۟ ان الله هو التواب الرحيم');
INSERT INTO "uthmani_simple" VALUES(9,119,'9:119','يايها الذين ءامنوا۟ اتقوا۟ الله وكونوا۟ مع الصدقين');
INSERT INTO "uthmani_simple" VALUES(9,120,'9:120','ما كان لاهل المدينة ومن حولهم من الاعراب ان يتخلفوا۟ عن رسول الله ولا يرغبوا۟ بانفسهم عن نفسه ذلك بانهم لا يصيبهم ظما ولا نصب ولا مخمصة فى سبيل الله ولا يطـون موطئا يغيظ الكفار ولا ينالون من عدو نيلا الا كتب لهم به عمل صلح ان الله لا يضيع اجر المحسنين');
INSERT INTO "uthmani_simple" VALUES(9,121,'9:121','ولا ينفقون نفقة صغيرة ولا كبيرة ولا يقطعون واديا الا كتب لهم ليجزيهم الله احسن ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(9,122,'9:122','وما كان المؤمنون لينفروا۟ كافة فلولا نفر من كل فرقة منهم طائفة ليتفقهوا۟ فى الدين ولينذروا۟ قومهم اذا رجعوا۟ اليهم لعلهم يحذرون');
INSERT INTO "uthmani_simple" VALUES(9,123,'9:123','يايها الذين ءامنوا۟ قتلوا۟ الذين يلونكم من الكفار وليجدوا۟ فيكم غلظة واعلموا۟ ان الله مع المتقين');
INSERT INTO "uthmani_simple" VALUES(9,124,'9:124','واذا ما انزلت سورة فمنهم من يقول ايكم زادته هذه ايمنا فاما الذين ءامنوا۟ فزادتهم ايمنا وهم يستبشرون');
INSERT INTO "uthmani_simple" VALUES(9,125,'9:125','واما الذين فى قلوبهم مرض فزادتهم رجسا الى رجسهم وماتوا۟ وهم كفرون');
INSERT INTO "uthmani_simple" VALUES(9,126,'9:126','اولا يرون انهم يفتنون فى كل عام مرة او مرتين ثم لا يتوبون ولا هم يذكرون');
INSERT INTO "uthmani_simple" VALUES(9,127,'9:127','واذا ما انزلت سورة نظر بعضهم الى بعض هل يرىكم من احد ثم انصرفوا۟ صرف الله قلوبهم بانهم قوم لا يفقهون');
INSERT INTO "uthmani_simple" VALUES(9,128,'9:128','لقد جاءكم رسول من انفسكم عزيز عليه ما عنتم حريص عليكم بالمؤمنين رءوف رحيم');
INSERT INTO "uthmani_simple" VALUES(9,129,'9:129','فان تولوا۟ فقل حسبى الله لا اله الا هو عليه توكلت وهو رب العرش العظيم');
INSERT INTO "uthmani_simple" VALUES(10,1,'10:1','الر تلك ءايت الكتب الحكيم');
INSERT INTO "uthmani_simple" VALUES(10,2,'10:2','اكان للناس عجبا ان اوحينا الى رجل منهم ان انذر الناس وبشر الذين ءامنوا۟ ان لهم قدم صدق عند ربهم قال الكفرون ان هذا لسحر مبين');
INSERT INTO "uthmani_simple" VALUES(10,3,'10:3','ان ربكم الله الذى خلق السموت والارض فى ستة ايام ثم استوى على العرش يدبر الامر ما من شفيع الا منۢ بعد اذنه ذلكم الله ربكم فاعبدوه افلا تذكرون');
INSERT INTO "uthmani_simple" VALUES(10,4,'10:4','اليه مرجعكم جميعا وعد الله حقا انه يبدؤا۟ الخلق ثم يعيده ليجزى الذين ءامنوا۟ وعملوا۟ الصلحت بالقسط والذين كفروا۟ لهم شراب من حميم وعذاب اليمۢ بما كانوا۟ يكفرون');
INSERT INTO "uthmani_simple" VALUES(10,5,'10:5','هو الذى جعل الشمس ضياء والقمر نورا وقدره منازل لتعلموا۟ عدد السنين والحساب ما خلق الله ذلك الا بالحق يفصل الءايت لقوم يعلمون');
INSERT INTO "uthmani_simple" VALUES(10,6,'10:6','ان فى اختلف اليل والنهار وما خلق الله فى السموت والارض لءايت لقوم يتقون');
INSERT INTO "uthmani_simple" VALUES(10,7,'10:7','ان الذين لا يرجون لقاءنا ورضوا۟ بالحيوة الدنيا واطمانوا۟ بها والذين هم عن ءايتنا غفلون');
INSERT INTO "uthmani_simple" VALUES(10,8,'10:8','او۟لئك ماوىهم النار بما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(10,9,'10:9','ان الذين ءامنوا۟ وعملوا۟ الصلحت يهديهم ربهم بايمنهم تجرى من تحتهم الانهر فى جنت النعيم');
INSERT INTO "uthmani_simple" VALUES(10,10,'10:10','دعوىهم فيها سبحنك اللهم وتحيتهم فيها سلم وءاخر دعوىهم ان الحمد لله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(10,11,'10:11','ولو يعجل الله للناس الشر استعجالهم بالخير لقضى اليهم اجلهم فنذر الذين لا يرجون لقاءنا فى طغينهم يعمهون');
INSERT INTO "uthmani_simple" VALUES(10,12,'10:12','واذا مس الانسن الضر دعانا لجنۢبه او قاعدا او قائما فلما كشفنا عنه ضره مر كان لم يدعنا الى ضر مسه كذلك زين للمسرفين ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(10,13,'10:13','ولقد اهلكنا القرون من قبلكم لما ظلموا۟ وجاءتهم رسلهم بالبينت وما كانوا۟ ليؤمنوا۟ كذلك نجزى القوم المجرمين');
INSERT INTO "uthmani_simple" VALUES(10,14,'10:14','ثم جعلنكم خلئف فى الارض منۢ بعدهم لننظر كيف تعملون');
INSERT INTO "uthmani_simple" VALUES(10,15,'10:15','واذا تتلى عليهم ءاياتنا بينت قال الذين لا يرجون لقاءنا ائت بقرءان غير هذا او بدله قل ما يكون لى ان ابدله من تلقائ نفسى ان اتبع الا ما يوحى الى انى اخاف ان عصيت ربى عذاب يوم عظيم');
INSERT INTO "uthmani_simple" VALUES(10,16,'10:16','قل لو شاء الله ما تلوته عليكم ولا ادرىكم به فقد لبثت فيكم عمرا من قبله افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(10,17,'10:17','فمن اظلم ممن افترى على الله كذبا او كذب بـايته انه لا يفلح المجرمون');
INSERT INTO "uthmani_simple" VALUES(10,18,'10:18','ويعبدون من دون الله ما لا يضرهم ولا ينفعهم ويقولون هؤلاء شفعؤنا عند الله قل اتنبـون الله بما لا يعلم فى السموت ولا فى الارض سبحنه وتعلى عما يشركون');
INSERT INTO "uthmani_simple" VALUES(10,19,'10:19','وما كان الناس الا امة وحدة فاختلفوا۟ ولولا كلمة سبقت من ربك لقضى بينهم فيما فيه يختلفون');
INSERT INTO "uthmani_simple" VALUES(10,20,'10:20','ويقولون لولا انزل عليه ءاية من ربه فقل انما الغيب لله فانتظروا۟ انى معكم من المنتظرين');
INSERT INTO "uthmani_simple" VALUES(10,21,'10:21','واذا اذقنا الناس رحمة منۢ بعد ضراء مستهم اذا لهم مكر فى ءاياتنا قل الله اسرع مكرا ان رسلنا يكتبون ما تمكرون');
INSERT INTO "uthmani_simple" VALUES(10,22,'10:22','هو الذى يسيركم فى البر والبحر حتى اذا كنتم فى الفلك وجرين بهم بريح طيبة وفرحوا۟ بها جاءتها ريح عاصف وجاءهم الموج من كل مكان وظنوا۟ انهم احيط بهم دعوا۟ الله مخلصين له الدين لئن انجيتنا من هذه لنكونن من الشكرين');
INSERT INTO "uthmani_simple" VALUES(10,23,'10:23','فلما انجىهم اذا هم يبغون فى الارض بغير الحق يايها الناس انما بغيكم على انفسكم متع الحيوة الدنيا ثم الينا مرجعكم فننبئكم بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(10,24,'10:24','انما مثل الحيوة الدنيا كماء انزلنه من السماء فاختلط به نبات الارض مما ياكل الناس والانعم حتى اذا اخذت الارض زخرفها وازينت وظن اهلها انهم قدرون عليها اتىها امرنا ليلا او نهارا فجعلنها حصيدا كان لم تغن بالامس كذلك نفصل الءايت لقوم يتفكرون');
INSERT INTO "uthmani_simple" VALUES(10,25,'10:25','والله يدعوا۟ الى دار السلم ويهدى من يشاء الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(10,26,'10:26','للذين احسنوا۟ الحسنى وزيادة ولا يرهق وجوههم قتر ولا ذلة او۟لئك اصحب الجنة هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(10,27,'10:27','والذين كسبوا۟ السيـات جزاء سيئةۭ بمثلها وترهقهم ذلة ما لهم من الله من عاصم كانما اغشيت وجوههم قطعا من اليل مظلما او۟لئك اصحب النار هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(10,28,'10:28','ويوم نحشرهم جميعا ثم نقول للذين اشركوا۟ مكانكم انتم وشركاؤكم فزيلنا بينهم وقال شركاؤهم ما كنتم ايانا تعبدون');
INSERT INTO "uthmani_simple" VALUES(10,29,'10:29','فكفى بالله شهيدۢا بيننا وبينكم ان كنا عن عبادتكم لغفلين');
INSERT INTO "uthmani_simple" VALUES(10,30,'10:30','هنالك تبلوا۟ كل نفس ما اسلفت وردوا۟ الى الله مولىهم الحق وضل عنهم ما كانوا۟ يفترون');
INSERT INTO "uthmani_simple" VALUES(10,31,'10:31','قل من يرزقكم من السماء والارض امن يملك السمع والابصر ومن يخرج الحى من الميت ويخرج الميت من الحى ومن يدبر الامر فسيقولون الله فقل افلا تتقون');
INSERT INTO "uthmani_simple" VALUES(10,32,'10:32','فذلكم الله ربكم الحق فماذا بعد الحق الا الضلل فانى تصرفون');
INSERT INTO "uthmani_simple" VALUES(10,33,'10:33','كذلك حقت كلمت ربك على الذين فسقوا۟ انهم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(10,34,'10:34','قل هل من شركائكم من يبدؤا۟ الخلق ثم يعيده قل الله يبدؤا۟ الخلق ثم يعيده فانى تؤفكون');
INSERT INTO "uthmani_simple" VALUES(10,35,'10:35','قل هل من شركائكم من يهدى الى الحق قل الله يهدى للحق افمن يهدى الى الحق احق ان يتبع امن لا يهدى الا ان يهدى فما لكم كيف تحكمون');
INSERT INTO "uthmani_simple" VALUES(10,36,'10:36','وما يتبع اكثرهم الا ظنا ان الظن لا يغنى من الحق شيـا ان الله عليمۢ بما يفعلون');
INSERT INTO "uthmani_simple" VALUES(10,37,'10:37','وما كان هذا القرءان ان يفترى من دون الله ولكن تصديق الذى بين يديه وتفصيل الكتب لا ريب فيه من رب العلمين');
INSERT INTO "uthmani_simple" VALUES(10,38,'10:38','ام يقولون افترىه قل فاتوا۟ بسورة مثله وادعوا۟ من استطعتم من دون الله ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(10,39,'10:39','بل كذبوا۟ بما لم يحيطوا۟ بعلمه ولما ياتهم تاويله كذلك كذب الذين من قبلهم فانظر كيف كان عقبة الظلمين');
INSERT INTO "uthmani_simple" VALUES(10,40,'10:40','ومنهم من يؤمن به ومنهم من لا يؤمن به وربك اعلم بالمفسدين');
INSERT INTO "uthmani_simple" VALUES(10,41,'10:41','وان كذبوك فقل لى عملى ولكم عملكم انتم بريـون مما اعمل وانا۠ برىء مما تعملون');
INSERT INTO "uthmani_simple" VALUES(10,42,'10:42','ومنهم من يستمعون اليك افانت تسمع الصم ولو كانوا۟ لا يعقلون');
INSERT INTO "uthmani_simple" VALUES(10,43,'10:43','ومنهم من ينظر اليك افانت تهدى العمى ولو كانوا۟ لا يبصرون');
INSERT INTO "uthmani_simple" VALUES(10,44,'10:44','ان الله لا يظلم الناس شيـا ولكن الناس انفسهم يظلمون');
INSERT INTO "uthmani_simple" VALUES(10,45,'10:45','ويوم يحشرهم كان لم يلبثوا۟ الا ساعة من النهار يتعارفون بينهم قد خسر الذين كذبوا۟ بلقاء الله وما كانوا۟ مهتدين');
INSERT INTO "uthmani_simple" VALUES(10,46,'10:46','واما نرينك بعض الذى نعدهم او نتوفينك فالينا مرجعهم ثم الله شهيد على ما يفعلون');
INSERT INTO "uthmani_simple" VALUES(10,47,'10:47','ولكل امة رسول فاذا جاء رسولهم قضى بينهم بالقسط وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(10,48,'10:48','ويقولون متى هذا الوعد ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(10,49,'10:49','قل لا املك لنفسى ضرا ولا نفعا الا ما شاء الله لكل امة اجل اذا جاء اجلهم فلا يستـخرون ساعة ولا يستقدمون');
INSERT INTO "uthmani_simple" VALUES(10,50,'10:50','قل ارءيتم ان اتىكم عذابه بيتا او نهارا ماذا يستعجل منه المجرمون');
INSERT INTO "uthmani_simple" VALUES(10,51,'10:51','اثم اذا ما وقع ءامنتم به ءالـن وقد كنتم به تستعجلون');
INSERT INTO "uthmani_simple" VALUES(10,52,'10:52','ثم قيل للذين ظلموا۟ ذوقوا۟ عذاب الخلد هل تجزون الا بما كنتم تكسبون');
INSERT INTO "uthmani_simple" VALUES(10,53,'10:53','ويستنۢبـونك احق هو قل اى وربى انه لحق وما انتم بمعجزين');
INSERT INTO "uthmani_simple" VALUES(10,54,'10:54','ولو ان لكل نفس ظلمت ما فى الارض لافتدت به واسروا۟ الندامة لما راوا۟ العذاب وقضى بينهم بالقسط وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(10,55,'10:55','الا ان لله ما فى السموت والارض الا ان وعد الله حق ولكن اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(10,56,'10:56','هو يحى ويميت واليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(10,57,'10:57','يايها الناس قد جاءتكم موعظة من ربكم وشفاء لما فى الصدور وهدى ورحمة للمؤمنين');
INSERT INTO "uthmani_simple" VALUES(10,58,'10:58','قل بفضل الله وبرحمته فبذلك فليفرحوا۟ هو خير مما يجمعون');
INSERT INTO "uthmani_simple" VALUES(10,59,'10:59','قل ارءيتم ما انزل الله لكم من رزق فجعلتم منه حراما وحللا قل ءالله اذن لكم ام على الله تفترون');
INSERT INTO "uthmani_simple" VALUES(10,60,'10:60','وما ظن الذين يفترون على الله الكذب يوم القيمة ان الله لذو فضل على الناس ولكن اكثرهم لا يشكرون');
INSERT INTO "uthmani_simple" VALUES(10,61,'10:61','وما تكون فى شان وما تتلوا۟ منه من قرءان ولا تعملون من عمل الا كنا عليكم شهودا اذ تفيضون فيه وما يعزب عن ربك من مثقال ذرة فى الارض ولا فى السماء ولا اصغر من ذلك ولا اكبر الا فى كتب مبين');
INSERT INTO "uthmani_simple" VALUES(10,62,'10:62','الا ان اولياء الله لا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(10,63,'10:63','الذين ءامنوا۟ وكانوا۟ يتقون');
INSERT INTO "uthmani_simple" VALUES(10,64,'10:64','لهم البشرى فى الحيوة الدنيا وفى الءاخرة لا تبديل لكلمت الله ذلك هو الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(10,65,'10:65','ولا يحزنك قولهم ان العزة لله جميعا هو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(10,66,'10:66','الا ان لله من فى السموت ومن فى الارض وما يتبع الذين يدعون من دون الله شركاء ان يتبعون الا الظن وان هم الا يخرصون');
INSERT INTO "uthmani_simple" VALUES(10,67,'10:67','هو الذى جعل لكم اليل لتسكنوا۟ فيه والنهار مبصرا ان فى ذلك لءايت لقوم يسمعون');
INSERT INTO "uthmani_simple" VALUES(10,68,'10:68','قالوا۟ اتخذ الله ولدا سبحنه هو الغنى له ما فى السموت وما فى الارض ان عندكم من سلطنۭ بهذا اتقولون على الله ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(10,69,'10:69','قل ان الذين يفترون على الله الكذب لا يفلحون');
INSERT INTO "uthmani_simple" VALUES(10,70,'10:70','متع فى الدنيا ثم الينا مرجعهم ثم نذيقهم العذاب الشديد بما كانوا۟ يكفرون');
INSERT INTO "uthmani_simple" VALUES(10,71,'10:71','واتل عليهم نبا نوح اذ قال لقومه يقوم ان كان كبر عليكم مقامى وتذكيرى بـايت الله فعلى الله توكلت فاجمعوا۟ امركم وشركاءكم ثم لا يكن امركم عليكم غمة ثم اقضوا۟ الى ولا تنظرون');
INSERT INTO "uthmani_simple" VALUES(10,72,'10:72','فان توليتم فما سالتكم من اجر ان اجرى الا على الله وامرت ان اكون من المسلمين');
INSERT INTO "uthmani_simple" VALUES(10,73,'10:73','فكذبوه فنجينه ومن معه فى الفلك وجعلنهم خلئف واغرقنا الذين كذبوا۟ بـايتنا فانظر كيف كان عقبة المنذرين');
INSERT INTO "uthmani_simple" VALUES(10,74,'10:74','ثم بعثنا منۢ بعده رسلا الى قومهم فجاءوهم بالبينت فما كانوا۟ ليؤمنوا۟ بما كذبوا۟ به من قبل كذلك نطبع على قلوب المعتدين');
INSERT INTO "uthmani_simple" VALUES(10,75,'10:75','ثم بعثنا منۢ بعدهم موسى وهرون الى فرعون وملاي۟ه بـايتنا فاستكبروا۟ وكانوا۟ قوما مجرمين');
INSERT INTO "uthmani_simple" VALUES(10,76,'10:76','فلما جاءهم الحق من عندنا قالوا۟ ان هذا لسحر مبين');
INSERT INTO "uthmani_simple" VALUES(10,77,'10:77','قال موسى اتقولون للحق لما جاءكم اسحر هذا ولا يفلح السحرون');
INSERT INTO "uthmani_simple" VALUES(10,78,'10:78','قالوا۟ اجئتنا لتلفتنا عما وجدنا عليه ءاباءنا وتكون لكما الكبرياء فى الارض وما نحن لكما بمؤمنين');
INSERT INTO "uthmani_simple" VALUES(10,79,'10:79','وقال فرعون ائتونى بكل سحر عليم');
INSERT INTO "uthmani_simple" VALUES(10,80,'10:80','فلما جاء السحرة قال لهم موسى القوا۟ ما انتم ملقون');
INSERT INTO "uthmani_simple" VALUES(10,81,'10:81','فلما القوا۟ قال موسى ما جئتم به السحر ان الله سيبطله ان الله لا يصلح عمل المفسدين');
INSERT INTO "uthmani_simple" VALUES(10,82,'10:82','ويحق الله الحق بكلمته ولو كره المجرمون');
INSERT INTO "uthmani_simple" VALUES(10,83,'10:83','فما ءامن لموسى الا ذرية من قومه على خوف من فرعون وملاي۟هم ان يفتنهم وان فرعون لعال فى الارض وانه لمن المسرفين');
INSERT INTO "uthmani_simple" VALUES(10,84,'10:84','وقال موسى يقوم ان كنتم ءامنتم بالله فعليه توكلوا۟ ان كنتم مسلمين');
INSERT INTO "uthmani_simple" VALUES(10,85,'10:85','فقالوا۟ على الله توكلنا ربنا لا تجعلنا فتنة للقوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(10,86,'10:86','ونجنا برحمتك من القوم الكفرين');
INSERT INTO "uthmani_simple" VALUES(10,87,'10:87','واوحينا الى موسى واخيه ان تبوءا لقومكما بمصر بيوتا واجعلوا۟ بيوتكم قبلة واقيموا۟ الصلوة وبشر المؤمنين');
INSERT INTO "uthmani_simple" VALUES(10,88,'10:88','وقال موسى ربنا انك ءاتيت فرعون وملاه زينة وامولا فى الحيوة الدنيا ربنا ليضلوا۟ عن سبيلك ربنا اطمس على امولهم واشدد على قلوبهم فلا يؤمنوا۟ حتى يروا۟ العذاب الاليم');
INSERT INTO "uthmani_simple" VALUES(10,89,'10:89','قال قد اجيبت دعوتكما فاستقيما ولا تتبعان سبيل الذين لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(10,90,'10:90','وجوزنا ببنى اسرءيل البحر فاتبعهم فرعون وجنوده بغيا وعدوا حتى اذا ادركه الغرق قال ءامنت انه لا اله الا الذى ءامنت به بنوا۟ اسرءيل وانا۠ من المسلمين');
INSERT INTO "uthmani_simple" VALUES(10,91,'10:91','ءالـن وقد عصيت قبل وكنت من المفسدين');
INSERT INTO "uthmani_simple" VALUES(10,92,'10:92','فاليوم ننجيك ببدنك لتكون لمن خلفك ءاية وان كثيرا من الناس عن ءايتنا لغفلون');
INSERT INTO "uthmani_simple" VALUES(10,93,'10:93','ولقد بوانا بنى اسرءيل مبوا صدق ورزقنهم من الطيبت فما اختلفوا۟ حتى جاءهم العلم ان ربك يقضى بينهم يوم القيمة فيما كانوا۟ فيه يختلفون');
INSERT INTO "uthmani_simple" VALUES(10,94,'10:94','فان كنت فى شك مما انزلنا اليك فسـل الذين يقرءون الكتب من قبلك لقد جاءك الحق من ربك فلا تكونن من الممترين');
INSERT INTO "uthmani_simple" VALUES(10,95,'10:95','ولا تكونن من الذين كذبوا۟ بـايت الله فتكون من الخسرين');
INSERT INTO "uthmani_simple" VALUES(10,96,'10:96','ان الذين حقت عليهم كلمت ربك لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(10,97,'10:97','ولو جاءتهم كل ءاية حتى يروا۟ العذاب الاليم');
INSERT INTO "uthmani_simple" VALUES(10,98,'10:98','فلولا كانت قرية ءامنت فنفعها ايمنها الا قوم يونس لما ءامنوا۟ كشفنا عنهم عذاب الخزى فى الحيوة الدنيا ومتعنهم الى حين');
INSERT INTO "uthmani_simple" VALUES(10,99,'10:99','ولو شاء ربك لءامن من فى الارض كلهم جميعا افانت تكره الناس حتى يكونوا۟ مؤمنين');
INSERT INTO "uthmani_simple" VALUES(10,100,'10:100','وما كان لنفس ان تؤمن الا باذن الله ويجعل الرجس على الذين لا يعقلون');
INSERT INTO "uthmani_simple" VALUES(10,101,'10:101','قل انظروا۟ ماذا فى السموت والارض وما تغنى الءايت والنذر عن قوم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(10,102,'10:102','فهل ينتظرون الا مثل ايام الذين خلوا۟ من قبلهم قل فانتظروا۟ انى معكم من المنتظرين');
INSERT INTO "uthmani_simple" VALUES(10,103,'10:103','ثم ننجى رسلنا والذين ءامنوا۟ كذلك حقا علينا ننج المؤمنين');
INSERT INTO "uthmani_simple" VALUES(10,104,'10:104','قل يايها الناس ان كنتم فى شك من دينى فلا اعبد الذين تعبدون من دون الله ولكن اعبد الله الذى يتوفىكم وامرت ان اكون من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(10,105,'10:105','وان اقم وجهك للدين حنيفا ولا تكونن من المشركين');
INSERT INTO "uthmani_simple" VALUES(10,106,'10:106','ولا تدع من دون الله ما لا ينفعك ولا يضرك فان فعلت فانك اذا من الظلمين');
INSERT INTO "uthmani_simple" VALUES(10,107,'10:107','وان يمسسك الله بضر فلا كاشف له الا هو وان يردك بخير فلا راد لفضله يصيب به من يشاء من عباده وهو الغفور الرحيم');
INSERT INTO "uthmani_simple" VALUES(10,108,'10:108','قل يايها الناس قد جاءكم الحق من ربكم فمن اهتدى فانما يهتدى لنفسه ومن ضل فانما يضل عليها وما انا۠ عليكم بوكيل');
INSERT INTO "uthmani_simple" VALUES(10,109,'10:109','واتبع ما يوحى اليك واصبر حتى يحكم الله وهو خير الحكمين');
INSERT INTO "uthmani_simple" VALUES(11,1,'11:1','الر كتب احكمت ءايته ثم فصلت من لدن حكيم خبير');
INSERT INTO "uthmani_simple" VALUES(11,2,'11:2','الا تعبدوا۟ الا الله اننى لكم منه نذير وبشير');
INSERT INTO "uthmani_simple" VALUES(11,3,'11:3','وان استغفروا۟ ربكم ثم توبوا۟ اليه يمتعكم متعا حسنا الى اجل مسمى ويؤت كل ذى فضل فضله وان تولوا۟ فانى اخاف عليكم عذاب يوم كبير');
INSERT INTO "uthmani_simple" VALUES(11,4,'11:4','الى الله مرجعكم وهو على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(11,5,'11:5','الا انهم يثنون صدورهم ليستخفوا۟ منه الا حين يستغشون ثيابهم يعلم ما يسرون وما يعلنون انه عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(11,6,'11:6','وما من دابة فى الارض الا على الله رزقها ويعلم مستقرها ومستودعها كل فى كتب مبين');
INSERT INTO "uthmani_simple" VALUES(11,7,'11:7','وهو الذى خلق السموت والارض فى ستة ايام وكان عرشه على الماء ليبلوكم ايكم احسن عملا ولئن قلت انكم مبعوثون منۢ بعد الموت ليقولن الذين كفروا۟ ان هذا الا سحر مبين');
INSERT INTO "uthmani_simple" VALUES(11,8,'11:8','ولئن اخرنا عنهم العذاب الى امة معدودة ليقولن ما يحبسه الا يوم ياتيهم ليس مصروفا عنهم وحاق بهم ما كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(11,9,'11:9','ولئن اذقنا الانسن منا رحمة ثم نزعنها منه انه ليـوس كفور');
INSERT INTO "uthmani_simple" VALUES(11,10,'11:10','ولئن اذقنه نعماء بعد ضراء مسته ليقولن ذهب السيـات عنى انه لفرح فخور');
INSERT INTO "uthmani_simple" VALUES(11,11,'11:11','الا الذين صبروا۟ وعملوا۟ الصلحت او۟لئك لهم مغفرة واجر كبير');
INSERT INTO "uthmani_simple" VALUES(11,12,'11:12','فلعلك تاركۢ بعض ما يوحى اليك وضائقۢ به صدرك ان يقولوا۟ لولا انزل عليه كنز او جاء معه ملك انما انت نذير والله على كل شىء وكيل');
INSERT INTO "uthmani_simple" VALUES(11,13,'11:13','ام يقولون افترىه قل فاتوا۟ بعشر سور مثله مفتريت وادعوا۟ من استطعتم من دون الله ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(11,14,'11:14','فالم يستجيبوا۟ لكم فاعلموا۟ انما انزل بعلم الله وان لا اله الا هو فهل انتم مسلمون');
INSERT INTO "uthmani_simple" VALUES(11,15,'11:15','من كان يريد الحيوة الدنيا وزينتها نوف اليهم اعملهم فيها وهم فيها لا يبخسون');
INSERT INTO "uthmani_simple" VALUES(11,16,'11:16','او۟لئك الذين ليس لهم فى الءاخرة الا النار وحبط ما صنعوا۟ فيها وبطل ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(11,17,'11:17','افمن كان على بينة من ربه ويتلوه شاهد منه ومن قبله كتب موسى اماما ورحمة او۟لئك يؤمنون به ومن يكفر به من الاحزاب فالنار موعده فلا تك فى مرية منه انه الحق من ربك ولكن اكثر الناس لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(11,18,'11:18','ومن اظلم ممن افترى على الله كذبا او۟لئك يعرضون على ربهم ويقول الاشهد هؤلاء الذين كذبوا۟ على ربهم الا لعنة الله على الظلمين');
INSERT INTO "uthmani_simple" VALUES(11,19,'11:19','الذين يصدون عن سبيل الله ويبغونها عوجا وهم بالءاخرة هم كفرون');
INSERT INTO "uthmani_simple" VALUES(11,20,'11:20','او۟لئك لم يكونوا۟ معجزين فى الارض وما كان لهم من دون الله من اولياء يضعف لهم العذاب ما كانوا۟ يستطيعون السمع وما كانوا۟ يبصرون');
INSERT INTO "uthmani_simple" VALUES(11,21,'11:21','او۟لئك الذين خسروا۟ انفسهم وضل عنهم ما كانوا۟ يفترون');
INSERT INTO "uthmani_simple" VALUES(11,22,'11:22','لا جرم انهم فى الءاخرة هم الاخسرون');
INSERT INTO "uthmani_simple" VALUES(11,23,'11:23','ان الذين ءامنوا۟ وعملوا۟ الصلحت واخبتوا۟ الى ربهم او۟لئك اصحب الجنة هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(11,24,'11:24','مثل الفريقين كالاعمى والاصم والبصير والسميع هل يستويان مثلا افلا تذكرون');
INSERT INTO "uthmani_simple" VALUES(11,25,'11:25','ولقد ارسلنا نوحا الى قومه انى لكم نذير مبين');
INSERT INTO "uthmani_simple" VALUES(11,26,'11:26','ان لا تعبدوا۟ الا الله انى اخاف عليكم عذاب يوم اليم');
INSERT INTO "uthmani_simple" VALUES(11,27,'11:27','فقال الملا الذين كفروا۟ من قومه ما نرىك الا بشرا مثلنا وما نرىك اتبعك الا الذين هم اراذلنا بادى الراى وما نرى لكم علينا من فضلۭ بل نظنكم كذبين');
INSERT INTO "uthmani_simple" VALUES(11,28,'11:28','قال يقوم ارءيتم ان كنت على بينة من ربى وءاتىنى رحمة من عنده فعميت عليكم انلزمكموها وانتم لها كرهون');
INSERT INTO "uthmani_simple" VALUES(11,29,'11:29','ويقوم لا اسـلكم عليه مالا ان اجرى الا على الله وما انا۠ بطارد الذين ءامنوا۟ انهم ملقوا۟ ربهم ولكنى ارىكم قوما تجهلون');
INSERT INTO "uthmani_simple" VALUES(11,30,'11:30','ويقوم من ينصرنى من الله ان طردتهم افلا تذكرون');
INSERT INTO "uthmani_simple" VALUES(11,31,'11:31','ولا اقول لكم عندى خزائن الله ولا اعلم الغيب ولا اقول انى ملك ولا اقول للذين تزدرى اعينكم لن يؤتيهم الله خيرا الله اعلم بما فى انفسهم انى اذا لمن الظلمين');
INSERT INTO "uthmani_simple" VALUES(11,32,'11:32','قالوا۟ ينوح قد جدلتنا فاكثرت جدلنا فاتنا بما تعدنا ان كنت من الصدقين');
INSERT INTO "uthmani_simple" VALUES(11,33,'11:33','قال انما ياتيكم به الله ان شاء وما انتم بمعجزين');
INSERT INTO "uthmani_simple" VALUES(11,34,'11:34','ولا ينفعكم نصحى ان اردت ان انصح لكم ان كان الله يريد ان يغويكم هو ربكم واليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(11,35,'11:35','ام يقولون افترىه قل ان افتريته فعلى اجرامى وانا۠ برىء مما تجرمون');
INSERT INTO "uthmani_simple" VALUES(11,36,'11:36','واوحى الى نوح انه لن يؤمن من قومك الا من قد ءامن فلا تبتئس بما كانوا۟ يفعلون');
INSERT INTO "uthmani_simple" VALUES(11,37,'11:37','واصنع الفلك باعيننا ووحينا ولا تخطبنى فى الذين ظلموا۟ انهم مغرقون');
INSERT INTO "uthmani_simple" VALUES(11,38,'11:38','ويصنع الفلك وكلما مر عليه ملا من قومه سخروا۟ منه قال ان تسخروا۟ منا فانا نسخر منكم كما تسخرون');
INSERT INTO "uthmani_simple" VALUES(11,39,'11:39','فسوف تعلمون من ياتيه عذاب يخزيه ويحل عليه عذاب مقيم');
INSERT INTO "uthmani_simple" VALUES(11,40,'11:40','حتى اذا جاء امرنا وفار التنور قلنا احمل فيها من كل زوجين اثنين واهلك الا من سبق عليه القول ومن ءامن وما ءامن معه الا قليل');
INSERT INTO "uthmani_simple" VALUES(11,41,'11:41','وقال اركبوا۟ فيها بسم الله مجر۪ىها ومرسىها ان ربى لغفور رحيم');
INSERT INTO "uthmani_simple" VALUES(11,42,'11:42','وهى تجرى بهم فى موج كالجبال ونادى نوح ابنه وكان فى معزل يبنى اركب معنا ولا تكن مع الكفرين');
INSERT INTO "uthmani_simple" VALUES(11,43,'11:43','قال سـاوى الى جبل يعصمنى من الماء قال لا عاصم اليوم من امر الله الا من رحم وحال بينهما الموج فكان من المغرقين');
INSERT INTO "uthmani_simple" VALUES(11,44,'11:44','وقيل يارض ابلعى ماءك ويسماء اقلعى وغيض الماء وقضى الامر واستوت على الجودى وقيل بعدا للقوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(11,45,'11:45','ونادى نوح ربه فقال رب ان ابنى من اهلى وان وعدك الحق وانت احكم الحكمين');
INSERT INTO "uthmani_simple" VALUES(11,46,'11:46','قال ينوح انه ليس من اهلك انه عمل غير صلح فلا تسـلن ما ليس لك به علم انى اعظك ان تكون من الجهلين');
INSERT INTO "uthmani_simple" VALUES(11,47,'11:47','قال رب انى اعوذ بك ان اسـلك ما ليس لى به علم والا تغفر لى وترحمنى اكن من الخسرين');
INSERT INTO "uthmani_simple" VALUES(11,48,'11:48','قيل ينوح اهبط بسلم منا وبركت عليك وعلى امم ممن معك وامم سنمتعهم ثم يمسهم منا عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(11,49,'11:49','تلك من انۢباء الغيب نوحيها اليك ما كنت تعلمها انت ولا قومك من قبل هذا فاصبر ان العقبة للمتقين');
INSERT INTO "uthmani_simple" VALUES(11,50,'11:50','والى عاد اخاهم هودا قال يقوم اعبدوا۟ الله ما لكم من اله غيره ان انتم الا مفترون');
INSERT INTO "uthmani_simple" VALUES(11,51,'11:51','يقوم لا اسـلكم عليه اجرا ان اجرى الا على الذى فطرنى افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(11,52,'11:52','ويقوم استغفروا۟ ربكم ثم توبوا۟ اليه يرسل السماء عليكم مدرارا ويزدكم قوة الى قوتكم ولا تتولوا۟ مجرمين');
INSERT INTO "uthmani_simple" VALUES(11,53,'11:53','قالوا۟ يهود ما جئتنا ببينة وما نحن بتاركى ءالهتنا عن قولك وما نحن لك بمؤمنين');
INSERT INTO "uthmani_simple" VALUES(11,54,'11:54','ان نقول الا اعترىك بعض ءالهتنا بسوء قال انى اشهد الله واشهدوا۟ انى برىء مما تشركون');
INSERT INTO "uthmani_simple" VALUES(11,55,'11:55','من دونه فكيدونى جميعا ثم لا تنظرون');
INSERT INTO "uthmani_simple" VALUES(11,56,'11:56','انى توكلت على الله ربى وربكم ما من دابة الا هو ءاخذۢ بناصيتها ان ربى على صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(11,57,'11:57','فان تولوا۟ فقد ابلغتكم ما ارسلت به اليكم ويستخلف ربى قوما غيركم ولا تضرونه شيـا ان ربى على كل شىء حفيظ');
INSERT INTO "uthmani_simple" VALUES(11,58,'11:58','ولما جاء امرنا نجينا هودا والذين ءامنوا۟ معه برحمة منا ونجينهم من عذاب غليظ');
INSERT INTO "uthmani_simple" VALUES(11,59,'11:59','وتلك عاد جحدوا۟ بـايت ربهم وعصوا۟ رسله واتبعوا۟ امر كل جبار عنيد');
INSERT INTO "uthmani_simple" VALUES(11,60,'11:60','واتبعوا۟ فى هذه الدنيا لعنة ويوم القيمة الا ان عادا كفروا۟ ربهم الا بعدا لعاد قوم هود');
INSERT INTO "uthmani_simple" VALUES(11,61,'11:61','والى ثمود اخاهم صلحا قال يقوم اعبدوا۟ الله ما لكم من اله غيره هو انشاكم من الارض واستعمركم فيها فاستغفروه ثم توبوا۟ اليه ان ربى قريب مجيب');
INSERT INTO "uthmani_simple" VALUES(11,62,'11:62','قالوا۟ يصلح قد كنت فينا مرجوا قبل هذا اتنهىنا ان نعبد ما يعبد ءاباؤنا واننا لفى شك مما تدعونا اليه مريب');
INSERT INTO "uthmani_simple" VALUES(11,63,'11:63','قال يقوم ارءيتم ان كنت على بينة من ربى وءاتىنى منه رحمة فمن ينصرنى من الله ان عصيته فما تزيدوننى غير تخسير');
INSERT INTO "uthmani_simple" VALUES(11,64,'11:64','ويقوم هذه ناقة الله لكم ءاية فذروها تاكل فى ارض الله ولا تمسوها بسوء فياخذكم عذاب قريب');
INSERT INTO "uthmani_simple" VALUES(11,65,'11:65','فعقروها فقال تمتعوا۟ فى داركم ثلثة ايام ذلك وعد غير مكذوب');
INSERT INTO "uthmani_simple" VALUES(11,66,'11:66','فلما جاء امرنا نجينا صلحا والذين ءامنوا۟ معه برحمة منا ومن خزى يومئذ ان ربك هو القوى العزيز');
INSERT INTO "uthmani_simple" VALUES(11,67,'11:67','واخذ الذين ظلموا۟ الصيحة فاصبحوا۟ فى ديرهم جثمين');
INSERT INTO "uthmani_simple" VALUES(11,68,'11:68','كان لم يغنوا۟ فيها الا ان ثمودا۟ كفروا۟ ربهم الا بعدا لثمود');
INSERT INTO "uthmani_simple" VALUES(11,69,'11:69','ولقد جاءت رسلنا ابرهيم بالبشرى قالوا۟ سلما قال سلم فما لبث ان جاء بعجل حنيذ');
INSERT INTO "uthmani_simple" VALUES(11,70,'11:70','فلما رءا ايديهم لا تصل اليه نكرهم واوجس منهم خيفة قالوا۟ لا تخف انا ارسلنا الى قوم لوط');
INSERT INTO "uthmani_simple" VALUES(11,71,'11:71','وامراته قائمة فضحكت فبشرنها باسحق ومن وراء اسحق يعقوب');
INSERT INTO "uthmani_simple" VALUES(11,72,'11:72','قالت يويلتى ءالد وانا۠ عجوز وهذا بعلى شيخا ان هذا لشىء عجيب');
INSERT INTO "uthmani_simple" VALUES(11,73,'11:73','قالوا۟ اتعجبين من امر الله رحمت الله وبركته عليكم اهل البيت انه حميد مجيد');
INSERT INTO "uthmani_simple" VALUES(11,74,'11:74','فلما ذهب عن ابرهيم الروع وجاءته البشرى يجدلنا فى قوم لوط');
INSERT INTO "uthmani_simple" VALUES(11,75,'11:75','ان ابرهيم لحليم اوه منيب');
INSERT INTO "uthmani_simple" VALUES(11,76,'11:76','يابرهيم اعرض عن هذا انه قد جاء امر ربك وانهم ءاتيهم عذاب غير مردود');
INSERT INTO "uthmani_simple" VALUES(11,77,'11:77','ولما جاءت رسلنا لوطا سىء بهم وضاق بهم ذرعا وقال هذا يوم عصيب');
INSERT INTO "uthmani_simple" VALUES(11,78,'11:78','وجاءه قومه يهرعون اليه ومن قبل كانوا۟ يعملون السيـات قال يقوم هؤلاء بناتى هن اطهر لكم فاتقوا۟ الله ولا تخزون فى ضيفى اليس منكم رجل رشيد');
INSERT INTO "uthmani_simple" VALUES(11,79,'11:79','قالوا۟ لقد علمت ما لنا فى بناتك من حق وانك لتعلم ما نريد');
INSERT INTO "uthmani_simple" VALUES(11,80,'11:80','قال لو ان لى بكم قوة او ءاوى الى ركن شديد');
INSERT INTO "uthmani_simple" VALUES(11,81,'11:81','قالوا۟ يلوط انا رسل ربك لن يصلوا۟ اليك فاسر باهلك بقطع من اليل ولا يلتفت منكم احد الا امراتك انه مصيبها ما اصابهم ان موعدهم الصبح اليس الصبح بقريب');
INSERT INTO "uthmani_simple" VALUES(11,82,'11:82','فلما جاء امرنا جعلنا عليها سافلها وامطرنا عليها حجارة من سجيل منضود');
INSERT INTO "uthmani_simple" VALUES(11,83,'11:83','مسومة عند ربك وما هى من الظلمين ببعيد');
INSERT INTO "uthmani_simple" VALUES(11,84,'11:84','والى مدين اخاهم شعيبا قال يقوم اعبدوا۟ الله ما لكم من اله غيره ولا تنقصوا۟ المكيال والميزان انى ارىكم بخير وانى اخاف عليكم عذاب يوم محيط');
INSERT INTO "uthmani_simple" VALUES(11,85,'11:85','ويقوم اوفوا۟ المكيال والميزان بالقسط ولا تبخسوا۟ الناس اشياءهم ولا تعثوا۟ فى الارض مفسدين');
INSERT INTO "uthmani_simple" VALUES(11,86,'11:86','بقيت الله خير لكم ان كنتم مؤمنين وما انا۠ عليكم بحفيظ');
INSERT INTO "uthmani_simple" VALUES(11,87,'11:87','قالوا۟ يشعيب اصلوتك تامرك ان نترك ما يعبد ءاباؤنا او ان نفعل فى امولنا ما نشؤا۟ انك لانت الحليم الرشيد');
INSERT INTO "uthmani_simple" VALUES(11,88,'11:88','قال يقوم ارءيتم ان كنت على بينة من ربى ورزقنى منه رزقا حسنا وما اريد ان اخالفكم الى ما انهىكم عنه ان اريد الا الاصلح ما استطعت وما توفيقى الا بالله عليه توكلت واليه انيب');
INSERT INTO "uthmani_simple" VALUES(11,89,'11:89','ويقوم لا يجرمنكم شقاقى ان يصيبكم مثل ما اصاب قوم نوح او قوم هود او قوم صلح وما قوم لوط منكم ببعيد');
INSERT INTO "uthmani_simple" VALUES(11,90,'11:90','واستغفروا۟ ربكم ثم توبوا۟ اليه ان ربى رحيم ودود');
INSERT INTO "uthmani_simple" VALUES(11,91,'11:91','قالوا۟ يشعيب ما نفقه كثيرا مما تقول وانا لنرىك فينا ضعيفا ولولا رهطك لرجمنك وما انت علينا بعزيز');
INSERT INTO "uthmani_simple" VALUES(11,92,'11:92','قال يقوم ارهطى اعز عليكم من الله واتخذتموه وراءكم ظهريا ان ربى بما تعملون محيط');
INSERT INTO "uthmani_simple" VALUES(11,93,'11:93','ويقوم اعملوا۟ على مكانتكم انى عمل سوف تعلمون من ياتيه عذاب يخزيه ومن هو كذب وارتقبوا۟ انى معكم رقيب');
INSERT INTO "uthmani_simple" VALUES(11,94,'11:94','ولما جاء امرنا نجينا شعيبا والذين ءامنوا۟ معه برحمة منا واخذت الذين ظلموا۟ الصيحة فاصبحوا۟ فى ديرهم جثمين');
INSERT INTO "uthmani_simple" VALUES(11,95,'11:95','كان لم يغنوا۟ فيها الا بعدا لمدين كما بعدت ثمود');
INSERT INTO "uthmani_simple" VALUES(11,96,'11:96','ولقد ارسلنا موسى بـايتنا وسلطن مبين');
INSERT INTO "uthmani_simple" VALUES(11,97,'11:97','الى فرعون وملاي۟ه فاتبعوا۟ امر فرعون وما امر فرعون برشيد');
INSERT INTO "uthmani_simple" VALUES(11,98,'11:98','يقدم قومه يوم القيمة فاوردهم النار وبئس الورد المورود');
INSERT INTO "uthmani_simple" VALUES(11,99,'11:99','واتبعوا۟ فى هذه لعنة ويوم القيمة بئس الرفد المرفود');
INSERT INTO "uthmani_simple" VALUES(11,100,'11:100','ذلك من انۢباء القرى نقصه عليك منها قائم وحصيد');
INSERT INTO "uthmani_simple" VALUES(11,101,'11:101','وما ظلمنهم ولكن ظلموا۟ انفسهم فما اغنت عنهم ءالهتهم التى يدعون من دون الله من شىء لما جاء امر ربك وما زادوهم غير تتبيب');
INSERT INTO "uthmani_simple" VALUES(11,102,'11:102','وكذلك اخذ ربك اذا اخذ القرى وهى ظلمة ان اخذه اليم شديد');
INSERT INTO "uthmani_simple" VALUES(11,103,'11:103','ان فى ذلك لءاية لمن خاف عذاب الءاخرة ذلك يوم مجموع له الناس وذلك يوم مشهود');
INSERT INTO "uthmani_simple" VALUES(11,104,'11:104','وما نؤخره الا لاجل معدود');
INSERT INTO "uthmani_simple" VALUES(11,105,'11:105','يوم يات لا تكلم نفس الا باذنه فمنهم شقى وسعيد');
INSERT INTO "uthmani_simple" VALUES(11,106,'11:106','فاما الذين شقوا۟ ففى النار لهم فيها زفير وشهيق');
INSERT INTO "uthmani_simple" VALUES(11,107,'11:107','خلدين فيها ما دامت السموت والارض الا ما شاء ربك ان ربك فعال لما يريد');
INSERT INTO "uthmani_simple" VALUES(11,108,'11:108','واما الذين سعدوا۟ ففى الجنة خلدين فيها ما دامت السموت والارض الا ما شاء ربك عطاء غير مجذوذ');
INSERT INTO "uthmani_simple" VALUES(11,109,'11:109','فلا تك فى مرية مما يعبد هؤلاء ما يعبدون الا كما يعبد ءاباؤهم من قبل وانا لموفوهم نصيبهم غير منقوص');
INSERT INTO "uthmani_simple" VALUES(11,110,'11:110','ولقد ءاتينا موسى الكتب فاختلف فيه ولولا كلمة سبقت من ربك لقضى بينهم وانهم لفى شك منه مريب');
INSERT INTO "uthmani_simple" VALUES(11,111,'11:111','وان كلا لما ليوفينهم ربك اعملهم انه بما يعملون خبير');
INSERT INTO "uthmani_simple" VALUES(11,112,'11:112','فاستقم كما امرت ومن تاب معك ولا تطغوا۟ انه بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(11,113,'11:113','ولا تركنوا۟ الى الذين ظلموا۟ فتمسكم النار وما لكم من دون الله من اولياء ثم لا تنصرون');
INSERT INTO "uthmani_simple" VALUES(11,114,'11:114','واقم الصلوة طرفى النهار وزلفا من اليل ان الحسنت يذهبن السيـات ذلك ذكرى للذكرين');
INSERT INTO "uthmani_simple" VALUES(11,115,'11:115','واصبر فان الله لا يضيع اجر المحسنين');
INSERT INTO "uthmani_simple" VALUES(11,116,'11:116','فلولا كان من القرون من قبلكم او۟لوا۟ بقية ينهون عن الفساد فى الارض الا قليلا ممن انجينا منهم واتبع الذين ظلموا۟ ما اترفوا۟ فيه وكانوا۟ مجرمين');
INSERT INTO "uthmani_simple" VALUES(11,117,'11:117','وما كان ربك ليهلك القرى بظلم واهلها مصلحون');
INSERT INTO "uthmani_simple" VALUES(11,118,'11:118','ولو شاء ربك لجعل الناس امة وحدة ولا يزالون مختلفين');
INSERT INTO "uthmani_simple" VALUES(11,119,'11:119','الا من رحم ربك ولذلك خلقهم وتمت كلمة ربك لاملان جهنم من الجنة والناس اجمعين');
INSERT INTO "uthmani_simple" VALUES(11,120,'11:120','وكلا نقص عليك من انۢباء الرسل ما نثبت به فؤادك وجاءك فى هذه الحق وموعظة وذكرى للمؤمنين');
INSERT INTO "uthmani_simple" VALUES(11,121,'11:121','وقل للذين لا يؤمنون اعملوا۟ على مكانتكم انا عملون');
INSERT INTO "uthmani_simple" VALUES(11,122,'11:122','وانتظروا۟ انا منتظرون');
INSERT INTO "uthmani_simple" VALUES(11,123,'11:123','ولله غيب السموت والارض واليه يرجع الامر كله فاعبده وتوكل عليه وما ربك بغفل عما تعملون');
INSERT INTO "uthmani_simple" VALUES(12,1,'12:1','الر تلك ءايت الكتب المبين');
INSERT INTO "uthmani_simple" VALUES(12,2,'12:2','انا انزلنه قرءنا عربيا لعلكم تعقلون');
INSERT INTO "uthmani_simple" VALUES(12,3,'12:3','نحن نقص عليك احسن القصص بما اوحينا اليك هذا القرءان وان كنت من قبله لمن الغفلين');
INSERT INTO "uthmani_simple" VALUES(12,4,'12:4','اذ قال يوسف لابيه يابت انى رايت احد عشر كوكبا والشمس والقمر رايتهم لى سجدين');
INSERT INTO "uthmani_simple" VALUES(12,5,'12:5','قال يبنى لا تقصص رءياك على اخوتك فيكيدوا۟ لك كيدا ان الشيطن للانسن عدو مبين');
INSERT INTO "uthmani_simple" VALUES(12,6,'12:6','وكذلك يجتبيك ربك ويعلمك من تاويل الاحاديث ويتم نعمته عليك وعلى ءال يعقوب كما اتمها على ابويك من قبل ابرهيم واسحق ان ربك عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(12,7,'12:7','لقد كان فى يوسف واخوته ءايت للسائلين');
INSERT INTO "uthmani_simple" VALUES(12,8,'12:8','اذ قالوا۟ ليوسف واخوه احب الى ابينا منا ونحن عصبة ان ابانا لفى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(12,9,'12:9','اقتلوا۟ يوسف او اطرحوه ارضا يخل لكم وجه ابيكم وتكونوا۟ منۢ بعده قوما صلحين');
INSERT INTO "uthmani_simple" VALUES(12,10,'12:10','قال قائل منهم لا تقتلوا۟ يوسف والقوه فى غيبت الجب يلتقطه بعض السيارة ان كنتم فعلين');
INSERT INTO "uthmani_simple" VALUES(12,11,'12:11','قالوا۟ يابانا ما لك لا تام۫نا على يوسف وانا له لنصحون');
INSERT INTO "uthmani_simple" VALUES(12,12,'12:12','ارسله معنا غدا يرتع ويلعب وانا له لحفظون');
INSERT INTO "uthmani_simple" VALUES(12,13,'12:13','قال انى ليحزننى ان تذهبوا۟ به واخاف ان ياكله الذئب وانتم عنه غفلون');
INSERT INTO "uthmani_simple" VALUES(12,14,'12:14','قالوا۟ لئن اكله الذئب ونحن عصبة انا اذا لخسرون');
INSERT INTO "uthmani_simple" VALUES(12,15,'12:15','فلما ذهبوا۟ به واجمعوا۟ ان يجعلوه فى غيبت الجب واوحينا اليه لتنبئنهم بامرهم هذا وهم لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(12,16,'12:16','وجاءو اباهم عشاء يبكون');
INSERT INTO "uthmani_simple" VALUES(12,17,'12:17','قالوا۟ يابانا انا ذهبنا نستبق وتركنا يوسف عند متعنا فاكله الذئب وما انت بمؤمن لنا ولو كنا صدقين');
INSERT INTO "uthmani_simple" VALUES(12,18,'12:18','وجاءو على قميصه بدم كذب قال بل سولت لكم انفسكم امرا فصبر جميل والله المستعان على ما تصفون');
INSERT INTO "uthmani_simple" VALUES(12,19,'12:19','وجاءت سيارة فارسلوا۟ واردهم فادلى دلوه قال يبشرى هذا غلم واسروه بضعة والله عليمۢ بما يعملون');
INSERT INTO "uthmani_simple" VALUES(12,20,'12:20','وشروه بثمنۭ بخس درهم معدودة وكانوا۟ فيه من الزهدين');
INSERT INTO "uthmani_simple" VALUES(12,21,'12:21','وقال الذى اشترىه من مصر لامراته اكرمى مثوىه عسى ان ينفعنا او نتخذه ولدا وكذلك مكنا ليوسف فى الارض ولنعلمه من تاويل الاحاديث والله غالب على امره ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(12,22,'12:22','ولما بلغ اشده ءاتينه حكما وعلما وكذلك نجزى المحسنين');
INSERT INTO "uthmani_simple" VALUES(12,23,'12:23','ورودته التى هو فى بيتها عن نفسه وغلقت الابوب وقالت هيت لك قال معاذ الله انه ربى احسن مثواى انه لا يفلح الظلمون');
INSERT INTO "uthmani_simple" VALUES(12,24,'12:24','ولقد همت به وهم بها لولا ان رءا برهن ربه كذلك لنصرف عنه السوء والفحشاء انه من عبادنا المخلصين');
INSERT INTO "uthmani_simple" VALUES(12,25,'12:25','واستبقا الباب وقدت قميصه من دبر والفيا سيدها لدا الباب قالت ما جزاء من اراد باهلك سوءا الا ان يسجن او عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(12,26,'12:26','قال هى رودتنى عن نفسى وشهد شاهد من اهلها ان كان قميصه قد من قبل فصدقت وهو من الكذبين');
INSERT INTO "uthmani_simple" VALUES(12,27,'12:27','وان كان قميصه قد من دبر فكذبت وهو من الصدقين');
INSERT INTO "uthmani_simple" VALUES(12,28,'12:28','فلما رءا قميصه قد من دبر قال انه من كيدكن ان كيدكن عظيم');
INSERT INTO "uthmani_simple" VALUES(12,29,'12:29','يوسف اعرض عن هذا واستغفرى لذنۢبك انك كنت من الخاطـين');
INSERT INTO "uthmani_simple" VALUES(12,30,'12:30','وقال نسوة فى المدينة امرات العزيز ترود فتىها عن نفسه قد شغفها حبا انا لنرىها فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(12,31,'12:31','فلما سمعت بمكرهن ارسلت اليهن واعتدت لهن متكـا وءاتت كل وحدة منهن سكينا وقالت اخرج عليهن فلما راينه اكبرنه وقطعن ايديهن وقلن حش لله ما هذا بشرا ان هذا الا ملك كريم');
INSERT INTO "uthmani_simple" VALUES(12,32,'12:32','قالت فذلكن الذى لمتننى فيه ولقد رودته عن نفسه فاستعصم ولئن لم يفعل ما ءامره ليسجنن وليكونا من الصغرين');
INSERT INTO "uthmani_simple" VALUES(12,33,'12:33','قال رب السجن احب الى مما يدعوننى اليه والا تصرف عنى كيدهن اصب اليهن واكن من الجهلين');
INSERT INTO "uthmani_simple" VALUES(12,34,'12:34','فاستجاب له ربه فصرف عنه كيدهن انه هو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(12,35,'12:35','ثم بدا لهم منۢ بعد ما راوا۟ الءايت ليسجننه حتى حين');
INSERT INTO "uthmani_simple" VALUES(12,36,'12:36','ودخل معه السجن فتيان قال احدهما انى ارىنى اعصر خمرا وقال الءاخر انى ارىنى احمل فوق راسى خبزا تاكل الطير منه نبئنا بتاويله انا نرىك من المحسنين');
INSERT INTO "uthmani_simple" VALUES(12,37,'12:37','قال لا ياتيكما طعام ترزقانه الا نباتكما بتاويله قبل ان ياتيكما ذلكما مما علمنى ربى انى تركت ملة قوم لا يؤمنون بالله وهم بالءاخرة هم كفرون');
INSERT INTO "uthmani_simple" VALUES(12,38,'12:38','واتبعت ملة ءاباءى ابرهيم واسحق ويعقوب ما كان لنا ان نشرك بالله من شىء ذلك من فضل الله علينا وعلى الناس ولكن اكثر الناس لا يشكرون');
INSERT INTO "uthmani_simple" VALUES(12,39,'12:39','يصىحبى السجن ءارباب متفرقون خير ام الله الوحد القهار');
INSERT INTO "uthmani_simple" VALUES(12,40,'12:40','ما تعبدون من دونه الا اسماء سميتموها انتم وءاباؤكم ما انزل الله بها من سلطن ان الحكم الا لله امر الا تعبدوا۟ الا اياه ذلك الدين القيم ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(12,41,'12:41','يصىحبى السجن اما احدكما فيسقى ربه خمرا واما الءاخر فيصلب فتاكل الطير من راسه قضى الامر الذى فيه تستفتيان');
INSERT INTO "uthmani_simple" VALUES(12,42,'12:42','وقال للذى ظن انه ناج منهما اذكرنى عند ربك فانسىه الشيطن ذكر ربه فلبث فى السجن بضع سنين');
INSERT INTO "uthmani_simple" VALUES(12,43,'12:43','وقال الملك انى ارى سبع بقرت سمان ياكلهن سبع عجاف وسبع سنۢبلت خضر واخر يابست يايها الملا افتونى فى رءيى ان كنتم للرءيا تعبرون');
INSERT INTO "uthmani_simple" VALUES(12,44,'12:44','قالوا۟ اضغث احلم وما نحن بتاويل الاحلم بعلمين');
INSERT INTO "uthmani_simple" VALUES(12,45,'12:45','وقال الذى نجا منهما وادكر بعد امة انا۠ انبئكم بتاويله فارسلون');
INSERT INTO "uthmani_simple" VALUES(12,46,'12:46','يوسف ايها الصديق افتنا فى سبع بقرت سمان ياكلهن سبع عجاف وسبع سنۢبلت خضر واخر يابست لعلى ارجع الى الناس لعلهم يعلمون');
INSERT INTO "uthmani_simple" VALUES(12,47,'12:47','قال تزرعون سبع سنين دابا فما حصدتم فذروه فى سنۢبله الا قليلا مما تاكلون');
INSERT INTO "uthmani_simple" VALUES(12,48,'12:48','ثم ياتى منۢ بعد ذلك سبع شداد ياكلن ما قدمتم لهن الا قليلا مما تحصنون');
INSERT INTO "uthmani_simple" VALUES(12,49,'12:49','ثم ياتى منۢ بعد ذلك عام فيه يغاث الناس وفيه يعصرون');
INSERT INTO "uthmani_simple" VALUES(12,50,'12:50','وقال الملك ائتونى به فلما جاءه الرسول قال ارجع الى ربك فسـله ما بال النسوة التى قطعن ايديهن ان ربى بكيدهن عليم');
INSERT INTO "uthmani_simple" VALUES(12,51,'12:51','قال ما خطبكن اذ رودتن يوسف عن نفسه قلن حش لله ما علمنا عليه من سوء قالت امرات العزيز الـن حصحص الحق انا۠ رودته عن نفسه وانه لمن الصدقين');
INSERT INTO "uthmani_simple" VALUES(12,52,'12:52','ذلك ليعلم انى لم اخنه بالغيب وان الله لا يهدى كيد الخائنين');
INSERT INTO "uthmani_simple" VALUES(12,53,'12:53','وما ابرئ نفسى ان النفس لامارةۢ بالسوء الا ما رحم ربى ان ربى غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(12,54,'12:54','وقال الملك ائتونى به استخلصه لنفسى فلما كلمه قال انك اليوم لدينا مكين امين');
INSERT INTO "uthmani_simple" VALUES(12,55,'12:55','قال اجعلنى على خزائن الارض انى حفيظ عليم');
INSERT INTO "uthmani_simple" VALUES(12,56,'12:56','وكذلك مكنا ليوسف فى الارض يتبوا منها حيث يشاء نصيب برحمتنا من نشاء ولا نضيع اجر المحسنين');
INSERT INTO "uthmani_simple" VALUES(12,57,'12:57','ولاجر الءاخرة خير للذين ءامنوا۟ وكانوا۟ يتقون');
INSERT INTO "uthmani_simple" VALUES(12,58,'12:58','وجاء اخوة يوسف فدخلوا۟ عليه فعرفهم وهم له منكرون');
INSERT INTO "uthmani_simple" VALUES(12,59,'12:59','ولما جهزهم بجهازهم قال ائتونى باخ لكم من ابيكم الا ترون انى اوفى الكيل وانا۠ خير المنزلين');
INSERT INTO "uthmani_simple" VALUES(12,60,'12:60','فان لم تاتونى به فلا كيل لكم عندى ولا تقربون');
INSERT INTO "uthmani_simple" VALUES(12,61,'12:61','قالوا۟ سنرود عنه اباه وانا لفعلون');
INSERT INTO "uthmani_simple" VALUES(12,62,'12:62','وقال لفتينه اجعلوا۟ بضعتهم فى رحالهم لعلهم يعرفونها اذا انقلبوا۟ الى اهلهم لعلهم يرجعون');
INSERT INTO "uthmani_simple" VALUES(12,63,'12:63','فلما رجعوا۟ الى ابيهم قالوا۟ يابانا منع منا الكيل فارسل معنا اخانا نكتل وانا له لحفظون');
INSERT INTO "uthmani_simple" VALUES(12,64,'12:64','قال هل ءامنكم عليه الا كما امنتكم على اخيه من قبل فالله خير حفظا وهو ارحم الرحمين');
INSERT INTO "uthmani_simple" VALUES(12,65,'12:65','ولما فتحوا۟ متعهم وجدوا۟ بضعتهم ردت اليهم قالوا۟ يابانا ما نبغى هذه بضعتنا ردت الينا ونمير اهلنا ونحفظ اخانا ونزداد كيل بعير ذلك كيل يسير');
INSERT INTO "uthmani_simple" VALUES(12,66,'12:66','قال لن ارسله معكم حتى تؤتون موثقا من الله لتاتننى به الا ان يحاط بكم فلما ءاتوه موثقهم قال الله على ما نقول وكيل');
INSERT INTO "uthmani_simple" VALUES(12,67,'12:67','وقال يبنى لا تدخلوا۟ منۢ باب وحد وادخلوا۟ من ابوب متفرقة وما اغنى عنكم من الله من شىء ان الحكم الا لله عليه توكلت وعليه فليتوكل المتوكلون');
INSERT INTO "uthmani_simple" VALUES(12,68,'12:68','ولما دخلوا۟ من حيث امرهم ابوهم ما كان يغنى عنهم من الله من شىء الا حاجة فى نفس يعقوب قضىها وانه لذو علم لما علمنه ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(12,69,'12:69','ولما دخلوا۟ على يوسف ءاوى اليه اخاه قال انى انا۠ اخوك فلا تبتئس بما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(12,70,'12:70','فلما جهزهم بجهازهم جعل السقاية فى رحل اخيه ثم اذن مؤذن ايتها العير انكم لسرقون');
INSERT INTO "uthmani_simple" VALUES(12,71,'12:71','قالوا۟ واقبلوا۟ عليهم ماذا تفقدون');
INSERT INTO "uthmani_simple" VALUES(12,72,'12:72','قالوا۟ نفقد صواع الملك ولمن جاء به حمل بعير وانا۠ به زعيم');
INSERT INTO "uthmani_simple" VALUES(12,73,'12:73','قالوا۟ تالله لقد علمتم ما جئنا لنفسد فى الارض وما كنا سرقين');
INSERT INTO "uthmani_simple" VALUES(12,74,'12:74','قالوا۟ فما جزؤه ان كنتم كذبين');
INSERT INTO "uthmani_simple" VALUES(12,75,'12:75','قالوا۟ جزؤه من وجد فى رحله فهو جزؤه كذلك نجزى الظلمين');
INSERT INTO "uthmani_simple" VALUES(12,76,'12:76','فبدا باوعيتهم قبل وعاء اخيه ثم استخرجها من وعاء اخيه كذلك كدنا ليوسف ما كان لياخذ اخاه فى دين الملك الا ان يشاء الله نرفع درجت من نشاء وفوق كل ذى علم عليم');
INSERT INTO "uthmani_simple" VALUES(12,77,'12:77','قالوا۟ ان يسرق فقد سرق اخ له من قبل فاسرها يوسف فى نفسه ولم يبدها لهم قال انتم شر مكانا والله اعلم بما تصفون');
INSERT INTO "uthmani_simple" VALUES(12,78,'12:78','قالوا۟ يايها العزيز ان له ابا شيخا كبيرا فخذ احدنا مكانه انا نرىك من المحسنين');
INSERT INTO "uthmani_simple" VALUES(12,79,'12:79','قال معاذ الله ان ناخذ الا من وجدنا متعنا عنده انا اذا لظلمون');
INSERT INTO "uthmani_simple" VALUES(12,80,'12:80','فلما استيـسوا۟ منه خلصوا۟ نجيا قال كبيرهم الم تعلموا۟ ان اباكم قد اخذ عليكم موثقا من الله ومن قبل ما فرطتم فى يوسف فلن ابرح الارض حتى ياذن لى ابى او يحكم الله لى وهو خير الحكمين');
INSERT INTO "uthmani_simple" VALUES(12,81,'12:81','ارجعوا۟ الى ابيكم فقولوا۟ يابانا ان ابنك سرق وما شهدنا الا بما علمنا وما كنا للغيب حفظين');
INSERT INTO "uthmani_simple" VALUES(12,82,'12:82','وسـل القرية التى كنا فيها والعير التى اقبلنا فيها وانا لصدقون');
INSERT INTO "uthmani_simple" VALUES(12,83,'12:83','قال بل سولت لكم انفسكم امرا فصبر جميل عسى الله ان ياتينى بهم جميعا انه هو العليم الحكيم');
INSERT INTO "uthmani_simple" VALUES(12,84,'12:84','وتولى عنهم وقال ياسفى على يوسف وابيضت عيناه من الحزن فهو كظيم');
INSERT INTO "uthmani_simple" VALUES(12,85,'12:85','قالوا۟ تالله تفتؤا۟ تذكر يوسف حتى تكون حرضا او تكون من الهلكين');
INSERT INTO "uthmani_simple" VALUES(12,86,'12:86','قال انما اشكوا۟ بثى وحزنى الى الله واعلم من الله ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(12,87,'12:87','يبنى اذهبوا۟ فتحسسوا۟ من يوسف واخيه ولا تا۟يـسوا۟ من روح الله انه لا يا۟يـس من روح الله الا القوم الكفرون');
INSERT INTO "uthmani_simple" VALUES(12,88,'12:88','فلما دخلوا۟ عليه قالوا۟ يايها العزيز مسنا واهلنا الضر وجئنا ببضعة مزجىة فاوف لنا الكيل وتصدق علينا ان الله يجزى المتصدقين');
INSERT INTO "uthmani_simple" VALUES(12,89,'12:89','قال هل علمتم ما فعلتم بيوسف واخيه اذ انتم جهلون');
INSERT INTO "uthmani_simple" VALUES(12,90,'12:90','قالوا۟ اءنك لانت يوسف قال انا۠ يوسف وهذا اخى قد من الله علينا انه من يتق ويصبر فان الله لا يضيع اجر المحسنين');
INSERT INTO "uthmani_simple" VALUES(12,91,'12:91','قالوا۟ تالله لقد ءاثرك الله علينا وان كنا لخطـين');
INSERT INTO "uthmani_simple" VALUES(12,92,'12:92','قال لا تثريب عليكم اليوم يغفر الله لكم وهو ارحم الرحمين');
INSERT INTO "uthmani_simple" VALUES(12,93,'12:93','اذهبوا۟ بقميصى هذا فالقوه على وجه ابى يات بصيرا واتونى باهلكم اجمعين');
INSERT INTO "uthmani_simple" VALUES(12,94,'12:94','ولما فصلت العير قال ابوهم انى لاجد ريح يوسف لولا ان تفندون');
INSERT INTO "uthmani_simple" VALUES(12,95,'12:95','قالوا۟ تالله انك لفى ضللك القديم');
INSERT INTO "uthmani_simple" VALUES(12,96,'12:96','فلما ان جاء البشير القىه على وجهه فارتد بصيرا قال الم اقل لكم انى اعلم من الله ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(12,97,'12:97','قالوا۟ يابانا استغفر لنا ذنوبنا انا كنا خطـين');
INSERT INTO "uthmani_simple" VALUES(12,98,'12:98','قال سوف استغفر لكم ربى انه هو الغفور الرحيم');
INSERT INTO "uthmani_simple" VALUES(12,99,'12:99','فلما دخلوا۟ على يوسف ءاوى اليه ابويه وقال ادخلوا۟ مصر ان شاء الله ءامنين');
INSERT INTO "uthmani_simple" VALUES(12,100,'12:100','ورفع ابويه على العرش وخروا۟ له سجدا وقال يابت هذا تاويل رءيى من قبل قد جعلها ربى حقا وقد احسن بى اذ اخرجنى من السجن وجاء بكم من البدو منۢ بعد ان نزغ الشيطن بينى وبين اخوتى ان ربى لطيف لما يشاء انه هو العليم الحكيم');
INSERT INTO "uthmani_simple" VALUES(12,101,'12:101','رب قد ءاتيتنى من الملك وعلمتنى من تاويل الاحاديث فاطر السموت والارض انت ولى فى الدنيا والءاخرة توفنى مسلما والحقنى بالصلحين');
INSERT INTO "uthmani_simple" VALUES(12,102,'12:102','ذلك من انۢباء الغيب نوحيه اليك وما كنت لديهم اذ اجمعوا۟ امرهم وهم يمكرون');
INSERT INTO "uthmani_simple" VALUES(12,103,'12:103','وما اكثر الناس ولو حرصت بمؤمنين');
INSERT INTO "uthmani_simple" VALUES(12,104,'12:104','وما تسـلهم عليه من اجر ان هو الا ذكر للعلمين');
INSERT INTO "uthmani_simple" VALUES(12,105,'12:105','وكاين من ءاية فى السموت والارض يمرون عليها وهم عنها معرضون');
INSERT INTO "uthmani_simple" VALUES(12,106,'12:106','وما يؤمن اكثرهم بالله الا وهم مشركون');
INSERT INTO "uthmani_simple" VALUES(12,107,'12:107','افامنوا۟ ان تاتيهم غشية من عذاب الله او تاتيهم الساعة بغتة وهم لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(12,108,'12:108','قل هذه سبيلى ادعوا۟ الى الله على بصيرة انا۠ ومن اتبعنى وسبحن الله وما انا۠ من المشركين');
INSERT INTO "uthmani_simple" VALUES(12,109,'12:109','وما ارسلنا من قبلك الا رجالا نوحى اليهم من اهل القرى افلم يسيروا۟ فى الارض فينظروا۟ كيف كان عقبة الذين من قبلهم ولدار الءاخرة خير للذين اتقوا۟ افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(12,110,'12:110','حتى اذا استيـس الرسل وظنوا۟ انهم قد كذبوا۟ جاءهم نصرنا فنجى من نشاء ولا يرد باسنا عن القوم المجرمين');
INSERT INTO "uthmani_simple" VALUES(12,111,'12:111','لقد كان فى قصصهم عبرة لاو۟لى الالبب ما كان حديثا يفترى ولكن تصديق الذى بين يديه وتفصيل كل شىء وهدى ورحمة لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(13,1,'13:1','المر تلك ءايت الكتب والذى انزل اليك من ربك الحق ولكن اكثر الناس لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(13,2,'13:2','الله الذى رفع السموت بغير عمد ترونها ثم استوى على العرش وسخر الشمس والقمر كل يجرى لاجل مسمى يدبر الامر يفصل الءايت لعلكم بلقاء ربكم توقنون');
INSERT INTO "uthmani_simple" VALUES(13,3,'13:3','وهو الذى مد الارض وجعل فيها روسى وانهرا ومن كل الثمرت جعل فيها زوجين اثنين يغشى اليل النهار ان فى ذلك لءايت لقوم يتفكرون');
INSERT INTO "uthmani_simple" VALUES(13,4,'13:4','وفى الارض قطع متجورت وجنت من اعنب وزرع ونخيل صنوان وغير صنوان يسقى بماء وحد ونفضل بعضها على بعض فى الاكل ان فى ذلك لءايت لقوم يعقلون');
INSERT INTO "uthmani_simple" VALUES(13,5,'13:5','وان تعجب فعجب قولهم اءذا كنا تربا اءنا لفى خلق جديد او۟لئك الذين كفروا۟ بربهم واو۟لئك الاغلل فى اعناقهم واو۟لئك اصحب النار هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(13,6,'13:6','ويستعجلونك بالسيئة قبل الحسنة وقد خلت من قبلهم المثلت وان ربك لذو مغفرة للناس على ظلمهم وان ربك لشديد العقاب');
INSERT INTO "uthmani_simple" VALUES(13,7,'13:7','ويقول الذين كفروا۟ لولا انزل عليه ءاية من ربه انما انت منذر ولكل قوم هاد');
INSERT INTO "uthmani_simple" VALUES(13,8,'13:8','الله يعلم ما تحمل كل انثى وما تغيض الارحام وما تزداد وكل شىء عنده بمقدار');
INSERT INTO "uthmani_simple" VALUES(13,9,'13:9','علم الغيب والشهدة الكبير المتعال');
INSERT INTO "uthmani_simple" VALUES(13,10,'13:10','سواء منكم من اسر القول ومن جهر به ومن هو مستخفۭ باليل وساربۢ بالنهار');
INSERT INTO "uthmani_simple" VALUES(13,11,'13:11','له معقبت منۢ بين يديه ومن خلفه يحفظونه من امر الله ان الله لا يغير ما بقوم حتى يغيروا۟ ما بانفسهم واذا اراد الله بقوم سوءا فلا مرد له وما لهم من دونه من وال');
INSERT INTO "uthmani_simple" VALUES(13,12,'13:12','هو الذى يريكم البرق خوفا وطمعا وينشئ السحاب الثقال');
INSERT INTO "uthmani_simple" VALUES(13,13,'13:13','ويسبح الرعد بحمده والملئكة من خيفته ويرسل الصوعق فيصيب بها من يشاء وهم يجدلون فى الله وهو شديد المحال');
INSERT INTO "uthmani_simple" VALUES(13,14,'13:14','له دعوة الحق والذين يدعون من دونه لا يستجيبون لهم بشىء الا كبسط كفيه الى الماء ليبلغ فاه وما هو ببلغه وما دعاء الكفرين الا فى ضلل');
INSERT INTO "uthmani_simple" VALUES(13,15,'13:15','ولله يسجد من فى السموت والارض طوعا وكرها وظللهم بالغدو والءاصال');
INSERT INTO "uthmani_simple" VALUES(13,16,'13:16','قل من رب السموت والارض قل الله قل افاتخذتم من دونه اولياء لا يملكون لانفسهم نفعا ولا ضرا قل هل يستوى الاعمى والبصير ام هل تستوى الظلمت والنور ام جعلوا۟ لله شركاء خلقوا۟ كخلقه فتشبه الخلق عليهم قل الله خلق كل شىء وهو الوحد القهر');
INSERT INTO "uthmani_simple" VALUES(13,17,'13:17','انزل من السماء ماء فسالت اوديةۢ بقدرها فاحتمل السيل زبدا رابيا ومما يوقدون عليه فى النار ابتغاء حلية او متع زبد مثله كذلك يضرب الله الحق والبطل فاما الزبد فيذهب جفاء واما ما ينفع الناس فيمكث فى الارض كذلك يضرب الله الامثال');
INSERT INTO "uthmani_simple" VALUES(13,18,'13:18','للذين استجابوا۟ لربهم الحسنى والذين لم يستجيبوا۟ له لو ان لهم ما فى الارض جميعا ومثله معه لافتدوا۟ به او۟لئك لهم سوء الحساب وماوىهم جهنم وبئس المهاد');
INSERT INTO "uthmani_simple" VALUES(13,19,'13:19','افمن يعلم انما انزل اليك من ربك الحق كمن هو اعمى انما يتذكر او۟لوا۟ الالبب');
INSERT INTO "uthmani_simple" VALUES(13,20,'13:20','الذين يوفون بعهد الله ولا ينقضون الميثق');
INSERT INTO "uthmani_simple" VALUES(13,21,'13:21','والذين يصلون ما امر الله به ان يوصل ويخشون ربهم ويخافون سوء الحساب');
INSERT INTO "uthmani_simple" VALUES(13,22,'13:22','والذين صبروا۟ ابتغاء وجه ربهم واقاموا۟ الصلوة وانفقوا۟ مما رزقنهم سرا وعلانية ويدرءون بالحسنة السيئة او۟لئك لهم عقبى الدار');
INSERT INTO "uthmani_simple" VALUES(13,23,'13:23','جنت عدن يدخلونها ومن صلح من ءابائهم وازوجهم وذريتهم والملئكة يدخلون عليهم من كل باب');
INSERT INTO "uthmani_simple" VALUES(13,24,'13:24','سلم عليكم بما صبرتم فنعم عقبى الدار');
INSERT INTO "uthmani_simple" VALUES(13,25,'13:25','والذين ينقضون عهد الله منۢ بعد ميثقه ويقطعون ما امر الله به ان يوصل ويفسدون فى الارض او۟لئك لهم اللعنة ولهم سوء الدار');
INSERT INTO "uthmani_simple" VALUES(13,26,'13:26','الله يبسط الرزق لمن يشاء ويقدر وفرحوا۟ بالحيوة الدنيا وما الحيوة الدنيا فى الءاخرة الا متع');
INSERT INTO "uthmani_simple" VALUES(13,27,'13:27','ويقول الذين كفروا۟ لولا انزل عليه ءاية من ربه قل ان الله يضل من يشاء ويهدى اليه من اناب');
INSERT INTO "uthmani_simple" VALUES(13,28,'13:28','الذين ءامنوا۟ وتطمئن قلوبهم بذكر الله الا بذكر الله تطمئن القلوب');
INSERT INTO "uthmani_simple" VALUES(13,29,'13:29','الذين ءامنوا۟ وعملوا۟ الصلحت طوبى لهم وحسن مـاب');
INSERT INTO "uthmani_simple" VALUES(13,30,'13:30','كذلك ارسلنك فى امة قد خلت من قبلها امم لتتلوا۟ عليهم الذى اوحينا اليك وهم يكفرون بالرحمن قل هو ربى لا اله الا هو عليه توكلت واليه متاب');
INSERT INTO "uthmani_simple" VALUES(13,31,'13:31','ولو ان قرءانا سيرت به الجبال او قطعت به الارض او كلم به الموتى بل لله الامر جميعا افلم يا۟يـس الذين ءامنوا۟ ان لو يشاء الله لهدى الناس جميعا ولا يزال الذين كفروا۟ تصيبهم بما صنعوا۟ قارعة او تحل قريبا من دارهم حتى ياتى وعد الله ان الله لا يخلف الميعاد');
INSERT INTO "uthmani_simple" VALUES(13,32,'13:32','ولقد استهزئ برسل من قبلك فامليت للذين كفروا۟ ثم اخذتهم فكيف كان عقاب');
INSERT INTO "uthmani_simple" VALUES(13,33,'13:33','افمن هو قائم على كل نفسۭ بما كسبت وجعلوا۟ لله شركاء قل سموهم ام تنبـونه بما لا يعلم فى الارض ام بظهر من القول بل زين للذين كفروا۟ مكرهم وصدوا۟ عن السبيل ومن يضلل الله فما له من هاد');
INSERT INTO "uthmani_simple" VALUES(13,34,'13:34','لهم عذاب فى الحيوة الدنيا ولعذاب الءاخرة اشق وما لهم من الله من واق');
INSERT INTO "uthmani_simple" VALUES(13,35,'13:35','مثل الجنة التى وعد المتقون تجرى من تحتها الانهر اكلها دائم وظلها تلك عقبى الذين اتقوا۟ وعقبى الكفرين النار');
INSERT INTO "uthmani_simple" VALUES(13,36,'13:36','والذين ءاتينهم الكتب يفرحون بما انزل اليك ومن الاحزاب من ينكر بعضه قل انما امرت ان اعبد الله ولا اشرك به اليه ادعوا۟ واليه مـاب');
INSERT INTO "uthmani_simple" VALUES(13,37,'13:37','وكذلك انزلنه حكما عربيا ولئن اتبعت اهواءهم بعدما جاءك من العلم ما لك من الله من ولى ولا واق');
INSERT INTO "uthmani_simple" VALUES(13,38,'13:38','ولقد ارسلنا رسلا من قبلك وجعلنا لهم ازوجا وذرية وما كان لرسول ان ياتى بـاية الا باذن الله لكل اجل كتاب');
INSERT INTO "uthmani_simple" VALUES(13,39,'13:39','يمحوا۟ الله ما يشاء ويثبت وعنده ام الكتب');
INSERT INTO "uthmani_simple" VALUES(13,40,'13:40','وان ما نرينك بعض الذى نعدهم او نتوفينك فانما عليك البلغ وعلينا الحساب');
INSERT INTO "uthmani_simple" VALUES(13,41,'13:41','اولم يروا۟ انا ناتى الارض ننقصها من اطرافها والله يحكم لا معقب لحكمه وهو سريع الحساب');
INSERT INTO "uthmani_simple" VALUES(13,42,'13:42','وقد مكر الذين من قبلهم فلله المكر جميعا يعلم ما تكسب كل نفس وسيعلم الكفر لمن عقبى الدار');
INSERT INTO "uthmani_simple" VALUES(13,43,'13:43','ويقول الذين كفروا۟ لست مرسلا قل كفى بالله شهيدۢا بينى وبينكم ومن عنده علم الكتب');
INSERT INTO "uthmani_simple" VALUES(14,1,'14:1','الر كتب انزلنه اليك لتخرج الناس من الظلمت الى النور باذن ربهم الى صرط العزيز الحميد');
INSERT INTO "uthmani_simple" VALUES(14,2,'14:2','الله الذى له ما فى السموت وما فى الارض وويل للكفرين من عذاب شديد');
INSERT INTO "uthmani_simple" VALUES(14,3,'14:3','الذين يستحبون الحيوة الدنيا على الءاخرة ويصدون عن سبيل الله ويبغونها عوجا او۟لئك فى ضللۭ بعيد');
INSERT INTO "uthmani_simple" VALUES(14,4,'14:4','وما ارسلنا من رسول الا بلسان قومه ليبين لهم فيضل الله من يشاء ويهدى من يشاء وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(14,5,'14:5','ولقد ارسلنا موسى بـايتنا ان اخرج قومك من الظلمت الى النور وذكرهم بايىم الله ان فى ذلك لءايت لكل صبار شكور');
INSERT INTO "uthmani_simple" VALUES(14,6,'14:6','واذ قال موسى لقومه اذكروا۟ نعمة الله عليكم اذ انجىكم من ءال فرعون يسومونكم سوء العذاب ويذبحون ابناءكم ويستحيون نساءكم وفى ذلكم بلاء من ربكم عظيم');
INSERT INTO "uthmani_simple" VALUES(14,7,'14:7','واذ تاذن ربكم لئن شكرتم لازيدنكم ولئن كفرتم ان عذابى لشديد');
INSERT INTO "uthmani_simple" VALUES(14,8,'14:8','وقال موسى ان تكفروا۟ انتم ومن فى الارض جميعا فان الله لغنى حميد');
INSERT INTO "uthmani_simple" VALUES(14,9,'14:9','الم ياتكم نبؤا۟ الذين من قبلكم قوم نوح وعاد وثمود والذين منۢ بعدهم لا يعلمهم الا الله جاءتهم رسلهم بالبينت فردوا۟ ايديهم فى افوههم وقالوا۟ انا كفرنا بما ارسلتم به وانا لفى شك مما تدعوننا اليه مريب');
INSERT INTO "uthmani_simple" VALUES(14,10,'14:10','قالت رسلهم افى الله شك فاطر السموت والارض يدعوكم ليغفر لكم من ذنوبكم ويؤخركم الى اجل مسمى قالوا۟ ان انتم الا بشر مثلنا تريدون ان تصدونا عما كان يعبد ءاباؤنا فاتونا بسلطن مبين');
INSERT INTO "uthmani_simple" VALUES(14,11,'14:11','قالت لهم رسلهم ان نحن الا بشر مثلكم ولكن الله يمن على من يشاء من عباده وما كان لنا ان ناتيكم بسلطن الا باذن الله وعلى الله فليتوكل المؤمنون');
INSERT INTO "uthmani_simple" VALUES(14,12,'14:12','وما لنا الا نتوكل على الله وقد هدىنا سبلنا ولنصبرن على ما ءاذيتمونا وعلى الله فليتوكل المتوكلون');
INSERT INTO "uthmani_simple" VALUES(14,13,'14:13','وقال الذين كفروا۟ لرسلهم لنخرجنكم من ارضنا او لتعودن فى ملتنا فاوحى اليهم ربهم لنهلكن الظلمين');
INSERT INTO "uthmani_simple" VALUES(14,14,'14:14','ولنسكننكم الارض منۢ بعدهم ذلك لمن خاف مقامى وخاف وعيد');
INSERT INTO "uthmani_simple" VALUES(14,15,'14:15','واستفتحوا۟ وخاب كل جبار عنيد');
INSERT INTO "uthmani_simple" VALUES(14,16,'14:16','من ورائه جهنم ويسقى من ماء صديد');
INSERT INTO "uthmani_simple" VALUES(14,17,'14:17','يتجرعه ولا يكاد يسيغه وياتيه الموت من كل مكان وما هو بميت ومن ورائه عذاب غليظ');
INSERT INTO "uthmani_simple" VALUES(14,18,'14:18','مثل الذين كفروا۟ بربهم اعملهم كرماد اشتدت به الريح فى يوم عاصف لا يقدرون مما كسبوا۟ على شىء ذلك هو الضلل البعيد');
INSERT INTO "uthmani_simple" VALUES(14,19,'14:19','الم تر ان الله خلق السموت والارض بالحق ان يشا يذهبكم ويات بخلق جديد');
INSERT INTO "uthmani_simple" VALUES(14,20,'14:20','وما ذلك على الله بعزيز');
INSERT INTO "uthmani_simple" VALUES(14,21,'14:21','وبرزوا۟ لله جميعا فقال الضعفؤا۟ للذين استكبروا۟ انا كنا لكم تبعا فهل انتم مغنون عنا من عذاب الله من شىء قالوا۟ لو هدىنا الله لهدينكم سواء علينا اجزعنا ام صبرنا ما لنا من محيص');
INSERT INTO "uthmani_simple" VALUES(14,22,'14:22','وقال الشيطن لما قضى الامر ان الله وعدكم وعد الحق ووعدتكم فاخلفتكم وما كان لى عليكم من سلطن الا ان دعوتكم فاستجبتم لى فلا تلومونى ولوموا۟ انفسكم ما انا۠ بمصرخكم وما انتم بمصرخى انى كفرت بما اشركتمون من قبل ان الظلمين لهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(14,23,'14:23','وادخل الذين ءامنوا۟ وعملوا۟ الصلحت جنت تجرى من تحتها الانهر خلدين فيها باذن ربهم تحيتهم فيها سلم');
INSERT INTO "uthmani_simple" VALUES(14,24,'14:24','الم تر كيف ضرب الله مثلا كلمة طيبة كشجرة طيبة اصلها ثابت وفرعها فى السماء');
INSERT INTO "uthmani_simple" VALUES(14,25,'14:25','تؤتى اكلها كل حينۭ باذن ربها ويضرب الله الامثال للناس لعلهم يتذكرون');
INSERT INTO "uthmani_simple" VALUES(14,26,'14:26','ومثل كلمة خبيثة كشجرة خبيثة اجتثت من فوق الارض ما لها من قرار');
INSERT INTO "uthmani_simple" VALUES(14,27,'14:27','يثبت الله الذين ءامنوا۟ بالقول الثابت فى الحيوة الدنيا وفى الءاخرة ويضل الله الظلمين ويفعل الله ما يشاء');
INSERT INTO "uthmani_simple" VALUES(14,28,'14:28','الم تر الى الذين بدلوا۟ نعمت الله كفرا واحلوا۟ قومهم دار البوار');
INSERT INTO "uthmani_simple" VALUES(14,29,'14:29','جهنم يصلونها وبئس القرار');
INSERT INTO "uthmani_simple" VALUES(14,30,'14:30','وجعلوا۟ لله اندادا ليضلوا۟ عن سبيله قل تمتعوا۟ فان مصيركم الى النار');
INSERT INTO "uthmani_simple" VALUES(14,31,'14:31','قل لعبادى الذين ءامنوا۟ يقيموا۟ الصلوة وينفقوا۟ مما رزقنهم سرا وعلانية من قبل ان ياتى يوم لا بيع فيه ولا خلل');
INSERT INTO "uthmani_simple" VALUES(14,32,'14:32','الله الذى خلق السموت والارض وانزل من السماء ماء فاخرج به من الثمرت رزقا لكم وسخر لكم الفلك لتجرى فى البحر بامره وسخر لكم الانهر');
INSERT INTO "uthmani_simple" VALUES(14,33,'14:33','وسخر لكم الشمس والقمر دائبين وسخر لكم اليل والنهار');
INSERT INTO "uthmani_simple" VALUES(14,34,'14:34','وءاتىكم من كل ما سالتموه وان تعدوا۟ نعمت الله لا تحصوها ان الانسن لظلوم كفار');
INSERT INTO "uthmani_simple" VALUES(14,35,'14:35','واذ قال ابرهيم رب اجعل هذا البلد ءامنا واجنبنى وبنى ان نعبد الاصنام');
INSERT INTO "uthmani_simple" VALUES(14,36,'14:36','رب انهن اضللن كثيرا من الناس فمن تبعنى فانه منى ومن عصانى فانك غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(14,37,'14:37','ربنا انى اسكنت من ذريتى بواد غير ذى زرع عند بيتك المحرم ربنا ليقيموا۟ الصلوة فاجعل افـدة من الناس تهوى اليهم وارزقهم من الثمرت لعلهم يشكرون');
INSERT INTO "uthmani_simple" VALUES(14,38,'14:38','ربنا انك تعلم ما نخفى وما نعلن وما يخفى على الله من شىء فى الارض ولا فى السماء');
INSERT INTO "uthmani_simple" VALUES(14,39,'14:39','الحمد لله الذى وهب لى على الكبر اسمعيل واسحق ان ربى لسميع الدعاء');
INSERT INTO "uthmani_simple" VALUES(14,40,'14:40','رب اجعلنى مقيم الصلوة ومن ذريتى ربنا وتقبل دعاء');
INSERT INTO "uthmani_simple" VALUES(14,41,'14:41','ربنا اغفر لى ولولدى وللمؤمنين يوم يقوم الحساب');
INSERT INTO "uthmani_simple" VALUES(14,42,'14:42','ولا تحسبن الله غفلا عما يعمل الظلمون انما يؤخرهم ليوم تشخص فيه الابصر');
INSERT INTO "uthmani_simple" VALUES(14,43,'14:43','مهطعين مقنعى رءوسهم لا يرتد اليهم طرفهم وافـدتهم هواء');
INSERT INTO "uthmani_simple" VALUES(14,44,'14:44','وانذر الناس يوم ياتيهم العذاب فيقول الذين ظلموا۟ ربنا اخرنا الى اجل قريب نجب دعوتك ونتبع الرسل اولم تكونوا۟ اقسمتم من قبل ما لكم من زوال');
INSERT INTO "uthmani_simple" VALUES(14,45,'14:45','وسكنتم فى مسكن الذين ظلموا۟ انفسهم وتبين لكم كيف فعلنا بهم وضربنا لكم الامثال');
INSERT INTO "uthmani_simple" VALUES(14,46,'14:46','وقد مكروا۟ مكرهم وعند الله مكرهم وان كان مكرهم لتزول منه الجبال');
INSERT INTO "uthmani_simple" VALUES(14,47,'14:47','فلا تحسبن الله مخلف وعده رسله ان الله عزيز ذو انتقام');
INSERT INTO "uthmani_simple" VALUES(14,48,'14:48','يوم تبدل الارض غير الارض والسموت وبرزوا۟ لله الوحد القهار');
INSERT INTO "uthmani_simple" VALUES(14,49,'14:49','وترى المجرمين يومئذ مقرنين فى الاصفاد');
INSERT INTO "uthmani_simple" VALUES(14,50,'14:50','سرابيلهم من قطران وتغشى وجوههم النار');
INSERT INTO "uthmani_simple" VALUES(14,51,'14:51','ليجزى الله كل نفس ما كسبت ان الله سريع الحساب');
INSERT INTO "uthmani_simple" VALUES(14,52,'14:52','هذا بلغ للناس ولينذروا۟ به وليعلموا۟ انما هو اله وحد وليذكر او۟لوا۟ الالبب');
INSERT INTO "uthmani_simple" VALUES(15,1,'15:1','الر تلك ءايت الكتب وقرءان مبين');
INSERT INTO "uthmani_simple" VALUES(15,2,'15:2','ربما يود الذين كفروا۟ لو كانوا۟ مسلمين');
INSERT INTO "uthmani_simple" VALUES(15,3,'15:3','ذرهم ياكلوا۟ ويتمتعوا۟ ويلههم الامل فسوف يعلمون');
INSERT INTO "uthmani_simple" VALUES(15,4,'15:4','وما اهلكنا من قرية الا ولها كتاب معلوم');
INSERT INTO "uthmani_simple" VALUES(15,5,'15:5','ما تسبق من امة اجلها وما يستـخرون');
INSERT INTO "uthmani_simple" VALUES(15,6,'15:6','وقالوا۟ يايها الذى نزل عليه الذكر انك لمجنون');
INSERT INTO "uthmani_simple" VALUES(15,7,'15:7','لو ما تاتينا بالملئكة ان كنت من الصدقين');
INSERT INTO "uthmani_simple" VALUES(15,8,'15:8','ما ننزل الملئكة الا بالحق وما كانوا۟ اذا منظرين');
INSERT INTO "uthmani_simple" VALUES(15,9,'15:9','انا نحن نزلنا الذكر وانا له لحفظون');
INSERT INTO "uthmani_simple" VALUES(15,10,'15:10','ولقد ارسلنا من قبلك فى شيع الاولين');
INSERT INTO "uthmani_simple" VALUES(15,11,'15:11','وما ياتيهم من رسول الا كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(15,12,'15:12','كذلك نسلكه فى قلوب المجرمين');
INSERT INTO "uthmani_simple" VALUES(15,13,'15:13','لا يؤمنون به وقد خلت سنة الاولين');
INSERT INTO "uthmani_simple" VALUES(15,14,'15:14','ولو فتحنا عليهم بابا من السماء فظلوا۟ فيه يعرجون');
INSERT INTO "uthmani_simple" VALUES(15,15,'15:15','لقالوا۟ انما سكرت ابصرنا بل نحن قوم مسحورون');
INSERT INTO "uthmani_simple" VALUES(15,16,'15:16','ولقد جعلنا فى السماء بروجا وزينها للنظرين');
INSERT INTO "uthmani_simple" VALUES(15,17,'15:17','وحفظنها من كل شيطن رجيم');
INSERT INTO "uthmani_simple" VALUES(15,18,'15:18','الا من استرق السمع فاتبعه شهاب مبين');
INSERT INTO "uthmani_simple" VALUES(15,19,'15:19','والارض مددنها والقينا فيها روسى وانۢبتنا فيها من كل شىء موزون');
INSERT INTO "uthmani_simple" VALUES(15,20,'15:20','وجعلنا لكم فيها معيش ومن لستم له برزقين');
INSERT INTO "uthmani_simple" VALUES(15,21,'15:21','وان من شىء الا عندنا خزائنه وما ننزله الا بقدر معلوم');
INSERT INTO "uthmani_simple" VALUES(15,22,'15:22','وارسلنا الريح لوقح فانزلنا من السماء ماء فاسقينكموه وما انتم له بخزنين');
INSERT INTO "uthmani_simple" VALUES(15,23,'15:23','وانا لنحن نحى ونميت ونحن الورثون');
INSERT INTO "uthmani_simple" VALUES(15,24,'15:24','ولقد علمنا المستقدمين منكم ولقد علمنا المستـخرين');
INSERT INTO "uthmani_simple" VALUES(15,25,'15:25','وان ربك هو يحشرهم انه حكيم عليم');
INSERT INTO "uthmani_simple" VALUES(15,26,'15:26','ولقد خلقنا الانسن من صلصل من حما مسنون');
INSERT INTO "uthmani_simple" VALUES(15,27,'15:27','والجان خلقنه من قبل من نار السموم');
INSERT INTO "uthmani_simple" VALUES(15,28,'15:28','واذ قال ربك للملئكة انى خلقۢ بشرا من صلصل من حما مسنون');
INSERT INTO "uthmani_simple" VALUES(15,29,'15:29','فاذا سويته ونفخت فيه من روحى فقعوا۟ له سجدين');
INSERT INTO "uthmani_simple" VALUES(15,30,'15:30','فسجد الملئكة كلهم اجمعون');
INSERT INTO "uthmani_simple" VALUES(15,31,'15:31','الا ابليس ابى ان يكون مع السجدين');
INSERT INTO "uthmani_simple" VALUES(15,32,'15:32','قال يابليس ما لك الا تكون مع السجدين');
INSERT INTO "uthmani_simple" VALUES(15,33,'15:33','قال لم اكن لاسجد لبشر خلقته من صلصل من حما مسنون');
INSERT INTO "uthmani_simple" VALUES(15,34,'15:34','قال فاخرج منها فانك رجيم');
INSERT INTO "uthmani_simple" VALUES(15,35,'15:35','وان عليك اللعنة الى يوم الدين');
INSERT INTO "uthmani_simple" VALUES(15,36,'15:36','قال رب فانظرنى الى يوم يبعثون');
INSERT INTO "uthmani_simple" VALUES(15,37,'15:37','قال فانك من المنظرين');
INSERT INTO "uthmani_simple" VALUES(15,38,'15:38','الى يوم الوقت المعلوم');
INSERT INTO "uthmani_simple" VALUES(15,39,'15:39','قال رب بما اغويتنى لازينن لهم فى الارض ولاغوينهم اجمعين');
INSERT INTO "uthmani_simple" VALUES(15,40,'15:40','الا عبادك منهم المخلصين');
INSERT INTO "uthmani_simple" VALUES(15,41,'15:41','قال هذا صرط على مستقيم');
INSERT INTO "uthmani_simple" VALUES(15,42,'15:42','ان عبادى ليس لك عليهم سلطن الا من اتبعك من الغاوين');
INSERT INTO "uthmani_simple" VALUES(15,43,'15:43','وان جهنم لموعدهم اجمعين');
INSERT INTO "uthmani_simple" VALUES(15,44,'15:44','لها سبعة ابوب لكل باب منهم جزء مقسوم');
INSERT INTO "uthmani_simple" VALUES(15,45,'15:45','ان المتقين فى جنت وعيون');
INSERT INTO "uthmani_simple" VALUES(15,46,'15:46','ادخلوها بسلم ءامنين');
INSERT INTO "uthmani_simple" VALUES(15,47,'15:47','ونزعنا ما فى صدورهم من غل اخونا على سرر متقبلين');
INSERT INTO "uthmani_simple" VALUES(15,48,'15:48','لا يمسهم فيها نصب وما هم منها بمخرجين');
INSERT INTO "uthmani_simple" VALUES(15,49,'15:49','نبئ عبادى انى انا الغفور الرحيم');
INSERT INTO "uthmani_simple" VALUES(15,50,'15:50','وان عذابى هو العذاب الاليم');
INSERT INTO "uthmani_simple" VALUES(15,51,'15:51','ونبئهم عن ضيف ابرهيم');
INSERT INTO "uthmani_simple" VALUES(15,52,'15:52','اذ دخلوا۟ عليه فقالوا۟ سلما قال انا منكم وجلون');
INSERT INTO "uthmani_simple" VALUES(15,53,'15:53','قالوا۟ لا توجل انا نبشرك بغلم عليم');
INSERT INTO "uthmani_simple" VALUES(15,54,'15:54','قال ابشرتمونى على ان مسنى الكبر فبم تبشرون');
INSERT INTO "uthmani_simple" VALUES(15,55,'15:55','قالوا۟ بشرنك بالحق فلا تكن من القنطين');
INSERT INTO "uthmani_simple" VALUES(15,56,'15:56','قال ومن يقنط من رحمة ربه الا الضالون');
INSERT INTO "uthmani_simple" VALUES(15,57,'15:57','قال فما خطبكم ايها المرسلون');
INSERT INTO "uthmani_simple" VALUES(15,58,'15:58','قالوا۟ انا ارسلنا الى قوم مجرمين');
INSERT INTO "uthmani_simple" VALUES(15,59,'15:59','الا ءال لوط انا لمنجوهم اجمعين');
INSERT INTO "uthmani_simple" VALUES(15,60,'15:60','الا امراته قدرنا انها لمن الغبرين');
INSERT INTO "uthmani_simple" VALUES(15,61,'15:61','فلما جاء ءال لوط المرسلون');
INSERT INTO "uthmani_simple" VALUES(15,62,'15:62','قال انكم قوم منكرون');
INSERT INTO "uthmani_simple" VALUES(15,63,'15:63','قالوا۟ بل جئنك بما كانوا۟ فيه يمترون');
INSERT INTO "uthmani_simple" VALUES(15,64,'15:64','واتينك بالحق وانا لصدقون');
INSERT INTO "uthmani_simple" VALUES(15,65,'15:65','فاسر باهلك بقطع من اليل واتبع ادبرهم ولا يلتفت منكم احد وامضوا۟ حيث تؤمرون');
INSERT INTO "uthmani_simple" VALUES(15,66,'15:66','وقضينا اليه ذلك الامر ان دابر هؤلاء مقطوع مصبحين');
INSERT INTO "uthmani_simple" VALUES(15,67,'15:67','وجاء اهل المدينة يستبشرون');
INSERT INTO "uthmani_simple" VALUES(15,68,'15:68','قال ان هؤلاء ضيفى فلا تفضحون');
INSERT INTO "uthmani_simple" VALUES(15,69,'15:69','واتقوا۟ الله ولا تخزون');
INSERT INTO "uthmani_simple" VALUES(15,70,'15:70','قالوا۟ اولم ننهك عن العلمين');
INSERT INTO "uthmani_simple" VALUES(15,71,'15:71','قال هؤلاء بناتى ان كنتم فعلين');
INSERT INTO "uthmani_simple" VALUES(15,72,'15:72','لعمرك انهم لفى سكرتهم يعمهون');
INSERT INTO "uthmani_simple" VALUES(15,73,'15:73','فاخذتهم الصيحة مشرقين');
INSERT INTO "uthmani_simple" VALUES(15,74,'15:74','فجعلنا عليها سافلها وامطرنا عليهم حجارة من سجيل');
INSERT INTO "uthmani_simple" VALUES(15,75,'15:75','ان فى ذلك لءايت للمتوسمين');
INSERT INTO "uthmani_simple" VALUES(15,76,'15:76','وانها لبسبيل مقيم');
INSERT INTO "uthmani_simple" VALUES(15,77,'15:77','ان فى ذلك لءاية للمؤمنين');
INSERT INTO "uthmani_simple" VALUES(15,78,'15:78','وان كان اصحب الايكة لظلمين');
INSERT INTO "uthmani_simple" VALUES(15,79,'15:79','فانتقمنا منهم وانهما لبامام مبين');
INSERT INTO "uthmani_simple" VALUES(15,80,'15:80','ولقد كذب اصحب الحجر المرسلين');
INSERT INTO "uthmani_simple" VALUES(15,81,'15:81','وءاتينهم ءايتنا فكانوا۟ عنها معرضين');
INSERT INTO "uthmani_simple" VALUES(15,82,'15:82','وكانوا۟ ينحتون من الجبال بيوتا ءامنين');
INSERT INTO "uthmani_simple" VALUES(15,83,'15:83','فاخذتهم الصيحة مصبحين');
INSERT INTO "uthmani_simple" VALUES(15,84,'15:84','فما اغنى عنهم ما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(15,85,'15:85','وما خلقنا السموت والارض وما بينهما الا بالحق وان الساعة لءاتية فاصفح الصفح الجميل');
INSERT INTO "uthmani_simple" VALUES(15,86,'15:86','ان ربك هو الخلق العليم');
INSERT INTO "uthmani_simple" VALUES(15,87,'15:87','ولقد ءاتينك سبعا من المثانى والقرءان العظيم');
INSERT INTO "uthmani_simple" VALUES(15,88,'15:88','لا تمدن عينيك الى ما متعنا به ازوجا منهم ولا تحزن عليهم واخفض جناحك للمؤمنين');
INSERT INTO "uthmani_simple" VALUES(15,89,'15:89','وقل انى انا النذير المبين');
INSERT INTO "uthmani_simple" VALUES(15,90,'15:90','كما انزلنا على المقتسمين');
INSERT INTO "uthmani_simple" VALUES(15,91,'15:91','الذين جعلوا۟ القرءان عضين');
INSERT INTO "uthmani_simple" VALUES(15,92,'15:92','فوربك لنسـلنهم اجمعين');
INSERT INTO "uthmani_simple" VALUES(15,93,'15:93','عما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(15,94,'15:94','فاصدع بما تؤمر واعرض عن المشركين');
INSERT INTO "uthmani_simple" VALUES(15,95,'15:95','انا كفينك المستهزءين');
INSERT INTO "uthmani_simple" VALUES(15,96,'15:96','الذين يجعلون مع الله الها ءاخر فسوف يعلمون');
INSERT INTO "uthmani_simple" VALUES(15,97,'15:97','ولقد نعلم انك يضيق صدرك بما يقولون');
INSERT INTO "uthmani_simple" VALUES(15,98,'15:98','فسبح بحمد ربك وكن من السجدين');
INSERT INTO "uthmani_simple" VALUES(15,99,'15:99','واعبد ربك حتى ياتيك اليقين');
INSERT INTO "uthmani_simple" VALUES(16,1,'16:1','اتى امر الله فلا تستعجلوه سبحنه وتعلى عما يشركون');
INSERT INTO "uthmani_simple" VALUES(16,2,'16:2','ينزل الملئكة بالروح من امره على من يشاء من عباده ان انذروا۟ انه لا اله الا انا۠ فاتقون');
INSERT INTO "uthmani_simple" VALUES(16,3,'16:3','خلق السموت والارض بالحق تعلى عما يشركون');
INSERT INTO "uthmani_simple" VALUES(16,4,'16:4','خلق الانسن من نطفة فاذا هو خصيم مبين');
INSERT INTO "uthmani_simple" VALUES(16,5,'16:5','والانعم خلقها لكم فيها دفء ومنفع ومنها تاكلون');
INSERT INTO "uthmani_simple" VALUES(16,6,'16:6','ولكم فيها جمال حين تريحون وحين تسرحون');
INSERT INTO "uthmani_simple" VALUES(16,7,'16:7','وتحمل اثقالكم الى بلد لم تكونوا۟ بلغيه الا بشق الانفس ان ربكم لرءوف رحيم');
INSERT INTO "uthmani_simple" VALUES(16,8,'16:8','والخيل والبغال والحمير لتركبوها وزينة ويخلق ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(16,9,'16:9','وعلى الله قصد السبيل ومنها جائر ولو شاء لهدىكم اجمعين');
INSERT INTO "uthmani_simple" VALUES(16,10,'16:10','هو الذى انزل من السماء ماء لكم منه شراب ومنه شجر فيه تسيمون');
INSERT INTO "uthmani_simple" VALUES(16,11,'16:11','ينۢبت لكم به الزرع والزيتون والنخيل والاعنب ومن كل الثمرت ان فى ذلك لءاية لقوم يتفكرون');
INSERT INTO "uthmani_simple" VALUES(16,12,'16:12','وسخر لكم اليل والنهار والشمس والقمر والنجوم مسخرتۢ بامره ان فى ذلك لءايت لقوم يعقلون');
INSERT INTO "uthmani_simple" VALUES(16,13,'16:13','وما ذرا لكم فى الارض مختلفا الونه ان فى ذلك لءاية لقوم يذكرون');
INSERT INTO "uthmani_simple" VALUES(16,14,'16:14','وهو الذى سخر البحر لتاكلوا۟ منه لحما طريا وتستخرجوا۟ منه حلية تلبسونها وترى الفلك مواخر فيه ولتبتغوا۟ من فضله ولعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(16,15,'16:15','والقى فى الارض روسى ان تميد بكم وانهرا وسبلا لعلكم تهتدون');
INSERT INTO "uthmani_simple" VALUES(16,16,'16:16','وعلمت وبالنجم هم يهتدون');
INSERT INTO "uthmani_simple" VALUES(16,17,'16:17','افمن يخلق كمن لا يخلق افلا تذكرون');
INSERT INTO "uthmani_simple" VALUES(16,18,'16:18','وان تعدوا۟ نعمة الله لا تحصوها ان الله لغفور رحيم');
INSERT INTO "uthmani_simple" VALUES(16,19,'16:19','والله يعلم ما تسرون وما تعلنون');
INSERT INTO "uthmani_simple" VALUES(16,20,'16:20','والذين يدعون من دون الله لا يخلقون شيـا وهم يخلقون');
INSERT INTO "uthmani_simple" VALUES(16,21,'16:21','اموت غير احياء وما يشعرون ايان يبعثون');
INSERT INTO "uthmani_simple" VALUES(16,22,'16:22','الهكم اله وحد فالذين لا يؤمنون بالءاخرة قلوبهم منكرة وهم مستكبرون');
INSERT INTO "uthmani_simple" VALUES(16,23,'16:23','لا جرم ان الله يعلم ما يسرون وما يعلنون انه لا يحب المستكبرين');
INSERT INTO "uthmani_simple" VALUES(16,24,'16:24','واذا قيل لهم ماذا انزل ربكم قالوا۟ اسطير الاولين');
INSERT INTO "uthmani_simple" VALUES(16,25,'16:25','ليحملوا۟ اوزارهم كاملة يوم القيمة ومن اوزار الذين يضلونهم بغير علم الا ساء ما يزرون');
INSERT INTO "uthmani_simple" VALUES(16,26,'16:26','قد مكر الذين من قبلهم فاتى الله بنينهم من القواعد فخر عليهم السقف من فوقهم واتىهم العذاب من حيث لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(16,27,'16:27','ثم يوم القيمة يخزيهم ويقول اين شركاءى الذين كنتم تشقون فيهم قال الذين اوتوا۟ العلم ان الخزى اليوم والسوء على الكفرين');
INSERT INTO "uthmani_simple" VALUES(16,28,'16:28','الذين تتوفىهم الملئكة ظالمى انفسهم فالقوا۟ السلم ما كنا نعمل من سوءۭ بلى ان الله عليمۢ بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(16,29,'16:29','فادخلوا۟ ابوب جهنم خلدين فيها فلبئس مثوى المتكبرين');
INSERT INTO "uthmani_simple" VALUES(16,30,'16:30','وقيل للذين اتقوا۟ ماذا انزل ربكم قالوا۟ خيرا للذين احسنوا۟ فى هذه الدنيا حسنة ولدار الءاخرة خير ولنعم دار المتقين');
INSERT INTO "uthmani_simple" VALUES(16,31,'16:31','جنت عدن يدخلونها تجرى من تحتها الانهر لهم فيها ما يشاءون كذلك يجزى الله المتقين');
INSERT INTO "uthmani_simple" VALUES(16,32,'16:32','الذين تتوفىهم الملئكة طيبين يقولون سلم عليكم ادخلوا۟ الجنة بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(16,33,'16:33','هل ينظرون الا ان تاتيهم الملئكة او ياتى امر ربك كذلك فعل الذين من قبلهم وما ظلمهم الله ولكن كانوا۟ انفسهم يظلمون');
INSERT INTO "uthmani_simple" VALUES(16,34,'16:34','فاصابهم سيـات ما عملوا۟ وحاق بهم ما كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(16,35,'16:35','وقال الذين اشركوا۟ لو شاء الله ما عبدنا من دونه من شىء نحن ولا ءاباؤنا ولا حرمنا من دونه من شىء كذلك فعل الذين من قبلهم فهل على الرسل الا البلغ المبين');
INSERT INTO "uthmani_simple" VALUES(16,36,'16:36','ولقد بعثنا فى كل امة رسولا ان اعبدوا۟ الله واجتنبوا۟ الطغوت فمنهم من هدى الله ومنهم من حقت عليه الضللة فسيروا۟ فى الارض فانظروا۟ كيف كان عقبة المكذبين');
INSERT INTO "uthmani_simple" VALUES(16,37,'16:37','ان تحرص على هدىهم فان الله لا يهدى من يضل وما لهم من نصرين');
INSERT INTO "uthmani_simple" VALUES(16,38,'16:38','واقسموا۟ بالله جهد ايمنهم لا يبعث الله من يموت بلى وعدا عليه حقا ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(16,39,'16:39','ليبين لهم الذى يختلفون فيه وليعلم الذين كفروا۟ انهم كانوا۟ كذبين');
INSERT INTO "uthmani_simple" VALUES(16,40,'16:40','انما قولنا لشىء اذا اردنه ان نقول له كن فيكون');
INSERT INTO "uthmani_simple" VALUES(16,41,'16:41','والذين هاجروا۟ فى الله منۢ بعد ما ظلموا۟ لنبوئنهم فى الدنيا حسنة ولاجر الءاخرة اكبر لو كانوا۟ يعلمون');
INSERT INTO "uthmani_simple" VALUES(16,42,'16:42','الذين صبروا۟ وعلى ربهم يتوكلون');
INSERT INTO "uthmani_simple" VALUES(16,43,'16:43','وما ارسلنا من قبلك الا رجالا نوحى اليهم فسـلوا۟ اهل الذكر ان كنتم لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(16,44,'16:44','بالبينت والزبر وانزلنا اليك الذكر لتبين للناس ما نزل اليهم ولعلهم يتفكرون');
INSERT INTO "uthmani_simple" VALUES(16,45,'16:45','افامن الذين مكروا۟ السيـات ان يخسف الله بهم الارض او ياتيهم العذاب من حيث لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(16,46,'16:46','او ياخذهم فى تقلبهم فما هم بمعجزين');
INSERT INTO "uthmani_simple" VALUES(16,47,'16:47','او ياخذهم على تخوف فان ربكم لرءوف رحيم');
INSERT INTO "uthmani_simple" VALUES(16,48,'16:48','اولم يروا۟ الى ما خلق الله من شىء يتفيؤا۟ ظلله عن اليمين والشمائل سجدا لله وهم دخرون');
INSERT INTO "uthmani_simple" VALUES(16,49,'16:49','ولله يسجد ما فى السموت وما فى الارض من دابة والملئكة وهم لا يستكبرون');
INSERT INTO "uthmani_simple" VALUES(16,50,'16:50','يخافون ربهم من فوقهم ويفعلون ما يؤمرون');
INSERT INTO "uthmani_simple" VALUES(16,51,'16:51','وقال الله لا تتخذوا۟ الهين اثنين انما هو اله وحد فايى فارهبون');
INSERT INTO "uthmani_simple" VALUES(16,52,'16:52','وله ما فى السموت والارض وله الدين واصبا افغير الله تتقون');
INSERT INTO "uthmani_simple" VALUES(16,53,'16:53','وما بكم من نعمة فمن الله ثم اذا مسكم الضر فاليه تجـرون');
INSERT INTO "uthmani_simple" VALUES(16,54,'16:54','ثم اذا كشف الضر عنكم اذا فريق منكم بربهم يشركون');
INSERT INTO "uthmani_simple" VALUES(16,55,'16:55','ليكفروا۟ بما ءاتينهم فتمتعوا۟ فسوف تعلمون');
INSERT INTO "uthmani_simple" VALUES(16,56,'16:56','ويجعلون لما لا يعلمون نصيبا مما رزقنهم تالله لتسـلن عما كنتم تفترون');
INSERT INTO "uthmani_simple" VALUES(16,57,'16:57','ويجعلون لله البنت سبحنه ولهم ما يشتهون');
INSERT INTO "uthmani_simple" VALUES(16,58,'16:58','واذا بشر احدهم بالانثى ظل وجهه مسودا وهو كظيم');
INSERT INTO "uthmani_simple" VALUES(16,59,'16:59','يتورى من القوم من سوء ما بشر به ايمسكه على هون ام يدسه فى التراب الا ساء ما يحكمون');
INSERT INTO "uthmani_simple" VALUES(16,60,'16:60','للذين لا يؤمنون بالءاخرة مثل السوء ولله المثل الاعلى وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(16,61,'16:61','ولو يؤاخذ الله الناس بظلمهم ما ترك عليها من دابة ولكن يؤخرهم الى اجل مسمى فاذا جاء اجلهم لا يستـخرون ساعة ولا يستقدمون');
INSERT INTO "uthmani_simple" VALUES(16,62,'16:62','ويجعلون لله ما يكرهون وتصف السنتهم الكذب ان لهم الحسنى لا جرم ان لهم النار وانهم مفرطون');
INSERT INTO "uthmani_simple" VALUES(16,63,'16:63','تالله لقد ارسلنا الى امم من قبلك فزين لهم الشيطن اعملهم فهو وليهم اليوم ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(16,64,'16:64','وما انزلنا عليك الكتب الا لتبين لهم الذى اختلفوا۟ فيه وهدى ورحمة لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(16,65,'16:65','والله انزل من السماء ماء فاحيا به الارض بعد موتها ان فى ذلك لءاية لقوم يسمعون');
INSERT INTO "uthmani_simple" VALUES(16,66,'16:66','وان لكم فى الانعم لعبرة نسقيكم مما فى بطونه منۢ بين فرث ودم لبنا خالصا سائغا للشربين');
INSERT INTO "uthmani_simple" VALUES(16,67,'16:67','ومن ثمرت النخيل والاعنب تتخذون منه سكرا ورزقا حسنا ان فى ذلك لءاية لقوم يعقلون');
INSERT INTO "uthmani_simple" VALUES(16,68,'16:68','واوحى ربك الى النحل ان اتخذى من الجبال بيوتا ومن الشجر ومما يعرشون');
INSERT INTO "uthmani_simple" VALUES(16,69,'16:69','ثم كلى من كل الثمرت فاسلكى سبل ربك ذللا يخرج منۢ بطونها شراب مختلف الونه فيه شفاء للناس ان فى ذلك لءاية لقوم يتفكرون');
INSERT INTO "uthmani_simple" VALUES(16,70,'16:70','والله خلقكم ثم يتوفىكم ومنكم من يرد الى ارذل العمر لكى لا يعلم بعد علم شيـا ان الله عليم قدير');
INSERT INTO "uthmani_simple" VALUES(16,71,'16:71','والله فضل بعضكم على بعض فى الرزق فما الذين فضلوا۟ برادى رزقهم على ما ملكت ايمنهم فهم فيه سواء افبنعمة الله يجحدون');
INSERT INTO "uthmani_simple" VALUES(16,72,'16:72','والله جعل لكم من انفسكم ازوجا وجعل لكم من ازوجكم بنين وحفدة ورزقكم من الطيبت افبالبطل يؤمنون وبنعمت الله هم يكفرون');
INSERT INTO "uthmani_simple" VALUES(16,73,'16:73','ويعبدون من دون الله ما لا يملك لهم رزقا من السموت والارض شيـا ولا يستطيعون');
INSERT INTO "uthmani_simple" VALUES(16,74,'16:74','فلا تضربوا۟ لله الامثال ان الله يعلم وانتم لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(16,75,'16:75','ضرب الله مثلا عبدا مملوكا لا يقدر على شىء ومن رزقنه منا رزقا حسنا فهو ينفق منه سرا وجهرا هل يستون الحمد لله بل اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(16,76,'16:76','وضرب الله مثلا رجلين احدهما ابكم لا يقدر على شىء وهو كل على مولىه اينما يوجهه لا يات بخير هل يستوى هو ومن يامر بالعدل وهو على صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(16,77,'16:77','ولله غيب السموت والارض وما امر الساعة الا كلمح البصر او هو اقرب ان الله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(16,78,'16:78','والله اخرجكم منۢ بطون امهتكم لا تعلمون شيـا وجعل لكم السمع والابصر والافـدة لعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(16,79,'16:79','الم يروا۟ الى الطير مسخرت فى جو السماء ما يمسكهن الا الله ان فى ذلك لءايت لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(16,80,'16:80','والله جعل لكم منۢ بيوتكم سكنا وجعل لكم من جلود الانعم بيوتا تستخفونها يوم ظعنكم ويوم اقامتكم ومن اصوافها واوبارها واشعارها اثثا ومتعا الى حين');
INSERT INTO "uthmani_simple" VALUES(16,81,'16:81','والله جعل لكم مما خلق ظللا وجعل لكم من الجبال اكننا وجعل لكم سربيل تقيكم الحر وسربيل تقيكم باسكم كذلك يتم نعمته عليكم لعلكم تسلمون');
INSERT INTO "uthmani_simple" VALUES(16,82,'16:82','فان تولوا۟ فانما عليك البلغ المبين');
INSERT INTO "uthmani_simple" VALUES(16,83,'16:83','يعرفون نعمت الله ثم ينكرونها واكثرهم الكفرون');
INSERT INTO "uthmani_simple" VALUES(16,84,'16:84','ويوم نبعث من كل امة شهيدا ثم لا يؤذن للذين كفروا۟ ولا هم يستعتبون');
INSERT INTO "uthmani_simple" VALUES(16,85,'16:85','واذا رءا الذين ظلموا۟ العذاب فلا يخفف عنهم ولا هم ينظرون');
INSERT INTO "uthmani_simple" VALUES(16,86,'16:86','واذا رءا الذين اشركوا۟ شركاءهم قالوا۟ ربنا هؤلاء شركاؤنا الذين كنا ندعوا۟ من دونك فالقوا۟ اليهم القول انكم لكذبون');
INSERT INTO "uthmani_simple" VALUES(16,87,'16:87','والقوا۟ الى الله يومئذ السلم وضل عنهم ما كانوا۟ يفترون');
INSERT INTO "uthmani_simple" VALUES(16,88,'16:88','الذين كفروا۟ وصدوا۟ عن سبيل الله زدنهم عذابا فوق العذاب بما كانوا۟ يفسدون');
INSERT INTO "uthmani_simple" VALUES(16,89,'16:89','ويوم نبعث فى كل امة شهيدا عليهم من انفسهم وجئنا بك شهيدا على هؤلاء ونزلنا عليك الكتب تبينا لكل شىء وهدى ورحمة وبشرى للمسلمين');
INSERT INTO "uthmani_simple" VALUES(16,90,'16:90','ان الله يامر بالعدل والاحسن وايتائ ذى القربى وينهى عن الفحشاء والمنكر والبغى يعظكم لعلكم تذكرون');
INSERT INTO "uthmani_simple" VALUES(16,91,'16:91','واوفوا۟ بعهد الله اذا عهدتم ولا تنقضوا۟ الايمن بعد توكيدها وقد جعلتم الله عليكم كفيلا ان الله يعلم ما تفعلون');
INSERT INTO "uthmani_simple" VALUES(16,92,'16:92','ولا تكونوا۟ كالتى نقضت غزلها منۢ بعد قوة انكثا تتخذون ايمنكم دخلۢا بينكم ان تكون امة هى اربى من امة انما يبلوكم الله به وليبينن لكم يوم القيمة ما كنتم فيه تختلفون');
INSERT INTO "uthmani_simple" VALUES(16,93,'16:93','ولو شاء الله لجعلكم امة وحدة ولكن يضل من يشاء ويهدى من يشاء ولتسـلن عما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(16,94,'16:94','ولا تتخذوا۟ ايمنكم دخلۢا بينكم فتزل قدمۢ بعد ثبوتها وتذوقوا۟ السوء بما صددتم عن سبيل الله ولكم عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(16,95,'16:95','ولا تشتروا۟ بعهد الله ثمنا قليلا انما عند الله هو خير لكم ان كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(16,96,'16:96','ما عندكم ينفد وما عند الله باق ولنجزين الذين صبروا۟ اجرهم باحسن ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(16,97,'16:97','من عمل صلحا من ذكر او انثى وهو مؤمن فلنحيينه حيوة طيبة ولنجزينهم اجرهم باحسن ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(16,98,'16:98','فاذا قرات القرءان فاستعذ بالله من الشيطن الرجيم');
INSERT INTO "uthmani_simple" VALUES(16,99,'16:99','انه ليس له سلطن على الذين ءامنوا۟ وعلى ربهم يتوكلون');
INSERT INTO "uthmani_simple" VALUES(16,100,'16:100','انما سلطنه على الذين يتولونه والذين هم به مشركون');
INSERT INTO "uthmani_simple" VALUES(16,101,'16:101','واذا بدلنا ءاية مكان ءاية والله اعلم بما ينزل قالوا۟ انما انت مفترۭ بل اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(16,102,'16:102','قل نزله روح القدس من ربك بالحق ليثبت الذين ءامنوا۟ وهدى وبشرى للمسلمين');
INSERT INTO "uthmani_simple" VALUES(16,103,'16:103','ولقد نعلم انهم يقولون انما يعلمه بشر لسان الذى يلحدون اليه اعجمى وهذا لسان عربى مبين');
INSERT INTO "uthmani_simple" VALUES(16,104,'16:104','ان الذين لا يؤمنون بـايت الله لا يهديهم الله ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(16,105,'16:105','انما يفترى الكذب الذين لا يؤمنون بـايت الله واو۟لئك هم الكذبون');
INSERT INTO "uthmani_simple" VALUES(16,106,'16:106','من كفر بالله منۢ بعد ايمنه الا من اكره وقلبه مطمئنۢ بالايمن ولكن من شرح بالكفر صدرا فعليهم غضب من الله ولهم عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(16,107,'16:107','ذلك بانهم استحبوا۟ الحيوة الدنيا على الءاخرة وان الله لا يهدى القوم الكفرين');
INSERT INTO "uthmani_simple" VALUES(16,108,'16:108','او۟لئك الذين طبع الله على قلوبهم وسمعهم وابصرهم واو۟لئك هم الغفلون');
INSERT INTO "uthmani_simple" VALUES(16,109,'16:109','لا جرم انهم فى الءاخرة هم الخسرون');
INSERT INTO "uthmani_simple" VALUES(16,110,'16:110','ثم ان ربك للذين هاجروا۟ منۢ بعد ما فتنوا۟ ثم جهدوا۟ وصبروا۟ ان ربك منۢ بعدها لغفور رحيم');
INSERT INTO "uthmani_simple" VALUES(16,111,'16:111','يوم تاتى كل نفس تجدل عن نفسها وتوفى كل نفس ما عملت وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(16,112,'16:112','وضرب الله مثلا قرية كانت ءامنة مطمئنة ياتيها رزقها رغدا من كل مكان فكفرت بانعم الله فاذقها الله لباس الجوع والخوف بما كانوا۟ يصنعون');
INSERT INTO "uthmani_simple" VALUES(16,113,'16:113','ولقد جاءهم رسول منهم فكذبوه فاخذهم العذاب وهم ظلمون');
INSERT INTO "uthmani_simple" VALUES(16,114,'16:114','فكلوا۟ مما رزقكم الله حللا طيبا واشكروا۟ نعمت الله ان كنتم اياه تعبدون');
INSERT INTO "uthmani_simple" VALUES(16,115,'16:115','انما حرم عليكم الميتة والدم ولحم الخنزير وما اهل لغير الله به فمن اضطر غير باغ ولا عاد فان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(16,116,'16:116','ولا تقولوا۟ لما تصف السنتكم الكذب هذا حلل وهذا حرام لتفتروا۟ على الله الكذب ان الذين يفترون على الله الكذب لا يفلحون');
INSERT INTO "uthmani_simple" VALUES(16,117,'16:117','متع قليل ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(16,118,'16:118','وعلى الذين هادوا۟ حرمنا ما قصصنا عليك من قبل وما ظلمنهم ولكن كانوا۟ انفسهم يظلمون');
INSERT INTO "uthmani_simple" VALUES(16,119,'16:119','ثم ان ربك للذين عملوا۟ السوء بجهلة ثم تابوا۟ منۢ بعد ذلك واصلحوا۟ ان ربك منۢ بعدها لغفور رحيم');
INSERT INTO "uthmani_simple" VALUES(16,120,'16:120','ان ابرهيم كان امة قانتا لله حنيفا ولم يك من المشركين');
INSERT INTO "uthmani_simple" VALUES(16,121,'16:121','شاكرا لانعمه اجتبىه وهدىه الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(16,122,'16:122','وءاتينه فى الدنيا حسنة وانه فى الءاخرة لمن الصلحين');
INSERT INTO "uthmani_simple" VALUES(16,123,'16:123','ثم اوحينا اليك ان اتبع ملة ابرهيم حنيفا وما كان من المشركين');
INSERT INTO "uthmani_simple" VALUES(16,124,'16:124','انما جعل السبت على الذين اختلفوا۟ فيه وان ربك ليحكم بينهم يوم القيمة فيما كانوا۟ فيه يختلفون');
INSERT INTO "uthmani_simple" VALUES(16,125,'16:125','ادع الى سبيل ربك بالحكمة والموعظة الحسنة وجدلهم بالتى هى احسن ان ربك هو اعلم بمن ضل عن سبيله وهو اعلم بالمهتدين');
INSERT INTO "uthmani_simple" VALUES(16,126,'16:126','وان عاقبتم فعاقبوا۟ بمثل ما عوقبتم به ولئن صبرتم لهو خير للصبرين');
INSERT INTO "uthmani_simple" VALUES(16,127,'16:127','واصبر وما صبرك الا بالله ولا تحزن عليهم ولا تك فى ضيق مما يمكرون');
INSERT INTO "uthmani_simple" VALUES(16,128,'16:128','ان الله مع الذين اتقوا۟ والذين هم محسنون');
INSERT INTO "uthmani_simple" VALUES(17,1,'17:1','سبحن الذى اسرى بعبده ليلا من المسجد الحرام الى المسجد الاقصا الذى بركنا حوله لنريه من ءايتنا انه هو السميع البصير');
INSERT INTO "uthmani_simple" VALUES(17,2,'17:2','وءاتينا موسى الكتب وجعلنه هدى لبنى اسرءيل الا تتخذوا۟ من دونى وكيلا');
INSERT INTO "uthmani_simple" VALUES(17,3,'17:3','ذرية من حملنا مع نوح انه كان عبدا شكورا');
INSERT INTO "uthmani_simple" VALUES(17,4,'17:4','وقضينا الى بنى اسرءيل فى الكتب لتفسدن فى الارض مرتين ولتعلن علوا كبيرا');
INSERT INTO "uthmani_simple" VALUES(17,5,'17:5','فاذا جاء وعد اولىهما بعثنا عليكم عبادا لنا او۟لى باس شديد فجاسوا۟ خلل الديار وكان وعدا مفعولا');
INSERT INTO "uthmani_simple" VALUES(17,6,'17:6','ثم رددنا لكم الكرة عليهم وامددنكم بامول وبنين وجعلنكم اكثر نفيرا');
INSERT INTO "uthmani_simple" VALUES(17,7,'17:7','ان احسنتم احسنتم لانفسكم وان اساتم فلها فاذا جاء وعد الءاخرة ليسـوا۟ وجوهكم وليدخلوا۟ المسجد كما دخلوه اول مرة وليتبروا۟ ما علوا۟ تتبيرا');
INSERT INTO "uthmani_simple" VALUES(17,8,'17:8','عسى ربكم ان يرحمكم وان عدتم عدنا وجعلنا جهنم للكفرين حصيرا');
INSERT INTO "uthmani_simple" VALUES(17,9,'17:9','ان هذا القرءان يهدى للتى هى اقوم ويبشر المؤمنين الذين يعملون الصلحت ان لهم اجرا كبيرا');
INSERT INTO "uthmani_simple" VALUES(17,10,'17:10','وان الذين لا يؤمنون بالءاخرة اعتدنا لهم عذابا اليما');
INSERT INTO "uthmani_simple" VALUES(17,11,'17:11','ويدع الانسن بالشر دعاءه بالخير وكان الانسن عجولا');
INSERT INTO "uthmani_simple" VALUES(17,12,'17:12','وجعلنا اليل والنهار ءايتين فمحونا ءاية اليل وجعلنا ءاية النهار مبصرة لتبتغوا۟ فضلا من ربكم ولتعلموا۟ عدد السنين والحساب وكل شىء فصلنه تفصيلا');
INSERT INTO "uthmani_simple" VALUES(17,13,'17:13','وكل انسن الزمنه طئره فى عنقه ونخرج له يوم القيمة كتبا يلقىه منشورا');
INSERT INTO "uthmani_simple" VALUES(17,14,'17:14','اقرا كتبك كفى بنفسك اليوم عليك حسيبا');
INSERT INTO "uthmani_simple" VALUES(17,15,'17:15','من اهتدى فانما يهتدى لنفسه ومن ضل فانما يضل عليها ولا تزر وازرة وزر اخرى وما كنا معذبين حتى نبعث رسولا');
INSERT INTO "uthmani_simple" VALUES(17,16,'17:16','واذا اردنا ان نهلك قرية امرنا مترفيها ففسقوا۟ فيها فحق عليها القول فدمرنها تدميرا');
INSERT INTO "uthmani_simple" VALUES(17,17,'17:17','وكم اهلكنا من القرون منۢ بعد نوح وكفى بربك بذنوب عباده خبيرۢا بصيرا');
INSERT INTO "uthmani_simple" VALUES(17,18,'17:18','من كان يريد العاجلة عجلنا له فيها ما نشاء لمن نريد ثم جعلنا له جهنم يصلىها مذموما مدحورا');
INSERT INTO "uthmani_simple" VALUES(17,19,'17:19','ومن اراد الءاخرة وسعى لها سعيها وهو مؤمن فاو۟لئك كان سعيهم مشكورا');
INSERT INTO "uthmani_simple" VALUES(17,20,'17:20','كلا نمد هؤلاء وهؤلاء من عطاء ربك وما كان عطاء ربك محظورا');
INSERT INTO "uthmani_simple" VALUES(17,21,'17:21','انظر كيف فضلنا بعضهم على بعض وللءاخرة اكبر درجت واكبر تفضيلا');
INSERT INTO "uthmani_simple" VALUES(17,22,'17:22','لا تجعل مع الله الها ءاخر فتقعد مذموما مخذولا');
INSERT INTO "uthmani_simple" VALUES(17,23,'17:23','وقضى ربك الا تعبدوا۟ الا اياه وبالولدين احسنا اما يبلغن عندك الكبر احدهما او كلاهما فلا تقل لهما اف ولا تنهرهما وقل لهما قولا كريما');
INSERT INTO "uthmani_simple" VALUES(17,24,'17:24','واخفض لهما جناح الذل من الرحمة وقل رب ارحمهما كما ربيانى صغيرا');
INSERT INTO "uthmani_simple" VALUES(17,25,'17:25','ربكم اعلم بما فى نفوسكم ان تكونوا۟ صلحين فانه كان للاوبين غفورا');
INSERT INTO "uthmani_simple" VALUES(17,26,'17:26','وءات ذا القربى حقه والمسكين وابن السبيل ولا تبذر تبذيرا');
INSERT INTO "uthmani_simple" VALUES(17,27,'17:27','ان المبذرين كانوا۟ اخون الشيطين وكان الشيطن لربه كفورا');
INSERT INTO "uthmani_simple" VALUES(17,28,'17:28','واما تعرضن عنهم ابتغاء رحمة من ربك ترجوها فقل لهم قولا ميسورا');
INSERT INTO "uthmani_simple" VALUES(17,29,'17:29','ولا تجعل يدك مغلولة الى عنقك ولا تبسطها كل البسط فتقعد ملوما محسورا');
INSERT INTO "uthmani_simple" VALUES(17,30,'17:30','ان ربك يبسط الرزق لمن يشاء ويقدر انه كان بعباده خبيرۢا بصيرا');
INSERT INTO "uthmani_simple" VALUES(17,31,'17:31','ولا تقتلوا۟ اولدكم خشية املق نحن نرزقهم واياكم ان قتلهم كان خطـا كبيرا');
INSERT INTO "uthmani_simple" VALUES(17,32,'17:32','ولا تقربوا۟ الزنى انه كان فحشة وساء سبيلا');
INSERT INTO "uthmani_simple" VALUES(17,33,'17:33','ولا تقتلوا۟ النفس التى حرم الله الا بالحق ومن قتل مظلوما فقد جعلنا لوليه سلطنا فلا يسرف فى القتل انه كان منصورا');
INSERT INTO "uthmani_simple" VALUES(17,34,'17:34','ولا تقربوا۟ مال اليتيم الا بالتى هى احسن حتى يبلغ اشده واوفوا۟ بالعهد ان العهد كان مسـولا');
INSERT INTO "uthmani_simple" VALUES(17,35,'17:35','واوفوا۟ الكيل اذا كلتم وزنوا۟ بالقسطاس المستقيم ذلك خير واحسن تاويلا');
INSERT INTO "uthmani_simple" VALUES(17,36,'17:36','ولا تقف ما ليس لك به علم ان السمع والبصر والفؤاد كل او۟لئك كان عنه مسـولا');
INSERT INTO "uthmani_simple" VALUES(17,37,'17:37','ولا تمش فى الارض مرحا انك لن تخرق الارض ولن تبلغ الجبال طولا');
INSERT INTO "uthmani_simple" VALUES(17,38,'17:38','كل ذلك كان سيئه عند ربك مكروها');
INSERT INTO "uthmani_simple" VALUES(17,39,'17:39','ذلك مما اوحى اليك ربك من الحكمة ولا تجعل مع الله الها ءاخر فتلقى فى جهنم ملوما مدحورا');
INSERT INTO "uthmani_simple" VALUES(17,40,'17:40','افاصفىكم ربكم بالبنين واتخذ من الملئكة انثا انكم لتقولون قولا عظيما');
INSERT INTO "uthmani_simple" VALUES(17,41,'17:41','ولقد صرفنا فى هذا القرءان ليذكروا۟ وما يزيدهم الا نفورا');
INSERT INTO "uthmani_simple" VALUES(17,42,'17:42','قل لو كان معه ءالهة كما يقولون اذا لابتغوا۟ الى ذى العرش سبيلا');
INSERT INTO "uthmani_simple" VALUES(17,43,'17:43','سبحنه وتعلى عما يقولون علوا كبيرا');
INSERT INTO "uthmani_simple" VALUES(17,44,'17:44','تسبح له السموت السبع والارض ومن فيهن وان من شىء الا يسبح بحمده ولكن لا تفقهون تسبيحهم انه كان حليما غفورا');
INSERT INTO "uthmani_simple" VALUES(17,45,'17:45','واذا قرات القرءان جعلنا بينك وبين الذين لا يؤمنون بالءاخرة حجابا مستورا');
INSERT INTO "uthmani_simple" VALUES(17,46,'17:46','وجعلنا على قلوبهم اكنة ان يفقهوه وفى ءاذانهم وقرا واذا ذكرت ربك فى القرءان وحده ولوا۟ على ادبرهم نفورا');
INSERT INTO "uthmani_simple" VALUES(17,47,'17:47','نحن اعلم بما يستمعون به اذ يستمعون اليك واذ هم نجوى اذ يقول الظلمون ان تتبعون الا رجلا مسحورا');
INSERT INTO "uthmani_simple" VALUES(17,48,'17:48','انظر كيف ضربوا۟ لك الامثال فضلوا۟ فلا يستطيعون سبيلا');
INSERT INTO "uthmani_simple" VALUES(17,49,'17:49','وقالوا۟ اءذا كنا عظما ورفتا اءنا لمبعوثون خلقا جديدا');
INSERT INTO "uthmani_simple" VALUES(17,50,'17:50','قل كونوا۟ حجارة او حديدا');
INSERT INTO "uthmani_simple" VALUES(17,51,'17:51','او خلقا مما يكبر فى صدوركم فسيقولون من يعيدنا قل الذى فطركم اول مرة فسينغضون اليك رءوسهم ويقولون متى هو قل عسى ان يكون قريبا');
INSERT INTO "uthmani_simple" VALUES(17,52,'17:52','يوم يدعوكم فتستجيبون بحمده وتظنون ان لبثتم الا قليلا');
INSERT INTO "uthmani_simple" VALUES(17,53,'17:53','وقل لعبادى يقولوا۟ التى هى احسن ان الشيطن ينزغ بينهم ان الشيطن كان للانسن عدوا مبينا');
INSERT INTO "uthmani_simple" VALUES(17,54,'17:54','ربكم اعلم بكم ان يشا يرحمكم او ان يشا يعذبكم وما ارسلنك عليهم وكيلا');
INSERT INTO "uthmani_simple" VALUES(17,55,'17:55','وربك اعلم بمن فى السموت والارض ولقد فضلنا بعض النبين على بعض وءاتينا داود زبورا');
INSERT INTO "uthmani_simple" VALUES(17,56,'17:56','قل ادعوا۟ الذين زعمتم من دونه فلا يملكون كشف الضر عنكم ولا تحويلا');
INSERT INTO "uthmani_simple" VALUES(17,57,'17:57','او۟لئك الذين يدعون يبتغون الى ربهم الوسيلة ايهم اقرب ويرجون رحمته ويخافون عذابه ان عذاب ربك كان محذورا');
INSERT INTO "uthmani_simple" VALUES(17,58,'17:58','وان من قرية الا نحن مهلكوها قبل يوم القيمة او معذبوها عذابا شديدا كان ذلك فى الكتب مسطورا');
INSERT INTO "uthmani_simple" VALUES(17,59,'17:59','وما منعنا ان نرسل بالءايت الا ان كذب بها الاولون وءاتينا ثمود الناقة مبصرة فظلموا۟ بها وما نرسل بالءايت الا تخويفا');
INSERT INTO "uthmani_simple" VALUES(17,60,'17:60','واذ قلنا لك ان ربك احاط بالناس وما جعلنا الرءيا التى ارينك الا فتنة للناس والشجرة الملعونة فى القرءان ونخوفهم فما يزيدهم الا طغينا كبيرا');
INSERT INTO "uthmani_simple" VALUES(17,61,'17:61','واذ قلنا للملئكة اسجدوا۟ لءادم فسجدوا۟ الا ابليس قال ءاسجد لمن خلقت طينا');
INSERT INTO "uthmani_simple" VALUES(17,62,'17:62','قال ارءيتك هذا الذى كرمت على لئن اخرتن الى يوم القيمة لاحتنكن ذريته الا قليلا');
INSERT INTO "uthmani_simple" VALUES(17,63,'17:63','قال اذهب فمن تبعك منهم فان جهنم جزاؤكم جزاء موفورا');
INSERT INTO "uthmani_simple" VALUES(17,64,'17:64','واستفزز من استطعت منهم بصوتك واجلب عليهم بخيلك ورجلك وشاركهم فى الامول والاولد وعدهم وما يعدهم الشيطن الا غرورا');
INSERT INTO "uthmani_simple" VALUES(17,65,'17:65','ان عبادى ليس لك عليهم سلطن وكفى بربك وكيلا');
INSERT INTO "uthmani_simple" VALUES(17,66,'17:66','ربكم الذى يزجى لكم الفلك فى البحر لتبتغوا۟ من فضله انه كان بكم رحيما');
INSERT INTO "uthmani_simple" VALUES(17,67,'17:67','واذا مسكم الضر فى البحر ضل من تدعون الا اياه فلما نجىكم الى البر اعرضتم وكان الانسن كفورا');
INSERT INTO "uthmani_simple" VALUES(17,68,'17:68','افامنتم ان يخسف بكم جانب البر او يرسل عليكم حاصبا ثم لا تجدوا۟ لكم وكيلا');
INSERT INTO "uthmani_simple" VALUES(17,69,'17:69','ام امنتم ان يعيدكم فيه تارة اخرى فيرسل عليكم قاصفا من الريح فيغرقكم بما كفرتم ثم لا تجدوا۟ لكم علينا به تبيعا');
INSERT INTO "uthmani_simple" VALUES(17,70,'17:70','ولقد كرمنا بنى ءادم وحملنهم فى البر والبحر ورزقنهم من الطيبت وفضلنهم على كثير ممن خلقنا تفضيلا');
INSERT INTO "uthmani_simple" VALUES(17,71,'17:71','يوم ندعوا۟ كل اناسۭ باممهم فمن اوتى كتبه بيمينه فاو۟لئك يقرءون كتبهم ولا يظلمون فتيلا');
INSERT INTO "uthmani_simple" VALUES(17,72,'17:72','ومن كان فى هذه اعمى فهو فى الءاخرة اعمى واضل سبيلا');
INSERT INTO "uthmani_simple" VALUES(17,73,'17:73','وان كادوا۟ ليفتنونك عن الذى اوحينا اليك لتفترى علينا غيره واذا لاتخذوك خليلا');
INSERT INTO "uthmani_simple" VALUES(17,74,'17:74','ولولا ان ثبتنك لقد كدت تركن اليهم شيـا قليلا');
INSERT INTO "uthmani_simple" VALUES(17,75,'17:75','اذا لاذقنك ضعف الحيوة وضعف الممات ثم لا تجد لك علينا نصيرا');
INSERT INTO "uthmani_simple" VALUES(17,76,'17:76','وان كادوا۟ ليستفزونك من الارض ليخرجوك منها واذا لا يلبثون خلفك الا قليلا');
INSERT INTO "uthmani_simple" VALUES(17,77,'17:77','سنة من قد ارسلنا قبلك من رسلنا ولا تجد لسنتنا تحويلا');
INSERT INTO "uthmani_simple" VALUES(17,78,'17:78','اقم الصلوة لدلوك الشمس الى غسق اليل وقرءان الفجر ان قرءان الفجر كان مشهودا');
INSERT INTO "uthmani_simple" VALUES(17,79,'17:79','ومن اليل فتهجد به نافلة لك عسى ان يبعثك ربك مقاما محمودا');
INSERT INTO "uthmani_simple" VALUES(17,80,'17:80','وقل رب ادخلنى مدخل صدق واخرجنى مخرج صدق واجعل لى من لدنك سلطنا نصيرا');
INSERT INTO "uthmani_simple" VALUES(17,81,'17:81','وقل جاء الحق وزهق البطل ان البطل كان زهوقا');
INSERT INTO "uthmani_simple" VALUES(17,82,'17:82','وننزل من القرءان ما هو شفاء ورحمة للمؤمنين ولا يزيد الظلمين الا خسارا');
INSERT INTO "uthmani_simple" VALUES(17,83,'17:83','واذا انعمنا على الانسن اعرض ونـا بجانبه واذا مسه الشر كان يـوسا');
INSERT INTO "uthmani_simple" VALUES(17,84,'17:84','قل كل يعمل على شاكلته فربكم اعلم بمن هو اهدى سبيلا');
INSERT INTO "uthmani_simple" VALUES(17,85,'17:85','ويسـلونك عن الروح قل الروح من امر ربى وما اوتيتم من العلم الا قليلا');
INSERT INTO "uthmani_simple" VALUES(17,86,'17:86','ولئن شئنا لنذهبن بالذى اوحينا اليك ثم لا تجد لك به علينا وكيلا');
INSERT INTO "uthmani_simple" VALUES(17,87,'17:87','الا رحمة من ربك ان فضله كان عليك كبيرا');
INSERT INTO "uthmani_simple" VALUES(17,88,'17:88','قل لئن اجتمعت الانس والجن على ان ياتوا۟ بمثل هذا القرءان لا ياتون بمثله ولو كان بعضهم لبعض ظهيرا');
INSERT INTO "uthmani_simple" VALUES(17,89,'17:89','ولقد صرفنا للناس فى هذا القرءان من كل مثل فابى اكثر الناس الا كفورا');
INSERT INTO "uthmani_simple" VALUES(17,90,'17:90','وقالوا۟ لن نؤمن لك حتى تفجر لنا من الارض ينۢبوعا');
INSERT INTO "uthmani_simple" VALUES(17,91,'17:91','او تكون لك جنة من نخيل وعنب فتفجر الانهر خللها تفجيرا');
INSERT INTO "uthmani_simple" VALUES(17,92,'17:92','او تسقط السماء كما زعمت علينا كسفا او تاتى بالله والملئكة قبيلا');
INSERT INTO "uthmani_simple" VALUES(17,93,'17:93','او يكون لك بيت من زخرف او ترقى فى السماء ولن نؤمن لرقيك حتى تنزل علينا كتبا نقرؤه قل سبحان ربى هل كنت الا بشرا رسولا');
INSERT INTO "uthmani_simple" VALUES(17,94,'17:94','وما منع الناس ان يؤمنوا۟ اذ جاءهم الهدى الا ان قالوا۟ ابعث الله بشرا رسولا');
INSERT INTO "uthmani_simple" VALUES(17,95,'17:95','قل لو كان فى الارض ملئكة يمشون مطمئنين لنزلنا عليهم من السماء ملكا رسولا');
INSERT INTO "uthmani_simple" VALUES(17,96,'17:96','قل كفى بالله شهيدۢا بينى وبينكم انه كان بعباده خبيرۢا بصيرا');
INSERT INTO "uthmani_simple" VALUES(17,97,'17:97','ومن يهد الله فهو المهتد ومن يضلل فلن تجد لهم اولياء من دونه ونحشرهم يوم القيمة على وجوههم عميا وبكما وصما ماوىهم جهنم كلما خبت زدنهم سعيرا');
INSERT INTO "uthmani_simple" VALUES(17,98,'17:98','ذلك جزاؤهم بانهم كفروا۟ بـايتنا وقالوا۟ اءذا كنا عظما ورفتا اءنا لمبعوثون خلقا جديدا');
INSERT INTO "uthmani_simple" VALUES(17,99,'17:99','اولم يروا۟ ان الله الذى خلق السموت والارض قادر على ان يخلق مثلهم وجعل لهم اجلا لا ريب فيه فابى الظلمون الا كفورا');
INSERT INTO "uthmani_simple" VALUES(17,100,'17:100','قل لو انتم تملكون خزائن رحمة ربى اذا لامسكتم خشية الانفاق وكان الانسن قتورا');
INSERT INTO "uthmani_simple" VALUES(17,101,'17:101','ولقد ءاتينا موسى تسع ءايتۭ بينت فسـل بنى اسرءيل اذ جاءهم فقال له فرعون انى لاظنك يموسى مسحورا');
INSERT INTO "uthmani_simple" VALUES(17,102,'17:102','قال لقد علمت ما انزل هؤلاء الا رب السموت والارض بصائر وانى لاظنك يفرعون مثبورا');
INSERT INTO "uthmani_simple" VALUES(17,103,'17:103','فاراد ان يستفزهم من الارض فاغرقنه ومن معه جميعا');
INSERT INTO "uthmani_simple" VALUES(17,104,'17:104','وقلنا منۢ بعده لبنى اسرءيل اسكنوا۟ الارض فاذا جاء وعد الءاخرة جئنا بكم لفيفا');
INSERT INTO "uthmani_simple" VALUES(17,105,'17:105','وبالحق انزلنه وبالحق نزل وما ارسلنك الا مبشرا ونذيرا');
INSERT INTO "uthmani_simple" VALUES(17,106,'17:106','وقرءانا فرقنه لتقراه على الناس على مكث ونزلنه تنزيلا');
INSERT INTO "uthmani_simple" VALUES(17,107,'17:107','قل ءامنوا۟ به او لا تؤمنوا۟ ان الذين اوتوا۟ العلم من قبله اذا يتلى عليهم يخرون للاذقان سجدا');
INSERT INTO "uthmani_simple" VALUES(17,108,'17:108','ويقولون سبحن ربنا ان كان وعد ربنا لمفعولا');
INSERT INTO "uthmani_simple" VALUES(17,109,'17:109','ويخرون للاذقان يبكون ويزيدهم خشوعا');
INSERT INTO "uthmani_simple" VALUES(17,110,'17:110','قل ادعوا۟ الله او ادعوا۟ الرحمن ايا ما تدعوا۟ فله الاسماء الحسنى ولا تجهر بصلاتك ولا تخافت بها وابتغ بين ذلك سبيلا');
INSERT INTO "uthmani_simple" VALUES(17,111,'17:111','وقل الحمد لله الذى لم يتخذ ولدا ولم يكن له شريك فى الملك ولم يكن له ولى من الذل وكبره تكبيرۢا');
INSERT INTO "uthmani_simple" VALUES(18,1,'18:1','الحمد لله الذى انزل على عبده الكتب ولم يجعل له عوجا');
INSERT INTO "uthmani_simple" VALUES(18,2,'18:2','قيما لينذر باسا شديدا من لدنه ويبشر المؤمنين الذين يعملون الصلحت ان لهم اجرا حسنا');
INSERT INTO "uthmani_simple" VALUES(18,3,'18:3','مكثين فيه ابدا');
INSERT INTO "uthmani_simple" VALUES(18,4,'18:4','وينذر الذين قالوا۟ اتخذ الله ولدا');
INSERT INTO "uthmani_simple" VALUES(18,5,'18:5','ما لهم به من علم ولا لءابائهم كبرت كلمة تخرج من افوههم ان يقولون الا كذبا');
INSERT INTO "uthmani_simple" VALUES(18,6,'18:6','فلعلك بخع نفسك على ءاثرهم ان لم يؤمنوا۟ بهذا الحديث اسفا');
INSERT INTO "uthmani_simple" VALUES(18,7,'18:7','انا جعلنا ما على الارض زينة لها لنبلوهم ايهم احسن عملا');
INSERT INTO "uthmani_simple" VALUES(18,8,'18:8','وانا لجعلون ما عليها صعيدا جرزا');
INSERT INTO "uthmani_simple" VALUES(18,9,'18:9','ام حسبت ان اصحب الكهف والرقيم كانوا۟ من ءايتنا عجبا');
INSERT INTO "uthmani_simple" VALUES(18,10,'18:10','اذ اوى الفتية الى الكهف فقالوا۟ ربنا ءاتنا من لدنك رحمة وهيئ لنا من امرنا رشدا');
INSERT INTO "uthmani_simple" VALUES(18,11,'18:11','فضربنا على ءاذانهم فى الكهف سنين عددا');
INSERT INTO "uthmani_simple" VALUES(18,12,'18:12','ثم بعثنهم لنعلم اى الحزبين احصى لما لبثوا۟ امدا');
INSERT INTO "uthmani_simple" VALUES(18,13,'18:13','نحن نقص عليك نباهم بالحق انهم فتية ءامنوا۟ بربهم وزدنهم هدى');
INSERT INTO "uthmani_simple" VALUES(18,14,'18:14','وربطنا على قلوبهم اذ قاموا۟ فقالوا۟ ربنا رب السموت والارض لن ندعوا۟ من دونه الها لقد قلنا اذا شططا');
INSERT INTO "uthmani_simple" VALUES(18,15,'18:15','هؤلاء قومنا اتخذوا۟ من دونه ءالهة لولا ياتون عليهم بسلطنۭ بين فمن اظلم ممن افترى على الله كذبا');
INSERT INTO "uthmani_simple" VALUES(18,16,'18:16','واذ اعتزلتموهم وما يعبدون الا الله فاوا۟ الى الكهف ينشر لكم ربكم من رحمته ويهيئ لكم من امركم مرفقا');
INSERT INTO "uthmani_simple" VALUES(18,17,'18:17','وترى الشمس اذا طلعت تزور عن كهفهم ذات اليمين واذا غربت تقرضهم ذات الشمال وهم فى فجوة منه ذلك من ءايت الله من يهد الله فهو المهتد ومن يضلل فلن تجد له وليا مرشدا');
INSERT INTO "uthmani_simple" VALUES(18,18,'18:18','وتحسبهم ايقاظا وهم رقود ونقلبهم ذات اليمين وذات الشمال وكلبهم بسط ذراعيه بالوصيد لو اطلعت عليهم لوليت منهم فرارا ولملئت منهم رعبا');
INSERT INTO "uthmani_simple" VALUES(18,19,'18:19','وكذلك بعثنهم ليتساءلوا۟ بينهم قال قائل منهم كم لبثتم قالوا۟ لبثنا يوما او بعض يوم قالوا۟ ربكم اعلم بما لبثتم فابعثوا۟ احدكم بورقكم هذه الى المدينة فلينظر ايها ازكى طعاما فلياتكم برزق منه وليتلطف ولا يشعرن بكم احدا');
INSERT INTO "uthmani_simple" VALUES(18,20,'18:20','انهم ان يظهروا۟ عليكم يرجموكم او يعيدوكم فى ملتهم ولن تفلحوا۟ اذا ابدا');
INSERT INTO "uthmani_simple" VALUES(18,21,'18:21','وكذلك اعثرنا عليهم ليعلموا۟ ان وعد الله حق وان الساعة لا ريب فيها اذ يتنزعون بينهم امرهم فقالوا۟ ابنوا۟ عليهم بنينا ربهم اعلم بهم قال الذين غلبوا۟ على امرهم لنتخذن عليهم مسجدا');
INSERT INTO "uthmani_simple" VALUES(18,22,'18:22','سيقولون ثلثة رابعهم كلبهم ويقولون خمسة سادسهم كلبهم رجمۢا بالغيب ويقولون سبعة وثامنهم كلبهم قل ربى اعلم بعدتهم ما يعلمهم الا قليل فلا تمار فيهم الا مراء ظهرا ولا تستفت فيهم منهم احدا');
INSERT INTO "uthmani_simple" VALUES(18,23,'18:23','ولا تقولن لشا۟ىء انى فاعل ذلك غدا');
INSERT INTO "uthmani_simple" VALUES(18,24,'18:24','الا ان يشاء الله واذكر ربك اذا نسيت وقل عسى ان يهدين ربى لاقرب من هذا رشدا');
INSERT INTO "uthmani_simple" VALUES(18,25,'18:25','ولبثوا۟ فى كهفهم ثلث ما۟ئة سنين وازدادوا۟ تسعا');
INSERT INTO "uthmani_simple" VALUES(18,26,'18:26','قل الله اعلم بما لبثوا۟ له غيب السموت والارض ابصر به واسمع ما لهم من دونه من ولى ولا يشرك فى حكمه احدا');
INSERT INTO "uthmani_simple" VALUES(18,27,'18:27','واتل ما اوحى اليك من كتاب ربك لا مبدل لكلمته ولن تجد من دونه ملتحدا');
INSERT INTO "uthmani_simple" VALUES(18,28,'18:28','واصبر نفسك مع الذين يدعون ربهم بالغدوة والعشى يريدون وجهه ولا تعد عيناك عنهم تريد زينة الحيوة الدنيا ولا تطع من اغفلنا قلبه عن ذكرنا واتبع هوىه وكان امره فرطا');
INSERT INTO "uthmani_simple" VALUES(18,29,'18:29','وقل الحق من ربكم فمن شاء فليؤمن ومن شاء فليكفر انا اعتدنا للظلمين نارا احاط بهم سرادقها وان يستغيثوا۟ يغاثوا۟ بماء كالمهل يشوى الوجوه بئس الشراب وساءت مرتفقا');
INSERT INTO "uthmani_simple" VALUES(18,30,'18:30','ان الذين ءامنوا۟ وعملوا۟ الصلحت انا لا نضيع اجر من احسن عملا');
INSERT INTO "uthmani_simple" VALUES(18,31,'18:31','او۟لئك لهم جنت عدن تجرى من تحتهم الانهر يحلون فيها من اساور من ذهب ويلبسون ثيابا خضرا من سندس واستبرق متكـين فيها على الارائك نعم الثواب وحسنت مرتفقا');
INSERT INTO "uthmani_simple" VALUES(18,32,'18:32','واضرب لهم مثلا رجلين جعلنا لاحدهما جنتين من اعنب وحففنهما بنخل وجعلنا بينهما زرعا');
INSERT INTO "uthmani_simple" VALUES(18,33,'18:33','كلتا الجنتين ءاتت اكلها ولم تظلم منه شيـا وفجرنا خللهما نهرا');
INSERT INTO "uthmani_simple" VALUES(18,34,'18:34','وكان له ثمر فقال لصحبه وهو يحاوره انا۠ اكثر منك مالا واعز نفرا');
INSERT INTO "uthmani_simple" VALUES(18,35,'18:35','ودخل جنته وهو ظالم لنفسه قال ما اظن ان تبيد هذه ابدا');
INSERT INTO "uthmani_simple" VALUES(18,36,'18:36','وما اظن الساعة قائمة ولئن رددت الى ربى لاجدن خيرا منها منقلبا');
INSERT INTO "uthmani_simple" VALUES(18,37,'18:37','قال له صاحبه وهو يحاوره اكفرت بالذى خلقك من تراب ثم من نطفة ثم سوىك رجلا');
INSERT INTO "uthmani_simple" VALUES(18,38,'18:38','لكنا۠ هو الله ربى ولا اشرك بربى احدا');
INSERT INTO "uthmani_simple" VALUES(18,39,'18:39','ولولا اذ دخلت جنتك قلت ما شاء الله لا قوة الا بالله ان ترن انا۠ اقل منك مالا وولدا');
INSERT INTO "uthmani_simple" VALUES(18,40,'18:40','فعسى ربى ان يؤتين خيرا من جنتك ويرسل عليها حسبانا من السماء فتصبح صعيدا زلقا');
INSERT INTO "uthmani_simple" VALUES(18,41,'18:41','او يصبح ماؤها غورا فلن تستطيع له طلبا');
INSERT INTO "uthmani_simple" VALUES(18,42,'18:42','واحيط بثمره فاصبح يقلب كفيه على ما انفق فيها وهى خاوية على عروشها ويقول يليتنى لم اشرك بربى احدا');
INSERT INTO "uthmani_simple" VALUES(18,43,'18:43','ولم تكن له فئة ينصرونه من دون الله وما كان منتصرا');
INSERT INTO "uthmani_simple" VALUES(18,44,'18:44','هنالك الولية لله الحق هو خير ثوابا وخير عقبا');
INSERT INTO "uthmani_simple" VALUES(18,45,'18:45','واضرب لهم مثل الحيوة الدنيا كماء انزلنه من السماء فاختلط به نبات الارض فاصبح هشيما تذروه الريح وكان الله على كل شىء مقتدرا');
INSERT INTO "uthmani_simple" VALUES(18,46,'18:46','المال والبنون زينة الحيوة الدنيا والبقيت الصلحت خير عند ربك ثوابا وخير املا');
INSERT INTO "uthmani_simple" VALUES(18,47,'18:47','ويوم نسير الجبال وترى الارض بارزة وحشرنهم فلم نغادر منهم احدا');
INSERT INTO "uthmani_simple" VALUES(18,48,'18:48','وعرضوا۟ على ربك صفا لقد جئتمونا كما خلقنكم اول مرةۭ بل زعمتم الن نجعل لكم موعدا');
INSERT INTO "uthmani_simple" VALUES(18,49,'18:49','ووضع الكتب فترى المجرمين مشفقين مما فيه ويقولون يويلتنا مال هذا الكتب لا يغادر صغيرة ولا كبيرة الا احصىها ووجدوا۟ ما عملوا۟ حاضرا ولا يظلم ربك احدا');
INSERT INTO "uthmani_simple" VALUES(18,50,'18:50','واذ قلنا للملئكة اسجدوا۟ لءادم فسجدوا۟ الا ابليس كان من الجن ففسق عن امر ربه افتتخذونه وذريته اولياء من دونى وهم لكم عدوۢ بئس للظلمين بدلا');
INSERT INTO "uthmani_simple" VALUES(18,51,'18:51','ما اشهدتهم خلق السموت والارض ولا خلق انفسهم وما كنت متخذ المضلين عضدا');
INSERT INTO "uthmani_simple" VALUES(18,52,'18:52','ويوم يقول نادوا۟ شركاءى الذين زعمتم فدعوهم فلم يستجيبوا۟ لهم وجعلنا بينهم موبقا');
INSERT INTO "uthmani_simple" VALUES(18,53,'18:53','ورءا المجرمون النار فظنوا۟ انهم مواقعوها ولم يجدوا۟ عنها مصرفا');
INSERT INTO "uthmani_simple" VALUES(18,54,'18:54','ولقد صرفنا فى هذا القرءان للناس من كل مثل وكان الانسن اكثر شىء جدلا');
INSERT INTO "uthmani_simple" VALUES(18,55,'18:55','وما منع الناس ان يؤمنوا۟ اذ جاءهم الهدى ويستغفروا۟ ربهم الا ان تاتيهم سنة الاولين او ياتيهم العذاب قبلا');
INSERT INTO "uthmani_simple" VALUES(18,56,'18:56','وما نرسل المرسلين الا مبشرين ومنذرين ويجدل الذين كفروا۟ بالبطل ليدحضوا۟ به الحق واتخذوا۟ ءايتى وما انذروا۟ هزوا');
INSERT INTO "uthmani_simple" VALUES(18,57,'18:57','ومن اظلم ممن ذكر بـايت ربه فاعرض عنها ونسى ما قدمت يداه انا جعلنا على قلوبهم اكنة ان يفقهوه وفى ءاذانهم وقرا وان تدعهم الى الهدى فلن يهتدوا۟ اذا ابدا');
INSERT INTO "uthmani_simple" VALUES(18,58,'18:58','وربك الغفور ذو الرحمة لو يؤاخذهم بما كسبوا۟ لعجل لهم العذاب بل لهم موعد لن يجدوا۟ من دونه موئلا');
INSERT INTO "uthmani_simple" VALUES(18,59,'18:59','وتلك القرى اهلكنهم لما ظلموا۟ وجعلنا لمهلكهم موعدا');
INSERT INTO "uthmani_simple" VALUES(18,60,'18:60','واذ قال موسى لفتىه لا ابرح حتى ابلغ مجمع البحرين او امضى حقبا');
INSERT INTO "uthmani_simple" VALUES(18,61,'18:61','فلما بلغا مجمع بينهما نسيا حوتهما فاتخذ سبيله فى البحر سربا');
INSERT INTO "uthmani_simple" VALUES(18,62,'18:62','فلما جاوزا قال لفتىه ءاتنا غداءنا لقد لقينا من سفرنا هذا نصبا');
INSERT INTO "uthmani_simple" VALUES(18,63,'18:63','قال ارءيت اذ اوينا الى الصخرة فانى نسيت الحوت وما انسىنيه الا الشيطن ان اذكره واتخذ سبيله فى البحر عجبا');
INSERT INTO "uthmani_simple" VALUES(18,64,'18:64','قال ذلك ما كنا نبغ فارتدا على ءاثارهما قصصا');
INSERT INTO "uthmani_simple" VALUES(18,65,'18:65','فوجدا عبدا من عبادنا ءاتينه رحمة من عندنا وعلمنه من لدنا علما');
INSERT INTO "uthmani_simple" VALUES(18,66,'18:66','قال له موسى هل اتبعك على ان تعلمن مما علمت رشدا');
INSERT INTO "uthmani_simple" VALUES(18,67,'18:67','قال انك لن تستطيع معى صبرا');
INSERT INTO "uthmani_simple" VALUES(18,68,'18:68','وكيف تصبر على ما لم تحط به خبرا');
INSERT INTO "uthmani_simple" VALUES(18,69,'18:69','قال ستجدنى ان شاء الله صابرا ولا اعصى لك امرا');
INSERT INTO "uthmani_simple" VALUES(18,70,'18:70','قال فان اتبعتنى فلا تسـلنى عن شىء حتى احدث لك منه ذكرا');
INSERT INTO "uthmani_simple" VALUES(18,71,'18:71','فانطلقا حتى اذا ركبا فى السفينة خرقها قال اخرقتها لتغرق اهلها لقد جئت شيـا امرا');
INSERT INTO "uthmani_simple" VALUES(18,72,'18:72','قال الم اقل انك لن تستطيع معى صبرا');
INSERT INTO "uthmani_simple" VALUES(18,73,'18:73','قال لا تؤاخذنى بما نسيت ولا ترهقنى من امرى عسرا');
INSERT INTO "uthmani_simple" VALUES(18,74,'18:74','فانطلقا حتى اذا لقيا غلما فقتله قال اقتلت نفسا زكيةۢ بغير نفس لقد جئت شيـا نكرا');
INSERT INTO "uthmani_simple" VALUES(18,75,'18:75','قال الم اقل لك انك لن تستطيع معى صبرا');
INSERT INTO "uthmani_simple" VALUES(18,76,'18:76','قال ان سالتك عن شىءۭ بعدها فلا تصحبنى قد بلغت من لدنى عذرا');
INSERT INTO "uthmani_simple" VALUES(18,77,'18:77','فانطلقا حتى اذا اتيا اهل قرية استطعما اهلها فابوا۟ ان يضيفوهما فوجدا فيها جدارا يريد ان ينقض فاقامه قال لو شئت لتخذت عليه اجرا');
INSERT INTO "uthmani_simple" VALUES(18,78,'18:78','قال هذا فراق بينى وبينك سانبئك بتاويل ما لم تستطع عليه صبرا');
INSERT INTO "uthmani_simple" VALUES(18,79,'18:79','اما السفينة فكانت لمسكين يعملون فى البحر فاردت ان اعيبها وكان وراءهم ملك ياخذ كل سفينة غصبا');
INSERT INTO "uthmani_simple" VALUES(18,80,'18:80','واما الغلم فكان ابواه مؤمنين فخشينا ان يرهقهما طغينا وكفرا');
INSERT INTO "uthmani_simple" VALUES(18,81,'18:81','فاردنا ان يبدلهما ربهما خيرا منه زكوة واقرب رحما');
INSERT INTO "uthmani_simple" VALUES(18,82,'18:82','واما الجدار فكان لغلمين يتيمين فى المدينة وكان تحته كنز لهما وكان ابوهما صلحا فاراد ربك ان يبلغا اشدهما ويستخرجا كنزهما رحمة من ربك وما فعلته عن امرى ذلك تاويل ما لم تسطع عليه صبرا');
INSERT INTO "uthmani_simple" VALUES(18,83,'18:83','ويسـلونك عن ذى القرنين قل ساتلوا۟ عليكم منه ذكرا');
INSERT INTO "uthmani_simple" VALUES(18,84,'18:84','انا مكنا له فى الارض وءاتينه من كل شىء سببا');
INSERT INTO "uthmani_simple" VALUES(18,85,'18:85','فاتبع سببا');
INSERT INTO "uthmani_simple" VALUES(18,86,'18:86','حتى اذا بلغ مغرب الشمس وجدها تغرب فى عين حمئة ووجد عندها قوما قلنا يذا القرنين اما ان تعذب واما ان تتخذ فيهم حسنا');
INSERT INTO "uthmani_simple" VALUES(18,87,'18:87','قال اما من ظلم فسوف نعذبه ثم يرد الى ربه فيعذبه عذابا نكرا');
INSERT INTO "uthmani_simple" VALUES(18,88,'18:88','واما من ءامن وعمل صلحا فله جزاء الحسنى وسنقول له من امرنا يسرا');
INSERT INTO "uthmani_simple" VALUES(18,89,'18:89','ثم اتبع سببا');
INSERT INTO "uthmani_simple" VALUES(18,90,'18:90','حتى اذا بلغ مطلع الشمس وجدها تطلع على قوم لم نجعل لهم من دونها سترا');
INSERT INTO "uthmani_simple" VALUES(18,91,'18:91','كذلك وقد احطنا بما لديه خبرا');
INSERT INTO "uthmani_simple" VALUES(18,92,'18:92','ثم اتبع سببا');
INSERT INTO "uthmani_simple" VALUES(18,93,'18:93','حتى اذا بلغ بين السدين وجد من دونهما قوما لا يكادون يفقهون قولا');
INSERT INTO "uthmani_simple" VALUES(18,94,'18:94','قالوا۟ يذا القرنين ان ياجوج وماجوج مفسدون فى الارض فهل نجعل لك خرجا على ان تجعل بيننا وبينهم سدا');
INSERT INTO "uthmani_simple" VALUES(18,95,'18:95','قال ما مكنى فيه ربى خير فاعينونى بقوة اجعل بينكم وبينهم ردما');
INSERT INTO "uthmani_simple" VALUES(18,96,'18:96','ءاتونى زبر الحديد حتى اذا ساوى بين الصدفين قال انفخوا۟ حتى اذا جعله نارا قال ءاتونى افرغ عليه قطرا');
INSERT INTO "uthmani_simple" VALUES(18,97,'18:97','فما اسطعوا۟ ان يظهروه وما استطعوا۟ له نقبا');
INSERT INTO "uthmani_simple" VALUES(18,98,'18:98','قال هذا رحمة من ربى فاذا جاء وعد ربى جعله دكاء وكان وعد ربى حقا');
INSERT INTO "uthmani_simple" VALUES(18,99,'18:99','وتركنا بعضهم يومئذ يموج فى بعض ونفخ فى الصور فجمعنهم جمعا');
INSERT INTO "uthmani_simple" VALUES(18,100,'18:100','وعرضنا جهنم يومئذ للكفرين عرضا');
INSERT INTO "uthmani_simple" VALUES(18,101,'18:101','الذين كانت اعينهم فى غطاء عن ذكرى وكانوا۟ لا يستطيعون سمعا');
INSERT INTO "uthmani_simple" VALUES(18,102,'18:102','افحسب الذين كفروا۟ ان يتخذوا۟ عبادى من دونى اولياء انا اعتدنا جهنم للكفرين نزلا');
INSERT INTO "uthmani_simple" VALUES(18,103,'18:103','قل هل ننبئكم بالاخسرين اعملا');
INSERT INTO "uthmani_simple" VALUES(18,104,'18:104','الذين ضل سعيهم فى الحيوة الدنيا وهم يحسبون انهم يحسنون صنعا');
INSERT INTO "uthmani_simple" VALUES(18,105,'18:105','او۟لئك الذين كفروا۟ بـايت ربهم ولقائه فحبطت اعملهم فلا نقيم لهم يوم القيمة وزنا');
INSERT INTO "uthmani_simple" VALUES(18,106,'18:106','ذلك جزاؤهم جهنم بما كفروا۟ واتخذوا۟ ءايتى ورسلى هزوا');
INSERT INTO "uthmani_simple" VALUES(18,107,'18:107','ان الذين ءامنوا۟ وعملوا۟ الصلحت كانت لهم جنت الفردوس نزلا');
INSERT INTO "uthmani_simple" VALUES(18,108,'18:108','خلدين فيها لا يبغون عنها حولا');
INSERT INTO "uthmani_simple" VALUES(18,109,'18:109','قل لو كان البحر مدادا لكلمت ربى لنفد البحر قبل ان تنفد كلمت ربى ولو جئنا بمثله مددا');
INSERT INTO "uthmani_simple" VALUES(18,110,'18:110','قل انما انا۠ بشر مثلكم يوحى الى انما الهكم اله وحد فمن كان يرجوا۟ لقاء ربه فليعمل عملا صلحا ولا يشرك بعبادة ربه احدۢا');
INSERT INTO "uthmani_simple" VALUES(19,1,'19:1','كهيعص');
INSERT INTO "uthmani_simple" VALUES(19,2,'19:2','ذكر رحمت ربك عبده زكريا');
INSERT INTO "uthmani_simple" VALUES(19,3,'19:3','اذ نادى ربه نداء خفيا');
INSERT INTO "uthmani_simple" VALUES(19,4,'19:4','قال رب انى وهن العظم منى واشتعل الراس شيبا ولم اكنۢ بدعائك رب شقيا');
INSERT INTO "uthmani_simple" VALUES(19,5,'19:5','وانى خفت المولى من وراءى وكانت امراتى عاقرا فهب لى من لدنك وليا');
INSERT INTO "uthmani_simple" VALUES(19,6,'19:6','يرثنى ويرث من ءال يعقوب واجعله رب رضيا');
INSERT INTO "uthmani_simple" VALUES(19,7,'19:7','يزكريا انا نبشرك بغلم اسمه يحيى لم نجعل له من قبل سميا');
INSERT INTO "uthmani_simple" VALUES(19,8,'19:8','قال رب انى يكون لى غلم وكانت امراتى عاقرا وقد بلغت من الكبر عتيا');
INSERT INTO "uthmani_simple" VALUES(19,9,'19:9','قال كذلك قال ربك هو على هين وقد خلقتك من قبل ولم تك شيـا');
INSERT INTO "uthmani_simple" VALUES(19,10,'19:10','قال رب اجعل لى ءاية قال ءايتك الا تكلم الناس ثلث ليال سويا');
INSERT INTO "uthmani_simple" VALUES(19,11,'19:11','فخرج على قومه من المحراب فاوحى اليهم ان سبحوا۟ بكرة وعشيا');
INSERT INTO "uthmani_simple" VALUES(19,12,'19:12','ييحيى خذ الكتب بقوة وءاتينه الحكم صبيا');
INSERT INTO "uthmani_simple" VALUES(19,13,'19:13','وحنانا من لدنا وزكوة وكان تقيا');
INSERT INTO "uthmani_simple" VALUES(19,14,'19:14','وبرۢا بولديه ولم يكن جبارا عصيا');
INSERT INTO "uthmani_simple" VALUES(19,15,'19:15','وسلم عليه يوم ولد ويوم يموت ويوم يبعث حيا');
INSERT INTO "uthmani_simple" VALUES(19,16,'19:16','واذكر فى الكتب مريم اذ انتبذت من اهلها مكانا شرقيا');
INSERT INTO "uthmani_simple" VALUES(19,17,'19:17','فاتخذت من دونهم حجابا فارسلنا اليها روحنا فتمثل لها بشرا سويا');
INSERT INTO "uthmani_simple" VALUES(19,18,'19:18','قالت انى اعوذ بالرحمن منك ان كنت تقيا');
INSERT INTO "uthmani_simple" VALUES(19,19,'19:19','قال انما انا۠ رسول ربك لاهب لك غلما زكيا');
INSERT INTO "uthmani_simple" VALUES(19,20,'19:20','قالت انى يكون لى غلم ولم يمسسنى بشر ولم اك بغيا');
INSERT INTO "uthmani_simple" VALUES(19,21,'19:21','قال كذلك قال ربك هو على هين ولنجعله ءاية للناس ورحمة منا وكان امرا مقضيا');
INSERT INTO "uthmani_simple" VALUES(19,22,'19:22','فحملته فانتبذت به مكانا قصيا');
INSERT INTO "uthmani_simple" VALUES(19,23,'19:23','فاجاءها المخاض الى جذع النخلة قالت يليتنى مت قبل هذا وكنت نسيا منسيا');
INSERT INTO "uthmani_simple" VALUES(19,24,'19:24','فنادىها من تحتها الا تحزنى قد جعل ربك تحتك سريا');
INSERT INTO "uthmani_simple" VALUES(19,25,'19:25','وهزى اليك بجذع النخلة تسقط عليك رطبا جنيا');
INSERT INTO "uthmani_simple" VALUES(19,26,'19:26','فكلى واشربى وقرى عينا فاما ترين من البشر احدا فقولى انى نذرت للرحمن صوما فلن اكلم اليوم انسيا');
INSERT INTO "uthmani_simple" VALUES(19,27,'19:27','فاتت به قومها تحمله قالوا۟ يمريم لقد جئت شيـا فريا');
INSERT INTO "uthmani_simple" VALUES(19,28,'19:28','ياخت هرون ما كان ابوك امرا سوء وما كانت امك بغيا');
INSERT INTO "uthmani_simple" VALUES(19,29,'19:29','فاشارت اليه قالوا۟ كيف نكلم من كان فى المهد صبيا');
INSERT INTO "uthmani_simple" VALUES(19,30,'19:30','قال انى عبد الله ءاتىنى الكتب وجعلنى نبيا');
INSERT INTO "uthmani_simple" VALUES(19,31,'19:31','وجعلنى مباركا اين ما كنت واوصنى بالصلوة والزكوة ما دمت حيا');
INSERT INTO "uthmani_simple" VALUES(19,32,'19:32','وبرۢا بولدتى ولم يجعلنى جبارا شقيا');
INSERT INTO "uthmani_simple" VALUES(19,33,'19:33','والسلم على يوم ولدت ويوم اموت ويوم ابعث حيا');
INSERT INTO "uthmani_simple" VALUES(19,34,'19:34','ذلك عيسى ابن مريم قول الحق الذى فيه يمترون');
INSERT INTO "uthmani_simple" VALUES(19,35,'19:35','ما كان لله ان يتخذ من ولد سبحنه اذا قضى امرا فانما يقول له كن فيكون');
INSERT INTO "uthmani_simple" VALUES(19,36,'19:36','وان الله ربى وربكم فاعبدوه هذا صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(19,37,'19:37','فاختلف الاحزاب منۢ بينهم فويل للذين كفروا۟ من مشهد يوم عظيم');
INSERT INTO "uthmani_simple" VALUES(19,38,'19:38','اسمع بهم وابصر يوم ياتوننا لكن الظلمون اليوم فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(19,39,'19:39','وانذرهم يوم الحسرة اذ قضى الامر وهم فى غفلة وهم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(19,40,'19:40','انا نحن نرث الارض ومن عليها والينا يرجعون');
INSERT INTO "uthmani_simple" VALUES(19,41,'19:41','واذكر فى الكتب ابرهيم انه كان صديقا نبيا');
INSERT INTO "uthmani_simple" VALUES(19,42,'19:42','اذ قال لابيه يابت لم تعبد ما لا يسمع ولا يبصر ولا يغنى عنك شيـا');
INSERT INTO "uthmani_simple" VALUES(19,43,'19:43','يابت انى قد جاءنى من العلم ما لم ياتك فاتبعنى اهدك صرطا سويا');
INSERT INTO "uthmani_simple" VALUES(19,44,'19:44','يابت لا تعبد الشيطن ان الشيطن كان للرحمن عصيا');
INSERT INTO "uthmani_simple" VALUES(19,45,'19:45','يابت انى اخاف ان يمسك عذاب من الرحمن فتكون للشيطن وليا');
INSERT INTO "uthmani_simple" VALUES(19,46,'19:46','قال اراغب انت عن ءالهتى يابرهيم لئن لم تنته لارجمنك واهجرنى مليا');
INSERT INTO "uthmani_simple" VALUES(19,47,'19:47','قال سلم عليك ساستغفر لك ربى انه كان بى حفيا');
INSERT INTO "uthmani_simple" VALUES(19,48,'19:48','واعتزلكم وما تدعون من دون الله وادعوا۟ ربى عسى الا اكون بدعاء ربى شقيا');
INSERT INTO "uthmani_simple" VALUES(19,49,'19:49','فلما اعتزلهم وما يعبدون من دون الله وهبنا له اسحق ويعقوب وكلا جعلنا نبيا');
INSERT INTO "uthmani_simple" VALUES(19,50,'19:50','ووهبنا لهم من رحمتنا وجعلنا لهم لسان صدق عليا');
INSERT INTO "uthmani_simple" VALUES(19,51,'19:51','واذكر فى الكتب موسى انه كان مخلصا وكان رسولا نبيا');
INSERT INTO "uthmani_simple" VALUES(19,52,'19:52','وندينه من جانب الطور الايمن وقربنه نجيا');
INSERT INTO "uthmani_simple" VALUES(19,53,'19:53','ووهبنا له من رحمتنا اخاه هرون نبيا');
INSERT INTO "uthmani_simple" VALUES(19,54,'19:54','واذكر فى الكتب اسمعيل انه كان صادق الوعد وكان رسولا نبيا');
INSERT INTO "uthmani_simple" VALUES(19,55,'19:55','وكان يامر اهله بالصلوة والزكوة وكان عند ربه مرضيا');
INSERT INTO "uthmani_simple" VALUES(19,56,'19:56','واذكر فى الكتب ادريس انه كان صديقا نبيا');
INSERT INTO "uthmani_simple" VALUES(19,57,'19:57','ورفعنه مكانا عليا');
INSERT INTO "uthmani_simple" VALUES(19,58,'19:58','او۟لئك الذين انعم الله عليهم من النبين من ذرية ءادم وممن حملنا مع نوح ومن ذرية ابرهيم واسرءيل وممن هدينا واجتبينا اذا تتلى عليهم ءايت الرحمن خروا۟ سجدا وبكيا');
INSERT INTO "uthmani_simple" VALUES(19,59,'19:59','فخلف منۢ بعدهم خلف اضاعوا۟ الصلوة واتبعوا۟ الشهوت فسوف يلقون غيا');
INSERT INTO "uthmani_simple" VALUES(19,60,'19:60','الا من تاب وءامن وعمل صلحا فاو۟لئك يدخلون الجنة ولا يظلمون شيـا');
INSERT INTO "uthmani_simple" VALUES(19,61,'19:61','جنت عدن التى وعد الرحمن عباده بالغيب انه كان وعده ماتيا');
INSERT INTO "uthmani_simple" VALUES(19,62,'19:62','لا يسمعون فيها لغوا الا سلما ولهم رزقهم فيها بكرة وعشيا');
INSERT INTO "uthmani_simple" VALUES(19,63,'19:63','تلك الجنة التى نورث من عبادنا من كان تقيا');
INSERT INTO "uthmani_simple" VALUES(19,64,'19:64','وما نتنزل الا بامر ربك له ما بين ايدينا وما خلفنا وما بين ذلك وما كان ربك نسيا');
INSERT INTO "uthmani_simple" VALUES(19,65,'19:65','رب السموت والارض وما بينهما فاعبده واصطبر لعبدته هل تعلم له سميا');
INSERT INTO "uthmani_simple" VALUES(19,66,'19:66','ويقول الانسن اءذا ما مت لسوف اخرج حيا');
INSERT INTO "uthmani_simple" VALUES(19,67,'19:67','اولا يذكر الانسن انا خلقنه من قبل ولم يك شيـا');
INSERT INTO "uthmani_simple" VALUES(19,68,'19:68','فوربك لنحشرنهم والشيطين ثم لنحضرنهم حول جهنم جثيا');
INSERT INTO "uthmani_simple" VALUES(19,69,'19:69','ثم لننزعن من كل شيعة ايهم اشد على الرحمن عتيا');
INSERT INTO "uthmani_simple" VALUES(19,70,'19:70','ثم لنحن اعلم بالذين هم اولى بها صليا');
INSERT INTO "uthmani_simple" VALUES(19,71,'19:71','وان منكم الا واردها كان على ربك حتما مقضيا');
INSERT INTO "uthmani_simple" VALUES(19,72,'19:72','ثم ننجى الذين اتقوا۟ ونذر الظلمين فيها جثيا');
INSERT INTO "uthmani_simple" VALUES(19,73,'19:73','واذا تتلى عليهم ءايتنا بينت قال الذين كفروا۟ للذين ءامنوا۟ اى الفريقين خير مقاما واحسن نديا');
INSERT INTO "uthmani_simple" VALUES(19,74,'19:74','وكم اهلكنا قبلهم من قرن هم احسن اثثا ورءيا');
INSERT INTO "uthmani_simple" VALUES(19,75,'19:75','قل من كان فى الضللة فليمدد له الرحمن مدا حتى اذا راوا۟ ما يوعدون اما العذاب واما الساعة فسيعلمون من هو شر مكانا واضعف جندا');
INSERT INTO "uthmani_simple" VALUES(19,76,'19:76','ويزيد الله الذين اهتدوا۟ هدى والبقيت الصلحت خير عند ربك ثوابا وخير مردا');
INSERT INTO "uthmani_simple" VALUES(19,77,'19:77','افرءيت الذى كفر بـايتنا وقال لاوتين مالا وولدا');
INSERT INTO "uthmani_simple" VALUES(19,78,'19:78','اطلع الغيب ام اتخذ عند الرحمن عهدا');
INSERT INTO "uthmani_simple" VALUES(19,79,'19:79','كلا سنكتب ما يقول ونمد له من العذاب مدا');
INSERT INTO "uthmani_simple" VALUES(19,80,'19:80','ونرثه ما يقول وياتينا فردا');
INSERT INTO "uthmani_simple" VALUES(19,81,'19:81','واتخذوا۟ من دون الله ءالهة ليكونوا۟ لهم عزا');
INSERT INTO "uthmani_simple" VALUES(19,82,'19:82','كلا سيكفرون بعبادتهم ويكونون عليهم ضدا');
INSERT INTO "uthmani_simple" VALUES(19,83,'19:83','الم تر انا ارسلنا الشيطين على الكفرين تؤزهم ازا');
INSERT INTO "uthmani_simple" VALUES(19,84,'19:84','فلا تعجل عليهم انما نعد لهم عدا');
INSERT INTO "uthmani_simple" VALUES(19,85,'19:85','يوم نحشر المتقين الى الرحمن وفدا');
INSERT INTO "uthmani_simple" VALUES(19,86,'19:86','ونسوق المجرمين الى جهنم وردا');
INSERT INTO "uthmani_simple" VALUES(19,87,'19:87','لا يملكون الشفعة الا من اتخذ عند الرحمن عهدا');
INSERT INTO "uthmani_simple" VALUES(19,88,'19:88','وقالوا۟ اتخذ الرحمن ولدا');
INSERT INTO "uthmani_simple" VALUES(19,89,'19:89','لقد جئتم شيـا ادا');
INSERT INTO "uthmani_simple" VALUES(19,90,'19:90','تكاد السموت يتفطرن منه وتنشق الارض وتخر الجبال هدا');
INSERT INTO "uthmani_simple" VALUES(19,91,'19:91','ان دعوا۟ للرحمن ولدا');
INSERT INTO "uthmani_simple" VALUES(19,92,'19:92','وما ينۢبغى للرحمن ان يتخذ ولدا');
INSERT INTO "uthmani_simple" VALUES(19,93,'19:93','ان كل من فى السموت والارض الا ءاتى الرحمن عبدا');
INSERT INTO "uthmani_simple" VALUES(19,94,'19:94','لقد احصىهم وعدهم عدا');
INSERT INTO "uthmani_simple" VALUES(19,95,'19:95','وكلهم ءاتيه يوم القيمة فردا');
INSERT INTO "uthmani_simple" VALUES(19,96,'19:96','ان الذين ءامنوا۟ وعملوا۟ الصلحت سيجعل لهم الرحمن ودا');
INSERT INTO "uthmani_simple" VALUES(19,97,'19:97','فانما يسرنه بلسانك لتبشر به المتقين وتنذر به قوما لدا');
INSERT INTO "uthmani_simple" VALUES(19,98,'19:98','وكم اهلكنا قبلهم من قرن هل تحس منهم من احد او تسمع لهم ركزۢا');
INSERT INTO "uthmani_simple" VALUES(20,1,'20:1','طه');
INSERT INTO "uthmani_simple" VALUES(20,2,'20:2','ما انزلنا عليك القرءان لتشقى');
INSERT INTO "uthmani_simple" VALUES(20,3,'20:3','الا تذكرة لمن يخشى');
INSERT INTO "uthmani_simple" VALUES(20,4,'20:4','تنزيلا ممن خلق الارض والسموت العلى');
INSERT INTO "uthmani_simple" VALUES(20,5,'20:5','الرحمن على العرش استوى');
INSERT INTO "uthmani_simple" VALUES(20,6,'20:6','له ما فى السموت وما فى الارض وما بينهما وما تحت الثرى');
INSERT INTO "uthmani_simple" VALUES(20,7,'20:7','وان تجهر بالقول فانه يعلم السر واخفى');
INSERT INTO "uthmani_simple" VALUES(20,8,'20:8','الله لا اله الا هو له الاسماء الحسنى');
INSERT INTO "uthmani_simple" VALUES(20,9,'20:9','وهل اتىك حديث موسى');
INSERT INTO "uthmani_simple" VALUES(20,10,'20:10','اذ رءا نارا فقال لاهله امكثوا۟ انى ءانست نارا لعلى ءاتيكم منها بقبس او اجد على النار هدى');
INSERT INTO "uthmani_simple" VALUES(20,11,'20:11','فلما اتىها نودى يموسى');
INSERT INTO "uthmani_simple" VALUES(20,12,'20:12','انى انا۠ ربك فاخلع نعليك انك بالواد المقدس طوى');
INSERT INTO "uthmani_simple" VALUES(20,13,'20:13','وانا اخترتك فاستمع لما يوحى');
INSERT INTO "uthmani_simple" VALUES(20,14,'20:14','اننى انا الله لا اله الا انا۠ فاعبدنى واقم الصلوة لذكرى');
INSERT INTO "uthmani_simple" VALUES(20,15,'20:15','ان الساعة ءاتية اكاد اخفيها لتجزى كل نفسۭ بما تسعى');
INSERT INTO "uthmani_simple" VALUES(20,16,'20:16','فلا يصدنك عنها من لا يؤمن بها واتبع هوىه فتردى');
INSERT INTO "uthmani_simple" VALUES(20,17,'20:17','وما تلك بيمينك يموسى');
INSERT INTO "uthmani_simple" VALUES(20,18,'20:18','قال هى عصاى اتوكؤا۟ عليها واهش بها على غنمى ولى فيها مـارب اخرى');
INSERT INTO "uthmani_simple" VALUES(20,19,'20:19','قال القها يموسى');
INSERT INTO "uthmani_simple" VALUES(20,20,'20:20','فالقىها فاذا هى حية تسعى');
INSERT INTO "uthmani_simple" VALUES(20,21,'20:21','قال خذها ولا تخف سنعيدها سيرتها الاولى');
INSERT INTO "uthmani_simple" VALUES(20,22,'20:22','واضمم يدك الى جناحك تخرج بيضاء من غير سوء ءاية اخرى');
INSERT INTO "uthmani_simple" VALUES(20,23,'20:23','لنريك من ءايتنا الكبرى');
INSERT INTO "uthmani_simple" VALUES(20,24,'20:24','اذهب الى فرعون انه طغى');
INSERT INTO "uthmani_simple" VALUES(20,25,'20:25','قال رب اشرح لى صدرى');
INSERT INTO "uthmani_simple" VALUES(20,26,'20:26','ويسر لى امرى');
INSERT INTO "uthmani_simple" VALUES(20,27,'20:27','واحلل عقدة من لسانى');
INSERT INTO "uthmani_simple" VALUES(20,28,'20:28','يفقهوا۟ قولى');
INSERT INTO "uthmani_simple" VALUES(20,29,'20:29','واجعل لى وزيرا من اهلى');
INSERT INTO "uthmani_simple" VALUES(20,30,'20:30','هرون اخى');
INSERT INTO "uthmani_simple" VALUES(20,31,'20:31','اشدد به ازرى');
INSERT INTO "uthmani_simple" VALUES(20,32,'20:32','واشركه فى امرى');
INSERT INTO "uthmani_simple" VALUES(20,33,'20:33','كى نسبحك كثيرا');
INSERT INTO "uthmani_simple" VALUES(20,34,'20:34','ونذكرك كثيرا');
INSERT INTO "uthmani_simple" VALUES(20,35,'20:35','انك كنت بنا بصيرا');
INSERT INTO "uthmani_simple" VALUES(20,36,'20:36','قال قد اوتيت سؤلك يموسى');
INSERT INTO "uthmani_simple" VALUES(20,37,'20:37','ولقد مننا عليك مرة اخرى');
INSERT INTO "uthmani_simple" VALUES(20,38,'20:38','اذ اوحينا الى امك ما يوحى');
INSERT INTO "uthmani_simple" VALUES(20,39,'20:39','ان اقذفيه فى التابوت فاقذفيه فى اليم فليلقه اليم بالساحل ياخذه عدو لى وعدو له والقيت عليك محبة منى ولتصنع على عينى');
INSERT INTO "uthmani_simple" VALUES(20,40,'20:40','اذ تمشى اختك فتقول هل ادلكم على من يكفله فرجعنك الى امك كى تقر عينها ولا تحزن وقتلت نفسا فنجينك من الغم وفتنك فتونا فلبثت سنين فى اهل مدين ثم جئت على قدر يموسى');
INSERT INTO "uthmani_simple" VALUES(20,41,'20:41','واصطنعتك لنفسى');
INSERT INTO "uthmani_simple" VALUES(20,42,'20:42','اذهب انت واخوك بـايتى ولا تنيا فى ذكرى');
INSERT INTO "uthmani_simple" VALUES(20,43,'20:43','اذهبا الى فرعون انه طغى');
INSERT INTO "uthmani_simple" VALUES(20,44,'20:44','فقولا له قولا لينا لعله يتذكر او يخشى');
INSERT INTO "uthmani_simple" VALUES(20,45,'20:45','قالا ربنا اننا نخاف ان يفرط علينا او ان يطغى');
INSERT INTO "uthmani_simple" VALUES(20,46,'20:46','قال لا تخافا اننى معكما اسمع وارى');
INSERT INTO "uthmani_simple" VALUES(20,47,'20:47','فاتياه فقولا انا رسولا ربك فارسل معنا بنى اسرءيل ولا تعذبهم قد جئنك بـاية من ربك والسلم على من اتبع الهدى');
INSERT INTO "uthmani_simple" VALUES(20,48,'20:48','انا قد اوحى الينا ان العذاب على من كذب وتولى');
INSERT INTO "uthmani_simple" VALUES(20,49,'20:49','قال فمن ربكما يموسى');
INSERT INTO "uthmani_simple" VALUES(20,50,'20:50','قال ربنا الذى اعطى كل شىء خلقه ثم هدى');
INSERT INTO "uthmani_simple" VALUES(20,51,'20:51','قال فما بال القرون الاولى');
INSERT INTO "uthmani_simple" VALUES(20,52,'20:52','قال علمها عند ربى فى كتب لا يضل ربى ولا ينسى');
INSERT INTO "uthmani_simple" VALUES(20,53,'20:53','الذى جعل لكم الارض مهدا وسلك لكم فيها سبلا وانزل من السماء ماء فاخرجنا به ازوجا من نبات شتى');
INSERT INTO "uthmani_simple" VALUES(20,54,'20:54','كلوا۟ وارعوا۟ انعمكم ان فى ذلك لءايت لاو۟لى النهى');
INSERT INTO "uthmani_simple" VALUES(20,55,'20:55','منها خلقنكم وفيها نعيدكم ومنها نخرجكم تارة اخرى');
INSERT INTO "uthmani_simple" VALUES(20,56,'20:56','ولقد ارينه ءايتنا كلها فكذب وابى');
INSERT INTO "uthmani_simple" VALUES(20,57,'20:57','قال اجئتنا لتخرجنا من ارضنا بسحرك يموسى');
INSERT INTO "uthmani_simple" VALUES(20,58,'20:58','فلناتينك بسحر مثله فاجعل بيننا وبينك موعدا لا نخلفه نحن ولا انت مكانا سوى');
INSERT INTO "uthmani_simple" VALUES(20,59,'20:59','قال موعدكم يوم الزينة وان يحشر الناس ضحى');
INSERT INTO "uthmani_simple" VALUES(20,60,'20:60','فتولى فرعون فجمع كيده ثم اتى');
INSERT INTO "uthmani_simple" VALUES(20,61,'20:61','قال لهم موسى ويلكم لا تفتروا۟ على الله كذبا فيسحتكم بعذاب وقد خاب من افترى');
INSERT INTO "uthmani_simple" VALUES(20,62,'20:62','فتنزعوا۟ امرهم بينهم واسروا۟ النجوى');
INSERT INTO "uthmani_simple" VALUES(20,63,'20:63','قالوا۟ ان هذن لسحرن يريدان ان يخرجاكم من ارضكم بسحرهما ويذهبا بطريقتكم المثلى');
INSERT INTO "uthmani_simple" VALUES(20,64,'20:64','فاجمعوا۟ كيدكم ثم ائتوا۟ صفا وقد افلح اليوم من استعلى');
INSERT INTO "uthmani_simple" VALUES(20,65,'20:65','قالوا۟ يموسى اما ان تلقى واما ان نكون اول من القى');
INSERT INTO "uthmani_simple" VALUES(20,66,'20:66','قال بل القوا۟ فاذا حبالهم وعصيهم يخيل اليه من سحرهم انها تسعى');
INSERT INTO "uthmani_simple" VALUES(20,67,'20:67','فاوجس فى نفسه خيفة موسى');
INSERT INTO "uthmani_simple" VALUES(20,68,'20:68','قلنا لا تخف انك انت الاعلى');
INSERT INTO "uthmani_simple" VALUES(20,69,'20:69','والق ما فى يمينك تلقف ما صنعوا۟ انما صنعوا۟ كيد سحر ولا يفلح الساحر حيث اتى');
INSERT INTO "uthmani_simple" VALUES(20,70,'20:70','فالقى السحرة سجدا قالوا۟ ءامنا برب هرون وموسى');
INSERT INTO "uthmani_simple" VALUES(20,71,'20:71','قال ءامنتم له قبل ان ءاذن لكم انه لكبيركم الذى علمكم السحر فلاقطعن ايديكم وارجلكم من خلف ولاصلبنكم فى جذوع النخل ولتعلمن اينا اشد عذابا وابقى');
INSERT INTO "uthmani_simple" VALUES(20,72,'20:72','قالوا۟ لن نؤثرك على ما جاءنا من البينت والذى فطرنا فاقض ما انت قاض انما تقضى هذه الحيوة الدنيا');
INSERT INTO "uthmani_simple" VALUES(20,73,'20:73','انا ءامنا بربنا ليغفر لنا خطينا وما اكرهتنا عليه من السحر والله خير وابقى');
INSERT INTO "uthmani_simple" VALUES(20,74,'20:74','انه من يات ربه مجرما فان له جهنم لا يموت فيها ولا يحيى');
INSERT INTO "uthmani_simple" VALUES(20,75,'20:75','ومن ياته مؤمنا قد عمل الصلحت فاو۟لئك لهم الدرجت العلى');
INSERT INTO "uthmani_simple" VALUES(20,76,'20:76','جنت عدن تجرى من تحتها الانهر خلدين فيها وذلك جزاء من تزكى');
INSERT INTO "uthmani_simple" VALUES(20,77,'20:77','ولقد اوحينا الى موسى ان اسر بعبادى فاضرب لهم طريقا فى البحر يبسا لا تخف دركا ولا تخشى');
INSERT INTO "uthmani_simple" VALUES(20,78,'20:78','فاتبعهم فرعون بجنوده فغشيهم من اليم ما غشيهم');
INSERT INTO "uthmani_simple" VALUES(20,79,'20:79','واضل فرعون قومه وما هدى');
INSERT INTO "uthmani_simple" VALUES(20,80,'20:80','يبنى اسرءيل قد انجينكم من عدوكم ووعدنكم جانب الطور الايمن ونزلنا عليكم المن والسلوى');
INSERT INTO "uthmani_simple" VALUES(20,81,'20:81','كلوا۟ من طيبت ما رزقنكم ولا تطغوا۟ فيه فيحل عليكم غضبى ومن يحلل عليه غضبى فقد هوى');
INSERT INTO "uthmani_simple" VALUES(20,82,'20:82','وانى لغفار لمن تاب وءامن وعمل صلحا ثم اهتدى');
INSERT INTO "uthmani_simple" VALUES(20,83,'20:83','وما اعجلك عن قومك يموسى');
INSERT INTO "uthmani_simple" VALUES(20,84,'20:84','قال هم او۟لاء على اثرى وعجلت اليك رب لترضى');
INSERT INTO "uthmani_simple" VALUES(20,85,'20:85','قال فانا قد فتنا قومك منۢ بعدك واضلهم السامرى');
INSERT INTO "uthmani_simple" VALUES(20,86,'20:86','فرجع موسى الى قومه غضبن اسفا قال يقوم الم يعدكم ربكم وعدا حسنا افطال عليكم العهد ام اردتم ان يحل عليكم غضب من ربكم فاخلفتم موعدى');
INSERT INTO "uthmani_simple" VALUES(20,87,'20:87','قالوا۟ ما اخلفنا موعدك بملكنا ولكنا حملنا اوزارا من زينة القوم فقذفنها فكذلك القى السامرى');
INSERT INTO "uthmani_simple" VALUES(20,88,'20:88','فاخرج لهم عجلا جسدا له خوار فقالوا۟ هذا الهكم واله موسى فنسى');
INSERT INTO "uthmani_simple" VALUES(20,89,'20:89','افلا يرون الا يرجع اليهم قولا ولا يملك لهم ضرا ولا نفعا');
INSERT INTO "uthmani_simple" VALUES(20,90,'20:90','ولقد قال لهم هرون من قبل يقوم انما فتنتم به وان ربكم الرحمن فاتبعونى واطيعوا۟ امرى');
INSERT INTO "uthmani_simple" VALUES(20,91,'20:91','قالوا۟ لن نبرح عليه عكفين حتى يرجع الينا موسى');
INSERT INTO "uthmani_simple" VALUES(20,92,'20:92','قال يهرون ما منعك اذ رايتهم ضلوا۟');
INSERT INTO "uthmani_simple" VALUES(20,93,'20:93','الا تتبعن افعصيت امرى');
INSERT INTO "uthmani_simple" VALUES(20,94,'20:94','قال يبنؤم لا تاخذ بلحيتى ولا براسى انى خشيت ان تقول فرقت بين بنى اسرءيل ولم ترقب قولى');
INSERT INTO "uthmani_simple" VALUES(20,95,'20:95','قال فما خطبك يسمرى');
INSERT INTO "uthmani_simple" VALUES(20,96,'20:96','قال بصرت بما لم يبصروا۟ به فقبضت قبضة من اثر الرسول فنبذتها وكذلك سولت لى نفسى');
INSERT INTO "uthmani_simple" VALUES(20,97,'20:97','قال فاذهب فان لك فى الحيوة ان تقول لا مساس وان لك موعدا لن تخلفه وانظر الى الهك الذى ظلت عليه عاكفا لنحرقنه ثم لننسفنه فى اليم نسفا');
INSERT INTO "uthmani_simple" VALUES(20,98,'20:98','انما الهكم الله الذى لا اله الا هو وسع كل شىء علما');
INSERT INTO "uthmani_simple" VALUES(20,99,'20:99','كذلك نقص عليك من انۢباء ما قد سبق وقد ءاتينك من لدنا ذكرا');
INSERT INTO "uthmani_simple" VALUES(20,100,'20:100','من اعرض عنه فانه يحمل يوم القيمة وزرا');
INSERT INTO "uthmani_simple" VALUES(20,101,'20:101','خلدين فيه وساء لهم يوم القيمة حملا');
INSERT INTO "uthmani_simple" VALUES(20,102,'20:102','يوم ينفخ فى الصور ونحشر المجرمين يومئذ زرقا');
INSERT INTO "uthmani_simple" VALUES(20,103,'20:103','يتخفتون بينهم ان لبثتم الا عشرا');
INSERT INTO "uthmani_simple" VALUES(20,104,'20:104','نحن اعلم بما يقولون اذ يقول امثلهم طريقة ان لبثتم الا يوما');
INSERT INTO "uthmani_simple" VALUES(20,105,'20:105','ويسـلونك عن الجبال فقل ينسفها ربى نسفا');
INSERT INTO "uthmani_simple" VALUES(20,106,'20:106','فيذرها قاعا صفصفا');
INSERT INTO "uthmani_simple" VALUES(20,107,'20:107','لا ترى فيها عوجا ولا امتا');
INSERT INTO "uthmani_simple" VALUES(20,108,'20:108','يومئذ يتبعون الداعى لا عوج له وخشعت الاصوات للرحمن فلا تسمع الا همسا');
INSERT INTO "uthmani_simple" VALUES(20,109,'20:109','يومئذ لا تنفع الشفعة الا من اذن له الرحمن ورضى له قولا');
INSERT INTO "uthmani_simple" VALUES(20,110,'20:110','يعلم ما بين ايديهم وما خلفهم ولا يحيطون به علما');
INSERT INTO "uthmani_simple" VALUES(20,111,'20:111','وعنت الوجوه للحى القيوم وقد خاب من حمل ظلما');
INSERT INTO "uthmani_simple" VALUES(20,112,'20:112','ومن يعمل من الصلحت وهو مؤمن فلا يخاف ظلما ولا هضما');
INSERT INTO "uthmani_simple" VALUES(20,113,'20:113','وكذلك انزلنه قرءانا عربيا وصرفنا فيه من الوعيد لعلهم يتقون او يحدث لهم ذكرا');
INSERT INTO "uthmani_simple" VALUES(20,114,'20:114','فتعلى الله الملك الحق ولا تعجل بالقرءان من قبل ان يقضى اليك وحيه وقل رب زدنى علما');
INSERT INTO "uthmani_simple" VALUES(20,115,'20:115','ولقد عهدنا الى ءادم من قبل فنسى ولم نجد له عزما');
INSERT INTO "uthmani_simple" VALUES(20,116,'20:116','واذ قلنا للملئكة اسجدوا۟ لءادم فسجدوا۟ الا ابليس ابى');
INSERT INTO "uthmani_simple" VALUES(20,117,'20:117','فقلنا يـادم ان هذا عدو لك ولزوجك فلا يخرجنكما من الجنة فتشقى');
INSERT INTO "uthmani_simple" VALUES(20,118,'20:118','ان لك الا تجوع فيها ولا تعرى');
INSERT INTO "uthmani_simple" VALUES(20,119,'20:119','وانك لا تظمؤا۟ فيها ولا تضحى');
INSERT INTO "uthmani_simple" VALUES(20,120,'20:120','فوسوس اليه الشيطن قال يـادم هل ادلك على شجرة الخلد وملك لا يبلى');
INSERT INTO "uthmani_simple" VALUES(20,121,'20:121','فاكلا منها فبدت لهما سوءتهما وطفقا يخصفان عليهما من ورق الجنة وعصى ءادم ربه فغوى');
INSERT INTO "uthmani_simple" VALUES(20,122,'20:122','ثم اجتبه ربه فتاب عليه وهدى');
INSERT INTO "uthmani_simple" VALUES(20,123,'20:123','قال اهبطا منها جميعۢا بعضكم لبعض عدو فاما ياتينكم منى هدى فمن اتبع هداى فلا يضل ولا يشقى');
INSERT INTO "uthmani_simple" VALUES(20,124,'20:124','ومن اعرض عن ذكرى فان له معيشة ضنكا ونحشره يوم القيمة اعمى');
INSERT INTO "uthmani_simple" VALUES(20,125,'20:125','قال رب لم حشرتنى اعمى وقد كنت بصيرا');
INSERT INTO "uthmani_simple" VALUES(20,126,'20:126','قال كذلك اتتك ءايتنا فنسيتها وكذلك اليوم تنسى');
INSERT INTO "uthmani_simple" VALUES(20,127,'20:127','وكذلك نجزى من اسرف ولم يؤمنۢ بـايت ربه ولعذاب الءاخرة اشد وابقى');
INSERT INTO "uthmani_simple" VALUES(20,128,'20:128','افلم يهد لهم كم اهلكنا قبلهم من القرون يمشون فى مسكنهم ان فى ذلك لءايت لاو۟لى النهى');
INSERT INTO "uthmani_simple" VALUES(20,129,'20:129','ولولا كلمة سبقت من ربك لكان لزاما واجل مسمى');
INSERT INTO "uthmani_simple" VALUES(20,130,'20:130','فاصبر على ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل غروبها ومن ءانائ اليل فسبح واطراف النهار لعلك ترضى');
INSERT INTO "uthmani_simple" VALUES(20,131,'20:131','ولا تمدن عينيك الى ما متعنا به ازوجا منهم زهرة الحيوة الدنيا لنفتنهم فيه ورزق ربك خير وابقى');
INSERT INTO "uthmani_simple" VALUES(20,132,'20:132','وامر اهلك بالصلوة واصطبر عليها لا نسـلك رزقا نحن نرزقك والعقبة للتقوى');
INSERT INTO "uthmani_simple" VALUES(20,133,'20:133','وقالوا۟ لولا ياتينا بـاية من ربه اولم تاتهم بينة ما فى الصحف الاولى');
INSERT INTO "uthmani_simple" VALUES(20,134,'20:134','ولو انا اهلكنهم بعذاب من قبله لقالوا۟ ربنا لولا ارسلت الينا رسولا فنتبع ءايتك من قبل ان نذل ونخزى');
INSERT INTO "uthmani_simple" VALUES(20,135,'20:135','قل كل متربص فتربصوا۟ فستعلمون من اصحب الصرط السوى ومن اهتدى');
INSERT INTO "uthmani_simple" VALUES(21,1,'21:1','اقترب للناس حسابهم وهم فى غفلة معرضون');
INSERT INTO "uthmani_simple" VALUES(21,2,'21:2','ما ياتيهم من ذكر من ربهم محدث الا استمعوه وهم يلعبون');
INSERT INTO "uthmani_simple" VALUES(21,3,'21:3','لاهية قلوبهم واسروا۟ النجوى الذين ظلموا۟ هل هذا الا بشر مثلكم افتاتون السحر وانتم تبصرون');
INSERT INTO "uthmani_simple" VALUES(21,4,'21:4','قال ربى يعلم القول فى السماء والارض وهو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(21,5,'21:5','بل قالوا۟ اضغث احلمۭ بل افترىه بل هو شاعر فلياتنا بـاية كما ارسل الاولون');
INSERT INTO "uthmani_simple" VALUES(21,6,'21:6','ما ءامنت قبلهم من قرية اهلكنها افهم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(21,7,'21:7','وما ارسلنا قبلك الا رجالا نوحى اليهم فسـلوا۟ اهل الذكر ان كنتم لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(21,8,'21:8','وما جعلنهم جسدا لا ياكلون الطعام وما كانوا۟ خلدين');
INSERT INTO "uthmani_simple" VALUES(21,9,'21:9','ثم صدقنهم الوعد فانجينهم ومن نشاء واهلكنا المسرفين');
INSERT INTO "uthmani_simple" VALUES(21,10,'21:10','لقد انزلنا اليكم كتبا فيه ذكركم افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(21,11,'21:11','وكم قصمنا من قرية كانت ظالمة وانشانا بعدها قوما ءاخرين');
INSERT INTO "uthmani_simple" VALUES(21,12,'21:12','فلما احسوا۟ باسنا اذا هم منها يركضون');
INSERT INTO "uthmani_simple" VALUES(21,13,'21:13','لا تركضوا۟ وارجعوا۟ الى ما اترفتم فيه ومسكنكم لعلكم تسـلون');
INSERT INTO "uthmani_simple" VALUES(21,14,'21:14','قالوا۟ يويلنا انا كنا ظلمين');
INSERT INTO "uthmani_simple" VALUES(21,15,'21:15','فما زالت تلك دعوىهم حتى جعلنهم حصيدا خمدين');
INSERT INTO "uthmani_simple" VALUES(21,16,'21:16','وما خلقنا السماء والارض وما بينهما لعبين');
INSERT INTO "uthmani_simple" VALUES(21,17,'21:17','لو اردنا ان نتخذ لهوا لاتخذنه من لدنا ان كنا فعلين');
INSERT INTO "uthmani_simple" VALUES(21,18,'21:18','بل نقذف بالحق على البطل فيدمغه فاذا هو زاهق ولكم الويل مما تصفون');
INSERT INTO "uthmani_simple" VALUES(21,19,'21:19','وله من فى السموت والارض ومن عنده لا يستكبرون عن عبادته ولا يستحسرون');
INSERT INTO "uthmani_simple" VALUES(21,20,'21:20','يسبحون اليل والنهار لا يفترون');
INSERT INTO "uthmani_simple" VALUES(21,21,'21:21','ام اتخذوا۟ ءالهة من الارض هم ينشرون');
INSERT INTO "uthmani_simple" VALUES(21,22,'21:22','لو كان فيهما ءالهة الا الله لفسدتا فسبحن الله رب العرش عما يصفون');
INSERT INTO "uthmani_simple" VALUES(21,23,'21:23','لا يسـل عما يفعل وهم يسـلون');
INSERT INTO "uthmani_simple" VALUES(21,24,'21:24','ام اتخذوا۟ من دونه ءالهة قل هاتوا۟ برهنكم هذا ذكر من معى وذكر من قبلى بل اكثرهم لا يعلمون الحق فهم معرضون');
INSERT INTO "uthmani_simple" VALUES(21,25,'21:25','وما ارسلنا من قبلك من رسول الا نوحى اليه انه لا اله الا انا۠ فاعبدون');
INSERT INTO "uthmani_simple" VALUES(21,26,'21:26','وقالوا۟ اتخذ الرحمن ولدا سبحنه بل عباد مكرمون');
INSERT INTO "uthmani_simple" VALUES(21,27,'21:27','لا يسبقونه بالقول وهم بامره يعملون');
INSERT INTO "uthmani_simple" VALUES(21,28,'21:28','يعلم ما بين ايديهم وما خلفهم ولا يشفعون الا لمن ارتضى وهم من خشيته مشفقون');
INSERT INTO "uthmani_simple" VALUES(21,29,'21:29','ومن يقل منهم انى اله من دونه فذلك نجزيه جهنم كذلك نجزى الظلمين');
INSERT INTO "uthmani_simple" VALUES(21,30,'21:30','اولم ير الذين كفروا۟ ان السموت والارض كانتا رتقا ففتقنهما وجعلنا من الماء كل شىء حى افلا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(21,31,'21:31','وجعلنا فى الارض روسى ان تميد بهم وجعلنا فيها فجاجا سبلا لعلهم يهتدون');
INSERT INTO "uthmani_simple" VALUES(21,32,'21:32','وجعلنا السماء سقفا محفوظا وهم عن ءايتها معرضون');
INSERT INTO "uthmani_simple" VALUES(21,33,'21:33','وهو الذى خلق اليل والنهار والشمس والقمر كل فى فلك يسبحون');
INSERT INTO "uthmani_simple" VALUES(21,34,'21:34','وما جعلنا لبشر من قبلك الخلد افاي۟ن مت فهم الخلدون');
INSERT INTO "uthmani_simple" VALUES(21,35,'21:35','كل نفس ذائقة الموت ونبلوكم بالشر والخير فتنة والينا ترجعون');
INSERT INTO "uthmani_simple" VALUES(21,36,'21:36','واذا رءاك الذين كفروا۟ ان يتخذونك الا هزوا اهذا الذى يذكر ءالهتكم وهم بذكر الرحمن هم كفرون');
INSERT INTO "uthmani_simple" VALUES(21,37,'21:37','خلق الانسن من عجل ساو۟ريكم ءايتى فلا تستعجلون');
INSERT INTO "uthmani_simple" VALUES(21,38,'21:38','ويقولون متى هذا الوعد ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(21,39,'21:39','لو يعلم الذين كفروا۟ حين لا يكفون عن وجوههم النار ولا عن ظهورهم ولا هم ينصرون');
INSERT INTO "uthmani_simple" VALUES(21,40,'21:40','بل تاتيهم بغتة فتبهتهم فلا يستطيعون ردها ولا هم ينظرون');
INSERT INTO "uthmani_simple" VALUES(21,41,'21:41','ولقد استهزئ برسل من قبلك فحاق بالذين سخروا۟ منهم ما كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(21,42,'21:42','قل من يكلؤكم باليل والنهار من الرحمن بل هم عن ذكر ربهم معرضون');
INSERT INTO "uthmani_simple" VALUES(21,43,'21:43','ام لهم ءالهة تمنعهم من دوننا لا يستطيعون نصر انفسهم ولا هم منا يصحبون');
INSERT INTO "uthmani_simple" VALUES(21,44,'21:44','بل متعنا هؤلاء وءاباءهم حتى طال عليهم العمر افلا يرون انا ناتى الارض ننقصها من اطرافها افهم الغلبون');
INSERT INTO "uthmani_simple" VALUES(21,45,'21:45','قل انما انذركم بالوحى ولا يسمع الصم الدعاء اذا ما ينذرون');
INSERT INTO "uthmani_simple" VALUES(21,46,'21:46','ولئن مستهم نفحة من عذاب ربك ليقولن يويلنا انا كنا ظلمين');
INSERT INTO "uthmani_simple" VALUES(21,47,'21:47','ونضع الموزين القسط ليوم القيمة فلا تظلم نفس شيـا وان كان مثقال حبة من خردل اتينا بها وكفى بنا حسبين');
INSERT INTO "uthmani_simple" VALUES(21,48,'21:48','ولقد ءاتينا موسى وهرون الفرقان وضياء وذكرا للمتقين');
INSERT INTO "uthmani_simple" VALUES(21,49,'21:49','الذين يخشون ربهم بالغيب وهم من الساعة مشفقون');
INSERT INTO "uthmani_simple" VALUES(21,50,'21:50','وهذا ذكر مبارك انزلنه افانتم له منكرون');
INSERT INTO "uthmani_simple" VALUES(21,51,'21:51','ولقد ءاتينا ابرهيم رشده من قبل وكنا به علمين');
INSERT INTO "uthmani_simple" VALUES(21,52,'21:52','اذ قال لابيه وقومه ما هذه التماثيل التى انتم لها عكفون');
INSERT INTO "uthmani_simple" VALUES(21,53,'21:53','قالوا۟ وجدنا ءاباءنا لها عبدين');
INSERT INTO "uthmani_simple" VALUES(21,54,'21:54','قال لقد كنتم انتم وءاباؤكم فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(21,55,'21:55','قالوا۟ اجئتنا بالحق ام انت من اللعبين');
INSERT INTO "uthmani_simple" VALUES(21,56,'21:56','قال بل ربكم رب السموت والارض الذى فطرهن وانا۠ على ذلكم من الشهدين');
INSERT INTO "uthmani_simple" VALUES(21,57,'21:57','وتالله لاكيدن اصنمكم بعد ان تولوا۟ مدبرين');
INSERT INTO "uthmani_simple" VALUES(21,58,'21:58','فجعلهم جذذا الا كبيرا لهم لعلهم اليه يرجعون');
INSERT INTO "uthmani_simple" VALUES(21,59,'21:59','قالوا۟ من فعل هذا بـالهتنا انه لمن الظلمين');
INSERT INTO "uthmani_simple" VALUES(21,60,'21:60','قالوا۟ سمعنا فتى يذكرهم يقال له ابرهيم');
INSERT INTO "uthmani_simple" VALUES(21,61,'21:61','قالوا۟ فاتوا۟ به على اعين الناس لعلهم يشهدون');
INSERT INTO "uthmani_simple" VALUES(21,62,'21:62','قالوا۟ ءانت فعلت هذا بـالهتنا يابرهيم');
INSERT INTO "uthmani_simple" VALUES(21,63,'21:63','قال بل فعله كبيرهم هذا فسـلوهم ان كانوا۟ ينطقون');
INSERT INTO "uthmani_simple" VALUES(21,64,'21:64','فرجعوا۟ الى انفسهم فقالوا۟ انكم انتم الظلمون');
INSERT INTO "uthmani_simple" VALUES(21,65,'21:65','ثم نكسوا۟ على رءوسهم لقد علمت ما هؤلاء ينطقون');
INSERT INTO "uthmani_simple" VALUES(21,66,'21:66','قال افتعبدون من دون الله ما لا ينفعكم شيـا ولا يضركم');
INSERT INTO "uthmani_simple" VALUES(21,67,'21:67','اف لكم ولما تعبدون من دون الله افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(21,68,'21:68','قالوا۟ حرقوه وانصروا۟ ءالهتكم ان كنتم فعلين');
INSERT INTO "uthmani_simple" VALUES(21,69,'21:69','قلنا ينار كونى بردا وسلما على ابرهيم');
INSERT INTO "uthmani_simple" VALUES(21,70,'21:70','وارادوا۟ به كيدا فجعلنهم الاخسرين');
INSERT INTO "uthmani_simple" VALUES(21,71,'21:71','ونجينه ولوطا الى الارض التى بركنا فيها للعلمين');
INSERT INTO "uthmani_simple" VALUES(21,72,'21:72','ووهبنا له اسحق ويعقوب نافلة وكلا جعلنا صلحين');
INSERT INTO "uthmani_simple" VALUES(21,73,'21:73','وجعلنهم ائمة يهدون بامرنا واوحينا اليهم فعل الخيرت واقام الصلوة وايتاء الزكوة وكانوا۟ لنا عبدين');
INSERT INTO "uthmani_simple" VALUES(21,74,'21:74','ولوطا ءاتينه حكما وعلما ونجينه من القرية التى كانت تعمل الخبئث انهم كانوا۟ قوم سوء فسقين');
INSERT INTO "uthmani_simple" VALUES(21,75,'21:75','وادخلنه فى رحمتنا انه من الصلحين');
INSERT INTO "uthmani_simple" VALUES(21,76,'21:76','ونوحا اذ نادى من قبل فاستجبنا له فنجينه واهله من الكرب العظيم');
INSERT INTO "uthmani_simple" VALUES(21,77,'21:77','ونصرنه من القوم الذين كذبوا۟ بـايتنا انهم كانوا۟ قوم سوء فاغرقنهم اجمعين');
INSERT INTO "uthmani_simple" VALUES(21,78,'21:78','وداود وسليمن اذ يحكمان فى الحرث اذ نفشت فيه غنم القوم وكنا لحكمهم شهدين');
INSERT INTO "uthmani_simple" VALUES(21,79,'21:79','ففهمنها سليمن وكلا ءاتينا حكما وعلما وسخرنا مع داود الجبال يسبحن والطير وكنا فعلين');
INSERT INTO "uthmani_simple" VALUES(21,80,'21:80','وعلمنه صنعة لبوس لكم لتحصنكم منۢ باسكم فهل انتم شكرون');
INSERT INTO "uthmani_simple" VALUES(21,81,'21:81','ولسليمن الريح عاصفة تجرى بامره الى الارض التى بركنا فيها وكنا بكل شىء علمين');
INSERT INTO "uthmani_simple" VALUES(21,82,'21:82','ومن الشيطين من يغوصون له ويعملون عملا دون ذلك وكنا لهم حفظين');
INSERT INTO "uthmani_simple" VALUES(21,83,'21:83','وايوب اذ نادى ربه انى مسنى الضر وانت ارحم الرحمين');
INSERT INTO "uthmani_simple" VALUES(21,84,'21:84','فاستجبنا له فكشفنا ما به من ضر وءاتينه اهله ومثلهم معهم رحمة من عندنا وذكرى للعبدين');
INSERT INTO "uthmani_simple" VALUES(21,85,'21:85','واسمعيل وادريس وذا الكفل كل من الصبرين');
INSERT INTO "uthmani_simple" VALUES(21,86,'21:86','وادخلنهم فى رحمتنا انهم من الصلحين');
INSERT INTO "uthmani_simple" VALUES(21,87,'21:87','وذا النون اذ ذهب مغضبا فظن ان لن نقدر عليه فنادى فى الظلمت ان لا اله الا انت سبحنك انى كنت من الظلمين');
INSERT INTO "uthmani_simple" VALUES(21,88,'21:88','فاستجبنا له ونجينه من الغم وكذلك نۨجى المؤمنين');
INSERT INTO "uthmani_simple" VALUES(21,89,'21:89','وزكريا اذ نادى ربه رب لا تذرنى فردا وانت خير الورثين');
INSERT INTO "uthmani_simple" VALUES(21,90,'21:90','فاستجبنا له ووهبنا له يحيى واصلحنا له زوجه انهم كانوا۟ يسرعون فى الخيرت ويدعوننا رغبا ورهبا وكانوا۟ لنا خشعين');
INSERT INTO "uthmani_simple" VALUES(21,91,'21:91','والتى احصنت فرجها فنفخنا فيها من روحنا وجعلنها وابنها ءاية للعلمين');
INSERT INTO "uthmani_simple" VALUES(21,92,'21:92','ان هذه امتكم امة وحدة وانا۠ ربكم فاعبدون');
INSERT INTO "uthmani_simple" VALUES(21,93,'21:93','وتقطعوا۟ امرهم بينهم كل الينا رجعون');
INSERT INTO "uthmani_simple" VALUES(21,94,'21:94','فمن يعمل من الصلحت وهو مؤمن فلا كفران لسعيه وانا له كتبون');
INSERT INTO "uthmani_simple" VALUES(21,95,'21:95','وحرم على قرية اهلكنها انهم لا يرجعون');
INSERT INTO "uthmani_simple" VALUES(21,96,'21:96','حتى اذا فتحت ياجوج وماجوج وهم من كل حدب ينسلون');
INSERT INTO "uthmani_simple" VALUES(21,97,'21:97','واقترب الوعد الحق فاذا هى شخصة ابصر الذين كفروا۟ يويلنا قد كنا فى غفلة من هذا بل كنا ظلمين');
INSERT INTO "uthmani_simple" VALUES(21,98,'21:98','انكم وما تعبدون من دون الله حصب جهنم انتم لها وردون');
INSERT INTO "uthmani_simple" VALUES(21,99,'21:99','لو كان هؤلاء ءالهة ما وردوها وكل فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(21,100,'21:100','لهم فيها زفير وهم فيها لا يسمعون');
INSERT INTO "uthmani_simple" VALUES(21,101,'21:101','ان الذين سبقت لهم منا الحسنى او۟لئك عنها مبعدون');
INSERT INTO "uthmani_simple" VALUES(21,102,'21:102','لا يسمعون حسيسها وهم فى ما اشتهت انفسهم خلدون');
INSERT INTO "uthmani_simple" VALUES(21,103,'21:103','لا يحزنهم الفزع الاكبر وتتلقىهم الملئكة هذا يومكم الذى كنتم توعدون');
INSERT INTO "uthmani_simple" VALUES(21,104,'21:104','يوم نطوى السماء كطى السجل للكتب كما بدانا اول خلق نعيده وعدا علينا انا كنا فعلين');
INSERT INTO "uthmani_simple" VALUES(21,105,'21:105','ولقد كتبنا فى الزبور منۢ بعد الذكر ان الارض يرثها عبادى الصلحون');
INSERT INTO "uthmani_simple" VALUES(21,106,'21:106','ان فى هذا لبلغا لقوم عبدين');
INSERT INTO "uthmani_simple" VALUES(21,107,'21:107','وما ارسلنك الا رحمة للعلمين');
INSERT INTO "uthmani_simple" VALUES(21,108,'21:108','قل انما يوحى الى انما الهكم اله وحد فهل انتم مسلمون');
INSERT INTO "uthmani_simple" VALUES(21,109,'21:109','فان تولوا۟ فقل ءاذنتكم على سواء وان ادرى اقريب ام بعيد ما توعدون');
INSERT INTO "uthmani_simple" VALUES(21,110,'21:110','انه يعلم الجهر من القول ويعلم ما تكتمون');
INSERT INTO "uthmani_simple" VALUES(21,111,'21:111','وان ادرى لعله فتنة لكم ومتع الى حين');
INSERT INTO "uthmani_simple" VALUES(21,112,'21:112','قل رب احكم بالحق وربنا الرحمن المستعان على ما تصفون');
INSERT INTO "uthmani_simple" VALUES(22,1,'22:1','يايها الناس اتقوا۟ ربكم ان زلزلة الساعة شىء عظيم');
INSERT INTO "uthmani_simple" VALUES(22,2,'22:2','يوم ترونها تذهل كل مرضعة عما ارضعت وتضع كل ذات حمل حملها وترى الناس سكرى وما هم بسكرى ولكن عذاب الله شديد');
INSERT INTO "uthmani_simple" VALUES(22,3,'22:3','ومن الناس من يجدل فى الله بغير علم ويتبع كل شيطن مريد');
INSERT INTO "uthmani_simple" VALUES(22,4,'22:4','كتب عليه انه من تولاه فانه يضله ويهديه الى عذاب السعير');
INSERT INTO "uthmani_simple" VALUES(22,5,'22:5','يايها الناس ان كنتم فى ريب من البعث فانا خلقنكم من تراب ثم من نطفة ثم من علقة ثم من مضغة مخلقة وغير مخلقة لنبين لكم ونقر فى الارحام ما نشاء الى اجل مسمى ثم نخرجكم طفلا ثم لتبلغوا۟ اشدكم ومنكم من يتوفى ومنكم من يرد الى ارذل العمر لكيلا يعلم منۢ بعد علم شيـا وترى الارض هامدة فاذا انزلنا عليها الماء اهتزت وربت وانۢبتت من كل زوجۭ بهيج');
INSERT INTO "uthmani_simple" VALUES(22,6,'22:6','ذلك بان الله هو الحق وانه يحى الموتى وانه على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(22,7,'22:7','وان الساعة ءاتية لا ريب فيها وان الله يبعث من فى القبور');
INSERT INTO "uthmani_simple" VALUES(22,8,'22:8','ومن الناس من يجدل فى الله بغير علم ولا هدى ولا كتب منير');
INSERT INTO "uthmani_simple" VALUES(22,9,'22:9','ثانى عطفه ليضل عن سبيل الله له فى الدنيا خزى ونذيقه يوم القيمة عذاب الحريق');
INSERT INTO "uthmani_simple" VALUES(22,10,'22:10','ذلك بما قدمت يداك وان الله ليس بظلم للعبيد');
INSERT INTO "uthmani_simple" VALUES(22,11,'22:11','ومن الناس من يعبد الله على حرف فان اصابه خير اطمان به وان اصابته فتنة انقلب على وجهه خسر الدنيا والءاخرة ذلك هو الخسران المبين');
INSERT INTO "uthmani_simple" VALUES(22,12,'22:12','يدعوا۟ من دون الله ما لا يضره وما لا ينفعه ذلك هو الضلل البعيد');
INSERT INTO "uthmani_simple" VALUES(22,13,'22:13','يدعوا۟ لمن ضره اقرب من نفعه لبئس المولى ولبئس العشير');
INSERT INTO "uthmani_simple" VALUES(22,14,'22:14','ان الله يدخل الذين ءامنوا۟ وعملوا۟ الصلحت جنت تجرى من تحتها الانهر ان الله يفعل ما يريد');
INSERT INTO "uthmani_simple" VALUES(22,15,'22:15','من كان يظن ان لن ينصره الله فى الدنيا والءاخرة فليمدد بسبب الى السماء ثم ليقطع فلينظر هل يذهبن كيده ما يغيظ');
INSERT INTO "uthmani_simple" VALUES(22,16,'22:16','وكذلك انزلنه ءايتۭ بينت وان الله يهدى من يريد');
INSERT INTO "uthmani_simple" VALUES(22,17,'22:17','ان الذين ءامنوا۟ والذين هادوا۟ والصبـين والنصرى والمجوس والذين اشركوا۟ ان الله يفصل بينهم يوم القيمة ان الله على كل شىء شهيد');
INSERT INTO "uthmani_simple" VALUES(22,18,'22:18','الم تر ان الله يسجد له من فى السموت ومن فى الارض والشمس والقمر والنجوم والجبال والشجر والدواب وكثير من الناس وكثير حق عليه العذاب ومن يهن الله فما له من مكرم ان الله يفعل ما يشاء');
INSERT INTO "uthmani_simple" VALUES(22,19,'22:19','هذان خصمان اختصموا۟ فى ربهم فالذين كفروا۟ قطعت لهم ثياب من نار يصب من فوق رءوسهم الحميم');
INSERT INTO "uthmani_simple" VALUES(22,20,'22:20','يصهر به ما فى بطونهم والجلود');
INSERT INTO "uthmani_simple" VALUES(22,21,'22:21','ولهم مقمع من حديد');
INSERT INTO "uthmani_simple" VALUES(22,22,'22:22','كلما ارادوا۟ ان يخرجوا۟ منها من غم اعيدوا۟ فيها وذوقوا۟ عذاب الحريق');
INSERT INTO "uthmani_simple" VALUES(22,23,'22:23','ان الله يدخل الذين ءامنوا۟ وعملوا۟ الصلحت جنت تجرى من تحتها الانهر يحلون فيها من اساور من ذهب ولؤلؤا ولباسهم فيها حرير');
INSERT INTO "uthmani_simple" VALUES(22,24,'22:24','وهدوا۟ الى الطيب من القول وهدوا۟ الى صرط الحميد');
INSERT INTO "uthmani_simple" VALUES(22,25,'22:25','ان الذين كفروا۟ ويصدون عن سبيل الله والمسجد الحرام الذى جعلنه للناس سواء العكف فيه والباد ومن يرد فيه بالحادۭ بظلم نذقه من عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(22,26,'22:26','واذ بوانا لابرهيم مكان البيت ان لا تشرك بى شيـا وطهر بيتى للطائفين والقائمين والركع السجود');
INSERT INTO "uthmani_simple" VALUES(22,27,'22:27','واذن فى الناس بالحج ياتوك رجالا وعلى كل ضامر ياتين من كل فج عميق');
INSERT INTO "uthmani_simple" VALUES(22,28,'22:28','ليشهدوا۟ منفع لهم ويذكروا۟ اسم الله فى ايام معلومت على ما رزقهم منۢ بهيمة الانعم فكلوا۟ منها واطعموا۟ البائس الفقير');
INSERT INTO "uthmani_simple" VALUES(22,29,'22:29','ثم ليقضوا۟ تفثهم وليوفوا۟ نذورهم وليطوفوا۟ بالبيت العتيق');
INSERT INTO "uthmani_simple" VALUES(22,30,'22:30','ذلك ومن يعظم حرمت الله فهو خير له عند ربه واحلت لكم الانعم الا ما يتلى عليكم فاجتنبوا۟ الرجس من الاوثن واجتنبوا۟ قول الزور');
INSERT INTO "uthmani_simple" VALUES(22,31,'22:31','حنفاء لله غير مشركين به ومن يشرك بالله فكانما خر من السماء فتخطفه الطير او تهوى به الريح فى مكان سحيق');
INSERT INTO "uthmani_simple" VALUES(22,32,'22:32','ذلك ومن يعظم شعئر الله فانها من تقوى القلوب');
INSERT INTO "uthmani_simple" VALUES(22,33,'22:33','لكم فيها منفع الى اجل مسمى ثم محلها الى البيت العتيق');
INSERT INTO "uthmani_simple" VALUES(22,34,'22:34','ولكل امة جعلنا منسكا ليذكروا۟ اسم الله على ما رزقهم منۢ بهيمة الانعم فالهكم اله وحد فله اسلموا۟ وبشر المخبتين');
INSERT INTO "uthmani_simple" VALUES(22,35,'22:35','الذين اذا ذكر الله وجلت قلوبهم والصبرين على ما اصابهم والمقيمى الصلوة ومما رزقنهم ينفقون');
INSERT INTO "uthmani_simple" VALUES(22,36,'22:36','والبدن جعلنها لكم من شعئر الله لكم فيها خير فاذكروا۟ اسم الله عليها صواف فاذا وجبت جنوبها فكلوا۟ منها واطعموا۟ القانع والمعتر كذلك سخرنها لكم لعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(22,37,'22:37','لن ينال الله لحومها ولا دماؤها ولكن يناله التقوى منكم كذلك سخرها لكم لتكبروا۟ الله على ما هدىكم وبشر المحسنين');
INSERT INTO "uthmani_simple" VALUES(22,38,'22:38','ان الله يدفع عن الذين ءامنوا۟ ان الله لا يحب كل خوان كفور');
INSERT INTO "uthmani_simple" VALUES(22,39,'22:39','اذن للذين يقتلون بانهم ظلموا۟ وان الله على نصرهم لقدير');
INSERT INTO "uthmani_simple" VALUES(22,40,'22:40','الذين اخرجوا۟ من ديرهم بغير حق الا ان يقولوا۟ ربنا الله ولولا دفع الله الناس بعضهم ببعض لهدمت صومع وبيع وصلوت ومسجد يذكر فيها اسم الله كثيرا ولينصرن الله من ينصره ان الله لقوى عزيز');
INSERT INTO "uthmani_simple" VALUES(22,41,'22:41','الذين ان مكنهم فى الارض اقاموا۟ الصلوة وءاتوا۟ الزكوة وامروا۟ بالمعروف ونهوا۟ عن المنكر ولله عقبة الامور');
INSERT INTO "uthmani_simple" VALUES(22,42,'22:42','وان يكذبوك فقد كذبت قبلهم قوم نوح وعاد وثمود');
INSERT INTO "uthmani_simple" VALUES(22,43,'22:43','وقوم ابرهيم وقوم لوط');
INSERT INTO "uthmani_simple" VALUES(22,44,'22:44','واصحب مدين وكذب موسى فامليت للكفرين ثم اخذتهم فكيف كان نكير');
INSERT INTO "uthmani_simple" VALUES(22,45,'22:45','فكاين من قرية اهلكنها وهى ظالمة فهى خاوية على عروشها وبئر معطلة وقصر مشيد');
INSERT INTO "uthmani_simple" VALUES(22,46,'22:46','افلم يسيروا۟ فى الارض فتكون لهم قلوب يعقلون بها او ءاذان يسمعون بها فانها لا تعمى الابصر ولكن تعمى القلوب التى فى الصدور');
INSERT INTO "uthmani_simple" VALUES(22,47,'22:47','ويستعجلونك بالعذاب ولن يخلف الله وعده وان يوما عند ربك كالف سنة مما تعدون');
INSERT INTO "uthmani_simple" VALUES(22,48,'22:48','وكاين من قرية امليت لها وهى ظالمة ثم اخذتها والى المصير');
INSERT INTO "uthmani_simple" VALUES(22,49,'22:49','قل يايها الناس انما انا۠ لكم نذير مبين');
INSERT INTO "uthmani_simple" VALUES(22,50,'22:50','فالذين ءامنوا۟ وعملوا۟ الصلحت لهم مغفرة ورزق كريم');
INSERT INTO "uthmani_simple" VALUES(22,51,'22:51','والذين سعوا۟ فى ءايتنا معجزين او۟لئك اصحب الجحيم');
INSERT INTO "uthmani_simple" VALUES(22,52,'22:52','وما ارسلنا من قبلك من رسول ولا نبى الا اذا تمنى القى الشيطن فى امنيته فينسخ الله ما يلقى الشيطن ثم يحكم الله ءايته والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(22,53,'22:53','ليجعل ما يلقى الشيطن فتنة للذين فى قلوبهم مرض والقاسية قلوبهم وان الظلمين لفى شقاقۭ بعيد');
INSERT INTO "uthmani_simple" VALUES(22,54,'22:54','وليعلم الذين اوتوا۟ العلم انه الحق من ربك فيؤمنوا۟ به فتخبت له قلوبهم وان الله لهاد الذين ءامنوا۟ الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(22,55,'22:55','ولا يزال الذين كفروا۟ فى مرية منه حتى تاتيهم الساعة بغتة او ياتيهم عذاب يوم عقيم');
INSERT INTO "uthmani_simple" VALUES(22,56,'22:56','الملك يومئذ لله يحكم بينهم فالذين ءامنوا۟ وعملوا۟ الصلحت فى جنت النعيم');
INSERT INTO "uthmani_simple" VALUES(22,57,'22:57','والذين كفروا۟ وكذبوا۟ بـايتنا فاو۟لئك لهم عذاب مهين');
INSERT INTO "uthmani_simple" VALUES(22,58,'22:58','والذين هاجروا۟ فى سبيل الله ثم قتلوا۟ او ماتوا۟ ليرزقنهم الله رزقا حسنا وان الله لهو خير الرزقين');
INSERT INTO "uthmani_simple" VALUES(22,59,'22:59','ليدخلنهم مدخلا يرضونه وان الله لعليم حليم');
INSERT INTO "uthmani_simple" VALUES(22,60,'22:60','ذلك ومن عاقب بمثل ما عوقب به ثم بغى عليه لينصرنه الله ان الله لعفو غفور');
INSERT INTO "uthmani_simple" VALUES(22,61,'22:61','ذلك بان الله يولج اليل فى النهار ويولج النهار فى اليل وان الله سميعۢ بصير');
INSERT INTO "uthmani_simple" VALUES(22,62,'22:62','ذلك بان الله هو الحق وان ما يدعون من دونه هو البطل وان الله هو العلى الكبير');
INSERT INTO "uthmani_simple" VALUES(22,63,'22:63','الم تر ان الله انزل من السماء ماء فتصبح الارض مخضرة ان الله لطيف خبير');
INSERT INTO "uthmani_simple" VALUES(22,64,'22:64','له ما فى السموت وما فى الارض وان الله لهو الغنى الحميد');
INSERT INTO "uthmani_simple" VALUES(22,65,'22:65','الم تر ان الله سخر لكم ما فى الارض والفلك تجرى فى البحر بامره ويمسك السماء ان تقع على الارض الا باذنه ان الله بالناس لرءوف رحيم');
INSERT INTO "uthmani_simple" VALUES(22,66,'22:66','وهو الذى احياكم ثم يميتكم ثم يحييكم ان الانسن لكفور');
INSERT INTO "uthmani_simple" VALUES(22,67,'22:67','لكل امة جعلنا منسكا هم ناسكوه فلا ينزعنك فى الامر وادع الى ربك انك لعلى هدى مستقيم');
INSERT INTO "uthmani_simple" VALUES(22,68,'22:68','وان جدلوك فقل الله اعلم بما تعملون');
INSERT INTO "uthmani_simple" VALUES(22,69,'22:69','الله يحكم بينكم يوم القيمة فيما كنتم فيه تختلفون');
INSERT INTO "uthmani_simple" VALUES(22,70,'22:70','الم تعلم ان الله يعلم ما فى السماء والارض ان ذلك فى كتب ان ذلك على الله يسير');
INSERT INTO "uthmani_simple" VALUES(22,71,'22:71','ويعبدون من دون الله ما لم ينزل به سلطنا وما ليس لهم به علم وما للظلمين من نصير');
INSERT INTO "uthmani_simple" VALUES(22,72,'22:72','واذا تتلى عليهم ءايتنا بينت تعرف فى وجوه الذين كفروا۟ المنكر يكادون يسطون بالذين يتلون عليهم ءايتنا قل افانبئكم بشر من ذلكم النار وعدها الله الذين كفروا۟ وبئس المصير');
INSERT INTO "uthmani_simple" VALUES(22,73,'22:73','يايها الناس ضرب مثل فاستمعوا۟ له ان الذين تدعون من دون الله لن يخلقوا۟ ذبابا ولو اجتمعوا۟ له وان يسلبهم الذباب شيـا لا يستنقذوه منه ضعف الطالب والمطلوب');
INSERT INTO "uthmani_simple" VALUES(22,74,'22:74','ما قدروا۟ الله حق قدره ان الله لقوى عزيز');
INSERT INTO "uthmani_simple" VALUES(22,75,'22:75','الله يصطفى من الملئكة رسلا ومن الناس ان الله سميعۢ بصير');
INSERT INTO "uthmani_simple" VALUES(22,76,'22:76','يعلم ما بين ايديهم وما خلفهم والى الله ترجع الامور');
INSERT INTO "uthmani_simple" VALUES(22,77,'22:77','يايها الذين ءامنوا۟ اركعوا۟ واسجدوا۟ واعبدوا۟ ربكم وافعلوا۟ الخير لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(22,78,'22:78','وجهدوا۟ فى الله حق جهاده هو اجتبىكم وما جعل عليكم فى الدين من حرج ملة ابيكم ابرهيم هو سمىكم المسلمين من قبل وفى هذا ليكون الرسول شهيدا عليكم وتكونوا۟ شهداء على الناس فاقيموا۟ الصلوة وءاتوا۟ الزكوة واعتصموا۟ بالله هو مولىكم فنعم المولى ونعم النصير');
INSERT INTO "uthmani_simple" VALUES(23,1,'23:1','قد افلح المؤمنون');
INSERT INTO "uthmani_simple" VALUES(23,2,'23:2','الذين هم فى صلاتهم خشعون');
INSERT INTO "uthmani_simple" VALUES(23,3,'23:3','والذين هم عن اللغو معرضون');
INSERT INTO "uthmani_simple" VALUES(23,4,'23:4','والذين هم للزكوة فعلون');
INSERT INTO "uthmani_simple" VALUES(23,5,'23:5','والذين هم لفروجهم حفظون');
INSERT INTO "uthmani_simple" VALUES(23,6,'23:6','الا على ازوجهم او ما ملكت ايمنهم فانهم غير ملومين');
INSERT INTO "uthmani_simple" VALUES(23,7,'23:7','فمن ابتغى وراء ذلك فاو۟لئك هم العادون');
INSERT INTO "uthmani_simple" VALUES(23,8,'23:8','والذين هم لامنتهم وعهدهم رعون');
INSERT INTO "uthmani_simple" VALUES(23,9,'23:9','والذين هم على صلوتهم يحافظون');
INSERT INTO "uthmani_simple" VALUES(23,10,'23:10','او۟لئك هم الورثون');
INSERT INTO "uthmani_simple" VALUES(23,11,'23:11','الذين يرثون الفردوس هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(23,12,'23:12','ولقد خلقنا الانسن من سللة من طين');
INSERT INTO "uthmani_simple" VALUES(23,13,'23:13','ثم جعلنه نطفة فى قرار مكين');
INSERT INTO "uthmani_simple" VALUES(23,14,'23:14','ثم خلقنا النطفة علقة فخلقنا العلقة مضغة فخلقنا المضغة عظما فكسونا العظم لحما ثم انشانه خلقا ءاخر فتبارك الله احسن الخلقين');
INSERT INTO "uthmani_simple" VALUES(23,15,'23:15','ثم انكم بعد ذلك لميتون');
INSERT INTO "uthmani_simple" VALUES(23,16,'23:16','ثم انكم يوم القيمة تبعثون');
INSERT INTO "uthmani_simple" VALUES(23,17,'23:17','ولقد خلقنا فوقكم سبع طرائق وما كنا عن الخلق غفلين');
INSERT INTO "uthmani_simple" VALUES(23,18,'23:18','وانزلنا من السماء ماءۢ بقدر فاسكنه فى الارض وانا على ذهابۭ به لقدرون');
INSERT INTO "uthmani_simple" VALUES(23,19,'23:19','فانشانا لكم به جنت من نخيل واعنب لكم فيها فوكه كثيرة ومنها تاكلون');
INSERT INTO "uthmani_simple" VALUES(23,20,'23:20','وشجرة تخرج من طور سيناء تنۢبت بالدهن وصبغ للءاكلين');
INSERT INTO "uthmani_simple" VALUES(23,21,'23:21','وان لكم فى الانعم لعبرة نسقيكم مما فى بطونها ولكم فيها منفع كثيرة ومنها تاكلون');
INSERT INTO "uthmani_simple" VALUES(23,22,'23:22','وعليها وعلى الفلك تحملون');
INSERT INTO "uthmani_simple" VALUES(23,23,'23:23','ولقد ارسلنا نوحا الى قومه فقال يقوم اعبدوا۟ الله ما لكم من اله غيره افلا تتقون');
INSERT INTO "uthmani_simple" VALUES(23,24,'23:24','فقال الملؤا۟ الذين كفروا۟ من قومه ما هذا الا بشر مثلكم يريد ان يتفضل عليكم ولو شاء الله لانزل ملئكة ما سمعنا بهذا فى ءابائنا الاولين');
INSERT INTO "uthmani_simple" VALUES(23,25,'23:25','ان هو الا رجلۢ به جنة فتربصوا۟ به حتى حين');
INSERT INTO "uthmani_simple" VALUES(23,26,'23:26','قال رب انصرنى بما كذبون');
INSERT INTO "uthmani_simple" VALUES(23,27,'23:27','فاوحينا اليه ان اصنع الفلك باعيننا ووحينا فاذا جاء امرنا وفار التنور فاسلك فيها من كل زوجين اثنين واهلك الا من سبق عليه القول منهم ولا تخطبنى فى الذين ظلموا۟ انهم مغرقون');
INSERT INTO "uthmani_simple" VALUES(23,28,'23:28','فاذا استويت انت ومن معك على الفلك فقل الحمد لله الذى نجىنا من القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(23,29,'23:29','وقل رب انزلنى منزلا مباركا وانت خير المنزلين');
INSERT INTO "uthmani_simple" VALUES(23,30,'23:30','ان فى ذلك لءايت وان كنا لمبتلين');
INSERT INTO "uthmani_simple" VALUES(23,31,'23:31','ثم انشانا منۢ بعدهم قرنا ءاخرين');
INSERT INTO "uthmani_simple" VALUES(23,32,'23:32','فارسلنا فيهم رسولا منهم ان اعبدوا۟ الله ما لكم من اله غيره افلا تتقون');
INSERT INTO "uthmani_simple" VALUES(23,33,'23:33','وقال الملا من قومه الذين كفروا۟ وكذبوا۟ بلقاء الءاخرة واترفنهم فى الحيوة الدنيا ما هذا الا بشر مثلكم ياكل مما تاكلون منه ويشرب مما تشربون');
INSERT INTO "uthmani_simple" VALUES(23,34,'23:34','ولئن اطعتم بشرا مثلكم انكم اذا لخسرون');
INSERT INTO "uthmani_simple" VALUES(23,35,'23:35','ايعدكم انكم اذا متم وكنتم ترابا وعظما انكم مخرجون');
INSERT INTO "uthmani_simple" VALUES(23,36,'23:36','هيهات هيهات لما توعدون');
INSERT INTO "uthmani_simple" VALUES(23,37,'23:37','ان هى الا حياتنا الدنيا نموت ونحيا وما نحن بمبعوثين');
INSERT INTO "uthmani_simple" VALUES(23,38,'23:38','ان هو الا رجل افترى على الله كذبا وما نحن له بمؤمنين');
INSERT INTO "uthmani_simple" VALUES(23,39,'23:39','قال رب انصرنى بما كذبون');
INSERT INTO "uthmani_simple" VALUES(23,40,'23:40','قال عما قليل ليصبحن ندمين');
INSERT INTO "uthmani_simple" VALUES(23,41,'23:41','فاخذتهم الصيحة بالحق فجعلنهم غثاء فبعدا للقوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(23,42,'23:42','ثم انشانا منۢ بعدهم قرونا ءاخرين');
INSERT INTO "uthmani_simple" VALUES(23,43,'23:43','ما تسبق من امة اجلها وما يستـخرون');
INSERT INTO "uthmani_simple" VALUES(23,44,'23:44','ثم ارسلنا رسلنا تترا كل ما جاء امة رسولها كذبوه فاتبعنا بعضهم بعضا وجعلنهم احاديث فبعدا لقوم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(23,45,'23:45','ثم ارسلنا موسى واخاه هرون بـايتنا وسلطن مبين');
INSERT INTO "uthmani_simple" VALUES(23,46,'23:46','الى فرعون وملاي۟ه فاستكبروا۟ وكانوا۟ قوما عالين');
INSERT INTO "uthmani_simple" VALUES(23,47,'23:47','فقالوا۟ انؤمن لبشرين مثلنا وقومهما لنا عبدون');
INSERT INTO "uthmani_simple" VALUES(23,48,'23:48','فكذبوهما فكانوا۟ من المهلكين');
INSERT INTO "uthmani_simple" VALUES(23,49,'23:49','ولقد ءاتينا موسى الكتب لعلهم يهتدون');
INSERT INTO "uthmani_simple" VALUES(23,50,'23:50','وجعلنا ابن مريم وامه ءاية وءاوينهما الى ربوة ذات قرار ومعين');
INSERT INTO "uthmani_simple" VALUES(23,51,'23:51','يايها الرسل كلوا۟ من الطيبت واعملوا۟ صلحا انى بما تعملون عليم');
INSERT INTO "uthmani_simple" VALUES(23,52,'23:52','وان هذه امتكم امة وحدة وانا۠ ربكم فاتقون');
INSERT INTO "uthmani_simple" VALUES(23,53,'23:53','فتقطعوا۟ امرهم بينهم زبرا كل حزبۭ بما لديهم فرحون');
INSERT INTO "uthmani_simple" VALUES(23,54,'23:54','فذرهم فى غمرتهم حتى حين');
INSERT INTO "uthmani_simple" VALUES(23,55,'23:55','ايحسبون انما نمدهم به من مال وبنين');
INSERT INTO "uthmani_simple" VALUES(23,56,'23:56','نسارع لهم فى الخيرت بل لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(23,57,'23:57','ان الذين هم من خشية ربهم مشفقون');
INSERT INTO "uthmani_simple" VALUES(23,58,'23:58','والذين هم بـايت ربهم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(23,59,'23:59','والذين هم بربهم لا يشركون');
INSERT INTO "uthmani_simple" VALUES(23,60,'23:60','والذين يؤتون ما ءاتوا۟ وقلوبهم وجلة انهم الى ربهم رجعون');
INSERT INTO "uthmani_simple" VALUES(23,61,'23:61','او۟لئك يسرعون فى الخيرت وهم لها سبقون');
INSERT INTO "uthmani_simple" VALUES(23,62,'23:62','ولا نكلف نفسا الا وسعها ولدينا كتب ينطق بالحق وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(23,63,'23:63','بل قلوبهم فى غمرة من هذا ولهم اعمل من دون ذلك هم لها عملون');
INSERT INTO "uthmani_simple" VALUES(23,64,'23:64','حتى اذا اخذنا مترفيهم بالعذاب اذا هم يجـرون');
INSERT INTO "uthmani_simple" VALUES(23,65,'23:65','لا تجـروا۟ اليوم انكم منا لا تنصرون');
INSERT INTO "uthmani_simple" VALUES(23,66,'23:66','قد كانت ءايتى تتلى عليكم فكنتم على اعقبكم تنكصون');
INSERT INTO "uthmani_simple" VALUES(23,67,'23:67','مستكبرين به سمرا تهجرون');
INSERT INTO "uthmani_simple" VALUES(23,68,'23:68','افلم يدبروا۟ القول ام جاءهم ما لم يات ءاباءهم الاولين');
INSERT INTO "uthmani_simple" VALUES(23,69,'23:69','ام لم يعرفوا۟ رسولهم فهم له منكرون');
INSERT INTO "uthmani_simple" VALUES(23,70,'23:70','ام يقولون به جنةۢ بل جاءهم بالحق واكثرهم للحق كرهون');
INSERT INTO "uthmani_simple" VALUES(23,71,'23:71','ولو اتبع الحق اهواءهم لفسدت السموت والارض ومن فيهن بل اتينهم بذكرهم فهم عن ذكرهم معرضون');
INSERT INTO "uthmani_simple" VALUES(23,72,'23:72','ام تسـلهم خرجا فخراج ربك خير وهو خير الرزقين');
INSERT INTO "uthmani_simple" VALUES(23,73,'23:73','وانك لتدعوهم الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(23,74,'23:74','وان الذين لا يؤمنون بالءاخرة عن الصرط لنكبون');
INSERT INTO "uthmani_simple" VALUES(23,75,'23:75','ولو رحمنهم وكشفنا ما بهم من ضر للجوا۟ فى طغينهم يعمهون');
INSERT INTO "uthmani_simple" VALUES(23,76,'23:76','ولقد اخذنهم بالعذاب فما استكانوا۟ لربهم وما يتضرعون');
INSERT INTO "uthmani_simple" VALUES(23,77,'23:77','حتى اذا فتحنا عليهم بابا ذا عذاب شديد اذا هم فيه مبلسون');
INSERT INTO "uthmani_simple" VALUES(23,78,'23:78','وهو الذى انشا لكم السمع والابصر والافـدة قليلا ما تشكرون');
INSERT INTO "uthmani_simple" VALUES(23,79,'23:79','وهو الذى ذراكم فى الارض واليه تحشرون');
INSERT INTO "uthmani_simple" VALUES(23,80,'23:80','وهو الذى يحى ويميت وله اختلف اليل والنهار افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(23,81,'23:81','بل قالوا۟ مثل ما قال الاولون');
INSERT INTO "uthmani_simple" VALUES(23,82,'23:82','قالوا۟ اءذا متنا وكنا ترابا وعظما اءنا لمبعوثون');
INSERT INTO "uthmani_simple" VALUES(23,83,'23:83','لقد وعدنا نحن وءاباؤنا هذا من قبل ان هذا الا اسطير الاولين');
INSERT INTO "uthmani_simple" VALUES(23,84,'23:84','قل لمن الارض ومن فيها ان كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(23,85,'23:85','سيقولون لله قل افلا تذكرون');
INSERT INTO "uthmani_simple" VALUES(23,86,'23:86','قل من رب السموت السبع ورب العرش العظيم');
INSERT INTO "uthmani_simple" VALUES(23,87,'23:87','سيقولون لله قل افلا تتقون');
INSERT INTO "uthmani_simple" VALUES(23,88,'23:88','قل منۢ بيده ملكوت كل شىء وهو يجير ولا يجار عليه ان كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(23,89,'23:89','سيقولون لله قل فانى تسحرون');
INSERT INTO "uthmani_simple" VALUES(23,90,'23:90','بل اتينهم بالحق وانهم لكذبون');
INSERT INTO "uthmani_simple" VALUES(23,91,'23:91','ما اتخذ الله من ولد وما كان معه من اله اذا لذهب كل الهۭ بما خلق ولعلا بعضهم على بعض سبحن الله عما يصفون');
INSERT INTO "uthmani_simple" VALUES(23,92,'23:92','علم الغيب والشهدة فتعلى عما يشركون');
INSERT INTO "uthmani_simple" VALUES(23,93,'23:93','قل رب اما ترينى ما يوعدون');
INSERT INTO "uthmani_simple" VALUES(23,94,'23:94','رب فلا تجعلنى فى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(23,95,'23:95','وانا على ان نريك ما نعدهم لقدرون');
INSERT INTO "uthmani_simple" VALUES(23,96,'23:96','ادفع بالتى هى احسن السيئة نحن اعلم بما يصفون');
INSERT INTO "uthmani_simple" VALUES(23,97,'23:97','وقل رب اعوذ بك من همزت الشيطين');
INSERT INTO "uthmani_simple" VALUES(23,98,'23:98','واعوذ بك رب ان يحضرون');
INSERT INTO "uthmani_simple" VALUES(23,99,'23:99','حتى اذا جاء احدهم الموت قال رب ارجعون');
INSERT INTO "uthmani_simple" VALUES(23,100,'23:100','لعلى اعمل صلحا فيما تركت كلا انها كلمة هو قائلها ومن ورائهم برزخ الى يوم يبعثون');
INSERT INTO "uthmani_simple" VALUES(23,101,'23:101','فاذا نفخ فى الصور فلا انساب بينهم يومئذ ولا يتساءلون');
INSERT INTO "uthmani_simple" VALUES(23,102,'23:102','فمن ثقلت موزينه فاو۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(23,103,'23:103','ومن خفت موزينه فاو۟لئك الذين خسروا۟ انفسهم فى جهنم خلدون');
INSERT INTO "uthmani_simple" VALUES(23,104,'23:104','تلفح وجوههم النار وهم فيها كلحون');
INSERT INTO "uthmani_simple" VALUES(23,105,'23:105','الم تكن ءايتى تتلى عليكم فكنتم بها تكذبون');
INSERT INTO "uthmani_simple" VALUES(23,106,'23:106','قالوا۟ ربنا غلبت علينا شقوتنا وكنا قوما ضالين');
INSERT INTO "uthmani_simple" VALUES(23,107,'23:107','ربنا اخرجنا منها فان عدنا فانا ظلمون');
INSERT INTO "uthmani_simple" VALUES(23,108,'23:108','قال اخسـوا۟ فيها ولا تكلمون');
INSERT INTO "uthmani_simple" VALUES(23,109,'23:109','انه كان فريق من عبادى يقولون ربنا ءامنا فاغفر لنا وارحمنا وانت خير الرحمين');
INSERT INTO "uthmani_simple" VALUES(23,110,'23:110','فاتخذتموهم سخريا حتى انسوكم ذكرى وكنتم منهم تضحكون');
INSERT INTO "uthmani_simple" VALUES(23,111,'23:111','انى جزيتهم اليوم بما صبروا۟ انهم هم الفائزون');
INSERT INTO "uthmani_simple" VALUES(23,112,'23:112','قل كم لبثتم فى الارض عدد سنين');
INSERT INTO "uthmani_simple" VALUES(23,113,'23:113','قالوا۟ لبثنا يوما او بعض يوم فسـل العادين');
INSERT INTO "uthmani_simple" VALUES(23,114,'23:114','قل ان لبثتم الا قليلا لو انكم كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(23,115,'23:115','افحسبتم انما خلقنكم عبثا وانكم الينا لا ترجعون');
INSERT INTO "uthmani_simple" VALUES(23,116,'23:116','فتعلى الله الملك الحق لا اله الا هو رب العرش الكريم');
INSERT INTO "uthmani_simple" VALUES(23,117,'23:117','ومن يدع مع الله الها ءاخر لا برهن له به فانما حسابه عند ربه انه لا يفلح الكفرون');
INSERT INTO "uthmani_simple" VALUES(23,118,'23:118','وقل رب اغفر وارحم وانت خير الرحمين');
INSERT INTO "uthmani_simple" VALUES(24,1,'24:1','سورة انزلنها وفرضنها وانزلنا فيها ءايتۭ بينت لعلكم تذكرون');
INSERT INTO "uthmani_simple" VALUES(24,2,'24:2','الزانية والزانى فاجلدوا۟ كل وحد منهما ما۟ئة جلدة ولا تاخذكم بهما رافة فى دين الله ان كنتم تؤمنون بالله واليوم الءاخر وليشهد عذابهما طائفة من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(24,3,'24:3','الزانى لا ينكح الا زانية او مشركة والزانية لا ينكحها الا زان او مشرك وحرم ذلك على المؤمنين');
INSERT INTO "uthmani_simple" VALUES(24,4,'24:4','والذين يرمون المحصنت ثم لم ياتوا۟ باربعة شهداء فاجلدوهم ثمنين جلدة ولا تقبلوا۟ لهم شهدة ابدا واو۟لئك هم الفسقون');
INSERT INTO "uthmani_simple" VALUES(24,5,'24:5','الا الذين تابوا۟ منۢ بعد ذلك واصلحوا۟ فان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(24,6,'24:6','والذين يرمون ازوجهم ولم يكن لهم شهداء الا انفسهم فشهدة احدهم اربع شهدتۭ بالله انه لمن الصدقين');
INSERT INTO "uthmani_simple" VALUES(24,7,'24:7','والخمسة ان لعنت الله عليه ان كان من الكذبين');
INSERT INTO "uthmani_simple" VALUES(24,8,'24:8','ويدرؤا۟ عنها العذاب ان تشهد اربع شهدتۭ بالله انه لمن الكذبين');
INSERT INTO "uthmani_simple" VALUES(24,9,'24:9','والخمسة ان غضب الله عليها ان كان من الصدقين');
INSERT INTO "uthmani_simple" VALUES(24,10,'24:10','ولولا فضل الله عليكم ورحمته وان الله تواب حكيم');
INSERT INTO "uthmani_simple" VALUES(24,11,'24:11','ان الذين جاءو بالافك عصبة منكم لا تحسبوه شرا لكم بل هو خير لكم لكل امرئ منهم ما اكتسب من الاثم والذى تولى كبره منهم له عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(24,12,'24:12','لولا اذ سمعتموه ظن المؤمنون والمؤمنت بانفسهم خيرا وقالوا۟ هذا افك مبين');
INSERT INTO "uthmani_simple" VALUES(24,13,'24:13','لولا جاءو عليه باربعة شهداء فاذ لم ياتوا۟ بالشهداء فاو۟لئك عند الله هم الكذبون');
INSERT INTO "uthmani_simple" VALUES(24,14,'24:14','ولولا فضل الله عليكم ورحمته فى الدنيا والءاخرة لمسكم فى ما افضتم فيه عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(24,15,'24:15','اذ تلقونه بالسنتكم وتقولون بافواهكم ما ليس لكم به علم وتحسبونه هينا وهو عند الله عظيم');
INSERT INTO "uthmani_simple" VALUES(24,16,'24:16','ولولا اذ سمعتموه قلتم ما يكون لنا ان نتكلم بهذا سبحنك هذا بهتن عظيم');
INSERT INTO "uthmani_simple" VALUES(24,17,'24:17','يعظكم الله ان تعودوا۟ لمثله ابدا ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(24,18,'24:18','ويبين الله لكم الءايت والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(24,19,'24:19','ان الذين يحبون ان تشيع الفحشة فى الذين ءامنوا۟ لهم عذاب اليم فى الدنيا والءاخرة والله يعلم وانتم لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(24,20,'24:20','ولولا فضل الله عليكم ورحمته وان الله رءوف رحيم');
INSERT INTO "uthmani_simple" VALUES(24,21,'24:21','يايها الذين ءامنوا۟ لا تتبعوا۟ خطوت الشيطن ومن يتبع خطوت الشيطن فانه يامر بالفحشاء والمنكر ولولا فضل الله عليكم ورحمته ما زكى منكم من احد ابدا ولكن الله يزكى من يشاء والله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(24,22,'24:22','ولا ياتل او۟لوا۟ الفضل منكم والسعة ان يؤتوا۟ او۟لى القربى والمسكين والمهجرين فى سبيل الله وليعفوا۟ وليصفحوا۟ الا تحبون ان يغفر الله لكم والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(24,23,'24:23','ان الذين يرمون المحصنت الغفلت المؤمنت لعنوا۟ فى الدنيا والءاخرة ولهم عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(24,24,'24:24','يوم تشهد عليهم السنتهم وايديهم وارجلهم بما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(24,25,'24:25','يومئذ يوفيهم الله دينهم الحق ويعلمون ان الله هو الحق المبين');
INSERT INTO "uthmani_simple" VALUES(24,26,'24:26','الخبيثت للخبيثين والخبيثون للخبيثت والطيبت للطيبين والطيبون للطيبت او۟لئك مبرءون مما يقولون لهم مغفرة ورزق كريم');
INSERT INTO "uthmani_simple" VALUES(24,27,'24:27','يايها الذين ءامنوا۟ لا تدخلوا۟ بيوتا غير بيوتكم حتى تستانسوا۟ وتسلموا۟ على اهلها ذلكم خير لكم لعلكم تذكرون');
INSERT INTO "uthmani_simple" VALUES(24,28,'24:28','فان لم تجدوا۟ فيها احدا فلا تدخلوها حتى يؤذن لكم وان قيل لكم ارجعوا۟ فارجعوا۟ هو ازكى لكم والله بما تعملون عليم');
INSERT INTO "uthmani_simple" VALUES(24,29,'24:29','ليس عليكم جناح ان تدخلوا۟ بيوتا غير مسكونة فيها متع لكم والله يعلم ما تبدون وما تكتمون');
INSERT INTO "uthmani_simple" VALUES(24,30,'24:30','قل للمؤمنين يغضوا۟ من ابصرهم ويحفظوا۟ فروجهم ذلك ازكى لهم ان الله خبيرۢ بما يصنعون');
INSERT INTO "uthmani_simple" VALUES(24,31,'24:31','وقل للمؤمنت يغضضن من ابصرهن ويحفظن فروجهن ولا يبدين زينتهن الا ما ظهر منها وليضربن بخمرهن على جيوبهن ولا يبدين زينتهن الا لبعولتهن او ءابائهن او ءاباء بعولتهن او ابنائهن او ابناء بعولتهن او اخونهن او بنى اخونهن او بنى اخوتهن او نسائهن او ما ملكت ايمنهن او التبعين غير او۟لى الاربة من الرجال او الطفل الذين لم يظهروا۟ على عورت النساء ولا يضربن بارجلهن ليعلم ما يخفين من زينتهن وتوبوا۟ الى الله جميعا ايه المؤمنون لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(24,32,'24:32','وانكحوا۟ الايمى منكم والصلحين من عبادكم وامائكم ان يكونوا۟ فقراء يغنهم الله من فضله والله وسع عليم');
INSERT INTO "uthmani_simple" VALUES(24,33,'24:33','وليستعفف الذين لا يجدون نكاحا حتى يغنيهم الله من فضله والذين يبتغون الكتب مما ملكت ايمنكم فكاتبوهم ان علمتم فيهم خيرا وءاتوهم من مال الله الذى ءاتىكم ولا تكرهوا۟ فتيتكم على البغاء ان اردن تحصنا لتبتغوا۟ عرض الحيوة الدنيا ومن يكرههن فان الله منۢ بعد اكرههن غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(24,34,'24:34','ولقد انزلنا اليكم ءايت مبينت ومثلا من الذين خلوا۟ من قبلكم وموعظة للمتقين');
INSERT INTO "uthmani_simple" VALUES(24,35,'24:35','الله نور السموت والارض مثل نوره كمشكوة فيها مصباح المصباح فى زجاجة الزجاجة كانها كوكب درى يوقد من شجرة مبركة زيتونة لا شرقية ولا غربية يكاد زيتها يضىء ولو لم تمسسه نار نور على نور يهدى الله لنوره من يشاء ويضرب الله الامثل للناس والله بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(24,36,'24:36','فى بيوت اذن الله ان ترفع ويذكر فيها اسمه يسبح له فيها بالغدو والءاصال');
INSERT INTO "uthmani_simple" VALUES(24,37,'24:37','رجال لا تلهيهم تجرة ولا بيع عن ذكر الله واقام الصلوة وايتاء الزكوة يخافون يوما تتقلب فيه القلوب والابصر');
INSERT INTO "uthmani_simple" VALUES(24,38,'24:38','ليجزيهم الله احسن ما عملوا۟ ويزيدهم من فضله والله يرزق من يشاء بغير حساب');
INSERT INTO "uthmani_simple" VALUES(24,39,'24:39','والذين كفروا۟ اعملهم كسرابۭ بقيعة يحسبه الظمـان ماء حتى اذا جاءه لم يجده شيـا ووجد الله عنده فوفىه حسابه والله سريع الحساب');
INSERT INTO "uthmani_simple" VALUES(24,40,'24:40','او كظلمت فى بحر لجى يغشىه موج من فوقه موج من فوقه سحاب ظلمتۢ بعضها فوق بعض اذا اخرج يده لم يكد يرىها ومن لم يجعل الله له نورا فما له من نور');
INSERT INTO "uthmani_simple" VALUES(24,41,'24:41','الم تر ان الله يسبح له من فى السموت والارض والطير صفت كل قد علم صلاته وتسبيحه والله عليمۢ بما يفعلون');
INSERT INTO "uthmani_simple" VALUES(24,42,'24:42','ولله ملك السموت والارض والى الله المصير');
INSERT INTO "uthmani_simple" VALUES(24,43,'24:43','الم تر ان الله يزجى سحابا ثم يؤلف بينه ثم يجعله ركاما فترى الودق يخرج من خلله وينزل من السماء من جبال فيها منۢ برد فيصيب به من يشاء ويصرفه عن من يشاء يكاد سنا برقه يذهب بالابصر');
INSERT INTO "uthmani_simple" VALUES(24,44,'24:44','يقلب الله اليل والنهار ان فى ذلك لعبرة لاو۟لى الابصر');
INSERT INTO "uthmani_simple" VALUES(24,45,'24:45','والله خلق كل دابة من ماء فمنهم من يمشى على بطنه ومنهم من يمشى على رجلين ومنهم من يمشى على اربع يخلق الله ما يشاء ان الله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(24,46,'24:46','لقد انزلنا ءايت مبينت والله يهدى من يشاء الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(24,47,'24:47','ويقولون ءامنا بالله وبالرسول واطعنا ثم يتولى فريق منهم منۢ بعد ذلك وما او۟لئك بالمؤمنين');
INSERT INTO "uthmani_simple" VALUES(24,48,'24:48','واذا دعوا۟ الى الله ورسوله ليحكم بينهم اذا فريق منهم معرضون');
INSERT INTO "uthmani_simple" VALUES(24,49,'24:49','وان يكن لهم الحق ياتوا۟ اليه مذعنين');
INSERT INTO "uthmani_simple" VALUES(24,50,'24:50','افى قلوبهم مرض ام ارتابوا۟ ام يخافون ان يحيف الله عليهم ورسوله بل او۟لئك هم الظلمون');
INSERT INTO "uthmani_simple" VALUES(24,51,'24:51','انما كان قول المؤمنين اذا دعوا۟ الى الله ورسوله ليحكم بينهم ان يقولوا۟ سمعنا واطعنا واو۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(24,52,'24:52','ومن يطع الله ورسوله ويخش الله ويتقه فاو۟لئك هم الفائزون');
INSERT INTO "uthmani_simple" VALUES(24,53,'24:53','واقسموا۟ بالله جهد ايمنهم لئن امرتهم ليخرجن قل لا تقسموا۟ طاعة معروفة ان الله خبيرۢ بما تعملون');
INSERT INTO "uthmani_simple" VALUES(24,54,'24:54','قل اطيعوا۟ الله واطيعوا۟ الرسول فان تولوا۟ فانما عليه ما حمل وعليكم ما حملتم وان تطيعوه تهتدوا۟ وما على الرسول الا البلغ المبين');
INSERT INTO "uthmani_simple" VALUES(24,55,'24:55','وعد الله الذين ءامنوا۟ منكم وعملوا۟ الصلحت ليستخلفنهم فى الارض كما استخلف الذين من قبلهم وليمكنن لهم دينهم الذى ارتضى لهم وليبدلنهم منۢ بعد خوفهم امنا يعبدوننى لا يشركون بى شيـا ومن كفر بعد ذلك فاو۟لئك هم الفسقون');
INSERT INTO "uthmani_simple" VALUES(24,56,'24:56','واقيموا۟ الصلوة وءاتوا۟ الزكوة واطيعوا۟ الرسول لعلكم ترحمون');
INSERT INTO "uthmani_simple" VALUES(24,57,'24:57','لا تحسبن الذين كفروا۟ معجزين فى الارض وماوىهم النار ولبئس المصير');
INSERT INTO "uthmani_simple" VALUES(24,58,'24:58','يايها الذين ءامنوا۟ ليستـذنكم الذين ملكت ايمنكم والذين لم يبلغوا۟ الحلم منكم ثلث مرت من قبل صلوة الفجر وحين تضعون ثيابكم من الظهيرة ومنۢ بعد صلوة العشاء ثلث عورت لكم ليس عليكم ولا عليهم جناحۢ بعدهن طوفون عليكم بعضكم على بعض كذلك يبين الله لكم الءايت والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(24,59,'24:59','واذا بلغ الاطفل منكم الحلم فليستـذنوا۟ كما استـذن الذين من قبلهم كذلك يبين الله لكم ءايته والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(24,60,'24:60','والقوعد من النساء التى لا يرجون نكاحا فليس عليهن جناح ان يضعن ثيابهن غير متبرجتۭ بزينة وان يستعففن خير لهن والله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(24,61,'24:61','ليس على الاعمى حرج ولا على الاعرج حرج ولا على المريض حرج ولا على انفسكم ان تاكلوا۟ منۢ بيوتكم او بيوت ءابائكم او بيوت امهتكم او بيوت اخونكم او بيوت اخوتكم او بيوت اعممكم او بيوت عمتكم او بيوت اخولكم او بيوت خلتكم او ما ملكتم مفاتحه او صديقكم ليس عليكم جناح ان تاكلوا۟ جميعا او اشتاتا فاذا دخلتم بيوتا فسلموا۟ على انفسكم تحية من عند الله مبركة طيبة كذلك يبين الله لكم الءايت لعلكم تعقلون');
INSERT INTO "uthmani_simple" VALUES(24,62,'24:62','انما المؤمنون الذين ءامنوا۟ بالله ورسوله واذا كانوا۟ معه على امر جامع لم يذهبوا۟ حتى يستـذنوه ان الذين يستـذنونك او۟لئك الذين يؤمنون بالله ورسوله فاذا استـذنوك لبعض شانهم فاذن لمن شئت منهم واستغفر لهم الله ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(24,63,'24:63','لا تجعلوا۟ دعاء الرسول بينكم كدعاء بعضكم بعضا قد يعلم الله الذين يتسللون منكم لواذا فليحذر الذين يخالفون عن امره ان تصيبهم فتنة او يصيبهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(24,64,'24:64','الا ان لله ما فى السموت والارض قد يعلم ما انتم عليه ويوم يرجعون اليه فينبئهم بما عملوا۟ والله بكل شىء عليمۢ');
INSERT INTO "uthmani_simple" VALUES(25,1,'25:1','تبارك الذى نزل الفرقان على عبده ليكون للعلمين نذيرا');
INSERT INTO "uthmani_simple" VALUES(25,2,'25:2','الذى له ملك السموت والارض ولم يتخذ ولدا ولم يكن له شريك فى الملك وخلق كل شىء فقدره تقديرا');
INSERT INTO "uthmani_simple" VALUES(25,3,'25:3','واتخذوا۟ من دونه ءالهة لا يخلقون شيـا وهم يخلقون ولا يملكون لانفسهم ضرا ولا نفعا ولا يملكون موتا ولا حيوة ولا نشورا');
INSERT INTO "uthmani_simple" VALUES(25,4,'25:4','وقال الذين كفروا۟ ان هذا الا افك افترىه واعانه عليه قوم ءاخرون فقد جاءو ظلما وزورا');
INSERT INTO "uthmani_simple" VALUES(25,5,'25:5','وقالوا۟ اسطير الاولين اكتتبها فهى تملى عليه بكرة واصيلا');
INSERT INTO "uthmani_simple" VALUES(25,6,'25:6','قل انزله الذى يعلم السر فى السموت والارض انه كان غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(25,7,'25:7','وقالوا۟ مال هذا الرسول ياكل الطعام ويمشى فى الاسواق لولا انزل اليه ملك فيكون معه نذيرا');
INSERT INTO "uthmani_simple" VALUES(25,8,'25:8','او يلقى اليه كنز او تكون له جنة ياكل منها وقال الظلمون ان تتبعون الا رجلا مسحورا');
INSERT INTO "uthmani_simple" VALUES(25,9,'25:9','انظر كيف ضربوا۟ لك الامثل فضلوا۟ فلا يستطيعون سبيلا');
INSERT INTO "uthmani_simple" VALUES(25,10,'25:10','تبارك الذى ان شاء جعل لك خيرا من ذلك جنت تجرى من تحتها الانهر ويجعل لك قصورۢا');
INSERT INTO "uthmani_simple" VALUES(25,11,'25:11','بل كذبوا۟ بالساعة واعتدنا لمن كذب بالساعة سعيرا');
INSERT INTO "uthmani_simple" VALUES(25,12,'25:12','اذا راتهم من مكانۭ بعيد سمعوا۟ لها تغيظا وزفيرا');
INSERT INTO "uthmani_simple" VALUES(25,13,'25:13','واذا القوا۟ منها مكانا ضيقا مقرنين دعوا۟ هنالك ثبورا');
INSERT INTO "uthmani_simple" VALUES(25,14,'25:14','لا تدعوا۟ اليوم ثبورا وحدا وادعوا۟ ثبورا كثيرا');
INSERT INTO "uthmani_simple" VALUES(25,15,'25:15','قل اذلك خير ام جنة الخلد التى وعد المتقون كانت لهم جزاء ومصيرا');
INSERT INTO "uthmani_simple" VALUES(25,16,'25:16','لهم فيها ما يشاءون خلدين كان على ربك وعدا مسـولا');
INSERT INTO "uthmani_simple" VALUES(25,17,'25:17','ويوم يحشرهم وما يعبدون من دون الله فيقول ءانتم اضللتم عبادى هؤلاء ام هم ضلوا۟ السبيل');
INSERT INTO "uthmani_simple" VALUES(25,18,'25:18','قالوا۟ سبحنك ما كان ينۢبغى لنا ان نتخذ من دونك من اولياء ولكن متعتهم وءاباءهم حتى نسوا۟ الذكر وكانوا۟ قومۢا بورا');
INSERT INTO "uthmani_simple" VALUES(25,19,'25:19','فقد كذبوكم بما تقولون فما تستطيعون صرفا ولا نصرا ومن يظلم منكم نذقه عذابا كبيرا');
INSERT INTO "uthmani_simple" VALUES(25,20,'25:20','وما ارسلنا قبلك من المرسلين الا انهم لياكلون الطعام ويمشون فى الاسواق وجعلنا بعضكم لبعض فتنة اتصبرون وكان ربك بصيرا');
INSERT INTO "uthmani_simple" VALUES(25,21,'25:21','وقال الذين لا يرجون لقاءنا لولا انزل علينا الملئكة او نرى ربنا لقد استكبروا۟ فى انفسهم وعتو عتوا كبيرا');
INSERT INTO "uthmani_simple" VALUES(25,22,'25:22','يوم يرون الملئكة لا بشرى يومئذ للمجرمين ويقولون حجرا محجورا');
INSERT INTO "uthmani_simple" VALUES(25,23,'25:23','وقدمنا الى ما عملوا۟ من عمل فجعلنه هباء منثورا');
INSERT INTO "uthmani_simple" VALUES(25,24,'25:24','اصحب الجنة يومئذ خير مستقرا واحسن مقيلا');
INSERT INTO "uthmani_simple" VALUES(25,25,'25:25','ويوم تشقق السماء بالغمم ونزل الملئكة تنزيلا');
INSERT INTO "uthmani_simple" VALUES(25,26,'25:26','الملك يومئذ الحق للرحمن وكان يوما على الكفرين عسيرا');
INSERT INTO "uthmani_simple" VALUES(25,27,'25:27','ويوم يعض الظالم على يديه يقول يليتنى اتخذت مع الرسول سبيلا');
INSERT INTO "uthmani_simple" VALUES(25,28,'25:28','يويلتى ليتنى لم اتخذ فلانا خليلا');
INSERT INTO "uthmani_simple" VALUES(25,29,'25:29','لقد اضلنى عن الذكر بعد اذ جاءنى وكان الشيطن للانسن خذولا');
INSERT INTO "uthmani_simple" VALUES(25,30,'25:30','وقال الرسول يرب ان قومى اتخذوا۟ هذا القرءان مهجورا');
INSERT INTO "uthmani_simple" VALUES(25,31,'25:31','وكذلك جعلنا لكل نبى عدوا من المجرمين وكفى بربك هاديا ونصيرا');
INSERT INTO "uthmani_simple" VALUES(25,32,'25:32','وقال الذين كفروا۟ لولا نزل عليه القرءان جملة وحدة كذلك لنثبت به فؤادك ورتلنه ترتيلا');
INSERT INTO "uthmani_simple" VALUES(25,33,'25:33','ولا ياتونك بمثل الا جئنك بالحق واحسن تفسيرا');
INSERT INTO "uthmani_simple" VALUES(25,34,'25:34','الذين يحشرون على وجوههم الى جهنم او۟لئك شر مكانا واضل سبيلا');
INSERT INTO "uthmani_simple" VALUES(25,35,'25:35','ولقد ءاتينا موسى الكتب وجعلنا معه اخاه هرون وزيرا');
INSERT INTO "uthmani_simple" VALUES(25,36,'25:36','فقلنا اذهبا الى القوم الذين كذبوا۟ بـايتنا فدمرنهم تدميرا');
INSERT INTO "uthmani_simple" VALUES(25,37,'25:37','وقوم نوح لما كذبوا۟ الرسل اغرقنهم وجعلنهم للناس ءاية واعتدنا للظلمين عذابا اليما');
INSERT INTO "uthmani_simple" VALUES(25,38,'25:38','وعادا وثمودا۟ واصحب الرس وقرونۢا بين ذلك كثيرا');
INSERT INTO "uthmani_simple" VALUES(25,39,'25:39','وكلا ضربنا له الامثل وكلا تبرنا تتبيرا');
INSERT INTO "uthmani_simple" VALUES(25,40,'25:40','ولقد اتوا۟ على القرية التى امطرت مطر السوء افلم يكونوا۟ يرونها بل كانوا۟ لا يرجون نشورا');
INSERT INTO "uthmani_simple" VALUES(25,41,'25:41','واذا راوك ان يتخذونك الا هزوا اهذا الذى بعث الله رسولا');
INSERT INTO "uthmani_simple" VALUES(25,42,'25:42','ان كاد ليضلنا عن ءالهتنا لولا ان صبرنا عليها وسوف يعلمون حين يرون العذاب من اضل سبيلا');
INSERT INTO "uthmani_simple" VALUES(25,43,'25:43','ارءيت من اتخذ الهه هوىه افانت تكون عليه وكيلا');
INSERT INTO "uthmani_simple" VALUES(25,44,'25:44','ام تحسب ان اكثرهم يسمعون او يعقلون ان هم الا كالانعم بل هم اضل سبيلا');
INSERT INTO "uthmani_simple" VALUES(25,45,'25:45','الم تر الى ربك كيف مد الظل ولو شاء لجعله ساكنا ثم جعلنا الشمس عليه دليلا');
INSERT INTO "uthmani_simple" VALUES(25,46,'25:46','ثم قبضنه الينا قبضا يسيرا');
INSERT INTO "uthmani_simple" VALUES(25,47,'25:47','وهو الذى جعل لكم اليل لباسا والنوم سباتا وجعل النهار نشورا');
INSERT INTO "uthmani_simple" VALUES(25,48,'25:48','وهو الذى ارسل الريح بشرۢا بين يدى رحمته وانزلنا من السماء ماء طهورا');
INSERT INTO "uthmani_simple" VALUES(25,49,'25:49','لنحى به بلدة ميتا ونسقيه مما خلقنا انعما واناسى كثيرا');
INSERT INTO "uthmani_simple" VALUES(25,50,'25:50','ولقد صرفنه بينهم ليذكروا۟ فابى اكثر الناس الا كفورا');
INSERT INTO "uthmani_simple" VALUES(25,51,'25:51','ولو شئنا لبعثنا فى كل قرية نذيرا');
INSERT INTO "uthmani_simple" VALUES(25,52,'25:52','فلا تطع الكفرين وجهدهم به جهادا كبيرا');
INSERT INTO "uthmani_simple" VALUES(25,53,'25:53','وهو الذى مرج البحرين هذا عذب فرات وهذا ملح اجاج وجعل بينهما برزخا وحجرا محجورا');
INSERT INTO "uthmani_simple" VALUES(25,54,'25:54','وهو الذى خلق من الماء بشرا فجعله نسبا وصهرا وكان ربك قديرا');
INSERT INTO "uthmani_simple" VALUES(25,55,'25:55','ويعبدون من دون الله ما لا ينفعهم ولا يضرهم وكان الكافر على ربه ظهيرا');
INSERT INTO "uthmani_simple" VALUES(25,56,'25:56','وما ارسلنك الا مبشرا ونذيرا');
INSERT INTO "uthmani_simple" VALUES(25,57,'25:57','قل ما اسـلكم عليه من اجر الا من شاء ان يتخذ الى ربه سبيلا');
INSERT INTO "uthmani_simple" VALUES(25,58,'25:58','وتوكل على الحى الذى لا يموت وسبح بحمده وكفى به بذنوب عباده خبيرا');
INSERT INTO "uthmani_simple" VALUES(25,59,'25:59','الذى خلق السموت والارض وما بينهما فى ستة ايام ثم استوى على العرش الرحمن فسـل به خبيرا');
INSERT INTO "uthmani_simple" VALUES(25,60,'25:60','واذا قيل لهم اسجدوا۟ للرحمن قالوا۟ وما الرحمن انسجد لما تامرنا وزادهم نفورا');
INSERT INTO "uthmani_simple" VALUES(25,61,'25:61','تبارك الذى جعل فى السماء بروجا وجعل فيها سرجا وقمرا منيرا');
INSERT INTO "uthmani_simple" VALUES(25,62,'25:62','وهو الذى جعل اليل والنهار خلفة لمن اراد ان يذكر او اراد شكورا');
INSERT INTO "uthmani_simple" VALUES(25,63,'25:63','وعباد الرحمن الذين يمشون على الارض هونا واذا خاطبهم الجهلون قالوا۟ سلما');
INSERT INTO "uthmani_simple" VALUES(25,64,'25:64','والذين يبيتون لربهم سجدا وقيما');
INSERT INTO "uthmani_simple" VALUES(25,65,'25:65','والذين يقولون ربنا اصرف عنا عذاب جهنم ان عذابها كان غراما');
INSERT INTO "uthmani_simple" VALUES(25,66,'25:66','انها ساءت مستقرا ومقاما');
INSERT INTO "uthmani_simple" VALUES(25,67,'25:67','والذين اذا انفقوا۟ لم يسرفوا۟ ولم يقتروا۟ وكان بين ذلك قواما');
INSERT INTO "uthmani_simple" VALUES(25,68,'25:68','والذين لا يدعون مع الله الها ءاخر ولا يقتلون النفس التى حرم الله الا بالحق ولا يزنون ومن يفعل ذلك يلق اثاما');
INSERT INTO "uthmani_simple" VALUES(25,69,'25:69','يضعف له العذاب يوم القيمة ويخلد فيه مهانا');
INSERT INTO "uthmani_simple" VALUES(25,70,'25:70','الا من تاب وءامن وعمل عملا صلحا فاو۟لئك يبدل الله سيـاتهم حسنت وكان الله غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(25,71,'25:71','ومن تاب وعمل صلحا فانه يتوب الى الله متابا');
INSERT INTO "uthmani_simple" VALUES(25,72,'25:72','والذين لا يشهدون الزور واذا مروا۟ باللغو مروا۟ كراما');
INSERT INTO "uthmani_simple" VALUES(25,73,'25:73','والذين اذا ذكروا۟ بـايت ربهم لم يخروا۟ عليها صما وعميانا');
INSERT INTO "uthmani_simple" VALUES(25,74,'25:74','والذين يقولون ربنا هب لنا من ازوجنا وذريتنا قرة اعين واجعلنا للمتقين اماما');
INSERT INTO "uthmani_simple" VALUES(25,75,'25:75','او۟لئك يجزون الغرفة بما صبروا۟ ويلقون فيها تحية وسلما');
INSERT INTO "uthmani_simple" VALUES(25,76,'25:76','خلدين فيها حسنت مستقرا ومقاما');
INSERT INTO "uthmani_simple" VALUES(25,77,'25:77','قل ما يعبؤا۟ بكم ربى لولا دعاؤكم فقد كذبتم فسوف يكون لزامۢا');
INSERT INTO "uthmani_simple" VALUES(26,1,'26:1','طسم');
INSERT INTO "uthmani_simple" VALUES(26,2,'26:2','تلك ءايت الكتب المبين');
INSERT INTO "uthmani_simple" VALUES(26,3,'26:3','لعلك بخع نفسك الا يكونوا۟ مؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,4,'26:4','ان نشا ننزل عليهم من السماء ءاية فظلت اعنقهم لها خضعين');
INSERT INTO "uthmani_simple" VALUES(26,5,'26:5','وما ياتيهم من ذكر من الرحمن محدث الا كانوا۟ عنه معرضين');
INSERT INTO "uthmani_simple" VALUES(26,6,'26:6','فقد كذبوا۟ فسياتيهم انۢبؤا۟ ما كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(26,7,'26:7','اولم يروا۟ الى الارض كم انۢبتنا فيها من كل زوج كريم');
INSERT INTO "uthmani_simple" VALUES(26,8,'26:8','ان فى ذلك لءاية وما كان اكثرهم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,9,'26:9','وان ربك لهو العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(26,10,'26:10','واذ نادى ربك موسى ان ائت القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(26,11,'26:11','قوم فرعون الا يتقون');
INSERT INTO "uthmani_simple" VALUES(26,12,'26:12','قال رب انى اخاف ان يكذبون');
INSERT INTO "uthmani_simple" VALUES(26,13,'26:13','ويضيق صدرى ولا ينطلق لسانى فارسل الى هرون');
INSERT INTO "uthmani_simple" VALUES(26,14,'26:14','ولهم على ذنۢب فاخاف ان يقتلون');
INSERT INTO "uthmani_simple" VALUES(26,15,'26:15','قال كلا فاذهبا بـايتنا انا معكم مستمعون');
INSERT INTO "uthmani_simple" VALUES(26,16,'26:16','فاتيا فرعون فقولا انا رسول رب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,17,'26:17','ان ارسل معنا بنى اسرءيل');
INSERT INTO "uthmani_simple" VALUES(26,18,'26:18','قال الم نربك فينا وليدا ولبثت فينا من عمرك سنين');
INSERT INTO "uthmani_simple" VALUES(26,19,'26:19','وفعلت فعلتك التى فعلت وانت من الكفرين');
INSERT INTO "uthmani_simple" VALUES(26,20,'26:20','قال فعلتها اذا وانا۠ من الضالين');
INSERT INTO "uthmani_simple" VALUES(26,21,'26:21','ففررت منكم لما خفتكم فوهب لى ربى حكما وجعلنى من المرسلين');
INSERT INTO "uthmani_simple" VALUES(26,22,'26:22','وتلك نعمة تمنها على ان عبدت بنى اسرءيل');
INSERT INTO "uthmani_simple" VALUES(26,23,'26:23','قال فرعون وما رب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,24,'26:24','قال رب السموت والارض وما بينهما ان كنتم موقنين');
INSERT INTO "uthmani_simple" VALUES(26,25,'26:25','قال لمن حوله الا تستمعون');
INSERT INTO "uthmani_simple" VALUES(26,26,'26:26','قال ربكم ورب ءابائكم الاولين');
INSERT INTO "uthmani_simple" VALUES(26,27,'26:27','قال ان رسولكم الذى ارسل اليكم لمجنون');
INSERT INTO "uthmani_simple" VALUES(26,28,'26:28','قال رب المشرق والمغرب وما بينهما ان كنتم تعقلون');
INSERT INTO "uthmani_simple" VALUES(26,29,'26:29','قال لئن اتخذت الها غيرى لاجعلنك من المسجونين');
INSERT INTO "uthmani_simple" VALUES(26,30,'26:30','قال اولو جئتك بشىء مبين');
INSERT INTO "uthmani_simple" VALUES(26,31,'26:31','قال فات به ان كنت من الصدقين');
INSERT INTO "uthmani_simple" VALUES(26,32,'26:32','فالقى عصاه فاذا هى ثعبان مبين');
INSERT INTO "uthmani_simple" VALUES(26,33,'26:33','ونزع يده فاذا هى بيضاء للنظرين');
INSERT INTO "uthmani_simple" VALUES(26,34,'26:34','قال للملا حوله ان هذا لسحر عليم');
INSERT INTO "uthmani_simple" VALUES(26,35,'26:35','يريد ان يخرجكم من ارضكم بسحره فماذا تامرون');
INSERT INTO "uthmani_simple" VALUES(26,36,'26:36','قالوا۟ ارجه واخاه وابعث فى المدائن حشرين');
INSERT INTO "uthmani_simple" VALUES(26,37,'26:37','ياتوك بكل سحار عليم');
INSERT INTO "uthmani_simple" VALUES(26,38,'26:38','فجمع السحرة لميقت يوم معلوم');
INSERT INTO "uthmani_simple" VALUES(26,39,'26:39','وقيل للناس هل انتم مجتمعون');
INSERT INTO "uthmani_simple" VALUES(26,40,'26:40','لعلنا نتبع السحرة ان كانوا۟ هم الغلبين');
INSERT INTO "uthmani_simple" VALUES(26,41,'26:41','فلما جاء السحرة قالوا۟ لفرعون ائن لنا لاجرا ان كنا نحن الغلبين');
INSERT INTO "uthmani_simple" VALUES(26,42,'26:42','قال نعم وانكم اذا لمن المقربين');
INSERT INTO "uthmani_simple" VALUES(26,43,'26:43','قال لهم موسى القوا۟ ما انتم ملقون');
INSERT INTO "uthmani_simple" VALUES(26,44,'26:44','فالقوا۟ حبالهم وعصيهم وقالوا۟ بعزة فرعون انا لنحن الغلبون');
INSERT INTO "uthmani_simple" VALUES(26,45,'26:45','فالقى موسى عصاه فاذا هى تلقف ما يافكون');
INSERT INTO "uthmani_simple" VALUES(26,46,'26:46','فالقى السحرة سجدين');
INSERT INTO "uthmani_simple" VALUES(26,47,'26:47','قالوا۟ ءامنا برب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,48,'26:48','رب موسى وهرون');
INSERT INTO "uthmani_simple" VALUES(26,49,'26:49','قال ءامنتم له قبل ان ءاذن لكم انه لكبيركم الذى علمكم السحر فلسوف تعلمون لاقطعن ايديكم وارجلكم من خلف ولاصلبنكم اجمعين');
INSERT INTO "uthmani_simple" VALUES(26,50,'26:50','قالوا۟ لا ضير انا الى ربنا منقلبون');
INSERT INTO "uthmani_simple" VALUES(26,51,'26:51','انا نطمع ان يغفر لنا ربنا خطينا ان كنا اول المؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,52,'26:52','واوحينا الى موسى ان اسر بعبادى انكم متبعون');
INSERT INTO "uthmani_simple" VALUES(26,53,'26:53','فارسل فرعون فى المدائن حشرين');
INSERT INTO "uthmani_simple" VALUES(26,54,'26:54','ان هؤلاء لشرذمة قليلون');
INSERT INTO "uthmani_simple" VALUES(26,55,'26:55','وانهم لنا لغائظون');
INSERT INTO "uthmani_simple" VALUES(26,56,'26:56','وانا لجميع حذرون');
INSERT INTO "uthmani_simple" VALUES(26,57,'26:57','فاخرجنهم من جنت وعيون');
INSERT INTO "uthmani_simple" VALUES(26,58,'26:58','وكنوز ومقام كريم');
INSERT INTO "uthmani_simple" VALUES(26,59,'26:59','كذلك واورثنها بنى اسرءيل');
INSERT INTO "uthmani_simple" VALUES(26,60,'26:60','فاتبعوهم مشرقين');
INSERT INTO "uthmani_simple" VALUES(26,61,'26:61','فلما ترءا الجمعان قال اصحب موسى انا لمدركون');
INSERT INTO "uthmani_simple" VALUES(26,62,'26:62','قال كلا ان معى ربى سيهدين');
INSERT INTO "uthmani_simple" VALUES(26,63,'26:63','فاوحينا الى موسى ان اضرب بعصاك البحر فانفلق فكان كل فرق كالطود العظيم');
INSERT INTO "uthmani_simple" VALUES(26,64,'26:64','وازلفنا ثم الءاخرين');
INSERT INTO "uthmani_simple" VALUES(26,65,'26:65','وانجينا موسى ومن معه اجمعين');
INSERT INTO "uthmani_simple" VALUES(26,66,'26:66','ثم اغرقنا الءاخرين');
INSERT INTO "uthmani_simple" VALUES(26,67,'26:67','ان فى ذلك لءاية وما كان اكثرهم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,68,'26:68','وان ربك لهو العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(26,69,'26:69','واتل عليهم نبا ابرهيم');
INSERT INTO "uthmani_simple" VALUES(26,70,'26:70','اذ قال لابيه وقومه ما تعبدون');
INSERT INTO "uthmani_simple" VALUES(26,71,'26:71','قالوا۟ نعبد اصناما فنظل لها عكفين');
INSERT INTO "uthmani_simple" VALUES(26,72,'26:72','قال هل يسمعونكم اذ تدعون');
INSERT INTO "uthmani_simple" VALUES(26,73,'26:73','او ينفعونكم او يضرون');
INSERT INTO "uthmani_simple" VALUES(26,74,'26:74','قالوا۟ بل وجدنا ءاباءنا كذلك يفعلون');
INSERT INTO "uthmani_simple" VALUES(26,75,'26:75','قال افرءيتم ما كنتم تعبدون');
INSERT INTO "uthmani_simple" VALUES(26,76,'26:76','انتم وءاباؤكم الاقدمون');
INSERT INTO "uthmani_simple" VALUES(26,77,'26:77','فانهم عدو لى الا رب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,78,'26:78','الذى خلقنى فهو يهدين');
INSERT INTO "uthmani_simple" VALUES(26,79,'26:79','والذى هو يطعمنى ويسقين');
INSERT INTO "uthmani_simple" VALUES(26,80,'26:80','واذا مرضت فهو يشفين');
INSERT INTO "uthmani_simple" VALUES(26,81,'26:81','والذى يميتنى ثم يحيين');
INSERT INTO "uthmani_simple" VALUES(26,82,'26:82','والذى اطمع ان يغفر لى خطيـتى يوم الدين');
INSERT INTO "uthmani_simple" VALUES(26,83,'26:83','رب هب لى حكما والحقنى بالصلحين');
INSERT INTO "uthmani_simple" VALUES(26,84,'26:84','واجعل لى لسان صدق فى الءاخرين');
INSERT INTO "uthmani_simple" VALUES(26,85,'26:85','واجعلنى من ورثة جنة النعيم');
INSERT INTO "uthmani_simple" VALUES(26,86,'26:86','واغفر لابى انه كان من الضالين');
INSERT INTO "uthmani_simple" VALUES(26,87,'26:87','ولا تخزنى يوم يبعثون');
INSERT INTO "uthmani_simple" VALUES(26,88,'26:88','يوم لا ينفع مال ولا بنون');
INSERT INTO "uthmani_simple" VALUES(26,89,'26:89','الا من اتى الله بقلب سليم');
INSERT INTO "uthmani_simple" VALUES(26,90,'26:90','وازلفت الجنة للمتقين');
INSERT INTO "uthmani_simple" VALUES(26,91,'26:91','وبرزت الجحيم للغاوين');
INSERT INTO "uthmani_simple" VALUES(26,92,'26:92','وقيل لهم اين ما كنتم تعبدون');
INSERT INTO "uthmani_simple" VALUES(26,93,'26:93','من دون الله هل ينصرونكم او ينتصرون');
INSERT INTO "uthmani_simple" VALUES(26,94,'26:94','فكبكبوا۟ فيها هم والغاون');
INSERT INTO "uthmani_simple" VALUES(26,95,'26:95','وجنود ابليس اجمعون');
INSERT INTO "uthmani_simple" VALUES(26,96,'26:96','قالوا۟ وهم فيها يختصمون');
INSERT INTO "uthmani_simple" VALUES(26,97,'26:97','تالله ان كنا لفى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(26,98,'26:98','اذ نسويكم برب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,99,'26:99','وما اضلنا الا المجرمون');
INSERT INTO "uthmani_simple" VALUES(26,100,'26:100','فما لنا من شفعين');
INSERT INTO "uthmani_simple" VALUES(26,101,'26:101','ولا صديق حميم');
INSERT INTO "uthmani_simple" VALUES(26,102,'26:102','فلو ان لنا كرة فنكون من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,103,'26:103','ان فى ذلك لءاية وما كان اكثرهم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,104,'26:104','وان ربك لهو العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(26,105,'26:105','كذبت قوم نوح المرسلين');
INSERT INTO "uthmani_simple" VALUES(26,106,'26:106','اذ قال لهم اخوهم نوح الا تتقون');
INSERT INTO "uthmani_simple" VALUES(26,107,'26:107','انى لكم رسول امين');
INSERT INTO "uthmani_simple" VALUES(26,108,'26:108','فاتقوا۟ الله واطيعون');
INSERT INTO "uthmani_simple" VALUES(26,109,'26:109','وما اسـلكم عليه من اجر ان اجرى الا على رب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,110,'26:110','فاتقوا۟ الله واطيعون');
INSERT INTO "uthmani_simple" VALUES(26,111,'26:111','قالوا۟ انؤمن لك واتبعك الارذلون');
INSERT INTO "uthmani_simple" VALUES(26,112,'26:112','قال وما علمى بما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(26,113,'26:113','ان حسابهم الا على ربى لو تشعرون');
INSERT INTO "uthmani_simple" VALUES(26,114,'26:114','وما انا۠ بطارد المؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,115,'26:115','ان انا۠ الا نذير مبين');
INSERT INTO "uthmani_simple" VALUES(26,116,'26:116','قالوا۟ لئن لم تنته ينوح لتكونن من المرجومين');
INSERT INTO "uthmani_simple" VALUES(26,117,'26:117','قال رب ان قومى كذبون');
INSERT INTO "uthmani_simple" VALUES(26,118,'26:118','فافتح بينى وبينهم فتحا ونجنى ومن معى من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,119,'26:119','فانجينه ومن معه فى الفلك المشحون');
INSERT INTO "uthmani_simple" VALUES(26,120,'26:120','ثم اغرقنا بعد الباقين');
INSERT INTO "uthmani_simple" VALUES(26,121,'26:121','ان فى ذلك لءاية وما كان اكثرهم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,122,'26:122','وان ربك لهو العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(26,123,'26:123','كذبت عاد المرسلين');
INSERT INTO "uthmani_simple" VALUES(26,124,'26:124','اذ قال لهم اخوهم هود الا تتقون');
INSERT INTO "uthmani_simple" VALUES(26,125,'26:125','انى لكم رسول امين');
INSERT INTO "uthmani_simple" VALUES(26,126,'26:126','فاتقوا۟ الله واطيعون');
INSERT INTO "uthmani_simple" VALUES(26,127,'26:127','وما اسـلكم عليه من اجر ان اجرى الا على رب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,128,'26:128','اتبنون بكل ريع ءاية تعبثون');
INSERT INTO "uthmani_simple" VALUES(26,129,'26:129','وتتخذون مصانع لعلكم تخلدون');
INSERT INTO "uthmani_simple" VALUES(26,130,'26:130','واذا بطشتم بطشتم جبارين');
INSERT INTO "uthmani_simple" VALUES(26,131,'26:131','فاتقوا۟ الله واطيعون');
INSERT INTO "uthmani_simple" VALUES(26,132,'26:132','واتقوا۟ الذى امدكم بما تعلمون');
INSERT INTO "uthmani_simple" VALUES(26,133,'26:133','امدكم بانعم وبنين');
INSERT INTO "uthmani_simple" VALUES(26,134,'26:134','وجنت وعيون');
INSERT INTO "uthmani_simple" VALUES(26,135,'26:135','انى اخاف عليكم عذاب يوم عظيم');
INSERT INTO "uthmani_simple" VALUES(26,136,'26:136','قالوا۟ سواء علينا اوعظت ام لم تكن من الوعظين');
INSERT INTO "uthmani_simple" VALUES(26,137,'26:137','ان هذا الا خلق الاولين');
INSERT INTO "uthmani_simple" VALUES(26,138,'26:138','وما نحن بمعذبين');
INSERT INTO "uthmani_simple" VALUES(26,139,'26:139','فكذبوه فاهلكنهم ان فى ذلك لءاية وما كان اكثرهم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,140,'26:140','وان ربك لهو العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(26,141,'26:141','كذبت ثمود المرسلين');
INSERT INTO "uthmani_simple" VALUES(26,142,'26:142','اذ قال لهم اخوهم صلح الا تتقون');
INSERT INTO "uthmani_simple" VALUES(26,143,'26:143','انى لكم رسول امين');
INSERT INTO "uthmani_simple" VALUES(26,144,'26:144','فاتقوا۟ الله واطيعون');
INSERT INTO "uthmani_simple" VALUES(26,145,'26:145','وما اسـلكم عليه من اجر ان اجرى الا على رب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,146,'26:146','اتتركون فى ما ههنا ءامنين');
INSERT INTO "uthmani_simple" VALUES(26,147,'26:147','فى جنت وعيون');
INSERT INTO "uthmani_simple" VALUES(26,148,'26:148','وزروع ونخل طلعها هضيم');
INSERT INTO "uthmani_simple" VALUES(26,149,'26:149','وتنحتون من الجبال بيوتا فرهين');
INSERT INTO "uthmani_simple" VALUES(26,150,'26:150','فاتقوا۟ الله واطيعون');
INSERT INTO "uthmani_simple" VALUES(26,151,'26:151','ولا تطيعوا۟ امر المسرفين');
INSERT INTO "uthmani_simple" VALUES(26,152,'26:152','الذين يفسدون فى الارض ولا يصلحون');
INSERT INTO "uthmani_simple" VALUES(26,153,'26:153','قالوا۟ انما انت من المسحرين');
INSERT INTO "uthmani_simple" VALUES(26,154,'26:154','ما انت الا بشر مثلنا فات بـاية ان كنت من الصدقين');
INSERT INTO "uthmani_simple" VALUES(26,155,'26:155','قال هذه ناقة لها شرب ولكم شرب يوم معلوم');
INSERT INTO "uthmani_simple" VALUES(26,156,'26:156','ولا تمسوها بسوء فياخذكم عذاب يوم عظيم');
INSERT INTO "uthmani_simple" VALUES(26,157,'26:157','فعقروها فاصبحوا۟ ندمين');
INSERT INTO "uthmani_simple" VALUES(26,158,'26:158','فاخذهم العذاب ان فى ذلك لءاية وما كان اكثرهم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,159,'26:159','وان ربك لهو العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(26,160,'26:160','كذبت قوم لوط المرسلين');
INSERT INTO "uthmani_simple" VALUES(26,161,'26:161','اذ قال لهم اخوهم لوط الا تتقون');
INSERT INTO "uthmani_simple" VALUES(26,162,'26:162','انى لكم رسول امين');
INSERT INTO "uthmani_simple" VALUES(26,163,'26:163','فاتقوا۟ الله واطيعون');
INSERT INTO "uthmani_simple" VALUES(26,164,'26:164','وما اسـلكم عليه من اجر ان اجرى الا على رب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,165,'26:165','اتاتون الذكران من العلمين');
INSERT INTO "uthmani_simple" VALUES(26,166,'26:166','وتذرون ما خلق لكم ربكم من ازوجكم بل انتم قوم عادون');
INSERT INTO "uthmani_simple" VALUES(26,167,'26:167','قالوا۟ لئن لم تنته يلوط لتكونن من المخرجين');
INSERT INTO "uthmani_simple" VALUES(26,168,'26:168','قال انى لعملكم من القالين');
INSERT INTO "uthmani_simple" VALUES(26,169,'26:169','رب نجنى واهلى مما يعملون');
INSERT INTO "uthmani_simple" VALUES(26,170,'26:170','فنجينه واهله اجمعين');
INSERT INTO "uthmani_simple" VALUES(26,171,'26:171','الا عجوزا فى الغبرين');
INSERT INTO "uthmani_simple" VALUES(26,172,'26:172','ثم دمرنا الءاخرين');
INSERT INTO "uthmani_simple" VALUES(26,173,'26:173','وامطرنا عليهم مطرا فساء مطر المنذرين');
INSERT INTO "uthmani_simple" VALUES(26,174,'26:174','ان فى ذلك لءاية وما كان اكثرهم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,175,'26:175','وان ربك لهو العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(26,176,'26:176','كذب اصحب لـيكة المرسلين');
INSERT INTO "uthmani_simple" VALUES(26,177,'26:177','اذ قال لهم شعيب الا تتقون');
INSERT INTO "uthmani_simple" VALUES(26,178,'26:178','انى لكم رسول امين');
INSERT INTO "uthmani_simple" VALUES(26,179,'26:179','فاتقوا۟ الله واطيعون');
INSERT INTO "uthmani_simple" VALUES(26,180,'26:180','وما اسـلكم عليه من اجر ان اجرى الا على رب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,181,'26:181','اوفوا۟ الكيل ولا تكونوا۟ من المخسرين');
INSERT INTO "uthmani_simple" VALUES(26,182,'26:182','وزنوا۟ بالقسطاس المستقيم');
INSERT INTO "uthmani_simple" VALUES(26,183,'26:183','ولا تبخسوا۟ الناس اشياءهم ولا تعثوا۟ فى الارض مفسدين');
INSERT INTO "uthmani_simple" VALUES(26,184,'26:184','واتقوا۟ الذى خلقكم والجبلة الاولين');
INSERT INTO "uthmani_simple" VALUES(26,185,'26:185','قالوا۟ انما انت من المسحرين');
INSERT INTO "uthmani_simple" VALUES(26,186,'26:186','وما انت الا بشر مثلنا وان نظنك لمن الكذبين');
INSERT INTO "uthmani_simple" VALUES(26,187,'26:187','فاسقط علينا كسفا من السماء ان كنت من الصدقين');
INSERT INTO "uthmani_simple" VALUES(26,188,'26:188','قال ربى اعلم بما تعملون');
INSERT INTO "uthmani_simple" VALUES(26,189,'26:189','فكذبوه فاخذهم عذاب يوم الظلة انه كان عذاب يوم عظيم');
INSERT INTO "uthmani_simple" VALUES(26,190,'26:190','ان فى ذلك لءاية وما كان اكثرهم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,191,'26:191','وان ربك لهو العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(26,192,'26:192','وانه لتنزيل رب العلمين');
INSERT INTO "uthmani_simple" VALUES(26,193,'26:193','نزل به الروح الامين');
INSERT INTO "uthmani_simple" VALUES(26,194,'26:194','على قلبك لتكون من المنذرين');
INSERT INTO "uthmani_simple" VALUES(26,195,'26:195','بلسان عربى مبين');
INSERT INTO "uthmani_simple" VALUES(26,196,'26:196','وانه لفى زبر الاولين');
INSERT INTO "uthmani_simple" VALUES(26,197,'26:197','اولم يكن لهم ءاية ان يعلمه علمؤا۟ بنى اسرءيل');
INSERT INTO "uthmani_simple" VALUES(26,198,'26:198','ولو نزلنه على بعض الاعجمين');
INSERT INTO "uthmani_simple" VALUES(26,199,'26:199','فقراه عليهم ما كانوا۟ به مؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,200,'26:200','كذلك سلكنه فى قلوب المجرمين');
INSERT INTO "uthmani_simple" VALUES(26,201,'26:201','لا يؤمنون به حتى يروا۟ العذاب الاليم');
INSERT INTO "uthmani_simple" VALUES(26,202,'26:202','فياتيهم بغتة وهم لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(26,203,'26:203','فيقولوا۟ هل نحن منظرون');
INSERT INTO "uthmani_simple" VALUES(26,204,'26:204','افبعذابنا يستعجلون');
INSERT INTO "uthmani_simple" VALUES(26,205,'26:205','افرءيت ان متعنهم سنين');
INSERT INTO "uthmani_simple" VALUES(26,206,'26:206','ثم جاءهم ما كانوا۟ يوعدون');
INSERT INTO "uthmani_simple" VALUES(26,207,'26:207','ما اغنى عنهم ما كانوا۟ يمتعون');
INSERT INTO "uthmani_simple" VALUES(26,208,'26:208','وما اهلكنا من قرية الا لها منذرون');
INSERT INTO "uthmani_simple" VALUES(26,209,'26:209','ذكرى وما كنا ظلمين');
INSERT INTO "uthmani_simple" VALUES(26,210,'26:210','وما تنزلت به الشيطين');
INSERT INTO "uthmani_simple" VALUES(26,211,'26:211','وما ينۢبغى لهم وما يستطيعون');
INSERT INTO "uthmani_simple" VALUES(26,212,'26:212','انهم عن السمع لمعزولون');
INSERT INTO "uthmani_simple" VALUES(26,213,'26:213','فلا تدع مع الله الها ءاخر فتكون من المعذبين');
INSERT INTO "uthmani_simple" VALUES(26,214,'26:214','وانذر عشيرتك الاقربين');
INSERT INTO "uthmani_simple" VALUES(26,215,'26:215','واخفض جناحك لمن اتبعك من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(26,216,'26:216','فان عصوك فقل انى برىء مما تعملون');
INSERT INTO "uthmani_simple" VALUES(26,217,'26:217','وتوكل على العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(26,218,'26:218','الذى يرىك حين تقوم');
INSERT INTO "uthmani_simple" VALUES(26,219,'26:219','وتقلبك فى السجدين');
INSERT INTO "uthmani_simple" VALUES(26,220,'26:220','انه هو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(26,221,'26:221','هل انبئكم على من تنزل الشيطين');
INSERT INTO "uthmani_simple" VALUES(26,222,'26:222','تنزل على كل افاك اثيم');
INSERT INTO "uthmani_simple" VALUES(26,223,'26:223','يلقون السمع واكثرهم كذبون');
INSERT INTO "uthmani_simple" VALUES(26,224,'26:224','والشعراء يتبعهم الغاون');
INSERT INTO "uthmani_simple" VALUES(26,225,'26:225','الم تر انهم فى كل واد يهيمون');
INSERT INTO "uthmani_simple" VALUES(26,226,'26:226','وانهم يقولون ما لا يفعلون');
INSERT INTO "uthmani_simple" VALUES(26,227,'26:227','الا الذين ءامنوا۟ وعملوا۟ الصلحت وذكروا۟ الله كثيرا وانتصروا۟ منۢ بعد ما ظلموا۟ وسيعلم الذين ظلموا۟ اى منقلب ينقلبون');
INSERT INTO "uthmani_simple" VALUES(27,1,'27:1','طس تلك ءايت القرءان وكتاب مبين');
INSERT INTO "uthmani_simple" VALUES(27,2,'27:2','هدى وبشرى للمؤمنين');
INSERT INTO "uthmani_simple" VALUES(27,3,'27:3','الذين يقيمون الصلوة ويؤتون الزكوة وهم بالءاخرة هم يوقنون');
INSERT INTO "uthmani_simple" VALUES(27,4,'27:4','ان الذين لا يؤمنون بالءاخرة زينا لهم اعملهم فهم يعمهون');
INSERT INTO "uthmani_simple" VALUES(27,5,'27:5','او۟لئك الذين لهم سوء العذاب وهم فى الءاخرة هم الاخسرون');
INSERT INTO "uthmani_simple" VALUES(27,6,'27:6','وانك لتلقى القرءان من لدن حكيم عليم');
INSERT INTO "uthmani_simple" VALUES(27,7,'27:7','اذ قال موسى لاهله انى ءانست نارا سـاتيكم منها بخبر او ءاتيكم بشهاب قبس لعلكم تصطلون');
INSERT INTO "uthmani_simple" VALUES(27,8,'27:8','فلما جاءها نودى انۢ بورك من فى النار ومن حولها وسبحن الله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(27,9,'27:9','يموسى انه انا الله العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(27,10,'27:10','والق عصاك فلما رءاها تهتز كانها جان ولى مدبرا ولم يعقب يموسى لا تخف انى لا يخاف لدى المرسلون');
INSERT INTO "uthmani_simple" VALUES(27,11,'27:11','الا من ظلم ثم بدل حسنۢا بعد سوء فانى غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(27,12,'27:12','وادخل يدك فى جيبك تخرج بيضاء من غير سوء فى تسع ءايت الى فرعون وقومه انهم كانوا۟ قوما فسقين');
INSERT INTO "uthmani_simple" VALUES(27,13,'27:13','فلما جاءتهم ءايتنا مبصرة قالوا۟ هذا سحر مبين');
INSERT INTO "uthmani_simple" VALUES(27,14,'27:14','وجحدوا۟ بها واستيقنتها انفسهم ظلما وعلوا فانظر كيف كان عقبة المفسدين');
INSERT INTO "uthmani_simple" VALUES(27,15,'27:15','ولقد ءاتينا داود وسليمن علما وقالا الحمد لله الذى فضلنا على كثير من عباده المؤمنين');
INSERT INTO "uthmani_simple" VALUES(27,16,'27:16','وورث سليمن داود وقال يايها الناس علمنا منطق الطير واوتينا من كل شىء ان هذا لهو الفضل المبين');
INSERT INTO "uthmani_simple" VALUES(27,17,'27:17','وحشر لسليمن جنوده من الجن والانس والطير فهم يوزعون');
INSERT INTO "uthmani_simple" VALUES(27,18,'27:18','حتى اذا اتوا۟ على واد النمل قالت نملة يايها النمل ادخلوا۟ مسكنكم لا يحطمنكم سليمن وجنوده وهم لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(27,19,'27:19','فتبسم ضاحكا من قولها وقال رب اوزعنى ان اشكر نعمتك التى انعمت على وعلى ولدى وان اعمل صلحا ترضىه وادخلنى برحمتك فى عبادك الصلحين');
INSERT INTO "uthmani_simple" VALUES(27,20,'27:20','وتفقد الطير فقال ما لى لا ارى الهدهد ام كان من الغائبين');
INSERT INTO "uthmani_simple" VALUES(27,21,'27:21','لاعذبنه عذابا شديدا او لاا۟ذبحنه او لياتينى بسلطن مبين');
INSERT INTO "uthmani_simple" VALUES(27,22,'27:22','فمكث غير بعيد فقال احطت بما لم تحط به وجئتك من سباۭ بنبا يقين');
INSERT INTO "uthmani_simple" VALUES(27,23,'27:23','انى وجدت امراة تملكهم واوتيت من كل شىء ولها عرش عظيم');
INSERT INTO "uthmani_simple" VALUES(27,24,'27:24','وجدتها وقومها يسجدون للشمس من دون الله وزين لهم الشيطن اعملهم فصدهم عن السبيل فهم لا يهتدون');
INSERT INTO "uthmani_simple" VALUES(27,25,'27:25','الا يسجدوا۟ لله الذى يخرج الخبء فى السموت والارض ويعلم ما تخفون وما تعلنون');
INSERT INTO "uthmani_simple" VALUES(27,26,'27:26','الله لا اله الا هو رب العرش العظيم');
INSERT INTO "uthmani_simple" VALUES(27,27,'27:27','قال سننظر اصدقت ام كنت من الكذبين');
INSERT INTO "uthmani_simple" VALUES(27,28,'27:28','اذهب بكتبى هذا فالقه اليهم ثم تول عنهم فانظر ماذا يرجعون');
INSERT INTO "uthmani_simple" VALUES(27,29,'27:29','قالت يايها الملؤا۟ انى القى الى كتب كريم');
INSERT INTO "uthmani_simple" VALUES(27,30,'27:30','انه من سليمن وانه بسم الله الرحمن الرحيم');
INSERT INTO "uthmani_simple" VALUES(27,31,'27:31','الا تعلوا۟ على واتونى مسلمين');
INSERT INTO "uthmani_simple" VALUES(27,32,'27:32','قالت يايها الملؤا۟ افتونى فى امرى ما كنت قاطعة امرا حتى تشهدون');
INSERT INTO "uthmani_simple" VALUES(27,33,'27:33','قالوا۟ نحن او۟لوا۟ قوة واو۟لوا۟ باس شديد والامر اليك فانظرى ماذا تامرين');
INSERT INTO "uthmani_simple" VALUES(27,34,'27:34','قالت ان الملوك اذا دخلوا۟ قرية افسدوها وجعلوا۟ اعزة اهلها اذلة وكذلك يفعلون');
INSERT INTO "uthmani_simple" VALUES(27,35,'27:35','وانى مرسلة اليهم بهدية فناظرةۢ بم يرجع المرسلون');
INSERT INTO "uthmani_simple" VALUES(27,36,'27:36','فلما جاء سليمن قال اتمدونن بمال فما ءاتىن الله خير مما ءاتىكم بل انتم بهديتكم تفرحون');
INSERT INTO "uthmani_simple" VALUES(27,37,'27:37','ارجع اليهم فلناتينهم بجنود لا قبل لهم بها ولنخرجنهم منها اذلة وهم صغرون');
INSERT INTO "uthmani_simple" VALUES(27,38,'27:38','قال يايها الملؤا۟ ايكم ياتينى بعرشها قبل ان ياتونى مسلمين');
INSERT INTO "uthmani_simple" VALUES(27,39,'27:39','قال عفريت من الجن انا۠ ءاتيك به قبل ان تقوم من مقامك وانى عليه لقوى امين');
INSERT INTO "uthmani_simple" VALUES(27,40,'27:40','قال الذى عنده علم من الكتب انا۠ ءاتيك به قبل ان يرتد اليك طرفك فلما رءاه مستقرا عنده قال هذا من فضل ربى ليبلونى ءاشكر ام اكفر ومن شكر فانما يشكر لنفسه ومن كفر فان ربى غنى كريم');
INSERT INTO "uthmani_simple" VALUES(27,41,'27:41','قال نكروا۟ لها عرشها ننظر اتهتدى ام تكون من الذين لا يهتدون');
INSERT INTO "uthmani_simple" VALUES(27,42,'27:42','فلما جاءت قيل اهكذا عرشك قالت كانه هو واوتينا العلم من قبلها وكنا مسلمين');
INSERT INTO "uthmani_simple" VALUES(27,43,'27:43','وصدها ما كانت تعبد من دون الله انها كانت من قوم كفرين');
INSERT INTO "uthmani_simple" VALUES(27,44,'27:44','قيل لها ادخلى الصرح فلما راته حسبته لجة وكشفت عن ساقيها قال انه صرح ممرد من قوارير قالت رب انى ظلمت نفسى واسلمت مع سليمن لله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(27,45,'27:45','ولقد ارسلنا الى ثمود اخاهم صلحا ان اعبدوا۟ الله فاذا هم فريقان يختصمون');
INSERT INTO "uthmani_simple" VALUES(27,46,'27:46','قال يقوم لم تستعجلون بالسيئة قبل الحسنة لولا تستغفرون الله لعلكم ترحمون');
INSERT INTO "uthmani_simple" VALUES(27,47,'27:47','قالوا۟ اطيرنا بك وبمن معك قال طئركم عند الله بل انتم قوم تفتنون');
INSERT INTO "uthmani_simple" VALUES(27,48,'27:48','وكان فى المدينة تسعة رهط يفسدون فى الارض ولا يصلحون');
INSERT INTO "uthmani_simple" VALUES(27,49,'27:49','قالوا۟ تقاسموا۟ بالله لنبيتنه واهله ثم لنقولن لوليه ما شهدنا مهلك اهله وانا لصدقون');
INSERT INTO "uthmani_simple" VALUES(27,50,'27:50','ومكروا۟ مكرا ومكرنا مكرا وهم لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(27,51,'27:51','فانظر كيف كان عقبة مكرهم انا دمرنهم وقومهم اجمعين');
INSERT INTO "uthmani_simple" VALUES(27,52,'27:52','فتلك بيوتهم خاويةۢ بما ظلموا۟ ان فى ذلك لءاية لقوم يعلمون');
INSERT INTO "uthmani_simple" VALUES(27,53,'27:53','وانجينا الذين ءامنوا۟ وكانوا۟ يتقون');
INSERT INTO "uthmani_simple" VALUES(27,54,'27:54','ولوطا اذ قال لقومه اتاتون الفحشة وانتم تبصرون');
INSERT INTO "uthmani_simple" VALUES(27,55,'27:55','ائنكم لتاتون الرجال شهوة من دون النساء بل انتم قوم تجهلون');
INSERT INTO "uthmani_simple" VALUES(27,56,'27:56','فما كان جواب قومه الا ان قالوا۟ اخرجوا۟ ءال لوط من قريتكم انهم اناس يتطهرون');
INSERT INTO "uthmani_simple" VALUES(27,57,'27:57','فانجينه واهله الا امراته قدرنها من الغبرين');
INSERT INTO "uthmani_simple" VALUES(27,58,'27:58','وامطرنا عليهم مطرا فساء مطر المنذرين');
INSERT INTO "uthmani_simple" VALUES(27,59,'27:59','قل الحمد لله وسلم على عباده الذين اصطفى ءالله خير اما يشركون');
INSERT INTO "uthmani_simple" VALUES(27,60,'27:60','امن خلق السموت والارض وانزل لكم من السماء ماء فانۢبتنا به حدائق ذات بهجة ما كان لكم ان تنۢبتوا۟ شجرها اءله مع الله بل هم قوم يعدلون');
INSERT INTO "uthmani_simple" VALUES(27,61,'27:61','امن جعل الارض قرارا وجعل خللها انهرا وجعل لها روسى وجعل بين البحرين حاجزا اءله مع الله بل اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(27,62,'27:62','امن يجيب المضطر اذا دعاه ويكشف السوء ويجعلكم خلفاء الارض اءله مع الله قليلا ما تذكرون');
INSERT INTO "uthmani_simple" VALUES(27,63,'27:63','امن يهديكم فى ظلمت البر والبحر ومن يرسل الريح بشرۢا بين يدى رحمته اءله مع الله تعلى الله عما يشركون');
INSERT INTO "uthmani_simple" VALUES(27,64,'27:64','امن يبدؤا۟ الخلق ثم يعيده ومن يرزقكم من السماء والارض اءله مع الله قل هاتوا۟ برهنكم ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(27,65,'27:65','قل لا يعلم من فى السموت والارض الغيب الا الله وما يشعرون ايان يبعثون');
INSERT INTO "uthmani_simple" VALUES(27,66,'27:66','بل ادرك علمهم فى الءاخرة بل هم فى شك منها بل هم منها عمون');
INSERT INTO "uthmani_simple" VALUES(27,67,'27:67','وقال الذين كفروا۟ اءذا كنا تربا وءاباؤنا ائنا لمخرجون');
INSERT INTO "uthmani_simple" VALUES(27,68,'27:68','لقد وعدنا هذا نحن وءاباؤنا من قبل ان هذا الا اسطير الاولين');
INSERT INTO "uthmani_simple" VALUES(27,69,'27:69','قل سيروا۟ فى الارض فانظروا۟ كيف كان عقبة المجرمين');
INSERT INTO "uthmani_simple" VALUES(27,70,'27:70','ولا تحزن عليهم ولا تكن فى ضيق مما يمكرون');
INSERT INTO "uthmani_simple" VALUES(27,71,'27:71','ويقولون متى هذا الوعد ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(27,72,'27:72','قل عسى ان يكون ردف لكم بعض الذى تستعجلون');
INSERT INTO "uthmani_simple" VALUES(27,73,'27:73','وان ربك لذو فضل على الناس ولكن اكثرهم لا يشكرون');
INSERT INTO "uthmani_simple" VALUES(27,74,'27:74','وان ربك ليعلم ما تكن صدورهم وما يعلنون');
INSERT INTO "uthmani_simple" VALUES(27,75,'27:75','وما من غائبة فى السماء والارض الا فى كتب مبين');
INSERT INTO "uthmani_simple" VALUES(27,76,'27:76','ان هذا القرءان يقص على بنى اسرءيل اكثر الذى هم فيه يختلفون');
INSERT INTO "uthmani_simple" VALUES(27,77,'27:77','وانه لهدى ورحمة للمؤمنين');
INSERT INTO "uthmani_simple" VALUES(27,78,'27:78','ان ربك يقضى بينهم بحكمه وهو العزيز العليم');
INSERT INTO "uthmani_simple" VALUES(27,79,'27:79','فتوكل على الله انك على الحق المبين');
INSERT INTO "uthmani_simple" VALUES(27,80,'27:80','انك لا تسمع الموتى ولا تسمع الصم الدعاء اذا ولوا۟ مدبرين');
INSERT INTO "uthmani_simple" VALUES(27,81,'27:81','وما انت بهدى العمى عن ضللتهم ان تسمع الا من يؤمن بـايتنا فهم مسلمون');
INSERT INTO "uthmani_simple" VALUES(27,82,'27:82','واذا وقع القول عليهم اخرجنا لهم دابة من الارض تكلمهم ان الناس كانوا۟ بـايتنا لا يوقنون');
INSERT INTO "uthmani_simple" VALUES(27,83,'27:83','ويوم نحشر من كل امة فوجا ممن يكذب بـايتنا فهم يوزعون');
INSERT INTO "uthmani_simple" VALUES(27,84,'27:84','حتى اذا جاءو قال اكذبتم بـايتى ولم تحيطوا۟ بها علما اماذا كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(27,85,'27:85','ووقع القول عليهم بما ظلموا۟ فهم لا ينطقون');
INSERT INTO "uthmani_simple" VALUES(27,86,'27:86','الم يروا۟ انا جعلنا اليل ليسكنوا۟ فيه والنهار مبصرا ان فى ذلك لءايت لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(27,87,'27:87','ويوم ينفخ فى الصور ففزع من فى السموت ومن فى الارض الا من شاء الله وكل اتوه دخرين');
INSERT INTO "uthmani_simple" VALUES(27,88,'27:88','وترى الجبال تحسبها جامدة وهى تمر مر السحاب صنع الله الذى اتقن كل شىء انه خبيرۢ بما تفعلون');
INSERT INTO "uthmani_simple" VALUES(27,89,'27:89','من جاء بالحسنة فله خير منها وهم من فزع يومئذ ءامنون');
INSERT INTO "uthmani_simple" VALUES(27,90,'27:90','ومن جاء بالسيئة فكبت وجوههم فى النار هل تجزون الا ما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(27,91,'27:91','انما امرت ان اعبد رب هذه البلدة الذى حرمها وله كل شىء وامرت ان اكون من المسلمين');
INSERT INTO "uthmani_simple" VALUES(27,92,'27:92','وان اتلوا۟ القرءان فمن اهتدى فانما يهتدى لنفسه ومن ضل فقل انما انا۠ من المنذرين');
INSERT INTO "uthmani_simple" VALUES(27,93,'27:93','وقل الحمد لله سيريكم ءايته فتعرفونها وما ربك بغفل عما تعملون');
INSERT INTO "uthmani_simple" VALUES(28,1,'28:1','طسم');
INSERT INTO "uthmani_simple" VALUES(28,2,'28:2','تلك ءايت الكتب المبين');
INSERT INTO "uthmani_simple" VALUES(28,3,'28:3','نتلوا۟ عليك من نبا موسى وفرعون بالحق لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(28,4,'28:4','ان فرعون علا فى الارض وجعل اهلها شيعا يستضعف طائفة منهم يذبح ابناءهم ويستحى نساءهم انه كان من المفسدين');
INSERT INTO "uthmani_simple" VALUES(28,5,'28:5','ونريد ان نمن على الذين استضعفوا۟ فى الارض ونجعلهم ائمة ونجعلهم الورثين');
INSERT INTO "uthmani_simple" VALUES(28,6,'28:6','ونمكن لهم فى الارض ونرى فرعون وهمن وجنودهما منهم ما كانوا۟ يحذرون');
INSERT INTO "uthmani_simple" VALUES(28,7,'28:7','واوحينا الى ام موسى ان ارضعيه فاذا خفت عليه فالقيه فى اليم ولا تخافى ولا تحزنى انا رادوه اليك وجاعلوه من المرسلين');
INSERT INTO "uthmani_simple" VALUES(28,8,'28:8','فالتقطه ءال فرعون ليكون لهم عدوا وحزنا ان فرعون وهمن وجنودهما كانوا۟ خطـين');
INSERT INTO "uthmani_simple" VALUES(28,9,'28:9','وقالت امرات فرعون قرت عين لى ولك لا تقتلوه عسى ان ينفعنا او نتخذه ولدا وهم لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(28,10,'28:10','واصبح فؤاد ام موسى فرغا ان كادت لتبدى به لولا ان ربطنا على قلبها لتكون من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(28,11,'28:11','وقالت لاخته قصيه فبصرت به عن جنب وهم لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(28,12,'28:12','وحرمنا عليه المراضع من قبل فقالت هل ادلكم على اهل بيت يكفلونه لكم وهم له نصحون');
INSERT INTO "uthmani_simple" VALUES(28,13,'28:13','فرددنه الى امه كى تقر عينها ولا تحزن ولتعلم ان وعد الله حق ولكن اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(28,14,'28:14','ولما بلغ اشده واستوى ءاتينه حكما وعلما وكذلك نجزى المحسنين');
INSERT INTO "uthmani_simple" VALUES(28,15,'28:15','ودخل المدينة على حين غفلة من اهلها فوجد فيها رجلين يقتتلان هذا من شيعته وهذا من عدوه فاستغثه الذى من شيعته على الذى من عدوه فوكزه موسى فقضى عليه قال هذا من عمل الشيطن انه عدو مضل مبين');
INSERT INTO "uthmani_simple" VALUES(28,16,'28:16','قال رب انى ظلمت نفسى فاغفر لى فغفر له انه هو الغفور الرحيم');
INSERT INTO "uthmani_simple" VALUES(28,17,'28:17','قال رب بما انعمت على فلن اكون ظهيرا للمجرمين');
INSERT INTO "uthmani_simple" VALUES(28,18,'28:18','فاصبح فى المدينة خائفا يترقب فاذا الذى استنصره بالامس يستصرخه قال له موسى انك لغوى مبين');
INSERT INTO "uthmani_simple" VALUES(28,19,'28:19','فلما ان اراد ان يبطش بالذى هو عدو لهما قال يموسى اتريد ان تقتلنى كما قتلت نفسۢا بالامس ان تريد الا ان تكون جبارا فى الارض وما تريد ان تكون من المصلحين');
INSERT INTO "uthmani_simple" VALUES(28,20,'28:20','وجاء رجل من اقصا المدينة يسعى قال يموسى ان الملا ياتمرون بك ليقتلوك فاخرج انى لك من النصحين');
INSERT INTO "uthmani_simple" VALUES(28,21,'28:21','فخرج منها خائفا يترقب قال رب نجنى من القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(28,22,'28:22','ولما توجه تلقاء مدين قال عسى ربى ان يهدينى سواء السبيل');
INSERT INTO "uthmani_simple" VALUES(28,23,'28:23','ولما ورد ماء مدين وجد عليه امة من الناس يسقون ووجد من دونهم امراتين تذودان قال ما خطبكما قالتا لا نسقى حتى يصدر الرعاء وابونا شيخ كبير');
INSERT INTO "uthmani_simple" VALUES(28,24,'28:24','فسقى لهما ثم تولى الى الظل فقال رب انى لما انزلت الى من خير فقير');
INSERT INTO "uthmani_simple" VALUES(28,25,'28:25','فجاءته احدىهما تمشى على استحياء قالت ان ابى يدعوك ليجزيك اجر ما سقيت لنا فلما جاءه وقص عليه القصص قال لا تخف نجوت من القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(28,26,'28:26','قالت احدىهما يابت استـجره ان خير من استـجرت القوى الامين');
INSERT INTO "uthmani_simple" VALUES(28,27,'28:27','قال انى اريد ان انكحك احدى ابنتى هتين على ان تاجرنى ثمنى حجج فان اتممت عشرا فمن عندك وما اريد ان اشق عليك ستجدنى ان شاء الله من الصلحين');
INSERT INTO "uthmani_simple" VALUES(28,28,'28:28','قال ذلك بينى وبينك ايما الاجلين قضيت فلا عدون على والله على ما نقول وكيل');
INSERT INTO "uthmani_simple" VALUES(28,29,'28:29','فلما قضى موسى الاجل وسار باهله ءانس من جانب الطور نارا قال لاهله امكثوا۟ انى ءانست نارا لعلى ءاتيكم منها بخبر او جذوة من النار لعلكم تصطلون');
INSERT INTO "uthmani_simple" VALUES(28,30,'28:30','فلما اتىها نودى من شطئ الواد الايمن فى البقعة المبركة من الشجرة ان يموسى انى انا الله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(28,31,'28:31','وان الق عصاك فلما رءاها تهتز كانها جان ولى مدبرا ولم يعقب يموسى اقبل ولا تخف انك من الءامنين');
INSERT INTO "uthmani_simple" VALUES(28,32,'28:32','اسلك يدك فى جيبك تخرج بيضاء من غير سوء واضمم اليك جناحك من الرهب فذنك برهنان من ربك الى فرعون وملاي۟ه انهم كانوا۟ قوما فسقين');
INSERT INTO "uthmani_simple" VALUES(28,33,'28:33','قال رب انى قتلت منهم نفسا فاخاف ان يقتلون');
INSERT INTO "uthmani_simple" VALUES(28,34,'28:34','واخى هرون هو افصح منى لسانا فارسله معى ردءا يصدقنى انى اخاف ان يكذبون');
INSERT INTO "uthmani_simple" VALUES(28,35,'28:35','قال سنشد عضدك باخيك ونجعل لكما سلطنا فلا يصلون اليكما بـايتنا انتما ومن اتبعكما الغلبون');
INSERT INTO "uthmani_simple" VALUES(28,36,'28:36','فلما جاءهم موسى بـايتنا بينت قالوا۟ ما هذا الا سحر مفترى وما سمعنا بهذا فى ءابائنا الاولين');
INSERT INTO "uthmani_simple" VALUES(28,37,'28:37','وقال موسى ربى اعلم بمن جاء بالهدى من عنده ومن تكون له عقبة الدار انه لا يفلح الظلمون');
INSERT INTO "uthmani_simple" VALUES(28,38,'28:38','وقال فرعون يايها الملا ما علمت لكم من اله غيرى فاوقد لى يهمن على الطين فاجعل لى صرحا لعلى اطلع الى اله موسى وانى لاظنه من الكذبين');
INSERT INTO "uthmani_simple" VALUES(28,39,'28:39','واستكبر هو وجنوده فى الارض بغير الحق وظنوا۟ انهم الينا لا يرجعون');
INSERT INTO "uthmani_simple" VALUES(28,40,'28:40','فاخذنه وجنوده فنبذنهم فى اليم فانظر كيف كان عقبة الظلمين');
INSERT INTO "uthmani_simple" VALUES(28,41,'28:41','وجعلنهم ائمة يدعون الى النار ويوم القيمة لا ينصرون');
INSERT INTO "uthmani_simple" VALUES(28,42,'28:42','واتبعنهم فى هذه الدنيا لعنة ويوم القيمة هم من المقبوحين');
INSERT INTO "uthmani_simple" VALUES(28,43,'28:43','ولقد ءاتينا موسى الكتب منۢ بعد ما اهلكنا القرون الاولى بصائر للناس وهدى ورحمة لعلهم يتذكرون');
INSERT INTO "uthmani_simple" VALUES(28,44,'28:44','وما كنت بجانب الغربى اذ قضينا الى موسى الامر وما كنت من الشهدين');
INSERT INTO "uthmani_simple" VALUES(28,45,'28:45','ولكنا انشانا قرونا فتطاول عليهم العمر وما كنت ثاويا فى اهل مدين تتلوا۟ عليهم ءايتنا ولكنا كنا مرسلين');
INSERT INTO "uthmani_simple" VALUES(28,46,'28:46','وما كنت بجانب الطور اذ نادينا ولكن رحمة من ربك لتنذر قوما ما اتىهم من نذير من قبلك لعلهم يتذكرون');
INSERT INTO "uthmani_simple" VALUES(28,47,'28:47','ولولا ان تصيبهم مصيبةۢ بما قدمت ايديهم فيقولوا۟ ربنا لولا ارسلت الينا رسولا فنتبع ءايتك ونكون من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(28,48,'28:48','فلما جاءهم الحق من عندنا قالوا۟ لولا اوتى مثل ما اوتى موسى اولم يكفروا۟ بما اوتى موسى من قبل قالوا۟ سحران تظهرا وقالوا۟ انا بكل كفرون');
INSERT INTO "uthmani_simple" VALUES(28,49,'28:49','قل فاتوا۟ بكتب من عند الله هو اهدى منهما اتبعه ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(28,50,'28:50','فان لم يستجيبوا۟ لك فاعلم انما يتبعون اهواءهم ومن اضل ممن اتبع هوىه بغير هدى من الله ان الله لا يهدى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(28,51,'28:51','ولقد وصلنا لهم القول لعلهم يتذكرون');
INSERT INTO "uthmani_simple" VALUES(28,52,'28:52','الذين ءاتينهم الكتب من قبله هم به يؤمنون');
INSERT INTO "uthmani_simple" VALUES(28,53,'28:53','واذا يتلى عليهم قالوا۟ ءامنا به انه الحق من ربنا انا كنا من قبله مسلمين');
INSERT INTO "uthmani_simple" VALUES(28,54,'28:54','او۟لئك يؤتون اجرهم مرتين بما صبروا۟ ويدرءون بالحسنة السيئة ومما رزقنهم ينفقون');
INSERT INTO "uthmani_simple" VALUES(28,55,'28:55','واذا سمعوا۟ اللغو اعرضوا۟ عنه وقالوا۟ لنا اعملنا ولكم اعملكم سلم عليكم لا نبتغى الجهلين');
INSERT INTO "uthmani_simple" VALUES(28,56,'28:56','انك لا تهدى من احببت ولكن الله يهدى من يشاء وهو اعلم بالمهتدين');
INSERT INTO "uthmani_simple" VALUES(28,57,'28:57','وقالوا۟ ان نتبع الهدى معك نتخطف من ارضنا اولم نمكن لهم حرما ءامنا يجبى اليه ثمرت كل شىء رزقا من لدنا ولكن اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(28,58,'28:58','وكم اهلكنا من قريةۭ بطرت معيشتها فتلك مسكنهم لم تسكن منۢ بعدهم الا قليلا وكنا نحن الورثين');
INSERT INTO "uthmani_simple" VALUES(28,59,'28:59','وما كان ربك مهلك القرى حتى يبعث فى امها رسولا يتلوا۟ عليهم ءايتنا وما كنا مهلكى القرى الا واهلها ظلمون');
INSERT INTO "uthmani_simple" VALUES(28,60,'28:60','وما اوتيتم من شىء فمتع الحيوة الدنيا وزينتها وما عند الله خير وابقى افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(28,61,'28:61','افمن وعدنه وعدا حسنا فهو لقيه كمن متعنه متع الحيوة الدنيا ثم هو يوم القيمة من المحضرين');
INSERT INTO "uthmani_simple" VALUES(28,62,'28:62','ويوم يناديهم فيقول اين شركاءى الذين كنتم تزعمون');
INSERT INTO "uthmani_simple" VALUES(28,63,'28:63','قال الذين حق عليهم القول ربنا هؤلاء الذين اغوينا اغوينهم كما غوينا تبرانا اليك ما كانوا۟ ايانا يعبدون');
INSERT INTO "uthmani_simple" VALUES(28,64,'28:64','وقيل ادعوا۟ شركاءكم فدعوهم فلم يستجيبوا۟ لهم وراوا۟ العذاب لو انهم كانوا۟ يهتدون');
INSERT INTO "uthmani_simple" VALUES(28,65,'28:65','ويوم يناديهم فيقول ماذا اجبتم المرسلين');
INSERT INTO "uthmani_simple" VALUES(28,66,'28:66','فعميت عليهم الانۢباء يومئذ فهم لا يتساءلون');
INSERT INTO "uthmani_simple" VALUES(28,67,'28:67','فاما من تاب وءامن وعمل صلحا فعسى ان يكون من المفلحين');
INSERT INTO "uthmani_simple" VALUES(28,68,'28:68','وربك يخلق ما يشاء ويختار ما كان لهم الخيرة سبحن الله وتعلى عما يشركون');
INSERT INTO "uthmani_simple" VALUES(28,69,'28:69','وربك يعلم ما تكن صدورهم وما يعلنون');
INSERT INTO "uthmani_simple" VALUES(28,70,'28:70','وهو الله لا اله الا هو له الحمد فى الاولى والءاخرة وله الحكم واليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(28,71,'28:71','قل ارءيتم ان جعل الله عليكم اليل سرمدا الى يوم القيمة من اله غير الله ياتيكم بضياء افلا تسمعون');
INSERT INTO "uthmani_simple" VALUES(28,72,'28:72','قل ارءيتم ان جعل الله عليكم النهار سرمدا الى يوم القيمة من اله غير الله ياتيكم بليل تسكنون فيه افلا تبصرون');
INSERT INTO "uthmani_simple" VALUES(28,73,'28:73','ومن رحمته جعل لكم اليل والنهار لتسكنوا۟ فيه ولتبتغوا۟ من فضله ولعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(28,74,'28:74','ويوم يناديهم فيقول اين شركاءى الذين كنتم تزعمون');
INSERT INTO "uthmani_simple" VALUES(28,75,'28:75','ونزعنا من كل امة شهيدا فقلنا هاتوا۟ برهنكم فعلموا۟ ان الحق لله وضل عنهم ما كانوا۟ يفترون');
INSERT INTO "uthmani_simple" VALUES(28,76,'28:76','ان قرون كان من قوم موسى فبغى عليهم وءاتينه من الكنوز ما ان مفاتحه لتنوا بالعصبة او۟لى القوة اذ قال له قومه لا تفرح ان الله لا يحب الفرحين');
INSERT INTO "uthmani_simple" VALUES(28,77,'28:77','وابتغ فيما ءاتىك الله الدار الءاخرة ولا تنس نصيبك من الدنيا واحسن كما احسن الله اليك ولا تبغ الفساد فى الارض ان الله لا يحب المفسدين');
INSERT INTO "uthmani_simple" VALUES(28,78,'28:78','قال انما اوتيته على علم عندى اولم يعلم ان الله قد اهلك من قبله من القرون من هو اشد منه قوة واكثر جمعا ولا يسـل عن ذنوبهم المجرمون');
INSERT INTO "uthmani_simple" VALUES(28,79,'28:79','فخرج على قومه فى زينته قال الذين يريدون الحيوة الدنيا يليت لنا مثل ما اوتى قرون انه لذو حظ عظيم');
INSERT INTO "uthmani_simple" VALUES(28,80,'28:80','وقال الذين اوتوا۟ العلم ويلكم ثواب الله خير لمن ءامن وعمل صلحا ولا يلقىها الا الصبرون');
INSERT INTO "uthmani_simple" VALUES(28,81,'28:81','فخسفنا به وبداره الارض فما كان له من فئة ينصرونه من دون الله وما كان من المنتصرين');
INSERT INTO "uthmani_simple" VALUES(28,82,'28:82','واصبح الذين تمنوا۟ مكانه بالامس يقولون ويكان الله يبسط الرزق لمن يشاء من عباده ويقدر لولا ان من الله علينا لخسف بنا ويكانه لا يفلح الكفرون');
INSERT INTO "uthmani_simple" VALUES(28,83,'28:83','تلك الدار الءاخرة نجعلها للذين لا يريدون علوا فى الارض ولا فسادا والعقبة للمتقين');
INSERT INTO "uthmani_simple" VALUES(28,84,'28:84','من جاء بالحسنة فله خير منها ومن جاء بالسيئة فلا يجزى الذين عملوا۟ السيـات الا ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(28,85,'28:85','ان الذى فرض عليك القرءان لرادك الى معاد قل ربى اعلم من جاء بالهدى ومن هو فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(28,86,'28:86','وما كنت ترجوا۟ ان يلقى اليك الكتب الا رحمة من ربك فلا تكونن ظهيرا للكفرين');
INSERT INTO "uthmani_simple" VALUES(28,87,'28:87','ولا يصدنك عن ءايت الله بعد اذ انزلت اليك وادع الى ربك ولا تكونن من المشركين');
INSERT INTO "uthmani_simple" VALUES(28,88,'28:88','ولا تدع مع الله الها ءاخر لا اله الا هو كل شىء هالك الا وجهه له الحكم واليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(29,1,'29:1','الم');
INSERT INTO "uthmani_simple" VALUES(29,2,'29:2','احسب الناس ان يتركوا۟ ان يقولوا۟ ءامنا وهم لا يفتنون');
INSERT INTO "uthmani_simple" VALUES(29,3,'29:3','ولقد فتنا الذين من قبلهم فليعلمن الله الذين صدقوا۟ وليعلمن الكذبين');
INSERT INTO "uthmani_simple" VALUES(29,4,'29:4','ام حسب الذين يعملون السيـات ان يسبقونا ساء ما يحكمون');
INSERT INTO "uthmani_simple" VALUES(29,5,'29:5','من كان يرجوا۟ لقاء الله فان اجل الله لءات وهو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(29,6,'29:6','ومن جهد فانما يجهد لنفسه ان الله لغنى عن العلمين');
INSERT INTO "uthmani_simple" VALUES(29,7,'29:7','والذين ءامنوا۟ وعملوا۟ الصلحت لنكفرن عنهم سيـاتهم ولنجزينهم احسن الذى كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(29,8,'29:8','ووصينا الانسن بولديه حسنا وان جهداك لتشرك بى ما ليس لك به علم فلا تطعهما الى مرجعكم فانبئكم بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(29,9,'29:9','والذين ءامنوا۟ وعملوا۟ الصلحت لندخلنهم فى الصلحين');
INSERT INTO "uthmani_simple" VALUES(29,10,'29:10','ومن الناس من يقول ءامنا بالله فاذا اوذى فى الله جعل فتنة الناس كعذاب الله ولئن جاء نصر من ربك ليقولن انا كنا معكم اوليس الله باعلم بما فى صدور العلمين');
INSERT INTO "uthmani_simple" VALUES(29,11,'29:11','وليعلمن الله الذين ءامنوا۟ وليعلمن المنفقين');
INSERT INTO "uthmani_simple" VALUES(29,12,'29:12','وقال الذين كفروا۟ للذين ءامنوا۟ اتبعوا۟ سبيلنا ولنحمل خطيكم وما هم بحملين من خطيهم من شىء انهم لكذبون');
INSERT INTO "uthmani_simple" VALUES(29,13,'29:13','وليحملن اثقالهم واثقالا مع اثقالهم وليسـلن يوم القيمة عما كانوا۟ يفترون');
INSERT INTO "uthmani_simple" VALUES(29,14,'29:14','ولقد ارسلنا نوحا الى قومه فلبث فيهم الف سنة الا خمسين عاما فاخذهم الطوفان وهم ظلمون');
INSERT INTO "uthmani_simple" VALUES(29,15,'29:15','فانجينه واصحب السفينة وجعلنها ءاية للعلمين');
INSERT INTO "uthmani_simple" VALUES(29,16,'29:16','وابرهيم اذ قال لقومه اعبدوا۟ الله واتقوه ذلكم خير لكم ان كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(29,17,'29:17','انما تعبدون من دون الله اوثنا وتخلقون افكا ان الذين تعبدون من دون الله لا يملكون لكم رزقا فابتغوا۟ عند الله الرزق واعبدوه واشكروا۟ له اليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(29,18,'29:18','وان تكذبوا۟ فقد كذب امم من قبلكم وما على الرسول الا البلغ المبين');
INSERT INTO "uthmani_simple" VALUES(29,19,'29:19','اولم يروا۟ كيف يبدئ الله الخلق ثم يعيده ان ذلك على الله يسير');
INSERT INTO "uthmani_simple" VALUES(29,20,'29:20','قل سيروا۟ فى الارض فانظروا۟ كيف بدا الخلق ثم الله ينشئ النشاة الءاخرة ان الله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(29,21,'29:21','يعذب من يشاء ويرحم من يشاء واليه تقلبون');
INSERT INTO "uthmani_simple" VALUES(29,22,'29:22','وما انتم بمعجزين فى الارض ولا فى السماء وما لكم من دون الله من ولى ولا نصير');
INSERT INTO "uthmani_simple" VALUES(29,23,'29:23','والذين كفروا۟ بـايت الله ولقائه او۟لئك يئسوا۟ من رحمتى واو۟لئك لهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(29,24,'29:24','فما كان جواب قومه الا ان قالوا۟ اقتلوه او حرقوه فانجىه الله من النار ان فى ذلك لءايت لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(29,25,'29:25','وقال انما اتخذتم من دون الله اوثنا مودة بينكم فى الحيوة الدنيا ثم يوم القيمة يكفر بعضكم ببعض ويلعن بعضكم بعضا وماوىكم النار وما لكم من نصرين');
INSERT INTO "uthmani_simple" VALUES(29,26,'29:26','فـامن له لوط وقال انى مهاجر الى ربى انه هو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(29,27,'29:27','ووهبنا له اسحق ويعقوب وجعلنا فى ذريته النبوة والكتب وءاتينه اجره فى الدنيا وانه فى الءاخرة لمن الصلحين');
INSERT INTO "uthmani_simple" VALUES(29,28,'29:28','ولوطا اذ قال لقومه انكم لتاتون الفحشة ما سبقكم بها من احد من العلمين');
INSERT INTO "uthmani_simple" VALUES(29,29,'29:29','ائنكم لتاتون الرجال وتقطعون السبيل وتاتون فى ناديكم المنكر فما كان جواب قومه الا ان قالوا۟ ائتنا بعذاب الله ان كنت من الصدقين');
INSERT INTO "uthmani_simple" VALUES(29,30,'29:30','قال رب انصرنى على القوم المفسدين');
INSERT INTO "uthmani_simple" VALUES(29,31,'29:31','ولما جاءت رسلنا ابرهيم بالبشرى قالوا۟ انا مهلكوا۟ اهل هذه القرية ان اهلها كانوا۟ ظلمين');
INSERT INTO "uthmani_simple" VALUES(29,32,'29:32','قال ان فيها لوطا قالوا۟ نحن اعلم بمن فيها لننجينه واهله الا امراته كانت من الغبرين');
INSERT INTO "uthmani_simple" VALUES(29,33,'29:33','ولما ان جاءت رسلنا لوطا سىء بهم وضاق بهم ذرعا وقالوا۟ لا تخف ولا تحزن انا منجوك واهلك الا امراتك كانت من الغبرين');
INSERT INTO "uthmani_simple" VALUES(29,34,'29:34','انا منزلون على اهل هذه القرية رجزا من السماء بما كانوا۟ يفسقون');
INSERT INTO "uthmani_simple" VALUES(29,35,'29:35','ولقد تركنا منها ءايةۢ بينة لقوم يعقلون');
INSERT INTO "uthmani_simple" VALUES(29,36,'29:36','والى مدين اخاهم شعيبا فقال يقوم اعبدوا۟ الله وارجوا۟ اليوم الءاخر ولا تعثوا۟ فى الارض مفسدين');
INSERT INTO "uthmani_simple" VALUES(29,37,'29:37','فكذبوه فاخذتهم الرجفة فاصبحوا۟ فى دارهم جثمين');
INSERT INTO "uthmani_simple" VALUES(29,38,'29:38','وعادا وثمودا۟ وقد تبين لكم من مسكنهم وزين لهم الشيطن اعملهم فصدهم عن السبيل وكانوا۟ مستبصرين');
INSERT INTO "uthmani_simple" VALUES(29,39,'29:39','وقرون وفرعون وهمن ولقد جاءهم موسى بالبينت فاستكبروا۟ فى الارض وما كانوا۟ سبقين');
INSERT INTO "uthmani_simple" VALUES(29,40,'29:40','فكلا اخذنا بذنۢبه فمنهم من ارسلنا عليه حاصبا ومنهم من اخذته الصيحة ومنهم من خسفنا به الارض ومنهم من اغرقنا وما كان الله ليظلمهم ولكن كانوا۟ انفسهم يظلمون');
INSERT INTO "uthmani_simple" VALUES(29,41,'29:41','مثل الذين اتخذوا۟ من دون الله اولياء كمثل العنكبوت اتخذت بيتا وان اوهن البيوت لبيت العنكبوت لو كانوا۟ يعلمون');
INSERT INTO "uthmani_simple" VALUES(29,42,'29:42','ان الله يعلم ما يدعون من دونه من شىء وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(29,43,'29:43','وتلك الامثل نضربها للناس وما يعقلها الا العلمون');
INSERT INTO "uthmani_simple" VALUES(29,44,'29:44','خلق الله السموت والارض بالحق ان فى ذلك لءاية للمؤمنين');
INSERT INTO "uthmani_simple" VALUES(29,45,'29:45','اتل ما اوحى اليك من الكتب واقم الصلوة ان الصلوة تنهى عن الفحشاء والمنكر ولذكر الله اكبر والله يعلم ما تصنعون');
INSERT INTO "uthmani_simple" VALUES(29,46,'29:46','ولا تجدلوا۟ اهل الكتب الا بالتى هى احسن الا الذين ظلموا۟ منهم وقولوا۟ ءامنا بالذى انزل الينا وانزل اليكم والهنا والهكم وحد ونحن له مسلمون');
INSERT INTO "uthmani_simple" VALUES(29,47,'29:47','وكذلك انزلنا اليك الكتب فالذين ءاتينهم الكتب يؤمنون به ومن هؤلاء من يؤمن به وما يجحد بـايتنا الا الكفرون');
INSERT INTO "uthmani_simple" VALUES(29,48,'29:48','وما كنت تتلوا۟ من قبله من كتب ولا تخطه بيمينك اذا لارتاب المبطلون');
INSERT INTO "uthmani_simple" VALUES(29,49,'29:49','بل هو ءايتۢ بينت فى صدور الذين اوتوا۟ العلم وما يجحد بـايتنا الا الظلمون');
INSERT INTO "uthmani_simple" VALUES(29,50,'29:50','وقالوا۟ لولا انزل عليه ءايت من ربه قل انما الءايت عند الله وانما انا۠ نذير مبين');
INSERT INTO "uthmani_simple" VALUES(29,51,'29:51','اولم يكفهم انا انزلنا عليك الكتب يتلى عليهم ان فى ذلك لرحمة وذكرى لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(29,52,'29:52','قل كفى بالله بينى وبينكم شهيدا يعلم ما فى السموت والارض والذين ءامنوا۟ بالبطل وكفروا۟ بالله او۟لئك هم الخسرون');
INSERT INTO "uthmani_simple" VALUES(29,53,'29:53','ويستعجلونك بالعذاب ولولا اجل مسمى لجاءهم العذاب ولياتينهم بغتة وهم لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(29,54,'29:54','يستعجلونك بالعذاب وان جهنم لمحيطةۢ بالكفرين');
INSERT INTO "uthmani_simple" VALUES(29,55,'29:55','يوم يغشىهم العذاب من فوقهم ومن تحت ارجلهم ويقول ذوقوا۟ ما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(29,56,'29:56','يعبادى الذين ءامنوا۟ ان ارضى وسعة فايى فاعبدون');
INSERT INTO "uthmani_simple" VALUES(29,57,'29:57','كل نفس ذائقة الموت ثم الينا ترجعون');
INSERT INTO "uthmani_simple" VALUES(29,58,'29:58','والذين ءامنوا۟ وعملوا۟ الصلحت لنبوئنهم من الجنة غرفا تجرى من تحتها الانهر خلدين فيها نعم اجر العملين');
INSERT INTO "uthmani_simple" VALUES(29,59,'29:59','الذين صبروا۟ وعلى ربهم يتوكلون');
INSERT INTO "uthmani_simple" VALUES(29,60,'29:60','وكاين من دابة لا تحمل رزقها الله يرزقها واياكم وهو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(29,61,'29:61','ولئن سالتهم من خلق السموت والارض وسخر الشمس والقمر ليقولن الله فانى يؤفكون');
INSERT INTO "uthmani_simple" VALUES(29,62,'29:62','الله يبسط الرزق لمن يشاء من عباده ويقدر له ان الله بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(29,63,'29:63','ولئن سالتهم من نزل من السماء ماء فاحيا به الارض منۢ بعد موتها ليقولن الله قل الحمد لله بل اكثرهم لا يعقلون');
INSERT INTO "uthmani_simple" VALUES(29,64,'29:64','وما هذه الحيوة الدنيا الا لهو ولعب وان الدار الءاخرة لهى الحيوان لو كانوا۟ يعلمون');
INSERT INTO "uthmani_simple" VALUES(29,65,'29:65','فاذا ركبوا۟ فى الفلك دعوا۟ الله مخلصين له الدين فلما نجىهم الى البر اذا هم يشركون');
INSERT INTO "uthmani_simple" VALUES(29,66,'29:66','ليكفروا۟ بما ءاتينهم وليتمتعوا۟ فسوف يعلمون');
INSERT INTO "uthmani_simple" VALUES(29,67,'29:67','اولم يروا۟ انا جعلنا حرما ءامنا ويتخطف الناس من حولهم افبالبطل يؤمنون وبنعمة الله يكفرون');
INSERT INTO "uthmani_simple" VALUES(29,68,'29:68','ومن اظلم ممن افترى على الله كذبا او كذب بالحق لما جاءه اليس فى جهنم مثوى للكفرين');
INSERT INTO "uthmani_simple" VALUES(29,69,'29:69','والذين جهدوا۟ فينا لنهدينهم سبلنا وان الله لمع المحسنين');
INSERT INTO "uthmani_simple" VALUES(30,1,'30:1','الم');
INSERT INTO "uthmani_simple" VALUES(30,2,'30:2','غلبت الروم');
INSERT INTO "uthmani_simple" VALUES(30,3,'30:3','فى ادنى الارض وهم منۢ بعد غلبهم سيغلبون');
INSERT INTO "uthmani_simple" VALUES(30,4,'30:4','فى بضع سنين لله الامر من قبل ومنۢ بعد ويومئذ يفرح المؤمنون');
INSERT INTO "uthmani_simple" VALUES(30,5,'30:5','بنصر الله ينصر من يشاء وهو العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(30,6,'30:6','وعد الله لا يخلف الله وعده ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(30,7,'30:7','يعلمون ظهرا من الحيوة الدنيا وهم عن الءاخرة هم غفلون');
INSERT INTO "uthmani_simple" VALUES(30,8,'30:8','اولم يتفكروا۟ فى انفسهم ما خلق الله السموت والارض وما بينهما الا بالحق واجل مسمى وان كثيرا من الناس بلقائ ربهم لكفرون');
INSERT INTO "uthmani_simple" VALUES(30,9,'30:9','اولم يسيروا۟ فى الارض فينظروا۟ كيف كان عقبة الذين من قبلهم كانوا۟ اشد منهم قوة واثاروا۟ الارض وعمروها اكثر مما عمروها وجاءتهم رسلهم بالبينت فما كان الله ليظلمهم ولكن كانوا۟ انفسهم يظلمون');
INSERT INTO "uthmani_simple" VALUES(30,10,'30:10','ثم كان عقبة الذين اسـوا۟ السواى ان كذبوا۟ بـايت الله وكانوا۟ بها يستهزءون');
INSERT INTO "uthmani_simple" VALUES(30,11,'30:11','الله يبدؤا۟ الخلق ثم يعيده ثم اليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(30,12,'30:12','ويوم تقوم الساعة يبلس المجرمون');
INSERT INTO "uthmani_simple" VALUES(30,13,'30:13','ولم يكن لهم من شركائهم شفعؤا۟ وكانوا۟ بشركائهم كفرين');
INSERT INTO "uthmani_simple" VALUES(30,14,'30:14','ويوم تقوم الساعة يومئذ يتفرقون');
INSERT INTO "uthmani_simple" VALUES(30,15,'30:15','فاما الذين ءامنوا۟ وعملوا۟ الصلحت فهم فى روضة يحبرون');
INSERT INTO "uthmani_simple" VALUES(30,16,'30:16','واما الذين كفروا۟ وكذبوا۟ بـايتنا ولقائ الءاخرة فاو۟لئك فى العذاب محضرون');
INSERT INTO "uthmani_simple" VALUES(30,17,'30:17','فسبحن الله حين تمسون وحين تصبحون');
INSERT INTO "uthmani_simple" VALUES(30,18,'30:18','وله الحمد فى السموت والارض وعشيا وحين تظهرون');
INSERT INTO "uthmani_simple" VALUES(30,19,'30:19','يخرج الحى من الميت ويخرج الميت من الحى ويحى الارض بعد موتها وكذلك تخرجون');
INSERT INTO "uthmani_simple" VALUES(30,20,'30:20','ومن ءايته ان خلقكم من تراب ثم اذا انتم بشر تنتشرون');
INSERT INTO "uthmani_simple" VALUES(30,21,'30:21','ومن ءايته ان خلق لكم من انفسكم ازوجا لتسكنوا۟ اليها وجعل بينكم مودة ورحمة ان فى ذلك لءايت لقوم يتفكرون');
INSERT INTO "uthmani_simple" VALUES(30,22,'30:22','ومن ءايته خلق السموت والارض واختلف السنتكم والونكم ان فى ذلك لءايت للعلمين');
INSERT INTO "uthmani_simple" VALUES(30,23,'30:23','ومن ءايته منامكم باليل والنهار وابتغاؤكم من فضله ان فى ذلك لءايت لقوم يسمعون');
INSERT INTO "uthmani_simple" VALUES(30,24,'30:24','ومن ءايته يريكم البرق خوفا وطمعا وينزل من السماء ماء فيحى به الارض بعد موتها ان فى ذلك لءايت لقوم يعقلون');
INSERT INTO "uthmani_simple" VALUES(30,25,'30:25','ومن ءايته ان تقوم السماء والارض بامره ثم اذا دعاكم دعوة من الارض اذا انتم تخرجون');
INSERT INTO "uthmani_simple" VALUES(30,26,'30:26','وله من فى السموت والارض كل له قنتون');
INSERT INTO "uthmani_simple" VALUES(30,27,'30:27','وهو الذى يبدؤا۟ الخلق ثم يعيده وهو اهون عليه وله المثل الاعلى فى السموت والارض وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(30,28,'30:28','ضرب لكم مثلا من انفسكم هل لكم من ما ملكت ايمنكم من شركاء فى ما رزقنكم فانتم فيه سواء تخافونهم كخيفتكم انفسكم كذلك نفصل الءايت لقوم يعقلون');
INSERT INTO "uthmani_simple" VALUES(30,29,'30:29','بل اتبع الذين ظلموا۟ اهواءهم بغير علم فمن يهدى من اضل الله وما لهم من نصرين');
INSERT INTO "uthmani_simple" VALUES(30,30,'30:30','فاقم وجهك للدين حنيفا فطرت الله التى فطر الناس عليها لا تبديل لخلق الله ذلك الدين القيم ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(30,31,'30:31','منيبين اليه واتقوه واقيموا۟ الصلوة ولا تكونوا۟ من المشركين');
INSERT INTO "uthmani_simple" VALUES(30,32,'30:32','من الذين فرقوا۟ دينهم وكانوا۟ شيعا كل حزبۭ بما لديهم فرحون');
INSERT INTO "uthmani_simple" VALUES(30,33,'30:33','واذا مس الناس ضر دعوا۟ ربهم منيبين اليه ثم اذا اذاقهم منه رحمة اذا فريق منهم بربهم يشركون');
INSERT INTO "uthmani_simple" VALUES(30,34,'30:34','ليكفروا۟ بما ءاتينهم فتمتعوا۟ فسوف تعلمون');
INSERT INTO "uthmani_simple" VALUES(30,35,'30:35','ام انزلنا عليهم سلطنا فهو يتكلم بما كانوا۟ به يشركون');
INSERT INTO "uthmani_simple" VALUES(30,36,'30:36','واذا اذقنا الناس رحمة فرحوا۟ بها وان تصبهم سيئةۢ بما قدمت ايديهم اذا هم يقنطون');
INSERT INTO "uthmani_simple" VALUES(30,37,'30:37','اولم يروا۟ ان الله يبسط الرزق لمن يشاء ويقدر ان فى ذلك لءايت لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(30,38,'30:38','فـات ذا القربى حقه والمسكين وابن السبيل ذلك خير للذين يريدون وجه الله واو۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(30,39,'30:39','وما ءاتيتم من ربا ليربوا۟ فى امول الناس فلا يربوا۟ عند الله وما ءاتيتم من زكوة تريدون وجه الله فاو۟لئك هم المضعفون');
INSERT INTO "uthmani_simple" VALUES(30,40,'30:40','الله الذى خلقكم ثم رزقكم ثم يميتكم ثم يحييكم هل من شركائكم من يفعل من ذلكم من شىء سبحنه وتعلى عما يشركون');
INSERT INTO "uthmani_simple" VALUES(30,41,'30:41','ظهر الفساد فى البر والبحر بما كسبت ايدى الناس ليذيقهم بعض الذى عملوا۟ لعلهم يرجعون');
INSERT INTO "uthmani_simple" VALUES(30,42,'30:42','قل سيروا۟ فى الارض فانظروا۟ كيف كان عقبة الذين من قبل كان اكثرهم مشركين');
INSERT INTO "uthmani_simple" VALUES(30,43,'30:43','فاقم وجهك للدين القيم من قبل ان ياتى يوم لا مرد له من الله يومئذ يصدعون');
INSERT INTO "uthmani_simple" VALUES(30,44,'30:44','من كفر فعليه كفره ومن عمل صلحا فلانفسهم يمهدون');
INSERT INTO "uthmani_simple" VALUES(30,45,'30:45','ليجزى الذين ءامنوا۟ وعملوا۟ الصلحت من فضله انه لا يحب الكفرين');
INSERT INTO "uthmani_simple" VALUES(30,46,'30:46','ومن ءايته ان يرسل الرياح مبشرت وليذيقكم من رحمته ولتجرى الفلك بامره ولتبتغوا۟ من فضله ولعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(30,47,'30:47','ولقد ارسلنا من قبلك رسلا الى قومهم فجاءوهم بالبينت فانتقمنا من الذين اجرموا۟ وكان حقا علينا نصر المؤمنين');
INSERT INTO "uthmani_simple" VALUES(30,48,'30:48','الله الذى يرسل الريح فتثير سحابا فيبسطه فى السماء كيف يشاء ويجعله كسفا فترى الودق يخرج من خلله فاذا اصاب به من يشاء من عباده اذا هم يستبشرون');
INSERT INTO "uthmani_simple" VALUES(30,49,'30:49','وان كانوا۟ من قبل ان ينزل عليهم من قبله لمبلسين');
INSERT INTO "uthmani_simple" VALUES(30,50,'30:50','فانظر الى ءاثر رحمت الله كيف يحى الارض بعد موتها ان ذلك لمحى الموتى وهو على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(30,51,'30:51','ولئن ارسلنا ريحا فراوه مصفرا لظلوا۟ منۢ بعده يكفرون');
INSERT INTO "uthmani_simple" VALUES(30,52,'30:52','فانك لا تسمع الموتى ولا تسمع الصم الدعاء اذا ولوا۟ مدبرين');
INSERT INTO "uthmani_simple" VALUES(30,53,'30:53','وما انت بهد العمى عن ضللتهم ان تسمع الا من يؤمن بـايتنا فهم مسلمون');
INSERT INTO "uthmani_simple" VALUES(30,54,'30:54','الله الذى خلقكم من ضعف ثم جعل منۢ بعد ضعف قوة ثم جعل منۢ بعد قوة ضعفا وشيبة يخلق ما يشاء وهو العليم القدير');
INSERT INTO "uthmani_simple" VALUES(30,55,'30:55','ويوم تقوم الساعة يقسم المجرمون ما لبثوا۟ غير ساعة كذلك كانوا۟ يؤفكون');
INSERT INTO "uthmani_simple" VALUES(30,56,'30:56','وقال الذين اوتوا۟ العلم والايمن لقد لبثتم فى كتب الله الى يوم البعث فهذا يوم البعث ولكنكم كنتم لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(30,57,'30:57','فيومئذ لا ينفع الذين ظلموا۟ معذرتهم ولا هم يستعتبون');
INSERT INTO "uthmani_simple" VALUES(30,58,'30:58','ولقد ضربنا للناس فى هذا القرءان من كل مثل ولئن جئتهم بـاية ليقولن الذين كفروا۟ ان انتم الا مبطلون');
INSERT INTO "uthmani_simple" VALUES(30,59,'30:59','كذلك يطبع الله على قلوب الذين لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(30,60,'30:60','فاصبر ان وعد الله حق ولا يستخفنك الذين لا يوقنون');
INSERT INTO "uthmani_simple" VALUES(31,1,'31:1','الم');
INSERT INTO "uthmani_simple" VALUES(31,2,'31:2','تلك ءايت الكتب الحكيم');
INSERT INTO "uthmani_simple" VALUES(31,3,'31:3','هدى ورحمة للمحسنين');
INSERT INTO "uthmani_simple" VALUES(31,4,'31:4','الذين يقيمون الصلوة ويؤتون الزكوة وهم بالءاخرة هم يوقنون');
INSERT INTO "uthmani_simple" VALUES(31,5,'31:5','او۟لئك على هدى من ربهم واو۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(31,6,'31:6','ومن الناس من يشترى لهو الحديث ليضل عن سبيل الله بغير علم ويتخذها هزوا او۟لئك لهم عذاب مهين');
INSERT INTO "uthmani_simple" VALUES(31,7,'31:7','واذا تتلى عليه ءايتنا ولى مستكبرا كان لم يسمعها كان فى اذنيه وقرا فبشره بعذاب اليم');
INSERT INTO "uthmani_simple" VALUES(31,8,'31:8','ان الذين ءامنوا۟ وعملوا۟ الصلحت لهم جنت النعيم');
INSERT INTO "uthmani_simple" VALUES(31,9,'31:9','خلدين فيها وعد الله حقا وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(31,10,'31:10','خلق السموت بغير عمد ترونها والقى فى الارض روسى ان تميد بكم وبث فيها من كل دابة وانزلنا من السماء ماء فانۢبتنا فيها من كل زوج كريم');
INSERT INTO "uthmani_simple" VALUES(31,11,'31:11','هذا خلق الله فارونى ماذا خلق الذين من دونه بل الظلمون فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(31,12,'31:12','ولقد ءاتينا لقمن الحكمة ان اشكر لله ومن يشكر فانما يشكر لنفسه ومن كفر فان الله غنى حميد');
INSERT INTO "uthmani_simple" VALUES(31,13,'31:13','واذ قال لقمن لابنه وهو يعظه يبنى لا تشرك بالله ان الشرك لظلم عظيم');
INSERT INTO "uthmani_simple" VALUES(31,14,'31:14','ووصينا الانسن بولديه حملته امه وهنا على وهن وفصله فى عامين ان اشكر لى ولولديك الى المصير');
INSERT INTO "uthmani_simple" VALUES(31,15,'31:15','وان جهداك على ان تشرك بى ما ليس لك به علم فلا تطعهما وصاحبهما فى الدنيا معروفا واتبع سبيل من اناب الى ثم الى مرجعكم فانبئكم بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(31,16,'31:16','يبنى انها ان تك مثقال حبة من خردل فتكن فى صخرة او فى السموت او فى الارض يات بها الله ان الله لطيف خبير');
INSERT INTO "uthmani_simple" VALUES(31,17,'31:17','يبنى اقم الصلوة وامر بالمعروف وانه عن المنكر واصبر على ما اصابك ان ذلك من عزم الامور');
INSERT INTO "uthmani_simple" VALUES(31,18,'31:18','ولا تصعر خدك للناس ولا تمش فى الارض مرحا ان الله لا يحب كل مختال فخور');
INSERT INTO "uthmani_simple" VALUES(31,19,'31:19','واقصد فى مشيك واغضض من صوتك ان انكر الاصوت لصوت الحمير');
INSERT INTO "uthmani_simple" VALUES(31,20,'31:20','الم تروا۟ ان الله سخر لكم ما فى السموت وما فى الارض واسبغ عليكم نعمه ظهرة وباطنة ومن الناس من يجدل فى الله بغير علم ولا هدى ولا كتب منير');
INSERT INTO "uthmani_simple" VALUES(31,21,'31:21','واذا قيل لهم اتبعوا۟ ما انزل الله قالوا۟ بل نتبع ما وجدنا عليه ءاباءنا اولو كان الشيطن يدعوهم الى عذاب السعير');
INSERT INTO "uthmani_simple" VALUES(31,22,'31:22','ومن يسلم وجهه الى الله وهو محسن فقد استمسك بالعروة الوثقى والى الله عقبة الامور');
INSERT INTO "uthmani_simple" VALUES(31,23,'31:23','ومن كفر فلا يحزنك كفره الينا مرجعهم فننبئهم بما عملوا۟ ان الله عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(31,24,'31:24','نمتعهم قليلا ثم نضطرهم الى عذاب غليظ');
INSERT INTO "uthmani_simple" VALUES(31,25,'31:25','ولئن سالتهم من خلق السموت والارض ليقولن الله قل الحمد لله بل اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(31,26,'31:26','لله ما فى السموت والارض ان الله هو الغنى الحميد');
INSERT INTO "uthmani_simple" VALUES(31,27,'31:27','ولو انما فى الارض من شجرة اقلم والبحر يمده منۢ بعده سبعة ابحر ما نفدت كلمت الله ان الله عزيز حكيم');
INSERT INTO "uthmani_simple" VALUES(31,28,'31:28','ما خلقكم ولا بعثكم الا كنفس وحدة ان الله سميعۢ بصير');
INSERT INTO "uthmani_simple" VALUES(31,29,'31:29','الم تر ان الله يولج اليل فى النهار ويولج النهار فى اليل وسخر الشمس والقمر كل يجرى الى اجل مسمى وان الله بما تعملون خبير');
INSERT INTO "uthmani_simple" VALUES(31,30,'31:30','ذلك بان الله هو الحق وان ما يدعون من دونه البطل وان الله هو العلى الكبير');
INSERT INTO "uthmani_simple" VALUES(31,31,'31:31','الم تر ان الفلك تجرى فى البحر بنعمت الله ليريكم من ءايته ان فى ذلك لءايت لكل صبار شكور');
INSERT INTO "uthmani_simple" VALUES(31,32,'31:32','واذا غشيهم موج كالظلل دعوا۟ الله مخلصين له الدين فلما نجىهم الى البر فمنهم مقتصد وما يجحد بـايتنا الا كل ختار كفور');
INSERT INTO "uthmani_simple" VALUES(31,33,'31:33','يايها الناس اتقوا۟ ربكم واخشوا۟ يوما لا يجزى والد عن ولده ولا مولود هو جاز عن والده شيـا ان وعد الله حق فلا تغرنكم الحيوة الدنيا ولا يغرنكم بالله الغرور');
INSERT INTO "uthmani_simple" VALUES(31,34,'31:34','ان الله عنده علم الساعة وينزل الغيث ويعلم ما فى الارحام وما تدرى نفس ماذا تكسب غدا وما تدرى نفسۢ باى ارض تموت ان الله عليم خبيرۢ');
INSERT INTO "uthmani_simple" VALUES(32,1,'32:1','الم');
INSERT INTO "uthmani_simple" VALUES(32,2,'32:2','تنزيل الكتب لا ريب فيه من رب العلمين');
INSERT INTO "uthmani_simple" VALUES(32,3,'32:3','ام يقولون افترىه بل هو الحق من ربك لتنذر قوما ما اتىهم من نذير من قبلك لعلهم يهتدون');
INSERT INTO "uthmani_simple" VALUES(32,4,'32:4','الله الذى خلق السموت والارض وما بينهما فى ستة ايام ثم استوى على العرش ما لكم من دونه من ولى ولا شفيع افلا تتذكرون');
INSERT INTO "uthmani_simple" VALUES(32,5,'32:5','يدبر الامر من السماء الى الارض ثم يعرج اليه فى يوم كان مقداره الف سنة مما تعدون');
INSERT INTO "uthmani_simple" VALUES(32,6,'32:6','ذلك علم الغيب والشهدة العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(32,7,'32:7','الذى احسن كل شىء خلقه وبدا خلق الانسن من طين');
INSERT INTO "uthmani_simple" VALUES(32,8,'32:8','ثم جعل نسله من سللة من ماء مهين');
INSERT INTO "uthmani_simple" VALUES(32,9,'32:9','ثم سوىه ونفخ فيه من روحه وجعل لكم السمع والابصر والافـدة قليلا ما تشكرون');
INSERT INTO "uthmani_simple" VALUES(32,10,'32:10','وقالوا۟ اءذا ضللنا فى الارض اءنا لفى خلق جديدۭ بل هم بلقاء ربهم كفرون');
INSERT INTO "uthmani_simple" VALUES(32,11,'32:11','قل يتوفىكم ملك الموت الذى وكل بكم ثم الى ربكم ترجعون');
INSERT INTO "uthmani_simple" VALUES(32,12,'32:12','ولو ترى اذ المجرمون ناكسوا۟ رءوسهم عند ربهم ربنا ابصرنا وسمعنا فارجعنا نعمل صلحا انا موقنون');
INSERT INTO "uthmani_simple" VALUES(32,13,'32:13','ولو شئنا لءاتينا كل نفس هدىها ولكن حق القول منى لاملان جهنم من الجنة والناس اجمعين');
INSERT INTO "uthmani_simple" VALUES(32,14,'32:14','فذوقوا۟ بما نسيتم لقاء يومكم هذا انا نسينكم وذوقوا۟ عذاب الخلد بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(32,15,'32:15','انما يؤمن بـايتنا الذين اذا ذكروا۟ بها خروا۟ سجدا وسبحوا۟ بحمد ربهم وهم لا يستكبرون');
INSERT INTO "uthmani_simple" VALUES(32,16,'32:16','تتجافى جنوبهم عن المضاجع يدعون ربهم خوفا وطمعا ومما رزقنهم ينفقون');
INSERT INTO "uthmani_simple" VALUES(32,17,'32:17','فلا تعلم نفس ما اخفى لهم من قرة اعين جزاءۢ بما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(32,18,'32:18','افمن كان مؤمنا كمن كان فاسقا لا يستون');
INSERT INTO "uthmani_simple" VALUES(32,19,'32:19','اما الذين ءامنوا۟ وعملوا۟ الصلحت فلهم جنت الماوى نزلۢا بما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(32,20,'32:20','واما الذين فسقوا۟ فماوىهم النار كلما ارادوا۟ ان يخرجوا۟ منها اعيدوا۟ فيها وقيل لهم ذوقوا۟ عذاب النار الذى كنتم به تكذبون');
INSERT INTO "uthmani_simple" VALUES(32,21,'32:21','ولنذيقنهم من العذاب الادنى دون العذاب الاكبر لعلهم يرجعون');
INSERT INTO "uthmani_simple" VALUES(32,22,'32:22','ومن اظلم ممن ذكر بـايت ربه ثم اعرض عنها انا من المجرمين منتقمون');
INSERT INTO "uthmani_simple" VALUES(32,23,'32:23','ولقد ءاتينا موسى الكتب فلا تكن فى مرية من لقائه وجعلنه هدى لبنى اسرءيل');
INSERT INTO "uthmani_simple" VALUES(32,24,'32:24','وجعلنا منهم ائمة يهدون بامرنا لما صبروا۟ وكانوا۟ بـايتنا يوقنون');
INSERT INTO "uthmani_simple" VALUES(32,25,'32:25','ان ربك هو يفصل بينهم يوم القيمة فيما كانوا۟ فيه يختلفون');
INSERT INTO "uthmani_simple" VALUES(32,26,'32:26','اولم يهد لهم كم اهلكنا من قبلهم من القرون يمشون فى مسكنهم ان فى ذلك لءايت افلا يسمعون');
INSERT INTO "uthmani_simple" VALUES(32,27,'32:27','اولم يروا۟ انا نسوق الماء الى الارض الجرز فنخرج به زرعا تاكل منه انعمهم وانفسهم افلا يبصرون');
INSERT INTO "uthmani_simple" VALUES(32,28,'32:28','ويقولون متى هذا الفتح ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(32,29,'32:29','قل يوم الفتح لا ينفع الذين كفروا۟ ايمنهم ولا هم ينظرون');
INSERT INTO "uthmani_simple" VALUES(32,30,'32:30','فاعرض عنهم وانتظر انهم منتظرون');
INSERT INTO "uthmani_simple" VALUES(33,1,'33:1','يايها النبى اتق الله ولا تطع الكفرين والمنفقين ان الله كان عليما حكيما');
INSERT INTO "uthmani_simple" VALUES(33,2,'33:2','واتبع ما يوحى اليك من ربك ان الله كان بما تعملون خبيرا');
INSERT INTO "uthmani_simple" VALUES(33,3,'33:3','وتوكل على الله وكفى بالله وكيلا');
INSERT INTO "uthmani_simple" VALUES(33,4,'33:4','ما جعل الله لرجل من قلبين فى جوفه وما جعل ازوجكم الـى تظهرون منهن امهتكم وما جعل ادعياءكم ابناءكم ذلكم قولكم بافوهكم والله يقول الحق وهو يهدى السبيل');
INSERT INTO "uthmani_simple" VALUES(33,5,'33:5','ادعوهم لءابائهم هو اقسط عند الله فان لم تعلموا۟ ءاباءهم فاخونكم فى الدين وموليكم وليس عليكم جناح فيما اخطاتم به ولكن ما تعمدت قلوبكم وكان الله غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(33,6,'33:6','النبى اولى بالمؤمنين من انفسهم وازوجه امهتهم واو۟لوا۟ الارحام بعضهم اولى ببعض فى كتب الله من المؤمنين والمهجرين الا ان تفعلوا۟ الى اوليائكم معروفا كان ذلك فى الكتب مسطورا');
INSERT INTO "uthmani_simple" VALUES(33,7,'33:7','واذ اخذنا من النبين ميثقهم ومنك ومن نوح وابرهيم وموسى وعيسى ابن مريم واخذنا منهم ميثقا غليظا');
INSERT INTO "uthmani_simple" VALUES(33,8,'33:8','ليسـل الصدقين عن صدقهم واعد للكفرين عذابا اليما');
INSERT INTO "uthmani_simple" VALUES(33,9,'33:9','يايها الذين ءامنوا۟ اذكروا۟ نعمة الله عليكم اذ جاءتكم جنود فارسلنا عليهم ريحا وجنودا لم تروها وكان الله بما تعملون بصيرا');
INSERT INTO "uthmani_simple" VALUES(33,10,'33:10','اذ جاءوكم من فوقكم ومن اسفل منكم واذ زاغت الابصر وبلغت القلوب الحناجر وتظنون بالله الظنونا۠');
INSERT INTO "uthmani_simple" VALUES(33,11,'33:11','هنالك ابتلى المؤمنون وزلزلوا۟ زلزالا شديدا');
INSERT INTO "uthmani_simple" VALUES(33,12,'33:12','واذ يقول المنفقون والذين فى قلوبهم مرض ما وعدنا الله ورسوله الا غرورا');
INSERT INTO "uthmani_simple" VALUES(33,13,'33:13','واذ قالت طائفة منهم ياهل يثرب لا مقام لكم فارجعوا۟ ويستـذن فريق منهم النبى يقولون ان بيوتنا عورة وما هى بعورة ان يريدون الا فرارا');
INSERT INTO "uthmani_simple" VALUES(33,14,'33:14','ولو دخلت عليهم من اقطارها ثم سئلوا۟ الفتنة لءاتوها وما تلبثوا۟ بها الا يسيرا');
INSERT INTO "uthmani_simple" VALUES(33,15,'33:15','ولقد كانوا۟ عهدوا۟ الله من قبل لا يولون الادبر وكان عهد الله مسـولا');
INSERT INTO "uthmani_simple" VALUES(33,16,'33:16','قل لن ينفعكم الفرار ان فررتم من الموت او القتل واذا لا تمتعون الا قليلا');
INSERT INTO "uthmani_simple" VALUES(33,17,'33:17','قل من ذا الذى يعصمكم من الله ان اراد بكم سوءا او اراد بكم رحمة ولا يجدون لهم من دون الله وليا ولا نصيرا');
INSERT INTO "uthmani_simple" VALUES(33,18,'33:18','قد يعلم الله المعوقين منكم والقائلين لاخونهم هلم الينا ولا ياتون الباس الا قليلا');
INSERT INTO "uthmani_simple" VALUES(33,19,'33:19','اشحة عليكم فاذا جاء الخوف رايتهم ينظرون اليك تدور اعينهم كالذى يغشى عليه من الموت فاذا ذهب الخوف سلقوكم بالسنة حداد اشحة على الخير او۟لئك لم يؤمنوا۟ فاحبط الله اعملهم وكان ذلك على الله يسيرا');
INSERT INTO "uthmani_simple" VALUES(33,20,'33:20','يحسبون الاحزاب لم يذهبوا۟ وان يات الاحزاب يودوا۟ لو انهم بادون فى الاعراب يسـلون عن انۢبائكم ولو كانوا۟ فيكم ما قتلوا۟ الا قليلا');
INSERT INTO "uthmani_simple" VALUES(33,21,'33:21','لقد كان لكم فى رسول الله اسوة حسنة لمن كان يرجوا۟ الله واليوم الءاخر وذكر الله كثيرا');
INSERT INTO "uthmani_simple" VALUES(33,22,'33:22','ولما رءا المؤمنون الاحزاب قالوا۟ هذا ما وعدنا الله ورسوله وصدق الله ورسوله وما زادهم الا ايمنا وتسليما');
INSERT INTO "uthmani_simple" VALUES(33,23,'33:23','من المؤمنين رجال صدقوا۟ ما عهدوا۟ الله عليه فمنهم من قضى نحبه ومنهم من ينتظر وما بدلوا۟ تبديلا');
INSERT INTO "uthmani_simple" VALUES(33,24,'33:24','ليجزى الله الصدقين بصدقهم ويعذب المنفقين ان شاء او يتوب عليهم ان الله كان غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(33,25,'33:25','ورد الله الذين كفروا۟ بغيظهم لم ينالوا۟ خيرا وكفى الله المؤمنين القتال وكان الله قويا عزيزا');
INSERT INTO "uthmani_simple" VALUES(33,26,'33:26','وانزل الذين ظهروهم من اهل الكتب من صياصيهم وقذف فى قلوبهم الرعب فريقا تقتلون وتاسرون فريقا');
INSERT INTO "uthmani_simple" VALUES(33,27,'33:27','واورثكم ارضهم وديرهم وامولهم وارضا لم تطـوها وكان الله على كل شىء قديرا');
INSERT INTO "uthmani_simple" VALUES(33,28,'33:28','يايها النبى قل لازوجك ان كنتن تردن الحيوة الدنيا وزينتها فتعالين امتعكن واسرحكن سراحا جميلا');
INSERT INTO "uthmani_simple" VALUES(33,29,'33:29','وان كنتن تردن الله ورسوله والدار الءاخرة فان الله اعد للمحسنت منكن اجرا عظيما');
INSERT INTO "uthmani_simple" VALUES(33,30,'33:30','ينساء النبى من يات منكن بفحشة مبينة يضعف لها العذاب ضعفين وكان ذلك على الله يسيرا');
INSERT INTO "uthmani_simple" VALUES(33,31,'33:31','ومن يقنت منكن لله ورسوله وتعمل صلحا نؤتها اجرها مرتين واعتدنا لها رزقا كريما');
INSERT INTO "uthmani_simple" VALUES(33,32,'33:32','ينساء النبى لستن كاحد من النساء ان اتقيتن فلا تخضعن بالقول فيطمع الذى فى قلبه مرض وقلن قولا معروفا');
INSERT INTO "uthmani_simple" VALUES(33,33,'33:33','وقرن فى بيوتكن ولا تبرجن تبرج الجهلية الاولى واقمن الصلوة وءاتين الزكوة واطعن الله ورسوله انما يريد الله ليذهب عنكم الرجس اهل البيت ويطهركم تطهيرا');
INSERT INTO "uthmani_simple" VALUES(33,34,'33:34','واذكرن ما يتلى فى بيوتكن من ءايت الله والحكمة ان الله كان لطيفا خبيرا');
INSERT INTO "uthmani_simple" VALUES(33,35,'33:35','ان المسلمين والمسلمت والمؤمنين والمؤمنت والقنتين والقنتت والصدقين والصدقت والصبرين والصبرت والخشعين والخشعت والمتصدقين والمتصدقت والصئمين والصئمت والحفظين فروجهم والحفظت والذكرين الله كثيرا والذكرت اعد الله لهم مغفرة واجرا عظيما');
INSERT INTO "uthmani_simple" VALUES(33,36,'33:36','وما كان لمؤمن ولا مؤمنة اذا قضى الله ورسوله امرا ان يكون لهم الخيرة من امرهم ومن يعص الله ورسوله فقد ضل ضللا مبينا');
INSERT INTO "uthmani_simple" VALUES(33,37,'33:37','واذ تقول للذى انعم الله عليه وانعمت عليه امسك عليك زوجك واتق الله وتخفى فى نفسك ما الله مبديه وتخشى الناس والله احق ان تخشىه فلما قضى زيد منها وطرا زوجنكها لكى لا يكون على المؤمنين حرج فى ازوج ادعيائهم اذا قضوا۟ منهن وطرا وكان امر الله مفعولا');
INSERT INTO "uthmani_simple" VALUES(33,38,'33:38','ما كان على النبى من حرج فيما فرض الله له سنة الله فى الذين خلوا۟ من قبل وكان امر الله قدرا مقدورا');
INSERT INTO "uthmani_simple" VALUES(33,39,'33:39','الذين يبلغون رسلت الله ويخشونه ولا يخشون احدا الا الله وكفى بالله حسيبا');
INSERT INTO "uthmani_simple" VALUES(33,40,'33:40','ما كان محمد ابا احد من رجالكم ولكن رسول الله وخاتم النبين وكان الله بكل شىء عليما');
INSERT INTO "uthmani_simple" VALUES(33,41,'33:41','يايها الذين ءامنوا۟ اذكروا۟ الله ذكرا كثيرا');
INSERT INTO "uthmani_simple" VALUES(33,42,'33:42','وسبحوه بكرة واصيلا');
INSERT INTO "uthmani_simple" VALUES(33,43,'33:43','هو الذى يصلى عليكم وملئكته ليخرجكم من الظلمت الى النور وكان بالمؤمنين رحيما');
INSERT INTO "uthmani_simple" VALUES(33,44,'33:44','تحيتهم يوم يلقونه سلم واعد لهم اجرا كريما');
INSERT INTO "uthmani_simple" VALUES(33,45,'33:45','يايها النبى انا ارسلنك شهدا ومبشرا ونذيرا');
INSERT INTO "uthmani_simple" VALUES(33,46,'33:46','وداعيا الى الله باذنه وسراجا منيرا');
INSERT INTO "uthmani_simple" VALUES(33,47,'33:47','وبشر المؤمنين بان لهم من الله فضلا كبيرا');
INSERT INTO "uthmani_simple" VALUES(33,48,'33:48','ولا تطع الكفرين والمنفقين ودع اذىهم وتوكل على الله وكفى بالله وكيلا');
INSERT INTO "uthmani_simple" VALUES(33,49,'33:49','يايها الذين ءامنوا۟ اذا نكحتم المؤمنت ثم طلقتموهن من قبل ان تمسوهن فما لكم عليهن من عدة تعتدونها فمتعوهن وسرحوهن سراحا جميلا');
INSERT INTO "uthmani_simple" VALUES(33,50,'33:50','يايها النبى انا احللنا لك ازوجك التى ءاتيت اجورهن وما ملكت يمينك مما افاء الله عليك وبنات عمك وبنات عمتك وبنات خالك وبنات خلتك التى هاجرن معك وامراة مؤمنة ان وهبت نفسها للنبى ان اراد النبى ان يستنكحها خالصة لك من دون المؤمنين قد علمنا ما فرضنا عليهم فى ازوجهم وما ملكت ايمنهم لكيلا يكون عليك حرج وكان الله غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(33,51,'33:51','ترجى من تشاء منهن وتـوى اليك من تشاء ومن ابتغيت ممن عزلت فلا جناح عليك ذلك ادنى ان تقر اعينهن ولا يحزن ويرضين بما ءاتيتهن كلهن والله يعلم ما فى قلوبكم وكان الله عليما حليما');
INSERT INTO "uthmani_simple" VALUES(33,52,'33:52','لا يحل لك النساء منۢ بعد ولا ان تبدل بهن من ازوج ولو اعجبك حسنهن الا ما ملكت يمينك وكان الله على كل شىء رقيبا');
INSERT INTO "uthmani_simple" VALUES(33,53,'33:53','يايها الذين ءامنوا۟ لا تدخلوا۟ بيوت النبى الا ان يؤذن لكم الى طعام غير نظرين انىه ولكن اذا دعيتم فادخلوا۟ فاذا طعمتم فانتشروا۟ ولا مستـنسين لحديث ان ذلكم كان يؤذى النبى فيستحى منكم والله لا يستحى من الحق واذا سالتموهن متعا فسـلوهن من وراء حجاب ذلكم اطهر لقلوبكم وقلوبهن وما كان لكم ان تؤذوا۟ رسول الله ولا ان تنكحوا۟ ازوجه منۢ بعده ابدا ان ذلكم كان عند الله عظيما');
INSERT INTO "uthmani_simple" VALUES(33,54,'33:54','ان تبدوا۟ شيـا او تخفوه فان الله كان بكل شىء عليما');
INSERT INTO "uthmani_simple" VALUES(33,55,'33:55','لا جناح عليهن فى ءابائهن ولا ابنائهن ولا اخونهن ولا ابناء اخونهن ولا ابناء اخوتهن ولا نسائهن ولا ما ملكت ايمنهن واتقين الله ان الله كان على كل شىء شهيدا');
INSERT INTO "uthmani_simple" VALUES(33,56,'33:56','ان الله وملئكته يصلون على النبى يايها الذين ءامنوا۟ صلوا۟ عليه وسلموا۟ تسليما');
INSERT INTO "uthmani_simple" VALUES(33,57,'33:57','ان الذين يؤذون الله ورسوله لعنهم الله فى الدنيا والءاخرة واعد لهم عذابا مهينا');
INSERT INTO "uthmani_simple" VALUES(33,58,'33:58','والذين يؤذون المؤمنين والمؤمنت بغير ما اكتسبوا۟ فقد احتملوا۟ بهتنا واثما مبينا');
INSERT INTO "uthmani_simple" VALUES(33,59,'33:59','يايها النبى قل لازوجك وبناتك ونساء المؤمنين يدنين عليهن من جلبيبهن ذلك ادنى ان يعرفن فلا يؤذين وكان الله غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(33,60,'33:60','لئن لم ينته المنفقون والذين فى قلوبهم مرض والمرجفون فى المدينة لنغرينك بهم ثم لا يجاورونك فيها الا قليلا');
INSERT INTO "uthmani_simple" VALUES(33,61,'33:61','ملعونين اينما ثقفوا۟ اخذوا۟ وقتلوا۟ تقتيلا');
INSERT INTO "uthmani_simple" VALUES(33,62,'33:62','سنة الله فى الذين خلوا۟ من قبل ولن تجد لسنة الله تبديلا');
INSERT INTO "uthmani_simple" VALUES(33,63,'33:63','يسـلك الناس عن الساعة قل انما علمها عند الله وما يدريك لعل الساعة تكون قريبا');
INSERT INTO "uthmani_simple" VALUES(33,64,'33:64','ان الله لعن الكفرين واعد لهم سعيرا');
INSERT INTO "uthmani_simple" VALUES(33,65,'33:65','خلدين فيها ابدا لا يجدون وليا ولا نصيرا');
INSERT INTO "uthmani_simple" VALUES(33,66,'33:66','يوم تقلب وجوههم فى النار يقولون يليتنا اطعنا الله واطعنا الرسولا۠');
INSERT INTO "uthmani_simple" VALUES(33,67,'33:67','وقالوا۟ ربنا انا اطعنا سادتنا وكبراءنا فاضلونا السبيلا۠');
INSERT INTO "uthmani_simple" VALUES(33,68,'33:68','ربنا ءاتهم ضعفين من العذاب والعنهم لعنا كبيرا');
INSERT INTO "uthmani_simple" VALUES(33,69,'33:69','يايها الذين ءامنوا۟ لا تكونوا۟ كالذين ءاذوا۟ موسى فبراه الله مما قالوا۟ وكان عند الله وجيها');
INSERT INTO "uthmani_simple" VALUES(33,70,'33:70','يايها الذين ءامنوا۟ اتقوا۟ الله وقولوا۟ قولا سديدا');
INSERT INTO "uthmani_simple" VALUES(33,71,'33:71','يصلح لكم اعملكم ويغفر لكم ذنوبكم ومن يطع الله ورسوله فقد فاز فوزا عظيما');
INSERT INTO "uthmani_simple" VALUES(33,72,'33:72','انا عرضنا الامانة على السموت والارض والجبال فابين ان يحملنها واشفقن منها وحملها الانسن انه كان ظلوما جهولا');
INSERT INTO "uthmani_simple" VALUES(33,73,'33:73','ليعذب الله المنفقين والمنفقت والمشركين والمشركت ويتوب الله على المؤمنين والمؤمنت وكان الله غفورا رحيمۢا');
INSERT INTO "uthmani_simple" VALUES(34,1,'34:1','الحمد لله الذى له ما فى السموت وما فى الارض وله الحمد فى الءاخرة وهو الحكيم الخبير');
INSERT INTO "uthmani_simple" VALUES(34,2,'34:2','يعلم ما يلج فى الارض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو الرحيم الغفور');
INSERT INTO "uthmani_simple" VALUES(34,3,'34:3','وقال الذين كفروا۟ لا تاتينا الساعة قل بلى وربى لتاتينكم علم الغيب لا يعزب عنه مثقال ذرة فى السموت ولا فى الارض ولا اصغر من ذلك ولا اكبر الا فى كتب مبين');
INSERT INTO "uthmani_simple" VALUES(34,4,'34:4','ليجزى الذين ءامنوا۟ وعملوا۟ الصلحت او۟لئك لهم مغفرة ورزق كريم');
INSERT INTO "uthmani_simple" VALUES(34,5,'34:5','والذين سعو فى ءايتنا معجزين او۟لئك لهم عذاب من رجز اليم');
INSERT INTO "uthmani_simple" VALUES(34,6,'34:6','ويرى الذين اوتوا۟ العلم الذى انزل اليك من ربك هو الحق ويهدى الى صرط العزيز الحميد');
INSERT INTO "uthmani_simple" VALUES(34,7,'34:7','وقال الذين كفروا۟ هل ندلكم على رجل ينبئكم اذا مزقتم كل ممزق انكم لفى خلق جديد');
INSERT INTO "uthmani_simple" VALUES(34,8,'34:8','افترى على الله كذبا ام به جنةۢ بل الذين لا يؤمنون بالءاخرة فى العذاب والضلل البعيد');
INSERT INTO "uthmani_simple" VALUES(34,9,'34:9','افلم يروا۟ الى ما بين ايديهم وما خلفهم من السماء والارض ان نشا نخسف بهم الارض او نسقط عليهم كسفا من السماء ان فى ذلك لءاية لكل عبد منيب');
INSERT INTO "uthmani_simple" VALUES(34,10,'34:10','ولقد ءاتينا داود منا فضلا يجبال اوبى معه والطير والنا له الحديد');
INSERT INTO "uthmani_simple" VALUES(34,11,'34:11','ان اعمل سبغت وقدر فى السرد واعملوا۟ صلحا انى بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(34,12,'34:12','ولسليمن الريح غدوها شهر ورواحها شهر واسلنا له عين القطر ومن الجن من يعمل بين يديه باذن ربه ومن يزغ منهم عن امرنا نذقه من عذاب السعير');
INSERT INTO "uthmani_simple" VALUES(34,13,'34:13','يعملون له ما يشاء من محريب وتمثيل وجفان كالجواب وقدور راسيت اعملوا۟ ءال داود شكرا وقليل من عبادى الشكور');
INSERT INTO "uthmani_simple" VALUES(34,14,'34:14','فلما قضينا عليه الموت ما دلهم على موته الا دابة الارض تاكل منساته فلما خر تبينت الجن ان لو كانوا۟ يعلمون الغيب ما لبثوا۟ فى العذاب المهين');
INSERT INTO "uthmani_simple" VALUES(34,15,'34:15','لقد كان لسبا فى مسكنهم ءاية جنتان عن يمين وشمال كلوا۟ من رزق ربكم واشكروا۟ له بلدة طيبة ورب غفور');
INSERT INTO "uthmani_simple" VALUES(34,16,'34:16','فاعرضوا۟ فارسلنا عليهم سيل العرم وبدلنهم بجنتيهم جنتين ذواتى اكل خمط واثل وشىء من سدر قليل');
INSERT INTO "uthmani_simple" VALUES(34,17,'34:17','ذلك جزينهم بما كفروا۟ وهل نجزى الا الكفور');
INSERT INTO "uthmani_simple" VALUES(34,18,'34:18','وجعلنا بينهم وبين القرى التى بركنا فيها قرى ظهرة وقدرنا فيها السير سيروا۟ فيها ليالى واياما ءامنين');
INSERT INTO "uthmani_simple" VALUES(34,19,'34:19','فقالوا۟ ربنا بعد بين اسفارنا وظلموا۟ انفسهم فجعلنهم احاديث ومزقنهم كل ممزق ان فى ذلك لءايت لكل صبار شكور');
INSERT INTO "uthmani_simple" VALUES(34,20,'34:20','ولقد صدق عليهم ابليس ظنه فاتبعوه الا فريقا من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(34,21,'34:21','وما كان له عليهم من سلطن الا لنعلم من يؤمن بالءاخرة ممن هو منها فى شك وربك على كل شىء حفيظ');
INSERT INTO "uthmani_simple" VALUES(34,22,'34:22','قل ادعوا۟ الذين زعمتم من دون الله لا يملكون مثقال ذرة فى السموت ولا فى الارض وما لهم فيهما من شرك وما له منهم من ظهير');
INSERT INTO "uthmani_simple" VALUES(34,23,'34:23','ولا تنفع الشفعة عنده الا لمن اذن له حتى اذا فزع عن قلوبهم قالوا۟ ماذا قال ربكم قالوا۟ الحق وهو العلى الكبير');
INSERT INTO "uthmani_simple" VALUES(34,24,'34:24','قل من يرزقكم من السموت والارض قل الله وانا او اياكم لعلى هدى او فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(34,25,'34:25','قل لا تسـلون عما اجرمنا ولا نسـل عما تعملون');
INSERT INTO "uthmani_simple" VALUES(34,26,'34:26','قل يجمع بيننا ربنا ثم يفتح بيننا بالحق وهو الفتاح العليم');
INSERT INTO "uthmani_simple" VALUES(34,27,'34:27','قل ارونى الذين الحقتم به شركاء كلا بل هو الله العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(34,28,'34:28','وما ارسلنك الا كافة للناس بشيرا ونذيرا ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(34,29,'34:29','ويقولون متى هذا الوعد ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(34,30,'34:30','قل لكم ميعاد يوم لا تستـخرون عنه ساعة ولا تستقدمون');
INSERT INTO "uthmani_simple" VALUES(34,31,'34:31','وقال الذين كفروا۟ لن نؤمن بهذا القرءان ولا بالذى بين يديه ولو ترى اذ الظلمون موقوفون عند ربهم يرجع بعضهم الى بعض القول يقول الذين استضعفوا۟ للذين استكبروا۟ لولا انتم لكنا مؤمنين');
INSERT INTO "uthmani_simple" VALUES(34,32,'34:32','قال الذين استكبروا۟ للذين استضعفوا۟ انحن صددنكم عن الهدى بعد اذ جاءكم بل كنتم مجرمين');
INSERT INTO "uthmani_simple" VALUES(34,33,'34:33','وقال الذين استضعفوا۟ للذين استكبروا۟ بل مكر اليل والنهار اذ تامروننا ان نكفر بالله ونجعل له اندادا واسروا۟ الندامة لما راوا۟ العذاب وجعلنا الاغلل فى اعناق الذين كفروا۟ هل يجزون الا ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(34,34,'34:34','وما ارسلنا فى قرية من نذير الا قال مترفوها انا بما ارسلتم به كفرون');
INSERT INTO "uthmani_simple" VALUES(34,35,'34:35','وقالوا۟ نحن اكثر امولا واولدا وما نحن بمعذبين');
INSERT INTO "uthmani_simple" VALUES(34,36,'34:36','قل ان ربى يبسط الرزق لمن يشاء ويقدر ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(34,37,'34:37','وما امولكم ولا اولدكم بالتى تقربكم عندنا زلفى الا من ءامن وعمل صلحا فاو۟لئك لهم جزاء الضعف بما عملوا۟ وهم فى الغرفت ءامنون');
INSERT INTO "uthmani_simple" VALUES(34,38,'34:38','والذين يسعون فى ءايتنا معجزين او۟لئك فى العذاب محضرون');
INSERT INTO "uthmani_simple" VALUES(34,39,'34:39','قل ان ربى يبسط الرزق لمن يشاء من عباده ويقدر له وما انفقتم من شىء فهو يخلفه وهو خير الرزقين');
INSERT INTO "uthmani_simple" VALUES(34,40,'34:40','ويوم يحشرهم جميعا ثم يقول للملئكة اهؤلاء اياكم كانوا۟ يعبدون');
INSERT INTO "uthmani_simple" VALUES(34,41,'34:41','قالوا۟ سبحنك انت ولينا من دونهم بل كانوا۟ يعبدون الجن اكثرهم بهم مؤمنون');
INSERT INTO "uthmani_simple" VALUES(34,42,'34:42','فاليوم لا يملك بعضكم لبعض نفعا ولا ضرا ونقول للذين ظلموا۟ ذوقوا۟ عذاب النار التى كنتم بها تكذبون');
INSERT INTO "uthmani_simple" VALUES(34,43,'34:43','واذا تتلى عليهم ءايتنا بينت قالوا۟ ما هذا الا رجل يريد ان يصدكم عما كان يعبد ءاباؤكم وقالوا۟ ما هذا الا افك مفترى وقال الذين كفروا۟ للحق لما جاءهم ان هذا الا سحر مبين');
INSERT INTO "uthmani_simple" VALUES(34,44,'34:44','وما ءاتينهم من كتب يدرسونها وما ارسلنا اليهم قبلك من نذير');
INSERT INTO "uthmani_simple" VALUES(34,45,'34:45','وكذب الذين من قبلهم وما بلغوا۟ معشار ما ءاتينهم فكذبوا۟ رسلى فكيف كان نكير');
INSERT INTO "uthmani_simple" VALUES(34,46,'34:46','قل انما اعظكم بوحدة ان تقوموا۟ لله مثنى وفردى ثم تتفكروا۟ ما بصاحبكم من جنة ان هو الا نذير لكم بين يدى عذاب شديد');
INSERT INTO "uthmani_simple" VALUES(34,47,'34:47','قل ما سالتكم من اجر فهو لكم ان اجرى الا على الله وهو على كل شىء شهيد');
INSERT INTO "uthmani_simple" VALUES(34,48,'34:48','قل ان ربى يقذف بالحق علم الغيوب');
INSERT INTO "uthmani_simple" VALUES(34,49,'34:49','قل جاء الحق وما يبدئ البطل وما يعيد');
INSERT INTO "uthmani_simple" VALUES(34,50,'34:50','قل ان ضللت فانما اضل على نفسى وان اهتديت فبما يوحى الى ربى انه سميع قريب');
INSERT INTO "uthmani_simple" VALUES(34,51,'34:51','ولو ترى اذ فزعوا۟ فلا فوت واخذوا۟ من مكان قريب');
INSERT INTO "uthmani_simple" VALUES(34,52,'34:52','وقالوا۟ ءامنا به وانى لهم التناوش من مكانۭ بعيد');
INSERT INTO "uthmani_simple" VALUES(34,53,'34:53','وقد كفروا۟ به من قبل ويقذفون بالغيب من مكانۭ بعيد');
INSERT INTO "uthmani_simple" VALUES(34,54,'34:54','وحيل بينهم وبين ما يشتهون كما فعل باشياعهم من قبل انهم كانوا۟ فى شك مريبۭ');
INSERT INTO "uthmani_simple" VALUES(35,1,'35:1','الحمد لله فاطر السموت والارض جاعل الملئكة رسلا او۟لى اجنحة مثنى وثلث وربع يزيد فى الخلق ما يشاء ان الله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(35,2,'35:2','ما يفتح الله للناس من رحمة فلا ممسك لها وما يمسك فلا مرسل له منۢ بعده وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(35,3,'35:3','يايها الناس اذكروا۟ نعمت الله عليكم هل من خلق غير الله يرزقكم من السماء والارض لا اله الا هو فانى تؤفكون');
INSERT INTO "uthmani_simple" VALUES(35,4,'35:4','وان يكذبوك فقد كذبت رسل من قبلك والى الله ترجع الامور');
INSERT INTO "uthmani_simple" VALUES(35,5,'35:5','يايها الناس ان وعد الله حق فلا تغرنكم الحيوة الدنيا ولا يغرنكم بالله الغرور');
INSERT INTO "uthmani_simple" VALUES(35,6,'35:6','ان الشيطن لكم عدو فاتخذوه عدوا انما يدعوا۟ حزبه ليكونوا۟ من اصحب السعير');
INSERT INTO "uthmani_simple" VALUES(35,7,'35:7','الذين كفروا۟ لهم عذاب شديد والذين ءامنوا۟ وعملوا۟ الصلحت لهم مغفرة واجر كبير');
INSERT INTO "uthmani_simple" VALUES(35,8,'35:8','افمن زين له سوء عمله فرءاه حسنا فان الله يضل من يشاء ويهدى من يشاء فلا تذهب نفسك عليهم حسرت ان الله عليمۢ بما يصنعون');
INSERT INTO "uthmani_simple" VALUES(35,9,'35:9','والله الذى ارسل الريح فتثير سحابا فسقنه الى بلد ميت فاحيينا به الارض بعد موتها كذلك النشور');
INSERT INTO "uthmani_simple" VALUES(35,10,'35:10','من كان يريد العزة فلله العزة جميعا اليه يصعد الكلم الطيب والعمل الصلح يرفعه والذين يمكرون السيـات لهم عذاب شديد ومكر او۟لئك هو يبور');
INSERT INTO "uthmani_simple" VALUES(35,11,'35:11','والله خلقكم من تراب ثم من نطفة ثم جعلكم ازوجا وما تحمل من انثى ولا تضع الا بعلمه وما يعمر من معمر ولا ينقص من عمره الا فى كتب ان ذلك على الله يسير');
INSERT INTO "uthmani_simple" VALUES(35,12,'35:12','وما يستوى البحران هذا عذب فرات سائغ شرابه وهذا ملح اجاج ومن كل تاكلون لحما طريا وتستخرجون حلية تلبسونها وترى الفلك فيه مواخر لتبتغوا۟ من فضله ولعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(35,13,'35:13','يولج اليل فى النهار ويولج النهار فى اليل وسخر الشمس والقمر كل يجرى لاجل مسمى ذلكم الله ربكم له الملك والذين تدعون من دونه ما يملكون من قطمير');
INSERT INTO "uthmani_simple" VALUES(35,14,'35:14','ان تدعوهم لا يسمعوا۟ دعاءكم ولو سمعوا۟ ما استجابوا۟ لكم ويوم القيمة يكفرون بشرككم ولا ينبئك مثل خبير');
INSERT INTO "uthmani_simple" VALUES(35,15,'35:15','يايها الناس انتم الفقراء الى الله والله هو الغنى الحميد');
INSERT INTO "uthmani_simple" VALUES(35,16,'35:16','ان يشا يذهبكم ويات بخلق جديد');
INSERT INTO "uthmani_simple" VALUES(35,17,'35:17','وما ذلك على الله بعزيز');
INSERT INTO "uthmani_simple" VALUES(35,18,'35:18','ولا تزر وازرة وزر اخرى وان تدع مثقلة الى حملها لا يحمل منه شىء ولو كان ذا قربى انما تنذر الذين يخشون ربهم بالغيب واقاموا۟ الصلوة ومن تزكى فانما يتزكى لنفسه والى الله المصير');
INSERT INTO "uthmani_simple" VALUES(35,19,'35:19','وما يستوى الاعمى والبصير');
INSERT INTO "uthmani_simple" VALUES(35,20,'35:20','ولا الظلمت ولا النور');
INSERT INTO "uthmani_simple" VALUES(35,21,'35:21','ولا الظل ولا الحرور');
INSERT INTO "uthmani_simple" VALUES(35,22,'35:22','وما يستوى الاحياء ولا الاموت ان الله يسمع من يشاء وما انت بمسمع من فى القبور');
INSERT INTO "uthmani_simple" VALUES(35,23,'35:23','ان انت الا نذير');
INSERT INTO "uthmani_simple" VALUES(35,24,'35:24','انا ارسلنك بالحق بشيرا ونذيرا وان من امة الا خلا فيها نذير');
INSERT INTO "uthmani_simple" VALUES(35,25,'35:25','وان يكذبوك فقد كذب الذين من قبلهم جاءتهم رسلهم بالبينت وبالزبر وبالكتب المنير');
INSERT INTO "uthmani_simple" VALUES(35,26,'35:26','ثم اخذت الذين كفروا۟ فكيف كان نكير');
INSERT INTO "uthmani_simple" VALUES(35,27,'35:27','الم تر ان الله انزل من السماء ماء فاخرجنا به ثمرت مختلفا الونها ومن الجبال جددۢ بيض وحمر مختلف الونها وغرابيب سود');
INSERT INTO "uthmani_simple" VALUES(35,28,'35:28','ومن الناس والدواب والانعم مختلف الونه كذلك انما يخشى الله من عباده العلمؤا۟ ان الله عزيز غفور');
INSERT INTO "uthmani_simple" VALUES(35,29,'35:29','ان الذين يتلون كتب الله واقاموا۟ الصلوة وانفقوا۟ مما رزقنهم سرا وعلانية يرجون تجرة لن تبور');
INSERT INTO "uthmani_simple" VALUES(35,30,'35:30','ليوفيهم اجورهم ويزيدهم من فضله انه غفور شكور');
INSERT INTO "uthmani_simple" VALUES(35,31,'35:31','والذى اوحينا اليك من الكتب هو الحق مصدقا لما بين يديه ان الله بعباده لخبيرۢ بصير');
INSERT INTO "uthmani_simple" VALUES(35,32,'35:32','ثم اورثنا الكتب الذين اصطفينا من عبادنا فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابقۢ بالخيرت باذن الله ذلك هو الفضل الكبير');
INSERT INTO "uthmani_simple" VALUES(35,33,'35:33','جنت عدن يدخلونها يحلون فيها من اساور من ذهب ولؤلؤا ولباسهم فيها حرير');
INSERT INTO "uthmani_simple" VALUES(35,34,'35:34','وقالوا۟ الحمد لله الذى اذهب عنا الحزن ان ربنا لغفور شكور');
INSERT INTO "uthmani_simple" VALUES(35,35,'35:35','الذى احلنا دار المقامة من فضله لا يمسنا فيها نصب ولا يمسنا فيها لغوب');
INSERT INTO "uthmani_simple" VALUES(35,36,'35:36','والذين كفروا۟ لهم نار جهنم لا يقضى عليهم فيموتوا۟ ولا يخفف عنهم من عذابها كذلك نجزى كل كفور');
INSERT INTO "uthmani_simple" VALUES(35,37,'35:37','وهم يصطرخون فيها ربنا اخرجنا نعمل صلحا غير الذى كنا نعمل اولم نعمركم ما يتذكر فيه من تذكر وجاءكم النذير فذوقوا۟ فما للظلمين من نصير');
INSERT INTO "uthmani_simple" VALUES(35,38,'35:38','ان الله علم غيب السموت والارض انه عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(35,39,'35:39','هو الذى جعلكم خلئف فى الارض فمن كفر فعليه كفره ولا يزيد الكفرين كفرهم عند ربهم الا مقتا ولا يزيد الكفرين كفرهم الا خسارا');
INSERT INTO "uthmani_simple" VALUES(35,40,'35:40','قل ارءيتم شركاءكم الذين تدعون من دون الله ارونى ماذا خلقوا۟ من الارض ام لهم شرك فى السموت ام ءاتينهم كتبا فهم على بينت منه بل ان يعد الظلمون بعضهم بعضا الا غرورا');
INSERT INTO "uthmani_simple" VALUES(35,41,'35:41','ان الله يمسك السموت والارض ان تزولا ولئن زالتا ان امسكهما من احد منۢ بعده انه كان حليما غفورا');
INSERT INTO "uthmani_simple" VALUES(35,42,'35:42','واقسموا۟ بالله جهد ايمنهم لئن جاءهم نذير ليكونن اهدى من احدى الامم فلما جاءهم نذير ما زادهم الا نفورا');
INSERT INTO "uthmani_simple" VALUES(35,43,'35:43','استكبارا فى الارض ومكر السيئ ولا يحيق المكر السيئ الا باهله فهل ينظرون الا سنت الاولين فلن تجد لسنت الله تبديلا ولن تجد لسنت الله تحويلا');
INSERT INTO "uthmani_simple" VALUES(35,44,'35:44','اولم يسيروا۟ فى الارض فينظروا۟ كيف كان عقبة الذين من قبلهم وكانوا۟ اشد منهم قوة وما كان الله ليعجزه من شىء فى السموت ولا فى الارض انه كان عليما قديرا');
INSERT INTO "uthmani_simple" VALUES(35,45,'35:45','ولو يؤاخذ الله الناس بما كسبوا۟ ما ترك على ظهرها من دابة ولكن يؤخرهم الى اجل مسمى فاذا جاء اجلهم فان الله كان بعباده بصيرۢا');
INSERT INTO "uthmani_simple" VALUES(36,1,'36:1','يس');
INSERT INTO "uthmani_simple" VALUES(36,2,'36:2','والقرءان الحكيم');
INSERT INTO "uthmani_simple" VALUES(36,3,'36:3','انك لمن المرسلين');
INSERT INTO "uthmani_simple" VALUES(36,4,'36:4','على صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(36,5,'36:5','تنزيل العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(36,6,'36:6','لتنذر قوما ما انذر ءاباؤهم فهم غفلون');
INSERT INTO "uthmani_simple" VALUES(36,7,'36:7','لقد حق القول على اكثرهم فهم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(36,8,'36:8','انا جعلنا فى اعنقهم اغللا فهى الى الاذقان فهم مقمحون');
INSERT INTO "uthmani_simple" VALUES(36,9,'36:9','وجعلنا منۢ بين ايديهم سدا ومن خلفهم سدا فاغشينهم فهم لا يبصرون');
INSERT INTO "uthmani_simple" VALUES(36,10,'36:10','وسواء عليهم ءانذرتهم ام لم تنذرهم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(36,11,'36:11','انما تنذر من اتبع الذكر وخشى الرحمن بالغيب فبشره بمغفرة واجر كريم');
INSERT INTO "uthmani_simple" VALUES(36,12,'36:12','انا نحن نحى الموتى ونكتب ما قدموا۟ وءاثرهم وكل شىء احصينه فى امام مبين');
INSERT INTO "uthmani_simple" VALUES(36,13,'36:13','واضرب لهم مثلا اصحب القرية اذ جاءها المرسلون');
INSERT INTO "uthmani_simple" VALUES(36,14,'36:14','اذ ارسلنا اليهم اثنين فكذبوهما فعززنا بثالث فقالوا۟ انا اليكم مرسلون');
INSERT INTO "uthmani_simple" VALUES(36,15,'36:15','قالوا۟ ما انتم الا بشر مثلنا وما انزل الرحمن من شىء ان انتم الا تكذبون');
INSERT INTO "uthmani_simple" VALUES(36,16,'36:16','قالوا۟ ربنا يعلم انا اليكم لمرسلون');
INSERT INTO "uthmani_simple" VALUES(36,17,'36:17','وما علينا الا البلغ المبين');
INSERT INTO "uthmani_simple" VALUES(36,18,'36:18','قالوا۟ انا تطيرنا بكم لئن لم تنتهوا۟ لنرجمنكم وليمسنكم منا عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(36,19,'36:19','قالوا۟ طئركم معكم ائن ذكرتم بل انتم قوم مسرفون');
INSERT INTO "uthmani_simple" VALUES(36,20,'36:20','وجاء من اقصا المدينة رجل يسعى قال يقوم اتبعوا۟ المرسلين');
INSERT INTO "uthmani_simple" VALUES(36,21,'36:21','اتبعوا۟ من لا يسـلكم اجرا وهم مهتدون');
INSERT INTO "uthmani_simple" VALUES(36,22,'36:22','وما لى لا اعبد الذى فطرنى واليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(36,23,'36:23','ءاتخذ من دونه ءالهة ان يردن الرحمن بضر لا تغن عنى شفعتهم شيـا ولا ينقذون');
INSERT INTO "uthmani_simple" VALUES(36,24,'36:24','انى اذا لفى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(36,25,'36:25','انى ءامنت بربكم فاسمعون');
INSERT INTO "uthmani_simple" VALUES(36,26,'36:26','قيل ادخل الجنة قال يليت قومى يعلمون');
INSERT INTO "uthmani_simple" VALUES(36,27,'36:27','بما غفر لى ربى وجعلنى من المكرمين');
INSERT INTO "uthmani_simple" VALUES(36,28,'36:28','وما انزلنا على قومه منۢ بعده من جند من السماء وما كنا منزلين');
INSERT INTO "uthmani_simple" VALUES(36,29,'36:29','ان كانت الا صيحة وحدة فاذا هم خمدون');
INSERT INTO "uthmani_simple" VALUES(36,30,'36:30','يحسرة على العباد ما ياتيهم من رسول الا كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(36,31,'36:31','الم يروا۟ كم اهلكنا قبلهم من القرون انهم اليهم لا يرجعون');
INSERT INTO "uthmani_simple" VALUES(36,32,'36:32','وان كل لما جميع لدينا محضرون');
INSERT INTO "uthmani_simple" VALUES(36,33,'36:33','وءاية لهم الارض الميتة احيينها واخرجنا منها حبا فمنه ياكلون');
INSERT INTO "uthmani_simple" VALUES(36,34,'36:34','وجعلنا فيها جنت من نخيل واعنب وفجرنا فيها من العيون');
INSERT INTO "uthmani_simple" VALUES(36,35,'36:35','لياكلوا۟ من ثمره وما عملته ايديهم افلا يشكرون');
INSERT INTO "uthmani_simple" VALUES(36,36,'36:36','سبحن الذى خلق الازوج كلها مما تنۢبت الارض ومن انفسهم ومما لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(36,37,'36:37','وءاية لهم اليل نسلخ منه النهار فاذا هم مظلمون');
INSERT INTO "uthmani_simple" VALUES(36,38,'36:38','والشمس تجرى لمستقر لها ذلك تقدير العزيز العليم');
INSERT INTO "uthmani_simple" VALUES(36,39,'36:39','والقمر قدرنه منازل حتى عاد كالعرجون القديم');
INSERT INTO "uthmani_simple" VALUES(36,40,'36:40','لا الشمس ينۢبغى لها ان تدرك القمر ولا اليل سابق النهار وكل فى فلك يسبحون');
INSERT INTO "uthmani_simple" VALUES(36,41,'36:41','وءاية لهم انا حملنا ذريتهم فى الفلك المشحون');
INSERT INTO "uthmani_simple" VALUES(36,42,'36:42','وخلقنا لهم من مثله ما يركبون');
INSERT INTO "uthmani_simple" VALUES(36,43,'36:43','وان نشا نغرقهم فلا صريخ لهم ولا هم ينقذون');
INSERT INTO "uthmani_simple" VALUES(36,44,'36:44','الا رحمة منا ومتعا الى حين');
INSERT INTO "uthmani_simple" VALUES(36,45,'36:45','واذا قيل لهم اتقوا۟ ما بين ايديكم وما خلفكم لعلكم ترحمون');
INSERT INTO "uthmani_simple" VALUES(36,46,'36:46','وما تاتيهم من ءاية من ءايت ربهم الا كانوا۟ عنها معرضين');
INSERT INTO "uthmani_simple" VALUES(36,47,'36:47','واذا قيل لهم انفقوا۟ مما رزقكم الله قال الذين كفروا۟ للذين ءامنوا۟ انطعم من لو يشاء الله اطعمه ان انتم الا فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(36,48,'36:48','ويقولون متى هذا الوعد ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(36,49,'36:49','ما ينظرون الا صيحة وحدة تاخذهم وهم يخصمون');
INSERT INTO "uthmani_simple" VALUES(36,50,'36:50','فلا يستطيعون توصية ولا الى اهلهم يرجعون');
INSERT INTO "uthmani_simple" VALUES(36,51,'36:51','ونفخ فى الصور فاذا هم من الاجداث الى ربهم ينسلون');
INSERT INTO "uthmani_simple" VALUES(36,52,'36:52','قالوا۟ يويلنا منۢ بعثنا من مرقدنا هذا ما وعد الرحمن وصدق المرسلون');
INSERT INTO "uthmani_simple" VALUES(36,53,'36:53','ان كانت الا صيحة وحدة فاذا هم جميع لدينا محضرون');
INSERT INTO "uthmani_simple" VALUES(36,54,'36:54','فاليوم لا تظلم نفس شيـا ولا تجزون الا ما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(36,55,'36:55','ان اصحب الجنة اليوم فى شغل فكهون');
INSERT INTO "uthmani_simple" VALUES(36,56,'36:56','هم وازوجهم فى ظلل على الارائك متكـون');
INSERT INTO "uthmani_simple" VALUES(36,57,'36:57','لهم فيها فكهة ولهم ما يدعون');
INSERT INTO "uthmani_simple" VALUES(36,58,'36:58','سلم قولا من رب رحيم');
INSERT INTO "uthmani_simple" VALUES(36,59,'36:59','وامتزوا۟ اليوم ايها المجرمون');
INSERT INTO "uthmani_simple" VALUES(36,60,'36:60','الم اعهد اليكم يبنى ءادم ان لا تعبدوا۟ الشيطن انه لكم عدو مبين');
INSERT INTO "uthmani_simple" VALUES(36,61,'36:61','وان اعبدونى هذا صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(36,62,'36:62','ولقد اضل منكم جبلا كثيرا افلم تكونوا۟ تعقلون');
INSERT INTO "uthmani_simple" VALUES(36,63,'36:63','هذه جهنم التى كنتم توعدون');
INSERT INTO "uthmani_simple" VALUES(36,64,'36:64','اصلوها اليوم بما كنتم تكفرون');
INSERT INTO "uthmani_simple" VALUES(36,65,'36:65','اليوم نختم على افوههم وتكلمنا ايديهم وتشهد ارجلهم بما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(36,66,'36:66','ولو نشاء لطمسنا على اعينهم فاستبقوا۟ الصرط فانى يبصرون');
INSERT INTO "uthmani_simple" VALUES(36,67,'36:67','ولو نشاء لمسخنهم على مكانتهم فما استطعوا۟ مضيا ولا يرجعون');
INSERT INTO "uthmani_simple" VALUES(36,68,'36:68','ومن نعمره ننكسه فى الخلق افلا يعقلون');
INSERT INTO "uthmani_simple" VALUES(36,69,'36:69','وما علمنه الشعر وما ينۢبغى له ان هو الا ذكر وقرءان مبين');
INSERT INTO "uthmani_simple" VALUES(36,70,'36:70','لينذر من كان حيا ويحق القول على الكفرين');
INSERT INTO "uthmani_simple" VALUES(36,71,'36:71','اولم يروا۟ انا خلقنا لهم مما عملت ايدينا انعما فهم لها ملكون');
INSERT INTO "uthmani_simple" VALUES(36,72,'36:72','وذللنها لهم فمنها ركوبهم ومنها ياكلون');
INSERT INTO "uthmani_simple" VALUES(36,73,'36:73','ولهم فيها منفع ومشارب افلا يشكرون');
INSERT INTO "uthmani_simple" VALUES(36,74,'36:74','واتخذوا۟ من دون الله ءالهة لعلهم ينصرون');
INSERT INTO "uthmani_simple" VALUES(36,75,'36:75','لا يستطيعون نصرهم وهم لهم جند محضرون');
INSERT INTO "uthmani_simple" VALUES(36,76,'36:76','فلا يحزنك قولهم انا نعلم ما يسرون وما يعلنون');
INSERT INTO "uthmani_simple" VALUES(36,77,'36:77','اولم ير الانسن انا خلقنه من نطفة فاذا هو خصيم مبين');
INSERT INTO "uthmani_simple" VALUES(36,78,'36:78','وضرب لنا مثلا ونسى خلقه قال من يحى العظم وهى رميم');
INSERT INTO "uthmani_simple" VALUES(36,79,'36:79','قل يحييها الذى انشاها اول مرة وهو بكل خلق عليم');
INSERT INTO "uthmani_simple" VALUES(36,80,'36:80','الذى جعل لكم من الشجر الاخضر نارا فاذا انتم منه توقدون');
INSERT INTO "uthmani_simple" VALUES(36,81,'36:81','اوليس الذى خلق السموت والارض بقدر على ان يخلق مثلهم بلى وهو الخلق العليم');
INSERT INTO "uthmani_simple" VALUES(36,82,'36:82','انما امره اذا اراد شيـا ان يقول له كن فيكون');
INSERT INTO "uthmani_simple" VALUES(36,83,'36:83','فسبحن الذى بيده ملكوت كل شىء واليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(37,1,'37:1','والصفت صفا');
INSERT INTO "uthmani_simple" VALUES(37,2,'37:2','فالزجرت زجرا');
INSERT INTO "uthmani_simple" VALUES(37,3,'37:3','فالتليت ذكرا');
INSERT INTO "uthmani_simple" VALUES(37,4,'37:4','ان الهكم لوحد');
INSERT INTO "uthmani_simple" VALUES(37,5,'37:5','رب السموت والارض وما بينهما ورب المشرق');
INSERT INTO "uthmani_simple" VALUES(37,6,'37:6','انا زينا السماء الدنيا بزينة الكواكب');
INSERT INTO "uthmani_simple" VALUES(37,7,'37:7','وحفظا من كل شيطن مارد');
INSERT INTO "uthmani_simple" VALUES(37,8,'37:8','لا يسمعون الى الملا الاعلى ويقذفون من كل جانب');
INSERT INTO "uthmani_simple" VALUES(37,9,'37:9','دحورا ولهم عذاب واصب');
INSERT INTO "uthmani_simple" VALUES(37,10,'37:10','الا من خطف الخطفة فاتبعه شهاب ثاقب');
INSERT INTO "uthmani_simple" VALUES(37,11,'37:11','فاستفتهم اهم اشد خلقا ام من خلقنا انا خلقنهم من طين لازبۭ');
INSERT INTO "uthmani_simple" VALUES(37,12,'37:12','بل عجبت ويسخرون');
INSERT INTO "uthmani_simple" VALUES(37,13,'37:13','واذا ذكروا۟ لا يذكرون');
INSERT INTO "uthmani_simple" VALUES(37,14,'37:14','واذا راوا۟ ءاية يستسخرون');
INSERT INTO "uthmani_simple" VALUES(37,15,'37:15','وقالوا۟ ان هذا الا سحر مبين');
INSERT INTO "uthmani_simple" VALUES(37,16,'37:16','اءذا متنا وكنا ترابا وعظما اءنا لمبعوثون');
INSERT INTO "uthmani_simple" VALUES(37,17,'37:17','اوءاباؤنا الاولون');
INSERT INTO "uthmani_simple" VALUES(37,18,'37:18','قل نعم وانتم دخرون');
INSERT INTO "uthmani_simple" VALUES(37,19,'37:19','فانما هى زجرة وحدة فاذا هم ينظرون');
INSERT INTO "uthmani_simple" VALUES(37,20,'37:20','وقالوا۟ يويلنا هذا يوم الدين');
INSERT INTO "uthmani_simple" VALUES(37,21,'37:21','هذا يوم الفصل الذى كنتم به تكذبون');
INSERT INTO "uthmani_simple" VALUES(37,22,'37:22','احشروا۟ الذين ظلموا۟ وازوجهم وما كانوا۟ يعبدون');
INSERT INTO "uthmani_simple" VALUES(37,23,'37:23','من دون الله فاهدوهم الى صرط الجحيم');
INSERT INTO "uthmani_simple" VALUES(37,24,'37:24','وقفوهم انهم مسـولون');
INSERT INTO "uthmani_simple" VALUES(37,25,'37:25','ما لكم لا تناصرون');
INSERT INTO "uthmani_simple" VALUES(37,26,'37:26','بل هم اليوم مستسلمون');
INSERT INTO "uthmani_simple" VALUES(37,27,'37:27','واقبل بعضهم على بعض يتساءلون');
INSERT INTO "uthmani_simple" VALUES(37,28,'37:28','قالوا۟ انكم كنتم تاتوننا عن اليمين');
INSERT INTO "uthmani_simple" VALUES(37,29,'37:29','قالوا۟ بل لم تكونوا۟ مؤمنين');
INSERT INTO "uthmani_simple" VALUES(37,30,'37:30','وما كان لنا عليكم من سلطنۭ بل كنتم قوما طغين');
INSERT INTO "uthmani_simple" VALUES(37,31,'37:31','فحق علينا قول ربنا انا لذائقون');
INSERT INTO "uthmani_simple" VALUES(37,32,'37:32','فاغوينكم انا كنا غوين');
INSERT INTO "uthmani_simple" VALUES(37,33,'37:33','فانهم يومئذ فى العذاب مشتركون');
INSERT INTO "uthmani_simple" VALUES(37,34,'37:34','انا كذلك نفعل بالمجرمين');
INSERT INTO "uthmani_simple" VALUES(37,35,'37:35','انهم كانوا۟ اذا قيل لهم لا اله الا الله يستكبرون');
INSERT INTO "uthmani_simple" VALUES(37,36,'37:36','ويقولون ائنا لتاركوا۟ ءالهتنا لشاعر مجنونۭ');
INSERT INTO "uthmani_simple" VALUES(37,37,'37:37','بل جاء بالحق وصدق المرسلين');
INSERT INTO "uthmani_simple" VALUES(37,38,'37:38','انكم لذائقوا۟ العذاب الاليم');
INSERT INTO "uthmani_simple" VALUES(37,39,'37:39','وما تجزون الا ما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(37,40,'37:40','الا عباد الله المخلصين');
INSERT INTO "uthmani_simple" VALUES(37,41,'37:41','او۟لئك لهم رزق معلوم');
INSERT INTO "uthmani_simple" VALUES(37,42,'37:42','فوكه وهم مكرمون');
INSERT INTO "uthmani_simple" VALUES(37,43,'37:43','فى جنت النعيم');
INSERT INTO "uthmani_simple" VALUES(37,44,'37:44','على سرر متقبلين');
INSERT INTO "uthmani_simple" VALUES(37,45,'37:45','يطاف عليهم بكاس من معينۭ');
INSERT INTO "uthmani_simple" VALUES(37,46,'37:46','بيضاء لذة للشربين');
INSERT INTO "uthmani_simple" VALUES(37,47,'37:47','لا فيها غول ولا هم عنها ينزفون');
INSERT INTO "uthmani_simple" VALUES(37,48,'37:48','وعندهم قصرت الطرف عين');
INSERT INTO "uthmani_simple" VALUES(37,49,'37:49','كانهن بيض مكنون');
INSERT INTO "uthmani_simple" VALUES(37,50,'37:50','فاقبل بعضهم على بعض يتساءلون');
INSERT INTO "uthmani_simple" VALUES(37,51,'37:51','قال قائل منهم انى كان لى قرين');
INSERT INTO "uthmani_simple" VALUES(37,52,'37:52','يقول اءنك لمن المصدقين');
INSERT INTO "uthmani_simple" VALUES(37,53,'37:53','اءذا متنا وكنا ترابا وعظما اءنا لمدينون');
INSERT INTO "uthmani_simple" VALUES(37,54,'37:54','قال هل انتم مطلعون');
INSERT INTO "uthmani_simple" VALUES(37,55,'37:55','فاطلع فرءاه فى سواء الجحيم');
INSERT INTO "uthmani_simple" VALUES(37,56,'37:56','قال تالله ان كدت لتردين');
INSERT INTO "uthmani_simple" VALUES(37,57,'37:57','ولولا نعمة ربى لكنت من المحضرين');
INSERT INTO "uthmani_simple" VALUES(37,58,'37:58','افما نحن بميتين');
INSERT INTO "uthmani_simple" VALUES(37,59,'37:59','الا موتتنا الاولى وما نحن بمعذبين');
INSERT INTO "uthmani_simple" VALUES(37,60,'37:60','ان هذا لهو الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(37,61,'37:61','لمثل هذا فليعمل العملون');
INSERT INTO "uthmani_simple" VALUES(37,62,'37:62','اذلك خير نزلا ام شجرة الزقوم');
INSERT INTO "uthmani_simple" VALUES(37,63,'37:63','انا جعلنها فتنة للظلمين');
INSERT INTO "uthmani_simple" VALUES(37,64,'37:64','انها شجرة تخرج فى اصل الجحيم');
INSERT INTO "uthmani_simple" VALUES(37,65,'37:65','طلعها كانه رءوس الشيطين');
INSERT INTO "uthmani_simple" VALUES(37,66,'37:66','فانهم لءاكلون منها فمالـون منها البطون');
INSERT INTO "uthmani_simple" VALUES(37,67,'37:67','ثم ان لهم عليها لشوبا من حميم');
INSERT INTO "uthmani_simple" VALUES(37,68,'37:68','ثم ان مرجعهم لالى الجحيم');
INSERT INTO "uthmani_simple" VALUES(37,69,'37:69','انهم الفوا۟ ءاباءهم ضالين');
INSERT INTO "uthmani_simple" VALUES(37,70,'37:70','فهم على ءاثرهم يهرعون');
INSERT INTO "uthmani_simple" VALUES(37,71,'37:71','ولقد ضل قبلهم اكثر الاولين');
INSERT INTO "uthmani_simple" VALUES(37,72,'37:72','ولقد ارسلنا فيهم منذرين');
INSERT INTO "uthmani_simple" VALUES(37,73,'37:73','فانظر كيف كان عقبة المنذرين');
INSERT INTO "uthmani_simple" VALUES(37,74,'37:74','الا عباد الله المخلصين');
INSERT INTO "uthmani_simple" VALUES(37,75,'37:75','ولقد نادىنا نوح فلنعم المجيبون');
INSERT INTO "uthmani_simple" VALUES(37,76,'37:76','ونجينه واهله من الكرب العظيم');
INSERT INTO "uthmani_simple" VALUES(37,77,'37:77','وجعلنا ذريته هم الباقين');
INSERT INTO "uthmani_simple" VALUES(37,78,'37:78','وتركنا عليه فى الءاخرين');
INSERT INTO "uthmani_simple" VALUES(37,79,'37:79','سلم على نوح فى العلمين');
INSERT INTO "uthmani_simple" VALUES(37,80,'37:80','انا كذلك نجزى المحسنين');
INSERT INTO "uthmani_simple" VALUES(37,81,'37:81','انه من عبادنا المؤمنين');
INSERT INTO "uthmani_simple" VALUES(37,82,'37:82','ثم اغرقنا الءاخرين');
INSERT INTO "uthmani_simple" VALUES(37,83,'37:83','وان من شيعته لابرهيم');
INSERT INTO "uthmani_simple" VALUES(37,84,'37:84','اذ جاء ربه بقلب سليم');
INSERT INTO "uthmani_simple" VALUES(37,85,'37:85','اذ قال لابيه وقومه ماذا تعبدون');
INSERT INTO "uthmani_simple" VALUES(37,86,'37:86','ائفكا ءالهة دون الله تريدون');
INSERT INTO "uthmani_simple" VALUES(37,87,'37:87','فما ظنكم برب العلمين');
INSERT INTO "uthmani_simple" VALUES(37,88,'37:88','فنظر نظرة فى النجوم');
INSERT INTO "uthmani_simple" VALUES(37,89,'37:89','فقال انى سقيم');
INSERT INTO "uthmani_simple" VALUES(37,90,'37:90','فتولوا۟ عنه مدبرين');
INSERT INTO "uthmani_simple" VALUES(37,91,'37:91','فراغ الى ءالهتهم فقال الا تاكلون');
INSERT INTO "uthmani_simple" VALUES(37,92,'37:92','ما لكم لا تنطقون');
INSERT INTO "uthmani_simple" VALUES(37,93,'37:93','فراغ عليهم ضربۢا باليمين');
INSERT INTO "uthmani_simple" VALUES(37,94,'37:94','فاقبلوا۟ اليه يزفون');
INSERT INTO "uthmani_simple" VALUES(37,95,'37:95','قال اتعبدون ما تنحتون');
INSERT INTO "uthmani_simple" VALUES(37,96,'37:96','والله خلقكم وما تعملون');
INSERT INTO "uthmani_simple" VALUES(37,97,'37:97','قالوا۟ ابنوا۟ له بنينا فالقوه فى الجحيم');
INSERT INTO "uthmani_simple" VALUES(37,98,'37:98','فارادوا۟ به كيدا فجعلنهم الاسفلين');
INSERT INTO "uthmani_simple" VALUES(37,99,'37:99','وقال انى ذاهب الى ربى سيهدين');
INSERT INTO "uthmani_simple" VALUES(37,100,'37:100','رب هب لى من الصلحين');
INSERT INTO "uthmani_simple" VALUES(37,101,'37:101','فبشرنه بغلم حليم');
INSERT INTO "uthmani_simple" VALUES(37,102,'37:102','فلما بلغ معه السعى قال يبنى انى ارى فى المنام انى اذبحك فانظر ماذا ترى قال يابت افعل ما تؤمر ستجدنى ان شاء الله من الصبرين');
INSERT INTO "uthmani_simple" VALUES(37,103,'37:103','فلما اسلما وتله للجبين');
INSERT INTO "uthmani_simple" VALUES(37,104,'37:104','وندينه ان يابرهيم');
INSERT INTO "uthmani_simple" VALUES(37,105,'37:105','قد صدقت الرءيا انا كذلك نجزى المحسنين');
INSERT INTO "uthmani_simple" VALUES(37,106,'37:106','ان هذا لهو البلؤا۟ المبين');
INSERT INTO "uthmani_simple" VALUES(37,107,'37:107','وفدينه بذبح عظيم');
INSERT INTO "uthmani_simple" VALUES(37,108,'37:108','وتركنا عليه فى الءاخرين');
INSERT INTO "uthmani_simple" VALUES(37,109,'37:109','سلم على ابرهيم');
INSERT INTO "uthmani_simple" VALUES(37,110,'37:110','كذلك نجزى المحسنين');
INSERT INTO "uthmani_simple" VALUES(37,111,'37:111','انه من عبادنا المؤمنين');
INSERT INTO "uthmani_simple" VALUES(37,112,'37:112','وبشرنه باسحق نبيا من الصلحين');
INSERT INTO "uthmani_simple" VALUES(37,113,'37:113','وبركنا عليه وعلى اسحق ومن ذريتهما محسن وظالم لنفسه مبين');
INSERT INTO "uthmani_simple" VALUES(37,114,'37:114','ولقد مننا على موسى وهرون');
INSERT INTO "uthmani_simple" VALUES(37,115,'37:115','ونجينهما وقومهما من الكرب العظيم');
INSERT INTO "uthmani_simple" VALUES(37,116,'37:116','ونصرنهم فكانوا۟ هم الغلبين');
INSERT INTO "uthmani_simple" VALUES(37,117,'37:117','وءاتينهما الكتب المستبين');
INSERT INTO "uthmani_simple" VALUES(37,118,'37:118','وهدينهما الصرط المستقيم');
INSERT INTO "uthmani_simple" VALUES(37,119,'37:119','وتركنا عليهما فى الءاخرين');
INSERT INTO "uthmani_simple" VALUES(37,120,'37:120','سلم على موسى وهرون');
INSERT INTO "uthmani_simple" VALUES(37,121,'37:121','انا كذلك نجزى المحسنين');
INSERT INTO "uthmani_simple" VALUES(37,122,'37:122','انهما من عبادنا المؤمنين');
INSERT INTO "uthmani_simple" VALUES(37,123,'37:123','وان الياس لمن المرسلين');
INSERT INTO "uthmani_simple" VALUES(37,124,'37:124','اذ قال لقومه الا تتقون');
INSERT INTO "uthmani_simple" VALUES(37,125,'37:125','اتدعون بعلا وتذرون احسن الخلقين');
INSERT INTO "uthmani_simple" VALUES(37,126,'37:126','الله ربكم ورب ءابائكم الاولين');
INSERT INTO "uthmani_simple" VALUES(37,127,'37:127','فكذبوه فانهم لمحضرون');
INSERT INTO "uthmani_simple" VALUES(37,128,'37:128','الا عباد الله المخلصين');
INSERT INTO "uthmani_simple" VALUES(37,129,'37:129','وتركنا عليه فى الءاخرين');
INSERT INTO "uthmani_simple" VALUES(37,130,'37:130','سلم على ال ياسين');
INSERT INTO "uthmani_simple" VALUES(37,131,'37:131','انا كذلك نجزى المحسنين');
INSERT INTO "uthmani_simple" VALUES(37,132,'37:132','انه من عبادنا المؤمنين');
INSERT INTO "uthmani_simple" VALUES(37,133,'37:133','وان لوطا لمن المرسلين');
INSERT INTO "uthmani_simple" VALUES(37,134,'37:134','اذ نجينه واهله اجمعين');
INSERT INTO "uthmani_simple" VALUES(37,135,'37:135','الا عجوزا فى الغبرين');
INSERT INTO "uthmani_simple" VALUES(37,136,'37:136','ثم دمرنا الءاخرين');
INSERT INTO "uthmani_simple" VALUES(37,137,'37:137','وانكم لتمرون عليهم مصبحين');
INSERT INTO "uthmani_simple" VALUES(37,138,'37:138','وباليل افلا تعقلون');
INSERT INTO "uthmani_simple" VALUES(37,139,'37:139','وان يونس لمن المرسلين');
INSERT INTO "uthmani_simple" VALUES(37,140,'37:140','اذ ابق الى الفلك المشحون');
INSERT INTO "uthmani_simple" VALUES(37,141,'37:141','فساهم فكان من المدحضين');
INSERT INTO "uthmani_simple" VALUES(37,142,'37:142','فالتقمه الحوت وهو مليم');
INSERT INTO "uthmani_simple" VALUES(37,143,'37:143','فلولا انه كان من المسبحين');
INSERT INTO "uthmani_simple" VALUES(37,144,'37:144','للبث فى بطنه الى يوم يبعثون');
INSERT INTO "uthmani_simple" VALUES(37,145,'37:145','فنبذنه بالعراء وهو سقيم');
INSERT INTO "uthmani_simple" VALUES(37,146,'37:146','وانۢبتنا عليه شجرة من يقطين');
INSERT INTO "uthmani_simple" VALUES(37,147,'37:147','وارسلنه الى ما۟ئة الف او يزيدون');
INSERT INTO "uthmani_simple" VALUES(37,148,'37:148','فـامنوا۟ فمتعنهم الى حين');
INSERT INTO "uthmani_simple" VALUES(37,149,'37:149','فاستفتهم الربك البنات ولهم البنون');
INSERT INTO "uthmani_simple" VALUES(37,150,'37:150','ام خلقنا الملئكة انثا وهم شهدون');
INSERT INTO "uthmani_simple" VALUES(37,151,'37:151','الا انهم من افكهم ليقولون');
INSERT INTO "uthmani_simple" VALUES(37,152,'37:152','ولد الله وانهم لكذبون');
INSERT INTO "uthmani_simple" VALUES(37,153,'37:153','اصطفى البنات على البنين');
INSERT INTO "uthmani_simple" VALUES(37,154,'37:154','ما لكم كيف تحكمون');
INSERT INTO "uthmani_simple" VALUES(37,155,'37:155','افلا تذكرون');
INSERT INTO "uthmani_simple" VALUES(37,156,'37:156','ام لكم سلطن مبين');
INSERT INTO "uthmani_simple" VALUES(37,157,'37:157','فاتوا۟ بكتبكم ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(37,158,'37:158','وجعلوا۟ بينه وبين الجنة نسبا ولقد علمت الجنة انهم لمحضرون');
INSERT INTO "uthmani_simple" VALUES(37,159,'37:159','سبحن الله عما يصفون');
INSERT INTO "uthmani_simple" VALUES(37,160,'37:160','الا عباد الله المخلصين');
INSERT INTO "uthmani_simple" VALUES(37,161,'37:161','فانكم وما تعبدون');
INSERT INTO "uthmani_simple" VALUES(37,162,'37:162','ما انتم عليه بفتنين');
INSERT INTO "uthmani_simple" VALUES(37,163,'37:163','الا من هو صال الجحيم');
INSERT INTO "uthmani_simple" VALUES(37,164,'37:164','وما منا الا له مقام معلوم');
INSERT INTO "uthmani_simple" VALUES(37,165,'37:165','وانا لنحن الصافون');
INSERT INTO "uthmani_simple" VALUES(37,166,'37:166','وانا لنحن المسبحون');
INSERT INTO "uthmani_simple" VALUES(37,167,'37:167','وان كانوا۟ ليقولون');
INSERT INTO "uthmani_simple" VALUES(37,168,'37:168','لو ان عندنا ذكرا من الاولين');
INSERT INTO "uthmani_simple" VALUES(37,169,'37:169','لكنا عباد الله المخلصين');
INSERT INTO "uthmani_simple" VALUES(37,170,'37:170','فكفروا۟ به فسوف يعلمون');
INSERT INTO "uthmani_simple" VALUES(37,171,'37:171','ولقد سبقت كلمتنا لعبادنا المرسلين');
INSERT INTO "uthmani_simple" VALUES(37,172,'37:172','انهم لهم المنصورون');
INSERT INTO "uthmani_simple" VALUES(37,173,'37:173','وان جندنا لهم الغلبون');
INSERT INTO "uthmani_simple" VALUES(37,174,'37:174','فتول عنهم حتى حين');
INSERT INTO "uthmani_simple" VALUES(37,175,'37:175','وابصرهم فسوف يبصرون');
INSERT INTO "uthmani_simple" VALUES(37,176,'37:176','افبعذابنا يستعجلون');
INSERT INTO "uthmani_simple" VALUES(37,177,'37:177','فاذا نزل بساحتهم فساء صباح المنذرين');
INSERT INTO "uthmani_simple" VALUES(37,178,'37:178','وتول عنهم حتى حين');
INSERT INTO "uthmani_simple" VALUES(37,179,'37:179','وابصر فسوف يبصرون');
INSERT INTO "uthmani_simple" VALUES(37,180,'37:180','سبحن ربك رب العزة عما يصفون');
INSERT INTO "uthmani_simple" VALUES(37,181,'37:181','وسلم على المرسلين');
INSERT INTO "uthmani_simple" VALUES(37,182,'37:182','والحمد لله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(38,1,'38:1','ص والقرءان ذى الذكر');
INSERT INTO "uthmani_simple" VALUES(38,2,'38:2','بل الذين كفروا۟ فى عزة وشقاق');
INSERT INTO "uthmani_simple" VALUES(38,3,'38:3','كم اهلكنا من قبلهم من قرن فنادوا۟ ولات حين مناص');
INSERT INTO "uthmani_simple" VALUES(38,4,'38:4','وعجبوا۟ ان جاءهم منذر منهم وقال الكفرون هذا سحر كذاب');
INSERT INTO "uthmani_simple" VALUES(38,5,'38:5','اجعل الءالهة الها وحدا ان هذا لشىء عجاب');
INSERT INTO "uthmani_simple" VALUES(38,6,'38:6','وانطلق الملا منهم ان امشوا۟ واصبروا۟ على ءالهتكم ان هذا لشىء يراد');
INSERT INTO "uthmani_simple" VALUES(38,7,'38:7','ما سمعنا بهذا فى الملة الءاخرة ان هذا الا اختلق');
INSERT INTO "uthmani_simple" VALUES(38,8,'38:8','اءنزل عليه الذكر منۢ بيننا بل هم فى شك من ذكرى بل لما يذوقوا۟ عذاب');
INSERT INTO "uthmani_simple" VALUES(38,9,'38:9','ام عندهم خزائن رحمة ربك العزيز الوهاب');
INSERT INTO "uthmani_simple" VALUES(38,10,'38:10','ام لهم ملك السموت والارض وما بينهما فليرتقوا۟ فى الاسبب');
INSERT INTO "uthmani_simple" VALUES(38,11,'38:11','جند ما هنالك مهزوم من الاحزاب');
INSERT INTO "uthmani_simple" VALUES(38,12,'38:12','كذبت قبلهم قوم نوح وعاد وفرعون ذو الاوتاد');
INSERT INTO "uthmani_simple" VALUES(38,13,'38:13','وثمود وقوم لوط واصحب لـيكة او۟لئك الاحزاب');
INSERT INTO "uthmani_simple" VALUES(38,14,'38:14','ان كل الا كذب الرسل فحق عقاب');
INSERT INTO "uthmani_simple" VALUES(38,15,'38:15','وما ينظر هؤلاء الا صيحة وحدة ما لها من فواق');
INSERT INTO "uthmani_simple" VALUES(38,16,'38:16','وقالوا۟ ربنا عجل لنا قطنا قبل يوم الحساب');
INSERT INTO "uthmani_simple" VALUES(38,17,'38:17','اصبر على ما يقولون واذكر عبدنا داود ذا الايد انه اواب');
INSERT INTO "uthmani_simple" VALUES(38,18,'38:18','انا سخرنا الجبال معه يسبحن بالعشى والاشراق');
INSERT INTO "uthmani_simple" VALUES(38,19,'38:19','والطير محشورة كل له اواب');
INSERT INTO "uthmani_simple" VALUES(38,20,'38:20','وشددنا ملكه وءاتينه الحكمة وفصل الخطاب');
INSERT INTO "uthmani_simple" VALUES(38,21,'38:21','وهل اتىك نبؤا۟ الخصم اذ تسوروا۟ المحراب');
INSERT INTO "uthmani_simple" VALUES(38,22,'38:22','اذ دخلوا۟ على داود ففزع منهم قالوا۟ لا تخف خصمان بغى بعضنا على بعض فاحكم بيننا بالحق ولا تشطط واهدنا الى سواء الصرط');
INSERT INTO "uthmani_simple" VALUES(38,23,'38:23','ان هذا اخى له تسع وتسعون نعجة ولى نعجة وحدة فقال اكفلنيها وعزنى فى الخطاب');
INSERT INTO "uthmani_simple" VALUES(38,24,'38:24','قال لقد ظلمك بسؤال نعجتك الى نعاجه وان كثيرا من الخلطاء ليبغى بعضهم على بعض الا الذين ءامنوا۟ وعملوا۟ الصلحت وقليل ما هم وظن داود انما فتنه فاستغفر ربه وخر راكعا واناب');
INSERT INTO "uthmani_simple" VALUES(38,25,'38:25','فغفرنا له ذلك وان له عندنا لزلفى وحسن مـاب');
INSERT INTO "uthmani_simple" VALUES(38,26,'38:26','يداود انا جعلنك خليفة فى الارض فاحكم بين الناس بالحق ولا تتبع الهوى فيضلك عن سبيل الله ان الذين يضلون عن سبيل الله لهم عذاب شديدۢ بما نسوا۟ يوم الحساب');
INSERT INTO "uthmani_simple" VALUES(38,27,'38:27','وما خلقنا السماء والارض وما بينهما بطلا ذلك ظن الذين كفروا۟ فويل للذين كفروا۟ من النار');
INSERT INTO "uthmani_simple" VALUES(38,28,'38:28','ام نجعل الذين ءامنوا۟ وعملوا۟ الصلحت كالمفسدين فى الارض ام نجعل المتقين كالفجار');
INSERT INTO "uthmani_simple" VALUES(38,29,'38:29','كتب انزلنه اليك مبرك ليدبروا۟ ءايته وليتذكر او۟لوا۟ الالبب');
INSERT INTO "uthmani_simple" VALUES(38,30,'38:30','ووهبنا لداود سليمن نعم العبد انه اواب');
INSERT INTO "uthmani_simple" VALUES(38,31,'38:31','اذ عرض عليه بالعشى الصفنت الجياد');
INSERT INTO "uthmani_simple" VALUES(38,32,'38:32','فقال انى احببت حب الخير عن ذكر ربى حتى توارت بالحجاب');
INSERT INTO "uthmani_simple" VALUES(38,33,'38:33','ردوها على فطفق مسحۢا بالسوق والاعناق');
INSERT INTO "uthmani_simple" VALUES(38,34,'38:34','ولقد فتنا سليمن والقينا على كرسيه جسدا ثم اناب');
INSERT INTO "uthmani_simple" VALUES(38,35,'38:35','قال رب اغفر لى وهب لى ملكا لا ينۢبغى لاحد منۢ بعدى انك انت الوهاب');
INSERT INTO "uthmani_simple" VALUES(38,36,'38:36','فسخرنا له الريح تجرى بامره رخاء حيث اصاب');
INSERT INTO "uthmani_simple" VALUES(38,37,'38:37','والشيطين كل بناء وغواص');
INSERT INTO "uthmani_simple" VALUES(38,38,'38:38','وءاخرين مقرنين فى الاصفاد');
INSERT INTO "uthmani_simple" VALUES(38,39,'38:39','هذا عطاؤنا فامنن او امسك بغير حساب');
INSERT INTO "uthmani_simple" VALUES(38,40,'38:40','وان له عندنا لزلفى وحسن مـاب');
INSERT INTO "uthmani_simple" VALUES(38,41,'38:41','واذكر عبدنا ايوب اذ نادى ربه انى مسنى الشيطن بنصب وعذاب');
INSERT INTO "uthmani_simple" VALUES(38,42,'38:42','اركض برجلك هذا مغتسلۢ بارد وشراب');
INSERT INTO "uthmani_simple" VALUES(38,43,'38:43','ووهبنا له اهله ومثلهم معهم رحمة منا وذكرى لاو۟لى الالبب');
INSERT INTO "uthmani_simple" VALUES(38,44,'38:44','وخذ بيدك ضغثا فاضرب به ولا تحنث انا وجدنه صابرا نعم العبد انه اواب');
INSERT INTO "uthmani_simple" VALUES(38,45,'38:45','واذكر عبدنا ابرهيم واسحق ويعقوب او۟لى الايدى والابصر');
INSERT INTO "uthmani_simple" VALUES(38,46,'38:46','انا اخلصنهم بخالصة ذكرى الدار');
INSERT INTO "uthmani_simple" VALUES(38,47,'38:47','وانهم عندنا لمن المصطفين الاخيار');
INSERT INTO "uthmani_simple" VALUES(38,48,'38:48','واذكر اسمعيل واليسع وذا الكفل وكل من الاخيار');
INSERT INTO "uthmani_simple" VALUES(38,49,'38:49','هذا ذكر وان للمتقين لحسن مـاب');
INSERT INTO "uthmani_simple" VALUES(38,50,'38:50','جنت عدن مفتحة لهم الابوب');
INSERT INTO "uthmani_simple" VALUES(38,51,'38:51','متكـين فيها يدعون فيها بفكهة كثيرة وشراب');
INSERT INTO "uthmani_simple" VALUES(38,52,'38:52','وعندهم قصرت الطرف اتراب');
INSERT INTO "uthmani_simple" VALUES(38,53,'38:53','هذا ما توعدون ليوم الحساب');
INSERT INTO "uthmani_simple" VALUES(38,54,'38:54','ان هذا لرزقنا ما له من نفاد');
INSERT INTO "uthmani_simple" VALUES(38,55,'38:55','هذا وان للطغين لشر مـاب');
INSERT INTO "uthmani_simple" VALUES(38,56,'38:56','جهنم يصلونها فبئس المهاد');
INSERT INTO "uthmani_simple" VALUES(38,57,'38:57','هذا فليذوقوه حميم وغساق');
INSERT INTO "uthmani_simple" VALUES(38,58,'38:58','وءاخر من شكله ازوج');
INSERT INTO "uthmani_simple" VALUES(38,59,'38:59','هذا فوج مقتحم معكم لا مرحبۢا بهم انهم صالوا۟ النار');
INSERT INTO "uthmani_simple" VALUES(38,60,'38:60','قالوا۟ بل انتم لا مرحبۢا بكم انتم قدمتموه لنا فبئس القرار');
INSERT INTO "uthmani_simple" VALUES(38,61,'38:61','قالوا۟ ربنا من قدم لنا هذا فزده عذابا ضعفا فى النار');
INSERT INTO "uthmani_simple" VALUES(38,62,'38:62','وقالوا۟ ما لنا لا نرى رجالا كنا نعدهم من الاشرار');
INSERT INTO "uthmani_simple" VALUES(38,63,'38:63','اتخذنهم سخريا ام زاغت عنهم الابصر');
INSERT INTO "uthmani_simple" VALUES(38,64,'38:64','ان ذلك لحق تخاصم اهل النار');
INSERT INTO "uthmani_simple" VALUES(38,65,'38:65','قل انما انا۠ منذر وما من اله الا الله الوحد القهار');
INSERT INTO "uthmani_simple" VALUES(38,66,'38:66','رب السموت والارض وما بينهما العزيز الغفر');
INSERT INTO "uthmani_simple" VALUES(38,67,'38:67','قل هو نبؤا۟ عظيم');
INSERT INTO "uthmani_simple" VALUES(38,68,'38:68','انتم عنه معرضون');
INSERT INTO "uthmani_simple" VALUES(38,69,'38:69','ما كان لى من علمۭ بالملا الاعلى اذ يختصمون');
INSERT INTO "uthmani_simple" VALUES(38,70,'38:70','ان يوحى الى الا انما انا۠ نذير مبين');
INSERT INTO "uthmani_simple" VALUES(38,71,'38:71','اذ قال ربك للملئكة انى خلقۢ بشرا من طين');
INSERT INTO "uthmani_simple" VALUES(38,72,'38:72','فاذا سويته ونفخت فيه من روحى فقعوا۟ له سجدين');
INSERT INTO "uthmani_simple" VALUES(38,73,'38:73','فسجد الملئكة كلهم اجمعون');
INSERT INTO "uthmani_simple" VALUES(38,74,'38:74','الا ابليس استكبر وكان من الكفرين');
INSERT INTO "uthmani_simple" VALUES(38,75,'38:75','قال يابليس ما منعك ان تسجد لما خلقت بيدى استكبرت ام كنت من العالين');
INSERT INTO "uthmani_simple" VALUES(38,76,'38:76','قال انا۠ خير منه خلقتنى من نار وخلقته من طين');
INSERT INTO "uthmani_simple" VALUES(38,77,'38:77','قال فاخرج منها فانك رجيم');
INSERT INTO "uthmani_simple" VALUES(38,78,'38:78','وان عليك لعنتى الى يوم الدين');
INSERT INTO "uthmani_simple" VALUES(38,79,'38:79','قال رب فانظرنى الى يوم يبعثون');
INSERT INTO "uthmani_simple" VALUES(38,80,'38:80','قال فانك من المنظرين');
INSERT INTO "uthmani_simple" VALUES(38,81,'38:81','الى يوم الوقت المعلوم');
INSERT INTO "uthmani_simple" VALUES(38,82,'38:82','قال فبعزتك لاغوينهم اجمعين');
INSERT INTO "uthmani_simple" VALUES(38,83,'38:83','الا عبادك منهم المخلصين');
INSERT INTO "uthmani_simple" VALUES(38,84,'38:84','قال فالحق والحق اقول');
INSERT INTO "uthmani_simple" VALUES(38,85,'38:85','لاملان جهنم منك وممن تبعك منهم اجمعين');
INSERT INTO "uthmani_simple" VALUES(38,86,'38:86','قل ما اسـلكم عليه من اجر وما انا۠ من المتكلفين');
INSERT INTO "uthmani_simple" VALUES(38,87,'38:87','ان هو الا ذكر للعلمين');
INSERT INTO "uthmani_simple" VALUES(38,88,'38:88','ولتعلمن نباه بعد حينۭ');
INSERT INTO "uthmani_simple" VALUES(39,1,'39:1','تنزيل الكتب من الله العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(39,2,'39:2','انا انزلنا اليك الكتب بالحق فاعبد الله مخلصا له الدين');
INSERT INTO "uthmani_simple" VALUES(39,3,'39:3','الا لله الدين الخالص والذين اتخذوا۟ من دونه اولياء ما نعبدهم الا ليقربونا الى الله زلفى ان الله يحكم بينهم فى ما هم فيه يختلفون ان الله لا يهدى من هو كذب كفار');
INSERT INTO "uthmani_simple" VALUES(39,4,'39:4','لو اراد الله ان يتخذ ولدا لاصطفى مما يخلق ما يشاء سبحنه هو الله الوحد القهار');
INSERT INTO "uthmani_simple" VALUES(39,5,'39:5','خلق السموت والارض بالحق يكور اليل على النهار ويكور النهار على اليل وسخر الشمس والقمر كل يجرى لاجل مسمى الا هو العزيز الغفر');
INSERT INTO "uthmani_simple" VALUES(39,6,'39:6','خلقكم من نفس وحدة ثم جعل منها زوجها وانزل لكم من الانعم ثمنية ازوج يخلقكم فى بطون امهتكم خلقا منۢ بعد خلق فى ظلمت ثلث ذلكم الله ربكم له الملك لا اله الا هو فانى تصرفون');
INSERT INTO "uthmani_simple" VALUES(39,7,'39:7','ان تكفروا۟ فان الله غنى عنكم ولا يرضى لعباده الكفر وان تشكروا۟ يرضه لكم ولا تزر وازرة وزر اخرى ثم الى ربكم مرجعكم فينبئكم بما كنتم تعملون انه عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(39,8,'39:8','واذا مس الانسن ضر دعا ربه منيبا اليه ثم اذا خوله نعمة منه نسى ما كان يدعوا۟ اليه من قبل وجعل لله اندادا ليضل عن سبيله قل تمتع بكفرك قليلا انك من اصحب النار');
INSERT INTO "uthmani_simple" VALUES(39,9,'39:9','امن هو قنت ءاناء اليل ساجدا وقائما يحذر الءاخرة ويرجوا۟ رحمة ربه قل هل يستوى الذين يعلمون والذين لا يعلمون انما يتذكر او۟لوا۟ الالبب');
INSERT INTO "uthmani_simple" VALUES(39,10,'39:10','قل يعباد الذين ءامنوا۟ اتقوا۟ ربكم للذين احسنوا۟ فى هذه الدنيا حسنة وارض الله وسعة انما يوفى الصبرون اجرهم بغير حساب');
INSERT INTO "uthmani_simple" VALUES(39,11,'39:11','قل انى امرت ان اعبد الله مخلصا له الدين');
INSERT INTO "uthmani_simple" VALUES(39,12,'39:12','وامرت لان اكون اول المسلمين');
INSERT INTO "uthmani_simple" VALUES(39,13,'39:13','قل انى اخاف ان عصيت ربى عذاب يوم عظيم');
INSERT INTO "uthmani_simple" VALUES(39,14,'39:14','قل الله اعبد مخلصا له دينى');
INSERT INTO "uthmani_simple" VALUES(39,15,'39:15','فاعبدوا۟ ما شئتم من دونه قل ان الخسرين الذين خسروا۟ انفسهم واهليهم يوم القيمة الا ذلك هو الخسران المبين');
INSERT INTO "uthmani_simple" VALUES(39,16,'39:16','لهم من فوقهم ظلل من النار ومن تحتهم ظلل ذلك يخوف الله به عباده يعباد فاتقون');
INSERT INTO "uthmani_simple" VALUES(39,17,'39:17','والذين اجتنبوا۟ الطغوت ان يعبدوها وانابوا۟ الى الله لهم البشرى فبشر عباد');
INSERT INTO "uthmani_simple" VALUES(39,18,'39:18','الذين يستمعون القول فيتبعون احسنه او۟لئك الذين هدىهم الله واو۟لئك هم او۟لوا۟ الالبب');
INSERT INTO "uthmani_simple" VALUES(39,19,'39:19','افمن حق عليه كلمة العذاب افانت تنقذ من فى النار');
INSERT INTO "uthmani_simple" VALUES(39,20,'39:20','لكن الذين اتقوا۟ ربهم لهم غرف من فوقها غرف مبنية تجرى من تحتها الانهر وعد الله لا يخلف الله الميعاد');
INSERT INTO "uthmani_simple" VALUES(39,21,'39:21','الم تر ان الله انزل من السماء ماء فسلكه ينبيع فى الارض ثم يخرج به زرعا مختلفا الونه ثم يهيج فترىه مصفرا ثم يجعله حطما ان فى ذلك لذكرى لاو۟لى الالبب');
INSERT INTO "uthmani_simple" VALUES(39,22,'39:22','افمن شرح الله صدره للاسلم فهو على نور من ربه فويل للقسية قلوبهم من ذكر الله او۟لئك فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(39,23,'39:23','الله نزل احسن الحديث كتبا متشبها مثانى تقشعر منه جلود الذين يخشون ربهم ثم تلين جلودهم وقلوبهم الى ذكر الله ذلك هدى الله يهدى به من يشاء ومن يضلل الله فما له من هاد');
INSERT INTO "uthmani_simple" VALUES(39,24,'39:24','افمن يتقى بوجهه سوء العذاب يوم القيمة وقيل للظلمين ذوقوا۟ ما كنتم تكسبون');
INSERT INTO "uthmani_simple" VALUES(39,25,'39:25','كذب الذين من قبلهم فاتىهم العذاب من حيث لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(39,26,'39:26','فاذاقهم الله الخزى فى الحيوة الدنيا ولعذاب الءاخرة اكبر لو كانوا۟ يعلمون');
INSERT INTO "uthmani_simple" VALUES(39,27,'39:27','ولقد ضربنا للناس فى هذا القرءان من كل مثل لعلهم يتذكرون');
INSERT INTO "uthmani_simple" VALUES(39,28,'39:28','قرءانا عربيا غير ذى عوج لعلهم يتقون');
INSERT INTO "uthmani_simple" VALUES(39,29,'39:29','ضرب الله مثلا رجلا فيه شركاء متشكسون ورجلا سلما لرجل هل يستويان مثلا الحمد لله بل اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(39,30,'39:30','انك ميت وانهم ميتون');
INSERT INTO "uthmani_simple" VALUES(39,31,'39:31','ثم انكم يوم القيمة عند ربكم تختصمون');
INSERT INTO "uthmani_simple" VALUES(39,32,'39:32','فمن اظلم ممن كذب على الله وكذب بالصدق اذ جاءه اليس فى جهنم مثوى للكفرين');
INSERT INTO "uthmani_simple" VALUES(39,33,'39:33','والذى جاء بالصدق وصدق به او۟لئك هم المتقون');
INSERT INTO "uthmani_simple" VALUES(39,34,'39:34','لهم ما يشاءون عند ربهم ذلك جزاء المحسنين');
INSERT INTO "uthmani_simple" VALUES(39,35,'39:35','ليكفر الله عنهم اسوا الذى عملوا۟ ويجزيهم اجرهم باحسن الذى كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(39,36,'39:36','اليس الله بكاف عبده ويخوفونك بالذين من دونه ومن يضلل الله فما له من هاد');
INSERT INTO "uthmani_simple" VALUES(39,37,'39:37','ومن يهد الله فما له من مضل اليس الله بعزيز ذى انتقام');
INSERT INTO "uthmani_simple" VALUES(39,38,'39:38','ولئن سالتهم من خلق السموت والارض ليقولن الله قل افرءيتم ما تدعون من دون الله ان ارادنى الله بضر هل هن كشفت ضره او ارادنى برحمة هل هن ممسكت رحمته قل حسبى الله عليه يتوكل المتوكلون');
INSERT INTO "uthmani_simple" VALUES(39,39,'39:39','قل يقوم اعملوا۟ على مكانتكم انى عمل فسوف تعلمون');
INSERT INTO "uthmani_simple" VALUES(39,40,'39:40','من ياتيه عذاب يخزيه ويحل عليه عذاب مقيم');
INSERT INTO "uthmani_simple" VALUES(39,41,'39:41','انا انزلنا عليك الكتب للناس بالحق فمن اهتدى فلنفسه ومن ضل فانما يضل عليها وما انت عليهم بوكيل');
INSERT INTO "uthmani_simple" VALUES(39,42,'39:42','الله يتوفى الانفس حين موتها والتى لم تمت فى منامها فيمسك التى قضى عليها الموت ويرسل الاخرى الى اجل مسمى ان فى ذلك لءايت لقوم يتفكرون');
INSERT INTO "uthmani_simple" VALUES(39,43,'39:43','ام اتخذوا۟ من دون الله شفعاء قل اولو كانوا۟ لا يملكون شيـا ولا يعقلون');
INSERT INTO "uthmani_simple" VALUES(39,44,'39:44','قل لله الشفعة جميعا له ملك السموت والارض ثم اليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(39,45,'39:45','واذا ذكر الله وحده اشمازت قلوب الذين لا يؤمنون بالءاخرة واذا ذكر الذين من دونه اذا هم يستبشرون');
INSERT INTO "uthmani_simple" VALUES(39,46,'39:46','قل اللهم فاطر السموت والارض علم الغيب والشهدة انت تحكم بين عبادك فى ما كانوا۟ فيه يختلفون');
INSERT INTO "uthmani_simple" VALUES(39,47,'39:47','ولو ان للذين ظلموا۟ ما فى الارض جميعا ومثله معه لافتدوا۟ به من سوء العذاب يوم القيمة وبدا لهم من الله ما لم يكونوا۟ يحتسبون');
INSERT INTO "uthmani_simple" VALUES(39,48,'39:48','وبدا لهم سيـات ما كسبوا۟ وحاق بهم ما كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(39,49,'39:49','فاذا مس الانسن ضر دعانا ثم اذا خولنه نعمة منا قال انما اوتيته على علمۭ بل هى فتنة ولكن اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(39,50,'39:50','قد قالها الذين من قبلهم فما اغنى عنهم ما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(39,51,'39:51','فاصابهم سيـات ما كسبوا۟ والذين ظلموا۟ من هؤلاء سيصيبهم سيـات ما كسبوا۟ وما هم بمعجزين');
INSERT INTO "uthmani_simple" VALUES(39,52,'39:52','اولم يعلموا۟ ان الله يبسط الرزق لمن يشاء ويقدر ان فى ذلك لءايت لقوم يؤمنون');
INSERT INTO "uthmani_simple" VALUES(39,53,'39:53','قل يعبادى الذين اسرفوا۟ على انفسهم لا تقنطوا۟ من رحمة الله ان الله يغفر الذنوب جميعا انه هو الغفور الرحيم');
INSERT INTO "uthmani_simple" VALUES(39,54,'39:54','وانيبوا۟ الى ربكم واسلموا۟ له من قبل ان ياتيكم العذاب ثم لا تنصرون');
INSERT INTO "uthmani_simple" VALUES(39,55,'39:55','واتبعوا۟ احسن ما انزل اليكم من ربكم من قبل ان ياتيكم العذاب بغتة وانتم لا تشعرون');
INSERT INTO "uthmani_simple" VALUES(39,56,'39:56','ان تقول نفس يحسرتى على ما فرطت فى جنۢب الله وان كنت لمن السخرين');
INSERT INTO "uthmani_simple" VALUES(39,57,'39:57','او تقول لو ان الله هدىنى لكنت من المتقين');
INSERT INTO "uthmani_simple" VALUES(39,58,'39:58','او تقول حين ترى العذاب لو ان لى كرة فاكون من المحسنين');
INSERT INTO "uthmani_simple" VALUES(39,59,'39:59','بلى قد جاءتك ءايتى فكذبت بها واستكبرت وكنت من الكفرين');
INSERT INTO "uthmani_simple" VALUES(39,60,'39:60','ويوم القيمة ترى الذين كذبوا۟ على الله وجوههم مسودة اليس فى جهنم مثوى للمتكبرين');
INSERT INTO "uthmani_simple" VALUES(39,61,'39:61','وينجى الله الذين اتقوا۟ بمفازتهم لا يمسهم السوء ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(39,62,'39:62','الله خلق كل شىء وهو على كل شىء وكيل');
INSERT INTO "uthmani_simple" VALUES(39,63,'39:63','له مقاليد السموت والارض والذين كفروا۟ بـايت الله او۟لئك هم الخسرون');
INSERT INTO "uthmani_simple" VALUES(39,64,'39:64','قل افغير الله تامرونى اعبد ايها الجهلون');
INSERT INTO "uthmani_simple" VALUES(39,65,'39:65','ولقد اوحى اليك والى الذين من قبلك لئن اشركت ليحبطن عملك ولتكونن من الخسرين');
INSERT INTO "uthmani_simple" VALUES(39,66,'39:66','بل الله فاعبد وكن من الشكرين');
INSERT INTO "uthmani_simple" VALUES(39,67,'39:67','وما قدروا۟ الله حق قدره والارض جميعا قبضته يوم القيمة والسموت مطويتۢ بيمينه سبحنه وتعلى عما يشركون');
INSERT INTO "uthmani_simple" VALUES(39,68,'39:68','ونفخ فى الصور فصعق من فى السموت ومن فى الارض الا من شاء الله ثم نفخ فيه اخرى فاذا هم قيام ينظرون');
INSERT INTO "uthmani_simple" VALUES(39,69,'39:69','واشرقت الارض بنور ربها ووضع الكتب وجا۟ىء بالنبين والشهداء وقضى بينهم بالحق وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(39,70,'39:70','ووفيت كل نفس ما عملت وهو اعلم بما يفعلون');
INSERT INTO "uthmani_simple" VALUES(39,71,'39:71','وسيق الذين كفروا۟ الى جهنم زمرا حتى اذا جاءوها فتحت ابوبها وقال لهم خزنتها الم ياتكم رسل منكم يتلون عليكم ءايت ربكم وينذرونكم لقاء يومكم هذا قالوا۟ بلى ولكن حقت كلمة العذاب على الكفرين');
INSERT INTO "uthmani_simple" VALUES(39,72,'39:72','قيل ادخلوا۟ ابوب جهنم خلدين فيها فبئس مثوى المتكبرين');
INSERT INTO "uthmani_simple" VALUES(39,73,'39:73','وسيق الذين اتقوا۟ ربهم الى الجنة زمرا حتى اذا جاءوها وفتحت ابوبها وقال لهم خزنتها سلم عليكم طبتم فادخلوها خلدين');
INSERT INTO "uthmani_simple" VALUES(39,74,'39:74','وقالوا۟ الحمد لله الذى صدقنا وعده واورثنا الارض نتبوا من الجنة حيث نشاء فنعم اجر العملين');
INSERT INTO "uthmani_simple" VALUES(39,75,'39:75','وترى الملئكة حافين من حول العرش يسبحون بحمد ربهم وقضى بينهم بالحق وقيل الحمد لله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(40,1,'40:1','حم');
INSERT INTO "uthmani_simple" VALUES(40,2,'40:2','تنزيل الكتب من الله العزيز العليم');
INSERT INTO "uthmani_simple" VALUES(40,3,'40:3','غافر الذنۢب وقابل التوب شديد العقاب ذى الطول لا اله الا هو اليه المصير');
INSERT INTO "uthmani_simple" VALUES(40,4,'40:4','ما يجدل فى ءايت الله الا الذين كفروا۟ فلا يغررك تقلبهم فى البلد');
INSERT INTO "uthmani_simple" VALUES(40,5,'40:5','كذبت قبلهم قوم نوح والاحزاب منۢ بعدهم وهمت كل امةۭ برسولهم لياخذوه وجدلوا۟ بالبطل ليدحضوا۟ به الحق فاخذتهم فكيف كان عقاب');
INSERT INTO "uthmani_simple" VALUES(40,6,'40:6','وكذلك حقت كلمت ربك على الذين كفروا۟ انهم اصحب النار');
INSERT INTO "uthmani_simple" VALUES(40,7,'40:7','الذين يحملون العرش ومن حوله يسبحون بحمد ربهم ويؤمنون به ويستغفرون للذين ءامنوا۟ ربنا وسعت كل شىء رحمة وعلما فاغفر للذين تابوا۟ واتبعوا۟ سبيلك وقهم عذاب الجحيم');
INSERT INTO "uthmani_simple" VALUES(40,8,'40:8','ربنا وادخلهم جنت عدن التى وعدتهم ومن صلح من ءابائهم وازوجهم وذريتهم انك انت العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(40,9,'40:9','وقهم السيـات ومن تق السيـات يومئذ فقد رحمته وذلك هو الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(40,10,'40:10','ان الذين كفروا۟ ينادون لمقت الله اكبر من مقتكم انفسكم اذ تدعون الى الايمن فتكفرون');
INSERT INTO "uthmani_simple" VALUES(40,11,'40:11','قالوا۟ ربنا امتنا اثنتين واحييتنا اثنتين فاعترفنا بذنوبنا فهل الى خروج من سبيل');
INSERT INTO "uthmani_simple" VALUES(40,12,'40:12','ذلكم بانه اذا دعى الله وحده كفرتم وان يشرك به تؤمنوا۟ فالحكم لله العلى الكبير');
INSERT INTO "uthmani_simple" VALUES(40,13,'40:13','هو الذى يريكم ءايته وينزل لكم من السماء رزقا وما يتذكر الا من ينيب');
INSERT INTO "uthmani_simple" VALUES(40,14,'40:14','فادعوا۟ الله مخلصين له الدين ولو كره الكفرون');
INSERT INTO "uthmani_simple" VALUES(40,15,'40:15','رفيع الدرجت ذو العرش يلقى الروح من امره على من يشاء من عباده لينذر يوم التلاق');
INSERT INTO "uthmani_simple" VALUES(40,16,'40:16','يوم هم برزون لا يخفى على الله منهم شىء لمن الملك اليوم لله الوحد القهار');
INSERT INTO "uthmani_simple" VALUES(40,17,'40:17','اليوم تجزى كل نفسۭ بما كسبت لا ظلم اليوم ان الله سريع الحساب');
INSERT INTO "uthmani_simple" VALUES(40,18,'40:18','وانذرهم يوم الءازفة اذ القلوب لدى الحناجر كظمين ما للظلمين من حميم ولا شفيع يطاع');
INSERT INTO "uthmani_simple" VALUES(40,19,'40:19','يعلم خائنة الاعين وما تخفى الصدور');
INSERT INTO "uthmani_simple" VALUES(40,20,'40:20','والله يقضى بالحق والذين يدعون من دونه لا يقضون بشىء ان الله هو السميع البصير');
INSERT INTO "uthmani_simple" VALUES(40,21,'40:21','اولم يسيروا۟ فى الارض فينظروا۟ كيف كان عقبة الذين كانوا۟ من قبلهم كانوا۟ هم اشد منهم قوة وءاثارا فى الارض فاخذهم الله بذنوبهم وما كان لهم من الله من واق');
INSERT INTO "uthmani_simple" VALUES(40,22,'40:22','ذلك بانهم كانت تاتيهم رسلهم بالبينت فكفروا۟ فاخذهم الله انه قوى شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(40,23,'40:23','ولقد ارسلنا موسى بـايتنا وسلطن مبين');
INSERT INTO "uthmani_simple" VALUES(40,24,'40:24','الى فرعون وهمن وقرون فقالوا۟ سحر كذاب');
INSERT INTO "uthmani_simple" VALUES(40,25,'40:25','فلما جاءهم بالحق من عندنا قالوا۟ اقتلوا۟ ابناء الذين ءامنوا۟ معه واستحيوا۟ نساءهم وما كيد الكفرين الا فى ضلل');
INSERT INTO "uthmani_simple" VALUES(40,26,'40:26','وقال فرعون ذرونى اقتل موسى وليدع ربه انى اخاف ان يبدل دينكم او ان يظهر فى الارض الفساد');
INSERT INTO "uthmani_simple" VALUES(40,27,'40:27','وقال موسى انى عذت بربى وربكم من كل متكبر لا يؤمن بيوم الحساب');
INSERT INTO "uthmani_simple" VALUES(40,28,'40:28','وقال رجل مؤمن من ءال فرعون يكتم ايمنه اتقتلون رجلا ان يقول ربى الله وقد جاءكم بالبينت من ربكم وان يك كذبا فعليه كذبه وان يك صادقا يصبكم بعض الذى يعدكم ان الله لا يهدى من هو مسرف كذاب');
INSERT INTO "uthmani_simple" VALUES(40,29,'40:29','يقوم لكم الملك اليوم ظهرين فى الارض فمن ينصرنا منۢ باس الله ان جاءنا قال فرعون ما اريكم الا ما ارى وما اهديكم الا سبيل الرشاد');
INSERT INTO "uthmani_simple" VALUES(40,30,'40:30','وقال الذى ءامن يقوم انى اخاف عليكم مثل يوم الاحزاب');
INSERT INTO "uthmani_simple" VALUES(40,31,'40:31','مثل داب قوم نوح وعاد وثمود والذين منۢ بعدهم وما الله يريد ظلما للعباد');
INSERT INTO "uthmani_simple" VALUES(40,32,'40:32','ويقوم انى اخاف عليكم يوم التناد');
INSERT INTO "uthmani_simple" VALUES(40,33,'40:33','يوم تولون مدبرين ما لكم من الله من عاصم ومن يضلل الله فما له من هاد');
INSERT INTO "uthmani_simple" VALUES(40,34,'40:34','ولقد جاءكم يوسف من قبل بالبينت فما زلتم فى شك مما جاءكم به حتى اذا هلك قلتم لن يبعث الله منۢ بعده رسولا كذلك يضل الله من هو مسرف مرتاب');
INSERT INTO "uthmani_simple" VALUES(40,35,'40:35','الذين يجدلون فى ءايت الله بغير سلطن اتىهم كبر مقتا عند الله وعند الذين ءامنوا۟ كذلك يطبع الله على كل قلب متكبر جبار');
INSERT INTO "uthmani_simple" VALUES(40,36,'40:36','وقال فرعون يهمن ابن لى صرحا لعلى ابلغ الاسبب');
INSERT INTO "uthmani_simple" VALUES(40,37,'40:37','اسبب السموت فاطلع الى اله موسى وانى لاظنه كذبا وكذلك زين لفرعون سوء عمله وصد عن السبيل وما كيد فرعون الا فى تباب');
INSERT INTO "uthmani_simple" VALUES(40,38,'40:38','وقال الذى ءامن يقوم اتبعون اهدكم سبيل الرشاد');
INSERT INTO "uthmani_simple" VALUES(40,39,'40:39','يقوم انما هذه الحيوة الدنيا متع وان الءاخرة هى دار القرار');
INSERT INTO "uthmani_simple" VALUES(40,40,'40:40','من عمل سيئة فلا يجزى الا مثلها ومن عمل صلحا من ذكر او انثى وهو مؤمن فاو۟لئك يدخلون الجنة يرزقون فيها بغير حساب');
INSERT INTO "uthmani_simple" VALUES(40,41,'40:41','ويقوم ما لى ادعوكم الى النجوة وتدعوننى الى النار');
INSERT INTO "uthmani_simple" VALUES(40,42,'40:42','تدعوننى لاكفر بالله واشرك به ما ليس لى به علم وانا۠ ادعوكم الى العزيز الغفر');
INSERT INTO "uthmani_simple" VALUES(40,43,'40:43','لا جرم انما تدعوننى اليه ليس له دعوة فى الدنيا ولا فى الءاخرة وان مردنا الى الله وان المسرفين هم اصحب النار');
INSERT INTO "uthmani_simple" VALUES(40,44,'40:44','فستذكرون ما اقول لكم وافوض امرى الى الله ان الله بصيرۢ بالعباد');
INSERT INTO "uthmani_simple" VALUES(40,45,'40:45','فوقىه الله سيـات ما مكروا۟ وحاق بـال فرعون سوء العذاب');
INSERT INTO "uthmani_simple" VALUES(40,46,'40:46','النار يعرضون عليها غدوا وعشيا ويوم تقوم الساعة ادخلوا۟ ءال فرعون اشد العذاب');
INSERT INTO "uthmani_simple" VALUES(40,47,'40:47','واذ يتحاجون فى النار فيقول الضعفؤا۟ للذين استكبروا۟ انا كنا لكم تبعا فهل انتم مغنون عنا نصيبا من النار');
INSERT INTO "uthmani_simple" VALUES(40,48,'40:48','قال الذين استكبروا۟ انا كل فيها ان الله قد حكم بين العباد');
INSERT INTO "uthmani_simple" VALUES(40,49,'40:49','وقال الذين فى النار لخزنة جهنم ادعوا۟ ربكم يخفف عنا يوما من العذاب');
INSERT INTO "uthmani_simple" VALUES(40,50,'40:50','قالوا۟ اولم تك تاتيكم رسلكم بالبينت قالوا۟ بلى قالوا۟ فادعوا۟ وما دعؤا۟ الكفرين الا فى ضلل');
INSERT INTO "uthmani_simple" VALUES(40,51,'40:51','انا لننصر رسلنا والذين ءامنوا۟ فى الحيوة الدنيا ويوم يقوم الاشهد');
INSERT INTO "uthmani_simple" VALUES(40,52,'40:52','يوم لا ينفع الظلمين معذرتهم ولهم اللعنة ولهم سوء الدار');
INSERT INTO "uthmani_simple" VALUES(40,53,'40:53','ولقد ءاتينا موسى الهدى واورثنا بنى اسرءيل الكتب');
INSERT INTO "uthmani_simple" VALUES(40,54,'40:54','هدى وذكرى لاو۟لى الالبب');
INSERT INTO "uthmani_simple" VALUES(40,55,'40:55','فاصبر ان وعد الله حق واستغفر لذنۢبك وسبح بحمد ربك بالعشى والابكر');
INSERT INTO "uthmani_simple" VALUES(40,56,'40:56','ان الذين يجدلون فى ءايت الله بغير سلطن اتىهم ان فى صدورهم الا كبر ما هم ببلغيه فاستعذ بالله انه هو السميع البصير');
INSERT INTO "uthmani_simple" VALUES(40,57,'40:57','لخلق السموت والارض اكبر من خلق الناس ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(40,58,'40:58','وما يستوى الاعمى والبصير والذين ءامنوا۟ وعملوا۟ الصلحت ولا المسىء قليلا ما تتذكرون');
INSERT INTO "uthmani_simple" VALUES(40,59,'40:59','ان الساعة لءاتية لا ريب فيها ولكن اكثر الناس لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(40,60,'40:60','وقال ربكم ادعونى استجب لكم ان الذين يستكبرون عن عبادتى سيدخلون جهنم داخرين');
INSERT INTO "uthmani_simple" VALUES(40,61,'40:61','الله الذى جعل لكم اليل لتسكنوا۟ فيه والنهار مبصرا ان الله لذو فضل على الناس ولكن اكثر الناس لا يشكرون');
INSERT INTO "uthmani_simple" VALUES(40,62,'40:62','ذلكم الله ربكم خلق كل شىء لا اله الا هو فانى تؤفكون');
INSERT INTO "uthmani_simple" VALUES(40,63,'40:63','كذلك يؤفك الذين كانوا۟ بـايت الله يجحدون');
INSERT INTO "uthmani_simple" VALUES(40,64,'40:64','الله الذى جعل لكم الارض قرارا والسماء بناء وصوركم فاحسن صوركم ورزقكم من الطيبت ذلكم الله ربكم فتبارك الله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(40,65,'40:65','هو الحى لا اله الا هو فادعوه مخلصين له الدين الحمد لله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(40,66,'40:66','قل انى نهيت ان اعبد الذين تدعون من دون الله لما جاءنى البينت من ربى وامرت ان اسلم لرب العلمين');
INSERT INTO "uthmani_simple" VALUES(40,67,'40:67','هو الذى خلقكم من تراب ثم من نطفة ثم من علقة ثم يخرجكم طفلا ثم لتبلغوا۟ اشدكم ثم لتكونوا۟ شيوخا ومنكم من يتوفى من قبل ولتبلغوا۟ اجلا مسمى ولعلكم تعقلون');
INSERT INTO "uthmani_simple" VALUES(40,68,'40:68','هو الذى يحى ويميت فاذا قضى امرا فانما يقول له كن فيكون');
INSERT INTO "uthmani_simple" VALUES(40,69,'40:69','الم تر الى الذين يجدلون فى ءايت الله انى يصرفون');
INSERT INTO "uthmani_simple" VALUES(40,70,'40:70','الذين كذبوا۟ بالكتب وبما ارسلنا به رسلنا فسوف يعلمون');
INSERT INTO "uthmani_simple" VALUES(40,71,'40:71','اذ الاغلل فى اعنقهم والسلسل يسحبون');
INSERT INTO "uthmani_simple" VALUES(40,72,'40:72','فى الحميم ثم فى النار يسجرون');
INSERT INTO "uthmani_simple" VALUES(40,73,'40:73','ثم قيل لهم اين ما كنتم تشركون');
INSERT INTO "uthmani_simple" VALUES(40,74,'40:74','من دون الله قالوا۟ ضلوا۟ عنا بل لم نكن ندعوا۟ من قبل شيـا كذلك يضل الله الكفرين');
INSERT INTO "uthmani_simple" VALUES(40,75,'40:75','ذلكم بما كنتم تفرحون فى الارض بغير الحق وبما كنتم تمرحون');
INSERT INTO "uthmani_simple" VALUES(40,76,'40:76','ادخلوا۟ ابوب جهنم خلدين فيها فبئس مثوى المتكبرين');
INSERT INTO "uthmani_simple" VALUES(40,77,'40:77','فاصبر ان وعد الله حق فاما نرينك بعض الذى نعدهم او نتوفينك فالينا يرجعون');
INSERT INTO "uthmani_simple" VALUES(40,78,'40:78','ولقد ارسلنا رسلا من قبلك منهم من قصصنا عليك ومنهم من لم نقصص عليك وما كان لرسول ان ياتى بـاية الا باذن الله فاذا جاء امر الله قضى بالحق وخسر هنالك المبطلون');
INSERT INTO "uthmani_simple" VALUES(40,79,'40:79','الله الذى جعل لكم الانعم لتركبوا۟ منها ومنها تاكلون');
INSERT INTO "uthmani_simple" VALUES(40,80,'40:80','ولكم فيها منفع ولتبلغوا۟ عليها حاجة فى صدوركم وعليها وعلى الفلك تحملون');
INSERT INTO "uthmani_simple" VALUES(40,81,'40:81','ويريكم ءايته فاى ءايت الله تنكرون');
INSERT INTO "uthmani_simple" VALUES(40,82,'40:82','افلم يسيروا۟ فى الارض فينظروا۟ كيف كان عقبة الذين من قبلهم كانوا۟ اكثر منهم واشد قوة وءاثارا فى الارض فما اغنى عنهم ما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(40,83,'40:83','فلما جاءتهم رسلهم بالبينت فرحوا۟ بما عندهم من العلم وحاق بهم ما كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(40,84,'40:84','فلما راوا۟ باسنا قالوا۟ ءامنا بالله وحده وكفرنا بما كنا به مشركين');
INSERT INTO "uthmani_simple" VALUES(40,85,'40:85','فلم يك ينفعهم ايمنهم لما راوا۟ باسنا سنت الله التى قد خلت فى عباده وخسر هنالك الكفرون');
INSERT INTO "uthmani_simple" VALUES(41,1,'41:1','حم');
INSERT INTO "uthmani_simple" VALUES(41,2,'41:2','تنزيل من الرحمن الرحيم');
INSERT INTO "uthmani_simple" VALUES(41,3,'41:3','كتب فصلت ءايته قرءانا عربيا لقوم يعلمون');
INSERT INTO "uthmani_simple" VALUES(41,4,'41:4','بشيرا ونذيرا فاعرض اكثرهم فهم لا يسمعون');
INSERT INTO "uthmani_simple" VALUES(41,5,'41:5','وقالوا۟ قلوبنا فى اكنة مما تدعونا اليه وفى ءاذاننا وقر ومنۢ بيننا وبينك حجاب فاعمل اننا عملون');
INSERT INTO "uthmani_simple" VALUES(41,6,'41:6','قل انما انا۠ بشر مثلكم يوحى الى انما الهكم اله وحد فاستقيموا۟ اليه واستغفروه وويل للمشركين');
INSERT INTO "uthmani_simple" VALUES(41,7,'41:7','الذين لا يؤتون الزكوة وهم بالءاخرة هم كفرون');
INSERT INTO "uthmani_simple" VALUES(41,8,'41:8','ان الذين ءامنوا۟ وعملوا۟ الصلحت لهم اجر غير ممنون');
INSERT INTO "uthmani_simple" VALUES(41,9,'41:9','قل ائنكم لتكفرون بالذى خلق الارض فى يومين وتجعلون له اندادا ذلك رب العلمين');
INSERT INTO "uthmani_simple" VALUES(41,10,'41:10','وجعل فيها روسى من فوقها وبرك فيها وقدر فيها اقوتها فى اربعة ايام سواء للسائلين');
INSERT INTO "uthmani_simple" VALUES(41,11,'41:11','ثم استوى الى السماء وهى دخان فقال لها وللارض ائتيا طوعا او كرها قالتا اتينا طائعين');
INSERT INTO "uthmani_simple" VALUES(41,12,'41:12','فقضىهن سبع سموات فى يومين واوحى فى كل سماء امرها وزينا السماء الدنيا بمصبيح وحفظا ذلك تقدير العزيز العليم');
INSERT INTO "uthmani_simple" VALUES(41,13,'41:13','فان اعرضوا۟ فقل انذرتكم صعقة مثل صعقة عاد وثمود');
INSERT INTO "uthmani_simple" VALUES(41,14,'41:14','اذ جاءتهم الرسل منۢ بين ايديهم ومن خلفهم الا تعبدوا۟ الا الله قالوا۟ لو شاء ربنا لانزل ملئكة فانا بما ارسلتم به كفرون');
INSERT INTO "uthmani_simple" VALUES(41,15,'41:15','فاما عاد فاستكبروا۟ فى الارض بغير الحق وقالوا۟ من اشد منا قوة اولم يروا۟ ان الله الذى خلقهم هو اشد منهم قوة وكانوا۟ بـايتنا يجحدون');
INSERT INTO "uthmani_simple" VALUES(41,16,'41:16','فارسلنا عليهم ريحا صرصرا فى ايام نحسات لنذيقهم عذاب الخزى فى الحيوة الدنيا ولعذاب الءاخرة اخزى وهم لا ينصرون');
INSERT INTO "uthmani_simple" VALUES(41,17,'41:17','واما ثمود فهدينهم فاستحبوا۟ العمى على الهدى فاخذتهم صعقة العذاب الهون بما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(41,18,'41:18','ونجينا الذين ءامنوا۟ وكانوا۟ يتقون');
INSERT INTO "uthmani_simple" VALUES(41,19,'41:19','ويوم يحشر اعداء الله الى النار فهم يوزعون');
INSERT INTO "uthmani_simple" VALUES(41,20,'41:20','حتى اذا ما جاءوها شهد عليهم سمعهم وابصرهم وجلودهم بما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(41,21,'41:21','وقالوا۟ لجلودهم لم شهدتم علينا قالوا۟ انطقنا الله الذى انطق كل شىء وهو خلقكم اول مرة واليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(41,22,'41:22','وما كنتم تستترون ان يشهد عليكم سمعكم ولا ابصركم ولا جلودكم ولكن ظننتم ان الله لا يعلم كثيرا مما تعملون');
INSERT INTO "uthmani_simple" VALUES(41,23,'41:23','وذلكم ظنكم الذى ظننتم بربكم اردىكم فاصبحتم من الخسرين');
INSERT INTO "uthmani_simple" VALUES(41,24,'41:24','فان يصبروا۟ فالنار مثوى لهم وان يستعتبوا۟ فما هم من المعتبين');
INSERT INTO "uthmani_simple" VALUES(41,25,'41:25','وقيضنا لهم قرناء فزينوا۟ لهم ما بين ايديهم وما خلفهم وحق عليهم القول فى امم قد خلت من قبلهم من الجن والانس انهم كانوا۟ خسرين');
INSERT INTO "uthmani_simple" VALUES(41,26,'41:26','وقال الذين كفروا۟ لا تسمعوا۟ لهذا القرءان والغوا۟ فيه لعلكم تغلبون');
INSERT INTO "uthmani_simple" VALUES(41,27,'41:27','فلنذيقن الذين كفروا۟ عذابا شديدا ولنجزينهم اسوا الذى كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(41,28,'41:28','ذلك جزاء اعداء الله النار لهم فيها دار الخلد جزاءۢ بما كانوا۟ بـايتنا يجحدون');
INSERT INTO "uthmani_simple" VALUES(41,29,'41:29','وقال الذين كفروا۟ ربنا ارنا الذين اضلانا من الجن والانس نجعلهما تحت اقدامنا ليكونا من الاسفلين');
INSERT INTO "uthmani_simple" VALUES(41,30,'41:30','ان الذين قالوا۟ ربنا الله ثم استقموا۟ تتنزل عليهم الملئكة الا تخافوا۟ ولا تحزنوا۟ وابشروا۟ بالجنة التى كنتم توعدون');
INSERT INTO "uthmani_simple" VALUES(41,31,'41:31','نحن اولياؤكم فى الحيوة الدنيا وفى الءاخرة ولكم فيها ما تشتهى انفسكم ولكم فيها ما تدعون');
INSERT INTO "uthmani_simple" VALUES(41,32,'41:32','نزلا من غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(41,33,'41:33','ومن احسن قولا ممن دعا الى الله وعمل صلحا وقال اننى من المسلمين');
INSERT INTO "uthmani_simple" VALUES(41,34,'41:34','ولا تستوى الحسنة ولا السيئة ادفع بالتى هى احسن فاذا الذى بينك وبينه عدوة كانه ولى حميم');
INSERT INTO "uthmani_simple" VALUES(41,35,'41:35','وما يلقىها الا الذين صبروا۟ وما يلقىها الا ذو حظ عظيم');
INSERT INTO "uthmani_simple" VALUES(41,36,'41:36','واما ينزغنك من الشيطن نزغ فاستعذ بالله انه هو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(41,37,'41:37','ومن ءايته اليل والنهار والشمس والقمر لا تسجدوا۟ للشمس ولا للقمر واسجدوا۟ لله الذى خلقهن ان كنتم اياه تعبدون');
INSERT INTO "uthmani_simple" VALUES(41,38,'41:38','فان استكبروا۟ فالذين عند ربك يسبحون له باليل والنهار وهم لا يسـمون');
INSERT INTO "uthmani_simple" VALUES(41,39,'41:39','ومن ءايته انك ترى الارض خشعة فاذا انزلنا عليها الماء اهتزت وربت ان الذى احياها لمحى الموتى انه على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(41,40,'41:40','ان الذين يلحدون فى ءايتنا لا يخفون علينا افمن يلقى فى النار خير ام من ياتى ءامنا يوم القيمة اعملوا۟ ما شئتم انه بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(41,41,'41:41','ان الذين كفروا۟ بالذكر لما جاءهم وانه لكتب عزيز');
INSERT INTO "uthmani_simple" VALUES(41,42,'41:42','لا ياتيه البطل منۢ بين يديه ولا من خلفه تنزيل من حكيم حميد');
INSERT INTO "uthmani_simple" VALUES(41,43,'41:43','ما يقال لك الا ما قد قيل للرسل من قبلك ان ربك لذو مغفرة وذو عقاب اليم');
INSERT INTO "uthmani_simple" VALUES(41,44,'41:44','ولو جعلنه قرءانا اعجميا لقالوا۟ لولا فصلت ءايته ءا۬عجمى وعربى قل هو للذين ءامنوا۟ هدى وشفاء والذين لا يؤمنون فى ءاذانهم وقر وهو عليهم عمى او۟لئك ينادون من مكانۭ بعيد');
INSERT INTO "uthmani_simple" VALUES(41,45,'41:45','ولقد ءاتينا موسى الكتب فاختلف فيه ولولا كلمة سبقت من ربك لقضى بينهم وانهم لفى شك منه مريب');
INSERT INTO "uthmani_simple" VALUES(41,46,'41:46','من عمل صلحا فلنفسه ومن اساء فعليها وما ربك بظلم للعبيد');
INSERT INTO "uthmani_simple" VALUES(41,47,'41:47','اليه يرد علم الساعة وما تخرج من ثمرت من اكمامها وما تحمل من انثى ولا تضع الا بعلمه ويوم يناديهم اين شركاءى قالوا۟ ءاذنك ما منا من شهيد');
INSERT INTO "uthmani_simple" VALUES(41,48,'41:48','وضل عنهم ما كانوا۟ يدعون من قبل وظنوا۟ ما لهم من محيص');
INSERT INTO "uthmani_simple" VALUES(41,49,'41:49','لا يسـم الانسن من دعاء الخير وان مسه الشر فيـوس قنوط');
INSERT INTO "uthmani_simple" VALUES(41,50,'41:50','ولئن اذقنه رحمة منا منۢ بعد ضراء مسته ليقولن هذا لى وما اظن الساعة قائمة ولئن رجعت الى ربى ان لى عنده للحسنى فلننبئن الذين كفروا۟ بما عملوا۟ ولنذيقنهم من عذاب غليظ');
INSERT INTO "uthmani_simple" VALUES(41,51,'41:51','واذا انعمنا على الانسن اعرض ونـا بجانبه واذا مسه الشر فذو دعاء عريض');
INSERT INTO "uthmani_simple" VALUES(41,52,'41:52','قل ارءيتم ان كان من عند الله ثم كفرتم به من اضل ممن هو فى شقاقۭ بعيد');
INSERT INTO "uthmani_simple" VALUES(41,53,'41:53','سنريهم ءايتنا فى الءافاق وفى انفسهم حتى يتبين لهم انه الحق اولم يكف بربك انه على كل شىء شهيد');
INSERT INTO "uthmani_simple" VALUES(41,54,'41:54','الا انهم فى مرية من لقاء ربهم الا انه بكل شىء محيطۢ');
INSERT INTO "uthmani_simple" VALUES(42,1,'42:1','حم');
INSERT INTO "uthmani_simple" VALUES(42,2,'42:2','عسق');
INSERT INTO "uthmani_simple" VALUES(42,3,'42:3','كذلك يوحى اليك والى الذين من قبلك الله العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(42,4,'42:4','له ما فى السموت وما فى الارض وهو العلى العظيم');
INSERT INTO "uthmani_simple" VALUES(42,5,'42:5','تكاد السموت يتفطرن من فوقهن والملئكة يسبحون بحمد ربهم ويستغفرون لمن فى الارض الا ان الله هو الغفور الرحيم');
INSERT INTO "uthmani_simple" VALUES(42,6,'42:6','والذين اتخذوا۟ من دونه اولياء الله حفيظ عليهم وما انت عليهم بوكيل');
INSERT INTO "uthmani_simple" VALUES(42,7,'42:7','وكذلك اوحينا اليك قرءانا عربيا لتنذر ام القرى ومن حولها وتنذر يوم الجمع لا ريب فيه فريق فى الجنة وفريق فى السعير');
INSERT INTO "uthmani_simple" VALUES(42,8,'42:8','ولو شاء الله لجعلهم امة وحدة ولكن يدخل من يشاء فى رحمته والظلمون ما لهم من ولى ولا نصير');
INSERT INTO "uthmani_simple" VALUES(42,9,'42:9','ام اتخذوا۟ من دونه اولياء فالله هو الولى وهو يحى الموتى وهو على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(42,10,'42:10','وما اختلفتم فيه من شىء فحكمه الى الله ذلكم الله ربى عليه توكلت واليه انيب');
INSERT INTO "uthmani_simple" VALUES(42,11,'42:11','فاطر السموت والارض جعل لكم من انفسكم ازوجا ومن الانعم ازوجا يذرؤكم فيه ليس كمثله شىء وهو السميع البصير');
INSERT INTO "uthmani_simple" VALUES(42,12,'42:12','له مقاليد السموت والارض يبسط الرزق لمن يشاء ويقدر انه بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(42,13,'42:13','شرع لكم من الدين ما وصى به نوحا والذى اوحينا اليك وما وصينا به ابرهيم وموسى وعيسى ان اقيموا۟ الدين ولا تتفرقوا۟ فيه كبر على المشركين ما تدعوهم اليه الله يجتبى اليه من يشاء ويهدى اليه من ينيب');
INSERT INTO "uthmani_simple" VALUES(42,14,'42:14','وما تفرقوا۟ الا منۢ بعد ما جاءهم العلم بغيۢا بينهم ولولا كلمة سبقت من ربك الى اجل مسمى لقضى بينهم وان الذين اورثوا۟ الكتب منۢ بعدهم لفى شك منه مريب');
INSERT INTO "uthmani_simple" VALUES(42,15,'42:15','فلذلك فادع واستقم كما امرت ولا تتبع اهواءهم وقل ءامنت بما انزل الله من كتب وامرت لاعدل بينكم الله ربنا وربكم لنا اعملنا ولكم اعملكم لا حجة بيننا وبينكم الله يجمع بيننا واليه المصير');
INSERT INTO "uthmani_simple" VALUES(42,16,'42:16','والذين يحاجون فى الله منۢ بعد ما استجيب له حجتهم داحضة عند ربهم وعليهم غضب ولهم عذاب شديد');
INSERT INTO "uthmani_simple" VALUES(42,17,'42:17','الله الذى انزل الكتب بالحق والميزان وما يدريك لعل الساعة قريب');
INSERT INTO "uthmani_simple" VALUES(42,18,'42:18','يستعجل بها الذين لا يؤمنون بها والذين ءامنوا۟ مشفقون منها ويعلمون انها الحق الا ان الذين يمارون فى الساعة لفى ضللۭ بعيد');
INSERT INTO "uthmani_simple" VALUES(42,19,'42:19','الله لطيفۢ بعباده يرزق من يشاء وهو القوى العزيز');
INSERT INTO "uthmani_simple" VALUES(42,20,'42:20','من كان يريد حرث الءاخرة نزد له فى حرثه ومن كان يريد حرث الدنيا نؤته منها وما له فى الءاخرة من نصيب');
INSERT INTO "uthmani_simple" VALUES(42,21,'42:21','ام لهم شركؤا۟ شرعوا۟ لهم من الدين ما لم ياذنۢ به الله ولولا كلمة الفصل لقضى بينهم وان الظلمين لهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(42,22,'42:22','ترى الظلمين مشفقين مما كسبوا۟ وهو واقعۢ بهم والذين ءامنوا۟ وعملوا۟ الصلحت فى روضات الجنات لهم ما يشاءون عند ربهم ذلك هو الفضل الكبير');
INSERT INTO "uthmani_simple" VALUES(42,23,'42:23','ذلك الذى يبشر الله عباده الذين ءامنوا۟ وعملوا۟ الصلحت قل لا اسـلكم عليه اجرا الا المودة فى القربى ومن يقترف حسنة نزد له فيها حسنا ان الله غفور شكور');
INSERT INTO "uthmani_simple" VALUES(42,24,'42:24','ام يقولون افترى على الله كذبا فان يشا الله يختم على قلبك ويمح الله البطل ويحق الحق بكلمته انه عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(42,25,'42:25','وهو الذى يقبل التوبة عن عباده ويعفوا۟ عن السيـات ويعلم ما تفعلون');
INSERT INTO "uthmani_simple" VALUES(42,26,'42:26','ويستجيب الذين ءامنوا۟ وعملوا۟ الصلحت ويزيدهم من فضله والكفرون لهم عذاب شديد');
INSERT INTO "uthmani_simple" VALUES(42,27,'42:27','ولو بسط الله الرزق لعباده لبغوا۟ فى الارض ولكن ينزل بقدر ما يشاء انه بعباده خبيرۢ بصير');
INSERT INTO "uthmani_simple" VALUES(42,28,'42:28','وهو الذى ينزل الغيث منۢ بعد ما قنطوا۟ وينشر رحمته وهو الولى الحميد');
INSERT INTO "uthmani_simple" VALUES(42,29,'42:29','ومن ءايته خلق السموت والارض وما بث فيهما من دابة وهو على جمعهم اذا يشاء قدير');
INSERT INTO "uthmani_simple" VALUES(42,30,'42:30','وما اصبكم من مصيبة فبما كسبت ايديكم ويعفوا۟ عن كثير');
INSERT INTO "uthmani_simple" VALUES(42,31,'42:31','وما انتم بمعجزين فى الارض وما لكم من دون الله من ولى ولا نصير');
INSERT INTO "uthmani_simple" VALUES(42,32,'42:32','ومن ءايته الجوار فى البحر كالاعلم');
INSERT INTO "uthmani_simple" VALUES(42,33,'42:33','ان يشا يسكن الريح فيظللن رواكد على ظهره ان فى ذلك لءايت لكل صبار شكور');
INSERT INTO "uthmani_simple" VALUES(42,34,'42:34','او يوبقهن بما كسبوا۟ ويعف عن كثير');
INSERT INTO "uthmani_simple" VALUES(42,35,'42:35','ويعلم الذين يجدلون فى ءايتنا ما لهم من محيص');
INSERT INTO "uthmani_simple" VALUES(42,36,'42:36','فما اوتيتم من شىء فمتع الحيوة الدنيا وما عند الله خير وابقى للذين ءامنوا۟ وعلى ربهم يتوكلون');
INSERT INTO "uthmani_simple" VALUES(42,37,'42:37','والذين يجتنبون كبئر الاثم والفوحش واذا ما غضبوا۟ هم يغفرون');
INSERT INTO "uthmani_simple" VALUES(42,38,'42:38','والذين استجابوا۟ لربهم واقاموا۟ الصلوة وامرهم شورى بينهم ومما رزقنهم ينفقون');
INSERT INTO "uthmani_simple" VALUES(42,39,'42:39','والذين اذا اصابهم البغى هم ينتصرون');
INSERT INTO "uthmani_simple" VALUES(42,40,'42:40','وجزؤا۟ سيئة سيئة مثلها فمن عفا واصلح فاجره على الله انه لا يحب الظلمين');
INSERT INTO "uthmani_simple" VALUES(42,41,'42:41','ولمن انتصر بعد ظلمه فاو۟لئك ما عليهم من سبيل');
INSERT INTO "uthmani_simple" VALUES(42,42,'42:42','انما السبيل على الذين يظلمون الناس ويبغون فى الارض بغير الحق او۟لئك لهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(42,43,'42:43','ولمن صبر وغفر ان ذلك لمن عزم الامور');
INSERT INTO "uthmani_simple" VALUES(42,44,'42:44','ومن يضلل الله فما له من ولى منۢ بعده وترى الظلمين لما راوا۟ العذاب يقولون هل الى مرد من سبيل');
INSERT INTO "uthmani_simple" VALUES(42,45,'42:45','وترىهم يعرضون عليها خشعين من الذل ينظرون من طرف خفى وقال الذين ءامنوا۟ ان الخسرين الذين خسروا۟ انفسهم واهليهم يوم القيمة الا ان الظلمين فى عذاب مقيم');
INSERT INTO "uthmani_simple" VALUES(42,46,'42:46','وما كان لهم من اولياء ينصرونهم من دون الله ومن يضلل الله فما له من سبيل');
INSERT INTO "uthmani_simple" VALUES(42,47,'42:47','استجيبوا۟ لربكم من قبل ان ياتى يوم لا مرد له من الله ما لكم من ملجا يومئذ وما لكم من نكير');
INSERT INTO "uthmani_simple" VALUES(42,48,'42:48','فان اعرضوا۟ فما ارسلنك عليهم حفيظا ان عليك الا البلغ وانا اذا اذقنا الانسن منا رحمة فرح بها وان تصبهم سيئةۢ بما قدمت ايديهم فان الانسن كفور');
INSERT INTO "uthmani_simple" VALUES(42,49,'42:49','لله ملك السموت والارض يخلق ما يشاء يهب لمن يشاء انثا ويهب لمن يشاء الذكور');
INSERT INTO "uthmani_simple" VALUES(42,50,'42:50','او يزوجهم ذكرانا وانثا ويجعل من يشاء عقيما انه عليم قدير');
INSERT INTO "uthmani_simple" VALUES(42,51,'42:51','وما كان لبشر ان يكلمه الله الا وحيا او من ورائ حجاب او يرسل رسولا فيوحى باذنه ما يشاء انه على حكيم');
INSERT INTO "uthmani_simple" VALUES(42,52,'42:52','وكذلك اوحينا اليك روحا من امرنا ما كنت تدرى ما الكتب ولا الايمن ولكن جعلنه نورا نهدى به من نشاء من عبادنا وانك لتهدى الى صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(42,53,'42:53','صرط الله الذى له ما فى السموت وما فى الارض الا الى الله تصير الامور');
INSERT INTO "uthmani_simple" VALUES(43,1,'43:1','حم');
INSERT INTO "uthmani_simple" VALUES(43,2,'43:2','والكتب المبين');
INSERT INTO "uthmani_simple" VALUES(43,3,'43:3','انا جعلنه قرءنا عربيا لعلكم تعقلون');
INSERT INTO "uthmani_simple" VALUES(43,4,'43:4','وانه فى ام الكتب لدينا لعلى حكيم');
INSERT INTO "uthmani_simple" VALUES(43,5,'43:5','افنضرب عنكم الذكر صفحا ان كنتم قوما مسرفين');
INSERT INTO "uthmani_simple" VALUES(43,6,'43:6','وكم ارسلنا من نبى فى الاولين');
INSERT INTO "uthmani_simple" VALUES(43,7,'43:7','وما ياتيهم من نبى الا كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(43,8,'43:8','فاهلكنا اشد منهم بطشا ومضى مثل الاولين');
INSERT INTO "uthmani_simple" VALUES(43,9,'43:9','ولئن سالتهم من خلق السموت والارض ليقولن خلقهن العزيز العليم');
INSERT INTO "uthmani_simple" VALUES(43,10,'43:10','الذى جعل لكم الارض مهدا وجعل لكم فيها سبلا لعلكم تهتدون');
INSERT INTO "uthmani_simple" VALUES(43,11,'43:11','والذى نزل من السماء ماءۢ بقدر فانشرنا به بلدة ميتا كذلك تخرجون');
INSERT INTO "uthmani_simple" VALUES(43,12,'43:12','والذى خلق الازوج كلها وجعل لكم من الفلك والانعم ما تركبون');
INSERT INTO "uthmani_simple" VALUES(43,13,'43:13','لتستوا۟ على ظهوره ثم تذكروا۟ نعمة ربكم اذا استويتم عليه وتقولوا۟ سبحن الذى سخر لنا هذا وما كنا له مقرنين');
INSERT INTO "uthmani_simple" VALUES(43,14,'43:14','وانا الى ربنا لمنقلبون');
INSERT INTO "uthmani_simple" VALUES(43,15,'43:15','وجعلوا۟ له من عباده جزءا ان الانسن لكفور مبين');
INSERT INTO "uthmani_simple" VALUES(43,16,'43:16','ام اتخذ مما يخلق بنات واصفىكم بالبنين');
INSERT INTO "uthmani_simple" VALUES(43,17,'43:17','واذا بشر احدهم بما ضرب للرحمن مثلا ظل وجهه مسودا وهو كظيم');
INSERT INTO "uthmani_simple" VALUES(43,18,'43:18','اومن ينشؤا۟ فى الحلية وهو فى الخصام غير مبين');
INSERT INTO "uthmani_simple" VALUES(43,19,'43:19','وجعلوا۟ الملئكة الذين هم عبد الرحمن انثا اشهدوا۟ خلقهم ستكتب شهدتهم ويسـلون');
INSERT INTO "uthmani_simple" VALUES(43,20,'43:20','وقالوا۟ لو شاء الرحمن ما عبدنهم ما لهم بذلك من علم ان هم الا يخرصون');
INSERT INTO "uthmani_simple" VALUES(43,21,'43:21','ام ءاتينهم كتبا من قبله فهم به مستمسكون');
INSERT INTO "uthmani_simple" VALUES(43,22,'43:22','بل قالوا۟ انا وجدنا ءاباءنا على امة وانا على ءاثرهم مهتدون');
INSERT INTO "uthmani_simple" VALUES(43,23,'43:23','وكذلك ما ارسلنا من قبلك فى قرية من نذير الا قال مترفوها انا وجدنا ءاباءنا على امة وانا على ءاثرهم مقتدون');
INSERT INTO "uthmani_simple" VALUES(43,24,'43:24','قل اولو جئتكم باهدى مما وجدتم عليه ءاباءكم قالوا۟ انا بما ارسلتم به كفرون');
INSERT INTO "uthmani_simple" VALUES(43,25,'43:25','فانتقمنا منهم فانظر كيف كان عقبة المكذبين');
INSERT INTO "uthmani_simple" VALUES(43,26,'43:26','واذ قال ابرهيم لابيه وقومه اننى براء مما تعبدون');
INSERT INTO "uthmani_simple" VALUES(43,27,'43:27','الا الذى فطرنى فانه سيهدين');
INSERT INTO "uthmani_simple" VALUES(43,28,'43:28','وجعلها كلمةۢ باقية فى عقبه لعلهم يرجعون');
INSERT INTO "uthmani_simple" VALUES(43,29,'43:29','بل متعت هؤلاء وءاباءهم حتى جاءهم الحق ورسول مبين');
INSERT INTO "uthmani_simple" VALUES(43,30,'43:30','ولما جاءهم الحق قالوا۟ هذا سحر وانا به كفرون');
INSERT INTO "uthmani_simple" VALUES(43,31,'43:31','وقالوا۟ لولا نزل هذا القرءان على رجل من القريتين عظيم');
INSERT INTO "uthmani_simple" VALUES(43,32,'43:32','اهم يقسمون رحمت ربك نحن قسمنا بينهم معيشتهم فى الحيوة الدنيا ورفعنا بعضهم فوق بعض درجت ليتخذ بعضهم بعضا سخريا ورحمت ربك خير مما يجمعون');
INSERT INTO "uthmani_simple" VALUES(43,33,'43:33','ولولا ان يكون الناس امة وحدة لجعلنا لمن يكفر بالرحمن لبيوتهم سقفا من فضة ومعارج عليها يظهرون');
INSERT INTO "uthmani_simple" VALUES(43,34,'43:34','ولبيوتهم ابوبا وسررا عليها يتكـون');
INSERT INTO "uthmani_simple" VALUES(43,35,'43:35','وزخرفا وان كل ذلك لما متع الحيوة الدنيا والءاخرة عند ربك للمتقين');
INSERT INTO "uthmani_simple" VALUES(43,36,'43:36','ومن يعش عن ذكر الرحمن نقيض له شيطنا فهو له قرين');
INSERT INTO "uthmani_simple" VALUES(43,37,'43:37','وانهم ليصدونهم عن السبيل ويحسبون انهم مهتدون');
INSERT INTO "uthmani_simple" VALUES(43,38,'43:38','حتى اذا جاءنا قال يليت بينى وبينك بعد المشرقين فبئس القرين');
INSERT INTO "uthmani_simple" VALUES(43,39,'43:39','ولن ينفعكم اليوم اذ ظلمتم انكم فى العذاب مشتركون');
INSERT INTO "uthmani_simple" VALUES(43,40,'43:40','افانت تسمع الصم او تهدى العمى ومن كان فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(43,41,'43:41','فاما نذهبن بك فانا منهم منتقمون');
INSERT INTO "uthmani_simple" VALUES(43,42,'43:42','او نرينك الذى وعدنهم فانا عليهم مقتدرون');
INSERT INTO "uthmani_simple" VALUES(43,43,'43:43','فاستمسك بالذى اوحى اليك انك على صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(43,44,'43:44','وانه لذكر لك ولقومك وسوف تسـلون');
INSERT INTO "uthmani_simple" VALUES(43,45,'43:45','وسـل من ارسلنا من قبلك من رسلنا اجعلنا من دون الرحمن ءالهة يعبدون');
INSERT INTO "uthmani_simple" VALUES(43,46,'43:46','ولقد ارسلنا موسى بـايتنا الى فرعون وملاي۟ه فقال انى رسول رب العلمين');
INSERT INTO "uthmani_simple" VALUES(43,47,'43:47','فلما جاءهم بـايتنا اذا هم منها يضحكون');
INSERT INTO "uthmani_simple" VALUES(43,48,'43:48','وما نريهم من ءاية الا هى اكبر من اختها واخذنهم بالعذاب لعلهم يرجعون');
INSERT INTO "uthmani_simple" VALUES(43,49,'43:49','وقالوا۟ يايه الساحر ادع لنا ربك بما عهد عندك اننا لمهتدون');
INSERT INTO "uthmani_simple" VALUES(43,50,'43:50','فلما كشفنا عنهم العذاب اذا هم ينكثون');
INSERT INTO "uthmani_simple" VALUES(43,51,'43:51','ونادى فرعون فى قومه قال يقوم اليس لى ملك مصر وهذه الانهر تجرى من تحتى افلا تبصرون');
INSERT INTO "uthmani_simple" VALUES(43,52,'43:52','ام انا۠ خير من هذا الذى هو مهين ولا يكاد يبين');
INSERT INTO "uthmani_simple" VALUES(43,53,'43:53','فلولا القى عليه اسورة من ذهب او جاء معه الملئكة مقترنين');
INSERT INTO "uthmani_simple" VALUES(43,54,'43:54','فاستخف قومه فاطاعوه انهم كانوا۟ قوما فسقين');
INSERT INTO "uthmani_simple" VALUES(43,55,'43:55','فلما ءاسفونا انتقمنا منهم فاغرقنهم اجمعين');
INSERT INTO "uthmani_simple" VALUES(43,56,'43:56','فجعلنهم سلفا ومثلا للءاخرين');
INSERT INTO "uthmani_simple" VALUES(43,57,'43:57','ولما ضرب ابن مريم مثلا اذا قومك منه يصدون');
INSERT INTO "uthmani_simple" VALUES(43,58,'43:58','وقالوا۟ ءالهتنا خير ام هو ما ضربوه لك الا جدلۢا بل هم قوم خصمون');
INSERT INTO "uthmani_simple" VALUES(43,59,'43:59','ان هو الا عبد انعمنا عليه وجعلنه مثلا لبنى اسرءيل');
INSERT INTO "uthmani_simple" VALUES(43,60,'43:60','ولو نشاء لجعلنا منكم ملئكة فى الارض يخلفون');
INSERT INTO "uthmani_simple" VALUES(43,61,'43:61','وانه لعلم للساعة فلا تمترن بها واتبعون هذا صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(43,62,'43:62','ولا يصدنكم الشيطن انه لكم عدو مبين');
INSERT INTO "uthmani_simple" VALUES(43,63,'43:63','ولما جاء عيسى بالبينت قال قد جئتكم بالحكمة ولابين لكم بعض الذى تختلفون فيه فاتقوا۟ الله واطيعون');
INSERT INTO "uthmani_simple" VALUES(43,64,'43:64','ان الله هو ربى وربكم فاعبدوه هذا صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(43,65,'43:65','فاختلف الاحزاب منۢ بينهم فويل للذين ظلموا۟ من عذاب يوم اليم');
INSERT INTO "uthmani_simple" VALUES(43,66,'43:66','هل ينظرون الا الساعة ان تاتيهم بغتة وهم لا يشعرون');
INSERT INTO "uthmani_simple" VALUES(43,67,'43:67','الاخلاء يومئذۭ بعضهم لبعض عدو الا المتقين');
INSERT INTO "uthmani_simple" VALUES(43,68,'43:68','يعباد لا خوف عليكم اليوم ولا انتم تحزنون');
INSERT INTO "uthmani_simple" VALUES(43,69,'43:69','الذين ءامنوا۟ بـايتنا وكانوا۟ مسلمين');
INSERT INTO "uthmani_simple" VALUES(43,70,'43:70','ادخلوا۟ الجنة انتم وازوجكم تحبرون');
INSERT INTO "uthmani_simple" VALUES(43,71,'43:71','يطاف عليهم بصحاف من ذهب واكواب وفيها ما تشتهيه الانفس وتلذ الاعين وانتم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(43,72,'43:72','وتلك الجنة التى اورثتموها بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(43,73,'43:73','لكم فيها فكهة كثيرة منها تاكلون');
INSERT INTO "uthmani_simple" VALUES(43,74,'43:74','ان المجرمين فى عذاب جهنم خلدون');
INSERT INTO "uthmani_simple" VALUES(43,75,'43:75','لا يفتر عنهم وهم فيه مبلسون');
INSERT INTO "uthmani_simple" VALUES(43,76,'43:76','وما ظلمنهم ولكن كانوا۟ هم الظلمين');
INSERT INTO "uthmani_simple" VALUES(43,77,'43:77','ونادوا۟ يملك ليقض علينا ربك قال انكم مكثون');
INSERT INTO "uthmani_simple" VALUES(43,78,'43:78','لقد جئنكم بالحق ولكن اكثركم للحق كرهون');
INSERT INTO "uthmani_simple" VALUES(43,79,'43:79','ام ابرموا۟ امرا فانا مبرمون');
INSERT INTO "uthmani_simple" VALUES(43,80,'43:80','ام يحسبون انا لا نسمع سرهم ونجوىهم بلى ورسلنا لديهم يكتبون');
INSERT INTO "uthmani_simple" VALUES(43,81,'43:81','قل ان كان للرحمن ولد فانا۠ اول العبدين');
INSERT INTO "uthmani_simple" VALUES(43,82,'43:82','سبحن رب السموت والارض رب العرش عما يصفون');
INSERT INTO "uthmani_simple" VALUES(43,83,'43:83','فذرهم يخوضوا۟ ويلعبوا۟ حتى يلقوا۟ يومهم الذى يوعدون');
INSERT INTO "uthmani_simple" VALUES(43,84,'43:84','وهو الذى فى السماء اله وفى الارض اله وهو الحكيم العليم');
INSERT INTO "uthmani_simple" VALUES(43,85,'43:85','وتبارك الذى له ملك السموت والارض وما بينهما وعنده علم الساعة واليه ترجعون');
INSERT INTO "uthmani_simple" VALUES(43,86,'43:86','ولا يملك الذين يدعون من دونه الشفعة الا من شهد بالحق وهم يعلمون');
INSERT INTO "uthmani_simple" VALUES(43,87,'43:87','ولئن سالتهم من خلقهم ليقولن الله فانى يؤفكون');
INSERT INTO "uthmani_simple" VALUES(43,88,'43:88','وقيله يرب ان هؤلاء قوم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(43,89,'43:89','فاصفح عنهم وقل سلم فسوف يعلمون');
INSERT INTO "uthmani_simple" VALUES(44,1,'44:1','حم');
INSERT INTO "uthmani_simple" VALUES(44,2,'44:2','والكتب المبين');
INSERT INTO "uthmani_simple" VALUES(44,3,'44:3','انا انزلنه فى ليلة مبركة انا كنا منذرين');
INSERT INTO "uthmani_simple" VALUES(44,4,'44:4','فيها يفرق كل امر حكيم');
INSERT INTO "uthmani_simple" VALUES(44,5,'44:5','امرا من عندنا انا كنا مرسلين');
INSERT INTO "uthmani_simple" VALUES(44,6,'44:6','رحمة من ربك انه هو السميع العليم');
INSERT INTO "uthmani_simple" VALUES(44,7,'44:7','رب السموت والارض وما بينهما ان كنتم موقنين');
INSERT INTO "uthmani_simple" VALUES(44,8,'44:8','لا اله الا هو يحى ويميت ربكم ورب ءابائكم الاولين');
INSERT INTO "uthmani_simple" VALUES(44,9,'44:9','بل هم فى شك يلعبون');
INSERT INTO "uthmani_simple" VALUES(44,10,'44:10','فارتقب يوم تاتى السماء بدخان مبين');
INSERT INTO "uthmani_simple" VALUES(44,11,'44:11','يغشى الناس هذا عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(44,12,'44:12','ربنا اكشف عنا العذاب انا مؤمنون');
INSERT INTO "uthmani_simple" VALUES(44,13,'44:13','انى لهم الذكرى وقد جاءهم رسول مبين');
INSERT INTO "uthmani_simple" VALUES(44,14,'44:14','ثم تولوا۟ عنه وقالوا۟ معلم مجنون');
INSERT INTO "uthmani_simple" VALUES(44,15,'44:15','انا كاشفوا۟ العذاب قليلا انكم عائدون');
INSERT INTO "uthmani_simple" VALUES(44,16,'44:16','يوم نبطش البطشة الكبرى انا منتقمون');
INSERT INTO "uthmani_simple" VALUES(44,17,'44:17','ولقد فتنا قبلهم قوم فرعون وجاءهم رسول كريم');
INSERT INTO "uthmani_simple" VALUES(44,18,'44:18','ان ادوا۟ الى عباد الله انى لكم رسول امين');
INSERT INTO "uthmani_simple" VALUES(44,19,'44:19','وان لا تعلوا۟ على الله انى ءاتيكم بسلطن مبين');
INSERT INTO "uthmani_simple" VALUES(44,20,'44:20','وانى عذت بربى وربكم ان ترجمون');
INSERT INTO "uthmani_simple" VALUES(44,21,'44:21','وان لم تؤمنوا۟ لى فاعتزلون');
INSERT INTO "uthmani_simple" VALUES(44,22,'44:22','فدعا ربه ان هؤلاء قوم مجرمون');
INSERT INTO "uthmani_simple" VALUES(44,23,'44:23','فاسر بعبادى ليلا انكم متبعون');
INSERT INTO "uthmani_simple" VALUES(44,24,'44:24','واترك البحر رهوا انهم جند مغرقون');
INSERT INTO "uthmani_simple" VALUES(44,25,'44:25','كم تركوا۟ من جنت وعيون');
INSERT INTO "uthmani_simple" VALUES(44,26,'44:26','وزروع ومقام كريم');
INSERT INTO "uthmani_simple" VALUES(44,27,'44:27','ونعمة كانوا۟ فيها فكهين');
INSERT INTO "uthmani_simple" VALUES(44,28,'44:28','كذلك واورثنها قوما ءاخرين');
INSERT INTO "uthmani_simple" VALUES(44,29,'44:29','فما بكت عليهم السماء والارض وما كانوا۟ منظرين');
INSERT INTO "uthmani_simple" VALUES(44,30,'44:30','ولقد نجينا بنى اسرءيل من العذاب المهين');
INSERT INTO "uthmani_simple" VALUES(44,31,'44:31','من فرعون انه كان عاليا من المسرفين');
INSERT INTO "uthmani_simple" VALUES(44,32,'44:32','ولقد اخترنهم على علم على العلمين');
INSERT INTO "uthmani_simple" VALUES(44,33,'44:33','وءاتينهم من الءايت ما فيه بلؤا۟ مبين');
INSERT INTO "uthmani_simple" VALUES(44,34,'44:34','ان هؤلاء ليقولون');
INSERT INTO "uthmani_simple" VALUES(44,35,'44:35','ان هى الا موتتنا الاولى وما نحن بمنشرين');
INSERT INTO "uthmani_simple" VALUES(44,36,'44:36','فاتوا۟ بـابائنا ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(44,37,'44:37','اهم خير ام قوم تبع والذين من قبلهم اهلكنهم انهم كانوا۟ مجرمين');
INSERT INTO "uthmani_simple" VALUES(44,38,'44:38','وما خلقنا السموت والارض وما بينهما لعبين');
INSERT INTO "uthmani_simple" VALUES(44,39,'44:39','ما خلقنهما الا بالحق ولكن اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(44,40,'44:40','ان يوم الفصل ميقتهم اجمعين');
INSERT INTO "uthmani_simple" VALUES(44,41,'44:41','يوم لا يغنى مولى عن مولى شيـا ولا هم ينصرون');
INSERT INTO "uthmani_simple" VALUES(44,42,'44:42','الا من رحم الله انه هو العزيز الرحيم');
INSERT INTO "uthmani_simple" VALUES(44,43,'44:43','ان شجرت الزقوم');
INSERT INTO "uthmani_simple" VALUES(44,44,'44:44','طعام الاثيم');
INSERT INTO "uthmani_simple" VALUES(44,45,'44:45','كالمهل يغلى فى البطون');
INSERT INTO "uthmani_simple" VALUES(44,46,'44:46','كغلى الحميم');
INSERT INTO "uthmani_simple" VALUES(44,47,'44:47','خذوه فاعتلوه الى سواء الجحيم');
INSERT INTO "uthmani_simple" VALUES(44,48,'44:48','ثم صبوا۟ فوق راسه من عذاب الحميم');
INSERT INTO "uthmani_simple" VALUES(44,49,'44:49','ذق انك انت العزيز الكريم');
INSERT INTO "uthmani_simple" VALUES(44,50,'44:50','ان هذا ما كنتم به تمترون');
INSERT INTO "uthmani_simple" VALUES(44,51,'44:51','ان المتقين فى مقام امين');
INSERT INTO "uthmani_simple" VALUES(44,52,'44:52','فى جنت وعيون');
INSERT INTO "uthmani_simple" VALUES(44,53,'44:53','يلبسون من سندس واستبرق متقبلين');
INSERT INTO "uthmani_simple" VALUES(44,54,'44:54','كذلك وزوجنهم بحور عين');
INSERT INTO "uthmani_simple" VALUES(44,55,'44:55','يدعون فيها بكل فكهة ءامنين');
INSERT INTO "uthmani_simple" VALUES(44,56,'44:56','لا يذوقون فيها الموت الا الموتة الاولى ووقىهم عذاب الجحيم');
INSERT INTO "uthmani_simple" VALUES(44,57,'44:57','فضلا من ربك ذلك هو الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(44,58,'44:58','فانما يسرنه بلسانك لعلهم يتذكرون');
INSERT INTO "uthmani_simple" VALUES(44,59,'44:59','فارتقب انهم مرتقبون');
INSERT INTO "uthmani_simple" VALUES(45,1,'45:1','حم');
INSERT INTO "uthmani_simple" VALUES(45,2,'45:2','تنزيل الكتب من الله العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(45,3,'45:3','ان فى السموت والارض لءايت للمؤمنين');
INSERT INTO "uthmani_simple" VALUES(45,4,'45:4','وفى خلقكم وما يبث من دابة ءايت لقوم يوقنون');
INSERT INTO "uthmani_simple" VALUES(45,5,'45:5','واختلف اليل والنهار وما انزل الله من السماء من رزق فاحيا به الارض بعد موتها وتصريف الريح ءايت لقوم يعقلون');
INSERT INTO "uthmani_simple" VALUES(45,6,'45:6','تلك ءايت الله نتلوها عليك بالحق فباى حديثۭ بعد الله وءايته يؤمنون');
INSERT INTO "uthmani_simple" VALUES(45,7,'45:7','ويل لكل افاك اثيم');
INSERT INTO "uthmani_simple" VALUES(45,8,'45:8','يسمع ءايت الله تتلى عليه ثم يصر مستكبرا كان لم يسمعها فبشره بعذاب اليم');
INSERT INTO "uthmani_simple" VALUES(45,9,'45:9','واذا علم من ءايتنا شيـا اتخذها هزوا او۟لئك لهم عذاب مهين');
INSERT INTO "uthmani_simple" VALUES(45,10,'45:10','من ورائهم جهنم ولا يغنى عنهم ما كسبوا۟ شيـا ولا ما اتخذوا۟ من دون الله اولياء ولهم عذاب عظيم');
INSERT INTO "uthmani_simple" VALUES(45,11,'45:11','هذا هدى والذين كفروا۟ بـايت ربهم لهم عذاب من رجز اليم');
INSERT INTO "uthmani_simple" VALUES(45,12,'45:12','الله الذى سخر لكم البحر لتجرى الفلك فيه بامره ولتبتغوا۟ من فضله ولعلكم تشكرون');
INSERT INTO "uthmani_simple" VALUES(45,13,'45:13','وسخر لكم ما فى السموت وما فى الارض جميعا منه ان فى ذلك لءايت لقوم يتفكرون');
INSERT INTO "uthmani_simple" VALUES(45,14,'45:14','قل للذين ءامنوا۟ يغفروا۟ للذين لا يرجون ايام الله ليجزى قومۢا بما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(45,15,'45:15','من عمل صلحا فلنفسه ومن اساء فعليها ثم الى ربكم ترجعون');
INSERT INTO "uthmani_simple" VALUES(45,16,'45:16','ولقد ءاتينا بنى اسرءيل الكتب والحكم والنبوة ورزقنهم من الطيبت وفضلنهم على العلمين');
INSERT INTO "uthmani_simple" VALUES(45,17,'45:17','وءاتينهم بينت من الامر فما اختلفوا۟ الا منۢ بعد ما جاءهم العلم بغيۢا بينهم ان ربك يقضى بينهم يوم القيمة فيما كانوا۟ فيه يختلفون');
INSERT INTO "uthmani_simple" VALUES(45,18,'45:18','ثم جعلنك على شريعة من الامر فاتبعها ولا تتبع اهواء الذين لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(45,19,'45:19','انهم لن يغنوا۟ عنك من الله شيـا وان الظلمين بعضهم اولياء بعض والله ولى المتقين');
INSERT INTO "uthmani_simple" VALUES(45,20,'45:20','هذا بصئر للناس وهدى ورحمة لقوم يوقنون');
INSERT INTO "uthmani_simple" VALUES(45,21,'45:21','ام حسب الذين اجترحوا۟ السيـات ان نجعلهم كالذين ءامنوا۟ وعملوا۟ الصلحت سواء محياهم ومماتهم ساء ما يحكمون');
INSERT INTO "uthmani_simple" VALUES(45,22,'45:22','وخلق الله السموت والارض بالحق ولتجزى كل نفسۭ بما كسبت وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(45,23,'45:23','افرءيت من اتخذ الهه هوىه واضله الله على علم وختم على سمعه وقلبه وجعل على بصره غشوة فمن يهديه منۢ بعد الله افلا تذكرون');
INSERT INTO "uthmani_simple" VALUES(45,24,'45:24','وقالوا۟ ما هى الا حياتنا الدنيا نموت ونحيا وما يهلكنا الا الدهر وما لهم بذلك من علم ان هم الا يظنون');
INSERT INTO "uthmani_simple" VALUES(45,25,'45:25','واذا تتلى عليهم ءايتنا بينت ما كان حجتهم الا ان قالوا۟ ائتوا۟ بـابائنا ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(45,26,'45:26','قل الله يحييكم ثم يميتكم ثم يجمعكم الى يوم القيمة لا ريب فيه ولكن اكثر الناس لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(45,27,'45:27','ولله ملك السموت والارض ويوم تقوم الساعة يومئذ يخسر المبطلون');
INSERT INTO "uthmani_simple" VALUES(45,28,'45:28','وترى كل امة جاثية كل امة تدعى الى كتبها اليوم تجزون ما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(45,29,'45:29','هذا كتبنا ينطق عليكم بالحق انا كنا نستنسخ ما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(45,30,'45:30','فاما الذين ءامنوا۟ وعملوا۟ الصلحت فيدخلهم ربهم فى رحمته ذلك هو الفوز المبين');
INSERT INTO "uthmani_simple" VALUES(45,31,'45:31','واما الذين كفروا۟ افلم تكن ءايتى تتلى عليكم فاستكبرتم وكنتم قوما مجرمين');
INSERT INTO "uthmani_simple" VALUES(45,32,'45:32','واذا قيل ان وعد الله حق والساعة لا ريب فيها قلتم ما ندرى ما الساعة ان نظن الا ظنا وما نحن بمستيقنين');
INSERT INTO "uthmani_simple" VALUES(45,33,'45:33','وبدا لهم سيـات ما عملوا۟ وحاق بهم ما كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(45,34,'45:34','وقيل اليوم ننسىكم كما نسيتم لقاء يومكم هذا وماوىكم النار وما لكم من نصرين');
INSERT INTO "uthmani_simple" VALUES(45,35,'45:35','ذلكم بانكم اتخذتم ءايت الله هزوا وغرتكم الحيوة الدنيا فاليوم لا يخرجون منها ولا هم يستعتبون');
INSERT INTO "uthmani_simple" VALUES(45,36,'45:36','فلله الحمد رب السموت ورب الارض رب العلمين');
INSERT INTO "uthmani_simple" VALUES(45,37,'45:37','وله الكبرياء فى السموت والارض وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(46,1,'46:1','حم');
INSERT INTO "uthmani_simple" VALUES(46,2,'46:2','تنزيل الكتب من الله العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(46,3,'46:3','ما خلقنا السموت والارض وما بينهما الا بالحق واجل مسمى والذين كفروا۟ عما انذروا۟ معرضون');
INSERT INTO "uthmani_simple" VALUES(46,4,'46:4','قل ارءيتم ما تدعون من دون الله ارونى ماذا خلقوا۟ من الارض ام لهم شرك فى السموت ائتونى بكتب من قبل هذا او اثرة من علم ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(46,5,'46:5','ومن اضل ممن يدعوا۟ من دون الله من لا يستجيب له الى يوم القيمة وهم عن دعائهم غفلون');
INSERT INTO "uthmani_simple" VALUES(46,6,'46:6','واذا حشر الناس كانوا۟ لهم اعداء وكانوا۟ بعبادتهم كفرين');
INSERT INTO "uthmani_simple" VALUES(46,7,'46:7','واذا تتلى عليهم ءايتنا بينت قال الذين كفروا۟ للحق لما جاءهم هذا سحر مبين');
INSERT INTO "uthmani_simple" VALUES(46,8,'46:8','ام يقولون افترىه قل ان افتريته فلا تملكون لى من الله شيـا هو اعلم بما تفيضون فيه كفى به شهيدۢا بينى وبينكم وهو الغفور الرحيم');
INSERT INTO "uthmani_simple" VALUES(46,9,'46:9','قل ما كنت بدعا من الرسل وما ادرى ما يفعل بى ولا بكم ان اتبع الا ما يوحى الى وما انا۠ الا نذير مبين');
INSERT INTO "uthmani_simple" VALUES(46,10,'46:10','قل ارءيتم ان كان من عند الله وكفرتم به وشهد شاهد منۢ بنى اسرءيل على مثله فـامن واستكبرتم ان الله لا يهدى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(46,11,'46:11','وقال الذين كفروا۟ للذين ءامنوا۟ لو كان خيرا ما سبقونا اليه واذ لم يهتدوا۟ به فسيقولون هذا افك قديم');
INSERT INTO "uthmani_simple" VALUES(46,12,'46:12','ومن قبله كتب موسى اماما ورحمة وهذا كتب مصدق لسانا عربيا لينذر الذين ظلموا۟ وبشرى للمحسنين');
INSERT INTO "uthmani_simple" VALUES(46,13,'46:13','ان الذين قالوا۟ ربنا الله ثم استقموا۟ فلا خوف عليهم ولا هم يحزنون');
INSERT INTO "uthmani_simple" VALUES(46,14,'46:14','او۟لئك اصحب الجنة خلدين فيها جزاءۢ بما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(46,15,'46:15','ووصينا الانسن بولديه احسنا حملته امه كرها ووضعته كرها وحمله وفصله ثلثون شهرا حتى اذا بلغ اشده وبلغ اربعين سنة قال رب اوزعنى ان اشكر نعمتك التى انعمت على وعلى ولدى وان اعمل صلحا ترضىه واصلح لى فى ذريتى انى تبت اليك وانى من المسلمين');
INSERT INTO "uthmani_simple" VALUES(46,16,'46:16','او۟لئك الذين نتقبل عنهم احسن ما عملوا۟ ونتجاوز عن سيـاتهم فى اصحب الجنة وعد الصدق الذى كانوا۟ يوعدون');
INSERT INTO "uthmani_simple" VALUES(46,17,'46:17','والذى قال لولديه اف لكما اتعداننى ان اخرج وقد خلت القرون من قبلى وهما يستغيثان الله ويلك ءامن ان وعد الله حق فيقول ما هذا الا اسطير الاولين');
INSERT INTO "uthmani_simple" VALUES(46,18,'46:18','او۟لئك الذين حق عليهم القول فى امم قد خلت من قبلهم من الجن والانس انهم كانوا۟ خسرين');
INSERT INTO "uthmani_simple" VALUES(46,19,'46:19','ولكل درجت مما عملوا۟ وليوفيهم اعملهم وهم لا يظلمون');
INSERT INTO "uthmani_simple" VALUES(46,20,'46:20','ويوم يعرض الذين كفروا۟ على النار اذهبتم طيبتكم فى حياتكم الدنيا واستمتعتم بها فاليوم تجزون عذاب الهون بما كنتم تستكبرون فى الارض بغير الحق وبما كنتم تفسقون');
INSERT INTO "uthmani_simple" VALUES(46,21,'46:21','واذكر اخا عاد اذ انذر قومه بالاحقاف وقد خلت النذر منۢ بين يديه ومن خلفه الا تعبدوا۟ الا الله انى اخاف عليكم عذاب يوم عظيم');
INSERT INTO "uthmani_simple" VALUES(46,22,'46:22','قالوا۟ اجئتنا لتافكنا عن ءالهتنا فاتنا بما تعدنا ان كنت من الصدقين');
INSERT INTO "uthmani_simple" VALUES(46,23,'46:23','قال انما العلم عند الله وابلغكم ما ارسلت به ولكنى ارىكم قوما تجهلون');
INSERT INTO "uthmani_simple" VALUES(46,24,'46:24','فلما راوه عارضا مستقبل اوديتهم قالوا۟ هذا عارض ممطرنا بل هو ما استعجلتم به ريح فيها عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(46,25,'46:25','تدمر كل شىءۭ بامر ربها فاصبحوا۟ لا يرى الا مسكنهم كذلك نجزى القوم المجرمين');
INSERT INTO "uthmani_simple" VALUES(46,26,'46:26','ولقد مكنهم فيما ان مكنكم فيه وجعلنا لهم سمعا وابصرا وافـدة فما اغنى عنهم سمعهم ولا ابصرهم ولا افـدتهم من شىء اذ كانوا۟ يجحدون بـايت الله وحاق بهم ما كانوا۟ به يستهزءون');
INSERT INTO "uthmani_simple" VALUES(46,27,'46:27','ولقد اهلكنا ما حولكم من القرى وصرفنا الءايت لعلهم يرجعون');
INSERT INTO "uthmani_simple" VALUES(46,28,'46:28','فلولا نصرهم الذين اتخذوا۟ من دون الله قربانا ءالهةۢ بل ضلوا۟ عنهم وذلك افكهم وما كانوا۟ يفترون');
INSERT INTO "uthmani_simple" VALUES(46,29,'46:29','واذ صرفنا اليك نفرا من الجن يستمعون القرءان فلما حضروه قالوا۟ انصتوا۟ فلما قضى ولوا۟ الى قومهم منذرين');
INSERT INTO "uthmani_simple" VALUES(46,30,'46:30','قالوا۟ يقومنا انا سمعنا كتبا انزل منۢ بعد موسى مصدقا لما بين يديه يهدى الى الحق والى طريق مستقيم');
INSERT INTO "uthmani_simple" VALUES(46,31,'46:31','يقومنا اجيبوا۟ داعى الله وءامنوا۟ به يغفر لكم من ذنوبكم ويجركم من عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(46,32,'46:32','ومن لا يجب داعى الله فليس بمعجز فى الارض وليس له من دونه اولياء او۟لئك فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(46,33,'46:33','اولم يروا۟ ان الله الذى خلق السموت والارض ولم يعى بخلقهن بقدر على ان يحى الموتى بلى انه على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(46,34,'46:34','ويوم يعرض الذين كفروا۟ على النار اليس هذا بالحق قالوا۟ بلى وربنا قال فذوقوا۟ العذاب بما كنتم تكفرون');
INSERT INTO "uthmani_simple" VALUES(46,35,'46:35','فاصبر كما صبر او۟لوا۟ العزم من الرسل ولا تستعجل لهم كانهم يوم يرون ما يوعدون لم يلبثوا۟ الا ساعة من نهارۭ بلغ فهل يهلك الا القوم الفسقون');
INSERT INTO "uthmani_simple" VALUES(47,1,'47:1','الذين كفروا۟ وصدوا۟ عن سبيل الله اضل اعملهم');
INSERT INTO "uthmani_simple" VALUES(47,2,'47:2','والذين ءامنوا۟ وعملوا۟ الصلحت وءامنوا۟ بما نزل على محمد وهو الحق من ربهم كفر عنهم سيـاتهم واصلح بالهم');
INSERT INTO "uthmani_simple" VALUES(47,3,'47:3','ذلك بان الذين كفروا۟ اتبعوا۟ البطل وان الذين ءامنوا۟ اتبعوا۟ الحق من ربهم كذلك يضرب الله للناس امثلهم');
INSERT INTO "uthmani_simple" VALUES(47,4,'47:4','فاذا لقيتم الذين كفروا۟ فضرب الرقاب حتى اذا اثخنتموهم فشدوا۟ الوثاق فاما منۢا بعد واما فداء حتى تضع الحرب اوزارها ذلك ولو يشاء الله لانتصر منهم ولكن ليبلوا۟ بعضكم ببعض والذين قتلوا۟ فى سبيل الله فلن يضل اعملهم');
INSERT INTO "uthmani_simple" VALUES(47,5,'47:5','سيهديهم ويصلح بالهم');
INSERT INTO "uthmani_simple" VALUES(47,6,'47:6','ويدخلهم الجنة عرفها لهم');
INSERT INTO "uthmani_simple" VALUES(47,7,'47:7','يايها الذين ءامنوا۟ ان تنصروا۟ الله ينصركم ويثبت اقدامكم');
INSERT INTO "uthmani_simple" VALUES(47,8,'47:8','والذين كفروا۟ فتعسا لهم واضل اعملهم');
INSERT INTO "uthmani_simple" VALUES(47,9,'47:9','ذلك بانهم كرهوا۟ ما انزل الله فاحبط اعملهم');
INSERT INTO "uthmani_simple" VALUES(47,10,'47:10','افلم يسيروا۟ فى الارض فينظروا۟ كيف كان عقبة الذين من قبلهم دمر الله عليهم وللكفرين امثلها');
INSERT INTO "uthmani_simple" VALUES(47,11,'47:11','ذلك بان الله مولى الذين ءامنوا۟ وان الكفرين لا مولى لهم');
INSERT INTO "uthmani_simple" VALUES(47,12,'47:12','ان الله يدخل الذين ءامنوا۟ وعملوا۟ الصلحت جنت تجرى من تحتها الانهر والذين كفروا۟ يتمتعون وياكلون كما تاكل الانعم والنار مثوى لهم');
INSERT INTO "uthmani_simple" VALUES(47,13,'47:13','وكاين من قرية هى اشد قوة من قريتك التى اخرجتك اهلكنهم فلا ناصر لهم');
INSERT INTO "uthmani_simple" VALUES(47,14,'47:14','افمن كان على بينة من ربه كمن زين له سوء عمله واتبعوا۟ اهواءهم');
INSERT INTO "uthmani_simple" VALUES(47,15,'47:15','مثل الجنة التى وعد المتقون فيها انهر من ماء غير ءاسن وانهر من لبن لم يتغير طعمه وانهر من خمر لذة للشربين وانهر من عسل مصفى ولهم فيها من كل الثمرت ومغفرة من ربهم كمن هو خلد فى النار وسقوا۟ ماء حميما فقطع امعاءهم');
INSERT INTO "uthmani_simple" VALUES(47,16,'47:16','ومنهم من يستمع اليك حتى اذا خرجوا۟ من عندك قالوا۟ للذين اوتوا۟ العلم ماذا قال ءانفا او۟لئك الذين طبع الله على قلوبهم واتبعوا۟ اهواءهم');
INSERT INTO "uthmani_simple" VALUES(47,17,'47:17','والذين اهتدوا۟ زادهم هدى وءاتىهم تقوىهم');
INSERT INTO "uthmani_simple" VALUES(47,18,'47:18','فهل ينظرون الا الساعة ان تاتيهم بغتة فقد جاء اشراطها فانى لهم اذا جاءتهم ذكرىهم');
INSERT INTO "uthmani_simple" VALUES(47,19,'47:19','فاعلم انه لا اله الا الله واستغفر لذنۢبك وللمؤمنين والمؤمنت والله يعلم متقلبكم ومثوىكم');
INSERT INTO "uthmani_simple" VALUES(47,20,'47:20','ويقول الذين ءامنوا۟ لولا نزلت سورة فاذا انزلت سورة محكمة وذكر فيها القتال رايت الذين فى قلوبهم مرض ينظرون اليك نظر المغشى عليه من الموت فاولى لهم');
INSERT INTO "uthmani_simple" VALUES(47,21,'47:21','طاعة وقول معروف فاذا عزم الامر فلو صدقوا۟ الله لكان خيرا لهم');
INSERT INTO "uthmani_simple" VALUES(47,22,'47:22','فهل عسيتم ان توليتم ان تفسدوا۟ فى الارض وتقطعوا۟ ارحامكم');
INSERT INTO "uthmani_simple" VALUES(47,23,'47:23','او۟لئك الذين لعنهم الله فاصمهم واعمى ابصرهم');
INSERT INTO "uthmani_simple" VALUES(47,24,'47:24','افلا يتدبرون القرءان ام على قلوب اقفالها');
INSERT INTO "uthmani_simple" VALUES(47,25,'47:25','ان الذين ارتدوا۟ على ادبرهم منۢ بعد ما تبين لهم الهدى الشيطن سول لهم واملى لهم');
INSERT INTO "uthmani_simple" VALUES(47,26,'47:26','ذلك بانهم قالوا۟ للذين كرهوا۟ ما نزل الله سنطيعكم فى بعض الامر والله يعلم اسرارهم');
INSERT INTO "uthmani_simple" VALUES(47,27,'47:27','فكيف اذا توفتهم الملئكة يضربون وجوههم وادبرهم');
INSERT INTO "uthmani_simple" VALUES(47,28,'47:28','ذلك بانهم اتبعوا۟ ما اسخط الله وكرهوا۟ رضونه فاحبط اعملهم');
INSERT INTO "uthmani_simple" VALUES(47,29,'47:29','ام حسب الذين فى قلوبهم مرض ان لن يخرج الله اضغنهم');
INSERT INTO "uthmani_simple" VALUES(47,30,'47:30','ولو نشاء لارينكهم فلعرفتهم بسيمهم ولتعرفنهم فى لحن القول والله يعلم اعملكم');
INSERT INTO "uthmani_simple" VALUES(47,31,'47:31','ولنبلونكم حتى نعلم المجهدين منكم والصبرين ونبلوا۟ اخباركم');
INSERT INTO "uthmani_simple" VALUES(47,32,'47:32','ان الذين كفروا۟ وصدوا۟ عن سبيل الله وشاقوا۟ الرسول منۢ بعد ما تبين لهم الهدى لن يضروا۟ الله شيـا وسيحبط اعملهم');
INSERT INTO "uthmani_simple" VALUES(47,33,'47:33','يايها الذين ءامنوا۟ اطيعوا۟ الله واطيعوا۟ الرسول ولا تبطلوا۟ اعملكم');
INSERT INTO "uthmani_simple" VALUES(47,34,'47:34','ان الذين كفروا۟ وصدوا۟ عن سبيل الله ثم ماتوا۟ وهم كفار فلن يغفر الله لهم');
INSERT INTO "uthmani_simple" VALUES(47,35,'47:35','فلا تهنوا۟ وتدعوا۟ الى السلم وانتم الاعلون والله معكم ولن يتركم اعملكم');
INSERT INTO "uthmani_simple" VALUES(47,36,'47:36','انما الحيوة الدنيا لعب ولهو وان تؤمنوا۟ وتتقوا۟ يؤتكم اجوركم ولا يسـلكم امولكم');
INSERT INTO "uthmani_simple" VALUES(47,37,'47:37','ان يسـلكموها فيحفكم تبخلوا۟ ويخرج اضغنكم');
INSERT INTO "uthmani_simple" VALUES(47,38,'47:38','هانتم هؤلاء تدعون لتنفقوا۟ فى سبيل الله فمنكم من يبخل ومن يبخل فانما يبخل عن نفسه والله الغنى وانتم الفقراء وان تتولوا۟ يستبدل قوما غيركم ثم لا يكونوا۟ امثلكم');
INSERT INTO "uthmani_simple" VALUES(48,1,'48:1','انا فتحنا لك فتحا مبينا');
INSERT INTO "uthmani_simple" VALUES(48,2,'48:2','ليغفر لك الله ما تقدم من ذنۢبك وما تاخر ويتم نعمته عليك ويهديك صرطا مستقيما');
INSERT INTO "uthmani_simple" VALUES(48,3,'48:3','وينصرك الله نصرا عزيزا');
INSERT INTO "uthmani_simple" VALUES(48,4,'48:4','هو الذى انزل السكينة فى قلوب المؤمنين ليزدادوا۟ ايمنا مع ايمنهم ولله جنود السموت والارض وكان الله عليما حكيما');
INSERT INTO "uthmani_simple" VALUES(48,5,'48:5','ليدخل المؤمنين والمؤمنت جنت تجرى من تحتها الانهر خلدين فيها ويكفر عنهم سيـاتهم وكان ذلك عند الله فوزا عظيما');
INSERT INTO "uthmani_simple" VALUES(48,6,'48:6','ويعذب المنفقين والمنفقت والمشركين والمشركت الظانين بالله ظن السوء عليهم دائرة السوء وغضب الله عليهم ولعنهم واعد لهم جهنم وساءت مصيرا');
INSERT INTO "uthmani_simple" VALUES(48,7,'48:7','ولله جنود السموت والارض وكان الله عزيزا حكيما');
INSERT INTO "uthmani_simple" VALUES(48,8,'48:8','انا ارسلنك شهدا ومبشرا ونذيرا');
INSERT INTO "uthmani_simple" VALUES(48,9,'48:9','لتؤمنوا۟ بالله ورسوله وتعزروه وتوقروه وتسبحوه بكرة واصيلا');
INSERT INTO "uthmani_simple" VALUES(48,10,'48:10','ان الذين يبايعونك انما يبايعون الله يد الله فوق ايديهم فمن نكث فانما ينكث على نفسه ومن اوفى بما عهد عليه الله فسيؤتيه اجرا عظيما');
INSERT INTO "uthmani_simple" VALUES(48,11,'48:11','سيقول لك المخلفون من الاعراب شغلتنا امولنا واهلونا فاستغفر لنا يقولون بالسنتهم ما ليس فى قلوبهم قل فمن يملك لكم من الله شيـا ان اراد بكم ضرا او اراد بكم نفعۢا بل كان الله بما تعملون خبيرۢا');
INSERT INTO "uthmani_simple" VALUES(48,12,'48:12','بل ظننتم ان لن ينقلب الرسول والمؤمنون الى اهليهم ابدا وزين ذلك فى قلوبكم وظننتم ظن السوء وكنتم قومۢا بورا');
INSERT INTO "uthmani_simple" VALUES(48,13,'48:13','ومن لم يؤمنۢ بالله ورسوله فانا اعتدنا للكفرين سعيرا');
INSERT INTO "uthmani_simple" VALUES(48,14,'48:14','ولله ملك السموت والارض يغفر لمن يشاء ويعذب من يشاء وكان الله غفورا رحيما');
INSERT INTO "uthmani_simple" VALUES(48,15,'48:15','سيقول المخلفون اذا انطلقتم الى مغانم لتاخذوها ذرونا نتبعكم يريدون ان يبدلوا۟ كلم الله قل لن تتبعونا كذلكم قال الله من قبل فسيقولون بل تحسدوننا بل كانوا۟ لا يفقهون الا قليلا');
INSERT INTO "uthmani_simple" VALUES(48,16,'48:16','قل للمخلفين من الاعراب ستدعون الى قوم او۟لى باس شديد تقتلونهم او يسلمون فان تطيعوا۟ يؤتكم الله اجرا حسنا وان تتولوا۟ كما توليتم من قبل يعذبكم عذابا اليما');
INSERT INTO "uthmani_simple" VALUES(48,17,'48:17','ليس على الاعمى حرج ولا على الاعرج حرج ولا على المريض حرج ومن يطع الله ورسوله يدخله جنت تجرى من تحتها الانهر ومن يتول يعذبه عذابا اليما');
INSERT INTO "uthmani_simple" VALUES(48,18,'48:18','لقد رضى الله عن المؤمنين اذ يبايعونك تحت الشجرة فعلم ما فى قلوبهم فانزل السكينة عليهم واثبهم فتحا قريبا');
INSERT INTO "uthmani_simple" VALUES(48,19,'48:19','ومغانم كثيرة ياخذونها وكان الله عزيزا حكيما');
INSERT INTO "uthmani_simple" VALUES(48,20,'48:20','وعدكم الله مغانم كثيرة تاخذونها فعجل لكم هذه وكف ايدى الناس عنكم ولتكون ءاية للمؤمنين ويهديكم صرطا مستقيما');
INSERT INTO "uthmani_simple" VALUES(48,21,'48:21','واخرى لم تقدروا۟ عليها قد احاط الله بها وكان الله على كل شىء قديرا');
INSERT INTO "uthmani_simple" VALUES(48,22,'48:22','ولو قتلكم الذين كفروا۟ لولوا۟ الادبر ثم لا يجدون وليا ولا نصيرا');
INSERT INTO "uthmani_simple" VALUES(48,23,'48:23','سنة الله التى قد خلت من قبل ولن تجد لسنة الله تبديلا');
INSERT INTO "uthmani_simple" VALUES(48,24,'48:24','وهو الذى كف ايديهم عنكم وايديكم عنهم ببطن مكة منۢ بعد ان اظفركم عليهم وكان الله بما تعملون بصيرا');
INSERT INTO "uthmani_simple" VALUES(48,25,'48:25','هم الذين كفروا۟ وصدوكم عن المسجد الحرام والهدى معكوفا ان يبلغ محله ولولا رجال مؤمنون ونساء مؤمنت لم تعلموهم ان تطـوهم فتصيبكم منهم معرةۢ بغير علم ليدخل الله فى رحمته من يشاء لو تزيلوا۟ لعذبنا الذين كفروا۟ منهم عذابا اليما');
INSERT INTO "uthmani_simple" VALUES(48,26,'48:26','اذ جعل الذين كفروا۟ فى قلوبهم الحمية حمية الجهلية فانزل الله سكينته على رسوله وعلى المؤمنين والزمهم كلمة التقوى وكانوا۟ احق بها واهلها وكان الله بكل شىء عليما');
INSERT INTO "uthmani_simple" VALUES(48,27,'48:27','لقد صدق الله رسوله الرءيا بالحق لتدخلن المسجد الحرام ان شاء الله ءامنين محلقين رءوسكم ومقصرين لا تخافون فعلم ما لم تعلموا۟ فجعل من دون ذلك فتحا قريبا');
INSERT INTO "uthmani_simple" VALUES(48,28,'48:28','هو الذى ارسل رسوله بالهدى ودين الحق ليظهره على الدين كله وكفى بالله شهيدا');
INSERT INTO "uthmani_simple" VALUES(48,29,'48:29','محمد رسول الله والذين معه اشداء على الكفار رحماء بينهم ترىهم ركعا سجدا يبتغون فضلا من الله ورضونا سيماهم فى وجوههم من اثر السجود ذلك مثلهم فى التورىة ومثلهم فى الانجيل كزرع اخرج شطـه فـازره فاستغلظ فاستوى على سوقه يعجب الزراع ليغيظ بهم الكفار وعد الله الذين ءامنوا۟ وعملوا۟ الصلحت منهم مغفرة واجرا عظيمۢا');
INSERT INTO "uthmani_simple" VALUES(49,1,'49:1','يايها الذين ءامنوا۟ لا تقدموا۟ بين يدى الله ورسوله واتقوا۟ الله ان الله سميع عليم');
INSERT INTO "uthmani_simple" VALUES(49,2,'49:2','يايها الذين ءامنوا۟ لا ترفعوا۟ اصوتكم فوق صوت النبى ولا تجهروا۟ له بالقول كجهر بعضكم لبعض ان تحبط اعملكم وانتم لا تشعرون');
INSERT INTO "uthmani_simple" VALUES(49,3,'49:3','ان الذين يغضون اصوتهم عند رسول الله او۟لئك الذين امتحن الله قلوبهم للتقوى لهم مغفرة واجر عظيم');
INSERT INTO "uthmani_simple" VALUES(49,4,'49:4','ان الذين ينادونك من وراء الحجرت اكثرهم لا يعقلون');
INSERT INTO "uthmani_simple" VALUES(49,5,'49:5','ولو انهم صبروا۟ حتى تخرج اليهم لكان خيرا لهم والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(49,6,'49:6','يايها الذين ءامنوا۟ ان جاءكم فاسقۢ بنبا فتبينوا۟ ان تصيبوا۟ قومۢا بجهلة فتصبحوا۟ على ما فعلتم ندمين');
INSERT INTO "uthmani_simple" VALUES(49,7,'49:7','واعلموا۟ ان فيكم رسول الله لو يطيعكم فى كثير من الامر لعنتم ولكن الله حبب اليكم الايمن وزينه فى قلوبكم وكره اليكم الكفر والفسوق والعصيان او۟لئك هم الرشدون');
INSERT INTO "uthmani_simple" VALUES(49,8,'49:8','فضلا من الله ونعمة والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(49,9,'49:9','وان طائفتان من المؤمنين اقتتلوا۟ فاصلحوا۟ بينهما فانۢ بغت احدىهما على الاخرى فقتلوا۟ التى تبغى حتى تفىء الى امر الله فان فاءت فاصلحوا۟ بينهما بالعدل واقسطوا۟ ان الله يحب المقسطين');
INSERT INTO "uthmani_simple" VALUES(49,10,'49:10','انما المؤمنون اخوة فاصلحوا۟ بين اخويكم واتقوا۟ الله لعلكم ترحمون');
INSERT INTO "uthmani_simple" VALUES(49,11,'49:11','يايها الذين ءامنوا۟ لا يسخر قوم من قوم عسى ان يكونوا۟ خيرا منهم ولا نساء من نساء عسى ان يكن خيرا منهن ولا تلمزوا۟ انفسكم ولا تنابزوا۟ بالالقب بئس الاسم الفسوق بعد الايمن ومن لم يتب فاو۟لئك هم الظلمون');
INSERT INTO "uthmani_simple" VALUES(49,12,'49:12','يايها الذين ءامنوا۟ اجتنبوا۟ كثيرا من الظن ان بعض الظن اثم ولا تجسسوا۟ ولا يغتب بعضكم بعضا ايحب احدكم ان ياكل لحم اخيه ميتا فكرهتموه واتقوا۟ الله ان الله تواب رحيم');
INSERT INTO "uthmani_simple" VALUES(49,13,'49:13','يايها الناس انا خلقنكم من ذكر وانثى وجعلنكم شعوبا وقبائل لتعارفوا۟ ان اكرمكم عند الله اتقىكم ان الله عليم خبير');
INSERT INTO "uthmani_simple" VALUES(49,14,'49:14','قالت الاعراب ءامنا قل لم تؤمنوا۟ ولكن قولوا۟ اسلمنا ولما يدخل الايمن فى قلوبكم وان تطيعوا۟ الله ورسوله لا يلتكم من اعملكم شيـا ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(49,15,'49:15','انما المؤمنون الذين ءامنوا۟ بالله ورسوله ثم لم يرتابوا۟ وجهدوا۟ بامولهم وانفسهم فى سبيل الله او۟لئك هم الصدقون');
INSERT INTO "uthmani_simple" VALUES(49,16,'49:16','قل اتعلمون الله بدينكم والله يعلم ما فى السموت وما فى الارض والله بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(49,17,'49:17','يمنون عليك ان اسلموا۟ قل لا تمنوا۟ على اسلمكم بل الله يمن عليكم ان هدىكم للايمن ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(49,18,'49:18','ان الله يعلم غيب السموت والارض والله بصيرۢ بما تعملون');
INSERT INTO "uthmani_simple" VALUES(50,1,'50:1','ق والقرءان المجيد');
INSERT INTO "uthmani_simple" VALUES(50,2,'50:2','بل عجبوا۟ ان جاءهم منذر منهم فقال الكفرون هذا شىء عجيب');
INSERT INTO "uthmani_simple" VALUES(50,3,'50:3','اءذا متنا وكنا ترابا ذلك رجعۢ بعيد');
INSERT INTO "uthmani_simple" VALUES(50,4,'50:4','قد علمنا ما تنقص الارض منهم وعندنا كتب حفيظۢ');
INSERT INTO "uthmani_simple" VALUES(50,5,'50:5','بل كذبوا۟ بالحق لما جاءهم فهم فى امر مريج');
INSERT INTO "uthmani_simple" VALUES(50,6,'50:6','افلم ينظروا۟ الى السماء فوقهم كيف بنينها وزينها وما لها من فروج');
INSERT INTO "uthmani_simple" VALUES(50,7,'50:7','والارض مددنها والقينا فيها روسى وانۢبتنا فيها من كل زوجۭ بهيج');
INSERT INTO "uthmani_simple" VALUES(50,8,'50:8','تبصرة وذكرى لكل عبد منيب');
INSERT INTO "uthmani_simple" VALUES(50,9,'50:9','ونزلنا من السماء ماء مبركا فانۢبتنا به جنت وحب الحصيد');
INSERT INTO "uthmani_simple" VALUES(50,10,'50:10','والنخل باسقت لها طلع نضيد');
INSERT INTO "uthmani_simple" VALUES(50,11,'50:11','رزقا للعباد واحيينا به بلدة ميتا كذلك الخروج');
INSERT INTO "uthmani_simple" VALUES(50,12,'50:12','كذبت قبلهم قوم نوح واصحب الرس وثمود');
INSERT INTO "uthmani_simple" VALUES(50,13,'50:13','وعاد وفرعون واخون لوط');
INSERT INTO "uthmani_simple" VALUES(50,14,'50:14','واصحب الايكة وقوم تبع كل كذب الرسل فحق وعيد');
INSERT INTO "uthmani_simple" VALUES(50,15,'50:15','افعيينا بالخلق الاول بل هم فى لبس من خلق جديد');
INSERT INTO "uthmani_simple" VALUES(50,16,'50:16','ولقد خلقنا الانسن ونعلم ما توسوس به نفسه ونحن اقرب اليه من حبل الوريد');
INSERT INTO "uthmani_simple" VALUES(50,17,'50:17','اذ يتلقى المتلقيان عن اليمين وعن الشمال قعيد');
INSERT INTO "uthmani_simple" VALUES(50,18,'50:18','ما يلفظ من قول الا لديه رقيب عتيد');
INSERT INTO "uthmani_simple" VALUES(50,19,'50:19','وجاءت سكرة الموت بالحق ذلك ما كنت منه تحيد');
INSERT INTO "uthmani_simple" VALUES(50,20,'50:20','ونفخ فى الصور ذلك يوم الوعيد');
INSERT INTO "uthmani_simple" VALUES(50,21,'50:21','وجاءت كل نفس معها سائق وشهيد');
INSERT INTO "uthmani_simple" VALUES(50,22,'50:22','لقد كنت فى غفلة من هذا فكشفنا عنك غطاءك فبصرك اليوم حديد');
INSERT INTO "uthmani_simple" VALUES(50,23,'50:23','وقال قرينه هذا ما لدى عتيد');
INSERT INTO "uthmani_simple" VALUES(50,24,'50:24','القيا فى جهنم كل كفار عنيد');
INSERT INTO "uthmani_simple" VALUES(50,25,'50:25','مناع للخير معتد مريب');
INSERT INTO "uthmani_simple" VALUES(50,26,'50:26','الذى جعل مع الله الها ءاخر فالقياه فى العذاب الشديد');
INSERT INTO "uthmani_simple" VALUES(50,27,'50:27','قال قرينه ربنا ما اطغيته ولكن كان فى ضللۭ بعيد');
INSERT INTO "uthmani_simple" VALUES(50,28,'50:28','قال لا تختصموا۟ لدى وقد قدمت اليكم بالوعيد');
INSERT INTO "uthmani_simple" VALUES(50,29,'50:29','ما يبدل القول لدى وما انا۠ بظلم للعبيد');
INSERT INTO "uthmani_simple" VALUES(50,30,'50:30','يوم نقول لجهنم هل امتلات وتقول هل من مزيد');
INSERT INTO "uthmani_simple" VALUES(50,31,'50:31','وازلفت الجنة للمتقين غير بعيد');
INSERT INTO "uthmani_simple" VALUES(50,32,'50:32','هذا ما توعدون لكل اواب حفيظ');
INSERT INTO "uthmani_simple" VALUES(50,33,'50:33','من خشى الرحمن بالغيب وجاء بقلب منيب');
INSERT INTO "uthmani_simple" VALUES(50,34,'50:34','ادخلوها بسلم ذلك يوم الخلود');
INSERT INTO "uthmani_simple" VALUES(50,35,'50:35','لهم ما يشاءون فيها ولدينا مزيد');
INSERT INTO "uthmani_simple" VALUES(50,36,'50:36','وكم اهلكنا قبلهم من قرن هم اشد منهم بطشا فنقبوا۟ فى البلد هل من محيص');
INSERT INTO "uthmani_simple" VALUES(50,37,'50:37','ان فى ذلك لذكرى لمن كان له قلب او القى السمع وهو شهيد');
INSERT INTO "uthmani_simple" VALUES(50,38,'50:38','ولقد خلقنا السموت والارض وما بينهما فى ستة ايام وما مسنا من لغوب');
INSERT INTO "uthmani_simple" VALUES(50,39,'50:39','فاصبر على ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل الغروب');
INSERT INTO "uthmani_simple" VALUES(50,40,'50:40','ومن اليل فسبحه وادبر السجود');
INSERT INTO "uthmani_simple" VALUES(50,41,'50:41','واستمع يوم يناد المناد من مكان قريب');
INSERT INTO "uthmani_simple" VALUES(50,42,'50:42','يوم يسمعون الصيحة بالحق ذلك يوم الخروج');
INSERT INTO "uthmani_simple" VALUES(50,43,'50:43','انا نحن نحى ونميت والينا المصير');
INSERT INTO "uthmani_simple" VALUES(50,44,'50:44','يوم تشقق الارض عنهم سراعا ذلك حشر علينا يسير');
INSERT INTO "uthmani_simple" VALUES(50,45,'50:45','نحن اعلم بما يقولون وما انت عليهم بجبار فذكر بالقرءان من يخاف وعيد');
INSERT INTO "uthmani_simple" VALUES(51,1,'51:1','والذريت ذروا');
INSERT INTO "uthmani_simple" VALUES(51,2,'51:2','فالحملت وقرا');
INSERT INTO "uthmani_simple" VALUES(51,3,'51:3','فالجريت يسرا');
INSERT INTO "uthmani_simple" VALUES(51,4,'51:4','فالمقسمت امرا');
INSERT INTO "uthmani_simple" VALUES(51,5,'51:5','انما توعدون لصادق');
INSERT INTO "uthmani_simple" VALUES(51,6,'51:6','وان الدين لوقع');
INSERT INTO "uthmani_simple" VALUES(51,7,'51:7','والسماء ذات الحبك');
INSERT INTO "uthmani_simple" VALUES(51,8,'51:8','انكم لفى قول مختلف');
INSERT INTO "uthmani_simple" VALUES(51,9,'51:9','يؤفك عنه من افك');
INSERT INTO "uthmani_simple" VALUES(51,10,'51:10','قتل الخرصون');
INSERT INTO "uthmani_simple" VALUES(51,11,'51:11','الذين هم فى غمرة ساهون');
INSERT INTO "uthmani_simple" VALUES(51,12,'51:12','يسـلون ايان يوم الدين');
INSERT INTO "uthmani_simple" VALUES(51,13,'51:13','يوم هم على النار يفتنون');
INSERT INTO "uthmani_simple" VALUES(51,14,'51:14','ذوقوا۟ فتنتكم هذا الذى كنتم به تستعجلون');
INSERT INTO "uthmani_simple" VALUES(51,15,'51:15','ان المتقين فى جنت وعيون');
INSERT INTO "uthmani_simple" VALUES(51,16,'51:16','ءاخذين ما ءاتىهم ربهم انهم كانوا۟ قبل ذلك محسنين');
INSERT INTO "uthmani_simple" VALUES(51,17,'51:17','كانوا۟ قليلا من اليل ما يهجعون');
INSERT INTO "uthmani_simple" VALUES(51,18,'51:18','وبالاسحار هم يستغفرون');
INSERT INTO "uthmani_simple" VALUES(51,19,'51:19','وفى امولهم حق للسائل والمحروم');
INSERT INTO "uthmani_simple" VALUES(51,20,'51:20','وفى الارض ءايت للموقنين');
INSERT INTO "uthmani_simple" VALUES(51,21,'51:21','وفى انفسكم افلا تبصرون');
INSERT INTO "uthmani_simple" VALUES(51,22,'51:22','وفى السماء رزقكم وما توعدون');
INSERT INTO "uthmani_simple" VALUES(51,23,'51:23','فورب السماء والارض انه لحق مثل ما انكم تنطقون');
INSERT INTO "uthmani_simple" VALUES(51,24,'51:24','هل اتىك حديث ضيف ابرهيم المكرمين');
INSERT INTO "uthmani_simple" VALUES(51,25,'51:25','اذ دخلوا۟ عليه فقالوا۟ سلما قال سلم قوم منكرون');
INSERT INTO "uthmani_simple" VALUES(51,26,'51:26','فراغ الى اهله فجاء بعجل سمين');
INSERT INTO "uthmani_simple" VALUES(51,27,'51:27','فقربه اليهم قال الا تاكلون');
INSERT INTO "uthmani_simple" VALUES(51,28,'51:28','فاوجس منهم خيفة قالوا۟ لا تخف وبشروه بغلم عليم');
INSERT INTO "uthmani_simple" VALUES(51,29,'51:29','فاقبلت امراته فى صرة فصكت وجهها وقالت عجوز عقيم');
INSERT INTO "uthmani_simple" VALUES(51,30,'51:30','قالوا۟ كذلك قال ربك انه هو الحكيم العليم');
INSERT INTO "uthmani_simple" VALUES(51,31,'51:31','قال فما خطبكم ايها المرسلون');
INSERT INTO "uthmani_simple" VALUES(51,32,'51:32','قالوا۟ انا ارسلنا الى قوم مجرمين');
INSERT INTO "uthmani_simple" VALUES(51,33,'51:33','لنرسل عليهم حجارة من طين');
INSERT INTO "uthmani_simple" VALUES(51,34,'51:34','مسومة عند ربك للمسرفين');
INSERT INTO "uthmani_simple" VALUES(51,35,'51:35','فاخرجنا من كان فيها من المؤمنين');
INSERT INTO "uthmani_simple" VALUES(51,36,'51:36','فما وجدنا فيها غير بيت من المسلمين');
INSERT INTO "uthmani_simple" VALUES(51,37,'51:37','وتركنا فيها ءاية للذين يخافون العذاب الاليم');
INSERT INTO "uthmani_simple" VALUES(51,38,'51:38','وفى موسى اذ ارسلنه الى فرعون بسلطن مبين');
INSERT INTO "uthmani_simple" VALUES(51,39,'51:39','فتولى بركنه وقال سحر او مجنون');
INSERT INTO "uthmani_simple" VALUES(51,40,'51:40','فاخذنه وجنوده فنبذنهم فى اليم وهو مليم');
INSERT INTO "uthmani_simple" VALUES(51,41,'51:41','وفى عاد اذ ارسلنا عليهم الريح العقيم');
INSERT INTO "uthmani_simple" VALUES(51,42,'51:42','ما تذر من شىء اتت عليه الا جعلته كالرميم');
INSERT INTO "uthmani_simple" VALUES(51,43,'51:43','وفى ثمود اذ قيل لهم تمتعوا۟ حتى حين');
INSERT INTO "uthmani_simple" VALUES(51,44,'51:44','فعتوا۟ عن امر ربهم فاخذتهم الصعقة وهم ينظرون');
INSERT INTO "uthmani_simple" VALUES(51,45,'51:45','فما استطعوا۟ من قيام وما كانوا۟ منتصرين');
INSERT INTO "uthmani_simple" VALUES(51,46,'51:46','وقوم نوح من قبل انهم كانوا۟ قوما فسقين');
INSERT INTO "uthmani_simple" VALUES(51,47,'51:47','والسماء بنينها بايي۟د وانا لموسعون');
INSERT INTO "uthmani_simple" VALUES(51,48,'51:48','والارض فرشنها فنعم المهدون');
INSERT INTO "uthmani_simple" VALUES(51,49,'51:49','ومن كل شىء خلقنا زوجين لعلكم تذكرون');
INSERT INTO "uthmani_simple" VALUES(51,50,'51:50','ففروا۟ الى الله انى لكم منه نذير مبين');
INSERT INTO "uthmani_simple" VALUES(51,51,'51:51','ولا تجعلوا۟ مع الله الها ءاخر انى لكم منه نذير مبين');
INSERT INTO "uthmani_simple" VALUES(51,52,'51:52','كذلك ما اتى الذين من قبلهم من رسول الا قالوا۟ ساحر او مجنون');
INSERT INTO "uthmani_simple" VALUES(51,53,'51:53','اتواصوا۟ به بل هم قوم طاغون');
INSERT INTO "uthmani_simple" VALUES(51,54,'51:54','فتول عنهم فما انت بملوم');
INSERT INTO "uthmani_simple" VALUES(51,55,'51:55','وذكر فان الذكرى تنفع المؤمنين');
INSERT INTO "uthmani_simple" VALUES(51,56,'51:56','وما خلقت الجن والانس الا ليعبدون');
INSERT INTO "uthmani_simple" VALUES(51,57,'51:57','ما اريد منهم من رزق وما اريد ان يطعمون');
INSERT INTO "uthmani_simple" VALUES(51,58,'51:58','ان الله هو الرزاق ذو القوة المتين');
INSERT INTO "uthmani_simple" VALUES(51,59,'51:59','فان للذين ظلموا۟ ذنوبا مثل ذنوب اصحبهم فلا يستعجلون');
INSERT INTO "uthmani_simple" VALUES(51,60,'51:60','فويل للذين كفروا۟ من يومهم الذى يوعدون');
INSERT INTO "uthmani_simple" VALUES(52,1,'52:1','والطور');
INSERT INTO "uthmani_simple" VALUES(52,2,'52:2','وكتب مسطور');
INSERT INTO "uthmani_simple" VALUES(52,3,'52:3','فى رق منشور');
INSERT INTO "uthmani_simple" VALUES(52,4,'52:4','والبيت المعمور');
INSERT INTO "uthmani_simple" VALUES(52,5,'52:5','والسقف المرفوع');
INSERT INTO "uthmani_simple" VALUES(52,6,'52:6','والبحر المسجور');
INSERT INTO "uthmani_simple" VALUES(52,7,'52:7','ان عذاب ربك لوقع');
INSERT INTO "uthmani_simple" VALUES(52,8,'52:8','ما له من دافع');
INSERT INTO "uthmani_simple" VALUES(52,9,'52:9','يوم تمور السماء مورا');
INSERT INTO "uthmani_simple" VALUES(52,10,'52:10','وتسير الجبال سيرا');
INSERT INTO "uthmani_simple" VALUES(52,11,'52:11','فويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(52,12,'52:12','الذين هم فى خوض يلعبون');
INSERT INTO "uthmani_simple" VALUES(52,13,'52:13','يوم يدعون الى نار جهنم دعا');
INSERT INTO "uthmani_simple" VALUES(52,14,'52:14','هذه النار التى كنتم بها تكذبون');
INSERT INTO "uthmani_simple" VALUES(52,15,'52:15','افسحر هذا ام انتم لا تبصرون');
INSERT INTO "uthmani_simple" VALUES(52,16,'52:16','اصلوها فاصبروا۟ او لا تصبروا۟ سواء عليكم انما تجزون ما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(52,17,'52:17','ان المتقين فى جنت ونعيم');
INSERT INTO "uthmani_simple" VALUES(52,18,'52:18','فكهين بما ءاتىهم ربهم ووقىهم ربهم عذاب الجحيم');
INSERT INTO "uthmani_simple" VALUES(52,19,'52:19','كلوا۟ واشربوا۟ هنيـۢا بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(52,20,'52:20','متكـين على سرر مصفوفة وزوجنهم بحور عين');
INSERT INTO "uthmani_simple" VALUES(52,21,'52:21','والذين ءامنوا۟ واتبعتهم ذريتهم بايمن الحقنا بهم ذريتهم وما التنهم من عملهم من شىء كل امرئۭ بما كسب رهين');
INSERT INTO "uthmani_simple" VALUES(52,22,'52:22','وامددنهم بفكهة ولحم مما يشتهون');
INSERT INTO "uthmani_simple" VALUES(52,23,'52:23','يتنزعون فيها كاسا لا لغو فيها ولا تاثيم');
INSERT INTO "uthmani_simple" VALUES(52,24,'52:24','ويطوف عليهم غلمان لهم كانهم لؤلؤ مكنون');
INSERT INTO "uthmani_simple" VALUES(52,25,'52:25','واقبل بعضهم على بعض يتساءلون');
INSERT INTO "uthmani_simple" VALUES(52,26,'52:26','قالوا۟ انا كنا قبل فى اهلنا مشفقين');
INSERT INTO "uthmani_simple" VALUES(52,27,'52:27','فمن الله علينا ووقىنا عذاب السموم');
INSERT INTO "uthmani_simple" VALUES(52,28,'52:28','انا كنا من قبل ندعوه انه هو البر الرحيم');
INSERT INTO "uthmani_simple" VALUES(52,29,'52:29','فذكر فما انت بنعمت ربك بكاهن ولا مجنون');
INSERT INTO "uthmani_simple" VALUES(52,30,'52:30','ام يقولون شاعر نتربص به ريب المنون');
INSERT INTO "uthmani_simple" VALUES(52,31,'52:31','قل تربصوا۟ فانى معكم من المتربصين');
INSERT INTO "uthmani_simple" VALUES(52,32,'52:32','ام تامرهم احلمهم بهذا ام هم قوم طاغون');
INSERT INTO "uthmani_simple" VALUES(52,33,'52:33','ام يقولون تقوله بل لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(52,34,'52:34','فلياتوا۟ بحديث مثله ان كانوا۟ صدقين');
INSERT INTO "uthmani_simple" VALUES(52,35,'52:35','ام خلقوا۟ من غير شىء ام هم الخلقون');
INSERT INTO "uthmani_simple" VALUES(52,36,'52:36','ام خلقوا۟ السموت والارض بل لا يوقنون');
INSERT INTO "uthmani_simple" VALUES(52,37,'52:37','ام عندهم خزائن ربك ام هم المصۣيطرون');
INSERT INTO "uthmani_simple" VALUES(52,38,'52:38','ام لهم سلم يستمعون فيه فليات مستمعهم بسلطن مبين');
INSERT INTO "uthmani_simple" VALUES(52,39,'52:39','ام له البنت ولكم البنون');
INSERT INTO "uthmani_simple" VALUES(52,40,'52:40','ام تسـلهم اجرا فهم من مغرم مثقلون');
INSERT INTO "uthmani_simple" VALUES(52,41,'52:41','ام عندهم الغيب فهم يكتبون');
INSERT INTO "uthmani_simple" VALUES(52,42,'52:42','ام يريدون كيدا فالذين كفروا۟ هم المكيدون');
INSERT INTO "uthmani_simple" VALUES(52,43,'52:43','ام لهم اله غير الله سبحن الله عما يشركون');
INSERT INTO "uthmani_simple" VALUES(52,44,'52:44','وان يروا۟ كسفا من السماء ساقطا يقولوا۟ سحاب مركوم');
INSERT INTO "uthmani_simple" VALUES(52,45,'52:45','فذرهم حتى يلقوا۟ يومهم الذى فيه يصعقون');
INSERT INTO "uthmani_simple" VALUES(52,46,'52:46','يوم لا يغنى عنهم كيدهم شيـا ولا هم ينصرون');
INSERT INTO "uthmani_simple" VALUES(52,47,'52:47','وان للذين ظلموا۟ عذابا دون ذلك ولكن اكثرهم لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(52,48,'52:48','واصبر لحكم ربك فانك باعيننا وسبح بحمد ربك حين تقوم');
INSERT INTO "uthmani_simple" VALUES(52,49,'52:49','ومن اليل فسبحه وادبر النجوم');
INSERT INTO "uthmani_simple" VALUES(53,1,'53:1','والنجم اذا هوى');
INSERT INTO "uthmani_simple" VALUES(53,2,'53:2','ما ضل صاحبكم وما غوى');
INSERT INTO "uthmani_simple" VALUES(53,3,'53:3','وما ينطق عن الهوى');
INSERT INTO "uthmani_simple" VALUES(53,4,'53:4','ان هو الا وحى يوحى');
INSERT INTO "uthmani_simple" VALUES(53,5,'53:5','علمه شديد القوى');
INSERT INTO "uthmani_simple" VALUES(53,6,'53:6','ذو مرة فاستوى');
INSERT INTO "uthmani_simple" VALUES(53,7,'53:7','وهو بالافق الاعلى');
INSERT INTO "uthmani_simple" VALUES(53,8,'53:8','ثم دنا فتدلى');
INSERT INTO "uthmani_simple" VALUES(53,9,'53:9','فكان قاب قوسين او ادنى');
INSERT INTO "uthmani_simple" VALUES(53,10,'53:10','فاوحى الى عبده ما اوحى');
INSERT INTO "uthmani_simple" VALUES(53,11,'53:11','ما كذب الفؤاد ما راى');
INSERT INTO "uthmani_simple" VALUES(53,12,'53:12','افتمرونه على ما يرى');
INSERT INTO "uthmani_simple" VALUES(53,13,'53:13','ولقد رءاه نزلة اخرى');
INSERT INTO "uthmani_simple" VALUES(53,14,'53:14','عند سدرة المنتهى');
INSERT INTO "uthmani_simple" VALUES(53,15,'53:15','عندها جنة الماوى');
INSERT INTO "uthmani_simple" VALUES(53,16,'53:16','اذ يغشى السدرة ما يغشى');
INSERT INTO "uthmani_simple" VALUES(53,17,'53:17','ما زاغ البصر وما طغى');
INSERT INTO "uthmani_simple" VALUES(53,18,'53:18','لقد راى من ءايت ربه الكبرى');
INSERT INTO "uthmani_simple" VALUES(53,19,'53:19','افرءيتم اللت والعزى');
INSERT INTO "uthmani_simple" VALUES(53,20,'53:20','ومنوة الثالثة الاخرى');
INSERT INTO "uthmani_simple" VALUES(53,21,'53:21','الكم الذكر وله الانثى');
INSERT INTO "uthmani_simple" VALUES(53,22,'53:22','تلك اذا قسمة ضيزى');
INSERT INTO "uthmani_simple" VALUES(53,23,'53:23','ان هى الا اسماء سميتموها انتم وءاباؤكم ما انزل الله بها من سلطن ان يتبعون الا الظن وما تهوى الانفس ولقد جاءهم من ربهم الهدى');
INSERT INTO "uthmani_simple" VALUES(53,24,'53:24','ام للانسن ما تمنى');
INSERT INTO "uthmani_simple" VALUES(53,25,'53:25','فلله الءاخرة والاولى');
INSERT INTO "uthmani_simple" VALUES(53,26,'53:26','وكم من ملك فى السموت لا تغنى شفعتهم شيـا الا منۢ بعد ان ياذن الله لمن يشاء ويرضى');
INSERT INTO "uthmani_simple" VALUES(53,27,'53:27','ان الذين لا يؤمنون بالءاخرة ليسمون الملئكة تسمية الانثى');
INSERT INTO "uthmani_simple" VALUES(53,28,'53:28','وما لهم به من علم ان يتبعون الا الظن وان الظن لا يغنى من الحق شيـا');
INSERT INTO "uthmani_simple" VALUES(53,29,'53:29','فاعرض عن من تولى عن ذكرنا ولم يرد الا الحيوة الدنيا');
INSERT INTO "uthmani_simple" VALUES(53,30,'53:30','ذلك مبلغهم من العلم ان ربك هو اعلم بمن ضل عن سبيله وهو اعلم بمن اهتدى');
INSERT INTO "uthmani_simple" VALUES(53,31,'53:31','ولله ما فى السموت وما فى الارض ليجزى الذين اسـوا۟ بما عملوا۟ ويجزى الذين احسنوا۟ بالحسنى');
INSERT INTO "uthmani_simple" VALUES(53,32,'53:32','الذين يجتنبون كبئر الاثم والفوحش الا اللمم ان ربك وسع المغفرة هو اعلم بكم اذ انشاكم من الارض واذ انتم اجنة فى بطون امهتكم فلا تزكوا۟ انفسكم هو اعلم بمن اتقى');
INSERT INTO "uthmani_simple" VALUES(53,33,'53:33','افرءيت الذى تولى');
INSERT INTO "uthmani_simple" VALUES(53,34,'53:34','واعطى قليلا واكدى');
INSERT INTO "uthmani_simple" VALUES(53,35,'53:35','اعنده علم الغيب فهو يرى');
INSERT INTO "uthmani_simple" VALUES(53,36,'53:36','ام لم ينبا بما فى صحف موسى');
INSERT INTO "uthmani_simple" VALUES(53,37,'53:37','وابرهيم الذى وفى');
INSERT INTO "uthmani_simple" VALUES(53,38,'53:38','الا تزر وازرة وزر اخرى');
INSERT INTO "uthmani_simple" VALUES(53,39,'53:39','وان ليس للانسن الا ما سعى');
INSERT INTO "uthmani_simple" VALUES(53,40,'53:40','وان سعيه سوف يرى');
INSERT INTO "uthmani_simple" VALUES(53,41,'53:41','ثم يجزىه الجزاء الاوفى');
INSERT INTO "uthmani_simple" VALUES(53,42,'53:42','وان الى ربك المنتهى');
INSERT INTO "uthmani_simple" VALUES(53,43,'53:43','وانه هو اضحك وابكى');
INSERT INTO "uthmani_simple" VALUES(53,44,'53:44','وانه هو امات واحيا');
INSERT INTO "uthmani_simple" VALUES(53,45,'53:45','وانه خلق الزوجين الذكر والانثى');
INSERT INTO "uthmani_simple" VALUES(53,46,'53:46','من نطفة اذا تمنى');
INSERT INTO "uthmani_simple" VALUES(53,47,'53:47','وان عليه النشاة الاخرى');
INSERT INTO "uthmani_simple" VALUES(53,48,'53:48','وانه هو اغنى واقنى');
INSERT INTO "uthmani_simple" VALUES(53,49,'53:49','وانه هو رب الشعرى');
INSERT INTO "uthmani_simple" VALUES(53,50,'53:50','وانه اهلك عادا الاولى');
INSERT INTO "uthmani_simple" VALUES(53,51,'53:51','وثمودا۟ فما ابقى');
