INSERT INTO "uthmani_simple" VALUES(53,53,'53:53','والمؤتفكة اهوى');
INSERT INTO "uthmani_simple" VALUES(53,54,'53:54','فغشىها ما غشى');
INSERT INTO "uthmani_simple" VALUES(53,55,'53:55','فباى ءالاء ربك تتمارى');
INSERT INTO "uthmani_simple" VALUES(53,56,'53:56','هذا نذير من النذر الاولى');
INSERT INTO "uthmani_simple" VALUES(53,57,'53:57','ازفت الءازفة');
INSERT INTO "uthmani_simple" VALUES(53,58,'53:58','ليس لها من دون الله كاشفة');
INSERT INTO "uthmani_simple" VALUES(53,59,'53:59','افمن هذا الحديث تعجبون');
INSERT INTO "uthmani_simple" VALUES(53,60,'53:60','وتضحكون ولا تبكون');
INSERT INTO "uthmani_simple" VALUES(53,61,'53:61','وانتم سمدون');
INSERT INTO "uthmani_simple" VALUES(53,62,'53:62','فاسجدوا۟ لله واعبدوا۟');
INSERT INTO "uthmani_simple" VALUES(54,1,'54:1','اقتربت الساعة وانشق القمر');
