INSERT INTO "uthmani_simple" VALUES(54,7,'54:7','خشعا ابصرهم يخرجون من الاجداث كانهم جراد منتشر');
INSERT INTO "uthmani_simple" VALUES(54,8,'54:8','مهطعين الى الداع يقول الكفرون هذا يوم عسر');
INSERT INTO "uthmani_simple" VALUES(54,9,'54:9','كذبت قبلهم قوم نوح فكذبوا۟ عبدنا وقالوا۟ مجنون وازدجر');
INSERT INTO "uthmani_simple" VALUES(54,10,'54:10','فدعا ربه انى مغلوب فانتصر');
INSERT INTO "uthmani_simple" VALUES(54,11,'54:11','ففتحنا ابوب السماء بماء منهمر');
