INSERT INTO "uthmani_simple" VALUES(54,37,'54:37','ولقد رودوه عن ضيفه فطمسنا اعينهم فذوقوا۟ عذابى ونذر');
INSERT INTO "uthmani_simple" VALUES(54,38,'54:38','ولقد صبحهم بكرة عذاب مستقر');
INSERT INTO "uthmani_simple" VALUES(54,39,'54:39','فذوقوا۟ عذابى ونذر');
INSERT INTO "uthmani_simple" VALUES(54,40,'54:40','ولقد يسرنا القرءان للذكر فهل من مدكر');
INSERT INTO "uthmani_simple" VALUES(54,41,'54:41','ولقد جاء ءال فرعون النذر');
INSERT INTO "uthmani_simple" VALUES(54,42,'54:42','كذبوا۟ بـايتنا كلها فاخذنهم اخذ عزيز مقتدر');
