INSERT INTO "uthmani_simple" VALUES(56,8,'56:8','فاصحب الميمنة ما اصحب الميمنة');
INSERT INTO "uthmani_simple" VALUES(56,9,'56:9','واصحب المشـمة ما اصحب المشـمة');
INSERT INTO "uthmani_simple" VALUES(56,10,'56:10','والسبقون السبقون');
INSERT INTO "uthmani_simple" VALUES(56,11,'56:11','او۟لئك المقربون');
INSERT INTO "uthmani_simple" VALUES(56,12,'56:12','فى جنت النعيم');
INSERT INTO "uthmani_simple" VALUES(56,13,'56:13','ثلة من الاولين');
INSERT INTO "uthmani_simple" VALUES(56,14,'56:14','وقليل من الءاخرين');
INSERT INTO "uthmani_simple" VALUES(56,15,'56:15','على سرر موضونة');
INSERT INTO "uthmani_simple" VALUES(56,16,'56:16','متكـين عليها متقبلين');
INSERT INTO "uthmani_simple" VALUES(56,17,'56:17','يطوف عليهم ولدن مخلدون');
INSERT INTO "uthmani_simple" VALUES(56,18,'56:18','باكواب واباريق وكاس من معين');
INSERT INTO "uthmani_simple" VALUES(56,19,'56:19','لا يصدعون عنها ولا ينزفون');
INSERT INTO "uthmani_simple" VALUES(56,20,'56:20','وفكهة مما يتخيرون');
INSERT INTO "uthmani_simple" VALUES(56,21,'56:21','ولحم طير مما يشتهون');
INSERT INTO "uthmani_simple" VALUES(56,22,'56:22','وحور عين');
INSERT INTO "uthmani_simple" VALUES(56,23,'56:23','كامثل اللؤلؤ المكنون');
INSERT INTO "uthmani_simple" VALUES(56,24,'56:24','جزاءۢ بما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(56,25,'56:25','لا يسمعون فيها لغوا ولا تاثيما');
INSERT INTO "uthmani_simple" VALUES(56,26,'56:26','الا قيلا سلما سلما');
INSERT INTO "uthmani_simple" VALUES(56,27,'56:27','واصحب اليمين ما اصحب اليمين');
INSERT INTO "uthmani_simple" VALUES(56,28,'56:28','فى سدر مخضود');
INSERT INTO "uthmani_simple" VALUES(56,29,'56:29','وطلح منضود');
INSERT INTO "uthmani_simple" VALUES(56,30,'56:30','وظل ممدود');
INSERT INTO "uthmani_simple" VALUES(56,31,'56:31','وماء مسكوب');
INSERT INTO "uthmani_simple" VALUES(56,32,'56:32','وفكهة كثيرة');
INSERT INTO "uthmani_simple" VALUES(56,33,'56:33','لا مقطوعة ولا ممنوعة');
INSERT INTO "uthmani_simple" VALUES(56,34,'56:34','وفرش مرفوعة');
INSERT INTO "uthmani_simple" VALUES(56,35,'56:35','انا انشانهن انشاء');
INSERT INTO "uthmani_simple" VALUES(56,36,'56:36','فجعلنهن ابكارا');
INSERT INTO "uthmani_simple" VALUES(56,37,'56:37','عربا اترابا');
INSERT INTO "uthmani_simple" VALUES(56,38,'56:38','لاصحب اليمين');
INSERT INTO "uthmani_simple" VALUES(56,39,'56:39','ثلة من الاولين');
INSERT INTO "uthmani_simple" VALUES(56,40,'56:40','وثلة من الءاخرين');
INSERT INTO "uthmani_simple" VALUES(56,41,'56:41','واصحب الشمال ما اصحب الشمال');
INSERT INTO "uthmani_simple" VALUES(56,42,'56:42','فى سموم وحميم');
INSERT INTO "uthmani_simple" VALUES(56,43,'56:43','وظل من يحموم');
INSERT INTO "uthmani_simple" VALUES(56,44,'56:44','لا بارد ولا كريم');
INSERT INTO "uthmani_simple" VALUES(56,45,'56:45','انهم كانوا۟ قبل ذلك مترفين');
INSERT INTO "uthmani_simple" VALUES(56,46,'56:46','وكانوا۟ يصرون على الحنث العظيم');
INSERT INTO "uthmani_simple" VALUES(56,47,'56:47','وكانوا۟ يقولون ائذا متنا وكنا ترابا وعظما اءنا لمبعوثون');
INSERT INTO "uthmani_simple" VALUES(56,48,'56:48','اوءاباؤنا الاولون');
INSERT INTO "uthmani_simple" VALUES(56,49,'56:49','قل ان الاولين والءاخرين');
INSERT INTO "uthmani_simple" VALUES(56,50,'56:50','لمجموعون الى ميقت يوم معلوم');
INSERT INTO "uthmani_simple" VALUES(56,51,'56:51','ثم انكم ايها الضالون المكذبون');
INSERT INTO "uthmani_simple" VALUES(56,52,'56:52','لءاكلون من شجر من زقوم');
INSERT INTO "uthmani_simple" VALUES(56,53,'56:53','فمالـون منها البطون');
INSERT INTO "uthmani_simple" VALUES(56,54,'56:54','فشربون عليه من الحميم');
INSERT INTO "uthmani_simple" VALUES(56,55,'56:55','فشربون شرب الهيم');
INSERT INTO "uthmani_simple" VALUES(56,56,'56:56','هذا نزلهم يوم الدين');
INSERT INTO "uthmani_simple" VALUES(56,57,'56:57','نحن خلقنكم فلولا تصدقون');
INSERT INTO "uthmani_simple" VALUES(56,58,'56:58','افرءيتم ما تمنون');
INSERT INTO "uthmani_simple" VALUES(56,59,'56:59','ءانتم تخلقونه ام نحن الخلقون');
INSERT INTO "uthmani_simple" VALUES(56,60,'56:60','نحن قدرنا بينكم الموت وما نحن بمسبوقين');
INSERT INTO "uthmani_simple" VALUES(56,61,'56:61','على ان نبدل امثلكم وننشئكم فى ما لا تعلمون');
INSERT INTO "uthmani_simple" VALUES(56,62,'56:62','ولقد علمتم النشاة الاولى فلولا تذكرون');
INSERT INTO "uthmani_simple" VALUES(56,63,'56:63','افرءيتم ما تحرثون');
INSERT INTO "uthmani_simple" VALUES(56,64,'56:64','ءانتم تزرعونه ام نحن الزرعون');
INSERT INTO "uthmani_simple" VALUES(56,65,'56:65','لو نشاء لجعلنه حطما فظلتم تفكهون');
INSERT INTO "uthmani_simple" VALUES(56,66,'56:66','انا لمغرمون');
INSERT INTO "uthmani_simple" VALUES(56,67,'56:67','بل نحن محرومون');
INSERT INTO "uthmani_simple" VALUES(56,68,'56:68','افرءيتم الماء الذى تشربون');
INSERT INTO "uthmani_simple" VALUES(56,69,'56:69','ءانتم انزلتموه من المزن ام نحن المنزلون');
INSERT INTO "uthmani_simple" VALUES(56,70,'56:70','لو نشاء جعلنه اجاجا فلولا تشكرون');
INSERT INTO "uthmani_simple" VALUES(56,71,'56:71','افرءيتم النار التى تورون');
INSERT INTO "uthmani_simple" VALUES(56,72,'56:72','ءانتم انشاتم شجرتها ام نحن المنشـون');
INSERT INTO "uthmani_simple" VALUES(56,73,'56:73','نحن جعلنها تذكرة ومتعا للمقوين');
INSERT INTO "uthmani_simple" VALUES(56,74,'56:74','فسبح باسم ربك العظيم');
INSERT INTO "uthmani_simple" VALUES(56,75,'56:75','فلا اقسم بموقع النجوم');
INSERT INTO "uthmani_simple" VALUES(56,76,'56:76','وانه لقسم لو تعلمون عظيم');
INSERT INTO "uthmani_simple" VALUES(56,77,'56:77','انه لقرءان كريم');
INSERT INTO "uthmani_simple" VALUES(56,78,'56:78','فى كتب مكنون');
INSERT INTO "uthmani_simple" VALUES(56,79,'56:79','لا يمسه الا المطهرون');
INSERT INTO "uthmani_simple" VALUES(56,80,'56:80','تنزيل من رب العلمين');
INSERT INTO "uthmani_simple" VALUES(56,81,'56:81','افبهذا الحديث انتم مدهنون');
INSERT INTO "uthmani_simple" VALUES(56,82,'56:82','وتجعلون رزقكم انكم تكذبون');
INSERT INTO "uthmani_simple" VALUES(56,83,'56:83','فلولا اذا بلغت الحلقوم');
INSERT INTO "uthmani_simple" VALUES(56,84,'56:84','وانتم حينئذ تنظرون');
INSERT INTO "uthmani_simple" VALUES(56,85,'56:85','ونحن اقرب اليه منكم ولكن لا تبصرون');
INSERT INTO "uthmani_simple" VALUES(56,86,'56:86','فلولا ان كنتم غير مدينين');
INSERT INTO "uthmani_simple" VALUES(56,87,'56:87','ترجعونها ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(56,88,'56:88','فاما ان كان من المقربين');
INSERT INTO "uthmani_simple" VALUES(56,89,'56:89','فروح وريحان وجنت نعيم');
INSERT INTO "uthmani_simple" VALUES(56,90,'56:90','واما ان كان من اصحب اليمين');
INSERT INTO "uthmani_simple" VALUES(56,91,'56:91','فسلم لك من اصحب اليمين');
INSERT INTO "uthmani_simple" VALUES(56,92,'56:92','واما ان كان من المكذبين الضالين');
INSERT INTO "uthmani_simple" VALUES(56,93,'56:93','فنزل من حميم');
INSERT INTO "uthmani_simple" VALUES(56,94,'56:94','وتصلية جحيم');
INSERT INTO "uthmani_simple" VALUES(56,95,'56:95','ان هذا لهو حق اليقين');
INSERT INTO "uthmani_simple" VALUES(56,96,'56:96','فسبح باسم ربك العظيم');
INSERT INTO "uthmani_simple" VALUES(57,1,'57:1','سبح لله ما فى السموت والارض وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(57,2,'57:2','له ملك السموت والارض يحى ويميت وهو على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(57,3,'57:3','هو الاول والءاخر والظهر والباطن وهو بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(57,4,'57:4','هو الذى خلق السموت والارض فى ستة ايام ثم استوى على العرش يعلم ما يلج فى الارض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو معكم اين ما كنتم والله بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(57,5,'57:5','له ملك السموت والارض والى الله ترجع الامور');
INSERT INTO "uthmani_simple" VALUES(57,6,'57:6','يولج اليل فى النهار ويولج النهار فى اليل وهو عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(57,7,'57:7','ءامنوا۟ بالله ورسوله وانفقوا۟ مما جعلكم مستخلفين فيه فالذين ءامنوا۟ منكم وانفقوا۟ لهم اجر كبير');
INSERT INTO "uthmani_simple" VALUES(57,8,'57:8','وما لكم لا تؤمنون بالله والرسول يدعوكم لتؤمنوا۟ بربكم وقد اخذ ميثقكم ان كنتم مؤمنين');
INSERT INTO "uthmani_simple" VALUES(57,9,'57:9','هو الذى ينزل على عبده ءايتۭ بينت ليخرجكم من الظلمت الى النور وان الله بكم لرءوف رحيم');
INSERT INTO "uthmani_simple" VALUES(57,10,'57:10','وما لكم الا تنفقوا۟ فى سبيل الله ولله ميرث السموت والارض لا يستوى منكم من انفق من قبل الفتح وقتل او۟لئك اعظم درجة من الذين انفقوا۟ منۢ بعد وقتلوا۟ وكلا وعد الله الحسنى والله بما تعملون خبير');
INSERT INTO "uthmani_simple" VALUES(57,11,'57:11','من ذا الذى يقرض الله قرضا حسنا فيضعفه له وله اجر كريم');
INSERT INTO "uthmani_simple" VALUES(57,12,'57:12','يوم ترى المؤمنين والمؤمنت يسعى نورهم بين ايديهم وبايمنهم بشرىكم اليوم جنت تجرى من تحتها الانهر خلدين فيها ذلك هو الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(57,13,'57:13','يوم يقول المنفقون والمنفقت للذين ءامنوا۟ انظرونا نقتبس من نوركم قيل ارجعوا۟ وراءكم فالتمسوا۟ نورا فضرب بينهم بسور له بابۢ باطنه فيه الرحمة وظهره من قبله العذاب');
INSERT INTO "uthmani_simple" VALUES(57,14,'57:14','ينادونهم الم نكن معكم قالوا۟ بلى ولكنكم فتنتم انفسكم وتربصتم وارتبتم وغرتكم الامانى حتى جاء امر الله وغركم بالله الغرور');
INSERT INTO "uthmani_simple" VALUES(57,15,'57:15','فاليوم لا يؤخذ منكم فدية ولا من الذين كفروا۟ ماوىكم النار هى مولىكم وبئس المصير');
INSERT INTO "uthmani_simple" VALUES(57,16,'57:16','الم يان للذين ءامنوا۟ ان تخشع قلوبهم لذكر الله وما نزل من الحق ولا يكونوا۟ كالذين اوتوا۟ الكتب من قبل فطال عليهم الامد فقست قلوبهم وكثير منهم فسقون');
INSERT INTO "uthmani_simple" VALUES(57,17,'57:17','اعلموا۟ ان الله يحى الارض بعد موتها قد بينا لكم الءايت لعلكم تعقلون');
INSERT INTO "uthmani_simple" VALUES(57,18,'57:18','ان المصدقين والمصدقت واقرضوا۟ الله قرضا حسنا يضعف لهم ولهم اجر كريم');
INSERT INTO "uthmani_simple" VALUES(57,19,'57:19','والذين ءامنوا۟ بالله ورسله او۟لئك هم الصديقون والشهداء عند ربهم لهم اجرهم ونورهم والذين كفروا۟ وكذبوا۟ بـايتنا او۟لئك اصحب الجحيم');
INSERT INTO "uthmani_simple" VALUES(57,20,'57:20','اعلموا۟ انما الحيوة الدنيا لعب ولهو وزينة وتفاخرۢ بينكم وتكاثر فى الامول والاولد كمثل غيث اعجب الكفار نباته ثم يهيج فترىه مصفرا ثم يكون حطما وفى الءاخرة عذاب شديد ومغفرة من الله ورضون وما الحيوة الدنيا الا متع الغرور');
INSERT INTO "uthmani_simple" VALUES(57,21,'57:21','سابقوا۟ الى مغفرة من ربكم وجنة عرضها كعرض السماء والارض اعدت للذين ءامنوا۟ بالله ورسله ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO "uthmani_simple" VALUES(57,22,'57:22','ما اصاب من مصيبة فى الارض ولا فى انفسكم الا فى كتب من قبل ان نبراها ان ذلك على الله يسير');
INSERT INTO "uthmani_simple" VALUES(57,23,'57:23','لكيلا تاسوا۟ على ما فاتكم ولا تفرحوا۟ بما ءاتىكم والله لا يحب كل مختال فخور');
INSERT INTO "uthmani_simple" VALUES(57,24,'57:24','الذين يبخلون ويامرون الناس بالبخل ومن يتول فان الله هو الغنى الحميد');
INSERT INTO "uthmani_simple" VALUES(57,25,'57:25','لقد ارسلنا رسلنا بالبينت وانزلنا معهم الكتب والميزان ليقوم الناس بالقسط وانزلنا الحديد فيه باس شديد ومنفع للناس وليعلم الله من ينصره ورسله بالغيب ان الله قوى عزيز');
INSERT INTO "uthmani_simple" VALUES(57,26,'57:26','ولقد ارسلنا نوحا وابرهيم وجعلنا فى ذريتهما النبوة والكتب فمنهم مهتد وكثير منهم فسقون');
INSERT INTO "uthmani_simple" VALUES(57,27,'57:27','ثم قفينا على ءاثرهم برسلنا وقفينا بعيسى ابن مريم وءاتينه الانجيل وجعلنا فى قلوب الذين اتبعوه رافة ورحمة ورهبانية ابتدعوها ما كتبنها عليهم الا ابتغاء رضون الله فما رعوها حق رعايتها فـاتينا الذين ءامنوا۟ منهم اجرهم وكثير منهم فسقون');
INSERT INTO "uthmani_simple" VALUES(57,28,'57:28','يايها الذين ءامنوا۟ اتقوا۟ الله وءامنوا۟ برسوله يؤتكم كفلين من رحمته ويجعل لكم نورا تمشون به ويغفر لكم والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(57,29,'57:29','لئلا يعلم اهل الكتب الا يقدرون على شىء من فضل الله وان الفضل بيد الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO "uthmani_simple" VALUES(58,1,'58:1','قد سمع الله قول التى تجدلك فى زوجها وتشتكى الى الله والله يسمع تحاوركما ان الله سميعۢ بصير');
INSERT INTO "uthmani_simple" VALUES(58,2,'58:2','الذين يظهرون منكم من نسائهم ما هن امهتهم ان امهتهم الا الـى ولدنهم وانهم ليقولون منكرا من القول وزورا وان الله لعفو غفور');
INSERT INTO "uthmani_simple" VALUES(58,3,'58:3','والذين يظهرون من نسائهم ثم يعودون لما قالوا۟ فتحرير رقبة من قبل ان يتماسا ذلكم توعظون به والله بما تعملون خبير');
INSERT INTO "uthmani_simple" VALUES(58,4,'58:4','فمن لم يجد فصيام شهرين متتابعين من قبل ان يتماسا فمن لم يستطع فاطعام ستين مسكينا ذلك لتؤمنوا۟ بالله ورسوله وتلك حدود الله وللكفرين عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(58,5,'58:5','ان الذين يحادون الله ورسوله كبتوا۟ كما كبت الذين من قبلهم وقد انزلنا ءايتۭ بينت وللكفرين عذاب مهين');
INSERT INTO "uthmani_simple" VALUES(58,6,'58:6','يوم يبعثهم الله جميعا فينبئهم بما عملوا۟ احصىه الله ونسوه والله على كل شىء شهيد');
INSERT INTO "uthmani_simple" VALUES(58,7,'58:7','الم تر ان الله يعلم ما فى السموت وما فى الارض ما يكون من نجوى ثلثة الا هو رابعهم ولا خمسة الا هو سادسهم ولا ادنى من ذلك ولا اكثر الا هو معهم اين ما كانوا۟ ثم ينبئهم بما عملوا۟ يوم القيمة ان الله بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(58,8,'58:8','الم تر الى الذين نهوا۟ عن النجوى ثم يعودون لما نهوا۟ عنه ويتنجون بالاثم والعدون ومعصيت الرسول واذا جاءوك حيوك بما لم يحيك به الله ويقولون فى انفسهم لولا يعذبنا الله بما نقول حسبهم جهنم يصلونها فبئس المصير');
INSERT INTO "uthmani_simple" VALUES(58,9,'58:9','يايها الذين ءامنوا۟ اذا تنجيتم فلا تتنجوا۟ بالاثم والعدون ومعصيت الرسول وتنجوا۟ بالبر والتقوى واتقوا۟ الله الذى اليه تحشرون');
INSERT INTO "uthmani_simple" VALUES(58,10,'58:10','انما النجوى من الشيطن ليحزن الذين ءامنوا۟ وليس بضارهم شيـا الا باذن الله وعلى الله فليتوكل المؤمنون');
INSERT INTO "uthmani_simple" VALUES(58,11,'58:11','يايها الذين ءامنوا۟ اذا قيل لكم تفسحوا۟ فى المجلس فافسحوا۟ يفسح الله لكم واذا قيل انشزوا۟ فانشزوا۟ يرفع الله الذين ءامنوا۟ منكم والذين اوتوا۟ العلم درجت والله بما تعملون خبير');
INSERT INTO "uthmani_simple" VALUES(58,12,'58:12','يايها الذين ءامنوا۟ اذا نجيتم الرسول فقدموا۟ بين يدى نجوىكم صدقة ذلك خير لكم واطهر فان لم تجدوا۟ فان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(58,13,'58:13','ءاشفقتم ان تقدموا۟ بين يدى نجوىكم صدقت فاذ لم تفعلوا۟ وتاب الله عليكم فاقيموا۟ الصلوة وءاتوا۟ الزكوة واطيعوا۟ الله ورسوله والله خبيرۢ بما تعملون');
INSERT INTO "uthmani_simple" VALUES(58,14,'58:14','الم تر الى الذين تولوا۟ قوما غضب الله عليهم ما هم منكم ولا منهم ويحلفون على الكذب وهم يعلمون');
INSERT INTO "uthmani_simple" VALUES(58,15,'58:15','اعد الله لهم عذابا شديدا انهم ساء ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(58,16,'58:16','اتخذوا۟ ايمنهم جنة فصدوا۟ عن سبيل الله فلهم عذاب مهين');
INSERT INTO "uthmani_simple" VALUES(58,17,'58:17','لن تغنى عنهم امولهم ولا اولدهم من الله شيـا او۟لئك اصحب النار هم فيها خلدون');
INSERT INTO "uthmani_simple" VALUES(58,18,'58:18','يوم يبعثهم الله جميعا فيحلفون له كما يحلفون لكم ويحسبون انهم على شىء الا انهم هم الكذبون');
INSERT INTO "uthmani_simple" VALUES(58,19,'58:19','استحوذ عليهم الشيطن فانسىهم ذكر الله او۟لئك حزب الشيطن الا ان حزب الشيطن هم الخسرون');
INSERT INTO "uthmani_simple" VALUES(58,20,'58:20','ان الذين يحادون الله ورسوله او۟لئك فى الاذلين');
INSERT INTO "uthmani_simple" VALUES(58,21,'58:21','كتب الله لاغلبن انا۠ ورسلى ان الله قوى عزيز');
INSERT INTO "uthmani_simple" VALUES(58,22,'58:22','لا تجد قوما يؤمنون بالله واليوم الءاخر يوادون من حاد الله ورسوله ولو كانوا۟ ءاباءهم او ابناءهم او اخونهم او عشيرتهم او۟لئك كتب فى قلوبهم الايمن وايدهم بروح منه ويدخلهم جنت تجرى من تحتها الانهر خلدين فيها رضى الله عنهم ورضوا۟ عنه او۟لئك حزب الله الا ان حزب الله هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(59,1,'59:1','سبح لله ما فى السموت وما فى الارض وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(59,2,'59:2','هو الذى اخرج الذين كفروا۟ من اهل الكتب من ديرهم لاول الحشر ما ظننتم ان يخرجوا۟ وظنوا۟ انهم مانعتهم حصونهم من الله فاتىهم الله من حيث لم يحتسبوا۟ وقذف فى قلوبهم الرعب يخربون بيوتهم بايديهم وايدى المؤمنين فاعتبروا۟ ياو۟لى الابصر');
INSERT INTO "uthmani_simple" VALUES(59,3,'59:3','ولولا ان كتب الله عليهم الجلاء لعذبهم فى الدنيا ولهم فى الءاخرة عذاب النار');
INSERT INTO "uthmani_simple" VALUES(59,4,'59:4','ذلك بانهم شاقوا۟ الله ورسوله ومن يشاق الله فان الله شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(59,5,'59:5','ما قطعتم من لينة او تركتموها قائمة على اصولها فباذن الله وليخزى الفسقين');
INSERT INTO "uthmani_simple" VALUES(59,6,'59:6','وما افاء الله على رسوله منهم فما اوجفتم عليه من خيل ولا ركاب ولكن الله يسلط رسله على من يشاء والله على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(59,7,'59:7','ما افاء الله على رسوله من اهل القرى فلله وللرسول ولذى القربى واليتمى والمسكين وابن السبيل كى لا يكون دولةۢ بين الاغنياء منكم وما ءاتىكم الرسول فخذوه وما نهىكم عنه فانتهوا۟ واتقوا۟ الله ان الله شديد العقاب');
INSERT INTO "uthmani_simple" VALUES(59,8,'59:8','للفقراء المهجرين الذين اخرجوا۟ من ديرهم وامولهم يبتغون فضلا من الله ورضونا وينصرون الله ورسوله او۟لئك هم الصدقون');
INSERT INTO "uthmani_simple" VALUES(59,9,'59:9','والذين تبوءو الدار والايمن من قبلهم يحبون من هاجر اليهم ولا يجدون فى صدورهم حاجة مما اوتوا۟ ويؤثرون على انفسهم ولو كان بهم خصاصة ومن يوق شح نفسه فاو۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(59,10,'59:10','والذين جاءو منۢ بعدهم يقولون ربنا اغفر لنا ولاخوننا الذين سبقونا بالايمن ولا تجعل فى قلوبنا غلا للذين ءامنوا۟ ربنا انك رءوف رحيم');
INSERT INTO "uthmani_simple" VALUES(59,11,'59:11','الم تر الى الذين نافقوا۟ يقولون لاخونهم الذين كفروا۟ من اهل الكتب لئن اخرجتم لنخرجن معكم ولا نطيع فيكم احدا ابدا وان قوتلتم لننصرنكم والله يشهد انهم لكذبون');
INSERT INTO "uthmani_simple" VALUES(59,12,'59:12','لئن اخرجوا۟ لا يخرجون معهم ولئن قوتلوا۟ لا ينصرونهم ولئن نصروهم ليولن الادبر ثم لا ينصرون');
INSERT INTO "uthmani_simple" VALUES(59,13,'59:13','لانتم اشد رهبة فى صدورهم من الله ذلك بانهم قوم لا يفقهون');
INSERT INTO "uthmani_simple" VALUES(59,14,'59:14','لا يقتلونكم جميعا الا فى قرى محصنة او من وراء جدرۭ باسهم بينهم شديد تحسبهم جميعا وقلوبهم شتى ذلك بانهم قوم لا يعقلون');
INSERT INTO "uthmani_simple" VALUES(59,15,'59:15','كمثل الذين من قبلهم قريبا ذاقوا۟ وبال امرهم ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(59,16,'59:16','كمثل الشيطن اذ قال للانسن اكفر فلما كفر قال انى برىء منك انى اخاف الله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(59,17,'59:17','فكان عقبتهما انهما فى النار خلدين فيها وذلك جزؤا۟ الظلمين');
INSERT INTO "uthmani_simple" VALUES(59,18,'59:18','يايها الذين ءامنوا۟ اتقوا۟ الله ولتنظر نفس ما قدمت لغد واتقوا۟ الله ان الله خبيرۢ بما تعملون');
INSERT INTO "uthmani_simple" VALUES(59,19,'59:19','ولا تكونوا۟ كالذين نسوا۟ الله فانسىهم انفسهم او۟لئك هم الفسقون');
INSERT INTO "uthmani_simple" VALUES(59,20,'59:20','لا يستوى اصحب النار واصحب الجنة اصحب الجنة هم الفائزون');
INSERT INTO "uthmani_simple" VALUES(59,21,'59:21','لو انزلنا هذا القرءان على جبل لرايته خشعا متصدعا من خشية الله وتلك الامثل نضربها للناس لعلهم يتفكرون');
INSERT INTO "uthmani_simple" VALUES(59,22,'59:22','هو الله الذى لا اله الا هو علم الغيب والشهدة هو الرحمن الرحيم');
INSERT INTO "uthmani_simple" VALUES(59,23,'59:23','هو الله الذى لا اله الا هو الملك القدوس السلم المؤمن المهيمن العزيز الجبار المتكبر سبحن الله عما يشركون');
INSERT INTO "uthmani_simple" VALUES(59,24,'59:24','هو الله الخلق البارئ المصور له الاسماء الحسنى يسبح له ما فى السموت والارض وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(60,1,'60:1','يايها الذين ءامنوا۟ لا تتخذوا۟ عدوى وعدوكم اولياء تلقون اليهم بالمودة وقد كفروا۟ بما جاءكم من الحق يخرجون الرسول واياكم ان تؤمنوا۟ بالله ربكم ان كنتم خرجتم جهدا فى سبيلى وابتغاء مرضاتى تسرون اليهم بالمودة وانا۠ اعلم بما اخفيتم وما اعلنتم ومن يفعله منكم فقد ضل سواء السبيل');
INSERT INTO "uthmani_simple" VALUES(60,2,'60:2','ان يثقفوكم يكونوا۟ لكم اعداء ويبسطوا۟ اليكم ايديهم والسنتهم بالسوء وودوا۟ لو تكفرون');
INSERT INTO "uthmani_simple" VALUES(60,3,'60:3','لن تنفعكم ارحامكم ولا اولدكم يوم القيمة يفصل بينكم والله بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(60,4,'60:4','قد كانت لكم اسوة حسنة فى ابرهيم والذين معه اذ قالوا۟ لقومهم انا برءؤا۟ منكم ومما تعبدون من دون الله كفرنا بكم وبدا بيننا وبينكم العدوة والبغضاء ابدا حتى تؤمنوا۟ بالله وحده الا قول ابرهيم لابيه لاستغفرن لك وما املك لك من الله من شىء ربنا عليك توكلنا واليك انبنا واليك المصير');
INSERT INTO "uthmani_simple" VALUES(60,5,'60:5','ربنا لا تجعلنا فتنة للذين كفروا۟ واغفر لنا ربنا انك انت العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(60,6,'60:6','لقد كان لكم فيهم اسوة حسنة لمن كان يرجوا۟ الله واليوم الءاخر ومن يتول فان الله هو الغنى الحميد');
INSERT INTO "uthmani_simple" VALUES(60,7,'60:7','۞ عسى الله ان يجعل بينكم وبين الذين عاديتم منهم مودة والله قدير والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(60,8,'60:8','لا ينهىكم الله عن الذين لم يقتلوكم فى الدين ولم يخرجوكم من ديركم ان تبروهم وتقسطوا۟ اليهم ان الله يحب المقسطين');
INSERT INTO "uthmani_simple" VALUES(60,9,'60:9','انما ينهىكم الله عن الذين قتلوكم فى الدين واخرجوكم من ديركم وظهروا۟ على اخراجكم ان تولوهم ومن يتولهم فاو۟لئك هم الظلمون');
INSERT INTO "uthmani_simple" VALUES(60,10,'60:10','يايها الذين ءامنوا۟ اذا جاءكم المؤمنت مهجرت فامتحنوهن الله اعلم بايمنهن فان علمتموهن مؤمنت فلا ترجعوهن الى الكفار لا هن حل لهم ولا هم يحلون لهن وءاتوهم ما انفقوا۟ ولا جناح عليكم ان تنكحوهن اذا ءاتيتموهن اجورهن ولا تمسكوا۟ بعصم الكوافر وسـلوا۟ ما انفقتم وليسـلوا۟ ما انفقوا۟ ذلكم حكم الله يحكم بينكم والله عليم حكيم');
INSERT INTO "uthmani_simple" VALUES(60,11,'60:11','وان فاتكم شىء من ازوجكم الى الكفار فعاقبتم فـاتوا۟ الذين ذهبت ازوجهم مثل ما انفقوا۟ واتقوا۟ الله الذى انتم به مؤمنون');
INSERT INTO "uthmani_simple" VALUES(60,12,'60:12','يايها النبى اذا جاءك المؤمنت يبايعنك على ان لا يشركن بالله شيـا ولا يسرقن ولا يزنين ولا يقتلن اولدهن ولا ياتين ببهتن يفترينه بين ايديهن وارجلهن ولا يعصينك فى معروف فبايعهن واستغفر لهن الله ان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(60,13,'60:13','يايها الذين ءامنوا۟ لا تتولوا۟ قوما غضب الله عليهم قد يئسوا۟ من الءاخرة كما يئس الكفار من اصحب القبور');
INSERT INTO "uthmani_simple" VALUES(61,1,'61:1','سبح لله ما فى السموت وما فى الارض وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(61,2,'61:2','يايها الذين ءامنوا۟ لم تقولون ما لا تفعلون');
INSERT INTO "uthmani_simple" VALUES(61,3,'61:3','كبر مقتا عند الله ان تقولوا۟ ما لا تفعلون');
INSERT INTO "uthmani_simple" VALUES(61,4,'61:4','ان الله يحب الذين يقتلون فى سبيله صفا كانهم بنين مرصوص');
INSERT INTO "uthmani_simple" VALUES(61,5,'61:5','واذ قال موسى لقومه يقوم لم تؤذوننى وقد تعلمون انى رسول الله اليكم فلما زاغوا۟ ازاغ الله قلوبهم والله لا يهدى القوم الفسقين');
INSERT INTO "uthmani_simple" VALUES(61,6,'61:6','واذ قال عيسى ابن مريم يبنى اسرءيل انى رسول الله اليكم مصدقا لما بين يدى من التورىة ومبشرۢا برسول ياتى منۢ بعدى اسمه احمد فلما جاءهم بالبينت قالوا۟ هذا سحر مبين');
INSERT INTO "uthmani_simple" VALUES(61,7,'61:7','ومن اظلم ممن افترى على الله الكذب وهو يدعى الى الاسلم والله لا يهدى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(61,8,'61:8','يريدون ليطفـوا۟ نور الله بافوههم والله متم نوره ولو كره الكفرون');
INSERT INTO "uthmani_simple" VALUES(61,9,'61:9','هو الذى ارسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون');
INSERT INTO "uthmani_simple" VALUES(61,10,'61:10','يايها الذين ءامنوا۟ هل ادلكم على تجرة تنجيكم من عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(61,11,'61:11','تؤمنون بالله ورسوله وتجهدون فى سبيل الله بامولكم وانفسكم ذلكم خير لكم ان كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(61,12,'61:12','يغفر لكم ذنوبكم ويدخلكم جنت تجرى من تحتها الانهر ومسكن طيبة فى جنت عدن ذلك الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(61,13,'61:13','واخرى تحبونها نصر من الله وفتح قريب وبشر المؤمنين');
INSERT INTO "uthmani_simple" VALUES(61,14,'61:14','يايها الذين ءامنوا۟ كونوا۟ انصار الله كما قال عيسى ابن مريم للحوارين من انصارى الى الله قال الحواريون نحن انصار الله فـامنت طائفة منۢ بنى اسرءيل وكفرت طائفة فايدنا الذين ءامنوا۟ على عدوهم فاصبحوا۟ ظهرين');
INSERT INTO "uthmani_simple" VALUES(62,1,'62:1','يسبح لله ما فى السموت وما فى الارض الملك القدوس العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(62,2,'62:2','هو الذى بعث فى الامين رسولا منهم يتلوا۟ عليهم ءايته ويزكيهم ويعلمهم الكتب والحكمة وان كانوا۟ من قبل لفى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(62,3,'62:3','وءاخرين منهم لما يلحقوا۟ بهم وهو العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(62,4,'62:4','ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO "uthmani_simple" VALUES(62,5,'62:5','مثل الذين حملوا۟ التورىة ثم لم يحملوها كمثل الحمار يحمل اسفارۢا بئس مثل القوم الذين كذبوا۟ بـايت الله والله لا يهدى القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(62,6,'62:6','قل يايها الذين هادوا۟ ان زعمتم انكم اولياء لله من دون الناس فتمنوا۟ الموت ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(62,7,'62:7','ولا يتمنونه ابدۢا بما قدمت ايديهم والله عليمۢ بالظلمين');
INSERT INTO "uthmani_simple" VALUES(62,8,'62:8','قل ان الموت الذى تفرون منه فانه ملقيكم ثم تردون الى علم الغيب والشهدة فينبئكم بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(62,9,'62:9','يايها الذين ءامنوا۟ اذا نودى للصلوة من يوم الجمعة فاسعوا۟ الى ذكر الله وذروا۟ البيع ذلكم خير لكم ان كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(62,10,'62:10','فاذا قضيت الصلوة فانتشروا۟ فى الارض وابتغوا۟ من فضل الله واذكروا۟ الله كثيرا لعلكم تفلحون');
INSERT INTO "uthmani_simple" VALUES(62,11,'62:11','واذا راوا۟ تجرة او لهوا انفضوا۟ اليها وتركوك قائما قل ما عند الله خير من اللهو ومن التجرة والله خير الرزقين');
INSERT INTO "uthmani_simple" VALUES(63,1,'63:1','اذا جاءك المنفقون قالوا۟ نشهد انك لرسول الله والله يعلم انك لرسوله والله يشهد ان المنفقين لكذبون');
INSERT INTO "uthmani_simple" VALUES(63,2,'63:2','اتخذوا۟ ايمنهم جنة فصدوا۟ عن سبيل الله انهم ساء ما كانوا۟ يعملون');
INSERT INTO "uthmani_simple" VALUES(63,3,'63:3','ذلك بانهم ءامنوا۟ ثم كفروا۟ فطبع على قلوبهم فهم لا يفقهون');
INSERT INTO "uthmani_simple" VALUES(63,4,'63:4','واذا رايتهم تعجبك اجسامهم وان يقولوا۟ تسمع لقولهم كانهم خشب مسندة يحسبون كل صيحة عليهم هم العدو فاحذرهم قتلهم الله انى يؤفكون');
INSERT INTO "uthmani_simple" VALUES(63,5,'63:5','واذا قيل لهم تعالوا۟ يستغفر لكم رسول الله لووا۟ رءوسهم ورايتهم يصدون وهم مستكبرون');
INSERT INTO "uthmani_simple" VALUES(63,6,'63:6','سواء عليهم استغفرت لهم ام لم تستغفر لهم لن يغفر الله لهم ان الله لا يهدى القوم الفسقين');
INSERT INTO "uthmani_simple" VALUES(63,7,'63:7','هم الذين يقولون لا تنفقوا۟ على من عند رسول الله حتى ينفضوا۟ ولله خزائن السموت والارض ولكن المنفقين لا يفقهون');
INSERT INTO "uthmani_simple" VALUES(63,8,'63:8','يقولون لئن رجعنا الى المدينة ليخرجن الاعز منها الاذل ولله العزة ولرسوله وللمؤمنين ولكن المنفقين لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(63,9,'63:9','يايها الذين ءامنوا۟ لا تلهكم امولكم ولا اولدكم عن ذكر الله ومن يفعل ذلك فاو۟لئك هم الخسرون');
INSERT INTO "uthmani_simple" VALUES(63,10,'63:10','وانفقوا۟ من ما رزقنكم من قبل ان ياتى احدكم الموت فيقول رب لولا اخرتنى الى اجل قريب فاصدق واكن من الصلحين');
INSERT INTO "uthmani_simple" VALUES(63,11,'63:11','ولن يؤخر الله نفسا اذا جاء اجلها والله خبيرۢ بما تعملون');
INSERT INTO "uthmani_simple" VALUES(64,1,'64:1','يسبح لله ما فى السموت وما فى الارض له الملك وله الحمد وهو على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(64,2,'64:2','هو الذى خلقكم فمنكم كافر ومنكم مؤمن والله بما تعملون بصير');
INSERT INTO "uthmani_simple" VALUES(64,3,'64:3','خلق السموت والارض بالحق وصوركم فاحسن صوركم واليه المصير');
INSERT INTO "uthmani_simple" VALUES(64,4,'64:4','يعلم ما فى السموت والارض ويعلم ما تسرون وما تعلنون والله عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(64,5,'64:5','الم ياتكم نبؤا۟ الذين كفروا۟ من قبل فذاقوا۟ وبال امرهم ولهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(64,6,'64:6','ذلك بانه كانت تاتيهم رسلهم بالبينت فقالوا۟ ابشر يهدوننا فكفروا۟ وتولوا۟ واستغنى الله والله غنى حميد');
INSERT INTO "uthmani_simple" VALUES(64,7,'64:7','زعم الذين كفروا۟ ان لن يبعثوا۟ قل بلى وربى لتبعثن ثم لتنبؤن بما عملتم وذلك على الله يسير');
INSERT INTO "uthmani_simple" VALUES(64,8,'64:8','فـامنوا۟ بالله ورسوله والنور الذى انزلنا والله بما تعملون خبير');
INSERT INTO "uthmani_simple" VALUES(64,9,'64:9','يوم يجمعكم ليوم الجمع ذلك يوم التغابن ومن يؤمنۢ بالله ويعمل صلحا يكفر عنه سيـاته ويدخله جنت تجرى من تحتها الانهر خلدين فيها ابدا ذلك الفوز العظيم');
INSERT INTO "uthmani_simple" VALUES(64,10,'64:10','والذين كفروا۟ وكذبوا۟ بـايتنا او۟لئك اصحب النار خلدين فيها وبئس المصير');
INSERT INTO "uthmani_simple" VALUES(64,11,'64:11','ما اصاب من مصيبة الا باذن الله ومن يؤمنۢ بالله يهد قلبه والله بكل شىء عليم');
INSERT INTO "uthmani_simple" VALUES(64,12,'64:12','واطيعوا۟ الله واطيعوا۟ الرسول فان توليتم فانما على رسولنا البلغ المبين');
INSERT INTO "uthmani_simple" VALUES(64,13,'64:13','الله لا اله الا هو وعلى الله فليتوكل المؤمنون');
INSERT INTO "uthmani_simple" VALUES(64,14,'64:14','يايها الذين ءامنوا۟ ان من ازوجكم واولدكم عدوا لكم فاحذروهم وان تعفوا۟ وتصفحوا۟ وتغفروا۟ فان الله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(64,15,'64:15','انما امولكم واولدكم فتنة والله عنده اجر عظيم');
INSERT INTO "uthmani_simple" VALUES(64,16,'64:16','فاتقوا۟ الله ما استطعتم واسمعوا۟ واطيعوا۟ وانفقوا۟ خيرا لانفسكم ومن يوق شح نفسه فاو۟لئك هم المفلحون');
INSERT INTO "uthmani_simple" VALUES(64,17,'64:17','ان تقرضوا۟ الله قرضا حسنا يضعفه لكم ويغفر لكم والله شكور حليم');
INSERT INTO "uthmani_simple" VALUES(64,18,'64:18','علم الغيب والشهدة العزيز الحكيم');
INSERT INTO "uthmani_simple" VALUES(65,1,'65:1','يايها النبى اذا طلقتم النساء فطلقوهن لعدتهن واحصوا۟ العدة واتقوا۟ الله ربكم لا تخرجوهن منۢ بيوتهن ولا يخرجن الا ان ياتين بفحشة مبينة وتلك حدود الله ومن يتعد حدود الله فقد ظلم نفسه لا تدرى لعل الله يحدث بعد ذلك امرا');
INSERT INTO "uthmani_simple" VALUES(65,2,'65:2','فاذا بلغن اجلهن فامسكوهن بمعروف او فارقوهن بمعروف واشهدوا۟ ذوى عدل منكم واقيموا۟ الشهدة لله ذلكم يوعظ به من كان يؤمن بالله واليوم الءاخر ومن يتق الله يجعل له مخرجا');
INSERT INTO "uthmani_simple" VALUES(65,3,'65:3','ويرزقه من حيث لا يحتسب ومن يتوكل على الله فهو حسبه ان الله بلغ امره قد جعل الله لكل شىء قدرا');
INSERT INTO "uthmani_simple" VALUES(65,4,'65:4','والـى يئسن من المحيض من نسائكم ان ارتبتم فعدتهن ثلثة اشهر والـى لم يحضن واو۟لت الاحمال اجلهن ان يضعن حملهن ومن يتق الله يجعل له من امره يسرا');
INSERT INTO "uthmani_simple" VALUES(65,5,'65:5','ذلك امر الله انزله اليكم ومن يتق الله يكفر عنه سيـاته ويعظم له اجرا');
INSERT INTO "uthmani_simple" VALUES(65,6,'65:6','اسكنوهن من حيث سكنتم من وجدكم ولا تضاروهن لتضيقوا۟ عليهن وان كن او۟لت حمل فانفقوا۟ عليهن حتى يضعن حملهن فان ارضعن لكم فـاتوهن اجورهن واتمروا۟ بينكم بمعروف وان تعاسرتم فسترضع له اخرى');
INSERT INTO "uthmani_simple" VALUES(65,7,'65:7','لينفق ذو سعة من سعته ومن قدر عليه رزقه فلينفق مما ءاتىه الله لا يكلف الله نفسا الا ما ءاتىها سيجعل الله بعد عسر يسرا');
INSERT INTO "uthmani_simple" VALUES(65,8,'65:8','وكاين من قرية عتت عن امر ربها ورسله فحاسبنها حسابا شديدا وعذبنها عذابا نكرا');
INSERT INTO "uthmani_simple" VALUES(65,9,'65:9','فذاقت وبال امرها وكان عقبة امرها خسرا');
INSERT INTO "uthmani_simple" VALUES(65,10,'65:10','اعد الله لهم عذابا شديدا فاتقوا۟ الله ياو۟لى الالبب الذين ءامنوا۟ قد انزل الله اليكم ذكرا');
INSERT INTO "uthmani_simple" VALUES(65,11,'65:11','رسولا يتلوا۟ عليكم ءايت الله مبينت ليخرج الذين ءامنوا۟ وعملوا۟ الصلحت من الظلمت الى النور ومن يؤمنۢ بالله ويعمل صلحا يدخله جنت تجرى من تحتها الانهر خلدين فيها ابدا قد احسن الله له رزقا');
INSERT INTO "uthmani_simple" VALUES(65,12,'65:12','الله الذى خلق سبع سموت ومن الارض مثلهن يتنزل الامر بينهن لتعلموا۟ ان الله على كل شىء قدير وان الله قد احاط بكل شىء علمۢا');
INSERT INTO "uthmani_simple" VALUES(66,1,'66:1','يايها النبى لم تحرم ما احل الله لك تبتغى مرضات ازوجك والله غفور رحيم');
INSERT INTO "uthmani_simple" VALUES(66,2,'66:2','قد فرض الله لكم تحلة ايمنكم والله مولىكم وهو العليم الحكيم');
INSERT INTO "uthmani_simple" VALUES(66,3,'66:3','واذ اسر النبى الى بعض ازوجه حديثا فلما نبات به واظهره الله عليه عرف بعضه واعرض عنۢ بعض فلما نباها به قالت من انۢباك هذا قال نبانى العليم الخبير');
INSERT INTO "uthmani_simple" VALUES(66,4,'66:4','ان تتوبا الى الله فقد صغت قلوبكما وان تظهرا عليه فان الله هو مولىه وجبريل وصلح المؤمنين والملئكة بعد ذلك ظهير');
INSERT INTO "uthmani_simple" VALUES(66,5,'66:5','عسى ربه ان طلقكن ان يبدله ازوجا خيرا منكن مسلمت مؤمنت قنتت تئبت عبدت سئحت ثيبت وابكارا');
INSERT INTO "uthmani_simple" VALUES(66,6,'66:6','يايها الذين ءامنوا۟ قوا۟ انفسكم واهليكم نارا وقودها الناس والحجارة عليها ملئكة غلاظ شداد لا يعصون الله ما امرهم ويفعلون ما يؤمرون');
INSERT INTO "uthmani_simple" VALUES(66,7,'66:7','يايها الذين كفروا۟ لا تعتذروا۟ اليوم انما تجزون ما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(66,8,'66:8','يايها الذين ءامنوا۟ توبوا۟ الى الله توبة نصوحا عسى ربكم ان يكفر عنكم سيـاتكم ويدخلكم جنت تجرى من تحتها الانهر يوم لا يخزى الله النبى والذين ءامنوا۟ معه نورهم يسعى بين ايديهم وبايمنهم يقولون ربنا اتمم لنا نورنا واغفر لنا انك على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(66,9,'66:9','يايها النبى جهد الكفار والمنفقين واغلظ عليهم وماوىهم جهنم وبئس المصير');
INSERT INTO "uthmani_simple" VALUES(66,10,'66:10','ضرب الله مثلا للذين كفروا۟ امرات نوح وامرات لوط كانتا تحت عبدين من عبادنا صلحين فخانتاهما فلم يغنيا عنهما من الله شيـا وقيل ادخلا النار مع الدخلين');
INSERT INTO "uthmani_simple" VALUES(66,11,'66:11','وضرب الله مثلا للذين ءامنوا۟ امرات فرعون اذ قالت رب ابن لى عندك بيتا فى الجنة ونجنى من فرعون وعمله ونجنى من القوم الظلمين');
INSERT INTO "uthmani_simple" VALUES(66,12,'66:12','ومريم ابنت عمرن التى احصنت فرجها فنفخنا فيه من روحنا وصدقت بكلمت ربها وكتبه وكانت من القنتين');
INSERT INTO "uthmani_simple" VALUES(67,1,'67:1','تبرك الذى بيده الملك وهو على كل شىء قدير');
INSERT INTO "uthmani_simple" VALUES(67,2,'67:2','الذى خلق الموت والحيوة ليبلوكم ايكم احسن عملا وهو العزيز الغفور');
INSERT INTO "uthmani_simple" VALUES(67,3,'67:3','الذى خلق سبع سموت طباقا ما ترى فى خلق الرحمن من تفوت فارجع البصر هل ترى من فطور');
INSERT INTO "uthmani_simple" VALUES(67,4,'67:4','ثم ارجع البصر كرتين ينقلب اليك البصر خاسئا وهو حسير');
INSERT INTO "uthmani_simple" VALUES(67,5,'67:5','ولقد زينا السماء الدنيا بمصبيح وجعلنها رجوما للشيطين واعتدنا لهم عذاب السعير');
INSERT INTO "uthmani_simple" VALUES(67,6,'67:6','وللذين كفروا۟ بربهم عذاب جهنم وبئس المصير');
INSERT INTO "uthmani_simple" VALUES(67,7,'67:7','اذا القوا۟ فيها سمعوا۟ لها شهيقا وهى تفور');
INSERT INTO "uthmani_simple" VALUES(67,8,'67:8','تكاد تميز من الغيظ كلما القى فيها فوج سالهم خزنتها الم ياتكم نذير');
INSERT INTO "uthmani_simple" VALUES(67,9,'67:9','قالوا۟ بلى قد جاءنا نذير فكذبنا وقلنا ما نزل الله من شىء ان انتم الا فى ضلل كبير');
INSERT INTO "uthmani_simple" VALUES(67,10,'67:10','وقالوا۟ لو كنا نسمع او نعقل ما كنا فى اصحب السعير');
INSERT INTO "uthmani_simple" VALUES(67,11,'67:11','فاعترفوا۟ بذنۢبهم فسحقا لاصحب السعير');
INSERT INTO "uthmani_simple" VALUES(67,12,'67:12','ان الذين يخشون ربهم بالغيب لهم مغفرة واجر كبير');
INSERT INTO "uthmani_simple" VALUES(67,13,'67:13','واسروا۟ قولكم او اجهروا۟ به انه عليمۢ بذات الصدور');
INSERT INTO "uthmani_simple" VALUES(67,14,'67:14','الا يعلم من خلق وهو اللطيف الخبير');
INSERT INTO "uthmani_simple" VALUES(67,15,'67:15','هو الذى جعل لكم الارض ذلولا فامشوا۟ فى مناكبها وكلوا۟ من رزقه واليه النشور');
INSERT INTO "uthmani_simple" VALUES(67,16,'67:16','ءامنتم من فى السماء ان يخسف بكم الارض فاذا هى تمور');
INSERT INTO "uthmani_simple" VALUES(67,17,'67:17','ام امنتم من فى السماء ان يرسل عليكم حاصبا فستعلمون كيف نذير');
INSERT INTO "uthmani_simple" VALUES(67,18,'67:18','ولقد كذب الذين من قبلهم فكيف كان نكير');
INSERT INTO "uthmani_simple" VALUES(67,19,'67:19','اولم يروا۟ الى الطير فوقهم صفت ويقبضن ما يمسكهن الا الرحمن انه بكل شىءۭ بصير');
INSERT INTO "uthmani_simple" VALUES(67,20,'67:20','امن هذا الذى هو جند لكم ينصركم من دون الرحمن ان الكفرون الا فى غرور');
INSERT INTO "uthmani_simple" VALUES(67,21,'67:21','امن هذا الذى يرزقكم ان امسك رزقه بل لجوا۟ فى عتو ونفور');
INSERT INTO "uthmani_simple" VALUES(67,22,'67:22','افمن يمشى مكبا على وجهه اهدى امن يمشى سويا على صرط مستقيم');
INSERT INTO "uthmani_simple" VALUES(67,23,'67:23','قل هو الذى انشاكم وجعل لكم السمع والابصر والافـدة قليلا ما تشكرون');
INSERT INTO "uthmani_simple" VALUES(67,24,'67:24','قل هو الذى ذراكم فى الارض واليه تحشرون');
INSERT INTO "uthmani_simple" VALUES(67,25,'67:25','ويقولون متى هذا الوعد ان كنتم صدقين');
INSERT INTO "uthmani_simple" VALUES(67,26,'67:26','قل انما العلم عند الله وانما انا۠ نذير مبين');
INSERT INTO "uthmani_simple" VALUES(67,27,'67:27','فلما راوه زلفة سيـت وجوه الذين كفروا۟ وقيل هذا الذى كنتم به تدعون');
INSERT INTO "uthmani_simple" VALUES(67,28,'67:28','قل ارءيتم ان اهلكنى الله ومن معى او رحمنا فمن يجير الكفرين من عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(67,29,'67:29','قل هو الرحمن ءامنا به وعليه توكلنا فستعلمون من هو فى ضلل مبين');
INSERT INTO "uthmani_simple" VALUES(67,30,'67:30','قل ارءيتم ان اصبح ماؤكم غورا فمن ياتيكم بماء معينۭ');
INSERT INTO "uthmani_simple" VALUES(68,1,'68:1','ن والقلم وما يسطرون');
INSERT INTO "uthmani_simple" VALUES(68,2,'68:2','ما انت بنعمة ربك بمجنون');
INSERT INTO "uthmani_simple" VALUES(68,3,'68:3','وان لك لاجرا غير ممنون');
INSERT INTO "uthmani_simple" VALUES(68,4,'68:4','وانك لعلى خلق عظيم');
INSERT INTO "uthmani_simple" VALUES(68,5,'68:5','فستبصر ويبصرون');
INSERT INTO "uthmani_simple" VALUES(68,6,'68:6','باييكم المفتون');
INSERT INTO "uthmani_simple" VALUES(68,7,'68:7','ان ربك هو اعلم بمن ضل عن سبيله وهو اعلم بالمهتدين');
INSERT INTO "uthmani_simple" VALUES(68,8,'68:8','فلا تطع المكذبين');
INSERT INTO "uthmani_simple" VALUES(68,9,'68:9','ودوا۟ لو تدهن فيدهنون');
INSERT INTO "uthmani_simple" VALUES(68,10,'68:10','ولا تطع كل حلاف مهين');
INSERT INTO "uthmani_simple" VALUES(68,11,'68:11','هماز مشاءۭ بنميم');
INSERT INTO "uthmani_simple" VALUES(68,12,'68:12','مناع للخير معتد اثيم');
INSERT INTO "uthmani_simple" VALUES(68,13,'68:13','عتلۭ بعد ذلك زنيم');
INSERT INTO "uthmani_simple" VALUES(68,14,'68:14','ان كان ذا مال وبنين');
INSERT INTO "uthmani_simple" VALUES(68,15,'68:15','اذا تتلى عليه ءايتنا قال اسطير الاولين');
INSERT INTO "uthmani_simple" VALUES(68,16,'68:16','سنسمه على الخرطوم');
INSERT INTO "uthmani_simple" VALUES(68,17,'68:17','انا بلونهم كما بلونا اصحب الجنة اذ اقسموا۟ ليصرمنها مصبحين');
INSERT INTO "uthmani_simple" VALUES(68,18,'68:18','ولا يستثنون');
INSERT INTO "uthmani_simple" VALUES(68,19,'68:19','فطاف عليها طائف من ربك وهم نائمون');
INSERT INTO "uthmani_simple" VALUES(68,20,'68:20','فاصبحت كالصريم');
INSERT INTO "uthmani_simple" VALUES(68,21,'68:21','فتنادوا۟ مصبحين');
INSERT INTO "uthmani_simple" VALUES(68,22,'68:22','ان اغدوا۟ على حرثكم ان كنتم صرمين');
INSERT INTO "uthmani_simple" VALUES(68,23,'68:23','فانطلقوا۟ وهم يتخفتون');
INSERT INTO "uthmani_simple" VALUES(68,24,'68:24','ان لا يدخلنها اليوم عليكم مسكين');
INSERT INTO "uthmani_simple" VALUES(68,25,'68:25','وغدوا۟ على حرد قدرين');
INSERT INTO "uthmani_simple" VALUES(68,26,'68:26','فلما راوها قالوا۟ انا لضالون');
INSERT INTO "uthmani_simple" VALUES(68,27,'68:27','بل نحن محرومون');
INSERT INTO "uthmani_simple" VALUES(68,28,'68:28','قال اوسطهم الم اقل لكم لولا تسبحون');
INSERT INTO "uthmani_simple" VALUES(68,29,'68:29','قالوا۟ سبحن ربنا انا كنا ظلمين');
INSERT INTO "uthmani_simple" VALUES(68,30,'68:30','فاقبل بعضهم على بعض يتلومون');
INSERT INTO "uthmani_simple" VALUES(68,31,'68:31','قالوا۟ يويلنا انا كنا طغين');
INSERT INTO "uthmani_simple" VALUES(68,32,'68:32','عسى ربنا ان يبدلنا خيرا منها انا الى ربنا رغبون');
INSERT INTO "uthmani_simple" VALUES(68,33,'68:33','كذلك العذاب ولعذاب الءاخرة اكبر لو كانوا۟ يعلمون');
INSERT INTO "uthmani_simple" VALUES(68,34,'68:34','ان للمتقين عند ربهم جنت النعيم');
INSERT INTO "uthmani_simple" VALUES(68,35,'68:35','افنجعل المسلمين كالمجرمين');
INSERT INTO "uthmani_simple" VALUES(68,36,'68:36','ما لكم كيف تحكمون');
INSERT INTO "uthmani_simple" VALUES(68,37,'68:37','ام لكم كتب فيه تدرسون');
INSERT INTO "uthmani_simple" VALUES(68,38,'68:38','ان لكم فيه لما تخيرون');
INSERT INTO "uthmani_simple" VALUES(68,39,'68:39','ام لكم ايمن علينا بلغة الى يوم القيمة ان لكم لما تحكمون');
INSERT INTO "uthmani_simple" VALUES(68,40,'68:40','سلهم ايهم بذلك زعيم');
INSERT INTO "uthmani_simple" VALUES(68,41,'68:41','ام لهم شركاء فلياتوا۟ بشركائهم ان كانوا۟ صدقين');
INSERT INTO "uthmani_simple" VALUES(68,42,'68:42','يوم يكشف عن ساق ويدعون الى السجود فلا يستطيعون');
INSERT INTO "uthmani_simple" VALUES(68,43,'68:43','خشعة ابصرهم ترهقهم ذلة وقد كانوا۟ يدعون الى السجود وهم سلمون');
INSERT INTO "uthmani_simple" VALUES(68,44,'68:44','فذرنى ومن يكذب بهذا الحديث سنستدرجهم من حيث لا يعلمون');
INSERT INTO "uthmani_simple" VALUES(68,45,'68:45','واملى لهم ان كيدى متين');
INSERT INTO "uthmani_simple" VALUES(68,46,'68:46','ام تسـلهم اجرا فهم من مغرم مثقلون');
INSERT INTO "uthmani_simple" VALUES(68,47,'68:47','ام عندهم الغيب فهم يكتبون');
INSERT INTO "uthmani_simple" VALUES(68,48,'68:48','فاصبر لحكم ربك ولا تكن كصاحب الحوت اذ نادى وهو مكظوم');
INSERT INTO "uthmani_simple" VALUES(68,49,'68:49','لولا ان تدركه نعمة من ربه لنبذ بالعراء وهو مذموم');
INSERT INTO "uthmani_simple" VALUES(68,50,'68:50','فاجتبه ربه فجعله من الصلحين');
INSERT INTO "uthmani_simple" VALUES(68,51,'68:51','وان يكاد الذين كفروا۟ ليزلقونك بابصرهم لما سمعوا۟ الذكر ويقولون انه لمجنون');
INSERT INTO "uthmani_simple" VALUES(68,52,'68:52','وما هو الا ذكر للعلمين');
INSERT INTO "uthmani_simple" VALUES(69,1,'69:1','الحاقة');
INSERT INTO "uthmani_simple" VALUES(69,2,'69:2','ما الحاقة');
INSERT INTO "uthmani_simple" VALUES(69,3,'69:3','وما ادرىك ما الحاقة');
INSERT INTO "uthmani_simple" VALUES(69,4,'69:4','كذبت ثمود وعادۢ بالقارعة');
INSERT INTO "uthmani_simple" VALUES(69,5,'69:5','فاما ثمود فاهلكوا۟ بالطاغية');
INSERT INTO "uthmani_simple" VALUES(69,6,'69:6','واما عاد فاهلكوا۟ بريح صرصر عاتية');
INSERT INTO "uthmani_simple" VALUES(69,7,'69:7','سخرها عليهم سبع ليال وثمنية ايام حسوما فترى القوم فيها صرعى كانهم اعجاز نخل خاوية');
INSERT INTO "uthmani_simple" VALUES(69,8,'69:8','فهل ترى لهم منۢ باقية');
INSERT INTO "uthmani_simple" VALUES(69,9,'69:9','وجاء فرعون ومن قبله والمؤتفكت بالخاطئة');
INSERT INTO "uthmani_simple" VALUES(69,10,'69:10','فعصوا۟ رسول ربهم فاخذهم اخذة رابية');
INSERT INTO "uthmani_simple" VALUES(69,11,'69:11','انا لما طغا الماء حملنكم فى الجارية');
INSERT INTO "uthmani_simple" VALUES(69,12,'69:12','لنجعلها لكم تذكرة وتعيها اذن وعية');
INSERT INTO "uthmani_simple" VALUES(69,13,'69:13','فاذا نفخ فى الصور نفخة وحدة');
INSERT INTO "uthmani_simple" VALUES(69,14,'69:14','وحملت الارض والجبال فدكتا دكة وحدة');
INSERT INTO "uthmani_simple" VALUES(69,15,'69:15','فيومئذ وقعت الواقعة');
INSERT INTO "uthmani_simple" VALUES(69,16,'69:16','وانشقت السماء فهى يومئذ واهية');
INSERT INTO "uthmani_simple" VALUES(69,17,'69:17','والملك على ارجائها ويحمل عرش ربك فوقهم يومئذ ثمنية');
INSERT INTO "uthmani_simple" VALUES(69,18,'69:18','يومئذ تعرضون لا تخفى منكم خافية');
INSERT INTO "uthmani_simple" VALUES(69,19,'69:19','فاما من اوتى كتبه بيمينه فيقول هاؤم اقرءوا۟ كتبيه');
INSERT INTO "uthmani_simple" VALUES(69,20,'69:20','انى ظننت انى ملق حسابيه');
INSERT INTO "uthmani_simple" VALUES(69,21,'69:21','فهو فى عيشة راضية');
INSERT INTO "uthmani_simple" VALUES(69,22,'69:22','فى جنة عالية');
INSERT INTO "uthmani_simple" VALUES(69,23,'69:23','قطوفها دانية');
INSERT INTO "uthmani_simple" VALUES(69,24,'69:24','كلوا۟ واشربوا۟ هنيـۢا بما اسلفتم فى الايام الخالية');
INSERT INTO "uthmani_simple" VALUES(69,25,'69:25','واما من اوتى كتبه بشماله فيقول يليتنى لم اوت كتبيه');
INSERT INTO "uthmani_simple" VALUES(69,26,'69:26','ولم ادر ما حسابيه');
INSERT INTO "uthmani_simple" VALUES(69,27,'69:27','يليتها كانت القاضية');
INSERT INTO "uthmani_simple" VALUES(69,28,'69:28','ما اغنى عنى ماليه');
INSERT INTO "uthmani_simple" VALUES(69,29,'69:29','هلك عنى سلطنيه');
INSERT INTO "uthmani_simple" VALUES(69,30,'69:30','خذوه فغلوه');
INSERT INTO "uthmani_simple" VALUES(69,31,'69:31','ثم الجحيم صلوه');
INSERT INTO "uthmani_simple" VALUES(69,32,'69:32','ثم فى سلسلة ذرعها سبعون ذراعا فاسلكوه');
INSERT INTO "uthmani_simple" VALUES(69,33,'69:33','انه كان لا يؤمن بالله العظيم');
INSERT INTO "uthmani_simple" VALUES(69,34,'69:34','ولا يحض على طعام المسكين');
INSERT INTO "uthmani_simple" VALUES(69,35,'69:35','فليس له اليوم ههنا حميم');
INSERT INTO "uthmani_simple" VALUES(69,36,'69:36','ولا طعام الا من غسلين');
INSERT INTO "uthmani_simple" VALUES(69,37,'69:37','لا ياكله الا الخطـون');
INSERT INTO "uthmani_simple" VALUES(69,38,'69:38','فلا اقسم بما تبصرون');
INSERT INTO "uthmani_simple" VALUES(69,39,'69:39','وما لا تبصرون');
INSERT INTO "uthmani_simple" VALUES(69,40,'69:40','انه لقول رسول كريم');
INSERT INTO "uthmani_simple" VALUES(69,41,'69:41','وما هو بقول شاعر قليلا ما تؤمنون');
INSERT INTO "uthmani_simple" VALUES(69,42,'69:42','ولا بقول كاهن قليلا ما تذكرون');
INSERT INTO "uthmani_simple" VALUES(69,43,'69:43','تنزيل من رب العلمين');
INSERT INTO "uthmani_simple" VALUES(69,44,'69:44','ولو تقول علينا بعض الاقاويل');
INSERT INTO "uthmani_simple" VALUES(69,45,'69:45','لاخذنا منه باليمين');
INSERT INTO "uthmani_simple" VALUES(69,46,'69:46','ثم لقطعنا منه الوتين');
INSERT INTO "uthmani_simple" VALUES(69,47,'69:47','فما منكم من احد عنه حجزين');
INSERT INTO "uthmani_simple" VALUES(69,48,'69:48','وانه لتذكرة للمتقين');
INSERT INTO "uthmani_simple" VALUES(69,49,'69:49','وانا لنعلم ان منكم مكذبين');
INSERT INTO "uthmani_simple" VALUES(69,50,'69:50','وانه لحسرة على الكفرين');
INSERT INTO "uthmani_simple" VALUES(69,51,'69:51','وانه لحق اليقين');
INSERT INTO "uthmani_simple" VALUES(69,52,'69:52','فسبح باسم ربك العظيم');
INSERT INTO "uthmani_simple" VALUES(70,1,'70:1','سال سائلۢ بعذاب واقع');
INSERT INTO "uthmani_simple" VALUES(70,2,'70:2','للكفرين ليس له دافع');
INSERT INTO "uthmani_simple" VALUES(70,3,'70:3','من الله ذى المعارج');
INSERT INTO "uthmani_simple" VALUES(70,4,'70:4','تعرج الملئكة والروح اليه فى يوم كان مقداره خمسين الف سنة');
INSERT INTO "uthmani_simple" VALUES(70,5,'70:5','فاصبر صبرا جميلا');
INSERT INTO "uthmani_simple" VALUES(70,6,'70:6','انهم يرونه بعيدا');
INSERT INTO "uthmani_simple" VALUES(70,7,'70:7','ونرىه قريبا');
INSERT INTO "uthmani_simple" VALUES(70,8,'70:8','يوم تكون السماء كالمهل');
INSERT INTO "uthmani_simple" VALUES(70,9,'70:9','وتكون الجبال كالعهن');
INSERT INTO "uthmani_simple" VALUES(70,10,'70:10','ولا يسـل حميم حميما');
INSERT INTO "uthmani_simple" VALUES(70,11,'70:11','يبصرونهم يود المجرم لو يفتدى من عذاب يومئذۭ ببنيه');
INSERT INTO "uthmani_simple" VALUES(70,12,'70:12','وصحبته واخيه');
INSERT INTO "uthmani_simple" VALUES(70,13,'70:13','وفصيلته التى تـويه');
INSERT INTO "uthmani_simple" VALUES(70,14,'70:14','ومن فى الارض جميعا ثم ينجيه');
INSERT INTO "uthmani_simple" VALUES(70,15,'70:15','كلا انها لظى');
INSERT INTO "uthmani_simple" VALUES(70,16,'70:16','نزاعة للشوى');
INSERT INTO "uthmani_simple" VALUES(70,17,'70:17','تدعوا۟ من ادبر وتولى');
INSERT INTO "uthmani_simple" VALUES(70,18,'70:18','وجمع فاوعى');
INSERT INTO "uthmani_simple" VALUES(70,19,'70:19','ان الانسن خلق هلوعا');
INSERT INTO "uthmani_simple" VALUES(70,20,'70:20','اذا مسه الشر جزوعا');
INSERT INTO "uthmani_simple" VALUES(70,21,'70:21','واذا مسه الخير منوعا');
INSERT INTO "uthmani_simple" VALUES(70,22,'70:22','الا المصلين');
INSERT INTO "uthmani_simple" VALUES(70,23,'70:23','الذين هم على صلاتهم دائمون');
INSERT INTO "uthmani_simple" VALUES(70,24,'70:24','والذين فى امولهم حق معلوم');
INSERT INTO "uthmani_simple" VALUES(70,25,'70:25','للسائل والمحروم');
INSERT INTO "uthmani_simple" VALUES(70,26,'70:26','والذين يصدقون بيوم الدين');
INSERT INTO "uthmani_simple" VALUES(70,27,'70:27','والذين هم من عذاب ربهم مشفقون');
INSERT INTO "uthmani_simple" VALUES(70,28,'70:28','ان عذاب ربهم غير مامون');
INSERT INTO "uthmani_simple" VALUES(70,29,'70:29','والذين هم لفروجهم حفظون');
INSERT INTO "uthmani_simple" VALUES(70,30,'70:30','الا على ازوجهم او ما ملكت ايمنهم فانهم غير ملومين');
INSERT INTO "uthmani_simple" VALUES(70,31,'70:31','فمن ابتغى وراء ذلك فاو۟لئك هم العادون');
INSERT INTO "uthmani_simple" VALUES(70,32,'70:32','والذين هم لامنتهم وعهدهم رعون');
INSERT INTO "uthmani_simple" VALUES(70,33,'70:33','والذين هم بشهدتهم قائمون');
INSERT INTO "uthmani_simple" VALUES(70,34,'70:34','والذين هم على صلاتهم يحافظون');
INSERT INTO "uthmani_simple" VALUES(70,35,'70:35','او۟لئك فى جنت مكرمون');
INSERT INTO "uthmani_simple" VALUES(70,36,'70:36','فمال الذين كفروا۟ قبلك مهطعين');
INSERT INTO "uthmani_simple" VALUES(70,37,'70:37','عن اليمين وعن الشمال عزين');
INSERT INTO "uthmani_simple" VALUES(70,38,'70:38','ايطمع كل امرئ منهم ان يدخل جنة نعيم');
INSERT INTO "uthmani_simple" VALUES(70,39,'70:39','كلا انا خلقنهم مما يعلمون');
INSERT INTO "uthmani_simple" VALUES(70,40,'70:40','فلا اقسم برب المشرق والمغرب انا لقدرون');
INSERT INTO "uthmani_simple" VALUES(70,41,'70:41','على ان نبدل خيرا منهم وما نحن بمسبوقين');
INSERT INTO "uthmani_simple" VALUES(70,42,'70:42','فذرهم يخوضوا۟ ويلعبوا۟ حتى يلقوا۟ يومهم الذى يوعدون');
INSERT INTO "uthmani_simple" VALUES(70,43,'70:43','يوم يخرجون من الاجداث سراعا كانهم الى نصب يوفضون');
INSERT INTO "uthmani_simple" VALUES(70,44,'70:44','خشعة ابصرهم ترهقهم ذلة ذلك اليوم الذى كانوا۟ يوعدون');
INSERT INTO "uthmani_simple" VALUES(71,1,'71:1','انا ارسلنا نوحا الى قومه ان انذر قومك من قبل ان ياتيهم عذاب اليم');
INSERT INTO "uthmani_simple" VALUES(71,2,'71:2','قال يقوم انى لكم نذير مبين');
INSERT INTO "uthmani_simple" VALUES(71,3,'71:3','ان اعبدوا۟ الله واتقوه واطيعون');
INSERT INTO "uthmani_simple" VALUES(71,4,'71:4','يغفر لكم من ذنوبكم ويؤخركم الى اجل مسمى ان اجل الله اذا جاء لا يؤخر لو كنتم تعلمون');
INSERT INTO "uthmani_simple" VALUES(71,5,'71:5','قال رب انى دعوت قومى ليلا ونهارا');
INSERT INTO "uthmani_simple" VALUES(71,6,'71:6','فلم يزدهم دعاءى الا فرارا');
INSERT INTO "uthmani_simple" VALUES(71,7,'71:7','وانى كلما دعوتهم لتغفر لهم جعلوا۟ اصبعهم فى ءاذانهم واستغشوا۟ ثيابهم واصروا۟ واستكبروا۟ استكبارا');
INSERT INTO "uthmani_simple" VALUES(71,8,'71:8','ثم انى دعوتهم جهارا');
INSERT INTO "uthmani_simple" VALUES(71,9,'71:9','ثم انى اعلنت لهم واسررت لهم اسرارا');
INSERT INTO "uthmani_simple" VALUES(71,10,'71:10','فقلت استغفروا۟ ربكم انه كان غفارا');
INSERT INTO "uthmani_simple" VALUES(71,11,'71:11','يرسل السماء عليكم مدرارا');
INSERT INTO "uthmani_simple" VALUES(71,12,'71:12','ويمددكم بامول وبنين ويجعل لكم جنت ويجعل لكم انهرا');
INSERT INTO "uthmani_simple" VALUES(71,13,'71:13','ما لكم لا ترجون لله وقارا');
INSERT INTO "uthmani_simple" VALUES(71,14,'71:14','وقد خلقكم اطوارا');
INSERT INTO "uthmani_simple" VALUES(71,15,'71:15','الم تروا۟ كيف خلق الله سبع سموت طباقا');
INSERT INTO "uthmani_simple" VALUES(71,16,'71:16','وجعل القمر فيهن نورا وجعل الشمس سراجا');
INSERT INTO "uthmani_simple" VALUES(71,17,'71:17','والله انۢبتكم من الارض نباتا');
INSERT INTO "uthmani_simple" VALUES(71,18,'71:18','ثم يعيدكم فيها ويخرجكم اخراجا');
INSERT INTO "uthmani_simple" VALUES(71,19,'71:19','والله جعل لكم الارض بساطا');
INSERT INTO "uthmani_simple" VALUES(71,20,'71:20','لتسلكوا۟ منها سبلا فجاجا');
INSERT INTO "uthmani_simple" VALUES(71,21,'71:21','قال نوح رب انهم عصونى واتبعوا۟ من لم يزده ماله وولده الا خسارا');
INSERT INTO "uthmani_simple" VALUES(71,22,'71:22','ومكروا۟ مكرا كبارا');
INSERT INTO "uthmani_simple" VALUES(71,23,'71:23','وقالوا۟ لا تذرن ءالهتكم ولا تذرن ودا ولا سواعا ولا يغوث ويعوق ونسرا');
INSERT INTO "uthmani_simple" VALUES(71,24,'71:24','وقد اضلوا۟ كثيرا ولا تزد الظلمين الا ضللا');
INSERT INTO "uthmani_simple" VALUES(71,25,'71:25','مما خطيـتهم اغرقوا۟ فادخلوا۟ نارا فلم يجدوا۟ لهم من دون الله انصارا');
INSERT INTO "uthmani_simple" VALUES(71,26,'71:26','وقال نوح رب لا تذر على الارض من الكفرين ديارا');
INSERT INTO "uthmani_simple" VALUES(71,27,'71:27','انك ان تذرهم يضلوا۟ عبادك ولا يلدوا۟ الا فاجرا كفارا');
INSERT INTO "uthmani_simple" VALUES(71,28,'71:28','رب اغفر لى ولولدى ولمن دخل بيتى مؤمنا وللمؤمنين والمؤمنت ولا تزد الظلمين الا تبارۢا');
INSERT INTO "uthmani_simple" VALUES(72,1,'72:1','قل اوحى الى انه استمع نفر من الجن فقالوا۟ انا سمعنا قرءانا عجبا');
INSERT INTO "uthmani_simple" VALUES(72,2,'72:2','يهدى الى الرشد فـامنا به ولن نشرك بربنا احدا');
INSERT INTO "uthmani_simple" VALUES(72,3,'72:3','وانه تعلى جد ربنا ما اتخذ صحبة ولا ولدا');
INSERT INTO "uthmani_simple" VALUES(72,4,'72:4','وانه كان يقول سفيهنا على الله شططا');
INSERT INTO "uthmani_simple" VALUES(72,5,'72:5','وانا ظننا ان لن تقول الانس والجن على الله كذبا');
INSERT INTO "uthmani_simple" VALUES(72,6,'72:6','وانه كان رجال من الانس يعوذون برجال من الجن فزادوهم رهقا');
INSERT INTO "uthmani_simple" VALUES(72,7,'72:7','وانهم ظنوا۟ كما ظننتم ان لن يبعث الله احدا');
INSERT INTO "uthmani_simple" VALUES(72,8,'72:8','وانا لمسنا السماء فوجدنها ملئت حرسا شديدا وشهبا');
INSERT INTO "uthmani_simple" VALUES(72,9,'72:9','وانا كنا نقعد منها مقعد للسمع فمن يستمع الءان يجد له شهابا رصدا');
INSERT INTO "uthmani_simple" VALUES(72,10,'72:10','وانا لا ندرى اشر اريد بمن فى الارض ام اراد بهم ربهم رشدا');
INSERT INTO "uthmani_simple" VALUES(72,11,'72:11','وانا منا الصلحون ومنا دون ذلك كنا طرائق قددا');
INSERT INTO "uthmani_simple" VALUES(72,12,'72:12','وانا ظننا ان لن نعجز الله فى الارض ولن نعجزه هربا');
INSERT INTO "uthmani_simple" VALUES(72,13,'72:13','وانا لما سمعنا الهدى ءامنا به فمن يؤمنۢ بربه فلا يخاف بخسا ولا رهقا');
INSERT INTO "uthmani_simple" VALUES(72,14,'72:14','وانا منا المسلمون ومنا القسطون فمن اسلم فاو۟لئك تحروا۟ رشدا');
INSERT INTO "uthmani_simple" VALUES(72,15,'72:15','واما القسطون فكانوا۟ لجهنم حطبا');
INSERT INTO "uthmani_simple" VALUES(72,16,'72:16','والو استقموا۟ على الطريقة لاسقينهم ماء غدقا');
INSERT INTO "uthmani_simple" VALUES(72,17,'72:17','لنفتنهم فيه ومن يعرض عن ذكر ربه يسلكه عذابا صعدا');
INSERT INTO "uthmani_simple" VALUES(72,18,'72:18','وان المسجد لله فلا تدعوا۟ مع الله احدا');
INSERT INTO "uthmani_simple" VALUES(72,19,'72:19','وانه لما قام عبد الله يدعوه كادوا۟ يكونون عليه لبدا');
INSERT INTO "uthmani_simple" VALUES(72,20,'72:20','قل انما ادعوا۟ ربى ولا اشرك به احدا');
INSERT INTO "uthmani_simple" VALUES(72,21,'72:21','قل انى لا املك لكم ضرا ولا رشدا');
INSERT INTO "uthmani_simple" VALUES(72,22,'72:22','قل انى لن يجيرنى من الله احد ولن اجد من دونه ملتحدا');
INSERT INTO "uthmani_simple" VALUES(72,23,'72:23','الا بلغا من الله ورسلته ومن يعص الله ورسوله فان له نار جهنم خلدين فيها ابدا');
INSERT INTO "uthmani_simple" VALUES(72,24,'72:24','حتى اذا راوا۟ ما يوعدون فسيعلمون من اضعف ناصرا واقل عددا');
INSERT INTO "uthmani_simple" VALUES(72,25,'72:25','قل ان ادرى اقريب ما توعدون ام يجعل له ربى امدا');
INSERT INTO "uthmani_simple" VALUES(72,26,'72:26','علم الغيب فلا يظهر على غيبه احدا');
INSERT INTO "uthmani_simple" VALUES(72,27,'72:27','الا من ارتضى من رسول فانه يسلك منۢ بين يديه ومن خلفه رصدا');
INSERT INTO "uthmani_simple" VALUES(72,28,'72:28','ليعلم ان قد ابلغوا۟ رسلت ربهم واحاط بما لديهم واحصى كل شىء عددۢا');
INSERT INTO "uthmani_simple" VALUES(73,1,'73:1','يايها المزمل');
INSERT INTO "uthmani_simple" VALUES(73,2,'73:2','قم اليل الا قليلا');
INSERT INTO "uthmani_simple" VALUES(73,3,'73:3','نصفه او انقص منه قليلا');
INSERT INTO "uthmani_simple" VALUES(73,4,'73:4','او زد عليه ورتل القرءان ترتيلا');
INSERT INTO "uthmani_simple" VALUES(73,5,'73:5','انا سنلقى عليك قولا ثقيلا');
INSERT INTO "uthmani_simple" VALUES(73,6,'73:6','ان ناشئة اليل هى اشد وطـا واقوم قيلا');
INSERT INTO "uthmani_simple" VALUES(73,7,'73:7','ان لك فى النهار سبحا طويلا');
INSERT INTO "uthmani_simple" VALUES(73,8,'73:8','واذكر اسم ربك وتبتل اليه تبتيلا');
INSERT INTO "uthmani_simple" VALUES(73,9,'73:9','رب المشرق والمغرب لا اله الا هو فاتخذه وكيلا');
INSERT INTO "uthmani_simple" VALUES(73,10,'73:10','واصبر على ما يقولون واهجرهم هجرا جميلا');
INSERT INTO "uthmani_simple" VALUES(73,11,'73:11','وذرنى والمكذبين او۟لى النعمة ومهلهم قليلا');
INSERT INTO "uthmani_simple" VALUES(73,12,'73:12','ان لدينا انكالا وجحيما');
INSERT INTO "uthmani_simple" VALUES(73,13,'73:13','وطعاما ذا غصة وعذابا اليما');
INSERT INTO "uthmani_simple" VALUES(73,14,'73:14','يوم ترجف الارض والجبال وكانت الجبال كثيبا مهيلا');
INSERT INTO "uthmani_simple" VALUES(73,15,'73:15','انا ارسلنا اليكم رسولا شهدا عليكم كما ارسلنا الى فرعون رسولا');
INSERT INTO "uthmani_simple" VALUES(73,16,'73:16','فعصى فرعون الرسول فاخذنه اخذا وبيلا');
INSERT INTO "uthmani_simple" VALUES(73,17,'73:17','فكيف تتقون ان كفرتم يوما يجعل الولدن شيبا');
INSERT INTO "uthmani_simple" VALUES(73,18,'73:18','السماء منفطرۢ به كان وعده مفعولا');
INSERT INTO "uthmani_simple" VALUES(73,19,'73:19','ان هذه تذكرة فمن شاء اتخذ الى ربه سبيلا');
INSERT INTO "uthmani_simple" VALUES(73,20,'73:20','ان ربك يعلم انك تقوم ادنى من ثلثى اليل ونصفه وثلثه وطائفة من الذين معك والله يقدر اليل والنهار علم ان لن تحصوه فتاب عليكم فاقرءوا۟ ما تيسر من القرءان علم ان سيكون منكم مرضى وءاخرون يضربون فى الارض يبتغون من فضل الله وءاخرون يقتلون فى سبيل الله فاقرءوا۟ ما تيسر منه واقيموا۟ الصلوة وءاتوا۟ الزكوة واقرضوا۟ الله قرضا حسنا وما تقدموا۟ لانفسكم من خير تجدوه عند الله هو خيرا واعظم اجرا واستغفروا۟ الله ان الله غفور رحيمۢ');
INSERT INTO "uthmani_simple" VALUES(74,1,'74:1','يايها المدثر');
INSERT INTO "uthmani_simple" VALUES(74,2,'74:2','قم فانذر');
INSERT INTO "uthmani_simple" VALUES(74,3,'74:3','وربك فكبر');
INSERT INTO "uthmani_simple" VALUES(74,4,'74:4','وثيابك فطهر');
INSERT INTO "uthmani_simple" VALUES(74,5,'74:5','والرجز فاهجر');
INSERT INTO "uthmani_simple" VALUES(74,6,'74:6','ولا تمنن تستكثر');
INSERT INTO "uthmani_simple" VALUES(74,7,'74:7','ولربك فاصبر');
INSERT INTO "uthmani_simple" VALUES(74,8,'74:8','فاذا نقر فى الناقور');
INSERT INTO "uthmani_simple" VALUES(74,9,'74:9','فذلك يومئذ يوم عسير');
INSERT INTO "uthmani_simple" VALUES(74,10,'74:10','على الكفرين غير يسير');
INSERT INTO "uthmani_simple" VALUES(74,11,'74:11','ذرنى ومن خلقت وحيدا');
INSERT INTO "uthmani_simple" VALUES(74,12,'74:12','وجعلت له مالا ممدودا');
INSERT INTO "uthmani_simple" VALUES(74,13,'74:13','وبنين شهودا');
INSERT INTO "uthmani_simple" VALUES(74,14,'74:14','ومهدت له تمهيدا');
INSERT INTO "uthmani_simple" VALUES(74,15,'74:15','ثم يطمع ان ازيد');
INSERT INTO "uthmani_simple" VALUES(74,16,'74:16','كلا انه كان لءايتنا عنيدا');
INSERT INTO "uthmani_simple" VALUES(74,17,'74:17','سارهقه صعودا');
INSERT INTO "uthmani_simple" VALUES(74,18,'74:18','انه فكر وقدر');
INSERT INTO "uthmani_simple" VALUES(74,19,'74:19','فقتل كيف قدر');
INSERT INTO "uthmani_simple" VALUES(74,20,'74:20','ثم قتل كيف قدر');
INSERT INTO "uthmani_simple" VALUES(74,21,'74:21','ثم نظر');
INSERT INTO "uthmani_simple" VALUES(74,22,'74:22','ثم عبس وبسر');
INSERT INTO "uthmani_simple" VALUES(74,23,'74:23','ثم ادبر واستكبر');
INSERT INTO "uthmani_simple" VALUES(74,24,'74:24','فقال ان هذا الا سحر يؤثر');
INSERT INTO "uthmani_simple" VALUES(74,25,'74:25','ان هذا الا قول البشر');
INSERT INTO "uthmani_simple" VALUES(74,26,'74:26','ساصليه سقر');
INSERT INTO "uthmani_simple" VALUES(74,27,'74:27','وما ادرىك ما سقر');
INSERT INTO "uthmani_simple" VALUES(74,28,'74:28','لا تبقى ولا تذر');
INSERT INTO "uthmani_simple" VALUES(74,29,'74:29','لواحة للبشر');
INSERT INTO "uthmani_simple" VALUES(74,30,'74:30','عليها تسعة عشر');
INSERT INTO "uthmani_simple" VALUES(74,31,'74:31','وما جعلنا اصحب النار الا ملئكة وما جعلنا عدتهم الا فتنة للذين كفروا۟ ليستيقن الذين اوتوا۟ الكتب ويزداد الذين ءامنوا۟ ايمنا ولا يرتاب الذين اوتوا۟ الكتب والمؤمنون وليقول الذين فى قلوبهم مرض والكفرون ماذا اراد الله بهذا مثلا كذلك يضل الله من يشاء ويهدى من يشاء وما يعلم جنود ربك الا هو وما هى الا ذكرى للبشر');
INSERT INTO "uthmani_simple" VALUES(74,32,'74:32','كلا والقمر');
INSERT INTO "uthmani_simple" VALUES(74,33,'74:33','واليل اذ ادبر');
INSERT INTO "uthmani_simple" VALUES(74,34,'74:34','والصبح اذا اسفر');
INSERT INTO "uthmani_simple" VALUES(74,35,'74:35','انها لاحدى الكبر');
INSERT INTO "uthmani_simple" VALUES(74,36,'74:36','نذيرا للبشر');
INSERT INTO "uthmani_simple" VALUES(74,37,'74:37','لمن شاء منكم ان يتقدم او يتاخر');
INSERT INTO "uthmani_simple" VALUES(74,38,'74:38','كل نفسۭ بما كسبت رهينة');
INSERT INTO "uthmani_simple" VALUES(74,39,'74:39','الا اصحب اليمين');
INSERT INTO "uthmani_simple" VALUES(74,40,'74:40','فى جنت يتساءلون');
INSERT INTO "uthmani_simple" VALUES(74,41,'74:41','عن المجرمين');
INSERT INTO "uthmani_simple" VALUES(74,42,'74:42','ما سلككم فى سقر');
INSERT INTO "uthmani_simple" VALUES(74,43,'74:43','قالوا۟ لم نك من المصلين');
INSERT INTO "uthmani_simple" VALUES(74,44,'74:44','ولم نك نطعم المسكين');
INSERT INTO "uthmani_simple" VALUES(74,45,'74:45','وكنا نخوض مع الخائضين');
INSERT INTO "uthmani_simple" VALUES(74,46,'74:46','وكنا نكذب بيوم الدين');
INSERT INTO "uthmani_simple" VALUES(74,47,'74:47','حتى اتىنا اليقين');
INSERT INTO "uthmani_simple" VALUES(74,48,'74:48','فما تنفعهم شفعة الشفعين');
INSERT INTO "uthmani_simple" VALUES(74,49,'74:49','فما لهم عن التذكرة معرضين');
INSERT INTO "uthmani_simple" VALUES(74,50,'74:50','كانهم حمر مستنفرة');
INSERT INTO "uthmani_simple" VALUES(74,51,'74:51','فرت من قسورةۭ');
INSERT INTO "uthmani_simple" VALUES(74,52,'74:52','بل يريد كل امرئ منهم ان يؤتى صحفا منشرة');
INSERT INTO "uthmani_simple" VALUES(74,53,'74:53','كلا بل لا يخافون الءاخرة');
INSERT INTO "uthmani_simple" VALUES(74,54,'74:54','كلا انه تذكرة');
INSERT INTO "uthmani_simple" VALUES(74,55,'74:55','فمن شاء ذكره');
INSERT INTO "uthmani_simple" VALUES(74,56,'74:56','وما يذكرون الا ان يشاء الله هو اهل التقوى واهل المغفرة');
INSERT INTO "uthmani_simple" VALUES(75,1,'75:1','لا اقسم بيوم القيمة');
INSERT INTO "uthmani_simple" VALUES(75,2,'75:2','ولا اقسم بالنفس اللوامة');
INSERT INTO "uthmani_simple" VALUES(75,3,'75:3','ايحسب الانسن الن نجمع عظامه');
INSERT INTO "uthmani_simple" VALUES(75,4,'75:4','بلى قدرين على ان نسوى بنانه');
INSERT INTO "uthmani_simple" VALUES(75,5,'75:5','بل يريد الانسن ليفجر امامه');
INSERT INTO "uthmani_simple" VALUES(75,6,'75:6','يسـل ايان يوم القيمة');
INSERT INTO "uthmani_simple" VALUES(75,7,'75:7','فاذا برق البصر');
INSERT INTO "uthmani_simple" VALUES(75,8,'75:8','وخسف القمر');
INSERT INTO "uthmani_simple" VALUES(75,9,'75:9','وجمع الشمس والقمر');
INSERT INTO "uthmani_simple" VALUES(75,10,'75:10','يقول الانسن يومئذ اين المفر');
INSERT INTO "uthmani_simple" VALUES(75,11,'75:11','كلا لا وزر');
INSERT INTO "uthmani_simple" VALUES(75,12,'75:12','الى ربك يومئذ المستقر');
INSERT INTO "uthmani_simple" VALUES(75,13,'75:13','ينبؤا۟ الانسن يومئذۭ بما قدم واخر');
INSERT INTO "uthmani_simple" VALUES(75,14,'75:14','بل الانسن على نفسه بصيرة');
INSERT INTO "uthmani_simple" VALUES(75,15,'75:15','ولو القى معاذيره');
INSERT INTO "uthmani_simple" VALUES(75,16,'75:16','لا تحرك به لسانك لتعجل به');
INSERT INTO "uthmani_simple" VALUES(75,17,'75:17','ان علينا جمعه وقرءانه');
INSERT INTO "uthmani_simple" VALUES(75,18,'75:18','فاذا قرانه فاتبع قرءانه');
INSERT INTO "uthmani_simple" VALUES(75,19,'75:19','ثم ان علينا بيانه');
INSERT INTO "uthmani_simple" VALUES(75,20,'75:20','كلا بل تحبون العاجلة');
INSERT INTO "uthmani_simple" VALUES(75,21,'75:21','وتذرون الءاخرة');
INSERT INTO "uthmani_simple" VALUES(75,22,'75:22','وجوه يومئذ ناضرة');
INSERT INTO "uthmani_simple" VALUES(75,23,'75:23','الى ربها ناظرة');
INSERT INTO "uthmani_simple" VALUES(75,24,'75:24','ووجوه يومئذۭ باسرة');
INSERT INTO "uthmani_simple" VALUES(75,25,'75:25','تظن ان يفعل بها فاقرة');
INSERT INTO "uthmani_simple" VALUES(75,26,'75:26','كلا اذا بلغت التراقى');
INSERT INTO "uthmani_simple" VALUES(75,27,'75:27','وقيل من راق');
INSERT INTO "uthmani_simple" VALUES(75,28,'75:28','وظن انه الفراق');
INSERT INTO "uthmani_simple" VALUES(75,29,'75:29','والتفت الساق بالساق');
INSERT INTO "uthmani_simple" VALUES(75,30,'75:30','الى ربك يومئذ المساق');
INSERT INTO "uthmani_simple" VALUES(75,31,'75:31','فلا صدق ولا صلى');
INSERT INTO "uthmani_simple" VALUES(75,32,'75:32','ولكن كذب وتولى');
INSERT INTO "uthmani_simple" VALUES(75,33,'75:33','ثم ذهب الى اهله يتمطى');
INSERT INTO "uthmani_simple" VALUES(75,34,'75:34','اولى لك فاولى');
INSERT INTO "uthmani_simple" VALUES(75,35,'75:35','ثم اولى لك فاولى');
INSERT INTO "uthmani_simple" VALUES(75,36,'75:36','ايحسب الانسن ان يترك سدى');
INSERT INTO "uthmani_simple" VALUES(75,37,'75:37','الم يك نطفة من منى يمنى');
INSERT INTO "uthmani_simple" VALUES(75,38,'75:38','ثم كان علقة فخلق فسوى');
INSERT INTO "uthmani_simple" VALUES(75,39,'75:39','فجعل منه الزوجين الذكر والانثى');
INSERT INTO "uthmani_simple" VALUES(75,40,'75:40','اليس ذلك بقدر على ان يحى الموتى');
INSERT INTO "uthmani_simple" VALUES(76,1,'76:1','هل اتى على الانسن حين من الدهر لم يكن شيـا مذكورا');
INSERT INTO "uthmani_simple" VALUES(76,2,'76:2','انا خلقنا الانسن من نطفة امشاج نبتليه فجعلنه سميعۢا بصيرا');
INSERT INTO "uthmani_simple" VALUES(76,3,'76:3','انا هدينه السبيل اما شاكرا واما كفورا');
INSERT INTO "uthmani_simple" VALUES(76,4,'76:4','انا اعتدنا للكفرين سلسلا۟ واغللا وسعيرا');
INSERT INTO "uthmani_simple" VALUES(76,5,'76:5','ان الابرار يشربون من كاس كان مزاجها كافورا');
INSERT INTO "uthmani_simple" VALUES(76,6,'76:6','عينا يشرب بها عباد الله يفجرونها تفجيرا');
INSERT INTO "uthmani_simple" VALUES(76,7,'76:7','يوفون بالنذر ويخافون يوما كان شره مستطيرا');
INSERT INTO "uthmani_simple" VALUES(76,8,'76:8','ويطعمون الطعام على حبه مسكينا ويتيما واسيرا');
INSERT INTO "uthmani_simple" VALUES(76,9,'76:9','انما نطعمكم لوجه الله لا نريد منكم جزاء ولا شكورا');
INSERT INTO "uthmani_simple" VALUES(76,10,'76:10','انا نخاف من ربنا يوما عبوسا قمطريرا');
INSERT INTO "uthmani_simple" VALUES(76,11,'76:11','فوقىهم الله شر ذلك اليوم ولقىهم نضرة وسرورا');
INSERT INTO "uthmani_simple" VALUES(76,12,'76:12','وجزىهم بما صبروا۟ جنة وحريرا');
INSERT INTO "uthmani_simple" VALUES(76,13,'76:13','متكـين فيها على الارائك لا يرون فيها شمسا ولا زمهريرا');
INSERT INTO "uthmani_simple" VALUES(76,14,'76:14','ودانية عليهم ظللها وذللت قطوفها تذليلا');
INSERT INTO "uthmani_simple" VALUES(76,15,'76:15','ويطاف عليهم بـانية من فضة واكواب كانت قواريرا۠');
INSERT INTO "uthmani_simple" VALUES(76,16,'76:16','قواريرا۟ من فضة قدروها تقديرا');
INSERT INTO "uthmani_simple" VALUES(76,17,'76:17','ويسقون فيها كاسا كان مزاجها زنجبيلا');
INSERT INTO "uthmani_simple" VALUES(76,18,'76:18','عينا فيها تسمى سلسبيلا');
INSERT INTO "uthmani_simple" VALUES(76,19,'76:19','ويطوف عليهم ولدن مخلدون اذا رايتهم حسبتهم لؤلؤا منثورا');
INSERT INTO "uthmani_simple" VALUES(76,20,'76:20','واذا رايت ثم رايت نعيما وملكا كبيرا');
INSERT INTO "uthmani_simple" VALUES(76,21,'76:21','عليهم ثياب سندس خضر واستبرق وحلوا۟ اساور من فضة وسقىهم ربهم شرابا طهورا');
INSERT INTO "uthmani_simple" VALUES(76,22,'76:22','ان هذا كان لكم جزاء وكان سعيكم مشكورا');
INSERT INTO "uthmani_simple" VALUES(76,23,'76:23','انا نحن نزلنا عليك القرءان تنزيلا');
INSERT INTO "uthmani_simple" VALUES(76,24,'76:24','فاصبر لحكم ربك ولا تطع منهم ءاثما او كفورا');
INSERT INTO "uthmani_simple" VALUES(76,25,'76:25','واذكر اسم ربك بكرة واصيلا');
INSERT INTO "uthmani_simple" VALUES(76,26,'76:26','ومن اليل فاسجد له وسبحه ليلا طويلا');
INSERT INTO "uthmani_simple" VALUES(76,27,'76:27','ان هؤلاء يحبون العاجلة ويذرون وراءهم يوما ثقيلا');
INSERT INTO "uthmani_simple" VALUES(76,28,'76:28','نحن خلقنهم وشددنا اسرهم واذا شئنا بدلنا امثلهم تبديلا');
INSERT INTO "uthmani_simple" VALUES(76,29,'76:29','ان هذه تذكرة فمن شاء اتخذ الى ربه سبيلا');
INSERT INTO "uthmani_simple" VALUES(76,30,'76:30','وما تشاءون الا ان يشاء الله ان الله كان عليما حكيما');
INSERT INTO "uthmani_simple" VALUES(76,31,'76:31','يدخل من يشاء فى رحمته والظلمين اعد لهم عذابا اليمۢا');
INSERT INTO "uthmani_simple" VALUES(77,1,'77:1','والمرسلت عرفا');
INSERT INTO "uthmani_simple" VALUES(77,2,'77:2','فالعصفت عصفا');
INSERT INTO "uthmani_simple" VALUES(77,3,'77:3','والنشرت نشرا');
INSERT INTO "uthmani_simple" VALUES(77,4,'77:4','فالفرقت فرقا');
INSERT INTO "uthmani_simple" VALUES(77,5,'77:5','فالملقيت ذكرا');
INSERT INTO "uthmani_simple" VALUES(77,6,'77:6','عذرا او نذرا');
INSERT INTO "uthmani_simple" VALUES(77,7,'77:7','انما توعدون لوقع');
INSERT INTO "uthmani_simple" VALUES(77,8,'77:8','فاذا النجوم طمست');
INSERT INTO "uthmani_simple" VALUES(77,9,'77:9','واذا السماء فرجت');
INSERT INTO "uthmani_simple" VALUES(77,10,'77:10','واذا الجبال نسفت');
INSERT INTO "uthmani_simple" VALUES(77,11,'77:11','واذا الرسل اقتت');
INSERT INTO "uthmani_simple" VALUES(77,12,'77:12','لاى يوم اجلت');
INSERT INTO "uthmani_simple" VALUES(77,13,'77:13','ليوم الفصل');
INSERT INTO "uthmani_simple" VALUES(77,14,'77:14','وما ادرىك ما يوم الفصل');
INSERT INTO "uthmani_simple" VALUES(77,15,'77:15','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(77,16,'77:16','الم نهلك الاولين');
INSERT INTO "uthmani_simple" VALUES(77,17,'77:17','ثم نتبعهم الءاخرين');
INSERT INTO "uthmani_simple" VALUES(77,18,'77:18','كذلك نفعل بالمجرمين');
INSERT INTO "uthmani_simple" VALUES(77,19,'77:19','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(77,20,'77:20','الم نخلقكم من ماء مهين');
INSERT INTO "uthmani_simple" VALUES(77,21,'77:21','فجعلنه فى قرار مكين');
INSERT INTO "uthmani_simple" VALUES(77,22,'77:22','الى قدر معلوم');
INSERT INTO "uthmani_simple" VALUES(77,23,'77:23','فقدرنا فنعم القدرون');
INSERT INTO "uthmani_simple" VALUES(77,24,'77:24','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(77,25,'77:25','الم نجعل الارض كفاتا');
INSERT INTO "uthmani_simple" VALUES(77,26,'77:26','احياء واموتا');
INSERT INTO "uthmani_simple" VALUES(77,27,'77:27','وجعلنا فيها روسى شمخت واسقينكم ماء فراتا');
INSERT INTO "uthmani_simple" VALUES(77,28,'77:28','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(77,29,'77:29','انطلقوا۟ الى ما كنتم به تكذبون');
INSERT INTO "uthmani_simple" VALUES(77,30,'77:30','انطلقوا۟ الى ظل ذى ثلث شعب');
INSERT INTO "uthmani_simple" VALUES(77,31,'77:31','لا ظليل ولا يغنى من اللهب');
INSERT INTO "uthmani_simple" VALUES(77,32,'77:32','انها ترمى بشرر كالقصر');
INSERT INTO "uthmani_simple" VALUES(77,33,'77:33','كانه جملت صفر');
INSERT INTO "uthmani_simple" VALUES(77,34,'77:34','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(77,35,'77:35','هذا يوم لا ينطقون');
INSERT INTO "uthmani_simple" VALUES(77,36,'77:36','ولا يؤذن لهم فيعتذرون');
INSERT INTO "uthmani_simple" VALUES(77,37,'77:37','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(77,38,'77:38','هذا يوم الفصل جمعنكم والاولين');
INSERT INTO "uthmani_simple" VALUES(77,39,'77:39','فان كان لكم كيد فكيدون');
INSERT INTO "uthmani_simple" VALUES(77,40,'77:40','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(77,41,'77:41','ان المتقين فى ظلل وعيون');
INSERT INTO "uthmani_simple" VALUES(77,42,'77:42','وفوكه مما يشتهون');
INSERT INTO "uthmani_simple" VALUES(77,43,'77:43','كلوا۟ واشربوا۟ هنيـۢا بما كنتم تعملون');
INSERT INTO "uthmani_simple" VALUES(77,44,'77:44','انا كذلك نجزى المحسنين');
INSERT INTO "uthmani_simple" VALUES(77,45,'77:45','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(77,46,'77:46','كلوا۟ وتمتعوا۟ قليلا انكم مجرمون');
INSERT INTO "uthmani_simple" VALUES(77,47,'77:47','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(77,48,'77:48','واذا قيل لهم اركعوا۟ لا يركعون');
INSERT INTO "uthmani_simple" VALUES(77,49,'77:49','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(77,50,'77:50','فباى حديثۭ بعده يؤمنون');
INSERT INTO "uthmani_simple" VALUES(78,1,'78:1','عم يتساءلون');
INSERT INTO "uthmani_simple" VALUES(78,2,'78:2','عن النبا العظيم');
INSERT INTO "uthmani_simple" VALUES(78,3,'78:3','الذى هم فيه مختلفون');
INSERT INTO "uthmani_simple" VALUES(78,4,'78:4','كلا سيعلمون');
INSERT INTO "uthmani_simple" VALUES(78,5,'78:5','ثم كلا سيعلمون');
INSERT INTO "uthmani_simple" VALUES(78,6,'78:6','الم نجعل الارض مهدا');
INSERT INTO "uthmani_simple" VALUES(78,7,'78:7','والجبال اوتادا');
INSERT INTO "uthmani_simple" VALUES(78,8,'78:8','وخلقنكم ازوجا');
INSERT INTO "uthmani_simple" VALUES(78,9,'78:9','وجعلنا نومكم سباتا');
INSERT INTO "uthmani_simple" VALUES(78,10,'78:10','وجعلنا اليل لباسا');
INSERT INTO "uthmani_simple" VALUES(78,11,'78:11','وجعلنا النهار معاشا');
INSERT INTO "uthmani_simple" VALUES(78,12,'78:12','وبنينا فوقكم سبعا شدادا');
INSERT INTO "uthmani_simple" VALUES(78,13,'78:13','وجعلنا سراجا وهاجا');
INSERT INTO "uthmani_simple" VALUES(78,14,'78:14','وانزلنا من المعصرت ماء ثجاجا');
INSERT INTO "uthmani_simple" VALUES(78,15,'78:15','لنخرج به حبا ونباتا');
INSERT INTO "uthmani_simple" VALUES(78,16,'78:16','وجنت الفافا');
INSERT INTO "uthmani_simple" VALUES(78,17,'78:17','ان يوم الفصل كان ميقتا');
INSERT INTO "uthmani_simple" VALUES(78,18,'78:18','يوم ينفخ فى الصور فتاتون افواجا');
INSERT INTO "uthmani_simple" VALUES(78,19,'78:19','وفتحت السماء فكانت ابوبا');
INSERT INTO "uthmani_simple" VALUES(78,20,'78:20','وسيرت الجبال فكانت سرابا');
INSERT INTO "uthmani_simple" VALUES(78,21,'78:21','ان جهنم كانت مرصادا');
INSERT INTO "uthmani_simple" VALUES(78,22,'78:22','للطغين مـابا');
INSERT INTO "uthmani_simple" VALUES(78,23,'78:23','لبثين فيها احقابا');
INSERT INTO "uthmani_simple" VALUES(78,24,'78:24','لا يذوقون فيها بردا ولا شرابا');
INSERT INTO "uthmani_simple" VALUES(78,25,'78:25','الا حميما وغساقا');
INSERT INTO "uthmani_simple" VALUES(78,26,'78:26','جزاء وفاقا');
INSERT INTO "uthmani_simple" VALUES(78,27,'78:27','انهم كانوا۟ لا يرجون حسابا');
INSERT INTO "uthmani_simple" VALUES(78,28,'78:28','وكذبوا۟ بـايتنا كذابا');
INSERT INTO "uthmani_simple" VALUES(78,29,'78:29','وكل شىء احصينه كتبا');
INSERT INTO "uthmani_simple" VALUES(78,30,'78:30','فذوقوا۟ فلن نزيدكم الا عذابا');
INSERT INTO "uthmani_simple" VALUES(78,31,'78:31','ان للمتقين مفازا');
INSERT INTO "uthmani_simple" VALUES(78,32,'78:32','حدائق واعنبا');
INSERT INTO "uthmani_simple" VALUES(78,33,'78:33','وكواعب اترابا');
INSERT INTO "uthmani_simple" VALUES(78,34,'78:34','وكاسا دهاقا');
INSERT INTO "uthmani_simple" VALUES(78,35,'78:35','لا يسمعون فيها لغوا ولا كذبا');
INSERT INTO "uthmani_simple" VALUES(78,36,'78:36','جزاء من ربك عطاء حسابا');
INSERT INTO "uthmani_simple" VALUES(78,37,'78:37','رب السموت والارض وما بينهما الرحمن لا يملكون منه خطابا');
INSERT INTO "uthmani_simple" VALUES(78,38,'78:38','يوم يقوم الروح والملئكة صفا لا يتكلمون الا من اذن له الرحمن وقال صوابا');
INSERT INTO "uthmani_simple" VALUES(78,39,'78:39','ذلك اليوم الحق فمن شاء اتخذ الى ربه مـابا');
INSERT INTO "uthmani_simple" VALUES(78,40,'78:40','انا انذرنكم عذابا قريبا يوم ينظر المرء ما قدمت يداه ويقول الكافر يليتنى كنت تربۢا');
INSERT INTO "uthmani_simple" VALUES(79,1,'79:1','والنزعت غرقا');
INSERT INTO "uthmani_simple" VALUES(79,2,'79:2','والنشطت نشطا');
INSERT INTO "uthmani_simple" VALUES(79,3,'79:3','والسبحت سبحا');
INSERT INTO "uthmani_simple" VALUES(79,4,'79:4','فالسبقت سبقا');
INSERT INTO "uthmani_simple" VALUES(79,5,'79:5','فالمدبرت امرا');
INSERT INTO "uthmani_simple" VALUES(79,6,'79:6','يوم ترجف الراجفة');
INSERT INTO "uthmani_simple" VALUES(79,7,'79:7','تتبعها الرادفة');
INSERT INTO "uthmani_simple" VALUES(79,8,'79:8','قلوب يومئذ واجفة');
INSERT INTO "uthmani_simple" VALUES(79,9,'79:9','ابصرها خشعة');
INSERT INTO "uthmani_simple" VALUES(79,10,'79:10','يقولون اءنا لمردودون فى الحافرة');
INSERT INTO "uthmani_simple" VALUES(79,11,'79:11','اءذا كنا عظما نخرة');
INSERT INTO "uthmani_simple" VALUES(79,12,'79:12','قالوا۟ تلك اذا كرة خاسرة');
INSERT INTO "uthmani_simple" VALUES(79,13,'79:13','فانما هى زجرة وحدة');
INSERT INTO "uthmani_simple" VALUES(79,14,'79:14','فاذا هم بالساهرة');
INSERT INTO "uthmani_simple" VALUES(79,15,'79:15','هل اتىك حديث موسى');
INSERT INTO "uthmani_simple" VALUES(79,16,'79:16','اذ نادىه ربه بالواد المقدس طوى');
INSERT INTO "uthmani_simple" VALUES(79,17,'79:17','اذهب الى فرعون انه طغى');
INSERT INTO "uthmani_simple" VALUES(79,18,'79:18','فقل هل لك الى ان تزكى');
INSERT INTO "uthmani_simple" VALUES(79,19,'79:19','واهديك الى ربك فتخشى');
INSERT INTO "uthmani_simple" VALUES(79,20,'79:20','فارىه الءاية الكبرى');
INSERT INTO "uthmani_simple" VALUES(79,21,'79:21','فكذب وعصى');
INSERT INTO "uthmani_simple" VALUES(79,22,'79:22','ثم ادبر يسعى');
INSERT INTO "uthmani_simple" VALUES(79,23,'79:23','فحشر فنادى');
INSERT INTO "uthmani_simple" VALUES(79,24,'79:24','فقال انا۠ ربكم الاعلى');
INSERT INTO "uthmani_simple" VALUES(79,25,'79:25','فاخذه الله نكال الءاخرة والاولى');
INSERT INTO "uthmani_simple" VALUES(79,26,'79:26','ان فى ذلك لعبرة لمن يخشى');
INSERT INTO "uthmani_simple" VALUES(79,27,'79:27','ءانتم اشد خلقا ام السماء بنىها');
INSERT INTO "uthmani_simple" VALUES(79,28,'79:28','رفع سمكها فسوىها');
INSERT INTO "uthmani_simple" VALUES(79,29,'79:29','واغطش ليلها واخرج ضحىها');
INSERT INTO "uthmani_simple" VALUES(79,30,'79:30','والارض بعد ذلك دحىها');
INSERT INTO "uthmani_simple" VALUES(79,31,'79:31','اخرج منها ماءها ومرعىها');
INSERT INTO "uthmani_simple" VALUES(79,32,'79:32','والجبال ارسىها');
INSERT INTO "uthmani_simple" VALUES(79,33,'79:33','متعا لكم ولانعمكم');
INSERT INTO "uthmani_simple" VALUES(79,34,'79:34','فاذا جاءت الطامة الكبرى');
INSERT INTO "uthmani_simple" VALUES(79,35,'79:35','يوم يتذكر الانسن ما سعى');
INSERT INTO "uthmani_simple" VALUES(79,36,'79:36','وبرزت الجحيم لمن يرى');
INSERT INTO "uthmani_simple" VALUES(79,37,'79:37','فاما من طغى');
INSERT INTO "uthmani_simple" VALUES(79,38,'79:38','وءاثر الحيوة الدنيا');
INSERT INTO "uthmani_simple" VALUES(79,39,'79:39','فان الجحيم هى الماوى');
INSERT INTO "uthmani_simple" VALUES(79,40,'79:40','واما من خاف مقام ربه ونهى النفس عن الهوى');
INSERT INTO "uthmani_simple" VALUES(79,41,'79:41','فان الجنة هى الماوى');
INSERT INTO "uthmani_simple" VALUES(79,42,'79:42','يسـلونك عن الساعة ايان مرسىها');
INSERT INTO "uthmani_simple" VALUES(79,43,'79:43','فيم انت من ذكرىها');
INSERT INTO "uthmani_simple" VALUES(79,44,'79:44','الى ربك منتهىها');
INSERT INTO "uthmani_simple" VALUES(79,45,'79:45','انما انت منذر من يخشىها');
INSERT INTO "uthmani_simple" VALUES(79,46,'79:46','كانهم يوم يرونها لم يلبثوا۟ الا عشية او ضحىها');
INSERT INTO "uthmani_simple" VALUES(80,1,'80:1','عبس وتولى');
INSERT INTO "uthmani_simple" VALUES(80,2,'80:2','ان جاءه الاعمى');
INSERT INTO "uthmani_simple" VALUES(80,3,'80:3','وما يدريك لعله يزكى');
INSERT INTO "uthmani_simple" VALUES(80,4,'80:4','او يذكر فتنفعه الذكرى');
INSERT INTO "uthmani_simple" VALUES(80,5,'80:5','اما من استغنى');
INSERT INTO "uthmani_simple" VALUES(80,6,'80:6','فانت له تصدى');
INSERT INTO "uthmani_simple" VALUES(80,7,'80:7','وما عليك الا يزكى');
INSERT INTO "uthmani_simple" VALUES(80,8,'80:8','واما من جاءك يسعى');
INSERT INTO "uthmani_simple" VALUES(80,9,'80:9','وهو يخشى');
INSERT INTO "uthmani_simple" VALUES(80,10,'80:10','فانت عنه تلهى');
INSERT INTO "uthmani_simple" VALUES(80,11,'80:11','كلا انها تذكرة');
INSERT INTO "uthmani_simple" VALUES(80,12,'80:12','فمن شاء ذكره');
INSERT INTO "uthmani_simple" VALUES(80,13,'80:13','فى صحف مكرمة');
INSERT INTO "uthmani_simple" VALUES(80,14,'80:14','مرفوعة مطهرةۭ');
INSERT INTO "uthmani_simple" VALUES(80,15,'80:15','بايدى سفرة');
INSERT INTO "uthmani_simple" VALUES(80,16,'80:16','كرامۭ بررة');
INSERT INTO "uthmani_simple" VALUES(80,17,'80:17','قتل الانسن ما اكفره');
INSERT INTO "uthmani_simple" VALUES(80,18,'80:18','من اى شىء خلقه');
INSERT INTO "uthmani_simple" VALUES(80,19,'80:19','من نطفة خلقه فقدره');
INSERT INTO "uthmani_simple" VALUES(80,20,'80:20','ثم السبيل يسره');
INSERT INTO "uthmani_simple" VALUES(80,21,'80:21','ثم اماته فاقبره');
INSERT INTO "uthmani_simple" VALUES(80,22,'80:22','ثم اذا شاء انشره');
INSERT INTO "uthmani_simple" VALUES(80,23,'80:23','كلا لما يقض ما امره');
INSERT INTO "uthmani_simple" VALUES(80,24,'80:24','فلينظر الانسن الى طعامه');
INSERT INTO "uthmani_simple" VALUES(80,25,'80:25','انا صببنا الماء صبا');
INSERT INTO "uthmani_simple" VALUES(80,26,'80:26','ثم شققنا الارض شقا');
INSERT INTO "uthmani_simple" VALUES(80,27,'80:27','فانۢبتنا فيها حبا');
INSERT INTO "uthmani_simple" VALUES(80,28,'80:28','وعنبا وقضبا');
INSERT INTO "uthmani_simple" VALUES(80,29,'80:29','وزيتونا ونخلا');
INSERT INTO "uthmani_simple" VALUES(80,30,'80:30','وحدائق غلبا');
INSERT INTO "uthmani_simple" VALUES(80,31,'80:31','وفكهة وابا');
INSERT INTO "uthmani_simple" VALUES(80,32,'80:32','متعا لكم ولانعمكم');
INSERT INTO "uthmani_simple" VALUES(80,33,'80:33','فاذا جاءت الصاخة');
INSERT INTO "uthmani_simple" VALUES(80,34,'80:34','يوم يفر المرء من اخيه');
INSERT INTO "uthmani_simple" VALUES(80,35,'80:35','وامه وابيه');
INSERT INTO "uthmani_simple" VALUES(80,36,'80:36','وصحبته وبنيه');
INSERT INTO "uthmani_simple" VALUES(80,37,'80:37','لكل امرئ منهم يومئذ شان يغنيه');
INSERT INTO "uthmani_simple" VALUES(80,38,'80:38','وجوه يومئذ مسفرة');
INSERT INTO "uthmani_simple" VALUES(80,39,'80:39','ضاحكة مستبشرة');
INSERT INTO "uthmani_simple" VALUES(80,40,'80:40','ووجوه يومئذ عليها غبرة');
INSERT INTO "uthmani_simple" VALUES(80,41,'80:41','ترهقها قترة');
INSERT INTO "uthmani_simple" VALUES(80,42,'80:42','او۟لئك هم الكفرة الفجرة');
INSERT INTO "uthmani_simple" VALUES(81,1,'81:1','اذا الشمس كورت');
INSERT INTO "uthmani_simple" VALUES(81,2,'81:2','واذا النجوم انكدرت');
INSERT INTO "uthmani_simple" VALUES(81,3,'81:3','واذا الجبال سيرت');
INSERT INTO "uthmani_simple" VALUES(81,4,'81:4','واذا العشار عطلت');
INSERT INTO "uthmani_simple" VALUES(81,5,'81:5','واذا الوحوش حشرت');
INSERT INTO "uthmani_simple" VALUES(81,6,'81:6','واذا البحار سجرت');
INSERT INTO "uthmani_simple" VALUES(81,7,'81:7','واذا النفوس زوجت');
INSERT INTO "uthmani_simple" VALUES(81,8,'81:8','واذا الموءدة سئلت');
INSERT INTO "uthmani_simple" VALUES(81,9,'81:9','باى ذنۢب قتلت');
INSERT INTO "uthmani_simple" VALUES(81,10,'81:10','واذا الصحف نشرت');
INSERT INTO "uthmani_simple" VALUES(81,11,'81:11','واذا السماء كشطت');
INSERT INTO "uthmani_simple" VALUES(81,12,'81:12','واذا الجحيم سعرت');
INSERT INTO "uthmani_simple" VALUES(81,13,'81:13','واذا الجنة ازلفت');
INSERT INTO "uthmani_simple" VALUES(81,14,'81:14','علمت نفس ما احضرت');
INSERT INTO "uthmani_simple" VALUES(81,15,'81:15','فلا اقسم بالخنس');
INSERT INTO "uthmani_simple" VALUES(81,16,'81:16','الجوار الكنس');
INSERT INTO "uthmani_simple" VALUES(81,17,'81:17','واليل اذا عسعس');
INSERT INTO "uthmani_simple" VALUES(81,18,'81:18','والصبح اذا تنفس');
INSERT INTO "uthmani_simple" VALUES(81,19,'81:19','انه لقول رسول كريم');
INSERT INTO "uthmani_simple" VALUES(81,20,'81:20','ذى قوة عند ذى العرش مكين');
INSERT INTO "uthmani_simple" VALUES(81,21,'81:21','مطاع ثم امين');
INSERT INTO "uthmani_simple" VALUES(81,22,'81:22','وما صاحبكم بمجنون');
INSERT INTO "uthmani_simple" VALUES(81,23,'81:23','ولقد رءاه بالافق المبين');
INSERT INTO "uthmani_simple" VALUES(81,24,'81:24','وما هو على الغيب بضنين');
INSERT INTO "uthmani_simple" VALUES(81,25,'81:25','وما هو بقول شيطن رجيم');
INSERT INTO "uthmani_simple" VALUES(81,26,'81:26','فاين تذهبون');
INSERT INTO "uthmani_simple" VALUES(81,27,'81:27','ان هو الا ذكر للعلمين');
INSERT INTO "uthmani_simple" VALUES(81,28,'81:28','لمن شاء منكم ان يستقيم');
INSERT INTO "uthmani_simple" VALUES(81,29,'81:29','وما تشاءون الا ان يشاء الله رب العلمين');
INSERT INTO "uthmani_simple" VALUES(82,1,'82:1','اذا السماء انفطرت');
INSERT INTO "uthmani_simple" VALUES(82,2,'82:2','واذا الكواكب انتثرت');
INSERT INTO "uthmani_simple" VALUES(82,3,'82:3','واذا البحار فجرت');
INSERT INTO "uthmani_simple" VALUES(82,4,'82:4','واذا القبور بعثرت');
INSERT INTO "uthmani_simple" VALUES(82,5,'82:5','علمت نفس ما قدمت واخرت');
INSERT INTO "uthmani_simple" VALUES(82,6,'82:6','يايها الانسن ما غرك بربك الكريم');
INSERT INTO "uthmani_simple" VALUES(82,7,'82:7','الذى خلقك فسوىك فعدلك');
INSERT INTO "uthmani_simple" VALUES(82,8,'82:8','فى اى صورة ما شاء ركبك');
INSERT INTO "uthmani_simple" VALUES(82,9,'82:9','كلا بل تكذبون بالدين');
INSERT INTO "uthmani_simple" VALUES(82,10,'82:10','وان عليكم لحفظين');
INSERT INTO "uthmani_simple" VALUES(82,11,'82:11','كراما كتبين');
INSERT INTO "uthmani_simple" VALUES(82,12,'82:12','يعلمون ما تفعلون');
INSERT INTO "uthmani_simple" VALUES(82,13,'82:13','ان الابرار لفى نعيم');
INSERT INTO "uthmani_simple" VALUES(82,14,'82:14','وان الفجار لفى جحيم');
INSERT INTO "uthmani_simple" VALUES(82,15,'82:15','يصلونها يوم الدين');
INSERT INTO "uthmani_simple" VALUES(82,16,'82:16','وما هم عنها بغائبين');
INSERT INTO "uthmani_simple" VALUES(82,17,'82:17','وما ادرىك ما يوم الدين');
INSERT INTO "uthmani_simple" VALUES(82,18,'82:18','ثم ما ادرىك ما يوم الدين');
INSERT INTO "uthmani_simple" VALUES(82,19,'82:19','يوم لا تملك نفس لنفس شيـا والامر يومئذ لله');
INSERT INTO "uthmani_simple" VALUES(83,1,'83:1','ويل للمطففين');
INSERT INTO "uthmani_simple" VALUES(83,2,'83:2','الذين اذا اكتالوا۟ على الناس يستوفون');
INSERT INTO "uthmani_simple" VALUES(83,3,'83:3','واذا كالوهم او وزنوهم يخسرون');
INSERT INTO "uthmani_simple" VALUES(83,4,'83:4','الا يظن او۟لئك انهم مبعوثون');
INSERT INTO "uthmani_simple" VALUES(83,5,'83:5','ليوم عظيم');
INSERT INTO "uthmani_simple" VALUES(83,6,'83:6','يوم يقوم الناس لرب العلمين');
INSERT INTO "uthmani_simple" VALUES(83,7,'83:7','كلا ان كتب الفجار لفى سجين');
INSERT INTO "uthmani_simple" VALUES(83,8,'83:8','وما ادرىك ما سجين');
INSERT INTO "uthmani_simple" VALUES(83,9,'83:9','كتب مرقوم');
INSERT INTO "uthmani_simple" VALUES(83,10,'83:10','ويل يومئذ للمكذبين');
INSERT INTO "uthmani_simple" VALUES(83,11,'83:11','الذين يكذبون بيوم الدين');
INSERT INTO "uthmani_simple" VALUES(83,12,'83:12','وما يكذب به الا كل معتد اثيم');
INSERT INTO "uthmani_simple" VALUES(83,13,'83:13','اذا تتلى عليه ءايتنا قال اسطير الاولين');
INSERT INTO "uthmani_simple" VALUES(83,14,'83:14','كلا بل ران على قلوبهم ما كانوا۟ يكسبون');
INSERT INTO "uthmani_simple" VALUES(83,15,'83:15','كلا انهم عن ربهم يومئذ لمحجوبون');
INSERT INTO "uthmani_simple" VALUES(83,16,'83:16','ثم انهم لصالوا۟ الجحيم');
INSERT INTO "uthmani_simple" VALUES(83,17,'83:17','ثم يقال هذا الذى كنتم به تكذبون');
INSERT INTO "uthmani_simple" VALUES(83,18,'83:18','كلا ان كتب الابرار لفى عليين');
INSERT INTO "uthmani_simple" VALUES(83,19,'83:19','وما ادرىك ما عليون');
INSERT INTO "uthmani_simple" VALUES(83,20,'83:20','كتب مرقوم');
INSERT INTO "uthmani_simple" VALUES(83,21,'83:21','يشهده المقربون');
INSERT INTO "uthmani_simple" VALUES(83,22,'83:22','ان الابرار لفى نعيم');
INSERT INTO "uthmani_simple" VALUES(83,23,'83:23','على الارائك ينظرون');
INSERT INTO "uthmani_simple" VALUES(83,24,'83:24','تعرف فى وجوههم نضرة النعيم');
INSERT INTO "uthmani_simple" VALUES(83,25,'83:25','يسقون من رحيق مختوم');
INSERT INTO "uthmani_simple" VALUES(83,26,'83:26','ختمه مسك وفى ذلك فليتنافس المتنفسون');
INSERT INTO "uthmani_simple" VALUES(83,27,'83:27','ومزاجه من تسنيم');
INSERT INTO "uthmani_simple" VALUES(83,28,'83:28','عينا يشرب بها المقربون');
INSERT INTO "uthmani_simple" VALUES(83,29,'83:29','ان الذين اجرموا۟ كانوا۟ من الذين ءامنوا۟ يضحكون');
INSERT INTO "uthmani_simple" VALUES(83,30,'83:30','واذا مروا۟ بهم يتغامزون');
INSERT INTO "uthmani_simple" VALUES(83,31,'83:31','واذا انقلبوا۟ الى اهلهم انقلبوا۟ فكهين');
INSERT INTO "uthmani_simple" VALUES(83,32,'83:32','واذا راوهم قالوا۟ ان هؤلاء لضالون');
INSERT INTO "uthmani_simple" VALUES(83,33,'83:33','وما ارسلوا۟ عليهم حفظين');
INSERT INTO "uthmani_simple" VALUES(83,34,'83:34','فاليوم الذين ءامنوا۟ من الكفار يضحكون');
INSERT INTO "uthmani_simple" VALUES(83,35,'83:35','على الارائك ينظرون');
INSERT INTO "uthmani_simple" VALUES(83,36,'83:36','هل ثوب الكفار ما كانوا۟ يفعلون');
INSERT INTO "uthmani_simple" VALUES(84,1,'84:1','اذا السماء انشقت');
INSERT INTO "uthmani_simple" VALUES(84,2,'84:2','واذنت لربها وحقت');
INSERT INTO "uthmani_simple" VALUES(84,3,'84:3','واذا الارض مدت');
INSERT INTO "uthmani_simple" VALUES(84,4,'84:4','والقت ما فيها وتخلت');
INSERT INTO "uthmani_simple" VALUES(84,5,'84:5','واذنت لربها وحقت');
INSERT INTO "uthmani_simple" VALUES(84,6,'84:6','يايها الانسن انك كادح الى ربك كدحا فملقيه');
INSERT INTO "uthmani_simple" VALUES(84,7,'84:7','فاما من اوتى كتبه بيمينه');
INSERT INTO "uthmani_simple" VALUES(84,8,'84:8','فسوف يحاسب حسابا يسيرا');
INSERT INTO "uthmani_simple" VALUES(84,9,'84:9','وينقلب الى اهله مسرورا');
INSERT INTO "uthmani_simple" VALUES(84,10,'84:10','واما من اوتى كتبه وراء ظهره');
INSERT INTO "uthmani_simple" VALUES(84,11,'84:11','فسوف يدعوا۟ ثبورا');
INSERT INTO "uthmani_simple" VALUES(84,12,'84:12','ويصلى سعيرا');
INSERT INTO "uthmani_simple" VALUES(84,13,'84:13','انه كان فى اهله مسرورا');
INSERT INTO "uthmani_simple" VALUES(84,14,'84:14','انه ظن ان لن يحور');
INSERT INTO "uthmani_simple" VALUES(84,15,'84:15','بلى ان ربه كان به بصيرا');
INSERT INTO "uthmani_simple" VALUES(84,16,'84:16','فلا اقسم بالشفق');
INSERT INTO "uthmani_simple" VALUES(84,17,'84:17','واليل وما وسق');
INSERT INTO "uthmani_simple" VALUES(84,18,'84:18','والقمر اذا اتسق');
INSERT INTO "uthmani_simple" VALUES(84,19,'84:19','لتركبن طبقا عن طبق');
INSERT INTO "uthmani_simple" VALUES(84,20,'84:20','فما لهم لا يؤمنون');
INSERT INTO "uthmani_simple" VALUES(84,21,'84:21','واذا قرئ عليهم القرءان لا يسجدون');
INSERT INTO "uthmani_simple" VALUES(84,22,'84:22','بل الذين كفروا۟ يكذبون');
INSERT INTO "uthmani_simple" VALUES(84,23,'84:23','والله اعلم بما يوعون');
INSERT INTO "uthmani_simple" VALUES(84,24,'84:24','فبشرهم بعذاب اليم');
INSERT INTO "uthmani_simple" VALUES(84,25,'84:25','الا الذين ءامنوا۟ وعملوا۟ الصلحت لهم اجر غير ممنونۭ');
INSERT INTO "uthmani_simple" VALUES(85,1,'85:1','والسماء ذات البروج');
INSERT INTO "uthmani_simple" VALUES(85,2,'85:2','واليوم الموعود');
INSERT INTO "uthmani_simple" VALUES(85,3,'85:3','وشاهد ومشهود');
INSERT INTO "uthmani_simple" VALUES(85,4,'85:4','قتل اصحب الاخدود');
INSERT INTO "uthmani_simple" VALUES(85,5,'85:5','النار ذات الوقود');
INSERT INTO "uthmani_simple" VALUES(85,6,'85:6','اذ هم عليها قعود');
INSERT INTO "uthmani_simple" VALUES(85,7,'85:7','وهم على ما يفعلون بالمؤمنين شهود');
INSERT INTO "uthmani_simple" VALUES(85,8,'85:8','وما نقموا۟ منهم الا ان يؤمنوا۟ بالله العزيز الحميد');
INSERT INTO "uthmani_simple" VALUES(85,9,'85:9','الذى له ملك السموت والارض والله على كل شىء شهيد');
INSERT INTO "uthmani_simple" VALUES(85,10,'85:10','ان الذين فتنوا۟ المؤمنين والمؤمنت ثم لم يتوبوا۟ فلهم عذاب جهنم ولهم عذاب الحريق');
INSERT INTO "uthmani_simple" VALUES(85,11,'85:11','ان الذين ءامنوا۟ وعملوا۟ الصلحت لهم جنت تجرى من تحتها الانهر ذلك الفوز الكبير');
INSERT INTO "uthmani_simple" VALUES(85,12,'85:12','ان بطش ربك لشديد');
INSERT INTO "uthmani_simple" VALUES(85,13,'85:13','انه هو يبدئ ويعيد');
INSERT INTO "uthmani_simple" VALUES(85,14,'85:14','وهو الغفور الودود');
INSERT INTO "uthmani_simple" VALUES(85,15,'85:15','ذو العرش المجيد');
INSERT INTO "uthmani_simple" VALUES(85,16,'85:16','فعال لما يريد');
INSERT INTO "uthmani_simple" VALUES(85,17,'85:17','هل اتىك حديث الجنود');
INSERT INTO "uthmani_simple" VALUES(85,18,'85:18','فرعون وثمود');
INSERT INTO "uthmani_simple" VALUES(85,19,'85:19','بل الذين كفروا۟ فى تكذيب');
INSERT INTO "uthmani_simple" VALUES(85,20,'85:20','والله من ورائهم محيطۢ');
INSERT INTO "uthmani_simple" VALUES(85,21,'85:21','بل هو قرءان مجيد');
INSERT INTO "uthmani_simple" VALUES(85,22,'85:22','فى لوح محفوظۭ');
INSERT INTO "uthmani_simple" VALUES(86,1,'86:1','والسماء والطارق');
INSERT INTO "uthmani_simple" VALUES(86,2,'86:2','وما ادرىك ما الطارق');
INSERT INTO "uthmani_simple" VALUES(86,3,'86:3','النجم الثاقب');
INSERT INTO "uthmani_simple" VALUES(86,4,'86:4','ان كل نفس لما عليها حافظ');
INSERT INTO "uthmani_simple" VALUES(86,5,'86:5','فلينظر الانسن مم خلق');
INSERT INTO "uthmani_simple" VALUES(86,6,'86:6','خلق من ماء دافق');
INSERT INTO "uthmani_simple" VALUES(86,7,'86:7','يخرج منۢ بين الصلب والترائب');
INSERT INTO "uthmani_simple" VALUES(86,8,'86:8','انه على رجعه لقادر');
INSERT INTO "uthmani_simple" VALUES(86,9,'86:9','يوم تبلى السرائر');
INSERT INTO "uthmani_simple" VALUES(86,10,'86:10','فما له من قوة ولا ناصر');
INSERT INTO "uthmani_simple" VALUES(86,11,'86:11','والسماء ذات الرجع');
INSERT INTO "uthmani_simple" VALUES(86,12,'86:12','والارض ذات الصدع');
INSERT INTO "uthmani_simple" VALUES(86,13,'86:13','انه لقول فصل');
INSERT INTO "uthmani_simple" VALUES(86,14,'86:14','وما هو بالهزل');
INSERT INTO "uthmani_simple" VALUES(86,15,'86:15','انهم يكيدون كيدا');
INSERT INTO "uthmani_simple" VALUES(86,16,'86:16','واكيد كيدا');
INSERT INTO "uthmani_simple" VALUES(86,17,'86:17','فمهل الكفرين امهلهم رويدۢا');
INSERT INTO "uthmani_simple" VALUES(87,1,'87:1','سبح اسم ربك الاعلى');
INSERT INTO "uthmani_simple" VALUES(87,2,'87:2','الذى خلق فسوى');
INSERT INTO "uthmani_simple" VALUES(87,3,'87:3','والذى قدر فهدى');
INSERT INTO "uthmani_simple" VALUES(87,4,'87:4','والذى اخرج المرعى');
INSERT INTO "uthmani_simple" VALUES(87,5,'87:5','فجعله غثاء احوى');
INSERT INTO "uthmani_simple" VALUES(87,6,'87:6','سنقرئك فلا تنسى');
INSERT INTO "uthmani_simple" VALUES(87,7,'87:7','الا ما شاء الله انه يعلم الجهر وما يخفى');
INSERT INTO "uthmani_simple" VALUES(87,8,'87:8','ونيسرك لليسرى');
INSERT INTO "uthmani_simple" VALUES(87,9,'87:9','فذكر ان نفعت الذكرى');
INSERT INTO "uthmani_simple" VALUES(87,10,'87:10','سيذكر من يخشى');
INSERT INTO "uthmani_simple" VALUES(87,11,'87:11','ويتجنبها الاشقى');
INSERT INTO "uthmani_simple" VALUES(87,12,'87:12','الذى يصلى النار الكبرى');
INSERT INTO "uthmani_simple" VALUES(87,13,'87:13','ثم لا يموت فيها ولا يحيى');
INSERT INTO "uthmani_simple" VALUES(87,14,'87:14','قد افلح من تزكى');
INSERT INTO "uthmani_simple" VALUES(87,15,'87:15','وذكر اسم ربه فصلى');
INSERT INTO "uthmani_simple" VALUES(87,16,'87:16','بل تؤثرون الحيوة الدنيا');
INSERT INTO "uthmani_simple" VALUES(87,17,'87:17','والءاخرة خير وابقى');
INSERT INTO "uthmani_simple" VALUES(87,18,'87:18','ان هذا لفى الصحف الاولى');
INSERT INTO "uthmani_simple" VALUES(87,19,'87:19','صحف ابرهيم وموسى');
INSERT INTO "uthmani_simple" VALUES(88,1,'88:1','هل اتىك حديث الغشية');
INSERT INTO "uthmani_simple" VALUES(88,2,'88:2','وجوه يومئذ خشعة');
INSERT INTO "uthmani_simple" VALUES(88,3,'88:3','عاملة ناصبة');
INSERT INTO "uthmani_simple" VALUES(88,4,'88:4','تصلى نارا حامية');
INSERT INTO "uthmani_simple" VALUES(88,5,'88:5','تسقى من عين ءانية');
INSERT INTO "uthmani_simple" VALUES(88,6,'88:6','ليس لهم طعام الا من ضريع');
INSERT INTO "uthmani_simple" VALUES(88,7,'88:7','لا يسمن ولا يغنى من جوع');
INSERT INTO "uthmani_simple" VALUES(88,8,'88:8','وجوه يومئذ ناعمة');
INSERT INTO "uthmani_simple" VALUES(88,9,'88:9','لسعيها راضية');
INSERT INTO "uthmani_simple" VALUES(88,10,'88:10','فى جنة عالية');
INSERT INTO "uthmani_simple" VALUES(88,11,'88:11','لا تسمع فيها لغية');
INSERT INTO "uthmani_simple" VALUES(88,12,'88:12','فيها عين جارية');
INSERT INTO "uthmani_simple" VALUES(88,13,'88:13','فيها سرر مرفوعة');
INSERT INTO "uthmani_simple" VALUES(88,14,'88:14','واكواب موضوعة');
INSERT INTO "uthmani_simple" VALUES(88,15,'88:15','ونمارق مصفوفة');
INSERT INTO "uthmani_simple" VALUES(88,16,'88:16','وزرابى مبثوثة');
INSERT INTO "uthmani_simple" VALUES(88,17,'88:17','افلا ينظرون الى الابل كيف خلقت');
INSERT INTO "uthmani_simple" VALUES(88,18,'88:18','والى السماء كيف رفعت');
INSERT INTO "uthmani_simple" VALUES(88,19,'88:19','والى الجبال كيف نصبت');
INSERT INTO "uthmani_simple" VALUES(88,20,'88:20','والى الارض كيف سطحت');
INSERT INTO "uthmani_simple" VALUES(88,21,'88:21','فذكر انما انت مذكر');
INSERT INTO "uthmani_simple" VALUES(88,22,'88:22','لست عليهم بمصيطر');
INSERT INTO "uthmani_simple" VALUES(88,23,'88:23','الا من تولى وكفر');
INSERT INTO "uthmani_simple" VALUES(88,24,'88:24','فيعذبه الله العذاب الاكبر');
INSERT INTO "uthmani_simple" VALUES(88,25,'88:25','ان الينا ايابهم');
INSERT INTO "uthmani_simple" VALUES(88,26,'88:26','ثم ان علينا حسابهم');
INSERT INTO "uthmani_simple" VALUES(89,1,'89:1','والفجر');
INSERT INTO "uthmani_simple" VALUES(89,2,'89:2','وليال عشر');
INSERT INTO "uthmani_simple" VALUES(89,3,'89:3','والشفع والوتر');
INSERT INTO "uthmani_simple" VALUES(89,4,'89:4','واليل اذا يسر');
INSERT INTO "uthmani_simple" VALUES(89,5,'89:5','هل فى ذلك قسم لذى حجر');
INSERT INTO "uthmani_simple" VALUES(89,6,'89:6','الم تر كيف فعل ربك بعاد');
INSERT INTO "uthmani_simple" VALUES(89,7,'89:7','ارم ذات العماد');
INSERT INTO "uthmani_simple" VALUES(89,8,'89:8','التى لم يخلق مثلها فى البلد');
INSERT INTO "uthmani_simple" VALUES(89,9,'89:9','وثمود الذين جابوا۟ الصخر بالواد');
INSERT INTO "uthmani_simple" VALUES(89,10,'89:10','وفرعون ذى الاوتاد');
INSERT INTO "uthmani_simple" VALUES(89,11,'89:11','الذين طغوا۟ فى البلد');
INSERT INTO "uthmani_simple" VALUES(89,12,'89:12','فاكثروا۟ فيها الفساد');
INSERT INTO "uthmani_simple" VALUES(89,13,'89:13','فصب عليهم ربك سوط عذاب');
INSERT INTO "uthmani_simple" VALUES(89,14,'89:14','ان ربك لبالمرصاد');
INSERT INTO "uthmani_simple" VALUES(89,15,'89:15','فاما الانسن اذا ما ابتلىه ربه فاكرمه ونعمه فيقول ربى اكرمن');
INSERT INTO "uthmani_simple" VALUES(89,16,'89:16','واما اذا ما ابتلىه فقدر عليه رزقه فيقول ربى اهنن');
INSERT INTO "uthmani_simple" VALUES(89,17,'89:17','كلا بل لا تكرمون اليتيم');
INSERT INTO "uthmani_simple" VALUES(89,18,'89:18','ولا تحضون على طعام المسكين');
INSERT INTO "uthmani_simple" VALUES(89,19,'89:19','وتاكلون التراث اكلا لما');
INSERT INTO "uthmani_simple" VALUES(89,20,'89:20','وتحبون المال حبا جما');
INSERT INTO "uthmani_simple" VALUES(89,21,'89:21','كلا اذا دكت الارض دكا دكا');
INSERT INTO "uthmani_simple" VALUES(89,22,'89:22','وجاء ربك والملك صفا صفا');
INSERT INTO "uthmani_simple" VALUES(89,23,'89:23','وجا۟ىء يومئذۭ بجهنم يومئذ يتذكر الانسن وانى له الذكرى');
INSERT INTO "uthmani_simple" VALUES(89,24,'89:24','يقول يليتنى قدمت لحياتى');
INSERT INTO "uthmani_simple" VALUES(89,25,'89:25','فيومئذ لا يعذب عذابه احد');
INSERT INTO "uthmani_simple" VALUES(89,26,'89:26','ولا يوثق وثاقه احد');
INSERT INTO "uthmani_simple" VALUES(89,27,'89:27','يايتها النفس المطمئنة');
INSERT INTO "uthmani_simple" VALUES(89,28,'89:28','ارجعى الى ربك راضية مرضية');
INSERT INTO "uthmani_simple" VALUES(89,29,'89:29','فادخلى فى عبدى');
INSERT INTO "uthmani_simple" VALUES(89,30,'89:30','وادخلى جنتى');
INSERT INTO "uthmani_simple" VALUES(90,1,'90:1','لا اقسم بهذا البلد');
INSERT INTO "uthmani_simple" VALUES(90,2,'90:2','وانت حلۢ بهذا البلد');
INSERT INTO "uthmani_simple" VALUES(90,3,'90:3','ووالد وما ولد');
INSERT INTO "uthmani_simple" VALUES(90,4,'90:4','لقد خلقنا الانسن فى كبد');
INSERT INTO "uthmani_simple" VALUES(90,5,'90:5','ايحسب ان لن يقدر عليه احد');
INSERT INTO "uthmani_simple" VALUES(90,6,'90:6','يقول اهلكت مالا لبدا');
INSERT INTO "uthmani_simple" VALUES(90,7,'90:7','ايحسب ان لم يره احد');
INSERT INTO "uthmani_simple" VALUES(90,8,'90:8','الم نجعل له عينين');
INSERT INTO "uthmani_simple" VALUES(90,9,'90:9','ولسانا وشفتين');
INSERT INTO "uthmani_simple" VALUES(90,10,'90:10','وهدينه النجدين');
INSERT INTO "uthmani_simple" VALUES(90,11,'90:11','فلا اقتحم العقبة');
INSERT INTO "uthmani_simple" VALUES(90,12,'90:12','وما ادرىك ما العقبة');
INSERT INTO "uthmani_simple" VALUES(90,13,'90:13','فك رقبة');
INSERT INTO "uthmani_simple" VALUES(90,14,'90:14','او اطعم فى يوم ذى مسغبة');
INSERT INTO "uthmani_simple" VALUES(90,15,'90:15','يتيما ذا مقربة');
INSERT INTO "uthmani_simple" VALUES(90,16,'90:16','او مسكينا ذا متربة');
INSERT INTO "uthmani_simple" VALUES(90,17,'90:17','ثم كان من الذين ءامنوا۟ وتواصوا۟ بالصبر وتواصوا۟ بالمرحمة');
INSERT INTO "uthmani_simple" VALUES(90,18,'90:18','او۟لئك اصحب الميمنة');
INSERT INTO "uthmani_simple" VALUES(90,19,'90:19','والذين كفروا۟ بـايتنا هم اصحب المشـمة');
INSERT INTO "uthmani_simple" VALUES(90,20,'90:20','عليهم نار مؤصدةۢ');
INSERT INTO "uthmani_simple" VALUES(91,1,'91:1','والشمس وضحىها');
INSERT INTO "uthmani_simple" VALUES(91,2,'91:2','والقمر اذا تلىها');
INSERT INTO "uthmani_simple" VALUES(91,3,'91:3','والنهار اذا جلىها');
INSERT INTO "uthmani_simple" VALUES(91,4,'91:4','واليل اذا يغشىها');
INSERT INTO "uthmani_simple" VALUES(91,5,'91:5','والسماء وما بنىها');
INSERT INTO "uthmani_simple" VALUES(91,6,'91:6','والارض وما طحىها');
INSERT INTO "uthmani_simple" VALUES(91,7,'91:7','ونفس وما سوىها');
INSERT INTO "uthmani_simple" VALUES(91,8,'91:8','فالهمها فجورها وتقوىها');
INSERT INTO "uthmani_simple" VALUES(91,9,'91:9','قد افلح من زكىها');
INSERT INTO "uthmani_simple" VALUES(91,10,'91:10','وقد خاب من دسىها');
INSERT INTO "uthmani_simple" VALUES(91,11,'91:11','كذبت ثمود بطغوىها');
INSERT INTO "uthmani_simple" VALUES(91,12,'91:12','اذ انۢبعث اشقىها');
INSERT INTO "uthmani_simple" VALUES(91,13,'91:13','فقال لهم رسول الله ناقة الله وسقيها');
INSERT INTO "uthmani_simple" VALUES(91,14,'91:14','فكذبوه فعقروها فدمدم عليهم ربهم بذنۢبهم فسوىها');
INSERT INTO "uthmani_simple" VALUES(91,15,'91:15','ولا يخاف عقبها');
INSERT INTO "uthmani_simple" VALUES(92,1,'92:1','واليل اذا يغشى');
INSERT INTO "uthmani_simple" VALUES(92,2,'92:2','والنهار اذا تجلى');
INSERT INTO "uthmani_simple" VALUES(92,3,'92:3','وما خلق الذكر والانثى');
INSERT INTO "uthmani_simple" VALUES(92,4,'92:4','ان سعيكم لشتى');
INSERT INTO "uthmani_simple" VALUES(92,5,'92:5','فاما من اعطى واتقى');
INSERT INTO "uthmani_simple" VALUES(92,6,'92:6','وصدق بالحسنى');
INSERT INTO "uthmani_simple" VALUES(92,7,'92:7','فسنيسره لليسرى');
INSERT INTO "uthmani_simple" VALUES(92,8,'92:8','واما منۢ بخل واستغنى');
INSERT INTO "uthmani_simple" VALUES(92,9,'92:9','وكذب بالحسنى');
INSERT INTO "uthmani_simple" VALUES(92,10,'92:10','فسنيسره للعسرى');
INSERT INTO "uthmani_simple" VALUES(92,11,'92:11','وما يغنى عنه ماله اذا تردى');
INSERT INTO "uthmani_simple" VALUES(92,12,'92:12','ان علينا للهدى');
INSERT INTO "uthmani_simple" VALUES(92,13,'92:13','وان لنا للءاخرة والاولى');
INSERT INTO "uthmani_simple" VALUES(92,14,'92:14','فانذرتكم نارا تلظى');
INSERT INTO "uthmani_simple" VALUES(92,15,'92:15','لا يصلىها الا الاشقى');
INSERT INTO "uthmani_simple" VALUES(92,16,'92:16','الذى كذب وتولى');
INSERT INTO "uthmani_simple" VALUES(92,17,'92:17','وسيجنبها الاتقى');
INSERT INTO "uthmani_simple" VALUES(92,18,'92:18','الذى يؤتى ماله يتزكى');
INSERT INTO "uthmani_simple" VALUES(92,19,'92:19','وما لاحد عنده من نعمة تجزى');
INSERT INTO "uthmani_simple" VALUES(92,20,'92:20','الا ابتغاء وجه ربه الاعلى');
INSERT INTO "uthmani_simple" VALUES(92,21,'92:21','ولسوف يرضى');
INSERT INTO "uthmani_simple" VALUES(93,1,'93:1','والضحى');
INSERT INTO "uthmani_simple" VALUES(93,2,'93:2','واليل اذا سجى');
INSERT INTO "uthmani_simple" VALUES(93,3,'93:3','ما ودعك ربك وما قلى');
INSERT INTO "uthmani_simple" VALUES(93,4,'93:4','وللءاخرة خير لك من الاولى');
INSERT INTO "uthmani_simple" VALUES(93,5,'93:5','ولسوف يعطيك ربك فترضى');
INSERT INTO "uthmani_simple" VALUES(93,6,'93:6','الم يجدك يتيما فـاوى');
INSERT INTO "uthmani_simple" VALUES(93,7,'93:7','ووجدك ضالا فهدى');
INSERT INTO "uthmani_simple" VALUES(93,8,'93:8','ووجدك عائلا فاغنى');
INSERT INTO "uthmani_simple" VALUES(93,9,'93:9','فاما اليتيم فلا تقهر');
INSERT INTO "uthmani_simple" VALUES(93,10,'93:10','واما السائل فلا تنهر');
INSERT INTO "uthmani_simple" VALUES(93,11,'93:11','واما بنعمة ربك فحدث');
INSERT INTO "uthmani_simple" VALUES(94,1,'94:1','الم نشرح لك صدرك');
INSERT INTO "uthmani_simple" VALUES(94,2,'94:2','ووضعنا عنك وزرك');
INSERT INTO "uthmani_simple" VALUES(94,3,'94:3','الذى انقض ظهرك');
INSERT INTO "uthmani_simple" VALUES(94,4,'94:4','ورفعنا لك ذكرك');
INSERT INTO "uthmani_simple" VALUES(94,5,'94:5','فان مع العسر يسرا');
INSERT INTO "uthmani_simple" VALUES(94,6,'94:6','ان مع العسر يسرا');
INSERT INTO "uthmani_simple" VALUES(94,7,'94:7','فاذا فرغت فانصب');
INSERT INTO "uthmani_simple" VALUES(94,8,'94:8','والى ربك فارغب');
INSERT INTO "uthmani_simple" VALUES(95,1,'95:1','والتين والزيتون');
INSERT INTO "uthmani_simple" VALUES(95,2,'95:2','وطور سينين');
INSERT INTO "uthmani_simple" VALUES(95,3,'95:3','وهذا البلد الامين');
INSERT INTO "uthmani_simple" VALUES(95,4,'95:4','لقد خلقنا الانسن فى احسن تقويم');
INSERT INTO "uthmani_simple" VALUES(95,5,'95:5','ثم رددنه اسفل سفلين');
INSERT INTO "uthmani_simple" VALUES(95,6,'95:6','الا الذين ءامنوا۟ وعملوا۟ الصلحت فلهم اجر غير ممنون');
INSERT INTO "uthmani_simple" VALUES(95,7,'95:7','فما يكذبك بعد بالدين');
INSERT INTO "uthmani_simple" VALUES(95,8,'95:8','اليس الله باحكم الحكمين');
INSERT INTO "uthmani_simple" VALUES(96,1,'96:1','اقرا باسم ربك الذى خلق');
INSERT INTO "uthmani_simple" VALUES(96,2,'96:2','خلق الانسن من علق');
INSERT INTO "uthmani_simple" VALUES(96,3,'96:3','اقرا وربك الاكرم');
INSERT INTO "uthmani_simple" VALUES(96,4,'96:4','الذى علم بالقلم');
INSERT INTO "uthmani_simple" VALUES(96,5,'96:5','علم الانسن ما لم يعلم');
INSERT INTO "uthmani_simple" VALUES(96,6,'96:6','كلا ان الانسن ليطغى');
INSERT INTO "uthmani_simple" VALUES(96,7,'96:7','ان رءاه استغنى');
INSERT INTO "uthmani_simple" VALUES(96,8,'96:8','ان الى ربك الرجعى');
INSERT INTO "uthmani_simple" VALUES(96,9,'96:9','ارءيت الذى ينهى');
INSERT INTO "uthmani_simple" VALUES(96,10,'96:10','عبدا اذا صلى');
INSERT INTO "uthmani_simple" VALUES(96,11,'96:11','ارءيت ان كان على الهدى');
INSERT INTO "uthmani_simple" VALUES(96,12,'96:12','او امر بالتقوى');
INSERT INTO "uthmani_simple" VALUES(96,13,'96:13','ارءيت ان كذب وتولى');
INSERT INTO "uthmani_simple" VALUES(96,14,'96:14','الم يعلم بان الله يرى');
INSERT INTO "uthmani_simple" VALUES(96,15,'96:15','كلا لئن لم ينته لنسفعۢا بالناصية');
INSERT INTO "uthmani_simple" VALUES(96,16,'96:16','ناصية كذبة خاطئة');
INSERT INTO "uthmani_simple" VALUES(96,17,'96:17','فليدع ناديه');
INSERT INTO "uthmani_simple" VALUES(96,18,'96:18','سندع الزبانية');
INSERT INTO "uthmani_simple" VALUES(96,19,'96:19','كلا لا تطعه واسجد واقترب');
INSERT INTO "uthmani_simple" VALUES(97,1,'97:1','انا انزلنه فى ليلة القدر');
INSERT INTO "uthmani_simple" VALUES(97,2,'97:2','وما ادرىك ما ليلة القدر');
INSERT INTO "uthmani_simple" VALUES(97,3,'97:3','ليلة القدر خير من الف شهر');
INSERT INTO "uthmani_simple" VALUES(97,4,'97:4','تنزل الملئكة والروح فيها باذن ربهم من كل امر');
INSERT INTO "uthmani_simple" VALUES(97,5,'97:5','سلم هى حتى مطلع الفجر');
INSERT INTO "uthmani_simple" VALUES(98,1,'98:1','لم يكن الذين كفروا۟ من اهل الكتب والمشركين منفكين حتى تاتيهم البينة');
INSERT INTO "uthmani_simple" VALUES(98,2,'98:2','رسول من الله يتلوا۟ صحفا مطهرة');
INSERT INTO "uthmani_simple" VALUES(98,3,'98:3','فيها كتب قيمة');
INSERT INTO "uthmani_simple" VALUES(98,4,'98:4','وما تفرق الذين اوتوا۟ الكتب الا منۢ بعد ما جاءتهم البينة');
INSERT INTO "uthmani_simple" VALUES(98,5,'98:5','وما امروا۟ الا ليعبدوا۟ الله مخلصين له الدين حنفاء ويقيموا۟ الصلوة ويؤتوا۟ الزكوة وذلك دين القيمة');
INSERT INTO "uthmani_simple" VALUES(98,6,'98:6','ان الذين كفروا۟ من اهل الكتب والمشركين فى نار جهنم خلدين فيها او۟لئك هم شر البرية');
INSERT INTO "uthmani_simple" VALUES(98,7,'98:7','ان الذين ءامنوا۟ وعملوا۟ الصلحت او۟لئك هم خير البرية');
INSERT INTO "uthmani_simple" VALUES(98,8,'98:8','جزاؤهم عند ربهم جنت عدن تجرى من تحتها الانهر خلدين فيها ابدا رضى الله عنهم ورضوا۟ عنه ذلك لمن خشى ربه');
INSERT INTO "uthmani_simple" VALUES(99,1,'99:1','اذا زلزلت الارض زلزالها');
INSERT INTO "uthmani_simple" VALUES(99,2,'99:2','واخرجت الارض اثقالها');
INSERT INTO "uthmani_simple" VALUES(99,3,'99:3','وقال الانسن ما لها');
INSERT INTO "uthmani_simple" VALUES(99,4,'99:4','يومئذ تحدث اخبارها');
INSERT INTO "uthmani_simple" VALUES(99,5,'99:5','بان ربك اوحى لها');
INSERT INTO "uthmani_simple" VALUES(99,6,'99:6','يومئذ يصدر الناس اشتاتا ليروا۟ اعملهم');
INSERT INTO "uthmani_simple" VALUES(99,7,'99:7','فمن يعمل مثقال ذرة خيرا يره');
INSERT INTO "uthmani_simple" VALUES(99,8,'99:8','ومن يعمل مثقال ذرة شرا يره');
INSERT INTO "uthmani_simple" VALUES(100,1,'100:1','والعديت ضبحا');
INSERT INTO "uthmani_simple" VALUES(100,2,'100:2','فالموريت قدحا');
INSERT INTO "uthmani_simple" VALUES(100,3,'100:3','فالمغيرت صبحا');
INSERT INTO "uthmani_simple" VALUES(100,4,'100:4','فاثرن به نقعا');
INSERT INTO "uthmani_simple" VALUES(100,5,'100:5','فوسطن به جمعا');
INSERT INTO "uthmani_simple" VALUES(100,6,'100:6','ان الانسن لربه لكنود');
INSERT INTO "uthmani_simple" VALUES(100,7,'100:7','وانه على ذلك لشهيد');
INSERT INTO "uthmani_simple" VALUES(100,8,'100:8','وانه لحب الخير لشديد');
INSERT INTO "uthmani_simple" VALUES(100,9,'100:9','افلا يعلم اذا بعثر ما فى القبور');
INSERT INTO "uthmani_simple" VALUES(100,10,'100:10','وحصل ما فى الصدور');
INSERT INTO "uthmani_simple" VALUES(100,11,'100:11','ان ربهم بهم يومئذ لخبيرۢ');
INSERT INTO "uthmani_simple" VALUES(101,1,'101:1','القارعة');
INSERT INTO "uthmani_simple" VALUES(101,2,'101:2','ما القارعة');
INSERT INTO "uthmani_simple" VALUES(101,3,'101:3','وما ادرىك ما القارعة');
INSERT INTO "uthmani_simple" VALUES(101,4,'101:4','يوم يكون الناس كالفراش المبثوث');
INSERT INTO "uthmani_simple" VALUES(101,5,'101:5','وتكون الجبال كالعهن المنفوش');
INSERT INTO "uthmani_simple" VALUES(101,6,'101:6','فاما من ثقلت موزينه');
INSERT INTO "uthmani_simple" VALUES(101,7,'101:7','فهو فى عيشة راضية');
INSERT INTO "uthmani_simple" VALUES(101,8,'101:8','واما من خفت موزينه');
INSERT INTO "uthmani_simple" VALUES(101,9,'101:9','فامه هاوية');
INSERT INTO "uthmani_simple" VALUES(101,10,'101:10','وما ادرىك ما هيه');
INSERT INTO "uthmani_simple" VALUES(101,11,'101:11','نار حاميةۢ');
INSERT INTO "uthmani_simple" VALUES(102,1,'102:1','الهىكم التكاثر');
INSERT INTO "uthmani_simple" VALUES(102,2,'102:2','حتى زرتم المقابر');
INSERT INTO "uthmani_simple" VALUES(102,3,'102:3','كلا سوف تعلمون');
INSERT INTO "uthmani_simple" VALUES(102,4,'102:4','ثم كلا سوف تعلمون');
INSERT INTO "uthmani_simple" VALUES(102,5,'102:5','كلا لو تعلمون علم اليقين');
INSERT INTO "uthmani_simple" VALUES(102,6,'102:6','لترون الجحيم');
INSERT INTO "uthmani_simple" VALUES(102,7,'102:7','ثم لترونها عين اليقين');
INSERT INTO "uthmani_simple" VALUES(102,8,'102:8','ثم لتسـلن يومئذ عن النعيم');
INSERT INTO "uthmani_simple" VALUES(103,1,'103:1','والعصر');
INSERT INTO "uthmani_simple" VALUES(103,2,'103:2','ان الانسن لفى خسر');
INSERT INTO "uthmani_simple" VALUES(103,3,'103:3','الا الذين ءامنوا۟ وعملوا۟ الصلحت وتواصوا۟ بالحق وتواصوا۟ بالصبر');
INSERT INTO "uthmani_simple" VALUES(104,1,'104:1','ويل لكل همزة لمزة');
INSERT INTO "uthmani_simple" VALUES(104,2,'104:2','الذى جمع مالا وعدده');
INSERT INTO "uthmani_simple" VALUES(104,3,'104:3','يحسب ان ماله اخلده');
INSERT INTO "uthmani_simple" VALUES(104,4,'104:4','كلا لينۢبذن فى الحطمة');
INSERT INTO "uthmani_simple" VALUES(104,5,'104:5','وما ادرىك ما الحطمة');
INSERT INTO "uthmani_simple" VALUES(104,6,'104:6','نار الله الموقدة');
INSERT INTO "uthmani_simple" VALUES(104,7,'104:7','التى تطلع على الافـدة');
INSERT INTO "uthmani_simple" VALUES(104,8,'104:8','انها عليهم مؤصدة');
INSERT INTO "uthmani_simple" VALUES(104,9,'104:9','فى عمد ممددةۭ');
INSERT INTO "uthmani_simple" VALUES(105,1,'105:1','الم تر كيف فعل ربك باصحب الفيل');
INSERT INTO "uthmani_simple" VALUES(105,2,'105:2','الم يجعل كيدهم فى تضليل');
INSERT INTO "uthmani_simple" VALUES(105,3,'105:3','وارسل عليهم طيرا ابابيل');
INSERT INTO "uthmani_simple" VALUES(105,4,'105:4','ترميهم بحجارة من سجيل');
INSERT INTO "uthmani_simple" VALUES(105,5,'105:5','فجعلهم كعصف ماكولۭ');
INSERT INTO "uthmani_simple" VALUES(106,1,'106:1','لايلف قريش');
INSERT INTO "uthmani_simple" VALUES(106,2,'106:2','الفهم رحلة الشتاء والصيف');
INSERT INTO "uthmani_simple" VALUES(106,3,'106:3','فليعبدوا۟ رب هذا البيت');
INSERT INTO "uthmani_simple" VALUES(106,4,'106:4','الذى اطعمهم من جوع وءامنهم من خوفۭ');
INSERT INTO "uthmani_simple" VALUES(107,1,'107:1','ارءيت الذى يكذب بالدين');
INSERT INTO "uthmani_simple" VALUES(107,2,'107:2','فذلك الذى يدع اليتيم');
INSERT INTO "uthmani_simple" VALUES(107,3,'107:3','ولا يحض على طعام المسكين');
INSERT INTO "uthmani_simple" VALUES(107,4,'107:4','فويل للمصلين');
INSERT INTO "uthmani_simple" VALUES(107,5,'107:5','الذين هم عن صلاتهم ساهون');
INSERT INTO "uthmani_simple" VALUES(107,6,'107:6','الذين هم يراءون');
INSERT INTO "uthmani_simple" VALUES(107,7,'107:7','ويمنعون الماعون');
INSERT INTO "uthmani_simple" VALUES(108,1,'108:1','انا اعطينك الكوثر');
INSERT INTO "uthmani_simple" VALUES(108,2,'108:2','فصل لربك وانحر');
INSERT INTO "uthmani_simple" VALUES(108,3,'108:3','ان شانئك هو الابتر');
INSERT INTO "uthmani_simple" VALUES(109,1,'109:1','قل يايها الكفرون');
INSERT INTO "uthmani_simple" VALUES(109,2,'109:2','لا اعبد ما تعبدون');
INSERT INTO "uthmani_simple" VALUES(109,3,'109:3','ولا انتم عبدون ما اعبد');
INSERT INTO "uthmani_simple" VALUES(109,4,'109:4','ولا انا۠ عابد ما عبدتم');
INSERT INTO "uthmani_simple" VALUES(109,5,'109:5','ولا انتم عبدون ما اعبد');
INSERT INTO "uthmani_simple" VALUES(109,6,'109:6','لكم دينكم ولى دين');
INSERT INTO "uthmani_simple" VALUES(110,1,'110:1','اذا جاء نصر الله والفتح');
INSERT INTO "uthmani_simple" VALUES(110,2,'110:2','ورايت الناس يدخلون فى دين الله افواجا');
INSERT INTO "uthmani_simple" VALUES(110,3,'110:3','فسبح بحمد ربك واستغفره انه كان توابۢا');
INSERT INTO "uthmani_simple" VALUES(111,1,'111:1','تبت يدا ابى لهب وتب');
INSERT INTO "uthmani_simple" VALUES(111,2,'111:2','ما اغنى عنه ماله وما كسب');
INSERT INTO "uthmani_simple" VALUES(111,3,'111:3','سيصلى نارا ذات لهب');
INSERT INTO "uthmani_simple" VALUES(111,4,'111:4','وامراته حمالة الحطب');
INSERT INTO "uthmani_simple" VALUES(111,5,'111:5','فى جيدها حبل من مسدۭ');
INSERT INTO "uthmani_simple" VALUES(112,1,'112:1','قل هو الله احد');
INSERT INTO "uthmani_simple" VALUES(112,2,'112:2','الله الصمد');
INSERT INTO "uthmani_simple" VALUES(112,3,'112:3','لم يلد ولم يولد');
INSERT INTO "uthmani_simple" VALUES(112,4,'112:4','ولم يكن له كفوا احدۢ');
INSERT INTO "uthmani_simple" VALUES(113,1,'113:1','قل اعوذ برب الفلق');
INSERT INTO "uthmani_simple" VALUES(113,2,'113:2','من شر ما خلق');
INSERT INTO "uthmani_simple" VALUES(113,3,'113:3','ومن شر غاسق اذا وقب');
INSERT INTO "uthmani_simple" VALUES(113,4,'113:4','ومن شر النفثت فى العقد');
INSERT INTO "uthmani_simple" VALUES(113,5,'113:5','ومن شر حاسد اذا حسد');
INSERT INTO "uthmani_simple" VALUES(114,1,'114:1','قل اعوذ برب الناس');
INSERT INTO "uthmani_simple" VALUES(114,2,'114:2','ملك الناس');
INSERT INTO "uthmani_simple" VALUES(114,3,'114:3','اله الناس');
INSERT INTO "uthmani_simple" VALUES(114,4,'114:4','من شر الوسواس الخناس');
INSERT INTO "uthmani_simple" VALUES(114,5,'114:5','الذى يوسوس فى صدور الناس');
INSERT INTO "uthmani_simple" VALUES(114,6,'114:6','من الجنة والناس');
