INSERT INTO "vn_mokhtasar" VALUES(1,1,'1:1','Nhân danh Allah, bề tôi bắt đầu đọc Qur''an, với đại danh của Ngài bề tôi cầu xin Ngài phúc lành. Quả thật, câu Bismillah bao gồm ba dại danh hoàn mỹ của Allah: "Allah" có nghĩa là Đấng đáng được thờ phượng, đây là danh xưng chỉ dành riêng cho một mình Allah, không ai (vật gì) ngoài Ngài được phép đặt hay gọi bằng danh xưng này. 2- "Al-Rahman" - "Đấng Rất Mực Độ Lượng" có nghĩa là Đấng với lòng nhân từ bao la phủ trùm mọi tạo vật. 3- "Al-Rahim" - "Đấng Rất Mực Khoan Dung" có nghĩa là Đấng với lòng nhân từ chỉ đối với những bề tôi có đức tin của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(1,2,'1:2','Mọi sự ca ngợi và tán dương đều chỉ dành riêng cho một mình Allah bởi vì chỉ có Ngài là Đấng duy nhất đáng được ca ngợi và tán dương và bởi vì Ngài là Thượng Đế, Đấng Tạo Hóa, Đấng Chi Phối vạn vật; còn vũ trụ và muôn loài ý nói tất cả tạo vật của Ngài tức tất cả những gì khác ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(1,3,'1:3','Ca ngợi và tán dương Allah, Đấng Tối Cao sau khi đã tán dương và ca ngợi Ngài trong câu Kinh trước');
INSERT INTO "vn_mokhtasar" VALUES(1,4,'1:4','Vinh quang thay Allah rằng Ngài nắm mọi vương quyền vào Ngày Phục Sinh, Ngày mà không một linh hồn nào giúp đỡ được một linh hồn khác. Ngày Phán Xử Cuối Cùng: Ngày Allah xét xử để thưởng phạt các bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(1,5,'1:5','Bầy tôi chỉ thờ phượng một mình Ngài và chỉ phục tùng mệnh lệnh của riêng Ngài, bầy tôi không tổ hợp cùng với Ngài một đối tác ngang vai, và bầy tôi chỉ cầu xin sự phù hộ và sự giúp đỡ từ nơi Ngài duy nhất trong tất cả mọi vụ việc của bầy tôi bởi lẽ mọi điều tốt lành đều đến từ bàn tay của Ngài và không có đấng phù hộ nào ngoài Ngài cả.');
INSERT INTO "vn_mokhtasar" VALUES(1,6,'1:6','Xin Ngài chỉ dắt bầy tôi đến với con đường ngay chính và làm vững bước bầy tôi trên con đường đó và xin Ngài ban thêm cho bầy tôi sự hướng dẫn. Con đường Ngay Chính tức con đường thẳng tắp không ngoằn nghèo uốn lượn, ý nói tôn giáo Islam mà Allah đã cử Thiên Sứ Muhammad mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(1,7,'1:7','Con đường của những người bề tôi mà Ngài đã ban ân huệ cho họ qua sự hướng dẫn của Ngài như các vị Thiên Sứ, các vị Nabi, những người Siddeeq (có niềm tin tuyệt đối), những người Shaheed (hy sinh cho con đường chính nghĩa của Ngài) và những người ngoan đạo; và thật tốt đẹp và phúc lành thay cho những ai được cùng hội cùng thuyền với những người đó. Không phải con đường của những kẻ mà Ngài đã giận dữ ý nói người Do Thái bởi vì họ biết chân lý nhưng không đi theo. Cũng không phải con đường của những ai lầm đường lạc lối ý nói những người Thiên Chúa giáo vì họ đã lạc khỏi chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(2,1,'2:1','"Alif. La-m. Mi-m" là những chữ cái trong số các chữ cái được mở đầu cho một số chương Qur''an. Những chữ cái này chỉ là những mẫu tự, bản thân nó không mang nghĩa nếu như đứng một mình giống như mẫu tự a, b, c, v.v,. Tuy nhiên, các chữ cái được đề cập trong Qur''an mang một giá trị khác bởi vì trong Qur''an không có bất cứ điều gì lại không mang giá trị và ý nghĩa. Và một trong những giá trị và ý nghĩa của việc một số chương Qur''an được bắt đầu với những mẫu tự chữ cái là chỉ ra sự thách thức của Qur''an khi Nó được cấu tạo bởi những chữ cái riêng lẻ mà những người Ả Rập biết rõ chúng và dùng chúng tạo thành ngôn ngữ để nói chuyện với nhau. Chính vì lẽ này nên mỗi khi các chữ cái được nêu ra thì thường Kinh Qur''an được đề cập theo sau chẳng hạn như trong chương này.');
INSERT INTO "vn_mokhtasar" VALUES(2,2,'2:2','Đây là Kinh Qur''an thiêng liêng và vĩ đại, không có gì phải nghi ngờ về điều đó dù là từ phương diện mặc khải hay từ phương diện ngôn từ và ý nghĩa nội dung của Nó. Kinh Qur''an đích thực chính là lời phán của Allah hướng dẫn những người ngoan đạo tới con đường dẫn đến nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,3,'2:3','4 - Những điều vô hình là tất cả những gì mà con người không thể cảm nhận được bằng các giác quan, chúng bị che khuất khỏi sự cảm nhận của con người chúng ta, chúng là những gì được Allah hoặc Thiên Sứ của Ngài thông tin cho biết chẳng hạn như Ngày Sau. Những người ngoan đạo tức là những người có đức tin nơi những điều vô hình vừa nêu, chu đáo dâng lễ nguyện Salah theo đúng qui định của Allah và sự hướng dẫn của Thiên Sứ, chi dùng những gì được Allah ban phát cho con đường chính nghĩa của Ngài như thực thi nghĩa vụ xuất Zakah, làm Sadaqah để mong được ân phước, và có đức tin nơi những lời mặc khải được Allah ban xuống cho Thiên Sứ Muhammad cũng như nơi những gì được mặc khải xuống cho các vị Thiên Sứ và Nabi thời trước Người một cách không phân biệt. Họ là những người có đức tin kiên định nơi cõi Đời Sau và những gì được thưởng hoặc bị phạt ở nơi đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,4,'2:4','4 - Những điều vô hình là tất cả những gì mà con người không thể cảm nhận được bằng các giác quan, chúng bị che khuất khỏi sự cảm nhận của con người chúng ta, chúng là những gì được Allah hoặc Thiên Sứ của Ngài thông tin cho biết chẳng hạn như Ngày Sau. Những người ngoan đạo tức là những người có đức tin nơi những điều vô hình vừa nêu, chu đáo dâng lễ nguyện Salah theo đúng qui định của Allah và sự hướng dẫn của Thiên Sứ, chi dùng những gì được Allah ban phát cho con đường chính nghĩa của Ngài như thực thi nghĩa vụ xuất Zakah, làm Sadaqah để mong được ân phước, và có đức tin nơi những lời mặc khải được Allah ban xuống cho Thiên Sứ Muhammad cũng như nơi những gì được mặc khải xuống cho các vị Thiên Sứ và Nabi thời trước Người một cách không phân biệt. Họ là những người có đức tin kiên định nơi cõi Đời Sau và những gì được thưởng hoặc bị phạt ở nơi đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,5,'2:5','Những người được mô tả với những thuộc tính và đặc điểm kia thực sự là những người theo đúng con đường chính đạo, họ sẽ thành công trên cõi đời này và cõi Đời Sau, họ sẽ đạt được điều họ hy vọng và tránh được những gì họ sợ hãi.');
INSERT INTO "vn_mokhtasar" VALUES(2,6,'2:6','Quả thật, những người vô đức tin vẫn tiếp tục đi trên sự lầm lạc và sai trái của họ cho dù Thiên Sứ Muhammad có cảnh báo hay không cảnh báo họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,7,'2:7','Bởi vì Allah đã niêm phong trái tim của họ nên trái tim của họ đóng kín lại giữ lấy những điều sai trái, Ngài niêm phong tai của họ nên tai của họ không nghe được điều chân lý, Ngài niêm phong đôi mắt của họ nên đôi mắt của họ không nhìn thấy được điều chân lý; và vào cõi Đời Sau họ sẽ phải chịu sự trừng phạt khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(2,8,'2:8','Trong thiên hạ có nhóm người khẳng định họ là những người có đức tin, họ nói điều đó trên chiếc lưỡi của họ chỉ vì lo sợ cho tính mạng và tài sản của họ còn bên trong thì họ hoàn toàn là những kẻ vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(2,9,'2:9','Chúng lừa dối Allah và những người có đức tin bằng cách thể hiện đức tin ra ngoài và che giấu sự vô đức tin bên trong trái tim của chúng. Chúng tưởng chúng có thể lừa dối được Allah nhưng thực chất chúng chỉ đang lừa dối chính bản thân của chúng nhưng chúng khồng hề hay biết điều đó, bởi quả thật Allah là Đấng Am Tường mọi sự việc, Ngài biết tất cả mọi điều bí mật và thầm kín và Ngài đã phơi bày cho những người có đức tin các thuộc tính cũng như các tình huống giả tạo của họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,10,'2:10','Chứng bệnh của họ là chứng ngờ vực trong lòng, thế nên, Allah gia tăng chứng ngờ vực trong lòng họ nhiều hơn nữa để rồi họ sẽ phải chịu sự trừng phạt đau đớn ở tận đáy của Hỏa Ngục bởi vì họ đã dối gạt Allah và nhân loại, bởi vì họ đã phủ nhận những điều mà Thiên Sứ Muhammad mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(2,11,'2:11','Khi có người ngăn cản họ (những người Munafiq) làm điều thối nát trên trái đất bởi sự vô đức tin và hành vi tội lỗi của họ thì họ phản bác và khẳng định rằng họ mới chính là những người tốt đẹp, họ mới chính là những người cải thiện.');
INSERT INTO "vn_mokhtasar" VALUES(2,12,'2:12','Họ không phải là những người cải thiện mà họ thực chất là những người phá hoại một cách thối tha nhưng họ không cảm thấy điều đó, họ không nhận ra hành động thối tha của họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,13,'2:13','Khi họ được bảo hãy có đức tin giống như những người bạn đạo của Thiên Sứ Muhammad thì họ trả lời với giọng điệu châm biếm và chế giễu: chẳng lẽ chúng tôi lại đi tin giống như những kẻ ngu đần, khờ khạo ư? Nhưng thật ra họ mới là những kẻ ngu dần, khờ khạo mà bản thân không hay biết.');
INSERT INTO "vn_mokhtasar" VALUES(2,14,'2:14','Khi những người Munafiq gặp những người có đức tin thì họ bảo: chúng tôi tin nơi những gì mà các người có đức tin. Họ nói như thế là vì họ sợ những người có đức tin. Nhưng khi họ rời đi khỏi những người có đức tin đến với những kẻ cầm đầu của họ thì họ lại khẳng định chắn chắn rằng họ chỉ đi theo những kẻ cầm đầu của họ, họ nói: Chúng tôi theo phe của quí ngài, chúng tôi thể hiện đồng cảm với những người có đức tin chỉ là bề ngoài thôi, chúng tôi đang đùa giỡn và chế nhạo họ đấy.');
INSERT INTO "vn_mokhtasar" VALUES(2,15,'2:15','Allah chế giễu lại họ để đáp trả cho việc chế giễu của họ đối với những người có đức tin, một sự đáp trả tương thích với từng hành động, chính vì lẽ này Ngài đã qui định những giáo lý để những người Muslim đối xử với họ trên cõi trần này nhưng ở Đời Sau họ sẽ bị trừng phạt thích đáng cho sự vô đức tin và giả tạo của họ. Cùng với sự đáp trả bằng sự chế giễu thì Allah cũng buông thả họ tự do thể hiện sự lầm lạc và thái quá của họ mục đích để họ càng lún sâu vào tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(2,16,'2:16','Những kẻ Munafiq là những kẻ ngu đần bởi vì họ đã lấy sự vô đức tin để đối với đức tin nên cuộc đổi chác đó của họ không những không thu được lợi nhuận nào cho họ vì họ đã bị lỗ mất đức tin nơi Allah mà còn không được hướng dẫn đến với con đường đúng đắn và chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(2,17,'2:17','Allah đưa ra một hình ảnh thí dụ về những người Munafiq này qua hai hình ảnh: hình ảnh của lửa và hình ảnh của nước. Thí dụ về lửa thì Ngài ví hình ảnh của họ giống như hình ảnh của nhóm người nhúm lên ngọn lửa để lấy ánh sáng, khi ngọn lửa được nhúm lên tỏa sáng xung quanh thì họ cứ tưởng ánh sáng đó sẽ soi rõ con đường cho họ thế nhưng Allah lấy mất đi ánh sáng mà chỉ chừa lại cho họ cái nóng nên họ phải ở trong bóng tối mịt mù không nhìn thấy gì cả và cũng không được hướng dẫn bất cứ con đường nào.');
INSERT INTO "vn_mokhtasar" VALUES(2,18,'2:18','Chúng điếc nên không nghe được điều chân lý, chúng câm nên không thể nói điều chân lý và chúng mù nên không nhìn thấy điều chân lý, bởi thế, chúng không thể quay về với Chính đạo từ sự lầm lạc của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(2,19,'2:19','Allah đưa ra hình ảnh thí dụ của họ với nước: hình ảnh của họ giống như hình ảnh của một nhóm người bị mắc kẹt trong một cơn mưa lớn do một đám mây chứa mưa khổng lồ, đám mây đó mang theo bóng tối, sấm sét và cả tia chớp; tiếng sấm sét nghe chát chúa điếng cả người nên họ lấy ngón tay bịt tai lại vì sợ chết; Allah sẽ bao vây những kẻ vô đức tin như thế bởi vì Ngài không bất lực trước một điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(2,20,'2:20','Tia chớp gần như làm cho họ lóa mắt vì sự chói lòa, mỗi khi tia chớp lóe lên thì họ lần mò di chuyển từng bước khó khăn nhưng khi tia chớp biến mất thì trời lại tối mịt, lúc đó họ chỉ biết dừng lại đứng yên một chỗ, họ không thể di chuyển trong âm u tối tăm. Và nếu Allah muốn, Ngài có thể lấy đi thính giác và thị giác của họ với quyền năng vô song của Ngài và không hoàn trả lại cho họ vì tội ương ngạnh và chống đối điều chân lý của họ. Hình ảnh cơn mưa là Qur''an, hình ảnh tiếng sấm sét chát chúa là những trách cứ trong Qur''an, hình ảnh tia chớp là những điều chân lý đôi lúc được phơi bày cho họ, còn hình ảnh họ lấy ngón tay bịt tai lại là sự từ chối không chịu chấp nhận điều chân lý của Qur''an. Ý nghĩa những người Munafiq được ví như hai nhóm người trong hai hình ảnh: nhóm người nhúm lửa không thể hưởng ích lợi từ ngọn lửa mà họ đã nhúm, họ nhúm lửa lên nhưng họ không tìm thấy ánh sáng mà chỉ thấy sức nóng của lửa; còn nhóm người mắc kẹt trong mưa, cơn mưa được ban xuống mang lại phúc lành tuy nhiên họ lại không tìm thấy điều phúc lành nào cả, họ chỉ tìm thấy trong cơn mưa đó những sấm sét, sự âm u mù mịt và những tia chớp chói lòa, điều đó giống như những người Munafiq chỉ tìm thấy trong Islam sự khó khăn và khắc nghiệt.');
INSERT INTO "vn_mokhtasar" VALUES(2,21,'2:21','Này hỡi nhân loại! Các ngươi hãy thờ phượng Thượng Đế của ngươi, hãy thờ phượng một mình Ngài, không thờ phượng ai khác ngoài Ngài bởi vì Ngài là Đấng đã tạo hóa các ngươi và đã tạo hóa các cộng đồng trước các ngươi, mong rằng các ngươi sợ Ngài mà tránh xa những điều Ngài nghiêm cấm và chấp hành những điều Ngài sai bảo để các ngươi được cứu rỗi khỏi hình phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,22,'2:22','Ngài (Allah) là Đấng đã tạo trái đất bằng phẳng cho các ngươi để các ngươi di chuyện và đi lại dễ dàng, đã tạo bầu trời như một mái che bên trên các ngươi, và Ngài đã ban bố ân huệ của Ngài cho các ngươi qua việc ban mưa xuống để làm mọc ra cây trái đủ loại thành nguồn thực phẩm cho các ngươi. Cho nên, các ngươi chớ đừng dựng lên cùng với Ngài những đấng ngang vai trong khi các ngươi thừa biết không có Đấng Tạo Hóa nào khác ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,23,'2:23','Này hỡi nhân loại! Nếu các ngươi nghi ngờ những gì từ Qur''an được ban xuống cho người bề tôi của TA Muhammad thì TA thách thức các ngươi mang đến một chương giống như một chương của Qur''an thử xem, thậm chí chỉ cần giống như một chương ngắn nhất của Nó, và các ngươi được quyền gọi những ai mà các ngươi cho rằng chúng có quyền năng đến hỗ trợ các ngươi nếu các ngươi trung thực về những gì các ngươi hô hào.');
INSERT INTO "vn_mokhtasar" VALUES(2,24,'2:24','Nhưng nếu các ngươi không làm dược theo lời thách thức đó và TA khẳng định chắc chắn các ngươi sẽ không bao giờ làm được cho dù các ngươi có cố gắng thế nào thì các ngươi hãy sợ Hỏa Ngục nơi mà trong đó chất đốt toàn là con người và đá từ những bục tượng mà chúng từng được thờ phượng. Và Hỏa Ngục là nơi được chuẩn bị để chào đón những kẻ vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(2,25,'2:25','Trước đó, Allah đã cảnh báo những người vô đức tin thì giờ đây Ngài ra lệnh cho Thiên Sứ Muhammad báo tin vui đến những ai có đức tin và năng hành thiện về phần thưởng mà Ngài đã chuẩn bị cho họ. Đó là những ngôi vườn nơi Thiên Đàng bên dưới có các dòng sông chảy. Ở nơi đó, mỗi lần họ được ban cho thức ăn từ những loại trái quả ngon lành thì họ nói những trái quả này giống như những trái quả của cõi trần gian mà họ đã từng ăn do hình dáng có sự tương đồng. Allah tiếp đãi họ với những loại trái cây có hình dáng và tên gọi giống như những loại trái cây mà họ đã từng biết ở cõi trần mục đích chỉ để họ khỏi bỡ ngỡ chứ thực chất những trái quả ở những ngôi vườn nơi Thiên Đàng hoàn toàn khác về chất lượng hương vị và ngon lành. Trong Thiên Đàng, họ còn được ban cho những người vợ thanh khiết chưa từng có một ai va chạm. Và sự hưởng thụ khoái lạc nơi Thiên Đàng là bất tận, là vô hạn còn sự hưởng thụ khoái lạc nơi cõi trần chỉ là hữu hạn và ngắn ngủi.');
INSERT INTO "vn_mokhtasar" VALUES(2,26,'2:26','Quả thật, Allah Tối Cao và Toàn Năng không hề cảm thấy e thẹn và ngượng ngùng từ những hình ảnh thí dụ mà Ngài đưa ra, dù cho Ngài đưa ra hình ảnh của con muỗi hay những con vật lớn hơn hoặc nhỏ hơn để làm thí dụ thì nó luôn là điều chân lý và đầy giá trị. Và nhân loại trước những hình ảnh thí dụ của Ngài có hại nhóm: một nhóm có đức tin và một nhóm vô đức tin. Đối với nhóm có đức tin thì họ luôn tin và biết rằng những hình ảnh thí dụ mà Ngài đưa ra đều ẩn chứa ý nghĩa và giá trị thiêng liêng, riêng nhóm người vô đức tin thì lại hỏi nhau một cách giễu cợt đầy châm biếm về những hình ảnh thí dụ mà Ngài đưa ra, họ bảo nhau rằng Allah muốn ngụ ý điều gì khi mà Ngài lại đem những con vật tầm thường như muỗi, ruồi, nhện và những con vật thấp hèn khác làm hình ảnh thí dụ. Allah trả lời cho câu hỏi của họ rằng những hình ảnh thí dụ đó là để làm nguồn chỉ đạo, hướng dẫn và thử thách nhân loại; với những hình ảnh thí dụ đó, có những người bị Ngài làm cho lạc lối vì họ không chịu tiếp nhận và suy ngẫm nó và nhóm người này thì rất nhiều; tuy nhiên, có những người được hướng dẫn và soi sáng bởi những hình ảnh thí dụ này của Ngài và họ cũng đông người không kém. Và Allah khẳng định rằng những hình ảnh thí dụ mà Ngài đưa ra mục đích không để làm lạc lối một ai trong đám bề tôi của Ngài trừ những kẻ bất tuân và đi quá giới hạn của Ngài như những người Munafiq chẳng hạn.');
INSERT INTO "vn_mokhtasar" VALUES(2,27,'2:27','Giao Ước của Allah chính là con người phải có nghĩa vụ và bổn phận thờ phượng một mình Ngài và đi theo sự chỉ đạo của vị Thiên Sứ của Ngài, vị Thiên Sứ mà Người đã được các vị Thiên Sứ trước Người báo trước về sự xuất hiện của Người. Những ai vi phạm Giao Ước này của Ngài thì họ được mô tả là những người cắt đứt những gì mà Ngài đã ra lệnh phải duy trì như phải duy trì mối quan hệ máu mủ ruột thịt và họ chính là những kẻ gieo rắc sự thối tha trên trái đất bởi những tội lỗi của họ. Và họ chính là những người mất mát phần phước của mình trên cõi trần và cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(2,28,'2:28','Quả thật, sự việc của các ngươi - hỡi những kẻ vô đức tin - thật kỳ lạ! Sao các ngươi lại phủ nhận Allah trong khi các ngươi chứng kiến các bằng chứng rõ ràng về quyền năng của Ngài nơi bản thân các ngươi. Từ cõi hư vô khi các ngươi không là cái gì thì Ngài đã tạo hóa các ngươi và ban cho các ngươi sự sống, rồi Ngài làm cho các ngươi chết đi, sau đó từ cõi chết đó Ngài làm cho các ngươi sống lại lần thứ hai (Phục Sinh) để Ngài xét xử và thưởng phạt các ngươi tương ứng với những gì các ngươi đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(2,29,'2:29','Allah là Đấng Duy Nhất đã tạo ra cho các ngươi mọi vật ở trái đất từ sông, núi, cây cối và vô số những thứ khác để các ngươi sử dụng và hưởng lợi. Sau đó, Ngài hướng về bầu trời và tạo chúng thành bảy tầng trời và Ngài là Đấng bao quát tất cả mọi vật bằng kiến thức vô hạn của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,30,'2:30','Allah, Đấng Tối Cao cho biết rằng Ngài đã phán với các Thiên Thần của Ngài rằng Ngài sẽ tạo ra loài người trên trái đất để họ đại diện Ngài trông coi và quản lý lẫn nhau và để họ xây dựng cuộc sống của họ trên trái đất trong phạm vị mệnh lệnh của Ngài. Các Thiên Thần khi được Ngài phán về quyết định đó của Ngài thì họ đã hỏi Ngài với mong muốn được sự giảng giải những khúc mắc mà họ chưa hiểu về giá trị cũng như ý nghĩa của việc Ngài tạo ra con người để họ làm đại diện của Ngài trên trái đất. Các Thiên Thần nghĩ rằng con người sẽ chỉ làm điều thối nát và làm đổ máu trên trái đất trong khi họ luôn phục tùng Ngài, luôn ca ngợi và tán dương Ngài và lúc nào cũng tôn vinh sự Tối Cao và Hoàn Mỹ của Ngài, họ lúc nào cũng tuyệt đối chấp hành và thực thi mệnh lệnh và ý chỉ của Ngài thì Ngài cần chi phải tạo ra con người làm gì nữa. Thế là Allah trả lời thắc mắc của họ rằng Ngài biết những điều họ không biết, Ngài biết ý nghĩa và giá trị của việc tạo ra con người nhưng họ không biết.');
INSERT INTO "vn_mokhtasar" VALUES(2,31,'2:31','Allah đã cho biết về vai trò của Nabi Adam khi Ngài đã dạy cho Người biết hết tất cả tên gọi của vạn vật từ động vật, thực vật cho đến các vật vô tri vô giác; Ngài đã dạy Người ngôn từ và ý nghĩa. Sau đó, Ngài phơi bày vạn vật mà Ngài đã dạy Adam tên gọi của chúng cho các Thiên Thần và yêu cầu họ cho biết tên của chúng nếu như họ chân thật với những điều họ nói rằng họ tốt và cao quý hơn Adam.');
INSERT INTO "vn_mokhtasar" VALUES(2,32,'2:32','Các Thiên Thần thừa nhận sự yếu kém và hèn mọn của mình trước Allah, họ nói: bầy tôi vinh quang Ngài thưa Thượng Đế của bầy tôi và xin thần phục trước sự sáng suốt và sắc lệnh của Ngài, bầy tôi không biết một thứ gì ngoại trừ điều mà Ngài đã dạy, quả thật Ngài là Đấng Hằng Biết, không có điều gì che giấu được Ngài, Ngài là Đấng Sáng Suốt và Anh Minh mỗi khi sắp đặt và an bài một điều nào đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,33,'2:33','Lúc đó, Allah phán bảo Adam: ngươi hãy cho Họ (các Thiên Thần) biết các tên gọi của chúng (vạn vật). Rồi khi Adam nói cho họ biết những gì mà Allah đã dạy Người thì Allah phán với các Thiên Thần: chẳng phải TA đã không nói với các ngươi rằng TA biết hết tất cả mọi thứ trong các tầng trời và trái đất và TA biết tất cả những gì mà các ngươi tiết lộ ra ngoài hay giấu kín trong lòng từ lời nói, hành đồng và suy nghĩ?.');
INSERT INTO "vn_mokhtasar" VALUES(2,34,'2:34','Allah cho biết rằng Ngài đã ra lệnh cho các Thiên Thần quì phủ phục trước Adam mang tính chất tôn kính và nể trọng. Thế là các Thiên Thần phụng mệnh Ngài liền quì xuống phủ phục trừ Iblis thuộc loại Jinn, hắn không chịu thực thi theo mệnh lệnh của Ngài, hắn từ chối phủ phục trước Adam vì sự tự cao tự đại của hắn trước Người. Với hành động bất tuân đó, hắn đã trở thành một kẻ phản nghịch nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(2,35,'2:35','Allah phán với Adam rằng Người và vợ của Người - Hauwa - cứ sống trong Thiên Đàng và ăn trái quả trong đó tùy thích nhưng Ngài lưu ý họ không được đến gần một cái cây, Ngài không cho họ ăn trái của cây đó, vì nếu họ ăn trái của nó thì họ sẽ trở thành những người làm điều sai quấy và nghịch lại mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,36,'2:36','Shaytan đã cám dỗ Adam và vợ của Người với lời lẽ ngon ngọt, hắn đã làm cho hai người họ phạm vào điều cấm của Allah khi ăn trái của cây mà Ngài đã cấm. Cho nên, hình phạt cho hành động trái lệnh của họ là bị trục xuất khỏi một cuộc sống hạnh phúc mà họ đã từng hưởng thụ. Allah phán với hai người họ và Shaytan (Iblis): các ngươi hãy đi xuống trái đất, người này sẽ là kẻ thù của người kia, các ngươi sẽ sống dưới đó và hưởng những gì tốt đẹp từ dưới đó cho đến khi thời hạn kết thúc (tuổi đời) và đến khi tận thế.');
INSERT INTO "vn_mokhtasar" VALUES(2,37,'2:37','Nabi Adam nhận lấy lời dạy từ nơi Allah khi Ngài mặc khải cho Người, đó là lời cầu nguyện xin Ngài tha thứ, nó được nói trong chương - Al-A''raf, câu 23 với ý nghĩa rằng lạy Thượng Đế của bầy tôi, bầy tôi đã tự hại bản thân mình, nếu Ngài không tha thứ và thương xót bầy tôi thì chắc chắn bầy tôi sẽ trở thành những kẻ thua thiệt và thất bại. Adam đã cầu nguyện Allah với lời cầu nguyện đó và được Ngài chấp nhận, Ngài đã chấp nhận sự sám hối của Người và tha thứ cho Người bởi vì Ngài là Đấng luôn đón nhận sứ sám hối của bầy tôi và bởi vì Ngài là Đấng Rất Mực Nhân Từ.');
INSERT INTO "vn_mokhtasar" VALUES(2,38,'2:38','TA tức Allah phán với họ: tất cả các ngươi hãy rời Thiên Đàng đi xuống trái đất, nếu nguồn Chỉ Đạo của TA được mang đến cho các ngươi bởi các vị Thiên Sứ của TA, ai đi theo nguồn Chỉ Đạo đó, tin các vị Thiên Sứ của TA thì họ sẽ không lo sợ ở cõi Đời Sau và sẽ không buồn phiền về những gì mất mát trên cõi trần.');
INSERT INTO "vn_mokhtasar" VALUES(2,39,'2:39','Riêng đối với những ai không có đức tin nơi những Lời Mặc Khải của Allah và cho rằng những lời Mặc Khải của Ngài là ngụy tạo thì họ sẽ là những người đời đời kiếp kiếp sống trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(2,40,'2:40','Này hỡi người dân Isra-el tức con cháu của Y''aqub (Jacob) - vị Nabi của Allah, các ngươi hãy nhớ lại Ân Huệ của Allah đã ban bố cho các ngươi mà tạ ơn Ngài; các ngươi hãy thực hiện giao ước của các ngươi với Ngài, đó là các ngươi tin nơi Ngài và nơi các vị Thiên Sứ của Ngài đồng thời chấp hành và tuân thủ theo giáo luật của Ngài, nếu các ngươi thực hiện giao ước đó thì Ngài sẽ thực hiện giao ước của Ngài với các ngươi, đó là Ngài sẽ ban cho các ngươi cuộc sống tốt lành nơi cõi trần và sẽ ban thưởng cho các ngươi điều tốt đẹp ở cõi Đời Sau; và các ngươi hãy sợ riêng một mình Ngài chớ đừng vi phạm giao ước của các ngươi đối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,41,'2:41','Các ngươi hãy tin nơi Qur''an được TA ban xuống cho Muhammad. Kinh Qur''an mang giáo lý Tawhid (tôn thờ một mình Allah) cũng như thông tin về sứ mạng Nabi của Muhammad tương đồng với những gì được nói trong Kinh Tawrah trước khi Kinh Tawrah bị bóp méo và sửa đổi. Các ngươi hãy coi chừng đừng để mình là nhóm người đầu tiên phủ nhận Kinh Qur''an của Ngài, và các ngươi chớ lấy những lời Mặc Khải của Ngài đổi chác với cái giá rẻ mạt và ít ỏi từ bổng lộc, chức quyền của thế gian. Các ngươi hãy sợ cơn thịnh nộ và sự trừng phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,42,'2:42','Các ngươi chớ đừng lấy điều chân lý mà TA đã ban xuống cho các vị Thiên Sứ của TA pha trộn với những điều dối trá và ngụy tạo, và các ngươi chớ che giấu sự thật được nói trong các Kinh Sách của các ngươi về Muhammad trong khi các ngươi biết rõ đó là sự thật.');
INSERT INTO "vn_mokhtasar" VALUES(2,43,'2:43','Và các ngươi hãy thực hiện và duy trì lễ nguyện Salah một cách chu đáo qua các nghi thức Rukun (trụ cột), các nghi thức Wajib (bắt buộc) và các nghi thức Sunnah (khuyến khích) của nó; các ngươi hãy đóng Zakah tức các ngươi hãy trích một phần từ nguồn tài sản mà Allah đã ban cho các ngươi để phân phát cho người nghèo và khó khăn; và các ngươi hãy hạ mình kính cẩn trước Allah cùng với những người hạ mình kính cẩn trước Allah trong đám tín đồ của Muhammad.');
INSERT INTO "vn_mokhtasar" VALUES(2,44,'2:44','Thật là điều tồi tệ và xấu xa cho việc các ngươi bảo ban người khác tin và hành thiện nhưng bản thân các ngươi lại không làm trong khi các ngươi là những người đọc hiểu Kinh Tawrah và biết rõ điều lệnh trong đó, chẳng lẽ các ngươi không hiểu gì cả hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(2,45,'2:45','Các ngươi hãy cầu xin sự phù hộ nơi Allah trong mọi hoàn cảnh và trong mọi vụ việc của đời và đạo bằng lòng kiên nhẫn và lễ nguyện Salah. Lễ nguyện Salah là cái giúp các ngươi đến gần Allah và giữ vững sự kết nối giữa các ngươi với Ngài, nó sẽ bảo vệ các ngươi khỏi những việc làm tội lỗi; tuy nhiên, lễ nguyện Salah sẽ là điều khó khăn và nặng nề rất lớn ngoại trừ những ai hạ mình sợ Thượng Đế của họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,46,'2:46','Những người sợ Allah là những ai luôn kiên định rằng họ sẽ gặp Ngài để trình diện trước Ngài vào Ngày Phán Xét và họ chắc chắn phải quay trở lại với Ngài trong sớm muộn để Ngài thưởng phạt cho các việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,47,'2:47','Hỡi con cháu của Y''aqub (Jacob)! Các ngươi hãy nhớ lại các Ân Huệ mà Allah đã ban cho các ngươi cũng như những gì mà Ngài đã ưu đãi các ngươi hơn hẳn những nhóm người khác trong nhân loại từ sứ mạng Nabi, vương quyền và ưu đãi đặc biệt khác.');
INSERT INTO "vn_mokhtasar" VALUES(2,48,'2:48','Các ngươi hãy đặt giữa các ngươi với sự trừng phạt của Ngày Phán Xét một vách chắn bằng sự chấp hành các mệnh lệnh của Allah và từ bỏ những điều cấm của Ngài. Vào Ngày đó, không có linh hồn nào có khả năng giúp ích và can thiệp cho linh hồn khác, không ai có quyền can thiệp cho ai ngoại trừ có phép của Allah, không ai được phép chuộc lỗi cho ai dù phải chuộc bằng cả điều tốt đẹp của một trái đất đi chăng nữa; vào Ngày hôm đó sẽ không có sự can thiệp, giúp ích, chuộc tội cho nhau thì linh hồn sẽ chạy trốn về đâu?!');
INSERT INTO "vn_mokhtasar" VALUES(2,49,'2:49','Hãy nhớ lại hỡi người dân Isra-el về việc TA đã cứu các ngươi thoát khỏi đám thuộc hạ của Fir-''awn khi chúng đàn áp các ngươi với nhiều loài đàn áp khác nhau: chúng tàn sát các con trai của ngươi đến mức tiệt diệt, chúng chừa lại phụ nữ của các ngươi để họ hầu hạ và phục dịch cho chúng; chúng làm thế như một sự hạ nhục dành cho các ngươi, và trong việc Ngài giải phóng các ngươi khỏi sự đàn áp tàn bạo của Fir-''awn và đám thuộc hạ của hắn là một sự thử thách lớn lao từ nơi Ngài và Ngài mong các ngươi biết tri ân Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,50,'2:50','Các ngươi hãy nhớ lại hỡi người dân Isra-el về việc TA đã tách biển Hồng Hải ra tạo thành một con đường cho các ngươi băng qua để cứu các ngươi thoát khỏi sự truy sát của Fir-''awn và thuộc hạ của hắn, và TA đã nhận chìm chúng trước mặt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,51,'2:51','Các ngươi hãy nhớ lại hỡi người dân Isra-el lúc TA hẹn gặp Musa trong thời gian bốn mươi đêm để TA ban xuống Kinh Tawrah cho Y làm nguồn ánh sáng và chỉ đạo nhưng rồi trong thời gian vắng mặt của Y thì các ngươi lại đúc ra một con bò cái tơ và thờ cùng nó, quả thật hành động đó của các ngươi thật là sai quấy.');
INSERT INTO "vn_mokhtasar" VALUES(2,52,'2:52','Sau khi các ngươi sám hối cho việc làm đó của các ngươi thì TA đã xí xóa và tha thứ cho các ngươi, TA đã không bắt tội các ngươi vì mong rằng các ngươi biết tri ân TA qua việc chu đáo thờ phượng và tuân lệnh TA.');
INSERT INTO "vn_mokhtasar" VALUES(2,53,'2:53','Các ngươi hãy nhớ lại một trong những ân huệ mà TA đã ban cho các ngươi, đó là TA đã ban cho Musa Kinh Tawrah và Chuẩn Mực Phân Biệt giữa chân lý và điều ngụy tạo, giữa điều phúc và điều tội lỗi, giữa sự hướng dẫn và lầm lạc; mong rằng các ngươi được nó hướng dẫn đúng đường.');
INSERT INTO "vn_mokhtasar" VALUES(2,54,'2:54','Các ngươi hãy nhớ lại một trong các ân huệ mà TA ban cho các ngươi, đó là việc TA đã chấp nhận sự sám hối của các ngươi từ việc các ngươi thờ cúng con bò khi mà Musa nói với các ngươi: quả thật, các người đã tự làm hại bản thân mình qua việc nhận lấy con bò làm thượng đế để các ngươi thờ cùng nó, các ngươi hãy quay đầu sám hối với Đấng đã tạo hóa ra các ngươi bằng cách giết những kẻ tội lỗi trong các ngươi, sự sám hối theo cách này sẽ tốt cho các ngươi hơn việc các ngươi phải bị vĩnh viễn trong Hỏa Ngục, nếu các ngươi làm như thế thì Allah sẽ phù hộ cho các ngươi và tha thứ cho các ngươi bởi Ngài là Đấng Hằng Tha Thứ, Hằng Nhân Từ với đám bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,55,'2:55','Các ngươi hãy nhớ lại lúc cha mẹ của các ngươi, những kẻ sai quấy, nói với Musa một cách thách thức: chúng tôi sẽ không tin ngươi trừ phi chúng tôi tận mắt nhìn thấy Allah, Ngài phải hiện rõ công khai cho chúng tôi thấy. Thế là sét đã đánh họ trong lúc một số người trong bọn họ chỉ biết đưa mắt nhìn.');
INSERT INTO "vn_mokhtasar" VALUES(2,56,'2:56','Sau đó, TA đã làm cho các ngươi sống lại từ cái chết của các ngươi mong rằng các ngươi biết tạ ơn Ngài về những ân huệ mà Ngài đã ban cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,57,'2:57','Một trong những ân huệ mà TA đã ban cho các ngươi là TA đã gửi mây đến che mát các ngươi khỏi cái nóng của mặt trời khi nó chói rọi xuống trái đất, TA đã ban xuống cho các ngươi Al-Man (có người nói đó là các loại thực phẩm tốt lành như mật ong, và có người thì nói đó là những thực phẩm hảo hạng, cao cấp và quí hiếm từ chiếc bàn thực phẩm mà Allah đã ban xuống cho Nabi Ysa và các tông đồ của Người) cùng với Salwa (một loài chim nhỏ mà thịt của nó thơm ngon, có lời cho rằng đó là chim cút). TA đã phán bảo các ngươi hãy ăn thỏa thích từ những nguồn thực phẩm tốt lành mà TA đã ban cấp cho các ngươi. Và TA không hề bị mất giảm bất cứ điều gì từ việc chúng phủ nhận các ân huệ này của TA, tuy nhiên, chính chúng đã làm thiệt hại cho bản thân chúng vì chúng đã đánh mất phần ân phước và sẽ phải đối diện với sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(2,58,'2:58','Các ngươi hãy nhớ lại một trong các ân huệ mà Allah đã ban cho các ngươi khi Ngài phán với các ngươi: Hãy đi vào Jerusalem nơi có ngôi đền Thiêng Al-Maqdis và hãy ăn những gì tốt lành trong đó ở bất cứ nơi nào các ngươi muốn một cách thỏa thích, và khi đi vào trong đó thì các ngươi hãy cúi đầu phủ phục Allah và hãy luôn cầu xin Ngài với lời nguyện cầu: "lạy Thượng Đế của bầy tôi, xin Ngài xóa tội của bầy tôi", TA sẽ đáp lại lời nguyện cầu của các ngươi và sẽ ban thêm cho những người làm tốt những phần thưởng tốt đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(2,59,'2:59','Những kẻ làm điều sai quấy đã thay đổi hành động và bóp méo lời nói, họ đi vào với tư thế bò trườn bằng lưng của họ và họ nói: hạt trên sợi tóc, ý trêu cợt với mệnh lệnh của Allah. Thế là Alalh đã giáng một tai họa từ trên trời xuống để trừng phạt họ bởi tội họ đã làm trái lệnh Ngài và có thái độ bỡn cợt với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,60,'2:60','Các ngươi hãy nhớ lại ân huệ của Allah ban cho các ngươi khi mà các các ngươi trong cơn khát dữ dội nhưng lại không tìm thấy nguồn nước. Musa đã phủ phục cầu nguyện Thượng Đế của Y xin cho các ngươi nguồn nước. TA đã bảo Y đánh chiếc gậy của Y xuống một tảng đá, Y đã tuân lệnh làm theo, từ tảng đá đó phun ra mười hai dòng suối để làm nguồn nước cho mười hai bộ lạc của dân Isra-el các ngươi, mỗi dòng suối là nguồn nước dành riêng cho từng bộ lạc để các ngươi khỏi tranh giành và xung đột lẫn nhau. TA đã phán với các ngươi: "Các ngươi hãy ăn và uống từ bổng lộc của Allah ban cho các ngươi nhưng chớ có hành động bất tuân TA và chớ làm điều phá hoại và thối nát trên trái đất".');
INSERT INTO "vn_mokhtasar" VALUES(2,61,'2:61','Các ngươi hãy nhớ lại lúc các ngươi phủ nhận ân huệ của Thượng Đế các ngươi, các ngươi chán ngán với các thực phẩm mà Allah đã ban xuống cho các ngươi từ Al-Man và Salwa, các ngươi than rằng các ngươi không thể chịu được một loại thức ăn mãi không có gì thay đổi cả, các ngươi đòi hỏi Musa cầu xin Allah làm mọc ra cho các ngươi từ đất những loại rau tươi, dưa chuột, tỏi, đậu lăng và hành để các ngươi làm thực phẩm. Musa đã nói với các ngươi bằng thái độ không chấp nhận yêu cầu của các ngươi rằng chẳng lẽ các ngươi lại đòi những thứ tệ và thua kém hơn những thứ mà Ngài đã ban cho các ngươi từ Al-Man và Salwa hay sao. Quả thật, Al-Man và Salwa là nguồn thực phẩm tốt lành và hảo hạng cho các ngươi, nguồn thực phẩm này đến với các ngươi mà các ngươi không cần phải mệt mỏi và quá tốn sức. Musa bảo các ngươi hãy rời khỏi vùng đất ân phúc này mà đi đến các thị trấn nào khác của Ai Cập, ở những nơi đó, các ngươi sẽ tìm thấy những thứ các ngươi đòi hỏi ở các nương rẫy và các khu chợ. Bởi việc chạy theo dục vọng và việc cứ lặp đi lặp lại hành động phủ nhận những lựa chọn của Allah dành cho các ngươi nên Ngài đã làm cho các ngươi nghèo khó và cơ cực; và các ngươi phải hứng chịu cơn thịnh nộ của Ngài cho việc các ngươi chống lại tôn giáo của Ngài và việc các ngươi phủ nhận các Lời Mặc Khải của Ngài cũng như việc các ngươi đã giết các vị Nabi của Ngài một cách bất công bởi lòng hận thù của các ngươi; và tất cả những hành động và việc làm đó của các ngươi đều là bất tuân và vượt quá mức giới hạn của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,62,'2:62','Những người tín đồ của cộng đồng này tức cộng đồng tín đồ của Muhammad và những tín đồ của những cộng đồng trước Muhammad từ Do Thái, Thiên Chúa và Sabian (một dân tộc trong quá khứ đã sống tại Musal (I-rắc), tôn thờ Allah và đọc Kinh Al-Zabur), ai trong số họ đi theo các vị Nabi của Allah bằng đức tin Iman nơi Allah và Ngày Phán Xử Cuối Cùng thì đều tìm thấy ân phước của mình nơi Thượng Đế của họ, họ sẽ không lo sợ khi đối mặt với cuộc sống cõi Đời Sau và cũng không buồn phiền về những mất mát trên cõi trần.');
INSERT INTO "vn_mokhtasar" VALUES(2,63,'2:63','Các ngươi hãy nhớ lại khi TA dã nhận Giáo Ước của các ngươi về việc các ngươi tin nơi TA và các vị Thiên Sứ của TA, và TA đã nhấc quả núi lên bên trên các ngươi để răn đe và cảnh báo những ai từ bỏ hành động theo Giao ước. TA đã ra lệnh cho các ngươi phải bám chặt lấy Kinh Tawrah không được hời hợt và xao lãng, và TA bảo các ngươi phải ghi nhớ những gì trong đó mong rằng với sự ngay chính đó các ngươi tránh được sự trừng phạt của TA.');
INSERT INTO "vn_mokhtasar" VALUES(2,64,'2:64','Tuy nhiên, sau đó các ngươi lại nghịch đạo và trái lệnh và nếu như không do phúc đức và lòng khoan dung của Allah cho hành động vượt quá giới hạn của các ngươi cũng như nếu không do lòng thương xót của Ngài trong việc đón nhận sự sám hối của các ngươi thì chắc chắn các ngươi đã là những kẻ thất bại thảm hại.');
INSERT INTO "vn_mokhtasar" VALUES(2,65,'2:65','Quả thật, các ngươi biết rất rõ thông tin về cha ông của các ngươi khi họ bị cấm đánh bắt cá vào ngày thứ bảy thì họ đã giăng lưới vào ngày trước thứ bảy, rồi đợi đến ngày chủ nhật họ mới thu lượm cá mang về, thế là Allah cho họ biến thành những con khỉ, một hình phạt dành cho những ai bỡn cợt và gian xảo trước giới cấm của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,66,'2:66','TA đã trừng phạt cái thị trấn vượt giới hạn của TA để làm một bài học cho các thị trấn khác và cho những ai sau này và TA cũng lấy đó làm sự nhắc nhở đến những ai ngay chính sợ sự trừng phạt của TA.');
INSERT INTO "vn_mokhtasar" VALUES(2,67,'2:67','Các ngươi hãy nhớ lại thông tin về cha ông của các ngươi trước đây rằng khi Musa nói với họ: Allah ra lệnh cho các ngươi giết một còn bò cái thì họ lại giễu cợt nói với Người: há ngươi muốn lấy bọn ta ra làm trò đùa điên rồ ấy sao? Musa nói với họ rằng Người không hề nói dối và cũng không hề có ý bỡn cợt và Người cầu xin Allah phù hộ và che chở tránh khỏi những hạng người đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,68,'2:68','Họ bảo Musa cầu xin Allah trình bày rõ hơn về con bò mà Ngài ra lệnh cho họ giết. Musa nói với họ: Allah phán rằng đó là con bò cái tơ, không quá già cũng không quá non, giờ đã rõ thì các ngươi hãy mau thực hiện theo lệnh Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,69,'2:69','Họ vẫn tiếp tục gây khó khăn cho Musa, họ đòi Người cầu xin Allah trình bày cụ thể hơn nữa về màu sắc của con bò. Musa bảo họ: Allah phán rằng đó là con bò màu vàng hung lông mượt làm cho người nhìn thích thú.');
INSERT INTO "vn_mokhtasar" VALUES(2,70,'2:70','Rồi các ngươi vẫn không dừng lại, các ngươi vẫn tiếp tục yêu cầu Musa cầu xin Thượng Đế của Người cho biết thêm các thuộc tính của con bò cái và các ngươi bảo rằng vì con bò như đã được mô tả vẫn còn rất giống nhau, các ngươi chưa thể phân biệt được, lần này nếu như được trình bày thêm các đặc điểm của nó - insha-Allah các ngươi sẽ được hướng dẫn đến với con bò được yêu cầu.');
INSERT INTO "vn_mokhtasar" VALUES(2,71,'2:71','Musa nói với họ: quả thật Allah phán rằng đặc điểm của con bò được yêu cầu là nó không được bắt đi cày đất hay kéo nước vào ruộng, nó khỏe mạnh và lành lặn không có bất kỳ một khiếm khuyết và dị tật nào, và lông của nó vàng hung không có pha lẫn với bất cứ màu sắc nào khác. Lúc này, họ mới nói: giờ ngươi đã mô tả đặc điểm của con bò được yêu cầu rất rõ ràng và tỉ mỉ và mới chịu tiến hành giết con bò mặc dầu trước đó gần như không muốn làm.');
INSERT INTO "vn_mokhtasar" VALUES(2,72,'2:72','Các ngươi hãy nhớ lại lúc các ngươi giết chết một người trong đám các ngươi nhưng mỗi người các ngươi đều chối bỏ mình liên can và tố cáo người khác, các ngươi tranh cãi nhau, nhưng rồi Allah đã làm sáng tỏ điều các ngươi che giấu.');
INSERT INTO "vn_mokhtasar" VALUES(2,73,'2:73','TA đã phán bảo các ngươi đánh nạn nhân bằng một phần của con bò cái mà các ngươi đã giết. Bằng cách đó, TA đã làm nạn nhân sống lại để khai tên của kẻ sát nhân. Các ngươi làm theo lời phán của TA và TA đã cho nạn nhân sống lại khai ra tên của kẻ sát nhân và đó là hình ảnh mà TA sẽ làm cho người chết sống lại từ cõi chết vào Ngày Phục Sinh. TA muốn cho các ngươi thấy các dấu hiệu rõ ràng về quyền năng của TA mong rằng các ngươi hiểu ra sự việc mà có đức tin thực sự nơi TA.');
INSERT INTO "vn_mokhtasar" VALUES(2,74,'2:74','Nhưng rồi sau những lời khuyên cũng như những phép lạ, tấm lòng của họ lại trở nên chai cứng như đá, không, cứng còn hơn đá, tấm lòng của họ sẽ không bao giờ còn có thể thay đổi được nữa trong khi đá có thể thay đổi và biến chuyển bởi vì có loại đá từ đó các dòng sông phun ra, có loại đá nứt ra để nước trong đó chảy ra để làm nguồn lợi cho con người và loài vật, và có loại đá rơi xuống từ các ngọn núi cao vì sợ Allah, còn trái tim và tấm lòng của họ thì không như thế. Và Allah không phải không hay biết những điều họ làm mà Ngài am tường tất cả mọi thứ, rồi đây Ngài sẽ tưởng thưởng cho họ thích đáng theo những gì họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(2,75,'2:75','Hỡi những người có đức tin! Có phải các ngươi đang hy vọng những người Do Thái sẽ có đức tin và đáp lại lời kêu gọi của các ngươi chăng? Quả thật đã có một nhóm người thuộc giới học giả và tu sĩ của họ nghe thấy Lời Phán của Allah được ban xuống cho họ trong Kinh Tawrah nhưng rồi họ đã thay đổi lời lẽ và nội dung trong đó sau khi họ đã thực sự hiểu trong khi họ biết rõ hành động đó của họ là đại trọng tội.');
INSERT INTO "vn_mokhtasar" VALUES(2,76,'2:76','Một trong những điều gian xảo của những người Do Thái là khi gặp những người có đức tin thì họ thừa nhận họ thực sự đã tin nơi Nabi Muhammad và nơi Bức Thông Điệp Người mang đến và họ bảo Kinh Tawrah đã xác nhận điều đó, nhưng khi họ ở riêng nhóm của họ thì họ trách cứ lẫn nhau sao lại để cho những người có đức tin biết những gì trong Tawrah để rồi họ có cái để tranh luận về sứ mạng của Muhammad.');
INSERT INTO "vn_mokhtasar" VALUES(2,77,'2:77','Những người Do Thái này đi trên con đường sai trai đó chẳng lẽ họ tưởng rằng Allah không hay biết gì về những điều họ giấu giếm hay công khai từ lời nói và hành động của họ hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(2,78,'2:78','Trong số những người Do thái, có nhóm người chẳng biết gì về Kinh Tawrah, họ chẳng hiểu gì cả, những gì họ nói đều toàn là những lời nói dối bởi sự tự cao tự đại của họ và họ cứ tưởng đó là Kinh Tawrah mà Allah đã ban xuống.');
INSERT INTO "vn_mokhtasar" VALUES(2,79,'2:79','Sự hủy diệt và hình phạt khắc nghiệt đang đợi những người này, những người mà họ đã viết ra kinh sách bằng chính bàn tay của họ rồi dối trá rằng đó là Kinh Sách đến từ nơi Allah mục đích chỉ để đổi chác điều chần lý với một giá rẻ mạt của thế gian như của cải vật chất và quyền hành. Sự hủy diệt và hình phạt khắc nghiệt dành cho họ bởi những điều mà bàn tay của họ đã viết ra để dối lừa Allah và sự hủy diệt và hình phạt khắc nghiệt dành cho họ bởi những lợi lộc tầm thường mà họ kiếm được.');
INSERT INTO "vn_mokhtasar" VALUES(2,80,'2:80','Họ (những người Do Thái) nói trong sự lừa dối và gian trá rằng họ sẽ không bao giờ phải chịu hình phạt trong Hỏa Ngục trừ một số ngày ít ỏi. Ngươi - Muhammad hãy bảo họ: Há các người khẳng định như thế là bởi vì các người đã nhận được lời hứa của Allah hay sao? Và chắc chắn sự việc không phải như vậy bởi Allah không bao giờ thất hứa. Hoặc chẳng lẽ các người muốn dối gạt cả Allah về những điều mà các ngươi không biết gì ư?');
INSERT INTO "vn_mokhtasar" VALUES(2,81,'2:81','Sự việc không như những gì mà họ đã khẳng định bởi chắc chắn Allah sẽ trừng phạt tất cả những ai làm tội lỗi từ những việc làm vô đức tin và tội lỗi đã vây quanh con người họ; họ chắc chắn sẽ bị đày trong Hỏa Ngục và sẽ vĩnh viễn sống trong đó không có ngày trở ra, đó là điều hiển nhiên dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,82,'2:82','Những người có đức tin nơi Allah và nơi vị Thiên Sứ của Ngài đồng thời làm việc thiện tốt và hành đạo thì phần thưởng của họ nơi Allah là Thiên Đàng, họ sẽ vào sống mãi trong đó, và đó là điều hiển nhiên dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,83,'2:83','Các ngươi hãy nhớ lại - hỡi con cháu Isra-el - về Giao ước mà TA đã thiết lập với các ngươi, đó là các ngươi phải tôn thờ riêng một mình Allah, không thờ phương ai (vật) nào khác cùng với Ngài; các ngươi phải hiếu thảo và sống tử tế với cha mẹ, tử tế với bà con ruột thịt, các trẻ mồ côi và những người nghèo khó; các ngươi hãy nói năng nhã nhặn và lịch thiệp với mọi người trong việc kêu gọi làm việc thiện tốt và ngăn cản mọi người làm điều xấu, chớ buông lời thô tục và khiếm nhã; các ngươi phải chu đáo dâng lễ nguyện Salah và phải đóng Zakah tức trích một phần tài sản bắt buộc từ nguồn tài sản tốt lành của các ngươi đem phân phát cho người nghèo. Tuy nhiên, các ngươi đã quay lưng với những điều trong Giao ước đó của Allah bằng sự đại nghịch và bất tuân Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,84,'2:84','Các ngươi hãy nhớ lại hỡi người dân Isra-el khi TA thiết lập giao ước với các ngươi trong Kinh Tawrah từ việc cấm các ngươi gây đổ máu cho nhau, cấm các ngươi xua đuổi nhau ra khỏi nhà cửa của các ngươi, các ngươi đã thừa nhận giao ước đó và các ngươi là những người làm chứng cho tính xác thực của nó.');
INSERT INTO "vn_mokhtasar" VALUES(2,85,'2:85','Nhưng rồi các ngươi đã làm trái với giao ước của Allah, các ngươi đã giết hại lẫn nhau, các ngươi xua đuổi một thành phần trong các ngươi ra khỏi nhà cửa của họ, các ngươi tiếp tay cho kẻ thù chống lại họ bằng sự bất công và lòng hận thù, và khi họ rơi vào tay các ngươi như một tù binh thì các ngươi đòi tiền chuộc, các ngươi thừa biết các ngươi không có quyền đuổi họ ra khỏi nhà cửa của họ, việc làm đó của các ngươi bị cấm đoán. Sao các ngươi lại có thể tin một phần của Kinh Tawrah về việc bắt buộc chuộc tù binh nhưng lại phủ nhận một phần của Nó về việc gây ra sự đổ máu cũng như việc cấm xua đuổi nhau ra khỏi nhà cửa của các ngươi? Và những kẻ làm như thế chắc chắn sẽ bị trừng phạt một cách nhục nhã trên thế gian này và vào Ngày y còn phải chịu một hình phạt khác nặng nề hơn, và Allah không phải không hay biết những gì các ngươi làm mà thật ra Ngài đang theo dõi và quan sát các ngươi và Ngài sẽ trừng phạt các ngươi một cách thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(2,86,'2:86','Những kẻ đó chính là những kẻ đã đổi chác cuộc sống trần gian với cuộc sống cõi Đời Sau, họ lấy cõi đời tiêu vọng tạm bợ để đổi lấy cõi đời vĩnh hằng. Bởi vậy, vào Ngày Sau họ sẽ không được giảm nhẹ bất cứ hình phạt nào và họ cũng sẽ không có được bất kỳ sự giúp đỡ nào trong Ngày Hôm đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,87,'2:87','Và quả thật, TA đã ban cho Musa Kinh Tawrah và sau Y TA gửi đến các vị Thiên Sứ tiếp nối bước đường của Y, TA đã ban cho Ysa con trai của Maryam những phép lạ để chứng minh sự trung thực của Y như làm cho người chết sống lại, chữa bệnh căm điếc, v.v, cùng với sự hỗ trợ của đại Thiên Thần Jibril. Nhưng mỗi khi một vị Thiên Sứ nào được TA cử đến - hỡi người dân Isra-el - thì các ngươi đều không thuận ý, các ngươi tự cao tự đại không chịu chấp nhận sự thật, các ngươi phủ nhận một nhóm vị Thiên Sứ này và giết hại một nhóm vị Thiên Sứ khác.');
INSERT INTO "vn_mokhtasar" VALUES(2,88,'2:88','Quả thật người Do Thái lấy lý do không đi theo Muhammad với lời: trái tim của chúng tôi đã đóng lại, nó sẽ không nghe và không hiểu bất cứ điều gì ngươi nói. Nhưng thực tế không phải như họ đã khẳng định mà thật ra họ không tin là do Allah đã trục xuất họ khỏi lòng nhân từ của Ngài bởi sự vô đức tin của họ. Bởi thế, họ chỉ tin rất ít nơi điều Allah ban xuống.');
INSERT INTO "vn_mokhtasar" VALUES(2,89,'2:89','Và khi Kinh Qur''an từ nơi Allah đến với họ (người dân Isra-el) tương đồng với những gì được nói trong Kinh Tawrah và Injil về những điều căn bản - và mặc dầu trước đó họ luôn nói: "chúng tôi sẽ giành thắng lợi đối với những người thờ đa thần khi nào một vị Nabi được phái đến, chúng tôi tin nơi Người và sẽ đi theo Người" - nhưng rồi khi Qur''an và Nabi Muhammad đến với họ đúng với những gì mà họ thừa nhận là đúng theo Kinh sách của họ thì họ lại quay lưng phủ nhận. Bởi thế, Allah nguyền rủa những kẻ vô đức tin nơi Allah và Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,90,'2:90','Thật xấu xa thay về việc họ đã bán đi linh hồn của mình khi nó đã từng có đức tin nơi Allah và Thiên Sứ của Ngài trước đó, họ phủ nhận những gì Allah ban xuống và phủ nhận các vị Thiên Sứ của Ngài chỉ vì lòng đố kỵ cho việc sứ mạng Nabi và Qur''an được ban xuống cho Muhammad. Cho nên, họ đáng bị sự phẫn nộ của Allah từ sự phẫn nộ này đến sự phẫn nộ khác chồng chất lên nhau do sự vô đức tin của họ nơi Muhammad và do sự bóp méo của họ về một số nội dung của Kinh Tawrah. Và những kẻ vô đức tin nơi sứ mạng Nabi của Muhammad sẽ bị trừng phạt một cách nhục nhã vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(2,91,'2:91','Khi có lời bảo những người Do Thái rằng các người hãy tin nơi những gì được ban xuống cho vị Thiên Sứ của Ngài - Muhammad từ chân lý và sự hướng dẫn thì họ nói họ chỉ tin nơi những gì được ban xuống cho các vị Nabi của họ và phủ nhận những gì được ban xuống cho Muhammad. Họ phủ nhận Qur''an mặc dù Qur''an đã tương đồng với những gì được nói trong Kinh Tawrah của họ. Và nếu họ thực sự tin nơi những gì được ban xuống cho họ trước đó thì chắc chắn họ đã tin nơi Qur''an. Này hỡi vị Nabi Muhammad! Ngươi hãy trả lời họ: Nếu các ngươi thực sự tin nơi các vị Nabi của các ngươi trước đây thì cớ vì sao các ngươi lại giết hại họ?');
INSERT INTO "vn_mokhtasar" VALUES(2,92,'2:92','Và quả thật, vị Thiên Sứ của các ngươi, Musa đã đến với các ngươi bằng những phép lạ để chứng minh sự trung thực của Người nhưng rồi sau đó, khi Người đi vắng thì các ngươi lấy con bò đúc ra làm thần linh để thờ phượng, họ thật là những người sai quấy khi làm điều Shirk với Allah trong khi Ngài là Đấng duy nhất đáng được thờ phượng, không ai (vật gì) ngoài Ngài có quyền đó cả.');
INSERT INTO "vn_mokhtasar" VALUES(2,93,'2:93','Các ngươi hãy nhớ lại khi TA thiết lập giao ước cho các ngươi rằng các ngươi phải theo Musa và đón nhận những gì Y mang đến từ nơi Allah, và TA đã nhấc cao quả núi Saina'' lên bên trên các ngươi đe dọa các ngươi và phán bảo các ngươi: hãy nắm thật chắc những gì mà TA đã ban xuống cho các ngươi trong Kinh Tawrah bằng sự nghe theo và nỗ lực chấp hành mệnh lệnh, bằng không TA sẽ cho quả núi đè lên các ngươi. Nhưng họ nói: chúng tôi nghe bằng đôi tai nhưng chúng tôi không làm. Hình ảnh con bò đã ăn sâu vào trong trái tim của họ bởi sự vô đức tin của họ. Này Muhammad, Ngươi hãy bảo họ: nếu các ngươi là những người có đức tin thì thật xấu xa thay cho đức tin khiến các ngươi phủ nhận Allah bởi lẽ đức tin đích thực không đi cùng với sự vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(2,94,'2:94','Này hỡi Nabi Muhammad! Ngươi hãy nói với những người Do Thái rằng nếu Thiên Đàng ở cõi Đời Sau là nơi chỉ dành riêng cho họ chứ không cho ai khác trong nhân loại thì họ hãy mong cho được chết sớm để đạt được điều đó sớm hơn và để khỏi mệt mỏi nơi cõi trần nếu họ là những người nói thật về điều mà họ đã khẳng định.');
INSERT INTO "vn_mokhtasar" VALUES(2,95,'2:95','Chắc chắn sẽ không bao giờ họ dám mong được chết sớm bởi những tội lỗi mà họ đã làm trên thế gian từ việc vô đức tin nơi Allah, phủ nhận các vị Thiên Sứ của Ngài và bóp méo các Kinh Sách của Ngài; và Allah biết rõ những kẻ làm điều sai quấy và cả những ai khác ngoài họ và Ngài chắc chắn sẽ thưởng phạt công bằng tương thích với hành động của họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,96,'2:96','Này Nabi Muhammad, Người sẽ thấy những người Do Thái là những kẻ tham sống sợ chết hơn những ai khác trong nhân loại cho dù cuộc sống trần gian có rẻ mạt thế nào, không, họ còn tham sống hơn cả những người thờ đa thần, những kẻ mà chúng không tin nơi sự Phục sinh và Phán xét trong khi họ là dân Kinh Sách, họ tin nơi sự Phục sinh và Phán xét. Mỗi người của họ đều mong ước được sống thọ đến một ngàn năm tuổi nhưng cho dù họ có sống thọ lâu đến mấy thì họ cũng không thể thoát khỏi sự trừng phạt của Allah. Ngài am tường tất cả mọi hành động mà chúng đã làm bởi vì không có bất cứ điều gì có thể che giấu được Ngài và Ngài sẽ trừng phạt họ thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(2,97,'2:97','Này Nabi Muhammad, Ngươi hãy nói với những người Do Thái đã nói: "quả thật đại Thiên Thần Jibril là kẻ thù của chúng tôi": những ai là kẻ thù của Jibril - bởi vì Y mang Kinh Qur''an xuống đặt vào trái tim của Ngươi nhằm xác nhận những gì trong các Thiên Kinh trước như Tawrah và Injil và để hướng dẫn đến với điều tốt đẹp đồng thời làm một nguồn tin vui cho những người có đức tin về những gì mà Allah đã chuẩn bị cho họ nơi Thiên Đàng - thì họ là những kẻ lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(2,98,'2:98','Ai là kẻ thù của Allah, của các vị Thiên Thần, của các vị Thiên Sứ và của hai vị đại Thiên Thần Jibril và Mika-il thì Allah chính là kẻ thù của những kẻ vô đức tin đó, và những ai mà họ là kẻ thù của Allah thì họ là những kẻ thua thiệt và thất bại quá rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(2,99,'2:99','Quả thật, TA đã ban xuống cho Ngươi - hỡi Nabi Muhammad - những dấu hiệu rõ ràng để chứng minh sự trung thực của Ngươi về những gì Ngươi mang đến từ sứ mạng Nabi và sự Mặc Khải, tuy nhiên, chỉ có những kẻ làm điều sai quấy và đại nghịch bất đạo mới chối bỏ tôn giáo của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(2,100,'2:100','Một trong những bản chất xấu của người Do Thái là mỗi khi họ nhận lấy Giao Ước thì họ vứt bỏ nó sang một bên. Và một điều thuộc Giao Ước đó chính là tin nơi những gì mà Kinh Tawrah đã báo trước về sứ mạng Nabi của Muhammad nhưng một thành phần trong bọn họ đã gạt bỏ sự việc đó, không phải một thành phần mà đích thực đa số bọn họ đều không tin nơi những gì được Allah ban xuống bởi lẽ đức tin phải được thể hiện qua việc thực hiện và chấp hành theo Giao Ước.');
INSERT INTO "vn_mokhtasar" VALUES(2,101,'2:101','Khi Muhammad đến với họ trên cương vị là một vị Thiên Sứ của Allah ăn khớp với những gì được báo trước trong Kinh Tawrah thì một phần thành phần của họ phủ nhận, họ ném điều đó ra sau lưng họ giống như họ không biết không hay gì cả, họ hành động giống như những kẻ ngu dốt không hiểu gì từ sự chân lý và nguồn chỉ đạo cho nên họ không quan tâm và bất cần.');
INSERT INTO "vn_mokhtasar" VALUES(2,102,'2:102','Và khi chúng bỏ tôn giáo của Allah, đi theo những thứ mà Shaytan đã dối gạt và bịa đặt rằng nó đến từ quyền lực của Sulayman và cho rằng quyền lực của Người có được là bùa chú (pháp thuật và bùa ngải) nhưng Sulayman không hề vô đức tin bởi việc cho đi bùa chú như người Do Thái đã khẳng định mà chính những tên Shaytan đã phủ nhận đức tin khi họ dạy nhân loại bùa chú. Những tên Shaytan đã dạy loài người bùa chú, thứ mà đã được ban xuống cho hai vị Thiên Thần Harut và Marut tại thành phố Babylon (I-rắc) để làm điều Fitnah (sự cám dỗ và thử thách) nhân loại. Và hai vị Thiên Thần này không dạy bùa chú cho bất cứ ai ngoại trừ đã cảnh báo và trình bày rõ với lời rằng chúng tôi chỉ là sự cám dỗ và thử thách cho nhân loại cho nên chớ đừng vô đức tin với việc học bùa chú. Tuy nhiên, những ai không chấp nhận lời khuyên răn của hai vị Thiên Thần đó thì đã học bùa chú từ họ và trong thuật bùa chú có loại chuyên làm chia cách hai vợ chồng và gieo sự thù ghét giữa đôi vợ chồng. Và những người dùng thuật bùa chú không thể gây hại được bất cứ ai trừ phi có phép của Allah. Những người học thuật bùa chú chỉ mang lại điều thiệt hại cho bản thân chứ không mang lại lợi ích gì cho họ; và những người Do Thai đó đã đối lấy bùa chú với Kinh Sách của Allah cho nên vào Đời Sau họ sẽ không có được bất cứ phần phước nào cả; thật xấu xa thay cho những kẻ đã đổi chác lời mặc khải và giáo lý của Allah với thuật bùa chú, nếu họ biết điều tệ hại và xấu xa đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,103,'2:103','Giá như những người Do Thái chịu tin và kính sợ Allah một cách chân thật, họ chấp hành các mệnh lệnh của Ngài và từ bỏ những điều trái lệnh Ngài thì chắc chắn Allah đã ban cho họ phần thưởng tốt đẹp hơn, nếu như họ biết điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,104,'2:104','Allah phán bảo những người có đức tin lựa chọn các ngôn từ khi nói chuyện với Thiên Sứ của Allah rằng họ chớ nói với Người bằng tiếng "Ra-''ina" có nghĩa là ''hãy lắng nghe chúng tôi" nhưng đồng thời cũng mang nghĩa khùng điên và ngu muội. Bởi vì người Do Thái thường bóp méo tiếng nói cũng như nội dung ý nghĩa nhằm để nhạo báng Thiên Sứ của Allah nên Allah cấm dùng tiếng nói mang những hàm ý xấu khi nói chuyện với Thiên Sứ của Allah để khỏi rơi vào hành vi xúc phạm Người, Ngài ra lệnh cho những người bề tôi có đức tin của Ngài hãy dùng tiếng Unzhurna (hãy đợi chúng tôi) để thay cho Ra-''ina bởi vì tiếng Unzhurna không mang hàm ý xấu nào khác ngoài nghĩa gốc của nó. Và những kẻ vô đức tin nơi Allah sẽ bị trừng phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(2,105,'2:105','Những người vô đức tin dù là dân Kinh sách hay những người thờ đa thần đều không thích điều tốt đẹp nào từ nơi Thượng Đế của các ngươi được ban xuống cho các ngươi dù nhiều hay ít. Tuy nhiên, Allah quyết định ban sứ mạng Nabi và lời mặc khải cũng như đức tin Iman cho bất kỳ ai Ngài muốn trong đám bề tôi của Ngài. Và Allah là Đấng có vô tận hồng ân vĩ đại, và việc vị Thiên Sứ được dựng lên và việc Kinh Sách được ban xuống đều là từ hồng phúc của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,106,'2:106','Allah cho biết rằng việc Ngài xóa bỏ một điều luật nào trong câu Kinh Qur''an hoặc xóa bỏ lời câu Kinh và làm cho con người quên đi câu Kinh đó thì chắc chắn Ngài sẽ mang đến một câu Kinh khác hoặc một giáo lý khác tốt đẹp hơn cho đời này và Đời Sau hoặc Ngài sẽ mang đến một câu Kinh có giá trị ngang bằng với câu Kinh cũ. Tất cả đều do kiến thức vô hạn và sự anh minh tuyệt đối của Allah, và Ngươi (Muhammad) chẳng lẽ không biết rằng Allah là Đấng Toàn Năng trên tất cả mọi sự việc, Ngài muốn làm gì tùy ý Ngài và Ngài quyết định điều gì tùy ý Ngài hay sao?.');
INSERT INTO "vn_mokhtasar" VALUES(2,107,'2:107','Này hỡi Nabi Muhammad! Chắc chắn Ngươi biết rõ rằng Allah là Đấng Chủ Tể của các tầng trời và trái đất, Ngài muốn quyết định điều gì tùy ý Ngài, ra lệnh bất cứ điều gì tùy ý Ngài, Ngài cấm điều gì Ngài muốn và ban hành điều luật nào Ngài muốn, Ngài xóa bỏ điều gì Ngài không thích và giữ lại điều gì Ngài hài lòng, không ai có quyền can thiệp, chỉ một mình Ngài duy nhất có toàn quyền và toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(2,108,'2:108','Hỡi những người có đức tin! Không phải là việc của các ngươi khi mà các ngươi cứ vặn hỏi Thiên Sứ Muhammad của các ngươi giống như cư dân của Musa đã vặn hỏi Y, chẳng hạn họ đã đòi hỏi Musa hãy cho họ nhìn thấy Allah một cách công khai (chương - Al-Nisa'', câu 153). Và người nào từ sự có đức tin đổi thành vô đức tin thì kẻ đó thực sự đã đi lệch khỏi con đường Ngay Chính, đó là con đường của Islam.');
INSERT INTO "vn_mokhtasar" VALUES(2,109,'2:109','Đa số người dân kinh sách tức những người Do Thái và Thiên Chúa luôn mong muốn các ngươi quay trở lại tình trạng vô đức tin giống như tình trạng các ngươi đã từng thờ phượng các bục tượng, nguyên nhân là vì lòng ganh tị nơi bản thân chúng; chúng mong muốn điều đó sau khi việc Nabi Muhammad mang đến Bức Thông Điệp từ nơi Allah đã được chứng minh rõ ràng là sự thật ở nơi chúng. Bởi vậy, các ngươi hãy lượng thứ cho chúng - hỡi những người có đức tin - về những việc làm của chúng, và các ngươi hãy bỏ qua cho sự thiếu hiểu biết của chúng cũng như lòng dạ xấu xa của chúng cho tới khi Allah ban hành mệnh lệnh của Ngài xuống - những người vô đức tin sẽ lựa chọn giữa Islam và đóng thuế hoặc chiến tranh - Quả thật Allah là Đấng Toàn Năng trên tất cả mọi thứ, không có bất cứ thứ gì có thể khiến Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(2,110,'2:110','Các ngươi hãy chu đáo thực hiện các lễ nguyện Salah, các ngươi hãy chỉnh chu các Rukun của nó, các Wajib của nó và các Sunnah của nó; các ngươi hãy xuất Zakah phần tài sản của các ngươi cho những đối tượng được hưởng theo qui định; tất cả mọi việc làm ngoan đạo và thiện tốt mà các ngươi đã làm trên thế gian này là những thứ mà các ngươi đã gửi đi trước cho bản thân của các ngươi ở Đời Sau và chắc chắn các ngươi sẽ có được những ân phước của những việc làm đó của các ngươi ở nơi Allah vào Ngày Phán Xét, bởi quả thật Allah nhìn thấy hết tất cả những gì các ngươi làm và Ngài sẽ ban thưởng thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(2,111,'2:111','Những người Do Thái và những người Thiên Chúa luôn cho rằng Thiên Đàng là dành riêng cho họ. Những người Do Thái thì bảo: không ai vào được Thiên Đàng ngoại trừ người Do Thái, những người Thiên Chúa thì bảo: không ai vào được Thiên Đàng ngoại trừ người Thiên Chúa. Tuy nhiên, đó chỉ là những mơ ước hão huyền, nó sẽ không bao giờ trở thành hiện thực. Ngươi - Muhammad, hãy đáp lại lời của chúng: nào các ngươi hãy đưa ra các bằng chứng về điều các ngươi khẳng định quả quyết nếu các ngươi là những người nói thật.');
INSERT INTO "vn_mokhtasar" VALUES(2,112,'2:112','Chỉ những ai toàn tâm, toàn ý hướng về Allah đồng thời luôn hoàn thành tốt trong nghĩa vụ thờ phượng Ngài, đi theo đúng sự hướng dẫn của vị Thiên Sứ của Ngài thì mới được vào Thiên Đàng, cho dù là họ là nhóm người nào đi chăng nữa, họ sẽ được phần ban thưởng ở nơi Thượng Đế của họ, họ sẽ không lo sợ về những gì ở tương lai của cõi Đời Sau và họ cũng sẽ không buồn phiền về những gì mà họ đã mất trên cõi trần. Và những điều này chỉ được chứng thực đối với ai qui phục Islam sau khi Nabi Muhammad đến.');
INSERT INTO "vn_mokhtasar" VALUES(2,113,'2:113','Người Do Thái nói người Thiên Chúa giáo không phải là tôn giáo đúng, còn người Thiên Chúa thì nói người Do Thái không đi trên tôn giáo đúng trong khi tất cả họ đều đọc các Kinh Sách được Allah ban xuống và trong các Kinh sách đó đều có lệnh bảo rằng phải tin nơi tất cả các vị Nabi không được phép phân biệt giữa họ; và những người thờ đa thần cũng nói những lời tương tự giống như họ khi mà họ phủ nhận các vị Thiên Sứ cũng như những gì được ban xuống cho họ từ các Kinh Sách. Rồi đây, vào Ngày Phục Sinh, Allah sẽ phán xét và phân xử giữa những điều mà họ mâu thuẫn với nhau, Ngài sẽ xét xử công minh và chỉ những ai có đức tin nơi tất cả những gì Ngài ban xuống thì mới là những người thành đạt.');
INSERT INTO "vn_mokhtasar" VALUES(2,114,'2:114','Không có người nào bất công và sai trái hơn những kẻ đã ngăn cản việc tụng niệm Allah trong các Nhà Nguyện của Ngài, họ ngăn dâng lễ nguyện Salah, tụng niệm và đọc Qur''an trong đó và họ cố tìm cách đập phá và làm hư hại các Nhà Nguyện. Những kẻ này, không để cho họ vào các Nhà Nguyện trừ phi phải làm cho họ sợ bởi sự vô đức tin và sự phá hại của họ. Những kẻ này không những bị hạ nhục trên cõi trần mà rồi đây họ sẽ phải bị trừng phạt nặng nề ở cõi Đời Sau cho việc đã ngăn cản mọi người đến các Nhà Nguyện của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(2,115,'2:115','Hướng đông, hướng tây và những gì giữa hai hướng đó đều thuộc vương quyền của Allah, Ngài ra lệnh cho các bề tôi của Ngài với những gì Ngài muốn, và cho dù các ngươi có quay mặt về hướng nào thì các ngươi cũng đều hướng về Ngài cả, dù các ngươi hướng về ngôi đền Al-Maqdis (Palestine) hay ngôi đền K''abah (Makkah) hoặc các ngươi hướng về nơi khác do nhầm lẫn hoặc do không thể xác định được thì các ngươi không bị bắt tội bởi lẽ tất cả mọi hướng đều thuộc về Allah. Quả thật, Allah bao la phủ lấy tất cả tạo vật của Ngài bằng lòng nhân từ và sự dễ dãi của Ngài và Ngài hằng biết tất cả tâm niệm cũng như các hành vi và việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,116,'2:116','Những người Do Thái, những người Thiên Chúa giáo và những người thờ đa thần nói: Allah có một đứa con trai. Allah vinh quang và thiêng liêng hơn điều họ nói, Ngài không cần đến tạo vật, chỉ những ai cần con cái mới có con chứ riêng Ngài không cần, tất cả vũ trụ và vạn vật đều thuộc về Ngài, tất cả vạn vật đều là những bề tôi của Ngài, tất cả phải thần phục và tuân thủ theo mệnh lệnh và ý muốn của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,117,'2:117','Allah là Đấng đã thiết lập và sáng tạo ra các tầng trời và trái đất và những gì giữa chúng từ cái "không". Và khi Ngài muốn định điều gì thì Ngài chỉ cần phán với lệnh "hãy thành" thì nó sẽ thành theo ý muốn của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,118,'2:118','Những người không biết gì từ nhóm dân Kinh Sách và những người thờ đa thần nói: tại sao Allah không nói chuyện trực tiếp với bọn ta hoặc tại sao không có một dấu hiệu nào đặc biệt dành riêng cho bọn ta? Và những lời nói này cũng đã được những kẻ vô đức tin trước họ đã nói khi các vị Thiên Sứ được cử phái đến. Quả thật TA đã trình bày rõ ràng các lời mặc khải của TA cho những người vững tin nơi điều chân lý, họ không ngờ vực và cũng không bị cản trở bởi bất cứ chướng ngại vật nào.');
INSERT INTO "vn_mokhtasar" VALUES(2,119,'2:119','Này hỡi Nabi Muhammad! Quả thật TA đã cử Ngươi đến với tôn giáo chân lý, không có gì phải ngờ vực, để Ngươi báo tin mừng về Thiên Đàng cho những người có đức tin và cảnh báo về Hỏa Ngục cho những kẻ vô đức tin; và Ngươi chỉ là một người truyền đạt lại một cách công khai nên Ngươi sẽ không bị chất vất về việc những kẻ không tin nơi Ngươi khi chúng bị đày trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(2,120,'2:120','Allah phán nói chuyện với vị Nabi của Ngài - Muhammad với lời cảnh báo: Những người Do Thái và những người Thiên Chúa sẽ không bao giờ bằng lòng với Ngươi cho tới khi nào Ngươi từ bỏ Islam và đi theo tôn giáo của chúng. Nếu Ngươi hoặc những ai đi theo Ngươi từ bỏ Islam sau khi sự thật rõ ràng đã đến với Ngươi thì chắc chắn Ngươi và những người đó sẽ không bao giờ tìm thấy sự cứu giúp nào để thoát khỏi sự trừng phạt của Allah. Đây chỉ là sự trình bày về sự nguy hại cho việc từ bỏ chân lý để đến với điều sai trái và ngụy tạo.');
INSERT INTO "vn_mokhtasar" VALUES(2,121,'2:121','Kinh Qur''an phán rằng nhóm người dân Kinh Sách chịu làm theo những gì được ban xuống trong các Kinh Sách và họ đi theo đúng với lẽ đi theo thì chắc chắn họ sẽ tìm thấy trong các Kinh Sách đó những bằng chứng rõ rệt về sự trung thực của Nabi Muhammad và chắc chắn họ sẽ nhanh chóng có đức tin nơi Người; còn một nhóm khác cứ ngoan cố ở lại trên sự vô đức tin thì họ là những kẻ thua thiệt và thất bại.');
INSERT INTO "vn_mokhtasar" VALUES(2,122,'2:122','Hỡi dân chúng Isra-el! Các ngươi hãy nhớ lại các Ân Huệ mà Allah đã ban cho các ngươi cũng như những gì mà Ngài đã ưu đãi các ngươi hơn hẳn những nhóm người khác trong nhân loại từ sứ mạng Nabi, vương quyền và những ưu đãi đặc biệt khác.');
INSERT INTO "vn_mokhtasar" VALUES(2,123,'2:123','Các ngươi hãy đặt giữa các ngươi và sự trừng phạt của Ngày Phán Xét một vách chắn phòng ngừa qua việc đi theo các mệnh lệnh của Allah và tránh những điều ngắn cấm của Ngài, bởi quả thật vào Ngày hôm đó, không ai có thể giúp ích được cho ai, và cũng không có sự thế thân hay chuộc tội cho nhau, sự can thiệp trong Ngày hôm đó sẽ trở nên vô ích cho dù kẻ can thiệp có vị trí cao cả thế nào, và Ngày hôm đó sẽ không có một thế lực che chở và phù hộ nào ngoài Allah cả.');
INSERT INTO "vn_mokhtasar" VALUES(2,124,'2:124','Và hãy nhớ lại lúc Allah thử thách Nabi Ibrahim qua những trọng trách và giáo luật và Người đã hoàn thành tốt đẹp những thử thách của Ngài. Allah phán với Nabi Ibrahim: quả thật, TA sẽ để Ngươi làm một tấm gương đức hạnh và tốt đẹp cho nhân loại. Nabi Ibrahim nói: và xin Ngài - ôi Thượng Đế của bề tôi - hãy ban điều đó cho cả thế hệ con cháu của bề tôi nữa tức hãy để con cháu của bề tôi là những vị Imam cho nhân loại noi theo. Allah phán đáp lại lời của Nabi Ibrahim: lời hứa của TA dành cho Ngươi về vấn đề Imam trong đạo không dính dấp tới những kẻ làm điều sai quấy trong đám thế hệ con cháu của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,125,'2:125','Và nhớ lại lúc Allah qui định lấy Ngôi Đền K''abah ở Nhà nguyện Al-Haram làm nơi tới lui thăm viếng cho những ai có trái tim hướng về Nó, TA đã làm cho Nó thành một nơi an toàn cho họ, không ai có thể gây hại đến họ tại nơi đó. TA đã phán với nhân loại: các ngươi hãy lấy chỗ của tảng đá mà Ibrahim đã đứng trên đó trong lúc xây Ngôi Đền K''abah (được gọi là Maqam Ibrahim) làm nơi dâng lễ nguyện Salah. Và TA đã dặn Ibrahim và con trai của Y - Isma-''il phải tẩy sạch Nhà nguyện Alharam khỏi những thứ dơ bẩn và luôn giữ Nó thanh khiết khỏi những bục tượng để tiếp đón những ai muốn thờ phượng TA trong đó bằng việc đi vòng quanh Ngôi Đền K''abah, ở lại trong đó lánh trần để chuyên tâm thờ phượng, dâng lễ nguyện Salah và các việc làm hành đạo khác.');
INSERT INTO "vn_mokhtasar" VALUES(2,126,'2:126','Này hỡi Nabi! Ngươi hãy nhớ lại lúc Ibrahim cầu nguyện Thượng Đế của Y, nói: Lạy Thượng Đế của bề tôi, xin Ngài làm cho thành phố này (Makkah) thành nơi an bình khỏi những điều nguy hại, xin Ngài nuôi sống cư dân của nó với các loại hoa quả và cây trái, đặc biệt đối với những ai có đức tin nơi Ngài và cõi Đời Sau. Allah phán: còn ai trong số chúng vô đức tin thì TA sẽ cho y hưởng thụ trên cõi trần trong một lúc ngắn ngủi rồi vào cõi Đời Sau TA sẽ cho lôi cổ y ném vào trong Hỏa Ngục, một nơi ở tồi tệ nhất mà y phải quay trở về vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(2,127,'2:127','Này hỡi Nabi! Ngươi hãy nhớ lại lúc Ibrahim và Isma-''il xây móng Ngôi Đền K''abah và hai người họ đã cầu nguyện trong sự hạ mình và kính cẩn: lạy Thượng Đế của bầy tôi, xin Ngài chấp nhận việc làm của bầy tôi - trong đó có việc xây dựng Ngôi Đền này - bởi quả thật Ngài là Đấng đáp lại lời cầu nguyện và Ngài là Đấng am tường những việc làm cũng như tâm niệm của bầy tôi.');
INSERT INTO "vn_mokhtasar" VALUES(2,128,'2:128','Lạy Thượng Đế của bầy tôi, xin Ngài làm cho bầy tôi thành những người luôn thần phục mệnh lệnh của Ngài, bầy tôi không tổ hợp với Ngài bất cứ điều gì, xin Ngài hãy tạo ra từ thế hệ con cháu của bầy tôi một cộng đồng thần phục mệnh lệnh của Ngài, xin Ngài chỉ dạy bầy tôi cách thức thờ phượng Ngài và xin xí xóa các hành vi sai trái của bầy tôi cũng như sự thiếu sót của bề tôi trong thờ phượng Ngài; bởi quả thật, Ngài là Đấng Hằng Tha Thứ cho những ai biết quay đầu sám hối với Ngài và là Đấng Rất Mực Khoan Dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,129,'2:129','Lạy Thượng Đế của bầy tôi, xin Ngài hãy dựng lệnh trong chúng (con cháu của bầy tôi) một vị Thiên Sứ xuất từ chúng để y đọc các Lời Mặc Khải của Ngài cho họ nghe và để y dạy chúng Qur''an và Sunnah, và để y thanh sạch chúng khỏi tội Shirk và sự lầm lạc; quả thật Ngài là Đấng Quyền Năng trên tất cả mọi thứ và Ngài là Đấng Anh Minh và Khôn Ngoan trong mọi phán quyết.');
INSERT INTO "vn_mokhtasar" VALUES(2,130,'2:130','Không một ai quay mặt bỏ đi trước tôn giáo của Ibrahim để đến với các tôn giáo khác ngoại trừ những ai bất công với chính bản thân họ do sự ngu dốt không hiểu được điều chân lý, họ đã rời bỏ chân lý để đến với sự lầm lạc, rời bỏ sự hài lòng của Allah đến với sự phẫn nộ của Ngài. Quả thật, TA đã chọn Ibrahim làm vị Thiên Sứ và Khaleel trên thế gian này và ở cõi Đời Sau Y sẽ là một trong những người ngoan đạo, những người mà họ đã chấp hành tốt các mệnh lệnh và chỉ thị của Allah, và họ sẽ là những người ở trên tầng cao cấp nhất.');
INSERT INTO "vn_mokhtasar" VALUES(2,131,'2:131','Allah đã lựa chọn Ibrahim bởi vì sự nhanh chóng đến với Islam của Người lúc Ngài phán với Người: "hãy thành tâm thờ phượng TA, hãy thần phục một mình TA" thì Người liền đáp: bề tôi xin thần phục Allah, Đấng Tạo Hóa và Trông Coi vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(2,132,'2:132','Nabi Ibrahim dặn con cái của Người với lời: "Bề tôi thần phục Thượng Đế của vũ trụ và muôn loại". Nabi Y''aqub cũng dặn các con của Người với lời: này các con, quả thật Allah đã chon cho các con tôn giáo Islam, bởi thế các con hãy nắm chặt lấy nó cho tới khi các con đối mặt với cái chết, các con hãy là những người thần phục Allah một cách công khai và thầm kín.');
INSERT INTO "vn_mokhtasar" VALUES(2,133,'2:133','Chẳng lẽ các ngươi không nghe được thông tin về Y''aqub lúc lâm chung đã hỏi các các con của mình: các ngươi sẽ thờ gì sau khi cha chết? Các con của Y''aqub đều trả lời: chúng con sẽ thờ Thượng Đế của cha, Thượng Đế của tổ tiên cha Ibrahim, Isma-''il và Is-haq, một Thượng Đế Duy Nhất, chúng con không tổ hợp với Ngài bất cứ ai (vật gì), và chúng con chỉ thần phục một mình Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,134,'2:134','Đó là cộng đồng đã đi qua trước thời các ngươi, họ đã gửi đi trước những việc làm của họ, họ sẽ nhận được điều tốt hoặc điều xấu tùy theo việc làm của họ; còn các ngươi cũng sẽ nhận được kết quả tốt hay xấu tùy theo việc làm của các ngươi, các ngươi sẽ không bị chất vất về các việc làm của họ và họ cũng sẽ không bị chất vất về việc làm của các ngươi, người này không bị bắt tội thay cho người kia mà mỗi người sẽ chỉ nhận được kết quả đúng theo những gì mà y đã gửi đi trước, bởi thế, các ngươi chớ bận tâm đến những gì đã qua của cộng đồng trước vì không một ai hưởng được lòng thương xót của Allah ngoại trừ những việc làm thiện tốt và ngoan đạo (được Ngài qui định).');
INSERT INTO "vn_mokhtasar" VALUES(2,135,'2:135','Người Do Thái nói với cộng động này (cộng đồng Islam): các người hãy là những người Do Thái để được hướng dẫn đúng Chính Đạo; còn người Thiên Chúa thì nói: hãy là những người Thiên Chúa để được hướng dẫn đúng Chính Đạo. Ngươi - Muhammad hãy đáp lại lời họ: không chúng tôi chỉ đi theo tôn giáo của Ibrahim, một tôn giáo chân lý và Ibrahim không phải là người thuộc những người đã tổ hợp với Allah một ai (vật gì).');
INSERT INTO "vn_mokhtasar" VALUES(2,136,'2:136','Hỡi những người có đức tin! Các ngươi hãy nói với những kẻ cho rằng mình đang đi trên con đường chân lý nhưng thực chất là con đường lầm lạc và ngụy tạo từ những người Do Thái và Thiên Chúa: Chúng tôi tin nơi Allah; nơi Qur''an được ban xuống cho chúng tôi; chúng tôi tin nơi những gì được ban xuống cho Nabi Ibrahim và con cháu của Người: Isma-''il, Is-haq và Y''aqub; chúng tôi tin nơi những điều được ban xuống cho các vị Nabi từ con cháu của Nabi Y''aqub; chúng tôi tin nơi Kinh Tawrah được ban xuống cho Nabi Musa, nơi Kinh Injil được ban xuống cho Nabi Ysa; và chúng tôi tin nơi tất cả các Kinh Sách được Allah ban xuống cho tất cả các vị Nabi của Ngài, chúng tôi không kỳ thị phân biệt bất kỳ ai trong số họ, chúng tôi không tin một số vị và phủ nhận một số mà chúng tôi tin toàn bộ; và chúng tôi là những người chỉ thần phục một mình Allah.');
INSERT INTO "vn_mokhtasar" VALUES(2,137,'2:137','Nếu những người Do Thái và những người Thiên Chúa giáo cũng như những nhóm người vô đức tin khác có đức tin giống như đức tin của các ngươi - hỡi những người có đức tin - thì họ đã được hướng dẫn đến với chính đạo được Allah hài lòng; nhưng nếu họ quay lưng với đức tin qua việc phủ nhận toàn bộ các vị Nabi hoặc một số vị thì họ mới chính là những kẻ chia rẽ và tạo ra mối thù hằn. Tuy nhiên, Ngươi - Muhammad - chớ buồn lòng bởi một mình Allah thôi là đã đủ giúp Ngươi đương đầu với hành vi gây hại của họ, chỉ một mình Ngài thôi là đã đủ giúp Ngươi giành thắng lợi trước họ bởi lẽ Ngài là Đấng Hằng Nghe những lời nói của họ và biết hết tâm niệm và việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,138,'2:138','Các ngươi hãy bám lấy tôn giáo của Allah, tôn giáo mà Ngài đã ban cho các ngươi thích ứng với quy luật tự nhiên một cách công khai và thầm kín. Quả thật, không có tôn giáo nào tốt hơn tôn giáo của Allah và nó phù hợp với bản chất tự nhiên, tôn giáo hướng thiện và ngăn cản điều xấu. Thế nên, các ngươi hãy nói: chúng tôi là những người thờ phượng một mình Allah, chúng tôi không tổ hợp Ngài với bất kỳ ai (vật gì) khác ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,139,'2:139','Hỡi Nabi Muhammad! Ngươi hãy nói với bọn chúng - dân Kinh Sách: chẳng lẽ các người lại tranh luận vợi chúng tôi rằng các người ở nơi Allah tốt hơn chúng tôi bởi vì tôn giáo của các người cổ xưa hơn và Kinh sách của các người được ban xuống trước, quả thật điều đó không mang lại ích lợi gì cho các người ở nơi Allah, quả thật Allah là Thượng Đế của toàn bộ chứ không phải là Thượng Đế của riêng nhóm người nào đó. Và chúng tôi sẽ bị chất vất về việc làm của chúng tôi còn các người sẽ bị chất vất về việc làm của các người, mỗi người đều sẽ có được kết quả tương thích với việc làm của y; và chúng tôi một lòng tôn thờ và tuân phục một mình Allah, chúng tôi không tổ hợp với Ngài bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(2,140,'2:140','Hỡi dân Kinh Sách! Chẳng lẽ các ngươi cho rằng Ibrahim, Isma-''il, Is-haq, Y''aqub và các vị Nabi thuộc con cháu của Y''aqub đều là đạo Do Thái hay Thiên Chúa? Ngươi - Muhammad hãy đáp lại lời của chúng: há các người biết hơn Allah chăng? Bởi quả thật họ đã khẳng định rằng đều thuộc môn phái của họ thực ra điều là giả dối,bởi vì sự phục sinh họ và cái chết của họ trước lúc ban xuống kinh Tawrah (cho Musa) và Kinh Injil (cho Ysa). Do đó tất cả đều là lời nói dối và bịa đặt cho Allah và các vị Thiên Sứ của Ngài. Họ đã che giấu sự thật được ban xuống cho họ, và không ai sai quấy hơn kẻ che giấu sự thật từ nơi Allah đã được ban xuống. Và Allah không phải không hay biết về những gì các ngươi đã làm, chắc chắn rồi đây Ngài sẽ thưởng phạt một cách thỏa đáng.');
INSERT INTO "vn_mokhtasar" VALUES(2,141,'2:141','Cộng đồng đó đã ra đi trước các ngươi, những việc làm của họ cũng đã gửi đi trước việc làm của các ngươi, họ sẽ hưởng thành quả từ các việc làm của họ còn các ngươi sẽ hướng thành quả theo các việc làm của các ngươi, họ không bị chất vấn về việc làm của các ngươi và các ngươi cũng không bị chất vất bởi việc làm của họ, không ai thay tội cho ai, không ai can thiệp được giùm ai mà mỗi người sẽ hưởng được thành quả cho những gì mình đã gửi đi trước.');
INSERT INTO "vn_mokhtasar" VALUES(2,142,'2:142','Những kẻ dốt nát, tư duy kém thuộc tộc Do Thái và những ai tương tự thuộc nhóm Munafiq (đạo đức giả) sẽ nói: "Những người Muslim sao lại không tiếp tục hướng mặt về Qiblah (hướng quay mặt khi lễ nguyện Salah) như trước đây họ đã từng hướng?" Ngươi - hỡi Nabi - hãy nói đáp lại lời của chúng: "Hướng đông, hướng tây và các hướng khác đều là của một mình Allah. Ngài hướng bất cứ ai trong đám nô lệ của Ngài về bất cứ hướng nào tùy Ngài, và Ngài muốn hướng dẫn bất cứ ai tùy Ngài trong đám nô lệ của Ngài đến với con đường ngay chính, tức không bị uốn khúc, quanh co (bởi sự lầm lạc).');
INSERT INTO "vn_mokhtasar" VALUES(2,143,'2:143','Giống như việc TA (Allah) đã qui định cho các ngươi hướng Qiblah mà TA đã hài lòng cho các ngươi, TA đã làm cho các ngươi thành một cộng đồng Wasat tức một cộng đồng công bằng, chính trực và dung hòa so với tất cả các cộng đồng khác về đức tin, sự thờ phượng và cách cư xử giao tiếp; mục đích để vào Ngày Phục Sinh các ngươi trở thành nhân chứng cho các vị Thiên Sứ của Allah rằng họ đã hoàn thành sứ mạng truyền bá mà Allah đã ra lệnh và để Thiên Sứ Muhammad làm nhân chứng cho các ngươi rằng Y đã hoàn thành sứ mạng truyền đạt đến với các ngươi. Và việc TA thay đổi hướng Qiblah từ Masjid Al-Aqsa (Palestine) sang hướng Masjid Al-Haram (Makkak) là muốn biết ai hài lòng, tuân thủ theo định luật của Allah và noi theo Thiên Sứ và ai là kẻ phản đạo, chạy theo dục vọng và không qui phục giáo luật của Allah. Lệnh thay đổi hướng Qiblah đúng là một sự việc lớn lao nhưng đối với ai được Allah phù hộ cho đức tin vững chắc thì sự việc đó không làm cho họ ngần ngại trong việc tuân thủ bởi những qui định Allah đã ban hành đều phải được tuân thủ tuyệt đối. Và Allah không hề đánh mất phần công sức cho đức tin của các ngươi trước đó tức lễ nguyện Salah đã thực hiện trước khi thay đổi hướng Qiblah. Quả thật, Allah luôn nhân từ và thương xót nhân loại, Ngài không hề gây khó dễ họ lại càng không đánh mất phần thưởng qua việc thiện mà họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(2,144,'2:144','Quả thật, TA (Allah) đã thấy Ngươi - hỡi Nabi Muhammad - đã ngẩn đầu nhìn lên trời bằng ánh mắt khao khát được thiên khải về vấn đề đổi Qiblah theo hướng mà Ngươi mong muốn. Bởi thế, TA đã cho lệnh Ngươi hướng mặt về hướng mà Ngươi đã yêu thích và hài lòng - đó là Ngôi Đề Ka''bah - thay thế Masjid Al-Aqsa. Từ nay, Ngươi hãy hướng mặt mình về hướng ngôi nhà của Allah Al-Haram tại Makkah lúc lễ nguyện Salah. Và các ngươi - hỡi những người có đức tin - hãy quay mặt về hướng đó khi lễ nguyện Salah. Và quả thật, những kẻ hiểu biết về Kinh Sách thuộc tộc Do Thái và Thiên Chúa đều biết rõ việc chuyển hướng Qiblah đúng thực là Chỉ Đạo được thiên khải từ Đấng Tạo Hóa của chúng, Đấng Quản Lý mọi công việc của chúng, bởi vì nó được khẳng định trong Kinh Sách của chúng. Và quả thật Allah không phải không biết những gì mà những kẻ chống đối chân lý đang làm. Thật vinh quang thay Ngài, Ngài luôn am tường va thông toàn điều đó và Ngài sẽ trừng phạt chúng thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(2,145,'2:145','Thề bởi Allah, cho dù Ngươi - hỡi Nabi Muhammad - có mang ra trưng bày trước những kẻ được ban cho Kinh Sách thuộc tộc Do Thái và Thiên Chúa tất cả bằng chứng và dấu lạ rằng việc thay đổi Qiblah là Chỉ Đạo từ Allah thì chúng vẫn không chịu hướng theo hướng Qiblah mà Ngươi đã được chỉ thị do bản chất tự cao tự đại không muốn theo chân lý. Và dĩ nhiên Ngươi không quay mặt về Qiblah của chúng sau khi Allah đã đổi hướng cho các ngươi; và trong họ nhóm này vẫn không chịu hướng mặt theo Qiblah của nhóm kia, bởi chúng vốn phủ nhận lẫn nhau. Và nếu Ngươi chìu theo ham muốn của chúng về vấn đề Qiblah, về giáo luật, giáo lý mà Ngươi đã nhận được Chị Đạo đích thực từ Allah thì Ngươi sẽ trở thành tên lầm lạc vì đã bỏ đi Chỉ Đạo và đi theo ham muốn. Đây là lời phán đến Nabi Muhammad về vấn đề thay đổi Qiblah, tuy nhiên, Allah đã phù hộ Y tránh phạm sai lầm; cho nên đây là lời cảnh cáo đến toàn thể tín đồ Muslim sau Người.');
INSERT INTO "vn_mokhtasar" VALUES(2,146,'2:146','Những người được ban cho Kinh Sách từ các học giả thuộc tộc Do Thái và Thiên Chúa đều hiểu rõ việc thay đổi Qiblah là một dấu hiệu chứng minh sứ mạng của Muhammad đối với họ, giống như họ nhận biết rõ về con cái họ vậy, họ biết phân biệt được con cái với ai khác chúng, dù vậy vẫn có một nhóm người trong bọn họ cố tình giấu che chân lý mà Người đã mang đến, với lòng ganh tỵ họ đã ngoan cố đối nghịch trong khi họ biết đó là chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(2,147,'2:147','Đây đích thực là chân lý đến từ Thượng Đế Ngươi - hỡi Thiên Sứ Muhammad - thế nên, Ngươi chớ là kẻ nghi ngờ về sự thật của nó.');
INSERT INTO "vn_mokhtasar" VALUES(2,148,'2:148','Mỗi cộng đồng đều có một hướng riêng để họ quay mặt về đó bằng thể xác hoặc chỉ bằng tấm lòng được Allah qui định phù hợp cho họ. Cho dù có khác nhau về hướng quay mặt nhưng tất cả đều là mệnh lệnh của Allah. Bởi thế các ngươi - hỡi những người có đức tin - hãy thi đua làm những việc làm tốt đẹp mà Allah đã ra lệnh thực hiện chúng. Và dù các ngươi có ở đâu đi chăng nữa thì Allah cũng sẽ tập trung tất cả các ngươi trở lại trong Ngày Phục Sinh để thưởng phạt mọi việc làm của các ngươi. Quả thật, Allah toàn năng trên tất cả mọi thứ, bởi thế, việc tập hợp các ngươi cũng như việc thưởng phạt các ngươi không làm Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(2,149,'2:149','Và dù bất cứ nơi nào Ngươi đi và bất cư nơi nào Ngươi có mặt - hỡi Nabi - Ngươi cũng như tín đồ của Ngươi, mỗi khi muốn hành lễ Salah thì hãy hướng mặt về Masjid Al-Haram, bởi đó là sự thật mà Thượng Đế đã thiên khải cho Ngươi. Allah không phải không hay biết những gì các ngươi làm, ngược lại, Ngài luôn quan sát và theo dõi từng hành động của các ngươi và Ngài sẽ thưởng phạt các ngươi theo các việc làm đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,150,'2:150','Và dù từ bất cứ nơi nào Ngươi - hỡi Nabi - rời đi và muốn hành lễ Salah thì Ngươi hãy hướng mặt về Masjid Al-Haram, và dù các ngươi đang ở đâu - hỡi những người có đức tin - các ngươi hãy hướng mặt về Masjid Al-Haram khi các ngươi muốn hành lễ Salah, để thiên hạ không có chứng cứ phản bác lại các ngươi ngoại trừ những ai lầm lạc trong chúng. Và chúng vẫn tiếp tục chống đối và phản bác các ngươi bằng những lý do lố bịch, vì vậy các ngươi đừng sợ hãi chúng mà hãy sợ một mình Thượng Đế của các ngươi bằng cách thực thi theo mọi lệnh Ngài đã bảo ban các ngươi và tránh xa mọi điều Ngài đã cấm các ngươi. Quả thật, việc Allah qui định thay đổi hướng Qiblah về Ka''bah là để ban đầy đủ hồng ân cho các ngươi qua việc tách biệt các ngươi với các cộng đồng khác và để hướng các ngươi trở thành tâm điểm của nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(2,151,'2:151','Giống như TA (Allah) đã ban một hồng phúc khác cho các ngươi, đó là gởi đến cho các ngươi một Thiên Sứ xuất thân từ đồng bào của các ngươi, Y xướng đọc Qur''an cho các ngươi nghe, tẩy sạch các ngươi qua việc ra lệnh các ngươi làm những điều ân phúc, tốt lành và cấm các ngươi làm những điều khả ố và sai trái, Y dạy các ngươi Qur''an và Sunnah, dạy các ngươi mọi thứ mà các ngươi chưa từng biết về tôn giáo và thế giới trần gian của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,152,'2:152','Các ngươi hãy tưởng nhớ TA (Allah) trong lòng và bằng hành động thì TA sẽ tuyên dương, khen ngợi và phù hộ các ngươi, bởi phần thưởng tương ứng với việc làm; các ngươi hãy biết ơn TA về ân phúc mà TA đã ban cho các ngươi và các ngươi chớ bác bỏ, phủ nhận nó bằng cách sử dụng nó vào những thứ mà TA đã cấm các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,153,'2:153','Hỡi những người có đức tin, các ngươi hãy cầu xin TA (Allah) sự phù hộ bằng sự kiên nhẫn, lễ nguyện Salah, và bằng việc tuân thủ theo mệnh lệnh của TA, quả thật, Allah cận kề với những người biết kiên nhẫn, Ngài sẽ giúp đỡ và phù hộ họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,154,'2:154','Hỡi những người có đức tin, các ngươi chớ nói rằng những ai đã hi sinh trên chiến trường vì con đường chính nghĩa của Allah rằng họ đã chết giống như cái chết của bao người khác. Không, họ vẫn còn sống nơi Thượng Đế nhưng các ngươi không cảm nhận được cuộc sống đó, bởi đó là cuộc sống riêng biệt mà chỉ có những ai nhận được mặc khải từ Allah mới biết được nó.');
INSERT INTO "vn_mokhtasar" VALUES(2,155,'2:155','Và chắc chắn TA sẽ thử thách các ngươi bởi đủ loại tai họa, như sợ hãi trước kẻ thù; đói khát vì thiếu lương thực; hao hụt và mất mát tài sản; hy sinh tính mạng vì chính nghĩa của Allah; mất mùa màng do đất khô cằn, nhưng Ngươi hãy báo tin mừng đi - hỡi Nabi - cho những ai biết kiên nhẫn trước bao tai nạn đó sẽ được ban cho thuận lợi ở trần gian và Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(2,156,'2:156','Đối với những người gặp phải một trong những tai nạn đã liệt kê (ở câu Kinh trước) liền nói với trái tim hài lòng và chấp nhận: Tất cả chúng ta là tài sản của Allah, Ngài muốn xử trí thế nào tùy Ngài và rồi chúng ta sẽ trở về trình diện Ngài vào Ngài Phục Sinh, Ngài là Đấng đã tạo hóa ra chúng ta, đã ban hồng phúc cho chúng ta bằng những ân huệ khác nhau và cuối cùng mọi việc của chúng ta đều kết thúc nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,157,'2:157','Những người được mô tả với những đức tính đó sẽ được Allah khen ngợi trước giới Thiên Thần trên trời, lòng thương xót của Allah sẽ bao trùm lên họ và họ được hướng dẫn đến với con đường Chính Đạo.');
INSERT INTO "vn_mokhtasar" VALUES(2,158,'2:158','Quả thật, hai ngọn đồi Al-Safa và Al-Marwah là hai biểu hiệu bề nổi của Islam. Bởi thế, ai đến Makkah để hành hương Hajj hoặc ''Umrah thì y sẽ không mắc tội khi đi qua lại (được gọi là Sa''i) giữa hai ngọn đồi này. Việc phủ nhận tội ở đây nhằm khẳng định với tín đồ Muslim rằng việc đi qua lại giữa hai ngọn đồi Al-Safa và Al-Marwah là một trong các nghi thức hành hương chứ không phải là hành động mang tôi như những người Jahiliyah (tiền Islam) đã quan niệm. Ai thực hiện nó mang tính làm thêm với định tâm vì Allah, chắc chắn sẽ được Allah ghi ơn, chấp nhận việc làm đó và ban thưởng cho y, bởi Ngài là Đấng biết rõ ai là người làm tốt đáng được thưởng.');
INSERT INTO "vn_mokhtasar" VALUES(2,159,'2:159','Quả thật, những ai trong số những người Do Thái và Thiên Chúa đã che giấu các bằng chứng xác nhận sứ mạng và những gì được Thiên Sứ mang đến, sau khi TA (Allah) đã phơi bày cho thiên hạ biết qua Kinh Sách của chúng, là đám người bị Allah trục xuất khỏi lòng thương xót của Ngài, đồng thời bị các Thiên Thần, các vị Nabi và toàn thể nhân loại đồng cầu xin Allah xua đuổi chúng rời khỏi lòng thương xót của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,160,'2:160','Ngoại trừ những ai quay đầu về với Allah, ăn năn hối hận cho hành động che giấu các lời phán rõ ràng đó, cải thiện hành động thân thể lẫn nội tâm và phơi bày sự thật đã che giấu thì họ sẽ được TA chấp nhận sự sám hối đó, bởi TA là Đấng hằng tha thứ và nhân từ đối với ai biết sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(2,161,'2:161','Quả thật, những người vô đức tin chết trong hiện trạng vô đức tin đó của họ thì họ sẽ bị Allah trục xuất khỏi lòng thương xót của Ngài, không những thế, các Thiên Thần và nhân loại sẽ khẩn cầu Allah xua đuổi họ rời khỏi lòng thương xót của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,162,'2:162','Họ sẽ mãi mãi trong sự nguyền rủa đó, không được giảm nhẹ hình phạt dù là chỉ một ngày và không được buông tha ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(2,163,'2:163','Và - hỡi nhân loại - Đấng Thờ Phượng đích thực của các chỉ có một mà thôi, Ngài hoàn toàn độc lập về thể xác và thuộc tính, ngoài Ngài không có đấng nào khác đáng được tôn thờ, Ngài là Đấng Al-Rahman có lòng thương xót vô hạn, nhân từ với đám nô lệ của Ngài qua việc ban phát ân phước vô số kể.');
INSERT INTO "vn_mokhtasar" VALUES(2,164,'2:164','Quả thật, trong việc tạo hóa trời đất và mọi tạo vật kỳ diệu trong chúng, sự luân chuyển ngày đêm, việc những chiếc tàu chở đầy ấp hàng hóa, lương thực, quần áo và mọi thứ con người cần di chuyển lênh đênh trên mặt biển, việc Allah trút xuống những cơn mưa cứu sống những vùng đất chết để mọc lên cây cối và thảo mộc, việc sắp xếp mọi thứ phát triển và sinh soi, việc chuyển các cơn gió đi khắp mọi nơi, việc đẩy các đám mây phủ kín trời đất... tất cả là bằng chứng rõ ràng khẳng định sự tồn tại của một Thượng Đế duy nhất dành cho những ai có khói óc suy nghĩ muốn tìm kiếm bằng chứng và muốn chấp nhận sự thật.');
INSERT INTO "vn_mokhtasar" VALUES(2,165,'2:165','Mặc dù với các dấu hiệu rõ ràng đó, trong thiên hạ vẫn có người mù quáng tôn thờ những thần linh thay vì thờ phượng Allah, họ tự dựng lên các thần linh và tự gán chúng thành những đối tác ngang vai với Ngài, họ thương yêu các thần linh đó như tình yêu dành cho Allah. Tuy nhiên, những người có đức tin thì lại có tình yêu mãnh liệt nơi Allah hơn cả tình yêu mà những kẻ lầm lạc đã dành cho các thần linh của họ, vì họ không tổ hợp với Allah bất cứ thần linh ngang vài nào cùng với Ngài, họ thương yêu Ngài lúc hưng thịnh và cả lúc khó khăn. Còn những kẻ tổ hợp kia, họ yêu các thần linh của họ trong lúc phồn thịnh nhưng khi khó khăn thì hò lại khấn vái Allah. Và nếu như những kẻ làm điều sai quấy tận mắt nhìn thấy hinh phạt của Ngày Phục Sinh thì họ nhận biết rằng mọi quyền lực đều trong tay Allah, Ngài rất hà khắc trong việc trừng phạt đám người bất tuân; giá như họ thấy điều đó là họ chẳng dám tổ hợp với Ngài bất cứ ai.');
INSERT INTO "vn_mokhtasar" VALUES(2,166,'2:166','Đó là lúc những kẻ được suy tôn tự tuyên bố cắt đứt quan hệ với những người yếu thế đi theo chúng, bởi chúng tận mắt nhìn thấy những cảnh tượng hãi hùng trong Ngày Phục Sinh và họ đã mất hết mọi hi vọng về việc được cứu rỗi.');
INSERT INTO "vn_mokhtasar" VALUES(2,167,'2:167','Và đám người yếu thế và đi theo nói: Ôi ước chi chúng tôi có cơ hội trở lại trần gian thì chúng tôi quyết cắt đứt quan hệ với họ giống như họ đã cắt đứt quan hệ với chúng tôi tại đây, đó là lúc đám người yếu thế thấy được hinh phạt kinh hoàng mà Allah dành trừng phạt cả họ lẫn đám người được suy tôn, cho dù họ có hối hận và hoảng sợ thì họ vẫn không thể thoát khỏi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(2,168,'2:168','Hỡi nhân loại, các ngươi hãy tự do ăn uống mọi thứ có trên đất từ động vật, thực vật mà các ngươi đã thu được bằng hình thức Halal (hợp pháp), và các ngươi đừng mù quáng đi theo những lối mòn mà lũ Shaytan đã bày vẽ để dụ các ngươi, chính chúng là kẻ thù công khai của các ngươi, nếu là người tinh khôn sẽ không đi theo lối mòn của kẻ thù luôn tìm cách đưa các ngươi vào lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(2,169,'2:169','Hắn (Shaytan) toàn xúi dục các ngươi làm điều sàm bậy và xem thường tội lỗi, và hắn xúi các ngươi mô tả Allah bằng những niềm tin và giáo lý không có cơ sở kiến thức, khác hẳn những gì được Allah và các Thiên Sứ của Ngài mang đến cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,170,'2:170','Và khi những kẻ vô đức tin này được bảo: Các người hãy noi theo Chỉ Đạo và Ánh Sáng mà Allah đã ban xuống. Chúng đáp: Không, chúng tôi quyết làm theo những gì chúng tôi thấy tổ tiên mình đã để lại từ niềm tin và tập quán. Phải chăng chúng mù quáng theo tổ tiên của chúng ngay cả khi tổ tiên của chúng không hề biết tí nào về Chỉ Đạo hay Ánh Sáng, cũng như không được hướng dẫn đến với Chân Lý, điều mà Allah hài lòng ư?');
INSERT INTO "vn_mokhtasar" VALUES(2,171,'2:171','Và hình ảnh của những kẻ vô đức tin mù quáng đi theo tổ tiên chúng giống như hình ảnh một người chăn cừu cố la hét để dọa nạt đàn cừu của mình, nhưng đàn cừu chỉ nghe được âm thanh la hét chứ nào có hiểu được y nói gì. Chúng bị điếc không nghe được chân lý nên không tiếp thu được lợi ích; chúng bị câm nên không nói ra được điều chân lý; và chúng bị mù nên không nhìn thấy được đúng sai, cho nên chúng không thể nhận thức Chỉ Đạo đang mời gọi chúng.');
INSERT INTO "vn_mokhtasar" VALUES(2,172,'2:172','Hỡi những người có đức tin nơi Allah và đi theo Thiên Sứ của Ngài, các ngươi hãy ăn uống những thực phẩm tốt sạch mà Allah đã ban cho các ngươi và cho phép các ngươi sử dụng; các ngươi hãy biết tạ ơn Allah bằng tấm lòng và hành động đúng giáo lý về những thiên ân đã ban; và một trong những hình thức tạ ơn Allah là tuân thủ theo mọi điều mà Ngài sai bảo và tránh xa việc bất tuân Ngài nếu các ngươi thực sự là những người thờ phượng một mình Allah, không tổ hợp với Ngài bất cứ ai (vật gì).');
INSERT INTO "vn_mokhtasar" VALUES(2,173,'2:173','Allah chỉ cấm các ngươi ăn những động vật chết chưa được cắt cổ (theo luật Islam), máu Masfuh (máu xuất ra hoàn toàn khỏi cơ thể con vật), thịt heo và tất cả động vật khi cắt cổ không được nhân danh Allah. Tuy nhiên, trong trường hợp khẩn cấp ngoài ý muốn cần phải ăn (để duy trì sự sống) và chỉ ăn đủ lượng (cho cơ thể duy trì sự sống) chứ không quá độ, thì các ngươi không bị tội cũng không bị phạt. Quả thật, Allah luôn rộng lượng đối với những người biết sám hối, Ngài nhân từ với họ, Ngài xí xóa việc ăn những món cấm trong hoàn cảnh bất đắc dĩ.');
INSERT INTO "vn_mokhtasar" VALUES(2,174,'2:174','Quả thật, những ai dám che giấu những gì mà Allah đã thiên khải trong Kinh Sách và những bằng chứng xác nhận về sứ mạng của Muhammad, giống như hành động của Do Thái và Thiên Chúa, những kẻ mà chúng sẵn sàng che giấu để đổi lại lợi nhuận cỏn con từ địa vị, quyền lực, chức vụ hoặc tiền bạc. Những kẻ đó thật ra chỉ tạo ra các nguyên nhân để đẩy bản thân mình sớm sa vào sự trừng phạt trong Hỏa Ngục. Vào Ngày Phục Sinh, Allah không thèm nói chuyện với chúng bằng giọng thương yêu mà bằng giọng dọa nạt, Ngài cũng không rửa tội hay tuyên dương chúng, ngược lại chúng phải chịu hình phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(2,175,'2:175','Những kẻ được mô tả che giấu kiến thức mà thiên hạ cần đến là những kẻ đã đổi Chỉ Đạo để lấy sự lầm lạc, bởi vì chúng đã che giấu kiến thức chân lý. Chúng thực sự đã đổi lấy hình phạt của Allah thay vì sự tha thứ của Ngài. Chúng đã tự tạo cơ hội để bản thân nhanh rơi vào hình phạt nơi Hỏa Ngục như thể chúng không thể kiên nhẫn hơn cho sự việc đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,176,'2:176','Đó là hậu quả cho việc che giấu kiến thức và Chỉ Đạo trong khi mọi thứ đã rõ ràng trong các Kinh Sách mà Allah đã ban xuống, cái mà đáng lẽ ra phải trình bày công khai chứ không được che giấu. Và những ai bất đồng về các Kinh Sách đã được thiên khải rồi chỉ tin tưởng một số giáo điều và phủ nhận một số giáo điều khác trong đó thì đấy là những kẻ đã đi qua xa khỏi chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(2,177,'2:177','Sự ngoan đạo làm hài lòng Allah không phải ở việc hướng mặt về hướng đông hoặc hướng tây cũng không phải ở việc tranh luận về điều đó mà sự ngoan đạo là đối với ai tin nơi Allah là Thượng Đế duy nhất, tin nơi Ngày Tận Thế, nơi tất cả các Thiên Thần, nơi tất cả các Kinh Sách đã thiên khải, nơi tất cả các Nabi không có sự phân biệt giữa họ; song song với niềm tin đó, y bố thí tài sản cho bà con họ hàng bằng niềm vui và tình yêu, cho trẻ mồ côi, cho người thiếu thốn, cho người cơ nhỡ, người lỡ đường, người bất đắc dĩ phải ăn xin; y dùng tiền để chuộc thân nô lệ và tù binh, năng hành lễ Salah một cách chu đáo mà Allah yêu cầu, xuất Zakat, thực hiện mọi điều đã giao ước, biết kiên nhẫn lúc khó khăn, nghèo khổ, bệnh tật và kiên cường không bỏ chạy khi đối điện kẻ thù. Những người được mô tả như thế mới là những đã thật lòng với Allah bằng đức tin Iman và hành động. Họ mới là những người thực sự biết kính sợ, biết tuân thủ theo mệnh lệnh của Allah và biết tránh xa những điều Ngài nghiêm cấm.');
INSERT INTO "vn_mokhtasar" VALUES(2,178,'2:178','Hỡi những người đã tin tưởng nơi Allah và noi theo Thiên Sứ của Ngài, luật Qisas (trả đũa) cho việc cố tình giết người được qui định cụ thể cho các ngươi như sau: Kẻ giết người phải đền mạng, người tự do đền mạng bằng người tự do, người nô lệ đền mạng bằng người nô lệ, phụ nữ đền mạng bằng phụ nữ. Nếu người bị sát hại chịu tha thứ trước khi chết hoặc gia đình nạn nhân đồng ý nhận tiền đền bù thì người gây hại phải bồi thường một cách thỏa đáng; về bên bị hại không được tạo áp lực bằng cách gieo tiếng xấu, hãm hại bên bên người gây hại. Đó là cách mà Thượng Đế dùng giảm nhẹ cho các ngươi, một sự nhân từ dành cho cộng đồng (Islam) này. Sau khi bên bị hại chấp nhận bồi thường và bên hại đã hoàn tất việc đền bù thỏa đáng mà bên bị hại vẫn còn hành động xấu cho bên hại thì sẽ phải đối mặt với hình phạt đau đớn từ Allah.');
INSERT INTO "vn_mokhtasar" VALUES(2,179,'2:179','Và luật Qisas mà Allah đã qui định cho các ngươi mang lại cho các ngươi một lối sống giúp bảo vệ sinh mạng và xóa đi thù hằn giữa các ngươi, ý nghĩa cao quí này chỉ có nhóm người biết suy nghĩ và kính sợ Allah mới nhận thức và tuân thủ theo.');
INSERT INTO "vn_mokhtasar" VALUES(2,180,'2:180','Các ngươi được qui định rằng khi ai đó trong các ngươi hấp hối và nguy kịch, nếu y bỏ lại tài sản nhiều thì y hãy di chúc cho cha mẹ, cho người thân tộc theo đúng mức lượng mà giáo lý qui định, đó là không vượt mức một phần ba tổng tài sản. Đây được xem là hành động đáng làm của những người biết kính sợ Allah Tối Cao. Tuy nhiên, câu Kinh này được mặc khải trước câu Kinh phân chia tài sản, kể từ khi câu Kinh qui định chia tài sản đã liệt kê cụ thể từng người, ai được hưởng và được hưởng bao nhiêu (thì giáo lý của câu Kinh này đã bị thay thế).');
INSERT INTO "vn_mokhtasar" VALUES(2,181,'2:181','Do đó, ai đã biết rõ di chúc nhưng cố tình chỉnh sửa thêm bớt hoặc cấm cản ai đó hưởng theo di chúc, y phải gánh chịu cho tội lỗi thay đổi đó còn người viết di chúc vô can. Quả thật, Allah hằng nghe mọi lời nói của người bề tôi, thông toàn mọi hành động của họ, không có bất cứ việc gì có thể vượt khỏi tầm kiểm soát của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,182,'2:182','Ai nhận thấy được người viết di chúc viết lệch chân lý hoặc có sự bất công trong di chúc, y cố gắng khuyên bảo người viết di chúc chỉnh sửa cho đúng và cố cải thiện lỗi sai trong di chúc thì y không bị tội, ngược lại còn được phước do công cải thiện của y, quả thật Allah rất nhân từ đối với ai biết sám hối và khoan dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,183,'2:183','Hỡi những người đã tin tưởng nơi Allah và Thiên Sứ của Ngài, lệnh truyền từ Thượng Đế bắt buộc các ngươi nhịn chay giống như Ngài đã bắt buộc các thế hệ trước các ngươi phải nhịn chay. Hy vọng qua việc nhịn chay đó các ngươi biết kính sợ Allah bằng cách xây bức tường ngoan đạo bảo vệ các ngươi khỏi hình phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,184,'2:184','Việc nhịn chay chỉ bắt buộc các ngươi trong một số ngày ít ỏi của năm. Nhưng ai trong các ngươi bị bệnh không thể nhịn chay hoặc đang đi đường xa thì được phép ăn uống, rồi nhịn chay bù lại vào các ngày khác tương ứng số ngày đã không nhịn. Đối với người không thể nhịn thì phải xuất thức ăn mỗi ngày cho một người nghèo tương ứng một ngày không nhịn. Tuy nhiên, việc nhịn chay sẽ tốt cho các ngươi hơn việc không nhịn và xuất thức ăn thay thế, giá các ngươi biết rõ giá trị của nhịn chay. Đây là giáo lý đầu tiên khi bắt buộc nhịn chay, ai muốn nhịn thì nhịn và ai không muốn thì xuất thức ăn thay thế, sau đó Allah bắt buộc tất cả phải nhịn chay khi trưởng thành và có khả năng nhịn.');
INSERT INTO "vn_mokhtasar" VALUES(2,185,'2:185','Tháng Ramadan là tháng mà Qur''an được bắt đầu thiên khải cho Thiên Sứ Muhammad, cụ thể là trong đêm Al-Qadar. Allah dùng Qur''an để hướng dẫn con người, trong Nó chứa đựng các chỉ đạo minh bạch; tiêu chuẩn phân biệt chân lý và ngụy tạo. Ai có mặt tại nhà trong tháng Ramadan với sức khỏe bình thường thì phải nhịn chay trọn tháng, còn ai bị bệnh hoặc đang du hành thì được phép ăn uống nhưng phải nhịn bù lại vào các ngày khác tương ứng với số ngày đã không nhịn. Qua giáo luật này Allah muốn tạo sự dễ dàng cho các ngươi chứ không muốn gây khó khăn cho các ngươi; hãy nhịn chay đầy đủ tất cả các ngày trong tháng và hãy Takbir (nói Allahu Akbar) khi kết thúc Ramadan và trong ngày Eid để tạ ơn Allah đã phù hộ các ngươi hoàn thành bổn phận nhịn chay. Qua đó, hi vọng các ngươi biết tạ ơn Ngài vì đã hướng dẫn tôn giáo này cho các ngươi, tôn giáo mà Ngài đã hài lòng cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,186,'2:186','Và khi đám nô lệ của TA hỏi Ngươi - hỡi Nabi Muhammad - về sự gần kề của TA đối với họ cũng như về việc TA đáp lại lời cầu xin của họ thì Ngươi hãy cho họ biết TA ở rất gần với họ, TA hiểu rõ hoàn cảnh của họ, nghe rõ lời khẩn cầu của họ, cho nên không cần đến kẻ trung gian làm chiếc cầu nối giữa họ và TA và họ không cần phải gào thét khi cầu xin, TA sẽ đáp lại lời khẩn cầu của ai thành tâm khẩn cầu TA. Đổi lại, họ phải thực hiện đúng mọi mệnh lệnh của TA, phải vững chắc trên đức tin Iman, bởi đó là những cách hữu ích nhất giúp lời cầu xin của họ được TA chấp nhận, hi vọng họ biết luôn tuân thủ chỉ đạo trong cuộc sống và hành đạo.');
INSERT INTO "vn_mokhtasar" VALUES(2,187,'2:187','Quả thật, trong luật định ban đầu, khi người đàn ông ngủ trong những đêm của tháng nhịn chay rồi thức dậy trước giờ Fajr thì bị cấm ăn uống và gần gũi vợ. Sau đó, luật này được Allah xóa bỏ và cho phép tín đồ Muslim gần gũi vợ vào ban đêm trở lại; vợ của các ngươi là tấm chắn che đậy phần kín cho các ngươi, ngược lại, các ngươi là tấm chắn che đậy phần kín cho họ. Quả thật, Allah đã biết các ngươi đã lén lút vi phạm giới luật cấm này như với lòng nhân từ dành cho các ngươi, Ngài đã tha thứ cho các ngươi, Ngài đã giảm nhẹ cho các ngươi. Bởi thế, bây giờ các ngươi hãy gần gũi những người vợ của các ngươi để tìm thiên ân mà Ngài đã định cho các ngươi về con cái, các ngươi hãy tự do ăn uống suốt đêm cho đến ánh rạng đông thật sự xuất hiện, tức khi thấy vệt màu trắng xuất hiện trong trời tối đen là lúc các ngươi phải nhịn chay cho đến mặt trời lặn hẳn. Cấm các ngươi gần gũi vợ trong lúc các ngươi đang ''Etikaf trong Masjid, bởi hành động đó làm các ngươi vi phạm giới cấm của Allah. Tất cả đã được bày rõ ràng cụ thể cho nhân loại để họ kính sợ Allah mà thực thi theo lệnh của Ngài và tránh xa điều Ngài cấm.');
INSERT INTO "vn_mokhtasar" VALUES(2,188,'2:188','Và cấm các ngươi lấy tài sản của nhau một cách bất chính như trộm cắp, chiếm đoạt, gian lận và cũng đừng thưa kiện ra tòa bằng mưu mô xảo quyệt để lấy tài sản của người khác một cách tội lỗi trong khi các ngươi biết đó là điều Allah nghiêm cấm. Việc biết điều đó bị cấm mà vẫn làm quả là một hành động xấu xa và đáng phải chịu phạt nặng nề.');
INSERT INTO "vn_mokhtasar" VALUES(2,189,'2:189','Họ hỏi Ngươi - hỡi Thiên Sứ Muhammad - về hiện tượng trăng lưỡi liềm thay đổi, Ngươi hãy trả lời chúng biết về ý nghĩa của hiện tượng đó là để con người nhận biết thời gian hành đạo như các tháng hành hương Hajj, tháng nhịn chay, thời gian tròn một năm để xuất Zakat; để nhận biết thời gian trao đổi mua bán như định ngày giờ trả nợ. Việc ngoan đạo và tốt đẹp không thể bằng cách leo từ trần nhà vào trong nhà sau khi đã định tâm Ehram hành hương Hajj và ''Umrah như các ngươi đã từng làm thời tiền Islam, mà thật ra ngoan đạo là lòng kính sợ Allah từ bên ngoài lẫn bên trong. Vì vậy các ngươi hãy vào nhà từ các cửa, điều đó sẽ dễ dàng cho các ngươi hơn và các ngươi sẽ không phải gặp khó khăn bởi Allah đã không bắt các ngươi làm những điều khó khăn đó. Các ngươi hãy xây bức tường bảo vệ các ngươi với hình phạt của Allah bằng nhiều việc làm ngoan đạo, hi vọng các ngươi đạt được mong muốn của mình trong nó và tránh được mọi thứ các ngươi ghét bỏ.');
INSERT INTO "vn_mokhtasar" VALUES(2,190,'2:190','Và các ngươi hãy chiến đấu - để giơ cao lời phán của Allah - với những kẻ vô đức tin muốn tìm cách tách các ngươi rời xa tôn giáo của Allah. Tuy nhiên, các ngươi không được phép lạm dụng điều được phép mà vượt quá giới hạn của Allah như giết trẻ con, phụ nữ, người già hoặc phanh thây người chết... quả thật Allah không yêu thương những kẻ vượt giới hạn của Allah sau khi đã được định thành luật.');
INSERT INTO "vn_mokhtasar" VALUES(2,191,'2:191','Và các ngươi hãy đánh chúng ở bất cứ nơi nào mà các ngươi gặp chúng, các ngươi hãy đuổi chúng ra khỏi Makkah giống như chúng đã xua đuổi các ngươi trước đây. Hành động cấm cản người có đức tin hành đạo và cũng như ép họ bỏ đạo còn nguy hại hơn cả hành động giết chóc. Và các ngươi không được khai chiến tại Masjid Al-Haram nhằm tôn trọng Masjid ngoại trừ họ khai chiến với các ngươi trước, một khi họ khai chiến với các ngươi tại Masjid Al-Haram thì các ngươi mới đáp trả họ - nghĩa là giết họ khi họ dám gây hại tại Masjid Al-Haram - đây là phần thưởng dành cho đám người ngoại đạo.');
INSERT INTO "vn_mokhtasar" VALUES(2,192,'2:192','Một khi họ chủ động kết thúc chiến tranh và tự chấm dứt mọi hành động phá rối thì các ngươi hãy chấm dứt theo, quả thật, Allah hằng tha thứ cho ai biết sám hối, vì thế các ngươi đừng trách móc các tội lỗi đã qua mà họ đã làm, Ngài khoan hồng cho họ nên không trừng phạt họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,193,'2:193','Và các ngươi cứ tiếp tục chiến đấu với những kẻ vô đức tin mãi đến khi họ chấm dứt mọi hành động Shirk cũng như hành động ngăn cản thiên hạ đến với Islam và cho đến khi tôn giáo của Allah được thể hiện công khai (không khuất phục trước một thế lực nào). Một khi chúng tự chấm dứt chiến tranh thì các ngươi đừng đánh với chúng nữa, bởi quả thật, không ai là kẻ thù ngoại trừ những kẻ bất công và ngăn chặn con đường đến với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(2,194,'2:194','Tháng Haram năm thứ bảy là tháng mà Allah cho phép các ngươi vào vùng đất Haram để hành hương ''Umrah thay cho tháng Haram năm thứ sáu trước đó đã bị nhóm đa thần cấm không cho vào vùng đất Haram. Các điều cấm kỵ - giống như sự cấm kỵ của vùng đất Makkah này, trong tháng cấm kỵ này và thời gian Ehram - ai vị phạm sẽ bị trả đũa. Vì vậy, ai xâm hại đến các ngươi thì các ngươi hãy đáp trả lại y giống như y đã làm với các ngươi nhưng đừng lạm dụng. Quả thật, Allah không hề thương yêu những ai vượt hạn định của Allah và hãy biết sợ Allah về các hạn định mà Ngài cho phép các ngươi, hãy biết rõ Allah luôn sát cánh với nhóm người ngay chính, sợ Allah bằng sự ủng hộ và hướng dẫn của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,195,'2:195','Và các ngươi hãy chi dùng tài sản của các ngươi trong con đường phục tùng Allah bằng các hình thức Jihad và các hình thức khác, cấm các ngươi tự đẩy thân mình vào mối nguy hiểm diệt vong bằng cách bỏ Jihad thay thế bằng con đường khác hoặc tự tạo nguyên nhân đưa mình vào nguy hiểm; các ngươi hãy hành đạo thật tốt, quan hệ và cư xử với mọi người thật đàng hoàng. Quả thật, Allah rất yêu thương nhóm người đức hạnh, Ngài sẽ ban thưởng hậu hĩnh cho họ và hướng dẫn họ đến chính đạo.');
INSERT INTO "vn_mokhtasar" VALUES(2,196,'2:196','Và các ngươi hãy thực hiện trọn vẹn cả Hajj và ''Umrah vì sắc diện của Allah. Trường hợp các ngươi bị cản trở không thể hoàn thành bởi bệnh tật hoặc kẻ thù thì các ngươi hãy giết mổ Hadi - lạc đà, bò hoặc dê, cừu - theo khả năng của các ngươi để các ngươi được phép trở lại bình thường. Các ngươi không được cạo đầu hoặc cắt ngắn tóc cho đến khi nào Hadi được dắt đến nơi được phép giết. Nếu bị cản trở không đến được khu vực Haram thì các ngươi hãy giết Hadi tại nơi bị cản trở đó, còn nếu không bị cản trở thì các ngươi hãy giết Hadi trong khu vực Haram vào ngày Eid Al-Adh-ha và các ngày Tashreeq (ngày 11, 12, 13). Ai đang hành hương thì bị bệnh hoặc bị ngứa da đầu, hãy cạo đầu và chuộc tội bằng một trong các hình thức: nhịn chay ba ngày hoặc xuất thức ăn cho sáu người nghèo hoặc giết mổ một con cừu lấy thịt phân phát cho người nghèo trong khu vực Haram. Trường hợp các ngươi không lo sợ, ai trong các ngươi muốn làm Hajj dạng Tamattu''a bằng cách thực hiện ''Umrah trong các tháng của Hajj để được tự do thoải mái khỏi những điều cấm của tình trạng Ihram thì y hãy giết một con cừu hoặc xuất một phần bảy con lạc đà hoặc một phần bảy con bò; nếu không có khả năng giết Hadi thì y hãy nhịn chay ba ngày trong những ngày hành hương và nhịn tiếp bảy ngày sau khi trở về nhà để thay thế việc giết Hadi, tổng cộng là mười ngày trọn vẹn. Việc thực hiện Hajj dạng Tamattu''a qua việc phải giết Hadi hoặc nhịn chay nếu không có khả năng giết Hadi là đối với những ai không phải là cư dân trong khu vực Al-Haram cũng như cư dân lân cận bởi vì cư dân trong khu vực Al-Haram cũng như cư dân lân cận không cần đến Hajj dạng Tamadtu''a, họ chỉ cần thực hiện Hajj (khi tới ngày) là đủ (được gọi là Hajj Ifrad). Các ngươi hãy kính sợ Allah mà tuân thủ giáo luật mà Ngài đã quy định, các ngươi hãy tôn trọng giới luật của Ngài, và các ngươi hãy biết rằng quả thật Allah rất nghiêm khắc trong việc trừng phạt những kẻ làm trái lệnh Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,197,'2:197','Thời gian hành hương Hajj đã được qui định rõ ràng. Nó bắt đầu từ tháng Shauwal (tháng 10) đến ngày mồng mười tháng Zdul-Hijjah (tháng 12) theo niên lịch Islam. Ai bắt bản thân mình hành hương trong những tháng này và đã định tâm vào tình trạng Ehram thì y không được phép có các hành động mơn trớn và quan hệ tình dục với vợ của mình, không được phép làm điều tội lỗi nhằm tôn trọng thời gian và không gian thiêng liêng, và y không được phép tranh cãi dẫn đến xung đột do tức giận và nóng nảy. Và tất cả điều tốt đẹp mà các ngươi đã làm thì Allah đều biết rất rõ và Ngài sẽ ban thưởng đầy đủ cho các ngươi. Các ngươi hãy chuẩn bị hành trang như lương thực và các vật dụng cần thiết khác cho chuyến hành hương Hajj, tuy nhiên thứ tốt nhất để các ngươi chuẩn bị cho hành trang của mình chính là lòng Taqwa (ngay chính, kính sợ) Allah. Vì vậy, các ngươi hãy sợ TA mà tuân thủ đúng mọi mệnh lệnh của TA và hãy tránh xa mọi điều TA đã cấm, hỡi đám người có tâm trí lành mạnh.');
INSERT INTO "vn_mokhtasar" VALUES(2,198,'2:198','Các ngươi sẽ không mắc tội khi các ngươi tìm kiếm bổng lộc Halal bằng việc kinh doanh mua bán trong suốt thời gian hành hương Hajj. Khi các ngươi rời ''Arafah sau khi đã có mặt tại đó vào ngày mồng chín để hướng đến Muzdalifah vào đêm mồng mười tháng Zdul-Hijjah thì các ngươi hãy tụng niệm Allah bằng Tasbih (nói Sub ha nol loh), Tahlil (Laa i laa ha il lol loh) và cầu xin, khấn vái tại Mash''ar Al-Haram ở Muzdalifah, các ngươi hãy tụng niệm Allah như đã được Ngài hướng dẫn các ngươi đến được các biểu hiệu của tôn giáo và được hành hương Hajj tại Ngôi Nhà của Ngài trong khi trước đó các ngươi là nhóm người mù tịt về các giáo lý của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,199,'2:199','Rồi các ngươi hãy rời khỏi ''Arafah cùng với thiên hạ bắt chước theo cách làm của Ibrahim, chứ đừng làm theo cách mà nhóm người tiền Islam đã làm khi họ đã không dừng chân tại đó; và các ngươi hãy cầu xin Allah sự tha thứ do sơ xuất trong việc hành hương mà Allah đã qui định, quả thật, Allah rất nhân từ đối với ai biết quay lại ăn năn và biết lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(2,200,'2:200','Sau khi các ngươi hoàn thành mọi nghi thức hành hương Hajj, trong thời gian rảnh rỗi, các ngươi hãy tụng niệm, tán dương Allah thật nhiều như việc các ngươi lấy làm hãnh diện và ca ngợi về cha mẹ các ngươi hoặc tụng niệm Allah nhiều hơn việc nhắc về cha mẹ các ngươi, bởi mọi hồng phúc đều xuất phát từ Đấng Oai Nghiêm và Tối Cao. Trong thiên hạ có đa dạng người, có người là kẻ phủ nhận đức tin, thờ đa thần, họ chỉ tin vào mỗi cuộc sống trần gian này, họ chỉ cầu xin Thượng Đế sức khỏe, tài sản, con cái và những thứ vật chất của thế gian mà thôi, bởi thế họ sẽ chẳng có thứ gì ở Đời Sau từ những thứ mà Allah đã chuẩn bị cho đám nô lệ có đức tin của Ngài, nguyên nhân là vì họ chỉ biết mỗi cuộc sống hiện tại và ngoảnh mặt với Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(2,201,'2:201','Và trong thiên hạ có những người đã tin tưởng vào Allah, tin tưởng Đời Sau nên họ đã khấn vái Thượng Đế của họ sự hưởng thụ ở trần gian và luôn hành đạo trong lúc sống trên thế gian, đồng thời họ cũng cầu xin sự thành công nơi Thiên Đàng và được bình an khỏi hình phạt nơi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(2,202,'2:202','Đó là những người đã cầu xin cả hai điều tốt ở trần gian và Đời Sau, họ sẽ được ân phước vĩ đại về các việc làm ngoan đạo mà họ đã từng làm ở trần gian, Allah rất nhanh nhẹn thanh toán mọi việc.');
INSERT INTO "vn_mokhtasar" VALUES(2,203,'2:203','Và hãy tụng niệm Allah bằng các lời Takbir và Talil trong những ngày ngắn ngủi, gồm: ngày 11, 12 và 13 của tháng Zdul-Hijjah. Ai muốn rời Mina sớm sau khi đã ném đá trong ngày 12 thì y được phép làm thế và không bị tội, bởi Allah đã giảm nhẹ cho y, còn ai muốn ở lại đến ngày 13 thì càng tốt, điều đó trọn vẹn hơn. Các ngươi hãy bám sát đúng theo đường lối của Thiên Sứ như Allah đã truyền lệnh cho Người về các nghi thức hành hương Hajj, đó là hành động của người biết kính sợ Allah. Hãy kính sợ Allah mà tuân thủ theo mọi lệnh đã truyền và tránh xa mọi điều bị Ngài cấm và tuyệt đối tin rằng các ngươi sẽ được hồi triệu trình diện Ngài để được Ngài thưởng phạt mọi việc mà các người đã từng làm.');
INSERT INTO "vn_mokhtasar" VALUES(2,204,'2:204','Hỡi Nabi, trong thiên hạ có những tên Munafiq (ngụy Islam) đã thu hút Ngươi bằng lời nói điêu ngoa về cuộc sống trần gian làm Ngươi tưởng rằng chúng thật thà và ngay chính, nhưng thật ra chúng chỉ muốn bảo việc sinh mạng và tài sản của chúng mà thôi. Allah chứng nhận chúng là những kẻ nói dối, trong tim chúng chẳng hề có tí niềm tin Iman hay điều tốt đẹp nào, chúng là những kẻ chống đối và thù nghịch những người Muslim mạnh mẽ nhất.');
INSERT INTO "vn_mokhtasar" VALUES(2,205,'2:205','Và khi y - kẻ Munafiq quay lưng rời khỏi Ngươi - Thiên Sứ Muhammad - thì y cố gắng đi khắp thế gian để gieo rắc tội lỗi, hủy hoại mùa màng, giết chết động vật, trong khi Allah không hề ưa thích hành động phá hại trái đất và không yêu thích những kẻ phá hại đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,206,'2:206','Khi kẻ phá hại đó được khuyên bảo rằng hãy kính sợ Allah mà tôn trọng các giới nghiêm của Ngài và tránh xa các điều Ngài cấm thì y liền hất mũi thể hiện sự cao ngạo không chấp nhận chân lý và tiếp tục công việc tội lỗi của y. Phần thưởng dành cho kẻ này là bị tống cổ vào Hỏa Ngục, một chổ ở tồi tệ nhất.');
INSERT INTO "vn_mokhtasar" VALUES(2,207,'2:207','Và trong thiên hạ, có người tin tưởng Allah sẵn sàng bán mạng mình để phục vụ Thượng Đế bằng con đường Jihad, tìm kiếm sự hài lòng nơi Ngài. Allah luôn thương xót, khoan dung với đám nô lệ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,208,'2:208','Hỡi những người có đức tin nơi Allah và Thiên Sứ của Ngài, các ngươi hãy hòa mình hoàn toàn vào Islam mà chấp nhận mọi thứ trong nó. Đừng như dân Kinh Sách, chỉ tin tưởng một số trong Kinh Sách và bác bỏ số còn lại, cũng đừng giẫm theo lối mòn của lũ Shaytan bởi chúng là kẻ thù công khai với các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,209,'2:209','Nếu các ngươi lệch hướng sau khi các tiếp cận và nhận thức rõ các bằng chứng thì hãy biết rằng Allah rất nghiêm khắc và mạnh mẽ, Ngài sáng suốt trong quản lý và qui định, vì vậy hãy kính sợ Ngài và sùng bái Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,210,'2:210','Nhóm người đuổi theo những lối mòn của Shaytan mà xa lánh chính đạo, chúng không chờ đợi gì ngoài điều vào Ngày Tận Thế Allah sẽ được Allah đến gặp chúng bằng cách riêng của Ngài dưới mái vòm của đám mây để phân xử chúng, Thiên Thần sẽ bao quanh chúng từ mọi phía. Lúc đó, Allah phân xử mọi điều giữa chúng và chỉ Ngài duy nhất xem xét và phán quyết mọi vấn đề của con người.');
INSERT INTO "vn_mokhtasar" VALUES(2,211,'2:211','Hỡi Nabi, Ngươi hãy hỏi người dân Israel một câu hỏi trách mắng: Đã bao lần Allah trình bày rõ cho các ngươi các dấu hiệu khẳng định sứ mạng của các Thiên Sứ, vậy mà các ngươi vẫn ngoan cố phủ nhận và chống đối các dấu hiệu đó. Đối với ai đánh đổi các thiên ân của Allah bằng sự phủ nhận và bác bỏ sau khi y đã nhận biết rõ sự thật về nó thì hãy biết rằng Allah sẽ dành những hình phạt khắc nghiệt để trừng trị đám người vô đức tin và phủ nhận.');
INSERT INTO "vn_mokhtasar" VALUES(2,212,'2:212','Những người phủ nhận Allah bị cám dỗ bởi cuộc sống và sự hưởng thụ ngắn ngủi của trần gian, họ đã nhạo báng những người tin tưởng Allah và Đời Sau. Tuy nhiên, họ không biết rằng những người kính sợ Allah, chấp hành các mệnh lệnh của Ngài và tránh xa những điều cấm của Ngài chắc chắn sẽ tốt hơn những kẻ vô đức tin bởi họ sẽ được Allah ban thưởng cho Thiên Đàng vĩnh cửu. Quả thật, Allah muốn ban phát cho ai tùy ý Ngài mà không cần tính toán.');
INSERT INTO "vn_mokhtasar" VALUES(2,213,'2:213','Trước đây, nhân loại đã từng là một cộng đồng duy nhất đi theo đúng chỉ đạo, theo đúng tôn giáo của ông tổ Adam mãi đến khi họ bị lũ Shaytan cám dỗ, chia thành hai nhóm có đức tin và vô đức tin. Để kéo nhân loại trở lại tôn giáo cũ nên Allah đã lần lượt cử phái các Thiên Sứ đến làm những người báo tin mừng cho nhóm người tin tưởng những gì mà Allah đã chuẩn bị cho họ từ lòng khoan dung của Ngài, đồng thời cảnh cáo nhóm vô đức tin về hình phạt khủng khiếp mà Allah sẽ trừng phạt. Song song với điều đó, Allah gởi cho mỗi Thiên Sứ một quyển Kinh Sách chứa đựng những điều chân lý để làm bộ luật để phân xử khi mọi người có bất đồng và tranh chấp. Và không có tranh cãi về Kinh Tawrah (Cựu Ước) ngoại trừ những ai đã được ban cho kiến thức trong đám dân Do Thái sau khi các bằng chứng rõ rệt của Allah trưng bày cho họ thấy. Tuy nhiên, nhóm người có đức tin được Allah phù hộ đón nhận chân lý đó, quả thật Allah muốn hướng dẫn bất cứ ai tùy Ngài đến với con đường ngay chính, đó là con đường của đức tin Iman.');
INSERT INTO "vn_mokhtasar" VALUES(2,214,'2:214','Há các ngươi - hỡi những người có đức tin - tưởng rằng các ngươi được vào Thiên Đàng mà không bị thử thách tương tự các thế hệ trước các ngươi qua việc bị nghèo khó và bệnh tật ư? Những thử thách đó đã đẩy họ rơi vào sợ hãi khiến họ trở nên nôn nóng được Allah ban cho chiến thắng, thế là Thiên Sứ và nhóm người có đức tin cùng Y đã than: Bao giờ sự giúp đỡ của Allah mới đến đây?! Chẳng phải sự giúp đỡ của Allah gần với nhóm người tin tưởng và phó thác cho Ngài hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(2,215,'2:215','Các vị Sahbah của Ngươi - hỡi Nabi - đã hỏi Ngươi lấy tài sản nào để bố thí và bố thí cho ai ? Ngươi hãy trả lời họ: Thứ tốt nhất để các ngươi bố thí - là thứ tài sản Halal sạch sẽ -, hãy chu cấp cho cha mẹ, bố thí cho người trong họ hàng, ưu tiên cho người có quan hệ họ hàng gần nhất tùy theo nhu cầu của mỗi người, cho trẻ mồ côi, cho người không tài sản, cho người lỡ đường cơ nhỡ. Với bất cứ điều tốt đẹp nào mà các ngươi - hỡi những người có đức tin - đã làm dù ít hay nhiều thì Allah đều biết rõ, không điều gì có thể giấu giếm được Ngài và Ngài sẽ ban thưởng cho các ngươi dựa vào việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,216,'2:216','Hỡi những người có đức tin, các ngươi được ban hành nghĩa vụ phải chiến đấu vì chính nghĩa của Allah nhưng đây là điều làm các ngươi ghét bỏ bởi phải hi sinh tài sản và sinh mạng. Nhưng các ngươi nào biết điều mà các ngươi ghét bỏ lại là điều tốt hữu ích cho các ngươi như việc chiến đấu vì chính nghĩa của Allah có biết bao ân phước vĩ đại, trong đó chiến thắng được kẻ thù và giương cao lời phán của Allah (tức câu Shahadah); và ngược lại điều mà các ngươi yêu thích lại là điều xấu có hại cho các ngươi như việc bỏ Jihad bởi nó thể hiện sự hèn nhác và bị kẻ thù khống chế, trong khi Allah duy nhất biết rõ điều nào tốt có lợi cho các ngươi và điều nào xấu gây hại đến các ngươi còn các ngươi thì mù tịt không biết gì, thế nên hãy đáp lại mệnh lệnh của Allah vì nó sẽ tốt cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,217,'2:217','Thiên hạ hỏi Ngươi - hỡi Nabi - về giáo lý giết chóc trong những tháng cấm kỵ gồm: Zdul-Qa''dah, Zdul-Hijjah, Muharram và Rajab (tức tháng 11, tháng 12, tháng giêng và tháng 7 lịch Islam). Hãy trả lời họ việc giết chóc trong những tháng này là điều đại cấm kỵ đối với Allah giống như hành động người đa thần ngăn chặn con đường của Allah bị xem là hành động thối tha, nhưng việc cấm người người có đức tin đến Masjid Al-Haram và trục xuất dân cư của khu vực Masjid Al-Haram ra khỏi nó còn tệ hại hơn cả việc giết chóc trong các tháng Haram nữa. Nhóm người đa thần sẽ không ngừng đấu tranh với các ngươi - Hỡi những người có đức tin - để kéo các ngươi rời khỏi tôn giáo chân lý bằng mọi khả năng và kế sách họ thấy được. Thế nên ai phản đạo trong các ngươi và bị chết trên hiện trạng phủ nhận Allah thì mọi công đức trước đây của họ bị tiêu tan và ở Đời Sau họ sẽ bị tống cổ vào Hỏa Ngục và mãi mãi trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,218,'2:218','Quả thật, đối với nhóm người tin tưởng Allah và Thiên Sứ của Ngài và nhóm người bỏ quê hương để di cư đến với Allah và Thiên Sứ của Ngài, họ đấu tranh để giương cao lời phán của Allah, đó là nhóm người khát khao lòng thương xót và sự tha thứ của Allah, và Allah sẵn sàng tha thứ tội lỗi cho đám nô lệ của Ngài và thương xót họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,219,'2:219','Các vị Sahabah (các bạn đạo) của Ngươi hỏi Ngươi - hỡi Nabi - về rượu (gồm tất cả những chất gây say và làm mất lí trí). Họ hỏi Ngươi về giáo lý uống và mua bán rượu; hỏi ngươi về cờ bạc (gồm tất cả trò chơi ăn thua bằng tiền bạc)? Ngươi hãy trả lời họ: Đó là hai điều gây thiệt hại lớn về tôn giáo và cuộc sống như làm mất lí trí, tiêu hao tiền bạc, gây hận thù và căm ghét; tuy nhiên, nó vẫn mang lại tí lợi ích như kiếm thêm tiền nhưng mặt hại và tội lỗi của nó nhiều hơn mặt có lợi. Một khi mặt hại nhiều hơn mặt lợi thì người trí tuệ chắc sẽ lánh xa, đây là lời giải thích đầu tiên của Allah khi bắt đầu cấm rượu. Các bạn đạo của Ngươi - hỡi Nabi - hỏi Ngươi về định lượng để họ bố thí theo cách tự nguyện từ tài sản của họ, hãy đáp lại họ: Hãy bố thí phần tiền thừa mà các ngươi không cần tới (đây là lệnh đầu tiên liên quan đến Zakat, sau đó Allah qui định cụ thể về thể loại tài sản, mức lượng Zakat và giáo lý liên quan) qua các lời phân tích rõ ràng thế này chính là các giáo lý mà Allah qui định cho các ngươi, hi vọng các ngươi biết suy nghĩ.');
INSERT INTO "vn_mokhtasar" VALUES(2,220,'2:220','Ngài đã qui định thư thế để các ngươi biết suy nghĩ về cuộc sống hiện tại và Đời Sau. Các bạn đạo của Ngươi - hỡi Nabi - hỏi Ngươi về cách cư xử với trẻ mồ côi, có được phép chung chạ với chúng về tiền bạc, ăn uống và nhà ở không? Người hãy bảo họ: Nếu các ngươi dùng tài sản của riêng mình để nuôi nấng, cải thiện cuộc sống cho chúng quả là hành động cao cả và sẽ được ân phước vĩ đại từ Allah, điều đó sẽ tốt cho chúng bởi tài sản của chúng được cất giữ cẩn thận; nếu như các ngươi trộn lẫn hai tài sản của các ngươi và chúng lại với nhau cũng không sao bởi tất cả các ngươi đều là anh em đồng đạo của nhau nên việc giúp đỡ nhau là nên làm, bởi Allah biết rõ ai là kẻ muốn phá hại và ai là người muốn cải thiện trong số những người bảo hộ trẻ mồ côi. Nếu Allah muốn gây khó dễ cho các ngươi về vấn đề trẻ mồ côi là Ngài đã làm nhưng giáo lý của Ngài vốn xây dựng trên cơ sở dễ dàng và đơn giản. Quả thật, Allah luôn chiến thắng tất cả, Ngài sáng suốt trong tạo hóa, quản lý và qui định.');
INSERT INTO "vn_mokhtasar" VALUES(2,221,'2:221','Và các ngươi - hỡi người người có đức tin - không được kết hôn với phụ nữ đa thần mãi đến khi người phụ nữ đó có đức tin nơi một mình Allah, cải đạo sang Islam. Quả thật, một người nữ nô lệ có đức tin nơi Allah và Thiên Sứ của Ngài tốt hơn cả một người phụ nữ tự do thờ phượng bục tượng, cho dù người phụ nữ tự do đó có thu hút các ngươi bằng nhan sắc và tài sản của cô ta. Và phụ nữ Muslim không được kết hôn với đàn ông thờ đa thần, quả thật một người đàn ông nô lệ có đức tin nơi Allah và Thiên Sứ của Ngài tốt hơn cả người đàn ông tự do thờ đa thần cho dù người đàn ông tự do đó có thu hút các ngươi. Những người đó - những người đa thần nam và nữ - kêu gọi các ngươi bằng lời nói và hành động của họ để lôi kéo các ngươi vào Hỏa Ngục trong khi Allah kêu gọi các ngươi đến với các việc làm ngoan đạo để đưa các ngươi vào Thiên Đàng và để được Ngài tha thứ tội lỗi bằng ý muốn và hồng phúc của Ngài. Allah đã trình bày các dấu hiệu của Ngài cho nhân loại, hi vọng họ tiếp thu và noi theo.');
INSERT INTO "vn_mokhtasar" VALUES(2,222,'2:222','Các vị Sahabah của Ngươi - hỡi Nabi - hỏi Ngươi về kinh nguyệt (đó là loại máu tự nhiên xuất ra từ tử cung phụ nữ vào khoảng thời gian nhất định), Ngươi hãy trả lời họ: Kinh nguyệt có hại đến cả nam và nữ khi ân ái, vì vậy các ngươi hãy tạm dừng giao hợp (với vợ của các ngươi) trong suốt thời gian này, không được quan hệ tình dục với họ mãi đến khi họ dứt kinh nguyệt và đã tắm Junub. Sau khi, họ đã hết kinh nguyệt và đã tắm Junub thì các ngươi được phép giao hợp với họ tùy thích theo đúng qui định mà Ngài đã cho phép các ngươi: không được giao hợp qua đường hậu môn. Quả thật, Allah rất yêu thương người luôn biết quay đầu sám hối khi làm lỗi và thường xuyên giữ mình sạch sẽ.');
INSERT INTO "vn_mokhtasar" VALUES(2,223,'2:223','Các bà vợ của các ngươi là mảnh ruộng của các ngươi, họ sẽ sinh con cái cho các ngươi, tựa như mảnh đất cho ra cây trái. Bởi thế, các ngươi hãy đến mảnh đất của các ngươi như thế nào tùy thích miễn sao là theo đường âm đạo. Các ngươi hãy gửi đi trước cho bản thân mình bằng những điều tốt đẹp, trong đó, có cả việc một người đàn ông gần gũi vợ mình với định tâm thờ phượng Allah và hi vọng được con cái ngoan đạo. Các ngươi hãy kính sợ Allah mà tuân thủ đúng các giáo điều của Ngài và tránh xa những gì Ngài đã cấm liên quan đến phụ nữ, hãy nhớ rằng các ngươi sẽ phải trình diện Ngài vào Ngày Phán Xét, sẽ phải đứng trước mặt Ngài để chịu phán xét về mọi hành vi tốt, xấu mà các ngươi đã làm. Và Ngươi - hỡi Nabi - hãy báo tin mừng cho những người có đức tin về niềm hạnh phúc vĩnh hằng cũng như việc được ngắm nhìn sắc diện Thượng Đế của họ khi trình diện Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,224,'2:224','Và các ngươi đừng lấy việc thề thốt với Allah để cấm cản bản thân hành động thiện tốt, kính sợ Allah và can thiệp hòa giải mọi người. Ngược lại, khi các ngươi đã thề không làm điều thiện tốt thì các ngươi hãy thực hiện nó và chấp nhận chịu phạt Kaffa-rah vì phạm lời thề. Quả thật, Allah nghe rõ lời các ngươi nói, thông toàn mọi hành động các ngươi làm và Ngài sẽ thưởng phạt tùy theo việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,225,'2:225','Allah sẽ không thanh toán các ngươi về những lời thề thốt quen miệng không có chủ tâm của các ngươi, như câu cửa miệng thường ngày của các ngươi: Không, xin thề với Allah; đúng vậy, xin thề với Allah. Những lời thề này không cần phải chuộc tội Kaffa-rah hay phải chịu bất cứ sự trừng phạt nào. Ngài chỉ thanh toán các ngươi về những lời thề mang chủ đích từ lòng dạ của các ngươi. Và Allah luôn tha thứ tội lỗi cho đám nô lệ của Ngài, luôn chịu đựng không vội vã trừng trị họ ngay mỗi khi họ phạm tội.');
INSERT INTO "vn_mokhtasar" VALUES(2,226,'2:226','Đối với những ai thề không giao hợp với vợ thì thời chờ đợi không quá bốn tháng kể từ thời điểm họ thề. Điều này được xem giới luật Al-I-la''. Nếu y trở lại giao hợp với vợ trong khoảng thời gian bốn tháng thì quả thật Allah luôn khoang dung xí xóa mọi việc đã xảy ra giữa họ, Ngài đại lượng khi qui định cho họ chuộc tội để thoát khỏi lời thề thốt đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,227,'2:227','Và nếu họ đã quyết ly hôn bằng cách không ân ái với vợ cũng như không quay lại thì Allah luôn nghe rõ lời họ nói muốn ly hôn, Ngài tận tường chi tiết hoàn cảnh của họ và tâm ý của họ, rồi Ngài sẽ thưởng phạt dựa theo điều họ muốn.');
INSERT INTO "vn_mokhtasar" VALUES(2,228,'2:228','Phụ nữ sau li hôn phải ở vậy trong ba kỳ kinh nguyệt, không được tái giá trong suốt thời gian này, họ không được phép che giấu bào thai mà Allah đã tạo trong bụng của họ nếu họ thực sự có đức tin nơi Allah và Đời Sau. Và những người chồng của họ ưu tiên được quyền trở lại với họ trong khoảng thời gian ba kỳ kinh nguyệt này nếu muốn giảng hòa và xóa bỏ mọi hệ lụy xấu của li hôn. Cả vợ lẫn chồng được hưởng quyền lợi như nhau phù hợp theo truyền thống phong tục nhưng người chồng được giao quyền trội hơn vợ một bậc như quyền trụ cột gia đình (trong trách nhiệm bảo vệ) và quyền chủ động li hôn. Quả thật, Allah luôn chiến thắng tất cả, Ngài sáng suốt trong việc định đoạt và quản lý của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,229,'2:229','Người chồng chỉ được li hôn và trở lại chung sống với vợ tối đa là hai lần, cụ thể như người chồng tuyên bố li hôn rồi trở lại chung sống, sau đó tuyên bố li hôn rồi trở lại chung sống. Sau hai lần li hôn này, nếu muốn thì người chồng tiếp tục cuộc sống bền vững với vợ hoặc nếu muốn thì li hôn tiếp lần thứ ba và cho người vợ hưởng đúng mọi quyền lợi mà cô ta đáng được hưởng. Cấm các ngươi - hỡi những ông chồng - lấy lại bất cứ gì từ những thứ mà các ngươi đã trao tặng vợ làm quà cưới, ngoại trừ trường hợp người vợ ghét chồng do bản tính hoặc một điểm nào đó của chồng làm cho đôi bên có khoảng cách ngày càng xa hơn và không ai chịu trao quyền lợi cho nhau đúng giáo lý thì lúc này hãy trình bày sự việc cho những người trong họ hàng của hai bên để họ can thiệp tìm cách giải hòa, một khi những người đại diện họ hàng của hai bên đồng nhận thấy cả hai vợ chồng không thể tiếp tục chung sống thì người vợ được phép chuộc sự tự do của mình từ chồng bằng cách trả lại tiền cưới cho người chồng để được anh ta li hôn. Đây là giáo lý được Allah qui định phân biệt rõ giữa điều Halal (được phép) và điều Haram (bị cấm), các ngươi không được vượt qua giới hạn. Ai cố ý vượt qua giới hạn Halal và Haram thì y thuộc đám người sai quấy tự đẩy bản thân rơi vào sự hủy diệt, phải đối mặt với sự giận dữ và hình phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(2,230,'2:230','Khi người chồng li hôn người vợ đủ ba lần thì y không được phép quay lại kết hôn với cô ta cho đến khi cô ta kết hôn với một người chồng khác, một người chồng thực sự và đúng nghĩa chứ không mang mục đích cho việc trở lại của người chồng cũ. Nếu người chồng thứ hai li dị cô ta hoặc anh ta qua đời thì cô ta và người chồng thứ nhất không bị mắc tội nếu muốn quay lại với nhau nhưng phải có cuộc giao ước ''Nikah'' mới kèm theo tiền cưới mới khi cả hai cảm thấy có thể xây dựng một gia đình mới đúng theo giao luật Islam. Đó là bộ giáo luật li hôn mà Allah đã qui định cho loài người để họ biết rõ các giới hạn của Ngài nhằm giúp họ hưởng lợi từ bộ giáo lý đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,231,'2:231','Và khi các ngươi đã ly dị những người vợ của các ngươi rồi họ sắp hết ''Iddah (thời gian ở vậy được qui định cho họ), các ngươi được phép trở lại chung sống hoặc không trở lại đến hết ''Iddah. Các ngươi không được phép quay lại với họ với mục đích hành hạ và gây khổ sở cho họ giống như chế độ thời tiền Islam. Ai có chủ định gây hại vợ là y đã tự gây hại bản thân mình, tự đẩy thân mình vào tội lỗi và hình phạt phía trước. Các ngươi chớ lấy các lời phán của Allah mang ra để trêu đùa hay xúc phạm, các ngươi hãy tưởng nhớ các ân huệ mà Allah đã ban cho các ngươi và ân huệ lớn nhất là Ngài đã thiên khải cho các ngươi Qur''an và Sunnah để khuyến cáo và nhắc nhở các ngươi. Các ngươi hãy kính sợ Allah mà tuân thủ theo các giáo điều của Ngài và tránh xa mọi thứ bị Ngài cấm, các ngươi hãy luôn biết rằng Allah hằng biết rõ mọi điều các ngươi làm, không có bất cứ thứ gì có thể che giấu được Ngài và Ngài sẽ ban thưởng dựa vào việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,232,'2:232','Và khi các ngươi li hôn vợ ít hơn ba lần và thời gian ''Iddah đã hết thì không cấm các ngươi trở lại chung sống với vợ nhưng phải bằng cuộc giao ước mới, tiền cưới mới sau khi các cô vợ đồng ý trở lại chung sống với các ngươi. Đó là giáo lý của Allah dùng để nhắc nhở ai là người có đức tin Allah và Đời Sau, điều đó mang lại nhiều lợi ích cho các ngươi, nó bảo vệ danh dự và hành động của các ngươi khỏi sư nhơ nhuốc, và Allah luôn biết rõ chân tướng cũng như hậu quả của mọi sự việc trong khi các ngươi không hề biết điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,233,'2:233','Và các bà mẹ hãy cho các con bú sữa trong hai năm tròn, đây là qui định dành cho ai muốn đứa con được bú đầy đủ lượng sữa của mẹ. Và các ông bố phải cấp dưỡng cho các bà mẹ đã ly hôn đầy đủ chi phí sinh hoạt và quần áo tùy theo mức sống của từng vùng miền miễn không nghịch lại luật Islam, bởi Allah không hề ép bất cứ ai làm quá sức, quá khả năng. Cả hai cha và mẹ chớ lấy lý do con cái mà gây khó dễ lẫn nhau và cả người thừa kế chăm sóc đứa trẻ không cha cũng thế, bởi đứa trẻ không có tài sản riêng giống như cha của nó. Cả cha và mẹ sẽ không mắc tội khi cả hai bàn bạc với nhau dứt sữa cho con trước thời hạn hai năm và sau khi cả hai đã thống nhất để giúp đứa trẻ được tốt hơn; người cha sẽ không bị mắc tội khi y muốn tìm cho con mình người phụ nữ khác để cho bú thay mẹ ruột nhưng phải trả thù lao tương ứng phù hợp với từng vùng miền. Các ngươi hãy kính sợ Allah mà tuân thủ đúng giới luật của Ngài và tránh xa mọi điều Ngài cấm; các ngươi hãy biết rằng Allah luôn thấy rõ mọi việc các ngươi làm, không thứ gì, điều gì giấu được Ngài và Ngài sẽ thưởng phạt các ngươi dựa vào những việc mà các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,234,'2:234','Đối với phụ nữ chết chồng không có thai nghén phải giữ mình bốn tháng mười ngày, không rời khỏi nhà chồng, không trang điểm và không tái giá. Sau khi thời gian ở vậy đã hết thì các ngươi - những người bảo hộ - sẽ không bị mắc tội khi những quả phụ đó làm những điều đã bị cấm trong thời gian ở vậy miễn sao phù hợp với luật Islam và truyền thống. Mọi điều các ngươi làm Allah đều biết rõ, không việc gì giấu được Ngài dù các ngươi có hành động hay chỉ nghĩ trong lòng và Ngài sẽ dựa vào đó mà thưởng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,235,'2:235','Và các ngươi sẽ không bị mắc tội khi ngỏ ý muốn dạm hỏi phụ nữ chết chồng hoặc đã li hôn dứt khoát đang trong thời gian ở vậy, như nói "Khi nào cô hết ''Iddah (thời gian ở vậy) thì hãy cho tôi biết". Tương tự, các ngươi cũng không bị mắc tội khi trong lòng ham muốn kết hôn với góa phụ sau khi ''Iddah đã chấm dứt. Allah biết rõ các ngươi sẽ nhớ nhung da diết những phụ nữ đó nên Ngài cho phép các ngươi ngỏ ý nhưng không thổ lộ, Ngài cảnh báo các ngươi âm thầm hứa hẹn cũng như tuyên bố việc kết hôn lúc họ đang trong ''Iddah. Các ngươi hãy biết là Allah luôn thấu hiểu mọi ham muốn giấu kín trong lòng các ngươi về những điều Ngài cho phép và những thứ Ngài cấm các ngươi; và các ngươi chớ có nghịch lệnh Ngài, các ngươi hãy biết Ngài luôn nhân từ với nô lệ nào quay lại sám hối, Ngài nhẫn nại không vội vã trong việc trừng phạt họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,236,'2:236','Và các ngươi không bị mắc tội khi li hôn những người vợ ngay sau cuộc hôn ước trước khi động phòng và trước khi các ngươi đã định Mahr (tiền cưới) cho họ. Trong trường hợp này, không bắt buộc các ngươi phải trao cho họ tiền cưới mà chỉ cần các ngươi tặng họ một ít gì đó coi như bù đắp và an ủi họ, phần quà tặng tùy thuộc vào khả năng giàu nghèo của các ngươi, đây là phần quà bắt buộc đối với người ngoan đạo trong hành động và cư xử.');
INSERT INTO "vn_mokhtasar" VALUES(2,237,'2:237','Và trường hợp các ngươi li hôn vợ sau cuộc hôn ước trước khi động phòng nhưng đã định tiền Mahr cho họ thì các ngươi phải đưa cho họ một nửa, trừ phi họ chịu xí xóa cho các ngươi - nếu họ là những phụ nữ được hướng dẫn - hoặc người chồng rộng lượng chịu trao toàn bộ phần tiền cưới đã định. Tuy nhiên, việc xí xóa cho nhau là hành động gần hơn với lòng kính sợ Allah và tuân lệnh Ngài. Các ngươi - hỡi con người - chớ quên việc tôn trọng lẫn nhau và xí xóa cho nhau, quả thật Allah luôn thấy rõ mọi điều các ngươi làm, cho nên, hãy cố gắng làm tốt để hưởng được nhiều ân phước từ nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(2,238,'2:238','Các ngươi hãy chu đáo duy trì đầy đủ các lễ nguyện Salah theo lệnh của Allah, đặc biệt là lễ nguyện Salah Al-Wusa, đó là lễ nguyện Salah ''Asr; và các ngươi hãy đứng hành lễ bằng sự phục tùng và khúm núm trước Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,239,'2:239','Trường hợp các ngươi lo sợ kẻ thù hay một điều gì đó, các ngươi không thể hành lễ trọn vẹn theo cách đã được qui định thì hãy hành lễ trong lúc đi bộ hoặc lúc đang cưỡi trên lưng lạc đà, lưng ngựa hoặc trên bất cứ phương tiện tương tự. Nhưng khi không còn gặp phải nỗi sợ nữa thì các ngươi hãy tụng niệm Allah giống như Ngài đã dạy các ngươi; và một trong những cách tụng niệm Ngài là hành lễ Salah một cách chu đáo. Và các ngươi hãy tụng niệm Ngài vì đã dạy các ngươi chân lý trong lúc các ngươi không biết gì về Ánh Sáng và Chỉ Đạo.');
INSERT INTO "vn_mokhtasar" VALUES(2,240,'2:240','Những người đàn ông qua đời bỏ lại vợ thì phải di chúc lại cho người thừa kế cấp dưỡng nhà ở và tiền chi tiêu tròn một năm cho các góa phụ đó và không được phép đuổi họ ra khỏi nhà bởi họ đã phải đối mặt với tai họa mất chồng. Nhưng nếu họ tự nguyện rời khỏi nhà trước thời hạn một năm thì các ngươi - người thừa kế - không bị mắc tội, và họ cũng không bị tội khi chưng diện trở lại. Allah mạnh mẽ không gì vượt hơn, Ngài sáng suốt trong việc quản lý và định đoạt. Theo đại đa số ''Ulama (học giả Islam) chuyên về giảng giải Qur''an cho rằng giáo lý câu Kinh này đã bị thay thế bởi câu Kinh số 234 của chương Al-Baqarah: {Đối với phụ nữ chết chồng không có thai nghén phải giữ mình bốn tháng mười ngày.}');
INSERT INTO "vn_mokhtasar" VALUES(2,241,'2:241','Phụ nữ sau li hôn vẫn được quyền hưởng cấp dưỡng quần áo và tiền bạc hoặc các phụ cấp khác, đây là điều bắt buộc những ông chồng dựa vào tài sản nhiều hay ít của họ. Đây là giáo lý bắt buộc đối với người có lòng kính sợ Allah mà tuân thủ theo luật của Ngài và biết tránh xa điều Ngài cấm.');
INSERT INTO "vn_mokhtasar" VALUES(2,242,'2:242','Tương tự những gì đã được trình bày trước đó, Allah muốn chỉ rõ cho các ngươi - hỡi những người có đức tin - thấy các lời mặc khải của Ngài chứa đựng các giới hạn và các giáo lý, hi vọng các ngươi biết nhận thức và hiểu rõ để đạt được sự tốt đẹp ở trần gian và Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(2,243,'2:243','Phải chăng chưa đến tai Ngươi - hỡi Nabi - thông tin về nhóm người đã rời khỏi nhà với số lượng rất đông chạy trốn cái chết vì họ sợ bị dịch bệnh hoặc nguyên nhân khác. Họ thuộc nhóm con cháu Israel, Allah bảo họ: Các ngươi hãy chết đi, thế là họ liền bị chết, xong Ngài lại phục sinh họ trở lại để chứng tỏ cho họ biết mọi việc đều nằm trong tay Allah, chính họ không nắm trong tay bất cứ quyền hạn nào cho bản thân họ dù lợi hay hại, quả thật, Allah là Đấng ban phát mọi thứ cho thiên hạ nhưng đa số thiên hạ lại vong ân không biết tạ ơn Ngài về những hồng phúc mà Ngài đã ban cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,244,'2:244','Và các ngươi hãy anh dũng chiến đấu với kẻ thù - hỡi những người có đức tin - để mang niềm kiêu hãnh cho tôn giáo và giơ cao câu Shahadah. Các ngươi hãy biết rằng Allah nghe rõ điều các ngươi nói, biết rõ hoàn cảnh và hành động của các ngươi, dựa vào đó Ngài sẽ thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(2,245,'2:245','Ai cho người khác mượn (tiền) rồi bố thí nó vì chính nghĩa là y đã tạo cho bản thân điều tốt đẹp và làm cho tâm hồn mình thanh cao, y sẽ được bù lại rất nhiều lần so với thứ đã chi ra, Allah là Đấng thu hẹp bổng lộc, sức khỏe và mọi thứ cũng như ban phát vô hạn, tất cả thể hiện sự sáng suốt và công minh của Ngài, mọi thứ sẽ trở lại trình diện Ngài ở Đời Sau và Ngài sẽ dựa vào đó mà ban thưởng.');
INSERT INTO "vn_mokhtasar" VALUES(2,246,'2:246','Phải chăng Ngươi - hỡi Nabi - chưa nghe thông tin về nhóm thượng lưu thuộc dân Israel sau thời Musa, khi chúng nói: Hãy chọn cho chúng tôi một vị vua để chúng tôi cùng người đó sát phạt kẻ thù vì chính nghĩa của Allah. Nabi của chúng nói: E là khi Allah bắt buộc các ngươi chiếu đấu thì các ngươi không chịu xuất binh. Chúng liền đáp lại sự nghĩ xấu về họ: Điều gì cản chúng tôi đấu tranh vì chính nghĩa của Allah khi có người hướng dẫn chúng tôi chứ, hơn nữa khi chúng tôi đã bị kẻ thù trục xuất khỏi quê hương của mình và con cháu chúng tôi đã bị sát hại? Chắc chắn chúng tôi sẽ chiến đấu để giành lại quê hương và xóa tan mọi điều gây hại chúng tôi. Nhưng rồi khi Allah ban lệnh chiến đấu thì chúng lại ngoảnh mặt không chịu làm theo những gì đã hứa hẹn ngoại trừ số ít trong bọn chúng. Allah biết rõ đám người kháng lệnh Ngài, thất hứa với điều đã giao ước với Ngài, Ngài sẽ trừng phạt chúng về các tội lỗi đó.');
INSERT INTO "vn_mokhtasar" VALUES(2,247,'2:247','Và Nabi của chúng nói với chúng: Quả thật, Allah đã chọn Talut làm vua lãnh đạo các ngươi để các ngươi cùng y chiến đấu. Những kẻ giàu có và danh vọng phản đối: Làm sao y xứng đáng làm vua cai trị chúng tôi cho được, chúng tôi mới đáng làm vua, bởi y không thuộc dòng dõi hoàng tộc, cũng không phải là người giàu để hỗ trợ quyền lực của y? Nabi của chúng đáp: Quả thật, Allah đã lựa chọn y cho các ngươi, Ngài đã ban cho y thêm kiến thức và sức mạnh cơ bắp, Allah muốn ban vương quyền cho bất cứ ai Ngài muốn bằng sự sáng suốt và lòng nhân từ của Ngài, Allah là Đấng có bao la điều hồng phúc, Ngài muốn ban cho ai tùy ý Ngài, Ngài biết rõ ai xứng đáng được hưởng.');
INSERT INTO "vn_mokhtasar" VALUES(2,248,'2:248','Và Nabi của chúng bảo chúng: Quả thật, minh chứng thật sự cho việc Allah đã lựa chọn cho các ngươi một vị vua là Ngài cho lại các ngươi chiếc hộp - mà dân Israel luôn xem trọng - để họ an tâm tháp tùng với vị vua đó, và trong chiếc hộp vẫn còn những thứ được dòng dõi Musa và Harun truyền lại điển hình như chiếc gậy và một số Kinh Thánh, đó chính là minh chứng cho các ngươi nếu các ngươi thật sự tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(2,249,'2:249','Khi Talut xuất binh rời quê thì Người dặn dò quân lính: Quả thật, Allah thử thách các ngươi bởi một con sông, ai uống nước của nó thì y không được phép cùng ta xuất binh đánh trận, và những ai không uống nước sông đó thì y được phép xuất binh cùng ta ngoại trừ những ai quá khát nước thì được phép uống một ngụm cho đỡ khát thì không sao. Thế là hầu như đoàn quân đều uống nước sông ngoại trừ số ít trong bọn họ cố chịu đựng không uống trong khi rất khát nước. Đến khi Talut cùng đoàn quân có đức tin còn lại qua khỏi con sông, số quân còn lại lên tiếng: Ngày hôm nay, làm sao chúng ta có đủ lực lượng để đối đầu với đoàn quân của Jalut đây. Nhóm người có lòng tin kiên định rằng họ trở về trình diện Allah ở Đời Sau dù như thế nào lên tiếng: Há không phải đã có biết bao đoàn quân có đức tin trước đây tuy số lượng ít ỏi vẫn đại thắng trước đoàn quân hùng hậu của những kẻ vô đức tin dưới sự phù hộ và và giúp đỡ của Allah đó sao?! Động lực chính tạo nên chiến thắng là đức tin Iman chứ không phải là lực lượng nhiều hay ít, Allah luôn sát cánh với đám nô lệ kiên nhẫn để phù hộ họ giành chiến thắng.');
INSERT INTO "vn_mokhtasar" VALUES(2,250,'2:250','Và khi gần giáp mặt với đoàn quân Jalut thì Talut hướng về Allah mà khấn vái: Lạy Thượng Đế của bầy tôi, xin hãy đổ lên con tim của bầy tôi lòng kiên nhẫn, xin hãy củng cố đôi chân của bầy tôi trước kẻ thù để bầy tôi không bỏ chạy, xin Ngài đừng để bầy tôi thất bại trước kẻ thù, và Ngài gia tăng thêm sức mạnh cho bầy tôi đánh bại nhóm người vô đức tin này.');
INSERT INTO "vn_mokhtasar" VALUES(2,251,'2:251','Thế là họ được Allah phù hộ giành thắng lợi trước kẻ thù và Dawood đã tiêu diệt được Jalut, rồi Người được Allah ban cho vương quyền và sứ mạng Nabi, Ngài dạy Người mọi thứ Ngài muốn và tập hợp cho Người mọi thứ tốt đẹp của trần gian và Đời Sau. Nếu như Allah không tạo ra qui luật nhóm người này cấm cản nhóm kia gây rối và tàn phá thì chắc chắn trái đất đã bị nhóm người hủy hoại tàn phá mất rồi, nhưng Allah rất nhân từ đối với vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(2,252,'2:252','Đó là những Lời Mặc Khải rõ ràng của Allah được trình bày. TA đọc chúng cho Ngươi nghe - hỡi Nabi Muhammad -, trong nó chứa đựng đầy đủ thông tin trung thực và những giáo lý công minh, và Ngươi đích thực là vị Thiên Sứ thuộc nhóm các vị Thiên Sứ được Đấng Chúa Tể của vũ trụ và muôn loài cử phái đến.');
INSERT INTO "vn_mokhtasar" VALUES(2,253,'2:253','Đó là những Thiên Sứ mà TA (Allah) đã kể cho Ngươi (Muhammad) nghe, trong số họ có người được TA ban phúc trội hơn người kia về mặt thiên khải, số lượng tín đồ và cấp bậc, trong họ có người được trực tiếp nói chuyện với Allah như Musa; có người được nâng cấp bậc cao như Muhammad, Y được gởi đến cho toàn thể loài người, là người cuối cùng kết thúc sứ mạng Nabi và cộng đồng của Y được ưu đãi hơn các cộng đồng trước; TA đã ban cho Ysa con trai Maryam nhiều phép mầu để chứng minh sứ mạng Nabi của Y như làm sống lại người chết, chữa lành bệnh câm, bệnh bạch tạng, cho Jibril hổ trợ Y để Y mạnh mẽ hoàn thành sứ mạng được Allah giao phó. Và nếu như Allah muốn thì đã không xảy ra thảm cảnh giết chóc sau khi những Thiên Sứ được gởi đến với họ mang theo nhiều bằng chứng rõ rệt. Tuy nhiên, họ đã tranh cãi dẫn đến chia rẻ thành hai nhóm tin tưởng và phủ nhận. Nếu Allah muốn thì họ đã không hề sát hại lẫn nhau, nhưng Allah muốn làm sao tùy Ngài, Ngài hướng dẫn bất cứ ai Ngài muốn đến với đức tin Iman bằng lòng thương xót và hồng phúc nơi Ngài, và Ngài làm cho bất cứ ai lầm lạc tùy ý Ngài bằng sự công bằng và sự sáng suốt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,254,'2:254','Hỡi những người đã tin tưởng Allah và noi theo Thiên Sứ của Ngài, các ngươi hãy bố thí từ những loại tài sản hợp pháp mà Ngài đã ban bố cho các ngươi trước khi đại họa của Ngày Tận Thế xảy ra. Bởi trong Ngày hôm đó, sẽ không có bất cứ cuộc mua bán nào giúp ích được cho con người, không có tình nghĩa bạn bè để có thể giúp ích cho nhau vượt qua khó khăn, và cũng không một ai có thể đứng ra can thiệp bào chữa và xin tội cho ai ngoại trừ ai đó sau khi được Allah cho phép và hài lòng, và những người vô đức tin chính là những kẻ người làm điều sai quấy vì chúng đã phủ nhận Allah Tối Cao.');
INSERT INTO "vn_mokhtasar" VALUES(2,255,'2:255','Allah là Đấng duy nhất đáng được thờ phượng, không ai ngoài Ngài có quyền đó. Ngài là Đấng Bất Diệt, Toàn Năng và không khiếm khuyết; Ngài là Đấng Tự Hữu tồn tại độc lập không cần đến bất cứ tạo vật nào của Ngài, tất cả vạn vật đều phải cần đến Ngài chứ Ngài không cần đến vạn vật trong mọi hoàn cảnh; Ngài không buồn ngủ và không ngủ bởi đó là sự sống toàn mỹ và độc lập của Ngài; mọi vương quyền trong các tầng trời và trái đất đều thuộc về một mình Ngài; không một ai có quyền can thiệp cho ai trước mặt Ngài ngoại trừ ai đó được Ngài cho phép và hài lòng về y; Ngài tận tường và thông toàn mọi việc đã xảy ra trong quá khứ, đang xảy ra ở hiện tại và sẽ xảy ra ở tương lai; không một ai có thể xâm nhập vào kiến thức của Ngài ngoại trừ những gì Ngài muốn cho biết. Kursi - Bệ đặt bàn chân - của Ngài rộng trùm trọn cả các tầng trời và trái đất; tuy nhiên, việc quản lý và bảo vệ trời đất không hề làm Ngài mỏi mệt, Ngài là Đấng Tối Cao bằng bản chất, sự toàn năng và quyền lực vĩ đại của Ngài trong sự thống trị và chi phối.');
INSERT INTO "vn_mokhtasar" VALUES(2,256,'2:256','Không có sự ép buộc ai đó gia nhập Islam, bởi Islam là tôn giáo chân lý quá rõ ràng nên không cần phải ép buộc bất cứ ai gia nhập. Quả thật, điều chân lý luôn rõ ràng và chắc chắn có thể phân biệt được với điều ngụy tạo và lầm lạc. Thế nên, ai phủ nhận và vô can với mọi thứ được thờ phượng ngoài Allah và có đức tin nơi một mình Allah thì y đã bám chặt tôn giáo một cách mạnh mẽ và vững chắc, y sẽ không vuột khỏi sự cứu rỗi vào Ngày Phán Xet. Và Allah luôn nghe thấu mọi lời nói của đám nô lệ của Ngài, tận tường hết mọi cử chỉ của họ và sẽ ban thưởng tương xứng cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,257,'2:257','Allah bảo hộ những người có đức tin, Ngài sẽ phù hộ và giúp đỡ họ, Ngài đưa họ từ nơi tối tăm của sự vô đức tin và ngu muội ra ánh sáng của đức tin Iman và kiến thức. Và những kẻ vô đức tin, những tên bảo hộ của chúng là những bục tượng, những thần linh mà chúng đã tưởng tượng và dựng lên, đưa chúng từ ánh sáng của đức tin Iman và kiến thức để đến với nơi tối tăm của sự vô đức tin và ngu muội. Những kẻ đó sẽ làm bạn với Hỏa Ngục nơi mà chúng sẽ phải ở mãi mãi và đời đời.');
INSERT INTO "vn_mokhtasar" VALUES(2,258,'2:258','Chắc chắn Ngươi - hỡi Nabi - sẽ thấy kỳ lạ và không tin được về tính ngông cuồng của tên bạo chúa (Nimrud) đã tranh luận với Ibrahim về quyền năng của Allah. Quả thật, sau khi hắn được ban cho quyền bính thì trở nên ngông cuồng quá mức. Ibrahim đã trình bày cho hắn biết về quyền năng của Thượng Đế, Y nói: Thượng Đế của tôi là Đấng có quyền năng làm cho sống và làm chết vạn vật. Tên bạo chúa khoác lác một cách ngông cuồng: Ta cũng có quyền năng làm cho sống và làm cho chết bởi việc ra lệnh giết bất cứ ai ta muốn và chừa lại bất cứ ai ta muốn. Ibrahim trình bày một bằng chứng khác mạnh mẽ hơn, Y nói: Quả thật, Thượng Đế mà tôi đang thờ phượng có quyền năng làm cho mặt trời mọc từ hướng đông, vậy ngài có thể làm cho nó mọc từ hướng tây không? Thế là tên bạo chúa lúng túng vì đuối lý trước bằng chứng mà Ibrahim đã trình bày. Quả thật, Allah không phù hộ đám người lầm lạc, bất công, bạo ngược.');
INSERT INTO "vn_mokhtasar" VALUES(2,259,'2:259','Hoặc Ngươi có thấy kẻ khi y đi ngang qua một ngôi làng tan nát, điêu tàn, mọi thứ đều vỡ vụn, y lấy làm ngạc nhiên nói: Làm sao Allah có thể phục sinh ngôi làng này sau khi nó đã hoang tàn thế này cho được? Thế là Allah đã cho y ngủ thiếp đi một trăm năm, rồi Ngài cho y tỉnh lại và hỏi y: Ngươi đã ngủ thiếp đi bao lâu rồi? Y đáp: Tôi nghĩ mình thiếp đi khoảng một ngày hoặc nửa ngày gì đó thôi. Ngài phán: Không, ngươi đã ngủ một trăm năm tròn, tuy nhiên, ngươi hãy nhìn vào thức ăn và nước uống mà ngươi mang theo rằng nó vẫn được giữ nguyên hiện trạng ban đầu không có bất cứ một dấu hiệu bị thiu hay mục rã trong khi thức ăn, nước uống không thể không bị phân hủy với thời gian trôi qua lâu thế, và ngươi hãy nhìn vào con lừa của nhà ngươi đã chết, TA lấy đó làm dấu hiệu để trình bày cho thiên hạ biết về khả năng phục sinh của TA, ngươi hãy nhìn vào những khúc xương vỡ vụn khắp nơi sẽ tự gom lại kết chặt nhau, rồi TA dùng thịt bao bọc nó và cho nó sống trở lại. Sau khi tận mắt nhìn thấy cảnh tượng phục sinh thì y thừa nhận quyền năng của Allah nên liền thốt lên lời chứng nhận trong phủ phục: Bề tôi đã biết rằng Allah toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(2,260,'2:260','Và hãy nhớ lại - hỡi Nabi - khi Ibrahim cầu xin Allah: Lạy Thượng Đế của bề tôi, xin Ngài cho bề tôi chứng kiến cảnh Ngài làm cho cái chết sống lại như thế nào? Allah phán bảo Y: Há Ngươi không tin về điều đó sao? Ibrahim đáp: Dạ không, bề tôi rất tin nhưng bề tôi chỉ muốn gia tăng thêm đức tin trong lòng mình mà thôi. Thế là Allah ra lệnh bảo Y: Ngươi hãy lấy bốn con chim, rồi bầm nhuyễn chúng ra và trộn lẫn nhau, sau đó, leo lên đỉnh những ngọn núi quanh Ngươi và đặt mỗi phần lên mỗi đỉnn núi. Xong, Ngươi cất tiếng gọi chúng thì chúng lập tức bay đến nơi Ngươi và sự sống sẽ trở lại với chúng. Hỡi Ibrahim, ngươi hãy biết rằng Allah Toàn Năng trong việc thống trị, Ngài sáng suốt trong mọi việc định đoạt, tạo hóa và ban hành.');
INSERT INTO "vn_mokhtasar" VALUES(2,261,'2:261','Hình ảnh về phần thưởng dành cho người có đức tin đã bố thí tài sản vì chính nghĩa của Allah giống như hình ảnh của một hạt giống được gieo xuống mảnh đất màu mỡ mọc ra một thân cây có bảy nhánh bông, cứ mỗi nhánh bông gồm có một trăm hạt. Allah nhân lên phần thưởng cho bất cứ ai Ngài muốn trong đám nô lệ của Ngài, Ngài ban thưởng cho họ một cách không tính toán, bởi Allah có bao la thiên lộc, Ngài biết rõ ai xứng đáng được nhân lên phần thưởng.');
INSERT INTO "vn_mokhtasar" VALUES(2,262,'2:262','Những ai chi tiêu tài sản của mình vì tuân lệnh Allah và sự hài lòng Ngài nhưng không kèm theo những lời lẽ, thái độ hay hành động làm tổn thương người khác hoặc để phô trương lòng tốt chắc chắn sẽ được phần thưởng nơi Thượng Đế của họ; họ sẽ không phải lo sợ về tương lai của mình và cũng không buồn phiền về những gì đã qua bởi sự hưởng thụ vĩ đại mà họ sẽ có ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(2,263,'2:263','Lời nói tốt đẹp làm cho người có đức tin vui vẻ và việc xí xóa cho ai cư xử tệ với mình tốt hơn cả việc bố thí có kèm theo lời kể lể làm tổn thương người nhận sự bố thí. Quả thật, Allah luôn giàu có không cần đến đám nô lệ như thế, Ngài luôn nhẫn nại không vội vã trừng phạt họ.');
INSERT INTO "vn_mokhtasar" VALUES(2,264,'2:264','Hỡi những người có đức tin nơi Allah và tuân theo Thiên Sứ của Ngài, các ngươi chớ tự xóa đi ân phước mà các ngươi đã bố thí bằng cách kể lể làm tổn thương người nhận sự bố thí. Hình ảnh của những người hành động như thế giống như hình ảnh của những kẻ bố thí chỉ để được thiên hạ khen ngợi, chúng là những kẻ vô đức tin nơi Allah và nơi cõi Đời Sau cũng như không có đức tin nơi sự ban thưởng và sự trừng phạt. Hình ảnh của những kẻ này giống như hình ảnh một tảng đá to, bên trên bề mặt bằng phẳng của nó dính một lớp bụi, khi bị cơn mưa to trút xuống thì lớp bụi đó bị nước mưa quét trôi đi mất chỉ còn lại mỗi tảng đá trông không. Ân phước của việc bố thí và việc hành đạo của những kẻ phô trương muốn được lòng thiên hạ cũng tương tự như thế, chúng chẳng còn gì ở nơi Allah cả, bởi Allah không hề hướng dẫn đám người vô đức tin đến với những gì làm Ngài hài lòng, cũng như những việc làm mang lại lợi ích cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(2,265,'2:265','Và hình ảnh của những người có đức tin chi dùng tài sản của mình tìm kiếm sự hài lòng của Allah và cảm thấy an lòng về phần thưởng mà Ngài đã hứa hẹn giống như hình ảnh một ngôi vườn mọc trên gò đất cao mầu mỡ, khi gặp được mưa to thì nó tăng thêm sản lượng mùa màng còn khi gặp mưa nhỏ thì nó cũng đủ phát triển tốt tươi do đất vốn mầu mỡ. Tương tự với hình ảnh đó, những người bố thí thành tâm vì Allah rồi được Ngài chấp nhận sẽ được nhân lên phần ân thưởng gấp nhiều lần cho dù sự bố thí chỉ là ít ỏi. Quả thật, Allah luôn thấy rõ mọi việc các ngươi làm, Ngài thông toàn ai là kẻ phô trương và ai là người thành tâm, rồi Ngài sẽ ban thưởng xứng đáng cho từng người.');
INSERT INTO "vn_mokhtasar" VALUES(2,266,'2:266','Một ngôi vườn trái cây gồm chà là, nho cùng nhiều loại trái cây chín mộng đang chờ thu hoạch, và bên trong vườn luôn có dòng nước ngọt chảy bên dưới, nhưng ông chủ ngôi vườn lại già yếu không thể lao động và con cái thì còn quá nhỏ không thể lao động, rồi bổng cơn gió mạnh mang theo hơi nóng đốt cháy rụi cả ngôi vườn, ai đó trong các ngươi có muốn như thế không?. Đó là hoàn cảnh của người chi dùng tài sản chỉ để phô trương với thiên hạ, và vào Ngày Tận Thế họ chẳng có bất cứ ân phước nào nơi Allah trong khi đó là thời điểm rất cần đến ân phước. Với hình ảnh thí dụ này Allah muốn trình bày cho các ngươi những gì có thể giúp ích các ngươi ở trần gian và cõi Đời Sau, mong rằng các ngươi biết suy ngẫm và cân nhắc.');
INSERT INTO "vn_mokhtasar" VALUES(2,267,'2:267','Hỡi những người đã có tin nơi Allah và tuân theo Thiên Sứ của Ngài, các ngươi hãy chi dùng tài sản hợp pháp, tốt sạch mà các ngươi đã thu gom được và những thứ tài sản mà TA đã ban cho các ngươi thu hoạch được từ đất đai cho con đường chính nghĩa của Allah; các ngươi chớ lấy phần tệ nhất trong các tài sản của các ngươi để bố thí, cái mà ngay cả bản thân các ngươi cũng không muốn nếu các ngươi được cho do nó quá tệ giống như đồ bỏ đi; bởi thế, làm sao các ngươi có thể làm Allah hài lòng bởi những thứ không làm bản thân các ngươi hài lòng?! Các ngươi hãy biết rằng Allah là Đấng Rất Mực Giàu Có vốn không cần đến tất cả sự bố thí của các ngươi, Ngài là Đấng đáng được ca tụng về bản chất và hành động của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,268,'2:268','Shaytan làm cho các ngươi lo sợ về cái nghèo, xúi giục các ngươi keo kiệt và mời gọi các ngươi đến với tội lỗi và bất tuân Allah trong khi Allah luôn mời gọi các ngươi đến với sự tha thứ của Ngài và Ngài hứa sẽ ban bố bổng lộc cho các ngươi, bởi Allah có vô số thiên lộc, Ngài tận tường mọi hoàn cảnh của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,269,'2:269','Sự sáng suốt trong suy nghĩ và hành động là ân huệ mà Allah muốn ban cho ai tùy Ngài trong đám nô lệ của Ngài. Ai được ban cho điều đó là đã được ban biết bao điều tốt đẹp, và không ai nhận thức được điều đó ngoại trừ nhóm người có khối óc hoàn chỉnh được Ngài soi sáng và hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(2,270,'2:270','Và bất cứ tài sản nào mà các ngươi đã bố thí dù ít hay nhiều bằng định tâm làm hài lòng Allah hoặc việc các n gươi tự bắt bản thân luôn duy trì việc phục tùng Allah về những điều Ngài không bắt buộc thì quả thật Allah luôn am tường tất cả. Ngài sẽ không làm mất bất cứ thứ gì từ ân phước của các ngươi, ngược lại Ngài sẽ ban thưởng trọng hậu cho các ngươi. Và đám người làm điều sai quấy chắc chắn sẽ bị sự trưng phạt của Allah vì dám vượt quá giới hạn của Allah, chắc chắn họ sẽ không có ai cứu thoát khỏi hình phạt ở Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(2,271,'2:271','Nếu các ngươi công khai bố thí điều đó vẫn là một sự bố thí tốt đẹp, tuy nhiên, nếu các ngươi âm thầm bố thí cho người nghèo thì điều đó sẽ tốt đẹp hơn việc bố thí công khai, bởi nó thể hiện lòng Ikhlas nhiều hơn. Vả lại, sự bố thí bằng lòng Ikhlas sẽ che đậy tội lỗi và được sự tha thứ, Allah thông toàn mọi điều các ngươi làm, không một điều gì có thể giấu giếm được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,272,'2:272','Ngươi - hỡi Nabi Muhammad - không có quyền năng hướng dẫn cũng như làm cho họ chấp nhận Chân Lý, Ngươi chỉ có bổn phận trình bày, giới thiệu và dẫn dắt họ đến với Chân Lý, bởi quyền quyết định phù hộ cho ai đó giác ngộ được Chân Lý là ở trong tay Allah. Ngài muốn hướng dẫn ai đến với chân lý là tùy ý Ngài. Và mọi thứ tốt đẹp mà các ngươi đã bố thí sẽ mang lại lợi ích cho các ngươi, bởi Allah là Đấng rất mực giàu có không cần đến chúng. Và các ngươi hãy cần phải thành tâm vì Allah khi bố thí vì người có đức tin chân chính sẽ không bố thí ngoại trừ là để tìm sự hài lòng nơi Allah. Và mọi thứ mà các ngươi bố thí dù ít hay nhiều thì các ngươi cũng đều sẽ nhận được ân phước đầy đủ không bị thiếu sót bất cứ thứ gì, bởi Allah không hề bất công với một ai cả.');
INSERT INTO "vn_mokhtasar" VALUES(2,273,'2:273','Các ngươi hãy đưa tài sản bố thí cho người nghèo, những người mà họ do bận rộn con đường chính nghĩa của Allah nên không thể bôn ba tìm kiếm bổng lộc, những kẻ giàu có thiếu hiểu biết đã ngộ nhận họ có cuộc sống đầy đủ vì họ không ngửa tay xin xỏ thiên hạ do lòng tự trọng. Tuy nhiên, sự khốn khó vẫn hiện rõ lên cơ thể và quần áo họ mặc. Và mọi thứ các ngươi từng bố thí dù là tiền hay bất cứ vật gì thì Allah luôn biết rõ tất cả, rồi Ngài sẽ ban thưởng cho các ngươi trên những việc làm với những phần thưởng xứng đáng.');
INSERT INTO "vn_mokhtasar" VALUES(2,274,'2:274','Những người đã chi dùng tài sản của mình nhằm tìm kiếm sự hài lòng nơi Allah cả ngày lẫn đêm, lúc công khai và lúc thầm kín nhưng không hề phô trương cũng như không muốn tiếng tăm, đổi lại họ sẽ được phần thưởng trọng hậu ở Đời Sau, họ sẽ không lo sợ cho tương lai của mình cũng không buồn phiền với những gì đã lỡ mất ở trần gian, đó là hồng phúc và ân huệ từ nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(2,275,'2:275','Những kẻ giao dịch và trao đổi bằng hình thức Riba (cho vay lấy lãi), vào Ngày Phục Sinh, chúng sẽ đứng dậy từ cõi mộ giống như tình trạng bị quỷ nhập, chúng đi ngã nghiêng, đứng lên té xuống. Chúng bị như thế là vì chúng tự cho phép mình ăn đồng tiền Riba, chúng đã không phân biệt giữa hình thức Riba và hình thức mua bán kinh doanh được Allah cho phép, chúng bảo: Thật ra việc cho vay lấy lãi cũng giống như mua bán thôi, tất cả đều là mang tính hợp pháp, cả hai hình thức đều giúp tiền tăng trưởng. Thế là Allah bác bỏ sự so sánh điêu ngoa của chúng, Ngài trình bày cho chúng biết rằng Ngài chỉ chấp nhận việc mua bán kinh doanh vì đó là hình thức mang lại lợi ích đích thực cho từng cá nhân và xã hội, và Ngài cấm mọi hình thức manh tính chất cho vay lấy lãi bởi nó là nguồn gốc của sự bất công, ăn chặn đồng tiên của thiên hạ một cách bất chính. Ai đã nhận được lời cảnh báo từ Thượng Đế về việc cấm mọi hình thức mang tính chất Riba, y liền chấm dứt và sám hối với Allah thì y sẽ được Ngài xóa mọi tội lỗi mà y đã phạm vào hình thức Riba trong thời gian qua. Và những ai đã nhận được lệnh cấm này nhưng vẫn còn tái phạm thì đã có bằng chứng buộc tội họ, họ xứng đáng bị tống cổ vào Hỏa Ngục chịu hình phạt dài hạn, bởi việc ở trong Hỏa Ngục đời đời kiếp kiếp chỉ dành cho những người vô đức tin. Riêng những người mà trong trái tim họ vẫn còn đức tin nơi Allah thì họ chỉ bị trừng phạt trong một khoảng thời gian nhất định tương ứng với tội lỗi của mình.');
INSERT INTO "vn_mokhtasar" VALUES(2,276,'2:276','Allah sẽ hủy hoại tài sản thu gom được từ hình thức Riba, hoặc nó sẽ tiêu tan thật sự hoặc nó vẫn tồn tại nhưng mất đi Barakah (hồng phúc). Đối với tài sản làm Sadaqah (bố thí) thì sẽ được Allah gia tăng và sẽ được Ngài nhân ân phước lên nhiều thêm theo tiêu chuẩn cứ một ân phước được nhân lên mười và có thể lên đến bảy trăm lần hoặc nhiều hơn nữa, đồng thời được Ngài gia tăng thêm Barakah cho người làm Sadaqah. Quả thật, Allah không yêu thương những người vô đức tin ương ngạnh đã dám cho phép làm điều Haram và đã xem thường tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(2,277,'2:277','Quả thật những ai đã có đức tin nơi Allah và tuân theo Thiên Sứ của Ngài, luôn hành động ngoan đạo, hoàn thành bổn phận Salah đúng theo giáo luật của Allah và xuất Zakat cho người xứng đáng, họ chắc chắn sẽ hưởng được ân phước nơi Thượng Đế của họ, họ sẽ không lo sợ về tương lai của mình và không tiếc nuối mọi thứ đã qua ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(2,278,'2:278','Hỡi những người đã có đức tin nơi Allah và tuân theo Thiên Sứ của Ngài, các ngươi hãy kính sợ Allah mà tuân thủ mọi sắc lệnh của Ngài và tránh xa những gì Ngài đã cấm; các người hãy từ bỏ mọi hình thức kiếm lời từ hình thức Riba nếu các ngươi đích thực là những người có đức tin nơi Allah và nơi những gì Ngài đã cấm các người từ hình thức Riba.');
INSERT INTO "vn_mokhtasar" VALUES(2,279,'2:279','Nếu các ngươi không tuân theo mọi điều Ngài ra lệnh thì các ngươi hãy chuẩn bị tiếp đón một cuộc chiến từ Allah và Thiên Sứ của Ngài. Tuy nhiên, khi các ngươi biết lỗi mà sám hối và từ bỏ Riba thì các ngươi được quyền lấy lại đúng số tiền gốc đã cho vay, các ngươi sẽ không gây bất công cho ai và bản thân cũng không bị thiệt thòi gì.');
INSERT INTO "vn_mokhtasar" VALUES(2,280,'2:280','Và khi các ngươi đòi nợ những ai không có tiền trả nợ thì các ngươi hãy hoãn thêm cho họ đến một thời gian mà họ có thể trả. Nhưng nếu các ngươi rộng lượng xóa nợ hoặc giảm bớt một phần nợ cho họ thì điều đó sẽ tốt hơn cho các ngươi giá như các ngươi nhận biết nơi Allah có vô số thiên ân và hồng phúc.');
INSERT INTO "vn_mokhtasar" VALUES(2,281,'2:281','Và các ngươi hãy sợ hãi về hình phạt vào Ngày mà tất cả phải trở về trình diện Allah, mỗi người sẽ đơn độc đứng trước mặt Ngài và sẽ được thưởng phạt công minh về mọi hành động tốt xấu đã từng làm, không một ân phước nào của các ngươi bị mất đi và cũng không bị phạt oan cho bất cứ tội lỗi nào.');
INSERT INTO "vn_mokhtasar" VALUES(2,282,'2:282','Hỡi những người đã có đức tin nơi Allah và tuân theo Thiên Sứ của Ngài, khi các ngươi cho nhau vay mượn và hứa hẹn sẽ hoàn nợ trong thời gian nhất định thì các ngươi hãy viết số nợ đó ra; các ngươi hãy chọn một người công bằng để viết rõ ràng số nợ đó đúng theo giáo lý Islam, không gì cấm cản người viết nợ viết đúng theo giáo lý mà Allah đã dạy y một cách công bằng. Bởi thế y hãy viết những gì người mắc nợ khẳng định, và người mặc nợ hãy kính sợ Allah Thượng Đế của y mà nói chính xác phần nợ y đã mượn và nêu rõ hình thức thanh toán ra sao. Trường hợp người mắc nợ không giỏi xử trí vấn đề do còn nhỏ hoặc bệnh thần kinh hoặc không thể nói chuyện do bị câm thì hãy tìm người đại diện đáng tin cậy đứng ra đại diện và cần sự chứng nhận của hai người đàn ông trưởng thành, đáng tin; nếu không đủ hai người đàn ông thì cần một đàn ông và hai phụ nữ trong số những người mà các ngươi hài lòng, tin tưởng vào uy tín của họ phòng ngừa trường hợp một trong hai phụ nữ có quên thì người kia nhắc nhở. Nhân chứng không được phép từ chối khi được mời tham gia làm chứng cho số nợ đó, nghĩa vụ của nhân chứng phải đáp lại lời mời khi được yêu cầu. Các ngươi đừng có lười biếng phải viết cụ thể các khoản nợ dù ít hay nhiều và được trả trong thời gian nhất định, bởi viết giấy nợ là cách công bằng nhất trong luật của Allah, là bằng chứng thiết thực nhất để thanh toán nợ và là cách xóa tan mọi ngờ vực về số lượng, thể loại và hình thức hoàn nợ, bằng không thì các ngươi hãy mua bán bằng hình thức trả tiền đủ khi nhận hàng. Tuy nhiên, các ngươi không bị mắc tội nếu không viết giấy nợ khi không cần thiết, bởi luật được qui định là để tránh mọi tranh chấp về sau. Các ngươi không được tạo áp lực và gây hại đến người viết giấy nợ và nhân chứng, lại càng không được gây hại những ai yêu cầu viết giấy nợ và chứng cứ về khoản nợ, nếu ai dám gây hại là y đã kháng lệnh của Allah. Các ngươi - hỡi những người có đức tin - hãy sợ Allah mà tuân thủ đúng theo mọi điều Ngài ra lệnh và tránh xa mọi thứ Ngài cấm. Và Allah đã dạy các ngươi mọi thứ cải thiện mọi sự việc ở đời này và Đời Sau cho các ngươi, bởi Allah luôn tận tường mọi thứ, không điều gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(2,283,'2:283','Và khi các ngươi đang trong chuyến du hành, không tìm thấy ai viết hộ cho giấy nợ thì người mượn nợ chỉ cần đưa cho người chủ nợ một tín vật để bảo đảm quyền lợi của người chủ nợ cho tới khi món nợ được thanh toán. Trường hợp các ngươi đã tin tưởng lẫn nhau, không cần phải viết giấy nợ, nhân chứng hay tín vật thì lúc này nợ được xem là món hàng được ký gởi (Ama-nah) trong tay người mượn nợ, y phải hoàn trả đúng kỳ hạn, y hãy kính sợ Allah mà hoàn trả vật ký gởi này. Nếu nhân chứng bị cản trở thực hiện bổn phận làm nhân chứng thì y càng không được giấu giếm sự thật, nếu giấu giếm là y đã mang trong người con tim hư đốn, Allah biết rõ mọi thứ các ngươi làm, không có bất cứ thứ gì che giấu được Ngài, Ngài sẽ thưởng phạt thích đáng cho các ngươi qua các việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(2,284,'2:284','Tất cả các tầng trời và trái đất đều là tạo vật của một mình Allah, Ngài toàn quyền chi phối và thống trị. Cho dù các ngươi có phơi bày hay giấu giếm trong lòng bất cứ điều gì thì Ngài vẫn luôn am tường nó, Ngài sẽ thanh toán các ngươi về điều đó. Tuy nhiên, Ngài sẽ tha thứ cho bất cứ ai Ngài muốn bằng hồng phúc và lòng khoan dung của Ngài, và Ngài sẽ trừng phạt bất cứ kẻ nào Ngài muốn bằng sự công bằng và anh minh của Ngài; và Allah toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(2,285,'2:285','Thiên Sứ Muhammad đã có đức nơ tất cả những điều được mặc khải cho Người từ Thượng Đế Người và những người có đức tin cũng đã tin đúng như thế. Tất cả họ đều có đức tin nơi Allah, nơi tất cả Thiên Thần của Ngài, nơi các Kinh Sách của Ngài đã được ban xuống cho các vị Nabi, và nơi tất cả Thiên Sứ đã được cử phái đến, họ đồng thốt lên câu: Chúng tôi không kỳ thị phân biệt bất cứ ai trong số Thiên Sứ của Allah. Và họ nói cầu nguyện Allah: Bầy tôi xin tuân lệnh và phục tùng mọi điều Ngài đã ra lệnh, bầy tôi hứa sẽ hoàn thành tốt bổn phận và thực hiện đúng nghĩa vụ. Bầy tôi khấn vái Ngài, xin hãy tha thứ cho bầy tôi về tội lỗi đã phạm, lạy Thượng Đế của bầy tôi. Tất cả mọi việc của bầy tôi đều trở về trình diện Ngài duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(2,286,'2:286','Allah không bắt bất cứ ai làm quá khả năng của mình, bởi tôn giáo của Allah vốn được dựng trên sự dễ dàng và không gây khó khăn. Ai làm điều tốt thì sẽ có được phần thưởng một cách đầy đủ, không sót bất cứ điều gì; còn ai làm tội thì sẽ gặp phải hậu quả cho hành động tội lỗi đó. Thiên Sứ và những có đức tin thường cầu xin Allah: Lạy Thượng Đế của bầy tôi, xin đừng bắt tội bầy tôi trong những lúc bầy tôi quên hoặc lầm lỡ qua lời nói và hành động; lạy Thượng Đế của bầy tôi, xin đừng bắt bầy tôi mang những nghĩa vụ quá khả năng của bầy tôi giống như Ngài đã bắt những đám người trước bầy tôi vị sự sai trái của họ như những người Do Thái; xin Ngài xí xóa cho bầy tôi và tha thứ cho bầy tôi, xin Ngài thương xót bầy tôi bằng hồng phúc và sự khoan dung nơi Ngài, quả thật Ngài là Đấng Bảo Hộ của bầy tôi, xin Ngài phù hộ bầy tôi giành thắng lợi trước đám người vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(3,1,'3:1','{Alif. La-m. Mi-m} Đây là những chữ cái, ý nghĩa của nó đã được giải thích ở chương Al-Baqarah. Tuy nhiên, các chữ cái này muốn ám chỉ rằng sự bất lực của dân Ả-rập không thể mang đến một quyển sách tương tự như Qur''an trong khi Qur''an được sử dụng các ký tự Ả-rập ghép lại và tiếng nói của họ là tiếng Ả-rập.');
INSERT INTO "vn_mokhtasar" VALUES(3,2,'3:2','Allah là Thượng Đế đích thực, Đấng đáng được thờ phượng, ngoài Ngài không có ai (vật) xứng đáng được quyền đó; Ngài là Đấng có sự sống hoàn hảo tuyệt đối, Ngài không bao giờ chết và không có bất kỳ khiếm khuyết và điểm yếu nào; Ngài là Đấng Tự Hữu, Ngài tồn tại độc lập bất cần mọi thứ trong tạo vật của Ngài, Ngài mới chính là Đấng mà tất cả vạn vật phải lệ thuộc và cần.');
INSERT INTO "vn_mokhtasar" VALUES(3,3,'3:3','- 4 - Ngài đã ban cho Ngươi - hỡi Nabi - Thiên Kinh Qur''an chứa các nguồn tin chính xác và sự công bằng trong giáo lý. Nó tương tự như các Kinh Sách được thiên khải trước đây không có sự đối lập. Trước khi ban Qur''an cho Ngươi, TA đã thiên khải Kinh Tawrah (Cựu Ước) cho Musa và ban Kinh Injil (Tân Ước) cho Ysa. Tất cả Kinh Sách đã thiên khải đều hướng dẫn và cải thiện con người đến với điều tốt đẹp trong đạo và đời. Và Ngài đã ban chuẩn mực (Furqan) xuống để phân biệt giữa chân lý và ngụy tạo, giữa chỉ đạo và lầm lạc. Và những kẻ phủ nhận các Lời Mặc Khải của Allah đã ban xuống cho Ngươi sẽ phải bị một sự trừng phạt khủng khiếp, và Allah là Đấng Toàn Năng không có bất cứ thứ gì có thể vượt qua Ngài, và Ngài nắm quyền trừng phạt những kẻ đã phủ nhận các vị Thiên Sứ của Ngài và những kẻ chống đối lại ý chỉ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,4,'3:4','- 4 - Ngài đã ban cho Ngươi - hỡi Nabi - Thiên Kinh Qur''an chứa các nguồn tin chính xác và sự công bằng trong giáo lý. Nó tương tự như các Kinh Sách được thiên khải trước đây không có sự đối lập. Trước khi ban Qur''an cho Ngươi, TA đã thiên khải Kinh Tawrah (Cựu Ước) cho Musa và ban Kinh Injil (Tân Ước) cho Ysa. Tất cả Kinh Sách đã thiên khải đều hướng dẫn và cải thiện con người đến với điều tốt đẹp trong đạo và đời. Và Ngài đã ban chuẩn mực (Furqan) xuống để phân biệt giữa chân lý và ngụy tạo, giữa chỉ đạo và lầm lạc. Và những kẻ phủ nhận các Lời Mặc Khải của Allah đã ban xuống cho Ngươi sẽ phải bị một sự trừng phạt khủng khiếp, và Allah là Đấng Toàn Năng không có bất cứ thứ gì có thể vượt qua Ngài, và Ngài nắm quyền trừng phạt những kẻ đã phủ nhận các vị Thiên Sứ của Ngài và những kẻ chống đối lại ý chỉ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,5,'3:5','Quả thật, không có bất cứ thứ gì, dù trên trời hay dưới lòng đất, có thể che giấu được Allah, Ngài luôn tận tường tất cả mọi thứ dù kín đáo hay công khai.');
INSERT INTO "vn_mokhtasar" VALUES(3,6,'3:6','Ngài là Đấng đã tạo các ngươi trong bụng mẹ các ngươi thành các hình dáng khác nhau theo ý Ngài, Ngài tạo các ngươi thành nam hoặc nữ, đẹp hay xấu, trắng hay đen. Không có Thượng Đế đích thực nào đáng được thờ phượng ngoài Ngài; Ngài là Đấng Quyền Năng, không có bất cứ thứ gì có thể vượt qua Ngài; Ngài là Đấng Sáng Suốt trong tạo hóa, điều hành và định đoạt.');
INSERT INTO "vn_mokhtasar" VALUES(3,7,'3:7','Ngài là Đấng đã thiên khải cho Ngươi - hỡi Nabi - Thiên Kinh Qur''an gồm những câu Kinh rõ ràng làm bằng chứng và cơ sở giáo lý. Chúng là bộ luật gốc để trở lại tham khảo khi có tranh cãi và bất đồng quan điểm. Ngoài ra còn có những câu Kinh khác mang nhiều nghĩa làm cho đa số người ngộ nhận về ý nghĩa đích thực của nó. Đối với những người mà trái tim của họ muốn lệch khỏi Chân Lý thì bỏ đi nghĩa nổi trội (xác nghĩa nhất với ngôn từ được diễn đạt) của câu Kinh để hướng đến các nghĩa mang tính có khả năng (không chắc chắn) nhằm mục đích đưa thiên hạ rơi vào lầm lạc, họ giảng giải những câu Kinh đó theo dục vọng của bản thân họ như thế là để làm bình phong cho trường phái sai lệch của họ trong khi không ai hiểu rõ ý nghĩa đích thực của câu Kinh ngoại trừ một mình Allah. Riêng những người có kiến thức và hiểu biết thực sự thì họ luôn vững vàng mà nói: Chúng tôi tin tưởng toàn bộ vào Qur''an, bởi Nó vốn đến từ Thượng Đế của chúng tôi. Họ đã giảng giải những câu Kinh tổng quát đó theo nghĩa nổi trội và chắc chắn nhất. Và không ai ghi nhớ lời nhắc nhở ngoại trừ nhóm người có tâm trí lành mạnh.');
INSERT INTO "vn_mokhtasar" VALUES(3,8,'3:8','Và những người thông thạo Qur''an sẽ nói: Lạy Thượng Đế của bầy tôi, xin đừng làm trái tim bầy tôi lệch khỏi Chân Lý sau khi Ngài đã hướng dẫn bầy tôi đến với Nó; xin Ngài bảo vệ bầy tôi được bình an trước nhóm người bóp méo Chân Lý; xin Ngài bao trùm bầy tôi bằng lòng thương xót bao la của Ngài; xin Ngài hãy bảo vệ bầy tôi khỏi cơn bảo lệch lạc, quả thật Ngài - hỡi Thượng Đế của bầy tôi - là Đấng Ban Phát vô hạn.');
INSERT INTO "vn_mokhtasar" VALUES(3,9,'3:9','Lạy Thượng Đế của bầy tôi, việc Ngài sẽ tập trung toàn bộ nhân loại lại trước mặt Ngài để phán xét trong Ngày đã được ấn định nơi Ngài chắc chắn sẽ xảy ra, bởi Ngài - hỡi Thượng Đế của bầy tôi - không thất hứa.');
INSERT INTO "vn_mokhtasar" VALUES(3,10,'3:10','Quả thật, những ai phủ nhận Allah và các vị Thiên Sứ của Ngài thì tài sản và con cái của họ không bảo vệ được họ khỏi hình phạt của Allah, ở trần gian và cả Đời Sau, những người được miêu tả đó sẽ là chất đốt của Hỏa Ngục trong Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(3,11,'3:11','Và hoàn cảnh của những người vô đức tin này giống như hoàn cảnh của dòng họ Fir''awn và những thế hệ trước chúng đã phủ nhận Allah và bác bỏ các Lời Mặc Khải của Ngài. Allah đã trừng phạt chúng bởi tội lỗi mà chúng đã phạm, tài sản và con cái của chúng không giúp ích gì được cho chúng. Quả thật, Allah rất khắc nghiệt trong việc trừng phạt những ai đã phủ nhận Ngài và bác bỏ các Lời Mặc Khải của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,12,'3:12','Ngươi - hỡi Thiên Sứ - hãy nói vơi những kẻ theo các tôn giáo khác ngoài Islam: những người có đức tin sẽ đánh bại các ngươi và các ngươi sẽ phải chết trên tình trạng vô đức tin, rồi Allah sẽ tập trung tất cả các ngươi trong Hỏa Ngục, một nơi cư ngụ tồi tệ.');
INSERT INTO "vn_mokhtasar" VALUES(3,13,'3:13','Quả thật, các ngươi đã có được một bài học qua việc hai phe giao chiến nhau tại Badr, một trong hai là những người có đức tin thuộc quân Thiên Sứ của Allah, họ đã anh dũng chiến đấu để giơ cao lời phán của Allah và hạ thấp khẩu hiệu của những kẻ vô đức tín; phe thứ hai là những kẻ vô đức tin ở Makkah đã xuất binh với khí thế diễu võ dương oai nhằm khẳng định sức mạnh và uy lực trước những người có đức tin bởi sự yếu thế mà chúng tận mắt nhìn thấy lực lượng ít ỏi của họ. Tuy nhiên, kết quả là Allah đã phù hộ phe đại diện của Ngài giành chiến thắng và dĩ nhiên Ngài muốn cho ai chiến thắng là tùy ý Ngài. Quả thật, trong cuộc chiến là những bài học và lời cảnh báo dành cho những ai biết suy ngẫm rằng với đức tin Iman thì dù số lượng có ít và không hùng hậu nhưng cũng sẽ giành lấy chiến thắng trước những kẻ vô đức tin dù thế lực của chúng có đông đảo và hùng mạnh đến mấy.');
INSERT INTO "vn_mokhtasar" VALUES(3,14,'3:14','Allah cho biết con người sẽ bị thử thách bởi niềm đam mê, yêu thích cuộc sống trần gia như: phụ nữ, con cái, tích lũy tài sản, vàng bạc, giống ngựa tốt, lạc đà, bò, dê, cừu, cây trồng, trong khi tất cả chỉ là sự tô điểm cho cuộc sống thế gian, những thứ mà con người chỉ hưởng thụ trong chốc lát rồi sớm tan biến. Cho nên, những người có đức tin không nên đầu tư cho sự hưởng thụ ngắn ngủi đó mà hãy đầu tư cho điều đã được Allah hứa hẹn khi trở về trình diện Ngài, đó là Thiên Đàng với khoảng rộng bao la của trời đất.');
INSERT INTO "vn_mokhtasar" VALUES(3,15,'3:15','Hãy nói đi - hỡi Thiên Sứ: Các ngươi có muốn Ta báo cho các ngươi biết thứ tốt hơn sở thích của các ngươi không?! Phần thưởng dành cho người biết kính sợ Allah, luôn tuân thủ đúng giáo luật của Ngài và tránh xa những gì Ngài cấm sẽ là những ngôi vườn Thiên Đàng cùng những con sông chảy bên dưới những tòa lâu đài nguy nga, họ sẽ ở trong đó mãi mã, bất tử và không bao giờ có sự tàn lụi, trong đó, họ sẽ có được những người vợ thuần khiết không bị nhiễm bất kỳ điểm xấu nào; đồng thời họ sẽ được sự hài lòng mãi mãi từ Allah và sẽ không bao giờ bị Ngài giận dữ và phẫn nộ nữa. Quả thật Allah luôn thấy rõ mọi tình trạng của đám nô lệ của Ngài, không có bất cứ điều gì có thể giấu được Ngài và Ngài sẽ ban thưởng cho họ về các việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,16,'3:16','Cư dân Thiên Đàng là những người thường nói trong lời cầu nguyện Thượng Đế của họ: Lạy Thượng Đế của bầy tôi, chắc chắn bầy tôi đã tin tưởng nơi Ngài, tin vào tất cả những gì Ngài đã thiên khải cho các vị Thiên Sứ của Ngài, bầy tôi luôn tuân thủ theo giáo luật của Ngài, xin Ngài hãy tha thứ mọi tội lỗi mà bầy tôi đã phạm và xin Ngài cứu rỗi bầy tôi tránh khỏi hình phạt nơi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(3,17,'3:17','Họ là những người nhẫn nại hành đạo vì tuân lệnh Allah cũng như tránh xa mọi thứ Ngài đã cấm, họ đứng vững vượt qua được mọi thử thách, họ thật lòng trong lời nói và hành động của mình, họ hoàn toàn nạp mình cho Allah, họ bố thí tài sản của mình vì chính nghĩa của Allah, họ khẩn cầu sự tha thứ vào những lúc cuối đêm bởi lời cầu xin những lúc này có cơ hội được đáp lại rất cao vì tâm hồn của họ thanh tịnh.');
INSERT INTO "vn_mokhtasar" VALUES(3,18,'3:18','Allah tự xác nhận rằng chính một mình là Thượng Đế đáng được thờ phượng, không có bất cứ Thượng Đế nào đích thực khác ngoài Ngài. Điều đó đã được chứng minh bởi biết bao bằng chứng từ Kinh Sách và từ vũ trụ bao la của Ngài, đồng thời điều đó đã được giới Thiên Thần và giới ''Ulama (học giả) thông thạo kiến thức đồng xác nhận. Tất cả họ đã xác nhận Tawhid (giáo lý độc tôn Allah) vĩ đại dựa theo tạo hóa và định luật của Allah. Đúng là không có Thượng Đế đích thực nào ngoại trừ Đấng Oai Nghiêm, Đấng mà không có bất cứ thứ gì gì chiến thắng được Ngài, Ngài luôn sáng suốt trong tạo hóa, chi phối, điều hành và định đoạt.');
INSERT INTO "vn_mokhtasar" VALUES(3,19,'3:19','Quả thật, tôn giáo được Allah chấp nhận chỉ có Islam, bởi Islam là thần phục Allah duy nhất bằng cách hành đạo theo lệnh Ngài, chấp nhận thờ phượng riêng Ngài, có đức tin nơi tất cả Thiên Sứ của Ngài, trong đó, Thiên Sứ cuối cùng là Muhammad, một vị Thiên Sứ được Allah dùng làm dấu ấn kết thúc việc thiên khải cho nhân loại. Allah không chấp nhận bất cứ giáo luật nào ngoài Islam. Việc tranh cãi và chia rẻ trong nội bộ Do Thái và Thiên Chúa thành nhiều phe nhóm xảy ra sau khi họ nhận được bằng chứng bằng kiến thức vốn có, nhưng do lòng ganh tỵ và đam mê cuộc sống trần gian mà họ không chấp nhận Islam. Đối với ai không chấp nhận những gì được thiên khải cho Muhammad thì Allah sẽ sớm thanh toán đối với những ai phủ nhận Ngài và Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,20,'3:20','Nếu chúng tranh luận với Ngươi - hỡi Thiên Sứ - về Chân Lý đã thiên khải cho Ngươi, Ngươi hãy trả lời chúng: Ta và những ai theo Ta đã nạp mình phủ phục Allah. Và Ngươi hãy nói - hỡi Thiên Sứ - với đám dân Kinh Sách và những người thờ Đa Thần: Các ngươi có chấp thuận phủ phục Allah và tuân theo những gì được Ta mang đến hay không? Nếu chúng chấp nhận phủ phục Allah và tuân thủ theo giáo luật của Ngươi thì chúng quả thật đã chọn đúng chỉ đạo và nếu chúng ngoảnh mặt không chịu qui phục thì bổn phận của Ngươi chỉ là truyền đạt lời mặt khải mà Người đã được giao. Allah Tối Cao thấy rõ mọi việc làm của đám nô lệ của Ngài và Ngài sẽ tưởng thưởng họ một cách thỏa đáng.');
INSERT INTO "vn_mokhtasar" VALUES(3,21,'3:21','Quả thật, những kẻ phủ nhận mọi bằng chứng từ Allah đã thiên khải đến chúng và cố tình sát hại các Nabi một cách vô lý là hành động bất công và gây hận thù; và chúng cũng sát hại cả những ai đứng lên kêu gọi công bằng, động viên mọi người làm thiện và khuyên răng mọi người từ bỏ hành động sai trái, chúng quả thật chính là một đám vô đức tin xấu xa và chắc chắn sẽ bị một sự trừng phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(3,22,'3:22','Những ai có thuộc tính như đã miêu tả thì mọi việc làm thiện tốt của họ sẽ vô giá trị, chúng không giúp ích được gì cho họ ở trần gian lẫn Đời Sau bởi họ đã không có đức tin nơi Allah, và họ sẽ không có ai cứu giúp thoát khỏi hình phạt.');
INSERT INTO "vn_mokhtasar" VALUES(3,23,'3:23','Há Ngươi - hỡi Nabi - không nhìn vào hoàn cảnh của những người Do Thái đã được ban cho một phần kiến thức về Kinh Tawrah (Cựu Ước) và các bằng chứng xác minh sứ mạng của Ngươi, và khi chúng được mời gọi trở lại tham khảo trong Kính Sách của Allah tức Kinh Tawrah (Cựu Ước) để phân xử mọi tranh cãi giữa chúng nhưng có một nhóm học giả và lãnh đạo của chúng đã chống đối các giáo lý trong Kinh Sách vì đã không như mong muốn của chúng, trong khi điều tốt nhất cho chúng là chúng cần tuân thủ theo mọi giáo lý mà chúng tìm thấy trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(3,24,'3:24','Việc họ đã ngoảnh mặt với Chân Lý như thế là bởi vì họ tuyên bố rằng họ chỉ bị Hỏa Ngục thiêu đốt vài ngày ngắn ngủi rồi sẽ được vào Thiên Đàng. Với ảo tưởng này đã đẩy họ bạo gan thêu dệt thêm biết bao là sự dối trá và ngụy tạo về Allah và tôn giáo của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,25,'3:25','Thế họ sẽ ra sao khi họ phải bị sa vào tuyệt vọng của hối hận? Họ sẽ bị triệu tập trong hoàn cảnh tồi tệ nhất trong Ngày Phán Xét đó, Ngày mà mỗi người sẽ nhận được một quyển sổ ghi chép đầy đủ mọi hành động tốt xấu đã từng làm, theo đó họ sẽ được thưởng hoặc bị phạt một cách công bằng nhất, không ai bị cắt giảm phần ân thưởng cũng như không ai bị phạt oan ức.');
INSERT INTO "vn_mokhtasar" VALUES(3,26,'3:26','Hãy nói đi - hỡi Thiên Sứ - lời tụng niệm và tôn vinh Allah: Lạy Allah, Ngài là Chúa Tể nắm trọn trong tay cả trần gian và Đời Sau, Ngài muốn ban vương quyền cho bất cứ ai Ngài muốn; muốn tước đoạt vương quyền từ tay ai tùy Ngài; Ngài muốn ban vinh dự cho ai tùy Ngài và muốn hạ nhục ai tùy Ngài. Tất cả đều bằng sự sáng suốt và công bằng của Ngài, tất cả điều tốt đẹp đều ở trong Tay của một mình Ngài và Ngài toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(3,27,'3:27','Và trong những quyền năng của Ngài là Ngài nhập ban đêm vào ban ngày và kéo ban ngày dài ra, nhập ban ngày vào ban đêm và kéo ban đêm dài ra; Ngài phục sinh cái chết giống như Ngài kéo người có đức tin ra khỏi tình trạng vô đức tin, Ngài cho cây đâm chồi từ hạt giống; Ngài giết chết sự sống giống như tách người vô đức tin ra khỏi người người có đức tin, tách cái trứng ra khỏi con gà và ban phát vô định lượng cho bất cứ ai Ngài muốn.');
INSERT INTO "vn_mokhtasar" VALUES(3,28,'3:28','Cấm các ngươi - hỡi những người có đức tin - nhận những kẻ vô đức tin làm người bảo hộ, các ngươi thương yêu chúng, giúp đỡ chúng thay vì dành cho những người có đức tin. Ai dám làm điều đó thì y vô can với Allah và Allah vô can với y ngoại trừ việc lo sợ bị hại đến thân mình trước thế lực lớn mạnh thì các ngươi được phép nhẹ nhàng từ tốn trong lời nói và dè dặn trong hành động nhưng trong lòng luôn câm ghét chúng. Allah khuyến cáo các ngươi coi chừng Ngài, hãy sợ Ngài, không nên làm Ngài giận dữ bằng cách vi phạm điều Ngài cấm, bởi vì các ngươi sẽ phải trở lại đơn độc một mình trình diện trước Ngài trong Ngày Phán Xét để Ngài thưởng phạt thích đáng cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,29,'3:29','Hãy nói đi - hỡi Nabi -: Cho dù các ngươi có giấu kín trong tận đáy lòng những điều Allah cấm giống như quan hệ mật thiết với người vô đức tin hoặc thể hiện mối quan hệ đó ra bên ngoài thì Allah luôn biết rõ nó, không gì có thể giấu kín được Ngài, Ngài biết rõ mọi việc trong các tầng trời và dưới đất, Allah Toàn Năng trên tất cả mọi thứ, không có gì làm Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(3,30,'3:30','Vào Ngày Phán Xét, mỗi linh hồn sẽ đối diện với những việc làm mà nó đã làm dù tốt hay xấu, tất cả được mang ra xét xử một cách không thiếu không thừa. Những kẻ làm điều xấu khát khao khoảng cách giữa họ và tội lỗi là khoảng thời gian thật dài nhưng làm sao ước muốn của họ thành hiện thực cho được! Allah đã cảnh báo các ngươi về Ngài, cho nên đừng xâm phạm các giới luật mà làm Ngài giận dữ trong khi Allah luôn nhân từ với đám nô lệ của Ngài. Đó là lý do Ngài cảnh báo họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,31,'3:31','Hãy nói đi - hỡi Thiên Sứ -: Nếu các ngươi thật sự thương yêu Allah thì các ngươi hãy theo chỉ dẫn của Ta từ niềm tin bên trong đến hành động bên ngoài, các ngươi sẽ được tình yêu của Allah và được Ngài tha thứ tội lỗi. Allah luôn tha thứ và nhân từ đối với đám nô lệ biết sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(3,32,'3:32','Hãy nói đi - hỡi Thiên Sứ -: "Các ngươi hãy tuân lệnh Allah và tuân lệnh Thiên Sứ của Ngài bằng cách thực hiện tất cả lệnh Ngài đã bảo và tránh xa mọi điều mà Ngài cấm." Nếu họ ngoảnh mặt bỏ đi thì quả thật Allah không thương yêu đám người vô đức tin nghịch lại lệnh Ngài và lệnh Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,33,'3:33','Quả thật, Allah đã ưu đãi Adam khi Ngài ra lệnh tất cả Thiên Thần của Ngài quỳ lạy chào hỏi Người; Ngài đã ưu đãi Nuh khi chọn Người làm Thiên Sứ đầu tiên trên trái đất; Ngài đã ưu đãi dòng họ Ibrahim khi lưu giữ cho con cháu của Người sứ mạng Nabi và đã ưu đãi dòng họ ''Imran. Đó là nhóm người được Allah ưu đãi trong thời đại của họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,34,'3:34','Những người đã được nhắc gồm những Nabi và dòng dõi của họ đã đi đúng Chỉ Đạo mà thế hệ trước truyền lại trong việc bám chặt lấy Tawhid (tôn thờ một mình Allah) và làm những điều thiện tốt. Họ thừa hưởng các đức tín và ưu điểm của nhau. Quả thật, Allah luôn nghe rõ mọi lời nói và thấy rõ mọi hành động của đám nô lệ của Ngài. Vì vậy, Ngài muốn lựa chọn ai tùy Ngài và ưu đãi bất cứ ai Ngài muốn.');
INSERT INTO "vn_mokhtasar" VALUES(3,35,'3:35','Hãy nhớ lại - hỡi Thiên Sứ - khi vợ của ''Imran tức mẹ của Maryam nguyện cầu: Lạy Thượng Đế của bề tôi, bề tôi đã nguyện với lòng rằng xin hiến trọn đứa con trong bụng của bề tôi cho Ngài, để nó phục dịch Ngài và ngôi nhà của Ngài, xin Ngài hãy chấp nhận nguyện vọng này của bề tôi, quả thật, Ngài luôn thấu đáo lời thỉnh cầu của bề tôi và biết rõ định tâm của bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(3,36,'3:36','Khi bào thai đã tròn tháng và nữ ta đã hạ sinh thì nữ ta nói trong tiếc nuối - bởi nữ ta luôn hi vọng bào thai là con trai -: Lạy Thượng Đế của bề tôi, quả thật bề tôi đã hạ sinh bé gái, Allah biết rõ việc sinh nở của bề tôi, chắc chắn con trai mà bề tôi hi vọng khác với con gái mà bề tôi đã hạ sinh về mặt sức mạnh và ngoại hình, bề tôi đặt tên cho bé là Maryam (nghĩa là nữ nô lệ), bề tôi khẩn cầu Ngài bảo vệ nó và con cháu của nó tránh khỏi lũ Shaytan bị trục xuất khỏi lòng thương xót của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,37,'3:37','Nguyện vọng đó của nữ (mẹ của Maryam) được Allah đón nhận tốt đẹp, Ngài đã phù hộ cho Maryam được trưởng thành tốt đẹp và được lòng của nhóm bầy tôi ngoan đạo của Ngài quan tâm, Ngài cho Zakkariya quyền bảo hộ nuôi dưỡng Maryam. Cứ mỗi khi Zakkariya bước vào thư phòng riêng của Maryam, nơi cô hành đạo đều thấy nhiều loại thức ăn đã bày sẵn (toàn thức ăn trái mùa), ngạc nhiên hỏi: Này Maryam, từ đâu mà con có những loại thức ăn này vậy? Maryam đáp: Đây là thiên lộc từ nơi Allah, quả thật Ngài ban phát một cách vô định lượng cho bất cứ ai Ngài muốn.');
INSERT INTO "vn_mokhtasar" VALUES(3,38,'3:38','Sau khi Zakariya tận mắt thấy bổng lộc mà Allah ban cho Maryam con gái của ''Imran một cách khác thường, Người hy vọng được Allah ban cho đứa con trai trong lúc tuổi đã già còn vợ thì bị chứng hiếm muộn, thế là Người liền khẩn cầu: Lạy Thượng Đế của bề tôi, xin Ngài ban cho bề tôi đứa con trai ngoan đạo, quả thật Ngài là Đấng nghe rõ lời cầu xin của những ai khẩn cầu Ngài và Ngài sẽ đáp lại.');
INSERT INTO "vn_mokhtasar" VALUES(3,39,'3:39','Lúc Zakariya đang dâng lễ nguyện Salah tại nơi thờ phượng riêng thì các Thiên Thần gọi Người, nói: Quả thật, Allah báo tin mừng cho Ngươi về một đứa con trai tên là Yahya, một trong những đặc điểm của Y là Y xác thực lại lời phán của Allah về hoàn cảnh của Ysa con trai của Maryam - bởi Y được tạo hóa bằng cách đặc biệt bằng lời phán của Allah - đứa trẻ sẽ là nhà lãnh đạo của cộng đồng Y về mặt kiến thức và hành đạo, Y sẽ kiềm chế được bản thân tránh xa những dục vọng, trong đó có việc gần gũi phụ nữ, Y chuyên tâm hành đạo thờ phượng Thượng Đế và đồng thời là một Nabi ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(3,40,'3:40','Zakariya nói khi các Thiên Thần báo tin mừng về Yahya: Lạy Thượng Đế, làm sao bề tôi có con cho được trong lúc bề tôi đã già còn vợ bề tôi lại bị chứng hiếm muộn! Allah đáp lại: Hình ảnh việc tạo hóa Yahya lúc ngươi đã già và vợ ngươi bị chứng vô sinh cũng giống như hình ảnh Allah tạo hóa bất cứ thứ gì Ngài muốn cho dù có khác thường, bởi Allah có quyền năng trên tất cả mọi thứ, Ngài muốn làm gì Ngài muốn bằng sự sáng suốt và kiến thức của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,41,'3:41','Zakkariya thưa: Lạy Thượng Đế, xin Ngài ban cho bề tôi một dấu hiệu báo cho biết khi vợ bề tôi đã mang thai. Allah phán: Dấu hiệu mà Ngươi yêu cầu là khi nào Ngươi không thể nói chuyện với mọi người bằng lời suốt ba ngày, ba đêm, chỉ có thể ra dấu bằng tay, nhưng không phải là một triệu chứng bất thường. Lúc đó, Ngươi hãy tụng niệm và tán dương Allah thật nhiều vào lúc hừng sáng và lúc chạng vạng.');
INSERT INTO "vn_mokhtasar" VALUES(3,42,'3:42','Và hãy nhớ lại - hỡi Thiên Sứ - khi các Thiên Thần nói với Maryam: Quả thật, Allah đã ưu đãi ngươi khi ban cho ngươi những đức tính tốt đẹp, giúp ngươi trở thành người hoàn mỹ và Ngài đã ưu đãi hơn tất cả phụ nữ trong thời đại của ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,43,'3:43','Hỡi Maryam, ngươi hãy kéo dài lễ nguyện Salah và quỳ lạy Thượng Đế của ngươi và hãy cúi mình phủ phục Ngài giống như đám nô lệ ngoan đạo cúi đầu phủ phục Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,44,'3:44','Thông tin về Zakariya và Maryam được đề cấp đến là những thông tin trong quá khứ được thiên khải cho Ngươi - hỡi Thiên Sứ - và Ngươi đã không có mặt lúc những học giả cũng như những người ngoan đạo thời đó đã tranh giành quyền nuôi dưỡng Maryam, cuối cùng họ đi đến quyết định rút thăm bằng cách ném viết (xuống một dòng nước) và cây viết của Zakariya đã chiến thắng.');
INSERT INTO "vn_mokhtasar" VALUES(3,45,'3:45','Hãy nhớ lại - hỡi Thiên Sứ - khi các Thiên Thần nói: Hỡi Maryam, quả thật Allah báo cho ngươi tin mừng về đứa con trai được tạo ra không cần đến người cha, Y sẽ được tạo bằng lời phán {hãy thành}, lập tức sẽ trở thành đứa bé trai theo lệnh của Allah, và tên của bé trai này là: Masih Ysa (Giê su) con trai của Maryam, Y sẽ có địa vị cao quý ở đời này và Đời sau,và sẽ thuộc thành phần những người ở kế cận Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,46,'3:46','Và Y sẽ nói chuyện với thiên hạ lúc còn sơ sinh và nói chuyện với họ khi trưởng thành, Y sẽ khuyên bảo họ mọi điều tốt đẹp của trần gian và Đời Sau và Y thuộc nhóm người ngoan đạo trong cả lời nói lẫn hành động.');
INSERT INTO "vn_mokhtasar" VALUES(3,47,'3:47','Maryam bối rối về việc mình sẽ có con trong tình trạng không lập gia đình, nói: Làm sao tôi có con cho được khi không có người đàn ông nào chạm vào tôi dù là hợp pháp cũng như không hợp pháp? Thiên Thần đáp: Hình ảnh Allah tạo hóa đứa trẻ không cần đến người cha giống như Ngài muốn tạo hóa bất cứ gì Ngài muốn khác một cách khác thường. Quả thật, một khi Ngài muốn một điều gì thì Ngài chỉ cần phán {hãy thành} lặp tức nó sẽ thành theo ý của Ngài; chẳng có gì làm Ngài bất lực cả.');
INSERT INTO "vn_mokhtasar" VALUES(3,48,'3:48','Và Ngài (Allah) sẽ dạy Y (Ysa) biết đọc và biết viết, phù hộ Y trong lời nói và hành động luôn sáng suốt, Ngài dạy Y Kinh Tawrah (Cựu Ước) đã thiên khải cho Musa và dạy Y Kinh Injil (Tân Ước) sẽ thiên khải cho Y.');
INSERT INTO "vn_mokhtasar" VALUES(3,49,'3:49','Và TA (Allah) chọn Y là Thiên Sứ cho dân Israel với bức thông điệp: Quả thật, Ta là Thiên Sứ của Allah được cử phái đến với các ngươi, Ta mang đến bằng chứng khẳng định sứ mạng của Ta là thật, đó là: Ta có thể nắn con chim bằng đất và hà hơi vào nó thì nó trở thành con chim sống bằng sự ưng thuận của Allah; Ta chữa được bệnh mù bẩm sinh, bệnh cùi; Ta làm cho người chết sống lại, tất cả đều được Allah cho phép. Ta cho các ngươi biết loại thức ăn nào phải ăn và loại nào cần dự trữ mà các ngươi đã giấu kín trong nhà mình. Quả thật, với mọi thứ mà Ta cho các ngươi biết chắc rằng không một người phàm nào có thể làm được. Đó chính là bằng chứng khẳng định Ta đích thực là Thiên Sứ của Allah được gởi đến các ngươi, nếu các ngươi muốn đức tin Iman cũng như tin các bằng chứng.');
INSERT INTO "vn_mokhtasar" VALUES(3,50,'3:50','Và Ta đến còn để xác nhận lại sự thật về Kinh Tawrah đã được thiên khải trước Ta, để cho phép các ngươi vài thứ đã từng là những điều cấm đối với các ngươi trước đây mục đích giảm nhẹ cho các ngươi, Ta đến với bằng chứng minh bạch xác định đúng sự thật mà Ta đã tuyên bố. Các ngươi hãy kính sợ Allah mà thực thi theo mệnh lệnh của Ngài và tránh xa mọi điều Ngài cấm, các ngươi hãy phục tùng Ta khi Ta mời gọi các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,51,'3:51','Đó là bởi vì Allah là Thượng Đế của Ta và cũng là Thượng Đế của các ngươi, Ngài là Đấng duy nhất đáng được phục tùng và kính sợ, các ngươi hãy thờ phượng một mình Ngài, đó là điều mà Ta ra lệnh cho các ngươi thờ phượng và kính sợ Ngài. Và đó là con đường ngay chính.');
INSERT INTO "vn_mokhtasar" VALUES(3,52,'3:52','Khi Ysa thấy dân chúng Israel cứ ngoan cố trên sự vô đức tin thì Y hỏi trực diện họ: Ai đồng ý phò trợ Ta trên con đường kêu gọi đến với Allah? Nhóm người trung kiên đáp: Chúng tôi nguyện phò trợ tôn giáo của Allah, chúng tôi đã tin tưởng nơi Allah và xin tuân theo lệnh của Ngài, và chúng tôi xin tuyên thệ trước Người - hỡi Ysa - rằng chúng tôi là nhóm người phục tùng Allah bằng Tawhid (độc tôn Ngài).');
INSERT INTO "vn_mokhtasar" VALUES(3,53,'3:53','Và nhóm Hawari nói: Lạy Thượng Đế của bầy tôi, bầy tôi đã tin tưởng vào Kinh Injil mà Ngài đã thiên khải, bầy tôi nguyện noi theo Ysa, xin Ngài hãy xác nhận cho bầy tôi là nhóm người đã phục tùng Chân Lý, đã tin tưởng nơi Ngài và tất cả Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,54,'3:54','Và những kẻ vô đức tin thuộc dân Israel đã âm mưu sát hại Ysa. Thế là Allah lên kế hoạch bỏ mặc chúng trong lầm lạc và Ngài biến hình dạng một người đàn ông khác giống hệt Ysa. Kế hoạch của Allah là hoàn mỹ nhất, bởi không có kế hoạch nào của bất cứ ai có thể vượt trội hơn kế hoạch của Ngài cả.');
INSERT INTO "vn_mokhtasar" VALUES(3,55,'3:55','Và Allah còn lên kế hoạch khác với họ khi Ngài phán bảo với Ysa: Này Ysa, TA sẽ lấy Ngươi lên trời cả linh hồn lẫn thể xác nhưng không phải chết; TA tẩy sạch Ngươi khỏi mọi ô uế mà những kẻ vô đức tin gây hại Ngươi và kéo Ngươi rời xa họ. Vào Ngày Phán Xét, TA sẽ nâng cao những ai đi theo tôn giáo Chân Lý của Ngươi - trong đó có niềm tin vào Muhammad - trên nhóm người phủ nhận Ngươi qua các bằng chứng và niềm kiêu hãnh. Và vào Ngày Tận Thế các ngươi phải trở về trình diện TA để chịu sự xét xử của TA một cách công bằng về những điều các ngươi từng tranh cãi.');
INSERT INTO "vn_mokhtasar" VALUES(3,56,'3:56','Đối với những kẻ phủ nhận Ngươi và những Chân Lý mà Ngươi mang đến, chúng sẽ bị TA (Allah) trừng phạt một cực hình đau đớn ở trần gian bằng việc bị giết, bị bắt làm tù binh, bị xỉ nhục, ... và ở Đời Sau chúng sẽ bị thiêu đốt trong Hỏa Ngục mà không một ai hay thế lực nào có thể can thiệp và cứu vớt chúng khỏi hình phạt đó.');
INSERT INTO "vn_mokhtasar" VALUES(3,57,'3:57','Đối với những người có đức tin nơi Ngươi và cả Chân Lý mà Người mang đến, họ ngoan đạo qua các mặt Salah, Zakat, nhịn chay, hàn gắn tình dòng tộc ... chắc chắn sẽ được Allah ban thưởng trọng hậu, đầy đủ không thiếu sót bất cứ điều gì. Lời này là về những ai đi theo Masih Ysa trước sứ mạng của Muhammad mà Ysa đã từng báo tin mừng cho tín đồ của Người. Quả thật, Allah không yêu thương đám người bất công, và sự bất công lớn nhất là Shirk (tổ hợp với Allah trong thờ phượng) và phủ nhận sứ mạng của các Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(3,58,'3:58','Những gì mà TA (Allah) đọc cho Ngươi (Muhammad) từ thông tin về Ysa là những lời phán minh bạch khẳng định sự thực về những gì được mặc khải cho Ngươi, và đó cũng là lời nhắn nhở dành cho những người biết kính sợ một cách chuẩn xác không hề có chút ngụy tạo.');
INSERT INTO "vn_mokhtasar" VALUES(3,59,'3:59','Quả thật, hình ảnh tạo hóa Ysa đối với Allah giống như hình ảnh của việc Ngài đã tạo hóa Adam từ đất vậy, vốn không cần cha mẹ, chỉ cần Ngài phán {hãy thành con người} lập tức nó biến thành như ý muốn của Ngài. Làm sao mà chúng có thể khẳng định Ysa là thượng bởi vì Y được tạo hóa theo cách không có cha trong khi chúng vẫn luôn công nhận Adam - tổ tiên của loài người vốn đã được tạo hóa không cha và không mẹ?!');
INSERT INTO "vn_mokhtasar" VALUES(3,60,'3:60','Sự thật về Ysa đã được Thượng Đế của Ngươi phơi bày rõ ràng cho Người, cho nên Ngươi chớ nghi ngờ gì cả. Ngược lại, Ngươi hãy đứng vững trên Chân Lý đã thiên khải cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,61,'3:61','Ai trong đám dân Thiên Chúa tranh luận với Ngươi - hỡi Thiên Sứ - về Ysa và cho rằng Y không phải là người bề tôi của Allah sau khi Ngươi đã trình bày rõ ràng đúng thật về sứ mạng của Ysa, Ngươi hãy nói với chúng: Tất cả chúng ta cùng tập trung tất cả vợ con lại một nơi rồi cùng nhau khẩn cầu Allah nguyền rủa những ai trong chúng ta là người nói dối.');
INSERT INTO "vn_mokhtasar" VALUES(3,62,'3:62','Quả thật, nguồn tin mà TA đã kể cho Ngươi nghe về Ysa này đây hoàn toàn là sự thật, trong đó không hề có bất cứ chi tiết giả dối nào phải nghi ngờ; và không có Đấng thờ phượng đích thực nào ngoài Allah duy nhất. Quả thật Allah là Đấng Toàn Năng trong vương quyền của Ngài, Đấng Sáng Suốt trong điều hành, chi phối và tạo hóa của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,63,'3:63','Nếu họ nghoảnh mặt với những gì Ngươi mang đến, quyết không tuân theo Ngươi thì đó là hành động hư đốn, Allah biết rõ ai là kẻ hư đốn trên trái đất, rồi Ngài sẽ trừng phạt những kẻ đó một cách thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(3,64,'3:64','Hãy nói đi - hỡi Thiên Sứ -: "Hỡi dân Kinh Sách thuộc người Do Thái và Thiên Chúa, các người hãy đến đây để chúng ta cùng nhau thống nhất công bằng trên một lời phán dành cho tất cả chúng ta, đó là chúng ta chỉ thờ phượng Allah duy nhất, ngoài Ngài chúng ta không thờ phượng bất cứ ai dù kẻ đó có địa vị thế nào, chúng ta không tôn ai trong chúng ta lên vị trí thượng đế để nghe lời y mà kháng lệnh Allah." Nhưng nếu chúng quay bỏ đi, không nghe theo lời Chân Lý và lẽ công bằng mà Ngươi mời gọi thì Ngươi hãy nói với những người có đức tin: Mọi người hãy tự xác nhận chúng ta là những người thần phục Allah tuân thủ theo lệnh Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,65,'3:65','Hỡi đám dân Kinh Sách, tại sao các ngươi lại tranh luận về tôn giáo của Ibrahim? Những người Do Thái thì khẳng định Ibrahim là người Do Thái, còn những người Thiên Chúa thì khẳng định Y là người Thiên Chúa trong khi tất cả các ngươi biết rõ dân Do Thái và dân Thiên Chúa chỉ hiện diện sau khi Ibrahim đã qua đời rất lâu kia mà?! Há các ngươi không dùng đầu óc để suy nghĩ về lời nói điêu ngoa cũng như sự khẳng định sai trái của các ngươi ư?');
INSERT INTO "vn_mokhtasar" VALUES(3,66,'3:66','Chẳng phải các ngươi - đám dân Kinh Sách - đã tranh luận với Nabi Muhammad bằng kiến thức của các ngươi về tôn giáo của các ngươi cũng như mọi thứ đã thiên khải cho các ngươi, vậy tại sao các ngươi giờ đây lại tranh luận với Muhammad về Ibrahim và tôn giáo của Y trong khi các ngươi không hề có một tí kiến thức nào về Ibrahim và tôn giáo của Y, điều mà nó không hề có trong các Kinh Sách của các ngươi cũng như không hề được các Nabi của các ngươi mang đến?! Và Allah mới biết tận tường sự thật của mọi việc từ trong ra ngoài còn các ngươi thì không biết gì.');
INSERT INTO "vn_mokhtasar" VALUES(3,67,'3:67','Ibrahim vốn không phải là người Do Thái cũng không phải là người Thiên Chúa, Y vô can với tất cả tôn giáo lầm lạc, Y vốn là người Muslim thần phục Allah duy nhất, và Y không phải là người đa thần mà những người đa thần Ả-rập đã khẳng định.');
INSERT INTO "vn_mokhtasar" VALUES(3,68,'3:68','Quả thật, người xứng đáng thuộc cộng đồng Ibrahim là những ai đã tuân thủ theo mọi thứ mà Y đã truyền đạt trong thời đại của Y, sau đó người xứng đáng kế tiếp chính là vị Nabi Muhammad này và những người đã tin nơi Y, Allah luôn phù hộ nhóm người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(3,69,'3:69','Những tu sĩ của dân Kinh Sách thuộc người Do Thái và Thiên Chúa luôn ước ao đẩy các ngươi vào lầm lạc - hỡi những người cód đức tin. Chúng muốn kéo các ngươi rời khỏi Chân Lý mà Allah đã hướng dẫn các ngươi, tuy nhiên, chúng không thể đẩy ai vào lầm lạc ngoài bản thân chúng bởi khi chúng càng tìm cách đẩy những người có đức tin vào lầm lạc thì chúng càng lún sâu vào lầm lạc đó nhưng chúng không nhận ra hậu quả việc làm của mình.');
INSERT INTO "vn_mokhtasar" VALUES(3,70,'3:70','Hỡi đám dân Kinh Sách thuộc người Do Thái và Thiên Chúa, tại sao các ngươi dám bác bỏ các Lời phán của Allah đã thiên khải cho các ngươi và những bằng chứng khẳng định sứ mạng của Muhammad trong khi các ngươi xác định đó là sự thật đã được nêu rõ trong Kinh Sách của các ngươi?!');
INSERT INTO "vn_mokhtasar" VALUES(3,71,'3:71','Hỡi đám dân Kinh Sách, tại sao các ngươi dám hư cấu thêm vào Kinh Sách đã thiên khải cho các ngươi, tại sao các ngươi pha điều ngụy tạo vào điều Chân Lý, tại sao các ngươi che giấu sự thật, trong đó có sự thật về sứ mạng của Muhammad trong khi các ngươi phân biệt rõ đâu là đích thực là sự được hướng dẫn và đâu là lầm lạc?!');
INSERT INTO "vn_mokhtasar" VALUES(3,72,'3:72','Và một nhóm học giả Do Thái nói: Các ngươi cứ thể hiện là mình tin tưởng Qur''an ở buổi sáng như những người có đức tin nhưng đến chiều thì các ngươi bác bỏ nó, hi vọng như thế sẽ tác động mạnh mẽ đến những người có đức tin làm chúng nảy sinh mâu thuẫn về tôn giáo mà chúng đi theo vì lý do các ngươi đã phủ nhận Islam sau khi đã tin tưởng. Với việc làm này, có thể chúng sẽ quay về với đức tin xưa cũ và nói: Những ngươi dân Kinh Sách biết rõ hơn chúng ta về Kinh Sách của Allah, vậy mà họ vẫn bác bỏ Islam.');
INSERT INTO "vn_mokhtasar" VALUES(3,73,'3:73','Và chúng còn nói: "Các ngươi đừng tin tưởng và đi theo ngoại trừ ai từng đi theo tôn giáo của các ngươi." Hãy nói - hỡi Thiên Sứ -: Quả thật, Chân Lý chỉ nằm ở Chỉ Đạo của Allah mà thôi, chứ không có trong những thứ mà các ngươi giả tạo và chống đối, các ngươi sợ ai đó được ưu đãi giống như sự ưu đãi dành cho các ngươi hoặc các ngươi sợ sẽ bị thưa kiện trước Thượng Đế nếu các ngươi chấp nhận điều đã thiên khải cho họ. Hãy nói đi - hỡi Thiên Sứ -: Quả thật, sự ưu đãi nằm trong tay Allah, Ngài muốn ban cho ai tùy ý Ngài, không có việc ban cho cộng đồng này là không ban cho cộng đồng kia, Allah có hồng phúc bao la, Ngài biết rõ ai xứng đáng được ưu đãi.');
INSERT INTO "vn_mokhtasar" VALUES(3,74,'3:74','Ngài dành riêng Rahmah (lòng thương xót) của Ngài cho bất cứ tạo vật nào Ngài muốn, Ngài ưu đãi họ bằng sự Chỉ Đạo, sứ mạng Nabi cũng như biết bao thể loại ban phát khác, Allah có vô hạn hồng phúc vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(3,75,'3:75','Và trong đám dân Kinh Sách, có người Ngươi (Muhammad) an tâm giao cho y số tiền lớn rồi y uy tín giao trả lại cho Ngươi, có người dù Ngươi có giao cho y số tiền ít ỏi thì y vẫn sẽ không trả lại cho đến khi nào Ngươi đòi nhiều lần và thưa ra tòa, bởi những kẻ này nghĩ và tuyên bố rằng: Chúng tôi không bị mắc tội khi ăn tiền của người Ả-rập, bởi Allah đã cho phép chúng tôi điều đó. Đó là lời nói điêu ngoa vu khống cho Allah trong khi chúng biết rõ là như thế.');
INSERT INTO "vn_mokhtasar" VALUES(3,76,'3:76','Sự thật không đúng như chúng đã khẳng định. Không, chúng sẽ bị mắc tội, tuy nhiên, ai thực hiện đúng lời giao ước với Allah, đó là tin tưởng nơi Ngài và nơi các Thiên Sứ của Ngài, đồng thời giữ chữ tín với mọi người trong lời hứa, y kính sợ Allah mà tuân thủ đúng theo luật lệ của Ngài thì quả thật, Allah rất yêu thương nhóm người biết kính sợ Ngài và Ngài sẽ ban thưởng cho họ phần thưởng trọng hậu.');
INSERT INTO "vn_mokhtasar" VALUES(3,77,'3:77','Quả thật, những kẻ đánh đổi lời dặn của Allah về việc phải tuân thủ theo Kinh Sách của Ngài và nghe lời theo các Thiên Sứ của Ngài; cắt đứt giao ước với Allah bằng lợi lộc cỏn con của trần gian, chắc chắn chúng sẽ chẳng được bất cứ thứ gì ở Đời Sau, chúng sẽ không được Allah nói chuyện về điều làm chúng vui mừng, và Ngài sẽ không nhìn chúng bằng ánh mắt thương xót và chúng sẽ phải bị trừng phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(3,78,'3:78','Và quả thật, trong số những người Do Thái có một nhóm người đã cố tình lấy chiếc lưỡi điêu ngoa mình để bóp méo Kinh Tawrah (Cựu Ước) được ban xuống từ nơi Allah làm cho thiên hạ cứ tưởng họ đang đọc Tawrah, thật ra không phải từ Tawrah. Chẳng qua đó là lời dối trá rồi vu khống cho Allah, họ nói "Những gì chúng tôi đọc là do Allah thiên khải" nhưng sự thật không phải thế, họ đã cố ý vu khống cho Allah và các Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,79,'3:79','Một người được Allah ban cho Kinh Sách từ Ngài, được ban cho kiến thức và sự hiểu biết, rồi được chọn làm Nabi, không nên kêu gọi thiên hạ "Hãy là nô lệ thần phục riêng tôi thay vì Allah" mà y phải nên nói "Các ngươi hãy là những học giả tuân thủ đúng theo luật của Allah, hãy là nhóm người cải thiện mọi vấn đề của thiên hạ bởi lẽ các ngươi đã tiếp nhận được Kinh Sách, được học hỏi và hiểu biết".');
INSERT INTO "vn_mokhtasar" VALUES(3,80,'3:80','Tương tự, y cũng không được phép ra lệnh các ngươi tôn Thiên Thần hay Nabi lên làm thượng đế để thờ phượng thay vì Allah. Phải chăng y được phép ra lệnh các ngươi vi phạm tội Kufr (phủ nhận đức tin) nơi Allah sau khi y đã gọi và hướng dẫn các ngươi trở về thần phục Ngài ư?!');
INSERT INTO "vn_mokhtasar" VALUES(3,81,'3:81','Và hãy nhớ lại - hỡi Thiên Sứ - khi Allah đã xác định lại lời giao ước với các vị Nabi, Ngài phán với họ: Cho dù TA có ban cho các ngươi Kinh Sách ra sao, có dạy các ngươi sự khôn ngoan cũng như nâng cao địa vị của các ngươi lên cao thế nào thì đến khi vị Thiên Sứ được TA phái đến sau này là Muhammad với sứ mạng xác định lại Kinh Sách và sự Khôn Ngoan của các ngươi thì các ngươi vẫn phải tin tưởng Y hết mực và phò trợ Y, các ngươi xác nhận giời giao ước này không? Họ đồng đáp: Bầy tôi xin xác nhận. Allah phán: Các ngươi hãy tự xác nhận cho mình và cho tín đồ của các ngươi, còn TA sẽ cùng với các ngươi xác nhận điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(3,82,'3:82','Những ai chối bỏ sau cuộc giao ước này với Allah và các Thiên Sứ của Ngài thì đó là những kẻ đã rời khỏi tôn giáo và mệnh lệnh của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,83,'3:83','Phải chăng ngoài tôn giáo Islam mà Allah đã lựa chọn cho đám nô lệ của Ngài là thứ mà những kẻ rời bỏ tôn giáo và mệnh lệnh của Allah đang khao khát trong khi vạn vật trong trời đất dù muốn hay không đều phục tùng Ngài và ghét ở trong tình trạng vô đức tin?! Rồi đây, tất cả đều sẽ trở về trình diện trước Ngài ở cõi Đời Sau để chịu thanh toán và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(3,84,'3:84','Ngươi hãy nói đi - hỡi Thiên Sứ -: Chúng tôi đã tin Allah là Thượng Đế, tuân lệnh theo mọi điều Ngài ra lệnh, tin vào sự thiên khải cho chúng tôi, tin cả những gì đã được thiên khải cho Ibrahim, Isma-''il, Is-haq, Ya''qub, tin vào những điều đã được thiên khải cho giới Nabi thuộc dòng dõi Ya''qub, tin vào những điều đã ban cho Musa, Ysa và tất cả Nabi từ các Kinh Sách, các phép mầu; chúng tôi không kỳ thị hay phân biệt bất cứ ai trong tất cả các vị Nabi cũng như các vị Thiên Sứ, chúng tôi vâng lệnh Allah và quy phục Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,85,'3:85','Và những ai tìm lấy một tôn giáo nào khác ngoài tôn giáo đã được Allah hài lòng - đó là tôn giáo Islam - thì tôn giáo đó không được Allah chấp nhận. Ở Đời Sau, họ sẽ là những kẻ thua thiệt vì phải bị đày vào Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(3,86,'3:86','Làm sao Allah lại phù hộ đến với đức tin Iman nơi Ngài và Thiên Sứ của Ngài đối với đám người đã phủ nhận đức tin sau khi đã tin nơi Ngài cũng như sau khi đã nhận biết rõ sứ mạng của Muhammad là sự thật, đồng thời Y đã chứng minh sự thật về sứ mạng của mình?! Allah không bao giờ phù hộ đến với đức tin Iman đối với đám người sai quấy đã chọn lấy sự lầm lạc thay vì chọn Chỉ Đạo.');
INSERT INTO "vn_mokhtasar" VALUES(3,87,'3:87','Quả thật, phần thưởng dành cho đám người sai quấy đã chọn lấy lầm lạc là sẽ bị Allah, các Thiên Thần và tất cả loài người nguyền rủa, họ sẽ bị Allah xua đuổi khỏi lòng thương xót của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,88,'3:88','Chúng sẽ mãi mãi cư ngụ trong Hỏa Ngục không bao giờ được trở ra, chúng sẽ không được giảm nhẹ, không được trì hoãn chờ đợi để sám hối cũng như biện minh.');
INSERT INTO "vn_mokhtasar" VALUES(3,89,'3:89','Ngoại trừ những ai kịp thời sám hối sau khi đã phủ nhận đức tin và họ nỗ lực hành đạo chuộc lại tội lỗi đã phạm thì chắc sẽ được Allah tha thứ và thu nạp vào cùng đám nô lệ của Ngài, những người mà Ngài luôn nhân từ với họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,90,'3:90','Quả thật, những ai vô đức tin sau khi đã tin tưởng và ngoan cố trên tình trạng vô đưc tin đó cho đến chết thì sự sám hối lúc hấp hối không được chấp nhận vì đã hết thời gian sám hối, đó là những kẻ đã đi lệch con đường ngay chính dẫn đến Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,91,'3:91','Quả thật, đối với những ai chết trên tình trạng vô đức tin, cho dù y có được cả một trái đất vàng để chuộc thân khỏi Hỏa Ngục thì vẫn không được chấp nhận. Đám người đó vào Ngày Phán Xét sẽ phải bị trừng phạt đau đớn, chúng sẽ không có một ai giúp đỡ thoát khỏi hình phạt đó cả.');
INSERT INTO "vn_mokhtasar" VALUES(3,92,'3:92','Hỡi những người tin tưởng! Các ngươi sẽ không đạt được đỉnh điểm của đức tin và vị trí cao nhất của người ngoan đạo đến khi nào các ngươi bố thí vì chính nghĩa của Allah bằng chính loại tài sản mà các ngươi yêu thích, và với tất cả tài sản mà các ngươi đã bố thí dù ít hay nhiều thì Allah luôn biết rõ sự định tâm và việc làm của các ngươi, và Ngài sẽ ban thưởng đầy đủ cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,93,'3:93','Tất cả thức ăn tốt sạch là thực phẩm Halal cho bộ tộc Isra-il vốn không hề bị cấm trước khi Kinh Tawrah (Cựu Ước) được thiên khải xuống, ngoại trừ việc Nabi Y''aqub tự cấm bản thân mình. Không như lời nói khống của nhóm Do Thái là điều đó đã bị Kinh Tawrah cấm. Hãy bảo họ - hỡi Nabi - các ngươi hãy mang Kinh Tawrah đến đây và đọc công khai nếu những gì các ngươi nói thật. Thế là họ giật mình không dám mang Kinh Tawrah đến để đối chứng. Đây là một hình ảnh điển hình về việc nhóm Do Thái đã vu khống và cố tình chỉnh sửa, thêm bớt nội dung Kinh Tawrah.');
INSERT INTO "vn_mokhtasar" VALUES(3,94,'3:94','Đối với ai dám bịa đặt dối trá rồi đổ thừa cho Allah sau khi đã tiếp nhận được bằng chứng về việc Nabi Y''aqub tự mình cấm bản thân chứ nó không phải là lệnh của Allah thì đó là đám người bất công với bản thân mình vì đã chối bỏ chân lý sau khi bằng chứng đã được phơi bày rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(3,95,'3:95','Hãy nói đi - hỡi Nabi -: Thông tin mà Allah nói về Nabi Y''aqub là sự thật cũng như tất cả giáo lý và lời mặc khải từ Ngài, các ngươi hãy tuân theo tôn giáo của Nabi Ibrahim, bởi Người đã trối bỏ tất cả tôn giáo để qui phục Islam và Người chưa từng tổ hợp bất cứ ai, thần linh nào cùng với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,96,'3:96','Quả thật, Ngôi Đền đầu tiên được dựng trên trái đất để thờ phượng Allah chính là Ngôi Đền Makkah. Đó là Ngôi Đền Mubarak (hồng phúc) mang lại nhiều lợi ích về tôn giáo và cuộc sống, trong nó là Chỉ Đạo cho toàn thể loài người.');
INSERT INTO "vn_mokhtasar" VALUES(3,97,'3:97','Trong Ngôi Đền này có rất nhiều biểu hiệu thể hiện sự cao quí và giá trị của Ngôi Đền như các nghi thức hành hương, các địa điểm để hành hương, có phiếm đá mà Nabi Ibrahim đã đứng lúc xây dựng tường K''abah, ngoài ra ai bước vào bên trong Ngôi Đền là được an toàn, không ai dám gây hại. Thiên hạ được Allah qui định phải hướng đến đây để hành hương Hajj, tuy nhiên chỉ bắt buộc đối với người có đủ khả năng,nhưng ai đã phủ nhận bổn phận hành hương Hajj này thì hãy biết Allah rất mực giàu có không cần đến toàn thể vũ trụ.');
INSERT INTO "vn_mokhtasar" VALUES(3,98,'3:98','Hãy nói đi - hỡi Nabi -: Hỡi thị dân Kinh Sách trong nhóm Do Thái và Thiên Chúa, tại sao các ngươi lại dám bác bỏ về các bằng chứng khẳng định sứ mạng thật sự của Thiên Sứ Muhammad, trong khi đó có những bằng chứng được Kinh Tawrah (Cựu Ước), Kinh Injil (Tân Ước) khẳng định? Allah luôn biết rõ việc các ngươi đã làm, Ngài sẽ làm chứng cho điều đó và ban thưởng đúng chuẩn cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,99,'3:99','Hãy nói đi - hỡi Nabi -: Hỡi thị dân Kinh Sách trong nhóm Do Thái và Thiên Chúa, tại sao các ngươi lại làm điều nghịch lý ngăn chặn tôn giáo của Allah, kéo thiên hạ rời xa Chân Lý và đẩy họ rơi vào lầm lạc trong khi các ngươi biết rõ đây là tôn giáo chính thống xác định lại điều đã ghi chép trong Kinh Sách của các ngươi ?! Allah không hề xao lãng về hành động nghịch đạo này của các ngươi và việc các ngươi cấm cản con đường của Ngài, và Ngài sẽ cho các ngươi nếm hậu quả đó.');
INSERT INTO "vn_mokhtasar" VALUES(3,100,'3:100','Hỡi những người đã tin tưởng Allah và tuân theo Thiên Sứ của Ngài, nếu các ngươi nghe theo nhóm thị dân Kinh Sách thuộc người Do Thái và người Thiên Chúa về những gì họ nói, đón nhận ý kiến của họ về thứ mà họ đã khẳng định thì chúng sẽ kéo các ngươi trở lại hiện trạng vô đức tin sau khi các ngươi đã tin tưởng chỉ vì lòng ganh tị và hận thù vốn ẩn chứa trong lòng nên họ đã làm thế.');
INSERT INTO "vn_mokhtasar" VALUES(3,101,'3:101','Và làm sao các ngươi lại phủ nhận đức tin nơi Allah sau khi đã tin tưởng nơi Ngài, kèm thêm các ngươi có nguyên nhân lớn nhất để kiên định trên đức tin Iman như các câu Kinh của Allah đã xướng đọc cho các ngươi nghe, các Thiên Sứ của Ngài đã giải thích rõ cho các ngươi về ý nghĩa của lời phán đó. Vì vậy, ai biết bám chặt lấy Kinh Sách của Allah (tức Qur''an) và Sunnah của Thiên Sứ của Ngài là y đã được hướng dẫn đến Allah bằng con đường ngay chính không quanh co.');
INSERT INTO "vn_mokhtasar" VALUES(3,102,'3:102','Hỡi những người đã tin tưởng Allah và tuân theo Thiên Sứ của Ngài, hãy kính sợ Thượng Đế các người bằng lòng kính sợ thật sự bằng cách luôn tuân theo mọi mệnh lệnh của Ngài, tránh xa mọi điều Ngài đã cấm, tạ ơn Ngài về những hồng phúc đã ban và bám chặt lấy tôn giáo của Ngài mãi đến khi cái chết đến túm lấy các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,103,'3:103','Và hãy bám chặt lấy - hỡi tín đồ Mu''min - Qur''an và Sunnah, các ngươi chớ dẫm lên những điều khiến các ngươi phải chia rẽ, hãy cùng nhớ lại ân huệ mà Allah đã ban cho các ngươi khi các ngươi còn là kẻ thù của nhau trước Islam chém giết lẫn nhau chỉ vì lý do vớ vẫn, rồi các ngươi được Allah thống nhất con tim của các ngươi bằng Islam, từ đó các ngươi trở thành anh em đồng đạo của nhau, thương yêu lẫn nhau, quan tâm cho nhau, trong khi trước đây các ngươi suýt rơi vào Hỏa Ngục vì tội vô đức tin thì các ngươi được Allah cứu vớt bằng Islam và hướng dẫn các ngươi đến với niềm tin Iman. Qua những gì Allah đã trình bày cho các ngươi, Ngài muốn cải thiện các ngươi được tốt hơn trong cuộc sống ở đời này và ở Đời Sau và để các ngươi tự nhận biết con đường Chỉ Đạo và biết bám lấy chính đạo.');
INSERT INTO "vn_mokhtasar" VALUES(3,104,'3:104','Các ngươi - hỡi tín đồ Mu''min - hãy lập ra một nhóm mời gọi mọi người đến với sự tốt đẹp mà Allah thương yêu, khuyên bảo mọi người làm việc thiện đúng theo giáo lý qui định Islam và phù hợp với nhân cách sống, song cấm cản mọi người làm những điều nghịch giáo lý và phản nhân cách con người. Với những ai có tính cách vừa được nêu sẽ là nhóm người thành đạt mỹ mãn ở trần gian và ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(3,105,'3:105','Và các ngươi - hỡi những người có đức tin - chớ giống như nhóm thị dân Kinh Sách đã tự chia rẽ rồi trở thành phe đảng và băng nhóm, họ tranh luận về tôn giáo mình sau khi các bằng chứng rõ ràng của Allah đã trình bày cho họ thấy, nhóm người đó sẽ bị Allah trừng phạt thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(3,106,'3:106','Một hành phạt khủng khiếp sẽ gián lên họ trong Ngày Tận Thế, khi mà những gương mặt trắng sáng của nhóm người tin tưởng vui mừng hạnh phúc, song cũng có những gương mặt đen thui của nhóm người Kafir buồn bã và tuyệt vọng. Về nhóm người bị gương mặt đen đúa trong Ngày Vĩ Đại đó, có lời hỏi họ bằng giọng khiển trách: Sao các ngươi lại phủ nhận Tawhid (thuyết độc tôn) Allah khi Ngài đã giao ước với các ngươi đừng tổ hợp với Ngài bất cứ gì, với ai và các ngươi đã thống nhất điều đó. Thế nên các ngươi hãy nếm hành phạt của Allah đã dành sẵn cho các ngươi vì sự phủ nhận của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,107,'3:107','Và về nhóm người có gương mặt trắng sáng thì nơi ở của họ là trong Thiên Đàng hạnh phúc,họ ở mãi mãi trong nó với mọi hưởng thụ không bị thay đổi và không bị tiêu tan.');
INSERT INTO "vn_mokhtasar" VALUES(3,108,'3:108','Đó là những câu Kinh bao gồm những hứa hẹn ban thưởng và trừng phạt mà TA đã thiên khải cho Ngươi - hỡi Nabi - bằng nguồn thông tin chính xác, công bằng về giáo lý và Allah không hề muốn bất công với bất cứ ai trong nhân loại, Ngài sẽ không trừng phạt một ai ngoại trừ đúng với tội lỗi do bàn tay y tạo nên.');
INSERT INTO "vn_mokhtasar" VALUES(3,109,'3:109','Allah là Đấng duy nhất sở hữu tất cả trời đất từ việc tạo hóa và quản lý, rồi tất cả sẽ phải trở về trình diện Ngài để chịu thưởng phạt tùy vào hành động mỗi người đã từng làm trên trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(3,110,'3:110','Các ngươi - hỡi tín đồ của Muhammad - là cộng đồng tốt đẹp nhất được Allah tuyên dương bởi niềm tin Imam và hành động của các ngươi, là nhóm người hữu ích trong loài người, bởi vì các ngươi đã hiệu lệnh thiên hạ làm tốt đúng chuẩn mực tôn giáo, đúng nhân cách và cấm họ làm điều xấu nghịch giáo lý và không phù hợp nhân cách, ngoài ra các ngươi còn thật lòng tin tưởng Allah và khẳng định niềm tin đó qua việc làm của các ngươi. Giá như thị dân Kinh Sách thuộc Do Thái và Thiên Chúa cũng tin tưởng Muhammad thì quả là điều tốt đẹp cho họ ở trần gian và ở Đời Sau. Trong số thị dân Kinh Sách có một số ít đã tin tưởng vào những điều được Muhammad mang đến còn đa số thì tách ly khỏi tôn giáo của Allah và giáo luật của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,111,'3:111','Và cho dù họ có hận thù bao lớn cũng không gây hại được các ngươi đâu - hỡi tín đồ Mu''min - không hại được tôn giáo của các ngươi cũng như không hại được các ngươi ngoại trừ việc họ tự làm bẩn miệng của họ qua việc nói xấu về tôn giáo, chế nhạo các ngươi... nếu các ngươi bị tấn công thì họ sẽ cúi đầu rút lúi trước mặt các ngươi và không hề giúp đỡ các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,112,'3:112','Sự sỉ nhục và hèn hạ luôn vây lấy người Do Thái dù họ có sinh sống bất cứ ở đâu, họ sẽ không được bình an cho đến khi có được giao ước hoặc sự cam kết từ Allah hoặc từ thiên hạ, và họ một lần nữa bị sa vào cơn thịnh nộ của Allah, song họ bị cái nghèo khổ, túng thiếu bao trùm họ vì những nguyên nhân bác bỏ các dấu hiệu của Allah, cố tình giết chết các vị Nabi của Ngài, kèm thêm bản tính chống đối của họ và việc họ vượt xa giới hạn của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,113,'3:113','Tât cả thị dân Kinh Sách không hề giống nhau, trong họ có nhóm người ngoan đạo theo tôn giáo của Allah, họ luôn tuân theo lệnh làm và lệnh cấm của Ngài, họ xướng đọc các câu Kinh của Allah trong đêm dài lúc hành lễ Salah vì Allah, họ đã sống trước sứ mạng của Thiên Sứ Muhammad và những ai còn sống đến thời Thiên Sứ thì đã gia nhập Islam.');
INSERT INTO "vn_mokhtasar" VALUES(3,114,'3:114','Họ tin tưởng nơi Allah và vào Ngày Tận Thế bằng niềm tin mạnh mẽ, họ kêu gọi mọi người làm điều tốt và cản mọi người làm tội lỗi và xấu xa, họ tranh đua nhau làm việc thiện, họ tận dụng triệt để các cơ hội hành đạo. Những thuộc tính ngoan đạo đó, họ là đám nô lệ của Allah đã cải thiện được sự định tâm và việc làm của mình.');
INSERT INTO "vn_mokhtasar" VALUES(3,115,'3:115','Với những việc tốt mà họ đã làm dù ít hay nhiều sẽ không bị mất phần ân phước cũng như không bị hưởng ít hơn. Allah biết rõ ai là người Mutaqin (kính sợ Allah) đã tuân thủ theo mọi sắc lệnh của Ngài, biết tránh xa những điều Ngài cấm, bởi không việc làm nào của các ngươi có thể giấu kín được Ngài và Ngài sẽ ban thưởng thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(3,116,'3:116','Quả thật, những ai phủ nhận Allah và các Thiên Sứ của Ngài thì tài sản và con cái của họ không giúp ích gì được cho họ trước Allah, không bảo vệ được họ trước hình phạt cũng không mang đến cho họ được Rahmah (lòng thương xót), ngược lại họ sẽ bị gia tăng thêm hành phạt và tuyệt vọng, đó là nhóm bạn của Hỏa Ngục, họ muôn đời trong nó.');
INSERT INTO "vn_mokhtasar" VALUES(3,117,'3:117','Việc người ngoại đạo chi dùng tài sản vào những mặt tốt với hi vọng được phước giống như hình ảnh một cơn gió rét đậm thổi vào khu vườn chờ thu hoạch của đám người bất công bởi tội lỗi họ đã phạm, khu vườn là cả ước mơ tươi sáng của họ bổng chóc lát bị cơn gió rét đó tàn phá xác xơ. Tương tự với hành động bác bỏ đức tin sẽ xóa tan mọi ân phước của việc làm tốt đẹp mà người chủ nuôi hi vọng. Quả thật, Allah không hề bất công với họ mà chỉ riêng họ tự bất công với bản thân mình vì đã bác bỏ Ngài và phủ nhận các Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,118,'3:118','Hỡi những người đã tin tưởng Allah và noi theo Thiên Sứ của Ngài, chớ kết thân với ai đó ngoài tín đồ Mu''min làm thân tín, rồi phơi bày cho họ biết hết mọi bí mật và sinh hoạt của các ngươi. Chẳng những họ muốn gây hại các ngươi mà còn muốn hủy diệt toàn bộ các ngươi, lòng hận thù của họ thể hiện rõ qua lời nói của họ như xúc phạm đến tôn giáo của các ngươi, đẩy các ngươi vào thế khó và công khai bí mật của các ngươi, chưa đâu những gì họ giấu kín trong lòng họ còn nguy hiểm hơn nữa kìa. TA đã phân tích rõ ràng cho các ngươi thấy - hỡi nhóm người Mu''min - qua các bằng chứng cụ thể để cải thiện cuộc sống đời này và Đời Sau cho các ngươi, nếu như các ngươi nhận thức hết những gì Thượng Đế đã thiên khải cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,119,'3:119','Hay là các ngươi - hỡi tín đồ Mu''min - lại yêu thích họ mất rồi, nuôi hi vọng tốt đẹp từ họ nhưng họ lại không yêu thích các ngươi và cũng không hi vọng điều tốt gì từ các ngươi cả. Ngược lại, họ hận các ngươi vì việc các ngươi đã tin tưởng hết tất cả Kinh Sách kể cả Kinh Sách của họ, còn họ thì không tí tin tưởng vào Kinh Sách Qur''an đã ban cho Thiên Sứ của các ngươi, khi nào họ gặp các ngươi thì luôn miệng bảo: "Chúng ta là bạn thân của nhau" nhưng khi ở nơi riêng chỉ có họ thì lại cắn tay mà gầm gừ giận dữ khi thấy các ngươi đoàn kết và bao che lẫn nhau, giúp Islam phát triển còn họ thì đang bị rơi sâu vào nhục nhã. Hãy nói với chúng đi - hỡi Nabi -: Các ngươi hãy ôm hận và thịnh nộ trong lòng như thế vào nấm mộ đi, quả thật, Allah biết rõ trong tâm khảm có niềm tin Iman hay không, ngoan hiền hay không.');
INSERT INTO "vn_mokhtasar" VALUES(3,120,'3:120','Nếu như các ngươi - hỡi tín đồ Mu''min - được cơ hội chiến thắng kẻ thù hoặc được gia tăng tài sản và con cái, điều đó khiến họ thêm lo lắng và buồn bả; nhưng nếu các ngươi bị kẻ thù đánh bại hoặc bị mất mác về tài sản và con cái thì họ lấy đó vui mừng, hân hoan. Tuy nhiên, nếu các ngươi biết nhẫn nại tuân thủ theo sắc lệnh, tiền định của Ngài và biết lo sợ về cơn thịnh nộ của Ngài thì mọi hành động của họ không gây hại được các ngươi, quả thật, Allah luôn biết rõ mọi động tĩnh của họ và Ngài sẽ đáp trả họ một các nhục nhã.');
INSERT INTO "vn_mokhtasar" VALUES(3,121,'3:121','Hãy nhớ lại đi - hỡi Nabi - khi Ngươi rời khỏi Madinah lúc sáng sớm để chiến đấu với nhóm đa thần tại Uhud, Ngươi đã sắp xếp chiến thuật ai vào vị trí nấy và luôn ở tư thế sẵn sàng chiến đấu, Allah nghe rõ từng lời nói của các ngươi và tận tường mọi hành động của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,122,'3:122','Hãy nhớ lại - hỡi Nabi - về hai nhóm người Mu''min thuộc bộ tộc Salamah và bộ tộc Harithah khi họ trở nên hoang mang khi nhìn thấy nhóm Munaafiq (ngụy Islam) quay về, lúc đó họ được Allah phù hộ giử chân lại tại chiến trường và xua tan khỏi họ sự hoang mang trong lòng. Và là tín đồ Mu''min hãy nương tựa hết mình vào Allah trong mọi hoàn cảnh cuộc đời.');
INSERT INTO "vn_mokhtasar" VALUES(3,123,'3:123','Và quả thật, Allah đã phù hộ các ngươi chiến thắng được người đa thần ở trận chiến Badr, lúc đó các ngươi chỉ làm nhóm người yếu thế, số lượng ít ỏi và vũ trang thì thiếu thốn. Hãy biết kính sợ Allah mà tạ ơn Ngài về những thiên lộc đã ban cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,124,'3:124','Hãy nhớ lại - hỡi Nabi - khi Ngươi nói với nhóm tín đồ Mu''min lời động viên, khích lệ họ ở trận chiến Badr, sau khi họ nghe được thông tin quân viện trợ của phe đa thần đang đến: Há không đủ hổ trợ các ngươi khi Allah gởi xuống ba ngàn quân Thiên Thần cùng chiến đấu với các ngươi ư ?');
INSERT INTO "vn_mokhtasar" VALUES(3,125,'3:125','Chắc chắn, bấy nhiêu đó đủ hổ trợ cho các ngươi, ngoài ra các ngươi còn một tin vui khác về việc hổ trợ cho các ngươi từ Allah: Nếu các ngươi kiên nhẫn chiến đấu và biết kính sợ Allah thì cho dù kẻ thù có thêm quân viện trợ, lúc đó Thượng Đế các ngươi sẽ gởi thêm năm ngàn Thiên Thần cưỡi chiến mã hiển hiện ra công khai tiếp viện các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,126,'3:126','Qua việc cho đoàn quân Thiên Thần gián trần hổ trợ các ngươi quả là nguồn tin vui cho các ngươi nhằm kiên định con tim của các ngươi. Mặc dù vậy, đây không phải là những nguyên tố quyết định chiến thắng, bởi quyền ban cho chiến thắng thật sự trong tay Allah Đấng Oai Nghiêm mà không gì chiến thắng được Ngài, Đấng Sáng Suốt trong tiền định và qui định của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,127,'3:127','Qua chiến thắng ở trận Badr mà Allah ban cho các ngươi, Ngài muốn dùng nó tiêu diệt một nhóm người Kafir bằng cái chết và hạ nhục một nhóm khác để họ trở về nếm được cảm giác thất bại tồi tệ.');
INSERT INTO "vn_mokhtasar" VALUES(3,128,'3:128','Khi Thiên Sứ cầu xin Allah tiêu diệt các tên đầu sỏ đa thần sau khi bị thất bại tại Uhud, Allah phán bảo: Ngươi không có quyền hạn gì việc xử lý họ, mọi quyền hạn thuộc về Allah, Ngươi hãy kiên nhẫn đến khi Allah phân xử giửa các ngươi hoặc khiến họ biết sám hối mà gia nhập Islam hoặc họ sẽ tiếp tục sống trong vô đức tin đến chết, lúc đó họ là đám người sai quấy đáng bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(3,129,'3:129','Và tất cả mọi thứ trong các tầng trời và đất là của Allah, bởi Ngài đã tạo hóa và quản lý, Ngài tha thứ tội lỗi cho ai Ngài muốn trong đám nô lệ của Ngài bằng lòng thương xót của Ngài, song Ngài muốn trừng phạt ai Ngài muốn bằng lẽ công bằng. Allah hằng tha thứ cho ai biết sám hối trong đám nô lệ của Ngài, Ngài nhân từ với họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,130,'3:130','Hỡi những người đã tin tưởng Allah và tuân thủ theo Thiên Sứ của Ngài, hãy tránh xa mọi hình thức vay lãi để thu lại số tiền cao hơn số tiền mà các ngươi đã cho mượn ban đầu giống như hành động của nhóm người thời tiền Islam. Hãy kính sợ Allah mà tuân thủ theo lệnh của Ngài và tránh xa điều Ngài đã cấm, hi vọng các ngươi sẽ đạt được nhiều điều tốt đẹp ở trần gian và ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(3,131,'3:131','Các ngươi hãy tự dựng vách bảo vệ giữa các ngươi và Hỏa Ngục một hành phạt mà Allah dùng trừng phạt đám người vô đức tin, vách đó được dựng bằng các việc ngoan đạo và từ bỏ mọi điều Haram.');
INSERT INTO "vn_mokhtasar" VALUES(3,132,'3:132','Và hãy tuân theo Allah và Thiên Sứ của Ngài bằng cách làm đúng theo yêu cầu của Ngài và tránh xa điều Ngài đã cấm, hi vọng các ngươi sẽ được thương xót ở trần gian và ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(3,133,'3:133','Và các ngươi hãy thi đua và hơn thua nhau về việc hành đạo, mọi hình thức việc ngoan đạo đưa các ngươi đến gần Allah hơn để đạt được sự tha thứ vĩ đại từ Allah và được vào Thiên Đàng có khoảng rộng như các tầng trời và đất gọp lại đã được Allah chuẩn bị sẵn cho đám nô lệ biết kính sợ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,134,'3:134','Những người biết kính sợ Allah là những ai biết hi sinh tài sản vì chính nghĩa của Allah lúc hưng thịnh cũng như lúc khó khăn; biết kìm chế bản thân lúc giận khi họ có khả năng hành đáp trả; biết tha thứ cho người gây bất công với mình, bởi Allah luôn thương yêu nhóm người sùng đạo có được những đức tín tốt đẹp này.');
INSERT INTO "vn_mokhtasar" VALUES(3,135,'3:135','Và mỗi khi họ vi phạm đại tội hoặc vi phạm những tiểu tội khác liền nhớ đến Allah, liên tưởng đến các hành phạt dành cho đám người bất tuân và những hứa hẹn cho nhóm người biết kính sợ thì họ liền hối hận mà cầu xin Thượng Đế giấu che tội lỗi đó cho họ và lượng thứ bỏ qua cho họ, bởi ngoài Allah không ai dám tha thứ tội lỗi cho bất cứ ai và họ không bao giờ tái phạm lại tội lỗi đó, họ luôn ghi nhớ mình là người tội lỗi và Allah sẵn sàng tha thứ tất cả tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(3,136,'3:136','Với nhóm người có những đức tính tốt đẹp đó thì phần thưởng dành riêng cho họ là được Allah giấu che cho tội lỗi, xí xóa cho họ, ngoài ra ở Đời Sau họ còn được ban thưởng cho Thiên Đàng có những dòng sông chảy dưới những tòa lâu đài, họ mãi mãi trong nó, quả là diễm phúc thay cho những ai đã phục tùng Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,137,'3:137','Và ngay khi Allah thử thách tín đồ Mu''min bằng những tai nạn họ gặp phải ở Uhud thì Ngài an ủi họ: Đó đâu cũng là qui luật trong tạo hóa về việc tiêu diệt đám người vô đức tin và kết quả vẫn thuộc về nhóm người Mu''min sau khi họ trải qua gian truân. Thế nên, các ngươi hãy đi khắp thế gian mà xem hậu quả của đám người phủ nhận Allah và các Thiên Sứ của Ngài, nhà của họ đã trở nên hoang tàn và vương quyền của họ đã tiêu tan.');
INSERT INTO "vn_mokhtasar" VALUES(3,138,'3:138','Đây là Thiên Kinh Qur''an chỉ định rõ ràng đâu là chân lý, đâu là giả tạo cho toàn thể nhân loại biết. Nó hướng con người đến với Chính Đạo và là bức chắn dành cho nhóm người biết kính sợ, bởi chỉ có họ mới nhận thức được chỉ đạo có trong Nó.');
INSERT INTO "vn_mokhtasar" VALUES(3,139,'3:139','Các ngươi chớ yếu đuối và buồn bã - hỡi tín đồ Mu''min - về những tai nạn đi qua các ngươi ở Uhud, đó là điều không nên bởi các ngươi luôn cao cơ hơn họ bởi có niềm tin Iman, cao cơ hơn vì được Allah phù hộ và hi vọng các ngươi được chiến thắng, nếu các ngươi là nhóm người Mu''min hết lòng tin tưởng Allah về những gì đã hứa hẹn cho nhóm người biết kính sợ.');
INSERT INTO "vn_mokhtasar" VALUES(3,140,'3:140','Nếu các ngươi - hỡi tín đồ Mu''min - bị phải hoạn nạn chết chóc, thương tích ở Uhud thì nhóm ngoại đạo cũng bị thương tích và chết chóc như các ngươi, đó là ngày tháng mà Allah hoán chuyển giữ loài người gồm Mu''min và nhóm Kafir như thế nào tùy Ngài, điều đó thể hiện tính sáng suốt của Allah, nhân đó Ngài loại bỏ nhóm Munafiq rác rưởi khỏi nhóm người Mu''min; tạo cơ hội cho tín đồ Mu''min hi sinh Shahid vì chính nghĩa của Allah. Allah không hề thương yêu đám người sai quấy đã bỏ lỡ cơ hội Jihad vì Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,141,'3:141','Trong những giá trị quí giá là tẩy xóa tội lỗi cho tín đồ Mu''min, xóa sạch đám Munafiq khỏi hàng ngũ và tiêu diệt nhóm người Kafir, đập tan họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,142,'3:142','Phải chăng các ngươi - hỡi tín đồ Mu''min - tưởng là các ngươi được suông sẽ vào Thiên Đàng không bị thử thách và chịu đựng ư ? Trong khi điều đó xảy ra với nhóm người Jihad vì chính nghĩa của Allah và họ luôn kiên nhẫn đối diện với bao thử thách.');
INSERT INTO "vn_mokhtasar" VALUES(3,143,'3:143','Há không phải các ngươi - tín đồ Mu''min - từng ước ao đối mặt với người Kafir để có được chứng nhận Shahid vì chính nghĩa của Allah giống như anh em đồng đạo của các ngươi đã đạt được trong trận Badr trước đây ư, họ đã đối mặt với biết bao là hiểm họa trực chờ. Thế mà khi các ngươi tận mắt chứng kiến thấy được khát vọng đó tại Uhud nhưng các ngươi chỉ biết trố mà nhìn thôi.');
INSERT INTO "vn_mokhtasar" VALUES(3,144,'3:144','Và Muhammad vốn chỉ là một Thiên Sứ giống như bao Thiên Sứ của Allah trước đây đã chết hoặc đã bị giết, phải chăng Người chết hoặc bị giết thì các ngươi liền phản đạo và từ bỏ việc Jihad ư? Cho dù ai đó có phản đạo thì chẳng gây hại gì cho Allah cả, bởi Ngài là Đấng Hùng Mạnh, Oai Nghiêm, chỉ có kẻ phản đạo mới bị thất bại thảm hại ở trần gian và ở Đời Sau đó thôi. Rồi Allah sẽ đền bù trọng hậu cho nhóm người biết tạ ơn bằng một phần thưởng tốt đẹp nhất đó là khiến họ đứng vững trên tôn giáo Islam và anh dũng Jihad vì chính nghĩa của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,145,'3:145','Và không một ai có thể tự kết liễu cuộc đời mình ngoại trừ đã được Allah tiền định và sau khi sống hết tuổi thọ mà Allah đã sắp xếp, không thể sống nhiều hơn hoặc ít hơn tuổi thọ đã qui định. Ai khát khao cuộc sống trần gian này thì y chỉ được ban đầy đủ những gì đã tiền định cho y và ở Đời Sau y không có bất cứ gì và ai muốn được ân phước của Allah ở Đời Sau thì y sẽ được ân phước đó và Ta sẽ ban thưởng cho những ai biết tạ ơn Thượng Đế họ một phần thưởng vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(3,146,'3:146','Và đã có biết bao Nabi trong số Nabi của Allah đã được rất đông tín đồ cùng sát cánh chiến đấu với Họ, việc họ bị chết chóc và thương tích vì chính nghĩa của Allah không làm họ nhác gan, ngược lại họ càng mạnh mẽ hơn, kiên nhẫn hơn, quyết liệt hơn, quả thật Allah rất thương yêu những ai biết kiên nhẫn trước khó khăn, trước nguy hiểm vì chính nghĩa của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,147,'3:147','Và lời cửa miệng mà nhóm người nhẫn nại này thốt ra khi họ gặp phải gian khó: Lạy Thượng Đế của bầy tôi, xin tha thứ tội lỗi cho bầy tôi, cho việc bầy tôi lỡ vi phạm giới nghiêm, xin kiên định đôi chân của bầy tôi khi đối diện với kẻ thù và xin phù hộ bầy tôi chiến thắng nhóm người đã phủ nhận Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,148,'3:148','Thế là họ được Allah ban cho phần thưởng ở trần gian là anh dũng chiến thắng kẻ thù và ban tiếp cho họ phần thưởng tốt đẹp ở Đời Sau là hài lòng về họ, cho họ hưởng thụ mãi mãi nơi Thiên Đàng vĩnh cữu, quả thật Allah luôn thương yêu nhóm người sùng đạo hoàn thành tốt bổn phận hành đạo và cách cư xử tốt.');
INSERT INTO "vn_mokhtasar" VALUES(3,149,'3:149','Hỡi những ai đã tin tưởng Allah và tuân theo Thiên Sứ của Ngài, nếu các ngươi nghe theo lời nói của nhóm Kafir thuộc Do Thái, Thiên Chúa và đa thần thì họ chỉ lôi kéo các người rơi vào lầm lạc, kéo các người rời khỏi niềm tin Iman để trở về hiện trạng Kafir trước đây của các ngươi, rồi các ngươi sẽ trở về với sự thất bại thảm hại ở trần gian và ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(3,150,'3:150','Đám người ngoại đạo đó không hề giúp đỡ các ngươi cho dù các ngươi có nghe lời họ, duy chỉ Allah mới là Đấng phù hộ các ngươi chiến thắng kẻ thù, thế nên hãy tuân lệnh Ngài, Ngài là Đấng Hiển Vinh, Đấng hổ trợ mạnh mẽ nhất không cần đến bất cứ ai ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,151,'3:151','TA sẽ gieo nổi sợ khôn lường vào lòng của nhóm người vô đức tin khiến chúng không đứng vững trên sa trường chỉ vì họ đã phạm Shirk thờ phượng ai khác bằng dục vọng của họ thay vì Allah duy nhất, vậy mà họ vẫn còn ngoan cố mãi đến khi họ bị ném vào Hỏa Ngục ở Đời Sau, đúng là một nơi ở tồi tệ cho nhóm người sai quấy nơi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(3,152,'3:152','Và Allah đã tròn lời hứa giúp các ngươi chiến thắng kẻ thù tại Uhud khi các ngươi đã anh dũng tấn công mạnh mẽ vào kẻ thù dưới sự ưng thuận của Allah Tối Cao, đến khi các ngươi nhúc nhát và xem nhẹ mệnh lệnh Thiên Sứ đã sai bảo, rồi các ngươi tranh luận nhau là tiếp tục ở lại tại (núi Rumah) hay xuống cùng thu gom chiến lợi phẩm và cuối cùng các ngươi đã kháng lệnh của Thiên Sứ bảo dù tình thế có xảy ra thế nào thì vẫn cố thủ trên núi. Điều đó lại xảy ra ngay sau khi Allah nhìn thấy các ngươi rất muốn chiến thắng kẻ thù, trong các ngươi có người ham muốn chiến lợi phẩm của trần gian nên đã rời bỏ vị trí và nhóm khác đã khát khao phần thưởng ở Đời Sau nên họ đã cố thủ tại nơi mà Thiên Sứ đã ra lệnh họ. Thế là các người đã bị Allah lật úp tình thế khiến địch tấn công bất ngờ làm các ngươi không kịp trở tay, với điều này muốn thử thách các ngươi và để phân loại người Mu''min kiên trì với thử thách với những ai trượt dài thất bại và niềm tin Iman yếu ớt. Nhưng cuối cùng Allah đã tha thứ về việc các ngươi kháng lệnh Thiên Sứ của Ngài, bởi Allah có muôn vàn thiên ân dành cho nhóm Mu''min khi Ngài hướng dẫn họ đến với niềm tin Iman, tha thứ tội lỗi họ đã phạm và ban thưởng cho họ về những tai nạn đã gặp.');
INSERT INTO "vn_mokhtasar" VALUES(3,153,'3:153','Hãy nhớ lại đi - hỡi những người Mu''min - khi các ngươi bỏ chạy hoảng loạn trong ngày Uhud tại vì các ngươi nghịch lại mệnh lệnh của Thiên Sứ. Do chạy trối chết nên không ai nhìn ai cả trong khi Thiên Sứ đã réo gọi các ngươi từ phía sau và lúc đó nhóm đa thần cũng có mặt ở đó, Người hô to: Hãy đến bên Ta hỡi nô lệ của Allah, hãy đến bên Ta hỡi nô lệ của Allah. Và rồi Allah cũng bù đắp cho sự mất mác và đau lòng của các người đã vụt mất cơ hội chiến thắng và chiến lợi phẩm, tiếp theo sau là tin đồn Thiên Sứ bị giết. Thế là Allah thiên khải để trấn an để các ngươi khỏi đau lòng và buồn phiền sau thất bại thảm hại đó, song khẳng định với các ngươi là Thiên Sứ không hề bị sát hại nhưng Người đau đớn hơn so với những gì mà các ngươi đã bị. Allah biết rõ mọi điều các ngươi làm, không một điều gì giấu che được Ngài kể cả những suy nghĩ trong tim của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,154,'3:154','Sau đó Allah xoa dịu nổi đau và sự mất mác của các ngươi bằng sự thư giản và tin tưởng, Ngài khiến cho một nhóm trong các ngươi - kỳ vọng vào lời hứa của Allah - nên Ngài đã ru họ rơi vào giấc ngủ ngon lành và thư giản, bên cạnh đó Allah khiến nhóm khác mang tâm trạng hoang mang lo lắng đó là nhóm Munafiq chỉ muốn bình an cho riêng mình, từ đó họ nghĩ bậy về Allah, họ nghĩ là Allah sẽ không ban chiến thắng cho Thiên Sứ của Ngài và không phù hộ cho đám nô lệ của Ngài, quả một lối nghĩ giống hệt như nhóm người thời tiền Islam đã không đón đúng về Allah. Nhóm Munafiq lên tiếng trong sự ngu muội về Allah: "Chúng mình không có quyền hạn trong lần xuất binh này, nếu không là chúng mình đã không xuất binh rồi." Hãy trả lời họ - hỡi Nabi -: "Quả thật, mọi mệnh lệnh đều do Allah bảo, Ngài muốn xem xét và quyết định sao tùy Ngài và Ngài đã định là các ngươi phải xuất binh." Nhóm Munafiq đó đã giấu che trong lòng chúng sự nghi ngờ và ý định xấu, khi họ nói: "Nếu chúng tôi có quyền hành là chúng tôi đã không bị sát hại ở tại đây đâu." Hãy nói với họ - hỡi Nabi -: "Cho dù nhà cửa của các ngươi có ở xa khu chiến địa và sự chết chóc thì các ngươi cũng phải chết đúng theo cách và tại vị trí mà Allah đã tiền định trước cho các ngươi." Allah tiền định về điều đó muốn phơi bày nổi lòng thầm kín của các ngươi điều mà các ngươi đã suy nghĩ, để tách biệt niềm tin Iman và đạo đức giả, bởi Allah luôn biết rõ nỗi lòng thầm kín của đám nô lệ của Ngài, không điều gì giấu được Ngài cả.');
INSERT INTO "vn_mokhtasar" VALUES(3,155,'3:155','Quả thật, số người đã thất bại trong các ngươi - hỡi bằng hữu của Thiên Sứ Muhammad - trong ngày mà hai bên Muslim và đa thần giao chiến ác liệt tại Uhud, chẳng qua đã bị Shaytan xúi dục khiến họ nhất thời ham muốn mà kháng lệnh, nhưng Allah đã tha thứ không tính toán với họ xem như là sự rộng lượng và nhân từ của Ngài. Quả thật, Allah luôn nhân từ với ai biết sám hối và luôn nhẫn nại không vội vàng trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(3,156,'3:156','Hỡi những người đã tin tưởng Allah và tuân theo Thiên Sứ của Ngài, các ngươi đừng làm như nhóm người Kafir thuộc nhóm Munafiq đã trách móc vì người thân của họ phải mưu sinh rời khỏi nhà hoặc đã tham gia chiến địa và hi sinh: "Nếu họ ở cùng chúng tôi hoặc không rời khỏi nhà tham chiến là đã không chết và không bị giết như thế đâu." Allah khiến họ phải đau lòng như thế để con tim họ nặng trĩu nỗi niềm hối hận và đau khổ, chỉ Allah duy nhất ban cho sống và chết ra sao tùy Ngài, không gì cấm cản được định mệnh của Ngài cho dù các ngươi có ngồi tại chổ và cố tình trì hoãn việc ra đi. Allah luôn thấy rõ hành động của các ngươi, không điều gì qua mắt được Ngài và đựa vào đó Ngài sẽ ban thưởng cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,157,'3:157','Và nếu các ngươi có bị giết hoặc chết vì chính nghĩa của Allah - hỡi người Mu''min - là xem như các ngươi đã được tha thứ tội lỗi, thương xót các ngươi, đó là điều tốt đẹp nhất ở trần gian hơn cả mọi thứ được con người thu gom rồi đều phải tiêu tan.');
INSERT INTO "vn_mokhtasar" VALUES(3,158,'3:158','Và cho dù các ngươi có chết hoặc bị sát hại như thế nào thì các ngươi đều trở về trình diện Ngài và được Ngài thanh toán từng hành động của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(3,159,'3:159','Với bản tính nhân từ, thương người mà Allah đã phú cho Ngươi - hỡi Nabi - khiến Ngươi được lòng của Sahabah của Ngươi. Nếu Ngươi là người hà khắc trong lời nói, trong hành động và có con tim lạnh nhạt là mọi người đã bỏ chạy khỏi Người, thế nên Ngươi hãy xí xóa cho họ khi họ có hành động thiếu xót; hãy xin sự tha thứ cho họ; hãy bàn bạc với họ về việc công, đến khi Ngươi có quyết định sau khi đã bàn bạc thì hãy thi hành nó và phó thác cho Allah, bởi Allah rất yêu thích những ai biết phó thách cho Ngài, Ngài sẽ phù hộ và kiên định họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,160,'3:160','Nếu các ngươi được Allah phù hộ cho các ngươi thắng lợi thì không ai có thể lật úp các ngươi, cho dù cả thế giới có hợp sức chống đối, và khi Ngài bỏ mặc các ngươi thì chắc chắn không một thế lực nào có thể giúp các ngươi thay đổi tình thế. Sự chiến thắng nằm trong tay Allah duy nhất, thế nên tín đồ Mu''min hãy nương tựa vào Allah duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(3,161,'3:161','Không một vị Nabi nào dám tự ý chiếm đoạt chiến lợi phẩm nếu không được Allah cho phép, và ai dám chiếm đoạt chiến lợi phẩm thì sẽ bị công khai tội lỗi đó trong Ngày Tận Thế, y sẽ phải vác nó trước mặt toàn thể loài người. Rồi Allah sẽ ban thưởng đầy đủ không thiếu xót bất cứ gì đã từng làm, không chịu thêm dù làm tội và không bị mất đi dù là một ân phước.');
INSERT INTO "vn_mokhtasar" VALUES(3,162,'3:162','Đối với Allah không hề ngang hàng nhau với hai hạng người, một bên là luôn làm hài lòng Allah, một lòng tin tưởng nơi Ngài và ngoan đạo với một bên phủ nhận Ngài và luôn nghịch lại Ngài, rồi họ sẽ hứng chịu cơn thịnh nộ của Allah và tống cổ họ vào Hỏa Ngục một nơi ở tồi tệ.');
INSERT INTO "vn_mokhtasar" VALUES(3,163,'3:163','Đối với Allah địa vị họ không hề ngang nhau dù ở trần gian hay ở Đời Sau, Allah nhìn thấy rõ hành động của họ, không gì giấu được Ngài và Ngài sẽ ban thưởng phù hợp cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,164,'3:164','Quả thật, Allah đã ân sủng nhóm người Mu''min và cư xử tốt với họ khi Ngài đã chọn cho họ một Thiên Sứ xuất thân từ họ, Người xướng đọc cho họ nghe Qur''an, tẩy sạch họ khỏi mọi thờ phượng đa thần và bản tính xấu xa, Người dạy họ Qur''an và Sunnah trong khi trước đây họ là nhóm người rơi vào lầm lạc không biết đâu là Chân Lý, là ánh sáng.');
INSERT INTO "vn_mokhtasar" VALUES(3,165,'3:165','Hay là các ngươi - hỡi người Mu''min - cho là mình gặp phải tai nạn khủng khi thất bại ở Uhud và các ngươi thiệt hại nhiều về nhân mạng, trong khi ở Badr trước đây các ngươi đã giết được và bắt được số tù binh gấp đôi số lượng mà các ngươi mất mát, lúc đó các ngươi kiêu hãnh tuyên bố: "Làm sao chúng ta gặp nạn cho được khi chúng ta là người Mu''min và Thiên Sứ của Allah ở cùng chúng ta !" Hãy cho họ biết - hỡi Nabi -: Với những gì các ngươi đã bị là do các ngươi đã bất chấp mà kháng lệnh Thiên Sứ, quả thật Allah có khả năng trên tất cả, Ngài muốn phù hộ ai chiến thắng là tùy Ngài và muốn hạ nhục ai là quyền của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,166,'3:166','Tất cả rủi go chết chóc, thương tích, thất bại mà các ngươi đã trải qua ở Uhud khi các ngươi đối mặt với đa thần đều nằm trong ý muốn và tiền định của Allah, điều đó thể hiện tính sáng suốt tuyệt đối để phơi bày ai là người Mu''min chân chính.');
INSERT INTO "vn_mokhtasar" VALUES(3,167,'3:167','Và để nhóm Munafiq lộ bản tính thật khi có lời báo họ: Các ngươi hãy xuất binh chiến đấu vì chính nghĩa của Allah hoặc góp phần gia tăng thêm lực lượng của phe Muslim, họ bảo: Nếu chúng tôi biết xảy ra chiến tranh là chúng tôi đã sát cánh với các người rồi, chúng tôi nghĩ giữa các người và họ không thể xảy ra chiến tranh. Đúng thời điểm đó họ lộ diện bản tính phủ nhận đức tin hơn là một người tin tưởng, họ nói bằng chiếc lưỡi không xương hoàn toàn ngược lại suy nghĩ trong lòng họ, nhưng Allah biết rõ mọi thứ được giấu kín trong lòng họ và họ sẽ phải gánh hậu quả đó.');
INSERT INTO "vn_mokhtasar" VALUES(3,168,'3:168','Nhóm không tham chiến nói với nhóm bị nạn trong trận Uhud: Giá như các người nghe lời nói của chúng tôi không tham chiến là đâu bị sát hại thế này. Hãy nói cho họ biết - hỡi Nabi -: Các ngươi hãy bảo vệ bản thân mình khỏi cái chết khi nó đến túm lấy các ngươi nếu các ngươi nói thật, nếu họ nghe lời các ngươi là không bị sát hại và nguyên nhân các ngươi thoát chết là do các ngươi ở nhà không tham chiến vì chính nghĩa của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,169,'3:169','Và Ngươi - hỡi Nabi - đừng nghĩ những người đã hi sinh vì chính nghĩa của Allah rằng họ là những cái xác, không, họ đang sống bằng sự sống đặc biệt nơi Thượng Đế họ ở Thiên Đàng, họ được ban bố cho vô số thiên lộc để hưởng thụ, chỉ riêng Allah mới biết họ sung sướng thế nào.');
INSERT INTO "vn_mokhtasar" VALUES(3,170,'3:170','Họ đang tận hưởng và vui vẻ bất tận với những thiên ân mà Allah chiêu đãi họ, họ luôn ước mơ và chờ đợi những anh em đồng đạo còn đang sống ở trần gian, nếu họ hi sinh bằng Jihad là hưởng được ân phước như họ, không sợ hãi khi phải đối diện với cuộc sống Đời Sau và không buồn bã với những gì đã đánh mất ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(3,171,'3:171','Và họ mừng vui với phần thưởng vĩ đại mà Allah rộng lượng ban thưởng cho họ, ngoài ra Allah còn không bỏ sót những ân phước mà người Mu''min đã làm, không Ngài ban thưởng đầy đủ và còn ban thêm cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,172,'3:172','Đối với những ai đáp lại mệnh lệnh của Allah và Thiên Sứ của Ngài khi réo gọi họ xuất binh vì chính nghĩa của Allah, nhằm chặn đầu phe đa thần tại vành đai lửa của Madinah ngay sau khi vừa chịu thất bại tại Uhud, dù vậy vẫn không ngăn cản được ý chí xuất binh của họ để đáp lại lời gọi của Allah và Thiên Sứ của Ngài. Riêng những ai có việc hành đạo tốt đẹp, biết kính sợ Allah mà tuân thủ đúng theo mệnh lệnh của Ngài và tránh xa mọi thứ Ngài cấm sẽ được hưởng ân phước vĩ đại đó là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(3,173,'3:173','Một số người đa thần đã hăm dọa họ: Là Quraish với lãnh đạo là Abu Sufyan đang dẫn đại quân tấn công để tiêu diệt các ngươi, hãy coi cẩn trọng khi đối mặt với họ. Lời dù dọa chỉ khiến họ gia tăng thêm niềm tin vào lời hứa hẹn của Allah, họ đã anh dũng xuất binh đối mặt kẻ thù và họ nói: Chỉ riêng Allah đủ phù hộ chúng tôi, Ngài là Đấng Hồng Phúc đáng để chúng tôi phó thác cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,174,'3:174','Thế là họ hưởng được ân phước vĩ đại sau khi trở về từ vành đai lửa và địa vị của họ cũng được nâng lên, họ bình an không phải đối mặt với chém giết hay thương tích, họ đã tuân thủ đúng làm Allah hài lòng và tránh được việc bất tuân Ngài, Allah có muôn vàn thiên lộc dành ban thưởng cho đám người Mu''min.');
INSERT INTO "vn_mokhtasar" VALUES(3,175,'3:175','Chẳng qua Shaytan đang hù dọa các ngươi khi hắn phô bày thế lực giả tạo, không cần trốn tránh họ bởi họ thật sự không có năng lực đó, các ngươi hãy kính sợ Allah mà tuân thủ đúng mệnh lệnh của Ngài nếu các ngươi thật sự là người Mu''min tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(3,176,'3:176','Ngươi - hỡi Thiên Sứ - đừng rơi vào kế hoạch của chúng khi thấy chúng hổ trợ nhóm phủ nhận đức tin phản đạo thuộc nhóm Munafiq. Rằng họ không gây cho Allah bất cứ thiệt hại nào, chẳng qua họ chỉ tự hại thân mình vì đã tự xa rời niềm tin Iman và thuần phục Allah. Chẳng qua Allah muốn hạ nhục họ, không phù hộ họ để họ không hưởng được ân phước ở Đời Sau và họ phải đối diện với hành phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(3,177,'3:177','Quả thật, những kẻ thay đổi sự vô đức tin bằng niềm tin Iman không gây thiệt hại gì đến Allah, chẳng qua tự họ hại họ mà thôi và họ sẽ bị trừng phạt đau đớn ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(3,178,'3:178','Và đối với những kẻ phủ nhận Thượng Đế, chống đối lại bộ giáo lý của Ngài đừng có tưởng việc trì hoãn không trừng phạt họ và cho họ sống thọ để họ tiếp tục trên hiện trạng phủ nhận đức tin là điều tốt đối với bản thân họ, chẳng qua việc trì hoãn là để tội lỗi họ thêm nặng và gánh chịu cực hình lớn hơn.');
INSERT INTO "vn_mokhtasar" VALUES(3,179,'3:179','Không phải là điều sáng suốt nếu Allah không mặc cho các ngươi - tín đồ Mu''min - sống chung với nhóm người Munafiq, không tách biệt các ngươi khỏi họ thậm chí cho các ngươi nếm biết bao gian truân khó nhọc để nhận biết rõ ai là người Mu''min chính thống từ đám Munafiq nhơ nhuốc. Và không phải là sự sáng suốt nếu Allah cho các ngươi biết được điều huyền bí để tách biệt nhóm Mu''min và nhóm Munafiq, tuy nhiên Allah muốn cho ai biết tùy Ngài trong số Thiên Sứ của Ngài giống như Ngài đã cho Thiên Sứ Muhammad biết rõ đích danh từng tên Munafiq một, ngụ ý muốn xác định niềm tin Iman của các ngươi với Allah và Thiên Sứ của Ngài, nếu các ngươi là người Mu''min chính thống và biết kính sợ Allah mà tuân thủ theo mệnh lệnh của Ngài và tránh xa điều Ngài cấm thì các ngươi sẽ được phần hưởng vĩ đại nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,180,'3:180','Và những kẻ keo kiệt đã được Allah ban cho giàu có thì lại không chịu thực hiện bổn phận (xuất Zakah) chớ tưởng rằng điều đó tốt đẹp cho họ, không là điều xấu cho họ, bởi với tài sản mà họ keo kiệt sẽ bị treo trên cổ họ trong Ngày Tận Thế để trừng phạt họ, Allah là Đấng duy nhất quáng xuyến tất cả trời đất, Ngài sống mãi sau khi vạn vật đều tan biến, Ngài biết rõ chi tiết việc họ từng làm và sẽ thưởng phạt phù hợp cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,181,'3:181','Allah nghe rõ câu nói của nhóm Do Thái khi họ nói: "Chắc chắn Allah nghèo lắm vì Ngài đã hỏi nợ chúng tôi còn chúng tôi giàu có bởi khối tài sản." TA sẽ viết lại hết mọi lời khoác lác và xỉ nhục mà họ đã thốt ra, và việc họ vô cớ giết chết những vị Nabi của Ngài. TA nói với họ rằng, các ngươi hãy nếm đi hành phạt bị thiêu đốt trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(3,182,'3:182','Đó là hành phạt do những gì mà đôi tay của các ngươi đã tạo ra - hỡi Do Thái - từ tội lỗi và sự ngông cuồng của các ngươi, bởi Allah không hề bất công với bất cứ ai trong đám nô lệ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,183,'3:183','Và họ đã vu khống và nói dối rằng, Allah đã di huấn bảo chúng tôi trong Kinh Sách của Ngài, bằng chiếc lưỡi của giới Nabi của Ngài là chớ tin tưởng vào Thiên Sứ mãi khi nào Người mang đến điều gì đó chứng minh lời nói của mình và minh chứng phải là cuộc dâng hiến cho Allah và được lửa từ trời bay xuống thiêu đốt, họ đã bịa đặt về lời di huấn sàm bậy đó và việc họ qui định bằng chứng xác minh sứ mạng của Thiên Sứ. Vì vậy, Allah đã ra lệnh Thiên Sứ Muhammad đáp lại họ: Quả thật, trước đây Allah đã gởi đến các ngươi nhiều Thiên Sứ với những bằng chứng rõ ràng khẳng định sứ mạng của họ, gồm cả hình thức Allah chấp nhận việc Qurban là phóng lửa từ trời xuống thiêu đốt vật đó, thế tại sao các ngươi còn giết chết những Thiên Sứ đó, nếu các ngươi là những người nói thật?');
INSERT INTO "vn_mokhtasar" VALUES(3,184,'3:184','Nếu như họ đã phủ nhận Ngươi - hỡi Nabi - thì cũng đừng buồn, đó là đường lối của người ngoại đạo từ ngàn xưa đã phủ nhận sứ mạng của rất nhiều Thiên Sứ trước Ngươi, trong khi họ đã trình bày rất nhiều bằng chứng cụ thể, có cả những Kinh Sách hàm chứa những lời khuyến cáo, những lời khuyến khích, lời chỉ đạo, giáo lý và một bộ luật.');
INSERT INTO "vn_mokhtasar" VALUES(3,185,'3:185','Tất cả mỗi người dù có như thế nào cũng đều phải nếm cái chết, thế nên con người đừng bị trần gian lôi cuốn trong khi ở Đời Sau sẽ được ban thưởng đầy đủ mọi thứ mà con người đã làm, không tí thiếu xót. Cho nên, ai được Allah kéo xa Hỏa Ngục và thu nhận vào Thiên Đàng là y đã hưởng được điều tốt đẹp khao khát bấy lâu, đã bình an khỏi mọi thứ xấu xa, và cuộc sống trần gian này vốn chỉ là sự sống tạm, ai đặt nhiều hi vọng vào nó sẽ phải thất vọng.');
INSERT INTO "vn_mokhtasar" VALUES(3,186,'3:186','Chắc chắn các ngươi - hỡi tín đồ Mu''min - sẽ bị thử thách về tài sản như bắt buộc thực hiện bổn phận và những thất thoát khác liên quan đến tài sản, sẽ bị thử thách về sinh mạng của các ngươi như hoàn thành nghĩa vụ với tôn giáo và đối diện với bao khó khăn khác; các ngươi sẽ nghe những lời lẽ xúc phạm từ nhóm người được ban Kinh Sách trước các ngươi và từ nhóm người đa thần rất nhiều điều xấu về các ngươi, về tôn giáo của các ngươi. Tuy nhiên, nếu các ngươi kiên nhẫn trước bao thử thách và giông tố cuộc đời, biết kính sợ Allah mà hoàn thành bổn phận với Allah và tránh xa điều Ngài cấm nhưng cần ở các ngươi một nghị lực chịu đựng và sự tranh đua quyết liệt.');
INSERT INTO "vn_mokhtasar" VALUES(3,187,'3:187','Và hãy nhớ lại - hỡi Nabi - khi Allah đã giao ước, sự khẳng định với ''Ulama thị dân Kinh Sách thuộc Do Thái và Thiên Chúa là các ngươi phải phơi bày tất cả cho thiên hạ về chỉ đạo trong Kinh Sách của Allah, về những bằng chứng khẳng định sứ mạng của Muhammad, không được giấu giếm bất cứ gì. Thế mà tất cả họ đều bội ước, bất cần đến điều đã giao ước, họ cố giấu che chân lý và công khai điều bịa đặt, họ đã đánh đổi lời giao ước của Allah bằng vật chất rẻ mạc ở trần gian như danh lợi, công danh, địa vị, tiền bạc và họ đã đạt được mục đích. Thật là khốn khổ cho những ích lợi mà họ đã đánh đổi bằng lời giao ước của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(3,188,'3:188','Ngươi - hỡi Nabi - đừng tưởng những kẻ đang hân hoan với hành động tội lỗi của mình, họ yêu thích được tân bóc trong khi họ không làm bất cứ gì tốt rằng họ sẽ bình an thoát khỏi hành phạt. Không, điểm hẹn dành cho họ chính là Hỏa Ngục họ sẽ phải chịu hành phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(3,189,'3:189','Tất cả các tầng trời và đất là đặc quyền riêng của Allah từ việc tạo hóa đến quản lý, Allah có khả năng tuyệt đối vượt bậc tất cả.');
INSERT INTO "vn_mokhtasar" VALUES(3,190,'3:190','Quả thật, việc tạo hóa các tầng trời và đất từ không là gì trước đó, việc giao thoa của ban đêm và ban ngày và việc trên lệch thời gian nhiều hơn, ngắn hơn của chúng là bằng chứng rõ ràng dành cho đám người có khối óc bình thường nhận biết là chỉ có Đấng tạo hóa vũ trụ mới xứng được thờ phượng riêng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(3,191,'3:191','Họ là những người duy trì lời tụng niệm Allah trong mọi hoàn cảnh trong ngày, lúc đang đứng, lúc đang ngồi, lúc đang nằm nghĩ ngơi, họ suy nghĩ về sự vĩ đại của tạo hóa các tầng trời và đất khiến họ thốt rằng, lạy Thượng Đế của bầy tôi, công trình tạo hóa vĩ đại này của Ngài không phải để giải trí, xin bảo vệ bầy tôi bình an khỏi Hỏa Ngục bằng cách phù hộ bầy tôi thành nhóm người ngoan đạo và bảo vệ bầy tôi tránh xa tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(3,192,'3:192','Quả thật, Ngài - hỡi Thượng Đế của bầy tôi - khi tống cổ ai vào Hỏa Ngục trong đám tạo hóa của Ngài là xem như Ngài đã xỉ nhục họ, đã công khai điều xấu của họ. Và không một kẻ sai quấy nào trong Ngày Tận Thế được ai đó dám đứng ra cứu họ thoát khỏi sự trừng phạt của Allah dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,193,'3:193','Lạy Thượng Đế của bầy tôi, bầy tôi đã nghe được lời người réo gọi đến niềm tin Iman - người ấy là Nabi Muhammad của Ngài - Người gọi: Các ngươi hãy tin tưởng Allah, Thượng Đế của các ngươi Đấng duy nhất thì bầy tôi liền tin tưởng lời Người đã gọi, bầy tôi tuân thủ theo giáo lý của Người, xin hãy giấu che tội lỗi của bầy tôi đừng công khai nó, xin hãy xí xóa sai xót của bầy tôi đừng bắt tội bầy tôi về điều đó, xin cho bầy tôi được chết cùng nhóm người ngoan đạo bằng cách khiến bầy tôi luôn làm hành đạo tốt và từ bỏ được điều xấu xa.');
INSERT INTO "vn_mokhtasar" VALUES(3,194,'3:194','Lạy Thượng Đế của bầy tôi, xin ban cho bầy tôi mọi thứ mà Ngài đã hứa qua chiếc lưỡi của các Thiên Sứ của Ngài, gồm sự chỉ đạo và sự chiến thắng ở trần gian, xin đừng hạ nhục bầy tôi trong Ngày Tận Thế bằng cách đẩy vào Hỏa Ngục, quả thật Ngài - hỡi Thượng Đế của bầy tôi - rất rộng lượng không bao giờ thất hứa.');
INSERT INTO "vn_mokhtasar" VALUES(3,195,'3:195','Lời cầu xin của họ được Thượng Đế đáp lại rằng, TA sẽ không làm mất ân phước về bất cứ điều gì các ngươi đã từng làm dù ít hay nhiều, không phân biệt nam hay nữ, tất cả được xem ngang hàng nhau để ban thưởng, không ban thêm cho nam giới và cũng không cắt xén phần của nữ giới. Đối với những ai di cư vì chính nghĩa của Allah; những ai bị nhóm Kafir trục xuất khỏi quê hương; những ai bị thiệt hại vì tuân lệnh Thượng Đế, những ai Jihad vì chính nghĩa của Allah; những ai hi sinh Shahid vì muốn vươn cao câu Shahadah, tất cả họ sẽ được Allah tha thứ tội lỗi trong Ngày Tận Thế, xí xóa những gì họ đã phạm và thu nhận họ vào Thiên Đàng có những dòng sông chảy dưới những tòa lâu đài, một phần thưởng từ Allah, bởi Allah luôn có phần thưởng dành riêng cho nhóm người ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(3,196,'3:196','Ngươi - hỡi Nabi - chớ bị nhóm người Kafir lừa gạt khi chúng thường xuyên du ngoạn đây đó, cách họ hưởng thụ cuộc sống, họ mua bán thuận lợi và có thu nhập ổn định khiến Ngươi buồn bã và hoang mang.');
INSERT INTO "vn_mokhtasar" VALUES(3,197,'3:197','Trần gian này vốn chỉ là sự hưởng thụ cỏn con chống tàn, sau đó hướng đi của họ trở về Ngày Sau sẽ là Hỏa Ngục, một chốn ở tồi tệ dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(3,198,'3:198','Nhưng đối với ai biết kính sợ Thượng Đế, y luôn tuân thủ theo mệnh lệnh của Ngài và tránh xa mọi điều Ngài cấm thì họ sẽ được ở nơi Thiên Đàng có những con sông chảy bên dưới những tòa lâu đài, họ muôn đời trong đó, một phần thưởng mà Allah dành ban thưởng cho họ, và Allah chỉ dùng những thứ thượng hạng nhất để ban thưởng cho nhóm người ngoan đạo tốt hơn nhiều so với sự hưởng thụ của người Kafir ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(3,199,'3:199','Thị dân Kinh Sách không hề giống nhau, trong họ có nhóm người hết lòng tin tưởng Allah, tin vào chân lý và chỉ đạo đã thiên khải cho các ngươi và họ tin hết những gì đã thiên khải cho họ trong Kinh Sách của họ, họ không phân biệt bất cứ Thiên Sứ nào của Allah, họ khiêm tốn hạ mình trước Allah, họ hi vọng mọi thứ nơi Allah, họ không bán rẻ lời phán của Allah bằng ích lợi cỏn con của trần gian, với nhứng ai được miêu tả như thế sẽ được phần thưởng vĩ đại nơi Thượng Đế họ, quả thật, Allah rất nhanh chóng tính sổ việc làm của họ và thưởng phạt rất phù hợp.');
INSERT INTO "vn_mokhtasar" VALUES(3,200,'3:200','Hỡi những người đã tin tưởng Allah và tuân theo lệnh của Thiên Sứ, hãy kiên nhẫn hoàn thành bổn phận với tôn giáo, với bao thử thách khó nhọc ở trần gian, hãy chiến thắng người Kafir bằng lòng nhẫn nại đừng để chúng có cơ hội nhẫn nại hơn các ngươi; hãy đứng lên mà Jihad vì chính nghĩa của Allah, hãy kính sợ Allah mà tuân thủ đúng giáo lý của Ngài đã bảo và cấm, hi vọng các ngươi sẽ được bình an khỏi Hỏa Ngục và được vào Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(4,1,'4:1','Hỡi con người, hãy kính sợ Thượng Đế của các ngươi, bởi Ngài chính là Đấng đã tạo hóa ra các người từ một cá thể duy nhất là ông tổ Adam, rồi từ Adam Ngài tại tiếp ra người mẹ cho các ngươi là bà tổ Hawwa. Với hai cá thể đó Ngài đã phân bố loài người có mặt khắp mọi nơi trên trái đất gồm cả nam và nữ. Các ngươi hãy kính sợ Allah, Đấng mà các ngươi đã lấy Ngài ra để áp đặt người khác bằng câu: Tôi xin thề với Allah là tôi muốn anh làm cái này. Các ngươi hãy tránh việc đoạt tuyệt tình họ hàng một sợi dây kết nối các ngươi. Quả thật, Allah luôn quan sát các ngươi, cho nên không một việc làm nào của các ngươi có thể qua mặt được Ngài, tất cả nằm trong tầm kiểm soát của Ngài và Ngài sẽ thưởng phạt đầy đủ cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,2,'4:2','Và các ngươi - hỡi những người giám hộ - hãy giao cho trẻ mồ côi (là những đứa trẻ bị mất cha trước khi trưởng thành) hết phần tài sản của chúng cho chúng sau khi chúng đã trưởng thành và khôn ngoan; các ngươi chớ lợi dụng đổi thứ Haram bằng thứ Halal, chớ hoán đổi tài sản quí giá của bọn trẻ bằng thứ tài sản tầm thường hồng chiếm làm của riêng và cũng đừng nhập chung tài sản của bọn trẻ vào tài sản của các ngươi, hành động đó bị Allah xem là tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(4,3,'4:3','Và nếu các ngươi lo sợ bản thân sẽ không công bằng khi các ngươi cưới những phụ nữ từng là trẻ mồ côi dưới sự quản lý của các ngươi làm vợ, sợ không nộp đủ tiền cưới bắt buộc hoặc sợ sẽ đối xử xấu thì các ngươi đừng cưới họ mà hãy cưới những phụ nữ tự do khác làm vợ, nếu muốn thì hãy cưới hai vợ hoặc ba vợ hoặc bốn vợ. Tuy nhiên, nếu sợ bản thân không công bằng giữa các bà vợ thì hãy cưới một người vợ thôi hoặc hưởng lạc cùng với những nữ nô lệ trong tay của cac ngươi, bởi không bắt buộc các ngươi cho họ phần quyền lợi như các bà vợ chính thức. Đó là những giáo lý qui định về vấn đề trẻ mồ côi, kết hôn với một phụ nữ hoặc hưởng lạc với nữ nô lệ, những điều này giúp các ngươi tránh xa sự bất công và bạo ngược.');
INSERT INTO "vn_mokhtasar" VALUES(4,4,'4:4','Và các ngươi hãy giao cho phụ nữ phần tiền cưới bắt buộc, tuy nhiên khi các nàng tự nguyện, hài lòng giảm bớt phần nào tiền cưới thì các ngươi hãy chấp nhận nó, chớ xem thường nó.');
INSERT INTO "vn_mokhtasar" VALUES(4,5,'4:5','Và các ngươi - hỡi các giám hộ - đừng giao tài sản cho những ai không biết sử dụng đúng cách, bởi tài sản là thứ mà Allah ban cho các ngươi dùng để cải thiện mối quan hệ và cuộc sống, bởi chúng không đủ sáng suốt để sử dụng tiền bạc đúng mục đích cũng như cất giử tài sản; các ngươi hãy lấy tiền đó mà cấp dưỡng ăn mặc cho chúng và nói năng đàng hoàng với chúng và hãy hứa hẹn với chúng khi nào chúng trưởng thành, biết sử dụng tiền cách khôn ngoan thì giao lại chúng tài sản của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,6,'4:6','Và các ngươi - hỡi các giám hộ - hãy thử thách trẻ mồ côi khi chúng đến tuổi trưởng thành bằng cách giao chúng một ít tiền trong số tài sản riêng của chúng để chúng tự sử dụng. Nếu thấy chúng sử dụng đúng mục đích và thể hiện cho các ngươi biết chúng đã khôn ngoan thì các ngươi hãy giao trả hết cho chúng tài sản riêng của chúng; các ngươi chớ lợi dụng mà tiêu xài tài sản của chúng vượt mức Allah cho phép trong trường hợp cần thiết, cũng chớ tranh thủ tiêu xài cho hết phần tài sản riêng của chúng sợ lớn lên chúng lấy lại. Đối với người giàu có thì bị cấm sử dụng tài sản của trẻ mồ côi, còn ai nghèo khó thì được phép tiêu xài phù hợp đúng mực. Khi các ngươi giao tài sản lại cho trẻ khi chúng đã trưởng thành và khôn ngoan thì hãy tìm nhân chứng xác nhận điều đó tránh mọi cãi vả về sau, tuy nhiên chỉ cần Allah là đủ xác nhận sự việc đó, Ngài sẽ thanh toán chi tiết về mọi hành động đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(4,7,'4:7','Nam giới được quyền quản lý tài sản mà cha mẹ và họ hàng như anh em và chú bác sau khi họ qua đời dù ít hay nhiều và phụ nữ cũng có quyền quản lý như nam giới. Một giáo lý trái ngược với qui tắc thời tiền Islam đã cấm phụ nữ và trẻ em quyền thừa kế, nhưng đây lại là quyền lợi được Allah qui định rõ ràng bắt buộc.');
INSERT INTO "vn_mokhtasar" VALUES(4,8,'4:8','Và khi những người không được quyền thừa kế gồm người trong họ hàng, trẻ mồ côi và người nghèo có mặt lúc các ngươi chia tài sản thì hãy chia trước cho họ - chỉ là khuyến khích - ít tài sản trước khi phân chia cho người thừa kế nhưng các ngươi phải hài lòng về phần chia đó, họ chỉ âm thầm có mặt không đòi hỏi các ngươi điều gì nên hãy nói năn đàng hoàng với họ.');
INSERT INTO "vn_mokhtasar" VALUES(4,9,'4:9','Để tránh tâm trạng lo lắng sợ hãi nếu các ngươi đột ngột qua đời để lại con thơ ấu nhỏ dạy, sợ chúng bị khổ thì các ngươi hãy kính sợ Allah mà quản lý tốt những ai dưới quyền của các ngươi như trẻ mồ côi, không bất công với họ, có thế mới hi vọng Allah sẽ phù hộ đám con của các ngươi sau khi các ngươi qua đời được giáo dục tốt. Và hãy vì quyền lời của con trẻ mà nói ra lời công bằng khi ai đó để lại di chúc gây thiệt hại đến người thừa kế và ngăn cản người viết di chúc hưởng phước sau khi qua đời.');
INSERT INTO "vn_mokhtasar" VALUES(4,10,'4:10','Quả thật, đối với kẻ chiếm đoạt tài sản của trẻ mồ côi, sử dụng tài sản đó một cách bất công hoang phí thì chẳng qua y đang tự nhét vào bụng mình những cục than của Hỏa Ngục và họ sẽ bị thiêu đốt trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(4,11,'4:11','Allah giao ước và ra lệnh các ngươi luật thừa kế dành cho con cái của các ngươi; tài sản thừa kế chia cho nam giới gấp đôi nữ giới; trường hợp người chết để lại toàn con gái, nếu từ hai người con gái trở lên thì được hưởng hai phần ba trong tổng tài sản để lại; trường hợp chỉ có một đứa con gái duy nhất thì được hưởng một nửa tài sản để lại; trường hợp người chết có một con trai hoặc một con gái thì cha và mẹ của y mỗi người được hưởng một phần sáu số tài sản để lại; trường hợp không con cái thừa kế mà chỉ có mỗi cha và mẹ thì người mẹ được hưởng một phần ba và số tài sản còn lại là của người cha; trường hợp người chết có anh chị em ruột hoặc anh chị em cùng mẹ từ hai người trở lên thì người mẹ được hưởng một phần sáu theo mức định lượng còn lại bấy nhiêu thì người cha hưởng theo mức không định lượng và không có phần dành cho anh em. Luật chia này chỉ thực hiện sau khi đã trả nợ cho người chết nếu có và thực hiện xong di chúc của người chết để lại liên quan đế tài sản nhưng không quá một phần ba trong số tài sản để lại. Allah đã qui định luật chia tài sản như thế bởi các ngươi không biết trước cha mẹ hay con cái ai mới là người thật sự tốt với các ngươi ở trần gian và ở Đời Sau. Có người đã giao hết tài sản cho một người thừa kế duy nhất do cứ tưởng người đó đáng được hưởng hoặc cho rằng ai đó trong số người thừa kế không đáng hưởng nên đã tự tước quyền thừa kế của y, đôi khi thực tế lại xảy ra ngược lại, chỉ có Allah duy nhất mới biết trước sự việc xảy ra như thế nào, không điều gì giấu giếm được Ngài. Với định lượng thừa kế đã qui định là qui luật bắt buộc tín đồ Muslim phải áp dụng, quả thật Allah luôn biết rõ điều nào thật sự mang lại lợi ích cho con người, Ngài rất sáng suốt trong qui định và quản lý.');
INSERT INTO "vn_mokhtasar" VALUES(4,12,'4:12','Và các ngươi - hỡi những người chồng - được thừa kế một nửa tài sản do vợ các ngươi để lại trong trường hợp các nàng không có con cái; nếu như các nàng có con cái dù con chung hay con riêng - dù trai hay gái - thì các ngươi chỉ được thừa kế một phần tư trong tổng số tài sản các nàng để lại, luật chia này chỉ có hiệu lực sau khi thực hiện xong di chúc và trả nợ xong cho các nàng. Và các bà vợ được thừa kế một phần tư số tài sản mà các ngươi - các ông chồng - để lại trong trường hợp các ngươi không có con cái dù con chung hay con riêng; nếu như các ngươi có con - dù trai hay gái - thì các nàng chỉ được thừa kế một phần tám trong tổng số tài sản các ngươi để lại và các nàng chỉ được thừa kế sau khi đã hoàn thành di chúc và trả nợ cho các ngươi xong. Trường hợp chết là nam hoặc là nữ, y không còn cha mẹ và cũng không có con cái mà chỉ có một anh em trai và một chị em gái cùng mẹ thì hai người anh chị em cùng mẹ đó được thừa kế mỗi người một phần sáu theo định lượng; trường hợp người chết có nhiều hơn một anh em trai và có nhiều hơn một chị em gái và tất cả đều cùng mẹ thì tất cả anh chị em đó cùng hưởng một phần ba theo định lượng, nam và nữ được hưởng ngang bằng nhau. Tuy nhiên chỉ nhận được phần thừa kế sau khi hoàn thành di chúc và trả nợ xong cho người chết với điều kiện lời di chúc không gây thiệt hại cho người thừa kế giống như di chúc bảo chi hơn một phần ba trong tổng tài sản để lại thì chỉ thực hiện tối đa một phần ba mà thôi, đây là qui luật mà Allah đã bắt buộc tín đồ Muslim áp dụng theo. Allah luôn biết rõ điều nào mang lại lợi ích tối đa cho đám nô lệ của Ngài và rất kiên trì không vội vàng trừng phạt kẻ phạm tội.');
INSERT INTO "vn_mokhtasar" VALUES(4,13,'4:13','Với những giáo lý đã trình bày qui định về trẻ mồ côi và các giáo lý khác là những giáo lý mà Allah muốn đám nô lệ của Ngài tuân theo. Những ai nghe lời Allah và Thiên Sứ của Ngài áp dụng đúng mọi sắc lệnh làm và dừng sẽ được Ngài thu nhận vào Thiên Đàng có những dòng sông chảy bên dưới những tòa lâu đài, họ sống mãi trong đó không hồi kết, đó là phần thưởng từ Thượng Đế xem như là sự chiến thắng vĩ đại mà không có chiến thắng nào so sánh cho bằng.');
INSERT INTO "vn_mokhtasar" VALUES(4,14,'4:14','Và ai dám kháng lệnh Allah và Thiên Sứ của Ngài bằng cách thay đổi giáo lý của Ngài và không áp dụng theo giáo luật đó hoặc nẩy sinh nghi ngờ và vượt giới hạn đã qui định thì y sẽ bị tống cổ vào Hỏa Ngục, và y phải chịu hành phạt nhục nhã.');
INSERT INTO "vn_mokhtasar" VALUES(4,15,'4:15','Đối với những phụ nữ đã kết hôn và chưa kết hôn có quan hệ tình dục bất chính, để kết tội các ngươi phải đưa ra bốn nhân chứng ngoan đạo là nam giới, nếu cả bốn nhân chứng đồng chứng nhận người phụ nữ đã vi phạm giới nghiêm thì hãy giam lõng các bà trong nhà cho đến chết, một hành phạt dành cho họ hoặc Allah sẽ trừng phạt các bà bằng hành phạt khác. Sau đó, Allah hạ lệnh trừng phạt người Zina là đánh một trăm roi và đuổi khỏi xứ một năm nếu là chưa kết hôn và tử hình bằng cách ném đá đến chết nếu là người đã kết hôn.');
INSERT INTO "vn_mokhtasar" VALUES(4,16,'4:16','Đối với nam giới đã có gia đình hoặc chưa có gia đình vi phạm Zina thì hãy trừng phạt hai loại người này bằng lời lẽ và hành động để hạ nhục họ. Khi nào họ từ bỏ hẳn tội lỗi mình phạm và có hành động tốt đẹp thì chấm dứt việc miệt thị họ, bởi người sám hối giống như người chưa từng phạm tội, quả thật Allah luôn chấp nhận sám hối đối với ai biết ăn năn sám hối, Ngài nhân từ với họ. Trước đây, hành phạt họ theo cách như trình bày ở trên sau đó được thay thế một bộ luật khác đó là đánh một trăm roi và đuổi khỏi xứ một năm nếu là chưa kết hôn và tử hình bằng cách ném đá đến chết nếu là người đã kết hôn.');
INSERT INTO "vn_mokhtasar" VALUES(4,17,'4:17','Allah chỉ chấp nhận sự sám hối của ai vi phạm tội lỗi do không biết hậu quả phải gánh cho tội lỗi đó - không phân biệt cố ý hay chỉ vô tình vi phạm - sau đó họ kịp thời sám hối với Allh trước khi chết, Nhóm người đó sẽ được Allah chấp nhận sự sám hối của họ, Ngài xí xóa lỗi lầm họ đã phạm, bởi Allah luôn tận tường mọi hoàn cảnh của tạo hóa, rất sáng suốt trong quản lý và qui định.');
INSERT INTO "vn_mokhtasar" VALUES(4,18,'4:18','Và Allah không chấp nhận sự sám hối của những ai mãi mê trong tội lỗi và không hề biết sám hối mãi đến khi rơi vào hiện trạng hấp hối của cái chết, lúc đó y mới thốt lên, giờ đây bề tôi hối hận về tội lỗi mình đã làm, lời sám hối lúc đó không được Allah chấp nhận bởi họ đã mãi mê trong hiện trạng vô đức tin, bất chấp tội lỗi và những ai chết là người không có đức tin, họ được TA tiếp đón bằng một cực hình đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(4,19,'4:19','Hỡi những người đã tin tưởng Allah và noi theo Thiên Sứ của Ngài, cấm các ngươi liệt phụ nữ của ông cha vào thể loại tài sản để thừa kế, rồi các ngươi lạm quyền tựa như các ngươi là chồng họ vậy muốn gã cho ai hoặc không muốn gã cho ai theo ý thích của các ngươi. Và cấm người chồng quyết giử vợ không chịu li hôn để hành hạ vợ do không còn tình cảm, để y rút lại phần nào tiền cưới mà y đã giao trước đây hoặc vì mục đích khác, ngoại trừ người vợ làm điều khả ố như ngoại tình và các ngươi có đầy đủ chứng cớ thì các ngươi được quyền giử các bà lại và yêu cầu trả lại tiền cưới mà các ngươi đã giao lúc cưới để được tự do, các ngươi hãy cư xử tử tế với vợ trong cuộc sống vợ chồng, đừng gây hại mà hãy đối xử thật tốt với vợ. Nếu như các ngươi ghét bỏ vợ về tật xấu nào đó thì hãy cố nhẫn nại hi vọng điều mà các ngươi ghét bỏ đó sẽ được Allah bù đắp cho thứ khá tốt đẹp hơn rất nhiều ở trần gian và cả ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(4,20,'4:20','Và nếu các ngươi - hỡi những người chồng - muốn li hôn vợ để cưới vợ khác thì các ngươi được quyền đó. Nhưng về đóng tài sản mà các ngươi đã giao các nàng làm sính lễ cưới hỏi trước đây thì cấm các ngươi lấy lại dù chỉ chút ít, nếu các ngươi ngoan cố lấy lại là xem như các ngươi là kẻ xảo quyệt và đã gây nên tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(4,21,'4:21','Và làm sao các ngươi lại nhẫn tâm lấy lại những gì mà các ngươi đã trao tặng cho các nàng lúc tình cảm giữa hai ngươi còn son sắc mặn nồng, lúc các ngươi ân ái thỏa mãn với các nàng, nếu các ngươi tham lam lấy lại số sinh lễ đó thì hành động của các ngươi bị xem là đồi bại đáng bị lên án, trong khi các ngươi đã có cuộc hôn ước trịnh trọng bằng đường lối của Allah và bằng giáo lý của Ngài để các ngươi hợp pháp gần gủi các nàng.');
INSERT INTO "vn_mokhtasar" VALUES(4,22,'4:22','Và cấm các ngươi cưới mẹ kế làm vợ ngoại trừ chuyện đã xảy ra trước Islam thì các ngươi không bị tính toán, bởi việc con cái mà cưới mẹ kế là hành động khả ố vô cùng, đó là nguyên nhân rơi vào cơn thịnh nộ của Allah và là một lối đi xấu xa.');
INSERT INTO "vn_mokhtasar" VALUES(4,23,'4:23','Allah cấm các ngươi cưới mẹ ruột và các bà nội, ngoại; cưới con gái ruột và các cháu nội, ngoại; cưới các chị em gái ruột hoặc chỉ cùng cha hoặc chỉ cùng mẹ; cưới các cô ruột là chị em của cha và các cô dì của cha; cưới các dì ruột là chị em gái của mẹ và các cô dì của mẹ; cưới các cháu gái ruột con của anh chị em và các cháu nội ngoại của chúng; cưới các người mẹ đã nuôi vú các ngươi; cưới các chị em gái bú cùng bầu sữa với các ngươi; cưới những người mẹ vợ cho dù các người có động phòng hay chưa động phòng với vợ; cưới những con gái riêng của vợ thường được các ngươi nuôi dưỡng sống chung và cho dù chúng không sống chung cũng vậy nếu các người đã động phòng với mẹ chúng, trường hợp các ngươi chưa động phòng với mẹ chúng thì các người không mắc tội khi muốn kết hôn vơi chúng; cấm các ngươi kết hôn với con dâu là vợ của con trai ruột của các ngươi cho dù con trai của các ngươi chưa kịp động phòng và vợ của những đứa con được vợ các ngươi nuôi vú cũng được xem là con dâu của các ngươi; và cấm các ngươi kết hôn cùng lúc với hai phụ nữ có quan hệ ruột thịt hoặc được nuôi cùng bầu sữa, ngoại trừ chuyện đó đã xảy ra thời tiền Islam thì sẽ được Allah xí xóa. Quả thật, Allah rất nhân từ với đám nô lệ của Ngài biết ăn năn sám hối, Ngài luôn thương xót họ. Ngoài ra, trong Sunnah cũng có những Hadith cấm kết hôn cùng lúc hai người phụ nữ là chị em ruột, là dì cháu ruột, là cô cháu ruột.');
INSERT INTO "vn_mokhtasar" VALUES(4,24,'4:24','Cấm các ngươi cưới những người phụ nữ đã lập gia đình (đang chung sống với chồng của họ) trừ những ai mà các ngươi sở hữu họ như tù binh do chiến đấu trên con đường chính nghĩa của Allah, các ngươi được phép ăn nằm với họ (cưới thành vợ) sau một chu kỳ kinh nguyệt. Đó là sắc lệnh mà Allah đã ban hành cho các ngươi. Ngoài những phụ nữ (cấm lấy làm vợ) được nêu (trong câu Kinh này và trong các câu Kinh trước) thì các ngươi được phép cưới bất kỳ người phụ nữ nào các ngươi muốn, các ngươi hãy cưới họ đàng hoàng và chớ quan hệ với họ theo cách Zina (ngoài hôn ước của Islam: thông dâm và gian dâm). Vì các ngươi hưởng lạc từ nơi họ qua việc chăn gối với họ nên các ngươi hãy trao tặng họ những món tiền cưới (Mahr) được Allah qui định là nghĩa vụ bắt buộc của các ngươi đối với họ; tuy nhiên, các ngươi sẽ không mang tội trong việc đưa tặng thêm phần Mahr hoặc giảm bớt nó nếu giữa các ngươi và họ đã có sự thỏa thuận. Quả thật, Allah là Đấng Hằng Biết, không có gì có thể che giấu được Ngài, Ngài là Đấng Anh Minh trong việc ban hành và phán quyết');
INSERT INTO "vn_mokhtasar" VALUES(4,25,'4:25','Hỡi những người đàn ông, nếu ai đó trong các ngươi không có khả năng cưới vợ từ những phụ nữ tự do vì ít tiền của thì các ngươi hãy cưới những người phụ nữ nô lệ có đức tin bởi vì các ngươi và họ đều ngang bằng nhau trong tôn giáo cũng như trong bản chất con người. Các ngươi hãy cưới họ dưới sự chấp thuận từ chủ nhân của họ, các ngươi hãy đưa cho họ tiền cưới (Mahr) theo đúng với lệ thường, đó là đối với phụ nữ nết na đức hạnh không phạm Zina một cách công khai hay thầm kín. Và khi nào các ngươi đã cưới họ rồi họ lại phạm điều Zina thì mức phạt đối với họ bằng một nửa mức phạt đối với phụ nữ tự do: Đánh roi, không ném đá. Qui định được phép cưới các nữ nô lệ có đức tin và tiết hạnh là dành cho những ai sợ bản thân mình rơi vào hành vi Zina trong khi không có đủ khả năng cưới những phụ nữ tự do và cũng không kiềm chế được ham muốn. Và Allah là Đấng hằng Tha Thứ cho những bề tôi quay đầu sám hối với Ngài, Ngài sẽ thương xót họ; và một trong những biểu hiện cho lòng nhân từ của Ngài đối với họ là Ngài cho phép họ cưới những nữ nô lệ khi mà họ không có khả năng cưới những phụ nữ tự do và lo sợ bản thân rơi vào tội Zina');
INSERT INTO "vn_mokhtasar" VALUES(4,26,'4:26','Allah muốn trình bày rõ cho các ngươi về giáo lý của Ngài cũng như những gì cải thiện các ngươi trên thế gian và cõi Đời Sau, Ngài muốn hướng dẫn các ngươi đến với đường lối của các vị Nabi trước các ngươi trong việc cho phép và ngăn cấm, Ngài muốn hướng dẫn các ngươi đến với các phẩm chất đạo đức cao quý của họ cũng như tiểu sử đáng ca ngợi của họ để các ngươi noi theo, Ngài muốn đưa các ngươi rời khỏi việc làm sai trái đến với sự tuân lệnh Ngài, và Allah hằng biết những gì có thể cải thiện tốt đẹp cho đám bầy tôi của Ngài và Ngài luôn Anh Minh và Sáng Suốt trong việc ban hành và quyết định các mệnh lệnh của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(4,27,'4:27','Và Allah muốn tha thứ cho các ngươi, Ngài muốn xí xóa những tội lỗi của các ngươi nhưng những kẻ đi theo dục vọng của bản thân cứ muốn rời xa khỏi con đường ngay chính càng ngày càng xa hơn.');
INSERT INTO "vn_mokhtasar" VALUES(4,28,'4:28','Allah muốn giảm nhẹ gánh nặng cho các ngươi trong những điều Ngài qui định, Ngài không bắt các ngươi gánh vác những gì quá khả năng của các ngươi bởi lẽ Ngài luôn biết con người được tạo ra vốn rất yếu đuối tức dễ sa ngã vào con đường tội lỗi (vì sự lôi kéo của dục vọng và Shaytan).');
INSERT INTO "vn_mokhtasar" VALUES(4,29,'4:29','Hỡi những người có đức tin nơi Allah và đi theo vị Thiên Sứ của Ngài, các ngươi không được phép chiếm đoạt tài sản của nhau bằng sự cướp bóc, trộm cắp, tham nhũng, v.v trừ phi các ngươi trao đổi mua bán với nhau bằng sự thỏa thuận giữa đôi bên. Và các ngươi không được giết hại lẫn nhau và cũng không được tự giết hại bản thân các ngươi, các ngươi không được phép đưa bản thân mình vào con đường hủy diệt, bởi quả thật, Allah luôn thương xót các ngươi; và một trong những biểu hiện lòng thương xót của Ngài đối với các ngươi là Ngài cấm xâm phạm đến tính mạng, tài sản và danh dự lẫn nhau giữa các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,30,'4:30','Ai làm những điều mà TA (Allah) đã ngăn cấm như xâm phạm tài sản của người khác hoặc cướp đi tính mạng của người khác một cách có chủ ý (mưu sát) hay không có chủ ý (ngộ sát) thì Allah sẽ đày y vào Hỏa Ngục vào Ngày Phục Sinh, y sẽ phải đối mặt với cái nóng thiêu đốt của Hỏa Ngục, y sẽ phải nếm lấy mùi vị đau đớn của sự trừng phạt trong đó, và việc trừng phạt đó đối với Allah vô cùng đơn giản và rất dễ dàng bởi Ngài Toàn Năng, Ngài không bất lực trước điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(4,31,'4:31','Hỡi những người có đức tin, nếu các ngươi tránh xa những hành vi mang đại trọng tội như Shirk với Allah, bất hiếu với cha mẹ, giết người, cho vay lấy lãi, v.v thì TA (Allah) sẽ xí xóa và bỏ qua những tội lỗi nhỏ cho các ngươi, rồi TA sẽ thu nhận các ngươi vào một nơi vinh dự ở nơi TA, đó là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(4,32,'4:32','Hỡi những người có đức tin, các ngươi chớ đừng ham muốn những thứ mà Allah đã ưu đãi người này vượt trội hơn người kia để rồi nó dẫn các ngươi rơi vào sự oán than và ghen tị; phụ nữ không nên ham muốn những gì mà Allah đã dùng ưu đãi riêng cho đàn ông bởi lẽ tất cả mỗi giới đều được hưởng phần ân huệ cho việc làm của họ. Các ngươi hãy cầu xin Allah ban thêm cho các ngươi ân huệ bởi vì Allah hằng biết mọi thứ và Ngài ban phát tương thích với việc làm của người bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(4,33,'4:33','Và tất cả mỗi người trong các ngươi đều được TA (Allah) qui định cụ thể quyền và mức lượng thừa hưởng tài sản mà cha mẹ và người thân để lại; và những ai mà các ngươi có giáo ước thề nguyện và giúp đỡ họ thì các ngươi hãy đưa cho họ phần của họ được qui định từ di sản, quả thật Allah làm chứng tất cả mọi sự việc, và trong đó Ngài làm chứng về đức tin Iman và sự giao ước này của các ngươi; và việc kế thừa bởi sự thề nguyện đã từng hiện hữu trong thời đầu của Islam nhưng đã bị xóa bỏ sau đó.');
INSERT INTO "vn_mokhtasar" VALUES(4,34,'4:34','Những người đàn ông (những người chồng) phải có nghĩa vụ chăm sóc phụ nữ (những người vợ), họ phải trông coi và quán xuyến các vụ việc của phụ nữ (tức họ phải có nghĩa vụ bảo vệ phụ nữ) bởi vì Allah đã ban cho họ sức lực vượt trội hơn phụ nữ và bởi vì họ có bổn phận và trách nhiệm phải cấp dưỡng cho phụ nữ; cho nên, những người phụ nữ ngoan đạo phải vâng lời Thượng Đế của họ và phải vâng lời những người chồng của họ, họ phải giữ gìn và bảo quản nhà cửa của chồng khi chồng đi vắng dưới sự trông coi của Allah. Và những người vợ nào mà các ngươi (hỡi những người chồng) sợ họ không vâng lời trong lời nói cũng như trong hành động (phạm những điều ngăn cấm trong Islam) thì trước tiên các ngươi hãy khuyên răn họ bằng những lời nhắc nhở họ sợ Allah, nếu như lời khuyên răn vô ích thì các ngươi hãy tạm không ăn nằm với họ (nằm quay lưng với họ và không quan hệ tình dục với họ), và nếu họ vẫn không nghe lời thì các ngươi được quyền đánh đòn họ nhưng không gây thương tích và tránh đánh vùng mặt; nhưng nếu họ đã chịu nghe lời thì các ngươi chớ tìm cớ gây sự với họ. Quả thật Allah là Đấng Tối Cao trên tất cả mọi thứ, Đấng Vĩ Đại bởi các thuộc tính và bản chất của Ngài, bởi thế các ngươi hãy sợ Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,35,'4:35','Nếu các ngươi - hỡi những người thân thích của cặp vợ chồng có mâu thuẫn với nhau - sợ vì mâu thuẫn hai vợ chồng họ có thể dẫn đến sự ly dị thì các ngươi hãy cử một người đàn ông công bằng, chính trực từ phía bên người chồng và một người đàn ông công bằng, chính trực từ phía bên người vợ để hai người đó cùng đứng ra hòa giải. Và nếu hai vợ chồng muốn hòa giả thì Allah sẽ phù hộ cho họ và điều đó tốt hơn cho họ vì Allah yêu thích sự hòa giải hơn, bởi quả thật không có điều gì có thể che giấu được Ngài và Ngài là Đấng Am Tường tất cả mọi sự việc trong lòng của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,36,'4:36','Các ngươi hãy thờ phượng riêng một mình Allah, chớ thờ phượng cùng với Ngài một ai (vật gì) khác ngoài Ngài, các ngươi hãy ăn ở tử tế (hiếu thảo, kính trọng, lễ phép và chu đáo trong việc chăm sóc) cha mẹ của các ngươi, các ngươi hãy đối xử tử tế với bàn con thân thích, với trẻ mồ côi và những người nghèo khó, các ngươi hãy cư xử tốt với người láng giềng gần xa của các ngươi, các ngươi hãy cư xử tốt với bạn bè gần gũi của các ngươi, các ngươi hãy cư xử tốt với người lỡ đường và hoạn nạn, và các ngươi hãy cư xử tốt với cả những ai nằm dưới quyền của các ngươi. Quả thật, Allah không yêu thích những kẻ luôn thấy mình cao hơn người, luôn tỏ thái độ kiêu căng trước người khác, và luôn tự hào về bản thân trong sự kiêu ngạo và khinh thường người khác.');
INSERT INTO "vn_mokhtasar" VALUES(4,37,'4:37','Allah không yêu thích những ai keo kiệt không chịu chi dùng thiên lộc được Ngài ban phát vào con đường chính nghĩa của Ngài, họ không những keo kiệt mà còn xúi giục người khác keo kiệt giống như họ bằng lời nói và hành động, và họ luôn che giấu thiên lộc và kiến thức mà Allah đã ban phát cho họ, họ không chịu trình bày điều chân lý cho mọi người, họ che giấu điều chân lý và công khai điều sai trái và sàm bậy. Đây là những đặc điểm và bản chất của những kẻ vô đức tin và những kẻ vô đức tin đã được Allah chuẩn bị một hình phạt nhục nhã dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(4,38,'4:38','Và TA (Allah) đã chuẩn bị sự trừng phạt như thế đối với những ai chi dùng tài sản của họ vào con đường chính nghĩa của Allah chỉ vì muốn người đời nhìn thấy để được ca ngợi và tán dương chứ thực sự không vì đức tin nơi Allah và nơi cõi Đời Sau. Và những ai lấy Shaytan làm bạn kết thân thì kẻ đó đã kết bạn với một người bạn xấu xa nhất.');
INSERT INTO "vn_mokhtasar" VALUES(4,39,'4:39','Những người này sẽ mất mát gì chăng nếu họ có đức tin nơi Allah và nơi Ngày Phán Xét một cách chân thật đồng thời chi dùng tài sản của họ cho con đường của Allah với lòng thành tâm vì muốn được ân phước ở nơi Ngài? Không, họ không bào giờ bị mất mát hay thiệt thòi mà họ sẽ được điều tốt đẹp. Và Allah luôn am tường tất cả mọi sự việc về họ, không có gì có thể giấu giếm được Ngài, và Ngài sẽ ban thưởng thích đáng cho từng việc làm.');
INSERT INTO "vn_mokhtasar" VALUES(4,40,'4:40','Quả thật, Allah là Đấng Công Bằng tuyệt đối, Ngài không hề bất công với đám bề tôi của Ngài bất cứ điều gì cho dù điều đó có nhỏ đến dường nào, Ngài không bao giờ cắt xén điều ân phúc nào cho dù chỉ bằng một con kiến nhỏ và Ngài không bao giờ tăng thêm tội cho việc làm xấu nào, và nếu việc tốt được thực hiện thì Ngài sẽ ban cho công đức và nhân nó lên đến nhiều lần theo phúc của Ngài và Ngài sẽ ban cho việc làm tốt đó một phần thưởng to lớn từ nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,41,'4:41','Thế sự việc sẽ như thế nào vào Ngày Phục Sinh khi TA (Allah) mang đến mỗi vị Nabi cho mỗi cộng đồng để làm nhân chứng về những việc làm mà họ đã làm và khi TA mang Ngươi - hỡi Thiên Sứ Muhammad đến để làm nhân chứng cho cộng đồng của Ngươi?!');
INSERT INTO "vn_mokhtasar" VALUES(4,42,'4:42','Trong Ngày vĩ đại đó, những người vô đức tin nơi Allah và bất tuân vị Thiên Sứ của Ngài sẽ ước ao mình vẫn là đất bụi được hòa lẫn với mặt đất. Tuy nhiên, sẽ không có bất cứ việc làm nào mà họ đã làm có thể che giấu được Allah bởi Allah sẽ khóa chặt chiếc lưỡi của họ lại vào Ngày Hôm Đó và chỉ cho phép các bộ phận khác của cơ thể nói chuyện và làm chứng cho các việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(4,43,'4:43','Hỡi những người có đức tin nơi Allah và đi theo vị Thiên Sứ của Ngài, các ngươi chớ dâng lễ nguyện Salah trong lúc các ngươi đang say rượu, đợi khi nào các ngươi hết say, tỉnh táo trở lại và hiểu được những gì mình nói thì các ngươi mới được phép dâng lễ nguyện Salah. Điều luật này là trước khi rượu bị cấm hoàn toàn. Các ngươi không được dâng lễ nguyện Salah trong lúc bản thân các ngươi đang trong tình trạng Junub (đã ăn nằm với vợ hoặc xuất tinh nhưng chưa tắm), các ngươi cũng không được vào Masjid trong tình trạng như thế trừ phi các ngươi chi đi ngang qua trong Masjid như một lối đi chứ không ngồi lại trong đó, điều cấm đoán đó đối với các ngươi cho tới khi các ngươi đã tắm rửa thanh sạch cơ thể. Nếu trường hợp các ngươi bị bệnh không thể dùng nước hoặc các ngươi đang là khách lữ hành hoặc các ngươi vừa đi vệ sinh (đại tiện, tiểu tiện) hoặc các ngươi vừa mới ăn nằm với vợ mà các ngươi không tìm thấy nước để tắm hay làm Wudu'' thì các ngươi hãy lấy đất bụi (cát) sạch làm Tayammum bằng cách lau mặt và tay của các ngươi. Quả thật, Allah là Đấng Nhân Từ, Ngài sẽ xí xóa cho sự thiếu sót của các ngươi và sẽ tha thứ cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,44,'4:44','Này hỡi Thiên Sứ Muhammad, Ngươi có lưu ý hay không về việc những người Do Thái, những người mà họ đã được Allah ban cho một phần kiến thức từ Kinh Tawrah (Cựu Ước), đã đổi lấy sự lầm lạc bằng sự hướng dẫn, họ luôn cố gắng tìm đủ mọi cách làm cho các ngươi - hỡi những người có đức tin - lầm lạc khỏi con đường Ngay Chính mà vị Thiên Sứ của Ngài đã mang đến?!');
INSERT INTO "vn_mokhtasar" VALUES(4,45,'4:45','Allah, Đấng Tối Cao biết rõ hơn các ngươi về kẻ thù của các ngươi - hỡi những người có đức tin. Bởi thế, Ngài sẽ cho các ngươi biết về họ và trình bày rõ cho các ngươi về sự thù hằn của họ đối với các ngươi. Và một mình Allah đã đủ là vị Bảo Hộ cho các ngươi khỏi những mũi tên của chúng, và một mình Allah thôi đã đủ giúp các ngươi ngăn cản âm mưu hãm hại của chúng đối với các ngươi, và một mình Allah thôi đã đủ giúp các ngươi giành thắng lợi trước chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,46,'4:46','Trong số những người Do Thái, có nhóm kẻ xấu thay đổi những lời phán mà Allah đã ban xuống, chúng bóp méo các lời phán của Allah, và khi Thiên Sứ của Allah ra lệnh cho chúng thì chúng nói: chúng tôi nghe lời của ngươi nhưng chúng tôi làm trái lệnh ngươi, chúng giễu cợt nói: hãy nghe những gì chúng tôi nói nhưng ngươi không nghe, chúng dùng từ Ra''ina để ngụ ý xúc phạm Người, chúng luốn muốn nhạo báng tôn giáo. Và phải chi chúng nói ''hãy đợi chúng tôi hiểu những gì ngươi nói'' để thay cho lời ''Ra''ina'' thì điều đó tốt hơn cho chúng bởi vì nó thể hiện sự lễ phép đối với vị Nabi của Ngài hơn; tuy nhiên, Allah nguyền rủa chúng và xua đuổi chúng khỏi lòng nhân từ của Ngài bởi sự vô đức tin của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,47,'4:47','Hỡi những người được ban cho Kinh Sách từ dân Do Thái và Thiên Chúa, các ngươi hãy tin nơi những điều mặc khải được ban xuống cho Muhammad dùng để xác nhận lại những điều được mặc khải trong Kinh Tawrah và Injil ở nơi các ngươi trước khi TA (Allah) làm cho những gương mặt của các ngươi quay ra sau lưng hoặc TA sẽ tống khứ các ngươi ra khỏi lòng nhân từ của TA giống như TA đã tống khứ những người bạn của Ngày Thứ Bảy (những người mà họ đã vi phạm săn bắt vào Ngày Thứ Bảy sau khi họ đã bị ngăn cấm), TA đã biến chúng thành loài khỉ; và quả thật mệnh lệnh của TA luôn được thi hành không có bất cứ quyền năng nào có thể cản trở.');
INSERT INTO "vn_mokhtasar" VALUES(4,48,'4:48','Quả thật, Allah không bao giờ tha thứ cho tội Shirk (tổ hợp các thần linh cùng với Allah) với Ngài bất cứ điều gì nhưng Ngài có thể sẽ tha thứ những tội lỗi khác ngoài tội Shirk và Kufr (vô đức tin) đối với người nào Ngài muốn bởi hồng phúc của riêng Ngài hoặc có thể Ngài sẽ trừng phạt những ai Ngài muốn theo mức độ tội lỗi của họ bằng sự công bằng ở nơi Ngài. Và ai Shirk với Allah thì quả thật người đó đã mang vào thân mình một đại trọng tội, y sẽ không được tha thứ nếu như y chết đi trong tình trạng đó.');
INSERT INTO "vn_mokhtasar" VALUES(4,49,'4:49','Này hỡi Thiên Sứ Muhammad, chẳng lẽ Ngươi không biết những kẻ đã tự khẳng định bản thân chúng là trong sạch và được ban phước qua các việc làm của chúng ư? Không, chúng không hề trong sạch và không được ban phước, chỉ một mình Allah mới là Đấng thanh sạch cho những ai Ngài muốn và sẽ ban phước cho những ai Ngài muốn bởi lẽ Ngài am tường mọi điều thầm kín trong những con tim, Ngài sẽ không cắt xén một điều gì từ ân phước của những việc làm tốt cho dù chỉ là một đường chẻ nhỏ từ hạt chà là.');
INSERT INTO "vn_mokhtasar" VALUES(4,50,'4:50','Này hỡi Thiên Sứ Muhammad, Ngươi hãy xem chúng đặt điều nói dối cho Allah như thế nào và việc làm đó của chúng đã đủ là một tội công khai trong sự lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(4,51,'4:51','Hỡi Thiên Sứ Muhammad, chẳng lẽ Ngươi không biết những người Do Thái đã rất tự hào cho việc chúng được Allah ban cho một phần của kiến thức nhưng chúng lại tin vào những thứ (bục tượng, Shaytan, con người và Jinn) mà chúng tự dựng lên làm thần linh ngoài Allah, và chúng cho rằng những thần linh ngụy tạo đó của chúng là những vị được hướng dẫn đúng đường hơn những tín đồ của Muhammad.');
INSERT INTO "vn_mokhtasar" VALUES(4,52,'4:52','Những kẻ đó, những kẻ mà chúng đã mang niềm tin lệch lạc và sai trái sẽ bị Allah tống khử ra khỏi lòng thương xót của Ngài và ai bị Allah tống khứ ra khỏi lòng thương xót của Ngài thì chắc chắn Ngươi (Muhammad) sẽ không bao giờ tìm thấy vị cứu tinh nào có thể giúp y được.');
INSERT INTO "vn_mokhtasar" VALUES(4,53,'4:53','Chúng không hề có bất kỳ vương quyên nào cả, và nếu như chúng có thì chắc chắn chúng sẽ không bao giờ đưa cho ai trong nhân loại dù chỉ là một thứ gì đó rất nhỏ bé đi chăng nữa, ngay cả chỉ bằng một đường chẻ trên hạt chà là.');
INSERT INTO "vn_mokhtasar" VALUES(4,54,'4:54','Chúng ganh tị với Thiên Sứ Muhammad và những tín đồ của Người về những gì mà Allah đã ban cho họ từ sứ mạng Nabi và đức tin Iman cũng như quyền ngự trị trên trái đất. Sao chúng lại ghen tị trong khi trước đây TA (Allah) đã ban cho hậu duệ của Ibrahim Kinh Sách và đã mặc khải cho họ những Kinh Sách khác và TA cũng đã ban cho họ một vương quốc rộng lớn?!');
INSERT INTO "vn_mokhtasar" VALUES(4,55,'4:55','Trong những người dân Kinh Sách (Do Thái, Thiên Chúa), có người tin nơi những gì được ban xuống cho Ibrahim và các vị Nabi khac từ con cháu của Người nhưng có người thì quay lưng với đức tin nơi họ, và tình trạng vô đức tin này của họ cũng được lặp lại với những gì được ban xuống cho Muhammad. Và Hỏa Ngục là nơi đủ để trừng phạt cho những ai vô đức tin trong bọn chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,56,'4:56','Quả thật, những ai vô đức tin nơi các lời mặc khải của TA (Allah) thì vào Ngày Phục Sinh TA sẽ đày chúng vào Hỏa Ngục, và mỗi khi da của chúng bị lửa thiêu cháy TA sẽ cho lớp da khác thay thế để chúng tiếp tục nếm lấy mùi đau đớn của sự trừng phạt; quả thật Allah là Đấng Toàn Năng trên tất cả mọi thứ, không có gì có thể cưỡng lại được sức mạnh và quyền năng của Ngài, và Ngài là Đấng Chí Minh trong phán quyết và điều hành các công việc của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,57,'4:57','Còn những ai có đức tin nơi Allah và đi theo các vị Thiên Sứ của Ngài đồng thời năng làm những việc làm ngoan đạo và thiện tốt thì vào Ngày Phục Sinh TA (Allah) sẽ thu nhận họ vào Thiên Đàng với những ngôi vườn xanh tươi bên các tòa lâu đài tráng lệ phía dưới có các dòng sông chảy, họ sẽ sống mãi mãi nơi đó; và nơi Thiên Đàng, họ sẽ được ban cho những người vợ (những người chồng) thanh khiết, và họ sẽ được cho nghỉ ngơi bên dưới những bóng râm mát dịu và trong lành.');
INSERT INTO "vn_mokhtasar" VALUES(4,58,'4:58','Quả thật, Allah ra lệnh bảo các ngươi phải giao hoàn tín vật cho chủ nhân của nó và Ngài ra lệnh bảo các ngươi phải phân xử công bằng và ngay chính, không được thiên vị và bất công; quả thật Allah đã ban cho các ngươi một đặc ân khi Ngài ban xuống điều răn dạy và hướng dẫn các ngươi trong mọi tình huống, quả thật Allah là Đấng Hằng Nghe những lời các ngươi nói và Hằng Thấy các hành động và việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,59,'4:59','Hỡi những người có đức tin nơi Allah và đi theo vị Thiên Sứ của Ngài, các ngươi hãy vâng lệnh Allah và vâng lệnh Thiên Sứ của Ngài bằng việc thực thi các mệnh lệnh mà Ngài và Thiên Sứ của Ngài ban hành và chỉ đạo, tránh xa những điều mà Ngài và Thiên Sứ của Ngài ngăn cấm; và các ngươi hãy vâng lệnh các nhà lãnh đạo trông coi các vụ việc của các ngươi về những điều không trái nghịch với giáo lý của Allah và Thiên Sứ của Ngài. Nếu các ngươi mâu thuẫn và tranh cãi với nhau về một điều gì đó thì các ngươi hãy đưa vấn đề trở về với Allah (Kinh Sách của Ngài tức Qur''an) và Thiên Sứ của Ngài (Sunnah: đường lối của Nabi Muhammad) nếu các ngươi thực sự có đức tin nơi Allah và cõi Đời Sau. Việc trở về với Qur''an và Sunnah của Nabi là cách giải trình tốt nhất cho sự bất đồng quan điểm và sẽ mang lại kết cục tốt đẹp cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,60,'4:60','Này hỡi Thiên Sứ Muhammad, chẳng lẽ Ngươi không nhận thấy sự mâu thuẫn của những tên đạo đức giả thuộc nhóm người Do Thái hay sao? Chúng giả dối rằng chúng đã có đức tin nơi những điều được ban xuống cho Ngươi cũng như những điều được ban xuống cho các vị Thiên Sứ thời trước Ngươi nhưng rồi chúng muốn được phân xử về những sự việc tranh chấp của chúng theo luật ngoài giáo luật của Allah do con người đặt ra trong khi chúng được lệnh phải phủ nhận tất cả các luật trừ giáo luật của Ngài. Quả thật, Shaytan đã dẫn chúng đi quá xa khỏi chân lý để chúng không thể được hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(4,61,'4:61','Và khi những kẻ đạo đức giả hay giả tạo đức tin được bảo rằng hãy đến với những giáo luật được Allah ban xuống trong Kinh Sách của Ngài và hãy đến với vị Thiên Sứ của Ngài để Người phân xử những vụ việc tranh chấp của các ngươi thì Ngươi - hỡi Thiên Sứ Muhammad - chúng sẽ quay lưng với Ngươi để đến với những ai khác ngoài Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,62,'4:62','Những kẻ giả tạo đức tin sẽ như thế nào khi chúng gặp phải những tai họa do kết quả của những tội lỗi mà chúng đã phạm rồi chúng đến gặp Ngươi - Thiên Sứ Muhammad - cáo lỗi với Ngươi, chúng thề thốt với Allah rằng việc chúng nhờ đến những ai khác ngoài Ngươi phân xử cho các vụ việc tranh chấp của chúng là chỉ nhằm muốn được điều lành và sự hòa giải giữa đôi bên?! Nhưng thật ra, chúng đều giả dối trong sự việc đó bởi lẽ điều tốt lành là ở việc phải phân xử theo giáo luật của Allah ban xuống cho đám bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,63,'4:63','Chúng - những tên đạo đức giả - là những kẻ mà Allah biết rõ tâm niệm giả tạo và thối nát của chúng. Bởi thế, Ngươi - hỡi Thiên Sứ Muhammad - hãy lánh xa chúng, tuy nhiên, Ngươi hãy trình bày rõ ràng cho chúng các giáo luật của Allah và nói với chúng những lời lẽ làm thấu tim gan của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,64,'4:64','Và TA (Allah) gửi các vị Thiên Sứ đến là chỉ nhằm để nhân loại tuân lệnh và vâng lời họ về những gì mà Ngài đã ra lệnh dưới ý chỉ và quyền năng của Ngài. Nếu chúng bất công với bản thân mình qua việc phạm điều tội lỗi rồi chúng đến tìm Ngươi - Thiên Sứ Muammad- bằng sự ăn năn cho những điều tội lỗi mà chúng đã phạm để nhờ Ngươi cầu xin Allah tha thứ cho chúng và Ngươi đã cầu xin cho chúng thì chắc chắc chúng sẽ thấy Allah là Đấng Nhân Từ và Khoan Dung với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,65,'4:65','Sự việc không như những gì mà những kẻ giả tạo đức tin này khẳng định. Allah thề bởi chính bản thân Ngài rằng chúng không hề thực sự có đức tin cho tới khi chúng nhờ Ngươi - Muhammad - phân xử các vụ việc tranh chấp của chúng lúc Ngươi còn sống hoặc lấy giáo luật của Người để phân xử sau khi Ngươi qua đời rồi trong bụng dạ của chúng không thấy uất ức và cũng không nghi ngờ về sự phân xử cũng như giáo luật của Ngươi, và chúng hoàn toàn quy phục với những phán quyết của Ngươi một cách công khai và thầm kín.');
INSERT INTO "vn_mokhtasar" VALUES(4,66,'4:66','Và nếu TA (Allah) sắc lệnh cho chúng người này giết người kia hoặc rời bỏ nhà cửa của chúng thì chúng sẽ không chấp hành mệnh lệnh của Ngài trừ một số ít. Bởi thế chúng hãy tạ ơn Allah vì Ngài đã không gây khó khăn cho chúng. Nhưng nếu chúng thực hiện theo lệnh của Allah thì tốt hơn việc chúng làm trái lệnh Ngài và đức tin Iman của chúng sẽ vững chắc hơn, và chắc chắn TA sẽ ban cho chúng phần thưởng to lớn ở nơi TA và sẽ phù hộ chúng trên con đường đến với TA và Thiên Đàng của TA.');
INSERT INTO "vn_mokhtasar" VALUES(4,67,'4:67','Và nếu TA (Allah) sắc lệnh cho chúng người này giết người kia hoặc rời bỏ nhà cửa của chúng thì chúng sẽ không chấp hành mệnh lệnh của Ngài trừ một số ít. Bởi thế chúng hãy tạ ơn Allah vì Ngài đã không gây khó khăn cho chúng. Nhưng nếu chúng thực hiện theo lệnh của Allah thì tốt hơn việc chúng làm trái lệnh Ngài và đức tin Iman của chúng sẽ vững chắc hơn, và chắc chắn TA sẽ ban cho chúng phần thưởng to lớn ở nơi TA và sẽ phù hộ chúng trên con đường đến với TA và Thiên Đàng của TA.');
INSERT INTO "vn_mokhtasar" VALUES(4,68,'4:68','Và nếu TA (Allah) sắc lệnh cho chúng người này giết người kia hoặc rời bỏ nhà cửa của chúng thì chúng sẽ không chấp hành mệnh lệnh của Ngài trừ một số ít. Bởi thế chúng hãy tạ ơn Allah vì Ngài đã không gây khó khăn cho chúng. Nhưng nếu chúng thực hiện theo lệnh của Allah thì tốt hơn việc chúng làm trái lệnh Ngài và đức tin Iman của chúng sẽ vững chắc hơn, và chắc chắn TA sẽ ban cho chúng phần thưởng to lớn ở nơi TA và sẽ phù hộ chúng trên con đường đến với TA và Thiên Đàng của TA.');
INSERT INTO "vn_mokhtasar" VALUES(4,69,'4:69','Những ai tuân lệnh Allah và Thiên Sứ của Ngài thì những người đó là những người sẽ đồng hành vào Thiên Đàng cùng với những người được Allah ban Ân Huệ từ các vị Thiên Sứ, các vị Nabi, các vị Siddeeq (tin tuyệt đối nơi những gì được các vị Thiên Sứ mang đến), các vị Shaheed (hy sinh vì chính nghĩa của Allah) và những người ngoan đạo một cách công khai và thầm kín. Và thật là điều tốt đẹp cho những ai được đồng hành với những người đó nơi Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(4,70,'4:70','Phần thưởng vừa nêu là hồng phúc của Allah dành cho đám bề tôi của Ngài, và chỉ một mình Ngài thôi cũng đã đủ am tường hết mọi sự việc và Ngài sẽ ban thưởng thích đáng cho mọi việc làm.');
INSERT INTO "vn_mokhtasar" VALUES(4,71,'4:71','Này hỡi những người có đức tin nơi Allah và đi theo vị Thiên Sứ của Ngài, các ngươi hãy cảnh giác trước kẻ thù cúa các ngươi bằng cách tìm kiếm những chiếc lược để chiến đấu với họ, các ngươi hãy xuất quân từng nhóm một hoặc cả đoàn tùy theo cách tính toán và mưu lược của các ngươi trong trận chiến.');
INSERT INTO "vn_mokhtasar" VALUES(4,72,'4:72','Hỡi các tín đồ Muslim, quả thật trong các ngươi có nhóm người trì trệ ở đằng sau trong việc xuất quân đánh kẻ thù vì muốn trốn tránh giáp mặt với địch. Những người này là những kẻ Munafiq (giả tạo đức tin hãy đạo đức giả). Nếu các ngươi bị giết hoặc bị bại trận thì một trong số chúng sẽ vui mừng về sự an toàn của bản thân mình, nói: quả thật Allah đã ban phúc lành cho tôi khi tôi đã không có mặt cùng với họ để phải chịu tai họa giống như họ.');
INSERT INTO "vn_mokhtasar" VALUES(4,73,'4:73','Ngược lại, nếu các ngươi - hỡi các tín đồ Muslim - có được hồng phúc từ nơi Allah trong việc giành lấy chiến thắng trước kẻ thù và thu được chiến lợi phẩm thì hắn (kẻ trong nhóm những người Munafiq không chịu tham chiến cùng với những người Muslim) lại tỏ vẻ tiếc nuối như thể hắn không thuộc phe các ngươi và như thể giữa hắn và các ngươi không hề có bất cứ mối quan hệ nào, hắn nói: phải chi mình cũng có mặt chiến đấu với họ thì mình đã giành lấy sự vẻ vang như họ rồi.');
INSERT INTO "vn_mokhtasar" VALUES(4,74,'4:74','Hỡi những người có đức tin trung thực, những người mà họ bán đời sống trần tục này chỉ muốn đổi lấy cuộc sống tốt đẹp ở cõi Đời Sau, hãy chiến đấu vì con đường chính nghĩa của Allah để lời phán của Ngài được giơ cao. Và ai chiến đấu vì con đường chính nghĩa của Allah với mục đích giơ cao lời phán của Ngài rồi bị giết hoặc giành lấy chiến thắng vẻ vang trước kẻ thù thì Allah sẽ ban cho y một phần ân thưởng to lớn ở nơi Ngài, đó là Thiên Đàng và sự hài lòng của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,75,'4:75','Hỡi những người có đức tin, điều gì ngăn cản bước chân của các ngươi đi chiến đấu cho con đường chính nghĩa của Allah để giơ cao lời phán của Ngài và để giải thoát những người đàn ông, phụ nữ và trẻ con cô thế bị đàn áp, những người mà họ thường xuyên cầu nguyện Allah: Lạy Thượng Đế của bầy tôi, xin Ngài hãy cứu bầy tôi ra khỏi Makkah do cư dân của nó làm điều Shirk với Ngài và đán áp đám bầy tôi của Ngài, xin Ngài dựng lên cho bầy tôi một vị bảo hộ từ nơi Ngài để trông coi và bảo vệ bầy tôi, giúp bầy tôi chống lại sự xâm hại của kẻ thù.');
INSERT INTO "vn_mokhtasar" VALUES(4,76,'4:76','Những người có đức tin đích thực sẽ chiến đấu cho con đường chính nghĩa của Allah để giơ cao lời phán của Ngài còn những kẻ vô đức tin sẽ chiến đấu cho các thần linh ngụy tạo của họ. Bởi thế, các ngươi - những người có đức tin, hãy chiến đấu với những kẻ trợ lực của Shaytan, nếu các ngươi chiến đấu với chúng thì các ngươi sẽ chiến thắng chúng bởi mưu kế của Shaytan luôn yếu kém không thể hãm hại được những ai phó thác cho Allah, Đấng Tối Cao và Quyền Năng.');
INSERT INTO "vn_mokhtasar" VALUES(4,77,'4:77','Này hỡi vị Thiên Sứ của Allah, chẳng lẽ Ngươi không biết một số người bạn đạo của Ngươi, những người mà họ đã cầu xin Allah ban hành lệnh Jihad cho họ và họ được bảo: hãy ngừng tay chiến đấu, hãy dâng lễ nguyện Salah và xuất Zakah - đó là trước khi có lệnh Jihad - nhưng khi họ di cư đến Madinah lúc Islam bị cản trở thì lệnh Jihad đã được ban hành xuống cho họ; tuy nhiên, lệnh đó lại khó khăn cho họ, họ trở nên sợ nhân loại giống như họ sợ Allah hoặc nhiều hơn, họ than: lạy Thượng Đế của bầy tôi, cớ sao Ngài là ban hành nghĩa vụ đi đánh chiến cho bầy tôi? sao Ngài không để bầy tôi sống thêm một thời gian nữa đến khi tuổi thọ đã định của bầy tôi kết thúc. Ngươi - Muhammad - hãy bảo họ: sự hưởng thụ của đời sống trần tục này rất ít ỏi vì nó ngắn ngủi, riêng cuộc sống Đời Sau mới tốt đẹp hơn cho những ai có lòng Taqwa (ngay chính và sợ Allah) vì đó là cõi vĩnh hằng, và Allah không bất công với các ngươi bất cứ điều gì cho dù nó có nhỏ bằng rãnh nứt của hạt chà là đi chăng nữa.');
INSERT INTO "vn_mokhtasar" VALUES(4,78,'4:78','Cho dù các ngươi ở nơi đâu thì cái chết vẫn đến được với các ngươi một khi thời hạn qui định đã hết, ngay cả các ngươi có ở trong những tòa lâu đài được rào chắn bởi những thành lũy kiên cố nằm cách xa khỏi nơi giao chiến thì cái chết vẫn giáp mặt các ngươi. Và nếu những người Munafiq này gặp được điều lành từ con cái, bổng lộc dồi dào thì chúng bảo: những thứ này đến từ nơi Allah; nhưng nếu chúng gặp phải điều dữ đối với con cái và bổng lộc thì chúng bi quan than trách Thiên Sứ của Allah, bảo: những điều xấu này là từ nơi ngươi mà ra. Này hỡi Thiên Sứ của Allah, Ngươi hãy nói đáp lại lời của chúng: mọi điều phúc lành, mọi điều dữ đều do sự an bài và định đoạt của Allah. Tuy nhiên, dường như những kẻ này không hiểu được lời của Ngươi khi Ngươi nói chuyện với chúng!');
INSERT INTO "vn_mokhtasar" VALUES(4,79,'4:79','Này hỡi con của Adam (con người), ngươi hãy biết rằng những gì tốt đẹp từ con cái, bổng lộc mà ngươi có được là do Allah ban cho, do hồng phúc của Ngài đối với ngươi; riêng những gì không tốt lành từ con cái và bổng lộc mà ngươi gặp phải là từ nơi bản thân ngươi do hành vi tội lỗi và sai trái của ngươi. Hỡi vị Nabi Muhammad, quả thật TA (Allah) cử Ngươi đến với nhân loại trên cương vị một Sứ Giả từ nơi TA là để Ngươi truyền đạt bức Thông Điệp của TA - Thượng Đế của Ngươi và của toàn nhân loại. Và chỉ một mình Allah thôi cũng đã đủ làm một nhân chứng cho sự trung thực của Ngươi về sứ mạng truyền đạt bức Thông Điệp qua những bằng chứng và dấu lạ mà TA đã ban cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,80,'4:80','Ai vâng lời Thiên Sứ của Allah trong việc tuân thủ và chấp hành những gì Người sai bảo và hướng dẫn, tránh xa những gì Người ngăn cản và nghiêm cấm, ngươi đó đích thực đã đáp lại mệnh lệnh của Allah; còn ai quay mặt bỏ đi, không chịu chấp hành mệnh lệnh của Ngươi - hỡi Thiên Sứ Muhammad - thì Ngươi chớ buồn lòng bởi quả thật TA không cử phái Ngươi đến để trông chừng và giám sát việc làm của họ, quả thật TA chỉ tính sổ và phán xét việc làm của họ chứ không tính sổ và phán xét Ngươi về việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(4,81,'4:81','Những kẻ Munafiq nói với Ngươi - Muhammad - với chiếc lưỡi của chúng: chúng tôi xin nghe lệnh và làm theo. Nhưng khi chúng rời đi khỏi Ngươi thì một phần trong bọn chúng âm thầm làm trái với những điều mà chúng đã nói với Ngươi. Allah biết rõ những gì chúng bàn tính sau lưng Ngươi và Ngài sẽ đáp trả thích đáng với mưu đồ và kế hoạch của chúng. Bởi thế, Ngươi không cần quan tâm đến chúng bởi chúng sẽ không bao giờ làm hại được Ngươi, Ngươi cứ bỏ mặc chúng cho Allah và phó thác cho Ngài, quả thật một mình Allah thôi đã đủ cho Ngươi phó thác và tin cậy mọi sự việc.');
INSERT INTO "vn_mokhtasar" VALUES(4,82,'4:82','Tại sao chúng không chịu nghiên cứu và suy ngẫm về những lời phán trong Qur''an để chúng thấy được trong Qur''an không hề có sự mâu thuẫn và sai sót nào, để chúng biết những điều Ngươi - Muhammad - mang đến là sự thật?! Nếu Qur''an đến từ một ai đó từ loài Jin, loài người hay từ một tạo vật nào đó thì chắc chắn chúng sẽ tìm thấy trong Qur''an nhiều điều mâu thuẫn về ý nghĩa nội dung cũng như những sai sót về giáo lý.');
INSERT INTO "vn_mokhtasar" VALUES(4,83,'4:83','Và khi có tin tức liên quan đến sự an lành và hân hoan hay tin tức liên quan đến sự bất an và những điều buồn lòng của những người Muslim thì những kẻ Munafiq này liền loan tin và phổ biến rộng rãi. Đáng lẽ ra chúng phải đưa những tin tức đó cho Thiên Sứ của Allah hoặc cho những người hiểu biết có khả năng phân tích các vấn đề để họ xem xét nên phổ biến hay giữ bí mật. Quả thật, nếu không nhờ phúc của Allah và lòng nhân từ của Ngài dành cho các ngươi - hỡi những người có đức tin thì chắc chắn các ngươi sẽ gặp họa từ những kẻ Munafiq này và chắc chắc đa số các ngươi sẽ đi theo sự quấy phá của Shaytan.');
INSERT INTO "vn_mokhtasar" VALUES(4,84,'4:84','Hỡi Thiên Sứ Muhammad, Ngươi hãy chiến đấu cho con đường chính nghĩa của Allah để giơ cao lời phán của Ngài. Chắc chắn Ngươi sẽ không bị tra hỏi về việc làm của người khác vì Ngươi chỉ chịu trách nhiệm của riêng bản thân Người thôi trong việc chiến đấu, tuy nhiên, Ngươi hãy động viên tinh thần của những người có đức tin trong việc chiến đấu, mong rằng Allah sẽ ban cho các ngươi sức mạnh để đánh bại những kẻ vô đức tin bởi Allah là Đấng Hùng Mạnh nhất về sức lực và khả năng trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(4,85,'4:85','Ai đem đến điều tốt đẹp cho người khác thì y sẽ được phần ân phước còn ai đem đến điều xấu cho người khác thì y sẽ có một phần tội và Allah luôn trông coi và quan sát những hành vi của con người và Ngài sẽ ban thưởng thích đáng cho mỗi việc làm của họ. Bởi thế, ai trong các ngươi là nguyên nhân thúc đẩy người khác làm việc tốt thì y sẽ có được một phần thưởng trong việc tốt đó còn ai là nguyên nhân thúc đẩy người khác làm việc xấu thì y cũng sẽ bị một phần tội trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(4,86,'4:86','Và khi có ai đó chào Salam các ngươi thì các ngươi hãy đáp lại người đó bằng lời chào Salam tốt hơn lời chào mà y đã chào các ngươi hoặc ít ra bằng lời tương tự, và việc chào lại bằng lời lẽ tốt hơn là điều tốt đẹp hơn cho các ngươi; bởi quả thật Allah là Đấng không bỏ sót bất cứ điều gì, Ngài sẽ ban thưởng cho tất cả mọi việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,87,'4:87','Allah là Đấng duy nhất đáng được thờ phượng, ngoài Ngài không ai (vật gì) đáng có được điều đó. Vào Ngày Phục Sinh, chắc chắn Ngài sẽ tập hợp tất cả các ngươi từ người đầu tiên cho đến người cuối cùng để Ngài thưởng hoặc phạt qua các việc làm của các ngươi; và sự việc đó không có gì phải ngờ vực cả. Và không có một ai nói thật hơn Allah cả!');
INSERT INTO "vn_mokhtasar" VALUES(4,88,'4:88','Chuyện gì xảy ra với các ngươi hỡi những người có đức tin, sao các ngươi lại chia rẽ nhau thành hai nhóm riêng biệt trong việc cư xử đối với những người Munafiq (giả tạo đức tin hay đạo đức giả): một nhóm thì bảo giết chúng vì sự vô đức tin của chúng còn một nhóm thì bảo không giết chúng vì đức tin Iman của chúng? Thật ra không đáng để các ngươi bất đồng và mâu thuẫn nhau chỉ vì vấn đề của chúng bởi Allah đã đẩy chúng vào trong sự vô đức tin và lầm lạc do những việc làm của chúng. Chẳng lẽ các ngươi lại muốn hướng dẫn những kẻ mà Allah đã không cho phép chúng đến với chân lý ư? Ngươi (Muhammad) hãy biết rằng ai mà Allah đã làm cho y lầm lạc thì Ngươi sẽ không bao giờ tìm được cho y một con đường đến với sự hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(4,89,'4:89','Những kẻ Munafiq luôn mong các ngươi vô đức tin nơi những gì được ban xuống cho các ngươi giống như việc chúng đã vô đức tin để các ngươi và chúng ngang bằng nhau trong việc vô đức tin. Bởi thế, các ngươi chớ lấy ai trong bọn chúng làm người thân tín của các ngươi bởi sự hiềm khích của chúng đối với các ngươi trừ phi chúng chịu di cư từ vùng đất Shirk để đến với vùng đất của Islam để chứng minh đức tin của chúng. Nếu chúng quay lưng và tiếp tục trên tình trạng vô đức tin của chúng thì các ngươi hãy bắt và giết chúng bất cứ nơi nào các ngươi tìm thấy chúng; và các ngươi chớ lấy ai trong bọn chúng là người thân tín để các ngươi gửi gắm các vụ việc của các ngươi cho chúng và cũng chớ lấy chúng làm người giúp đỡ các ngươi chống lại kẻ thù của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,90,'4:90','Trừ những ai trong bọn chúng gia nhập vào nhóm mà giữa các ngươi và họ có một hiệp ước hòa bình hoặc những ai đến gặp các ngươi mà trái tim của họ không muốn chiến tranh với các ngươi và cũng không muốn chiến tranh với nhóm dân vô đức tin của họ. Và nếu muốn, Allah có thể làm cho họ chiếm ưu thế hơn các ngươi và đánh bại các ngươi. Nhưng nếu họ đã cầu hòa với các ngươi và không muốn chiến tranh với các ngươi hãy chấp thuận bởi Allah không cho các ngươi bất cứ một lý do nào để giết họ hoặc bắt họ là tù binh.');
INSERT INTO "vn_mokhtasar" VALUES(4,91,'4:91','Rồi các ngươi - hỡi những người có đức tin - sẽ thấy một nhóm người khác thuộc những người Munafiq, chúng thể hiện trước các ngươi đức tin Iman để được sống yên bình với các ngươi nhưng lại thể hiện sự vô đức tin trước đồng bọn ngoại đạo của chúng mỗi khi chúng quay trở về để được sống yên ổn; và mỗi khi chúng được gọi đến với sự vô đức tin nơi Allah và Shirk với Ngài thì chúng càng lún sâu hơn trong sự vô đức tin và Shirk. Những kẻ này, nếu chúng không chịu từ bỏ việc đánh các ngươi và cũng không có ý thỏa hiệp hòa bình mà cứ cố tìm cách đánh các ngươi thì các ngươi hãy bắt và đánh chúng bất cứ nơi nào các ngươi giáp mặt chúng. Chúng là những kẻ mà TA (Allah) cho các ngươi quyền đánh giết và trừng phạt bởi vì chúng đã mưu mô xảo quyệt.');
INSERT INTO "vn_mokhtasar" VALUES(4,92,'4:92','Người có đức tin sẽ không giết một người có đức tin ngoài trừ do lỡ tay (tức không cố ý, tội ngộ sát). Ai giết một người có đức tin vì lỡ tay chứ không phải do cố y thì y phải phóng thích một người nữ nô lệ vô đức tin để chuộc tội đồng thời phải trả tiền bồi thương nhân mạng cho gia đình của nạn nhân trừ phi họ xí xóa không nhận. Nếu nạn nhân thuộc nhóm dân có sự thù nghịch với các ngươi và y là người có đức tin thì chỉ bắt buộc người giết phóng thích một nữ nô lệ có đức tin chứ không cần phải trả tiền bồi thường nhân mạng. Nếu nạn nhân không phải là người có đức tin nhưng y thuộc nhóm dân có hiệp ước hòa bình với các ngươi thì người giết phải trả tiền bồi thương nhân mạng cho gia đình của nạn nhân đồng thời phài phóng thích một nữ nô lệ có đức tin để chuộc tội. Nếu người giết không tìm thấy nô lệ để phóng thích hoặc không có khả năng trả tiền bồi thường thì y phải nhịn chay hai tháng liền mục đích để Allah tha thứ cho tội lỗi của y. Quả thật, Allah là Đấng Hằng Biết tất cả các việc làm của đám bầy tôi của Ngài cũng như các tâm niệm của họ, và là Đấng Sáng Suốt và Anh Minh trong định đoạt và ban hành giáo luật.');
INSERT INTO "vn_mokhtasar" VALUES(4,93,'4:93','Ai giết một người có đức tin một cách cố tình (có chủ ý, mưu sát) mà không có lý do chính đáng theo giáo luật thì phần thưởng dành cho y là bị đày vào Hỏa Ngục và sống đời đời trong đó, y sẽ Allah giận dữ và bị đuổi khỏi lòng thương xót của Ngài, và Ngài sẽ chuẩn bị cho y một sự trừng phạt vô cùng khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(4,94,'4:94','Hỡi những người có đức tin nơi Allah và đi theo vị Thiên Sứ của Ngài, khi các ngươi xuất quân đi chinh chiến cho con đường chính nghĩa của Allah thì các ngươi hãy xem xét cho kỹ lưỡng về những ai mà các ngươi phải đánh chiến với họ, các ngươi cho nói với ai biểu hiện cho các ngươi thấy rằng họ là người Islam: "ngươi không phải là người có đức tin, ngươi chỉ thể hiện Islam vì sợ mất mạng" rồi các ngươi giết y chỉ muốn được lợi lộc của thế gian, đó là chiến lợi phẩm từ y; các ngươi hãy biết rằng ở nơi Allah có nhiều chiến lợi phẩm tốt đẹp và vĩ đại hơn chiến lợi phẩm tầm thường đó. Tương tự, trước đây các ngươi cũng gặp phải tình cảnh như thế, tình cảnh mà các ngươi phải che giấu đức tin Iman của các ngươi trước cộng đồng của các ngươi, rồi Allah đã ban ân huệ Islam cho các ngươi và tình mạng của các ngươi được bảo toàn. Bởi thế, các ngươi hãy xem xét cho kỹ lưỡng bởi quả thật không có bất cứ điều gì có thể che giấu được Allah, cho dù bất cứ việc gì các ngươi làm có nhỏ như thế nào thì chắc chắn Ngài cũng sẽ ban thưởng hay trừng phạt thích đáng cho việc làm đó của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,95,'4:95','Những người có đức tin ngồi ở nhà ngoài những người bệnh tật hoặc những người có những lý do chính đáng không thể tham chiến không ngang bằng với những người có đức tin tòng quân chiến đấu trên con đường chính nghĩa của Allah, vì họ đã chiến đấu trên con đường chính nghĩa của Allah bằng tài sản và cả tính mạng của họ. Allah ban hồng phúc cho những ai chiến đấu vì con đường chính nghĩa của Ngài bằng tài sản lẫn tính mạng của họ vượt trội hơn những ai ngồi ở nhà không tham chiến. Và mỗi người đi tham chiến cũng như những ai không tham chiến do có nguyên nhân chính đáng đều được Allah ban thưởng xứng đang. Và Allah ban thưởng cho những ai đi tham chiến một phần thưởng to lớn những ai ở lại nhà.');
INSERT INTO "vn_mokhtasar" VALUES(4,96,'4:96','Phần thưởng ở nơi Allah có cấp độ khác nhau, cái này hơn cái kia, và ở nơi Ngài còn có cả sự tha thứ tội lỗi và lòng nhân từ, bởi quả thật Allah là Đấng hằng tha thứ và rất mực thương xót các bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,97,'4:97','Quả thật những kẻ mà các Thiên Thần đến rút hồn chúng trong lúc chúng bất công với chính bản thân chúng qua việc chúng không chịu di cư rời bỏ vùng đất vô đức tin để đến với vùng đất Islam, các Thiên Thần bảo chúng lúc rút hồn chúng trong sự quở trách: vì lý do gì mà các ngươi cứ ở trên tình trạng Shirk như thế? Chúng trả lời: nguyên nhân vì chúng tôi yếu thế, chúng tôi không đủ sức để chống lại họ nên chúng tôi đành theo họ. Thế là các Thiên Thần la mắng vào mặt chúng: chẳng phải đất đai của Allah luôn bao la rộng lớn để các ngươi ra đi lánh nạn an toàn cho tôn giáo và bản thân của các ngươi hay sao?! Như vậy, những kẻ đó, những kẻ mà chúng không chiu di cư sẽ phải đời đời kiếp kiếp trong Hỏa Ngục, một nơi trở về xấu xa và tồi tệ nhất dành cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,98,'4:98','Lời cảnh báo trừng phạt những kẻ không chịu di cư trừ những người thực sự có lý do không thể di cư từ đàn ông, phụ nữ và trẻ con. Những người này mới thực sự là những người yếu thế bởi vì họ không có phương tiện để di cư và cũng không có ai hướng dẫn họ. Những người này là những người hy vọng được Allah xí xóa bởi lòng nhân từ của Ngài vì Ngài là Đấng Hằng Tha Thứ cho các bề tôi của Ngài, cho những ai quay đầu sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,99,'4:99','Lời cảnh báo trừng phạt những kẻ không chịu di cư trừ những người thực sự có lý do không thể di cư từ đàn ông, phụ nữ và trẻ con. Những người này mới thực sự là những người yếu thế bởi vì họ không có phương tiện để di cư và cũng không có ai hướng dẫn họ. Những người này là những người hy vọng được Allah xí xóa bởi lòng nhân từ của Ngài vì Ngài là Đấng Hằng Tha Thứ cho các bề tôi của Ngài, cho những ai quay đầu sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,100,'4:100','Và ai di cư từ vùng đất vô đức tin để đến với vùng đất của Islam vì muốn được sự hài lòng nơi Allah thì người đó sẽ tìm thấy chỗ lánh thân tốt đẹp hơn, vì đất đai của Allah bao la và rộng lớn. Và ai rời bỏ nhà cửa của mình để di cư đến với Allah và Thiên Sứ của Ngài rồi chết trên đường đi trước khi tới nơi cần di cư đến thì phần thưởng dành cho y chắc chắn ở nơi Allah, việc y chưa tới được nơi cần di cư đến không làm mất đi phần thưởng mà Ngài đã dành cho y. Quả thật, Allah là Đấng Hằng Tha Thứ cho những bề tôi quay về sám hối với Ngài và Ngài luôn thương xót và nhân từ với họ.');
INSERT INTO "vn_mokhtasar" VALUES(4,101,'4:101','Và khi nào các ngươi đi lại xa xôi trên trái đất thì các ngươi không mang tội trong việc rút ngắn các lễ nguyện Salah gồm bốn Rak-''at thành hai Rak-''at nếu như các ngươi sợ những kẻ vô đức tin có thể sẽ tấn công bất ngờ các ngươi; bởi quả thật sự thù hằn của những kẻ vô đức tin đối với các ngươi đã lộ ra quá rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(4,102,'4:102','Hỡi vị Thiên Sứ, khi Ngươi đang ở cùng với đội quân trong khoảng thời gian chinh chiến với kẻ thù, Ngươi muốn dẫn lễ nguyện Salah cho họ thì Ngươi hãy chia đội quân thành hai nhóm. Một nhóm dâng lễ nguyện Salah cùng với Ngươi, họ phải mang theo vũ khí cùng với họ trong lễ nguyện Salah, còn nhóm kia sẽ canh gác cho các ngươi. Khi nhóm thứ nhất đã thực hiện xong một Rak-''at cùng với Imam thì họ sẽ tự tiếp tục hoàn tất nốt lễ nguyện Salah. Khi họ đã xong lễ nguyện Salah thì họ sẽ lui về phía sau hướng mặt về phía kẻ thù, nhóm thứ hai tức nhóm đã canh gác trước đó sẽ bước lên, nhóm này sẽ thực hiện một Rak-''at cùng với Imam và khi Imam cho Salam thì họ đứng dậy tiếp tục thực hiện nốt phần còn lại của lễ nguyện Salah, và họ cũng phải nên mang theo vũ khí trong lúc lễ nguyện Salah bởi quả thật những kẻ ngoại đạo luôn mong muốn thừa lúc các ngươi lơ là vũ khí và quân trang của các ngươi thì chúng sẽ tiến đến đánh các ngươi khiến các ngươi không kịp trở tay. Tuy nhiên, các ngươi sẽ không bị bắt tội khi các ngươi không mang theo vũ khí do trời mưa làm các ngươi bất tiện hoặc do các ngươi đau ốm; nhưng dù thế nào, các ngươi cũng phải thực sự cảnh giác và thận trọng để ý đến kẻ thù của các ngươi theo khả năng có thể. Quả thật, Allah đã chuẩn bị cho những kẻ vô đức tin một sự trừng phạt nhục nhã.');
INSERT INTO "vn_mokhtasar" VALUES(4,103,'4:103','Hỡi những người có đức tin, khi nào các ngươi đã xong cuộc lễ nguyện Salah thì các ngươi hãy tụng niệm Allah bằng các lời Tasbih (Subha-nallah), Tahmeed (Alhamdulillah) và Tahleel (La ila-ha illalla-h) trong mọi hoàn cảnh của các ngươi, các ngươi hãy tụng niệm Ngài lúc đứng, lúc ngồi và cả lúc các ngươi nằm nghiêng một bên. Và khi nào nỗi sợ hãi không còn nữa, các ngươi đã được an bình thì các ngươi hãy thực hiện lễ nguyện Salah một cách chu đáo theo đúng với các nghi thức Rukun, Wajib, Sunnah của nó như các ngươi được bảo. Quả thật, lễ nguyện Salah được qui định cho những người có đức tin vào các giờ giấc nhất định, không ai được phép trì hoãn mà không có lý do chính đáng, đây là trường hợp các ngươi đang ở tại nơi định cư hoặc ở nơi tạm trú ổn định, còn riêng trường hợp các ngươi đang là người đi đường xa thì các ngươi được phép dâng lễ nguyện Salah theo hình thức Jamu''a (gộp chung hai lễ nguyện Salah trong cùng một giờ) và theo hình thức Qasr (rút ngắn các lễ nguyện Salah bốn Rak-''at thành hai Rak-''at).');
INSERT INTO "vn_mokhtasar" VALUES(4,104,'4:104','Hỡi những người có đức tin, các ngươi chớ yếu đuối rồi bỏ cuộc trong việc đuổi bắt kẻ thù từ những người vô đức tin. Bởi quả thật, nếu các ngươi phải chịu đau đớn từ việc giết chóc và thương tích thì chúng cũng phải chịu đau đớn giống như các ngươi, cho nên, các ngươi chớ để sự kiên nhẫn của họ vượt trội hơn sự kiên nhẫn của các ngươi. Các ngươi hãy nhớ rằng tuy các ngươi phải hứng chịu sự đau đớn nhưng các ngươi có niềm hy vọng về phần thưởng cũng như sự trợ giúp từ nơi Allah trong khi chúng - kẻ thù của các ngươi chẳng có gì để hy vọng cả. Và Allah là Đấng Hằng Biết các hoàn cảnh của đám bề tôi của Ngài và Ngài là Đấng Anh Minh và Sáng Suốt trong việc định đoạt và phán quyết.');
INSERT INTO "vn_mokhtasar" VALUES(4,105,'4:105','Quả thật TA (Allah) đã ban xuống cho Ngươi - hỡi vị Thiên Sứ - Kinh Qur''an chứa đựng điều chân lý để Ngươi dùng Nó phân xử các vụ việc của thiên hạ qua những gì mà Ngài đã chỉ dạy và hướng dẫn Ngươi. Và Ngươi chớ bênh vực cho những kẻ phản bội, xảo biện và lừa lọc khi chúng đòi điều chân lý về chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,106,'4:106','Ngươi (Muhammad) hãy cầu xin Allah tha thứ và xí xóa tội lỗi, quả thật Allah là Đấng Hằng Tha Thứ cho những bề tôi quay đầu sám hối với Ngài và Ngài luôn nhân từ và khoan dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(4,107,'4:107','Ngươi (Muhammad) chớ đừng tranh cãi và biện minh cho bất kỳ kẻ nào phản bội lương tâm của hắn bởi Allah không yêu thương những kẻ dối gạt, lừa lọc, gian trá và bội phản.');
INSERT INTO "vn_mokhtasar" VALUES(4,108,'4:108','Chúng che giấu thiên hạ những việc làm tội lỗi và sai trái của chúng vì lo sợ và e ngại nhưng chúng không thể che giấu được Allah bởi vì Ngài luôn có mặt cùng với chúng bằng sự bao trùm của Ngài, không có bất cứ điều gì chúng bí mật bàn tính mà Ngài không biết và Ngài không chấp nhận những lời nói xảo biện nhằm để trốn tội của chúng. Quả thật, Allah biết hết tất cả những gì chúng làm dù công khai hay bí mật rồi Ngài sẽ đáp lại xứng đáng cho các việc làm của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,109,'4:109','Đây - hỡi những ai cố gắng bênh vực cho những kẻ tội lỗi - những kẻ này đã được các ngươi biện minh cho chúng tránh tội ở trên cuộc sống trần gian để chúng thoát khỏi hình phạt nhưng vào Ngày Phán Xét, khi chúng đứng trình diện trước Allah thì ai sẽ là người có thể bênh vực cho chúng bởi Ngài đã tường tận mọi sự thật của chúng, và ai sẽ đứng ra thụ lãnh tội lỗi giùm cho chúng?! Dĩ nhiên, chắc chắn không một ai có khả năng làm điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(4,110,'4:110','Ai làm điều tội lỗi hay bất công với chính bản thân mình qua việc làm trái lệnh rồi y ăn năn sám hối cầu xin Allah tha thứ thì Ngài chắc chắn sẽ tha thứ cho y bởi Ngài là Đấng Hằng Tha Thứ tội lỗi và Hằng Thương Xót các bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,111,'4:111','Ai làm điều tội lỗi dù nhỏ hay lớn thì chỉ bản thân người đó mới bị trừng phạt chứ không có ai ngoài y phải chịu tội lây. Quả thật, Allah là Đấng Am Tương mọi hành động của các bề tôi và Ngài là Đấng Sáng Suốt và Anh Minh trong việc định đoạt và phán quyết.');
INSERT INTO "vn_mokhtasar" VALUES(4,112,'4:112','Ai lỡ làm sai do không cố tình hoặc phạm phải tội một cách cố tình rồi lại vu oan cho một người vô tội nào đó thì y đã mang tội cho việc vu khống đó và cho cả tội lỗi hiển nhiên của y.');
INSERT INTO "vn_mokhtasar" VALUES(4,113,'4:113','Nếu không nhờ phúc của Allah cũng như lòng nhân từ của Ngài dành cho Ngươi - hỡi vị Thiên Sứ - trong việc bảo vệ Ngươi thì một nhóm của những người của bọn chúng - những kẻ lừa dối bản thân chúng - đã quyết làm cho Ngươi lệch khỏi chân lý; nhưng những kẻ đó chỉ làm lệch lạc bản thân của chúng bởi vì mọi cố gắng của chúng trong việc làm cho Người lầm lạc cuối cùng đã rơi trở lại cho bản thân chúng. Và Allah đã ban cho Ngươi Kinh Qur''an và Sunnah, Ngài đã dạy Ngươi nguồn chỉ đạo và ánh sáng cái mà trước đó Ngươi chưa từng biết. Quả thật, Allah đã ban cho Ngươi sứ mạng Nabi và bảo vệ Ngươi khỏi những sai sót và tội lỗi, đó là hồng ân vĩ đại mà Ngài đã ban cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,114,'4:114','Việc thiên hạ nói chuyện bí mật với nhau đa số đều không tốt lành, không mang lại lợi ích gì cả, ngoại trừ cuộc nói chuyện của họ là để bảo bàn nhau việc bố thí, làm điều thiện tốt và ngoan đạo, làm những điều mà lương trí mách bảo hoặc kêu gọi hai bên xung đột nhau đến với sự hòa giải. Và ai làm những điều đó (bố thí, hành thiện, ngoan đạo và giải hòa giữa mọi người) vì muốn làm Allah hài lòng thì Ngài sẽ ban cho y một phần thưởng vô cùng to lớn.');
INSERT INTO "vn_mokhtasar" VALUES(4,115,'4:115','Ai làm trái lệnh và chống đối Thiên Sứ của Allah về những gì mà Người mang đến sau khi sự thật đã được phơi bày rõ ràng và đi theo con đường khác với con đường của những người có đức tin thì TA (Allah) sẽ bỏ mặc y cùng với những gì mà y đã chọn, TA sẽ không phù hộ cho y giác ngộ điều chân lý và TA sẽ đày y vào trong Hỏa Ngục để y nếm lấy cái nóng của Nó, và Hỏa Ngục chính là điểm quay về xấu xa cho cư dân của Nó.');
INSERT INTO "vn_mokhtasar" VALUES(4,116,'4:116','Quả thật, Allah không tha thứ cho hành vi Shirk với Ngài, ai làm điều Shirk với Ngài thì sẽ đời đời trong Hỏa Ngục. Allah chỉ tha thứ cho các tội khác ngoài tội Shirk đối với ai Ngài muốn bằng hồng phúc và sự khoan dung của Ngài. Và ai Shirk với Allah một đấng ngang vai thì quả thật y đã đi khỏi chân lý quá xa bởi y đã làm ngang vai giữa Đấng Tạo Hóa và tạo vật.');
INSERT INTO "vn_mokhtasar" VALUES(4,117,'4:117','Những kẻ thờ đa thần này cầu nguyện cùng với Allah các nữ thần với tên gọi như Allat, Al-Uzza, đây là những thần linh ngụy tạo không mang lợi cũng không gây hại được cho ai. Thật ra chúng đang thờ cúng những tên Shaytan thay vì tuân lệnh và thờ phụng Allah; Shaytan đã bảo chúng thờ phụng các bục tượng.');
INSERT INTO "vn_mokhtasar" VALUES(4,118,'4:118','Allah đã trục xuất và xua đuổi Shaytan khỏi lòng Nhân Từ của Ngài và Shaytan đã thề với Ngài: chắc chắn bề tôi sẽ làm cho đám bề tôi của Ngài lạc khỏi chân lý một phần.');
INSERT INTO "vn_mokhtasar" VALUES(4,119,'4:119','Shaytan thề với Allah: "Và bề tôi sẽ dắt đám bề tôi của Ngài lệch khỏi con đường Ngay Chính của Ngài, bề tôi sẽ xúi giục chúng theo đuổi những dục vọng với những lời lẽ hoa mỹ đầy sự lừa lọc để chúng lầm lạc, bề tôi sẽ bảo chúng cắt tai gia súc để cấm những gì Allah cho phép, và bề tôi sẽ bảo chúng thay đổi sự tạo hóa của Allah". Ai nhận lấy Shaytan làm kẻ trợ giúp, y hướng về hắn và phục tùng mệnh lệnh của hắn thì y thực sự đã thất bại thảm hại, điều đó thật quá rõ ràng không có gì phải ngờ vực.');
INSERT INTO "vn_mokhtasar" VALUES(4,120,'4:120','Shaytan hứa với chúng bằng lời hứa giả dối, chúng xúi giục chúng đi theo những ham muốn sai trái, và lời hứa của Shaytan chỉ là sự lừa dối không thật.');
INSERT INTO "vn_mokhtasar" VALUES(4,121,'4:121','Những kẻ đi theo các bước chân của Shaytan, chỗ ở của chúng sẽ là Hỏa Ngục, nơi mà chúng sẽ không bao giờ thoát ra được.');
INSERT INTO "vn_mokhtasar" VALUES(4,122,'4:122','Những người có đức tin nơi Allah và làm việc thiện tốt vì sự hài lòng của Ngài thì Ngài sẽ thu nhận họ vào Thiên Đàng nơi Ngài với những ngôi vườn có các dòng sông chảy bên dưới các tòa lâu đài, họ sẽ sống trong đó đời đời. Đó là lời hứa từ nơi Allah và lời hứa của Ngài là sự thật bởi vì Ngài không bao giờ làm khác với lời hứa, và quả thật không một ai nói thật hơn Allah cả.');
INSERT INTO "vn_mokhtasar" VALUES(4,123,'4:123','Việc được cứu rỗi và được thành công không phải theo ước muốn của các ngươi - hỡi những người có đức tin - cũng không phải theo ước muốn của những người dân Kinh Sách mà là tùy theo việc làm và hành động của mỗi người. Ai trong các ngươi làm điều xấu và tội lỗi thì vào Ngày Phán Xét y sẽ được đền bù thích đáng, y sẽ không tìm thấy ngoài Allah một vị bảo hộ hay một vị cứu tính nào giúp đỡ và can thiệp cho y.');
INSERT INTO "vn_mokhtasar" VALUES(4,124,'4:124','Những ai hành thiện và làm những điều ngoan đạo, dù là nam hay nữ, trong khi họ là những người có đức tin thực sự nơi Allah thì họ là những người đã tập hợp được đức tin cùng với sự thực hành biểu hiện đức tin của họ, họ sẽ được vào Thiên Đàng và họ sẽ không bị cắt xén ân phước của những điều mà họ đã làm cho dù chỉ bằng cái đường rãnh nhỏ trên hạt của quả chà là.');
INSERT INTO "vn_mokhtasar" VALUES(4,125,'4:125','Không có một ai tốt đạo hơn một người qui phục Allah từ thân xác đến tâm hồn, luôn thành tâm vì Ngài, luôn thi hành tốt theo giáo luật, và đi theo tôn giáo của Ibrahim, tôn giáo mà nó là gốc tôn giáo của Muhammad, tôn giáo mà nó loại bỏ hoàn toàn điều Shirk và sự vô đức tin để đến với Tawhid và đức tin Iman. Và Allah đã chọn Nabi Ibrahim làm vị Khaleel của Ngài tức người được Allah yêu thương nhất.');
INSERT INTO "vn_mokhtasar" VALUES(4,126,'4:126','Mọi vật trong các tầng trời và mọi vật trên trái đất đều thuộc về một mình Allah, Ngài có quyền chi phối và quyết định trên tất cả mọi vật, và Allah là Đấng Bao Trùm tất cả vạn vật bằng kiến thức, quyền năng và sự điều hành của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,127,'4:127','Hỡi Thiên Sứ Muhammad, họ hỏi Ngươi về phụ nữ, về quyền và nghĩa vụ của họ. Người hãy nói với họ: Allah đã nói rõ cho các ngươi về điều các ngươi hỏi, Ngài trình bày cho các ngươi qua những gì được đọc cho các ngươi nghe từ Kinh Qur''an. Về các phụ nữ mồ côi nằm dưới quyền quản lý của các ngươi thì các ngươi không đưa cho họ thứ mà Allah đã sắc lệnh bắt buộc các ngươi phải đưa cho họ từ tiền cưới (Mahr) và tài sản thừa kế, các ngươi không muốn kết hôn với họ nhưng các ngươi ngăn cấm họ kết hôn vì tham tiền của và tài sản của họ. Ngài trình bày cho các ngươi về điều các người phải đối xử với những trẻ cô thế rằng các ngươi phải đưa cho chúng phần quyền lợi của chúng từ tài sản thừa kế, các ngươi không được chiếm đoạt tài sản của chúng một cách bất chính, các ngươi phải công bằng trong việc trông coi các vụ việc của chúng trên cõi trần và cõi Đời Sau. Và những điều tốt mà các ngươi đã làm cho các trẻ mồ côi và những ai khác ngoài chúng thì Allah đều am tường tất cả, Ngài sẽ đền đáp xứng đáng cho công lao của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,128,'4:128','Nếu người vợ lo sợ người chồng của mình không mặn nồng vời mình hoặc bỏ rơi mình thì hai người sẽ không mang tội cho việc tìm cách giải hòa mối quan hệ bằng cách giảm bớt một số nghĩa vụ bắt buộc của người chồng đối với vợ chẳng hạn như nghĩa vụ chu cấp và ngủ đêm. Và sự hòa giải là giải pháp tốt cho hai vợ chồng hơn việc phải ly dị. Nhưng quả thật trong bản thân mỗi người đều tồn tại sự ích kỷ và hẹp hòi, con người thường không muốn giảm bớt các quyền lợi của mình cho nên vợ chồng cần phải điều trị bản chất này của mình bằng sự xí xóa và lòng tốt. Nếu các ngươi làm tốt trong tất cả mọi vụ việc của các ngươi, các ngươi sợ Allah mà thực thi những mệnh lệnh của Ngài và tránh xa những điều cấm của Ngài thì các ngươi hãy biết rằng Allah luôn am tường những gì các ngươi đã làm, không có điều gì nằm ngoài tầm hiểu biết của Ngài, Ngài sẽ ban thưởng cho các ngươi một cách thỏa đáng.');
INSERT INTO "vn_mokhtasar" VALUES(4,129,'4:129','Hỡi những người chồng, các ngươi sẽ không bao giờ có thể công bằng tuyệt đối với những người vợ của các ngươi về tình cảm trong con tim của các ngươi cho dù các ngươi có cố gắng như thế nào bởi lẽ cảm xúc của các ngươi không nằm trong tầm kiểm soát của các ngươi. Bởi thế, các ngươi chớ nghiêng hẳn về một người vợ nào đó và bỏ rơi người vợ khác vì các ngươi không còn mặn nồng tình cảm. Nếu các ngươi hoàn toàn bỏ rơi không chịu ngó ngàng đến người vợ đó thì điều đó giống như một hình phạt treo đối với cô ta, cô ta giống như một người phụ nữ không chồng. Do đó, nếu các ngươi giải hòa và bắt bản thân các ngươi phải thực hiện nghĩa vụ thiết yếu của một người chồng đúng nghĩa đối với người vợ đó thì điều đó tốt cho các ngươi. Các ngươi hãy kính sợ Allah về bổn phận làm chồng đúng nghĩa đối với các bà vợ của các ngươi và Allah là Đấng Hằng Tha Thứ, Đấng Rất Mực Nhân Từ đối với các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,130,'4:130','Và nếu hai vợ chồng không cần thiết muốn sống chung với nhau nữa, hai người quyết định ly dị nhau thì Allah sẽ ban phúc cho họ, Ngài sẽ ban cho người đàn ông một người vợ khác tốt đẹp hơn và sẽ ban cho người phụ nữ một người chồng khác tốt đẹp hơn. Bởi quả thật, Allah là Đấng có hồng phúc và lòng nhân từ bao la, và Ngài là Đấng Sáng Suốt và Anh Minh trong sự định đoạt và phán quyết.');
INSERT INTO "vn_mokhtasar" VALUES(4,131,'4:131','Vạn vật trên các tầng trời và trên trái đất đều thuộc về một mình Allah. Quả thật Ngài đã ban xuống cho những người dân Kinh Sách từ Do Thái và Thiên Chúa và đã ban xuống cho các ngươi - hỡi những người có đức tin - một giao ước: những mệnh lệnh phải được thi hành cùng với những điều cấm cần phải tránh xa. Nếu các ngươi phủ nhận giao ước này thì các ngươi chỉ tự làm hại chính bản thân các ngươi; bởi Allah không cần đền sự tuân lệnh và vâng lời của các ngươi, Ngài sở hữu tất cả vạn vật trong các tầng trời và trái đất và Ngài là Đấng Giàu Có và Tự Hữu, Ngài không cần đến bất cứ thứ gì từ tạo vật và Ngài là Đấng đáng được ca ngợi về tất cả thuộc tính và hành vi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,132,'4:132','Allah sở hữu tất cả mọi vật trong các tầng trời và trái đất, một mình Ngài duy nhất xứng đáng được thờ phụng và tuân mệnh, và một mình Ngài đủ chi phối và điều hành mọi vụ việc của tạo vật.');
INSERT INTO "vn_mokhtasar" VALUES(4,133,'4:133','Này hỡi nhân loại, nếu muốn Allah sẽ hủy diệt toàn bộ các ngươi rồi Ngài sẽ tạo ra một nhóm tạo vật khác để họ phục tùng Ngài và không làm trái lệnh Ngài, và Allah thừa khả năng làm việc đó.');
INSERT INTO "vn_mokhtasar" VALUES(4,134,'4:134','Hỡi nhân loại, ai trong các ngươi chỉ muốn phần thưởng ở cuộc sống thế gian thôi thì y hãy biết rằng ở nơi Allah có cả phần thưởng của cuộc sống thế gian và phần thưởng của cõi Đời Sau. Các ngươi nên mong muốn cả hai. Quả thật, Allah là Đấng nghe thấy hết lời nói của các người và là Đấng nhìn thấy hết mọi việc làm của các ngươi, và Ngài đền đáp xứng đáng tất cả mọi việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,135,'4:135','Hỡi những người có đức tin nơi Allah và đi theo Thiên Sứ của Ngài, các ngươi hãy đứng cùng với công lý trong mọi hoàn cảnh, các ngươi hãy làm nhân chứng cho sự thật dù việc làm đó có nghịch lại với bản thân các ngươi hoặc với cha mẹ của các ngươi hoặc với bà con thân thuộc của các ngươi, các ngươi chớ đừng quan tâm đến sự giàu nghèo của một ai trong việc làm chứng hay thôi làm chứng bởi Allah mới là Đấng biết rõ việc giàu hay nghèo là điều cải thiện cho các ngươi. Bởi thế, các ngươi chớ đừng đi theo dục vọng của bản thân các ngươi, nếu các ngươi làm nhân chứng không đúng sự thật thì các ngươi hãy biết rằng Allah thông tường hết những gì các ngươi đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(4,136,'4:136','Hỡi những người có đức tin, các ngươi hãy củng cố đức tin Iman của các ngươi nơi Allah, nơi vị Thiên Sứ của Ngài, nơi Kinh Qur''an được ban xuống cho vị Thiên Sứ của Ngài, nơi các Kinh Sách được ban xuống cho các vị Thiên Sứ trước Y (Muhammad). Và ai phủ nhận Allah, các Thiên Thần của Ngài, các Kinh Sách của Ngài, các vị Thiên Sứ của Ngài và Ngày Phán Xét thì quả thật y đã lệch khỏi qúa xa con đường Ngay Chính.');
INSERT INTO "vn_mokhtasar" VALUES(4,137,'4:137','Quả thật những người cứ lặp đi lặp lại sự vô đức tin sau khi đã tin tưởng và đã ngoan cố hơn nữa trong việc vô đức tin thì Allah sẽ không tha thứ cho y và Ngài sẽ không phù hộ y tìm thấy con đường Chính Đạo, đó chính là con đường Islam.');
INSERT INTO "vn_mokhtasar" VALUES(4,138,'4:138','Hỡi vị Thiên Sứ Muhammad, Ngươi hãy báo tin cho những người giả tạo đức tin hay đạo đức giả tức những kẻ biểu hiện đức tin Iman ở bề ngoài những bên trong thì vô đức tin rằng vào Ngày Phục Sinh họ sẽ trừng phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(4,139,'4:139','Họ sẽ bị trừng phạt bởi vì họ đã nhận lấy những người Thiên Chúa làm người bảo hộ thay vì những người có đức tin. Chẳng lẽ họ muốn đi tìm sức mạnh từ những kẻ chống đối Allah và Thiên Sứ của Ngài bởi quả thật mọi sức mạnh và quyền lực đều thuộc về một mình Allah.');
INSERT INTO "vn_mokhtasar" VALUES(4,140,'4:140','Hỡi những người có đức tin, quả thật Allah đã ban xuống kinh Qur''an cho các ngươi rằng khi các ngồi trong một buổi tọa đàm nào đó rồi các ngươi nghe ai đó phủ nhận và chế giễu các lời phán của Allah thì các ngươi phải rời đi chỗ khác. Nếu các ngươi ngồi cùng với họ lúc họ phủ nhận và chế giễu các lời mặt khải của Allah thì các ngươi cũng giống như họ, các ngươi sẽ làm trái lệnh Allah bởi vì các ngươi đã cãi lệnh Ngài mà ngồi cùng với họ. Quả thật, những kẻ biểu hiện Islam ở bên ngoài nhưng bên trong thì hoàn toàn vô đức tin thì sẽ được Allah tập hợp tất cả bọn chúng trong Hỏa Ngục vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(4,141,'4:141','Những kẻ Munafiq luôn dòm ngó và theo dõi các ngươi xem các ngươi đạt được điều tốt hay gặp phải điều xấu. Khi các ngươi được Allah phù hộ chiến thắng và thu về chiến lợi phẩm thì chúng bảo các ngươi: chẳng phải chúng tôi đã cùng phe với các người đó sao hầu muốn được chia phần từ chiến lợi phẩm. Và khi những người vô đức tin giành lấy chiến thắng thì chúng lại nói với họ: chẳng phải chúng tôi đã ủng hộ và hậu thuẫn các người để các ngươi giành lấy chiến thắng từ những người có đức tin đó sao?! Rồi đây vào Ngày Phán Xét Allah sẽ xét xử giữa tất cả các ngươi, Ngài sẽ ban thưởng cho những người có đức tin Thiên Đàng, còn những người Munafiq sẽ bị đày xuống tận đáy của Hỏa Ngục. Quả thật, Allah không bao giờ để những kẻ vô đức tin thắng được những người có đức tin, Ngài sẽ ban kết cục tốt đẹp của những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(4,142,'4:142','Quả thật, những kẻ Munafiq cứ đánh lừa Allah bằng cảnh biểu hiện Islam ở bên ngoài còn bên trong thì không hề tin tưởng, chúng đánh lừa Allah nhưng thật ra chúng bị Allah đánh lừa, Ngài bảo toàn mạng sống của chúng trong khi Ngài biết sự vô đức tin của chúng bởi vì Ngài đã chuẩn bị cho chúng một sự trừng phạt đau đớn ở cõi Đời Sau. Những kẻ Munafiq mỗi khi dâng lễ nguyện Salah thì chúng lễ nguyện Salah trong bộ dạng uể oải và nặng nề, chúng chỉ muốn phô trương cho thiên hạ thấy, và chúng không hề tưởng nhớ Allah trừ phi muốn những người có đức tin nhìn thấy.');
INSERT INTO "vn_mokhtasar" VALUES(4,143,'4:143','Những kẻ Munafiq cứ lưỡng lự và không biết thuộc phe bên nào, chúng cứ lưng chừng giữa bên này và bên nọ, chúng biểu hiện đức tin ra ngoài khi ở cùng với những người có đưc tin những bên trong là ở cùng với những kẻ vô đức tin. Và ai bị Allah làm cho lệch lạc thì Ngươi (Muhammad) sẽ không bao giờ tìm thấy một con đường để hướng dẫn y khỏi sự lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(4,144,'4:144','Hỡi những người có đức tin nơi Allah và đi theo vị Thiên Sứ của Ngài, các ngươi chớ nhận lấy những kẻ vô đức tin làm những người bảo hộ của các ngươi thay vì những người có đức tin, nếu các ngươi làm thế thì các ngươi không có lý do gì để tránh sự trừng phạt của TA dành cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,145,'4:145','Quả thật những kẻ Munafiq sẽ bị Allah đày xuống tận đáy của Hỏa Ngục vào Ngày Phục Sinh, và Ngươi (Muhammad) sẽ không bao giờ tìm thấy một vị cứu tinh nào can thiệp cho chúng khỏi sự trừng phạt đó.');
INSERT INTO "vn_mokhtasar" VALUES(4,146,'4:146','Ngoại trừ những ai trở về với Allah bằng sự sám hối, họ ăn năn hối cải cho sự giả tạo đức tin trước đó của họ, họ cải thiện tấm lòng của họ, họ bám chặt lấy giao ước của Allah, họ thành tâm thờ phượng và tuân thủ mệnh lệnh của Ngài không có bất cứ sự phô trương nào, những người này sẽ là những người được mô tả chung với những người có đức tin ở trên thế gian và ở cõi Đời Sau. Và rồi đây Allah sẽ ban cho những người có đức tin một phần thưởng vô cùng to lớn.');
INSERT INTO "vn_mokhtasar" VALUES(4,147,'4:147','Nếu các ngươi biết ơn Allah và tin nơi Ngài thì Ngài sẽ không trừng phạt các ngươi bởi Ngài là Đấng Nhân Từ và Khoan Dung. Ngài chỉ trừng phạt các ngươi bởi những tội lỗi của các ngươi mà thôi, nếu các ngươi sửa đổi hành vi của mình và biết tạ ơn ân huệ của Ngài và tin nơi Ngài từ ngoài vào trọng thì Ngài sẽ không bao giờ trừng phạt các ngươi. Quả thật Allah là Đấng Tri Ân đối với ai thừa nhận ân huệ của Ngài, Ngài sẽ ban thưởng cho họ thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(4,148,'4:148','Allah không thích việc mang những tội lỗi ra la lối trước công chúng, Ngài ghét và cảnh báo về sự trừng phạt cho hành vi đó. Ngoại trừ ai bị đối xử bất công thì y được phép mang điều xấu đó ra trước công chúng nhằm vạch trần kẻ làm điều bất công và đòi sự công bằng cho bản thân mình. Tuy nhiên, người bị đối xử bất công chịu đựng và kiên nhẫn thì điều đó tốt hơn việc mang ra công khai trước công chúng. Quả thật, Allah là Đấng Hằng Nghe tất cả lời lẽ của các ngươi và Ngài Hằng Biết tâm niệm và lòng dạ của các ngươi; bởi thế, các ngươi hãy cẩn trọng trong lời ăn tiếng nói của mình.');
INSERT INTO "vn_mokhtasar" VALUES(4,149,'4:149','Nếu các ngươi công khai lời lẽ hoặc hành động tốt đẹp hay các ngươi che giấu nó hoặc các ngươi bỏ qua và xí xóa cho những ai có hành vi xấu với các ngươi thì các ngươi hãy biết rằng Allah là Đấng Hằng Xí Xóa và Bao Dung, Đấng Toàn Năng trên mọi thứ, Ngài sẽ xí xóa và bao dung cho các ngươi qua những phẩm chất đạo đức của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(4,150,'4:150','Quả thật, những người phủ nhận Allah và các vị Thiên Sứ của Ngài, muốn phân biệt giữa Allah và các vị Thiên Sứ của Ngài, họ tin nơi Ngài nhưng phủ nhận các vị Thiên Sứ của Ngài, họ nói: "Chúng tôi tin một số vị Thiên Sứ này và phủ nhận một số vị Thiên Sứ khác", họ muốn đi trên con đường giữa sự vô đức tin và có đức tin và tưởng rằng nó sẽ cứu rỗi họ.');
INSERT INTO "vn_mokhtasar" VALUES(4,151,'4:151','Chúng là những kẻ tin một số vị Thiên Sứ và phủ nhận một số vị khác là những kẻ vô đức tin đích thực bởi lẽ ai phủ nhận toàn bộ các vị Thiên Sứ hoặc một số vị thì y đã phủ nhận Allah và các Thiên Sứ của Ngài. TA đã chuẩn bị cho những kẻ vô đức tin một sự trừng phạt đầy nhục nhã và đau đớn vào Ngày Phục Sinh, một hình phạt dành cho chúng bởi tội ngạo mạn không chịu tin nơi Allah và các Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,152,'4:152','Những ai có đức tin nơi một mình Allah, không tổ hợp bất kỳ ai (vật gì) với Ngài, tin nơi toàn bộ các vị Thiên Sứ của Ngài, không phân biệt giữa các vị Thiên Sứ giống như những kẻ vô đức tin thì họ sẽ được Allah ban trả cho công lao của họ với phần thưởng vĩ đại, một sự ân thưởng cho đức tin Iman và việc làm ngoan đạo và thiện tốt của họ. Quả thật, Allah là Đấng hằng tha thứ và khoan dung với những bề tôi quay đầu sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,153,'4:153','Những người Do Thái đòi Ngươi - hỡi vị Thiên Sứ Muhammad - mang xuống cho chúng một Kinh Sách trọn vẹn từ trên trời xuống. Điều này cũng đã xảy ra với Nabi Musa, chúng cũng đã đòi Musa giống như thế, chúng đòi Musa một điều lớn lao cái mà chúng đòi Ngươi, đó là chúng đòi Musa cho chúng nhìn thấy Allah tận mắt. Nhưng ngay sau khi chúng đòi thì một sự trừng phạt đã giáng xuống bọn chúng, Allah đã cho lưỡi tầm sét đánh chúng bất tỉnh. Nhưng sau khi Allah làm cho chúng sống trở lại và cho chúng chứng kiến nhiều bằng chứng rõ rệt chứng minh sự hiện hữu của Ngài thì chúng lại thờ phượng ngoài Allah một thần linh khác, đó là một con bò con được đúc do chính bàn tay của chúng. Nhưng TA vẫn lượng thứ cho chúng và TA đã ban cho Musa một thẩm quyền rõ ràng trên đám dân của y.');
INSERT INTO "vn_mokhtasar" VALUES(4,154,'4:154','Và TA đã dựng lên một ngọn núi cao khỏi đầu của chúng dọa chúng sợ để bắt chúng cam kết lời giao ước mà thực hành và chấp hành theo. Và TA đã phán bảo chúng rằng hãy đi vào cánh cửa của ngôi đến Al-Maqdis (Jerusalem - Palestine) quỳ mọp đầu xuống phủ phục TA nhưng chúng lại quay lưng bước lui vào; và khi TA phán bảo chúng chớ đi đánh bắt cá vào ngày thứ bảy thì chúng lại gian lận ngoan cố đánh bắt cá cho bằng được. Và TA đã giao ước với chúng bằng một sự giáo ước long trọng và mạnh bạo tức nếu chúng vị phạm giáo ước thì chúng sẽ bị trừng phạt nặng nề.');
INSERT INTO "vn_mokhtasar" VALUES(4,155,'4:155','TA đã đuổi chúng ra khỏi lòng thương xót và hồng phúc của TA bởi việc chúng đã bội ước, bởi chúng đã phủ nhận các lời mặc khải của TA, bởi chúng đã giết hại các vị Nabi của TA, và bởi lời của chúng với Muhammad: "trái tim của chúng tôi đã đóng lại không hiểu được những gì ngươi nói" nhưng sự việc không phải như chúng nói mà chính Allah đã niêm kín trái tim của chúng để chúng không thể chạm đến điều tốt khiến chúng chẳng có đức tin ngoài một niềm tin vô cùng ít ỏi không mang lại giá trị gì cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,156,'4:156','Và TA đã đuổi chúng ra khỏi lòng thương xót và hồng phúc của TA bởi sự vô đức tin của chúng và bởi chúng đã vu oan cho nữ Mar-yam (mẹ của Nabi Ysa) làm chuyện Zina.');
INSERT INTO "vn_mokhtasar" VALUES(4,157,'4:157','TA đã nguyền rủa chúng bởi lời giả dối của chúng: "quả thật, chúng tôi đã giết Masih Ysa con trai của Mar-yam, Sứ giả của Allah" nhưng thực chất chúng không hề giết được Y cũng không hề đóng đinh Y trên cây thánh giá như lời chúng đã hô hào. Người mà chúng giết và đóng đinh trên cây thánh giá chỉ là một người được Allah hoán đổi hình hài giống với hình hài của Ysa nhưng chúng cứ tưởng chúng đã giết được Ysa. Những kẻ thuộc những người Do Thái hô hào nói rằng chúng đã giết chết Ysa và những kẻ tin lời của chúng từ những người Thiên Chúa, cả hai bọn chúng đều chỉ phỏng đoán chứ không hề có sự hiểu biết chắc chắn nào. Nói túm lại, chúng thực sự không giết được Ysa và cũng không hề đóng đinh Y trên cây thánh giá.');
INSERT INTO "vn_mokhtasar" VALUES(4,158,'4:158','Không, chúng không giết được Ysa và chúng không đóng đinh được Y mà chính Allah đã cứu Y và đưa Y lên cùng với Ngài, Ngài đã đưa Y lên bằng thân xác lẫn linh hồn của Y. Quả thật, Allah là Đấng Toàn Năng trong vương quyền, không có gì có thể chi phối được Ngài, và Ngài là Đấng Sáng Suốt trong việc điều hành, chi phối và định đoạt sự việc.');
INSERT INTO "vn_mokhtasar" VALUES(4,159,'4:159','Chắc chắn rồi đây tất cả mọi người của dân Kinh Sách đều sẽ tin nơi Ysa sau khi Y giáng thế trở lại vào cuối thời đại sau cùng của thế gian trước khi Y chết. Và vào Ngày Phục Sinh, Ysa sẽ là người làm chứng cho tất cả việc làm của chúng, Y sẽ xác nhận đâu là việc làm đúng với giáo lý của Allah và đâu là việc làm sai trái.');
INSERT INTO "vn_mokhtasar" VALUES(4,160,'4:160','Vì sự sai trái của người Do Thái nên TA đã cấm chúng một số loại thực phẩm tốt lành mà trước kia chúng đã từng được cho phép dùng. TA đã cấm chúng tất cả loài vật có móng chân liền (có học giả thì cho rằng đó là lạc đà, đà điểu, ngỗng, vịt; có học giả thì cho rằng đó là lạc đà và đà điểu), và TA cấm chúng dùng mỡ bò và mỡ dê ngoại trừ mỡ dính vào lưng và ruột của con vật. Và vì chúng luôn cản trở bản thân chúng và thiên hạ đi theo con đường của TA nên TA cản trở chúng để trừng phạt chúng.');
INSERT INTO "vn_mokhtasar" VALUES(4,161,'4:161','Vì chúng đã giao dịch kinh doanh bằng hình thức vay lãi sau khi Allah đã nghiêm cấm chúng và vì chúng đã chiếm đoạt tài sản của thiên hạ nên TA đã chuẩn bị cho chúng - những kẻ vô đức tin một sự trừng phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(4,162,'4:162','Tuy nhiên, những người có kiến thức vững chắc trong đám người Do Thái và những người có đức tin đều tin tưởng nơi những gì được Allah mặc khải xuống cho Ngươi - hỡi Thiên Sứ Muhammad - từ Kinh Qur''an và tin tưởng nơi những gì được Ngài mặc khải xuống trong các Kinh Sách cho các vị Thiên Sứ ở những thời trước Ngươi như Kinh Tawrah, Injil, những người dâng lễ nguyện Salah, những người xuất Zakah từ nguồn tài sản của họ, những người tin nơi Allah là một Thượng Đế duy nhất, một Đấng được thờ phượng duy nhât, Ngài không có tổ hợp ngang vai, và những người tin nơi Ngày Phán Xét cuối cùng. Những người mang những mô tả này là những người sẽ được TA ban cho họ một phẫn thưởng vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(4,163,'4:163','Hỡi Thiên Sứ Muhammad! Quả thật, TA đã mặc khải cho Ngươi giống như việc TA đã mặc khải cho các vị Nabi trước Ngươi. Ngươi thực sự không phải là người với vai trò mới mẻ gì cả so với các vị Thiên Sứ trước Ngươi. Quả thật TA đã mặc khải cho Ngươi giống như TA đã mặc khải cho Nuh (Noah), và các vị Nabi thời sau Y; TA đã mặc khải cho Ibrahim (Abraham), con trai của Y Isma-''il và Ishaq; TA đã mặc khải cho Y''aqub (Jacob) con trai của Ishaq; và TA đã mặc khải cho các bộ lạc Isra-el (họ là những bộ lạc thuộc đứa con của Yaqub); và TA đã ban cho xuống cho Dawood (David) Kinh Zabur (tức Kinh Thi Thiên).');
INSERT INTO "vn_mokhtasar" VALUES(4,164,'4:164','Có các vị Thiên Sứ, TA đã kể câu chuyện của họ cho Ngươi trong Qur''an nhưng có một số vị khác TA đã không kể cho Ngươi về các chuyện của họ. TA không đề cập họ trong Qur''an là do có ngụ ý của TA; và TA đã phán truyền lời mặc khải của TA trực tiếp cho Musa mà không có bất cứ sự trung gian nào, TA phán trực tiếp với Musa như là một vinh dự của TA dành cho Y.');
INSERT INTO "vn_mokhtasar" VALUES(4,165,'4:165','TA đã cử các vị Thiên Sứ đến làm những người báo tin mừng về những phần thưởng nồng hậu cho những ai tin nơi TA đồng thời làm những người cảnh báo về sự trừng phạt đau đớn dành cho những kẻ vô đức tin. Mục đích để nhân loại không có cớ để biện minh cho việc sai trái của họ trước Allah sau khi các vị Thiên Sứ của Ngài đã được cử phái đến với họ. Quả thật, Allah là Đấng Toàn Năng trong chi phối và điều hành và Ngài là Đấng Sáng Suốt và Anh Minh trong định đoạt và Chỉ Thị.');
INSERT INTO "vn_mokhtasar" VALUES(4,166,'4:166','Nếu những người Do Thái phủ nhận Ngươi - Thiên Sứ Muhammad - thì quả thật Allah tin Ngươi và làm chứng cho những gì được ban xuống cho Ngươi từ Qur''an là sự thật. Bởi chính Ngài đã ban Nó xuống cho Ngươi nhằm để cho các bề tôi của Ngài chiếu theo đó mà thực hiện những điều Ngài yêu thích và hài lòng cũng như tránh xa những gì Ngài ghét bỏ. Và các Thiên Thần củng tin và làm chứng cho Ngươi và cho những gì Ngươi mang đến. Và cho dù không ai làm chứng thì một mình Allah cũng đã đủ làm chứng cho sự trung thực của Ngươi rồi.');
INSERT INTO "vn_mokhtasar" VALUES(4,167,'4:167','Những ai phủ nhận sứ mạng Nabi của Ngươi và ngăn cản thiên hạ đi theo Islam thì quả thật họ đã rời quá xa khỏi chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(4,168,'4:168','Những ai phủ nhận Allah và các Thiên Sứ của Ngài và bất công với chính bản thân họ bởi việc duy trì trên sự vô đức tin của họ thì Allah sẽ không tha thứ cho họ, và Ngài cũng sẽ không hướng dẫn họ đến với con đường cứu rỗi khỏi sự trừng phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(4,169,'4:169','Những kẻ phủ nhận Allah và các Thiên Sứ của Ngài và luôn duy trì sự vô đức tin của mình thì họ chỉ có một con đường duy nhất, đó là con đường đến với Hỏa Ngục, họ sẽ phải sống trong đó mãi mãi và mãi mãi. Và điều đó với Allah vô cùng đơn gian và dễ dàng.');
INSERT INTO "vn_mokhtasar" VALUES(4,170,'4:170','Hỡi những người có đức tin! Quả thật Thiên Sứ Muhammad đến với các ngươi mang theo sự chỉ đạo và tôn giáo chân lý từ nơi Allah, Thượng Đế của các ngươi. Do đó, các ngươi cần phải tin nơi Y, như thế mới tốt cho các ngươi ở trên cõi trần và cõi Đời Sau. Còn nếu các ngươi phủ nhận Allah và phủ nhận Y thì thật ra Allah vẫn là Đấng Giàu Có, Ngài không cần đến đức tin của các ngươi và sự vô đức tin của các ngươi cũng không làm ảnh hưởng gì đến Ngài, Ngài vẫn là Đấng Thống Trị và Nắm Quyền trên tất cả vạn vật trong các tầng trời và trái đất. Và Allah là Đấng Hằng Biết ai đáng được hướng dẫn và y sẽ được hướng dẫn, và ai không đáng được hướng dẫn và sẽ không được hướng dẫn, và Ngài luôn Anh Minh và Sáng Suốt trong lời nói, hành động, định đoạt và ban hành.');
INSERT INTO "vn_mokhtasar" VALUES(4,171,'4:171','Hỡi vị Thiên Sứ của Allah! Ngươi hãy nói với những người Thiên Chúa, dân của Kinh Injil: các ngươi chớ thái quá trong tôn giáo của các ngươi, các ngươi chớ đừng nói gì về Ysa ngoại trừ đó là sự thật. Quả thật, Masih Ysa con trai của Mar-yam chỉ là vị Thiên Sứ của Ngài được Ngài cử phái đến cho nhân loại, Ngài tạo hóa Y bằng lời phán "Hãy thành" của Ngài được truyền đến Mar-yam qua đại Thiên Thần Jibril, và linh hồn của Y là từ nơi Allah được Jibril mang xuống thổi vào nữ ta theo lệnh của Ngài. Bởi thế, các ngươi cần phải tin nơi Allah và tất cả các vị Thiên Sứ của Ngài, không phân biệt và kỳ thị một ai trong số họ, các ngươi chớ nói "chúa ba ngôi", các ngươi phải ngừng ngay lời nói sàm bậy đó của các ngươi, có như thế thì mới tốt cho các ngươi trên cõi đời này và cõi Đời Sau. Quả thật, Allah là Thượng Đế duy nhất, Ngài không có đối tác ngang vai và cũng không có con, Ngài không cần có con bởi Ngài là Đấng Tạo Hóa, mọi vật trong các tầng trời và mọi vật trên trái đất đều là tạo vật của Allah, và chỉ một mình Ngài là Đấng cai quản, chi phối và điều hành mọi vật trong các tầng trời và mọi vật trên trái đất.');
INSERT INTO "vn_mokhtasar" VALUES(4,172,'4:172','Masih Ysa con trai của Mar-yam không hề cảm thấy e ngại khi làm người bề tôi của Allah và Y không hề phủ nhận mình là bề tôi của Ngài; các Thiên Thần cận kề Allah cũng không e ngại cho bổn phận bề tôi của họ đối với Ngài, họ không hề đưa cao họ lên khỏi vị trí bề tôi của họ, vậy cớ sao các ngươi lại nhận lấy Ysa làm Thượng Đế, sao những kẻ đa thần lại nhận lấy các Thiên Thần làm những thần linh của họ? Và những ai e ngại không muốn thờ phượng Allah, những ai ngạo mạn phủ nhận Ngài thì vào Ngày Phục Sinh tất cả họ vẫn phải bị Allah triệu tập và Ngài sẽ trừng phạt họ một cách thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(4,173,'4:173','Những ai có đức tin nơi Allah và tin nơi các vị Thiên Sứ của Ngài đồng thời thành tâm làm điều thiện tốt và ngoan đạo vì Allah theo đúng yêu cầu và chỉ định của giáo lý mà Ngài ban hành thì họ sẽ được ban thưởng tương xứng với các việc làm của họ, không những được ban thưởng tương xứng mà còn được ban thêm từ sự hồng phúc và sự tử tế của Ngài. Ngược lại, những ai phủ nhận sự thờ phượng Allah, không chịu tuân phục Ngài vì sự ngạo mạn, tự cao tự đạị của họ thì họ sẽ bị Ngài trừng phạt bằng một sự trừng phạt đau đớn tột cùng, và họ sẽ không tìm thấy ngoài Allah vị bảo hộ hay vị cứu tinh nào.');
INSERT INTO "vn_mokhtasar" VALUES(4,174,'4:174','Này hỡi nhân loại! Quả thật một bằng chứng xác thực - một bằng chứng cắt đứt mọi sự biện minh và xóa hết mọi ngờ vực - từ Thượng Đế của các ngươi đã đến với các ngươi, đó là Muhammad; và TA đã ban xuống cho các ngươi nguồn Ánh Sáng rõ rệt, đó là Qur''an.');
INSERT INTO "vn_mokhtasar" VALUES(4,175,'4:175','Những người có đức tin nơi Allah và bám lấy Qur''an, Kinh sách được ban xuống cho vị Nabi của họ, thì họ sẽ được thương xót thu nhận vào Thiên Đàng của Ngài và Ngài sẽ ban thêm cho họ phần ân thưởng, sẽ nâng họ lên cấp bậc cao ở nơi Ngài và sẽ phù hộ họ trên con đường ngay chính để trở về với Ngài, và đó là con đường dẫn đến những ngôi vườn Thiên Đàng - nơi của cuộc sống vĩnh hằng với niềm hạnh phúc và sự tận hưởng bất tận.');
INSERT INTO "vn_mokhtasar" VALUES(4,176,'4:176','Hỡi Thiên Sứ của Allah! Họ hỏi Ngươi về một vấn đề liên quan đến việc chia tài sản của một người chết đi nhưng y không có con cái và cha mẹ. Ngươi hãy bảo họ: Allah trình bày luật định này như sau: nếu một người chết đi nhưng y không có con cái cũng không có cha mẹ mà chỉ có một chị (em gái) ruột hoặc một người chị (em gái) cùng cha khác mẹ thì người chị (em gái) đó sẽ hưởng một nửa tài sản mà y để lại; nếu một người chết đi và y không có con cái lẫn cha mẹ mà chỉ có một người anh (em trai) ruột hoặc một người anh (em trai) cùng cha khác mẹ thì người anh (em trai) đó sẽ hưởng tòa bộ tài sản mà y để lại nếu như không có ai cùng thừa kế theo mức định, còn nếu có người thừa kế theo mức định thì người anh (em trai) đó hưởng phần còn lại sau khi đã chia cho người hưởng thừa kế theo mức định; nếu người không cha mẹ, con cái chết đi mà có chị (em gái) ruột hoặc cùng cha khác mẹ nhiều hơn một thì các chị (em gái) đó cùng hưởng 3 (hai phần ba) tài sản y để lại; còn nếu người không cha mẹ, con cái chết đi có các anh, chị (em gái, em trai) thì nam giới hưởng hai phần của nữ giới. Đó là những gì mà Allah đã trình bày về luật định chia tài sản của trường hợp một người chết đi nhưng không có cha mẹ và con cái, Ngài trình bày rõ ràng và cụ thể như thế để các ngươi không lầm lạc trong vấn đề. Và Allah là Đấng thấu hiểu và am tường hết mọi sự việc, không có bất cứ điều gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,1,'5:1','Hỡi những người có đức tin! Các ngươi hãy thực hiện và hoàn thành tất cả các giao ước giữa các ngươi với Đấng Tạo Hóa của các ngươi và các giao ước giữa các ngươi với tạo vật của Ngài (nhân loại). Quả thật, vì thương xót các ngươi, Allah đã cho phép các ngươi dùng thịt các loại gia súc (lạc đà, bò, dê) trừ những loại mà Ngài đã cấm các ngươi được đọc ra cho các ngươi nghe. Các ngươi không được phép săn bắt trong lúc các ngươi đang trong tình trạng Ihram làm Hajj hay Umrah. Quả thật, Allah định đoạt bất cứ điều gì Ngài muốn, Ngài muốn cấm điều này cho phép điều kia là tùy ý Ngài, nhưng tất cả đều là sự Sáng Suốt và Anh Minh của Ngài, không ai có quyền và khả năng can thiệp và chống đối sự định đoạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,2,'5:2','Hỡi những người có đức tin! Các ngươi không được phạm vào các biểu hiệu mà Ngài đã ra lệnh cho các ngươi phải tôn vinh và xem trọng chúng; các ngươi không được phạm những điều cấm của tình trạng Ihram trong thời gian làm Hajj chẳng hạn như mặc quần áo, săn bắt thú; các ngươi không được giết chóc trong những tháng cấm kỵ, đó là tháng: Zdul-Qa''dah, Zdul-Hijjah, Muharram và Rajab; các ngươi không được phạm đến những con vật tế cho nghi thức của Hajj hoặc ngăn cản chúng đến chỗ mà chúng được giết tế; các ngươi không được phạm đến những con vật đèo vòng cổ để làm dấu chuẩn bị cho việc giết tế trong nghi thứ Hajj; các ngươi không được quấy rầy những người đến viếng ngôi nhà của Allah - Alharam để tìm kiếm lợi lộc từ việc kinh doanh mua bán cũng như sự hài lòng của Allah. Khi nào các ngươi đã chấm dứt tình trạng Ihram trong Hajj hay Umrah và các ngươi đã ra khỏi vùng đất Alharam thì các ngươi được săn bắt nếu các ngươi muốn. Các ngươi chớ để cho lòng thù hằn đến nhóm nào đó khiến các ngươi cản trở họ đến thăm viếng Masjid Alharam một cách bất công. Các ngươi hãy giúp đỡ nhau trên những việc được sắc lệnh và giúp đỡ nhau từ bỏ những điều bị nghiêm cấm; các ngươi hãy kính sợ Allah mà tuân thủ mệnh lệnh của Ngài, tránh xa việc làm trái lệnh Ngài bởi quả thật Allah là Đấng trừng phạt nghiêm khắc đối với ai làm trái lệnh Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,3,'5:3','Allah cấm các ngươi ăn các con thú bị chết không được giết theo cách thức của Islam (nhân danh Allah và cắt cổ), Ngài cấm các ngươi ăn máu Masfuh (máu tuông ra khỏi cơ thể), cấm các ngươi ăn thịt heo, cấm các ngươi ăn những con vật được giết nhân danh ai (vật gì) khác ngoài Allah, cấm các ngươi ăn con vật bị chết ngạt, con vật chết do đánh đập, chết do rơi từ trên cao xuống, chết do húc hay ẩu đả nhau, cấm các ngươi ăn những con vật bị thú dữ (sư tử, cọp, sói, ...) săn được trừ phi chúng còn sống và được các ngươi kịp thời cắt cổ theo nghi thức của Islam thì các ngươi được phép ăn, cấm các ngươi ăn những món vật dược dâng cúng các bục tượng, cấm các ngươi chia phần cho nhau bằng hình thức xin xăm. Tất cả những điều cấm được nói trên, ai vi phạm đều là hành vi bất tuân Allah. Ngày hôm nay, những kẻ vô đức tin đã chán nản về việc bỏ đạo Islam của các ngươi vì chúng thấy Islam đã vững chắc trong các ngươi, bởi thế, các ngươi chớ sợ chúng mà hãy sợ một mình TA. Ngày hôm nay, TA đã hoàn tất cho các ngươi tôn giáo Islam, đã hoàn thiện cho các ngươi ân huệ của TA, TA đã chọn Islam làm tôn giáo cho các ngươi nên TA sẽ không chấp nhận bất cứ tôn giáo nào khác ngoài Islam. Ai vì quá đói bắt buộc phải ăn những món vật cấm để duy trì sự sống chứ không phải cố tình vi phạm thì y không bị bắt tội; quả thật Allah là Đấng Hằng Tha Thứ và Nhân Từ.');
INSERT INTO "vn_mokhtasar" VALUES(5,4,'5:4','Hỡi vị Thiên Sứ của Allah! Những người bạn đạo của Ngươi hỏi Ngươi Allah cho phép họ ăn những món thực phẩm nào? Ngươi hãy nói cho họ biết: Allah cho phép các ngươi ăn những thứ tốt lành và thanh sạch, Ngài cho phép các ngươi ăn những con vật mà các ngươi đã dùng những con thú săn của các ngươi được các ngươi huấn luyện như chó, báo, chim ưng săn được theo cách mà Allah đã dạy các ngươi, các ngươi hãy ăn các con vật mà các con thú săn của các ngươi bắt được ngay cả khi chúng đã chết do thú săn của các ngươi miễn sao các ngươi đã nhân danh Allah lúc các ngươi thả thú săn của các ngươi đi. Các ngươi hãy kính sợ Allah trong việc thực thi và chấp hành các mệnh lệnh của Ngài, chấm dứt những điều Ngài nghiêm cấm bởi quả thật Allah sẽ nhanh chóng thanh toán các việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(5,5,'5:5','Ngày hôm nay, Allah cho phép các ngươi dùng những thực phẩm tốt lành và thanh sạch, các ngươi được phép ăn những con vật do dân Kinh Sách từ Do Thái và Thiên Chúa giết và ngược lại những con vật do các ngươi giết cũng được phép đối với họ. Các ngươi được phép cưới những phụ nữ tự do có đức tin và những phụ nữ tự do thuộc những người được ban cho Kinh Sách trước các ngươi (Do Thái, Thiên Chúa) với điều kiện các ngươi phải trao cho họ tiền cưới Mahr, và các ngươi cưới họ là để họ làm vợ của các ngươi chứ không phải để thỏa mãn tình dục của các ngươi hay chỉ để làm người tình vụng trộm. Người nào phủ nhận những gì được Allah ban hành thành giáo luật cho các bề tôi của Ngài thì kẻ đó đã làm mất giá trị các việc làm thiện tốt và ngoan đạo của họ và vào Đời Sau y sẽ thuộc những người thất bại thảm hại vì sẽ phải vào Hỏa Ngục và sống đời đời trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(5,6,'5:6','Hỡi những người có đức tin! Khi các ngươi muốn dâng lễ nguyện Salah, nếu các ngươi trong tình trạng tiểu Hadath (sau khi đại tiện, tiểu tiện hoặc xì hơi) thì các ngươi hãy làm Wudu'' bằng cách rửa mặt, rửa hai tay từ đầu ngón tay đến cùi chỏ, dùng tay thấm nước rồi lau vuốt toàn đầu, và cuối cùng là rửa đôi bàn chân từ đầu ngón chân đến mắt cá chân; còn nếu các ngươi trong tình trạng đại Hadath (Junub: sau khi ăn nằm với vợ) thì các ngươi phải tắm. Trường hợp các ngươi bị bệnh ngại việc dùng nước sẽ làm bệnh tình thêm nặng hơn hoặc lâu khỏi hơn, hay các ngươi đang là những người đi đường xa, hoặc khi các ngươi trong tình trạng tiểu Hadath hay đại Hadith, và các ngươi không tìm thấy nước sau khi đã cố gắng tìm kiếm thì các ngươi hãy tẩy sạch thân thể các ngươi theo hình thức Tayammum bằng cách tìm đến chỗ đất bụi (cát) sạch, đánh hai bàn tay lên bề mặt của nó rồi lau mặt và tay của các ngươi. Quả thật, Allah không muốn gây khó khăn cho các ngươi về giáo luật của Ngài trong việc bắt các ngươi phải dùng nước khi các ngươi gặp trở ngại nên Ngài qui định cho các ngươi một hình thức Tayammum để thay thế. Tất cả là vì Ngài muốn tẩy sạch các ngươi cũng như để hoàn tất ân huệ của Ngài đối với các ngươi mong rằng các ngươi biết tạ ơn Ngài và không phủ nhận các ân huệ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,7,'5:7','Các ngươi hãy nhớ lại ân huệ của Allah ban cho các ngươi qua việc hướng dẫn các ngươi đến với Islam và các ngươi cũng hãy nhớ lại việc các ngươi giao ước với Ngài với lời nói của các ngươi rằng: chúng tôi nguyện nghe và vâng lời Thiên Sứ Muhammad dù thích hay không thích. Các ngươi hãy kính sợ Allah mà chấp hành các mệnh lệnh của Ngài, tránh xa các điều nghiêm cấm, bởi quả thật Ngài hằng biết những gì ẩn chứa trong trái tim của các ngươi, không có điều gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,8,'5:8','Hỡi những người có đức tin nơi Allah và nơi các vị Thiên Sứ của Ngài, vì Allah các ngươi hãy hiên ngang đứng lên duy trì công lý, hãy vì Ngài mà đứng lên làm chứng cho lẽ công bằng. Các ngươi không được để cho lòng hận thù nơi bản thân của các ngươi đối với một nhóm người nào đó thôi thúc các ngươi hành động bất công, sự công bằng được yêu cầu đối với bạn bè lẫn kẻ thù, cho nên các ngươi phải luôn công bằng vì điều đó gần với lòng Taqwa (kính sợ) của các ngươi đối với Allah. Các ngươi hãy kính sợ Allah mà thực hiện các mệnh lệnh của Ngài, tránh xa những điều cấm của Ngài, các ngươi hãy biết rằng Allah luôn thấu hiểu và thông toàn tất cả mọi việc làm của các ngươi, Ngài sẽ thanh toán và trả công lao xứng đáng cho từng việc làm của các ngươi, không có bất cứ điều gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,9,'5:9','Allah đã hứa - và Ngài không bao giờ thất hứa - với những người có đức tin nơi Ngài và nơi các vị Thiên Sứ của Ngài đồng thời siêng năng hành đạo và làm việc thiện tốt rằng họ sẽ được tha thứ tội lỗi và sẽ được ban thưởng một phần thưởng vô cùng to lớn, đó là được vào Thiên Đàng của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(5,10,'5:10','Đối với những ai vô đức tin nơi Allah, phủ nhận các lời mặc khải của Ngài thì họ sẽ làm bạn với Hỏa Ngục, họ sẽ vào sống trong đó đời đời, một sự trừng phạt dành cho tội vô đức tin và phủ nhận của họ.');
INSERT INTO "vn_mokhtasar" VALUES(5,11,'5:11','Hỡi những người có đức tin! Các ngươi hãy nhớ lại bằng trái tim và chiếc lưỡi của các ngươi về những ân huệ mà Allah đã ban cho các ngươi khi Ngài đã gieo nỗi sợ hãi trong lòng kẻ thù của các ngươi khi chúng có ý định giăng tay của chúng ra hãm hại các ngươi khiến chúng dừng lại và các ngươi được an toàn. Các ngươi hãy kính sợ Allah bằng cách chấp hành mệnh lệnh của Ngài và tránh những điều Ngài nghiêm cấm; và Allah là Đấng Duy Nhất mà những người có đức tin phải phó thác trong việc muốn có được sử cải thiện tốt đẹp trong đạo và đời của họ.');
INSERT INTO "vn_mokhtasar" VALUES(5,12,'5:12','Quả thật, Allah đã nhận lấy giao ước của con cháu Isra-el (với những điều sẽ sớm được đề cập đến dưới đây) và bổ nhiệm mười hai người trong bọn họ làm lãnh đạo, mỗi vĩ lãnh đạo có trách nhiệm và quyền hạn trông coi những ai dưới quyền của mình. Allah đã phán với con cháu Isra-el: quả thật TA sẽ phù hộ và trợ giúp các ngươi nếu các ngươi duy trì lễ nguyện Salah một cách chu đáo, xuất Zakah, tin nơi tất cả các vị Thiên Sứ của TA, không phân biệt giữa họ, tôn vinh họ và trợ giúp cho họ, và các ngươi chi dùng tài sản cho con đường tốt đẹp. Nếu các ngươi làm thế thì ngoài việc phù hộ và trợ giúp cho các ngươi thì TA còn xóa tội lỗi cho các ngươi và vào Ngày Phục Sinh TA sẽ thu nhận các ngươi vào Thiên Đàng với những ngôi vườn cùng với những tòa lâu đài bên dưới có các dòng sông chảy. Nhưng nếu ai vô đức tin sau khi đã giao ước với Allah thì chắc chắn y đã cố ý lạc khỏi con đường chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(5,13,'5:13','Do chúng bội ước nên TA đã xua đuổi chúng ra khỏi lòng nhân từ của TA và TA đã làm cho trái tim của chúng chai cứng không thể tiếp nhận điều chân lý cũng như sự khuyên răn. Chúng đã bóp méo lời phán của TA trong Kinh Sách khỏi nội dung ý nghĩa nguyên gốc của nó, chúng đã thay đổi ngôn từ và lời lẽ trong đó, chúng suy diễn nó theo ý của chúng, và chúng không làm theo một số điều của lời phán mà chúng đề cập; không ngừng ở đó, Ngươi - Thiên Sứ Muhammad - sẽ thấy chúng vẫn luôn tìm cách lừa dối Allah và những bề tôi có đức tin của Ngài trừ một số ít trong bọn chúng; tuy nhiên, Allah vẫn lượng thứ cho chúng và không bắt tội chúng, bởi đó là lòng tốt của Ngài và Ngài luôn yêu thương những người có lòng tốt và tử tế.');
INSERT INTO "vn_mokhtasar" VALUES(5,14,'5:14','Giống như việc TA đã nhận lấy lời giáo ước với những người Do Thái, TA đã giao ước với những người tự thanh lọc bản thân rằng chúng là những người đi theo Ysa nhưng lại bỏ thực hiện một phần được nói đến trong Kinh Sách được ban xuống cho chúng nên TA đã gieo rắc xung đột và hiềm khích dữ dội giữa chúng cho đến Ngày Phục Sinh làm cho chúng giết chóc và phủ nhận lẫn nhau; và rồi đây TA sẽ cho chúng biết tất cả những điều sai trái mà chúng đã làm và TA sẽ trừng phạt chúng thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(5,15,'5:15','Hỡi người dân Kinh Sách từ những người Do Thái - những người của Kinh Tawrah và những người Thiên Chúa - những người của Kinh Injil, quả thật, vị Thiên Sứ của TA - Muhammad - đã đến với các ngươi, trình bày rõ ràng cho các ngươi nhiều điều mà các ngươi đã che giấu trong Kinh Sách được ban xuống cho các ngươi và Y đã lướt qua nhiều điều thuộc những điều không còn giá trị và hữu dụng. Quả thật, Kinh Qur''an là một Kinh Sách được gởi đến cho các ngươi từ nơi Allah, Nó là nguồn ánh sáng, là Kinh Sách Quang Minh cho tất cả mọi sự việc mà nhân loại cần đến trong cuộc sống đời này và cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(5,16,'5:16','Với Kinh Qur''an, Allah sẽ hướng dẫn những ai đi theo sự hài lòng của Ngài từ đức tin Iman và việc làm thiện tốt đến với con đường bình an khỏi sự trừng phạt của Allah, và đó là con đường dẫn đến Thiên Đàng, con đường mà nó sẽ đưa họ ra khỏi nơi tắm tối của sự vô đức tin và tội lỗi để đến với nguồn ánh sáng và sự tuân lệnh. Allah sẽ phù hộ cho họ vững bước trên con đường Ngay Chính - con đường của Islam.');
INSERT INTO "vn_mokhtasar" VALUES(5,17,'5:17','Quả thật, những ai nói rằng Allah chính là Masih Ysa - con trai của Mar-yam thì chúng là những kẻ vô đức tin. Ngươi - Muhammad - hãy nói với chúng: Ai là người có khả năng cản Allah khi Ngài muốn hủy diệt Masih Ysa con trai của Mar-yam và người mẹ của y và cả những ai sống trên trái đất? Nếu không ai có khả năng ngăn cản điều đó thì đấy là bằng chứng chứng minh không có Thượng Đế nào khác ngoài Allah duy nhất còn tất cả: Ysa con trai của Mar-yam, mẹ của Y và cả nhân loại đều là tạo vật của Allah; bởi lẽ các tầng trời và trái đất và mọi vật giữa trời đất đều thuộc về Ngài, Ngài tạo hóa bất cứ vật gì Ngài muốn và tạo như thế nào tùy ý Ngài; Ysa chỉ là người bề tôi và là vị Thiên Sứ của Ngài, và Allah là Đấng Toàn Năng trên tất cả mọi sự việc.');
INSERT INTO "vn_mokhtasar" VALUES(5,18,'5:18','Những người Do Thái và những người Thiên Chúa đều tự nhận mình là con của Allah, được Ngài yêu quý nhất. Ngươi hãy đáp lại lời của chúng hỡi Thiên Sứ của Allah - Muhammad: Vậy tại sao Allah lại trừng phạt các ngươi bởi tội lỗi mà các ngươi đã làm?! Nếu các ngươi thực sự là con cái yêu quí của Ngài như các ngươi đã khẳng định thì tại sao Ngài lại trừng phạt các ngươi bởi sự giết chóc và biến đổi các ngươi thành động vật (loài khỉ) trên thế gian và sẽ trừng phạt các ngươi ở cõi Đời Sau, bởi lẽ nếu Ngài yêu quý các ngươi thì chắc Ngài đã không và sẽ không trừng phạt các ngươi, nhưng đằng này các ngươi là người phạm tục giống như bao người phàm tục khác. Cho nên, ai làm tốt sẽ được ban thưởng Thiên Đàng còn ai làm điều xấu thì phải chịu sự trừng phạt nơi Hỏa Ngục. Allah muốn tha thứ cho ai tùy ý Ngài bởi hồng phúc của Ngài và Ngài muốn trừng phạt ai tùy ý Ngài bởi sự công bằng của Ngài; và Allah là Đấng duy nhất chi phối và điều hành các tầng trời và trái đất và mọi vật giữa trời đất, và Ngài chính là nơi trở về duy nhất của tất cả tạo vật.');
INSERT INTO "vn_mokhtasar" VALUES(5,19,'5:19','Hỡi những người dân Kinh Sách từ Do Thái và Thiên Chúa! Quả thật, vị Thiên Sứ của TA - Muhammad - đã đến với các ngươi sau một khoảng thời gian TA đã tạm ngưng việc cử phái các vị Thiên Sứ đến với nhân loại để các ngươi khỏi phải nói: chẳng có một vị nào đến báo tin mừng về phần thưởng của Allah cũng như cảnh báo về sự trừng phạt của Ngài. Và quả thật, Muhammad đã đến với các ngươi như một vị báo tin vui đồng thời cảnh báo về sự trừng phạt, và Allah là Đấng Toàn Năng trên tất cả mọi thứ, không có gì có thể làm Ngài bất lực, Ngài có thể cứ phải thêm nhiều vị Thiên Sứ khác nữa nhưng Ngài đã chấm dứt và lấy Muhammad làm vị Thiên Sứ cuối cùng.');
INSERT INTO "vn_mokhtasar" VALUES(5,20,'5:20','Hãy nhớ lại hỡi Thiên Sứ của Allah khi Musa nói với đám dân của Y - Isra-el: Này người dân, các người hãy nhớ lại bằng trái tim và chiếc lưỡi của các ngươi về ân huệ mà Allah đã ban cho các người khi mà Ngài dựng lên các vị Nabi trong các người để kêu gọi các người đến với sự chỉ đạo, và Ngài đã ban cho người vương quyền cai quản bản thân các người sau khi các người đã bị thống trị; và Ngài đã cho các người những ân huệ mà Ngài không cho bất cứ ai khác trên thế gian trong thời đại của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(5,21,'5:21','Musa bảo người dân của Y: Này hỡi người dân, các ngươi hãy đi vào vùng đất tinh khiết (ngôi đền Al-Maqdis và những nơi xung quanh), nơi mà Allah đã hứa cho các ngươi chinh phục được và đánh đuổi những kẻ vô đức tin đi ra khỏi nó; các ngươi chớ quay đầu lùi bước trước những kẻ ngạo mạn để rồi các ngươi sẽ là những người thất bại và thiệt thòi ở đời này và cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(5,22,'5:22','Đám dân của Musa nói với Y: Này Musa, quả thật trong vùng đất thanh sạch đó có một nhóm người rất hùng mạnh và dữ tợn, chúng sẽ ngăn cản chúng tôi tiến vào, chúng tôi sẽ không bao giờ đi vào đó bởi vì chúng không đủ mạnh để đánh chúng, nhưng nếu chúng đi ra khỏi nơi đó thì chúng tôi sẽ đi vào.');
INSERT INTO "vn_mokhtasar" VALUES(5,23,'5:23','Có hai người đàn ông trong số những người bạn của Musa thuộc những người kính sợ Allah và sợ sự trừng phạt của Ngài, Allah đã ban cho hai người họ ân huệ bằng sự phù hộ hai người họ biết tuân lệnh Ngài, hai người họ kêu gọi người dân thực hiện theo lệnh của Musa: Các người hãy tiến vào đánh những kẻ ngạo mạn từ lối cổng thành đi vào, nếu các người đã mở được cổng thành thì các người sẽ vào đó được dưới sự cho phép của Allah, và các ngươi sẽ giành chiến thắng bởi lời hứa giúp các người chiến thắng từ nơi Allah. Và những người có đức tin phải phó thác cho Allah, Đấng Tối Cao.');
INSERT INTO "vn_mokhtasar" VALUES(5,24,'5:24','Đám dân Isra-el của Musa không nghe theo lệnh Người, họ nói với Musa: quả thật chúng tôi sẽ không bao giờ đi vào thành khi những kẻ hùng mạnh kia vẫn con đang ở trong đó, ngươi - hỡi Musa - và Thượng Đế ngươi hãy đi vào chiến đấu, còn chúng tôi sẽ ở đây đợi tin');
INSERT INTO "vn_mokhtasar" VALUES(5,25,'5:25','Musa thưa với Thượng Đế của Người: Lạy Thượng Đế của bề tôi, bề tôi không thể kiểm soát ai khác ngoài bản thân mình và người em của bề tôi Harun. Xin Ngài hãy tách hai anh em bề tôi ra khỏi đám người bất tuân Ngài và bất tuân vị Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,26,'5:26','Allah phán với vị Nabi của Ngài - Musa: Quả thật, Allah cấm đám dân Isra-el vào vùng đất thanh sạch Al-Maqdis (Palestine) trong vòng năm, trong suốt thời gian này, chúng phải sống lang thang đây đó ngoài vùng sa mạc. Hỡi Musa, Ngươi chớ buồn lòng cho đám dân bất tuân này bởi việc những gì chúng gặp phải từ sự trừng phạt là do sự bất tuân và trái lệnh của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(5,27,'5:27','Hỡi Thiên Sứ Muhammad, Ngươi hãy kể cho những kẻ đố kị sai quấy từ những người Do Thái nghe về câu chuyện của hai đứa con trai Qabil và Habil của Adam. Câu chuyện là sự thật không có gì phải ngờ vực. Qabil và Habil cùng dâng tế lễ vật lên Allah nhưng Ngài chỉ chấp nhận món lễ vật của Habil bởi vì y là một người có lòng Taqwa và Ngài không chấp nhận món lễ vật của Qabil bởi vì y không phải là người có lòng Taqwa. Thế là Qabil ghen tị nói với Habil: chắc chắn tao sẽ giết mày. Habil nói: quả thật, Allah chỉ đón nhận món lễ vật từ những ai lòng Taqwa đối với Ngài, đó là những ai thi thành các mệnh lệnh của Ngài và tránh xa những điều Ngài nghiêm cấm.');
INSERT INTO "vn_mokhtasar" VALUES(5,28,'5:28','Habil nói với người anh của mình Qabil: Nếu anh nhất định muốn giết tôi cho bằng được thì tôi sẽ không đáp trả giống như hành động của anh đối với tôi. Tôi làm như thế không phải là vì tôi hèn nhát mà là vì tôi sợ Allah - Thượng Đế của vũ trụ và vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(5,29,'5:29','Habil tiếp tục nói với Qabil như một lời cảnh báo: Quả thật tôi muốn anh gánh tất cả tội lỗi cho việc anh đã giết tôi để anh trở thành người bạn của Hỏa Ngục vào Ngày Phục Sinh. Đó là hình phạt dành cho những kẻ làm điều sai quấy. Tôi không muốn ra tay để rồi tôi phải mang tội giết anh.');
INSERT INTO "vn_mokhtasar" VALUES(5,30,'5:30','Sự đố kỵ và ganh ghét trong lòng đã làm cho Qabil mất đi lý trí, Qabil đã bị nó lôi kéo đến việc làm tội lỗi và xấu xa. Qabil đã xuống tay giết chết Habil, đứa em của mình một cách bất công. Với hành động tội lỗi đó, Qabil đã trở thành một kẻ thua thiệt ở trên đời này và cả Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(5,31,'5:31','Allah đã gởi đến một con quạ, nó cào đất trước mặt của Qabil để chôn xác chết của một con quạ đồng loại của nó. Allah muốn dạy Qabil cách chôn cất xác chết của đứa em. Trước cảnh tượng đó, Qabil đã hối hận cho hành động của mình.');
INSERT INTO "vn_mokhtasar" VALUES(5,32,'5:32','Bởi việc Qabil giết chết đứa em của mình Habil nên Allah đã sắc lệnh qui định cho người dân Isra-el: Ai giết một người mà người đó không phải là một tên sát nhân cũng không phải là một kẻ phá hoại trái đất bằng sự vô đức tin và sự ác bá thì coi như người đó đã giết toàn thể nhân loại bởi vì y đã không phân biệt người vô tội và kẻ tội phạm. Và ai cứu một mạng người thì coi như y đã cứu lấy toàn thể nhân loại bởi vì hạnh động cứu người của y sẽ mang lại sự an lành cho tất cả. Quả thật, các vị Thiên Sứ của TA đã được gửi đến cho người dân Isra-el với những bằng chứng rõ rệt, tuy nhiên, đa số bọn chúng vẫn vượt quá giới hạn của Allah, chúng cứ luôn làm trái lệnh Ngài và bất tuân Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,33,'5:33','Sự trừng trị những kẻ gây chiến với Allah và Thiên Sứ của Ngài và luôn gieo rắc sự hận thù và phá hoại trên trái đất từ việc giết chóc, cướp đoạt tài sản, và phá đường phải bằng những các hình phạt sau: Giết không đóng đinh, hoặc giết rồi đóng đinh trên cây thập tự, hoặc cắt tay phải cùng với chân trái hoặc cắt tay trái cùng với chân phải, hoặc trục xuất ra khỏi xứ. Đó là sự trừng phạt dành cho chúng trên đời này và Đời Sau chúng sẽ phải chịu một sự trừng phạt lớn hơn và khủng khiếp hơn.');
INSERT INTO "vn_mokhtasar" VALUES(5,34,'5:34','Những kẻ gây chiến và làm điều thối tha đó sẽ bị trừng phạt trừ những ai trong bọn chúng kịp thời ăn năn sám hối trước khi các ngươi - hỡi những lãnh đạo của Islam - chế ngự được chúng. Và các ngươi hãy biết rằng Allah là Đấng Hằng Tha Thứ cho chúng và thương xót chúng sau khi chúng ăn năn sám hối, và một trong những sự thương xót dành cho chúng là Ngài không trừng phạt chúng.');
INSERT INTO "vn_mokhtasar" VALUES(5,35,'5:35','Hỡi những người có đức tin, các ngươi hãy kính sợ Allah mà chấp hành các mệnh lệnh của Ngài cũng như tránh xa những điều Ngài nghiêm cấm, các ngươi hãy tìm những việc làm thiện tốt và ngoan đạo để đến gần Ngài, và các ngươi hãy chiến đấu với những kẻ vô đức tin để tìm sự hài lòng nơi Ngài mong rằng các ngươi sẽ thành công trong việc đến được với Thiên Đàng và tránh được Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(5,36,'5:36','Quả thật những kẻ vô đức tin nơi Allah và Thiên Sứ của Ngài, cho dù chúng có khả năng sở hữu tất cả mọi thứ trên trái đất và tăng mọi thứ đó lên gấp đôi và chúng dùng tất cả chuộc lấy bản thân chúng khỏi sự trừng phạt thì của Allah vào Ngày Phán Xét thì chắc chắn việc chuộc đó không được chấp nhận và chắc chắn chúng sẽ bị trừng phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(5,37,'5:37','Họ mong muốn được thoát khỏi Hỏa Ngục sau khi bị đày vào trong đó nhưng điều ấy chỉ là ảo mộng bởi lẽ họ sẽ không bao giờ ra khỏi đó và họ sẽ phải chịu sự trừng phạt trong đó đời đời và mãi mãi.');
INSERT INTO "vn_mokhtasar" VALUES(5,38,'5:38','Hỡi những người chấp pháp theo giáo luật của Allah và Thiên Sứ của Ngài, đối với những kẻ trộm cắp dù là nam hay nữ thì các ngươi hãy cho chặt tay phải của chúng như một sự đền tội trộm cắp của chúng. Đó là hình phạt từ nơi Allah dành cho những ai phạm vào hành vi lấy tài sản của người khác một cách bất chính, và là một biện pháp để răn đe cho chúng và cho những người khác. Và Allah là Đấng Toàn Năng trên tất cả mọi thứ và Rất Mực Sáng Suốt trong định đoạt và phán quyết.');
INSERT INTO "vn_mokhtasar" VALUES(5,39,'5:39','Sau khi trộm cắp,ai đó biết ăn năn sám hối với Allah cho hành vi trộm cắp của mình và cố gắng sửa mình qua những việc làm thiện tốt và đúng đắn thì quả thật Allah sẽ đón nhận y bởi hồng phúc của Ngài. Đó là bởi vì Allah là Đấng Nhân Từ, Ngài luôn tha thứ tội lỗi cho những người bề tôi nào biết quay đầu sám hối với Ngài; tuy nhiên, việc chấp hành án phạt cho tội mà y đã phạm vẫn phải được thi hành nếu như nó đã được đưa tới những người chấp pháp.');
INSERT INTO "vn_mokhtasar" VALUES(5,40,'5:40','Hỡi Thiên Sứ Muhammad, quả thật Ngươi đã biết rõ rằng Allah là Đấng toàn quyền thống trị và chi phối các tầng trời và trái đất, Ngài muốn điều khiển và điều hành thế nào tùy ý Ngài, Ngài muốn trừng phạt ai tùy ý Ngài và muốn tha thứ cho ai tùy ý Ngài, không ai có quyền can thiệp. Quả thật, Allah là Đấng Toàn Năng trên tất cả mọi thứ, không có bất kỳ điều gì làm Ngài bất lực cả.');
INSERT INTO "vn_mokhtasar" VALUES(5,41,'5:41','Này hỡi Thiên Sứ Muhammad, Ngươi chớ buồn lo cho những kẻ tranh đua nhau thể hiện các việc làm vô đức tin thuộc những kẻ Munafiq, những kẻ mà chúng công khai đức tin Iman ra ngoài nhưng trong lòng che giấu sự vô đức tin của chúng. Và Ngươi cũng chớ buồn lo cho những người Do Thái luôn nghe theo những lời dối gian từ những người cầm đầu của chúng, chúng đi theo những kẻ đã bóp méo và thay đổi lời phán của Allah trong Kinh Tawrah cho phù hợp với dục vọng và sở thích của chúng, những kẻ bóp méo và thay đổi lời phán của Allah nói những ai đi theo chúng: Nếu những gì mà Muhammad định đoạt tương đồng với sở thích và dục vọng của các người thì các ngươi hãy theo y còn nều nó không tương đồng với sở thích và dục vọng của các ngươi thì các ngươi hãy cảnh giác. Và ai mà Allah muốn cho hắn lầm lạc thì Ngươi - Thiên Sứ Muhammad - sẽ không thể cứu lấy hắn khỏi sự lầm lạc và không thể dẫn dắt hắn đến con đường chân lý được. Những kẻ Do Thái và Munafiq được mô tả với những thuộc tính này là những kẻ mà Allah không muốn thanh lọc trái tim của chúng khỏi sự vô đức tin, trên thế gian chúng bị hạ nhục và vào Đời Sau chúng sẽ bị trừng phạt nặng nề và khủng khiếp hơn, đó là sự trừng phạt nơi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(5,42,'5:42','Những kẻ Do Thái thường thích nghe những điều gian dối, chúng thường thích ăn những món Haram như ăn tiền lãi cho vay. Này hỡi Thiên Sứ Muhammad, nếu chúng tìm đến Ngươi để nhờ phân xử vụ việc của chúng thì nếu muốn Người không cần tiếp chúng và nếu muốn Ngươi có thể phân xử cho chúng, Ngươi được quyền lựa chọn một trong hai phương án; nếu Ngươi không tiếp chúng thì chắc chắn chúng không bao giờ làm hại được Ngươi nhưng nếu Người đồng ý phân xử vụ việc cho chúng thì Ngươi hãy phân xử một cách công bằng và liêm chính cho dù chúng là những kẻ bất công và là kẻ thù của Ngươi. Quả thật, Allah yêu thích những người công bằng trong phân xử cho dù những kẻ được phân xử có là kẻ thù của họ đi chăng nữa.');
INSERT INTO "vn_mokhtasar" VALUES(5,43,'5:43','Quả thật, những kẻ này thật lạ lùng, chúng vô đức tin nơi Ngươi - Muhammad - nhưng lại mang các vụ việc của chúng nhờ Ngươi phân xử với niềm hy vọng sự phân xử của Ngươi phù hợp với sở thích và ý muốn của chúng. Chúng mang vụ việc của chúng đến nhờ Ngươi phân xử trong khi nơi chúng đã có Kinh Tawrah, Kinh Sách mà chúng cho rằng chúng có đức tin nơi Nó và trong Kinh Sách đó đều chứa đựng luật định của Allah. Nhưng rồi chúng quay lưng với sự phân xử của Ngươi nếu nó không phù hợp với sở thích và ý muốn của chúng, chúng gom lại trong bản thân chúng hai sự vô đức tin, sự vô đức tin nơi Kinh Sách của họ và sự quay lưng với việc phân xử của Ngươi. Những hành động của những kẻ này không phải là hành vi của những người có đức tin, chúng hành động như thế là bởi vì chúng không hề có đức tin nơi Ngươi và nơi những gì Ngươi mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(5,44,'5:44','Quả thật, TA đã ban Kinh Tawrah xuống cho Musa, trong đó chứa đựng nguồn chỉ đạo và hướng dẫn, chứa đựng ánh sáng soi rọi con đường. Các vị Nabi của người dân Isra-el - những vị mà họ luôn tuân lệnh và phủ phục Allah -, những học giả và những người được Allah cho thông hiểu Kinh sách và giữ gìn Kinh Sách có nhiệm vụ trông coi vụ việc của nhân loại đã dùng Kinh Tawrah này để xét xử. Và những người được Allah giao cho họ nhiệm vụ bảo tồn Kinh Tawrah không để bị bóp méo và sửa đổi sẽ là những làm chứng rằng Nó là chân lý, là nguồn chỉ đạo mà tất cả nhân loại phải tìm về. Bởi thế, các ngươi - hỡi những người Do Thái - chớ sợ nhân loại mà hãy sợ một mình TA, các ngươi chớ lấy những gì TA ban xuống đem bán với giá ít ỏi từ chức quyền và của cải. Và những ai không phân xử bởi những điều được Allah ban xuống mà tìm đến những nguồn chỉ đạo khác hoặc dùng những nguồn chỉ đạo khác cùng với lời mặc khải của Allah thì những kẻ đó là những kẻ vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(5,45,'5:45','TA đã sắc lệnh ban hành luật cho những người Do Thái trong Kinh Tawrah rằng ai cố ý giết một mạng người một cách không chính đáng (tức y đã giết một người mà người đó không phải là một tên sát nhân cũng không phải môt kẻ phá hại trên trái đất đáng bị giết) thì y sẽ bị giết theo qui luật giết người phải đền mạng; ai cố ý lấy đi một con mắt của người khác thì y phải bị lấy đi một con mắt để đền lại, ai cố ý cắt cái mũi của người khác thì y phải bị cắt đi cái mũi để đền lại, ai cố ý cắt cái tai của người khác thì y phải bị cắt đi cái tai để đền lại, và ai làm gãy một chiếc răng của người khác thì y phải bị bẻ gãy chiếc răng để đền lại. TA đã ban hành luật cho họ rằng thương tích phải đền bằng thương tích, tuy nhiên, ai vì lòng tốt mà bỏ qua thì sự bỏ qua đó sẽ xóa đi tội lỗi của y vì y đã bao dung với người đã bất công với mình; và ai không phân xử theo điều luật đáp trả công bằng của Allah cũng như trong các vấn đề khác thì y đã đi ra ngoài giới hạn của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,46,'5:46','Và TA đã cử Ysa con trai của Mar-yam nối gót các vị Nabi của dân Isra-el làm một vị Nabi tin tưởng nơi Kinh Tawrah và dùng Nó để phân xử; và TA đã ban thêm cho Y Kinh Injil chứa đựng nguồn chỉ đạo cho điều chân lý và giải tỏa mọi điều chưa rõ cũng như xác nhận những điều được ban xuống trong Tawrah ngoại trừ một số luật định bị xóa bỏ và được thay cái mới. TA đã ban Injil xuống vừa làm một nguồn chỉ đạo vừa là một sự cảnh báo cho những làm điều tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(5,47,'5:47','Những người Thiên Chúa hãy tin nơi những điều được Allah ban xuống trong Kinh Injil và hãy xét xử các vụ việc theo những gì ở trong đó. Và ai không xét xử theo những điều mà Allah ban xuống thì những người đó là những kẻ bất tuân Allah, từ bỏ điều chân lý và hướng về điều sai quấy và sàm bậy.');
INSERT INTO "vn_mokhtasar" VALUES(5,48,'5:48','Này hỡi Thiên Sứ Muhammad, TA đã ban xuống cho Ngươi Kinh Qur''an là sự thật, chẳng có gì phải nghi ngờ rằng Nó đích thực đến từ nơi Allah dùng để xác nhận lại những gì trong các Kinh Sách trước đó, Nó tuyệt đối trung thực và nói chính xác tất cả những gì được nói các Kinh Sách trước. Bởi thế, những gì trong các Kinh Sách trước tương đồng với Qur''an thì đó là sự thật, còn những gì khác với Nó là ngụy tạo. Cho nên, Ngươi - Muhammad - hãy xét xử nhân loại theo những điều Allah ban xuống cho Ngươi, Ngươi chớ đi theo dục vọng và sở thích của chúng mà bỏ đi những gì được ban xuống cho Ngươi. Quả thật, TA đã ban hành cho mỗi cộng đồng một lề lối và giáo lý riêng để làm nguồn chỉ đạo, nếu muốn TA có thể làm thành một cộng đồng duy nhất mang một lề lối và giáo lý chung, nhưng TA muốn mỗi cộng đồng có một giáo lý riêng để thử thách xem ai tuân lệnh và ai bất tuân. Do đó, các ngươi hãy nhanh chân đến với những việc làm ngoan đạo, thiện tốt và từ bỏ những việc làm sai trái và nghịch lệnh; các ngươi hãy nhanh chấn đến với Allah bởi lẽ các ngươi phải quay trở về với Ngài vào Ngày Phục Sinh, và Ngài sẽ cho các ngươi biết những điều các ngươi đã bất đồng nhau, và Ngài sẽ đáp trả xứng đáng cho những gì các ngươi đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(5,49,'5:49','Hỡi vị Thiên Sứ của Allah, Ngươi hãy xét xử giữa chúng bằng những điều mà Allah đã ban xuống cho Ngươi, Ngươi chớ đi theo ý kiến và quan điểm của chúng bởi vì ý kiến và quan điểm của chúng đều xuất phát từ dục vọng và sở thích nơi bản thân chúng, Ngươi hãy cẩn thận trông chừng chúng đừng để chúng làm cho Ngươi lệch lạc khỏi một số điều được ban xuống cho Ngươi; nếu chúng quay lưng không chịu chấp nhận luật định mà Allah đã ban xuống cho Ngươi thì Ngươi hãy biết rằng thật ra Allah chỉ muốn trừng phạt chúng một số tội của chúng trên thế gian này mà thôi nhưng Ngài sẽ trừng phạt chúng tất cả tội lỗi ở Đời Sau. Và quả thật, đa số nhân loại thường không phụng mệnh Allah.');
INSERT INTO "vn_mokhtasar" VALUES(5,50,'5:50','Há chúng quay lưng với sự phân xử của Ngươi - Muhammad - và đòi luật phân xử của thời kỳ ngu muội của những kẻ thờ bục tượng, những kẻ mà chúng chỉ phân xử theo dục vọng và sở thích của chúng? Bởi thế, đối với những người có đức tin kiên định thì không có một ai có thể ban hành luật xét xử tốt hơn Allah cả bởi vì họ ý thức được sự chân lý nơi những điều mà Allah đã ban xuống cho vị Thiên Sứ của Ngài; riêng những kẻ của thời kỳ ngu muội, những kẻ đi theo dục vọng thì không nhận thức được điều chân lý nơi Allah mà chỉ yêu thích đón nhận những gì thuận với dục vọng và ham muốn của chúng cho dù điều đó có là ngụy tạo và sai trái.');
INSERT INTO "vn_mokhtasar" VALUES(5,51,'5:51','Hỡi những người có đức tin nơi Allah và nơi Thiên Sứ của Ngài, các ngươi chớ nhận lấy những người Do Thái và những người Thiên Chúa làm những người để các ngươi gửi gắm và đặt lòng tin; bởi người Do Thái chỉ nhận lậy đồng bọn của chúng làm người gửi gắm và đặt lòng tin và những người Thiên Chúa cũng thế, chúng chỉ nhận lấy đồng bọn của chúng; và cả hai nhóm đó đều thù nghịch với các ngươi. Và ai trong các ngươi - hỡi những người có đức tin - nhận lấy chúng là người thân tín thì người đó thuộc nhóm của chúng. Quả thật, Allah không hướng dẫn nhóm người làm điều sai quấy.');
INSERT INTO "vn_mokhtasar" VALUES(5,52,'5:52','Ngươi - Thiên Sứ Muhammad - sẽ thấy những người Munafiq, những kẻ mà chúng chỉ có đức tin Iman rất ít nhanh chân kết thân với những người Do Thái và Thiên Chúa, chúng nói: Chúng tôi sợ những kẻ này (những người có đức tin) giành chiến thắng rồi họ sẽ có một quốc gia cai trị chúng tôi. Thế là Allah đã ban sự thắng lợi hãy một điều tốt đẹp nào đó cho vị Thiên Sứ của Ngài và những ngươi có đức tin thì những kẻ đã nhanh chân kết thân với những người Do Thái lại hối tiếc cho điều dối trá và giả tạo được che giấu trong lòng của chúng, Ngài phá tan tâm địa gian dối của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(5,53,'5:53','Những người có đức tin ngạc nhiên cho tình huống của những kẻ Munafiq này, nói: Những người này chẳng phải đã thề thốt mạnh bạo rằng họ sẽ cùng với các anh - những người có đức tin - trong Iman, trong sự ủng hộ và trong tình thần hữu nghị thân tín đó sao?! Quả thật, tất cả mọi việc làm của chúng đều đã trở nên vô giá trị, chúng đã trở thành những kẻ thua thiệt bởi đã không đạt được mục đích, và chúng phải sẽ đối mặt với hình phạt chuẩn bị dành cho chúng ở cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(5,54,'5:54','Này hỡi những người có đức tin, những ai trong các ngươi rời bỏ tôn giáo của mình để trở lại với sự vô đức tin thì Allah sẽ mang đến một nhóm người khác thay thế họ, Ngài sẽ yêu thương họ và họ sẽ yêu thương Ngài trên con đường Ngay Chính của họ, họ sẽ nhân từ với những người có đức tin nhưng sẽ rất khắt khe với những kẻ vô đức tin, họ sẽ chiến đấu bằng tài sản và tính mạng của họ để giơ cao lời phán của Allah, họ sẽ không sợ sự trách móc của một ai, họ đặt sự hài lòng của Allah bên trên sự hài lòng của con người. Đó là hồng phúc mà Allah muốn ban cho ai tùy ý Ngài, Ngài là Đấng với hồng phúc và tấm lòng bao la, và Ngài là Đấng hằng biết ai đáng được nhận hồng phúc và ai không đáng được nhận.');
INSERT INTO "vn_mokhtasar" VALUES(5,55,'5:55','Những người Do Thái, những người Thiên Chúa cũng như những nhóm người vô đức tin khác không phải là bạn hữu đáng tin của các ngươi, mà bạn hữu đích thực đáng tin cậy và sẵn sàng ủng hộ các ngươi chỉ có Allah và Thiên Sứ của Ngài cùng với những người có đức tin, những người mà họ luôn chu đáo duy trì lễ nguyện Salah, xuất tiền của cho nghĩa vụ Zakah và luôn hạ mình phủ phục Allah.');
INSERT INTO "vn_mokhtasar" VALUES(5,56,'5:56','Và ai nhận lấy Allah, Thiên Sứ của Ngài và những người có đức tin làm bạn hữu đỡ đầu và ủng hộ thì y thuộc về đảng phái của Allah, và đảng phái của Allah thì luôn giành chiến thắng bởi Allah là Đấng ban sự thắng lợi cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(5,57,'5:57','Hỡi những người có đức tin, các ngươi chớ đừng lấy những ai chế giễu và đùa giỡn với tôn giáo của các ngươi từ những người của Kinh Sách trước các ngươi - Do Thái, Thiên Chúa - và những người thờ đa thần làm bạn hữu đỡ đầu cho các ngươi. Các ngươi hãy kính sợ Allah mà tránh xa những gì Ngài nghiêm cấm nếu các ngươi thực sự là những người có đức tin nơi Ngài và nơi những điều được ban xuống cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(5,58,'5:58','Cũng tương tự như thế, chúng bỡn cợt và chế giễu khi các ngươi hô gọi nhau đến lễ nguyện Salah. Chúng hành động như vậy là bởi vì chúng là một đám người không hiểu được ý nghĩa của việc thờ phượng Allah cũng như giáo lý mà Ngài đã sắc lệnh cho nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(5,59,'5:59','Hỡi vị Thiên Sứ của Allah, Ngươi hãy nói với những kẻ chế giễu thuộc dân Kinh Sách: Chẳng lẽ các ngươi bỡn cợt với bọn ta chỉ vì bọn ta tin nơi Allah và những điều được ban xuống cho bọn ta cũng như những điều được ban xuống trước bọn ta; và chỉ vì bọn ta tin rằng đa số các ngươi thường bất tuân Allah, từ bỏ đức tin Iman và không chấp hành mệnh lệnh của Ngài hay sao?! Vì vậy, những gì các ngươi đang làm cho bọn ta xấu hổ lại là một lời khen cho bọn ta chứ không phải là điều xấu hổ dành cho bọn ta.');
INSERT INTO "vn_mokhtasar" VALUES(5,60,'5:60','Hỡi vị Thiên Sứ của Allah, Ngươi hãy bảo chúng: Có phải các ngươi muốn Ta cho các ngươi biết ai mới là những kẻ đáng xấu hổ và sẽ bị một sự trừng phạt nặng nề nhất? Đó chính là những kẻ bị Allah xua đuổi khỏi lòng thương xót của Ngài và bị Ngài biến thành loài khỉ và heo và bị Ngài bỏ mặc trong thờ phượng Taghut tức tất cả những gì được thờ phượng ngoài Allah (Tà Thần). Những kẻ được đề cập đến này là những kẻ sẽ có một nơi ở tồi tệ nhất vào Ngày Phục Sinh và chúng chính là những kẻ lầm lạc quá xa khỏi con đường Ngay Chính (Islam).');
INSERT INTO "vn_mokhtasar" VALUES(5,61,'5:61','Hỡi những người có đức tin, khi chúng - những tên Munafiq đến với các ngươi thì chúng thể hiện cho các ngươi thấy chúng là những người tin tưởng, tuy nhiên, việc chúng nhập đạo hãy rời khỏi đạo đều giả dối, chúng luôn che giấu sự vô đức tin của chúng. Và dĩ nhiên Allah thông toàn sự vô đức tin mà chúng giấu kín trong lòng của chúng cho dù bề ngoài chúng cố thể hiện niềm tin như thế nào trước mặt các ngươi, và rồi đây Ngài sẽ đền đáp xứng đáng cho việc làm của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(5,62,'5:62','Hỡi vị Thiên Sứ của Allah, Ngươi thấy nhiều người Do Thái cũng như những người Munafiq cứ lao vào tội lỗi như nói dối, gây oan thù với người khác bằng sự bất công của chúng và ăn tài sản của thiên hạ một cách bất chính. Thật tồi tệ thay cho việc làm của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(5,63,'5:63','Sao các vị Imam cũng như các học giả của chúng không chịu răn dạy và chỉ dẫn rằng chúng lao vào những điều tội lỗi từ việc nói dối, làm chứng giả, ăn tài sản bất chính của thiên hạ là sai trái. Quả thật, việc những học giả và các vị Imam của chúng không chịu ngăn cản những điều sai trái và nghịch đạo là những hành động tội tệ và xấu xa.');
INSERT INTO "vn_mokhtasar" VALUES(5,64,'5:64','Những người Do Thái khi gặp những điều chẳng lành và rủi ro thì chúng nói rằng tay của Allah bị xiềng xích lại nên đã không ban phát điều tốt lành mà chỉ biết giữ lại ở nơi Ngài. Không phải như thế mà chính tay của chúng mới cản trở điều tốt đẹp và phúc lành từ nơi Ngài. Lời nói này của chúng đã khiến chúng bị trục xuất khỏi lòng thương xót của Allah. Quả thật, hay tay của Allah luôn dang rộng cho điều tốt đẹp và phúc lành, Ngài muốn chi dùng như thế nào là tùy ý Ngài không ai có quyền can thiệp. Quả thật, những gì được ban xuông cho Ngươi - hỡi vị Thiên Sứ Muhammad - không thêm ích lợi gì cho chúng ngoại trừ sự vượt quá giới hạn của Allah cũng như sự chống đối Ngài. Tất cả điều đó chỉ vì chúng mang lòng đố kỵ; và chúng cứ thường gây ra những mối hiềm khích giữa chúng với nhau. Và mỗi lần chúng khơi dậy ngọn lửa chiến tranh thì Ngài dập tắt nó ngay lập tức nhưng chúng cữ vẫn nỗ lực phá hại trên trái đất và tìm cách hủy hoại Islam. Và Allah không yêu thương những kẻ phá hại.');
INSERT INTO "vn_mokhtasar" VALUES(5,65,'5:65','Nếu những người Do Thái và những người Thiên Chúa tin những gì Muhammad mang đến và sợ Allah mà tránh xa những điều tội lỗi và trái đạo thì Allah chắc chắn sẽ xỏa bỏ những tội lỗi mà chúng đã phạm cho dù có nhiều thế nào và vào Ngày Sau chắc chắn Ngài sẽ thu nhận chúng vào Thiên Đàng của Ngài, nơi với những ngôi vườn mang lại niềm hạnh phúc bất tận.');
INSERT INTO "vn_mokhtasar" VALUES(5,66,'5:66','Nếu người Do Thái chịu chấp hành những điều khoản được qui định trong Kinh Tawrah và những người Thiên Chúa chịu chấp hành những điều khoản được qui định trong Kinh Injil, và tất cả họ đều thi hành và thực hiện những điều lệnh được Allah ban xuống trong Kinh Qur''an thì chắc chắn họ sẽ tìm thấy nguồn bổng lộc dồi dào từ mọi nơi trên trái đất. Tuy nhiên, trong đám dân Kinh Sách có nhóm người đi theo chân lý nhưng đa số thì làm điều tội lỗi và sai trái vì không có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(5,67,'5:67','Hỡi Thiên Sứ Muhammad, Ngươi phải truyền đạt lại toàn bộ những gì mà Thượng Đế của Ngươi đã ban xuống cho Ngươi, Ngươi không được phép giấu giếm bất cứ điều gì, nếu Ngươi che giấu giữ lại bất cứ điều gì từ Bức Thông Điệp của TA thì có nghĩa rằng Ngươi đã chưa truyền đạt Bức Thông Điệp của TA (quả thật Thiên Sứ Muhammad đã truyền đạt toàn bộ Bức Thông Điệp của Allah và ai không công nhận điều đó thì người đó đã dối Allah). Allah sẽ bảo vệ Ngươi - Muhammad - khỏi sự hãm hại của nhân loại kể từ ngày hôm nay, chắc chắn họ sẽ không thể mang những điều xấu chạm đến Ngươi, nhiệm vụ của Ngươi là cứ truyền đạt còn Allah mới là Đấng hướng dẫn; nhưng Allah không hướng dẫn những kẻ vô đức tin không muốn nguồn chỉ đạo.');
INSERT INTO "vn_mokhtasar" VALUES(5,68,'5:68','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói: Hỡi dân Do Thái và Thiên Chúa, các người chẳng có nguồn cơ sở nào cho đạo ngoại trừ các ngươi làm theo những gì được nói trong Kinh Tawrah và Injil, và làm theo những gì được ban xuống cho các ngươi trong Kinh Qur''an, Kinh Sách mà đức tin của các ngươi không được chấp nhận trừ phi phải có đức tin nơi Nó. Tuy nhiên, những gì được ban xuống cho Ngươi - Muhammad - từ Thượng Đế của Ngươi sẽ không mang lại điều hữu ích gì cho đám dân Kinh Sách ngoại trừ sự thái quá và vô đức tin. Cho nên, Ngươi - Muhammad - chớ đau buồn và rầu lo cho những kẻ vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(5,69,'5:69','Quả thật, những người có đức tin, những người Do Thái, những người Sabian tức một nhóm người đi theo một số vị Nabi, những người Thiên Chúa và những ai tin nơi Allah, tin nơi cõi Đời Sau đồng thời làm việc thiện tốt thì họ sẽ không phải gặp bất điều gì xấu trong lương lai và họ cũng không phải lo cho những gì đã mất trên thế gian này.');
INSERT INTO "vn_mokhtasar" VALUES(5,70,'5:70','Quả thật, TA đã nhận lấy giao ước với đám dân Isra-el rằng chúng phải nghe và tuân lệnh nhưng chúng đã bội giao ước và đi theo dục vọng của bản thân chúng, chúng quay lưng với những gì mà các vị Thiên Sứ của TA đã mang đến cho chúng, mỗi khi các vị Thiên Sứ của TA đến với chúng thì hoặc là chúng phủ nhận họ hoặc là chúng giết họ.');
INSERT INTO "vn_mokhtasar" VALUES(5,71,'5:71','Chúng tưởng rằng việc chúng bội giao ước với Allah, việc chúng dối trá, và việc chúng giết các vị Nabi của Ngài sẽ không phải đối mặt với sự trừng phạt nào của Allah, chúng giả mù, giả điếc như không hề nghe thấy chân lý. Nhưng rồi Allah đã tha thứ cho chúng bởi hồng phúc của Ngài, tuy nhiên, sau đó chúng lại vẫn tiếp tục giả mù giả điếc trước điều chân lý. Dĩ nhiên, Allah thấy hết và nghe hết những gì chúng làm, không có điều gì có thể che giấu được Ngài và Ngài sẽ đáp trả lại thích đáng cho từng việc làm của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(5,72,'5:72','Quả thật, những người Thiên Chúa đã vô đức tin khi nói rằng Allah chính là Masih Ysa, con trai của Mar-yam, theo thuyết ba ngôi của họ, trong khi Masih con trai Mar-yam bảo họ: Này hỡi dân Isra-el, các ngươi hãy thờ phượng một mình Allah bởi vì Ngài là Thượng Đế của Ta và là Thượng Đế của các ngươi nữa, tất cả chúng ta đều phải thờ phượng Ngài như nhau, ai tổ hợp cùng với Allah một đối tác ngang vai thì Allah không bao giờ thu nhận y vào Thiên Đàng của Ngài và chỗ ở của y chắc chắn sẽ là Hỏa Ngục, và y sẽ không bao giờ tìm thấy một vị cứu tinh nào ngoài Allah có thể giúp y thoát khoải sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(5,73,'5:73','Quả thật, những người Thiên Chúa là những người vô đức tin bởi vì họ đã nói rằng Allah là hiện thân của một trong ba ngôi chúa theo thuyết chúa ba ngôi: Đức chúa cha, đức chúa con và chúa thánh linh. Quả thật, Allah vĩ đại và tối cao hơn những gì họ nói, Allah chỉ có một, không có Thượng Đế nào khác ngoài Ngài, Ngài không có đối tác ngang vai. Nếu họ không chịu dừng lại lời nói sàm bậy và phạm thượng của họ thì Allah sẽ cho họ nếm mùi của hình phạt đau đớn trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(5,74,'5:74','Sao những người này không chịu dừng lại lời nói của họ mà quay đầu sám hối với Allah và xin Ngài tha thứ cho tội gán ghép đối tác ngang vai cho Ngài?! Quả thật, Allah là Đấng Hằng Tha Thứ, Ngài sẵn sàng tha thứ mọi tỗi lỗi cho những ai thành tâm quay đầu sám hối với Ngài bởi Ngài luôn nhân từ và thương xót với những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(5,75,'5:75','Masih Ysa con trai của Mar-yam chỉ là một vị Thiên Sứ trong số bao vị Thiên Sứ khác đều phải đối mặt với cái chết. Mẹ của Y, Mar-yam là một người phụ nữ trung thực đầy đức tin, các hai mẹ con họ đều ăn thức ăn để duy trì sự sống thì làm sao hai ngươi họ có thể là hai vị chúa cho được? Bởi thế, Ngươi - hỡi Thiên Sứ Muhammad - hãy xem và suy ngẫm: Cách TA giải thích rõ ràng cho chúng những lời mặc khải để chứng tính duy nhất của TA và vô hiệu hóa tất cả các thần linh khác ngoài TA, tuy nhiên chúng vẫn phủ nhận các lời mặc khải của TA. Sau đó, Ngươi hãy xem và suy ngẫm: Cách chúng ngoảnh đi với chân lý trong khi các bằng chứng chứng minh tính duy nhất của Allah đã quá rõ ràng với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(5,76,'5:76','Này Thiên Sứ Muhammad, Ngươi hãy lý luận với chúng trong việc chúng thờ phượng các thần linh khác ngoài Allah: Sao các ngươi lại thờ phượng những thứ không mang lại lợi ích cũng chẳng gây hại được các ngươi?! Những thứ đó bất lực và vô năng còn Allah quyền năng vô song, Ngài là Đấng Duy Nhất nghe thấy mọi lời nói của các ngươi, không một lời lẽ nào có thể giấu giếm được Ngài, Ngài nhìn thấy tất cả mọi việc làm và hạnh động của các ngươi, không một hành vi nào của các ngươi nằm ngoài tầm nhìn của Ngài, rồi đây Ngài sẽ thưởng phạt các ngươi trên tất cả những việc làm và lời nói của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(5,77,'5:77','Này hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những người Thiên Chúa: Các ngươi chớ vượt quá giới hạn mà các ngươi được lệnh phải đi theo chân lý, các ngươi chớ đề cao và tôn vinh quá mức đối với những ai mà các ngươi được lệnh phải đề cao và tôn vinh họ - chẳng hạn như các vị Nabi - để rồi các ngươi đưa đẩy họ lên cao mang những thuộc tính của Thượng Đế giống như các ngươi đã hành động với Ysa con trai của Mar-yam, nguyên nhân là các ngươi đã đi theo dấu chân của những người lầm lạc trước các ngươi, những người mà họ đã dắt nhiều người đi lạc khỏi con đường chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(5,78,'5:78','Allah cho biết rằng những kẻ vô đức tin thuộc đám dân Isra-el đã bị xua đuổi khỏi lòng thương xót của Ngài được nói trong Kinh Zabur được ban xuống cho Nabi Dawood và trong Kinh Injil được ban xuống cho Nabi Ysa. Chúng bị xua đuổi khỏi lòng thương xót của Allah như thế là bởi vì chúng cứ luôn bất tuân và vi phạm giới cấm của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,79,'5:79','Chúng không những không ngăn cản nhau làm điều tội lỗi mà còn ủng hộ cho các hành động xấu và trái lệnh. Việc làm chúng thật tồi tệ và xấu xa.');
INSERT INTO "vn_mokhtasar" VALUES(5,80,'5:80','Hỡi Thiên Sứ Muhammad, Ngươi chứng kiến có nhiều người Do Thái yêu thích kết bạn với những kẻ vô đức tin để chống lại Ngươi và những người của Tawhid.Thật tồi tệ và xấu thay cho hành động kết bạn của chúng, và đó là nguyên làm cho Allah giận dữ chúng và đày chúng vào Hỏa Ngục để sống mãi trong đó không có ngày trở ra.');
INSERT INTO "vn_mokhtasar" VALUES(5,81,'5:81','Nếu những kẻ Do Thái này chịu tin nơi Allah và tin nơi vị Nabi của Ngài một cách chân thành thì chúng sẽ không kết bạn với những người vô đức tin, đa số bọn chúng thường rời khỏi sự tuân lệnh Allah, rời khỏi việc nhận lấy Ngài và những người tin nơi Ngài làm những người ủng hộ.');
INSERT INTO "vn_mokhtasar" VALUES(5,82,'5:82','Hỡi Thiên Sứ Muhammad, Ngươi sẽ thấy trong thiên hạ, những người chống đối và thù hằn mạnh mẽ nhất với những người có đức tin nơi Ngươi và nơi những gì Ngươi mang đến là những người Do Thái bởi sự đố kỵ, sự khinh thường, lòng tự cao tự đại của chúng; và những kẻ thờ đa thần cũng vậy. Và Ngươi sẽ thấy những người có mối quan hệ gần gũi nhất với những người có đức tin nơi Ngươi và nơi những gì Ngươi mang đến là những người mà họ nói với bản thân họ rằng họ là Thiên Chúa giáo; họ sẽ có mối quan hệ tình cảm gần gũi hơn với những người có đức tin bởi họ là những học giả, những người đạo hạnh và họ khiêm tốn không kiêu căng và ngạo mạn, và bởi vì những kẻ tự cao tự đại thì trái tim của họ không chạm được với điều phúc lành.');
INSERT INTO "vn_mokhtasar" VALUES(5,83,'5:83','Những người này - chẳng hạn hoàng đế Negus của Ethiopia và thần dân của ông - trái tim của họ không chai cứng, họ đã khóc khi nghe những điều được ban xuống trong Kinh Qur''an bởi vì họ biết đó là sự thật, sự nhận biết của họ là qua những gì được ban xuống cho Nabi Ysa. Họ nói: Lạy Thượng Đế của bầy tôi, bầy tôi tin nơi những điều được ban xuống cho vị Thiên Sứ của Ngài - Muhammad, xin Ngài hãy ghi bầy tôi cùng với các tín đồ của Muhammad, cộng đồng sẽ làm những nhân chứng cho nhân loại vào Ngày Phán Xét".');
INSERT INTO "vn_mokhtasar" VALUES(5,84,'5:84','Lý do gì mà chúng tôi lại không có đức tin nơi Allah cũng như những sự thật mà Muhammad đã mang đến cho chúng tôi trong khi chúng tôi luôn mong được vào Thiên Đàng cùng với các vị Nabi và những người đi theo họ trong việc tuân lệnh Allah vì sợ sự trừng phạt của Ngài?!');
INSERT INTO "vn_mokhtasar" VALUES(5,85,'5:85','Thế là Allah đã tưởng thưởng cho họ bởi đức tin Iman của họ cũng như sự thừa nhận của họ về chân lý những Ngôi Vườn Thiên Đàng, có các dòng sông chảy bên dưới các tòa lâu đài, họ sẽ sống đời đời kiếp kiếp trong đó. Đây là phần thưởng mà Allah dành cho những người làm tốt trong việc đi theo chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(5,86,'5:86','Ngược lại, đối với những ai vô đức tin nơi Allah và Thiên Sứ của Ngài, phủ nhận các Lời Mặc Khải của Allah được ban xuống cho vị Thiên Sứ của Ngài thì họ chắc chắn sẽ phải làm bạn với Hỏa Ngục, không bao giờ có ngày trở ra khỏi nơi đó.');
INSERT INTO "vn_mokhtasar" VALUES(5,87,'5:87','Hỡi những người có đức tin, các ngươi chớ cấm những thứ hưởng thụ được phép từ thức ăn, đồ uống và kết hôn, các ngưới chớ đừng cấm (bản thân và người khác) dùng những thứ đó để thể hiện sự không màng đến cuộc sống thế gian hoặc để thể hiện sự tu hành và ngoan đạo. Tuy nhiên, các ngươi chớ vượt quá giới hạn những gì mà Allah nghiêm cấm các ngươi. Quả thật, Allah không yêu thương những kẻ vượt khỏi giới hạn của Ngài mà giận dữ đối với họ.');
INSERT INTO "vn_mokhtasar" VALUES(5,88,'5:88','Và các ngươi hãy ăn những gì mà Allah cho phép các ngươi dùng từ những thức ăn, đồ uống tốt lành, thanh sạch; và các ngươi hãy kính sợ Ngài bằng cách thực thi các mệnh lệnh của Ngài, tránh xa những gì Ngài nghiêm cấm bởi Ngài là Đấng mà các ngươi có đức tin, và đức tin của các ngươi ở nơi Ngài bắt buộc các ngươi phải kính sợ Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,89,'5:89','Hỡi những người có đức tin, Allah không bắt tội các ngươi bởi những lời thề thốt quen miệng của các ngươi vì đó là những lời thề trống rỗng không mang một ý nghĩa đích thực nào cả. Allah chỉ bắt tội các ngươi về những lời thề mang mục đích thực sự và ý nguyện rõ ràng từ trong trái tim của các ngươi. Ngài sẽ xóa tội cho các ngươi về điều mà các ngươi đã thề nhưng các ngươi không có khả năng thực hiện đổi lại các ngươi phải thực hiện một trong ba điều tùy chọn sau: nuôi ăn mười người nghèo theo mức lượng trung bình mà các ngươi nuôi ăn gia đình của các ngươi, mỗi người nghèo là nửa Sa'' thức ăn; hoặc chu cấp quần áo cho họ theo lệ thường; hoặc giải phóng một nữ nô lễ có đức tin. Nhưng nếu các ngươi không có khả năng thực hiện một trong ba điều đó để được xóa tội thì các ngươi hãy nhịn chay ba ngày. Đó là cách thức Kaffa-rah (xóa tội) cho lời thề giả dối của các ngươi khi nào các ngươi đã thề với Allah rồi các ngươi phạm tội do không thực hiện. Và các ngươi hãy cẩn trọng: đừng thề thốt với Allah bằng những điều giả dối, đừng lãm dụng lời thề, đừng tùy tiện thề thốt nếu điều đó không làm cho sự việc trở nên tốt đẹp hơn, các ngươi hãy làm điều tốt và dùng Kaffa-rah để xóa tội cho lời thề của các ngươi. Allah đã trình bày rõ ràng cho các ngươi về những điều được phép và không được phép thực hiện, mong rằng các ngươi biết ơn về những gì mà Ngài đã giảng dạy cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(5,90,'5:90','Hỡi những người có đức tin, quả thật chất gây mất lý trí, cờ bạc, cá cược, bàn thờ đá được những người thờ đa thần giết tế dâng cúng hoặc để thờ phượng và việc xin xăm để xem chuyện may rủi, tất cả những thứ đó đều tội lỗi, chúng là việc làm của Shaytan. Bởi thế, các ngươi hãy tránh xa chúng mong rằng các ngươi đạt được thắng lợi ở cuộc đời này và niềm hạnh phúc ở cuộc sống Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(5,91,'5:91','Quả thật, Shaytan chỉ muốn trang hoàng dụ dỗ người uống rượu, đánh bạc rơi vào sự căm ghét và thù hằn lẫn nhau, nó muốn dùng rượu và cờ bạc để đưa họ rời khỏi sự tưởng nhớ Allah và bỏ lễ nguyện Salah. Thế, các ngươi - hỡi những người có đức tin - không chịu bỏ những thứ tội lỗi này hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(5,92,'5:92','Và các ngươi hãy tuân lệnh Allah và vâng lời Thiên Sứ Muhammad trong việc chấp hành và thực thi các mệnh lệnh và chỉ đạo cũng như tránh xa những điều nghiêm cấm, và các ngươi hãy coi chừng chớ làm những điều trái mệnh lệnh. Nếu các ngươi quay lưng không chịu nghe theo thì các ngươi hãy biết rằng nhiệm vụ của vị Thiên Sứ của TA chỉ là truyền đạt một cách rõ ràng cho các ngươi mà thôi, và Y thực sự đã truyền đạt, nếu các ngươi tiếp nhận sự hướng dẫn thì là điều tốt cho bản thân các ngươi còn nếu các ngươi không tiếp nhận thì là điều bất hạnh cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(5,93,'5:93','Những người có đức tin nơi Allah đồng thời làm điều thiện tốt và ngoan đạo sẽ không bị mang tội cho việc họ đã uống rượu trong quá khứ trước khi lệnh cấm rượu được ban xuống. Nếu họ tránh xa những điều nghiêm cấm, sợ sự giận dữ của Allah giáng xuống họ, có đức tin nơi Ngài, làm những điều thiện tốt và ngoan đạo rồi họ tăng thêm đức tin Iman rằng Allah luôn giám sát họ khi họ thờ phượng Ngài và họ thực hiện điều đó giống như họ đang trước mặt Ngài. Allah yêu thương những ai thờ phượng Ngài như thể họ đang nhìn thấy Ngài trước mặt, thì điều đó sẽ dẫn họ đến với sự tốt đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(5,94,'5:94','Hỡi những người có đức tin, Allah thử thách các ngươi một thứ gì đó từ việc săn bắt các động vật trên cạn trong lúc các ngươi đang trong tình trạng Ihram (cho cuộc hành hương Hajj), Ngài cấm các ngươi bắt những con nhỏ bằng tay và các con lớn bằng những ngọn lao, mục đích Ngài muốn biết trong các ngươi ai là những bề tôi thực sự sợ Ngài và đạt được đức tin Iman trọn vẹn dù không nhìn thấy Ngài. Bởi thế, hãy dừng lại việc săn bắt để tỏ lòng kính sợ Đấng Tạo Hóa, Đấng mà không có điều gì có thể giấu giếm được Ngài. Ai vượt giới cấm của Ngài, cố tình săn bắt trong lúc đang trong tình trang Ihram cho nghi thức Hajj hoặc Umrah thì người đó sẽ bị trừng phạt đau đớn vào Ngày Phán Xét vì y đã phạm điều Allah cấm đoán.');
INSERT INTO "vn_mokhtasar" VALUES(5,95,'5:95','Hỡi những người có đức tin, đừng giết thú săn trong lúc các ngươi đang trong tình trạng Ihram (hãm mình) cho nghi thức Hajj hoặc Umrah. Ai cố tình giết thú săn thì y sẽ phải chịu phạt bằng cách giết tế để đền lại một con vật tương tự với con thú săn đã bị giết, từ loài gia súc: lạc đà, bò, hay cừu; người đó phải thực hiện việc làm đó trước sự chứng kiến của hai người đàn ông Muslim được cho là công bằng, trung trực và phải được thực hiện tại khu vực Al-Haram; nếu không thực hiện được việc giết tế thì người đó phải định giá của con vật qua thức ăn để phân phát cho những người nghèo sống trong vùng Al-Haram, mỗi người nghèo là nửa Sa'', còn không thì người đó phải nhịn chay tương ứng với mỗi nửa Sa'' thức ăn. Tất cả những sự việc đó là nhằm để người giết thú săn nếm lấy hậu quả của việc cố tình vi phạm điều cấm. Allah xí xóa cho những hành vi phạm việc giết thú săn trước khi lệnh cấm ban hành nhưng ai ngoan cố tái phạm sau khi lệnh cấm đã ban hành thì Allah sẽ trừng phạt kẻ đó. Bởi quả thật, Allah là Đấng Quyền Lực và Hùng Mạnh. Và một trong những biểu hiện sức mạnh và uy quyền của Ngài là xử phạt đối với những ai làm trái lệnh Ngài, và không ai có thể cản trở được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,96,'5:96','Allah cho phép các ngươi săn bắt các loại động vật dưới nước, và những gì trong biển dù sống hay chết đều là nguồn thực phẩm cho các ngươi lúc các ngươi đang tại nơi định cư hay là người lữ hành. Tuy nhiên, Ngài cấm các ngươi săn bắt thú trên cạn trong lúc các ngươi đang trong tình trạng Ihram của nghi thức Hajj hoặc Umrah. Các ngươi hãy kính sợ Allah mà thức hiện các mệnh lệnh của Ngài cũng như tránh xa những điều Ngài ngăn cấm; bởi quả thật Ngài là Đấng mà các ngươi phải quay trở về trình diện vào Ngày Phục Sinh để Ngài thưởng hoặc phạt theo những việc mà các ngươi đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(5,97,'5:97','Allah làm cho Ngôi Đền Thiêng Liêng Ka''bah thành chỗ đứng cho nhân loại để họ sinh hoạt tôn giáo như: Dâng lễ nguyện Salah, Hajj, Umrah và để cải thiện cuộc sống của họ như: Được an ninh trong vùng đất thiêng và được hưởng nhiều loại trái cây; và Ngài làm cho các tháng trở nên thiêng liêng (đó là tháng Zdul-Qa''dah, Zdul-hijjah, Muharram và Rajab) để dựng lên việc an ninh trong những thời gian đó, và các con vật được mang đi giết tế cũng như những con vật được đeo vòng cổ để làm dấu để mang đi giết tế mục đích không để chúng bị tổn hại. Tất cả những điều đó là ân huệ từ nơi Allah ban cho các ngươi để các ngươi biết rằng Ngài am tường và thông toàn tất cả mọi thứ trong các tầng trời và trên trái đất, và Ngài là Đấng biết tất cả mọi thứ. Quả thật, hệ thống giáo luật về những điều đó - nhằm cải thiện cho ngươi cũng như để ngăn những điều tai hại - là bằng chứng về sự am tường của Allah về những điều cải thiện cho đám bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,98,'5:98','Hỡi nhân loại, các ngươi hãy biết rằng Allah rất nghiêm khắc trong việc trừng phạt những ai trái lệnh Ngài nhưng đồng thời Ngài cũng là Đấng Hằng Tha Thứ và Khoan Dung cho những ai quay về sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,99,'5:99','Thiên Sứ của Allah chỉ có nhiệm vụ truyền đạt những gì mà Allah ra lệnh phải truyền đạt chứ Y không có quyền và không có khả năng khiến cho ai đó được hướng dẫn. Quyền hạn và khả năng đó chỉ nằm trong tay một mình Ngài. Và Allah biết rõ tất cả những gì các ngươi công khai cũng như những gì các ngươi giữ kín, rồi đây Ngài sẽ thưởng phạt thích đáng cho tất cả các việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(5,100,'5:100','Hỡi Thiên Sứ của Allah, Ngươi hãy nói: những thứ ô uế, bẩn thỉu không bao giờ có thể ngang bằng với những thứ tốt sạch cho dù sự dồi dào của chúng có làm cho ngươi ưa thích bởi lẽ số lượng nhiều không là bằng chứng rằng chúng tốt hơn. Do đó, các ngươi hãy sợ Allah hỡi những người hiểu biết trong việc từ bỏ những thứ ô uế dơ bẩn để đến với những thứ tốt lành và thanh sạch, hy vọng rằng các ngươi sẽ đạt được Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(5,101,'5:101','Hỡi những người có đức tin, các ngươi chớ hỏi Thiên Sứ của các ngươi về những gì không cần thiết cho các ngươi cũng như những gì không giúp ích cho các vụ việc tôn giáo của các ngươi. Và nếu những điều đó được trình bày thì chúng sẽ khiến các ngươi thêm rắc rối. Nếu các ngươi thắc mắc những điều bị cấm hỏi này lúc những Lời Mặc Khải đang được ban xuống cho Thiên Sứ Muhammad thì Nó sẽ giải trình cho các ngươi, điều đó đối với Allah rất đơn giản. Quả thật, Allah không bắt tội về những thứ mà Qur''an đã im lặng. Do đó, các ngươi chớ đừng hỏi về những thứ đó vì nếu các ngươi hỏi về chúng thì chúng sẽ đè nặng trách nhiệm lên các ngươi bởi giáo luật của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(5,102,'5:102','Quả thật, nhóm người trước thời các ngươi cũng đã thắc mắc những điều giống như vậy, nhưng rồi khi họ được áp đặt luật định trên những thứ họ thắc mắc thì họ không chấp hành, vì thế họ trở thành những kẻ vô đức tin bởi sự việc đó.');
INSERT INTO "vn_mokhtasar" VALUES(5,103,'5:103','Allah cho phép các ngươi dùng các gia súc, Ngài không cấm các ngươi những thứ mà những kẻ thờ đa thần ngăn cấm bản thân họ vì các thần linh bục tượng của họ như Bahirah (lạc đà cái bị cắt tai nếu nó đẻ ra một số lượng con non nhất định nào đó), Sa-ibah (lạc đà cái khi đến một độ tuổi nhất định nào đó thì sẽ được làm vật tế cho các thần linh bục tượng của họ), Wasilah (lạc đà cái đẻ ra các lứa con đều là con cái), và Ham (lạc đà đực được lấy giống và cho ra một số các con đực nhất định nào đó thì nó không được cưỡi và không được dùng để chuyên chở). Tất cả những thứ này, chính những kẻ vô đức tin tự bản thân chúng đã khẳng định một cách dối trá rằng những thứ đó Haram rồi đổ thừa Allah đã nghiêm cấm. Quả thật, đa số những kẻ vô đức tin không phân biệt được giữa những điều sai trái và chân lý, giữa những thứ được phép và không được phép.');
INSERT INTO "vn_mokhtasar" VALUES(5,104,'5:104','Khi những kẻ nói dối cho Allah này nói về việc cấm một số loại gia súc "các người hãy đến với điều mà Allah đã ban xuống trong Qur''an và hãy đến với Sunnah của vị Thiên Sứ của Ngài để biết đâu là những thứ Haram và đâu là những thứ Halal" thì chúng nói "chúng tôi đi theo ông bà cha mẹ của chúng tôi là đủ rồi". Há như thế là đủ cho chúng ngay cả khi ông bà cha mẹ của chúng không hiểu biết gì cũng như không được hướng dẫn đến chân lý ư?! Bởi thế, chẳng ai đi theo ông bà cha mẹ (thiếu hiểu biết) của họ ngoại trừ những người thiếu hiểu biết và lầm lạc hơn họ.');
INSERT INTO "vn_mokhtasar" VALUES(5,105,'5:105','Hỡi những người có đức tin, các ngươi phải bắt bản thân các ngươi thực hiện và chấp hành theo đúng mệnh lệnh (của Allah), việc ai đó trong nhân loại lầm lạc và không đáp lại sự hướng dẫn của các ngươi thì cũng không ảnh hưởng gì đến các ngươi miễn sao các ngươi được hướng dẫn đúng đường, và một trong những sự hướng dẫn của các ngươi là các ngươi kêu gọi người làm điều thiện tốt và ngăn cản người làm điều xấu. Rồi đây vào Ngày Phục Sinh, tất cả các ngươi sẽ trở về trình diện Allah, Ngài sẽ cho các ngươi biết những gì các ngươi đã làm trên cuộc sống thế gian và Ngài sẽ thưởng hoặc phạt các ngươi một cách thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(5,106,'5:106','Hỡi những người có đức tin, khi ai đó trong các ngươi sắp chết được nhận biết qua các dấu hiệu của nó và y muốn làm di chúc thì y hãy yêu cầu hai người Muslim được cho là chính trực và ngay thẳng làm chứng cho việc lập di chúc đó; nếu không có người Muslim nào làm chứng thì hãy nhờ đến hai người đàn ông vô đức tin khi thực sự cần; các ngươi hãy nên lập di chúc khi các ngươi đi xa và lo ngại cái chết sẽ đến với các ngươi. Và nếu có sự nghi ngờ về việc làm chứng của hai người được yêu cầu thì các người hãy để cho hai người đó đứng sau cuộc lễ nguyện Salah và thề với Allah rằng họ sẽ không bán những cam kết với Allah dù đó là người thân thích của họ, và họ cũng không giấu giếm bất cứ điều gì trong sự chứng giám của họ, và nếu họ làm như thế thì họ sẽ là những kẻ tội lỗi và đại nghịch với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(5,107,'5:107','Nếu sau khi đã thề thì phát hiện ra hai người làm chứng đó đã nói dối hoặc có sự gian lận thì hãy cử hai người khác thuộc họ hàng gần nhất với người chết đứng ra thế chỗ cho hai người làm chứng đó. Hai người cũng được yêu cầu thề với Allah rằng họ phải trung thực và đáng tin hơn hai người làm chứng trước và nếu họ gian dối thì họ là những kẻ làm điều sai quấy đã vượt quá giới hạn của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(5,108,'5:108','Việc bắt hai người làm chứng phải thề sau cuộc lễ nguyện Salah khi nghi ngờ sự làm chứng của hai người kia là nhằm để hai người họ không bóp méo, thay đổi hoặc gian lận trong việc làm chứng hoặc ít ra để họ sợ sau này lời thề của họ sẽ không được chấp nhận sau lời thề gian dối đó. Và các ngươi hãy kính sợ Allah mà từ bỏ việc nói dối, gian lận trong việc làm chứng và cho lời khai man; Các ngươi hãy nghe theo mệnh lệnh chỉ đạo bởi quả thật Allah không phù hộ cho những kẻ bất tuân Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,109,'5:109','Này hỡi nhân loại, các ngươi hãy nhớ rằng vào Ngày Phục Sinh khi Allah tập hợp tất cả các vị Thiên Sứ của Ngài lại và Ngài hỏi họ: Những cộng đồng của các ngươi mà TA đã cử các ngươi đến với họ đã đáp lại lời kêu gọi của các ngươi như thế nào? Họ đồng thưa: Bầy tôi không biết, quả thật sự hiểu biết là ở nơi Ngài, quả thật chỉ có Ngài mới biết được những điều vô hình.');
INSERT INTO "vn_mokhtasar" VALUES(5,110,'5:110','Hãy nhớ lại lúc Allah phán câu chuyện với Ysa: Này hỡi Ysa con trai của Maryam, Ngươi hãy nhớ lại các Ân Huệ của TA ban cho Ngươi về việc TA tạo ra Ngươi không cần có cha, việc TA đã làm cho mẹ của Ngươi thành người phụ nữ đức hạnh nhất trong thời của bà, việc TA hỗ trợ Ngươi sức mạnh bởi Đại Thiên Thần Jibril để Ngươi nói chuyện với dân chúng lúc Người còn nằm nôi và lúc Ngươi trưởng thành, việc TA đã dạy Ngươi Kinh Tawrah - Kinh sách được ban xuống cho Musa và Kinh Injil được ban xuống cho Ngươi, việc TA đã dạy Ngươi sự khôn ngoan, việc TA ban cho Ngươi khả năng tạo ra con chim sống từ đất sét, việc TA ban cho Ngươi khả năng chữa sáng mắt cho người mù, chữa lành cho người bệnh cùi, làm cho người chết sống lại, việc TA giúp Ngươi thoát khỏi đám dân Israel khi chúng muốn giết hại Ngươi do Ngươi đã trưng bày những phép lạ rõ ràng nhưng họ đã phủ nhận và rằng những gì Ngươi mang đến rõ ràng chỉ là một trò ảo thuật.');
INSERT INTO "vn_mokhtasar" VALUES(5,111,'5:111','Hãy nhớ lại một trong các ân huệ mà TA đã ban cho Ngươi - hỡi Ysa - là TA đã ban cho Ngươi những người trợ lực, đó là đám Hawariyun (những học trò của Ngươi), TA đã mặc khải cho họ bảo họ tin nơi TA và ở nơi Ngươi và họ đã đáp lại, họ nói: bầy tôi đã tin, xin Ngài hãy chứng giám rằng bầy tôi là những người Muslim (thần phục Ngài).');
INSERT INTO "vn_mokhtasar" VALUES(5,112,'5:112','Và hãy nhớ lại lúc Hawariyun nói với Ysa: Thượng Đế của Người có thể nào ban từ trên trời xuống một chiếc bàn ăn đầy thực phẩm khi Người cầu xin Ngài không? Thế là Ysa đã trả lời họ bằng cách bảo họ hãy kính sợ Allah và từ bỏ sự đòi hỏi đó bởi e rằng trong sự việc ấy sẽ mang lại Fitnah (sự thử thách) cho họ, và Y nói với họ: các ngươi hãy phó thác cho Thượng Đế của các ngươi trong việc tìm kiếm nguồn bổng lộc nếu các ngươi là những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(5,113,'5:113','Đám Hawariyun nói với Ysa: Chúng tôi muốn ăn từ chiếc bàn đầy thực phẩm đó, chúng tôi muốn lòng mình vững tin về sự toàn năng của Allah và Người đích thực là vị Thiên Sứ của Ngài, chúng tôi muốn biết chắc rằng Người đã trung thực về những gì Người mang đến từ nơi Allah, và chúng tôi muốn là những người làm chứng về nó cho những ai trong nhân loại không có mặt để chứng kiến nó.');
INSERT INTO "vn_mokhtasar" VALUES(5,114,'5:114','Thế là Ysa đáp lại yêu cầu của họ, Y cầu nguyện Allah, nói: Lạy Thượng Đế của bây tôi, xin Ngài hãy ban từ trên trời xuống cho bầy tôi một chiếc bàn ăn đầy thực phẩm để bầy tôi lấy ngày nó được ban xuống làm ngày đại lễ mục đích để tôn vinh nó như là một sự tạ ơn Ngài, để làm dấu hiệu chứng minh tính Duy Nhất của Ngài, và để chứng minh cho sứ mạng mà bề tôi được cử phái đến, xin Ngài ban cho bầy tôi những bổng lộc giúp bầy tôi trong việc thờ phượng Ngài, ôi Thượng Đế của bầy tôi, Ngài là Đấng ban bổng lộc tốt nhất.');
INSERT INTO "vn_mokhtasar" VALUES(5,115,'5:115','Allah chấp nhận lời cầu nguyện của Ysa và Ngài phán: Quả thật, TA sẽ ban xuống cho các ngươi một chiếc bàn ăn mà các ngươi đòi hỏi. Nhưng sau khi nó được ban xuống mà ai đó trong các ngươi không có đức tin thì y chớ than trách ai ngoài bản thân y, bởi TA chắc chắn sẽ trừng phạt y bằng một hình phạt khủng khiếp mà TA chưa từng trừng phạt bất kỳ ai. Nguyên nhân do y đã chứng kiến phép lạ quá rõ ràng của TA nhưng không có đức tin thì y là kẻ vô đức tin một cách ngoan cố. Rồi Allah đã chứng thực lời hứa của Ngài và đã ban xuống cho họ một chiếc bàn ăn như đã hứa.');
INSERT INTO "vn_mokhtasar" VALUES(5,116,'5:116','Hãy nhớ đến lúc Allah sẽ phán bảo Ysa con trai của Maryam vào Ngày Phục Sinh: Này Ysa con trai của Maryam, có phải ngươi đã bảo nhân loại "các ngươi hãy lấy ta và mẹ của ta làm thượng đế ngoài Allah" đúng không? Ysa liền thưa với Thượng Đế của Y: Bề tôi không nên nói bất cứ điều gì ngoại trừ đó là sự thật và chân lý, nhưng nếu bề tôi thực sự đã nói như thế thì chắc chắn Ngài đã biết bởi vì không có điều gì có thể che giấu được Ngài, Ngài biết rõ những gì trong lòng và trong suy nghĩ của bề tôi nhưng bề tôi lại không biết gì trong bản thân Ngài. Quả thật chỉ một mình Ngài mới là Đấng biết rõ và thông toàn mọi điều vô hình, mọi điều ẩn khuất và mọi điều công khai hé lộ.');
INSERT INTO "vn_mokhtasar" VALUES(5,117,'5:117','Ysa thưa với Thượng Đế của Y: Bề tôi không hề nói bất cứ điều gì ngoại trừ điều Ngài đã ra lệnh cho bề tôi phải nói, đó là "hãy thờ phượng một mình Ngài". Và bề tôi đã trông chừng những điều họ nói trong suốt thời gian bề tôi còn sống cùng với họ. Nhưng khi Ngài đưa bề tôi lên nơi Ngài thì Ngài - ôi Thượng Đế của bề tôi - là Đấng giám sát các việc làm của họ và Ngài là Đấng làm chứng cho tất cả mọi sự việc, không có điều gì có thể giấu giếm được Ngài, những gì bề tôi đã nói với họ cũng như những gì họ nói sau khi bề tôi ra đi đều không giấu giếm được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(5,118,'5:118','Ôi Thượng Đế của bề tôi, nếu Ngài trừng phạt họ thì họ vẫn là những người bề tôi của Ngài, Ngài làm gì với họ là quyền của Ngài, nhưng nếu Ngài tha thứ cho họ thì chẳng có gì cản trở Ngài làm điều đó bởi Ngài là Đấng Quyền Năng không có gì có thể cưỡng lại và là Đấng Anh Minh trong việc xử trí và định đoạt các sự việc.');
INSERT INTO "vn_mokhtasar" VALUES(5,119,'5:119','Allah phán với Ysa: Đây là Ngày mà những người chân thật trong tâm niệm, hành động và lời nói của họ được hưởng lợi từ sự chân thật của họ, họ được ban cho các Ngôi Vườn Thiên Đàng, bên dưới có các dòng sông chảy, họ sẽ sống trong đó mãi mãi, cái chết không còn xảy ra với họ, Allah sẽ hài lòng với họ, Ngài sẽ không phẫn nộ và giận dữ với họ nữa, và họ sẽ thỏa mãn và toại nguyện về những gì Ngài ban cho nơi Thiên Đàng hạnh phúc. Phần thưởng và sự toại nguyện đó là một sự thành công vĩ đại, không có sự thành công nào sánh bằng.');
INSERT INTO "vn_mokhtasar" VALUES(5,120,'5:120','Quyền thống trị các tầng trời và trái đất bởi Ngài, và Ngài là Đấng Tạo Hóa ra chúng rồi điều hành, chi phối chúng; và quyền thống trị vạn vất giữa trời đất cũng thuộc về Ngài, Ngài là Đấng Toàn Năng trên tất cả mọi thứ, không có thứ gì làm Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(6,1,'6:1','Mô tả thuộc tính hoàn hảo tuyệt đối, ca ngợi bởi những lời tán dương cao cả cùng với tình yêu thương cho Allah. Khẳng định Allah là Đấng đã tạo hóa các tầng trời và trái đất và trước đó không có sự vật nào giống như vậy, Ngài là Đấng đã tạo ban đêm và ban ngày nối tiếp nhau liên tục, Ngài tạo ban đêm cho bóng tối, ban ngày cho ánh sáng. Với sự tạo hóa vĩ đại như thế nhưng những kẻ vô đức tin vẫn dựng lên các thần linh khác cùng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,2,'6:2','Hỡi nhân loại, Allah là Đấng đã tạo các ngươi từ đất sét - khi mà Ngài tạo Adam, tổ tiên của các ngươi, từ nó - rồi Ngài quy định tuổi đời của các ngươi trên thế gian, và Ngài quy định một tuổi đời khác mà chỉ có Allah mới biết rõ, đó là Ngày Tận Thế và Phục Sinh; nhưng rồi các ngươi cứ luôn ngờ vực về quyền năng phục sinh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,3,'6:3','Allah là Đấng đáng được thờ phượng, Ngài hiện diện trong các tầng trời và trái đất, không có thứ gì có thể giấu giếm được Ngài. Ngài biết những gì các ngươi che giấu từ tâm niệm, lời nói và hành động; và Ngài biết những gì các người công khai; và chắc chắn Ngài sẽ phân xét và thưởng phạt tất cả.');
INSERT INTO "vn_mokhtasar" VALUES(6,4,'6:4','Không một bằng chứng nào đến với những kẻ thờ đa thần mà chúng không mặc kệ. Quả thật, đã được đưa đến với chúng những bằng chứng chứng minh rõ ràng về tính duy nhất của Allah, nhiều dấu lạ đã đến với họ để chứng minh sự trung thực của các vị Thiên Sứ. Tuy nhiên, dù vậy chúng vẫn quay lưng, không chịu tin.');
INSERT INTO "vn_mokhtasar" VALUES(6,5,'6:5','Nếu chúng quay lưng với những bằng chứng rõ ràng đó thì quả thật chúng đã quay lưng với những gì rõ ràng hơn. Chúng đã phủ nhận những điều mà Muhammad saw mang đến từ Qur''an. Và rồi đây, chúng sẽ sớm biết được hậu quả của những điều mà chúng đã chế giễu khi chúng đối diện với sự trừng phạt vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(6,6,'6:6','Chẳng lẽ những kẻ vô đức tin này không biết đường lối của Allah là hủy diệt các cộng đồng sai quấy ư?! Quả thật, Allah đã hủy diệt trước những kẻ vô đức tin này nhiều cộng đồng, những cộng đồng mà Ngài đã ban cho họ nhiều nguồn lợi dồi dào trên trái đất, Ngài đã ban cho họ lượng nước mưa liên tục, tạo ra cho họ những con sông chảy bên dưới nhà của họ, nhưng họ đã làm trái mệnh lệnh của Allah nên Ngài đã hủy diệt họ và tạo ra những thế hệ khác thời sau họ.');
INSERT INTO "vn_mokhtasar" VALUES(6,7,'6:7','Hỡi Thiên Sứ Muhammad, nếu TA (Allah) ban xuống cho Ngươi một Kinh Sách được ghi trên các tờ giấy rồi chúng tận mặt chứng kiến và chúng sờ những tờ Kinh đó bằng chính đôi tay của chúng để xác nhận là thật thì chắc chắn chúng cũng sẽ không tin vào Nó, và chúng nói: những gì ngươi mang đến chỉ là trò ảo thuật rõ ràng, bọn ta không bao giờ tin nơi nó.');
INSERT INTO "vn_mokhtasar" VALUES(6,8,'6:8','Những người vô đức tin này nói: "Nếu Allah phái xuống cùng với Muhammad một vị Thiên Thần để ngài nói chuyện với bón ta và xác nhận Y là Thiên Sứ thì chắc chắn bọn ta đã tin." (Sự việc sẽ không như thế) nếu TA (Allah) ban xuống một vị Thiên Thần như chúng muốn thì chắc chắn TA đã hủy diệt chúng khi chúng không tin và chúng không tìm thấy cơ hội để sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(6,9,'6:9','Và nếu TA (Allah) dựng Thiên Thần làm Sứ Giả gửi đến chúng thì TA cũng sẽ làm cho Thiên Thần mang hình hài của một người phàm để chúng có thể đối mặt trực diện và nghe được từ y, bởi quả thật, chúng không có khả năng đó khi Thiên Thần xuất hiện trên hình hài thực sự mà TA đã tạo ra. Và cho dù TA thực sự làm cho Thiên Thần mang hình hài một người phàm thì chắc chắn chúng cũng sẽ hoài nghi.');
INSERT INTO "vn_mokhtasar" VALUES(6,10,'6:10','Nếu những kẻ vô đức tin này chế giễu Ngươi - Muhammad - bằng cách đòi Thiên Thần xuống cùng với Ngươi thì những vị Thiên Sứ trước Ngươi cũng bị các cộng đồng của họ chế giễu tương tự. Tuy nhiên, rồi đây sự trừng phạt cho việc chúng đã chế giễu và phủ nhận họ sẽ vây hãm chúng.');
INSERT INTO "vn_mokhtasar" VALUES(6,11,'6:11','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ phủ nhận và chế giễu này: Các người hãy đi chu du khắp trái đất rồi hãy suy ngẫm về kết cuộc của những kẻ phủ nhận các vị Thiên Sứ của Allah. Quả thật sự trừng phạt đã xảy đến với họ sau khi họ đã từng có sức mạnh và sự phồn thịnh.');
INSERT INTO "vn_mokhtasar" VALUES(6,12,'6:12','Hỡi Thiên Sứ Muhammad, Ngươi hãy hỏi chúng: Các tầng trời và trái đất cũng như vạn vật giữa chúng thuộc về ai? Ngươi hãy nói với chúng khẳng định rằng tất cả đều thuộc về Allah, Ngài tự quy định cho bản thân Ngài phải thương xót các bề tôi của Ngài. Bởi thế, các bề tôi của Ngài chớ hối thúc sự trừng phạt mau đến với họ. Nếu họ không sám hối, Ngài vẫn tập trung họ lại tất cả vào Ngày Phán Xét. Đấy là Ngày chắc chắn sẽ đến, không có gì phải hoài nghi. Chỉ có những kẻ làm thiệt bản thân mình bởi sự vô đức tin nơi Allah mới không tin; họ chính là những kẻ thua cuộc.');
INSERT INTO "vn_mokhtasar" VALUES(6,13,'6:13','Tất cả vạn vật, tồn tại trong đêm và ngày, đều thuộc về một mình Allah, và Ngài là Đấng nghe thấy tất cả mọi lời nói của họ, Ngài biết hết mọi hành động của họ, và Ngài sẽ đền bù xứng đáng cho lời nói và hành động của họ.');
INSERT INTO "vn_mokhtasar" VALUES(6,14,'6:14','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những người thờ đa thần, những kẻ mà họ thờ phượng cùng với Allah những thần linh bục tượng khác: Làm sao mà Ta lại nhận lấy ai khác ngoài Allah làm đấng bảo hộ trong khi Ngài là Đấng đã tạo ra các tầng trời và trái đất, những thứ chưa từng có trước đó, là Đấng ban phát bổng lộc cho ai Ngài muốn trong đám bề tôi của Ngài và Ngài không cần bất cứ ai ban bổng lộc, tất cả đám bề tôi của Ngài luôn lệ thuộc vào Ngài và cần đến Ngài, sao mà Ta có thể làm như thế? Ngươi hãy nói - hỡi Thiên Sứ Muhammad - : Quả thật, Thượng Đế của Ta ra lệnh cho Ta phải là người thần phục Ngài đầu tiên trong cộng đồng này và Ngài cấm Ta trở thành một trong những kẻ thờ tổ hợp thần linh khác cùng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,15,'6:15','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói: Quả thật, nếu Ta bất tuân và làm trái lệnh Allah bởi việc vi phạm các điều cấm từ việc làm Shirk và tội lỗi khác hoặc không chấp hành các mệnh lệnh của Ngài từ việc tin nơi Ngài và tuân lệnh Ngài thì Ta sợ Ngài sẽ trừng phạt Ta bằng một hình phạt khủng khiếp vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(6,16,'6:16','Ai được Allah cứu rỗi khỏi sự trừng phạt đó vào Ngày Phục Sinh thì quả thật y là người đã nhận được lòng thương xót của Ngài. Và sự cứu rỗi khỏi sự trừng phạt đó là một thắng lợi rõ ràng, không có thắng lợi nào hơn nó cả.');
INSERT INTO "vn_mokhtasar" VALUES(6,17,'6:17','Và nếu Allah cho tai họa ập đến với ngươi - hỡi con cháu của Adam (con người) - thì không một ai có thể đẩy nó ra khỏi ngươi ngoại trừ bản thân Ngài; và nếu Ngài ban điều tốt lành đến cho ngươi thì không có gì có thể ngăn cản được bởi Ngài là Đấng Toàn Năng trên tất cả mọi thứ, mọi sự việc, không có gì có thể làm Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(6,18,'6:18','Ngài là Đấng Quyền Lực vượt trên các bề tôi của Ngài, không có gì có thể làm Ngài bất lực và không một ai có thể vượt qua sức mạnh của Ngài, tất cả đều nhỏ bé, thấp hèn, yếu ớt trước Ngài, Ngài vượt trên các bề tôi của Ngài về mọi mặt bằng sự tối cao và vinh quang của Ngài. Và Ngài là Đấng Sáng Suốt trong việc tạo hóa, điều hành và định đoạt, và Ngài là Đấng Thông Toàn mọi thứ, không có gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,19,'6:19','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những người thờ đa thần đã phủ nhận Ngươi: "Cái gì là bằng chứng lớn nhất cho sự trung thực của Ta?" Ngươi hãy nói: "Bằng chứng lớn nhất cho sự trung thực của Ta là sự chứng giám của Allah, Ngài làm chứng những gì giữa Ta và các ngươi, Ngài biết điều Ta mang đến cho các ngươi và việc các ngươi từ chối như thế nào. Quả thật, Ngài đã mặc khải cho Ta Kinh Qur''an này để cảnh báo các ngươi và cảnh báo những ai mà Nó tiếp xúc từ loài người và Jinn. Hỡi những kẻ thờ đa thần, quả thật, các người tin có các thần linh khác cùng song hành với Allah ư? Ngươi - Muhammad, hãy nói: Còn Ta không chứng nhận điều mà các ngươi chứng nhận bởi vì đó là sự ngụy tạo. Quả thật, chỉ có một Thượng Đế duy nhất, đó là Allah, không có đối tác ngang vai cùng với Ngài, và Ta vô can với tất cả những gì các ngươi đã gán ghép cho Allah.');
INSERT INTO "vn_mokhtasar" VALUES(6,20,'6:20','Những người Do Thái được TA (Allah) ban cho Kinh Tawrah (Cựu Ước) và những người Thiên Chúa được TA ban cho Kinh Injil (Tân Ước) đều nhận biết Muhammad là một vị Nabi, một vị Thiên Sứ giống như việc họ nhận biết con cái của họ. Họ là những người làm bản thân mình thua thiệt và thất bại bởi đã tự đẩy mình vào trong Hỏa Ngục vì đã không có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(6,21,'6:21','Không ai sai quấy hơn những kẻ đã gán ghép thần linh ngang vai với Allah rồi thờ phượng nó hoặc phủ nhận các lời mặc khải của Ngài đã được ban xuống cho vị Thiên Sứ của Ngài. Quả thật, những kẻ làm điều sai quấy trong việc gán ghép thần linh cùng với Allah và phủ nhận các lời mặc khải của Ngài sẽ không bao giờ thắng lợi nếu không quay đầu sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(6,22,'6:22','Và hãy nghĩ đến Ngày Phán Xét khi mà TA (Allah) sẽ tập trung họ lại tất cả, TA không bỏ sót một ai trong bọn họ, rồi TA sẽ nói với những kẻ thờ phượng cùng với Allah các thần linh khác ngoài Ngài với ý nghĩa hạ nhục họ: Đâu là những thần linh mà các ngươi đã gán ghép và cho rằng chúng là thần linh ngang vai với Allah?!');
INSERT INTO "vn_mokhtasar" VALUES(6,23,'6:23','Rồi họ không tìm thấy lý do nào để trở nên vô can với việc thờ cúng các thần linh của họ mà họ đã dựng lên, và họ chỉ biết nói một cách giả đối (hầu để thoát tội): Xin thề bởi Allah, bầy tôi đã không là những người thờ đa thần trên thế gian mà bầy tôi đã tin nơi Ngài và chỉ thừa nhận một mình Ngài là Thượng Đế.');
INSERT INTO "vn_mokhtasar" VALUES(6,24,'6:24','Này Muhammad, Ngươi hãy xem những kẻ này đã tự lừa dối bản thân mình như thế nào trong việc phủ nhận điều Shirk (gán ghét thần linh ngang vái với Allah) mà chúng đã làm. Và những thần linh ngụy tạo mà chúng gán ghép cho Allah trên thế gian đã bỏ họ đi mất biệt.');
INSERT INTO "vn_mokhtasar" VALUES(6,25,'6:25','Hỡi Thiên Sứ Muhammad, một số kẻ thờ đa thần có nghe khi Ngươi đọc Qur''an. Tuy nhiên, chúng không gặt hái được bất cứ lợi ích nào khi nghe Nó bởi vì TA (Allah) đã bao kín quả tim của chúng để chúng không hiểu được Qur''an, nguyên nhân là do sự ngoan cố và việc quay lưng làm ngơ của chúng. TA đã làm cho tai của chúng như điếc không nghe được những điều lợi ích. Và cho dù chúng có nhìn thấy các dấu hiệu một cách rõ ràng đi chăng nữa thì chúng cũng không tin. Thậm chí chúng còn đến tìm Ngươi để tranh luận phủ nhận điều chân lý thay bằng những điều ngụy tạo và giả dối, chúng nói: Những gì ngươi mang đến thật ra chỉ được lấy từ những quyển sách ở những thời trước mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(6,26,'6:26','Chúng ngăn cản thiên hạ tin nơi Thiên Sứ Muhammad và tránh xa Y, chúng không để người khác tiếp thu những điều lợi ích từ Y và chúng cũng vậy. Tuy nhiên, hành động đó thật ra chỉ hủy hoại bản thân chúng nhưng chúng không biết.');
INSERT INTO "vn_mokhtasar" VALUES(6,27,'6:27','Hỡi Thiên Sứ Muhammad, nếu Ngươi có thể nhìn thấy lúc chúng được phơi bày cho thấy Hỏa Ngục vào Ngày Phán Xét thì chúng sẽ than trong sự hối tiếc muộn màng: Ôi, ước gì chúng tôi được cho trở lại trần gian một lần nữa thì chúng tôi sẽ không phủ nhận các lời mặc khải của Allah và chúng tôi sẽ là những người có đức tin nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,28,'6:28','Họ đã nói nếu được cơ hội trở lại trần gian lần nữa thì họ sẽ có đức tin, sự việc sẽ không như họ nói mà đó chỉ là điều giả dối giống như khi được Allah hỏi thì họ nói "Xin thề với Allah, bầy tôi đã không là những kẻ vô đức tin". Và cho dù họ được quay trở về trần gian lần nữa thì họ vẫn trở lại với sự vô đức tin và Shirk; Quả thật, họ là những người nói dối trong lời hứa về đức tin của họ nếu họ quay trở lại.');
INSERT INTO "vn_mokhtasar" VALUES(6,29,'6:29','Những kẻ thờ đa thần này nói: Chẳng có cuộc sống nào khác ngoài cuộc sống mà chúng ta đang sống này - cuộc sống trần gian, và chúng ta không sẽ không được phục sinh để phán xét gì cả.');
INSERT INTO "vn_mokhtasar" VALUES(6,30,'6:30','Và nếu Ngươi - hỡi Thiên Sứ Muhammad - nhìn thấy cảnh những kẻ phủ nhận sự Phục Sinh bị bắt đứng trước Thượng Đế của chúng thì chắc chắn Ngươi sẽ rất đỗi ngạc nhiên về tình trạng của chúng khi Allah phán bảo chúng: Há sự Phục Sinh này, điều mà các ngươi đã luôn phủ nhận nó, không phải là sự thật sao? Các ngươi còn gì nghi ngờ nữa không? Chúng nói: Bầy tôi thề bởi Thượng Đế của bầy tôi, Đấng đã tạo hóa bầy tôi rằng đó là sự thật, một sự thật kiên định không có gì phải nghi ngờ. Thế là Allah phán: Vậy thì các ngươi hãy nếm lấy sự trừng phạt cho việc vô đức tin vào Ngày Này và cho việc các ngươi đã phủ nhận nó lúc còn trên cõi trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(6,31,'6:31','Quả thật, những kẻ phủ nhận sự Phục Sinh vào Ngày Phán Xét cũng như sự trình diện trước Allah chắc chắn sẽ là những kẻ thua thiệt. Họ cứ phủ nhận cho tới khi Giờ Tận Thế bất ngờ xảy đến thì mới kêu than trong hối tiếc muộn màng: "Ôi thật hối hận thay vì đã thờ ơ và hờ hững với điều mà Allah đã cảnh báo và việc không chuẩn bị cho Ngày Phán Xét." Và họ sẽ gánh các tội lỗi của họ trên lưng, há không tồi tệ hay sao cho những ai gánh trên lưng những điều xấu đó?!');
INSERT INTO "vn_mokhtasar" VALUES(6,32,'6:32','Thế giới trần gian mà các ngươi đang sống chỉ là trò vui chơi và thú tiêu khiển đầy dối trá cho những ai không làm vì sự hài lòng của Allah. Riêng đối với thế giới cõi Đời Sau mới thực sự là nơi ở tốt nhất cho những ai kính sợ Allah mà hành động theo mệnh lệnh của Ngài bằng đức tin và sự tuân thủ, từ bỏ những điều trái lệnh và Shirk mà Ngài nghiêm cấm. Hỡi những kẻ thờ đa thần, há các ngươi không chịu động não để hiểu ra điều đó để các ngươi tin và làm những việc làm thiện tốt ư?!');
INSERT INTO "vn_mokhtasar" VALUES(6,33,'6:33','Hỡi Thiên Sứ Muhammad, TA (Allah) biết rằng Ngươi đau buồn cho việc chúng đã phủ nhận Ngươi nhưng Ngươi hãy biết rằng chúng chỉ phủ nhận Ngươi ở bên ngoài còn trong lòng của chúng thì chúng không phủ nhận Ngươi, chúng biết Ngươi trung thực và đáng tin; tuy nhiên, do chúng là một nhóm người sai quấy nên bề ngoài thì cãi lại mệnh lệnh của Ngươi những trong thâm tâm chúng thì vẫn tin kiên định.');
INSERT INTO "vn_mokhtasar" VALUES(6,34,'6:34','Ngươi đừng nghĩ rằng việc Ngươi bị người dân phủ nhận những gì Ngươi mang đến là chỉ xảy ra đối với Ngươi thôi mà thật ra các vị Thiên Sứ trước Ngươi cũng đã bị phủ nhận và bị xâm hại bởi cộng đồng dân chúng của họ. Họ đã đối diện với những khó khăn đó bằng sự kiên nhẫn, họ kiên nhẫn rao truyền kêu gọi và hết lòng chiến đấu cho con đường chính nghĩa của Allah cho tới khi được Allah trợ giúp. Và không ai có thể làm thay đổi lời phán của Allah khi Ngài hứa trợ giúp các vị Thiên Sứ của Ngài giành thắng lợi. Hỡi Thiên Sứ Muhammad, quả thật, đã đến với Ngươi thông tin về các vị Thiên Sứ về việc họ đã đối mặt với cộng đồng dân chúng của họ như thế nào, về việc họ được Allah yêu thương như thế nào và đã trợ giúp họ giành thắng lợi trước kẻ thù như thế nào.');
INSERT INTO "vn_mokhtasar" VALUES(6,35,'6:35','Hỡi Thiên Sứ Muhammad, nếu việc Ngươi gặp phải sự phủ nhận và cự tuyệt của dân chúng về điều chân lý Ngươi mang đến là trở ngại và khó khăn lớn đối với Ngươi thì nếu như Ngươi có khả năng, Ngươi hãy tìm một cái lỗ dưới đất để chui xuống hoặc tìm một cái thang leo lên trời để mang đến cho dân chúng của Ngươi một bằng chứng mà không cần sự trợ giúp của TA thì hãy làm. Quả thật, nếu Allah muốn, Ngài thừa sức tập hợp tất cả họ lại trên sự hướng dẫn mà Ngươi mang đến cho họ, nhưng Ngài không muốn điều đó bởi một ý nghĩa và giá trị lớn lao khác. Bởi thế, Ngươi chớ để bản thân mình trở thành một trong những kẻ không hiểu biết, khiến bản thân mình hối tiếc chỉ vì dân chúng của Ngươi không tin.');
INSERT INTO "vn_mokhtasar" VALUES(6,36,'6:36','Chỉ những ai nghe và hiểu thì mới đáp lại những gì Ngươi (Muhammad) mang đến. Còn những kẻ vô đức tin là những linh hồn đã chết bởi trái tim của họ đã chết. Và những người chết, vào Ngày Phán Xét Allah sẽ phục sinh họ và bắt họ trở về trình diện Ngài để Ngài ban thưởng hoặc trừng phạt thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(6,37,'6:37','Những kẻ thờ đa thần nói để phủ nhận đức tin: Sau Muhammad không được ban cho một phép lạ nào từ Thượng Đế của y để chứng minh những gì y mang đến là sự thật? Ngươi - Muhammad - hãy nói: Quả thật, Allah thừa khả năng ban xuống một phép lạ theo những gì chúng muốn. Tuy nhiên, đa số những kẻ vô đức tin này, những kẻ mà chúng đòi ban xuống phép lạ không biết những phép lạ khi được ban xuống mang ngụ ý gì từ nơi Allah, nên Ngài không ban xuống, vả lại nếu các phép lạ được ban xuống mà chúng không tin thì chắc chắn Allah sẽ hủy diệt chúng.');
INSERT INTO "vn_mokhtasar" VALUES(6,38,'6:38','Không có một loài thú nào sống trên trái đất, không có một loài chim nào bay trên trời mà lại không sống kết đoàn thành cộng đồng giống như các ngươi - hỡi con cháu Adam (con người). TA (Allah) không hề bỏ sót bất kỳ một thứ gì trong Quyển Kinh Mẹ (Văn bản Lưu Trữ - Lawhul-Mahfuzh), tất cả đều nằm trong kiến thức và sự thông toàn của Ngài. Rồi đây, vào Ngày Phán Xét, tất cả đều phải quay về trình diện Thượng Đế của chúng để Ngài phân xét và thưởng phạt một cách thỏa đáng.');
INSERT INTO "vn_mokhtasar" VALUES(6,39,'6:39','Và những kẻ phủ nhận những lời Mặc Khải của TA (Allah) giống như những người điếc không nghe gì, những người câm không nói chuyện, và chúng câm điếc đi trong bóng tối không thấy gì, thế thì làm sao chúng có thể được hướng dẫn trong tình trạng như vậy? Ai trong nhân loại mà Allah muốn làm cho lầm lạc thì y sẽ lầm lạc còn ai mà Ngài muốn hướng dẫn thì người đó sẽ được hướng dẫn trên con đường ngay chính.');
INSERT INTO "vn_mokhtasar" VALUES(6,40,'6:40','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần này: Nếu các người nói thật về việc các người cho rằng các người có các thần linh có thể mang lại điều lành và đẩy lùi điều xấu thì các người hãy cho Ta biết liệu các người sẽ cầu xin ai khác ngoài Allah cứu rỗi các người thoát khỏi tai họa khi hình phạt của Ngài hoặc Giờ Tận Thế xảy đến với các người như đã được hứa?');
INSERT INTO "vn_mokhtasar" VALUES(6,41,'6:41','Sự thật, lúc đó, chắc chắn các ngươi sẽ không cầu xin ai khác ngoài Allah, Đấng đã tạo hóa các ngươi bởi vì Ngài mới là Đấng xua đuổi tai họa khỏi các ngươi, và chỉ có Ngài mới có quyền năng đó; còn những thần linh mà các ngươi tổ hợp cùng với Ngài thì chắc chắn các ngươi sẽ từ bỏ chúng vì các ngươi biết chúng không mang lợi ích cũng không gây hại được gì.');
INSERT INTO "vn_mokhtasar" VALUES(6,42,'6:42','Quả thật, thời trước Ngươi - hỡi Thiên Sứ Muhammad - TA (Allah) đã cử phái các vị Thiên Sứ đến với các cộng đồng nhưng chúng đã phủ nhận họ, chúng quay lưng với những gì họ mang đến. Thế là TA đã trừng phạt chúng bởi những khó khăn như sự nghèo đói và bởi những tai hại đến thân thể của chúng như bệnh tật mục đích để chúng quy phục Thượng Đế của chúng và hạ mình trước Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,43,'6:43','Nếu chúng chịu hạ mình phủ phục trước Allah khi Ngài giáng xuống tai họa lên chúng thì chắc chắn Ngài đã giải thoát chúng khỏi những tai họa đó và đã thương xót chúng, nhưng chúng đã không làm thế do trái tim của chúng đã chai cứng nên không nhận thức được sự việc và hơn nữa Shaytan đã làm cho chúng thấy việc chúng vô đức tin và làm điều tội lỗi là tốt, là đẹp đối với chúng nên chúng cứ tiếp tục làm những điều tội lỗi đó.');
INSERT INTO "vn_mokhtasar" VALUES(6,44,'6:44','Khi chúng bỏ qua những lời nhắc nhở và cảnh báo từ sự khốn khó của nghèo đói và bệnh tật, và chúng không chịu làm theo mệnh lệnh của Allah thì Ngài mở ra cho chúng mọi cánh cửa của bổng lộc, chúng trở nên giàu có sau cái nghèo, chúng trở nên khỏe mạnh sau bệnh tật, cho tới khi chúng không còn biết đến lòng tri ân và chỉ biết vui say tận hưởng những bổng lộc thì Allah đột ngột trừng phạt chúng khiến chúng chết lặng trong vô vọng.');
INSERT INTO "vn_mokhtasar" VALUES(6,45,'6:45','Những kẻ vô đức tin, tất cả họ đều bị hủy diệt toàn bộ, không sót một ai, các vị Thiên Sứ của Allah được ban cho sự thắng lợi, mọi lời ca ngợi và tán dương dâng lên một mình Allah, Đấng Chủ Tể của vũ trụ và muôn loài cho việc đã hủy diệt kẻ thù của Ngài và ban thắng lợi cho những Wali của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,46,'6:46','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần này: Các người hãy cho Ta biết nếu Allah làm cho tai các ngươi không nghe được, mắt của các ngươi không nhìn thấy được và Ngài khóa trái tim của các ngươi lại để các ngươi không hiểu ra điều gì thì đấng thờ phượng đích thực nào sẽ mang đến cho các người những gì các ngươi đã mất đó? Hỡi Thiên Sứ Muhammad, Ngươi xem TA (Allah) đã trình bày rõ ràng cho họ các lý lẽ với những bằng chứng rõ ràng nhưng rồi chúng vẫn quay lưng bỏ đi.');
INSERT INTO "vn_mokhtasar" VALUES(6,47,'6:47','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với họ: Các người hãy cho ta biết nếu sự trừng phạt của Allah xảy đến với các người một cách bất ngờ mà các người không hề hay biết hoặc nó xảy đến một cách công khai mà các ngươi đang trông đợi thì chẳng phải chỉ có đám người làm điều sai quấy bởi sự vô đức tin của họ nơi Allah và bởi sự phủ nhận của họ đối với các vị Thiên Sứ của Ngài mới hứng chịu sự trừng phạt đó?');
INSERT INTO "vn_mokhtasar" VALUES(6,48,'6:48','TA (Allah) cử các vị Thiên Sứ của TA đến chỉ nhằm múc đích để họ báo tin mừng cho những người có đức tin và tuân lệnh về những ân huệ trường tồn và bất tận (Thiên Đàng) đồng thời cảnh báo những kẻ vô đức tin và trái lệnh về sự trừng phạt khủng khiếp của TA (Hỏa Ngục). Bởi thế, ai tin các vị Thiên Sứ và cải thiện việc làm của mình thì sẽ không lo sợ cho những gì đang chờ đón y ở cõi Đời Sau và cũng sẽ không buồn phiền và hối tiếc cho những gì đã mất trên cõi trần.');
INSERT INTO "vn_mokhtasar" VALUES(6,49,'6:49','Ngược lại, đối với những ai phủ nhận các lời Mặc Khải của TA (Allah) thì những người đó sẽ phải đối mặt với sự trừng phạt do đã bất tuân TA.');
INSERT INTO "vn_mokhtasar" VALUES(6,50,'6:50','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần này: "Ta không nói với các ngươi rằng ở nơi Ta có kho trữ bổng lộc của Allah để Ta có thể quyết định như thế nào Ta muốn, Ta cũng không nói với các ngươi rằng Ta biết những điều ở cõi vô hình trừ những gì Allah phơi bày cho Ta từ sự mặc khải của Ngài, và Ta cũng không nói với các người rằng Ta là Thiên Thần, mà quả thật Ta chỉ là Thiên Sứ được Allah cử phái đến, Ta chỉ đi theo những gì được mặc khải cho Ta mà thôi, Ta không hề tự suy tôn những gì không thuộc quyền và trách nhiệm của Ta." Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với họ: "Chẳng lẽ người vô đức tin không nhìn thấy điều chân lý và người có đức tin nhìn thấy chân lý và tin vào nó lại ngang hàng với nhau ư? Sao các ngươi không chịu dùng trí óc mà suy ngẫm - hỡi những người thờ đa thần - về những dấu hiệu xung quanh các ngươi?"');
INSERT INTO "vn_mokhtasar" VALUES(6,51,'6:51','Hỡi Thiên Sứ Muhammad, Ngươi hãy dùng Kinh Qur''an này cảnh báo những người sợ bị triệu tập trình diện Thượng Đế của họ vào Ngày Phán Xét, Ngày mà họ sẽ không tìm thấy người bảo hộ nào ngoài Allah để can thiệp về những quyền lợi cho họ và giúp họ tránh khỏi tai họa, mong rằng với lời cảnh báo đó họ biết sợ Allah mà chấp hành mệnh lệnh của Ngài và tránh xa những điều Ngài nghiêm cấm. Họ chính là những người thu được lợi ích từ Qur''an.');
INSERT INTO "vn_mokhtasar" VALUES(6,52,'6:52','Hỡi Thiên Sứ Muhammad, Ngươi chớ xua đuổi những người Muslim nghèo khỏi chỗ giảng đạo của Ngươi, những người mà họ luôn thờ phượng Allah sáng chiều một cách thành tâm vì Ngài. Ngươi chớ xua đuổi họ để thu hút những kẻ đa thần danh vọng chức quyền, Ngươi không gánh chịu cho những người nghèo đó bất cứ một điều gì và họ cũng không gánh chịu cho Ngươi bất cứ một điều gì. Quả thật, nếu Ngươi xua đuổi họ thì chắc chắn Ngươi là kẻ đã vượt quá giới hạn của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(6,53,'6:53','Tương tự, TA (Allah) đã thử thách một số người này bởi một số người kia, TA đã cho họ vượt trội lẫn nhau về bổng lộc ở cõi trần. TA đã thử thách họ bởi điều đó để những người vô đức tin giàu có nói về những người có đức tin nghèo khó: "Chẳng lẽ những người nghèo này được Allah ban hồng phúc cho họ bằng sự hướng dẫn hơn cả chúng ta sao?! Nếu đức tin thực sự tốt hơn thì chúng ta đã nhanh chân đến với nó trước để chúng ta là những người thắng cuộc." Chẳng lẽ Allah không biết ai là những người thực sự biết ơn ân huệ của Ngài sao? Thế nên Ngài đã ban cho họ, những người nghèo đức tin Iman, còn những kẻ vô đức tin kia, Ngài biết họ không tin nên Ngài làm cho họ xem thường để họ không tin. Quả thật, Allah biết về họ hơn ai hết.');
INSERT INTO "vn_mokhtasar" VALUES(6,54,'6:54','Hỡi Thiên Sứ Muhammad, khi những người có đức tin nơi các dấu hiệu của TA, những dấu hiệu mà nó minh chứng cho sự trung thực của Ngươi về những gì Ngươi mang đến, thì Ngươi hãy đáp lại lời chào của họ bằng lời chào Salam (chào an lành!) để thể hiện sự trân trọng dành cho họ, đồng thời Ngươi hãy báo tin mừng cho họ rằng lòng thương xót của Allah rất bao la, quả thật Allah đã bắt bản thân mình phải có lòng thương xót và khoan dung; bởi thế, ai lỡ phạm vào điều trái lệnh Ngài do thiếu hiểu biết và sự ngu muội rồi vội quay về sám hối với Ngài và cải thiện việc làm của mình thì quả thật Allah chắc chắn sẽ tha thứ tội lỗi của y bởi vì Allah là Đấng Hằng Tha Thứ và Khoan Dung đối với những bề tôi nào quay về sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,55,'6:55','TA (Allah) đã trình bày cho Ngươi những gì đã được nêu ra, và cũng giống như thế, TA trình bày các lập luận và lý lẽ của TA chống lại những kẻ sai quấy và làm rõ con đường của những kẻ tội lỗi xấu xa, mục đích để các ngươi cảnh giác và né tránh.');
INSERT INTO "vn_mokhtasar" VALUES(6,56,'6:56','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói: Quả thật Ta bị Allah cấm thờ phượng những thần linh mà các người thờ phượng ngoài Ngài. Ngươi hãy nói - hỡi Thiên Sứ Muhammad: Ta không làm theo ý của các ngươi trong việc thờ phượng những thần linh ngoài Allah, bởi nếu Ta theo ý của các ngươi trong sự việc đó thì Ta là kẻ lầm lạc khỏi con đường chân lý, Ta không còn là người được hướng dẫn đến với nó, tất cả sự việc của những ai đi theo dục vọng và sở thích riêng của họ đều không có bằng chứng từ nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(6,57,'6:57','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần này: Quả thật Ta đang dựa trên bằng chứng rõ ràng từ Thượng Đế của Ta, không phải trên ý muốn và sở thích riêng của cá nhân, các người đã phủ nhận bằng chứng này, ở nơi Ta chẳng có sự trừng phạt cũng như những dấu hiệu diệu kỳ mà các người hối thúc và đòi hỏi, quả thật những thứ đó là ở nơi Allah, quyền định đoạt cho tất cả những gì các người yêu cầu đều thuộc về nơi một mình Allah, Ngài nói sự thật và định đoạt nó, và Ngài là Đấng Phân Định tốt nhất giữa chân lý và ngụy tạo.');
INSERT INTO "vn_mokhtasar" VALUES(6,58,'6:58','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với chúng: Nếu ở nơi Ta có những hình phạt mà các người hối thúc chúng mau đến thì chắc chắn Ta sẽ giáng chúng xuống các ngươi và lúc đó sự việc giữa Ta và các người sẽ được giải quyết. Tuy nhiên, Allah biết rõ về những kẻ sai quấy, Ngài biết rõ khi nào bỏ mặc họ và khi nào trừng phạt họ.');
INSERT INTO "vn_mokhtasar" VALUES(6,59,'6:59','Mọi kho trữ những điều vô hình đều nằm ở nơi một mình Allah, chỉ có Ngài mới biết rõ chúng, Ngài biết mọi vật trong đất liền từ động vật, thực vật và các vật vô tri vô giác khác, Ngài biết mọi vật trong biển cả từ động vật, thực vật và các vật vô tri vô giác khác, không một chiếc lá nào rơi khỏi vị trí của nó cũng như không một loại hạt nào được gieo xuống đất mà Ngài không biết, tất cả đều nằm trong kiến thức của Ngài, không một thứ xanh tươi hay khô héo nào mà Ngài không thông toàn, tất cả đều được ghi rõ trong Lawhul-Mahfuzh - Bản Văn Lưu Trữ ở nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,60,'6:60','Và Allah là Đấng bắt giữ linh hồn các ngươi lúc ngủ như là một tình trạng tạm thời của cái chết và Ngài biết những gì các ngươi làm vào ban ngày. Rồi sau khi Ngài bắt linh hồn các ngươi trong lúc ngủ, thì đến ban ngày Ngài làm cho các ngươi sống lại để các ngươi tiếp tục công việc của các ngươi cho tới khi thời hạn tuổi đời của các ngươi chấm đứt theo qui định của Ngài. Sau đó, các ngươi sẽ trở về Ngài bằng sự phục sinh vào Ngày Phán Xét, rồi Ngài sẽ cho các ngươi biết những gì các ngươi đã làm trong cuộc sống của các ngươi trên thế gian, và Ngài sẽ xét xử và thưởng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(6,61,'6:61','Allah là Đấng Quyền Lực trên các bề tôi của Ngài, Ngài bên trên (nắm quyền) họ ở tất cả mọi mặt, tất cả mọi thứ phải phủ phục và hạ mình trước Ngài. Và Ngài đã cử các Thiên Thần Vinh Dự - hỡi nhân loại - theo dõi và giám sát những việc làm của các ngươi cho tới khi các ngươi kết thúc tuổi đời bằng việc bắt hồn của Malak-Almawt (Thiên Thần Chết) và các Thiên Thần phụ tá Y, và tất cả họ đều không chểnh mảng trong công việc của mình.');
INSERT INTO "vn_mokhtasar" VALUES(6,62,'6:62','Rồi đây, tất cả những linh hồn bị bắt đưa về trình diện Allah, Đức Vua đích thực của họ, để Ngài thưởng phạt các việc làm của họ,mọi quyền phán xét và định đoạt đều thuộc về một mình Ngài, Ngài phán xét công bằng, và Ngài là Đấng nhang chóng trong việc thanh toán và phán xét.');
INSERT INTO "vn_mokhtasar" VALUES(6,63,'6:63','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần này: "Ai đã cứu các người an toàn khỏi những nguy hiểm mà các người gặp phải trong bóng tối của đất liền và biển cả? Các người đã hạ mình cầu xin một cách âm thầm và công khai: Lạy Thượng Đế nếu Ngài cứu chúng tôi an toàn khỏi tai họa này thì chắc chắn chúng tôi sẽ là những người biết ơn ân huệ mà Ngài ban cho chúng tôi và chúng tôi không thờ phượng ai khác ngoài Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(6,64,'6:64','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với chúng: Chính Allah là Đấng đã cứu các người thoát khỏi cảnh nguy khốn đó và cho các người an toàn khỏi mọi tai họa khác. Nhưng rồi sau đó, các người lại tổ hợp các thần linh cùng với Ngài khi các người trong tình trạng bình an. Vậy có điều gì sai quấy hơn những hành động đó của các người?!');
INSERT INTO "vn_mokhtasar" VALUES(6,65,'6:65','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói: Allah thừa khả năng gởi đến các ngươi những hình phạt từ trên cao như đá, giông tố, sấm sét và lũ lụt hoặc từ bên dưới các ngươi như động đất và sạt lỡ hoặc Ngài làm cho các ngươi không đồng tâm làm các ngươi xung đột và giết chóc lẫn nhau. Hỡi Thiên Sứ Muhammad, Ngươi suy ngẫm xem TA (Allah) trình bày nhiều bằng chứng và lập luận để mong rằng chúng sẽ hiểu ra những gì Ngươi mang đến là chân lý và những gì ở nơi họ là sai trái và ngụy tạo.');
INSERT INTO "vn_mokhtasar" VALUES(6,66,'6:66','Dân chúng của Ngươi - Muhammad - đã phủ nhận Kinh Qur''an này trong khi Nó là chân lý, và không có gì phải nghi ngờ rằng nó đến từ nơi Allah. Ngươi - hỡi Thiên Sứ Muhammad - hãy nói với chúng: Ta không được giao nhiệm trông chừng và giám sát các người mà Ta chỉ là một người cảnh báo các người về sự trừng phạt khủng khiếp ở phía trước các người mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(6,67,'6:67','Mọi thông tin đều được xác nhận bởi thời gian ấn định, chúng sẽ thể hiện khi thời gian đã tới, trong đó có kết cục của của các ngươi. Rồi đây các ngươi sẽ sớm biết điều đó khi các ngươi được phục sinh vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(6,68,'6:68','Hỡi Thiên Sứ Muhammad, khi nào Ngươi bắt gặp những kẻ thờ đa thần đang chế giễu và bỡn cợt về những lời phán của TA thì Ngươi hãy lánh xa chúng cho tới khi chúng nói sang chuyện khác. Nếu Shaytan làm cho ngươi quên trong lúc Ngươi đang ngồi cùng với chúng rồi khi Ngươi nhớ lại thì hãy rời đi khỏi chỗ của chúng, Ngươi chớ ngồi cùng với những kẻ vượt quá giới hạn này.');
INSERT INTO "vn_mokhtasar" VALUES(6,69,'6:69','Những người ngoan đạo sợ Allah qua việc thực thi các mệnh lệnh của Ngài và lánh xa những điều Ngài nghiêm cấm không phải chịu trách nhiệm bất cứ điều gì về hành động của những kẻ làm điều sai trái và tội lỗi, tuy nhiên, họ cần phải khuyên răn hy vọng rằng những kẻ đó biết quay đầu sợ Allah mà thực thi các mệnh lệnh của Ngài và tránh những điều Ngài cấm.');
INSERT INTO "vn_mokhtasar" VALUES(6,70,'6:70','Hỡi Thiên Sứ Muhammad, Ngươi hãy mặc kệ những kẻ thờ đa thần này, những kẻ mà chúng lấy tôn giáo của chúng để làm trò vui chơi, để châm biếm và giễu cợt, những kẻ mà chúng bị cuộc sống trần gian lừa dối. Ngươi - hỡi Thiên Sứ Muhammad - hãy dùng Qur''an mà khuyên răn nhân loại để họ an toàn khỏi con đường tự hủy do phạm các điều tội lỗi xấu xa. Linh hồn sẽ không tìm thấy ngoài Allah vị cứu tinh nào cũng như sẽ không tìm thấy ai giúp đỡ tránh khỏi sự trừng phạt của Allah vào Ngày Phán xét. Và nếu linh hồn có đem bất cứ thứ gì để chuộc bẩn thân khỏi sự trừng phạt đó của Ngài nó cũng không được chấp nhận. Những kẻ đã làm điều sai quấy đó, vào Ngày Phát Xét, họ sẽ được cho uống một loại nước cực nóng và bị trừng phạt vô cùng đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(6,71,'6:71','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những người thờ đa thần này: "Chẳng lẽ chúng tôi lại đi thờ phượng ngoài Allah những bục tượng không mang lại lợi ích cũng chẳng gây hại cho chúng tôi ư? Chẳng lẽ chúng tôi lại rời bỏ đức tin Iman sau khi Allah đã ban cho chúng tôi đạt được nó ư? Chẳng lẽ chúng tôi lại giống kẻ bị Shaytan dắt đi lạc và bỏ mặc ngu ngơ không được hướng dẫn trong khi y có những người bạn đang đi trên đường ngay chính kêu gọi y đến với chân lý và y cứ khăng khăng từ chối nghe theo ư?" Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với chúng: "Quả thật, sự Chỉ Đạo của Allah mới là sự Chỉ Đạo đúng đắn và chân lý. Và quả thật, Allah đã ra lệnh cho chúng tôi phải phủ phục và quy thuận Ngài bằng sự độc tôn Ngài trong thờ phượng bởi Ngài là Đấng Chủ Tể của vũ trụ và muôn loài."');
INSERT INTO "vn_mokhtasar" VALUES(6,72,'6:72','Và quả thật, Ngài (Allah) ra lệnh cho chúng tôi phải chu đáo dâng lễ nguyện Salah, Ngài ra lệnh cho chúng tôi phải kính sợ Ngài bằng việc thực thi các mệnh lệnh của Ngài và tránh xa những điều cấm của Ngài, bởi Ngài là Đấng Duy Nhất mà tất cả các bề tôi phải quay trở về trình diện trước Ngài vào Ngày Phán Xét để Ngài xét xử và thưởng phạt cho các việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(6,73,'6:73','Và Ngài (Allah) là Đấng đã tạo các tầng trời và trái đất vì điều chân lý hiển nhiên. Vào Ngày mà Allah sẽ nói vào Ngày Phục Sinh: ''Các ngươi hãy trở dậy!'' thì tất cả đều sống và trở dậy từ cõi mộ bởi vì Ngài là Đấng khi nói với một điều gì ''Hãy thành!'' thì nó sẽ thành đúng như thế. Lời Nói của Ngài là chân lý, những gì Ngài phán chắc chắn phải xảy ra. Và vào Ngày Phán Xét khi đại Thiên Thần Israfil thổi chiếc Sừng với tiếng còi thứ hai, mọi quyền hành đều thuộc về một mình Allah. Ngài là Đấng thông toàn mọi điều vô hình và hữu hình, Ngài là Đấng Thông Thái trong việc tạo hóa và điều hành các tạo vật của Ngài, Ngài là Đáng Am Tường tất cả mọi thứ, không có bất cứ thứ gì có thể che giấu được Ngài, mọi sự việc ẩn mình bên trong đối với Ngài cũng sáng tỏ giống như những sự việc bên ngoài.');
INSERT INTO "vn_mokhtasar" VALUES(6,74,'6:74','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại khi Ibrahim nói với cha của mình, một người thờ đa thần, tên Azar: Này cha của con, sao cha lại nhận các bục tượng làm thần linh và thờ cúng chúng ngoài Allah? Quả thật, con thấy cha và cộng đồng của cha, những người đang thờ phượng các bục tượng rõ ràng đang đi trên con đường lầm lạc, không phải là con đường chân lý. Chỉ có Allah mới là Đấng Thờ Phượng đích thực, còn những ai (vật) ngoài Ngài đều là ngụy tạo.');
INSERT INTO "vn_mokhtasar" VALUES(6,75,'6:75','Giống như việc TA (Allah) để cho Ibrahim thấy sự lầm lạc của cha mình, TA cho Y thấy vương quyền chi phối và điều hành các tầng trời và trái đất để cho Y trở nên vững lòng tin hơn rằng Allah là Đấng Duy Nhất, Ngài không có đối tác ngang vai, và Ngài là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(6,76,'6:76','Khi màn đêm buông xuống, Ibrahim nhìn thấy một vì sao trên trời, Người nói: Đây là Thượng Đế của ta. Nhưng khi vì sao khuất dạng thì Người nói rằng Người không yêu thích những gì khuất dạng bởi Thương Đế là Chân Lý, Ngài luôn hiện diện không biến mất.');
INSERT INTO "vn_mokhtasar" VALUES(6,77,'6:77','Và khi thấy mặt trăng nhô lên thì Ibrahim nói: Đây là Thượng Đế của ta. Nhưng khi mặt trăng khuất dạng thì Người nói: Nếu Allah không hướng dẫn ta đến với Tawhid và sự thờ phượng một mình Allah thì chắc chắn ta sẽ trở thành một kẻ rời khỏi tôn giáo chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(6,78,'6:78','Và khi thấy mặt trời mọc, Ibrahim nói: Đây là Thượng Đế của ta, vị này lớn hơn vì sao và mặt trăng. Và khi thấy mặt trời lặn mất thì Người nói: Này hỡi người dân, quả thật, Ta vô can với những gì mà các người đã tổ hợp với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(6,79,'6:79','Quả thật, Ta thành tâm hướng về Đấng đã tạo hóa các tầng trời và trái đất một cách chưa từng có trước đó, Ta hoàn toàn nghiêng khỏi điều Shirk (sự tổ hợp) để đến với Tawhid (độc tôn Allah), và Ta không là một kẻ thờ đa thần thờ những thần linh khác ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,80,'6:80','Cộng đồng thờ đa thần của Người (Ibrahim) đã tranh luận với Người về việc độc tôn Allah trong thờ phượng, họ lấy các thần linh bục tượng của họ đe dọa Người nhưng Người nói với họ: Chẳng lẽ các người tranh luận với Ta về việc độc tôn Allah trong thờ phượng trong khi Thượng Đế của Ta đã phù hộ Ta đến với điều đó, quả thật, Ta không hề sợ các thần linh bục tượng của các người bởi chúng không có quyền năng làm hại được Ta và cũng chẳng thể mang lại lợi ích gì cho Ta cả. Tất cả mọi thứ đều phụ thuộc vào ý muốn của Allah, những gì Ngài muốn thì nó sẽ xảy ra, những gì Ngài không muốn nó không bao giờ xảy ra, kiến thức và sự hiểu biết của Ngài bao trùm tất cả mọi thứ, không một điều gì trong trời đất có thể che giấu được Ngài. Thế các ngươi chưa thức tỉnh sao? Các người không được vô đức tin nơi Allah và tổ hợp các thần linh cùng với Ngài mà các người tin nơi một mình Ngài và thờ phượng một mình Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,81,'6:81','Sao Ta lại sợ những thứ mà các người thờ phượng ngoài Allah từ những bục tượng kia trong khi các ngươi không hề lo sợ cho việc các ngươi đã tổ hợp các thần linh cùng với Allah một cách không có bằng chứng và cơ sở nào cho sự việc đó cả? Vậy trong hai nhóm chúng ta: nhóm những người độc tôn Allah và nhóm những người thờ đa thần, nhóm nào được bằng an và an toàn nếu các ngươi thực sự biết? Dĩ nhiên, chắc chắn là nhóm những người độc tôn Allah trong thờ phượng mới là nhóm được bằng an và an toàn.');
INSERT INTO "vn_mokhtasar" VALUES(6,82,'6:82','Những người có đức tin nơi Allah, đi theo những gì Ngài sắc lệnh, không pha trộn đức tin với Shirk (sự tổ hợp các đối tác ngang vai cùng với Allah), là những người được an toàn và bằng an, chỉ có họ đạt được điều đó mà không ai khác ngoài họ đạt được, và họ là những người được Thượng Đế của họ phù hộ và hướng dẫn trên con đường Chỉ Đạo.');
INSERT INTO "vn_mokhtasar" VALUES(6,83,'6:83','Và lập luận đó - câu nói "nhóm nào trong hai nhóm được an toàn" - cái mà brahim đã tranh luận và làm cho cộng đồng người dân của Y đuối lý chính là lập luận mà TA (Allah) đã ban cho Y để đối đáp với họ. Và TA nâng cấp bậc cho ai TA muốn từ đám bề tôi của TA ở cõi đời này và cõi Đời Sau. Quả thật, Thượng Đế của Ngươi - hỡi Thiên Sứ Muhammad - là Đấng Sáng Suốt trong việc tạo hóa và chi phối các tạo vật, là Đấng Thông Toàn về những bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,84,'6:84','TA (Allah) đã ban Ibrahim đứa con trai Ishaq và đứa cháu nội Ya''qub, cả hai đều được TA hướng dẫn trên con đường Ngay Chính, và trước đó TA cũng đã hướng dẫn Nuh, và trong thế hệ con cháu của Ibrahim, TA cũng đã hướng dẫn Dawood, Sulayman con trai của Dawood, Ayyub, Yusuf, Musa và người em của Y Harun. Và cũng giống như việc TA đã ân thưởng cho các vị Nabi bởi những việc làm tốt của họ, ngoài họ TA cũng sẽ ân thưởng cho những ai làm điều tốt.');
INSERT INTO "vn_mokhtasar" VALUES(6,85,'6:85','Và TA cũng đã hướng dẫn Zakariya, Yahya, Ysa con trai của Maryam và Ilyas. Tất cả các vị Nabi này đều là những người ngoan đạo mà TA đã chọn làm các Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(6,86,'6:86','Tương tự, TA (Allah) đã hướng dẫn Isma''il, Al-Yasa'', Yunus và Lut. Tất cả các vị Nabi này đều được TA ban ân huệ nhiều hơn những ai khác trong vũ trụ này, trong đó Nabi Muhammad saw là đứng đầu trong đặc ân này.');
INSERT INTO "vn_mokhtasar" VALUES(6,87,'6:87','Và một số cha mẹ, một số con cái, và một số anh em của họ cũng được TA (Allah) hướng dẫn đến với Chính Đạo, đó là con đường độc tôn Allah và tuân lệnh Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,88,'6:88','Những gì mà họ được hướng dẫn là do sự phù hộ của Allah dành cho những ai Ngài muốn trong đám bề tôi của Ngài, và nếu họ tổ hợp với Allah một thần linh nào cùng với Ngài thì tất các việc làm của họ đều trở nên vô giá trị bởi sự tổ hợp hủy hoại các việc làm ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(6,89,'6:89','Các vị Nabi được đề cập đó là những người mà TA (Allah) đã ban cho họ các Kinh Sách, sự thông thái và sứ mạng truyền giáo. Bởi thế, nếu cộng đồng của Ngươi (Muhammad) phủ nhận ba thứ đó mà TA đã ban cho họ thì quả thật TA sẽ giao chúng cho những người khác không những không phủ nhận chúng mà còn tin một cách kiên định và giữ chặt chúng; và họ là những người Muhajir (cư dân Makkah đến tị nạn tại Madinah) và những người Ansar (cư dân Madinah) và những người đi theo họ trên con đường tốt đẹp cho tới Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(6,90,'6:90','Những vị Nabi đó và những người được nhắc đến từ cha mẹ, con cái và anh em của họ là những người thực sự được Allah hướng dẫn. Bởi thế, Ngươi - Thiên Sứ Muhammad phải đi theo đường lối của họ và Ngươi hãy nói với cộng đồng người dân của Ngươi: Ta không đòi các người bất cứ phần thưởng nào từ việc truyền đạt Kinh Qur''an này, mà quả thật Qur''an chỉ là một thông điệp nhắc nhở cho thiên hạ: con người và loài Jinn để tất cả được hướng dẫn trên Chính Đạo, một con đường đúng đắn.');
INSERT INTO "vn_mokhtasar" VALUES(6,91,'6:91','Và những kẻ thờ đa thần đã không tôn vinh Allah đúng với giá trị của Ngài khi họ nói với Thiên Sứ Muhammad: "Allah chẳng ban xuống bất cứ thứ gì từ Sự Mặc Khải cho người phàm cả." Ngươi - hỡi Thiên Sứ Muhammad, hãy nói với chúng: "Thế ai đã ban xuống Kinh Tawrah (Cựu Ước) cho Musa để làm Ánh Sáng và nguồn Chỉ Đạo cho cộng đồng người dân của Người? Kinh Sách mà những người Do Thái đã phơi bày một phần tương đồng với ý muốn của họ thành các tờ Kinh và giấu đi một phần mà nó trái với ý muốn của họ." Ngươi - hỡi Thiên Sứ Muhammad - hãy bảo chúng: "Chính Allah là Đấng đã ban Qur''an xuống" rồi Ngươi hãy mặc kệ chúng trong sự ngu muội và lầm lạc cho tới khi điều kiên định xảy đến (tức cái chết).');
INSERT INTO "vn_mokhtasar" VALUES(6,92,'6:92','Và Qur''an này là Kinh Sách mà TA (Allah) đã ban xuống cho Ngươi - hỡi Thiên Sứ Muhammad - là một Kinh Sách phúc lành, Nó xác nhận lại những điều trong các Thiên Kinh trước đó, Nó được ban xuống để Ngươi cảnh báo cư dân Makkah và những người khác còn lại trên trái đất này. Và những ai có đức tin nơi cuộc sống ở cõi Đời Sau thì chắc chắn sẽ tin nơi Qur''an này và sẽ làm theo những gì trong Nó, họ sẽ chu đáo duy trì các lễ nguyện Salah của họ một cách tốt đẹp bằng việc giữ vựng các trụ cột của nó, giữ vựng những điều bắt buộc của nó, và giữ vựng những gì được khuyến khích thực hiện trong những giới hạn thời gian nhất định để thực hiện nó đúng với giáo luật.');
INSERT INTO "vn_mokhtasar" VALUES(6,93,'6:93','Không một ai sai quấy hơn kẻ nói không đúng cho Allah rằng Allah không ban xuống bất cứ điều gì cho người phàm hoặc nói dối rằng Allah đã mặc khải xuống cho y trong khi Allah không hề ban xuống cho y bất cứ điều gì hoặc y nói rằng ta sẽ ban xuống giống với những gì được Allah ban xuống trong Qur''an. Hỡi Thiên Sứ Muhammad, nếu Ngươi nhìn thấy cảnh tưởng những kẻ làm điều sai quấy này giẫy giụa lúc chết trong khi các Thiên Thần giăng tay hành hạ trừng phạt chúng và nói với chúng: Linh hồn các ngươi hãy mau xuất ra để bọn ta bắt các ngươi. Vào ngày hôm nay các ngươi sẽ phải chịu sự trừng phạt cho việc các ngươi đã nói dối cho Allah và cho việc các ngươi đã tự cao tự đại không chịu tin những lời phán của Ngài. Và nếu Ngươi (Muhammad) nhìn thấy cảnh tượng đó thì chắc chắn Ngươi đã nhìn thấy một cảnh tượng khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(6,94,'6:94','Vào Ngày Phục Sinh, chúng được bảo: Quả thật, ngày hôm nay các ngươi đến trình diện TA (Allah) trong hoàn cảnh một thân một mình, không tiền bạc, không quyền hành giống như TA đã tạo các ngươi ở lần đầu tiên trong hoàn cảnh trần trụi (mới sinh) và các ngươi đã bỏ lại sau lưng các ngươi trên thế gian tất cả những gì TA đã ban cho. Và ngày hôm nay, TA không thấy một kẻ thần linh nào - từ những thần linh mà các ngươi cho rằng chúng là đại diện cho các ngươi và chúng là đối tác ngang vai cùng với Allah đáng được thờ phượng - đồng hành cùng các ngươi cả. Quả thật, mối quan hệ giữa các ngươi đã bị cắt đứt hoàn toàn, những thần linh mà các ngươi đã cho rằng chúng sẽ can thiệp cho các ngươi đã bỏ các ngươi đi mất dạng.');
INSERT INTO "vn_mokhtasar" VALUES(6,95,'6:95','Quả thật, chỉ một mình Allah là Đấng đã chẻ hạt giống để làm mọc ra cây cối, hoa màu và thảo dược; chỉ có duy nhất Ngài là Đấng đã chẻ hạt chà là làm mọc ra cây chà là; chỉ có duy nhất Ngài là Đấng lấy sự sống ra từ cái chết, đó là con người và tất cả các loài động vật đều được Ngài tạo ra từ Nutfah (hợp tử được hình thành từ hai cá thế đực cái); và chỉ một mình Ngài duy nhất lấy cái chết ra từ cái sống, đó là Nutfah từ con người, trứng từ con gà, và các loài vật khác. Đấng đã làm những thứ đó chính Allah, Đấng mà Ngài đã tạo ra các ngươi. Thế sao các ngươi lại quay lưng với điều chân lý - hỡi những kẻ thờ đa thần - khi mà các ngươi đã chứng kiến sự kì diệu trong cách tạo hóa của Ngài?!');
INSERT INTO "vn_mokhtasar" VALUES(6,96,'6:96','Và Ngài (Allah) là Đấng làm vỡ ra ánh sáng của bình minh từ trong bóng tối của ban đêm, Ngài là Đấng làm cho ban đêm thành một sự nghỉ ngơi cho con người sau những hoạt động tìm kiếm kế sinh nhai trong ban ngày, Ngài là Đấng làm cho mặt trời, mặt trăng thành những thứ mà con người dùng để tính thời gian. Tất cả những cái vừa nêu đều là sự kì diệu từ công việc của Allah và Ngài là Đấng Toàn Năng không có ai (vật gì) có thể vượt qua quyền năng của Ngài; và Ngài là Đấng Toàn Tri, Ngài thông toàn hết tất cả mọi vật, mọi sự việc và Ngài biết rõ điều gì tốt cho đám bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,97,'6:97','Allah là Đấng tạo ra cho các ngươi - hỡi con cháu Adam (con người) - các vì sao trên bầu trời để các ngươi dùng chúng định hướng đi trong lúc các ngươi là những lữ khách đang trên các con đường giông giống nhau trong đất liền và biển cả. Quả thật, TA đã trình bày và giải thích các bằng chứng chứng minh về quyền năng của TA cho những người biết suy ngẫm.');
INSERT INTO "vn_mokhtasar" VALUES(6,98,'6:98','Allah là Đấng đã tạo các ngươi từ một cá thể duy nhất, đó là cha của các ngươi Adam. Quả thật, Ngài đã khởi tạo cha của các ngươi từ đất, rồi Ngài tạo các ngươi từ Y. Ngài tạo ra cho các ngươi chỗ để các ngươi trú ngụ, đó là dạ con của các bà mẹ của các ngươi, và Ngài tạo ra một kho chứa từ xương lưng của những người cha của các ngươi. Quả thật, TA đã trình bày các dấu hiệu cho những người hiểu lời lẽ của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(6,99,'6:99','Allah là Đấng đã ban nước mưa từ trên trời xuống để làm mọc ra đủ loại cây cối và thảo mộc, Ngài cho mọc ra các loại cây xanh tươi, cho mọc ra các loại cây có trái chồng lên nhau trên một nhánh, Ngài làm cho mọc ra các cây chà là thấp và gần để người đứng hay ngồi đều với tới, Ngài cho mọc ra các vườn nho, và cho mọc ra cây ô liu, lựu hơi giống nhau về lá nhưng trái lại khác nhau. Này hỡi con người, các ngươi hãy xem các trái non lúc mới nhú và lúc đã chín, trong sự việc đó là bằng chứng rõ ràng cho quyền năng của Allah, đây là bằng chứng dành cho những ai có đức tin nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,100,'6:100','Và những kẻ thờ đa thần đã tôn loài Jinn làm đấng thờ phượng cùng với Allah khi họ tin rằng Jinn mang lại lợi ích và tai hại trong khi Ngài tạo ra họ và những ai (vật gì) ngoài Ngài không tạo ra họ và Ngài mới đáng để họ thờ phượng. Họ gán cho Allah có những đứa con trai, giống như hành động của Do Thái với Uzair, và Thiên Chúa giáo với Ysa, và họ gán cho Allah có những đứa con gái như hành động của những kẻ thờ đa thần với các Thiên Thần. Nhưng Allah vĩ đại và siêu việt hơn những gì mà họ đã mô tả Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,101,'6:101','Và Ngài là Đấng tạo hóa các tầng trời và trái đất, trước đó không có gì giống chúng cả. Làm sao Ngài lại nói Ngài có con trong khi Ngài không có vợ, trong khi Ngài tạo ra mọi vật?! Và Ngài là Đấng hằng biết mọi thứ, không có gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,102,'6:102','Hỡi nhân loại, Đấng được mô tả đó là Allah, Thượng Đế của các ngươi, các ngươi không có Thượng Đế nào khác ngoài Ngài, không có Đấng Thờ Phượng nào khác ngoài Ngài, Ngài là Đấng tạo hóa tất cả mọi vật. Bởi thế, các ngươi hãy thờ phượng riêng một mình Ngài, chỉ có Ngài mới đáng được thờ phượng, và Ngài là Đấng quản lý tất cả mọi vật và mọi sự việc.');
INSERT INTO "vn_mokhtasar" VALUES(6,103,'6:103','Không có cái nhìn có thể bao quát được Ngài mà chỉ có Ngài bao quát tất cả mọi cái nhìn, tất cả đều nằm trong tầm kiểm soát của Ngài, Ngài Tinh Tế với các bề tôi ngoan đạo của Ngài và Ngài Thông Toàn về họ.');
INSERT INTO "vn_mokhtasar" VALUES(6,104,'6:104','Quả thật, đã đến với các ngươi - hỡi nhân loại - bằng chứng rõ ràng từ Thượng Đế của các ngươi. Bởi thế, ai hiểu được nó thì có lợi cho bản thân còn ai không hiểu được nó thì bất lợi cho bản thân. Và Ta (Muhammad) không phải là người giám sát theo canh chừng các ngươi, mà Ta chỉ là một vị Thiên Sứ từ Thượng Đế của Ta, và chính Ngài mới là Đấng Giám Sát các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(6,105,'6:105','Cũng như TA (Allah) đã thiết lập các bằng chứng và về quyền năng của TA, TA đã làm cho các lời phán phong phú chứa đựng lời cảnh báo, lời khuyên răn, và lời hứa ban thưởng. Nhưng những kẻ thờ đa thần nói: Đây không phải là lời mặc khải mà chính Ngươi (Muhammad) đã học nó từ người dân Kinh Sách trước Ngươi. Và đó là chân lý mà TA trình bày cho những người có đức tin thuộc cộng đồng của Muhammad, bởi họ là những người chấp nhận sự thật và đi theo nó.');
INSERT INTO "vn_mokhtasar" VALUES(6,106,'6:106','Hỡi Thiên Sứ Muhammad, Ngươi hãy đi theo điều chân lý được mặc khải cho Ngươi từ Thượng Đế của Ngươi. Bởi chỉ một mình Ngài là Đấng thờ phượng đích thực, Ngươi chớ bận tâm đến những kẻ vô đức tin và thần linh của chúng, Ngươi hãy bỏ mặc chúng cho Allah.');
INSERT INTO "vn_mokhtasar" VALUES(6,107,'6:107','Và nếu Allah muốn, họ đã không tổ hợp với Allah bất cứ ai (vật gì). Và Ngươi - hỡi Thiên Sứ Muhammad - TA (Allah) không cử Ngươi đến để giám sát canh chừng việc làm của họ, Ngươi cũng không phải chịu trách nhiệm cho họ bởi việc làm của họ, mà Ngươi chỉ là một vị Thiên Sứ, nhiệm vụ của Ngươi không có gì khác ngoài việc truyền đạt.');
INSERT INTO "vn_mokhtasar" VALUES(6,108,'6:108','Hỡi những người có đức tin, các ngươi chớ xúc phạm các thần linh bục tượng mà những kẻ thờ đa thần đang thờ phượng cùng với Allah để mà họ không xúc phạm Allah trở lại dưới sự thiếu hiểu biết của họ. Tương tự việc TA (Allah) trang hoàng cho những người này thấy việc lầm lạc của họ là tốt đẹp, thì mỗi cộng đồng TA cũng đã trang hoàng cho các việc làm của họ, tốt hay xấu, họ làm theo những gì mà TA trang hoàng cho họ rồi sau đó họ đều sẽ trở về trình diện TA vào Ngày Phục Sinh, TA sẽ cho họ biết về những điều họ đã làm trên cõi trần và TA sẽ thưởng phạt cho việc làm của họ một cách thỏa đáng.');
INSERT INTO "vn_mokhtasar" VALUES(6,109,'6:109','Những kẻ thờ đa thần thề thốt với Allah bằng một lời long trọng rằng nếu Muhammad mang đến cho họ một dấu lạ từ những dấu là mà họ yêu cầu thì chắc chắn họ sẽ tin. Ngươi - hỡi Thiên Sứ Muhammad - hãy nói với họ: Các dấu lạ không phải ở nơi Ta mà chúng ở nơi Allah, Ngài ban chúng xuống khi nào Ngài muốn. Và điều gì cho các ngươi cảm thấy - hỡi những người có đức tin - rằng các dấu lạ này khi chúng xảy đến đúng với yêu cầu của họ thì họ sẽ tin? Không chắc chắn họ vẫn ngoan cố với sự sai trái của họ bởi họ không muốn được hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(6,110,'6:110','Và TA (Allah) sẽ lật ngược trái tim và cái nhìn của chúng ra khỏi sự hướng dẫn giống như TA đã làm chúng không có đức tin nơi Qur''an ở lần đầu do sự ngoan cố của chúng. TA bỏ mặc chúng trong sự lầm lạc và để chúng bơ vơ trong sự thái quá của chúng mà không có sự hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(6,111,'6:111','Và nếu TA có đáp ứng cho họ những gì họ đề nghị là TA đã cho các Thiên Thần hạ thế cho thấy tận mắt, rồi cho người chết sống lại báo cho họ biết sự thật về sứ mạng của Ngươi, song TA tập họp tất cả yêu cầu của họ đặt trước mặt họ để đối chất với họ thì họ vẫn không tin vào những gì Ngươi mang đến ngoại trừ một số người nào mà Allah muốn hướng dẫn họ đi đến con đường ngay chính, nhưng đa số bọn chúng không biết về điều đó và cũng không dựa dẫm vào Allah để được Ngài hướng dẫn đến chính đạo.');
INSERT INTO "vn_mokhtasar" VALUES(6,112,'6:112','Và TA đã thử thách Ngươi bởi sự phản đối của nhóm người đa thần giống như TA đã thử thách các Nabi trước Ngươi. Thế là TA tạo ra mỗi người trong số họ những kẽ thù chống đối từ con người và loài Jinn, và họ thì thầm nói những lời xấu xa để lừa gạt thiên hạ. Nếu Allah muốn họ không làm những điều đó chắc chắn họ cũng không bao giờ làm được, nhưng Ngài đã muốn cho họ gặp phải những thử thách đó, bởi thế chớ bận tâm đến việc của bọn họ mặc cho họ sống trong vô niềm tin và vọng tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(6,113,'6:113','Và để họ thì thầm to nhỏ với nhau làm cho những con tim không tin tưởng vào ngày tận thế chấp nhận những lời nói đó cho bản thân họ, họ hài lòng tuân theo lời nói đó, và họ sẽ thu gom thêm tội lỗi và chống đối.');
INSERT INTO "vn_mokhtasar" VALUES(6,114,'6:114','Ngươi - hỡi Thiên Sứ - hãy nói với những kẻ đa thần đang tôn thờ Allah cùng với thần linh khác: Phải chăng là sự khôn ngoan khi chấp nhận một kẻ nào khác ngoài Allah để phán xét giữa Ta và các ngươi ư? Trong khi Allah đã mặc khải Thiên Kinh Qur''an xuống cho các ngươi giải thích cặn kẽ rõ ràng về tất cả mọi đều. Và TA đã ban cho dân chúng Do Thái Kinh Tawrah - Kinh Cựu Ước -, TA cũng ban xuống cho người dân Thiên Chúa Kinh Injil - Kinh Tân Ước-. Họ biết rõ về Thiên Kinh Qur''an được mặc khải cho ngươi - Muhammad - đó là sự thật qua những bằng chứng họ tìm thấy từ trong hai Kinh Sách của họ, cho nên Ngươi đừng là kẻ sinh nghi về những gì TA đã thiên khải cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(6,115,'6:115','Qur''an đã đạt đến độ chính xác tuyệt đối về lời phán và về thông điệp, không bao giờ sai lệch đi ý nghĩa trong Nó. Bởi Ngài luôn hằng nghe mọi lời nói của bầy tôi Ngài, Ngài am tường hết mọi việc, không có gì có thể giấu được Ngài và Ngài sẽ trừng trị những ai cố thay đổi những lời phán trong Nó.');
INSERT INTO "vn_mokhtasar" VALUES(6,116,'6:116','Nếu có định cho Ngươi - hỡi Thiên Sứ - làm theo đa số mọi người trong trái đất này thì họ sẽ dắt Ngươi đi lạc khỏi tôn giáo của Allah. Trong khi qui luật của Allah đã định là chân lý ở cùng số ít, nhưng hầu hết mọi người chỉ làm theo cảm nhận chứ không có bằng chứng xác thực bởi họ nghĩ rằng những thần linh họ đang thờ phượng đưa họ đến gần Allah hơn và họ là những kẻ nói dốc trong những đều nghĩ đó.');
INSERT INTO "vn_mokhtasar" VALUES(6,117,'6:117','Quả thật, Thượng Đế của Ngươi - hỡi Thiên Sứ - Ngài biết rõ trong thiên hạ ai là kẻ đi lạc khỏi chính đạo của Ngài, và Ngài Am Tường tìm đến sự hướng dẫn đó bởi không có gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,118,'6:118','Cho nên, các ngươi - hỡi nhân loại - hãy nhân danh Allah nếu các ngươi có ý định giết con vật để ăn thịt hoặc dâng tế nếu các ngươi là người tin tưởng chân lý qua những bằng chứng rõ ràng của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,119,'6:119','Điều gì mà cấm các ngươi - hỡi những người có đức tin - về việc các ngươi dùng thực phẩm đã được nhắc tên Allah lên nó, và chắc chắn TA đã trình bày rõ cho các ngươi điều gì cấm các ngươi thì mới bắt buộc các ngươi từ bỏ nó ngoại trừ trường hợp đặc biệt cần thiết thì các ngươi được dùng đến nó, bởi khi cấp bách là được sử dụng điều cấm. Quả thật, đa số những kẻ đa thần đã lôi kéo các tín đồ của họ lại để làm theo những gì họ cho là đúng, phù hợp với các quan điểm ngu muội của họ, cụ thể là họ cho phép những gì Allah cấm từ xác động vật và những thứ khác, họ từ chối dùng thực phẩm mà Allah cho phép họ được phép dùng như hồ nước, dây, bảo vệ và nhiều thứ khác. Quả thật, Thượng Đế của Ngươi - hỡi Thiên Sứ - Ngài biết rõ ai là kẻ vượt giới hạn của Allah và Ngài sẽ cho chúng biết hậu quả về việc làm của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(6,120,'6:120','Và các ngươi hãy từ bỏ đi - hỡi nhân loại - mọi hành động tội lỗi dù công khai hay thầm kín. Quả thật, những ai phạm tội lỗi cho dù trong thầm kín hay công khai thì TA sẽ ban cho họ phần thưởng xứng đáng với cái tội lỗi của họ đã gây ra.');
INSERT INTO "vn_mokhtasar" VALUES(6,121,'6:121','Và các ngươi - hỡi tín đồ Muslim - chớ có ăn thịt động vật mà lúc giết không nhắc đến tên của Allah lên nó, không phân biệt là chúng được nhắc tên ai đó ngoài Ngài hay không, và việc ăn thực phẩm đó là đã bất tuân Allah. Và quả thật lũ Shaytan đã thì thào vào tai đồng bọn chúng đấu tranh với các ngươi về việc ăn thịt động vật đã chết. Nếu các ngươi - hỡi tín đồ Muslim - nghe lời xúi dục của chúng là được phép ăn thịt động vật chết thì các ngươi sẽ không khác gì những kẽ thờ đa thần.');
INSERT INTO "vn_mokhtasar" VALUES(6,122,'6:122','Phải chăng có sự tương xứng với người trước khi được Allah hướng dẫn, y tựa như xác chết - bởi y sống trong vô đức tin, tội lỗi và ngu muội - rồi y được Allah cứu sống y bằng niềm tin Iman, kiến thức và phục tùng so với kẻ chìm sâu trong tâm tối của sự vô đức tin, ngu muội và tội lỗi khiến y không biết cách nào thoát khỏi mặc dù hắn đã cố gắng thoát khỏi, cố tìm đường để ra?! Giống như việc khiến những kẻ đa thần mê muội trong sự vô đức tin, ăn xác chết và họ thảo luận với nhau bằng những đều xàm bậy để đám người vô đức tin chất chồng thêm tội lỗi để chúng bị trừng phạt thật đau đớn ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(6,123,'6:123','Và giống như những gì đã xảy ra với những kẻ đa thần đầy quyền uy tại Makkah tìm cách đẩy lùi con đường chính đạo của Allah. TA sẽ sắp đặt cho từng thị trấn những tên thủ lĩnh tài giỏi lên kế hoạch để dụ dỗ chúng đến với đường lối của Shaytan mà tìm cách chống đối với Thiên Sứ và hậu duệ của Người nhưng thật ra bọn chúng lại bị gậy ông đập lưng ông, họ không nhận ra điều đó bởi họ vốn ngu dốt chỉ biết thõa mãn bản thân.');
INSERT INTO "vn_mokhtasar" VALUES(6,124,'6:124','Và khi có một dấu hiệu mà Allah thiên khải cho Nabi của Ngài đến trình bày với những kẻ đầu xỏ của nhóm ngoại đạo thì chúng bảo: Chúng tôi không bao giờ tin tưởng đều đó cho đến khi chúng tôi nhận được một đều nào đó giống như Allah đã ban cho các vị Thiên Sứ của Ngài. Allah đáp trả chúng rằng Ngài thừa biết ai là người đáng đảm nhận thông điệp và thực thi được sứ mạng truyền đạt lại. Rồi đây những kẻ bạo hành ngang ngược các ngươi sẽ sớm bị khinh miệt và sĩ nhục chỉ vì họ lánh xa sự thật và chịu trừng phạt đầy khắc nghiệt về những điều mà các ngươi đã từng âm mưu toan tính.');
INSERT INTO "vn_mokhtasar" VALUES(6,125,'6:125','Bởi thế, khi Allah muốn hướng dẫn ai chấp thuận chân lý là Ngài mở cõi lòng y sẵn sàng đón nhận Islam. Và ngược lại, kẻ nào bị Allah muốn hạ nhục và không hướng dẫn y đến với chân lý thì TA sẽ siết chật lòng y hẹp lại để cho y không chấp nhận chân lý tựa như việc y đang bị đẩy lên cao rơi vào hiện trạng khó thở. Giống như Allah siết chặt cho cuộc sống y thêm đau khổ và giáng hành phạt lên những kẻ vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(6,126,'6:126','Và đây là một tôn giáo mà TA đã thiên khải nó cho Ngươi - hỡi Thiên Sứ - là con đường của Allah thẳng tắp không quanh co. Quả thật, TA đã trình bày rõ các lời phán cho những ai có khối óc biết nhận thức về Allah.');
INSERT INTO "vn_mokhtasar" VALUES(6,127,'6:127','Họ được hưởng một tòa lâu đài đầy sự bằng an bảo vệ họ tránh khỏi mọi đều xấu xa, đó chính là Thiên Đàng. Và Ngài luôn phù hộ và ủng hộ họ bằng những phần thưởng do việc làm ngoan đạo mà họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(6,128,'6:128','Hãy nhớ lại đi - hỡi Thiên Sứ - vào ngày mà Allah phục sinh loài người và Jinn, rồi Ngài phán: "Hỡi tập đoàn Jinn, quả thật các ngươi xúi giục rất nhiều thiên hạ làm điều sai quấy và cản trở họ đến con đường của Allah." Và đám người ùa theo lời gọi của Jinn đáp lại Thượng Đế: "Lạy Thượng Đế của bầy tôi, mỗi người trong bầy tôi đã thỏa mãn với nhau, rằng loài Jinn hoan hỉ khi loài người nghe lời chúng và loài người được thỏa mãn dục vọng của mình, cho đến khi bầy tôi hưởng hết tuổi thọ mà Ngài đã định cho bầy tôi, thì đây là Ngày Tận Thế." Allah phán: "Lửa là nơi cư ngụ của các ngươi trong đó đời đời, ngoại trừ Allah muốn đều gì đó thay đổi với các ngươi về thời gian giữa việc phục sinh từ ngôi mộ rồi sau đó các ngươi tiếp tục bị tống cổ vào Hỏa Ngục. Đây là khoảng thời gian mà Allah tạm ngưng việc trừng phạt họ bằng lửa. Quả thật, Thượng Đế của ngươi - hỡi Thiên Sứ - luôn sáng suốt trong việc ước tính và cai quản, Ngài luôn am tường về bầy tôi của Ngài, và ai là kẻ đáng bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(6,129,'6:129','Tương tự như những tên cầm đầu của bọn chúng là loài Jinn, bọn chúng sẽ sai khiến một số loài người này để làm điều sai quấy với số người kia, tất cả mọi điều bất công áp đặt lên với người bất công khác và hắn thúc dục các ngươi làm mọi đều ác, hắn thẳng thừng từ chối mọi điều tốt đẹp, chắc rằng hậu quả của bọn chúng sẽ nhận được là vô vàng tội lỗi mà chúng đã tạo nên.');
INSERT INTO "vn_mokhtasar" VALUES(6,130,'6:130','Và TA nói với chúng trong Ngày Phán Xử: "Hỡi loài người và loài Jinn, há không có các Thiên Sứ nào xuất thân từ chính cộng đồng của các ngươi - loài người - Họ truyền đạt lại mọi lời phán từ Allah để các nghe và khuyến cáo về cuộc trình diện trong Ngày Phán Xét này hay sao ?" Chúng thưa: "Hôm nay bầy tôi xin chứng nhận rằng giới Thiên Sứ của Ngài đã truyền đạt tất cả cho bầy tôi, và bầy tôi đã chấp nhận cuộc trình diện này nhưng bầy tôi đã phủ nhận các Thiên Sứ của Ngài, phủ nhận luôn cuộc trình diện Ngày hôm nay. Thế là họ đã bị cuộc sống trần tục lừa dối chúng bằng những đều lọng lẫy xa hoa và đầy phù phiếm, và chúng xác nhận họ đã sống trên trần gian bằng sự vô đức tin, phủ nhận Allah và các Thiên Sứ của Ngài, tuy nhiên sự thú tội đó không giúp ích gì cho họ và niềm tin trở nên vô ích khi đã hết thời hạn.');
INSERT INTO "vn_mokhtasar" VALUES(6,131,'6:131','Đó là nguyên nhân Thiên Sứ được gởi đến cho loài người và loài Jinn để không ai trong họ còn viện cớ không có Thiên Sứ đến báo trước và không nhận được thông điệp. Thế nên, không một cộng đồng nào trước đây bị trừng phạt ngoại trừ đã gởi đến họ vị Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(6,132,'6:132','Và tất cả mọi việc làm của họ sẽ được phân ra từng cấp bật tương xứng, không bao giờ có sự làm nhiều điều tội lỗi lại ngang bằng với việc làm tội lỗi ít hơn, cũng không có việc người đề xướng hoặc người làm theo lại như nhau. Và Thượng Đế của Ngươi không bao làm ngơ với việc làm của họ, đúng hơn là ngày luôn quan sát họ, cũng không có một điều gì có thể giấu giếm được Ngài, Ngài sẽ thưởng phạt tùy vào việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(6,133,'6:133','Và Thượng Đế của Ngươi - hỡi Thiên Sứ - Ngài là Đấng Giàu Có, Ngài không cần bất cứ thứ gì từ họ, Ngài không cần tới việc thờ phượng của họ, cho dù chúng có toan tính ra sao cũng không làm hại được Ngài, tuy Ngài rất giàu bất cần họ nhưng Ngài rất nhân từ với họ. Nếu muốn là Ngài đã hủy diệt các ngươi - hỡi đám bầy tôi đầy tội lỗi - Ngài sẽ trừng phạt các ngươi, rồi sau khi trừng phạt các ngươi Ngài sẽ thay thế các ngươi bởi những người có đức tin và thần phục Ngài. Cũng giống như Ngài tạo các ngươi từ con cháu của người khác từ những tổ tiên của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(6,134,'6:134','Quả thật, về những gì đã cảnh cáo các ngươi - hỡi nhóm người vô đức tin - như phục sinh, thanh toán và sự trừng phạt chắc chắn sẽ xảy ra không ai có thể tránh khỏi, các ngươi sẽ không chạy thoát khỏi Thượng Đế đâu, Ngài túm lấy chùm tóc trán của các ngươi và Ngài giáng hành phạt lên các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(6,135,'6:135','Ngươi hãy bảo họ - hỡi Thiên Sứ -: Hỡi dân ta, các ngươi hãy trụ lấy trên con đường các ngươi đang đi, đó không phải là con đường của nhóm vô đức tin và lầm lạc đâu. Quả thật, Ta đã cảnh báo cho các ngươi với bằng chứng rõ ràng, Ta không phải là kẻ dẫn các ngươi đến với sự vô đức tin và lầm lạc. Ta quyết trụ lấy chân lý mà Ta đã đi, rồi các ngươi sẽ biết ai mới thật sự chiến thắng ở trần gian, ai là người thừa kế trái đất này và cả Đời Sau. Quả thật, những kẻ đa thần các ngươi sẽ không bao giờ chiến thắng được trên trần gian này lẫn Đời Sau được đâu. Đúng hơn, kết quả dành cho họ là sự thất bại ê chề và nếu được hưởng thụ chỉ hưởng thụ được cuộc sống trần gian này mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(6,136,'6:136','Và những kẻ đa thần họ tự đưa ra quy định cho Allah rằng họ sẽ cúng tế cho Allah một phần trồng trọt và một số lượng gia súc, họ cuồn ngôn cho đây là phần của Allah, còn một phần cúng tế khác họ bảo đây là của các thần tượng họ sùng bái và các thần linh của họ. Thế nên, các phần mà họ dành cho thần linh của họ không đến được những mục tiêu mà Allah đã qui định, chẳng hạn như người nghèo và người thiếu thốn, còn những phần dành cho Allah thì đến được các bụt tượng mà họ thờ. Không gì tồi tệ hơn về quyết định và sự phân chia của họ.');
INSERT INTO "vn_mokhtasar" VALUES(6,137,'6:137','Và tương tự, nhóm người đa thần đã bị lũ Shaytan mê hoặc mà sát hại con cái mình do dợ nghèo khổ, điều tồi tệ này ngày càng lan rộng hơn, do bởi lũ Shaytan muốn họ phải vị phạm lệnh cấm của Allah bằng cách lạm sát người vô tội ngoại trừ người đó phạm tội, ngoài ra chúng còn xáo trộn thị phi khiến mọi người không thể phân biệt được đâu là được, đâu là cấm. Tuy nhiên, nếu như Allah không muốn điều đó xảy ra thì họ sẽ không bao giờ làm được, nhưng Ngài muốn điều đó xảy ra bởi giá trị lớn lao hơn. Thôi hãy bỏ mặt họ - hỡi Thiên Sứ - nhóm người đa thần và lời vu khống của họ về Allah, quả thật, điều đó không gây hại gì cho Ngài đâu.');
INSERT INTO "vn_mokhtasar" VALUES(6,138,'6:138','Và những kẻ đa thần nói: Thú nuôi và hoa mầu này không được dùng, ngoại trừ những ai được sự đồng ý của họ; họ còn ngông cuồng cho rằng để phục vụ cho thần linh họ và cho điều khác, họ còn cấm cưỡi trên lưng những con thú nuôi, không chuyên chở và chúng cần sự chăm sóc, nâng niu và được bảo vệ. Và những con vật nuôi bị giết chết cũng không cho phép nhắc đến tên Allah mà phải nhắc tên các thần linh của họ lên con vật bị giết chết; họ làm mọi chuyện rồi đỗ thừa cho Allah và họ đỗ thừa mọi điều đó là đến từ giáo lý của Ngài, rồi Allah sẽ trừng phạt họ bởi vì những lời cuồng ngôn của họ.');
INSERT INTO "vn_mokhtasar" VALUES(6,139,'6:139','Và họ nói: Với những bào thai được sinh ra từ những súc vật đó, nếu sinh vẫn còn sống thì nó dành riêng cho nam giới trong chúng tôi, cấm phụ nữ hưởng nó, còn nếu sinh ra bị chết thì cả đàn ông lẫn phụ nữ điều được phép dùng. Rồi đây, Allah Tối Cao sẽ trừng phạt những gì họ nói mà không dựa theo giáo lý của Ngài. Quả thật, Ngài rất sáng suốt về giáo lý và sự quản lý của Ngài, Ngài biết rõ mọi việc.');
INSERT INTO "vn_mokhtasar" VALUES(6,140,'6:140','Chắc chắn rằng họ là kẻ thua thiệt khi họ đã tước đoạt đi sinh mạng của các đứa trẻ sơ sinh trong khi chúng chưa có ý trí và nhận thức được, họ tự cấm bản thân dùng những thiên lộc được Allah ban cho từ động vật rồi họ đặt điều nói dối rồi đổ thừa cho Allah. Quả thật, họ đã đi quá xa con đường chính đạo và không được ai hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(6,141,'6:141','Và Allah Hiển Vinh là Đấng đã tạo hóa ra hàng loạt các vườn cây trải đều trên mặt đất gồm cả những cây thân bò, Ngài đã tạo ra chà là và những mảnh đất trồng trọt đủ các loại hình dạng và mùi vị, Ngài còn tạo ra trái ô liu và trái lựu có những chiếc lá tương tự nhau nhưng cả hai lại có khẩu vị khác nhau. Này - hỡi con người - hãy ăn đi những trái đã chín mộng và hãy thực hiện Zakat khi đã thu hoạch mùa vụ xong và chớ hoang phí vượt quá giớ hạn từ giáo lý trong việc ăn uống và chi tiêu. Alllah không bao giờ yêu thích sự hoang phí vượt quá giới hạng của hai điều đó, Ngài rất ghét sự phung phí. Quả thật, Ngài tạo ra tất cả thực phẩm để cho phép đám nô lệ của Ngài dùng nến đám người đa thần không có thẩm quyền cấm nó.');
INSERT INTO "vn_mokhtasar" VALUES(6,142,'6:142','Ngài đã tạo ra cho các ngươi những con vật mạnh khỏe, để cho các ngươi dùng để chuyên chở giống như con lạc đà to lớn mạnh khỏe chứ không phải những con yếu ớt và những con nhỏ bé giống như những con cừu, dê. Này - hỡi con người - hãy ăn đi thì những gì mà Allah đã ban cho các ngươi được phép ăn, các ngươi chớ làm theo lời xàm bậy của lũ Shaytan bởi vì chúng đã cấm những điều Allah cho phép và cho phép những điều Allah đã cấm giống như những việc làm của những kẻ đa thần. Quả thật, lũ Shaytan là kẻ thù công khai của các ngươi đó - hỡi con người - chúng muốn các ngươi làm những điều tội lỗi với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(6,143,'6:143','Ngài đã tạo cho các ngươi tám loài vật, gồm loài cừu hai giống đực cái và loài dê cũng hai giống. Ngươi hãy hỏi nhóm người đa thần - hỡi Thiên Sứ -: Có phải Allah đã cấm ăn thịt hai loài đực vì lý do chúng là giống đực ? Nếu họ đáp: Đúng vậy thì hỏi họ tiếp: Tại sao lại cấm hai giống cái, phải chăng vì lý do chúng là giống cái? Nếu họ đáp: Đúng vậy thì hỏi họ tiếp: Tại sao cấm hai loài đực, phải chăng vì lý do chúng lẫn lộn giống đực và cái trong một bào thai? Nếu họ nói: Đúng vậy thì hãy hỏi họ: Tại sao lại phân biệt giữa cái bào thai đó lúc thì cấm giống đực, lúc thì cấm giống cái. Nào hãy cho Ta biết - hỡi những kẻ đa thần - rằng dựa vào đâu mà các ngươi cho đó là đúng đắn nếu các ngươi nói thật trong việc các ngươi cuồng ngôn nói là Allah đã cấm như thế.');
INSERT INTO "vn_mokhtasar" VALUES(6,144,'6:144','Những loài súc vật còn lại trong tám loài gồm một cặp lạc đà và cặp bò. Hãy hỏi nhóm đa thần - hỡi Thiên Sứ -: Há phải chăng cho rằng Allah cấm dùng thịt vì chúng là giống đực hoặc là giống cái hoặc là con đực, con cái trong cùng bào thai? Hay là các ngươi - hỡi nhóm đa thần đang có mặt khi các ngươi khẳng định - Allah đã hạ lệnh riêng cho các ngươi cấm điều này phải không ? Cho nên không có một điều bất công nào to tát rồi cũng không có điều cấm nào lớn lao hơn khi các ngươi dốc láo rồi đỗ thừa cho Allah, các ngươi tự cấm rồi đổ thừa Allah đã cấm để kéo thiên hạ khỏi con đường chính đạo trong khi các ngươi không có tí kiến thức gì về nó. Quả thật, Allah không bao giờ hướng dẫn những kẻ bất công trong khi họ nói dốc láo rồi đổ thừa cho Allah.');
INSERT INTO "vn_mokhtasar" VALUES(6,145,'6:145','Ngươi hãy nói đi - hỡi Thiên Sứ - rằng Ta không tìm thấy trong những gì mà Allah đã mặc khải cho Ta cấm dùng ngoại trừ những con vật chết tự nhiên không được cắt cổ hoặc máu chảy thành dòng hoặc thịt heo, bởi nó ô uế bị cấm hoặc những con vật nào cắt cổ không nhắc tên Allah giống như cúng bái bụt tượng. Riêng trường hợp ai bất đắt dĩ phải ăn thịt những con vật bị cấm ăn ở trên vì quá đói chứ không phải để thưởng thức và cũng không ăn quá nhiều thì y không bị bắt tội. Quả thật, Thượng Đế của ngươi - hỡi Thiên Sứ - luôn tha thứ cho những ai bắt đắc dĩ phải ăn, Ngài tội nghiệp họ.');
INSERT INTO "vn_mokhtasar" VALUES(6,146,'6:146','Và TA hạ lệnh cấm người Do Thái dùng thịt của con vật mà móng của chúng dính vào nhau như con lạc đà và đà điểu, và TA cấm họ dùng mỡ bò, mỡ dê cừu ngoại trừ phần mỡ nào dính ở lưng, bao quanh bao tử, bao quanh xương sườn. TA phạt họ vì tội bất công mà họ đã gây nên và chắc chắn, thông tin của TA là chính xác.');
INSERT INTO "vn_mokhtasar" VALUES(6,147,'6:147','Quả thật, nếu họ cho rằng Ngươi nói dốc - hỡi Thiên Sứ - và họ không tin những gì do Ngươi mang đến từ Thượng Đế của Ngươi thì hãy cho họ biết một điều: Thượng Đế của các ngươi vô cùng khoan dung, trong những hình thức Ngài đã khoan dung là đã trì hoãn không vội vã trừng phạt các ngươi, song hãy cảnh báo họ rằng sự trừng phạt của Ngài khi giáng xuống thì những kẻ tội lỗi và sai quấy không sao tránh khỏi.');
INSERT INTO "vn_mokhtasar" VALUES(6,148,'6:148','Những người đa thần họ sẽ biện minh rằng là Allah đã sắp đặt và an bài số phận họ là tổ hợp thần linh khác cùng Ngài: Nếu Allah muốn là chúng tôi, lẫn tổ tiên của chúng tôi đã không tài nào có thể tổ hợp thần linh khác cùng Ngài được, nếu Allah muốn là chúng tôi không thể cấm những thứ mà chúng tôi đã cấm. Và cũng với lời biện minh này mà thế hệ trước họ đã phủ nhận sứ mạng của những Thiên Sứ được gởi đến họ, họ nói: Nếu Allah an bài cho chúng tôi không phủ nhận sứ mạng của Thiên Sứ là chúng tôi đã không thể phủ nhận được họ và họ còn tiếp tục sống trong sự phủ nhận đó mãi đến khi TA giáng hình phạt lên họ. Hãy bảo nhóm đa thần đó - hỡi Thiên Sứ -: Các ngươi có bằng chứng nào là cơ sở rằng Allah hài lòng việc các ngươi tổ hợp thần linh khác cùng Ngài và việc các ngươi cấm điều Allah cho phép và cho phép làm điều Allah cấm? Đó là sự lập luận của các ngươi chứ thật ra đó không phải là bằng chứng mà Allah hài lòng với các ngươi, các ngươi chỉ làm theo cảm tính và bao giờ cảm tính là sự thật cho được, các ngươi vốn là đám người dốc láo.');
INSERT INTO "vn_mokhtasar" VALUES(6,149,'6:149','Ngươi - hỡi Thiên Sứ- hãy nói với người đa thần: Quả thật, các ngươi không có những lập luận nào khác ngoại trừ những lập luận mơ hồ này thì quả thật Allah có lập luận quả quyết sẽ dập tắt đi mọi lập luận mà các ngươi đưa ra và dập tắt luôn những lệch lạc mà các ngươi đã trụ lấy. Nếu Allah muốn là Ngài đã hướng dẫn tất cả các ngươi đến chính đạo rồi - hỡi nhóm người đa thần - chắc chắn Ngài làm được điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(6,150,'6:150','Ngươi - hỡi Thiên Sứ - hãy nói với nhóm người đa thần đã cấm điều mà Allah cho phép và khẳng định Allah đã cấm: Các ngươi hãy đem ra những nhân chứng đã khẳng định Allah đã cấm những điều mà các ngươi đã cấm. Nếu họ chỉ khẳng định không dựa trên cơ sở rõ ràng thì đừng tin vào họ - hỡi Thiên Sứ - bởi lời nói của họ là giả dối, Ngươi đừng có chìu theo sở thích của họ. Quả thật, họ đã bác bỏ các lời mặc khải khi họ cấm những gì Allah cho phép nên đừng ùa theo đám người không tí niềm tin vào Đời Sau, và làm sao các ngươi nghe theo đám người đã cố tình thờ phượng thần linh khác cùng Thượng Đế cho được.');
INSERT INTO "vn_mokhtasar" VALUES(6,151,'6:151','Ngươi - hỡi Thiên Sứ - hãy nói với thiên hạ: Các ngươi hãy đến đây, để Ta đọc cho các ngươi nghe những gì Allah đã cấm, Ngài cấm các ngươi phạm Shirk - thờ phượng ai đó cùng với Ngài -; cấm bất hiếu với cha mẹ, đáng lẽ ra các ngươi phải cung kính đối xử tốt với họ; cấm giết con cái vì lý do nghèo khổ giống như đám người thời tiền Islam đã làm trong khi chính TA mới là Đấng ban phát bổng lộc cho các ngươi và chúng; cấm các ngươi tìm mọi cách đến gần những điều khả ố như tình dục ngoài hôn nhân bằng cách công khai hay thầm kín; cấm các ngươi giết lạm sát người vô tội ngoại trừ vì lý do chính đáng như phạm tội ngoại tình sau khi đã kết hôn sau, phản đạo. Đó là điều mà Allah chỉ thị cho các ngươi để cho các ngươi thông hiểu về giáo lý của Ngài và làm theo mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,152,'6:152','Và Ngài cấm các ngươi chạm vào tài sản của trẻ mồ côi - đó là đứa trẻ mất cha trước tuổi trưởng thành - ngoại trừ cải thiện tốt hơn, làm tăng thêm lợi nhuận từ tài sản cho nó đến khi đứa trẻ trưởng thành và khôn ngoan; cấm các ngươi gian lận trong cân và đo lường, đúng hơn là Ngài bắt buộc các ngươi phải trung thực trong mua bán, Ngài chỉ bắt mỗi linh hồn gánh vác tùy theo sức lực của nó, và khi các ngươi bất lực kiểm soát việc thừa và thiếu trong cân đo thì các ngươi vô tội; cấm các ngươi ăn nói không đúng sự thật về nguồn thông tin hoặc thiên vị khi làm chứng; cấm các ngươi vi phạm những giao ước mà Ngài đã giao ước với các ngươi hoặc các ngươi giao ước với Ngài, đúng hơn là Allah bắt buộc các ngươi hoàn thành nó. Đó là những mệnh lệnh mà Ngài muốn nhấn mạnh với các ngươi, mong rằng các ngươi biết suy nghĩ về kết quả cho hành động của mình.');
INSERT INTO "vn_mokhtasar" VALUES(6,153,'6:153','Và Ngài cấm các ngươi đi vào con đường lầm lạc và sai quấy, đúng hơn là Ngài bắt buộc các ngươi đi theo con đường ngay chính không uẩn khúc của Ngài. Còn đi con đường lầm lạc nó sẽ làm cho các ngươi lệch khỏi con đường chính đạo. Việc bám theo con đường ngay chính của Allah là mệnh lệnh của Ngài mong rằng các ngươi biết kính sợ mà làm theo những gì Ngài đã ra lệnh cũng như tránh xa những gì Ngài đã cấm');
INSERT INTO "vn_mokhtasar" VALUES(6,154,'6:154','Sau những nguồn tin đã thông báo, TA đã ban cho Musa Kinh Tawrah (Cựu Ước) nhằm hoàn chỉnh ân huệ làm phần thưởng cho việc ngoan đạo của Người, trong Nó giải thích cặn kẻ mọi điều cần thiết trong tôn giáo và là bằng chứng khẳng định chân lý và nhân từ hi vọng các ngươi tin tưởng vào cuộc trình diện Thượng Đế các ngươi ở Đời Sau, vì vậy các ngươi phải chuẩn bị cho việc thực hiện hành đạo tốt đẹp');
INSERT INTO "vn_mokhtasar" VALUES(6,155,'6:155','Và đây là Thiên Kinh Qur''an mà Ta đã mặc khải xuống có rất nhiều hồng ân, bởi trong Nó hàm chứa rất nhiều điều tốt đẹp cho tôn giáo và trần tục, các ngươi hãy tuân thủ theo Nó và tránh nghịch lại Nó, có thế các ngươi mới có hi vọng được Ngài thương xót.');
INSERT INTO "vn_mokhtasar" VALUES(6,156,'6:156','Để các ngươi đừng nói - hỡi nhóm đa thần Ả-rập -: Allah chỉ ban xuống Kinh Tawrah - Cựu Ước - và Kinh Injil - Tân Ước - cho người Do Thái và người Thiên Chúa trước chúng tôi mà thôi, chớ không ban cho chúng tôi một kinh sách nào, và thật sự chúng tôi không biết đọc kinh sách của họ, bởi đó là ngôn ngữ của họ chứ không phải ngôn ngữ của chúng tôi.');
INSERT INTO "vn_mokhtasar" VALUES(6,157,'6:157','Và để các ngươi đừng nói: Nếu Allah ban cho chúng tôi một kinh sách như Ngài đã ban cho Do Thái và Thiên Chúa thì chắc chắn chúng tôi sẽ ngay chính hơn họ. Nhưng Allah đã ban Kinh Sách cho vị Nabi của các ngươi Muhammad rồi mà, bằng chính ngôn ngữ của các ngươi, đó là một minh chứng rõ ràng và một chỉ đạo hướng đến chân lý và là lòng nhân từ dành cho cộng đồng. Cho nên sẽ không chấp nhận bất cứ lý do mơ hồ nào mà các ngươi đã biện minh nữa, quả thật không có điều bất công nào hơn sự bất công bác bỏ mọi giáo lý của Allah và ngoãnh mặt với nó, TA sẽ tống cổ những kẻ đã ngoãnh mặt với các lời mặc khải của TA vào Hỏa Ngục, một phần thưởng dành cho đám người đó.');
INSERT INTO "vn_mokhtasar" VALUES(6,158,'6:158','Nhóm phủ nhận đức tin không chờ đợi điều gì ngoài việc Thần Chết, cùng với nhóm Thiên Thần đến rút hồn họ trên trần gian này hoặc Thượng Đế của Ngươi sẽ đến gặp họ vào ngày Phán Xét ở Đời Sau - hỡi Thiên Sứ - để phân xử bọn họ, hoặc một số dấu hiệu từ Thượng Đế của Ngươi đến để báo trước thời khắc tận thế - như việc mặt trời mọc lên từ hướng tây - lúc đó niềm tin Iman của người Kafir không còn lợi ích gì và việc làm ngoan đạo của người có đức tin cũng trở nên vô giá trị lúc đó. Ngươi hãy bảo - hỡi Thiên Sứ - với nhóm người đa thần phủ nhận đức tin: Các ngươi hãy chờ đợi một trong những điều đó xảy ra đi và Ta cũng đang chờ đợi điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(6,159,'6:159','Quả thật, những ai đã phân chia phe phái giống như Do Thái và Thiên Chúa, chỉ tuân theo một số giáo lý và bác bỏ số giáo lý còn lại thì Ngươi - hỡi Thiên Sứ - không thuộc nhóm họ, Ngươi vô can với mọi điều lầm lạc họ đã làm, chẳng qua Ngươi chỉ là người cảnh báo trước cho chọ, hãy bỏ mặc việc làm của họ cho Allah, nhất định vào Ngày Sau Allah sẽ cho họ biết cụ thể về việc làm của họ ở trần gian và ban thưởng xứng đáng cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(6,160,'6:160','Đối với người có đức tin nào mang đến việc làm tốt đẹp sẽ được Allah nhân lên đến mười lần, còn ai mang đến điều tội lỗi thì chỉ bị trừng phạt tương ứng với tội lỗi đó, không chịu nặng hơn. Trong Ngày Tận Thế họ không hề bị bất công như được hưởng ân phước ít hơn việc đã làm hoặc bị trừng phạt nặng hơn tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(6,161,'6:161','Ngươi - hỡi Thiên Sứ - hãy nói với nhóm người đa thần dối trá: Quả thật,Thượng Đế của Ta đã chỉ dẫn Ta đến con đường ngay chính của một tôn giáo hoàn chỉnh toàn mỹ trong sự tốt đẹp trên trần gian lẫn Ngày Sau. Đó là tính ngưỡng của Ibrahim một con đường chính thống hướng đến chân lý không hề có sự tổ hợp trong nó.');
INSERT INTO "vn_mokhtasar" VALUES(6,162,'6:162','Ngươi - hỡi Thiên Sứ - hãy nói: Chắc rằng lễ nguyện Salah của Ta, việc giết tế của Ta là vì Allah, tên Ngài được nhắc lên nó khi cắt cổ, chứ không vì ai khác ngoài Ngài, kể cả sự sống và cái chết của Ta đều do Allah, Chủ Nhân duy nhất của muôn loài, ngoài Ngài không ai có thẩm quyền đó.');
INSERT INTO "vn_mokhtasar" VALUES(6,163,'6:163','Ngài là Đấng Hiển Vinh không có đối tác ngang hàng, không có Thượng Đế đích thực nào ngoài Ngài, đây chính là Tawhid thuyết độc tôn Allah duy nhất mà Ta được Allah ra lệnh phục tùng, và Ta nguyện là một tín đồ Muslim đầu tiên thuần phục Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(6,164,'6:164','Ngươi - hỡi Thiên Sứ - hãy nói với nhóm người đa thần: Há ngoài Allah Ta còn phải tìm một thượng đế khác ư? Trong khi Ngài là Đấng Hiển Vinh, Tối Cao Chủ Nhân của tất cả, Ngài còn là Thượng Đế địch thực đáng được thờ phượng. Không ai gánh tội thay ai cả và rồi tất cả trở về trình diện Ngài ở Đời Sau sẽ được Ngài cho biết cụ thể mọi việc đã từng làm ở trần gian về hành động thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(6,165,'6:165','Và Allah là Đấng bổ nhiệm các ngươi việc thừa kế trên trái đất này từ thế hệ trước để duy trì việc dựng lên kiến trúc của nó và Ngài đã ân sủng một số người này hơn một số người kia trong sự tạo hóa, trong bổng lộc và những thứ khác, Ngài còn nâng cao địa vị người này trội hơn người kia để thử thách các ngươi bằng chính thiên ân đã ban ban cho các ngươi. Quả thật, Thượng Đế của Ngươi - hỡi Thiên Sứ - rất nhanh chóng trong sự trừng phạt, tất cả mọi việc diễn ra rất gần và Ngài cũng luôn tha thứ cho ai biết hối cải trong đám nô lệ của Ngài, nhân từ với họ.');
INSERT INTO "vn_mokhtasar" VALUES(7,1,'7:1','Đã được giải thích ở câu đầu tiên của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(7,2,'7:2','Thiên Kinh Qur''an là Kinh Sách được Allah ban xuống cho Ngươi - hỡi Thiên Sứ - cho nên Ngươi chớ khổ tâm và nghi ngờ, TA ban Nó xuống để Ngươi cảnh cáo thiên hạ, để làm bằng chứng và để nhắc nhở tín đồ có đức tin bởi chỉ với họ lời nhắc nhở đó mới đó mới có giá trị.');
INSERT INTO "vn_mokhtasar" VALUES(7,3,'7:3','Các ngươi hãy tuân theo - hỡi nhân loại - Kinh Sách đã được Thượng Đế mặc khải cho các ngươi, cùng như đường lối Sunnah của Thiên Sứ của các ngươi, các ngươi chớ làm theo cảm hứng của nhóm người đầu đảng đã bị Shaytan mê hoặc những tên giáo sĩ bại hoại, họ đã lãnh đạo các ngươi từ bỏ mọi thứ được Allah thiên khải nhằm thỏa mãn ham muốn của họ, nhưng các ngươi rất ít người quan tâm, nên khiến các ngươi không nhận biết được vết tích của chân lý, và phải làm theo những gì Thiên Sứ của các ngươi đã nói và từ bỏ tất cả những gì không phải Người mang đến');
INSERT INTO "vn_mokhtasar" VALUES(7,4,'7:4','Sở dĩ có rất nhiều ngôi làng đã bị TA tiêu diệt vì họ miệt mài trong tội lỗi, vô đức tin và lầm lạc, TA đã giáng xuống họ sự trừng phạt khủng khiếp trong lúc họ không hề biết trước vào ban đêm hoặc ban ngày, họ bất lực bảo vệ khỏi hành phạt cũng như thần linh mà họ đã thờ phượng không giúp gì được họ');
INSERT INTO "vn_mokhtasar" VALUES(7,5,'7:5','Ngay sau khi hành phạt giáng xuống họ thì họ mới công nhận mình là những kẻ làm đều sai quấy đã phủ nhận Allah.');
INSERT INTO "vn_mokhtasar" VALUES(7,6,'7:6','Vào Ngày Phục Sinh chắc chắn TA sẽ thẩm vấn các thế hệ mà TA đã gởi Thiên Sứ của TA đến họ xem họ đã đáp lại lời gọi của những Thiên Sứ đó như thế nào, và chắc chắc TA cũng thẩm vấn các Thiên Sứ đã gởi đi về thông tin mà TA sai bảo họ tuyên truyền cho dân chúng của họ.');
INSERT INTO "vn_mokhtasar" VALUES(7,7,'7:7','Chắc chắn TA sẽ kể tận tường từng việc làm của từng người đã làm gì trên thế gian bằng kiến thức của TA, TA luôn biết rõ điều họ làm, không gì giấu được TA và TA không hề xao lãng việc quan tâm họ bất cứ giây phút nào.');
INSERT INTO "vn_mokhtasar" VALUES(7,8,'7:8','Và vào Ngày Phục Sinh việc cân đo công bằng, không có gian lận hay bất công. Bởi thế ai có bàn cân công đức nặng hơn bàn cân tội lỗi thì họ là những người chiến thắng và thoát được nổi khiếp sợ hãi');
INSERT INTO "vn_mokhtasar" VALUES(7,9,'7:9','Còn ai có bàn cân tội lỗi nặng hơn bàn cân công đức thì đó là nhóm người tự hủy hoại bản thân họ trong Ngày Tận Thế chỉ vì họ đã chống đối các lời mặc khải của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(7,10,'7:10','Và quả thật TA đã định cư các ngươi - hỡi con cháu của Adam - ở trên trái đất, và TA đã tạo ra cho các ngươi vô số cách để sinh sống, đáng lẻ ra các ngươi phải tạ ơn Allah nhưng tiếc thay lời tạ ơn đó quá ít.');
INSERT INTO "vn_mokhtasar" VALUES(7,11,'7:11','Và quả thật, TA đã tạo hóa ra các ngươi - hỡi loài người - từ ông tổ Adam của các ngươi, rồi TA nắn các ngươi với một hình thể hoàn hảo nhất, rồi Ngài hạ lệnh cho giới Thiên Thần quỳ xuống ra mắt Người và tất cả đều quỳ xuống chỉ riêng tên Iblis đã kháng lệnh, hắn còn tỏ ra kiêu ngạo và bướng bỉnh.');
INSERT INTO "vn_mokhtasar" VALUES(7,12,'7:12','Allah Tối Cao đã hỏi quở trách tên Iblis: Điều gì cản trở nhà ngươi không chịu quỳ xuống trước Adam trong khi TA ra lệnh cho ngươi? Iblis bẩm tâu với Thượng Đế rằng: Điều cản trở bề tôi là bởi bề tôi tốt đẹp hơn hắn, Ngài đã tạo bề tôi từ lửa còn hắn chỉ được tạo ra từ đất sét và lửa cao quý hơn đất sét.');
INSERT INTO "vn_mokhtasar" VALUES(7,13,'7:13','Allah phán với hắn: Nhà ngươi hãy cút đi ra khỏi Vườn Trời, nhà ngươi đừng có kiêu căn ngạo mạn ở nơi đó, bởi nơi đó chỉ dành cho những gì tốt đẹp trong sáng, thế nên nhà ngươi không xứng đáng cư ngụ nơi đó. Quả thật ngươi - hỡi Iblis - là kẻ thật khinh bỉ, đáng bị sĩ nhục vậy mà nhà ngươi còn dám nghĩ nhà ngươi cao quý hơn Adam.');
INSERT INTO "vn_mokhtasar" VALUES(7,14,'7:14','Iblis van xin: Lạy Thượng Đế của bề tôi, xin Ngài tạm tha mạng cho bề tôi được sống đến Ngày Phục Sinh để bề tôi cám dỗ, xúi dục loài người bằng sự cố gắng của bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(7,15,'7:15','Allah phán với hắn: Quả thật nhà ngươi - hỡi Iblis - TA sẽ tạm tha mạng cho các ngươi cho đến ngày mà tiếng còi chác chúa đầu tiên được thổi, lúc đó tất cả vạn vật điều chết ngoại trừ Đấng Chúa Tể đã tạo hóa.');
INSERT INTO "vn_mokhtasar" VALUES(7,16,'7:16','Iblis nói: Vì nguyên nhân lầm lạc của bề tôi đã phạm vì không quỳ lạy Adam được Ngài tạm tha thứ bề tôi quyết sẽ lôi kéo con cháu Adam lệch khỏi con đường chính đạo của Ngài, quyết dụ dỗ họ rơi vào lầm lạc giống như bề tôi đã lầm lạc không quỳ lạy tổ tiên của họ là Adam.');
INSERT INTO "vn_mokhtasar" VALUES(7,17,'7:17','Sau đó, bề tôi quyết lôi kéo họ ở mọi hướng để họ rời xa cuộc sống Đời Sau bằng sự ham muốn phù phiếm của trần gian, sẽ nung nấu ham muốn của họ khiến họ trở nên mù quáng, và Ngài - thưa Thượng Đế - sẽ thấy rất ít người trong số họ biết tạ ơn bởi họ chìm ngập trong sự vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(7,18,'7:18','Allah bảo hắn: Hãy cút đi - hỡi Iblis - khỏi Vườn Trời, đồ đáng khinh, ngươi bị đuổi lòng thương xót của Allah. Trong Ngày Tận Thế, TA quyết sẽ nhét đầy Hỏa Ngục gồm ngươi và tất cả những ai theo ngươi cũng như nghe lời ngươi và làm trái mệnh lệnh TA.');
INSERT INTO "vn_mokhtasar" VALUES(7,19,'7:19','Allah phán bảo Adam: Hỡi Adam, Ngươi và vợ của Ngươi là Hawwa'' hãy định cư nơi Thiên Đàng, hai Ngươi tha hồ ăn uống thỏa thích những loại trái cây tốt đẹp mà hai Ngươi muốn nhưng không được ăn loại quả của cái cây này (Allah chỉ tận mắt hai Người Họ cây đó), nếu hai Ngươi dám ăn trái của nó kể từ khi TA cấm là xem như hai Ngươi kháng lệnh vượt quá giới hạn của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(7,20,'7:20','Rồi Iblis đến thì thầm to nhỏ với hai vợ chồng để lột trần cơ thể của Hai Người Họ, Iblis nói, sở dĩ Allah cấm hai anh chị ăn trái của cây này là vì Ngài không muốn anh chị trở thành hai Thiên Thần, Ngài không muốn hai anh chị sống mãi trong Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(7,21,'7:21','Và hắn đã thề bởi Allah với hai vợ chồng: Tôi khẳng định với hai người - hỡi Adam và Hawwa'' - là người thật lòng khuyên bảo cho anh chị được tốt hơn.');
INSERT INTO "vn_mokhtasar" VALUES(7,22,'7:22','Thế là hắn đã thành công đẩy hai vợ chồng vào mưu kế của hắn, khiến hai vợ chồng ăn đi trái cấm đó, lặp tức cơ thể họ bị lột trần nên cả hai đã cố lấy lá cây trong Thiên Đàng che chắn lại. Lúc đó Thượng Đế gọi hai vợ chồng: Há không phải TA đã cấm các ngươi ăn trái từ cây này hay sao, và TA đã cảnh cáo hai Ngươi rằng Shaytan là kẻ thù công khai của hai ngươi đó sao?');
INSERT INTO "vn_mokhtasar" VALUES(7,23,'7:23','Adam và Hawwa'' thưa: Lạy Thượng Đế của bầy tôi, bầy tôi tự làm hại chính bản thân mình vì lỡ phạm phải điều mà Ngài đã hạ lệnh cấm bầy tôi là không được phép ăn trái của cây mà Ngài cấm. Quả thật, nếu Ngài không tha thứ cho bầy tôi về tội lỗi của bầy tôi đã gây ra và không thương xót bầy tôi bằng lòng khoan dung của Ngài thì chắc chắn bầy tôi sẽ thất bại đánh mất hết thành quả ở trần gian và ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(7,24,'7:24','Allah phán với cả ba, vợ chồng Adam và Iblis: Cả ba ngươi hãy cút đi ra khỏi Thiên Đàng mà xuống mặt đất, ba ngươi sẽ là kẻ thù của nhau, mặt đất sẽ là nơi sinh sống của ba ngươi cho đến hết tuổi thọ và hưởng thụ đến một thời gian nhất định.');
INSERT INTO "vn_mokhtasar" VALUES(7,25,'7:25','Allah phán phán trực tiếp cho vợ chồng Adam và Hawwa'' cùng con cháu của họ: Trên trái đất này các ngươi sẽ có cuộc sống được ấn định cùng với định mệnh mà Allah qui định sẵn cho các ngươi, rồi nơi đó các ngươi sẽ chết và được chôn cất xuống mộ và từ dưới lòng mộ các ngươi được phục sinh sống trở lại.');
INSERT INTO "vn_mokhtasar" VALUES(7,26,'7:26','Hỡi con cháu của Adam, chắc chắn TA đã tạo ra cho các ngươi những bộ y phục cần thiết cho các ngươi để che đậy đi phần kín trên cơ thể của các ngươi giống như tô điểm cho các ngươi thêm đẹp đẽ, và trang phục Taqwa biết kính sợ - biết tuân thủ theo mọi mệnh lệnh của Allah - tốt đẹp hơn cả những bộ cánh khoác trên người. Với những bộ quần áo đã liệt kê cho các ngươi là những bằng chứng khẳng định quyền năng của Allah hi vọng các ngươi biết tạ ơn Allah bởi những thiên lộc đã ban cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(7,27,'7:27','Hỡi con cháu Adam: Các ngươi đừng bị lũ Shaytan mê hoặc các ngươi bởi lội lỗi bằng cách lột bỏ quần áo che phần kín trên cơ thể của các ngươi hoặc từ lột bỏ y phục Taqwa. Trong khi hắn đã lừa gạt tổ tiên của các ngươi ăn trái cấm khiến cho cả Hai Người Họ bị đuổi ra khỏi Thiên Đàng và cả hai bị phơi bày phần kín trên cơ thể đáng xấu hổ. Chắc chắn Shatan và dòng dõi của hắn nhìn thấy các ngươi nhưng các ngươi lại không nhìn thấy chúng, các ngươi hãy luôn cảnh giác hắn và dòng dõi của hắn. Quả thật, TA khiến cho những tên Shaytan làm kẻ bảo hộ của những ai không có đức tin với Allah, còn đối với tín đồ có đức tin luôn ngoan đạo thì chúng không thể đến gần được.');
INSERT INTO "vn_mokhtasar" VALUES(7,28,'7:28','Và khi nhóm người đa thần phạm phải một điều thô bỉ chẳng hạn như Shirk, việc Tawwaf trần truồng quanh ngôi đền K''abah và nhưng thứ khác thì họ xảo biện do đã thấy tổ tiên đã làm như thế, điều đó do Allah ra lệnh họ. Ngươi hãy bảo - hỡi Muhammad - với họ: Chắc chắn Allah không ra lệnh làm những điều thô bỉ đó, nói đúng hơn là Ngài cấm việc làm thô bỉ đó. Làm thế nào mà các ngươi có thể đổ thừa cho Ngài về hành động thối tha đó? Có phải các ngươi - hỡi nhóm người đa thần - cho rằng Allah không hề hay biết các ngươi dốc lao và phỉ báng Ngài chăng?');
INSERT INTO "vn_mokhtasar" VALUES(7,29,'7:29','Ngươi - hỡi Muhammad - hãy nói với nhóm người đa thần: Allah chỉ ra lệnh làm những điều đúng đắn chứ Ngài không chỉ thị làm những điều thô bỉ hay điều tà ác, Ngài còn chỉ thị cho các ngươi phải thành tâm thờ phượng Ngài dù bằng hình thức nào, nhất là trong các Masjid, một lòng khấn vái Ngài duy nhất giống như việc tạo hóa các ngươi từ không là gì, rồi Ngài sẽ phục sinh các ngươi sống lại lần nữa, bởi Ngài là Đấng có khả năng tạo hóa các ngươi là có khả năng phục sinh các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(7,30,'7:30','Chắc chắn Allah đã chia thiên hạ ra thành hai nhóm: Một nhóm được Ngài hướng dẫn, Ngài tạo mọi cách để họ dễ dàng nhận thức được chỉ đạo và nhóm còn lại bị Ngài ruồng bỏ trong lầm lạc chỉ vì họ bị những tên Shaytan mê hoặc khiến họ mù quáng chọn lấy Shaytan làm người bảo hộ thay vì Allah, vậy mà họ cứ tưởng họ đang được hướng dẫn đi trên con đường ngay chính.');
INSERT INTO "vn_mokhtasar" VALUES(7,31,'7:31','Hỡi con cháu Adam, các ngươi hãy ăn mặc phủ kín hết cơ thể của các ngươi và ăn mặc sạch sẽ hơn đẹp đẽ hơn khi dâng lễ nguyện Salah hay Tawwaf vòng quanh K''abah, hãy tự do ăn uống thỏa thích những thứ gì mà các ngươi muốn từ những món thực phẩm tốt và sạch mà Allah cho phép nhưng chớ lãng phí trong việc ăn uống, các ngươi chớ phung phí những điều Halal khiến các ngươi phạm đến Haram. Quả thật, Allah không thích những người phung phí để rồi vượt quá chuẩn mực.');
INSERT INTO "vn_mokhtasar" VALUES(7,32,'7:32','Ngươi - hỡi Thiên Sứ - hãy đáp lại nhóm người đa thần đã cấm những gì Allah cho phép trong trang phục, trong những món thực phẩm tốt sạch hoặc những thứ khác: Là ai đã cấm các ngươi mặc những bộ quần áo vốn tôn vinh vẽ đẹp của các ngươi? Và ai cấm các ngươi ăn uống những món tốt sạch từ thực phẩm mà Allah đã ban tặng cho các ngươi ? Ngươi hãy bảo - hỡi Thiên Sứ - đó là những thực phẩm tốt sạch dành cho tín đồ có đức tin trong cuộc sống trần gian này, điều này có thể người ngoài họ vẫn được phép cùng hưởng nhưng ở Đời Sau Thiên Đàng chỉ dành riêng cho người có đức tin cấm tuyệt người Kafir bước vào. Qua những lời phân tích này dành cho nhóm người biết nhận thức, bởi nó giúp ích được họ.');
INSERT INTO "vn_mokhtasar" VALUES(7,33,'7:33','Ngươi - hỡi Thiên Sứ - hãy nói với nhóm người đa thần đã cấm điều Allah cho phép: Quả thật, Allah chỉ cấm đám bầy tôi của Ngài làm những điều thô bỉ, đó là việc xấu xa đầy tội lỗi dù có phô bày hay che đậy, Ngài cấm tất cả việc làm tội lỗi, cấm vô cớ lạm sát, chiếm đoạt tài sản và xúc phạm danh dự họ, cấm tuyệt đối việc tổ hợp thần linh cùng Allah điều mà họ không hề có cơ sở, cấm nói sàm bậy về các đại danh, các thuộc tính, các hành động và giáo lý của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(7,34,'7:34','Tất cả mọi thế hệ hay thời đại đều được Allah ấn định một kỳ hạn nhất định, và kỳ hạn đó sẽ hết đúng y như thời gian đã định trước không sớm hơn cũng không bị trể hơn.');
INSERT INTO "vn_mokhtasar" VALUES(7,35,'7:35','Hỡi con cháu của Adam, khi Thiên Sứ của TA đến gặp các ngươi và họ xuất thân từ chính bộ tộc của các ngươi, họ sẽ truyền cho các ngươi nghe lời mặc khải của TA, lúc đó các ngươi hãy vâng lệnh họ và làm theo những gì mà họ mang đến. Những ai hạ mình kính sợ Allah rồi làm theo lời mặc khải, tuân lệnh Ngài thì y sẽ không lo sợ trong Ngày Tận Thế và cũng không buồn rầu với những gì đã qua ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(7,36,'7:36','Còn những ai phủ nhận các lời mặc khải của TA cũng như sứ mạng của những Thiên Sứ đã gởi đến họ, thì họ chính là nhóm bạn của Hỏa Ngục, họ phải cư ngụ mãi mãi trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(7,37,'7:37','Không một cái tệ hại nào hơn cho những kẻ đặt chuyện nói dốc láo rồi đổ lỗi cho Allah như việc cho rằng Ngài có cộng sự hoặc Ngài thiếu xót hoặc nói những điều Ngài không nói hoặc bác bỏ các lời mặc khải của Ngài hướng đến con đường chính đạo. Đó là những người sẽ hưởng được những gì đã tiền định cho họ trong Quyển Sổ Mẹ ở cuộc sống trần gian mãi đến khi Thần Chết cùng nhóm Thiên Thần khác đến rút hồn họ thì Họ hỏi mĩa mai chúng: Đâu rồi những thần linh mà các ngươi thờ phượng ngoài Allah? Hãy kêu họ đến giúp các ngươi lúc này. Nhóm đa thần đáp lại Thiên Thần: Thần linh mà chúng tôi thờ phượng đã biến mất và vắng mặt rồi, không biết giờ họ ở đâu và họ thú nhận mình là nhóm người vô đức tin, tất nhiên lời thú tội đó không giúp ích gì được họ.');
INSERT INTO "vn_mokhtasar" VALUES(7,38,'7:38','Thiên Thần bảo họ: Hãy đi vào đó - hỡi đám người đa thần - để hòa nhập vào những cộng đồng đi trước các ngươi vì đã bất tuân và lầm lạc từ loài Jinn và loài người đang bị thiêu đốt trong Hỏa Ngục. Cứ mỗi lần cộng đồng nào bị tống vào Hỏa Ngục liền nguyền rủa các thế hệ đàn anh, đàn chị đang ở trong đó, mãi cho đến khi tất cả đều tập trung hết trong Hỏa Ngục, những nhóm vào sau thưa: Lạy Thượng Đế của bầy tôi, đây là nhóm lãnh đạo của bầy tôi, họ đã lôi kéo bầy tôi vào lầm lạc khỏi con đường chính đạo, xin hãy trừng phạt họ gấp đôi hành phạt vì đã lôi kéo bầy tôi. Allah đáp lại họ: Tất cả các ngươi đều sẽ bị trừng phạt nhân đôi như nhau nhưng các ngươi không hề hay biết.');
INSERT INTO "vn_mokhtasar" VALUES(7,39,'7:39','Và nhóm lãnh đạo đáp nhóm đi theo: Các ngươi - hỡi nhóm đi theo - cũng chẳng hưởng được đặc ân hơn chúng tao về hình phạt được giảm nhẹ đâu, kết quả là dựa vào những gì đã làm kìa, không có lý do là nhóm người đi theo sẽ được giảm nhẹ, các ngươi - hỡi nhóm đi theo - hãy nếm đi hình phạt như chúng tao đã chịu vì nguyên nhân vô đức tin và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(7,40,'7:40','Quả thật, những ai phủ nhận các lời mặc khải rõ ràng của Ta, họ khinh thường ngông cuồn bất tuân thì họ sẽ bị đoạt tuyệt với mọi điều tốt, các cửa trời sẽ không mở chào đón những việc làm tội lỗi của họ, cũng không dung chứa các linh hồn của họ sau khi chết, họ tuyệt đối không được vào được Thiên Đàng trừ phi con lạc đà - thuộc loài động vật to lớn - có thể chiu qua được cái lỗ kim may một cái lỗ rất chật hẹp, đây là điều không thể xảy ra nghĩa là cơ hội vào Thiên Đàng dành cho họ là không bao giờ, và như thế đó Allah dùng để thưởng cho những kẻ mang đại tội.');
INSERT INTO "vn_mokhtasar" VALUES(7,41,'7:41','Đối với những kẻ chối bỏ, ngạo mạng sẽ có một cái gường trong Hỏa Ngục rồi chúng sẽ nằm trên nó và có các tấm bao phủ bên trên chúng bằng lửa. Và sự trừng phạt như thế là giành cho những kẻ vi phạm vào những giới hạn của Allah bởi sự bất tuân của chúng với Ngài bằng một cách công khai.');
INSERT INTO "vn_mokhtasar" VALUES(7,42,'7:42','Còn đối với những ai tin tưởng Thượng Đế họ luôn làm những việc làm ngoan đạo mà họ đã tuân theo - và Allah không bắt một linh hồn nào gánh vác trách nhiệm cao hơn khả năng của nó - rồi họ sẽ là những người bạn của Thiên Đàng họ vào trong đó định cư vĩnh viễn.');
INSERT INTO "vn_mokhtasar" VALUES(7,43,'7:43','Và sự ngập tràn niềm hạnh phúc giành cho họ trong Thiên Đàng là được Allah xóa bỏ khỏi lòng họ sự oán thù, sự ganh ghét rồi có các dòng sông chảy bên dưới họ và họ thưa trong với tâm trạng biết được hồng ân mà Allah đã ban "Alhamdulillah, xin tạ ơn Allah, Đấng đã khiến bầy tôi hành đạo được Ngài hài lòng để ban cho bầy tôi vị trí như thế này; làm sao bầy tôi có thể được mãn nguyện như thế này nếu không được Ngài phù hộ cơ chứ. Quả thật, các Thiên Sứ của Ngài đã mang chân lý đến bằng sự trung thực trong lời hứa và lời khuyến cáo. Có lời phán bảo: Đây là Thiên Đàng mà các ngươi được Thiên Sứ của TA cho biết ở trần gian, Ngài luôn giám sát và theo giỏi các ngươi để các ngươi làm việc thiện với tấm lòng muốn được Allah hài lòng.');
INSERT INTO "vn_mokhtasar" VALUES(7,44,'7:44','Và dân cư sống mãi trong Thiên Đàng réo gọi dân cư muôn đời trong Hỏa Ngục: "Chắc rằng bọn ta đã thấy được những gì mà Thượng Đế của bọn ta đã từng hứa sau khi vào được Thiên Đàng, thế còn các ngươi - hỡi người chối bỏ đức tin - không phải các ngươi thấy được những gì mà Allah đã từng hứa với các ngươi trong Hỏa Ngục đó ư ? Họ đáp: "Chắc rồi chúng tôi đã thấy được những gì mà Ngài đã từng hứa hẹn về Hỏa Ngục. Có lời khẩn cầu Allah xua đuổi những kẻ bất công ra khỏi lòng thương xót của Ngài, bởi họ đã chối bỏ lòng thương xót khi mở cho họ ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(7,45,'7:45','Những kẻ bất công mà trước kia họ ngoãnh mặt với con đường của Allah, và chúng còn lôi kéo những người khác ra khỏi con đường đó, chúng còn hy vọng những con đường chân lý là con đường quanh co để cho thiên hạ này không tuân thủ theo nó và chúng là những kẻ đa thần chẳng có sự chuẩn bị gì cho Ngày Sau.');
INSERT INTO "vn_mokhtasar" VALUES(7,46,'7:46','Và giữ hai nhóm người này: Dân cư Thiên Đàng và dân cư Hỏa Ngục được chắn ngang bởi một cái rào chắn cao vút được gọi là Al-''Araaf rồi trên cái rào chắn cao vút đó có những người đàn ông ngoan đạo và có những người tội lỗi, họ được nhận biết là cư dân Thiên Đàng bởi gương mặt sáng chói của họ và nhận biết được cư dân Hỏa Ngục với gương mặt đen xì, có lời réo gọi cư dân Thiên Đàng mà bảo: "Xin chào Salam đến các ngươi." Và cư dân Thiên Đàng vẫn chưa được vào Thiên Đàng nhưng họ đặt niềm hy vọng là được vào với lòng Nhân Từ của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(7,47,'7:47','Và khi nhóm người trên đỉnh Al-''Araaf nhìn xuống những người trong Hỏa Ngục đang bị trừng phạt khủng khiếp, họ khẩn cầu Allah: "Lạy Thượng Đế của bầy tôi, xin Ngài đừng biến bầy tôi trở thành những kẻ bất công không tin tưởng Ngài và phạm Shirk với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(7,48,'7:48','Và khi nhóm người trên đỉnh Al-''Araaf gọi những người vô đức tin trong Hỏa Ngục, nhận biết họ qua gương mặt đen xì cùng đôi mắt màu xanh dương, họ nói: "Khối tài sản kếch xù và những người đàn ông đầy tớ của các ngươi chẳng giúp ích gì cho các ngươi cũng như hành động ngoãnh mặt với chân lý thể hiện sự tự cao và ngạo mạn chẳng có lợi ích gì."');
INSERT INTO "vn_mokhtasar" VALUES(7,49,'7:49','Allah hỏi thách đố những kẻ vô đức tin: "Phải chăng, đây là những người mà các ngươi từng thề khẳng định là họ sẽ không được Allah khoan dung phải không?" Rồi Allah phán bảo nhóm người có đức tin: "Các ngươi hãy bước vào Thiên Đàng đi, các ngươi sẽ không còn sợ sệt đều gì cho tương lai cũng như không buồn bã về mọi điều đã bị mất ở trần gian khi các ngươi được hưởng thụ trong Thiên Đàng vĩnh cữu.');
INSERT INTO "vn_mokhtasar" VALUES(7,50,'7:50','Và cư dân Hỏa Ngục to tiếng gọi dân cư Thiên Đàng mà nài nỉ: "Xin quý vị cho cho chúng tôi ít nước uống đi - hỡi cư dân Thiên Đàng - hoặc bất cứ thức ăn mà Allah ban cấp cho quý vị". Cư dân Thiên Đàng đáp: "Quả thật, Allah cấm hai thứ đó với người vô đức tin bởi họ đã không tin tưởng nên chúng tôi không thể đưa cho các ngươi những gì Allah đã cấm."');
INSERT INTO "vn_mokhtasar" VALUES(7,51,'7:51','"Nhóm người vô đức tin đã biến tôn giáo họ trở thành hình thức bởn cợt và vô bổ, họ bị mệ muội bởi cuộc sống trần tục này bởi sự phù phiếm và xa hoa của nó, họ đã lãng quên Ngày Tận Thế và bỏ mặc những điều khiến họ rơi vào hành phạt mà không có sự chuẩn bị tránh xa, họ cố chống đối bác bỏ mọi bằng chứng của Allah trong khi họ biết đó là chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(7,52,'7:52','Và quả thật, TA đã mang đến cho họ Qur''an, đó là Kinh sách được mặc khải cho Muhammad, trong đó TA giải thích rất rõ ràng dựa vào sự Am Tường của TA. Và Nó là sự chỉ đạo cho người có đức tin đi đến con đường chân lý, lòng thương xót mang lại sự tốt đẹp ở trần gian và Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(7,53,'7:53','Những người vô đức tin không chờ đợi gì ngoài những điều đã thông báo về hình phạt đau đớn sẽ xảy ra đến với họ vào Ngày Phán Xét, ngày mà họ được báo trước rồi nó sẽ diễn ra như thế, cũng là ngày mà những người có đức tin được nhận được phần thưởng. Những kẻ lãng quên Qur''an ở trần gian hay họ chưa tuân theo lời di huấn trong Nó thốt lên: "Quả thật, những gì Thiên Sứ đã mang đến đúng là chân lý đến không có gì để bàn cải nữa. Ôi ước chi có ai đó đứng ra can thiệp cho chúng tôi với Allah để khỏi bị hình phạt hoặc ước gì được trở về cuộc sống trần gian để chúng tôi có thể làm những việc làm ngoan đạo thay vì chúng tôi đã từng làm chuyện đầy tội lỗi," nhưng đám người vô đức tin này đã tự hủy hoại đi chính bản thân mình bằng cách tự tạo ra những nguyên nhân đẩy mình vào diệt vong do không có đức tin, những thần linh mà họ thờ phượng ngoài Allah đã trốn mất chẳng giúp ích gì cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(7,54,'7:54','Quả thật, Thượng Đế của các ngươi - hỡi nhân loại - là Allah, Đấng đã tạo dựng các tầng trời và trái đất cùng với những thứ khác từ không là gì trước đó chỉ trong vòng sáu ngày. Sau đó, Ngài an vị trên Ngai Vương theo hình thức phù hợp với uy quyền của Ngài, điều mà không ai có thể nhận thức được. Ngài xóa tan bóng tối bóng màn đêm bằng ánh sáng của ban ngày và che lấp ánh sáng của ban ngày bằng bóng tối của màn đêm, sự thay đổi diễn ra rất nhanh chóng không chút chậm trễ, khi cái này bước ra là cái khác bước vào thay thế. Ngài tạo ra mặt trời và mặt trăng cùng với tất cả tinh tú, há chẳng phải tất cả vạn vật là do Allah một mình đã tạo ra hay sao, nếu không phải Ngài thì ai đã tạo ra chúng? Ngài toàn quyền quyết định, Ngài là Đấng Chúa Tể của toàn vũ trụ và muôn loài.');
INSERT INTO "vn_mokhtasar" VALUES(7,55,'7:55','Các ngươi - hỡi những người có đức tin - hãy cầu nguyện Thượng Đế của các ngươi trong sự tuyệt đối khiêm tốn, khép nép hạ mình và một cách thầm lặng cùng với sự chân thành trong lúc cầu khẩn Ngài cho dù không ai thấy, chớ nên là những kẻ chống đối Ngài và cầu nguyện không phải là Ngài. Quả thật, Ngài không yêu thích những kẻ thái quá vượt quá mức trong lúc cầu nguyện, đó đại trọng tội như việc làm của bọn đa thần.');
INSERT INTO "vn_mokhtasar" VALUES(7,56,'7:56','Và các ngươi chớ phá hoại trái đất bằng những việc làm đầy tội lỗi sau khi đã được Allah hoàn tất nó bằng cách gửi các vị Thiên Sứ đến hướng dẫn mọi thứ phục tùng Ngài duy nhất, hãy cầu nguyện Allah duy nhất bằng cả tấm lòng, rùng mình khiếp sợ về hình phạt của Ngài cùng với sự đầy hy vọng về ân phước của Ngài. Quả thật, hồng ân của Allah luôn tiếp cận với những người sùng đạo, các ngươi hãy là họ.');
INSERT INTO "vn_mokhtasar" VALUES(7,57,'7:57','Và Allah đã đưa những luồng gió báo trước sẽ có những trận mưa; mãi cho đến khi những luồng gió tạo những đám mây trong đó trĩu nặng những hạt mưa rồi Ngài sẽ duy chuyển đến một mảnh đất khô cằn rồi Ngài ban xuống những trận mưa lên đó và sau những trận mưa đó sẽ làm mọc ra đủ loại hoa màu, cũng giống như hình ảnh đó Ngài phục sinh người chết sống lại từ cõi mộ. Với việc làm đó Ngài hy vọng các ngươi - hỡi nhân loại - tưởng nhớ đến sự quyền năng của Allah sẽ phục sinh các ngươi trở lại, chắc chắn Ngài có khả năng phục sinh cái chết.');
INSERT INTO "vn_mokhtasar" VALUES(7,58,'7:58','Và vùng đất màu mỡ sẽ mọc ra những hoa mầu phù nhiêu trìu trĩu bằng sự chấp thuận của Allah, tương tự như người có đức tin biết nghe lời khuyên bảo sẽ mạng lại lợi ích cho y và khiến y năng hành đạo, và vùng đất khô cằn sẽ không mọc ra ngoài những thứ chẳng tốt lành gì, tương tự như người ngoại đạo thì những lời khuyên không mang lại cho họ lợi ích. Với những bằng chứng xác định chân lý luôn giúp ích tín đồ có đức tin biết tạ ơn những hồng phúc được Allah ban cho, họ không vong ơn và luôn ngoan ngoãn.');
INSERT INTO "vn_mokhtasar" VALUES(7,59,'7:59','Chắc chắn rằng, TA đã gởi Nuh làm Thiên Sứ đến gặp người dân của Y để kêu gọi họ đến với thuyết Tawhid độc tôn Allah duy nhất và bỏ hết mọi việc thờ phượng ngoài Ngài, Y bảo họ: "Này hỡi dân Ta, hãy thờ phượng Allah duy nhất, bởi không có Thượng Đế nào xứng đáng để thờ phượng ngoài Ngài cả, Ta sợ giùm cho các ngươi - hỡi dân Ta - về sự trừng phạt vào ngày Trọng Đại nếu các ngươi cứ mãi chìm trong vô đức tin."');
INSERT INTO "vn_mokhtasar" VALUES(7,60,'7:60','Các tên cầu cầu cùng những tên đầu sỏ trong cộng đồng của Người lên tiếng: "Trong mắt bọn ta - hỡi Nuh - Ngươi đang ở rất xa với chân lý."');
INSERT INTO "vn_mokhtasar" VALUES(7,61,'7:61','Rồi Nuh bảo với những tên đầu sỏ trong bộ tộc Người: "Thật ra Ta không lạc lối như các ngươi đã tuyên bố; chẳng qua Ta được sự chỉ dẫn của Thương Đế mình chỉ bảo và Ta là Thiên Sứ được phái đến với các ngươi, Ngài là Thượng Đế của Ta cũng là Thượng Đế của các ngươi và là Chúa Tể của toàn vũ trụ và muôn loài."');
INSERT INTO "vn_mokhtasar" VALUES(7,62,'7:62','"Ta truyền đạt cho các ngươi Bức Thông Điệp mà Ta nhận được từ Allah thiên khải cho Ta, Ta muốn các ngươi được tốt đẹp qua việc tuân lệnh Ngài và phần thưởng mà Ngài sẽ ban thưởng, song khuyến cáo các ngươi về những điều Ngài cấm và hành phạt mà Ngài sẽ trừng trị, bởi Ta biết rõ về Ngài hơn các ngươi qua lời mặc khải cho Ta.');
INSERT INTO "vn_mokhtasar" VALUES(7,63,'7:63','"Phải chăng các ngươi ngạc nhiên và bất ngờ khi biết lời mặc khải và lời phán bảo của Thượng Đế được truyền đạt lại từ một người đàn ông mà các ngươi biết về Y ư?" Và chắc chắn, Y là người phàm tục xuất thân từ các ngươi, Y chưa từng dốc láo hay lạc lối, cũng không phải là người xa lạ, Y đến để cảnh tỉnh các ngươi về sự trừng phạt của Allah nếu các ngươi chối bỏ và chống đối. Các ngươi hãy kính sợ Allah mà tuân thủ theo mệnh lệnh của Ngài hi vọng các ngươi được thương xót.');
INSERT INTO "vn_mokhtasar" VALUES(7,64,'7:64','Nhưng dân làng lại cho rằng Người nói dối, họ chẳng tin tưởng nơi Người, đúng hơn là chúng cương quyết chống đối. Và Người cầu xin Allah hãy tiêu diệt bọn họ liền được Ngài giải cứu Người Y một cách an toàn trên một chiếc tàu cùng với những người có đức tin theo Người, còn những kẻ vô đức tin đều bị Ngài tiêu diệt trong trận đại hồng thủy giáng lên họ, bởi con tim của bọn sống trong sự mù quáng.');
INSERT INTO "vn_mokhtasar" VALUES(7,65,'7:65','Và TA đã phái đến với bộ tộc ''Ad một vị Thiên Sứ xuất thân từ họ, Người là Hud, Người bảo họ: "Này hỡi dân Ta, hãy tôn thờ Allah duy nhất, ngoài Ngài các ngươi chẳng có Thượng Đế đích thực nào cả. Há các ngươi chưa biết kính sợ Ngài mà tuân thủ theo mệnh lệnh của Ngài để bình an khỏi hành phạt.');
INSERT INTO "vn_mokhtasar" VALUES(7,66,'7:66','Những tên đầu sỏ cùng các tên bô lão trong bộ tộc Người, những kẻ phủ nhận Allah và chối bỏ Thiên Sứ của Ngài nói: "Trong mắt bọn ta thấy Ngươi - hỡi Hud - là một Người thiểu năng, ngu ngốc khi kêu gọi bọn ta chỉ thờ phượng Allah duy nhất, từ bỏ hết bức tượng của bọn ta và trong lòng bọn ta khẳng định Ngươi là tên nói dối tự nhận mình là một Thiên Sứ."');
INSERT INTO "vn_mokhtasar" VALUES(7,67,'7:67','Hud đáp lại dân chúng của Người: "Này hỡi dân Ta, Ta không phải là Người thiểu năng hay kẻ ngu ngốc, nói đúng hơn Ta là một Thiên Sứ của Đấng Chúa Tể của vũ trụ và muôn loài.');
INSERT INTO "vn_mokhtasar" VALUES(7,68,'7:68','"Ta truyền đạt cho các ngươi những chiếu lệnh của Allah đó là mệnh lệnh cho các ngươi là phải thờ phượng Ngài và tuân theo giáo luật của Ngài. Ta răn đe các ngươi từ chiếu lệnh của Ngài một cách trung thực chính xác Ta không thêm vào và cũng không bớt ra những gì trong Nó."');
INSERT INTO "vn_mokhtasar" VALUES(7,69,'7:69','"Có phải, những điều đã xảy ra làm cho các ngươi ngạc nhiên và bất ngờ khi lời nhắc nhở đến với các ngươi là người trong tộc với các ngươi, nói chuyện bằng ngôn ngữ của các ngươi, y không thuộc giống loài Thiên Thần hay Jinn đó ư? Hãy tạ ơn Thượng Đế khi Ngài cho các ngươi thừa hưởng trái đất sau khi Ngài đã tiêu diệt dân của Nuh vì tội vô đức tin, hãy tạ ơn Ngài đã cho các ngươi vóng dáng và sức mạnh, hãy tạ ơn về những hồng phúc đã ban cho các ngươi hi vọng các ngươi được toại nguyện và không hoảng sợ.');
INSERT INTO "vn_mokhtasar" VALUES(7,70,'7:70','Những tên đầu sỏ của dân chúng Người đáp lại: Hỡi Hud, phải chăng Ngươi đến đây để truyền mệnh lệnh của Thượng Đế của Ngươi là để kêu bọn ta phải tôn thờ Allah duy nhất và từ bỏ hết tất cả những tổ tiên, ông bà của bọn ta đã từng thờ phượng chăng ? Và nếu như những gì Ngươi nói là thật thì hãy mang đến đây cái hình phạt mà Ngươi thường nói để hăm dọa bọn ta đến trừng phạt bọn ta đi.”');
INSERT INTO "vn_mokhtasar" VALUES(7,71,'7:71','Hud đáp lại: "Chắc chắn, hình phạt cùng với cơn thịnh nộ của Allah mà các ngươi đã thách thức nó sẽ diễn ra với các ngươi mà không làm sao thay đổi được. Phải chăng, các ngươi tranh luận với Ta về những cách xưng hô cho các bụt tượng mà các ngươi lẫn tổ tiên của các ngươi tự đặt trong khi điều đó không bao giờ có thật? Bởi thế, những ân điển mà Allah đã ban cho các ngươi rồi các ngươi lấy nó để xúi dục mọi người thờ phượng thần linh các ngươi. Các ngươi hãy chờ xem những gì mà các ngươi đã thách thức nó sẽ diễn ra với các ngươi trong một cơn thịnh nộ cùng với hình phạt mà Ngài sẽ giành cho các ngươi và Ta sẽ cùng với các ngươi chờ đợi điều đó diễn ra."');
INSERT INTO "vn_mokhtasar" VALUES(7,72,'7:72','Bởi thế, với lòng thương xót của TA đã cứu vớt Hud cùng với những người có đức tin theo Y và sau đó TA đã tiêu diệt hết những kẻ phủ nhận đi những dấu hiệu của TA bởi bọn họ là những kẻ không có đức tin hay chính xác hơn bọn chúng là những kẻ ương ngạnh luôn chống đối và rồi bọn họ sẽ sớm bị trừng trị thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(7,73,'7:73','TA đã gởi đến bộ lạc Thamud một Thiên Sứ là người anh em xuất thân từ họ là Saleh để kêu gọi họ đến với thuyết Tawhid tôn thờ Allah duy nhất, Người bảo họ: "Này hỡi dân ta, hãy thờ phượng Allah duy nhất, các ngươi không có Thượng Đế đích thực nào ngoài Ngài để thờ phượng, đã có một dấu hiệu rõ ràng của Allah đến với các ngươi chứng minh sứ mạng của Ta, đó là có một con lạc đà cái nó đi ra từ một tản đá, giếng nước một ngày dành cho nó uống và một ngày dành cho các ngươi uống, các ngươi hãy bỏ mặc nó ăn trên đất của Allah không cần các ngươi bận tâm, cũng đừng sát hại nó, nếu không nghe thì đó là lý do khiến các ngươi phải gánh chịu một hình phạt đau đớn."');
INSERT INTO "vn_mokhtasar" VALUES(7,74,'7:74','Các ngươi hãy tưởng nhớ những hồng ân mà Allah đã ban cho các ngươi thừa kế sau bộ tộc ''Ad, Ngài ban cho các ngươi mảnh đất để cho các ngươi lấy đó làm nơi cư ngụ theo mong muốn riêng, sau khi Ngài đã tiêu diệt bộ tộc ''Ad vì ngoan cố không tin tưởng và chống đối với Ngài, họ xây lên những mảnh đất bằng phẳng bằng với những tòa lâu đài và đục phá núi để làm dinh thự cho các ngươi. Bởi thế, các ngươi hãy nhớ đến những hồng ân mà Allah ban cho các ngươi mà lấy đó để tạ ơn Ngài, hãy bỏ ngay những việc làm xáo trộn trên trái đất này bằng những điều tồi tệ như phủ nhận Allah và bất tuân.');
INSERT INTO "vn_mokhtasar" VALUES(7,75,'7:75','Những tên bô lão cùng với những tên đầu sỏ trong số những kẻ ngông cuồn thuộc dân của Y nói với những người có đức tin yếu thế cùng Y: "Các ngươi có biết không - hỡi người có đức tin - rằng Saleh là một Thiên Sứ của Allah, đúng thực không ?" Và những người có đức tin yếu thế đáp: "Chắc rằng chúng tôi tin, chấp nhận và tuân thủ theo những gì mà Người mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(7,76,'7:76','Những kẻ ngông cuồng của bộ tộc của Y nói: "Quả thật, bọn ta phủ nhận những gì mà các ngươi hằng tin tưởng - hỡi những người có đức tin - bọn ta sẽ không làm theo những giáo luật đó."');
INSERT INTO "vn_mokhtasar" VALUES(7,77,'7:77','Và rồi bọn họ đã sát hại con lạc đà cái vi phạm lệnh cấm, họ còn hách dịch trước mệnh lệnh của Allah mà nói chế nhạo: "Này hỡi Salah, hãy mang đến cho bọn ta hình phạt khủng khiếp mà Ngươi đã từng dọa dẫm bọn ta đến để trừng phạt bọn ta đi, nếu như Ngươi là một Thiên Sứ của Allah thật sự."');
INSERT INTO "vn_mokhtasar" VALUES(7,78,'7:78','Bởi thế, một sự trừng phạt liền giáng lên những kẻ phủ nhận vội vả hối thúc, đó là một trận động đất dữ dội túm lấy bọn chúng thật kinh hoàng làm mặt và đầu gối bọn họ lúng xuống đất và không ai trong họ thoát khỏi cảnh khủng khiếp đó.');
INSERT INTO "vn_mokhtasar" VALUES(7,79,'7:79','Tiếp đó, Saleh bỏ mặc dân chúng mình và bảo với họ: '''' Này hỡi dân Ta, Ta làm tròn trách nhiệm mà Allah đã giao phó cho Ta đến với các ngươi về Bức Thông Điệp của Ngài và khuyên bảo các ngươi những điều tốt lành và kèm theo những lời cảnh cáo kinh hoàng, và rồi hình như điều đó đã làm cho các ngươi không ưa thích những lời răn bảo của các vị cố vấn đã quan tâm đến chỉ dẫn các ngươi về những điều tốt đẹp và ngăn chặn các ngươi tránh những điều xấu xa.''''');
INSERT INTO "vn_mokhtasar" VALUES(7,80,'7:80','Và hãy nhớ lại Lut trong lúc Y vạch trần tội lỗi dân chúng của Người: "Há phải chăng, bọn ngươi đã làm một việc làm thứ đồi bại, xấu xa bằng cách quan hệ đồng tính? Đó là một việc làm thô bỉ động trời nhất mà không có một ai trong thiên hạ này dám làm điều thô bỉ đó trước đây !"');
INSERT INTO "vn_mokhtasar" VALUES(7,81,'7:81','"Quả thật, các ngươi đã giải quyết chuyện chăn gối cùng với những người đàn ông mà không cần đến những người phụ nữ trong khi họ được tạo ra là để phục vụ cho mục đích đó, hành động đồi bại này không phù hợp lý trí, tập quán cũng như bản năng của con người. Không, các ngươi đã vượt quá giới luật của Allah của một con người, vượt xa lý trí và bản năng tự nhiên.');
INSERT INTO "vn_mokhtasar" VALUES(7,82,'7:82','Người dân của Người đáp lại khi bị cấm cản làm những điều tội lỗi, họ phản đối chân lý: "Hãy trục xuất Lut cùng gia đình của Y ra khỏi xóm làng của quí vị; và chắc chắn, họ phải tách rời ra khỏi những việc làm của chúng ta và Y không thích hợp ở cùng với chúng ta."');
INSERT INTO "vn_mokhtasar" VALUES(7,83,'7:83','Rồi TA đã giải cứu Y cùng gia đình Y, TA lệnh họ rời khỏi thị trấn của họ trong đêm vì TA sắp giáng trừng phạt lên nó, ngoại trừ mụ vợ của Y sẽ lại cùng với đám người đồi trụy, mụ ta bị trừng phạt như những người dân còn lại.');
INSERT INTO "vn_mokhtasar" VALUES(7,84,'7:84','Và TA đã trút lên bọn họ một trận mưa đá khủng khiếp, TA ném xuống họ những viên đá bằng đất sét, rồi TA lật úp cả thị trấn khiến nó bị đảo ngược. Bởi thế, Ngươi - hỡi Thiên Sứ - hãy xem hậu quả của đám người tội lỗi trong dân chúng Lut, họ đã bị tiêu diệt và sự nhục nhã tột cùng.');
INSERT INTO "vn_mokhtasar" VALUES(7,85,'7:85','Và rồi TA đã gởi đến bộ tộc Madyan một người anh em xuất thân từ họ là Shu-''aib, Người nói với họ: "Này hỡi dân Ta, hãy thờ phượng Allah duy nhất, ngoài Ngài các ngươi không có Thượng Đế đích thực nào để thờ phượng. Chắc chắn Allah đã gởi đến các ngươi bằng chứng quá rõ ràng, đầy thuyết phục khẳng định sự thật được đến từ Allah. Các ngươi hãy đo cho đủ và cân cho đầy, chớ bớt xén đi hàng hóa của họ bằng cách lường gạt họ, làm thất thoát đi hoặc gian lận lợi nhuận của người sở hữu. Và rồi, các ngươi chớ là những kẻ phá rối trên trái đất này bằng hành động chống đối, tội lỗi, vô đức tin, với những điều đã kể rất hữu ích nếu như các ngươi là những có đức tin, vì trong nó giúp các ngươi từ bỏ tội lỗi mà Allah đã cấm và giúp các ngươi đến gần Allah hơn.');
INSERT INTO "vn_mokhtasar" VALUES(7,86,'7:86','"Và rồi các ngươi chớ ngồi tại mọi ngã đường hành hung đe dọa mọi người để cướp bóc đi tài sản của họ, cản trở mọi người đến với tôn giáo của Allah trong khi mọi người đều muốn được sự chỉ dẫn của Ngài và rồi các ngươi luôn tìm cách bóp méo đi con đường chính đạo của Ngài cho đến khi không còn ai biết đó mà bám lấy. Các ngươi nên nhớ lại những hồng ân mà Allah đã ban cho các ngươi rồi hãy lấy đó mà tạ ơn Ngài. Bởi trước kia các ngươi rất là thưa thớt rồi Ngài đã gia tăng nhân số lên đông và rồi các ngươi hãy xem cho kỹ sự trừng trị của những kẻ phá hoại trái đất này trước các ngươi. Quả thật, TA trừng phạt bọn chúng bằng cách tiêu diệt và tàn phá."');
INSERT INTO "vn_mokhtasar" VALUES(7,87,'7:87','"Và nếu có một nhóm người các ngươi tin tưởng những điều mà TA đã mang đến từ Thượng Đế Ta và một nhóm khác không tin tưởng vào những điều đó, thì hãy chờ đi - hỡi nhóm người chối bỏ - Allah sẽ phân xử các ngươi bởi Ngài luôn công bằng anh minh trong việc phán xét."');
INSERT INTO "vn_mokhtasar" VALUES(7,88,'7:88','Những tên ngông cuồng cùng với những kẻ đầu sỏ của đám người kiêu căng trong dân chúng của Shu-''aib lên tiếng: "Bọn ta sẽ tống cổ Ngươi - hỡi Shu-''aib - ra khỏi xứ sở của bọn ta cùng với những tín đồ tin tưởng vào Ngươi, hoặc Ngươi cùng với họ trở lại tôn giáo của bọn ta." Shu-''aib ngạc nhiên hỏi họ: "Hả, các ngươi nói sao, các ngươi kêu Ta trở lại với tôn giáo của các ngươi và tín ngưỡng cùng với các ngươi trong khi chúng tôi ghét nó vì chúng tôi biết rõ các ngươi đang lầm lạc hay sao ?"');
INSERT INTO "vn_mokhtasar" VALUES(7,89,'7:89','"Chúng tôi sẽ là kẻ đổ thừa cho Allah, nếu tin tín ngưỡng Shirk và vô đức tin cùng với quý vị sau khi chúng tôi được Allah ban sự bình an khỏi nó. Chúng tôi không được phép trở lại và không đáng phải trở lại tín ngưỡng sai lầm chung với các người ngoại trừ Ngài muốn đều đó; tất cả đều nằm dưới quyết định của Allah, kiến thức của Thượng Đế của chúng tôi sẽ bao quát hết tất cả mọi điều, không có gì giấu giếm được Ngài, rồi chỉ có duy nhất Allah là Đấng để cho chúng tôi dựa dẫm để được đến với con đường ngay chính và bảo vệ cho chúng tôi tránh khỏi con đường đến Hỏa Ngục. Lạy Thượng Đế của bầy tôi, xin Ngài hãy phán xét quyết định giữa bề tôi và đám dân chúng của bề tôi, đám người không tin vào Chân Lý. Xin hãy phù hộ người đúng đắn đang bị bất công chống lại tên cậy quyền đàn áp.- Lạy Thượng Đế của bầy tôi - quả thật Ngài là Đấng Quyết Định Ưu Việt.”');
INSERT INTO "vn_mokhtasar" VALUES(7,90,'7:90','Các tên ngông cuồng cùng với những tên đầu sỏ của những kẻ bất tuân trong đám thị dân của Người đã khước từ lời kêu gọi đến với Tawhid, họ cảnh báo về Shu-''aib và tôn giáo của Người rằng: "Nếu các ngươi gia nhập - hỡi dân ta - vào tôn giáo của Shu-''aib mà từ bỏ đi tôn giáo của các ngươi, tôn giáo của ông bà lẫn tổ tiên của các ngươi vậy thì chắc chắn các ngươi đáng bị hủy diệt."');
INSERT INTO "vn_mokhtasar" VALUES(7,91,'7:91','Bởi thế, một trận động đất dữ dội ụp xuống tiêu diệt bọn chúng vào buổi sáng khiên họ chết trong nhà với tư thế nằm xấp úp mặt và đầu gói xuống đất, bọn họ chết trong trạng thái không biết gì khi đang ở trong chính ngôi nhà của mình.');
INSERT INTO "vn_mokhtasar" VALUES(7,92,'7:92','Những ai cho rằng Shu-''aib nói dốc thì bọn chúng đã bị tiêu diệt toàn bộ, bọn chúng giống như chưa sống tại trong nhà họ và cũng như chưa từng tồn tại ở nơi đó, rồi những kẻ nào cho rằng Shu-''aib nói dối thì chúng là những kẻ thua thiệt; bởi họ khiến bản thân và mọi thứ họ chiếm hữu đều thất bại khi không phải là người tin tưởng như nhóm người có đức tin cùng với mọi người.');
INSERT INTO "vn_mokhtasar" VALUES(7,93,'7:93','Và rồi Thiên Sứ của họ, Shu-''aib đã bỏ mặc họ sau khi họ bị tiêu diệt. Người nói với họ: "Này mọi người! Quả thật, Ta đã truyền đạt cho các ngươi về mọi điều mà Thượng Đế bảo Ta truyền đạt, Ta cũng đã khuyên bảo các ngươi cạn lời vậy mà các ngươi không chịu chấp nhận lời khuyên bảo và không làm theo hướng dẫn của Ta. Bởi thế, tại sao Ta lại tiếc thương cho một đám người không tin tưởng Allah thế vậy mà còn khăng khăng chống đối Ngài ?"');
INSERT INTO "vn_mokhtasar" VALUES(7,94,'7:94','Không có một xứ sở nào khi đã phủ nhận Nabi mà TA đã gởi đến họ mà họ lại không bị TA bắt nếm cảnh nghèo khổ, tai kiếp, bệnh tật để mai ra họ biết khiêm nhường với Allah, bỏ đi tính ngạo mạng và sự kiêu căng của họ. Và đó cũng là sự cảnh báo cho Quraish cũng như tất những kẻ không có tin tưởng lẫn với những kẻ xảo biện qua hình ảnh những cộng đồng phủ nhận trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(7,95,'7:95','Rồi TA hoán đổi cho họ từ điều khốn khổ tai kiếp thành những điều tốt đẹp trù phú và thanh thản cho đến khi họ phát đạt dồi dào và thừa hưởng khối tài sản kết xù rồi họ lại nói: "Chẳng phải rằng tổ tiên của chúng tôi đã từng nếm qua cảnh khắc khổ và giàu sang đó chăng và đó cũng là những cảnh mà ông bà của chúng tôi từng trải qua. Nhưng họ không nhận ra được điều mà khi tai kiếp xảy ra với họ để họ rút kinh nghiệm, còn những ân huệ đến với họ là đang lôi cuống họ, thế là họ đã bị hành phạt của TA túm lấy bất ngờ khiến họ không lường trước được.');
INSERT INTO "vn_mokhtasar" VALUES(7,96,'7:96','Và nếu như dân làng của những xứ sở tin tưởng vào bức thông điệp của Ngài được mang đến từ Thiên Sứ của họ, rồi hạ mình kính sợ Thượng Đế của họ, biết dừng ngay những việc làm chống đối lẫn với những điều tội lỗi và những điều tồi tệ tương tự là TA đã mở cho họ những cánh cửa muôn vạn ân phúc đến từ mọi hướng, nhưng họ chẳng tin tưởng cũng chẳng biết hạ mình kính sợ. Tệ hại hơn, họ đã phủ nhận bức thông điệp của Ngài đến từ Thiên Sứ họ, nên TA túm bắt họ bất ngờ bằng hình phạt bởi lý do tội lỗi mà họ đã làm trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(7,97,'7:97','Há phải chăng, cư dân của một xứ sở phủ nhận đức tin cảm thấy được an toàn khi cơn thịnh nộ của TA túm lấy họ vào màng đêm trong lúc họ đang say sưa trong giấc ngủ thanh thản và bình yên ư ?');
INSERT INTO "vn_mokhtasar" VALUES(7,98,'7:98','Há chăng, bọn họ cảm thấy an toàn khi cơn thịnh nộ của TA bất chợt trừng phạt chúng ở buổi rạng đông lúc họ không hề hay biết gì, lúc họ đang bận rộn với trần gian ?');
INSERT INTO "vn_mokhtasar" VALUES(7,99,'7:99','Hãy xem điều mà Allah đã nhân nhượng với họ, đã ban ân phúc cho họ về sức mạnh, bổng lộc trù phú là để dụ họ lúng sâu hơn trong tội lỗi; há rằng, những kẻ ngỗ nghịch của đám dân cư trong thị trấn sẽ cảm thấy an toàn trước an bài vô hình của Allah ư ? Không, chẳng ai cảm thấy được an toàn trước sự an bài vô hình của Allah ngoại trừ đám người đáng bị tiêu diệt, còn đối với những ai được phù hộ luôn biết kính sợ sự an bài vô hình của Ngài, họ không bị mê hoặc bởi hồng ân mà Ngài đã ban, họ luôn cảm thấy mãn nguyện và tạ ơn Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(7,100,'7:100','Há chẳng phải một bài học quá rõ ràng cho các thế hệ sau này khi tổ tiên của họ bị hủy diệt bởi những tội lỗi mà cha ông họ đã gây ra, vậy mà họ chẳng chịu lấy điều đó mà rút kinh nghiệm. Chính xác hơn đó là một vết xe đỗ giành cho họ. Há chẳng phải quá rõ ràng cho bọn họ ư ? Nếu Allah muốn tiêu diệt bọn họ vì tội lỗi đã làm thì Ngài thừa sức tiêu diệt họ giống như cách mà Ngài thường làm. Và rồi Ngài đã niêm kín tấm lòng của họ khiến họ không có nhận thức được lời khuyên bảo và lời nhắc nhở cũng không ích lợi gì cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(7,101,'7:101','Đó là những câu truyện đã xảy ra - đó là xứ sở của Nuh, Hud, Saleh, Lut, và Shu-''aib - mà chính TA đã kể cho Ngươi nghe - hỡi Thiên Sứ - về sự phủ nhận, sự ngang bướng sẽ có kết cuộc là bị hủy diệt; để lấy đó làm bài học cho những ai biết nghiền ngẫm, là tấm gương cho những ai muốn noi theo. Quả thật, có nhiều Thiên Sứ xuất thân từ họ mang đến những bằng chứng rõ ràng và chính xác về sự thật Họ nói nhưng họ chẳng tin tưởng lời mặc khải mà các Thiên Sứ mang lại giống như Allah đã biết trước là họ khăn khăn phủ nhận nó. Thành thử, Allah đã niêm kín tấm lòng của những kẻ ương ngạnh của các xứ sở đó khiến họ vẫn không tin tưởng Muhammad và không được hướng dẫn đến niềm tin Iman.');
INSERT INTO "vn_mokhtasar" VALUES(7,102,'7:102','Và TA xét thấy hầu hết những cộng đồng điều được TA gửi các Thông Điệp cho họ từ các vị Thiên Sứ lệnh họ phải tuân lệnh và duy trì đúng theo duy huấn của Allah, thế nhưng TA chẳng tìm thấy nơi họ sự phục tùng đó, mà quả thật TA chỉ tìm thấy đa số bọn chúng là chống đối, không tuân thủ theo Allah.');
INSERT INTO "vn_mokhtasar" VALUES(7,103,'7:103','Rồi sau thời đại của họ, TA đã cử Musa mang những dấu hiệu cùng bằng chứng lẫn chứng cớ xác thực đến gặp Fir-''awn và dân chúng của y, thế nhưng chúng lại khước từ và chống đối những điều đó. Bởi thế, hãy nhìn xem - hỡi Thiên Sứ - hậu quả của Fir-''awn cùng đồng bọn của hắn như thế nào. Chẳng phải Allah đã tiêu diệt họ bằng cách nhấn chìm họ và cùng theo họ là lời nguyền rủa của Ngài ở trần gian này và ngày sau.');
INSERT INTO "vn_mokhtasar" VALUES(7,104,'7:104','Musa đến gặp Fir-''awn theo mệnh lệnh của Allah, Người bảo hắn: "Hỡi Fir-awn, quả thật, Ta là Thiên Sứ của Đấng Tạo Hóa ra tất các vạn vật cũng là Đức Vua thánh minh của họ cũng là Đấng cai quản mọi vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(7,105,'7:105','Musa bảo :"Ta được cử đến làm Thiên Sứ cho Ngài, nên Ta quyết không nói bất cứ điều gì ngoại trừ sự thật. Chắc chắn, Ta mang đến cho các ngươi những dấu hiệu quá rõ ràng một bằng chứng xác thực chứng minh Ta được Thượng Đế của Ta cử đến với các người; do đó, hãy để con cháu của Isra-el đi cùng với Ta vì họ đang bị giam cầm và đàn áp."');
INSERT INTO "vn_mokhtasar" VALUES(7,106,'7:106','Fir-''awn đáp lại Musa :"Quả thật, nếu như Ngươi đem đến một dấu hiệu giống như Ngươi nói thì hãy trưng bài nó ra xem nếu đó là sự thật như những gì Ngươi đã hô hào.');
INSERT INTO "vn_mokhtasar" VALUES(7,107,'7:107','Bởi thế, Musa liền ném chiếc gậy của Người xuống thì nó lập tức biến thành một con rắn thật to hiện ra cho mọi người nhìn thấy nó.');
INSERT INTO "vn_mokhtasar" VALUES(7,108,'7:108','Và khi Người rút bàn tay ra từ cổ áo hoặc dưới kẽ nách của Người thì nó hiện ra một màu trắng toát mà đó không phải là do chứng bệnh bạch tạng, lấp lánh trước mặt những người đứng nhìn với cường độ cực trắng của nó.');
INSERT INTO "vn_mokhtasar" VALUES(7,109,'7:109','Các quần thần lẫn những người có chức sắc sau khi chứng kiến chiếc gậy của Musa biến thành con rắn và bàn tay của Người rút ra bổng trở nên trắng toát mà không phải do bệnh bạch tạng chúng liền khẳng định: " Musa đích thực là một phù thủy hùng mạnh, tài ba."');
INSERT INTO "vn_mokhtasar" VALUES(7,110,'7:110','"Y có ý định dùng phép thuật đó để đánh đuổi các khanh ra khỏi lãnh thổ của các khanh, đó là đất nước Ai Cập. Sau đó, Fir-''awn bàn bạc với các quần thần sẽ xử lý Musa, hắn nói với các quần thần: Thế các khanh hãy đưa ra cho trẫm một ý kiến ?"');
INSERT INTO "vn_mokhtasar" VALUES(7,111,'7:111','Chúng tâu với Fir-''awn rằng :"Hãy tạm tha cho Musa và người anh em của Y là Harun, và chúng ta ra thông báo hết các thành phố của Ai Cập để triệu tập các vị pháp sư."');
INSERT INTO "vn_mokhtasar" VALUES(7,112,'7:112','"Rồi sẽ đến trình diện bệ hạ những tên pháp sư mà bệ hạ đã thông báo triệu tập chúng từ các thành phố cùng tất cả những nhà ảo thuật vô cùng tài ba."');
INSERT INTO "vn_mokhtasar" VALUES(7,113,'7:113','Và các vị pháp sư đến trình diện Fir-''awn, họ tâu: "Có phải, liệu chúng thần xứng đáng nếu đánh bại Musa bằng phép thuật của chúng thần và sẽ được ân thưởng hậu hĩnh chăng ?"');
INSERT INTO "vn_mokhtasar" VALUES(7,114,'7:114','Fir-''awn đáp lại đám pháp sư: "Đúng thế, chắc chắn sẽ có cho các ngươi một phần thưởng thỏa mãn và nhất định các ngươi sẽ là những cận thần bên cạnh ta."');
INSERT INTO "vn_mokhtasar" VALUES(7,115,'7:115','Những tên pháp sư đầy tự tin trong chiến thắng trước Musa bằng sự ngạo mạng và chúng to tiếng bảo: Hãy lựa chọn - hỡi Musa - Ngươi muốn là người đầu tiên ném trước bất cứ bửu bối gì để cho bọn ta xem hoặc để bọn ta sẽ là người đầu tiên ném trước ?"');
INSERT INTO "vn_mokhtasar" VALUES(7,116,'7:116','Musa đáp lại họ một cách tự tin đầy lòng chiến thắng trong sự giúp đỡ của Thượng Đế mình không tí e dè: "Hãy ném sợi dây thừng hoặc chiếc gậy của các ngươi xuống và rồi khi họ ném xuống nó liền thu hút những ánh mắt của mọi người bằng cách làm phân tâm mọi người khỏi sự hiểu biết đó lẫn với sự kinh hoàng trước mắt họ một màng ảo thuật tài ba để mọi người chứng kiến."');
INSERT INTO "vn_mokhtasar" VALUES(7,117,'7:117','Allah liền mặc khải cho Musa: "Hãy ném - hỡi Musa - cây gậy của Ngươi xuống, Người liền ném nó lập tức biến thành con rắn nuốt chửng đi sợ dây thừng lẫn cây gậy của bọn họ, thứ mà bọn họ đã phù phép cho nó thành sự thật hồng để lừa gạt mọi người cho việc mưu sinh."');
INSERT INTO "vn_mokhtasar" VALUES(7,118,'7:118','Một điều hiển nhiên quá rõ ràng được mang đến từ Thượng Đế của Musa đã hoàn toàn chiến thắng hai món vật giả mà bọn pháp sư đã tạo ra.');
INSERT INTO "vn_mokhtasar" VALUES(7,119,'7:119','Thế nên bọn chúng là kẻ thua cuộc, Musa là Người chiến thắng trong lần thử tài đó và rồi bọn chúng chuốc lấy sự nhục nhã đáng bị khinh bỉ.');
INSERT INTO "vn_mokhtasar" VALUES(7,120,'7:120','Bởi thế, những tên pháp sư đã chứng kiến được sức mạnh vĩ đại của Allah và nhìn thấy được các dấu hiệu quá rõ ràng nên chúng chỉ biết cuối đầu xuống quỳ lạy Đấng Hiển Vinh và Tối Cao.');
INSERT INTO "vn_mokhtasar" VALUES(7,121,'7:121','Những tên pháp sư đồng thanh nói: "Chúng tôi tin tưởng nơi Thượng Đế đã tạo hóa ra vạn vật."');
INSERT INTO "vn_mokhtasar" VALUES(7,122,'7:122','"Thượng Đế của Musa và Harun, Ngài là Đấng để thờ phượng hơn tất cả thần linh đã được bịa ra."');
INSERT INTO "vn_mokhtasar" VALUES(7,123,'7:123','Fir-''awn lớn tiếng lên giọng đe dọa họ sau khi họ đã tin tưởng nơi Allah duy nhất: "Các ngươi thật to gan dám tin tương nơi Musa trước khi ta cho phép các ngươi hay sao ? Quả thật, đây chỉ là một âm mưu đến bởi Musa để làm cho các ngươi có đức tin và tin tưởng vào Thượng Đế của Y hồng để lừa dối các người để rồi Musa sẽ trục xuất dân cư ra khỏi thành phố. Rồi đây các ngươi sẽ sớm biết - hỡi những tên pháp sư - các ngươi sẽ phải gánh chịu sự trừng phạt và nó sẽ diễn ra với các ngươi."');
INSERT INTO "vn_mokhtasar" VALUES(7,124,'7:124','"Chăc chắn, ta sẽ chặt hết tất cả các ngươi từng người một cánh tay phải và chân trái hoặc cánh tay trái và chân phải. Sau đó, ta treo tất cả các ngươi lên thân cây cọ để làm tấm gương răn đe cho tất cả các ngươi thấy được điều mà chưa được sự đồng ý của ta."');
INSERT INTO "vn_mokhtasar" VALUES(7,125,'7:125','Những tên pháp sư đồng thanh đáp lại lời thị uy của Fir-''awn: "Quả thật, đằng nào chúng tôi cũng trở về với Thượng Đế của chúng tôi Đấng Duy Nhất, thế nên chúng tôi chẳng còn bận tâm những gì bệ hạ đe dọa."');
INSERT INTO "vn_mokhtasar" VALUES(7,126,'7:126','Chẳng lẽ, chỉ vì chúng tôi đã khước từ bệ hạ trong khi chúng tôi tìm được Thượng Đế của chúng tôi hay sao? Hỡi Fir-''awn - hay chỉ vì chúng tôi tin tưởng những dấu hiệu của Thượng Đế của chúng tôi được đến từ tay của Musa. Quả thật, nếu cho đó là cái tội thì chúng tôi gánh chịu tội lỗi đó. Nếu vậy, chúng tôi chỉ biết hướng về Allah để thỉnh cầu van xin Ngài :"Lạy Thượng Đế của bầy tôi, xin Ngài hãy xối lên người bầy tôi sự chịu đựng kiên nhẫn cho đến khi chết là một người Muslim thần phục Ngài và làm theo đường lối của Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(7,127,'7:127','Đám quần thần lẫn những tên đầu sỏ của dân chúng Fir-''awn đồng thanh tâu với Fir-''awn và bọn chúng còn kích động hắn hồng để hãm hại Musa cùng những ai có đức tin với Y: "Há rằng, bệ hạ bỏ mặc - hỡi Fir-''awn - Musa và những ai theo Y để cho họ được lan truyền điều tệ hại trong trần gian này, bỏ mặc bệ hạ và cả thần linh của bệ hạ và để Y kêu gọi đến với việc tôn thờ Allah duy nhất hay sao? Fir-''awn đáp: Ta sẽ giết chết những đứa con trai của Isra-el rồi tha mạng cho những người phụ nữ của họ để giúp việc. Và quả thật, ta đã khống chế chúng bằng cách đàn áp, lấn lướt và cai trị họ."');
INSERT INTO "vn_mokhtasar" VALUES(7,128,'7:128','Musa căn dặn người dân của mình: "Này hỡi dân Ta, hãy van cầu Allah duy nhất che chở tránh khỏi những điều tệ hại, mang lại những đều tốt lành và các ngươi cố mà kiên nhẫn trong tai kiếp thử thách này, bởi vì đất đai này là của Allah duy nhất, chớ không phải của Fir-''awn và cũng không của ai ngoài Ngài, chỉ riêng Ngài mới có thẩm quyền quyết định. Và Allah sẽ định đoạt giữa mọi người cho sự cai quản tùy ý Ngài, nhưng chắc chắn kết quả tốt đẹp trên mặt đất này sẽ thuộc về người có đức tin có đức tin nơi Allah, luôn tuân thủ theo mệnh lệnh của Ngài, đó là của họ cho dù họ có phải đối diện những điều khó nhọc và đầy sự thử thách."');
INSERT INTO "vn_mokhtasar" VALUES(7,129,'7:129','Người dân của Musa, họ là con cháu của Isra-el đáp lại Musa: Thưa Musa, chúng tôi bị tai kiếp bởi Fir-''awn khi hắn đã giết chết tất cả những người con trai mà chỉ để lại những người phụ nữ của chúng tôi thôi, sự việc xảy ra từ trước và sau khi Thầy đến với chúng tôi". Musa an ủi họ và cho họ biết tin vui sắp diễn ra: "Có lẽ Thượng Đế của các ngươi sẽ tiêu diệt kẻ thù của ngươi là Fir-''awn lẫn bè lũ của hắn rồi cho các ngươi nối nghiệp họ trên trái đất này. Bởi thế, Ngài luôn quan sát hành động cử chỉ của các ngươi sau khi đã thay thế họ xem xem các ngươi biết tạ ơn hay vong ân."');
INSERT INTO "vn_mokhtasar" VALUES(7,130,'7:130','Quả thật, Ngài đã trừng phạt đám thuộc hạ của Fir-''awn qua những trận hạn hán khô cằn và Ngài còn cho họ thấy được sự thất mùa trên mảnh đất khô cằn đầy thất thu; để cho họ có thể tỉnh ngộ mà nhận ra rằng điều đó là sự cảnh cáo cho việc làm chống đối của chúng để mai ra họ sớm trở lại sám hối với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(7,131,'7:131','Nếu như những gì tốt lành đến với quần thần của Fir-''awn như là được mùa bội thu hay trúng giá thì chúng liền nói: "Chúng tôi xứng đáng được phần thưởng này vì đó cũng là năng lực của chúng tôi và rồi khi chúng gặp một nạn kiếp nào hay điều bất hạnh đến từ thiên tai hạn hán hoặc trận dịch tả hay còn hơn thế nữa thì họ liền đổ tội cho Musa và con cháu của Isra-el đem lại. Nhưng tất cả những tai ương mà chúng đang phải gánh chịu đó là do Allah Hiển Vinh đã an bài cho chúng, chứ chẳng phải do chúng cũng không phải do Musa muốn ngọai trừ những gì Musa cầu xin điều đó cho chúng, thế nhưng đa số chúng không ngộ nhận ra, cho nên chúng tự cho là do một thần lình nào đó mà không phải là Allah.');
INSERT INTO "vn_mokhtasar" VALUES(7,132,'7:132','Đám thuộc hạ của Fir-''awn bảo với Musa rằng chúng quyết cự tuyệt chân lý đến với họ: "Bất kì, dấu hiệu lạ hay bằng chứng nào được mang đến cho bọn ta hay bất cứ chứng cớ gì có giá trị quan trọng mang đế hồng dụ dỗ hay làm mê hoặc hầu trục lợi từ bọn ta làm cho bọn ta tin tưởng những điều đó đến từ Thượng Đế của Y thì chắc chắn, bọn ta không bao giờ tin Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(7,133,'7:133','Rồi TA giáng xuống cho bọn họ một trận lũ lụt nhằm để cảnh cáo bọn họ cho việc cứng đầu và ngang bướng của họ bằng cách TA nhấn chìm hết những mảnh vườn lẫn hoa màu, nạn cào cào tàn phá mùa màng, nạn ký sinh trùng với những con chấy rận lây nhiễm cho nhau hoặc làm tổn thương ở da đầu vùng lông tóc, nạn ếch nhái đầy dẫy khắp nơi làm cho họ cảm thấy bất an mất ăn mất ngủ kế tiếp là TA xối lên chúng một trận máu tươi nó làm cho tất cả các giếng nước những con sông thành những giọt máu tươi. Tất cả những gì TA giáng lên chúng đó là một số dấu hiệu này nối tiếp một số dấu hiệu kia một cách quá rõ ràng, còn đi kèm theo những cảnh cáo để làm cho chúng chợt nhận ra rồi đặt niềm tin nơi Allah và tin tưởng vào những gì đến từ Thượng Đế của Y Musa. Thế nhưng, chúng cứ lẩn vẩn vào tội lỗi, không thể nào thoát khỏi những điều tệ hại và chúng cũng không chịu sự chỉ dẫn đến với chính đạo.');
INSERT INTO "vn_mokhtasar" VALUES(7,134,'7:134','Và cứ thế, mỗi khi có trận thiên tai nào nhằm để cảnh cáo bọn họ, thì bọn họ chỉ biết nài nỉ Musa, họ nói: "Hỡi Musa, hãy cầu xin Thượng Đế của Ngươi giùm bọn ta bởi gì Ngươi là một Thiên Sứ của Ngài, bởi vì Ngài cũng từng hứa với Ngươi về việc kiếp nạn mang theo lời cảnh cáo nếu bọn ta biết ăn năn sám hối thì mai ra kiếp nạn đó sẽ được hóa giải. Quả thật, nếu những kiếp nạn đó được hóa giải thì chắc chắn bọn ta sẽ tin tưởng nơi Người và rồi bọn ta sẽ để con cháu của Isra-el ra đi cùng với Ngươi và bọn ta không giữ bọn họ nữa."');
INSERT INTO "vn_mokhtasar" VALUES(7,135,'7:135','Nhưng khi TA đã hóa giải hết những thiên tai kiếp nạn cho họ, theo hạn kì được ấn định trước khi cơn lũ lụt đến nhấn chìm bọn họ, thì tức khắc bọn họ nuốt lời với những gì mà chính bản thân bọn họ đã từng hứa là sẽ tin tưởng và còn sẽ thả con cháu của Isra-el. Chưa hết bọn chúng còn tỏ ra ý định chống đối và bắt giữ con cháu của Isra-el lại không cho đi với Musa.');
INSERT INTO "vn_mokhtasar" VALUES(7,136,'7:136','Bởi thế, khi đến kỳ hạn được ấn định tiêu diệt họ thì TA liền giáng lên bọn họ kiếp nạn bằng cách nhấn chìm bọn họ dưới đái biển hồng hải với lý do bọn chúng dám chống đối những dấu hiệu của Allah và phủ nhận đi những bằng chứng chính xác của Ngài mà điều đó không cần phải tranh luận.');
INSERT INTO "vn_mokhtasar" VALUES(7,137,'7:137','Và TA đã chỉ định con cháu của Isra-el làm người thừa kế Fir-''awn và thuộc hạ của hắn trên những phần đất nằm ở phía đông và những phần đất nằm phía tây, được gọi là phần đất Shaam hiện là những nước: Syria, Li-băng, Jordan, Palestine ngày nay, đó là những đất nước được Allah ban cho nhiều hồng phúc và làm cho cây cối hoa màu đâm chồi nảy lộc tốt tươi. Giữ đúng như lời đã hứa Thượng Đế Ngươi - hỡi Thiên Sứ - rồi lời hứa tốt đẹp mà được Ngài phán: {Và TA muốn ban ân cho những người cô thế trong xứ, và làm cho họ thành những lãnh đạo trên trái đất} (Chương - Al-Qasas: 5). Bởi vậy, Allah đã hoàn tất cho họ với lý do là vì họ đã chịu đựng cực khổ dưới sự hành hạ của Fir-''awn và những tên thuộc hạ của hắn, và rồi TA cũng đã san bằng hết biết bao nhiêu là công trình đồ sộ lẫn những cung điện nguy nga tráng lệ của Fir-''awn mà hắn dùng để làm trang trại và nhà ở với những gì chúng đã xây dựng.');
INSERT INTO "vn_mokhtasar" VALUES(7,138,'7:138','Và TA đã đưa con cháu của Isra-el băng qua biển hồng hải an toàn bằng quyền năng của TA lệnh cho Musa dùng cây gậy đánh xuống biển thì tức khắc biển nó chẻ đôi. Rồi khi họ đi ngang qua một ngôi làng bổng nhiên họ bắt gặp một đám người đang thờ phượng một số bức tượng thay vì thờ Allah. Thấy thế, con cháu của Isra-el thưa với Musa: "Hỡi Musa, xin Thầy hãy tạo cho chúng tôi một thần linh tương tự bọn họ đang thờ phượng ngoài Allah". Musa đáp lại chúng: "Này hỡi dân Ta, quả thật, các ngươi đúng là những người ngu ngóc chẳng hiểu biết gì gọi là sùng bái Allah và độc tôn Ngài trong thờ phượng, và những điều không phù hợp như Shirk thờ phượng ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(7,139,'7:139','Quả thật, TA sẽ tiêu diệt đám dân làng đang sùng bái những bức tượng thay vì thờ phượng TA và rồi tất cả mọi việc làm của chúng sẽ trở nên vô ích; bởi vì, họ đã tổ họp những thần linh khác cùng với Allah trong việc thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(7,140,'7:140','Musa đáp lại dân chúng của Y: "Này hỡi dân Ta, há phải chăng, các người yêu cầu Ta chỉ định một thần linh nào đó để thờ phượng mà chẳng phải là Allah hay sao, thật bất hạnh thay cho các ngươi vì đã chứng kiến được những dấu hiệu vĩ đại của Ngài thế mà các ngươi chưa tin vào nó. Thật vinh quang thay Ngài Đấng Tối Cao, Đấng đã ban đặc ân cho các ngươi nhiều hơn thiên hạ, và còn được sự bảo vệ của Ngài trong lúc các ngươi đang bị kẻ thù tiêu diệt, rồi còn được Ngài chỉ định làm người kế nhiệm trái đất này và cũng chẳng phải các ngươi được toàn quyền thống trị nó hay sao ?');
INSERT INTO "vn_mokhtasar" VALUES(7,141,'7:141','Và này hãy nhớ lại đi - hỡi con cháu của Isra-el - trong lúc, TA giải cứu các ngươi trong cực hình của Fir-''awn và đám thuộc hạ chúng đang hành hạ các ngươi. Vì bọn chúng dùng mọi cực hình man rợ để tra tấn các ngươi từ việc giết hại những đứa con trai của các ngươi, nhưng may thay cho những người phụ nữ của các ngươi được chúng tha mạng. Bởi vậy, trong việc TA giải cứu các ngươi từ đám người Fir-''awn và đám thuộc hạ của chúng, thật sự ra đó chỉ là một sự thử thách đầy gian nan từ Thượng Đế của các ngươi, thế nên các ngươi hãy tạ ơn Ngài thật nhiều.');
INSERT INTO "vn_mokhtasar" VALUES(7,142,'7:142','Và Allah đã hẹn gặp Thiên Sứ của Ngài là Musa để trình diện Ngài ba mươi đêm và sau đó Allah kéo dài thêm mười đêm nữa, thế nên cuộc trình diện đó kéo dài đến bốn mươi đêm. Thế nên, Musa căn dặn người anh của mình là Harun trước khi Y lên đường để trình diện Thượng Đế của Y: "Này hỡi Harun, anh hãy làm người đại diện cho Ta để trông coi dân chúng, mà hãy quản lý họ cho thật tốt và cư xử họ một cách hòa nhã, chớ nên làm theo đường lối của những kẻ thối nát đê hèn để rồi chuốt tội lỗi vào người và chớ nên nghe lời phân bua của những kẻ đốn mạt."');
INSERT INTO "vn_mokhtasar" VALUES(7,143,'7:143','Và trong khi Musa đến điểm hẹn để trình diện Thượng Đế của Y đúng như thời hạn đã giao ước mà nó đã kéo dài hết bốn mươi đêm, đó là khoảng thời gian Thượng Đế đã nói chuyện với Người bao gồm sắc lệnh, điều cấm kỵ và những đều tương tự thế. Sau đó, Người khao khát được tận mắt thấy được Thượng Đế nên Người đã cầu xin một cách tha thiết mong Ngài hiện diện để được nhìn thấy. Allah đáp: "Ngươi không bao giờ nhìn thấy TA trong cuộc sống trần gian này, vì quả thật Ngươi không có khả năng đó, nhưng Ngươi hãy nhìn về ngọn núi kia nếu như Ánh Hào Quang của TA xuất hiện mà nó còn nguyên không có gì biến đổi thì Ngươi hả nhìn thấy TA trực tiếp, còn nếu như TA xuất hiện mà ngọn núi kia nó tan chảy bằng phẳng như mặt đất thì Ngươi chớ nên ao ước nhìn thấy TA ở trần gian này". Thế nên, khi Allah thể hiện thiên uy phát ra Ánh Hào Quang thì ngọn núi kia lập tức tan chảy ra thành cát bụi lan tỏa khắp nơi trên mặt đất, thấy thế Musa liền té xuống bất tỉnh trước thiên uy của Ngài và rồi một lát sau Musa tỉnh lại và thưa: "Bề tôi xin thề trước Ngài - lạy Thượng Đế - quả thật bề tôi quyết tẩy sạch Ngài khỏi mọi thứ không phù hợp với Ngài, bề tôi thật sự hối hận trước Ngài về điều mà bề tôi đã khẩn cầu để được nhìn thấy Ngài ở trên trần gian này và rồi bề tôi sẽ là Người đầu tiên tin tưởng Ngài một cách tuyệt đối."');
INSERT INTO "vn_mokhtasar" VALUES(7,144,'7:144','Allah phán xuống cho Musa: "Này hỡi Musa, quả thật, TA đã lựa chọn Ngươi là một người tốt đẹp hơn mọi người bằng những bức thông điệp của TA gửi xuống cho Ngươi để truyền đạt lại cho họ và Ngươi còn được sự ân điển là nhận được lời phán của TA một cách trực tiếp. Bởi thế, hãy nhận lấy những ân điển cao quý và vinh dự đó và rồi hãy tỏ lòng biết ơn Allah thật nhiều vì nhận được những ân điển vĩ đại đó."');
INSERT INTO "vn_mokhtasar" VALUES(7,145,'7:145','Và TA đã ghi chép cho Musa lên những tấm bảng từ những miếng ván hay những thứ tương tự thế với tất cả mọi đều cần thiết cho Người để lấy đó mà làm cơ sở răn dạy con cháu của Isra-el trong tôn giáo, cũng là lời răn đe ở trần gian này cho những ai trong bọn họ biết giác ngộ, nó là một giáo luật quan trọng giành cho Người." Bởi thế, hãy nắm giữa Kinh Sách Tawrah - Cựu Ước - này hỡi Musa - cho thật thận trọng và lấy đó làm chỉ thị cho dân chúng của Ngươi đám con cháu của Isra-el và hãy gìn giữ cho thật kỹ những điều cao quý của TA từ những hành động, lời phán mà đã tích lũy được giống như sự kiên nhẫn và lòng vị tha. TA sẽ cho các ngươi nhìn thấy được sự trừng phạt của những kẻ cố chấp với lời phán của TA, luôn có hành động chông đối TA và hãy xem TA trừng trị và tiêu diệt."');
INSERT INTO "vn_mokhtasar" VALUES(7,146,'7:146','TA sẽ khiến cho những kẻ ngông cuồng lẫn những kẻ kiêu căng tự đại tránh xa các dấu hiệu của TA ra khỏi tâm hồn họ và sự hiểu biết về Kinh Sách của TA; những kẻ tỏ ra cao ngạo với đám bầy tôi của TA một cách vô cớ. Đối với họ cho dù có nhìn thấy các dấu hiệu của Allah thì họ chẳng bao giờ tin tưởng vào đó; họ còn phản đối và kháng cự nó, thách đố Allah và Thiên Sứ của Ngài, còn khi nhận được con đường đúng đắn chính đạo làm cho hài lòng Allah thì họ không chịu nắm lấy mà lại khước từ và khi họ thấy được con đường lầm lạc đem họ đến sự thịnh nộ của Allah thì họ nắm chặt, thật bất hạnh thay cho họ, bởi vậy sự bất hạnh đó đã làm cho họ phủ nhận đi những dấu hiệu vĩ đại của Allah, một bằng chứng xác thực mà được chính Thiên Sứ của Ngài đem đến, thế nhưng họ vẫn còn lơ là không để tâm gì đến nó.');
INSERT INTO "vn_mokhtasar" VALUES(7,147,'7:147','Và những kẻ nào phủ nhận đi các dấu hiệu của TA là chứng cớ xác thực được đem đến từ Thiên Sứ của TA cùng với việc khước từ đi cuộc hội ngộ Allah vào Đời Sau thì những việc làm của họ là vô nghĩa, bởi một việc làm ô uế bám lấy họ. Bởi thế, chẳng có ân phước nào giành cho họ ngoại việc họ chẳng có đức tin và cũng chẳng có phần thưởng nào giành cho họ vào ngày phán xét ngoài những việc làm bất tuân Allah và tổ hợp Ngài mà phần thưởng giành cho họ chính là ở trong Hỏa Ngục đời đời.');
INSERT INTO "vn_mokhtasar" VALUES(7,148,'7:148','Thế rồi, người dân của Musa chờ sau khi Người đi đến trình diện với Thượng Đế thì họ liền dùng trang sức đúc thành một con nghé, tuy nó không có linh hồn nhưng nó phát ra được tiếng rống. Há chúng không nhận ra rằng con bò đó không thể nói chuyện với chúng hay hướng dẫn chúng đến với con đường tốt đẹp hoặc con đường chân chính và cũng không mang lại điều hữu ích hoặc mang tại hại gì đến với họ hay sao ? Thế nhưng, chúng lại tôn thờ nó và trở thành những kẻ làm điều sai quấy lên chính bản thân chúng về điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(7,149,'7:149','Và khi họ tỉnh ngộ về việc làm sai trái, thế nên họ tỏ ra nuối tiếc gì đã lầm lạc trên con đường chân chính cho nên chúng lập tức thờ phượng Allah và van cầu nơi Allah, bọn họ cầu nguyện :" Nếu như Thượng Đế của bầy tôi không có lòng thương xót chấp nhận cho bầy tôi hướng về Ngài và tha thứ cho đám bầy tôi về những việc làm tệ hại đã qua là đi thờ phượng một con bê con thì chắc chắn bầy tôi là đám người thua thiệt ở trần gian này và lẫn Ngày Sau."');
INSERT INTO "vn_mokhtasar" VALUES(7,150,'7:150','Và sau khi trình diện với Thượng Đế Musa trở về với người dân của mình thì Y vô cùng giận dữ và buồn bã vì nhìn thấy dân chúng đang thờ phượng một con bê con, Y quát to: "Thật là điên rồ, các ngươi đã làm trái lời Ta - này đám dân của Ta - sau khi Ta đi vắng; phải chăng, các ngươi muốn Allah tiêu diệt và trừng trị các ngươi, trong khi Ta kêu hãy chờ đợi Ta, nào ngờ các ngươi lại hấp tấp đi thờ phượng một con bê con? Thế nên, Người ném thật mạnh những tấm Bia Ký trong cơn giận dữ và đau buồn rồi túm lấy chùm tóc của anh trai Harun luôn cả chùm râu rồi kéo lại gần mình cùng với mọi người và hỏi anh trông coi họ như thế nào mà để cho họ đi tôn thờ con bê, Harun thưa: "Thật sự xin lỗi em Musa để cho sự việc thành ra như thế này đây: Hỡi người con của mẹ ta, quả thật, họ đã lấn lướt xem thường Ta và còn hâm dọa sẽ giết chết Ta, do đó em đừng trừng phạt anh mà để cho kẻ thù đắc ý trên sự đau khổ của anh và cũng đừng vì cơn tức giận mà biến anh trở thành một đám kẻ thù sai quấy bởi vì họ đã tôn thờ một thần linh nào đó mà không phải là Allah."');
INSERT INTO "vn_mokhtasar" VALUES(7,151,'7:151','Bởi thế, Musa cầu nguyện Thượng Đế của mình: "Lạy Thượng Đế của bề tôi, xin Ngài hãy tha thứ cho bề tôi và anh trai của bề tôi là Harun, hãy đưa bầy tôi vào lòng nhân từ của Ngài và hãy bao chùm lấy bầy tôi tất cả mọi hướng - lạy Thượng Đế của bầy tôi - hãy thương xót bầy tôi với tất cả lòng thương xót của Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(7,152,'7:152','Quả thật, cho những ai lấy con bê con để mà tôn thờ, họ sẽ bị Thượng Đế của họ nỗi cơn thịnh nộ và còn bị hạ nhục ở trần gian này trong cơn thịnh nộ của Thượng Đế của họ và còn bị khinh khi, và với sự trừng phạt như thế giành cho những kẻ nào dám đặt điều dối trá với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(7,153,'7:153','Ngược lại, những ai làm điều tội lỗi vì từng tổ hợp với Allah đó là một việc làm đầy tội lỗi, rồi sau đó biết ăn năn sám hối với Allah và tin tưởng vào Ngài, sẽ không tái phạm tội lỗi đó nữa. Quả thật, Thượng Đế của Ngươi - hỡi Thiên Sứ - sau khi ăn năn sám hối trở về từ việc tổ hợp với Ngài nay trở thành người có đức tin, từ sự chống đối nay trở thành người tuân theo thì chắc chắn Ngài sẽ tha thứ bao chùm lấy họ và khoan dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(7,154,'7:154','Và khi nguôi giận Musa lượm lại các tấm Bia Ký mà đã ném trong cơn tức giận và trong tấm Bia đó có chứa đựng chỉ đạo nhận định những điều lầm lạc và xác thực con đường chính đạo và nó cũng chứa đựng những hồng ân cho những ai biết kính sợ Ngài và khiếp sợ trước sự trừng phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(7,155,'7:155','Và Musa đã lựa chọn bảy mươi người đàn ông từ người dân của mình để đến nhận lỗi với Thượng Đế của họ cho việc làm thối nát đã gây ra cho việc đi tôn thờ một con bê, thế nên mọi người cùng đi đến điểm hẹn của Allah để trình diện Ngài. Đến nơi họ tỏ thái độ xấc xượt và yêu cầu Musa cho được tận mắt nhìn thấy Allah. Bởi thế, Ngài đã cảnh cáo họ bằng một trận động đất dữ dội lên họ làm cho họ có một phen kinh hoàng khiếp vía, thế rồi Musa liền khấn cầu đến Thượng Đế của Y: "Lạy Thượng Đế, nếu Ngài muốn tiêu diệt họ và bề tôi cùng với nhau thì Ngài đã tiêu diệt bầy tôi trước khi đến trình diện Ngài. Hay phải chăng, Ngài tiêu diệt bầy tôi chỉ vì có một người trong bầy tôi đã làm những điều ngu xuẩn chăng? Chẳng qua việc đi thờ phượng một con bê chỉ là một sự thử thách của Ngài nhằm để đánh lạc lối người nào mà Ngài muốn và chỉ dẫn người nào tùy ý Ngài. Bởi vì, Ngài là Đấng quản lý mọi việc của bầy tôi, xin hãy tha thứ tội lỗi cho bầy tôi, hãy nhân từ với bầy tôi bằng sự nhân từ bao la của Ngài. Bởi vì Ngài là Đấng Hằng Tha Thứ tội lỗi cũng là Đấng Ưu Việt.');
INSERT INTO "vn_mokhtasar" VALUES(7,156,'7:156','"Và xin Ngài hãy thu nhận bầy tôi vào những người cao quý ở trên trần gian này bằng những phước lành, sức khỏe dồi dào mà Ngài đã chấp nhận những việc làm ngoan đạo của họ, thuộc nhóm người được Ngài chuẩn bị cho Thiên Đàng, bầy tôi xin ăn năn về những gì bầy tôi chưa hoàn thành trong việc hành đạo. Allah Tối Cao phán: "TA sẽ dùng hình phạt của TA trừng trị kẻ nào TA muốn bởi việc làm thối nát của họ trong khi lòng khoan dung của TA bao trùm tất cả mọi thứ trên trần gian này, không một sinh vật nào mà không được lòng khoan dung của TA trùm lấy y. Và TA sẽ quy định lòng thương xót của TA cho ngày sau đối với ai có lòng kính sợ Allah mà tuân thủ theo mệnh lệnh của Ngài, những ai xuất Zakah đúng quy định và những ai tin tưởng các dấu hiệu của TA."');
INSERT INTO "vn_mokhtasar" VALUES(7,157,'7:157','Những ai tuân theo Muhammad, một vị Thiên Sứ mù chữ không biết đọc, không biết viết, chẳng qua Người được thiên khải từ Thượng Đế mình. Tên và các đặc điểm về Người được nhắc trong Kinh Tawrah được ban cho Musa, được nhắc trong Injil được ban cho Ysa, Người ra lệnh họ tuân thủ theo mọi điều mang lại tốt đẹp cho họ, Người cấm cản họ làm những điều gây hại đến phẩm chất con người và trí tuệ, Người cho phép họ hưởng thụ mọi thứ vô hại từ thức ăn, thức uống và cưới hỏi, Người cấm cản họ hưởng thụ những thứ có hại, Người xóa những quy định đè nặng họ trước đây điển hình như bắt buộc tử hình kẻ giết người dù là có chủ định hay không. Những ai thuộc dòng tộc Isra-el hoặc ai khác biết tôn trọng Người, chấp nhận Người, ủng hộ Người chống lại nhóm vô đức tin, biết tuân theo Qur''an được thiên khải cho Người tựa như ánh sáng chỉ đạo, đó là nhóm người thành đạt đã chiến thắng được điều họ muốn và được bảo vệ tránh khỏi mọi điều họ không ưa thích.');
INSERT INTO "vn_mokhtasar" VALUES(7,158,'7:158','Hãy bảo họ - hỡi Thiên Sứ -: Này hỡi nhân loại, quả thật, Ta là Thiên Sứ của Allah được cử phái đến cho tất cả nhân loại, đến với dân Ả-rập và không phải là Ả-rập; Ngài là Đấng duy nhất sở hữu các tầng trời và trái đất, không có Thượng Đế nào đích thực ngoài Ngài, Đấng Hiển Vinh, Ngài ban cho sự sống và gây ra cái chết. Hãy tin nơi Ngài - hỡi loài người -, hãy tin tưởng Muhammad vị Thiên Sứ mù chữ của Ngài, những gì Người mang đến chính là lời mặc khải từ Thượng Đế của Người. Cho nên, ai tin tưởng Allah, tin tưởng vào những gì đã ban xuống cho Người, cho những vị Nabi trước Người không có lòng kỳ thị chia rẻ và luôn tuân theo những gì Người mang đến từ Thượng Đế của mình, mai ra các ngươi sẽ là những người được chỉ dẫn đến với con đường đúng đắn trên trần gian này và lẫn ngày sau.');
INSERT INTO "vn_mokhtasar" VALUES(7,159,'7:159','Và trong đám dân chúng của Musa thuộc bộ tộc Isra-el, có nhóm người đi đúng Chính Đạo trên tôn giáo chính thống, họ hướng dẫn nhân loại đến với nó và luôn phân xử công bằng không hề bất công.');
INSERT INTO "vn_mokhtasar" VALUES(7,160,'7:160','Và TA đã phân chia con cháu của Isra-el ra làm mười hai bộ tộc. Và TA đã mặc khải cho Musa khi nào người dân của Ngươi đến nài nỉ Ngươi cầu xin Allah cung cấp cho họ nguồn nước uống, thì hãy làm như sau: "Hãy lấy cây gậy của Ngươi đánh lên tản đá". Thế là Musa đánh lên thì lập tức nó phun ra mười hai nguồn nước tương ứng với mười hai bộ lạc, mỗi một bộ tộc họ tự biết đâu là nguồn nước uống của họ bằng cách của riêng mình, không bộ lạc nào uống cùng bộ lạc nào cả. Rồi TA lấy mây che bóng mát trên đầu dù họ có đi đâu, đứng đâu, TA còn ân điển cho chúng loại thức uống ngọt ngào như mật ong, ăn thịt chim cút, kèm theo lời phán: "Hãy ăn những thực phẩm tốt đẹp mà được TA cung cấp cho các ngươi, dù các ngươi có phủ nhận hết tất cả ân điển đó thì cũng không gây hại gì đến TA cả, điều đó chỉ khiến các ngươi bị tiêu diệt vì đã nghịch lệnh của Allah."');
INSERT INTO "vn_mokhtasar" VALUES(7,161,'7:161','Và hãy nhớ - hỡi Thiên Sứ - lời phán của Allah cho con cháu của Isra-el: Các ngươi hãy vào ngôi đền Masjid Al-Aqsa, hãy ăn các loại trái cây ở xứ xở đó bất cứ nơi nào, bất cứ lúc nào mà các ngươi muốn, và hãy cầu xin: "Lạy Thượng Đế của bầy tôi, xin Ngài hãy tha thứ tội lỗi cho đám bầy tôi và khi bước vào cửa với dáng vẻ khép nép cuối đầu một cách thật kính trọng trước Thượng Đế của các ngươi". Có thế những việc làm của các ngươi sẽ được xóa tội lỗi và tăng lên thêm nhiều ân phước tốt đẹp cho các ngươi ở đời này lẫn đời sau.');
INSERT INTO "vn_mokhtasar" VALUES(7,162,'7:162','Thế nhưng những kẻ làm điều sai quấy chúng đã thay đổi lời nói của Ngài bằng lời nói sàm bậy khác, họ nói: "Hạt lúa mạch được họ thay thế từ câu cầu xin sự tha thứ, họ thay đổi luôn hành động thay vì phải bước vào khúm núm cúi đầu kính phục Allah thì họ lại xoay lưng đi vào. Bởi thế, TA đã giáng xuống hình phạt từ trên trời xuống cho chúng bởi vì chúng là những kẻ sai quấy bất công.');
INSERT INTO "vn_mokhtasar" VALUES(7,163,'7:163','Và hãy hỏi - hỡi Thiên Sứ - nhóm Do Thái về câu chuyện của xứ sở nằm dọc bờ biển ở thời trước họ đã bị Allah trừng phạt vì họ đã vượt quá giới hạn của Allah vì việc săn bắt cá vào ngày thứ bảy sau khi chúng bị nghiêm cấm săn bắt cá vào ngày đó, ngặt nổi khi đúng vào ngày đó thì cá lại nhô lên mặt biển đến gần họ còn vào những ngày khác thì đàn cá không thấy tăm hơi đâu cả, Allah thử thách họ thế bởi họ đã vượt giới luật của Allah, không biết tuân lệnh Ngài. Thế là họ đã đặt lợp cá vào ngày thứ sau để thứ bảy cá vào lợp và ngày chủ nhật sau đó thì họ ra lấy lợp về.');
INSERT INTO "vn_mokhtasar" VALUES(7,164,'7:164','Và hãy nhớ lại - hỡi Thiên Sứ - trong số họ có nhóm người cản mọi người làm tội lỗi và cảnh báo họ về mối hiểm họa đó thì họ bị nhóm người khác chỉ trích nói: "Tại sao quý vị còn đi khuyên can cho một đám người mà Allah muốn tiêu diệt chúng trên trần gian này bởi việc làm tệ hại mang đầy tội lỗi hoặc sẽ bị Ngài trừng trị bằng một hình phạt thật khủng khiếp vào ngày sau ? Nhóm người khuyên bảo nói: Chúng tôi khuyên họ để khỏi bị Allah bắt tội không chịu ra lệnh làm điều tốt và cấm làm điều xấu, biết đâu họ sẽ nghe theo lời khuyên đó mà từ bỏ điều xấu đó.');
INSERT INTO "vn_mokhtasar" VALUES(7,165,'7:165','Bởi khi những việc làm tội lỗi quá lỗ liễu thì chúng được những người biết kính sợ nhắc nhở cho việc làm tệ hại đó, vậy mà chúng chưa chịu dừng lại. Thế nên, TA đã cứu vớt những người đã từng ngăn cản chúng làm những điều xấu xa đầy tội lỗi và rồi TA đã trừng trị những kẽ làm điều sai quấy cố tình cho việc săn bắn cá vào ngày thứ bảy bằng một hình phạt thật khủng khiếp bởi vì chúng đã bất tuân Allah mà chúng cố tình cho việc làm đầy tội lỗi đó.');
INSERT INTO "vn_mokhtasar" VALUES(7,166,'7:166','Bởi thế, khi tội lỗi vượt quá giới hạn của Allah vì sự tự cao, ngạo mạng và chẳng chịu nghe lời can ngăn, thế nên Ngài phán với chúng :" Này hỡi những kẽ mang đầy tội lỗi! Hãy trở thành một loài khỉ thấp hèn nếu như TA muốn chúng như thế." Quả thật, những lời phán của TA nếu như TA muốn nó thành thì TA chỉ cần phán : " Hãy thành thì lập tức Nó sẽ thành."');
INSERT INTO "vn_mokhtasar" VALUES(7,167,'7:167','Và hãy nhớ - hỡi Thiên Sứ - Allah là Đấng Rất Mực Sáng Suốt cho một lời phán quá rõ ràng không có một chút nhầm lẫn nào đối với người Do thái về sự nhục mạ hay lăng nhục chúng ở trần gian này cho đến ngày tận thế. Quả thật, Thượng Đế của Ngươi - hỡi Thiên Sứ - Ngài rất nhanh chóng cho việc xét xử đối với những kẽ mang đầy tội lỗi và chắn chắn Ngài sẽ trừng trị bọn chúng ngay trên trần gian này. Quả thật, Ngài luôn sẵn lòng Tha Thứ cho bề tôi của Ngài biết hối cải và Ngài cũng hết Mực Nhân Từ với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(7,168,'7:168','Và TA phân chia rải rác họ khắp trên mặt đất thành nhiều cộng đồng, cho đến khi chúng tập hợp thành một nhóm đông rồi có một số người trong bọn họ là những người ngoan đạo chân chính với Allah trong việc thờ phượng đúng đắn và cũng có một số người không được như thế, do chính chúng tự gây ra cho bản thân mình những điều tội lỗi. Và rồi, TA cũng đã cảnh báo họ trong sự dễ dãi và có sự nghiêm khắt, để may ra họ biết để quay về với con đường chính đạo của TA.');
INSERT INTO "vn_mokhtasar" VALUES(7,169,'7:169','Và rồi đến sau thời đại của họ là một đám dân ô hợp bạo hành ngông cuồng thay thế họ, chúng nắm lấy Kinh Sách Tawrah (Cựu Ước) từ tổ tiên của họ làm cơ sở để làm chuẩn mực, thế nhưng chúng chẳng bao giờ làm theo chỉ dẫn đó. Chúng chỉ ham hố sự phù phiếm ngắn ngủi của trần gian này thế rồi chúng đã bóp méo đi Kinh Sách của Allah, chúng tự tạo một giáo luật mà không hề được đưa xuống, thế vậy chúng còn tỏ tự tin rằng xin Allah sẽ tha thứ tội lỗi cho chúng và rồi nếu như có những điều phù phiếm rẻ mạc ở trần gian này đến với chúng thì chúng sẽ nắm bắt ngay hết lần này tới lần khác. Há ! chẳng phải, Allah đã đưa ra một lời giao ước với họ hay sao? Một lời giao ước là chúng sẽ không được đặt điều vu khống Allah trừ phi đó là chân lý mà không bị bóp méo hoặc chỉnh sửa. Bởi vì, chẳng có một ai có thể chấp nhận được một việc làm đến từ Kinh Sách mà lại có sự điên rồ đến thế, trong khi chúng lại nắm rõ và thấu hiểu được nó, thế nên chúng đáng bị trừng trị. Nói về ngôi nhà của Ngày Sau và những gì cho cuộc sống ngày sau thật sự nó ngập tràng hạnh phúc vĩnh cửu cùng với những gì tốt đẹp nhất mà Ngài giành cho những ai biết kính sợ Allah bằng việc tuân lệnh Ngài và không làm trái ý Ngài."Há ! Chẳng phải có một điều quá phi lý cho những kẽ ham hố sự phù phiếm mà những gì Thiên Đàng Allah chuẩn bị cho những người biết kính sợ Ngài trong sự tốt đẹp vĩnh cửu ở ngày sau ư ?"');
INSERT INTO "vn_mokhtasar" VALUES(7,170,'7:170','Và những ai nắm giữ Kinh Sách lấy nó làm chuẩn mực và chu đáo dâng lễ nguyện Salah một cách hăng hái đúng giờ ấn định, tuân thủ hết những điều kiện, những điều bắt buột lẫn những điều Sunah trong sự cho phép của Allah cho việc làm đó. Chắc chắn, Allah sẽ không làm mất đi phần thưởng của những ai luôn làm điều ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(7,171,'7:171','Và hãy nhớ lại - hỡi Muhammad - khi TA nhấc ngọn núi lên cao khỏi đầu con cháu của Israel bởi gì chúng đã khước từ đi Kinh Sách Tawrah, ngọn núi nó lơ lững như một đám mây che mát trên đầu chúng và chúng tưởng chừng như sắp rơi xuống; rồi TA phán bảo chúng :"Hãy nắm lấy cho thật chặt những gì mà TA đã Thiên Khải cho các ngươi bằng sự năng nổ, hăng hái, nhiệt quyết và cũng hãy nhớ cho thật kỹ những gì được ghi chép trong nó từ giáo lý vì đó là một bộ giáo luật mà Allah ban cho các ngươi và chớ sao lãng nó, mai ra nó sẽ làm cho các ngươi biết kính sợ Allah nếu các ngươi là người ngay chính."');
INSERT INTO "vn_mokhtasar" VALUES(7,172,'7:172','Và hãy nhớ lại - hỡi Muhammad - khi Thượng Đế của Ngươi đã kéo con cháu Adam ra từ các đốt khớp xương của họ rồi TA bắt họ phải chứng minh xác thực lên bản thân chúng trước những câu hỏi đức tin về sự tạo hóa ra họ chính là Thượng Đế của họ, rồi Ngài phán :" Há ! TA chẳng phải là Thượng Đế của các ngươi chăng ?"Họ đồng thanh hô to :"Vâng, Ngài là Thượng Đế của bầy tôi," Ngài phán tiếp :" Quả thật, TA cần lời hô to đó của các ngươi để TA lấy đó làm lời giáo ước với các ngươi chớ để vào ngày phán xét các ngươi diện ra một cái cớ trước Allah;" để rồi các ngươi thưa rằng : "Quả thật, bầy tôi không hề biết về điều này."');
INSERT INTO "vn_mokhtasar" VALUES(7,173,'7:173','Hoặc các ngươi cho rằng mình sẽ vô can vì tổ tiên của các ngươi đã hủy bỏ lời giao ước đó, rồi cũng chính họ đã tổ hợp thần linh khác cùng với Allah, bởi thế các ngươi cho rằng mình là kẻ chỉ bắt chước họ đã làm điều tổ hợp với Ngài", thế nên các ngươi đồng thanh hô to: "Phải chăng, Ngài sẽ hủy diệt bầy tôi - hỡi Thượng Đế của bầy tôi - bởi vì việc làm của tổ tiên của bầy tôi đã gây ra, một việc làm đánghổ thẹn gì đã tổ hợp ai đó cùng với Allah để rồi bầy tôi chuốt lấy sự diệt vong chăng ?" Mà đó chẳng phải là tội lỗi của bầy tôi chỉ vì sự ngu muội của bầy tôi đã bắt chước họ.');
INSERT INTO "vn_mokhtasar" VALUES(7,174,'7:174','Và tương tự TA đã phân tích quá rõ ràng về các dấu hiệu cho từng thời đại cho mỗi cộng đồng mang tính dối trá lộc lừa của chúng; may ra chúng tỉnh ngộ mà sớm quay về trên con đường tôn thờ Allah Đấng Độc Tôn Duy Nhất và cũng giống như là sớm trở lại để nắm giữ lời giao ước mà tổ tiên họ đã lật lọng với Allah lên bản thân họ.');
INSERT INTO "vn_mokhtasar" VALUES(7,175,'7:175','Và hãy đọc - hỡi Thiên Sứ - rồi kể lại cho con cháu của Isra-el biết về câu chuyện của một người đàn ông mà được TA ban cho các dấu hiệu để cho y nhận thức và thấu hiểu ra chân lý mà đã được TA tiết lộ. Thế nhưng, y chẳng chịu làm theo chân lý đó, đúng hơn là y bỏ mặc và còn làm cho nó xáo trộn lên. Đành vậy, y còn cho rằng lũ Shaytan là đúng đắn rồi y hòa nhập vào cùng với chúng nó. Bởi thế, y đã trở thành một kẻ lạc lối chuốc lấy sự diệt vong sau khi y đã cải đạo.');
INSERT INTO "vn_mokhtasar" VALUES(7,176,'7:176','Và nếu muốn TA sẽ nâng y cao lên cùng với những dấu hiệu bởi những việc làm của y ở trần gian lẫn ngày sau, thế nhưng y lại chọn con đường đầy thất bại do sự thèm khát xa hoa tráng lệ ở trần tục này, thế nên y sẽ là kẻ thất bại cho đời này lẫn đời sau, bởi vì y lo chạy theo dục vọng phù phiếm. Điển hình sự thèm khát của y ở trần tục này giống như một con chó đang le lưỡi ở mọi trạng thái dù là khỏe mạnh hay mệt mỏi. Do vậy, những hình ảnh được nhắc đến giống như một đám người đang lầm lạc vì đã khước từ đi những dấu hiệu của Ngài. Bởi thế - hỡi Thiên Sứ - Ngươi hãy kể câu chuyện đó lại cho chúng rõ, để may ra chúng còn biết ngẫm nghĩ về những hành động chống đối và sự lầm lối của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(7,177,'7:177','Chẳng phải, đây là hình ảnh của một đám dân ô hợp mà chúng đã đặt điều cho bằng chứng và chứng cớ của TA rồi không chịu tin tưởng vào nó, thế nên bọn chúng chuốc lấy sự bất công cho chính bản thân chúng để rồi dẫn đến sự diệt vong.');
INSERT INTO "vn_mokhtasar" VALUES(7,178,'7:178','Thế ai nằm dưới sự hướng dẫn của Allah thì chắc chắn y sẽ thành công đi đến con đường ngay chính và đúng đắn, còn những ai xa lánh con đường ngay chính của TA sẽ là một điều hết sức bất hạnh cho bản thân chúng. Bởi vì, chúng là những kẻ thua thiệt cho bản thân chúng lẫn gia quyến của chúng vào Ngày Phán Xét và chắn chắn rằng y là một kẻ thua thiệt một cách rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(7,179,'7:179','Và chắn chắn, TA sẽ đẩy vào Hỏa Ngục đa số là loài Jinn và loài Người; vì TA rất am hiểu về việc chúng sẽ làm những điều mà của tổ tiên chúng đã làm. Vì chúng có tấm lòng không biết nhận thức được đâu là cái lợi và đâu là tai hại, thế chúng có đôi mắt cũng coi như mù chẳng nhìn thấy được các dấu hiệu của Allah còn biết giữ bản thân chúng và chúng có lỗ tai mà coi như điếc chẳng chịu lắng nghe và nhận thức các dấu hiệu của Allah. Bởi thế, chúng chẳng khác gì là một con thú vật chẳng có đầu óc, nói đúng hơn ! Đa số bọn chúng còn tệ hơn một con thú. Bởi do chúng là những kẻ lơ là xem nhẹ việc thờ phượng Allah và tin tưởng vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(7,180,'7:180','Allah - thật vinh quang thay Ngài - Đấng có các Đại Danh Xưng thật cao quý chứng minh uy quyền lẫn sự hoàn hảo của Ngài. Do đó, hãy cầu nguyện với Allah mọi đều mà các ngươi mong muốn cùng với lời ca tụng Ngài và rồi hãy dừng ngay lập tức đối với những ai có ý định dùng những Đại Danh đó mà không phải là Ngài hoặc phủ nhận Nó hoặc bóp méo đi ý nghĩa hay có nghĩa tương đồng mà ý nghĩa đó không phải là Ngài. Và rồi cuối cùng chúng ngoan cố làm sai lệch đi các Đại Danh của Ngài. Thế rồi kết cuộc thật sự của chúng đó là:"Sự trừng phạt thật khủng khiếp cho những việc làm sai trái của chúng gây ra."');
INSERT INTO "vn_mokhtasar" VALUES(7,181,'7:181','Và trong đám dân chúng của Musa lại có một nhóm người họ áp dụng cho bản thân họ một chân lý để đổi lấy được sự chỉ dẫn của Ngài để họ dùng đó đi kêu gọi mọi người đến với sự chỉ dẫn và họ cũng dùng điều đó cho việc phán xét một cách thật công bằng chính trực và tránh đi sự bất công.');
INSERT INTO "vn_mokhtasar" VALUES(7,182,'7:182','Thức tỉnh đi những kẻ đã từ chối các dấu hiệu của TA hay chẳng có chút đức tin nào. Đúng hơn, là chúng đã thẳng thừng khước từ. Thế nên, TA sẽ mở toang cho chúng những cánh cửa bổng lộc thế nhưng chúng chẳng cảm thấy được sự thoải mái. Không! Chúng sẽ từ từ bị TA đem chúng đến với con đường lạc lối rồi sau đó TA sẽ trút xuống chúng sự trừng phạt thật khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(7,183,'7:183','Và rồi TA tạm hoãn việc trừng phạt chúng cho đến khi chúng tưởng chừng điều đó sẽ không còn xảy ra nữa, rồi chúng vẫn cứ tiếp tục những hành động sai trái và tỏ thái độ bất tin của mình cho đến khi hình phạt của chúng được nhân lên gấp bội lần. Quả thật, sự tính toán của TA rất vững chắc, vì TA chỉ thể hiện cho họ những thứ tốt đẹp, và TA muốn chúng sẽ phải nhận lấy sự thất bại ê chề.');
INSERT INTO "vn_mokhtasar" VALUES(7,184,'7:184','Há chúng chẳng chịu suy ngẫm đã khước từ đi những dấu hiệu của Allah và Thiên Sứ của Ngài ư? Với những hành động minh mẫn của chúng đã nhận thức ra được điều hết sức rõ ràng là Muhammad - cầu xin Allah ban phúc lành cho Người - không phải là một người mất trí. Quả thật, Người là một Thiên Sứ của Allah đến để cảnh báo về hình phạt của Allah một cách công khai minh bạch.');
INSERT INTO "vn_mokhtasar" VALUES(7,185,'7:185','Há! Chúng không nhìn thấu được quyền lực của Allah ở các tầng trời và trái đất, và vạn vật ở giữa bầu trời và mặt đất như động vật, thực vật và ngoài hai loài đó ra ư? Chẳng lẽ, chúng không nhận thấy tuổi thọ của chúng sắp hết hạn rồi, sớm quay đầu sám hối trước khi chúng đi vào cái chết ư? Rồi nếu như họ không tin tưởng vào Thiên Kinh Qur''an có chứa đựng việc ấn định lời hứa hẹn thì thật sự chúng sẽ không biết dựa vào đâu để tin tưởng được.');
INSERT INTO "vn_mokhtasar" VALUES(7,186,'7:186','Ai mà bị Allah bỏ mặc không hướng dẫn đến với chân lý, và còn bị Ngài đánh lạc hướng đối với con đường ngay thẳng thì không có một ai có thể hướng dẫn y được. Và Allah bỏ mặc để chúng lầm lạc và phủ nhận đức tin. Chúng lạc lối không được hướng dẫn bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(7,187,'7:187','Những kẻ ngang tàn ngạo mạng hỏi Ngươi Ngày Tận Thế: Thế một thời điểm nào mà Ngươi cho rằng nó sẽ diễn ra ? Hãy bảo chúng - hỡi Muhammad -: Không! Tôi không biết điều đó và cũng chẳng có một ai biết điều đó. Quả thật, chỉ có duy nhất Allah mới am tường khi nào nó mới diễn ra, không có một thời gian cụ thể nào ngoại trừ chỉ có quyền năng của Allah mới hiểu thấu, rồi nó sẽ diễn ra thật kinh hoàng trong bầu trời và trái đất nó đến với các ngươi một cách bất chợt, thế chúng có hỏi Ngươi về ngày tận thế mà Ngươi cũng muốn biết nó và họ biết rằng Ngươi cũng không bao giờ dám hỏi Thượng Đế của Ngươi vào ngày đó. Hãy bảo họ - hỡi Muhammad - rằng: "Quả thật, ngày tận thế đó duy chỉ một mình Allah biết rõ, và đa số nhân loại không biết điều đó."');
INSERT INTO "vn_mokhtasar" VALUES(7,188,'7:188','Hãy bảo chúng - hỡi Muhammad - :" Thật sự, Ta không có khả năng đem lại điều mai mắn hay tai ương đến cho bản thân Ta ngoại trừ Allah muốn điều đó xảy ra, quả thật mọi điều đó là đến từ nơi Allah, Ta không hề biết ngoại trừ Allah cho Ta biết điều đó cũng như Ta không hề biết trước được điều gì và nếu như Ta biết trước được một điều nào đó thì Ta sẽ tích lũy những điều tốt đẹp và tránh xa những điều tồi tệ và nếu như Ta biết được cái gì chăng nữa thì Ta sẽ là Người truyền giáo. Thế nhưng, không phải thế Ta chỉ là một Thiên Sứ của Allah, Ta rất sợ sự trừng phạt khủng khiếp của Ngài, Ta chỉ là Người loan tin cho phần thưởng hào phóng của Ngài giành cho những người có đức tin, thật sự Ta chỉ là một Thiên Sứ của Ngài. Thật vinh quang thay Ngài Đấng Tối Cao, bởi thế mọi người hãy tin tưởng vào những điều Ta mang đến từ Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(7,189,'7:189','Ngài là Đấng đã tạo hóa ra các ngươi - hỡi những người đàn ông và những người phụ nữ - từ một Người duy nhất đó là Adam - cầu xin sự bằng an cho Người -. Rồi từ Adam - cầu xin sự bằng an cho Người - Ngài đã tạo ra vợ của Người là bà Hawa'', Ngài tạo Hawa'' ra từ chiếc xương sườn của Adam cho cả hai được cuộc sống yên bề. Bởi thế, khi người chồng ôm phủ lấy người vợ trong lúc giao hợp, sau đó người vợ thụ thai, nhưng cảm giác lại rất nhẹ nhàng gần như là không hề có cảm giác mang thai. Quả thật, đó chỉ là sự bắt đầu cho sự thụ thai điều đó không có gì là khó khăn. Bởi thế, cho đến khi cảm nhận được sự nặng nề ở trên bụng vì bào thai to lên từng ngày thì lúc đó cả hai cầu nguyện với Ngài :"Cầu xin Ngài ban cho bầy tôi - lạy Thượng Đế của bầy tôi - có được một đứa con thật tốt đẹp với sự hoàn chỉnh toàn diện thì chắc chắn bầy sẽ tạ ơn Ngài với hồng ân mà Ngài đã ban cho."');
INSERT INTO "vn_mokhtasar" VALUES(7,190,'7:190','Thế nhưng, khi Allah đáp lại lời cầu nguyện của hai vợ chồng mà ban cho họ một đứa con thật tốt đẹp giống như lời cầu xin thì họ lại bắt đầu tôn thờ đứa con cùng với Allah, rồi họ còn đặt tên cho con họ tên Abdul Haarith. Bởi vì, Allah là Đấng có địa vị cao nhất của tất cả mọi điều mà họ tổ hợp với Ngài. Quả thật, Ngài Đấng duy nhất tạo hóa vạn vật là Đấng xứng đáng duy nhất để cho bầy tôi thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(7,191,'7:191','Há, phải chăng, chúng tạo ra những bức tượng và những thứ khác rồi chúng đem đi tổ hợp với Allah cho việc tôn thờ mà chúng thừa biết những thứ đó chúng tạo ra không hề xứng đáng trong việc thờ phượng. Đúng hơn, những thứ đó chỉ là vô bổ, vậy tại sao chúng phải tạo ra để rồi tổ hợp với Allah ?');
INSERT INTO "vn_mokhtasar" VALUES(7,192,'7:192','Những thần linh không hề có quyền năng để giúp mọi người và cũng chẳng có quyền năng giúp chính bản thân chúng. Bởi thế! Làm sao phải thờ phượng chúng cơ chứ?');
INSERT INTO "vn_mokhtasar" VALUES(7,193,'7:193','Và nếu các ngươi - này hỡi những tên đa thần - có kêu gọi là những bức tượng mà các ngươi lấy đó làm hướng dẫn ngoài Allah và rồi chúng sẽ không bao giờ đáp lại cho lời kêu gọi của các ngươi, chúng cũng sẽ không làm theo các ngươi. Bởi thế, các ngươi kêu gọi chúng chỉ là bằng thừa hay các ngươi có cố im lặng đi chăng nữa. Quả thật, bọn chúng là những kẻ vô tri, không có đầu óc, những kẻ bị điếc và bị câm.');
INSERT INTO "vn_mokhtasar" VALUES(7,194,'7:194','Quả thật, những thứ (thần linh) mà các ngươi thờ phượng đó - hỡi những tên đa thần - không phải là Allah mà chúng chính là tạo vật của Allah, mọi thứ đều thuộc về Ngài, chúng cũng giống như các ngươi. Nhưng, mà các ngươi tốt đẹp hơn chúng. Bởi vì, các ngươi có được sự sống, biết nói, biết đi, biết nghe và nhìn thấy, còn chúng là những bức tượng vô tri còn các ngươi thì không. Bởi thế, hãy cầu xin chúng đi rồi chúng sẽ đáp lại cho các ngươi và nếu các ngươi chắc chắn đó là sự thật cho những lời cầu xin các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(7,195,'7:195','Há! Phải chăng những thần linh bằng bức tượng mà các ngươi tôn thờ: Chúng có cái chân bước đi cho nhu cầu cần thiết như các ngươi ư? Hay chúng có cánh tay để nắm chặt như các ngươi chăng? Hoặc chúng có đôi mắt để quan sát để rồi nói lại cho các ngươi á? Hoặc chúng có lỗ tai để nghe những chuyện thầm kín để rồi dạy lại cho các ngươi biết chăng? Quả thật, mọi thứ đó đều không có, bởi thế tại sao các ngươi thờ phượng chúng? Phải chăng, chúng mang lại cho các ngươi điềm lành hoặc điềm gỡ chăng? Hãy bảo chúng - hỡi Thiên Sứ - này những tên đa thần: "Các ngươi hãy kêu gọi những ai mà cho là ngang hàng với Allah, sao đó để cho chúng toan tính lên kế hoạch chống đối lại với TA và các ngươi chớ để TA đợi quá lâu."');
INSERT INTO "vn_mokhtasar" VALUES(7,196,'7:196','" Quả thật, Allah luôn hậu thuẫn và trong chừng Ta, Ngài là Đấng bảo vệ Ta, bởi vì ngoài Ngài ra Ta không tìm ra ai khác để đặt niềm tin vào, và Ta không hề khiếp sợ bất cứ thứ gì từ những pho tượng của các ngươi. Bởi vì, Ngài đã ban cho Ta Thiên Kinh Qur''an dùng để chỉ dẫn nhân loại; Ngài là Đấng che chở cho những người ngoan đạo tôn thờ, Ngài cũng là Đấng bảo vệ và là Đấng hằng giúp đỡ họ."');
INSERT INTO "vn_mokhtasar" VALUES(7,197,'7:197','Những kẻ mà các ngươi đang cầu nguyện - hỡi những tên đa thần - chúng chỉ là những pho tượng chẳng có quyền hạn gì giúp đỡ các ngươi và cũng chẳng làm hại được các ngươi, vì chúng là những kẻ vô tri! Bởi thế, tại sao các ngươi phải cầu nguyện chúng mà không phải là Allah ?');
INSERT INTO "vn_mokhtasar" VALUES(7,198,'7:198','Và nếu như các ngươi kêu gọi - hỡi những tên đa thần - những bức tượng mà các ngươi coi là xứng đáng thờ phượng mà không phải là Allah để đạt đến con đường ngay chính thì chúng chẳng nghe được lời kêu gọi của các ngươi, Ngươi thấy chúng đối diện với Ngươi bằng đôi mắt đứng trơ ra nhưng nó không hề thấy gì cả. Bởi thế, chúng chỉ là những pho tượng được tạo ra dưới hình dạng của con cháu Adam hay những loài động vật, chúng có tay, có chân, có mắt, thế nhưng bị đơ cứng chẳng có chút sức sống và cũng chẳng cử động được.');
INSERT INTO "vn_mokhtasar" VALUES(7,199,'7:199','Hãy chấp nhận chúng - hỡi Thiên Sứ - rồi thứ lỗi cho bản thân chúng, hãy dùng sự phóng khoáng dễ dãi trước những hành động và thái độ của chúng, và chớ nên để chúng làm ảnh hưởng về những bản chất thối nát của chúng. Quả thật, những điều đó không đáng trách chúng mà hãy dùng lời lẽ tốt đẹp khuyên can cùng với những việc làm đức hạnh cảm hóa chúng đến với con đường đúng đắn và hãy lánh xa những việc làm ngu muội và không chấp nhận việc làm đần độn của chúng. Thế những ai làm tổn hại Ngươi thì chớ nên oán thù và còn ai câm ghét Ngươi thì chớ nên oán hận chúng.');
INSERT INTO "vn_mokhtasar" VALUES(7,200,'7:200','Và nếu như Ngươi có cảm thấy - hỡi Thiên Sứ - lũ Shaytan đang cám dỗ hay thấy nản lòng thì hãy nâng tay cầu nguyện với Allah xin Ngài che chở Ngươi. Quả thật, Ngài nằng nghe những lời cầu nguyện đó, Ngài hằng biết Ngươi cần điều gì và Ngài sẽ bảo vệ Ngươi tránh khỏi lũ Shaytan.');
INSERT INTO "vn_mokhtasar" VALUES(7,201,'7:201','Quả thật, những ai kính sợ Allah trước những mệnh lệnh của Ngài và tránh xa những điều mà Ngài nghiêm cấm và lỡ may bị lũ Shaytan cám dỗ làm điều tội lỗi; thì hãy suy ngẫm đến sự vĩ đại của Allah và sự trừng trị cho việc làm tội lỗi và ban thưởng cho việc khuất phục. Bởi thế, hãy ăn năn xám hối về tội lỗi và tỏ ra hối hận trước Thượng Đế. Và nếu như thật sự họ muốn hướng đến con đường ngay thẳng đúng đắn để tìm ra chân lý và từ bỏ mọi hành động trước đây thì hãy chấm dứt ngay.');
INSERT INTO "vn_mokhtasar" VALUES(7,202,'7:202','Thế nhưng, những tên ăn chơi trác táng, những kẻ ngỗ nghịch chúng chính là một lũ với Shaytan, chúng xưng anh xưng em với nhau và chúng sẽ không hề bỏ qua cho họ, chúng còn lôi kéo họ đi vào con đường lạc lối làm cho tội lỗi của họ chồng chất và chúng không hề bỏ qua ý định đó. Chúng đúng là một lũ Shaytan chẳng chịu dừng lại việc cám dỗ và xui khiến đi lầm lạc, thế rồi chúng lại cho là mình vô can cho việc dụ dỗ cùng với hành độ bỉ ổi của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(7,203,'7:203','Và nếu như Ngươi mang đến - hỡi Thiên Sứ - một dấu hiệu mà chúng cho là dối trá rồi ngoảnh mặt làm ngơ, thế rồi khi Ngươi không mang một dấu hiệu nào đến cho chúng thì chúng lại bảo:" Tại sao Ngươi không sáng kiến ra một dấu hiệu nào đó từ Ngươi hoặc một giả thiết nào đó? Hãy bảo chúng - hỡi Thiên Sứ - các dấu hiệu đó không phải của bản thân Ta mà rồi Ta mang đến cho các ngươi, Ta không hề mang đến bất cứ điều gì ngoại trừ những lời mặc khải của Allah, đó là Thiên Kinh Qur''an mà Ta đã xướng đọc lên cho các ngươi vì đó là những lập luận cùng với bằng chứng của Allah Đấng đã tạo hóa các ngươi, Đấng ngự trị và chi phối các ngươi, cũng là Đấng chỉ dẫn, Đấng Nhân Từ với đám bầy tôi chung kiên của Ngài và ngược lại còn đối với những kẻ không có đức tin chúng sẽ đi vào con đường lạc lối.');
INSERT INTO "vn_mokhtasar" VALUES(7,204,'7:204','Và một khi Thiên Kinh Qur''an được xướng đọc lên thì các ngươi hãy lắng nghe, chớ thì thầm to nhỏ và chớ bận tâm một điều gì khác ngoài Nó; may ra các ngươi sẽ nhận được sự thương xót từ Allah.');
INSERT INTO "vn_mokhtasar" VALUES(7,205,'7:205','Và hãy tưởng nhớ Allah - hỡi Thiên Sứ - Thượng Đế của Ngươi sâu thẩm trong tâm trí và sự khép nép lẫn sự kính cẩn Ngài. Rồi Ngươi hãy từ tốn cầu nguyện bằng giọng tương đối không to cũng không nhỏ vào buổi sáng và buổi xế tàn vì đó là hai khoảng thời gian thật tốt đẹp và rồi Người chớ là một Người mất tập trung trong lúc tụng niệm Allah Tối Cao.');
INSERT INTO "vn_mokhtasar" VALUES(7,206,'7:206','Quả thật, những ai ở cận kề bên Thượng Đế Ngươi - hỡi Thiên Sứ - điển hình như là các Thiên Thần chẳng bao giờ đắc chí trong lúc thờ phường Ngài, Đấng Hiển Vinh. Ngược lại, chúng luôn hạ mình phục tùng Allah, họ luôn tụng niệm Allah cả ngày lẫn đêm bằng lời lẽ phù hợp và xứng đáng với Ngài và họ cúi đầu quỳ lạy Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(8,1,'8:1','Các bằng hữu của Ngươi hỏi Ngươi - hỡi Thiên Sứ - về chiến lợi phẩm; họ sẽ phân chia như thế nào? Và ai là người xứng đáng nhận được nó ? Hãy bảo họ - hỡi Thiên Sứ - trong sự từ tốn đáp lại họ:"Chiến lợi phẩm đó là của Allah và Thiên Sứ của Ngài, thế nên hãy dùng giáo luật của Allah và Thiên sứ của Ngài để phân phát chia, bởi thế sẽ không có sự lựa chọn nào khác cho các người ngoài việc chỉ biết vâng lời và phục tùng thôi. Bởi thế, hãy kính sợ Allah - hỡi những người có đức tin - và phục tùng mệnh lệnh của Ngài, kiềm hảm bản thân trước những điều mà Ngài nghiêm cấm và hãy giải quyết theo một cách thỏa đáng giữa các ngươi với nhau trong sự kết nối, thắt chặt lại với nhau bằng một mối quan hệ thân thiện và luôn tỏ ra thái độ ôn hòa lẫn sự nhân từ. Do đó, hãy là người phục tùng Allah và Thiên Sứ của Ngài nếu các ngươi là người có đức tin thật sự; Vì quả thật, Đức tin sẽ đưa ngươi thành người luôn tuân thủ và kéo ngươi tránh xa điều tội lỗi". Câu hỏi được hỏi sau khi chiến thắng trận đánh Badr.');
INSERT INTO "vn_mokhtasar" VALUES(8,2,'8:2','Quả thật, những ai có đức tin thật sự khi họ được nhắc đến Allah Tối Cao thì họ sẽ kinh hoàng khiếp vía làm cho con tim họ rúng động. Bởi thế, sự rúng động con tim họ làm cho họ bất đầu tuân thủ và còn nếu như họ nghe được ai đó xướng đọc lên những lời mặc khải của Allah thì sẽ làm cho họ thêm vững chắc đức tin này tiếp nối đức tin khác và rồi họ chọn sự phó thác hết vào Thượng Đế của họ Đấng Duy Nhất đem lại cho họ những điều phúc lành và tránh được mọi tai ương.');
INSERT INTO "vn_mokhtasar" VALUES(8,3,'8:3','Họ là những ai luôn có sự chuẩn bị chu đáo để thực hiện dâng lễ nguyện Salah một cách chỉnh chu hoàn hảo trong một thời gian được ấn định mà còn chi tiêu cho nhu cầu cần thiết một cách phù hợp với thiên lộc mà Allah đã ban cho.');
INSERT INTO "vn_mokhtasar" VALUES(8,4,'8:4','Những ai có được đức tính như thế chính là những người có đức tin thật sự; vì họ hội tụ đủ những phẩm chất đức tin và họ chính là những tín đồ Islam chính tông. Thế nên, họ xứng đáng nhận được những địa vị cao quý của Thượng Đế của họ lẫn với sự Tha Thứ cho tội lỗi kèm theo thiên lộc dồi dào, đó là được Allah chu cấp những hồng ân giành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(8,5,'8:5','Giống như việc Allah Tối Cao đã tịch thu lại phần chiến lợi phẩm sau khi diễn ra cuộc nội chiến và xung đột giữ các ngươi về việc phân chia, rồi Ngài qui định quyền quyết định thuộc về Ngài và Thiên Sứ của Ngài - cầu xin Allah ban sự bình an cho Người -, rồi Ngài hạ lệnh cho Ngươi - hỡi Thiên Sứ - ra khỏi thị trấn để đối mặt với bọn đa thần qua lời mặc khải của Ngài được ban xuống cho Ngươi, tuy nhiên điều đó khiến một số tín đồ có đức tin không ưa thích đi đối mặt với bọn đa thần.');
INSERT INTO "vn_mokhtasar" VALUES(8,6,'8:6','Nhóm người có đức tin đó tranh cãi với Ngươi - hỡi Thiên Sứ - lý do giao chiến với bọn đa thần sau khi Ngươi đã trình bày quá rõ ràng cho họ. Bởi vì, họ liên tưởng rằng như Người đang đưa họ đi vào chỗ chết mà họ đã nhìn thấy tận mắt. Bởi vì, sự căng thẳng của hận thù diễn ra ngoài trận chiến thật ác liệt mà họ thật sự chưa có sự chuẩn bị kỹ càng và họ cũng không thấy lời hứa của Ngài là sự an toàn với họ.');
INSERT INTO "vn_mokhtasar" VALUES(8,7,'8:7','Và hãy nên nhớ kỹ - hỡi những tín đồ có đức tin đang tranh luận - nếu như Allah đã hứa với các ngươi thì chắc chắn sẽ chiến thắng một trong hai đoàn quân của bọn đa thần. Một là đoàn quân áp tải ngân lương mà các người sẽ chiếm được chiến lợi phầm hoặc hai là đoàn quân có một đội quân hùng hậu mà ngươi sẽ chiến thắng họ. TA am hiểu các ngươi sẽ mong muốn đối đầu với đoàn quân áp tải ngân lương vì chúng không có trang bị vũ khí để tránh được cảnh chém giết. Thế nhưng không, Allah muốn chứng minh lời hứa của Ngài là sự thật nên Ngài ra lệnh các ngươi chiến đấu với đoàn quân hùng mạnh để tiêu diệt tận gốc bọn đa thần và bắt giữ bọn chúng thật nhiều cho đến khi Islam thật sự xưng hùng xưng bá.');
INSERT INTO "vn_mokhtasar" VALUES(8,8,'8:8','Nhằm để Allah chứng minh rằng sự thật là Islam lẫn các tín đồ thật sự là xưng hùng xưng bá đó là một sự thật, song Đấng Hiển Vinh muốn làm hiện rõ lên bằng chứng giả dối cho dù bọn đa thần chẳng hề thích tí nào. Thế nhưng Allah muốn điều đó hiện lên một cách rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(8,9,'8:9','Và các ngươi hãy nhớ lại vào ngày ở trận đánh Badr khi các ngươi đã cầu xin Allah cứu giúp các ngươi trong lúc đương đầu với kẻ thù. Thế nên, Allah đã đáp lại lời cầu cứu đó bởi vì các ngươi là tôi tớ của TA - hỡi những người có đức tin -: "TA đã cử đến với các ngươi một ngàn Thiên Thần, họ giăng thành đoàn này nối tiếp đoàn kia."');
INSERT INTO "vn_mokhtasar" VALUES(8,10,'8:10','Và Allah chi diện các vị Thiên Thần đó là để cho các ngươi vững tâm có thêm động lực - hỡi những người có đức tin - bởi vì, Ngài luôn yểm hộ các ngươi chiến thắng trước kẻ thù, làm thế để cho các ngươi có sự an tâm vào chiến thắng, chứ không phải chiến thắng bằng một đội quân hùng hậu hay sự chuẩn bị kỹ càng của một đội quân. Bởi vì, chiến thắng là do Allah định đoạt. Quả thật, Allah Toàn Năng mà không một ai chế ngự được và Ngài Sáng Suốt trước mọi vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(8,11,'8:11','Hãy nhớ lại - hỡi những người có đức tin - khi Allah làm cho các ngươi say nồng trong giấc ngủ dưới sự bảo vệ an toàn của Ngài trước sự sợ hãi trước kẻ thù của các ngươi, Ngài ban xuống cho các ngươi một cơn mưa dùng để tẩy sạch sự ô uế cho các ngươi bị lây nhiễm bởi lũ Shaytan và Ngài củng cố tấm lòng của các ngươi thêm vững trãi trước tôn giáo của các ngươi và cũng chứng minh bàn chân các ngươi đứng vững vàng trên vùng đất cát mà không còn có sự lay động trên bước chân.');
INSERT INTO "vn_mokhtasar" VALUES(8,12,'8:12','Khi Thượng Đế của Ngươi đã mặc khải - hỡi Sứ Giả (Muhammad) - đến cho các vị Thiên Thần mà Allah sai họ yểm hộ cho những người có đức tin trong trận đánh Badr: "TA luôn ở cùng với các ngươi - hỡi các Thiên Thần - bằng sự chiến thắng vẻ vang, bởi đó là một động lực mạnh mẻ cho những người có đức tin trong trận chiến đối mặt trước kẻ thù của họ, rồi TA cũng sẽ gieo vào lòng của những kẻ không có đức tin một nỗi kinh hoàng; bởi thế hãy đánh - hỡi những người có đức tin - vào cổ của những kẻ không có đức tin cho chúng chết và cũng đánh vào các khớp ở tứ chi và các lóng tay để cho chúng không còn khả năng đi chiến đấu."');
INSERT INTO "vn_mokhtasar" VALUES(8,13,'8:13','Những kẻ bất tuân chúng đáng bị giết chết và bị đánh vào các khớp tứ chi bởi vì chúng luôn chống đối với Allah và Thiên Sứ của Ngài. Bởi vì, chúng là những kẻ luôn kháng cự mệnh lệnh của Ngài, chúng chẳng cam chịu với những gì mà Ngài nghiêm cấm, chúng còn tỏ ra thái độ chống đối với Allah và Thiên Sứ của Ngài. Quả thật, Allah sẽ trừng trị chúng thật khủng khiếp ở trần giàn này bằng cách tiêu diệt chúng lẫn gia quyến của chúng và vào Ngày Phán Xét, chúng sẽ ở trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(8,14,'8:14','Đây là sự trừng phạt mà TA đã cảnh cáo các ngươi - hỡi những tên chống đối Allah và Thiên Sứ của Ngài - các người hãy nếm mùi vị của sự trừng phạt của cuộc sống trần gian này và rồi vào Ngày Sau, các ngươi sẽ bị hành hạ trong Hỏa Ngục bao chùm lấy các ngươi, bởi vì các ngươi là những kẽ chống đối và ương ngạnh.');
INSERT INTO "vn_mokhtasar" VALUES(8,15,'8:15','Hỡi những ai đã tin tưởng vào Allah và làm theo Thiên Sứ của Ngài! Nếu như các ngươi đang đối mặt với những tên đa thần trong lúc hai bên giao tranh thì chớ sợ bại trước chúng rồi quây lưng bỏ chạy, mà hãy chứng minh cho chúng thấy được trên gương mặt của các ngươi trong sự hiên ngang khí thế khi đối diện với chúng. Bởi vì, Allah luôn bên các ngươi làm hậu thuẫn và yểm hộ các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(8,16,'8:16','Và những ai quay lưng về phía địch bỏ chạy trong lúc hỗn chiến chứ không phải âm mưu là dụ địch vào trận rồi đánh úp hoặc ý định rút về đoàn quân của mình để được sự giúp đỡ. Quả thật, việc làm đó sẽ chuốc lấy cơn thịnh nộ của Allah và rồi nơi ẩn náu của y vào Ngày Sau chắc chắn là Hỏa Ngục. Thật là tệ hại con đường mà chúng phải đi và thật là tệ hại cho việc chúng đã quay lưng');
INSERT INTO "vn_mokhtasar" VALUES(8,17,'8:17','Không phải các ngươi - hỡi những người có đức tin - đã giết chết kẻ thù vào ngày ở trận chiến Badr khi đối mặt với bọn đa thần bằng chiến lượt và sức mạnh của các ngươi, chính Allah đã giúp các ngươi chiến thắng. Song việc Ngươi - hỡi Thiên Sứ - đã ném nắm cát vào bọn đa thần mà chính Allah đã ném vào chúng khi Ngươi ném chúng. Nhằm mục đích Ngài chứng minh cho những người có đức tin thấy được ân sửng của Ngài trước kẻ thù của họ chứ không phải là do số lượng quân ít hay nhiều mà là do số lần tạ ơn Ngài. Quả thật, Ngài là Đấng Hằng Nghe những lời cầu nguyện và van xin của các ngươi, Ngài là Đấng Am Tường về mọi việc của các ngươi và là Đấng cho các ngươi những điều tốt đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(8,18,'8:18','Thế như! Đây chỉ là việc nhắc nhở lại chiến thắng trước bọn đa thần. Sự ném cát vào chúng làm cho chúng thất bại rồi đâm đầu bỏ chạy. Đó là một Ân Sủng mà Ngài dành cho những người có đức tin đang đối diện trước kẻ thù của họ; mọi thứ là do Allah. Bởi vậy, Allah đã làm tàn lụi âm mưu của những kẻ không có đức tin mà chúng toan tính hảm hại tôn giáo Islam.');
INSERT INTO "vn_mokhtasar" VALUES(8,19,'8:19','Này những tên thích giao chiến - từ người đa thần - rằng Allah hạ sắc lệnh trừng phạt đối với những kẻ bất công áp bức thì chắn chắc Allah sẽ cho các ngươi được gây sự. Bởi vì, Ngài ban xuống cho các ngươi những điều tốt lành và bài học cho những người biết kính sợ. Thế nên các ngươi hãy bỏ ý định gây chiến đó đi nó sẽ tốt cho các ngươi may ra Ngài sẽ hủy diệt các ngươi không phải ngay bây giờ mà còn nếu các ngươi muốn lập lại chiến tranh giao chiến với những người có đức tin thì Ngài hứa với các ngươi là sẽ có một hình phạt thích đáng cho các ngươi và phần thắng đó sẽ nằm bên những người có đức tin. Do đó, dù cho có sở hữu đội quân hùng hậu cũng chẳng giúp các ngươi chiến thắng, cho dù đội quân đó có đông đến đâu và đội quân của người có đức tin có ít đi chăng nữa. Quả thật, Allah luôn chỉ ở cùng với những người có đức tin luôn giúp họ chiến thắng và yểm trợ họ và ngược lại còn ai là người mà Allah hảm hại thì không bao giờ thắng được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(8,20,'8:20','Hỡi những người tin tưởng vào Allah và vâng lệnh Thiên Sứ của Ngài! Các ngươi hãy phục tùng Allah và làm theo Thiên Sứ của Ngài bằng những mệnh lệnh và ngăn cấm mình tránh những điều mà Ngài đã cấm, chớ nên là những tên ương ngạnh khánh cự lại những mệnh lệnh của Ngài cùng với những gì Ngài nghiêm cấm, và các ngươi hãy luôn lắng nghe những lời mặc khải của Allah mà khi có người nào đó xướng đọc lên cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(8,21,'8:21','Và đừng xử sự - hỡi những người có đức tin - như một bọn đạo đức giả hay một lũ người đa thần mà nếu như có ai đó xướng đọc những lời mặc khải của Allah thì họ lại bảo:" Chúng tôi đã từng nghe được lời xướng đọc của Thiên Kinh Qur''an này." Nhưng thật sự họ không hề nghe hay được ai chỉ dạy gì mà chỉ là nhận được từ truyền miệng cho nhau.');
INSERT INTO "vn_mokhtasar" VALUES(8,22,'8:22','Quả thật, điều nghiệt ngã nhất luôn đeo bám con người mà Allah đã tạo hóa ra ở trên mặt đất này đó chính là những kẻ bị điếc chúng chẳng chịu lắng nghe chân lý rồi tiếp nhận nó hay những kẻ bị câm không thể nói chuyện được. Bởi thế, chúng là những kẻ không hề biết chút gì về Allah hay mệnh lệnh của Ngài hoặc những gì Ngài nghiêm cấm.');
INSERT INTO "vn_mokhtasar" VALUES(8,23,'8:23','Và nếu như Allah phát hiện ra được bọn đa thần chuyên lừa dối có được một điều tốt đẹp nào thì chắc chắn Ngài sẽ cho họ lắng nghe được những điều hữu ích của Ngài, để cho chúng nhận thức ra được Ngài qua những bằng chứng và chứng cớ. Thế nhưng Ngài am hiểu rõ về họ không có chút tốt đẹp nào cả. Còn nếu như Ngài cho họ nghe được - về sự toan tính và giả tâm của chúng - là chúng bỏ chạy vì đức tin chúng đã bị lu mờ bởi vì chúng có giả tâm chống đối.');
INSERT INTO "vn_mokhtasar" VALUES(8,24,'8:24','Hỡi những người vững chắc đức tin nơi Allah và làm theo Thiên Sứ của Ngài! Hãy đáp lại lời kêu gọi của Allah và Thiên Sứ của Ngài bằng cách tuân thủ tuyệt đối mệnh lệnh của Ngài và hãy kiềm hảm mình trước những gì mà Ngài nghiêm cấm khi Ngài kêu gọi các ngươi đến với lối sống chân chính thì hãy biết hạ mình kính sợ Allah vì quả thật Ngài có khả năng cho mọi vạn vật. Bởi vì, Ngài là Đấng có khả năng làm thay đổi các ngươi và thay đổi tấm lòng các ngươi đến với chính đạo nếu các ngươi có ý định làm điều sai trái với Ngài thế nên hãy hướng về Ngài. Do đó, các ngươi hãy hạ mình kính sợ Allah duy nhất Đấng sẽ triệu các ngươi vào ngày tận thế, bởi vì Ngài luôn thưởng phạt phân minh cho việc làm ngoan đạo của các ngươi ở trên trần gian này.');
INSERT INTO "vn_mokhtasar" VALUES(8,25,'8:25','Hãy biết giữ mình - hỡi những người có đức tin - trước hình phạt chớ để bản thân mình rơi vào hố sâu tội lỗi, từ những gì trực tiếp hay giáng tiếp mà một khi có sự bất công xuất hiện hay không đi chăng nữa. Hãy hạ mình kính sợ Allah bởi vì Ngài trừng phạt rất nặng đối với những ai mang tội. Bởi thế, hãy nên biết giữ mình trước hố sâu tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(8,26,'8:26','Và hãy nhớ lại - hỡi những người có đức tin - trước kia các ngươi chỉ với số lượng người rất ít, dân làng của các ngươi đã hạ nhục và xem thường các ngươi ra sao ư, các ngươi còn luôn khiếp sợ họ sẽ nhanh chóng bắt được các ngươi. Thế rồi, Allah đã giúp các ngươi có được một nơi tị nan an toàn đó chính là Al- Madinah Al-Munawwarah rồi Ngài còn củng cố thêm sức mạnh cho các ngươi đối mặt trước kẻ thù trong trận đánh ở Badr, Ngài còn cung dưỡng các ngươi với những thiên lộc tốt đẹp bao gồm những chiến lợi phẩm đã lấy được từ kẻ thù của các ngươi. Thế nên, hãy tỏ lòng biết ơn Allah thật nhiều về thiên lộc của Ngài vì đã ban thêm cho các ngươi và chớ là những kẻ phụ ơn vì nó sẽ đẩy các ngươi rơi vào hố sâu tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(8,27,'8:27','Hỡi những người có đức tin vào Allah và làm theo Thiên Sứ của Ngài! Chớ làm điều bội ước với Allah và Thiên Sứ của Ngài bằng cách sao lãng đi mệnh lệnh của Ngài và vô tư làm những gì mà Ngài nghiêm cấm, rồi chớ bội tín những gì mà Ngài đã tín dụng các ngươi ở trần gian này hay ngoài điều đó ra mà trong khi các ngươi biết rõ rằng điều đó là bội tín. Bởi thế, các ngươi là những kẽ liều lĩnh.');
INSERT INTO "vn_mokhtasar" VALUES(8,28,'8:28','Và các ngươi hãy nên biết - hỡi những người có đức tin - khối tài sản và con cái của các ngươi, quả thật đó là sự thử thách mà Allah muốn thử thách các ngươi và cũng cảnh báo các ngươi, đôi khi nó sẽ cản trở làm những việc dành cho Đời Sau và nó sẽ khiến các ngươi thành những người bội ước. Các ngươi nên biết rằng ở nơi Allah có một phần thưởng rất là vĩ đại. Bởi thế, các ngươi chớ nên bỏ lở phần thưởng đó bằng khối tài sản hay con cái của các ngươi rồi khiến mình thành người phản bội và rơi vào hố sâu như chúng.');
INSERT INTO "vn_mokhtasar" VALUES(8,29,'8:29','Hỡi những người có đức tin nơi Allah và làm theo Thiên Sứ của Ngài! Nếu các ngươi biết kính sợ Allah thì hãy tuân thủ mệnh lệnh của Ngài và biết kiềm hảm mình trước điều mà Ngài nghiêm cấm, rồi Ngài sẽ ban cho các ngươi được một sự Chuẩn Mực để phân biệt được giữa điều phải và điều quấy. Bởi thế, các ngươi chớ thấy mình quá hổ thẹn trong khi Ngài xóa tội cho các ngươi mà những gì các ngươi đã lầm lỗi vì Ngài sẽ tha thứ tội lỗi cho các ngươi. Quả thật, Allah sở hữu được vô vàn hồng ân vĩ đại và những hồng ân vĩ đại đó Ngài chuẩn bị ở nơi Thiên Đàng và Ngài chỉ giành cho những người nào biết kính sợ Ngài trong đám tôi tớ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(8,30,'8:30','Và hãy nhớ lại - hỡi Thiên Sứ - trong lúc những người đa thần có ý định tống giam Ngươi hoặc sát hại Ngươi hay muốn trục xuất Ngươi đi đến một đất nước nào đó. Thì bọn chúng sẽ lên âm mưu và toan tính kế hoạch thế rồi Allah cũng đã sẵn sàn cho kế hoạch đáp trả lại chúng, quả thật Allah là Đấng hoạch định tinh vi bậc nhất.');
INSERT INTO "vn_mokhtasar" VALUES(8,31,'8:31','Và nếu như lời mặc khải của TA được xướng đọc lên để cảnh báo chúng thì chúng tỏ ra ngạo mạng to tiếng bảo: "Quả thật, chúng tôi đã từng nghe điều này trước kia. Nếu muốn thì chúng tôi sẽ xướng đọc lên điều mà giống như Thiên Kinh Qur''an mà chúng tôi đã từng nghe. Thế! Thiên Kinh Qur''an này mà chúng tôi từng nghe Nó chẳng khác gì là một truyền thuyết ở thời tiền sử. Bởi thế. chúng tôi không tin tưởng vào Nó."');
INSERT INTO "vn_mokhtasar" VALUES(8,32,'8:32','Và hãy nhớ lại - hỡi Thiên Sứ - khi bọn đa thần cầu nguyện - "Lạy Thượng Đế! nếu như những gì Muhammad mang đến đúng là chân lý thì xin Ngài hãy trút xuống chúng tôi một trận mưa đá để hủy diệt chúng tôi hoặc mang đến cho chúng tôi một hình phạt thật khủng khiếp. Chúng nói thế với giọng diệu ngông cuồng và khước từ."');
INSERT INTO "vn_mokhtasar" VALUES(8,33,'8:33','Và Allah sẽ không giáng hình phạt xuống cộng đồng của Ngươi - cho dù đó là một cộng đồng ngỗ nghịch hoặc một cộng đồng luôn đi kêu gọi mọi người là điều tốt - bằng một hình phạt lên người chúng và Ngươi - hỡi Muhammad - trong khi Ngươi đang chung sống với bọn chúng. Bởi vì, sự hiện diện của Ngươi là sự bảo vệ cho chúng tránh khỏi sự trừng phạt của TA và Allah sẽ không trừng phạt chúng trong khi chúng luôn cầu nguyện Allah thư thứ tội lỗi cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(8,34,'8:34','Nguyên nhân gì mà có thể ngăn cản được hình phạt giáng lên chúng hay có lời giao ước gì mà khiến hình phạt không giáng xuống chúng trong khi chúng là kẻ ngông cuồng dám cấm cản thiên hạ bước vào Masjid Linh Thiêng tại Makkah để họ Tawwaf đi vòng K''abah hoặc dâng lễ nguyện Salah ở nơi đó? Và chúng là những người đa thần không được Ngài cho phép quản lý. Bởi vậy, Allah không cho ai cai quản cả ngoại trừ những người ngay chính biết kính sợ Ngài bằng cách tuân thủ mệnh lệnh của Ngài và kiềm hảm mình trước những điều Ngài nghiêm cấm, nhưng đa số bọn đa thần chúng không hề biết được điều đó trong khi đó chúng lại tranh giành nhau để bảo quản và rồi thật chất chúng không phải là người bảo quản.');
INSERT INTO "vn_mokhtasar" VALUES(8,35,'8:35','Thật quá đáng hơn là khi những tên đa thần chúng dâng lễ nguyện Salah tại Masjid Linh Thiên tại Makkah chỉ là tiếng huýt sao hay tiếng vỗ tay thì làm sao TA có thể giao cho các người bảo quản thánh địa Linh Thiêng Makkah này được? Thôi! - hỡi những tên đa thần - hãy nếm thử hình phạt bằng việc các ngươi bị giết chết và cả gia quyến của các ngươi vào ngày diễn ra trận chiến Badr bởi vì các ngươi dám bất tuân Allah và chống đối Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(8,36,'8:36','Quả thật, những kẻ bất tuân Allah chúng đã chi tiêu khối tài sản của chúng vào việc ngăn chặn thiên hạ đến với tôn giáo của Allah. Thế vậy chúng còn muốn chi tiêu vào những điều không chân chính. Để rồi sau đó chúng sẽ nhận lấy hậu quả của khối tài sản đó là sự nuối tiếc cho những gì mà chúng chi tiêu, rồi cuối cùng chúng chuốc lấy sự thua cuộc một cách nhục nhã trước những người có đức tin. Và những kẻ bất tuân Allah sẽ bị ném vào Hỏa Ngục ở Ngày Sau và chúng sẽ ở trong đó đời đời vĩnh viễn.');
INSERT INTO "vn_mokhtasar" VALUES(8,37,'8:37','Mục tiêu của những kẻ bất tuân là chi tiêu khối tài sản của chúng với ý định là đẩy lùi con đường của Allah để đến với lửa của Hỏa Ngục. Thế nhưng, Allah đã tách rời những kẻ bất tuân ô uế ra khỏi những người có đức tin tốt đẹp và Ngài chất chồng những việc làm của những kẻ chuyên dùng khối tài sản để làm những điều ô uế lên nhau thành trùng trùng điệp điệp sau đó Ngài gom lại rồi ném vào lửa của Hỏa Ngục và bọn chúng là những kẻ thua thiệt. Bởi vì bản thân chúng và gia quyến của chúng là những kẻ thua thiệt ở Ngày Sau.');
INSERT INTO "vn_mokhtasar" VALUES(8,38,'8:38','Hãy bảo chúng - hỡi Thiên Sứ - này những kẻ bất tuân với Allah và Thiên Sứ của Ngài mà từ thị dân của Ngươi: Nếu như chúng ngưng việc chống đối Allah và Thiên Sứ của Ngài rồi có ý định đi đến với con đường của Allah trong sự che trở của Ngài thì chắc chắn Allah sẽ tha thứ những tội lỗi đã qua của chúng. Bởi vì, nếu vào Islam thì Ngài sẽ xóa hết tội lỗi đã qua. Còn ngược lại, nếu như chúng muốn tái diễn là những kẻ chống đối với những gì đã từng là chân lý của Allah trong thời tổ tiên của chúng, là những kẻ ương ngạnh và liên tục chống đối thì chúng sẽ nhận lấy ngay cái hình phạt thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(8,39,'8:39','Hãy chiến đâu với chúng - hỡi những người có đức tin - khi chúng muốn làm kẻ chống đối cho đến khi nào chúng không còn trở thành là những kẻ tổ hợp Ngài và với ý định không muốn trở thành người Muslim thuần phục Allah. Và khi trở thành tín đồ là phải tuyệt đối tuân thủ theo Allah Đấng Độc Tôn không tổ hợp một ai khác cùng Ngài. Nếu như chúng chấm dứt là những kẻ chống đối mà đã đẩy y trở thành là kẻ tổ hợp thì bị đẩy lùi bởi sự kêu gọi đến với con đường của Allah. Quả thật, Allah Rất Am Tường về việc làm mà chúng đã làm không có gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(8,40,'8:40','Và nếu như chúng lật lộng những gì mà chúng đã từng ra lệnh là phải chấm dứt chống đối và đẩy lùi con đường của Allah. Bởi thế, nên biết - hỡi những người có đức tin - quả thật Allah sẽ giúp đỡ các ngươi chiến thắng chúng vì Ngài là Đấng Bảo Hộ Ưu Việt cho ai những ai được Ngài yểm trợ, Đấng Cứu Giúp Ưu Việt cho những ai mà Ngài muốn y chiến thắng và những ai có được Ngài yểm trợ luôn là chiến thắng và những ai được Ngài giúp đỡ chiến thắng.');
INSERT INTO "vn_mokhtasar" VALUES(8,41,'8:41','Các ngươi hãy biết rằng - hỡi những người có đức tin -những gì các ngươi giành lấy được từ những kẻ vô đức tin sau những cuộc Jihad vì chính nghĩa của Allah, các ngươi hãy chia tất cả thành năm phần đều nhau, lấy bốn phần chia cho những người lính tham gia Jihad; một phần còn lại chia thành năm phân nhỏ, một phần cho Allah và cho Thiên Sứ của Ngài để sử dụng mục đích chung của cộng đồng Islam, một phần cho gia quyến của Thiên Sứ thuộc hai dòng tộc Hashim và Al-Muttalib, một phần cho trẻ mồ côi, một phần cho người nghèo và người khó khăn, và một phần cho người lỡ đường, nếu các ngươi đã thực sự tin nơi Allah và những gì được TA thiên khải cho Nô Lệ của TA Muhammad vào ngày Badr, đó là Ngày Allah đã phân loại rõ ràng giữa chân lý và ngụy tạo khi Ngài phù hộ các ngươi chiến thắng được kẻ thù của các ngươi. Và Allah, Đấng đã giúp các ngươi chiến thắng là Đấng toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(8,42,'8:42','Các ngươi hãy nhớ lại khi các ngươi ở bên đây thung lũng gần với Madinah, và nhóm đa thần ở bên kia thung lũng gần với Makkah hơn, còn đoàn lạc đà chở hàng hóa của chúng thì ở bên dưới các ngươi gần bờ biển đỏ, nếu như các ngươi hẹn gặp trước với những kẻ đa thần giao tranh tại Badr thì chắc chắn nội bội của các ngươi sẽ nảy sinh mâu thuẫn. Tuy nhiên, Đấng Toàn Năng đã sắp đặt cuộc gặp mặt không hẹn trước tại Badr để hoàn thành lời hứa mà Ngài đã hứa về việc sẽ phù hộ những người có đức tin giành chiến thắng và tiêu diệt những kẻ vô đức tin, để Ngài nâng cao tôn giáo của Ngài và hạ nhục sự đa thần; và để cho những tên bị giết trong chúng cúi đầu phủ phục khi biết sự chiến thắng được ban cho những người có đức tin mặc dù số lượng và quân lực của họ ít hơn và để cho người còn sống nhận thức rõ chứng cứ mà Allah đã trình bày cho họ thấy và để không còn bất cứ ai viện lý do trước Allah. Bởi quả thật Allah nghe rõ mọi lời nói của tất cả, Ngài thông toàn mọi hành động của họ, không có bất cứ thứ gì có thể che giấu được Ngài và Ngài sẽ ban thưởng hoặc trừng phạt cho họ trên những lời nói và việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(8,43,'8:43','Ngươi hãy nhớ lại - hỡi Thiên Sứ - về hồng ân mà Allah đã ban cho Ngươi và cho những người có đức tin khi Ngài cho Ngươi nằm mộng thấy lực lượng ít ỏi của quân địch, Ngươi liền loan báo tin đó cho những người có đức tín khiến họ phấn chấn tinh thần, họ vui mừng về tin tốt, họ trở nên mạnh mẽ và kiên quyết đối mặt với kẻ thù. Nếu như Đấng Toàn Năng cho Ngươi thấy lực lượng hùng mạnh của kẻ thù trong giấc mộng thì chắc chắn Ngươi đã gây hoang mang cho các bạn đạo của Ngươi khiến họ trở nên sợ hãi cho cuộc giao chiến. Nhưng Allah đã trấn an điều đó, Ngài đã củng cố tinh thần của họ bằng cách làm cho lực lượng của kẻ thù trở thành nhỏ bé và ít ỏi trong mắt của vị Thiên Sứ của Ngài. Quả thật Alalh là Đấng hằng biết những gì xua tan nỗi sợ hãi trong trái tim và những gì ẩn khuất trong lòng.');
INSERT INTO "vn_mokhtasar" VALUES(8,44,'8:44','Và các ngươi hãy nhớ lại - hỡi những người có đức tin - khi Allah cho các ngươi nhìn thấy phe đa thần chỉ là một lực lượng nhỏ lúc giáp mặt với chúng khiến các ngươi bạo gan tiến lên chiến đấu với chúng. Ngược lại, Ngài cũng đã làm cho các ngươi thành một lực lượng ít ỏi trong mắt của chúng để chúng không ngại tiến lên chiến đấu với các ngươi, khiến họ không kịp nghĩ đến việc phải quay về để Allah hoàn thành kế hoạch trước đây của Ngài về việc tiêu diệt những người đa thần và bắt chúng làm tù binh đồng thời ban chiến thắng vẻ vang cho những người có đức tin trước kẻ thù. Rồi đây, tất cả mọi việc đều trở về trình diện một mình Allah để Ngài sẽ thưởng phạt, người làm tốt được điều tốt và người làm xấu sẽ bị điều xấu.');
INSERT INTO "vn_mokhtasar" VALUES(8,45,'8:45','Hỡi những người có đức tin nơi Allah và tuân theo Thiên Sứ của Ngài, khi các ngươi chạm trán với lực lượng của những kẻ vô đức tin thì các hãy vững bước lúc giáp mặt giao chiến, các ngươi chớ hèn nhát, các ngươi hãy tụng niệm Allah thật nhiều và cầu xin Ngài, bởi Ngài là Đấng thừa khả năng ban chiến thắng cho các ngươi, Ngài là Đấng để các ngươi hi vọng đạt được điều các ngươi mong muốn và để các ngươi hy vọng được bảo vệ tránh khỏi những điều các ngươi lo lắng.');
INSERT INTO "vn_mokhtasar" VALUES(8,46,'8:46','Và các ngươi hãy phục tùng mệnh lệnh của Allah và phục tùng mệnh lệnh của Thiên Sứ của Ngài trong lời nói lẫn hành động và trong mọi hoàn cảnh của các ngươi, các ngươi chớ cố ý không tán thành ý kiến của nhau, bởi sự bất đồng ý kiến là nguyên nhân khiến lực lượng yếu đi, mất đi sức mạnh đoàn kết và trở nên nhát gan, các ngươi hãy kiên trì khi chạm mặt kẻ thù. Quả thật Allah luôn bên cạnh những người kiên nhẫn, Ngài sẽ luôn phù hộ họ và giúp đỡ họ, và ai được Allah bên cạnh thì y chắc chắn sẽ chiến thắng.');
INSERT INTO "vn_mokhtasar" VALUES(8,47,'8:47','Và các ngươi chớ như nhóm người đa thần đã xuất binh khỏi Makkah với tinh thần ngạo mạn, khoe khoang, phô diễn sức mạnh trước thiên hạ và ý định ngăn cản thiên hạ gia nhập tôn giáo của Allah, trong khi Ngài bao trùm mọi hành động của chúng, không có việc làm nào của chúng có thể che giấu được Ngài và Ngài sẽ trừng phạt chúng thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(8,48,'8:48','Và các ngươi hãy nhớ - hỡi những người có đức tin - về hồng ân mà Allah đã ban cho các ngươi khi Shaytan trang hoàng khiến những kẻ đa thần thấy việc làm của chúng tốt đẹp, hắn động viên chúng đối đầu chiến đấu với các tín đồ Muslim, hắn bảo: "Ngày hôm nay sẽ không ai có thể địch nổi các người đâu, ta sẽ hỗ trợ các người, bảo vệ các người khỏi kẻ thù của các người." Nhưng khi hai phe đối đầu nhau: Phe những người có đức tin được sự trợ lực từ đội ngũ Thiên Thần còn những kẻ đa thần được sự trợ lực từ Shaytan. Nhưng tên Shaytan quay lưng bỏ chạy và nói với những người đa thần: Quả thật, ta vô can với các người, vì ta nhìn thấy Thiên Thần đến hỗ trợ những người có đức tin, ta sợ sẽ bị Allah tiêu diệt, bởi Allah rất nghiêm khắc trong hình phạt và không ai có thể chịu đựng nổi hình phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(8,49,'8:49','Và các ngươi hãy nhớ lại khi nhóm người đạo đức giả và nhóm người có niềm tin yếu ớt đã nói: "Những kẻ Muslim này đã bị chính tôn giáo của chúng lường gạt rồi, chúng được hứa hẹn sẽ giành chiến thắng trước kẻ thù của chúng trong khi số lượng của chúng thì ít ỏi và yếu còn lực lượng kẻ thù của chúng thì đông và hùng mạnh." Tuy nhiên, những kẻ này nào biết rằng nếu ai đã nương tựa vào riêng một mình Allah, một lòng tin vào lời hứa ban cho chiến thắng của Ngài thì quả thật Ngài sẽ ban cho họ chiến thắng, Ngài không bao giờ bội ước cho dù lực lượng của họ có yếu ra sao, bởi Allah quyền năng, không có gì chiến thắng được Ngài, Ngài sáng suốt trong sự định đoạt và ban hành luật.');
INSERT INTO "vn_mokhtasar" VALUES(8,50,'8:50','Và nếu Ngươi - hỡi Thiên Sứ - chứng kiến cảnh nhóm người phủ nhận Allah và Thiên Sứ của Ngài, khi chúng bị các Thiên Thần giật mạnh linh hồn chúng ra, các Thiên Thần đánh vào mặt chúng khi chúng tiến đến và đánh vào lưng chúng khi chúng quay lưng bỏ chạy. Các Thiên Thần quát chúng: "Các ngươi - hỡi những kẻ vô đức tin - hãy nếm lấy hình phạt bị thiêu đốt!" Giá như Ngươi (Muhammad) thấy được cảnh tượng khủng khiếp đó.');
INSERT INTO "vn_mokhtasar" VALUES(8,51,'8:51','Đó là hình phạt đau đớn khi rút hồn các ngươi - hỡi những người vô đức tin - và hình phạt bị thiêu đốt sẽ ở trong mộ của các ngươi và ở Đời Sau. Đó là do những gì mà đôi tay của các ngươi đã tạo ra ở trần gian, bởi Allah không hề bất công với thiên hạ, Ngài phán xét họ bằng lẽ công bằng bởi Ngài là Đấng Sáng Suốt, Công Minh.');
INSERT INTO "vn_mokhtasar" VALUES(8,52,'8:52','Sự trừng phạt giáng xuống đám người vô đức tin này không phải là sự trừng phạt riêng đối với chúng mà đó là qui luật của Allah trong việc đối xử với đám người vô đức tin trong mọi thời đại và mọi nơi. Quả thật, Ngài đã trừng phạt đám thuộc hạ của Fir''awn và những cộng đồng thời trước khi chúng đã phủ nhận các lời mặc khải của Ngài. Allah đã túm bắt lấy chúng bởi tội lỗi mà chúng đã phạm và giáng lên chúng sự trừng phạt. Quả thật Allah là Đấng hùng mạnh nhất, không có gì cưỡng lại được sức mạnh của Ngài, và Ngài rất nghiêm khắc trong việc trừng phạt đối với ai bất tuân Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(8,53,'8:53','Sở dĩ hành phạt khủng khiếp như thế là vì một khi Allah đã ban ân huệ cho bất cứ nhóm người nào thì Ngài không bao giờ rút ân huệ đó lại cho đến khi bản thân họ thay đổi từ tình trạng có đức tin Iman, ngoan đạo, và biết tri ân trở thành kẻ xấu xa, phủ nhận Allah, bất tuân Ngài và vong ơn. Quả thật Allah nghe rõ mọi lời nói của nô lệ Ngài và biết rõ mọi hành động của họ, không có gì che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(8,54,'8:54','Sự việc của những người vô đức tin này giống như sự việc của những người khác thuộc những kẻ phủ nhận Allah, điển hình như đồng bọn của Fir''awn và những cộng đồng vô đức tin trước chúng, tất cả đều phủ nhận các lời mặc khải của Thượng Đế nên chúng đã bị Allah tiêu diệt toàn bộ bởi tội lỗi mà chúng đã gây ra và Allah đã tiêu diệt đồng bọn của Fir''awn bằng cách nhấn chúng chết chìm trong biển. Tất cả, đồng bọn của Fir''awn và những cộng đồng thời trước đều là những kẻ sai quấy vì đã phủ nhận Allah và thờ phượng các thần linh ngụy tạo ngoài Ngài, thế nên chúng đáng bị Đấng Toàn Năng trừng phạt thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(8,55,'8:55','Chắc chắn loài động vật tồi tệ nhất đi trên trái đất này là những ai phủ nhận Allah và Thiên Sứ của Ngài, họ sẽ chẳng có đức tin Iman cho dù có mang đến cho họ mọi lời mặc khải, vì họ vẫn muốn ngoan cố trên sự vô đức tin của họ. Quả thật, họ đã được gởi đến biết bao phương tiện hướng dẫn từ tâm trí, thính giác và thị giác.');
INSERT INTO "vn_mokhtasar" VALUES(8,56,'8:56','Trong số những kẻ mà Ngươi - Muhammad - đã giao ước và cam kết với chúng - chẳng hạn như bộ tộc Quraizhah - nhưng rồi chúng lại đơn phương hủy ước hết lần này đến lần khác mà không hề biết sợ Allah. Chúng thực sự đã không giữ tròn lời giao ước của chúng và cũng không hề tuân theo cam kết mà chúng đã thỏa hiệp.');
INSERT INTO "vn_mokhtasar" VALUES(8,57,'8:57','Nếu Ngươi - hỡi Thiên Sứ - có bắt gặp những kẻ đơn phương hủy giao ước trong chiến tranh này đây thì Ngươi hãy trừng phạt chúng bằng cực hình để cho những kẻ khác thấy đó mà làm bài học, hi vọng sẽ không ai còn dám bội ước hoặc gây chiến với Ngươi nữa.');
INSERT INTO "vn_mokhtasar" VALUES(8,58,'8:58','Còn nếu Ngươi - hỡi Thiên Sứ - sợ đám người nào đó gian lận và đơn phương hủy giao ước, Ngươi hãy tuyên bố hủy giao ước với chúng để cả hai phe đều biết rõ sự việc, để chúng không ngỡ ngàng khi Ngươi tấn công chúng. Nếu Ngươi tấn công chúng trước khi thông báo hủy giao ước thì Ngươi chính là kẻ lừa dối, bởi Allah không hề yêu thương đám người lừa dối. Do đó, Ngươi hãy cẩn trọng đừng để mình là kẻ gian lận và lừa dối.');
INSERT INTO "vn_mokhtasar" VALUES(8,59,'8:59','Và Ngươi chớ nghĩ rằng những kẻ phủ nhận Allah đã thoát khỏi hình của Ngài và chúng sẽ được bình an. Không bao giờ, chắc chắn chúng sẽ bị trừng phạt thích đáng, chắc chắn hình phạt sẽ giáp mặt chúng.');
INSERT INTO "vn_mokhtasar" VALUES(8,60,'8:60','Và các ngươi - hỡi những người có đức tin - hãy tận dụng hết khả năng mà chuẩn bị đầy đủ lực lượng và vũ khí như cung tên cũng như những con chiến mã để tạo nỗi khiếp sợ trong lòng kẻ thù của Allah, kẻ thù của các ngươi trong nhóm người ngoại đạo đã bao vây các ngươi và để hù dọa đám người khác. Quả thật, các ngươi không biết về việc chúng đang ôm hận trong lòng muốn tấn công các ngươi nhưng Allah biết rất rõ về tấm lòng của chúng. Và bất cứ thứ gì dù nhiều hay ít mà các ngươi đã chi dùng vì Allah ở trần gian chắc chắn sẽ được Allah bù đáp lại đầy đủ không thiếu bất cứ phần thưởng nào ở Đời Sau. Thế nên các ngươi hãy cố gắng mà chi dùng cho con đường chính nghĩa của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(8,61,'8:61','Và nếu bọn chúng muốn nghị hòa chấm dứt chiến tranh thì Ngươi - hỡi Thiên Sứ - hãy nghị hòa mà ký giao ước với chúng; và các ngươi hãy phó mặc và tin nơi Allah, quả thật Ngài nghe rõ lời lẽ của chúng và biết rõ suy nghĩ lẫn hành động của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(8,62,'8:62','Và nếu như họ muốn lợi dụng việc nghị hòa và việc chấm dứt chiến tranh để đánh lừa Ngươi - hỡi Thiên Sứ -mục đích thừa cơ hội chuẩn bị tấn công Ngươi thì quả thật Allah thừa sức giúp Ngươi thoát khỏi kế hoạch của họ, Ngài ủng hộ Ngươi bằng sự phù hộ của Ngài và làm cho Ngươi trở nên mạnh lên qua việc phù hộ cho những người có đức tin trong số những người Muhajir và Ansar.');
INSERT INTO "vn_mokhtasar" VALUES(8,63,'8:63','Và Ngài đã hòa hợp tấm lòng của những người có đức tin, những người mà họ đã giúp đỡ Ngươi, sau khi họ đã từng chia rẽ nhau. Cho dù Ngươi có chi dùng tất cả tài sản có được trên trái đất để thắt chặt những con tim của họ lại với nhau thì Ngươi không tài nào làm được, chỉ duy nhất một mình Allah mới có thể hòa hợp tấm lòng của họ lại với nhau được thôi. Quả thật, Ngài Toàn Năng trong vương quyền của Ngài, không ai có thể kháng lại Ngài, và Ngài là Đấng Sáng Suốt trong định đoạt, điều hành và ban luật.');
INSERT INTO "vn_mokhtasar" VALUES(8,64,'8:64','Hỡi Nabi! Quả thật, Allah thừa sức bảo vệ Ngươi khỏi sự hãm hại từ kẻ thù của Ngươi, và luôn cả những người có đức tin đi theo Ngươi. Cho nên, Ngươi hãy tin nơi Allah và hãy nương tựa vào Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(8,65,'8:65','Hỡi Nabi! Ngươi hãy động viên những người có đức tin tham gia chinh chiến, hãy khích lệ tinh thần và ý chí của họ rằng nếu lực lượng các ngươi - hỡi những người có đức tin - gồm hai mươi người kiên cường sẽ chiến thắng được đoàn quân hai trăm người của nhóm người vô đức tin, và nếu các ngươi gồm một trăm người kiên cường sẽ chiến thắng được đoàn quân một ngàn người của nhóm người vô đức tin. Sở dĩ được thế là vì nhóm người vô đức tin không hiểu được qui luật của Allah rằng Ngài luôn phù hộ những người thuộc phe của Ngài cũng như họ không nhận thức được mục đích của chiến tranh bởi họ chỉ vì lợi nhuận của trần gian mà xả thân.');
INSERT INTO "vn_mokhtasar" VALUES(8,66,'8:66','Giờ đây, Allah đã giảm nhẹ cho các ngươi - hỡi những người có đức tin - vì Ngài biết rõ sự yếu thế của các ngươi, Ngài giảm nhẹ là đối xử tế nhị với các ngươi, Ngài bắt buộc các ngươi cứ mỗi một người phải kiên cường đối đầu với hai người thay vì mười người, nếu các ngươi có lực lượng một trăm người kiên cường chắc chắc sẽ chiến thắng được hai trăm người, nếu lực lượng các ngươi được một ngàn người kiên cường chắc chắn sẽ chiến thắng được hai ngàn người theo lệnh của Allah, và Allah luôn sát cánh cùng những người có đức tin kiên cường, Ngài luôn phù hộ và trợ giúp họ.');
INSERT INTO "vn_mokhtasar" VALUES(8,67,'8:67','Một vị Nabi không nên giữ lại những tù binh vô đức tin đã tham chiến mà cần phải giết sạch tất cả để gieo nỗi hoảng sợ vào lòng của chúng mục đích để chúng không dám tái chiến. Các ngươi - hỡi những người co đức tin - ham muốn lấy tù binh Badr để đổi lấy tiền chuộc trong khi Allah lại muốn cuộc sống ở Đời Sau để vinh danh tôn giáo của Ngài bằng sự chiến thắng, và Allah Toàn Năng trong bản chất và các thuộc tính và quyền bính của Ngài, không có gì kháng được Ngài; và Ngài luôn sáng suốt trong sắp xếp và định đoạt.');
INSERT INTO "vn_mokhtasar" VALUES(8,68,'8:68','Nếu như quyển sách ghi chép tiền định của Allah trước đây đã định cho phép các ngươi sử dụng chiến lợi phẩm và cho tù binh chuộc mạng là các ngươi đã phải nếm cực hình cả rồi vì các ngươi dám cho tù binh chuộc mạng bằng tài sản trước khi lời mặc khải về điều đó được thiên khải xuống.');
INSERT INTO "vn_mokhtasar" VALUES(8,69,'8:69','Vì vậy các ngươi - hỡi những người có đức tin - hãy ăn uống những thứ mà các ngươi đã thu được trong chiến tranh từ những người vô đức tin. Nó hợp pháp cho các ngươi sử dụng và các ngươi hãy kính sợ Allah mà tuân thủ theo sắc lệnh của Ngài, quả thật Allah luôn tha thứ cho những bề tôi có đức tin và luôn khoan dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(8,70,'8:70','Hỡi Nabi! Ngươi hãy bảo những tù binh đa thần mà các ngươi đã bắt giữ trong trận chiến Badr: Nếu như Allah biết được trong lòng các ngươi có định tâm tốt đẹp và cải thiện tâm niệm thì Ngài sẽ ban cho các ngươi thứ khác tốt hơn những thứ mà các ngươi đã dùng chuộc mạng mình. Cho nên các ngươi chớ buồn rầu về những thứ đã bị lấy đi và Ngài sẽ tha thứ cho các ngươi, quả thật Allah hằng tha thứ cho những ai biết sám hối trong đám nô lệ của Ngài, Ngài sẽ khoan dung với họ, điển hình như lời hứa mà Allah đã hứa với Al-''Abbaas bác của Thiên Sứ và những người khác đã gia nhập vào Islam.');
INSERT INTO "vn_mokhtasar" VALUES(8,71,'8:71','Và nếu chúng có mưu đồ lừa Ngươi - hỡi Muhammad - qua lời lẽ ngon ngọt trước mặt ngươi thì quả thật chúng đã từng lừa Allah trước đây và quả thật Allah đã phù hộ Ngươi chiến thắng được chúng, khiến bọn chúng có số bị giết và số khác bị bắt làm tù binh. Chúng hãy chờ kết quả như thế nếu chúng tái diễn. Và Allah biết tận tường về các tạo vật của Ngài, Ngài biết rõ điều gì cải thiện họ, Ngài rất sáng suốt trong quản lý và điều hành.');
INSERT INTO "vn_mokhtasar" VALUES(8,72,'8:72','Quả thật, những ai đã có đức tin nơi Allah, đã thật lòng với Thiên Sứ của Ngài, tuân thủ đúng theo luật lệ của Ngài, đã di cư từ vùng đất vô đức tin đến với vùng đất Islam hoặc đi đến nơi được tự do thờ phượng Allah cùng với nhóm người có đức tin và đã đấu tranh bằng tài sản lẫn cả sinh mạng của mình vì nêu cao lời tuyên thệ và với những ai đã tương trợ, đã đùm bọc, đã giúp đỡ họ, thì hai nhóm người họ - dân di cư và dân tương trợ - là một tập thể bảo hộ của nhau. Còn những ai chỉ có tin nơi Allah nhưng chưa di cư như nhóm người đã di cư thì đối với các ngươi - hỡi những người có đức tin - không có bổn phận tương trợ họ và bảo vệ họ cho đến khi họ di cư vì chính nghĩa của Allah. Nhưng nếu họ bị đám người vô đức tin đàn áp và họ yêu cầu các ngươi viện trợ họ thì các ngươi hãy tương trợ họ đánh đuổi kẻ thù, ngoại trừ giữa các ngươi và kẻ thù của họ có giao ước đã ký kết thì các ngươi không được bội ước, quả thật Allah thấy rõ mọi điều các ngươi làm, không có gì che giấu được Ngài và Ngài sẽ thưởng phạt các ngươi theo các việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(8,73,'8:73','Và những ai vô đức tin nơi Allah thì sự vô đưc tin đó sẽ tập hợp họ lại với nhau, họ là đồng minh của nhau, họ sẽ giúp đỡ lẫn nhau. Cho nên, người có đức tin không là của họ, nếu các ngươi không là đồng minh của những người có đức tin và đối đầu với những người vô đức tin thì đó sẽ là một thử thách lớn cho những người có đức tin vì họ không tìm thấy sự giúp đỡ và tương trợ từ ai trong đám anh em đồng đạo họ. Điều đó sẽ khiến cho sự dấy loạn lan tràn khắp nơi ngăn cản con đường chính nghĩa của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(8,74,'8:74','Và những ai đã tin nơi Allah, đã di cư vì con đường của Ngài cùng với những ai tương trợ và giúp đỡ những họ, tất cả đều là những người thực sự có đức tin ngay chính, và phần thưởng dành cho họ từ Allah là sự tha thứ về tội lỗi đã phạm và bổng lộc dồi dào ở nơi Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(8,75,'8:75','Và những ai có đức tin sau những người Muhajirin và Ansar, đã di cư từ vùng đất vô đức tin đến với vùng đất Islam và cũng đã đấu tranh vì chính nghĩa của Allah để giơ cao lời tuyên thệ của Allah và hạ thấp tiếng nói của những người vô đức tin thì họ là đồng minh của các ngươi - hỡi những người có đức tin - họ và các ngươi đều được hưởng quyền lợi và nghĩa vụ như nhau. Và những người có huyết thống họ hàng được ưu tiên trong vấn đề thừa kế hơn những người chỉ là anh em đồng đạo của nhau như đã từng có trước đây. Quả thật, Allah tinh thông tất cả mọi việc, không có gì giấu được Ngài, Ngài biết rõ điều gì tốt đẹp cho đám nô lệ của Ngài nên đã qui định nó cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,1,'9:1','Đây là lời tuyên bố hủy bỏ thỏa hiệp của Allah và Thiên Sứ của Ngài và là thông báo cuối cùng về những cuộc giao ước mà các ngươi - hỡi tín đồ Muslim - đã ký kết với người đa thần trong bán đảo Ả-rập.');
INSERT INTO "vn_mokhtasar" VALUES(9,2,'9:2','Các ngươi - hỡi những người đa thần - sẽ được tự do và bình an đi lại trong thời gian bốn tháng. Sau đó các ngươi sẽ không còn được bất cứ giao ước nào nữa và các ngươi hãy thức tỉnh đi rằng các ngươi sẽ không thoát khỏi hình phạt của Allah nếu các ngươi hãy còn tiếp tục trên sự vô đức tin nơi Ngài. Các ngươi hãy tin chắc rằng Allah sẽ hạ nhục những kẻ vô đức tin bằng sự giết chóc và tù đày ở trần gian và tiếp tục bị đày vào Hỏa Ngục ở Đời Sau. Và lệnh hủy bỏ này có hiệu lực với những ai đã phạm giao ước và những ai giao ước không giới hạn, riêng những ai giao ước có giới hạn hơn bốn tháng thì hãy duy trì giao ước đó cho đến thời gian đã ký kết.');
INSERT INTO "vn_mokhtasar" VALUES(9,3,'9:3','Một thông báo từ Allah và Thiên Sứ của Ngài gởi đến toàn thể những ai đang tập trung trong ngày Đại Lễ ''Eid giết tế rằng Allah Toàn Năng đoạn tuyệt quan hệ với người đa thần và Thiên Sứ của Ngài cũng thế. Nếu các ngươi - hỡi những người đa thần - biết hối cải về tội đa thần thì đó là điều tốt đẹp cho các ngươi, còn nếu các ngươi không chịu hối cải thì các ngươi hãy biết rằng các ngươi sẽ không thoát khỏi Allah, các ngươi sẽ không tránh được hình phạt của Ngài. Và Ngươi - hỡi Thiên Sứ - hãy thông báo với những kẻ đã phủ nhận Allah rằng chúng sẽ phải đón nhận một hình phạt đau đớn đang chờ đón họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,4,'9:4','Ngoại trừ những người đa thần nào giữ đúng giao ước với các ngươi, họ không phạm bất cứ điều gì từ giao ước, họ không phải trong số những người bị kết tội trước đó thì các ngươi hãy tiếp tục giữ tròn giao ước với họ cho đến hết thời hạn đã ký kết. Quả thật, Allah yêu thương nhóm người biết kính sợ mà tuân thủ đúng luật lệ của Ngài, trong đó có việc giữ tròn chữ tín, và tránh xa mọi điều Ngài cấm, trong đó gồm sự lường gạt.');
INSERT INTO "vn_mokhtasar" VALUES(9,5,'9:5','Cho đến khi những tháng cấm kỵ mà các ngươi đã hứa cho người đa thần được tự do và bình an chấm dứt thì các ngươi hãy tiêu diệt họ ở bất cứ nơi đâu mà các ngươi bắt gặp họ. Các ngươi hãy vây hãm họ, tấn công họ trên mọi nẻo đường. Nếu như họ biết sám hối với Allah về tội đa thần, chịu dâng lễ nguyện Salah và xuất Zakah từ tài sản của họ thì họ đã trở thành anh em đồng đạo của các ngươi trong Islam, các ngươi hãy dừng ngay việc đánh chiến với họ. Quả thật Allah luôn tha thứ cho những ai biết ăn năn sám hối trong đám nô lệ của Ngài và luôn khoan dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,6,'9:6','Và khi có người đa thần nào đã được phép xâm hại đến y lẫn tài sản của y tìm đến Ngươi - hỡi Thiên Sứ - xin được ở bên cạnh Ngươi thì Ngươi hãy chấp nhận thỉnh cầu đó để y có cơ hội lắng nghe Qur''an, sau đó Ngươi hãy đưa y đến nơi an toàn. Bởi lẽ người ngoại đạo không hiểu biết sự thật về Islam, nên khi họ nghe được Qur''an biết đâu họ sẽ được hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(9,7,'9:7','Người đa thần không có thẩm quyền giao ước nghị hòa với Allah và Thiên Sứ của Ngài, ngoại trừ những giao ước đã ký với các ngươi - hỡi các đồ Muslim - tại Masjid Al-Haram (Makkah) trong lần nghị hòa Al-Hudaibiyah. Một khi họ vẫn duy trì tốt bản nghị hòa đó với các ngươi thì các ngươi hãy hoàn thành bổn phận với họ, các ngươi chớ có bội ước, quả thật Allah luôn yêu thương người ngay thẳng, những người mà họ chấp hành các mệnh lệnh của Ngài của Ngài và tránh xa những điều cấm của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(9,8,'9:8','Sao các ngươi lại nghị hòa với kẻ thù của các ngươi như thế? Trong khi chúng muốn chiến thắng các ngươi, chúng không hề tôn trọng các ngươi bao giờ, vả lại giữa các ngươi và chúng không hề có quan hệ họ hàng hay ràng buộc giao ước nào cả, đúng hơn là chúng muốn trừng phạt các ngươi bằng mọi cách có thể. Chúng nói chuyện mật ngọt chẳng qua để dụ các ngươi mà thôi chứ trong lòng của chúng không phải vậy đâu. Các ngươi chớ tin vào lời lẽ của chúng và đa số bọn chúng đã rời xa sự phục tùng Allah nhằm cố hủy bỏ giao ước.');
INSERT INTO "vn_mokhtasar" VALUES(9,9,'9:9','Chúng lấy việc đi theo các lời phán của Allah mang sắc lệnh thực hiện đúng giao ước để đổi những thứ rẻ mạt của trần gian nhằm thỏa mãn ham muốn và dục vọng của chúng. Chúng ngăn cản bản thân chúng đi theo chân lý, chúng ngoảnh mặt với chân lý và còn cản trở người khác đến với chân lý. Thật tồi tệ và xấu xa cho điều chúng làm.');
INSERT INTO "vn_mokhtasar" VALUES(9,10,'9:10','Chúng chưa từng tôn trọng Allah hay mối quan hệ họ hàng cũng như giao ước nghị hòa với những người có đức tin. Chúng luôn nung nóng hận thù và cố tình vượt giới luật của Allah; bởi bản chất của chúng vốn bất công và thù hằn.');
INSERT INTO "vn_mokhtasar" VALUES(9,11,'9:11','Nếu như chúng biết hối cải về tội vô đức tin của mình, chịu tuyên thệ hai câu Shahadah, dâng lễ nguyện Salah, xuất Zakah từ tài sản của chúng thì chúng đã trở thành tín đồ Muslim và chúng là anh em đồng đạo với các ngươi, chúng có quyền lợi và nghĩa vụ như các ngươi, và các ngươi không được xâm phạm đến chúng, bởi Islam bảo vệ sinh mạng, tài sản và danh dự của chúng. Và TA (Allah) đã trình bày rõ các câu Kinh cho nhóm người có kiến thức bởi điều đó sẽ mang lại hữu ích cho họ và cho những người khác.');
INSERT INTO "vn_mokhtasar" VALUES(9,12,'9:12','Và nếu những kẻ đa thần này đơn phương vi phạm giao ước chấm dứt chiến tranh đã ký kết với các ngươi và xúc phạm đến tôn giáo của các ngươi thì các ngươi hãy đánh toàn thể lãnh đạo vô đức tin đó, chúng chẳng còn bất cứ giao ước hay thỏa hiệp nào với các ngươi nữa. Hy vọng sau cuộc đánh này chúng biết dừng lại hành động vô đức tin của chúng cũng như về lỗi vì phạm giao ước và chúng biết tôn trọng tôn giáo.');
INSERT INTO "vn_mokhtasar" VALUES(9,13,'9:13','Tại sao các ngươi - hỡi những người có đức tin - vẫn không chịu đánh những kẻ đã từng bội ước với các ngươi, chúng đã từng hội nghị bàn bạc cách trục xuất Thiên Sứ ra khỏi Makkah và chính chúng đã chủ động tấn công sát hại các ngươi trước, phải chăng các ngươi sợ bọn chúng mà không dám giao chiến với chúng? Đáng lẽ ra Allah mới xứng đáng để các ngươi kính sợ nếu các ngươi thật sự là người có đức tin Iman.');
INSERT INTO "vn_mokhtasar" VALUES(9,14,'9:14','Các ngươi - hỡi những người có đức tin - hãy đánh những kẻ đa thần này. Nếu các ngươi tấn công chúng, Allah sẽ trừng phạt chúng bằng chính đôi tay của các ngươi, đó là các ngươi sẽ giết chúng, hạ nhục chúng, và bắt chúng làm tù bình. Ngài sẽ phù hộ các ngươi chiến thắng được chúng và Ngài sẽ chữa lành nỗi đau trong lòng của nhứng người có đức tin không tham chiến chống lại kẻ thù, không có cơ hội chứng kiến cảnh kẻ thù bị thất bại và bị bắt làm tù binh.');
INSERT INTO "vn_mokhtasar" VALUES(9,15,'9:15','Allah sẽ xua tan cơn thịnh nộ trong lòng đám nô lệ có đức tin của Ngài bằng sự chiến thắng mà họ đạt được, rồi Ngài sẽ chấp nhận sự sám hối của bất cứ ai Ngài muốn trong số những người chống đối nếu họ biết ăn năn hối cải như đã xảy ra với một số người ở Makkah trong ngày chinh phục giành chiến thắng. Quả thật Allah biết rõ ai thật lòng sám hối và Ngài sáng suốt trong việc tạo hóa, quản lý, ban hành luật.');
INSERT INTO "vn_mokhtasar" VALUES(9,16,'9:16','Lẽ nào các ngươi - hỡi những người có đức tin - cho rằng Allah sẽ không thử thách các ngươi ư? Trong khi thử thách là qui luật của Ngài, Ngài sẽ thử thách các ngươi để phân loại rõ ràng ai là nô lệ kiên cường chiến đấu vì Allah, ai là những chỉ chọn lấy Allah, Thiên Sứ của Ngài cũng như những người có đức tin làm đồng minh và không quan hệ thân thiết với những kẻ vô đức tin. Quả thật Allah luôn am tường mọi việc các ngươi làm, không có gì che giấu được Ngài và Ngài se dựa vào đó để thưởng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(9,17,'9:17','Không thể chấp nhận việc để người đa thần quán xuyến các Masjid của Allah nơi để thờ phượng Ngài trong khi chúng hài lòng cho bản thân mình trên tình trạng vô đức tin qua các thể hiện bên ngoài của chúng. Mọi hành động thiện tốt của nhóm người đó đã bị hủy bỏ, không được chấp nhận vì chúng không có đức tin Iman và vào Ngày Phán Xét chúng sẽ phải vào Hỏa Ngục mà sống muôn đời ngoại trừ ai đã hối cải về tội đa thần của mình trước khi chết.');
INSERT INTO "vn_mokhtasar" VALUES(9,18,'9:18','Người xứng đáng quán xuyến các Masjid của Allah phải là những người có đức tin nơi một mình Allah và không đồng đẳng bất cứ ai cùng Ngài, tin nơi Đời Sau, hành lễ nguyện Salah, xuất Zakah và không sợ bất cứ ai ngoại trừ Allah Toàn Năng. Nhóm người này mới là những người thuộc nhóm người được hướng dẫn đến con đường chính đạo, còn những kẻ đa thần thì ở rất xa với điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(9,19,'9:19','Hay là các ngươi - hỡi những người đa thần - xem việc phục vụ nước cho người hành hương Hajj và việc quán xuyến Masjid Al-Haram (ở Makkah) giống như ai có đức tin nơi Allah, không thờ phượng bất cứ ai (vật) cùng với Ngài, tin nơi Ngày Phán Xét, kiên cường đấu tranh bằng tài sản lẫn sinh mạng của mình để giơ cao lời phán của Allah và hạ thấp lời của những kẻ vô đức tin; các ngươi cho rằng cả hai ngang bằng nhau trước Allah sao? Đối với Allah, họ không hề ngang nhau và Allah không hề phù hộ những kẻ làm điều sai quấy qua hành động Shirk cho dù chúng có làm bao điều tốt đẹp như phục vụ nước cho người hành hương Hajj.');
INSERT INTO "vn_mokhtasar" VALUES(9,20,'9:20','Những ai tập hợp giữa đức tin Iman nơi Allah, di cư từ vùng đất vô đức tin đến vùng đất Islam và kiên cường chiến đấu vì chính nghĩa của Allah bằng tài sản và sinh của mình se đạt được địa vị cao hơn những ai khác nơi Allah, họ là những người chiến thắng giành được Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(9,21,'9:21','Họ được Allah báo cho biết tin mừng về lòng thương xót của Ngài, về sự hài lòng của Ngài đối với họ và Ngài sẽ không bao giờ phẫn nộ với họ, và họ được thu nhận vào Thiên Đàng, nơi mà họ sẽ hưởng được niềm hạnh phúc và sự khoái lạc bất tận.');
INSERT INTO "vn_mokhtasar" VALUES(9,22,'9:22','Họ sẽ ở muôn đời trong những ngôi vườn Thiên Đàng đó, một phần thưởng cho việc làm ngoan đạo của họ ở trần gian. Quả thật, Allah có phần thưởng vĩ đại dành cho những ai biết phục tùng các mệnh lệnh của Ngài một cách thành tâm.');
INSERT INTO "vn_mokhtasar" VALUES(9,23,'9:23','Hỡi những người có đức tin và noi theo những gì Thiên Sứ của Ngài mang đến, các ngươi chớ lấy tin mật của những người có đức tin hoặc những gì các ngươi bàn bạc với họ mang đưa cho cha mẹ cũng như anh em cùng huyết thống với các ngươi và những người thân thuộc trong họ hàng của các ngươi. Nếu các ngươi xem trọng sự vô đức tin hơn đức tin Iman nơi một mình Allah và nhất định chọn lấy những kẻ vô đức tin, các ngươi thương yêu chúng trong khi chúng vẫn ở trên tình trạng vô đức tin nơi Allah thì quả thật các ngươi đã nghịch lại Allah, đã bất công với bản thân mình, e là các ngươi đang đứng trên bờ của sự diệt vong.');
INSERT INTO "vn_mokhtasar" VALUES(9,24,'9:24','Ngươi hãy nói - hỡi Thiên Sứ -: Nếu các ngươi - hỡi những người có đức tin - xem cha mẹ, con cái, anh em, những người vợ, họ hàng thân thuộc, tài sản mà các ngươi tích lũy được, việc kinh doanh mà các ngươi muốn nhân rộng và lo sợ cho việc bị thua lỗ cũng như những căn nhà mà các ngươi hài lòng được sống trong nó hơn cả tình yêu dành cho Allah, cho Thiên Sứ của Ngài và việc Jihad vì chính nghĩa của Ngài thì các ngươi hãy chờ đợi hình phạt mà Allah sẽ giáng xuống trừng phạt các ngươi; và Allah không bao giờ phù hộ những kẻ rời khỏi sự phục tùng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(9,25,'9:25','Quả thật, Allah đã phù hộ các ngươi - hỡi những người có đức tin - chiến thắng kẻ thù đa thần của mình trong rất nhiều trận chiến mặc dù số lượng các ngươi rất ít và lực lượng các ngươi rất yếu nhưng các ngươi biết phó thác cho Allah sau khi đã làm đủ lý do và không ngạo mạn, kiêu hãnh về số lượng đông đảo của mình, vả lại số lượng đông đảo không phải yếu tố giúp các ngươi chiến thắng. Riêng về ngày của trận chiến Hunain khi các ngươi kiêu hãnh về lực lượng hùng hậu của mình khiến các ngươi nói "Nếu số lượng ít hơn sẽ không chiến thắng chúng ta đâu" nhưng lực lượng hùng hậu làm các ngươi tự đắc đó nào giúp ích được cho các ngươi, các ngươi đã bị kẻ thù đánh úp khiến các ngươi cảm thấy mình trái đất thu hẹp lại trong khi nó vẫn mênh mông, rồi các ngươi đã quay lưng tháo chạy để thoát thân.');
INSERT INTO "vn_mokhtasar" VALUES(9,26,'9:26','Sau khi các ngươi bỏ chạy trốn kẻ thù thì Allah đã trấn an tấm lòng Thiên Sứ của Ngài và cả những người có đức tin, Ngài giữ vững bước chân họ trên sa trường, Ngài phái xuống một đoàn thiên binh mà các ngươi không nhìn thấy và Ngài đã trừng phạt những kẻ vô đức tin bằng những gì đã xảy ra với chúng từ việc bị giết, bị bắt làm tù binh, bị giành lấy tài sản, bị đầy làm nô lệ. Đó là phần phạt dành cho những kẻ vô đức tin đã phủ nhận những gì mà Thiên Sứ đã mang đến cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,27,'9:27','Rồi ai biết hối cải về tội vô đức tin và lầm lạc của mình sau sự trừng phạt đó thì y sẽ được Allah chấp nhận sự sám hối, vì Allah luôn tha thứ cho ai biết hối cải trong đám nô lệ của Ngài và Ngài khoan dung với họ mặc dù họ đã vô đức tin và đại nghịch với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(9,28,'9:28','Hỡi những người có tin nơi Allah, nơi Thiên Sứ của Ngài và đã tuân thủ theo giáo luật của Ngài! Thật vậy, những kẻ đa thần là những kẻ không sạch sẽ bởi sự vô đức tin, hành động sai quấy, bản chất xấu xa và phong tục thối nát của chúng. Cho nên chúng không được phép đến gần vùng đất linh thiêng Makkah - trong đó có Masjid Al-Haram - kể từ năm nay trở đi, đó là năm thứ lịch Islam, cho dù chúng đến để hành hương Hajj và ''Umrah. Nếu các ngươi - hỡi những người có đức tin - sợ nghèo khổ vì chấm dứt mọi nguồn cung cấp lương thực và các nguồn thương mại khác thì Allah sẽ bù đắp đầy đủ cho các ngươi bằng nguồn thiên lộc của Ngài. Quả thật Allah luôn tận tường mọi hoàn cảnh của các ngươi, luôn sáng suốt trong việc quản lý các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(9,29,'9:29','Các ngươi - hỡi những người có đức tin - hãy đánh chiến với những kẻ vô đức tin trong số những người Do Thái và Thiên Chúa đã không tin Allah là Thượng Đế mà chấm dứt việc thờ phượng những ai (vật) khác ngoài Ngài, đã không tin vào Đời Sau, không tránh xa những thứ Allah và Thiên Sứ của Ngài đã cấm như xác chết, thịt heo, rượu, vay lãi, ... không chịu phục tùng các giáo luật của Allah, cho đến khi nào chúng chịu cúi đầu giao nộp thuế thân bằng đôi tay của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(9,30,'9:30','Quả thật, tất cả dân Do Thái và Thiên Chúa đều phạm tội Shirk, Do Thái thì cho rằng ''Uzair (Ezra) là con trai của Allah, còn Thiên Chúa thì cho rằng Al-Masih Ysa tức Giê-su là con trai của Allah. Đó quả là những lời nói khống không có cơ sở, nó chỉ được thốt ra từ cửa miệng của chúng, lời nói của chúng giống như những thế hệ trước chúng đã nói "Chính Thiên Thần là những đứa con gái của Allah" nhưng Allah tối thượng hơn mọi thứ chúng gán ghép cho Ngài. Chúng đã bị Allah tiêu diệt. Làm sao mà chúng lại lệch khỏi chân lý để sa lầy vào lầm lạc đến thế cơ chứ!');
INSERT INTO "vn_mokhtasar" VALUES(9,31,'9:31','Người Do Thái đã suy tôn các học giả của họ và người Thiên Chúa thì suy tôn các thầy tu của họ lên làm thượng đế thay vì Allah. Những kẻ được suy tôn đó đã cho phép những thứ Allah cấm và đã cấm những thứ Allah cho phép. Những người Thiên Chúa còn suy tôn Al-Masih Ysa con trai của Maryam lên thành thượng đế cùng với Allah. Trong khi Allah ra lệnh cho các học giả Do Thái và các thầy tu Thiên Chúa, luôn cả ''Uzair và Ysa con trai của Maryam là phải thờ phượng một mình Ngài duy nhất, tuyệt đối không được thờ phượng ai khác cùng Ngài. Allah là Đấng Toàn Năng duy nhất, không có Thượng Đế đích thực nào khác ngoài Ngài. Ngài trong sạch với tất cả mọi thứ mà những kẻ đa thần và những ai khác ngoài họ đã gán ghép cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(9,32,'9:32','Những kẻ vô đức tin này và những kẻ vô đức tin khác đã vu khống và phủ nhận mọi thứ được Muhammad mang đến để hủy hoại Islam. Chúng phủ nhận những bằng chứng rõ rệt cũng như những chứng cứ thuyết phục khẳng định tính duy nhất của Allah được mang đến cho chúng và chúng phủ nhận mọi sự thật mà Thiên Sứ của Ngài mang đến. Tuy nhiên Allah vẫn hoàn thành tôn giáo của Ngài, vẫn phơi bày nó và nâng nó lên đỉnh cao của vinh quang mặc cho những kẻ người vô đức tin cay ghét và khó chịu. Và một khi Allah muốn điều gì thì mọi ý muốn khác đều vô giá trị.');
INSERT INTO "vn_mokhtasar" VALUES(9,33,'9:33','Và Allah Toàn Năng là Đấng đã gởi Thiên Sứ Muhammad của Ngài mang Qur''an, một nguồn hướng dẫn nhân loại, một tôn giáo chân lý - đó là tôn giáo Islam - để nâng cao nó qua các chứng cứ, các bằng chứng và những giáo lý vượt trội hơn tất cả tôn giáo khác, mặc cho những kẻ đa thần luôn ghét điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(9,34,'9:34','Hỡi những người có đức tin và tuân thủ đúng theo giáo luật của Allah! Quả thật, có rất nhiều học giả Do Thái và nhiều thầy tu Thiên Chúa đã chiếm lấy tài sản của thiên hạ một cách bất chính như hối lộ và những cách ăn chặn khác. Ngoài ra, chúng còn ngăn cản thiên hạ gia nhập vào tôn giáo của Allah. Và những ai tích lũy vàng và bạc nhưng không chịu thực hiện bổn phận Zakah, Ngươi - hỡi Thiên Sứ - hãy cho chúng biết về hậu quả mà chúng phải gặp ở Đời Sau, đó là một cực hình đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(9,35,'9:35','Vào Ngày Tận Thế, tất cả những tài sản tích trữ mà những kẻ không chịu xuất Zakah sẽ được đem nung trong Hỏa Ngục cho đến khi đỏ rực thì chúng được lấy ra mà dán lên trán, lên hông và lên lưng của những kẻ đó, và chúng được bảo: Đây là tài sản mà các ngươi đã tích trữ nhưng không chịu hoàn thành bổn phận Zakah, vì thế, các ngươi hãy nếm lấy thứ mà các ngươi đã ôm chặt trong người, đây là hậu quả của việc làm đó.');
INSERT INTO "vn_mokhtasar" VALUES(9,36,'9:36','Quả thật, số lượng tháng trong một năm được giáo lý của Allah qui định là gồm mười hai tháng. Điều đó đã được ghi rõ ràng trong Quyển Sổ Mẹ nơi Ngài trước khi trời đất được khởi tạo. Trong số mười hai tháng này có bốn tháng cấm kỵ vì Allah cấm giao chiến trong những tháng này. Bốn tháng cấm kỵ này gồm ba tháng liền nhau: Zdul Qa''dah, Zdul Hijjah và Muharram tức tháng 12 và tháng giêng theo lịch Islam và một tháng không liền khác, đó là Rajab tức tháng 7. Việc liệt kê ra số lượng tháng trong năm cũng như qui định bốn tháng cấm kỵ là một tôn giáo chính thống. Bởi thế, các ngươi chớ làm điều bất công cho bản thân các ngươi trong các tháng cấm kỵ này bằng sự giết chóc lẫn nhau. Và các ngươi hãy chiến đấu với tất cả những kẻ đa thần giống như chúng chiến đấu với tất cả các ngươi. Và các ngươi hãy nên biết là Allah luôn ở bên giúp đỡ và phù hộ những ai ngay chính tuân thủ đúng mọi mệnh lệnh của Ngài; và những ai được Allah bên cạnh thì không bao giờ thất bại.');
INSERT INTO "vn_mokhtasar" VALUES(9,37,'9:37','Quả thật, việc dời sự cấm kỵ của tháng cấm kỵ đến tháng không cấm kỵ nhằm thay thế vị trí của tháng - giống như người Ả-rập đã thường làm ở thời tiền Islam - chỉ gia tăng sự vô đức tin cho những kẻ vốn đã không tin Allah vì họ đã không tin vào giáo lý cấm kỵ đó. Từ đó Shaytan mê hoặc những kẻ vô đức tin này bằng chính cách xấu xa này, chúng đã thay đổi tháng cấm kỵ bằng những tháng khác để cho đủ số lượng tháng cấm kỵ mà Allah đã cấm mặc dù có khác về vị trí của tháng. Cho nên, không được cấm bất cứ tháng nào ngoại trừ tháng mà Allah đã cấm, không được phép làm những gì Allah đã cấm trong các tháng cấm đó xem như các ngươi đã vi phạm lệnh cấm của Ngài. Shaytan tác động chúng làm chúng tưởng việc làm xấu xa của chúng là tốt nên chúng đã duy trì. Và Allah không bao giờ hướng dẫn những kẻ ngoan cố trong sự vô đức tin của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(9,38,'9:38','Hỡi những người có đức tin nơi Allah, nơi Thiên Sứ của Ngài và tuân thủ theo giáo luật của Ngài! Các ngươi bị sao thế, tại sao khi các ngươi được kêu gọi đi Jihad vì chính nghĩa của Allah để giết kẻ thù của các ngươi thì các ngươi lại lười biếng, chán ngán chỉ muốn bám lấy ngôi nhà của các ngươi như thế? Phải chăng các ngươi đã hài lòng với những vật chất sớm tiêu vong và sự hưởng thụ tạm bợ đó thay vì cuộc sống hạnh phúc mãi mãi ở Đời Sau đã được Allah chuẩn bị sẵn cho những người chiến đấu trên đường chính nghĩa của Ngài ư? Quả thật, vật chất của cuộc sống trần gian này so với cuộc sống Đời Sau chỉ là những thứ đáng khinh, làm sao người khôn ngoan lại lựa chọn thứ tạm bợ thay vì thứ trường tồn, chọn thứ rẻ mạt thay vì thứ quí giá?!');
INSERT INTO "vn_mokhtasar" VALUES(9,39,'9:39','Nếu các ngươi - hỡi những người có đức tin - không chịu đi ra tham gia Jihad vì chính nghĩa của Allah để đánh kẻ thù của các ngươi thì Ngài sẽ trừng phạt các ngươi bằng cực hình và sỉ nhục các ngươi, và rồi Ngài sẽ thay thế các ngươi bằng một nhóm người khác luôn phục tùng Allah, một nhóm người mà khi được Ngài yêu cầu xuất binh Jihad thì họ liền xuất binh. Tất nhiên các ngươi không thể gây thiệt hại cho Allah khi các ngươi nghịch lệnh Ngài, bởi Ngài giàu có, bất cần các ngươi, chỉ có các ngươi mới là những kẻ nghèo khổ luôn cần đến Ngài. Và Allah toàn năng trên tất cả mọi thứ, không có gì làm khó Ngài và Ngài thừa sức ban chiến thắng cho tôn giáo của Ngài cũng như vị Thiên Sứ của Ngài mà không cần đến các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(9,40,'9:40','Nếu các ngươi - hỡi những người có đức tin - không chịu phò trợ Thiên Sứ của Allah và không đáp lại lời kêu gọi Jihad của Y cho con đường chính nghĩa của Ngài thì các ngươi hãy biết rằng quả thật Allah đã phù hộ Thiên Sứ của Ngài và Abu Bakr mà không cần đến các ngươi khi cả hai bị nhóm đa thần trục xuất khỏi Makkah, không có người thứ ba bên cạnh khi cả hai đang lẫn trốn trong hang núi Thawr trước sự truy sát của kẻ thù. Lúc Abu Bakr thấy kẻ thù đang đứng ngay ngoài miệng hang làm y lo sợ thì Thiên Sứ của TA đã trấn an: "Anh đừng lo sợ, quả thật Allah đang bên cạnh bảo vệ và phù hộ chúng ta". Thế là Allah ban xuống cho Thiên Sứ của Ngài sự điềm tĩnh trong tim và phái xuống một đoàn Thiên Binh để phò trợ Y nhưng các ngươi không nhìn thấy và Ngài đã hạ thấp tin thần của những kẻ đa thần và Ngài nâng cao lời phán của Ngài khi Islam chạm đến đỉnh cao của vinh quang. Và Allah toàn năng trong vương quyền và uy thế của Ngài, không ai chiến thắng được Ngài và Ngài luôn sáng suốt trong việc thống trị, chi phối và định đoạt.');
INSERT INTO "vn_mokhtasar" VALUES(9,41,'9:41','Các ngươi - hỡi những người có đức tin - hãy ra đi Jihad vì chính nghĩa của Allah dù trong hoàn cảnh khó khăn hay phồn thịnh, dù là thanh niên hay đã già, các ngươi hãy đấu tranh bằng tài sản và cả sinh mạng của các ngươi. Quả thật, việc các ngươi ra đi Jihad bằng tài sản và sinh mạng sẽ mang lại rất nhiều lợi ích cho cuộc sống trần gian và cả Đời Sau nếu các ngươi nhận biết được điều đó, các ngươi hãy cố mà tuân lệnh.');
INSERT INTO "vn_mokhtasar" VALUES(9,42,'9:42','Với bao lý do mà những kẻ Munafiq (đạo đức giả) đã đưa ra làm cái cớ để được Ngươi cho phép chúng ở lại. Nếu như chiến lợi phẩm dễ lấy và đường đi không khó nhọc thì chắc chắn chúng đã xuất binh cùng Ngươi mất rồi - hỡi Thiên Sứ -, nhưng vì đường đi xa xôi và nhiều trở ngại khó khăn nên chúng đã quyết ở lại nhà. Và rồi khi Ngươi trở về, những kẻ trong nhóm Munafiq này sẽ thề với Allah mà nói với Ngươi rằng, nếu có thể là chúng đã đi cùng với các ngươi rồi. Quả thật chúng đáng bị tiêu diệt vì dám đẩy thân mình vào hình phạt của Allah bởi sự thề thốt dối trá của chúng. Va Allah biết rõ chúng là những kẻ nói dối trong lời nói cũng như trong lời thề thốt này của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(9,43,'9:43','Allah tha thứ cho Ngươi - hỡi Thiên Sứ - về việc Ngươi tự ý cho phép chúng không tham chiến trong lúc Ngươi chưa rõ ai là kẻ thành thật và ai là kẻ giả dối trong việc đưa ra các lý do không thể cùng Ngươi tham chiến.');
INSERT INTO "vn_mokhtasar" VALUES(9,44,'9:44','Những người có đức tin nơi Allah và Ngày Phán Xét một cách trung thực và thành tâm sẽ không xin Ngươi - hỡi Thiên Sứ - cho phép họ ở lại nhà, không tham chiến vì chính nghĩa của Allah mà họ sẽ sẵn sàng chiến đấu bằng cả tài sản và sinh mạng của họ khi được kêu gọi. Và Allah luôn am tường về người ngay chính trong đám nô lệ của Ngài, bởi họ không bao giờ xin phép trừ phi có lý do thật sự chính đáng.');
INSERT INTO "vn_mokhtasar" VALUES(9,45,'9:45','Quả thật những kẻ đến xin phép Ngươi - hỡi Thiên Sứ - ở lại nhà, không tham chiến vì chính nghĩa của Allah, chính là nhóm Munafiq. Chúng chẳng có đức tin nơi Allah, cũng không tin vào cuộc sống Đời Sau, và chúng là những kẻ luôn mang chứng bệnh nghi ngờ trong tim về tôn giáo của Allah; và với tấm lòng nghi ngờ đó, chúng đã do dư và lưỡng lự nên chúng đã không được hướng dẫn đến với chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(9,46,'9:46','Và nếu thật sự chúng muốn tham chiến như lời chúng nói rằng rất muốn ra đi cùng Ngươi tham chiến vì chính nghĩa của Allah thì chắc chúng đã trang bị đầy đủ ngay từ đầu. Tuy nhiên, Allah đã ghét việc chúng xuất binh cùng Ngươi nên đã khiến chúng cảm thấy ì ạch và nặng nề để rồi cuối cùng chúng phải ở lại nhà.');
INSERT INTO "vn_mokhtasar" VALUES(9,47,'9:47','Là một điều tốt khi nhóm Munafiq này không tham chiến với các ngươi, bởi khi chúng đi cùng các ngươi thì chúng chỉ toàn tạo ra sự lộn xộn, rắc rối cho các ngươi. Chúng sẽ nhanh mồm nói xấu người này và đâm thọc người kia nhằm chia rẽ sự đoàn kết và gây bất hòa giữa các ngươi. Trong nhóm các ngươi - hỡi những người có đưc tin - có những tên dọ thám của chúng loan truyền những tin tức bịa đặt, những điều nghi kỵ khiến các ngươi bất hòa nhau. Và Allah biết rõ những kẻ làm điều sai quấy trong đám Munafiq đó, những kẻ mà chúng luôn tìm cách gây sự xáo trộn hàng ngũ của những người có đưc tin.');
INSERT INTO "vn_mokhtasar" VALUES(9,48,'9:48','Nhóm Munafiq này đã cố gắng chia rẽ sự đoàn kết của những người có đức tin với mọi khả năng có thể trước khi xuất binh trong trận Tabuk. Chúng đã tìm trăm mưu ngàn kế để làm giảm ý chí xuất binh của Ngươi - hỡi Thiên Sứ - mãi khi Allah phù hộ và bảo vệ Ngươi, Allah phù hộ tôn giáo của Ngài và hạ nhục kẻ thù tôn giáo. Và chúng căm ghét diều đó bởi chúng luôn khao khát phổ biến mọi điều ngụy tạo hồng che khuất chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(9,49,'9:49','Và trong số những tên Munafiq viện lý do để không tham chiến, hắn nói: "Thưa Thiên Sứ của Allah, xin cho tôi được không tham chiến, xin đừng lôi kéo tôi vào tội lỗi bởi sự cám dỗ phụ nữ - Hi Lạp - của kẻ thù khi tôi gặp các ả." Há chúng chẳng phải đã tự đẩy mình rơi vào thử thách khủng khiếp hơn so với thứ mà chúng khẳng định ư? Đó là tội ngụy đức tin và tội không tham chiến, chắc chắn Hỏa Ngục ở Đời Sau sẽ bao trùm những kẻ vô đức tin, không một ai trong chúng thoát được và chúng không thể tìm thấy lối thoát hiểm nào.');
INSERT INTO "vn_mokhtasar" VALUES(9,50,'9:50','Khi Ngươi - hỡi Thiên Sứ - được Allah ban cho hồng ân làm ngươi hoan hỉ bằng chiến thắng hoặc chiến lợi phẩm thì chúng lại căm ghét và đau buồn. Nhưng khi Ngươi bị gặp tai họa hoặc bị thất bại trước kẻ thù thì những kẻ Munafiq này liền nói "Chúng tôi đã lường trước cho bản thân mình điều này nên đã không ra đi chinh chiến giống như những người có đức tin đã ra đi và đã bị giết chóc và giam cầm" rồi quay lưng bỏ đi về với gia đình bằng niềm vui bởi sự bình an của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(9,51,'9:51','Ngươi - hỡi Thiên Sứ - hãy nói với những kẻ Munafiq: Không bao giờ có một điều gì đó gây hại được chúng tôi ngoài những gì Allah đã định cho chúng tôi, Ngài là Chủ Nhân của chúng tôi, là điểm dựa của chúng tôi và chúng tôi phó thác mọi việc của chúng tôi cho Ngài và những người có đức tin luôn hướng đến Ngài để phó mặc cho Ngài; Ngài thừa sức giúp đỡ họ và một mình Ngài đã đủ cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,52,'9:52','Ngươi - hỡi Thiên Sứ - hãy bảo chúng: "Hay là các ngươi đang chờ đợi bọn ta được chiến thắng hoặc hy sinh vì đạo?! Và bọn ta thì đang chờ Allah giáng lên các ngươi hình phạt tiêu diệt toàn bộ các ngươi hoặc Ngài sẽ trừng phạt các ngươi bằng bàn tay của bọn ta bởi việc giết các ngươi hoặc bắt các ngươi làm tù binh khi Ngài cho phép bọn ta đánh các ngươi. Các ngươi cứ hãy chờ xem kết quả của bọn ta và bọn ta đang chờ đợi kết cuộc của các ngươi."');
INSERT INTO "vn_mokhtasar" VALUES(9,53,'9:53','Ngươi hãy bảo chúng - hỡi Thiên Sứ -: "Mặc cho các ngươi có cố gắng chi dùng tài sản ra sao, tự nguyện làm từ thiện hoặc bị ép làm từ thiện thì mọi thứ các ngươi đã chi dùng vẫn không được chấp nhận vì các ngươi vốn không tin tưởng và đã rời khỏi sự phục tùng Allah."');
INSERT INTO "vn_mokhtasar" VALUES(9,54,'9:54','Và nguyên nhân việc bố thí của chúng không được chấp nhận là do ba vấn đề: Vô đức tin nơi Allah và Thiên sứ của Ngài; lười biếng và uể oải khi dâng lễ nguyện Salah và miễn cưỡng bố thí tài sản chứ lòng không chút mong muốn; bởi lẽ chúng không hề hy vọng về phần thưởng qua việc lễ nguyện Salah cũng như qua việc bố thí của họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,55,'9:55','Vì vậy, Ngươi - hỡi Thiên Sứ - đừng để tài sản và con cái của những tên Munafiq lôi cuốn Ngươi khiến Ngươi tưởng đó là điều tốt. Sự việc không phải như thế, hậu quả xấu sẽ đến từ tài sản và con cái của chúng. Allah sẽ biến tất cả thành một cực hình dành cho chúng để có được những thứ đó và chúng sẽ phải đối mặt với hàng loại tai nạn cho đến khi Allah rút hồn chúng và chúng vẫn trên tình trạng vô đức tin để rồi cuối cùng chúng sẽ mãi mãi ở tầng đáy của Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(9,56,'9:56','Và những kẻ Munafiq đã thề thốt dối trá với các ngươi - hỡi những người có đức tin - rằng chúng tự khẳng định là người của các ngươi, nhưng thực sự chúng đâu phải là người của các ngươi mặc dù chúng sinh sống cùng quê hương với các ngươi, chúng luôn cố gắng thể hiện là người cùng nhóm với các ngươi. Đúng hơn, chúng là nhóm người sợ phải hậu quả giống người đa thần đã bị như bị giết, bị bắt làm tù binh, từ đó chúng đã cố gắng thể hiện mình là người Muslim mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(9,57,'9:57','Nếu như đám Munafiq đó tìm thấy một điểm nương thân nào có thể ẩn mình dù là hang động trong núi hoặc là đường hầm thì chắc chắn chúng đã nhanh chân lẫn trốn vào đó mất rồi.');
INSERT INTO "vn_mokhtasar" VALUES(9,58,'9:58','Và trong số đám người Munafiq, có kẻ đã nói xấu về Ngươi - hỡi Thiên Sứ - về việc Ngươi chia phần bố thí không thuận theo ý muốn của chúng. Nếu Ngươi chia theo yêu cầu đòi hỏi của chúng thì chúng hài lòng về Ngươi còn khi Ngươi không chia theo yêu cầu của chúng thì chúng liền bọc lộ bản tính xấu của chúng ra.');
INSERT INTO "vn_mokhtasar" VALUES(9,59,'9:59','Và giá như những kẻ Munafiq xuyên tạc Ngươi về việc chia phần bố thí biết hài lòng với những gì Allah đã ban cho chúng, thỏa mãn những gì được Thiên Sứ chia phần và biết nói: Đối với chúng tôi Allah là đủ, Ngài sẽ sớm ban cho chúng tôi thiên lộc như Ngài muốn và Thiên Sứ sẽ chia đúng phần mà Allah đã định cho chúng tôi, chúng tôi chỉ hi vọng vào Allah duy nhất trong việc ban phát. Và nếu như chúng đã làm thế thì đã tốt đẹp biết bao cho chúng, tốt đẹp hơn hẳn việc chúng đã xuyên tạc.');
INSERT INTO "vn_mokhtasar" VALUES(9,60,'9:60','Quả thật, các của cải Zakah chỉ được chia cho người nghèo, họ rất cần sự giúp đỡ mặc dù họ có thu nhập và có công việc nhưng số tiền kiếm được hoàn toàn không đủ cho họ xoay sở; cho người cận nghèo, họ gần như không sở hữu bất cứ gì có giá trị và hoàn cảnh của họ ai cũng rõ; cho người thu gom tiền Zakah để giao nộp cho lãnh đạo; cho người ngoại đạo mục đích để tác động hi vọng họ vào Islam hoặc trợ giúp người có đức tin có thể gia tăng niềm tin hoặc để giảm thiểu sự gây hại của họ đối với tín đồ Muslim khác; cho người nô lệ để họ chuộc thân được tự do; cho người thiếu nợ không phải do chi tiêu phung phí, cũng không vì mục đích tội lỗi và không có khả năng trả nợ; phục vụ cho quân lính đi Jihad vì chính nghĩa của Allah; và cho người lỡ đường không còn tiền đi tiếp về đến quê nhà. Tiền Zakah chỉ được chi dùng cho tám đối tượng này, đó là qui định mà Allah đã ban hành. Allah luôn biết rõ mọi thứ cải thiện cuộc sống đám nô lệ của Ngài, Ngài rất sáng suốt trong việc thống trị và định đoạt.');
INSERT INTO "vn_mokhtasar" VALUES(9,61,'9:61','Và trong nhóm Munafiq có kẻ đã xúc phạm đến Thiên Sứ của Allah, chúng đã nói khi chứng kiến sức chịu đựng của Người: "Quả là bất cứ ai nói gì Y cũng tin, Y không phân biệt được giữa chân lý và bịa đặt." Hãy đáp lại chúng - hỡi Thiên Sứ -: Quả thật, Thiên Sứ chỉ nghe điều tốt đẹp, tin tưởng vào Allah, tin cậy những nguồn tin mà những người có đức tin chân thật loan báo và nhân từ với họ, sứ mạng của Người là một hồng ân dành cho ai tin tưởng. Đối với những ai xúc phạm đến Thiên Sứ của Allah với bất cứ hình thức nào sẽ phải đối diện với hình phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(9,62,'9:62','Những kẻ Munafiq đã nhân danh Allah mà thề thốt với các ngươi - hỡi những người có đức tin - rằng chúng đã không nói bất cứ điều gì xúc phạm đến Thiên Sứ chẳng qua để làm hài lòng các ngươi, trong khi Allah và Thiên Sứ của Ngài xứng đáng hơn để chúng làm hài lòng, để tin tưởng và hành thiện, nếu chúng thật sự là những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(9,63,'9:63','Hay là nhóm Munafiq không biết rằng hành động đó của chúng đã đại nghịch với Allah và Thiên Sứ của Ngài, và đối với ai đại nghịch với Ngài và Thiên Sứ của Ngài thì vào Ngày Sau sẽ phải ở muôn đời trong Hỏa Ngục hay sao? Đó đúng là một sự hạ nhục đáng khinh.');
INSERT INTO "vn_mokhtasar" VALUES(9,64,'9:64','Những kẻ Munafiq đang lo lắng việc Allah sẽ thiên khải cho Thiên Sứ của Ngài một chương Kinh phô bày cho những người có đức tin biết được sự vô đức tin đang được chúng cố giấu kín trong lòng. Hãy bảo chúng - hỡi Thiên Sứ - các ngươi cứ việc nhạo báng đi - hỡi những kẻ Munafiq - cứ tiếp tục xúc phạm đến tôn giáo đi, rồi Allah sẽ phơi bày tất cả mọi thứ mà các ngươi đang lo lắng cho Thiên Sứ của Ngài biết qua một chương Kinh hoặc một thông báo về sự thật đó.');
INSERT INTO "vn_mokhtasar" VALUES(9,65,'9:65','Và nếu Ngươi - hỡi Thiên Sứ - có hỏi nhóm Munafiq về nguyên nhân chúng đã xúc phạm và mắng chửi những người có đức tin sau khi được Allah báo cho Ngươi biết thông tin về chúng, chắc chắn chúng sẽ nói: Chúng tôi chỉ thuận miệng nói đùa chứ không cố ý xúc phạm. Hãy bảo họ - hỡi Thiên Sứ -: Phải chăng đến cả Allah và Thiên Sứ của Ngài các ngươi cũng dám mang ra nhạo báng ư?');
INSERT INTO "vn_mokhtasar" VALUES(9,66,'9:66','Các ngươi đừng có cố giải thích làm gì với lời lẽ giả tạo đó, các ngươi đã rõ ràng phủ nhận đức tin bằng hành động chế nhạo của các ngươi sau khi các ngươi đã cố che giấu nó. Nếu như TA (Allah) bỏ qua cho một nhóm của các ngươi vì chúng đã từ bỏ việc ngụy tạo Islam, đã biết sám hối và thành tâm với Allah thì TA vẫn phải trừng phạt nhóm khác vì chúng vẫn ngoan cố ngụy tạo Islam mà không chịu ăn năn sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(9,67,'9:67','Những người Munafiq cả nam lẫn nữa đều có chung một bản tính, đó là họ muốn đối nghịch với những người có đức tin nên họ luôn động viên mọi người làm điều tội lỗi và ngăn chặn mọi người làm việc thiện, việc tốt đẹp, họ luôn keo kiệt không muốn bố thí vì con đường của Allah, họ đã bỏ hẳn việc tuân lệnh Allah nên họ đã bị Ngài bỏ mặc và không được phù hộ thành công. Quả thật, nhóm Munafiq đã rời xa việc tuân lệnh Allah, lệch khỏi con đường chân lý, sa vào lầm lạc và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(9,68,'9:68','Allah hứa trừng phạt những kẻ Munafiq, cả nam lẫn nữ, cùng tất cả những kẻ vô đức tin vẫn không chịu ăn năn bằng cách đày chúng vào trong lửa của Hỏa Ngục muôn đời ở trong đó. Hình phạt đó đã đủ cho chúng. Allah trục xuất chúng ra khỏi lòng thương xót của Ngài và chúng sẽ phải bị một sự trừng phạt liên tục không có sự ngừng nghỉ.');
INSERT INTO "vn_mokhtasar" VALUES(9,69,'9:69','Các ngươi - hỡi những kẻ Munafiq - cứ nhởn nhơ trong sự vô đức tin và nhạo báng giống như các thế hệ dối trá trước các ngươi. Những kẻ trước đây mạnh hơn các ngươi, nhiều hơn các ngươi về tiền bạc và con cái, chúng đã sống hưởng thụ với những bổng lộc đã định sẵn cho chúng và thỏa mãn mọi dục vọng của chúng ở trần gian. Thế nên các ngươi - hỡi những kẻ Munafiq - cứ hãy tận hưởng bổng lộc đã định sẵn cho các ngươi như chúng đã từng hưởng thụ và các ngươi cứ thoải mái mà phủ nhận chân lý, xúc phạm Thiên Sứ như việc chúng đã từng như vậy trước đây. Tất cả việc làm thiện tốt của chúng đều trở nên vô nghĩa ở nơi Allah bởi sự vô đức tin và chúng là những kẻ đã thua thiệt vì chúng tự đẩy bản thân mình rơi vào những cái hố diệt vong.');
INSERT INTO "vn_mokhtasar" VALUES(9,70,'9:70','Phải chăng những kẻ Munafiq này chưa từng biết đến nguồn thông tin đã xảy ra với những cộng đồng lọc lừa trước đây và đã phải chịu hậu quả thế nào ư? Chẳng hạn như dân chúng của Nuh, của Hud, của Saleh, của Ibrahim, của ngôi làng Madyan và của những ngôi làng của Lut; các Thiên Sứ đã đến gặp chúng với nhiều bằng chứng rõ ràng, đầy thuyết phục. Quả thật, Allah không hề bất công với chúng vì các Thiên Sứ của chúng đã luôn cố cảnh báo chúng nhưng chúng lại tự bất công với chính bản thân mình khi đã phủ nhận Allah và Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(9,71,'9:71','Những người có đức tin, nam và nữ, là những đồng minh hỗ trợ, giúp đỡ lẫn nhau, vì họ có điểm chung là đức tin Iman, họ luôn tuyên truyền kêu gọi nhau làm việc tốt thiện, đó là tất cả những gì được Allah yêu thích qua mọi mặt hành đạo như Tawhid, Salah; họ không ngừng ngăn cản nhau làm điều tội lỗi, đó là tất cả mọi thứ khiến Allah phẫn nộ bằng mọi hành động tội lỗi như vô đức tin, Riba (cho vay lấy lãi). Ngoài ra, họ còn hoàn thành tốt bổn phận Salah, biết vâng lời Allah và tuân lệnh Thiên Sứ của Ngài. Nhóm người được miêu tả đó sẽ được Allah thu nhận vào Thiên Đàng bằng lòng bao dung của Ngài, quả thật Allah là Đấng Toàn Năng, không có gì kháng lại Ngài, Ngài là Đấng rất mực sáng suốt trong sự điều hành, quản lý và định đoạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(9,72,'9:72','Allah hứa với những người có đức tin nơi Allah, cả nam lẫn nữ, rằng ở Đời Sau họ sẽ được thu nhận vào những Ngôi Vườn Thiên Đàng có những dòng sông chảy bên dưới các tòa lâu đài, họ sẽ ở trong đó mãi mãi, họ không bao giờ chết, sự hưởng thụ không bao giờ bị gián đoạn. Ngoài ra, họ còn đạt được một thứ khác vĩ đại hơn, đó là sự hài lòng của Allah. Tất cả những gì được hứa hẹn là sự chiến thắng vinh quang, chiến thắng vĩ đại mà không chiến thắng nào sánh bằng.');
INSERT INTO "vn_mokhtasar" VALUES(9,73,'9:73','Hỡi Thiên Sứ! Ngươi hãy dùng kiếm mà chiến đấu với những kẻ vô đức tin và Ngươi hãy dùng ngôn luận và chứng cứ để đấu tranh với những tên Munafiq, Ngươi hãy đứng vững lập trường đối với cả hai nhóm người này. Chúng là như thế, vào Ngày Phán Xét nơi ở của chúng sẽ là Hỏa Ngục, một nơi ở tồi tệ nhất.');
INSERT INTO "vn_mokhtasar" VALUES(9,74,'9:74','Những kẻ Munafiq đã thề dối trá bằng danh nghĩa của Allah rằng chúng đã không nói những gì mà Ngươi (Muhammad) đã nghe được như việc xúc phạm Ngươi và bêu xấu tôn giáo của Ngươi, nhưng thật ra chúng đã nói như thế, chúng đã lộ diện bản chất vô đức tin sau khi đã cố gắng thể hiện là người tin tưởng, chúng đã có ý định loại trừ Ngươi nhưng chưa thực hiện được. Chúng đã bác bỏ mọi thứ ngoại trừ một thứ, đó là Allah đã cho chúng giàu có bằng chính những chiến lợi phẩm đã ban cho Thiên Sứ của Ngài. Nếu như chúng biết ăn năn quay lại sám hối thì đó là giải pháp tốt cho chúng, còn nếu chúng quay lưng bỏ đi thì sẽ bị Allah trừng phạt bằng cực hình đau đớn ở trần gian như giết chóc và giam cầm và một cực hình khác ở Đời Sau bằng Hỏa Ngục. Và chúng sẽ không có bất cứ ai dám đứng ra bênh vực cũng như bảo vệ thoát khỏi hình phạt khủng khiếp đó.');
INSERT INTO "vn_mokhtasar" VALUES(9,75,'9:75','Và một số người Munafiq hứa hẹn với Allah bằng lời khẳng định: Nếu như chúng tôi được Ngài ban cho thiên lộc từ Ngài, nhất định chúng tôi sẽ bố thí và sẽ trở thành nhóm người ngoan đạo trong việc làm của mình.');
INSERT INTO "vn_mokhtasar" VALUES(9,76,'9:76','Đến khi Allah Toàn Năng ban cho chúng thiên lộc của Ngài thì chúng lại không thực hiện đúng lời đã hứa hẹn với Allah, ngược lại, chúng trở nên keo kiệt giữ chặt tài sản của chúng, không chịu bố thí bất cứ gì và chúng ngoảnh mặt với đức tin Iman.');
INSERT INTO "vn_mokhtasar" VALUES(9,77,'9:77','Từ đó, chúng bị kết tội là ngụy tạo Islam trong con tim của chúng cho đến Ngày Tận Thế; một hậu quả dành cho chúng do chúng đã lật lọng lời đã giao ước với Allah và giả dối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(9,78,'9:78','Phải chăng những kẻ Munafiq không biết rằng Allah luôn biết tận tường mọi âm mưu và kế hoạch mà chúng đã bàn bạc với nhau hay sao? Trong khi Allah Toàn Năng am tường mọi thứ vô hình, không có hành động nào của họ có thể che giấu được Ngài và Ngài sẽ phạt chúng theo các việc làm của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(9,79,'9:79','Chúng đã chỉ trích những người có đức tin trong việc tự nguyện bố thí nhiều của cải và những ai chỉ bố thí chút đỉnh theo khả năng của mình. Chúng lấy những người đó ra nhạo báng mà nói: Làm được gì với thứ ít ỏi đó? Thế là chúng đã bị Allah đáp trả lại bằng chính sự nhạo báng mà chúng đã đối xử với những người có đức tin và chúng sẽ phải gánh chịu cực hình đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(9,80,'9:80','Ngươi - hỡi Thiên Sứ - dù có cầu xin sự tha thứ cho chúng hoặc không cầu xin sự tha thứ cho chúng, thậm chí Ngươi có cầu xin tha thứ cho chúng đến bảy mươi lần thì Allah cũng không bao giờ tha thứ cho chúng, bởi chúng đã vô đức tin nơi Allah và Thiên Sứ của Ngài. Và Allah không hề hướng dẫn nhóm người cố tình rời khỏi giáo lý của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(9,81,'9:81','Những kẻ Munafiq rất vui mừng khi được ở nhà mà không phải đi tham chiến cùng với Thiên Sứ của Allah trong trận Tabuk. Chúng đã nghịch lại lệnh của Thiên Sứ và căm ghét phải sung tài sản và sinh mạng của họ vào cuộc chiến vì chính nghĩa của Allah như những người có đức tin. Chúng tác động đến những tên Munafiq như chúng: "Các ngươi chớ xuất binh trong nắng nóng." Quả thật trận chiến Tabuk diễn ra dưới cái nóng cháy da nhưng Ngươi - Hỡi Thiên Sứ ! - hãy nói cho chúng biết đi rằng lửa của Hỏa Ngục đang chờ đợi nhóm Munafiq có sức nóng còn khủng khiếp hơn cái nóng mà chúng đã cố chạy trốn, giá như chúng biết được điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(9,82,'9:82','Cứ mặc cho những kẻ Munafiq cười đùa đôi chút trong cuộc sống tạm bợ này, rồi đây chúng sẽ phải khóc thật nhiều cho cuộc sống trường tồn ở Đời Sau, một phần thưởng dành cho những gì mà chúng đã tích lũy được từ sự vô đức tin, tội lỗi và phản nghịch ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(9,83,'9:83','Nếu như Allah có đưa Ngươi - hỡi Thiên Sứ - trở lại gặp một nhóm thuộc thành phần những kẻ Munafiq quyết không từ bỏ bản chất ngụy trang Islam của mình để chúng xin phép Ngươi được tham chiến cùng Ngươi ở các trận chiến khác, Ngươi hãy bảo chúng: Các ngươi - hỡi những kẻ Munafiq - sẽ không bao giờ còn cơ hội tham chiến cùng Ta vì chính nghĩa của Allah. Đó là hình phạt dành cho các ngươi, đề phòng xảy ra những hậu quả đáng tiếc khi các ngươi tham chiến cùng Ta, bởi ngay từ đầu các ngươi chỉ muốn được ở nhà để không tham chiến trận Tabuk, các ngươi cứ tiếp tục ở lại cùng những người bệnh tật, phụ nữ và trẻ con.');
INSERT INTO "vn_mokhtasar" VALUES(9,84,'9:84','Và Ngươi - hỡi Thiên Sứ - chớ dâng lễ nguyện Salah cho bất cứ người chết nào trong số chúng và cũng chớ đứng tại mộ của hắn để cầu xin sự tha thứ cho hắn. Sở dĩ như thế là chúng vốn vô đức tin nơi Allah và phủ nhận Thiên Sứ của Ngài và chúng đã chết trên tình trạng bất tuân Allah. Tương tự, bất cứ ai đã chết giống tình trạng của chúng thì không được hành lễ Salah và cầu xin sự tha thứ cho y.');
INSERT INTO "vn_mokhtasar" VALUES(9,85,'9:85','Và Ngươi - hỡi Thiên Sứ - đừng để tài sản của những kẻ Munafiq đó cũng như con cái của chúng khiến Ngươi ngưỡng mộ. Quả thật, chẳng qua Allah muốn dùng những thứ đó để trừng phạt chúng trong cuộc sống trần gian này, khiến chúng gặp nhiều khó khăn và tai ương nhiều hơn để lúc linh hồn chúng rời khỏi thể xác thì chúng vẫn là nhóm người vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(9,86,'9:86','Và khi Allah thiên khải xuống một chương Kinh nào đó cho Thiên Sứ của Ngài có mang sắc lệnh buộc phải có đức tin nơi Allah và xuất binh Jihad vì chính nghĩa của Ngài thì số người giàu có trong số chúng liền xin phép Ngươi cho chúng được ở lại quê nhà, chúng nói: Xin cho chúng tôi được ở lại cùng với những người yếu đuối không tham chiến.');
INSERT INTO "vn_mokhtasar" VALUES(9,87,'9:87','Nhóm Munafiq này đã bằng lòng cho bản thân chúng bị nhục nhã và bị khinh rẻ khi chấp nhận ở lại nhà cùng những người có lý do không tham chiến và rồi con tim của chúng đã bị Allah bao trùm bằng sự vô đức tin và sự ngụy tạo Islam nên chúng không sao nhận thức được điều nào tốt cho mình.');
INSERT INTO "vn_mokhtasar" VALUES(9,88,'9:88','Riêng về Thiên Sứ và những người có đức tin cùng với Ngươi không hề trốn tránh việc Jihad vì chính nghĩa của Allah, ngược lại, họ luôn ở trong tư thế sẵn sàng, chấp nhận hi sinh cả tài sản lẫn sinh mạng vì chính nghĩa của Allah. Thế nên họ được Allah dành cho phần thưởng tốt đẹp ở trần gian bằng sự thắng lợi và thu được chiến lợi phẩm. Họ còn được hưởng phần thưởng vĩ đại khác ở Đời Sau như được vào Thiên Đàng, đạt được điều họ muốn và được tránh khỏi mọi thứ họ sợ.');
INSERT INTO "vn_mokhtasar" VALUES(9,89,'9:89','Allah chào đón họ bằng những Ngôi Vườn Thiên Đàng với những dòng sông chảy bên dưới những tòa lâu đài, họ ở trong đó mãi mãi, không có sự kết thúc. Phần thưởng đó là sự chiến thắng vĩ đại mà không niềm vui nào so sánh đặng.');
INSERT INTO "vn_mokhtasar" VALUES(9,90,'9:90','Và một nhóm người thuộc dân Ả-rập vùng sa mạc của Madinah và số người khác sống xung quanh lần lượt tìm đến Thiên Sứ của Allah để xin phép được không tham chiến vì chính nghĩa của Allah. Tuy nhiên, có nhóm người khác vốn đã không tin tưởng Nabi và không tin vào lời hứa hẹn của Allah nên đã bất cần đến việc phải xin phép ở lại hay không, nhóm người vô đức tin này sẽ gặp phải cực hình đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(9,91,'9:91','Không có tội đối với phụ nữ, trẻ em, người bệnh, người già, người mù, người nghèo không có tiền bạc để trang bị cho cuộc chiến cho dù họ có ở lại quê nhà không tham chiến, bởi họ có lý do miễn sao họ luôn thật lòng với Allah và Thiên Sứ của Ngài, biết tuân thủ theo giáo luật của Ngài, đặc biệt là những người sùng đạo trong số những người này lại càng không có đường nào để trừng phạt họ cả, và Allah sẵn sàng tha thứ cho người sùng đạo và khoan dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,92,'9:92','Và cũng không bị bắt tội đối với những ai đã tìm đến Ngươi - hỡi Thiên Sứ - yêu cầu Ngươi hỗ trợ họ vật cưỡi để tham chiến, Ngươi đã trả lời họ: Ta không có vật cưỡi nào để hỗ trợ cho các ngươi cả, thế là họ phải quay lưng đi trong buồn bã và đôi mắt họ ngấn lệ vì họ không tìm được bất cứ thứ gì để đóng góp cho cuộc Jihad.');
INSERT INTO "vn_mokhtasar" VALUES(9,93,'9:93','Sau khi trình bày về việc không có đường nào để trừng phạt những người có lý do chính đáng không tham chiến, Ngài đề cập đến những kẻ đáng bị trừng phạt qua lời phán: Hình phạt chỉ để dành cho những kẻ giàu có, sở hữu đầy đủ phương tiện tham chiến nhưng lại xin phép Ngươi - hỡi Thiên Sứ - được ở nhà, nhóm người này mới đáng bị trừng vì chúng chấp nhận sự ô nhục cho bản thân khi chọn lấy việc ở lại nhà cùng những người không thể tham chiến, và chúng đã bị Allah đóng kín con tim lại nên đã không tiếp thu được bất cứ lời khuyên bảo nào. Vì con tim bị đóng kín nên chúng đã không nhận thấy được đâu là điều tốt để làm và đâu là điều xấu để tránh.');
INSERT INTO "vn_mokhtasar" VALUES(9,94,'9:94','Những kẻ Munafiq (nhóm đạo đức giả) đã không tham gia cuộc chiến đưa ra những lời bào chữa trước những người Muslim khi họ trở về từ cuộc chiến. Allah đã hướng dẫn Thiên Sứ của Ngài và những người có đức tin đáp trả lại những kẻ Munafiq này: Các người đừng xin lỗi với những lời bào chữa dối trá, chúng tôi không tin vào những điều các người đã nói với chúng tôi. Quả thật, Allah đã báo cho chúng tôi biết những điều ẩn chứa trong lòng các người, và Allah cùng với Sứ Giả của Ngài sẽ quan sát hành động của các người: Hoặc các người sẽ ăn năn, sám hồi rồi Ngài sẽ chấp nhận sự sám hối của các người, hoặc các người sẽ tiếp tục dối trá? Cuối cùng các người cũng sẽ được đưa về trình diện với Allah Đấng Am Tường Tất Cả, Ngài sẽ báo cho các người biết rõ về các việc mà các người đã từng làm, và theo đó Ngài sẽ thưởng phạt các người, thế nên hãy nhanh chóng đến với sự sám hối và làm những việc ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(9,95,'9:95','Những kẻ không tham gia cuộc chiến, họ sẽ thề thốt với danh nghĩa của Allah khi các ngươi - hỡi những người có đức tin - trở về từ cuộc chiến để đưa ra những lí do bào chữa giả dối khi họ không tham chiến, để các người không đổ lỗi và khiển trách họ nữa, các người hãy bỏ mặc họ, hãy xa lánh họ, quả thật họ là những kẻ có lòng dạ bẩn thỉu, rồi nơi trú ngụ của họ sẽ là Hỏa Ngục. Đó là phần hình phạt dành cho những gì họ đã phạm phải từ bản tính đạo đức giả và những tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(9,96,'9:96','Những kẻ không tham gia cuộc chiến thề thốt với các ngươi - hỡi những người có đức tin - hồng để các ngươi hài lòng với họ, và chấp nhận những lời biện hộ từ họ, nhưng các ngươi chớ có hài lòng về họ bởi nếu các ngươi có hài lòng về họ thì quả thật các ngươi đã làm trái với Thượng Đế của các ngươi, vì Ngài không hài lòng với những kẻ không tuân phục Ngài bằng sự phủ nhận và đạo đức giả. Các ngươi hãy thận trọng - hỡi những người Muslim - phải chăng các ngươi hài lòng với những kẻ mà Allah không hài lòng về họ ư.');
INSERT INTO "vn_mokhtasar" VALUES(9,97,'9:97','Những người Ả Rập du mục nếu họ là những người phủ nhận hoặc đạo đức giả thì sự phủ nhận của họ nghiêm trọng hơn rất nhiều so với những người ở phố thị, và sự đạo đức giả của họ cũng nặng nề hơn những người ở phố, và họ đứng đầu trong sự thiếu hiểu biết về tôn giáo, và họ cũng là những người không biết gì về điều bắt buộc, về điều Sunnah luôn cả những nền tảng giáo lý đã được mặc khải cho vị Sứ Giả của Allah. Bởi vì họ là những người khô cằn về tình cảm và sống độc lập. Allah hằng biết về họ, không có gì có thể che giấuđược Ngài, Ngài khôn ngoan trong sự quản lý và ban giáo luật.');
INSERT INTO "vn_mokhtasar" VALUES(9,98,'9:98','Trong những dân cư Ả Rập du mục, những người đạo đức giả trong số họ tin rằng những tài sản chi dùng cho con đường chính nghĩa của Allah là sự thua thiệt hoặc như tiền phạt, bởi vì họ cho là tài sản chi dùng ấy không mang lại ân phước và nếu có giữ lại cũng không bị Allah bắt phạt, nhưng đôi khi họ chi dùng để mọi người nhìn thấy và không bàn tán về họ và họ chờ đợi những tai họa sẽ xảy đến với các ngươi - hỡi những người có đức tin - để họ thoát khỏi mọi điều xấu đến từ các ngươi. Tuy nhiên, Allah đã làm cho những gì chúng mong đợi - tai họa sẽ xảy đến với những người tin tưởng - lại xảy ra với chính bản thân họ, và Allah hằng nghe những gì họ nói, và hằng biết những gì họ che giấu.');
INSERT INTO "vn_mokhtasar" VALUES(9,99,'9:99','Và cũng trong những người dân Ả Rập du mục, có những người tin tưởng Allah và tin vào Ngày Phán Xử Cuối Cùng và họ tin những của cải mà họ chi dùng cho chính nghĩa của Allah khiến họ được đến gần với Allah và là phương tiện để nhận được lời cầu xin của Sứ Giả và được sự tha thứ tội lỗi. Há chẳng phải việc chi dùng cho chính nghĩa của Allah và lời cầu xin từ Thiên Sứ cho họ sẽ khiến cho họ đến gần với Allah hơn hay sao! Họ sẽ được phần thưởng và sẽ được Allah cho họ vào lòng thương xót của Ngài mà bao gồm trong đó là sự tha thứ tội lỗi và Thiên Đàng của Ngài. Quả thật, Allah hết mực tha thứ cho những ai quay lại sám hối trong đám bầy tôi của Ngài, Ngài rất độ lượng với họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,100,'9:100','Những người tiên phong đến với đức tin từ những người Muhajirin - những người rời bỏ nhà cửa và quê hương của họ đến với Allah -, những người Ansar đã trợ giúp vị Sứ Giả của họ, và những người đầu tiên đi theo những người Muhajirin và cư dân Ansar để đến với sự tốt đẹp về đức tin, lời nói và việc làm là những người được Allah hài lòng và họ cũng hài lòng về Ngài khi Ngài ban cho họ phần thưởng vĩ đại. Và Ngài đã chuẩn bị cho họ các Ngôi Vườn Thiên Đàng bên dưới có các dòng sông chảy và họ sẽ ở trong đó đời đời. Đó là phần thưởng vĩ đại dành cho những người chiến thắng.');
INSERT INTO "vn_mokhtasar" VALUES(9,101,'9:101','Và một số dân chúng Ả Rập du mục sống xung quanh Madinah và một số dân cư của Madinah là những tên đạo đức giả, họ luôn cố sống trong sự giả dối, Ngươi - hỡi Thiên Sứ - không nhận biết được họ nhưng Allah biết rõ về họ, Ngài sẽ trừng phạt họ đến hai lần, một lần trên thế gian bằng cách vạch trần thân phận giả tạo đức tin của họ, họ bị giết và bị bắt làm tù binh; và một lần khác ở Ngày Sau bằng hình phạt trong cõi mộ, rồi tiếp tục bị trừng phạt khủng khiếp hơn khi bị đày vào tầng đáy của Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(9,102,'9:102','và một số cư dân khác của Madinah đã không tham gia chiến đấu một cách không có lý do nhưng họ đã thú nhận tội lỗi đó và họ đã không nêu những lý do dối trá, họ đã pha trộn những giữa việc làm tốt đẹp trước đây của họ về sự tuân phục Allah và tuân thủ các luật lệ của Ngài, và chiến đấu trên con đường của Ngài với những việc làm xấu. Hi vọng Allah tha thứ cho tội lỗi của họ và bỏ qua cho họ. Bởi quả thật Allah luôn tha thứ và đoái thương cho những bề tôi quay về sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(9,103,'9:103','Ngươi - hỡi Thiên Sứ - hãy lấy tài sản của họ mà xuất Zakat để tẩy sạch họ từ những ô uế của sự bất tuân và tội lỗi và làm tăng ân phước cho họ qua việc làm Zakat đó, và Ngươi hãy cầu nguyện cho họ sau khi đã Zakat thay họ. Quả thật, lời cầu nguyện của Ngươi là sự thương xót và là sự bảo đảm cho họ, và Allah hằng nghe và biết những việc làm cũng như ý định của họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,104,'9:104','Để những người không tham chiến và những người hối cải tội lỗi của họ với Allah biết rằng Allah hằng chấp nhận sự sám hối của những bầy tôi biết quay về với Ngài, và quả thật Ngài chấp nhận của bố thí và Ngài rất mực giàu có. Ngài sẽ trả lại xứng đáng cho những người bố thí phần mà họ đã bố thí, và quả thật Ngài hằng quay lại tha thứ cho những bầy tôi trở về sám hối với Ngài, Ngài rất mực khoan dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,105,'9:105','Ngươi - hỡi Thiên Sứ - hãy bảo những người đã không tham chiến và những người đã sám hối tội lỗi của họ: Hãy cố mà chịu đựng những thiệt hại đã qua mà hãy thành tâm trong việc làm vì Allah, và hãy làm những việc mà Allah hài lòng, rồi Allah và Thiên Sứ của Ngài cũng như những người tin tưởng sẽ quan sát việc làm của các người, và các người sẽ được đưa trở về gặp lại Thượng Đế của các người, Đấng Am Tường tất cả mọi việc, Ngài biết điều vô hình và hữu hình, rồi Ngài sẽ cho các người biết những việc mà các ngươi đã làm trên thế gian này và Ngài sẽ ban thưởng các ngươi về điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(9,106,'9:106','Và trong những người không tham gia trận chiến Tabuk mà không có lý do. Họ là những người nằm trong án treo chờ quyết định của Allah và phán quyết của Ngài, Ngài phán xử họ theo những gì Ngài muốn, hoặc là Ngài sẽ trừng phạt nếu họ không quay lại sám hối tội lỗi với Ngài hoặc Ngài sẽ tha thứ cho họ nếu họ chịu sám hối tội lỗi. Allah biết ai là người đáng sẽ bị trừng phạt, và ai đáng nhận được sự tha thứ từ Ngài, Đấng thông lãm trong giáo luật và trong quản lý, những người không tham chiến bao gồm: Murarah bin Al-Rabi''a, Ka''ab bin Malik và Hilal bin Umayah.');
INSERT INTO "vn_mokhtasar" VALUES(9,107,'9:107','Và trong số những kẻ đạo đức giả, họ cũng xây dựng Masjid nhưng không phải để tôn thờ Allah mà để làm hại những người Muslim, và phơi bày sự phủ nhận để làm lớn mạnh những kẻ đạo đức giả, và để chia rẽ những người có đức tin, họ chuẩn bị và chờ đợi kẻ gây chiến chống Allah và Sứ Giả của Ngài bằng cách xây dựng Masjid. Tuy nhiên, họ vẫn thề thốt với các ngươi: Chúng tôi không nghĩ gì ngoại trừ muốn thương xót những người Muslim, nhưng Allah xác nhận chúng là những kẻ nói dối.');
INSERT INTO "vn_mokhtasar" VALUES(9,108,'9:108','Với Masjid như thế, Ngươi - hỡi Thiên Sứ - không được đáp lại lời mời gọi của những kẻ đạo đức giả khi họ mời Ngươi đến cầu nguyện trong đó. Quả thật, Quba'' là Masjid đầu tiên được xây dựng trên lòng kính sợ Allah xứng đáng để cầu nguyện trong đó hơn Masjid mà họ xây dựng trên vô đức tin. Trong Masjid Quba'' có người yêu thích việc thanh sạch bản thân từ những chất dơ, ô uế bằng nước, và tẩy sạch những tội lỗi với sự sám hối và cầu xin sự tha thứ, và Allah yêu thương những người luôn tẩy sạch khỏi chất dơ, ô uế và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(9,109,'9:109','Chẳng lẽ những người xây dựng Masjid trên lòng ngay chính sợ Allah và luôn tuân theo mệnh lệnh của Ngài và tránh xa những gì Ngài cấm và muốn được sự hài lòng từ Allah trong sự mở rộng những việc làm tốt đẹp lại ngang bằng với người xây dựng Masjid để làm hại những người có đức tin và để làm tăng sự phủ nhận cũng như để chia rẽ giữa những người tin tưởng? Chắc chắn không bao giờ có sự ngang bằng đó! Cái đầu tiên được xây dựng với cấu trúc bền vững và không sợ bị sụp đổ và cái thứ hai giống như được xây trên miệng hố sẽ bị hủy hoại sụp đổ xuống đáy của Hỏa Ngục cùng với y. Vì Allah không hướng dẫn đám người hư đốn bằng sự phủ nhận đức tin và đạo đức giả và những người tương tự.');
INSERT INTO "vn_mokhtasar" VALUES(9,110,'9:110','Masjid mà họ đã xây dựng sẽ vẫn gây hại, tạo ra sự ngờ vực và đạo đức giả. Nó vẫn tồn tại trong con tim của bọn họ trừ phi trái tim của họ bị cắt đi bởi cái chết hoặc bị giết bằng lưỡi kiếm. Allah hằng biết và thông suốt những việc làm của đám bầy tôi của Ngài, Ngài rất mực khôn ngoan trong việc xét xử, Ngài sẽ thưởng phạt cho việc làm tốt hoặc xấu.');
INSERT INTO "vn_mokhtasar" VALUES(9,111,'9:111','Quả thật, Allah đã mua sinh mạng của những người có đức tin - trong khi họ là nô lệ của Allah - bằng phần thưởng vô cùng lớn, đó là Thiên Đàng, khi họ đã chiến đấu chống lại những kẻ phủ nhận để giơ cao đại danh của Allah. Họ đã giết địch và bị địch giết và Allah đã hứa cho họ bằng một lời hứa xác thực trong Tawrah - Kinh Sách được ban cho Musa, và trong Injil - Kinh Sách được ban cho Ysa, và trong Qur''an - Kinh Sách được mặc khải cho Sứ Giả Muhammad. Và không một ai giữ trọn lời hứa hơn Allah Tối Cao. Bởi thế, các ngươi hãy hoan hỉ và vui mừng hỡi những người có đức tin bởi sự đổi chác mà các ngươi đã giao dịch với Ngài, quả thật các ngươi đã được phần lời rất lớn trong đó, và cuộc đổi chác đó là một thắng lợi vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(9,112,'9:112','Những người đạt được phần thưởng này là những người từ bỏ những điều Allah cấm và giận dữ để đến với những gì Ngài yêu thương và hài lòng; những người sợ hãi và khiêm tốn trước Allah, nhanh chóng phục tùng Ngài; nhhững người tán dương Thượng Đế của họ ở mọi tình huống trong mọi hoàn cảnh; những người nhịn chay; những người dâng lễ nguyện Salah, và những người kêu gọi những điều mà Allah và Sứ Giả của Ngài kêu gọi, ngăn cấm những gì mà Allah và Thiên Sứ của Ngài cấm; những người giữ gìn việc tuân theo mệnh lệnh của Allah và tránh xa những điều Ngài nghiêm cấm. Và Ngươi - hỡi Sứ Giả - hãy báo tin mừng cho những người có đức tin với những mô tả ấy rằng họ sẽ được hạnh phúc trong đời này và cả Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(9,113,'9:113','Nabi và những người có đức tin không được phép cầu xin Allah tha thứ cho những người dân thờ đa thần cho dù đó là bà con ruột thịt của họ, sau khi đã chứng minh rõ ràng rằng những người họ hàng đó là những người bạn của Hỏa Ngục, bởi vì họ đã chết trong tình trạng thờ đa thần.');
INSERT INTO "vn_mokhtasar" VALUES(9,114,'9:114','Và nguyên nhân khiến Ibrahim phải cầu xin Allah tha thứ cho người cha của mình là vì Người đã hứa với cha Người rằng Người cầu xin cho ông, với hi vọng là ông sẽ phủ phục Allah. Nhưng sau khi Ibrahim biết rõ rằng cha của Người là kẻ thù của Allah và những lời khuyên giải không còn ý nghĩa gì nữa, hoặc sau khi được mặc khải biết ông sẽ chết với tình trạng phủ nhận đức tin thì Người đã đoạn tuyệt với ông. Thật ra lời cầu xin đó chỉ bắt nguồn từ tấm lòng nhân hậu của Người, chứ Người không cố ý nghịch lại lời mặc khải của Allah. Quả thật Ibrahim [cầu xin sự bằng an và phước lành cho Người] rất nhanh nhẹn trong việc hối cải và thường xuyên xí xóa mọi lỗi lầm mà dân chúng đã hại mình.');
INSERT INTO "vn_mokhtasar" VALUES(9,115,'9:115','Và Allah đã không phán quyết ai là kẻ lầm lạc sau khi Ngài đã ban cho họ sự hướng dẫn mãi đến khi Ngài giải thích cho họ thấy những điều cấm kỵ để họ biết mà tránh. Nhưng nếu họ phạm phải những điều cấm sau khi Ngài đã giải thích rõ thì Ngài sẽ phán quyết họ là những người lạc lối. Quả thật, Allah am tường mọi việc, không có gì có thể che giấu được Ngài, và chắc chắn Ngài đã dạy cho các ngươi mọi thứ trong khi trước đây các ngươi không biết gì cả.');
INSERT INTO "vn_mokhtasar" VALUES(9,116,'9:116','Quả thật, Allah nắm quyền thống trị các tầng trời và trái đất, hoàn toàn không có đối tác cùng với Ngài trong đó. Không gì có thể che giấu được Ngài. Ngài ban sự sống cho ai Ngài muốn và làm chết người nào Ngài muốn. Thế còn các ngươi - hỡi nhân loại - ngoài Allah các ngươi không có vị bảo hộ nào có thể giải quyết những vấn đề của các ngươi, và các ngươi không có một vị cứu tin nào có thể cứu các người khỏi những điều xấu xa, và giúp các ngươi chiến thắng kẻ thù.');
INSERT INTO "vn_mokhtasar" VALUES(9,117,'9:117','Quả thật, Allah hằng quay lại tha thứ cho Nabi Muhammad [cầu xin bình an và phúc lành cho Người] khi Người cho phép những kẻ đạo đức giả không tham gia chiến đấu trong trận Tabuk, và Ngài cũng tha thứ cho những những người Muhajirin (những người di cư từ Makkah đến Madinah) và tha thứ cho những người Ansar (cư dân Madinah) họ đã không từ bỏ Người mà luôn sát cánh cùng với Người trong trận chiến Tabuk cho dù phải đối mặt với sức nóng khắc nghiệt của thời tiết và quân trang thô sơ đối chọi với sức mạnh của kẻ thù; mặc dù có một số trái tim có xu hướng từ bỏ cuộc chiến khi vấp phải sự khó khăn, thiếu thốn. Nhưng Allah đã làm họ vững vàng và xung trận, Ngài đã tha thứ cho họ, bởi quả thật Ngài Tối Cao luôn khoan hồng và thương xót họ, và trong sự thương xót của Ngài là chấp nhận sự sám hối, ăn năn từ họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,118,'9:118','Quả thật, Allah đã tha thứ cho ba người gồm Ka''ab bin Malik, Murarah bin Al-Rabi''a và Hilal bin Umaiyah, là những người đã bị khước từ sự sám hối và trì hoãn chấp nhận sám hối của họ khi họ đã không xuất chinh cùng với Thiên Sứ [cầu xin bình an và phúc lành cho Người] đến Tabuk. Và Nabi đã ra lệnh người dân rời bỏ họ làm cho họ rất buồn bã và phiền muộn đến nỗi trái đất như thu hẹp lại cho dù rất nó rộng lớn và thênh thang vì lòng ngực thu hẹp lại do sự cô đơn. Và họ biết rằng không có nơi nào cho họ trú ngụ ngoại trừ đến với riêng một mình Allah. Ngài đã quay lại tha thứ và chấp nhận sự ăn năn của họ bởi quả thật, Ngài là Đấng hằng tha thứ những bầy tôi của Ngài, luôn thương xót họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,119,'9:119','Hỡi những người đã tin nơi Allah, đã tuân theo Thiên Sứ của Ngài và đã làm theo giáo luật mà Ngài đã quy định, các ngươi hãy kính sợ Allah bằng cách tuân thủ những mệnh lệnh của Ngài, và hãy tránh xa những gì Ngài cấm, hãy nhập đoàn với những người chân thật trong đức tin, trong lời nói và hành động. Sẽ không có sự cứu rỗi cho các ngươi nêu không có sự chân thật.');
INSERT INTO "vn_mokhtasar" VALUES(9,120,'9:120','Dân chúng của Madinah và những người du mục sống xung quanh không được bỏ mặc Thiên Sứ của Allah [cầu xin sự bình an và phúc lành cho Người] khi Người đích thân dẫn binh chinh chiến, và họ không được ít kỷ mạng sống của mình và bảo vệ nó hơn mạng sống của Thiên Sứ [cầu xin sự bình an và phúc lành cho Người], mà bắt buộc họ phải đổi mạng sống của họ cho mạng sống của Người. Với bất cứ hình thức khó khăn nào mà họ gặp phải trên đường chinh chiến như đói khát, mệt mỏi, hoặc họ xuống ở vị trí mà có mặt của họ làm cho những người không có đức tin phẫn nộ, hoặc họ bị tổn hại bởi sự giết chóc, bị bắt làm tù binh, bị bại trận ... tất cả đều được Allah ghi chép đó là việc làm công đức của họ. Quả thật, Allah sẽ không làm mất phần thưởng của những người làm điều thiện tốt mà Ngài ban cho họ thật đầy đủ và còn thêm cho họ nữa.');
INSERT INTO "vn_mokhtasar" VALUES(9,121,'9:121','Không có sự chi dùng tài sản nào dù ít hay nhiều và không có sự khổ ải nào cũng như không có việc vượt qua các thung lũng nào bị bỏ sót, tất cả đều được ghi công đầy đủ. Rồi Allah sẽ trọng thưởng cho họ phần thưởng ở Đời Sau tốt hơn những điều mà họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(9,122,'9:122','Và những người có đức tin không bắt buộc tất cả phải đi chiến đấu để những người ở lại khỏi bị kẻ thù tấn công. Vì vậy hãy tham chiến với một nhóm người trong họ và hãy ở lại một nhóm đồng hành cùng với Thiên Sứ của Allah [cầu xin bằng an và phước lành cho Người] để trao dồi kiến thức tôn giáo qua việc giảng truyền của Người từ Qur''an và những giáo luật Shariah, và để cảnh báo người dân khi quay về với họ từ những gì các người chưa học hỏi được. Hi vọng với cảnh báo đó họ biết cách tránh xa sự trừng phạt của Allah, biết tuân theo mệnh lệnh và tránh xa những điều Ngài cấm. Và đó là đội quân được Thiên Sứ của Allah gửi đến các khu vực, và Người đã chọn cho đội quân đó những bạn đạo của Người.');
INSERT INTO "vn_mokhtasar" VALUES(9,123,'9:123','Allah Tối Cao ra lệnh cho những người có đức tin chiến đấu với những kẻ ngoại đạo đang ở gần, vì họ là nguyên nhân gây ra sự nguy hiểm cho các tín đồ có đức tin khi họ ở gần, và Ngài cũng ra lệnh cho những người có đức tin thể hiện sức mạnh bởi vì sự đe dọa khủng bố của những kẻ phủ nhận và để chống lại cái tàn ác của họ. Allah Tối Cao luôn sát cánh cùng với những người có đức tin bằng sự giúp đỡ và hỗ trợ từ Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(9,124,'9:124','Và khi Allah mặc khải chương Kinh cho vị Sứ Giả [cầu xin bình an và phúc lành cho Người] của Ngài thì một số trong những kẻ đạo đức giả đã hỏi bằng những lời nhạo báng và châm biếm: "Ai trong số các vị sẽ tăng thêm đức tin từ chương Kinh đã được mặc khải cho Muhammad?" Riêng những người có đức tin với Allah và thật lòng với Thiên Sứ của Ngài thì sẽ tăng thêm đức tin chồng lên đức tin trước đây của họ và họ hoan hỉ từ những gì đã được mặc khải xuống vì trong sự mặc khải đó mang lại lợi ích cho họ trong trần gian và cả Ngày Sau.');
INSERT INTO "vn_mokhtasar" VALUES(9,125,'9:125','Còn đối với những tên đạo đức giả thì sự mặc khải Thiên Kinh Quran mà trong đó chứa đựng những giáo luật và những câu chuyện chỉ làm cho bệnh tình trong lòng của họ thêm trầm trọng vì sự dối trá của họ về những gì được mặc khải. Căn bệnh trong lòng họ trầm trọng thêm khi sự mặc khải Qur''an tăng lên. Bởi vì họ luôn nghi ngờ khi nghe những lời mặc khải và họ chết trong tình trạng phủ nhận đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(9,126,'9:126','Há những kẻ đạo đức giả chúng không nhìn thấy những tai họa từ Allah giáng lên họ để phơi bày tình trạng và phơi bày đạo đức giả của họ mỗi năm một hoặc hai lần hay sao? Sau những tai họa chúng biết rằng quả thật Allah chính là Đấng đã làm điều đó nhưng chúng vẫn không chịu ăn năn sám hối với Allah, và chúng không từ bỏ thói đạo đức giả, và cũng không chịu nghĩ những tai họa đã đến với chúng quả thật từ Allah.');
INSERT INTO "vn_mokhtasar" VALUES(9,127,'9:127','Và khi Allah ban xuống chương kinh cho vị Sứ Giả [cầu xin Allah ban bình an và phúc lành cho Người] của Ngài trong đó mô tả tình trạng của những kẻ đạo đức giả họ nhìn nhau và hỏi "Có người nào nhìn thấy quí vị hay không?" và nếu ai không nhìn thấy họ thì họ sẽ rời bỏ nơi tụ họp. Chẳng phải Allah đã xoay đổi tấm lòng của họ từ sự hướng dẫn và điều tốt đẹp và bỏ rơi họ bởi vì họ là một đám người không hiểu gì cả.');
INSERT INTO "vn_mokhtasar" VALUES(9,128,'9:128','Quả thật, đã đến với các người - hỡi người Ả Rập - một vị Sứ Giả xuất thân từ các ngươi, Y cũng là một n người dân ngươi Ả Rập giống như các ngươi. Y đau buồn khi thấy các ngươi đau buồn, Y vô cùng quan tâm đến việc hướng dẫn các ngươi và luôn lo lắng cho các ngươi. Và đặc biệt đối với những tín đồ tin tưởng Y luôn yêu thương và nhân từ với họ.');
INSERT INTO "vn_mokhtasar" VALUES(9,129,'9:129','Nếu họ chống đối Ngươi và không chịu tin tưởng vào những gì Ngươi mang đến thì hãy nói với họ - hỡi Thiên Sứ -: Allah đủ cho Ta, Đấng mà không có Thượng Đế nào khác đáng được thờ phụng ngoài Ngài, duy chỉ Ngài là Thượng đế để thờ phụng và Ngài là Chủ Nhân của chiếc Ngai Vương vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(10,1,'10:1','Alif. Lam. Ra. Những chữ cái mở đầu như thế này đã được nói trước đây ở đầu chương Al-Baqarah. Những câu Kinh trong chương này là những câu Kinh của Qur''an mạch lạc, chính xác, chứa đựng sự khôn ngoan và các giáo luật.');
INSERT INTO "vn_mokhtasar" VALUES(10,2,'10:2','Phải chăng thiên hạ lại ngạc nhiên khi Allah mặc khải Kinh Sách cho người phàm xuất thân từ họ hay sao? Họ được lệnh phải cảnh báo thiên hạ về sự trừng phạt của Allah. Và Ngươi - hỡi Thiên Sứ - hãy thông báo cho những ai tin tưởng nơi Allah về tin mừng dành cho họ, quả thật rằng họ có một vị trí cao nơi Allah, một phần thưởng cho việc họ đã làm từ những việc làm ngoan đạo. Nhưng những kẻ không có đức tin lại nói: Quả thật người đàn ông đã mang đến những câu Kinh này chỉ là một tên phù thủy.');
INSERT INTO "vn_mokhtasar" VALUES(10,3,'10:3','Quả thật, Thượng Đế của các ngươi - hỡi những ai cảm thấy kinh ngạc - chính Allah là Đấng đã tạo ra các tầng trời với sự vĩ đại của nó, và trái đất với sự thênh thang của nó chỉ trong sáu ngày, rồi Ngài thăng lên an vị trên Ngai Vương của Ngài. Thế tại sao các ngươi lại ngạc nhiên khi Ngài gửi một người phàm xuất thân từ họ?! Duy chỉ Ngài là Đấng quyết định và có khả năng trên tất cả các tạo vật bằng vương quyền bao la của Ngài, và không một ai có thể can thiệp với Ngài trong bất cứ điều gì ngoại trừ Ngài cho phép và hài lòng, Thượng Đế của các ngươi là Allah, Ngài sở hữu những thuộc tính như thế đấy, do đó các ngươi hãy chỉ thờ phụng riêng Ngài. Chẳng lẽ các ngươi không suy nghĩ về tất cả những bằng chứng và lập luận về sự độc tôn của Ngài hay sao? Và ai có thể tiếp cận cảnh báo thì sẽ nhận thức điều đó và sẽ tin tưởng nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(10,4,'10:4','Tất cả các ngươi sẽ được đưa trở về gặp Ngài vào Ngày Phán Xét Cuối Cùng, để thưởng, phạt cho những việc làm của các ngươi. Lời hứa của Allah là sự thật không thể bị phá vỡ, vì quả thật Ngài thừa khả năng cho việc đó. Ngài khai tạo vạn vật không cần dựa vào hình mẫu có sẵn, rồi Ngài lại khôi phục tất cả sống lại sau khi đã chết để tưởng thưởng những ai có đức tin nơi Ngài và những ai làm việc thiện bằng sự công bằng tuyệt đối. Ngài không giảm bớt những điều tốt đẹp của họ và cũng không thêm vào tội lỗi của họ. Còn những ai phủ nhận Allah và Sứ Giả của Ngài thì sẽ uống một loại nước cực kỳ nóng, nó sẽ cắt đứt ruột gan của họ và họ sẽ phải chịu một hình phạt rất đau đớn vì họ đã phủ nhận Allah và Sứ Giả của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(10,5,'10:5','Chính Ngài đã làm cho mặt trời chói rọi tỏa ánh sáng khắp nơi, và Ngài làm cho mặt trăng chiếu sáng, và quy định cho nó những vị trí mọc lặn, tròn khuyết là hai mươi tám địa điểm khác nhau, và mỗi địa điểm là một ngày đêm, để các ngươi - hỡi con người - với mặt trời có thể biết được số ngày và với mặt trăng có thể biết được số tháng và năm. Allah không tạo hóa những tầng trời, trái đất và những gì giữa các tầng trời và trái đất ngoại trừ là vì chân lý, và để thể hiện quyền năng và sự vĩ đại của Ngài cho nhân loại, Allah đã trình bày cho thấy những bằng chứng rõ ràng và xác thực về tính độc tôn của Ngài để những người có hiểu biết có thể suy luận bởi những bằng chứng đó.');
INSERT INTO "vn_mokhtasar" VALUES(10,6,'10:6','Quả thật, trong sự luân chuyển giữa ban đêm và bàn ngày mà đám bầy tôi của Ngài chứng kiến, và ai là chủ nhân của bóng tối và ánh sáng, và khoảng thời gian dài hay ngắn giữa chúng ngày và đêm. Những tạo hóa của Ngài trong các tầng trời và trái đất đều là dấu hiệu về quyền năng siêu việt của Allah cho những người kính sợ Ngài mà tuân theo những mệnh lệnh và tránh xa những điều Ngài cấm.');
INSERT INTO "vn_mokhtasar" VALUES(10,7,'10:7','Quả thật, những kẻ phủ nhận, không mong gặp Allah, không kính sợ Ngài và Không khao khát được gặp Ngài, họ đã hài lòng về cuộc sống ngắn ngủi của trần tục này thay vì cuộc sống vĩnh viễn ở Đời Sau, họ vui thích định cư nơi đó cùng với những kẻ không lưu ý đến những dấu hiệu của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(10,8,'10:8','Những kẻ với những mô tả đó, vào Ngày Phán Xét Cuối Cùng chỗ cư ngụ của họ chính là Hỏa Ngục bởi những gì họ đã làm như phủ nhận và dối trá.');
INSERT INTO "vn_mokhtasar" VALUES(10,9,'10:9','Quả thật, những ai tin tưởng vào Allah và làm việc thiện thì Allah sẽ ban cho họ sự hướng dẫn đến với những việc làm tốt đẹp đưa họ đến với sự hài lòng của Ngài. Và với đức tin của họ, rồi đầy vào Ngày Phán Xét Allah sẽ cho họ vào Thiên Đàng hạnh phúc bên dưới có các dòng sông chảy. Họ sẽ sống mãi mãi trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(10,10,'10:10','Lời cầu nguyện của họ trong Thiên Đàng sẽ là lời táng dương Allah và tôn vinh Ngài, và lời chào của Allah dành cho họ và lời chào của những Thiên Thần với họ và lời chào giữa họ với nhau đều là Salam (cầu xin bằng an cho quí vị). Cuối cùng là lời cầu nguyện của họ sẽ là ca tụng Allah Thượng Đế của toàn vũ trụ và muôn loài.');
INSERT INTO "vn_mokhtasar" VALUES(10,11,'10:11','Và nếu Allah vội vã chấp nhận lời cầu xin của con người để hại bản thân, hại con cái và hại tài sản của họ mỗi khi giận, cũng như vội vã chấp nhận lời cầu xin của họ về điều tốt thì tất cả họ đã bị diệt vong mất rồi, nhưng Allah đã cố tình trì hoãn nó. Vì vậy Ngài bỏ mặc những kẻ không màng đến việc gặp Ngài ở Đời Sau - bởi vì họ không sợ sự trừng phạt cũng như họ không hy vọng được phần thưởng tốt đẹp gì cả - Ngài bỏ mặc những kẻ luôn nghi ngờ, không tin tưởng vào Ngày Phán Xét Cuối Cùng.');
INSERT INTO "vn_mokhtasar" VALUES(10,12,'10:12','Và khi con người gặp phải những chuyện không tốt đến với bản thân họ như bệnh hoạn hay hoạn nạn, họ van vái TA (Allah) một cách khiêm nhường, nài nỉ, ở mọi tư thế lúc nằm, lúc ngồi hoặc lúc đứng với hi vọng sẽ được Ngài giải nạn cho họ. Nhưng khi TA chấp nhận lời cầu xin của họ và giải cứu họ khỏi cơn nguy khốn thì họ bước đi dửng dưng làm như họ chẳng hề cầu xin TA giải cứu họ khi gặp hoạn nạn bao giờ. Với thái độ vong ơn nó đã tô điểm làm đẹp cho những kẻ phạm tội tiếp tục trên con đường tội lỗi của mình và vượt quá giới luật về mặt vô đức tin, tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(10,13,'10:13','Quả thật, TA đã tiêu diệt những cộng đồng trước các ngươi - hỡi những kẻ đa thần - vì họ đã phủ nhận những sứ giả của Allah và làm điều sai quấy, quả thật những Sứ Giả đã đến với họ được TA gửi đếncùng với những bằng chứng rõ ràng xác thực, nhưng họ vẫn không tin tưởng vì họ vốn không có đức tin.Bởi thế, Allah đã bỏ rơi họ và Ngài cũng không phù hộ. Tương tự, TA đã trừng phạt những cộng đồng làm đều tội lỗi ở khắp mọi nơi, mọi thời đại.');
INSERT INTO "vn_mokhtasar" VALUES(10,14,'10:14','Rồi TA đã bổ nhiệm các ngươi - hỡi loài người - trở thành cộng đồng tiếp theo thay thế cho cộng đồng giả dối mà TA đã tiêu diệt trước đây, để TA xem các ngươi cư xử như thế nào. Nếu các ngươi làm tốt thì sẽ được khen thưởng xứng đáng còn nếu các ngươi gây tội lỗi sẽ bị trừng phạt thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(10,15,'10:15','Và khi xướng đọc cho họ nghe những lời mặc khải từ Thiên Kinh Qur''an với những bằng chứng rõ ràng chứng minh thuyết độc thần của Allah thì những kẻ phủ nhận Ngày Phục Sinh vốn không màng đến hình phạt nói rằng: Này Muhammad, Ngươi hãy mang cho bọn ta một quyển khác quyển Qur''an này không bao gồm lời phỉ báng sự thờ phượng bục tượng hoặc xóa bỏ một phần của quyển Kinh này hoặc thay thế cả quyển phù hợp với sở thích của bọn ta. Ngươi hãy bảo họ - hỡi Sứ Giả - Ta không có thẩm quyền thay đổi Nó, và tất nhiên Ta không thể mang đến quyển khác, chỉ Allah duy nhất mới có thể thay đổi những gì Ngài muốn, Ta chỉ biết tuân theo điều đã được Allah mặc khải cho Ta, Ta sợ mình phải bị cực hình của Allah trong Ngày Vĩ Đại, đó là Ngày Tận Thế, nếu làm theo nguyện vọng của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(10,16,'10:16','Ngươi - hỡi Thiên Sứ - hãy bảo họ: Nếu Allah không muốn Ta đọc Qur''an này thì Ta đã không đọc cho các ngươi nghe rồi, và Ta cũng sẽ không truyền đạt Nó đến các ngươi, và nếu Allah muốn Ngài đã không cho các người biết về Qur''an này từ chiếc lưỡi của Ta qua việc xướng đọc Nó. Thật sự ta đã sống giữa các ngươi khoảng thời gian rất dài - đến bốn mươi năm - với các ngươi, Ta vốn mù chữ không biết đọc cũng không biết viết, Ta không yêu cầu hay tìm kiếm để có được Nó. Không lẽ các ngươi không nhận ra rằng những gì Ta mang đến đều từ Allah chứ Ta không phải là kẻ bịa đặt ra Nó.');
INSERT INTO "vn_mokhtasar" VALUES(10,17,'10:17','Không một ai có thể bất công hơn người nói dối rồi đổ thừa cho Allah, thế thì làm sao Ta có thể tự ý thay đổi Qur''an rồi nói dối là từ Ngài. Quả thật, những kẻ vượt quá giới luật của Allah bằng cách vu khống cho Ngài thì không bao giờ thắng được những gì họ muốn.');
INSERT INTO "vn_mokhtasar" VALUES(10,18,'10:18','Và những người đa thần tôn thờ ngoài Allah những thần linh do chính họ bịa đặt trong khi chúng không mang lại lợi ích cũng chẳng làm hại họ được gì. Còn Thượng Đế đích thực có thể cứu giúp hoặc trừng phạt bất cứ khi nào Ngài muốn, vậy mà họ biện minh nói: Những vị này là trung gian để can thiệp giùm chúng tôi với Allah để Ngài không trừng phạt chúng tôi vì những tội lỗi của chúng tôi. Ngươi - hỡi Thiên Sứ - hãy bảo họ: "Phải chăng các người muốn báo cho Allah, Đấng Am Tường rằng Ngài hiện còn có đối tác ngang hàng ư trong khi Ngài biết rõ là Ngài không có bất cứ đối tác nào cùng Ngài, không ở các tầng trời và cũng không ở dưới đất? Thật vinh quang và trong sạch thay Ngài, Ngài hoàn toàn vô can với mọi điều mà họ đã dối trá và bịa đặt cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(10,19,'10:19','Và trước đây nhân loại là một cộng đồng thống nhất trong đức tin nhưng về sau họ bất đồng ý kiến với nhau. Trong số họ có nhóm vẫn còn là người tin tưởng và số khác là những kẻ phủ nhận. Nếu không vì một lời phán mà Allah đã tuyên bố trước đây, đó là Ngài không giải quyết những bất đồng của họ ở thế gian này mà Ngài sẽ giải quyết bất đồng giữa họ ở Ngày Tận Thế. Nếu không phải như vậy là Ngài đã giải quyết sự bất đồng của họ ở thế gian này, rồi Ngài sẽ chỉ rõ ai là người được hướng dẫn và ai là những kẻ lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(10,20,'10:20','Nhóm người đa thần bảo: "Tại sao không có một phép là nào được ban cho Muhammad từ Thượng Đế của Y để chứng minh Y là một vị Sứ Giả thật sự?" Ngươi - hỡi Thiên Sứ - hãy đáp lại họ: "Mặc khải một phép lạ là điều vô hình thuộc về kiến thức riêng của Allah. Nhưng thôi, các ngươi hãy chờ đợi lời đề nghị của các ngươi về một phép lạ, Ta sẽ cùng chờ đợi phép lạ đó với các ngươi."');
INSERT INTO "vn_mokhtasar" VALUES(10,21,'10:21','Và Khi TA ban sự khoan dung cho những người đa thần bằng những cơn mưa và sự phì nhiêu, hưng thịnh sau sự khô cằn, hạn hán mà họ gặp phải, để rồi chúng chế nhạo và phủ nhận những dấu hiệu của TA. Ngươi - hỡi Thiên Sứ - hãy bảo với đám người đa thần: Allah mưu tính còn nhanh hơn các ngươi, và Ngài sẽ nhanh chóng đưa các ngươi đến với sự trừng phạt của Ngài. Quả thật, những Thiên Thần luôn ghi chép tất cả các kế hoạch mà các ngươi mưu định không bỏ sót một điều gì, làm sao các ngươi có thể qua mặt Đấng đã tạo ra các ngươi?! Và Allah sẽ tính sổ các ngươi về những mưu tính của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(10,22,'10:22','Allah Ngài chính là Đấng giúp các ngươi - hỡi nhân loại - du lịch trên đất liền bằng đôi chân của các ngươi và bằng những con vật cưỡi của các ngươi, và Ngài cũng giúp các ngươi du lịch trên biển cả bằng những con tàu. Khi các ngươi ở trên tàu và gặp những cơn gió tốt lành làm cho các ngươi thuận buồm xuôi gió thì các ngươi vui thú. Nhưng khi một trận bão nổi lên và những cơn sóng khổng lồ vây bủa họ tứ phía, họ nghĩ rằng tất cả họ sẽ bị hủy diệt, họ tha thiết khẩn cầu chỉ riêng một mình Allah, không tổ hợp Ngài với bất cứ thứ gì, họ thưa rằng: "Nếu Ngài giải cứu bầy tôi thoát khỏi cơn nguy khốn này thì bầy tôi sẽ là những người biết ơn vì những gì Ngài đã ban cho bầy tôi".');
INSERT INTO "vn_mokhtasar" VALUES(10,23,'10:23','Nhưng sau khi Ngài chấp nhận lời cầu xin của họ và giải cứu họ thoát khỏi nguy khốn thì chính họ lại là những kẻ phá hoại trên trái đất bằng sự phủ nhận và làm những điều bất công và tội lỗi. Hỡi loài người, hình phạt dành cho các ngươi là do việc làm xấu xa của các ngươi và Allah không hề bị ảnh hưởng một chút nào. Những lạc thú mà các ngươi hưởng thụ trên đời sống trần tục này chỉ là tạm thời, sau đó các ngươi sẽ được đưa về gặp TA vào Ngày Phán Xét Cuối Cùng rồi TA sẽ cho các ngươi biết về những tội lỗi mà các ngươi đã phạm và TA sẽ trừng phạt các ngươi về những tội lỗi đó.');
INSERT INTO "vn_mokhtasar" VALUES(10,24,'10:24','Quả thật, đời sống trần tục mà các ngươi đang hưởng lạc, nó sẽ kết thúc rất nhanh giống như hình ảnh của cơn mưa rào khiến nước dung nạp với đất đai để làm mọc ra đủ loại hoa màu cung cấp thực phẩm cho loài người từ củ, hạt và quả, và cho muôn thú từ cây cỏ và những thứ khác. Cho đến lúc mặt đất khoác lên mình một màu tươi sáng và được trang hoàng bởi nhiều loại thực phẩm thì những người chủ của hoa mầu nghĩ rằng họ có khả năng toàn quyền gặt hái và thu hoạch toàn bộ số hoa mầu của họ. Nhưng định mệnh của TA bắt đầu có hiệu lực cho việc phá hủy toàn bộ sô hoa màu. Thế là TA tàn phá chúng toàn bộ giống như chúng chưa từng có những cây cối và thực phẩm trước đó. Như TA đã giải thích cho các ngươi tình trạng của đời sống trần tục và sự kết thúc nhanh chóng của nó. TA đã giải thích và đưa bằng chứng như thế cho những người biết suy ngẫm và cân nhắc.');
INSERT INTO "vn_mokhtasar" VALUES(10,25,'10:25','Allah kêu gọi tất cả loài người đến với Thiên Đàng của Ngài và đó là ngôi nhà Bằng An, nơi mà mọi người sẽ được an lành không bị bất cứ tai họa hay buồn phiền nào và họ cũng sẽ không bao giờ phải chết nữa. Và Allah Ngài hướng dẫn cho bất kì ai Ngài muốn từ những bề tôi của Ngài đến với tôn giáo Islam để họ được đến với ngôi nhà Bằng An này.');
INSERT INTO "vn_mokhtasar" VALUES(10,26,'10:26','Những ai làm tốt những nhiệm vụ bắt buộc mà Allah đã giao phó cho họ như sự tuân lệnh Ngài, và từ bỏ những điều cấm từ những việc làm sai trái, phần thưởng tốt đẹp nhất dành cho họ chính là Thiên Đàng và họ sẽ nhận được nhiều hơn nữa, đó là được ngắm nhìn khuôn mặt hồng phúc của Allah, và những khuôn mặt của họ sẽ không bị che phủ bởi bụi mờ, họ sẽ không bị xấu hổ và nhục nhã. Những người được giống như lời miêu tả tốt đẹp là những cư dân của Thiên Đàng và họ sẽ ở trong đó mãi mãi.');
INSERT INTO "vn_mokhtasar" VALUES(10,27,'10:27','Còn những ai chuyên làm những điều xấu xa gồm sự vô đức tin, tội lỗi, họ sẽ bị trừng phạt ngang bằng với những xấu xa mà họ đã phạm, đó là sự trừng phạt của Allah vào Ngày Sau. Và gương mặt của họ sẽ bị bao trùm bởi những điều nhục nhã, sẽ không có ai có thể giúp họ thoát khỏi hình phạt của Allah nếu Ngài trừng phạt họ. Gương mặt của họ sẽ đen đúa như màn đêm tăm tối bị phủ bởi lớp khói đen xì, những người vừa được miêu tả có những phẩm chất xấu xa này sẽ là cư dân của Hỏa Ngục, họ sẽ phải ở trong đó vĩnh viễn.');
INSERT INTO "vn_mokhtasar" VALUES(10,28,'10:28','Hãy ghi nhớ - hỡi Sứ Giả - vào Ngày Phán Xét Cuối Cùng khi TA tập trung tất cả tạo vật, rồi TA sẽ bảo những kẻ đã tổ hợp với Allah cùng những thần linh khác trên thế gian này: "Hỡi những kẻ đa thần và thần linh của các ngươi tôn thờ ngoài Allah, các ngươi hãy ở nguyên vị trí của các ngươi". Rồi TA tách biệt giữa những kẻ tôn thờ và những thần linh được tôn thờ, và khi những kẻ đa thần được tách ra khỏi những thần linh của họ thì các thần linh của họ sẽ bảo: "Các ngươi đâu có thờ phượng bọn ta trên thế gian".');
INSERT INTO "vn_mokhtasar" VALUES(10,29,'10:29','Tại đây, các thần linh được thờ phượng phủ nhận những kẻ thờ phượng chúng thay vì Allah, chúng nói: "Allah là nhân chứng - chỉ Ngài là đủ - và bọn ta không chấp nhận việc tôn thờ của các ngươi dành cho bọn ta, và bọn ta đã không ra lệnh các ngươi làm điều đó, vả lại bọn ta không hay biết gì về việc thờ phượng của các ngươi cả".');
INSERT INTO "vn_mokhtasar" VALUES(10,30,'10:30','Tại thời điểm trọng đại đó, mỗi một người sẽ được biết đầy đủ về những việc đã làm trên trần gian. Và những người đa thần sẽ được đưa trở về với Allah - Thượng Đế đích thực của họ, Ngài nắm quyền trừng phạt họ, và những thần linh mà họ bịa đặt đã bỏ họ chạy mất dạng cùng với lời biện hộ mà chúng đã khoác lác trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(10,31,'10:31','Ngươi - hỡi Thiên Sứ - hãy hỏi những kẻ đa thần: Ai cấp dưỡng các ngươi từ trên trời với việc ban mưa xuống cho các ngươi? Ai cấp dưỡng cho các ngươi ở dưới đất với việc làm đâm chồi nảy lộc cây cối, và với những kho báu trong đó? Và ai đã đưa cái sống ra từ cái chết như việc con người được hình thành từ tinh dịch, và loài chim được nở từ trứng? Và ai đã đưa cái chết ra khỏi sự sống như tinh dịch từ động vật và trứng từ loài chim? Và ai quản lý định đoạt công việc của bầu trời và trái đất và những tạo hóa ở trong đó? Họ sẽ trả lời: Tất cả những đều này là từ Allah. Ngươi hãy bảo họ: Các ngươi biết như thế mà không kính sợ Allah hay sao? Các ngươi biết như thế mà không tuân theo mệnh lệnh của Ngài và tránh xa những đều cấm của Ngài ư?!');
INSERT INTO "vn_mokhtasar" VALUES(10,32,'10:32','Như vậy đó - hỡi nhân loại - Đấng đã thực hiện tất cả những công việc đó chính là Allah, Ngài là Thượng Đế đích thực đã tạo hóa ra các ngươi, Ngài quản lý hành động của các ngươi. Thế tại sao các ngươi lại đi vào lầm lạc sau khi đã nhận thức được chân lý? Các ngươi để đầu óc các ngươi ở nơi đâu trước sự thật quá rõ ràng như thế?!');
INSERT INTO "vn_mokhtasar" VALUES(10,33,'10:33','Giống như việc khẳng định việc tạo hóa chỉ thuộc riêng về Allah thì lời phán của Thượng Đế của Ngươi - hỡi Sứ Giả - cũng đã định đoạt cho đám người ngoan cố chống lại chân lý rằng họ sẽ không bao giờ tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(10,34,'10:34','Ngươi - hỡi Thiên Sứ - hãy hỏi những kẻ đa thần: "Trong số các thần linh mà các ngươi tôn thờ ngoài Allah, ai có khả năng khởi sự tạo hóa từ cái không là gì trước đây rồi dựng chúng sống trở lại sau khi đã chết"? Ngươi hãy bảo chúng: "Chính Allah đã khởi sự tạo hóa từ cái không là gì trở thành hiện hữu rồi cũng chính Ngài phục sinh chúng lại sau khi đã chết". Sao các ngươi - hỡi những kẻ đa thần - bỏ đi sự thật để đi theo sự giả dối?!');
INSERT INTO "vn_mokhtasar" VALUES(10,35,'10:35','Ngươi - hỡi Sứ Giả - hãy hỏi họ: "Trong số các thần linh mà các ngươi tôn thờ ngoài Allah, ai có khả năng hướng dẫn đến chân lý? Và hãy bảo họ: "Chỉ riêng Allah mới là Đấng hướng dẫn đến được chân lý". Phải chăng Đấng hướng dẫn đến với chân lý không đáng được tuân theo mà phải nghe theo những thần linh của các ngươi, những thần linh mà chúng còn không hướng dẫn được bản thân chúng thì làm sao có thể hướng dẫn được ai khác? Làm sao các ngươi lại bịa chuyện khi tuyên bố rằng những thần linh đó là đối tác của Allah?! Allah Tối Cao vượt hẳn mọi điều mà các ngươi đã gán ghép cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(10,36,'10:36','Và đa số những kẻ đa thần chỉ biết bám theo những điều mà chúng không hề có kiến thức, chúng chỉ làm theo sự phỏng đoán và nghi ngờ. Chắc chắn sự nghi ngờ sẽ không phải là chân lý, và sẽ không có lợi ích gì. Quả thật, Allah am tường hết những điều chúng làm, không có gì có thể che giấu khỏi Ngài từ những hành động của chúng, và Ngài sẽ trừng phạt chúng với những điều chúng đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(10,37,'10:37','Và Thiên Kinh Qur''an không phải là sự bịa đặt, không thể do một ai khác ngoài Allah làm ra, bởi toàn bộ loài người bất lực trong việc tạo ra một quyển Kinh tương tự. Tuy nhiên, sứ mạng của Nó là xác nhận lại mọi việc đã được mặc khải trong các Kinh Sách trước Nó, và giải thích đầy đủ rõ ràng những giáo luật. Và không phải nghi ngờ bất cứ điều gì về Nó bởi đích thực Nó được mặc khải xuống từ Thượng Đế của muôn loài.');
INSERT INTO "vn_mokhtasar" VALUES(10,38,'10:38','Có lẽ những kẻ đa thần sẽ nói: "Chính Muhammad [cầu xin Allah bình an cho Người] đã tự bịa đặt ra Qur''an rồi cho là từ Allah". Ngươi hãy bảo họ - hỡi Sứ Giả -: "Nếu các ngươi cho rằng Ta đã bịa đặt ra Nó trong khi Ta cũng là con người giống như các ngươi, vậy thì các ngươi hãy mang đến một chương giống như chương Kinh của Nó đi và các ngươi hãy gọi bất cứ ai có khả năng đến giúp các ngươi nếu như việc các ngươi khẳng định ''Qur''an được bịa ra'' là sự thật". Chắc chắn các ngươi sẽ không thể và sẽ không có khả năng làm như vậy - trong khi các ngươi là những nhà văn thơ, là bậc thầy của ngôn ngữ và sự hùng biện. Sự việc này đã khẳng định rằng Qur''an đích thực là do Allah mặc khải.');
INSERT INTO "vn_mokhtasar" VALUES(10,39,'10:39','Họ quyết không nghe theo, ngược lại họ vội vã phủ nhận Qur''an trước khi họ nhận thức và hiểu được Nó, và trước khi họ nhận được những lời cảnh báo sự trừng phạt từ Nó trong khi sự trừng phạt đang đến rất gần. Giống như những kẻ trước họ cũng đã phủ nhận sự thật nên đã bị giáng xuống đòn trừng phạt. Ngươi hãy suy ngẫm - hỡi Sứ Giả - hậu quả của những kẻ đã làm điều sai quấy rằng chúng đều đã bị Allah hủy diệt.');
INSERT INTO "vn_mokhtasar" VALUES(10,40,'10:40','Và trong những người đa thần có những người đã tin tưởng Qur''an trước khi họ chết, nhưng vẫn có kẻ bướng bỉnh ngoan cố không tin cho đến khi y qua đời. Nhưng Thượng Đế của Ngươi - hỡi Sứ Giả - thừa biết những kẻ thối nát trong họ và Ngài sẽ trừng phạt họ về việc vô đức tin của họ.');
INSERT INTO "vn_mokhtasar" VALUES(10,41,'10:41','Và nếu dân chúng của Ngươi - hỡi Thiên Sứ - bảo rằng Ngươi nói dối thì Ngươi hãy bảo họ: "Ta chịu trách nhiệm về công việc của Ta và các ngươi chịu trách nhiệm về công việc của các ngươi và hình phạt sẽ giáng lên các ngươi. Các ngươi vô can với công việc của Ta và Ta vô can với sự trừng phạt vì công việc các ngươi đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(10,42,'10:42','Và trong những kẻ đa thần có kẻ lắng nghe Ngươi - hỡi Sứ Giả - khi Ngươi xướng đọc Qur''an cho chúng nghe nhưng chúng một mực từ chối và không nghe, nhưng làm sao Ngươi có thể khiến cho kẻ điếc nghe? Như thế đó Ngươi không thể hướng dẫn những kẻ cố tình giả điếc không chịu nghe chân lý vì chúng không thể nhận thức được.');
INSERT INTO "vn_mokhtasar" VALUES(10,43,'10:43','Và trong những người đa thần, có kẻ chăm chú nhìn Ngươi - hỡi Sứ Giả - bằng đôi mắt ở trên khuôn mặt chúng chứ không phải bằng tâm trí. Làm sao Ngươi có thể soi sáng cho những người có mắt nhưng không nhìn thấy được gì?! Quả thật, Ngươi không thể hướng dẫn cho kẻ không nhìn thấy gì.');
INSERT INTO "vn_mokhtasar" VALUES(10,44,'10:44','Quả thật, Allah hoàn toàn vô can trong việc sai trái của đám bầy tôi của Ngài. Và Ngài không hề bất công với họ dù sự bất công có nhỏ bằng hạt bụi. Nhưng chính họ đã tự bất công với chính bản thân mình, tự đẩy mình vào sự hủy diệt với nguyên nhân vẫn ngoan cố đi theo điều giả dối và không chấp nhận sự thật.');
INSERT INTO "vn_mokhtasar" VALUES(10,45,'10:45','Và vào Ngày Đại Triệu Tập, Allah tập trung tất cả nhân loại lại để tính sổ, lúc đó họ cứ tưởng chừng mình ở trên trần gian chỉ khoảng một tiếng đồng hồ ngắn ngủi trong ngày, họ nhận biết lẫn nhau trong Ngày hôm đó. Nhưng rồi họ sẽ không còn nhận diện nhau nữa khi họ nhìn thấy sự khủng khiếp của Ngày Phán Xét. Chắc chắn sẽ thua thiệt đối với những ai từng phủ nhận việc gặp gỡ với Thượng Đế của họ vào Ngày Sau bởi vì ở trên trần gian đã không tin tưởng vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(10,46,'10:46','Hoặc TA sẽ cho Ngươi thấy - hỡi Sứ Giả - một vài điều mà TA đã hứa với họ về sự trừng phạt trong lúc Ngươi còn sống hoặc TA bắt hồn của Ngươi ra đi trước khi xảy ra điều đã hứa. Trong hai trường hợp đó, trường hợp nào họ cũng sẽ đưa về gặp TA vào Ngày Phán Xét. Rồi Allah sẽ là nhân chứng về những điều họ đã làm, không gì có thể che giấu được Ngài, Ngài sẽ xét xử họ về việc mà họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(10,47,'10:47','Và tất cả cộng đồng trước đây đều đã được Allah phái Sứ Giả đến với họ, và khi Sứ Giả đã đến gặp họ để tuyên truyền những gì Allah bắt buộc họ truyền đạt, nhưng nếu như họ phủ nhận Sứ Giả thì Ngài sẽ phán xét giữa họ và Người một cách công bằng. Thế là Allah cứu vớt Người được bình an bằng hồng phúc của Ngài và hủy diệt những kẻ chống đối bằng lẽ công bằng và họ sẽ không bị đối xử bất công bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(10,48,'10:48','Và những người không tin tưởng thách thức: "Khi nào lời hứa về một sự trừng phạt sẽ xảy ra đối với bọn ta nếu các ngươi nói thật?".');
INSERT INTO "vn_mokhtasar" VALUES(10,49,'10:49','Ngươi - hỡi Thiên Sứ - hãy bảo họ: "Ta không kiểm soát được điều hại và điều lợi ngay cho chính bản thân Ta thì làm thế nào Ta có thể mang điều lợi hay điều hại cho người khác được chứ, ngoại trừ điều nào Allah muốn như vậy, và làm sao Ta có thể biết điều vô hình của Ngài được chứ? Tất cả mỗi cộng đồng đều được Allah ấn định thời hạn hủy diệt nhất định, không một ai có thể biết được thời hạn đó ngoại trừ Ngài, và khi thời hạn hủy diệt đến thì sẽ không chậm trễ hay sớm hơn một giây phút nào.');
INSERT INTO "vn_mokhtasar" VALUES(10,50,'10:50','Ngươi - hỡi Thiên Sứ - hãy bảo những kẻ mong chờ sự trừng phạt: "Các ngươi hãy nói cho Ta biết nếu Allah giáng hình phạt của Ngài lên cho các ngươi vào ban đêm hoặc ban ngày thì hình phạt nào sẽ là cái mà những kẻ tội lỗi các ngươi mong nó chóng đến?!');
INSERT INTO "vn_mokhtasar" VALUES(10,51,'10:51','Hay là khi nào các ngươi bị trừng phạt đúng như lời được hứa thì các ngươi mới chịu tin tưởng ư? Ngay giờ đây các ngươi mới tin ư, trong khi trước đây các ngươi đã giục nó cho chóng đến vì các ngươi đã phủ nhận nó?!');
INSERT INTO "vn_mokhtasar" VALUES(10,52,'10:52','Sau khi bị tống vào sự trừng phạt thì họ cầu xin được thả ra, lúc đó có lời bảo họ: "Các ngươi hãy nếm sự trừng phạt vĩnh viễn ở Đời Sau, phải chăng đây không phải là hình phạt cho việc vô đức tin và tội lỗi của các ngươi đã phạm hay sao?"');
INSERT INTO "vn_mokhtasar" VALUES(10,53,'10:53','Và những kẻ đa thần hỏi Ngươi - hỡi Sứ Giả -: "Có phải sự trừng phạt đã được hứa với bọn ta là sự thật phải không? Ngươi hãy bảo chúng: "Đúng, ta xin thề trước Thượng Đế của Ta, chắc chắn đó là sự thật, và các ngươi sẽ không thể thoát khỏi nó."');
INSERT INTO "vn_mokhtasar" VALUES(10,54,'10:54','Và quả thật, nếu như những kẻ thờ thần linh khác cùng với Allah mang tất cả những tài sản quí giá nhất trên đời này để chuộc tội hầu tránh khỏi sự trừng phạt của Allah. Và những người đa thần sẽ hối hận về tội đã làm khi họ đối diện với sự trừng phạt vào Ngày Phán Xét. Và Allah rất công bằng trong việc xét xử, họ sẽ không bị đối xử bất công, Ngài sẽ phán xử đúng với những hành động mà họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(10,55,'10:55','Chẳng phải Allah là Đấng Duy Nhất sở hữu các tầng trời và trái đất hay sao? Chẳng phải lời hứa của Allah về sự trừng phạt những kẻ không có đức tin là sự thật mà không có một chút nghi ngờ nào đó sao? Nhưng hầu hết bọn họ không biết về điều đó nên đâm ra nghi ngờ.');
INSERT INTO "vn_mokhtasar" VALUES(10,56,'10:56','Ngài, Đấng Vinh Quang ban sự sống và làm cho chết và các ngươi sẽ được đưa về gặp Ngài trở lại vào Ngày Phán Xét, Ngài sẽ phân xử các ngươi trên những việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(10,57,'10:57','Hỡi nhân loại! Quả thật, Qur''an đã được Thượng Đế ban xuống cho các ngươi, trong Nó chứa đựng những lời nhắc nhở, khuyên bảo, răn đe và là một phương thức chữa lành con tim của các ngươi khỏi những căn bệnh nghi ngờ, đố kỵ; là một sự chỉ đạo đến với con đường chân lý, và là một hồng ân cho những người có đức tin; họ sẽ được những lợi ích từ Nó.');
INSERT INTO "vn_mokhtasar" VALUES(10,58,'10:58','Ngươi - hỡi Thiên Sứ - hãy báo cho nhân loại biết: "Những gì đã đến với các ngươi từ Qur''an là hồng ân của Allah đã ban cho các ngươi và là sự khoan dung của Ngài. Vì vậy Ngài đã ban hồng ân và sự khoan dung của Ngài cho các ngươi với việc mặc khải Thiên Kinh Qur''an. Do đó, các ngươi hãy vui hưởng chúng, và Sứ Giả Muhammad [cầu xin Allah cho Người bình an] cũng được cử đến từ Thượng Đế của các ngươi, tất cả tốt hơn cho việc các ngươi tích lũy những tài sản tạm bợ của thế gian ngắn ngủi này.');
INSERT INTO "vn_mokhtasar" VALUES(10,59,'10:59','Ngươi - hỡi Thiên Sứ - hãy bảo những người đa thần: "Các ngươi hãy cho ta biết những ân huệ mà Allah đã cho các ngươi với việc ban xuống bổng lộc, nhưng các ngươi đã tự ý làm theo sở thích của các ngươi, tự ý cấm sử dụng một số và cho phép sử dụng một số khác. Ngươi hãy hỏi họ: "Phải chăng Allah cho phép các ngươi tự do cho phép và tự do cấm dùng các món vật mà Allah đã ban xuống cho các ngươi làm thực phẩm, hay là các ngươi bịa đặt rồi đỗ thừa cho Allah?!');
INSERT INTO "vn_mokhtasar" VALUES(10,60,'10:60','Và những kẻ bịa đặt điều gian dối rồi đổ thừa cho Allah, họ đã nghĩ gì về số phận của mình vào Ngày Phán Xét vậy? Họ có nghĩ rằng Ngài sẽ tha thứ cho họ?! Thật khốn khổ cho họ, quả thật Allah rất mực đối xử tốt với nhân loại khi ban cho họ đầy đủ ân huệ của Ngài và không vội trừng phạt họ mỗi khi họ phạm tội. Vậy mà đa số nhân loại vẫn phủ nhận ân huệ của Ngài và không biết ơn về điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(10,61,'10:61','Và không công việc nào mà Ngươi - hỡi Thiên Sứ - đã làm, không một đoạn Thiên Kinh Qur''an nào mà Ngươi xướng đọc nó cũng như không một hành động nào của các ngươi - hỡi những người có đức tin - lại không nằm dưới sự chứng kiến của TA và sự lắng nghe từ TA khi các ngươi miệt mài thực hiện nó. Và không có gì có thể nằm ngoài kiến thức của Thượng Đế Ngươi dù cho điều đó chỉ bằng sức nặng của một hạt nguyên tử trên bầu trời hoặc nằm dưới đất đi chăng nữa và không có cái gì nhỏ hoặc lớn hơn cái đó mà lại không được ghi chép trong quyển sổ Định Mệnh rõ ràng, không có gì mất đi dù nhỏ nhất hoặc lớn nhất ngoại trừ Ngài biết được điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(10,62,'10:62','Chắc chắn những người Wali của Allah sẽ không còn phải lo sợ về những gì họ đối mặt vào Ngày Phán Xét, và họ cũng không buồn phiền về những gì họ đã bỏ lỡ trên thế gian này.');
INSERT INTO "vn_mokhtasar" VALUES(10,63,'10:63','Những người Wali của Allah là những người được miêu tả là những người có đức tin vào Allah và Sứ Giả của Ngài. Và họ là những người luôn kính sợ Allah bằng việc tuân theo những mệnh lệnh của Ngài và tránh xa những điều cấm của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(10,64,'10:64','Họ nhận được tin mừng từ Thượng Đế của họ ở đời này với những hạnh phúc mà họ có thể nhìn thấy được hoặc họ sẽ được người đời khen ngợi, và họ sẽ nhận được tin mừng từ những vị Thiên Thần ngay lúc rút linh hồn của họ, ngay sau khi chết và sau khi được phục sinh trở lại. Chẳng có gì thay đổi trong các lời phán của Allah, đó là một chiến thắng vĩ đại khi họ đã hoàn thành việc được yêu cầu và họ sẽ được cứu rỗi khỏi điều sợ hãi.');
INSERT INTO "vn_mokhtasar" VALUES(10,65,'10:65','Và Ngươi chớ buồn - hỡi Sứ Giả - với những lời phỉ báng và vu khống của họ đến tôn giáo của Ngươi. Quả thật, tất cả việc giảm sát và quyền thống trị đều thuộc về Allah, bởi thế không gì có thể nằm ngoài quyền năng của Ngài. Ngài là Đấng Hằng Nghe những lời nói của họ và Hằng Biết những việc làm của họ, rồi Ngài sẽ xử phạt họ về điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(10,66,'10:66','Chẳng phải Allah là chủ nhân duy nhất của các tầng trời và trái đất hay sao, thế những kẻ đa thần đang theo đuổi điều gì khi họ lại dựng lên những đối tác cùng Ngài trong việc thờ phượng vậy?! Họ chỉ đi theo một cách mập mờ và họ bịa đặt dựng lên những đối tác cùng với Allah. Allah Tối Cao vượt hẵn tất cả mọi lời gán ghép của họ.');
INSERT INTO "vn_mokhtasar" VALUES(10,67,'10:67','Ngài là Đấng duy nhất đã tạo cho các ngươi - hỡi con người - màn đêm để các ngươi nghỉ ngơi khỏi cuộc sống lao động mệt nhọc, và Ngài tạo ra ban ngày tươi sáng để các ngươi hoạt động và tìm kiếm nguồn lợi cho cuộc sống. Quả thật, đó là những dấu hiệu rõ ràng cho đám người biết lắng nghe và chấp nhận.');
INSERT INTO "vn_mokhtasar" VALUES(10,68,'10:68','Một số trong nhóm người đa thần nói: "Allah đã chọn những Thiên Thần là những đứa con gái". Allah vô can với lời vu khống của họ, vì Ngài rất mực giàu có so với tất cả những tạo hóa của Ngài. Ngài là Đấng Chúa Tể của những tầng trời và trái đất. Các ngươi - hỡi những kẻ đa thần - không có bất cứ bằng chứng nào cho những lời nói bịa đặt của các ngươi. Phải chăng các ngươi dám nói cho Allah điều mà các ngươi không biết - khi cho rằng Ngài có con - trong khi các ngươi không có bằng chứng về điều này?!');
INSERT INTO "vn_mokhtasar" VALUES(10,69,'10:69','Ngươi - hỡi Thiên Sứ - hãy bảo họ: "Chắc chắn những kẻ bịa đặt điều nói dối rồi đổ thừa cho Allah rằng Allah có con, họ sẽ không được Ngài chấp nhận việc cầu xin của họ và họ sẽ không thoát khỏi nỗi sợ hãi từ Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(10,70,'10:70','Các ngươi chớ bị cám dỗ bởi vẻ hào nhoáng của thế gian và thú vui trong đó. Những hướng thụ và thú vui của thế gian chỉ là một sự hưởng thụ ngắn ngủi chóng tàn. Rồi đây họ sẽ được đưa trở về gặp lại TA vào Ngày Phán Xét và TA sẽ cho họ nếm hình phạt khủng khiếp về tội đã phủ nhận Allah của họ và từ chối vị Sứ Giả của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(10,71,'10:71','Và Ngươi - hỡi Thiên Sứ - hãy kể cho những người đa thần về câu chuyện của Nuh khi Y bảo dân chúng của mình: "Này hỡi dân ta! Nếu các ngươi cảm thấy khó chịu về sự tồn tại của Ta đây cũng như việc các ngươi bực tức khi Ta nhắc nhở các ngươi về những dấu hiệu của Allah và về lời rao giảng của Ta. Vả lại các ngươi cũng đã quyết tâm xác hại Ta thì Ta hoàn toàn phó thác cho Allah trước mọi âm mưu của các ngươi. Thế nên các ngươi hãy lên kế hoạch mà giết Ta đi, và hãy cầu cứu những thần linh của các ngươi giúp đỡ thêm, và các ngươi không cần phải giấu giếm kế hoạch của các ngươi làm gì, hãy công khai nó, rồi cứ dựa vào đó mà thực hiện và chớ để Ta yên thân bất cứ giờ phút nào.');
INSERT INTO "vn_mokhtasar" VALUES(10,72,'10:72','Nhưng nếu các ngươi quay bỏ đi không chấp nhận lời kêu gọi của Ta thì các ngươi nên biết rằng Ta không có đòi hỏi các ngươi phần thù lao về việc truyền bá thông điệp của Thượng Đế của Ta, bởi vì phần thưởng của Ta là do Allah ban cho, cho dù các ngươi có tin nơi Ta hoặc không tin đi chăng nữa. Và Ta được lệnh của Allah phải là một người thần phục Ngài bằng sự tuân lệnh và việc làm ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(10,73,'10:73','Người dân của Y cho rằng Y nói dối nên họ không tin nơi Y. Thế nên, Allah đã giải cứu Y và những tín đồ có đức tin theo Y trên một chiếc tàu, và bổ nhiệm họ thành những người nối nghiệp trên trái đất thay những người trước họ. Và TA đã nhấn chìm những kẻ phủ nhận những dấu hiệu và bằng chứng của TA bằng trận đại hồng thủy. Ngươi - hỡi Thiên Sứ - hãy nhìn xem hậu quả của những kẻ đã được Nuh cảnh báo nhưng không chịu tin tưởng đã có kết cuộc ra sao.');
INSERT INTO "vn_mokhtasar" VALUES(10,74,'10:74','Khoảng thời gian sau Nuh, TA đã cử phái nhiều Sứ Giả đến với người dân của họ, Những vị Sứ Giả đến với cộng đồng họ mang theo những dấu hiệu và bằng chứng rõ ràng. Nhưng họ không có ý định tin tưởng vì trước đây họ cũng từng khăng khăng phủ nhận những vị Sứ Giả. Vì vậy Allah đã niêm kín con tim của họ như Ngài đã từng niêm kín con tim của những người đi theo các Sứ Giả trong quá khứ. TA đã niêm kín tấm lòng của những kẻ phạm tội bởi việc đã vượt quá giới hạn của Allah bằng sự vô đức tin của chúng ở tất cả mọi thời đại và bất cứ nơi đâu.');
INSERT INTO "vn_mokhtasar" VALUES(10,75,'10:75','Rồi khoảng thời gian sau những Sứ Giả tiền nhiệm, TA đã sử phái Musa và người em của Y, Harun, đến gặp Fir''awn - một tên bạo chúa của Ai Cập và những người đứng đầu dân chúng của hắn. TA cử phái hai anh em họ cùng với những bằng chứng để chứng minh họ đến với sự thật. Tuy nhiên, chúng là đám người ngạo mạn cố tình phủ nhận những gì hai anh em Musa mang đến, và chúng là một đám người tội lỗi đã phủ nhận Allah cũng như những Sứ Giả của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(10,76,'10:76','Đến khi Musa và Harun (cầu xin bằng an cho hai Người) mang tôn giáo và những dấu hiệu chứng minh chân lý đến gặp Fir''awn và những tên đứng đầu dân chúng thì chúng lại nói: "Chắc chắn đây là một trò ma thuật chứ không phải là sự thật".');
INSERT INTO "vn_mokhtasar" VALUES(10,77,'10:77','Musa bác bỏ lời nói: "Sao, các ngươi cho rằng chân lý được mang đến là ma thuật ư"? Không, đó không phải là ma thuật, và Ta biết chắc chắn rằng những kẻ dùng bùa thuật sẽ không bao giờ được thành công, thế làm sao Ta có thể làm?!');
INSERT INTO "vn_mokhtasar" VALUES(10,78,'10:78','Phe của Fir''awn trả lời Musa (cầu xin Người được bằng an) nói rằng: "Phải chăng Ngươi đến gặp bọn ta với ma thuật của Ngươi để làm bọn ta từ bỏ những điều mà bọn ta thấy ông bà cha mẹ của bọn ta đang theo trong tôn giáo của bọn ta, để hai anh em nhà ngươi có thể trở thành vua hay sao? Và bọn ta sẽ không tin tưởng nơi hai ngươi đâu - hỡi Musa và Harun - nhất là khi các ngươi cho rằng hai ngươi là hai Sứ Giả được phái đến cho bọn ta.');
INSERT INTO "vn_mokhtasar" VALUES(10,79,'10:79','Và Fir''awn ra lệnh cho các chư thần của hắn: "Các ngươi hãy triệu tập cho ta tất cả các nhà phù thủy tài ba nhất".');
INSERT INTO "vn_mokhtasar" VALUES(10,80,'10:80','Khi các thầy phù thủy đến trình diện Fir''awn, Musa (cầu xin cho bằng an cho Người) bảo họ bằng niềm tin được Allah phù hộ cho chiến thắng: "Các ngươi hãy ném đi - hỡi những tên phù thủy - bất cứ bửu bối nào mà các ngươi muốn ném."');
INSERT INTO "vn_mokhtasar" VALUES(10,81,'10:81','Đến khi họ ném những bửu bối của họ từ trò ma thuật thì Musa (cầu xin cho bằng an cho Người) bảo: "Những gì mà các ngươi đang thể hiện chỉ là trò ma thuật, chắc chắn Allah sẽ làm cho những gì mà các ngươi thực hiện thành vô dụng, không có chút lợi ích nào. Quả thật trò ma thuật đã biến các ngươi trở thành những tên phá hoại trên trái đất và chắc chắn Allah sẽ không để cho việc làm của những kẻ thối nát được thành công."');
INSERT INTO "vn_mokhtasar" VALUES(10,82,'10:82','Và Allah sẽ thiết lập chân lý và khiến nó bền vững bằng lời phán quyền năng của Ngài, và bằng những lời phán thành luật định được dùng làm bằng chứng và lý luận, dù cho những kẻ vô đức tin tội lỗi từ đám dân của Fir''awn căm ghét điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(10,83,'10:83','Tất cả người chứng kiến đều im lặng, họ dám không tin vào Musa (cầu xin cho bằng an cho Người) với những dấu hiệu rõ ràng và những bằng chứng thuyết phục đã được trình bày, ngoại trừ một vài thanh niên thuộc dân Israel. Tuy nhiên, một số người này sợ Fir''awn và đám đầu sỏ của hắn sẽ hành hạ một khi sự thật về đức tin của họ bị phơi bày. Và quả thật, Fir''awn là tên bạo chúa tự cao tự đại cai trị lãnh thổ Ai Cập và hắn là kẻ đã vượt quá giới hạn về sự vô đức tin, sát hại và tra tấn con cháu Israel.');
INSERT INTO "vn_mokhtasar" VALUES(10,84,'10:84','Và Musa (cầu xin bằng an cho người) bảo: "Này hỡi dân ta! Nếu các ngươi có đức tin nơi Allah với một đức tin kiên định thì các ngươi hãy đặt hết niềm tin vào Ngài nếu các ngươi là những tín đồ Muslim thần phục Ngài, và các ngươi hãy phó thác cho Allah, rồi đây Ngài sẽ xóa bỏ mọi tội lỗi đã phạm cho các ngươi và ban thêm cho các ngươi những đều tốt khác.');
INSERT INTO "vn_mokhtasar" VALUES(10,85,'10:85','Bởi thế, họ thưa với Musa (cầu xin cho người bằng an): "Chúng tôi chỉ phó thác cho Allah duy nhất, lạy Thượng Đế của bầy tôi, xin Ngài đừng ban cho những kẻ sai quấy quyền cai trị bầy tôi, và xin Ngài đừng thử thách bầy tôi về tôn giáo bằng những hình thức tra tấn, giết chóc và sự cám dỗ."');
INSERT INTO "vn_mokhtasar" VALUES(10,86,'10:86','Và với sự khoan dung của Ngài - lạy Thượng Đế của bầy tôi -, xin Ngài hãy giải thoát bầy tôi khỏi bàn tay của đám người Fir''awn không có đức tin này, họ đã bắt bầy tôi làm nô lệ, rồi hành hạ, rồi tra tấn, rồi giết chóc.');
INSERT INTO "vn_mokhtasar" VALUES(10,87,'10:87','Và TA đã mặc khải cho Musa và người em của Y Harun (cầu xin cho hai người bằng an) rằng: "Hãy chọn xây cho người dân của hai người ngôi nhà để thờ phượng riêng Allah duy nhất, và hãy hướng ngôi nhà đến hướng Qiblah (Masjid Al-Aqsa''), và hãy chu đáo dâng lễ nguyện Salah. Và hãy báo tin mừng đi - hỡi Musa - cho những tín đồ tin tưởng rằng Allah sẽ làm cho họ chiến thắng, Ngài sẽ cứu giúp họ, và Ngài sẽ hủy diệt kẻ thù của họ và bổ nhiệm họ thành những người thừa kế trên trái đất.');
INSERT INTO "vn_mokhtasar" VALUES(10,88,'10:88','Và Musa (cầu xin bằng an cho Người) cầu nguyện: "Lạy Thượng Đế của bầy tôi! Ngài đã ban cho Fir''awn và các tên đầu sỏ của hắn sự huy hoàng và giàu sang trên đời này và Ngài đã ban cho họ vô số của cải trong đời sống trần tục này. Nhưng không vì thế mà họ biết tạ ơn Ngài bởi những thứ Ngài đã ban cho họ mà ngược lại họ sử dụng những tài sản đó để dẫn dắt thiên hạ đi lạc khỏi con đường của Ngài. Lạy Thượng Đế của bầy tôi! Xin Ngài hãy tàn phá và hủy hoại tài sản của họ và hãy làm cho tấm lòng của họ chai cứng lại. Bởi thế, họ sẽ tiếp tục không tin tưởng cho đến khi họ chứng kiến hình phạt đau đớn khi đó dù họ có tin tưởng cũng không mang lại lợi ích cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(10,89,'10:89','Allah phán: "Quả thật, TA đã chấp nhận lời cầu xin của hai Ngươi - hỡi Musa và Harun - về Fir''awn và những tên đầu sỏ của hắn. Do đó, hai Ngươi hãy vững chắc trên tôn giáo của hai Ngươi và chớ đi theo con đường của những kẻ ngu dốt, những kẻ không đi theo con đường chân lý."');
INSERT INTO "vn_mokhtasar" VALUES(10,90,'10:90','Và TA đã đưa con cháu của Israel an toàn vượt biển sau khi TA chẻ đôi biển hồng hải. Và đuổi theo họ là đội quân của Fir''awn đầy hận thù và hung hãn cho đến khi hắn (Fir''awn) bị nhấn chìm chết đuối dưới biển không còn khả năng sống sót thì hắn mới nói: "Tôi đã tin tưởng quả thật không có Thượng Đế đích thực đáng được thờ phượng nào ngoài Đấng mà con cháu Israel đã tin tưởng, và bề tôi là một trong những người vâng phục mệnh lệnh của Allah".');
INSERT INTO "vn_mokhtasar" VALUES(10,91,'10:91','Allah hỏi: Bây giờ ngươi mới chịu tin hay sao khi đã hết hy vọng được sống? Quả thật, ngươi đã chống đối Allah - hỡi Fir''awn - trước khi ngươi bị trừng phạt như thế này bằng cách phủ nhận Ngài, và cản trở thiên hạ đi theo con đường của Ngài, và ngươi là một trong đám người phá hoại chỉ vì sự lạc lối của bản thân ngươi và kéo mọi người lạc lối theo ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(10,92,'10:92','Ngày hôm nay TA sẽ đưa thân xác ngươi - hỡi Fir''awn - ra khỏi biển, và đặt ngươi tại một chỗ cao trên mặt đất để làm một bài học cảnh báo cho những người theo sau ngươi. Và quả thật đa số nhân loại vẫn còn thờ ơ trước những dấu hiệu và bằng chứng về sức mạnh tuyệt đối của TA, họ không suy ngẫm về những điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(10,93,'10:93','Và TA đã ban cho con cháu Israel một chỗ định cư tốt đẹp, một nơi ở hạnh phúc trên đất nước Sham đầy ân phúc, và cung cấp cho họ lương thực tốt sạch. Bởi thế họ không bất đồng ý kiến với nhau trong vấn đề tôn giáo của họ, cho đến khi Thiên Kinh Qur''an được gửi đến cho họ như một bằng chứng xác nhận lại điều họ đã đọc trong Tawrah mô tả về những đặt tính của vị Sứ Giả Muhammad (cầu xin bằng an cho Người). Ngay khi họ phủ nhận điều đó thì Ngài đã lấy lại chỗ cư ngụ của họ. Quả thật, Thượng Đế của Ngươi - hỡi Sứ Giả - sẽ phán quyết giữa họ vào Ngày Phục Sinh về những điều mà họ tranh chấp, và Ngài sẽ thưởng phạt công bằng giữa những người chân thật và những kẻ dối trá về những gì xứng đáng đối với họ.');
INSERT INTO "vn_mokhtasar" VALUES(10,94,'10:94','Nếu Ngươi - hỡi Sứ Giả - có nghi ngờ hay hoang mang trước sự thật về những điều mặc khải từ Thiên Kinh Qur''an mà TA đã ban xuống cho Ngươi thì Ngươi hãy hỏi điều này với những kẻ tin tưởng từ những tín đồ Do Thái đã đọc Kinh Tawrah, hay những tín đồ Thiên Chúa đã đọc Kinh Injil thì họ sẽ cho Ngươi biết chắc chắn những gì được ban xuống cho Ngươi đều là chân lý, khi họ thấy được những mô tả về Ngươi trong hai quyển Kinh đó. Quả thật, những gì được mặc khải cho Ngươi đều là chân lý, không có bất cứ một sự ngờ vực nào từ Thượng Đế của Ngươi. Vì vậy Ngươi chớ là một kẻ sinh lòng nghi ngờ.');
INSERT INTO "vn_mokhtasar" VALUES(10,95,'10:95','Và Ngươi cũng chớ là một trong những kẻ phủ nhận những dấu hiệu và bằng chứng của Allah vì như thế Ngươi sẽ nằm trong những kẻ thua thiệt mà họ chỉ tự làm thua thiệt chính bản thân của họ và nằm trong sự hủy diệt của Ngài vì nguyên nhân họ đã không tin tưởng. Và tất cả những cảnh báo này để chỉ ra sự nguy hại của lòng nghi ngờ và sự phủ nhận, và nếu không thì chắc chắn vị Sứ Giả sẽ vô can trước những đều này.');
INSERT INTO "vn_mokhtasar" VALUES(10,96,'10:96','Quả thật, những ai đã bị định đoạt bởi lời phán quyết của Allah rằng sẽ chết trong tình trạng vô đức tin thì dù có như thế nào y vẫn ngoan cố trong vô đức tin cho đến chết, y sẽ không bao giờ có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(10,97,'10:97','Cho dù có mang đến cho họ tất cả các bằng chứng xác thực thì họ vẫn không tin tưởng cho đến khi họ chứng kiến một hình phạt đau đớn. Khi đó cho dù họ muốn tin cũng không có ích gì cho đức tin của họ.');
INSERT INTO "vn_mokhtasar" VALUES(10,98,'10:98','Chưa từng xảy ra với bất cứ thị trấn nào trong số những thị trấn mà TA đã gửi Sứ Giả đến với họ trước đây về việc dân làng phải đối diện với hình phạt thì họ mới sợ và có đức tin và đức tin đó giúp ích cho họ, ngoại trừ người dân của Yunus. Đám dân của Yunus là đám dân duy nhất đã tin tưởng trước hình phạt đang tiến đến gần họ và được chấp nhận. TA đã xóa hình phạt nhục nhã khỏi họ ở đời sống trần tục này và cho họ tiếp tục hưởng thụ cuộc sống thế gian đến một thời hạn ấn định.');
INSERT INTO "vn_mokhtasar" VALUES(10,99,'10:99','Và nếu như Thượng Đế của Ngươi muốn - hỡi Sứ Giả - cho tất cả nhân loại trên trái đất trở thành người tin tưởng thì chắc chắn tất cả họ đã là những người tin tưởng rồi, nhưng Ngài đã không làm vậy vì sự khôn ngoan của Ngài. Ngài làm lầm lạc ai Ngài muốn với công lý của Ngài và hướng dẫn ai Ngài muốn với hồng ân của Ngài. Và Ngươi không thể ép buộc nhân loại trở thành người tin tưởng, bởi việc họ có tin hay không là do sự định đoạt của một mình Allah.');
INSERT INTO "vn_mokhtasar" VALUES(10,100,'10:100','Và không có một người nào có đức tin ngoại trừ việc Allah cho phép đều đó, và cũng sẽ không có đức tin ngoại trừ với ý muốn của Ngài. Và Ngươi chớ có để bản thân buồn rầu vì họ, và Allah sẽ giáng sự đau khổ và ô nhục lên những kẻ không nhận thức về Ngài và chống đối lại những mệnh lệnh và những điều cấm của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(10,101,'10:101','Ngươi - hỡi Sứ Giả - hãy bảo những kẻ đa thần đã yêu cầu Ngươi về những dấu hiệu: "Các ngươi hãy suy ngẫm về mọi vật trong các tầng trời và trái đất, tất cả đều là những dấu hiệu chứng minh sự độc tôn của Allah và sự toàn năng của Ngài. Sẽ không có lợi ích gì cho việc ban xuống những dấu hiệu, những bằng chứng cũng như các vị Thiên Sứ khi đám người đó cố tình không muốn tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(10,102,'10:102','Thế có phải những kẻ phủ nhận, chúng đang chờ đợi sự trừng phạt của Allah như việc ngài đã trừng phạt những cộng đồng trước chúng hay sao?. Ngươi hãy bảo chúng - hỡi Sứ Giả -: Các ngươi cứ chờ đợi sự trừng phạt từ Allah, Ta sẽ cùng chờ đợi cùng với các ngươi về lời hứa của Thượng Đế Ta.');
INSERT INTO "vn_mokhtasar" VALUES(10,103,'10:103','Rồi TA đã giáng xuống họ sự trừng phạt, nhưng TA đã giải cứu các Sứ Giả và những ai có đức tin cùng với Họ, cho nên Họ đã không bị ảnh hưởng bởi sự trừng phạt đang xảy ra với người dân của mình. Cũng giống như TA giải cứu những Sứ Giả và những tín đồ có đức tin cùng với Họ, bổn phận của TA là giải cứu Sứ Giả và những người tin tưởng một sự giải thoát thực sự vững chắc.');
INSERT INTO "vn_mokhtasar" VALUES(10,104,'10:104','Ngươi - hỡi Sứ Giả - hãy bảo: "Hỡi nhân loại! nếu các ngươi nghi ngờ về tôn giáo của Ta, điều Ta đang mời gọi các ngươi. Đó là tôn giáo của Tawhid chỉ tôn thờ một Thượng Đế duy nhất, và Ta chắc chắn rằng tôn giáo của các ngươi là vô giá trị, Ta không bao giờ đi theo nó, Ta không thờ phượng những kẻ mà các ngươi thờ phượng ngoài Allah. Ngược lại, Ta thờ phượng Allah Đấng sẽ làm cho các ngươi chết và Ta nhận lệnh phải trở thành một người tin tưởng Allah hoàn toàn thần phục Ngài một cách chính trực.');
INSERT INTO "vn_mokhtasar" VALUES(10,105,'10:105','Và Ta được lệnh bám trên tôn giáo chân lý đích thực, và đứng vững trên đó, tránh xa mọi tôn giáo khác và Ta bị cấm trở thành một người thờ đa thần.');
INSERT INTO "vn_mokhtasar" VALUES(10,106,'10:106','Và Ngươi - hỡi Sứ Giả - chớ cầu nguyện ngoài Allah những thần linh, những bục tượng và những thần linh nào khác, những kẻ chẳng mang lợi ích cũng gây hại được Ngươi. Nếu làm thế thì Ngươi chắc chắc sẽ trở thành một kẻ làm điều sai quấy, không thực hiện đúng nghĩa vụ đối với Allah và không hưởng được quyền lợi của chính mình.');
INSERT INTO "vn_mokhtasar" VALUES(10,107,'10:107','Và nếu Allah cho Ngươi - hỡi Sứ Giả - gặp tai họa, và Ngươi cầu xin sự giải cứu thì không một ai có thể giải cứu Ngươi ngoài Ngài duy nhất. Và nếu Ngài ban điều tốt nào cho Ngươi thì không ai có thể ngăn cản điều tốt của Ngài. Ngài ban nó cho người nào Ngài muốn trong số bầy tôi của Ngài, không có bất kỳ sự ép buộc nào có thể cưỡng chế lên Ngài. Và Ngài là Đấng Hằng Tha Thứ cho những bầy tôi biết quay lại sám hối của Ngài, Ngài Rất Mực Khoan Dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(10,108,'10:108','Ngươi - hỡi Sứ Giả - hãy bảo: "Hỡi nhân loại! Quả thật, Thiên Kinh Qur''an đã được ban xuống từ Thượng Đế của các Ngươi. Do đó, ai đi theo chính đạo và tin tưởng Nó thì chỉ có lợi cho bản thân mình, bởi vì chắc chắn Allah rất mực giàu có không cần đến những bầy tôi của Ngài; và ai đi lạc thì việc lạc đó chỉ bất lợi cho bản thân y và Allah không bị ảnh hưởng bởi việc làm của những bầy tôi của Ngài. Và Ta không phải người chịu trách nhiệm cho việc làm của các ngươi cũng như Ta không có quyền thanh toán các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(10,109,'10:109','Ngươi - hỡi Sứ Giả - hãy tuân theo những điều đã được mặc khải cho Ngươi từ Thượng Đế của Ngươi và hãy kiên nhẫn chịu đựng trước mọi sự hãm hại của những kẻ chống đối trong đám người dân của Ngươi, và Ngươi hãy truyền bá những gì Ngươi được lệnh truyền bá từ Ngài. Và Ngươi hãy tiếp tục truyền bá cho đến khi Allah phán xử giữa họ với phán quyết của Ngài và Ngài sẽ giúp Ngươi chiến thắng ở thế gian này, và Ngài sẽ trừng phạt họ vào Ngày Sau nếu họ chết trong tình trạng phủ nhận.');
INSERT INTO "vn_mokhtasar" VALUES(11,1,'11:1','Alif. Lam. Ra. Các chữ cái được mở đầu cho những chương Kinh giống như trong Chương Al-Baqarah. Qur''an là quyển Kinh Sách gồm những câu cơ bản có ý nghĩa rõ ràng. Không có bất kỳ những khiếm khuyết hay thiếu sót nào. Nó đề cập đến vấn đề Halal (được phép), Haram (không được phép), mệnh lệnh, những điều cấm, lời hứa, lời cảnh báo, những câu chuyện và những thông điệp khác từ Đấng Sáng Suốt trong việc quản lý và quy định của Ngài, Đấng Am Tường hoàn cảnh của những bầy tôi của Ngài, và những gì có thể mang lại sự tốt đẹp cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(11,2,'11:2','Nội dung của những câu Kinh được mặc khải cho vị Sứ Giả Muhammad (cầu xin cho Người bằng an) là cấm những bầy tôi tôn thờ cùng với Allah những thần linh khác. Hỡi nhân loại! Quả thật, Ta (Muhammad) là Người được cử phái đến để thông báo về sự khủng khiếp của sự trừng phạt nơi Allah nếu các ngươi phủ nhận và chống đối Ngài. Ta cũng là Người mang tin mừng về những phần thưởng dành cho các ngươi nếu các ngươi tin tưởng và tuân thủ theo giáo luật của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(11,3,'11:3','Hỡi nhân loại! các ngươi hãy cầu xin sự tha thứ từ Thương Đế (Allah) của các ngươi, và hãy quay về sám hối với Ngài về những gì mà các ngươi đã không làm tròn nhiệm vụ mà Ngài đã giao. Khi các ngươi hoàn thành thì Ngài cho các ngươi hưởng thụ một đời sống vui tươi cho đến hết tuổi thọ đã được qui định cho các ngươi, và Ngài sẽ ban cho tất cả những người đức hạnh trong việc vâng lời Ngài và việc làm ngoan đạo một phần thưởng trọn vẹn. Và nếu các ngươi quay lưng với đức tin về những gì mà Ta đã mang đến từ Thượng Đế của Ta thì chắc chắn Ta lo sợ cho các ngươi về sự trừng phạt đau đớn, đó là vào Ngày Phán Xét Vĩ Đại.');
INSERT INTO "vn_mokhtasar" VALUES(11,4,'11:4','Hỡi nhân loại! Các ngươi sẽ trở về gặp Allah, Đấng Duy Nhất vào Ngày Phán Xét Cuối Cùng, và Ngài là Đấng đáng được Tôn Vinh, Ngài toàn quyền định đoạt về tất cả mọi việc, không gì có thể làm Ngài bất lực. Chẳng có gì là khó đối với Ngài trong việc phục sinh các ngươi sống lại để thanh toán sau khi các ngươi đã chết.');
INSERT INTO "vn_mokhtasar" VALUES(11,5,'11:5','Chẳng phải những kẻ thờ đa thần đã khép kín cõi lòng của họ để che giấu khỏi Allah về những nghi ngờ và sự dốt nát của họ với Ngài. Chẳng phải dù cho họ lấy áo quần che đậy thân mình của họ thì Allah vẫn thừa biết những điều họ che giấu và những điều họ phơi bày. Chắc chắn Ngài biết hết những điều thầm kín trong lòng.');
INSERT INTO "vn_mokhtasar" VALUES(11,6,'11:6','Và không một sinh vật nào sinh sống trên trái đất này mà không nhận được nguồn cung cấp lương thực tốt đẹp từ nơi Allah phân phát. Đấng Toàn Năng luôn nắm rõ tường tận nơi cư ngụ của chúng trên trái đất và biết rõ khi nào chúng chết mà cái chết đó nằm nơi đâu. Tất cả mọi thứ liên quan đến từng sinh vật một như bổng lộc, nơi cư ngụ ổn định và nơi mà chúng phải chết ở đâu đều đã được ghi chép sẵn trong Quyển Sổ Mẹ Lawhul Mahfudh.');
INSERT INTO "vn_mokhtasar" VALUES(11,7,'11:7','Và Ngài (Allah), Đấng Toàn Năng đã tạo hóa các tầng trời và trái đất với sự hùng vĩ của chúng cùng với tất cả mọi thứ trong chúng trong vòng sáu ngày, trước khi tạo hóa ra hai chúng thì chiếc Ngai Vương của Ngài ở trên mặt nước; để Ngài thử thách các ngươi - hỡi con người - rằng ai sẽ là người làm những việc thiện phước để may ra nhận được sự hài lòng của Allah và ai trong các ngươi ai sẽ làm những việc tồi tệ nhất để rồi chuốc lấy sự bất mãn của Ngài, rồi Ngài sẽ ban thưởng cho kết quả tương xứng cho y. Và nếu như Ngươi - hỡi Thiên Sứ - có bảo chúng rằng: Chắc chắn các ngươi - hỡi con người - sẽ được phục sinh trở lại sau khi các ngươi chết để rồi gánh lấy trách nhiệm thì chắc hẳn những kẻ bất tuân Allah và những kẻ phủ nhận việc phục sinh sẽ đáp: "Với Thiên Kinh Qur''an này, thứ mà các ngươi đang xướng đọc Nó chỉ toàn là những trò ma thuật quá lộ liễu, là một sự giả tạo quá rõ ràng."');
INSERT INTO "vn_mokhtasar" VALUES(11,8,'11:8','Và nếu TA trì hoãn sự trừng phạt đối với bọn đa thần ở cuộc sống trần tục này đến một ngày được chọn sẵn phía trước, chắc hẳn bọn chúng sẽ khua môi múa mép mà bảo rằng: "Vì cớ sao mà hoãn lại trừng phạt đối với chúng tôi vậy?" Tuy nhiên, sự trừng phạt nó sẽ diễn ra mà chỉ có Allah mới nắm rõ được thời gian. Ôi! Vào một ngày mà bắt chợt ụp đến thì thật sự không có thể thay đổi được mà chỉ có riêng Ngài mới thay đổi được nó với họ. Không, điều đó chắc chắn sẽ ụp lên họ và rồi sự trừng phạt sẽ vồ lấy chúng bởi những lời khua môi múa mép của chúng đã từng thách thức, chế giễu.');
INSERT INTO "vn_mokhtasar" VALUES(11,9,'11:9','Và nếu như TA ban cho loài người một ân huệ như là sức khỏe hay sự giàu có, rồi sau đó TA tước đoạt lại cái ân huệ đó thì chắc chắn y sẽ tuyệt vọng rất nhiều về sự Nhân Từ của TA rồi làm chúng mất đi niềm tin vào ân huệ của Allah. Vĩ đại cho việc bạc ơn về ân huệ của Ngài và mau chóng quên lãng tất cả khi Allah tước đoạt nó đi.');
INSERT INTO "vn_mokhtasar" VALUES(11,10,'11:10','Và nếu như TA cho y nếm thử chút bổng lộc và sức khỏe sau khi y đã từng trãi qua cơn nghèo khổ và bệnh tật thì y sẽ nói :" Mọi điều tai ương đã đi qua lẫn với sự khó khăn." Thế mà y vẫn không hề biết tạ ơn Allah về điều đó, ngược lại y còn tỏ vẻ vui mừng tột cùng cho sự vô ơn đó và rất nhiều sự khoác lác kiêu căng đối với mọi người và đắc ý về những hồng ân của Allah ban cho y.');
INSERT INTO "vn_mokhtasar" VALUES(11,11,'11:11','Ngoại trừ những ai có lòng kiên nhẫn trước những điều bất hạnh, luôn biết phục tùng, biết tránh xa tội lỗi và hành đạo tốt, chắc hẳn họ sẽ được rơi vào tình huống khác hoàn toàn như không tuyệt vọng khi gặp khó khăn, không vong ơn với mọi ân huệ được Allah ban cho và càng không kiêu căng đắc ý trước mọi người. Với đám người vừa được miêu tả sẽ được Thượng Đế của họ tha thứ đi tội lỗi mà họ từng gây ra và rồi họ sẽ có được một phần thưởng rất to lớn ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(11,12,'11:12','Có lẽ Ngươi - hỡi Thiên Sứ - sẽ phải đối diện trước những thái độ bất tin và ương ngạnh với lời đề nghị của chúng về các Dấu hiệu khiến Ngươi trở nên yếu lòng bỏ qua một số Thông Điệp của Ngài đã ra lệnh là phải truyền đạt đến chúng để thực thi và rồi điều đó cũng làm cho tấm lòng của Ngươi thấy khó chịu về lời nói của chúng: "Phải chăng, Y được ban xuống cho một kho báu để cho Y giàu có hoặc cử đến cho Y một vị Thiên Thần để xác định sự thật về Y." Dù có thế nào thì Ngươi cũng chớ có bỏ qua bất cứ Thông Điệp nào mà TA đã truyền đạt cho Ngươi để áp dụng." Chẳng qua, Ngươi chỉ là một người cảnh báo Thông Điệp mà Allah ra lệnh để truyền đạt lại cho Ngươi và Ngươi không cần phải đáp ứng mọi yêu sách mà họ đã đề nghị về những dấu hiệu, bởi chỉ có Allah mới thật là Đấng đã nắm rõ hết mọi sự việc.');
INSERT INTO "vn_mokhtasar" VALUES(11,13,'11:13','Hay là bọn đa thần nói rằng: "Muhammad là Người đã bịa đặt ra Thiên Kinh Qur''an chứ nó không được mặc khải từ Allah", Ngươi - hỡi Thiên Sứ - hãy thách thức bọn họ rằng: "Có giỏi các ngươi hãy đem ra mười Chương kinh giống như Thiên Kinh Qur''an mà các ngươi cho là địa đặt và các ngươi cứ kêu gọi sự hổ trợ của bất cứ ai khác nếu các ngươi vẫn khăng khăng cho rằng Thiên Kinh Qur''an được ai đó sáng tạo.');
INSERT INTO "vn_mokhtasar" VALUES(11,14,'11:14','Nếu bọn họ không đáp ứng lại được lời thử thách của các ngươi, vì sự bất lực của bọn họ thì các ngươi - hỡi những người có tin tưởng - hãy biết kiên định rằng quả thật Thiên Kinh Qur''an được Allah mặc khải cho Thiên Sứ của Ngài bằng chính kiến thức của Ngài, chứ không phải một kiện tác của ai đó'''' và hãy biết rằng, không có Thượng Đế đích thực nào đáng để thờ phượng mà chỉ duy nhất Allah. Vậy đó các ngươi có chịu phục tùng Ngài sau khi có những bằng chứng xác thực đó không?');
INSERT INTO "vn_mokhtasar" VALUES(11,15,'11:15','Ai muốn việc hành đạo của mình để được cuộc sống trần tục này cũng như muốn tận hưởng thứ tạm bợ đó chứ không hề muốn hành đạo vì cuộc sống Đời Sau, chắc hẳn sẽ được TA ban đầy đủ mọi thứ cho y ở cỏi trần tục này như là sức khỏe, bình an và giàu có và không một phần thưởng nào của y bị cắt giảm cả.');
INSERT INTO "vn_mokhtasar" VALUES(11,16,'11:16','Đám người có định tâm tồi tệ như thế thì ở Đời Sau họ chẳng có bất cứ phần thưởng nào ngoại trừ đó là Hỏa Ngục mà họ phải đi vào và phần thưởng của những việc làm của họ đã biến mất khỏi họ cũng như việc hành đạo cũng bị hủy bỏ. Bởi vì những việc làm đó không được thực hiện bằng đức tin cũng như không muốn thực hiện vì Allah và vì cuộc sống ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(11,17,'11:17','Không có một địa vị nào có thể ngang hàng với Thiên Sứ Muhammad (cầu xin Allah ban bình an cho Người) vì luôn bên Người một minh chứng Qur''an đến từ Thượng Đế của Người Đấng Tối Cao, và luôn có một vị theo sát chứng kiến bằng chứng đó đến từ Thượng Đế của Người, vị đó là Đại Thiên Thần Jibril. Cũng như trước thời đại của Người có một bằng chứng đó là Kinh Sách Tawrah (Cựu Ước) được ban xuống cho Musa (cầu xin sự bình an cho Người) dùng làm gương mẫu cho nhân loại và là Hồng Ân cho họ, thế nên không bao giờ có chuyện Người và những ai theo Y mà lại ngang hàng với những kẻ bất tuân đang chìm đắm trong mù quáng, họ là những người tin tưởng vào Thiên Kinh Qur''an, tin tưởng vào Thiên Sứ Muhammad (cầu xin Allah ban bình an cho Người) rồi còn những kể bất tuân Người thuộc những tôn giáo tà thần thì điểm hẹn của bọn chúng đó là Hỏa Ngục của Đời Sau. Bởi thế, Ngươi - hỡi Thiên Sứ - chớ nên có sự nghi ngờ về Qur''an và điểm hẹn giành cho chúng, vì đó là một sự thật không có gì phải nghi ngờ. Thế nhưng, đa số người trong thiên hạ không chịu tin đó là một chứng cứ rõ ràng và một minh chứng vô cùng xác thực.');
INSERT INTO "vn_mokhtasar" VALUES(11,18,'11:18','Và không có một kẻ nào sai quấy hơn kẻ bịa ra chuyện dối trá rồi gán ghép cho Allah có cùng đối tác hoặc có con trai. Rồi đây những kẻ chuyên đặt điều dối trá rồi vu khống cho Allah thì chắc chắn chúng sẽ bị Thượng Đế của chúng triệu tập vào ngày sau để thẩm vấn về những gì mà chúng từng làm và rồi các nhân chứng của chúng như là các vị Thiên Thần và những vị Thiên Sứ sẽ nói :"Đây là những kẻ chuyên đặt điều dối trá rồi vu khống cho Allah bằng những gì mà bọn chúng gán ghép cho Ngài từ việc tổ hợp hoặc có đứa con trai". Quả thật, Allah sẽ xua đuổi những kẻ bất công ra khỏi lòng thương xót của Ngài bởi vì chính bản thân bọn chúng đặt điều dối trá rồi đổ thừa cho Allah.');
INSERT INTO "vn_mokhtasar" VALUES(11,19,'11:19','Những ai đã ngăn cản mọi người đến với con đường của Allah một con đường ngay thẳng, vậy mà họ còn tìm cách uốn cong con đường ngay thẳng đó cho đến khi nào không còn ai có thể tìm ra chân lý. Họ là những kẻ phủ nhận viêc phục sinh sau khi chết và họ còn luôn tỏ ra chống đối Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(11,20,'11:20','Đám người như được miêu tả sẽ không tài nào thoát khỏi ở bất cứ nơi nào trên trái đất này mà không bị Allah trừng phạt nếu như muốn gián lên họ và rồi họ cũng sẽ không có một đồng minh hay một ai đó sẽ phù hộ cho họ hầu thoát khỏi sự trừng phạt của Allah và rồi nó có thể còn tăng lên gấp bội vào Đời Sau, bởi lý do họ đã kéo bản thân mình rồi khỏi con đường của Allah, lúc ở trần giàn họ đã không có khả năng nghe được Chân Lý hay nghe được sự chỉ dẫn để đón nhận nó và cũng sẽ không thấy được các dấu hiệu của Allah trong vũ trụ này để giúp ích cho họ vì thế mà họ đã nghoãnh mặt với Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(11,21,'11:21','Đám người được miêu tả đó đã rơi vào thất bại khi họ chọn lấy điều tổ hợp với Allah. Thế rồi, những điều mà chúng bịa ra và kể cả những đồng minh của chúng lúc đó sẽ bỏ chúng chạy đi mất dạng.');
INSERT INTO "vn_mokhtasar" VALUES(11,22,'11:22','Chắc chắn, bọn họ vào Đời sau là những kẻ thảm bại nhất khi họ đã đánh đổi sự phủ nhận với đức tin, đổi cuộc sống trần gian với Đời Sau và đổi sự hình phạt với lòng thương xót.');
INSERT INTO "vn_mokhtasar" VALUES(11,23,'11:23','Thật ra đối với những ai tin tưởng vào Allah và các Thiên Sứ của Ngài, cũng như năng làm những việc ngoan đạo và hạ mình kính sợ Allah thì đó là những người bạn của Thiên Đàng, họ sẽ ở trong đó nghìn thu vĩnh cửu.');
INSERT INTO "vn_mokhtasar" VALUES(11,24,'11:24','Thí dụ về hai nhóm người, bên vô đức tin và bên kia là người có đức tin giống như người mù không nhìn thấy gì và người điếc không nghe được chi. Điều này cũng giống như nhóm người phủ nhận đức tin, họ chẳng hề nghe được Chân Lý để chấp nhận và cũng không nhìn thấu được điều ích lợi giành cho mình. Và nhóm kia là người nghe thấy điều đó, nó giống như nhóm người có đức tin họ hội tụ được sự nghe và nhìn thấu ra được. Há phải chăng, hai nhóm người đó có cùng một trạng thái và một hình ảnh giống nhau chăng ? Không bao giờ có sự tương đồng như thế, lẽ nào các ngươi không xem xét rằng hai điều đó nó không có sự tương đồng ư ?');
INSERT INTO "vn_mokhtasar" VALUES(11,25,'11:25','Và trước đây TA đã phái Nuh (kinh thánh gọi là Noah - cầu xin bình an cho Người) là một vị Thiên Sứ đến với dân chúng của Y. Bởi vậy, Y bảo chúng: "Này hỡi dân Ta, quả thật Ta chỉ là một Người cảnh báo các ngươi về sự trừng phạt của Allah và đó là một điều quá rõ ràng với các ngươi về Bức Thông Điệp mà Ta đem đến cho các ngươi."');
INSERT INTO "vn_mokhtasar" VALUES(11,26,'11:26','Ta mời gọi các ngươi đến với việc thờ phượng Allah, chỉ riêng Ngài và chớ tôn thờ ai khác ngoài Ngài. Bởi vì Ta khiếp sợ cho các ngươi về sự trừng phạt sẽ diễn ra vào một ngày thật là kinh hoàng.');
INSERT INTO "vn_mokhtasar" VALUES(11,27,'11:27','Những tên bô lão cùng với những kẽ đầu sỏ trong đám dân chúng của Y đáp: "Bọn ta sẽ không đáp ứng lại lời kêu gọi của Ngươi đâu, bởi điều đó nó không có lợi ích gì cho bọn ta, vả lại Ngươi cũng là người phàm giống như bọn ta, hơn nữa bọn ta không nhìn thấy bất cứ ai đi theo Ngươi ngoại trừ những kẻ bần hàn nhất trong bọn ta, toàn là đám người đầu óc thô thiển, và bởi các ngươi không có gì nỗi bật hơn bọn ta từ danh dự, tiền bạc, sự giàu có kể cả địa vị cũng cao quí hơn các ngươi. Đúng hơn bọn ta thầm nghĩ các người là những kẻ nói dối rồi để kêu gọi bọn ta."');
INSERT INTO "vn_mokhtasar" VALUES(11,28,'11:28','Nuh bảo với dân chúng của Y: "Này hỡi dân Ta, các ngươi hãy cho Ta biết phải làm sao khi Ta có bằng chứng từ Thượng Đế của Ta để chứng minh sự thật về Ta và rồi bắt buộc các ngươi phải tin đó là sự thật và Ta còn được Ngài ban cho Đặc Ân là một vị Thiên Sứ truyền đạt sứ mệnh và điều đó đã che giấu khỏi các ngươ, lẻ nào Ta ép buộc phải tin tưởng, phải cố nhét nó vào con tim các ngươi trong khi các ngươi rất ghét về điều đó ư ? Quả thật, Ta không có khả năng làm điều đó, bởi vì sự đức tin là do Allah an bài.');
INSERT INTO "vn_mokhtasar" VALUES(11,29,'11:29','"Và hỡi dân Ta! Ta không cần khối tài sản của các ngươi về việc Ta đã truyền bá Bức Thông Điệp này cho các ngươi. Ta chỉ cần phần thưởng của Allah là đủ và rồi Ta không thể xa lánh những tầng lớp nghèo khổ trong khi họ là những người có đức tin mà các ngươi yêu cầu ta xua đuổi họ. Bởi vì, chắc chắn họ sẽ trở về trình diện Thượng Đế của họ vào Ngày Phục Sinh và Ngài sẽ ban thưởng cho họ. Tuy nhiên Ta thấy các ngươi không hiểu biết gì về sự thật cho lời kêu gọi này khi các ngươi muốn Ta xua đuổi những tầng lớp yếu thế, trong khi đó họ lại là những người có đức tin."');
INSERT INTO "vn_mokhtasar" VALUES(11,30,'11:30','Và này hỡi dân Ta! Ai có thể che chở cho Ta tránh được hình phạt của Allah nếu như Ta xua đuổi họ, trong khi đó họ là những người có đức tin bị đối sử bất công cũng không phải là người phạm tội ? Thế các ngươi sao không suy ngẫm và liên tưởng đến điều đó nó không tốt đẹp và chẳng mang lợi ích gì cho các ngươi ư ?');
INSERT INTO "vn_mokhtasar" VALUES(11,31,'11:31','"Và Ta đã không nói với các ngươi rằng - hỡi dân của Ta -: Ta đang sở hữu được những kho tàn của Allah mà trong đó chứa đựng những Thiên Lộc của Ngài, để Ta ban cho ai trong các ngươi có đức tin, và Ta đã không nói với các ngươi rằng: Chính Ta biết rất rõ điều vô hình và rồi Ta cũng không nói với các ngươi rằng: Ta chính là Thiên Thần. Không! Ta chỉ là một Người phàm tục giống các ngươi, Ta đã không nói với các ngươi về những người có tầng lớp nghèo khổ mà cặp mắt các ngươi luôn xem thường và khinh khi họ rằng : Allah sẽ không cho họ có được sự may mắn và cũng không có sự chỉ dẫn. Allah am hiểu được tâm can và tình trạng của họ ra sao. Nếu như Ta gọi họ như thế thì Ta đã làm điều bất công mà chắc chắn sẽ bị Allah trừng phạt."');
INSERT INTO "vn_mokhtasar" VALUES(11,32,'11:32','Họ đáp bằng giọng điệu khinh khi và cao ngạo: "Hỡi Nuh! Bọn ta và Ngươi đã tranh luận khá nhiều rồi. Thôi thì Ngươi hãy mang đến đây một cái hình phạt nào đó từ Thượng Đế của Ngươi mà bấy lâu nay Ngươi luôn đem ra để hù dọa bọn ta, nếu như Ngươi là một Người nói thật cho những gì mà Ngươi hay hù dọa."');
INSERT INTO "vn_mokhtasar" VALUES(11,33,'11:33','Nuh bảo chúng: "Ta không đem bất cứ hình phạt nào đến cho các ngươi cả, duy nhất Allah mới đem nó đến với các ngươi nếu như Ngài muốn thế và tới lúc đó các ngươi sẽ không thể nào thoát được sự trừng của Allah một khi Ngài muốn trừng phạt các ngươi."');
INSERT INTO "vn_mokhtasar" VALUES(11,34,'11:34','"Và rồi Ta có khuyên bảo hay cảnh cáo các ngươi thế nào đi chăng nữa thì nó cũng sẽ không giúp ích gì cho các ngươi một khi Allah muốn cho các ngươi lầm lạc trên con đường chính đạo, thì không có một ai có thể chỉ dẫn các ngươi bời vì các ngươi là những tên cứng đầu láo xược. Ngài là Thượng Đế của các ngươi, Ngài là Đức Vua ra lệnh các ngươi nếu Ngài muốn các ngươi lạc lối và Ngài cũng là Đấng duy nhất mà các ngươi sẽ trở về trình diện với Ngài vào Ngày Phục Sinh để nhận lấy hậu quả mà các ngươi đã từng làm"');
INSERT INTO "vn_mokhtasar" VALUES(11,35,'11:35','Và nguyên nhân khiến cho dân chúng của Nuh không tin tưởng vào Người là họ khẳng định Nuh đã tự bịa đặt sứ mạng của mình là do Allah thiên khải. Hãy bảo họ - hỡi Thiên Sứ -: "Nếu như Ta có bịa đặt điều dốc láo thì một mình Ta sẽ đứng ra gánh tội và Ta sẽ không gánh vác sự dối trá mang đầy tội lỗi của các ngươi. Bởi vì, Ta vô can về những điều đó."');
INSERT INTO "vn_mokhtasar" VALUES(11,36,'11:36','Và rồi Allah cũng măc khải xuống cho Nuh: Chắc rằng sẽ không một ai trong dân chúng của Ngươi sẽ tin tưởng nữa đâu - hỡi Nuh - ngoại trừ những ai đã có đức tin vững chắc trước đó. Bởi vậy, Ngươi chớ nên buồn bã - hỡi Nuh - vì lý do chúng từng bịa đặt điều dối trá mà còn chế giễu trong suốt thời gian dài.');
INSERT INTO "vn_mokhtasar" VALUES(11,37,'11:37','"Và Ngươi hãy đóng một chiếc thuyền dưới sự chỉ đạo của TA, và tuân thủ theo lời Mặc Khải của TA bằng cách TA sẽ dạy Ngươi làm thế nào để có thể đóng được chiếc thuyền đó và rồi Ngươi chớ nài nỉ van xin TA sẽ tha thứ cho những kẻ mà chính bản thân họ khiến họ làm những điều sai quấy bằng cách phủ nhận TA. Chắc chắn, chúng sẽ bị nhấn chìm - không một kẻ nào thoát khỏi - bởi một trận đại hồng thủy; chúng sẽ bị trừng phạt thích đáng cho sự ngoan cố phủ nhận của họ."');
INSERT INTO "vn_mokhtasar" VALUES(11,38,'11:38','Và Nuh tuân theo mệnh lệnh của Thượng Đế của Y đóng chiếc thuyền. Và rồi mỗi khi những tên đầu sỏ của đám dân chúng Y đi ngang qua chỗ của Y thì chúng luôn tỏ ra vẻ chế nhạo Y, bởi vì Y đóng chiếc thuyền ở một nơi không có nước và cũng không có sông ngòi, mãi khi họ lặp lại việc chế nhạo thì Y bảo: "Nếu ngày nay các ngươi cứ cười chế nhạo bọn Ta - hỡi cộng đồng của Ta - thì sẽ có một ngày Ta hoàn thành xong chiếc thuyền thì bọn Ta sẽ cười nhạo các ngươi là những kẻ ngu si sắp trở thành những tên chết đuối mà Ngài sẽ hạ lệnh nhấn chìm các ngươi."');
INSERT INTO "vn_mokhtasar" VALUES(11,39,'11:39','"Rồi đây các ngươi sẽ sớm biết ai là kẻ sẽ phải nhận một hình phạt ở trần gian này đầy xấu hổ và nhục nhã. Rồi sự nhục nhã đó nó sẽ bám chặt theo hắn đến Ngày Phục Sinh để gánh chịu một hình phạt truyền kiếp mà không có hồi kết."');
INSERT INTO "vn_mokhtasar" VALUES(11,40,'11:40','Và rồi Nuh (cầu xin sự bình an cho Người) cuối cùng cũng hoàn thành xong chiếc thuyền mà Allah đã ra lệnh cho Y đóng nó. Rồi cũng đã đến lúc Allah ra lệnh tiêu diệt bọn họ thì tức khắc nước dâng trào lên cao, nước bắt đầu phun trào tại lò đốt bánh mỳ, một dấu hiệu bắt đầu cho trận đại hồng thủy; TA ra lệnh cho Nuh (cầu xin sự bình an cho người): "Ngươi hãy mang lên thuyền tất cả các loài động vật ở trên mặt đất này mỗi loài một cặp (một con đực và một con cái) và cùng với gia đình của Ngươi ngoại trừng những ai mà giáo lý qui định y phải chết đuối, bởi vì chúng là những kẻ không có đức tin; tuy Y ở với dân của mình rất lâu nhưng số lượng tin tưởng Y quá ít."');
INSERT INTO "vn_mokhtasar" VALUES(11,41,'11:41','Và Nuh bảo với gia quyến và dân chúng của Y những người có đức tin: "Mọi người hãy nhanh chóng lên thuyền bằng Nhân Nghĩa của Allah cho dù chiếc thuyền này nó có trôi nỗi hay dừng ra sao. Quả thật, Thượng Đế của Ta hằng tha thứ tội lỗi cho đám bầy tôi của Ngài biết quay đầu sám hối và Ngài rất mực thương xót cho đám bầy tôi có đức tin của Ngài tránh được trận hủy diệt này."');
INSERT INTO "vn_mokhtasar" VALUES(11,42,'11:42','Và khi con thuyền chở những ai đã lên nó gồm con người và các thứ khác ngoài họ, lênh đênh trên các làn sóng khổng lồ như những ngọn núi, và tình yêu thương của người cha khiến Nuh (cầu xin sự bình an cho Người) lớn tiếng kêu gọi đứa con trai vô đức tin của mình, lúc đó đứa con đang ở một mình không một ai bên cạnh: "Này hỡi con trai! Hãy nhanh chóng lên thuyền cùng với mọi người để tránh được cái chết chìm và con chớ hòa nhập vào đám người không có đức tin, kẻo con sẽ bị chết chìm giống như bọn họ trong trận hủy diệt này."');
INSERT INTO "vn_mokhtasar" VALUES(11,43,'11:43','Đứa con trai của Nuh đáp lại Người: "Con sẽ chạy lên ngọn núi cao, nó sẽ giúp con thoát khỏi trận lũ lụt," rồi Nuh bảo đứa con trai của mình rằng: "Ngày giờ này đây không có gì có thể giúp con ngăn chặn được hình phạt của Allah khi cái chết đuối nó sắp diễn ra với con trong cơn lũ lụt, trừ phi ai đó được Allah Khoan dung, thương xót y nếu Ngài muốn. Dứt lời cơn sóng ập đến chia lìa Nuh và con trai vô đức tin của Người. Thế là con trai của Người bị chết chìm trong trận lũ lụt bởi vì phủ nhận đức tin nơi Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(11,44,'11:44','Và sau khi nước đạt đến đỉnh điểm của nó thì Allah phán với đất: "Này hỡi đất! Ngươi hãy rút cạn nước của ngươi đi," và Ngài cũng phán với bầu trời: "Này hỡi bầu trời! Ngươi hãy dừng ngay và không được trút mưa xuống nữa." Từ đó nước lũ đã rút xuống làm khô ráo hết mặt đất, cùng nghĩa là đám người vô đức tin đã bi Allah tiêu diệt sạch. Và con thuyền bị mắc kẹt lại trên một ngọn núi Judi. Và có lời phán bảo: "Đáng đời cho một dám dân ô hợp bị hủy diệt bởi sự vượt quá giới hạn cho phép của Allah."');
INSERT INTO "vn_mokhtasar" VALUES(11,45,'11:45','Và Nuh (cầu xin sự bình an cho Người) khấn cầu với Thượng Đế của Y để tìm được sự cứu rỗi từ Ngài, Y thưa :" Lạy Thượng Đế của bề Tôi! Quả thật, đứa con trai của bề tôi là một thành viên của gia đình bề tôi nên bề tôi có trách nhiệm cứu vớt y cùng với họ và chắc rằng lời hứa của Ngài đó là một sự thật không thể có sai xót, và Ngài là Đấng Phán Xét Công Bằng nhất trong các vị thẩm phán và Ngài là Đấng Am Tường về bọn họ."');
INSERT INTO "vn_mokhtasar" VALUES(11,46,'11:46','Allah phán bảo Nuh: "Hỡi Nuh! Chắc chắn đứa con trai của Ngươi mà Ngươi đang cầu xin TA cứu vớt hắn, thật sự hắn không phải là thành viên trong gia đình của Ngươi mà TA đã từng hứa với Ngươi là sẽ cứu vớt họ; bởi vì hắn là một kẻ ngoại đạo. Nếu Ngươi còn cầu xin TA thì đó là một việc làm không đáng cho Ngươi chút nào và điều đó nó không phù hợp với thân phận của Ngươi hiện tại. Bởi thế, chớ nên cầu xin TA mà trong khi Ngươi không hề hiểu biết điều gì về hắn, TA khuyên Ngươi cẩn thận coi chừng trở thành những tên ngu ngốc. Bởi vì, sự cầu xin của Ngươi đó là sự nghịch lý với sự am tường và phán xét của TA."');
INSERT INTO "vn_mokhtasar" VALUES(11,47,'11:47','Nuh (cầu xin bình an cho Người) khẩn thưa với Thượng Đế: "Lạy Thượng Đế của bề tôi! Xin Ngài cho bề tôi rút lại viêc đòi hỏi Ngài về những điều mà bề tôi không hề hay biết. Quả thật, nếu Ngài không tha thứ tội lỗi cho bề tôi và sự khoan dung bằng sự khoan dung của Ngài cho bề tôi thì chắc chắn bề tôi sẽ trở thành những kẻ bị thua thiệt chẳng còn gì ở Ngày Sau."');
INSERT INTO "vn_mokhtasar" VALUES(11,48,'11:48','Allah phán bảo Nuh (cầu xin bình an cho Người): "Này hỡi Nuh! Hãy an tâm xuống khỏi con thuyền mà bước lên bờ trong sự bình an và an toàn, bởi hồng ân của Allah giành quá nhiều cho Ngươi và những tín hữu cùng đồng hành với Ngươi trên chiếc thuyền từ những người có đức tin đến từ các thế hệ sau Ngươi. Và rồi sẽ có những cộng đồng khác nhau từ con cháu của họ, họ là những kẻ bất tuân mà vẫn được TA cho hưởng thụ được cuộc sống xa hoa của trần gian này với một cuộc sống thật sung túc, rồi sau đó họ sẽ lãnh đủ hình phạt khủng khiếp vào Ngày Sau."');
INSERT INTO "vn_mokhtasar" VALUES(11,49,'11:49','Câu chuyện kể về Nuh đó là một câu chuyện của thời trước, Ngươi - hỡi Thiên Sứ - đã chưa từng biết và dân chúng của Ngươi cũng vậy mãi đến khi TA thiên khải cho Ngươi về nó. Bởi thế, Ngươi hãy kiên nhẫn chịu đựng về lời nói gian xảo của dân chúng của Ngươi cũng giống như sự kiên nhẫn chịu đựng của Nuh (cầu xin bình an cho Người) vậy. Rồi chắc chắn, kết quả sẽ là sự chiến thắng và thành công cho sự tuân theo mệnh lệnh của Allah và tránh những gì mà Ngài nghiêm cấm.');
INSERT INTO "vn_mokhtasar" VALUES(11,50,'11:50','Và TA đã phái đến cho dân chúng của ''Ad một Thiên Sứ Hud (cầu xin sự bình an cho Người) Y xuất thân từ họ, Y bảo họ: "Này hỡi dân Ta! Hãy thờ phượng Allah duy nhất và chớ tổ hợp bất cứ ai ngang hàng với Ngài, vì các ngươi không có Thượng Đế nào xứng đáng để thờ phượng ngoại trừ Ngài, Đấng Vinh Quang. Thế nhưng các ngươi không làm được như vậy mà ngược lại các ngươi kêu gọi ai đó rồi tổ hợp với Ngài trong khi đó chúng là những kẻ dối trá."');
INSERT INTO "vn_mokhtasar" VALUES(11,51,'11:51','"Hỡi dân ta! Thật sự, ta không hề ham muốn lợi lộc gì của các ngươi về việc Ta đã truyền đạt bức thông điệp từ Thượng Đế của Ta mà Ta chỉ muốn kêu gọi các ngươi đến với Ngài, Ta sẽ không nhận lợi lộc gì ngoại trừ đó là phần thưởng của Allah Đấng đã tạo hóa ra Ta. Thế các ngươi không hiểu ra điều đó ư và các ngươi vẫn không nghe theo lời gọi của hay sao?"');
INSERT INTO "vn_mokhtasar" VALUES(11,52,'11:52','"Và hỡi dân Ta! Hãy cầu xin Allah tha thứ cho các ngươi rồi sau đó hãy sám hối với Ngài cho tội lỗi của các ngươi gây ra - trong đó đại trọng tội vì tội tổ hợp (điều gì,vật gì) cùng với Ngài - có thế Ngài sẽ ban thật nhiều cơn mưa và Ngài sẽ gia tăng thân thế cho các ngươi thật nhiều kể cả con cháu lẫn tiền tài, thế nên các ngươi chớ quay lưng đi với lời kêu gọi của Ta đến với Ngài, bằng không các ngươi sẽ trở thành những kẻ tội đồ khi quay lưng đi với lời kêu gọi của Ta, và các ngươi sẽ là những kẻ phủ nhận Allah và phủ nhận mọi thứ Ta đã mang đến."');
INSERT INTO "vn_mokhtasar" VALUES(11,53,'11:53','Dân chúng của Y đáp :"Hỡi Hud! Thật ra Ngươi không đưa ra được bằng chứng cụ thể để cho bọn ta có thể tin tưởng Ngươi, thế bọn ta sẽ không vì vậy mà bỏ mặc sự thờ phượng các thần linh của bọn ta bằng một lời nói của Ngươi mà không có gì chứng minh. Và bọn ta sẽ không tin tưởng Ngươi nữa với những lời kêu gọi của Ngươi đến với Ngài như Ngươi đã khăng khăng cho rằng mình là một Thiên Sứ."');
INSERT INTO "vn_mokhtasar" VALUES(11,54,'11:54','"Bọn ta nói cho Ngươi hay rằng có lẽ một số thần linh của bọn ta đã khiến Ngươi lên cơn điên mất trí rồi bởi vì Ngươi không còn thờ phượng họ nữa." Hud đáp: "Ta cầu xin Allah làm chứng và yêu cầu các ngươi cũng làm chứng rằng Ta hoàn toàn vô can về việc các ngươi thờ phượng các thần linh mà các ngươi đang thờ phượng thay vì Allah."');
INSERT INTO "vn_mokhtasar" VALUES(11,55,'11:55','Do đó, các ngươi hãy tiếp tục bảo thần linh của các ngươi hãm hại Ta đi, đừng để Ta yên thân giống như lời cáo buộc mà các ngươi đã bảo Ta bị thần linh của các ngươi làm cho khùng điên.');
INSERT INTO "vn_mokhtasar" VALUES(11,56,'11:56','"Quả thật, Ta tin cậy và phó thác hết cho Allah Đấng Duy Nhất, Ngài đáng để Ta dựa dẫm cho tiền đồ của Ta. Bởi vì, Ngài là Thượng Đế của Ta và cũng là Thượng Đế của các ngươi, không có một sinh vật nào hiện diện trên mặt đất mà nó lại không thuộc quyền cay trị và quản lý của Allah cả, Ngài muốn xoay chuyển nó ra sao tùy Ngài. Chắc chắn, Thượng Đế của Ta luôn là chân lý và công bằng, do đó Ngài sẽ không cho các ngươi chiến thắng được như Ta, bởi vì Ta trên con đường Chân Lý còn các ngươi thì đang trên con đường xàm bậy."');
INSERT INTO "vn_mokhtasar" VALUES(11,57,'11:57','"Bởi thế, nếu các ngươi quay lưng bỏ đi thì ít nhiều gì Ta cũng đã truyền đạt xong về Bức Thông Điệp mà Ta đã mang đến cho các ngươi, Ta khẳng định với các ngươi rằng tất cả nội dung Bức Thông Điệp là đến từ Allah, Ngài ra lệnh Ta phải truyền đạt Nó và chắc chắn đó cũng là chứng cớ để tố cáo các ngươi. Và Thượng Đế của Ta sẵn sàng tiêu diệt hết các ngươi, rồi Ngài sẽ cho những thế hệ sau đó kế nghiệp các ngươi và các ngươi sẽ không có cơ hội để hãm hại được Allah cho dù tác hại đó có to lớn đến cỡ nào hay bé nhỏ đi chăng nữa, bởi Ngài là Đấng Giàu Có bất cần đến mọi thứ, quả thật Thượng Đế của Ta Đấng Giám Sát hết tất cả mọi vạn vật, Ngài là Đấng sẽ bảo vệ Ta tránh khỏi điều xấu xa mà các ngươi muốn âm mưu hãm hại Ta');
INSERT INTO "vn_mokhtasar" VALUES(11,58,'11:58','Và khi mệnh lệnh của TA có hiệu lực là hủy diệt bọn họ thì TA đã nhủ lòng thương xót giải cứu Hud và những ai có đức tin đang đồng hành cùng Y bằng lòng khoan dung của TA. Và TA đã giải cứu họ an toàn tránh được hình phạt thật khủng khiếp mà TA sẽ trừng trị một đám dân ô hợp không có đức tin của Y.');
INSERT INTO "vn_mokhtasar" VALUES(11,59,'11:59','Và đó là số phận được an bài cho đám dân ''Ad. Những kẻ đã phủ nhận các Dấu hiệu của Allah, Thượng Đế của họ và bất tuân lại Hud Thiên Sứ của bọn họ, thế nhưng ngược lại bọn họ mù quáng đi theo mọi mệnh lệnh của những tên ngạo mạn phủ nhận Chính Đạo, ngoan cố không phục tùng.');
INSERT INTO "vn_mokhtasar" VALUES(11,60,'11:60','Và bọn họ đáng phải trả một cái giá thật đắc cho cuộc sống trần gian này đó là bị xua đuổi khỏi lòng khoan dung của Allah và điều đó còn bám sát họ vào Ngày Sau, chỉ vì họ là những kẻ phủ nhận đức tin vào Allah Tối Cao. Thật đáng đời cho bọn họ đã bị Allah loại ra khỏi mọi điều tốt và đẩy họ gần sát với điều xấu xa.');
INSERT INTO "vn_mokhtasar" VALUES(11,61,'11:61','Và TA đã cử phái Saleh đến với dân chúng của Thamud, Y là người anh em của họ, Y bảo: "Hỡi dân Ta! Hãy thờ phượng Allah Đấng Duy Nhất vì không có Thượng Đế nào xứng đáng để thờ phượng ngoại trừ Ngài, Ngài là Đấng tạo hóa tổ tiên của các ngươi từ đất sét, đó là Adam cũng từ đất rồi sau đó Ngài ủy quyền cho các ngươi cai quản trái đất. Bởi thế, các ngươi hãy cầu xin Ngài tha thứ tiếp và hãy quay đầu sám hối với Ngài thể hiện qua sự tuân thủ mệnh lệnh của Ngài và từ bỏ những việc làm phạm tội. Quả thật, Thượng Đế của Ta luôn kế cận đối với những ai thành thật với Ngài trong việc thờ phụng để đáp ứng lại lời cầu xin đến Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(11,62,'11:62','Chúng đáp lại Người: "Hỡi Saleh! Đúng là trước đây Ngươi rất được bọn ta tôn trọng vì địa vị và kiến thức của Ngươi, cho nên bọn ta rất kỳ vọng với trí tuệ của Ngươi sẽ cho bọn ta lời khuyên hữu ích, vậy mà giờ này đây - hỡi Saleh - Ngươi cấm bọn ta không được thờ phượng những vị mà tổ tiên của bọn ta đã thờ phượng đó ư ? Quả thật, Ngươi đã làm cho bọn ta nghi ngờ về những gì mà Ngươi kêu gọi bọn ta thờ phượng Allah Đấng Duy Nhất, Ngươi đã làm cho bọn ta nghĩ Ngươi là người đặt điều dối trá rồi đỗ thừa cho Allah."');
INSERT INTO "vn_mokhtasar" VALUES(11,63,'11:63','Saleh đáp lại đám dân của Mình: "Hỡi dân Ta! Các ngươi hãy cho Ta biết sẽ ra sao khi Ta đã dựa dẫm vào bằng chứng rõ rệt đến từ Thượng Đế của Ta và Ngài còn ban cho Ta thiên ân đó là đã chọn Ta là Thiên sứ. Bởi thế, ai có thể ngăn cản được hình phạt của Ngài nếu Ta bất tuân với Ngài cho việc là dám bỏ mặc không tuyên truyền sứ mạng đó lại cho các ngươi mà trong khi Ngài ra lệnh cho Ta là phải truyền đạt lại ? Bởi thế, các ngươi chỉ làm cho Ta tăng thêm lầm lạc và làm cho Ngài không hài lòng về Ta."');
INSERT INTO "vn_mokhtasar" VALUES(11,64,'11:64','Saleh bảo: "Hỡi dân Ta! Đây là con lạc đà cái của Allah đó là một dấu hiệu xác thực cho lời nói thật của Ta với các ngươi. Bởi thế, các ngươi bỏ mặc nó tự tìm kế sinh nhai ở trên mảnh đất của Allah và chớ hãm hại hay đá động đến nó bằng mọi hình thức nào đó vì Ta chỉ sợ rằng hình phạt sẽ đến rất gần nếu các ngươi dám to gan dám sát hại nó."');
INSERT INTO "vn_mokhtasar" VALUES(11,65,'11:65','Thế mà bọn chúng đã to gan dám cải lệnh mà đâm cổ con lạc đà cái đó. Thấy vậy, Saleh bảo chúng: "Các ngươi hãy hưởng thụ cuộc sống trong xứ này cho tốt vì các ngươi chỉ còn sống sót được ba ngày từ ngày kể từ khi các ngươi đã sát hại nó, sau đó hình phạt của Allah sẽ giáng xuống các ngươi. Chắc chắn khi Ngài đã hứa là sẽ trừng trị những ai dám làm điều đó. Không, đó là một lời hứa Danh Dự của Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(11,66,'11:66','Đến khi mệnh lệnh của TA ban hành về việc tiêu diệt bọn chúng bắt đầu có hiệu lực thì TA nhủ lòng nhân từ giải cứu Saleh cùng với những ai có đức tin đi theo Y thoát khỏi sự nhục nhã đầy xấu hổ của ngày hôm đó. Quả thật, Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng Hùng Mạnh không một ai có thể chiến thắng Ngài, vì vậy Ngài đã hủy diệt hết các cộng đồng đã phủ nhận');
INSERT INTO "vn_mokhtasar" VALUES(11,67,'11:67','Và tiếng gầm thét kinh hoàng bắt đầu, nó túm lấy đám dân chúng Thamud khiến cho họ chết úp mặt xuống đất vào buổi sáng.');
INSERT INTO "vn_mokhtasar" VALUES(11,68,'11:68','Hình như họ chưa từng có cuộc sống hưng thịnh và an nhàn trên quê hương đất nước của họ bao giờ. Quả thật, dân chúng Thamud là một lũ ô hợp đã phủ nhận Allah, Thượng Đế của họ. Ôi! Thật đáng đời cho họ đã bị Allah gạt bỏ ra khỏi lòng thương xót của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(11,69,'11:69','Và TA đã cử các Thiên Thần với hình dạng của những người đàn ông đến gặp Ibrahim (cầu xin sự bình an cho Người) để báo tin mừng đến cho Y và vợ của Y về đứa con trai do vợ chồng Y sinh ra tên là Ishaq và đứa cháu nội của Y là Y''aqub. Bởi thế, các vị Thiên Thần bảo: "Chào bằng an" và rồi Ibrahim đáp lại: "Chào bằng an" xong Y bước đi nhanh chóng và quay trở lại với một con bê con nướng để chiêu đãi những vị khách quí ăn vì Y cứ tưởng Họ là những người đàn ông.');
INSERT INTO "vn_mokhtasar" VALUES(11,70,'11:70','Thế nhưng khi Ibrahim nhìn thấy những bàn tay của Họ không chạm đến thịt con bê con đó và Họ đều không dùng đến thức ăn nên Y đem lòng sinh nghi và đâm ra sợ sệt. Bởi vậy, khi các Thiên thần nhìn thấy Y sợ sệt thì Họ bảo: "Này, Ngươi đừng sợ, chúng tôi là những Sứ Giả được Allah cử đến với dân chúng của Lut để trừng trị bọn họ."');
INSERT INTO "vn_mokhtasar" VALUES(11,71,'11:71','Và bà "Sarah" vợ của Ibrahim đứng cạnh đó. Thấy thế, chúng tôi sẽ báo cho bà một tin vui rằng bà sẽ hạ sinh ra được một đứa con trai tên là Ishaq và rồi đứa con trai Ishaq của bà sẽ có được đứa con trai là Y''aqub. Tuy nhiên, nghe xong thì bà bật cười trong niềm vui sướng."');
INSERT INTO "vn_mokhtasar" VALUES(11,72,'11:72','Bà Sarah lấy làm kinh ngạc sau khi được các vị Thiên Thần báo tin vui! Bà đáp: "Làm thế nào tôi có thể hạ sinh một đứa con trai trong khi đó tôi lại là một bào lão và chồng của tôi cũng lại là một ông già ? Quả thật, trong trường hợp hạ sinh đứa con đó là một điều hết sức kỳ lạ chưa từng có trước đây."');
INSERT INTO "vn_mokhtasar" VALUES(11,73,'11:73','Các vị Thiên Thần đáp lại bà Sarah khi bà ngạc nhiên về tin mừng đó: "Há bà lấy làm ngạc nhiên về sự sắp đặt và sự an bày của Allah ư ? Đáng lẽ với vị trí của bà cần nhận thức rõ là Allah luôn có khả năng hơn cả điều này, Ngài đã ân sủng và ban Hồng Ân cho các Ngươi - hỡi gia đình của Ibrahim - quả thật, Allah là Đấng Đáng Ca Tụng trong nhân cách và hành động của Ngài và là Đấng Rất Mực Quang Vinh."');
INSERT INTO "vn_mokhtasar" VALUES(11,74,'11:74','Đến khi Ibrahim (cầu xin sự bình an cho Người) tan đi cơn sợ hãi, lấy lại được bình tỉnh trước những vị khách không dùng đến thức ăn, sau khi nhận biết rằng Họ là những vị Thiên Thần đến để thông báo cho Y về những điều tốt đẹp rằng là sẽ sinh ra được đứa con trai là Ishaq và cháu nội là Y''aqub. Rồi Y bắt đầu đứng ra nài nỉ với chúng tôi giùm cho đám dân của Lut để tạm hoãn thi hành lệnh trừng phạt với hi vọng giải thoát được Lut và gia đình của Người');
INSERT INTO "vn_mokhtasar" VALUES(11,75,'11:75','Quả thật, Ibrahim là một Người hằng chịu đựng, lòng dạ nhân từ thương người luôn muốn trì hoãn hình phạt, Người thường xuyên cũng cầu nguyện với Thường Đế của Mình, lúc nào cũng cầu xin và luôn tỏ ra hối cải với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(11,76,'11:76','Các Thiên Thần bảo: "Hỡi Ibrahim! Hãy bỏ qua chuyện nài nỉ cho đám dân ô hợp của Lut đi. Quả thật, mệnh lệnh của Thượng Đế nhà Ngươi đã được ban hành trừng phạt họ giống như Ngài đã sắp đặt. Chính đám dân của Lut sẽ phải gánh chịu cực hình, thế nên không thể nào thu hồi cho dù có nài nỉ hay van xin đi chăng nữa."');
INSERT INTO "vn_mokhtasar" VALUES(11,77,'11:77','Và khi các vị Thiên Thần đến gặp Lut với hình dáng là những người đàn ông đã khiến Lut lo lắng cho Họ, làm cho lòng ngực của Y như đang siết chặt lại bởi sự khiếp sợ cho dân chúng của Mình sẽ kéo đến để thỏa mãn dục tính của họ với đàn ông thay vì với phụ nữ, Lut bảo: "Đây thật sự là một ngày quá tồi tệ, bởi Người tưởng dân chúng của Mình sẽ thắng được những vị khách quí đó.');
INSERT INTO "vn_mokhtasar" VALUES(11,78,'11:78','Và đám dân của Lut kéo đến gặp Lut với ý định dở trò đồi bại với những vị khách quý của Người, bởi vì chúng luôn có thói quen thõa mãn sinh lý với đàn ông thay vì với phụ nữ. Lúc này Lut cố ra sức kềm nén mà giải thích và nhẹ lời với đám dân trước những vị khách, Người bảo: "Đây là những cô con gái xinh đẹp của Ta, cũng thuộc dân tộc của các ngươi, các ngươi hãy cưới chúng nó làm vợ, chúng là những cô gái trinh tiết thay vì các ngươi làm những điều đồi bại. Các ngươi hãy kính sợ Allah, chớ dỡ trò đồi bại trước mặt các vị khách của Ta. Lẽ nào trong các ngươi - hỡi dân chúng - không có một ai sở hữu được một khối óc rất minh mẩn có thể cản trở các ngươi làm thói đồi bại đó hay sao ?"');
INSERT INTO "vn_mokhtasar" VALUES(11,79,'11:79','Đám dân của Y đáp lại Y: "Chắc chắn, Ngươi đã biết rõ - hỡi Lut - về bọn ta không cần đến các người con gái của Ngươi rồi mà và cũng không có nhu cầu sinh lý với phụ nữ của làng này. Quả thật, Ngươi đã thấu hiểu những gì mà bọn ta muốn, bọn ta không cần ai hết ngoại trừ những người đàn ông."');
INSERT INTO "vn_mokhtasar" VALUES(11,80,'11:80','Lut đáp lại: "Ước gì Ta có đủ sức mạnh để cản trở các ngươi làm điều đó hoặc có được viện trợ của một bộ lạc để bảo vệ các vị khách của Ta khỏi các ngươi."');
INSERT INTO "vn_mokhtasar" VALUES(11,81,'11:81','Các vị Thiên Thần bảo Lut (cầu xin sự bình an cho Người): "Hỡi Lut! Thật ra chúng tôi là những vị Thiên Sứ của Thượng Đế của Ngươi được phái đến, sẽ không ai trong đám dân đó hại được Ngươi. Bởi thế, Ngươi cùng gia đình của Ngươi hãy ra khỏi cái thị trấn này vào khoảng thời gian tối mịt và đừng để cho một ai quay đầu lại nhìn về phía sau ngoại trừ bà mụ vợ của Ngươi vì mụ ta sẽ là đồng minh với những kẻ sai quấy. Bởi vì, sự trừng phạt nó sắp xảy ra đến với mụ ta và với đám dân ô hợp của Ngươi. Quả thật, thời điểm mà để tiêu diệt bọn chúng là vào một buổi sáng và thời gian đó nó xảy ra rất là gần."');
INSERT INTO "vn_mokhtasar" VALUES(11,82,'11:82','Đến khi mệnh lệnh tiêu diệt đám dân ô hợp của Lut mà TA đã ban hành thì TA đã lật ngược cái thị trấn của họ lên lơ lững mà ụp xuống và TA đã trút xuống theo những trận mưa đá diêm sinh cứng như đất sét nung, lớp này chồng chất lên lớp kia.');
INSERT INTO "vn_mokhtasar" VALUES(11,83,'11:83','Đấy là những viên đá được Allah đánh dấu riêng biệt. Những viên đá đó nó không ở xa với những tên làm đều sai quấy trong bộ tộc Quraish và những kẻ khác họ. Không, nó luôn cận kề với họ, bất cứ khi nào Allah muốn nó đổ trút nó lên họ thì nó lập tức đổ ngay.');
INSERT INTO "vn_mokhtasar" VALUES(11,84,'11:84','Và TA đã cử phái Shu''aib đến với dân chúng của Madyan, Y bảo :"Hỡi dân Ta! Hãy thờ phượng Allah Đấng Duy Nhất vì các ngươi không có Thượng Đế nào xứng đáng thờ phượng nào khác ngoài Ngài. Và các ngươi không nên đông cân thiếu cho mọi người trong khi Ta nhìn thấy các ngươi đang sống trong thịnh vượng và hưởng thụ, vì vậy các ngươi đừng gian lận kẻo phải trả giá là đánh mất những hồng ân của Allah đã ban. Quả thật, Ta khiếp sợ giùm cho các ngươi về hình phạt của Ngày Sau, nó sẽ bao chùm các ngươi không một ai có thể tránh khỏi và sẽ không có một kẻ nào trốn thoát và cũng chẳng tìm được nơi nào trú ẩn."');
INSERT INTO "vn_mokhtasar" VALUES(11,85,'11:85','"Hỡi dân Ta! Hãy cân và đo lường cho đầy đủ, trung thực. Các ngươi chớ có chiếm đoạt quyền lợi của người khác bằng thủ đoạt, bằng gian lận hay lừa gạt. Và cũng chớ có gây ra điều thối nát trên trái đất này bằng cách giết chóc hoặc bằng tội lỗi."');
INSERT INTO "vn_mokhtasar" VALUES(11,86,'11:86','"Phần mà Allah để lại cho các ngươi từ phần lời Halal khi các ngươi đong, đo trung thực đó, trong đó có rất nhiều điều hữu ích và hồng ân làm gia tăng thêm ân đức và làm giảm đi những việc làm thối nát trên trái đất này. Quả thật, nếu các ngươi là những người tin tưởng thật sự thì hãy hài lòng về những phần còn lại đó. Ta không phải là người theo dõi ghi chép những việc làm của các ngươi hay là kiểm duyệt ân phước của các ngươi, mà người giám sát các ngươi chính là Đấng am tường mọi việc được giấu kín và công khai."');
INSERT INTO "vn_mokhtasar" VALUES(11,87,'11:87','Đám dân của Shu''aib đáp lại Shu''aib: "Hỡi Shu''aib! Phải chăng, lễ nguyện Salah của Ngươi đang thực hiện trước Allah đã chỉ thị cho Ngươi bắt bọn ta từ bỏ ngay việc tôn thờ những bụt tượng mà tổ tiên của bọn ta đang thờ phượng ư? Hay là bắt bọn ta dừng lại những điều mà bọn ta vốn chỉ muốn sử dụng tài sản của bọn ta như thế nào tùy thích và bọn ta muốn phát triển nó lớn lên không được ư ? Chắc chắn Ngươi là một Người biết nhẫn nại, thông minh. Quả thật, trước đây Ngươi sở hữu được một khối óc thông thái giống như bọn ta biết rõ về Ngươi, giờ đây Ngươi đã bị gì thế ?');
INSERT INTO "vn_mokhtasar" VALUES(11,88,'11:88','Shu''aib bảo với dân chúng của Y: "Hỡi dân Ta! Các ngươi hãy nói cho Ta biết cần phải xử sự ra sao khi Ta đang dựa dẫm vào một các bằng chứng xác thực đến từ Thượng Đế của Ta, một nhận thức rõ ràng, ngoài ra Ta luôn được Ngài chu cấp bổng lộc Halal và Ngài chọn cho Ta là một vị Thiên Sứ. Ta không muốn làm khác các ngươi về điều các ngươi đang làm, mà Ta chỉ muốn cải thiện các ngươi qua việc mời gọi các ngươi trở về với Tawhid thờ phượng Thượng Đế của các ngươi, Đấng Duy Nhất với khả năng có thể nhưng có thành công hay không thì còn phụ thuộc hết vào Allah mà thôi, Ta đã phó thác hết mọi viêc cho Allah duy nhất và luôn sám hối với Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(11,89,'11:89','"Và hỡi dân Ta! Chớ khiến lòng hận thù Ta làm động lực buộc các ngươi phủ nhận sứ mạng của Ta, Ta rất khiếp sợ cho các ngươi về cái hình phạt giống như hình phạt đã rơi vào dân chúng của Nuh, dân chúng của Hud, dân chúng của Saleh, và dân chúng của Lút đã không xa với các ngươi là bao, các ngươi đã biết rõ thời gian hay vị trí cụ thể về họ, các ngươi biết rõ điều gì đã xảy ra với họ, bởi thế các ngươi nên suy ngẫm."');
INSERT INTO "vn_mokhtasar" VALUES(11,90,'11:90','"Và các ngươi hãy cầu xin Thượng Đế của các ngươi tha thứ và sám hối với Ngài về những tội lỗi của các ngươi đã gây ra. Quả thật, Thượng Đế của Ta rất mực khoan dung với những ai biết hối cải và Ngài rất yêu mến những ai biết sám hối trong họ."');
INSERT INTO "vn_mokhtasar" VALUES(11,91,'11:91','Dân chúng của Shu''aib đáp lại Shu''aib: "Hỡi Shu''aib! Thật ra bọn ta không hiểu rất nhiều về điều mà Ngươi mang đến, vả lại Ngươi trong mắt bọn ta không khác gì là một kẻ yếu đuối hoặc chỉ là kẻ mù nhìn. Và nếu như không có dòng tộc của Ngươi thì bọn ta đã ném đá giết chết Ngươi rồi. Bọn ta không giết Ngươi không phải vì sợ Ngươi mà do tôn trọng dòng tộc của Ngươi đó."');
INSERT INTO "vn_mokhtasar" VALUES(11,92,'11:92','Shu''aib đáp lại dân chúng của Y: "Hỡi dân Ta, lẽ nào các ngươi cho rằng dòng tộc của Ta cao quí và mạnh hơn Allah Thượng Đế của các ngươi ư ? Thế nên, các ngươi đã quẳng Allah ra đằng sau lưng các ngươi nên không chịu tin tưởng bất cứ những gì mà Thiên Sứ của Ngài mang đến cho các ngươi. Quả thật, Thượng Đế của Ta thâu tóm hết tất cả mọi việc mà các ngươi làm, không có một việc làm nào của các ngươi mà có thể che giấu được Ngài, các ngươi sẽ gánh chịu hậu quả của các ngươi đã gây ra ở trần gian này đó là sự diệt vong và vào Ngày Sau là sự trừng phạt."');
INSERT INTO "vn_mokhtasar" VALUES(11,93,'11:93','"Và hỡi dân Ta! Hãy làm những gì mà các ngươi có thể trên con đường mà các ngươi cảm thấy hài lòng về nó, còn Ta cũng chỉ làm những gì trên con đường mà Ta cảm thấy hài lòng và trong khả năng cho phép. Rồi đây, các ngươi sẽ sớm biết ai sẽ bị Ngài trừng phạt một cách thật là nhục nhã và ai sẽ là kẻ dối trá mà bị Ngài cảnh báo. Bởi vậy, các ngươi hãy chờ xem những gì đã được Allah an bài và Ta sẽ ở cùng với các ngươi chờ xem điều đó."');
INSERT INTO "vn_mokhtasar" VALUES(11,94,'11:94','Và khi mệnh lệnh của TA được thi hành là tiêu diệt hết đám dân của Shu''aib, thế nhưng TA lại nhủ lòng thương xót đã giải cứu Shu''aib cùng với những người có đức tin đồng hành cùng với Người. Và tất cả bọn đã bị một âm vang kinh hoàng khiếp vía đã giết sạch bọn họ chết trong tư thế chúi đầu la liệt ở trong nhà vào buổi hừng đông và gương mặt của chúng mắt kẹt trong đất.');
INSERT INTO "vn_mokhtasar" VALUES(11,95,'11:95','Tựa như trước kia bọn chúng chưa từng có được cuộc sống ở nơi đó bao giờ. Quả thật, dân Madyan đã bị xua đuổi ra khỏi lòng khoan dung của Allah bằng cách Ngài đã trừng trị bọn chúng giống như Ngài đã từng xua đuổi dân Thamud bằng cách giáng lên họ cái hình phạt trong cơn thịnh nộ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(11,96,'11:96','Và trước đây, TA đã cử phái Musa mang những dấu hiệu lạ đến từ TA, đó bằng chứng cho thuyết Tawhid của Allah và đó cũng là chứng cớ xác thực chứng minh thật sự những gì đến từ TA.');
INSERT INTO "vn_mokhtasar" VALUES(11,97,'11:97','TA đã cử Người đến gặp Fir-''awn và những kẻ nắm quyền hành của đám dân hắn. Thế nhưng, những kẻ nắm quyền hành chúng chỉ phục tùng mệnh lệnh của Fir-''awn mà phủ nhận Allah. Trong khi đó mệnh lệnh của tên Fir-''awn lại là xàm bậy không đúng với Chân Lý cho họ noi theo.');
INSERT INTO "vn_mokhtasar" VALUES(11,98,'11:98','Vào Ngày Phục Sinh, Fir-''awn sẽ dẫn đầu đám dân của hắn vào Hỏa Ngục cùng với hắn. Ôi! Thật tồi tệ thay cho cái nơi mà chúng bị hắn dẫn đi vào.');
INSERT INTO "vn_mokhtasar" VALUES(11,99,'11:99','Và lời nguyền rủa của Allah sẽ còn đeo bám theo họ suốt cuộc đời này lẫn cả Đời Sau, nghĩa là họ bị xua đuổi và trục xuất ra khỏi lòng khoan dung của Ngài nên đã bị diệt vong bằng cách chết chìm. Tệ hại hơn là chúng phải gánh chịu sự trừng phạt của Ngài ở trần gian này và lẫn Ngày Sau.');
INSERT INTO "vn_mokhtasar" VALUES(11,100,'11:100','Với những nguồn tin mà TA đã kể cho Ngươi nghe - hỡi Thiên Sứ - về một số ngôi làng nó hãy vẫn còn giữ lại di tích xưa và một số khác nó đã bị xóa sạch.');
INSERT INTO "vn_mokhtasar" VALUES(11,101,'11:101','Và TA đã không hề khiến họ làm những chuyện bất công để rồi dẫn đến bị diệt vong, nhưng họ đã khiến chính bản thân mình thành những kẻ sai quấy để rồi chuốt lấy cảnh diệt vong vì phủ nhận Allah. Thì giờ này đây không có một thần linh nào mà họ thờ phượng thay vì là Allah có thể giúp ích được cho bản thân họ tránh khỏi sự trừng phạt khi mệnh lệnh của Thượng Đế của Ngươi được ban hành - hỡi Thiên Sứ - bọn họ sẽ bị tiêu diệt và ngược lại, những thần linh đó còn gây tệ hại hơn khiến chúng trầm trọng thêm để rồi chuốt lấy sự hủy diệt.');
INSERT INTO "vn_mokhtasar" VALUES(11,102,'11:102','Và sự thâu tóm và trừ tiệt của Allah đã nắm lấy những ngôi làng của đám người dối trá vào bất cứ thời gian và địa điểm nào là như thế đó. Quả thật, Ngài đã túm lấy ngôi làng trong lúc bọn chúng đang làm điều sai quấy một cách thật là kinh hoàng.');
INSERT INTO "vn_mokhtasar" VALUES(11,103,'11:103','Quả thật, trong sự việc mà Allah đã tóm cổ mạnh bạo đối với những kẻ ở trong thị trấn khi họ đang say xưa làm chuyện sai quấy để cho những ai biết khiếp sợ về hình phạt của Ngày Phục Sinh. Đó là một ngày mà toàn thể nhân loại tập hợp lại để nhận lấy trách nhiệm của họ và đó là ngày mà tất cả đều hiện diện để làm nhân chứng cho đám người Al-Mahshar.');
INSERT INTO "vn_mokhtasar" VALUES(11,104,'11:104','Và TA chỉ tạm quản Ngày làm nhân chứng đó đến một kỳ hạn được ấn định.');
INSERT INTO "vn_mokhtasar" VALUES(11,105,'11:105','Ngày hôm đó chính là Ngày mà không có một ai có thể thốt ra được một lời nào cho dù đó là bằng chứng hay lời biện hộ nào ngoại trừ được Ngài cho phép. Và vào Ngày đó nhân loại chia thành hai nhóm: Một nhóm bất hạnh sẽ đi vào Hỏa Ngục và nhóm hạnh phúc sẽ vào Thiên Đàng');
INSERT INTO "vn_mokhtasar" VALUES(11,106,'11:106','Thế nên những kẻ bất hạnh vì đã phủ nhận Allah và chuyên làm những điều sai quấy sẽ phải đi vào Hỏa Ngục và họ sẽ rào thét tự trách bản thân than vãn đủ điều bởi những gì mà chúng đang phải chịu đựng trong ngọn lửa cuồng cuộn.');
INSERT INTO "vn_mokhtasar" VALUES(11,107,'11:107','Chúng ở trong Hỏa Ngục mãi mãi mà không tài nào có thể thoát ra được, lâu như khoảng thời gian mà bầu trời và trái đất vẫn còn tồn tại ngoại trừ Allah muốn giải thoát cho những ai vẫn còn thuyết Tawhid trong tim y. Quả thật, Thượng Đế của Ngươi - hỡi Thiên Sứ - Ngài làm bất cứ thứ gì mà Ngài muốn và không có gì mà Ngài không thể làm đặng.');
INSERT INTO "vn_mokhtasar" VALUES(11,108,'11:108','Còn đối với những người có hạnh phúc đã được Allah định sẵn cho họ trước đây bởi đức tin Iman và việc làm ngoan đạo của họ. Bởi thế, họ sẽ ở trong Thiên Đàng mãi mãi như thời gian tồn tại của trời và đất ngoại trừ Allah muốn tống ai vào Hỏa Ngục trước khi cho y vào Thiên Đàng trong đám người Mu''min vì tội lỗi họ đã phạm. Quả thật, sự hưởng thụ mà Allah giành cho cư dân của Thiên Đàng là vĩnh hằng không bao giờ gián đoạn.');
INSERT INTO "vn_mokhtasar" VALUES(11,109,'11:109','Do đó Ngươi - hỡi Thiên Sứ - chớ hoài nghi về điều sai quấy mà bọn đa thần đang thờ phượng. Bởi vì, bọn họ không làm đúng theo chỉ đạo của Ngài, không phù hợp với tư duy và cũng không phù hợp với giáo lý, họ chỉ biết mù quáng bắt chước theo tổ tiên của họ mà thôi. Và chắc hẳn TA sẽ đáp ứng đầy đủ hình phạt cho bọn họ không hề giảm bớt tí nào.');
INSERT INTO "vn_mokhtasar" VALUES(11,110,'11:110','Và trước đây, TA đã ban Kinh Tawrah tức Cựu Ước xuống cho Musa. Thế nhưng, mọi người lại có sự bất đồng trong Nó, có một số người tin tưởng Nó còn một số người thì phủ nhận Nó. Nếu như sự việc đã không được Allah an bày sẵn trước đó là sẽ không trừng phạt họ sớm hơn mà chỉ tạm hoãn lại vào Ngày Phục Sinh vì mục đích vĩ đại hơn để họ phải gánh chịu một hình phạt thật xứng đáng ở trần gian này. Quả thật, những tên vô đức tin thuộc người Do Thái và người đa thần chắc hẳn có sự hoài nghi về Qur''an.');
INSERT INTO "vn_mokhtasar" VALUES(11,111,'11:111','Và chắc chắn, mỗi người sẽ nhận lại đủ phần tương ứng khác nhau từ Thượng Đế của Ngươi - hỡi Thiên Sứ - đó là phần thưởng của họ đã gầy công làm. Bởi thế, ai làm tốt đẹp sẽ được Ngài ban thưởng hậu hĩnh còn ai làm xấu sẽ nhận lấy được kết cuộc tệ hại. Quả thật, Allah chỉ trong giấy lát Ngài có thể am tường về họ làm những gì và họ làm bất cứ điều gì cũng không thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(11,112,'11:112','Do đó, hãy luôn luôn tuân thủ theo con đường Chính Đạo - hỡi Thiên Sứ - giống như Allah ra lệnh cho Ngươi vậy, hãy tuân thủ tuyệt đối mọi mệnh lệnh của Ngài và tránh xa những gì mà Ngài nghiêm cấm. Và tín đồ Mu''min biết hối cải cùng với Ngươi cũng thế, hãy đi đúng theo con đường Chính Đạo và chớ có vượt qua giới luật rồi các ngươi sẽ phải gánh tội. Quả thật, Ngài luôn thấy rõ hành động của các ngươi, không việc nào có thể che giấu được Ngài và theo đó Ngài sẽ thưởng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(11,113,'11:113','Và các ngươi chớ có khuynh hướng làm theo những tên ngoại đạo chuyên làm những điều sai quấy bằng khiêm nhường hoặc gây thiện cảm với họ, e rằng lửa của Hỏa Ngục sẽ bao chùm lấy các ngươi bởi sự khuynh hướng đó của các ngươi và rồi đến lúc đó ngoài Allah ra không một ai có thể giải thoát cho các ngươi và các ngươi cũng không tìm ra một ai đó có thể giúp đỡ các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(11,114,'11:114','Và Ngươi - hỡi Thiên Sứ - hãy dâng lễ nguyện Salah tốt nhất vào hai khoảng thời gian hai đầu của trong ngày, đó là vào buổi hừng đông và buổi chiều cùng với khoảng thời gian vào ban đêm. Quả thật, những việc làm ngoan đạo đó nó sẽ tẩy sạch đi những tội lỗi nhỏ cho các ngươi. Đó là điều để nhắc nhở cho những ai biết thận trọng chú ý và đó cũng là một bài học quý báo cho những ai biết ngẫm nghĩ.');
INSERT INTO "vn_mokhtasar" VALUES(11,115,'11:115','Và Ngươi hãy cố gắng kiên nhẫn làm theo mệnh lệnh là phải ngoan đạo và những điều khác, bỏ ngay ý định làm những điều sai quấy rồi dẫn đến sự bất công. Quả thật, Allah sẽ không làm mất đi phần thưởng tốt đẹp của người sùng đạo. Đúng hơn, Ngài sẽ chấp nhận mọi việc làm tốt đẹp của họ và ban thưởng phần thưởng tốt đẹp xứng đáng cho những việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(11,116,'11:116','Phải chi những cộng đồng bị tiêu diệt trước đây có được những người tốt đẹp và ngoan đạo để ngăn cản họ trở thành những tên phủ nhận chuyên làm những điều đồi bại trên trái đất này bởi những việc phạm tội của họ, thế nhưng cũng không bao gồm hết bọn chúng vẫn còn một số ít người họ luôn ra sức ngăn chặn những điều thối nát đó. Bởi thế, TA đã giải thoát họ trong khi TA đang tiêu diệt đám dân ô hợp những tên chuyên làm những điều sai quấy. Và tệ hại hơn cho những kẻ làm sai quấy chúng cứ lo chạy theo sự phù phiếm xa hoa của trần gian này và rồi biến mình thành những tên không ra gì để rồi chuốc họa sát thân.');
INSERT INTO "vn_mokhtasar" VALUES(11,117,'11:117','Và Thượng Đế của Ngươi - hỡi Thiên Sứ - không phải là TA vô cớ tiêu diệt những xóm làng đó nếu như bọn họ là những tên tốt đẹp trên đời này. Mà không TA tiêu diệt tất cả là vì cư dân ở đó là đám người thối nát bởi họ vô đức tin, bất công và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(11,118,'11:118','Và nếu như Thượng Đế của Ngươi muốn - hỡi Thiên Sứ - thì Ngài đã gom nhân loại này lại thành một cộng đồng duy nhất để cho họ làm những điều Chân Chính, thế nhưng Ngài không muốn làm điều đó. Cứ mặc cho họ bất đồng ý kiến với nhau bởi họ vẫn còn bất đồng ý kiến với nhau chỉ vì nguyên nhân chạy theo ham muốn và dục vọng.');
INSERT INTO "vn_mokhtasar" VALUES(11,119,'11:119','Ngoại trừ những ai được Allah thương xót mà phù hộ y đến với chỉ đạo. Tất nhiên họ không tranh luận nhau về thuyết Tawhid của Allah, vì thế mà họ đã bị thử thách về mặt tạo hóa là có người trong họ bị bất hạnh và có người được hạnh phúc. Và Lời Phán của Thượng Đế của Ngươi đã được thực hiện xong - hỡi Thiên Sứ - rằng Ngài phải nhét đầy của Hỏa Ngục là những ai ùa theo lũ Shaytan trong đó gồm có loài Jinn và loài Người.');
INSERT INTO "vn_mokhtasar" VALUES(11,120,'11:120','Và với tất cả những câu chuyện mà TA đã kể cho Ngươi nghe - hỡi Thiên Sứ - về các vị Sứ Giả là những nguồn tin thuộc thời trước Ngươi nhằm để củng cố cho con tim của Ngươi thêm vững chắc trên con đường Chính Đạo. Và qua Chương Kinh này mang đến cho Ngươi nguồn Chân Lý nên chẳng có gì phải nghi ngờ về Nó. Song Nó cũng mang đến lời cảnh tỉnh dành cho những kẻ vô đức tin và cũng là lời nhắc nhở dành cho những người có đức tin bởi lời nhắc nhở đó giúp ích được họ.');
INSERT INTO "vn_mokhtasar" VALUES(11,121,'11:121','Và hãy bảo - hỡi Thiên Sứ - với đám người không tin tưởng Allah và không chịu độc tôn Ngài: "Các ngươi hãy làm bất cứ thứ gì trên con đường mà các ngươi đã khước từ đi Chân Lý và luôn tìm cách ngăn chặn nó, còn bọn Ta sẽ làm những gì trên con đường kiên định của bọn Ta là kêu gọi đến với Ngài và sự kiên nhẫn với nó."');
INSERT INTO "vn_mokhtasar" VALUES(11,122,'11:122','"Và các ngươi hãy chờ coi những gì sẽ ban xuống cho bọn Ta. Quả thật, bọn Ta cũng đang chờ đợi coi điều gì sẽ gián lên các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(11,123,'11:123','Và chỉ có Allah Đấng Duy Nhất mới Am Tường được mọi điều gì bí ẩn nằm trong các tầng trời và sâu thẳm trong lòng đất, không có một điều vô hình nào có thể che giấu được Ngài, chỉ hướng về riêng Ngài duy nhất mà tất cả đến trình diện vào Ngày Phán Xét. Bởi thế, hãy thờ phượng Ngài - hỡi Thiên Sứ - vì Ngài là Đấng Độc Tôn, hãy phó thác tất cả mọi thứ cho Ngài, và chắc hẳn Thượng Đế của Ngươi sẽ không bao giờ làm lơ mọi thứ mà các ngươi đã làm. Không! Đúng hơn là Ngài thật am hiểu mọi điều đó và Ngài sẽ tưởng thưởng cho tất cả mọi việc làm đó.');
INSERT INTO "vn_mokhtasar" VALUES(12,1,'12:1','{Alif-Lam-Ra} tất cả đã được giải thích rõ ràng ở phần đầu của Chương Al-Baqarah. Với những câu Kinh đã truyền xuống thuộc một Chương Kinh của Thiên Kinh Qur''an rất rõ ràng qua lời phán của Nó.');
INSERT INTO "vn_mokhtasar" VALUES(12,2,'12:2','Chắc hẳn TA đã ban Thiên Kinh Qur''an xuống bằng ngôn ngữ Ả Rập để cho các ngươi - hỡi dân chúng Ả Rập - thấu hiểu được ý nghĩa của Nó.');
INSERT INTO "vn_mokhtasar" VALUES(12,3,'12:3','TA (Allah) kể cho Ngươi - hỡi Thiên Sứ - nghe về câu chuyện tuyệt về nhất về tính xác thực của nó, về cấu trúc câu và những phép tu từ trong nó thông qua những gì mà TA đã Mặc Khải cho Ngươi trong Thiên Kinh Qur''an. Quả thật, trước đây Ngươi chưa từng quan tâm đến những câu chuyện này và Ngươi cũng không hề biết gì về nó.');
INSERT INTO "vn_mokhtasar" VALUES(12,4,'12:4','TA sẽ kể cho Ngươi nghe - hỡi Thiên Sứ - khi Yusuf nói với cha mình là Y''aqub: "Dạ thưa cha! Quả thật, con nằm mộng thấy được mười một vị tinh tú và cả mặt trời lẫn mặt trăng, con thấy tất cả chúng điều phủ phục trước mặt con. Đây chính là giấc mộng báo điềm tốt cho Yusuf (cầu xin sự bình an cho Người) sắp được làm Sứ Giả."');
INSERT INTO "vn_mokhtasar" VALUES(12,5,'12:5','Ya''qub bảo với con trai Yusuf của Người: "Hỡi con trai yêu, con đừng bao giờ nhắc chuyện con nằm mộng thấy được những gì cho mấy người anh em của con biết. Bởi vì, cha sợ chúng nó sẽ ganh tỵ với con, thế rồi chúng sẽ bàn mưu tính kế hãm hại con. Quả thật, lũ Shaytan là một kẻ thù công khai của loài người."');
INSERT INTO "vn_mokhtasar" VALUES(12,6,'12:6','"Và với như giấc mộng mà con nhìn thấy là điềm báo rằng Thượng Đế đã chọn con - hỡi Yusuf - và rồi Ngài sẽ dạy con làm sao để giải thích uẩn khúc của giấc mộng và sẽ hoàn tất ân huệ của Ngài giành cho con khi lựa chọn con kế nghiệp Sứ Giả giống như các vị tổ tiên của con trước kia nhận được ân huệ của Ngài như: Tổ phụ Ibrahim, Ishaaq. Quả thật,Thượng Đế của con rất am tường trong sự tạo hóa của Ngài và Ngài sáng suốt trong sự sắp xếp của Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(12,7,'12:7','Chắc chắn, trong câu chuyện của Yusuf và những người anh em của Y đó là một minh chứng cho những ai luôn muốn tìm tòi những thông tin đến về họ.');
INSERT INTO "vn_mokhtasar" VALUES(12,8,'12:8','Khi những người anh em của Yusuf hội ý với nhau rồi bảo: "Rõ ràng Yusuf và em ruột của Nó được cha của chúng ta yêu thương nhiều hơn chúng ta trong khi đó chúng ta lại đông hơn, làm sao hai anh em của Nó hơn chúng ta cho được? Quả thật, chúng ta nhìn thấy cha tụi mình rỏ ràng là quá sai lầm khi xem hai anh em của nó tốt đẹp hơn tụi mình trong khi không có điều gì Nó hơn tụi mình cả.');
INSERT INTO "vn_mokhtasar" VALUES(12,9,'12:9','"Một là các anh hãy giết chết Yusuf, hai là các anh đem nó đi giấu ở một vùng đất nào đó thật là xa xôi, tới lúc đó các anh mới có thể nhận được hết sự ưu ái của cha giành hết cho các anh. Rồi sau đó là quẳng thời gian để các anh làm lại người tốt khi biết ăn năn sám với về tội giết chết Yusuf hoặc quẳng nó đi nơi khác mà các anh đã gây ra."');
INSERT INTO "vn_mokhtasar" VALUES(12,10,'12:10','Một người trong anh em của họ có ý kiến: "Không nên giết chết Yusuf làm gì. Tuy nhiên hãy ném Nó xuống một cái giếng khô rồi có thể có một số đoàn thương buôn đi ngang qua cứu vớt được Nó rồi họ sẽ mang Nó đi nơi khác. Cách này ít uy hại hơn là phải giết chết Nó, nếu như các anh hạ quyết tâm với những gì mà các anh đã nói.');
INSERT INTO "vn_mokhtasar" VALUES(12,11,'12:11','Và sau khi hội ý chúng nhất trí ném Yusuf đi xa, rồi chúng đến thưa với Ya''qud cha của bọn họ: "Dạ, thưa cha! Tại sao cha không tin tưởng tụi con có thể trông chừng được Yusuf ? Chắc chắn, tụi con sẽ trông chừng được em không cho bất cứ ai có thể làm hại được em, tụi con đảm bảo với cha sẽ bảo vệ và trông chừng em một cách thật an toàn để đưa trở về với cha. Vậy còn điều gì khiến cha không giao em cho tụi con vậy?');
INSERT INTO "vn_mokhtasar" VALUES(12,12,'12:12','Xin phép cha cho tụi con ngày mai đưa em đi cùng với tụi con ra ngoài để tụi con cùng nhau thưởng thức những món ăn và cùng vui đùa thỏa thích và chắc hẳn tụi con hứa là sẽ trông chừng em một cách cẩn thận không cho bất cứ thứ gì có thể xâm hại được em.');
INSERT INTO "vn_mokhtasar" VALUES(12,13,'12:13','Ya''qub bảo với các con của Y: "Quả thật, cha cảm thấy rất là lo lắng khi Nó đi cùng với các con. Bởi vì, cha không đủ kiên nhẫn khi Nó xa cha và cha cũng sợ khi Nó ra ngoài sẽ bị sói ăn thịt Nó trong lúc tụi con mải mê vui đùa không ngó ngàng đến Nó."');
INSERT INTO "vn_mokhtasar" VALUES(12,14,'12:14','Đám con của Y thưa: "Nếu bầy sói có ăn thịt Yusuf trong khi tụi con là một nhóm đông như thế này, lẽ nào tụi con toàn là kẻ vô dụng, lẽ nào tụi con không ngăn cản được bầy sói tấn công em ư?"');
INSERT INTO "vn_mokhtasar" VALUES(12,15,'12:15','Rồi cuối cùng Ya''qub đồng ý cho Yusuf đi cùng các anh. Khi họ dắt Y đến một nơi thật xa thì bọn họ nhất trí ném Y xuống dưới đáy giếng khô. Và ngay lúc đó TA (Allah) đã mặc khải xuống cho Yusuf: "Rồi đến một ngày, Ngươi sẽ báo cho họ biết về hành động này của họ, lúc đó họ không nhận biết Ngươi là ai."');
INSERT INTO "vn_mokhtasar" VALUES(12,16,'12:16','Và anh em của Yusuf trở về đến gặp người cha của họ vào khoảng thời gian trời tối và bọn chúng khóc lóc một cách thật thảm thiết để hoàn thành kế hoạch của họ.');
INSERT INTO "vn_mokhtasar" VALUES(12,17,'12:17','Bọn họ thưa: "Dạ! Thưa cha yêu của chúng con, tụi con đã rủ nhau chạy đua và mãi mê thi nhau bắn cung, chúng con đã bỏ Yusuf lại để trông coi các túi hành lý của chúng con. Vậy mà em đã bị sói ăn thịt mất rồi và tụi con thầm nghĩ chắc chắn rằng cha sẽ không tin đó là sự thật, mặc dầu chúng con rất thành thật với cha những gì mà đã xảy ra với tụi con."');
INSERT INTO "vn_mokhtasar" VALUES(12,18,'12:18','Và bọn họ tiếp tục kế hoạch của mình là trở về gặp cha với chiếc áo của Yusuf dính đầy máu mà thật chất máu đó không phải của Yusuf để làm chứng cớ quan trọng rằng Yusuf đã bị sói ăn thịt. Tuy nhiên, sau khi sự việc được trình bày thì Ya''qub hiểu ngay rằng - bọn chúng đang giở trò dối trá với Người bằng chứng là cái áo không bị rách và rồi Người bảo với chúng: "Sự việc diễn ra không như lời các con đã nói. Không, là điều đó chính tụi con đã toan tính trong đầu rồi lên kế hoạch xấu xa để rồi thực hiện đối với Yusuf. Thế nên, Ta giờ chỉ biết kiên nhẫn chịu đựng là giả pháp tốt đẹp nhất và cũng không phải tranh cải gì thêm nữa, Ta chỉ có Allah là Đấng đáng để Ta cầu xin được giúp đỡ về những gì mà tụi con đang xác nhận về cái chết của Yusuf."');
INSERT INTO "vn_mokhtasar" VALUES(12,19,'12:19','Và rồi có một đoàn lữ khách đi ngang qua rồi dừng chân tại giếng nước. Họ cử ra một người để múc nước, rồi khi người đó đến cái giếng để thả chiếc gàu xuống giếng múc nước thì Yusuf đã bám lấy sợ dây của chiếc gàu đó đi theo lên. Rồi khi, người đó kéo lên nhìn thấy được cậu bé thì hắn vui mừng reo lên: "Ôi có tin vui! Tôi đã kéo được một đứa trẻ." Và rồi một số người trong đoàn lữ khách đó đã giấu Y cùng với số hàng hóa để họ cùng mang ra mua bán. Và Allah luôn biết rõ họ đối xử ra sao với Yusuf vì mọi hành động của họ không giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(12,20,'12:20','Và đoàn lữ khách đã bán đứa trẻ cho những người ở xứ Ai Cập bằng một cái giá rẻ mạt, chỉ một vài đồng tiền bạc lẻ. Họ chỉ muốn bán đứa trẻ cho rồi vì họ lo sợ mọi người phát hiện đứa trẻ vốn không phải là nô lệ, họ sợ bị người trong gia đình mình phát hiện. Âu đây cũng là lòng thương xót mà Allah dành cho Yusuf khi không cho Yusuf ở với họ lâu hơn.');
INSERT INTO "vn_mokhtasar" VALUES(12,21,'12:21','Và người đàn ông đã mua Yusuf ở Ai Cập chính là đại quan, ông dắt cậu bé về nhà mà căn dặn vợ: "Nàng hãy quý mến và tôn trọng cậu ta ở trong nhà của chúng ta; hy vọng cậu ta sẽ mang lại điều tốt lành cho gia đình khi thay chúng ta làm một số việc hoặc mình có thể nhận cậu ta làm con nuôi. Và thế là TA đã giải cứu Yusuf khỏi cái chết và cũng cứu vớt Người ra khỏi giếng khô và Ngài còn khiến tấm lòng của đại quan nhân từ với Người. Thế là Ngài định cư Người sống tại đất nước Ai Cập, và để Ngài dạy Người cách giải thích giấc mộng, bởi Allah luôn chiến thắng trong công việc của Ngài, lệnh của Ngài sẽ thực hiện mà không ai cản được thế nhưng hầu hết cả thiên hạ - họ là đám người vô đức tin - đều không hay biết.');
INSERT INTO "vn_mokhtasar" VALUES(12,22,'12:22','Và đến khi Yusuf trưởng thành là một người thanh niên cường tráng thì TA ban cho Y một đầu óc thông thái và kiến thức uyên bác. Và Y được sự ưu ái như vậy giống như TA ưu ái cho những ai hoàn thành tốt nhiệm vụ được giao phó cho đám bề tôi của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(12,23,'12:23','Và vợ của đại quan, tức nữ chủ của ngôi nhà mà Yusuf đang tá túc đã tìm cách quyến rủ Yusuf (cầu xin sự bình an cho Người) bằng một kế hoạch được xếp đặt sẵn, bà ta đóng chặt hết các cửa lại lúc bà và Yusuf ở cùng phòng, bà ta mời mọc: "Hỡi chàng, hãy đến đây với ta!" Yusuf đáp lại: "Cầu xin Allah hãy che chở bề tôi tránh khỏi lời mời mọi của bà ta, quả thật vị đại quan là chủ nhân của tôi, ngài đã ban cho tôi một nơi tá túc tốt đẹp khi được ở gần ngài, bởi vậy làm sao tôi có thể phản bội ngài ấy cho được. Nếu phản bội tôi là kẻ sai quấy và kẻ sai quấy sẽ không bao giờ thành đạt.');
INSERT INTO "vn_mokhtasar" VALUES(12,24,'12:24','Và dục vọng trong bà ta nổi lên khiến cho bà ta giở trò tà dâm, hiểm họa lúc này đang rình rập Yusuf khi con tim của Y muốn đáp lại lời nữ ta, tuy nhiên nếu như Y không nhìn thấy được các bằng chứng của Allah thì Y cũng đã cùng nữ làm trò tà dâm mất rồi. Trước đây TA đã chỉ cho Y thấy được những điều đó để kéo Y ra khỏi mọi hành động xấu xa, bẩn thiểu và phản bội và quả thật, Yusuf là một bề tôi của TA được lựa chọn để làm Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(12,25,'12:25','Yusuf lặp tức chạy ra cửa để thoát thân, còn vợ đại quan thì đuổi theo cản lại và nữ ta cố sức níu kéo vạt áo sau của Yusuf làm nó bị rách đi. Khi vừa đến cửa thì cả hai người chợt nhìn thấy chồng của nữ ta đứng ngay tại cửa. Nữ ta lớn tiếng la lên nói người chồng: Sẽ không còn hình phạt nào dành cho kẻ dở trò đồi bại với vợ của ngài - hỡi đại quan - ngoài việc phải giam cầm y hoặc sẽ dùng cực hình với y.');
INSERT INTO "vn_mokhtasar" VALUES(12,26,'12:26','Yusuf (cầu xin sự bình an cho Người) trình bày: "Chính bà đại quan đã khêu gợi để mê hoặc tôi và tôi đã khước từ bà ta". Bỗng lúc đó Allah khiến cho đứa bé vẫn còn nằm nôi trong gia đình bà lên tiếng: "Quả thật, nếu như áo của Yusuf bị rách ở phía trước thì đó là bằng chứng là bà ta nói thật. Bởi vì, bà ta đã ngăn cản Y để tự vệ cho bản thân mình, thế nên Y là kẻ nói dốc.');
INSERT INTO "vn_mokhtasar" VALUES(12,27,'12:27','"Còn ngược lại, nếu như áo của Y bị rách ở phía sau thì đó là chứng cớ rằng Y là người nói thật; bởi vì, bà ta đã nếu kéo Y nên Y bỏ chạy ra khỏi bà ta, thế nên bà ta là người nói dốc.');
INSERT INTO "vn_mokhtasar" VALUES(12,28,'12:28','Bởi vậy, khi đại quan nhìn thấy áo của Yusuf (cầu xin sự bình an cho Người) bị rách ở phía sau ông ta liền cho là Yusuf nói thật, rồi ông bảo: "Chắc hẳn đây chỉ là một âm mưu để vu khống cho Yusuf đây mà, nó nằm trong của các nàng đây - hỡi các quí bà - quả thật, mưu kế của các quí bà rất cay nghiệt."');
INSERT INTO "vn_mokhtasar" VALUES(12,29,'12:29','Và đại quan bảo Yusuf: "Hỡi Yusuf! Thôi con hãy bỏ qua cho những lời vu oan này và chớ kể lại cho một ai biết hết, còn nàng hãy tự hối cải về tội lỗi đã gây ra khi nàng quyến rũ Yusuf.');
INSERT INTO "vn_mokhtasar" VALUES(12,30,'12:30','Và tin tức của vợ đại quan được lan truyền khắp cả thành phố, làm một số phụ nữ thì thầm bảo nhau: "Bà vợ của một vị đại quan đã cố ý khêu gợi cám dỗ một người hầu nam của bà để làm chuyện đồi bại. Quả thật, người hầu nam đó đã làm cho con tim bà ta điên đảo mất rồi, bọn mình thấy rằng vì thương yêu mù quáng nó đã khiến bà ta lệch lạc thật rồi."');
INSERT INTO "vn_mokhtasar" VALUES(12,31,'12:31','Sau khi vợ của đại quan nghe được lời của những người phụ nữ đàm tiếu về mình, thấy vậy bà ta cho mời tất cả những người phụ nữ đó đến để cùng nhau ngắm nhìn diện mạo Yusuf trong một buổi tuyệt sang trọng đã được bà ta chuẩn bị sẵn với miếng lót ngồi cùng một cái gối dựa lưng kèm theo cho mỗi quý bà một con dao gọt hoa quả để dùng cắt trái cây. Xong thì bà ta gọi Yusuf (cầu xin sự bình an cho Người): "Nào hãy bước ra để trình diện các quý bà." Khi các quý bà nhìn thấy Yusuf khiến họ ngạc nhiên đến ngơ ngác trước vẽ đẹp khôi ngô tuấn tú của Yusuf, điều đó khiến các bà tự cắt vào tay mình thật sâu nhưng không hề hay biết và các bà thốt lên: "Ôi Allah thật nhiệm mầu! Chàng thanh niên này không phải là người phàm, con người thì làm sao đẹp đến thế cơ chứ, chắc hẳn đây chính là Thiên Thần rồi.');
INSERT INTO "vn_mokhtasar" VALUES(12,32,'12:32','Vợ đại quan liền bảo với các bà sau khi mọi việc đã xảy ra: "Đây là chàng thanh niên mà đã làm bản thân tôi đem lòng yêu say đấm. Quả thật, tôi đã từng quyến rủ, cám dỗ nó nhưng nó đã thẳng thừ từ chối tình yêu đó của tôi và còn biết giữ mình kiềm chế lại dục vọng của bản thân mình. Kể từ giờ này trở đi, nếu nó không làm theo mọi chỉ thị của tôi thì hẳn nó phải vào tù và sẽ trở thành một kẻ thấp hèn."');
INSERT INTO "vn_mokhtasar" VALUES(12,33,'12:33','Yusuf (cầu xin sự bình an của Người) cầu xin Thượng đế của Người: "Lạy Thượng Đế của bề tôi! Chốn lao tù mà vợ đại quan hù dọa bề tôi được bề tôi thích hơn là phải đối mặt với điều thô bỉ mà họ kêu gọi. Và nếu Ngài giải thoát bề tôi khỏi âm mưu của các bà thì bề tôi sẽ trở thành một kẻ ngu muội mất."');
INSERT INTO "vn_mokhtasar" VALUES(12,34,'12:34','Lời thình cầu được Allah đã đáp lại, Ngài đã cứu Yusuf khỏi mọi âm mưu của vợ đại quan và những phụ nữ của thành phố. Quả thật, Đấng Toàn Năng nghe thấu lời cầu xin của Yusuf, cũng như lời cầu xin của những ai khẩn cần Ngài và Ngài m tường hoàn cảnh của Người cũng như mọi thứ khác.');
INSERT INTO "vn_mokhtasar" VALUES(12,35,'12:35','Vậy mà sau khi vị đại quan cùng tập thể của ông chứng kiến những bằng chứng vô Yusuf vẫn giam cầm Người vào tù - để không còn tiết lộ ra sự việc khả ố này ra ngoài - với khoảng thời hạn không giới hạn.');
INSERT INTO "vn_mokhtasar" VALUES(12,36,'12:36','Thế là Yusuf tống giam vào chốn lao tù, cùng lúc đó có hai người thanh niên cũng bị tông giam cùng Người, một trong hai thanh niên đó nói với Yusuf rằng: "Quả thật, tôi đã nhìn thấy trong giấc mộng mình đang vắt nho để làm rượu", người thứ hai nói: "Còn tôi thì nằm mộng nhìn thấy mình đội bánh mì trên đầu đang bị một đàn chim mổ ăn. Xin anh hãy giải mộng đó giùm cho chúng tôi - hỡi Yusuf - bản thân chúng tôi nhận xét thấy anh quả là một trong những người hiền lương.');
INSERT INTO "vn_mokhtasar" VALUES(12,37,'12:37','Yusuf (cầu xin sự bình an cho Người) bảo: "Trước khi thức ăn của vua hoặc của ai đó mang đến cho hai bạn Ta sẽ giải thích sự thật về điều bí ẩn cụ thể như thế nào với hai bạn biết. Cách giải mộng này của Ta là do Thượng Đế của Ta đã chỉ dạy Ta, chứ Ta không phải là một tên thầy bói hay là một nhà chiêm tinh gì cả. Thật vậy, Ta đã từ bỏ đi tôn giáo của những kẻ không tin tưởng Allah và họ cũng chẳng tin tưởng vào Ngày Sau.');
INSERT INTO "vn_mokhtasar" VALUES(12,38,'12:38','Và hiện nay Ta đang đi trên tôn giáo của tổ tiên của Ta: Ibrahim, Ishaq và Y''aqub, đó là tôn giáo độc tôn Allah trong thờ phượng và Ta không được phép tổ hợp bất cứ thứ gì ngang hàng với Allah. Ngài là Đấng Duy Nhất cho sự Độc Tôn của Ngài và đấy là thuyết Tawhid và đó chính là đức tin mà Ta lẫn cả tổ tiên của Ta đã tin. Chính Allah đã phù hộ và ban cho Ta như thế và đó cũng là ân huệ dành cho Ta, cho tổ tiên của Ta và cả nhân loại khi Ngài gởi đến họ những vị Thiên Sứ của Ngài. Thế nhưng, đa số thiên hạ không hề biết tạ ơn Allah về ân huệ đó, ngược lại bọn họ vong ơn Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(12,39,'12:39','Sau đó, Yusuf bắt đầu đặt câu hỏi với hai người bạn cùng lao tù với mình: "Phải chăng, việc thờ phượng nhiều thần linh khác nhau sẽ tốt hơn hay chỉ thờ phượng Allah Duy Nhất không có bất cứ chia sẽ với Ngài, Đấng mà không ai có thể chiến thắng được Ngài, Đấng bất khả chiến bại."');
INSERT INTO "vn_mokhtasar" VALUES(12,40,'12:40','"Những vật thể hay thần linh mà các anh đang tôn thờ thay vì thờ Allah, chẳng qua là những cái tên mà các anh và tổ tiên của các anh gán lên cho nó mà thôi, nó không xứng đáng là thần linh cho các anh gán ghép, Allah cũng không ban xuống cho chúng bất lời xác nhận nào để chứng minh rằng đó là sự thật, tất cả mệnh lệnh đều thuộc quyền của Allah duy nhất, chứ không phải là những cái tên do các ngươi hay tổ tiên của các ngươi tự ý gán lên nó. Allah Toàn Năng đã ra lệnh các anh phải thờ phượng Ngài và nghiêm cấm các anh tổ hợp một ai đó ngang hàng với Ngài. Đấy là thuyết Tawhid cũng là một tôn giáo ngay thẳng mà không có sự uẩn khúc gì trong đó. Thế nhưng, đa số thiên hạ không hề hay biết gì về điều đó mà ngược lại họ còn tổ hợp một ai đó ngang hàng với Allah, khi tôn thờ một số tạo vật của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(12,41,'12:41','"Này hỡi hai người bạn lao tù của Ta, đối với người nằm mộng thấy mình đang vắt nho chế biến rượu thì sẽ được ra khỏi chốn lao tù này mà đến với công việc hầu hạ rót rượu cho vua chúa và người còn lại nằm mộng nhìn thấy trên đầu mình đội bánh mì bị bầy chim mổ ăn thì y sẽ bị giết chết rồi đóng đinh trên thập tự giá và bị bầy chim mổ trên đầu ăn từng miếng thịt. Vấn đề nan giải mà hai anh yêu cầu Ta làm sáng tỏ nó thì nay đã được Ta phơi bài ra hết. Mọi chuyện đã được an bài như thế không thể nào thay đổi được."');
INSERT INTO "vn_mokhtasar" VALUES(12,42,'12:42','Và Yusuf bảo với một trong hai người bạn lao tù mà Người nghĩ người đó sẽ được ra tù - đó là người hầu hạ rót rượu cho vua chúa -: "Anh hãy nhắc câu chuyện của Ta và những nỗi oan ức của Ta trước chúa của anh nhé!" Hi vọng đức vua có thể giải oan cho Ta để Ta ra khỏi chốn lao tù này. Thế nhưng, lũ Shaytan đã làm cho người hầu đó quên nhắc tên Yusuf trước vua của y. Bởi thế, Yusuf đành phải ở lại nơi chốn lao tù thêm vài năm nữa.');
INSERT INTO "vn_mokhtasar" VALUES(12,43,'12:43','Đức vua bảo với các quần thần: "Quả thật, trong mộng trẫm đã nhìn thấy bảy con bò mập bị bảy con bò ốm ăn thịt và trẫm nhìn thấy bảy bông lúa xanh tươi và bảy bông lúa héo vàng. Này hỡi các hiền sĩ và các chư khanh! Ai có thể giải thích được giấc mộng của trẫm đã nhìn thấy nếu như các khanh hiểu biết cách giải mộng mà trẫm đã nhìn thấy."');
INSERT INTO "vn_mokhtasar" VALUES(12,44,'12:44','Các quan đại thần tâu: "Giấc mộng của bệ hạ chứa nhiều uẩn khúc lẫn lộn, đúng ra là giấc mộng không thể như thế nên nó không có lời giải và chúng thần không tài nào giải được giấc mộng lộn xộn đầy huyền bí như thế."');
INSERT INTO "vn_mokhtasar" VALUES(12,45,'12:45','Và người hầu rượu mà trước kia là một trong hai người bạn được thả ra ở chốn lao tù liền nhớ lại Yusuf (cầu xin sự bình an cho Người) là Người hiểu biết cách giải được giấc mộng mà bấy lâu nay đã quên lững y liền bẩm: "Nô tài sẽ hóa giải thích cho bệ hạ về giấc mộng của bệ hạ sau khi nô tài đi nhờ vả một người biết cách hóa giải giấc mộng ấy. Bởi thế, nô tài xin bệ hạ hãy cử nô tài đi đến gặp Yusuf để hóa giải giấc mộng đó cho bệ hạ."');
INSERT INTO "vn_mokhtasar" VALUES(12,46,'12:46','Ngay khi được chuẩn tấu y liền vội vã đến gặp Yusuf rồi bảo: "Hỡi Yusuf, hỡi người chân thật! Anh hãy hóa giải giùm cho chúng tôi về giấc mộng mà đức vua đã nhìn thấy bảy con bò ốm nuốt chửng bảy con bò mập và bảy hạt lúa xanh tươi với bảy bông lúa héo vàng; hi vọng tôi trở về bẩm tấu với đức vua và các quan thần cạnh vua, có lẽ họ sẽ nhận biết được sự tài giỏi và vị trí của anh.');
INSERT INTO "vn_mokhtasar" VALUES(12,47,'12:47','Yusuf (cầu xin sự bình an cho Người) hóa giải giấc mộng và bảo: "Các ngươi hãy cố gắng mà siêng năng trồng trọt suốt bảy năm liền, sau mỗi mùa thu hoạch của bảy năm rồng rã đó hãy dữ trữ nguyên vỏ cho cẩn thận đừng để nó bị hao hụt ngoại trừ một số ít các ngươi cần thiết để ăn.');
INSERT INTO "vn_mokhtasar" VALUES(12,48,'12:48','"Rồi sau bảy năm đất đai phì nhiêu cho các ngươi trồng trọt, tiếp đó sẽ xảy ra liên tiếp bảy năm hạn hán hoành hành, thiên hạ sẽ ăn tất cả số lương thực mà các ngươi đã thu hoạch trong những năm mà đất đai còn phì nhiêu ngoại trừ một số các ngươi biết gìn giữ cẩn thận để làm hạt giống."');
INSERT INTO "vn_mokhtasar" VALUES(12,49,'12:49','"Rồi sau, khoảng thời gian nhiều năm bị hạn hán đói khổ thì sẽ có một năm thiên hạ được trút xuống những trận mưa dồi dào làm cho đất đai phì nhiêu mùa màng bội thu đến mức mọi người cần phải ép những trái cây như nho, ô liu, mía để dự trữ."');
INSERT INTO "vn_mokhtasar" VALUES(12,50,'12:50','Và nhà vua ra khẩu dụ cho các chư thần sau khi nghe được Yusuf hóa giải uẩn khúc trong giấc mộng của mình: "Các khanh hãy đem Y ra khỏi chốn lao tù đến đây để gặp trẫm." Nhưng khi sứ thần của nhà vua đến gặp Yusuf truyền khẩu dụ của vua thì Yusuf bảo: "Anh hãy về tâu lại với đức vua của anh và hỏi ngài sẽ xử lý ra sao về chuyện mà các quý bà đã cắt tay của mình. Khi nào sự việc đó được làm sáng tỏ trả lại nỗi oan cho Ta thì Ta mới ra khỏi chốn lao tù này. Quả thật, Thượng Đế của Ta hằng biết hết mọi âm mưu của các bà ta vì không có bất cứ thứ gì che giấu được Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(12,51,'12:51','Nhà vua thẩm vấn các người phụ nữ: "Sự tình của các bà như thế nào khi các bà lên kế hoạch quyến rủ Yusuf để làm những trò tà dâm với các bà?" Bà vợ của đại quan thú tội cho những hành động sai trái của mình đã làm: "Giờ đây sự thật đã được phơi bày, tiện thiếp đã từng có hành động lẳng lơ để quyến rủ cậu ta nhưng bị cậu ta thẳng thừ từ chối. Quả thật, cậu ta là một trong những người chân thật, cậu ta hoàn toàn vô can với mọi lời vu khống trước đây."');
INSERT INTO "vn_mokhtasar" VALUES(12,52,'12:52','Bà vợ của đại quan giải bài: "Để cho chồng của tiện thiếp biết giữa thiếp và Yusuf không đi quá xa, bởi cậu ta đã biết giữ mình dẫu cho thiếp có quyến rủ và để chồng tiện thiếp biết là thiếp đã không hề phản bội ông lúc ông đi vắng. Sau sự việc như thế này cho thiếp biết là Allah không bao giờ ban thành công cho những ai lừa dối, xảo nguyệt."');
INSERT INTO "vn_mokhtasar" VALUES(12,53,'12:53','Và người phụ nữ tiếp tục những lời nói của bà ta: Và tôi đã không bảo bản thân mình tránh khỏi ý muốn xấu, và tôi không nói thế để đề cao bản thân mình; quả thực bản chất dục vọng của con người là thích hướng đến sự thỏa mãn trong những việc xấu xa và rất khó để ngăn chặn nó, trừ khi người đó được Allah thương xót, và che chở khỏi những điều xấu xa, quả thực Thượng đế của tôi là Đấng Hằng Tha Thứ cho những ai biết xám hối trong việc thờ phượng Ngài, Ngài nhân từ với họ');
INSERT INTO "vn_mokhtasar" VALUES(12,54,'12:54','Rồi vua hạ lênh với các cận thần ngay khi biết rõ sự vô tội của Yusuf: Các khanh hãy đưa Yusuf đến đây để Y làm việc riêng cho trẫm. Sau khi họ đưa Người đến thì Người tâu với nhà vua, cho đức vua thấy rõ kiến thức và trí tuệ của mình thì nhà vua ra lệnh: Quả thật, Khanh - hỡi Yusuf - trẫm khẳng định rằng ngày hôm nay khanh đã trở thành người có địa vị và được sự tin tưởng nơi trẫm');
INSERT INTO "vn_mokhtasar" VALUES(12,55,'12:55','Yusuf tâu với đức vua: Xin bệ hạ cho thần giữ kho bạc và lương thực của vùng đất Ai cập, vì hạ thần là một thủ quỹ uy tín, có kiến thức và tầm nhìn sâu sắc về những gì được giao');
INSERT INTO "vn_mokhtasar" VALUES(12,56,'12:56','Và tương tự như việc đã ban thanh danh cho Yusuf và giải thoát Ngươi khỏi nhà giam thì TA đa ban chức quyền ở Ai cập, Người có thể đi lại mọi nơi Người muốn, cũng như TA trao lòng thương sót của TA cho những ai mà TA muốn ở trên trần gian, và không làm mất phần thưởng của những người tốt, Ta còn trao cho họ trọn vẹn không thiếu bớt một chút nào');
INSERT INTO "vn_mokhtasar" VALUES(12,57,'12:57','Và chắc chắn phần thưởng mà Allah đã chuẩn bị ban vào Đời Sau tốt hơn phần thưởng của trần gian giành cho những ai tin tưởng vào Allah cũng như họ đã tuân theo những mệnh lệnh của Ngài và tránh xa những gì Ngài ngăn cấm');
INSERT INTO "vn_mokhtasar" VALUES(12,58,'12:58','Vì cần sự giúp đỡ nên những người anh của Yusuf đến Ai cập. Rồi khi họ đối diện với Người thì Người liền nhận ra họ là những người anh của mình, nhưng họ không nhận ra Người là em của họ; vì xa cách quá lâu và ngoại hình của Người đã thay đổi; vì lúc đó Người chỉ là đứa trẻ khi bị họ ném xuống giếng');
INSERT INTO "vn_mokhtasar" VALUES(12,59,'12:59','Và khi lương thực và thực phẩm được trao theo yêu cầu của họ, họ cho Người biết về người em cùng cha mà họ đã để lại với Cha: Người bảo: Các anh hãy đưa em các ngươi đến đây Ta sẽ thêm cho các bằng sức chở của con lạc đà, các ngươi không thấy Ta đã đong đầy lương thực không hề thiếu hụt, Ta thuộc những người rất quí mến khách');
INSERT INTO "vn_mokhtasar" VALUES(12,60,'12:60','Nếu các anh không đưa em trai của các anh đến đây thì chứng tỏ các anh nói dối về người em cùng cha đó, vậy thì Ta sẽ không lương thực cho các anh, và các anh không được đến gần đất nước của Ta');
INSERT INTO "vn_mokhtasar" VALUES(12,61,'12:61','Rồi các anh đáp lời, nói: Chúng tôi sẽ cầu xin Cha nó, chúng tôi sẽ cố gắng hết sức, và chắc chắn chúng tôi sẽ làm được điều mà ngài yêu để ngài không thất vọng');
INSERT INTO "vn_mokhtasar" VALUES(12,62,'12:62','Và Yusuf bảo những người giúp việc: Các ngươi hãy đặt tiền của họ vào hành lý của họ để khi trở về họ thấy và buộc họ phải trở lại lần hai với người em của mình. Điều đó để họ tin tưởng rằng Yusuf đã tin lời nói của họ là thật và chịu chấp nhận tiền của họ.');
INSERT INTO "vn_mokhtasar" VALUES(12,63,'12:63','Đến khi trở về gặp Cha, họ đã kể lại về sự rộng lượng của Yusuf, họ nói: Thưa Cha! Chúng ta sẽ không được số lương thực đó nếu chúng ta không đưa em trai đến đó, nếu Cha cho phép em đi theo các con chúng ta sẽ được đủ số lương thực, và tụi con cam đoan với Cha sẽ trông coi và đưa em ấy trở về bình an');
INSERT INTO "vn_mokhtasar" VALUES(12,64,'12:64','Cha họ bảo: Chẳng phải Cha đã tin tưởng mà giao nó cho các con giống như Cha đã từng tin tưởng và giao người anh ruột của nó: Yusuf trước đó ư?! Quả thật, Cha đã tin tưởng các con, và các con đã hứa là sẽ bảo vệ nó, rồi các con lại không thực hiện được lời hứa, Cha không còn niềm tin vào các con nữa, và Ta chỉ thật sự tin nơi Allah, vì Ngài là Đấng trông nom tốt nhất cho ai muốn được chăm nom, và Khoan dung cho những ai cần khoan dung');
INSERT INTO "vn_mokhtasar" VALUES(12,65,'12:65','Và khi mở túi đựng lương thực họ đem theo thì họ thấy số tiền được trả lại, rồi họ nói với Cha: Đây chả phải là sự rộng rãi của ngài ấy khi chúng ta yêu cầu hay sao? và đây là tiền mua lương thực của chúng ta ngài ấy đã ân sủng và trả lại cho chúng ta, chúng con sẽ mang lương thực về cho gia đình, và chúng con sẽ trông coi em trai mình cận thận, rồi chúng ta sẽ có thêm lương thực từ sự sắp xếp khiên vác cho lạc đà, đó là điều dễ dàng đối với ngài thủ kho');
INSERT INTO "vn_mokhtasar" VALUES(12,66,'12:66','Cha của họ bảo: Cha sẽ không gửi nó với các con trừ khi các con cho Cha một lời thề xưng danh Allah chắc chắn sẽ đưa nó về lại với Cha ngoại trừ tất cả các con bị vây hãm giết chết, không còn ai sống sót, hoặc không còn khả năng chống cự và không thể quay về, rồi khi họ xưng danh Allah với lời thề chắc chắn. Người Cha nói: Allah chứng giám những gì chúng ta cam kết, và Ngài giữ trọn vẹn lời hứa của chúng ta');
INSERT INTO "vn_mokhtasar" VALUES(12,67,'12:67','Rồi Cha dặn dò những người con: Tất cả các con chớ vào thành Ai Cập bằng một cửa duy nhất, mà các con hãy tách ra vào những cánh cửa khác nhau, có thế sẽ an toàn hơn nếu như ai đó có ý định làm hại các con, và Cha không nói là Cha bảo vệ được các con khi Allah đã muốn các con gặp phải điều xấu, và Cha cũng không thể mang lại điều tốt nào cho các con khi Allah không muốn, vì không có quyền quyết định ngoại trừ quyền quyết định của Allah, và không có mệnh lệnh nào ngoại trừ mệnh lệnh của Ngài, chỉ duy nhất mình Ngài Cha phó thác mọi việc, và chỉ duy nhất mình Ngài những người cần phó thác hãy phó thác mọi vấn đề cho Ngài');
INSERT INTO "vn_mokhtasar" VALUES(12,68,'12:68','Họ đưa người em ruột đi cùng và họ vào thành bằng nhiều cánh cửa riêng biệt như Cha dặn dò nhưng điều đó không giúp được gì cho họ khi Allah đã an bài mọi việc, quả thật lòng thương xót của Y''aqub đối với các con đã lộ rõ, và Người đã khuyên bảo họ, vì Người biết không có sự sắp đặt nào ngoài sự sắp đặt của Allah, và Người biết rõ về những gì TA đã chỉ dạy về đức tin nơi số phận và nguyên do nhưng đa số con người không biết điều đó');
INSERT INTO "vn_mokhtasar" VALUES(12,69,'12:69','Và khi những người anh của Yusuf vào trình diện Yusuf, đi với họ là người em ruột của Người, Người ôm chầm lấy người em, rồi nói nhỏ với em: Quả thật, Ta là anh ruột của em đây: Yusuf, em đừng buồn với những việc làm sai trái của họ, cũng đừng hận thù về việc họ đã đém anh xuống giếng');
INSERT INTO "vn_mokhtasar" VALUES(12,70,'12:70','Sau khi Yusuf ra lệnh người giúp việc khi đem lương thực đặt lên lưng lạc đà hãy giấu lít đong lương thực của vua vào túi người em ruột mình lúc họ không hề hay biết với ý định giữ chân người em ở lại, và khi họ bắt đầu khởi hành quay về thì có người truy hô lên: Này hỡi đoàn thương buôn, quả thật các ngươi là những người ăn cắp');
INSERT INTO "vn_mokhtasar" VALUES(12,71,'12:71','Những người anh của Yusuf nói sau khi họ quay về phía người vừa truy hô và những người ở đó: Các ngài đã mất gì mà lại vu cáo chúng tôi là kẻ trộm');
INSERT INTO "vn_mokhtasar" VALUES(12,72,'12:72','Họ đáp lại những người anh của Yusuf: Chúng tôi bị mất một dụng cụ đo lường của nhà vua dùng nó đong lương thực, ai trả lại chiếc cốc của vua trước khi lệnh lục soát được thực thi, người đó được thưởng một lượng lương thực bằng sức chở một con lạc đà, và Ta đảm bảo cho điều đó');
INSERT INTO "vn_mokhtasar" VALUES(12,73,'12:73','Những người anh của Yusuf đáp lại với họ: Thề với Allah, chắc chắn như ngài đã biết chúng tôi thiệt thà và vô tội, cũng như ngài thấy tình trạng của chúng tôi, chúng tôi đến mảnh đất Ai Cập không phải để làm điều xấu xa, và trong đời chúng tôi chưa từng trộm cắp');
INSERT INTO "vn_mokhtasar" VALUES(12,74,'12:74','Những người làm của Yusuf bảo: Vậy hình phạt là gì nếu một trong các ngươi trộm nó khi các ngươi nói dối rằng các ngươi vô tội ?');
INSERT INTO "vn_mokhtasar" VALUES(12,75,'12:75','Những người anh Yusuf đáp lại họ: Hình phạt cho kẻ trộm trong chúng tôi là nếu tìm thấy tang vật trong túi của ai thì người đó sẽ được giao cho người bị mất cắp xử lý, hình phạt dành cho kẻ trộm của chúng tôi là như vậy');
INSERT INTO "vn_mokhtasar" VALUES(12,76,'12:76','Họ đưa những cái túi cho Yusuf lục xoát, rồi Yusuf bắt đầu lục xoát những cái túi của những người anh trước khi xét người em ruột, khi xét đến túi người em, thì Người lấy tang vật ra, như kế hoạch của Yusuf khi khéo léo bỏ dụng cụ đong lường vào túi hành lý của em mình, kế hoạch của Người hướng đến vấn đề khác khi Người giữ những người anh em lại bằng lý do ăn trộm, điều đó sẽ không đạt được vì hình phạt dành cho kẻ trộm là bị đánh và phạt tiền, trừ khi Allah muốn sắp xếp nó khác đi vì Ngài có khả năng về điều đó, Ngài nâng cấp bậc cho ai Ngài muốn trong đám nô lệ của Ngài như Ngài đã nâng cấp bậc của Yusuf vậy, và trên tất cả những người có kiến thức Ngài là Đấng Am Tường và bên trên tất cả mọi kiến thức và trí tuệ.');
INSERT INTO "vn_mokhtasar" VALUES(12,77,'12:77','Những người anh của Yusuf thưa: Nếu nó ăn cắp thì không có gì ngạc nhiên, vì anh ruột của nó cũng từng ăn cắp trước đó, họ muốn nói đến Yusuf {cầu xin sự bình an đến Người}, nhưng Yusuf đã giấu kín điều xấu xa mà họ đã nói về mình, Ngươi không để lộ ra ngoài và Người thầm nói: Chỉ vì lòng ganh tỵ đã đẩy các anh làm điều tồi tệ với em như vậy, ở đây đó chính là tội ác, và Allah Tối Cao biết rõ điều vu khống mà các anh đã phun');
INSERT INTO "vn_mokhtasar" VALUES(12,78,'12:78','Các anh của Yusuf nói với Yusuf: Thưa ngài, quả thực em ấy còn một Cha rất già ông ấy rất thương nó, ngài hãy bắt một trong chúng tôi để thay thế nó, quả thực chúng tôi thấy ngài đối đãi tốt với chúng tôi và với mọi người, và như vậy là tốt nhất cho chúng tôi');
INSERT INTO "vn_mokhtasar" VALUES(12,79,'12:79','Yusuf {cầu xin sự bình an đến Người}: Cầu xin Allah che chở tránh khỏi hành động bất công đó, Ta chỉ bắt giữ người mà Ta tìm ra dụng cụ đo lường của vua trong túi hành lý của hắn mà thôi, nếu làm ngược lại thì Ta chính là kẻ bất công, đó là phạt người vô tội và tha cho kẻ có tội');
INSERT INTO "vn_mokhtasar" VALUES(12,80,'12:80','Khi thấy hết hy vọng từ lời đáp của Yusuf thì họ họp nhau lại để bàn bạc, người lớn nhất trong nhóm nói: Các em hãy nhớ rằng quả thật Cha các em đã lập lời thề trước Allah về việc đưa con ông ấy trở về trừ khi các em bị vây hãm không còn khả năng trốn thoát, và trước đó các em đã không làm tròn trách nhiễm đối với Yusuf, các em đã không hoàn thành giao ước với Cha, anh sẽ không rời khỏi mảnh đất Ai Cập cho đến khi Cha cho phép, hoặc Allah xét xử cho anh việc đã làm mất đứa em, vì Ngài là Đấng xét xử tốt nhất, Ngài xét xử một cách chính trực và công bằng');
INSERT INTO "vn_mokhtasar" VALUES(12,81,'12:81','Và người anh lớn nói: Các em hãy trở về gặp Cha mà thưa với Cha: Quả thật con trai của Cha đã ăn cắp, thế là nó đã bị ngài đại quan Ai Cập bắt giữ, chúng con chỉ nói những gì chúng con biết và tận mắt chứng kiến dụng cụ đong đo được lấy ra từ trong hành lý của nó, và chúng con không lường trước là nó sẽ ăn cắp, nếu không tụi con đã không hứa với Cha việc đưa nó trở về');
INSERT INTO "vn_mokhtasar" VALUES(12,82,'12:82','Và để xác nhận tín trung thực Cha hãy hỏi người dân Ai Cập nơi mà tụi con đã ghé, và Cha hãy hỏi những đoàn thương buôn mà họ trở về cùng tụi con họ sẽ trả lời như những gì tụi con cho Cha biết, và chắc chắn tụi con là những người thành thật với những gì tụi con đã nói với Cha về việc trộm cắp của em');
INSERT INTO "vn_mokhtasar" VALUES(12,83,'12:83','Cha đáp lại họ: Nó không hề ăn cắp như những gì các con kể, mà tự bản thân các con đã thiêu dệt nên điều đó vì các con ghét nó như các con từng ghét anh nó Yusuf trước đây, chỉ có kiên nhẫn mới là giải pháp tốt, không có sự than phiền nào ở đây ngoại trừ nơi Allah, biết đâu Allah sẽ đưa tất cả chúng nó về lại với Cha: Yusuf, em ruột nó và cả người anh của chúng, quả thật Ngài biết rõ tình trạng của Cha, Ngài sáng suốt trong việc sắp đặt mọi việc');
INSERT INTO "vn_mokhtasar" VALUES(12,84,'12:84','Và Cha rời khỏi các con mà than: Ta thực sự buồn làm sao cho Yusuf, và đôi mắt đen huyền đã trở nên trắng xóa do khóc quá nhiều, vì nó bị lấp đầy bởi nổi buồn và đau khổ, Người đã kiềm nén nỗi buồn của mình');
INSERT INTO "vn_mokhtasar" VALUES(12,85,'12:85','Những người anh Yusuf nói với Cha: Thề bởi Allah, Cha cứ không ngừng thương nhớ Yusuf, và khóc than như vậy Cha sẽ ngã bệnh, hoặc lâm chung đấy');
INSERT INTO "vn_mokhtasar" VALUES(12,86,'12:86','Rồi Cha đáp lại các con: Cha chỉ biết than thở nỗi u sầu và buồn phiền với riêng một mình Allah thôi, vì Cha biết sự tử tế của Allah và lòng tốt của Ngài, và Ngài đáp lại lời cầu xin của người bị bế tắc và phần thương của Ngài dành cho người bị nạn là những điều các ngươi không hề hay biết');
INSERT INTO "vn_mokhtasar" VALUES(12,87,'12:87','Người Cha nói với các con: Này các con của Cha, các con hãy đi hỏi thăm tin tức của Yusuf và em trai nó, và các con đừng tuyệt vọng từ sự khoan dung của Allah và sự nhân từ đối với bề tôi của Ngài, do chỉ có những kẻ vô đức tin mới tuyệt vọng về sự khoan dung và nhân từ của Ngài, vì họ không biết về quyền năng vĩ đại của Allah và Ngài bí mật nhân lên gấp đôi cho bề tôi của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(12,88,'12:88','Rồi họ nghe lời Cha, họ đi tìm Yusuf và em trai Người, khi vào gặp Yusuf họ thưa: Chúng tôi gặp phải khó khăn và đói nghèo, chúng tôi đến chỉ với một ít tiền lẻ, xin Ngài hãy đong đủ như trước đó đã đong cho chúng tôi, và hãy bố thí thêm cho chúng tôi hoặc bỏ qua phần tiền cỏn con này của chúng tôi, quả thực Allah sẽ ban thưởng cho những người bố thí bằng phần thưởng tốt đẹp');
INSERT INTO "vn_mokhtasar" VALUES(12,89,'12:89','Khi nghe lời nói đáng thương của họ làm Yusuf không cầm lòng đặng, buộc Người phải lộ diện trước họ: Các anh có biết những gì các anh đã làm với Yusuf và em trai Y khi các anh không biết hình phạt về những gì các anh đã làm với hai đứa nó?');
INSERT INTO "vn_mokhtasar" VALUES(12,90,'12:90','Họ ngạc nhiên, và nói: Ngài thực sự là Yusuf sao?! Yusuf trả lời: Phải, em là Yusuf đây, và người mà các anh đang thấy cùng em chính là em của em, quả thực Allah đã ban hồng ân cho em khi giải thoát em thoát khỏi mọi tai nạn bằng chính lòng thành tâm. Chắc chắn rằng ai kính sợ Allah bằng cách làm theo những gì Ngài bảo và tránh xa những gì Ngài cấm, rồi nhẫn nại trước thử thách; thì chắc chắn rằng việc làm của y là điều tốt và Allah không quên thưởng cho những người tốt, mà còn giữ nó cho họ');
INSERT INTO "vn_mokhtasar" VALUES(12,91,'12:91','Họ xin tha thứ về những gì họ đã làm: Xin thề trước Allah, thật ra Ngài đã ưu ái em hơn các anh bằng việc ban cho em những phẩm chất hoàn hảo, và trước đây các anh là những kẻ xấu xa khi đã làm những điều tồi tệ với em');
INSERT INTO "vn_mokhtasar" VALUES(12,92,'12:92','Và Yusuf đã chấp nhận lời xin lỗi của họ, Ngươi nói: Hôm nay không còn gì phải bắt lỗi hay trừng phạt các anh nữa, em khẩn cầu Allah tha thứ cho các anh, vì Ngài là Đấng Khoan Dung nhất của những người tỏ lòng khoan dung');
INSERT INTO "vn_mokhtasar" VALUES(12,93,'12:93','Sau khi nghe được các anh kể về nổi khổ mà Cha của họ phải trải qua thì Người đưa họ chiếc áo của mình và bảo: Các anh hãy mang chiếc áo này đi, rồi ném vào mặt Cha, như vậy cha sẽ thấy lại và rồi các anh hãy đưa tất cả gia đình về đây với em');
INSERT INTO "vn_mokhtasar" VALUES(12,94,'12:94','Và khi đoàn thương buôn lên đường rời Ai Cập, và cách xa nơi đó Y''aqub (cầu xin sự bình an đến Người) nói với các con và những người ở đó: Quả thật ta ngửi thấy mùi của Yusuf, các con đừng nghĩ Cha không biết gì và đừng nói Cha mất trí nhớ: Đây là ông già lẫm cẫm, ông đang nói mà không biết mình nói gì');
INSERT INTO "vn_mokhtasar" VALUES(12,95,'12:95','Những người con đang ở cùng Người đáp: Xin thề với Allah, chắc chắn cha vẫn còn bị ám ảnh bởi hình ảnh của Yusuf trước đây và khát khao gặp lại Nó');
INSERT INTO "vn_mokhtasar" VALUES(12,96,'12:96','Khi người đưa tin đến báo tin vui cho Y''aqub thì y ném chiếc áo của Yusuf vào mặt ông thì ông nhìn thấy trở lại, lúc đó ông nói với các con: Chẳng phải Cha đã nói với các con rồi sao, quả thực Cha biết sự tử tế của Allah và sự tốt đẹp của Ngài mà các con không biết hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(12,97,'12:97','Những đứa con xin lỗi Cha về những hành động của họ với Yusuf và em trai Người, họ nói: Thưa Cha! Cha hãy cầu xin Allah tha thứ tội lỗi trước đây của tụi con, quả thật tụi con là những kẻ có tội xấu xa về những việc làm của tụi con đối với Yusuf và em trai Nó');
INSERT INTO "vn_mokhtasar" VALUES(12,98,'12:98','Người Cha nói với các con: Cha sẽ cầu xin Thượng Đế của Cha tha thứ cho các con, quả thật Ngài là Đấng Hằng Thứ tội lỗi cho những người xám hối từ bề tôi của Ngài, Ngài là Đấng Khoan Dung với họ');
INSERT INTO "vn_mokhtasar" VALUES(12,99,'12:99','Rồi Y''aqub và gia đình ông rời mảnh đất của họ để tìm gặp Yusuf ở Ai Cập, khi họ vào trình diện Người thì Người ôm chầm lấy cha và mẹ mình, và Y nói với những người anh và gia đình mình: Mọi người hãy vào Ai Cập an toàn theo ý muốn của Allah không gặp bất cứ trở ngại nào');
INSERT INTO "vn_mokhtasar" VALUES(12,100,'12:100','Và Yusuf đưa cha mẹ mình lên ngồi trên ghế mà Người hay ngồi, rồi cha mẹ và những người anh em của Người tất cả là mười một người cùng sụp lạy trước mặt Người, đó là nghi thức chào ra mắt, thể hiện sự tôn vinh chứ không phải thờ phượng, nhằm xác nhận lại giấc mộng mà Yusuf đã thấy trước đây, Người nói với Cha mình: Đây là ý nghĩa việc mọi người sụp lạy con, nó giải thích cho giấc mơ mà con đã thấy trước đó mà con đã kể cho Cha, quả thực Thượng Đế của con đã biến nó thành sự thật, và quả thực Thượng Đế của con đã tốt với con khi Ngài đưa con ra khỏi nhà giam, và giờ đây Ngài đưa mọi người từ thôn quê đến đây sau khi Shaytan đã gây hiềm khích giữa con với các anh. Quả thật Thượng Đế của con đã rất tử tế với những gì Ngài muốn, quả thực Ngài là Đấng Hằng Biết tình trạng của đám bầy tôi của Ngài, Đấng Sáng Suót trong việc sắp xếp');
INSERT INTO "vn_mokhtasar" VALUES(12,101,'12:101','Sau đó Yusuf cầu xin Thượng Đế của Người: Lạy Thượng Đế của bề tôi, quả thật Ngài đã cho bề tôi quyền hành ở Ai Cập, và dạy bề tôi biết giải mộng. Lạy Đấng tạo ra các tầng trời và đất vốn chưa từng tồn tại trước đó, Ngài là Đấng Bảo Hộ tất cả mọi việc trong cuộc sống trần gian, và Đấng Bảo Hộ tất cả mọi việc vào Ngày Sau, xin Ngài hãy rút linh hồn của bề tôi lúc bề tôi là một người Muslim, và cho bề tôi gia nhập vào nhóm những vị sứ giả ngoan đạo từ cha ông của bề tôi và những vị ngoan đạo khác trên tầng Firdaw ở Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(12,102,'12:102','Đó là câu chuyện về Yusuf và những người anh em của Người đã mặc khải cho Ngươi - Hỡi Thiên Sứ - Ngươi đã không biết điều đó khi mà Ngươi không có mặt nơi những người anh của Yusuf khi họ quyết định ném Người xuống giếng, và họ đã thực hiện kế hoạch đó, nhưng Ta đã mặc khải điều đó cho Ngươi');
INSERT INTO "vn_mokhtasar" VALUES(12,103,'12:103','Và đa số nhân loại không tin dù Ngươi - hỡi Thiên Sứ - đã cố gắng tìm mọi cách chỉ để họ tin, cho nên Ngươi chớ có đau khổ vì họ');
INSERT INTO "vn_mokhtasar" VALUES(12,104,'12:104','Và nếu họ khôn ngoan là họ đã tin tưởng Ngươi rồi, vì Ngươi không đòi hỏi gì từ họ - hỡi Thiên Sứ - về việc phổ biến Qu''ran và không yêu cầu họ thưởng gì cho Ngươi cả, vì Qu''ran chỉ là thông điệp để nhắc nhở cho tất cả nhân loại');
INSERT INTO "vn_mokhtasar" VALUES(12,105,'12:105','Và có biết bao là dấu hiệu là bằng chứng cho việc chỉ tồn tai mỗi một Thượng Đế Tối Cao, trãi rộng khắp nơi trên các tầng trời và đất, con người đã lướt qua các dấu hiệu đó vậy mà họ vẫn không suy ngẫm và xem xét nó, rồi họ quay mặt xa lánh nó');
INSERT INTO "vn_mokhtasar" VALUES(12,106,'12:106','Và đa số nhân loại không tin rằng Allah là Đấng Tạo Hóa, Đấng Ban Bổng Lộc, Đấng Ban Sự Sống, Đấng Ban Cái Chết mà họ lại thờ phượng Ngài cùng với thần linh khác như các bức tượng và những hình hài vô tri vô giác, và họ vu khống rằng Allah có con');
INSERT INTO "vn_mokhtasar" VALUES(12,107,'12:107','Làm sao những kẻ thờ đa thần lại có cảm thấy an toàn khi mà hình phạt trên trần gian túm lấy và bao phủ bọn họ, họ không tài nào thoát ra được, hoặc Ngày Tận Thế bất ngờ ập xuống, và họ không nhận ra sự xuất hiện của nó để mà chuẩn bị cho Ngày đó, thế là họ đã không tin tưởng?!');
INSERT INTO "vn_mokhtasar" VALUES(12,108,'12:108','Ngươi - hỡi Thiên Sứ - hãy nói với người mà Ngươi kêu gọi: Đây là đường lối của Ta, con đường mà Ta kêu gọi nhân loại theo nó, hoàn toàn dựa trên bằng chứng rõ ràng, đó là phương án của Ta cũng như của tất cả những ai noi theo Ta, những ai kế nghiệp, và Ta không nằm trong nhóm người thờ phượng đa thần, không tổ hợp với Allah mà Ta thuộc nhóm người thờ phượng duy nhất mình Ngài');
INSERT INTO "vn_mokhtasar" VALUES(12,109,'12:109','Và Ta đã gửi những Sứ Giả trước Ngươi - hỡi Thiên Sứ - cũng là những người phàm không phải Thiên Thần, TA mặc khải cho Họ giống như Ta đã mặc khải cho Ngươi, Họ thuộc nhóm người thành thị không phải người thôn quê, rồi cộng đồng của Họ cho rằng Họ lừa dối nên Ta đã hủy diệt chúng. Phải chăng những kẻ cho rằng Ngươi nói dối đã không đi khắp nơi trên thế giới để xem xét kết cuộc của những kẻ nói dối trước đó để mà suy ngậm về họ?! Và ngôi nhà vào Ngày Sau là những đặc ân tốt nhất dành cho những người kính sợ Allah trên trần gian. Sao các ngươi không hiểu điều tốt đẹp đó mà kính sợ Allah như những gì Ngài yêu cầu - mệnh lệnh lớn nhất đó là đức tin Iman - và tránh xa những gì Ngài cấm, trong đó tội lớn nhất là tội Shirk tức tổ hợp với Allah');
INSERT INTO "vn_mokhtasar" VALUES(12,110,'12:110','TA trì hoãn không vội vàng gấp gáp trừng trị đám kẻ thù của những Sứ Giả mà TA đã gởi xuống trước đây, để TA lôi cuốn họ đi xa hơn. Mãi đến khi sự hủy diệt họ cũng được tạm hoãn khiến cho các Sứ giả của Ngài mất hết hi vọng về sự hủy diệt kẻ thù, và những kẻ không tin tưởng nghĩ rằng các Sứ giả đã lừa dối họ trong những gì họ được hứa là sẽ bị trừng phạt về những họ đã phủ nhận và việc giải thoát những người tin tưởng thì lúc đó sự cứu giúp của TA sẽ đến với Sứ giả của TA, Rồi TA giải cứu các Sứ giả của TA cùng với những người có đức tin thoát khỏi sự hủy hoại của những kẻ phủ nhận sự thật, nhưng sự trừng phạt của TA sẽ không tránh khỏi đối với những kẻ làm tội lỗi một khi đã giáng xuống cho họ');
INSERT INTO "vn_mokhtasar" VALUES(12,111,'12:111','Quả thật trong những câu chuyện về các vị Sứ Giả và cộng đồng của Họ, và trong câu chuyện về Yusuf và những người anh em của Y là những bài học cho những người thông hiểu. Tất cả những gì bao gồm trong Qur''an không phải là lời bịa đặt của Allah, mà nó là sự xác thật cho những Kinh Sách được Allah mặc khải xuống, và giải thích chi tiết về những gì cần giải thích về các quy định và giáo luật, và dẫn lối cho những điều tốt, và hồng ân cho nhóm người có đức tin, rồi họ là những người được hưởng lợi');
INSERT INTO "vn_mokhtasar" VALUES(13,1,'13:1','(Alif Lam Mim Ra) những từ ngữ mở đầu tương tự như sự mở đầu của chương Al-Baqarah. Đó là những câu Kinh cao quý trong chương này, và Qur''an là Kinh Sách mà Allah ban xuống cho Ngươi - hỡi Thiên Sứ - Nó là sự thật mà không có chút nghi vấn nào, và chắc chắn Nó đến từ Allah, nhưng đa số con người không tin tưởng nó do ngoan cố và tự cao');
INSERT INTO "vn_mokhtasar" VALUES(13,2,'13:2','Allah chính Đấng đã tạo ra các tầng trời cao vút không cần cột chống mà các ngươi có thể nhìn thấy nó, rồi Ngài bước lên ngài vàng cao nhất của Ngài bằng điệu bộ phù hợp với Ngài mà tín đồ không được phép thí dụ và so sánh. Ngài chế ngự mặt trăng và mặt trời để thích hợp với tạo vật của Ngài, cả mặt trăng lẫn mặt trời đều bơi theo một quy trình nhất định trong tầm quan sát của Allah, Ngài Toàn Năng luân chuyển mọi việc trong các tầng trời và đất theo ý muốn của Ngài, Ngài đưa ra các bằng chứng cụ thể về quyền lực của Ngài với hy vọng các ngươi có niềm tin kiên định về cuộc trình diện ở Đời Sau để các ngươi chuẩn bị nó bằng những việc làm lương thiện');
INSERT INTO "vn_mokhtasar" VALUES(13,3,'13:3','Và Ngài Hiển Vinh đã trải rộng mặt đất, rồi đặt vào nó những dãy núi vựng chắc để nó không gây ảnh hưởng cho con người, và Ngài tạo ra các loại trái cây gồm hai giới đực và cái giống như Ngài đã tạo ra loài động vật, rồi Ngài lấy bóng tối của ban đêm bao phủ ánh sáng của ban ngày. Quả thật những gì được nhắc đến để làm bằng chứng cho nhóm người biết suy nghĩ về sự tạo hóa của Allah, và họ ngẫm nghĩ về nó, vì họ là những người hưởng lợi từ các dấu hiệu và bằng chứng đó');
INSERT INTO "vn_mokhtasar" VALUES(13,4,'13:4','Rồi trên mặt đất có những nơi gần kề nhau, và trong đó có những mảnh vườn nho, những cánh đồng nông sản, những mảnh vườn chà là có chung một gốc, và những loại chà là không cùng một gốc, những mảnh vườn đó được tưới bằng cùng một loại nước, nhưng loại quả này lại có vị ngon hơn loại quả kia và loại kia lại có lợi ích khác mặc dù chúng nằm cạnh nhau và được tưới bằng cùng một loại nước, quả thật những gì được nhắc đến là những dấu hiệu và bằng chứng cho nhóm người biết suy nghĩ, vì chắc chắn họ là những người suy ngẫm về điều đó');
INSERT INTO "vn_mokhtasar" VALUES(13,5,'13:5','Và nếu Ngươi ngạc nhiên - hỡi Thiên Sứ - từ điều gì đó thì Ngươi sẽ càng ngạc nhiên hơn khi họ phủ nhận việc phục sinh mà nói lời bác bỏ về nó: Phải chăng khi bọn ta chết đi rồi hóa thành cát bụi, xương cốt bị tan rã, rồi sau đó lại được dựng sống trở lại hay sao?! Họ là những kẻ phủ nhận việc dựng sống lại sau khi chết cũng như họ là những kẻ không tin tưởng Thượng Đế của họ khi họ phủ nhận quyền lực của Ngài từ sự dựng sống lại về cái chết, và họ sẽ bị đeo xiềng xích vào cổ trong lửa Hỏa Ngục vào Ngày Phục Sinh, họ thuộc nhóm người của Hỏa Ngục, họ sẽ ở đó đời đời, không được thoát ra, và hình phạt của họ cũng không chấm dứt');
INSERT INTO "vn_mokhtasar" VALUES(13,6,'13:6','Những kẻ thờ đa thần thúc dục Ngươi - hỡi Thiên Sứ - mang đến sự trừng phạt, tuy nhiên hình phạt đã đến chậm trước khi họ hưởng được toàn bộ ân phước mà Allah đã tiền định cho họ. Trong khi trước họ là những cộng đồng tương tự như họ đã bị trừng phạt vì tội phủ nhận như họ, tại sao họ không cân nhắc đến điều đó? Và quả thật Thượng Đế của Ngươi - hỡi Thiên Sứ - nhẫn nhịn con người mặc dù họ rất bất công nhưng Ngài không vội vàng trừng phạt để họ có cơ hội sám hối với Allah, và chắc chắn Ngài trừng phạt nghiêm khắc với những kẻ phủ nhận mà không biết sám hối');
INSERT INTO "vn_mokhtasar" VALUES(13,7,'13:7','Và những kẻ phủ nhận Allah nói - trong sự ngang bướng và ngoan cố - tại sao Muhammad không được Thượng Đế của Y ban xuống một dấu hiệu nào giống như Ngài đã ban xuống cho Musa và Ysa. Quả thật Ngươi - hỡi Thiên Sứ - chỉ là răn đe thiên hạ về hình phạt từ Allah, và Ngươi không có dấu hiệu nào ngoài những dấu hiện mà Allah đã ban cho Ngươi, và mỗi cộng đồng điều có một Nabi dẫn dắt họ đến với con đường chính nghĩa, và hướng dẫn họ');
INSERT INTO "vn_mokhtasar" VALUES(13,8,'13:8','Allah biết rõ bào thai nằm trong bụng của mỗi người phụ nữ, Ngài biết tất cả về nó, và Ngài biết những gì đang diễn ra tử cung về việc bào thai thiếu hay dư tháng, khỏe mạnh hay bệnh tật, và tất cả mọi thứ đã được Ngài định lượng đúng mức không hề dư và cũng không hề thiếu hụt');
INSERT INTO "vn_mokhtasar" VALUES(13,9,'13:9','Bởi vì Ngài là Đấng Hiểu Biết tất cả những gì mà các giác quan không cảm nhận được, và là Đấng Hiểu Biết tất cả những gì mà các giác quan nắm bắt được, Ngài Vĩ Đại qua các Phẩm Chất, Tên Gọi và việc làm, Đấng Tối Cao trên tất cả tạo vật mà chính Ngài đã tạo ra');
INSERT INTO "vn_mokhtasar" VALUES(13,10,'13:10','Ngài biết mọi điều bí mật, mọi điều trắc ẩn cũng như việc Ngài biết những gì các ngươi che đậy - hỡi nhân loại -, luôn cả lời nói thì thầm và công bố, và Ngài cũng biết chuyện ai ẩn mình tránh né con người trong bóng đêm, rồi ai hiện rõ hành động của y vào ban ngày');
INSERT INTO "vn_mokhtasar" VALUES(13,11,'13:11','Và Đấng Vinh danh và Tối Cao có những thiên thần được chọn giám sát con người, rồi trong số Họ đến chỗ con người vào ban đêm, và một số khác đến vào ban ngày, Họ canh chừng con người theo mệnh lệnh của Allah từ chuỗi số phận mà Allah đã định sẵn nó cho họ, và ghi chép lại mọi hành động và lời nói của con người. Chắc chắn Allah không thay đổi bất cứ cộng đồng nào từ tình trạng tốt chuyển sang xấu trừ phi chính họ tự làm thay đổi nó, và khi Allah đã muốn hủy diệt một cộng đồng nào thì không ai có thể thay đổi ý muốn của Ngài, các ngươi sẽ như thế nào - hỡi nhân loại - khi không được Allah bảo hộ mọi việc, cho nên hãy tìm Allah làm Đấng Bảo Hộ thì các ngươi sẽ trông cậy vào Ngài để giải cứu các ngươi khỏi hoạn nạn');
INSERT INTO "vn_mokhtasar" VALUES(13,12,'13:12','Ngài là Đấng trình bày cho các ngươi - hỡi nhân loại - thấy được tia chớp sáng, nó khiến các ngươi sợ hãi từ tiếng sét chát chúa đó, song nó cũng là niềm hi vọng mang đến những cơn mưa, và Ngài là Đấng tạo ra đám mây nặng trĩu với lượng mưa lớn');
INSERT INTO "vn_mokhtasar" VALUES(13,13,'13:13','Và sấm sét tán dương lời ca tụng Thượng Đế bằng lời tán dương, và các Thiên Thần vì khiếp sợ Ngài cũng tán dương và ca tụng Ngài, Ngài ném những lưỡi tầm sét sắc bén vào ai mà Ngài muốn khi Ngài quyết định hủy diệt y, khi những kẻ phủ nhận tranh cãi về tính duy nhất của Allah, Allah vô cùng nghiêm khắc và mạnh bạo với những kẻ bất tuân');
INSERT INTO "vn_mokhtasar" VALUES(13,14,'13:14','Allah duy nhất sở hữu thuyết độc thần Tawhid không có việc chia sẽ quyền thống trị. Về những bụt tượng được người đa thàn cầu xin thay vì Allah, chúng không đáp lại lời cầu xin của họ cho được dù đó là vấn đề gì, và những lời cầu xin đó của họ giống như hình ảnh một người khát nước rồi bị ảo giác khiến y đưa tay ra hứng nước để đưa lên miệng uống, nhưng nước nào có để đến được miệng y, và lời cầu xin của những kẻ không có đức tin hoàn toàn không được chấp nhận, bởi chính những bụt tượng đó vốn nào giúp ích gì được họ cũng như cũng như không đuổi được khỏi họ điều xấu.');
INSERT INTO "vn_mokhtasar" VALUES(13,15,'13:15','Và chỉ duy nhất Allah xứng đáng để được cúi lạy, dù là bất cứ ai trên các tầng trời hay dưới đất, người có đức tin hay không có đức tin cũng phải cúi lạy Ngài, chỉ khác là người có đức tin tuân theo thành tâm cúi lạy Ngài, còn những kẻ không có đức tin lại hoàn toàn không muốn làm điều đó, và điều đầu tiên là phải thành tâm phục tùng Ngài, ngay cả những cái bóng của những tạo vật cũng cúi lạy Ngài vào buổi sáng và chiều');
INSERT INTO "vn_mokhtasar" VALUES(13,16,'13:16','Ngươi - hỡi Thiên Sứ - hãy nói với những kẻ không có đức tin đang thờ phượng Allah cùng với người khác: Ai là người tạo ra các tầng trời, trái đất và điều hành chúng? Ngươi hãy hỏi họ - hỡi Thiên Sứ -: Allah là Đấng tạo ra chúng và điều hành chúng, và các ngươi công nhận điều đó. Ngươi hãy hỏi họ - hỡi Thiên Sứ -: Chẳng lẻ các ngươi tự ý chọn những người bảo hộ bất lực mà không phải Allah, chúng không có khả năng tự đem lại lợi ích cho mình, và cũng không tự giải thoát điều tồi tệ khỏi mình, thế chẳng lẻ chúng lại có khả năng giúp đỡ người khác ư? Ngươi hãy bảo họ - hỡi Thiên Sứ - chẳng lẻ những kẻ đuôi mù không có đức tin lại giống những người có đức tin sáng mắt được dẫn dắt hay sao? hay chẳng lẻ những kẻ không có đức tin là bóng tối lại giống với người có đức tin là ánh sáng hay sao? hoặc là họ gọp Allah chung với những thần linh về quyền năng tạo hóa, họ nghĩ thần linh của họ đã tạo ra mọi thứ giống như Allah đã tạo ra, rồi nhầm lẫn giữa tạo vật của thần linh và của Allah? Ngươi hãy bảo họ - hỡi Thiên Sứ - Allah mới là Đấng duy nhất đã tạo hóa ra mọi thứ, không có ai tổ hợp chung với Ngài trong việc tạo hóa, và chỉ mình Ngài là Thượng Đế xứng đáng để thờ phượng, Đấng Bất Bại');
INSERT INTO "vn_mokhtasar" VALUES(13,17,'13:17','Allah đưa ra ví dụ cụ thể để xua tan điều giả dối và cô động lại mỗi sự thật bằng việc Ngài ban nước mưa từ trên trời xuống đến khi nó lắp đầy các thung lũng, tất cả chảy theo kích thước lớn nhỏ của chúng, rồi có dòng chảy mạnh cuốn theo bọt trên mặt nước; và một ví dụ khác cho họ bằng một số kim loại quý hiếm mà con người đem đốt để làm tan chảy chúng rồi con người dùng nó làm thành trang sức, với hai ví dụ trên Allah cho thấy được sự thật và điều giả dối, và điều giả dối giống như bọt nổi trên mặt nước, và những cái bọt cũng sẽ bị tan biến như trong quá trình nấu chảy kim loại. Còn sự thật thì như là nước tinh khiết được dùng để uống, và nó làm phát triển cây trái, gia súc và con người, và như những gì còn lại của kim loại sau khi đun chảy rồi nó đem lại lợi ích cho con người, với hai điều trên Allah đưa ra ví dụ cho nhân loại biết để làm rõ sự thật từ điều giả dối');
INSERT INTO "vn_mokhtasar" VALUES(13,18,'13:18','Đối với những người có đức tin họ đáp lại lời kêu gọi từ Thượng Đế của họ khi Ngài kêu họ thờ phượng Ngài duy nhất và tuân theo Ngài thì phần thưởng tốt nhất dành cho họ là Thiên Đàng; còn những kẻ không có đức tin không đáp lại lời kêu gọi của Thượng Đế thờ phượng Ngài duy nhất và tuân theo Ngài, thì dẫu cho họ có nhiều tài sản trên mặt đất này cùng với những thứ tương tự mà họ có được nhân đôi lên để xóa sạch tội lỗi và chuộc lại lỗi lầm của họ thì cũng vô ích, vì họ là những kẻ không tuân theo lời kêu gọi của Thượng Đế nên những tội lỗi của họ điều được đem ra xét xử, và nơi ở dành cho họ là Hỏa Ngục, giường nghỉ của họ rất tồi tệ và lửa là nơi xứng đáng dành cho họ');
INSERT INTO "vn_mokhtasar" VALUES(13,19,'13:19','Không hề giống nhau giữa người mà y biết việc Allah ban xuống cho ngươi - hỡi Thiên Sứ - là chân lý không có lòng nghi kỵ và y tin tưởng đáp lại mệnh lệnh của Allah, còn kẻ đuôi mù thì y chính là kẻ không có đức tin, không đáp lại mệnh lệnh của Allah, quả thật chỉ những người có đầu óc chính chắn mới nhận thức và tiếp thu được điều đó');
INSERT INTO "vn_mokhtasar" VALUES(13,20,'13:20','Họ thực hiện đúng theo lời giao ước với Allah hoặc thực hiện đúng lời hứa với mọi người, và y càng không nuốt lời thề trịnh trọng bằng danh nghĩa của Alalh với Allah hoặc với người khác');
INSERT INTO "vn_mokhtasar" VALUES(13,21,'13:21','Và họ luôn kết nối những điều mà Allah ra lệnh là phải gắn kết tình ruột thịt, và do họ kính sợ Thượng Đế khiến họ phải làm theo những gì Ngài bảo và tránh xa mọi điều Ngài cấm, rồi họ lo sợ Allah sẽ kết án họ về tất cả điều xấu xa họ đã làm, khi ai bị trách vấn về những tội lỗi xem như y đã bị hủy diệt');
INSERT INTO "vn_mokhtasar" VALUES(13,22,'13:22','Và họ biết kiên trì nghe theo Allah, biết kiên nhẫn về những điều tốt đẹp hay điều xấu mà Allah đã tiền định, biết kiềm chế bản thân không vi phạm tội lỗi với Allah để mong được sự hài lòng nơi Allah, họ dâng lễ Salah đẩy đủ về mọi mặt, họ biết thực hiện bổn phận về những gì mà Ngài ban cho từ tài sản, họ biết bố thí tự nguyện với sự kính đáo không khoe khoang, hay công khai để mọi người làm theo họ, họ biết lấy điều tốt để trải lại điều xấu. Với những ai có phẩm chất tốt vừa liệt kê chắc chắc sẽ được kết quản mỹ mãn vào Ngày Phục Sinh');
INSERT INTO "vn_mokhtasar" VALUES(13,23,'13:23','Kết quả mỹ mãn đó là được cư ngụ trong những ngôi vườn Thiên Đàng, họ sẽ được ở trong đó vĩnh viện, và để hoàn thiện thêm ân phước cho họ là cho họ hội ngộ cùng với những ai ngoan đạo thuộc tổ tiên, cha mẹ, vợ chồng, con cái, và các Thiên Thần vào chào hỏi chúc mừng họ từ tất cả các cánh cửa của Thiên Đàng');
INSERT INTO "vn_mokhtasar" VALUES(13,24,'13:24','Và các Thiên Thần luôn chào hỏi khi vào gặp họ với lời chào: Salamu a''laikum - cầu xin bình an cho các bạn - nghĩa là: Các bạn đã bình an thoát khỏi sự tổn hại vì các vị đã kiên trì tuân theo Allah, kiên nhẫn chịu đựng thử thách của Ngài, và nhẫn nại trước khó khăn. Thật là tuyệt hảo cho phần thưởng cuối cùng Ngài dành cho quí vị');
INSERT INTO "vn_mokhtasar" VALUES(13,25,'13:25','Những ai vi phạm lời cam kết với Allah sau khi đã tuyên thệ, và cắt đứt những gì Allah ra lệnh phải gắn kết lại từ tình thân, họ là những người đau khổ bị tước đi tình thương nơi Allah, và họ nhận một kết cuộc tồi tệ đó là Hỏa Ngục');
INSERT INTO "vn_mokhtasar" VALUES(13,26,'13:26','Allah gia tăng bổng lộc cho những ai Ngài muốn, và thu hẹp nó lại với những ai Ngài muốn, và không phải ai được cho nhiều bổng lộc là dấu hiệu của sự hạnh phúc hoặc Allah yêu thương người đó, cũng không phải người được ít bổng lộc thì sẽ bị đau khổ, chỉ những kẻ bất tin mới vui vẻ với đời sống trần tục, họ phụ thuộc và yên tâm với nó, nhưng cuộc sống trần tục không phải nơi cuối cùng mà nó chỉ là sự tạm bợ, ngắn ngủi, nhanh chóng biến mất');
INSERT INTO "vn_mokhtasar" VALUES(13,27,'13:27','Rồi những kẻ phủ nhận Allah và những dấu hiệu của Ngài lên tiếng: Tại sao Thượng Đế không ban xuống cho Muhammad dấu hiệu rõ ràng để chứng minh sự thật về Y để bọn ta tin tưởng vậy? Ngươi - hỡi Thiên Sứ - hãy đáp lại họ: Quả thật, Allah làm lạc lối những ai Ngài muốn bằng sự công minh của Ngài, và hướng dẫn những ai biết quay về sám hối bằng hồng phúc của Ngài, và sự hướng dẫn chắc rằng không nằm trong tầm tay của họ cho để họ đưa ra yêu sách ban xuống một dấu hiệu lạ.');
INSERT INTO "vn_mokhtasar" VALUES(13,28,'13:28','Những ai được Allah dẫn dắt họ là những người có đức tin, và trái tim của họ kiên định bằng sự tưởng nhớ Allah bằng lời tụng niệm, bàng lời tạ ơn, bằng việc xướng đọc và nghe Qur''an, chỉ có sự tưởng nhớ Allah duy nhất mới làm con tim kiên định, và xứng đáng được như vậy');
INSERT INTO "vn_mokhtasar" VALUES(13,29,'13:29','Những ai tin tưởng vào Allah và làm những điều tốt kéo họ được gần Allah hơn thì họ có cuộc sống tốt đẹp vào Ngày Sau, và có kết quả mỹ mãn đó là Thiên Đàng');
INSERT INTO "vn_mokhtasar" VALUES(13,30,'13:30','Giốn như việc Ta đã cử những vị Sứ Giả đến với cộng đồng của họ trước đây, y như việc Ta cử Ngươi - hỡi Thiên Sứ - đến với cộng đồng Ngươi. Để Ngươi xướng đọc nhắc nhở họ về Qur''an mà Ta mặc khải cho Ngươi, vì Qur''an đầy đủ bằng chứng chứng tỏ sự chân thật của Ngươi, nhưng cộng đồng của Ngươi quả thật họ đang chối bỏ các dấu hiệu đó. Bởi vì họ phủ nhận Đấng Rất Mực Độ Lượng khi họ tổ hợp Ngài với các thần linh khác. Ngươi hãy bảo họ - hỡi Thiên Sứ - Đấng Rất Mực Độ Lượng mà các ngươi tổ hợp với thần linh khác, Ngài chính là Thượng Đế của Ta không thần linh nào xứng đáng thờ phượng ngoài Ngài, chỉ riêng Ngài Ta phó thác tất cả mọi việc, và Ta quay về xám hối');
INSERT INTO "vn_mokhtasar" VALUES(13,31,'13:31','Và nếu một trong những thuộc tính của các Kinh Sáng được mặc khải từ Allah xuống có thể thay đổi vị trí của các ngọn núi, hoặc có thể chẻ mặt đất ra thành sông và suối, hay đọc Nó lên cho người chết sống lại - thì đó chính là Qur''an đã ban xuống cho Ngươi - hỡi Thiên Sứ - Nó là bằng chứng rõ rãng, là sự tác động lớn nếu như họ thực sự biết kính sợ nhưng họ lại chối bỏ. Không, mọi công việc điều do Allah chỉ thị kể cả việc ban xuống các phép màu hay những điều dường như không thể xảy ra và những điều khác nữa, chẳng lẻ những người có đức tin không biết rằng, quả thực nếu Allah muốn dẫn dắt con người mà không cần phải mặc khải các dấu hiệu để chỉ dẫn họ mọi thứ hay sao? Nhưng Ngài không muốn như vậy, và những kẻ không tin tưởng vào Allah không ngừng gặp tai họa với những gì họ đã làm và tai họa nặng nề giáng xuống cho họ, hoặc tai họa đó giáng xuống gần nhà của họ, cho đến khi lời hứa về hình phạt của Allah đến với họ. Quả thật Allah không bỏ qua bất cứ những gì mà Ngài đã hứa, điều đó sẽ đến khi thời thời gian của nó đến');
INSERT INTO "vn_mokhtasar" VALUES(13,32,'13:32','Và Ngươi không phải là vị Sứ Giả đầu tiên bị cộng đồng mình phủ nhận và cười nhạo, quả thật các vị Sứ Giả trước Ngươi - hỡi Thiên Sứ - cũng bị cộng đồng của họ chế giễu và phủ nhận những điều mặc khải cho họ, rồi Ta đã hoãn lại việc trừng phạt bọn chúng để chúng nghĩ rằng Ta sẽ không tiêu diệt chúng được, rồi sau thời gian trì hoãn Ta đã trừng phạt chúng, vậy đấy, Ngươi đã thấy sự trừng phạt của Ta dành cho chúng? Quả thật, hình phạt của Ta rất đau đớn');
INSERT INTO "vn_mokhtasar" VALUES(13,33,'13:33','Thế thì ai là Đấng trông coi bổng lộc của tất cả tạo vật, giám sát mọi linh hồn về những gì họ đã làm để thưởng phạt về điều họ đã làm. Lẽ nào Đấng đó lại bị xa lánh hơn những bức tượng vô năng không xứng đáng được thờ phượng hay sao? Vậy mà những kẻ không có đức tin đã cố đặt điều rằng những bức tượng đó lại là cộng sự với Allah một cách bất công và sai lầm. Ngươi hãy bảo họ - hỡi Thiên Sứ -: Đâu các ngươi hãy gọi tên những cộng sự mà các ngươi gán ghép với Allah trong việc thờ phượng, nếu những gì các ngươi nói là sự thật. Hay là các ngươi muốn chỉ bảo Allah về những cộng sự của Ngài trên mặt đất bằng lời nói xảo ngôn vô căn cứ hay sao? Không! Shaytan đã cám dỗ những kẻ không có đức tin làm nên những điều sai quấy, rồi họ phủ nhận Allah, và họ đã chuyển hướng lệch lạc khỏi con đường được dẫn dắt và chỉ đạo, khi người nào đã bị Allah làm lạc hướng thì người đó sẽ không có người dẫn dắt cho y');
INSERT INTO "vn_mokhtasar" VALUES(13,34,'13:34','Họ sẽ bị trừng phạt ở đời này bằng chính đôi tay của những người có đức tin bằng các hình thức giết chóc và tù đày. Nhưng chắc chắn rằng hình phạt đau đớn hơn và nặng nề hơn đang chờ đợi họ ở Đời Sau, và nó vô cùng tàn khóc và vĩnh viễn không bao giờ dừng lại. Rồi họ sẽ không có ai che chở để giải thoát họ khỏi hình phạt của Allah vào Ngày Sau');
INSERT INTO "vn_mokhtasar" VALUES(13,35,'13:35','Miêu tả về Thiên Đàng mà Allah đã hứa hẹn dành cho những ai biết kính sợ Ngài bằng việc làm theo những gì Ngài ra lệnh và tránh xa những gì Ngài cấm. Quả thật bên dưới những tòa lâu đài có các dòng sông chảy dài và những hàng cây lúc nào cũng ra trái không bao giờ ngưng, hoàn toàn trái ngược với trái cây trên trần gian. Và bóng mát của nó là vĩnh cửu không bao giờ tắt và bị thu hẹp. Đó là kết quả của những ai kính sợ Allah bằng việc thực hiện những gì Ngài ra lệnh và tránh xa những gì Ngài cấm, và hậu quả của những kẻ không có đức tin là Hỏa Ngục họ sẽ phải ở đó mãi mãi');
INSERT INTO "vn_mokhtasar" VALUES(13,36,'13:36','Và những ai được Ta ban cho Tawrah (Kinh Cựu Ước) thuộc người Do Thái, và ban cho Injin (Kinh Tân Ước) thuộc người Cơ Đốc, họ vui mừng về những gì được mặc khải cho Ngươi - hỡi Thiên Sứ - vì nó hoàn toàn phù hợp với mọi điều đã thiên khải cho họ, nhưng trong nhóm người Do Thái và Cơ Đốc vẫn có kẻ phủ nhận một số điều được mặc khải cho Ngươi vì nó không phù hợp với ham muốn của họ, hoặc vì đã miêu tả họ là tội đồ đã thay đổi và chỉnh sửa. Ngươi hãy nói với họ - hỡi Thiên Sứ -: Quả thật, Ta được Allah ra lệnh chỉ được thờ phượng Ngài duy nhất, không được tổ hợp Ngài với bất cứ ai, chỉ Ngài duy nhất Ta cầu xin và Ta không cầu xin bất cứ ai, và chỉ Ngài duy nhất Ta quay về. Những điều đó điều này đều có trong Tawrah và Injil');
INSERT INTO "vn_mokhtasar" VALUES(13,37,'13:37','Như những quyển Kinh Sách trước đây Ta mặc khải bằng ngôn ngữ của các cộng đồng trước kia, Ta mặc khải cho Ngươi - hỡi Thiên Sứ - Qur''an là lời phán, là sự phân tích rõ ràng về chân lý bằng ngôn ngữ Ả Rập, và nếu như Ngươi - hỡi Thiên Sứ - làm theo mong muốn của người dân Kinh Sách trong việc đàm phán với họ rồi Ngươi xóa bỏ những gì họ không đồng ý theo ước muốn của họ sau khi Ngươi đã tiếp thu kiến thức mà Allah đã truyền đạt cho Ngươi, thì Ngươi sẽ không được Allah bảo hộ cho việc làm của Ngươi, không giúp đỡ Ngươi chiến thắng kẻ thù, và Ngươi sẽ không có người che chở giúp thoát khỏi hình phạt của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(13,38,'13:38','Quả thật Ta đã cử các Sứ Giả đến trước Ngươi - hỡi Thiên Sứ - từ những người bình thường, và Ngươi không có gì mới mẻ so với họ, rồi Ta đã ban cho họ những người vợ, những đứa con như những người bình thường khác, chứ Ta không đặt họ là những vị Thiên Thần không vợ, không con. Và Ngươi cũng là một trong những Sứ Giả như họ có vợ, có con. Thế mà tại sao những kẻ thờ đa thần lại ngạc nhiên về Ngươi như vậy? Và không một sứ giả nào được phép mang đến một dấu hiệu hay phép màu nào cả ngoại trừ được sự chấp thuận của Allah, ở mỗi thời đại điều có một Kinh Sách để nhắc nhở và chỉ thị, và thời gian ấn định đó sẽ không đến sớm, cũng không muộn');
INSERT INTO "vn_mokhtasar" VALUES(13,39,'13:39','Allah xóa đi những gì Ngài muốn từ những điều tốt hay điều xấu, hạnh phúc hay đau khổ và những điều khác nữa. Và Ngài giữ lại những gì Ngài muốn, Ngài giữ Quyển Kinh Mẹ - Al-Lawhul Mahfuz), Nó là sách chứa đựng tất cả mọi thứ, và Nó tái hiện những điều đã xóa hay khẳng định những thứ còn tồn tại trong đó');
INSERT INTO "vn_mokhtasar" VALUES(13,40,'13:40','Và nếu như Ta cho Ngươi thấy - hỡi Thiên Sứ - một phần của hình phạt mà Ta đã hứa với họ trước khi Ngươi chết, hay Ta bắt linh hồn Ngươi trước khi chúng được thực hiện, thì nhiệm vụ của Ngươi cũng chỉ là người truyền đạt những gì mà Ta ra lệnh, và Ngươi không được phép vượt quá giới hạn cho phép. Ngươi cũng không được xét xử họ vì đó là việc của Ta');
INSERT INTO "vn_mokhtasar" VALUES(13,41,'13:41','Những kẻ bất tin không thấy rằng Ta đang thu hẹp dần dần lãnh thổ của đám người vô đức tin từ vùng ngoại biên bằng việc mở rộng Islam qua các cuộc chiến thắng của những người Muslim hay sao? Và khi Allah đã xét xử và phán quyết điều gì thì không một ai có thể quyết định được điều gì hay có thể thay đổi hoặc thay thế được nó. Và Ngài vô cùng nhanh chóng trong việc thanh toán, Ngài thanh toán những người đầu tiên và những người cuối cùng trong một ngày duy nhất');
INSERT INTO "vn_mokhtasar" VALUES(13,42,'13:42','Quả thật, những cộng đồng trước đó đã có mưu đồ với các Sứ Giả, và họ phủ nhận những điều mà các Sứ Giả mang đến, rồi họ đã làm gì với các Sứ Giả? Vốn không làm được gì cả. Bởi vì, việc sắp đặt hành động đó là sự sắp đặt của Allah không một ai khác, cũng như việc quả thật Ngài chính là Đấng biết hết những gì mà mỗi linh hồn đã làm và bỏ qua nó, và sau đó họ sẽ biết được có nhiều người sai trái khi không có đức tin nơi Allah, và nhiều người có đức tin trước đó làm điều tội lỗi, rồi họ đã chiến thắng với Thiên Đàng và có kết thúc tốt đẹp');
INSERT INTO "vn_mokhtasar" VALUES(13,43,'13:43','Và những kẻ không có đức tin nói: Ngươi - hỡi Muhammad - không phải là Sứ Giả của Allah. Ngươi hãy đáp lại họ - hỡi Thiên Sứ -: Chỉ cần Allah là đủ làm chứng giữa Ta và các ngươi về việc Ta có phải là Sứ Giả được Ngài phái xuống đến với các ngươi hay không, cộng thêm nhân chứng từ những ai có hiểu biết về những Kinh Sách được Ngài mặc khải trước đây, thì không ảnh hưởng gì vệ việc họ có phủ nhận hay không.');
INSERT INTO "vn_mokhtasar" VALUES(14,1,'14:1','Alif- Lam- Ra. Lời mở đầu tương tự phần mở đầu trong chương Al Baqarah. Qur''an này là một quyển Kinh mà Ta ban cho Ngươi - hỡi Thiên Sứ - để Ngươi đưa con người từ không có đức tin, ngu muội và lạc lối trở nên tin tưởng, hiểu biết, và được dẫn dắt đến với tôn giáo Islam mà nó chính là con đường của Allah Đấng Toàn Năng không ai có thể hơn được Ngài, và là Đấng Đáng Ca Ngợi trong tất cả mọi việc');
INSERT INTO "vn_mokhtasar" VALUES(14,2,'14:2','Allah là Đấng duy nhất cai trị các tầng trời và vạn vật dưới đất, vì vậy chỉ Ngài duy nhất xứng đáng được thờ phượng, không ai xứng đáng làm cộng sự với Ngài, và Ngài sẽ trừng phạt những kẻ không có đức tin bằng hình phạt khủng khiếp');
INSERT INTO "vn_mokhtasar" VALUES(14,3,'14:3','Những kẻ không có đức tin bị đời sống trần tục xa hoa tác động mà quên mất đi hồng ân vĩnh cựu vào Ngày Sau, và họ cố tình chuyển đổi nhận thức của con người về con đường của Allah, và cố tình lôi kéo họ lệch khỏi con đường chân lý, bị bẻ cong và chuyển hướng nó cho đến khi không một ai có thể đi trên con đường đó nữa. Đối với những ai có đức tính như miêu tả chính là đám người bị lạc lối rất xa với chân lý và sự đúng đắn');
INSERT INTO "vn_mokhtasar" VALUES(14,4,'14:4','Và không có Sứ Giả nào được Ta phái đến mà lại không nói thứ tiếng của cộng đồng Người, mục đích để Người dễ dàng truyền đạt những gì được Allah mặc khải, và cũng không ép buộc họ phải tin tưởng Allah. Rồi bằng sự công bằng Ngài đánh lạc hướng kẻ nào Ngài muốn, và Ngài đồng ý dẫn dắt người nào Ngài muốn bằng hồng ân của Ngài. Vì Ngài là Đấng Toàn Năng không ai có thể qua mặt được, Đấng Sáng Suốt trong tạo vật và quản lý nó');
INSERT INTO "vn_mokhtasar" VALUES(14,5,'14:5','Quả thật Ta đã cử Musa đến và TA đã ban cho Người những dấu hiệu làm bằng chứng cho sự trung thực của Người, và xác định Người đúng thực được Thượng Đế phái đến. Và TA ra lệnh cho Người giải thoát cộng đồng mình từ không có đức tin và ngu muội trở nên có đức tin và hiểu biết. Và Ta cũng ra lệnh Người phải nhắc nhở họ về những ngày của Allah, đó là những ngày mà TA đã ban hồng phúc cho họ, quả thật trong những ngày đó có các dấu hiệu rõ ràng về việc tôn thờ Allah duy nhất và quyền năng vĩ đại của Ngài, và Ngài cũng ban các hồng phúc cho những người có đức tin. Và những điều này rất có ý nghĩa với những người kiên nhẫn trong việc phục tùng Allah và luôn biết ơn về hồng ân của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(14,6,'14:6','Và Ngươi hãy nhớ lại - hỡi Thiên Sứ - khi Musa nhận mệnh lệnh từ Thượng Đế của Người, kêu Người bảo với người dân của mình là bộ tộc Israel về việc ghi nhớ ân huệ mà Allah đã ban cho: Hỡi dân ta! Các ngươi hãy nhớ đến ân huệ của Allah đã ban cho các ngươi khi Ngài đã giải thoát các ngươi từ đồng bọn của Fir-''awn, Ngài đã giải phóng các ngươi khỏi đau khổ, và cứu thoát các ngươi khỏi hình phạt tàn ác của chúng. Chúng giết con trai các ngươi để không ai tranh giành ngai vàng của chúng sau này, và chúng giữ phụ nữ của các ngươi lại để mà lăng mạ và sĩ nhục họ. Qua những hành động của chúng, đó chính là sự thử thách to lớn về lòng kiên nhẫn giành cho các ngươi. Rồi Allah xem xét lại lòng kiên nhẫn đó trong sự thử thách về việc giải thoát các ngươi khỏi sự đau khổ từ đồng bọn của Fir-''awn');
INSERT INTO "vn_mokhtasar" VALUES(14,7,'14:7','Và Musa đã bảo họ rằng: Các ngươi hãy nhớ, khi Thượng Đế của các ngươi công bố một vấn đề trọng đại: Nếu các ngươi biết ơn Ngài về những hồng phúc mà Ngài đã ban cho, thì Ngài sẽ ban thêm hồng phúc và ân huệ cho các ngươi. Còn nếu các ngươi phủ nhận hồng phúc của Ngài và không biết ơn về điều đó, thì chắc chắn rằng Ngài sẽ dành hình phạt khủng khiếp cho những ai phủ nhận hồng phúc của Ngài và bội ơn về điều đó');
INSERT INTO "vn_mokhtasar" VALUES(14,8,'14:8','Rồi Musa bảo người dân của mình: Này dân Ta! Nếu các ngươi và toàn thể nhân loại trên trái đất này có vong ơn, bạc nghĩa thì sự vong ơn đó sẽ trở về với các ngươi. Vì quả thật, Allah tự giàu có trong chính bản thân Ngài, và Ngài đáng được ca ngợi từ trong chính Ngài, đức tin của những người có đức tin không hề mang lợi ích gì cho Ngài, và sự phủ nhận của những kẻ bất tin cũng không gây thiệt hại gì cho Ngài');
INSERT INTO "vn_mokhtasar" VALUES(14,9,'14:9','Há các ngươi - hỡi đám người vô đức tin - đã chưa nghe thông tin về sự hủy diệt của những cộng đồng dối trá trước đây hay sao, như: Đám dân của Nuh, đám dân ''Ad, đám dân của Hud, đám dân Thamud của Saleh, và những người đến sau họ? và họ rất đông không ai biết rõ số lượng của họ ngoại trừ Allah. Khi các Sứ Giả mang đến cho họ những bằng chứng rõ ràng, nhưng họ đặt tay vào miệng mà cắn các ngón tay thể hiện sự tức giận với các Sứ Giả, rồi họ nói với các Sứ Giả: Chúng tôi không tin tưởng những điều quí vị mang đến, và chúng tôi vô cùng nghi ngờ về những gì quí vị kêu gọi chúng tôi chấp nhận');
INSERT INTO "vn_mokhtasar" VALUES(14,10,'14:10','Các Sứ Giả đáp lại họ: Chẳng lẻ các ngươi nghi ngờ về Tawhid - thuyết độc thần - và thuộc tính duy nhất của Allah trong việc thờ phượng Ngài hay sao, trong khi Ngài là Đấng đã khởi tạo ra các tầng trời và đất từ không là gì trước đây?! Ngài kêu gọi các ngươi đến với niềm tin Iman để Ngài tha thứ những lỗi lầm trước đó của các ngươi, và trì hoãn các ngươi cho đến ky hạn nhất định trên trần gian. Thế nhưng đám dân chúng đáp trả lại: Quí vị cũng chỉ là những người bình thường như chúng tôi, không có gì đặc biệt hơn chúng tôi cả, các vị muốn thay đổi chúng tôi thờ phượng những gì mà cha ông chúng tôi đã thờ phượng ư. Vậy thì quí vị hãy đưa ra các bằng chứng cụ thể để chúng tôi tin tưởng rằng quí vị là những vị Sứ Giả được Allah phái đến');
INSERT INTO "vn_mokhtasar" VALUES(14,11,'14:11','Các Sứ Giả đáp lại họ: Chúng tôi cũng là những người bình thường như mọi người, chúng tôi không phủ nhận điều đó, nhưng điều đó không có nghĩa là tất cả mọi thứ phải như nhau về mọi thứ. Vì Allah ban ân huệ với những hồng phúc riêng biệt cho ai mà Ngài muốn trong số bầy tôi của Ngài, và Ngài chọn lựa họ trong số nhân loại. Và sẽ không đúng nếu chúng tôi mang đến cho các người những điều mà các người yêu cầu trừ khi Allah cho phép làm điều đó, vì điều đó nằm ngoài khả năng của chúng tôi. Chỉ duy nhất Allah mới là Đấng có thẩm quyền, và chỉ duy nhất Allah những người có đức tin phó thác tất cả mọi việc nơi Ngài');
INSERT INTO "vn_mokhtasar" VALUES(14,12,'14:12','Và điều gì có thể ngăn cản, nguyên nhân gì khiến chúng tôi không phó thác nơi Ngài? Trong khi chính Ngài đã chỉ dẫn chúng tôi những đường lối ngay thẳng và đúng đắn. Và chắc chắn rằng chúng tôi sẽ kiên trì, chịu đựng trước mọi hành hạ của các người trong sự lừa dối và nhạo báng. Và hãy hướng về Allah duy nhất mà phó thác mọi công việc đi hỡi những ai muốn phó thác.');
INSERT INTO "vn_mokhtasar" VALUES(14,13,'14:13','Và những kẻ không có đức tin trong cộng đồng của các Sứ Giả nói khi họ không còn lời lẻ để tranh luận: Chúng tôi phải trục xuất các vị ra khỏi ngôi làng, hoặc các vị phải quay trở lại theo tôn giáo của chúng tôi. Cho nên Allah đã mặc khải cho các Sứ Giả với sự khẳng định rằng: TA chắc chắn sẽ tiêu diệt những kẻ sai quấy đã phủ nhận nơi Allah và các Sứ Giả của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(14,14,'14:14','Và chắc chắn TA sẽ bổ nhiệm các ngươi - hỡi các Sứ Giả và những ai theo các ngươi - làm đại diện trên trái đất này sau khi họ bị tiêu diệt, đó là sự gợi nhớ từ việc hủy diệt của những kẻ phủ nhận dối trá, rồi các Sứ Giả và những người có đức tin được định cư trên mặt đất sau khi những kẻ phủ nhận bị tiêu diệt, điều này dành cho ai nhớ đến sự vĩ đại của TA và sự giám sát của TA cũng như y sợ hãi hình phạt của TA');
INSERT INTO "vn_mokhtasar" VALUES(14,15,'14:15','Rồi các Sứ Giả cầu xin Allah ban cho Chúng được thắng lợi trước kẻ thù, và từng tên kiêu ngạo bất tuân vào lẻ phải điều bị thất bại, chớ nên đi theo vết xe đổ của hắn');
INSERT INTO "vn_mokhtasar" VALUES(14,16,'14:16','Trước mặt kẻ kiêu ngạo vào Ngày Phục Sinh là Hỏa Ngục, đó là nơi để xử phạt hắn, hắn sẽ bị bắt uống mủ của những kẻ nằm trong đó, vì không có bất cứ nước gì khác để uống, rồi hắn vẫn bị chịu hình phạt với cơn khát và các loại hình phạt khác nữa');
INSERT INTO "vn_mokhtasar" VALUES(14,17,'14:17','Hắn bị bắt uống hết lần này đến lần khác với vị đắng khủng khiếp, nhiệt độ sôi cực cao, và mùi hôi thúi bốc lên nồng nặc, không thể nào nuốt nổi. Và cái chết đến với hắn từ mọi khía cạnh với sự đau đớn tột cùng mà những gì hắn đang bị trừng phạt. Nhưng hắn không thể chết mà vẫn tiếp tục sống để chịu sự hình phạt. Và những hình phạt đau đớn khác đang chờ đợi hắn ở phía trước');
INSERT INTO "vn_mokhtasar" VALUES(14,18,'14:18','Hình ảnh những việc làm tốt đẹp của những kẻ phủ nhận như: bố thí, làm từ thiên, thương xót người yếu đuối, những việc làm đó của họ như những đống tro tàn mà gió sẽ thổi bay đi dữ dội vào một ngày bão tố, và bị cuốn đi một cách mạnh liệt, rồi bị phân tán khắp mọi nơi và mất hết mọi dấu vết. Và như vậy, những việc làm của những kẻ phủ nhận cũng sẽ bị vô dụng, không giúp ích được gì cho chúng vào Ngày Phục Sinh. bởi việc làm đó không dựa trên đức tin nên nó đi lạc xa với con đường chính đạo');
INSERT INTO "vn_mokhtasar" VALUES(14,19,'14:19','Há ngươi - hỡi nhân loại - không biết rằng chính vì chân lý mà Allah đã tạo nên các tầng trời và đất hay sao? Ngài đã không tạo ra chúng một cách vô nghĩa. Và nếu muốn, Ngài sẽ tiêu diệt các ngươi - hỡi con người - và Ngài sẽ mang đến một tạo hóa mới để thờ phượng và tuân theo Ngài thay thế các ngươi, và đó là việc hết sức đơn giản và dễ dàng đối với ngài');
INSERT INTO "vn_mokhtasar" VALUES(14,20,'14:20','Việc hủy diệt các ngươi và tái tạo lại một tạo hóa mới khác các ngươi không phải là điều khó khăn đối với Ngài, vì Ngài có toàn năng trong tất cả mọi việc, không gì có thể làm khó được Ngài');
INSERT INTO "vn_mokhtasar" VALUES(14,21,'14:21','Và tất cả các sinh linh được đưa ra khỏi ngôi mộ vào Ngày Được Chỉ Định. Rồi những người cô thế nói với những người lãnh đạo tối cao của họ: Hỡi những lãnh đạo mà chúng tôi đã theo, chúng tôi tiếp tục nghe theo các ngài, và chúng tôi cũng sẽ kết thúc cùng một kết cuộc như các ngài. Các ngài có thể giúp chúng tôi thoát khỏi hình phạt của Allah không? Rồi những vị lãnh đạo của họ đáp: Nếu như Allah chấp nhận hướng dẫn chúng tôi, thì chúng tôi đã dẫn dắt các người rồi, giờ thì tất cả chúng ta điều chịu hình phạt như nhau từ Ngài, vì chúng tôi đã lầm lỗi và đã làm cho các người lầm lỗi theo. Chúng ta điều như nhau cả dù cho có than vãn về hình phạt hay là ráng chịu đựng đi nữa thì chúng ta cũng sẽ không thoát khỏi sự trừng phạt');
INSERT INTO "vn_mokhtasar" VALUES(14,22,'14:22','Và Iblis lên tiếng khi mà nhóm người của Thiên Đàng đã được vào Thiên Đàng, và nhóm người Hỏa Ngục đã vào Hỏa Ngục: Quả thật, Allah đã giữ đúng lời hứa với các ngươi, Ngài đã thực hiện nó, và ta cũng đã hứa với các ngươi rồi ta thất hứa không hoàn thành được nó, và ta chẳng có quyền hạng gì với các ngươi ngoại trừ việc làm cho các ngươi phủ nhận Allah và lạc lối ở trần gian, ta đã kêu gọi các ngươi phủ nhận Allah, và cám dỗ các ngươi làm những việc xấu xa, rồi các ngươi đã nhanh chóng làm theo. Các ngươi đừng đỗ lỗi cho ta, mà hãy trách bản thân các ngươi, đó là điều đầu tiên các ngươi nên trách móc. Ta chẳng giúp được gì cho các ngươi để thoát khỏi sự trừng phạt, và các ngươi cũng chẳng giúp được gì cho ta. Quả thật, ta phủ nhận việc các ngươi đã cho ta là kẻ đối tác với Allah trong việc thờ phượng. Quả thật, những kẻ sai phạm - bằng việc tổ hợp với Allah trên trần gian và phủ nhận Ngài - sẽ có hình phạt dành cho họ vào Ngày Phục Sinh');
INSERT INTO "vn_mokhtasar" VALUES(14,23,'14:23','Và đối lập với số phận của những kẻ sai quấy, những người có đức tin và làm việc thiện thì sẽ được đưa vào các ngôi vườn Thiên Đàng có những tòa lâu đài mà bên dưới có các dòng sông chảy và những hàng cây, họ sẽ được ở đó đời đời theo ý nguyện của Thượng Đế của họ. Trong đó, mọi người chào hỏi lẫn nhau, các vị Thiên Thần cũng sẽ chào hỏi họ. Và Thượng Đế của họ cũng sẽ chào hỏi bằng lời chào bình an');
INSERT INTO "vn_mokhtasar" VALUES(14,24,'14:24','Chẳng lẻ ngươi không biết - hỡi Thiên Sứ - Allah đưa ra hình ảnh về Thuyết Độc Thần như thế nào sao! câu đó là: không có Thượng Đế nào đích thực ngoài Allah, nó như hình ảnh một loại cây tốt và đó là cây chà là, thân nó bám chặt mặt đất với hàng đống rể cắm sâu vững chắc để hút nước, nhánh của nó thì vươn cao lên trời để hút sương, và hít lấy không khí trong lành');
INSERT INTO "vn_mokhtasar" VALUES(14,25,'14:25','Loại cây tốt đẹp này sẽ cho ra trái cây tươi tốt vào mỗi giờ theo lệnh của Thượng Đế, và Allah đưa ra hình ảnh đó để nhân loại thấy mà may ra họ biết ghi nhớ');
INSERT INTO "vn_mokhtasar" VALUES(14,26,'14:26','Và hình ảnh về câu nói Shirk là xấu xa như hình ảnh của một loại cây xấu, và đó là cây dưa. Rễ của nó nhô lên, không bám chặt vào đất, và cũng không vươn cao lên bầu trời, nó sẽ chết và bị thổi bay bởi cơn gió. Rồi lời nói bất tin sẽ bị tan biến, và dù cho chủ nhân của lời nói bất tin có làm điều tốt đẹp thì nó cũng sẽ không đến được với Allah');
INSERT INTO "vn_mokhtasar" VALUES(14,27,'14:27','Allah củng cố vững chắc niềm tin cho những người có đức tin bằng câu nói Tawhid - Thuyết Độc Thần - vững chắc với trọn vẹn đức tin Iman trên cuộc sống trần gian cho đến khi họ chết đi và họ là những người có đức tin, và ở mảnh đất nhỏ bé trong ngôi mộ của họ khi được hỏi, và nó cũng sẽ củng cố vững chắc họ vào Ngày Sau. Và Allah sẽ bỏ mặc những kẻ làm điều sai quấy, bởi việc tổ hợp thần linh khác với Allah, và phủ nhận những gì đúng đắn và chỉ đạo. Allah làm những gì Ngài muốn, bằng sự công bằng Ngài bỏ mặt ai Ngài muốn cho họ lầm đường lạc lối, và dẫn dắt ai Ngài muốn bằng hồng phúc của mình, vì Ngài không ép buộc bất cứ ai');
INSERT INTO "vn_mokhtasar" VALUES(14,28,'14:28','Quả thật ngươi đã thấy tình trạng của những kẻ phủ nhận nơi Allah và các Sứ Giả của Ngài từ nhóm người Qurash khi họ quay lưng với ân phúc của Allah dành cho họ bằng việc tin tưởng những điều bị cấm, và với việc Muhammad {cầu xin sự bình an đến Người} được phái đến cùng họ, họ lại quay lưng với điều đó, phủ nhận ân phúc của Ngài khi họ không tin những gì Thượng Đế mang đến cho họ, rồi họ đưa những người theo họ vào nơi bị hủy diệt');
INSERT INTO "vn_mokhtasar" VALUES(14,29,'14:29','Nơi bị hủy diệt đó chính là Hỏa Ngục và họ sẽ bị đưa vào nơi đó, rồi họ sẽ biết được sức nóng của nó, và điều tồi tệ đó sẽ tiếp tục đến với họ');
INSERT INTO "vn_mokhtasar" VALUES(14,30,'14:30','Và những kẻ thờ đa thần đặt Allah ngang bằng với thứ chúng thờ phượng nhằm dẫn dắt những người theo chúng lạc khỏi con đường của Allah. Hãy bảo chúng - hỡi Thiên Sứ - các ngươi cứ thưởng thức những gì các ngươi muốn, và hãy truyền bá điều đó trong cuộc sống trần gian này. Vì chắc chắn rằng nơi trở về của các ngươi vào Ngày Phục Sinh sẽ là Hỏa Ngục, không thể nào là nơi khác được');
INSERT INTO "vn_mokhtasar" VALUES(14,31,'14:31','Ngươi - hỡi Thiên sứ - hãy bảo những người tin tưởng: Hỡi những người tin tưởng! Các ngươi hãy thực hiện việc dâng lễ nguyện một cách chu đáo, và hãy bố thí những bổng lộc mà Allah ban cho các ngươi bao gồm phần bố thí bắt buộc và tự nguyện, hãy làm kính đáo tránh mọi tai tiếng và thực hiện công khai để mọi người nhớ đến mà làm theo. Hãy làm những điều đó trước khi xảy đến Ngày mà không còn sự đổi trác và cứu giúp trước hình phạt của Allah, và cũng không còn tình bạn hữu để mà giúp đỡ lẫn nhau');
INSERT INTO "vn_mokhtasar" VALUES(14,32,'14:32','Allah là Đấng đã tạo ra các tầng trời và đất hoàn toàn không dựa trên cơ sở có sẵn, rồi Ngài cho mưa từ trên trời xuống, Ngài dùng nó để làm mọc ra các loại cây trái làm bổng lộc cho các ngươi - hỡi nhân loại -. Ngài đã chế ngự tàu thuyền cho các người để các ngươi thoải mái sử dụng nó, và Ngài đã chế ngự các dòng sông ngòi để các ngươi uống, và để phục vụ cho những đàn gia súc và mùa màng của các ngươi');
INSERT INTO "vn_mokhtasar" VALUES(14,33,'14:33','Và Ngài cũng đã chế ngự mặt trăng và mặt trời để chúng tiếp tục quay theo đúng quỹ đạo, rồi Ngài lại chế ngự việc kết thúc của ngày và đêm, ban đêm để các ngươi ngủ ngon và nghỉ ngơi, còn ban ngày để các ngươi hoạt động và làm việc');
INSERT INTO "vn_mokhtasar" VALUES(14,34,'14:34','Và Ngài đã ban cho các ngươi tất cả những gì các ngươi yêu cầu, và kể cả những gì các ngươi không yêu cầu. Nếu như các ngươi có đếm ân huệ mà Allah đã ban cho thì các ngươi không tài nào đếm hết được, vì nó quá nhiều. Rồi khi nhắc lại điều đó cho các ngươi thấy thì quả thật con người thật là sai quấy, đa số chối bỏ ân huệ của Allah Đấng Đáng Ca Ngợi và Tối Cao');
INSERT INTO "vn_mokhtasar" VALUES(14,35,'14:35','Ngươi - hỡi Thiên Sứ - hãy nhớ sau khi Ibrahim đã đưa Isma''il và mẹ của Người là bà Hajar đến sống ở thung lũng Makkah thì Ibrahim đã cầu xin: Lạy Thượng Đế của bề tôi! Xin Ngài hãy làm cho thành phố - Makkah - mà bề tôi đã đem gia đình mình sang định cư trở thành nơi yên bình, không có máu chảy, không ai bị đối xử bất công, và hãy tránh xa bề tôi và con cháu của bề tôi khỏi việc thờ phượng bụt tượng');
INSERT INTO "vn_mokhtasar" VALUES(14,36,'14:36','Lạy Thượng Đế của bề tôi! Quả thật, những tượng thần đã làm lạc hướng nhiều người, con người cứ nghĩ chúng sẽ giúp họ dành chiến thắng, thế là họ bị cám dỗ bởi điều đó, thế nên họ đã thờ phượng chúng. Nhưng ai đi theo bề tôi trong việc tôn thờ Allah duy nhất và tuân lệnh Ngài, thì chắc chắn y là người của bề tôi và theo bề tôi. Còn ai cải lại, không theo bề tôi trong việc thờ phượng Ngài duy nhất và nghe theo Ngài, thì quả thật, Ngài - lạy Thượng Đế của bề tôi - hãy tha thứ cho lỗi lầm của họ theo ý muốn của Ngài, vì Ngài là Đấng Hằng Tha Thứ, Đấng Khoan Dung với họ');
INSERT INTO "vn_mokhtasar" VALUES(14,37,'14:37','Lạy Thượng Đế của bầy tôi! Quả thật, bề tôi đã định cư con cháu của mình là Isma''il và con cháu của Y ở thung lũng Makkah không có đất trồng trọt, và không có nguồn nước kế bên Ngôi Đền Thiêng Liêng của Ngài. Lạy Thượng Đế của bầy tôi! bề tôi đã định cư con cháu của mình kế bên nó K''abah để chúng dâng lễ nguyện Salah nơi đó. Bởi vậy - lạy Thượng Đế - xin Ngài hãy làm cho tấm lòng của nhân loại vui mừng cho chúng khi đến mảnh đất này, bằng việc nuôi họ từ những hoa quả để họ có thể tạ ơn Ngài về ân phúc mà Ngài đã ban cho');
INSERT INTO "vn_mokhtasar" VALUES(14,38,'14:38','Lạy Thượng Đế bầy tôi! Quả thật, Ngài biết tất cả những gì bầy tôi giấu giếm cũng như biết mọi điều bầy tôi bộc lộ, vì không thể giấu được Allah bất cứ điều gì trên trời và dưới đất, Ngài luôn biết rõ mọi thứ. Và bề tôi không thể giấu được Ngài về nhu cầu cần thiết và sự nghèo khổ của bầy tôi');
INSERT INTO "vn_mokhtasar" VALUES(14,39,'14:39','Mọi lời cám ơn và ngợi khen đều dâng lên Allah Đấng đã chấp thuận lời cầu xin của bề tôi mà ban cho bề tôi nằm trong những người lương thiện, và ban cho bề tôi đứa con lớn là Isma''il với vợ Hajar, và Ishaaq với vợ Sarah. Quả thật, Thượng Đế của bề tôi là Đấng Hằng Nghe lời cầu xin mà ai cầu xin Ngài');
INSERT INTO "vn_mokhtasar" VALUES(14,40,'14:40','Lạy Thượng Đế! Xin Ngài làm cho bề tôi thành một người thực hiện việc dâng lễ nguyện Salah hoàn thiện về mọi mặt, và cho cả con cháu của bề tôi cũng được như vậy. Lạy Thượng Đế của bầy tôi! Xin Ngài hãy đáp lại lời cầu xin của bề tôi và chấp nhận nó');
INSERT INTO "vn_mokhtasar" VALUES(14,41,'14:41','Lạy Thượng Đế của bầy tôi! Xin Ngài hãy tha thứ tội lỗi cho bề tôi, cho cha mẹ bề tôi (Người cầu xin điều đó trước khi biết rằng cha mình là kẻ thù của Allah, rồi khi phát hiện điều đó thì Người liền cắt đứt quan hệ với cha), và hãy tha thứ tội lỗi cho những người có đức tin vào Ngày mà con người sẽ được phục sinh sống dậy để xét xử trước Thượng Đế của họ');
INSERT INTO "vn_mokhtasar" VALUES(14,42,'14:42','Và Ngươi - hỡi Sứ Giả - đừng nghỉ rằng khi Allah trì hoãn hình phạt đối với những kẻ sai quấy thì Ngài làm ngơ với những việc sai trái, dối trá, cự tuyệt con đường của Ngài và những việc sai quấy khác của họ. Không, Ngài luôn biết rõ tất cả điều đó, không điều gì có thể giấu được Ngài. Quả thật, Ngài hoãn lại hình phạt đối với họ cho đến Ngày Phục Sinh, đó là Ngày mà họ phải ngước lên sợ hãi những điều kinh dị mà họ thấy nó');
INSERT INTO "vn_mokhtasar" VALUES(14,43,'14:43','Khi con người được kêu dậy khỏi ngôi mộ của họ thì họ nhanh chóng chảy đến bên người kêu gọi, họ ngẩn đầu đăm chiêu nhìn lên trời, cặp mắt không hề quay sang nhìn mình, mà họ vẫn tiếp tục nhìn đăm đăm lên trời. Rồi tấm lòng của họ trống rộng không chút tâm trí, và họ cũng không hiểu gì về sự kinh hoàng của cảnh vật');
INSERT INTO "vn_mokhtasar" VALUES(14,44,'14:44','Người - hỡi Thiên Sứ - hãy cho cộng đồng của Ngươi biết về sự đáng sợ về hình phạt của Allah vào Ngày Phục Sinh. Rồi những kẻ mà bản thân họ làm điều sai quấy bằng việc phủ nhận Allah và tổ hợp thần linh khác với Ngài sẽ nói: Lạy Thượng Đế của bầy tôi! Xin Ngài hãy nới lại, xin Ngài hãy trì hoãn lại hình phạt dành cho bầy tôi, và hãy cho bầy tôi trở lại trần gian với thời hạn ngắn thôi thì bầy tôi sẽ tin tưởng nơi Ngài, bầy tôi cũng sẽ làm theo những gì mà các Thiên Sứ được Ngài mặc khải xuống cho Họ. Allah đáp lại họ bằng sự khiển trách: Há chẳng phải các ngươi đã từng thề thốt ở trên trần gian rằng các ngươi sẽ không bị dịch chuyển từ cuộc sống trần gian đến Ngày Tận Thế hay sao, há chẳng phải các ngươi là những kẻ phủ nhận những điều được diễn ra sau cái chết đó ư !?');
INSERT INTO "vn_mokhtasar" VALUES(14,45,'14:45','Và các ngươi đã ở những nơi mà những cộng đồng lầm lỗi thời trước các ngươi, tự bản thân họ đã gây lầm lỗi bằng việc phủ nhận Allah. Như cộng đồng của Hud, cộng đồng của Saleh, và TA đã nói rõ cho các ngươi biết về những gì mà TA đã trừng phạt họ. Rồi TA đưa ra những thí dụ cụ thể trong Kinh Sách của Allah để các ngươi rút kinh nghiệm, nhưng các ngươi chẳng rút ra được bài học gì từ nó.');
INSERT INTO "vn_mokhtasar" VALUES(14,46,'14:46','Và quả thật, những kẻ sinh sống ở nơi của những cộng đồng lầm lỗi trước đó, họ đã toan tính âm mưu giết hại Thiên Sứ Muhammad {cầu xin sự bình an đến Người}, và chối bỏ lời kêu gọi của Người. Nhưng Allah biết rõ sự toan tính của họ, không có bất cứ điều gì có thể che giấu được Ngài. Rồi sự tính toán của chúng trở nên yếu ớt, vì nó chưa thể lay chuyển nổi núi non và chưa thể làm lay chuyển những thứ khác, hoàn toàn trái ngược với hoạch định siêu việt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(14,47,'14:47','Ngươi - hỡi Thiên Sứ - chớ nghĩ rằng khi Allah đã hứa sẽ giúp đỡ các Thiên Sứ của Ngài giành chiến thắng và hiển hành tôn giáo của Ngài rồi Ngài lại thất hứa với Họ. Quả thật, Allah là Đấng Toàn Năng không điều gì có thể chiến thắng được Ngài, Ngài nắm giữ mọi quyền hành, Ngài sẽ trừng phạt thích đáng những ai chống đối Ngài và các Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(14,48,'14:48','Một hình phạt xác định giành cho những kẻ bất tin sẽ đến vào Ngày Phục Sinh, Ngày mà trái đất này sẽ hoàn toàn bị thay đổi bằng một trái đất khác với toàn màu trắng, và bầu trời cũng bị thay đổi bằng bầu trời khác, rồi con người sẽ tái sinh từ những ngôi mộ bằng những hình thể và những việc làm của họ để hiện diện trước mặt Allah, chỉ duy nhất tiếng nói và sự uy nghi của Ngài, Đấng Quyền Lực mà không ai có thể hơn được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(14,49,'14:49','Và Ngươi sẽ thấy - hỡi Thiên Sứ - Ngày mà trái đất sẽ bị thay thế bằng trái đất khác, và bầu trời cũng bị thay thế bằng bầu trời khác. Những kẻ bất tin và những kẻ thờ đa thần sẽ bị buộc chặt lại, tay và chân của họ sẽ bị xích lại bằng dây xích');
INSERT INTO "vn_mokhtasar" VALUES(14,50,'14:50','Họ sẽ bị bắt mặc bộ đồ làm bằng dầu hắc (là một loại vật liệu dễ cháy), và trên khuôn mặt của họ sẽ bị bao phủ bằng lửa.');
INSERT INTO "vn_mokhtasar" VALUES(14,51,'14:51','Để Allah thưởng, phạt cho mỗi linh hồn về những gì nó đã làm dù là điều tốt hay điều xấu. Quả thật Allah rất nhanh chóng trong việc xét xử cho những việc làm.');
INSERT INTO "vn_mokhtasar" VALUES(14,52,'14:52','Qu''ran được mặc khải cho Muhammad {cầu xin sự bình an đến Người} là thông điệp từ Allah gởi đến nhân loại, và để cảnh báo từ hình phạt và lời hứa tối cao, và cũng để cho nhân loại biết quả thật chỉ có Allah duy nhất mới xứng đáng được thờ phượng, vì vậy chỉ thờ phượng riêng Ngài thôi, không được tổ hợp Ngài với bất cứ thứ gì. Đó là điều giành cho những ai có hiểu biết để suy ngẫm, vì quả thật họ là những người hữu ích về điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(15,1,'15:1','{Alif-Lam-Mim} như đã được nói đến trong phần mở đầu của chương Al-Baraqah. Những câu Kinh cao quý và thiêng liêng là bằng chứng rằng Chúng đến từ nơi Allah. Đây là những câu Kinh Qur''an được trình bày rõ ràng về Tawhid (thuyết độc thần) và Shari''ah (hệ thống luật Islam).');
INSERT INTO "vn_mokhtasar" VALUES(15,2,'15:2','Vào Ngày Phục Sinh, có lẽ những kẻ vô đức tin sẽ mong ước mình là những người Muslim khi mà sự việc đã trở nên rõ ràng rằng họ đích thực đã lầm đường lạc lối vì đã không có đức tin lúc ở trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(15,3,'15:3','Hỡi Thiên Sứ Muhammad, Ngươi cứ bỏ mặc những kẻ phủ nhận này ăn uống giống như loài vật, cứ mặc chúng hưởng thụ những khoái lạc và niềm vui nhất thời của thế gian, cứ mặc chúng nuôi hy vọng điều tốt đẹp từ việc vô đức tin và những hành động sai quấy của chúng; bởi lẽ rồi đây chúng sẽ biết những thất bại và thiệt thòi mà chúng sẽ đối mặt khi trở về trình diện Allah vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(15,4,'15:4','Và TA (Allah) đã không ban sự hủy diệt xuống bất kỳ ngôi làng sai quấy nào trừ phi thời hạn của nó đã được ấn định trong sự hiểu biết của Allah; và thời hạn một khi được Allah ấn định thì nó sẽ không xảy ra sớm hơn hay muộn hơn thời khắc đã định.');
INSERT INTO "vn_mokhtasar" VALUES(15,5,'15:5','Không một cộng đồng nào hứng chịu sự diệt vong trước khi thời khắc của nó chưa đến hoặc có thể trì hoãn nó khi thời khắc đã đến đúng hẹn. Bởi thế, những kẻ làm điều sai quấy không cần phải hối thúc và thách thức Allah.');
INSERT INTO "vn_mokhtasar" VALUES(15,6,'15:6','Những kẻ vô đức tin của Makkah nói với Thiên Sứ Muhammad: Này hỡi kẻ tự nhận được ban cho Lời Nhắc Nhở, ngươi đích thực là một kẻ điên khùng khi tự nhận mình như thế.');
INSERT INTO "vn_mokhtasar" VALUES(15,7,'15:7','"Sau ngươi (Muhammad) không dẫn các Thiên Thần xuống gặp bọn ta để họ chứng minh cho ngươi hoặc để họ trừng phạt bọn ta bởi sự phủ nhận của bọn ta?!"');
INSERT INTO "vn_mokhtasar" VALUES(15,8,'15:8','Allah phán đáp trả yêu cầu của chúng về việc sao không cho các Thiên Thần xuống: TA không cử các Thiên Thần xuống trừ phi trừ phi thực sự cần thiết, chẳng hạn để trừng phạt các ngươi. Nhưng cho dù TA có cử phái các Thiên Thần xuống thì chắc chắn cũng không có đức tin, tuy nhiên, lúc đó TA sẽ sớm trừng phạt các ngươi khi các ngươi không có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(15,9,'15:9','Quả thật, TA (Allah) đã ban Kinh Qur''an này xuống và đặt Nó trong lòng của Muhammad để nhắc nhở nhân loại. Và chính TA sẽ bảo quản Nó khỏi sự bóp mép, thêm bớt và xuyên tạc.');
INSERT INTO "vn_mokhtasar" VALUES(15,10,'15:10','Hỡi Thiên Sứ Muhammad, quả thật, trước Ngươi TA (Allah) đã cử phái các Thiên Sứ đến các cộng đồng vô đức tin và chúng đã phủ nhận họ, bởi thế Ngươi không phải là người đầu tiên trong việc bị các cộng đồng của Ngươi phủ nhận.');
INSERT INTO "vn_mokhtasar" VALUES(15,11,'15:11','Và bất kỳ vị Thiên Sứ nào đến với cộng đồng của mình cũng đều bị họ phủ nhận và chế giễu.');
INSERT INTO "vn_mokhtasar" VALUES(15,12,'15:12','TA đã gieo sự phủ nhận và vô đức tin vào lòng của những cồng đồng trước và cũng như thế TA lại gieo nó vào trái tim của những kẻ thờ đa thần tại Makkah.');
INSERT INTO "vn_mokhtasar" VALUES(15,13,'15:13','Chúng không tin tưởng vào Kinh Qur''an này được ban xuống cho Muhammad,quả thật,đường lối của Allah về việc hủy diệt những kẻ phủ nhận những gì mà các vị Thiên Sứ mang đến đã đi qua,và đó là bài học cho những kẻ phủ nhận Ngươi (Muhammad)');
INSERT INTO "vn_mokhtasar" VALUES(15,14,'15:14','Những kẻ phủ nhận này, cho dù TA (Allah) có trình bày cho chúng những bằng chứng rõ ràng thế nào thì chúng vẫn thế, thậm chí cho dù TA có mở cửa trời cho chúng để chúng thường xuyên lên đó đi chăng nữa.');
INSERT INTO "vn_mokhtasar" VALUES(15,15,'15:15','Chúng chắc chắn vẫn không tin và chắc chắn chúng nói: quả thật, bọn ta chỉ hoa mắt mà thôi, không, bọn ta chỉ thấy điều đó là một sự mê hoặc.');
INSERT INTO "vn_mokhtasar" VALUES(15,16,'15:16','Quả thật, TA (Allah) đã tạo ra các vì sao trên bầu trời để nhân loại xác định phương hướng khi lữ hành trong bóng tối của đất liền và biển cả; và TA đã dùng chúng để trang hoàng cho bầu trời cho những người thích ngắm nhìn và để chứng tỏ quyền năng của TA.');
INSERT INTO "vn_mokhtasar" VALUES(15,17,'15:17','Và TA (Allah) bảo vệ bầu trời khỏi từng tên Shaytan đã bị trục xuất khỏi lòng thương xót của TA.');
INSERT INTO "vn_mokhtasar" VALUES(15,18,'15:18','Ngoại trừ tên Shaytan nào dám lên nghe lén tin tức và lệnh truyện của cõi trời xuống thế gian thì hắn sẽ gặp phải những ngôi sao lửa đuổi đánh.');
INSERT INTO "vn_mokhtasar" VALUES(15,19,'15:19','Và TA (Allah) đã trải rộng mặt đất bằng phẳng như một tấm thảm để nhân loại định cư,TA đã đặt trên mặt đất những quả núi kiên cố để giữ vững cho nhân loại và làm mọc ra các loại cây cối, hoa màu và thảo mộc, tất cả đều được định lượng theo chủ ý và ý nghĩa nhất định của TA.');
INSERT INTO "vn_mokhtasar" VALUES(15,20,'15:20','Hỡi nhân loại, TA (Allah) đã tạo ra cho các ngươi các phương tiện sinh sống trên trái đất từ thức ăn, đồ uống và cho cả những ai khác tức các loài sinh vật mà các ngươi không có trách nhiệm cung dưỡng cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(15,21,'15:21','Bất cứ mọi vật mà nhân loại cũng như các loại sinh vật khác hưởng lợi đều đến từ quyền năng tạo hóa của TA (Allah), tuy nhiên, mọi thứ đều được TA định lượng cụ thể theo ý muốn và sự thông thái của TA.');
INSERT INTO "vn_mokhtasar" VALUES(15,22,'15:22','Và TA (Allah) đã gửi những cơn gió lùa mây chứa nước, TA ban mưa xuống từ đám mấy chứa nước được lùa đó, TA đã ban cho các ngươi nguồn nước uống từ mưa; nhưng các ngươi - hỡi nhân loại - không phải là chủ kho giữ nước trong đất để chúng thành những dòng suối các và các giếng mà chính Allah mới là Đấng Chủ Kho.');
INSERT INTO "vn_mokhtasar" VALUES(15,23,'15:23','Và TA (Allah) chính là Đấng làm cho sống từ cái chết bằng cách tạo ra sự sống từ cái ''không'' và phục sinh con người từ cõi chết; và TA làm cho chết những cơ thể sống một khi đã mãn hạn, và chỉ một mình TA là Đấng còn mãi, TA kế thừa trái đất và những gì trong nó.');
INSERT INTO "vn_mokhtasar" VALUES(15,24,'15:24','Quả thật, TA (Allah) biết rất rõ ai là người được sinh ra trước và chết trước trong các ngươi và ai là người sinh ra sau và chết sau trong các ngươi, không có bất cứ điều gì có thể giấu giếm được TA.');
INSERT INTO "vn_mokhtasar" VALUES(15,25,'15:25','Hỡi Thiên Sứ Muhammad, quả thật Thượng Đế của Ngươi là Đấng triệu tập tất cả chúng vào Ngày Phục Sinh để thưởng những ai làm tốt và phạt những kẻ làm điều xấu. Quả thật, Ngài là Đấng Chí Minh trong việc điều hành và sắp xếp và là Đấng Thông Toàn tất cả mọi thứ, không có gì che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(15,26,'15:26','Quả thật, TA (Allah) đã tạo hóa Adam từ đất sét khô phát ra tiếng kêu nếu búng tay, đất sét này được lấy từ một loại Hama'' Masnun - đất sét đen sẽ đổi màu và mùi sau một thời gian dài.');
INSERT INTO "vn_mokhtasar" VALUES(15,27,'15:27','TA (Allah) đã tạo tổ tiên của loài Jinn - Jaann trước khi tạo Adam từ hơi lửa cực nóng');
INSERT INTO "vn_mokhtasar" VALUES(15,28,'15:28','Hỡi Thiên Sứ Muhammad, hãy nhớ lại khi Thượng Đế của Ngươi tuyên bố với các Thiên Thần và Iblis - lúc hắn đang ở cùng với họ -: Quả thật, TA sẽ tạo ra một người phàm từ đất sét khô phát ra tiếng kêu khi búng tay, đất sét này được lấy từ một loại Hama'' Masnun - đất sét đen sẽ đổi màu và mùi sau một thời gian dài.');
INSERT INTO "vn_mokhtasar" VALUES(15,29,'15:29','Khi TA đã uốn nắn thân hình của Y (Adam) và hoàn tất việc tạo hóa Y thì TA ra lệnh: Nào các ngươi hãy sụp xuống cúi đầu quỳ lạy Y để chấp hành theo lệnh của TA với ý nghĩa kính chào.');
INSERT INTO "vn_mokhtasar" VALUES(15,30,'15:30','Thế là, các Thiên Thần đều chấp hành mệnh lệnh Thượng Đế của họ, tất đều cúi đầu quỳ lạy Adam.');
INSERT INTO "vn_mokhtasar" VALUES(15,31,'15:31','Chỉ một mình Iblis - kẻ mà hắn đang ở cùng với các Thiên Thần - không chịu cùng với họ cúi đầu quỳ lạy Adam theo lệnh của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(15,32,'15:32','Allah phán với Iblis sau khi hắn không chịu cúi đầu quỳ lạy Adam: Sao ngươi không chịu phủ phục cùng với các Thiên Thần, điều gì đã ngăn cản ngươi trái lệnh của TA?');
INSERT INTO "vn_mokhtasar" VALUES(15,33,'15:33','Iblis nói một cách tự cao tự đại: thật là không đúng cho việc bắt bề tôi phủ phục quỳ lạy một con người phàm tục mà Ngài đã tạo ra từ đất sét khô, một loại đất sét đen thay đổi màu mùi sau đó.');
INSERT INTO "vn_mokhtasar" VALUES(15,34,'15:34','Allah phán với Iblis: Ngươi hãy đi ra khỏi Thiên Đàng, bởi quả thật ngươi là một tên đã bị trục xuất (khỏi lòng thương xót của TA).');
INSERT INTO "vn_mokhtasar" VALUES(15,35,'15:35','Và quả thật ngươi - Iblis - là một tên bị nguyện rủa và bị trục xuất khỏi lòng thương xót của TA cho đến Ngày Phán Xét Cuối Cùng.');
INSERT INTO "vn_mokhtasar" VALUES(15,36,'15:36','Iblis van nài Allah: Lạy Thượng Đế, xin Ngài gia hạn (tức để bề tôi sống) cho đến Ngày mà Ngài phục sinh con người từ cõi chết.');
INSERT INTO "vn_mokhtasar" VALUES(15,37,'15:37','Allah phán với Iblis: Ngươi được TA chấp thuận cho gia hạn.');
INSERT INTO "vn_mokhtasar" VALUES(15,38,'15:38','Ngươi được gia hạn cho đến Ngày mà tất cả tạo vật đều phải chết khi tiếng Còi đầu tiên được thổi lên.');
INSERT INTO "vn_mokhtasar" VALUES(15,39,'15:39','Iblis thưa: Lạy Thượng Đế, bởi việc Ngài xua đuổi bề tôi, bề tôi sẽ trang hoàng những tội lỗi trên trái đất cho con người và bề tôi sẽ dắt chúng đi lạc khỏi con đường Ngay Chính.');
INSERT INTO "vn_mokhtasar" VALUES(15,40,'15:40','Ngoại trừ những con người nào trong đám bề tôi của Ngài thành tâm thờ phường Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(15,41,'15:41','Allah phán: Đây là con đường Ngay Chính dẫn đến nơi TA.');
INSERT INTO "vn_mokhtasar" VALUES(15,42,'15:42','Quả thật, đối với bề tôi ngoan đạo thành tâm của TA thì nhà ngươi (Shaytan) không có quyền dụ dỗ họ trừ những kẻ lầm lạc đi theo nhà ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(15,43,'15:43','Và quả thật, Hỏa Ngục là điểm hẹn của Iblis và những kẻ lầm lạc đi theo hắn.');
INSERT INTO "vn_mokhtasar" VALUES(15,44,'15:44','Hỏa Ngục có cả thảy bảy cổng vào, mỗi cổng là lối vào danh riêng cho từng nhóm nhất định trong số những kẻ đi theo Iblis.');
INSERT INTO "vn_mokhtasar" VALUES(15,45,'15:45','Quả thật những người ngoan đạo kính sợ Allah, luôn thực thi và chấp hành các mệnh lệnh của Ngài và tránh xa những điều Ngài nghiêm cấm, sẽ được ở trong các Ngôi Vườn Thiên Đàng có những dòng suối chảy.');
INSERT INTO "vn_mokhtasar" VALUES(15,46,'15:46','Những người ngoan đạo sẽ được nghinh đón vào Thiên Đàng với lời: "Quí vị hãy đi vào một cách yên bình không một chút lo lắng và một cách an toàn khỏi những rắc rối".');
INSERT INTO "vn_mokhtasar" VALUES(15,47,'15:47','Và TA (Allah) sẽ xóa hết sự hận thù trong lòng của họ và thay vào đó là tình yêu thương huynh đệ tốt đẹp, họ sẽ ngồi nghỉ trên chiếc tràng kỹ, mặt hướng về nhau.');
INSERT INTO "vn_mokhtasar" VALUES(15,48,'15:48','Trong Thiên Đàng, họ sẽ không còn cảm giác mệt mỏi nữa (bởi họ không cần phải lao động vất vả cực nhọc mà chỉ có sự hưởng thụ khoái lạc) và họ sẽ không bị trục xuất ra khỏi đó vì họ sẽ sống đời đời và mãi mái trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(15,49,'15:49','Hỡi Thiên Sứ Muhammad, Ngươi hãy cho đám bề tôi của TA biết rằng quả thật TA là Đấng hằng tha thứ cho những ai quay về sám hối với TA bởi TA là Đấng Nhân Từ đầy lòng thương xót.');
INSERT INTO "vn_mokhtasar" VALUES(15,50,'15:50','Và Ngươi (Muhammad) hãy cho chúng biết rằng sự trừng phạt của TA rất đau đớn, bởi thế, chúng nên quay về sám hối với TA thì TA sẽ tha thứ cũng như bảo vệ an toàn khỏi sự trừng phạt của TA.');
INSERT INTO "vn_mokhtasar" VALUES(15,51,'15:51','Và Ngươi (Muhammad) hãy nói cho chúng biết về thông tin các vị khách quý của Ibrahim, những vị khách quý này là các Thiên Thần được cử đến báo cho Y tin mừng về một đứa con trai mà Ngài sẽ ban cho Y và đồng thời họ có nhiệm vụ hủy diệt cộng đồng người dân của Lut.');
INSERT INTO "vn_mokhtasar" VALUES(15,52,'15:52','Lúc các Thiên Thần vào, họ cho Salam, Ibrahim đáp lại bằng lời chào Salam tốt hơn lời chào của họ. Quả thật, Ibrahim sau đó đã tiếp đãi họ một con bê nướng bởi vì nghĩ rằng họ là người phàm nhưng khi thấy họ không ăn, Ibrahim nói: chúng tôi quả thật hoang mang lo sợ từ các quí vị');
INSERT INTO "vn_mokhtasar" VALUES(15,53,'15:53','Các Thiên Thần nói trấn an Ibrahim: Ngươi chớ đừng lo sợ, bởi quả thật chúng tôi chỉ mang tin mừng đến cho Ngươi rằng Ngươi sẽ được ban cho một đứa con trai khôn ngoan và kiến thức.');
INSERT INTO "vn_mokhtasar" VALUES(15,54,'15:54','Ibrahim nới với họ: Quả thật tôi quá ngạc nhiên cho tin mừng về đứa con trai mà các vị mang đến. Chẳng lẽ các vị mang tin mừng đến cho tôi về một đứa con trai trong lúc tôi đang ở độ tuổi già nua thế này sao? Các vị báo tin mừng cho tôi theo phương diện nào vậy?');
INSERT INTO "vn_mokhtasar" VALUES(15,55,'15:55','Các Thiên Thần nói với Ibrahim: Chúng tôi mang tin mừng đến cho ngươi là sự thật, không có gì phải nghi ngờ, bởi thế, ngươi chớ đừng tuyệt vọng cho tin mừng mà chúng tôi mang đến cho ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(15,56,'15:56','Ibrahim nói như một sự tiếp lời nhằm khẳng định: ai tuyệt vọng nơi lòng thương xót của Thượng Đế của mình thì đó là kẻ đã lệch khỏi con đường Ngay Chính của Allah!');
INSERT INTO "vn_mokhtasar" VALUES(15,57,'15:57','Ibrahim hỏi: Thế nhiệm vụ quan trọng của các ngài đến đây (ngoài chuyện báo tin mừng cho tôi) là gì hỡi các Sứ Giả (của Allah)?');
INSERT INTO "vn_mokhtasar" VALUES(15,58,'15:58','Các Thiên Thần Sứ Giả nói với Ibrahim: Quả thật, chúng tôi được Allah cử phái đến để tiêu diệt đám dân làm điều sai quấy,đó là đám dân của Lut.');
INSERT INTO "vn_mokhtasar" VALUES(15,59,'15:59','Chúng tôi đến để tiêu diệt đám dân của Lut ngoại trừ gia đình của Lut và những ai có đức tin đi theo Y, chúng tôi được lệnh phải đảm bảo an toàn cho tất cả họ.');
INSERT INTO "vn_mokhtasar" VALUES(15,60,'15:60','Tất cả gia đình của Lut được đảm bảo an toàn trừ mụ vợ của Y, chúng tôi quyết định cho mụ ta nằm trong số những người bị trừng phạt còn lại.');
INSERT INTO "vn_mokhtasar" VALUES(15,61,'15:61','Khi các Thiên Thần Sứ Giả vào gặp gia đình của Lut với hình hài của những người phàm');
INSERT INTO "vn_mokhtasar" VALUES(15,62,'15:62','Lut nói với các Thiên Thần Sứ Giả: quí vị là những vị khách lạ.');
INSERT INTO "vn_mokhtasar" VALUES(15,63,'15:63','Các Thiên Thần Sứ Giả nói với Lut: Ngươi chớ lo sợ, chúng tôi đến là để trừng phạt những kẻ còn nghi ngờ trong đám dân của Ngươi bằng một sự hủy diệt đến họ');
INSERT INTO "vn_mokhtasar" VALUES(15,64,'15:64','Chúng tôi đến với Ngươi bằng sự thật chứ không phải là một sự đùa giỡn, quả thật chúng tôi trung thực về điều mà chúng tôi đã thông báo với Người (từ sự trừng phạt này).');
INSERT INTO "vn_mokhtasar" VALUES(15,65,'15:65','Bởi thế, vào cuối đêm, Ngươi hãy đưa gia đình của Ngươi ra đi, Ngươi hãy đi ở phía sau lưng họ, Ngươi không để cho bất cứ ai quay đầu nhìn lại, Ngươi và gia đình của Ngươi cứ đi cho tới nơi mà Allah đã ra lệnh cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(15,66,'15:66','Và TA (Allah) đã mặc khải cho Lut biết sự việc trừng phạt bằng sự tiêu diệt những người còn lại là vào buổi sáng, tất cả sẽ bị tiêu diệt toàn bộ.');
INSERT INTO "vn_mokhtasar" VALUES(15,67,'15:67','Và người dân trong thị trấn Sodom vui mừng chạy ùa đến nhà của Lut vì những khách của Lut với mong muốn làm điều ô uế (quan hệ đồng tính).');
INSERT INTO "vn_mokhtasar" VALUES(15,68,'15:68','Lut nói với người dân: Quả thật, đây là những vị khách của ta, bởi thế, các ngươi chớ làm mất mặt ta về những điều các ngươi mong muốn với họ.');
INSERT INTO "vn_mokhtasar" VALUES(15,69,'15:69','Các ngươi hãy kính sợ Allah mà từ bỏ những việc làm ô uế và đồi bại này, các ngươi chớ hạ nhục ta bằng hành động bại hoại của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(15,70,'15:70','Đám dân của Lut nói với Lut: Há chẳng phải bọn ta đã cấm ngươi tiếp xúc với bất cứ ai trong nhân loại đó sao?');
INSERT INTO "vn_mokhtasar" VALUES(15,71,'15:71','Lut nói với đám dân của mình một cách xấu hổ trước những vị khách: Đây, những đứa con gái của ta, các ngươi hãy cưới chúng nó nếu các ngươi thực sự muốn giải tỏa nhu cầu dục vọng của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(15,72,'15:72','Hỡi Thiên Sứ Muhammad, TA (Allah) thề với sinh mạng của Ngươi rằng đám dân của Lut cứ lang thang vẩn vơ trong sự quá độ của dục vọng nơi bản thân chúng.');
INSERT INTO "vn_mokhtasar" VALUES(15,73,'15:73','Thế nên, một tiếng gầm dữ dội đã hủy diệt toàn bộ chúng vào lúc mặt trời đã mọc.');
INSERT INTO "vn_mokhtasar" VALUES(15,74,'15:74','Tiếp đó, TA (Allah) lật ngược chúng lên ngổn ngang và đổ lên chúng một trận mưa đá Sijjin (cứng như đất sét nung).');
INSERT INTO "vn_mokhtasar" VALUES(15,75,'15:75','Quả thật, trong sự việc được kể đó - việc đám dân của Lut bị tiêu diệt - là những bài học cho những người biết suy ngẫm.');
INSERT INTO "vn_mokhtasar" VALUES(15,76,'15:76','Và quả thật, thị trấn của đám dân của Lut vẫn còn nằm trên con đường mà ai đi ngang qua cũng sẽ nhìn thấy.');
INSERT INTO "vn_mokhtasar" VALUES(15,77,'15:77','Quả thật trong sự việc đó là những dấu hiệu cho những người có đức tin lấy làm bài học.');
INSERT INTO "vn_mokhtasar" VALUES(15,78,'15:78','Quả thật, đám dân của khu rừng Aykah - nhóm dân của Shu''aib - cũng là những đám người làm điều sai quấy, họ không có đức tin nơi Allah và phủ nhận vị Thiên Sứ của Ngài là Shu''aib.');
INSERT INTO "vn_mokhtasar" VALUES(15,79,'15:79','Bởi thế, TA (Allah) đã trừng phạt chúng - đám dân của Shu''aib; và quả thật, cả hai: thị trấn đám dân của Lut và những khu vực của đám dân Shu''aib, vẫn còn vết tích rõ rệt trên con đường mà mọi người qua lại.');
INSERT INTO "vn_mokhtasar" VALUES(15,80,'15:80','Quả thật, Thamud, và họ là đám dân của vùng núi Hijr (khu vực nằm giữa Hijaz - nay là Madinah - và Sham - nay chia làm bốn quốc gia: Palestine, Syria, Jordan và Li-băng), họ đã phủ nhận tất cả các vị Thiên Sứ và phủ nhận Nabi Saleh của họ.');
INSERT INTO "vn_mokhtasar" VALUES(15,81,'15:81','Và TA (Allah) đã ban cho chúng những bằng chứng và chứng minh cho sự trung thực của vị Nabi của chúng về những điều từ Thượng Đế đến với chúng, trong đó, có con lạc đà cái. Tuy nhiên, chúng không coi đó là những bằng chứng, chúng quay lưng cự tuyệt những bằng chứng đó.');
INSERT INTO "vn_mokhtasar" VALUES(15,82,'15:82','Chúng đã đục núi làm nhà một cách kiên cố và chúng cảm thấy sống trong đó thật an toàn khỏi những điều mà chúng lo sợ.');
INSERT INTO "vn_mokhtasar" VALUES(15,83,'15:83','Thế là một sự trừng phạt giáng lên chúng vào lúc sáng sớm.');
INSERT INTO "vn_mokhtasar" VALUES(15,84,'15:84','Những gì chúng có được từ tài sản và nhà cửa chẳng giúp ích được chúng tránh khỏi sự trừng phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(15,85,'15:85','TA (Allah) đã không tạo ra các tầng trời và trái đất cũng như những gì giữa chúng một cách vô nghĩa, mà quả thật TA tạo hóa mọi thứ đều mang chân lý và giá trị đích thực. Và quả thật, Giờ Tận Thế chắc chắn sẽ xảy đến, đó là sự thật. Bởi thế, hỡi Thiên Sứ Muhammad, Ngươi cứ bỏ mặc những kẻ phủ nhận Ngươi, Ngươi hãy tha thứ cho họ một cách tử tế và tốt đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(15,86,'15:86','Hỡi Thiên Sứ Muhammad, quả thật Thượng Đế của Ngươi là Đấng Tạo Hóa tất cả vạn vật, và Ngài là Đấng Toàn Tri');
INSERT INTO "vn_mokhtasar" VALUES(15,87,'15:87','Quả thật, TA (Allah) đã ban cho Ngươi (Muhammad) chương Kinh Al-Fatihah, chương Kinh mà Nó gồm bảy câu được lặp đi lặp trong các lễ nguyện Salah hằng ngày và Nó là Kinh Qur''an vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(15,88,'15:88','Ngươi - hỡi Muhammad - chớ trố mắt trước những thứ mà TA (Allah) đã ban cấp cho các hạng người trong số những kẻ vô đức tin từ những hưởng thụ tạm bợ rồi sẽ sớm tan biến, và Ngươi cũng chớ đau buồn cho việc chúng phủ nhận Ngươi, và Ngươi hãy nhân nhượng đối với những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(15,89,'15:89','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói: quả thật Ta là người cảnh báo về sự trừng phạt và làm rõ lời cảnh báo.đó');
INSERT INTO "vn_mokhtasar" VALUES(15,90,'15:90','Ta cảnh báo các ngươi rằng các ngươi sẽ bị trừng phạt giống như sự trừng phạt mà Allah đã giáng xuống cho những kẻ phân chia các Kinh Sách của Allah thành các phần để chúng tin một số phần và phủ nhận một số khác.');
INSERT INTO "vn_mokhtasar" VALUES(15,91,'15:91','Những kẻ đã chia Qur''an thành các phần và chúng bảo: đây là bùa chú, hoặc bói toán hoặc thơ ca');
INSERT INTO "vn_mokhtasar" VALUES(15,92,'15:92','Hỡi Thiên Sứ Muhammad, thề bởi Thượng Đế của Ngươi rằng vào Ngày Phán Xét TA (Allah) sẽ tra hỏi tất cả bọn chúng, những kẻ đã phân chia Qur''an.');
INSERT INTO "vn_mokhtasar" VALUES(15,93,'15:93','Chắc chắn TA (Allah) sẽ tra hỏi tất cả bọn chúng về những điều mà chúng đã làm từ việc vô đức tin và các việc làm trái lệnh trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(15,94,'15:94','Hỡi Thiên Sứ Muhammad, Ngươi hãy công bố những gì Allah ra lệnh cho Ngươi trong việc tuyên truyền và kêu gọi đến với Ngài, và Ngươi chớ quan tâm đến những lời nói cũng như những hành động của những kẻ thờ đá thần.');
INSERT INTO "vn_mokhtasar" VALUES(15,95,'15:95','Ngươi - Muhammad - chớ sợ chúng, quả thật, một mình TA (Allah) đủ giúp Ngươi chống lại các tên cầm đầu vô đức tin của Quraish.');
INSERT INTO "vn_mokhtasar" VALUES(15,96,'15:96','Những kẻ dựng lên cùng với Allah một thần linh nào đó để thờ phượng thì rồi đây chúng sẽ sớm biết kết cuộc cho việc Shirk (tổ hợp) xấu xa của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(15,97,'15:97','Và quả thật, TA (Allah) biết lòng Ngươi - hỡi Thiên Sứ Muhammad - đang se thắt lại vì sự phủ nhận của chúng cũng như sự nhạo báng của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(15,98,'15:98','Hãy quay về tìm sự cứu rỗi nơi Allah, và hãy tán dương ca tụng các thuộc tính hoàn hảo của Ngài, và hãy là người thờ phượng Ngài, dâng lễ nguyện Salah đến Ngài, và đó là cách điều trị tấm lòng của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(15,99,'15:99','Ngươi phải duy trì sự thờ phượng Thượng Đế của Ngươi cho tới khi cái chết đến với Ngươi, tức phải thờ phượng Allah cho đến chết.');
INSERT INTO "vn_mokhtasar" VALUES(16,1,'16:1','Lệnh trừng phạt các ngươi sắp được được Allah ban hành - hỡi những người vô đức tin -, bởi thế, các ngươi chớ hối thúc nó mau đến trước khi chưa phải lúc đã định. Thật vinh quang và trong sạch thay Allah, Ngài là Đấng Tối Cao và Vĩ Đại, sự tối cao và vĩ đại của Ngài vượt xa những gì mà những kẻ đa thần đã gán cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,2,'16:2','Allah cử phái các Thiên Thần mang Lời Mặc Khải của Ngài xuống cho những ai Ngài muốn từ các vị Thiên Sứ của Ngài: Hãy làm cho chúng hoảng sợ - hỡi nhân loại về sự tổ hợp (điều gì,vật gì) cùng với Allah,hãy cho họ biết rằng không có thần linh nào xứng đáng được thờ phụng ngoài TA cả.Bởi thế họ phải kính sợ TA - hỡi nhân loại -bằng cách tuân theo những điều TA ra lệnh và tránh xa những điều TA ngăn cấm');
INSERT INTO "vn_mokhtasar" VALUES(16,3,'16:3','Allah đã tạo hóa các tầng trời và trái đất khi chúng không là gì,vì chân lý, chứ Ngài không tạo chúng ra một cách vô nghĩa, Ngài đã tạo chúng ra vì một mục đích vĩ đại, và Ngài Tối Cao và Vĩ Đại hơn những gì chúng đã tổ hợp với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,4,'16:4','Allah đã tạo con người từ giọt tinh dịch qua các giai đoạn hình thành nhưng y lại là một kẻ thường hay tranh cãi và lý lẽ, dùng sự ngụy tạo để nhấn chìm chân lý; con người rõ ràng chỉ muốn tranh cãi, lập luận và lý lẽ.');
INSERT INTO "vn_mokhtasar" VALUES(16,5,'16:5','Gia súc từ lạc đà, bò, cừu dê, TA (Allah) đã tạo chúng ra làm nguồn cải thiện cuộc sống cho các ngươi - hỡi nhân loại -, và từ nguồn cải thiện này, các ngươi có được các vật dụng phủ ấm từ lông và da của chúng, ngoài ra các ngươi còn có những nguồn lợi khác từ sữa, da của chúng; và các ngươi có nguồn thực phẩm từ chúng.');
INSERT INTO "vn_mokhtasar" VALUES(16,6,'16:6','Từ nơi chúng - gia súc, các ngươi có được một cảnh tượng đẹp lúc các ngươi lùa chúng về chuồng vào buổi chiều cũng như lúc các ngươi lùa chúng ra đồng ăn cỏ vào buổi sáng.');
INSERT INTO "vn_mokhtasar" VALUES(16,7,'16:7','Các loại gia súc mà TA (Allah) đã tạo ra cho các ngươi giúp các ngươi vận chuyển đồ đạc, vật dụng nặng trong các chuyến đi dài đến với thành phố xa xôi, những nơi mà các ngươi phải rất vất vả lắm mới có thể đến được. Quả thật, Thượng Đế của các ngươi - hỡi nhân loại - là Đấng Rất Mực Nhân Từ, Rất Mực Khoan Dung đối với các ngươi khi mà Ngài chế ngự các loại gia súc này cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(16,8,'16:8','Allah đã tạo ra cho các ngươi ngựa, la và lừa để các ngươi cưỡi và chở đồ đạc cho các ngươi, chúng còn được dùng để trang hoàng cho các ngươi; và Ngài tạo ra các thứ mà các ngươi không hề biết.');
INSERT INTO "vn_mokhtasar" VALUES(16,9,'16:9','Allah có nghĩa vụ trình bày và giảng giải cho các ngươi con đường Ngay Chính dẫn đến sự hài lòng của Ngài, đó là Islam. Tuy nhiên, có những con đường lệch khỏi chân lý, đó là con đường của Shaytan, và tất cả con đường không phải con đường của Islam đều là con đường lệch lạc. Và nếu muốn, Allah thừa sức làm cho tất cả đều có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(16,10,'16:10','Allah là Đấng đã ban nước từ trên trời xuống qua những đám mây mưa, nhờ đó, các ngươi và các thú nuôi của các ngươi có nguồn nước để uống, và nhờ đó các cánh đồng cỏ mọc lên xanh tươi để các ngươi chăn nuôi gia súc của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(16,11,'16:11','Với nước mưa mà Allah ban xuống, Ngài làm mọc ra cho các ngươi các loại hoa mầu để các ngươi ăn và tiêu dùng, Ngài làm mọc ra cho các ngươi ô liu, chà là, nho và tất cả các loại cây trái khác. Quả thật, trong việc Ngài ban mưa xuống và làm mọc ra đủ loại cây trái và hoa mầu là bằng chứng cho quyền năng vô song của Ngài, là dấu hiệu cho những ai biết suy ngẫm về sự tạo hóa của Ngài để nhận thức về Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,12,'16:12','Allah đã làm ra cho các ngươi ban đêm để các ngươi nghỉ ngơi, ban ngày để các ngươi tìm kiếm kế sinh nhai, Ngài chế ngự cho các ngươi mặt trời và làm nó như một chiếc đèn chiếu sáng, mặt trăng như một ánh sáng dịu mát và ghi đếm thời gian, các ngôi sao làm vật chỉ hướng trong những u tối của đất liền và biển cả. Quả thật, trong sự việc đó là những dấu hiệu rõ ràng về quyền năng của Allah cho đám người thông suốt và thấu hiểu để nhận thức giá trị của chúng cũng như nhận thức về Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,13,'16:13','Allah đã tạo ra cho các ngươi các vật với những màu sắc khác nhau từ kim loại, động vật, thực vật. Trong sự việc đó là bằng chứng khẳng định quyền năng của Allah, là bài học dành cho những ai biết suy ngẫm để nhận thức về Allah.');
INSERT INTO "vn_mokhtasar" VALUES(16,14,'16:14','Allah đã chế ngự biển cả cho các ngươi và làm cho các ngươi có thể cưỡi trên lưng của nó, và Ngài để các săn bắt và có được các món thịt tươi ngon từ nó, và để các có được những món trang sức cho phụ nữ của các ngươi như ngọc trai chẳng hạn; và ngươi (con người) nhìn thấy những chiếc tàu thuyền vượt trùng dương, các ngươi cưỡi trên các chiếc tàu đó để đi tim nguồn lộc của Allah qua việc thương buôn, hy vọng rằng các ngươi biết tri ân Allah về các ân huệ mà Ngài đã ban cho các ngươi và cố gắng thờ phượng chỉ một mình Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,15,'16:15','Và Allah đã tạo ra những quả núi vững chắc trên mặt đất để cho mặt đất không rung chuyển; Ngài đã tạo ra các con sông chảy trên mặt đất để các ngươi và thú nuôi của các ngươi uống cũng như để tưới tiêu vườn tược của các ngươi; và Ngài đã chẻ ra trên mặt đất những con đường để các ngươi đi lại và đến những nơi các ngươi muốn đến một cách không bị lạc.');
INSERT INTO "vn_mokhtasar" VALUES(16,16,'16:16','Và Allah đã tạo ra trên bề mặt trái đất những nét đặc trưng của từng khu vực để xác định dòng chảy của các con sông, và Ngài đã tạo ra cho các ngươi những vì sao trên trời để các ngươi - hỡi nhân loại - nhờ đó mà xác định được phương hướng. vào ban đêm');
INSERT INTO "vn_mokhtasar" VALUES(16,17,'16:17','Chẳng lẽ Đấng Tạo Hóa những thứ này cũng như các tạo vật khác lại ngang bằng với những kẻ không tạo ra bất cứ một thứ gì ư? Chẳng lẽ các ngươi không nhận thức được sự Vị Đại của Allah, Đấng đã tạo hóa mọi vật, mới đáng được thờ phượng, và chỉ một mình Ngài mới đáng được cái quyền đó ư?');
INSERT INTO "vn_mokhtasar" VALUES(16,18,'16:18','Và nếu các ngươi - hỡi nhân loại - có cố gắng đếm các ân huệ mà Allah đã ban cho các ngươi thì chắc chắc các ngươi không tài nào đếm hết bởi các ân huệ của Ngài là vô số và đa dạng. Quả thật, Allah là Đấng Hằng Tha Thứ khi Ngài không bắt tội những kẻ vô ân, và Ngài là Đấng Nhân Từ khi Ngài không cắt đứt các ân huệ của Ngài đối với những ai trái lệnh Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,19,'16:19','Allah biết rõ những gì các ngươi che giấu - hỡi đám bề tôi - cũng như những gì các ngươi công khai tiết lộ, không có bất cứ điều gì có thế giấu giếm được Ngài, và Ngài sẽ đền bù xứng đáng cho tất cả mọi sự việc (thưởng hoặc phạt).');
INSERT INTO "vn_mokhtasar" VALUES(16,20,'16:20','Và những thần linh mà những kẻ đa thần thờ phượng ngoài Allah không hề tạo ra bất cứ thứ gì dù chỉ là một thứ gì đó nhỏ nhoi; và những ai thờ cúng chúng ngoài Allah thì chính họ đã tạo ra chúng, làm sao họ lại thờ cúng ngoài Allah những bục tượng mà chính họ đã làm ra bằng đôi tay của họ?');
INSERT INTO "vn_mokhtasar" VALUES(16,21,'16:21','Việc họ thợ phượng những thứ họ tự tạo ra bằng đôi tay của họ thì họ quả là những vật thể vô tri vô giác không có sự sống. Bởi thế, họ không biết bao giờ họ sẽ được dựng sống lại cùng với các thần linh của họ vào Ngày Phục Sinh để bị ném vào trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(16,22,'16:22','Đấng thờ phượng đích thực của các ngươi chỉ có một Đấng Duy Nhất, Ngài không có đối tác ngang vai, Ngài chính là Allah. Những kẻ không có đức tin nơi sự phục sinh để thưởng hoặc phạt thì trái tim của họ thường chống đối tính Duy Nhất của Allah và họ không sợ Ngài, trái tim của họ không tin nơi sự phán xét và thưởng phạt, họ luôn ngạo mạn trong việc chấp nhận điều chân lý và họ không chịu khuất phục Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,23,'16:23','Không có gì phải nghi ngờ rằng Allah chắc chắn biết rõ những gì những kẻ này công khai hay giấu giếm, không một điều gì có thế giấu giếm được Ngài, Ngài sẽ đền bù xứng đáng cho mọi sự việc, quả thật Ngài không yêu thích những kẻ tự cao tự đại trong việc từ chối thờ phượng và quy phục Ngài. Và đó là tội nghiệm trọng nhất đối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,24,'16:24','Khi có lời bảo những kẻ phủ nhận tính duy nhất của Đấng Tạo Hóa và phủ nhận sự Phục Sinh "Điều được ban xuống cho Muhammad là gì?" thì chúng mỉa mai đáp "Chẳng có gì cả mà chỉ là những câu chuyện cổ tích của người xưa thôi".');
INSERT INTO "vn_mokhtasar" VALUES(16,25,'16:25','Chắc chắn họ sẽ gánh lấy toàn bộ tội lỗi, kể cả tội lỗi của những do thiếu hiểu biết đã bị dắt đi lạc khỏi Islam.Thật tồi tệ thay cho việc họ phải gánh lấy tội lỗi của mình và cả tội lỗi của những ai đi theo họ!');
INSERT INTO "vn_mokhtasar" VALUES(16,26,'16:26','Quả thật, trước những kẻ này cũng đã có những kẻ vô đức tin bày mưu chống lại các vị Thiên Sứ của chúng nhưng Allah đã phá vỡ nền móng mà chúng đã xây dựng, Ngài cho cái mái của nó sụp xuống đè lên chúng và mang đến cho chúng một hình phạt mà chúng không ngờ tới, bởi lẽ chúng đã nghĩ rằng phần xây dựng của chúng rất kiên cố có thể bảo vệ chúng an toàn nhưng không, chúng đã bị hủy diệt.');
INSERT INTO "vn_mokhtasar" VALUES(16,27,'16:27','Rồi vào Ngày Phán Xét, Allah sẽ hạ nhục chúng bằng hình phạt và phán với chúng: "Đâu rồi những thần linh mà các ngươi tổ hợp với TA trong thờ phượng, những kẻ vì chúng mà các ngươi đã chống lại các vị Nabi của TA và những người có đức tin?" Những người có kiến thức đích thực về Thượng Đế nói: quả thật một hình phạt và sự nhục nhã của Ngày Phán Xét chắc chắn xảy ra đối với những kẻ vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(16,28,'16:28','Những kẻ mà khi Thiên Thần Chết và các Thiên Thần phụ tá bắt hồn đến bắt hồn họ trong lúc họ đang làm hại chính bản thân mình bằng sự vô đức tin nơi Allah, lúc đó, họ mới chịu thần phục và phủ nhận những gì họ đã vô đức tin và những điều tội lỗi, họ nghĩ rằng sự phủ nhận như thế sẽ giúp ích cho họ. Nhưng các Thiên Thần bảo họ: Các ngươi nói dối, quả thật các ngươi đã vô đức tin và đã làm điều tội lỗi, quả thật Allah biết rõ những gì các ngươi đã làm trên thế gian, không có điều gì có thể che giấu được Ngài, và Ngài sẽ phạt các ngươi một cách thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(16,29,'16:29','Và các Thiên Thần bảo họ: "Thôi, các ngươi hãy đi vào các cánh cửa của Hỏa Ngục theo các việc làm của các ngươi để sống mãi trong đó, quả thật, đó là chỗ ngụ tồi tệ nhất dành cho những kẻ tự cao tự đại trong việc không tin nơi Allah cũng như thờ phượng một mình Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,30,'16:30','Những người sợ Thượng Đế của họ trong việc thực hiện các mệnh lệnh của Ngài và tránh xa những điều Ngài ngăn cấm được hỏi: "Thượng Đế của các ngươi đã ban xuống điều gì cho vị Nabi Muhammad của các ngươi?" Họ đáp: "Allah đã ban xuống điều tốt vô cùng vĩ đại. Những ai thờ phượng Allah một cách tốt đẹp cũng như có hành vi cư xử tử tế đối với các tạo vật của Ngài trên cuộc sống thế gian thì sẽ được ban cho điều phúc lành và phần thưởng ở cõi Đời Sau được Allah chuẩn bị tốt đẹp hơn những gì trên thế gian. Chắc chắn những người sợ Thượng Đế của họ trong việc thực thi các mệnh lệnh của Ngài và tránh xa những điều Ngài ngăn cấm sẽ có một chỗ ngụ tốt đẹp nhất ở cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(16,31,'16:31','Những Ngôi Vườn Thiên Đàng mà họ sẽ vào, có những dòng sông chảy bên dưới các tòa lâu đài. Trong những Ngôi Vườn Thiên Đàng này, họ sẽ có được những gì họ mong ước từ thức ăn, đồ uống và mọi thứ khác. Phần thưởng như thế này sẽ được Allah tưởng thưởng cho những ai sợ Allah thuộc cộng đồng tín đồ Muhammad và những cộng đồng trước.');
INSERT INTO "vn_mokhtasar" VALUES(16,32,'16:32','Những ai mà Thiên Thần Chết và các Thiên Thần phụ tá bắt hồn trong tình trạng tốt sạch tức trái tim của họ không dính điều vô đức tin, các Thiên Thân sẽ nói với họ với lời: Chào an lành đến quí vị, quí vị đã được an toàn, nào xin mời vào Thiên Đàng bởi những điều mà quí vị đã làm trên thế gian từ niềm tin và việc làm ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(16,33,'16:33','Phải chăng những kẻ thờ đa thần vô đức tin chờ Thiên Thần Chết và các Thiên Thần phụ tá đến bắt hồn chúng và đánh vào mặt chúng hoặc chúng chờ lệnh trừng phạt của Allah đến với chúng trên thế gian này? Giống với hành động này của những người thờ đa thần ở Makkah, những người thờ đa thần trước chúng cũng đã hành động như thế và Allah đã tiêu diệt họ. Và Allah không bất công với họ khi Ngài tiêu diệt họ mà chính họ đã bất công với chính bản thân họ, họ đã muốn bị tiêu diệt bởi sự vô đức tin nơi Allah của họ.');
INSERT INTO "vn_mokhtasar" VALUES(16,34,'16:34','Các hình phạt sẽ giáng xuống chúng bởi những tội lỗi mà chúng đã làm và các hình phạt sẽ vây hãm lấy chúng bởi những điều mà chúng đã chế giễu khi chúng được nhắc nhở.');
INSERT INTO "vn_mokhtasar" VALUES(16,35,'16:35','Và những người tổ hợp với Allah các thần linh trong thờ phượng nói: "Nếu Allah muốn chúng tôi chỉ thờ phượng một mình Ngài thì chúng tôi đâu có tổ hợp với Ngài bất kỳ thần linh nào khác, chúng tôi và cả cha mẹ chúng tôi nữa, và nếu Ngài muốn chúng tôi không cấm một thứ gì đó thì chúng tôi đã không cấm theo ý Ngài nhưng chúng tôi vẫn cấm theo ý của chúng tôi." Giống như kiểu lập luận sai trái này, những người vô đức tin trước chúng cũng đã nói như thế. Nhưng các Thiên Sứ rõ ràng chỉ là những người công khai truyền đạt, và quả thật họ đã truyền đạt, cho nên những người vô đức tin không có lý do gì đổ lỗi cho sự tiền định khi mà Allah đã ban cho họ ý muốn và sự tự do lựa chọn cũng như Ngài đã gửi đến với họ các vị Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(16,36,'16:36','Và quả thật, mỗi cộng đồng, TA (Allah) đều cử phái đến với họ một Sứ Giả để y ra lệnh cộng đồng của mình thờ phượng một mình Allah và từ bỏ việc thờ phượng các bục tượng, các Shaytan và các tạo vật của Allah. Tuy nhiên, có người có đức tin nơi Allah và đi theo những gì vị Thiên Sứ đó mang đến dưới sự phù hộ của Allah; và có người vô đức tin nơi Allah và trái lệnh vị Thiên Sứ của Ngài, vì Ngài đã không phù hộ nên họ chắc chắn đã lầm lạc. Bởi thế, các ngươi hay du hành khắp trái đất để quan sát bằng cặp mắt của các ngươi về những kết cục của những người phủ nhận sự thật đã bị trừng phạt và tiêu diệt như thế nào.');
INSERT INTO "vn_mokhtasar" VALUES(16,37,'16:37','Hỡi Thiên Sứ Muhammad, dù Ngươi có nỗ lực và gắng sức để kêu gọi những người này, Ngươi cố gắng hết mình hướng dẫn họ, Ngươi tìm đủ mọi cách hầu đạt được điều đó, nhưng Ngươi hãy biết rằng quả thật Allah không hướng dẫn những ai mà Ngài làm cho họ lầm lạc, và họ sẽ không có bất kỳ ai ngoài Ngài có thể giúp họ tránh khỏi sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(16,38,'16:38','Những kẻ phủ nhận Sự Phục Sinh này đã thề thốt một cách nghiêm trọng nhằm quả quyết rằng "Allah không phục sinh bất kỳ ai đã chết". Nhưng họ đã không có bất cứ cơ sở và căn cứ nào cho điều đó cả. Chắc chắn không phải như điều họ khẳng định, Allah sẽ phục sinh tất cả những ai đã chết, đó là lời hứa thực sự, chắc chắn nó sẽ xảy ra đúng như thế, tuy nhiên, đa số nhân loại không biết rằng Allah làm sống lại những cơ thể chết, họ thường phủ nhận sự phục sinh.');
INSERT INTO "vn_mokhtasar" VALUES(16,39,'16:39','Vào Ngày Phục Sinh, Allah sẽ làm sống lại tất cả bọn họ mục đích để họ biết được sự thật mà họ đã từng tranh luận về Tawhid, sự phục sinh, và sứ mạng Nabi; và để những kẻ vô đức tin biết rằng chính họ mới là những kẻ đã không đúng về sự phục sinh.');
INSERT INTO "vn_mokhtasar" VALUES(16,40,'16:40','Quả thật, TA (Allah) khi muốn làm sống một sinh vật nào đã chết thì chắc chắn không gặp bất kỳ sự trở ngại nào, đơn giản TA chỉ cần nói với điều TA muốn "Hãy thành!" thì lập tức nó sẽ thành đúng như thế.');
INSERT INTO "vn_mokhtasar" VALUES(16,41,'16:41','Những người bỏ nhà cửa, gia đình, tài sản và quê hương của họ để rời khỏi xứ sở vô đức tin di cứ đến xứ sở Islam vì muốn làm hài lòng Allah sau khi họ đã bị áp bức từ những kẻ vô đức tin thì TA (Allah) sẽ ban cho một nơi ở tốt đẹp trên thế gian và Đời Sau một phần thưởng to lớn khác sẽ dành cho họ, đó là Thiên Đàng. Nếu những người không di cư biết được phần thưởng to lớn của những người di cư thì chắc chắn họ đã không do dự trong việc di cư.');
INSERT INTO "vn_mokhtasar" VALUES(16,42,'16:42','Những người di cư vì con đường chính nghĩa của Allah là những người đã chịu đựng sự bức hại của đám dân của họ, họ đã kiên nhẫn chịu đựng cho việc phải rời xa gia đình và quê hương của họ, họ đã kiên nhẫn trong việc tuân lệnh Allah, và họ đã phó thác tất cả mọi sự việc cho một mình Allah - Thượng Đế của họ. Bởi thế, Allah đã ban thưởng cho họ phần thưởng vĩ đại này.');
INSERT INTO "vn_mokhtasar" VALUES(16,43,'16:43','Hỡi Thiên Sứ Muhammad, trước Ngươi, TA (Allah) cũng đã gửi đến những người đàn ông phàm tục, TA đã mặc khải cho họ để họ trở thành những vị Thiên Sứ của TA, TA không cử phái các Thiên Thần là các vị Sứ Giả đến với nhân loại. Đây là đường lối của TA, nếu các ngươi phản bác điều đó thì các ngươi hãy hỏi đám dân Kinh Sách trước đây, họ sẽ nói cho các ngươi biết rằng các vị Thiên Sứ được cử phái đến là những con người phàm tục chứ không phải là các Thiên Thần.');
INSERT INTO "vn_mokhtasar" VALUES(16,44,'16:44','TA (Allah) đã cử phái các vị Thiên Sứ thuộc những người phàm tục này với những bằng chứng rõ rệt cũng như các Thiên Kinh Sách, và TA cũng đã ban cho Ngươi hỡi Thiên Sứ Muhammad Kinh Qur''an để Ngươi giảng giải rõ ràng cho nhân loại về những điều họ cần mong rằng họ biết suy ngẫm và tôn trọng và trân quý những gì Nó chứa đựng.');
INSERT INTO "vn_mokhtasar" VALUES(16,45,'16:45','Há những kẻ lập mưu ngăn cản con đường chính đạo của Allah cảm thấy an toàn trước việc Allah cho đất nuốt chửng họ giống như nó đã nuốt chửng Qarun hoặc cảm thấy an toàn trước một hình phạt xảy đến bất ngờ ư?!');
INSERT INTO "vn_mokhtasar" VALUES(16,46,'16:46','Hoặc một hình phạt sẽ đến với họ trong lúc họ đang trong những chuyến du hành và tìm kiếm kế sinh nhai và họ không thể trốn đi đâu cho thoát?!');
INSERT INTO "vn_mokhtasar" VALUES(16,47,'16:47','Hoặc họ cảm thấy an toàn khi Allah giáng xuống hình phạt bằng cách ban cho những nỗi sợ hãi về sự thất bát mùa màng và nguy hại tính mạng? Bởi quả thật, Allah toàn năng trong việc trừng phạt họ ở mọi hoàn cảnh và tình huống. Nhưng quả thật, Thượng Đế của các ngươi là Đấng Nhân Từ và Khoan Dung, Ngài không nhanh vội trừng phạt vì Ngài vẫn hy vọng đám bề tôi của Ngài sẽ quay về sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,48,'16:48','Sao những kẻ phủ nhận này không chịu quan sát các tạo vật của Ngài (Allah) mà suy ngẫm, bóng của các tạo vật nghiêng sang bên phải và bên trái theo sự chuyển động của mặt trời và sự di chuyển của nó vào ban ngày và mặt trăng vào ban đêm, tất cả đều phủ phục Thượng Đế của chúng bằng sự cúi đầu quỳ lạy thực thụ một cách hạ mình khiêm tốn.');
INSERT INTO "vn_mokhtasar" VALUES(16,49,'16:49','Tất cả mọi sinh vật trong trời đất đều phủ phục một mình Allah, và các Thiên Thần cũng chỉ phủ phục một mình Ngài, tất cả các Thiên Thần đều không tự cao tự đại trong việc thờ phượng và vâng lệnh Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,50,'16:50','Họ - các Thiên Thần, mặc dù họ luôn thờ phượng và vâng lệnh Allah mọi lúc mọi nơi - thì họ vẫn luôn sợ Thượng Đế của họ từ bên trên họ (Ngài ở bên trên bằng cơ thể, quyền lực và sự toàn năng của Ngài), họ tuyệt đối chấp hành và thực hiện mọi mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,51,'16:51','Allah phán với tất cả đám bề tôi của Ngài: "Các ngươi chớ thờ phượng hai thần linh, chỉ có một Đấng Thờ Phượng đích thực duy nhất, không có Đấng thứ hai và cũng không có đối tác ngang vai. Bởi thế, các ngươi hãy sợ TA và chớ sợ ai (vật gì) khác ngoài TA."');
INSERT INTO "vn_mokhtasar" VALUES(16,52,'16:52','Tất cả vạn vật trong trời đất đều là tạo vật của Allah, Ngài toàn quyền định đoạt và chi phối tất cả, mọi vật đều tuân lệnh, hạ mình phủ phục một mình Ngài, thế các ngươi lại sợ ai (vật) khác ngoài Ngài ư?! Các ngươi không được như thế, các ngươi phải chỉ sợ một mình Ngài thôi.');
INSERT INTO "vn_mokhtasar" VALUES(16,53,'16:53','Hỡi nhân loại, tất cả mọi ân huệ mà các ngươi có được từ tôn giáo đến đời sống thế tục đều đến từ nơi Allah chứ không đến từ ai (vật) khác ngoài Ngài. Rồi khi các ngươi gặp hoạn nạn, bệnh tật hoặc nghèo đói thì các ngươi cũng đều quay lại cầu cứu Ngài. Do đó, Đấng đẩy lùi tai họa cho các ngươi, ban cho các ngươi ân huệ mới là Đấng mà các ngươi phải thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(16,54,'16:54','Rồi khi Allah đáp lại lời khẩn cầu của các ngươi, Ngài cứu các ngươi thoát nạn thì một phần tử trong các ngươi lại tổ hợp với Ngài những thần linh khác, chúng thờ phượng những thần linh khác cùng với Ngài, thế có việc làm nào đáng trách hơn việc làm này?!');
INSERT INTO "vn_mokhtasar" VALUES(16,55,'16:55','Sự tổ hợp (điều gì,vật gì ) của họ cùng với Allah làm cho chúng phủ nhận ân huệ của Ngài đã ban cho họ. Bởi thế, Ngài phán với họ: Các ngươi cứ hưởng thụ những gì các ngươi đang có cho tới khi sự trừng phạt của TA đến với các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(16,56,'16:56','Những kẻ thờ đa thần trích một tài sản mà TA (Allah) ban cho họ dâng lên những thần linh bục tượng chẳng biết gì - vì chúng là vật vô tri vô giác, không mang lợi cũng chẳng gây hại. Thề bởi Allah, chắc chắn các ngươi hỡi những kẻ thờ đa thần, vào Ngày Phán Xét, các ngươi sẽ bị hạch hỏi về các thần linh bục tượng này của các ngươi cũng như việc các ngươi trích tài sản của các ngươi dâng lên chúng.');
INSERT INTO "vn_mokhtasar" VALUES(16,57,'16:57','Những kẻ thờ đa thần gán cho Allah có những đứa con gái, họ khẳng định rằng những đứa con gái đó chính là các Thiên Thần. Họ gán cho Ngài những đứa con gái, họ chọn cho Ngài điều mà họ không thích cho bản thân họ. Thật vinh quang và trong sạch thay Allah về điều mà họ gán cho Ngài trong khi họ chỉ thích bản thân mình có những đứa con trai. Có điều nào tội lỗi nghiêm trọng hơn sự việc này?!');
INSERT INTO "vn_mokhtasar" VALUES(16,58,'16:58','Khi ai đó trong đám những kẻ thờ đa thần này được báo tin về một bé gái vừa được chào đời thì y thay đổi sắc mặt, gương mặt trở nên đen sậm lại vì ghét tin vừa được báo, lòng dạ của y trở nên buồn bã và đau khổ. Vậy mà chúng dám gán cho Allah những thứ mà bản thân chúng không hài lòng!');
INSERT INTO "vn_mokhtasar" VALUES(16,59,'16:59','Y cảm thấy xấu hổ lẫn trốn người dân của mình vì tin xấu về sự chào đời của đứa con gái của mình, y tự nói với bản thân một cách thật chua xót: mình nên giữ đứa con gái này lại một cách nhục nhã hay mình phải chôn nó xuống đất? Thật xấu xa và tội lỗi cho điều mà những kẻ thờ đa thần đã khẳng định khi họ đã gán cho Thượng Đế của họ điều mà bản thân họ ghét bỏ.');
INSERT INTO "vn_mokhtasar" VALUES(16,60,'16:60','Những người vô đức tin không tin nơi cõi Đời Sau là một thuộc tính xấu: ngu dốt và vô đức tin; còn Allah mang những thuộc tính tối cao, hoàn mỹ đáng ca ngợi: hiểu biết, chí minh, giàu có không lệ thuộc bởi Ngài là Đấng Quyền Năng trong vương quyền của Ngài, không ai vượt trên quyền năng của Ngài, và Ngài là Đấng Sáng Suốt trong việc tạo hóa, điều hành và định đoạt.');
INSERT INTO "vn_mokhtasar" VALUES(16,61,'16:61','Nếu Allah trừng phạt nhân loại vì sự sai quấy và vô đức tin của họ thì chắc chắn Ngài sẽ không chừa bất kỳ loài sinh vật nào sống sót trên trái đất. Tuy nhiên, Ngài muốn trì hoãn việc trừng phạt họ đến một thời hạn mà Ngài đã ấn định. Bởi thế, khi nào thời hạn đó đã đến thì chắc chắn họ sẽ không thể trì hoãn hay làm cho nó sớm hơn dù chỉ là một khoảnh khắc.');
INSERT INTO "vn_mokhtasar" VALUES(16,62,'16:62','(Những kẻ thờ đa thần) đã qui cho Allah điều mà bản thân chúng ghét bỏ - việc có con gái - và chiếc lưỡi của chúng đã dối rằng chúng có một vị trí tốt đẹp ơ nơi Allah. Nhưng không, một điều chắc chắn rằng chỗ ở của chúng sẽ là Hỏa Ngục, chúng sẽ bị bỏ mặc trong đó và sẽ không bao giờ được trở ra khỏi đó.');
INSERT INTO "vn_mokhtasar" VALUES(16,63,'16:63','Allah thề với chính Ngài rằng quả thật Ngài đã cứ phái các vị Thiên Sứ đến với các cộng đồng trước Ngươi - hỡi Thiên Sứ Muhammad - nhưng Shaytan đã bày vẽ cho những cộng đồng đó thấy những việc làm của họ từ việc Shirk, vô đức tin và tội lỗi là những việc làm tốt đẹp và hấp dẫn đối với họ, và Shaytan trở thành kẻ đỡ đầu cho họ, cho nên vào Ngày Phán Xét chúng sẽ phải chịu hình phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(16,64,'16:64','Hỡi Thiên Sứ Muhammad, TA (Allah) đã ban Kinh Qur''an xuống cho Ngươi chỉ để Ngươi giảng giải cho tất cả nhân loại về những điều mà họ tranh cãi nhau từ Tawhid, sự phục sinh và các giáo luật; và Kinh Qur''an còn là sự hướng dẫn và hồng ân dành cho những người có đức tin nơi Allah, nơi các vị Thiên Sứ của Ngài và nơi những gì trong Qur''an. Bởi thế, những người này là người được điều chân lý mang lại điều hữu ích.');
INSERT INTO "vn_mokhtasar" VALUES(16,65,'16:65','Và Allah đã ban mưa từ trên trời xuống để làm sống lại đất đai đã bị chết khô bằng cách làm mọc ra cây cối và thảo mộc. Trong sự việc ban mưa từ trên trời xuống và làm mọc ra cây cối và thảo mộc là bằng chứng rõ ràng cho quyền năng của Allah đối với đám người chịu nghe và suy ngẫm lời phán của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(16,66,'16:66','Hỡi nhân loại, trong các loài gia súc của các ngươi từ lạc đà, bò, dê cừu có một bài học dành cho các ngươi khi mà TA (Allah) đã ban cho các ngươi thức uống từ chất nằm trong bụng của chúng, giữa phân và máu. Đó là sữa tinh khiết thơm ngon và tốt lành cho người uống.');
INSERT INTO "vn_mokhtasar" VALUES(16,67,'16:67','Và các ngươi cũng có bài học từ những trái chà là và nho khi mà các ngươi dùng chúng để làm ra chất gây say như rượu và nó không tốt lành, và để làm ra nguồn lương thực tốt lành như chà là khô, nho khô, giấm và mật đường. Quả thật trong sự việc đó là bằng chứng về quyền năng của Allah dành cho đám người thông hiểu.');
INSERT INTO "vn_mokhtasar" VALUES(16,68,'16:68','Hỡi Thiên Sứ Muhammad, Thượng Đế của Ngươi (Allah) đã mặc khải cho loài ong mật: các ngươi hãy xây tổ trên núi, trên cây cối và trên những thứ mà nhân loại dựng lên.');
INSERT INTO "vn_mokhtasar" VALUES(16,69,'16:69','Allah phán tiếp với loài ong mật: "Rồi các ngươi hay ăn (hút mật) từ các loại trái cây và đi theo các con đường mà Thượng Đế các ngươi đã chỉ cho các ngươi." Thế là từ bụng của loài ong đó cho ra các loại mật ong mang màu sắc khác nhau, trong đó có chứa dược liệu chữa bệnh cho nhân loại. Quả thật, trong sự việc Allah mặc khải cho loài ong mật, trong mật ong được tiết ra từ bụng của loài ong mật là bằng chứng về quyền năng của Allah cho đám người biết suy ngẫm.');
INSERT INTO "vn_mokhtasar" VALUES(16,70,'16:70','Allah đã tạo các ngươi - hỡi nhân loại - không theo một kiểu mẫu đã từng có trước đó, rồi Ngài cho các ngươi chết khi đến thời hạn; trong các ngươi, có người được cho sống đến giai đoạn tồi tệ nhất của tuổi đời, đó là lúc già yếu, giai đoạn mà y không còn biết gì nữa (lẩm cẩm, lú lẫn) sau khi đã biết nhiều thứ. Quả thật, Allah là Đấng Toàn Tri, Ngài thông toàn tất cả mọi sự việc, không một điều gì từ việc làm của đám bề tôi của Ngài có thể giấu giếm được Ngài; và Ngài là Đấng Toàn Lực, không có bất cứ thứ gì có thể làm cho Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(16,71,'16:71','Allah đã ưu đãi nguồn bổng lộc của Ngài cho một số người này hơn một số người kia, Ngài đã làm cho các ngươi thành kẻ giàu người nghèo, kẻ chủ nô và người thì tôi tớ. Và những ai được Allah ưu đãi nguồn bổng lộc không chịu chia sẻ những gì mà Allah ban cho họ với những người tôi tớ của họ để tất cả đều cùng nhau hưởng bổng lộc của Ngài thì đó là hành động phủ nhận ân huệ của Ngài. Có sự bất công nào hơn điều này, có sự phủ nhận nào nghiêm trọng hơn sự phủ nhận ân huệ của Allah?!');
INSERT INTO "vn_mokhtasar" VALUES(16,72,'16:72','Hỡi nhân loại, Allah đã tạo từ bản thân các ngươi những người vợ để các ngươi sống an lành với họ, và từ những người vợ của các ngươi Ngài đã tạo ra con cái và cháu chắt cho các ngươi; và Ngài đã ban cấp cho các người nguồn thức ăn tốt lành như thịt, hạt, trái quả. Há đối với các thần linh bục tượng thì chúng tin còn đối với bao nhiêu ân huệ của Allah mà chúng không thể đếm hết thì chúng lại phủ nhận và vô ơn để rồi chúng không tin nơi một mình Ngài ư?!');
INSERT INTO "vn_mokhtasar" VALUES(16,73,'16:73','Những kẻ đa thần thờ phượng ngoài Allah những bục tượng và những bục tượng đó không ban cấp cho họ bổng lộc từ các tầng trời hay trái đất và dĩ nhiên các bục tượng đó không nắm bất cứ quyền hành nào cho sự việc đó bởi lẽ chúng những vật vô tri vô giác, không có sự sống cũng không có kiến thức.');
INSERT INTO "vn_mokhtasar" VALUES(16,74,'16:74','Hỡi nhân loại, các ngươi chớ đừng so sánh Allah với các bục tượng không gây hại cũng không mang lợi này. Allah không hề có sự tương đồng nào với chúng để các ngươi tổ hợp chúng với Ngài trong thờ phượng. Quả thật, Allah biết rõ những thuộc tính hoàn mỹ và tối cao của Ngài còn các ngươi thì không biết gì về điều đó. Bởi thế, các ngươi cứ tổ hợp các thần linh bục tượng với Ngài trong việc thờ phượng và cầu nguyện.');
INSERT INTO "vn_mokhtasar" VALUES(16,75,'16:75','Allah đưa ra hình ảnh thí dụ cho những kẻ thờ đa thần: Một người nô lệ yếu hèn không chút quyền hành nào. Một người tự do được Ngài ban cho tài sản tốt lành, y có thể chi dùng tài sản của mình tùy ý công khai hay thầm kín. Hai người này có ngang bằng nhau không? Dĩ nhiên là không thể nào ngang bằng nhau được. Nếu vậy thì tại sao những kẻ thờ đa thần lại so sánh ngang bằng giữa Allah, Đấng có quyền chi phối vương quyền tùy ý và các bục tượng vô tri vô giác và vô năng của họ?! Mọi lời ca ngợi và tán dương đều xứng đáng dành cho Ngài, tuy nhiên, đa số những kẻ thờ đa thần không biết về tính duy nhất của Allah cũng như chỉ có một mình Ngài mới đáng được thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(16,76,'16:76','Allah đưa ra một hình ảnh thí dụ khác về hai người: Một người câm không có khả năng nghe, nói và hiểu, sự câm điếc của y không thể làm gì cho bản thân cũng như cho người khác, y luôn là gánh nặng cho người có trách nhiệm với y, mỗi khi y được giao công việc gì thì y đều không hoàn thành. Người với tình trạng thế này có ngang bằng với một người có khả năng nghe và nói một cách lành lặn, tự giúp ích được cho bản thân, sai bảo người khác thực hiện công lý, luôn đi đúng đường chăng?! Chắc chắn là không ngang bằng nhau, vậy tại sao, các ngươi - hỡi những kẻ thờ đa thần - lại so sánh ngang bằng giữa Allah, Đấng với các thuộc tính hoàn mỹ và tối cao và toàn quyền chi phối mọi thứ với những bục tượng của các ngươi, những thứ mà chúng không có khả năng nghe nói và cũng không có khả năng gây hại hay mang lại lợi ích gì?!');
INSERT INTO "vn_mokhtasar" VALUES(16,77,'16:77','Những điều vô hình trong các tầng trời và những điều vô hình trong trái đất đều thuộc về Allah, chỉ có Ngài mới biết rõ về chúng, không một ai trong tạo vật của Ngài biết được. Và giờ khắc tận thế sẽ xảy ra sớm thôi, nếu Ngài muốn Ngài cho nó xảy ra sớm giống như một cái nháy mắt hoặc sớm hơn nữa bởi quả thật Ngài là Đấng Toàn Năng trên tất cả mọi thứ, không có bất cứ thứ gì có thể làm Ngài bất lực, một điều gì đó Ngài muốn thì Ngài cần phán "Hãy thành!" thì nó sẽ thành giống như y của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,78,'16:78','Hỡi nhân loại, Allah đã mang các ngươi ra từ bụng mẹ của các ngươi trong tình trạng không biết gì, các ngươi là những đứa trẻ sơ sinh không biết được bất cứ điều gì rồi Ngài làm cho tai của các ngươi nghe được, làm cho mắt của các ngươi nhìn thấy được và làm cho trái tim của các ngươi hiểu được, Ngài mong rằng các ngươi biết tri ân Ngài về những ân huệ mà Ngài đã ban cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(16,79,'16:79','Những kẻ đa thần không quan sát thấy loài chim thăng bằng trên không trung với đôi cánh mà Allah đã ban cho chúng hay sao? Không ai có thể giữ thăng bằng cho chúng trên không trung như thế ngoại trừ một mình Allah. Trong việc những chú chim thăng bằng trên không trung như thế là dấu hiệu nhận thức về sự toàn năng của Allah cho đám người có đức tin nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,80,'16:80','Allah, Đấng Tối Cao đã làm cho các ngươi biết xây những ngôi nhà từ đá và các vật liệu khác để các ngươi cư ngụ và yên nghỉ. Ngài đã làm cho các ngươi biết dùng da của lạc đà, bò, dê cừu làm nên những căn lều trong sa mạc để các ngươi nhẹ bớt gánh nặng khi di dời chỗ ở và khi dừng chân nghỉ ngơi trong những chuyến đi. Và Ngài đã làm cho các ngươi biết dùng lông cừu, lông lạc đà và lông dê đực làm thành các vật dụng cũng như các vật trang trí trong nhà mà các ngươi hưởng lợi đến một thời gian nhất định.');
INSERT INTO "vn_mokhtasar" VALUES(16,81,'16:81','Allah đã tạo ra cho các ngươi bóng mát từ cây cối và các công trình để các ngươi tránh nắng. Ngài đã tạo ra những quả núi với những hang động để các ngươi làm nơi trú ẩn tránh được cái rét, cái nóng và cả kẻ thù. Ngài đã làm cho các ngươi biết làm ra những chiếc áo từ bông và những thứ khá để bảo vệ bản thân khỏi cái nóng và cái rét; và Ngài đã làm cho các ngươi biết làm ra những chiếu áo giáp, những cái khiên để bảo vệ bản thân khi đánh chiến với kẻ thù. Với những ân huệ đó, Ngài muốn hoàn thiện hồng phúc của Ngài cho các ngươi mong rằng các ngươi qui phục một mình Ngài và không tổ hợp với Ngài bất cứ thứ gì.');
INSERT INTO "vn_mokhtasar" VALUES(16,82,'16:82','Nếu họ không có đức tin và không chịu tin nơi những gì Ngươi mang đến - hỡi Thiên Sứ Muhammad - thì Ngươi hãy biết rằng thật ra Ngươi chỉ có nhiệm vụ truyền đạt một cách rõ ràng chứ Ngươi không chịu trách nhiệm cho việc họ được hướng dẫn hay không.');
INSERT INTO "vn_mokhtasar" VALUES(16,83,'16:83','Những kẻ đa thần nhận biết ân huệ của Allah đã ban cho họ, một trong số đó là việc Thiên Sứ Muhammad được gửi đến cho họ, nhưng rồi họ phủ nhận ân huệ của Ngài và không chịu tri ân bằng cách phủ nhận Thiên Sứ của Ngài; và đa số bọn họ là những kẻ phụ ơn Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,84,'16:84','Hỡi Thiên Sứ Muhammad, hãy nghĩ đến Ngày mà Allah dựng lên vị Thiên Sứ của mỗi cộng đồng mà Ngài đã cử phái đến với họ để Y làm chứng cho đức tin của những người có đức tin và sự vô đức tin của những người không có đức tin. Sau thời khắc đó, Ngài không cho phép những kẻ vô đức tin cáo lỗi về những gì họ đã làm và Ngài cũng không cho phép trở lại trần gian để làm những điều mà Ngài hài lòng, bởi lẽ thế giới cõi Đời Sau là nơi của sự thanh toán chứ không phải là nơi của hành động.');
INSERT INTO "vn_mokhtasar" VALUES(16,85,'16:85','Và khi những kẻ sai quấy đa thần tận mắt chứng kiến sự trừng phạt (Hỏa Ngục) thì họ sẽ không được giảm nhẹ và sẽ không được trì hoãn bất cứ thời khắc nào, và họ sẽ vào ở trong đó đời đời và mãi mãi.');
INSERT INTO "vn_mokhtasar" VALUES(16,86,'16:86','Vào Ngày Sau, khi những kẻ thờ đa thần nhìn thấy các thần linh mà họ thờ phượng ngoài Allah, họ sẽ thưa với Ngài: Lạy Thượng Đế của bầy tôi, đây là những thần linh mà bầy tôi đã thờ phượng ngoài Ngài. Họ nói điều đó vì muốn những thần linh mà họ đã tổ hợp gánh tội lỗi của họ, nhưng Allah làm cho các thần linh đó nói chuyện, chúng đáp lại họ với lời: này hỡi những kẻ thờ đa thần, quả thật các ngươi là những kẻ nói dối bởi Allah không có đối tác ngang vai.');
INSERT INTO "vn_mokhtasar" VALUES(16,87,'16:87','Và Ngày Sau, những kẻ đa thần mới chịu hạ mình quy phục một mình Allah và những thần linh bục tượng mà họ làm ra sẽ bỏ họ đi mất.');
INSERT INTO "vn_mokhtasar" VALUES(16,88,'16:88','Những ai vô đức tin nơi Allah và cản trở thiên hạ rời xa con đường chính đạo của Allah, Allah sẽ trừng phạt họ bởi tội xấu xa của bản thân họ và tội lôi kéo người khác đến với sự xấu xa cùng với họ bằng các hình phạt chồng chất lên nhau.');
INSERT INTO "vn_mokhtasar" VALUES(16,89,'16:89','Hỡi Thiên Sứ Muhammad, Ngươi hãy nghĩ tới Ngày mà TA (Allah) sẽ phục sinh trong mỗi cộng đồng một vị Thiên Sứ để làm chứng đối chất cộng đồng của mình về việc họ đã vô đức tin hoặc đã có đức tin. Mỗi vị Thiên Sứ đều thuộc loài với họ và nói bằng tiếng của họ. Và TA mang Ngươi - hỡi Thiên Sứ Muhammad - đến làm chứng cho tất cả các cộng đồng. TA đã ban cho Ngươi Kinh Qur''an để Ngươi trình bày rõ ràng về những điều Halal (được phép), Haram (không được phép), sự tưởng thưởng, sự trừng phạt và những thứ khác. TA đã ban Nó xuống cho Ngươi làm nguồn Chỉ Đạo nhân loại đến với chân lý, là một Hồng Ân cho những ai có đức tin và làm theo những gì chứa đựng trong Nó, và để làm nguồn tin mừng cho những người có đức tin mong đợi và khao khát một phần thưởng nơi cõi Vĩnh Hằng.');
INSERT INTO "vn_mokhtasar" VALUES(16,90,'16:90','Quả thật, Allah ra lệnh đám bề tôi của Ngài phải công bằng có nghĩa là người bề tôi phải thi hành các nghĩa vụ đối với Allah và các nghĩa vụ đối với đám bề tôi của Ngài, không được ưu ái người này hơn người kia trong việc phân xử mà phải công tâm; Ngài ra lệnh phải tử tế, có nghĩa là người bề tôi giúp đỡ và hỗ trợ cho người cần giúp đỡ và hỗ trợ, xí xóa và bỏ qua cho người cư xử bất công với mình; Ngài ra lệnh phải cho và chia sẻ đến bà con ruột thịt những gì họ cần; Ngài cấm tất cả mọi điều ô uế từ lời nói như lời dâm ô, hành động như quan hệ tình dục ngoài hôn nhân; Ngài cấm mọi hành vi trái với giáo lý của Ngài; và Ngài cấm bất công và tự cao tự đại với thiên hạ. Allah chỉ dạy các ngươi về những gì Ngài ra lệnh và những gì Ngài cấm trong câu Kinh này với hy vọng các ngươi biết lưu tâm ghi nhớ.');
INSERT INTO "vn_mokhtasar" VALUES(16,91,'16:91','Các ngươi hãy thực hiện tất cả mọi giao ước mà các ngươi đã giao ước với Allah hoặc giao ước với nhân loại; các ngươi chớ đừng vi phạm lời thề sau khi các ngươi đã thề thốt nghiêm trọng với Allah, quả thật, các ngươi đã nhận lấy Allah làm Đấng chứng giám cho các ngươi về điều các ngươi đã thề thốt. Quả thật, Allah biết rõ tất cả những gì các ngươi làm, không một điều gì có thể che giấu được Ngài và Ngài sẽ đền bù thích đáng cho tất cả mọi việc (bằng hình phạt hoặc sự tưởng thưởng).');
INSERT INTO "vn_mokhtasar" VALUES(16,92,'16:92','Các ngươi chớ đừng phá vỡ giao ước một cách ngốc nghếch thiếu suy nghĩ, giống như một người phụ nữ ngu ngốc đã mệt mỏi trong việc se thắt sợi len hoặc sợi bông nhưng sau đó lại tháo tung nó ra, cứ se rồi lại tháo và không đạt được điều mong muốn. Các ngươi dùng lời thề thốt dể lừa dối lẫn nhau vì muốn cộng đồng của các ngươi trở nên nhiều hơn và mạnh hơn cộng đồng của kẻ thù. Quả thật, TA (Allah) chỉ muốn thử thách các ngươi qua việc thực hiện các giao ước xem các ngươi giữ đúng các giao ước hay phá vỡ chúng?! Rồi vào Ngày Phán Xét, Allah sẽ cho các ngươi biết rõ những điều mà các ngươi đã từng tranh chấp nhau trên thế gian để các ngươi biết ai đúng và ai sai, ai thật và ai giả dối.');
INSERT INTO "vn_mokhtasar" VALUES(16,93,'16:93','Và nếu muốn, Allah đã làm cho các ngươi thành một cộng đồng duy nhất nhưng Ngài đánh lạc hướng kẻ nào Ngài muốn và hướng dẫn người nào Ngài muốn. Và chắc chắn, các ngươi sẽ bị tra hỏi về những điều các ngươi đã từng làm.');
INSERT INTO "vn_mokhtasar" VALUES(16,94,'16:94','Các ngươi chớ đừng dùng sự thề thốt của các ngươi để lừa dối nhau, các ngươi chớ đi theo dục vọng của bản thân các ngươi trong sự việc này mà cứ làm những gì các ngươi muốn. Nếu các ngươi đã làm như vậy thì bàn chân của các ngươi đã trượt khỏi con đường Ngay Chính sau khi các ngươi đã vững bước trên nó, và các ngươi sẽ nếm lấy sự trừng phạt vì sự lầm lạc khỏi con đường của Allah cũng như việc lôi kéo người khác lầm lạc, và các ngươi sẽ nếm lấy sự trừng phạt chồng chất.');
INSERT INTO "vn_mokhtasar" VALUES(16,95,'16:95','Các ngươi chớ mang Giao Ước của Allah để đổi lấy món vật với giá trị ít ỏi chẳng đáng gì. Những gì ở nơi Allah mới tốt đẹp hơn cho các ngươi, trên thế gian Ngài sẽ giúp các ngươi chiến thắng và ban cho các ngươi những chiến lợi phẩm, và ở cõi Đời Sau sẽ có Thiên Đàng Vĩnh Cửu cho các ngươi, một nơi ở và hưởng thụ tốt đẹp hơn món vật không đáng gì mà các ngươi đã đổi chác với Giao Ước của Ngài nếu các ngươi biết điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(16,96,'16:96','Hỡi nhân loại, những gì ở nơi các ngươi từ tài sản, sự hưởng thụ rồi đây sẽ cạn hết cho dù có nhiều đến dường nào, riêng những gì ở nơi Allah thì mãi mãi còn và không bao giờ cạn hết, vậy tại sao các ngươi lại chỉ quan tâm những thứ sẽ sớm mất mà bỏ mặc những thứ tồn tại mãi mãi?! Chắc chắn TA (Allah) sẽ ban thưởng cho những người kiên nhẫn trên các Giao Ước của TA, cố gắng không phá vỡ chúng những phần thường tương ứng với việc làm tốt nhất của họ trong việc tuân lệnh TA, và TA sẽ nhân các phần thưởng đó lên gấp mười lần, đến bảy trăm lần và nhiều hơn nữa theo ý của TA.');
INSERT INTO "vn_mokhtasar" VALUES(16,97,'16:97','Những ai có đức tin nơi Allah, ngoan đạo và làm việc thiện tốt dù nam hay nữ, TA (Allah) sẽ cho họ sống một cuộc sống an lành trên thế gian qua việc họ biết hài lòng với sự an bài và định đoạt của TA và qua việc được TA phù hộ trong thờ phượng và tuân lệnh TA; và ở cõi Đời Sau, TA sẽ ban thưởng cho họ những phần thưởng xứng đáng với việc làm tốt nhất mà họ đã làm trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(16,98,'16:98','Bởi thế, khi ngươi - hỡi người có đức tin - muốn đọc Kinh Qur''an thì ngươi hãy cầu xin Allah che chở tránh khỏi sự quấy nhiễu của Shaytan, kẻ đã bị trục xuất khỏi lòng thương xót của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(16,99,'16:99','Quả thật, Shaytan không có khả năng quấy nhiễu đối với những ai có đức tin nơi Allah và luôn phó thác mọi sự việc của họ cho một mình Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,100,'16:100','Quả thật, Shaytan chỉ có thể quấy nhiễu những ai nhận lấy nó làm kẻ bảo hộ và đi theo sự quyến rũ của nó và những ai tổ hợp các thần linh cùng với Allah trong thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(16,101,'16:101','Và Khi TA (Allah) xỏa bỏ một câu Kinh nào hoặc một luật định của câu Kinh nào để thay thế một câu Kinh khác trong Qur''an - và dĩ nhiên TA biết rõ những gì được xóa bỏ trong Qur''an mang giá trị như thế nào - thì chúng nói: Này Muhammad, Ngươi quả thật chỉ là một tên bịa đặt cho Allah. Không, đa số bọn chúng không hề biết gì về việc xóa bỏ các câu Kinh và thay thế các câu Kinh khác, mà sự việc chỉ có một mình Allah mới biết rõ và đó là sự thông thái và khôn ngoan của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,102,'16:102','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với họ: Kinh Qur''an này được Đại Thiên Thần Jibril mang xuống từ nơi Allah là chân lý, trong Nó không có sai sót, không có sự thay đổi, chỉnh sửa và bóp méo, để củng cố đức tin của những người có đức tin và để làm nguồn Chỉ Đạo cho họ dẫn họ đến với chân lý, và là nguồn báo tin mừng cho những người Muslim về những phần thưởng mà sẽ có được ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(16,103,'16:103','Và TA (Allah) biết rõ những kẻ thờ đa thần nói: Quả thật Muhammad được một người phàm dạy Qur''an. Họ là những kẻ nói dối cho những lời mà họ đã buông ra, bởi vì ngôn ngữ của kẻ mà họ cho rằng đã dạy Y là tiếng không phải Ả Rập trong khi Kinh Qur''an này được ban xuống bằng tiếng Ả Rập rõ ràng và chuẩn.');
INSERT INTO "vn_mokhtasar" VALUES(16,104,'16:104','Quả thật, những ai không có đức tin rằng các câu Kinh Qur''an đến từ nơi Allah là những người không được Allah hướng dẫn. Họ sẽ phải chịu một sự trừng phạt đau đớn bởi những gì mà họ đã vô đức tin nơi Allah và phủ nhận các lời mặc khải của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,105,'16:105','Muhammad không phải là người nói dối về những gì mà Người mang đến từ Thượng Đế của Người mà quả thật chính những kẻ không tin nơi các lời Mặc Khải của Allah mới là những kẻ nói dối; bởi vì họ không sợ sự trừng phạt, không hy vọng phần thưởng. Những kẻ vô đức tin đó mới thực sự là những kẻ nói dối bởi vì sự nói dối đã là thói quen của họ.');
INSERT INTO "vn_mokhtasar" VALUES(16,106,'16:106','Ai trở nên vô đức tin sau khi đã có đức tin - trừ trường hợp ai đó bị cưỡng ép phủ nhận đức tin của mình, bị cưỡng ép tuyên bố lời vô đức tin nhưng bên trong trái tim của y vẫn giữ trọn đức tin - bằng cách vỗ ngực tuyên bố sự vô đức tin thì y là kẻ Murtad (kẻ bỏ đạo). Những kẻ như thế này sẽ bị Allah phẫn nộ và giận dữ và sẽ phải chịu sự trừng phạt vô cùng to lớn.');
INSERT INTO "vn_mokhtasar" VALUES(16,107,'16:107','Việc rời bỏ đạo như thế, nguyên nhân là do họ yêu thích đời sống trần tục này, họ cho rằng cuộc sống trần tục là đã đủ cho họ và họ không cần đến cuộc sống cõi Đời Sau và họ phủ nhận nó. Và quả thật, Allah không phù hộ cho những kẻ vô đức tin đến với đức tin Iman, mà Ngài sẽ làm cho càng ngày càng lầm lạc xa hơn.');
INSERT INTO "vn_mokhtasar" VALUES(16,108,'16:108','Những kẻ rời bỏ đức tin sau khi đã có đức tin là những kẻ mà Allah đã niêm kín và khóa chặt trái tim của họ để không cho họ hiểu những lời khuyên răn; đã niêm kín và khóa chặt thính giác của họ để không cho họ nghe những điều hữu ích; và đã niêm kín và khóa chặt thị giác của họ để không cho họ nhìn thấy các dấu hiệu chứng minh cho đức tin. Những kẻ này là những kẻ thờ ơ và hờ hững, không quan tâm đến các nguyện nhân hạnh phúc và đau khổ, không quan tâm đến sự trừng phạt mà Allah đã chuẩn bị cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(16,109,'16:109','Vào Ngày Phán Xét, một điều chắc chắn rằng họ sẽ là những kẻ thất bại bởi sự vô đức tin của họ sau khi họ đã có đức tin - cái mà nếu họ giữ lấy nó thì họ sẽ vào Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(16,110,'16:110','Hỡi Thiên Sứ Muhammad, quả thật, Thượng Đế của Ngươi là Đấng Hằng Tha Thứ và Khoan Dung đối với những người có đức tin yếu thế, những người mà họ phải rời bỏ Makkah - quê hương của họ - để di cư đến Madinah sau khi bị những kẻ thờ đa thần áp bức bắt họ phải tuyên bố lời vô đức tin nhưng trái tim của họ vẫn vững đức tin, và sau đó họ chiến đấu anh dũng cho con đường Chính Nghĩa của Allah để giơ cao lời phán của Ngài và để hạ thấp tiếng nói của những kẻ vô đức tin, họ kiên nhẫn chịu dựng trước những khó khăn thử thách. Sau những thử thách mà họ đã trải qua cũng như sau những hành hạ và áp bức mà họ đã chịu đựng thì Thượng Đế của Ngươi - Thiên Sứ Muhammad - chắc chắn sẽ tha thứ cho họ và thương xót họ bởi vì họ đã không thốt lên lời vô đức tin hoặc nói lời vô đức tin do bị cưỡng ép bằng vũ lực.');
INSERT INTO "vn_mokhtasar" VALUES(16,111,'16:111','Hỡi Thiên Sứ Muhammad, Ngươi hãy nghĩ tới Ngày mà mỗi con người sẽ chỉ khiếu nại cho bản thân mình, không khiếu nại cho ai khác. Vào Ngày Hôm đó, mỗi linh hồn sẽ được đền bù xứng đáng cho những điều tốt và điều xấu mà nó đã làm; và tất cả sẽ không bị đối xử bất công: giảm bớt bất cứ điều tốt nào của họ cũng như không bị qui tội nhiều hơn việc làm xấu của họ.');
INSERT INTO "vn_mokhtasar" VALUES(16,112,'16:112','Allah đưa ra một hình ảnh thí dụ về một thị trấn, đó là Makkah. Nó đã từng là một nơi an ninh và yên bình, dân chúng của nó không mang bất cứ nỗi sợ hãi nào, và bổng lộc của nó được cung ứng dồi dào từ khắp mọi nơi. Tuy nhiên, dân chúng của nó đã phụ ân về những ân huệ mà Allah đã ban cho họ, họ không biết tri ân Ngài. Bởi thế, Allah trừng phạt họ bằng cơn đói và nỗi sợ hãi vì những gì mà họ đã làm từ sự vô đức tin và phủ nhận Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,113,'16:113','Quả thật, đã có một vị Thiên Sứ xuất thân từ dân chúng của Makkah đến với họ. Dân chúng Makkah đều biết rõ sự ngay thẳng, trung thực của Y, đó là Muhammad. Tuy nhiên, họ đã phủ nhận Y và phủ nhận những gì Y mang đến từ Thượng Đế của Y. Bởi vậy, Allah đã giáng xuống họ hình phạt của cơn đói và nỗi sợ hãi. Và họ chính là những kẻ bất công với chính bản thân của họ, vì chính họ muốn thị trấn của họ bị hủy diệt khi mà họ tổ hợp các thần linh cùng với Allah và phủ nhận vị Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(16,114,'16:114','Bởi thế, hỡi đám bề tôi, các ngươi hãy dùng những thức ăn Halal (được phép) và tốt sạch từ những nguồn thức ăn mà Allah đã ban cấp cho các ngươi; các ngươi hãy tạ ơn ân huệ mà Ngài đã ban cho các ngươi bằng cách sự thừa nhận chúng là của Allah, chi dùng chúng trong sự hài lòng của Ngài nếu các ngươi chỉ thờ phượng duy nhất một mình Allah và không tổ hợp với Ngài bất cứ thứ gì.');
INSERT INTO "vn_mokhtasar" VALUES(16,115,'16:115','Allah cấm các ngươi ăn xác chết (con vật chết không phải do giết thịt), máu (hoặc huyết, tức máu xuất ra ngoài cơ thể con vật), thịt heo, và những con vật được giết để hiến tế ai (vật) khác ngoài Allah. Sự cấm đoán này chỉ áp dụng trong tình huống được quyền lựa chọn, riêng những ai có nhu cầu cấp bách đến những thức ăn cấm đó và họ bắt buộc phải ăn chúng nhưng không quá độ tức chỉ ăn vừa đủ để duy trì sự sống thì họ không bị tội. Bởi quả thật, Allah là Đấng Hằng Tha Thư, Ngài tha thứ cho những gì họ đã ăn, và Ngài là Đấng Thương Xót khi cho phép ăn những thứ đã cấm trong trường hợp khẩn cấp của tình huống bất đắt dĩ.');
INSERT INTO "vn_mokhtasar" VALUES(16,116,'16:116','Hỡi những người thờ đa thần, các ngươi chớ nói về những gì mà chiếc lưỡi của các ngươi buông lời dối rồi đổ thừa cho Allah rằng cái này Halal (được phép) và cái kia Haram (bị cấm) với mục đích làm trái lệnh Allah bằng cách cấm những gì Ngài không cấm và cho phép những gì Ngài không cho phép. Quả thật, những ai dối trá để làm trái lệnh Allah chắc chắn sẽ không đạt được điều mong muốn và sẽ không tránh được sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(16,117,'16:117','Chúng có được một sự hưởng thụ ít ỏi đáng khinh qua việc đi theo dục vọng của bản thân chúng trên cõi trần nhưng rồi đây vào Ngày Phán Xét chúng sẽ phải hứng chịu một sự trừng phạt vô cùng đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(16,118,'16:118','Riêng đối với những người Do Thái, TA (Allah) đã cấm chúng dùng những thứ mà TA đã kể cho Ngươi - Thiên Sứ Muhammad - trước đó (trong câu Kinh của chương Al-An''am). Và TA không bất công với chúng về việc cấm đoán chúng những thứ đó mà chính bản thân chúng đã bất công với bản thân chúng khi mà chúng đã phạm những điều khiến chúng phải bị trừng phạt. TA phạt chúng vì tội lỗi của chúng nên TA đã cấm chúng những thứ đó.');
INSERT INTO "vn_mokhtasar" VALUES(16,119,'16:119','Hỡi Thiên Sứ Muhammad, những ai đã làm điều tội lỗi vì thiếu hiểu biết về hậu quả của những việc làm đó và ngay cả họ cố tình, nhưng rồi họ quay về sám hối với Allah và cải thiện hành động tội lỗi của mình, thì quả thật Thượng Đế của Ngươi sau sự sám hối đó của họ chắc chắn sẽ tha thứ tội lội của họ và sẽ thương xót họ.');
INSERT INTO "vn_mokhtasar" VALUES(16,120,'16:120','Quả thật, Ibrahim là một người toàn diện về những đức tính tốt đẹp, là một người hết lòng tuân lệnh Thượng Đế của Y, một lòng thờ phượng một mình Allah - đó là Islam, và không phải là một người thờ đa thần, chưa bao giờ là người như thế.');
INSERT INTO "vn_mokhtasar" VALUES(16,121,'16:121','Ibrahim là người luôn biết tạ ơn ân huệ của Allah, Allah đã chọn Người làm vị Nabi và hướng dẫn Người đến với tôn giáo Islam ngay chính và chuẩn mực.');
INSERT INTO "vn_mokhtasar" VALUES(16,122,'16:122','Trên cõi trần, TA (Allah) đã ban cho Ibrahim sứ mạng Nabi, sự được khen ngợi, sự tốt đẹp và đứa con ngoan đạo, và vào cuộc sống Đời Sau, Y chắc chắn thuộc thành phần những người ngoan đạo mà TA đã chuẩn bị cho họ những cấp bậc cao nhất trong Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(16,123,'16:123','Rồi sau đó, TA (Allah) đã mặc khải cho Ngươi - hỡi Thiên Sứ Muhammad - rằng hãy đi theo tín ngưỡng của Ibrahim, đó là tôn thờ một mình Allah, tuyệt đối không dính líu đến việc tổ hợp với Ngài, kêu gọi đến với Ngài và làm theo luật định của Ngài, quay mặt với toàn bộ các tôn giáo mà hướng trọn về Islam; và Ibrahim không phải là người thờ đa thần như những người đa thần đã khẳng định, chưa bao giờ là như thế cả, mà Y là chỉ tôn thờ duy nhất một mình Allah.');
INSERT INTO "vn_mokhtasar" VALUES(16,124,'16:124','Quả thật, việc tôn vinh ngày thứ bảy chỉ được qui định bắt buộc đối với những người Do Thái đã tranh chấp nhau về nó; để không bận rộn cho việc hành đạo vào ngày này nữa sau khi họ đã chối bó ngày thứ sáu, ngày mà được lệnh không cấm kỵ. Quả thật, Thượng Đế của Ngươi - hỡi Thiên Sứ Muhammad - chắc chắn sẽ phân xử giữa những người tranh chấp nhau này vào Ngày Phán Xét về những điều họ tranh chấp, và Ngài sẽ thượng phạt tương thích.');
INSERT INTO "vn_mokhtasar" VALUES(16,125,'16:125','Hỡi Thiên Sứ Muhammad, Ngươi và những người có đức tin theo Ngươi hãy kêu gọi thiên hạ đến với tôn giáo Islam bằng sự khôn ngoan và khéo léo, bằng những lời lẽ khuyên răn tốt đẹp; và hãy tranh luận với thiện hạ trong việc hướng họ đến với chân lý bằng phương cách tốt đẹp nhất: lời nói tử tế và lý lẽ đúng đắn. Nhưng việc hướng dẫn nhân loại giác ngộ được chân lý không phải là việc của Ngươi, mà việc của Ngươi chỉ là truyền đạt cho họ, bởi quả thật, Thượng Đế của Ngươi mới là Đấng biết rõ ai là lầm lạc khỏi tôn giáo Islam và ai được hướng dẫn đến với nó. Do đó, Ngươi chớ bắt bản thân Ngươi cảm thấy có lỗi trong việc họ không được hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(16,126,'16:126','Nếu các ngươi muốn đáp trả lại kẻ thù của các ngươi thì các ngươi hãy đáp trả giống như những gì mà chúng đã làm với các ngươi, không được nhiều hơn mức đó. Tuy nhiên, nếu các ngươi kiên nhẫn chịu đựng mặc dù các ngươi có khả năng đáp trả thì quả thật điều đó tốt hơn cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(16,127,'16:127','Hỡi Thiên Sứ Muhammad, Ngươi hãy kiên nhẫn chịu đựng về những gì mà họ đã gây tổn hại Ngươi, bởi lẽ sự kiên nhẫn của Ngươi là vì Allah, và Ngươi chớ đau buồn cho việc những kẻ vô đức tin quay lưng với Ngươi và Ngươi cũng chớ để lòng mình se thắt cho những âm mưu của họ.');
INSERT INTO "vn_mokhtasar" VALUES(16,128,'16:128','Quả thật, Allah luôn bên cạnh những người kính sợ Ngài mà từ bỏ những điều trái lệnh Ngài và những người mà họ luôn hoàn thành tốt những gì Ngài ra lệnh; và Ngài luôn phù hộ và giúp đỡ họ.');
INSERT INTO "vn_mokhtasar" VALUES(17,1,'17:1','Allah khẳng định sự Tối Cao và Quang Vinh của Ngài, Ngài khẳng định Ngài là Đấng Toàn Năng và quyền năng của Ngài là vô song. Ngài khẳng định chính Ngài đã đưa người bề tôi của Ngài - Muhammad - bằng cả linh hồn và thể xác của Người lúc Người vẫn còn tỉnh táo và có ý thức đi dạ hành trong đêm từ Masjid Al-Haram (ở Makkah) đến Masjid Al-Aqsa (ngôi đền Al-Maqdis - tại Jerusalem, Palestine) nơi mà Ngài đã ban phúc lành xung quanh nó với đất đai, vườn tược và hoa màu cùng với những ngôi nhà của các vị Nabi; mục đích cho sự việc đó là Ngài muốn để Người - Muhammad thấy một số dấu hiệu chứng minh quyền năng của Ngài. Quả thật, Ngài là Đấng hằng nghe, không có bất cứ điều gì nằm ngoài tầm nghe của Ngài, Ngài là Đấng hằng thấy, không có bất cứ điều gì nằm ngoài tầm nhìn của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(17,2,'17:2','TA (Allah) đã ban cho Musa (Moses - Môi-sê) Kinh Tawrah, TA dùng Nó làm nguồn chỉ đạo cho dân chúng Isra-el; và TA đã phán truyền với dân chúng Isra-el: các ngươi chớ nhận lấy ai (vật) ngoài TA làm đấng để các ngươi trông cậy và phó thác mà các ngươi chỉ trông cậy và phó thác một mình TA thôi.');
INSERT INTO "vn_mokhtasar" VALUES(17,3,'17:3','Các ngươi thuộc con cháu của những người mà TA (Allah) đã cứu rỗi cùng với Nuh (Noah - Nô-ê) trên chiếc thuyền trong trận đại hồng thủy. Bởi thế, các ngươi hãy ghi nhớ ân huệ này và hãy biết tạ ơn TA qua việc thờ phượng và tuân lệnh một mình TA; các ngươi hãy nói theo gương của Nuh trong sự việc đó bởi Y là một người bề tôi luôn biết ơn và cảm tạ TA.');
INSERT INTO "vn_mokhtasar" VALUES(17,4,'17:4','TA (Allah) đã mặc khải trong Kinh Tawrah cho dân chúng Isra-el biết rằng chắc chắn họ sẽ hành động thối nát trên trái đất hai lần và chắc chắn họ sẽ trở nên ngang tàng và bạo ngược quá mức đối với nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(17,5,'17:5','Khi dân chúng Isra-el hành động sự thối nát đầu tiên thì TA (Allah) đã cho một đám bầy tôi sở hữu sức mạnh vượt trội của TA tiêu diệt chúng. Họ đã tràn vào tàn phá nhà cửa của chúng và tấn công chúng dữ dỗi. Quả thật, lời hứa của Allah sẽ luôn được thể hiện đúng như thế.');
INSERT INTO "vn_mokhtasar" VALUES(17,6,'17:6','Sau đó, TA (Allah) làm cho các ngươi - hỡi dân chúng Isra-el - thành một quốc gia hùng mạnh và thẳng trở lại những người mà TA đã cho họ quyền áp đảo các ngươi trước đó sau khi các ngươi đã quay đầu sám hối với TA; và TA đã ban cho các ngươi thêm dồi dào tài sản và đông con cái hơn, và TA đã làm cho các ngươi đông đảo hơn kẻ thù của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(17,7,'17:7','Hỡi dân Isra-el, nếu các ngươi làm điều tốt thì phần ân phước tốt đẹp sẽ được hoàn lại cho các ngươi bởi Allah là Đấng bất cần các việc làm của các ngươi; còn nếu các ngươi làm điều xấu thì các ngươi phải chịu hình phạt cho việc làm xấu đó của các ngươi bởi các việc làm xấu hay tốt đều không ảnh hưởng đến Allah. Và khi hành động thối nát lần hai của các ngươi xảy ra thì Allah sẽ để kẻ thù của các ngươi xâm chiếm giành lấy quyền thống trị các ngươi, lúc đó, gương mặt của các ngươi sẽ bị bôi nhọ vì chúng sẽ giành lấy ngôi đền Al-Maqdis, chúng sẽ đi vào đó và phá hại nó giống như chúng đã đi vào và đập phá nó lần đầu, và chúng sẽ tàn phá bất cứ thứ gì nằm trong vùng kiểm soát của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(17,8,'17:8','Hỡi dân chúng Isra-el, có thể Thượng Đế của các ngươi sẽ khoan dung cho các ngươi sau hành động thối nát đó nếu các ngươi quay về sám hối với Ngài và làm điều thiện tốt. Nhưng nếu các ngươi quay trở lại với hành động thối nát lần thứ ba hoặc nhiều lần hơn nữa thì TA (Allah) sẽ tái diễn hình phạt đối với các ngươi; và TA sẽ dùng Hỏa Ngục làm nơi tù đày dành cho những kẻ vô đức tin và chúng không bao giờ ra khỏi đó.');
INSERT INTO "vn_mokhtasar" VALUES(17,9,'17:9','Quả thật Qur''an được ban xuống cho Muhammad này đây sẽ hướng dẫn đến với con đường tốt đẹp nhất, đó là con đường Islam, và Nó sẽ báo tin mừng cho những người có đức tin nơi Allah, những người mà họ làm điều thiện tốt và ngoan đạo rằng họ sẽ được ban thưởng nơi Ngài một phần thưởng vô cùng to lớn.');
INSERT INTO "vn_mokhtasar" VALUES(17,10,'17:10','Và Qur''an báo tin cho những ai không có đức tin nơi Ngày Phục Sinh về những điều làm họ buồn lòng rằng TA (Allah) đã chuẩn bị cho chúng một hình phạt đau đớn vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(17,11,'17:11','Con người với sự thiếu hiểu biết thường cầu nguyện điều xấu cho bản thân, con cái và tài sản của y lúc nóng giận giống như y cầu nguyện điều tốt cho bản thân của y. Bởi thế, nếu TA (Allah) đáp lại lời cầu nguyện điều xấu của y thì chắc chắn y, tài sản và con cái của y sẽ bị hủy diệt; và con người quả thật thường nóng vội và hấp tấp.');
INSERT INTO "vn_mokhtasar" VALUES(17,12,'17:12','Và TA (Allah) đã tạo ra ban đêm và ban ngày làm hai dấu hiệu chứng minh tính duy nhất của TA và chứng minh quyền năng vô song của TA bởi trong ban đêm và ban ngày có độ dài ngắn, nóng lạnh khác nhau. TA làm cho ban đêm tối mờ để nghỉ ngơi và yên giấc và TA làm cho ban ngày có ánh sáng để nhân loại nhìn thấy và tìm kiếm kế sinh nhai; và qua sự luân phiên giữa ban đêm và ban ngày, con người có thể biết được con số của niên kỷ và cách tính toán giờ giấc và thời gian của ngày, tháng. Tất cả mọi thứ đều được TA trình bày rõ ràng, cái đúng và cái sai đều được TA giảng giải rõ rệt.');
INSERT INTO "vn_mokhtasar" VALUES(17,13,'17:13','Và mỗi người đều được TA (Allah) treo lên cổ một cái vòng về các việc làm của y, nó không được tháo ra cho đến khi mỗi người đã được mang ra xét xử. Vào Ngày Phục Sinh, TA sẽ mang ra cho y một quyển số trong đó ghi chép lại tất cả mọi việc làm tốt, xấu của y và y sẽ thấy nó được mở sẵn trước mặt của mình.');
INSERT INTO "vn_mokhtasar" VALUES(17,14,'17:14','TA (Allah) sẽ phán với con người vào Ngày Phán Xét: ngươi hãy đọc quyển sổ ghi chép các việc làm của ngươi, bản thân ngươi tự phán xét lấy các việc làm của Ngươi và vào Ngày Phán Xét linh hôn của ngươi đã đủ để thanh toán ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(17,15,'17:15','Ai đi theo hướng dẫn để đến với đức tin Iman thì sẽ được ban thưởng cho sự sự đi theo của y, còn ai lạc khỏi sự hướng dẫn thì phải chịu hình phạt cho sự lạc đó của y. Và không một ai phải gánh lấy tội lỗi của người khác mà mỗi người chỉ phải chịu tội lỗi của riêng mình. Và TA (Allah) đã không trừng phạt bất cứ một nhóm người nào cho tới khi TA đã có đầy đủ lý do cho sự việc đó, đấy là TA đã gửi đến cho họ một Sứ Giả.');
INSERT INTO "vn_mokhtasar" VALUES(17,16,'17:16','Và khi TA (Allah) muốn hủy diệt một thị trấn hay một ngôi làng nào đó thì TA đều truyền lệnh xuống cho thị dân đang sống sung túc của nó rằng hãy tuân lệnh TA và chớ trái nghịch; nhưng rồi chúng ngoan cố làm trái lệnh TA, và lời hứa trừng phạt của TA phải được chứng thực nên TA đã hủy diệt chúng bằng một sự triệt tiêu toàn bộ.');
INSERT INTO "vn_mokhtasar" VALUES(17,17,'17:17','Đã có rất nhiều cộng đồng phủ nhận TA (Allah) và bất tuân TA đã bị TA hủy diệt sau thời của Nuh như cộng đồng Aad, Thamud. Và Thượng Đế của Ngươi - Muhammad - đủ thông toàn mọi tội lỗi của đám bầy tôi của Ngài, không có bất cứ điều gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(17,18,'17:18','Ai ham muốn cuộc sống ngắn ngủi và vô định của đời sống trần tục mà không hề có đức tin nơi cuộc sống Đời Sau, nơi mà TA (Allah) không hề quan tâm (vì nó không có giá trị gì đối với TA) thì TA sẽ mau mau ban bố cho y những gì y ham muốn theo ý muốn của TA; nhưng rồi TA đã dành sẵn Hỏa Ngục cho y, y sẽ phải bị đày trong đó vào Ngày Phục Sinh để hứng chịu sự đòa đày và đau đớn, đó là một sự hạ nhục cho việc y đã chọn kiếp phù sinh của thế gian và phủ nhận cuộc sống Đời Sau, và đó là một sự trục xuất khỏi lòng thương xót của TA.');
INSERT INTO "vn_mokhtasar" VALUES(17,19,'17:19','Và những ai muốn được phần ân phước ở cõi Đời Sau qua những việc làm thiện tốt và ngoan đạo và họ đã cố gắng hết mình trên những việc làm đó một cách thành tâm không hề pha lẫn vào những tạp chất của sự phô trương và giả tạo, đồng thời họ tin nơi những thứ mà Allah bắt họ phải tin, thì họ là những người được chấp nhận ở nơi Ngài và sẽ được Ngài ban thưởng xứng đáng.');
INSERT INTO "vn_mokhtasar" VALUES(17,20,'17:20','Cả hai nhóm người này: nhóm người nghịch đạo và nhóm người ngoan đạo, TA (Allah) đều ban phát cho họ, tất cả đều được ban phát từ nơi Thượng Đế của Ngươi - Muhammad một cách không giới hạn. Và phần ban phát của Thượng Đế của Ngươi - Muhammad không giới hạn đối với một ai, dù là người ngoan đạo hay là người nghịch đạo tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(17,21,'17:21','Hỡi Thiên Sứ Muhammad, Ngươi hãy xem TA (Allah) đã ban bổng lộc và ân huệ cho người này hơn người kia như thế nào trên thế gian. Tuy nhiên, ở cõi Đời Sau sự hơn kém nhau về ân huệ còn to lớn hơn so với cõi trần và sự ưu đãi ở đó sẽ nhiều hơn, bởi thế, người có đức tin phải nên cố gắng phấn đấu giành lấy những gì tốt đep ở cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(17,22,'17:22','Hỡi người bề tôi, ngươi chớ dựng một đấng thờ phượng khác cùng với Allah, e rằng ngươi sẽ trở thành một người bị khinh thường ở nơi Allah và ở nơi những người ngoan đạo ngươi không được khen ngợi, và ngươi sẽ không được ai quan tâm và cứu giúp.');
INSERT INTO "vn_mokhtasar" VALUES(17,23,'17:23','Hỡi người bề tôi, Thượng Đế của ngươi ra lệnh bắt các ngươi phải thờ phượng một mình Ngài, phải ăn ở tử tế với cha mẹ đặc biệt lúc họ trong tuổi già. Nếu một trong cha mẹ hoặc cả hai sống cùng với các ngươi lúc tuổi già thì các ngươi chớ buông tiếng hay tỏ thái độ vô lễ với họ và các ngươi chớ chửi rủa họ và bất kính với họ trong lời ăn tiếng nói mà các ngươi hãy nói với họ bằng lời lẽ tôn kính, nhẹ nhàng và yêu thương.');
INSERT INTO "vn_mokhtasar" VALUES(17,24,'17:24','Và ngươi hãy hạ mình lễ phép trước cha mẹ, hay thương yêu và nhân từ với họ và hãy luôn cầu nguyện: Lạy Thượng Đế của bề tôi, xin Ngài hãy yêu thương cha mẹ của bề tôi bởi vì họ đã yêu thương và chăm sóc bề tôi lúc hãy còn bé.');
INSERT INTO "vn_mokhtasar" VALUES(17,25,'17:25','Hỡi nhân loại, Thượng Đế của các ngươi biết rõ những gì nằm trong lòng của các ngươi từ sự thành tâm trong việc thờ phượng và việc làm thiện tốt cũng như trong việc ăn ở tử tế với cha mẹ. Nếu tâm niệm của các ngươi trong thờ phượng cũng như trong việc ăn ở tử tế với cha mẹ và những việc làm thiện tốt khác thì Ngài sẽ tha thứ cho những ai quay đầu sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(17,26,'17:26','Hỡi người có đức tin, ngươi hãy biếu tặng cho những người bà con ruột thịt và thân thích những phần bắt buộc để duy trì cũng như hàn gắn mối quan hệ tình thâm, và hãy bố thí cho những người nghèo khó cũng như những người lỡ đường; chớ chi dùng tài sản và của cải của ngươi vào những điều trái lệnh Allah hoặc chi dùng một cách hoang phí.');
INSERT INTO "vn_mokhtasar" VALUES(17,27,'17:27','Quả thật, những kẻ chi dùng của cải và tài sản của chúng vào những điều trái lệnh và tội lỗi cũng như những kẻ chi dùng một cách hoang phí là anh em của những tên Shaytan. Chúng đã nghe theo sự xúi bẩy của những tên Shaytan đó trong việc hoang phí và tội lỗi, và quả thật những tên Shaytan thường luôn bội ơn Thượng Đế của chúng, chúng thường chỉ làm điều trái lệnh Ngài và chúng thường lôi kéo đến với điều làm Thượng Đế của chúng phẫn nộ.');
INSERT INTO "vn_mokhtasar" VALUES(17,28,'17:28','Và nếu ngươi không cho những người này bởi vì ngươi không có những thứ để cho họ và ngươi cũng đang cần Allah ban cho ngươi những thứ đó từ nguồn bổng lộc của Ngài thì ngươi hãy nói với họ bằng lời lẽ nhẹ nhàng và tốt đẹp chẳng hạn như ngươi cầu nguyện cho họ được rộng rãi bổng lộc hoặc ngươi hứa sẽ chia sẻ với họ nếu Allah ban cho ngươi bộng lộc.');
INSERT INTO "vn_mokhtasar" VALUES(17,29,'17:29','Ngươi chớ trói chặt tay của ngươi vào cổ của mình vì không muốn chi ra một cách keo kiệt và ngươi cũng chớ giăng tay của ngươi ra quá mức trong việc chi dùng tài sản và của cải như một kẻ hoang phí; bởi lẽ biết đâu ngươi bị người đời chê bai vì sự keo kiệt của ngươi hoặc biết đâu ngươi sẽ trở nên nghèo khổ do sự hoang phí của ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(17,30,'17:30','Quả thật, Thượng Đế của ngươi muốn ban sự rộng rãi về bổng lộc cho ai tùy ý Ngài và muốn hạn chế bổng lộc cho ai cũng tùy ý Ngài, tất cả đều nói lên sự anh minh và hiểu biết của Ngài. Quả thật, Ngài là Đấng Thông Toàn về các bề tôi của Ngài, không có bất cứ thứ gì có thể che giấu được Ngài, và Ngài muốn định đoạt sự việc như thế nào là tùy ý Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(17,31,'17:31','Và các ngươi chớ giết con cái của các ngươi vì sợ cái nghèo ở tương lai bởi lẽ chính TA (Allah) là Đấng cung dưỡng con cái của các ngươi và cả bản thân các ngươi nữa. Nếu các ngươi giết con cái của các ngươi thì đó là một đại trọng tội bởi chúng không có tội tình gì để các ngươi giết chúng.');
INSERT INTO "vn_mokhtasar" VALUES(17,32,'17:32','Các ngươi hãy tránh xa Zina (quan hệ tình dục ngoài hôn nhân), các ngươi hãy tránh xa những phương tiện dẫn đến nó, bởi nó là một hành động tội lỗi và là một con đường xấu xa gây xáo trộn và loạn huyết thống, và nó là con đường dẫn tới sự trừng phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(17,33,'17:33','Và bằng đức tin Iman hoặc bằng sự an ninh, các ngươi chớ đừng giết một sinh mạng con người mà Allah đã không cho phép xâm phạm trừ phi kẻ đó đáng bị giết bởi lý do chính đáng theo giáo luật như bỏ đạo, phạm Zina sau khi đã kết hôn hoặc đền mạng theo luật Qisas (giết người phải trả mạng). Và ai bị giết một cách bất công không có lý do chính đáng cho việc bị giết đó thì TA (Allah) ban cho những người hưởng quyền thừa kế của y (cha mẹ, con cái, vợ hoặc chồng, và bà con) quyền đòi giết kẻ sát nhân theo luật Qisas; tuy nhiên, kẻ sát nhân được quyền nhận được sự lượng thứ không cần đền bù hoặc phải đền bù từ phía người thân của nạn nhân. Do đó, chớ vượt quá giới hạn mà Allah đã qui định cho phép trong việc giết chóc tức giết nhầm, giết oan không đủ bằng chứng rõ ràng. Và quả thật, luật pháp của Islam sẽ giúp đỡ người vô tội.');
INSERT INTO "vn_mokhtasar" VALUES(17,34,'17:34','Các ngươi không được phép chi dùng tài sản của các trẻ mồ côi trừ phi với mục đích cải thiện cho chúng, các ngươi hãy bảo quản tài sản giùm cho chúng cho đến khi chúng trưởng thành đủ lớn và đủ trí lực. Các ngươi hãy thực hiện lời giao ước giữa các ngươi và Allah cũng như lời giao ước giữa các ngươi và các bề tôi của Ngài một cách đầy đủ và đúng mực. Quả thật, Allah sẽ tra hỏi các ngươi về những giao ước của các ngươi vào Ngày Phán Xét: nếu các ngươi thực hiện theo đúng giao ước thì các ngươi sẽ được ban thưởng, còn nếu các ngươi không thực hiện đúng theo giao ước thì các ngươi phải bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(17,35,'17:35','Và khi các ngươi đo đạc cho người khác thì các ngươi hãy đo cho đúng, chớ gian lận, và khi các ngươi cân thì hãy cân trên những chiếc cân có độ chính xác bằng sự công bằng và trung thực, không được thiếu bớt một thứ gì của người khác. Việc cân đo ngay thẳng và trung thực như thế là không những là điều tốt cho các ngươi trên thế gian mà còn tốt đẹp cho cả cuộc sống Đời Sau, và nó sẽ mang lại hậu quả tốt đẹp nhất vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(17,36,'17:36','Này hỡi con người, ngươi chớ đi theo những điều mà ngươi không có kiến thức hiểu biết bởi vì như thế là đi theo sự phỏng đoán và trực giác. Quả thật, con ngươi sẽ bị tra hỏi về thính giác, thị giác và trái tim mà y đã dùng cho việc xấu và việc tốt, nếu y dùng cho việc tốt y sẽ được ban thưởng còn nếu y dùng cho việc xấu thì sẽ bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(17,37,'17:37','Này con người, ngươi chớ bước đi với dáng vẻ tự cao tự đại trên trái đất. Quả thật với điệu bộ tự cao tự đại của ngươi khi bước đi trên trái đất cũng sẽ không bao giờ làm cho nó nứt ra và chiều cao của ngươi cũng sẽ không bào giờ cao bằng núi. Nếu như thế thì cơ sao ngươi lại ngạo mạn và kiêu căng?!');
INSERT INTO "vn_mokhtasar" VALUES(17,38,'17:38','Tất cả những điều được đề cập ở trên đều là những việc làm tội lỗi và bị nghiêm cấm ở nơi Thượng Đế của ngươi - hỡi con ngươi -, Ngài sẽ không hài lòng cho những hành động đó, tất cả đều đáng ghét đối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(17,39,'17:39','Đó là những điều TA (Allah) đã trình bày rõ ràng từ những chỉ thị và các lệnh cấm cùng với các giáo luật mà TA đã mặc khải cho ngươi hỡi con người. Bởi thế, ngươi chớ dựng lên một thần linh khác cùng với Allah để rồi vào Ngày Phán Xét phải bị ném vào trong Hỏa Ngục, ngươi sẽ bị chính bản thân và nhân loại chê trách, và ngươi sẽ bị xua đuổi khỏi mọi điều tốt lành.');
INSERT INTO "vn_mokhtasar" VALUES(17,40,'17:40','Hỡi những ai cho rằng các Thiên Thần là những đứa con gái của Allah, hỡi những kẻ thờ đa thần, chẳng lẽ Thượng Đế của các ngươi đãi ngộ đặc biệt với các ngươi với các con trai và nhận lấy các Thiên Thần làm những đứa con gái của Ngài ư? Quả thật, Allah vĩ đại hơn những gì các ngươi nói, các ngươi thực sự đã nói cho Allah với lời lẽ vô đức tin và xúc phạm tày trời khi các ngươi gán cho Ngài đứa con cũng như các ngươi khẳng định rằng Ngài có những đứa con gái.');
INSERT INTO "vn_mokhtasar" VALUES(17,41,'17:41','Và quả thật, TA (Allah) đã trình bày rõ ràng trong Qur''an này các giáo luật, các lời khuyên răn và những thí dụ để nhắc nhở nhân loại, để chúng gặt hái điều tốt đẹp cho chúng và bỏ đi những điều gây hại cho chúng, nhưng rồi những điều đó chỉ làm cho chúng thêm câm ghét và lánh xa chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(17,42,'17:42','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những người thờ đa thần này rằng nếu thực sự có những thần linh cùng với Allah như chúng nói thì chắc chắn chúng sẽ cầu xin khấn vái các thần linh đó của chúng chỉ cho chúng con đường đến với Allah, Đấng Chủ Nhân của chiếc Ngai Vương Chí Đại.');
INSERT INTO "vn_mokhtasar" VALUES(17,43,'17:43','Allah thanh sạch và nâng cao bản thân Ngài vượt khỏi những gì mà những kẻ đa thần đã nói về Ngài, Ngài Tối Cao và Vĩ Đại hơn những gì chúng nói.');
INSERT INTO "vn_mokhtasar" VALUES(17,44,'17:44','Các tầng trời và trái đất cũng như vạn vật tồn tại trong trời đất đều tán dương ca tụng Allah. Không có bất cứ tạo vật nào không tán dương ca tụng Ngài. Tuy nhiên, các ngươi - hỡi con người - không hiểu được cách chúng tán dương và ca tụng Ngài, các ngươi chỉ có thể hiểu sự tán dương và ca tụng từ chiếc lưỡi của các ngươi thôi. Quả thật, Allah là Đấng Hằng Kiên Nhẫn, Ngài không vội vã trong việc trừng phạt, và Ngài là Đấng Hằng Tha Thứ cho những ai quay đầu sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(17,45,'17:45','Hỡi Thiên Sứ Muhammad, khi Ngươi đọc Qur''an, chúng nghe thấy lời nhắc nhở và khuyên răn trong đó nhưng TA (Allah) đã đặt giữa Ngươi và những kẻ không có đức tin nơi Ngày Phán Xét một bức màn để ngăn chúng hiểu Qur''an, một sự trừng phạt dành cho chúng vì tội chúng quay lưng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(17,46,'17:46','TA (Allah) đã bao phủ quả tim của chúng để chúng không hiểu được Qur''an, TA đã làm tai của chúng trở nặng nề y như điếc không thể nghe thấy điều hữu ích. Và khi nào Ngươi - Thiên Sứ Muhammad - chỉ nhắc về riêng Thượng Đế của Ngươi trong Qur''an thôi mà không nhắc đến các thần linh của chúng thì chúng quay mặt bỏ đi để rời xa Tawhid cũng như không thành tâm đối với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(17,47,'17:47','TA (Allah) biết rõ cách những kẻ cầm đầu bọn chúng lắng nghe Qur''an, chúng không muốn được hướng dẫn mà chúng muốn được sự nhẹ nhàng lúc Ngươi (Muhammad) đọc. Và TA biết rõ điều giả dối và gian trá mà những kẻ sai quấy này họp kín với nhau khi chúng bảo nhau: các người chớ đi theo hắn ta bởi hắn ta chỉ là một tên bị bùa ngải mê hoặc.');
INSERT INTO "vn_mokhtasar" VALUES(17,48,'17:48','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhìn xem về việc chúng đã mô tả Ngươi với những mô tả xấu khác nhau, vì chúng như thế nên chúng mới lạc khỏi chân lý và chúng sẽ không tìm thấy con đường nào dẫn chúng đến chân lý cả.');
INSERT INTO "vn_mokhtasar" VALUES(17,49,'17:49','Những kẻ thờ đa thần phủ nhận sự phục sinh, nói: chẳng lẽ sau khi chúng tôi đã chết và biến thành những khúc xương khô vỡ vụn, thân xác đã bị tan rã và phân hủy hết thì chúng tôi lại được làm cho sống lại một lần nữa chăng?! Điều đó không thể nào.');
INSERT INTO "vn_mokhtasar" VALUES(17,50,'17:50','Hỡi Thiên Sứ Muhammad, Ngươi hãy bảo những kẻ thờ đa thần: nếu có khả năng các ngươi hãy là đá và sắt để trở nên cứng và cường lực, nhưng chắc chắn các ngươi không bao giờ có khả năng.');
INSERT INTO "vn_mokhtasar" VALUES(17,51,'17:51','Hoặc các ngươi hãy là tạo vật nào khác mà tâm trí các ngươi cho rằng nó cứng nhất thì chắc chắn Allah sẽ tái tạo các ngươi trở lại như lúc ban đầu mà Ngài đã tạo các ngươi. Những kẻ thờ đa thần này sẽ nói: Ai sẽ làm cho bọn ta sống lại sau khi chết? Ngươi (Muhammad) hãy bảo chúng: Đấng đã tạo hóa các người lần đầu sẽ tái tạo các ngươi trở lại. Những kẻ vô đức tin này lại bảo: sự trở lại này biết bao giờ mới xảy ra chứ? Ngươi hãy bảo chúng: e rằng nó rất gần.');
INSERT INTO "vn_mokhtasar" VALUES(17,52,'17:52','Allah sẽ hoàn sự sống cho các người trở lại vào Ngày mà các ngươi được gọi đến một nơi "đại triệu tập" và các người phải đáp lời gọi đó bằng sự tán dương và ca tụng Ngài, và các người nghĩ rằng mình mới chỉ ở trên trái đất một khoảng thời gian ngắn ngủi.');
INSERT INTO "vn_mokhtasar" VALUES(17,53,'17:53','Hỡi Thiên Sứ Muhammad, Ngươi hãy bảo những bề tôi có đức tin của TA (Allah) nói lời nói tốt lành khi nói chuyện với nhau và tránh xa những lời nói xấu bởi Shaytan sẽ luôn tìm mọi cách gieo rắc điều xấu trong lòng của các ngươi để các ngươi hành động tội lỗi hủy hoải cuộc sống của các ngươi trên thế gian và cõi Đời Sau. Quả thật, Shaytan là kẻ thù công khai của con ngươi nền cần phải cảnh giác.');
INSERT INTO "vn_mokhtasar" VALUES(17,54,'17:54','Này hỡi nhân loại, Thượng Đế của các ngươi biết rõ về các ngươi nhất. Bởi thế, không có bất cứ điều gì có thể che giấu được Ngài, nếu muốn, Ngài sẽ thương xót và khoan dung cho các ngươi bởi các ngươi đã có đức tin và làm việc thiện tốt, và nếu muốn, Ngài sẽ trừng phạt và bỏ rơi các ngươi do không có đức tin( iman) cũng như Ngài sẽ làm cho các ngươi chết vì sự phủ nhận. Và TA (Allah) không cử Ngươi (Muhammad) làm đại diện của TA cưỡng ép chúng tin và ngăn cấm chúng vô đức tin mà TA cử Ngươi đến chỉ làm một người truyền đạt những gì mà TA ra lệnh cho Ngươi phải truyền đạt.');
INSERT INTO "vn_mokhtasar" VALUES(17,55,'17:55','Hỡi Thiên Sứ Muhammad, Thượng Đế của Ngươi biết rõ nhất về tất cả những ai tồn tại trong trời đất, và Ngài biết rõ tất cả mọi tình trạng của họ và ai trong số họ xứng đáng được những gì. Quả thật, TA (Allah) đã làm cho một vị Thiên Sứ này vượt trội hơn một số vị Thiên Sứ khác bởi những tín đồ đi theo cũng như bởi các Kinh Sách được ban xuống, và TA đã ban cho Dawood một Kinh Sách, đó là Kinh Zabur.');
INSERT INTO "vn_mokhtasar" VALUES(17,56,'17:56','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần: Hỡi những người thờ đa thần, các ngươi hãy cứ cầu xin các thần linh mà các ngươi khẳng định chúng là thần linh đích thực của các người ngoài Allah, nếu một tai họa nào đó được giáng xuống cho các người thì chúng - các thần linh đó - không có khả năng xua đuổi tai họa đó khỏi các người và chúng cũng không thể chuyển đổi tai họa đó sang người khác. Và ai bất lực thì không thể nào là thần linh.');
INSERT INTO "vn_mokhtasar" VALUES(17,57,'17:57','Những kẻ mà chúng cầu xin khấn vái từ các vị Thiên Thần cũng như những ai khác thì bản thân những kẻ được cầu xin van ván đó cũng đang tìm mọi phương cách để được đến gần Allah qua các việc làm ngoan đạo và thiện tốt, họ cũng thi đua nhau trong việc đến gần Allah bằng sự vâng lệnh Ngài, bởi họ cũng hy vọng lòng nhân từ của Ngài và sợ sự trừng phạt của Ngài. Quả thật, sự trừng phạt của Thượng Đế của Ngươi - hỡi Thiên Sứ Muhammad - là điều cần phải được lo sợ.');
INSERT INTO "vn_mokhtasar" VALUES(17,58,'17:58','Không một thị trận hay thành phố nào mà TA (Allah) không giáng xuống hình phạt và hủy diệt trên cuộc sống trần gian này bởi sự vô đức tin của nó,hoặc bị trừng phạt dữ dội bởi sự chết chóc hoặc sự trừng phạt khác nữa do tội phủ nhận.Bởi lẽ sự hủy diệt và sự trừng phạt đó là tiền định đã được ghi khắc trong Bản văn lưu trử trên cao');
INSERT INTO "vn_mokhtasar" VALUES(17,59,'17:59','Và TA (Allah) không ban xuống những Dấu Lạ chứng minh sự trung thực của Thiên Sứ Muhammad khi những kẻ đa thần đòi để chứng thực như làm cho người chết sống lại hoặc những Dấu Lạ tương tư bởi vì TA cũng đã từng ban xuống những Dấu Lạ cho các cộng đồng trước đây nhưng chúng đều phủ nhận. Quả thật, TA đã ban cho người dân Thamud một Dấu Lạ vĩ đại, đó là một con lạc đà cái, nhưng chúng đã không tin và chúng buộc TA nhanh chóng trừng phạt chúng. Và quả thật, TA không gửi xuống các Dấu Lạ trên tay của các vị Thiên Sứ trừ phi vì mục đích làm cho cộng đồng của họ sợ mà qui phục.');
INSERT INTO "vn_mokhtasar" VALUES(17,60,'17:60','Hỡi Thiên Sứ Muhammad, hãy nhớ lại khi TA (Allah) phán với Ngươi: Quả thật, Thượng Đế của Ngươi bao trùm nhân loại bằng quyền năng của Ngài nên họ đã ở trong sự mục kích của Ngài nhưng Ngài đã ngăn không để Ngươi cùng tình cảnh với họ. Do đó, Ngươi hãy truyền đạt những gì TA ra lệnh cho Ngươi phải truyền đạt. Và những gì mà TA đã phơi bày cho Ngươi thấy tận mắt trong đêm dạ hành là chỉ nhằm thử thách nhân loại để xem ai tin và ai không tin. Và cái cây Zaqqum mà TA đã đề cập trong Qur''an rằng nó mọc ở dưới đáy Hỏa Ngục là nhằm thử thách họ. Nếu họ không tin hai dấu hiệu đó thì sẽ không bao giờ tin những gì khác. Và TA đã đe dọa họ bởi những Dấu Lạ nhưng chúng càng trở nên thái quá và lầm lạc hơn.');
INSERT INTO "vn_mokhtasar" VALUES(17,61,'17:61','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại lúc TA (Allah) phán với các Thiên Thần: các ngươi hãy quì xuống phủ phục Adam mang tính chất chào hỏi chứ không mang tính thờ phượng. Lúc đó, tất cả đều thực thi mệnh lệnh, tất cả đều quì xuống phủ phục trừ tên Iblis, hắn ngạo mạn không chịu quì xuống phủ phục theo lệnh của TA, hắn nói: sao bề tôi phải phủ phục trước một kẻ được tạo ra từ đất trong khi Ngài đã tạo bề tôi từ lửa cao quí hơn nó?');
INSERT INTO "vn_mokhtasar" VALUES(17,62,'17:62','Iblis nói với Thượng Đế của hắn: Chẳng lẽ Ngài xem tên tạo vật này cao quí hơn bề tôi và phải bắt bề tôi phủ phục trước hắn ư? Nếu Ngài gia hạn tuổi thọ cho bề tôi đến thời gian cuối cùng của cõi trần thì chắc chắn bề tôi sẽ lôi kéo con cháu của hắn lạc khỏi con đường Ngay Chính của Ngài trừ một ít những ai mà Ngài đã bảo vệ, đó là những người bề tôi chân tâm hướng về Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(17,63,'17:63','Thượng Đế phán với Iblis: Ngươi hãy cút đi, và những ai theo ngươi thì Hỏa Ngục sẽ là phần thưởng dành cho Ngươi và chúng, một phần thưởng thích đáng cho những việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(17,64,'17:64','Allah phán bảo Iblis: Cứ tùy khả năng của ngươi, ngươi hãy dùng tất cả khả năng ngươi có được để cám dỗ chúng (con cháu của Adam), ngươi có thể dùng âm thanh quyến rũ để lôi kéo chúng, ngươi hãy trang hoàng trong tài sản của chúng để lôi kéo chúng đi ngược với giáo lý của TA, ngươi hãy dùng con cái của chúng để lôi kéo chúng rơi vào tội dối trá, ngươi hãy lôi kéo chúng rơi vào tội Zina, ngươi hãy lôi kéo chúng rời xa Allah và hãy cho chúng lời hứa. Nhưng lời hứa của Shaytan chỉ là hảo huyền đầy giả dối.');
INSERT INTO "vn_mokhtasar" VALUES(17,65,'17:65','Quả thật, các bề tôi có đức tín ngoan đạo của TA sẽ là những người mà ngươi không có thẩm quyền chi phối họ bởi Allah sẽ bảo vệ họ khỏi điều xấu của ngươi. Và Allah đủ là Đấng để cho ai muốn phó thác và tin cậy nơi Ngài trong mọi vụ việc.');
INSERT INTO "vn_mokhtasar" VALUES(17,66,'17:66','Hỡi nhân loại, Thượng Đế của các ngươi là Đấng làm cho con tàu đưa các ngươi di chuyển được trên biển cả để các ngươi đi tìm bổng lộc của Ngài. Quả thật, Ngài Thương Xót các ngươi khi mà Ngài đã ban cho các ngươi các phượng tiện này.');
INSERT INTO "vn_mokhtasar" VALUES(17,67,'17:67','Hỡi những người thờ đa thần, khi các ngươi gặp hoạn nạn trên biển cả đến mức các ngươi sợ mình bị diệt vong thì các ngươi không nghĩ tới những thần linh mà các ngươi thờ phượng ngoài Allah, các ngươi không nhắc đến họ ngoại trừ Allah, các ngươi chỉ cầu xin Ngài cứu giúp. Nhưng khi Ngài cứu các ngươi thoát nạn, giúp các ngươi được an toàn khỏi điều các ngươi sợ hãi, các ngươi vào bờ thì liền quay lưng với Ngài, các ngươi trở lại với những bục tượng của các ngươi. Quả thật, con người thường phủ nhận ân huệ của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(17,68,'17:68','Há các ngươi cảm thấy an toàn - hỡi những người thờ đa thần - khi Allah cho đất nuốt mất các ngươi ư? Hoặc phải chăng các ngươi cảm thấy an toàn khi Ngài cho cơn mưa đá rơi xuống các ngươi giống như Ngài đã làm với cộng đồng của Lut rồi các ngươi sẽ không tìm thấy được vị cứu tinh nào giúp đỡ các ngươi tránh khỏi sự hủy diệt tàn khốc hay sao?.');
INSERT INTO "vn_mokhtasar" VALUES(17,69,'17:69','Hoặc các ngươi cảm thấy an toàn chẳng khi Allah đưa các ngươi trở lại biển cả một lần nữa rồi gởi đến một trận cuồng phong để nhấn chìm các ngươi do sự vong ân của các ngươi về ân huệ của Ngài khi Ngài đã cứu các ngươi lần đầu; rồi các ngươi không tìm thấy bất cứ vị cứu tinh nào có thể giúp được các ngươi?!');
INSERT INTO "vn_mokhtasar" VALUES(17,70,'17:70','Quả thật, TA (Allah) đã ưu ái cho con cháu của Adam (nhân loại) với nguồn trí tuệ và đã làm vinh danh cho họ khi TA bắt các Thiên Thần phủ phục trước người cha của họ (Adam), TA đã chế ngự biển cả chuyên chở họ trên những phương tiện di chuyển, TA đã ban bổng lộc tốt lành và thanh sạch cho họ và TA đã làm cho họ vượt trội và phúc lành hơn các tạo vật khác. Bởi thế, họ phải biết ơn ân huệ của Allah đã ban tặng cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(17,71,'17:71','Và Ngươi hãy nhớ lại - hỡi Thiên Sứ Muhammad - Ngày mà TA (Allah) hiệu lệnh tập trung tất cả mỗi nhóm cùng với vị lãnh đạo của y mà từng nhóm đã đi theo. Những ai được đưa cho quyển sổ ghi chép các việc làm của y từ phía tay phải thì họ sẽ đọc quyển sổ của họ một cách vui mừng, họ sẽ không bị cắt xén bất cứ thứ gì từ phần thưởng của họ cho dù chỉ nhỏ bằng lỗ kim may.');
INSERT INTO "vn_mokhtasar" VALUES(17,72,'17:72','Ai ở trên thế gian với trái tim mù lòa không chịu đón nhận chân lý và luôn quay lưng với nó thì vào Ngày Phục Sinh y sẽ còn mù lòa hơn thế, y sẽ lạc khỏi con đường dẫn đến Thiên Đàng; và sự thưởng phạt tương ứng với từng việc làm.');
INSERT INTO "vn_mokhtasar" VALUES(17,73,'17:73','Quả thật, những người thờ đa thần đã âm mưu dụ dỗ Ngươi - hỡi Thiên Sứ Muhammad - bỏ những điều TA (Allah) đã mặc khải cho Ngươi từ Qur''an và xúi Ngươi bịa ra những điều khác phù hợp với ham muốn và sở thích của chúng. Nếu Ngươi làm theo điều chúng muốn thì chúng sẽ chọn ngươi làm người bạn thân tình của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(17,74,'17:74','Nếu TA (Allah) không ban ân cho Ngươi trong việc làm cho Ngươi vững lòng trên điều chân lý thì quả thật Ngươi suýt xiêu lòng theo chúng và đồng ý làm theo những gì chúng đề nghị,vì sức mạnh lừa dối cũng như xảo trá của chúng cùng với sự tràn ngập thắm thía của Ngươi trên đức tin thần túy của chúng,nhưng TA đã bảo vệ và ngăn cản Ngươi xiêu lòng theo chúng');
INSERT INTO "vn_mokhtasar" VALUES(17,75,'17:75','Nếu Ngươi - Thiên Sứ Muhammad - đi theo chúng về những gì mà chúng đề nghị thì chắc chắn Ngươi sẽ phải hứng chịu sự trừng phạt của TA (Allah) gấp đôi trên cuộc đời này và cả ở Đời Sau, và chắc chắn Ngươi sẽ không tìm được một vị cứu tin nào giúp Ngươi thoát được sự trừng phạt của TA.');
INSERT INTO "vn_mokhtasar" VALUES(17,76,'17:76','Quả thật, những kẻ thờ đa thần với lòng hận thù Ngươi đã âm mưu trục xuất Ngươi khỏi quê hương của Ngươi (Makkah) nhưng Allah đã ngăn điều đó cho tới khi Ngài ra lệnh cho Ngươi di cư rời bỏ nơi đó, và cho dù chúng có trục xuất được Ngươi thì chúng cũng không ở lại được nơi đó lâu ngoại trừ một khoảng thời gian ngắn ngủi.');
INSERT INTO "vn_mokhtasar" VALUES(17,77,'17:77','Việc không để chúng ở lại sau khi Ngươi (Muhammad) ra đi ngoại trừ một thời gian ngắn ngủi là đường lối của Allah đã được qui định cho các vị Thiên Sứ trước Ngươi, có nghĩa là bất kỳ vị Thiên Sứ nào bị dân của Y đuổi ra khỏi xứ thì Allah đều giáng xuống sự trừng phạt lên họ, và Ngươi - hỡi vị Thiên Sứ Muhammad - sẽ không tìm thấy sự thay đổi nào trong đường lối này của TA, mà Ngươi sẽ thấy nó luôn cố định.');
INSERT INTO "vn_mokhtasar" VALUES(17,78,'17:78','Hãy chỉnh chu duy trì lễ nguyện Salah từ lúc mặt trời nghiêng bóng bao gồm lễ nguyện Salah Zhuhr và Asr, cho đến đêm tối bao gồm lễ nguyện Salah Maghrib và ''I-sha''; và hãy chu đáo duy trì lễ nguyện Salah Fajr và hãy đọc Qur''an nhiều trong lễ nguyện Salah Fajr này được chứng giám bởi các vị Thiên Thần trực ban đêm và các vị Thiên Thần trực ban ngày.');
INSERT INTO "vn_mokhtasar" VALUES(17,79,'17:79','Hỡi Thiên Sứ Muhammad,Ngươi hãy thức dậy một phần thời gian của ban đêm để thực hiện một số lễ nguyện Salah tự nguyện được gọi lễ nguyện Salah Tahajjud (tức sự nỗ lực trong đêm) để được nâng lên cấp bậc ở nơi Thượng Đế của Ngươi bởi lẽ vào Ngày Phán Xét Ngươi sẽ được Ngài cử làm người được quyền cầu xin ân xá cho nhân loại và Ngươi sẽ là người mang một địa vị vinh dự đáng được khen ngợi nhất từ những người đầu tiên cho đến những người cuối cùng');
INSERT INTO "vn_mokhtasar" VALUES(17,80,'17:80','Hỡi Thiên Sứ Muhammad, Ngươi hãy cầu nguyện Thượng Đế của Ngươi, nói: "Lạy Thượng Đế của bề tôi, xin Ngài hãy để bề tôi đi vào Madinah và đi ra khỏi Makkah bằng lối đi của sự tuân lệnh Ngài và làm Ngài hài lòng, và xin Ngài ban cho bề tôi một lý do được yểm trợ nơi Ngài đối với kẻ thù của bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(17,81,'17:81','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần: Islam đã đến, đó là chân lý, lời hứa của Allah ban cho nó sự thắng lợi đã được chứng thực, sự đa thần và vô đức tin sẽ bị đánh bại bởi nó là điều ngụy tạo, và điều ngụy tạo không đứng vững trước điều chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(17,82,'17:82','Allah ban xuống trong Qur''an, có những điều dùng để chữa tâm bệnh từ sự ngu muội, vô đức tin và thờ đa thần, có những điều dùng để chữa bệnh tật của thể xác và có những điều là hồng ân cho những người có đức tin ngoan đạo. Tuy nhiên, Qur''an không mang lại thêm điều tốt đẹp cho những kẻ vô đức tin ngoại trừ sự diệt vong vì tai của chúng đã bị che lại và Qur''an không ban thêm gì cho chúng ngoại trừ sự phủ nhận và chống đối.');
INSERT INTO "vn_mokhtasar" VALUES(17,83,'17:83','Khi con người được ban cho ân huệ như sức khỏe, sự giàu có thì y quay lưng không biết ơn Allah, không tuân lệnh Ngài mà cứ mãi lánh xa Ngài một cách tự cao tự đại; nhưng khi y gặp bệnh tật, nghèo khó và những bất hạnh khác thì y chỉ biết tuyệt vọng và chán nản trước lòng nhân từ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(17,84,'17:84','Hỡi Thiên Sứ Muhamamd, Ngươi hãy nói: Tất cả mỗi người hãy cứ làm theo đường lối của riêng y, đường lối mà nó mập mờ giữa sự hướng dẫn và lầm lạc. Và Thượng Đế của các ngươi mới là Đấng biết rõ nhất là người đi trên con đường hướng dẫn đến chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(17,85,'17:85','Hỡi Thiên Sứ Muhammad, những kẻ vô đức tin thuộc dân Kinh Sách hỏi Ngươi về sự thật của linh hồn, Ngươi hãy nói với bọn chúng: không ai biết sự thật về linh hôn ngoại trừ Allah, các ngươi và tất cả nhân loại chỉ được cho biết một ít về nó.');
INSERT INTO "vn_mokhtasar" VALUES(17,86,'17:86','Và nếu muốn, Allah sẽ lấy đi mất khỏi Ngươi - hỡi Thiên Sứ Muhammad - những gì mà Ngài đã mặc khải cho Ngươi bằng cách xóa bỏ hoàn toàn khỏi tấm lòng của các ngươi và các Kinh Sách. Rồi Ngươi sẽ không thể tìm thấy cứu giúp Ngươi chống lại việc làm này của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(17,87,'17:87','Tuy nhiên, TA (Allah) không lấy nó đi vì lòng nhân từ và thương xót của TA dành cho Ngươi (Muhammad), TA đã bảo quản nó. Quả thật ân phúc mà Thượng Đế của Ngươi ban cho Ngươi là vô cùng lớn lao khi Ngài đã chọn Ngươi làm vị Thiên Sứ, lấy Ngươi làm vị Nabi cuối cùng và ban kinh Qur''an xuống cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(17,88,'17:88','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần: nếu loài người và loài Jinn có hợp sức nhau lại để làm ra một quyển kinh giống như Kinh Qur''an được ban xuống cho Ngươi thì họ cũng không tài nào làm ra được cho dù họ có cố gắng giúp đỡ nhau như thế nào đi nữa.');
INSERT INTO "vn_mokhtasar" VALUES(17,89,'17:89','Quả thật, TA (Allah) đã trình bày cho nhân loại biết trong Qur''an này rất rõ ràng, và TA dùng nhiều lời khuyên răn cùng với những câu chuyện thí dụ làm bài học với hy vọng họ sẽ tin tưởng nhưng đa số nhân loại đều từ chối cũng như phủ nhận Kinh Qur''an này');
INSERT INTO "vn_mokhtasar" VALUES(17,90,'17:90','Những kẻ thờ đa thần nói với Thiên Sứ Muhammad: bọn ta sẽ không bao giờ tin Ngươi trừ phi Ngươi làm cho phụt ra một con suối từ vùng đất Makkah này chảy xiết không ngừng');
INSERT INTO "vn_mokhtasar" VALUES(17,91,'17:91','Hoặc Ngươi làm ra hai ngôi vườn với nhiều cây cối và chính giữa chúng là những dòng sông chảy.');
INSERT INTO "vn_mokhtasar" VALUES(17,92,'17:92','Hoặc Ngươi hãy làm cho trời sụp xuống đè nát bọn ta giống như Ngươi đã đề cập về những hình phạt, hoặc Ngươi hãy mang Allah và các Thiên Thần xuống cho bọn ta thấy tận mặt để bọn ta xác nhận rằng Ngươi nói thật.');
INSERT INTO "vn_mokhtasar" VALUES(17,93,'17:93','Hoặc Ngươi có một ngôi nhà được trang trí bằng vàng và những thứ khác hoặc Ngươi đi lên trời nhưng bọn ta không bao giờ tin ngươi đi lên trời trừ phi Ngươi mang xuống cho bọn ta một quyển Kinh Sách trọn vẹn từ nơi Allah để bọn ta đọc trong đó biết rằng ngươi đích thực là Thiên Sứ của Allah. Ngươi hãy nói với bọn chúng hỡi Thiên Sứ Muhammad: Vinh quang thay Thượng Đế của ta! Há chẳng phải ta đây chỉ là một con người phàm tục được Allah chọn làm vị Thiên Sứ giống các vị Thiên Sứ khác đó sao, ta không hề sở hữu bất cứ quyền năng nào thì làm sao ta có thể mang đến những gì các ngươi đòi hỏi?!');
INSERT INTO "vn_mokhtasar" VALUES(17,94,'17:94','Không có điều gì cản trở những kẻ vô đức tin tin nơi Allah và Thiên Sứ của Ngài và làm theo những gì Thiên Sứ của Ngài mang đến ngoại trừ một điều rằng họ phủ nhận việc Thiên Sứ là con người phàm tục khi mà họ nói: “Há Allah đã dựng một người phàm làm một Sứ Giả (của Ngài) ư?”');
INSERT INTO "vn_mokhtasar" VALUES(17,95,'17:95','Hỡi Thiên Sứ Muhammad, hãy đáp phản hồi lại bọn chúng: Nếu trên đất các vị Thiên Thần có thể sống và di chuyển một cách yên ổn giống như tình trạng của các ngươi thì chắc chắn TA (Allah) sẽ gửi một vị Thiên Thần từ cùng loài với chúng, bởi vì để y có thể làm cho chúng hiểu những gì y được gửi đến, và lúc đó sẽ không còn ý nghĩa gì nữa cho việc TA gửi một vị Thiên Sứ cho từ loài người.');
INSERT INTO "vn_mokhtasar" VALUES(17,96,'17:96','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói đi: Một mình Allah thôi đủ làm chứng giữa Ta và các người rằng đích thực Ta là vị Thiên Sứ được cử phái đến cho các người và rằng Ta đã truyền đạt cho các người những gì Ta được giao cho nhiệm vụ phải truyền đạt. Quả thật, Ngài là Đấng Thông Toàn hết mọi tình trạng của đám bề tôi của Ngài, không có một điều gì có thể che giấu được Ngài, Ngài nhìn thấu tất cả mọi điều thầm kín ẩn giấu trong bản thân họ.');
INSERT INTO "vn_mokhtasar" VALUES(17,97,'17:97','Ai được Allah phù hộ đến với sự hướng dẫn thì y là người được hướng dẫn thực sự, còn ai mà Ngài đánh lạc hướng thì sẽ lầm lạc và Ngươi - Muhammad - sẽ không bao giờ tìm thấy được cho chúng người hướng dẫn chúng đến với chân lý, Ngươi sẽ không bao giờ tìm thấy được cho chúng người chống lại tai họa giùm cho chúng và mang lại cho chúng phúc lành; và vào Ngày Phán Xét, TA (Allah) sẽ triệu tập chúng lại và chúng sẽ bị lôi kéo úp mặt xuống đất, chúng không nhìn thấy, không nói chuyện và không nghe được, và chỗ ở của chúng chắc chắn là Hỏa Ngục, và khi nào lửa của nó giảm thì TA sẽ tăng nó trở lại.');
INSERT INTO "vn_mokhtasar" VALUES(17,98,'17:98','Hình phạt mà chúng phải hứng chịu như thế là phần thưởng thích đáng cho sự vô đức tin của chúng nơi các lời mặc khải mà TA (Allah) đã ban xuống cho vị Thiên Sứ của TA (Muhammad), và cho lời nói phủ nhận sự phục sinh của chúng: chẳng lẽ sau khi chúng tôi chết đi, thân xác và xương cốt của chúng tôi đã rã mục thì chúng tôi vẫn được làm cho sống lại thành một cơ thể mới ư? Ý phủ định điều này không thể xảy ra.');
INSERT INTO "vn_mokhtasar" VALUES(17,99,'17:99','Chẳng lẽ những kẻ phủ nhận sự phục sinh này không biết rằng Allah là Đấng đã tạo hóa các tầng trời và trái đất vĩ đại thế kia lại không đủ khả năng tạo ra những tạo vật giống như chúng sao? Bởi ai có khả năng tạo ra cái vĩ đại thì dĩ nhiên người đó phải có khả năng tạo ra cái bé hơn. Quả thật, Allah chỉ cho phép chúng sống trên thế gian này trong một khoảng thời gian ngắn ngủi rồi phải chấm dứt thời hạn để Ngài phục sinh chúng trở lại, điều mà chúng phủ nhận. Tuy nhiên, những kẻ làm điều sai quấy cứ quay lưng không chịu tin.');
INSERT INTO "vn_mokhtasar" VALUES(17,100,'17:100','Hãy nói - hỡi Thiên Sứ Muhammad - với những kẻ thờ đa thần này: Nếu các người nắm giữ những kho tàng hồng ân của Thượng Đế của Ta thì các người sẽ không chi dùng vì các ngươi sợ mình sẽ trở nên nghèo, và bản chất của con người thường keo kiệt trừ phi đó là người có đức tin thì y sẽ chi dùng vì Allah với hy vọng phần thưởng nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(17,101,'17:101','Và quả thật, TA (Allah) đã ban cho Musa chín Dấu Lạ rõ rệt để chứng minh sứ mạng của Y, đó là cây gậy, tay, hạn hán, thất bát mùa màng, lũ lụt, châu chấu, rận, cóc và máu. Ngươi - Muhammad - hãy hỏi những người Do Thái khi mà Musa mang những Dấu Lạ này đến. Fir-''awn nói với Musa: quả thật, Ta nghĩ ngươi - hỡi Musa - là một tên bùa thuật khi mà ngươi mang đến những điều kỳ lạ.');
INSERT INTO "vn_mokhtasar" VALUES(17,102,'17:102','Musa đáp lại lời của Fir-''awn: Này Fir-''awn, chắc chắn ngài biết rõ rằng những dấu hiệu này chỉ được ban xuống bởi Allah,Thượng Đế của các tầng trời và trái đất. Ngài ban chúng xuống để chứng tỏ quyền năng của Ngài và xác nhận sự trung thực của vị Thiên Sứ của Ngài. Tuy nhiên, ngài đã từ chối tin và Ta xin thông báo cho ngài biết rằng - hỡi Fir-''awn - rằng ngài sớm bị thất bại và diệt vong.');
INSERT INTO "vn_mokhtasar" VALUES(17,103,'17:103','Bởi thế, Fir-''awn muốn trừng phạt Musa người dân của Y ra khỏi lãnh thổ Ai Cập. Cho nên, TA (Allah) đã nhấn chìm hắn và toàn bộ thuộc hạ của hắn dưới biển Hồng hải.');
INSERT INTO "vn_mokhtasar" VALUES(17,104,'17:104','Sau khi đã tiêu diệt Fir-''awn và binh lính của hắn, TA (Allah) phán bảo dân Isra-el: Các ngươi hãy sống ở vùng đất Sham này, rồi khi đến Ngày Tận Thế thì TA sẽ tập trung tất cả các ngươi lại tại nơi triệu tập để phán xét.');
INSERT INTO "vn_mokhtasar" VALUES(17,105,'17:105','Và TA (Allah) đã ban Qur''an xuống cho Muhammad là sự thật và Qur''an đó không hề có sự bóp méo và chỉnh sửa. Và TA chỉ phái Ngươi - hỡi Thiên Sứ Muhammad - đến để làm công việc báo tin mừng về Thiên Đàng cho những người ngoan đạo và cảnh báo về sự trừng phạt nơi Hỏa Ngục đến những kẻ vô đức tin và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(17,106,'17:106','TA (Allah) đã ban Kinh Qur''an xuống và phân Nó ra thành từng phần hy vọng Ngươi (Muhammad) đọc Nó cho nhân loại một cách từ tốn và ngân nga; và Qur''an được yêu cầu phải hiểu và suy ngẫm về Nó. Và TA đã ban Kinh Qur''an xuống theo từng sự kiện và hoàn cảnh xảy ra của sự việc qua các thời đoạn khác nhau.');
INSERT INTO "vn_mokhtasar" VALUES(17,107,'17:107','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói: dù các ngươi tin Nó thì niềm tin của các ngươi cũng không tăng được gì và dù các ngươi không tin Nó thì sự vô đức tin của các ngươi cũng không giảm được gì. Quả thật, những ai đã đọc các Thiên Kinh được ban xuống trước đây sẽ nhận biết được lời mặc khải và sứ mạng Nabi khi họ nghe được Qur''an và họ sẽ quỳ mọp xuống đất mà phủ phục tạ ơn Allah.');
INSERT INTO "vn_mokhtasar" VALUES(17,108,'17:108','Và họ sẽ nói khi đang quỳ lạy: Vinh quang và trong sạch thay Thượng Đế của bầy tôi khi Ngài không thất hứa, Ngài đã hứa sẽ cử phái Muhammad đến và điều đó đã được thể hiện. Quả thật, Thượng Đế của bầy tôi một khi đã hứa một điều gì đó thì chắc chắn nó sẽ xảy ra đúng như vậy.');
INSERT INTO "vn_mokhtasar" VALUES(17,109,'17:109','Họ quỳ mọp xuống đất phủ phục Allah trong tiếng khóc; và việc nghe Qur''an và suy ngẫm ý nghĩa của Nó làm cho họ càng trở nên hạ mình và kính sợ Allah hơn nữa.');
INSERT INTO "vn_mokhtasar" VALUES(17,110,'17:110','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những ai phản đối cho việc Ngươi cầu nguyện với lời "Ôi Allah, Ôi Đấng Al-Rrahman (Rất Mực Độ Lượng)!": Allah và Al-Rrahman là hai đại danh của Thượng Đế, các ngươi hãy cầu nguyện Ngài với hai đại danh đó hoặc những đại danh khác của Ngài bởi vì Ngài những tên gọi tốt đẹp và hoàn hảo nhất. Và khi Ngươi - Muhammad - đọc Qur''an trong lễ nguyện Salah thì Ngươi chớ đọc quá lớn để những kẻ thờ đa thần nghe và Ngươi cũng chớ đọc quá nhỏ kẻo những người có đức tin không nghe được, Ngươi hãy đọc ở mức trung bình giữa sự việc đó.');
INSERT INTO "vn_mokhtasar" VALUES(17,111,'17:111','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói: Mọi lời ca ngợi và tán dương kính dâng Allah, Đấng đáng được khen ngợi với muôn vàn lời ca tụng và tán dương, Đấng mà Ngài không có con vì Ngài là Đấng Tạo Hóa, Đấng đáng được thờ phượng duy nhất vì Ngài không có đối tác ngang vai trong việc chia sẻ vương quyền, Đấng Quyền Năng độc nhất không cần sự giúp đỡ và ủng hộ của bất cứ ai (vật) ngoài Ngài, Ngài vĩ đại trên mọi sự vĩ đại, Ngài tối cao trên mọi sự tối cao. Bởi thế, hãy tôn vinh sự vĩ đại của Ngài với sự vĩ đại đúng nghĩa của Ngài rằng Ngài không có con và không có đối tác ngang vai và cũng không có kẻ trợ lực.');
INSERT INTO "vn_mokhtasar" VALUES(18,1,'18:1','Tán dương và ca ngợi Allah bởi các thuộc tính Tối Cao và Hoàn Hảo cũng như bởi các hồng ân công khai và thầm kín của Ngài, Ngài là Đấng Duy Nhất đã ban xuống cho người bề tôi của Ngài - Muhammad - Kinh Qur''an, và Ngài đã không làm cho Kinh Qur''an này cong quẹo khỏi sự thật.');
INSERT INTO "vn_mokhtasar" VALUES(18,2,'18:2','Kinh Qur''an không công quẹo khỏi những điều chân lý mà Nó luôn ngay thẳng, không có sự mâu thuận và mơ hồ trong đó. Nó được dùng để cảnh báo những kẻ vô đức tin về sự trừng phạt mạnh bạo đang chờ đợi họ ở nơi Allah đồng thời báo tin cho những người có đức tin và làm việc thiện tốt và ngoan đạo về phần thưởng tốt đẹp mà Allah đã dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(18,3,'18:3','Họ sẽ ở trong phần thượng này (Thiên Đàng) mãi mãi, những điều tốt đẹp trong đó không bao bị gián đoạn hay chấm dứt.');
INSERT INTO "vn_mokhtasar" VALUES(18,4,'18:4','Nó cảnh báo những người Do Thái, Thiên Chúa giáo và một số những kẻ đa thần đã nói: Allah có một đứa con trai.');
INSERT INTO "vn_mokhtasar" VALUES(18,5,'18:5','Những kẻ nói dối này không hề có kiến thức hay bằng chứng gì cho điều họ khẳng định rằng Allah có một đứa con trai. Và cha mẹ của họ cũng vậy, chỉ biết nói bắt chước theo nhau, chứ không hề có kiến thức hay dựa theo bất cứ cơ sở nào. Thật nghiêm trọng cho lời thốt ra từ cửa miệng của họ một cách tùy tiện. Những điều họ nói chỉ toàn là dối trá và bịa đặt chẳng có một cơ sở nào.');
INSERT INTO "vn_mokhtasar" VALUES(18,6,'18:6','Hỡi Thiên Sứ Muhammad, có lẽ Ngươi đau buồn đến nỗi làm tổn hại thân thể của Ngươi cho việc chúng đã không tin nơi Qur''an này. Ngươi chớ đừng như thế, bởi sự hướng dẫn chúng không phải ở bản thân Ngươi mà Ngươi chỉ là một người truyền đạt mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(18,7,'18:7','Quả thật, tất cả những gì TA (Allah) tạo ra trên mặt đất này chỉ để trang hoàng và tô điểm cho đẹp và để TA dùng chúng thử thách nhân loại xem ai trong số họ hoàn thành tốt nhất những việc làm làm TA hài lòng và ai trong số họ hành động xấu nhất để TA thưởng phạt chúng một cách thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(18,8,'18:8','Quả thật, TA (Allah) sẽ làm cho mọi thứ trên trái đất trở thành một hoang mạc khô cằn không một cây cỏ. Và sự việc đó là sau khi mọi sự sống trên nó đã chấm dứt. Bởi thế, các ngươi hãy quan tâm đến sự việc đó.');
INSERT INTO "vn_mokhtasar" VALUES(18,9,'18:9','Hỡi Thiên Sứ Muhammad, Ngươi đừng tưởng câu chuyện của những người trong Hang núi và tấm Bia ký được ghi tên của họ là một kỳ quan trong những Dấu Lạ của TA (Allah) mà còn có những Dấu Lạ khác còn kỳ diệu chẳng hạn như việc tạo hóa các tầng trời và trái đất.');
INSERT INTO "vn_mokhtasar" VALUES(18,10,'18:10','Hỡi Thiên Sứ Muhammad, hãy nhớ lại khi một nhóm thanh niên có đức tin chạy trốn vì tôn giáo của họ. Họ cầu nguyện Thượng Đế của họ, nói: Lạy Thượng Đế của bầy tôi, xin Ngài hãy thương xót và tha thứ tội lỗi của bầy tôi, xin Ngài cứu bầy tôi khỏi kẻ thù của bầy tôi, và xin Ngài hãy làm cho sự việc di cư khỏi những kẻ vô đức tin và mang theo đức tin của bầy tôi đến với con đường đúng đắn.');
INSERT INTO "vn_mokhtasar" VALUES(18,11,'18:11','Rồi sau khi họ vào ẩn nấp trong hang núi thì TA (Allah) cho một tấm màn bịt tai của họ lại để họ không nghe thấy bất cứ âm thanh nào và TA đã làm cho họ ngủ say sưa trong nhiều năm.');
INSERT INTO "vn_mokhtasar" VALUES(18,12,'18:12','Sau một giấc ngủ dài, TA đã đánh thức họ dậy để TA xem một trong hai nhóm của bọn họ giỏi tính toán nhất về thời gian mà lưu lại trong Hang núi đó.');
INSERT INTO "vn_mokhtasar" VALUES(18,13,'18:13','Hỡi Thiên Sứ Muhammad, TA (Allah) cho Ngươi biết thông tin thật về họ, không có gì phải nghi ngờ về thông tin này cả. Quả thật, họ là một nhóm thanh niên có đức tin nơi Thượng Đế của họ, họ tuân thủ mệnh lệnh của Ngài và TA đã gia tăng nguồn chỉ đạo chọ và củng cố họ trên điều chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(18,14,'18:14','TA (Allah) đã củng cố đức tin của họ và họ trở nên kiên nhẫn chịu đựng trên việc di cư rời bỏ quê hương xứ sở của họ. Bằng đức tin nơi Allah duy nhất, họ anh dũng đứng trước tên vua vô đức tin, nói: Thượng Đế mà chúng tôi tin và thờ phượng là Thượng Đế của các tầng trời và trái đất, chúng tôi không bao giờ thờ phượng một thần linh nào khác ngoài Ngài, quả thật, nếu chúng tôi thờ phượng ai khác ngoài Ngài thì đó là một điều xúc phạm tày trời.');
INSERT INTO "vn_mokhtasar" VALUES(18,15,'18:15','Sau đó, họ quay sang nói với nhau: "Những người vô đức tin này trong đám dân của chúng ta, họ đã nhận lấy một đấng thờ phượng khác ngoài Allah nhưng họ chẳng thể đưa ra các bằng chứng rõ rệt cho sự việc đó của họ. Bởi thế, còn ai sai quấy hơn những kẻ đã phủ nhận Allah và tổ hợp với Ngài một đối tác ngang vai."');
INSERT INTO "vn_mokhtasar" VALUES(18,16,'18:16','Và Khi các anh chạy trốn khỏi người dân của các anh, các anh rời bỏ những gì mà họ tôn thờ ngoài Allah bởi vì các anh chỉ tôn thờ một mình Allah, các anh hãy ẩn nấp vào hang núi cùng với tôn giáo của các anh, rồi Thượng Đế của các anh sẽ ban hồng phúc của Ngài xuống cho các anh và bảo vệ các anh khỏi kẻ thù, Ngài sẽ tạo sự dễ dàng cho các anh về những điều có lợi cho các anh.');
INSERT INTO "vn_mokhtasar" VALUES(18,17,'18:17','Bởi vì họ đã làm theo những gì Allah ra lệnh nên Ngài đã ban cho họ một giấc ngủ dài để bảo vệ họ khỏi kẻ thù của họ. Và ngươi - hỡi người chứng kiến họ - thấy mặt trời khi mọc lên ở hướng đông thì nó sẽ nghiêng về phía bên phải của cái hang còn khi nó lặn xuống ở hướng tây thì nó nghiêng về phía bên trái. Họ lúc nào cũng ở trong cái mát mẻ không hề bị quấy nhiễu bởi ánh nắng mặt trời, và trong hang Allah ban đủ cho họ nguồn không khí mà họ cần. Đó là giấc ngủ dài trong hang núi được Allah ban cho nhằm để cứu họ thoát khỏi sự hãm hại từ đám dân của họ, nó là một trong những Dấu Lạ của Allah mà Ngài muốn chứng tỏ quyền năng vô song của Ngài. Ai được Allah hướng dẫn thì y là người được hướng dẫn thực sự, y sẽ tìm thấy con đường chân lý, còn ai bị Allah làm cho lệch hướng thì ngươi sẽ không bao giờ tìm được cho y một hướng dẫn nào bởi sự hướng dẫn và chỉ đạo là ở trong tay Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(18,18,'18:18','Hỡi người đưa mắt nhìn họ, ngươi nghĩ họ đang còn thức vì mắt của họ vẫn còn mở nhưng thực tế là họ đang ngủ. TA (Allah) đã lật trở thân mình của họ một lúc bên phải và một lúc bên trái để mặt đất không thể ăn thân xác của họ. Con chó của họ cũng thế, nó cũng trong giấc ngủ say mặc dù mắt nó vẫn mở và hai chân trước của nó duỗi ra ngay tại cửa hang giống như một kẻ đang canh gác đầy tỉnh táo. Nếu ngươi đến quan sát cảnh tưởng của họ thì chắc chắn các ngươi sẽ bỏ chạy vị sợ trong lòng cũng như bản thân của Ngươi đầy sự khiếp sợ');
INSERT INTO "vn_mokhtasar" VALUES(18,19,'18:19','Sau một thời gian dài trong giấc ngủ, TA (Allah) đánh thức bọn họ dậy. Sau khi thức dậy, họ hỏi nhau rằng họ đã ngủ được bao lâu rồi,một số cho rằng một ngày hay một buổi gì đó.Một số khác do không rõ được thời gian bao lâu nên nói: Thượng Đế của các anh mới biết rõ các anh ngủ được bao lâu, hãy để sự việc đó cho Allah, giờ thì hãy lo cái việc giúp ích cho các anh,các anh hãy cử một người mang tiền đi xuống phố để đổi lấy thức ăn và những vật dụng cần thiết và nhắc anh ta phải quan sát và coi chừng cẩn thân khi đi vào thành cũng như lúc trở ra đừng để một ai phát hiện chỗ ở của các anh nhằm tránh điều không hay.');
INSERT INTO "vn_mokhtasar" VALUES(18,20,'18:20','Nếu người dân của các anh phát hiện ra các anh và biết được chỗ ẩn nấp của các anh thì họ sẽ giết các anh bằng cách ném đá hoặc họ sẽ bắt các anh quay trở lại với tôn giáo lệch lạc trước kia của các anh. Nếu các anh quay trở lại tôn giáó trước đây của các anh thì chắc chắn các anh không bao giờ thành đạt cả trên đời này lẫn cõi Đời Sau, chắn chắn các anh sẽ bị thua thiệt to lớn bởi sự rời bỏ tôn giáo chân lý mà Allah đã hướng dẫn các anh.');
INSERT INTO "vn_mokhtasar" VALUES(18,21,'18:21','Và cũng giống như những Dấu Lạ mà TA (Allah) đã làm để chứng tỏ quyền năng của TA từ việc làm cho họ ngủ với một giấc ngủ dài,rồi đánh thức họ dậy,và rồi TA để cho đám dân thành phố phát hiện ra họ mục đích để đám dân thành phố biết lời hứa giúp những người có đức tin của TA cũng như sự kiện phục sinh là sự thật,và việc Ngày Phán Xét sẽ đến là sự thật không có gì phải nghi ngờ.Và khi những người trong hang núi được phát hiện và họ đã chết sau đó thì những người phát hiện ra họ bàn luận với nhau:Chúng ta sẽ làm gì đối với sự việc này của họ? Một nhóm trong số họ thì bảo: Hãy xây trước cửa hang của họ một bức tường chắn lại để bảo vệ họ,Thượng Đế của họ biết hơn hết về tình trạng của họ.Một số người khác thuộc những con người không có kiến thức cũng như không hiểu chuyện,nói: chúng ta hãy xây một Masjid (Thánh Đường) tại nơi của họ để làm nơi thờ phượng như một sự vinh danh và tưởng nhớ đến chỗ của họ');
INSERT INTO "vn_mokhtasar" VALUES(18,22,'18:22','Một số người thời nay nói về câu chuyện của nhóm thanh niên trong hang núi, họ bàn tán về số lượng những người trong nhóm thanh niên, một số thì nói rằng nhóm của họ gồm ba người và con chó của họ nữa là bốn, một số khác thì bảo nhóm của họ gồm năm người và tính luôn con chó của họ nữa là sáu cả thảy. Hai tốp người này chỉ phỏng đoán không có cơ sở nảo cả. Và có một số người khẳng định rằng nhóm thanh niên trong hang núi gồm bảy người và con chó của họ nữa là tám. Ngươi - hỡi Thiên Sứ Muhammad - hãy nói với họ: Thượng Đế của Ta biết rõ hơn hết về số lượng của nhóm thánh niên đó. Không một ai biết được số lượng của họ ngoại trừ một số ít người mà Allah cho họ biết. Các ngươi chớ tranh luận về số lượng của họ.');
INSERT INTO "vn_mokhtasar" VALUES(18,23,'18:23','Hỡi Thiên Sứ Muhammad, Ngươi chớ đừng nói về một điều gì đó Ngươi định làm vào ngày mai: chắc chắn tôi sẽ làm nó ngay mai. Bởi quả thật, Ngươi không hề biết rõ Ngươi có làm được hay không, sự việc có xảy ra như ý định của Ngươi hay không. Đây là lời chỉ đạo đến tất cả mọi tín đồ Muslim.');
INSERT INTO "vn_mokhtasar" VALUES(18,24,'18:24','Trừ phi Ngươi phải kèm theo lời khẳng định rằng việc làm mà Ngươi định sẽ làm phụ thuộc vào ý muốn của Allah bằng cách nói: tôi sẽ làm nó - Insha-Allah - ngày mai. Và Ngươi hãy nhớ Thượng Đế của Ngươi qua lời của Ngươi: Insha-Allah. Nếu Ngươi quên nói lời đó thì Ngươi hãy nói: Ta hy vọng Allah sẽ hướng dẫn Ta đến gần với Ngài hơn điều này.');
INSERT INTO "vn_mokhtasar" VALUES(18,25,'18:25','Và họ tức đám thanh niên ẩn nấp trong hang núi đã ở lại đó ba trăm lẻ chín năm.');
INSERT INTO "vn_mokhtasar" VALUES(18,26,'18:26','Hãy nói đi - hỡi Thiên Sứ Muhammad -: Allah biết hơn hết về thời gian bao lâu mà nhóm thanh niên kia đã ở lại trong hang núi. Quả thật, Ngài đã cho chúng ta biết thời gian họ ở lại trong đó cho nên sẽ không có câu nói nào của ai khác được chấp nhận sau lời nói của Ngài; bởi lẽ Ngài nắm mọi thứ vô hình trong các tầng trời và trái đất qua sự tạo hóa và kiến thức của Ngài, Ngài hằng thấy tất cả mọi thứ và hằng nghe tất cả mọi điều, họ sẽ không có Đấng Bảo Hộ nào khác ngoài Ngài và không ai có quyền năng chia sẻ với Ngài bất cứ điều gì, Ngài là Đấng Duy Nhất trong việc định đoạt và ban hành luật lệ.');
INSERT INTO "vn_mokhtasar" VALUES(18,27,'18:27','Hỡi Thiên Sứ Muhammad, Ngươi hãy đọc và làm theo những gì được mặc khải cho Ngươi từ Kinh Qur''an, bởi những lời phán của Allah không thay đổi và chúng không cần phải thay đổi vì tất cả những lời trong Qur''an đều là ngay chính và đúng thực. Và Ngươi không bao giờ tìm được chỗ nương tựa nào khác ngoài Ngài, Ngài là nguồn che chở và bảo hộ duy nhất của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(18,28,'18:28','Ngươi - Thiên Sứ Muhammad - hãy bắt bản thân mình đồng hành với những người thành tâm cầu nguyện Thượng Đế của họ sáng chiều, Ngươi chớ đừng không ngó ngàng gì đến họ do chỉ muốn ngồi cùng với những kẻ giàu có và quý tộc, Ngươi cũng chớ nghe theo những kẻ làm trái tim Ngươi xao lãng việc tưởng nhớ TA (Allah), những kẻ xúi giục Ngươi lánh xa những người nghèo và xúi giục Ngươi đi theo ham muốn của bản thân hơn việc tuân lệnh Thượng Đế của Ngươi, và đó là hành động tồi tệ và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(18,29,'18:29','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ lơ là với Allah với trái tim hời hợt: Những gì Ta mang đến cho các người là chân lý, nó đến từ nơi Allah chứ không phải từ bản thân Ta, Ta không đáp lại lời kêu gọi của các người về việc rời bỏ những người có đức tin. Bởi thế, ai trong các người muốn tin điều chân lý này thì hãy tin, y sẽ được ban thưởng cho niềm tin đó; và ai trong các người muốn phủ nhận thì hãy cứ phủ nhận rồi y sẽ phải chịu sự trừng phạt thích đáng theo sự mong đợi của y. Quả thật, TA (Allah) đã chuẩn bị cho những kẻ làm điều sai quấy với bản thân của chúng bởi đã lựa chọn con đường vô đức tin Hỏa Ngục, nơi mà lửa sẽ bao trùm lấy chúng từ mọi phía và chúng không thể thoát ra ngoài được. Và nếu chúng van xin giảm nhẹ hình phạt thì TA sẽ giảm nhẹ cho chúng bằng loại nước giống như dầu sôi, nó sẽ nướng chín gương mặt của chúng bởi độ nóng của nó, loại nước uống này rất kinh khủng và đáng sợ, nó chẳng những không làm cho hết khát mà còn tăng thêm cơn khát. Và Hỏa Ngục là một nơi nghỉ thật tồi tệ và xấu xa nhất.');
INSERT INTO "vn_mokhtasar" VALUES(18,30,'18:30','Quả thật, những người có đức tin và làm việc ngoan đạo và thiện tốt, họ sẽ được ban thưởng với một phần thưởng vô cùng to lớn bởi TA (Allah) không lấy mất đi phần ân thưởng của những ai đã làm việc tốt mà ngược lại TA luôn ban cho họ một cách trọn vẹn hoặc hơn.');
INSERT INTO "vn_mokhtasar" VALUES(18,31,'18:31','Những người đó,những người mà họ được mô tả rằng họ có đức tin và hành đạo cũng như những việc thiện tốt,sẽ được ban thưởng cho Thiên Đàng,nơi của những ngôi vườn bên dươi có các dòng sông chảy,họ sẽ được đeo nhưng chiếc vòng vàng,được mặc y phục bằng lụa là màu xanh mịn màng,họ sẽ tựa mình nghỉ trên những chiếc tràng kỷ cao,họ sẽ sống trong Thiên Đàng mãi mãi,một phần thưởng thật tốt đẹp,một chốn ngụ vĩnh hằng');
INSERT INTO "vn_mokhtasar" VALUES(18,32,'18:32','Hỡi Thiên Sứ Muhammad, Ngươi hãy đưa ra hình ảnh thí dụ cho họ về hai người: một người vô đức tin và một người có đức tin. Người có đức tin được TA (Allah) ban cho hai ngôi vườn nho được rào quanh bởi cây chà là và giữa hai ngôi vườn là một mảnh rẫy.');
INSERT INTO "vn_mokhtasar" VALUES(18,33,'18:33','Mỗi ngôi vườn đều xum xuê trái quả, cả nho, chà là và mảnh rẫy, không có một trái quả nào bị hư hỏng hay bị lép, tất cả đều hoàn hảo, và TA đã cho một con sông chảy chính giữa hai ngôi vườn để tiện tưới tiêu.');
INSERT INTO "vn_mokhtasar" VALUES(18,34,'18:34','Người chủ của hai ngôi vườn (người vô đức tin) được mùa, y nói với người bạn có đức tin của y trong lúc đang trò chuyện với vẻ kiêu hãnh và tự cao tự đại: Tôi có nhiều của cải hơn anh và tôi cũng mạnh hơn anh về số thành viên trong gia đình.');
INSERT INTO "vn_mokhtasar" VALUES(18,35,'18:35','Người vô đức tin đi vào vườn của y cùng với người bạn có đức tin của y, y muốn cho người bạn của mình thấy sự tốt tươi của ngôi vườn của y như thế nào, y đi vào vườn và tự làm hại bản thân mình bằng sự vô đức tin và lòng tự đắc, y tự tin nói với người bạn của mình: tôi nghĩ ngôi vườn tốt tươi này như anh đã thấy không thể nào tiêu tan được; chẳng có lý do gì mà nó không còn.');
INSERT INTO "vn_mokhtasar" VALUES(18,36,'18:36','Y - người vô đức tin - tiếp tục nói với người bạn có đức tin của y với lời lẽ và quan điểm vô đức tin của mình:Tôi không nghĩ Ngày Tận Thế sẽ xảy ra, bởi chẳng có cuộc sống nào ngoài cuộc sống tiếp diễn này,mà giả sử cho dù có Tận Thế thực sự đi chăng nữa thì khi tôi được đưa trở về với Thượng Đế thì chắc chắn tôi sẽ trở về với một ngôi vườn tốt đẹp hơn ngôi vườn này bởi tôi đã giàu có ở đây thì tôi phải giàu có sau khi được phục sinh');
INSERT INTO "vn_mokhtasar" VALUES(18,37,'18:37','Người bạn có đức tin của y - kẻ vô đức tin, chủ của ngôi vườn - đáp lại lời của y: Chẳng lẽ anh lại vô đức tin nơi Đấng đã tạo tổ tiên của anh - Adam từ đất bụi, rồi tạo anh ra từ tinh dịch, rồi làm cho anh thành một người đàn ông hoàn chỉnh thế này? Đấng đã có khả năng trên tất cả những sự việc đó chắc chắn thừa khả năng làm cho anh sống lại từ cõi chết.');
INSERT INTO "vn_mokhtasar" VALUES(18,38,'18:38','Người có đức tin nói tiếp: "Nhưng đối với tôi, tôi không nói những lời này của anh mà tôi chỉ nói: Allah là Thượng Đế của tôi, Ngài đã ban ân huệ cho tôi, và tôi không tổ hợp với Ngài bất cứ đối tác ngang vai nào trong thờ phượng (tức tôi thờ phượng chỉ một mình Ngài)".');
INSERT INTO "vn_mokhtasar" VALUES(18,39,'18:39','Người có đức tin tiếp tục nói: "Đáng lý anh phải nên nói khi đi vào vườn của mình: đây là những gì Allah muốn, không có sức mạnh nào ngoài sức mạnh của Allah, Ngài muốn làm gì Ngài muốn không ai có khả năng ngăn cản. Nếu anh thấy tôi nghèo hơn anh về của cải và con cái"');
INSERT INTO "vn_mokhtasar" VALUES(18,40,'18:40','"(Nếu anh thấy tôi nghèo hơn anh về của cải và con cái ở trên đời này) thì ở Đời Sau, tôi hy vọng Allah sẽ ban cho tôi những thứ tốt đẹp hơn ngôi vườn này của anh, và biết đâu có thể Allah sẽ trừng phạt anh bằng cách giáng từ trên trời xuống ngôi vườn của anh làm cho nó trở thành một bãi đất trơn không thể bước đi trên nó và cũng không thể mọc ra bất cứ loại cây trồng nào"');
INSERT INTO "vn_mokhtasar" VALUES(18,41,'18:41','"Hoặc có thể Allah sẽ lấy đi mất nguồn nước trên mặt đất bằng cách làm nó chảy xuống lòng đất khiến anh không có được nguồn nước để tưới tiêu và anh cũng không có cách nào tìm ra nó."');
INSERT INTO "vn_mokhtasar" VALUES(18,42,'18:42','Những điều mà người bạn có đức tin nói đã được chứng thực, tất cả vườn tược của người vô đức tin đã bị hủy diệt toàn bộ. Y xoa hai bàn tay tỏ vẻ nuối tiếc cho bao nhiêu công sức và tiền của đổ vào trong ngôi vườn giờ mất trắng toàn bộ. Nhìn cảnh hoang tàn của ngôi vườn, y nói: "Ôi, phải chi mình tin nơi Thượng Đế của mình, một Đấng duy nhất, phải chi mình không tổ hợp với Ngài bất cứ đối tác nào trong thờ phượng."');
INSERT INTO "vn_mokhtasar" VALUES(18,43,'18:43','Người đàn ông vô đức tin này - chủ hai ngôi vườn không tìm thấy một nhóm phái nào có thể giúp y thoát khỏi sự trừng phạt, y đã từng tự đắc trước mọi người và giờ đây y không thể tự cứu lấy mình.');
INSERT INTO "vn_mokhtasar" VALUES(18,44,'18:44','Trước hoàn cảnh đó chỉ Allah mới là Đấng có thể cứu giúp và che chở, Ngài là Đấng tốt nhất trong việc ban thưởng cho những người có đức tin, Ngài sẽ gia tăng thêm phần ân thưởng cho họ, và Ngài là Đấng ban kết quả cuối cùng tốt nhất.');
INSERT INTO "vn_mokhtasar" VALUES(18,45,'18:45','Hỡi Thiên Sứ Muhammad, Ngươi hãy đưa ra hình ảnh thí dụ về cõi trần tục cho những kẻ kiêu ngạo rằng hình ảnh tạm bợ của nó cũng như sự nhanh chóng kết thúc của nó giống như hình ảnh của nước mưa được TA (Allah) ban từ trên trời xuống, nhờ nguồn nước này cây cối thảo mộc trở nên xanh tươi nhưng rồi sau đó chúng trở nên héo khô vỡ vụn và bị gió thổi bay đi tứ tán và mặt đất trở lại như cũ. Quả thật, Allah là Đấng định đoạt và an bài tất cả mọi thứ, không có thứ gì làm Ngài bất lực, Ngài làm cho sống đối với thứ gì Ngài muốn và hủy diệt những gì Ngài muốn.');
INSERT INTO "vn_mokhtasar" VALUES(18,46,'18:46','Của cải tài sản và con cái là những thứ tô điểm cho cuộc sống trần gian này. Tài sản của cải không mang lại lợi ích gì cho cuộc sống cõi Đời Sau trừ phi nó được chi dùng vào những điều được Allah hài lòng và yêu thích. Và những việc làm, những lời nói được Allah hài lòng tốt đẹp hơn tất cả mọi thứ trang hoàng của thế giới trần gian, nó mang lại phần thưởng và niềm hy vọng tốt nhất, bởi lẽ những trang hoàng của thế gian rồi sẽ tiêu vong còn những việc làm và những lời nói được Allah hài lòng sẽ còn mãi nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(18,47,'18:47','Và Ngươi - hỡi Thiên Sứ Muhammad - hãy nhớ đến Ngày mà những quả núi sẽ biến mất khỏi vị trí của chúng, vào Ngày đó Ngươi sẽ thấy mặt đất trống trải bằng phẳng, núi non, cây cối và các loại kiến trúc đều biến mất, và TA (Allah) sẽ tập hợp tất cả nhân loại và phục sinh tất cả không trừ một ai.');
INSERT INTO "vn_mokhtasar" VALUES(18,48,'18:48','Nhân loại được dẫn đến đứng trình diện trước Thượng Đế của Ngươi - Muhammad - thành từng hàng ngũ chỉnh tề để Ngài phân xử và phán xét. Ngài phán bảo họ: tất cả các ngươi đã được triệu tập trước TA trong thân trạng trần chuồng, chân trần, còn nguyên da qui đầu (đối với nam giới) giống như TA đã tạo hóa các ngươi trong lần đầu. Các ngươi đã chối bỏ việc TA phục sinh các ngươi, các ngươi nghĩ rằng TA sẽ không bao giờ định đoạt việc hội ngộ này.');
INSERT INTO "vn_mokhtasar" VALUES(18,49,'18:49','Và các quyển sổ ghi chép các việc làm được mang ra. Có người sẽ được nhận quyển sổ ghi chép của mình từ phía cánh tay phải và có người sẽ được nhận nó từ phía cánh tay trái. Ngươi - này hỡi con người - sẽ thấy những kẻ vô đức tin kinh hãi về những điều được ghi trong đó bởi vì chúng đã làm và gửi đi trước từ những điều vô đức tin và tội lỗi, chúng nói: Ôi, thật khổ thân chúng tôi, một đại họa sắp ập đến! Ôi đây là quyển sổ gì mà nó ghi chép không bỏ sót một thứ gì dù nhỏ hay lớn. Và chúng nhìn thấy rõ ràng toàn bộ việc làm của chúng mà chúng đã làm trên thế gian. Và Thượng Đế của Ngươi - hỡi Thiên Sứ Muhammad không trừng phạt một ai không có tội và Ngài cũng không giảm bớt bất cứ điều gì từ phần ân thưởng của ai tuân phục Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(18,50,'18:50','Hãy nhớ lại hỡi Thiên Sứ Muhammad khi TA (Allah) phán bảo các Thiên Thần: hãy quỳ xuống phủ phục trước Adam để thể hiện một cái chào long trọng đến Y. Thế là tất các Thiên Thần đều chấp hành thực hiện theo lệnh Thượng Đế của họ ngoại trừ Iblis, hắn thuộc loài Jinn chứ không thuộc loài Thiên Thần. Hắn đã từ chối và tự cao tự đại không chịu quỳ lạy phủ phục theo lệnh của TA. Hắn đã trở thành một tên phản nghịch. Há các ngươi hỡi con người lại nhận lấy hắn và con cháu của hắn làm kẻ bảo hộ thay vì Allah ư trong khi hắn và con cháu của hắn là kẻ thù của các ngươi? Sao các ngươi lại nhận lấy kẻ thù làm người bảo hộ cho các ngươi? Thật là một điều xấu xa và sai trái cho hành động lấy những tên Shaytan là vị bảo hộ thay vì Allah!');
INSERT INTO "vn_mokhtasar" VALUES(18,51,'18:51','Những kẻ mà các ngươi nhận lấy chúng làm những vị bảo hộ ngoài TA (Allah) cũng là những bề tôi giống như các ngươi, TA không bảo chúng chứng kiến việc tạo hóa trời đất lúc TA tạo ra hai thứ đó bởi chúng chưa tồn tại và ngay cả việc tạo hóa bản thân chúng. Một mình TA duy nhất là Đấng Tạo Hóa và Chi Phối mọi thứ. Và TA không lấy những kẻ lầm lạc từ đám Shaytan trong con người và Jinn làm những kẻ trợ lực bởi TA không cần đến bất kỳ nguồn trợ lực nào.');
INSERT INTO "vn_mokhtasar" VALUES(18,52,'18:52','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhắc nhở chúng rằng vào Ngày Phán Xét, Allah sẽ phán bảo những kẻ tổ hợp với Ngài trên thế gian: Các ngươi hãy cầu nguyện những thần linh mà các ngươi đã cho rằng chúng là những đối tác cùng với TA mong rằng những thần linh đó sẽ giúp đỡ các ngươi. Chúng đã cầu nguyện những thần linh đó của chúng nhưng chẳng ai đáp lại lời của chúng và giúp đỡ chúng. Và TA đã đặt giữa những kẻ thờ phượng và những kẻ được thờ phượng một tấm chắn để chúng chia sẻ nó với nhau, đó là Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(18,53,'18:53','Những thờ đa thần được cho biết rằng chúng sẽ bị đày vào Hỏa Ngục, đó là điều chắc chắn, và chúng sẽ không tìm thấy vị cứu tinh nào.');
INSERT INTO "vn_mokhtasar" VALUES(18,54,'18:54','Quả thật, TA (Allah) đã trình bày trong Qur''an, Kinh Sách được ban xuống cho Muhammad, nhiều loại hình ảnh thí dụ làm bài học để nhắc nhở con người nhưng con người đặc biệt là những kẻ vô đức tin thường có nhiều sự tranh luận không chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(18,55,'18:55','Chẳng có cản trở nào cho loài người có đức tin nơi những gì Muhammad mang đến từ Thượng Đế của y và chẳng có cản trở nào cho họ trong việc tìm sự tha thứ nơi Allah về tội lỗi của họ ngoại trừ việc họ mong đợi sự trừng phạt đã từng xảy đến cho những cộng đồng trước và sự trừng phạt dành riêng cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(18,56,'18:56','TA (Allah) chỉ cử các vị Thiên Sứ của TA đến làm những người báo tin mừng cho những người có đức tin và tuân lệnh TA và cảnh báo những kẻ vô đức tin và tội lỗi về sự trừng phạt chứ họ không có quyền hướng dẫn bất cứ trái tim của ai theo chính đạo. Và những kẻ vô đức tin nơi Allah sẽ chống lại các vị Thiên Sứ của Ngài mặc dù chúng đã được trình bày những bằng chứng rất rõ ràng, chúng muốn dùng điều ngụy tạo để xóa bỏ điều chân lý được ban xuống cho Muhammad, chúng giễu cợt và cười nhạo Qur''an.');
INSERT INTO "vn_mokhtasar" VALUES(18,57,'18:57','Không ai sai quấy hơn những kẻ được nhắc nhở với những lời phán của Thượng Đế của y về sự trừng phạt mà Ngài đã hứa lại quay mặt với chúng. Y đã quên những tội lỗi mà y đã gửi đi trước. Quả thật, TA (Allah) đã phủ kín trái tim cúng chúng để chúng không hiểu được Qur''an, và TA đã làm cho tai của chúng điếc để chúng không nghe thấy điều chân lý mà chấp nhận, và nếu Ngươi - Thiên Sứ Muhammad - có kêu gọi chúng đến với đức tin Iman thì chúng không bao giờ đáp lại khi mà trái tim của chúng đang bị niêm kín và tai của chúng bị điếc.');
INSERT INTO "vn_mokhtasar" VALUES(18,58,'18:58','Để cho Thiên Sứ của Ngài khỏi vội vàng nghĩ đến việc trừng phạt những kẻ phủ nhận, Allah phán bảo Người: Hỡi Thiên Sứ Muhammad, Thượng Đế của Ngươi là Đấng Hằng Tha Thứ tội lỗi cho những bề tôi quay đầu sám hối với Ngài, Ngài là Đấng Nhân Từ với lòng nhân từ bao la phủ trùm lấy mọi thứ, và một trong những biểu hiện lòng nhân từ của Ngài là Ngài chậm lại trong việc trừng phạt những kẻ tội lỗi vì Ngài mong rằng họ sẽ quay đầu sám hối với Ngài. Vả lại, họ vẫn còn một cuộc hẹn với Ngài vào Ngày Phục Sinh, lúc đó, họ sẽ không bao giờ tìm thấy chốn nương thân khi bị Ngài trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(18,59,'18:59','Và những thị trấn đó, những thị trấn tội lỗi vô đức tin gần kề với các ngươi như những thị trấn của cộng đồng Nabi Hud, Nabi Saleh và cộng đồng của Nabi Shu''aib, TA đã tiêu diệt chúng khi chúng làm điều sai quấy qua việc vô đức tin và trái lệnh, và TA đã qui định giờ giấc cụ thể cho việc tiêu diệt chúng.');
INSERT INTO "vn_mokhtasar" VALUES(18,60,'18:60','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại lúc Musa nói với tiểu đồng của Y - Yusha'' bin Nun: Ta sẽ vẫn tiếp tục đi cho tới khi đến được địa điểm mà hai biển giao nhau hoặc Ta sẽ cứ tiếp tục đi nữa cho tới khi gặp được một người bề tôi ngoan đạo để Ta hỏi kiến thức từ người đó.');
INSERT INTO "vn_mokhtasar" VALUES(18,61,'18:61','Nabi Musa và cậu tiểu đồng của Y vẫn tiếp tục đi, và khi hai người họ đã đến chỗ mà hai biển giao nhau, họ đã quên mất con cá mà họ mang theo dùng làm lương thực, Allah đã làm cho nó sống và đã tìm đường nhảy xuống biển chạy đi mất giống như trong một đường hầm.');
INSERT INTO "vn_mokhtasar" VALUES(18,62,'18:62','Khi cả hai người đi qua khỏi chỗ đó, Musa bảo cậu tiểu đồng của mình: nào hãy dọn bữa ăn của mình ra, quả thật chúng ta đã thấm mệt qua một chặn đường dài.');
INSERT INTO "vn_mokhtasar" VALUES(18,63,'18:63','Cậu tiểu đồng của Musa thưa với Y: Thầy có nhớ lúc chúng ta đến chỗ mũi đá không?! Con đã quên thưa với thầy về chuyện con cá, và sự quên đó là do chính Shaytan đã gây ra cho con, quả thật con cá đã sống lại và nhảy xuống biển và lội đi mất một cách kỳ diệu.');
INSERT INTO "vn_mokhtasar" VALUES(18,64,'18:64','Musa nói với cậu tiểu đồng của Y: "Đó chính là chỗ mà chúng ta muốn tìm." Vậy là hai thầy trò họ lần theo dấu vết cũ để quay lại chỗ đó, chỗ mà con cá đã nhãy xuống biển đi mất.');
INSERT INTO "vn_mokhtasar" VALUES(18,65,'18:65','Khi họ đến chỗ con cá nhảy xuống biển đi mất thì họ đã gặp một người bề tôi ngoan đạo của TA (Allah) và đó là Al-Khudhair, TA đã ban cho y hồng ân từ nơi TA và TA đã dạy y nguồn kiến thức của TA, nguồn kiến thức mà TA đã không ban cho ai trong nhân loại, và nguồn kiến thức đó được chứa đựng trong câu chuyện này.');
INSERT INTO "vn_mokhtasar" VALUES(18,66,'18:66','Musa nói với Al-Khudhair một cách khiêm tốn: Tôi muốn theo thầy học hỏi những gì mà thầy được Allah chỉ dạy từ nguồn kiến thức dẫn đến điều chân lý, có được chăng?');
INSERT INTO "vn_mokhtasar" VALUES(18,67,'18:67','Al-Khudhair nói: Ngươi không bao giờ có thể kiên nhẫn nổi về những gì ngươi sẽ nhìn thấy từ việc làm qua kiên thức của ta bởi nó sẽ không phù hợp với những kiến thức ở nơi ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(18,68,'18:68','Al-Khudhair nói tiếp: Và làm sao ngươi có thể kiên nhẫn về những gì ngươi sẽ nhìn thấy từ những việc làm mà ngươi không có sự hiểu biết đúng đắn bởi ngươi chỉ đánh giá và định đoạt sự việc theo kiến thức giới hạn ở nơi ngươi mà thôi?!');
INSERT INTO "vn_mokhtasar" VALUES(18,69,'18:69','Musa thưa với Al-Khudhair: Nếu Allah muốn, thầy sẽ thấy được ở nơi tôi sự kiên nhẫn về những gì mà tôi nhìn thấy từ việc làm và hành động của thầy, chắc chắn tôi sẽ nghe theo thầy, chắc chắn tôi sẽ không cãi lại thầy bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(18,70,'18:70','Al-Khudhair nói với Musa: Nếu đi theo ta, ngươi không được hỏi ta bất cứ điều gì khi ta hành động trừ phi tự bản thân ta giải thích cho ngươi biết ý nghĩa của điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(18,71,'18:71','Sau khi hai người họ đồng thuận những điều kiện của nhau, hai người ra đi đến bờ biển và gặp được một con thuyền, cả hai lên thuyền, họ không bắt phải trả tiền phí lên thuyền vì Al-Khudhair được ưu đãi. Nhưng rồi bỗng dưng Al-Khudhair đục thuyền. Musa thấy vậy liền bảo: Sao thầy lại đục thuyền mà nó đã cho chúng ta đi nhờ không phải tốn một chi phí nào, chẳng lẽ thầy muốn nhấn chìm mọi người trên tàu ư? Tôi thấy thầy thực sự đã hành động quá sai trái.');
INSERT INTO "vn_mokhtasar" VALUES(18,72,'18:72','Al-Khudhair bảo Musa: Chẳng phải ta đã bảo rằng ngươi sẽ không bao giờ có thể nhẫn nại khi đi cùng ta về những điều ngươi nhìn thấy ta làm đó sao?!');
INSERT INTO "vn_mokhtasar" VALUES(18,73,'18:73','Musa thưa với Al-Khudhair: Xin thầy chớ rầy la tôi vì tôi đã quên mất những điều kiện mà tôi đã hứa với thầy, xin thầy chớ gây khó khăn cho tôi trong việc đồng hành với thầy để học hỏi những kiến thức từ nơi thầy.');
INSERT INTO "vn_mokhtasar" VALUES(18,74,'18:74','Hai người họ tiếp tục đi bộ trên bờ biển sau khi xuống thuyền. Khi nhìn thấy một cậu bé chưa đến tuổi dậy thì đang vui đùa với những đứa trẻ khác, Al-Khudhair đã giết chết nó. Trước cảnh tượng không ngờ đó, Musa nói với Al-Khudhair: Sao thầy lại giết một mạng người trong sạch thế kia, sao thầy lại giết một đứa trẻ thơ chưa làm tội? Quả thật, thầy đã làm một việc làm tội lỗi tày trời!');
INSERT INTO "vn_mokhtasar" VALUES(18,75,'18:75','Al-Khudhair nói với Musa: Chẳng phải ta đã nói với ngươi rằng ngươi sẽ không bao giờ có thể nhẫn nại trước việc làm của ta đó sao?');
INSERT INTO "vn_mokhtasar" VALUES(18,76,'18:76','Musa thưa với Al-Khudhair: Nếu tôi còn hỏi thầy một điều gì nữa sau lần này thì thầy hãy cứ bỏ tôi, vì tôi đã không còn lý do đồng hành với thầy nữa bởi tôi đã làm trái lời thầy ba lần.');
INSERT INTO "vn_mokhtasar" VALUES(18,77,'18:77','Thế là hai người họ tiếp tục đi cho đến khi tới một thị trấn, hai người hỏi xin thức ăn từ ngươi dân của nó nhưng họ đã từ chối, họ đã không thể hiện lòng hiếu khách đối với hai thầy trò Musa. Rồi hai người họ nhìn thấy trong thị trấn có một bức tường gần như muốn ngã, Al-Khudhair liền dựng nó đứng thẳng trở lại. Thấy thế, Musa nói vơi Al-Khudhair: Nếu muốn thầy có thể đòi tiền công từ việc làm sửa chữa đó để đáp ứng cho nhu cầu thức ăn của chúng ta vì họ đã từ chối thể hiện lòng hiếu khách đối với chúng ta.');
INSERT INTO "vn_mokhtasar" VALUES(18,78,'18:78','Al-Khudhair nói với Musa: Việc ngươi không tán thành về việc ta không đòi tiền công cho công việc dựng thẳng lại bức tường là lý do để ta và ngươi phải chia tay nhau lúc này. Ta sẽ nói cho ngươi biết về ý nghĩa của những điều mà ngươi đã chứng kiến ta hành động nhưng ngươi đã không thể kiên nhẫn.');
INSERT INTO "vn_mokhtasar" VALUES(18,79,'18:79','Đối với chiếc thuyền mà ngươi đã phản đối việc ta hành xử với nó chính là chiếc thuyền của những người nông dân nghèo dùng để mưu sinh trên biển. Ta đã làm cho nó trở nên khiếm khuyết bởi vì có một ông vua luôn chiếm đoạt lấy bất cứ con thuyền nào nguyên vẹn và lành lặn, ta không muốn con thuyền của những người nghèo này bị tên vua đó chiếm lấy nên đã đục lỗ nó.');
INSERT INTO "vn_mokhtasar" VALUES(18,80,'18:80','Còn về đứa trẻ mà ta đã giết và ngươi đã phản đối là bởi vì cha mẹ của nó là những người có đức tin còn nó sẽ là một kẻ vô đức tin qua kiến thức của Allah, ta sợ nó dẫn dắt cha mẹ của nó đến với sự vô đức tin nơi Allah và sẽ làm những điều sai quấy vì tình yêu thương dành cho nó.');
INSERT INTO "vn_mokhtasar" VALUES(18,81,'18:81','Ta muốn Allah sẽ ban cho hai người họ một đứa con trai khác ngoan đạo, tốt đẹp và thanh sạch hơn và sẽ yêu thương cha mẹ hơn nó.');
INSERT INTO "vn_mokhtasar" VALUES(18,82,'18:82','Còn đối với bức tường mà ta đã dựng nó thẳng đứng trở lại nhưng ngươi phản đối, nó là của hai đứa trẻ mồ côi trong thị trấn, cha của chúng vừa mới qua đời khi chúng ta đến thị trấn, và có một kho tài sản được chôn ở phía dưới bức tường đó để dành cho chúng, vì cha của chúng lúc còn sống là một người ngoan đạo nên Thượng Đế của Ngươi - hỡi Musa - muốn khi nào hai đứa trẻ đó trưởng thành, chúng sẽ lấy kho tài sản đó ra sử dụng. Bởi vậy, nếu như bức tường đó ngã sụp lúc này thì kho tài sản sẽ được phát hiện và có nguy cơ bị lấy mất. Cách hành xử này đến từ lòng thương xót của Thượng Đế của ngươi dành cho hai đứa trẻ, và ta đã không hành động theo ý của ta. Đấy là ý nghĩa về những điều mà ngươi đã không thể kiên nhẫn.');
INSERT INTO "vn_mokhtasar" VALUES(18,83,'18:83','Hỡi Thiên Sứ Muhammad! Những người thờ đa thần và những người Do Thái hỏi Ngươi về thông tin của Zdul Qarnain (Người của hai chiếc sừng hay người có hai chiếc sừng) nhằm mục đích kiểm tra Ngươi. Ngươi hãy bảo chúng: Ta sẽ đọc cho các ngươi về một phần câu chuyện của Người mà các ngươi biết.');
INSERT INTO "vn_mokhtasar" VALUES(18,84,'18:84','Quả thật, TA (Allah) đã định cư Zdul Qarnain sống trên trái đất và TA đã ban cho Y mọi phương tiện giúp Y có thể đạt được mục tiêu Y mong muốn.');
INSERT INTO "vn_mokhtasar" VALUES(18,85,'18:85','Thế là Y (Zdul Qarnain) dùng những phương tiện mà TA (Allah) đã ban cho Y để hướng đến mục tiêu công lý. Y đi về phía tây.');
INSERT INTO "vn_mokhtasar" VALUES(18,86,'18:86','Zdul Qarnain đã du hành cho tới khi tới phần cuối cùng của trái đất nằm ở phía mặt trời lặn, Y nhìn thấy nó như thể nó đang lặn xuống một dòng suối nóng pha đất bùn đen, và Y thấy gần đó có một cộng đồng người vô đức tin sinh sống. TA (Allah) phán bảo Y được phép lựa chọn: Này hỡi Zdul Qarnain! Hoặc là ngươi trừng phạt chúng bằng cách giết chúng hay bằng cách gì khác, hoặc là ngươi đối xử nhân đạo với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(18,87,'18:87','Zdul Qarnain nói: Đối với ai làm điều Shirk với Allah và ngoan cố trên sự việc đó sau khi ta đã kêu gọi y đến với sự thờ phượng Allah thì ta sẽ trừng phạt y bằng cách giết y trên thế gian này rồi vào Ngày Sau khi trở về trình diện Thượng Đế của y thì Ngài sẽ trừng phạt y bằng một hình phạt khác lớn hơn.');
INSERT INTO "vn_mokhtasar" VALUES(18,88,'18:88','Zdul Qarnain nói tiếp: Còn đối với ai có đức tin nơi Allah đồng thời làm việc thiện tốt thì y sẽ được ban thưởng Thiên Đàng, một phần thưởng tốt đẹp từ nơi Thượng Đế của y cho đức tin Iman và việc làm ngoan đạo của y; và bản thân ta sẽ nói nhẹ nhàng với y về mệnh lệnh của ta.');
INSERT INTO "vn_mokhtasar" VALUES(18,89,'18:89','Sau đó, Zdul Qarnain tiếp tục du hành ở một con đường khác, Y du hành hướng về phía mặt trời mọc.');
INSERT INTO "vn_mokhtasar" VALUES(18,90,'18:90','Zdul Qarnain đã đi cho tới khi đến được một nơi ở phía mặt trời mọc, ở đó Y thấy mặt trời giống như đang mọc lên từ cộng đồng người dân, những người mà TA (Allah) đã không ban cho tấm phủ nào để che cái nóng của ánh nắng mặt trời tức họ không có nhà cửa cũng không có cây cối để che mát.');
INSERT INTO "vn_mokhtasar" VALUES(18,91,'18:91','Tương tự vụ việc của Zdul Qarnain, quả thật TA (Allah) thông toàn tất cả mọi điều ở nơi Y từ sức mạnh và quyền hành.');
INSERT INTO "vn_mokhtasar" VALUES(18,92,'18:92','Rồi Zdul Qarnain tiếp tục cuộc du hành trên con đường khác với hai con đường trước đó, nằm giữa hướng mặt trời mọc và hướng mặt trời lặn.');
INSERT INTO "vn_mokhtasar" VALUES(18,93,'18:93','Zdul Qarnain đã đi cho đến khi tới được một nơi nằm giữa hai ngọn núi, Y thấy gần đó có một đám dân gần như không hiểu được tiếng nói của ai khác ngoài dân của họ.');
INSERT INTO "vn_mokhtasar" VALUES(18,94,'18:94','Đám dân ở chỗ giữa hai ngọn núi nói: Hỡi Zdul Qarnain! Quả thật Ya''juj và Ma''juj (tức hai cộng đồng vô cùng lớn thuộc con cháu của Adam - con người) là những phá hại trong xứ bằng sự giết chóc và tàn phá mọi thứ. Chúng tôi sẽ dâng cho ngài tiền của để ngài giúp chúng tôi dựng lên một bức tường thành ngăn cách giữa chúng và chúng tôi được chăng?');
INSERT INTO "vn_mokhtasar" VALUES(18,95,'18:95','Zdul Qarnain nói: Những gì Allah ban cấp cho ta từ bổng lộc và vương quyền tốt hơn những gì các ngươi muốn dâng lên cho ta. Thế nên các ngươi chỉ cần giúp sức và các vật dụng, ta sẽ dựng lên một bức tường thành ngăn cách chúng khỏi các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(18,96,'18:96','Zdul Qarnain nói: Các ngươi hãy mang đến cho ta một thỏi sắt. Thế là họ đã mang sắt đến và Y bắt đầu xây bức tường chính giữa hai ngọn núi, Y xây bức tường cho đến khi nó cao bằng đỉnh hai ngọn núi thì Y bảo những công nhân: Các ngươi hãy nhóm lửa lên đốt bức tường, sau khi bức tường sắt đã đỏ lên thì Y bảo họ mang chất đồng nấu chảy đổ lên nó.');
INSERT INTO "vn_mokhtasar" VALUES(18,97,'18:97','Vậy là Ya''juj và Ma''juj không thể treo qua bức tường để đến với cộng đồng người dân và cũng không thể đào thủng được nó từ bên dưới do quá cứng.');
INSERT INTO "vn_mokhtasar" VALUES(18,98,'18:98','Zdul Qarnain nói: Bức tường thành này là một hồng phúc từ nơi Thượng Đế của ta giúp ngăn chặn Ya''juj và Ma''juj tràn ra ngoài tàn phá trái đất. Nhưng khi thời hạn mà Allah qui định đã đến thì bức tường thành này không còn tác dụng nữa, Ya''juj và Ma''juj sẽ làm vỡ nó và tràn ra ngoài trước giờ Tận Thế, chúng sẽ ra ngoài tàn phá khắp mọi nơi trên trái đất. Và lời hứa của Allah về việc Ya''juj và Ma''juj tràn ra ngoài tàn phát trái đất là sự thật không thay đổi.');
INSERT INTO "vn_mokhtasar" VALUES(18,99,'18:99','Và TA (Allah) đã chừa lại một số tạo vật cho đến thời kỳ cuối cùng của thế gian, chúng sẽ ồ ạt chạy ra chồng lên nhau như những cơn sóng, và TA cho tiếng còi thổi lên để tập hợp tất cả để xét xử và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(18,100,'18:100','Vào Ngày Tận Thế, TA (Allah) sẽ phơi bày Hỏa Ngục cho những kẻ vô đức tin tận mắt nhìn thấy, không còn là một thứ gì đó mơ hồ nữa.');
INSERT INTO "vn_mokhtasar" VALUES(18,101,'18:101','TA (Allah) sẽ phơi bày Hỏa Ngục cho những kẻ vô đức tin thấy tận mắt, những kẻ mà trên thế gian chúng đã đui mù trước lời Nhắc Nhở của Allah (Qur''an) bởi đã có một tấm màn che mắt chúng lại; và chúng cũng không thể nghe thấy các lời phán của TA.');
INSERT INTO "vn_mokhtasar" VALUES(18,102,'18:102','Há những kẻ vô đức tin nơi Allah tưởng rằng chúng có thể dựng đám bề tôi của tôi của Ngài từ các Thiên Thần, các vị Sứ Giả cũng những các tên Shaytan làm đấng thờ phượng ngoài Ngài hay sao?! Quả thật TA (Allah) đã chuẩn bị cho những kẻ vô đức tin Hỏa Ngục làm một nơi chốn ngụ.');
INSERT INTO "vn_mokhtasar" VALUES(18,103,'18:103','Hỡi Thiên Sứ Muhammad! Ngươi hãy nói: Này hỡi mọi người, há các ngươi muốn ta nói cho các ngươi biết ai là những kẻ bị thua thiệt và thất bại về phương diện việc làm chăng?');
INSERT INTO "vn_mokhtasar" VALUES(18,104,'18:104','Vào Ngày Phục Sinh, những kẻ đã tốn công sức làm trên cõi trần và cứ tưởng chúng những công sức mà chúng đã bỏ ra mang lại điều tốt đẹp cho chúng nhưng thực tế lại trái ngược hoàn toàn.');
INSERT INTO "vn_mokhtasar" VALUES(18,105,'18:105','Những kẻ đó là những kẻ đã phủ nhận các lời Mặc Khải của Thượng Đế của chúng cũng như đã phủ nhận sự trở lại trình diện Ngài nên tất cả các việc làm của chúng trở nên vô giá trị bởi sự vô đức tin của chúng. Bởi thế, vào Ngày Phán Xét, Allah sẽ không ban cho chúng một mức lượng tốt đẹp nào cho việc làm của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(18,106,'18:106','Hỏa Ngục là phần thưởng dành cho chúng, đó là phần thưởng thích đáng cho việc chúng đã vô đức tin nơi Allah và đã chế nhạo các lời Mặc Khải của Ngài cũng như đã chế nhạo các vị Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(18,107,'18:107','Quả thật, những người có đức tin nơi Allah và làm việc thiện tốt và ngoan đạo thì sẽ được ban thưởng cho các ngôi vườn ở tầng cao nhất nơi Thiên Đàng làm nơi chốn ngụ tốt đẹp, một sự ưu đãi danh dự dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(18,108,'18:108','Họ sẽ sống trong Thiên Đàng mãi mãi, họ không sẽ không yêu cầu được chuyển đi nơi khác bởi Thiên Đàng là phần thưởng mà không có bất cứ điều gì sánh bằng.');
INSERT INTO "vn_mokhtasar" VALUES(18,109,'18:109','Hỡi Thiên Sứ Muhammad! Ngươi hãy nói: "Quả thật, Lời Thượng Đế của Ta vô tận, cho dù lấy nước biển làm mực để viết các Lời của Ngài thì chắc chắn nước biển sẽ cạn hết trước khi Lời Phán của Ngài cạn hết, và cho dù chúng ta có mang đến thêm bao nhiêu biển nước khác như thế thì nó cũng sẽ cạn hết giống như vậy."');
INSERT INTO "vn_mokhtasar" VALUES(18,110,'18:110','Hỡi Thiên Sứ Muhammad! Ngươi hãy nói: "Quả thật Ta cũng chỉ là người phàm giống như các ngươi, chỉ có điều Ta được mặc khải rằng các ngươi chỉ có một Đấng Thờ Phượng duy nhất đáng được tôn thờ, Ngài không có đối tác chia sẻ, và đó là Allah. Bởi thế, ai lo sợ cho sự trình diện Thượng Đế của y thì hãy hành động theo giáo luật của Ngài, thành tâm trong hành động tuân lệnh Ngài và không tổ hợp bấy kỳ ai (vật gì) cùng với Ngài trong việc thờ phượng Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(19,1,'19:1','{Ka.Ha.Ya.''Ain.Sad} là những chữ cái được dùng để khai đề cho chương Kinh. Ý nghĩa của nó đã được trình bày ở phần đầu của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(19,2,'19:2','Đây là câu chuyện nhắc lại phần Hồng Phúc của Thượng Đế của Ngươi - Muhammad - đã được ban cấp cho người bề tôi của Ngài - Nabi Zakariya (Zacharias), TA sẽ kể nó cho Ngươi nghe để làm bài học.');
INSERT INTO "vn_mokhtasar" VALUES(19,3,'19:3','Lúc Y - Zakariya - một mình âm thầm cầu nguyện Thượng Đế của Y để mong sự cầu nguyện của mình được đáp lại.');
INSERT INTO "vn_mokhtasar" VALUES(19,4,'19:4','Zakariya cầu nguyện, nói: Lạy Thượng Đế của bề tôi! Quả thật xương cốt của bề tôi đã yếu, tóc của bề tôi đã nhiều sợi bạc, nhưng bề tôi vẫn không hết hy vọng trong việc cầu xin và khấn van Ngài, bề tôi vẫn luôn hy vọng Ngài sẽ đáp lại lời cầu nguyện của bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(19,5,'19:5','Zakariya nói tiếp trong lời cầu nguyện: Quả thật, bề tôi lo sợ cho dòng dõi của bề tôi không ai nối gót bề tôi đảm đương trông coi việc đạo mà chỉ lo cho cuộc sống thế tục sau khi bề tôi chết đi trong khi vợ của bề tôi làm một người phụ nữ hiếm muộn. Bởi vậy, bề tôi thành tâm khấn xin Ngài ban cho bề tôi một đứa con trai từ nơi Ngài để nối dõi.');
INSERT INTO "vn_mokhtasar" VALUES(19,6,'19:6','Zakariya tiếp tục nói trong lời cầu nguyện: Xin Ngài hãy để nó (đứa con mà bề tôi cầu xin Ngài và nếu Ngài ban cho) thừa kế sứ mạng Nabi từ bề tôi và từ dòng dõi của Y''aqub (Jacob), và lạy Thượng Đế của bề tôi, xin Ngài hãy làm cho nó thành một người bề tôi được Ngài hài lòng về phương diện tôn giáo, phẩm hạnh và việc làm.');
INSERT INTO "vn_mokhtasar" VALUES(19,7,'19:7','Allah đã chấp nhận lời cầu nguyện của Zakariya và phán với Y: Này Zakariya, TA thông báo tin mừng cho ngươi, quả thật TA đã chấp nhận lời cầu khẩn của ngươi, TA sẽ ban cho ngươi một đứa con trai và TA đặt tên cho nó là Yahya (Jonh), cái tên mà từ trước đến giờ TA chưa hề đặt cho ai cả.');
INSERT INTO "vn_mokhtasar" VALUES(19,8,'19:8','Zakariya nghe Allah phán như thế, Y ngạc nhiên thưa với Ngài: Lạy Thượng Đế của bề tôi! Làm sao vợ của bề tôi có thể sinh cho bề tôi một đứa con trai khi mà bà ấy là người phụ nữ hiếm muộn không thể sinh con, hơn nữa, bề tôi lại quá già, xương cốt thì yếu ớt thế này?!');
INSERT INTO "vn_mokhtasar" VALUES(19,9,'19:9','Thiên Thần nói với Zakariya: Sự việc như Ngươi đã nói rằng vợ của Ngươi không thể sinh con,và quả thật hơn nữa Ngươi lại quá già, xương cốt thì yếu ớt thế này,nhưng mà Thượng Đế của Ngươi có phán:ViệcThượng Đế của Ngươi tạo hóa ra Yahya từ một người mẹ không thể sinh đẻ được và từ một người cha tuổi đời lại già yếu sự việc đó rất dễ dàng đối với Thượng Đế Ngươi,và theo sự thật - hỡi zakariya TA đã tạo ra Ngươi trước đây trong lúc Ngươi không là gì cả');
INSERT INTO "vn_mokhtasar" VALUES(19,10,'19:10','Zakariya thưa với Allah: Lạy Thượng Đế của bề tôi! Xin Ngài hãy làm cho bề tôi một dấu hiệu chứng minh cho điều mà các Thiên Thần của Ngài báo tin mừng cho bề tôi để bề tôi yên tâm. Allah phán: Dấu hiệu của Ngươi là Ngươi không thể nói chuyện được với mọi người trong vòng ba đêm nhưng không phải do bệnh tật gì cả mà Ngươi vẫn khỏe mạnh bình thường.');
INSERT INTO "vn_mokhtasar" VALUES(19,11,'19:11','Rồi Zakariya rời phòng cầu nguyện đi ra ngoài gặp người dân của mình, Người ra dấu cho họ vì không thể nói chuyện: các người hãy tán dương Allah sáng và chiều.');
INSERT INTO "vn_mokhtasar" VALUES(19,12,'19:12','Allah ban cho Zakariya đứa con trai tên Yahya. Khi Yahya đến tuổi thiếu niên, Allah phán với Người: Này Yahya, Ngươi nắm lấy Kinh Sách - Tawrah (Cựu Ước) bằng nghị lực và quyết tâm, TA đã ban cho Ngươi sự thông hiểu, kiến thức, và sự nhiệt quyết trong lúc Ngươi còn rất trẻ.');
INSERT INTO "vn_mokhtasar" VALUES(19,13,'19:13','TA (Allah) đã thương xót Y bằng lòng nhân từ ở nơi TA, TA đã tẩy sạch tội lỗi cho Y, và Y thực sự là một người bề tôi ngay chính sợ Allah, luôn thực thi các mệnh lệnh của TA và tránh xa nhiều điều TA nghiêm cấm.');
INSERT INTO "vn_mokhtasar" VALUES(19,14,'19:14','Và Yahya là một người con hiếu thảo với cha mẹ, luôn đối xứ ân cần, từ tốn, đàng hoàng và kính yêu cha mẹ, Y không tự cao tự đại mà lúc nào cũng tuân lệnh Thượng Đế của Y và vâng lời cha mẹ của Y; và Y không hề nghịch lại lệnh của Thượng Đế của Y cũng như không hề làm phật lòng cha mẹ mình.');
INSERT INTO "vn_mokhtasar" VALUES(19,15,'19:15','Và sự bằng an và phúc lành từ nơi Allah được ban cho Y từ ngày Y sinh ra đời, từ ngày Y chết đi rời khỏi thế giới trần gian này và từ Ngày Phục Sinh. Đây là ba thời đoạn mà mỗi con người đều phải trải qua, nếu được bằng an và phúc lành trong ba thời đoạn này thì chắc chắn y sẽ không phải lo sợ cũng không buồn phiền.');
INSERT INTO "vn_mokhtasar" VALUES(19,16,'19:16','Hỡi Thiên Sứ Muhammad! Ngươi hãy nhắc lại trong Kinh Qur''an được ban xuống cho Ngươi thông tin về Mar-yam khi Nàng ta đi lánh mặt người thân của mình tại một nơi ở hướng Đông.');
INSERT INTO "vn_mokhtasar" VALUES(19,17,'19:17','Nàng đã giăng một bức màn để không ai nhìn thấy mình lúc Nàng thờ phượng Thượng Đế của Nàng. TA (Allah) đã cử đại Thiên Thần Jibril xuống, Y đã xuất hiện trước mặt Nàng dưới hình hài một người phàm toàn diện khiến Nàng lo sợ Y có ý định xấu với Nàng');
INSERT INTO "vn_mokhtasar" VALUES(19,18,'19:18','Khi Nàng nhìn thấy một người đàn ông lạ xuất hiện và tiến gần về phía mình, Nàng liền nói với người đàn ông đó trong lo sợ: tôi cầu xin Đấng Độ Lượng bảo vệ tôi tránh khỏi ông, chớ đến gần tôi, này người đàn ông, nếu ông là người sợ Allah thì hãy đi xa khỏi tôi.');
INSERT INTO "vn_mokhtasar" VALUES(19,19,'19:19','Đại Thiên Thần Jibril nói với Nàng:Ta không phải người phàm, quả thật ta là vị Sứ Giả của Thượng Đế của Ngươi, Ngài cử ta đến để báo cho Ngươi một tin mừng rằng Ngài sẽ ban cho Ngươi một đứa con trai trong sạch và phúc lành.');
INSERT INTO "vn_mokhtasar" VALUES(19,20,'19:20','Mar-yam ngạc nhiên bảo với đại Thiên Thần Jibril: Làm sao tôi có thể có con trong khi tôi chưa có chồng và cũng không hề chung đụng với người đàn ông nào, và hơn nữa tôi cũng không phải là dạng phụ nữ hư đốn quan hệ bừa bãi.');
INSERT INTO "vn_mokhtasar" VALUES(19,21,'19:21','Đại Thiên Thần Jibril nói với Mar-yam: Sự việc sẽ diễn ra đúng như những gì ta đã truyền mặc dù người không có chồng cũng như không người đàn ông nào từng chạm đến ngươi và ngươi cũng không phải là một người phụ nữ hư đốn. Bởi Thượng Đế của ngươi - Allah - Đấng Tối Cao và Quyền Năng đã phán: Việc tạo hóa một đứa con không cần người cha là điều dễ dàng đối với TA, và đứa con mà TA sẽ ban cho ngươi (Mar-yam) sẽ là một dấu lạ cho nhân loại về quyền năng của TA và cũng là hồng ân từ TA cho ngươi và những ai có đức tin; và việc tạo hóa đứa con này cho ngươi là một sự tiền định từ TA đã được ghi trong "Văn Bản Lưu Trữ" (hay còn gọi là Quyển Kinh Mẹ).');
INSERT INTO "vn_mokhtasar" VALUES(19,22,'19:22','Rồi Mar-yam thụ thai sau khi được Đại Thiên Thần Jibril thổi vào bụng và sau đó đi lánh mặt mọi người ở một nơi xa.');
INSERT INTO "vn_mokhtasar" VALUES(19,23,'19:23','Rồi cơn đau hạ sinh đã đưa đẩy Mar-yam đến một cây chà là. Mar-yam tựa lưng vào thân cây chà là và than: Ôi ước gì mình chết đi trước ngày hôm nay, ước gì mình sẽ không được ai nhớ đến để khỏi mang tiếng làm chuyện xấu xa.');
INSERT INTO "vn_mokhtasar" VALUES(19,24,'19:24','Lúc đó, Ysa đã cất tiếng gọi mẹ từ dưới chân của Mar-yam và nói: Mẹ chớ đừng buồn, có thể Allah sẽ tạo bên dưới chân mẹ nguồn nước để mẹ uống.');
INSERT INTO "vn_mokhtasar" VALUES(19,25,'19:25','Ysa nói tiếp: Mẹ hãy lấy tay lắc thân cây cha là, những trái chín tươi sẽ rụng xuống ngay lập tức.');
INSERT INTO "vn_mokhtasar" VALUES(19,26,'19:26','Ysa nói tiếp: "Mẹ hãy ăn trái chà là chín tươi, hãy uống từ mạch nước dưới chân, và hãy vui vẻ lên về đứa con của mẹ và chớ đừng buồn nữa. Nếu mẹ thấy có người đến hỏi mẹ về đứa con của mẹ thì mẹ hãy nói với người đó: tôi đã nguyện với Thượng Đế của tôi rằng tôi sẽ nhịn nói chuyện, bởi thế tôi không nói chuyện với bất cứ ai trong ngày hôm nay."');
INSERT INTO "vn_mokhtasar" VALUES(19,27,'19:27','Rồi Mar-yam bồng con trở về gặp lại người dân của mình, họ tỏ thái độ khinh thường nói: Này Mar-yam! Quả thật ngươi đã mang về một điều ô uế xấu xa đáng hổ thẹn khi mà ngươi mang về một đứa con không có cha.');
INSERT INTO "vn_mokhtasar" VALUES(19,28,'19:28','Này hỡi em gái của Harun (một người đàn ông ngoan đạo)! Cha mẹ của Ngươi đâu phải những kẻ hư đốn dâm loàn, ngươi xuất thân từ một gia đình phẩm hạnh tốt đẹp mà ai cũng biết, sao ngươi lại mang về một đứa con không cha thế này?');
INSERT INTO "vn_mokhtasar" VALUES(19,29,'19:29','Mar-yam lấy tay chỉ vào đứa con của mình (Ysa) và lúc đó Y vẫn còn đang nằm trong nôi, thế là người dân trố mắt ngạc nhiên: làm sao mà bọn ta có thể nói chuyện được với một đứa bé vẫn còn đang nằm trong nôi thế này chứ?!');
INSERT INTO "vn_mokhtasar" VALUES(19,30,'19:30','Ysa lên tiếng nói chuyện trả lời dân chúng (lúc vẫn còn là một đứa bé đang nằm trong nôi): Quả thật, Ta là người bề tôi của Allah, Ngài đã ban cho Ta Kinh Sách (Injil - Tân ước) và chỉ định Ta là một vị Nabi trong số các vị Nabi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(19,31,'19:31','Ysa nói tiếp: Và Ngài (Allah) đã sẽ phù hộ Ta mang lại nhiều phúc lành cho đám bề tôi của Ngài ở bất cứ nơi nào Ta có mặt, và Ngài ra lệnh cho Ta dâng lễ nguyện Salah và đóng Zakah trong suốt thời gian Ta tại thế.');
INSERT INTO "vn_mokhtasar" VALUES(19,32,'19:32','Ysa tiếp tục trong lời nói của mình với dân chúng: Và Ngài (Allah) ra lệnh cho Ta phải hiếu thảo với mẹ của Ta và Ta sẽ không tự cao tự đại chối bỏ sự tuân lệnh Ngài cũng như sẽ không làm trái lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(19,33,'19:33','Sự bằng an khỏi sự quấy nhiễu của Shaytan cũng như sự phù hộ của Allah dành cho Ta từ lúc Ta chào đời cho đến khi Ta chết đi và sự bằng an được tiếp tục ban cho Ta khi Ta được phục sinh trở lại vào Ngày Phán Xét. Shaytan sẽ không thể nào quấy nhiễu được Ta.');
INSERT INTO "vn_mokhtasar" VALUES(19,34,'19:34','Những mô tả được nói đó chính là Ysa con trai của Mar-yam, và lời nói này là lời thật chứ không phải là lời của những kẻ lầm lạc nghi ngờ và bất đồng về sự việc của Y.');
INSERT INTO "vn_mokhtasar" VALUES(19,35,'19:35','Việc cho rằng Allah có một đứa con trai là không thích hợp với Quyền Năng và Sự Tối Cao của Ngài, Ngài Vinh Quang và Trong Sạch khỏi những thứ tầm thường đó, bởi lẽ khi Ngài muốn bất cứ điều gì thì Ngài chỉ cần nói "Hãy thành!" thì chắc chắn nó sẽ thành đúng như ý của Ngài. Một Đấng có quyền năng vô song như thế chắc chắn không can hệ đến việc con cái.');
INSERT INTO "vn_mokhtasar" VALUES(19,36,'19:36','Ysa nói: Quả thật, Allah là Thượng Đế của Ta và cũng là Thượng Đế của tất cả các ngươi, bởi thế, các ngươi hãy thành tâm thờ phượng một mình Ngài. Những gì Ta nói với các ngươi chính là con đường Ngay Chính dẫn tới sự hài lòng của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(19,37,'19:37','Những kẻ bất đồng quan điểm đã không đồng thuận với nhau về câu chuyện của Ysa nên đã chia rẽ nhau thành những nhóm phái. Một số người tin nơi Y (Ysa) nói Y là Thiên Sứ của Allah, một số khác như những người Do Thái thì phủ nhận Y, một số kẻ tôn vinh Y một cách thái quá thì cho rằng Y là Allah và một số lệch lạc khác thì nói rằng Y là con trai của Allah. Nhưng Allah tối cao và vĩ đại vượt xa những gì họ nói. Và những kẻ bất đồng nhau về sự việc của Ysa sẽ gặp sự khốn khổ vào Ngày Phán Xét Vĩ Đại khi đối diện với sự thanh toán và trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(19,38,'19:38','Vào Ngày Hôm Đó, họ sẽ nghe và thấy tất cả, họ sẽ nghe những gì mà trước kia họ đã không tin và họ sẽ thấy những gì mà trước kia họ không tin; tuy nhiên, những kẻ làm điều sai quấy trong cuộc sống trần gian cứ công khai lệch lạc khỏi con đường Ngay Chính và không chịu chuẩn bị cho cuộc sống Đời Sau cho đến khi Ngày Đó đến với họ một cách bất ngờ trong lúc họ đang trong sự sai quấy của họ.');
INSERT INTO "vn_mokhtasar" VALUES(19,39,'19:39','Hỡi Thiên Sứ Muhammad! Hãy cảnh báo nhân loại về Ngày Hối Tiếc bởi vì Ngày Hôm Đó, kẻ làm điều xấu sẽ hối tiếc cho việc làm xấu của mình còn những ai làm tốt sẽ hối tiếc vì đã không làm nhiều thêm khi những quyển sổ ghi chép được đóng lại và sự thanh toán bắt đầu. Ngươi hãy cảnh báo họ về Ngày Đó trong lúc họ lơ là và hời hợt, không quan tâm tới Ngày Sau và không tin nơi Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(19,40,'19:40','Quả thật TA (Allah) sẽ mãi còn sau khi mọi tạo vật đều đã tiêu vong, TA sẽ kế thừa trái đất và định đoạt những gì TA muốn; và tất cả đều phải trở về trình diện TA vào Ngày Phán Xét để TA xét xử và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(19,41,'19:41','Hỡi Thiên Sứ Muhammad! Hãy nhớ lại trong Qur''an được ban xuống cho Ngươi về thông tin của Ibrahim, quả thật, Y là một người bề tôi trung thực, luôn tin những dấu hiệu của Allah, và Y là một vị Nabi từ nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(19,42,'19:42','Khi Y (Ibrahim) nói với cha của mình: Này cha, sao cha lại thờ phượng ngoài Allah những bục tượng không nghe được lời của cha khi cha cầu nguyện, không thấy hành động của cha khi cha thờ phượng và cũng không xua tan điều xấu hay mang lại điều tốt cho cha?!');
INSERT INTO "vn_mokhtasar" VALUES(19,43,'19:43','Ibrahim nói tiếp: Thưa cha, quả thật con đã lĩnh hội được kiến thức qua con đường khải thị của Allah mà cha không biết, xin cha hãy đi theo con, con sẽ hướng dẫn cha đến với con đường Ngày Chính.');
INSERT INTO "vn_mokhtasar" VALUES(19,44,'19:44','Ibrahim tiếp tục khuyên cha: Thưa cha, xin cha chớ thờ phụng Shaytan bởi quả thật Shaytan (Iblis) là kẻ đã từng phản nghịch Allah, Đấng Rất Mực Độ Lượng, khi Ngài ra lệnh hắn quỳ lạy phủ phục trước Adam thì hắn đã không chấp hành.');
INSERT INTO "vn_mokhtasar" VALUES(19,45,'19:45','Ibrahim tiếp tục khuyên cha: Thưa cha, quả thật con sợ Đấng Al-Rahman sẽ trừng phạt cha nếu cha chết trên sự vô đức tin của cha để rồi cha sẽ làm bạn với Shaytan, cùng phải chịu hình phạt chung với hắn trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(19,46,'19:46','Cha của Ibrahim (Azar) giận dữ nói: Này Ibrahim, chắc mày ghét các thần linh của tao lắm thì phải, nếu mày không chịu ngừng xúc phạm đến các thần linh của tao thì tao sẽ ném đá giết chết mày, mày hãy đi đâu cho khuất mắt tao thật lâu, chớ nói chuyện với tao nữa.');
INSERT INTO "vn_mokhtasar" VALUES(19,47,'19:47','Ibrahim nói với cha của mình: Chào tạm biệt và cầu xin sự bình an cho cha, việc cha ghét bỏ con không làm con ghét bỏ cha, con sẽ cầu xin Thượng Đế của con tha thứ cho cha và xin Ngài hướng dẫn cha; quả thật, Ngài là Đấng Hằng Nhân Từ.');
INSERT INTO "vn_mokhtasar" VALUES(19,48,'19:48','Ibrahim nói với người dân của Y: Tôi xin từ biệt các người và từ biệt những thứ thần linh mà các người đang thờ phượng ngoài Allah. Còn tôi sẽ chỉ cầu xin Thượng Đế của tôi, chỉ một mình Ngài, tôi không tổ hợp với Ngài bất cứ thần linh nào khác, chắc Thượng Đế của tôi sẽ không đến nỗi không đáp lại sự nguyện cầu của tôi để tôi gặp điều vô phúc.');
INSERT INTO "vn_mokhtasar" VALUES(19,49,'19:49','Sau khi Ibrahim bỏ người dân của Y và các thần linh mà họ thờ phượng ngoài Allah để ra đi cho con đường chân lý của Allah thì Ngài đã bù lại cho Y. Quả thật, Ngài đã ban cho y một đứa con trai tên Ishaq và một đứa cháu nội tên Y''aqub (Jacob). Cả hai đều đã được TA (Allah) chọn làm Nabi của TA.');
INSERT INTO "vn_mokhtasar" VALUES(19,50,'19:50','Và từ lòng Nhân Từ của TA, TA đã ban cho họ nhiều điều tốt đẹp và phúc lành qua sứ mạng Nabi và TA đã làm rạng danh họ qua sự ca tụng và khen ngợi trên chiếc lưỡi của những bề tôi của TA.');
INSERT INTO "vn_mokhtasar" VALUES(19,51,'19:51','Này hỡi Thiên Sứ Muhammad! Ngươi hãy nhớ lại trong Qur''an được ban xuống cho Ngươi về thông tin của Musa, quả thật, Y là một người bề tôi thành tâm, được TA chọn làm một Thiên Sứ và một Nabi.');
INSERT INTO "vn_mokhtasar" VALUES(19,52,'19:52','Và TA (Allah) đã gọi Y (Musa) từ phía bên phải của ngọn núi (Saina'') và TA đã để Y xích lại gần TA để TA nói nhỏ với Y và để Y nghe được lời phán của TA.');
INSERT INTO "vn_mokhtasar" VALUES(19,53,'19:53','Và TA (Allah) đã ban cho Musa từ Hồng Ân của TA trong việc TA đã để Harun - người em trai của Y làm một vị Nabi phụ tá Y theo lời cầu xin của Y khi Y cầu xin Ngài về điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(19,54,'19:54','Hỡi Thiên Sứ Muhammad! Ngươi hãy nhớ lại trong Qur''an được ban xuống cho Ngươi về thông tin của Isma-''il. Quả Thật, Y là một người trung thực và luôn giữ chữ tín, Y luôn thực hiện đúng theo lời hứa và Y là một vị Thiên Sứ và một vị Nabi.');
INSERT INTO "vn_mokhtasar" VALUES(19,55,'19:55','Isma-''il đã ra lệnh cho người nhà của mình duy trì lễ nguyễn Salah, xuất Zakah và Thượng Đế của Y đã hài lòng về Y.');
INSERT INTO "vn_mokhtasar" VALUES(19,56,'19:56','Hỡi Thiên Sứ Muhammad! Hãy nhớ lại trong Qur''an được ban xuống cho Ngươi về thông tin của Idris, quả thật, Y là một người bề tôi trung thực, tuyệt đối tin những dấu hiệu của Allah, và Y là một vị Nabi trong số các vị Nabi của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(19,57,'19:57','Và TA (Allah) nâng Idris lên một vị trí trên cao bởi sứ mạng Nabi mà TA đã giao phó cho Y.');
INSERT INTO "vn_mokhtasar" VALUES(19,58,'19:58','Những người được nhắc đến trong chương Kinh này được bắt đầu với Zakariya và kết thúc với Idris là những người được Allah ban hồng ân qua sứ mạng Nabi. Tất cả họ xuất thân từ con cháu của Adam, từ những người mà Allah đã chở họ trên con tàu cùng với Nuh, và từ con cháu của Ibrahim, của Y''aqub và những ai mà Allah đã hướng dẫn họ đến với Islam. TA (Allah) đã tuyển chọn họ và giao phó sứ mạng Nabi cho họ; và mỗi khi họ nghe được những lời phán của TA thì họ đọc và phủ phục quỳ lạy TA trong tiếng khóc vì sợ TA.');
INSERT INTO "vn_mokhtasar" VALUES(19,59,'19:59','Và sau các vị Nabi được tuyển chọn này sẽ có một hậu thế đi theo điều xấu và sự lầm lạc, họ bỏ bê lễ nguyện Salah, họ không thực hiện lễ nguyện Salah đúng theo yêu cầu, họ đi theo những dục vọng của bản thân để làm điều Haram như Zina. Bởi thế, họ sẽ phải gặp sự trừng phạt tồi tệ nơi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(19,60,'19:60','Ngoại trừ những ai biết ăn năn sám hối từ sự bê tha nghĩa vụ cũng những việc làm tội lỗi, tin nơi Allah và hành động nhiều việc làm thiện tốt thì những người đó là những người sẽ vào Thiên Đàng và họ sẽ không bị cắt giảm đi bất cứ điều gì từ ân phước cho việc làm của họ cho dù việc làm đó ít ỏi');
INSERT INTO "vn_mokhtasar" VALUES(19,61,'19:61','Những Ngôi Vườn Vĩnh Cửu nơi Thiên Đàng mà Đấng Rất Mực Độ Lượng (Allah) đã hứa ban cho những bề tôi ngoan đạo của Ngài để họ vào trong đó sống đời sống hạnh phúc vĩnh hằng mặc dầu nó thuộc điều vô hình, họ chưa nhìn thấy nó nhưng họ đã tin nơi nó thì hãy biết rằng lời hứa ban thưởng Thiên Đàng của Allah là sự thật, nó chắc chắn sẽ xảy ra đúng như vậy.');
INSERT INTO "vn_mokhtasar" VALUES(19,62,'19:62','Trong Thiên Đàng, họ sẽ không nghe thấy những lời nói sàm bậy và thô tục mà chỉ toàn nghe những lời nói an lành và tốt đẹp, họ sẽ được nghe lời chào Bằng An từ các vị Thiên Thần, và ở trong đó, họ sẽ được cung phụng những thực phẩm và nguồn thức ăn tốt lành và hảo hạng sáng và chiều.');
INSERT INTO "vn_mokhtasar" VALUES(19,63,'19:63','Thiên Đàng được mô tả đó là nơi sẽ được các bề tôi của TA (Allah) thừa hưởng, những người mà họ đã chấp hành theo các mệnh lệnh của TA và tránh xa những điều cấm của TA.');
INSERT INTO "vn_mokhtasar" VALUES(19,64,'19:64','Này Jibril, Ngươi hãy nói với Muhammad: Quả thật các Thiên Thần không xuống trần theo ý của họ mà họ xuống trần là theo lệnh truyền của Allah. Những gì phía trước chúng tôi nơi cuộc sống Đời Sau, những gì ở phía sau chúng tôi nơi cõi trần và những gì giữa cuộc sống trần gian và Đời Sau đều là của Allah. Và này Muhammad - Thiên Sứ của Allah, quả thật, Thượng Đế của Ngươi không hề quên bất cứ một điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(19,65,'19:65','Allah là Đấng Tạo Hóa các tầng trời, trái đất và vạn vật giữa trời đất, tất cả đều là của Ngài, nằm trong tầm kiếm soát của Ngài, Ngài chi phối, điều hành và chế ngự tất cả. Bởi thế, hãy thờ phượng một mình Ngài, duy chỉ Ngài mới đáng được thờ phượng, không có bất cứ ai (vật gì) là đối tác sánh vai cùng với Ngài trong cái quyền này.');
INSERT INTO "vn_mokhtasar" VALUES(19,66,'19:66','Người vô đức tin và phủ nhận sự Phục Sinh bỡn cợt nói: Chẳng lẽ khi tôi chết đi rồi tôi lại được làm cho sống lại một lần nữa từ cõi mộ ư, điều đó sự thật sao?! Quả thật, điều này quá xa vời.');
INSERT INTO "vn_mokhtasar" VALUES(19,67,'19:67','Há những kẻ phủ nhận sự Phục Sinh không nhớ lại rằng chính TA (Allah) đã tạo hóa y trước đây trong lúc y không là cái gì cả đó sao?! Sự tạo hóa lần đầu là minh chứng cho sự tạo hóa lần thứ hai, vả lại, sự tạo hóa lần hai chắc chắn sẽ dễ dàng và đơn gian hơn lần đầu.');
INSERT INTO "vn_mokhtasar" VALUES(19,68,'19:68','Thề bởi Thượng Đế của Ngươi - hỡi Thiên Sứ Muhammad - rằng chắc chắn TA sẽ đưa chúng ra từ các ngôi mộ của chúng và tập trung chúng lại cùng với lũ Shaytan, những kẻ đã dẫn chúng lầm lạc, rồi sau đó TA sẽ để chúng quì gối xung quanh Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(19,69,'19:69','Rồi đây, chắc chắn TA (Allah) sẽ lôi cổ từng tên ương ngạnh nhất của mỗi nhóm phái lệch lạc ra trình diện TA.');
INSERT INTO "vn_mokhtasar" VALUES(19,70,'19:70','Rồi chắc chắn TA (Allah) sẽ biết ai là những kẻ đáng bị đày vào trong Hỏa Ngục để chịu sự trừng phạt đó.');
INSERT INTO "vn_mokhtasar" VALUES(19,71,'19:71','Này hỡi nhân loại! Chắc chắn không một ai trong các ngươi có thể tránh khỏi việc đi qua chiếc cầu Sirat được bắt ngang qua Hỏa Ngục. Và việc đi qua chiếc cầu này đã được quyết định nơi Allah, không một ai có thể tránh khỏi quyết định của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(19,72,'19:72','Sau trận đi qua chiếc cầu Sirat, TA (Allah) sẽ làm cho những người ngoan đạo: ngay chính kính sợ Allah mà thực thi các mệnh lệnh của Ngài và tránh những điều cấm của Ngài. Nhưng đối với những kẻ làm điều sai quấy, TA sẽ bỏ mặc họ rơi xuống Hỏa Ngục và họ sẽ quỳ gối trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(19,73,'19:73','Và khi các Lời Mặc Khải của TA được đọc lên cho nhân loại nghe qua chiếc lưỡi của vị Thiên Sứ của TA một cách rõ ràng thì những kẻ vô đức tin nói với những người có đức tin: Nhóm nào trong hai nhóm chúng ta có cuộc sống tốt nhất trong xã hội và có địa vị tốt nhất trong Hội Đồng (ở Makkah này), nhóm của bọn ta hay nhóm của các ngươi?!');
INSERT INTO "vn_mokhtasar" VALUES(19,74,'19:74','Quả thật đã có không biết bao nhiêu thế hệ trước những kẻ vô đức tin kiêu ngạo này đã bị TA tiêu diệt, và những thế hệ đó có sự giàu có và địa vị cao trọng và hào nhoáng hơn chúng rất nhiều.');
INSERT INTO "vn_mokhtasar" VALUES(19,75,'19:75','Hãy nói đi - hỡi Thiên Sứ Muhammad -: Những ai cứ trong sự lầm lạc thì Đấng Độ Lượng (Allah) sẽ tăng thêm cho họ sự lầm lạc cho tới khi họ đối mặt với những gì đã được hứa từ sự trừng phạt, hoặc là ở trên thế gian hoặc là Ngài sẽ trì hoãn sự trừng phạt đến Ngày Phán Xét. Và lúc đó họ sẽ biết ai mới là nhóm người có địa vị tồi tệ nhất, nhóm của họ hay nhóm của những người có đức tin?');
INSERT INTO "vn_mokhtasar" VALUES(19,76,'19:76','Những kẻ lầm lạc bị Allah gia tăng thêm sự lầm lạc, trái ngược với họ, những người có đức tin sẽ được Allah gia tăng sự Chỉ Đạo, họ trở nên tuân lệnh Ngài nhiều hơn và làm nhiều việc làm thiện tốt hơn. Và các việc làm thiện tốt đối với Thượng Đế của Ngươi - hỡi Thiên Sứ Muhammad - mới là điều dẫn tới sự hạnh phúc vĩnh cửu và sẽ mang lại phần thưởng tốt đẹp và kết cục phúc lành ở nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(19,77,'19:77','Này Thiên Sứ Muhammad! Ngươi có thấy chăng kẻ phủ nhận Bức Thông Điệp của TA (Allah) và bác bỏ lời hứa thưởng phạt của TA lại nói: "Nếu tôi chết tôi sẽ được cho sống lại và được ban cho nhiều tài sản và con cái" không?');
INSERT INTO "vn_mokhtasar" VALUES(19,78,'19:78','Chẳng lẽ y đã thực sự biết rõ về những điều vô hình nên đã dám khẳng định như thế?! hay chẳng lẽ y đã nhận được một sự cam kết từ Thượng Đế của y rằng Ngài sẽ để y vào Thiên Đàng và sẽ ban cho y tài sản và con cái?!');
INSERT INTO "vn_mokhtasar" VALUES(19,79,'19:79','Sự việc chắc chắn không như y đã khẳng định. TA (Allah) sẽ cho ghi lại những gì y nói cũng như những gì y làm và chắc chắn TA sẽ tăng thêm mức hình phạt cho y về những điều ngụy tạo mà y đã bịa ra.');
INSERT INTO "vn_mokhtasar" VALUES(19,80,'19:80','TA (Allah) sẽ thừa kế những gì y bỏ lại từ tài sản và con cái sau khi TA hủy diệt y và vào Ngày Phán Xét y sẽ đến trình diện TA đơn lẻ một mình, chẳng có gì giúp được y cả.');
INSERT INTO "vn_mokhtasar" VALUES(19,81,'19:81','Những kẻ thờ đa thần nhận lấy các đấng thờ phượng ngoài Allah mục đích để chúng trợ giúp và phù hộ cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(19,82,'19:82','Sự việc không như những gì những kẻ thờ đa thần khẳng định. Những thứ mà họ thờ phượng ngoài Allah sẽ chối bỏ sự thờ phượng của họ vào Ngày Phán Xét, những thần linh đó của họ sẽ một mực chối bỏ sự can hệ với họ và chúng trở thành những kẻ thù chống lại họ.');
INSERT INTO "vn_mokhtasar" VALUES(19,83,'19:83','Này Thiên Sứ Muhammad! Há Ngươi không thấy TA (Allah) đã gởi các tên Shaytan đến lôi kéo những kẻ vô đức tin làm điều trái lệnh và ngăn cản tôn giáo của Ngài sao?');
INSERT INTO "vn_mokhtasar" VALUES(19,84,'19:84','Này Thiên Sứ Muhammad! Ngươi chớ vội yêu cầu Allah mau chóng hủy diệt chúng, thật ra, TA (Allah) chỉ gia hạn tuổi đời cho chúng đến một thời hạn nhất định rồi chúng sẽ bị trừng phạt thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(19,85,'19:85','Hỡi Thiên Sứ Muhammad! Hãy nhớ lại Ngày Phán Xét, đó là Ngày mà TA (Allah) sẽ triệu tập những người ngoan đạo đối với Thượng Đế của họ - chấp hành mệnh lệnh và tránh xa những điều cấm - đến với Thượng Đế của họ như một phái đoàn vinh dự.');
INSERT INTO "vn_mokhtasar" VALUES(19,86,'19:86','Và TA (Allah) sẽ lùa những kẻ vô đức tin đến Hỏa Ngục như một đàn thú, một hình ảnh đê hèn và nhục nhã dành cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(19,87,'19:87','Những kẻ vô đức tin này sẽ không có quyền can thiệp cho nhau, chỉ có những ai đã nhận lấy từ nơi Allah sự giao ước trên thế gian bằng đức tin nơi Ngài và nơi các vị Thiên Sứ của Ngài mới được ban cho quyền này.');
INSERT INTO "vn_mokhtasar" VALUES(19,88,'19:88','Những người Do Thái và Thiên Chúa giáo cũng như một số kẻ thờ đa thần đã nói rằng Allah - Đấng Rất Mực Độ Lượng có một đứa con trai.');
INSERT INTO "vn_mokhtasar" VALUES(19,89,'19:89','Hỡi những kẻ đã nói lời lẽ này, quả thật các ngươi đã mang đến một điều sàm bậy quá lớn.');
INSERT INTO "vn_mokhtasar" VALUES(19,90,'19:90','Từ lời nói sàm bậy và tội lỗi đó của các ngươi, các tầng trời gần như muốn vỡ tung, trái đất gần như muốn nứt ra và các quả núi gần như muốn sụp xuống tan tành.');
INSERT INTO "vn_mokhtasar" VALUES(19,91,'19:91','Tất cả những điều đó là bởi vì chúng đã gán cho Allah một đứa con trai trong khi Ngài tối cao và vĩ đại hơn thế.');
INSERT INTO "vn_mokhtasar" VALUES(19,92,'19:92','Và thật không có lý chút nào khi cho rằng Allah - Đấng Rất Mực Độ Lượng có một đứa con trai. Điều đó vô cùng sai trái và nghịch lý đối với một Đấng Tối Cao - Quyền Năng vô song và ưu việt.');
INSERT INTO "vn_mokhtasar" VALUES(19,93,'19:93','Quả thật, tất cả những ai trong các tầng trời và trái đất từ các Thiên Thần, con người, loài Jinn, vào Ngày Phán Xét đều đến trình diện trước Allah - Đấng Rất Mực Độ Lượng dưới cương vị là những bề tôi khép nép và sợ hãi trước Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(19,94,'19:94','Quả thật, Allah bao trùm lấy họ bởi kiến thức của Ngài, tất cả số lượng của họ đều được nắm rõ nơi Ngài, không có một điều gì nằm ngoài tầm kiểm soát của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(19,95,'19:95','Vào Ngày Phán Xét, tất cả đều phải một thân một mình đứng trình diện trước Allah, không có người trợ giúp cũng không thể dùng tài sản để can thiệp.');
INSERT INTO "vn_mokhtasar" VALUES(19,96,'19:96','Quả thật, những người có đức tin nơi Allah và làm điều thiện tốt mà Allah hài lòng thì sẽ được Ngài yêu thương và được Ngài khiến cho các bề tôi của Ngài yêu thương.');
INSERT INTO "vn_mokhtasar" VALUES(19,97,'19:97','Này hỡi Thiên Sứ Muhammad! Quả thật, TA (Allah) làm cho Qur''an thành dễ dàng trên chiếc lưỡi của Ngươi là chỉ nhằm mục đích để Ngươi báo tin vui cho những người ngoan đạo - những người mà họ luôn thi hành theo mệnh lệnh của TA và tránh xa những điều TA nghiêm cấm - và để Ngươi cảnh báo những đám người thường hay tranh cãi hầu chối bỏ chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(19,98,'19:98','Trước cộng đồng của Ngươi - Muhammad - đã có không biết bao nhiêu cộng đồng bị TA (Allah) tiêu diệt. Và ngay hôm nay, Ngươi có cảm thấy còn sót lại ai đó trong những cộng đồng đó không hoặc Ngươi có nghe thấy lời thì thầm của họ đâu đó không?! Bởi thế, những gì xảy ra với họ có thể sẽ xảy ra với những ai khác họ khi Allah cho phép.');
INSERT INTO "vn_mokhtasar" VALUES(20,1,'20:1','{Ta.ha.} là các chữ cái tiếng Ả Rập, ý nghĩa của việc dùng chúng khai đề cho chương Kinh đã được đề cập ở phần đầu của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(20,2,'20:2','Này Thiên Sứ Muhammad! Quả thật, TA (Allah) ban Kinh Qur''an xuống cho Ngươi không phải để khiến Ngươi buồn phiền trước việc người dân của Ngươi từ chối tin nơi Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(20,3,'20:3','TA (Allah) ban Qur''an xuống cho Ngươi (Muhammad) là chỉ làm lời nhắc nhở cho những ai có lòng kính sợ Allah.');
INSERT INTO "vn_mokhtasar" VALUES(20,4,'20:4','Chính Allah, Đấng Tạo Hóa trái đất và các tầng trời cao đã ban Nó xuống, Nó là Kinh Qur''an vĩ đại được mặc khải xuống từ Đấng Vĩ Đại.');
INSERT INTO "vn_mokhtasar" VALUES(20,5,'20:5','Đấng Rất Mực Độ Lượng an vị trên chiếc Ngai Vương của Ngài theo sự tối cao và vĩ đại của Ngài (khác xa và vượt trội so với các tạo vật của Ngài).');
INSERT INTO "vn_mokhtasar" VALUES(20,6,'20:6','Vạn vật trong các tầng trời, vạn vật trên trái đất và dưới lòng đất đều là tạo vật của Allah, Ngài toàn quyền chi phối, điều hành và chế ngự chúng; không có đối tác chia sẽ cùng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(20,7,'20:7','Này Thiên Sứ Muhammad! Dù Ngươi công khai hay bí mật trong lời nói của Ngươi thì quả thật Ngài (Allah) vẫn thông toàn tất cả, Ngài biết tường tận những điều bí mật cũng như những gì ẩn chứa trong các điều bí mật, không có bất cừ điều thầm kín và bí mật nào có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(20,8,'20:8','Allah là Đấng mà không có đấng thờ phượng đích thực nào khác ngoài Ngài, Ngài là Đấng Thờ Phượng, là Thượng Đế Tối Cao, duy chỉ một mình Ngài, và Ngài có các tên gọi hoàn mỹ.');
INSERT INTO "vn_mokhtasar" VALUES(20,9,'20:9','Và quả thật một câu chuyện của Musa đã đến với Ngươi hỡi Thiên Sứ Muhammad.');
INSERT INTO "vn_mokhtasar" VALUES(20,10,'20:10','Lúc Musa nhìn thấy một ngọn lửa trên chuyến đi, Y đã bảo gia đình của mình: Mọi người hãy tạm ở yên chỗ này, Ta sẽ đi đến ngọn lửa đó để xem, hy vọng Ta sẽ tìm thấy một sự chỉ dẫn đến với một con đường hoặc biết đâu Ta sẽ mang về một khúc than hồng (cho mọi người sưởi ấm hoặc dùng cho việc cần nào đó trong sinh hoạt của chúng ta).');
INSERT INTO "vn_mokhtasar" VALUES(20,11,'20:11','Khi Musa đến chỗ ngọn lửa thì Allah đã gọi Người với lời: Này hỡi Musa!');
INSERT INTO "vn_mokhtasar" VALUES(20,12,'20:12','Allah phán bảo Musa: Quả thật, TA chính là Thượng Đế của Ngươi, nào Ngươi hãy cởi giày của Ngươi ra để chuẩn bị cho cuộc nói chuyện riêng với TA, quả thật Ngươi đang ở tại thung lũng Tuwa thanh sạch.');
INSERT INTO "vn_mokhtasar" VALUES(20,13,'20:13','Này Musa, TA (Allah) đã chọn Ngươi làm người truyền đạt Bức Thông Điệp của TA. Bởi thế, Ngươi hãy lắng nghe TA mặc khải cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(20,14,'20:14','Allah phán với Musa: Quả thật, TA chính Allah, Đấng đáng được thờ phượng duy nhất, không có đấng nào khác đáng được thờ phượng ngoài TA. Bởi thế, hãy thờ phượng một mình TA và chu đáo dâng lễ nguyện Salah để tướng nhớ đến TA.');
INSERT INTO "vn_mokhtasar" VALUES(20,15,'20:15','Allah phán với Musa tiếp: Quả thật, Giờ Tận Thế chắc chắn sẽ xảy ra, nhưng TA giữ kín không để bất cứ tạo vật nào biết về thời khắc của nó. Tuy nhiên, TA đã cho chúng biết những dấu hiệu báo trước của nó thông qua các thông điệp từ vị Nabi của họ, mục đích để mỗi linh hồn được ban thưởng hoặc bị trừng phạt cho điều tốt và điều xấu mà họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(20,16,'20:16','Allah tiếp tục phán với Musa: Bởi thế, Ngươi chớ để cho ai trong những kẻ vô đức tin cản trở Ngươi tin nơi Giờ Tận Thế và chuẩn bị cho thời khắc đó bởi những việc làm ngoan đạo và thiện tốt; và Ngươi chớ để chúng lôi kéo Ngươi đi theo dục vọng của bản thân mà phạm vào điều cấm của TA để rồi Ngươi sẽ bị hủy diệt.');
INSERT INTO "vn_mokhtasar" VALUES(20,17,'20:17','Allah hỏi Nabi Musa: Tay phải Ngươi cầm vật gì, hỡi Musa?');
INSERT INTO "vn_mokhtasar" VALUES(20,18,'20:18','Nabi Musa thưa với Allah: Đây là cây gậy, bề tôi dùng nó để chống cho việc đi lại, để đập rụng lá cây cho đàn cừu ăn và bề tôi còn dùng cho nhiều việc khác nữa.');
INSERT INTO "vn_mokhtasar" VALUES(20,19,'20:19','Allah bảo Musa: Này Musa, Ngươi hãy ném chiếc gậy của Ngươi xuống!');
INSERT INTO "vn_mokhtasar" VALUES(20,20,'20:20','Vậy là Nabi Musa nghe theo lệnh Allah, ném chiếc gậy của mình xuống. Nó liền biến thành một con rắn bò nhanh một cách nhẹ nhàng.');
INSERT INTO "vn_mokhtasar" VALUES(20,21,'20:21','Allah phán bảo Musa cầm lấy chiếc gậy và bảo Người đừng sợ việc chiếc gậy biến thành con rắn, Ngài bảo Người cứ cầm lấy nó thì nó sẽ trở lại nguyên trạng ban đầu.');
INSERT INTO "vn_mokhtasar" VALUES(20,22,'20:22','Allah bảo Musa lấy bàn tay phải áp vào ba sườn của mình, nó sẽ hiện ra ánh sáng màu trắng. Và đó là Phép Mầu thứ hai mà Ngài cho Người.');
INSERT INTO "vn_mokhtasar" VALUES(20,23,'20:23','Hai Phép Lạ mà TA (Allah) vừa phơi bày cho Ngươi thấy - hỡi Musa - chính là hai Phép Lạ nằm trong các Phép Lạ lớn của TA. TA muốn cho Ngươi thấy quyền năng của TA và rằng Ngươi đích thực là vị Thiên Sứ của TA - Thượng Đế Toàn Năng.');
INSERT INTO "vn_mokhtasar" VALUES(20,24,'20:24','Này Musa, Ngươi hãy đi gặp Pha-ra-ông, quả thật, hắn là một tên vượt quá giới hạn trong sự vô đức tin và chống lại Allah.');
INSERT INTO "vn_mokhtasar" VALUES(20,25,'20:25','Musa thưa với Allah: Lạy Thượng Đế của bề tôi! Xin Ngài nới rộng lòng ngực của bề tôi để bề tôi chịu đựng những khó khăn và tổn hại.');
INSERT INTO "vn_mokhtasar" VALUES(20,26,'20:26','Musa xin Allah làm dễ dàng và thuận lợi cho công việc của mình.');
INSERT INTO "vn_mokhtasar" VALUES(20,27,'20:27','Musa cầu xin Allah làm cho chiếc lưỡi của Người nói chuyện được lưu loát và trôi chảy.');
INSERT INTO "vn_mokhtasar" VALUES(20,28,'20:28','Musa cầu xin Allah làm cho chiếc lưỡi của Người nói năng trôi chảy và rõ ràng để đối phương hiểu được lời nói của Người khi Người truyền đạt lại Bức Thông Điệp cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(20,29,'20:29','Musa cầu xin Allah chọn lấy một ai đó trong gia đình của Người để phú tá cho Người trong việc truyền đạt Bức Thông Điệp của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(20,30,'20:30','Musa cầu xin Ngài chọn người anh trai của mình làm phụ tá cho Người, đó là Harun bin ''Imran.');
INSERT INTO "vn_mokhtasar" VALUES(20,31,'20:31','Musa cầu xin Allah gia tăng thêm sức mạnh cho Người qua người anh trai Harun của Người.');
INSERT INTO "vn_mokhtasar" VALUES(20,32,'20:32','Musa cầu xin Allah để người anh của mình - Harun chia sẻ công việc rao truyền Bức Thông Điệp cùng với Người.');
INSERT INTO "vn_mokhtasar" VALUES(20,33,'20:33','Musa cầu xin Allah như thế mục đích để hai anh em họ có thể tán dương Ngài nhiều hơn.');
INSERT INTO "vn_mokhtasar" VALUES(20,34,'20:34','Và để hai người họ nhớ đến Ngài nhiều hơn.');
INSERT INTO "vn_mokhtasar" VALUES(20,35,'20:35','Musa tiếp tục thưa với Allah: Quả thật, Ngài luôn nhìn thấy bầy tôi, không có một điều gì từ việc làm của bầy tôi có thể giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(20,36,'20:36','Allah phán với Musa rằng tất cả những điều mà Musa yêu cầu đều được Ngài chấp thuận.');
INSERT INTO "vn_mokhtasar" VALUES(20,37,'20:37','Và quả thật, TA (Allah) đã ban cho Ngươi (Musa) ân huệ trong một lần khác nữa.');
INSERT INTO "vn_mokhtasar" VALUES(20,38,'20:38','Hãy nhớ lại khi TA (Allah) mặc khải cho mẹ Ngươi những lời mặc khải về việc Ngài bảo vệ Ngươi khỏi kế hoạch giết hại của Fir-''awn.');
INSERT INTO "vn_mokhtasar" VALUES(20,39,'20:39','Quả thật, TA (Allah) đã ra lệnh cho mẹ Ngươi (Musa) ném Ngươi xuống dòng sông: "Sau khi hạ sinh đứa bé, ngươi hãy bỏ nó vào một cái rương rồi đem bỏ cái rương đó xuống biển. TA sẽ làm cho biển đẩy chiếc rương vào bờ với quyền năng của TA rồi kẻ thù của TA và của nó (Musa) tức Fir-''awn sẽ nhặt lấy nó." Và TA đã để Ngươi (Musa) được yêu thương bởi mọi người và TA đã Ngươi được nuôi dưỡng dưới sự quan tâm và trông coi của TA.');
INSERT INTO "vn_mokhtasar" VALUES(20,40,'20:40','Hãy nhớ lại khi người chị của Ngươi - hỡi Musa - rời khỏi nhà để đi theo những người mang chiếc rương đi. Chị Ngươi nói với những ai lấy nó: quí Ngài có muốn tôi chỉ cho quí ngài một vú nuôi chăm sóc cho đứa bé không? Thế là TA (Allah) đã ban hồng ân cho Ngươi bằng cách trả Ngươi về lại với mẹ của Ngươi để bà ta không còn buồn lòng vì Ngươi nữa. Rồi Ngươi đã giết chết một người Ai Cập, TA ban hồng ân cho Ngươi bằng cách cứu Ngươi khỏi sự nguy khốn cho việc làm đó của Ngươi. TA đã thử thách Ngươi lần này đến lần khác và đã giúp Ngươi vượt qua chúng. Sau đó, Ngươi lưu vong ở cùng với dân Madyan trong nhiều năm và giờ đây Ngươi đến đây như đã định để TA phán truyền cho Ngươi hỡi Musa!');
INSERT INTO "vn_mokhtasar" VALUES(20,41,'20:41','Và TA (Allah) đã chọn Ngươi (Musa) làm một vị Sứ Giả rao truyền các lời mặc khải của TA đến nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(20,42,'20:42','Này Musa, Ngươi và anh của Ngươi - Harun hãy lên đường, với các Phép Lạ chứng tỏ quyền năng và tính duy nhất của TA (Allah), hai ngươi chớ yếu đuối trong việc kêu gọi mọi người đến với TA và chớ xao lãng trong việc nhớ đến TA.');
INSERT INTO "vn_mokhtasar" VALUES(20,43,'20:43','Này Musa, Ngươi hãy đi gặp Pha-ra-ông, quả thật, hắn là một tên vượt quá giới hạn trong sự vô đức tin và chống lại Allah.');
INSERT INTO "vn_mokhtasar" VALUES(20,44,'20:44','Hai ngươi hãy nói hắn (Pha-ra-ông) bằng lời lẽ nhã nhặn, chớ dùng ngôn từ thô lỗ, hy vọng hắn thức tỉnh, biết sợ Allah mà quay đầu sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(20,45,'20:45','Musa và Harun thưa với Allah: Quả thật bầy tôi sợ Pha-ra-ông ra lệnh bắt phạt bầy tôi trước khi bầy tôi hoàn thành việc kêu gọi y hoặc y sẽ giết hại bầy tôi.');
INSERT INTO "vn_mokhtasar" VALUES(20,46,'20:46','Allah phán với Musa và Harun: Hai ngươi chớ đừng sợ, quả thật, TA luôn ở bên các ngươi để phù hộ và giúp đỡ, TA luôn nghe và luôn thấy những gì diễn ra giữa hai ngươi và hắn.');
INSERT INTO "vn_mokhtasar" VALUES(20,47,'20:47','Allah phán bảo Musa và Harun: Nào, hai ngươi hãy đi gặp hắn (Pha-ra-ông), hai ngươi hãy nói với hắn: Này hỡi Pha-ra-ông! Quả thật, chúng tôi là Sứ Giả do Thượng Đế của ngài phái đến, mong ngài để cho dân Isra-el đi cùng với chúng tôi, mong ngài chớ trừng phạt họ bằng cách giết hại các con trai của họ và hạ nhục phụ nữ của họ. Quả thật, chúng tôi đã mang đến một bằng chứng từ Thượng Đế của ngài để minh chứng cho sứ mạng của chúng tôi; và sự an toàn khỏi sự trừng phạt của Allah đến với những ai có đức tin và đi theo Chỉ Đạo của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(20,48,'20:48','Quả thật, chúng tôi được Allah mặc khải cho biết rằng những ai phủ nhận các Lời Mặc Khải của Allah và quay lưng với những gì mà các vị Sứ Giả của Ngài mang đến sẽ bị Ngài trừng phạt trên trần gian và cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(20,49,'20:49','Pha-ra-ông nói với Musa và Harun một cách phản đối: Này Musa, thế ai là Thượng Đế của hai ngươi mà hai ngươi cho rằng đã cử phái hai ngươi đến với ta?');
INSERT INTO "vn_mokhtasar" VALUES(20,50,'20:50','Musa đáp với Pha-ra-ông: Thượng Đế của chúng tôi là Đấng đã tạo vạn vật với hình thể và bản chất thích hợp, rồi Ngài ban cho nguồn Chỉ Đạo đến các tạo vật sau khi Ngài tạo ra chúng.');
INSERT INTO "vn_mokhtasar" VALUES(20,51,'20:51','Pha-ra-ông nói: Thế đối với những cộng đồng trước đây đã vô đức tin thì sao?');
INSERT INTO "vn_mokhtasar" VALUES(20,52,'20:52','Musa trả lời Pha-ra-ông: Kiến thức về những cộng đồng trước là ở nơi Thượng Đế của tôi, nó được ghi chép rõ ràng trong Văn Bảng Lưu Trữ hay quyển Kinh Mẹ ở nơi Ngài. Thượng Đế của tôi không nhầm lẫn trong kiến thức của Ngài và cũng không hề quên sót bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(20,53,'20:53','Thượng Đế của tôi chính là Đấng đã làm cho trái đất thành một nơi để an cư và làm ra nơi đó những con đường để đi lại, và Ngài đã ban mưa từ trên trời xuống để làm mọc ra các loại cây trái và thảo mộc khác nhau.');
INSERT INTO "vn_mokhtasar" VALUES(20,54,'20:54','Này hỡi nhân loại! Các ngươi hãy ăn từ những gì tốt lành mà TA (Allah) đã làm mọc ra cho các ngươi, các ngươi hãy ăn chúng và dùng chúng để nuôi gia súc của các ngươi. Trong ân huệ được đề cập đó là những bằng chứng về quyền năng của Allah và tính duy nhất của Ngài cho những người hiểu biết nghiền ngẫm.');
INSERT INTO "vn_mokhtasar" VALUES(20,55,'20:55','Từ đất bụi TA (Allah) đã tạo ra người cha của các ngươi - Adam, và TA sẽ đưa các ngươi trở lại với đất bụi bằng sự chôn cất khi các ngươi chết đi và cũng từ đất bụi TA sẽ đưa các ngươi trở ra một lần nữa vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(20,56,'20:56','Quả thật, TA (Allah) đã phơi bày cho Pha-ra-ông tất cả chín Phép Lạ và hắn đã chứng kiến chúng nhưng hắn đã từ chối không chịu tin nơi TA.');
INSERT INTO "vn_mokhtasar" VALUES(20,57,'20:57','Pha-ra-ông nói với Musa: Này Musa, có phải ngươi đến gặp ta mang theo những phép thuật với mục đích muốn đuổi bọn ta ra khỏi lãnh thổ - Ai Cập của bọn ta để rồi ngươi chiếm hữu nó hoàn toàn ư?');
INSERT INTO "vn_mokhtasar" VALUES(20,58,'20:58','Này Musa, bọn ta sẽ mang đến những phép thuật tương tự để đối chọi với phép thuật của ngươi. Ta định một cuộc hẹn giữa bọn ta và ngươi, bọn ta và ngươi chớ đừng lỡ hẹn, và giữa bọn ta và ngươi sẽ có cuộc thi đấu công bằng.');
INSERT INTO "vn_mokhtasar" VALUES(20,59,'20:59','Musa nói với Pha-ra-ông: Cuộc hẹn giữa chúng tôi và các ngài là vào Ngày Đại Hội lúc dân chúng tụ tập vào buổi sáng khoảng tầm từ lúc mặt trời lên cao một sào trở đi.');
INSERT INTO "vn_mokhtasar" VALUES(20,60,'20:60','Vậy là Pha-ra-ông quay về, tập hợp các thầy pháp và bàn thảo kế hoạch rồi đến nơi hẹn vào ngày đã định.');
INSERT INTO "vn_mokhtasar" VALUES(20,61,'20:61','Musa khuyên bảo đến những thầy pháp của Pha-ra-ông: Quí ngài hãy cẩn thận, chớ gán cho Allah về những điều mà quí ngài sẽ lừa dối dân chúng qua những màn ảo thuật của quí ngài, có thể Allah sẽ trừng phạt quí ngài. Quả thật, những kẻ ngụy tạo rồi gán cho Allah chắc chắn thất bại.');
INSERT INTO "vn_mokhtasar" VALUES(20,62,'20:62','Khi nghe được những lời của Musa, những tên pháp sư của Pha-ra-ông tranh luận với nhau, và họ đã họp kín với nhau.');
INSERT INTO "vn_mokhtasar" VALUES(20,63,'20:63','Những tên pháp sư của Pha-ra-ông bảo nhau trong cuộc họp kín của chúng: Quả thật Musa và Harun là hai kẻ phù thủy, hai tên này muốn dùng pháp thuật của chúng để đuổi cổ chúng ta ra khỏi lãnh thổ - Ai Cập và chúng muốn xóa bỏ con đường pháp thuật cao quý của chúng ta khỏi cuộc sống.');
INSERT INTO "vn_mokhtasar" VALUES(20,64,'20:64','Những tên pháp sư dặn dò nhau trong cuộc họp kín: Các người phải tập trung hợp sức, không được rời nhau riêng lẻ, rồi tiến lên theo hàng ngũ và hãy ném những gì ở nơi các người cùng một lúc. Quả thật, hôm nay theo yêu cầu bên nào cao tay hơn thì bên đó giành chiến thắng.');
INSERT INTO "vn_mokhtasar" VALUES(20,65,'20:65','Những tên pháp sư của Pha-ra-ông nói với Musa: Này Musa, ngươi hãy lựa chọn một trong hai, hoặc ngươi ném những gì ở nơi ngươi xuống trước để làm pháp thuật hoặc bọn ta là những người thực hiện trước tiên.');
INSERT INTO "vn_mokhtasar" VALUES(20,66,'20:66','Musa nói: Không, quí vị hãy trổ tài trước những gì quí vị có. Vậy là những tên pháp sư trổ tài trước. Khi chúng ném bửu bối của chúng xuống, những sợi dây cũng như những chiếc gậy của chúng gây cho Musa ảo giác giống như nó biến thành hai con rắn to đang trườn bò rất nhanh.');
INSERT INTO "vn_mokhtasar" VALUES(20,67,'20:67','Nhìn thấy những gì từ trò ảo thuật của các tên pháp sư, Musa cảm thấy sợ hãi trong lòng.');
INSERT INTO "vn_mokhtasar" VALUES(20,68,'20:68','Allah trấn an Musa: Ngươi đừng sợ trước những điều ảo giác đang diễn ra trước mặt Ngươi. Quả thật, Ngươi - hỡi Musa - cao tay hơn chúng, Ngươi sẽ đánh bại chúng và giành chiến thắng.');
INSERT INTO "vn_mokhtasar" VALUES(20,69,'20:69','Và Ngươi hãy ném chiếc gậy ở nơi tay phải của Ngươi xuống, nó sẽ biến thành con rắn nuốt tất cả những gì chúng đã làm ra bởi những gì chúng làm chỉ là trò ảo thuật và kẻ làm trò ảo thuật sẽ không thắng được dù ở bất cứ nơi nào.');
INSERT INTO "vn_mokhtasar" VALUES(20,70,'20:70','Musa quăng chiếc gậy của Người xuống lập tức nó biến thành một con rắn nuốt hết tất cả những gì mà những tên pháp sư đã trổ tài ảo thuật. Thế là những tên pháp sư cúi đầu quỳ lạy phủ phục Allah vì họ biết những gì ở nơi Musa không phải là trỏ ảo thuật mà nó thực sự đến từ nơi Allah. Họ nói: chúng tôi tin Thương Đế của Musa và Harun và của tất cả vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(20,71,'20:71','Pha-ra-ông phẫn nộ quát những tên pháp sư với những lời đe dọa họ: Các ngươi bạo gan dám tin Musa trước khi ta cho phép các ngươi làm điều đó hay sao? Quả thật, Musa chính là kẻ đầu sỏ của các ngươi, hắn đã dạy các ngươi pháp thuật. Được, ta sẽ cho cắt tay và chân mỗi người các ngươi mỗi bên một cái, rồi ta sẽ cho đóng đinh các ngươi trên thân cây chà là cho đến chết để làm bài học răn đe cho những kẻ khác; rồi các ngươi sẽ biết trong mỗi bên, ta hay Thượng Đế của Musa sẽ có hình phạt mạnh hơn?!');
INSERT INTO "vn_mokhtasar" VALUES(20,72,'20:72','Những thầy pháp sư trả lời Pha-ra-ông khi hắn hăm dọa: Chúng tôi sẽ không bao giờ yêu thích đi theo ngài hơn việc đi theo những bằng chứng quá rõ rệt cho chúng tôi. Chúng tôi không bao giờ yêu quý ngài hơn Allah, Đấng đã tạo hóa ra chúng tôi. Bởi thế, ngài cứ làm những gì ngài muốn đối với chúng tôi, nhưng chắc chắn một điều rằng ngài chỉ có thể định đoạt những gì ngài muốn trên cuộc sống trần gian ngắn ngủi này thôi và quyền định đoạt đó sẽ sớm tiêu vong.');
INSERT INTO "vn_mokhtasar" VALUES(20,73,'20:73','Những thầy pháp sư tiếp lời với Pha-ra-ông: Chúng tôi đã tin thực sự tin nơi Thượng Đế của chúng tôi, hy vọng Ngài sẽ xóa tội lỗi trước đây của chúng tôi và sẽ xóa tội lỗi của trò pháp thuật mà ngài đã bắt chúng tôi học và hoạt động nó và Musa đã giành lấy chiến thắng nó. Allah là Đấng Phúc Lành, Ngài sẽ ban thưởng những gì Ngài đã hứa và sẽ trừng phạt theo đúng những gì Ngài đã cảnh báo.');
INSERT INTO "vn_mokhtasar" VALUES(20,74,'20:74','Quả thật người nào trở về trình diện Thượng Đế (Allah) của y vào Ngày Phán Xét trong tình trạng là kẻ vô đức tin nơi Ngài thì y chắc chắn sẽ phải vào Hỏa Ngục, y sẽ ở trong đó đời đời và mãi mãi, y chết không xong và sống của không được.');
INSERT INTO "vn_mokhtasar" VALUES(20,75,'20:75','Và những ai trở về trình diện Thượng Đế (Allah) vào Ngày Phán Xét trong tình trạng có đức tin nơi Ngài đồng thời đã làm việc thiện tốt và ngoan đạo thì họ là những ngươi sẽ được ban cho những nơi ở trên cao với bậc cấp cao trọng và ưu hạng.');
INSERT INTO "vn_mokhtasar" VALUES(20,76,'20:76','Phần thưởng cấp bậc ưu hạng chính là những Ngôi Vườn Vĩnh cửu nơi Thiên Đàng, bên trong những Ngôi Vườn đó là những dòng sông chảy phía dươi các tòa lâu đài, họ sẽ sống trong đó mãi mãi. Đó là phần thưởng dành cho những ai tẩy sạch bản thân mình khỏi sự vô đức tin và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(20,77,'20:77','Quả thật, TA (Allah) đã phán bảo Musa: Ngươi hãy đưa các bề tôi của TA rời Ai Cập trong đêm để không ai trong bọn chúng (Pha-ra-ông và quân lính của hắn) hay biết và Ngươi hãy dùng chiếc gậy của Ngươi đánh xuống biển để tạo ra cho họ một con đường khô rao băng qua biển (Hồng Hải), rồi sẽ an toàn nên Ngươi chớ sợ Pha-ra-ông và quân lính của hắn đuổi kịp và Ngươi cũng chớ lo sợ bị chết chìm trong biển.');
INSERT INTO "vn_mokhtasar" VALUES(20,78,'20:78','Pha-ra-ông và quân lính của hắn đuổi theo Musa và những người theo Người trên con đường băng qua biển và tất cả chúng đều bị sóng biển bao phủ và nhấn chìm, Musa và những người theo Người đều được cứu thoát');
INSERT INTO "vn_mokhtasar" VALUES(20,79,'20:79','Pha-ra-ông đã dắt dân chúng của hắn lầm lạc bởi những điều mà hắn trang hoàng từ sự vô đức tin và lừa gạt họ bởi những điều ngụy tạo, và hắn đã không hướng dẫn ho đi đúng con đường được chỉ đạo.');
INSERT INTO "vn_mokhtasar" VALUES(20,80,'20:80','TA (Allah) đã phán với dân Isra-el sau khi TA đã cứu họ thoát khỏi sự hãm hại và truy sát của Pha-ra-ông và quân lính của hắn: Này hỡi dân chúng Isra-el! Quả thật TA đã cứu các ngươi khỏi kẻ thù của các ngươi, TA đã giao ước với các Ngươi qua việc nói chuyện trực tiếp với Musa ở phía bên bải của ngọn núi Attur (hay còn gọi núi Saina''), và TA đã ban xuống cho các ngươi từ hồng phúc của TA loại thức uống ngon ngọt giống như mật ong (được gọi là Alman) và một loại chim Salwa giống như loài chim cút có thịt rất ngon.');
INSERT INTO "vn_mokhtasar" VALUES(20,81,'20:81','TA (Allah) phán tiếp với dân chúng Isra-el: Các ngươi hãy dùng các thực phẩm tốt lành mà TA đã ban cấp cho các ngươi nhưng các ngươi đừng vượt quá giới hạn mà phạm đến những gì TA đã cấm các ngươi để rồi cơn tức giận của TA sẽ giáng lên các ngươi. Và người nào mà cơn tức giận của TA giáng xuống thì chắc chắn y sẽ bị hủy diệt và y chính là kẻ bất hạnh trên cuộc sống trần gian này và cả ở cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(20,82,'20:82','TA (Allah) tiếp tục phán với dân chúng Isra-el: Nhưng TA chắc chắn sẽ tha thứ cho những ai biết quay đầu ăn năn sám hối với TA trong đức tin nơi TA và làm việc thiện tốt rồi thẳng bước trên điều chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(20,83,'20:83','Allah phán với Musa: Này Musa, điều gì đã làm Ngươi vội vã đến đây bỏ lại người dân của Ngươi phía sau lưng?');
INSERT INTO "vn_mokhtasar" VALUES(20,84,'20:84','Musa thưa với Allah: Họ đang ở phía sau bề tôi, họ theo dấu chân của bề tôi rồi sẽ đến cùng với bề tôi thôi, việc bề tôi vội vã đến trước trình diện Ngài chỉ vì bề tôi muốn làm Ngài hài lòng.');
INSERT INTO "vn_mokhtasar" VALUES(20,85,'20:85','Allah phán với Musa: Quả thật, TA đã thử thách đám dân của Ngươi sau khi Ngươi ra đi bằng việc thờ phượng con bò tơ. Quả thật, tên Samimi đã kêu gọi họ đến với sự thờ phượng con bò tơ đó, hắn đã dẫn họ đi lạc.');
INSERT INTO "vn_mokhtasar" VALUES(20,86,'20:86','Thế là Musa trở về với người dân của Người trong tâm trạng tức giận và đáng tiếc cho hành động sai trái của họ - hành vi thờ phượng con bò tơ mà họ đã tự tay đúc ra. Musa nói với họ: Này hỡi dân chúng! Chẳng lẽ Allah đã không hứa với các ngươi về những điều tốt đẹp rằng Ngài sẽ ban xuống cho các ngươi Kinh Tawrah và thu nhận các ngươi vào Thiên Đàng ư? Chẳng lẽ do thời gian quá lâu làm các ngươi quên mất chăng? Hay là các ngươi thực sự muốn cơn tức giận của Thượng Đế các ngươi giáng xuống lên các ngươi bằng sự trừng phạt của Ngài nên các ngươi đã làm trái lệnh của Ta?');
INSERT INTO "vn_mokhtasar" VALUES(20,87,'20:87','Đám dân của Musa nói với Musa: Chúng tôi không bội ước với Người theo ý của chúng tôi mà chỉ là điều bất đắc dĩ mà thôi. Quả thật, chúng tôi bị bắt vác nặng nữ trang của dân chúng của Pha-ra-ông nên chúng tôi đã quẳng nó vào trong hố lửa theo lời đề xuất của Samiri.');
INSERT INTO "vn_mokhtasar" VALUES(20,88,'20:88','Samiri đã đúc ra một con bò con bằng vàng từ đống nữ trang của đám dân Isra-el, con bò chỉ có thân hình không có linh hồn (sự sống) và nó phát ra tiếng kêu giống như con bò rống. Những kẻ cố vấn, qua công việc của Samiri bảo: Đây tức con bò được đúc bằng vàng nay chính là thần linh của các người và nó cũng chính là thần linh của Musa, tuy nhiên, Musa đã quên mất thần linh này.');
INSERT INTO "vn_mokhtasar" VALUES(20,89,'20:89','Há những người này, những người mà họ đã cho rằng con bò được đúc bằng vàng kia là thần linh đáng được thờ phượng không nhận thấy rằng nó không biết nói cũng chẳng thể trả lời họ tiếng nào, nó không thể đẩy lùi những điều xấu cho họ và cũng không ban lại điều lợi cho họ hay sao?!');
INSERT INTO "vn_mokhtasar" VALUES(20,90,'20:90','Quả thật, Harun cũng đã bảo họ trước khi Musa trở về với họ: Con bò được đúc bằng vàng và phát ra tiếng rống chỉ là một sự thử thách cho các người mà thôi. Allah muốn phơi bày ai là người có đức tin thực thụ và ai là kẻ vô đức tin qua sự việc đó. Này hỡi người dân! Thượng Đế của các người là Đấng Rất Mực Nhân Từ, bởi thế các người hãy theo ta thờ phượng một mình Ngài và hãy nghe lệnh của ta mà bỏ đi việc thờ phượng ai (vật) khác ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(20,91,'20:91','Những kẻ cố vấn cho việc thờ phượng con bò nói: Chúng tôi cứ vẫn thờ phượng nó cho tới khi Musa quay lại với chúng tôi.');
INSERT INTO "vn_mokhtasar" VALUES(20,92,'20:92','Musa bảo người anh trai của mình Harun: Điều gì ngăn anh cản họ khi thấy họ đi lạc trong việc thờ phượng con bò được đúc thay vì Allah?');
INSERT INTO "vn_mokhtasar" VALUES(20,93,'20:93','Musa trách cứ Harun: Sao anh lại bỏ mặc họ không đi theo sự hướng dẫn của em? Sao anh lại làm trái lệnh của em khi em giao quyền trông coi họ cho anh?');
INSERT INTO "vn_mokhtasar" VALUES(20,94,'20:94','Khi Musa nắm râu và đầu của người anh trai - Harun và lôi đi và trách cứ sao lại làm như thế thì Harun phân trần: Em chớ nắm râu và tóc của anh, thật ra anh có lý do cho việc ở cùng với họ. Quả thật, anh đã sợ rằng nếu bỏ lại họ một mình thì họ sẽ chia rẽ nhau để rồi em sẽ nói anh là người chia rẽ giữa họ và anh đã không làm theo lời dặn của em.');
INSERT INTO "vn_mokhtasar" VALUES(20,95,'20:95','Musa nói với Samiri: Mục đích của ngươi là gì trong việc đúc ra con bò, mục tiêu nào bắt ngươi làm như thế?');
INSERT INTO "vn_mokhtasar" VALUES(20,96,'20:96','Samiri nói với Musa: Tôi nhìn thấy những gì mà họ không thấy, quả thật tôi đã thấy đại Thiên Thần Jibril trên một con ngựa, tôi đã hốt một nắm đất từ dấu chân ngựa của ngài và ném lên con bò đã được đúc bằng vàng nữ trang, lập tức thân con bò phát ra tiếng kêu như bò rống. Đó là do tâm trí tôi xúi giục tôi làm như vậy.');
INSERT INTO "vn_mokhtasar" VALUES(20,97,'20:97','Musa nói với Samiri: Ngươi hãy cút đi, và suốt cuộc đời này, ngươi phải chịu một hình phạt rằng ngươi sẽ nói với mọi người: đừng chạm đến tôi và tôi không chạm đến các người, tức ngươi sẽ sống đời sống bị ruồng bỏ. Và sau này ngươi sẽ phải đối mặt với sự hứa hẹn cho việc trừng phạt vào Ngày Phán Xét. Chắc chắn Allah sẽ không bao giờ thất hứa. Và bây giờ ngươi hãy nhìn bọn ta sẽ làm gì với con bò mà ngươi đã lấy nó làm thần linh thờ phượng ngoài Allah, bọn ta sẽ đốt nó thành tro và đem vãi xuống biển không để bất cứ dấu vết nào của nó còn sót lại.');
INSERT INTO "vn_mokhtasar" VALUES(20,98,'20:98','Quả thật, Đấng Thờ Phượng đích thực của các ngươi - hỡi nhân loại - chỉ một mình Allah mà thôi, Ngài là Đấng mà không có đấng thờ phượng nào khác ngoài Ngài, Ngài là Đấng với nguồn kiến thức bao la bao trùm tất cả mọi vật, mọi sự việc, không có bất cứ điều gì nằm ngoài kiến thức của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(20,99,'20:99','Này hỡi Thiên Sứ Muhammad! TA kể cho Ngươi về các thông tin của những vị Nabi và những cộng đồng trước đây giống như những gì TA (Allah) đã kể cho Ngươi nghe về thông tin của Musa và Pha-ra-ông và đám dân của họ, để làm Ngươi vui và an tâm. Quả thật, TA đã cho Ngươi Kinh Qur''an để nhắc nhở.');
INSERT INTO "vn_mokhtasar" VALUES(20,100,'20:100','Ai quay lưng với Kinh Qur''an được ban xuống cho Ngươi tức y không tin nơi Nó và không làm theo những gì trong Nó thì vào Ngày Phục Sinh y sẽ vác nặng tội lỗi và đáng bị trừng phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(20,101,'20:101','Họ sẽ phải ở trong sự trừng phạt mãi mãi, và thật xấu xa và tồi tệ cho những ai gánh nặng tội lỗi vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(20,102,'20:102','Vào Ngày mà vị Thiên Thần Israfil cầm chiếc sừng thổi tiếng còi lần thứ hai cho sự Phục Sinh, TA triệu tập những kẻ vô đức tin trong Ngày hôm đó, chúng trở nên xanh tái mặt vì mắt của chúng chứng kiến những sự kiện của Ngày hôm đó một cách sợ hãi.');
INSERT INTO "vn_mokhtasar" VALUES(20,103,'20:103','Chúng thì thầm to nhỏ với nhau với lời nói: các người chỉ ở lại trong cõi mộ sau cái chết mươi đêm thôi.');
INSERT INTO "vn_mokhtasar" VALUES(20,104,'20:104','TA (Allah) biết rõ những gì chúng xì xầm to nhỏ với nhau, không một điều gì nằm khỏi tầm kiểm soát của TA, người được cho là giỏi nhất trong xét đoán nói: các người chỉ ở trong đó có một ngày thôi.');
INSERT INTO "vn_mokhtasar" VALUES(20,105,'20:105','Hỡi Thiên Sứ Muhammad! Chúng hỏi Ngươi về tình trạng của những quả núi vào Ngày Phục Sinh, Ngươi hãy nói với bọn chúng: Thượng Đế của ta sẽ bứng chúng và làm cho no bay tứ tán, chẳng còn gì.');
INSERT INTO "vn_mokhtasar" VALUES(20,106,'20:106','Ngài (Allah) sẽ làm cho trái đất trống không và trở trụi, chẳng còn một thứ gì trên lưng nó cả.');
INSERT INTO "vn_mokhtasar" VALUES(20,107,'20:107','Rồi ngươi - hỡi người chứng kiến - sẽ thấy mặt đất sẽ bằng phẳng không có chỗ nào lồi hay lõm.');
INSERT INTO "vn_mokhtasar" VALUES(20,108,'20:108','Trong Ngày hôm đó, nhân loại sẽ đi theo tiếng hô gọi của vị Thiên Thần đến tập trung tại nơi đại triệu tập, họ tất nhiên phải đi theo sự hô gọi đó, và mọi tiếng nói đều tắt lịm trước Đấng Rất Mực Độ Lượng, bởi thế vào Ngày đó sẽ không còn nghe thấy bất cứ tiếng động nào ngoài những âm thanh thật khẽ.');
INSERT INTO "vn_mokhtasar" VALUES(20,109,'20:109','Trong Ngày Vĩ Đại đó, sự can thiệp của bất cứ ai cũng trở nên vô ích trừ những ai được Allah cho phép quyền can thiệp cầu xin ân xá và Ngài hài lòng với sự cầu xin của y.');
INSERT INTO "vn_mokhtasar" VALUES(20,110,'20:110','Allah biết những gì mà nhân loại sẽ đối mặt từ sự việc của Giờ Tận Thế và Ngài biết những gì đã xảy ra phía sau họ trên cuộc sống trần gian này nhưng nhân loại không hề biết rõ về Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(20,111,'20:111','Tất cả nhân loại sẽ gục mặt xuống trước Allah, Đấng Hằng Sống, Đấng Bất Diệt, Đấng Trông Coi và Chi Phối vạn vật. Quả thật, những ai mang trên người tội lỗi chắc chắn sẽ thua thiệt và thất bại thảm hại vì họ tự dấn thân vào ngõ diệt vong.');
INSERT INTO "vn_mokhtasar" VALUES(20,112,'20:112','Và ai làm việc thiện tốt và tin nơi Allah và các vị Thiên Sứ của Ngài thì y sẽ được ban thưởng một phần thưởng tốt đẹp và vĩ đại, ý sẽ không lo sợ bị phạt cho tội lỗi đã làm và cũng sẽ phải lo sợ bị lấy mất phần ân thưởng cho các việc làm thiện tốt.');
INSERT INTO "vn_mokhtasar" VALUES(20,113,'20:113','Tương tự với những gì mà TA (Allah) đã ban xuống từ những câu chuyện của những người thời trước, TA đã ban xuống Kinh Qur''an này bằng tiếng Ả Rập rõ ràng, trong đó, TA đã giảng giải kỹ lưỡng những lời cảnh báo về sự trừng phạt, hy vọng họ biết sợ Allah hoặc Qur''an sẽ làm sự nhắc nhở để họ lưu tâm mà nhớ về TA.');
INSERT INTO "vn_mokhtasar" VALUES(20,114,'20:114','Allah là Đấng Tối Cao, Thiêng Liêng và Quyền Năng, Ngài là Đức Vua có vương quyền với tất cả mọi thứ, Ngài là Thượng Đế đích thực, lời phán của Ngài là sự thật, Ngài tối cao và vĩ đại hơn những gì mà những kẻ thờ đa thần mô tả về Ngài. Này hỡi Thiên Sứ Muhammad! Ngươi chớ hấp tấp ráng đọc cho thuộc lòng Qur''an cùng với Jibril trước khi y kết thúc phần mặc khải cho Ngươi, mà Ngươi hãy cầu nguyện: Lạy Thượng Đế của bề tôi! Xin Ngài tăng thêm nguồn kiến thức cho bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(20,115,'20:115','Và quả thật, TA (Allah) đã dặn Adam trước đây rằng không được ăn trái từ một loại cây, TA đã cấm Y đến gần cái cây đó và TA đã trình bày cho Y rõ về hậu quả của việc ăn trái của cây đó, nhưng Y đã quên lời dặn của TA và đã ăn trái của cây cấm. Y đã không thể kiên nhẫn trước sự cám dỗ của cây cấm và TA không thấy ở Y có sự cương quyết giữ gìn lời dặn dò của TA.');
INSERT INTO "vn_mokhtasar" VALUES(20,116,'20:116','Hỡi Thiên Sứ Muhammad! Ngươi hãy nhớ lại lúc TA ra lệnh cho các Thiên Thần quì xuống phủ phục trước Adam với ý nghĩa chào đón thì tất cả các Thiên Thần đều quì xuống phủ phục trừ Iblis - một tên ở cùng với các Thiên Thần nhưng không cùng loài với họ - không chịu tuân lệnh, hắn từ chối quì xuống phủ phục trước Adam một cách ngạo mạn.');
INSERT INTO "vn_mokhtasar" VALUES(20,117,'20:117','TA đã phán với Adam: Này Adam! Quả thật Iblis là kẻ thù của Ngươi và vợ của Ngươi. Bởi thế, Ngươi chớ để cho Iblis dụ Ngươi và vợ của Ngươi khiến cả hai phải bị trục xuất khỏi Thiên Đàng để rồi Ngươi phải đối mặt vợi sự đau khổ.');
INSERT INTO "vn_mokhtasar" VALUES(20,118,'20:118','Allah phán với Adam rằng quả thật, trong Thiên Đàng, Ngài cung dưỡng nguồn thực phẩm dồi dào cho Người và vợ của Người, họ sẽ không bị đói; và Ngài cho họ y phục tốt đẹp, họ sẽ không trần truồng.');
INSERT INTO "vn_mokhtasar" VALUES(20,119,'20:119','Allah phán với Adam rằng trong Thiên Đàng Ngài sẽ cấp thức uống nên Người và vợ của Người sẽ không khát; và Ngài che mát cho họ nên họ sẽ không phải chịu sự nóng nực của ánh mặt trời.');
INSERT INTO "vn_mokhtasar" VALUES(20,120,'20:120','Thế rồi Shaytan đã dụ dỗ Adam, hắn thì thầm vào tai của Adam, hắn bảo: Này Adam, Ngươi có muốn ta dẫn Ngươi đến cái cây mà khi Ngươi ăn trái của nó Ngươi sẽ bất tử, Ngươi sẽ trường sinh mãi mãi và sẽ sở hữu một vương quốc vĩnh hằng không bao giờ tàn lụi?');
INSERT INTO "vn_mokhtasar" VALUES(20,121,'20:121','Thế là Adam và vợ của Người đã ăn trái của cây cấm. Sau khi ăn thì cả hai đều lộ hết phần kín đáo. Cả hai sau đó liền dùng lá cây của Thiên Đàng khâu lại để che phần kín đáo của mình. Nguyên nhân làm trái lệnh của Thượng Đế khi không chịu tránh xa cây cấm đó theo lệnh của Ngài nên Adam đã bị lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(20,122,'20:122','Thế rồi, Allah quay lại chấp nhận sự ăn năn sám hối của Adam, Ngài tha thứ cho Người, Ngài hướng dẫn Người và chọ Người là vị Nabi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(20,123,'20:123','Allah phán với Adam và Hauwa'': Hai ngươi hãy khi đi xuống khỏi Thiên Đàng, hai ngươi và cả Iblis nữa. Hắn sẽ là kẻ thù của hai ngươi và hai ngươi là kẻ thù của hắn. Nhưng nếu có nguồn Chỉ Đạo từ TA ban xuống, ai trong các ngươi đi theo nguồn Chỉ Đạo đó thì sẽ không lầm lạc khỏi điều chân lý và cũng sẽ không phải bất hạnh ở cõi Đời Sau, và sẽ được vào Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(20,124,'20:124','Và ai quay lưng với Lời Nhắc Nhở của TA và không chịu chấp nhận Nó thì y sẽ sống một đời sống chật vật trên thế gian và ở cõi mộ, và vào Ngày Phục Sinh y sẽ được cho sống lại trong trái thái mù lòa, không nhìn thấy gì cả.');
INSERT INTO "vn_mokhtasar" VALUES(20,125,'20:125','Người quay lưng với Bức Thông Điệp nhắc nhở sẽ thưa với Allah vào Ngày Phục Sinh: Lạy Thượng Đế của bề tôi! Sao Ngài lại dựng bề tôi sống lại trong tình trạng mùa lòa trong khi trên thế gian bề tôi là người nhìn thấy?!');
INSERT INTO "vn_mokhtasar" VALUES(20,126,'20:126','Allah sẽ phán đáp lại: Cũng giống như những gì ngươi đã làm trên thế gian. Quả thật, những Lời Mặc Khải của TA đã đến với ngươi nhưng ngươi đã quay lưng và rời bỏ nó, và giờ đây trong ngày hôm nay, tương tự, ngươi sẽ bị bỏ quên trong sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(20,127,'20:127','Tương tự, TA sẽ trừng phạt xứng đáng cho những ai phạm vào các điều cấm và quay lưng với đức tin khi đã có bằng chứng rõ ràng tự Thượng Đế. Và sự trừng phạt của Allah ở cõi Đời Sau sẽ khủng khiếp và khắc nghiệt hơn sự eo hẹp trên thế gian và trong cõi mộ.');
INSERT INTO "vn_mokhtasar" VALUES(20,128,'20:128','Chẳng phải những kẻ thờ đa thần đã được trình bày cho biết rằng có rất nhiều thế hệ trước đã bị TA (Allah) trừng phạt và họ đang bước đi trên các chỗ ở điêu tàn của những cộng đồng trước, những dấu tích đó không cho họ bài học hay sao? Quả thật, nơi các sự việc đó là bài học cho những người thông hiểu.');
INSERT INTO "vn_mokhtasar" VALUES(20,129,'20:129','Này hỡi Thiên Sứ Muhammad! Nếu như không phải vì Lời phán của TA đã được ban xuống rằng TA không trừng phạt bất kỳ một ai trước khi có đủ bằng chứng tố cáo thì chắc chắn TA đã trừng phạt họ.');
INSERT INTO "vn_mokhtasar" VALUES(20,130,'20:130','Này hỡi Thiên Sứ Muhammad! Ngươi hãy kiên nhẫn cho những lời mỉa mai của những kẻ vô đức tin. Ngươi hãy lo ca ngợi và tán dương Thượng Đế của Ngươi trong lễ nguyện Salah Fajr trước khi mặt trời mọc, trong lễ nguyện Salah Asr trước khi mặt trời lặn, trong lễ nguyện Salah Maghrib và ''I-sha'' trong đêm, và trong lễ nguyện Zhuhur lúc mặt trời đã nghiêng bóng, mong rằng Ngươi sẽ được phần thưởng nơi Allah làm cho Ngươi toại nguyện.');
INSERT INTO "vn_mokhtasar" VALUES(20,131,'20:131','Và Ngươi - Thiên Sứ Muhamamd - chớ đừng trố mắt nhìn những thứ ân huệ mà TA đã ban cho những kẻ vô đức tin hưởng thụ trong cuộc sống trần gian này. Thật ra những thứ đó là để TA dùng thử thách chúng. Phần thưởng mà TA hứa ban cho Ngươi ở cõi Đời Sau tốt hơn những thứ của chúng trên thế gian, tốt hơn và lâu dài hơn.');
INSERT INTO "vn_mokhtasar" VALUES(20,132,'20:132','Này hỡi Thiên Sứ Muhammad! Ngươi hãy kêu người nhà của Ngươi dâng lễ nguyện Salah và Ngươi hãy kiên nhận trong việc thực hiện nó. TA (Allah) không đòi hỏi Ngươi hay những ai ngoài Ngươi ban cấp bổng lộc cho TA mà chính TA ban cấp bổng lộc cho Ngươi. Và kết cục tốt đẹp và đáng khen ngợi ở thế gian và cuộc sống Đời Sau là dành cho những người sợ Allah, chấp hành theo mệnh lệnh của Ngài và tránh xa những điều Ngài ngăn cấm.');
INSERT INTO "vn_mokhtasar" VALUES(20,133,'20:133','Những kẻ vô đức tin nơi Thiên Sứ Muhammad nói: Sao Muhammad không mang đến cho chúng ta những bằng chứng chứng minh sự trung thực của y? Há những kẻ vô đức tin này không thấy Kinh Qur''an đã chứng nhận lại những điều nằm trong các Kinh Sách trước đây hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(20,134,'20:134','Và nếu TA (Allah) trừng phạt những kẻ vô đức tin này trước khi cử phái vị Sứ Giả đến cho chúng cũng như ban xuống cho chúng một Kinh Sách thì chắc chắn chúng sẽ nói vào Ngày Phán Xét biện minh cho sự vô đức tin của chúng: Lạy Thượng Đế của bầy tôi! Sao Ngài không gửi đến cho chúng tôi một vị Thiên Sứ trên thế gian để bề tôi tin tưởng nơi y và đi theo những gì y mang đến trước khi bầy tôi đối diện với sự trừng phạt của Ngài?');
INSERT INTO "vn_mokhtasar" VALUES(20,135,'20:135','Hỡi Thiên Sứ Muhammad! Ngươi hãy nói với những kẻ vô đức tin này: Mỗi người trong chúng ta đều chờ đợi sự phân xét của Allah. Bởi thế, các ngươi hãy chờ đợi đi, rồi đây các ngươi sẽ biết ai mới là người đi trên con đường ngay chính và ai mới là người được hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(21,1,'21:1','Sự xét xử để thưởng phạt nhân loại tương ứng với những hành động và việc làm của chúng vào Ngày Phục Sinh càng ngày càng gần hơn, tuy nhiên, chúng vẫn cứ thờ ơ quay lưng với nó vì sự bận rộn, lo toan cho cuộc sống thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(21,2,'21:2','Bất cứ điều nhắc nhở mới nào trong Kinh Qur''an mà Thượng Đế của họ ban xuống cho họ, họ đều nghe nó với sự bỡn cợt một cách vô ích, không hề nhập vào tâm trí của họ.');
INSERT INTO "vn_mokhtasar" VALUES(21,3,'21:3','Chúng nghe những điều nhắc nhở trong Qur''an bằng trái tim thờ ơ. Những kẻ vô đức tin với lời nhắc nhở của Qur''an đã giấu kín cuộc mật đàm của chúng, chúng bảo nhau: Chẳng phải kẻ tự xưng là Thiên Sứ cũng chỉ là một con người phàm tục giống như các ngươi đó sao, chẳng phải hắn chẳng có gì vượt trội hơn các ngươi hay sao?! Hắn chỉ mang đến điều yêu thuật, chẳng lẽ các ngươi chịu đi theo hắn trong lúc các ngươi biết rõ hắn là người phàm giống như các ngươi sao?!');
INSERT INTO "vn_mokhtasar" VALUES(21,4,'21:4','Thiên Sứ Muhammad صلى الله عليه وسلم nói: Thượng Đế của Ta biết rõ những gì các ngươi che giấu từ cuộc nói chuyện, bởi lẽ Ngài biết rõ từng lời nói được thốt ra từ những ai ở trên các tầng trời và những ai ở trái đất, Ngài nghe thấy tất cả lời nói của đám bề tôi của Ngài, Ngài thông toàn tất cả các hành động và việc làm của họ, và Ngài sẽ thưởng, phạt cho tất cả điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(21,5,'21:5','Chúng nghi ngờ về những gì mà Thiên Sứ Muhammad صلى الله عليه وسلم mang đến, cho nên, có lúc chúng bảo: toàn là những thứ mộng mị hỗn tạp chẳng có gì đáng để phân tích; có lúc chúng nói: không, hắn chỉ bịa đặt không hề có cơ sở và bằng chứng nào cả; và có lúc chúng lại nói: hắn là một nhà thơ, và nếu sứ mạng của hắn là thật thì chúng ta hãy bảo hắn mang đến một phép lạ giống như những phép lạ đã được ban cấp cho những vị Thiên Sứ trước đây, quả thật, các vị Thiên Sứ trước đều mang đến những phép lạ, chẳng hạn như chiếc gậy của Musa, con lạc đà cái của Saleh.');
INSERT INTO "vn_mokhtasar" VALUES(21,6,'21:6','Trước thời những kẻ yêu cầu những phép lạ này (những kẻ ngoại đạo của Makkah), có những thị trấn đã yêu cầu TA (Allah) ban xuống những phép lạ và khi TA ban xuống cho chúng giống như những gì chúng yêu cầu nhưng rồi chúng vẫn phủ nhận và vô đức tin nên TA đã hủy diệt chúng. Thế phải chăng những kẻ này sẽ tin ư?!');
INSERT INTO "vn_mokhtasar" VALUES(21,7,'21:7','Hỡi Thiên Sứ Muhammad صلى الله عليه وسلم, tất cả những vị Thiên Sứ, những vị Nabi trước Ngươi đều là những người đàn ông phàm tục được TA mặc khải cho họ Kinh Sách, TA không hề cử phái các Thiên Thần làm Sứ Giả đến với nhân loại. Bởi thế, các ngươi hãy hỏi những người am hiểu Kinh Sách thời trước các ngươi nếu các ngươi không biết.');
INSERT INTO "vn_mokhtasar" VALUES(21,8,'21:8','Các vị Thiên Sứ, các vị Nabi mà TA (Allah) cử phái đến không phải là những người mà TA làm cho họ có một cơ thể không ăn uống, họ vẫn ăn, vẫn uống như bao người phàm khác, và họ không phải là những người sống mãi không chết trên thế gian này.');
INSERT INTO "vn_mokhtasar" VALUES(21,9,'21:9','Rồi sau đó, TA (Allah) đã chứng thực với các vị Thiên Sứ cũng như các vị Nabi của TA về những gì mà TA đã hứa với họ. Đó là TA đã cứu họ và những ai TA muốn trong số những người có đức tin khỏi sự diệt vong, TA đã tiêu diệt những kẻ vượt quá giới hạn bởi sự vô đức tin của chúng nơi TA và bởi những tội lỗi mà chúng đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(21,10,'21:10','Quả thật, TA (Allah) đã ban xuống cho các ngươi Kinh Qur''an, trong đó chứa đựng nguồn Chỉ đạo và Nhắc nhở làm các ngươi trở nên cao quý và tự hào nếu các ngươi tín nơi Nó và làm đúng theo Nó. Chẳng lẽ các ngươi không hiểu điều đó hay sao?! Bởi thế, các ngươi hãy nhanh chân đến với đức tin nơi Nó và hãy làm theo những gì mà Nó chứa đựng!');
INSERT INTO "vn_mokhtasar" VALUES(21,11,'21:11','Đã có rất nhiều thị trấn bị TA (Allah) hủy diệt vì đã làm điều sai trái bởi sự vô đức tin, và TA đã tạo ra một nhóm người khác sau chúng.');
INSERT INTO "vn_mokhtasar" VALUES(21,12,'21:12','Những kẻ bị TA (Allah) tiêu diệt khi nhìn thấy hình phạt của TA giáng xuống thì hoảng hốt và sợ hãi nhanh chân bỏ chạy để thoát thân.');
INSERT INTO "vn_mokhtasar" VALUES(21,13,'21:13','Chúng được hô gọi với những lời mỉa mai: các ngươi chớ bỏ chạy, mà hãy trở lại với những thứ xa hoa mà các ngươi đã hưởng thụ, những ngôi nhà tiện nghi và êm ấm mà các ngươi đã ở; hy vọng rằng các ngươi sẽ bị chất vất một điều gì đó từ cuộc sống của các ngươi trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(21,14,'21:14','Những kẻ làm điều sai trái thừa nhận tội lỗi của mình trong lời than thở: thật khốn khổ, chúng tôi đã hoàn toàn thất bại và thua thiệt, chúng tôi đã làm điều quá sai quấy vì chúng tôi đã không tin nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(21,15,'21:15','Chúng cứ vẫn khóc than cho tội lỗi đã làm và kêu la cho số phận của chúng mãi đến lúc TA (Allah) biến chúng trở thành như một cánh đồng sau mùa gặt, chúng chết la liệt nằm bất động.');
INSERT INTO "vn_mokhtasar" VALUES(21,16,'21:16','TA (Allah) đã không tạo hóa các tầng trời, trái đất và vạn vật để làm trò tiêu khiển vô ích mà TA tạo hóa chúng ra là để chứng tỏ quyền năng của TA.');
INSERT INTO "vn_mokhtasar" VALUES(21,17,'21:17','Nếu TA (Allah) thực sự muốn một thứ gì đó làm trò tiêu khiển cho riêng TA thì quả thật TA đã lấy nó từ chính nơi TA, nhưng TA đã không làm điều đó vì TA không muốn.');
INSERT INTO "vn_mokhtasar" VALUES(21,18,'21:18','Không, TA (Allah) đã ném chân lý mà TA mặc khải cho vị Thiên Sứ của TA vào sự giả dối và ngụy tạo của những kẻ vô đức tin, chân lý đánh mạnh vào sự giá dối và ngụy tạo của chúng làm cho nó tiêu tan. Và hỡi những kẻ nói rằng TA có bạn đời và có con cái, các ngươi sẽ bị hủy diệt cho việc các ngươi đã qui những điều không đúng về TA cho TA.');
INSERT INTO "vn_mokhtasar" VALUES(21,19,'21:19','Allah nắm quyền chi phối và thống trị toàn bộ vũ trụ, duy chỉ Ngài là Đấng Chủ Tể của các tầng trời và trái đất. Và các Thiên Thần ở nơi Ngài không hề tự cao tự đại trong việc thờ phượng và phục tùng mệnh lệnh của Ngài, họ luôn hạ mình tuân lệnh và phục tùng Ngài và họ không hề mệt mỏi trong việc thờ phượng và phục tùng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(21,20,'21:20','Các Thiên Thần vẫn luôn ca tụng và tán dương Allah cả ngày lẫn đêm, họ không chán ngán, mệt mỏi về việc làm đó.');
INSERT INTO "vn_mokhtasar" VALUES(21,21,'21:21','Phải chăng những kẻ thờ đa thần đã nhận lấy các tạo vật làm thần linh ngoài Allah để thờ phượng trong khi các tạo vật đó không có năng lực làm sống lại người chết? Sao chúng lại thờ phượng một thứ yếu ớt vô năng và bất lực như thế chứ?!');
INSERT INTO "vn_mokhtasar" VALUES(21,22,'21:22','Nếu trong các tầng trời và trái đất thực sự có những thần linh khác nhau cùng cai quản và chi phối chúng thì các tầng trời và trái đất sẽ đi đến sự hủy diệt vì sự bất đồng của các thần linh. Nhưng thực tế hoàn toàn ngược lại với điều đó. Thật vinh quang và trong sạch thay cho Allah - Thượng Đế của chiếc Ngai Vương Chí Đại - về những điều mà những kẻ thờ đa thần đã qui cho Ngài những kẻ đối tác ngang vai!');
INSERT INTO "vn_mokhtasar" VALUES(21,23,'21:23','Allah là Đấng Duy Nhất nắm quyền chi phối và định đoạt tất cả mọi thứ, Ngài sẽ không bị ai tra hỏi về công việc của Ngài, chẳng ai có quyền và khả năng làm điều đó với Ngài, ngược lại, chính Ngài mới có quyền tra hỏi và chất vấn các bề tôi của Ngài về những việc họ đã làm, và Ngài sẽ thưởng hoặc phạt họ.');
INSERT INTO "vn_mokhtasar" VALUES(21,24,'21:24','Không, chúng đã dựng lên những thần linh khác ngoài Allah để thờ phượng. Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần này: "Các ngươi hãy mang đến bằng chứng chứng minh rằng việc các ngươi thờ những thần linh đó là chân lý. Đây, trong Kinh Sách này (Qur''an) được ban xuống cho ta và những Kinh Sách được ban cho các vị Thiên Sứ trước ta không hề có một bằng chứng nào cho các ngươi cả về điều đó cả." Không, đa số những kẻ thờ đa thần thường thiếu hiểu biết, chúng chỉ thường bắt chước một cách mù quáng, bởi thế, chúng hay quay lưng với điều chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(21,25,'21:25','Hỡi Thiên Sứ Muhammad, quả thật tất cả các vị Thiên Sứ mà TA cử phái đến trước Ngươi đều được TA mặc khải: Không có Thượng Đế nào xứng đáng được thờ phượng ngoài TA, bởi thế hãy thờ phượng một mình TA, chớ tổ hợp với TA bất cứ thứ gì.');
INSERT INTO "vn_mokhtasar" VALUES(21,26,'21:26','Những kẻ thờ đa thần nói: "Các Thiên Thần là những đứa con gái của Allah." Thật vinh quang và trong sạch cho Ngài trước những điều chúng qui không đúng cho Ngài. Các Thiên Thần đều chỉ là các bề tôi vinh dự và cận kề của Ngài mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(21,27,'21:27','Các Thiên Thần của Allah không bao giờ qua mặt Ngài bởi bất cứ lời nói nào, họ không lên tiếng trừ phi Ngài ra lệnh, họ tuyệt đối chấp hành và thực thi mọi mệnh lệnh của Ngài, họ không bao giờ trái lệnh Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(21,28,'21:28','Allah biết rõ những gì xảy ra trước khi các Thiên Thần hành động và những gì xảy ra sau đó. Các Thiên Thần, không ai trong số họ dám can thiệp xin giùm cho bất kỳ ai ngoại trừ Ngài cho phép đối với người nào mà Ngài ưng thuận. Các Thiên Thần luôn hết mực sợ Allah và không bao giờ dám làm trái lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(21,29,'21:29','Bất kỳ ai trong số các Thiên Thần dám tự xưng mình là thần linh ngoài Allah thì TA (Allah) sẽ trừng phạt y bởi câu nói sai trái đó bằng hình phạt trong Hỏa Ngục vào Ngày Phán Xét, y sẽ ở trong đó mãi mãi. Và giống như vậy, TA cũng sẽ trừng phạt những kẻ tội lỗi và sai quấy trong loài người bởi sự vô đức tin và tổ hợp của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(21,30,'21:30','Há những kẻ vô đức tin nơi Allah không biết rằng các tầng trời và trái đất từng là một khối thống nhất trước khi TA (Allah) tách chúng ra làm hai hay sao? Há chúng không biết rằng TA đã ban nước mưa từ trên trời xuống trái đất để tạo ra mọi thứ từ động vật đến thực vật? Sao chúng không chiêm nghiệm và suy ngẫm về điều đó, sao chúng lại không tin nơi một mình Allah cơ chứ?!');
INSERT INTO "vn_mokhtasar" VALUES(21,31,'21:31','Và TA (Allah) đã tao ra trên trái đất những quả núi vững chắc và cố định để mặt đất không bị rung chuyển cùng với chúng (con người) và TA đã làm ra trên mặt đất những con đường rộng rãi để đi lại dễ dàng mong rằng chúng được hướng dẫn đến mục tiêu chúng muốn hướng đến.');
INSERT INTO "vn_mokhtasar" VALUES(21,32,'21:32','Và TA (Allah) đã tạo bầu trời giống như một cái mái che không có trụ chống đỡ và nó được canh giữ khỏi những cuộc nghe lén. Tuy nhiên, những kẻ thờ đa thần đã quay lưng với những dấu hiệu của nó (bầu trời) như mặt trời, mặt trăng.');
INSERT INTO "vn_mokhtasar" VALUES(21,33,'21:33','Allah là Đấng duy nhất đã tạo ra ban đêm để nghỉ ngơi, tạo ra ban ngày để tìm kiếm kế sinh nhai, tạo ra mặt trời làm dấu hiệu cho ban ngày, tạo ra mặt trăng làm dấu hiệu cho ban đêm; tất cả mặt trời, mặt trăng đều di chuyển trong quỹ đạo của riêng nó, không cái nào lệch khỏi quỹ đạo của nó dù chỉ là một chút ít.');
INSERT INTO "vn_mokhtasar" VALUES(21,34,'21:34','Này Thiên Sứ Muhammad, TA đã không làm cho bất cứ ai trước Ngươi sống mãi mà không chết trên thế giàn này. Há nếu như Ngươi chấm dứt tuổi đời của mình và chết đi thì những kẻ này sẽ sống mãi không chết ư? Điều đó không bào giờ.');
INSERT INTO "vn_mokhtasar" VALUES(21,35,'21:35','Tất cả mỗi linh hồn dù là có đức tin hay không có đức tin thì cũng đều nếm lấy mùi vị của cái chết trên thế gian này. Hỡi nhân loại, TA (Allah) thử thách các ngươi trên cuộc sống trần gian này bởi những nghĩa vụ, những hồng phúc và những điều không tốt lành, rồi sau khi các ngươi chết đi thì các Ngươi sẽ được đưa trở về trình diện TA để TA xét xử và thưởng phạt cho những việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(21,36,'21:36','Hỡi Thiên Sứ Muhammad, khi những kẻ thờ đa thần này nhìn thấy Ngươi thì chúng chỉ muốn giễu cợt với Ngươi, chúng thường nói lời mỉa mai: đây phải chăng là kẻ đã xúc phạm đến các thần linh mà các người đang thờ phượng? Cùng với sự chế giễu Ngươi, chúng đồng thời chối bỏ những gì mà Allah ban xuống cho chúng từ Qur''an và chúng bội ân trước những ân huệ mà Ngài đã ban cho chúng. Cho nên, chúng mới là những kẻ đáng bị chế giễu vì chúng tập hợp mọi điều xấu ở nơi chúng.');
INSERT INTO "vn_mokhtasar" VALUES(21,37,'21:37','Con người vốn được tạo hóa với bản tính nôn nóng và hấp tấp, con người thường vội vàng và gấp gáp muốn sự việc được hình thành trước khi nó xảy ra. Chẳng hạn như những kẻ thờ đa thần hay gấp gáp và nôn nóng sự trừng phạt xảy đến với chúng. Rồi đây, TA (Allah) sẽ cho các ngươi thấy hỡi những kẻ nôn nóng mong mỏi sự trừng phạt của TA, bởi thế, các ngươi không cần giục và hối TA làm gì.');
INSERT INTO "vn_mokhtasar" VALUES(21,38,'21:38','Những kẻ vô đức tin phủ nhận việc Phục Sinh nói một cách hối thúc: Khi nào bọn ta sẽ đối diện với điều mà bọn ta đã được hứa về sự trừng phạt qua lời nói của các ngươi hỡi những người Muslim nếu các ngươi nói thật?');
INSERT INTO "vn_mokhtasar" VALUES(21,39,'21:39','Nếu những kẻ vô đức tin này, những kẻ mà họ đã phủ nhận sự Phục Sinh biết được thời khắc mà họ sẽ không thể tránh khỏi lửa của Hỏa Ngục táp vào mặt và lưng của họ cũng như sẽ không có ai giúp đỡ thì chắc chắn họ sẽ không hối thúc sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(21,40,'21:40','Hỏa Ngục sẽ không đến trừng phạt họ theo cách để họ biết và chuẩn bị tinh thần đối mặt với Nó mà Nó đến với họ một cách bất ngờ để họ không kịp trở tay, và họ sẽ không thể trì hoãn thêm một thời khắc nào một khi Nó đến.');
INSERT INTO "vn_mokhtasar" VALUES(21,41,'21:41','Nếu người dân của Ngươi - hỡi Muhammad - chế giễu và nhạo báng Ngươi thì sự việc đó không phải là điều mới mẻ, bởi quả thật các vị Thiên Sứ trước Ngươi cũng đã bị chế giễu và nhạo báng. Tuy nhiên, những điều mà những kẻ vô đức tin đã chế nhạo Ngươi trên thế gian này sẽ bao quanh lấy chúng bằng sự trừng phạt ở cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(21,42,'21:42','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ hối thúc sự trừng phạt đó: ai sẽ bảo vệ các ngươi an toàn cả đêm lẫn ngày khỏi sự trừng phạt và hủy diệt của Allah, Đấng Rất Mực Độ Lượng khi Ngài muốn giáng xuống các ngươi? Không, những kẻ đó thường thờ ơ với lời nhắc nhở từ Thượng Đế của chúng, chúng cứ quay lưng bỏ đi không chịu suy ngẫm bất cứ điều gì một cách ngu muội.');
INSERT INTO "vn_mokhtasar" VALUES(21,43,'21:43','Phải chăng chúng có những thần lình khác ngoài Allah phù hộ và bảo vệ chúng khỏi sự trừng phạt của Ngài?! Những thần lình đó, bản thân chúng cũng không thể giúp được mình trong việc xua đuổi điều hại và mang lại điều tốt, nếu những ai không có khả năng giúp chính mình thì làm sao có khả năng giúp người khác?! Và chắc chắn chúng không thể nào tránh được sự trừng phạt của Ngài - Allah.');
INSERT INTO "vn_mokhtasar" VALUES(21,44,'21:44','Không, TA (Allah) để mặc những kẻ vô đức tin này và cha mẹ của chúng hưởng thụ khoái lạc với những ân huệ mà TA đã ban cho chúng; TA đã thêm cho chúng tuổi thọ để chúng cứ sa lầy vào sự vô đức tin của chúng. Há những kẻ đang hưởng thụ các ân huệ của TA và thôi thúc sự trừng phạt của TA mau đến với chúng không thấy việc TA đã đến tận đất đai của chúng và thu hẹp biên giới của nó lại ư? Những kẻ này sẽ không bao giờ giành được chiến thắng mà thực chất chúng là những kẻ thất bại.');
INSERT INTO "vn_mokhtasar" VALUES(21,45,'21:45','Hãy nói đi Thiên Sứ Muhammad: Hỡi nhân loại, quả thật ta chỉ là người cảnh báo các ngươi về sự trừng phạt của Allah qua những lời Mặc Khải được ban xuống từ nơi Ngài, và người điếc không nghe được tiếng gọi đến với chân lý để chấp nhận nó cho dù y có được cảnh báo về sự trừng phạt của Allah thế nào.');
INSERT INTO "vn_mokhtasar" VALUES(21,46,'21:46','Hỡi Thiên Sứ Muhammad, nếu có một phần nào đó từ sự trừng phạt của Thượng Đế của Ngươi chạm đến những kẻ nôn nóng này thì chắc chắn lúc đó chúng sẽ than: Ôi thật khổ cho chúng tôi, quả thật chúng tôi là những kẻ sai quấy vì đã tổ hợp thần linh khác với Allah và phủ nhận những gì Muhammad صلى الله عليه وسلم mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(21,47,'21:47','Và vào Ngày Phán Xét, TA (Allah) sẽ thiết lập những chiếc cân công lý để cân đo các việc làm của tất cả cư dân của Ngày Hôm Đó. Vào Ngày Hôm Đó, sẽ không có một linh hồn con người nào bị đối xử bất công, việc làm công đức, ân phước của họ sẽ không bị thiếu hụt và tội lỗi của họ cũng không bị tăng thêm, mọi sự việc đều được TA mang ra cân đo dù đó chỉ bằng hạt cải; và một mình TA đã đủ thanh toán và phán xét tất cả các việc làm của đám bề tôi của TA.');
INSERT INTO "vn_mokhtasar" VALUES(21,48,'21:48','Quả thật, TA (Allah) đã ban cho Musa và Harun Kinh Tawrah (Cựu Ước) để làm tiêu chuẩn phân biệt đúng sai, phân biệt điều chân lý và điều ngụy tạo, phân biệt điều Halal (được phép) và Haram (không được phép); và làm nguồn Chỉ Đạo cho những người có đức tin nơi Nó cũng như làm lời nhắc nhở cho những ai ngoan đạo, ngay chính và kính sợ Thượng Đế của họ.');
INSERT INTO "vn_mokhtasar" VALUES(21,49,'21:49','(Những người ngoan đạo, ngay chính) là những người sợ sự trừng phạt của Thượng Đế của họ, Đấng mà họ tin nơi Ngài mặc dù họ không nhìn thấy Ngài, và đối với giờ khắc Tận Thế thì họ khiếp đảm và sợ hãi.');
INSERT INTO "vn_mokhtasar" VALUES(21,50,'21:50','Đây là Kinh Qur''an được ban xuống cho Muhammad صلى الله عليه وسلم, được làm nguồn nhắc nhở cho những ai muốn có được những lời khuyên bảo và răn dạy, Nó mang lại nhiều phúc lành và tốt đẹp; vậy với những điều tốt đẹp như thế các ngươi lại từ chối hay sao, chẳng lẽ các ngươi không chịu thừa nhận và làm theo những điều tốt đẹp chứa đựng trong Nó ư?!');
INSERT INTO "vn_mokhtasar" VALUES(21,51,'21:51','Quả thật, TA (Allah) đã ban cho Nabi Ibrahim lý lẽ để tranh luận đúng sai với cộng đồng người dân của Y lúc Y hãy còn nhỏ và TA biết rõ về Y. TA đã ban cho Y từ kiến thức TA để Y có đủ lý lẽ trình bày cho người dân của Y.');
INSERT INTO "vn_mokhtasar" VALUES(21,52,'21:52','Hãy nhớ lại khi Ibrahim nói với cha Aazar và người dân của mình: những bục tượng mà các người đã làm ra từ chính đôi tay của các người và các ngươi thờ cúng chúng là gì?');
INSERT INTO "vn_mokhtasar" VALUES(21,53,'21:53','Người dân của Ibrahim nói với Người: bọn ta thấy cha mẹ ông bà xưa nay đã thờ cúng chúng nên bọn ta làm theo nền tảng truyền thống của họ.');
INSERT INTO "vn_mokhtasar" VALUES(21,54,'21:54','Nabi Ibrahim nói với người dân của mình: Này hỡi những người đi theo đường lối truyền thống của cha mẹ ông bà, quả thật các người và cha mẹ ông bà của các ngươi rõ ràng đang đi lạc khỏi con đường chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(21,55,'21:55','Người dân của Ibrahim nói với Người: Những gì ngươi đã nói có phải là những lời thật hay chỉ là những lời nói đùa vui thôi?');
INSERT INTO "vn_mokhtasar" VALUES(21,56,'21:56','Nabi Ibrahim nói với người dân của mình: Không, những lời tôi nói với các người là thật chứ không phải lời vui đùa. Thượng Đế của các người chính là Thượng Đế của các tầng trời và trái đất, Đấng đã tạo hóa ra chúng từ hư không, và tôi là một trong những người làm chứng khẳng định rằng Ngài là Thượng Đế của các người và là Thượng Đế của các tầng trời và trái đất; còn những bục tượng của các người không hề có phần trong quyền năng về sự việc đó.');
INSERT INTO "vn_mokhtasar" VALUES(21,57,'21:57','Ibrahim nói thầm không để người dân nghe thấy lời nói của mình: Thề bởi Allah, chắc chắn tôi sẽ lên kế hoạch đập phá các bục tượng của các người sau khi các người đi lễ hội của các người.');
INSERT INTO "vn_mokhtasar" VALUES(21,58,'21:58','Vậy theo kế hoạch, Nabi Ibrahim đã đập vỡ tất cả các bức tượng và chỉ chừa lại một bức tượng lớn nhất với hy vọng người dân của mình quay trở lại có thể hỏi nó về kẻ đã đập phá các bức tượng kia.');
INSERT INTO "vn_mokhtasar" VALUES(21,59,'21:59','Khi người dân của Ibrahim quay trở lại, họ thấy các bức tượng đã bị đập vỡ nát, họ bảo nhau: Kẻ nào đã dám làm điều này với các thần linh của chúng ta? Kẻ đã làm điều này chắc chắn là một tên vô cùng sai quấy bởi vì đáng lẽ ra hắn phải tôn vinh và kính trọng các thần linh của chúng ta.');
INSERT INTO "vn_mokhtasar" VALUES(21,60,'21:60','Một số người trong bọn họ nói: Chúng tôi có nghe một cậu thiếu niên tên Ibrahim đã nói với lời lẽ bất kính với các thần linh của chúng ta, có lẽ thủ phạm cho sự việc này chính là nó.');
INSERT INTO "vn_mokhtasar" VALUES(21,61,'21:61','Người đứng đầu của họ bảo: Thế thì các người hãy bắt Ibrahim đến để tra hỏi nó trước mặt dân chúng và các bá quan, phải bắt nó thừa nhận trước mặt mọi người về hành động sai trái của nó.');
INSERT INTO "vn_mokhtasar" VALUES(21,62,'21:62','Vậy là họ đã bắt Ibrahim đến và tra hỏi Người: Có phải chính ngươi đã làm điều tồi tệ này với các thần linh của bọn ta, đúng không Ibrahim?!');
INSERT INTO "vn_mokhtasar" VALUES(21,63,'21:63','Ibrahim nói với họ với ý muốn cho họ thấy rằng các bục tượng chẳng hề có một chút khả năng nào: Tôi không làm mà chính cái bực tượng lớn kia đã làm, không tin các người hãy hỏi các bục tượng của các người xem nếu như chúng biết nói chuyện.');
INSERT INTO "vn_mokhtasar" VALUES(21,64,'21:64','Họ quay lại nhìn nhau trong suy ngẫm và thừa nhận trong thâm tâm của họ rằng chính họ là những kẻ làm điều sai quấy vì đã tôn thờ những thứ ngoài Allah, những bục tượng mà nó chẳng hề mang lợi cũng như không gây hại được ai.');
INSERT INTO "vn_mokhtasar" VALUES(21,65,'21:65','Nhưng rồi họ vẫn ngoan cố, họ bảo: Này Ibrahim, ngươi thừa biết chắc chắn rằng những cái tượng này không thể nói chuyện sao ngươi lại bảo bọn ta hỏi chúng chứ? Họ tưởng rằng đây là lý lẽ biện hộ cho họ nhưng đó là lý lẽ tố cáo cho sự sai trái của họ.');
INSERT INTO "vn_mokhtasar" VALUES(21,66,'21:66','Ibrahim nói với họ để phản bác cho việc làm sai trái của họ: Vậy cớ làm sao các người lại thờ cúng ngoài Allah những thứ chẳng mang lại lợi ích gì cho các người và cũng chẳng làm hại được các người, chúng thậm chí còn không lo được cho bản thân chúng?!');
INSERT INTO "vn_mokhtasar" VALUES(21,67,'21:67','Thật xấu hổ cho việc làm của các người và cũng thật xấu hổ cho những thứ mà các người thờ cúng ngoài Allah từ các bục tượng vô tri vô giác thế này, chúng chẳng mang lợi cũng chẳng gây hại, chẳng lẽ các người không suy nghĩ và hiểu được sự việc đó sao, chẳng lẽ các người không hiểu là các người phải từ bỏ việc thờ cúng chúng hay sao?!');
INSERT INTO "vn_mokhtasar" VALUES(21,68,'21:68','Khi họ đuối lý với Ibrahim thì họ dùng đến sức mạnh quyền thế của họ, họ bảo: chúng ta hãy thiêu sống Ibrahim, hãy giúp các thần linh của chúng ta đòi lại điều mà nó đã xúc phạm và đập phá họ, nếu các người thực sự muốn đòi lại thanh danh cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(21,69,'21:69','Thế là họ đốt lửa và ném Nabi Ibrahim vào trong đó, Allah phán bảo lửa: Này lửa, ngươi hãy nguội mát và làm cho Ibrahim an toàn! Và lửa thực sự đã như thế, nó đã không làm hại Ibrahim.');
INSERT INTO "vn_mokhtasar" VALUES(21,70,'21:70','Người dân của Ibrahim đã mưu toan muốn hãm hại Y qua việc thiêu đốt Y nhưng TA (Allah) đã làm vỡ âm mưu của họ và làm cho họ thất bại.');
INSERT INTO "vn_mokhtasar" VALUES(21,71,'21:71','TA (Allah) đã cứu Ibrahim và Lut, TA đã đưa hai người họ đến vùng đất Sham, nơi mà TA đã ban phúc qua việc dựng lên nhiều vị Nabi trong đó và ban cho nơi đó nhiều thứ tốt đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(21,72,'21:72','TA (Allah) đã ban cho Ibrahim đứa con trai tên Is-haq khi Y cầu xin TA ban cho Y một đứa con trai, và TA đã ban thêm cho Y một đứa cháu nội tên Ya''qub. Tất cả, Ibrahim, Is-haq và Ya''qub đều được làm cho trở thành những người ngoan đạo, kính sợ Allah.');
INSERT INTO "vn_mokhtasar" VALUES(21,73,'21:73','TA (Allah) đã làm cho Ibrahim và con cháu của Y thành những vị Imam dẫn dắt nhân loại đến với điều tốt đẹp, kêu gọi nhân loại đến với sự thờ phượng một mình Allah dưới sự cho phép của Ngài, và TA đã sắc lệnh bảo họ phải làm điều thiện tốt, dâng lễ nguyện Salah một cách chu đáo, đóng Zakah, và chỉ thờ phượng một mình TA.');
INSERT INTO "vn_mokhtasar" VALUES(21,74,'21:74','Và TA (Allah) đã ban cho Lut (Lot) khả năng phân xử và phát xét giữa những người tranh chấp, TA cũng đã ban cho Y kiến thức về tôn giáo, TA đã cứu Y khỏi sự trừng phạt mà TA đã giáng xuống cho đám dân của Y (Sodom), đám dân mà chúng đã làm điều ô uế (quan hệ đồng tính nam), và chúng là một đám dân bất tuân Thượng Đế của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(21,75,'21:75','TA (Allah) đã thương xót Y (Lut) trong việc cứu Y khỏi hình phạt mà TA đã trừng phạt đám dân của Y bởi vì Y là một người ngoan đạo, luôn chấp hành và thực thi các mệnh lệnh của TA và tránh xa những điều TA nghiêm cấm.');
INSERT INTO "vn_mokhtasar" VALUES(21,76,'21:76','Hỡi Thiên Sứ Muhammad! Ngươi hãy nhớ lại câu chuyện của Nuh khi Y cầu xin Allah trước Ibrahim và Lut, TA đã đáp lại điều nguyện cầu của Y và TA đã cứu Y cùng với những người có đức tin trong đại nạn đó.');
INSERT INTO "vn_mokhtasar" VALUES(21,77,'21:77','Và TA đã cứu Y (Nuh) thoát khỏi đám dân đã phủ nhận những gì mà TA đã ban cho Y từ các dấu hiệu chứng minh sự chân thật của Y. Quả thật, đám dân đó của Y là một đám dân xấu xa và tội lỗi. Thế nên TA đã nhấn chìm tất cả bọn chúng bằng một trận đại hồng thủy.');
INSERT INTO "vn_mokhtasar" VALUES(21,78,'21:78','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại câu chuyện của của Dawood (David) và con trai của y Sulayman (Solomon) lúc cả hai người họ xét xử một vụ tranh chấp về việc một đàn cừu đã đột nhập vào một đám rẫy lúc ban đêm phá hoại cây trồng. Và TA (Allah) đã chứng kiến Dawood và Sulayman phân xử vụ việc đó một cách đúng đắn không sai trái.');
INSERT INTO "vn_mokhtasar" VALUES(21,79,'21:79','TA (Allah) đã để Sulayman hiểu vấn đề một cách triệt để hơn cha của Y Dawood mặc dù tất cả hai cha con họ đều được TA ban cho kiến thức giáo lý và sự khôn ngoan, sáng suốt trong phán đoán và suy luận các vấn đề. Và TA đã làm cho núi non và chim chóc cùng tán dương với Dawood khi Y tán dương TA. Tất cả những sự việc đó đều do chính Ta làm ra tất cả.');
INSERT INTO "vn_mokhtasar" VALUES(21,80,'21:80','Và TA (Allah) đã dạy Dawood hơn Sulayman ở việc Y biết làm ra những chiếc áo giáp để bảo vệ cơ thể của các ngươi khi các ngươi giao tranh. Thế hỡi nhân loại, các ngươi có biết ơn TA chăng về ân huệ này, ân huệ mà TA đã ban cho các ngươi?');
INSERT INTO "vn_mokhtasar" VALUES(21,81,'21:81','Và TA đã ban cho Sulayman khả năng điều khiển những cơn gió mạnh di chuyển theo lệnh của Y đến vùng đất Sham nơi mà TA ban phúc qua việc dựng lên trong đó các vị Nabi và trải ra trong đó nhiều điều tốt lành. Và TA hằng biết hết tất cả mọi thứ, mọi sự việc, không có bất cứ thứ gì che giấu được TA.');
INSERT INTO "vn_mokhtasar" VALUES(21,82,'21:82','Trong đám Shaytan (loài Jinn), TA (Allah) đã làm cho một số có khả năng lặn xuống các biển để lấy ngọc trai và những thứ khác cho Sulayman, và ngoài việc lặn đó còn có những việc làm khác nữa như xây cất. Và TA (Allah) luôn Trông Coi các việc làm của chúng, không có điều gì có thể nằm ngoài tầm kiểm soát của TA.');
INSERT INTO "vn_mokhtasar" VALUES(21,83,'21:83','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại câu chuyện của Ayyub (Job) khi Y cầu xin Thượng Đế của Y lúc gặp phải tai họa, Y nói: Ôi Thượng Đế của bề tôi! Quả thật bề tôi đã mắc bệnh và mất hết con cái, và Ngài là Đấng Nhân Từ có lòng thương xót nhất trong tất cả những ai có lòng thương xót và nhân từ, xin Ngài hãy xua tan khỏi bề tôi những tai họa mà bề tôi đang gặp phải.');
INSERT INTO "vn_mokhtasar" VALUES(21,84,'21:84','Thế là TA (Allah) đã chấp nhận sự cầu nguyện của Y (Ayyub), TA đã xua tan những nỗi thống khổ mà Y đã chịu đựng từ bao lây nay, TA ban lại cho Y những gì mà Y đã mất từ gia đình và con cái, TA đã ban cho Y những ân huệ tương tự. Tất cả những điều đó là do lòng thương xót từ nơi TA và là sự nhắc nhở cho tất cả những ai thờ phượng TA để họ biết kiên nhẫn chịu đựng giống như Ayyub đã kiên nhẫn chịu đựng.');
INSERT INTO "vn_mokhtasar" VALUES(21,85,'21:85','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại rằng Isma''il, Idris và Zdul-Kifli đều là những người biết kiên nhẫn chịu đựng trên những tai họa và nạn kiếp, kiên nhẫn trên những điều Allah ủy thác và giao phó.');
INSERT INTO "vn_mokhtasar" VALUES(21,86,'21:86','Và TA (Allah) đã thương xót họ nên đã ban cho họ thành những vị Nabi và thu nhận họ vào Thiên Đàng của TA, quả thật, họ là những người bề tôi ngoan đạo của Allah, những người mà luôn tuân thủ mệnh lệnh Thượng Đế của họ, họ ngoan đạo trong những sự việc thầm kín và công khai.');
INSERT INTO "vn_mokhtasar" VALUES(21,87,'21:87','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại câu chuyện về người bạn của con cá Kình (cái voi) - Yunus (Jonah) lúc Y bỏ đi vì tức giận người dân của Y khi họ bất tuân không chịu nghe lời nhắc nhở của Y. Y cứ tưởng TA (Allah) không làm gì để trừng phạt Y cho việc Y bỏ đi như thế. Vậy là TA đã cho Y gặp tai họa, TA đã để cho Y bị con cá voi nuốt vào bụng. Thế rồi, Y đã cầu xin TA trong những lớp màn đêm u tối: bụng cá voi, phần sâu dưới biển và đêm tối, Y thừa nhận lỗi lầm của mình và ăn năn sám hối, Y nói: Không có Đấng Thờ Phượng nào khác để xứng đáng (thờ phượng) ngoài Ngài, quả thật bề tôi là kẻ làm điều sai quấy.');
INSERT INTO "vn_mokhtasar" VALUES(21,88,'21:88','Vậy là TA (Allah) đã đáp lại sự cầu xin của Y (Yunus), TA đã cứu Y thoát khỏi tai họa đó, đưa Y trở ra khỏi những lớp màn u tối, từ bụng của con cá Kình, và tương tự với việc giải cứu Yunus khỏi những tai họa thì TA đã giải cứu những người có đức tin giống như vậy nếu họ biết cầu xin TA khi rơi vào cảnh hoạn nạn khó khăn.');
INSERT INTO "vn_mokhtasar" VALUES(21,89,'21:89','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại câu chuyện của Zakariya lúc Y cầu xin Thượng Đế của Y: Lạy Thượng Đế của bề tôi, xin Ngài đừng bỏ mặc bề tôi một mình đơn độc không có con cái nối dõi và Ngài là Đấng Vĩnh Hằng, Đấng để lại những điều tốt đẹp nhất sau bề tôi, xin Ngài hãy ban cho bề tôi một đứa con trai.');
INSERT INTO "vn_mokhtasar" VALUES(21,90,'21:90','Thế là TA (Allah) đã đáp lại lời cầu xin của Zakariy, TA đã ban cho Y một đứa con trai, đó là Yahya (John), TA đã chữa lành bệnh hiếm muộn cho vợ của Y để nữ ta có thể sinh con trở lại cho Y. Quả thật, Zakariya, vợ của Y và con trai của Y thường luôn đua nhau làm việc thiện tốt và ngoan đạo, họ thường cầu nguyện vì mong được ân phước nơi TA và rất sợ sự trừng phạt ở nơi TA; và họ là những người luôn hạ mình kính cẩn trước TA.');
INSERT INTO "vn_mokhtasar" VALUES(21,91,'21:91','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại câu chuyện của Maryam, người phụ nữ đã giữ gìn trinh tiết khỏi những hành vi Zina. Allah đã cử Ruh của Ngài (đại Thiên Thần Jibril) thổi vào Nữ và Nữ mang thai Ysa. Nữ và đứa con trai của Nữ Ysa, là một Dấu Lạ cho nhân loại về quyền năng của Allah và rằng Ngài không bất lực trước một điều gì, ngay cả việc tạo hóa ra Ysa mà không cần đến người cha.');
INSERT INTO "vn_mokhtasar" VALUES(21,92,'21:92','Hỡi nhân loại, quả thật giáo phái này của các ngươi là một giáo phái duy nhất, và đó là Tawhid (độc tôn: tôn thờ một thượng đế duy nhất) mà nó chính là tôn giáo của Islam, và TA (Allah) là Thượng Đế của các ngươi, bởi thế, các ngươi hãy thành tâm thờ phượng một mình TA.');
INSERT INTO "vn_mokhtasar" VALUES(21,93,'21:93','Và nhân loại đã chia rẽ nhau, một số họ là những người đi theo Tawhid tức tôn thờ duy nhất một mình Allah, một số thì thờ đa thần, và một số thì vô đức tin. Nhưng tất cả những người này đều sẽ trở về gặp lại TA vào Ngày Phán Xét để TA thưởng hoặc phạt cho các việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(21,94,'21:94','Bởi thế, ai làm việc thiện tốt và có đức tin nơi Allah, nơi các vị Thiên Sứ của Ngài và nơi cuộc sống cõi Đời Sau thì sự nỗ lực và công sức của y sẽ không uổng phí mà Allah sẽ tri ân y bằng phần thưởng và sẽ nhân phần thưởng đó lên, y sẽ tìm thấy những phần thưởng cho công sức của y được ghi trong quyển sổ bộ của y vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(21,95,'21:95','Và không bao giờ xảy ra cho việc trở lại trần gian để sám hối đối với cư dân của những thị trấn đã bị TA (Allah) hủy diệt vì tội chúng đã vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(21,96,'21:96','Cư dân của các thị trấn bị hủy diệt đó sẽ không bao giờ trở lại trần gian cho tới khi Ya''juj (Gog) và Ma''juj (Magog) được thả và chúng sẽ tràn ra từ mỗi ngọn đồi vào ngày hôm đó, và chúng tràn ra rất nhanh.');
INSERT INTO "vn_mokhtasar" VALUES(21,97,'21:97','Ngày Phục Sinh sắp đến gần bởi sự tràn ra của Ya''juj và Ma''juj, những điều đáng sợ và khủng khiếp của nó sẽ xuất hiện, khi những người vô đức tin chứng kiến những cảnh tượng đó thì chúng sẽ mở to mắt trừng trừng và than: Ôi thật khốn khổ cho chúng ta, quả thật trên trần giang chúng ta đã từng thờ ơ, không chuẩn bị bất cứ thứ gì cho Ngày Vĩ Đại này, mà chúng ta còn là những kẻ sai quấy toàn làm điều tội lỗi và vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(21,98,'21:98','Hỡi những kẻ thờ đa thần, quả thật các ngươi và những vật mà các người thờ phượng ngoài Allah từ bục tượng, con người và jinn đều sẽ là chất đốt trong Hỏa Ngục, nơi mà các ngươi và toàn bộ thần linh của các ngươi ngoài Allah sẽ đi vào.');
INSERT INTO "vn_mokhtasar" VALUES(21,99,'21:99','Nếu những vật thần linh được thờ phượng này là đích thực thì chắc chắn chúng đâu vào trong Hỏa Ngục ở cùng với những ai đã thờ phượng chúng, tất cả những vật thờ phượng và những kẻ thờ phượng chúng đều phải vào trong Hỏa Ngục không bao giờ được trở ra.');
INSERT INTO "vn_mokhtasar" VALUES(21,100,'21:100','Nơi Hỏa Ngục, trong những cơn đau đớn tột cùng, chúng sẽ rên rỉ dữ dội, và trong Hỏa Ngục chúng sẽ không nghe thấy được gì do những cơn đau điếng mà chúng đang hứng chịu.');
INSERT INTO "vn_mokhtasar" VALUES(21,101,'21:101','Khi những người thờ đa thần nói "quả thật Ysa và các Thiên Thần được thờ phượng đều sẽ vào Hỏa Ngục" thì Allah phán bảo: quả thật, những người đi trước trong kiến thức Allah rằng họ là những người được hưởng hạnh phúc chẳng hạn như Ysa đều được đưa ra xa khỏi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(21,102,'21:102','Họ (những người có đức tin, những người của Tawhid) sẽ không nghe thấy bất cứ âm thanh hay tiếng động nào của Hỏa Ngục, họ sẽ đời đời và vĩnh viễn ở trong sự hưởng thụ những khoái lạc và niềm hành phúc mà họ mong muốn, những khoái lạc và niềm hạnh phúc đó sẽ bất tận và không bao giờ đứt đoạn hay chấm dứt.');
INSERT INTO "vn_mokhtasar" VALUES(21,103,'21:103','Họ sẽ không còn sự kinh hãi lúc Hỏa Ngục đã đón lấy cư dân của nó vì các Thiên Thần nghênh đón với lời chúc mừng: đây là Ngày của quí vị, Ngày mà quí vị đã được hứa lúc còn trên trần gian, quí vị hãy vui cười cho niềm hạnh phúc mà quí vị được hưởng thụ.');
INSERT INTO "vn_mokhtasar" VALUES(21,104,'21:104','Vào Ngày đó, TA (Allah) sẽ cuốn bầu trời lại giống như các tờ kinh được cuốn lại, TA sẽ phục sinh nhân loại theo hình dáng mà TA đã tạo chúng ra ban đầu. TA đã hứa điều đó và với lời hứa đó TA sẽ không bao giờ thất hứa. Quả thật TA chắc chắn thực hiện đúng những gì đã hứa.');
INSERT INTO "vn_mokhtasar" VALUES(21,105,'21:105','Quả thật, TA (Allah) đã ghi trong các Kinh Sách mà TA đã ban xuống cho các vị Thiên Sứ sau khi TA đã ghi trong Quyển Kinh Mẹ (Lawhu Al-Mahfuz - Văn bản lưu trữ) rằng trái đất sẽ được thừa hưởng bởi các bề tôi ngoan đạo của TA, những người mà họ tuân lệnh TA, đó là cộng đồng tín đồ của Muhammad.');
INSERT INTO "vn_mokhtasar" VALUES(21,106,'21:106','Quả thật trong những gì mà TA đã ban xuống (Qur''an) là một lời khuyên răn cho nhóm người thực sự thờ phượng Thượng Đế của họ theo những gì mà Ngài đã qui định cho họ, và họ là những người được lợi ích từ nó.');
INSERT INTO "vn_mokhtasar" VALUES(21,107,'21:107','Này Muhammad, TA (Allah) cử Ngươi đến như một vị Thiên Sứ là chỉ để mang lại hồng phúc cho tất cả vũ trụ và muôn loài bởi vì Ngươi cố gắng hướng dẫn nhân loại và cứu họ khỏi sự trừng phạt của TA.');
INSERT INTO "vn_mokhtasar" VALUES(21,108,'21:108','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói: Quả thật, Ta được Thượng Đế của Ta mặc khải cho biết rằng Đấng Thờ Phượng đích thực của các người chỉ có một Đấng Duy Nhất, Ngài không có đối tác ngang vai, Ngài chính là Allah, bởi thế các người hãy tin nơi Ngài và làm theo mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(21,109,'21:109','Nếu những người này quay lưng với những gì Ngươi mang đến - hỡi Thiên Sứ Muhammad - thì Ngươi hãy bảo họ: Ta đã công bố cho các người những gì Ta được lệnh, giữa Ta và các người đều như nhau, và Ta không biết khi nào những hình phạt mà Allah đã hứa với các ngươi được ban xuống.');
INSERT INTO "vn_mokhtasar" VALUES(21,110,'21:110','Quả thật, Allah biết rõ những gì các ngươi công khai bằng lời nói và biết rõ những gì các ngươi che giấu, không có một điều gì có thể giấu giếm được Ngài, và rồi đây Ngài sẽ phạt các ngươi bởi việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(21,111,'21:111','Ta không biết, e rằng việc Allah trì hoãn sự trừng phạt là nhằm thử thách các ngươi hay muốn cho các ngươi hưởng thụ trong một thời gian ngắn ngủi nữa theo sự định đoạt của Ngài để các ngươi lún sâu hơn vào sự vô đức tin và lầm lạc của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(21,112,'21:112','Thiên Sứ Muhammad cầu nguyện Thượng Đế của Người: Lạy Thượng Đế của bề tôi, xin Ngài phân xử giữa bầy tôi và đám dân của bầy tôi, những người mà họ đã ngoan cố trên sự vô đức tin, bằng Chân Lý. Và Người nói với đám dân của Người: Chúng tôi cầu xin Thượng Đế của chúng tôi, Đấng Rất Mực Độ Lượng, giúp chúng tôi ứng phó với những điều các người nói từ sự vô đức tin và phủ nhận.');
INSERT INTO "vn_mokhtasar" VALUES(22,1,'22:1','Này hỡi nhân loại, các ngươi hãy kính sợ Thượng Đế của các ngươi mà thực hiện và chấp hành các mệnh lệnh của Ngài, từ bỏ và ngưng ngay những điều Ngài nghiêm cấm. Quả thật cơn địa chấn cũng như các sự việc khác xảy ra trong Ngày Tận Thế là những hiện tượng vô cùng khủng khiếp và kinh hoàng; các ngươi cần phải chuẩn bị cho Ngày đó với những việc làm được Ngài hài lòng.');
INSERT INTO "vn_mokhtasar" VALUES(22,2,'22:2','Vào Ngày Tận Thế, các ngươi sẽ chứng kiến những cảnh tượng: mỗi bà mẹ sẽ quên mất đứa con bé bỏng đang bú của mình, mỗi phụ nữ mang thai sẽ sẩy thai, đứa con trong bụng sẽ rơi khỏi dạ con của họ mà họ không hay biết do quá kinh hoàng và sợ hãi trước sự kiện và hiện tượng của Ngày hôm đó. Và Ngươi (Muhammad) sẽ thấy nhân loại trong Ngày hôm đó sẽ mất đi tâm trí giống như đang say rượu vì quá kinh hãi, họ không say vì uống rượu mà là vì sự trừng phạt của Allah rất khủng khiếp làm mất đi tâm trí của họ.');
INSERT INTO "vn_mokhtasar" VALUES(22,3,'22:3','Và trong nhân loại, có những kẻ đã tranh cãi về quyền năng của Allah trong việc phục sinh những người đã chết không một chút kiến thức và sự hiểu biết nào; và lời nói, quan niệm của họ về Thượng Đế chỉ là việc nghe theo những tên Shaytan và những tên Imam lệch lạc.');
INSERT INTO "vn_mokhtasar" VALUES(22,4,'22:4','Một điều đã được qui định đối với những tên Shaytan từ loài Jinn và loài người rằng ai đi theo chúng và tin chúng thì y sẽ bị chúng dắt đi lệch khỏi con đường chân lý và sẽ bị chúng đưa tới sự trừng phạt nơi Hỏa Ngục qua những điều vô đức tin và tội lỗi mà chúng chỉ ra.');
INSERT INTO "vn_mokhtasar" VALUES(22,5,'22:5','Hỡi nhân loại, quả thật các ngươi đã nghi ngờ về quyền năng phục sinh của TA (Allah) sau cái chết. Nếu như vậy thì các ngươi hãy suy ngẫm xem: quả thật, TA đã tạo người cha của các ngươi - Adam - từ đất bụi, rồi TA đã tạo hóa con cháu của Y từ tinh dịch được xuất ra từ người đàn ông trong dạ con của người phụ nữ, sau đó TA biến hợp tử (giữa tinh dịch của người đàn ông và noãn trứng của người phụ nữ) thành một hòn máu đặc, sau đó TA biến hòn máu đặc thành một cục thịt giống như một miếng thịt được nhai, sau đó TA biến cục thịt thành một tạo sinh đầy đủ hình hài và để nó sống trong dạ con cho đến ngày nó đi ra thành một đứa bé hoặc TA không làm cục thịt thành một hình hài tức sẩy thai, mục địch của những sự việc đó là để TA phơi bày cho các ngươi thấy quyền năng tạo hóa của TA; và TA để trong dạ con những gì TA muốn từ các bào thai cho đến một thời hạn nhất định, đó là chín tháng, rồi sau đó, TA cho các ngươi ra ngoài khỏi bụng mẹ của các ngươi thành một đứa trẻ, rồi các ngươi phát triển và tiến đến sức mạnh và trí tuệ hoàn chỉnh, TA đã làm cho một số trong các ngươi trên trước thời điểm đó nhưng có người được TA cho sống đến khi tuổi già yếu ớt không còn biết gì nữa giống như một đứa trẻ con. Và Ngươi (Muhammad) thấy mặt đất khô cằn không có một gốc cây rồi khi TA ban nước mưa xuống thì nó cựa mình sống lại và làm mọc ra cây cối xanh tươi.');
INSERT INTO "vn_mokhtasar" VALUES(22,6,'22:6','Tất cả những điều đó - từ việc khởi tạo các ngươi qua các giai đoạn cũng như các tình trạng sinh ra đời của các ngươi - là để các ngươi tin rằng Allah là Đấng đã tạo hóa các ngươi và đó là sự thật không có gì phải ngờ vực, khác với những bục tượng mà các ngươi thờ phượng, và để các ngươi tin rằng Ngài là Đấng làm cho người chết sống lại và Ngài có quyền năng trên tất cả mọi thứ, Ngài không bất lực trước bất cứ một sự việc gì.');
INSERT INTO "vn_mokhtasar" VALUES(22,7,'22:7','Và để các ngươi tin rằng giờ Tận Thế đang tiến đến gần, không phải ngờ vực về sự việc đó, và rằng Allah sẽ phục sinh những người chết từ trong cõi mộ để Ngài thưởng hoặc phạt cho các việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(22,8,'22:8','Và trong số những kẻ vô đức tin, có những tên tranh cãi về việc tôn thờ một mình Allah một cách không có kiến thức để dẫn tới điều chân lý, không có người chỉ dẫn để đưa họ tới đó và không có kinh sách nào soi sáng được ban xuống từ nơi Allah để hướng dẫn họ.');
INSERT INTO "vn_mokhtasar" VALUES(22,9,'22:9','Những kẻ xoay cổ sang hướng khác một cách tự cao tự đại để lôi kéo thiên hạ khỏi đức tin Iman và tôn giáo của Allah thì họ sẽ bị hạ nhục trên cõi trần gian này và sẽ phải nếm hình phạt bị thiêu đốt trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(22,10,'22:10','Kẻ bị thiêu đốt trong Hỏa Ngục được bảo: Đó là sự trừng phạt cho điều mà ngươi đã làm từ sự vô đức tin và tội lỗi, và Allah không trừng phạt bất kỳ ai trong tạo vật của Ngài trừ phi kẻ đó làm tội.');
INSERT INTO "vn_mokhtasar" VALUES(22,11,'22:11','Trong nhân loại, có một số người thờ phượng Allah trong sự hoài nghi, nếu điều tốt lành xảy đến với họ từ sự giàu có và sức khỏe thì họ tiếp tục bước đi trên đức tin và thờ phượng Allah nhưng nếu tai họa và nạn kiếp xảy đến với họ từ sự nghèo khổ và bệnh tật thì họ bi quan cho tôn giáo của họ và rời bỏ nó. Những người này là những người thua thiệt trên cuộc sống trần gian và cả ở cõi Đời Sau. Đây là sự thua thiệt và thất bại rất rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(22,12,'22:12','Y thờ phượng ngoài Allah các bục tượng, những thứ mà chúng không làm hại được y nếu y làm trái lệnh chúng và cũng không mang lại lợi ích gì cho y khi y vâng lời chúng. Việc thờ phượng và cầu nguyện các bục tượng không thể gây hại cũng như không có khẳ năng mang lại lợi ích, đó là một sự lầm lạc rất xa khỏi chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(22,13,'22:13','Kẻ vô đức tin này, kẻ mà y thờ phượng các bục tượng, các bục tượng đó chỉ gây hại cho y chứ không mang lợi gì cho y cả. Các bục tượng đó là những người chủ xấu và là những người bạn không đáng để đồng hành.');
INSERT INTO "vn_mokhtasar" VALUES(22,14,'22:14','Quả thật, Allah sẽ thu nhận những người có đức tin và làm việc thiện tốt vào các Ngôi Vườn Thiên Đàng của Ngài, nơi có các dòng sông chảy bên dưới các tòa lâu đài. Quả thật, Allah làm bất kỳ điều gì Ngài muốn và Ngài muốn trừng phạt ai Ngài muốn.');
INSERT INTO "vn_mokhtasar" VALUES(22,15,'22:15','Ai nghĩ rằng Allah không giúp đỡ và ủng hộ vị Nabi của Ngài trên cuộc sống cõi trần này và ở cõi Đời Sau thì y hãy buộc một sợi dây từ trần nhà xuống rồi treo cổ tự tử để xem liệu những gì y thấy có đúng như vậy không. Chắc chắn Allah luôn phù hộ vị Nabi của Ngài dù cho những kẻ chống đối muốn hay không.');
INSERT INTO "vn_mokhtasar" VALUES(22,16,'22:16','Và giống như TA (Allah) đã trình bày cho các ngươi các lý lẽ rõ ràng về sự Phục Sinh, TA đã ban xuống cho Muhammad saw Kinh Qur''an với những lời phán rõ ràng và rành mạch (dễ hiểu, không có sự mơ hồ); và rằng với hồng phúc của Allah, Ngài sẽ hướng dẫn ai Ngài muốn.');
INSERT INTO "vn_mokhtasar" VALUES(22,17,'22:17','Quả thật những người có đức tin nơi Allah từ cộng đồng này (cộng đồng tín đồ của Muhammad), những tín đồ Do Thái, những tín đồ Sabian (một nhóm người đi theo một số vị Nabi), những tín đồ Thiên Chúa, những tín đồ thờ lửa và những người thờ các bục tượng, vào Ngày Phục Sinh, Allah chắc chắn sẽ phân xử và phán xét giữa họ, những ai có đức tin sẽ được vào Thiên Đàng còn những ai khác ngoài họ sẽ đi vào Hỏa Ngục. Quả thật, Allah chứng giảm (biết) tất cả mọi thứ, Ngài biết mọi hành động, lời nói của đám bề tôi của Ngài, không có một điều gì có thể che giấu được Ngài, và Ngài sẽ thưởng hoặc phạt cho tất cả mọi hành động.');
INSERT INTO "vn_mokhtasar" VALUES(22,18,'22:18','Hỡi Thiên Sứ Muhammad, chẳng lẽ Ngươi không thấy rằng tất cả những ai trong các tầng trời (các Thiên Thần) và những ai có đức tin trên trái đất (từ loài người và Jinn) cùng với mặt trời, mặt trăng, các vì sao, núi non, cây cối, động vật, đều phủ phục quỳ lạy Allah đó sao? Trong nhân loại, có đa số người phủ phục quỳ lạy Ngài nhưng cũng có nhiều người không chịu phủ phục trước Ngài. Bởi thế, những ai không phủ phục Ngài đáng phải chịu sự trừng phạt của Ngài cho việc vô đức tin của họ. Và người nào bị Allah hạ nhục bởi sự vô đức tin của y thì y sẽ không bao giờ tìm thấy vị bảo hộ giúp y lấy lại sự vinh quang. Quả thật, Allah làm bất cứ điều gì Ngài muốn, không một ai có khả năng và quyền hạn áp đặt cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(22,19,'22:19','Hai nhóm đối lập này (những người có đức tin và những người vô đức tin) tranh cãi nhau về Thượng Đế của họ. Nhóm nào là chân lý: nhóm có đức tin Iman hay nhóm vô đức tin. Kết cuộc, nhóm người vô đức tin sẽ bị bao vây bởi Hỏa Ngục giống như những chiếc áo trùm phủ lên người, và từ trên đầu của họ, một loại nước cực sôi sẽ được đổ xuống.');
INSERT INTO "vn_mokhtasar" VALUES(22,20,'22:20','Nước cực sôi đó sẽ làm tan chảy các nội tạng bên trong do quá nóng và sẽ làm tan chảy cả làn da bên ngoài.');
INSERT INTO "vn_mokhtasar" VALUES(22,21,'22:21','Và trong Hỏa Ngục, các Thiên Thần sẽ dùng những chiếc búa bằng sắt đánh lên đầu của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(22,22,'22:22','Mỗi khi chúng cố gắng chạy thoát ra khỏi Hỏa Ngục do sự đau đớn trong đó thì bị bắt trở lại và chúng được bảo: các ngươi hãy nếm lấy sự trừng phạt của Hỏa Ngục, nơi của sự thiêu đốt.');
INSERT INTO "vn_mokhtasar" VALUES(22,23,'22:23','Nhóm những người có đưc tin, họ là những người tin nơi Allah và làm việc thiện tốt, Allah sẽ thu nhận họ vào Thiên Đàng với những ngôi vườn và tòa lâu đài bên dưới có các dòng sông chảy. Trong đó, Ngài sẽ cho họ đeo những chếc vòng vàng và các trang sức từ ngọc trai và y phục của họ sẽ là tơ lụa và gấm vóc.');
INSERT INTO "vn_mokhtasar" VALUES(22,24,'22:24','Trên cuộc sống trần gian, Allah đã hướng dẫn họ đến lời nói tốt đẹp, như lời tuyên thệ Shahadah "Không có Thượng Đế đích thực nào khác ngoài Allah", lời tụng niệm "Allahu-akbar, Alhamdulillah, ..." và Ngài hướng dẫn họ đến con đường Islam đáng ca ngợi.');
INSERT INTO "vn_mokhtasar" VALUES(22,25,'22:25','Quả thật, những kẻ vô đức tin nơi Allah, tìm cách cản trở người khác gia nhập Islam, ngăn cản thiên hạ đến Masjid Al-Haram (Makkah) giống như hành động của những kẻ thờ đa thần trong năm Hudaibiyah, họ sẽ nếm phải hình phạt đau đớn. Bởi lẽ, Masjid đó là nơi mà TA (Allah) qui định làm Qiblah (hướng phải quay mặt về đó) cho nhân loại trong lúc dâng lễ nguyện Salah và làm một nơi để thiên hạ đến hành hương Hajj và Umrah, dù là đối với dân bản địa Makkah hay những người ngoài Makkah. Bất kỳ ai cố tình muốn lệch khỏi chân lý để đến với một điều gì đó tội lỗi thì TA sẽ cho y nếm mùi hình phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(22,26,'22:26','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại lúc TA (Allah) chỉ cho Ibrahim chỗ để dựng lên Ngôi Đền Ka''bah sau khi nó bị bỏ hoang, và TA đã phán với Y: Chớ đừng tổ hợp với TA bất cứ thứ gì trong việc thờ phượng mà hãy thờ phượng một mình TA, và hãy giữ gìn cho Ngôi Đền đó của TA thanh sạch khỏi những ô uế (ở cả mặt hình thức và nội dung) cho những người đi Tawaf (nghi thức đi vòng quanh Ka''bah của hành hương Hajj và Umrah), và những người dâng lễ nguyện Salah.');
INSERT INTO "vn_mokhtasar" VALUES(22,27,'22:27','"Và hãy mời gọi thiên hạ đến thăm viếng Ngôi Đền này - Ka''bah - ngôi đền mà TA (Allah) đã ra lệnh cho Người (Ibrahim) dựng lên. Ngươi hãy kêu gọi họ đến theo khả năng của họ dù là bằng cách đi bộ hay bằng cách cưỡi trên những con lạc đà vì cuộc hành trình xa xôi.');
INSERT INTO "vn_mokhtasar" VALUES(22,28,'22:28','Mục đích để họ có được những điều hữu ích từ sự tha thứ và phần thưởng, để họ biểu hiệu chung một lời Tawhid, và để họ nhắc tên của Allah qua những con vật mà họ dùng làm con vật giết tế trong những ngày ấn định: mồng tháng Zdul-Hijjah và ba ngày sau đó; và để họ tạ ơn Allah về những bổng lộc mà Ngài đã cho họ từ lạc đà, bò, dê và cừu. Bởi thế, các ngươi hãy ăn thịt từ những con vật tế này và hãy chia sẻ chúng cho những người nghèo khổ.');
INSERT INTO "vn_mokhtasar" VALUES(22,29,'22:29','Rồi để họ chấm dựt tình trạng cấm kỵ của những ngày Hajj và tự do trở lại trong việc sinh hoạt bằng việc cạo đầu, cắt móng tay chân và vệ sinh thân thể; và để họ thực hiện điều mà họ đã thề nguyện từ việc làm Hajj, Umrah hay giết tế và để họ đi Tawaf Ngôi Đền mà Allah đã cho dựng từ lâu đời.');
INSERT INTO "vn_mokhtasar" VALUES(22,30,'22:30','Những gì các ngươi được lệnh - từ việc cạo đầu, cắt móng tay chân, vệ sinh thân thể, thực hiện điều thề nguyện và Tawaf Ngôi Đền - là những gì mà Allah đã bắt buộc cho các ngươi; bởi thế các ngươi hãy tôn vinh những điều Allah bắt buộc các ngươi. Và ai tránh những điều mà Allah qui định y phải tránh trong tình trạng Ihram của y vì tôn vinh giới hạn của Ngài thì điều đó tốt cho y trên cuộc sống thế gian và cõi Đời Sau ở nơi Thượng Đế của y. Và này hỡi nhân loại, các ngươi được phép ăn thịt của các thú nuôi từ lạc đà, bò, dê cừu; bởi thế, các ngươi đừng cấm bản thân mình từ các loại thú nuôi đó ngoại trừ những gì bị cấm được nhắc trong Qur''an như xác chết, máu, v.v. Và các ngươi hãy tránh xa sự ô uế của việc thờ cúng tượng thần, và hãy tránh xa tất cả mọi lời nói dối cho Allah và đối với nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(22,31,'22:31','Hãy chính trực trong tôn giáo của Allah bằng cách không tổ hợp với Ngài những đối tác ngang vai trong thờ phượng. Ai tổ hợp với Allah một đối tác ngang vai thì kẻ đó giống như bị rơi từ trên trời xuống rồi bị chim chóc tha đi hoặc là bị cơn gió mang đi đến một nơi rất xa xôi.');
INSERT INTO "vn_mokhtasar" VALUES(22,32,'22:32','Đó là sự chính trực, tức điều mà Allah ra lệnh phải tôn thờ một mình Ngài, toàn tâm toàn ý vì Ngài và tránh xa các bục tượng cũng như sự dối trá trong lời nói. Và ai tôn vinh các biểu hiệu của tôn giáo - trong đó, có việc giết tế và các nghi thức của Hajj - thì việc tôn vinh đó đến từ những trái tim kính sợ Thượng Đế của họ.');
INSERT INTO "vn_mokhtasar" VALUES(22,33,'22:33','Các ngươi sẽ có được từ những con vật mang đi giết tế các lợi ích nhất định trong một khoảng thời gian nhất định như việc cưỡi chúng, thu bộ lông của chúng, phần sữa của chúng và cả việc sinh sản của chúng cho đến thời điểm giết tế chúng ở một nơi gần Ngôi Nhà lâu đời của Allah - Masjid Al-Haram.');
INSERT INTO "vn_mokhtasar" VALUES(22,34,'22:34','Mỗi cộng động trước đây, TA (Allah) đều qui định cho họ một nghi lễ giết tế để họ nhắc tên của Allah trên những con vật mà họ giết tế, mục đích để họ biết ơn Allah về những bổng lộc mà Ngài đã ban cho họ từ lạc đà, bò và dê cừu. Bởi thế, hỡi con người, Đấng Thờ Phượng đích thực của các ngươi chỉ có một Đấng Duy Nhất, không có đối tác ngang vái, cho nên chỉ phục tụng, vâng lệnh một mình Ngài. Và này hỡi Thiên Sứ Muhammad, Ngươi hãy báo tin vui cho những người thành tâm kính sợ Allah.');
INSERT INTO "vn_mokhtasar" VALUES(22,35,'22:35','Những người thành tâm kính sợ Allah là những người khi nghe nhắc đến Allah thì họ run sợ sự trừng phạt của Ngài, họ tránh xa những điều trái nghịch mệnh lệnh của Ngài, họ chu đáo thực hiện các lễ nguyện Salah, họ kiên nhẫn trên những tai họa xảy đến, và họ chi dùng những bổng lộc mà Ngài đã ban cho họ vào những việc thiện tốt.');
INSERT INTO "vn_mokhtasar" VALUES(22,36,'22:36','Lạc đà, bò hoặc dê cừu mà các ngươi mang đi đến Ngôi Đền Ka''bah để làm con vật tế là một trong các biểu hiệu tôn giáo mà TA (Allah) đã ban cho các ngươi. Ở nơi chúng có những điều lợi cho các ngươi trong đạo cũng như trong đời. Bởi thế, các ngươi hãy nói "Bismillah" - "Nhân danh Allah" lúc các ngươi giết chúng sau khi chúng đứng thành hàng. Và khi nào chúng ngã xuống nằm một bên thì các ngươi - hỡi những người giết tế - hãy ăn một phần thịt từ chúng và đem phần thịt còn lại của chúng phân phát cho những người nghèo nhưng không muốn xin, không muốn lộ cho người ta biết mình nghèo để được cho. Và TA (Allah) đã chế ngự chúng các ngươi để các ngươi dùng chúng làm phương tiện chuyên chở, và TA đã chế ngự chúng để các ngươi giết chúng làm vật tế, mong rằng các ngươi biết ơn TA về những ân huệ đó.');
INSERT INTO "vn_mokhtasar" VALUES(22,37,'22:37','Thịt và máu của những con vật mà các ngươi giết tế không bao giờ lên đến tận nơi Allah, các ngươi cũng không bao giờ mang chúng lên đến tận nơi Ngài, mà chính lòng Taqwa của các ngươi (sự ngay chính và kính sợ của các ngươi đối với Ngài) mới là thứ đến được nơi Ngài bởi lẽ các ngươi đã thành tâm trong việc chấp hành theo lệnh của Ngài vì muốn được đến gần Ngài. Allah đã chế phục những con vật cho các ngươi như thế là vì Ngài muốn các ngươi có dịp tán dương sự vĩ đại của Ngài và biết ơn Ngài. Và Ngươi - hỡi Thiên Sứ Muhammad - hãy báo tin mừng cho những ai làm tốt trong việc thờ phượng Thượng Đế của họ cũng như trong việc cư xử với tạo vật của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(22,38,'22:38','Chắc chắn Allah sẽ bảo vệ những ai có đức tin nơi Ngài khỏi những điều xấu từ kẻ thù của họ, và chắc chắn Allah không yêu thương những kẻ gian dối và vô ơn với ân huệ của Ngài mà chắc chắn Ngài sẽ giận dữ đối với họ.');
INSERT INTO "vn_mokhtasar" VALUES(22,39,'22:39','Allah cho phép những người có đức tin chiếc đấu để đánh trả những kẻ đa thần khi họ gây chiến và đàn áp. Và quả thật Allah có toàn năng cứu giúp những người có đức tin đánh bại kẻ thù, tuy nhiên, đó là dụng ý từ sự thông thái và khôn ngoan của Ngài qua việc để họ đánh chiến với kẻ thù vô đức tin của họ.');
INSERT INTO "vn_mokhtasar" VALUES(22,40,'22:40','Những người bị những kẻ vô đức tin trục xuất khỏi nhà cửa của họ một cách bất công, họ bị trục xuất không phải do đã làm bất kỳ hành động xấu nào mà họ bị trục xuất là do bởi họ nói: "Thượng Đế của chúng tôi là Allah, chúng tôi không có Thượng Đế nào khác ngoài Ngài." Và nếu Allah không sắc lệnh cho phép các vị Nabi cũng như những người có đức tin chiến đấu đánh trả lại kẻ thù của họ vì chúng đã xâm phạm những nơi thờ phượng của họ thì chúng đã phá hủy các tu viên, nhà thờ, giáo đường và các thánh đường của tín đồ Muslim, nơi để những tín đồ Muslim dâng lễ nguyện Salah và tụng niệm Allah. Và chắc chắn Allah sẽ cứu giúp những ai ủng hộ tôn giáo và vị Nabi của Ngài, bởi quả thật, Allah là Đấng Hùng Mạnh trong việc giúp những ai ủng hộ tôn giáo của Ngài, là Đấng Quyền Năng vô song mà không một ai (vật gì) có thể kháng lại.');
INSERT INTO "vn_mokhtasar" VALUES(22,41,'22:41','Những người được Allah hứa giúp đỡ là những người mà nếu Ngài cho họ sống trên trái đất thì họ chu đáo dâng lễ nguyện Salah, chi dùng tài sản để xuất Zakah, kêu gọi và bảo ban mọi người làm những điều mà giáo luật Islam ra lệnh, ngăn cản mọi người làm điều cấm trong giáo luật. Và một mình Allah nắm quyền quyết định tất cả mọi sự việc: thưởng và phạt.');
INSERT INTO "vn_mokhtasar" VALUES(22,42,'22:42','Hỡi Thiên Sứ Muhammad, nếu đám dân của Ngươi phủ nhận Ngươi thì Ngươi hãy kiên nhẫn bởi vì Ngươi không phải là người đầu tiên bị người dân của mình phủ nhận mà quả thật trước Ngươi, đám dân của Nuh, đám dân Ad của Hud và đám dân Thamud của Saleh cũng đã phủ nhận các vị Nabi, các vị Thiên Sứ của họ giống như vậy.');
INSERT INTO "vn_mokhtasar" VALUES(22,43,'22:43','Và đám dân của Ibrahim thì phủ nhận Ibrahim, đám dân của Lut thì phủ nhận Lut.');
INSERT INTO "vn_mokhtasar" VALUES(22,44,'22:44','Đám dân của xứ Madyan cũng đã phủ nhận Shu''aib, Pha-ra-ông và đám dân của hắn cũng đã phủ nhận Musa. Bởi thế, TA (Allah) đã trì hoãn sự trừng phạt để chúng lún sâu hơn trong tội lỗi rồi sau đó TA đã bắt phạt chúng. Quả thật, TA đã bắt phạt chúng bởi sự vô đức tin của chúng và Ngươi (Muhammad) hãy xem TA đã trừng phạt chúng thích đáng như thế nào?');
INSERT INTO "vn_mokhtasar" VALUES(22,45,'22:45','Đã có biết bao nhiêu thị trấn bị TA hủy diệt khi đám dân của chúng làm điều sai quấy bởi sự vô đức tin. TA đã trừng phạt chúng đến mức các ngôi nhà lật ngược lên, các giếng nước bị bỏ hoang, và các lâu đài trở thành những bãi hoang tàn.');
INSERT INTO "vn_mokhtasar" VALUES(22,46,'22:46','Sao những kẻ phủ nhận những gì mà Thiên Sứ Muhammad saw mang đến không chịu đi du ngoạn khắp nơi trên trái đất để chứng kiến những vết tích bị tàn phá của các thị trấn bị tiêu diệt giúp tâm trí của chúng hiếu ra sự việc và tai của chúng nghe được những câu chuyện mà chấp nhận sự khuyên răn. Quả thật, chúng không đui mù ở đối mắt mà chúng đui mù ở con tim và tâm trí của chúng khi chúng không chịu lắng nghe và không chịu hiểu.');
INSERT INTO "vn_mokhtasar" VALUES(22,47,'22:47','Hỡi Thiên Sứ Muhammad, những kẻ vô đức tin từ đám dân của Ngươi hối thúc Ngươi mang hình phạt đến trừng phạt họ trên thế gian và cả sự trừng phạt ở cõi Đời Sau. Quả thực, Allah không bao giờ thất hứa về những gì Ngài đã hứa với chúng. Và một trong những hình phạt mà Ngài đã trừng phạt chúng trên thế gian, đó là ngày diễn ra cuộc chiến Badr. Còn sự trừng phạt ở cõi Đời Sau, một ngày của nó giống như một ngàn năm theo niên kỷ mà các ngươi tính toán trên thế gian nguyên nhân là do sự trừng phạt rất khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(22,48,'22:48','Đã có biết bao thị trấn đã được Allah trì hoãn sự trừng phạt trong lúc đám dân của chúng làm điều sai quấy bởi sự vô đức tin, nhưng rồi sự trừng phạt cũng đến với chúng vì tất cả đều phải quay về trình diện Allah vào Ngày Phán Xét. Bởi thế, phần thưởng xứng đáng cho sự vô đức tin của chúng là một hình phạt đời đời.');
INSERT INTO "vn_mokhtasar" VALUES(22,49,'22:49','Hãy nói đi Muhammad: "Hỡi nhân loại, quả thật, Ta chỉ là một người cảnh báo được cử phái đến cho các ngươi và những lời trong sự cảnh báo của Ta rất rõ ràng và công khai.');
INSERT INTO "vn_mokhtasar" VALUES(22,50,'22:50','Những người có đức tin nơi Allah và làm việc thiện tốt, họ sẽ được Thượng Đế của họ tha thứ tội lỗi và được ban cho bổng lộc dồi dào không dứt trong Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(22,51,'22:51','Còn những người phủ nhận các lời mặc khải của TA (Allah) với ý nghĩ rằng chúng có khả năng làm cho Allah bất lực khiến Ngài không thể trừng phạt chúng thì chúng là những người bạn của Hỏa Ngục, họ sẽ ở trong đó đời đời, kiếp kiếp.');
INSERT INTO "vn_mokhtasar" VALUES(22,52,'22:52','Hỡi Thiên Sứ Muhammad, trước Ngươi, không một vị Nabi hãy một vị Thiên Sứ nào được TA (Allah) cử đến mà không phải đối mặt với sự việc: khi Y đọc Kinh Sách của Allah thì Shaytan nhảy vào xúi giục Y bóp méo những lời mặc khải của thượng Đế trước nhân loại. Tuy nhiên, Allah dẹp bỏ những điều sai mà Shaytan đã xúi giục và làm vững chắc các lời mặc khải của Ngài bởi Allah là Đấng Hằng Biết mọi thứ, không một điều gì có thể che giấu được Ngài, Ngài Sáng Suốt trong việc tạo hóa, định đoạt và điều hành của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(22,53,'22:53','Shaytan xen vào việc truyền đạt của Thiên Sứ Muhammad, đó là mục đích của Allah, Ngài dùng nó để thử thách những kẻ đạo đức giả cũng như những kẻ mà trái tim của họ chai cứng. Và quả thật, những kẻ làm điều sai quấy từ nhóm người đạo đức giả và nhóm người thờ đa thần sẽ luôn ở trong sự thù nghịch với Allah và Thiên Sứ của Ngài, họ sẽ luôn ở cách xa chân lý và sự chỉ đạo.');
INSERT INTO "vn_mokhtasar" VALUES(22,54,'22:54','Và để những người mà Allah ban cho họ kiến thức kiên định rằng Qur''an được ban xuống cho Muhammad chính là Chân Lý mà Allah đã mặc khải xuống cho Ngươi - Hơi Thiên Sứ Muhammad - để họ tăng thêm đức tin nơi Nó và để trái tim của họ biết hạ mình kính sợ Allah và thành tâm với Ngài. Quả thật, Allah là Đấng Hướng Dẫn những ai có đức tin nơi Ngài đến với con đường chân lý và ngay chính, một phần thưởng dành cho sự hạ mình phủ phục của họ đối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(22,55,'22:55','Những kẻ vô đức tin nơi Allah và phủ nhận Thiên Sứ của Ngài vẫn cứ mãi nghi hoặc và ngờ vực về những gì mà Allah ban cho Ngươi - hỡi Thiên Sứ Muhammad - trong Qur''an, chúng vẫn cứ trong tình trạng như thế cho đến khi thời khắc kinh ngạc ập đến, hay là sự trừng phạt của Ngày mà không còn lòng thương xót, không có điều tốt đẹp ập đến, và đó là Ngày Phục Sinh được dựng lên cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(22,56,'22:56','Vào Ngày Phán Xét - Ngày mà những kẻ được hứa sẽ phải bị trừng phạt - mọi quyền hành đều thuộc về một mình Allah, Ngài sẽ phán xét và phân xử giữa những người có đức tin và những người vô đức tin. Ngài sẽ xét xử công minh, tất cả đều được thưởng hoặc bị phạt một cách xứng đáng. Đối với những người có đức tin nơi Allah và làm việc thiện tốt sẽ được ban thưởng vô cùng vĩ đại, đó là những Ngôi Vươn Thiên Đàng hạnh phúc và bất tận.');
INSERT INTO "vn_mokhtasar" VALUES(22,57,'22:57','Đối với những kẻ vô đức tin nơi Allah và cho rằng các lời Mặc Khải của Ngài ban xuống cho Muhammad là giả tạo không thực thì những kẻ đó sẽ bị trừng phạt bằng một sự trừng phạt nhục nhã trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(22,58,'22:58','Và những người rời bỏ nhà cửa và quê hương của họ để đi tìm sự hài lòng của Allah và đề cao tôn giáo của Ngài rồi họ bị giết hoặc chết trong cuộc chiến đấu vì chính nghĩa của Ngài thì sẽ được Allah ban phát nhiều bổng lộc tốt đẹp trong Thiên Đàng, một nguồn bổng lộc vô tận không hề dứt. Quả thật, Allah là Đấng ban phát bổng lộc tốt nhất.');
INSERT INTO "vn_mokhtasar" VALUES(22,59,'22:59','Chắc chắn Allah sẽ thu nhận họ vào một nơi ở mà họ hài lòng và thỏa nguyện, đó là Thiên Đàng, và quả thật Allah là Đấng hằng biết mọi hành động và tâm niệm của họ, Đấng hằng chịu đựng khi Ngài trì hoãn sự trừng phạt đối với những hành động trái nghịch mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(22,60,'22:60','Sự việc thu nhận những ai chiến đấu cho con đường chính nghĩa của Allah vào Thiên Đàng là đúng như thế. Và ai bị xâm hại thì được quyền đáp trả lại như những gì bị xâm hại, việc làm đó không mang tội. Và khi nào kẻ xâm hại tiếp tục hành động xâm hại thì Allah sẽ trợ giúp y bởi quả thật Ngài là Đấng xí xóa tội lỗi cho những người có đức tin và Ngài hằng tha thứ cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(22,61,'22:61','Việc trợ giúp đó (giúp người bị xâm hại) là bởi vì Allah là Đấng Toàn Năng trên bất cứ điều gì Ngài muốn. Và một trong những quyền năng của Ngài là Ngài nhập ban đêm vào ban ngày và ban ngày vào ban đêm, Ngài làm cho ban ngày có lúc dài hơn ban đêm và có lúc thì ngắn hơn. Quả thật, Allah là Đấng luôn nghe tất cả mọi lời nói của đám bề tôi của Ngài, luôn thấy rõ mọi hành động của họ, không một điều gì có thể che giấu được Ngài, và Ngài sẽ đền bù xứng đáng cho các hành động và việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(22,62,'22:62','Việc được đề cập đó - nhập ban ngay vào ban đêm và ban đêm vào ban ngày - là bởi vì Allah là chân lý tuyệt đối, tôn giáo của Ngài là chân lý, lời hứa của Ngài là sự thật, việc trợ giúp của Ngài dành cho những người có đức tin là sự thật, còn những thần linh mà những người thờ đa thần van vái cầu xin ngoài Allah từ những bục tượng là ngụy tạo không có một cơ sở và căn cứ nào cả. Quả thật, Allah là Đấng Tối Cao bên trên mọi tạo vật của Ngài về quyền năng và sức mạnh, Ngài là Đấng Vĩ Đại, mọi niềm tự hào, sự oai nghiêm và uy nghi đều thuộc về một mình Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(22,63,'22:63','Hỡi Thiên Sứ Muhammad, há Ngươi không nhìn thấy Allah đã ban mưa từ trên trời xuống và sau cơn mưa mặt đất trở nên xanh tươi bởi những cây cối mọc lên. Quả thật, Allah là Đấng Tinh Thông khi Ngài ban mưa xuống cho đám bề tôi của Ngài để làm mọc ra cây cối cho họ, và Ngài là Đấng Am Tường những gì cải thiện cho họ, không một điều gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(22,64,'22:64','Vương quyền chi phối và điều hành mọi vật trong trời đất đều thuộc về riêng một mình Allah. Và quả thật, Ngài là Đấng Giàu Có, Ngài không cần đến bất kỳ tạo vật nào trong các tạo vật của Ngài; và Ngài là Đấng Đáng Ca Ngợi trong mọi hoàn cảnh và tình huống.');
INSERT INTO "vn_mokhtasar" VALUES(22,65,'22:65','Hỡi Thiên Sứ Muhammad, há Ngươi không thấy việc Allah đã chế ngự cho Ngươi và nhân loại mọi thứ trên trái đất từ động vật cho đến các vật vô tri thành những điều mang lợi ích cho các ngươi và nhu cầu của các ngươi. Ngài đã chế ngự những con tàu băng qua biển cả dưới mệnh lệnh của Ngài, chúng di chuyển từ đất nước này đến đất nước khác. Và Ngài đã giữ cho bầu trời không rơi xuống trái đất theo ý muốn của Ngài bởi vì nếu Ngài cho phép nó rơi xuống thì chắc chắn nó sẽ rơi xuống. Quả thật, Allah rất mực thương xót và nhân từ đối với con người khi mà Ngài đã chế ngự những thứ này cho họ để họ sử dụng mặc dù họ làm điều sai quấy với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(22,66,'22:66','Allah là Đấng đã làm cho các ngươi sống từ cõi hư vô, Ngài đã tạo các ngươi khi mà trước đó các ngươi không là gì, sau đó Ngài làm cho các ngươi chết đi cho việc chấm dứt tuổi đời của các ngươi, rồi sau đó, Ngài làm cho các ngươi sống lại một lần nữa từ cõi chết để xét xử những việc làm của các ngươi và thưởng phạt một cách công bằng. Quả thật, con ngươi thường vô ơn đối với các ân huệ của Allah bằng việc thờ phượng thứ khác ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(22,67,'22:67','Mỗi cộng đồng đều được TA (Allah) qui định cho một hệ thống giáo lý, mỗi cộng đồng phải đi theo hệ thống giáo lý của riêng họ. Vì vậy, Ngươi không để những kẻ thờ đa thần cũng như các nhóm tôn giáo khác tranh luận với Ngươi - hỡi Thiên Sứ Muhammad - về hệ thống giáo lý của Ngươi, và Ngươi nói bằng những điều chân lý còn họ thì không, vì họ là nhóm người móp méo sự thật. Vì vậy, Ngươi hãy kêu gọi nhân loại hướng về Allah một cách thành tâm bởi vì Ngươi đang đi trên con đường Ngay Chính, con đường không có bất cứ sự bất công nào.');
INSERT INTO "vn_mokhtasar" VALUES(22,68,'22:68','Và nếu chúng cứ tranh luận với Ngươi sau khi các bằng chứng và lý lẽ đã sáng tỏ thì Ngươi hãy bỏ mặc chúng cho Allah và hãy nói với chúng bằng những lời cảnh báo: Quả thật Allah biết rõ những gì các người đang làm, không một điều gì từ việc làm của các người có thể che giấu được Ngài, và chắc chắn Ngài sẽ trừng phạt cho những việc làm của các người.');
INSERT INTO "vn_mokhtasar" VALUES(22,69,'22:69','Allah sẽ phán xét và phân xử giữa đám bề tôi của Ngài vào Ngày Phục Sinh: giữa những người có đức tin và những người vô đức tin về những sự việc trong tôn giáo mà họ mâu thuẫn và bất đồng trên trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(22,70,'22:70','Hỡi Thiên Sứ Muhammad, chẳng lẽ Ngươi không biết rằng Allah thông toàn tất cả mọi thứ, mọi sự việc trên trời, mọi sự việc ở trái đất, không có bất cứ thứ gì, việc gì có thể che giấu được Ngài. Tất cả mọi sự việc, mọi thứ xảy ra trong trời đất đều được ghi sẵn từ trước trong Quyển Kinh Mẹ (Al-Lawhu Al-Mahfuzh) ở nơi Ngài, và quả thật, việc biết rõ mọi thứ và mọi sự việc như thế đối với Allah là điều quá đơn giản và dễ dàng.');
INSERT INTO "vn_mokhtasar" VALUES(22,71,'22:71','Những kẻ thờ đa thần đã thờ phượng ngoài Allah những bục tượng, những thứ mà Allah không ban xuống một nguồn chỉ đạo nào cho việc thờ phượng chúng và những kẻ thờ đa thần này cũng chẳng có cơ sở nào về điều đó cả. Chúng chỉ bắt chước theo ông bà cha mẹ của chúng một cách mù quáng. Và chắc chắn những kẻ làm điều sai quấy sẽ không tìm thấy một ai cứu giúp và can thiệp trước sự trừng phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(22,72,'22:72','Và khi Ngươi (Muhammad) đọc các câu Kinh Qur''an của TA (Allah) cho những kẻ vô đức tin nghe thì Ngươi sẽ thấy trên gương mặt của chúng hiện rõ sự phản đối và phủ nhận Allah, chúng sẽ nhăn nhó khó chịu khi nghe. Chúng gần như giận dữ muốn ập đến tấn công những ai đọc các lời phán của TA cho chúng nghe. Ngươi - hỡi Thiên Sứ Muhammad - hãy nói với chúng: Có phải các người muốn ta cho các người biết điều tồi tệ hơn việc nhăn nhó và khó chịu của các ngươi chăng? Đó chính là Hỏa Ngục, nơi mà Allah đã chuẩn bị cho những kẻ vô đức tin đi vào đó, và đó là một nơi đến vô cùng xấu xa và tồi tệ.');
INSERT INTO "vn_mokhtasar" VALUES(22,73,'22:73','Này hỡi nhân loại, hình ảnh thí dù đã được trình bày cho các ngươi, vì vậy, các ngươi hãy lắng nghe và hãy lấy đó làm bài học. Quả thật, những thần linh mà các ngươi thờ phượng ngoài Allah từ các bục tượng và những thứ khác đều không thể tạo ra được một con ruồi, ngay cả chúng có hợp sức với nhau lại để làm điều đó cũng không thể nào. Các ngươi hãy nhìn xem, khi con ruồi tha thức ăn của chúng đi thì chúng không thể lấy lại thức ăn của mình được thì làm sao chúng có thể tạo ra một con ruồi. Nếu đó chỉ là con ruồi mà chúng cũng bất lực thì nói chi đến những thứ lớn lao hơn thế. Vậy sao các ngươi lại thờ cúng những thứ vô năng và bất lực thế kia thay vì Allah? Quả thật, kẻ cầu xin khấn vái và kẻ được cầu xin khấn vái đều bất lực trong việc tạo ra một con ruồi nhỏ bé không đáng gì.');
INSERT INTO "vn_mokhtasar" VALUES(22,74,'22:74','Những kẻ thờ đa thần đã không tôn vinh Allah đúng nghĩa với sự vĩ đại và toàn năng của Ngài khi mà chúng đã thờ phượng một số tạo vật cùng với Ngài. Quả thật, Allah là Đấng Toàn Lực, và một trong biểu hiện sự toàn lực của Ngài là Ngài đã tạo ra các tầng trời và trái đất vĩ đại như thế nào; Ngài là Đấng Toàn Năng, không thứ gì trong các thần linh của những kẻ thờ đa thần có thể sánh bằng bởi chúng chỉ là những thứ vô cùng thấp hẹn đối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(22,75,'22:75','Allah, Đấng Tối Cao chọn trong hàng ngũ Thiên Thần của Ngài và trong nhân loại những cá thể làm các vị Thiên Sứ cho Ngài. Ngài cử phái một số vị Thiên Thần đến với các vị Nabi, chẳng hạn như Ngài cử đại Thiên Jibril đến với các vị Thiên Sứ người phạm, và cử các vị Thiên Sứ người phàm đến với nhân loại. Quả thật, Allah là Đấng Hằng Nghe những lời lẽ mà những kẻ thờ đa thần đã nói về các vị Thiên Sứ của Ngài, và Ngài là Đấng Hằng Thấy những ai được Ngài lựa chọn gánh vác sứ mạng mà Ngài giao phó.');
INSERT INTO "vn_mokhtasar" VALUES(22,76,'22:76','Allah biết từng hành động của các Thiên Thần, Ngài biết rõ những gì trước khi tạo ra con người và sau khi họ chết đi. Và vào Ngày Phục Sinh tất cả mọi việc đều được đưa về với Allah để Ngài phân xử và thưởng phạt cho những hành động mà họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(22,77,'22:77','Hỡi những người có đức tin nơi Allah và những ai thực hành theo giáo luật mà Ngài đã ban hành cho, các ngươi hãy cúi mình và quỳ lạy trong lễ nguyện Salah để thờ phượng Allah, và các ngươi hãy làm điều thiện tốt từ việc Sadaqah cho đến hàn gắn tình máu mủ ruột thịt, hy vọng rằng các ngươi sẽ thành công trong việc tìm kiếm sự ban thưởng và tránh được sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(22,78,'22:78','Và các ngươi hãy chiến đấu cho con đường chính nghĩa của Allah bằng lòng thành tâm, Ngài đã chọn các ngươi và làm cho tôn giáo của các ngươi thành tôn giáo khoan dung, đơn giản và không khắt khe. Tôn giao khoan dung này chính là tín ngưỡng của tổ phụ Ibrahim của các ngươi. Quả thật, Allah đã xí xóa và khoan dung cho những người Muslim trong các kinh sách trước và cả trong Qur''an mục đích để vị Thiên Sứ làm chứng cho các người rằng Người đã truyền tải bức Thông Điệp cho các người. Và để các người làm chứng cho các cộng đồng trước. Bởi thế, các ngươi hãy nhớ Allah về sự việc đó bằng cách chu đáo duy trì lễ nguyện Salah, đóng Zakah; các ngươi hãy tìm sự cứu rỗi nơi Alllah bởi Ngài là Đấng mang ân huệ to lớn cho những người có đức tin, Ngài sẽ ban cho thắng lợi và thành công.');
INSERT INTO "vn_mokhtasar" VALUES(23,1,'23:1','Chắc chắn những người có đức tin nơi Allah sẽ thành công. Họ luôn tuân thủ theo sắc lệnh của Ngài cho nên họ sẽ đạt được điều họ mong muốn và tránh được điều họ lo sợ.');
INSERT INTO "vn_mokhtasar" VALUES(23,2,'23:2','Những người luôn khúm núm lúc hành lễ Salah, cơ thể họ nghiêm trang và con tim của họ gạt bỏ mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(23,3,'23:3','Những người mà họ tránh xa mọi chuyện tầm phào, vô bổ và mọi lời nói, hành động khiến họ tội lỗi với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(23,4,'23:4','Những người mà họ biết tẩy sạch cơ thể mình khỏi sự keo kiệt và tẩy sạch tài sản của mình bằng cách xuất Zakah.');
INSERT INTO "vn_mokhtasar" VALUES(23,5,'23:5','Những người mà họ biết giữ phần kín của mình tránh xa Zina (tình dục ngoài hôn nhân), tình dục đồng giới và mọi điều dâm ô khác, họ là những người thanh khiết và đức hạnh.');
INSERT INTO "vn_mokhtasar" VALUES(23,6,'23:6','Ngoại trừ với các bà vợ hoặc với các nữ nô lệ của họ thì họ không bị bắt tội khi muốn gần gũi, ân ái.');
INSERT INTO "vn_mokhtasar" VALUES(23,7,'23:7','Cho nên, ai tìm cách ân ái với ai khác ngoài các bà vợ và các nữ nô lệ của y thì y đã vượt giới cấm của Allah, y đã chối bỏ cách hưởng thụ Halal (được phép) để đến với cách hưởng thụ Haram (bị cấm đoán).');
INSERT INTO "vn_mokhtasar" VALUES(23,8,'23:8','Những ai mà họ giữ những gì được Allah ủy thác hoặc những gì mà đám nô lệ của Ngài ủy thác; họ thực hiện đúng giao ước đã cam kết.');
INSERT INTO "vn_mokhtasar" VALUES(23,9,'23:9','Những ai luôn duy trì các lễ nguyện Salah, luôn thực hiện đúng giờ giấc, đầy đủ các nền tảng, các điều bắt buộc và các điều khuyến khích của Salah.');
INSERT INTO "vn_mokhtasar" VALUES(23,10,'23:10','Tất cả những ai có được những đức tính như đã mô tả sẽ là những người xứng đáng được thừa kế.');
INSERT INTO "vn_mokhtasar" VALUES(23,11,'23:11','Họ sẽ được thừa hưởng Thiên Đàng tầng cao nhất, họ ở mãi mãi trong đó và sự hưởng thụ không bao giờ dứt.');
INSERT INTO "vn_mokhtasar" VALUES(23,12,'23:12','Và quả thật, TA (Allah) đã tạo tổ tiên loài người - Adam - từ đất được lấy khắp mọi nơi trên trái đất và trộn với nước để nhào nặn.');
INSERT INTO "vn_mokhtasar" VALUES(23,13,'23:13','Sau đó, TA tạo con cháu của Y tức con người từ tinh dịch được lưu trữ trong tử cung mãi đến khi sinh nở.');
INSERT INTO "vn_mokhtasar" VALUES(23,14,'23:14','Rồi Với tinh dịch được lưu trữ trong tử cung, TA tạo nó thành cục máu đỏ, rồi từ cục máu đỏ đó TA tạo tiếp thành thứ tựa như miếng thịt bị cắn, rồi từ miếng thịt đó TA tạo tiếp thành khúc xương cứng rắn, rồi TA bao phủ khúc xương đó bằng lớp thịt, rồi TA tạo ra một hình hài mới bằng cách thổi linh hồn vào nó, rồi cho y trào đời có cuộc sống. Thật phúc thay Allah, quả là một qui trình tạo hóa ưu việt.');
INSERT INTO "vn_mokhtasar" VALUES(23,15,'23:15','Rồi các ngươi - hỡi nhân loại - sau khi trải qua các quá trình hình thành và phát triển thành con người hoàn chỉnh thì các ngươi sẽ chết khi tuổi thọ đã hết.');
INSERT INTO "vn_mokhtasar" VALUES(23,16,'23:16','Rồi sau khi các ngươi chết các ngươi bị phục sinh sống lại từ cõi mộ trong Ngày Tận Thế để chịu sự thanh toán về tất cả mọi việc mà các người đã từng làm trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(23,17,'23:17','Và quả thật, TA (Allah) đã tạo hóa bên trên các ngươi - hỡi nhân loại - bảy tầng trời chất chồng lên nhau, TA chẳng bao giờ hời hợt, xao lãng những gì TA đã tạo hóa.');
INSERT INTO "vn_mokhtasar" VALUES(23,18,'23:18','Và TA (Allah) đã trút nước mưa từ trời xuống theo mức lượng phù hợp, không quá nhiều gây lũ lụt và không quá ít không đủ nước dùng cho các ngươi, rồi TA lưu trữ lượng nước đó trong đất để giúp ích con người và động thực vật, và chắc chắn TA thừa khả năng đẩy trôi hết lượng nước đó và các ngươi không hưởng được gì.');
INSERT INTO "vn_mokhtasar" VALUES(23,19,'23:19','Với nguồn nước mưa đó, TA đã ban cho các ngươi vườn chà là, vườn nho, từ đó chúng cho ra biết bao cây trái đủ hình dạng, màu sắc như sung, lựu, táo để các ngươi dùng.');
INSERT INTO "vn_mokhtasar" VALUES(23,20,'23:20','Và TA đã làm mọc lên cho các ngươi giống cây ô liu tại vùng núi Saina'' và từ nó các ngươi thu hoạch được dầu để thoa và ăn.');
INSERT INTO "vn_mokhtasar" VALUES(23,21,'23:21','Và qua các loài gia súc như lạc đà, bò, cừu, dê... giúp các ngươi - hỡi con người - thu thập được nhiều bằng chứng khẳng định khả năng và lòng tử tế của Allah dành cho các ngươi. TA đã cung cấp thức uống từ bụng của các loài thú nuôi này cho các ngươi, đó là loại sữa tinh khiết làm ngon miệng người uống, ngoài ra các ngươi còn hưởng được biết bao điều lợi của chúng như cưỡi, lấy lông, da và ăn thịt của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(23,22,'23:22','Và trên cạn các ngươi dùng lạc đà và trên biển các ngươi dùng tàu bè để chuyên chở.');
INSERT INTO "vn_mokhtasar" VALUES(23,23,'23:23','Và trước đây, TA đã gởi Nuh đến với dân chúng của Y để kêu gọi họ trở về với Allah, Y nói với họ: "Hỡi người dân của Ta, các ngươi hãy thờ phượng một mình Allah thôi, ngoài Ngài các ngươi không có một Thượng Đế đích thực nào khác, thế sao các ngươi vẫn chưa biết sợ Ngài mà thực thi theo mệnh lệnh của Ngài đã sai bảo và tránh xa những điều Ngài ngăn cấm?!"');
INSERT INTO "vn_mokhtasar" VALUES(23,24,'23:24','Những tên thượng lưu và những tên lãnh đạo vốn phủ nhận Allah trong đám dân của Nuh tuyên bố trước công chúng: "Đây là ai mà Y dám tự nhận mình là Thiên Sứ, chẳng phải Y là một người phàm như các người hay sao, chắc Y muốn lên nắm quyền thống trị các người, bởi nếu Allah muốn thì Ngài đã gởi đến chúng ta những vị Thiên Thần mất rồi, chứ không gởi đến một người phàm như thế này đâu, điều này chúng ta chưa từng nghe tổ tiên của chúng ta nhắc đến bao giờ."');
INSERT INTO "vn_mokhtasar" VALUES(23,25,'23:25','Và đây là một người đàn ông bị thần kinh, Y không ý thức được điều mình đang nói, mọi người hãy chờ xem thực hư về Y sẽ được sáng tỏ.');
INSERT INTO "vn_mokhtasar" VALUES(23,26,'23:26','Nuh khấn vái: "Lạy Thượng Đế của bề tôi, xin hãy phù hộ bề tôi trừng phạt họ vì họ đã phủ nhận bề tôi."');
INSERT INTO "vn_mokhtasar" VALUES(23,27,'23:27','Thế là TA đã thiên khải cho Y: Ngươi hãy đóng một chiếc tàu theo hướng dẫn và giám sát của TA. Đến khi TA ra lệnh trừng phạt bọn chúng thì có một dấu hiệu báo trước cho Ngươi, đó là nguồn nước phun trào mạnh từ bếp lò, lúc đó Ngươi hãy mang lên tàu mỗi loài động vật một cặp đực cái để chúng duy trì nồi giống, cùng với gia đình Ngươi, ngoại trừ những ai đã bị TA báo trước sẽ trừng phạt chúng chẳng hạn như vợ và con trai của Ngươi. Khi ấy, Ngươi đừng van xin TA tha mạng cho nhóm người ngoại đạo, bạo ngược, chắc chắn chúng sẽ bị nhấn chìm trong trận đại hồng thủy không thể tránh khỏi.');
INSERT INTO "vn_mokhtasar" VALUES(23,28,'23:28','Đến khi Ngươi và nhóm người có đức tin theo Ngươi lên tàu an toàn, các ngươi hãy nói: Alhamdulillah - tức xin tạ ơn Allah - Đấng đã cứu bầy tôi thoát khỏi đám người ngoại đạo mà Ngài đã tiêu diệt họ.');
INSERT INTO "vn_mokhtasar" VALUES(23,29,'23:29','Và Ngươi (Nuh) hãy cầu nguyện: "Lạy Thượng Đế của bề tôi, xin cho bề tôi cập được một bến đổ hồng phúc và Ngài là Đấng ban cho bến đổ tốt nhất."');
INSERT INTO "vn_mokhtasar" VALUES(23,30,'23:30','Quả thật, qua hình ảnh Nuh và nhóm người có đức tin cùng với Y được cứu rỗi bình an và việc nhấn chìm nhóm người vô đức tin là trong những bằng chứng khẳng định Quyền Năng vô song của Allah trong việc sẵn sàn phù hộ giới Thiên Sứ của Ngài và tiêu diệt hết nhóm người phủ nhận sứ mạng của Họ. Và chính TA đã thử thách dân chúng của Nuh qua việc gởi Người đến với họ để Ngài phân biệt rõ ai thuộc nhóm người có đức tin và ai thuộc nhóm người vô đức tin; ai vâng lời và ai bất tuân.');
INSERT INTO "vn_mokhtasar" VALUES(23,31,'23:31','Sau khi đã tiêu diệt hết dân chúng của Nuh, TA đã tạo ra một thế hệ mới.');
INSERT INTO "vn_mokhtasar" VALUES(23,32,'23:32','Rồi TA cũng chọn một người trong họ làm Thiên Sứ đứng lên kêu gọi họ trở về với Allah, vị Thiên Sứ đó bảo: "Các ngươi hãy thờ phượng Allah duy nhất, ngoài Ngài các ngươi sẽ không có Thượng Đế đích thực nào khác, thế sao các ngươi vẫn chưa biết sợ Ngài mà thực thi theo mệnh lệnh của Ngài đã sai bảo cũng như tránh xa những gì Ngài ngăn cấm?!"');
INSERT INTO "vn_mokhtasar" VALUES(23,33,'23:33','Và nhóm người thượng lưu và lãnh đạo trong đám dân của Y vốn phủ nhận đức tin nơi Allah, bác bỏ việc bị phục sinh và mọi thứ liên quan đến sự thanh toán, thưởng phạt bằng sự cao ngạo bởi sự giàu có, quyền lực trong cuộc sống trần gian lên tiếng trước công chúng: "Há tên này không phải là người phàm như mọi người sao? Y ăn uống giống như các người ăn uống, Y không có gì nổi trội hơn các người để y được chọn làm Thiên Sứ được cử đến với các người."');
INSERT INTO "vn_mokhtasar" VALUES(23,34,'23:34','Và nếu các ngươi nghe lời tên người phàm giống như các ngươi thì điều đó sẽ khiến các ngươi trở thành đám thất bại, việc các ngươi tuân lệnh Y và ruồng bỏ thần linh của mình sẽ không giúp ích được gì cho các ngươi cả, trong khi ở nơi Y không có điều gì tốt hơn các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(23,35,'23:35','Có phải tên này khẳng định với mọi người rằng Y là Thiên Sứ và cho mọi người biết sau khi mọi người chết thành cát bụi và đóng xương khô thì mọi người lại được phục sinh sống trở lại từ cõi mộ đúng không? Mọi người nghĩ việc này có thật ư?');
INSERT INTO "vn_mokhtasar" VALUES(23,36,'23:36','Thật viển vông cho những gì mà Y đã hứa rằng mọi người sẽ sống lại từ cõi mộ sau khi đã biến thành cát bụi và xương khô.');
INSERT INTO "vn_mokhtasar" VALUES(23,37,'23:37','Không có cuộc sống nào ngoài cuộc sống ở trần gian này, không có cuộc sống Đời Sau, người sống trong chúng ta sẽ chết chứ không có việc sống trở lại, trẻ con được sinh ra tiếp tục sống kế thừa thế hệ trước và chúng ta không hề bị phục sinh để chịu sự thanh toán sau khi chết vào Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(23,38,'23:38','Đây chỉ là kẻ mạo danh Thiên Sứ được gởi đến cho mọi người, Y vốn là một người phàm bịa ra chuyện rồi vu khống cho Allah nên chúng ta đừng tin điều Y nói.');
INSERT INTO "vn_mokhtasar" VALUES(23,39,'23:39','Thiên Sứ cầu nguyện: "Lạy Thượng Đế của bề tôi, xin Ngài hãy giúp bề tôi trừng phạt họ vì họ đã phủ nhận bề tôi."');
INSERT INTO "vn_mokhtasar" VALUES(23,40,'23:40','Allah trả lời Nuh: Chỉ với một thời gian ngắn nữa thôi, đám người phủ nhận điều Ngươi mang đến sẽ ân hận về hành động bác bỏ này của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(23,41,'23:41','Rồi bọn chúng bị một âm thanh chát chúa túm lấy tiêu diệt chúng bằng hình phạt thích đáng, chúng bị biến thành thứ như đống bọt biển, thật khốn nạn thay cho đám người sai quấy.');
INSERT INTO "vn_mokhtasar" VALUES(23,42,'23:42','Sau khi bọn chungs bị tiêu diệt thì TA (Allah) đã tạo ra một nhóm người khác giống như cộng đồng của Lut, cộng đồng của Shu''aib và cộng đồng của Yunus.');
INSERT INTO "vn_mokhtasar" VALUES(23,43,'23:43','Không một cộng đồng nào trong số những cộng đồng phủ nhận Chân Lý có thể thúc sự trừng phạt sớm hoặc trễ hơn thời hạn đã ấn định cho nó dù họ có cố gắng ra sao.');
INSERT INTO "vn_mokhtasar" VALUES(23,44,'23:44','Sau đó, TA đã không ngừng việc cử phái các Thiên Sứ của TA đến với họ hết lần này đến lần khác, nhưng bất cứ Thiên Sứ nào được TA gởi đến đều bị họ phủ nhận. Cho nên, TA lần lượt tiêu diệt hết nhóm này rồi đến nhóm kia. Giờ đây, họ chỉ còn mỗi những mẫu chuyện được lưu truyền. Đó là hậu quả của những những kẻ không vô đức tin nơi sứ mạng của những Thiên Sứ mà Thượng Đế gởi đến cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(23,45,'23:45','Sau đó, TA gởi hai anh em Musa và Harun mang theo chín dấu hiệu: (gồm cây gậy, bàn tay, nạn châu chấu, nạn rận, nạn ếch nhái, nạn máu, lũ lụt, hạn hán và mất mùa) với những bằng chứng rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(23,46,'23:46','TA phái hai anh em Y đến gặp Fir''awn và đám quân thần của hắn nhưng chúng tỏ ra hống hách, không chịu tin vào lời nói của hai người họ, chúng vốn là đám bạo ngược đàn áp thiên hạ.');
INSERT INTO "vn_mokhtasar" VALUES(23,47,'23:47','Chúng nói: "Lẽ nào chúng ta lại tin tưởng vào hai tên người phàm như chúng ta trong khi hai tên đó không có gì trội hơn chúng ta, vả lại dân chúng của hai tên đó (dân Israel) lại là đám người tôi tớ phục vụ cho chúng ta."');
INSERT INTO "vn_mokhtasar" VALUES(23,48,'23:48','Thế là họ đã phủ nhận những gì mà hai anh em Musa, Harun mang đến từ Allah, với lý do đó của họ khiến họ bị tiêu diệt.');
INSERT INTO "vn_mokhtasar" VALUES(23,49,'23:49','Và TA đã ban cho Musa Kinh Tawrah (Cựu Ước) với hi vọng dân chúng của Y dựa vào Nó để đến với Chân Lý và làm theo.');
INSERT INTO "vn_mokhtasar" VALUES(23,50,'23:50','Và TA đã làm cho Ysa và mẹ của Y, Maryam thành hiện tượng lạ chứng minh quyền năng của TA, rằng TA đã cho nữ ta mang thai không cần đến nam giới, và TA đã ban cho hai mẹ con họ ở trên vùng đất nhô cao có cuộc sống an lành, nơi đó có dòng nước chảy luôn tươi mới.');
INSERT INTO "vn_mokhtasar" VALUES(23,51,'23:51','Hỡi các Thiên Sứ, các ngươi hãy ăn uống mọi thức ăn Halal thuộc các loại thức ăn mà các ngươi ưa thích, các ngươi hãy hành đạo đúng theo luật lệ giáo lý, quả thật, TA luôn tận tường mọi việc mà các ngươi làm, không một hành động nào của các ngươi che giấu được TA.');
INSERT INTO "vn_mokhtasar" VALUES(23,52,'23:52','Và quả thật, cộng đồng của các ngươi - hỡi các Thiên Sứ - vốn là một cộng đồng duy nhất, đó là Islam và TA là Thượng Đế của các ngươi, ngoài TA không có Thượng Đế nào khác cả. Do đó các ngươi hãy kính sợ TA mà thực hiện theo mọi lệnh TA truyền cũng như tránh xa những gì TA cấm.');
INSERT INTO "vn_mokhtasar" VALUES(23,53,'23:53','Những người theo Họ (các Thiên Sứ) đã chia rẽ trong tôn giáo để tự thành lập giáo phái riêng, mỗi nhóm tự mãn cho rằng nhóm mình là nhóm được Allah hài lòng và chúng không thèm ngó ngàng gì đến các giáo phái khác.');
INSERT INTO "vn_mokhtasar" VALUES(23,54,'23:54','Vậy nên Ngươi - hỡi Thiên Sứ - cứ mặc chúng chìm trong ngu muội và lầm lạc đến khi hình phạt giáng xuống chúng.');
INSERT INTO "vn_mokhtasar" VALUES(23,55,'23:55','Phải chăng các nhóm phái này mừng rỡ khi chúng được TA ban cho tài sản và con cái trong cuộc sống trần gian này vì chúng xứng đáng được hưởng điều tốt ư? Sự việc không giống như chúng nghĩ. TA ban cho chúng chẳng qua là vì muốn gieo rắc cám dỗ cho chúng, nhưng chúng không cảm thấy điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(23,56,'23:56','Phải chăng các nhóm phái này mừng rỡ khi chúng được TA ban cho tài sản và con cái trong cuộc sống trần gian này vì chúng xứng đáng được hưởng điều tốt ư? Sự việc không giống như chúng nghĩ. TA ban cho chúng chẳng qua là vì muốn gieo rắc cám dỗ cho chúng, nhưng chúng không cảm thấy điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(23,57,'23:57','Quả thật, chỉ có những người có đức tin Iman và sùng đạo mới biết kính sợ Thượng Đế họ.');
INSERT INTO "vn_mokhtasar" VALUES(23,58,'23:58','Và những ai mà họ đã tin nơi các câu trong Kinh Sách (Qur''an).');
INSERT INTO "vn_mokhtasar" VALUES(23,59,'23:59','Và những ai mà họ chỉ thờ phượng riêng Allah, không tổ hợp đối tác ngang hàng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(23,60,'23:60','Và những ai mà họ cố gắng hành đạo, cố gắng dâng lên Allah nhiều việc ngoan đạo khác và tấm lòng của họ luôn lo sợ cho việc bố thí cũng như mọi việc hành đạo khác của họ không được Allah chấp nhận khi họ trở về trình diện Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(23,61,'23:61','Với những người được mô tả bởi những thuộc tính vĩ đại đó là nhóm người nỗ lực thi đua làm việc thiện, và là nhóm người tiên phong.');
INSERT INTO "vn_mokhtasar" VALUES(23,62,'23:62','Và TA không bao giờ ép một ai đảm nhận công việc quá sức, ở nơi TA có quyển số ghi chép đầy đủ mọi điều đã làm, đó là sự thật, họ không phải sợ bị đối xử bất công: mất đi ân phước và bị tội oan.');
INSERT INTO "vn_mokhtasar" VALUES(23,63,'23:63','Không, những con tim của những kẻ vô đức tin đang dửng dưng với Qur''an, Kinh Sách mà Nó nói điều chân lý, và một Kinh Sách được thiên khải cho chúng và chúng còn có những hành động vô đức tin khác nữa.');
INSERT INTO "vn_mokhtasar" VALUES(23,64,'23:64','Mãi khi TA (Allah) trừng phạt những kẻ hưởng lạc trong cuộc sống trần gian bằng hình phạt ở Đời Sau thì chúng hô la van xin tha thứ.');
INSERT INTO "vn_mokhtasar" VALUES(23,65,'23:65','Chúng được đáp lại bằng một lời khiến chúng tuyệt vọng khỏi lòng thương xót của Allah: Ngày hôm nay, các ngươi chớ gào thét, khấn vái làm gì, chắc chắn không còn ai dám đứng ra giúp đỡ các ngươi thoát khỏi hình phạt của Allah đâu.');
INSERT INTO "vn_mokhtasar" VALUES(23,66,'23:66','Quả thật, những lời phán trong Kinh Sách của Allah được xướng đọc cho các ngươi nghe ở trần gian, vậy mà các ngươi vẫn ngoảnh mặt không thèm nghe.');
INSERT INTO "vn_mokhtasar" VALUES(23,67,'23:67','Các ngươi tỏ ra tự cao trước thiên hạ cho rằng mình sẽ không thất bại. Không đâu, bởi chỉ có người kính sợ mới được Allah bảo vệ; và các ngươi thức thâu đêm để nói chuyện tầm phào nên các ngươi không phải là nhóm cao quí gì.');
INSERT INTO "vn_mokhtasar" VALUES(23,68,'23:68','Tại sao những kẻ đa thần không chịu suy nghĩ về lời phán mà Allah đã thiên khải trong Qur''an để chúng có đức tin và phục tùng theo Nó, hay phải chăng do tổ tiên của chúng trước đây không nhận được điều tương tự nên chúng mới phủ nhận Nó.');
INSERT INTO "vn_mokhtasar" VALUES(23,69,'23:69','Phải chăng chúng không nhận ra Muhammad, vị Thiên Sứ được Allah gởi đến cho chúng, nên chúng phủ nhận Y. Không đâu, Y biết rõ chúng và chúng biết rõ bản tính trung thực và uy tín của Y.');
INSERT INTO "vn_mokhtasar" VALUES(23,70,'23:70','Không, chúng bảo: Y là tên điên nên chúng phủ nhận Y. Không, Y đã mang Chân Lý đến cho chúng theo lệnh của Allah nhưng chỉ vì đa số bọn chúng ghét sự thật, chúng ganh tỵ trong lòng nên mù quáng chiều theo ham muốn của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(23,71,'23:71','Và nếu Allah chiều lòng bọn chúng, khiến mọi việc theo ý của chúng thì chắc chắn trời đất sẽ bị diệt vong, và tất cả mọi thứ trong trời đất cũng sẽ bị diệt vong bởi hậu quả của việc chúng không biết đâu là đúng và đâu là sai.');
INSERT INTO "vn_mokhtasar" VALUES(23,72,'23:72','Hay là Ngươi - hỡi Thiên Sứ - đã yêu cầu bọn chúng tiền thù lao cho những gì Ngươi mang đến cho chúng nên đã khiến chúng từ chối lời mời gọi của Ngươi? Điều này không xảy ra ở Ngươi vì phần thưởng và sự trả công của Thượng Đế Ngươi tốt đẹp hơn phần thưởng của chúng và những ai khác chúng, bởi Ngài - Đấng Vinh Quang - là Đấng Ban Phát tốt nhất.');
INSERT INTO "vn_mokhtasar" VALUES(23,73,'23:73','Và Ngươi - hỡi Thiên Sứ - thực sự luôn mời gọi chúng và những ai khác ngoài chúng đến với Con Đường Ngay Thẳng, không có sự uốn khúc và cong quẹo, đó chính là con đường Islam.');
INSERT INTO "vn_mokhtasar" VALUES(23,74,'23:74','Và chắc chắn những ai không tin tưởng Đời Sau và những sự kiện ở nơi đó từ việc tính sổ, thưởng phạt thì họ đã đi lệch khỏi Islam để đến với con đường uốn khúc, công quẹo dẫn lối tới Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(23,75,'23:75','Và nếu TA tội nghiệp mà giúp chúng vượt khỏi gian nan cực khổ thì bọn chúng vẫn sa vào lầm lạc, nảy sinh ngờ vực mà xa lánh Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(23,76,'23:76','Và quả thật, TA đã thử thách chúng biết bao tai nạn nhưng chúng vẫn ngoan cố không chịu hạ mình, không khúm núm van xin Ngài lấy khỏi chúng mọi tai nạn giáng xuống chúng.');
INSERT INTO "vn_mokhtasar" VALUES(23,77,'23:77','Mãi khi TA mở cho chúng cánh cửa hình phạt khủng khiếp thì chúng mới không còn hy vọng đến bất cứ điều tốt và lối thoát nào nữa.');
INSERT INTO "vn_mokhtasar" VALUES(23,78,'23:78','Và Allah Hiển Vinh là Đấng đã tạo cho các ngươi - hỡi những kẻ phủ nhận sự phục sinh - đôi tai để nghe, đôi mắt để nhìn, con tim để suy nghĩ, nhưng các ngươi rất ít tri ân về những ân huệ đó của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(23,79,'23:79','Và Ngài đã tạo hóa ra các ngươi - hỡi con người - trên trái đất và các ngươi sẽ trở về trình diện riêng Ngài vào Ngày Tận Thế để Ngài thanh toán và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(23,80,'23:80','Và Ngài là Đấng duy nhất khiến cho các ngươi sống hay chết, ngoài Ngài không ai có khả năng đó, cũng chỉ Ngài quyết định sự luân chuyển của ban đêm và ban ngày, làm cho chúng nắng, mát, dài, ngắn. Thế các ngươi vẫn chưa suy nghĩ về quyền năng của Ngài và Ngài là Đấng duy nhất điều hành và chi phối.');
INSERT INTO "vn_mokhtasar" VALUES(23,81,'23:81','Không, chúng đã nói giống như điều mà tổ tiên vô đức tin trước đây của chúng đã nói.');
INSERT INTO "vn_mokhtasar" VALUES(23,82,'23:82','Chúng hỏi bằng giọng điệu phủ nhận và khinh khi: Sao, chúng tôi cũng như tổ tiên chúng tôi sẽ được sống trở lại sau khi thể xác chúng tôi đã tan biến thành cát bụi và xương khô ư, rằng chúng tôi sẽ được phục sinh để tính sổ ư?');
INSERT INTO "vn_mokhtasar" VALUES(23,83,'23:83','Chúng tôi từng được hứa điều này - sự phục sinh sau khi đã chết - và tổ tiên chúng tôi cũng được hứa như thế trước đây nhưng đến giờ chúng tôi vẫn chưa thấy điều đó xảy ra, đây chắc là những mẫu chuyện cổ tích, những câu chuyện lừa bịp đây mà.');
INSERT INTO "vn_mokhtasar" VALUES(23,84,'23:84','Ngươi - hỡi Thiên Sứ - hãy nói với những kẻ vô đức tin phủ nhận sự phục sinh: "Trái đất này và tất cả những gì trong nó là của ai, nếu các ngươi có kiến thức về điều đó?"');
INSERT INTO "vn_mokhtasar" VALUES(23,85,'23:85','Chắc chắn chúng sẽ nói: Trái đất và tất cả mọi thứ trong nó là của Allah." Thế Ngươi hãy tiếp tục hỏi chúng: "Hay là các ngươi nghĩ rằng Đấng sở hữu toàn trái đất và mọi thứ trong nó lại không có khả năng phục sinh các ngươi sống lại sau khi đã chết ư?"');
INSERT INTO "vn_mokhtasar" VALUES(23,86,'23:86','Ngươi hãy nói với chúng: "Ai mới là Thượng Đế của bảy tầng trời? Ai là Thượng Đế của chiếc Ngai Vương vĩ đại, chiếc Ngai Vương mà không có bất cứ tạo vật nào lớn hơn nó nữa?"');
INSERT INTO "vn_mokhtasar" VALUES(23,87,'23:87','Chắc chắn chúng sẽ nói rằng bảy tầng trời và chiếc ngai vương vĩ đại đều thuộc về Allah. Thế Ngươi hãy hỏi họ tiếp: Thế tại sao các ngươi vẫn không tuân phục theo mệnh lệnh của Ngài để được bình an khỏi hành phạt của Ngài?');
INSERT INTO "vn_mokhtasar" VALUES(23,88,'23:88','Ngươi hãy hỏi họ: Ai là Đấng nắm giữ trong tay tất cả vạn vật, không ai dám tranh giành vương quyền của Ngài về bất cứ thứ gì, Ngài muốn phù hộ ai tùy Ngài trong đám nô lệ của Ngài, cũng không ai ngăn cản được khi Ngài muốn y gặp phải tai nạn, nếu các ngươi biết được điều đó?');
INSERT INTO "vn_mokhtasar" VALUES(23,89,'23:89','Chắc chắc chúng sẽ đáp: Tất cả vương quyền đều trong tay của Ngài, Đấng Hiển Vinh. Ngươi hãy hỏi tiếp: Thế đầu óc của các ngươi đi đâu mất rồi khiến các ngươi phải thờ phượng ngoài Ngài trong khi các ngươi công nhận điều đó?');
INSERT INTO "vn_mokhtasar" VALUES(23,90,'23:90','Sự việc không giống như lời chúng nói. Quả thật TA đã mang đến cho chúng sự thật, không có gì phải ngờ vực cả, nhưng chính chúng là những kẻ nói dối, chúng vu khống Allah có cộng sự và con trai. Dĩ nhiên, Allah tối cao và vĩ đại hơn những điều chúng nói.');
INSERT INTO "vn_mokhtasar" VALUES(23,91,'23:91','Allah không chọn ai làm con trai như nhóm người ngoại đạo đã tuyên bố, cũng không có một thần linh nào làm cộng sự cùng Ngài. Giả sử nếu có thần linh nào đó cộng sự cùng Ngài thì chắc chắn mỗi thần linh đã ôm lấy những gì mình đã tạo hóa, lúc đó chắc sẽ có sự tranh giành quyền lực dẫn đến sự bất ổn của vũ trụ. Và thực tế điều đó không hề xảy ra, điều đó khẳng định rằng không có Thượng Đế nào đích thực ngoại trừ Allah duy nhất, Ngài vô can và trong sạch với những gì những kẻ đa thần đã mô tả về Ngài rằng Ngài có con và cộng sự.');
INSERT INTO "vn_mokhtasar" VALUES(23,92,'23:92','Ngài tinh thông tất cả mọi điều mà con người có thể cảm nhận được bằng các giác quan hoặc không thể cảm nhận được, không điều gì giấu được Ngài. Ngài tối cao và vĩ đại hơn việc Ngài có cộng sự.');
INSERT INTO "vn_mokhtasar" VALUES(23,93,'23:93','Ngươi - hỡi Thiên Sứ - hay cầu nguyện: Lạy Thượng Đế của bề tôi, hoặc là Ngài cho bề tôi tận mắt nhìn thấy những hình phạt mà Ngài đã chuẩn bị cho đám người đa thần.');
INSERT INTO "vn_mokhtasar" VALUES(23,94,'23:94','Lạy Thượng Đế của bề tôi, xin đừng nhập bề tôi cùng với đám người sai lạc để rồi bề tôi phải chịu hình phạt khi Ngài giáng xuống cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(23,95,'23:95','Và chắc chắn rằng TA thừa khả năng cho Ngươi tận mắt nhìn thấy hình phạt mà TA đã chuẩn bị cho bọn chúng, TA không bất lực trong sự việc đó cũng như các sự việc khác.');
INSERT INTO "vn_mokhtasar" VALUES(23,96,'23:96','Ngươi - hỡi Thiên Sứ - hãy đáp trả đám người xấu xa bằng thứ tốt đẹp hơn, hãy lượng thứ bỏ qua, hãy nhẫn nại về điều chúng hãm hại, TA biết rõ điều chúng miêu tả và phủ nhận và mọi lời lẽ khiếm nhã mà chúng đã xúc phạm đến Ngươi như việc chúng gọi Ngươi là tên phù thủy, kẻ điên.');
INSERT INTO "vn_mokhtasar" VALUES(23,97,'23:97','Và Ngươi hãy khẩn cầu: Lạy Thượng Đế của bề tôi, xin được bám chặt lấy Ngài để tránh được mọi cám dỗ và xúi giục của lũ Shaytan.');
INSERT INTO "vn_mokhtasar" VALUES(23,98,'23:98','Và bề tôi cầu xin được Ngài che chở không cho chúng đến gần cuộc sống của bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(23,99,'23:99','Mãi đến khi một người trong nhóm đa thần này đối diện cái chết, tận mắt chứng kiến những gì đang phơi bày cho y thấy thì y hối hận việc đánh mất tuổi đời đã không chịu phục tùng Allah, y van xin: Lạy Thượng Đế của bề tôi, xin cho bề tôi được trở về cuộc sống trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(23,100,'23:100','Hi vọng bề tôi sẽ hành đạo khi bề tôi được trở về đó. Không đâu, sự việc không như những gì được cầu xin, đó chỉ là lời nói trên môi, dù hắn được trở về cuộc sống trần gian thì hắn vẫn không thực hiện lời đã hứa, hắn sẽ tiếp tục kết bè lũ với đám người chia cắt trần gian và Đời Sau cho đến Ngày Phục Sinh. Chúng chẳng có cơ hội trở lại trần gian để đạt được những gì đã bỏ lỡ và cải thiện những gì đã phá hủy.');
INSERT INTO "vn_mokhtasar" VALUES(23,101,'23:101','Đến khi Thiên Thần thổi còi được lệnh cất tiếng còi từ một chiếc sừng ở lần thứ hai để thông báo giờ tận thế, lúc đó giữa con người không còn bất cứ mối quan hệ máu mủ nào nữa bởi họ bận lòng lo sợ về sự kiện kinh hoàng của Ngày Tận Thế, không ai hỏi thăm ai vì họ luôn bận tâm cho bản thân mình.');
INSERT INTO "vn_mokhtasar" VALUES(23,102,'23:102','Thế nên bàn cân công đức của ai được nặng hơn bàn cân tội lỗi thì y thuộc nhóm người chiến thắng, đạt được nguyện vọng của bản thân và y không còn phải lo lắng cho điều gì nữa.');
INSERT INTO "vn_mokhtasar" VALUES(23,103,'23:103','Và ai có bàn cân công đức nhẹ hơn bàn cân tội lỗi thì y thuộc nhóm người tự gây hại bản thân mình bằng những hành động đối nghịch những điều khiến họ có niềm tin Iman và ngoan đạo, thế là họ phải cư ngụ trong lửa của Hỏa Ngục, không được rời khỏi Nó.');
INSERT INTO "vn_mokhtasar" VALUES(23,104,'23:104','Gương mặt của chúng bị thiêu đốt trong lửa, chúng sẽ nhăn nhó nhe hai hàm răng ra.');
INSERT INTO "vn_mokhtasar" VALUES(23,105,'23:105','Chúng được bảo một cách mỉa mai: chẳng phải khi các câu Kinh của TA đã được xướng đọc cho các ngươi nghe lúc ở trần gian thì các ngươi đã bác bỏ Nó đó sao?!');
INSERT INTO "vn_mokhtasar" VALUES(23,106,'23:106','Họ bào chữa: Lạy Thượng Đế của bầy tôi, như Ngài đã biết rõ số phận đã chiến thắng bầy tôi nên bầy tôi mới bị lệch khỏi Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(23,107,'23:107','Lạy Thượng Đế của bầy tôi, xin cứu bầy tôi khỏi Hỏa Ngục này, nếu bầy tôi còn trở lại tình trạng vô đức tin và lầm lạc như trước đây thì chính bầy tôi đã bất công với bản thân mình, lúc đó không còn gì để biện minh nữa.');
INSERT INTO "vn_mokhtasar" VALUES(23,108,'23:108','Allah phán: Câm miệng ngay hỡi những kẻ hèn hạ, các ngươi hãy cút vào trong Hỏa Ngục, chớ bẩm thưa gì với TA nữa.');
INSERT INTO "vn_mokhtasar" VALUES(23,109,'23:109','Rằng trước đây có một nhóm nô lệ của TA đã tin tưởng nơi TA, họ nguyện cầu: Lạy Thượng Đế của bầy tôi, bầy tôi đã tin tưởng nơi Ngài, xin Ngài hãy tha thứ tội lỗi cho bầy tôi, xin thương xót bầy tôi bằng lòng thương xót của Ngài bởi Ngài là Đấng Nhân Từ nhất.');
INSERT INTO "vn_mokhtasar" VALUES(23,110,'23:110','Thế nhưng các ngươi chọn cách lấy nhóm người có đức tin hướng lòng cầu xin Thượng Đế ra giễu cợt, chế nhạo. Các ngươi chỉ lo nhạo báng họ khiến các ngươi quên mất lời nhắc nhở của Allah và càng lúc các ngươi càng lún sâu hơn trong việc giễu cợt và nhạo báng đó.');
INSERT INTO "vn_mokhtasar" VALUES(23,111,'23:111','Trong Ngày Tận Thế, chính TA sẽ đặc biệt ban thưởng cho những người có đức tin này, họ đã chiến thắng được Thiên Đàng bởi vì họ đã kiên nhẫn phục tùng Allah và kiên nhẫn trước bao khổ ải đã qua.');
INSERT INTO "vn_mokhtasar" VALUES(23,112,'23:112','Allah hỏi: Các ngươi đã sống trên đời bao lâu? và các người đã đánh mất bao nhiêu thời gian?');
INSERT INTO "vn_mokhtasar" VALUES(23,113,'23:113','Họ đồng đáp lại bằng câu khẳng định rằng bầy tôi chỉ ở trên đó một ngày hoặc vài giờ gì đó thôi, xin hỏi những ai đã ghi chép ngày tháng.');
INSERT INTO "vn_mokhtasar" VALUES(23,114,'23:114','Allah đáp: Các ngươi chỉ ở trên trần gian một thời khắc rất ít rất dễ để các ngươi kiên nhẫn trong việc phục tùng và nghe lời, giá như các ngươi biết được khoảng thời gian mà các ngươi đã sống.');
INSERT INTO "vn_mokhtasar" VALUES(23,115,'23:115','Hay là các ngươi - hỡi con người - nghĩ là TA chỉ tạo hóa các ngươi ra chỉ để vui chơi không mang một mục đích nào cả, không thưởng cũng không phạt giống như loài súc vật ư? Và rằng các ngươi cũng không trở về trình diện TA để chịu sự thưởng phạt vào Ngày Phán Xét ư?');
INSERT INTO "vn_mokhtasar" VALUES(23,116,'23:116','Hoàn toàn không liên quan gì đến Allah, Đấng Chúa Tể chi phối vạn vật như thế nào tùy Ngài muốn, đó là Chân Lý, lời hứa của Ngài là sự thật, lời phán của Ngài là sự thật, không có Thượng Đế đích thực nào ngoài Ngài, Đấng Chủ Nhân của chiếc ngai vương vinh dự, một tạo vật vĩ đại nhất trong các tạo vật của Ngài, và Ngài là Đấng Chúa Tể vĩ đại nhất.');
INSERT INTO "vn_mokhtasar" VALUES(23,117,'23:117','Và ai cầu xin cùng với Allah một thần linh khác không có bằng chứng cơ sở xác định thần linh đó đáng được thờ phượng (đây là tình trạng chung cho tất cả thần linh, con người, cây cối... được thờ phượng ngoài Allah) thì việc thanh toán y dành riêng cho Thượng Đế y, Ngài sẽ thưởng chúng một hình phạt thích đáng, quả thật Allah không hề phù hộ những kẻ vô đức tin đạt được ý nguyện và chúng không bình an thoát khỏi điều chúng lo sợ.');
INSERT INTO "vn_mokhtasar" VALUES(23,118,'23:118','hỡi Thiên Sứ, Ngươi hãy nói: Lạy Thượng Đế, xin tha thứ tội lỗi cho bề tôi, xin thương xót bề tôi bằng lòng thương xót của Ngài bởi Ngài là Đấng nhân từ đối với tất cả ai phạm tội, hãy chấp nhận sự sám hối của bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(24,1,'24:1','Đây là chương Kinh được TA thiên khải xuống bắt buộc các ngươi phải thực thi theo các giáo lý trong Nó, TA đã ban xuống những câu Kinh rõ ràng minh bạch; hy vọng các ngươi khắc ghi các giáo lý đó để áp dụng theo.');
INSERT INTO "vn_mokhtasar" VALUES(24,2,'24:2','Khi đôi trai gái độc thân phạm Zina (quan hệ tình dục ngoài hôn nhân) thì các ngươi hãy đánh mỗi người một trăm roi. Nếu các ngươi thật sự tin tưởng Allah và vào Đời Sau thì các ngươi chớ nhân từ với họ để rồi không thi hành án hoặc giảm nhẹ án hình cho họ. Và lúc thi hành án phải có sự chứng kiến của một nhóm người có đức tin để làm gương cho chính họ và cho người khác.');
INSERT INTO "vn_mokhtasar" VALUES(24,3,'24:3','Allah kinh tởm hành động Zina vì người Zina không muốn kết hôn mà chỉ muốn quan hệ tình dục bừa bãi với người có sở thích Zina giống họ hoặc với người đa thần mặc dù không được kết hôn với họ, vì thế Allah cấm những người có đức tin nam nữ kết hôn với người Zina.');
INSERT INTO "vn_mokhtasar" VALUES(24,4,'24:4','Và những ai vu khống những phụ nữ tiết hạnh (hoặc những người nam đứng đắn) phạm Zina nhưng lại không đưa ra đủ bốn nhân chứng cho lời cáo buộc của mình thì các ngươi hãy đánh họ - hỡi những người cầm quyền - mỗi tên tám mươi roi. Sau đó, các ngươi không chấp nhận bất cứ nhân chứng nào từ họ đưa ra nữa, bởi họ là đám người vu khống những người thanh sạch, chúng là những kẻ bất tuân Allah.');
INSERT INTO "vn_mokhtasar" VALUES(24,5,'24:5','Ngoại trừ những ai biết hối cải sau khi phạm điều đó và năng nỗ hành đạo, họ sẽ được Allah chấp nhận sự sám hối và sẽ được chấp nhận làm nhân chứng trở lại. Quả thật, Allah rất nhân từ khi nô lệ của Ngài biết hối cải và Ngài luôn thương xót họ.');
INSERT INTO "vn_mokhtasar" VALUES(24,6,'24:6','Và những người đàn ông buộc tội vợ mình ngoại tình, nhưng họ không có nhân chứng xác định lời buộc tội đó; bắt buộc mỗi người trong số họ phải thề bốn lần nhân danh Allah rằng y là người nói thật với lời buộc tội rằng vợ đã ngoại tình.');
INSERT INTO "vn_mokhtasar" VALUES(24,7,'24:7','Sau đó, y thề tiếp lần thứ năm nhấn mạnh bản thân đáng bị nguyền rủa nếu lời buộc tội của mình là vu khống.');
INSERT INTO "vn_mokhtasar" VALUES(24,8,'24:8','Các lời thề của chồng đủ chứng cứ trừng phạt người vợ theo luật Zina, tuy nhiên, để thoát khỏi hình phạt thì người vợ phải thề bốn lần nhân danh Allah rằng lời buộc tội của chồng là lời vu khống.');
INSERT INTO "vn_mokhtasar" VALUES(24,9,'24:9','Sau đó, người vợ thề tiếp lần thứ năm cầu xin Allah trút cơn giận lên bản thân mình nếu lời buộc tội của chồng là thật.');
INSERT INTO "vn_mokhtasar" VALUES(24,10,'24:10','Và nếu như Allah không nhân từ, không khoan hồng cho các ngươi - hỡi con người - vả lại, Ngài sẵn sàng tha thứ cho bất cứ ai biết hối cải cũng như rất sáng suốt trong quản lý và qui định thì Ngài đã trừng phạt các ngươi do tội lỗi của các ngươi và việc các ngươi công khai phạm tội.');
INSERT INTO "vn_mokhtasar" VALUES(24,11,'24:11','Quả thật, những kẻ vu khống (đã buộc tội mẹ của những người có đức tin, bà ''A-ishah, ngoại tình) là một nhóm người thuộc các ngươi - hỡi những người có đức tin. Các ngươi chớ nghĩ lời vu khống của chúng là điều xấu cho các ngươi. Không, đó là cơ hội tốt để gia tăng ân phước và thử thách tấm lòng dành cho những người có đức tin. Sau khi mẹ của những người có đức tin được minh oan thì những tên tham gia tuyên truyền lời vu khống phải gánh chịu hậu quả cho hành động của chúng, riêng kẻ chủ mưu phải chịu sự trừng phạt lớn hơn, đó là tên đầu não của nhóm Munafiq, ''Abdullah bin Ubai bin Salul.');
INSERT INTO "vn_mokhtasar" VALUES(24,12,'24:12','Tại sao những người có đức tin nam và nữ khi nghe được lời nói sàm bậy đó lại không liền nghĩ đó là lời vu khống cho anh em đồng đạo của mình mà đáp lại: Đây rõ là lời nói dối.');
INSERT INTO "vn_mokhtasar" VALUES(24,13,'24:13','Thế sao chúng không đưa ra bốn nhân chứng chỉ tội rằng mẹ của những người có đức tin đã làm điều mà chúng đã hô hào buộc tội bà. Nếu như chúng không đưa ra đủ bốn nhân chứng cho điều đó - chắc chắn chúng không có nhân chứng để đưa ra -, theo giáo lý của Allah chúng là những kẻ dối trá.');
INSERT INTO "vn_mokhtasar" VALUES(24,14,'24:14','Và giá như Allah không nhân nhượng và tội nghiệp các ngươi - hỡi những người có đức tin - thì chắc chắn các ngươi đã bị trừng phạt nhưng Ngài luôn tha thứ cho những ai biết ăn năn, chứ các ngươi đáng bị trừng phạt thích đáng bởi các ngươi đã tiếp tay tuyên truyền lời vu khống cho mẹ của những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(24,15,'24:15','Khi các ngươi truyền tai nhau những lời nói bịa đặt vô căn cứ mà các ngươi nghĩ đó là điều bình thường không đáng nhưng với Allah đó là điều trọng đại bởi trong đó là sự dối trá và buộc tội người khác.');
INSERT INTO "vn_mokhtasar" VALUES(24,16,'24:16','Và phải chi khi nghe lời đồn thất thiệt này các ngươi nói: Chúng tôi không được tham gia bàn tán điều xấu xa này, Thượng Đế của bầy tôi vô can, đây quả là lời vu khống táo tợn cho mẹ của những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(24,17,'24:17','Allah nhắc nhở và khuyên các ngươi đừng tái phạm sai lầm tương tự việc vu khống ai đó làm điều khả ố, nếu các ngươi là những người hết lòng tin tưởng Allah.');
INSERT INTO "vn_mokhtasar" VALUES(24,18,'24:18','Và Allah đã giải thích cụ thể các câu Kinh mang những giáo lý và sự nhắc nhở cho các ngươi hiểu rõ. Allah biết rõ mọi hành động của các ngươi, không gì giấu kín được Ngài và dựa vào đó mà Ngài thưởng phạt công bằng cho các ngươi, Ngài luôn sáng suốt trong quản lý và qui định.');
INSERT INTO "vn_mokhtasar" VALUES(24,19,'24:19','Quả thật, đối với những ai thích những việc khả ố này lan truyền nhanh chống - trong đó có tin khống Zina - trong cộng đồng những người có đức tin, y phải chịu hình phạt thích đáng ở trần gian, đó là chịu đòn roi; và ở Đời Sau là hình phạt ở Hỏa Ngục. Allah biết rõ lời gian dối của họ, biết rõ điều nào tốt cải thiện được cuộc sống cho đám nô lệ của Ngài trong khi các ngươi không biết gì cả.');
INSERT INTO "vn_mokhtasar" VALUES(24,20,'24:20','Và nếu như Allah không nhân nhượng, không lượng thứ cho các ngươi - hỡi nhóm người tham gia vu khống - và nếu như không phải Ngài nhân từ, độ lượng đối với các ngươi thì Ngài đã nhanh chóng trừng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(24,21,'24:21','Hỡi những người đã tin tưởng Allah và tuân thủ theo giáo lý của Ngài, cấm các ngươi bám theo những lối mòn của lũ Shaytan bởi chúng dụ dỗ các ngươi. Ai theo chúng sẽ bị chúng điều khiển nói năng sàm bậy và hành động sai trái, nghịch lại giáo lý. Nếu như Allah không nhân nhượng, không độ lượng với các ngươi - hỡi những người có đức tin - thì chắc chắn không ai trong các ngươi sạch khỏi tội lỗi cho dù có ăn năn sám hối. Tuy nhiên, Allah muốn tẩy sạch ai Ngài muốn bằng cách chấp nhận sự hối cải của họ, Allah nghe rõ mọi lời nói của các ngươi, biết rõ mọi điều các ngươi làm, không gì giấu được Ngài và dựa vào đó Ngài thưởng phạt các ngươi một cách thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(24,22,'24:22','Và nhóm người ngoan đạo và giàu có chớ thề chấm dứt việc chu cấp cho những người thiếu thốn thuộc bà con dòng họ - gồm những người nghèo và những người đã di cư từ Makkah đến Madinah vì Allah - vì lỗi lầm mà họ đã gây nên. Những người ngoan đạo giàu có đó hãy lượng thứ bỏ qua cho họ. Há các ngươi không muốn Allah tha thứ cho các ngươi về tội lỗi đã phạm hay sao trong khi Allah sẵn sàng tha thứ và khoan dung với nô lệ nào biết hối cải?! Câu Kinh được thiên khải vì ông Abu Bakr đã thề không viện trợ cho Mistah nữa vì đã tham gia trong vụ vu khống.');
INSERT INTO "vn_mokhtasar" VALUES(24,23,'24:23','Quả thật, những ai vu khống cho những phụ nữ có đức tin đoan trang, tiết hạnh làm điều khả ố thì nhóm người vu khống đó sẽ bị trục xuất khỏi lòng thương xót của Allah ngay ở trần gian lẫn ở Đời Sau và một hình phạt khủng khiếp nữa chào đón chúng ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(24,24,'24:24','Một sự kiện xảy ra vào Ngày Phán Xét là những chiếc lưỡi, những đôi tay và những đôi chân sẽ làm nhân chứng kết tội cho những gì mà họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(24,25,'24:25','Trong ngày hôm đó, Allah ban thưởng đầy đủ công bằng và họ mới nhận ra được Allah là Đấng Chân Lý, tất cả mọi thứ xuất phát từ Ngài như thông tin hoặc lời cảnh cáo hoặc lời hứa hẹn đều là thật không có gì phải ngờ vực.');
INSERT INTO "vn_mokhtasar" VALUES(24,26,'24:26','Tất cả những người nam và những người nữ có những lời nói và hành động sàm bậy mới đáng là một đôi của nhau; còn những người nam và những người nữ đứng đắn, đoan trang mới xứng đáng là một đôi của nhau. Nhóm nam nữ đứng đắn, trong sạch đó vô tội với mọi lời cáo buộc từ nhóm người nam và nữ xấu xa kia, họ sẽ được Allah tha thứ tội lỗi cho họ và được ban thưởng vô định lượng nơi Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(24,27,'24:27','Hỡi những người đã tin tưởng Allah và tuân thủ theo giáo luật của Ngài, các ngươi chớ tự ý vào nhà ai đó ngoài nhà của các ngươi cho đến khi các ngươi đã xin phép chủ nhà, các ngươi hãy nói câu chào xin phép trước khi bước vào: "Assalamualaikum, tôi vào được không?" Việc xin phép như thế tốt hơn việc các ngươi đường đột bước vào, hi vọng các ngươi khắc ghi mệnh lệnh này mà chấp hành.');
INSERT INTO "vn_mokhtasar" VALUES(24,28,'24:28','Trường hợp các ngươi không thấy ai trong nhà thì chứ bước vào cho đến khi có lời mời vào, nếu có ai bảo các ngươi lui lại thì các hãy rời đi. Allah xem điều đó tốt nhất cho các ngươi, bởi Ngài biết rõ điều các ngươi đang làm, không việc gì của các ngươi giấu kín được Ngài và dựa vào đó Ngài ban thưởng hoặc trừng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(24,29,'24:29','Các ngươi không bị bắt tội là không xin phép khi vào ngôi nhà cộng đồng không dành riêng cho ai như thư viện, nhà trọ, Allah biết rõ điều các ngươi công khai và hoàn cảnh của các ngươi lúc riêng tư, không điều gì giấu được Ngài và dựa vào đó Ngài sẽ ban thưởng cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(24,30,'24:30','Hỡi Thiên Sứ, Ngươi hãy nói với những người có đức tin nam hạ thấp cái nhìn xuống, đừng nhìn vào những phụ nữ không được phép nhìn và những ''Awrah của họ, và họ phải giữ phần kín của họ khỏi phạm vào những điều cấm. Việc không ngắm nhìn vào những điều bị Allah cấm sẽ giữ các ngươi trong sạch, quả thật, Allah luôn tận tường mọi điều các ngươi làm, không điều gì giấu được Ngài và dựa vào đó Ngài sẽ ban thưởng và trừng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(24,31,'24:31','Và Ngươi hãy bảo những phụ nữ có đức tin hạ thấp cái nhìn xuống, họ không được nhìn vào ''Awrah, họ phải giữ phần kín tránh khỏi Zina, họ không được phô trương sắc đẹp trước người khác giới trừ phi những phần phô bày tự nhiên (như hai bàn tay và gương mặt), họ hãy che kín toàn thân nhất là phần đầu, cổ, ngực. Nhan sắc của các họ chỉ được phép công khai trước chồng hoặc cha ruột hoặc cha chồng hoặc các con trai ruột hoặc các con trai của chồng hoặc anh em trai ruột hoặc các cháu trai con của anh chị em ruột hoặc những phụ nữ đáng tin là Muslim hoặc Kafir hoặc những nam nữ nô lệ hoặc những đàn ông giúp việc đã lớn tuổi không còn ham muốn phụ nữ nữa hoặc những đứa trẻ chưa biết gì về ''Awrah của phụ nữ; và họ chớ có đi mạnh chân cố ý tạo âm thanh của vòng chân được che kín bên trong hoặc âm thanh của các nữ trang khác. Tất cả các ngươi - hỡi những người có đức tin - hãy sám hối với Allah về tất cả cái nhìn Haram mà các ngươi đã mắc phải, may ra các ngươi đạt được điều các ngươi mong muốn và bình an khỏi điều mà các ngươi lo sợ.');
INSERT INTO "vn_mokhtasar" VALUES(24,32,'24:32','Và các ngươi hãy kết hôn - hỡi những người có đức tin - với những người đàn ông, những người phụ nữ đơn thân dù phải cưới những nam nữ nô lệ, nếu nghèo khó thì sẽ được Allah ban cho giàu có, và ở nơi Allah có vô số thiên lộc, nguồn thiên lộc của Ngài không bao giờ cạn kiệt bởi việc ban sự giàu có cho một ai, Ngài luôn biết rõ hoàn cảnh của đám nô lệ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(24,33,'24:33','Và những ai không có khả năng kết hôn vì nghèo khó hãy giữ mình đứng đắn tránh phạm Zina cho đến khi Allah ban cho giàu có từ nguồn thiên lộc bao la của Ngài. Và những nô lệ nào yêu cầu chủ viết chứng nhận để họ tự chuộc thân thì ông chủ hãy cho họ toại nguyện khi thấy ở họ sự ngoan đạo và có khả năng chuộc thân, ngoài ra, người chủ phải trao một phần tiền để họ làm vốn để kiếm tiền chuộc thân, số tiền đó cộng thêm những gì được gi trong sổ chuộc thân của họ. Và cấm các ông chủ ép những nữ nô lệ hành nghề mại dâm để kiếm tiền - giống như ''Abdullah bin Ubai đã ép hai nữ nô lệ của mình hành nghề mại dâm trong khi cả hai đã van xin được tránh xa hành động khả ố này -. Những ai đã ép nữ nô lệ làm điều khả ố đó thì hãy biết rằng Allah thương xót và luôn nhân từ, Ngài sẽ xóa tội cho các nữ nô lệ đó bởi lẽ họ chỉ bị ép buộc còn tội lỗi là đối với kẻ đã cưỡng ép.');
INSERT INTO "vn_mokhtasar" VALUES(24,34,'24:34','Và chính TA đã thiên khải cho các ngươi - hỡi con người - những câu Kinh giải thích rõ ràng, TA cũng đã nhắc về những hình ảnh thí dụ về các thế hệ trước các ngươi thuộc những người có đức tin và những kẻ vô đức tin; và TA đã mặc khải những lời nhắc nhở nhằm răn đe những ai biết kính sợ mà tuân thủ theo sắc lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(24,35,'24:35','Allah là Ánh Sáng soi sáng cho các tầng trời và đất, hình ảnh ánh sáng của Ngài trong con tim của người có đức tin tựa như ánh sáng của một ngọn đèn được đặt tại một cái hốc tường không có cửa sổ, ngọn đèn được đặt trong lồng thủy tinh, chiếc lồng thủy tinh lấp lánh tựa như vì sao. Ngọn đèn đó được thắp lên bởi loại dầu của một loại cây hồng phúc, đó là cây ô liu, một loại cây không bị che khuất khỏi ánh sáng mặt trời dù buổi sáng hay buổi chiều, dầu của loại cây đó long lanh gần như tỏa sáng mặc dù lửa vẫn chưa chạm đến, và nó sẽ như thế nào nếu lửa đã chạm đến?! Ánh sang của đèn rọi lên ánh sáng của lồng thủy tinh, tương tự như thế, con tim của người có đức tin rực rở bởi ánh sáng của Chỉ Đạo, Allah muốn hướng dẫn bất cứ ai bám theo Qur''an tùy ý Ngài, Allah phơi bày rõ ràng mọi thứ bằng những hình ảnh thí dụ, Allah am tường tất cả, không gì che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(24,36,'24:36','Ánh đèn đó chiếu sáng soi rọi vào các Masjid, nơi Allah cho phép cất cao tiếng gọi Azdan, tụng niệm, hành lễ Salah nhằm làm hài lòng Allah vào mỗi sáng và mỗi chiều.');
INSERT INTO "vn_mokhtasar" VALUES(24,37,'24:37','Những người đàn ông mà việc kinh doanh mua bán không làm họ xao lãng bổn phận tụng niệm Allah, hoàn thành tốt lễ nguyện Salah, xuất Zakah đúng mức, và lo sợ về Ngày Tận Thế, đó là Ngày khiến những con tim đảo lộn giữa niềm hi vọng được bình an và sự lo sợ và cặp mắt cũng bị đảo lộn mất phương hướng.');
INSERT INTO "vn_mokhtasar" VALUES(24,38,'24:38','Họ làm những điều đó với hi vọng được Allah ban thưởng hậu hĩnh hơn những gì họ đã làm và Ngài rộng lượng ban thêm cho họ, bởi Allah muốn ban phát vô định lượng cho ai Ngài muốn, Ngài ban thưởng gấp nhiều lần so với việc họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(24,39,'24:39','Và những ai không tin tưởng nơi Allah thì việc làm của họ không được ban thưởng, tựa như ảo ảnh long lanh trên mặt đất khiến người đang khát cứ tưởng đó là nước, y cố chạy đến để uống nhưng khi đến nơi thì ngơ ngác không thấy nước đâu, hình ảnh đó giống như người vô đức tin cứ tưởng việc làm của mình hữu ích nhưng khi được phục sinh sau khi chết thì không thấy ân phước đâu cả, y chỉ thấy mỗi Thượng Đế đang sẵn sàng tính số toàn bộ việc làm của y. Quả thật, Allah rất nhanh trong việc thanh toán.');
INSERT INTO "vn_mokhtasar" VALUES(24,40,'24:40','Hoặc việc làm của họ giống như những lớp u tối dưới đáy biển sâu thẳm, bên trên là những lớp sóng chất chồng lên nhau, bên trên nữa là đám mây che khuất không thấy vì sao, đám u tối chất chồng lên nhau khiến y giơ tay ra vẫn không thấy tay của mình vì bóng tối mù mịt. Như thế đó, tình trạng của người vô đức tin bị chìm sâu trong tội lỗi của sự ngu muội, nghi ngờ, lưỡng lự và sự niêm kín lên con tim y. Đối với ai không được Allah ban cho chỉ đạo thì y không thể tìm đâu ra sự hướng dẫn để được hướng dẫn và không thể tìm đầu ra Kinh Sách để soi sáng.');
INSERT INTO "vn_mokhtasar" VALUES(24,41,'24:41','Há Ngươi không biết - hỡi Thiên Sứ - rằng Allah được tán dương bởi tất cả vạn vật trên các tầng trời, dưới đất kể cả loài chim đang xòe cánh bay trên không trung. Tất cả vạn vật đó đều biết rõ cách hành lễ Salah và tụng niệm Allah như con người, Allah biết rõ mọi việc làm của chúng, không cử động nào của chúng nằm ngoài tầm kiểm soát được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(24,42,'24:42','Và quyền thống trị các tầng trời và đất đều nằm trong tay một mình Allah và tất cả để phải trở về trình diện Ngài trong Ngày Phán Xét để chịu thanh toán thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(24,43,'24:43','Há Ngươi không biết - hỡi Thiên Sứ - việc Allah di chuyển những đám mây rồi kết chặt chúng thành một khối thống nhất chất chồng lên nhau, rồi Ngươi thấy nước mưa từ bên trong đám mây trút xuống và cũng có những đám mây trên trời tựa như những quả núi trút xuống những cơn mưa đá, Ngài dùng nó trừng phạt kẻ nào Ngài muốn và kéo nó ra khỏi ai Ngài muốn, ánh sáng của tia chớp từ đám mây gần như cướp đi ánh sáng của đôi mắt.');
INSERT INTO "vn_mokhtasar" VALUES(24,44,'24:44','Allah luân chuyển ban đêm và ban ngày khi dài khi ngắn, lúc đến lúc đi, quả thật hình ảnh đó là những dấu hiệu khẳng định quyền năng vĩ đại của Allah dành cho nhóm người thông suốt nhận ra Thượng Đế duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(24,45,'24:45','Và Allah đã tạo hóa tất cả sinh vật sinh sống trên trái đất từ tinh dịch, trong chúng có con trườn bằng bụng như loài rắn, có con đi bằng hai chân như con người và loài gia cầm, có con đi bằng bốn chân như loài gia súc. Allah muốn tạo hóa bất cứ thứ gì Ngài muốn dù đã liệt kê hay chưa liệt kê, quả thật Allah toàn năng trên tất cả mọi thứ, không có gì làm Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(24,46,'24:46','Chắc chắn TA đã thiên khải cho Muhammad những câu Kinh minh bạch không uẩn khúc, Allah muốn hướng dẫn ai đến với con đường ngay thẳng không uốn khúc là quyền của Ngài và con đường đó sẽ đưa lối đến Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(24,47,'24:47','Và nhóm Munafiq bảo: Chúng tôi đã tin tưởng Allah và Thiên Sứ của Ngài, xin tuân lệnh Allah và tuân lệnh Thiên Sứ của Ngài, tuy nhiên vẫn có đám người của chúng quay lưng bỏ đi quyết không tuân lệnh Allah và Thiên Sứ của Ngài về việc Jihad vì chính nghĩa của Allah và luôn cả những việc khác, vậy mà chúng đã tuyên bố phục tùng Allah và Thiên Sứ. Nhóm người đó nào có đức tin khi chúng nghịch lệnh Allah và Thiên Sứ của Ngài cho dù chúng có tự nhận mình là người có dức tin.');
INSERT INTO "vn_mokhtasar" VALUES(24,48,'24:48','Và khi có lời mời nhóm người Munafiq đến với Allah và Thiên Sứ của Ngài để Người phân xử sự tranh chấp giữa họ thì một nhóm người trong họ không chấp nhận lời phân xử đó vì tấm lòng ngụy tạo của họ.');
INSERT INTO "vn_mokhtasar" VALUES(24,49,'24:49','Và khi chúng biết được mình làm đúng, chắc sẽ đạt được mục đích thì chúng liền nhanh nhẩu kéo nhau đến với bộ dạng tội nghiệp.');
INSERT INTO "vn_mokhtasar" VALUES(24,50,'24:50','Chắc trong tim của nhóm người này có chứng bệnh hay họ sinh nghi Người có thật là Thiên Sứ của Allah hay không hoặc họ sợ bị Allah và Thiên Sứ của Ngài xử ép họ? Không, họ không bị bất cứ điều gì vừa nêu mà nguyên nhân chỉ vì họ ngoảnh mặt với giáo lý của Allah và chống đối Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(24,51,'24:51','Lời nói mà những người có đức tin thốt ra khi nghe được lời mời gọi đến với Allah và Thiên Sứ của Ngài để chịu phân xử những điều mà họ tranh chấp với nhau thì họ chỉ nói: "Chúng tôi xin vâng lời và làm theo mệnh lệnh." Đó là tính cách của nhóm người thành đạt ở trần gian và ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(24,52,'24:52','Và ai phục tùng Allah, phục tùng Thiên Sứ của Ngài, biết đón nhận giáo luật của Allah và Thiên Sứ ban hành, lo lắng bị lôi kéo rơi vào điều tội lỗi, sợ hãi hình phạt của Allah nên luôn tuân thủ theo mệnh lệnh của Ngài, đó là nhóm người được hứa hẹn ban cho sự thành đạt ở trần gian và ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(24,53,'24:53','Và nhóm đạo đức giả đã thề trịnh trọng với Allah rằng nếu Ngươi ra lệnh xuất binh Jihad thì chúng chắc chắn họ xuất binh. Ngươi hãy bảo chúng - hỡi Thiên Sứ -: Các ngươi chớ thề thốt làm chi, sự dối trá của các người đều được biết rõ và sự vâng lời giả tạo của các ngươi cũng đều được biết rõ. Allah biết rõ điều các ngươi làm, không gì giấu được Ngài cho dù các ngươi có che đậy ra sao.');
INSERT INTO "vn_mokhtasar" VALUES(24,54,'24:54','Ngươi hãy nói - hỡi Thiên Sứ - với nhóm người Munafiq: "Các ngươi hãy phục tùng Allah, phục tùng Thiên Sứ từ bên ngoài lẫn bên trong." Nếu các ngươi ngoảnh mặt với điều mà Y đã ra lệnh cho các ngươi phải tuân lệnh Allah và Thiên Sứ thì chẳng qua Y chỉ có bổn phận truyền đạt còn các ngươi phải có trách nhiệm phục tùng và làm theo điều Y chỉ dẫn. Nếu các ngươi tuân thủ đúng theo mệnh lệnh thì các ngươi đã được hướng dẫn đến Chân Lý, bởi nhiệm vụ của Thiên Sứ là công khai tuyên truyền, chứ Y không có thẩm quyền ép buộc ai phải tuân theo Chân Lý cả.');
INSERT INTO "vn_mokhtasar" VALUES(24,55,'24:55','Allah đã hứa với những người có đức tin nơi Allah và và làm việc thiện tốt trong số các ngươi sẽ được Ngài phù hộ chiến thắng kẻ thù và được bổ nhiệm thành nhóm kế thừa trái đất giống như Ngài đã bổ nhiệm các thế hệ có đức tin trước họ, rồi Ngài sẽ thiết lập cho họ một tôn giáo được Ngài hài lòng - đó là Islam - trường tồn, hiên ngang. Ngài hứa với họ sẽ thay đổi tình trạng bất an thành an bình nhưng với điều kiện rằng họ phải thờ phượng một mình Ngài, không thờ phượng bất cứ ai cùng với Ngài và ai không tin tưởng sau khi đã hưởng được diễm phúc an lành đó thì họ đã rời khỏi sự phục tùng Allah.');
INSERT INTO "vn_mokhtasar" VALUES(24,56,'24:56','Và các ngươi hãy chu đáo hành lễ Salah, hãy xuất Zakah từ tài sản của các ngươi, hãy tuân lệnh Thiên Sứ mà chấp hành những điều Người sai bảo và những điều Ngài ngăn cấm, có thế hi vọng các ngươi hưởng được lòng thương xót của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(24,57,'24:57','Ngươi - hỡi Thiên Sứ - đừng có tưởng đám người phủ nhận Allah sẽ thoát được hình phạt khi Ngài muốn giáng lên đầu họ, và vào Ngày Phán Xét, chỗ ở của chúng sẽ là Hỏa Ngục, đúng là tồi tệ đối với ai có nơi cư ngụ là Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(24,58,'24:58','Hỡi những người đã tin tưởng Allah và tuân thủ theo giáo luật của Ngài, hãy để cho những nam, nữ nô lệ và những đứa trẻ tự do chưa đến tuổi trưởng thành xin phép các ngươi vào ba khoảng thời gian, trước Salah Fajr (lễ nguyện hừng đông) lúc các ngươi đang thay đồ chuẩn bị ra khỏi phòng; lúc trưa khi các ngươi trút bỏ bớt áo quần để nghỉ trưa; và sau Salah ''Isha lúc các ngươi thay đồ ngủ. Đây là ba thời điểm mà các ngươi thường phơi bày ''Awrah nhất, cho nên họ không được tùy tiện vào gặp các ngươi cho đến khi các ngươi cho phép. Ngoài ba khoảng thời khắc đó thì các ngươi cũng như họ không bị bắt tội khi vào gặp nhau không xin phép, bởi đôi khi vì công việc nên các ngươi thường xuyên qua lại nếu phải xin phép mỗi lần vào sẽ gây khó khăn cho các ngươi. Giống như việc Allah trình bày cho các ngươi các giáo lý xin phép, Ngài trình bày cho các ngươi những câu Kinh qui định về điều đó, Allah biết rõ điều nào tốt cho đám nô lệ của Ngài, Ngài sáng suốt trong qui định các giáo lý.');
INSERT INTO "vn_mokhtasar" VALUES(24,59,'24:59','Và khi con cái của các ngươi đến tuổi trưởng thành thì các ngươi hãy bảo chúng xin phép khi muốn vào gặp các ngươi ở tư phòng giống như những người lớn đã xin phép. Giống như việc Allah trình bày cho các ngươi các giáo lý xin phép, Ngài trình bày cho các ngươi những câu Kinh qui định về điều đó, Allah biết rõ điều nào tốt cho đám nô lệ của Ngài, Ngài sáng suốt trong qui định các giáo lý của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(24,60,'24:60','Và những phụ nữ mãn kinh quá tuổi sinh nở do lớn tuổi, họ không còn ham muốn kết hôn gì nữa thì họ không bị bắt tội khi cởi bớt lớp áo choàng và khăn đội nhưng vẫn kín đáo không quá phơi bày cơ thể. Tuy nhiên, nếu họ không làm thế sẽ tốt hơn cho họ, Allah luôn nghe thấu mọi lời các ngươi nói, biết rõ mọi hành động của các ngươi, không gì giấu giếm được Ngài và dựa vào đó các ngươi sẽ bị phân xét và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(24,61,'24:61','Người mù không bị tội do không nhìn thấy, người què và người bệnh cũng không mang tội khi họ không thể hoàn thành bổn phận bắt buộc như Jihad vì chính nghĩa của Allah. Các ngươi - hỡi những người có đức tin - cũng không bị mang tội khi ăn uống tại nhà riêng, tại nhà các con, tại nhà cha mẹ hoặc nhà anh chị em ruột hoặc nhà chú bác ruột hoặc nhà cậu dì hoặc nhà của người làm công giữ vườn cho các ngươi, cũng như nhà của bạn bè các ngươi như một thói quen tốt. Các ngươi không bị bắt tội khi các ngươi cùng ăn chung hoặc ăn riêng lẻ nhưng khi vào những ngôi nhà vừa liệt kê thì các ngươi hãy chào hỏi những ai trong nhà bằng câu: Assalamu''alaikum. Trường hợp trong nhà không người thì các ngươi hãy tự chào bản thân các ngươi mà nói: Assalamu''alaina wa ''ala ''ibaadilla-hisso-lihin, một lời chào hồng phúc được Allah qui định, nó khiến các ngươi thích nhau hơn, gần gũi nhau hơn và lời chào tốt đẹp đó làm mát dạ người nghe. Với giáo lý được Allah trình bày qua các câu Kinh hi vọng các ngươi biết suy nghĩ và thực hành theo.');
INSERT INTO "vn_mokhtasar" VALUES(24,62,'24:62','Quả thật, những người có đức tin đích thực là những người tin tưởng Allah, tin tưởng Thiên Sứ của Ngài, và là những người khi họ ở cùng Thiên Sứ để bàn việc công cải thiện cộng đồng Muslim thì họ không ngang nhiên rời đi trừ phi họ đã xin phép Người. Quả thật, những ai xin phép Ngươi - hỡi Thiên Sứ - trước khi rời đi thì họ đích thực là người tin tưởng Allah và tin tưởng Thiên Sứ của Ngài. Cho nên khi họ xin phép Ngươi để đi làm việc riêng quan trọng của họ thì Ngươi hãy cho phép nếu Ngươi muốn, Ngươi hãy cầu xin Allah tha thứ cho họ, quả thật Allah luôn tha thứ cho ai biết ăn năn trong đám bề tôi của Ngài, và Ngài luôn thương xót họ.');
INSERT INTO "vn_mokhtasar" VALUES(24,63,'24:63','Các ngươi hãy tôn trọng Thiên Sứ của Allah - hỡi những người có đức tin -, cho nên khi các ngươi hô gọi Y thì các ngươi chớ gọi bằng tên như hỡi Muhammad hoặc tên cha của Người như hỡi con trai của ''Abdullah giống như các ngươi thường hay gọi tên nhau như thế, các ngươi hãy gọi Người: hỡi Thiên Sứ của Allah, hỡi Nabi của Allah. Và khi Người gọi các ngươi đến bàn công việc chung thì các ngươi chớ xem nhẹ lời gọi đó của Người giống như thói quen của các ngươi, ngược lại, các ngươi hãy tranh thủ đáp lại lời gọi. Chắc chắn Allah biết rõ ai đã lén lút rời đi mà không xin phép. Do đó, nhóm người cố ý kháng lệnh của Thiên Sứ hãy coi chừng kẻo họ phải bị thử thách, bị tai ương hoặc bị phải hình phạt đau đớn vì không biết nhẫn nại.');
INSERT INTO "vn_mokhtasar" VALUES(24,64,'24:64','Há không phải tất cả các tầng trời và trái đất từ sự tạo hóa, quyền lực cả việc quản lý là của riêng Allah đó sao! Ngài biết rõ các ngươi - hỡi con người - về hoàn cảnh của các ngươi từng chi tiết một, và trong Ngày Tận Thế khi họ tập trung đến trình diện Ngài sau khi được phục sinh Ngài sẽ báo cụ thể từng việc mà họ đã từng làm ở trần gian. Allah biết rõ mọi thứ, không gì trong các tầng trời và đất giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(25,1,'25:1','Vĩ đại và tốt đẹp thay việc Allah đã thiên khải Qur''an xuống làm tiêu chuẩn phân biệt giữa Chân Lý và điều ngụy tạo cho Người bề tôi của Ngài và là Thiên Sứ của Ngài, Muhammad, với sứ mạng mang bức Thông Điệp dến cho hai loài, Jinn và con người để cảnh báo họ về hình phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(25,2,'25:2','Ngài là Đấng duy nhất sở hữu các tầng trời và trái đất, Ngài không có con cũng không có đối tác cùng chia sẻ vương quyền. Ngài đã tạo hóa vạn vật và định sẵn cho từng cái một số mệnh nhất định phù hợp với nó bằng kiến thức và sự sáng suốt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(25,3,'25:3','Và nhóm người đa thần đã chọn lấy những thần linh ngoài Allah để thờ phượng thay vì Ngài. Những thần linh đó không có quyền năng tạo hóa bất cứ vật gì dù nhỏ hay lớn mà đúng hơn chính bản thân chúng được tạo ra. Chắc chắn Allah đã tạo hóa ra chúng từ không là gì, chúng bất lực tự bảo vệ bản thân, không thể tự giúp mình được điều tốt đẹp, không có khả năng cứu người chết sống lại, không thể giết người đang sống và càng không thể phục sinh người được chôn trong mộ.');
INSERT INTO "vn_mokhtasar" VALUES(25,4,'25:4','Và nhóm người phủ nhận Allah và Thiên Sứ của Ngài nói: "Qur''an này chẳng qua chỉ là một lời bịa đặt do chính Muhammad cộng tác với nhóm người nào đó sáng tác ra rồi đổ thừa cho Allah." Nhưng chính nhóm người vô đức tin này mới là nhóm bịa ra những điều ngụy tạo, bởi Qur''an thật sự là lời phán của Allah không con người hay tên Jinn nào có thể viết ra lời tương tự.');
INSERT INTO "vn_mokhtasar" VALUES(25,5,'25:5','Và nhóm người phủ nhận Qur''an đã nói: Qur''an chỉ là những câu chuyện cổ tích, những mẫu chuyện ngụ ngôn được ghi chép lại, ai đó đã đọc cho Muhammad nghe vào mỗi sáng và mỗi chiều.');
INSERT INTO "vn_mokhtasar" VALUES(25,6,'25:6','Hãy nói - hỡi Thiên Sứ - với nhóm người phủ nhận: Qur''an được Allah, Đấng thấu hiểu hết mọi chi tiết của các tầng trời và trái đất thiên khải, không do bất cứ ai sáng tác như các ngươi đã xảo ngôn. Sau đó Allah khuyến khích họ bằng sự hối cải: Quả thật Allah rất nhân từ đối với ai biết ăn năn trong đám nô lệ của Ngài và Ngài luôn thương xót họ.');
INSERT INTO "vn_mokhtasar" VALUES(25,7,'25:7','Và nhóm đa thần phủ nhận Thiên Sứ lên tiếng: Sao, kẻ tự xưng là Thiên Sứ được Allah gởi xuống lại ăn uống như bao người phạm khác và còn phải đi quanh chợ để bươn chải cuộc sống nữa kia ư?! Sao không gởi xuống thêm một Thiên Thần để làm bạn với Y, hỗ trợ Y!');
INSERT INTO "vn_mokhtasar" VALUES(25,8,'25:8','Những kẻ vô đức tin nói tiếp: Hoặc sao không ban từ trên trời cho riêng Y một kho báu hoặc sao không cho riêng Y sở hữu một vườn trái cây để Y ăn quả? Sao lại để Y phải kiếm sống giữa chợ thế này. Và nhóm người sai quấy này còn bảo: Các ngươi - hỡi những người có đức tin - không phải đang nghe theo vị Thiên Sứ nào cả mà đúng hơn là các ngươi đang nghe theo một tên mà đầu óc của y bị ma thuật khống chế.');
INSERT INTO "vn_mokhtasar" VALUES(25,9,'25:9','Chắc chắn Ngươi sẽ ngạc nhiên - hỡi Thiên Sứ - về việc chúng miêu tả Ngươi bằng lời lẽ châm biếm khi chúng gọi Ngươi là tên phù thủy, là tên bị trúng tà, là tên điên. Thế nên, chúng bị lầm lạc lệch khỏi Chân Lý, hoàn toàn không thể tìm ra con đường Chính Đạo và cũng đã quên mất hết tính trung thực và uy tín của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(25,10,'25:10','Hồng phúc thay Allah, Đấng mà nếu muốn thì Ngài đã tạo cho Ngươi nhiều thứ khác tốt đẹp hơn những thứ mà chúng đã gợi ý Ngươi. Ngài thừa sức ban cho cuộc sống của Ngươi hưởng thụ trong những ngôi vườn để Ngươi ăn trái quả của nó và trong những ngôi vườn đo có những dòng sông chảy bên dưới những tòa lâu đài tráng lệ để Ngươi sống hưởng lạc.');
INSERT INTO "vn_mokhtasar" VALUES(25,11,'25:11','Và họ đã không thật lòng nói ra để yêu cầu Chân Lý và tìm kiếm bằng chứng, đúng hơn là họ muốn thể hiện mình đã bác bỏ Ngày Tận Thế nhưng TA đã chuẩn bị sẵn cho đám người bác bỏ Ngày Tận Thế Hỏa Ngục bùng cháy dữ dội.');
INSERT INTO "vn_mokhtasar" VALUES(25,12,'25:12','Khi Hỏa Ngục nhìn thấy nhóm người ngoại đạo từ đằng xa khi chúng đang bị dẫn bộ đến thì nó rít lên dữ dội, những kẻ vô đức tin nghe thấy tiếng rít đáng sợ của nó.');
INSERT INTO "vn_mokhtasar" VALUES(25,13,'25:13','Và khi nhóm người ngoại đạo bị quẳng vào một xó chật hẹp của Hỏa Ngục với đối tay bị xiềng xích dính vào cổ thì chúng gào thét muốn được chết phứt cho rồi hồng thoát được cảnh bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(25,14,'25:14','Ngày hôm nay, các ngươi - hỡi nhóm người vô đức tin - đừng có cầu xin được chết một lần rồi im lặng mà hãy lặp lại nhiều lần lên nào xem ai quan tâm đến lời thỉnh cầu của các ngươi không. Không, các ngươi sẽ phải mãi mãi ở trong hình phạt đó.');
INSERT INTO "vn_mokhtasar" VALUES(25,15,'25:15','Ngươi hãy bảo họ - hỡi Thiên Sứ -: Với hình phạt đã miêu tả cho các ngươi tốt hơn hay là Thiên Đàng vĩnh cữu không bao giờ tiêu vong, không bao giờ chấm dứt tốt hơn? Đó là thứ mà Allah đã hứa làm phần thưởng ban cho đám nô lệ có đức tin của Ngài khi họ trở về trình diện Ngài trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(25,16,'25:16','Trong Thiên Đàng, họ tự do hưởng lạc. Đó là lời hứa mà Allah đã hứa với đám nô lệ có đức tin của Ngài, lời hứa đó nhất định được Allah thực hiện, Ngài không bao giờ thất hứa.');
INSERT INTO "vn_mokhtasar" VALUES(25,17,'25:17','Và vào ngày Allah phục sinh nhóm người đa thần vô đức tin cùng với thần linh mà chúng thờ phượng ngoài Allah, Ngài hỏi mang tính khinh miệt nhóm thần linh được tôn thờ: Có phải chính các ngươi đã ra lệnh đám nô lệ của TA thờ phượng các ngươi phải không hay là họ tự đẩy mình rời vào lầm lạc?');
INSERT INTO "vn_mokhtasar" VALUES(25,18,'25:18','Nhóm thần linh bào chữa: Lạy Thượng Đế của bầy tôi, Ngài vô can với tất cả những gì đã gán ghép cho Ngài, bầy tôi không dám tự ý chọn ai đó ngoài Ngài làm Đấng Bảo Hộ cho họ, thế thì làm sao bề tôi dám kêu gọi họ thờ phượng bầy tôi thay vì Ngài. Chẳng qua là Ngài đã ban cho họ và tổ tiên họ sống sung sướng, hưởng thụ trên trần gian khiến họ lún sâu hơn trong tội lỗi đến nỗi họ quên mất lời nhắc nhở của Ngài nên đã thờ phượng ngoài Ngài, họ là đám người bị diệt vong.');
INSERT INTO "vn_mokhtasar" VALUES(25,19,'25:19','Quả thật, lời thú tội của các ngươi đã bị bác bỏ - hỡi nhóm người đa thần - từ phía các thần linh mà các ngươi từng thờ phượng trước đây ngoài Allah. Thế là các ngươi bất lực bảo vệ bản thân khỏi hình phạt, lại càng không có ai dám giúp đỡ các ngươi. Về các ngươi - hỡi những người có đức tin - dám gây bất công thờ phượng ai khác cùng Allah, TA sẽ cho hắn nếm hình phạt khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(25,20,'25:20','Và TA đã cử phái trước Ngươi - hỡi Thiên Sứ - những Thiên Sứ cũng là người phàm, cũng ăn uống, cũng đi lại trong chợ, hoàn toàn không phải là điều mới mẻ gì. Và TA cũng định đoạt trong các ngươi - hỡi con người - có người giàu, có người nghèo, có người khỏe, có người bệnh, mỗi người mỗi hoàn cảnh, một khi các ngươi kiên nhẫn chịu đựng thử thách sẽ được Allah ban thưởng vì sự nhẫn nại của các ngươi. Thượng Đế của Ngươi luôn thấy rõ ai kiên nhẫn và ai không kiên nhẫn, ai ngoan ngoãn tuân lệnh Ngài và ai chống đối Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(25,21,'25:21','Và đám người ngoại đạo không tin tưởng cuộc gặp gỡ TA, cũng không sợ hình phạt của TA, chúng nói: "Sao Allah không cử Thiên Thần đến gặp bọn ta để xác định với bọn ta về sứ mạng của Muhammad hoặc cho bọn ta tận mắt nhìn thấy Thượng Đế để Ngài cho bọn ta biết sự thật về điều đó?" Sự ngạo mạn ngông cuồng đã ngăn chúng khỏi đức tin và lời nói của chúng đã vượt giới cho phép nên đã biến chúng thành đám người vô đức tin và sai quấy quá mức.');
INSERT INTO "vn_mokhtasar" VALUES(25,22,'25:22','Vào ngày mà người vô đức tin tận mắt nhìn thấy các Thiên Thần lúc chết, lúc trong cõi Barzakh (cõi mộ), lúc phục sinh, lúc được lùa đến tập trung để tính sổ và lúc bị tống vào Hỏa Ngục nhưng chúng không hề được an ủi trong những lúc đó, khác với những người có đức tin, và chúng sẽ được các Thiên Thần bảo: Các ngươi bị Allah cấm báo tin vui cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(25,23,'25:23','Và TA trở lại kiểm tra mọi việc mà những kẻ vô đức tin đã làm ở trần gian gồm những việc tốt, việc thiện. Tất cả đều bị TA xóa bỏ vì nguyên nhân vô đức tin của chúng, TA biến tất cả thành cát bụi bay tứ tán như hình ảnh thấy được lúc ánh nắng soi vào cửa sổ.');
INSERT INTO "vn_mokhtasar" VALUES(25,24,'25:24','Những người có đức tin là cư dân của Thiên Đàng trong ngày hôm đó, họ có một nơi ở tốt đẹp, một nơi an nghỉ tuyệt vời. Đói là bởi họ tin tưởng nơi Allah và việc làm của họ thì thiện tốt.');
INSERT INTO "vn_mokhtasar" VALUES(25,25,'25:25','Ngươi hãy nghĩ - hỡi Thiên Sứ - đến Ngày mà bầu trời bị tách ra khỏi những đám mây trắng và các Thiên Thần ồ ạt kéo xuống vùng đất phục sinh với số lượng rất đông.');
INSERT INTO "vn_mokhtasar" VALUES(25,26,'25:26','Vương quyền thật sự trong Ngày Tận Thế là của Đấng Arrahman Hiển Vinh, và đó là Ngày đầy khó khăn cực nhọc cho nhóm người vô đức tin, trái ngược với tình trạng của người có đức tin, họ được thuận lợi và dễ dàng.');
INSERT INTO "vn_mokhtasar" VALUES(25,27,'25:27','Ngươi hãy nghĩ - hỡi Thiên Sứ - đến Ngày mà những tên sai quấy do đã không chịu noi theo Thiên Sứ sẽ tự cắn bàn tay mình nuối tiếc mà nói: Ôi phải chi trước đây mình đi theo Thiên Sứ với những gì Người mang đến từ Thượng Đế thì mình đã đi theo Người đến được với chiến thắng.');
INSERT INTO "vn_mokhtasar" VALUES(25,28,'25:28','Và y nói trong đau khổ tột cùng và tự nguyền rủa bản thân: Mình thật là khốn nạn, phải chi đừng chọn lấy tên vô đức tin đó làm bạn thân.');
INSERT INTO "vn_mokhtasar" VALUES(25,29,'25:29','Chính tên bạn thân vô đức tin đó đã kéo mình rời xa Qur''an sau khi đường lối của Thiên Sứ đã đến với mình; và Shaytan chuyên lường gạt con người mãi đến khi con người rơi vào khó nhọc thì hắn chạy mất.');
INSERT INTO "vn_mokhtasar" VALUES(25,30,'25:30','Và trong ngày hôm đó Thiên Sứ than phiền về tình trạng của dân chúng của Người: Lạy Thượng Đế, quả thật dân chúng của bề tôi, nhóm người mà Ngài gởi bề tôi đến với họ đã bỏ mặc và xa lánh Qur''an.');
INSERT INTO "vn_mokhtasar" VALUES(25,31,'25:31','Và giống như những gì Ngươi đã gặp - hỡi Thiên Sứ - từ dân chúng của mình qua việc hãm hại, cấm cản con đường của nhà Ngươi, TA đã tạo cho mỗi vị Nabi phải đối đầu với quần chúng mình, nhưng chỉ cần Thượng Đế của Ngươi là đủ hướng dẫn đến với Chân Lý và đủ sức phù hộ Ngươi chiến thắng kẻ thù.');
INSERT INTO "vn_mokhtasar" VALUES(25,32,'25:32','Và nhóm người không tin tưởng Allah lên tiếng: Sao không ban xuống cho Thiên Sứ trọn quyển Qur''an cùng một lúc, sao lại phải ban xuống từng giai đoạn. Bằng cách thiên khải Qur''an xuống dần dần theo từng giai đoạn là để trấn an tấm lòng của Ngươi - hỡi Thiên Sứ - với cách thiên khải hết lần này đến lần khác là để giúp ngươi dễ hiểu và dễ thuộc.');
INSERT INTO "vn_mokhtasar" VALUES(25,33,'25:33','Và không một hình ảnh thí dụ nào mang đến hỏi Ngươi - hỡi Thiên Sứ - từ nhóm đa thần mà không được TA giúp Ngươi trả lời cặn kẻ, chính xác từng chi tiết và TA đã giúp Ngươi trình bày rõ ràng nhất.');
INSERT INTO "vn_mokhtasar" VALUES(25,34,'25:34','Vào Ngày Tận Thế, có những kẻ bị kéo úp mặt quẳng vào Hỏa Ngục, đó là nơi ở tồi tệ nhất, chỉ vì chúng đã rời xa Chân Lý bởi con đường của chúng là con đường của sự vô đức tin và lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(25,35,'25:35','Và chắc chắn TA đã ban cho Musa Kinh Tawrah và bổ nhiệm thêm cho Y người anh (em) trai Harun với vai trò hỗ trợ, phò tá.');
INSERT INTO "vn_mokhtasar" VALUES(25,36,'25:36','TA bảo với hai anh em Y: Hai Ngươi đi đến gặp Fir-''awn và dân chúng của hắn, những kẻ mà chúng đã phủ nhận những dấu hiệu của TA. Cả hai hãy thi hành mệnh lệnh. Thế là cả hai đến gặp chúng và mời gọi chúng đến với Tawhid (độc tôn) Allah nhưng chúng đã phủ nhận hai anh em của Y, thế là chúng bị TA tiêu diệt bằng một sự tiêu diệt khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(25,37,'25:37','Và dân chúng của Nuh đã mang tội bác bỏ sứ mạng của tất cả Thiên Sứ chỉ vì chúng đã bác bỏ sứ mạng của Nuh, chúng đã bị TA nhấn chìm trong biển, việc trừng phạt chúng là bằng chứng về năng lực của TA đối với đám người bất công và TA cũng đã chuẩn bị sẵn cho đám người bất công một hình phạt đau đớn trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(25,38,'25:38','Và TA cũng đã tiêu diệt người dân ''Ad của Hud, người dân Thamud của Saleh, người dân Al-Bi''r và TA cũng đã tiêu diệt biết bao cộng đồng giữa ba nhóm người đó.');
INSERT INTO "vn_mokhtasar" VALUES(25,39,'25:39','Và tất cả những nhóm người bị tiêu diệt trước đây đều được TA kể lại quá trình tiêu diệt chúng cho các ngươi nghe để các ngươi lấy đó làm bài học, tất cả chúng bị tiêu diệt vì lý do vô đức tin và ngông cuồng.');
INSERT INTO "vn_mokhtasar" VALUES(25,40,'25:40','Và chắc chắn nhóm người phủ nhận trong quần chúng của Ngươi - khi chúng hướng đến Sham - đi ngang qua ngôi làng của dân chúng Lut đã bị trận mưa đá trừng phạt vì tội lỗi ô nhục để chúng làm bài học, chẳng lẽ chúng đã không nhìn thấy ngôi làng đó sao? Không, thật ra chúng chỉ không tin sẽ được phục sinh để tính sổ sau đó mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(25,41,'25:41','Và khi gặp Ngươi - hỡi Thiên Sứ - đám người vô đức tin này chế nhạo Ngươi bằng câu: Liệu đây có phải là người đã được Allah gởi đến cho chúng ta với sứ mạng Thiên Sứ thật chăng?!');
INSERT INTO "vn_mokhtasar" VALUES(25,42,'25:42','Chúng nói: "Quả thật, suýt nữa thì Y đã khiến chúng ta từ bỏ việc thờ phượng thần linh của chúng ta rồi, giá như chúng ta không đủ kiên trì thì chúng ta đã bị những bằng chứng của Y đánh lừa mất rồi." Ngươi cứ mặc chúng, rồi đây chúng sẽ sớm biết khi tận mắt nhìn thấy hình phạt sau khi được phục sinh từ cõi mộ trong Ngày Tận Thế, chúng mới vỡ lẽ ai mới thật sự là kẻ bị lầm lạc?');
INSERT INTO "vn_mokhtasar" VALUES(25,43,'25:43','Há Ngươi - hỡi Thiên Sứ - đã không nhìn thấy những kẻ đã tôn dục vọng của bản thân lên làm thần linh để y phục tùng hay sao? Phải chăng Ngươi có trách nhiệm hộ mệnh ban đức tin Iman cho y và cấm cản y khỏi sự vô thần?');
INSERT INTO "vn_mokhtasar" VALUES(25,44,'25:44','Liệu Ngươi - hỡi Thiên Sứ - cho là đa số những kẻ mà Ngươi gọi họ đến với việc tôn thờ và phục tùng một mình Allah biết lắng nghe và chấp nhận hoặc chúng nhận thức được các bằng chứng đó chăng? Không, chúng chẳng khác gì loài súc vật không thể nghe, không thể nhận thức và càng không hiểu biết, chúng còn lạc đường xa hơn súc vật.');
INSERT INTO "vn_mokhtasar" VALUES(25,45,'25:45','Ngươi không thấy sao - hỡi Thiên Sứ - vết tích tạo hóa của Allah khi Ngài trải cái bóng trên mặt đất, nếu muốn Ngài đã giữ yên không cho chúng cử động. Sau đó, TA đã tạo mặt trời làm bằng chứng cho việc kéo dài hoặc thu ngắn cái bóng.');
INSERT INTO "vn_mokhtasar" VALUES(25,46,'25:46','Sau đó, TA đã túm lấy cái bóng thật chậm rãi, từ từ, phụ thuộc vào sự nhô lên của mặt trời.');
INSERT INTO "vn_mokhtasar" VALUES(25,47,'25:47','Và Allah là Đấng đã làm ban đêm như một tấm chăn đắp cho các ngươi và che phủ các sự vật, Ngài đã làm giấc ngủ thành một sự nghỉ ngơi cho các ngươi sau một cuộc lao động, và Ngài làm ban ngày thành thời gian lý tưởng để các ngươi lao động, sản xuất và tìm kiếm kế sinh nhai.');
INSERT INTO "vn_mokhtasar" VALUES(25,48,'25:48','Và Ngài là Đấng gởi những cơn gió mang tin vui báo hiệu về một cơn mưa được trút xuống cho đám nô lệ của Ngài từ lòng thương xót của Ngài, và Ngài đã trút xuống từ bầu trời những cơn mưa tinh khiết để tẩy rửa.');
INSERT INTO "vn_mokhtasar" VALUES(25,49,'25:49','TA dùng nước mưa đã trút xuống đó để phục hồi sự sống cho vùng đất chết khô, làm mọc ra cây trái, thảo mộc xanh tươi và dùng làm nguồn nước uống cho nhiều loại tạo hóa mà Ngài đã tạo trong đó có súc vật và con người.');
INSERT INTO "vn_mokhtasar" VALUES(25,50,'25:50','Và TA đã trình bày biết bao bằng chứng và lý lẽ để chúng lấy đó làm bài học, nhưng đa số nhân loại thường vô ơn và phủ nhận sự thật.');
INSERT INTO "vn_mokhtasar" VALUES(25,51,'25:51','Và nếu muốn là TA đã gởi đến mỗi ngôi làng một Thiên Sứ để cảnh báo họ, để dọa họ về hình phạt của Allah nhưng TA đã không làm thế, TA chỉ gởi Muhammad đảm nhận sứ mạng Thiên Sứ đến với toàn thể loài người.');
INSERT INTO "vn_mokhtasar" VALUES(25,52,'25:52','Thế nên, Ngươi chớ nghe theo lời đám người vô đức tin khi chúng muốn Ngươi chiều theo chúng, tâng bốc chúng về những gì chúng đã gợi ý với Ngươi, Ngươi hãy dùng Qur''an mà đấu tranh với chúng bằng cuộc đấu tranh mạnh mẽ với sự kiên nhẫn trên mọi gây hại của chúng khi chúng cố ngăn chặn con đường kêu gọi đến với Allah của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(25,53,'25:53','Và Allah Hiển Vinh là Đấng đã tách biệt hai nguồn nước mặn và nước ngọt bởi một vách chắc vô hình cản chúng quyện vào nhau.');
INSERT INTO "vn_mokhtasar" VALUES(25,54,'25:54','Và Ngài đã tạo hóa con người từ tinh dịch của đàn ông và phụ nữ, Ngài kết chặt loài người bằng hai mối quan hệ ruột thịt và kết hôn. Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng toàn năng, không gì làm Ngài bất lực và một trong những quyền năng của Ngài là Ngài đã tạo ra con người từ tinh dịch của đàn ông và đàn bà.');
INSERT INTO "vn_mokhtasar" VALUES(25,55,'25:55','Và nhóm người vô đức tin đã thờ phượng ngoài Allah những bục tượng không giúp gì được cho họ khi họ phục tùng chúng và cũng không thể hại được họ khi họ bất tuân chúng, và nhóm người vô đức tin đã ùa theo lũ Shaytan làm những điều làm Allah giận dữ.');
INSERT INTO "vn_mokhtasar" VALUES(25,56,'25:56','Và TA chỉ gởi Ngươi - hỡi Thiên Sứ - với sứ mạng báo tin mừng cho ai phục tùng Allah bằng đức tin Iman và năng hành đạo, đồng thời cảnh báo những kẻ bất tuân bằng sự vô đức tin và chống đối.');
INSERT INTO "vn_mokhtasar" VALUES(25,57,'25:57','Ngươi hãy bảo - hỡi Thiên Sứ - Ta không đòi hỏi các ngươi tiền thù lao cho sứ mạng truyền bá của Ta ngoại trừ ai đó tự nguyện muốn chọn con đường bố thí để hài lòng Allah thì hãy làm.');
INSERT INTO "vn_mokhtasar" VALUES(25,58,'25:58','Và Ngươi - hỡi Thiên Sứ - hãy phó thác mọi công việc của Ngươi cho Allah, Đấng Sống Mãi, Đấng Bất Diệt không bao giờ chết, Ngài vượt bên trên tất cả mọi khiếm khuyết, và một mình Ngài biết tận tường tội lỗi của đám nô lệ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(25,59,'25:59','Ngài đã tạo ra các tầng trời, trái đất và vạn vật giữa chúng trong sáu ngày, sau đó Ngài ngự bên trên chiếc Ngai Vương bằng thuộc tính tối cao của Ngài, Ngài là Đấng Rất Mực Độ Lượng, hãy cầu xin Ngài - hỡi Thiên Sứ - Ngài tinh thông tất cả, Ngài là Allah, Đấng không gì che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(25,60,'25:60','Và khi có lời bảo người vô đức tin: Các ngươi hãy quỳ lạy Đấng Rất Mực Độ Lượng. Chúng nói: Không, bọn ta không quỳ lạy Đấng Rất Mực Độ Lượng đó, Đấng đó là ai vậy? Bọn ta không biết và cũng không công nhận Ngài, sao Ngươi lại ra lệnh bọn ta quỳ lạy Ngài trong khi bọn ta không biết về Ngài? Việc bảo chúng quỳ lạy khiến chúng càng không tin Allah nhiều hơn.');
INSERT INTO "vn_mokhtasar" VALUES(25,61,'25:61','Thật phúc thay Đấng đã tạo cho bầu trời nhiều tinh tú và vì sao chỉ đường, đã tạo cho nó mặt trời như chiếc đèn chiếu sáng và mặt trăng chiếu ánh hào xuống mặt đất vào thời gian không có ánh sáng mặt trời.');
INSERT INTO "vn_mokhtasar" VALUES(25,62,'25:62','Và Allah là Đấng thay đổi ban đêm và ban ngày liên tiếp nhau để dành cho ai suy ngẫm về các dấu hiệu của Allah để được hướng dẫn hoặc muốn cám ơn Allah về hồng ân của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(25,63,'25:63','Và đám nô lệ có đức tin của Đấng Arrahman là nhóm người đi đứng với dạng điệu khiêm tốn và ôn tồn. Khi họ bị nhóm người thiếu hiểu biết gây sự thì họ không dùng cách tương tự để đáp trả mà họ dùng lời lẽ lịch thiệp.');
INSERT INTO "vn_mokhtasar" VALUES(25,64,'25:64','Họ là những ai tận dụng màn đêm để cúi đầu quỳ lạy Thượng Đế, đứng hành lễ Salah trên đôi chân của mình.');
INSERT INTO "vn_mokhtasar" VALUES(25,65,'25:65','Họ là những ai khẩn cầu trong lời cầu xin: Lạy Thượng Đế của bầy tôi, xin kéo Hỏa Ngục rời xa bầy tôi, quả thật hình phạt Hỏa Ngục sẽ mãi mãi song hành cùng nhóm người chết trong tình trạng vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(25,66,'25:66','Chắc chắn đó là một nơi ở tồi tệ nhất, kinh tởm nhất cho ai ở trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(25,67,'25:67','Và những ai mà họ khi chi tiêu thì không phung phí và cũng không bủn xỉn với những gì bắt buộc phải chi dùng cho bản thân hoặc những điều khác, họ luôn chi dùng theo tiêu chuẩn trung bình, đúng mực.');
INSERT INTO "vn_mokhtasar" VALUES(25,68,'25:68','Và những ai mà họ không hề khẩn cầu bất cứ thần linh nào khác cùng Allah Hiển Vinh, không sát hại bất cứ sinh mạng nào được Allah bảo vệ ngoại trừ Ngài cho phép giết như kẻ sát nhân hoặc phản đạo hoặc ngoại tình, và họ không vi phạm Zina. Đối với ai vi phạm những đại tội đó sẽ gánh chịu hình phạt đau đớn trong Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(25,69,'25:69','Vào Ngày Tận Thế hình phạt trừng trị được tăng gấp đôi và y sẽ phải mãi mãi chịu hình phạt một cách nhục nhã.');
INSERT INTO "vn_mokhtasar" VALUES(25,70,'25:70','Nhưng ai biết hối cải, biết tin tưởng Allah trở lại và siêng năng hành đạo chứng minh được sự hối cải của y là thật. Đó là nhóm người được Allah thay đổi việc làm xấu của y thành việc tốt, Allah luôn tha thứ tội lỗi của những ai biết sám hối, và Ngài nhân từ với họ.');
INSERT INTO "vn_mokhtasar" VALUES(25,71,'25:71','Và ai sám hối với Allah và kiên trì một cách thật lòng trong việc hành đạo và từ bỏ tội lỗi thì chắc chắn sự sám hối đó được chấp nhận.');
INSERT INTO "vn_mokhtasar" VALUES(25,72,'25:72','Và những ai mà họ không tham gia vào những điều giả dối như những chốn ăn chơi tội lỗi, những thú vui Haram và khi đi ngang qua những cuộc vui tầm phào làm mất nhân phẩm thì họ làm ngơ đi qua, họ không để bản thân trà trộn vào những chỗ như thế.');
INSERT INTO "vn_mokhtasar" VALUES(25,73,'25:73','Những ai mà khi các câu Kinh của Allah được xướng đọc cho họ nghe hoặc được mang ra cho họ thấy thì họ không hề tỏ vẻ mắt mù tai điếc.');
INSERT INTO "vn_mokhtasar" VALUES(25,74,'25:74','Và những ai mà họ thường khấn vái trong lời cầu xin: Lạy Thượng Đế của bầy tôi, xin Ngài làm cho vợ con bầy tôi trở thành thú vui trong đôi mắt của bầy tôi bởi lòng kính sợ Ngài và ngay chính trên Chân Lý của Ngài; xin Ngài bổ nhiệm bầy tôi trở thành lãnh đạo mẫu mực cương trực của những người ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(25,75,'25:75','Những người được miêu tả với những thuộc tính đó sẽ được ban thưởng ở trong những căn phòng cao sang của tầng Firdaws, tầng cao nhất trong Thiên Đàng vì lý do họ đã nhẫn nại phục tùng Allah và họ sẽ được Thiên Thần chào đón bằng lời chào an lành.');
INSERT INTO "vn_mokhtasar" VALUES(25,76,'25:76','Họ sẽ ở trong đó muôn đời, một chốn ở tuyệt vời cho ai ở trong nó.');
INSERT INTO "vn_mokhtasar" VALUES(25,77,'25:77','Ngươi - hỡi Thiên Sứ - hãy nói với nhóm người chìm sâu trong vô đức tin: Thượng Đế của các ngươi bất cần đến nguồn lợi trong việc ngoan đạo của các ngươi, nếu không có đám nô lệ đã cầu xin, đã khấn vái Ngài thì Ngài đã không thèm quan tâm đến các ngươi, do các ngươi đã cho rằng Thiên Sứ của Ngài là lường gạt khi Y đến với các ngươi, chắc chắn các ngươi sẽ phải chịu hậu quả cho việc phủ nhận đó, nó sẽ bám sát các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(26,1,'26:1','(Ta Sin Mim) những chữ cái, ý nghĩa của nó đã được giải thích ở chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(26,2,'26:2','Đó là những câu Kinh Qur''an rõ ràng giúp phân biệt Chân Lý và điều ngụy tạo.');
INSERT INTO "vn_mokhtasar" VALUES(26,3,'26:3','Có thể Ngươi - hỡi Thiên Sứ - vì quá mong muốn chúng được hướng dẫn khiến Ngươi buồn rầu và sự buồn rầu đó giết dần giết mòn thân xác Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(26,4,'26:4','Nếu muốn, TA đã ban từ trời xuống một phép màu cho chúng thì chúng đã cúi đầu thần phục nhưng TA đã không làm thế để thử thách xem chúng có tin tưởng vào điều vô hình hay không?');
INSERT INTO "vn_mokhtasar" VALUES(26,5,'26:5','Và bất cứ điều gì được thiên khải xuống từ Đấng Arrahman cho nhóm người đa thần để chứng minh chỉ riêng Ngài đáng được tôn thờ và phục tùng cũng như để chứng minh sự thật về sứ mạng của Thiên Sứ của Ngài thì chúng đều không nghe và không tin.');
INSERT INTO "vn_mokhtasar" VALUES(26,6,'26:6','Chúng nhất quyết chối bỏ những gì được Thiên Sứ mang đến cho chúng, cứ mặc chúng rồi đây chúng sẽ nhận được nguồn thông tin chính xác chứng nhận những gì mà chúng đã chế nhạo và chúng phải gánh lấy hình phạt.');
INSERT INTO "vn_mokhtasar" VALUES(26,7,'26:7','Sao nhóm người chìm sâu trong sự vô đức tin đã không nhìn khắp thế gian việc TA cho mọc ra biết bao cây cối và thảo mộc tạo nên khung cảnh tuyệt vời, mang lại nhiều nguồn lợi to lớn?');
INSERT INTO "vn_mokhtasar" VALUES(26,8,'26:8','Chắc chắn, trong việc cho đất mọc lên nhiều loại cây cối và thảo mộc là bằng chứng khẳng định quyền năng của Allah trong việc phục sinh người chết, tuy nhiên đa số nhân loại lại không chịu tin.');
INSERT INTO "vn_mokhtasar" VALUES(26,9,'26:9','Và chắc chắn Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng Quyền Lực và Hùng Mạnh, không thế lực nào đánh bại Ngài, nhưng Ngài luôn nhân từ với đám nô lệ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(26,10,'26:10','Và hãy nhớ lại - hỡi Thiên Sứ - khi Thượng Đế của Ngươi gọi Musa ra lệnh Y đến gặp đám người sai quấy đã phủ nhận Allah và áp bức dân chúng làm nô lệ.');
INSERT INTO "vn_mokhtasar" VALUES(26,11,'26:11','Và chúng là phe đảng của Fir''awn, hãy nhẹ nhàng và khoan dung bảo chúng kính sợ Allah trong việc thực thi những điều Ngài ra lệnh va tránh xa những điều Ngài ngăn cấm.');
INSERT INTO "vn_mokhtasar" VALUES(26,12,'26:12','Musa lo lắng nói: Quả thật, bề tôi sợ họ sẽ cho rằng bề tôi nói dối nếu bề tôi truyền tải đến họ thông tin từ Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(26,13,'26:13','Và việc họ bác bỏ sứ mạng khiến bề tôi hồi hộp không thể nói lưu lót, xin hãy cử đại Thiên Thần Jibril đến bổ nhiệm người anh (em) của bề tôi, Harun, theo hỗ trợ bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(26,14,'26:14','Và bề tôi đã phạm tội giết một tên Qibti (thuộc hoàng gia) nên bề tôi sợ họ sẽ giết bề tôi trả thù.');
INSERT INTO "vn_mokhtasar" VALUES(26,15,'26:15','Allah phán bảo Musa: Không đâu, chúng sẽ không giết Ngươi, hai anh em nhà Ngươi hãy mang những dấu lạ của TA khẳng định sự thật về sứ mạng của Hai Ngươi, TA luôn sát cánh phù hộ và lắng nghe tất cả lời lẽ các ngươi nói với chúng và lời lẽ chúng đáp lại các ngươi, TA sẽ không bỏ sót bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(26,16,'26:16','Hai Ngươi hãy đến gặp Fir''awn mà nói với chúng: Quả thật, Chúng Tôi là hai Thiên Sứ được gởi đến cho các ngươi từ Thượng Đế của vũ trụ và muôn loài.');
INSERT INTO "vn_mokhtasar" VALUES(26,17,'26:17','Xin bệ hạ thả bộ tộc Israel đi cùng Chúng Tôi.');
INSERT INTO "vn_mokhtasar" VALUES(26,18,'26:18','Fir''awn bảo Musa: Không phải ta đã nuôi nấng Ngươi từ lúc còn bé thơ và Ngươi đã trải qua tuổi đời của Ngươi cùng ta đó sao, điều gì đã khiến Ngươi tự nhận mình là Thiên Sứ vậy?');
INSERT INTO "vn_mokhtasar" VALUES(26,19,'26:19','Và Ngươi đã vi phạm một tội nghiêm trọng khi mà Người đã giết chết một người Qibti để tiếp tay người đàn ông trong bộ tộc của Ngươi, Ngươi là kẻ phụ ơn công lao của ta dành cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(26,20,'26:20','Musa nhận tội với Fir''awn: Đúng, Ta đã giết người đàn ông đó lúc ta thiếu hiểu biết trước khi lời mặc khải đến với Ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,21,'26:21','Ta đã bỏ trốn đến ngôi làng Madyan sau khi giết người vì lo sợ bị trả thù, từ đó Ta được Thượng Đế ban cho Ta kiến thức và chính thức bổ nhiệm Ta làm Thiên Sứ trong các vị Thiên Sứ của Ngài, những người mà Ngài cử phái họ đến với nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(26,22,'26:22','Và bệ hạ đã nuôi dưỡng Ta, đã không ép Ta làm nô lệ giống như bệ hạ đã cưỡng ép bộ tộc Israel làm nô lệ, Ngài kể công lao đó để trách móc Ta, nhưng Ta không vì điều đó mà bỏ việc mời gọi bệ hạ.');
INSERT INTO "vn_mokhtasar" VALUES(26,23,'26:23','Fir''awn hỏi Musa: Vậy Thượng Đế của vạn vật mà Ngươi đã tự xưng là Thiên Sứ của Ngài là ai vậy?');
INSERT INTO "vn_mokhtasar" VALUES(26,24,'26:24','Musa trả lời Fir''awn: Thượng Đế của vạn vật chính là Thượng Đế của các tầng trời, Thượng Đế của trái đất, và Thượng Đế của tất cả mọi thứ giữa trời đất, nếu bệ hạ thật lòng tin tưởng xin hãy thờ phượng Ngài duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(26,25,'26:25','Fir''awn nói với các đại thần quanh hắn: Các khanh có nghe rõ câu trả lời của Musa không, đúng là một lời khẳng định dối trá.');
INSERT INTO "vn_mokhtasar" VALUES(26,26,'26:26','Musa nói tiếp với họ: Allah là Thượng Đế của bệ hạ, cũng là Thượng Đế của tổ tiên của bệ hạ.');
INSERT INTO "vn_mokhtasar" VALUES(26,27,'26:27','Fir''awn nói: Chắc rằng tên tự nhận mình là Thiên Sứ được gởi đến với các ngươi là tên điên không biết mình đang nói gì và không nhận thức được lời mình nói.');
INSERT INTO "vn_mokhtasar" VALUES(26,28,'26:28','Musa nói: Allah là Đấng mà Ta mời gọi bệ hạ đến với Ngài là Thượng Đế của hướng Đông, Thượng Đế của hướng Tây và là Thượng Đế của tất cả vạn vật giữa hai hướng đó, nếu bệ hạ có khối óc nhận thức được điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(26,29,'26:29','Fir''awn dọa Musa sau khi bất lực đáp trả: Nếu Ngươi khăng khăng tôn thờ ai khác ngoài ta thì ta sẽ giam tù nhà Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(26,30,'26:30','Musa nói với Fir''awn: Lẽ nào kể cả khi Ta trình bày cho bệ hạ xem một bằng chứng xác định sứ mạng của Ta là thật được phái đến từ Allah, bệ hạ vẫn muốn tống giam Ta ư?');
INSERT INTO "vn_mokhtasar" VALUES(26,31,'26:31','Hắn bảo: Thế thì Ngươi hãy trình ra thứ mà Ngươi đã nói chứng minh sứ mạng của Ngươi là thật.');
INSERT INTO "vn_mokhtasar" VALUES(26,32,'26:32','Dứt lời, Musa ném cây gậy trên tay xuống đất thì nó biến thành một con rắn thật sự.');
INSERT INTO "vn_mokhtasar" VALUES(26,33,'26:33','Và Người cho bàn tay vào lòng ngực lúc đó vẫn bình thường nhưng khi rút tay ra thì nó phát sáng trắng toát mặc dù đó không phải do bị bệnh bạch tạng, tất cả những người chứng kiến đều nhìn thấy.');
INSERT INTO "vn_mokhtasar" VALUES(26,34,'26:34','Fir''awn nói với các chư thần quanh hắn: Chắc rằng đây là một tay phù thủy bậc thầy đây.');
INSERT INTO "vn_mokhtasar" VALUES(26,35,'26:35','Y muốn dùng ma thuật để đuổi các khanh rời khỏi quê hương của mình, vậy các khanh có kế sách nào không?');
INSERT INTO "vn_mokhtasar" VALUES(26,36,'26:36','Chư thần đáp lời hắn: Cứ tạm tha cho anh em nhà Y, đừng vội trừng phạt họ, chúng ta hãy thông báo triệu tập các pháp sư của các thành phố Ai Cập.');
INSERT INTO "vn_mokhtasar" VALUES(26,37,'26:37','Tất cả nhà phù thủy cao tay sẽ đến trình diện bệ hạ.');
INSERT INTO "vn_mokhtasar" VALUES(26,38,'26:38','Fir''awn đã tập trung nhiều tên phù thủy cao tay ấn đến thi đấu với Musa vào một ngày đã chọn tại một nơi đã định.');
INSERT INTO "vn_mokhtasar" VALUES(26,39,'26:39','Và có lời bảo dân chúng: Sao các ngươi không đến chứng kiến Musa thắng hay nhóm phù thủy thắng trong đại hội thi đấu?');
INSERT INTO "vn_mokhtasar" VALUES(26,40,'26:40','Hi vọng chúng ta sẽ theo tôn giáo của các nhà phù thủy nếu họ chiến thắng được Musa.');
INSERT INTO "vn_mokhtasar" VALUES(26,41,'26:41','Khi các nhà phù thủy đến trình diện Fir''awn để thắng được Musa, họ hỏi: Bẩm, chúng tôi có được ban thưởng vật chất hoặc niềm vui tinh thần nếu chúng tôi thắng được Musa không?');
INSERT INTO "vn_mokhtasar" VALUES(26,42,'26:42','Fir''awn hứa hẹn với họ: Chắc chắn là có, khi các khanh thắng cuộc sẽ được ta phong làm cận thần của ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,43,'26:43','Musa nói với lòng đầy tự tin sẽ được Allah phù hộ chiến thắng trước nhóm phù thủy: Các ngươi hãy ném đi những sợi dây, những cây gậy của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(26,44,'26:44','Họ liền ném những sợi dây và những cây gậy và nói: Với sức mạnh của Fir''awn, chắc chắn chúng tôi sẽ chiến thắng và Musa sẽ phải thất bại.');
INSERT INTO "vn_mokhtasar" VALUES(26,45,'26:45','Musa cũng ném cây gậy của Người xuống, tức thời nó hóa thành con rắn nuốt chửng hết những thứ ma thuật lường gạt thiên hạ của những tên phù thủy kia.');
INSERT INTO "vn_mokhtasar" VALUES(26,46,'26:46','Ngay khi những nhà phù thủy tận mắt nhìn thấy cây gậy của Musa nuốt hết những gì họ dùng ma thuật ném xuống thì họ cuối đầu quỳ lạy.');
INSERT INTO "vn_mokhtasar" VALUES(26,47,'26:47','Họ nói: Chúng tôi đã tin tưởng vào Thượng Đế của tất cả tạo vật.');
INSERT INTO "vn_mokhtasar" VALUES(26,48,'26:48','Thượng Đế của Musa và Thượng Đế của Harun.');
INSERT INTO "vn_mokhtasar" VALUES(26,49,'26:49','Fir''awn phản đối niềm tin của nhóm phù thủy, nói: Sao các ngươi dám tin tưởng Musa trước khi ta cho phép điều đó? Chắc chắn Musa là tên đầu sỏ đã dạy ma thuật cho các ngươi, chính các ngươi thông đồng nhau thực hiện âm mưu trục xuất hết dân Ai Cập rời khỏi quê hương này, rồi các ngươi sẽ sớm biết hậu quả mà các ngươi phải gánh chịu, ta sẽ chặt chân này và tay kia của từng tên trong các ngươi, và ta sẽ đóng đinh các ngươi lên thân cây chà là, không ai trong các ngươi thoát được.');
INSERT INTO "vn_mokhtasar" VALUES(26,50,'26:50','Nhóm phù thủy nói vời Fir''awn: Không gì phải sợ với sự trừng phạt bị chặt tay chân và đóng đinh trên cuộc sống này, hình phạt đó rồi sẽ chấm dứt và chúng tôi sẽ về trình diện Thượng Đế của mình, rồi sẽ được Ngài đón nhận bằng lòng nhân từ của Ngài mãi mãi.');
INSERT INTO "vn_mokhtasar" VALUES(26,51,'26:51','Chúng tôi hi vọng được Allah xóa tội cho tội lỗi của chúng tôi trước đây đã phạm, vì chúng tôi là những người đầu tiên đã tin tưởng Musa và chứng nhận sứ mạng của Người.');
INSERT INTO "vn_mokhtasar" VALUES(26,52,'26:52','Và TA đã thiên khải ra lệnh cho Musa dẫn bộc tộc Israel chạy trốn vào ban đêm, bởi quả thật Fir''awn và quân lính của hắn sẽ truy sát họ.');
INSERT INTO "vn_mokhtasar" VALUES(26,53,'26:53','Fir''awn thực sự phái một toán quân đi khắp các thành phố tập trung binh lính để truy sát bộ tộc Israel khi biết tin họ đang chay trốn khỏi Ai Cập.');
INSERT INTO "vn_mokhtasar" VALUES(26,54,'26:54','Fir''awn nói lời xem thường bộ tộc Israel: Chúng vốn chỉ là một nhóm nhỏ bé.');
INSERT INTO "vn_mokhtasar" VALUES(26,55,'26:55','Bởi vì chúng đã có những hành động làm ta nổi giận.');
INSERT INTO "vn_mokhtasar" VALUES(26,56,'26:56','Và ta đã có sự chuẩn bị phòng ngừa đối với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(26,57,'26:57','Do đó, TA đã đuổi cổ Fir''awn và quân lính của hắn rời khỏi Ai Cập, một vùng đất dồi dào vườn tược và sông ngòi.');
INSERT INTO "vn_mokhtasar" VALUES(26,58,'26:58','Và có nhiều kho báu và những nơi ở tốt lành.');
INSERT INTO "vn_mokhtasar" VALUES(26,59,'26:59','Với những hưởng thụ mà TA đã tước khỏi Fir''awn và quân lính của hắn, TA đã cho bộ tộc Israel thừa kế sau đó ở vùng đất Sham.');
INSERT INTO "vn_mokhtasar" VALUES(26,60,'26:60','Fir''awn cùng quân lính đã xuất binh từ tờ mờ sáng để truy đuổi bộ tộc Israel.');
INSERT INTO "vn_mokhtasar" VALUES(26,61,'26:61','Khi phe Fir''awn và phe Musa nhìn thấy được nhau thì nhóm người của Musa lên tiếng: Chắc chắn Fir''awn và quân lính hắn sẽ đuổi kịp chúng ta, chúng ta sẽ không tài nào thoát khỏi.');
INSERT INTO "vn_mokhtasar" VALUES(26,62,'26:62','Musa nói với dân mình: Sự việc sẽ không xảy ra như điều các người tưởng đâu, chắc chắn Thượng Đế của Ta sẽ phù hộ Ta chiến thắng, Ngài sẽ hướng dẫn Ta, Ngài sẽ chỉ Ta con đường cứu rỗi.');
INSERT INTO "vn_mokhtasar" VALUES(26,63,'26:63','Lúc đó TA thiên khải cho Musa ra lệnh cho Y cầm gậy đánh xuống mặt biển, ngay lập tức mặt biển hiện ra mười hai con đường tương ứng với mười hai bộ lạc của bộ tộc Israel, mỗi tảng nước được tách ra từ biển tựa như ngọn núi khổng lồ sừng sững, kiên cố, nước không chảy xuống.');
INSERT INTO "vn_mokhtasar" VALUES(26,64,'26:64','Và TA kéo Fir''awn và phe của hắn đến gần mãi khi chúng bước vào biển luôn nghĩ đó là con đường.');
INSERT INTO "vn_mokhtasar" VALUES(26,65,'26:65','Và TA đã cứu mạng Musa và phe của Y trong bộ tộc Israel, không ai bị thương vong.');
INSERT INTO "vn_mokhtasar" VALUES(26,66,'26:66','Sau đó, TA đã tiêu diệu Fir''awn và phe của hắn chết chìm trong biển.');
INSERT INTO "vn_mokhtasar" VALUES(26,67,'26:67','Quả thật, trong sự việc tách biển để cứu Musa và để tiêu diệt Fir''awn và phe của hắn là bằng chứng khẳng định sứ mạng của Musa là thật, tiếc thay đa số phe của Fir''awn đã không tin điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(26,68,'26:68','Và chắc chắc Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng Quyền Lực, đánh bại tất cả kẻ thù của Ngài, và Ngài là Đấng Nhân Từ với ai biết ăn năn, sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(26,69,'26:69','Và Ngươi - hỡi Thiên Sứ - hãy xướng đọc cho chúng nghe về câu chuyện của Ibrahim.');
INSERT INTO "vn_mokhtasar" VALUES(26,70,'26:70','Khi Y thưa với cha, Azar, và dân mình: Vật gì được các người thờ phượng thay vì Allah?');
INSERT INTO "vn_mokhtasar" VALUES(26,71,'26:71','Mọi người đáp lại Y: Chúng tôi tôn thờ những bục tượng, chúng tôi sẽ hạ mình bám chặt sự thờ phượng này.');
INSERT INTO "vn_mokhtasar" VALUES(26,72,'26:72','Ibrahim hỏi họ: Những bục tượng đó có nghe lời thỉnh cầu của các người khi các người cầu xin chúng không?');
INSERT INTO "vn_mokhtasar" VALUES(26,73,'26:73','Hay là chúng giúp ích được các người khi phục tùng chúng hay là chúng sẽ hại các người khi bất tuân chúng?');
INSERT INTO "vn_mokhtasar" VALUES(26,74,'26:74','Họ đáp: Tất nhiên họ không biết nghe khi chúng tôi khẩn cầu, không thể giúp chúng tôi dù có phục tùng và cũng không thể hại chúng tôi khi bất tuân. Tóm lại, chúng tôi đã chứng kiến tổ tiên mình đã làm thế nên chúng tôi bắt chước theo họ.');
INSERT INTO "vn_mokhtasar" VALUES(26,75,'26:75','Ibrahim hỏi: Liệu các người có suy ngẫm về những bục tượng được thờ phượng ngoài Allah không vậy?');
INSERT INTO "vn_mokhtasar" VALUES(26,76,'26:76','Và luôn những thứ mà tổ tiên của các người thờ phượng trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(26,77,'26:77','Tất cả chúng, chắc chắn là kẻ thù của Ta, bởi chúng vốn là hư cấu còn Allah, Đấng Chúa Tể của tất cả vạn vật mới là thật.');
INSERT INTO "vn_mokhtasar" VALUES(26,78,'26:78','Là Đấng đã tạo ra Ta, chính Ngài sẽ hướng dẫn Ta đến điều tốt đẹp ở trần gian và ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(26,79,'26:79','Chỉ mình Ngài là Đấng đã cho Ta ăn khi Ta đói và cho Ta uống khi Ta khát.');
INSERT INTO "vn_mokhtasar" VALUES(26,80,'26:80','Và khi Ta bị đau ốm, Ngài là Đấng duy nhất cho Ta khỏi bệnh.');
INSERT INTO "vn_mokhtasar" VALUES(26,81,'26:81','Là Đấng duy nhất khiến Ta chết khi Ta hết tuổi thọ và Ngài sẽ phục sinh Ta sống lại sau khi đã chết.');
INSERT INTO "vn_mokhtasar" VALUES(26,82,'26:82','Là Đấng duy nhất mà Ta kỳ vọng tội lỗi của mình sẽ được xóa ở nơi Ngài trong Ngày thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(26,83,'26:83','Ibrahim khẩn cầu Thượng Đế mình: Lạy Thượng Đế, xin Ngài hãy ban cho bề tôi sự thấu hiểu tôn giáo, xin Ngaif thu nhận bề tôi vào nhóm người sùng đạo thuộc nhóm Nabi trước bề tôi bằng cách cho bề tôi được vào Thiên Đàng cùng họ.');
INSERT INTO "vn_mokhtasar" VALUES(26,84,'26:84','Xin hãy lưu danh bề tôi qua việc tuyên dương khen ngợi của những thế hệ sau bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(26,85,'26:85','Xin cho bề tôi được thừa kế những nơi ở trong Thiên Đàng, nơi mà Ngài cho đám nô lệ có đức tin của Ngài hưởng thụ, xin cho bề tôi được định cư nơi đó.');
INSERT INTO "vn_mokhtasar" VALUES(26,86,'26:86','Xin tha thứ cho cha của bề tôi, quả thật ông ta đã lệch khỏi Chân Lý vì tội Shirk. Lời cầu xin này của Ibrahim trước khi Allah trình bày cho Người biết cha Người là cư dân của Hỏa Ngục. Nhưng khi Người đã biết rõ thì Người đã vô can với ông ta và chấm dứt việc cầu xin cho ông ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,87,'26:87','Và xin Ngài đừng xỉ nhục bề tôi bằng hình phạt trong Ngày phục sinh nhân loại để thanh toán.');
INSERT INTO "vn_mokhtasar" VALUES(26,88,'26:88','Ngày hôm đó không có bất cứ tài sản nào mà con người đã thu gom được ở trần gian sẽ không thể trợ giúp được gì cho họ, và con cái của họ cũng thế.');
INSERT INTO "vn_mokhtasar" VALUES(26,89,'26:89','Ngoại trừ những ai đến trình diện Allah với tấm lòng trong sạch không Shirk (không tổ hợp bất cứ thần linh nào cùng với Allah) không giả tạo, không khoe khoang, không tự mãn, ngoài ra còn có tài sản bố thí vì Allah sẽ giúp ích con người và những đứa con cầu xin cho y.');
INSERT INTO "vn_mokhtasar" VALUES(26,90,'26:90','Và Thiên Đàng được mang đến gần nhóm người biết kính sợ Allah mà tuân thủ theo mệnh lệnh của Ngài cũng như tránh xa những gì Ngài ngăn cấm.');
INSERT INTO "vn_mokhtasar" VALUES(26,91,'26:91','Và Hỏa Ngục được phơi bày tại nơi triệu tập dành cho đám người lầm lạc khỏi con đường Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(26,92,'26:92','Và có lời hỏi nhục mạ chúng: Đâu là kết quả mà các ngươi đã thờ phượng những bục tượng?');
INSERT INTO "vn_mokhtasar" VALUES(26,93,'26:93','Các ngươi thờ phượng chúng thay vì Allah, nhưng chúng có giúp các ngươi bằng cách ngăn cản hình phạt của Allah khỏi các ngươi hoặc chúng tự cứu mình được bình an không?');
INSERT INTO "vn_mokhtasar" VALUES(26,94,'26:94','Thế là chúng lần lượt bị ném hết vào trong Hỏa Ngục, luôn cả những kẻ khiến chúng lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(26,95,'26:95','Và tất cả binh tướng của Iblis từ lũ Shaytan không một ai thoát khỏi cả.');
INSERT INTO "vn_mokhtasar" VALUES(26,96,'26:96','Nhóm đa thần thờ phượng ngoài Allah cùng với những ai đã hợp tác thờ phượng ngoài Allah sẽ tranh cãi với những thứ mà chúng đã thờ phượng, nói:');
INSERT INTO "vn_mokhtasar" VALUES(26,97,'26:97','Xin thề với Allah, chắc rằng chúng tôi đã lệch rất xa Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(26,98,'26:98','Khi bọn ta nâng các ngươi lên ngang hàng với Đấng Chúa Tể của tất cả vạn vật để thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(26,99,'26:99','Và không ai kéo bọn ta lệch khỏi con đường Chân Lý ngoài đám người tội lỗi kêu gọi bọn ta tôn thờ chúng thay vì Allah.');
INSERT INTO "vn_mokhtasar" VALUES(26,100,'26:100','Bọn ta không có ai đứng ra biện hộ cho bọn ta với Allah để được thoát khỏi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(26,101,'26:101','Và cũng không có một người bạn thân nào xin giùm cho bọn ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,102,'26:102','Ôi phải chi bọn ta có dịp trở lại cuộc sống trần gian thì bọn ta sẽ là nhóm người tin tưởng Allah.');
INSERT INTO "vn_mokhtasar" VALUES(26,103,'26:103','Rằng qua câu chuyện về Ibrahim và hậu quả của nhóm người chống đối là bài học cho nhóm người biết ngẫm nghĩ, nhưng đa số mọi người vẫn không tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(26,104,'26:104','Và chắc chắc Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng Quyền Lực, Ngài đánh bại tất cả kẻ thù của Ngài, và Ngài là Đấng Nhân Từ với những ai biết ăn năn, sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(26,105,'26:105','Người dân của Nuh bị kết tội chối bỏ tất cả Thiên Sứ bởi chúng đã chối bỏ Nuh, một vị Thiên Sứ được gởi đến cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(26,106,'26:106','Khi Nuh hỏi chúng: Sao các người không biết sợ Allah mà bỏ việc thờ phượng ngoài Ngài?');
INSERT INTO "vn_mokhtasar" VALUES(26,107,'26:107','Quả thật Ta là Thiên Sứ được Allah gởi đến cho các ngươi, Ta là người trung thực không thêm bớt bất cứ gì về bức Thông Điệp mà Allah thiên khải cho Ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,108,'26:108','Các ngươi hãy kính sợ Allah băng cách tuân thủ theo mệnh lệnh của Ngài và tránh xa những điều cấm của Ngài; và hãy nghe theo Ta về những gì Ta ra lệnh các ngươi và những gì Ta cấm các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(26,109,'26:109','Ta sẽ không yêu cầu các ngươi tiền thù lao do công Ta truyền tải bức Thông Điệp cho các ngươi mà phần thưởng đó sẽ do Allah, Đấng Chúa Tể của tất cả vạn vật ban cho Ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,110,'26:110','Các ngươi hãy kính sợ Allah băng cách tuân thủ theo mệnh lệnh của Ngài và tránh xa những điều cấm của Ngài; và hãy nghe theo Ta về những gì Ta ra lệnh các ngươi và những gì Ta cấm các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(26,111,'26:111','Dân chúng của Y đáp lại Y: Liệu bọn ta có đáng phải tin Ngươi không - hỡi Nuh - và bọn ta có đáng phải làm theo những gì Ngươi bảo không trong khi chỉ có nhóm người thấp hèn của xã hội mới theo Ngươi, không có một người quí tộc nào trong số đó cả?');
INSERT INTO "vn_mokhtasar" VALUES(26,112,'26:112','Nuh nói với chúng: Ta không biết gì về hành động của nhóm người có đức tin này? Ta không phải là người được ủy thác giám sát việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(26,113,'26:113','Việc thanh toán họ không ai ngoài Allah mới biết rõ họ đã làm gì dù bí mật hay công khai, không phải là việc của Ta, giá như mọi ngươi nhận thức được lời lẽ mình đã nói.');
INSERT INTO "vn_mokhtasar" VALUES(26,114,'26:114','Ta không phải là người xua đuổi những người có đức tin khi họ đáp lại lời mời gọi của Ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,115,'26:115','Sứ mạng của Ta chỉ là cảnh báo một cách rõ ràng cho các người về hình phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(26,116,'26:116','Dân chúng của Nuh hăm dọa Y: Nếu Ngươi không dừng việc kêu gọi bọn ta thì chắc Ngươi sẽ phải hứng chịu một trận ném đá.');
INSERT INTO "vn_mokhtasar" VALUES(26,117,'26:117','Nuh khấn vái Thượng Đế mình: Lạy Thượng Đế, dân chúng của bề tôi bảo bề tôi nói dối, họ không tin những gì bề tôi mang đến cho họ từ Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(26,118,'26:118','Xin Ngài hãy công minh phân xử bề tôi và họ, hãy tiêu diệt họ vì ngoan cố chìm trong sự ngụy tạo và xin hãy cứu bề tôi cũng như những ai đã theo bề tôi thuộc nhóm người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(26,119,'26:119','Ta đáp lại lời thỉnh cầu của Y, TA cứu Y cũng như nhóm người có đức tin theo Y trên chiếc thuyền chở đầy người và động vật.');
INSERT INTO "vn_mokhtasar" VALUES(26,120,'26:120','Sau đó, TA nhấn chìm hết số còn lại trong dân chúng của Nuh trong biển nước.');
INSERT INTO "vn_mokhtasar" VALUES(26,121,'26:121','Trong sự việc từ câu chuyện của Nuh cùng với dân mình; sự thành công của Nuh cùng với ai theo Y và hậu quả của nhóm vô đức tin là một bài học cho nhóm người biết ngẫm nghĩ, nhưng đa số mọi người vẫn không tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(26,122,'26:122','Và quả thật Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng Quyền Lực đánh bại tất cả kẻ thù của Ngài, và Ngài là Đấng Nhân Từ đối với ai biết ăn năn, sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(26,123,'26:123','Người dân ''Ad bị kết tội chối bỏ tất cả Thiên Sứ chỉ vì chúng chối bỏ Hud, Thiên Sứ được gởi đến cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(26,124,'26:124','Ngươi hãy nhớ lại khi Hud bảo chúng: Sao các người không sợ Allah mà bỏ việc thờ phượng ngoài Ngài?');
INSERT INTO "vn_mokhtasar" VALUES(26,125,'26:125','Rằng Ta là Thiên Sứ được Allah gởi đến cho các người, trung thực không thêm bớt bất cứ gì về bức Thông Điệp mà Allah thiên khải lệnh Ta truyền tải đến các người.');
INSERT INTO "vn_mokhtasar" VALUES(26,126,'26:126','Các người hãy kính sợ Allah bằng cách thực hiện những gì Ngài ra lệnh và tránh xa những gì Ngài nghiêm cấm, đồng thời hãy vâng lời Ta mà chấp hành những gì Ta sai bảo và những gì Ta cấm.');
INSERT INTO "vn_mokhtasar" VALUES(26,127,'26:127','Ta sẽ không yêu cầu các người tiền thù lao cho công lao truyền tải bức Thông Điệp, phần thưởng đó sẽ do Allah Đấng Chúa Tể của tất cả vạn vật ban cho Ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,128,'26:128','Chắc các người muốn xây cất nhà trên cao để khoe khoang, thể hiện bản thân trong khi điều đó không mang lại bất cứ lợi ích nào cho các người, không ở trần gian và cả ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(26,129,'26:129','Các người muốn xây dựng những tòa thành, những tòa lâu đài, cứ như thể rằng các người sẽ sống bất tử chốn trần gian này và không chuyển đi đâu cả?');
INSERT INTO "vn_mokhtasar" VALUES(26,130,'26:130','Và khi các người giết chóc hoặc trân áp thì các ngươi như đám hung tàn vô nhân đạo, không có chút lòng thương xót.');
INSERT INTO "vn_mokhtasar" VALUES(26,131,'26:131','Các người hãy kính sợ Allah bằng cách thực hiện những gì Ngài ra lệnh và tránh xa những gì Ngài nghiêm cấm, đồng thời hãy vâng lời Ta mà chấp hành những gì Ta sai bảo và những gì Ta cấm.');
INSERT INTO "vn_mokhtasar" VALUES(26,132,'26:132','Và các người hãy sợ sự giận dữ của Allah, Đấng đã ban bố cho các người những thứ mà các người không hề biết.');
INSERT INTO "vn_mokhtasar" VALUES(26,133,'26:133','Ngài đã ban cho các người súc vật và con cái.');
INSERT INTO "vn_mokhtasar" VALUES(26,134,'26:134','Ngài ban cho các người vườn tược và những dòng suối chảy.');
INSERT INTO "vn_mokhtasar" VALUES(26,135,'26:135','Quả thật, Ta lo sợ cho các ngươi - hỡi dân Ta - về hình phạt trong Ngày Vĩ Đại, đó là Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(26,136,'26:136','Dân chúng đáp lại Y: Đối với bọn ta, dù Ngươi có nhắc nhở hay không thì cũng vậy, bọn ta sẽ không tin Ngươi đâu, sẽ không từ bỏ những gì bọn ta đang thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(26,137,'26:137','Điều này không khác gì tôn giáo, tập tục và tư tưởng cổ xưa.');
INSERT INTO "vn_mokhtasar" VALUES(26,138,'26:138','Và bọn ta chắc chắn sẽ không bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(26,139,'26:139','Chúng ngoan cố chối bỏ Hud, vị Thiên Sứ được gởi đến cho chúng nên chúng đã bị tiêu diệt bởi cơn gió tàn khốc. Quả thật, đó là một bài học cho nhóm người biết suy nghĩ nhưng đa số bọn chúng vẫn không tin.');
INSERT INTO "vn_mokhtasar" VALUES(26,140,'26:140','Và chắc chắc Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng Quyền Lực đánh bại tất cả kẻ thù của Ngài, và Ngài là Đấng Nhân Từ đối với ai biết ăn năn, sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(26,141,'26:141','Người dân Thamud bị kết tội chối bỏ tất cả Thiên Sứ chỉ vì chúng chối bỏ Saleh, vị Thiên Sứ được gởi đến với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(26,142,'26:142','Khi Saleh, người anh em của chúng bảo: Sao các người không sợ Allah mà bỏ việc thờ phượng ngoài Ngài?');
INSERT INTO "vn_mokhtasar" VALUES(26,143,'26:143','Quả thật Ta chính là Thiên Sứ được Allah gởi đến cho các người, Ta trung trực với sứ mạng đã nhận, không thêm bớt bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(26,144,'26:144','Các người hãy kính sợ Allah bằng cách thực hiện những gì Ngài ra lệnh và tránh xa những gì Ngài nghiêm cấm, đồng thời hãy vâng lời Ta mà chấp hành những gì Ta sai bảo và những gì Ta cấm.');
INSERT INTO "vn_mokhtasar" VALUES(26,145,'26:145','Ta không yêu cầu các người trả tiền thù lao cho công sức truyền tải bức Thông Điệp đến các người, phần thưởng đó sẽ do Allah Đấng Chúa Tể của tất cả vạn vật ban cho Ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,146,'26:146','Há các người an tâm cho rằng mình được sống hưởng thụ mãi thế này, không phải lo lắng điều gì nữa sao?');
INSERT INTO "vn_mokhtasar" VALUES(26,147,'26:147','Trong những khu vườn và những dòng suối chảy.');
INSERT INTO "vn_mokhtasar" VALUES(26,148,'26:148','Với đồng ruộng và vườn chà là cho ra trái chín mộng.');
INSERT INTO "vn_mokhtasar" VALUES(26,149,'26:149','Và các người đục núi làm nhà để ở và các người đã khéo tay làm được nó.');
INSERT INTO "vn_mokhtasar" VALUES(26,150,'26:150','Các người hãy kính sợ Allah bằng cách thực hiện những gì Ngài ra lệnh và tránh xa những gì Ngài nghiêm cấm, đồng thời hãy vâng lời Ta mà chấp hành những gì Ta sai bảo và những gì Ta cấm.');
INSERT INTO "vn_mokhtasar" VALUES(26,151,'26:151','Các ngươi chớ dại dột nghe theo lời của đám người thái quá ép bản thân mình làm tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(26,152,'26:152','Chúng là những kẻ đi khắp thế gian để gieo rắc tội lỗi, chúng không muốn cải thiện bản thân trở lại phục tùng Allah.');
INSERT INTO "vn_mokhtasar" VALUES(26,153,'26:153','Dân chúng đáp lại Y: Bọn ta quả quyết Ngươi đã bị trúng bùa rất nhiều lần nên tâm trí Ngươi không còn nữa.');
INSERT INTO "vn_mokhtasar" VALUES(26,154,'26:154','Ngươi cũng chỉ là một người phàm như bọn ta, không có ưu điểm gì trội hơn bọn ta sao lại tự xưng là Thiên Sứ, để chứng minh lời nói của mình là thật thì Ngươi hãy mang đến thứ gì đó để khẳng định xem nào.');
INSERT INTO "vn_mokhtasar" VALUES(26,155,'26:155','Saleh nói với chúng: Allah cho các người một bằng chứng, đây con lạc đà cái được Allah rút ra từ tảng đá to, con lạc đà bằng xương bằng thịt thực sự, nó chỉ uống phần nước của nó trong ngày dành riêng cho nó và các người cũng chỉ được uống nước trong ngày dành cho mình.');
INSERT INTO "vn_mokhtasar" VALUES(26,156,'26:156','Cấm các người gây hại nó như gây thương tích hoặc đánh đập hoặc bằng bất cứ hình thức nào, điều đó sẽ khiến các người rơi vào sự trừng phạt của Allah trong một Ngày Vĩ Đại, một Ngày đại họa cho các người.');
INSERT INTO "vn_mokhtasar" VALUES(26,157,'26:157','Saleh bảo chúng hãy tránh xa mọi hình thức gây hại đến con vật nhưng chúng vẫn đâm cổ con lạc đà có chủ định, rồi chúng trở nên hối hận cho hành động của mình khi nhận biết phải gánh chịu hình phạt sẽ giáng xuống chúng, nhưng việc hối nhận lúc nhìn thấy hình phạt không giúp ích được gì.');
INSERT INTO "vn_mokhtasar" VALUES(26,158,'26:158','Thế là chúng bị trừng phạt đúng y như lời đã cảnh báo từ trước, đó là chúng gặp phải một trận động đất và âm thanh chát chúa. Chắc rằng qua câu chuyện về Saleh và dân chúng của Y là một bài học cho nhóm người biết suy nghĩ nhưng đa số bọn chúng vẫn không tin.');
INSERT INTO "vn_mokhtasar" VALUES(26,159,'26:159','Và chắc chắc Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng Quyền Lực đánh bại tất cả kẻ thù, và là Đấng Nhân Từ đối với ai biết ăn năn, sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(26,160,'26:160','Người dân của Lut bị kết tội chối bỏ tất cả Thiên Sứ bởi họ đã chối bỏ Lut, một vị Thiên Sứ được gởi đến cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(26,161,'26:161','Khi Lut, người anh em của chúng bảo chúng: Sao các người không sợ Allah mà bỏ việc thờ phượng ngoài Ngài?');
INSERT INTO "vn_mokhtasar" VALUES(26,162,'26:162','Quả thật Ta chính là Thiên Sứ được Allah gởi đến cho các người, Ta trung trực với sứ mạng đã nhận, không thêm bớt bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(26,163,'26:163','Các người hãy kính sợ Allah bằng cách thực hiện những gì Ngài ra lệnh và tránh xa những gì Ngài nghiêm cấm, đồng thời hãy vâng lời Ta mà chấp hành những gì Ta sai bảo và những gì Ta cấm.');
INSERT INTO "vn_mokhtasar" VALUES(26,164,'26:164','Ta sẽ không yêu cầu các người tiền thù lao cho công sức truyền tải bức Thông Điệp cho các người, phần thưởng đó sẽ do Allah Đấng Chúa Tể của tất cả vạn vật ban cho Ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,165,'26:165','Sao các người lại phải giải tỏa sinh lý qua đường hậu môn của đàn ông?');
INSERT INTO "vn_mokhtasar" VALUES(26,166,'26:166','Sao các người lại bỏ cách mà Allah đã tạo ra để các người thỏa mãn sinh lý khi ân ái với vợ bằng con đường sinh con vậy? Không, các người đã thái quá vượt giới luật của Allah qua việc quan hệ dị biệt này.');
INSERT INTO "vn_mokhtasar" VALUES(26,167,'26:167','Dân chúng hăm dọa Lut: Nếu ngươi vẫn chưa chịu ngưng hành động cấm cản bọn ta làm điều này, chắc rằng nhà Ngươi và những ai theo Ngươi sẽ bị trục xuất khỏi xứ.');
INSERT INTO "vn_mokhtasar" VALUES(26,168,'26:168','Lut bảo họ: Đối với việc mà các người đang làm là thứ đáng ghét, bẩn thỉu.');
INSERT INTO "vn_mokhtasar" VALUES(26,169,'26:169','Lut khấn vái Thượng Đế của mình: Lạy Thượng Đế của bề tôi, xin Ngài hãy cứu bề tôi và gia đình bề tôi thoát khỏi hình phạt mà đám người này sẽ gánh chịu do hành động thối tha của họ.');
INSERT INTO "vn_mokhtasar" VALUES(26,170,'26:170','TA đã chấp nhận lời cầu xin của Y mà cứu Y và cả gia đình của Y.');
INSERT INTO "vn_mokhtasar" VALUES(26,171,'26:171','Ngoại trừ bà vợ của Y, đó là người vô đức tin, nên sẽ bị tiêu diệt.');
INSERT INTO "vn_mokhtasar" VALUES(26,172,'26:172','Sau khi Lut cùng gia đình rời khỏi thị trấn (Sodom), TA đã tàn phá, tiêu diệt sạch nhóm người còn lại.');
INSERT INTO "vn_mokhtasar" VALUES(26,173,'26:173','TA trút xuống từ trời xuống một trận đá dữ dội, thật là tồi tệ cho trận mưa trút lên đám người đã được Lut cảnh báo về hình phạt của Allah nhưng vẫn ngoan cố lún sâu trong tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(26,174,'26:174','Rằng qua câu chuyện về hình phạt giáng xuống dân chúng của Lut vì hành động bẩn thỉu của chúng là bài học cho nhóm người biết ngẫm nghĩ, nhưng đa số mọi người vẫn không tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(26,175,'26:175','Và chắc chắc Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng Quyền Lực đánh bại tất cả kẻ thù của Ngài, và Ngài là Đấng Nhân Từ đối với ai biết ăn năn, sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(26,176,'26:176','Dân chúng của ngôi làng có rừng gỗ quý, gần với ngôi làng Madyan bị kết tội chối bỏ tất cả Thiên Sứ vì chúng đã chối bỏ Shu-''aib, một Thiên Sứ được gởi đến cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(26,177,'26:177','Khi Shu-''aib hỏi họ: Sao các người không sợ Allah mà bỏ việc thờ phượng ngoài Ngài?');
INSERT INTO "vn_mokhtasar" VALUES(26,178,'26:178','Quả thật Ta chính là Thiên Sứ được Allah gởi đến cho các người, Ta trung trực với sứ mạng đã nhận, không thêm bớt bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(26,179,'26:179','Các người hãy kính sợ Allah bằng cách thực hiện những gì Ngài ra lệnh và tránh xa những gì Ngài nghiêm cấm, đồng thời hãy vâng lời Ta mà chấp hành những gì Ta sai bảo và những gì Ta cấm.');
INSERT INTO "vn_mokhtasar" VALUES(26,180,'26:180','Ta sẽ không yêu cầu các người tiền thù lao cho công sức truyền tải bức Thông Điệp đến các người, phần thưởng đó sẽ do Allah Đấng Chúa Tể của tất cả vạn vật ban cho Ta.');
INSERT INTO "vn_mokhtasar" VALUES(26,181,'26:181','Các người hãy đong cho đầy đủ số lượng khi bán cho thiên hạ, chớ gian lận mà đong thiếu trong mua bán.');
INSERT INTO "vn_mokhtasar" VALUES(26,182,'26:182','Khi cân bán cho người khác thì các người hãy cân cho đúng, hãy để cho đĩa cân ngang bằng nhau.');
INSERT INTO "vn_mokhtasar" VALUES(26,183,'26:183','Đừng có cân thiếu để chiếm đoạt quyền lợi của thiên hạ và cũng đừng đi khắp nơi mà gieo rắc tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(26,184,'26:184','Các ngươi hãy kính sợ Allah, Đấng đã tạo ra các người và những cộng đồng thời trước, các ngươi hãy cẩn thận trước hình phạt sẽ giáng xuống các người.');
INSERT INTO "vn_mokhtasar" VALUES(26,185,'26:185','Dân chúng của Shu-''aib nói với Y: Ngươi chắc chắn đã bị trúng bùa rất nhiều lần làm ảnh hưởng đến tâm trí Ngươi rồi.');
INSERT INTO "vn_mokhtasar" VALUES(26,186,'26:186','Và Ngươi vốn là người phàm như bọn ta, không có điểm nào trội hơn bọn ta, sao lại dám tự xưng là Thiên Sứ chứ? Bọn ta nghỉ ngươi là tên nói dối khi tự nhận mình là Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(26,187,'26:187','Vậy, ngươi hãy khiến cho một phần của bầu trời rơi xuống đè bọn ta đi nếu lời nói của người là thật.');
INSERT INTO "vn_mokhtasar" VALUES(26,188,'26:188','Shu-''aib nói với chúng: Thượng Đế của Ta am tường hết mọi điều các người làm từ Shirk, tội lỗi, không việc làm nào của các người che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(26,189,'26:189','Chúng ngoan cố chối bỏ Y nên đã bị hình phạt bắt lấy chúng khi bầu trời bổng trở nên đầy mây sau một ngày rất oi bức, rồi cơn mưa lửa từ trên đổ xuống thiêu cháy chúng, đó là ngày kinh hoàng mà chúng bị tiêu diệt.');
INSERT INTO "vn_mokhtasar" VALUES(26,190,'26:190','Rằng qua câu chuyện diệt vong dân chúng của Shu-''aib là bài học cho nhóm người biết ngẫm nghĩ, nhưng đa số mọi người vẫn không tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(26,191,'26:191','Và chắc chắc Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng Quyền Lực, Ngài đánh bại tất cả kẻ thù của Ngài, và Ngài là Đấng Nhân Từ đối với ai biết ăn năn, sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(26,192,'26:192','Quyển Qur''an này đây đích thực do Thượng Đế của vạn vật đã thiên khải xuống cho Muhammad.');
INSERT INTO "vn_mokhtasar" VALUES(26,193,'26:193','Do Đại Thiên Thần Jibril trung trực đưa Nó xuống.');
INSERT INTO "vn_mokhtasar" VALUES(26,194,'26:194','Nó được đưa xuống để đặt vào con tim của Ngươi - hỡi Thiên Sứ - để Ngươi chính thức là Thiên Sứ cảnh báo thiên hạ và dọa họ về hình phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(26,195,'26:195','Nó được thiên khải bằng tiếng Ả-rập rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(26,196,'26:196','Và rằng Qur''an này đã được nhắc đến trong các Kinh Sách đã được thiên khải trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(26,197,'26:197','Đối với nhóm người quyết chối bỏ Ngươi, lẽ nào lại không có bất cứ dấu hiệu nào đã thiên khải trước đây để những học giả của bộ tộc Israel dựa vào đó mà công nhận sứ mạng của Ngươi là thật hay sao, kể cả tên ''Abdullah bin Salam luôn ư?');
INSERT INTO "vn_mokhtasar" VALUES(26,198,'26:198','Nếu như TA thiên khải Qur''an này cho một số người không nói chuyện bằng tiếng Ả-rập.');
INSERT INTO "vn_mokhtasar" VALUES(26,199,'26:199','Rồi y đọc lại cho chúng nghe thì chúng sẽ không hề tin, bởi chúng sẽ nói: Bọn ta không hiểu. Tuy nhiên, chúng hãy nên tạ ơn Allah về đã thiên khải Nó xuống bằng tiếng nói của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(26,200,'26:200','Bằng cách đó, TA rót vào trong trái tim những kẻ tội lỗi sự dối trá và sự vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(26,201,'26:201','Chúng sẽ duy trì sự vô đức tin, không tin tưởng Nó mãi đến khi chúng tận mắt nhìn thấy hình phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(26,202,'26:202','Hình phạt sẽ bất ngờ chụp lấy chúng trong lúc chúng không hay biết bởi không có một dấu hiệu nào báo trước cho chúng cả.');
INSERT INTO "vn_mokhtasar" VALUES(26,203,'26:203','Chúng nói trong tuyệt vọng khi hình phạt bất ngờ giáng xuống: Chúng tôi có cơ hội kéo dài thêm thời gian để sám hối với Allah không?');
INSERT INTO "vn_mokhtasar" VALUES(26,204,'26:204','Vậy sao trước đây nhóm người vô đức tin này lại nói: Bọn ta sẽ không tin tưởng cho đến khi bầu trời sụp xuống đè bọn ta như Ngươi đã tuyên bố.');
INSERT INTO "vn_mokhtasar" VALUES(26,205,'26:205','Hãy cho TA biết - hỡi Thiên Sứ - nếu TA cho nhóm người vô đức tin đã không tin những gì Ngươi mang đến hưởng lạc thêm thời gian nữa.');
INSERT INTO "vn_mokhtasar" VALUES(26,206,'26:206','Sau đó, khi thời gian hưởng lạc được gia hạn thêm đã hết thì hình phạt đã báo trước sẽ đến túm lấy chúng.');
INSERT INTO "vn_mokhtasar" VALUES(26,207,'26:207','Đâu là thứ có thể giúp ích chúng trong những thứ hưởng lạc ở trần gian? Tất cả hưởng lạc đó đã chấm đứt không tồn tại bất cứ gì?');
INSERT INTO "vn_mokhtasar" VALUES(26,208,'26:208','Và TA đã không tiêu diệt bất cứ cộng đồng nào trước đây cho đến khi TA đã gởi đến chúng những Thiên Sứ và Kinh Sách.');
INSERT INTO "vn_mokhtasar" VALUES(26,209,'26:209','Để cảnh báo và nhắc nhở chúng, bởi TA không hề muốn trừng phạt chúng cho đến khi TA gởi đến chúng các Thiên Sứ và các Kinh Sách.');
INSERT INTO "vn_mokhtasar" VALUES(26,210,'26:210','Và lũ Shaytan đã không mang Qur''an xuống đặt vào con tim của Muhammad.');
INSERT INTO "vn_mokhtasar" VALUES(26,211,'26:211','Chúng không phù hợp đặt Nó vào tim của Y và chúng cũng không có khả năng làm điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(26,212,'26:212','Chúng không có khả năng bởi vì chúng bị trục xuất khỏi cõi trời thì làm sao đến gần để hộ tống Nó xuống cho được.');
INSERT INTO "vn_mokhtasar" VALUES(26,213,'26:213','Thế nên Ngươi đừng có tổ hợp bất cứ thần linh nào cùng với Allah trong việc thờ phượng, đó là lý do khiến Ngươi bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(26,214,'26:214','Và Ngươi hãy cảnh báo - hỡi Thiên Sứ - những họ hàng của Ngươi, ưu tiên những người quan hệ gần nhất kẻo họ bị Allah trừng phạt nếu vẫn tiếp tục sống trên Shirk.');
INSERT INTO "vn_mokhtasar" VALUES(26,215,'26:215','Ngươi hãy đối xử ôn hòa, lượng thứ từ lời nói đến hành động lịch thiệp đối với những người có đức tin đi theo Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(26,216,'26:216','Nếu chung nghịch lại Ngươi, không tuân thủ theo lệnh Ngươi bảo về việc độc tôn Allah và phục tùng Ngài, Ngươi hãy nói với chúng: Chắc rằng Ta vô can mọi điều mà các người làm từ Shirk và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(26,217,'26:217','Và Ngươi hãy giao phó hết mọi việc của Ngươi cho Đấng Quyền Lực, luôn chiến thắng mọi thế lực, Đấng Rất Khoan Dung với ai biết sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(26,218,'26:218','Ngài nhìn thấy Ngươi khi Ngươi đứng dậy Salah một mình trong đêm.');
INSERT INTO "vn_mokhtasar" VALUES(26,219,'26:219','Và Ngài nhìn thấy Ngươi luôn cả lúc thay đổi động tác trong Salah, không động tác nào của Ngươi trong Salah che giấu được Ngài và luôn cả những ai ngoài Ngươi đứng Salah.');
INSERT INTO "vn_mokhtasar" VALUES(26,220,'26:220','Bởi Ngài nghe tất cả lời Qur''an Ngươi đọc, lời tụng niệm của Ngươi trong Salah, Ngài biết rõ định tâm của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(26,221,'26:221','Lẽ nào các ngươi không muốn Ta cho các ngươi biết ai mới là kẻ thường bị Shaytan đến gặp chúng để các ngươi cho rằng Qur''an được Shaytan mang xuống?');
INSERT INTO "vn_mokhtasar" VALUES(26,222,'26:222','Lũ Shaytan thường đến gặp những tên nói dối đầy tội lỗi và không vâng lời như thầy bói.');
INSERT INTO "vn_mokhtasar" VALUES(26,223,'26:223','Lũ Shaytan nghe trộm những Thiên Thần trên trời nói chuyện về kể lại cho những tên thầy bói, chúng thêm bớt trong lời nói và những tên thầy bói cũng vậy, nếu chúng nói thật một lời thì cả trăm lời còn lại là nói dối.');
INSERT INTO "vn_mokhtasar" VALUES(26,224,'26:224','Về những tên nhà thơ mà các người đã cho rằng Muhammad trong số họ thì chỉ có những kẻ lạc lối, vô đạo đức mới theo họ để ghi chép lại thơ ca khi họ ngâm.');
INSERT INTO "vn_mokhtasar" VALUES(26,225,'26:225','Há Ngươi - hỡi Thiên Sứ - không thấy chúng có lúc vui thì khen ngợi, lúc buồn thì chê bai tùy theo tâm trạng mà chúng viết ra loại thơ khác nhau.');
INSERT INTO "vn_mokhtasar" VALUES(26,226,'26:226','Và bọn chúng nói dối: Bọn ta đã làm thế này nhưng thực sự bọn chúng nào đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(26,227,'26:227','Ngoại trừ tên thi sĩ nào đã tin tưởng, đã hành đạo tốt, đã tụng niệm Allah nhiều và đã phục thù trước kẻ thù của Allah sau khi chúng bị đàn áp giống vị Sahabah Hassan bin Thabit. Lúc đó, những kẻ làm Shirk với Allah, chống đối nô lệ của Ngài sẽ sớm nhận biết mình phải trở về đâu, chúng phải đến tập trung tại khu đại tập trung để chịu thanh toán chi tiết.');
INSERT INTO "vn_mokhtasar" VALUES(27,1,'27:1','{Ta. Sin} những chữ cái đã được giải thích ở đầu chương Al-Baqarah. Đây là những câu Kinh thiên khải cho Ngươi, Chúng là những câu Kinh Qur''an, một Kinh Sách rõ ràng không hề có uẩn khúc, ai nghiền ngẫm về Nó chắc sẽ nhận biết Nó đến từ Allah.');
INSERT INTO "vn_mokhtasar" VALUES(27,2,'27:2','Những câu Kinh này hướng dẫn đi đến Chân Lý và là nguồn tin vui dành cho nhóm người có đức tin đã tin tưởng Allah và các Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(27,3,'27:3','Những ai duy trì tốt bổn phận Salah, xuất Zakat từ tài sản riêng của họ vào đúng mục tiêu của Zakat và họ tin kiên định nơi những gì trong cuộc sống Đời Sau từ việc thưởng và phạt.');
INSERT INTO "vn_mokhtasar" VALUES(27,4,'27:4','Đối với nhóm người ngoại đạo không tin vào Đời Sau cũng như việc thưởng phạt thì việc làm xấu xa của chúng bị phủ lên một lớp ngụy trang khiến chúng nhìn rất đẹp, cứ thế chúng mãi mê chìm sâu trong hành động xấu xa đó, khiến chúng cứ lẩn quẩn không thấy được Chân Lý và sự hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(27,5,'27:5','Nhóm người có những đặc điểm như miêu tả sẽ gánh chịu sự trừng phạt nhục nhã ở trần gian bằng sự giết chóc và bắt làm tù binh, còn ở Đời Sau chúng sẽ là nhóm người thất bại nhất vì chúng đã khiến bản thân và gia đình phải mãi mãi trong Hỏa Ngục của Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(27,6,'27:6','Và chắc chắn Ngươi - hỡi Thiên Sứ - đang tiếp nhận Qur''an này từ Đấng Sáng Suốt trong tạo hóa, quản lý và qui định, Đấng Am Tường mọi việc của đám nô lệ của Ngài dù tốt hay xấu.');
INSERT INTO "vn_mokhtasar" VALUES(27,7,'27:7','Ngươi hãy nhớ lại - hỡi Thiên Sứ - khi Musa nói với gia đình mình: Ta đã nhìn thấy một ngọn lửa, Ta sẽ đến đó hỏi thăm đường đi từ người nhúm lửa đó hoặc sẽ xin họ một ít lửa để mọi người cùng sưởi ấm.');
INSERT INTO "vn_mokhtasar" VALUES(27,8,'27:8','Nhưng khi Musa đến nơi có lửa thì được Allah gọi: Thật hồng phúc cho ai đang ở gần lửa và những Thiên Thần đang ở xung quanh, đang tán tụng Đấng Chúa Tể của toàn vũ trụ và muôn loài, lọc ra khỏi Ngài mọi thuộc tính không phù hợp với Ngài đã bị nhóm lầm lạc gán ghép.');
INSERT INTO "vn_mokhtasar" VALUES(27,9,'27:9','Allah nói với Musa: Hỡi Musa, TA chính là Allah, Đấng Quyền Lực chế ngự tất cả, Đấng Sáng Suốt trong tạo hóa, quản lý và định đoạt.');
INSERT INTO "vn_mokhtasar" VALUES(27,10,'27:10','Allah bảo Musa ném cây gậy của Ngươi xuống. Khi Musa làm theo thì thấy cây gậy cử động thành con rắn khiến Người hoảng sợ quay lưng bỏ đi không dám nhìn lại. Lúc đó, Allah phán bảo Người: Ngươi đừng sợ, bởi chưa từng có Thiên Sứ nào của TA lại sợ con rắn cũng như những thứ tương tự.');
INSERT INTO "vn_mokhtasar" VALUES(27,11,'27:11','Nhưng đối với ai bất công với chính bản thân mình khi làm tội lỗi rồi hối hận cho hành động đó thì chắc chắn sẽ được TA tha thứ, khoan dung.');
INSERT INTO "vn_mokhtasar" VALUES(27,12,'27:12','Ngươi hãy đút bày tay mình vào cổ áo, sau khi rút ra Ngươi sẽ thấy nó trắng như tuyết không phải do bệnh, đó là một trong chín Dấu Lạ khẳng định sứ mạng của Ngươi là thật - cùng với bàn tay trắng toát, có cây gậy, hạn hán, mất mùa, lũ lụt, cào cào, rận, ếch nhái và máu - để gặp Fir''awn và đồng bọn của hắn. Quả thật chúng là đám người bất tuân Allah và phủ nhận Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(27,13,'27:13','Khi các dấu hiệu của TA đến với chúng. Musa đã trình bày rõ ràng các dấu hiệu cho chúng nhìn thấy tận mặt thì chúng bảo: Những gì Musa đã mang đến rõ ràng là ma thuật.');
INSERT INTO "vn_mokhtasar" VALUES(27,14,'27:14','Và chúng ngoan cố bác bỏ hết những Dấu Lạ đó, tỏ ra chống đối trong khi trong thâm tâm chúng đã nhận biết đó là của Allah. Chúng ngoảnh mặt với Chân Lý chỉ vì sự sai quấy và ngạo mạn. Ngươi hãy nghĩ xem - hỡi Thiên Sứ - hậu quả mà đám người hư đốn đó nhận được trong cuộc sống này do hành động vô đức tin và bất tuân không gì ngoài việc chúng bị TA tiêu diệt toàn bộ.');
INSERT INTO "vn_mokhtasar" VALUES(27,15,'27:15','Và chính TA đã ban cho hai cha con Dawood và Sulayman kiến thức như biết nói chuyện với loài chim. Cả hai cha con đồng tạ ơn Allah mà nói: Xin tạ ơn Allah, Đấng đã ưu ái hai cha con bầy tôi hơn đám nô lệ có đức tin của Ngài qua sứ mạng Thiên Sứ và khả năng kiểm soát và điều khiển được loài Jinn, lũ Shaytan.');
INSERT INTO "vn_mokhtasar" VALUES(27,16,'27:16','Và Sulayman kế thừa sứ mạng Thiên Sứ, kiến thức và vương quyền sau cha của mình Dawood. Người tuyên bố về hồng ân mà Allah đã đặc ân cho hai cha con Người: Hỡi mọi người, Allah đã dạy Ta hiểu được tiếng của loài chim, Ngài ban cho Ta mọi thứ mà Ngài đã ban cho Nabi và vua chúa trước đây. Quả thật, đây quả là những hồng ân đặc biệt mà Allah ban cho Ta.');
INSERT INTO "vn_mokhtasar" VALUES(27,17,'27:17','Đoàn quân của Sulayman gồm Jinn (ma quỷ), con người và chim chóc được gọi triệu tâp, tất cả đều đứng so hàng thẳng tấp.');
INSERT INTO "vn_mokhtasar" VALUES(27,18,'27:18','Khi đoàn quân hành quân đến một thung lũng của loài kiến (ở Sham), một con kiến lên tiếng: Hỡi loài kiến, các ngươi hãy mau chui vào hang của mình tránh phải bị Sulayman và đoàn quân của Người giẫm nát các ngươi trong lúc họ không hề hay biết, bởi nếu họ nhận biết là họ không giẫm đạp các người.');
INSERT INTO "vn_mokhtasar" VALUES(27,19,'27:19','Khi Sulayman nghe được lời nói của kiến thì Người mỉm cười và khấn vái: Lạy Thượng Đế của bề tôi, xin Ngài hãy phù hộ bề tôi biết tạ ơn Ngài về những hồng phúc mà Ngài đã ban cho bề tôi, cho song thân của bề tôi, xin Ngài hãy phù hộ bề tôi năng hành đạo được Ngài hài lòng và xin Ngài hãy lấy lòng thương xót của Ngài thu nhận bề tôi vào cùng đám nô lệ sùng đạo của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(27,20,'27:20','Và khi Sulayman kiểm quân số của loài chim thì không nhìn thấy con chim Hudhud (chim đầu rìu), Người nói: Sao Ta không nhìn thấy con Hudhud đâu cả, có vật gì đó cản Ta nhìn thấy nó hay nó vắng mặt?');
INSERT INTO "vn_mokhtasar" VALUES(27,21,'27:21','Sau khi biết rõ con chim vắng mặt, Người nói: Chắc chắn Ta sẽ trừng phạt nó thích đáng hoặc sẽ xử tử nó hoặc nó phải giải thích rõ ràng cho Ta biết lý do vắng mặt của nó.');
INSERT INTO "vn_mokhtasar" VALUES(27,22,'27:22','Chim Hudhud không dám chậm trễ lâu hơn, khi trở về gặp Sulayman thì Hudhud giải trình: Hạ thần đã phát hiện ra thứ mà bệ hạ chưa phát hiện, hạ thần mang đến cho bệ hạ một nguồn tin hệ trọng chính xác từ thành phố Saba'' (ở Yemen).');
INSERT INTO "vn_mokhtasar" VALUES(27,23,'27:23','Hạ thần thấy một nữ vương nắm quyền thống lãnh một vương quốc. Bà ta được ban cho tất cả mọi thứ giúp vương quốc và sự cai trị của bà hùng mạnh, bà ta ngồi trên một chiếc giường khổng lồ để giải quyết mọi việc trong cộng đồng mình.');
INSERT INTO "vn_mokhtasar" VALUES(27,24,'27:24','Hạ thần đã nhìn thấy bà ta và dân chúng của bà quỳ lạy mặt trời thay vì Allah Hiển Vinh và Tối Cao, họ đã bị Shaytan ngụy trang việc làm Shirk và chống đối của mình, hắn đã kéo họ lệch xa con đường Chân Lý khiến họ không tài nào đến được với nó.');
INSERT INTO "vn_mokhtasar" VALUES(27,25,'27:25','Shaytan đã ngụy trang việc làm Shirk và tội lỗi của họ khiến họ rất hài lòng, trong khi họ không được phép quỳ lạy ngoại trừ quỳ lạy Allah duy nhất bởi Ngài là Đấng đã kéo thứ đã bị bầu trời che đậy như nước mưa, bị đất che đậy như thảo mộc, Ngài biết rõ mọi việc làm của các ngươi dù công khai hay thầm kín, không gì giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(27,26,'27:26','Allah, Đấng không có Thượng Đế đích thực nào ngoài Ngài, Chủ Nhân của chiếc ngai vương vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(27,27,'27:27','Sulayman nói với chim Hudhud: Ta sẽ kiểm chứng lời nói của ngươi xem ngươi trung thực hay ngươi chỉ là một tên nói dối.');
INSERT INTO "vn_mokhtasar" VALUES(27,28,'27:28','Sulayman viết một bức thư giao cho chim Hudhud, bảo: Ngươi hãy mang bức thư này của Ta ném cho dân chúng Saba'', khi họ nhận được nó thì ngươi hãy tạm lánh mặt để lắng nghe xem họ bàn bạc như thế nào về bức thư này.');
INSERT INTO "vn_mokhtasar" VALUES(27,29,'27:29','Sau khi nữ vương nhận được bức thư, bà nói: Hơi các chư thần, ta vừa được ném cho một bức thư rất quan trọng.');
INSERT INTO "vn_mokhtasar" VALUES(27,30,'27:30','Bức thư này được Sulayman gởi đến được khai đề bằng câu: Nhân danh Allah, Đấng Rất Mực Độ Lượng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "vn_mokhtasar" VALUES(27,31,'27:31','Các ngươi đừng tỏ vẻ cao ngạo với Ta, hãy đến trình diện Ta như một tín đồ Muslim khi Ta mời gọi các ngươi đến với việc tôn thờ một mình Allah và từ bỏ sự tổ hợp các thần linh cùng với Ngài, đó là việc các ngươi đang thờ phượng mặt trời cùng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(27,32,'27:32','Nữ vương hỏi các chư thần của mình: Hỡi các chư thần, mong các chư thần cho ta biết ý kiến, ta nên quyết định thế nào, ta không thể tự phán quyết cho đến khi các chư thần trưng bày ý kiến của riêng mình.');
INSERT INTO "vn_mokhtasar" VALUES(27,33,'27:33','Các chư thần giải trình: Chúng ta vốn là một vương quốc hùng mạnh, quân binh của chúng ta có dày dặn kinh nghiệm trên sa trường, nữ vương cứ xem xét và ra lệnh, các thần nhất quyết phục tùng mệnh lệnh.');
INSERT INTO "vn_mokhtasar" VALUES(27,34,'27:34','Nữ vương nói: Theo thường lệ khi vua chúa muốn tấn công xâm lược một khu vực nào đó là họ sẽ tàn phá, giết chóc, cướp bóc, phế bỏ quyền lực của các lãnh đạo, tịch thu tài sản của người giàu, bắt tất cả làm thứ dân thấp hèn nhất, đó là kế sách phổ biến được vua chúa xâm lược triển khai khi họ chiếm được xóm làng, để gieo rắc hoang mang và sợ hãi trong lòng người.');
INSERT INTO "vn_mokhtasar" VALUES(27,35,'27:35','Ta sẽ gởi lại chủ nhân của bức thư này và quân lính của ông vật phẩm cống nạp, và ta chờ đợi xem sứ thần sẽ mang điều gì trở về sau lễ vật cống nạp này của ta.');
INSERT INTO "vn_mokhtasar" VALUES(27,36,'27:36','Khi sứ giả của nữ vương mang cống vật đến tặng ra mắt Sulayman thì bị Sulayman từ chối không nhận và nói: Các ngươi định lo lót Ta bằng tài sản này để Ta bỏ qua cho các ngươi đó ư? Với những gì mà Allah ban cho Ta như sứ mạng, quyền lực và tài sản tốt hơn nhiều so với những thứ mà Ngài ban cho các ngươi, vậy mà các ngươi tỏ ra thích thú với những thứ ảo huyền của trần gian này đó ư?!');
INSERT INTO "vn_mokhtasar" VALUES(27,37,'27:37','Sulayman nói với sứ giả: Ngươi hãy mang tất cả quà cống nạp này trở về trình tâu vơi họ, chắc chắn Ta sẽ cho một đoàn quân tấn công vương quốc của nữ vương khiến bà ta cũng như quân lính bất khả kháng, Ta sẽ trục xuất tất cả họ ra khỏi Saba'' một cách nhục nhã trong khi trước đây họ được tôn trọng nếu như họ vẫn không đầu hàng.');
INSERT INTO "vn_mokhtasar" VALUES(27,38,'27:38','Sulayman nói với toàn thể quân thần dưới quyền mình: Hỡi các chư thần, ai trong các ngươi mang đến cho Ta chiếc giường của nữ vương trước khi họ đến trình diệnTa?');
INSERT INTO "vn_mokhtasar" VALUES(27,39,'27:39','Một tên Jinn có sức mạnh phi thường lên tiếng: Hạ thần sẽ mang chiếc giường của Ả đến trước khi bệ hạ đứng dậy rời khỏi nơi bệ hạ đang ngồi. Quả thật hạ thần có thừa sức mạnh, uy tín mang chiếc Ngai vương đó đến mà không bị hư hao bất cứ thứ gì.');
INSERT INTO "vn_mokhtasar" VALUES(27,40,'27:40','Một người đàn ông ngoan đạo, uyên bác về Kinh Sách trong quân binh của Sulayman, ông biết được một đại danh của Allah, chỉ cần cầu xin Ngài bằng đại danh đó thì được toại nguyện, ông nói: Hạ thần sẽ mang chiếc giường đó đến đây trước khi bệ hạ chớp mắt, ông đã cầu xin Allah mang đến cho ông chiếc giường, lời cầu xin đó liền được đáp lại. Ngay khi Sulayman nhìn thấy chiếc giường nằm ngay trước mặt mình thì Người nói: Đây đúng là thiên ân mà Allah đã ban cho bề tôi, để thử xem bề tôi biết tạ ơn hay là kẻ vong ân? Ai tạ ơn Allah thì lời tạ ơn đó giúp ích cho y chứ không hề gia tăng thêm gì cho Allah bởi Ngài rất giàu, còn ai vong ân không biết tạ ơn thì Ngài rất giàu có không cần đến lời tạ ơn đó, Ngài luôn rộng lượng đối với họ.');
INSERT INTO "vn_mokhtasar" VALUES(27,41,'27:41','Sulayman ra lệnh: Các ngươi hãy thay đổi hình dạng của chiếc giường của nữ ta để xem nữ tya có thể tự nhận biết đó là chiếc giường của mình hay không thể tự nhận biết.');
INSERT INTO "vn_mokhtasar" VALUES(27,42,'27:42','Khi nữ vương của Saba'' đến trình diện Sulayman thì nữ ta được hỏi để kiểm tra: Có phải chiếc giường này giống như của nàng không? Nữ ta trả lời suôn theo câu hỏi: Dường như là vậy. Sulayman nói: Ta đã được Allah ban cho kiến thức từ trước để có khả năng làm những thứ như thế này và Ta đã thần phục Allah, luôn tuân lệnh Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(27,43,'27:43','Điều kéo nữ lệch khỏi sự độc tôn hóa Allah là việc nghe lời và bắt chước theo dân chúng của nữ, điều đó khiến nữ trở thành một trong nhóm người vô đức tin nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(27,44,'27:44','Có lời mời nữ: "Xin mời nữ vương bước vào cung điện" và cung điện có nền lót tựa như hồ nước long lanh khiến nữ tưởng là nước thật nên vén váy lên làm lộ ra cặp ống quyển. Sulayman lên tiếng: Đó chẳng qua chỉ là nền được lót bằng pha lê và Người mời gọi nữ vương vào Islam. Lập tức nữ vương đáp lại lời mời gọi và nói: Lạy Thượng Đế của bề tôi, bề tôi đã sai lầm khi thờ phượng thần linh khác ngoài Ngài, nay bề tôi xin cùng với Sulayman nguyện thần phục Allah, Chủ Nhân của toàn vũ trụ và muôn loài.');
INSERT INTO "vn_mokhtasar" VALUES(27,45,'27:45','Và TA đã gởi đến người dân Thamud một người anh em trong số chúng, Saleh, để mời gọi chúng thờ phượng Allah duy nhất, sau lời mời gọi đó chúng chia thành hai nhóm: một nhóm có đức tin và một nhóm vô đức tin, cả hai tranh cải nhau ai mới là nhóm ở trên Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(27,46,'27:46','Saleh nói với chúng: Tại sao các người lại cầu mong bị trừng phạt thay vì cầu xin được thương xót? Sao các người không cầu xin Allah tha thứ về mọi tội lỗi đã phạm mong rằng được Ngài đoái thương.');
INSERT INTO "vn_mokhtasar" VALUES(27,47,'27:47','Dân chúng của Saleh ngoan cố phủ nhận Chân Lý nói: Bọn ta có điềm báo rằng ngươi và những ai tin tưởng theo ngươi là sự xui xẻo. Saleh đáp lại chúng: Thứ mà các người đoán được khiến các người không thích đều được Allah biết rõ không gì giấu được Ngài. Đúng hơn các người đang bị thử thách bởi những điều xấu và điều mà các người sẽ gặp phải.');
INSERT INTO "vn_mokhtasar" VALUES(27,48,'27:48','Và trong thành phố Al-Hijr (phía tây bán đảo Ả-rập) có chín tên vô đức tin chuyên gieo rắc tội lỗi, chúng không biết hối cải để có đức tin Iman và hành đạo cho tốt hơn.');
INSERT INTO "vn_mokhtasar" VALUES(27,49,'27:49','Họ bảo nhau: Mỗi người trong chúng ta hãy lập lời thề trước Allah là chúng ta cùng nhau tấn công vào nhà của Y vào ban đêm để ám sát tất cả, sau đó chúng ta công bố với người thân của Y là chúng ta không có mặt lúc gia đình của Saleh bị sát hại, tin chắc chúng ta sẽ được tin là nói thật.');
INSERT INTO "vn_mokhtasar" VALUES(27,50,'27:50','Và chúng đã âm thầm lên kế hoạch sát hại Saleh và những ai theo Y và TA cũng đã có kế hoạch phù hộ, bảo vệ an toàn cho cả nhóm trước kế hoạch tàn sát của nhóm người vô đức tin mặc dù chúng không hề hay biết về điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(27,51,'27:51','Ngươi hãy suy ngẫm - hỡi Thiên Sứ - về kết quả của kế hoạch mà chúng đã vạch ra, há chúng đã không bị TA túm lấy bằng hình phạt tiêu diệt toàn bộ đó sao?!');
INSERT INTO "vn_mokhtasar" VALUES(27,52,'27:52','Đấy, những ngôi nhà của chúng đã bị hoang tàn chỉ còn mỗi đống đổ nát. Đó là hậu quả của hành động sai quấy của chúng mà ra. Trong hình phạt giáng xuống đám người sai quấy là một bài học quí cho nhóm người có đức tin, họ luôn có sự nhận thức về các sự kiện đã xảy ra.');
INSERT INTO "vn_mokhtasar" VALUES(27,53,'27:53','Và TA đã giải cứu cho đám người có đức tin nơi Allah trong dân chúng của Saleh, do trước đây chúng luôn tuân thủ đúng theo mệnh lệnh của Allah và tránh xa những gì Ngài cấm.');
INSERT INTO "vn_mokhtasar" VALUES(27,54,'27:54','Ngươi hãy nhớ lại - hỡi Thiên Sứ - khi Lut nói với người dân của mình bằng thái độ khiển trách: Sao các người lại làm những điều ô nhục này - đó là tình dục đồng tính - trong khi các người vẫn nhận thức được đó là điều xấu xa?');
INSERT INTO "vn_mokhtasar" VALUES(27,55,'27:55','Các người muốn tìm đến đàn ông để thỏa mãn tình dục thay vì tìm đến phụ nữ đó ư? Các người không muốn danh dự cũng như con cái mà chỉ muốn thỏa mãn ham muốn thú tính của các người. Các người đúng là một nhóm người ngu muội không biết điều nào giúp các người có đức tin Iman, danh dự và tránh xa tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(27,56,'27:56','Đám dân của Lut đã không có câu trả lời nào ngoại trừ câu nói: "Hãy trục xuất gia đình của Lut ra khỏi quê hương của quí vị, quả thật chúng là những kẻ luôn giữ mình tránh khỏi dơ bẩn và ô uế." Chúng nói như thế vì muốn nhạo báng gia đình Lut vì đã không tham gia cùng chúng trong quan hệ đồng tính, mà còn cản trở hành động của chúng nữa.');
INSERT INTO "vn_mokhtasar" VALUES(27,57,'27:57','Thế là TA đã cứu Lut và gia đình của Y khỏi sự trừng phạt mà nó sẽ xảy ra trong đám dân của Y, ngoại trừ mụ vợ của Y đã bị TA định trước phải ở lại cùng đám người bị trừng phạt để trở thành nhóm người bị tiêu diệt.');
INSERT INTO "vn_mokhtasar" VALUES(27,58,'27:58','Và TA đã trừng phạt chúng bằng một trận mưa đá từ trời đổ xuống. Đó là một trận mưa xấu xa để hủy diệt những kẻ đã bị cảnh báo mà không biết sợ, không biết vâng lời.');
INSERT INTO "vn_mokhtasar" VALUES(27,59,'27:59','Ngươi hãy nói - hỡi Thiên Sứ -: Mọi lời ca ngợi và tán dương kính dâng Allah về những thiên ân mà Ngài đã ban cũng như sự che chở bảo vệ của Ngài thoát khỏi sự trừng phạt mà Ngài đã trừng phạt đám dân của Lut và nhóm ngoan đạo trong số Sahabah của Thiên Sứ صلى الله عليه وسلم. Há không phải Allah đáng được tôn thờ khi Ngài nắm quyền thống trị vạn vật, mọi thứ tốt đẹp đều nằm trong tay Ngài hay những kẻ đa thần chỉ muốn tôn thờ ngoài Ngài những vật không giúp ích được cũng không hãm hại được chúng?!');
INSERT INTO "vn_mokhtasar" VALUES(27,60,'27:60','Ai đã tạo ra các tầng trời và trái đất khi chúng không là gì, và ban mưa từ trên trời xuống cho các ngươi - hỡi nhân loại -, rồi TA (Allah) làm mọc ra những mảnh vườn xanh tươi đẹp đẽ thứ mà các ngươi bất lực làm ra chúng. Chính Allah mới là Đấng cho thảo mộc mọc lên đó. Phải chăng có một thần linh cùng tham gia với Allah ư? Không, chúng là một đám người đi chệch khỏi Chân Lý khi đã bịa ra một đấng tạo hóa khác ngoài Ngài một cách bất công.');
INSERT INTO "vn_mokhtasar" VALUES(27,61,'27:61','Ai đã làm trái đất thành một nơi ở vững chắc ổn định không bị xáo trộn bởi bất cứ gì trên nó, đã đặt giữa nó các dòng sông chảy, đã đặt các dãy núi lên nó để được kiên cố, và đã đặt giữa hai biển: Nước mặn và nước ngọt một bức chắn để không cho nước mặn lấn chiếm nước ngọt khiến không thể uống được. Phải chăng có một thần linh cùng chia sẽ quyền năng với Allah? Không, đa số bọn chúng đều không biết gì, nếu chúng biết là chúng đã không tổ hợp bất kỳ tạo vật nào cùng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(27,62,'27:62','Ai đã đáp lại lời cầu cứu của người bị nguy nan khi y van xin Ngài, cũng như cứu con người thoát khỏi bệnh tật, nghèo đói và những thứ khác, và làm cho các ngươi thành những người kế thừa trên trái đất để lại cho nhau từ thế hệ này sang thế hệ khác. Phải chăng có một thần linh cùng chia sẽ quyền năng với Allah? Không, thật ít những ai biết nghĩ và lưu tâm đến.');
INSERT INTO "vn_mokhtasar" VALUES(27,63,'27:63','Ai đã hướng dẫn các ngươi qua những lớp tăm tối trên đất liền, ở ngoài biển khơi qua việc sắp cho các những ngôi sao để chỉ đường, làm cột mốc cho các ngươi; và ai đã gởi những luồng gió loan báo tin vui báo trước về trận mưa sắp đến, Ngài dùng nó để thương xót đám nô lệ của Ngài. Phải chăng có một thần linh cùng chia sẽ quyền năng với Allah? Allah trong sạch, Ngài vô can với mọi điều chúng qui cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(27,64,'27:64','Ai bắt đầu sự tạo hóa trong dạ con từ giai đoạn này đến giai đoạn khác, sau đó phục sinh y sống lại sau khi khiến y chết; và ai đã cung dưỡng cho các người từ trên trời với những cơn mưa cũng như cung dưỡng cho các ngươi từ mặt đất khi cho mọc ra thảo mộc! Phải chăng có một thần linh cùng chia sẽ quyền năng với Allah? Hãy nói đi - hỡi Thiên Sứ - với nhóm người đa thần: Hãy đưa các bằng chứng của các người ra xem nếu các người nói thật về sự đa thần cũng như những gì các người tuyên bố rằng các người đang ở trên Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(27,65,'27:65','Hãy nói đi - hỡi Thiên Sứ -: Không một ai biết rõ điều vô hình từ dân cư trên trời như các Thiên Thần đến dân cư của trái đất như loài người, chỉ riêng Allah duy nhất mới biết rõ, và không một ai trong tất cả dân cư trên trời và dân cư dưới đất biết rõ khi nào họ sẽ được phục sinh trở lại cho việc xét xử và thưởng phạt ngoại trừ Allah.');
INSERT INTO "vn_mokhtasar" VALUES(27,66,'27:66','Hay chúng nắm được tin tức Ngày Tận Thế, sao không tự xác định nó đi? Không, chúng nghi ngờ và lưỡng lự về Đời Sau, đúng hơn là chúng thực sự mù tịt về nó.');
INSERT INTO "vn_mokhtasar" VALUES(27,67,'27:67','Và những người không tin tưởng đã phủ nhận, nói: Khi chúng tôi chết và trở thành cát bụi, lúc đó chúng tôi thực sự sẽ được dựng sống trở lại ư?');
INSERT INTO "vn_mokhtasar" VALUES(27,68,'27:68','Thực ra chúng tôi lẫn tổ tiên của mình cũng đã được hứa rằng tất cả đều được dựng sống lại, nhưng chúng tôi nào thấy sự kiện đó thành hiện thực. Do đó lời hứa này mà tất cả chúng tôi đã được hứa chỉ là chuyện cổ tích của người xưa mà họ đã viết vào trong kinh sách của họ.');
INSERT INTO "vn_mokhtasar" VALUES(27,69,'27:69','Ngươi hãy nói - hỡi Thiên Sứ - với những kẻ phủ nhận việc phục sinh: Các ngươi hãy đi chu du bất cứ nơi nào trên trái đất rồi nhìn và suy ngẫm xem kết cuộc của những kẻ tội lỗi và phủ nhận việc phục sinh, họ đã bị TA tiêu diệt bởi sự phủ nhận của họ như thế nào?');
INSERT INTO "vn_mokhtasar" VALUES(27,70,'27:70','Và Ngươi chớ buồn phiền vì nguyên nhân bị những kẻ thờ đa thần khước từ lời kêu gọi của Ngươi và cũng đừng khổ tâm về những điều chúng lập mưu, bởi Allah sẽ giúp Ngươi chiến thắng chúng.');
INSERT INTO "vn_mokhtasar" VALUES(27,71,'27:71','Những kẻ vô đức tin phủ nhận việc phục sinh thuộc dân chúng của Ngươi nói rằng: Khi nào chúng tôi sẽ đối diện với điều mà chúng tôi đã được hứa về sự trừng phạt qua lời nói của Ngươi và những người có đức tin, nếu các ngươi nói thật?');
INSERT INTO "vn_mokhtasar" VALUES(27,72,'27:72','Ngươi hãy nói với chúng - hỡi Thiên Sứ -: Sự trừng phạt mà một số các ngươi mong muốn cho mau đến có lẽ đang ở sát sau lưng của các người đó.');
INSERT INTO "vn_mokhtasar" VALUES(27,73,'27:73','Và quả thật Thượng Đế của Ngươi - hỡi Thiên Sứ - đã đặc ân đối với loài người khi Ngài đã không vội vã trừng phạt họ với những gì họ phủ nhận cũng như những tội lỗi mà họ đã phạm, nhưng hầu hết bọn họ không biết ơn đến Ngài về những ân huệ mà Ngài đã ban cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(27,74,'27:74','Và quả thật,Thượng đế của Ngươi luôn biết rất rõ những điều mà bầy tôi của Ngài giấu giếm trong lòng cũng như những điều họ bộc lộ ra ngoài, không một điều nào có thể giấu giếm được Ngài và dựa vào đó Ngài sẽ phán xét và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(27,75,'27:75','Và không có một điều bí mật nào bị che khuất khỏi mắt người dù ở trên trời hay dưới đất, ngoại trừ được ghi rõ trong quyển sổ, đó là quyển Lawhul Mahfuzh (quyển Kinh Mẹ).');
INSERT INTO "vn_mokhtasar" VALUES(27,76,'27:76','Rõ thật, Kinh Qur''an này được ban xuống cho Muhammad để kể lại cho con cháu của Israel (Do Thái) nhiều điều mà họ thường tranh chấp nhau trong đó, và để vạch trần sự lệch lạc của họ.');
INSERT INTO "vn_mokhtasar" VALUES(27,77,'27:77','Và quả thật Qur''an là một sự hướng dẫn cũng như lòng thương sót của Ngài dành cho những người tin tưởng vào những gì trong Nó.');
INSERT INTO "vn_mokhtasar" VALUES(27,78,'27:78','Quả thật Thượng Đế của Ngươi - hỡi Thiên Sứ - sẽ phân xử loài người gồm những người có đức tin và người vô đức tin trong Ngày Phán Xét bằng luật công bằng của Ngài, Ngài dành sự yêu thương cho những người có đức tin và trừng phạt những kẻ vô đức tin, bởi Ngài là Đấng Oai Nghiêm chiến thắng mọi kẻ thù của Ngài, là Đấng Am Tường phân biệt rõ mọi điều đúng sai.');
INSERT INTO "vn_mokhtasar" VALUES(27,79,'27:79','Ngươi hãy phó thác cho Allah, hãy dựa dẫm vào Ngài trong tất cả mọi công việc của Ngươi, chắc chắn Ngươi đang ở trên Chân lý rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(27,80,'27:80','Quả thật Ngươi - hỡi Thiên Sứ - không thể làm cho những người mà con tim của họ đã chết vì phủ nhận Allah cũng như những người điếc nghe được những gì Ngươi kêu gọi chúng khi chúng quay mặt bỏ đi.');
INSERT INTO "vn_mokhtasar" VALUES(27,81,'27:81','Ngươi cũng không thể hướng dẫn ai không thể nhận thức được Chân Lý, vì vậy Ngươi chớ buồn phiền và khổ tâm về họ, Ngươi cũng không thể làm cho ai nghe được tiếng gọi của Ngươi ngoại trừ những ai tin tưởng nơi những lời mặc khải của TA rồi họ phục tùng làm theo mệnh lệnh của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(27,82,'27:82','Và khi hình phạt dành cho chúng xảy ra khi chúng vẫn mãi mê chìm đắm trong vô đức tin và tội lỗi, cũng như chỉ còn mỗi người xấu xa, TA sẽ đưa ra cho chúng một trong những đại dấu hiệu của Ngày Tận Thế gần kề. Đó là con thú chui ra từ lòng đất biết nói chuyện với chúng bằng ngôn ngữ mà chúng hiểu được, đó là lời mặc khải đã truyền xuống cho Thiên Sứ của TA mà thiên hạ không tin là thật.');
INSERT INTO "vn_mokhtasar" VALUES(27,83,'27:83','Hãy nhắc họ - hỡi Thiên Sứ - về Ngày mà TA sẽ cho tập trung từ mỗi cộng động những thành phần cầm đầu vốn đã phủ nhận những Lời mặc khải của TA thành một nhóm riêng từ người đầu tiên đến người cuối cùng, rồi tất cả được đưa đi để phán quyết.');
INSERT INTO "vn_mokhtasar" VALUES(27,84,'27:84','Và chúng vẫn tiếp tục bị đưa đi, cho đến khi đến nơi được phán quyết thì Allah hỏi chúng bằng lời quở trách: Phải chăng các ngươi đã phủ nhận những lời mặc khải của TA khẳng định việc thờ phượng TA duy nhất và bộ luật Shari''ah của TA, nhưng khi không thấu hiểu hết chúng với kiến thức của các ngươi thì các ngươi trở nên phủ nhận nó ư hoặc các ngươi xử trí thế nào, tin tưởng hay bác bỏ?');
INSERT INTO "vn_mokhtasar" VALUES(27,85,'27:85','Và lệnh trừng phạt được thi hành đối với chúng vì sự sai quấy của chúng khi phủ nhận Allah và những lời mặc khải của Ngài. Vì thế, chúng không thể nói được gì để bảo vệ bản thân minh khỏi hình phạt vì sự bất lực và mọi lập luận của chúng trở nên vô hiệu.');
INSERT INTO "vn_mokhtasar" VALUES(27,86,'27:86','Sao những kẻ phủ nhận việc phục sinh lại không nhìn vào ban đêm khi TA biến nó thành thời điểm để chúng có giấc ngủ ngon, TA làm ban ngày trở nên sáng tỏ cho chúng nhìn thấy để chúng lao động. Quả thật, cái chết luôn được lặp lại và sau đó là sự phục sinh. Đó là những dấu hiệu rất rõ ràng cho đám người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(27,87,'27:87','Ngươi hãy nhắc chúng - hỡi Thiên Sứ -: Vào Ngày mà Thiên Thần thổi còi được lệnh rú lên tiếng còi bằng chiếc sừng lần thứ hai. Tiếng rú rềnh vang đó khiến tất cư dân trên các tầng trời cũng như cư dân ở dưới đất đều phải kinh hồn bạt vía ngoại trừ những ai được Allah dung tha. Và tất cả tạo vật của Allah đều phải hạ mình khiêm tốn đến trình diện trước mặt Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(27,88,'27:88','Và Ngươi sẽ thấy những quả núi mà Ngươi tưởng chúng cố định vững chắc, nhưng vào Ngày Đó chúng sẽ tan vỡ rất nhanh như những đám mây tan. Sản phẩm của Allah đã tạo rồi Ngài rung chuyển chúng, quả thật Ngài luôn biết rõ mọi điều các ngươi làm, không gì giấu giếm được Ngài và dựa vào đó Ngài sẽ ban thưởng xứng đáng cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(27,89,'27:89','Ai đến trình diện trong Ngày Phán Xét bằng đức tin Iman và việc làm ngoan đạo, y sẽ được ban thưởng Thiên Đàng và được Allah cho bình an thoát khỏi cảnh sợ hãi kinh hồn bạc vía vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(27,90,'27:90','Và ai đến trình diện Allah bằng tấm lòng vô đức tin và tội lỗi thì họ sẽ bị quẳng úp mặt vào Hỏa Ngục, lúc đó có lời hỏi khinh khi, sỉ nhục họ: Phải chăng hành phạt này không tương xứng với việc phủ nhận đức tin và tội lỗi mà các người đã làm ở trên trần gian hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(27,91,'27:91','Ngưới hãy nói với họ - hỡi Thiên Sứ -: Thật ra Ta được lệnh chỉ tôn thờ Thượng Đế của thành phố Makkah này, Ngài đã cấm mọi hành động xấu như cấm gây đổ máu, cấm gây bất công với người khác, cấm săn bắn, cấm chặt phá cây cối mọc tự nhiên. Đấng Hiển Vinh Ngài là Chúa Tể của tất cả; và Ta được lệnh phải là một người thần phục và tuân theo Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(27,92,'27:92','Và Ta cũng được lệnh phải xướng đọc Qur''an cho nhân loại nghe. Bởi thế người nào nhận được sự Chỉ đạo từ Qur''an và làm theo những gì trong Nó thì việc hướng dẫn đó chỉ có lợi cho bản thân y, ngược lại người nào lầm lạc và phủ nhận Nó, không làm theo những gì trong Qur''an bảo thì Ngươi hãy nói với họ: Chẳng qua Ta chỉ là một trong những người cảnh báo các ngươi về sự trừng phạt của Allah, chứ Ta không có quyền hướng dẫn các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(27,93,'27:93','Và Ngươi hãy nói - hỡi Thiên Sứ -: Mọi lời các ngợi và tán dương kính dâng Allah bởi vô sô kể ân huệ của Ngài, Ngài sẽ sớm cho các ngươi thấy những dấu hiệu của Ngài ngay trên bản thân các ngươi, ở trên trời, ở dưới đất và trong bổng lộc, khiến các ngươi nhận biết chúng, điều đó hướng dẫn các ngươi đi đến khuất phục Chân Lý. Và Thượng Đế của Ngươi không làm ngơ về những điều các ngươi làm. Ngài luôn theo dõi và quan sát các ngươi, không gì có thể giấu giếm được Ngài, dựa vào đó Ngài sẽ ban thưởng xứng đáng cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(28,1,'28:1','(Ta.Sin.Mim.) đã giải thích ở chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(28,2,'28:2','Đây là những câu Kinh Qur''an rất rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(28,3,'28:3','TA đọc cho Ngươi nghe về nguồn tin chính xác giữa Musa và Fir''awn (Pharaoh), một sự thật không gì phải nghi ngờ dành cho những người có đức tin, bởi họ là những người hưởng được điều hữu ích từ nguồn tin này.');
INSERT INTO "vn_mokhtasar" VALUES(28,4,'28:4','Quả thật, Fir''awn là kẻ độc tài, hắn thống trị đất nước Ai Cập, hắn chia dân chúng thành nhiều nhóm nhỏ tách biệt, họ chính là dân Israel, hắn sát hại tất cả bé trai và giữ lại phụ nữ của họ để phục dịch trong sự nhục nhã. Rõ ràng hắn là một trong những kẻ tàn bạo trên trái đất với sự bất công, độc tài và ngạo mạn.');
INSERT INTO "vn_mokhtasar" VALUES(28,5,'28:5','Và TA muốn bảo trợ cho con cháu Israel, nhóm người yếu thế bị tên Fir''awn áp bức trong xứ Ai Cập bằng cách tiêu diệt kẻ thù của họ và loại bỏ sự áp bức khỏi họ, và biến họ thành những nhà lãnh đạo chân chính, và ủy quyền kế thừa cho họ ở vùng đất Sham đầy hồng phúc sau khi tên độc tài Fir''awn bị tiêu diệt, như Allah có phán: {Và TA đã ủy quyền thừa kế cho đám người bị đàn áp thừa hưởng phần đất phía đông và phía tây mà TA đã ban phúc . . .}');
INSERT INTO "vn_mokhtasar" VALUES(28,6,'28:6','Và TA muốn ban cho họ một vùng đất để sinh sống và tự quản lý, và làm cho Fir''awn và những tên đầu sỏ ủng hộ hắn, trong đó tên quyền lực nhất là Haman cùng với binh lính của chúng thấy được mối đe dọa khiến chúng lo lắng bị mất vương quyền và việc chúng sát hại bé trai của dân Israel.');
INSERT INTO "vn_mokhtasar" VALUES(28,7,'28:7','Và TA đã gởi đến mẹ của Musa lời phán rằng hãy cứ cho đứa con trai của ngươi bú sữa, đến khi ngươi lo sợ cho con trai mình bị Fir''awn và quân lính của hắn giết hại Y thì ngươi hãy để Y trong một chiếc rương rồi ném chiếc rương đó xuống sông Nile, nhưng ngươi chớ lo sợ Y bị chết chìm hay bị Fir''awn sát hại, và ngươi cũng đừng đau buồn vì phải chia cách Y. Chắc chắn TA sẽ mang Y còn sống trả lại cho ngươi, và đề cử Y làm Thiên Sứ cho TA giống như bao Thiên Sứ được TA gởi xuống cho nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(28,8,'28:8','Vậy là nữ ta đã làm theo chỉ thị của TA mà đặt đứa con trai của mình trong một chiếc rương và ném nó xuống sông. Chiếc rương trôi dạt vào cung điện của Fir''awn và được người nhà của Fir''awn vớt lấy mang về nuôi. Đó là kết quả mà Allah muốn Musa sẽ trở thành kẻ thù của Fir''awn và Allah sẽ xóa bỏ vương quyền của hắn bằng bàn tay của Musa. Quả thật, Fir''awn cùng cận thần của hắn là Haman và binh lính của hai đứa chúng là những kẻ tội lỗi vì đã vô đức tin, bạo ngược và tàn phá trái đất.');
INSERT INTO "vn_mokhtasar" VALUES(28,9,'28:9','Và khi Fir''awn muốn giết đứa bé thì vợ của hắn lên tiếng: "Đứa bé này sẽ là một niềm vui cho thiếp và cho bệ hạ, xin Bệ hạ chớ giết chết Nó, biết đâu nó sẽ giúp ích cho chúng mình hoặc chúng mình sẽ nhận nó làm con nuôi." Tuy nhiên, họ nào biết vương quyền của họ sẽ bị đôi tay đứa trẻ này hủy diệt.');
INSERT INTO "vn_mokhtasar" VALUES(28,10,'28:10','Và lòng của mẹ Musa trở nên trống rỗng không quan tâm đến bất kỳ việc gì của thế gian ngoại trừ việc sống chết của Musa, khiến nữ ta gần như không còn kiên nhẫn được nữa nên suýt đã tiết lộ câu chuyện về con trai mình. Nhưng TA đã cũng cố đức tin và kiên định tấm lòng của nữ lại để nữ trở thành một trong những người tin tưởng biết phó thác cho Thượng Đế và biết nhẫn nại trên những gì được Ngài định đoạt và an bài.');
INSERT INTO "vn_mokhtasar" VALUES(28,11,'28:11','Sau khi mẹ Musa ném Y xuống sông thì nữ nói với chị gái của Y rằng: Con hãy bám theo dõi đứa em trai của con để con biết được những gì bọn chúng đang làm gì với Nó. Chính vì thế người chị đứng nhìn đứa em từ đằng xa để không bị phát hiện, kể cả Fir''awn và đồng bọn của hắn không thể nhận biết đó là chị gái của Musa đang theo dõi được tung tích của đứa em.');
INSERT INTO "vn_mokhtasar" VALUES(28,12,'28:12','Với sự sắp đặt của Allah khiến Musa không thể bú được sữa của bất cứ bà vú nào, mãi đến lúc chị gái của Musa đến và nhìn thấy sự nhiệt tình của họ muốn cho Y bú thì mới nói: Quí ngài có muốn tiện nữ chỉ dẫn cho quí ngài đến với một gia đình đứng ra nuôi dưỡng cho đứa bé bú và chăm sóc cho bé hay không? và họ là những người chỉ cầu mong cho bé được mạnh khỏe.');
INSERT INTO "vn_mokhtasar" VALUES(28,13,'28:13','Bằng cách đó TA đã mang Musa trở về bên cạnh mẹ của Y để nữ thỏa mãn cặp mắt được nhìn con gần hơn, để nữ không còn buồn phiền vì phải xa cách con trai, cũng như để cho nữ biết rằng Lời Hứa của Allah là sự thật không gì phải nghi ngờ, nhưng đa số bọn chúng không hề biết gì về Lời Hứa này cũng như không một ai biết rằng nữ là mẹ của Musa.');
INSERT INTO "vn_mokhtasar" VALUES(28,14,'28:14','Và khi Musa đến tuổi trưởng thành với thân thể cường tráng và biết vận dụng sức mạnh đó thì Y được TA ban cho trí thông minh và kiến thức hiểu biết rõ về tôn giáo của Israel trước khi là Thiên Sứ. TA đã khen thưởng Musa vì sự vâng lệnh của Y, và đó cũng là cách mà TA khen thưởng những người ngoan đạo ở mọi lúc và mọi nơi.');
INSERT INTO "vn_mokhtasar" VALUES(28,15,'28:15','Và khi Musa đi vào thành phố lúc mọi người nghỉ ngơi trong nhà, Y thấy ở nơi đó có hai người đàn ông tranh luận và đánh nhau, một trong hai người thuộc phe bộ tộc Israel dân chúng của Musa, còn người kia thuộc phe Qibti dân chúng của Fir''awn kẻ thù của Musa. Người thuộc phe của Y nhờ Y tiếp sức để đánh lại người kia thuộc phe kẻ địch. Nhận lời Musa tiến đến đánh tên Qibti một đấm, với sức mạnh của mình đã giết chết tên kia. Musa liền sực tỉnh và nói: Đây là sự cám dỗ của Shaytan, quả thật Shaytan là kẻ thù công khai chuyên dụ dỗ đi lầm lạc đối với những ai thích theo hắn. Điều đó đã xảy ra với Ta, hắn khiến Ta bị lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(28,16,'28:16','Musa cầu khẩn Thượng Đế của Người, tỏ lòng ăn năn về những gì mình đã gây ra: Lạy Thượng Đế, bề tôi đã tự hại chính mình vì đã giết chết tên Qibti, xin Ngài hãy tha thứ tội lỗi của bề tôi. Tức thời Allah trình bày cho Musa biết Ngài đã tha thứ. Quả thật, Ngài sẵn sàng tha thứ cho những ai biết hối cải trong số bầy tôi của Ngài, Ngài luôn khoan dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(28,17,'28:17','Sau đó Musa tiếp tục lời cầu xin: Lạy Thượng Đế, với lý do được Ngài ban ân về sức mạnh, sự khôn ngoan và kiến thức cho nên bề tôi quyết sẽ không bao giờ tiếp tay cho những kẻ tội lỗi trong hành động tội tỗi của họ nữa.');
INSERT INTO "vn_mokhtasar" VALUES(28,18,'28:18','Sau những gì đã xảy ra từ vụ giết chết một người Qibti khiến Musa một buổi sáng khi vào thành phố luôn sợ hãi, ngó chừng. Bỗng nhiên Y thấy kẻ đã yêu cầu mình tiếp tay ngày hôm qua lại yêu cầu tiếp tay cho hắn để đánh bại một người Qibti khác. Do đó Musa nói với hắn: Rõ thật ngươi là một tên chuyên dắt người ta đi lầm lạc một cách hiển nhiên.');
INSERT INTO "vn_mokhtasar" VALUES(28,19,'28:19','Và khi Musa muốn túm lấy tên Qibti, kẻ thù của Y và của người Israel, nhưng tên Israel lại nghĩ rằng Musa muốn tấn công hắn vì hắn nghe được Musa nói: {“Rõ thật, mày là một tên chuyên dắt người ta đi lạc!”} nên hắn nói với Musa: Mày muốn giết chết tao giống như mày đã giết chết một người ngày hôm qua hay sao, mày chỉ muốn là trở thành một tên tàn bạo trong xứ, giết người và áp bức họ chớ mày không muốn trở thành một người hòa giải giữa những kẻ đối địch nhau.');
INSERT INTO "vn_mokhtasar" VALUES(28,20,'28:20','Và khi thông tin bị lan truyền ra thì có một người đàn ông ở cuối thành phố vì thương hại Musa đã nhanh chóng chạy đến và nói: Hỡi Musa, các vị tù trưởng của dân chúng Fir''awn đang họp nhau bàn về chuyện để giết Ngươi, vì vậy ta khuyên Ngươi hãy mau ra khỏi nơi này, ta lo sợ việc chúng truy sát Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(28,21,'28:21','Nghe theo lời khuyên của người đàn ông, Musa bỏ trốn đi nơi khác với tâm trạng lo sợ gặp phải chuyện không may nên Người cầu khẩn Thượng Đế của mình: Lạy Thượng Đế, xin Ngài giải cứu bề tôi thoát khỏi đám người làm điều sai quấy, khiến họ không hãm hại được bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(28,22,'28:22','Và khi Y đi về hướng của địa phận Madyan, Y nói: Không chừng Thượng Đế của Ta sẽ hướng dẫn Ta đi theo con đường tốt đẹp nhất, sẽ không khiến Ta đi lạc.');
INSERT INTO "vn_mokhtasar" VALUES(28,23,'28:23','Và khi Y đến địa điểm có nguồn nước mà dân chúng thường xuyên đến múc nước tại đó thuộc địa phận Madyan thì Y thấy có một đám người đang múc nước cho vật nuôi của họ uống. Và Y thấy hai người phụ nữ đang cố giữ đàn cừu của mình khỏi nguồn nước cho đến khi mọi người múc nước xong, vì vậy Musa đến hỏi hai nữ: Hai cô gặp chuyện gì sao không múc nước cùng với dân chúng vậy? Hai nữ trả lời: Chúng tôi thường chờ đợi mọi người múc nước và rời khỏi chỗ đó, để tránh hòa lẫn với họ, bởi cha của chúng tôi đã già yếu không thể múc nước cho đàn cừu uống được, chính vì vậy bắt buộc chúng tôi phải múc nước cho đàn cừu uống.');
INSERT INTO "vn_mokhtasar" VALUES(28,24,'28:24','Vì thương xót cho hai cô gái nên Musa ra tay múc nước giúp. Sau đó Người tiến về phía bóng cây để nghĩ ngơi, và tiếp đó Người cầu khẩn Thượng Đế của mình với mong muốn được giúp đỡ: Lạy Thượng Đế, bề tôi đang cần bất kỳ điều tốt nào mà Ngài sắp ban cho.');
INSERT INTO "vn_mokhtasar" VALUES(28,25,'28:25','Khi hai cô gái trở về nhà kể lại sự việc với cha mình về việc làm của Người. Thế là người cha cử một trong hai con gái đi mời Người đến nhà. Cô gái đến gặp Musa với dáng vẻ ngượng ngùng, nói: Quả thật cha tôi mời anh đến gặp người để người ban thưởng anh về công ơn mà anh đã múc nước giúp chúng tôi. Musa đến gặp người cha của hai cô gái và kể lại cho ông ta nghe về câu chuyện của mình. Nghe xong ông trấn an Người: Chớ sợ hãi, cậu đã thoát khỏi binh lính ác ôn của tên Fir''awn rồi. Quả thật, bọn họ không có quyền hành trên vùng đất Madyan này, lại càng không thể gây hại được cậu.');
INSERT INTO "vn_mokhtasar" VALUES(28,26,'28:26','Do đó, một trong hai cô con gái của ông lão thưa: Thưa cha, xin cha thuê anh ấy chăn nuôi đàn cừu cho chúng ta, thật sự anh ta rất xứng đáng để cha thuê giúp việc, vì anh ta có được sức khỏe và trung tín, với sức khỏe anh ta có thể hoàn thành những gì anh ta được yêu cầu, với lòng trung tín anh ta có thể giữ gìn những gì được giao phó.');
INSERT INTO "vn_mokhtasar" VALUES(28,27,'28:27','Người cha của hai cô gái nói với Musa: Quả thật, bác muốn gả một trong hai đứa con gái này của bác cho cháu với tiền cưới Mahr là cháu phải chăn nuôi đàn cừu cho gia đình bác trong vòng tám năm, nhưng nếu cháu muốn trọn vẹn được mười năm thì đó là sự tự nguyện của cháu chứ không ép buộc cháu. Bởi vì thỏa thuận bắt buộc là trong tám năm và trên tám năm là việc làm tự nguyện. Do đó, bác không muốn ép buộc cháu để gây ra khó khăn cho cháu. Cháu sẽ thấy bác - Insha-Allah - là một người ngoan đạo làm tròn lời giao ước và không nuốt lời.');
INSERT INTO "vn_mokhtasar" VALUES(28,28,'28:28','Musa đáp: Đó chính là lời giao ước giữa cháu và bác đã thỏa thuận, dù cháu hoàn thành bất cứ thời hạn nào trong hai thời hạn vừa nói: Tám năm hoặc mười năm là xem như cháu đã hoàn thành bổn phận. Do đó bác không được yêu cầu cháu làm nhiều hơn nữa, Allah là Đấng xem xét và chứng nhận cho điều chúng ta đã thỏa thuận.');
INSERT INTO "vn_mokhtasar" VALUES(28,29,'28:29','Vì vậy, khi Musa hoàn thành đầy đủ thời hạn là mười năm thì Người cùng với gia đình rời Madyan trở về Ai Cập, Người thấy một ngọn lửa trên sườn núi Tur, Người bảo người nhà: Hãy dừng lại đây, Quả thật Ta thấy ngọn lửa ở đằng xa, Ta hi vọng sẽ mang về cho các người tin tức từ nó hoặc mang về cho các người một khúc củi cháy, hi vọng các người có thể sưởi ấm trong cơn lạnh.');
INSERT INTO "vn_mokhtasar" VALUES(28,30,'28:30','Do đó, khi Musa tiến đến chỗ của ngọn lửa thì Thượng Đế kêu gọi Người từ phía bên phải của thung lũng tại một vùng đất đã được Allah ban phúc từ một loại cây với lời phán rằng: Hỡi Musa, Quả thật TA là Allah,Thượng Đế của vũ trụ và muôn loài.');
INSERT INTO "vn_mokhtasar" VALUES(28,31,'28:31','Và Ngươi hãy ném cây gậy xuống theo lệnh Thượng Đế của mình, nhưng khi Y thấy cây gậy động đậy và di chuyển nhanh nhẹn giống như một con rắn thì Y quay mình bỏ chạy vì sợ hãi và không dám nhìn lại. Chính vì thế Thượng Đế gọi Người: Hỡi Musa, hãy bước đến gần và chớ sợ nó, chắc chắn Ngươi sẽ được an toàn trước nó và trước mọi điều khiến Ngươi sợ.');
INSERT INTO "vn_mokhtasar" VALUES(28,32,'28:32','Hãy áp bàn tay phải của Ngươi vào lòng ngực của Ngươi, khi rút tay ra sẽ trở thành trắng toát không phải vì bệnh bạch tạng. Vì vậy Musa áp bàn tay vào lòng ngực và khi rút ra thì nó trắng toát giống như tuyết; và Ngươi hãy khép cánh tay vào cạnh sườn của Ngươi để làm mất đi sự sợ hãi của Ngươi. Vì vậy Musa khép nó vào cạnh sườn của Người thì lập tức sự sợ hãi đã biến mất. Với cây gậy và bàn tay là hai bằng chứng mà Thượng Đế của Người gởi đến trình bày cho Fir''awn và các vị tù trưởng của hắn. Quả thật chúng là một đám người không thần phục Allah, chúng phủ nhận và công khai phạm tội.');
INSERT INTO "vn_mokhtasar" VALUES(28,33,'28:33','Musa trình bày nỗi lo với Thượng Đế mình rằng: Quả thật bề tôi đã giết chết một người trong đám dân của họ. Do đó, bề tôi sợ họ sẽ giết bề tôi trả thù nếu bề tôi đi truyền đạt cho họ những điều mà Ngài cử phái bề tôi mang đến cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(28,34,'28:34','Và người em của bề tôi là Harun, nó hoạt bát hơn bề tôi trong cách ăn nói. Do đó, xin Ngài hãy cử phái nó đi cùng bề tôi với tư cách là người hỗ trợ bề tôi để xác minh cho lời nói của bề tôi là thật. Quả thật, bề tôi sợ Fir''awn và đồng bọn của hắn sẽ buộc tội bề tôi nói dối, giống như việc các Thiên Sứ trước bề tôi đến với các cộng đồng của mình và ho đã phủ nhận.');
INSERT INTO "vn_mokhtasar" VALUES(28,35,'28:35','Allah đáp ứng lời cầu xin của Musa, phán rằng: TA sẽ gia tăng sức mạnh cho Ngươi - hỡi Musa - bằng cách TA cử người anh của Ngươi, Harun, làm một Nabi để phụ tá Ngươi, và sẽ ban cho hai Ngươi quyền thế và sức mạnh khiến chúng không hãm hại được hai Ngươi, với những phép lạ của TA đã ban cho hai ngươi cũng như những người tin tưởng theo hai Ngươi chắc chắc sẽ thắng lợi.');
INSERT INTO "vn_mokhtasar" VALUES(28,36,'28:36','Rồi khi Musa đến gặp Fir''awn và đồng bọn của hắn với những lời mặc khải rõ rệt của TA, chúng nói: Đây chẳng qua là trò ảo thuật dối trá bịa đặt của Musa, bọn ta chưa bao giờ nghe chuyện này từ nơi tổ tiên của bọn ta trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(28,37,'28:37','Và Musa nói với Fir''awn: Thượng Đế của Ta biết rất rõ ai là người mang sự chỉ đạo từ Ngài đến và ai là người xứng đáng sẽ hưởng kết quả tốt đẹp trong cuộc sống Đời Sau. Quả thật những kẻ làm điều sai quấy sẽ không lấy được những gì chúng mong muốn cũng như không thoát khỏi điều mà chúng khiếp sợ.');
INSERT INTO "vn_mokhtasar" VALUES(28,38,'28:38','Và Fir''awn nói với các tù trưởng của hắn rằng: Hỡi quần thần, Ta không biết thần linh nào khác của các ngươi ngoài ta cả. Hỡi Haman, ngươi hãy đốt lò nung gạch đất sét cho trẫm cho đến khi nó chín lên và xây nó thành một cái tháp cao để trẫm đi lên trời và đứng trên đó gặp Thượng đế của Musa, và trẫm nghĩ rằng Musa chỉ là kẻ nói dối về những gì nó tự xưng là Thiên Sứ của Allah được cử phái đến cho trẫm cũng như cho dân chúng của trẫm.');
INSERT INTO "vn_mokhtasar" VALUES(28,39,'28:39','Nhưng Fir''awn và đồng bọn của hắn tỏ ra rất kiêu ngạo, chúng thống trị đất nước Ai Cập bằng quyền lực cho nên chúng phủ nhận việc phục sinh, và nghĩ rằng chúng sẽ không bị đưa trở về gặp lại TA vào Ngày Phán Quyết để chịu sự thanh toán thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(28,40,'28:40','Bởi thế,TA đã túm bắt hắn và đồng bọn của hắn rồi ném tất cả bọn chúng xuống biển và nhấn chúng chết chìm. Do đó Ngươi hãy suy ngẫm - hỡi Thiên Sứ - về kết cuộc của những kẻ làm điều sai quấy kết thúc như thế nào, quả thực kết cuộc cuối cùng của chúng là bị tiêu diệt.');
INSERT INTO "vn_mokhtasar" VALUES(28,41,'28:41','Và TA đã làm cho Fir''awn và đồng bọn của hắn thành những nhà lãnh đạo cho các kẻ bạo ngược và kẻ lạc đạo chuyên gọi người đi vào Hỏa Ngục, theo sau chúng là những kẻ phủ nhận và lầm lạc. Và vào Ngày Phục Sinh, chúng sẽ không được ai giúp đỡ để thoát khỏi sự trừng phạt, đúng hơn, chúng sẽ bị trừng phạt gấp đôi khi chúng đã tạo ra đường lối xấu xa và kêu gọi đến với sự lầm lạc, cũng như chúng sẽ nhận được việc gánh nặng tội lỗi của chúng và tội lỗi của những người đi theo chúng.');
INSERT INTO "vn_mokhtasar" VALUES(28,42,'28:42','Và TA đã tăng sự trừng phạt cho bọn chúng ở trên thế gian này với sự nhục nhã và xua đuổi, và vào Ngày Phục Sinh, chúng sẽ là những kẻ đáng khiển trách và bị trục xuất khỏi lòng thương xót của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(28,43,'28:43','Quả thật TA (Allah) đã ban cho Musa Kinh Tawrah sau khi đã cử phái các Sứ Giả đến với các cộng đồng thời trước, nhưng đều bị họ cho là giả mạo, bởi thế TA đã tiêu diệt họ vì lý do họ đã phủ nhận các vị Sứ Giả. Kinh Tawrah sẽ soi sáng nhân loại thấy những điều hữu ích để họ làm và những điều làm thiệt thân để họ từ bỏ. Và Kinh Tawrah vừa là một Chỉ Đạo đến với sự tốt đẹp vừa là một hồng ân cho họ ở Đời này và kể cả Đời Sau để may ra nhờ đó mà họ ghi nhớ Allah về ân huệ Ngài ban cho họ và để họ biết tạ ơn Ngài cũng như tin tưởng nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(28,44,'28:44','Hỡi Thiên Sứ Muhammad - và Ngươi đã không có mặt ở phía tây gần ngọn núi Tur nơi mà Musa nhận quyết định công việc TA ra lệnh cử phái Y đi gặp Fir''awn và các vị tù trưởng của hắn và Ngươi cũng không là một nhân chứng của sự việc đó cho đến khi Ngươi biết được nguồn tin đó để kể lại cho nhân loại nghe, chính vì vậy nó (nguồn tin) là điều mặc khải của Allah dành cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(28,45,'28:45','Và TA đã tạo ra những thế hệ khác sau Musa và cho họ ở lại lâu hơn cho đến khi họ quên lời giao ước của Allah; và Ngươi (Muhammad) không phải là người sinh sống chung với cư dân của Madyan để Ngươi đọc cho họ nghe về những Lời mặc khải của TA, nhưng chính TA là Đấng đã cử phái Ngươi cũng như TA đã mặc khải cho Ngươi về nguồn tin của Musa cùng với cuộc sống của Y ở tại Madyan. Bởi thế Ngươi báo cho nhân loại biết về những điều mà Allah đã mặc khải cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(28,46,'28:46','Và Ngươi cũng đã không ở bên sườn núi Tur khi TA gọi Musa và TA đã mặc khải cho Y điều mà TA đã mặc khải cho đến khi Ngươi thuật lại về điều đó. Tuy nhiên, TA cử phái Ngươi đến như là một Hồng ân từ Thượng Đế của Ngươi cho nhân loại. Vì vậy TA đã mặc khải cho Ngươi biết về nguồn tin đó để Ngươi cảnh báo một dân tộc mà trước Ngươi đã không có một vị Sứ Giả nào được cử đến cảnh báo họ may ra họ biết thức tỉnh và tin tưởng nơi những điều mặc khải từ Allah, Đấng Quang Vinh đã được Ngươi mang đến cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(28,47,'28:47','Và nếu không cử phải đến với họ các vị Thiên Sứ thì khi chúng nhận được sự trừng phạt của Allah về lý do phủ nhận và việc làm tội lỗi, chúng sẽ oán trách Allah rằng:"Tại sao Ngài không cử một Sứ Giả đến với chúng tôi để chúng tôi tuân theo và thực hiện các lời mặc khải của Ngài cũng như chúng tôi sẽ trở thành những người tin tưởng làm theo chỉ thị của Ngài?" Nếu không vì lý do đó, TA đã vội trừng phạt chúng, nhưng TA đã trì hoãn cho đến khi TA tha thứ cho chúng và cử một Sứ giả đến với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(28,48,'28:48','Nhưng khi Muhammad mang Thông điệp từ Thượng đế của Y đến với Quraish thì những người Do Thái yêu cầu Y đưa ra bằng chứng này, chúng nói: Tại sao Muhammad không được ban cho Phép Lạ giống như Phép Lạ đã được ban cho Musa để khẳng định Y là Sứ Giả đến từ Thượng Đế của Y như: Bàn tay và cây gậy. Hỡi Thiên Sứ, Ngươi hãy đáp lại chúng: Há những người Do Thái cũng đã phủ nhận nơi những điều đã được ban cho Musa trước đây đó sao? Và chúng nói về Tawrah và Qur''an: Quả thật cả hai quyển Kinh này đều là ma thuật hỗ trợ lẫn nhau và chúng tôi không tin bất cứ cái nào.');
INSERT INTO "vn_mokhtasar" VALUES(28,49,'28:49','Hỡi Thiên Sứ Muhammad - Ngươi hãy bảo chúng: Thế các ngươi hãy mang một Kinh Sách từ Allah đến làm một Chỉ Đạo tốt hơn hai quyển kinh Tawrah và Qur''an này. Nếu các ngươi mang đến thì Ta (Muhammad) sẽ theo nó, nếu các ngươi nói thật trong lời tuyên bố của các ngươi rằng Tawrah và Qur''an là hai loại ma thuật.');
INSERT INTO "vn_mokhtasar" VALUES(28,50,'28:50','Nhưng nếu bộ tộc Quraish không đáp lời Ngươi đã yêu cầu chúng đưa ra một Kinh Sách khác tốt hơn hai quyển kinh Tawrah và Qur''an này thì chắc chắn rằng việc chối bỏ của chúng không phải là lý do. Quả thật chúng chỉ làm theo sở thích và dục vọng của chúng mà thôi. Quả thật, không ai lầm lạc hơn những kẻ chỉ tuân theo dục vọng của mình thay vì tuân theo Chỉ Đạo của Allah. Quả thật Allah không hướng dẫn cho đám người làm điều sai quấy bởi sự phủ nhận Allah của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(28,51,'28:51','Và chắc chắn TA làm cho những kẻ thờ đa thần và người Do Thái trong con cháu Israel biết rõ về câu chuyện của những cộng đồng thời trước cũng như TA đã trừng phạt chúng khi chúng phủ nhận các Sứ Giả của TA. Hi vọng chúng quan tâm đến vấn đề này và tin tưởng cho đến khi chúng sẽ không gặp phải sự trừng phạt giống như những người đó.');
INSERT INTO "vn_mokhtasar" VALUES(28,52,'28:52','Những ai đã được TA củng cố đức tin Iman với Kinh Tawrah trước khi Kinh Qur''an được ban xuống. Họ chính là người Do Thái và Thiên Chúa giáo tin tưởng nơi Qur''an khi họ tìm thấy Nó trong Kinh sách của họ về những thông tin và đặc tính của Nó (Qur''an).');
INSERT INTO "vn_mokhtasar" VALUES(28,53,'28:53','Và khi Kinh Qur''an được đọc ra cho họ thì họ nói ngay: Chúng tôi tin tưởng vào Nó mà không nghi ngờ gì cả. Quả thật Nó là Chân lý đã được ban xuống từ Thượng Đế của chúng tôi. Thật sự trước khi Kinh Qur''an này được ban xuống, chúng tôi là những người Muslim tin tưởng về những điều được các Sứ Giả mang đến trước Nó.');
INSERT INTO "vn_mokhtasar" VALUES(28,54,'28:54','Những người được mô tả như vậy thì Allah sẽ ban thưởng hai lần cho việc làm của họ: Vì kiên nhẫn với sự tin tưởng vào Kinh sách của họ và tin tưởng vào Thiên sứ Muhammad khi Ngài cử phái đến cũng như họ dùng những việc làm tốt đẹp để xóa bỏ đi những việc xấu xa qua những tội lỗi đã phạm, và chi dùng của cải mà TA đã cung cấp cho họ để làm việc thiện.');
INSERT INTO "vn_mokhtasar" VALUES(28,55,'28:55','Và khi những người có đức tin nghe được thị dân kinh sách nói chuyện phi lý thì họ cáo lui quay mặt bỏ đi và nói: Chúng tôi có công việc của chúng tôi cũng như quí vị có công việc của quí vị. Chúc quí vị Bằng an từ cuộc trò chuyện, chúng tôi không muốn đồng hành với kẻ ngu dốt luôn gây tổn hại cho tôn giáo và thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(28,56,'28:56','Hỡi Thiên Sứ Muhammad - quả thật Ngươi không hướng dẫn được ai mà Ngươi yêu thương để họ thành công đến với đức tin Iman chẳng hạn như Abu Talib và những người khác, ngược lai, Allah là Đấng Duy Nhất hoàn thành việc hướng dẫn cho người nào Ngài muốn. Và Ngài biết rõ nhất ai là những người sẽ được hướng dẫn đi theo con đường Chính Đạo.');
INSERT INTO "vn_mokhtasar" VALUES(28,57,'28:57','Và những kẻ đa thần trong dân chúng Makkah viện lý do không có đức tin Iman và không gia nhập Islam, họ nói: "Nếu bọn ta gia nhập Islam mà Ngươi mang đến thì bọn ta sẽ bị kẻ thù túm bắt giết chết bọn ta ở vùng đất này một cách nhanh chóng." Há TA (Allah) đã không thiết lập cho những kẻ đa thần một vùng đất (Makkah) an toàn, cấm sát hại làm đổ máu và cấm gây bất công cũng như chúng được an toàn trước sự công kích của người khác chống lại chúng, và chúng được mang đến đủ loại hoa quả như là bổng lộc của TA cho chúng hay sao? Nhưng đa số bọn chúng không biết những gì Allah ban Ân huệ cho chúng và chúng không biết ơn Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(28,58,'28:58','Và đa số dân cư của thị trấn đã phủ nhận Ân huệ của Allah và vượt quá mức giới hạn trong việc phạm tội nên TA đã giáng xuống họ sự trừng phạt và tiêu diệt toàn bộ. Đó nhà cửa của họ vẫn còn điêu tàn hoang phế, không ai ở đó sau khi đã bị hủy diệt ngoại trừ vài người qua đường. Và chính TA là Đấng sẽ thừa hưởng các tầng trời và trái đất cũng như tất cả vạn vật trong chúng.');
INSERT INTO "vn_mokhtasar" VALUES(28,59,'28:59','Hỡi Thiên Sứ Muhammad - Thượng Đế của Ngươi đã không tiêu diệt bất cứ thị trấn nào cho đến khi Ngài gởi một Sứ Giả đến mỗi thị trấn lớn của nó giống như Ngài đã dựng lên Ngươi từ thị trấn mẹ (Makkah). Và TA (Allah) cũng không tiêu diệt dân cư của các thị trấn lúc họ là những người chính trực ở trên Chân lý; Ngài chỉ tiêu diệt họ khi nào họ là những kẻ làm điều sai quấy bởi sự vô đức tin và bất tuân.');
INSERT INTO "vn_mokhtasar" VALUES(28,60,'28:60','Và những vật đã được Thượng Đế ban cho các ngươi chỉ là vật yêu thích tạm thời và là những thứ tô điểm cho cuộc sống trần tục này rồi nó sẽ biến mất, còn những gì ở nơi Allah ở Đời Sau mới là phần thưởng cao quý, nó sẽ tốt hơn và còn mãi. Thế các ngươi không hiểu được rằng những thứ còn mãi sẽ tốt hơn những thứ sớm tan biến ư?');
INSERT INTO "vn_mokhtasar" VALUES(28,61,'28:61','Thế một người mà TA đã hứa sẽ ban thưởng Thiên Đàng ở Đời Sau và trong đó Y sẽ hưởng niềm hạnh phúc vĩnh cửu có giống với kẻ mà TA đã cho hưởng thụ về của cải và những món trang hoàng trong đời sống trần tục này, sau đó vào Ngày Phục Sinh y sẽ được dựng sống lại để chịu tội trong Hỏa Ngục hay không?!');
INSERT INTO "vn_mokhtasar" VALUES(28,62,'28:62','Và vào Ngày mà Thượng Đế sẽ gọi chúng đến chất vấn rồi Ngài sẽ phán: Đâu là những kẻ hợp tác của TA mà các ngươi đã từng thờ phụng ngoài TA cũng như các ngươi đã xác nhận chúng là những kẻ hợp tác của TA?');
INSERT INTO "vn_mokhtasar" VALUES(28,63,'28:63','Những kẻ xứng đáng nhận được sự trừng phạt từ việc mời gọi (nhân loại) đến với sự phủ nhận sẽ lên tiếng thưa: "Lạy Thượng Đế của bầy tôi! Đây là những kẻ mà bầy tôi đã dắt đi lạc giống như tự bản thân bầy tôi đi lạc, bầy tôi vô can với chúng trước mặt Ngài, không phải chúng đã thờ phụng bầy tôi trước đây và quả thật chúng là những kẻ thờ phụng lũ Shaytan.''''');
INSERT INTO "vn_mokhtasar" VALUES(28,64,'28:64','Và có lời phán bảo chúng: "Các ngươi hãy gọi những thần linh của các ngươi đến đây để cứu các ngươi thoát khỏi sự sĩ nhục." Thế là chúng gọi các thần linh của chúng nhưng không ai trả lời chúng cả; và chúng sẽ thấy hình phạt dành cho chúng ngay trước mặt. Phải chi chúng đã ở trên trần gian được hướng dẫn đi theo Chân lý thì đâu đến nỗi bị sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(28,65,'28:65','Và vào Ngày Thượng Đế sẽ gọi chúng đến rồi phán: "Đâu là những điều mà các ngươi đã trả lời các Sứ Giả mà TA cử phái Họ đến với các ngươi?"');
INSERT INTO "vn_mokhtasar" VALUES(28,66,'28:66','Và vào Ngày đó, những lý lẽ của họ sẽ mờ mịt không kể ra được bất cứ điều gì cũng như họ không thể hỏi han nhau được gì, khi họ quá khiếp sợ bởi họ biết chắc rằng họ đang đến gần với sự trừng phạt khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(28,67,'28:67','Nhưng đối với ai biết sám hối trong số những người đa thần từ việc phủ nhận đức tin của mình, biết tin tưởng nơi Allah và các Sứ Giả của Ngài cũng như làm việc thiện thì may ra y sẽ là một trong những người chiến thắng với những gì y theo đuổi, và được cứu rỗi thoát khỏi cảnh sợ hãi.');
INSERT INTO "vn_mokhtasar" VALUES(28,68,'28:68','Hỡi Thiên Sứ Muhammad - Thượng Đế của Ngươi tạo hóa bất cứ vật gì Ngài muốn cũng như chọn lựa những ai Ngài muốn để phục tùng và làm sứ mạng truyền giáo cho Ngài. Bọn đa thần không có một sự chọn lựa nào để họ có thể chống đối Allah. Ngài trong sạch và vô can với mọi điều mà họ đã tổ hợp việc thờ phụng cùng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(28,69,'28:69','Và quả thật, Thượng đế của Ngươi luôn biết rất rõ những điều mà bầy tôi của Ngài giấu giếm trong lòng cũng như những điều bộc lộ ra ngoài, không một điều nào có thể giấu giếm được Ngài và dựa vào đó Ngài sẽ thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(28,70,'28:70','Và Ngài là Allah, Đấng mà không có bất cứ Thượng Đế đích thực ngoài Ngài. Mọi lời ca tụng ở trần gian và ở Đời Sau đều thuộc về Ngài. Ngài nắm quyền xét xử không ai kháng lại được, và tất cả các ngươi sẽ được đưa về gặp Ngài trở lại vào Ngày Phục Sinh để thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(28,71,'28:71','Hỡi Thiên Sứ - hãy bảo những người đa thần: Các ngươi hãy cho Ta biết, nếu Allah làm ban đêm kéo dài vĩnh viễn cũng như không bị gián đoạn cho đến Ngày Phục Sinh thì có thượng đế nào ngoài Allah sẽ mang ánh sáng lại cho các ngươi giống như ánh sáng của ban ngày? Thế các ngươi không nghe thấy bằng chứng này sao? Và các ngươi nên biết rằng không có Thượng Đế đích thực nào khác ngoại trừ Ngài là Thượng Đế duy nhất mang lại cho các ngươi việc đó?!');
INSERT INTO "vn_mokhtasar" VALUES(28,72,'28:72','Hỡi Thiên Sứ - hãy bảo chúng: Các ngươi hãy cho Ta biết, nếu Allah làm ban ngày kéo dài vĩnh viễn cho đến Ngày Phục Sinh thì có thượng đế nào ngoài Allah sẽ mang lại cho các ngươi bóng tối để các ngươi có thể nghỉ ngơi sau những lao động mệt nhọc vào ban ngày chăng? Thế các ngươi không thấy những dấu hiệu này sao? Và các ngươi nên biết rằng không có Thượng Đế đích thực nào khác ngoại trừ Ngài là Thượng Đế duy nhất mang lại cho các ngươi tất cả mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(28,73,'28:73','Hỡi nhân loại - chính do lòng Thương sót của Ngài nên Ngài đã tạo ra ban đêm tối tăm để cho các ngươi có thể nghỉ ngơi sau khi kết thúc việc làm mệt nhọc vào ban ngày cũng như tạo ra ban ngày sáng tỏ để cho các ngươi tìm kế sinh nhai, và để cho các ngươi có dịp tỏ lòng biết ơn Allah đã ban Ân huệ cho các ngươi và để các ngươi không phụ ân Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(28,74,'28:74','Và vào Ngày mà Thượng Đế sẽ gọi chúng đến chất vấn rồi Ngài sẽ phán: Đâu là những kẻ hợp tác của TA mà các ngươi đã từng thờ phụng ngoài TA cũng như các ngươi đã xác nhận chúng là những kẻ hợp tác của TA?');
INSERT INTO "vn_mokhtasar" VALUES(28,75,'28:75','Và TA sẽ đưa ra từ mỗi cộng đồng một Sứ Giả để làm chứng về việc chúng đã phủ nhận và dối trá, rồi TA sẽ bảo những kẻ phủ nhận của các cộng đồng đó rằng: Hãy đưa ra lập luận và bằng chứng của các ngươi về việc các ngươi đã phủ nhận và dối trá, nhưng chúng không có lập luận nào cả và chúng biết chắc rằng sự thật luôn thuộc về Allah còn những điều giả tạo mà chúng đã từng tổ hợp với Ngài sẽ bỏ chúng đi mất.');
INSERT INTO "vn_mokhtasar" VALUES(28,76,'28:76','Quả thật, Qarun xuất thân trong đám dân của Musa nhưng hắn đối xử ngạo mạn với họ, và TA đã ban cho hắn kho tàng châu báu nhiều đến mức cần phải nhờ hàng trung đội người mạnh khỏe mới vác nổi những chìa khóa kho của hăn. Và khi người dân của hắn bảo hắn rằng: Chớ cuống quýt mừng rỡ. Quả thật, Allah không thích những kẻ mừng cuống quýt mà Ngài còn ghét bỏ chúng cũng như trừng trị chúng về điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(28,77,'28:77','Và ngươi hãy dùng của cải mà Allah đã ban cho ngươi mà tìm kiếm phần thưởng ở cuộc sống Đời Sau bằng cách làm nhiều việc thiện, và chớ quên phần của ngươi về việc ăn uống cũng như ăn mặc và những việc làm khác nhưng không được phung phí, kiêu ngạo mà hãy quan hệ tốt với Thượng Đế của ngươi và những người bề tôi của Ngài giống như Ngài đã đối xử tốt với ngươi, và chớ tìm cách làm điều thối nát trong xứ bằng việc phạm tội cũng như từ bỏ sự phục tùng Ngài. Quả thật Allah không yêu thương những kẻ làm điều xấu và thối nát trong xứ, ngược lại, Ngài rất căm ghét chúng.');
INSERT INTO "vn_mokhtasar" VALUES(28,78,'28:78','Qarun bảo: "Quả thật, ta được nhiều của cải là do sự hiểu biết và tài năng của ta, bởi thế ta rất xứng đáng được nhận điều đó." Phải chăng Qarun đã không biết việc Allah đã tiêu diệt những người đã có nhiều quyền uy và giàu của cải hơn hắn từ những cộng đồng trước hắn hay sao? Do đó quyền thế và của cải sẽ không giúp ích được cho chúng, và vào Ngày Phục Sinh những kẻ phạm tội sẽ không bị hỏi về những tội lỗi của chúng vì Ngài đã biết tường tận điều đó, nhưng Ngài sẽ hỏi chúng bằng lời lẽ nhạo báng và khiển trách.');
INSERT INTO "vn_mokhtasar" VALUES(28,79,'28:79','Rồi Qarun ra ngoài gặp dân chúng phô trương vẻ lộng lẫy của mình, những kẻ ham muốn danh lợi trong việc trang hoàng của đời sống trần tục - những người bạn của Qarun - vội lên tiếng: Ôi, ước chi mình được ban cho sự trang hoàng lộng lẫy của thế gian này giống như những gì đã được ban cho Qarun. Quả thật, Qarun có cả một kho của cải khổng lồ.');
INSERT INTO "vn_mokhtasar" VALUES(28,80,'28:80','Những người được Allah ban cho kiến thức khi họ nhìn thấy Qarun trong sự hào nhoáng lộng lẫy của hắn cũng như nghe được những điều mong ước của những người bạn của hắn thì nói rằng: Chỉ khổ thân cho các ngươi thôi! Bởi vì phần thưởng của Allah ở Đời Sau và những niềm vui sướng được Ngài chuẩn bị ban cho những ai có đức tin và làm việc thiện tốt hơn sự lộng lẫy của đời sống trần tục này, và chỉ những người kiên nhẫn mới đạt được phần thưởng vĩnh viễn nơi Allah, thứ mà nó tốt hơn của cải sớm tan biến của trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(28,81,'28:81','Bởi thế, TA đã làm cho đất sụp xuống nuốt mất y lẫn nhà cửa của y cũng như trừng phạt những ai gây ra sự bất công. Và y không có được một bè nhóm nào giải cứu y thoát khỏi hình phạt của Allah và y cũng không thể tự cứu mình được.');
INSERT INTO "vn_mokhtasar" VALUES(28,82,'28:82','Và những kẻ đã mơ ước muốn có thật nhiều của cải và sự hào nhoáng lộng lẫy trước lúc Y bị tàn phá nay chúng lại nói với vẻ ân hận và biết nhận thức: Chúng ta không biết rằng quả thật Allah nới rộng bổng lộc cho người nào Ngài muốn trong số bầy tôi của Ngài cũng như Ngài thu hẹp bổng lộc cho người nào Ngài muốn trong số bọn chúng?! Nếu Allah không nhân từ đối với chúng mình thì Ngài đã trừng phạt với những điều mà chúng mình đã từng mơ ước muốn có được, cũng như cho đất sụp xuống nuốt mất chúng mình giống như đã nuốt mất Qarun vậy. Quả thật những kẻ không có đức tin chắc chắn sẽ không chiến thắng ở trần gian kể cả Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(28,83,'28:83','Đó là ngôi nhà hạnh phúc và vẻ vang (Thiên Đàng) ở Đời Sau mà TA đã làm ra cho những ai không muốn tự suy tôn mình trên trái đất vì có đức tin Iman, đi theo chân lý và không làm điều thối nát. Và kết quả đáng được tuyên dương là được Thiên Đàng hạnh phúc cũng như nhận được sự hài lòng của Allah. Và Thiên Đàng chỉ dành cho nhóm người ngay chính với Thượng Đế của mình qua việc tuân theo những gì Ngài ra lệnh và tránh xa những gì Ngài ngăn cấm.');
INSERT INTO "vn_mokhtasar" VALUES(28,84,'28:84','Ai đến trình diện (Allah) vào Ngày phán xét cuối cùng với những việc làm tốt đẹp như: Hành lễ Salah, Zakat, nhịn chay và những việc làm khác thì y sẽ được phần thưởng tốt hơn những việc đã làm đó, với một việc tốt đẹp thì sẽ được hưởng gấp mười lần. Ai đến trình diện (Allah) vào Ngày phán xét cuối cùng với việc làm xấu xa như: sự vô đức tin, Riba (cho vay lấy lãi), Zina (tình dục ngoài hôn nhân) và những việc làm khác thì họ chỉ bị phạt ngang bằng với việc xấu mà họ đã làm không tăng cũng không bớt.');
INSERT INTO "vn_mokhtasar" VALUES(28,85,'28:85','Quả thật Allah đã mặc khải cho Ngươi Kinh Qur''an cũng như bắt buộc Ngươi phải truyền đạt Thông điệp của Ngài và thực hiện những điều trong Nó để đưa Ngươi trở lại Makkah đầy vinh quang. Hỡi Thiên sứ -Ngươi hãy nói với những người đa thần: Thượng Đế của Ta biết rất rõ ai là người mang Chỉ đạo của Ngài đến và ai là kẻ đi lầm đường lạc lối khỏi sự hướng dẫn và chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(28,86,'28:86','Hỡi Thiên sứ - trước lúc sứ mạng - Ngươi đã không nghỉ rằng việc Kinh Qur''an được ban xuống cho Ngươi là Mặc khải từ Allah, nhưng đó là một sự thương xót từ Ngài nên đã ban Nó xuống cho Ngươi. Bởi thế chớ bao giờ giúp đỡ những kẻ không có đức tin trên những điều chúng lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(28,87,'28:87','Và Ngươi chớ để những kẻ đa thần ngăn cản Ngươi từ bỏ những lời mặc khải của Allah sau khi Nó đã được ban xuống cho Ngươi và để Ngươi đọc cũng như truyền đạt Nó. Ngươi hãy kêu gọi nhân loại đến với đức tin Iman nơi Allah duy nhất và thực hiện theo một hệ thống luật pháp (Shari''ah) và Ngươi chớ trở thành một trong những kẻ đa thần, những kẻ mà chúng thờ phụng ngoài Allah. Ngược lại, Ngươi phải trở thành một trong những người thờ phụng Allah duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(28,88,'28:88','Và chớ bao giờ thờ phụng một thần linh nào khác cùng với Allah, bởi vì duy chỉ Ngài mới xứng đáng được thờ phụng. Mọi vật đều tiêu tan ngoại trừ Sắc Diện của Ngài. Ngài duy nhất nắm quyền xét xử với những điều Ngài muốn, và tất cả các ngươi sẽ được đưa về gặp Ngài trở lại vào Ngày Phục Sinh để chịu sự phán xét và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(29,1,'29:1','(Alif. La-m. Mi-m) Đã được giải thích ở câu đầu tiên của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(29,2,'29:2','Lẽ nào khi con người thốt ra rằng: Chúng tôi tin tưởng nơi Allah thì họ sẽ được yên thân, không còn bị thử thách để chứng minh sự thật về đức tin như lời mà họ đã nói, để xem xét họ có thật sự là người tin tưởng hay không? Sự việc không như lời họ nghĩ đâu.');
INSERT INTO "vn_mokhtasar" VALUES(29,3,'29:3','Và quả thật,TA đã thử thách những người trước họ để thấy rõ rằng họ chân thật trong đức tin Iman của họ hay chỉ là sự dối trá trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(29,4,'29:4','Hoặc phải chăng những ai làm điều phạm tội từ việc tổ hợp (thần linh cùng với Allah) và những việc làm khác, họ nghĩ rằng có thể qua mặt được TA cũng như thoát khỏi cảnh trừng phạt của TA ư? Thật tệ thay điều mà chúng xét đoán. Chúng sẽ không trốn thoát khỏi Allah cũng như không thoát khỏi cảnh trừng phạt của Ngài nếu chúng chết trên sự vô đức tin của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(29,5,'29:5','Ai hi vọng muốn gặp lại Allah vào Ngày Phán xét cuối cùng để nhận được phần thưởng của mình thì nên biết rằng thời hạn mà Allah đã ấn định về điều đó sẽ đến rất gần vì Ngài là Đấng Hằng Nghe những lời nói của đám bề tôi của Ngài, và là Đấng Hằng Biết mọi việc làm của họ, không một điều gì có thể qua mặt được Ngài, và Ngài sẽ thưởng phạt họ về các việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(29,6,'29:6','Và ai phấn đấu cho bản thân mình để thực hiện sự phục tùng và tránh xa việc phạm tội cũng như đấu tranh cho con đường Chính nghĩa của Allah thì chẳng qua y chỉ phấn đấu cho chính mình, bởi vì việc làm đó sẽ mang lại lợi ích cho y. Và Allah rất mực giàu có, Ngài không cần đến tất cả vạn vật. Do đó, sự phục tùng của chúng có thêm hay giảm đi thì không hề ảnh hướng đến quyền lực và sự giàu có của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(29,7,'29:7','Và những ai tin tưởng và kiên nhẫn về việc TA thử thách họ cũng như làm những việc thiện thì TA sẽ xóa bỏ tội lỗi cho họ bằng những việc thiện mà họ đã làm, và TA sẽ ban phần thưởng cho họ vào Đời Sau tốt hơn những việc mà họ đã làm ở trên trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(29,8,'29:8','Và TA đã ra lệnh cho nhân loại hãy hiếu thảo với cha mẹ và đối xử tốt với họ. Nhưng nếu cha mẹ đấu tranh với ngươi - hỡi con người - để buộc ngươi thờ phượng thần linh khác cùng với TA trong khi ngươi không có kiến thức rõ về sự tổ hợp đó - giống như đã xảy ra với S''ad bin Abi waqqas với người mẹ của y - vì vậy chớ nên nghe lời cha mẹ trong việc làm đó bởi vì không được phép nghe theo tạo vật để nghịch lại Đấng Tạo Hóa. Và các ngươi sẽ trở lại gặp TA vào Ngày Phục Sinh, rồi TA sẽ cho các ngươi biết về những điều các ngươi đã từng làm ở trên trần gian cũng như TA sẽ thưởng phạt cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(29,9,'29:9','Và những ai tin tưởng nơi Allah và làm những việc thiện thì chắc chắn vào Ngày Phục Sinh TA sẽ thu nhận họ vào đoàn thể những người ngoan đạo. TA sẽ tập trung những người này cùng với họ và ban thưởng cho họ bằng phần thưởng của những người đó.');
INSERT INTO "vn_mokhtasar" VALUES(29,10,'29:10','Và trong nhân loại đã có những kẻ nói: Chúng tôi tin tưởng vào Allah, nhưng khi những kẻ đa thần gây tổn hại về đức tin Iman của họ thì họ lại cho nỗi đau đó như là sự trừng phạt của Allah lên họ, thế là họ từ bỏ đức tin Iman để ùa theo người đa thần. Nếu có sự trợ giúp đến từ Thượng Đế của Ngươi - hỡi Thiên Sứ - thì chắc chắn họ sẽ nói: Chúng tôi sát cánh với mọi người - hỡi những người có đức tin - trong đức tin Iman. Há Allah không biết rõ về những điều nằm trong lòng của nhân loại hay sao? Không thể che giấu khỏi Ngài về sự phủ nhận và đức tin Iman. Rồi Allah sẽ cho họ biết như thế nào về những điều nằm trong lòng của họ và Ngài biết rất rõ về họ?');
INSERT INTO "vn_mokhtasar" VALUES(29,11,'29:11','Và chắc chắc Allah biết rất rõ ai là những người tin tưởng thật sự và ai là những người giả vờ biểu hiện đức tin Iman ở bên ngoài nhưng che đậy sự vô đức tin bên trong.');
INSERT INTO "vn_mokhtasar" VALUES(29,12,'29:12','Và những kẻ không có đức tin nói với những người tin tưởng nơi Allah duy nhất rằng: Hãy theo tôn giáo của chúng tôi và chúng tôi sẽ gánh chịu tội lỗi của các ngươi cũng như chúng tôi sẽ chịu phạt thay các ngươi, nhưng họ nào có gánh bất cứ tội lỗi nào của ai khác. Quả thật chúng là những kẻ dối trá trong lời nói này.');
INSERT INTO "vn_mokhtasar" VALUES(29,13,'29:13','Và chắc chắn những kẻ đa thần sẽ gánh vác tội lỗi của chúng về việc kêu gọi nhân loại đến với sự bịa đặt mà chúng đã ngụy tạo cũng như gánh vác tội lỗi của những ai đi theo lời mời gọi của chúng, không hề bị thiếu xót. Và chắc chắn,chúng sẽ bị thẩm tra vào Ngày Phục Sinh về những điều mà chúng đã từng bịa đặt ở trên trần gian từ những điều hư cấu.');
INSERT INTO "vn_mokhtasar" VALUES(29,14,'29:14','Và quả thật TA đã cử phái Nuh làm một Sứ Giả đến với dân chúng của Y, và Y đã sống với họ được chín trăm năm chục năm ( cho việc kêu gọi họ đến với Allah duy nhất nhưng bị họ từ chối và họ vẫn tiếp tục sống trong sự phủ nhận của họ, chính vì thế trận Đại Hồng Thủy túm bắt lấy họ và nhấn chìm chết họ trong lúc họ là những kẻ sai quấy với lý do phủ nhận Allah và và các Sứ Giả của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(29,15,'29:15','Vì thế TA đã cứu Nuh và những người có đức tin theo Y trên một chiếc tàu thoát khỏi bị chết chìm, và TA lấy sự việc đó làm một bài học cho nhân loại biết nhận thức.');
INSERT INTO "vn_mokhtasar" VALUES(29,16,'29:16','Và hãy nhớ - hỡi Thiên Sứ - về câu chuyện của Ibrahim khi Y bảo dân chúng của Y rằng: Hãy thờ phượng Allah duy nhất và hãy sợ sự trừng phạt của Ngài bằng cách tuân theo những việc Ngài ra lệnh và tránh xa những việc Ngài ngăn cấm. Những việc Ngài ra lệnh luôn tốt nhất cho các ngươi nếu các ngươi nhận thức được.');
INSERT INTO "vn_mokhtasar" VALUES(29,17,'29:17','Chẳng qua các ngươi - hỡi người đa thần - đang thờ phượng những hình tượng không giúp ích và cũng không làm hại được các ngươi và các ngươi chỉ bịa đặt chuyện dối trá để khẳng định cho việc thờ phượng đó là rất xứng đáng. Thật sự, những kẻ mà các ngươi thờ phượng không phải Allah, chúng sẽ không thể ban bổng lộc cho các ngươi; các ngươi hãy thờ phượng Allah duy nhất cũng như hãy tạ ân Ngài về những bổng lộc Ngài ban cho các ngươi, và các ngươi sẽ được đưa về gặp Ngài duy nhất vào Ngày Phục Sinh để chịu sự thưởng phạt cho việc thờ phượng các bục tượng của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(29,18,'29:18','Và nếu các ngươi - hỡi người đa thần - phủ nhận những điều mà Muhammad đã mang đến thì chắc chắn những cộng đồng trước các ngươi cũng đã phủ nhận như thế, điển hình như dân của Nuh và dân ''Ad và dân Thamud, trong khi trọng trách của một Sứ Giả chẳng qua là truyền đạt thông điệp cho các ngươi một cách rõ ràng, và Y thực ra đã truyền đạt cho các ngươi điều mà Thượng đế của Y đã ra lệnh truyền cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(29,19,'29:19','Há những kẻ phủ nhận không nhìn thấy việc Allah bắt đầu việc tạo hóa vạn vật rồi tái lập nó sau khi nó tan tành ư?! Quả thật việc đó rất dễ dàng đối với Allah, bởi Ngài là Đấng Toàn Năng, không việc gì có thể làm Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(29,20,'29:20','Người - hỡi Thiên Sứ - hãy bảo những kẻ phủ nhận việc phục sinh rằng: Các ngươi hãy đi khắp nơi trên trái đất để các ngươi suy xét việc Allah bắt đầu việc tạo hóa rồi Ngài tái phục sinh con người sau cái chết, sống lần hai để thanh toán như thế nào. Quả thật, Allah có khả năng trên tất cả mọi việc, không có bất cứ gì làm Ngài thất bại. Vì vậy Ngài không bất lực trong sự phục sinh nhân loại giống như Ngài đã khởi tạo họ lần đầu tiên.');
INSERT INTO "vn_mokhtasar" VALUES(29,21,'29:21','Ngài sẽ trừng phạt kẻ nào Ngài muốn trong số tạo vật của Ngài theo lẽ công bằng của Ngài và Ngài sẽ thương xót những người nào Ngài muốn trong số tạo vật của Ngài bằng lòng nhân từ của Ngài, và các ngươi sẽ bị đưa về gặp Ngài duy nhất vào Ngày Tận Thế để thưởng phạt khi Ngài phục sinh các ngươi từ cõi mộ.');
INSERT INTO "vn_mokhtasar" VALUES(29,22,'29:22','Và các ngươi sẽ không thể trốn thoát khỏi Thượng đế của các ngươi, không thể thoát được hình phạt của Ngài dù các ngươi có lên trời hay xuống đất, và ngoài Allah các ngươi sẽ không có một vị bảo hộ dám đứng ra che chở các ngươi, cũng như ngoài Ngài các ngươi sẽ không có được một vị cứu tinh nào để giải thoát cho các ngươi khỏi sự trừng phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(29,23,'29:23','Và những ai phủ nhận những Dấu hiệu của Allah và việc gặp lại Ngài trong Ngày Tận Thế, đó là những người đã tuyệt vọng vào lòng khoan dung của TA. Do đó chúng sẽ không được vào Thiên Đàng vì tội vô đức tin của chúng và sẽ phải nhận một sự trừng phạt khủng khiếp đang đợi chúng ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(29,24,'29:24','Và dân chúng của Ibrahim không trả lời điều nào khác - sau khi Người kêu gọi họ thờ phượng Allah duy nhất và từ bỏ việc thờ phượng bục tượng - ngoại trừ họ nói: Hãy giết Y hoặc ném Y vào lửa" nhưng Allah đã cứu sống Người ra khỏi lửa. Quả thật trong việc cứu sống Người ra khỏi lửa sau khi bị ném vào nó là những bài học quí cho đám người tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(29,25,'29:25','Và Ibrahim nói với dân chúng của Người rằng: Chẳng qua các ngươi đã tự tôn vinh các bục tượng này lên làm thần linh để tôn thờ là vì thói quen và tình yêu thương của các ngươi vào việc thờ phượng chúng ở đời sống này, rồi vào Ngày Tận Thế tình yêu thương giữa các ngươi sẽ bị đoạn tuyệt. Do đó các ngươi sẽ phủ nhận và nguyền rủa lẫn nhau khi nhìn thấy sự trừng phạt, và chỗ ở của các ngươi sẽ là Hỏa Ngục, và các ngươi sẽ không được ai giúp đỡ để tránh khỏi sự trừng phạt của Allah, không có ai từ những bục tượng mà các ngươi đã tôn thờ và cũng không có ai khác ngoài chúng.');
INSERT INTO "vn_mokhtasar" VALUES(29,26,'29:26','Riêng Lut thì tin nơi Ibrahim, và Người nói: Chắc chắn Ta sẽ di cư đến với Thượng Đế của Ta ở vùng đất Sham hồng phúc. Bởi quả thật Ngài là Đấng Toàn Năng không ai chiến thắng được Ngài cũng như không ai sẽ bị nhục nhã khi di cư đến với Ngài. Ngài là Đấng Chí Minh trong sắp xếp và an bài.');
INSERT INTO "vn_mokhtasar" VALUES(29,27,'29:27','Và TA đã ban cho Ibrahim đứa con trai là Is-haq và đứa cháu nội là Ya''qub, và TA đã tuyển chọn trong con cháu của Y nhận lãnh sứ mạng tiếp thu điều mặc khải và tiếp nhận Kinh Sách được ban xuống từ Allah và ban cho Y phần thưởng cho sự kiên nhẫn của Y ở trên chân lý lúc còn sống trên thế gian này qua việc con cháu ngoan hiền và tiếng thơm để đời. Và quả thật ở Đời Sau, Y sẽ được ban thưởng như phần thưởng của những người ngoan đạo, và những gì được ban thưởng ở trên thế gian này không hề làm giảm mất những phần thưởng mà Ngài đã chuẩn bị cho Y ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(29,28,'29:28','Và Ngươi hãy nhớ lại - hỡi Thiên Sứ - khi Lut nói với dân chúng của Y rằng: Quả thật các ngươi đã phạm vào một tội xấu xa nhất chưa một ai trong thiên hạ đã từng làm trước đây. Chính các ngươi là nhóm người đầu tiên gây ra tội lỗi này, các ngươi đã phá bỏ bản chất tự nhiên vốn có của mỗi con người từ khi sinh ra.');
INSERT INTO "vn_mokhtasar" VALUES(29,29,'29:29','Phải chăng các ngươi chọn làm tình với đàn ông để thỏa mãn dục vọng của các ngươi, cướp bóc những người đi đường, khiến họ không dám đi vì lo sợ những điều khả ố mà các ngươi đang làm cũng như làm những việc sai trái trong những cuộc hội họp của các ngươi như: Trần truồng và làm tổn thương những ai đi ngang qua các ngươi bằng lời nói và hành động? Do đó dân chúng của Người đã không có câu trả lời sau khi Y ngăn cấm chúng về những hành vi sai trái ngoại trừ chúng nói: Ngươi hãy mang hình phạt của Allah đến trừng phạt bọn ta đi nếu Ngươi thành thật trong những điều Ngươi đã nói.');
INSERT INTO "vn_mokhtasar" VALUES(29,30,'29:30','Trước sự ngoan cố của dân chúng, nhất là khi họ yêu cầu giáng xuống họ hình phạt bằng giọng điệu thách thức, nên Nabi Lut cầu khẩn Thượng Đế rằng: Lạy Thượng Đế của bề tôi! Xin Ngài trợ giúp bề tôi chiến thắng đám người thối nát trong xứ với những điều họ đang truyền bá về việc phủ nhận và tội lỗi ghê tởm.');
INSERT INTO "vn_mokhtasar" VALUES(29,31,'29:31','Và khi nhóm Thiên Thần đến theo lệnh của TA mang tin mừng cho Ibrahim về đứa con trai Is-haq và sau Người là đứa cháu Ya''qub. Xong, họ nói rằng: Bọn ta sắp tiêu diệt cư dân của thi trấn Sodom thuộc dân chúng của Lut. Quả thật dân cư của nó là những kẻ làm điều sai quấy với những việc làm ô uế.');
INSERT INTO "vn_mokhtasar" VALUES(29,32,'29:32','Ibrahim nói với các Thiên Thần: Quả thật trong thị trấn mà quí vị muốn hủy diệt có Lut, Y không phải là người làm điều sai quấy. Các Thiên Thần đáp: Bọn ta biết rất rõ ai ở trong đó, chắc chắn bọn ta sẽ giải thoát Y và gia đình của Y thoát khỏi sự hủy diệt ngoại trừ mụ vợ của Y, ả ta phải ở lại cùng đám người bị hủy diệt rồi bọn ta sẽ hủy diệt mụ ta cùng với đám dân làng.');
INSERT INTO "vn_mokhtasar" VALUES(29,33,'29:33','Và khi các Thiên Thần đến gặp Lut theo mệnh lệnh của TA để hủy diệt dân chúng của Lut. Điều đó đã làm Y lo âu và buồn bã vì sợ họ sẽ bị dân chúng hãm hại, vì nhóm Thiên Thần đến với hình dạng những người đàn ông phàm tục trong khi dân chúng của Y chỉ muốn thỏa mãn sinh lý với nam giới thay vì phụ nữ. Các Thiên Thần nói với Y rằng: Ngươi chớ lo sợ, dân chúng của Ngươi sẽ không gây thiệt hại gì được cho Ngươi đâu và Ngươi cũng chớ đau buồn về những điều mà bọn ta đã báo với Ngươi về sự hủy diệt chúng. Chắc chắn bọn ta sẽ giải cứu Ngươi lẫn gia đình Ngươi thoát khỏi sự hủy diệt, ngoại trừ mụ vợ của Ngươi phải ở lại cùng đám người bị hủy diệt rồi bọn ta sẽ hủy diệt mụ ta cùng với bọn dân làng.');
INSERT INTO "vn_mokhtasar" VALUES(29,34,'29:34','Chắc chắn TA phải giáng một hình phạt khủng khiếp từ trên trời xuống trừng phạt dân cư của thị trấn này bởi những việc ô uế của chúng, và đó là một trận mưa đá, một hình phạt vì sự bất tuân Allah qua những hành vi ghê tởm, và đó là làm tình với đàn ông thay vì phụ nữ.');
INSERT INTO "vn_mokhtasar" VALUES(29,35,'29:35','Và TA đã để lại một dấu hiệu rõ ràng cho một thị trấn này mà TA đã phá hủy nó cho đám người biết suy nghĩ. Bởi vì họ là những người biết lưu tâm về những dấu hiệu đó.');
INSERT INTO "vn_mokhtasar" VALUES(29,36,'29:36','Và với dân làng của Madyan, TA đã cử người anh em của họ là Shu''aib đến gặp họ, Y kêu gọi họ: Hỡi dân ta! Hãy thờ phượng Allah duy nhất, hãy đặt hi vọng trong việc thờ phượng của các ngươi hướng về Ngài rồi các người sẽ được Ngài ban cho phần thưởng ở Đời Sau, và chớ phá hại trên trái đất bằng những việc phạm tội cũng như lan truyền nó.');
INSERT INTO "vn_mokhtasar" VALUES(29,37,'29:37','Vì dân chúng của Người cho rằng Người nói dối nên một trận động đất khủng khiếp đã chụp bắt họ, thế là họ chết nằm úp mặt xuống đất, không ai thoát khỏi.');
INSERT INTO "vn_mokhtasar" VALUES(29,38,'29:38','Và tượng tự, TA đã hủy diệt ''Ad thuộc dân của Nabi Hud và Thamud thuộc dân của Nabi Saleh, TA cũng đã trình bày rõ ràng cho các ngươi - hỡi dân Makkah - về chốn ở của họ được dựng bằng đá ở thành phố Al-Shahr thuộc Hadramaut (Yemen) là bằng chứng cho việc họ đã bị tiêu diệt. Họ đã bị Shaytan mê hoặc bằng hành động thối tha tội lỗi khiến họ ngoảnh mặt với con đường chân lý trong khi họ đã được các Thiên Sứ của họ hướng dẫn cụ thể đâu là chân lý, đâu là tội lỗi lầm lạc và họ đã nhận thức rõ nhưng họ đã chọn con đường thỏa mãn dục vọng bản thân.');
INSERT INTO "vn_mokhtasar" VALUES(29,39,'29:39','Và TA đã tiêu diệt Qarun - khi hắn đối xử ngạo mạn với dân của Musa - bằng cách cho đất sụp xuống nuốt mất hắn lẫn nhà cửa của hắn, và TA cũng đã tiêu diệt Fir''awn và tên tù trưởng của hắn, Haman, bằng cách nhấn chúng chết chìm trong biển cả. Và chắc chắn Musa đã đến gặp chúng với bao bằng chứng rõ ràng chứng tỏ sự trung thực của Y, nhưng chúng vẫn tỏ thái độ kiêu ngạo trong xứ Ai Cập, và chúng nào thoát khỏi sự trừng phạt của TA.');
INSERT INTO "vn_mokhtasar" VALUES(29,40,'29:40','Tất cả đám người đã được liệt kê đã bị TA tiêu diệt bằng cực hình. Trong số đó, dân của Nabi Lut đã bị TA giáng xuống trận mưa đá nung có khắc tên chúng lên những tảng đá đó; trong số đó, dân của Nabi Saleh và dân của Nabi Shu''aib đã bị TA trừng phạt bằng tiếng thét kinh hoàng. Tên Qarun đã bị TA trừng phạt bằng cách làm cho đất sụp xuống nuốt mất hắn lẫn nhà cửa của hắn; trong số đó, dân của Nabi Nuh, tên Fir''awn và tên Haman đã bị TA tiêu diệt chúng bằng cách nhấn chìm trong biển cả. Và Allah đã không vô cớ tiêu diệt họ, mà Ngài tiêu diệt chúng bằng chính những tội lỗi mà họ đã vi phạm và chúng rất xứng đáng bị trừng phạt như thế.');
INSERT INTO "vn_mokhtasar" VALUES(29,41,'29:41','Hình ảnh của những người đa thần đã nhận các bục tượng để thờ phượng thay vì Allah bằng niềm hi vọng được hưởng lợi ích hoặc được biện hộ giống như hình ảnh của con nhện tự giăng tơ làm nhà để bảo vệ nó khỏi sự tấn công của kẻ thù, và trong khi ngôi nhà mỏng manh nhất trong tất cả thể loại nhà chính là nhà của con nhện. Nó không bảo vệ được ngôi nhà bình an khỏi sự tấn công của kẻ thù, và cũng như những bục tượng của chúng đang thờ phượng không giúp ích được và không hãm hại được cũng như không can thiệp được. Nếu như nhóm người đa thần biết rõ điều đó thì chúng sẽ không bao giờ nhận bục tượng để thờ phượng thay vì Allah.');
INSERT INTO "vn_mokhtasar" VALUES(29,42,'29:42','Quả thật Allah biết rất rõ bất cứ điều gì chúng cầu nguyện thay vì Ngài. Không điều gì có thể giấu giếm khỏi Ngài Đặng, bởi Ngài là Đấng Toàn Năng, không ai vượt trội hơn Ngài, Đấng Sáng Suốt trong việc tạo hóa, sắp xếp và quản lý.');
INSERT INTO "vn_mokhtasar" VALUES(29,43,'29:43','Và đây là những hình ảnh thí dụ mà TA trình bày cho nhân loại biết để thức tỉnh chúng và nhìn thấy rõ về Chân Lý cũng như hướng dẫn chúng đến với Ngài, và chỉ những người có kiến thức mới thấu hiểu và biết rõ được hệ thống luật pháp của Allah đã quy định.');
INSERT INTO "vn_mokhtasar" VALUES(29,44,'29:44','Allah Toàn Năng đã tạo hóa các tầng trời và trái đất chỉ vì Chân Lý, và Ngài không tạo hóa mọi thứ vô mục đích cũng như Ngài không tạo hóa mọi thứ vô ích. Quả thật trong việc tạo hóa đó là một dấu hiệu rất rõ ràng về quyền năng của Allah cho những người có đức tin. Bởi vì họ là những người dựa vào tạo vật của Allah mà hiểu được Đấng Tạo Hóa Toàn Năng. Ngược lại, đối với những kẻ phủ nhận đức tin, cho dù có chứng kiến bao dấu hiệu của tạo hóa và trên bản thân họ thì họ vẫn không chú tâm đến sự vĩ đại và quyền năng của Đấng Tạo Hóa.');
INSERT INTO "vn_mokhtasar" VALUES(29,45,'29:45','Ngươi hãy xướng đọc - hỡi Thiên Sứ - cho nhân loại nghe những điều đã được Allah mặc khải cho Ngươi trong Kinh Qur''an và hãy chu đáo dâng lễ nguyện Salah một cách hoàn chỉnh. Quả thật lễ nguyện Salah khi được thực hiện đúng một cách toàn vẹn sẽ ngăn cản người hành lễ rơi vào điều khả ố và tội lỗi, vì nó làm tăng ánh sáng trong tim, ngăn cản sự phạm tội, và chỉ dẫn đến các việc làm ngoan đạo, việc tưởng nhớ Allah, cái là điều lớn nhất và vĩ đại nhất trong tất cả mọi thứ. Và Allah biết rất rõ những điều các ngươi làm, không việc làm nào của các ngươi có thể giấu giếm khỏi Ngài đặng, và Ngài sẽ thưởng phạt về những việc làm của các ngươi, nếu tốt thì được tốt còn nếu xấu thì được xấu.');
INSERT INTO "vn_mokhtasar" VALUES(29,46,'29:46','Và các ngươi - hỡi những người có đức tin - chớ có tranh cãi với người Do Thái và Thiên Chúa ngoại trừ bằng lời lẽ lịch thiệp và bằng cách tốt nhất, đó là mời gọi họ bằng những lời khuyên bảo và bằng chứng rõ ràng. Riêng đối với kẻ nào ngang tàng chống đối ra mặt và công khai chiến đấu với các ngươi thì các ngươi hãy đánh chúng cho đến khi nào chúng chịu đầu hàng và cúi đầu đóng thuế thân bằng chính đôi tay của chúng. Các ngươi hãy nói với người Do Thái và Thiên Chúa rằng: "Bọn ta đã có đức tin nơi những điều mà Allah đã thiên khải cho bọn ta từ Qur''an và những điều đã thiên khải cho các ngươi trong Tawrah (Cựu Ước) và trong Injil (Tân Ước). Thượng Đế của bọn ta và Thượng Đế của các ngươi vốn là một Đấng duy nhất, Ngài không có đối tác ngang vai trong thờ phượng, tạo hóa cũng như sự toàn mỹ của Ngài. Tất cả chúng ta đều là nô lệ thần phục Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(29,47,'29:47','Và giống như việc TA đã thiên khải những Kinh Sách xuống thời trước, TA cũng đã thiên khải cho Ngươi Kinh Qur''an. Thế nên, trong số những người biết đọc Tawrah - tiêu biểu như ''Abdullah bin Salam - họ đã có đức tin nơi Nó, vì khi họ đã nhận thấy sự miêu tả như trong Kinh Sách của họ; và trong số người đa thần cũng đã có đức tin nơi Nó. Và không ai phủ nhận các Lời Mặc Khải của TA ngoại trừ nhóm người vô đức tin chìm sâu trong sự vô đức tin và phủ nhận chân lý dù nó đã được phơi bày rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(29,48,'29:48','Và Ngươi - hỡi Thiên Sứ - chưa từng đọc trước Kinh Qur''an bất cứ quyển kinh sách nào, và bàn tay phải của Ngươi cũng chưa từng viết bất cứ chữ nào, bởi vì Ngươi vốn là người mù chữ không biết đọc cũng không biết viết. Nếu như Ngươi biết đọc và biết viết thì chắc chắn thiên hạ sẽ sinh lòng nghi ngờ về sứ mạng của Ngươi, họ sẽ cho rằng Ngươi ghi chép lại từ những kinh sách trước.');
INSERT INTO "vn_mokhtasar" VALUES(29,49,'29:49','Quả thật Qur''an được thiên khải cho Ngươi gồm những câu Kinh rất rõ ràng nằm trong lòng của những ai được ban cho kiến thức thuộc những người có đức tin, và không ai bác bỏ các câu Kinh đó của TA ngoại trừ những kẻ bất công với chính bản thân chúng bởi sự vô đức tin nơi Allah và tổ hợp với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(29,50,'29:50','Nhóm người đa thần nói: "Tại sao Muhammad không được Thượng Đế của Y ban cho những dấu hiệu giống như đã từng ban cho những Thiên Sứ trước đây kia chứ?!" Ngươi - hỡi Thiên Sứ - hãy nói với những kẻ đã gợi ý: "Quả thật, những dấu hiệu đó vốn nằm trong tay của Allah Toàn Năng, chỉ được ban xuống khi nào Ngài muốn, Ta không có thẩm quyền gì cả, Ta chỉ là một người cảnh báo các ngươi về hình phạt của Allah một cách rõ ràng mà thôi."');
INSERT INTO "vn_mokhtasar" VALUES(29,51,'29:51','Phải chăng vẫn chưa đủ đối với nhóm người có ý kiến về các dấu hiệu của TA khi TA thiên khải cho Ngươi - hỡi Thiên Sứ - Kinh Qur''an để Ngươi xướng đọc cho chúng nghe. Thật ra, Qur''an chính là lòng thương xót và là lời cảnh tỉnh dành cho nhóm người có đức tin, chỉ có nhóm người này mới hưởng lợi được từ Nó. Bởi thế, những điều được thiên khải cho chúng tốt hơn những điều chúng yêu cầu qua việc so sánh với những gì được thiên khải cho các Thiên Sứ trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(29,52,'29:52','Hãy nói đi - hỡi Thiên Sứ - chỉ cần Allah là đủ để làm chứng cho sự thật mà Ta đã mang đến cho các ngươi, điều mà các ngươi đã phủ nhận. Ngài tường tận mọi việc trong các tầng trời và biết rõ mọi thứ trong trái đất, không có gì trong trời đất có thể che giấu được Ngài. Những ai đã có tin nơi điều bịa đặt trong việc thờ phượng ngoài Allah và phủ nhận việc Allah là Đấng duy nhất xứng đáng được thờ phượng là nhóm người thất bại bởi vì chúng đã đánh đổi sự vô đức tin bằng đức tin Iman.');
INSERT INTO "vn_mokhtasar" VALUES(29,53,'29:53','Nhóm người đa thần hối thúc Ngươi - hỡi Thiên Sứ - mang đến hình phạt mà Ngươi đã cảnh báo chúng. Nếu như Allah đã không ấn định trước thời hạn trừng phạt chúng - không sớm cũng không muộn -thì chắc chắn hình phạt mà chúng giục đòi đã túm lấy chúng. Và chắc chắn, sự trừng phạt sẽ đến với chúng một cách đột ngột trong lúc chúng không ngờ tới.');
INSERT INTO "vn_mokhtasar" VALUES(29,54,'29:54','Chúng hối thúc Ngươi mang đến hình phạt mà Ngươi đã cảnh báo chúng. Và chắc chắn Hỏa Ngục mà Allah đã hứa với những kẻ vô đức tin sẽ vây kín chúng. Chúng sẽ không thể chạy thoát khỏi nó.');
INSERT INTO "vn_mokhtasar" VALUES(29,55,'29:55','Vào Ngày mà hình phạt sẽ phủ trùm lấy chúng từ bên trên và sẽ trải rộng bên dưới chân chúng, và Allah nói với chúng bằng lời lẽ khiển trách: Các ngươi hãy nếm lấy hậu quả mà các ngươi đã từng làm từ việc Shirk (tổ hợp thần linh cùng với Allah) và điều tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(29,56,'29:56','Hỡi đám nô lệ có đức tin nơi TA, các ngươi hãy di cư đến những vùng đất có thể thờ phượng được TA, quả thật, đất đai của TA rộng bao la, thế nên hãy thờ phượng một mình TA và chớ tổ hợp cùng với TA bất cứ ai (vật gì).');
INSERT INTO "vn_mokhtasar" VALUES(29,57,'29:57','Các ngươi đừng để cái chết khiến các ngươi sợ việc di cư, vì tất cả mỗi linh hồn đều sẽ nếm cái chết, và rồi dâyd các ngươi sẽ phải trở về trình diện một mình TA vào Ngày Phán Xét để nhận lấy sự phán xét và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(29,58,'29:58','Và những người có đức tin nơi Allah và làm những việc làm thiện tốt để được gần Allah sẽ được thu nhận vào Thiên Đàng với những tòa nhà cao bên dưới có những dòng chảy. Họ sẽ ở trong đó đời đời, một sự hưởng thụ bất tận. Thật phúc thay cho phần thưởng của việc phục tùng Allah.');
INSERT INTO "vn_mokhtasar" VALUES(29,59,'29:59','Thật hồng phúc cho phần thưởng của những người phục tùng Allah, những người mà họ đã kiên nhẫn phục tùng Ngài và không phạm tội lỗi và họ luôn phó mặc cho Thượng Đế của họ, một Thượng Đế duy nhất, trong tất cả mọi vấn đề của họ.');
INSERT INTO "vn_mokhtasar" VALUES(29,60,'29:60','Có vô số động vật đã không thể tự thu gom bổng lộc cho bản thân, trong khi Allah luôn ban phát cho chúng và cho cả các ngươi. Cho nên các ngươi không có nguyên nhân gì để bỏ việc di cư vì sợ đói cả, trong khi Ngài luôn nghe thấu lời các ngươi nói, biết tận tường tâm niệm và ý định trong lòng các ngươi cũng như mọi hành động của các ngươi, không có gì che giấu được Ngài và Ngài sẽ dựa vào đó mà thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(29,61,'29:61','Và nếu Ngươi - hỡi Thiên Sứ - hỏi đám người đa thần: Ai đã tạo hóa ra bầu trời? Ai đã tạo hóa ra trái đất? Ai đã luân chuyển mặt trời, mặt trăng nối tiếp nhau? Chắc chắn chúng sẽ đáp: "Allah đã tạo ra chúng." Thế tại sao chúng vẫn không có đức tin nơi Allah?! Sao chúng cứ mù quáng thờ phượng những thần linh không mang lại lợi ích cũng như không gây hại được chúng thay vì Allah thế kia?!');
INSERT INTO "vn_mokhtasar" VALUES(29,62,'29:62','Allah toàn quyền quyết định trong việc ban bổng lộc nhiều hay ít cho bất cứ ai Ngài muốn trong đám nô lệ của Ngài, bởi một ý nghĩa khôn ngoan nào đó mà chỉ có Ngài mới biết. Quả thật, Allah biết rất rõ mọi sự việc, không điều gì có thể giấu giếm được Ngài. Bởi thế, Ngài biết điều gì cải thiện cho đám bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(29,63,'29:63','Và nếu Ngươi - hỡi Thiên Sứ - hỏi người đa thần: Ai đã ban nước mưa từ trời xuống làm đất mọc ra cây cối và thảo mộc sau khi nó đã chết khô? Chắc chắn chúng sẽ đáp: "Allah đã cho mưa rơi từ trời xuống và làm cho cây cối mọc lên từ đất." Ngươi hãy nói - hỡi Thiên Sứ -: "Xin tạ ơn Allah đã phơi bày rõ bằng chứng cho các người thấy." Tuy nhiên, đa số bọn chúng lại không nhận thức được, bởi nếu chúng suy ngẫm và hiểu được thì chúng đã không tổ hợp cùng với Allah những pho tượng không giúp ích được gì cho chúng cũng không hãm hại được chúng điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(29,64,'29:64','Cuộc sống trần gian này - với những ham muốn và vật chất - vốn chỉ là niềm vui tạm bợ và là thứ tiêu khiển phù phiếm, sẽ sớm tan biến. Những gì ở cõi Đời Sau mới là cuộc sống thực sự trường tồn vĩnh viễn, nếu như chúng biết được chúng đang quan tâm vào những thứ tạm bợ thay vì đầu tư vào những thứ trường tồn.');
INSERT INTO "vn_mokhtasar" VALUES(29,65,'29:65','Và khi những kẻ đa thần lên tàu ra biển thì chúng hết lòng khấn vái một mình Allah mong muốn được Ngài phù hộ khỏi bị chết chìm, nhưng sau khi chúng được Allah phù hộ bình an thì chúng lại cầu xin các thần linh khác thay vì cầu xin Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(29,66,'29:66','Những kẻ đa thần đã lật lọng, chúng phủ nhận những hồng ân mà Allah đã ban cho chúng và để hưởng thụ cái chớp nhoáng của trần gian, nhưng rồi chúng sẽ biết được hậu quả xấu mà chúng đã làm ngay sau khi chúng chết.');
INSERT INTO "vn_mokhtasar" VALUES(29,67,'29:67','Phải chăng những kẻ phủ nhận hồng ân của Allah không nhìn thấy việc Ngài cứu vớt chúng khỏi nạn chết chìm ư? Và một hồng ân khác nữa là Ngài đã bảo vệ vùng đất thiêng Makkah không bị cảnh máu đổ và cướp bóc trong khi xung quanh nó không ngừng xảy ra việc cướp bóc, ức hiếp phụ nữ và trẻ con, chiếm đoạt tài sản. Hay là bọn chúng chỉ tin vào những thần linh giả dối còn những hồng ân của Allah đã ban cho chúng thì chúng lại phủ nhận nên chúng vô ơn Ngài?!');
INSERT INTO "vn_mokhtasar" VALUES(29,68,'29:68','Không còn ai sai quấy hơn kẻ tự tạo dựng nên một thần linh rồi đổ thừa cho Allah hoặc chối bỏ Chân Lý khi được Thiên Sứ của Ngài mang đến cho y và tất nhiên Hỏa Ngục sẽ là chốn cư ngụ cho đám người vô đức tin và những ai như chúng.');
INSERT INTO "vn_mokhtasar" VALUES(29,69,'29:69','Và những ai đã anh dũng xả thân Jihad nhằm tìm kiếm sự hài lòng của TA, chắc chắn sẽ được TA hướng dẫn đến với con đường ngay chính và đúng đắn, Allah luôn bên cạnh phù hộ, hướng dẫn đám người làm tốt.');
INSERT INTO "vn_mokhtasar" VALUES(30,1,'30:1','(Alif. La-m. Mi-m), ý nghĩa của các chữ cái được khai đề đầu chương đã được giải thích ở câu đầu tiên của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(30,2,'30:2','Quân Ba Tư đã đánh bại quân La Mã.');
INSERT INTO "vn_mokhtasar" VALUES(30,3,'30:3','Trong vùng đất gần nhất của Sham đến Ba Tư. Sau cuộc bại trận đó, quân La Mã sẽ chiến thắng Ba Tư trở lại.');
INSERT INTO "vn_mokhtasar" VALUES(30,4,'30:4','Chỉ trong khoảng thời gian từ ba đến mười năm, mọi sự kiện xảy ra trước và sau khi La Mã thất bại, luôn cả cuộc chiến thắng sau này đều nằm trong lệnh của Allah; và vào ngày La Mã đánh bại Ba Tư, những người có đức tin sẽ vui mừng.');
INSERT INTO "vn_mokhtasar" VALUES(30,5,'30:5','Vui mừng vì Allah đã phù hộ quân La Mã chiến thắng bởi vì họ là dân Kinh Sách, Allah muốn phù hộ ai chiến thắng ai là quyền của Ngài, Ngài là Đấng Toàn Năng, Đấng bất bại, Đầng Nhân Từ với đám nô lệ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(30,6,'30:6','Đây là cuộc chiến thắng mà Allah Toàn Năng đã hứa hẹn và khi nó xảy ra làm cho những người có đức tin gia tăng thêm đức tin vào lời hứa Ngài ban cho chiến thắng. Tuy nhiên, đa số thiên hạ vẫn không nhận thức được điều này bởi vì sự vô đức tin của họ.');
INSERT INTO "vn_mokhtasar" VALUES(30,7,'30:7','Chúng không biết gì về đức tin Iman và giáo lý. Chúng chỉ biết vẻ ngoài của cuộc sống trần gian, những thứ liên quan đến cuộc sống và xây dựng cái xanh tươi của vật chất mà thôi, trong khi cuộc sống Đời Sau là đời sống đích thực nhưng chúng lại ngoảnh mặt bất cần.');
INSERT INTO "vn_mokhtasar" VALUES(30,8,'30:8','Sao đám người đa thần phủ nhận đã không chịu vận dụng tâm trí để suy nghĩ về công trình mà Allah đã tạo. Allah đã không tạo ra các tầng trời và trái đất ngoại trừ vì Chân Lý, Ngài không hề tạo chúng ra một cách vô nghĩa, Ngài qui định một tuổi thọ nhất định cho sự tồn tại của trời đất trong cõi trần gian. Tiếc thay có rất nhiều người trong thiên hạ đã phủ nhận cuộc gặp gỡ với Thượng Đế của họ vào Ngày Phán Xét. Chính vì vậy, họ đã không chuẩn bị cho cuộc gặp gỡ đó bằng việc làm đức hạnh được Allah hài lòng.');
INSERT INTO "vn_mokhtasar" VALUES(30,9,'30:9','Sao đám người này không chịu đi khắp nơi trên trái đất để suy nghĩ về hậu quả của những cộng đồng đã phủ nhận trước chúng, những cộng đồng đó hơn chúng về sức mạnh, hơn về khả năng lạo động bền bỉ trong việc khai đất trồng trọt, hơn về tuổi thọ. Và các Thiên Sứ của Allah cũng đã đến gặp đám người đó với các bằng chứng rõ ràng về tính độc nhất của Allah, vậy mà chúng vẫn phủ nhận. Allah quả thật đã không bất công với chúng khi tiêu diệt chúng mà chính chúng tự bất công với bản thân mình mà thôi khi chúng tự đẩy mình rơi vào sự hủy diệt bằng sự vô đức tin của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(30,10,'30:10','Sau đó, đám người làm điều Shirk (tổ hợp thần linh cùng với Allah) và những điều tội lỗi khác phải đối diện với hậu quả cực kỳ tệ hại, bởi vì chúng đã phủ nhận các Lời Mặc Khải của Allah và đã từng mang các Lời Mặc Khải đó ra chế giễu và nhạo báng.');
INSERT INTO "vn_mokhtasar" VALUES(30,11,'30:11','Allah là Đấng khai sáng vạn vật khi chúng không là gì, rồi Ngài sẽ phục sinh chúng lại và sau đó tất cả phải trở lại trình diện Ngài trong Ngày Phán Xét để chịu sự thanh toán và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(30,12,'30:12','Vào thời khắc của Ngày Tận Thế, đám người tội lỗi mất hết hi vọng được Allah cứu rỗi, mọi ước mơ đều chấm dứt và chúng không còn chứng cớ để biện minh cho tội vô đức tin nơi Allah nữa.');
INSERT INTO "vn_mokhtasar" VALUES(30,13,'30:13','Và chúng không được các thần linh - mà chúng từng thờ phượng trên trần gian - đứng ra biện hộ giúp chúng thoát khỏi Hỏa Ngục, ngược lại, các thần linh đó sẽ phủ nhận sự thờ phượng của chúng. Đúng là sự nhục nhã khi hướng đến các thần linh đó để khấn vái, bởi vì tất cả chúng đều bị tiêu diệt như nhau.');
INSERT INTO "vn_mokhtasar" VALUES(30,14,'30:14','Và vào Ngày mà Giờ Tận Thế được thiết lập, đó là Ngày mà thiên hạ sẽ được phân loại dựa theo việc làm mà họ đã từng làm trên thế gian, có người sẽ được nâng lên trên cao và có người sẽ bị hạ xuống tận đáy.');
INSERT INTO "vn_mokhtasar" VALUES(30,15,'30:15','Đối với những ai có đức tin nơi Allah và làm việc ngoan đạo và thiện tốt để làm hài lòng Ngài, họ sẽ được ở trong Thiên Đàng mà hưởng lạc với biết bao niềm hạnh phúc bất tận không bao giờ dứt.');
INSERT INTO "vn_mokhtasar" VALUES(30,16,'30:16','Và đối với những kẻ vô đức tin nơi Allah và phủ nhận các Lời Mặc Khải mà Ngài đã thiên khải cho Thiên Sứ của Ngài, đồng thời bác bỏ việc phục sinh, việc thanh toán thì đó là đám người sẽ mãi mãi ở trong sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(30,17,'30:17','Thế nên, các ngươi hãy tụng niệm Allah khi trời bắt đầu chập tối, đó là hai giờ Salah Maghrib và ''I-sha''; và tụng niệm Ngài khi bình minh ló dạng, tức giờ Salah Fajr.');
INSERT INTO "vn_mokhtasar" VALUES(30,18,'30:18','Ngài là Đấng duy nhất đáng được ca tụng, trên trời thì có các Thiên Thần tụng niệm Ngài, dưới đất thì được tất cả vạn vật tán dương Ngài, tất cả đều tán dương Ngài khi trời bắt đầu chiều lúc giờ Salah ''Asr, và lúc giữa trưa trong giờ Salah Zhuhr.');
INSERT INTO "vn_mokhtasar" VALUES(30,19,'30:19','Ngài kéo sự sống ra từ cõi chết giống như Ngài đã kéo con người ra từ tinh dịch và gà con ra từ cái trứng; Ngài kéo cái chết ra từ sự sống giống như Ngài lấy tinh dịch ra khỏi con người và cái trứng ra khỏi con gà; Ngài làm sống lại mảnh đất chết khô bằng cơn mưa làm mọc lên đồng cỏ. Và hình ảnh phục sinh đất chết khô đó giống như việc con người sẽ bị phục sinh sống lại từ cõi mộ để chịu sự thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(30,20,'30:20','Và trong những dấu hiệu vĩ đại của Allah chứng minh quyền năng vô biên của Ngài và tính duy nhất của Ngài là Ngài đã tạo các ngươi - hỡi con người - từ đất khi Ngài đã tạo ra tổ tiên của các ngươi bằng đất sét dẻo, rồi từ đó các ngươi trở thành những con người phàm được nhân rộng ra bằng sinh sản để sống khắp mọi nơi trên trái đất từ đông sang tây.');
INSERT INTO "vn_mokhtasar" VALUES(30,21,'30:21','Và trong những dấu hiệu vĩ đại chứng minh quyền năng của Allah và tính duy nhất của Ngài là Ngài đã vì các ngươi - hỡi những người đàn ông - mà tạo ra những người vợ từ chính cơ thể của các ngươi để các ngươi an tâm mà sống đùm bọc lẫn nhau, thương yêu nhau, nhường nhịn nhau. Chắc chắn với những gì đã được đề cập là bằng chứng thiết thực nhất dành cho đám người biết suy nghĩ, bởi chỉ có suy nghĩ mới giúp ích được đám người có khối óc.');
INSERT INTO "vn_mokhtasar" VALUES(30,22,'30:22','Và trong những dấu hiệu vĩ đại chứng minh quyền năng của Allah và tính duy nhất của Ngài là Ngài đã tạo ra các tầng trời, trái đất, sự khác biệt về ngôn ngữ, khác biệt về màu da. Chắc chắn những gì được đề cập là bằng chứng thiết thực nhất dành cho đám người biết nhận thức.');
INSERT INTO "vn_mokhtasar" VALUES(30,23,'30:23','Và trong những dấu hiệu vĩ đại chứng minh quyền năng của Allah và tính duy nhất của Ngài là việc các ngươi ngủ vào ban đêm và nghỉ ngơi vào ban ngày sau giờ lao động mệt mỏi, và trong những dấu hiệu khác của Ngài là Ngài dùng ban ngày để các ngươi đi khắp mọi nơi tìm kiếm bổng lộc của Thượng Đế các ngươi. Quả thật trong sự việc được đề cập là bằng chứng thiết thực nhất dành cho đám người biết lắng nghe và biết đón nhận.');
INSERT INTO "vn_mokhtasar" VALUES(30,24,'30:24','Và trong những dấu hiệu vĩ đại chứng minh quyền năng của Allah và tính duy nhất của Ngài là Ngài cho các ngươi nhìn thấy hiện tượng sấm chớp trên trời, nó khiến các ngươi vừa sợ sấm sét và vừa hi vọng trời sẽ đổ mưa; và Ngài đã trút xuống cho các ngươi từ trời xanh những cơn mưa để Ngài làm sống lại vùng đất đã chết khô làm cho mọc lên đồng cỏ. Quả thật trong sự việc được đề cập là bằng chứng thiết thực nhất dành cho đám người biết nhận thức. Đây là bằng chứng của việc phục sinh sau khi chết để tính sổ và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(30,25,'30:25','Và trong những dấu hiệu vĩ đại chứng minh quyền năng của Allah và tính duy nhất của Ngài là Ngài dựng đứng bầu trời không nghiêng ngã và giữ vững trái đất không lắc lư bằng mệnh lệnh của Ngài, sau đó Đấng Toàn Năng gọi các ngươi bằng tiếng còi của Thiên Thần thì lập tức các ngươi đứng dậy rời khỏi mồ của các ngươi để trình diện Ngài cho việc tính sổ và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(30,26,'30:26','Và Ngài duy nhất sở hữu và quản lý các tầng trời và vạn vật ở dưới đất, mọi thứ trên trời, mọi thứ dưới đất đều là tạo vật của Ngài, tất cả đều phục tùng theo mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(30,27,'30:27','Và Ngài là Đấng đã khởi tạo vạn vật khi chúng không là gì, sau đó Ngài sẽ phục sinh nó lại sau khi tiêu vong, trong khi việc phục sinh đơn giản hơn việc khởi tạo và cả hai đối với Allah đều đơn giản bởi khi Ngài muốn bất cứ thứ gì thì Ngài chỉ cần phán: {كن} "hãy thành" lập tức nó sẽ thành; Ngài vượt trên hết mọi thứ đã miêu tả về Ngài và Ngài là Đấng Quyền Năng chiến thắng tất cả và là Đấng Sáng Suốt trong chi phối và quản lý.');
INSERT INTO "vn_mokhtasar" VALUES(30,28,'30:28','Allah đã lấy bản thân các ngươi - hỡi những kẻ đa thần - làm hình ảnh thí dụ: Há không phải việc các ngươi cùng là những đối tác đồng hưởng chung một số nô lệ và tài sản, nó khiến các ngươi sợ những nô lệ chia số tài sản đó ra giống như việc các ngươi sợ đối tác tự tung tự tác chia tài sản đó hay sao? Các ngươi có hài lòng cho nô lệ của mình làm như thế không? Tất nhiên là các ngươi không bằng lòng như thế. Nếu như vậy thì Allah phải là Đấng ưu tiên không có đối tác chia sẽ quyền lực chi phối trước vạn vật cũng như đám nô lệ của Ngài. Với hình ảnh thí dụ đó là sự giải trình rõ ràng, là bằng chứng thiết thực dành cho đám người có khối óc, bởi điều đó mới giúp ích được cho họ mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(30,29,'30:29','Không phải nguyên nhân bị lầm lạc là do thiếu bằng chứng và thiếu sự giải trình mà là do chúng đi theo dục vọng của bản thân và mù quáng theo truyền thống ông bà. Thế ai sẽ hướng dẫn đến chính đạo khi Allah khiến họ phải lầm lạc?! Sẽ không có ai hướng dẫn họ và càng không có ai giúp đỡ họ thoát khỏi hình phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(30,30,'30:30','Thế nên Ngươi - hỡi Thiên Sứ - cùng những những tín đồ theo Ngươi, hãy hướng mặt về tôn giáo mà Allah đã định hướng cho Ngươi, hãy lánh xa tất cả tôn giáo khác, Islam là tôn giáo mà Allah đã chọn cho loài người, sẽ không có sự thay đổi nào khác cho tạo hóa của Allah. Đấy là tôn giáo ngay chính, không có sự quanh co và uốn khúc trong nó, tuy nhiên, đa số thiên hạ lại không nhận biết được tôn giáo chân lý này.');
INSERT INTO "vn_mokhtasar" VALUES(30,31,'30:31','Các ngươi hãy quay về sám hối với Allah vì tất cả tội lỗi đã phạm, hãy biết kính sợ Ngài mà tuân thủ theo mệnh lệnh của Ngài, hãy chu đáo duy trì lễ nguyện Salah một cách hoàn chỉnh nhất và chớ trở thành đám người đa thần thờ phượng những thần linh khác cùng với Allah, điều đó xóa mất đi tôn giáo tự nhiên.');
INSERT INTO "vn_mokhtasar" VALUES(30,32,'30:32','Nhóm người đa thần đã thay đổi tôn giáo của chúng đã không còn tin tưởng lẫn nhau nữa và chúng đã chia thành nhiều nhánh phái, hệ phái khác nhau, mỗi nhánh phái rất hớn hở với thứ hư cấu mà mình có được, chúng nhận thấy chúng đang ở trên chân lý còn những ai ngoài chúng đang sai lầm.');
INSERT INTO "vn_mokhtasar" VALUES(30,33,'30:33','Và khi người đa thần gặp phải nạn kiếp như bệnh tật hoặc bần hàn hoặc hạn hán thì chúng thành khẩn khấn vái Thượng Đế của chúng, chúng chỉ hướng đến một mình Ngài duy nhất, chúng chỉ trông chờ và hi vọng nơi một mình Ngài, vậy mà khi Ngài xua tan tai ương khỏi chúng và cho chúng hưởng được lòng khoan dung của Ngài thì một nhóm trong bọn chúng quay lại khẩn cầu các thần linh khác cùng với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(30,34,'30:34','Khi chúng phủ nhận những hồng ân của Allah - trong đó có hồng ân phù hộ tai qua nạn khỏi - và chúng hưởng thụ những gì chúng có được trong cuộc sống trần gian này. Rồi vào Ngày Tận Thế chúng sẽ tận mắt nhìn thấy mình trước đây đã lầm lạc thật sự.');
INSERT INTO "vn_mokhtasar" VALUES(30,35,'30:35','Điều gì đã kêu gọi chúng đến với việc tổ hợp cùng với Allah trong khi chúng không có bằng chứng nào cho điều đó cả?! TA (Allah) cũng đã không ban xuống cho chúng bất cứ thẩm quyền nào để chúng dựa vào đó mà tổ hợp với Allah, chúng cũng không có bất kỳ Kinh Sách bảo chúng tổ hợp các thần linh cùng với Allah và khẳng định sự vô đức tin của chúng là đúng cả.');
INSERT INTO "vn_mokhtasar" VALUES(30,36,'30:36','Và khi TA cho con người nếm một trong các hồng ân của TA như sức khỏe, sự hưng thịnh thì họ tỏ ra vui mừng tột độ và ngang tàng; còn khi họ gặp phải điều tai ương như bệnh hoạn, nghèo khổ do chính hậu quả của bàn tay tội lỗi của họ đã gây ra thì họ lại đâm ra tuyệt vọng nơi lòng thương xót của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(30,37,'30:37','Hay chúng không nhìn thấy việc Allah muốn ban dồi dào bổng lộc cho ai Ngài muốn trong đám nô lệ của Ngài là để xem y tạ ơn hay vong ơn Ngài? Và việc Ngài thu hẹp bổng lộc với người nào Ngài muốn là để thử thách xem y kiên nhẫn hay phẫn nộ và oán trách? Quả thật, qua việc ban bổng lộc dồi dào cho một số và thu hẹp bổng lộc đối với một số khác là bằng chứng khẳng định cho đám người có đức tin về sự tinh tế và lòng thương xót của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(30,38,'30:38','Hãy trao - hỡi tín đồ Muslim - cho bà con dòng họ quyền lợi của họ để kết chặt tình huyết thống, hãy trao cho người khó khăn cần sự giúp đỡ quyền lợi của họ, hãy trao cho người lỡ đường quyền lợi của họ, đó là những cách giúp đỡ mang lại điều tốt dành cho những ai muốn làm hài lòng Allah, những ai xuất phần trợ giúp đó và trao trả đúng quyền lợi là những người thành công đạt được những gì họ muốn ở Thiên Đàng và được bình an thoát khỏi hình phạt.');
INSERT INTO "vn_mokhtasar" VALUES(30,39,'30:39','Và việc các ngươi đưa tiền cho người khác để thu lại khoản tiền lời nhiều hơn số tiền mà các ngươi đã giao ban đầu, đối với Allah đó là khoản tiền lời bất chính không được ân phước. Và khi các ngươi dùng tiền để giúp đỡ người khó khăn bằng tấm lòng vì muốn được Allah hài lòng, chứ không muốn được bất cứ thứ gì từ thiên hạ, đó là đám người sẽ được Allah nhân lên nhiều lần cho ân phước mà Ngài dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(30,40,'30:40','Allah là Đấng duy nhất đã khởi tạo ra các ngươi, rồi Ngài chu cấp cho các ngươi bổng lộc, rồi Ngài khiến các ngươi phải chết và sau đó Ngài phục sinh các ngươi lại lần nữa. Thế đó, tất cả bục tượng mà các ngươi thờ phượng ngoài Ngài có ai có quyền năng làm bất cứ điều gì vừa được liệt kê không? Ngài trong sạch và vô can với tất cả mọi thứ mà những kẻ đa thần đã gán ghép cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(30,41,'30:41','Những thiệt hại, tổn thất đang xảy ra trên đất liền và ngoài đại dương khiến cuộc sống con người dần bị thu hẹp, khiến sức khỏe con người đang bị đe dọa bởi những căn bệnh mới phát hiện có tín nguy hiểm cao mà nguyên nhân là do hậu quả của những công trình tội lỗi của con người tạo ra. Với những gì đang diễn ra Allah muốn cho con người nếm một ít thành quả xấu do chính công trình mà họ đã làm ngay trong cuộc sống trần gian này với hi vọng họ sớm thức tỉnh mà ăn năn sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(30,42,'30:42','Hãy nói đi - hỡi Thiên Sứ - với đám người đa thần: Các ngươi hãy đi khắp mọi miền của trái đất, để nghiên cứu xem kết quả của những cộng đồng đã phủ nhận từng sống trước đây? Quả thật, chúng đã có kết quả rất thảm hại, đa số bọn chúng là những kẻ tôn thờ các thần linh ngoài Allah, và chúng đã bị Allah tiêu diệt tất cả vì lý do đó.');
INSERT INTO "vn_mokhtasar" VALUES(30,43,'30:43','Cho nên Ngươi - hỡi Thiên Sứ - hãy hướng mặt mình về tôn giáo Islam ngay chính, tôn giáo không có sự quanh co và uốn khúc, trước khi sự kiện Ngày Tận Thế được thiết lập, Ngày mà không ai có thể tránh khỏi. Trong Ngày hôm đó thiên hạ sẽ bị chia thành hai nhóm: Nhóm của Thiên Đàng được hưởng lạc và nhóm của Hỏa Ngục bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(30,44,'30:44','Ai vô đức tin nơi Allah sẽ gặp phải hậu quả - muôn đời trong Hỏa Ngục - mà y phải chịu, còn ai năng nổ làm việc ngoan đạo bằng định tâm làm hài lòng Allah thì bản thân y sẽ được thu nhận vào Thiên Đàng và được hưởng lạc muôn đời.');
INSERT INTO "vn_mokhtasar" VALUES(30,45,'30:45','Để ban thưởng cho những ai có đức tin nơi Allah và năng làm việc ngoan đạo bằng tâm niệm làm hài lòng Thượng Đế của họ. Quả thật, Đấng Vinh Quang không yêu thương đám người vô đức tin nơi Ngài và Thiên Sứ của Ngài, rồi chúng sẽ đối diện với hậu quả không thể xấu hơn và sẽ bị trừng phạt vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(30,46,'30:46','Và trong những dấu hiệu vĩ đại của Allah chứng minh quyền năng vô biên của Ngài và tính duy nhất của Ngài là Ngài gởi đi trước những cơn gió mang tin mừng cho đám nô lệ về cơn mưa sắp trút xuống, để cho các ngươi - hỡi con người - nếm được lòng thương xót của Ngài qua cơn mưa tốt lành đó, để những con tàu buồm ra khơi êm ả theo mệnh lệnh của Ngài và để các ngươi dễ dàng tìm kiếm bổng lộc của Ngài bằng những chuyến buôn trên đại dương, với hi vọng các ngươi biết tạ ơn Ngài về những thiên ân mà Allah đã ban cho, mục đích để Ngài lấy đó làm lý do ban thêm cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(30,47,'30:47','Và Trước Ngươi - hỡi Thiên Sứ - TA (Allah) đã gửi những vị Thiên Sứ đến với dân chúng của Họ, Họ đã trình bày cho dân chúng của mình những bằng chứng rõ ràng khẳng định sứ mạng của Họ là thật, vậy mà dân chúng vẫn phủ nhận sứ mạng của Họ, thế là dân chúng đã bị TA trừng phạt khi họ vi phạm điều xấu xa, và TA đã cứu vớt các Thiên Sứ và những người có đức tin thoát khỏi hình phạt đó; và việc giúp những người có đức tin giành lấy chiến thắng là nhiệm vụ TA phải làm.');
INSERT INTO "vn_mokhtasar" VALUES(30,48,'30:48','Allah Toàn Năng đã di chuyển những đám mây, Ngài kéo và thả chúng như thế nào, ít nhiều, tùy ý Ngài và khi chúng bị gom lại thành một khối thì các ngươi nhìn thấy - hỡi con người - những hạt mưa rơi xuống giữa khối mây đó. Khi những hạt mưa đó rơi xuống cho những ai trong đám nô lệ của Ngài thì họ liền mừng cuống quýt vì lòng thương xót của Allah dành cho họ; từ đó, Ngài cho mọc lên từ đất vô số cây cỏ để phục vụ cho các ngươi và động vật của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(30,49,'30:49','Và trước khi bầu trời đổ mưa, chúng đã tuyệt vọng rằng Allah đã không còn cho mưa xuống cho chúng nữa.');
INSERT INTO "vn_mokhtasar" VALUES(30,50,'30:50','Ngươi hãy nhìn xem - hỡi Thiên Sứ - vào những vết tích của mưa mà Allah đã ban xuống, đó sự là thể hiện lòng thương xót dành cho đám nô lệ của Ngài, Ngài lấy nó để phục sinh vùng đất đã chết khô, làm mọc lên vô số loài cây cỏ. Tương tự như hình ảnh phục sinh mảnh đất chết khô là việc phục sinh con người sau khi chết, và Ngài toàn năng trên tất cả mọi thứ, không có bất cứ thứ gì làm Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(30,51,'30:51','Và khi TA gởi đến vườn hoa màu của chúng cơn gió tàn phá làm cho nó héo hon, vàng úa sau khi đã rất xanh tươi thì bọn chúng phủ nhận hết tất cả hồng ân mà Allah đã ban cho chúng rất nhiều trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(30,52,'30:52','Chắc chắn Ngươi không thể khiến người chết và người điếc có thể nghe được trong khi chúng đã cố tình xa lánh Ngươi để không nghe những gì Ngươi nói. Tương tự như thế, Ngươi sẽ không thể hướng dẫn đám người cố tình muốn quay lưng với lời chỉ đạo của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(30,53,'30:53','Và Ngươi không phải là người phù hộ cho những kẻ lầm lạc đi đến con đường chính đạo, và lời chỉ đạo đó chỉ có hữu ích cho đám người đã tin tưởng vào các Lời mặc khải của TA mà thôi, vì lời nói của Ngươi giúp ích được cho họ, bởi lẽ họ biết phục tùng mệnh lệnh của TA và thần phục TA.');
INSERT INTO "vn_mokhtasar" VALUES(30,54,'30:54','Allah là Đấng đã tạo hóa ra các ngươi - hỡi con người - từ chất dịch đáng khinh, rồi Ngài ban sức mạnh đàn ông cho các ngươi sau khi các ngươi là những đứa trẻ yếu ớt, rồi sau sức mạnh đàn ông đó Ngài khiến các ngươi trở lại yếu ớt và già nua. Allah muốn tạo hóa mạnh và yếu như thế nào là tùy ý Ngài. Ngài biết rõ tất cả, không có gì che giấu được Ngài; Ngài là Đấng Toàn Năng, không có gì cản trở được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(30,55,'30:55','Và trong Ngày Tận Thế, đám người tội lỗi thề thốt là họ chỉ ở trong cõi mộ một thời khắc ngắn ngủi mà thôi. Tương tự việc họ không nhận biết được thời gian đã ở trong cõi mộ bao lâu giống như việc họ đã quay lưng với chân lý vậy.');
INSERT INTO "vn_mokhtasar" VALUES(30,56,'30:56','Và những ai được Allah ban cho kiến thức từ giới Nabi và Thiên Thần nói: Các ngươi đã ở đúng như Allah đã định sẵn cho các ngươi trong Kinh Sách của Ngài lúc Ngài bắt đầu tạo ra các ngươi cho đến Ngày Phục Sinh nhưng các ngươi đã bác bỏ điều đó, tuy nhiên các ngươi cứ tưởng không có Ngày Phục Sinh nên các ngươi đã phủ nhận nó.');
INSERT INTO "vn_mokhtasar" VALUES(30,57,'30:57','Vào Ngày mà Allah phục sinh vạn vật để thanh toán và thưởng phạt thì mọi lý do của đám người tội lỗi không giúp ích được họ, họ cũng mất cơ hội làm hài lòng Allah cũng như mất cơ hội quay lại sám hối với Ngài, vì cơ hội đã không còn.');
INSERT INTO "vn_mokhtasar" VALUES(30,58,'30:58','Và TA (Allah) đã dẫn chứng cho con người biết bao hình ảnh thí dụ trong Qur''an này, để trình bày cho họ rõ đâu là chân lý và đâu là ngụy tạo. Thế nhưng cho dù Ngươi - hỡi Thiên Sứ - có trình bày thêm cho họ bất cứ bằng chứng nào khẳng định sứ mạng của Ngươi là thật thì đám người vô đức tin vào Allah vẫn bảo: Các ngươi chỉ toàn là những kẻ bịa đặt điều các ngươi mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(30,59,'30:59','Allah niêm kín những con tim của đám người không biết rằng điều Ngươi đã mang đến cho chúng là chân lý giống như Hình ảnh việc niêm kín những con tim của đám người mà Ngươi đã trình bày cho họ dấu hiệu nhưng họ vẫn không tin.');
INSERT INTO "vn_mokhtasar" VALUES(30,60,'30:60','Ngươi hãy kiên nhẫn - hỡi Thiên Sứ - trước sự phủ nhận của dân chúng của Ngươi về sứ mạng của Ngươi. Quả thật, lời hứa ban chiến thắng và thành công cho Ngươi là điều chắc chắn, Ngươi đừng để đám người không có đức tin vào Ngày Phục Sinh làm Ngươi mất đi lòng kiên nhẫn.');
INSERT INTO "vn_mokhtasar" VALUES(31,1,'31:1','(Alif. La-m. Mi-m) Đã được giải thích ở câu đầu tiên của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(31,2,'31:2','Những câu Kinh được ban xuống cho Ngươi này đây - hỡi Thiên Sứ - là những câu Kinh của một quyển Kinh nói ra những lời thông suốt.');
INSERT INTO "vn_mokhtasar" VALUES(31,3,'31:3','Nó là sự hướng dẫn và là lòng thương xót dành cho nhóm người làm tốt qua việc chấp hành các bổn phận đối với Thượng Đế của họ và các nghĩa vụ đối với các bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(31,4,'31:4','Họ là nhóm người chu đáo hoàn tất bổn phận Salah, xuất Zakah từ tài sản của họ và họ có đức tin kiên định vào những gì trong cuộc sống Đời Sau từ sự phục sinh, thanh toán và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(31,5,'31:5','Nhóm người có thuộc tính như miêu tả đó là những người đi đúng với hướng dẫn của Thượng Đế và họ là nhóm người thành công đạt được mọi điều họ muốn và được bảo vệ tránh xa mọi điều họ lo lắng.');
INSERT INTO "vn_mokhtasar" VALUES(31,6,'31:6','Và trong thiên hạ có kẻ - ám chỉ Al-Nadhr bin Al-Harith - đã đưa ra những lời lẽ hoa mỹ làm thứ tiêu khiển (như âm nhạc) để lôi kéo mọi người thiếu hiểu biết rời xa tôn giáo của Allah. Và hắn còn lấy các câu Kinh của Allah ra để nhạo báng, bình luận. Đối với đám người có thuộc tính vừa miêu tả sẽ phải gánh hậu quả của cực hình đầy nhục nhã ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(31,7,'31:7','Và khi những câu Kinh của TA được xướng đọc cho hắn nghe thì hắn liền quay lưng bỏ đi một cách đầy kiêu ngạo giống như hắn không nghe thấy bất cứ điều gì, như thể đôi tai của hắn bị điếc không nghe được âm thanh. Ngươi - hỡi Thiên Sứ - hãy báo cho hắn về sự trừng phạt đau đơn đang chờ đợi hắn.');
INSERT INTO "vn_mokhtasar" VALUES(31,8,'31:8','Quả thật những ai có đức tin nơi Allah và năng hành đạo, họ sẽ được hưởng Thiên Đàng hạnh phúc, họ được hưởng lạc với tất cả mọi thứ mà Allah đã chuẩn bị cho họ trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(31,9,'31:9','Họ sẽ sống mãi mãi ở trong đó, lời mà Allah đã hứa với họ là lời chắc chắn không có gì phải nghi ngờ. Ngài là Đấng Toàn Năng không ai cưỡng lại và là Đấng Sáng Suốt trong tạo hóa, quản lý và định đoạt.');
INSERT INTO "vn_mokhtasar" VALUES(31,10,'31:10','Allah Toàn Năng đã khởi tạo các tầng trời và nâng chúng lên cao không cần đến cột để chống đỡ, Ngài trải rộng mặt đất và cắm lên nó những quả núi cố định không cho đất rung chuyển với các ngươi, Ngài rải khắp nơi trên mặt đất vô số loài động vật, Ngài trút mưa từ trời xuống để Ngài cho đất mọc lên vô số các loại cây cối và thảo mộc dùng làm nguồn lợi cho con người và động vật.');
INSERT INTO "vn_mokhtasar" VALUES(31,11,'31:11','Với mọi thứ đã liệt kê đều là tạo vật của Allah. Vì vậy, các ngươi - hỡi những kẻ đa thần - hãy cho TA xem đâu là tạo vật mà những thần linh mà các ngươi đã thờ phượng ngoài Allah? Không, đám người sai quấy đã đi lạc rất xa khỏi chân lý khi mà chúng tổ hợp với Thượng Đế những thần linh không tạo ra được bất cứ thứ gì và chính đám thần linh đó vốn là những thứ được tạo ra.');
INSERT INTO "vn_mokhtasar" VALUES(31,12,'31:12','Và TA đã ban cho Luqman sự thông suốt về tôn giáo và hành động đứng đắn trong các sự việc, TA bảo y: Ngươi - hỡi Luqman - hãy tạ ơn Thượng Đế của ngươi bởi những ân huệ mà Ngài đã ban cho ngươi như việc Ngài đã phù hộ ngươi biết phục tùng Ngài. Tuy nhiên, ai tạ ơn Thượng Đế của y thì việc tạ ơn đó chỉ mang lại lợi ích cho chính bản thân y, bởi Allah rất giàu có không cần đến lời tạ ơn đó; còn ai vong ơn với bao hồng ân mà Ngài đã ban thì sự vong ơn đó không gây hại đến Allah mà chỉ gây hại chính bản thân y mà thôi. Quả thật, Allah là Đấng bất cần đến các tạo vật của Ngài, Ngài là Đấng đáng được ca tụng trong mọi hoàn cảnh.');
INSERT INTO "vn_mokhtasar" VALUES(31,13,'31:13','Và hãy nhớ lại - hỡi Thiên Sứ - khi Luqman dạy con trai của y lẽ phải tốt đẹp và khuyến cáo con về điều xấu, y nói: Này con trai yêu của cha, con chớ thờ phượng bất cứ ai (vật gì) cùng với Allah, quả thật việc thờ phượng thần linh khác cùng với Allah là sự bất công nghiêm trọng đối với bản thân. Đó là một đại tôi đẩy bản thân bị đày muôn đời trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(31,14,'31:14','Và TA đã truyền lệnh cho con người phải vâng lời và hiếu thảo với cha mẹ trong khuôn khổ không nghịch lại Allah. Mẹ của y đã mang nặng y trong bụng hết khó khăn này đến khó khăn khác, mẹ của y cho y bú sữa và dứt sữa y trong hai năm, TA bảo y: Ngươi hãy tạ ơn Allah với bao hồng ân mà Ngài đã ban cho ngươi, sau đó hãy biết ơn cha mẹ của Người vì đã cưu mang, chăm sóc và dạy dỗ ngươi và ngươi sẽ trở về gặp TA để được ban thưởng phần xứng đáng.');
INSERT INTO "vn_mokhtasar" VALUES(31,15,'31:15','Và nếu cha mẹ của ngươi cố tình ép buộc ngươi phải tổ hợp thần linh khác cùng TA thì ngươi chớ có nghe lời hai người họ, bởi vì không có chuyện nghe lời tạo vật để nghịch lại Đấng Tạo Hóa, tuy nhiên, ngươi hãy ăn ở đàng hoàng với cha mẹ trong cuộc sống trần gian này như hiếu thảo, vâng lời và chăm sóc, ngươi hãy bám sát con đường của những ai quay về với TA bằng lòng Tawhid và phục tùng. Sau đó, tất cả các ngươi phải trở về trình diện TA trong Ngày Tận Thế, lúc đó TA sẽ báo cho các ngươi biết rõ mọi việc mà các ngươi đã từng làm trên trần gian và ban thưởng cho các ngươi xứng đáng.');
INSERT INTO "vn_mokhtasar" VALUES(31,16,'31:16','Này con trai yêu của cha! Dù điều tốt hay điều xấu có nhỏ như thế nào, chẳng hạn nó chỉ nhỏ bằng hạt nguyên tử và nó nằm trong tảng đá không ai phát hiện ra hoặc nó nằm ở bất cứ đâu trong các tầng trời hoặc trong lòng đất thì Allah cũng sẽ mang nó ra trong Ngày Tận Thế để thanh toán, quả thật, Allah là Đấng Tinh Thông, không có gì qua mặt được Ngài, Ngài am tường mọi việc dù nó ở tận đâu.');
INSERT INTO "vn_mokhtasar" VALUES(31,17,'31:17','Hỡi con trai yêu của cha! Con hãy chu đáo duy trì lễ nguyện Salah một cách hoàn thiện nhất, con hãy không ngừng bảo ban kêu gọi mọi người làm việc thiện và ngăn cản mọi người làm điều tội lỗi, con hãy kiên nhẫn trước những điều bất trắc. Quả thật, với những gì cha căn dặn con chính là những điều mà Allah muốn con hoàn thành nó, con không có quyền lựa chọn nào khác.');
INSERT INTO "vn_mokhtasar" VALUES(31,18,'31:18','Và con chớ hất mặt tự cao với thiên hạ, con cũng đừng bước đi với dáng điệu kiêu căng một cách tự đắc. Quả thật, Allah không yêu thương những kẻ kênh kiệu và nghênh ngang trong đi đứng, khoác lác với những gì mình có được để tự hào trước thiên hạ và không biết tạ ơn Allah.');
INSERT INTO "vn_mokhtasar" VALUES(31,19,'31:19','Con hãy đi đứng với điệu bộ khiêm tốn và ôn tồn; hãy hạ giọng khi nói chuyện, con chớ cao giọng khi nói chuyện bởi vì âm thanh đáng ghét nhất chính là tiếng hí của con lừa.');
INSERT INTO "vn_mokhtasar" VALUES(31,20,'31:20','Sao các ngươi - hỡi con người - không chịu nhìn và suy ngẫm việc Allah đã thâu tóm bầu trời gồm cả mặt trời, mặt trăng và vô số tinh tú khác để phục vụ cho con người, Ngài còn thâu tóm cả trái đất gồm có động vật, thực vật và vô số thứ khác để cho con người sử dụng, ngoài ra Ngài còn ban thêm những hồng ân cả hình dáng đẹp đẽ bên ngoài và những thứ ẩn bên trong như kiến thức và trí tuệ. Mặc dù với bao hồng ân mà Allah ban cho như thế nhưng trong thiên hạ vẫn có bao người đã tranh luận về sự độc tốn hóa Allah trong khi y không có cơ sở từ lời thiên khải hay sự nhận thức đúng đắn và cũng không dựa vào Kinh Sách mà Allah đã thiên khải.');
INSERT INTO "vn_mokhtasar" VALUES(31,21,'31:21','Và đám người tranh luận về việc độc tôn hóa Allah này đây, khi được bảo "Các ngươi hãy nghe theo những gì mà Allah đã thiên khải cho Thiên Sứ của Ngài" thì chúng đáp "Chúng tôi không theo, chúng tôi chỉ làm theo những gì chúng tôi thấy tổ tiên của mình đã thờ phượng những thần linh của họ thôi". Vậy là chúng vẫn mù quáng đi theo tổ tiên của chúng kể cả khi Shaytan kêu gọi tổ tiên của chúng - thờ phượng các thần linh bục tượng một cách lầm lạc - đến với hình phạt của Hỏa Ngục vào Ngày Tận Thế ư?!');
INSERT INTO "vn_mokhtasar" VALUES(31,22,'31:22','Và ai hướng về Allah bằng tấm lòng thành tâm trong hành đạo và hoàn thành tốt về mặt hành đạo thì y đã nắm chắc sợi dây cứu rỗi không sợ bị đứt. Và tất cả mọi việc đều được về một mình Allah duy để Ngài định đoạt và ban thưởng thứ xứng đáng.');
INSERT INTO "vn_mokhtasar" VALUES(31,23,'31:23','Và ai đã từ chối tin vào Allah thì Ngươi - hỡi Thiên Sứ - cũng chớ buồn phiền cho sự vô đức tin đó. Rồi đây, tất cả bọn chúng sẽ trở về trình diện một mình TA vào Ngày Phán Xét. Chính TA sẽ cho chúng biết những tội lỗi mà chúng đã từng làm ở trần gian và TA sẽ cho chúng nếm hậu quả của những tội lỗi mà chúng đã làm. Quả thật Allah luôn biết rõ mọi việc giấu kín trong lòng, bởi không việc gì che giấu được Ngài cả.');
INSERT INTO "vn_mokhtasar" VALUES(31,24,'31:24','TA (Allah) bỏ mặc cho chúng hưởng thụ với những thứ mà TA ban cho chúng ở trần gian trong khoảnh khắc ngắn ngủi, rồi vào Ngày Phán Xét TA sẽ tống chúng vào sự trừng phạt kinh hoàng nơi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(31,25,'31:25','Và nếu Ngươi - hỡi Thiên Sứ - hỏi đám người đa thần: Ai đã tạo ra các tầng trời và ai đã tạo ra trái đất? Chắc chắn chúng sẽ đáp: Allah đã tạo ra những thứ đó. Ngươi hãy nói với chúng: "Alhamdulillah - xin tạ ơn Allah - đã phơi bày bằng chứng rõ ràng cho các ngươi thấy." Tuy nhiên, đa số bọn chúng không biết ai đáng được tạ ơn vì sự ngu dốt của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(31,26,'31:26','Sự tạo hóa, chi phối và điều hành vạn vật trong các tầng trời và trái đất đều thuộc về một mình Allah. Quả thật Allah là Đấng Giàu Có, Ngài không cần đến tạo vật, Ngài là Đấng đáng được ca tụng ở trần gian và Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(31,27,'31:27','Và cho dù có chặt tất cả cây cối trên trái đất để làm cây viết và lấy nước biển làm mực và cho dù có lấy thêm bảy lần nước biển như thế để làm mực thì vẫn không sao viết hết Lời Phán của Allah. Quả thật, Allah là Đấng Toàn Năng, không có thứ gì kháng cự được Ngài, và Ngài là Đấng Sáng Suốt trong tạo hóa và chi phối.');
INSERT INTO "vn_mokhtasar" VALUES(31,28,'31:28','Việc tao hóa các ngươi - hỡi con người - cũng như việc làm sống lại các ngươi vào Ngày Phục Sinh để thanh toán và thưởng phạt chỉ giống như việc tạo ra một cơ thể duy nhất mà thôi, rất đơn giản. Quả thật, Allah nghe rõ từng âm thanh, không âm thanh nào làm nhiễu được âm thanh khác đối với Ngài và tương tự như thế việc Ngài tạo hóa và phục sinh cá thể này không làm ảnh hưởng đến việc Ngài tạo hóa và phục sinh cá thể khác.');
INSERT INTO "vn_mokhtasar" VALUES(31,29,'31:29','Há Ngươi không nhận thấy việc Allah thu ngắn ban đêm và kéo dài ban ngày, thu ngắn ban ngày và kéo dài ban đêm, và việc Ngài chế ngự mặt trời và mặt trăng chỉ bơi trên quỹ đạo riêng của mỗi vật. Quả thật, Allah luôn thông toàn mọi điều các ngươi làm, không hành động nào của các ngươi có thể giấu được Ngài và Ngài sẽ dựa theo đó mà thưởng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(31,30,'31:30','Sự điều hành và định đoạt đó là hai bằng chứng khẳng định Allah duy nhất là thật, Ngài thật trong bản chất, thuộc tính và hành động của Ngài còn tất cả mọi thứ mà những kẻ đa thần thờ phượng đều là ngụy tạo không có cơ sở. Quả thật, Allah là Đấng Tối Cao bằng chính thể xác của Ngài, Ngài quyền năng trên tất cả vạn vật, không có bất cứ thứ gì cao hơn Ngài và Ngài vĩ đại hơn tất cả mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(31,31,'31:31','Lẽ nào Ngươi không nhìn thấy những chiếc tàu lướt sóng băng qua các đại dương được là do sự tinh tế và chi phối của Ngài để Ngài cho các ngươi - hỡi con người - thấy những dấu hiệu chứng minh quyền năng của Ngài trong sự Chi Phối và Tinh Tế của Ngài đó sao? Tất cả quyền năng đó là bằng chứng cho những ai biết kiên nhẫn khi gặp nạn và biết tạ ơn khi được ân huệ.');
INSERT INTO "vn_mokhtasar" VALUES(31,32,'31:32','Và khi họ bị những cơn sóng to như những quả núi và những đám mây bao vây thì chúng liền khấn vái Allah bằng tấm lòng thành khẩn. Vậy mà khi Allah đáp lại lời khẩn cầu đó và cứu chúng trở về đất liền an toàn thì chúng lại hững hờ và thờ ơ với Ngài, trong chúng có kẻ bác bỏ các ân huệ của Ngài. Và không một ai bác bỏ các dấu hiệu của TA ngoại trừ đám người giả dối - giống như những kẻ từng hứa nếu như được Allah cứu bình an thì sẽ tạ ơn Ngài - phủ nhận mọi ân huệ của Ngài, không biết mang ơn khi Ngài ban cho họ ân huệ.');
INSERT INTO "vn_mokhtasar" VALUES(31,33,'31:33','Hỡi con người! Các ngươi hãy kính sợ Thượng Đế của các ngươi mà tuân thủ đúng theo mọi sắc lệnh của Ngài, các ngươi hãy lo sợ cho sự trừng phạt của Ngày mà cha con không ai có thể giúp đỡ được ai bất cứ điều gì. Chắc chắn Lời Hứa của Allah về việc thưởng phạt trong Ngày Tận Thế sẽ xảy ra. Thế nên các ngươi đừng để cuộc sống trần gian này đánh lừa các ngươi từ dục vọng của các ngươi và niềm vui ngắn ngủi của nó và các ngươi đừng để lũ Shaytan lường gạt các ngươi về sự chịu đựng của Allah khi Ngài trì hoãn việc trừng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(31,34,'31:34','Thật sự chỉ có Allah duy nhất mới tận tường bao giờ đến Ngày Tận Thế; Ngài biết rõ khi nào nó xảy ra, Ngài trút mưa xuống khi nào Ngài muốn, Ngài biết rõ trong dạ con đang mang thai là trai hay gái, gặp bất hạnh hay được hạnh phúc; và không một ai biết trước điều gì xảy ra với y vào ngày mai là tốt hay xấu, và cũng không một ai biết đâu là mảnh đất mà y sẽ chết. Chỉ Allah duy nhất luôn tận tường mọi thứ, Ngài tinh thông mọi thứ, không một điều gì có thể giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(32,1,'32:1','{Alif. Lam. Mim} là những chữ cái được dùng để khai đề cho chương Kinh. Ý nghĩa của nó đã được trình bày ở phần đầu của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(32,2,'32:2','Kinh Qur''an mà Muhammad mang đến này đây đích thực được mặc khải từ Thượng Đế của toàn vũ trụ và muôn loài, không có gì phải ngờ vực về điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(32,3,'32:3','Đám người vô đức tin này nói "Chắc chắn Muhammad đã tự bịa chuyện rồi nhân danh Thượng Đế của Y." Sự việc không như chúng nói đâu, mà đó là sự thật không có gì phải nghi ngờ cả, No đích thực được Thượng Đế thiên khải xuống cho Ngươi - hỡi Thiên Sứ -, để Ngươi cảnh báo đám người mà trước Ngươi chúng chưa từng được ai đến cảnh báo về hình phạt của Allah, hi vọng chúng sẽ được hướng dẫn đến với Chân Lý để chúng biết tuân thủ mà làm theo.');
INSERT INTO "vn_mokhtasar" VALUES(32,4,'32:4','Allah là Đấng đã tạo các tầng trời, trái đất và tất cả vạn vật giữa trời đất trong vòng sáu ngày, và dĩ nhiên Allah thừa khả năng tạo hóa tất cả trong thời gian ngắn hơn một cái nháy mắt. Sau đó Ngài an vị bên trên chiếc ''Arsh (Ngai Vương) của Ngài bằng thuộc tính tối cao của riêng Ngài. Các ngươi - hỡi con người - sẽ không tìm thấy đâu ngoài Ngài ai dám đứng ra bảo hộ công việc của các ngươi hoặc dám đứng ra biện hộ cho các ngươi trước Thượng Đế của các ngươi cả. Thế các ngươi không suy nghĩ rằng các ngươi nên thờ phượng Allah, Đấng đã tạo hóa các ngươi chứ không nên thờ phượng các thần linh khác cùng với Ngài?');
INSERT INTO "vn_mokhtasar" VALUES(32,5,'32:5','Allah Toàn Năng, Tối Cao định sẵn sự việc cho tất cả vạn vật trong các tầng trời và trái đất, rồi sự việc đó được mang lên trình tấu Ngài trong một ngày mà thời gian của nó tương đương một ngàn năm theo cách tính của các ngươi ở trần gian - hỡi con người.');
INSERT INTO "vn_mokhtasar" VALUES(32,6,'32:6','Như thế đó, Ngài là Đấng am tường mọi thứ có thể nhìn thấy và mọi thứ không thể nhìn thấy, không thứ gì giấu được Ngài, Ngài là Đấng quyền năng, không ai trong kẻ thù của Ngài có thể chiến thắng được Ngài, nhưng Ngài nhân từ với đám nô lệ có đức tin của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(32,7,'32:7','Ngài điều chỉnh tất cả vạn vật mà Ngài đã tạo đến mức hoàn mỹ và Ngài đã tạo ông tổ của loài ngươi - Adam - từ đất sét với hình dạng chưa từng có trước đó.');
INSERT INTO "vn_mokhtasar" VALUES(32,8,'32:8','Sau đó, Ngài tạo ra con cháu của Y - Adam - bằng loại dịch xuất ra từ cơ thể của y được gọi là tinh dịch.');
INSERT INTO "vn_mokhtasar" VALUES(32,9,'32:9','Sau đó, Ngài hoàn chỉnh hình dáng của con người, Ngài thổi linh hồn của Ngài vào y bằng cách hạ lệnh cho Thiên Thần thực hiện, và Ngài đã tạo cho các ngươi - hỡi con người - đôi tai để các ngươi nghe, cặp mắt để các ngươi nhìn và con tim để các ngươi suy nghĩ, thế mà các ngươi gần như không biết ơn về những hồng ân mà Ngài đã ban cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(32,10,'32:10','Và những kẻ đa thần phủ nhận sự Phục Sinh nói: Sau khi chúng tôi chết và trở thành cát bụi trong lòng đất thì chúng tôi được dựng sống lại lần nữa ư?! Không phải là chúng không hiểu được mà đúng hơn là chúng muốn bác bỏ việc phục sinh vì chúng không hề tin điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(32,11,'32:11','Ngươi - hỡi Thiên Sứ - hãy nói với đám người đa thần phủ nhận sự phục sinh: Allah đã giao nhiệm vụ cho Thần Chết đảm trách nhiệm vụ rút hồn các ngươi, sau đó các ngươi được đưa trở về trình diện một mình TA vaof Ngày Tận Thế để chịu sự thanh toán và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(32,12,'32:12','Rồi sẽ sớm cho đám người tội lỗi thấy được trong Ngày Tận Thế hoàn cảnh chính họ phải cúi đầu nhục nhã vì lý do phủ nhận sự phục sinh, khi họ nhận thức được sự nhục nhã, họ khấn vái: Lạy Thượng Đế của bầy tôi! Bầy tôi đã thấy được sai trái việc bầy tôi từng phủ nhận về sự phục sinh, bầy tôi đã nghe được sự thật mà các vị Thiên Sứ mang đến từ Ngài, xin Ngài cho bầy tôi trở lại cuộc sống trần gian lần nữa, bầy tôi quyết sẽ hành đạo làm Ngài hài lòng, quả thật bây giờ bầy tôi đã tin một cách kiên định rằng việc phục sinh là thật như các vị Thiên Sứ đã nói. Nếu như Ngươi - Muhammad - thấy được cảnh tượng của đám người tội lỗi lúc đó thì Ngươi đã thấy một cảnh tượng khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(32,13,'32:13','Và nếu muốn, TA đã ban cho mỗi người một chỉ đạo riêng, nhưng lời phán của TA phải được thực thị vị sự công bằng và anh minh của TA: TA sẽ nhét đầy Hỏa Ngục trong Ngày Tận Thế đám người vô đức tin thuộc hai loài Jinn (ma quỷ) và loài người; bởi vì chúng đã chọn con đường vô đức tin và lầm lạc khỏi con đường của đức tin và ngay chính.');
INSERT INTO "vn_mokhtasar" VALUES(32,14,'32:14','Trong Ngày Tận Thế có lời nói nhục mạ chúng: Đó, các ngươi hãy nếm đi hình phạt do sự lơ là của các ngươi trên trần gian về cuộc gặp gỡ Allah vào Ngày Tận Thế cho việc thanh toán các ngươi. TA (Allah) bỏ mặc cho các ngươi phải gánh chịu hình phạt như thế, cho nên các ngươi hãy mãi mãi nếm sự trừng phạt vì những tội lỗi mà các ngươi đã gây ra ở trên trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(32,15,'32:15','Quả thật, người tin tưởng vào các Lời Phán mà TA đã thiên khải cho Thiên Sứ của TA là những người mà khi họ được người khác dùng các Lời Phán đó khuyên bảo thì họ liền quỳ lạy mà tụng niệm, tạ ơn Allah và họ không hề có bản tính tự cao trong bổn phận thờ phượng Allah, và họ không e ngại phải quỳ lạy dù trong hoàn cảnh nào.');
INSERT INTO "vn_mokhtasar" VALUES(32,16,'32:16','Họ thức dậy rời khỏi nệm êm chăn ấm để hướng về Allah, họ khẩn cầu Ngài trong lễ nguyện Salah của mình bằng tấm lòng sợ hãi trước hình phạt của Allah đồng thời hi vọng nơi lòng khoan dung của Ngài, và họ dùng tài sản được Allah ban cấp cho con đường chính nghĩa của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(32,17,'32:17','Bởi thế, không một ai biết được những thứ mà Allah đã chuẩn bị cho họ, những thứ sẽ làm họ thỏa mãn và hài lòng, một phần thưởng bù đắp cho những việc làm ngoan đạo mà họ đã từng làm trên thế gian, đó là phần thưởng chỉ riêng Allah mới tận tường sự vĩ đại của nó.');
INSERT INTO "vn_mokhtasar" VALUES(32,18,'32:18','Người tin tưởng vào Allah, biết tuân thủ theo mệnh lệnh của Ngài không hề ngang bằng với kẻ rời khỏi sự phục tùng Allah, hai nhóm người này không hề ngang nhau trước Allah về phần đến đáp công lao.');
INSERT INTO "vn_mokhtasar" VALUES(32,19,'32:19','Đối với những ai đã tin tưởng Allah và hành đạo tốt, họ được Allah dành cho họ phần thưởng là những ngôi vườn của Thiên Đàng, một phần tưởng thưởng cho những việc làm ngoan đạo mà họ đã từng làm trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(32,20,'32:20','Và đối với những ai không phục tùng Allah bằng sự vô đức tin và vi phạm nhiều tội lỗi, chúng đã được chuẩn bị cho một chốn cư ngụ trong Ngày Tận Thế là Hỏa Ngục, chúng sẽ ở trong đó mãi mãi. Cứ mỗi lần chúng muốn rời khỏi nơi đó thì liền bị lôi trở lại vào trong đó kèm theo lời nhục mạ: Các ngươi hãy nếm hình phạt của Hỏa Ngục mà các ngươi đã từng phủ nhận ở trần gian, khi mà các Thiên Sứ đến cảnh báo các ngươi về nỗi kinh hoàng của Nó.');
INSERT INTO "vn_mokhtasar" VALUES(32,21,'32:21','Và chắc rằng TA sẽ cho những kẻ phủ nhận đã rời khỏi sự phụ tùng Thượng Đế của chúng gặp phải tai ách, khổ ải ở ngay trong cuộc sống trần gian trước khi cho chúng đối diện với sự trừng phạt khủng khiếp ở Đời Sau nếu như chúng vẫn không biết hối cải, hi vọng rằng chúng biết sợ mà quay đầu phục tùng Thượng Đế của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(32,22,'32:22','Và không còn ai bất công hơn kẻ đã được nhắc cho nghe về các lời mặc khải của Allah nhưng y vẫn thờ ơ quay lưng bỏ đi. Rồi đám người tội lỗi đó sẽ bị TA trừng phạt thích đáng, không thể nào tránh khỏi.');
INSERT INTO "vn_mokhtasar" VALUES(32,23,'32:23','Và TA (Allah) đã thiên khải cho Musa Kinh Tawrah (Cựu Ước), thế nên, Ngươi - hỡi Thiên Sứ - đừng nảy sinh sự nghi ngờ khi Ngươi gặp gỡ Musa trong lần Isra'' và Mi''araj (dạ hành trong đêm và thăng thiên) và TA còn lấy quyển Kinh đã thiên khải cho Musa làm chỉ đạo cho đám con cháu lầm lạc của Israel.');
INSERT INTO "vn_mokhtasar" VALUES(32,24,'32:24','Và TA đã thiết lập một nhóm lãnh đạo trong con cháu Israel để hướng dẫn thiên hạ đi theo Chân Lý, khi mà chúng luôn nhẫn nại tuân thủ theo mệnh lệnh của Allah và những khó khăn cản trở trong con đường truyền bá và chúng đã một lòng kiên định tin vào các Lời Mặc Khải của TA đã thiên khải xuống cho Thiên Sứ của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(32,25,'32:25','Chắc chắn Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng xét xử giữa bọn chúng trong Ngày Tận Thế về những điều mà chúng đã thường tranh cãi nhau trên trần gian, Ngài sẽ phân định rõ Chân Lý và ngụy tạo và thưởng phạt thích đáng cho mỗi người.');
INSERT INTO "vn_mokhtasar" VALUES(32,26,'32:26','Phải chăng đám người này đã không được trình bày cho thấy việc TA đã tiêu diệt biết bao cộng đồng ở thời trước chúng hay sao?! Chính nơi mà chúng đang bước đi là chốn cư ngụ của các cộng đồng đó từng sinh sống trước đây, chúng đã không biết lấy đó làm bài học rằng chỉ vì vô đức tin và tội lỗi mà các cộng đồng đó bị hủy diệt, và sự kiện đó cũng là bằng chứng xác minh sứ mạng của các Thiên Sứ thật sự đến từ Allah. Lẽ nào chúng không từng nghe thông tin về đám người phủ nhận các lời mặc khải của TA để lấy đó mà làm bài học hay sao?!');
INSERT INTO "vn_mokhtasar" VALUES(32,27,'32:27','Phải chăng đám người phủ nhận sự phục sinh không nhìn thấy việc TA đã trút mưa xuống vùng đất khô hạn không một cây cỏ, sau cơn mưa thì vùng đất đó lại mọc lên thảo mộc và cây cối dùng làm lương thực cho lạc đà, bò, dê của chúng ăn và cho cả bản thân chúng ăn đó hay sao? Chúng không nhìn thấy và cảm nhận rằng chính Đấng làm sống lại vùng đất đã chết khô thừa khả năng phục sinh con người đã chết hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(32,28,'32:28','Và đám người phủ nhận sự phục sinh đang hối thúc hình phạt mau đến, nói: Bao giờ lệnh trừng phạt mà các ngươi đã khẳng định, nó sẽ phân định ranh giới rõ ràng giữa bọn ta và các ngươi trong Ngày Tận Thế, rồi bọn ta sẽ vào Hỏa Ngục còn các ngươi sẽ được vào Thiên Đàng vậy?!');
INSERT INTO "vn_mokhtasar" VALUES(32,29,'32:29','Người hãy nói với chúng - hỡi Thiên Sứ -: Cuộc hẹn trong Ngày Tận Thế sẽ là cuộc phân loại con người, lúc đó việc đám người đã phủ nhận Allah ở trần gian tin sau khi nhìn thấy Ngày Phán Xét sẽ không có lợi ích đối với chúng, một sự chứng thực trong Ngày Tận Thế sau khi đã phân loại và chúng cũng mất cơ hội trì hoãn để ăn năn sám hối với Thượng Đế.');
INSERT INTO "vn_mokhtasar" VALUES(32,30,'32:30','Vì vậy, Ngươi - hỡi Thiên Sứ - hãy tránh xa đám người lầm lạc đó và hãy chờ đợi chuyện gì sẽ xảy đến với chúng và chúng cũng phải chờ đợi hình phạt đang đón chào chúng.');
INSERT INTO "vn_mokhtasar" VALUES(33,1,'33:1','Hỡi Thiên Sứ, Ngươi và những người có đức tin cùng Ngươi hãy luôn duy trì lòng kính sợ Allah bằng việc tuân thủ theo điều Ngài ra lệnh và tránh xa điều Ngài ngăn cấm và cho dù chỉ còn mỗi Ngươi cũng đừng chìu theo đám người vô đức tin và những tên Munafiq (giả tạo đức tin). Quả thật, Allah luôn tường tận kế hoạch của đám người vô đức tin và những tên Munafiq đó, Ngài chí minh trong tạo hóa và quản lý.');
INSERT INTO "vn_mokhtasar" VALUES(33,2,'33:2','Và Ngươi (Muhammad) hãy bám theo những điều mà Thượng Đế đã thiên khải cho Ngươi, quả thật Allah luôn biết tường tận hành động của các ngươi, không cử chỉ nào của các ngươi qua mặt được Ngài và Ngài sẽ dựa vào đó mà thưởng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(33,3,'33:3','Và Ngươi hãy phó thác cho một mình Allah duy nhất trong mọi công việc của Ngươi và chỉ cần Đấng Toàn Năng thôi là đủ bảo vệ tất cả những ai phó thác cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(33,4,'33:4','Giống như việc Allah không bao giờ đặt vào lòng ngực của một người đàn ông hai con tim, Ngài không hề xem vị trí người vợ như mẹ đẻ về mặt cấm giao hợp và Ngài cũng không bao giờ xem con nuôi như con ruột. Quả thật, Zhihar - là việc người đàn ông tự cấm mình gần gũi vợ - và con nuôi là hai thói quen mà người thời tiền Islam thường xuyên sử dụng và cả hai thói quen này đã bị Islam xóa bỏ. Hai điều đó thường được họ lặp đi lặp lại trên cửa miệng không phải là sự thật, vợ không thể là mẹ và con nuôi không thể là con ruột cho dù có tuyên bố, còn Allah luôn nói lời Chân Lý để đám nô lệ của Ngài tuân theo và Ngài hướng dẫn họ đến với con đường Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(33,5,'33:5','Các ngươi hãy gọi những đứa trẻ mà các ngươi tự cho rằng là con đẻ của mình theo đúng gia phả của cha ruột của chúng, việc gọi đúng gia phả như thế là lẽ công bằng đối với Allah. Nếu trường hợp không biết rõ cha đẻ của chúng là ai để gọi cho đúng thì chúng là anh em đồng đạo với các ngươi và là người đại diện của nhau với các ngươi. Cho nên hãy gọi chúng là người anh em hoặc là người con của chú (bác) của tôi. Và các ngươi không bị bắt tội khi các ngươi không cố ý gọi ai đó không đúng gia phả của y nhưng các ngươi sẽ bị bắt tội khi các ngươi cố tình gọi sai như thế. Allah luôn tha thứ cho người nô lệ nào biết ăn năn sám hối và luôn khoan dung, không bắt tội khi họ lỡ lầm vi phạm.');
INSERT INTO "vn_mokhtasar" VALUES(33,6,'33:6','Đối với những người có đức tin, Nabi Muhammad có quyền hơn ai nhất trong tất cả những gì Ngài kêu gọi họ đến với nó cho dù bản thân họ có nghiêng về ai khác ngoài Y và các bà vợ của Y là những người mẹ của toàn thể những người có đức tin. Người có đức tin tuyệt đối không được kết hôn với bất cứ người vợ nào của Y sau khi Y qua đời. Allah qui định người trong họ hàng ưu tiên thừa kế tài sản lẫn nhau hơn những người có đức tin khác cũng như những người di cư vì chính nghĩa của Allah - đây là những người từng được ở thời đầu của Islam, sau đó giáo lý này bị xóa bỏ. Tuy nhiên, không cấm các ngươi - hỡi những người có đức tin - đối xử tốt với những người đại diện của các ngươi. Đó là giáo luật mà Allah đã qui định trong quyển Kinh Mẹ bắt buộc phải tuân thủ.');
INSERT INTO "vn_mokhtasar" VALUES(33,7,'33:7','Và Ngươi hãy nhớ lại - hỡi Thiên Sứ - việc TA đã giao ước với các Nabi là phải thờ phượng riêng TA, không được tổ hợp bất cứ thứ gì với TA, phải truyền bá hết mọi giáo điều mà TA đã thiên khải. TA cũng đã giao ước như thế riêng với Ngươi, với Nuh, với Ibrahim, với Musa, với Ysa con trai của Maryam, TA đã giao ước với họ bằng cuộc giao ước trịnh trọng rằng họ phải hoàn thành sứ mạng truyền bá các Bức Thông Điệp của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(33,8,'33:8','Allah đã lập giao ước trịnh trọng này với một số Nabi để Ngài hỏi những người chân thật trong số Thiên Sứ sự thật về những người vô đức tin, và Allah đã chuẩn bị cho đám người vô đức tin nơi Ngài cũng như các Thiên Sứ của Ngài một hình phạt đau đớn trong Ngày Tận Thế, đó là lửa của Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(33,9,'33:9','Hỡi những người đã tin tưởng Allah và đã tuân thủ theo giáo luật của Ngài, các ngươi hãy nhớ lại ân huệ mà Alalh đã ban cho các ngươi khi Madinah bị nhiều đoàn quân của địch vây hãm bên ngoài thành để tiêu diệt các ngươi, cộng thêm sự bội ước của đám người Munafiq và Do Thái bên trong thành. Thế là TA đã gởi đến chúng một cơn gió mạnh hỗ trợ cho Nabi và phái xuống một đoàn quân Thiên Thần mà các ngươi không nhìn thấy. Điều đó khiến đám người vô đức tin bất lực không làm được gì đành phải rút lui. Allah luôn thấu đáo mọi điều các ngươi làm, không việc gì giấu được Ngài và Ngài sẽ dựa theo đó mà thưởng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(33,10,'33:10','Và thế đó khi đám người vô đức tin bao vây các ngươi từ hai phía, phía trên và phía dưới của thung lũng, tức hai hướng đông và tây. Thời điểm đó, cặp mắt của các ngươi tỏ vẻ hoảng loạn không nhìn thấy gì ngoại trừ việc bị kẻ thù bao vây và con tim của các ngươi thì nhảy lên đến tận cổ vì quá sợ hãi, nó khiến các ngươi có suy nghĩ khác nhau về Allah, lúc thì nghĩ sẽ được Ngài phù hộ chiến thắng và lúc thì lại mất hy vọng nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(33,11,'33:11','Trong bối cảnh của trận chiến Khandaq, những người có đức tin đã bị thử thách quá sức chịu đựng của họ trước kẻ thù, khiến họ rơi vào nỗi khiếp sợ khiến tâm trí họ bấn loạn, tuy nhiên, qua đó để phân loại đâu là những người có đức tin và đâu là những người Munafiq.');
INSERT INTO "vn_mokhtasar" VALUES(33,12,'33:12','Vào ngày hôm đó, đám người Munafiq và nhóm người có niềm tin Iman yếu ớt mang mầm bệnh nghi ngờ lên tiếng nói: Những gì Allah và Thiên của Ngài hứa hẹn nào là chiến thắng kẻ thù và tự do trên trái đất chỉ là điều viển vông.');
INSERT INTO "vn_mokhtasar" VALUES(33,13,'33:13','Và hãy nhớ lại - hỡi Thiên Sứ - khi một nhóm nhỏ của đám người Munafiq ở Madinah nói: Hỡi cư dân của Yathrib (tên của Madinah trước Islam) các ngươi không có cơ hội đứng gần Khandaq nữa đâu, các ngươi hãy trở về nhà của mình đi. Còn một nhóm khác thì đến gặp Nabi để xin phép trở về nhà bảo vệ nhà cửa trước sự tấn công của kẻ thù nhưng sự thật thì không phải như thế, chẳng qua chúng muốn viện lý do để chạy trốn mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(33,14,'33:14','Và nếu kẻ thù tấn công vào Madinah bằng mọi con đường và yêu cầu chúng trở lại việc phủ nhận Allah và Shirk với Ngài, chắc chắn chúng sẽ chấp nhận ngay lời yêu cầu đó và chúng sẽ trở lại sự vô đức tin trong sự ngần ngại chốc lát.');
INSERT INTO "vn_mokhtasar" VALUES(33,15,'33:15','Và sau lần bỏ chạy ở trận Uhud, nhóm người Munafiq này đã giao ước với Allah sẽ không bao giờ bỏ chạy nữa khi chiến tranh; nếu như chúng được Allah cho cơ hội gặp kẻ thù lần nữa thì chúng sẽ xả thân mà giết kẻ thù, nhất quyết không nhát gan mà bỏ chạy, nhưng chúng đã bội ước và mỗi tên trong bọn chúng sẽ phải chịu trách nhiệm cho lời giao ước đó và rồi chúng sẽ được thẩm tra.');
INSERT INTO "vn_mokhtasar" VALUES(33,16,'33:16','Hãy nói đi - hỡi Thiên Sứ - với đám người này: Việc các ngươi tháo chạy khỏi chiến tranh vì sợ chết hoặc sợ bị giết không hề có lợi gì cho các ngươi, bởi tuổi thọ đều đã được định sẵn, cho dù các ngươi có chạy thoát thì các ngươi không sống thọ hơn tuổi đã định trước, chắc rằng các ngươi chỉ hưởng lạc được thêm thời gian ngắn ngủi nữa thôi.');
INSERT INTO "vn_mokhtasar" VALUES(33,17,'33:17','Ngươi hãy nói với chúng - hỡi Thiên Sứ -: Ai có thể cản trở Allah khi Ngài muốn các ngươi phải chết hoặc bị giết hoặc khi Ngài muốn bảo vệ các ngươi được bình an và tốt đẹp, không ai có khả năng đó, và ngoài Allah, đám người Munafiq này cũng sẽ không tìm đâu ra người bảo hộ cho mọi công việc của chúng và không tìm được bất cứ ai giúp đỡ chúng thoát khỏi sự trừng phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(33,18,'33:18','Allah biết rõ một nhóm người trong các ngươi đã xúi giục nhóm người khác đào ngũ không tham chiến cùng Thiên Sứ nữa, chúng nói với anh em của chúng: Các anh hãy trở về bên cạnh chúng tôi, đừng có tham chiến kẻo bị giết chết, chúng tôi rất lo sợ các anh sẽ bị giết, trong khi đám người này chỉ tham chiến với số lượng rất ít để tự bảo vệ bản thân họ chứ họ nào muốn hỗ trợ Allah và Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(33,19,'33:19','Chúng đã đê hèn với các ngươi - hỡi những người có đức tin -, chúng đã không dùng tài sản của chúng để hỗ trợ các ngươi và cũng không xả thân tham chiến đánh đuổi kẻ thù cùng các ngươi, lại càng không thương yêu các ngươi. Đến khi bị kẻ thù tấn công thì bọn chúng hoảng loạn, bọn chúng đảo mắt nhìn Ngươi - hỡi Thiên Sứ - bằng ánh mắt nhát gan tựa như người sắp chết. Vậy mà khi nỗi sợ hãi tiêu tan và bình tĩnh trở lại thì bọn chúng lại xúc phạm các ngươi bằng chiếc lưỡi gớm ghiếc của chúng để hồng có được phần lợi từ chiến lợi phẩm thu được. Với những kẻ có những thuộc tính vừa miêu tả không phải là người có đức tin thực thụ, Allah đã xóa bỏ ân phước mà chúng đã hành đạo và việc xóa bỏ đó đối với Allah rất đơn giản.');
INSERT INTO "vn_mokhtasar" VALUES(33,20,'33:20','Đám người nhát gan đó cứ tưởng liên quân của địch vây hãm để giết Thiên Sứ và những người có đức tin vẫn chưa rút quân cho đến khi chúng nhổ tận gốc rễ những người có đức tin. Nếu như đám liên quân đó tấn công một lần nữa thì đám người Munafiq muốn rời khỏi Madinah đến ở cùng những người Ả-rập du mục, chúng sẽ hỏi thông tin về các ngươi: Chuyện gì xảy ra với các ngươi sau khi bị kẻ thù tấn công? và dẫu cho chúng ở cùng với các ngươi - hỡi những người có đức tin - thì chúng cũng không thiết tha tham chiến cùng các ngươi, thế nên đừng có trông chờ cũng như nhờ cậy vào chúng.');
INSERT INTO "vn_mokhtasar" VALUES(33,21,'33:21','Rõ ràng qua những gì Thiên Sứ của Allah đã nói, đã làm, đã hoàn thành là tấm gương mẫu mực, đích thân Người đã tham chiến thế thì tại sao các ngươi vẫn còn nhát gan không chịu xả thân để tham chiến cơ chứ? Và không ai bắt chước theo tấm gương của Thiên Sứ của Allah ngoại trừ những ai hi vọng vào cuộc sống Đời Sau và tụng niệm Allah rất nhiều. Còn những ai không mong ước cuộc sống Đời Sau và không tụng niệm Allah nhiều thì y không bao giờ bắt chước theo Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(33,22,'33:22','Và khi đoàn quân có đức tin nhìn thấy liên quân địch tấn công thì họ nói: Đây chính là điều mà Allah và Thiên Sứ của Ngài đã hứa để thử thách, để sàng lọc và để ban chiến thắng đây mà, những gì Allah và Thiên Sứ của Ngài đã hứa đều là sự thật, và việc bị liên quân bao vây chỉ làm gia tăng thêm niềm tin của họ với Allah và tuân phục mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(33,23,'33:23','Trong số những người có đức tin có những người đàn ông thật lòng với Allah, họ đã hoàn thành lời giao ước với Allah là luôn kiên cường, anh dũng xã thân vì Jihad trên con đường chính nghĩa của Allah. Trong số họ có người đã chết hoặc bị giết vì chính nghĩa đó và số khác vẫn đang chờ đợi được hi sinh và họ tuyệt đối không thay đổi những gì đã giao ước với Allah trước đây, không giống như đám người Munafiq đã bội ước.');
INSERT INTO "vn_mokhtasar" VALUES(33,24,'33:24','Để Allah ban thưởng cho những ngươi chân thật đã hoàn thành lời giao ước với Allah và để Ngài trừng phạt đám người Munafiq đã bội ước nếu Ngài muốn thế, bằng cách cho chúng chết trước khi sám hối về tội vô đức tin của chúng hoặc là Ngài sẽ quay lại chấp nhận sự sám hối của chúng. Quả thật, Allah là Đấng Nhân Từ, sẵn lòng tha thứ cho ai biết ăn năn sám hối về tội lỗi đã phạm.');
INSERT INTO "vn_mokhtasar" VALUES(33,25,'33:25','Và Allah đã đẩy lùi quân Quraish và liên quân của chúng trở về với tâm trạng bực tức và đau buồn vì chưa đạt được điều chúng mong muốn, chưa nhổ được tận gốc những người có đức tin, và một mình Allah thôi đã đủ để giúp những người có đức tin chiến đấu với kẻ thù, như việc Ngài gởi những cơn gió và phái đoàn quân Thiên Thần xuống viện trợ, quả thật Allah Toàn Lực, Toàn Năng, không ai địch nổi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(33,26,'33:26','Và Allah đã thiên khải báo cho biết về đám ngươi Do Thái có mặt trong thành lũy đã bắt tay với kẻ thù, và Ngài gieo nỗi sợ hãi vào lòng của chúng, một nhóm người trong chúng đã bị các ngươi giết - hỡi những người có đức tin - và nhóm khác đã bị các ngươi bắt làm tù binh.');
INSERT INTO "vn_mokhtasar" VALUES(33,27,'33:27','Và Allah đã cho các ngươi thừa kế vùng đất của chúng gồm nông sản và chà là sau khi tiêu diệt được chúng, Ngài cho các ngươi thừa kế nhà cửa và những tài sản khác của chúng và Ngài cho các ngươi thừa hưởng luôn vùng đất Khaibar, nơi mà các ngươi chưa từng đặt chân đến bao giờ, đây là lời hứa và là tin vui dành cho những người có đức tin. Quả thật, Allah có khả năng tuyệt đối trên tất cả mọi thứ, không có gì làm Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(33,28,'33:28','Hỡi Nabi! Ngươi hãy nói với các bà vợ của Ngươi khi mà các bà yêu cầu Ngươi tăng thêm mức cấp dưỡng trong lúc Ngươi không có bất cứ gì cấp thêm cho họ: Nếu như các cô muốn cuộc sống trần gian này và sự sống xa hoa của nó thì hãy đến đây Ta sẽ cho các cô hưởng thụ giống như những người vợ được hưởng thụ sau li hôn và rồi Ta sẽ li dị các cô mà không hề gây bất cứ khó khăn nào.');
INSERT INTO "vn_mokhtasar" VALUES(33,29,'33:29','Và nếu các nàng muốn sự hài lòng của Allah, sự hài lòng của Thiên Sứ của Ngài và muốn Thiên Đàng của cuộc sống Đời Sau thì các nàng phải kiên nhẫn trước hoàn cảnh hiện tại. Quả thật, Allah đã chuẩn bị sẵn cho những ai sùng đạo, biết kiên nhẫn và sống tốt trong các nàng một phần thưởng vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(33,30,'33:30','Hỡi những phụ nữ của Nabi! Ai trong các ngươi làm điều tội lỗi công khai thì sẽ bị trừng phạt gấp đôi trong Ngày Tận Thế vì địa vị và thân thế của các ngươi và để giữ thanh danh cho Thiên Sứ và việc tăng gấp đôi mức phạt đối với Allah vô cùng đơn giản.');
INSERT INTO "vn_mokhtasar" VALUES(33,31,'33:31','Và ai trong các nàng luôn là người biết hạ mình kính cẩn làm theo Allah và Thiên Sứ của Ngài trong những việc làm thiện dưới sự hài lòng của Allah - thì chắc chắn sẽ được Ngài ân thưởng cho những việc làm không đáng kể đó của tất cả các nàng và Ngài còn chuẩn bị sẵn cho các nàng vào Ngày Sau muôn vàn bổng lộc, đó là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(33,32,'33:32','Hỡi những người vợ của Nabi Muhammad - cầu xin sự bình an cho Người - hẳn các nàng là những người rất tốt đẹp và có địa vị cao quý không giống bao thiếu nữ khác. Không! đúng hơn, các nàng là những người có một phẩm chất tốt đẹp và một địa vị rất là cao quý trong xã hội mà không có ai có thể sánh được với các nàng nếu các nàng là những người biết hạ mình làm theo những gì mà Allah ra lệnh và tuyệt đối kìm hãm trước điều nghiêm cấm của Ngài. Bởi thế, các nàng chớ nên ăn nói với giọng điệu lả lơi khi nói chuyện với những người đàn ông bên cạnh, e rằng đó sẽ là nguyên nhân biến con tim các nàng trở thành những căn bệnh tà dâm và sự ham muốn dẫn đến hành động trái luật Haram. Bởi thế, các nàng hãy ăn nói một cách lịch thiệp và trang nhã tránh dùng những từ ẻo lả, quả thật đó là điều rất cần thiết cho các nàng.');
INSERT INTO "vn_mokhtasar" VALUES(33,33,'33:33','Và các nàng hãy sống khép kín trong nhà, chớ ra khỏi nhà nếu như đó là sự không cần thiết và chớ phô bày ra cái vẻ đẹp mĩ miều theo lối của những người phụ nữ ở thời kỳ trước Islam nơi mà những người phụ nữ mãi lo chải chuốc bởi những người đàn ông, các nàng hãy dâng lễ nguyện Salah một cách chu toàn, hãy xuất Zakah với số tài sản của các nàng và luôn làm theo mệnh lệnh của Allah và Thiên Sứ của Ngài. Quả thật, Allah Hiên Vinh chỉ muốn tẩy đi những điều ô uế còn động lại trên các nàng. Này hỡi các người vợ của Thiên Sứ của Allah và những người trong gia đình của Người, TA sẽ thanh lộc bản thân của các ngươi trở thành một tạo vật tốt đẹp nhất và khiến các ngươi trở thành những người sạch sẽ và tinh khiết nhất sẽ không còn xót lại khi được tẩy sạch ô uế.');
INSERT INTO "vn_mokhtasar" VALUES(33,34,'33:34','Và các nàng hãy luôn ghi nhớ những gì nằm trong lời mặc khải của Allah được xướng đọc ở trong nhà các nàng mà được tiết lộ bởi Thiên Sứ của Ngài vì đó là đường lối đúng đắn của Thiên Sứ của Ngài đã được tiết lộ. Quả thật, Allah Rất Mực Tinh Tế về mọi chuyện khi các nàng ở trong nhà của Thiên Sứ của Ngài và Ngài còn Rất Mực Am Tường khi tuyển chọn các nàng làm vợ của Thiên Sứ của Ngài và các nàng là những người phụ nữ ưu tú được tuyển chọn ra trong nhóm người có đức tin từ cộng đồng của Người.');
INSERT INTO "vn_mokhtasar" VALUES(33,35,'33:35','Chắc chắn đối với những bề tôi của Allah luôn biết phục tùng Ngài dù nam hay nữ, luôn là những người chân thành với Allah dù nam hay nữ, luôn là những người biết tôn kính Allah dù nam hay nữ, luôn là những người trung thực trong đức tin và từng lời nói dù nam hay nữ, luôn là những người biết chịu đựng trong sự phục tùng Ngài và khổ hạnh và đầy sự thử thách dù nam hay nữ, luôn là người có trách nhiệm xuất Zakah bắt buộc và bố thí tự nguyện dù nam hay nữ, luôn làm tròn bổn phận nhịn chay bắt buộc với Allah và nhịn thêm Sunah dù nam hay nữ, luôn là những người biết giữ lòng trinh bạch che đậy đi những điều thô thiển trước trạng thái bị nhìn thấy dù nam hay nữ, tránh xa những điều thông dâm và có ý định tà dâm và hãy thường xuyên tụng niệm Allah bằng con tim, bằng chiếc lưỡi ở nơi công khai hay nơi thầm kín dù nam hay nữ - Allah là Đấng Hằng Tha Thứ tội lỗi cho họ và là Đấng ban phần thưởng vĩ đại vào ngày sau, đó là nơi ở Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(33,36,'33:36','Và không đúng bổn phận đối với người có đức tin dù nam hay nữ, rằng nếu như giáo lý của Allah và Thiên Sứ của Ngài được ban hành cho họ một công việc nào đó rồi họ còn phân vân nên chấp nhận hay từ chối điều đó và cho những ai bất tuân ngang nhiên chống đối lại Allah và Thiên Sứ của Ngài thì chắc chắn sẽ chuốc lấy con đường lầm lạc một cách quá rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(33,37,'33:37','Và khi Ngươi - hỡi Thiên Sứ - nói với người được Allah ban cho hồng ân trở thành người Muslim và được Ngươi trả lại sự tự do cho y - nói về câu chuyện của Zaid bin Harithah khi Zaid đến để tham khảo ý kiến của Thiên Sứ về việc li hôn vợ của y là bà Zaynab bintu Jahash - Người đã bảo y rằng: ”Hãy giữ vợ của ngươi lại và chớ li hôn nàng ta và hãy biết kính sợ Allah trong sự tuyệt đối tuân theo mệnh lệnh của Ngài và tránh xa những gì mà Ngài nghiêm cấm. Trong khi đó Ngươi - hỡi Thiên Sứ - luôn giử kín trong lòng lời mặc khải của Allah về cuộc hôn nhân của Ngươi với Zaynab, Ngươi sợ thiên hạ đàm tiếu. Tuy nhiên Allah sẽ công khai sự việc Zaid đã li hôn với vợ của y rồi sau đó Ngài kết đôi bà ta cho Ngươi. Đáng lẽ ra Allah đáng để Ngươi kính sợ hơn trong vấn đề này. Cho đến sau khi nào chính bản thân của Zaid không thể nào ở được với vợ y và y đã li hôn vợ của y, thế là TA đã gả bà ta cho Ngươi. Sở dĩ thế là để cho những người có đức tin sẽ thấy đó là không có vần đề gì khi được phép cưới những người vợ của những đứa con nuôi của họ nếu như họ đã li hôn dứt khoác với các người vợ của họ và đó cũng mệnh lệnh của Allah cho việc làm mà không có sự nghiêm cấm và cũng không có sự ngăn cản nào cho điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(33,38,'33:38','Nabi Muhammad - cầu xin sự bình an cho Người - không sai phạm hay làm bóp méo đi sự thật mà Allah cho phép được cưới những bà vợ của những người con nuôi của Người sau khi cả hai đã li dị dứt khoát và đó cũng là đường lối đúng đắn làm theo các vị Thiên Sứ trước kia của Người. Bởi thế, Thiên Sứ - cầu xin sự bình an cho Người - làm những điều đó không phải là con đường mới mẻ mà các vị Thiên Sứ trước kia chưa từng làm mà đó chính là giáo luật của Allah cho phép Người - hoàn tất việc hôn nhân hoặc sự hủy bỏ việc nhận con nuôi và đó cũng không phải một ý kiến của Nabi hoặc sự lựa chọn của Người - mà đó là một chỉ dụ đã được quy định mà không có sự khước từ đối với Người.');
INSERT INTO "vn_mokhtasar" VALUES(33,39,'33:39','Các vị Nabi, họ là những người truyền bá đi những thông điệp của Allah xuống cho cộng đồng của họ mà không hề biết sợ một ai ngoài trừ duy nhất Allah Tối Cao cả. Bởi thế, họ chẳng có chút bận tâm về những lời đàm tiếu một ai đó mà đó là sự cho phép của Allah giành cho họ và chỉ riêng Allah mới có đủ thẩm quyền quản lý những việc làm của đám bề tôi của Ngài, để Ngài ban thưởng họ một cách xứng đáng. Quả thật, những điều tốt đẹp luôn nhận được những điều tốt đẹp còn những điều xấu xa luôn gánh chịu hậu quả xấu xa.');
INSERT INTO "vn_mokhtasar" VALUES(33,40,'33:40','Muhammad không phải là cha đẻ của một ai đó trong số những người đàn ông. Bởi thế, Người không phải là cha đẻ của Zaid nên không bị cấm việc kết hôn với vợ của Zaid sau khi Zaid đã li hôn vợ y. Ngược lại Người là một Thiên Sứ của Allah được cử đến với toàn nhân loại và cũng là một Ấn Tín của các vị Nabi bởi vì sau Người sẽ không còn có một vị Nabi nào nữa. Quả thật, Allah là Đấng Am Tường hết mọi việc vì không có một điều bí ẩn nào có thể che giấu được mệnh lệnh của Ngài giành cho đám bề bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(33,41,'33:41','Hỡi những ai có đức tin nơi Allah và luôn làm theo giáo luật cuả Ngài ! Các người hãy thường xuyên tụng niệm Allah bằng con tim, bằng chiếc lưỡi của các ngươi và luôn nhắc nhở nhau về giáo lý của Ngài cho thật nhiều.');
INSERT INTO "vn_mokhtasar" VALUES(33,42,'33:42','Và hãy táng dương Ngài, Đấng Hiển Vinh bằng những lời Tasbih, Tahlil vào mỗi buổi hừng đông và xế chiều đó là hai khoảng thời gian tốt đẹp nhất trong ngày.');
INSERT INTO "vn_mokhtasar" VALUES(33,43,'33:43','Chính Ngài là Đấng thương xót và tuyên dương các ngươi, các vị Thiên Thần của Ngài luôn luôn cầu nguyện cho các ngươi để giải thoát các ngươi khỏi sự tâm tối của sự vô đức tin ra ánh sáng của đức tin. Và Ngài là Đấng Hằng Khoan Dung đối với những người có đức tin; bởi thế, Ngài sẽ không trừng phạt những ai nếu như họ luôn vâng lời Ngài, tuyệt đối tuân theo mệnh lệnh của Ngài và tránh xa những điều Ngài nghiêm cấm.');
INSERT INTO "vn_mokhtasar" VALUES(33,44,'33:44','Lời kính chào của những người có đức tin vào ngày trình diện trước Thượng Đế của họ sẽ là bằng lời: "Chào Salam" và được Ngài bảo vệ tránh khỏi mọi điều xấu. Ngài còn chuẩn bị cho họ muôn vàn phần thưởng - đó chính là Thiên Đàng của Ngài - một phần thưởng giành cho những ai tuyệt đối tuân theo Ngài và tránh xa những điều tội lỗi mà Ngài cấm.');
INSERT INTO "vn_mokhtasar" VALUES(33,45,'33:45','Hỡi Nabi! Quả thật, chính TA đã cử phái Ngươi đến với thiên hạ để làm một nhân chứng sống rồi truyền tải một bức thông điệp của TA đến với họ và báo tin mừng cho những người có đức tin rằng TA đã chuẩn bị sẵn cho họ một Thiên Đàng giành cho họ và cũng cảnh báo cho những kẻ không có đức tin hãy nên khiếp sợ cái hình phạt mà TA đã chuẩn bị giành cho bọn họ ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(33,46,'33:46','Và TA đã cử phái Ngươi đến để kêu gọi thiên hạ đến với Allah và tuân theo mệnh lệnh của Ngài, TA cử phái Ngươi làm Người đại diện cũng như một chiếc đèn để soi sáng cho tất cả những ai muốn tìm ra chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(33,47,'33:47','Và hãy thông cáo tin mừng cho những ai có đức tin nơi Allah mà chính họ đã dựa trên một nền tảng giáo lý của Ngài sẽ nhận được một điều rất là hoan hỉ thật là vĩ đại của Allah giành cho họ đã chiến thắng thật sự ở trần gian này và giành quyền thằng lợi ở ngày sau sẽ được bước vào chốn Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(33,48,'33:48','Và đừng làm theo lời nói xàm bậy của những kẻ vô đức tin và những tên đạo đức giả về mọi điều mà chúng mời gọi nhằm ngăn chặng tôn giáo của Allah, hãy từ bỏ họ. Mong rằng điều đó khiến họ trở lại tin tưởng vào những gì mà Ngươi mang đến cho họ. Và hãy ủy thác hết tất cả mọi việc của Ngươi cho Allah, luôn cả việc được chiến thắng trước kẻ thù. Bởi vì, Allah đủ làm một phò trợ để cho một đám bề tôi dựa dẫm và phó thác những gì mà họ mong muốn ở trần gian này và Ngày Sau.');
INSERT INTO "vn_mokhtasar" VALUES(33,49,'33:49','Hỡi những ai tin tường nơi Allah và làm theo nền tảng giáo lý của Ngài! Nếu các ngươi kết hôn với những người phụ nữ đức hạnh, rồi sau đó các ngươi li hôn họ trước khi các ngươi ăn nằm với họ thì các ngươi không cần phải áp dụng thời gian ở vậy ''Iddah, không phân biệt là các ngươi mưới cưới hay đã cưới lâu, vì đều biết rõ cô vợ không hề có mang thai vì không có ăn nằm với nhau. Nhưng các ngươi hãy tặng cho họ một số tiền theo khả năng của các ngươi; rồi hãy dàn xếp mọi chuyện cho ổn thỏa sau cuộc li hôn và trả tự do cho họ trở về với gia đình của họ trong mọi chuyện đã được ổn thỏa đối với họ.');
INSERT INTO "vn_mokhtasar" VALUES(33,50,'33:50','Hỡi Thiên Sứ! Quả thật, TA đã cho phép Ngươi ăn nằm cùng với các bà vợ mà Ngươi đã đưa tiền cưới hỏi Mahhar cho họ, ngoài ra TA còn hợp pháp hóa cho Ngươi với những người dưới quyền sở hữu của Ngươi là nữ tù binh mà Allah đã ban cho Ngươi qua chiến tranh. Bên cạnh đó, Ngài còn cho phép Ngươi cưới những người phụ nữ như con gái của chú Ngươi, của cô Ngươi, của cậu Ngươi, của dì Ngươi và những người phụ nữ di cư cùng với Người từ Makkah đến Madinah. TA còn đặc biệt cho phép Ngươi cưới những người phụ nữ đức hạnh có đức tin tự nguyện hiến dâng đời mình cho Ngươi mà không cần đòi hỏi tiền cưới Mahhar nếu như Ngươi muốn cưới các nàng. Đó là ân điển đặc biệt TA giành riêng cho Ngươi, ngoài Ngươi ra trong cộng đồng của Ngươi không ai có được đặc ân đó. Quả thật, TA biết rõ điều đã ra lệnh cho những người có đức tin về hôn nhân là họ không được cưới vượt quá bốn người vợ. Và đó là những gì mà giáo lý TA đã quy định cho họ đó cũng là vấn đề đối với họ bởi gì những người phụ nữ tù binh nằm dưới quyền sở hữu của họ nếu như họ muốn tận hưởng những người phụ nữ đó thì thật sự không có con số giới hạn. Những gì TA đã cho phép Ngươi và điều không cho phép ngoài Ngươi đã được liệt kê rõ, do đó sẽ không có sự khó khăn hay khó dễ gì với Ngươi và quả thật, Allah là Đấng Hằng Tha Thứ cho đám bề tôi của Ngài biết hối cải và Ngài là Đấng Hằng Khoan Dung cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(33,51,'33:51','Sự trì hoãn đến từng nhà - hỡi Thiên Sứ - Ngươi được phép trì hoãn bất cứ người vợ nào của Ngươi tùy ý Ngươi muốn mà không cần nhất thiết phải đến qua đêm với nàng ta đúng lịch và Ngươi có thể tiếp bất cứ những người vợ nào mà Ngươi cảm thấy ưng ý và Ngươi cũng có thể đề nghị chuyện qua đêm với những người vợ của Ngươi mà điều đó Ngươi sẽ không có sự khiển trách. Điều đó là sự lựa chọn và sự mở rộng cho Ngươi dưới con mắt của những vợ của Ngươi để cho có sự thừa nhận của các nàng và đó cũng là những điều làm dịu lòng họ cho những gì mà Ngươi đã ban tất cả cho họ để cho họ hiểu biết rằng Ngươi là Người luôn có bổn phận với họ và không có sự tránh né trách nhiệm. Và Allah là Đấng Am Hiểu những gì nằm tận trong trái tim của các ngươi - hỡi những người đàn ông - đó cũng là một số xu hướng ganh tị của một số phụ nữ và một số sẽ không có. Quả thật, Allah Am Hiểu được tất mọi việc làm của đám bề tôi của Ngài, bởi vì không gì có thể che giấu được Ngài và Ngài là Đấng Rất Nhẫn Nại không có sự trừng trị thẳng tay ngay với họ mà Ngài luôn muốn họ quay đầu sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(33,52,'33:52','Ngươi không được phép - hỡi Thiên Sứ - kết hôn thêm với những người phụ nữ nào những người vợ mà Ngươi đã cưới, song Ngươi cũng không được phép li dị bất cứ người vợ nào của Ngươi hoặc Ngươi tự ý li dị một số người vợ của Ngươi rồi có ý định cưới một số người khác, dẫu cho Ngươi cảm thấy bị lôi cuốn bởi sắc đẹp của họ. Tuy nhiên Ngươi được phép tự do với các nữ nô lệ nằm trong tay của Ngươi mà không có giới hạn số lượng, quả thật, Allah giám sát tất cả mọi thứ. Giáo lý này nói lên giá trị của các bà mẹ của tín đồ có đức tin khi họ không bị Thiên Sứ li hôn họ.');
INSERT INTO "vn_mokhtasar" VALUES(33,53,'33:53','Hỡi những người có đức tin nơi Allah và tuân thủ theo giáo luật đã quy định cho các ngươi, rằng các ngươi không được tùy tiện bước vào nhà của Thiên Sứ ngoại trừ được Người mời gọi đến để dùng bữa ăn và cũng không được đến quá sớm khiến phải chờ đợi thức ăn nấu chín. Nếu như các ngươi là những người được mời đến dùng bữa ăn thì hãy đi vào và sau khi các ngươi đã dùng bữa xong thì chớ nên chần chừ ngồi lại rồi một số người các ngươi sẽ tán gẫu với nhau. Quả thật, cách ứng xử đó sẽ gây phiền hà cho Thiên Sứ - cầu xin sự ban bình an cho Người - và sẽ làm cho Người cảm thấy e ngại để mời các ngươi giải tán mà ngược lại Allah không e ngại với chân lý, Ngài hạ lệnh cho các ngươi phải giải tán không được gây phiền hà cho Thiên Sứ - cầu xin Allah ban bình an cho Người - về hành động ngồi lại đó. Một khi các ngươi muốn hầu chuyện với các người vợ của Thiên Sứ - cầu xin Allah ban bình an cho Người - vì nhu cần thiết thì các ngươi hãy nói thông qua một bức mang che lại và chớ có ý định là sẽ đối diện tận mắt để nhìn thấy họ và vì điều đó là sự bảo vệ danh dự cho họ và vì tôn trọng địa vị của Thiên Sứ - cầu xin Allah ban bình an cho Người - và chỉ có cách hầu chuyện như vậy mới giữ được tấm lòng của các ngươi được tinh khiết và tấm lòng của họ cũng được tinh khiết hơn; tránh cho lũ Shaytan thì thào xúi vào dục con tim của các ngươi và vợ Thiên Sứ. Và rồi có những điều mà các ngươi không nên làm - hỡi những ai có đức tin - các ngươi không được phép tán gẫu gây phiền hà cho Thiên Sứ của Allah và không được phép kết hôn với những người vợ của Thiên Sứ sau khi Người qua đời, bởi vì họ được tôn làm mẹ của tín đồ có đức tin, thế nên không ai lại kết hôn với mẹ mình bao giờ. Quả thật điều đó là một trong những hình thức gây phiền đến Thiên Sứ sau khi Người qua đời. Đối với Allah đó là một việc làm đại cấm kị.');
INSERT INTO "vn_mokhtasar" VALUES(33,54,'33:54','Quả thật, cho dù các ngươi có phô bày ra hết mọi việc làm hoặc các người có thầm giấu Ngài ở nơi tâm khảm của bản thân. Chắc chắn, những điều đó sẽ không có thể giấu kín được Ngài. Quả thật, Allah luôn am tường hết tất cả mọi việc, không có việc làm nào của các ngươi có thể che giấu được Ngài và không gì mà Ngài không biết. Và Allah luôn thưởng phạt phân minh đối với các ngươi nếu ai làm việc thiện tốt thì luôn nhận lấy điều tốt còn những ai làm điều ta ác sẽ nhận lấy những điều xấu xa tội lỗi');
INSERT INTO "vn_mokhtasar" VALUES(33,55,'33:55','Các nàng vợ của của Thiên Sứ sẽ vô tội khi không ăn măc che kín toàn thân lúc đối diện với cha, với con cái, với anh em trai, với những đứa con của anh em, với những đứa con trai của chị em vì mối quan hệ ruột thịt hoặc cùng chung bầu sữa. Tương tự các nàng cũng sẽ vô tội khi đối diện như thế với những người phụ nữ có đức tin khác như người giúp việc, những người nô lệ nằm dưới quyền kiểm xoát các nàng. Hãy kính sợ Allah - hỡi các nàng có đức tin - tuyệt đối tuân theo mệnh lênh của Ngài và tránh xa những gì mà Ngài nghiêm cấm. Quả thật, Ngài là Đấng Chứng Giám hết mọi việc và nhìn thấu được tâm can của các nàng.');
INSERT INTO "vn_mokhtasar" VALUES(33,56,'33:56','Quả thật, Allah luôn khen ngợi Thiên Sứ Muhammad - cầu xin Allah ban bình an cho Người - trước các Thiên Thần của Ngài và các Thiên Thần của Ngài luôn cầu nguyện cho Người. Hỡi những ai có đức tin nơi Allah và luôn dựa theo những quy định của giáo lý để thờ phượng Ngài! Hãy Solawat cho Thiên Sứ và gửi lời chào bình an đến cho Người.');
INSERT INTO "vn_mokhtasar" VALUES(33,57,'33:57','Quả thật, những ai dám quấy rầy Allah và Thiên Sứ của Ngài bằng lời lẽ hay hành động khó ưa thì chắc chắn chúng sẽ bị Allah xua đuổi và trục xuất ra khỏi lòng thương xót của Ngài ở trần gian này và lẫn Ngày Sau. Và Allah sẽ chuẩn bị cho chúng ở ngày sau là sự trừng phạt thật là nhục nhã ê trề đó cũng là hậu quả cho những kẻ không nghe lời cảnh báo của Ngài mà dám xúc phạm đến Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(33,58,'33:58','Còn những ai dám quấy rầy những tín đồ có đức tin dù nam hay nữ bằng những lời lẽ thiếu tế nhị hay hành động thiếu cân nhắc một cách vô cớ là xem như y đã xúc phạm đến họ. Chắc chắn, chúng sẽ cho chuốc vào mình một tội vu khống và đó cũng là một trọng tội quá rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(33,59,'33:59','Hỡi Thiên Sứ! Hãy bảo những người vợ của Ngươi, những đứa con gái của Ngươi và những người vợ của những người có đức tin rằng: "Hãy chỉnh chu ăn mặc rộng rãi những chiếc áo choàng của họ đừng để cho lộ ra các phần cơ thể nhạy cảm khi đối diện với những người đàn ông lạ; điều đó nó sẽ làm dễ dàng để nhận biết họ là phụ nữ tự do nhằm tránh khỏi mọi xúc phạm giống như phụ nữ nô lệ thường bị. Quả thật, Allah là Đấng Hằng Tha Thứ những điều tội lỗi cho đám bề tôi của Ngài biết ăn nan sám hối và là Đấng đầy lòng Khoan Dung."');
INSERT INTO "vn_mokhtasar" VALUES(33,60,'33:60','Nếu những kẻ đạo đức giả hay những tên giả mạo đức tin và những kẻ mang trên mình sự nghi ngờ chỉ biết chìu theo sở thích xàm bậy rồi loan đi khắp cả thành phố Madinah những tin tức giả dối để làm chia rẽ những người có đức tin. TA ra lệnh cho Ngươi - hỡi Thiên Sứ - hãy giám sát chặt chẻ chúng, rồi TA sẽ ban cho Ngươi quyền quản lý chúng bằng cách trừng trị chúng và TA sẽ giao quyền lực cho Ngươi để làm điều đó. Sau đó, chúng sẽ không có tư cách làm láng giềng với Ngươi ngoại trừ một thời gian ngắn; rồi TA sẽ tiêu diệt hết bọn chúng hoặc TA sẽ trục xuất chúng bởi vì chúng là những kẻ làm ô uế trên trái đất này.');
INSERT INTO "vn_mokhtasar" VALUES(33,61,'33:61','Chúng sẽ bị trục xuất ra khỏi lòng thương xót của Allah, ở bất cứ nơi nào nếu chúng bị phát hiện, sẽ bắt giữ lấy chúng và chúng sẽ bị giết chết không một chút thương tiếc; đó là cách trừng trị đối với những kẻ đạo đức giả và những kẻ xàm bậy làm ô uế trên trái đất này.');
INSERT INTO "vn_mokhtasar" VALUES(33,62,'33:62','Đấy chính là đường lối của Allah đối với những tên đạo đức giả nếu như chúng có biểu hiện là những kẻ giả mạo đức tin và đó cũng là đường lối xác thực của Allah sẽ không bao giờ có sự nhân nhượng nào nếu phát hiện ra được những kẻ phản bội.');
INSERT INTO "vn_mokhtasar" VALUES(33,63,'33:63','Những người đa thần hỏi Ngươi - hỡi Thiên Sứ - về câu hỏi mà chúng thường phủ nhận và còn bán tính bán nghi về nó và tương tự người Do Thái cũng đã hỏi bao giờ đến Ngày Tận Thế ? Hãy bảo bọn chúng: "Chỉ có Allah mới biết rõ về Ngày Tận Thế còn Ta thì không hề biết gì về nó." Và điều gì đã làm cho Ngươi có thể cảm nhận được về nó - hỡi Thiên Sứ - "Quả thật, ngày Tận Thế nó sắp diễn ra rất là gần."');
INSERT INTO "vn_mokhtasar" VALUES(33,64,'33:64','Quả thật, Allah đã trục xuất những tên phủ nhận đức tin ta khỏi lòng thương xót của Ngài và Ngài còn chuẩn bị cho bọn chúng vào Ngày Phục Sinh ngọn lửa cháy bùn nơi Hỏa Ngục đang chờ sẵn bọn chúng.');
INSERT INTO "vn_mokhtasar" VALUES(33,65,'33:65','Chúng sẽ gánh chịu sự trừng của Hỏa Ngục muôn đời và chúng sẽ không bao giờ tìm thấy một ai có thể che chở cho bọn chúng hay tìm ra một ai cứu tinh có thể giúp bọn chúng tránh khỏi sự trừng phạt đó.');
INSERT INTO "vn_mokhtasar" VALUES(33,66,'33:66','Vào Ngày Phán Xét mọi khuôn mặt của bọn chúng bị lất úp xuống nằm trong Hỏa Ngục, bọn chúng than van một cách thật thảm thiết trong sự hối hận:" Ôi! Thật khổ thân chúng tôi trong cuộc sống trần gian này quá. Phải chi, chúng tôi chịu phục tùng mọi mệnh lệnh của Allah và vâng lời Thiên Sứ cho những gì mà Người mang đến từ Thượng Đế của Người."');
INSERT INTO "vn_mokhtasar" VALUES(33,67,'33:67','Và bọn chúng đem ra một cái cớ và sự giả dối rồi bọn chúng thưa;" Lạy Thượng Đế của đám bầy tôi! Quả thật, bầy tôi đã nghe lời xúi giục của những tên đầu sỏ và những vị lãnh tụ của bầy tôi, thế rồi đã làm cho bầy tôi đi lầm lạc trên con đường ngay chính."');
INSERT INTO "vn_mokhtasar" VALUES(33,68,'33:68','"Lạy Thượng Đế của bầy tôi! Bầy tôi khẩn cầu Ngài trừng phạt những kẻ đầu sỏ và những nhà lãnh tụ đã dẫn dắt đám bầy tôi đi lầm lạc trên con đường ngày chính thật thẳng tay với hình phạt tăng lên gấp đôi cho bọn chúng vì đã dẫn dắt đám bầy tôi đi lạc lối và hãy trực xuất bọn chúng ra khỏi lòng thương xót của Ngài trong sự cương quyết của Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(33,69,'33:69','Hỡi những ai có đức tin nơi Allah và luôn làm theo giáo lý của Ngài! Chớ nên quấy rầy Thiên Sứ của các người giống như những kẻ đã từng quấy rầy Musa bởi vì chúng đã dám xúc phạm sỉ nhục Musa mà đồn rằng trên cơ thể của Người có điểm xấu hổ, thế là Allah đã giải hàm oan cho Y khỏi những điều vu khống mà chúng đã nói, cơ thể của Ngươi hoàn toàn lành lặng. Trong khi trước đây Musa là một người bề tôi đáng kính của Allah, Y không bao giờ dám làm trái mệnh lệnh của Ngài và cũng chưa bao giờ làm Ngài thất vọng trong nhiệm vụ được giao phó.');
INSERT INTO "vn_mokhtasar" VALUES(33,70,'33:70','Hỡi những ai có đức tin nơi Allah và luôn làm theo giáo lý của Ngài! Hãy kính sợ Allah bằng cách tuyệt đối tuân theo mọi mệnh lệnh của Ngài và luôn kiềm hãm trước những điều mà Ngày nghiêm cấm và luôn là những người nói ra những lời đúng đắn và thành thật nhất.');
INSERT INTO "vn_mokhtasar" VALUES(33,71,'33:71','Quả thật, nếu các ngươi biết kính sợ Allah và nói năng chuẩn mực, thì Ngài sẽ cải thiện việc làm cho các ngươi, chấp nhận mọi việc làm thiện đó của các ngươi, còn tha thứ mọi tội lỗi cho các ngươi mà không còn bắt tội của các ngươi. Và những ai tuân theo Allah và Thiên Sứ của Ngài thì chắc chắn sẽ thành đạt một cách vẻ vang mà không có sự thành đạt nào sánh bằng và đó cũng là sự chiến thắng mà được Allah hài lòng nhất mà được bước thẳng vào Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(33,72,'33:72','Quả thật, TA đã giao phó mọi trách nhiệm và ủy thác mọi trọng trách bảo vệ những khối tài sản và những điều thầm kín rất quan trọng đến cho các tầng trời, trái đất và núi non thế nhưng mọi thứ đều từ chối gánh vác trọng trách vì chúng khiếp sợ gánh vác không nỗi, sợ sự trừng phạt của Allah, thế nhưng chỉ có con người lại dám đảm nhận gánh vác mọi điều đó. Quả thật, một điều hết sức là sai quấy cho bản thân chúng không hề biết hậu quả của việc gánh vác trọng trách đó ra sao thật là một sự ngu muội.');
INSERT INTO "vn_mokhtasar" VALUES(33,73,'33:73','Con người gánh vác trọng trách này là do Allah đã an bày; để cho Allah trừng trị những tên đạo đức giả cả nam lẫn nữ, những người đa thần cả nam lẫn nữ; bọn chúng là những kẻ đạo đức giả và những người đa thần đối với Allah, và để Allah lượng thứ cho những người có đức tin dù nam hay nữ đang gánh vác trên mình một trọng trách thật tốt đẹp. Và Allah là Đấng Hằng Tha Thứ cho mọi tội lỗi của đám bầy tôi của Ngài biết ăn năn hối cải và Ngài cũng là Đấng Rất Mực Khoan Dung cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(34,1,'34:1','Alhamdulillah, xin tạ ơn Allah Đấng mà mọi vạn vật trong các tầng trời và mọi vạn vật dưới đất, mọi tạo hóa, mọi thế lực và mọi kế hoạch chu đáo đều là của Ngài, Ngài sở hữu luôn cả mọi lời ca tụng ở Ngày Sau. Ngài là Đấng Rất Sáng Suốt trong mọi tạo vật và trong sự cai quản của Ngài và là Đấng Am Tường hết tất cả hoàn cảnh của đám bầy tôi của Ngài không có gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(34,2,'34:2','Ngài biết rõ mọi vật chui vào lòng đất như từng giọt nước hay hạt giống, Ngài cũng nắm rõ những chồi non mọc lên khỏi lòng đất và các thứ khác, Ngài biết rõ những gì rời từ trên bầu trời rơi xuống như những hạt mưa, sự chuyển động của các vị Thiên Thần và những bổng lộc, Ngài biết rõ mọi vật bay lên bầu trời như các vị Thiên Thần, những việc làm của đám bầy tôi của Ngài và mọi linh hồn của chúng. Ngài là Đấng Hằng Khoan Dung cho đám bầy tôi đầy đức tin của Ngài, Đấng Hằng Tha Thứ tội lỗi cho những ai biết ăn năn sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(34,3,'34:3','Và những kẻ không có đức tin vào Allah bảo rằng:" Ngày Tận Thế sẽ không bao giờ đến với chúng tôi." Hãy bảo bọn chúng - hỡi Thiên Sứ - "Không đâu, thề bởi Allah, chắc chắn ngày Tận Thế sẽ ập xuống bọn chúng mà bọn chúng từng láo khoét cho rằng sẽ không bao giờ xảy ra, thế nhưng không ai biết được bao giờ sẽ xảy ra chỉ ngoại trừ Allah. Bởi vì, Ngài là Đấng biết rõ những điều huyền bí của ngày Tận Thế và còn ngoài điều đó nữa, không có một sự huyền bí nào mà Ngài không nắm rõ. Ngài nắm rõ trọng lượng của loài kiến nhỏ bé nhất trên bầu trời và trong trái đất và rồi không có sự kỳ bí nào cho dù nó bé nhỏ như sự việc đã được nhắc đến hay nó to lớn hơn mà nó lại không được ghi chép sẵn trong một Quyển Sổ một cách rõ ràng. Và tất cả mọi điều được lưu lại trên Quyển Sổ Mẹ được gìn giữ bảo quản một cách rất cẩn thận cho đến ngày Phán Xử."');
INSERT INTO "vn_mokhtasar" VALUES(34,4,'34:4','Allah xác định lại những gì đã biên chép trên Quyển Sổ Mẹ với mục đích Ngài tưởng thưởng cho những ai có đức tin ở nơi Allah và luôn làm những việc thiện. Họ là những người tiêu biểu đặc trưng bởi những phẩm chất tốt đẹp nhất rồi sẽ được Allah tha thứ mọi tội lỗi cho họ mà không có sự tính toán và sẽ được ban phát bổng lộc vô định lượng. Họ chính là những người của Thiên Đàng vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(34,5,'34:5','Còn những kẻ luôn tìm mọi kế sách để phá hoại những gì Allah đã mặc khải qua những câu Kinh. Họ nói về các dấu hiệu đó là ma thuật, họ bảo Thiên Sứ của chúng ta là tên thầy bói, tên phù thủ, nhà thơ. Tất cả những ai có những thuộc tính vừa nêu chắc chắn vào ngày Phán Xử sẽ chịu một hình phạt thật là khủng khiếp của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(34,6,'34:6','Và những nhà học giả thuộc nhóm Sahabah và những ai tin tưởng thuộc các học giả dân Kinh Sách thấy rằng những lời mặc khải mà Allah ban xuống cho Ngươi là Chân Lý mà không có chút nghi ngờ về nó. Và đó là sự chỉ dẫn thiên hạ đến với con đường của Đấng Toàn Năng mà không có một ai có thể kháng cự và cũng là Đấng Rất Đáng Ca Tụng ở trần gian này và ngày sau.');
INSERT INTO "vn_mokhtasar" VALUES(34,7,'34:7','Những tên phủ nhận Allah bảo với một số người trong bọn chúng bằng lời lẽ cay cú và nhạo báng về những gì được Thiên Sứ - cầu xin sự bình an cho Người - mang đến: "Há! Quý vị có muốn chúng tôi chỉ cho quý vị một người đàn ông, Y sẽ thông báo cho các quý vị về mọi chuyện xong thì quý vị đã chết xương rã mục hết dưới đất sau đó quý vị được phục sinh sống trở lại ?"');
INSERT INTO "vn_mokhtasar" VALUES(34,8,'34:8','Và chúng bảo: "Có lẽ người đàn ông đó đã bịa ra câu chuyện vô lý rồi đổ thừa cho Allah vì Y cho rằng sẽ dựng chúng ta sống trở lại sau chúng ta đã chết hay Y chỉ là một kẻ điên khùng nói ra không đúng sự thật chăng ?" Sự thật không giống như chúng từng tuyên bố, ngược lại đối với những người không có đức tin ở Ngày Sau chắc chắn sẽ phải bị trừng phạt thật đau đớn vào Ngày Phán Xét cho sự lầm lạc rất xa trên con đường Chân Lý trên trần gian này.');
INSERT INTO "vn_mokhtasar" VALUES(34,9,'34:9','Há những kẻ láo xược phủ nhận việc phục sinh không nhìn thấy đất trước mặt họ và bầu trời bên trên họ ư? Nếu muốn, TA thừa khả năng làm đất rung chuyển nuốt mất họ sâu trong lòng đất, và nếu muốn TA khiến mảnh vụng của bầu trời cho rơi xuống đè chết bọn chúng. Quả thật, bấy nhiêu đó đủ làm minh chứng dứt khoát để cảnh tĩnh đám bề tôi sớm biết quay về ăn năn sám hối tuân theo Thượng Đế của y, tất cả mọi điều đó chứng minh quyền năng của Allah. Bởi Ngài có khả năng dựng các ngươi sống lại sau cái chết và xé tan nát thân hình của các ngươi ra từng mảnh vụng.');
INSERT INTO "vn_mokhtasar" VALUES(34,10,'34:10','Và chắc chắn, TA đã ban cho Dawood - cầu xin bình an cho Người - sứ mạng và vương quyền, và TA phán bảo ngọn núi rằng: "Hỡi núi non! Hãy cùng hòa nhập vào Dawood để tán dương Allah, và hỡi chim chóc cũng làm như thế và TA cũng hóa sắt trở nên mềm dẻo để cho Y tùy ý làm bất cứ công cụ."');
INSERT INTO "vn_mokhtasar" VALUES(34,11,'34:11','Hãy sáng tạo ra những kiện tác - hỡi Dawood - "Hãy chế tạo chiếc giáp sắt dùng để chiến đấu với kẻ thù sao cho nó phù hợp cử động chân tay và dưới cằm, không mềm mại vô tác dụng và không cứng ngắt không mặc được và hãy chăm lo việc hành đạo. Quả thật, mọi điều mà các ngươi làm luôn được TA nhìn thấu, không gì có thể che giấu được Ngài và các ngươi sẽ nhận được một phần thưởng rất hậu hĩnh cho việc làm đó."');
INSERT INTO "vn_mokhtasar" VALUES(34,12,'34:12','Và TA bắt gió phục tùng Sulayman con trai của Dawood - cầu xin bình an cho hai người họ -. Gió giúp Người duy chuyển với tốc độ rất nhanh với một buổi sáng hoặc buổi chiều bằng thời gian di chuyển của một tháng theo cách bình thường. Và TA đã hóa một ngọn suối bằng thau nung chảy để cho Sulayman tiện sáng chế ra những gì mà Y muốn và Ngài khiến cho một trong những loài Jinn làm việc cho Y dưới chỉ thị của Thượng Đế Y. Và nếu tên Jinn nào dám cãi mệnh lệnh TA giao phó việc làm đó thì chắc chắn nó sẽ nếm hình phạt của ngọt lửa đang cháy bập bùng.');
INSERT INTO "vn_mokhtasar" VALUES(34,13,'34:13','Loài Jinn làm ra nhiều sản phẩm cho Sulayman như những gì Người muốn như: Những Masjid để dâng lễ nguyện Salah, những cung điện nguy nga tránh lệ làm theo ý muốn của Y, những cái chén bát lớn giống như bể chứa nước thật to và những cái chảo nấu ăn thật to đặt cố định mà không tài nào di chuyển cho đặng và Ngài phán bảo chúng - hãy làm hỡi dòng tộc Dawood - với tấm lòng tri ân cảm tạ Allah vì những hồng ân mà Ngài đã ban cho các ngươi, thế nhưng chỉ có một số ít bầy tôi của TA tỏ lòng biết ơn TA cho những hồng ân đó của TA.');
INSERT INTO "vn_mokhtasar" VALUES(34,14,'34:14','Và khi TA quyết định cho Sulayman chết, không một dấu hiệu nào cho thấy Sulayman đã chết cho đến khi một loại côn trùng ở dưới đất ăn mòn chiếc gậy mà Y đã tựa lên nó. Đến khi Y ngã xuống, nó khẳng định một điều là loài Jinn vốn không biết điều huyền bí. Nếu như chúng biết được điều này xảy ra là chúng đã không sự trừng phạt ê chề đầy nhục nhã như thế. Chúng luôn tưởng rằng Sulayman vẫn còn sống và đang giám sát chúng.');
INSERT INTO "vn_mokhtasar" VALUES(34,15,'34:15','Chắc chắn, trong bộ tộc Saba'' một nơi mà họ đang sinh sống đã xảy ra một dấu hiệu hết sức rõ ràng được Allah an bài và ban bố mọi điều cho họ; đó chính là hai ngôi vườn :" Một trong hai ngôi vườn đó nằm bên phải và ngôi vườn còn lại nằm bên trái," TA phán bảo chúng :" Hãy ăn những bổng lộc của Thượng Đế của các ngươi và hãy tạ ơn Ngài về bổng lộc đó của Ngài; đây chính là một thành phố tốt đẹp và có được một Thượng Đế Hằng Tha Thứ mọi tội lỗi cho những ai biết ăn năn sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(34,16,'34:16','Thế nhưng họ là những kẻ vong ân không biết tạ ơn Allah hay có một chút đức tin vào bức thông điệp của Ngài. Bởi thế, Ngài đã trừng phạt bọn chúng bằng cách làm thay đổi những hồng ân của Ngài giành cho họ. Vì vậy, Ngài đã gián xuống chúng một trận lũ lụt nhấn chìm hết tất cả hoa màu vườn tược và biến hai mảnh vườn của họ mọc ra những loại quả chát đắng, mọc những loại cây không quả và cây táo gai.');
INSERT INTO "vn_mokhtasar" VALUES(34,17,'34:17','TA đã làm thay đổi - tất cả mọi ân phúc mà TA ban cho chúng - nguyên nhân là vì chúng là những tên phản bội và khướt từ việc tạ ơn và Ngài đã không trừng trị bằng một hình phạt thật khủng khiếp ngoại trừ đối với những kẻ đáp trả lại những ân sủng của Allah bằng hành động vô đức tin');
INSERT INTO "vn_mokhtasar" VALUES(34,18,'34:18','Và TA đã dựng lên những thị trấn nằm giữa thị dân của Saba'' ở Yamen và giữa vùng đất Shaam - được chia thành bốn nước ngày nay gồm: Syria, Palestine, Libang và Jordan - được TA ban cho hồng phúc, điều đó giúp mọi người thuận tiện tới lui giữa Yamen và Shaam mà không gặp bất cứ khó khăn gì, rồi TA phán bảo chúng:" Các ngươi hãy tự do đi lại qua các địa điểm đó tùy ý các ngươi muốn kể cả ban đêm hay ban ngày, các ngươi sẽ luôn cảm thấy rất là an toàn không sợ kẻ thù tấn công hoặc đói khát."');
INSERT INTO "vn_mokhtasar" VALUES(34,19,'34:19','Nhưng họ lại phàn nàn về những ân huệ của Allah đã tạo sự thuận lợi cho những chiến đi du hành của chúng, chúng thưa:" Lạy Thượng Đế của bầy tôi! Cầu xin Ngài làm cho những chằng đường du hành của chúng tôi thêm cách xa với những thị trấn để chúng tôi có thể trãi nghiệm được cái sự mệt mỏi của những chuyến du hành, để thể hiện được sự uy nghi bề thế của chúng tôi. Thế nhưng, đó chính là những điều mà họ chuốc lấy tai ương lên bản thân họ bởi sự kiêu căng trước ân huệ của Allah cho việc miễn cưỡng tạ ơn Ngài và có lòng ganh tị với người nghèo. Bởi thế, TA đã lấy họ làm thành những câu chuyện để lại cho những thế hệ sau họ và TA cũng phân tán toàn bộ thị trấn của họ đi khắp mọi nơi không một ai có thể kết nối với một ai. Quả thật, mọi điều được đề cặp - đó cũng là món quà TA giành tặng thị dân Saba'' sau đó là sự hà khắc đối với đám người phủ nhận và đám người vong ân trong họ - mọi điều dấu hiệu cho từng người biết kiên nhẫn tuân theo Allah, tránh xa những điều tội lỗi của Ngài và sự thử của Ngài đó là những ai biết tạ ơn trước những ân huệ của Allah giành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(34,20,'34:20','Và điều suy nghĩ của họ đã được Iblis ủng hộ, hắn có thể cám dỗ họ đi lạc lối khỏi sự thật. Bởi thế, họ làm theo hắn trong sự vô đức tin và lầm lạc ngoại trừ một số ít người có đức tin, họ khiến chúng thất vọng khi theo cám giỗ làm theo hắn.');
INSERT INTO "vn_mokhtasar" VALUES(34,21,'34:21','Và thật ra, Iblis không có một chút thế lực nào có thể dẫn dắt họ đi lạc lối, thế nhưng chúng được TA ban cho chút quyền lực mới có thể cám giỗ được họ, và đó cũng là điều mà TA muốn biết rõ những ai sẽ là người tin tưởng ở Ngày Sau và những phần thưởng ở nơi đó so với ai trong họ là người còn tính hoài nghi về Ngày Sau. Và Thượng Đế của Ngươi - hỡi Thiên Sứ - là Đấng Cai Quảng hết tất cả mọi điều, Đấng Giám Sát tất cả mọi việc làm của đám bầy tôi của Ngài và ban thưởng thỏa đáng cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(34,22,'34:22','Hãy bảo chúng - hỡi Thiên Sứ - những người đa thần rằng: "Các ngươi hãy cầu nguyện những ai mà các ngươi tuyên bố là các vị thần linh mà không phải là Allah để chúng mang lại lợi ích cho các ngươi hoặc giúp các ngươi tránh được mọi điều tai ương. Bởi vì, chúng không có khả năng kiểm soát được dù chỉ là trọng lượng của một hạt nguyên tử hay hạt cải nằm trong bầu trời và trái đất, chúng sẽ không bao giờ có sự vướng víu phần chia nào cùng với Allah và Allah cũng không ủng hộ hay bổ nhiệm một ai. Bởi vì, Ngài là Đấng Giàu Có không cần sự hợp tác cùng ai hay ai đó bổ nhiệm.');
INSERT INTO "vn_mokhtasar" VALUES(34,23,'34:23','Và sự can thiệp không hề có giá trị với Allah ngoại trừ những ai được Ngài cho phép y, Allah không cho phép bất kỳ ai quyền can thiệp ngoại trừ những ai được sự hài lòng của Ngài; vì đó là sự vĩ đại của Ngài, và từ sự vĩ đại của Ngài khi Ngài phán bảo ở tận trời cao thì các vị Thiên Thần sẽ vỗ đôi cánh của họ trong sự rung sợ mà phục tùng trước lời phán của Ngài mãi cho đến khi các vị Thiên Thần hoàn hồn trở lại rồi họ bảo với đại Thiên Thần Jibril: "Thượng Đế của các người phán điều gì vậy ?" Đại Thiên Thần Jibril bảo: "Chân Lý." Bởi vì, Ngài là Đấng Tối Cao và đầy Quyền Lực, Đấng thật Vĩ Đại mọi thứ điều là của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(34,24,'34:24','Hãy bảo chúng - hỡi Thiên Sứ - với người đa thần rằng: "Ai cung cấp mọi bổng lộc cho các ngươi từ trên trời xuống bằng những trận mưa dồi dào đỗ xuống đất để làm cho hoa màu cây trái đâm chòi nãy lộc ?" Hãy bảo chúng: "Allah mới là Đấng cung cấp bổng lộc đó cho các ngươi. Và chắc hẳn rằng chúng tôi hoặc là quý vị - hỡi những người đa thần - ai sẽ là người công khai đi đúng đường hoặc đã đi lệch lạc. Bởi vì, một trong chúng ta sẽ không ai có thể tránh khỏi cho được điều đó và chắc hẳn người được chỉ đạo là người có đức tin và người lầm lạc chính là người đa thần');
INSERT INTO "vn_mokhtasar" VALUES(34,25,'34:25','Hãy bảo chúng - hỡi Thiên Sứ -: "Quý vị sẽ không bị tra hỏi vào Ngày Phán Xét về tội lỗi của chúng tôi đã từng mắc phải và chúng tôi cũng sẽ không bị tra hỏi về những việc làm mà quý vị đã từng làm."');
INSERT INTO "vn_mokhtasar" VALUES(34,26,'34:26','Ngươi hãy bảo họ:" Allah sẽ tập họp chúng tôi và cả quý vị trở lại vào Ngày Phán Xét sau đó chính Ngài sẽ phán xét giữa chúng tôi và quý vị một cách thật công bằng chính trực. Bởi vì, sự thật không tồn tại ở kẻ xàm bậy vì Ngài chính là Đấng Thẩm Phán mà là một vị Đại Thẩm Phán công bằng và là Đấng Toàn Tri');
INSERT INTO "vn_mokhtasar" VALUES(34,27,'34:27','Hãy bảo họ - hỡi Thiên Sứ -: "Hãy chỉ cho Ta đâu là những tên mà chúng cho là đối tác ngang hàng với Allah rồi chúng đem ra thờ phượng cùng với Ngài." Ta khẳng định sẽ không có ai, cũng không bao giờ có điều giống như những gì mà các ngươi đã tưởng tượng ra rằng Allah có đối tác. Không! Ngài là Allah, Đấng Hùng Mạnh mà không một ai có thể chiến thắng được Ngài, Đấng Chí Minh trong mọi tạo vật của Ngài, Đấng Đầy Quyền Lực và Đấng Cai Quảng tất cả.');
INSERT INTO "vn_mokhtasar" VALUES(34,28,'34:28','Và TA đã cử phái Ngươi - hỡi Thiên Sứ - làm một Người đến với toàn thể nhân loại để báo tin mừng cho những ai có lòng kính sợ rằng họ được Thiên Đàng, và bên cạnh đó cảnh báo những người vô đức tin và hư đốn về Hỏa Ngục. Thế nhưng đa số người trong thiên hạ không hể hay biết, nếu họ biết là họ đã không phủ nhận Ngươi bao giờ.');
INSERT INTO "vn_mokhtasar" VALUES(34,29,'34:29','Và người đa thần lại thúc giục hành phạt đã được khuyế cáo mau đến trừng phạt họ nên cao giọng bảo: "Bao giờ lời hứa của sự trừng phạt sẽ xảy ra nếu các ngươi nói đó là sự thật, nếu các ngươi tuyên bố rằng điều đó là thật."');
INSERT INTO "vn_mokhtasar" VALUES(34,30,'34:30','Hãy bảo chúng - hỡi Thiên Sứ - những ai mong muốn hình phạt diễn ra nhanh chóng:" Quý vị cứ an tâm cho cuộc hẹn vào ngày cố định; nó sẽ không bao giờ diễn ra trễ nãy với quý vị dù là một giây hoặc cũng không đến sớm hơn dù là một khắc. Đó chính là Ngày Phán Xét."');
INSERT INTO "vn_mokhtasar" VALUES(34,31,'34:31','Và những tên phủ nhận Allah lên tiếng: "Chúng tôi không bao giờ tin tưởng vào Thiên Kinh Qur''an mà do Muhammad đã khẳng định rằng Nó được thiên khải từ Allah và cũng không tin vào bất cứ Kinh Sách nào được thiên khải trước đây. Giá mà Ngươi - hỡi Thiên Sứ - có mặt ở hoàn cảnh lúc những tên làm điều bất công đó đứng trước Thượng Đế của chúng vào Ngày Phán Xét bị hỏi tội, Ngươi sẽ thấy được tình cảnh lúc đó của chúng sẽ đổ trách nhiệm cho nhau. Những kẻ yếu thế bảo những kẻ từng đàn áp họ trước đây ở nơi cõi trần: "Nếu không bị các ông đàn áp trước đây là chúng tôi đã trở thành những người có đức tin nơi Allah và Thiên Sứ của Ngài rồi."');
INSERT INTO "vn_mokhtasar" VALUES(34,32,'34:32','Những tên được đi theo thuộc đám người tự cao trước chân lý đáp lại những người yếu thế đi theo họ rằng: "Phải chăng, chúng tôi đã cấm cản các ngươi tiếp nhận sự chỉ đạo của Muhammad mang đến cho các ngươi ư?" Không, đúng hơn các ngươi là những tên hư đốn và quậy phá.');
INSERT INTO "vn_mokhtasar" VALUES(34,33,'34:33','Những ngươi yếu thế bảo với những người mà họ đi theo trước đây là đám người ngông cuồn tự đại trước chân lý rằng: "Không, các ngươi toan tính cả ngày lẫn đêm đẻ ngăn cản chúng tôi đến với chân lý, song còn ra lệnh chúng tôi phải phủ nhận Allah để hướng đến việc thờ phượng những tạo vật thay vì thờ Ngài." Họ đã âm thầm hối hận cho hành động vô đức tin của mình ở trần gian kể từ khi họ chứng kiến hình phạt và biết được mình sẽ bị trừng phạt. TA sẽ chòng những chiếc gông vào cổ của những kẻ phủ nhận đức tin và những hậu quả mà họ phải chịu là phần thưởng dành cho hành động vộ đức tin và tội lỗi mà họ đã gây ra ở trần gian"');
INSERT INTO "vn_mokhtasar" VALUES(34,34,'34:34','Và không một Thiên Sứ nào được TA cử phái đến cho những thị trấn để khuyến cáo dân làng về hành phạt của Allah ngoại trừ họ bị những tên hóng hách có thế lực, quyền hành và giàu có lên tiếng bảo: "Quả thật, những gì mà các quý vị mang đến - hỡi Thiên Sứ - chắc chắn bọn ta sẽ không tin."');
INSERT INTO "vn_mokhtasar" VALUES(34,35,'34:35','Và những kẻ có quyền hành tỏ ra ngông cuồng kiêu ngạo bảo rằng: "Chúng tôi, có rất là nhiều tài sản và con cháu. Thế nên, những gì mà các người thống cáo rằng chúng tôi sẽ bị trừng phạt thật là một điều vô lý. Chắc chắn chúng tôi sẽ không bao giờ bị trừng trị ở trần gian này và cũng như ở Ngày Sau."');
INSERT INTO "vn_mokhtasar" VALUES(34,36,'34:36','Hãy bảo chúng - hỡi Thiên Sứ - với những tên kiêu ngạo về việc phân phát ân huệ:" Thượng Đế của Ta, Đấng Hiển Vinh và Tối Cao muốn nới rộng bổng lộc cho ai tùy ý Ngài nhằm thử thách họ có biết mang ơn hay vong ân, và Ngài thu hẹp bổng lộc lại với ai Ngài muốn để Ngài thử thách xem họ biết kiên nhẫn hay oán trách ? Tuy nhiên hầu hết thiên hạ không hề hay biết Allah là Đấng Chí Minh; Ngài đã an bày như thế vì mục đích vĩ đại, nhưng có người biết và có người không."');
INSERT INTO "vn_mokhtasar" VALUES(34,37,'34:37','Và không phải khối tài sản hay con cháu của các ngươi đã làm niềm kiêu hãnh dẫn lối các ngươi đến sự hài lòng của Allah, ngược lại là những ai tin tưởng vào Allah và chăm lo việc hành đạo thì chắc chắn những người đó mới xứng đáng nhận được phần thưởng nhân đôi. Về loại tài sản khiến y được gần Allah hơn là loại được chi tiêu trên con đường chính đạo của Allah và lời cầu xin của con cháu dành cho y. Đối với những người có đức tin chăm lo việc hành đạo sẽ được tưởng thưởng bội phần cho công sức mà họ bỏ ra; và họ sẽ ở một ngôi nhà thật an toàn cao vút ở nơi Thiên Đàng mà không còn lo sợ sẽ bị Ngài trừng phạt hoặc không bị chết cũng như sự hưởng thụ không bị gián đoạn.');
INSERT INTO "vn_mokhtasar" VALUES(34,38,'34:38','Về những tên phủ nhận đức tin luôn hăng hái dùng mọi thủ đoạn để xua đẩy thiên hạ ra khỏi các dấu hiệu của TA và luôn tìm mọi cách để đạt đến mục tiêu. Chúng là những tên thất bại ở trần gian, còn ở Ngày Sau chúng phải nhận lấy một cái hình phạt thật xứng đáng giành cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(34,39,'34:39','Hãy bảo chúng - hỡi Thiên Sứ -: "Quả thật, Thượng Đế của Ta tùy ý nới rộng bổng lộc cho ai trong đám bầy tôi của Ngài và cũng như thu hẹp bổng lộc lại những ai mà Ngài muốn. Và bất cứ chi tiêu nào chi dùng trong con đường chính nghĩa của Allah, thì Allah sẽ bù lại cho các ngươi ngay ở trần gian này bằng thứ tốt đẹp hơn và ở Ngày Sau sẽ được phần thưởng thật bao la. Và Allah Hiển Vinh là Đấng Cung Dưỡng Tốt Nhất, thế nên ai mong muốn bổng lộc thì hãy cầu xin Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(34,40,'34:40','Và Ngươi hãy nhớ - hỡi Thiên Sứ - vào Ngày Phán Xét mà Allah sẽ tập hợp tất cả lại. Sau đó, Đấng Hiển Vinh thẩm vấn các Thiên Thần để xỉ nhục đám người đa thần: "Há! Phải chăng, các ngươi là những thần linh mà chúng đã từng thờ phượng ở trần gian thay vì Allah phải không ?"');
INSERT INTO "vn_mokhtasar" VALUES(34,41,'34:41','Các Thiên Thần thưa: "Thật vinh quang và trong sáng thay Ngài! Ngài chính là Chủ Nhân của bầy tôi chứ không phải là chúng. Giữa bầy tôi và họ không có mối quan hệ với nhau. Không, đúng hơn là những người đa thần này đã thờ phượng lũ Shaytan, chúng đã biến dạng thành thiên thần để dụ dỗ thiên hạ thờ phượng chúng và đa số thiên hạ đã tin tưởng vào chúng."');
INSERT INTO "vn_mokhtasar" VALUES(34,42,'34:42','Vào Ngày Phục Sinh và thanh toán thì không một thần linh từng được thờ phượng ở trần gian có thẩm quyền giúp được ai hoặc hại được ai trước Allah. Và TA phán bảo những tên bất công lên chính bản thân chúng bằng sự phủ nhận đức tin và tội lỗi khác rằng: "Các ngươi hãy nếm đi hình phạt của Hỏa Ngục mà các ngươi đã từng cho những gì là giả tạo ở dương thế."');
INSERT INTO "vn_mokhtasar" VALUES(34,43,'34:43','Và khi những câu Kinh mà TA mặc khải xuống cho Thiên Sứ của TA được xướng đọc rõ ràng cho những người đa thần nghe thì họ phủ nhận tất cả mà bảo: "Người đàn ông này chỉ muốn áp dụng những gì Y mang đến để thay đổi các vị rời khỏi những gì mà tổ tiên của các vị đã thờ phượng.," Và bọn chúng nói tiếp: "Còn lời phán trong Thiên Kinh Qur''an đó cũng chẳng qua là do Y khéo bịa đặt ra rồi đổ thừa cho Allah." Và rồi những kẻ phủ nhận Allah nói về Thiên Kinh Qur''an vốn được thiên khải từ Allah: "Thứ này chẳng khác nào là trò yêu thuật hết sức là tinh vi; nhằm để chia rẽ tình cảm cặp vợ chồng và tình thân ruột thịt cha con."');
INSERT INTO "vn_mokhtasar" VALUES(34,44,'34:44','Và TA không hề ban xuống cho người Quraish bất cứ quyển Kinh Sách để chúng tự đọc để có thể nhận biết Thiên Kinh Qur''an là do Muhammad bịa đặt ra và TA cũng đâu có cử phái một ai đó đến với bọn họ trước khi TA phái Ngươi - hỡi Thiên Sứ - để cảnh báo họ vế sự trừng phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(34,45,'34:45','Và nhiều cộng đồng trước đây như người ''Ad, người Thamud và dân của Nabi Lut. Dân đa thần đang sống ở thời đại của Ngươi không được ban cho sức mạnh, ân huệ, con cái hay tài sản dù chỉ bằng một phần mười của những cộng đồng thời đại trước của bọn họ. Vậy mà hầu hết bọn họ lại dám phủ nhận Thiên Sứ của Ngài, cho nên tất cả những gì họ được ban như tài sản, quyền lực, sức mạnh và số lượng vốn không hề giúp ích gì cho họ khi hình phạt của TA giáng lên họ. Bởi thế, hãy nhìn xem bọn chúng - hỡi Thiên Sứ - thế nào là lễ độ cho những kẻ đã phủ nhận TA và bọn chúng phải nhận lấy cái hình phạt thật kinh hoàng của TA giành cho chúng sẽ như thế nào.');
INSERT INTO "vn_mokhtasar" VALUES(34,46,'34:46','Hãy bảo - hỡi Thiên Sứ - với đám người đa thần: "Quả thật, giờ Ta chỉ có thể gợi ý và khuyên giải các ngươi một điều rằng, các ngươi đừng có e ngại mà hãy đứng thẳng lên từng cặp hay từng người một vì Allah, rồi sau đó các ngươi tự suy ngẫm lại mọi quy trình của người bạn Muhammad của các ngươi, Người mà các ngươi đã từng biết về trí tệ, sự thật và sự trung thực của Y. Điều đó cho các ngươi nhận thức ra rằng Y không phải là người điên rồ mà Y chính là một Người đến để cảnh báo các ngươi về một hình phạt thật là kinh khủng sắp ập đến, nếu các ngươi còn chưa chịu ăn năn sám hối với Allah về việc các ngươi dám tổ hợp một ai đó cùng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(34,47,'34:47','Hãy bảo chúng - hỡi Thiên Sứ - những người đa thần rằng: "Ta không đến đây để dòi hỏi các ngươi về bất cứ ân điển hay phần thưởng nào cho điều mà Ta đã truyền đạt cho các ngươi, mọi thứ tốt đẹp là của các ngươi. Còn phần thưởng của Ta nằm ở Allah duy nhấ ban cho. Ngài là Đấng chứng giám hết mọi việc, Ngài sẽ chứng nhận sự việc truyền đạt này của Ta và giám sát luôn mọi hành động của các ngươi để Ngài thưởng phạt nó.');
INSERT INTO "vn_mokhtasar" VALUES(34,48,'34:48','Hãy bảo - hỡi Thiên Sứ -: "Chắc chắn Thượng Đế của Tã lấy chân lý dẹp tan điều giả dối, Ngài thấu rõ hết mọi điều vô hình, không có điều bí ẩn nào nằm trong các tầng trời và sâu trong lòng đất mà Ngài không tận tường và cũng không có việc làm nào của đám bầy tôi của Ngài lại giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(34,49,'34:49','Hãy bảo - hỡi Thiên Sứ - những người đa thần phủ nhận rằng: "Chân lý đã đến, nó chính là Islam thì sự giả tạo sẽ tiêu tan không thể phô bày sức ảnh hưởng hay sức mạnh của nó cả và càng không còn tác dụng gì nữa"');
INSERT INTO "vn_mokhtasar" VALUES(34,50,'34:50','Hãy bảo - hỡi Thiên Sứ - những người đa thần phủ nhận: "Quả thật, nếu như Ta có lạc khỏi chân lý mà Ta đã truyền đạt thì điều đó chỉ gây hại cho chính bản thân Ta mà thôi, sẽ không ảnh hưởng với các ngươi điều gì, còn nếu Ta được chỉ dẫn thì đó là điều do lời mặc khải của Ngài đã ban xuống từ Thượng Đế của Ta, Đấng Toàn Năng. Quả thật, Ngài hằng nghe hết những gì đám bầy tôi của Ngài thưa và rất cận kề không gì mà Ngài có thể không nghe được điều mà Ta nói."');
INSERT INTO "vn_mokhtasar" VALUES(34,51,'34:51','Và nếu như Ngươi có thể nhìn thấy - hỡi Thiên Sứ - được tình trạng của những kẻ phủ nhận khi chúng trở nên kinh hồn bạt vía trước cái hình phạt của Ngày Phán Xét, không một ai trong bọn chúng lại có lối thoát thân và cũng chẳng tìm được một nơi nương náu và rồi chúng sẽ bị TA túm lấy một cách thật dễ từ một nơi ẩn náu của chúng vì đó điều thực thi đầu tiên. Nếu như Ngươi nhìn thấy được cảnh tượng đó thì Ngươi sẽ giật hết cả Người về điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(34,52,'34:52','Và rồi khi nhìn thấy được cảnh tượng hãi hùng đó chúng sẽ bảo: "Chúng tôi đã tin vào ngày Tận Thế." Giờ này đây làm thế nào chúng có thể lấy được đức tin cho được khi đã rồi khỏi cuộc sống trần gian, nơi mà được phép làm không được thưởng, đến với Đời Sau nơi chỉ có thưởng chứ không có làm?');
INSERT INTO "vn_mokhtasar" VALUES(34,53,'34:53','Và làm thế nào họ có thể có được đức tin và được Allah chấp nhận cho được ? Trong khi đó bọn chúng đã không tin tưởng nơi Ngài trong cuộc sống trần tục, vậy mà còn phỏng đoán về chân lý một cách viển vong, nên bọn chúng đã bảo Thiên Sứ - cầu xin Allah ban bình an cho Người - là một nhà ma thuật, thầy bói và một nhà văn nhà thơ."');
INSERT INTO "vn_mokhtasar" VALUES(34,54,'34:54','Và có sự cản ngăn bọn phủ nhận đạt được những gì mà chúng mong muốn trong niềm vui cuộc sống, về việc ăn năn sám hối, được bình an thoát khỏi Hỏa Ngục và được quay trở về cuộc sống trần tục. Cũng tương tự như cảnh tượng của cộng đồng dối trá sống trước họ. Quả thật, bọn họ là kẻ có tính nghi ngờ về những gì các vị Thiên Sứ mang đến là thuần phục Allah duy nhất, tin tưởng vào sự phục sinh.');
INSERT INTO "vn_mokhtasar" VALUES(35,1,'35:1','Alhamdulillah, xin tạ ơn Allah đã khởi tạo các tầng trời và trái đất không dựa trên bất cứ cơ sở nào. Ngài còn chỉ định cho các vị Thiên Thần đảm nhận trách nhiệm sứ giả để thi hành theo mệnh lệnh mà Ngài đã an bày, trong số Họ có Vị đảm nhận việc truyền đạt lời mặc khải, động viên Họ hoàn thành trọng trách đã giao phó cho họ. Trong số họ có Vị có hai cánh, có ba cánh và có bốn cánh, họ lấy đó để bay lượn thực hiện cho mọi mệnh lệnh mà Ngài giao phó. Ngài còn làm tăng thêm vô số những gì mà Ngài muốn về bất cứ bộ phận viên hoặc điều tốt đẹp hoặc âm thanh. Quả thật, Ngài thừa khả năng làm được tất cả mọi vật mà không gì mà không thể.');
INSERT INTO "vn_mokhtasar" VALUES(35,2,'35:2','Quả thật, chìa khóa mở mọi thứ đều trong tay Allah. Cho nên, một khi Allah ban cho ai đó bất cứ hồng phúc nào như bổng lộc, sự chỉ đạo, sự hạnh phúc .v.v. thì không có một ai có thể ngăn cản được nó và một khi Ngài nắm chặt không chịu chu cấp cho ai thì cũng không một ai có quyền hạn mà lấy đó đi chu cấp sau khi Ngài nắm chặt nó. Bởi vì, Ngài là Đấng Toàn Năng mà không có một ai có khả năng chiến thắng, Ngài là Đấng Chí Minh trong mọi tạo hóa của Ngài cùng sự cai quản và quản lý của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(35,3,'35:3','Hỡi con người! Hãy tưởng nhớ những ân huệ của Allah đã chu cấp vào con tim của các ngươi, vào chiếc lưỡi của các ngươi và cả tay chân của các ngươi để các ngươi lao động. Phải chăng, các ngươi tìm ra được một Đấng nào khác ngoài Allah đã chu cấp rất nhiều bổng lộc cho các ngươi từ trên trời xuống như mưa và còn ban bố bổng lộc từ dưới đất lên như cây cối và hoa màu hay sao ? Không có Thượng Đế nào xứng đáng để cho các ngươi thờ phượng ngoài Ngài. Bởi thế, các ngươi sao lại ngoảnh mặt làm ngơ trước những điều lại là sự thật vậy mà còn chưa chịu phục tùng Allah ư và còn phải công khai đối tác ai đó ngang hàng với Allah chăng ? Và Ngài là Đấng tạo hóa ra các ngươi và chu cấp bổng lộc cho các ngươi!');
INSERT INTO "vn_mokhtasar" VALUES(35,4,'35:4','Và nếu dân chúng của Ngươi cho rằng Ngươi dối trá - hỡi Thiên Sứ - thì hãy kiên nhẫn, vì đây không phải là sự việc xảy ra lần đầu cho rằng vị Thiên Sứ dối trá với dân chúng của Y mà hẳn là đã diễn ra ở từng thời đại trước Ngươi với tất cả Thiên Sứ điển hình như ở người ''Ad, người Thamud và dân của Lut. Nhưng mọi vấn đề đó sẽ được trình diện với Allah Đấng Duy Nhất quyết định tất cả. Bởi thế, Ngài đã tiêu diệt hết bọn phủ nhận và đem lại chiến thắng thật vẽ vang cho Thiên Sứ của Ngài và những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(35,5,'35:5','Này hỡi con người! Quả thật những gì mà Allah đã từng hứa như - dựng sống lại và phần thưởng vào Ngày Phán Xét - hoàn toàn là sự thật không gì phải nghi ngờ về điều đó. Bởi thế, các ngươi chớ để sự chóp nhoáng của trần tục này làm mê và đừng vì ham muốn mà từ bỏ sự chuẩn bị tốt cho việc hành đạo vì Ngày vĩ đại đó và cũng chớ để cho tên Shaytan lừa gạt rồi trở thành kẻ chỉ muốn mỗi cuộc sống trần tục này.');
INSERT INTO "vn_mokhtasar" VALUES(35,6,'35:6','Quả thật, lũ Shaytan là kẻ thù của các ngươi - hỡi nhân loại - một kẻ thù không đội trời chung với các ngươi. Bởi thế, các ngươi hãy đối xử với hắn như là một kẻ thù không ngừng chiến tranh với hắn và luôn đề phòng hắn. Quả thật, tên Shaytan là kẻ chuyên đi thì thào bên tai cám dỗ làm theo hắn rồi trở thành những kẻ phủ nhận Allah để rồi phải chuốc lấy sự trừng phạt của ngọn lửa hồng rực cháy của Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(35,7,'35:7','Những ai phủ nhận Allah đi theo tên Shaytan, chúng sẽ bị trừng trị thật khắc khe, ngược lại còn ai tin tưởng Allah và luôn hành đạo tốt thì họ sẽ được Allah tha thứ cho việc làm tội lỗi của họ và họ sẽ là người xứng đáng nhận lấy một phần thật vĩ đại, đó chính là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(35,8,'35:8','Quả thật, một kẻ nào đó hẳn cho rằng việc làm của tên Shaytan là tốt đẹp nhưng đó là điều hết sức tồi tệ đối với y thế mà y cứ thầm nghĩ đó là những điều tốt đẹp, chẳng giống như người được Allah tô điểm chân lý cho y rồi y cứ thầm nghĩ đó là chính đạo. Quả thật Allah muốn đánh lạc hướng kẻ nào tùy ý Ngài và tùy ý hướng dẫn ai mà Ngài muốn, cũng không ép buộc y về nó. Cho nên Ngươi - hỡi Thiên Sứ - đừng tự vằn vặt bản thân và buồn rầu cho sự lầm lạc của bọn chúng. Quả thật, Allah Toàn Năng hằng biết những gì chúng làm và không có một sự bí ẩn nào của Ngươi đã làm cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(35,9,'35:9','Và Allah là Đấng đã gửi những luồng gió làm khuấy động những đám mây. Bởi thế, chúng đã dịch chuyển những đám mây bay đi lơ lững đến những mãnh đất khô cằn rồi dùng nó để tưới tiêu cho những mãnh đất đó sau khi bị hạn hán để cho sự trồng trọt được thuận tiện. Bởi vậy, chúng phục hồi lại một mãnh đất sau khi đã chết khô trở nên màu mỡ cây cối um tùm, thế nên sự phục sinh sống lại người chết vào Ngày Phục Sinh tương tự thế.');
INSERT INTO "vn_mokhtasar" VALUES(35,10,'35:10','Ai muốn được sự vinh quang trong cuộc sống trần tục này hoặc vào ngày sau thì đừng cầu nguyện bất cứ ai ngoài Allah, bởi vì Allah là Đấng Duy Nhất ban cho sự vinh quang được ở hai cõi. Mọi lời ca tụng tốt đẹp điều kính dâng lên Ngài và những việc làm ngoan đạo của bầy tôi sẽ bốc lên tận Ngài còn những ai có mưu đồ vô lương xấu xa - điển hình như âm mưu ám sát Thiên Sứ - cầu xin Allah ban bình an cho Người - sẽ nhận lấy một hình phạt thật là khắc khe. Và mọi mưu đồ của bọn ngoại đạo đốn mạt và hư đốn đó sẽ không bao giờ được chút thành công.');
INSERT INTO "vn_mokhtasar" VALUES(35,11,'35:11','Và Allah là Đấng đã tạo hóa ông tổ của các ngươi đó chính là Adam từ đất, rồi Ngài tạo các ngươi ra từ giọt tinh trùng, sau đó Ngài định dạng các ngươi thành nam và nữ để ghép thành vợ chồng. Và rồi không một phụ nữ nào mang thai hoặc hạ sinh mà Ngài lại không biết vì không có một điều vô hình nào có thể giấu giếm được Ngài. Và không một ai sống thọ hơn hay sống đoản mạng mà lại không được ghi chép trong Quyển Sổ Mẹ cả. Quả thật, tất cả mọi điều đã được liệt kê đối với Allah là một việc hết sức dễ dàng.');
INSERT INTO "vn_mokhtasar" VALUES(35,12,'35:12','Và hai nước biển không có sự giống nhau: Một trong hai nguồn nước đó có nguồn nước thì rất ngọt ngào, dễ uống hương vị ngọt liệm và nguồn nước còn lại mặn đắn không dễ uống chút nào vì độ mặn rất cao. Và trong lòng hai đại dương đó luôn chứa đựng vô số những loại thịt tươi sống cho các ngươi ăn thỏa thích, chủ yếu là những loại cá. Và nó còn chiết xuất ra những viên ngọc chai và những rạng san hô quý hiếm để cho các ngươi chế tác ra những món trang sức đeo. Các ngươi còn nhìn thấy được những con thuyền (bưồm) tấp nập ra khơi - hỡi con người - chúng lướt trên mặt biển ngược xuôi để tìm kiếm những thiên lộc của Allah bằng các cuoodj thương mại. Qua những điều đó hi vọng các ngươi biết tạ ơn Allah về những thiên ân của Ngài ban cho vô số.');
INSERT INTO "vn_mokhtasar" VALUES(35,13,'35:13','Allah đã xác nhập ban đêm vào ban ngày thế nên màng đêm sẽ dài hơn và ban ngày vào ban đêm thế nên ánh sáng ban ngày sẽ dài hơn. Ngài là Đấng điều khiển mặt trời và mặt trăng làm cho hai hành tinh đó đi đến một điểm hẹn là Ngày Tận Thế đúng theo sự chỉ định của Allah mà vào ngày đó chỉ có Allah mới là Đấng Am Tường. Với thuộc tính được miêu tả đó chính là Allah Thượng Đế của các ngươi. Ngài là Đấng Độc Tôn nắm quyền thống trị mọi vật. Bất hạnh thay cho các ngươi lại đi thờ phượng những thần linh ngoài Allah trong khi chúng không thể kiểm soát được mỗi một hạt chà là. Bởi thế, làm sao các ngươi có thể tôn thờ chúng mà không phải là TA vậy chứ ?');
INSERT INTO "vn_mokhtasar" VALUES(35,14,'35:14','Quả thật, những kẻ mà các ngươi tôn thờ chúng sẽ chẳng bao giờ nghe được lời cầu nguyện của các ngươi. Bởi vì, chúng một vật vô tri không có sự sống, không nghe được điều gì và nếu chúng có nghe được lời cầu nguyện của các ngươi đi chăng nữa - chẳng qua đó là một giả định - thì chúng sẽ không đáp lại lời xin của các ngươi. Và rồi vào Ngày Phán Xét chúng sẽ phản bát chối từ việc các ngươi đã từng thờ phượng chúng. Bởi thế, không có một ai có thể giải thích cụ thể cho Ngươi - hỡi Thiên Sứ - về một sự thật như Allah Toàn Năng cả.');
INSERT INTO "vn_mokhtasar" VALUES(35,15,'35:15','Hỡi con người! Các ngươi là những kẻ cần phải nhờ vả Allah cho tất cả mọi vấn đề và mọi hoàn cảnh của các ngươi. Vì Allah rất mực giàu có không cần bất cứ thứ gì của các ngươi, Ngài đáng ca tụng ở trần gian này và cả ngày sau với những gì Ngài đã an bày cho đám nô lệ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(35,16,'35:16','Nếu muốn, Ngài sẽ loại bỏ các ngươi bằng một sự hủy diệt mà không còn ai trong các ngươi tồn gại và rồi Ngài sẽ tạo hóa lại mọi thứ điều mới hoàn toàn thay thế các ngươi để cho chúng thờ phượng Ngài duy nhất, không hề có việc tôn thờ ai đó cùng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(35,17,'35:17','Và sự loại bỏ các ngươi bằng một trận hủy diệt rồi Ngài tạo hóa lại mọi thứ đều mới hoàn toàn để thay thế các ngươi đối với Allah là một việc quá đỗi bình thường chẳng có chút gì là trở ngại.');
INSERT INTO "vn_mokhtasar" VALUES(35,18,'35:18','Và sẽ không có một ai có thể gánh vác tội lỗi thay cho bất cứ ai. Không, chỉ có chính bản thân y mới gánh vác tội lỗi của y và nếu một ai đó cho dù bản thân của y có cố hết sức gánh vác tội lỗi rồi bỗng dưng có một ai đó chịu san sẻ gánh nặng đó cùng y thì cũng chẳng có giúp ích được y chút nào dẫu rằng đó là họ hàng thân thích nhất của y. Ngươi chỉ có thể răn đe - hỡi Thiên Sứ - về sự trừng phạt của Allah cho những ai biết khiếp sợ Thượng Đế của họ bằng một điều vô hình và chu đáo dâng lễ nguyện Salah thì người đó mới lời răn đe của Ngươi mới có tác dụng với y. Ai biết tự tẩy sạch mình khỏi tội lỗi - nhất là tội Shirk - là y đã tẩy sạch được thân mình bởi điều lợi đó giúp ích cho bản thân y, chứ Allah rất giàu có vốn không cần đến sự phục tùng của y và rồi các ngươi phải trở về trình diện với Allah vào Ngày Phán Xét để nhận lấy kết quả và phần thưởng.');
INSERT INTO "vn_mokhtasar" VALUES(35,19,'35:19','Và người ngoại đạo và người có đức tin không thể có vị trí ngang nhau. Cũng như người mù và người sáng mắt không bao giờ tương tự nhau.');
INSERT INTO "vn_mokhtasar" VALUES(35,20,'35:20','Và sự vô đức tin và đức tin cũng không hề như nhau, giống như bóng tối và ánh sáng vậy.');
INSERT INTO "vn_mokhtasar" VALUES(35,21,'35:21','Nơi Thiên Đàng và Hỏa Ngục hoàn toàn khác biệt, cũng như bóng mát và sức nóng của cơn gió không hề như nhau.');
INSERT INTO "vn_mokhtasar" VALUES(35,22,'35:22','Người có đức tin và người phủ nhận đức tin không ngang nhau, cũng như người chết và người sống khác nhau hoàn toàn. Quả thật, Allah thừa sức làm cho ai đó nghe được chỉ đạo của Ngài. Còn Ngươi - hỡi Thiên Sứ - làm sao có thể khiến cho những kẻ phủ nhận nhận đức tin nghe được vì chúng không khác gì những người chết nằm trong ngôi mộ.');
INSERT INTO "vn_mokhtasar" VALUES(35,23,'35:23','Thật ra Ngươi chỉ là một Người đến để cảnh báo họ về hình phạt của Allah mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(35,24,'35:24','Quả thật, TA cử phái Ngươi - hỡi Thiên Sứ - mang Chân Lý đến mà trong nó không có gì phải nghi ngờ cả. Bên cạnh đó Ngươi cũng là người báo tin mừng cho người có đức tin về phần thưởng bao la mà Allah đã chuẩn bị cho họ và Ngươi cũng cảnh báo đến những kẻ phủ nhận đức tin về hình phạt thật đau đớn đang chờ đợi bọn chúng và rồi không một cộng đồng nào ở thời trước chúng mà lại không có Thiên Sứ của Allah cử đến để cảnh báo về cái hình phạt của TA.');
INSERT INTO "vn_mokhtasar" VALUES(35,25,'35:25','Và nếu như dân chúng của Ngươi phủ nhận Ngươi - hỡi Thiên Sứ - thì hãy kiên nhẫn. Bởi vì, đây không phải là lần đầu tiên một vị Thiên Sứ bị dân chúng của Y phủ nhận Y, vì chắc chắn có rất nhiều tên ở thế hệ thời trước đã từng phủ nhận các vị Thiên Sứ được gởi đến chúng điển hình người ''Ad, người Thamud và dân của Lut. Đã có các vị Thiên Sứ của Allah mang đến cho chúng một bằng chứng quá rõ ràng để chứng minh một sự thật cho bọn chúng, có những vị Thiên Sứ mang đến cho bọn chúng Kinh Zabur và Kinh khác để sói sáng cho những ai biết cân nhắc và suy nghĩ.');
INSERT INTO "vn_mokhtasar" VALUES(35,26,'35:26','Mặc dù vậy, họ vẫn phủ nhận Allah và không tin vào các Sứ Giả và sứ mạng của họ mang đến từ TA. Bởi thế, TA đã tiêu diệt những kẻ đã phủ nhận, thế nên hãy nghiền ngẫm - hỡi Thiên Sứ - xem TA đã hủy diệt những kẻ từ chối bằng một hình phạt thật kinh hoàng.');
INSERT INTO "vn_mokhtasar" VALUES(35,27,'35:27','Há Ngươi không nhìn thấy sao - hỡi Thiên Sứ - Allah Toàn Năng đã cho những trận mưa từ trên trời xuống, từ nước mưa đó TA đã làm cho những loại cây trái hoa màu đâm chồi nẩy lộc có được những màu sắc khác biệt nhau điển hình là màu đỏ, màu xanh lá cây, màu vàng và còn nhiều màu khác nữa sau khi được TA tưới lên những thân cây và có những ngọn núi tạo thành những đường nét trắng, đỏ và còn có những nét màu đen huyền.');
INSERT INTO "vn_mokhtasar" VALUES(35,28,'35:28','Và nhân loại, động vật lẫn gia súc (gồm lạ đà, bò và dê) đều mang trên mình một màu sắc khác biệt giống như TA đã đề cập. Quả thật, đa số bầy tôi của Allah Tối Cao chỉ có những ai hiểu biết mới khép mình kính sợ Ngài Toàn Năng. Bởi vì, họ là những người hiểu biết được thuộc tính của Ngài, giáo lý của Ngài và những bằng chứng về quyền năng của Ngài. Quả thật, Allah Quyền Lực không một ai có thể chế ngự được Ngài và Ngài Hằng Tha Thứ tội lỗi cho đám bầy tôi của Ngài biết ăn năn sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(35,29,'35:29','Quả thật, những ai đọc Kinh Sách của Allah được mặc khải xuống cho Thiên Sứ của Ngài, biết tuân thủ theo những gì trong nó, luôn chỉnh chu dâng lễ nguyện Salah một cách chu đáo và chi dùng những khối tài sản vào con đường Zakah hay bằng mọi con đường khác một cách thầm kính và công khai, với hy vọng những việc làm thiện đó có thể đổi chác với Allah mà sẽ không phải chuốc lấy sự thật bại.');
INSERT INTO "vn_mokhtasar" VALUES(35,30,'35:30','Allah là Đấng luôn đáp lại đầy đủ phần thưởng cho mọi tù lao của họ và còn tăng thêm cho họ nhiều thiên lộc của Ngài, y xứng đáng nhận được mọi điều đó. Quả thật Ngài Toàn Năng hằng tha thứ cho mọi tội lỗi tuyệt với nhất bởi đó là thuộc tính của Ngài và Ngài cũng là Đấng luôn Ghi Công cho những ai làm việc thiện.');
INSERT INTO "vn_mokhtasar" VALUES(35,31,'35:31','Và những gì mà TA đã mặc khải cho Ngươi - hỡi Thiên Sứ - từ Thiên Kinh đó là Chân Lý mà không có gì phải nghi ngờ về Nó, Nó được Allah ban xuống cho Ngươi nhằm để xác minh lại những quyển Kinh ở thời đại trước kia. Quả thật, Allah rất mực am tường và hằng thấy rõ các bầy tôi của Ngài. Bởi vì, Ngài mặc khải xuống cho Thiên Sứ của mỗi cộng đồng điều phù hợp ở thời đại Người đang sống.');
INSERT INTO "vn_mokhtasar" VALUES(35,32,'35:32','Rồi TA ban xuống cho cộng đồng của Muhammad - cầu xin Allah ban bình an cho Người - với những ai mà TA đã lựa chọn họ để thừa hưởng Thiên Kinh Qur''an. Thế nhưng, trong số họ có những người tự hãm hại bản thân mình bằng những việc làm cấm kỵ mà lại bỏ đi trách nhiệm bổn phận; có những người chỉ làm tròn trách nhiệm và biết tránh xa những điều cấm, song họ lơ là với những điều nên làm nhưng lại vi phạm những điều nên bỏ. Ngoài ra, trong họ có người với sự chấp nhận của Allah thì họ luôn hăng hái làm tròn trách nhiệm bổn phận cùng với những điều khuyến khích nên làm và luôn tránh xa những điều cấm kỵ và điều không nên. Đối với những ai vừa liệt kê - là những người được lựa chọn để thừa kế Qur''an - đó là thiên lộc rất là to lớn mà không có thiên lộc nào vĩ đại hơn.');
INSERT INTO "vn_mokhtasar" VALUES(35,33,'35:33','Những ngôi vườn vĩnh hằng nơi Thiên Đàng mà họ được lựa chọn để bước vào. Trong đó họ sẽ đeo được những viên ngọc trai và những chiếc vòng bằng vàng và khoác trên mình những bộ quần áo lụa tơ tằm.');
INSERT INTO "vn_mokhtasar" VALUES(35,34,'35:34','Và họ lên tiếng sau khi đã bước vào Thiên Đàng: "Alhamdulillah, xin tạ ơn Allah, Đấng đã làm tan biến đi nổi lo âu của bầy tôi vì lý do bầy tôi rung sợ mình phải đi vào Hỏa Ngục. Quả thật, Thượng Đế của bầy tôi hằng tha thứ mọi tội lỗi cho đám bầy tôi của Ngài biết ăn năn sám hối và Ngài hằng ghi công cho những ai đã phủ phục Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(35,35,'35:35','Ngài là Đấng đã ban cho bầy tôi có những ngôi nhà định cư thật là viên mãn - mà không cần phải lo nghĩ sẽ định cư nơi nào nữa - một nơi ở vĩnh hằng, có được không phải do công lao cũng như sức mạnh của bầy tôi làm tạo ra. Trong nó không có sự vất vả hay nhọc nhằn và chẳng có điều bận tâm.');
INSERT INTO "vn_mokhtasar" VALUES(35,36,'35:36','Còn đối với những kẻ phủ nhận Allah, chúng là những tên bị Hỏa Ngục đốt cháy mãi mãi, chúng không tài nào kết liễu đời mình cho chết phức để có thể chấm dứt đi cái hình phạt đeo bám chúng và chúng cũng không tài nào có thể làm vơi đi cái hình phạt và đó là hậu quả mà chúng phải gánh chịu vào Ngày Phán Xét cho tất cả sự ngạo mạng trước hồng ân của Thượng Đế của hắn.');
INSERT INTO "vn_mokhtasar" VALUES(35,37,'35:37','Và chúng gào thét trong đó bằng những tiếng la inh ỏi khóc lóc van xin: "Lạy thượng Đế của bầy tôi! Lạy Ngài thả bầy tôi ra khỏi Hỏa Ngục, bầy tôi hứa sẽ làm mọi việc thiện thay vì những điều tội lỗi ở trần gian để được đổi lấy sự hài lòng của Ngài và để thoát khỏi sự trừng phạt của Ngài," Allah phán bảo bọn chúng: "Há rằng, TA đã không cho các ngươi có được một cuộc sống với một tuổi thọ đủ dài để cho những ai muốn giác ngộ có cơ hội giác ngộ hay sao, đủ để y sám hối với Allah mà hành đạo tốt. Vả lại có Thiên Sứ mang đến cho các ngươi những lời cảnh báo về cái hình phạt của Allah rồi kia mà ? Các ngươi không còn cớ gì để biện hộ nữa, tất cả chỉ bằng thừa. Thôi được rồi, giờ thì hãy nếm thử cái hình phạt trong Lửa. Tất nhiên, sẽ không có bất cứ kẻ nào tự làm hại bản thân mình có thể tìm được một ai có thể giúp đỡ chúng thoát khỏi sự trừng phạt của Allah hoặc giảm nhẹ hình đó cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(35,38,'35:38','Quả thật, Allah là Đấng Am Tường Hết những điều vô hình ở trong các tầng trời và trái đất, không có gì có thể che giấu được Ngài. Quả thật Ngài biết rõ hết mọi điều bí của đám bầy tôi của Ngài ở nơi tâm khảm của con tim cho dù nó chứa đựng điều tốt đẹp hay xấu xa đi chăng nữa.');
INSERT INTO "vn_mokhtasar" VALUES(35,39,'35:39','Ngài là Đấng đã bổ nhiệm một số trong các ngươi - hỡi con người - trở thành những người nối nghiệp ở trái đất để Ngài thử thách xem các ngươi làm gì. Bởi thế, ai phủ nhận Allah và những gì được mang đến từ các vị Thiên Sứ của Ngài thì tự gánh hậu quả cho tội phủ nhận đức tin đó. Việc vô đức tin của y chẳng hề gây hại được Thượng Đế của y. Sự phủ nhận của những kẻ vô đức tin chẳng giúp ích gì mà còn làm cho bọn tăng thêm sự đáng nghét dưới cái nhìn của Thượng Đế của chúng Đấng Toàn Năng, và sự phủ nhận của những tên không có đức tin chỉ làm cho chúng thêm sự mất mát. Họ trở thành những kẻ thua thiệt phải trượt khỏi những gì mà Allah đã chuẩn bị sẵn nơi Thiên Đàng dành cho họ nếu như họ là những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(35,40,'35:40','Ngươi - hỡi Thiên Sứ - hãy bảo với những người đa thần: "Các ngươi hãy cho Ta biết về những thần linh mà các ngươi cầu nguyện không phải là Allah, chúng đã tạo hóa ra được những gì ở trên trái đất ? Chúng đã tạo ra núi non ư ? Chúng đã tạo ra sông ngòi ư ? Chúng tạo ra động vật ư ? Hay là bọn chúng là đối tác cùng với Allah trong việc tạo hóa ra các tầng trời này chăng ? Hoặc Ta đã ban cho chúng Kinh Sách rồi chúng dựa trên bằng chứng xác thực đó cho việc thờ phượng để chúng làm những điều tổ hợp chăng ? Mọi điều đó hoàn toàn là không có. Không, chẳng qua chỉ có những kẻ làm điều bất công cho bản thân chúng bằng sự phủ nhận đức tin và chuốc lấy tội lỗi lời hứa suông đối với nhau.');
INSERT INTO "vn_mokhtasar" VALUES(35,41,'35:41','Quả thật, Allah Toàn Năng bảo vệ các tầng trời và trái đất không cho chúng bị tiêu tan, nhưng nếu như thật sự chúng phải bị tiêu tan - đúng theo sự sắp đặt - thì không một ai ngòa Ngài có thể giử chúng lại được. Quả thật, Ngài hằng chịu đựng không vội vàng cho sự trừng phạt và cũng hằng tha thứ cho mọi tội lỗi của đám bầy tôi của Ngài biết ăn năn hối cải.');
INSERT INTO "vn_mokhtasar" VALUES(35,42,'35:42','Và rồi những kẻ ngoại đạo xấc xược buông lời thề thốt một cách rất là trịnh trọng rằng: "Nếu như có một Thiên Sứ nào được Allah cử phái đến gặp chúng để cảnh báo về hình phát của Ngà thì chắc chắn đa số chúng sẽ trở thành những người ngay thẳng và đi theo con đường chính đạo tốt hơn người Do Thái, ngườiThiên Chúa hoặc bất cứ ai khác. Thế nhưng, khi Muhammad - cầu xin Allah ban bình an cho Người - được cử đến với chúng mang theo bức thông điệp từ Thượng Đế của họ để cảnh báo họ về hình phạt của Allah thế nhưng những điều đó chỉ làm họ rời xa chính đạo hơn thôi và còn làm cho chúng thành những kẻ ngỗ nghịch." Thế nên, chúng không thực hiện đúng như lời thề thốt mà chúng từng cam đoan với lòng tin tưởng trung thực của chúng để trở thành những người được hướng tốt hơn những người tiền nhiệm trước chúng.');
INSERT INTO "vn_mokhtasar" VALUES(35,43,'35:43','Và chúng đem Allah ra thề thốt rồi những lời thề thốt đó trước Ngài nó không phải là sự định tâm tốt đẹp gì mà để cho chúng có ý định thực hiện. Không, bọn chúng chẳng qua chỉ là những kẻ kiêu ngạo ở trần gian này và là những kẻ lừa dối thiên hạ. Và rồi những âm mưu thâm độc của chúng chẳng hãm hại được một ai ngoài những kẻ sâu độc. Há phải chăng, đám người ngông cuồng đó không chờ đợi gì ngoài các đối xử cố định của Allah ư và đó là sự tiêu diệt mà Ngài đã loại trừ sạch đám người như họ trước đây? Bởi thế, Ngươi sẽ không tìm thấy cách đối xử Allah dành cho đám người ngông cuồng như thế bị thay đổi, nó sẽ túm lấy họ chứ không ai ngoài họ. Đó là qui định của Thượng Đế.');
INSERT INTO "vn_mokhtasar" VALUES(35,44,'35:44','Há đám người phủ nhận thuộc bộ tộc Quraish, không chịu đi khắp nơi trên trái đất để tận mắt nhìn thấy kết cuộc của những kẻ ngông cuồng ở thời đại trước chúng ? Há kết cuộc của chúng chẳng phải là bị Allah tiêu diệt, dẫu rằng họ rất mạnh mẽ hơn bộ tộc Quraish về quyền lực hay sao ? Và Allah là Đấng mà không có vật gì ở trong các tầng trời và trái đất có thể vượt khỏi Ngài. Quả thật. Ngài là Đấng Am Tường hết mọi âm mưu của bọn giả dối, không có một âm mưu đen tối nào của bọn giả dối có thể hảm hại được Ngài, Ngài là Đấng Đầy Quyền Lực muốn tiêu chúng lúc nào tùy ý Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(35,45,'35:45','Và nếu như Allah vội vã trừng phạt loài người về những việc làm tội lỗi mà họ đã phạm, về những gì họ tạo ra lầ tất cả đáng bị trừng phạt ngay bây giờ, không còn ai có thể sở hữu được bất cứ gì dù là một con vật hay tài sản. Thế nhưng, Đấng Toàn Năng đã trì hoãn tính mạng của họ lại vào một thời hạn được ấn định đó là Ngày Phán Xét. Bởi thế, khi mà Ngày Phán Xét được diễn ra thì chắc chắn Allah sẽ nhìn thấy hết tất cả đám bầy tôi của Ngà, lúc đó không một điều gì giấu được Ngài và Ngài sẽ ban thưởng dựa vào việc làm của họ, làm tốt được tốt và làm xấu bị xấu.');
INSERT INTO "vn_mokhtasar" VALUES(36,1,'36:1','(Ya sin) Đã được giải thích ở câu đầu tiên của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(36,2,'36:2','Allah thề bởi Qur''an một quyển Thiên Kinh danh giá trên từng câu của Nó, trong Nó không hề có bất cứ sai xót nào dù ở khía cạnh nào.');
INSERT INTO "vn_mokhtasar" VALUES(36,3,'36:3','Quả thật, Ngươi - hỡi Thiên Sứ - đích thực là một trong những Thiên Sứ mà Allah đã cứ phái Ngươi đến với đám bầy tôi của Ngài; để ra lệnh họ phải đặt niềm tin vào Ngài duy nhất và thờ phượng riêng.');
INSERT INTO "vn_mokhtasar" VALUES(36,4,'36:4','Trên một con đường ngay chính và chân thật.');
INSERT INTO "vn_mokhtasar" VALUES(36,5,'36:5','Và đó là con đường được truyền xuống từ Thượng Đế của Ngươi Đấng Đầy Quyền Lực mà không một ai có thể chế ngự được Ngài, Đấng Rất Mực Khoan Dung cho đám bề tôi có đức tin của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(36,6,'36:6','TA ban xuống cho Ngươi dùng để cảnh tỉnh đến một dân tộc và cũng là điều cảnh báo đối với họ, họ là những người dân Ả rập mà trước đây tổ tiên của họ chưa từng có Thiên Sứ nào đến để cảnh báo. Bởi thế, họ không hề hay biết gì về đức tin và Tawhid (sự tôn thờ Allah duy nhất), do đó khi thời đại của họ nhận được lời cảnh báo đã làm cho họ xem nhẹ cho những điều thiết thực của lời cảnh tỉnh của Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(36,7,'36:7','Chắc chắn, Allah sẽ trừng phạt đa số bọn họ là một điều quá hiển nhiên, nó sẽ diễn ra sau khi đã được hoàn chỉnh mọi chân lý của Allah từ chính chiếc lưỡi của Thiên Sứ của Ngài đến với họ, rõ ràng như thế mà họ còn chưa chịu tin ở nơi Ngài và tiếp tục ở trên hiện trạng vô đức tin. Bởi vì, họ là những người không tin tưởng nơi Allah và Thiên Sứ của Ngài và cũng không chịu làm theo chân lý được đến với họ.');
INSERT INTO "vn_mokhtasar" VALUES(36,8,'36:8','Và họ giống như đang bị đóng những chiếc gông cùm ở cổ và Ngài đã gom những đôi tay với cần cổ của họ lại với nhau. Bởi thế, bọn họ trong tư thế bị ngẩng cao đầu lên trời xanh, không thể hạ thấp xuống được chút nào. Bởi vì họ là những kẻ bị gạt bỏ ra khỏi đức tin của Allah, họ là những kẻ đã khước từ Ngài thế nên họ không thể hạ đầu xuống trong lúc bị Ngài phạt.');
INSERT INTO "vn_mokhtasar" VALUES(36,9,'36:9','Và TA đã dùng một bức tường làm tấm rào cản trước mặt và phía sau lưng chúng đối với việc tiếp cận chân lý, thế nên TA khiến chúng chẳng nhìn thấy được gì có lợi. Bởi thế, điều đó sẽ xảy ra với những kẻ sau khi thể hiện mình là những kẻ ngang tàn và bướng bỉnh xuất phát từ sự phủ nhận Allah.');
INSERT INTO "vn_mokhtasar" VALUES(36,10,'36:10','Quả thật, những kẻ phủ nhận đức tin và còn luôn tỏ vẻ ngông cuồng tự đại thì cho dù Ngươi - hỡi Muhammad - có răn đe họ hoặc bỏ mặc chúng đi chăng nữa thì điều đó cũng chỉ là dư thừa. Bởi vì, chúng là những kẻ không tin tưởng về những gì được Ngươi mang đến từ Allah.');
INSERT INTO "vn_mokhtasar" VALUES(36,11,'36:11','Quả thật, lời cảnh báo của Ngươi có giá trị đối với những người nào tin tưởng nơi Qur''an và tuân theo những gì được mang lại từ Nó, những người luôn biết kính sợ Thượng Đế của y lúc ở một mình nghĩa là ở nơi không ai nhìn thấy y. Bởi thế, hãy loan tin mừng cho những ai có được phẩm chất tốt đẹp đó được Allah sẽ xóa hết mọi tội lỗi của y và được Ngài tha thứ, không những thế Ngài còn ban cho một phần thưởng thật là hậu hĩ đang chờ đợi y ở Đời Sau đó chính là được bước vào Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(36,12,'36:12','Chắc chắn TA sẽ làm cho người chết sống lại bằng việc phục sinh vào Ngày Tận Thế để phân xử họ. TA đã ghi chép lại hết mọi điều mà họ đã từng làm trong cuộc sống ở trần gian này dù tốt hay xấu, và TA vẫn không bỏ sót những việc làm mà họ để lại hậu thế sau khi chết như những loại tài sản bố thí còn lưu truyền hoặc sự vô đức tin của y hãy vẫn còn tồn tại. Tất cả đều được TA cho ghi chép lại thật chi tiết trong Quyển Sổ Mẹ.');
INSERT INTO "vn_mokhtasar" VALUES(36,13,'36:13','Ngươi hãy trình bày - hỡi Thiên Sứ - cho những tên ngông cuồng ngạo mạng về một bài học qua một câu chuyện: "Đó là câu chuyện kể về dân cư của một thị trấn khi có những vị Thiên Sứ được cử phái đến gặp họ."');
INSERT INTO "vn_mokhtasar" VALUES(36,14,'36:14','Khi TA cử phái lần đầu xuống gặp họ là hai vị Thiên Sứ để kêu gọi họ đến với Tawhid tôn thờ duy nhất Allah và thờ phượng riêng Ngài. Thế nhưng, chúng lại cho là hai vị Thiên Sứ đều nói dối, thế nên TA cử thêm một vị Thiên Sứ thứ ba xuống gặp chúng và cả ba cùng đồng thanh kêu gọi dân chúng ở một thị trấn rằng: "Quả thật, chúng tôi là ba vị Thiên Sứ - được cử phái đến gặp các ngươi; để kêu gọi các ngươi đến với Tawhid tôn thờ duy nhất Allah và làm theo chỉ đạo của Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(36,15,'36:15','Đám dân của thị trấn đáp lại các vị Thiên Sứ rằng: "Các ông chẳng qua chỉ là những người phàm giống như chúng tôi, các người có gì trội hơn chúng tôi đâu nào. Và rồi Đấng Al-Rahman đã không mặc khải xuống cho các ông cả. Rõ ràng, các ông chỉ là những người nói dốc rồi đổ thừa cho Allah trong việc mấy ông kêu gọi chúng tôi thế này đây."');
INSERT INTO "vn_mokhtasar" VALUES(36,16,'36:16','Ba vị Thiên Sứ đáp lại lời của những kẻ ngông cuồng trong đám dân của thị trấn rằng: "Thượng Đế của chúng tôi chắc chắn biết rõ việc chúng tôi đến gặp các người - hỡi dân chúng của thị trấn - chúng tôi là những vị Thiên Sứ chính Ngài đã cử phái chúng tôi đến. Đó là bằng chứng mạnh nhất của chúng tôi."');
INSERT INTO "vn_mokhtasar" VALUES(36,17,'36:17','Chúng tôi không có trách nhiệm nào ngoài việc truyền đạt lại hết mọi mệnh lệnh mà chúng tôi đã nhận được một cách minh bạch và chúng tôi không hề có quyền hạn gì để chỉ đạo các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(36,18,'36:18','Đám dân của thị trấn đáp lại các vị Thiên Sứ: "Quả thật, chúng tôi đã gieo quẻ biết rằng các người là điềm gở. Nếu như các người không chịu dừng lại việc kêu gọi chúng tôi đến với Tawhid tôn thờ duy nhất Allah thì chúng tôi sẽ áp dụng ngay hình phạt với các người bằng việc ném đá đến khi nào các người chết mới thôi một hình phạt thật là khủng khiếp và đau đớn."');
INSERT INTO "vn_mokhtasar" VALUES(36,19,'36:19','Các vị Thiên Sứ đáp lại bọn chúng: "Việc bói toán của quý vị luôn bám chặt quý vị vì do quý vị phủ nhận Allah và không tuân theo các vị Thiên Sứ của Ngài. Làm sao quý vị vẫn còn bói toán khi được nhắc về Allah cho được cơ chứ? Không, quý vị đúng là một đám người tội lỗi vì sự vô đức tin và nhiều tội lỗi khác.');
INSERT INTO "vn_mokhtasar" VALUES(36,20,'36:20','Bỗng có một người đàn ông đến từ một nơi thật xa xôi của ngôi làng rất vội vã chạy lại vì sợ hãi người dân của mình cho các vị Thiên Sứ dốc láo và sẽ đe dọa họ rồi sợ sẽ giết chết họ hay hảm hại họ, y bảo: "Này hỡi người dân của ta! Hãy tuân theo những gì mà các vị Thiên Sứ mang đến đi."');
INSERT INTO "vn_mokhtasar" VALUES(36,21,'36:21','Hãy tuân theo họ đi - hỡi người dân của ta - những người mà không cần bất cứ tiền thù lao gì của qúy vị mà khi đã hoàn tất hết nhiệm vụ của họ đối với các ngươi, và họ là những người được hướng dẫn một cách hoàn thiện bởi Allah qua lời mặc khải của Ngài. Bởi thế, họ là những người xứng đáng để noi theo.');
INSERT INTO "vn_mokhtasar" VALUES(36,22,'36:22','Và người đàn ông đó khuyên bảo rằng: "Điều gì, có thể ngăn cản tôi thờ phượng Allah Đấng mà đã tạo hóa ra tôi ? Và điều gì có thể ngăn cản quý vị thờ phượng Thượng Đế của quý vị Đấng đã tạo hóa ra quý vị và cũng là Đấng duy nhất sẽ đưa quý vị trở về gặp lại Ngài vào Ngày Phục Sinh để nhận lấy kết quả ?"');
INSERT INTO "vn_mokhtasar" VALUES(36,23,'36:23','Làm sao tôi có thể thờ phượng những thần linh nào khác ngoài Allah, trong khi Ngài đã tạo hóa ra tôi để đến với việc thờ phượng những thứ không xứng đáng? Quả thật, nếu như Đấng Al-Rahman muốn hãm hại tôi thì không có một thần linh nào có quyền lực giúp đỡ. Chắc rằng chúng không có khả năng cứu vớt hay hãm hại tôi được, lại càng không bảo về tôi tránh khỏi tai ương mà Allah có ý định cho tôi chết là kẻ phủ nhận đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(36,24,'36:24','"Nếu như tôi đi thờ phượng một thần linh nào khác ngoài Allah thì quả thật tôi sẽ là một người quá là sai lầm một cách rõ rệt vì kẻ mà tôi tôn thờ không xứng đáng để cho tôi thờ phượng mà phải bỏ lỡ đi việc tôn thờ Allah."');
INSERT INTO "vn_mokhtasar" VALUES(36,25,'36:25','"Quả thật, giờ đây tôi - hỡi dân làng - đã tin tưởng vào Thượng Đế của tôi và cũng là Thượng Đế của tất cả quý vị. Xin quý vị hãy nghe lời khuyên của tôi, và tôi sẽ không bận tâm đến những gì quý vị đang đe dọa là sẽ giết chết tôi. Vì vậy, đám dân chúng của ông đã giết chết ông. Thế nên, Allah là Đấng đã ban cho ông vào được Thiên Đàng."');
INSERT INTO "vn_mokhtasar" VALUES(36,26,'36:26','Bỗng có một lời phán bảo thật là cao quý vang lên sau khi ông bị giết vì chính đạo của Allah: "Nhà ngươi hãy bước vào Thiên Đàng." Bởi thế, khi ông ta bước vào và nhìn thấy những hồng ân làm ông chóng ngợp ở chốn Thiên Đàng làm ông phải thốt lên trong sự nghẹn ngào: "Ôi, thật khổ thân cho dân chúng của tôi quá. Phải chi họ biết được những gì tôi được hưởng."');
INSERT INTO "vn_mokhtasar" VALUES(36,27,'36:27','Như được tha thứ tội lỗi mà tôi đã phạm và biết bao thiên ân khác mà Ngài đã ban cho. Giá như họ cũng tin tưởng như tôi là họ đã được hưởng như tôi rồi.');
INSERT INTO "vn_mokhtasar" VALUES(36,28,'36:28','Và TA không cần phải gởi các Thiên thần từ trên trời xuống để tiêu diệt đám dân của Y, những kẻ mà chúng cho rằng Y dối trá và ra tay giết chết Y. Bởi thế, TA đã ra lệnh cho các Thiên thần nhân nhượng với chúng. Bởi vì TA đã có sắp đặt sẵn chỉ thị tiêu diệt bọn chúng bằng một tiếng gầm thật to từ trên trời. Và TA không nhất thiết gởi các Thiên thần xuống để trừng phạt bọn chúng.');
INSERT INTO "vn_mokhtasar" VALUES(36,29,'36:29','Bởi thế, về câu chuyện tiêu diệt dân chúng của Y TA chỉ cần một tiếng gầm duy nhất giáng lên chúng thì chúng đã chết tươi không còn một kẻ nào trong số bọn chúng có thể sống sót. Bọn chúng giống như đang bị một đám lửa cháy ngùn ngụt rồi sau đó được dập tắt chỉ còn một đống tro tàn không còn sót lại thứ gì.');
INSERT INTO "vn_mokhtasar" VALUES(36,30,'36:30','Ôi! Thật tội nghiệp thay cho đám bầy tôi dối trá và cũng thấy xót xa cho chúng vào ngày Phục Sinh khi bọn chúng chứng kiến những cảnh trừng phạt. Như thế là vì chúng khi còn ở trần gian, không có một vị Thiên Sứ nào được Allah cử phái đến mà không bị chúng giễu cợt hay chế nhạo. Thế nên vào ngày Phục Sinh chúng phải chịu hậu quả cho việc chúng đã vượt quá mức cho phép của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(36,31,'36:31','Há! Những kẻ dối trá luôn chế giễu không nhìn thấy bài học của các bậc tiền nhân sống ở thời đại trước chúng đã bị TA giáng hình phạt hay sao? Chắc chắn, các tiền nhân đó đã chết và họ sẽ không thể trở lại trần gian một lần nào nữa. Thế nhưng, chúng vẫn cứ đâm đầu vào những việc làm vô bổ. Dựa vào đó Allah sẽ thanh toán cho việc làm của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(36,32,'36:32','Tất cả mọi cộng đồng không có trường hợp ngoại lệ đều sẽ phải trình diện TA vào ngày Phán Xét sau khi đã được TA phục sinh trở lại để nhận lấy kết quả cho từng việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(36,33,'36:33','Và có một dấu hiệu cho những kẻ dối trá nhận thấy được rằng TA (Allah) đã tái sinh mà sự tái sinh đó hoàn toàn là sự thật: Đó là một mảnh đất đã chết khô được TA tưới lên những trận mưa từ trên trời xuống. Bởi thế, TA đã làm cho cây cối đâm chồi nảy lộc đơm hoa kết trái cho ra những loại hoa quả thơm ngon để nhân loại dùng làm thực phẩm. Bởi thế, sự tái sinh mảnh đất chết khô bằng những trận mưa làm cho cây cối đâm chồi nảy lộc là minh chứng rằng TA thừa khả năng phục sinh chúng từ cái chết trở lại.');
INSERT INTO "vn_mokhtasar" VALUES(36,34,'36:34','Và TA biến mảnh đất ấy bằng những trận mưa tưới lên khiến nó trở thành hai mảnh vườn chà là và vườn nho và TA cũng đã làm cho nó có những mạch nước ngầm cho việc tưới tiêu.');
INSERT INTO "vn_mokhtasar" VALUES(36,35,'36:35','Để cho cả nhân loại hái ăn những loại trái cây từ hai mảnh vườn mà đã được Allah ban ân đến cho họ và đó là việc làm mà họ không thể làm được nó. Vậy mà họ còn không chịu tri ân Allah về các ân huệ của Ngài, họ không chịu thờ thượng duy nhất mình Ngài và tin tưởng vào các vị Thiên Sứ của Ngài, tại sao?');
INSERT INTO "vn_mokhtasar" VALUES(36,36,'36:36','Ôi! Thật thiêng liêng thay Allah Đấng Tối Cao, Đấng đã tạo ra cây cối và chính bản thân của loài người thành hai giới tính đực cái và cả trong những thứ mà nhân loại không biết từ đất liền, biển cả và những môi trường khác.');
INSERT INTO "vn_mokhtasar" VALUES(36,37,'36:37','Và có một bằng chứng để cho nhân loại tôn thờ một mình Allah, đó là TA (Allah) đã di chuyển ban ngày đi và để ban đêm đến; TA đem đến cho họ một màng đêm mù mịt sau khi ta đem ánh sáng ban ngày ra đi. Bởi thế, nhân loại chìm vào bóng tối.');
INSERT INTO "vn_mokhtasar" VALUES(36,38,'36:38','Có một dấu hiệu cho chúng tin tưởng vào Allah duy nhất là mặt trời sẽ di chuyển theo quỹ đạo của nó đến một thời hạn ấn định, điều mà chỉ có Allah mới biết rõ được thời khắc ấn định đó, không hề có bất cư sai lệch nào. Vì đó là sự định đoạt và an bài của Đấng Đầy Quyền Lực, Đấng mà không một ai vượt qua được quyền năng của Ngài và Ngài là Đấng Toàn Tri, không gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(36,39,'36:39','Và có một dấu hiệu cho họ để khẳng định tính duy nhất của Allah, đó là mặt trăng được Ngài ấn định thành những giai đoạn tròn khuyết cho tất cả các đêm; bắt đầu từ giai đoạn nhỏ (trăng lưỡi liềm) sau đó lớn dần lên (mặt trăng tròn) rồi lại nhỏ đi cho đến khi nó trở lại trạng thái (vầng trăng khuyết) giống như một quầy chà là đã chín vàng uốn công lại trông mỏng như lá lúa, hoặc cong như chiếc lưỡi liềm.');
INSERT INTO "vn_mokhtasar" VALUES(36,40,'36:40','Và các dấu hiệu như mặt trời, mặt trăng, ban đêm và ban ngày luôn di chuyển và vận hành theo đúng qui trình mà Allah đã ấn định, không có bất cứ sự sai lệch nào trong sự ấn định đó của Ngài. Bởi thế, mặt trời không được được phép phạm vào quỹ đạo của mặt trăng làm thay đổi hành trình vận hành hoặc làm mất đi ánh sáng của nó. Tương tự, ban đêm sẽ không được phép đi trước ban ngày khi giờ của ban ngày vẫn chưa kết thúc. Tất cả mọi tạo vật buộc phải di chuyển và vận hành đúng theo quỹ đạo của nó như đã được Allah ấn định và sắp đặt.');
INSERT INTO "vn_mokhtasar" VALUES(36,41,'36:41','Và có một dấu hiệu nữa cho chúng tin tưởng vào tính duy nhất của Allah cũng như để khẳng định về Hồng Phúc của Ngài dành cho chúng chính là Ngài đã che chở con cháu của Adam vào ở thời đại của Nuh được sống sót qua trận đại hồng thủy trên một chiếc tàu chở đầy tạo vật của Ngài. Quả thật, Allah đã chuyên chở đầy trên con thuyền đó mỗi thứ một cặp.');
INSERT INTO "vn_mokhtasar" VALUES(36,42,'36:42','Và có một dấu hiệu cho chúng tin vào tính duy nhất của Ngài cũng như cho thấy Hồng Phúc của Ngài đối với đám bầy tôi của Ngài là việc Ngài đã tạo ra cho chúng một chiếc tàu giống như chiếc tàu của Nuh để chuyên chở chúng.');
INSERT INTO "vn_mokhtasar" VALUES(36,43,'36:43','Và nếu muốn, TA sẽ nhấn chìm hết bọn chúng chết đuối và chúng không còn chút khả năng nào van xin hay cầu cứu một ai và cũng không một ai có khả năng cứu vớt chúng nếu như mệnh lệnh của TA được ban hành và thực thi.');
INSERT INTO "vn_mokhtasar" VALUES(36,44,'36:44','Ngoại trừ những ai được TA Thương Xót cứu vớt họ thoát khỏi sự chết đuối và cho họ được hưởng thụ khoái lạc trong một thời hạn đã được ấn định nhưng sẽ không có sự sai lệch nào về thời gian dù chỉ là một khoảnh khắc, để may ra cho chúng biết nghiền ngẫm để trở thành những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(36,45,'36:45','Và khi có lời kêu gọi những tên đa thần ương ngạnh đến với đức tin: "Các ngươi hãy cảnh giác điều trước (trên trần gian) và điều sau (ở đời sau) của các ngươi một cách hết sức thận trọng và hãy luôn luôn có sự cảnh giác ở trên trần gian này may ra các ngươi nhận được lòng Khoan Dung của Allah." Tuy nhiên, chúng không làm được như thế mà ngược lại chúng là những tên thờ ơ, quay lưng với lời kêu gọi đó.');
INSERT INTO "vn_mokhtasar" VALUES(36,46,'36:46','Và bất cứ khi nào các dấu hiệu nào của Allah được đem đến cho những kẻ đa thần cứng đầu để khẳng định tính duy nhất của Ngài và chỉ riêng Ngài đáng thờ phượng thì bọn chúng đều thẳng thừng quay lưng bỏ đi.');
INSERT INTO "vn_mokhtasar" VALUES(36,47,'36:47','Và khi có lời bảo những tên cứng đầu này:" Hãy giúp đỡ những người nghèo và những người gặp khó khăn bằng khối tài sản mà chính Allah đã ban bố cho quý vị." Chúng đáp lại với những người có đức tin rằng: "Chẳng lẽ chúng tôi phải nuôi ăn người mà nếu Allah muốn thì Ngài sẽ nuôi ăn người đó sao? Không, chúng tôi không phải là những người làm trái ý muốn của Ngài, các ngươi - hỡi những người có đức tin - thực sự là những người làm điều sai quấy một cách rõ ràng và ở xa chân lý."');
INSERT INTO "vn_mokhtasar" VALUES(36,48,'36:48','Và những kẻ vô đức tin phủ nhận sự phục sinh của bảo: "Bao giờ mới xảy ra sự phục sinh nếu các ngươi - hỡi những người có đức tin - là những người nói thật rằng nó thực sự xảy ra?"');
INSERT INTO "vn_mokhtasar" VALUES(36,49,'36:49','Điều mà những kẻ phủ nhận này đang chờ đợi chỉ là một tiếng còi đầu tiên được thổi lên. Bởi thế, khi tiếng còi đó được hụ lên, nó sẽ túm bắt lấy chúng trong lúc chúng đang bận rộn với cuộc sống trần gian này từ việc mua bán, trồng trọt, chăn nuôi và những bận rộn khác của cõi trần.');
INSERT INTO "vn_mokhtasar" VALUES(36,50,'36:50','Bởi thế, khi tiếng còi được hụ lên một cách bất ngờ thì chúng không kịp trăn trối cho nhau và chúng cũng không kịp trở về nhà đoàn tụ với gia đình của chúng. Không, chúng sẽ chết trong tình trạng bận rộn về những điều trần gian của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(36,51,'36:51','Và khi tiếng còi thứ hai được thổi lên vào ngày Phục Sinh thì chúng lần lượt từ dưới ngôi mộ đi ra và nhanh chân chạy đến Thượng Đế của chúng để nhận lấy sự thưởng phạt công minh từ nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(36,52,'36:52','Những kẻ vô đức tin phủ nhận sự phục sinh nói trong hối hận: " Ôi! Thật khổ cho thân chúng tôi. Ai đã làm chúng tôi sống lại từ nơi cõi mộ của chúng tôi vậy?" Rồi có lời đáp lại chúng: "Đấy chính là điều mà Allah đã hứa và chắc chắn nó phải diễn ra, và đây cũng là điều đã được các vị Thiên Sứ xác nhận trong bức Thông Điệp mà họ truyền đạt."');
INSERT INTO "vn_mokhtasar" VALUES(36,53,'36:53','Việc phục sinh chúng trở lại từ cõi mộ chẳng là gì ngoài việc chỉ cần một tiếng còi hụ thứ hai thì tất cả bọn chúng sẽ được tập hợp rồi được đưa đến trình diện trước TA vào ngày phán xét để nhận lấy sự thưởng phạt công minh của TA.');
INSERT INTO "vn_mokhtasar" VALUES(36,54,'36:54','Dưới sự phán xét Anh Minh Chính Trực (của Allah) vào ngày hôm đó, các ngươi - hỡi đám bầy tôi - sẽ không bị đối xử bất công bất cứ điều gì, việc xấu của các ngươi sẽ không bị thêm vào hay việc tốt của các ngươi cũng không bị lấy bớt đi mà quả thật các ngươi sẽ nhận lấy đầy đủ mọi điều mà các ngươi đã làm ở cuộc sống trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(36,55,'36:55','Quả thật, vào ngày Phán Xét, cư dân của Thiên Đàng sẽ không lo nghĩ đến ai khác bởi những gì họ đang chứng kiến từ niềm hạnh phúc vĩnh hằng, sự thành công vĩ đại. Họ cảm thấy vui sướng tột cùng trước phần thưởng đó.');
INSERT INTO "vn_mokhtasar" VALUES(36,56,'36:56','Họ và những người vợ của họ cùng nhau tận hưởng niềm hạnh phúc, họ sẽ nằm trên những chiếc trường kỉ cao bên dưới bóng mát của các Ngôi Vườn Thiên đàng.');
INSERT INTO "vn_mokhtasar" VALUES(36,57,'36:57','Trong Thiên Đàng, sẽ có đủ loại hoa quả tươi ngọn cho họ từ nho, sung, lựu và những gì họ yêu cầu. Họ sẽ có mọi thứ hưởng thụ mà họ mong muốn.');
INSERT INTO "vn_mokhtasar" VALUES(36,58,'36:58','Và điều hạnh phúc nhất đối với họ là lời chào Salam từ Thượng Đế Đấng Rất Mực Khoan Dung đến với họ. Bởi thế, khi Ngài chào Salam đến họ thì họ sẽ được bằng an về mọi phương diện và họ sẽ được lời chúc mừng mà không có lời chúc nào tốt hơn.');
INSERT INTO "vn_mokhtasar" VALUES(36,59,'36:59','Và vào ngày Phán Xét, những kẻ đa thần sẽ được bảo: "Các ngươi hãy dang ra, hãy tránh ra xa khỏi những người có đức tin, bởi họ không phù hợp ở cùng với các ngươi, để tương phản rõ ràng phần thưởng của các ngươi với phần thưởng của họ, và để tương phản rõ ràng nhóm các ngươi với nhóm của họ."');
INSERT INTO "vn_mokhtasar" VALUES(36,60,'36:60','Há TA đã không chỉ dụ và sắc lệnh cho các ngươi qua những chiếc lưỡi của các vị Thiên Sứ của TA rằng: "Này hỡi con cháu Adam, các ngươi chớ tuân theo Shaytan trong việc phạm vào các loại vô đức tin và tội lỗi. Quả thật, Shaytan là kẻ thù công khai của các ngươi. Làm sao mà một người khôn ngoan có thể tuân theo kẻ thù của y, kẻ thù mà nó đã được công khai cho y?!');
INSERT INTO "vn_mokhtasar" VALUES(36,61,'36:61','Và TA (Allah) đã sắc lệnh cho các ngươi - hỡi con cháu Adam - rằng hãy tôn thờ một mình TA, không được phép tổ hợp TA với bất cứ ai (vật gì). Việc thờ phượng riêng một mình TA và luôn vâng lời TA là con đường ngay thẳng dẫn đến sự hài lòng của TA và dẫn lối đến Thiên Đàng. Thế nhưng, các ngươi đã không tuân theo chỉ dụ và sắc lệnh của TA.');
INSERT INTO "vn_mokhtasar" VALUES(36,62,'36:62','Và quả thật, Shaytan đã làm lầm lạc đa số các ngươi. Chẳng lẽ các ngươi không có khối óc để hiểu rằng các ngươi được sắc lệnh tuân thủ theo Thượng Đế của các ngươi và tôn thờ duy nhất một mình Ngài - Đấng Vinh Quang - thôi ư?! Chẳng lẽ các ngươi không có khối óc để hiểu ra rằng TA đã cảnh báo các ngươi về việc đi theo Shaytan - kẻ thù công khai của các ngươi ư?!');
INSERT INTO "vn_mokhtasar" VALUES(36,63,'36:63','Đây chính là Hỏa Ngục mà các ngươi đã từng hứa hẹn khi còn ở trên trần gian vì tội vô đức tin của các ngươi. Nó là thứ đã từng vô hình đối với các ngươi nhưng giờ đây các ngươi đã tận mắt nhìn thấy Nó.');
INSERT INTO "vn_mokhtasar" VALUES(36,64,'36:64','Ngay hôm nay, các ngươi hãy đi vào Hỏa Ngục, các ngươi hãy hứng chịu cái nóng của Nó bởi sự vô đức tin nơi Allah của các ngươi lúc còn sống ở trên trần thế.');
INSERT INTO "vn_mokhtasar" VALUES(36,65,'36:65','Ngày Hôm nay, TA (Allah) sẽ khóa miệng chúng lại, chúng trở nên căm lặng không thể chối cải về những gì mà chúng đã từng vô đức tin và trái lệnh. TA sẽ khiến đôi tay của chúng nói chuyện để tố cáo chúng về những gì mà đã từng làm ở trần gian, TA để cho đôi chân của chúng làm nhân chứng cho những tội lỗi mà chúng đã từng bước đi đến với nó.');
INSERT INTO "vn_mokhtasar" VALUES(36,66,'36:66','Và nếu muốn TA (Allah) sẽ lấy đi ánh sáng của chúng khiến chúng bước đi trong sự tăm tối mù lòa. Chúng tranh nhau chạy đến chiếc cầu Sirat hầu đi qua để đến với Thiên Đàng nhưng chúng làm sao có thể đi qua được trong khi đôi mắt của chúng đã bị mù lòa.');
INSERT INTO "vn_mokhtasar" VALUES(36,67,'36:67','Và nếu muốn, TA (Allah) sẽ khiến cho chúng đứng yên tại chỗ trên đôi chân của chúng, chúng không thể di chuyển khỏi vị trí của chúng, chúng không thể bước tới phía trước cũng không thể bước lui về phía sau.');
INSERT INTO "vn_mokhtasar" VALUES(36,68,'36:68','Và những ai trong nhân loại được TA (Allah) kéo dài cuộc sống của y làm cho y sống thọ thêm thì TA sẽ biến y trở lại thành một người yếu ớt. Há chúng không chịu suy nghĩ để ngộ ra rằng trần gian này chỉ là cuộc sống tạm bợ và cuộc sống Đời Sau mới là vĩnh hằng ư?!');
INSERT INTO "vn_mokhtasar" VALUES(36,69,'36:69','Và TA (Allah) đã không dạy Muhammad (cầu xin Allah bình an cho người) thơ ca, vì điều đó không phù hợp với Y và cũng không phải thuộc kỹ năng của Y để Y hô to với các ngươi rằng Y là một nhà thơ. Quả thật cái mà TA dạy Y chính là lời Nhắc Nhở và Thiên Kinh Qur''an minh bạch dành cho những ai biết suy ngẫm về Nó.');
INSERT INTO "vn_mokhtasar" VALUES(36,70,'36:70','Mục đích để Y cảnh báo những ai có con tim còn sống biết nhận thức hầu mang lại điều lợi cho họ; và để những kẻ vô đức tin không còn lý do để chối cãi về tội lỗi của chúng khi chúng bị trừng phạt bởi vì lời cảnh báo đã đến với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(36,71,'36:71','Há chúng không thấy TA (Allah) đã tạo hóa các loại gia súc cho chúng để chúng tự do sử dụng cho lợi ích của chúng ư?!');
INSERT INTO "vn_mokhtasar" VALUES(36,72,'36:72','Và TA đã làm cho các loài gia súc phục tùng chúng. Bởi thế, một số loài chúng dùng để cưỡi trên lưng và còn chuyên chở các vật nặng cho chúng và một số loài chúng dùng để ăn thịt.');
INSERT INTO "vn_mokhtasar" VALUES(36,73,'36:73','Và ngoài việc dùng để cưỡi và chuyên chở đồ đạc cũng như để lấy thịt ăn thì các loài gia súc đó còn cho chúng nhiều mối lợi khác chẳng hạn như lông được dùng làm những tấm trải và quần áo; và chúng còn có nguồn thức uống từ các loại gia súc như sữa tươi. Thế bấy nhiêu Hồng Ân và còn những điều hơn thế nữa không đáng để cho chúng tri ân Allah hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(36,74,'36:74','Và những kẻ đa thần nhận các thần linh khác ngoài Allah để tôn thờ với niềm hy vọng các thần linh đó có thể giúp đỡ chúng thoát khỏi sự trừng phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(36,75,'36:75','Những thần linh mà họ đã thờ phượng chúng sẽ không giúp đỡ được gì cho bản thân họ và cũng không trợ giúp được gì cho việc họ đã tôn thờ chúng thay vì Allah. Và chúng chẳng qua chỉ là những pho tượng sẽ được tập hợp lại cùng với họ trong sự trừng phạt rồi tất cả bọn chúng lại chối bỏ nhau.');
INSERT INTO "vn_mokhtasar" VALUES(36,76,'36:76','Bởi thế, Ngươi - hỡi Thiên Sứ - chớ buồn phiền về những lời lẽ chế giễu của bọn chúng khi chúng nói rằng quả thật, Ngươi chẳng phải là một vị Thiên Sứ cũng chẳng là một nhà thơ phú hoặc chẳng là gì mà có thể thuyết phục được chúng. Quả thật, TA là Đấng đã biết rõ được những điều thầm kín và những điều công khai của chúng, không có gì có thể che giấu được TA và dựa vào đó TA sẽ trừng trị bọn chúng thích đáng.');
INSERT INTO "vn_mokhtasar" VALUES(36,77,'36:77','Há nhân loại không nghĩ đến việc Phục Sinh sau khi chết trong khi TA đã tạo hóa ra y từ một giọt tinh trùng trải qua nhiều giai đoạn cho đến khi y được hạ sinh và lớn lên hay sao mà lại có nhiểu tranh cãi? Há nhân loại không thấy sự việc đó là bằng chứng về việc Ngài thừa khả năng Phục Sinh hay sao?!');
INSERT INTO "vn_mokhtasar" VALUES(36,78,'36:78','Tầm hạn hẹp của kẻ vô đức tin và ngu dốt ki y lấy việc những khúc xương đã mục để làm bằng chứng cho việc không thể nào phục sinh. Y bảo " Ai có khả năng khôi phục nó lại được?" nhưng y lại quên mất bản thân y đã được tạo hóa ra từ không là gì cả.');
INSERT INTO "vn_mokhtasar" VALUES(36,79,'36:79','Ngươi- hỡi Muhammad - hãy trả lời y: "Đấng phục sinh khúc xương rã mục này là Đấng đã tạo ra nó lúc ban đầu." Bởi thế, việc đã tạo ra được nó lúc ban đầu thì việc tái tạo lại nó lần hai không hề khó khăn. Và Ngài là Đấng Quang Vinh, Ngài Am Tường tất cả mọi tạo hóa của Ngài, không có gì có thể giấu giếm được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(36,80,'36:80','"Ngài là Đấng đã tạo cho các ngươi - hỡi nhân loại - ngọn lửa từ một loại cây xanh tươi ẩm ướt mà các ngươi dùng nó để nhóm lửa." Bởi thế, Đấng đã kết hợp được giữa hai điều trái ngược - sự ẩm ướt của nước từ cây xanh có thể cho ra lửa cháy bùng - chắc chắn thừa khả năng phục sinh được cái chết.');
INSERT INTO "vn_mokhtasar" VALUES(36,81,'36:81','Há Ngài đã tạo hóa ra các tầng trời và trái đất và hai điều đó thật vĩ đại, vậy chẳng lẽ Ngài lại không có khả năng phục sinh chúng sau cái chết ư?! Chắc chắn, Ngài thừa khả năng bởi Ngài là Đấng tạo hóa ra tất cả vạn vật và Rất Mực Am Tường về chúng, không có gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(36,82,'36:82','Quả thật, khi Allah - Đấng Toàn Năng muốn bất cứ điều gì, Ngài chỉ cần phán "Hãy Thành!" thì ngay lập tức nó sẽ trở thành đúng theo ý của Ngài muốn. Tương tự, khi Ngài muốn làm cho sống, làm cho chết và phục sinh cái chết hoặc những những gì khác thì Ngài chỉ cần phán như thế.');
INSERT INTO "vn_mokhtasar" VALUES(36,83,'36:83','Bởi thế, Allah vinh quang và vượt xa những gì mà những kẻ thờ đa thần đã quy cho Ngài về sự bất lực. Vi Ngài là Đấng nắm quyền Thống Trị tất cả vạn vật, Ngài chi phối vạn vật theo ý Ngài muốn, tất cả mọi thứ đều nằm trong Tay của Ngài và Ngài là Đấng Duy Nhất mà các ngươi sẽ trở về gặp Ngài vào Ngày Sau để nhận lấy hậu quả cho những việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(37,1,'37:1','Thề bởi những Thiên Thần đứng thành hàng chỉnh tề, nghiêm trang, kính cẩn trong việc thờ phượng của họ.');
INSERT INTO "vn_mokhtasar" VALUES(37,2,'37:2','Và thề bởi những Thiên Thần di chuyển những đám mây đưa đến những nơi mà Allah muốn ban mưa xuống.');
INSERT INTO "vn_mokhtasar" VALUES(37,3,'37:3','Và thề bởi các Thiên Thần xướng đọc lời Mặc Khải của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(37,4,'37:4','Quả thật, Đấng Thờ Phượng đích thực của các ngươi - hởi nhân loại - là một Đấng duy nhất, Ngài không có đối tác ngang vai, và Ngài chính là Allah.');
INSERT INTO "vn_mokhtasar" VALUES(37,5,'37:5','Ngài (Allah) là Thượng Đế của các tầng trời và trái đất, là Thượng Đế của vạn vật nằm giữa trời đất và là Thượng Đế của mặt trời mọc và lặn suốt trong năm.');
INSERT INTO "vn_mokhtasar" VALUES(37,6,'37:6','Quả thật, TA đã tô điểm cho các tầng trời thấp nhất, gần với trái đất bằng sự trang hoàng lộng lẫy với các tinh tú trông giống như những viên ngọc lấp lánh.');
INSERT INTO "vn_mokhtasar" VALUES(37,7,'37:7','Và TA bảo vệ tầng trời hạ giới khỏi từng tên Shaytan phản nghịch bằng những vì sao. Chúng bị ném bởi những ngôi sao.');
INSERT INTO "vn_mokhtasar" VALUES(37,8,'37:8','Những tên Shaytan này không thể nghe trộm những lời hội nghị của các vị Thiên Thần trên trời khi họ nói chuyện với nhau về những điều mà Allah mặc khải cho họ từ các giáo luật của Ngài (không phải các điều trong sự tiền định). Chúng sẽ bị ném bởi những hòn lửa (các ngôi sao lửa) từ mọi phía.');
INSERT INTO "vn_mokhtasar" VALUES(37,9,'37:9','Chúng bị đuổi đi thật xa để không thể nghe trộm và vào Ngày Sau chúng sẽ bị trừng phạt bằng một hình phạt vô cùng đau đớn, vĩnh viễn và liên tục.');
INSERT INTO "vn_mokhtasar" VALUES(37,10,'37:10','Ngoại trừ những tên Shaytan nào tranh thủ nghe lén được một lời nào đó từ cuộc hội nghị của các vị Thiên Thần về một thông tin nào đó nhưng thông tin này chưa được truyền xuống cư dân của trái đất. Thế là tên Shaytan đó bị những hòn lửa sáng rực rượt đuổi và đôt cháy. Đôi lúc lời được nghe trộm đó có thể được truyền đạt cho đồng bọn của hắn trước khi hòn lửa bắt kịp và đốt cháy hắn. Và đồng bọn của hắn sau đó truyền tới tai của những tên bói toán. Và chúng thêm vào hàng trăm điều dối.');
INSERT INTO "vn_mokhtasar" VALUES(37,11,'37:11','Ngươi - hỡi Muhammad - hãy hỏi những kẻ phủ nhận việc Phục sinh: "Chẳng lẽ chúng cho rằng việc TA tạo hóa chúng với thân hình khỏe mạnh với các bộ phận cơ thể của chúng khó hơn việc TA tạo hóa ra các tầng trời, trái đất và các vị Thiên Thần ư?" Quả thật, TA đã tạo chúng ra từ một loại đất sét nhão được kết lại. Sao chúng lại có thể phủ nhận sự Phục Sinh khi mà bản thân chúng được tạo ra rất đơn giản, đó là từ một loại đất sét nhão, dính?!');
INSERT INTO "vn_mokhtasar" VALUES(37,12,'37:12','Quả thật, Ngươi - hỡi Muhammad - đã kinh ngạc về quyền năng của Allah cũng như sự điều hành mọi hoạt động của tạo vật. Ngươi cũng đã ngạc nhiên về việc những tên đa thần phủ nhận sự Phục Sinh và chúng luôn cương quyết bác bỏ về sự phục sinh qua việc chế giễu những gì Ngươi nói nó.');
INSERT INTO "vn_mokhtasar" VALUES(37,13,'37:13','Và một khi những tên đa thần được cảnh báo về một sự việc nào đó thì chúng không bao giờ để tâm, bởi vì con tim của chúng đã chai cứng.');
INSERT INTO "vn_mokhtasar" VALUES(37,14,'37:14','Và khi chúng nhìn thấy một dấu hiệu trong các dấu hiệu của Thiên Sứ để chứng minh cho sự thật của Người thì chúng thường buông lời chế giễu và tỏ vẻ ngạc nhiên thích thú.');
INSERT INTO "vn_mokhtasar" VALUES(37,15,'37:15','Và chúng nói: "Điều mà Muhammad mang đến rõ ràng chỉ là một trò ma thuật".');
INSERT INTO "vn_mokhtasar" VALUES(37,16,'37:16','Chúng nói: Phải chăng khi chúng tôi đã chết, thân xác chúng tôi bị phân hủy thành cát bụi và từng khúc xương bị rã mục rồi chúng tôi lại được cho sống trở lại ư? Quả thật, điều này thật xa vời.');
INSERT INTO "vn_mokhtasar" VALUES(37,17,'37:17','Chúng nói: "Phải chăng ngay cả tổ tiên của chúng tôi đã chết từ xa xưa cũng được cho sống lại nữa hay sao?"');
INSERT INTO "vn_mokhtasar" VALUES(37,18,'37:18','Ngươi - hỡi Muhammad - hãy trả lời bọn chúng: "Vâng, đúng vậy, chắc chắn các ngươi sẽ được phục sinh trở lại sau khi thân xác các ngươi bị phân hủy thành các bụi và từng khúc xương bị rã mục và kể cả tổ tiên của của các ngươi đã chết từ thời xa xưa, tất cả đều được phục sinh trở lại và các ngươi sẽ bị hạ nhục."');
INSERT INTO "vn_mokhtasar" VALUES(37,19,'37:19','Bởi thế, chỉ cần một tiếng còi duy nhất được vang lên (Tiếng còi thứ hai ở ngày tận thế) thì họ sẽ tập trung lại rồi họ sẽ ngóng nhìn những gì xảy ra ở cái ngày Phán Xét ấy và họ sẽ được biết điều mà Allah sẽ làm với họ.');
INSERT INTO "vn_mokhtasar" VALUES(37,20,'37:20','Và bọn đa thần từng phủ nhận về sự phục sinh than rằng: "Ôi thật bất hạnh cho chúng tôi vào cái ngày Xét Xử này đây, Ngày mà Allah thưởng phạt cho đám bầy tôi của Ngài về những gì mà họ đã từng làm ở cuộc sống trần gian".');
INSERT INTO "vn_mokhtasar" VALUES(37,21,'37:21','Bởi thế, có lời phán bảo chúng: "Đây là Ngày định đoạt giữa đám bây tôi. Ngày mà các ngươi đã từng phủ nhận và bác bỏ lúc còn ở thế giới trần gian."');
INSERT INTO "vn_mokhtasar" VALUES(37,22,'37:22','Vào Ngày đó các vị Thiên Thần được bảo: "Các ngươi hãy tập hợp những tên đa thần đã làm điều bất công cùng với những kẻ đồng hội đồng thuyền với chúng và những tên cầm đầu xúi giục chúng trong việc phủ nhận. Chúng đã không thờ phượng các bục tượng ngoài Allah, nhưng những kẻ cầm đầu đã dẫn chúng đến con đường vào Hỏa Ngục. Và quả thật đó cũng là số phận của chúng."');
INSERT INTO "vn_mokhtasar" VALUES(37,23,'37:23','Vào Ngày đó các vị Thiên Thần được bảo: "Các ngươi hãy tập hợp những tên đa thần đã làm điều bất công cùng với những kẻ đồng hội đồng thuyền với chúng và những tên cầm đầu xúi giục chúng trong việc phủ nhận. Chúng đã không thờ phượng các bục tượng ngoài Allah, nhưng những kẻ cầm đầu đã dẫn chúng đến con đường vào Hỏa Ngục. Và quả thật đó cũng là số phận của chúng."');
INSERT INTO "vn_mokhtasar" VALUES(37,24,'37:24','Các ngươi hãy giữ chúng lại trước khi dẫn chúng vào trong Hỏa Ngục vì chúng phải bị tra hỏi. Sau đó, các ngươi hãy dẫn chúng vào trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(37,25,'37:25','Và có lời khiển trách chúng: "Tại sao các ngươi không chịu giúp đỡ lẫn nhau giống như đã từng giúp đỡ lẫn nhau khi ở trên trần gian và các ngươi đã khẳng định rằng những pho tường này đây có thể giúp đỡ được các ngươi kia mà?"');
INSERT INTO "vn_mokhtasar" VALUES(37,26,'37:26','Không, vào ngày Phán Xét chúng tự hạ mình phủ phục trước Allah và lúc đó chúng không thể giúp đỡ nhau bởi sự bất lực và hết mưu đồ của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,27,'37:27','Và rồi chúng quay qua oán trách, đổ lỗi cho nhau trong lúc sự oán trách và cãi vã trở nên vô ích.');
INSERT INTO "vn_mokhtasar" VALUES(37,28,'37:28','Những người đi theo nói với những kẻ được theo: "Quả thật, các người - hỡi các nhà lãnh tụ của chúng tôi - chính là những người đến gặp chúng tôi ở trần gian. Chính các người là những người xúi giục chúng tôi trở thành những người vô đức tin và Shirk với Allah. Chính các ngươi dẫn chúng tôi vào hố sâu tội lỗi và đưa chúng tôi rời xa sự thật mà các vị Thiên Sứ của Allah mang đến."');
INSERT INTO "vn_mokhtasar" VALUES(37,29,'37:29','Các nhà lãnh tụ đáp lại những người đi theo họ: "Quả thât, chúng tôi không hề xúi giục các người giống như những gì các người đã nói. Ngược lại, các người mới là những kẻ vô đức tin, chính các người là những kẻ đã khước từ sự thật."');
INSERT INTO "vn_mokhtasar" VALUES(37,30,'37:30','Chúng tôi không hề thuyết phục các ngươi làm theo hay có chút đặc quyền nào ép buộc các ngươi phải phủ nhận, tổ hợp và lún vào hố sâu tội lỗi. Ngược lại, chính các ngươi tự mình vượt quá mức giới hạn nghiêm cấm trong sự vô đức tin và lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(37,31,'37:31','"Bởi thế, đó là điều tất nhiên đối với chúng tôi và các người y như Lời Allah đã hứa: { Rằng chắc chắn, TA sẽ nhét đầy Hỏa Ngục toàn bộ, nhà ngươi và kẻ nào trong chúng theo ngươi.} [ Câu kinh - Surah Al-Zumar, câu 39]. Bởi thế, chúng ta buộc phải nếm - không thể nào thoát khỏi - điều mà Thượng Đế của chúng ta đã từng hứa hẹn."');
INSERT INTO "vn_mokhtasar" VALUES(37,32,'37:32','"Chúng tôi đã dẫn các ngươi đi vào con đường lạc lối và vô đức tin. Quả thật, chúng tôi cũng đã lạc khỏi con đường Chỉ Đạo."');
INSERT INTO "vn_mokhtasar" VALUES(37,33,'37:33','Bởi thế, vào Ngày Phán Xét những kẻ đi theo và những tên được theo đều sẽ cùng chịu hình phạt với nhau.');
INSERT INTO "vn_mokhtasar" VALUES(37,34,'37:34','Quả thật, giống như những gì TA đã làm với những kẻ này trong việc để chúng nếm lấy sự trừng phạt, TA sẽ làm đối với những kẻ tội lỗi ngoài chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,35,'37:35','Quả thật, những kẻ đa thần lúc còn ở trên trần gian, khi được bảo: "(Không có Thượng Đế đích thực nào khác duy chỉ một mình Allah), hãy làm theo câu nói này và từ bỏ những gì trái ngược với nó" thì chúng chối bỏ, chúng ngoảnh mặt với chân lý một cách tự cao tự đại."');
INSERT INTO "vn_mokhtasar" VALUES(37,36,'37:36','Và chúng nói với lời lẽ biện minh cho sự vô đức tin của chúng rằng: "Chẳng lẽ chúng tôi phải bỏ đi việc thờ phượng của tổ tiên chúng tôi chỉ vì lời nói của một tên thi hào đãng trí này hay sao?" Lời này của chúng ám chỉ Thiên Sứ của Allah - cầu xin Allah ban bình an cho Người.');
INSERT INTO "vn_mokhtasar" VALUES(37,37,'37:37','Quả thật, chúng đã quá mức trong sự dối trá và bịa đặt. Bởi Thiên Sứ của Allah - cầu xin Allah ban bình an cho Người - không phải là một kẻ đãng trí cũng chẳng phải là một nhà thơ, mà Y thực sự đã mang Thiên Kinh Qur''an đến để kêu gọi mọi người trở về với sự tôn thờ một mình Allah và đi theo đường lối của Thiên Sứ của Ngài, đồng thời Y đến để xác nhận lại những điều mà các vị Thiên Sứ trước đây đã mang đến từ nơi Allah về sự thờ phượng một mình Ngài; Y đến để khẳng định cội nguồn và không hề làm khác họ bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(37,38,'37:38','Quả thật, các ngươi - hỡi những tên đa thần - sẽ phải nếm lấy hình phạt vô cùng đau đớn vào Ngày Phán Xét bởi sự vô đức tin nơi các vị Thiên Sứ của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(37,39,'37:39','Và các ngươi - hỡi những kẻ đa thần - sẽ bị sự trừng phạt chỉ bởi những gì mà các ngươi đã làm ở trên trần gian từ việc vô đức tin nơi Allah và phạm tội.');
INSERT INTO "vn_mokhtasar" VALUES(37,40,'37:40','Riêng các bầy tôi có đức tin của Allah, những người mà họ luôn thành tâm trong việc thờ phượng Ngài, sẽ được Ngài cứu rỗi khỏi sự trừng phạt này.');
INSERT INTO "vn_mokhtasar" VALUES(37,41,'37:41','Những bầy tôi chân thành là những người sẽ hưởng thụ được muôn vàn bổng lộc của Allah, những bổng lộc được biết rất rõ ràng về sự tốt lành cũng như sư vô tận của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,42,'37:42','Một trong những bổng lộc này là họ được thưởng thức những loại trái quả tươi ngon, họ sẽ ăn chúng tùy thích theo sự lựa chọn của mình; và họ sẽ được ưu đãi thêm bằng cách được nâng cao địa vị và được nhìn ngắm diện mạo Tuyệt Đẹp và Phục Lành của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(37,43,'37:43','Họ được hưởng thụ tất cả mọi thứ đó trong các Ngôi Vườn Thiên Đàng, nơi của niềm hạnh phúc trường tồn.');
INSERT INTO "vn_mokhtasar" VALUES(37,44,'37:44','Họ sẽ tựa mình thư giãn trên những chiếc tràng kỉ, mặt đối mặt nhìn nhau.');
INSERT INTO "vn_mokhtasar" VALUES(37,45,'37:45','Những ly rượu tinh khiết. trong suốt tựa như dòng suối chảy được chuyển vòng quanh họ.');
INSERT INTO "vn_mokhtasar" VALUES(37,46,'37:46','Trong vắt tinh khiết, tuyệt hảo, làm cho người uống thích thú, sảng khoái.');
INSERT INTO "vn_mokhtasar" VALUES(37,47,'37:47','Không giống như những loại rượu ở trần gian. Nó không làm họ mất đi lí trí của sự say xỉn và nó cũng không làm họ nhức đầu chóng mặt. Nó làm cho người uống thêm cường tráng và tinh thần sảng khoái.');
INSERT INTO "vn_mokhtasar" VALUES(37,48,'37:48','Trong Thiên Đàng, bên cạnh họ là những nàng trinh nữ xinh đẹp với đôi mắt to trữ tình, không nhìn ngắm bất cứ ai khác ngoài chồng của mình.');
INSERT INTO "vn_mokhtasar" VALUES(37,49,'37:49','Làn da mịn của các nàng trinh nữ tựa như quả trứng chim non được bao bọc kín chưa hề có một bàn tay nào chàm vào nó.');
INSERT INTO "vn_mokhtasar" VALUES(37,50,'37:50','Cư dân Thiên Đàng mặt đối mặt trò chuyện với nhau, họ thường ôn lại những kỷ niệm của trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(37,51,'37:51','Một trong những người có đức tin này nói: "Quả thật, trước kia ở trên trần gian tôi có một người bạn, y đã phủ nhận sự Phụ Sinh."');
INSERT INTO "vn_mokhtasar" VALUES(37,52,'37:52','Người bạn đó thường phản đối và buông lời mỉa mai tôi: "Phải chăng anh - hởi người tin tưởng - thực sự tin rằng những người đã chết sẽ được cho sống lại ư?"');
INSERT INTO "vn_mokhtasar" VALUES(37,53,'37:53','Kẻ phủ nhận sự Phục Sinh tiếp tục nói: "Phải chăng khi chúng ta chết đi, thân xác chúng ta đã bị phân hủy thành cát bụi, và xương cốt của chúng ta bị rã mục, chúng ta sẽ được cho sống trở lại để bị phán xét và thưởng phạt cho những việc làm của chúng ta trên thế gian thật sao?"');
INSERT INTO "vn_mokhtasar" VALUES(37,54,'37:54','Người có đức tin nói với những bạn của mình đang ngụ trong Thiên Đàng: "Các anh hãy cùng tôi nhìn xuống mà xem số phận của người bạn đó của tôi, người bạn mà trước kia anh ta đã phủ nhận về sự Phục Sinh?"');
INSERT INTO "vn_mokhtasar" VALUES(37,55,'37:55','Thế là, anh ta nhìn xuống và thấy người bạn đó của mình đang ở trung tâm của Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(37,56,'37:56','Y nói: "Thề bởi Allah! Hỡi người bạn của tôi - suýt nữa anh đã hủy diệt tôi, suýt nữa anh đã lôi kéo tôi vào Hỏa Ngục cùng với anh bởi việc anh kêu gọi tôi đến với sự vô đức tin và phủ nhận sự Phục Sinh."');
INSERT INTO "vn_mokhtasar" VALUES(37,57,'37:57','"Nếu như không nhờ Ân Huệ của Allah dẫn dắt tôi trở thành người có đức tin cũng như sự kiên định ở nơi Ngài thì giờ này đây tôi đã bị trừng phạt giống như anh mất rồi."');
INSERT INTO "vn_mokhtasar" VALUES(37,58,'37:58','Hỡi những bạn của Thiên Đàng, chúng ta sẽ không bao giờ chết nữa.');
INSERT INTO "vn_mokhtasar" VALUES(37,59,'37:59','"Ngoại trừ cái chết đầu tiên của chúng ta ở cuộc sống trần gian! Chúng ta sẽ sống mãi mãi ở nơi Thiên Đàng này và chúng ta không phải chịu bất cứ hình phạt nào giống như những kẻ vô đức tin."');
INSERT INTO "vn_mokhtasar" VALUES(37,60,'37:60','Quả thật, phần thưởng mà Thượng Đế đã ban cho chúng ta này đây - từ việc được vào Thiên Đàng, sự mãi mãi trong đó, và sự bằng an khỏi Hỏa Ngục - là một sự thắng lợi to lớn không có gì sánh bằng.');
INSERT INTO "vn_mokhtasar" VALUES(37,61,'37:61','Với phần thưởng vĩ đại như thế, những người làm phải nên phấn đấu làm tốt. Bởi vì, đấy là một giao dịch cho lợi nhuận to lớn.');
INSERT INTO "vn_mokhtasar" VALUES(37,62,'37:62','Hồng Phúc mà Allah đã chuẩn bị cho đám bầy tôi thành tâm phục tùng Ngài được nói đến tốt hơn và huy hoàng hơn hay cây Zaqqum đáng nguyền rủa đượcThiên Kinh Qur''an mô tả là thức ăn của những kẻ vô đức tin, thức ăn mà khi ăn vào nó không làm cho no cũng không làm cho hết đói?!');
INSERT INTO "vn_mokhtasar" VALUES(37,63,'37:63','Quả thật, TA tạo ra loại cây Zaqqum để trừng trị những tên sai quấy bởi sự vô đức tin và việc làm tội lỗi của chúng và bởi vì chúng đã từng nói: "Quả thật, lửa làm sẽ ăn cây cối chứ cây cối không thể mọc lên trong lửa được."');
INSERT INTO "vn_mokhtasar" VALUES(37,64,'37:64','Quả thật, loại cây Zaqqum là một loại cây sần sùi gai gốc và nó được mọc ra từ đáy của Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(37,65,'37:65','Quả của cây Zaqqum mọc ra với hình dạng trông giống như những cái đầu của lũ Shaytan, nó trong thật xấu xí và kinh tởm có nghĩa là vị của nó rất kinh tởm.');
INSERT INTO "vn_mokhtasar" VALUES(37,66,'37:66','Quả thật, những tên vô đức tin sẽ ăn trái Zaqqum cực đắng đó và chúng sẽ nhét đầy vào cái bụng đang đói trống rỗng của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,67,'37:67','Rồi sau khi đã ăn trái cực đặng đó, chúng được cho uống một loại nước dơ cực nóng.');
INSERT INTO "vn_mokhtasar" VALUES(37,68,'37:68','Sau đó, chúng được đưa vào Hỏa Ngục trở lại, chúng phải chịu từ hình phạt này đến hình phạt khác.');
INSERT INTO "vn_mokhtasar" VALUES(37,69,'37:69','Quả thật, những kẻ vô đức tin này đã thấy tổ tiên của chúng đi lạc khỏi con đường Chỉ Đạo nhưng chúng vẫn đi theo cách mù quáng.');
INSERT INTO "vn_mokhtasar" VALUES(37,70,'37:70','Thế nhưng chúng vẫn nhanh chân theo gót của tổ tiên họ trên con đường lạc lối.');
INSERT INTO "vn_mokhtasar" VALUES(37,71,'37:71','Và quả thật, đa số người trước chúng đều bị lầm lạc, chư không phải riêng đám dân của Ngươi - hỡi Thiên Sứ - là cộng đồng đầu tiên bị lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(37,72,'37:72','Và quả thật, TA đã cử phái các Sứ giả trong những cộng đồng đầu tiên đó đến để cảnh báo về sự trừng phạt của Allah nhưng bọn chúng đã phủ nhận.');
INSERT INTO "vn_mokhtasar" VALUES(37,73,'37:73','Bởi thế, Ngươi hãy nhìn xem - hỡi Thiên Sứ - kết cuộc của những kẻ được cảnh báo đã xảy ra như thế nào khi chúng không đáp lại lời cảnh báo của họ. Rồi đây, chúng sẽ bị đày vào Hỏa Ngục, chúng sẽ mãi mãi ở trong đó bởi sự vô đức tin và phủ nhận các Sứ giả được gửi đến với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,74,'37:74','Ngoại trừ những ai thành tâm với Allah trong đức tin nơi Ngài. Quả thật, họ sẽ được cứu rỗi khỏi sự trừng phạt, cái mà nó là kết cuộc của những kẻ phủ nhận và vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(37,75,'37:75','Quả thật, Nabi Nuh - cầu xin bình an cho Người - đã cầu xin TA trừng phạt đám dân phủ nhận Y và TA là Đấng Ưu Việt trong sự đáp lại nên TA đã nhanh đáp lại lời cầu xin của Y trong việc trừng phạt chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,76,'37:76','Và TA đã giải cứu cả nhà của Y và những người có đức tin cùng với Y thoát khỏi trận đại hồng thủy mà TA đã gửi đến tiêu diệt những kẻ vô đức tin trong đám dân của Y.');
INSERT INTO "vn_mokhtasar" VALUES(37,77,'37:77','Và TA chỉ giải cứu gia đình của Y và những người có đức tin theo Y. TA đã nhấn chìm những kẻ vô đức tin trong đám dây của Y.');
INSERT INTO "vn_mokhtasar" VALUES(37,78,'37:78','Và TA để lại cho Y những cộng đồng chân chính sau này luôn ca ngợi Y.');
INSERT INTO "vn_mokhtasar" VALUES(37,79,'37:79','Sự bằng an và phúc lành cho Nuh qua những lời cầu phúc của những cộng đồng chân chính sau này. Lời nhắc tốt đẹp về Y vẫn được lưu truyền.');
INSERT INTO "vn_mokhtasar" VALUES(37,80,'37:80','Quả thật, tương tự như phần thưởng mà TA ban thưởng cho Nuh - cầu xin bình an cho Người -, TA sẽ ban thưởng cho những người làm tốt trong việc tôn thờ TA và tuân theo mệnh lệnh của một mình TA.');
INSERT INTO "vn_mokhtasar" VALUES(37,81,'37:81','Quả thật, Nuh là một trong những người bề tôi có đức tin trung kiên của TA.');
INSERT INTO "vn_mokhtasar" VALUES(37,82,'37:82','Sau đó, TA đã nhấn chìm đám người còn lại bằng một trận đại hồng thủy bao mà TA đã gửi đến trừng phạt chúng. Bởi thế, không một ai trong bọn chúng có thể sống sót.');
INSERT INTO "vn_mokhtasar" VALUES(37,83,'37:83','Và Ibrahim đã thực sự thuộc tôn giáo của Nuh, đó là tôn giáo kêu gọi nhân loại thờ phượng duy nhất một mình Allah.');
INSERT INTO "vn_mokhtasar" VALUES(37,84,'37:84','Hãy nhớ rằng Y đã đến với Thượng Đế của Y bằng một tấm lòng thanh khiết không dính bất cứ Shirk nào, trái tim của Y một lòng hướng về Allah.');
INSERT INTO "vn_mokhtasar" VALUES(37,85,'37:85','Khi Y nói với cha và dân chúng của Y là những người đa thần với lời mang tính phản đối: "Đó là thứ gì mà các người thờ phượng thay vì Allah?"');
INSERT INTO "vn_mokhtasar" VALUES(37,86,'37:86','Chẳng phải những thần linh mà các người thờ phượng ngoài Allah chỉ là những thứ ngụy tạo đó sao?');
INSERT INTO "vn_mokhtasar" VALUES(37,87,'37:87','Các người nghĩ thế nào - hỡi người dân - về Thượng Đế của vũ trụ và muôn loài nếu như các người trở về gặp Ngài trong lúc các người tôn thờ những ai (vật) khác ngoài Ngài? Và Ngài sẽ làm gì với các ngươi khi các ngươi trình diện Ngài?');
INSERT INTO "vn_mokhtasar" VALUES(37,88,'37:88','Ibrahim nhìn lên các vì sao trên trời suy nghĩ toan tính làm sao thoát khỏi đám dân của Y.');
INSERT INTO "vn_mokhtasar" VALUES(37,89,'37:89','Thế rồi Y lựa lời khéo léo tìm cách không tháp tùng đi cùng với dân chúng của Y đến lễ hội (ngày tết) của họ: " Thật sự, tôi đã bị bệnh"');
INSERT INTO "vn_mokhtasar" VALUES(37,90,'37:90','Thế là người dân của Y bỏ lại Y, ra đi dự lễ hội.');
INSERT INTO "vn_mokhtasar" VALUES(37,91,'37:91','Thế là Y lén đi đến các thần linh mà chúng thờ phượng thay vì Allah, Y buông lời giễu cợt với các thần linh của chúng: Sao các ngươi không chịu ăn những thức ăn mà bọn đa thần đa dâng cúng các ngươi?!');
INSERT INTO "vn_mokhtasar" VALUES(37,92,'37:92','Các ngươi có chuyện gì mà sao không nói chuyện và cũng không trả lời người đang hỏi các ngươi vậy? Các thần linh được thờ phượng ngoài Allah là như thế này đó sao?!');
INSERT INTO "vn_mokhtasar" VALUES(37,93,'37:93','Thế rồi, Ibrahim nhảy thẳng tới, dùng tay phải của mình đập vỡ chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,94,'37:94','Sau đó, đám người thờ phượng những pho tượng này vội vàng nhanh chân chạy đến gặp Y trong sự giận dữ.');
INSERT INTO "vn_mokhtasar" VALUES(37,95,'37:95','Ibrahim giáp mặt chúng bằng sự kiên định, Y đã nói với chúng nhằm thức tỉnh chúng: "Chẳng lẽ các người lại thờ cúng ngoài Allah những thần linh do chính đôi tay các người đã nắn tạc ra ư?!"');
INSERT INTO "vn_mokhtasar" VALUES(37,96,'37:96','Các người thờ cúng các pho tượng mà các người làm ra trong khi Allah - Đấng Vinh Quang đã tạo ra các người và các pho tượng mà các người làm ra. Các người thờ cùng chúng trong khi chỉ một mình Ngài mới là Đấng xứng đáng được thờ phượng, không ai (vật gì) ngoài Ngài có quyền đó.');
INSERT INTO "vn_mokhtasar" VALUES(37,97,'37:97','Nhưng khi chúng đuối lý trước Ibrahim, chúng lại dùng đến vũ lực đáp trả Y. Chúng bàn bạc nhau cách xử trí Ibrahim, chúng bảo: "Chúng ta hãy dựng một tòa nhà nhét đầy củi khô trong đó, đốt cháy nó lên rồi ném hắn vào đó!".');
INSERT INTO "vn_mokhtasar" VALUES(37,98,'37:98','Đám dân của Ibrahim muốn giết hại Ibrahim. Nhưng TA đã làm cho chúng thất bại hoàn toàn, TA đã khiến cho ngọn lửa trở nên mát lạnh và an toàn cho Y.');
INSERT INTO "vn_mokhtasar" VALUES(37,99,'37:99','Và Ibrahim bảo: "Ta sẽ rời bỏ xứ sở này của ta để di cư đến Thượng Đế của Ta để Ta có thể thờ phượng Ngài và Thượng Đế của Ta sẽ dẫn dắt Ta đến với những gì tốt đẹp nhất ở trên đời này và cả Đời sau.');
INSERT INTO "vn_mokhtasar" VALUES(37,100,'37:100','"Lạy Thượng Đế của bề tôi! Xin Ngài ban cho bề tôi một đứa con trai đức độ để có thể giúp đỡ và san sẻ với bề tôi trong lúc đám dân của bề tôi đã xa lánh bề tôi."');
INSERT INTO "vn_mokhtasar" VALUES(37,101,'37:101','Bởi thế, TA đã đáp lại lời cầu xin đó của Y, TA đã ban cho Y tin mừng về một đứa con trai có lòng kiên nhẫn và tinh thần chịu đựng. Đứa con trai đó chính là Isma''il - cầu xin sự bằng an cho Người.');
INSERT INTO "vn_mokhtasar" VALUES(37,102,'37:102','Khi Isma''il là một cậu bé thường quanh quẩn bên cha của mình thì Ibrahim nằm mộng và giấc mộng của các vị Thiên Sứ chính là sự Mặc Khải. Ibrahim bày tỏ tâm tư cho con trai của mình về giấc mộng: "Này con trai yêu dấu của cha! Quả thật, cha nằm mộng thấy rằng cha cần phải giết tế con, thế con nghĩ sao về sự việc đó." Isma''il đã hiểu tâm tư của cha mình liền đáp lại: "Thưa cha! Cha hãy làm theo những gì mà Allah ra lệnh cho cha, rồi cha sẽ thấy con là một người biết chịu đựng và hài lòng với sắc lệnh của Allah."');
INSERT INTO "vn_mokhtasar" VALUES(37,103,'37:103','Bởi thế, khi hai cha con phục tùng chấp hành theo lệnh của Allah, Ibrahim đặt con trai của mình nằm nghiêng bắt đầu thực hiện việc giết tế theo lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(37,104,'37:104','TA đã gọi Ibrahim trong lúc Y đang cố gắng thực hiện mệnh lệnh của Allah bằng việc giết tế con trai của mình: "Này hỡi Ibrahim!"');
INSERT INTO "vn_mokhtasar" VALUES(37,105,'37:105','Ngươi thực sự đã chứng thực điều mà Ngươi đã thấy trong giấc mộng mà trong giấc mộng và Ngươi thực sự đã chấp hành việc giết tế con trai của mình. Quả thật, giống như việc TA đã ban thưởng cho Ngươi bởi tấm lòng thành của Ngươi từ cuộc thử thách to lớn này, TA sẽ ban thưởng cho những người làm tốt đẹp như thê, TA sẽ cứu vớt họ thoát khỏi nghịch cảnh và nguy khốn.');
INSERT INTO "vn_mokhtasar" VALUES(37,106,'37:106','Quả thật, đây là một thử thách rất rõ ràng, và Ibrahim thực sự đã vượt qua được.');
INSERT INTO "vn_mokhtasar" VALUES(37,107,'37:107','Và TA đã chuộc mạng Isma''il bằng một con cừu vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(37,108,'37:108','Và TA đã để danh tiếng thơm lừng của Ibrahim nơi hậu thế.');
INSERT INTO "vn_mokhtasar" VALUES(37,109,'37:109','Allah đã gởi lời chào bằng an đến cho Ibrahim, một sự bằng an khỏi mọi điều tai hại.');
INSERT INTO "vn_mokhtasar" VALUES(37,110,'37:110','Giống như TA đã ban thưởng cho Ibrahim về việc Y đã phục tùng mệnh lệnh, TA sẽ ban thưởng cho những người làm tốt đúng như thế.');
INSERT INTO "vn_mokhtasar" VALUES(37,111,'37:111','Quả thật, Ibrahim là một bề tôi đầy đức tin của TA vì Y luôn phục tùng những gì Allah ban hành.');
INSERT INTO "vn_mokhtasar" VALUES(37,112,'37:112','Và TA đã báo tin mừng cho Ibrahim về một đứa con trai khác của Y sẽ trở thành một vị Thiên Sứ và cũng là một bề tôi đức độ, đó là Is-haaq; một phần thưởng cho việc Y đã chấp hành lệnh của Allah cho việc giết tế Isma''il - đứa con trai duy nhất của Y.');
INSERT INTO "vn_mokhtasar" VALUES(37,113,'37:113','Và TA đã ban phúc lành cho Y (Ibrahim) và đứa con trai của Y (Is-haaq). Bởi thế, TA đã ban rất nhiều Hồng Ân đến với hai cha con của Y và kể cả chuyện hậu thế của cha con Y. Hậu thế sau này của hai cha con Y có những người phục tùng Thượng Đế của Y và có số người trong họ tự gây ra bất công lên bản thân mình điển hình như sự bất tuân và gây ra tội lỗi một cách công khai.');
INSERT INTO "vn_mokhtasar" VALUES(37,114,'37:114','Và quả thật, TA đã ban Hồng Ân cho Musa và người em trai của Y- Harun là bởi sứ mạng Nabi.');
INSERT INTO "vn_mokhtasar" VALUES(37,115,'37:115','Và TA đã giải cứu hai anh em (Musa và Harun) cùng với dân chúng của họ - người dân Israel - thoát khỏi cảnh làm nô lệ cho Fir''awn cũng như khỏi bị nhấn chìm trong trận Đại Hồng Thủy.');
INSERT INTO "vn_mokhtasar" VALUES(37,116,'37:116','Va TA đã giúp đỡ họ giành chiến thắng trước tên Fir''awn cùng đám thuộc hạ của hắn. Họ thực sự đã chiến thắng trước kẻ thù của họ.');
INSERT INTO "vn_mokhtasar" VALUES(37,117,'37:117','Và TA đã ban xuống cho Musa và người em trai của Y - Harun KinhTawrah (Cựu Ước) mang những thông điệp rõ ràng và rành mạch, không gì phải ngờ vực.');
INSERT INTO "vn_mokhtasar" VALUES(37,118,'37:118','Và TA đã hướng dẫn hai anh em họ con đường Ngay Thẳng không uốn khúc, và nó chính là con đường tôn giáo Islam dẫn đến sự hài lòng của Đấng Tạo Hóa, Đấng Toàn Năng.');
INSERT INTO "vn_mokhtasar" VALUES(37,119,'37:119','Và TA đã để lại cho hai anh em họ cả một hậu thế luôn nhắc điều tốt đẹp đến họ và luôn ca ngợi họ.');
INSERT INTO "vn_mokhtasar" VALUES(37,120,'37:120','Allah đã gởi lời chào tốt đẹp đến cho hai người họ, cùng với sự ca ngợi hai người họ và ban bằng an khỏi những điều nguy khốn.');
INSERT INTO "vn_mokhtasar" VALUES(37,121,'37:121','Quả thật, TA đã ban thưởng cho Musa và Harun một phần thưởng xứng đáng thật giá trị và TA cũng ban thưởng cho những ai làm những điều tốt bởi sự phục tùng Thượng Đế của họ giống như thế.');
INSERT INTO "vn_mokhtasar" VALUES(37,122,'37:122','Quả thật, Musa và Harun là hai bê tôi trong đám bề tôi của có đức tin của Allah, luôn làm theo những gì Ngài qui định.');
INSERT INTO "vn_mokhtasar" VALUES(37,123,'37:123','Và quả thật, Ilyas (Elias) là một trong các vị Thiên Sứ của Thượng Đế. Allah đã ban Ân Huệ cho Y qua sứ mạng Nabi và Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(37,124,'37:124','Khi Y bảo dân chúng của Y - Israel: "Này hỡi dân ta! Phải chăng các ngươi không sợ Allah trong việc tuân theo mệnh lệnh của Ngài, tôn thờ duy nhất một mình Ngài, tránh xa những điều cấm của Ngài và điều Shirk với Ngài?!"');
INSERT INTO "vn_mokhtasar" VALUES(37,125,'37:125','"Phải chăng, các ngươi tôn thờ ngoài Allah bục tượng Ba''la mà các ngươi làm ra và bỏ mặc việc thờ thượng Allah, Đấng Tạo Hóa Ưu Việt hay sao?"');
INSERT INTO "vn_mokhtasar" VALUES(37,126,'37:126','Allah là Thượng Đế của các ngươi, Đấng đã tạo hóa ra các ngươi và tổ tiên xa xưa của các ngươi. Bởi thế, Ngài đáng được thờ phượng, ngoài Ngài không ai (vật gì) đáng được thờ phượng cả, và dĩ nhiên các bục tượng lại càng không đáng bởi chúng không giúp ích và cũng không hại.');
INSERT INTO "vn_mokhtasar" VALUES(37,127,'37:127','Dân chúng của Y đã phủ nhận Y nên chúng sẽ phải bị triệu tập trong sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(37,128,'37:128','Ngoại trừ những ai trong dân chúng của Y có đức tin và thành tâm với Allah trong việc thờ phượng Ngài. Quả thật, họ mới là những người cứu rỗi khỏi sự trừng phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(37,129,'37:129','Và TA đã để lại tiếng thơm của Y ở nơi hậu thế.');
INSERT INTO "vn_mokhtasar" VALUES(37,130,'37:130','Allah gửi lời chào và khen ngợi đến Ilyas (Elias).');
INSERT INTO "vn_mokhtasar" VALUES(37,131,'37:131','Quả thật, TA đã ban thưởng cho Ilyas (Elias) một phần thưởng thật xứng đáng đầy và TA cũng ban thưởng cho những ai làm những điều tốt trong đám bề tôi có đức tin của Ta giống như thế.');
INSERT INTO "vn_mokhtasar" VALUES(37,132,'37:132','Quả thật, Ilyas (Elias) là một trong những người có đức tin chân chính, thật sự tin tưởng vào Thượng Đế của họ.');
INSERT INTO "vn_mokhtasar" VALUES(37,133,'37:133','Và quả thật, Lut là một trong những Sứ Giả của Allah, những vị mà Ngài đã cử phái đến với dân chúng của họ với mục đích báo tin mừng đồng thời cảnh báo về sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(37,134,'37:134','Hãy nhớ lại khi TA giải cứu Lut cùng toàn bộ gia đình của y cũng như những ai đi theo Y được an toàn khỏi sự trừng phạt mà TA đã giáng xuống đám dân của Y.');
INSERT INTO "vn_mokhtasar" VALUES(37,135,'37:135','Ngoại trừ người vợ của Y. Nữ ta phải gánh chịu hình phạt cùng với đám dân của Y; bởi vì, nữ ta đã trở thành một kẻ vô đức tin giống như họ.');
INSERT INTO "vn_mokhtasar" VALUES(37,136,'37:136','Sau đó, TA đã tiêu diệt hết đám người còn lại trong đám dân của Y, những kẻ mà chúng đã phủ nhận Y và không tin tưởng vào những gì mà Y đã mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(37,137,'37:137','Và quả thật, các ngươi - hỡi thị dân Makkah - thường đi ngang qua những ngôi nhà của chúng trong các chuyến lữ hành của các ngươi đến xứ Sham (nay phân thành quốc gia: Syria, Li-băng, Jordan, Palestine) vào buổi sáng.');
INSERT INTO "vn_mokhtasar" VALUES(37,138,'37:138','Và các ngươi cũng thường đi ngang qua chỗ của chúng vào buổi ban đêm, chẳng lẽ các ngươi không chịu suy ngẫm? Chẳng lẽ các ngươi không quan sát kỹ những gì trong suốt cuộc hành trình về hậu quả mà chúng đã phủ nhận, vô đức tin và làm điều ô uế mà trước chúng chưa từng có ai làm?!');
INSERT INTO "vn_mokhtasar" VALUES(37,139,'37:139','Và Yunus thực sự là một trong những vị Thiên Sứ của Allah mà Ngài cử phái đến với người dân của họ để báo tin mừng (về phần thưởng cho những ai tuân lệnh Ngài) và cảnh báo (về sự trừng phạt cho những ai bất tuân Ngài).');
INSERT INTO "vn_mokhtasar" VALUES(37,140,'37:140','Khi Y đã chạy bỏ dân làng của Y lúc chưa cho có phép của Thượng Đế của Y và bước lên con tàu chở đầy người và đồ đạc.');
INSERT INTO "vn_mokhtasar" VALUES(37,141,'37:141','Tuy nhiên, vì con tàu sắp phải đấm chìm vì chở đầy (người và đồ đạc) nên những người trên tàu đưa ra ý kiến cần ném một số người khỏi con tàu để giữ an nguy cho cả con tàu do lượng người quá tải. Thế là Yunus là một trong những người bị thua cuộc trong cuộc bốc thăm và Y bị ném xuống biển.');
INSERT INTO "vn_mokhtasar" VALUES(37,142,'37:142','Rồi khi Y bị ném xuống biển, một con cá voi đã nuốt chửng Y vào bụng vì Y đã phạm tội với Ngài (Allah), đó là Y đã tự ý bỏ lên tàu ra biển mà không được sự cho phép của Thượng Đế của Y.');
INSERT INTO "vn_mokhtasar" VALUES(37,143,'37:143','Nếu Yunus không phải là người đã thường xuyên tụng niệm Allah trước khi xảy ra tình trạng tồi tệ thế này cũng như nếu Y không tán dương Ngài trong khi ở bụng cá,');
INSERT INTO "vn_mokhtasar" VALUES(37,144,'37:144','Thì Y đã phải ở lại trong bụng cá voi đó cho tới ngày Phán Xét và lúc đó bụng con cá đã là ngôi mộ của Y.');
INSERT INTO "vn_mokhtasar" VALUES(37,145,'37:145','Rồi TA đã cho Y ra khỏi bụng cá voi trôi đạt vào một khu đất hoang vắng trong tình trạng sức khỏe yếu ớt do đã nằm trong bụng cá một thời gian.');
INSERT INTO "vn_mokhtasar" VALUES(37,146,'37:146','Và TA đã cho mọc ra trên khu đất hoang đó đầy những cây bầu (bí) che mát cho Y và làm thức ăn cho Y.');
INSERT INTO "vn_mokhtasar" VALUES(37,147,'37:147','Và TA đã cử phái Y đến với người dân của Y, và số lượng của họ khoảng hơn một trăm ngàn người.');
INSERT INTO "vn_mokhtasar" VALUES(37,148,'37:148','Những người có đức tin nơi những gì mà Y mang đến, Allah đã ban cho họ có cuộc sống hưởng lạc ở trần gian này cho tới một thời hạn đã ấn định.');
INSERT INTO "vn_mokhtasar" VALUES(37,149,'37:149','Ngươi - hỡi Muhammad - hãy hỏi chúng - những người đa thần -: Phải chăng các ngươi gán những đứa con gái mà các ngươi ghét bỏ cho Allah và các ngươi gán những đứa con trai mà các ngươi yêu thích cho bản thân các ngươi sao? Lám sao có sự phân chia như thế được?!');
INSERT INTO "vn_mokhtasar" VALUES(37,150,'37:150','Làm sao mà chúng có thể khẳng định rằng các vị Thiên Thần đều là giống cái khi mà chúng không có mặt trong lúc TA tạo hóa các Thiên Thần cũng như chúng không hề chứng kiến được việc tạo hóa của TA?!');
INSERT INTO "vn_mokhtasar" VALUES(37,151,'37:151','Quả thật, bọn đa thần đã bịa đặt cho Allah, chúng đã vu khống cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(37,152,'37:152','Chúng gán cho Allah rằng Ngài có con. Quả thật, chúng là những kẻ nói dối trong sự việc đó.');
INSERT INTO "vn_mokhtasar" VALUES(37,153,'37:153','Phải chăng Allah đã lựa chọn cho Ngài những đứa con gái mà các ngươi ghét bỏ mà không chọn những đứa con trai mà các ngươi yêu thích ư?! Chắc chắn Không có chuyện đó.');
INSERT INTO "vn_mokhtasar" VALUES(37,154,'37:154','Các ngươi - hỡi những tên đa thần - sao lại phân xử rằng con gái là của Allah còn con trai là của các ngươi ư?');
INSERT INTO "vn_mokhtasar" VALUES(37,155,'37:155','Chẳng lẽ các ngươi không lưu tâm về quan niệm sai trái này ư? Bởi quả thật, nếu các ngươi chịu lưu tâm thì các ngươi đã không phát ngôn những lời xàm bậy này.');
INSERT INTO "vn_mokhtasar" VALUES(37,156,'37:156','Hay các ngươi cho rằng các ngươi có đủ cơ sở và bằng chứng xác thực từ Kinh Sách nào đó hoặc từ một vị Thiện Sứ nào đó cho sự việc đó?!');
INSERT INTO "vn_mokhtasar" VALUES(37,157,'37:157','Bởi thế, các ngươi hãy đem Kinh Sách mà các ngươi cho là đã chứa đựng các cơ sở và các bằng chứng cho điều mà các ngươi đã khẳng định là thật xem nào?!');
INSERT INTO "vn_mokhtasar" VALUES(37,158,'37:158','Và bọn đa thần dựng ra chuyện giữa Allah và các vị Thiên Thần có mối quan hệ dòng tộc khi chúng đã khẳng định các vị Thiên Thần là con gái của Allah. Quả thật, các vị Thiên Thần biết chắc chắn rằng Allah sẽ triệu tập những kẻ đa thần cho việc xét xử và thanh toán chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,159,'37:159','Vinh Quang và trong sạch thay cho Allah về những gì mà những kẻ đa thần đã qui cho Ngài rằng Ngài có con cái, có kẻ đối tác chia sẻ quyền năng và những điều bịa đặt khác.');
INSERT INTO "vn_mokhtasar" VALUES(37,160,'37:160','Ngoại trừ, những bầy tôi chân thành của Allah, họ là những người không mô tả về Ngài ngoại trừ những gì thể hiện sự tối cao và vô song của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(37,161,'37:161','Quả thật, các ngươi - hỡi những tên đa thần - và những thần linh mà các ngươi thờ phượng ngoài Allah.');
INSERT INTO "vn_mokhtasar" VALUES(37,162,'37:162','Các ngươi đừng hồng dụ dỗ một ai đi lạc khỏi tôn giáo Chân Chính.');
INSERT INTO "vn_mokhtasar" VALUES(37,163,'37:163','Ngoại trừ những ai mà Allah đã định trước cho y là những người bạn của Hỏa Ngục. Những ai nằm trong sự tiền định của Allah về họ thì số phận của họ là bước vào trong Hỏa Ngục. Còn các ngươi và những thần linh mà các ngươi thờ phượng không có chút quyền lực gì trong sự việc đó cả.');
INSERT INTO "vn_mokhtasar" VALUES(37,164,'37:164','Và các vị Thiên Thần khẳng định sự thờ phượng của họ hoàn toàn dành cho Allah, họ thật sự vô can với những gì mà bọn đa thần dựng đã khẳng định, họ nói: không một ai trong chúng tôi lại không có một vị trí được ấn định trong việc thờ phượng Allah và phục tùng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(37,165,'37:165','Quả thật, - chúng tôi là những Thiên Thần - luôn đứng xếp thành hàng chỉnh tề trong việc thờ phượng Allah và tuân theo Ngài. Và quả thật, chúng tôi chưa hề nói sai lệch về những thuộc tính của Allah và những gì thuộc về Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(37,166,'37:166','Quả thật chúng tôi - những Thiên Thần - luôn đứng xếp thành hàng chỉnh tề trong việc thờ phượng Allah và phục tùng Ngài. Và quả thật, chúng tôi không bao giờ dám nói về Allah những gì không thuộc vê Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(37,167,'37:167','Quả thật, những tên đa thần thuộc thị dân Makkah đã thường nói trước khi Muhammad - cầu xin Allah ban bình an cho Người - nhận lãnh sứ mạng Thiên Sứ: "Nếu chúng tôi có được một Kinh Sách từ những quyển Kinh Sách ở thời trước như Kinh Tawrah (Cựu Ước) chẳng hạn thì chúng tôi đã thành thờ phượng Allah". Tuy nhiên, chúng là những kẻ nói dối về điều đó, bởi quả thật, Thiên Sứ Muhammad - cầu xin Allah ban bình an cho Người - đã mang Thiên Kinh Qur''an đến cho chúng nhưng chúng đã phủ nhận Nó. Bởi vậy, chúng sẽ sớm biết những gì đang chờ đợi chúng từ sự trừng phạt thật khủng khiếp của Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(37,168,'37:168','Quả thật, những tên đa thần thuộc thị dân Makkah đã thường nói trước khi Muhammad - cầu xin Allah ban bình an cho Người - nhận lãnh sứ mạng Thiên Sứ: "Nếu chúng tôi có được một Kinh Sách từ những quyển Kinh Sách ở thời trước như Kinh Tawrah (Cựu Ước) chẳng hạn thì chúng tôi đã thành thờ phượng Allah". Tuy nhiên, chúng là những kẻ nói dối về điều đó, bởi quả thật, Thiên Sứ Muhammad - cầu xin Allah ban bình an cho Người - đã mang Thiên Kinh Qur''an đến cho chúng nhưng chúng đã phủ nhận Nó. Bởi vậy, chúng sẽ sớm biết những gì đang chờ đợi chúng từ sự trừng phạt thật khủng khiếp của Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(37,169,'37:169','Quả thật, những tên đa thần thuộc thị dân Makkah đã thường nói trước khi Muhammad - cầu xin Allah ban bình an cho Người - nhận lãnh sứ mạng Thiên Sứ: "Nếu chúng tôi có được một Kinh Sách từ những quyển Kinh Sách ở thời trước như Kinh Tawrah (Cựu Ước) chẳng hạn thì chúng tôi đã thành thờ phượng Allah". Tuy nhiên, chúng là những kẻ nói dối về điều đó, bởi quả thật, Thiên Sứ Muhammad - cầu xin Allah ban bình an cho Người - đã mang Thiên Kinh Qur''an đến cho chúng nhưng chúng đã phủ nhận Nó. Bởi vậy, chúng sẽ sớm biết những gì đang chờ đợi chúng từ sự trừng phạt thật khủng khiếp của Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(37,170,'37:170','Quả thật, những tên đa thần thuộc thị dân Makkah đã thường nói trước khi Muhammad - cầu xin Allah ban bình an cho Người - nhận lãnh sứ mạng Thiên Sứ: "Nếu chúng tôi có được một Kinh Sách từ những quyển Kinh Sách ở thời trước như Kinh Tawrah (Cựu Ước) chẳng hạn thì chúng tôi đã thành thờ phượng Allah". Tuy nhiên, chúng là những kẻ nói dối về điều đó, bởi quả thật, Thiên Sứ Muhammad - cầu xin Allah ban bình an cho Người - đã mang Thiên Kinh Qur''an đến cho chúng nhưng chúng đã phủ nhận Nó. Bởi vậy, chúng sẽ sớm biết những gì đang chờ đợi chúng từ sự trừng phạt thật khủng khiếp của Ngày Phán Xét.(Qur''an). Bởi vậy, họ sẽ sớm biết những gì đang chờ đợi họ là một sự trừng phạt thật khủng khiếp ở Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(37,171,'37:171','Và quả thật Lời Phán của TA đã được mặc khải cho các vị Thiên Sứ của TA. Họ thực sự luôn được sự giúp đỡ của Allah trước kẻ thù của họ bằng những chứng cớ và sức mạnh. Quả thật, sự chiến thắng thuộc về các quân lính của TA (Allah), những người mà họ chiến đấu cho con đường chính nghĩa của Allah để giơ cao lời phán của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(37,172,'37:172','Và quả thật Lời Phán của TA đã được mặc khải cho các vị Thiên Sứ của TA. Họ thực sự luôn được sự giúp đỡ của Allah trước kẻ thù của họ bằng những chứng cớ và sức mạnh. Quả thật, sự chiến thắng thuộc về các quân lính của TA (Allah), những người mà họ chiến đấu cho con đường chính nghĩa của Allah để giơ cao lời phán của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(37,173,'37:173','Và quả thật Lời Phán của TA đã được mặc khải cho các vị Thiên Sứ của TA. Họ thực sự luôn được sự giúp đỡ của Allah trước kẻ thù của họ bằng những chứng cớ và sức mạnh. Quả thật, sự chiến thắng thuộc về các quân lính của TA (Allah), những người mà họ chiến đấu cho con đường chính nghĩa của Allah để giơ cao lời phán của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(37,174,'37:174','Bởi thế, Ngươi - hỡi Thiên Sứ - hãy tạm tránh xa những kẻ đa thần ngoan cố này một thời gian cho đến khi nào sự trừng phạt được ấn định cho chúng ập tới. Đó là thời điểm chỉ có Allah mới biết rõ về nó.');
INSERT INTO "vn_mokhtasar" VALUES(37,175,'37:175','Ngươi hãy nhìn xem sự trừng phạt giáng lên chúng. Rồi đây chúng sẽ nhìn thấy khi mà sự nhìn thấy của chúng trở nên vô ích đối với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,176,'37:176','Há những tên đa thần này hối thúc sự trừng phạt của Allah mau đến với chúng ư?!');
INSERT INTO "vn_mokhtasar" VALUES(37,177,'37:177','Bởi thế, khi hình phạt của Allah được giáng xuống họ vào một buổi hừng đông thì quả thật đó là một buổi sáng tồi tệ nhất đồi với họ.');
INSERT INTO "vn_mokhtasar" VALUES(37,178,'37:178','Bởi thế, Ngươi - hỡi Thiên Sứ - tạm bỏ mặc chúng một thời gian cho đến khi thời khắc đã được Allah ấn định sự trừng phạt dành cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,179,'37:179','Ngươi hãy nhìn xem sự trừng phạt giáng lên chúng. Rồi đây chúng sẽ nhìn thấy khi mà sự nhìn thấy của chúng trở nên vô ích đối với chúng.');
INSERT INTO "vn_mokhtasar" VALUES(37,180,'37:180','Thật Vinh Danh thay Thượng Đế của Ngươi - hỡi Muhammad - Thượng Đế đầy Sức Mạnh, Ngài trong sạch khỏi những gì mà những tên đa thần đã gán ghép cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(37,181,'37:181','Và Allah gửi lời chào bình an (Salam) và ca ngợi đến các vị Thiên Sứ cao quí của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(37,182,'37:182','Mọi lời ca tụng và tán dương đều kính dâng lên Allah, Đấng đầy Quyền Năng và Tối Cao. Ngài là Đấng xứng đáng cho điều đó, và Ngài là Thượng Đế của vũ trụ và muôn loài, không có Thượng Đế nào khác ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(38,1,'38:1','{Saad}. Phần giảng giải về các chữ cái mở đầu này đã được nói ở phần đầu của chương Al-Baqarah. Thề bởi Thiên Kinh Qur''an chứa đựng những lời nhắc nhở nhân loại về những gì hữu ích cho họ ở trần gian và cả Đời Sau. Sự việc không giống như những tên đa thần đã nghĩ rằng Allah có những kẻ đối tác.');
INSERT INTO "vn_mokhtasar" VALUES(38,2,'38:2','Tuy nhiên, những kẻ vô đức tin luôn cứ ngạo mạn không chấp nhận tính duy nhất của Allah và cứ ngoan cố chống đối Thiên Sứ Muhammad - cầu xin Allah ban bình an cho Người - và căm thù Người.');
INSERT INTO "vn_mokhtasar" VALUES(38,3,'38:3','Có biết bao nhiêu là thế hệ trước chúng đã bị TA tiêu diệt bởi sự phủ nhận các vị Thiên Sứ của Ngài, chúng đã cầu xin sự cứu rỗi khi sự trừng phạt giáng xuống. Nhưng sự cầu xin lúc đó trở nên vô ích.');
INSERT INTO "vn_mokhtasar" VALUES(38,4,'38:4','Và chúng tỏ vẻ kinh ngạc khi có một vị Thiên Sứ xuất thân từ chúng đến với chúng cảnh báo chúng về sự trừng phạt của Allah nếu chúng vẫn tiếp tục trong tình trạng vô đức tin của chúng. Và những kẻ vô đức tin này nói khi chúng chứng kiến được những bằng chứng xác thực mà Muhammad - cầu xin Allah ban bình an cho Người - mang đến: " Đây chẳng qua chỉ là một người đàn ông dùng ma thuật để biểu diễn trước thiên hạ, Y là một tên nói dối khi tự xưng là Thiên Sứ của Allah được Ngài mặc khải."');
INSERT INTO "vn_mokhtasar" VALUES(38,5,'38:5','Phải chăng người đàn ông này muốn nhập tất cả các thần linh lại thành một Thân Linh duy nhất, không có thần linh khác ngoài Ngài ư? Quả thật, hành động cho mục đích này của Y quả là một điều quái lạ.');
INSERT INTO "vn_mokhtasar" VALUES(38,6,'38:6','Những tên có địa vị lẫn những kẻ đầu sỏ của chúng bảo những người đi theo: "Các ngươi hãy tiếp tục đi trên những gì các ngươi đang theo, các ngươi chớ đừng đi vào tôn giáo của Muhammad, các ngươi hãy vẫn bám chặt việc thờ thượng các thần linh của các ngươi. Quả thật, những gì Muhammad tuyên truyền kêu gọi các ngươi đến với việc thờ phượng một Thượng Đế duy nhất chẳng qua chỉ là mưu đồ cho một kế hoạch của Y, Y chỉ muốn trở thành lãnh đạo của chúng ta và muốn chúng ta phải đi theo Y mà thôi."');
INSERT INTO "vn_mokhtasar" VALUES(38,7,'38:7','"Muhammad kêu gọi chúng ta thờ phượng một mình Allah. Đây là điều mà chúng ta không hề nghe cũng như không hề thấy trong tín ngưỡng mà tổ tiên của chúng ta đã theo, và ngay cả trong tôn giáo của Ysa - cầu xin bình an cho Người. Những gì chúng ta nghe được từ Y thật ra chỉ là một sự dối trá và bịa đặt."');
INSERT INTO "vn_mokhtasar" VALUES(38,8,'38:8','Chẳng lẽ Thiên Kinh Qur''an thực sự được ban xuống cho Y giữa chúng ta như thế này ư?! Nó thực sự được ban xuống cho Y trong khi chúng ta là những người lãnh đạo có địa vị cao trọng ư?! Không, những kẻ đa thần ngờ vực về lời Mặc Khải được truyền xuống cho Ngươi - Muhammad, bởi vì chúng chưa nếm được mùi vị của sự trừng phạt của Allah nên chúng mới cố chấp. Nếu chúng đã nếm được mùi vị hình phạt của Ngài thì chắc chắn chúng chẳng bao giờ dám vô đức tin và Shirk với Ngài nữa cũng như chúng chẳng còn dám nghi ngờ về lời Mặc Khải mà Ngươi mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(38,9,'38:9','Hoặc phải chăng những tên đa thần vô đức tin này đang sở hữu một kho tàng Ân Phúc của Thượng Đế của Ngươi - Đấng Toàn Năng, không một ai có thể chế ngự được Ngài; Đấng tùy ý ban bố bổng lộc cho bất kỳ ai Ngài muốn. Và hồng phúc từ trong kho tàng Ân Phúc của Ngài là sứ mạng Nabi, Ngài muốn đưa nó cho ai Ngài muốn. Nó không thuộc quyền sở hữu của chúng để chúng tùy ý đưa hay không đưa cho ai mà chúng muốn.');
INSERT INTO "vn_mokhtasar" VALUES(38,10,'38:10','Hoặc phải chăng chúng nắm quyền cai trị các tầng trời và trái đất và vạn vật trong trời đất để chúng quyết định cho hay không cho theo ý của chúng? Nếu chúng đã khẳng định như thế thì hãy chúng hãy tìm cách đi lên trời để định đoạt điều chúng muốn. Và chắc chắn chúng sẽ không bao giờ làm được điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(38,11,'38:11','Những kẻ phủ nhận Muhammad - cầu xin Allah ban bình an cho Người - là đoàn binh vô dụng sẽ bị đánh bại giống như các đội binh đã phủ nhận các vị Thiên Sứ của họ.');
INSERT INTO "vn_mokhtasar" VALUES(38,12,'38:12','Những tên phủ nhận đó không phải là những lớp người phủ nhận đầu tiên; bởi vì trước chúng đã có những cộng đồng đã phủ nhận các vị Thiên Sứ của họ, như đám dân của Nuh, ''Ad và Fir''awn - kẻ đã bức hại và đày ải thiên hạ để sở hữu được những tòa Kim Tự Tháp.');
INSERT INTO "vn_mokhtasar" VALUES(38,13,'38:13','Và Thamud, dân làng của Lut và dân chúng của Shu''aib cũng đã phủ nhận các Thiên Sứ. Họ là những liên minh phủ nhận các vị Thiên Sứ của họ và vô đức tin nơi những gì mà các Thiên Sứ của họ mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(38,14,'38:14','Tất cả nhóm liên minh phủ nhận các vị Thiên Sứ này đây phải nhận lấy sự trừng phạt của Allah. Sự trừng phạt chắc chắn sẽ đến với chúng cho dù có trễ đi chăng nữa.');
INSERT INTO "vn_mokhtasar" VALUES(38,15,'38:15','Và những tên đã phủ nhận Muhammad - cầu xin Allah ban bình an cho Người - chỉ ngóng chờ tiếng còi thứ hai được vang lên, lúc đó chúng không còn có sự quay lại. Chúng sẽ phải nhận lấy một hình phạt nếu chúng chết trong tình trạng vô đức tin của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(38,16,'38:16','Và chúng nói một cách chế giễu: "Lạy Thượng Đế của bầy tôi! Xin Ngài nhanh chóng cho số phận bị trừng phạt của bầy tôi mau diễn ra trên cuộc sống thế gian này trước khi Ngày Phán Xét đến."');
INSERT INTO "vn_mokhtasar" VALUES(38,17,'38:17','Hãy - hỡi Thiên Sứ - ráng kiên nhẫn với những lời nói của những tên vô đức tin ngạo mạn này khiến Ngươi không hài lòng. Và Ngươi hãy nghĩ đến một bề tôi của TA, Dawood, Y là một người rất kiên cường chịu đựng chống lại những kẻ thù của Y và luôn nhẫn nại trong việc tuân lệnh Allah. Quả thật, Y là người bề tôi luôn hằng quay về sám hối với Allah và luôn làm những gì mà Ngài hài lòng.');
INSERT INTO "vn_mokhtasar" VALUES(38,18,'38:18','Chính TA là Đấng đã chế ngự núi non, bắt chúng cùng với Dawood tụng niệm và tán dương TA đêm ngày.');
INSERT INTO "vn_mokhtasar" VALUES(38,19,'38:19','TA (Allah) đã khiến cho chim chóc hợp lại thành đàn trên không trung và tất cả cùng tuân theo Y trong trong việc ca tụng TA.');
INSERT INTO "vn_mokhtasar" VALUES(38,20,'38:20','Và TA đã củng cố sức mạnh cho Y bằng những thứ như địa vị, thể lực, trí lực và sự chiến thắng trước kẻ thù của Y. TA đã ban cho Y sứ mạng Nabi và sự đúng đắn trong các vụ việc khi đã quyết, và TA cũng đã ban cho Y cách ăn nói thu hút lòng người và sự quyết đoán.');
INSERT INTO "vn_mokhtasar" VALUES(38,21,'38:21','Há Ngươi - hỡi Thiên Sứ - có biết về câu chuyện hai người đàn ông thưa kiện nhau rồi họ tự ý trèo tường đột nhập vào phòng thờ phượng riêng của Dawood - cầu xin bình an cho Người - không?.');
INSERT INTO "vn_mokhtasar" VALUES(38,22,'38:22','Khi cả hai người đột nhập vào gặp Dawood làm cho Y giật mình. Cả hai cùng lên tiếng thưa rằng: "Xin Người đừng hoảng sợ! Bởi vì, cả hai chúng tôi có một sự tranh chấp với nhau vì có chuyện người này chèn ép người kia. Thế nên, chúng tôi đến đây nhờ Người phân xử giùm cho chúng tôi một cách thật công bằng và để không có chuyện bất công xảy ra giữa chúng tôi và cũng xin Người chỉ dẫn chúng tôi đến với con đường hòa thuận êm đẹp."');
INSERT INTO "vn_mokhtasar" VALUES(38,23,'38:23','Một trong hai người tranh chấp đó thưa với Dawood -cầu xin sự bình an cho Người rằng: "Thật ra, đây là người anh em của tôi (trong đạo), anh ta có chín mươi chín con cừu cái còn tôi thì chỉ có một con cừu cái duy nhất. Thế nhưng, anh ta còn yêu cầu tôi phải đưa con cừu đó cho anh ta. Và anh ta đã thắng tôi trong lý lẽ và lập luận.');
INSERT INTO "vn_mokhtasar" VALUES(38,24,'38:24','Bởi thế, Dawood đứng ra phân xử giữa hai người họ, Người bảo người thưa chuyện với Người rằng người anh em của y đã bất công với y: "Chắc chắn, người anh em của ngươi đã áp bức ngươi về việc y đã yêu cầu ngươi đưa con cừu cái của ngươi nhập chung với bầy cừu của y. Quả thật, đa số trong chuyện hợp tác làm ăn với nhau có rất nhiều người hay lấn lướt nhau bằng sự bất công, ngoại trừ những ai có đức tin và làm những việc làm thiện tốt thì mới có cuộc hợp tác làm ăn công bằng cho dù đó là sự hùn hạp nhỏ. Và rồi Dawood - cầu xin sự bình an cho Người - chợt nhận ra đây chẳng qua là một sự thử thách. Bởi thế, Y lập tức cầu xin Thượng Đế của Y tha thứ và cúi đầu quỳ mọp xuống đất để được gần với Allah rồi Y xám hối trước Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(38,25,'38:25','Thế nên, TA đáp lại lời cầu xin của Y bằng sự tha thứ những việc làm đó của Y. Quả thật, ở nơi TA luôn dành cho Y một chỗ gần gũi nhất và đó là chỗ tốt nhất mà Ysẽ đến ở Ngày Sau (ngày Phán Xét).');
INSERT INTO "vn_mokhtasar" VALUES(38,26,'38:26','Này hỡi Dawood! Quả thật, TA đã chỉ định Ngươi là Người kế vị ở trên trái đất để quán xuyến và phân xử mọi chuyện trong tôn giáo ở trần gian, do đó Ngươi hãy phán xét giữa mọi người bằng sự công bằng, chính trực và Ngươi chớ tham lợi lộc rồi thiên vị trong việc phán xét giữa người này với người kia; Ngươi chớ thiên vị bên nào dù đó là người thân hay bạn bè và Ngươi chớ bất công vì ai đó là người Ngươi thù ghét. Bởi lẽ sự bất công sẽ khiến Ngươi lầm lạc trên con đường ngay thẳng của Allah. Quả thật, những ai lầm lạc trên con đường chính đạo của Allah sẽ bị trừng phạt thật khủng khiếp vì họ đã quên mất đi Ngày Phán Xét; nếu như họ thực sự nghĩ đến Ngày đó và khiếp sợ những gì trong Ngày hôm đó thì chắc chắn sẽ không thiên vị.');
INSERT INTO "vn_mokhtasar" VALUES(38,27,'38:27','Và TA đã tạo ra bầu trời và trái đất không phải vô nghĩa. Đó chỉ là lối suy nghĩ của những kẻ vô đức tin. Bởi thế, thật khốn khổ cho những kẻ vô đức tin có suy nghĩ này bởi sự trừng phạt của Hỏa Ngục vào Ngày Phán Xét nếu như họ chết trên tình trạng vô đức tin và nghĩ xấu về Allah.');
INSERT INTO "vn_mokhtasar" VALUES(38,28,'38:28','TA sẽ không bao giờ đối xử với những người có đức tin nơi Allah, luôn vâng lệnh Thiên Sứ của Ngài và cố gắng làm những việc làm thiện phước giống như đối xử với những kẻ thối nát ở trên trái đất bằng sự vô đưc tin và bất tuân của chúng. Và TA cũng không đối xử với những người thành tâm tin tưởng Thượng Đế của họ bằng việc chấp hành các mệnh lệnh của Ngài và cố gắng tránh xa những điều cấm của Ngài giống như những kẻ vô đức tin, những tên đạo đức giả đắm mình tong tội lỗi. Quả thật, sự đối xử ngang bằng giữa hai nhóm người này là điều bất công, không tồn tại ở nơi Allah, Đấng Toàn Năng. Ngược lại, Allah ban thưởng cho những người có đức tin ngoan đạo bằng việc họ được thu nhận vào Thiên Đàng và Ngài sẽ trừng phạt những kẻ vô đức tin bằng cách đày họ vào Hỏa Ngục; bởi vì, họ không ngang bằng nhau ở nơi Allah, và phần thưởng của họ không giống nhau.');
INSERT INTO "vn_mokhtasar" VALUES(38,29,'38:29','Quả thật, Thiên Kinh Qur''an mà TA ban xuống cho Ngươi này đây là một Kinh Sách chứa đựng nhiều điều điều tốt đẹp và hữu ích, để nhân loại nghiền ngẫm về về lời lẽ của Nó và để họ thông hiểu về ý nghĩa của Nó và cũng để thức tỉnh những ai có một tâm trí lành mạnh và đúng đắn.');
INSERT INTO "vn_mokhtasar" VALUES(38,30,'38:30','Và TA đã ban cho Dawood đứa con trai, Sulayman, một Ân Điển tốt đẹp cho niềm vui của Y. Và Sulayman là một bề tôi ưu hạng nhất của TA. Quả thật, Y luôn hằng quay về sám hối với TA và luôn phó thác hết mọi chuyện cho TA.');
INSERT INTO "vn_mokhtasar" VALUES(38,31,'38:31','Hãy nhớ lại khi vào một buổi xế chiều, có một số con chiến mã đặc chủng nhanh nhất được mang đến cho Sulayman, chúng đứng thành ba hàng và thêm hàng thứ tư. Những con chiến mã đặc chủng vẫn cứ được cống nạp cho đến khi mặt trời lặn khuất.');
INSERT INTO "vn_mokhtasar" VALUES(38,32,'38:32','Rồi Sulayman bảo:" Quả thật, Ta yêu thích tàn sản -trong đó có những con chiến mã tốt này - hơn việc Ta tưởng nhớ đến Thượng Đế của Ta mãi cho đến khi mặt trời mất dạng và Ta đã trễ nải cuộc lễ nguyện Salah Al-Asar."');
INSERT INTO "vn_mokhtasar" VALUES(38,33,'38:33','Sulayman bảo: "Hãy dẫn những con chiến mã đó lại đây cho TA". Thế rồi những con chiến mã đó được đem đến gần Y. Y lấy đao chặt vào chân và cổ của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(38,34,'38:34','Và TA đã thử thách Sulayman bằng cách để cho một tên Jinn Shaytan chiếm lấy ngai vàng của Y. Tên Jinn đó đã hóa thân thành một con người cai trị vương quốc của Y trên ngai vàng đó một khoảng thời gian ngắn. Rồi sau đó, Sulayman quay về sám hối với TA thế nên TA đã khôi phục vương quyền cho Y từ lũ Shaytan kia.');
INSERT INTO "vn_mokhtasar" VALUES(38,35,'38:35','Sulayman thưa: "Lạy Thượng Đế của bề tôi! Xin Ngài hãy tha thứ cho việc làm tội lỗi của bề tôi và ban bố cho bề tôi một vương quyền đặc biệt cho bề tôi, vương quyền mà Ngài sẽ không ban bố cho một ai khác trong nhân loại sau bề tôi. Bởi vì Ngài là Thượng Đế - Đấng Hằng Ban Bố, Đấng Hào Phóng Vĩ Đại."');
INSERT INTO "vn_mokhtasar" VALUES(38,36,'38:36','Bởi thế, TA đã đáp lại lời cầu xin của Y và TA đã khiến cho những cơn gió tuân theo mệnh lệnh của Y lướt theo tốc độ nhanh chậm đến nơi nào đó tùy ý của Y.');
INSERT INTO "vn_mokhtasar" VALUES(38,37,'38:37','Và TA bắt đám Shaytan phục tùng theo mệnh lệnh của Y, chúng gồm: "Những tên thợ xây dựng và những thợ lặn, trong số những thợ lặn có là những thợ lặn sâu giữa lòng đại dương và những thợ lặn này đã lấy ngọc trai quý cho Y."');
INSERT INTO "vn_mokhtasar" VALUES(38,38,'38:38','Và một số khác trong đám Shaytan bị xiềng xích lại, chúng bị xiềng xích lại không thể cử động.');
INSERT INTO "vn_mokhtasar" VALUES(38,39,'38:39','Này hỡi Sulayman: "Đây là món quà TA đã đáp lại với những gì mà Ngươi cầu xin TA. Bởi thế, Ngươi được quyền phân phát hay giữ lại tùy ý Ngươi không sẽ bị tính sổ và hạch hỏi trong chuyện phân phát hay giữ lại."');
INSERT INTO "vn_mokhtasar" VALUES(38,40,'38:40','Và quả thật, Sulayman là một trong những người cận kề ở nơi TA và ở nơi TA y có một nơi trở về tốt đẹp nhất, đó chính là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(38,41,'38:41','Và hãy nhớ lại - hỡi Thiên Sứ - có một bề tôi của TA tên là Ayyub, Y đã cầu xin Allah, Thượng Đế của Y rằng: "Quả thật, bề tôi đã bị lũ Shaytan quấy nhiễu đủ điều làm cho bề tôi mệt mỏi đau đớn về thể xác lẫn tinh thần."');
INSERT INTO "vn_mokhtasar" VALUES(38,42,'38:42','Và TA (Allah) phán bảo Y: "Ngươi hãy dùng chân của Ngươi giẫm lên mặt đất, Y lấy liền lấy chân giẫm mạnh lên mặt đất, thế là từ đó phún lên những mạch nước, Y lấy nước đó dùng để uống và tắm rữa. Bởi thế, những giọt nước đó đã tẩy sạch sẽ cho Y hết cơn bạo bệnh."');
INSERT INTO "vn_mokhtasar" VALUES(38,43,'38:43','Và TA đã đáp lại lời thỉnh cầu của Y, do đó TA chữa hết căn bệnh hiểm nghèo cho Y, TA ban cho gia đình Y được đoàn tụ sum vầy và TA ban thêm cho Y con cháu nhiều hơn nữa, một hồng ân của TA dành cho Y. Phần thưởng mà Y nhận được là do sự kiên nhẫn chịu đựng của Y và hình ảnh của Y chính là điều để TA nhắc nhở cho những ai có được đầu óc thông suốt biết kiên nhẫn chịu đựng và chắc sẽ nhận được một phần thưởng to lớn.');
INSERT INTO "vn_mokhtasar" VALUES(38,44,'38:44','Khi Ayyub nổi giận với vợ của Y, Y đã thốt lên lời thề với TA rằng Y sẽ đánh vợ của Y một trăm roi. TA phán bảo Y: "Ngươi - hỡi Ayyub - hãy cầm lấy trong tay của mình một bó lá cây, dùng nó để đánh nữ ta bởi lời thề Ngươi đã thốt và chớ phạm lời thề." Quả thật, TA thấy Y - Ayyyub - là một người rất kiên nhẫn chịu đựng trên sự thử thách của TA dành cho Y, Y quả là một bề tôi ưu việt nhất bởi vì Y luôn hằng quay đầu sám hối với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(38,45,'38:45','Và Ngươi - hỡi Thiên Sứ - hãy nhớ lại đám bầy tôi của TA, những người mà TA đã tuyển chọn làm những vị Thiên Sứ của TA như: Ibrahim, Is-haaq và Ya''qub. Quả thật, họ là những người có ý trí kiên cường trong việc tuân phục Allah, họ là những người luôn làm cho Ngài hài lòng về họ và họ luôn có một cái nhìn sâu sắc thấu đáo về điều Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(38,46,'38:46','Quả thật, TA đã chọn lấy họ vì tấm lòng của họ luôn hướng tới ngôi nhà ở Ngày Sau (Thiên Đàng). Họ luôn chuẩn bị cho ngôi nhà đó bằng cách làm những điều thiện phước và kêu gọi mọi người cùng nhau làm việc thiện.');
INSERT INTO "vn_mokhtasar" VALUES(38,47,'38:47','Và quả thật, đối với TA họ chắc chắn là những người ưu tú trong việc phục tùng TA và là đám bầy tôi trung thành của TA được TA lựa chọn để gánh vác sứ mạng cho TA và đi truyền bá bức thông điệp của TA đến nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(38,48,'38:48','Và hãy nhớ lại - hỡi Thiên Sứ - Isma''il con trai của Ibrahim, Al-Yasa'' (Elisha) và Zdul-kifl, họ là những người đáng được khen ngợi. Bởi vì tất cả họ là những người ưu tú được Ngài lựa chọn.');
INSERT INTO "vn_mokhtasar" VALUES(38,49,'38:49','Thiên Kinh Qur''an là lời nhắc nhở tốt nhất dành cho họ. Và quả thật, những người có tấm lòng kính sợ bằng sự phục tùng mệnh lệnh của Allah và tránh xa những gì mà Ngài nghiêm cấm chắc chắn sẽ được trở về ngôi nhà thật tốt đẹp ở Ngày Sau (Thiên Đàng).');
INSERT INTO "vn_mokhtasar" VALUES(38,50,'38:50','Họ sẽ được trở về nơi tốt nhất, đấy chính là những Ngôi Vườn Thiên Đàng mà họ sẽ định cư đời đời ở trong đó vào ngày Phán Xét và những cánh cửa của Thiên Đàng đã được mở toan ra chào đón họ.');
INSERT INTO "vn_mokhtasar" VALUES(38,51,'38:51','Trong đó, họ sẽ tựa mình trên những chiếc tràng kỷ sang trọng và lộng lẫy, họ sẽ yêu cầu những người hầu mang đến cho mình bất cứ thức ăn, thức uống nào họ ưa thích từ đủ loại trái cây khác nhau đến những loại nước giải khát như rượu và các thức uống khác.');
INSERT INTO "vn_mokhtasar" VALUES(38,52,'38:52','Bên cạnh họ sẽ là những nàng trinh nữ với đôi mắt to trữ tình tuyệt đẹp, chỉ thích ngắm nhìn chồng của họ và không bao giờ nhìn người đàn ông khác. Và các nàng trinh nữ này đồng trang lứa với họ.');
INSERT INTO "vn_mokhtasar" VALUES(38,53,'38:53','Và đây là những gì mà các ngươi - hỡi những người thành tâm kính sợ Allah - đã được hứa hẹn về phần thưởng tốt đẹp vào ngày Phán Xét bởi các việc làm thiện tốt của các ngươi ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(38,54,'38:54','Quả thật, những gì mà TA đã đề cập về phần thưởng dành cho những người ngoan đạo vào ngày Phán Xét là những bổng lộc vô tận.');
INSERT INTO "vn_mokhtasar" VALUES(38,55,'38:55','Đây là những phần thưởng dành cho những người ngoan đạo. Riêng những kẻ vượt quá mức giới hạn của Allah bởi việc vô đức tin và tội lỗi thì phần thưởng của chúng sẽ trái ngược với phần thưởng của những người biết kính sợ Allah. Bởi thế, họ sẽ có một nơi rất tồi tệ để trở về.');
INSERT INTO "vn_mokhtasar" VALUES(38,56,'38:56','Phần thưởng của chúng sẽ là Hỏa Ngục, nó sẽ bao trùm lấy chúng, chúng sẽ phải hứng chịu cái nóng thiêu đốt của nó, chúng sẽ nằm trong đó. Và đó là nơi ở thật tồi tệ.');
INSERT INTO "vn_mokhtasar" VALUES(38,57,'38:57','Sự trừng phạt này là một loại nước cực sôi, một loại mủ (máu) hôi tanh được tiết ra từ da thịt tổn thương của cư dân Hỏa Ngục. Chúng uống loại nước sôi và các chất mủ này, đó là thức uống giải khát của chúng nhưng nó không giải được cơn khát của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(38,58,'38:58','Và họ còn phải gánh chịu thêm các loại hình phạt tương tự khác nữa. Họ sẽ phải gánh chịu các loại trừng phạt khác nhau vào Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(38,59,'38:59','Và khi cư dân của Hỏa Ngục vào Hỏa Ngục, giữa họ xảy ra sự trách mắng lẫn nhau, người này không muốn liên can người kia, người kia đổ lỗi cho người này. Bởi thế, một số họ lên tiếng bảo: "Đây là nhóm cư dân Hỏa Ngục vào Hỏa Ngục cùng với các ngươi." Một nhóm khác đáp lại: "Chúng tôi chẳng hề hoan nghênh họ, vì thật ra họ cũng bị đày vào Hỏa Ngục giống như chúng tôi thôi."');
INSERT INTO "vn_mokhtasar" VALUES(38,60,'38:60','Đám thuộc hạ đã làm theo những tên lãnh đạo nói với những kẻ chúng đi theo: "Không, các người - hỡi các nhà lãnh đạo - chúng tôi cũng không hề hoan nghênh các người. Bởi vì các người là những người khiến chúng tôi phải gánh chịu sự trừng phạt đau đớn bởi sự lạc lối mà các người đã lôi kéo chúng tôi. Bởi thế, đây chính là chỗ cư ngụ tồi tệ mà tất cả chúng ta đều phải gánh chịu."');
INSERT INTO "vn_mokhtasar" VALUES(38,61,'38:61','Những tên đi theo thưa: "Lạy Thượng Đế của bầy tôi! Những ai đã từng dẫn dắt bầy tôi đi lạc lối để rồi nhận lấy sự trừng phạt thế này đây thì cầu xin Ngài hãy tăng ngọn lửa gấp đôi lên trừng phạt chúng."');
INSERT INTO "vn_mokhtasar" VALUES(38,62,'38:62','Và những tên kiêu ngạo bảo: "Chuyện gì xảy ra với chúng tôi vậy? Tại sao, chúng tôi không nhìn thấy ở cùng với chúng tôi trong Hỏa Ngục những người đàn ông mà chúng tôi đã liệt kê họ vào danh sách khi còn ở trần gian rằng chắc chắn họ đáng phải gánh chịu sự trừng phạt?"');
INSERT INTO "vn_mokhtasar" VALUES(38,63,'38:63','Phải chăng, sự kiêu ngạo và khinh thường của chúng tôi đối với họ là đã sai. Bởi vì, số phận của họ không đáng bị trừng phạt hoặc do họ đã nằm sẵn trong Hỏa Ngục rồi, thế nên đã làm chúng tôi không nhìn thấy được họ?');
INSERT INTO "vn_mokhtasar" VALUES(38,64,'38:64','Quả thật, sự việc TA đã đề cập về sự tranh cãi và trách mắng nhau giữa như kẻ vo đức tin vào Ngài Phán Xét là sự thật, không có gì phải nghi ngờ cả.');
INSERT INTO "vn_mokhtasar" VALUES(38,65,'38:65','Ngươi- hỡi Muhammad - hãy nói với những kẻ vô đức tin trong dân chúng của Ngươi: "Quả thật, Ta chỉ là một người cảnh báo cho các người về hình phạt mà Allah sẽ trừng trị các người về sự vô đức tin của các người nơi Ngài và sự phủ nhận của các người đối với Thiên Sứ của Ngài. Và đích thực không có Thượng Đế nào xứng đáng được thờ phượng mà chỉ một mình Allah, Đấng duy nhất trong sự tối cao, vĩ đại và các thuộc tính cũng như danh xưng của Ngài, Ngài là Đấng Toàn Năng chi phối tất cả mọi thư. Tất cả vạn vật đều phục tùng Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(38,66,'38:66','Ngài (Allah) là Thượng Đế của các tầng trời, trái đất và tất cả vạn vật giữa trời đất. Ngài là Đấng Đầy Quyền Lực cai quản hết tất cả mọi vật, không ai có thể vượt qua quyền năng của Ngài nhưng Ngài cũng là Đấng Hằng Tha Thứ mọi tội lỗi của đám bầy tôi biết ăn năn sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(38,67,'38:67','Ngươi - hỡi Muhammad - hãy nói với những kẻ phủ nhận đức tin: "Quả thật, Thiên Kinh Qur''an là một nguồn tin vô cùng vĩ đại."');
INSERT INTO "vn_mokhtasar" VALUES(38,68,'38:68','"Các ngươi phản đối nguồn tin vĩ đại này và các ngươi quay mặt với Nó."');
INSERT INTO "vn_mokhtasar" VALUES(38,69,'38:69','"Ta không hề hay biết vấn đề gì đã diễn ra giữa các vị Thiên Thần về sự tạo hóa của Adam nếu như Allah không Mặc Khải xuống cho Ta và dạy Ta."');
INSERT INTO "vn_mokhtasar" VALUES(38,70,'38:70','"Quả thật, Allah chỉ mặc khải xuống cho Ta trong đó cho Ta biết rằng Ta là một người cảnh báo các ngươi về hình phạt của Ngài một cách rõ ràng, minh bạch."');
INSERT INTO "vn_mokhtasar" VALUES(38,71,'38:71','Hãy nhớ lại khi Thượng Đế của Ngươi phán bảo với các vị Thiên Thần rằng: "Quả thật, TA sắp tạo hóa ra một người phàm tục từ đất sét" và đó chính là Adam - cầu xin bình an cho Người."');
INSERT INTO "vn_mokhtasar" VALUES(38,72,'38:72','Bởi thế, khi TA uốn nắn Y (Adam) thành một hình thể con người hoàn chỉnh và sau đó TA thổi vào Y một linh hồn (sự sống). Rồi TA ra lệnh tất cả phải quỳ xuống cuối đầu lạy Y.');
INSERT INTO "vn_mokhtasar" VALUES(38,73,'38:73','Thế là, các Thiên Thần tuân theo mệnh lệnh của Thượng Đế họ, tất cả họ đều cuối đầu quỳ lại sự tạo hóa cao quí của Ngài, không một ai trong họ dám không quỳ.');
INSERT INTO "vn_mokhtasar" VALUES(38,74,'38:74','Chỉ một mình Iblis, hắn đã kiêu ngạo không chịu quỳ và chính vì sự kiêu ngạo đó của hắn đã khiến hắn trở thành một tên phản nghịch lại lệnh của của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(38,75,'38:75','Allah phán bảo: "Này Iblis! Điều gì đã ngăn cản nhà ngươi quì lạy Adam, một thứ do chính tay của TA đã tạo ra? Há có phải do sự kiêu ngạo đã ngăn cản nhà ngươi quỳ lạy hoặc do nhà ngươi quá tự phụ cho rằng mình cao hơn cả Thượng Đế của ngươi chăng?"');
INSERT INTO "vn_mokhtasar" VALUES(38,76,'38:76','Iblis thưa: "Bề tôi tốt đẹp hơn Adam. Bởi vì, Ngài tạo hóa bề tôi từ lửa trong khi Ngài tạo hóa Adam từ đất sét." Iblis tưởng rằng lửa sẽ cao quí và tốt hơn hơn đất sét.');
INSERT INTO "vn_mokhtasar" VALUES(38,77,'38:77','Allah phán với Iblis: "Ngươi hãy cút khỏi Thiên Đàng. Bởi quả thật, ngươi là một tên đã bị nguyền rủa".');
INSERT INTO "vn_mokhtasar" VALUES(38,78,'38:78','Và quả thật, nhà ngươi là một kẻ bị trục xuất ra khỏi Thiên Đàng cho đến ngày Thưởng Phạt Phân Minh, đó chính là ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(38,79,'38:79','Iblis thưa: "Xin Ngài tạm tha cho bề tôi và không xóa sổ bề tôi đến một ngày mà tất cả đám bề tôi của Ngài sẽ được dựng sống lại."');
INSERT INTO "vn_mokhtasar" VALUES(38,80,'38:80','Allah phán: " Được, TA sẽ tạm hoãn thời hạn đó cho ngươi."');
INSERT INTO "vn_mokhtasar" VALUES(38,81,'38:81','Cho đến Ngày mà thời khắc đã được ấn định cho việc nhà ngươi sẽ bị tiêu diệt.');
INSERT INTO "vn_mokhtasar" VALUES(38,82,'38:82','Iblis thưa: "Bề tôi thề bởi Quyền Năng và Sức Mạnh của Ngài rằng bề tôi sẽ lôi kéo tất cả đám con cháu của Adam đi vào con đường lầm lạc."');
INSERT INTO "vn_mokhtasar" VALUES(38,83,'38:83','Ngoại trừ những ai mà Ngài đã bảo vệ khỏi sự lầm lạc và những ai mà họ thực sự thành tâm và kiên định trong thờ phượng duy nhất một mình Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(38,84,'38:84','Allah, Đấng Tôi Cao phán: "Chân Lý luôn nằm ở nơi TA, và TA luôn nói điều chân lý, TA không nói bất cứ điều gì ngoài chân lý cả."');
INSERT INTO "vn_mokhtasar" VALUES(38,85,'38:85','"Chắc chắn rằng vào ngày Phán Xét, Hỏa Ngục là nơi giam giữ nhà ngươi và những ai trong đám con cháu của Adam đi theo ngươi trong sự vô đức tin."');
INSERT INTO "vn_mokhtasar" VALUES(38,86,'38:86','Ngươi - hỡi Thiên Sứ - hãy nói với những kẻ đa thần này: "Ta không hề đòi hỏi các ngươi bất cứ thù lao gì sau khi Ta đã hoàn thành xong lời khuyên can của Ta với các ngươi và Ta cũng không phải là một tên bịa đặt nói ra những điều không có hoặc thêm bớt vào những sắc lệnh của Allah."');
INSERT INTO "vn_mokhtasar" VALUES(38,87,'38:87','Không, Thiên Kinh Qur''an chỉ là lời nhắc nhở cảnh tỉnh cho những ai biết kính sợ Allah từ loài người và loài Jinn.');
INSERT INTO "vn_mokhtasar" VALUES(38,88,'38:88','"Và chắc chắn, các ngươi sẽ biết rõ thông tin trong Thiên Kinh Qur''an này. Quả thật, giây phút đó là điều hiển nhiên, nó sẽ sớm xảy ra sau cái chết của các ngươi."');
INSERT INTO "vn_mokhtasar" VALUES(39,1,'39:1','Thiên Kinh Qur''an được ban xuống từ Allah, Đấng Toàn Năng mà không một ai có thể vượt qua quyền năng của Ngài, Đấng Toàn Tri trong việc tạo hóa, Đấng định đoạt và ban hành giáo luật. Quả thật, không ai ban Nó xuống ngoài Ngài - Đấng Vinh Quang.');
INSERT INTO "vn_mokhtasar" VALUES(39,2,'39:2','Quả thật, chính TA đã ban xuống cho Ngươi - hởi Thiên Sứ - một Thiên Kinh Qur''an chứa đựng những điều Chân Lý. Do đó, Ngươi hãy thông báo tất cả mọi sự thật về Nó và dùng Nó để phán quyết cho mọi sự việc một cách thật công bằng. Ngươi hãy thờ phượng một mình Allah duy nhất, thành tâm thờ phượng Ngài, không tổ hợp với Ngài bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(39,3,'39:3','Chẳng phải tôn giáo của Allah là tín ngưỡng không có chỗ cho Shirk đó sao? Những kẻ đã chọn lấy ngoài Allah các vị thần linh từ những bục tượng và các tà thần để thờ phụng với lời biện minh: "Chúng tôi thờ phượng họ chỉ vì muốn nhờ họ đem chúng tôi đến gần Allah hơn thôi, để nhờ họ đưa những mong muốn của chúng tôi đến với Ngài và để họ cầu xin ân xá cho chúng tôi từ nơi Ngài." Quả thật, Allah sẽ phán xét giữa những người có đức tin kiên định với những kẻ vô đức tin, những kẻ đa thần vào ngày Phán Xét về những điều mà họ tranh luận về việc độc tôn một mình Allah. Chắc chắn, Allah sẽ không hướng dẫn đến với Chính Đạo cho những ai bịa đặt rồi đổ thừa cho Allah.');
INSERT INTO "vn_mokhtasar" VALUES(39,4,'39:4','Nếu như Allah muốn nhận cho Mình một đứa con thì Ngài sẽ lựa chọn trong số nhân vật mà Ngài đã tạo hóa ra một người mà Ngài thật vừa ý thì Ngài đã tạo cho Mình một đứa con. Ngài là Đấng Thiên Liêng và trong sạch khỏi những gì mà những kẻ đa thần đã vu khống cho Ngài. Ngài là Đấng Duy Nhất trong mọi thuộc tính và mọi hành động của Ngài, Ngài không có kẻ đối tác ngang vai, và Ngài là Đấng Cưỡng Chế tất cả tạo vật của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(39,5,'39:5','Ngài tạo hóa ra các tầng trời và trái đất bằng trí huệ vĩ đại không phải là vô ích giống như lời nói phiến diện của bọn ngu dốt. Ngài đã lấy ban đêm phủ lấy ban ngày và ban ngày bao trùm lấy ban đêm, ban ngày đến thì ban đêm phải vắng mặt và ngược lại. Ngài đã chế ngự mặt trời và mặt trăng, tất cả đều hoạt động trong một thời hạn nhất định trong cuộc sống trần gian. Há Ngài chẳng phải là Đấng Toàn Năng Đầy Quyền Lực mà không có một kẻ thù nào dám hiềm khích và cũng không một ai chế ngự được Ngài, nhưng Ngài là Đấng Hằng Tha Thứ mọi tội lỗi cho đám bầy tôi biết quay về sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(39,6,'39:6','Thượng Đế đã tạo hóa ra các ngươi - hỡi nhân loại - từ một cá thế duy nhất đó chính là Adam. Sau đó, từ Adam Ngài đã tạo ra người vợ của Y là Hauwa, tiếp theo Ngài tạo ra cho các ngươi tám loại gia súc như lạc đà, bò, cừu và dê, mỗi loài một cặp đực cái. Ngài đã tạo hóa các ngươi trong dạ con của các bà mẹ các ngươi theo từng giai đoạn, cái này sau cái kia trongba lớp màng tối: thành bụng, tử cung và nhau thai. Allah, Thượng Đế của các ngươi là thế, Ngài là Đấng duy nhất nắm Quyền Thống Trị. Không ai xứng đáng được thờ phượng ngoài một mình Ngài duy nhất. Bởi thế, tại sao các ngươi lại xa lánh việc thờ phượng Ngài mà lại đi thờ phượng một vật thể mà chúng không tài nào có thể tạo hóa ra được bất cứ thứ gì?');
INSERT INTO "vn_mokhtasar" VALUES(39,7,'39:7','Nếu các ngươi - hỡi nhân loại - vô đức tin nơi Thượng Đế của các ngươi thì chắc chắn Allah sẽ không cần đến đức tin của các ngươi, sự vô đức tin của các ngươi không ảnh hưởng gì đến Ngài mà ngược lại, nó chỉ ảnh hưởng đến chính bản thân các ngươi. Ngài không hài lòng trước thái độ vong ơn của đám bầy tôi của Ngài, Ngài không ra lệnh cho họ vô đức tin nơi Ngài bởi vì Allah không bảo ban điều tội lỗi và phản nghịch. Còn ngược lại, nếu các ngươi biết tạ ơn Allah về Hồng Phúc của Ngài và có đức tin nơi Ngài thì Ngài sẽ hài lòng về sự tri ân của các ngươi thì đó là bằng chứng cho các ngươi trước Ngài. Và không một linh hồn nào gánh vách tội lỗi cho một linh hồn nào khác. Không, tất cả mọi linh hồn sẽ gánh vác vừa sức của mình, sau đó các ngươi sẽ trở về trình diện trước Thượng Đế của các ngươi vào ngày Phán Xét. Sau đó, Ngài sẽ thông báo cho các ngươi biết về những gì mà các ngươi đã làm ở trên trần gian và nhận lấy phần thưởng tương xứng cho từng việc làm của các ngươi. Quả thật, Ngài là Đấng Toàn Năng, Đấng Biết Rõ hết trong lòng đám bề tôi của Ngài nghĩ gì và sẽ không có gì có thể giấu giếm được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(39,8,'39:8','Và khi kẻ vô đức tin gặp phải hoạn nạn tai ương từ bệnh tật, mất mát tài sản hay sợ chết chìm khi ra biển thì lúc đó y sẽ van xin cầu cứu Thượng Đế của y, Đấng Toàn Năng, y chỉ sẽ cầu xin một mình Ngài. Nhưng rồi khi Ngài ban cho y được Hồng Ân của Ngài giúp y thoát khỏi hoạn nạn thì y lại quên đi Đấng mà mình đã từng than thở van cầu trước kia. Và còn hơn thế nữa, y lại dám tôn sùng ai đó ngang hàng với Allah rồi đâm ra thờ phượng chúng mà không phải là Ngài hồng để dẫn dắt nhân loại đi chệch hướng con đường của Allah. Ngươi - hỡi Thiên Sứ - hãy nói với những ai trong tình trạng như y: "Ngươi hãy cứ tận hưởng phần tuổi đời còn lại ngắn ngủi của ngươi bằng sự vô đức tin của ngươi bởi quả thật rồi đây chắc chắn ngươi sẽ là một trong những người bạn của Hỏa Ngục vào ngày Phán Xét."');
INSERT INTO "vn_mokhtasar" VALUES(39,9,'39:9','Một người phục tùng Allah, dốc lòng cung kính Ngài trong những thời gian của đêm để dâng lễ nguyện Salah, có lúc thì cúi đầu quỳ lạy Thượng Đế của y, có lúc thì đứng thẳng người trước Ngài, y lo lắng khiếp sợ về hình phạt ở ngày sau và luôn khao khát có được lòng Khoan Dung của Ngài lại ngang bằng với kẻ vô đức tin chỉ thờ phượng Allah trong lúc gặp nạn và vô đức tin nơi Ngài và tổ hợp các thần linh với Ngài trong lúc bằng an và phồn thịnh ư? Ngươi - hỡi Thiên Sứ - hãy nói: "Chẳng lẽ những người hiểu biết luôn thành tâm cung kính trước Allah lại ngang bằng với những kẻ chẳng hiểu biết gì về chuyện thờ phượng Ngài ư?" Quả thật, chỉ có những người có tâm trí lành mạnh mới nhận biết hai nhóm đó hoàn toàn khác nhau.');
INSERT INTO "vn_mokhtasar" VALUES(39,10,'39:10','Ngươi - hỡi Thiên Sứ - hãy nói với đám bầy tôi có đức tin nơi TA và nơi các Sứ giả của TA: "Các ngươi hãy kính sợ Thượng Đế của các ngươi bằng cách tuân theo những mệnh lệnh của Ngài và tránh xa những điều Ngài ngăn cấm và luôn làm tròn bổn phận trách nhiệm trong tôn giáo, gom góp điều thiện phước ở trần gian bằng sự giúp đỡ, sức lực và tài sản để đổi lấy Thiên Đàng vào Ngày sau. Và đất đai của Allah thì bao la, các ngươi hãy di cư để tìm kiếm nơi thờ phượng Allah, các ngươi không bị cấm đoán về điều đó. Quả thật, chỉ những người biết kiên nhẫn mới nhận được phần thưởng của họ vào Ngày Phán Xét một cách vô định.');
INSERT INTO "vn_mokhtasar" VALUES(39,11,'39:11','Ngươi - hỡi Thiên Sứ - hãy nói: "Quả thật, Ta chỉ là một bề tôi được Allah sắc lệnh phải thờ phượng duy nhất một mình Ngài, phải thành tâm thờ phượng Ngài"');
INSERT INTO "vn_mokhtasar" VALUES(39,12,'39:12','"Và Ta được sắc lệnh phải trở thành một người Islam đầu tiên của Ngài và cũng là tín đồ đầu tiên của cộng đồng (Islam)."');
INSERT INTO "vn_mokhtasar" VALUES(39,13,'39:13','Ngươi - hỡi Thiên Sứ - hãy nói: "Quả thật, Ta rất khiếp sợ cái hình phạt vào ngày Trọng Đại nếu như Ta bất tuân Allah và không phục tùng Ngài, đó chính là ngày Phán Xét."');
INSERT INTO "vn_mokhtasar" VALUES(39,14,'39:14','Ngươi - hỡi Thiên Sứ - hãy nói: "Quả thật, Ta chỉ thờ phượng một mình Allah duy nhất bằng lòng thành tâm dành cho Ngài, Ta không tổ hợp kẻ hợp tác ngang vai cùng với Ngài."');
INSERT INTO "vn_mokhtasar" VALUES(39,15,'39:15','Thế vậy, các ngươi - hỡi những kẻ đa thần - hãy thờ phượng những ai mà các ngươi ưa thích trong các vị thần linh của các ngươi ngoài Ngài. Ngươi - hỡi Thiên Sứ - hãy nói: "Quả thật, những kẻ bất hạnh thật sự là những kẻ đã đánh mất đi chính linh hồn của họ và gia quyến dòng tộc của họ. Do đó, họ sẽ không bao giờ có cuộc gặp gỡ người thân của họ để cùng đồng hành mà họ chỉ đi được từng người một vào Thiên Đàng và còn ngược lại khi vào Hỏa Ngục thì họ sẽ đi vào cùng với mọi người. Bởi thế, họ không bao giờ có cuộc hội ngộ và điều đó là sự hiển nhiên đối với những kẻ bất hạnh quá rõ ràng không có gì cho là uẩn khúc trong nó."');
INSERT INTO "vn_mokhtasar" VALUES(39,16,'39:16','Bao phủ lên người họ là những làng khói mịt mù kèm theo là những ngọn lửa cháy bùn với độ cháy cực nóng. Đó chẳng qua chỉ là lời cảnh báo về hình phạt của Allah để cho đám bề tôi biết khiếp sợ Ngài." Này hỡi đám bầy tôi của TA! Hãy kính sợ TA bằng sự phục tùng mệnh lệnh của TA và kiềm hãm mình trước những điều mà TA đã nghiêm cấm."');
INSERT INTO "vn_mokhtasar" VALUES(39,17,'39:17','Và những ai tránh xa việc thờ phượng các bục tượng, các thần linh được thờ phượng ngoài Allah và quay về sám hối với Ngài thì họ sẽ được báo tin mừng về Thiên Đàng lúc chết, lúc trong cõi mộ và vào ngày Phán Xét. Bởi thế, Ngươi - hỡi Thiên Sứ - hãy báo tin mừng cho đám bầy tôi của TA.');
INSERT INTO "vn_mokhtasar" VALUES(39,18,'39:18','Những ai đã lắng nghe được Lời Phán rồi cân nhắc thận trọng giữa cái tốt và cái xấu, rồi tuân thủ theo Lời Phán tốt đẹp mang lại điều hữu ích thì họ là những người có một đức hạnh tốt đẹp được Allah phù hộ và hướng dẫn, họ thực sự là những người có khối có lành mạnh.');
INSERT INTO "vn_mokhtasar" VALUES(39,19,'39:19','Một ai đó mà lời xử phạt dành cho y đã được xác nhận bởi sự bất tuân và sự lạc lối của y thì Ngươi - hỡi Thiên Sứ - sẽ không thể hưởng dẫn được y. Bởi thế, Ngươi - hỡi Thiên Sứ - làm sao có thể cứu vớt được một người mà y đã được mô tả rằng mình là cư dân của Hỏa Ngục?');
INSERT INTO "vn_mokhtasar" VALUES(39,20,'39:20','Ngược lại, những ai biết kính sợ Thượng Đế của họ; bằng sự phục tùng tuyệt đối trước mệnh lệnh của Ngài và tránh xa những gì mà Ngài nghiêm cấm thì họ sẽ có được những ngôi nhà thật cao với các tầng chồng lên nhau, tầng này bên trên tầng kia, bên dưới các tòa nhà có các dòng sông chảy. Đó là điều mà Allah đã hứa hẹn với họ. Và Allah không bao giờ thất hứa.');
INSERT INTO "vn_mokhtasar" VALUES(39,21,'39:21','Quả thật, các ngươi quan sát thấy việc Allah ban nước mưa từ trên trời xuống, cho nó nhập vào các con suối và các dòng chảy, rồi Ngài dùng nguồn nước này làm mọc ra các loại cây trồng đủ màu sắc khác nhau, rồi sau đó các cây trồng héo khô. Bởi thế, ngươi - hỡi người đang quan sát - sẽ thấy màu của các loại cây trồng đủ màu sắc lại trở nên vàng úa sau khi nó đã từng xanh tươi, tiếp đó nó héo tàn và vỡ vụn. Quả thật, trong sự việc đó là những điều nhắc nhở dành cho những ai có trái tim vẫn còn sự sống.');
INSERT INTO "vn_mokhtasar" VALUES(39,22,'39:22','Phải chăng một ai đó được Allah mở rộng tấm lòng của y để đón nhận Islam và hướng dẫn y đến với nó và giúp y nhận thức về Thượng Đế của y, lại giống với một kẻ có một trái tim chai cứng trước lời nhắc nhở của Allah hay sao? Sẽ không bao giờ có sự ngang bằng nhau như thế. Bởi thế, thật nguy khốn cho những ai mà trái tim của họ bị chai cứng trước lời nhắc nhở của Allah. Những kẻ đó thật sự đang lầm lạc khỏi chân lý một cách rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(39,23,'39:23','Allah ban xuống cho Thiên Sứ của Ngài - Muhammad cầu xin sự bình an cho Người - Thiên Kinh Qur''an làm một lời Giáo Huấn thật tốt đẹp. Nó được ban xuống gồm các đại ý giống nhau về điều đúng, điều tốt đẹp, hợp lý và không bao giờ có sự đối lập. Trong Nó còn có những câu chuyện, những giáo lý, những điều hứa tốt đẹp cùng với những lời hăm dọa, những thuộc tính của những người Chính Đạo, những thuộc tính của đám người hư đốn và tội lỗi cùng những thông tin khác, v.v. Mỗi khi họ nghe được những lời cảnh báo về sừ phạt thì họ nổi gay ốc. Và mỗi khi họ nghe được những lời hứa hẹn và những tin mừng về những điều tốt đẹp thì làn da và con tim của họ dịu đi. Và đó là những gì trong Thiên Kinh Qur''an, Allah muốn dùng Nó để hướng dẫn ai tùy ý Ngài; và ai mà Allah muốn làm lệch lạc thì chắc chắn sẽ không tìm thấy người hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(39,24,'39:24','Phải chăng, một người được sự chỉ dẫn của Allah, được Ngài phù hộ ở trần gian và được Ngài thu nhận vào Thiên Đàng ở Đời Sau lại ngang bằng với một kẻ không có đức tin, chết trong sự bất tuân Ngài và bị xiềng xích cả hai tay hai chân ném vào Hỏa Ngục ư? Những kẻ bất công với chính bản thân mình bằng sự vô đức tin và tội lỗi sẽ được bảo: "Các ngươi hãy nếm lấy hậu quả về những gì mà các ngươi đã từng gây ra từ sự vô đức tin và tội lỗi. Đây chính là phần thưởng của các ngươi."');
INSERT INTO "vn_mokhtasar" VALUES(39,25,'39:25','Trước những kẻ đa thần nay, cũng đã có những cộng đồng đã phủ nhận, nên sự trừng phạt đã bất ngờ ụp xuống họ từ các hướng khiên họ không tài nào tránh kịp. Do đó, các ngươi hãy nên sớm chuẩn bị sám hối trước Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(39,26,'39:26','Bởi thế, Allah đã bắt họ nếm lấy sự trừng phạt ô nhục, đầy xấu hổ và đáng bị miệt thị ở cuộc sống trần gian. Và chắc chắn, ở Ngày Sau sự trừng phạt đang chờ họ sẽ khủng khiếp và khắc nghiệt hơn, nếu như họ là những người hiểu biết được điều đó!');
INSERT INTO "vn_mokhtasar" VALUES(39,27,'39:27','Quả thật, TA đã đưa ra cho nhân loại các hình ảnh thí dụ trong Thiên Kinh Qur''an mà TA đã ban xuống cho Muhammad - cầu xin Allah ban bình an cho Người - ẩn chứa những điều tốt đẹp cũng như những điều tồi tệ, những điều chây lý cũng như những điều ngụy tạo, đức tin Iman cũng như sự vô đức tin Kufr, v.v. Mong rằng qua những hình ảnh thí dụ ấy của TA, họ có những bài học, biết đâu là đúng và đâu là sai, biết làm điều chân lý và từ bỏ điều ngụy tạo.');
INSERT INTO "vn_mokhtasar" VALUES(39,28,'39:28','TA đã hoàn chỉnh Thiên Kinh Qur''an bằng ngôn ngữ Ả Rập, không có sự méo mó, sai lệch hay mơ hồ trong nó, mong rằng họ sẽ biết kính sợ Allah mà làm theo mệnh lệnh của Ngài cũng như tránh xa mọi điều mà Ngài nghiêm cấm.');
INSERT INTO "vn_mokhtasar" VALUES(39,29,'39:29','Allah đưa ra một hình ảnh thí dụ: một người đàn ông đa thần làm phục dịch cho nhiều vị chủ nhân gồm những đối tác với nhau nhưng họ thường hay cãi nhau; chắc chắn, một trong số họ sẽ hài lòng còn một số thì không hài lòng với y, do đó y trở nên hoang mang và lo lắng. Còn một người đàn ông khác chỉ phục dịch cho một vị chủ nhận duy nhất và còn hiểu được ý của người, nên y rất thoải mái và yên ổn. Nếu so sánh hay người đàn ông này thì sẽ thấy không có sự ngang bằng nhau giữa họ. Mọi lời ca ngợi đều kính dâng lên Allah! Không, hầu hết trong số họ không hề hay biết gì. Bởi thế, họ đã tổ hợp một ai đó cùng với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(39,30,'39:30','Quả thật Ngươi - hỡi Thiên Sứ - sẽ phải chết và chắc chắn họ cũng phải chết, điều đó là chắc chắn và kiên định.');
INSERT INTO "vn_mokhtasar" VALUES(39,31,'39:31','Sau đó chắc chắn các ngươi - hỡi nhân loại - vào ngày Phán Xét cuối cùng, các ngươi sẽ tranh chấp với nhau trước mặt Thượng Đế của các ngươi. Bởi vì, lúc đó mọi việc sẽ được sáng tỏ, đâu là chân lý và đâu là ngụy tạo.');
INSERT INTO "vn_mokhtasar" VALUES(39,32,'39:32','Và không có một ai gian ác hơn kẻ đã quy cho Allah những điều không xứng với Ngài như: Ngài có đối tác, có vợ và có con trai. Cũng như không có một ai gian ác hơn kẻ đã phủ nhận lời mặc khải mà Thiên Sứ của Allah đã mang đến. Phải chăng trong Hỏa Ngục không có một chỗ cư ngụ nào dành cho những kẻ không có đức tin ở nơi Allah cũng như với những điều đã được Thiên Sứ của Ngài mang đến hay sao?! Không, có một chỗ cư ngụ hẳn hồi ở trong đó dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(39,33,'39:33','Và những ai đến bằng sự trung thực trong lời nói và việc làm của y từ các vị Nabi và những người khác, cũng như y tin tưởng nơi nó như một người có đức tin và làm theo đúng quy định. Họ là những người kính sợ thực sự mà tuân theo mệnh lệnh của Thượng Đế của họ và tránh xa điều Ngài ngăn cấm.');
INSERT INTO "vn_mokhtasar" VALUES(39,34,'39:34','Vì thế, họ sẽ nhận được mọi điều mà họ mong muốn ở nơi Thượng Đế của họ về những niềm vui tốt đẹp vĩnh viễn. Đó là phần thưởng của những người làm tốt với Đấng Tạo Hóa của họ cũng như cùng với bầy tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(39,35,'39:35','Mục đích để Allah có thể xóa tội cho họ khỏi những điều tồi tệ nhất mà họ đã từng phạm tội ở trên trần gian này bởi sự sám hối của họ, và quay trở lại với Thượng Đế của họ, rồi ân thưởng họ với phần thưởng tốt nhất mà họ đã từng làm những việc làm ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(39,36,'39:36','Há Allah không đủ giúp cho Bề Tôi của Ngài là Muhammad - cầu xin Allah ban sự bình an đến Người - trong vấn đề tôn giáo và thế gian cũng như bảo vệ tránh khỏi kẻ thù ư?! Không, quả thật chỉ Ngài là đủ. Và chúng muốn làm cho Ngươi hoảng sợ- hỡi Thiên Sứ - về sự thiếu hiểu biết và ngu đần của chúng, từ việc tôn thờ pho tượng thay vì Allah, rằng họ sẽ hãm hại được Ngươi, và ai mà Allah bỏ rơi thì sẽ không có người hướng đạo và phù hộ.');
INSERT INTO "vn_mokhtasar" VALUES(39,37,'39:37','Và ai được Allah phù hộ cho sự hướng dẫn thì sẽ không thể bị ai dắt đi lạc. Há Allah không toàn năng chiến thắng tất cả ư, không nắm việc trừng phạt những kẻ phủ nhận và bất tuân với Ngài ư?! Không, quả thật Ngài toàn năng quyết phục thù.');
INSERT INTO "vn_mokhtasar" VALUES(39,38,'39:38','Nếu Ngươi - hỡi Thiên Sứ - có hỏi những kẻ đa thần rằng: Ai đã tạo ra các tầng trời và trái đất ? thì chắc chắn họ sẽ đáp: Allah đã tạo ra chúng. Ngươi hãy nói với họ một cách rõ ràng về sự bất lực của các thần linh của họ: Các ngươi hãy cho Ta biết về những pho tượng này mà các ngươi đã tôn thờ ngoài Allah có giúp Ta thoát khỏi sự đau đớn hay không nếu Allah muốn gây hại cho Ta?! hoặc nếu Thượng Đế của ta muốn ban cho Ta sự khoan dung từ Ngài thì chúng có thể ngăn chặn sự khoan dung của Ngài cho Ta được hay không ?! Ngươi hãy bảo họ: Chỉ duy nhất Allah đủ cho Ta rồi, và trong tất cả mọi sự việc của Ta đều phụ thuộc vào Ngài cũng như người tin tưởng Allah hãy phó thác cho Ngài duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(39,39,'39:39','Ngươi - hỡi Thiên Sứ - hãy nói: Hỡi dân chúng của ta ! Các ngươi hãy làm theo điều mà các ngươi cảm thấy thỏa mãn về việc Shikr cùng với Allah, còn ta làm theo những điều mà Thượng Đế của Ta ra lệnh gọi đến với thuyết Tawhid - độc thần - và trung thành trong việc thờ phượng Ngài, rồi đây các ngươi sẽ sớm biết kết cuộc của mọi con đường các ngươi đang theo.');
INSERT INTO "vn_mokhtasar" VALUES(39,40,'39:40','"Rồi đây các ngươi sẽ sớm biết ai là kẻ sẽ phải nhận một hình phạt ở trần gian này đầy xấu hổ và nhục nhã. Rồi sự nhục nhã đó sẽ bám chặt theo ngươi đến Ngày Phục Sinh để gánh chịu một hình phạt truyền kiếp mà không có hồi kết."');
INSERT INTO "vn_mokhtasar" VALUES(39,41,'39:41','Chắc chắn TA đã ban Kinh Qur''an xuống cho Ngươi - hỡi Thiên Sứ - dùng làm chỉ đạo cho nhân loại theo đúng chân lý để cảnh báo họ. Bởi thế, ai nhận được lợi ích của sự chỉ đạo thì được lợi cho bản thân y. Vì vậy Allah không cần đến lợi ích của sự chỉ đạo, bởi vì Ngài rất mực giàu có. Ngược lại ai đi lạc hướng thì sẽ bất lợi cho bản thân y, Allah thật Quang Vinh thay Ngài! y sẽ không bao giờ làm cho Ngài thiệt thòi về sự lạc hướng của y cả, và Ngươi không phải là người chịu trách nhiệm giùm cho họ để ép buộc họ đi theo con đường chỉ đạo. Do đó Ngươi chỉ là người truyền đạt cho họ những điều mà TA đã ra lệnh.');
INSERT INTO "vn_mokhtasar" VALUES(39,42,'39:42','Allah cho bắt những linh hồn khi kết thúc tuổi đời cũng như bắt những linh hồn chưa kết thúc tuổi đời khi nằm ngủ. Bởi thế, linh hồn của người nào mà Ngài quyết định cho chết thì sẽ bị giữ lại, và các hồn khác chưa được quyết định thì được gởi trở về thân thể đến một thời hạn ấn định trong sự hiểu biết của Ngài. Quả thật, trong việc bắt hồn đó và gởi trở về thân thể, rồi cho chết và phục sinh lại là những dấu hiệu cho đám người biết suy ngẫm, vì Ngài làm thế là Ngài có toàn quyền phục sinh nhân loại sau cái chết của họ để thưởng và phạt.');
INSERT INTO "vn_mokhtasar" VALUES(39,43,'39:43','Quả nhiên, những kẻ đa thần đã nhận các pho tượng can thiệp giùm họ với mong muốn giúp ích được họ thay vì Allah. Ngươi hãy bảo họ -hỡi Thiên Sứ -: Phải chăng các ngươi đã nhận các pho tượng làm vị can thiệp giùm các ngươi trong khi chúng không có quyền hành với các ngươi và luôn cả bản thân chúng, thậm chí chúng không có lý trí luôn ư. Chúng vốn là những đồ vật câm điếc không nói, không nghe, không thấy, không ích lợi cũng không làm hại vì cả ?!');
INSERT INTO "vn_mokhtasar" VALUES(39,44,'39:44','Ngươi - hỡi Thiên Sứ - hãy nói với những kẻ đa thần: Allah duy nhất nắm mọi quyền can thiệp. Bởi thế không có một ai được quyền đứng ra can thiệp trước Ngài ngoại trừ được phép của Ngài cũng như không có quyền can thiệp cho người khác ngoại trừ được Ngài hài lòng. Ngài là Đấng duy nhất nắm quyền thống trị các tầng trời và trái đất, rồi các ngươi sẽ được đưa về gặp Ngài duy nhất vào Ngày Phục Sinh cho việc thưởng và phạt. Dựa vào đó Ngài sẽ thưởng phạt cho những việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(39,45,'39:45','Và khi nghe nhắc đến Allah duy nhất thì làm trổi dậy lòng tràn đầy oán giận của những kẻ đa thần không tin tưởng nơi Allah ở Đời Sau và những điều ở nơi đó như phục sinh, thưởng và phạt. Ngược lại, khi những kẻ nhắc đến những pho tượng mà họ tôn thờ ngoài Allah thì họ cuống quýt mừng rỡ.');
INSERT INTO "vn_mokhtasar" VALUES(39,46,'39:46','Ngươi - hỡi Thiên Sứ - hãy nói: Lạy Allah, Ngài là Đấng đã khỏi tạo ra các tầng trời và trái đất không dựa trên nền tảng trước đó, Ngài Biết rất rõ mọi điều vô hình lẫn điều bộc lộ công khai, không có gì giấu giếm khỏi Ngài Đặng. Ngài là Đấng duy nhất xét xử giữa bầy tôi của Ngài vào Ngày Phục Sinh về những điều mà họ thường tranh chấp ở trên trần gian. Bởi thế, xin Ngài hãy làm sáng tỏ giữa sự thật và gian dối, giữa niềm vui và buồn bã.');
INSERT INTO "vn_mokhtasar" VALUES(39,47,'39:47','Và nếu như những kẻ làm điều sai quấy với bản thân mình bằng tội Shirk tức tổ hợp điều gì,vật gì cùng với Allah và tội lỗi khác có được nhiều vật quý báu và của cải trên trái đất thì chắc chắn họ sẵn sàn dùng nó để chuộc khỏi bị trừng phạt khủng khiếp mà họ đã chứng kiến sau khi bị phục sinh nhưng họ nào được toại nguyện. Và cho dù Ngài ban cho họ đạt được mong muốn thì Ngài vẫn không chấp nhận họ, và họ sẽ nhận được từ Allah nhiều loại hình phạt mà họ không ngờ đến.');
INSERT INTO "vn_mokhtasar" VALUES(39,48,'39:48','Và những điều xấu xa mà họ đã phạm đại tội Shirk cùng với Allah và những việc làm tội lỗi khác, tất cả sẽ theo họ cũng như bao quanh họ bằng nhiều hình phạt khiến họ hoảng sợ về những điều mà trên trần gian họ thường chế giễu.');
INSERT INTO "vn_mokhtasar" VALUES(39,49,'39:49','Nhưng khi kẻ không có đức tin gặp phải bệnh tật hoặc nghèo đói và những thứ khác thì y cầu xin TA để giải cứu về những điều y gặp phải, rồi khi TA ban ân huệ cho y về sức khỏe hoặc của cải thì y vong ân mà nói: Tôi được Allah ban cho ân huệ đó là do sự hiểu biết của tôi, bởi quả thật tôi rất xứng đáng nhận được nó. Và đúng hơn, đó là sự thử thách và lôi cuốn, nhưng đa số những kẻ không có đức tin không hề hay biết về điều đó. Bởi thế, họ bị đánh lừa về những ân huệ của Allah đã ban cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(39,50,'39:50','Chắc chắn lời nói này đã được nói ra từ những kẻ phủ nhận đức tin sống trước họ. Bởi thế những của cải lẫn địa vị mà họ có được chẳng giúp ích gì được cho họ cả.');
INSERT INTO "vn_mokhtasar" VALUES(39,51,'39:51','Thế là hậu quả do tội Shirk và các tội lỗi khác trút đổ lên họ. Và những kẻ bất công này phải nhận hậu quả của tội Shirk và các tội lỗi khác mà họ đã thu gom trước đấy giống như các thế hệ trước đó, họ chẳng bao giờ thoát khỏi Allah và càng không chiến thắng được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(39,52,'39:52','Há những kẻ đa thần này đã thốt những lời lẽ đó lại không biết việc Allah nới rộng bổng lộc cho bất cứ ai mà Ngài muốn để thử thách: Há tạ ơn hay phủ nhận ?! Cũng như Ngài thu hẹp bổng lộc cho bất cứ ai mà Ngài muốn để kiểm tra: Há nhẫn nại hay tức giận về sự sự an bày của Allah ?! Quả thật, trong sự việc đó được đề cập đến việc nới rộng bổng lộc và thu hẹp là các dấu hiệu mà Allah đã an bày cho đám người có đức tin; bởi vì họ là những người hữu ích cho các dấu hiệu đó, và ngược lại, những kẻ không có đức tin sẽ ngoãnh mặt bỏ đi.');
INSERT INTO "vn_mokhtasar" VALUES(39,53,'39:53','Ngươi - hỡi Thiên Sứ - hãy nói với đám bầy tôi của TA những người đã vượt qua giới hạn nghiêm cấm vì đã phạm tội Shirk cùng với Allah và các tội lỗi khác, chớ nên tuyệt vọng về đức khoan dung của Allah và sự dung tha của Ngài, Ngài sẽ tha thứ tội lỗi cho các ngươi. Chắc chắn Allah sẽ tha thứ mọi thứ tội cho ai biết sám hối với Ngài. Quả thật, Ngài là Đấng Hằng Tha Thứ các tội lỗi cho những người sám hối và là Đấng Rất Mực khoan Dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(39,54,'39:54','Và các ngươi hãy quay về với Thượng Đế của các ngươi bằng việc sám hối và làm những việc ngoan đạo cũng như thần phục Ngài trước khi đối diện với hình phạt vào Ngày Phục Sinh rồi các ngươi sẽ không tìm thấy đâu là những pho tượng hoặc gia tộc của các ngươi để giúp các ngươi thoát khỏi sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(39,55,'39:55','Và các ngươi hãy làm theo Thiên Kinh Qur''an, đó là điều tốt nhất mà Thượng Đế của các ngươi đã ban xuống cho Thiên Sứ của Ngài. Vì thế, hãy thực hiện về điều Ngài ra lệnh và tránh xa điều Ngài ngăn cấm, trước khi hình phạt xảy ra bất ngờ cho các ngươi mà các ngươi không hề nhận thấy. Do đó, các ngươi phải chuẩn bị sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(39,56,'39:56','Các ngươi hãy làm điều đó một cách thận trọng để tránh việc phải than vản đầy hối hận vào Ngày Phục Sinh: Ôi thật khổ thân tôi! vì sự hờ hững của tôi đối với Allah về những điều phủ nhận và phạm tội cũng như chế giễu những người có đức tin và người biết phục tùng.');
INSERT INTO "vn_mokhtasar" VALUES(39,57,'39:57','Hoặc ngươi đổ thừa cho sự an bày mà nói: Nếu như Allah đã hướng dẫn tôi thì chắc chắn tôi đã trở thành một trong những người ngay chính sợ Ngài, rồi tuân theo điều Ngài ra lệnh và tránh xa điều Ngài ngăn cấm.');
INSERT INTO "vn_mokhtasar" VALUES(39,58,'39:58','Hoặc ngươi sẽ nói khi nhìn thấy hình phạt rồi ao ước: Nếu như tôi được trở lại trần gian thì tôi sẽ sám hối với Allah cũng như tôi sẽ trở thành một trong những người tốt đẹp trong những việc làm hành đạo.');
INSERT INTO "vn_mokhtasar" VALUES(39,59,'39:59','Hoàn toàn không phải sự việc giống như ngươi đã từng tuyên bố mong muốn được hướng dẫn. Sự thật, các dấu hiệu của TA đã đến cho ngươi nhưng ngươi đã phủ nhận chúng và tỏ ra kiêu ngạo cũng như trở thành một trong những người không tin tưởng vào Allah, vào các dấu hiệu của Ngài và các Sứ Giả của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(39,60,'39:60','Và vào Ngày Phục Sinh, Ngươi sẽ nhìn thấy những kẻ nói dối rồi gán ghép cho Allah mà cho rằng Ngài có con thì gương mặt của họ sẽ có nám đen, một dấu hiệu cho sự bất hạnh của họ. Há trong Hỏa Ngục không có một chỗ cư ngụ dành cho những kẻ kiêu ngạo về niềm tin Iman ở nơi Allah và các Sứ Giả của Ngài hay sao?! Không, chắc chắn ở trong đó là nơi cư ngụ dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(39,61,'39:61','Và Allah sẽ cứu thoát khỏi sự trừng phạt cho những người biết kính sợ Thượng Đế của họ bằng việc tuân theo điều Ngài ra lệnh và tránh xa điều Ngài ngăn cấm, và đưa họ vào nơi thắng lợi của họ là Thiên Đàng, không một hình phạt nào chạm đến họ cũng như họ sẽ không buồn phiền về những điều mà họ đã bỏ ở thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(39,62,'39:62','Allah là Đấng Tạo Hóa tất cả mọi thứ. Bởi thế, không có Đấng Tạo Hóa nào ngoài Ngài cả, và Ngài bảo vệ gìn giữ tất cả mọi thứ, giám sát sắp xếp tất cả mọi việc cũng như Ngài điều khiển như thế nào Ngài muốn.');
INSERT INTO "vn_mokhtasar" VALUES(39,63,'39:63','Ngài duy nhất nắm giữ tất cả các chìa khóa kho tốt đẹp nhất trong các tầng trời và trái đất, Ngài ban cho ai Ngài muốn cũng như ngăn cấm đến với ai Ngài muốn, và những ai phủ nhận các dấu hiệu của Allah thì họ là những kẻ thua thiệt, sẽ mất đi niềm tin Iman trong cuộc sống thế gian. Vào Ngày Sau họ sẽ phải vào Hỏa Ngục ở trong nó muôn đời.');
INSERT INTO "vn_mokhtasar" VALUES(39,64,'39:64','Ngươi - hỡi Thiên Sứ - hãy bảo những kẻ đa thần muốn đẩy Ngươi trở lại tôn thờ những bục tượng của họ: Phải chăng các ngươi - hỡi đám ngươi không biết gì về Thượng Đế của các ngươi - ra lệnh Ta tôn thờ ai đó không phải là Allah hay sao?! Không có một ai xứng đáng để thờ phụng ngoại trừ Allah duy nhất. Bởi thế Ta không thể thờ phụng một ai khác ngoài Ngài cả.');
INSERT INTO "vn_mokhtasar" VALUES(39,65,'39:65','Và chắc chắn Allah đã Mặc khải cho Ngươi - hỡi Thiên Sứ - cũng như mặc khải cho các Sứ Giả trước Ngươi: Nếu như Ngươi thờ phụng những kẻ không phải là Allah thì việc làm ngoan đạo của Ngươi sẽ không có phần thưởng nào cả, và chắc chắn Ngươi sẽ trở thành một kẻ thua thiệt ở trên đời này bằng việc bị đánh mất tôn giáo của Ngươi, và vào Ngày Sau sẽ bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(39,66,'39:66','Đúng hơn, Ngươi hãy thờ phụng Allah duy nhất, và không tổ hợp bất cứ ai với Ngài cả, và hãy trở thành một người biết tạ ơn đến Ngài về những ân huệ mà Ngài đã ban đến cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(39,67,'39:67','Và những kẻ đa thần đánh giá Allah không đúng với sự vĩ đại của Ngài khi họ tổ hợp một ai khác cùng với Ngài từ những tạo vật yếu ớt bất lực so với Ngài, và họ đã lờ đi về phép mầu nhiệm của Allah với các biểu hiện rõ rệt ở dưới mặt đất bao gồm: núi non, cây cối, sông ngòi và biển cả, tất cả sẽ bị nắm trọn trong tay Ngài vào Ngày Phục Sinh cũng như tất cả bảy tầng trời sẽ được xếp gọn lại trong tay phải của Ngài. Thật Quang Vinh và Trong Sạch thay Ngài, Ngài tối cao vượt hẳn điều mà họ đã gán ghép rằng Ngài có những kẻ hợp tác.');
INSERT INTO "vn_mokhtasar" VALUES(39,68,'39:68','Vào Ngày mà Thiên Thần thổi còi được lệnh rú lên tiếng còi bằng sừng, nó khiến tất cả những ai ở trong các tầng trời và dưới đất đều phải chết, sau đó Thiên Thần rú lên tiếng còi lần thứ hai cho việc phục sinh thì tất cả họ sẽ sống lại đứng thẳng và nhìn thấy những gì Allah đã làm cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(39,69,'39:69','Và trái đất được chói sáng ánh hào quang của Thượng Đế của quyền lực để phân xử đám bầy tôi của Ngài, các quyển sổ ghi những việc làm của nhân loại sẽ được mở ra, các vị Nabi cùng với cộng đồng Muhammad (cầu xin Allah ban sự bình an cho Người) sẽ được triệu đến để chứng kiến các vị Nabi xác nhận cộng đồng của Họ, cũng như Allah xét xử giữa họ bằng lẻ công bằng, và họ sẽ không bị đối xử thiệt thòi một tí nào về Ngày Đó. Bởi thế con người sẽ không bị tăng thêm điều xấu nào cũng như không bị cắt giảm điều tốt nào.');
INSERT INTO "vn_mokhtasar" VALUES(39,70,'39:70','Và Allah sẽ thanh toán đầy đủ phần thưởng cho mỗi người về việc y đã làm tốt hoặc xấu, và Allah biết rất rõ về những điều họ đã làm, không một điều nào có thể giấu giếm khỏi Ngài về những việc làm tốt hoặc xấu của họ cũng như Ngài sẽ thưởng phạt họ vào Ngày này về những việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(39,71,'39:71','Và các Thiên Thần kéo những kẻ phủ nhận nơi Allah đến Hỏa Ngục từng nhóm từng nhóm một cách nhục nhã, mãi cho đến khi họ đến nơi Hỏa Ngục thì các cánh cửa đã được mở ra cho họ và các Thiên Thần cai ngục sẽ đón tiếp hỏi họ bằng lời quở trách: Há các vị Sứ Giả xuất thân từ các ngươi đã không đến gặp các ngươi để đọc các lời mặc khải của Thượng Đế của các ngươi đã truyền xuống cho họ ư, cũng như không làm hoảng sợ các ngươi về sự gặp gỡ vào Ngày Phục Sinh cho sự trừng phạt kinh hoàng hay sao ?! Những kẻ phủ nhận sẽ đáp rằng: Vâng, tất cả đều diễn ra như vậy, nhưng lời xử phạt đã xác nhận đúng với những kẻ phủ nhận, và chúng tôi đã trở thành những người không có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(39,72,'39:72','Sẽ có lời nhục nhã cho họ và sự tuyệt vọng về lòng thương xót của Allah từ bên ngoài Hỏa Ngục: Các ngươi hãy bước vào các cánh cửa của Hỏa Ngục để vào ở trong nó vĩnh viển. Bởi thế, thật tồi tệ và xấu xa thay chỗ ngụ của những kẻ kiêu ngạo tự cao đối với sự thật.');
INSERT INTO "vn_mokhtasar" VALUES(39,73,'39:73','Ngược lại, các Thiên Thần sẽ dẫn đi nhã nhặn, lịch sự đến với Thiên Đàng từng nhóm người tin tưởng biết kính sợ Thượng Đế của họ bằng việc tuân theo điều Ngài ra lệnh và tránh xa điều Ngài ngăn cấm, mãi đến lúc họ đến được Thiên Đàng thì các cánh cửa cũng đã được mở ra cho họ, và các Thiên Thần gác cửa Thiên Đàng tươi cười nói: Bằng An cho quý vị từ mọi tai hại cũng như tất cả mọi thứ quý vị căm ghét, con tim và những việc làm của quý vị thật hoàn hảo. Bởi thế hãy bước vào Thiên Đàng cư ngụ muôn đời trong nó.');
INSERT INTO "vn_mokhtasar" VALUES(39,74,'39:74','Và những người tin tưởng sau khi được bước vào Thiên Đàng, họ nói: Alhamdulillah, xin tạ ơn Allah đã thực hiện lời hứa của Ngài đã hứa với bầy tôi từ cửa miệng của các Sứ Giả của Ngài. Bởi quả thực bầy tôi đã được hứa sẽ vào được Thiên Đàng cũng như cho bầy tôi thừa hưởng vùng đất của Thiên Đàng. Do đó, bầy tôi ở trong Thiên Đàng bất cứ nơi nào mà bầy tôi muốn. Bởi thế phần thưởng ban cho những người làm việc tốt thật là ưu việt mà họ làm những việc ngoan đạo để tìm kiếm sự hài lòng từ Thượng Đế của họ.');
INSERT INTO "vn_mokhtasar" VALUES(39,75,'39:75','Và vào Ngày này mà các Thiên Thần đều hiện diện bao quanh Ngai Vương tán dương Allah và xóa sạch khỏi Ngài về những gì không xứng đáng với Ngài mà những kẻ phủ nhận đã gán ghép, và Allah xét xử giữa tất cả tạo vật với sự công bằng. Bởi thế, Ngài sẽ tôn trọng những ai biết tôn trọng giúp đỡ người khác và sẽ gây đau đớn những ai gây đau đớn người khác và có lời hô hào: Alhamdulillah, xin tạ ơn Allah, Thượng Đế của vạn vật trong sự quyết định của Ngài với những gì Ngài xét xử bằng sự khoan dung cho đám bầy tôi tin tưởng của Ngài cũng như đã trừng phạt đám bầy tôi bất tuân của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(40,1,'40:1','(Ha.Mim) đã được trình bày ở phần đầu của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(40,2,'40:2','Kinh Qur''an được Allah mặc khải xuống cho Thiên Sứ của Ngài là Muhammad. Ngài là Đấng Toàn Năng mà không ai chiến thắng được Ngài, Đấng Am Tường biết hết mọi điều tốt đẹp đối với đám bầy tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(40,3,'40:3','Ngài là Đấng tha thứ các tội lỗi cho những người phạm tội, Đấng chấp nhận sự hối cải của đám bầy tôi của Ngài, nhưng hình phạt rất khủng khiếp cho những ai không biết sám hối về tội lỗi của mình, Đấng có lòng khoan dung và ưu ái. Do đó, không có thần linh nào xứng đáng để thờ phượng ngoài Ngài. Ngài là Đấng duy nhất, tất cả đám bầy tôi đều trở về gặp Ngài vào Ngày Phục Sinh. Bởi thế, Ngài sẽ thưởng cho họ với những gì họ xứng đáng.');
INSERT INTO "vn_mokhtasar" VALUES(40,4,'40:4','Không một ai dám tranh cải về các dấu hiệu của Allah được biểu hiện qua sự tồn tại của Đấng tối cao duy nhất và được xác nhận sự thật của các Sứ Giả của Ngài ngoài những kẻ không có đức tin đã phủ nhận nơi Allah vì sự hư hỏng nhận thức của họ. Bởi thế, Ngươi chớ buồn rầu cho chúng cũng như chớ để bị họ đánh lừa Ngươi về sự phù phiếm xa hoa.');
INSERT INTO "vn_mokhtasar" VALUES(40,5,'40:5','Trước họ, đã có nhiều kẻ phủ nhận các Sứ Giả của Allah như người dân của Nuh và những thành phần của liên quân ngoại đạo sau dân của Nuh như dân ''Ad, dân Thamud, dân của Lut, dân Madyan và Fir-''awn. Và mỗi một cộng đồng đều có âm mưu hãm hại Thiên Sứ của họ để mong giết được Người cũng như dùng lời lẽ ngụy biện mà họ có được để tranh cải hầu dập tắt được chân lý, nhưng TA đã túm bắt trừng phạt tất cả những cộng đồng đó. Bởi thế, hãy suy ngẫm việc trừng phạt của TA dành cho họ như thế nào đi. Thật sự, việc trừng phạt của TA rất khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(40,6,'40:6','Và tương tự như quyết định của Allah bằng sự hủy diệt những cộng đồng dối trá đó, và lời phán của Thượng Đế của Ngươi là thật - hỡi Thiên Sứ - đối với những kẻ phủ nhận, chắc chắn họ là những người bạn của Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(40,7,'40:7','Các vị Thiên Thần khuân Ngai vương của Thượng Đế của Ngươi - hỡi Thiên Sứ - và những Thiên Thần đứng hầu xung quanh Ngài đều tán dương Thượng Đế của họ và tin tưởng nơi Ngài, và họ luôn cầu xin sự tha thứ cho những ai có đức tin vào Allah mà thưa: Lạy Thượng Đế của bầy tôi! với sự hiểu biết và Lòng khoan dung của Ngài bao trùm tất cả mọi vật, xin Ngài tha thứ cho những ai biết quay về sám hối tội lỗi của mình và đi theo tôn giáo của Ngài cũng như bảo vệ họ tránh khỏi Hỏa Ngục chạm vào họ.');
INSERT INTO "vn_mokhtasar" VALUES(40,8,'40:8','Và các Thiên Thần thưa: Lạy Thượng Đế của bầy tôi ! Xin Ngài cho những người tin tưởng được vào Thiên Đàng vĩnh cửu mà Ngài đã hứa cho họ vào cùng với người ngoan đạo trong số cha mẹ, vợ chồng và con cái của họ. Quả thật, Ngài là Đấng Toàn Năng mà không ai chiến thắng được Ngài, Đấng Chí Minh trong sự đo lường sắp xếp của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(40,9,'40:9','Và xin Ngài hãy bảo vệ họ tránh khỏi điều xấu xa từ những việc làm của họ và chớ trừng phạt họ, và ai được Ngài bảo vệ khỏi sự trừng phạt của những việc làm xấu xa vào Ngày Phục Sinh thì chắc chắc đã được Ngài khoan dung. Đó là sự che chở thoát khỏi hình phạt và được thu nhận vào Thiên đàng bằng llòng khoan dung của Ngài. Đó là một thắng lợi vẻ vang mà không có thắng lợi nào so sánh được.');
INSERT INTO "vn_mokhtasar" VALUES(40,10,'40:10','Quả thật, những ai phủ nhận nơi Allah và các Sứ Giả của Ngài thì họ bị Thiên Thần cai ngục la mắng vào Ngày Phục Sinh khi họ bước vào Hỏa Ngục bằng lời lẽ khinh khi và nguyền rủa họ: Chắc chắn Allah sẽ ghét bỏ các ngươi còn hơn việc các ngươi ghét bỏ lẫn nhau khi các ngươi ở trên cõi đời lúc các ngươi được mời chấp nhận đến với đức tin nơi Allah thì các ngươi lại từ chối và phủ nhận Ngài và chọn lấy một thần linh khác cùng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(40,11,'40:11','Và những kẻ không tin tưởng thú nhận các tội lỗi trong quá khứ chẳng giúp ích gì được cho họ khi họ vẫn chưa ăn năn hối cải, họ thưa: Lạy Thượng Đế của bầy tôi! Ngài đã làm cho bầy tôi chết hai lần: Một lần, khi bầy tôi không là gì cả, rồi Ngài tạo ra cho bầy tôi, và một lần Ngài làm cho bầy tôi chết đi sau khi đã tạo ra bầy tôi. Ngược lại, và làm sống hai lần: Một lần là Ngài tạo ra cho bầy tôi sống từ thuở vốn không tồn tại, và một lần Ngài phục sinh lại bầy tôi từ cõi chết. Bây giờ, bầy tôi thú nhận các tội lỗi mà bầy tôi đã gây ra. Thế nên, có cách nào đưa bầy tôi ra khỏi Hỏa Ngục và quay trở lại với cuộc sống trần gian để bầy tôi sửa đổi và làm những việc ngoan đạo, để được Ngài hài lòng về bầy tôi?!');
INSERT INTO "vn_mokhtasar" VALUES(40,12,'40:12','Đó là hình phạt dành cho các ngươi vì lý do khi các ngươi được Allah mời gọi đến với Đấng duy nhất không được tổ hợp bất cứ ai với Ngài thì các ngươi lại phủ nhận quyết tạo ra những thần linh làm kẻ hợp tác của Ngài, và nếu có các thần linh nào được tôn thờ cùng với Allah thì các ngươi lại tin tưởng ngay. Bởi thế, việc xét xử là của Allah duy nhất, Đấng Tối Cao bằng chính bản thân Ngài, bằng chính quyền năng của Ngài và bằng chính năng lực của Ngài. Đấng Vĩ Đại mà chi phối trên tất cả vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(40,13,'40:13','Allah là Đấng cho các ngươi nhìn thấy các dấu hiệu của Ngài trong vũ trụ và chính trong bản thân các ngươi để chỉ rõ cho các ngươi thấy quyền năng duy nhất của Ngài cũng như ban nước mưa từ trên trời xuống cho các ngươi để làm mọc ra đủ loại thảo mộc và các hạt giống và những thứ khác, và đó là các dấu hiệu của Allah cho những ai thành tâm quay về sám hối với Ngài mới ghi nhớ được.');
INSERT INTO "vn_mokhtasar" VALUES(40,14,'40:14','Do đó, các ngươi hãy cầu xin Allah - hỡi những người tin tưởng - với tấm lòng chân thành với Ngài trong việc phục tùng lẫn cầu nguyện, chính trực với Ngài trong đức tin dẫu rằng những kẻ bất tuân rất ghét và tức giận điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(40,15,'40:15','Ngài là Chủ Nhân đáng phải kính sợ, phải thành tâm với Ngài trong việc phục tùng lẫn cầu nguyện, cũng như Ngài là Đấng Tối Cao của cấp bậc khác với tất cả vạn vật mà Ngài đã tạo hóa, và Ngài là Chủ Nhân của Ngai Vương chí đại, với lòng thương xót của Ngài nên Ngài cử phái Ruh tức Đại Thiên Thần Jibril mang mặc khải xuống gặp người nào mà Ngài muốn trong số bầy tôi là Sứ Giả của Ngài để Người làm thức tỉnh thiên hạ thoát khỏi sự mê muội cũng như để Người cảnh báo nhân loại về sự trừng phạt vào Ngày Phục Sinh mà sẽ gặp tất cả từ con người đầu tiên đến người cuối cùng.');
INSERT INTO "vn_mokhtasar" VALUES(40,16,'40:16','Vào ngày mà tất cả sẽ phải tập hợp lại trong cùng một thời điểm nhất định, không một điều gì họ có thể giấu giếm khỏi Allah về chính bản thân của họ và những việc làm và tội lỗi của họ, và Ngài sẽ hỏi: Quyền hành phán xử vào Ngày nay thuộc về ai ? giờ đây không còn câu trả lời nào ngoài câu thuộc về Allah, Ngài là Đấng duy nhất chính trong bản thân của Ngài, thuộc tính của Ngài và hành động việc làm của Ngài. Đấng Tối Thượng mà chi phối trên tất cả vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(40,17,'40:17','Vào ngày hôm nay, mỗi người được thưởng phạt về việc làm của mình đã gầy dựng nên, nếu xấu thì được xấu còn nếu tốt thì được tốt, không có việc xét xử bất công trong Ngày hôm nay. Bởi vì Ngài là Allah Đấng Thẩm Phán công bằng. Quả thật, Allah rất nhanh chóng trong việc thanh toán thưởng và phạt cho đám bầy tôi của Ngài, bởi sự hiểu biết của Ngài bao trùm lên họ.');
INSERT INTO "vn_mokhtasar" VALUES(40,18,'40:18','Và Ngươi - hỡi Thiên Sứ - hãy cảnh báo họ về Ngày Phục Sinh đang tiến đến rất gần. Vào Ngày đó những quả tim sẽ nhảy thót lên đến cổ họng rất khủng khiếp rồi làm nghẹt thở, họ sẽ câm nín không một ai trong số họ có thể nói chuyện được ngoại trừ những ai được Đấng Al-Rahman cho phép cũng như những kẻ tự bất công với bản thân chúng bằng Shirk và các tôi lỗi khác sẽ không có bạn bè, người thân hay người can thiệp nào để nhờ cậy cả.');
INSERT INTO "vn_mokhtasar" VALUES(40,19,'40:19','Allah biết rất rõ điều dối trá được giấu kính mà cặp mắt của họ đã nhìn cũng như biết rất rõ điều kín đáo trong lòng của họ, không bất cứ điều gì có thể giấu giếm khỏi Ngài được.');
INSERT INTO "vn_mokhtasar" VALUES(40,20,'40:20','Và Allah xét xử bằng luật công bằng. Bởi thế không một ai bị bất công về việc cắt giảm đi điều tốt của mình cũng như không bị tăng thêm điều xấu nào cả, và những kẻ đa thần mà họ tôn thờ ngoài Allah hoàn toàn không quyết định được gì cả. Bởi vì chúng không có quyền hành đó. Quả thật, Allah là Đấng Hằng Nghe mọi lời nói của đám bầy tôi của Ngài, Đấng Hằng Thấy những việc làm và sự định tâm trong lòng của họ. Dựa vào đó Ngài sẽ thanh toán thưởng phạt cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(40,21,'40:21','Và há những kẻ đa thần không chu du trên trái đất để suy ngẫm và xem xét kết cuộc của những cộng đồng dối trá đã sống trước họ như thế nào ư? Thật ra, kết cuộc của họ rất tồi tệ, và những cộng đồng trước đó vượt trội hơn họ về quyền lực lẫn di tích mà chúng còn để lại trên trái đất, nhưng Allah đã tiêu diệt chúng chỉ vì tội lỗi của chúng. Và không một ai có thể ngăn cản sự trừng phạt của Allah dành cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(40,22,'40:22','Đó là sự trừng phạt dành cho họ, bởi vì các Sứ Giả của họ đã đến gặp họ với các bằng chứng rất rõ ràng, nhưng họ đã phủ nhận và không tin tưởng vào Allah rồi còn cho rằng các Sứ Giả của Ngài là kẻ dối trá, và chỉ vì cứng đầu mà họ đã bị Allah túm bắt rồi tiêu diệt. Quả thật, Ngài rất hùng mạnh trong sự trừng phạt đối với ai đã phủ nhận Ngài và các Sứ Giả của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(40,23,'40:23','Và trước đây TA đã cử phái Musa cùng với các phép lạ rõ rệt của TA lẫn minh chứng xác thực.');
INSERT INTO "vn_mokhtasar" VALUES(40,24,'40:24','Đến gặp Fir-''awn và bộ trưởng của hắn là Haman và Qarun, nhưng bọn họ bảo Musa là một tên phù thủy chuyên dối trá mà lại luôn miệng khẳng định mình là một vị Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(40,25,'40:25','Do đó, khi Musa mang bằng chứng đến cho họ để chứng minh cho sứ mạng thật của Người thì Fir-''awn liền hạ lệnh: Hãy giết chết các đứa con trai của những ai mà tin tưởng theo Y, và bắt giữ các phụ nữ để làm nhục họ. Và mưu đồ của những kẻ không có đức tin muốn cố động lại số lượng người có đức tin, nhưng điều đó chỉ rơi vào lầm lạc và bị tiêu diệt.');
INSERT INTO "vn_mokhtasar" VALUES(40,26,'40:26','Và Fir-''awn bảo: Hãy để ta giết Musa như là một sự trừng phạt dành cho Nó cũng như hãy để Nó kêu gọi Thượng Đế của Nó đến để ngăn cản ta đi, nhưng ta sẽ không sợ việc kêu gọi Thượng Đế của Nó đến đây đâu, ta chỉ sợ nó sẽ thay đổi tôn giáo của các ngươi rồi các ngươi đi theo Nó hoặc Nó sẽ dấy loạn trong xứ bằng cách giết chóc và tàn phá.');
INSERT INTO "vn_mokhtasar" VALUES(40,27,'40:27','Khi biết được lời hăm dọa của Fir-''awn đối với mình thì Musa liền bảo: Ta dựa dẫm và nhờ cậy đến Thượng Đế của Ta và của các ngươi để bảo vệ Ta thoát khỏi tay của những tên ngạo mạn không tin tưởng vào sự thật cũng như không tin tưởng vào Ngày Phục Sinh để thanh toán trong việc thưởng và phạt.');
INSERT INTO "vn_mokhtasar" VALUES(40,28,'40:28','Và có một người đàn ông có đức tin vào Allah trong đám người của Fir-''awn đã từng dấu kín đức tin của mình, một khi biết được họ quyết định muốn giết chết Musa thì người vội lên tiếng thưa: Phải chăng bệ hạ giết một người không hề phạm tội với ai chỉ vì lý do Người nói: Thượng Đế của tôi là Allah, và Người đã mang cho bệ hạ với những bằng chứng và minh chứng để chứng minh cho sự chân thật của Người về sự tuyên bố rằng Người là một Sứ Giả đến từ Thượng Đế của Người?! Và nếu Người là tên dối trá sẽ hoàn toàn chịu tội về lời dối trá của mình. Và ngược lại, nếu Người nói sự thật thì điều đã hăm dọa bệ hạ thì bệ hạ sớm trừng phạt mà không tránh khỏi. Thật sự Allah sẽ không hướng dẫn đến với chân lý đối với ai vượt qua giới hạn nghiêm cấm về sự bịa đặt điều gian dối rồi đỗ thừa cho Ngài lẫn các Sứ Giả của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(40,29,'40:29','Người đàn ông trình tiếp: Thưa bệ hạ! Ngày nay uy quyền nằm trong tay của bệ hạ nên bệ hạ chiếm ưu thế trong xứ Ai cập. Bởi thế, nhưng ai sẽ cứu chúng ta thoát khỏi hình phạt của Allah nếu nó xảy ra với chúng ta chỉ vì lý do giết chết Musa ?! Fir-''awn đáp: Ý kiến và phán quyết đều thuộc về trẫm, và trẫm thấy rằng trẫm phải giết chết Musa để giải trừ cái xấu lẫn cái ác cũng như trẫm chỉ hướng dẫn các ngươi đi theo con đường chân chính và đúng đắn.');
INSERT INTO "vn_mokhtasar" VALUES(40,30,'40:30','Và người tin tưởng đó khuyên bảo đám dân chúng của người rằng: Thật sự, tôi chỉ sợ giùm cho quí vị về đại họa - nếu giết chết đi Musa một cách bất công lẫn hận thù - thì sẽ bị trừng phạt giống như sự trừng phạt dành cho những thành phần của liên quân người ngoại đạo đã phủ nhận các Sứ Giả thời trước của chúng. Do đó,Allah đã tiêu diệt chúng.');
INSERT INTO "vn_mokhtasar" VALUES(40,31,'40:31','Giống số phận của kẻ phủ nhận lẫn kẻ vu khống các Sứ Giả đều nói dối như: Đám dân của Nuh, dân ''Ad và dân Thamud cũng như những kẻ sống sau họ. Thế là Allah đã tiêu diệt họ với sự phủ nhận và bịa đăt, Allah không muốn tạo bất công với đám bầy tôi của Ngài mà Ngài chỉ trừng phạt họ đúng theo tội lỗi mà họ đã từng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(40,32,'40:32','Và hỡi dân của tôi! Thật sự, tôi sợ giùm cho quí vị về Ngày Phục Sinh. Đó là Ngày mà tất cả mọi người sẽ cất tiếng gọi nhau vì mối quan hệ thân thuộc hoặc họ tin rằng cách này sẽ giúp ích được họ trong tình huống khủng khiếp này.');
INSERT INTO "vn_mokhtasar" VALUES(40,33,'40:33','Ngày, mà quí vị sẽ quay lưng tháo chạy vì sợ lửa của Hỏa Ngục. Quí vị sẽ không có một vị che chở nào đến ngăn cản từ sự trừng phạt của Allah, và ai mà Allah bỏ rơi thì sẽ không được một người hướng dẫn. Bởi vì sự hướng dẫn thành công là nằm trong tay Allah duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(40,34,'40:34','Và thật ra trước Musa đã có Yusuf mang các bằng chứng rất rõ ràng về thuyết Tawhid về sự tồn tại của Allah duy nhất đến gặp quí ngài, nhưng quí ngài không ngừng nghi ngờ và còn cho rằng Người là kẻ dối trá về thông điệp mà Người mang đến, mãi cho đến khi Người qua đời thì quí ngài lại càng nghi ngờ và không tin, và qúi ngài bảo: ''''Allah sẽ không bao giờ dựng lên một Sứ Giả nào sau Y nữa''''. Giống như sự lầm lạc của quí ngài đã ngăn cản không cho quí ngài đi theo chân lý và Allah sẽ đánh lạc hướng kẻ nào mà vượt qua giới hạn nghiêm cấm của Allah cũng như nghi ngờ về sự tồn tại của Allah duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(40,35,'40:35','Người nào hay tranh cải về các dấu hiệu của Allah hầu dập tắt được nó mà không có lập luận hay bằng chứng nào thì cuộc tranh cải đó thật đáng ghê tởm đối với Allah và đối với những người có đức tin cũng như các Sứ Giả của Ngài. Do đó, Allah sẽ niêm kín tấm lòng của những người hay tranh cải về các dấu hiệu của Ngài hầu dập tắt được nó cũng như Allah niêm kín tấm lòng của từng tên kiêu ngạo về sự thật giống như thế. Vì vậy sẽ không được Ngài hướng dẫn đến với điều đúng đắn và tốt đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(40,36,'40:36','Và Fir-''awn ra lệnh với tên cận thần của hắn: Hỡi Haman! Thần hãy xây cho trẫm một cái tháp cao, hi vọng trẫm có thể đến giáp hết các con đường');
INSERT INTO "vn_mokhtasar" VALUES(40,37,'40:37','Hi vọng rằng trẫm có thể lên tới những con đường của các tầng trời để diện kiến Thượng Đế của Musa mà nó tuyên bố rằng Ngài là Thượng Đế đích thực rất xứng đáng để thờ phượng, và trẫm nghĩ rằng Musa là một tên dối trá về điều mà Y đã tuyên bố. Vì vậy, đối với Fir-''awn việc làm tội lỗi của hắn đã hấp dẫn hắn khi nhờ cậy đến Haman, và hắn bị ngăn chặn ra khỏi con đường chân lý để đến với con đường lầm lạc, và mưu đồ của Fir-''awn muốn lấy mưu kế nhằm thể hiện sự giả dối mà hắn đang làm hầu muốn dập tắt được chân lý mà Musa đã mang đến. Bởi thế, kết cuộc thất bại đã phán tan sự nổ lực của hắn và dẫn đến sự đau khổ không có hồi kết');
INSERT INTO "vn_mokhtasar" VALUES(40,38,'40:38','Và người đàn ông tin tưởng thuộc dân chúng của Fir-''awn khuyên bảo và hướng dẫn người dân của mình để đi đến con đường chân chính mà bảo rằng: Hỡi người dân của tôi! Xin mọi người hãy nghe lời của tôi. Tôi sẽ hướng dẫn và dìu dắt quí vị đi đến con đường đúng đắn cũng như đến với con đường chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(40,39,'40:39','Hỡi người dân của tôi ! Quả thật, đời sống trần tục này chỉ là một sự hưởng thụ tạm bợ. Bởi thế, chớ để nó đánh lừa các ngươi về bất cứ cái gì mà các ngươi được ban rồi sẽ tiêu tan, trong khi cuộc sống ở Đời Sau mới là đời sống thực sự hưởng trọn niềm hạnh phúc vĩnh cửu. Đó là một chốn ở vĩnh viễn và trường tồn. Do đó, hãy làm những việc để được sự hài lòng của Allah cũng như cẩn thận kẻo bị lôi cuốn vào cuộc sống trần tục này rồi quên lãng đi cuộc sống của Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(40,40,'40:40','Ai làm điều xấu thì chỉ bị phạt giống với tội đã phạm. Ngược lại, ai làm việc ngoan đạo với mong muốn chỉ làm hài lòng Allah, bất luận nam hay nữ và tin tưởng vào Allah cũng như các Sứ Giả của Ngài sẽ được vào Thiên Đàng vào Ngày Phục Sinh, nơi mà Allah sẽ ban thưởng cho họ vô số thiên lộc cùng với niềm hạnh phúc vĩnh cửu không hề bị đứt đoạn.');
INSERT INTO "vn_mokhtasar" VALUES(40,41,'40:41','Và hỡi dân của tôi ! Tại sao lại có chuyện trái với lẽ thường như thế, trong lúc tôi mời gọi quí vị đến với chỗ cứu rỗi để thoát khỏi sự thua thiệt, mất mát ở đời này lẫn Đời Sau bằng cách đặt đức tin Iman vào Allah và làm những việc ngoan đạo thì quí vị lại mời gọi tôi đi vào Hỏa Ngục bằng những việc phủ nhận nơi Allah và bất tuân đối với Ngài ư ?!');
INSERT INTO "vn_mokhtasar" VALUES(40,42,'40:42','Quí vị kêu gọi tôi đến với chỗ lầm lạc của quí vị với mong muốn để tôi phủ nhận Allah cũng như để tôi tôn thờ những kẻ không phải là Ngài, điều mà tôi không hề biết một tí gì về giá trị của việc tôn thờ người khác cùng với Allah trong lúc tôi kêu gọi quí vị đến với đức tin Iman vào Allah, Đấng Toàn Năng mà không một ai chiến thắng được Ngài, Đấng Hằng Tha thứ tội lỗi cho đám bầy tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(40,43,'40:43','Quả nhiên, quí vị kêu gọi tôi đến với đức tin và phục tùng vào kẻ không có quyền mời gọi phải tôn thờ mình ở đời này lẫn Đời Sau, trong khi chúng không thể đáp lại bất cứ ai cầu nguyện chúng cả. Song, mục tiêu cuối cùng của chúng ta là trở về với Allah duy nhất, rồi đây những kẻ bất tuân và phạm tội chính là những người bạn của Hỏa Ngục, họ sẽ phải bước vào nó vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(40,44,'40:44','Nhưng họ đã bác bỏ lời khuyên của người đàn ông người tin tưởng đó. Bởi thế, y nói: Sau này quí vị sẽ nhớ những điều tôi đã khuyên răn cũng như quí vị sẽ hối tiếc vì đã bác bỏ nó, và tôi nguyện phó thác mọi công việc, tiền đồ của tôi cho Allah duy nhất. Quả thật, Allah là Đấng mà bất cứ việc hành đạo nào của đám bầy tôi của Ngài đều không thể giấu giếm khỏi Ngài đặng.');
INSERT INTO "vn_mokhtasar" VALUES(40,45,'40:45','Do đó, Allah đã bảo vệ y tránh khỏi điều xấu xa mà chúng âm mưu muốn giết chết y, và sự trừng phạt chết chìm đã bao trùm lên đám lâu la của Fir-''awn, rồi Allah đã nhấn hắn chết chìm dưới nước lụt cùng với tất cả đám thuộc hạ của hắn ở trên trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(40,46,'40:46','Và sau khi chết, họ sẽ bị dắt đi đến Hỏa Ngục ở trong ngôi mộ từ buổi sáng đến buổi chiều. Và vào Ngày Phục Sinh sẽ có lời bảo: ''''Các ngươi hãy đưa Fir-''awn lẫn đám thuộc hạ của hắn vào nơi trừng phạt kinh khủng nhất, bởi vì họ đã từng phủ nhận và dối trá cũng như cản trở thiên hạ không cho theo chính đạo của Allah');
INSERT INTO "vn_mokhtasar" VALUES(40,47,'40:47','Và Ngươi - hỡi Thiên Sứ - hãy nhớ lại khi kẻ yếu thế và kẻ bạo ngược tranh cải với nhau sau khi tất cả bị tống cổ vào Hỏa Ngục. Những người yếu thế nói với với những tên bạo ngược rằng: Quả thật, trước đây chúng tôi là người đi theo quí ngài trong sự lầm lạc ở trên trần gian. Có thể nào quí ngài lấy đi một phần hình phạt của Allah và gánh vác giùm cho chúng tôi được hay không ?!');
INSERT INTO "vn_mokhtasar" VALUES(40,48,'40:48','Những kẻ bạo ngược đáp lại: Dù sao thì tất cả chúng ta đều đã ở trong Hỏa Ngục cả rồi, và không một ai trong chúng ta gánh vác giùm bất cứ một phần hình phạt nào của người khác. Chắc chắn Allah đã phân xử đúng tội với đám bầy tôi của Ngài. Do đó, Ngài đã trừng phạt chúng ta những điều xứng đáng.');
INSERT INTO "vn_mokhtasar" VALUES(40,49,'40:49','Và đám người bị trừng phạt trong Hỏa Ngục gồm những kẻ đi theo và những tên bạo ngược đồng khấn cần Thiên Thần quản Hỏa Ngục để được thoát khỏi đó để trở lại trần gian mà ăn năn sám hối: ''''Xin ngài hãy cầu xin Thượng Đế của quí ngài giảm bớt hình phạt giùm chúng tôi dù chỉ một ngày từ sự trừng phạt vĩnh viễn này');
INSERT INTO "vn_mokhtasar" VALUES(40,50,'40:50','Thiên Thần quản Hỏa Ngục đáp lại lời van xin của kẻ vô đức tin rằng:''''Há các Sứ Giả của các ngươi đã không đến gặp các ngươi với những minh chứng và bằng chứng rõ ràng ư ?! Họ thưa: Dạ,có ! Các Thiên Sứ đã mang đến cho chúng tôi với những minh chứng và bằng chứng rất rõ ràng. Thiên Thần chế giễu chúng rằng: Thế thì các ngươi hãy cầu xin tiếp tục đi, còn chúng tôi sẽ không can thiệp giùm cho những kẻ vô đức tin đâu. Tuy nhiên, lời cầu xin của những kẻ không có đức tin chỉ quanh quẩn trong lầm lạc và thua thiệt, Ngài không chấp nhận chỉ vì sự phủ nhận đức tin của họ.');
INSERT INTO "vn_mokhtasar" VALUES(40,51,'40:51','Chắc chắn,TA (Allah) sẽ giúp các Sứ Giả của TA và những người tin tưởng vào Allah lẫn các Sứ Giả của TA ở trên trần gian bằng cách phơi bày các bằng chứng để giúp họ chống lại những kẻ thù của họ cũng như TA sẽ giúp họ vào Thiên Đàng vào Ngày Phục Sinh, và TA sẽ trừng phạt những kẻ thù của họ ở trên trần gian bằng cách tống cổ họ vào Hỏa Ngục sau khi các vị Nabi, các Thiên Thần và những người tin tưởng đứng ra làm chứng cho việc họ đã truyền đạt thông điệp đến dân làng nhưng đều bị cho là giả dối.');
INSERT INTO "vn_mokhtasar" VALUES(40,52,'40:52','Ngày mà những lý lẽ chạy tội của những kẻ làm điều sai quấy cho bản thân với tội phủ nhận đức tin và các tội lỗi khác đều không giúp ích gì được cho họ. Ngược lại, vào Ngày đó họ sẽ bị đuổi ra khỏi lòng thương xót của Allah cũng như họ phải nhận được một chỗ cư ngụ xấu xa vào Đời Sau cùng với sự trừng phạt rất đau đớn');
INSERT INTO "vn_mokhtasar" VALUES(40,53,'40:53','Và quả thật trước đây, TA đã ban kiến thức cho Musa để Người chỉ dẫn con cháu Israel đến với chân lý, cũng như TA bổ nhiệm con cháu Israel thừa hưởng Kinh Tawrah được truyền từ thế này đến thế hệ khác');
INSERT INTO "vn_mokhtasar" VALUES(40,54,'40:54','Một sự chỉ đạo đến với con đường chân chính, và là sự nhắc nhở cho những người có khối óc lành mạnh');
INSERT INTO "vn_mokhtasar" VALUES(40,55,'40:55','Bởi thế, Ngươi - hỡi Thiên Sứ - hãy kiên nhẫn về những điều mà dân chúng của Ngươi đã phủ nhận Ngươi cũng như gây hại đến Ngươi. Quả thật, Allah hứa với Ngươi đều là thật, không phải nghi ngờ gì cả, bằng cách cho Ngươi chiến thắng và ủng hộ Ngươi. Và hãy cầu xin Ngài tha thứ cho lỗi lầm của Ngươi và hãy tán dương ca tụng Thượng Đế của Ngươi vào lúc sớm tinh mơ và lúc cuối ngày');
INSERT INTO "vn_mokhtasar" VALUES(40,56,'40:56','Đối với những ai tranh luận về các dấu hiệu của Allah hầu dập tắt nó, tuy nhiên y không có bất kỳ bằng chứng làm cơ sở mà chỉ muốn thể hiện tự cao và vĩ đại ở trong lòng mình để lánh xa chân lý thì họ sẽ không bao giờ đạt nó được điều mà họ muốn. Bởi thế, Ngươi - hỡi Thiên Sứ - hãy bám chặt vào Allah, quả thật Ngài là Đấng Hằng Nghe mọi lời nói của đám bầy tôi của Ngài, Đấng Hằng Thấy mọi việc làm của họ, không bất cứ điều gì thoát khỏi Ngài được. Dựa vào đó Ngài sẽ thưởng phạt họ.');
INSERT INTO "vn_mokhtasar" VALUES(40,57,'40:57','Quả thật, việc tạo hóa các tầng trời và trái đất vĩ đại hơn, bao la hơn việc tạo hóa ra con người. Bởi thế, Ngài tạo ra chúng thật là hùng vĩ cũng như Ngài thừa khả năng phục sinh người chết nằm dưới ngôi mộ để thanh toán chúng cho việc thưởng phạt. Nhưng đa số nhân loại không hay biết và không hề xem xét cũng như họ không thu được bằng chứng về sự phục sinh với sự rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(40,58,'40:58','Và không bao giờ ngang bằng với nhau giữa người mù mắt và người sáng mắt cũng như những người tin tưởng vào Allah lẫn các Sứ Giả của Ngài và làm những việc ngoan đạo không ngang bằng với người làm điều xấu xa và phạm tội. Ít thay việc các ngươi biết đến. Lúc đó, nếu các ngươi nhớ lại để biết sự khác biệt giữa hai nhóm này thì các ngươi sẽ cố gắng để trở thành những người tin tưởng và làm những việc ngoan đạo với mong muốn được sự hài lòng của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(40,59,'40:59','Quả thật, Ngày Giờ mà Allah đã an bày để phục sinh người chết sống lại để thanh toán cho việc thưởng và phạt là điều không thể tránh khỏi, không cần phải nghi ngờ gì cả. Nhưng đa số nhân loại không tin tưởng về việc này cũng như không hề quan tâm chuẩn bị về nó.');
INSERT INTO "vn_mokhtasar" VALUES(40,60,'40:60','Và Thượng Đế của các ngươi - hỡi nhân loại - đã phán: Các ngươi hãy thờ phượng và cầu xin duy nhất mình TA thì TA sẽ đáp lại lời cầu xin của các ngươi cũng như TA sẽ tha thứ và yêu thương các ngươi nhiều hơn. Đối với những ai kiêu ngạo xem thường việc thờ phượng riêng TA thì sẽ phải đi vào Hỏa Ngục vào Ngày Phục Sinh một cách đáng khinh và nhục nhã.');
INSERT INTO "vn_mokhtasar" VALUES(40,61,'40:61','Allah là Đấng thiết lập ra ban đêm cho các ngươi nằm nghỉ ngơi cũng như làm ra ban ngày sáng lạng để các ngươi tìm kế sinh nhai. Quả thật, Allah đã đặc ân đối với loài người khi Ngài đã công khai hay kín đáo hoàn tất ân huệ của Ngài cho các ngươi. Nhưng đa số nhân loại không hề biết ơn đến Ngài về những điều mà Ngài đã ban đến cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(40,62,'40:62','Đó là Allah của các ngươi, Ngài đã ban cho các ngươi thiên lộc của Ngài, Ngài là Đấng Tạo Hóa tất cả vạn vật. Bởi thế, không có Đấng Tạo Hóa nào ngoài Ngài cả. Không có ai xứng đáng để thờ phượng ngoại trừ Ngài duy nhất. Thế tại sao các ngươi lại tránh xa việc thờ phượng Ngài để đến với việc thờ phượng những kẻ không có quyền hãm hại cũng chẳng làm lợi gì cho các ngươi đâu nào.');
INSERT INTO "vn_mokhtasar" VALUES(40,63,'40:63','Giống như những kẻ đã phủ nhận đức tin Iman ở nơi Allah và việc thờ phượng Ngài duy nhất thường lánh xa và hay chống đối lại các dấu hiệu của Allah trông khi có vô số dấu hiệu khẳng định thuyết Tawhid về Allah duy nhất vốn tồn tại khắp nơi trong tất cả mọi thời đại. Bởi thế, y không được hướng dẫn đến với chân lý cũng như không được phù hộ đến với sự thật.');
INSERT INTO "vn_mokhtasar" VALUES(40,64,'40:64','Allah là Đấng đã thiết lập trái đất cho các ngươi - hỡi nhân loại - thành một nơi cư ngụ vững chắc, và thiết lập bầu trời như một chiếc lọng ở bên trên để che cho các ngươi, rồi giữ không cho nó rơi xuống; Ngài đã nắn tạo các ngươi trong dạ con của các bà mẹ để hoàn thiện hình thể của các ngươi cũng như cung dưỡng các ngươi với thực phẩm Halal tốt, sạch. Đó là Allah, Thượng Đế của các ngươi đã ban cho các ngươi với những hồng ân này. Bởi thế, thật phúc thay Allah! Thượng Đế của vũ trụ và muôn loài. Do đó, không có một Thượng Đế nào ngoài Ngài cả.');
INSERT INTO "vn_mokhtasar" VALUES(40,65,'40:65','Ngài là Đấng Sống Mãi không bao giờ chết, không có Thượng Đế nào xứng đáng ngoài Ngài cả. Do đó, các ngươi hãy thờ phượng và hướng đến Ngài mà cầu xin, và chớ tổ hợp bất cứ vạn vật nào cùng với Ngài. Xin tạ ơn Allah, Thượng Đế của vũ trụ và muôn loài');
INSERT INTO "vn_mokhtasar" VALUES(40,66,'40:66','Ngươi - hỡi Thiên Sứ - hãy nói: Quả thật, Allah cấm Ta không được tôn thờ những ai mà các ngươi thờ phượng ngoài Ngài từ những bụt tượng vô tri, chẳng có lợi gì và cũng không làm hại được các ngươi, sau khi Ta đã nhận được minh chứng lẫn bằng chứng rất rõ rệt để dập tắt đi việc thờ phượng ngoài Ngài, cũng như Allah đã ra lệnh Ta phải thần phục Ngài duy nhất trong việc thờ phượng. Bởi vì Ngài là Thượng Đế tạo ra tất cả vạn vật mà không có Thượng Đế nào ngoài Ngài cả.');
INSERT INTO "vn_mokhtasar" VALUES(40,67,'40:67','Ngài là Đấng đã tạo ra ông tổ của các ngươi là Adam từ đất bụi, rồi sau Người Ngài đã tạo các ngươi từ tinh dịch, rồi từ hòn máu đặc, rồi mang các ngươi ra khỏi bụng mẹ thành các đứa bé, rồi làm cho các ngươi lớn mạnh với thân thể cường tráng, sau đó làm cho các ngươi trở thành già yếu, và trong các ngươi có người chết sớm và có người sẽ được sống cho đến thời hạn quy định trong sự hiểu biết của Allah, không thiếu và không tăng thêm số tuổi đã định cũng như để cho các ngươi biết được hữu ích với các luận cứ và bằng chứng này đến từ quyền năng của Ngài duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(40,68,'40:68','Ngài là Đấng duy nhất nắm toàn quyền quyết định sống chết tất cả vạn vật. Bởi thế, khi Ngài quyết định một việc gì đó thì Ngài chỉ phán với việc đó rằng: Hãy thành !" thì nó liền thành lập tức');
INSERT INTO "vn_mokhtasar" VALUES(40,69,'40:69','Phải chăng Ngươi - hỡi Thiên Sứ - không nhận thấy những kẻ phủ nhận đã tranh luận về các dấu hiệu của Allah trong khi nó đã quá rõ ràng ư, vậy mà bọn họ còn tỏ ra kinh ngạc rồi xa lánh chân lý rành rành như thế');
INSERT INTO "vn_mokhtasar" VALUES(40,70,'40:70','Những ai đã phủ nhận Kinh Qur''an và chân lý mà TA đã gửi đi cho các Sứ Giả của TA mang đến. Rồi đây, những kẻ phủ nhận sẽ sớm biết hậu quả của việc làm của họ cũng như họ sẽ nhìn thấy một kết thúc thật là tồi tệ');
INSERT INTO "vn_mokhtasar" VALUES(40,71,'40:71','Họ sẽ sớm biết hậu quả khi những chiếc gông cùm sẽ được đóng vào cổ của họ và những sợi dây xích sẽ cột vào chân của họ, rồi họ sẽ bị các cảnh vệ Hỏa Ngục lôi kéo đi để trừng phạt');
INSERT INTO "vn_mokhtasar" VALUES(40,72,'40:72','Họ bị lôi úp mặt trong nước nóng cực sôi, rồi vào trong lửa của Hỏa Ngục nơi mà họ sẽ bị đốt');
INSERT INTO "vn_mokhtasar" VALUES(40,73,'40:73','Rồi sẽ có lời bảo họ bằng giọng điệu chửi rủa và khiển trách: Đâu là những thần linh mà các ngươi thường tổ hợp ?!');
INSERT INTO "vn_mokhtasar" VALUES(40,74,'40:74','Cùng với Allah như những bụt tượng vô tri, không có lợi cũng chẳng hãm hại được các ngươi ?! những kẻ phủ nhận sẽ thưa: Họ đã bỏ chúng tôi đi biệt dạng. Bởi thế chúng tôi không nhìn thấy họ. Đúng hơn, trước đây ở trên trần gian chúng tôi đã không tôn thờ ai xứng đáng được thờ phượng. Tương tự việc khiến đám người này lầm lạc, Allah đã đánh lạc hướng những kẻ không tin tưởng rời xa chân lý ở mỗi thời đại và mọi nơi.');
INSERT INTO "vn_mokhtasar" VALUES(40,75,'40:75','Và có lời bảo họ: Với hình phạt mà các ngươi phải hứng chịu trong đau đớn là vì các ngươi đã thỏa thích vui mừng với những việc mà các ngươi đã Shirk tổ hợp những thần thánh cùng với Allah trong việc tôn thờ cũng như các ngươi phấn khởi, hớn hở và tươi cười trong sự vui mừng đó.');
INSERT INTO "vn_mokhtasar" VALUES(40,76,'40:76','Các ngươi hãy cút vào các cánh cửa của Hỏa Ngục để vào ở trong đó đời đời. Thật xấu xa thay chỗ ở của những kẻ ngạo mạn về chân lý');
INSERT INTO "vn_mokhtasar" VALUES(40,77,'40:77','Thôi, Ngươi - hỡi Thiên Sứ - hãy kiên nhẫn trước bao hãm hại mà dân chúng hướng về Ngươi. Chắc chăn lời hứa của Allah giúp Ngươi chiến thắng là sự thật không gì phải nghi ngờ. Bởi thế, dẫu TA có cho Ngươi thấy lúc Ngươi còn sống của Ngươi về một số điều mà TA đã chuẩn bị cho chúng về hình phạt giống như đã xảy ra vào ngày đại chiến tại Badr hoặc TA có bắt hồn Ngươi trước khi điều đó xảy ra thì bề nào họ vẫn phải trở về gặp TA riêng vào Ngày Phục Sinh để chịu sự thanh toán cho những việc làm của họ, rồi TA sẽ tống cổ họ vào Hỏa Ngục mà ở muôn đời.');
INSERT INTO "vn_mokhtasar" VALUES(40,78,'40:78','Và chắc chắn TA đã cử phái rất nhiều Sứ Giả đến với cộng đồng của Họ trước Ngươi - hỡi Thiên Sứ -. Tuy nhiên dân chúng đã phủ nhận và gây hại đến Họ, nhưng Họ đã kiên nhẫn về sự dối trá và sự gây hại của dân làng. Trong số các Sứ Giả đó, có người TA đã kể cho Ngươi biết về Họ, và có người TA đã không hề gì về Họ cả. Và tất nhiên không một Sứ Giả nào được phép mang đến cho dân chúng của Minh bất cứ dấu hiệu nào từ Thượng Đế của Người nếu không có lệnh của Ngài. Ngược lại những kẻ phủ nhận luôn đề xuất mang đến cho các cộng đồng của chúng với những dấu hiệu sai trái. Bởi thế, khi mệnh lệnh của Allah đã được thực thi hoặc phân xử giữa các Sứ Giả và các dân chúng của Họ thì Ngài xét xử giữa họ theo lẽ công bằng. Do đó, Ngài sẽ tiêu diệt những kẻ không tin tưởng và giải cứu các Sứ Giả của Ngài. Thế là sự thất bại trùm lên đám người vô đức tin khi chúng đã tự đẩy bản thân mình vào sự hủy diệt bằng sự vô đức tin của mình');
INSERT INTO "vn_mokhtasar" VALUES(40,79,'40:79','Allah là Đấng đã khống chế lạc đà,bò và dê cho các ngươi sử dụng, trong số đó các ngươi dùng để cưỡi và một số thì các ngươi dùng để ăn thịt');
INSERT INTO "vn_mokhtasar" VALUES(40,80,'40:80','Trong tất cả vạn vật xung quanh các ngươi có được nhiều lợi ích khác nhau được biến đổi trong mọi thời đại, và nhờ chúng các ngươi có thể đạt được những ước muốn về các nhu cầu cần thiết ở trong bản thân các ngươi cũng như các ngươi được dùng để chuyên chở trên đất liền và biển cả');
INSERT INTO "vn_mokhtasar" VALUES(40,81,'40:81','Và Đấng Hiển Vinh đã trưng bày cho các ngươi nhìn thấy các dấu hiệu của Ngài để chứng minh quyền năng duy nhất của Ngài. Thế đâu là các dấu hiệu của Allah mà các ngươi không công nhận nó sau khi các ngươi đã quyết định rằng nó là các dấu hiệu của Ngài ?!');
INSERT INTO "vn_mokhtasar" VALUES(40,82,'40:82','Há những kẻ phủ nhận không đi chu du trên trái đất để suy ngẫm kết cuộc của các cộng đồng phủ nhận sự thật đã sống trước họ như thế nào ư ?! Quả thật, các cộng đồng đó có nhiều của cải hơn họ và mạnh hơn họ cũng như có nhiều di tích lịch sử của các thế hệ trước vẫn còn sót lại trên trái đất. Thế nhưng, những gì được tạo nên từ bàn tay của các thế hệ trước có thể hiện sức mạnh của họ nhưng chẳng giúp ích gì được cho họ khi hình phạt hủy diệt của Allah giáng xuống họ.');
INSERT INTO "vn_mokhtasar" VALUES(40,83,'40:83','Đến khi các Sứ Giả của họ đến gặp họ với những bằng chứng rõ ràng để răn đe họ thì họ lại phủ nhận, và còn thỏa mãn mà tin theo kiến thức mà họ đang đã bác bỏ những gì do các Sứ Giả mang đến cho họ. Tuy nhiên với những điều họ thường chế nhạo đã bao vây họ bằng hình phạt mà các Sứ Giả đã từng cảnh báo họ');
INSERT INTO "vn_mokhtasar" VALUES(40,84,'40:84','Mãi khi họ nhìn thấy sự trừng phạt của TA thì họ lại thú tội khi mà lời thú tội chẳng giúp ích gì được cho họ cả, họ thưa: Bầy tôi đã tin tưởng vào Allah duy nhất, song phủ nhận những kẻ hợp tác và những bụt tượng mà bầy tôi đã tôn thờ ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(40,85,'40:85','Nhưng đức tin của họ chẳng giúp ích gì được cho họ khi đã nhìn thấy hình phạt của TA giáng xuống họ. Đó là qui luật của Allah dùng để áp dụng đối với đám bầy tôi của Ngài. Bởi vì đức tin của họ lúc đó không bao giờ đem lại lợi ích cho họ được vì họ đã nhìn thấy hình phạt ngay trước mắt. Và những kẻ không có đức tin sẽ thua thiệt khi Ngài giáng hình phạt lên bản thân họ bằng cả sự hủy diệt vì lý do họ đã phủ nhận nơi Allah cũng như không sám hối trước khi nhìn thấy sự trừng phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(41,1,'41:1','{Ha.Mim} là những chữ cái được dùng để khai đề cho chương Kinh. Ý nghĩa của nó đã được trình bày ở phần đầu của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(41,2,'41:2','Đây là Kinh Qur''an được ban xuống bởi Allah, Đấng Rất Mực Độ lượng, Đấng Rất Mực Khoan Dung');
INSERT INTO "vn_mokhtasar" VALUES(41,3,'41:3','Một Kinh sách với các câu Kinh hoàn chỉnh được giải thích rất rõ ràng và chi tiết. Và Ngài đã thiên khải Qur''an bằng tiếng Ả-rập cho những người hiểu biết. Bởi vì Nó sẽ rất hữu ích cho đám người hiểu biết qua những ý nghĩa sâu xa của Nó. Song, những điều trong Nó sẽ hướng dẫn đến với chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(41,4,'41:4','Nó báo tin mừng cho những người tin tưởng về những điều mà Allah đã chuẩn bị sẵn cho họ phần thưởng rất to lớn, song Nó cảnh báo những kẻ bất tuân về sự trừng phạt của Allah rất đau đớn, nhưng đa số bọn họ quay lưng bỏ đi cho nên họ không nghe thấu được về những điều được chỉ dẫn trong Nó');
INSERT INTO "vn_mokhtasar" VALUES(41,5,'41:5','Và họ bảo: Trái tim của bọn ta đã được niêm kín nên không nghe được những điều mà Ngươi mời gọi bọn ta chấp nhận, và tai của bọn ta điếc nên không nghe gì cả cũng như giữa bọn ta và Ngươi có một bức màn ngăn cách, vì vậy những điều Ngươi nói không truyền đạt đến bọn ta được. Bởi thế, hãy làm theo đường lối của Ngươi, còn bọn ta thì làm theo đường lối của bọn ta, và bọn ta sẽ không bao giờ đi theo Ngươi cả.');
INSERT INTO "vn_mokhtasar" VALUES(41,6,'41:6','Ngươi - hỡi Thiên Sứ - hãy nói với những kẻ ngoan cố: Chắc chắn Ta cũng chỉ là một người phàm tục giống như các ngươi, có điều Ta được Allah mặc khải cho biết là các ngươi chỉ có một Thượng Đế đích thực mà thôi, Ngài là Allah. Do đó, các ngươi hãy theo con đường ngay chính để đến với Ngài cũng như hãy cầu xin Ngài tha thứ cho những lỗi lầm của các ngươi, và rồi sự trừng phạt sẽ giáng lên những kẻ đa thần đã tôn thờ ngoài Allah hoặc đã tổ hợp bất cứ ai khác cùng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(41,7,'41:7','Họ là những ai không xuất Zakat từ tài sản của họ, và đối với Ngày Sau - về niềm tin hưởng niềm hạnh phúc vô tận và sự trừng phạt rất đau đớn - thì họ là những kẻ phủ nhận không tin tưởng');
INSERT INTO "vn_mokhtasar" VALUES(41,8,'41:8','Quả thật, với những ai có đức tin vào Allah lẫn các Sứ Giả của Ngài và luôn hành đạo tốt thì họ sẽ nhận được một phần thưởng bất tận, đó là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(41,9,'41:9','Ngươi - hỡi Thiên Sứ - hãy bảo những kẻ đa thần đáng bị khiển trách: Tại sao các ngươi lại phủ nhận Allah trong khi Ngài là Đấng đã tạo hóa trái đất trong hai ngày: Ngày chủ nhật và ngày thứ hai cũng như việc các ngươi dựng những đối thủ ngang vai với Ngài trong việc thờ phượng?! Đó là Thượng Đế của tất cả vạn vật kia mà');
INSERT INTO "vn_mokhtasar" VALUES(41,10,'41:10','Và Ngài đã đặt đè lên mặt đất bằng những quả núi kiên cố cao hơn mặt đất để làm vững chắc khỏi bị lung lai cũng như tính toán và ban phát lương thực cho nhân loại lẫn gia súc trong bốn ngày, và đã hoàn thành trọn vẹn cho hai ngày trước đó, và hai ngày còn lại là: Ngày thứ ba và thứ tư, đồng đều cho những ai muốn đòi hỏi về nó');
INSERT INTO "vn_mokhtasar" VALUES(41,11,'41:11','Rồi Ngài hướng về bầu trời và ngày hôm đó là một lớp khói mờ. Tiếp đó, Ngài phán cho bầu trời và trái đất: ''''Hai ngươi làm theo mệnh lệnh của TA là hòa quyện vào nhau hay không hòa quyện'''', nhưng hai người không được né tránh về điều này. Cả hai cùng thưa: Bầy tôi sẽ hòa quyện với nhau, bởi sẽ không có ý muốn cho bầy tôi nếu không thuận với ý muốn của Ngài, hỡi Thượng Đế của bầy tôi');
INSERT INTO "vn_mokhtasar" VALUES(41,12,'41:12','Rồi Allah hoàn thành việc tạo hóa các tầng trời trong hai ngày: Ngày thứ năm và thứ sáu. Như vậy Ngài đã tạo dựng trọn vẹn các tầng trời và trái đất trong sáu ngày và Allah mặc khải cho mỗi tầng trời về những điều quy định việc làm ở trong nó cũng như Ngài ra lệnh phải phục tùng lẫn thờ phượng, và Ngài đã trang hoàng lổng lẫy tầng trời thấp nhất bằng những ngôi sao tinh tú và TA cử các Thiên Thần canh giữ bầu trời để đánh đuổi những Shaytan nghe lén. Tất cả những điều đã được đề cập đó là quyết định của Đấng Toàn Năng mà không ai có thể vượt trội hơn Ngài, Đấng Toàn Chi biết hết mọi vạn vật của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(41,13,'41:13','Nhưng nếu họ lánh xa không tin tưởng những điều Ngươi - hỡi Thiên Sứ - đã mang đến thì hãy bảo họ: Ta cảnh báo về sự trừng phạt sẽ xảy ra đến với các ngươi giống như sự trừng phạt đã giáng xuống dân ''Ad thuộc cộng đồng của Hud và dân Thamut thuộc cộng đồng của Saleh khi họ đã phủ nhận hai vị Thiên Sứ đó.');
INSERT INTO "vn_mokhtasar" VALUES(41,14,'41:14','Khi các Sứ Giả của họ lần lượt đến gặp họ, tất cả đều ra lệnh chung một lệnh làchỉ được thờ phụng Allah duy nhất. Tuy nhiên những kẻ phủ nhận trong số họ lên tiếng: Nếu muốn, Thượng Đế của chúng tôi sẽ cử các Thiên Thần xuống làm Sứ Giả cho chúng tôi. Bởi thế, chúng tôi sẽ không tin vào những điều mà quí vị mang đến, bởi vì quý vị cũng là người phàm giống như chúng tôi vậy.');
INSERT INTO "vn_mokhtasar" VALUES(41,15,'41:15','Về dân ''Ad thuộc cộng đồng của Hud, họ đã phủ nhận Allah cùng với sự ngạo mạn trên trái đất không một lý do chính đáng cũng như làm những điều bất công với những người sống xung quanh họ. Với sức mạnh sẵn có đã biến họ thành những kẻ ngu dại mà nói rằng: Ai mạnh hơn bọn ta về sức mạnh ?! Không một ai mạnh hơn họ về sức mạnh mà họ đã khẳng định. Do đó Allah đáp lại họ: Há họ không biết và nhìn thấy rằng Allah là Đấng đã tạo hóa ra họ và đã mạnh hơn họ về sức mạnh và quyền lực hay sao ?! và họ thường phủ nhận các dấu hiệu của Allah do Hud mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(41,16,'41:16','Bởi thế, TA đã giáng xuống một trận cuồng phong bão táp trong suốt những ngày bất hạnh để trừng phạt họ cũng như TA cho họ nếm sự trừng phạt nhục nhã ở đời này, lẫn Đời Sau, tuy nhiên sự trừng phạt ở Đời Sau sẽ đợi họ mà còn nhục nhã hơn thế nữa kìa và họ sẽ không tìm thấy ai để cứu giúp chúng thoát khỏi hình phạt này.');
INSERT INTO "vn_mokhtasar" VALUES(41,17,'41:17','Còn về Thamud dân chúng Saleh, trước đây TA đã hướng dẫn họ để biết phân biệt con đường đi đến chính đạo, nhưng họ thích sự lầm lạc hơn là chỉ đạo sáng suốt đến với chân lý, cho nên hình phạt đến chụp bắt họ đầy nhục nhã do tội lỗi mà họ đã từng làm từ việc vô đức tin và các tội lỗi khác.');
INSERT INTO "vn_mokhtasar" VALUES(41,18,'41:18','Và TA đã giải cứu những người tin tưởng vào Allah và các Sứ Giả của Ngài cũng như họ là những người đã từng kính sợ Allah với sự tuân theo những điều Ngài ra lệnh và tránh xa những điều Ngài ngăn cấm. TA đã cứu sống họ thoát khỏi sự trừng phạt đã giáng xuống cho dân chúng của họ.');
INSERT INTO "vn_mokhtasar" VALUES(41,19,'41:19','Và vào Ngày Allah sẽ tụ tập hết kẻ thù của Ngài rồi dẫn vào Hỏa Ngục, các cảnh vệ của Hỏa Ngục canh giữ họ từ người đầu tiên đến người cuối cùng rồi họ sẽ không bao giờ chốn thoát khỏi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(41,20,'41:20','Mãi cho đến khi họ được đưa tới Hỏa Ngục thì họ muốn phủ nhận tất cả những việc tội lỗi mà họ đã từng làm ở trên trần gian, nhưng đôi tai, cặp mắt và làn da của họ lại làm chứng chống lại họ về sự vô đức tin và các tội lỗi khác mà họ đã từng làm ở trần gian');
INSERT INTO "vn_mokhtasar" VALUES(41,21,'41:21','Và đám người vô đức tin sẽ bảo lớp da của mình rằng: Tại sao tụi bây làm chứng chống lại bọn ta về những điều mà bọn ta đã làm ở trên trần gian ?! Lớp da sẽ đáp: Allah khiến chúng tôi nói giống như Ngài khiến cho mọi vật đều nói chuyện và Ngài đã tạo ra quí vị lần đầu khi đã ở trên trần gian. Và quý vị sẽ được đưa về gặp Ngài duy nhất vào Đời Sau để thanh toán cho việc thưởng và phạt');
INSERT INTO "vn_mokhtasar" VALUES(41,22,'41:22','Và quý vị đã không chịu tìm cách che dấu đi sự phạm tội của bản thân để đôi tai, cặp mắt và làn da của quý vị không thể làm chứng chống lại quý vị được. Đó là hậu quả việc quý vị không hề tin tưởng về sự thanh toán thưởng phạt sau khi chết, song quý vị nghĩ rằng Allah Hiển Vinh không biết gì nhiều về những điều mà quý vị đã từng làm nên quí vị nghĩ rằng mình đã che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(41,23,'41:23','Và với ý nghĩ tồi tệ của quí vị về Thượng Đế của quý vị nên đã bị Ngài tiêu diệt quý vị. Bởi lý do đó quý vị đã trở thành một trong những kẻ thua thiệt, mất mát ở trên đời này lẫn Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(41,24,'41:24','Bởi thế, nếu họ có kiên nhẫn chịu đựng về đôi tai, cặp mắt và làn da làm chứng chống lại họ hay không thì Hỏa Ngục vẫn là nơi cư ngụ của họ. Và cho dù có cầu xin để xóa bỏ hình phạt và sự hài lòng của Allah dành cho họ thì lời cầu xin của họ sẽ không chiếm được sự chấp nhận của Ngài cũng như không bao giờ vào được Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(41,25,'41:25','Và TA đã chuẩn bị cho những kẻ vô đức tin đám bạn thân, kết họ thành đôi bạn đồng hành với tên Shaytan có nhiệm vụ làm cho họ sai mê với những việc làm xấu xa ở trên trần gian cũng như làm cho chúng sai đắm với những lời dụ dỗ ngon ngọt để không tin tưởng vào những sự việc của Đời Sau. Bởi thế, họ quên hẳn đi và không chuẩn bị cho Đời Sau. Và lời hứa hứa xử phạt bọn họ đều xác nhận đúng như các cộng đồng của loài Jinn và loài người trước đây. Quả thật, họ là những kẻ sẽ thua cuộc đánh mất đi bản thân mình và gia đình của minh vào Ngày Phục Sinh sẽ bị tống cổ vào Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(41,26,'41:26','Và những kẻ không tin tưởng khuyên bảo nhau khi họ bất lực đối mặt với bằng chứng thép này thì họ bảo: Các ngươi chớ nghe Qur''an này khi Nó được xướng lên bởi Muhammad cũng như không được quy phục Y, ngược lại hãy gây náo động ồn ào cùng với tiếng la lét dữ dội của các ngươi khi Y xướng Nó lên, để may ra các ngươi giành được chiến thắng, và Y sẽ từ bỏ việc đọc Nó lẫn việc mời gọi. Có thế chúng ta mới được yên thân.');
INSERT INTO "vn_mokhtasar" VALUES(41,27,'41:27','Rồi đây TA sẽ cho những kẻ phủ nhận Allah cũng như những kẻ đã cho rằng các Sứ Giả của Ngài nói dối nếm sự trừng phạt khủng khiếp nhất vào Ngày Phục Sinh cũng như TA sẽ trừng phạt họ vô cùng tàn tệ vì đại tội Shirk mà họ đã từng tổ hợp với Allah cùng với các tội lỗi khác của họ.');
INSERT INTO "vn_mokhtasar" VALUES(41,28,'41:28','Với sự trừng phạt đã đề cặp chính là để dành cho những kẻ thù của Allah, đám người đã phủ nhận Ngài cũng như những kẻ đã cho rằng các Sứ Giả của Ngài nói dối: Họ sẽ ở trong Hỏa Ngục vĩnh viễn không có lối thoát, trừng phạt họ vì đã phủ nhận các dấu hiệu của Allah cũng như họ không có đức tin Iman vào Nó khi Nó rất rõ ràng và là một bằng chứng xác thực nữa.');
INSERT INTO "vn_mokhtasar" VALUES(41,29,'41:29','Và những kẻ đã phủ nhận Allah cũng như những kẻ đã cho rằng các Sứ Giả của Ngài nói dối sẽ thưa: Lạy Thượng Đế của bầy tôi ! Xin Ngài hãy vạch mặt những tên Jinn và đám người đã dắt bầy tôi đi lạc: Iblis chính là tên đã vạch ra Shirk và sự phủ nhận để kêu gọi nhân loại đi theo sự lầm lạc của hắn. Còn con cháu Adam - tức con người - là những tên tạo ra sự hận thù và gây ra đổ máu. Do đó, cầu xin Ngài để cho bầy tôi chà đạp hai chúng nó trong Hỏa Ngục dưới bàn chân của bầy tôi, khiến cho chúng trở thành một trong những tên hèn hạ đê tiện nhất và chịu sự trừng phạt kinh khủng nhất.');
INSERT INTO "vn_mokhtasar" VALUES(41,30,'41:30','Quả thật, những ai nói: Thượng Đế của chúng tôi là Allah, chúng tôi không có Thượng Đế nào ngoài Ngài cả, và ngay thẳng trong việc tuân theo những điều Ngài ra lệnh cũng như tránh xa điều Ngài ngăn cấm thì các Thiên Thần sẽ hạ giới động viên họ lúc họ hấp hối mà bảo: Các ngươi chớ lo sợ về cái chết và những gì ở sau nó, cũng như chớ buồn phiền về những gì mà quý vị bỏ lại ở trần gian này. Quí vị hãy vui lên với tin mừng về Thiên Đàng mà quý vị đã được hứa ở trên trần gian vì có đức tin Iman vào Allah lẫn việc làm ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(41,31,'41:31','Chúng tôi là những người bạn đồng hành của quý vị ở đời này lẫn Đời Sau. Quả thực, chúng tôi sẽ hướng dẫn và bảo vệ quý vị liên tục không ngừng cũng như quý vị ở trong Thiên Đàng sẽ được thưởng thức bất cứ món ăn vật lạ nào mà bản thân của quý vị thèm muốn và trong Thiên Đàng quý vị sẽ được ao ước bất cứ món gì mà quý vị yêu cầu.');
INSERT INTO "vn_mokhtasar" VALUES(41,32,'41:32','Một sự hậu đãi được chuẩn bị cho các ngươi từ Thượng Đế Đấng Hằng Tha Thứ cho các tội lỗi đối với ai biết sám hối trong đám bầy tôi của Ngài, Đấng Rất Mực Khoan Dung với họ');
INSERT INTO "vn_mokhtasar" VALUES(41,33,'41:33','Và không một ai tốt hơn người mời gọi nhân loại đến với Allah duy nhất, họ năng nổ tuân thủ đúng với luật pháp của Ngài cũng như làm những việc ngoan đạo để được Ngài hài lòng và còn tự xưng: Quả thật, tôi là một người Muslim thuần phục Allah. Bởi thế, ai làm tất cả điều này thì y là người tốt nhất trong nhân loại xét về mặt lời nói.');
INSERT INTO "vn_mokhtasar" VALUES(41,34,'41:34','Và sẽ không bao giờ ngang bằng với nhau giữa việc làm thiện lẫn việc làm ngoan đạo được Allah hài lòng với việc làm xấu xa lẫn việc làm tội lỗi khiến Ngài phẫn nộ. Ngươi hãy dùng đức tính độ lượng mà tha thứ cho những ai làm tổn hại đến Ngươi, rồi Ngươi sẽ thấy kẻ thù của Ngươi trước đó - khi Ngươi đã dùng sự độ lượng để xóa bỏ sự hận thù - sẽ trở thành người bạn thân tình của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(41,35,'41:35','Và không ai được phú cho đức tính tốt đẹp đó ngoại trừ những ai thật sự kiên nhẫn chịu đựng điều rủi ro xảy ra đến y cũng như không ai được phú cho cái đó ngoại trừ y có phần số lớn, bởi có rất nhiều điều tốt và lợi ích to lớn');
INSERT INTO "vn_mokhtasar" VALUES(41,36,'41:36','Và nếu Shaytan quấy nhiễu Ngươi vào bất cứ giờ giấc nào với những điều sai trái thì hãy bám chắc vào Allah cũng như dựa dẫm vào Ngài. Quả thật, Ngài là Đấng Hằng Nghe khi Ngươi cầu xin đến Ngài, Đấng Hằng Biết mọi hoàn cảnh của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(41,37,'41:37','Và trong những dấu hiệu của Allah được chứng minh qua sự hùng vĩ của Ngài và thuộc tính duy nhất của Ngài là ban đêm và ban ngày luân chuyển nối tiếp nhau và mặt trời và mặt trăng cũng thế. Thế nên, các ngươi - hỡi con người - chớ phủ phục quỳ lạy mặt trời hay mặt trăng mà hãy phủ phục quỳ lại Allah duy nhất, Đấng đã tạo hóa chúng, nếu các ngươi chỉ thờ phụng riêng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(41,38,'41:38','Nhưng nếu chúng ngạo mạn và quay lưng bỏ đi cũng như không phủ phục quỳ lạy Allah, Đấng Tạo Hóa thì những Thiên Thần ở quanh Allah không ngừng tán dương Ngài cả ban đêm lẫn ban ngày và không bao giờ mỏi mệt trong việc thờ phượng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(41,39,'41:39','Và trong các dấu hiệu của Ngài được chứng minh qua sự hùng vĩ và thuộc tính duy nhất của Ngài cũng như về quyền năng hồi sinh của Ngài rằng Ngươi đã thấy đất đai khô cằn không một cây cỏ nào sống sót trên nó cả. Nhưng khi TA tưới nước mưa lên thì nó lại đâm chồi nảy lộc đơm hoa kết trái. Quả thật, Ngài làm cho đất đai này sống lại với nhiều thảo mộc trên nó, chắc chắn sẽ làm cho người chết sống lại cũng như phục sinh chúng để thanh toán cho việc thưởng và phạt. Quả thật, Ngài có quyền chi phối trên tất cả vạn vật, không ai có thể làm Ngài bất lực trước việc tái sinh đất đai khô cằn sau khi đã chết và làm người chết sống lại cũng như phục sinh họ đang nằm ở dưới mộ.');
INSERT INTO "vn_mokhtasar" VALUES(41,40,'41:40','Những ai bài bác về sự thật trong các dấu hiệu của Allah bằng cách khước từ, phủ nhận và sửa đổi làm sai lạc Nó thì sẽ không thể giấu giếm việc làm của họ khỏi TA được, vì TA biết rất rõ về họ. Thế nên ai tốt hơn, kẻ bị tống cổ vào Hỏa Ngục hay là kẻ bước ra an toàn không bị trừng phạt vào Ngày Phục Sinh ? Hỡi nhân loại - hãy làm những điều tốt hoặc xấu mà các ngươi muốn. Thật sự Ngài đã trình bày rất rõ cho các ngươi biết được điều tốt và điều xấu rồi. Quả thật, Allah luôn thấy những điều mà các ngươi làm, không việc làm nào của các ngươi có thể giấu giếm khỏi Ngài được.');
INSERT INTO "vn_mokhtasar" VALUES(41,41,'41:41','Quả thật,những ai phủ nhận Kinh Qur''an từ nơi Allah đến với họ thì sẽ bị trừng phạt vào Ngày Phục Sinh. Và quả thật, Nó là một Kinh sách đầy quyền năng quý giá của Ngài, không ai có thể chỉnh sửa hoặc thay đổi khi Ngài đã đã bảo quản Nó.');
INSERT INTO "vn_mokhtasar" VALUES(41,42,'41:42','Không có một điều ngụy tạo nào có thể xâm nhập vào Qur''an cho được dù từ đằng trước hay đằng sau hồng để thêm bớt hoặc thay đổi và chỉnh sửa. Bởi vì, Nó được ban xuống từ Đấng Rất Mực Sáng Suốt trong tạo hóa của Ngài, trong quyền năng của Ngài, trong luật pháp của Ngài, Đấng Rất Đáng ca tụng trong mọi hoàn cảnh.');
INSERT INTO "vn_mokhtasar" VALUES(41,43,'41:43','Những điều mà họ nói cho Ngươi - hỡi Thiên Sứ - cho rằng Ngươi dối trá chẳng khác gì với điều đã được nói cho các Sứ Giả thời trước Ngươi, bởi thế Ngươi hãy kiên nhẫn chịu đựng. Quả thật,Thượng Đế của Ngươi nắm trọn sự tha thứ cho những ai biết quay về sám hối trong đám bầy tôi của Ngài cũng như nắm trọn sự trừng phạt rất đau đớn cho những ai thích chìm đắm trong tội lỗi nhưng không biết quay về sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(41,44,'41:44','Và nếu TA ban xuống Kinh Qur''an này không phải là ngôn ngữ Ả-rập thì những kẻ Ả-rập ngoại đạo trong bọn họ sẽ nói rằng: Tại sao các câu Kinh của Nó không được giải thích từng chi tiết theo ngôn ngữ Ả-rập để chúng tôi hiểu được Nó. Sao ! một Kinh sách Qur''an không phải bằng ngôn ngữ Ả-rập à, còn Sứ Giả được cử phái đến lại là người Ả-rập ư? Hỡi Thiên Sứ - hãy bảo chúng: Qur''an - mà những ai tin tưởng vào Allah và các Sứ Giả của Ngài - Nó sẽ hướng dẫn đi đến chân lý và là một phương thuốc chữa lành những căn bệnh trong lòng có sự ngu muội và nhiều bệnh tật khác cho những ai làm theo Nó. Ngược lại, những ai không tin tưởng vào Allah thì đôi tai của chúng sẽ điếc, và Nó là một sự mù lòa đối với họ khiến họ không hiểu biết về Nó. Với những người được mô tả với đặc điểm đó thì giống như những ai được kêu gọi từ một chỗ rất xa xôi. Chính vì thế, làm sao mà chúng nghe được tiếng gọi đó !');
INSERT INTO "vn_mokhtasar" VALUES(41,45,'41:45','Và thật sự, TA đã ban cho Musa một Kinh Tawrah nhưng lại có sự tranh luận trong đó. Một trong số họ thì tin tưởng vào Nó còn một số thì phủ nhận Nó. Và nếu không vì một lời hứa của Allah rằng Ngài sẽ phân xử giữa đám bầy tôi vào Ngày Phục Sinh về những điều mà họ thường tranh luận trong Nó là Ngài xét xử ngay tức khắc giữa bọn họ trong Tawrah để làm rõ đâu là chân lý và đâu là giả tạo, rồi Ngài sẽ ca ngợi điều chân lý và hạ nhục điều giả tạọ. Nhưng kẻ không tin tưởng rất băn khoăn mà còn hay nghi ngờ về Nó.');
INSERT INTO "vn_mokhtasar" VALUES(41,46,'41:46','Ai thực hiện việc làm ngoan đạo thì việc thực hiện đó sẽ được lợi cho bản thân y, và Allah không hưởng lợi việc làm tốt từ bất cứ ai. Ngược lại, ai làm xấu xa thì sẽ bất lợi cho bản thân y, và Allah không bị thiệt hại một chút nào bởi tội lỗi của bất cứ ai trong sự tạo hóa của Ngài. Và Ngài sẽ thưởng phạt tất cả với những điều thích đáng. Bởi vì Thượng Đế của Ngươi - hỡi Thiên Sứ - không bao giờ bất công với bầy tôi của Ngài. Do đó, không có công đức nào bị giảm đi và cũng không có tội lỗi nào bị tăng thêm.');
INSERT INTO "vn_mokhtasar" VALUES(41,47,'41:47','Sự hiểu biết về Giờ Tận Thế chỉ được qui về Allah duy nhất mà thôi, chỉ Ngài mới biết khi nào Nó sẽ diễn ra, ngoài ra không một ai biết về điều đó cả. Cũng như không có bất cứ một loại quả nào có thể đâm chồi khỏi vỏ của nó được khi Ngài muốn giữ nó lại, và cũng không có một người phụ nữ mang thai nào và không có một đứa trẻ nào được sinh ra mà Ngài không biết, không gì có thể qua mặt được Ngài. Và vào Ngày mà Allah sẽ kêu gọi những kẻ đa thần luôn thờ phượng bụt tượng chung với Allah, rồi Ngài sẽ hỏi họ về các thần linh của họ với sự khiển trách: Đâu là những thần linh mà các ngươi luôn khẳng định rằng họ là đối tác của TA đâu? Những kẻ thờ đa thần đáp: Bầy tôi đảm bảo trước mặt Ngài, không một ai trong bầy tôi xác nhận Ngài có đối tác cả');
INSERT INTO "vn_mokhtasar" VALUES(41,48,'41:48','Và những bụt tượng mà những kẻ đa thần đã thờ phượng đã bỏ mặt họ mà đi, và họ sẽ nhận ra rằng họ sẽ không tày nào thoát khỏi hình phạt của Allah được');
INSERT INTO "vn_mokhtasar" VALUES(41,49,'41:49','Con người không ngừng cầu xin về sức khỏe, tiền bạc, con cái, và những hồng ân khác cho bản thân mình. Nhưng khi gặp phải nghèo khó, ốm đau bệnh tật, hay các thứ tương tự khác thì đa số nhân loại lại tuyệt vọng và buông xuôi trước sự khoan dung của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(41,50,'41:50','Và khi TA cho họ nếm được hương vị của sự giàu sang, được sức khỏe, và hưng thịnh sau khi họ đã trải qua thử thách của bệnh tật thì chắc chắn họ sẽ nói: Đó là do tôi, vì tôi có khả năng cũng như tôi rất xứng đáng có những thứ tốt đẹp, phú quý giàu sang, và hẳn là Giờ Tận Thế sẽ không đến, và cho dù Giờ Tận Thế có đến đi chăng thì Allah cũng sẽ ban cho tôi sự giàu sang phú quý. Tương tự như tôi đã có được ở trên trần gian bằng chính công sức của tôi nên ở Ngày Sau tôi cũng sẽ được như vậy. Nhưng chắc chắn TA sẽ sớm cho những kẻ phủ nhận Allah biết được những điều mà họ đã phủ nhận và tội lỗi, và TA sẽ cho họ nếm một hình phạt vô cùng đau đớn và khủng khiếp');
INSERT INTO "vn_mokhtasar" VALUES(41,51,'41:51','Và khi TA ban ân huệ cho nhân loại bằng việc ban cho y sức khỏe, cho y được hưng thịnh, và các thứ tương tự khác thì con người lại xao lãng với việc tụng niệm Allah và phục tùng Ngài, chẳng những thế y còn kiêu ngạo tự cao trước ân huệ của Allah. Rồi khi lâm vào bệnh tật, nghèo khổ, và các thứ tương tự thì nhân loại lại cầu xin Allah liên tục, y kể lệ than khóc về những gì y mắc phải để Ngài giải cứu y, nhưng y lại không biết ơn Thượng Đế của y khi được ban ân huệ cũng như không nhẫn nại trước thử thách đã an bày cho y.');
INSERT INTO "vn_mokhtasar" VALUES(41,52,'41:52','Ngươi - hỡi Thiên Sứ - hãy bảo những kẻ đa thần dối trá: Các vị hãy cho Ta biết, khi mà Qur''an là do Allah ban xuống, sau đó các vị lại phủ nhận Nó cũng như không tin tưởng vào Nó thì tình trạng của các vị sẽ trở nên như thế nào!? Rồi ai sẽ là người lầm lạc hơn đám người một mực chống đối chân lý sau khi biết rõ ràng các bằng chứng sắt thép của Nó?');
INSERT INTO "vn_mokhtasar" VALUES(41,53,'41:53','TA sẽ sớm cho những kẻ vô đức tin Quraish thấy các dấu hiệu của TA trong vũ trụ từ việc Allah mở lối cho những người Muslim, và TA sẽ cho họ thấy các dấu hiệu của TA ngay trên bản thân họ bằng việc giải phóng Makkah, cho đến khi Qur''an làm cho họ thấy rõ không còn chút nghi ngờ gì và khẳng định rằng Qur''an là chân lý không thể nào chối cải. Chẳng lẻ những kẻ đa thần không thấy đủ hay sao khi đích thân Allah xác nhận rằng Qur''an là chân lý và Nó là của Ngài!? Và ai có thể có lời xác nhận vĩ đại hơn Allah!? Rồi nếu họ muốn chân lý nhưng họ lại không tiếp nhận lời xác nhận của Thượng Đế của họ.');
INSERT INTO "vn_mokhtasar" VALUES(41,54,'41:54','Phải chăng những kẻ đa thần hãy còn nghi ngờ về cuộc trình diện trước Thượng Đế của họ vào Ngày Phục Sinh ư, vì họ vẫn một mực phủ nhận Sự Phục Sinh nên họ không tin vào ngày Tận Thế. Vì thế họ không hề chuẩn bị cho Ngày đó bằng những việc làm ngoan đạo. Há không phải Allah không ngừng bao trùm tất cả mọi thứ bàng kiến thức và quyền lực của Ngài ư?');
INSERT INTO "vn_mokhtasar" VALUES(42,1,'42:1','Ha. Mi-m. ''Ai-n. Si-n. Qa-f. Đã được giải thích ở câu đầu tiên của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(42,2,'42:2','Ha. Mi-m. ''Ai-n. Si-n. Qa-f. Đã được giải thích ở câu đầu tiên của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(42,3,'42:3','Giống như sự mặc khải đã thiên khải cho Ngươi hỡi Muhammad, nó đã được mặc khải cho các vị Sứ Giả của Ngài thời trước Ngươi. Allah là Đấng Toàn Năng trong việc xử lý những kẻ thù của Ngài, Đấng Chí Minh trong việc sắp xếp và tạo hóa.');
INSERT INTO "vn_mokhtasar" VALUES(42,4,'42:4','Mọi vật trong các tầng trời và vạn vật dưới mặt đất đều do Allah duy nhất tạo hóa, cai trị và sắp xếp. Và Ngài là Đấng Tối Cao từ chính bản thân của Ngài, bằng chính năng lực của Ngài, và bằng chính quyền năng của Ngài. Đấng Vĩ Đại trong chính bản thân Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(42,5,'42:5','Và với sự vĩ đại của Ngài, Đấng Quang Vinh đãkhiến các tầng trời tuy rất hùng vĩ, tuy rất cao nhưng gần như bị tan vỡ rơi vụng trên mặt đất. Và các Thiên Thần không ngừng ca ngợi Thượng Đế của Họ, Họ tôn vinh Ngài bằng những lời tán dương thành tâm và tôn kính. Và Họ cầu xin sự tha thứ từ Allah cho những ai dưới mặt đất, vì quả thật Ngài là Đấng Hằng Tha Thứ tội lỗi cho ai biết xám hối từ đám bề tôi của Ngài, Ngài là Đấng Khoan Dung cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(42,6,'42:6','Và những ai nhận các bụt tượng thay vì Allah để làm kẻ bảo hộ và để thờ phượng, thì Allah luôn giám sát họ và Ngài ghi lại những việc làm của họ và sẽ thưởng phạt họ. Còn Ngươi - hỡi Thiên Sứ - không phải là người chịu trách nhiệm cho những việc làm của họ, nên Ngươi sẽ không được chấp vấn những gì họ làm, chẳng qua Ngươi chỉ là người truyền tin.');
INSERT INTO "vn_mokhtasar" VALUES(42,7,'42:7','Và như những gì TA đã mặc khải cho các Sứ Giả trước Ngươi - hỡi Thiên Sứ - nay TA mặc khải cho Ngươi Thiên Kinh Qur''an bằng ngôn ngữ Ả-rập để Ngươi cảnh báo người dân Makkah và các thị trấn lân cận thuộc dân Ả-rập, rồi sau đó là toàn thể nhân loại. Để con người biết sợ hãi Ngày Phục Sinh, Ngày mà Allah sẽ tập hợp lại tất cả nhân loại từ thời đại đầu tiên cho đến thời đại cuối cùng trong cùng một thời điểm để xét xử và thưởng phạt, không có gì để nghi ngờ cho việc thiết lập Ngày Phục Sinh đó. Và trong Ngày đó nhân loại sẽ được chia thành hai nhóm: Một nhóm của Thiên Đàng và họ là những người có đức tin, nhóm còn lại là của Hỏa Ngục và họ là những người phủ nhận đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(42,8,'42:8','Và nếu muốn Allah đã tập hợp nhân loại thành một cộng đồng duy nhất theo tôn giáo Islam, và cho tất cả nhân loại vào Thiên Đàng. Thế nhưng, với sự sáng suốt của Ngài thì Ngài muốn ai vào Islam theo ý Ngài cũng như cho người đó vào Thiên Đàng. Còn những kẻ bất công với bản thân họ bằng sự phủ nhận và tội lỗi sẽ không có một ai che chở lẫn giải cứu giúp họ thoát khỏi sự trừng phạt của Allah');
INSERT INTO "vn_mokhtasar" VALUES(42,9,'42:9','Nhưng những kẻ đa thần đã nhận kẻ khác ngoài Allah làm đấng bảo hộ để bảo vệ họ. Trong khi Allah mới là Đấng Bảo Hộ thật sự, và những kẻ khác ngoài Ngài họ chả giúp ích hay gây hại được gì. Ngài là Đấng làm cho người chết sống lại bằng cách phục sinh họ trở lại để xét xử và thưởng phạt, và điều đó không làm Ngài bất lực chút nào, thật Vinh Quang thay Ngài!.');
INSERT INTO "vn_mokhtasar" VALUES(42,10,'42:10','Và hỡi nhân loại! Những gì các ngươi bất đồng với nhau về nguồn gốc của tôn giáo hay về phân nhánh của nó thì tất cả những điều đó đều được trình lên Allah để phân xử. Các ngươi hãy trở về với Kinh Sách của Ngài tức Qur''an hoặc Sunnah của Thiên Sứ của Ngài - cầu xin sự bình an đến Người -. Với những thuộc tính được miêu tả chính là Thượng Đế của Ta, Đấng duy nhất mà Ta phó thác tất cả mọi việc của Ta cho Ngài, và với Ngài Ta sẽ quay về sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(42,11,'42:11','Allah là Đấng Tạo Hóa các tầng trời và trái đất mà trước đó chưa hề có giống như vậy, tạo hóa ra các ngươi rồi từ các ngươi Ngài tạo hóa ra những người vợ cho các ngươi, và Ngài cũng tạo hóa cho các ngươi những con lạc đà, những con bò, và những con dê cừu đực và cái để chúng tăng số lượng cho các ngươi sử dụng, Ngài tạo hóa các ngươi với việc ban cho các ngươi những người vợ bằng việc kết hôn, rồi Ngài ban sự sinh tồn cho các ngươi với việc tạo hóa các loài vật nuôi để các ngươi lấy thịt và sữa từ chúng. Không có bất cứ vật thể nào trong vũ trụ giống như Ngài, trong khi Ngài là Đấng Hằng Nghe mọi lời nói của đám bề tôi của Ngài, Đấng Hằng Thấy mọi việc làm của họ, không gì có thể qua mặt được Ngài. Và Ngài sẽ thưởng phạt cho họ về những việc làm của họ. Quả thật nếu tốt thì được tốt còn nếu xấu thì được xấu');
INSERT INTO "vn_mokhtasar" VALUES(42,12,'42:12','Chỉ duy nhất Ngài nắm giữ chìa khóa kho tàng của các tầng trời và đất, Ngài nới rộng bổng lộc cho ai mà Ngài muốn từ đám bề tôi của Ngài để Ngài kiểm tra xem hắn có biết tạ ơn hay là phủ nhận? Và Ngài thu hẹp bổng lộc cho ai mà Ngài muốn để Ngài thử thách xem hắn có biết nhẫn nại hay bất mãn với sự an bày của Allah? Quả thật, Ngài biết rất rõ tất cả mọi thứ, không gì có thể che giấu được Ngài từ những thứ cải thiện của đám bầy tôi của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(42,13,'42:13','Allah đã ban hành tôn giáo cho các ngươi giống như Ngài đã ban cho Nuh với sứ mạng là truyền đạt thông điệp và thi hành nó, và những gì mà Ngài mặc khải cho Ngươi - hỡi Thiên Sứ - cũng như các giáo luật được ban cho Ngươi cũng tương tự với những gì mà Ngài đã ra lệnh cho Ibrahim, cho Musa, và cho Ysa truyền đạt thông điệp và thi hành nó. Nói tóm lại về là các ngươi phải gầy dựng tôn giáo, xóa bỏ mọi ngăn cản, mọi chia rẻ cộng đồng. Đối với người đa thần, điều mà Ngươi kêu gọi họ theo thuyết Tawhid và từ bỏ mọi hình thức thờ phượng ngoài Allah là điều rất kinh khủng. Tuy nhiên Allah tuyển chọn những ai mà Ngài muốn trong đám bề tôi của Ngài, rồi Ngài chấp thuận y để thờ phượng và tuân mệnh lệnh Ngài, cũng như Ngài sẽ dẫn dắt y trở lại sám hối về những tội lỗi mà y đã gây ra.');
INSERT INTO "vn_mokhtasar" VALUES(42,14,'42:14','Và những kẻ phủ nhận đức tin và những kẻ thờ đa thần đã chia rẻ nhau sau khi họ bị đánh bại bởi các bằng chứng về sứ mạng của Muhammad - cầu xin sự bình an đến Người - được cử phái xuống là thật, và họ trở nên chia rẻ là do sự thối nát và bất công của họ. Nếu như Allah không tiền định việc trừng phạt bọn họ vào Ngày Tận Thế là Ngài đã nhanh chóng xử phạt họ vì tội đức tin và tội phủ nhận sứ mạng của các Sứ giả của Ngài. Quả thật, những người thừa hưởng Kinh Tawrah thuộc người Do Thái, và những người thừa hưởng Kinh Injil thuộc người Thiên Chúa sau các thế hệ trước họ, và sau đó là những người đa thần, họ đều nghi ngờ về Thiên Kinh Qur''an được ban xuống cho Muhammad và họ còn phủ nhận Nó');
INSERT INTO "vn_mokhtasar" VALUES(42,15,'42:15','Ngươi hãy kêu gọi nhân loại đến với tôn giáo ngay thẳng này, và riêng Ngươi hãy trụ vững trên tôn giáo Islam như Allah đã ra lệnh, và chớ có đi theo những kẻ hư đốn. Ngươi hãy bảo với những kẻ cãi bướng rằng: Ta hoàn toàn tin tưởng vào Allah và hoàn toàn tin tưởng vào Kinh Sách mà Allah đã ban xuống cho các Thiên Sứ của Ngài, và Allah đã ra lệnh cho Ta phải phân xử giữa các ngươi một cách công bằng. Allah là Đấng mà Ta chỉ được thờ phượng, Ngài chính là Thượng Đế của Ta và cũng là Thượng Đế của tất cả các ngươi, Ta có những việc làm của Ta dù là tốt hay không tốt đi chăng nữa, và các ngươi có những việc làm của các ngươi dù là tốt hay xấu, không có sự tranh luận nào nữa giữa Ta và các ngươi sau khi bằng chứng đã được trình bày rõ ràng. Alllah sẽ tập hợp tất cả chúng ta lại vào Ngày Phục Sinh. Rồi Ngài sẽ thưởng phạt chúng ta bằng những điều xứng đáng. Rồi khi đó Ngài sẽ làm rõ đâu là sự thật và đâu là dối trá, đâu là chân lý và đâu là sự ngụy tạo');
INSERT INTO "vn_mokhtasar" VALUES(42,16,'42:16','Và những ai thường hay dựa vào những bằng chứng hư cấu để tranh cãi về tôn giáo được ban xuống cho Muhummad - cầu xin sự bình an đến Người - sau khi tôn giáo đã được nhân loại công nhận nó. Vơi những bằng chứng mà đám người tranh cãi trình bày đều vô ích, chẳng là gì đối với Allah và với những người có đức tin, chẳng gây ảnh hưởng gì. Ngược lại họ chỉ nhận được cơn giận dữ từ nơi Allah do bản tính vô đức tin và chối bỏ chân lý, rồi hình phạt khủng khiếp đang chờ đợi họ vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(42,17,'42:17','Allah là Đấng ban Qur''an xuống bằng chân lý mà trong Nó không gì phải nghi ngờ, cũng như Ngài ban xuống nền công lý để phân xử nhân loại theo lẽ công bằng. Và có thể giờ tận thế mà kẻ phủ nhận không tin vào nó sẽ đến rất gần kề và tất nhiên mọi thức đều báo biết rằng Ngày Tận Thế đang tiến gần kề.');
INSERT INTO "vn_mokhtasar" VALUES(42,18,'42:18','Những kẻ không có đức tin họ mong muốn Giờ Tận Thế cho mau đến, bởi vì họ vốn không tin vào việc xét xử cũng như không tin vào việc thưởng phạt. Còn những người tin tưởng vào Allah, họ luôn sợ Giờ Tận Thế, họ sợ cho số phận của họ vào Ngày đó, và họ biết rằng cái chết và Ngày Tận Thế chắc chắn là sự thật không có gì để nghi ngờ cả. Những kẻ thường hay tranh cãi về Giờ Tận Thế luôn tranh chấp Nó, và họ luôn nghi ngờ về sự xuất hiện của Nó, vì vậy họ bị lầm lạc xa với sự thật.');
INSERT INTO "vn_mokhtasar" VALUES(42,19,'42:19','Allah luôn tử tế với đám bầy tôi của Ngài, Ngài ban phát bổng lộc cho người nào mà Ngài muốn, và Ngài nới rộng bổng lộc hay thu hẹp nó lại đối với người nào theo ý muốn của Ngài. Vì luôn có sự khác biệt, và Ngài là Đấng Toàn Lực không ai có thể vượt trội hơn Ngài, Đấng Toàn Năng mà trừng trị từ những kẻ thù của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(42,20,'42:20','Ai mong muốn phần thưởng ở Đời Sau bởi việc hành đạo của y đã làm thì y sẽ được nhân phần thưởng lên bội phần, cứ một việc làm tốt thì phần thưởng sẽ tương đương với mười điều tốt đó cho đến bảy trăm lần và còn hơn thế nữa. Còn ai chỉ muốn phần thưởng ở trần gian này thì Ngài sẽ ban cho theo năng lực của y ở trần gian, và y sẽ không có bất cứ phần thưởng nào nữa ở Ngày Sau từ những điều y đã làm ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(42,21,'42:21','Hay đám người đa thần này có thần linh khác ngoài Allah đã thiết lập cho họ một tôn giáo mà Allah vốn không hề cho phép phạm Shirk với Ngài, cấm điều Ngài cho phép và cho phép thứ Ngài đã cấm ư? Và nếu như Allah không đưa ra hạn định cho việc phân xử giữa hai nhóm đối lập, và quả thật Ngài trì hoãn việc phân xử họ, và chắc chắn những kẻ lầm lỗi từ chính bản thân họ vì đại tội Shirk với Allah và tội lỗi khác sẽ có hình phạt đang chờ đợi họ vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(42,22,'42:22','Ngươi - hỡi Thiên Sứ - sẽ thấy những kẻ mà bản thân họ làm điều sai quấy bởi tội Shirk và nhiều tội lỗi khác lo sợ về sự trừng phạt với bao tội lỗi mà họ đã tích lũy, và một hình phạt giành cho họ là điều không thể tránh khỏi, rồi họ sợ hãi và sám hối nhưng ở thời khắc đó đã là vô ích. Còn với những ai tin tưởng Allah, vào các Thiên Sứ của Ngài, và năng nổ hành đạo hoàn toàn trái ngược với những kẻ lầm lỗi; họ sẽ được ở trong những ngôi vườn của Thiên Đàng mà họ mong muốn, cũng như họ có được những thứ mà họ mong muốn từ Thượng Đế của họ về những niềm hạnh phúc mà không bao giờ bị gián đoạn. Đó là một hồng ân rất lớn mà không có hồng ân nào so sánh được');
INSERT INTO "vn_mokhtasar" VALUES(42,23,'42:23','Đó là một tin mừng to lớn mà Allah báo tin thông qua sứ mạng của Thiên Sứ của Ngài dành cho những ai tin tưởng vào Allah, vào các Thiên Sứ của Ngài, và năng nổ hành đạo. Ngươi hãy bảo họ - hỡi Thiên Sứ -: Ta không đòi hỏi các ngươi bất cứ phần thưởng nào cho việc truyền đạt chân lý ngoại trừ một phần thưởng đơn thuần duy nhất mà lại có lợi cho các ngươi, đó là các ngươi hãy thương yêu Ta vì mối quan hệ họ hàng ruột thịt giữa chúng ta. Và ai làm một việc tốt thì sẽ được nhân phần thưởng của y lên, vì một việc làm tốt phần thưởng của nó sẽ được nhân lên gấp mười lần. Quả thật, Allah hằng tha thứ tội lỗi cho đám bầy tôi của Ngài khi y biết quay về sám hối, hằng ghi công những việc làm ngoan đạo mà họ làm vì muốn nhận được sự hài lòng của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(42,24,'42:24','Nhưng những kẻ đa thần đã tuyên bố rằng Muhammad - cầu xin sự bình an đến Người - đã ngụy tạo ra Thiên Kinh Qur''an này rồi gán Nó cho Thượng Đế của Y. Allah liền bác bỏ lời vu khống đó của họ: Nếu như Ngươi dám ngụy tạo và dối trá là TA đã niêm kín trái tim Ngươi rồi, và TA đã lấy đi sự dối trá, rồi chỉ để lại điều chân lý. Và khi sự việc đã không xảy ra chứng tỏ sự trung thực của Sứ Giả - cầu xin sự bình an đến Người - rằng nó được mặc khải từ Thượng Đế của Y. Quả Thật, Ngài là Đấng Hằng Biết rất rõ những điều nằm trong lòng của đám bầy tôi của Ngài, không gì có thể che giấu được Ngài');
INSERT INTO "vn_mokhtasar" VALUES(42,25,'42:25','Và Ngài là Đấng Quang Vinh chấp nhận sự hối cải của đám bầy tôi của Ngài dù là tội vô đức tin hay bất cứ tội lỗi nào khác khi họ đã sám hối với Ngài, và Ngài sẽ bỏ qua những lỗi lầm mà họ đã mắc phải. Vì Ngài biết rất rõ những gì họ làm, không gì có thể giấu giếm được Ngài về những bất cứ việc làm nào của các ngươi. Và Ngài sẽ thưởng phạt cho các ngươi về những việc làm đó');
INSERT INTO "vn_mokhtasar" VALUES(42,26,'42:26','Và Ngài chấp nhận lời cầu xin của những người tin tưởng vào Allah, vào các Sứ Giả của Ngài, và năng nổ hành đạo. Ngài sẽ tăng thêm thiên lộc cho họ khi họ cầu xin Ngài. Còn những kẻ không tin tưởng vào Allah, vào các Sứ Giả của Ngài thì hình phạt rất khủng khiếp sẽ đang chờ đợi họ vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(42,27,'42:27','Và nếu Allah nới rộng bổng lộc cho tất cả đám bầy tôi của Ngài thì họ sẽ làm điều bất công trên trái đất, cho nên Đấng Hiển Vinh chỉ ban bổng lộc theo định lượng mà Ngài muốn nới rộng hay thu hẹp nó. Quả thật, Ngài là Đấng Am Tường về tình trạng của đám bầy tôi của Ngài, rồi Ngài ban bố và ngăn cấm nó bằng sự sáng suốt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(42,28,'42:28','Và Ngài là Đấng ban nước mưa cho đám bầy tôi của Ngài sau khi họ tuyệt vọng trong cầu xin, rồi Ngài trải rộng cơn mưa trên khắp mặt đất. Và Ngài là Đấng Bảo Hộ cho đám bầy tôi của Ngài, Đấng Đáng Ca Tụng trên tất cả mọi việc.');
INSERT INTO "vn_mokhtasar" VALUES(42,29,'42:29','Và trong các dấu hiệu chứng tỏ quyền năng và thuyết độc tôn của Allah là việc tạo hóa các tầng trời, trái đất, và những tạo vật đáng kinh ngạc được Ngài trải rộng giữa trời và đất. Và Ngài có toàn quyền tập trung tất cả lại để thưởng phạt khi nào Ngài muốn, Ngài không hề bất lực về điều đó cũng như Ngài đã không bất lực khi tạo hóa tất cả lúc ban đầu.');
INSERT INTO "vn_mokhtasar" VALUES(42,30,'42:30','Và những điều bất hạnh xảy ra với các ngươi - hỡi nhân loại - gây thiệt hại cho chính bản thân hoặc tài sản của các ngươi là do đôi bàn tay tội lỗi của các ngươi tạo ra đó. Trong khi đó Ngài đã bỏ qua cho các ngươi rất nhiều lần không tính sổ với các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(42,31,'42:31','Và các ngươi hoàn toàn bất lực thoát khỏi khỏi sự trừng phạt của Thượng Đế các ngươi khi Ngài đã muốn trừng phạt như vậy, và các ngươi sẽ không tìm được vị bảo hộ nào ngoài Ngài và cũng không có vị cứu tinh nào có thể cứu giúp các ngươi thoát khỏi hình phạt khi Ngài đã muốn điều đó cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(42,32,'42:32','Và trong những dấu hiệu của Allah chứng minh quyền năng và thuyết độc tôn của Ngài là duy chuyển các tàu thuyền lưu thông trên biển như những quả núi khổng lồ.');
INSERT INTO "vn_mokhtasar" VALUES(42,33,'42:33','Nếu muốn Allah đã làm ngưng những cơn gió để cho thuyền buồm đứng yên bất động, không thể di chuyển trên biển. Quả thật, việc tạo hóa thuyền buồm và việc lợi dụng sức gió để lưu thông thuyền buồm được nhắc đến là bằng chứng rõ ràng cho thấy quyền năng của Allah cho những ai biết kiên nhận trước thử thách và tai ương, biết tạ ơn trước ân huệ mà Allah đã ban cho người đó.');
INSERT INTO "vn_mokhtasar" VALUES(42,34,'42:34','Hoặc nếu muốn là Đấng Hiển Vinh đã hủy diệt những chiếc tàu thuyền đó bằng cách đưa trận cuồng phong đến nhấn chìm chúng với lý do tội lỗi mà con người đã làm nên. Tuy nhiên Ngài đã bỏ qua rất nhiều lỗi lầm của đám bầy tôi của Ngài nên Ngài không trừng phạt họ.');
INSERT INTO "vn_mokhtasar" VALUES(42,35,'42:35','Và Ngài biết rõ vị trí khi mà những chiếc thuyền buồm đó bị hủy diệt bởi trận cuồng phong đối với những ai hay tranh cãi về các dấu hiệu của Allah hầu dập tắt Nó thì họ sẽ không thoát khỏi sự hủy diệt. Thế nên các ngươi chỉ được cầu xin Allah mà thôi, và từ bỏ kẻ thù của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(42,36,'42:36','Bởi thế, những gì TA ban cho các ngươi - hỡi nhân loại - từ tài sản, danh vọng hoặc con cái thì đó là sự hưởng thụ tạm bợ của đời sống trần gian này, và nó chỉ là sự thoáng qua rồi chóng tàn. Còn ân huệ vĩnh hằng, đó chính là ân huệ Thiên Đàng mà Allah đã chuẩn bị cho những người tin tưởng Allah và tin tưởng các Sứ Giả của Ngài. Và chỉ duy nhất Thượng Đế của họ, họ phó thác tất cả mọi việc.');
INSERT INTO "vn_mokhtasar" VALUES(42,37,'42:37','Và những ai tránh xa các trọng tội, tránh những hành động đại bại, và giận dữ khi bị đối xử xấu bằng lời lẽ hoặc hành động thì họ biết đè nén cơn giận và tha thứ không trả đũa người đó thì sự tha thứ đó tốt cho họ khi đó là đức tính tốt đẹp và đáng quan tâm.');
INSERT INTO "vn_mokhtasar" VALUES(42,38,'42:38','Và những ai đáp lại Thượng Đế của họ bằng việc làm theo mệnh lệnh của Ngài, và từ bỏ những gì Ngài ngăn cấm, dâng lễ nguyện Salah một cách chu đáo, và họ kham khảo ý kiến về những vấn đề liên quan đến họ, và bố thí những gì họ được ban bố bổng lộc với mong muốn nhận được sự hài lòng của Allah');
INSERT INTO "vn_mokhtasar" VALUES(42,39,'42:39','Và những ai khi bị áp bức, bất công thì biết phản kháng để tự vệ thể hiện sự tôn trọng và yêu thương nhau bản thân minh, nếu người áp bức không đáng được tha thứ. Đây là cách phơi bày chân lý, đặc biệt nếu trong trường hợp tha thứ không có lợi ích gì.');
INSERT INTO "vn_mokhtasar" VALUES(42,40,'42:40','Và ai muốn đòi lại quyền lợi của mình thì y được điều đó nhưng chỉ được tương đồng với điều y đã bị không thêm cũng không vượt mức. Còn ai biết tha thứ cho người áp bức y và không cần báo thù, rồi hòa giải giữa y và người anh em của y thì sẽ được phần thưởng ở nơi Allah. Bởi vì Ngài không thích những ai làm điều bất công với nhân loại từ chính bản thân của họ hay của cải hay các thứ của họ, và Ngài câm ghét họ.');
INSERT INTO "vn_mokhtasar" VALUES(42,41,'42:41','Và ai dám tự dành công bằng cho mình thì họ chỉ là giành lấy phần của họ mà không ai được phép tước đoạt nó.');
INSERT INTO "vn_mokhtasar" VALUES(42,42,'42:42','Quả thật, sự khiển trách và hình phạt chỉ dành cho những người áp bức người khác, và gây tội lỗi trên trái đất. Họ là những người sẽ bị trừng phạt đau đớn vào Ngày Sau.');
INSERT INTO "vn_mokhtasar" VALUES(42,43,'42:43','Và chắc rằng ai biết nhẫn nhịn khi bị áp bức và tha thứ cho người khác, quả thật đó là sự nhẫn nhịn sẽ mang lại điều tốt đẹp cho y và cho cộng đồng. Và đó là việc làm đáng được ca ngợi, và chỉ hoàn thành tốt điều đó khi có quyết tâm cao.');
INSERT INTO "vn_mokhtasar" VALUES(42,44,'42:44','Và ai đã bị Allah bỏ rơi không dẫn dắt thì hắn sẽ bị lạc khỏi chân lý, và không có người bảo hộ nào đứng ra che chở cho hắn. Rồi Ngươi sẽ thấy những kẻ bất công với chính bản thân họ bằng việc vô đức tin và nhiều tội lỗi khác khi nhìn thấy hình phạt của Ngày Phục Sinh thì họ sẽ nói với niềm hy vọng mong manh: Còn con đường nào để chúng tôi trở về trần gian để sám hối với Allah không?.');
INSERT INTO "vn_mokhtasar" VALUES(42,45,'42:45','Và Ngươi - hỡi Thiên Sứ - sẽ nhìn thấy đám người bất công khi bị dắt đi đến Hỏa Ngục dưới hiện trạng khúm núm và bẽn lẽn nhìn mọi người một cách rụt rè do vô cùng sợ hãi. Còn những người tin tưởng vào Allah, vào các Sứ Giả của Ngài thì lại nói: Quả thật, những người thua cuộc thực sự là những người đã đánh mất chính mình và đánh mất gia đình mình vào Ngày Phục Sinh vì họ phải gánh chịu hình phạt của Allah, chẳng phải họ là những người tự bất công với chính bản thân họ bằng việc vô đức tin và nhiều tội lỗi khác rồi bị trừng phạt vĩnh viễn không bao giờ bị gián đoạn');
INSERT INTO "vn_mokhtasar" VALUES(42,46,'42:46','Và họ sẽ không có người bảo hộ nào đứng ra giúp đỡ họ thoát khỏi hình phạt từ Allah vào Ngày Phục Sinh, và ai bị Allah bỏ rơi khỏi chân lý thì chắn chắc người đó sẽ bị lầm lạc, y sẽ không bao giờ tìm được con đường nào cho sự dẫn dắt đến với chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(42,47,'42:47','Các ngươi - hỡi nhân loại - hãy mau mau đáp lại lời gọi của Thượng Đế của các ngươi bằng việc tuân theo những mệnh lệnh của Ngài và tránh xa những điều Ngài ngăn cấm, trước khi xảy ra Ngày Phục Sinh mà không ai có thể ngăn cản, các ngươi không còn nơi nào để trú ẩn cũng như các ngươi không có lý do nào để chạy tội mà các ngươi đã phạm ở trên trần gian');
INSERT INTO "vn_mokhtasar" VALUES(42,48,'42:48','Nhưng nếu họ quay lưng với những điều Ngươi ra lệnh thì TA vốn không cử Ngươi - hỡi Thiên Sứ - đến làm người trông nom để trông coi những việc làm của họ, mà TA cử Ngươi đến với tư cách là người truyền thông điệp của TA, còn việc thanh toán xét xử là việc của Allah. Và quả thật, khi TA cho nhân loại nếm mùi khoan dung từ sự giàu có, sức khỏe, và các thứ tương tự khác thì họ lại vui mừng, rồi khi con người gặp phải tai ương do những tội lỗi của họ gây ra thì quả thật, họ thường phủ nhận hồng ân của Allah, và không bao giờ biết ơn Ngài, và họ không hài lòng với sự sắp đặt của Allah bằng sự sáng suốt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(42,49,'42:49','Allah nắm giữ quyền hành trong các tầng trời và trái đất, Ngài tạo hóa ra nam hay nữ hay những thứ khác theo ý của Ngài, Ngài ban cho ai có con gái mà không cho con trai theo ý của Ngài, rồi Ngài ban cho ai có con trai mà không có con gái theo ý muốn của Ngài');
INSERT INTO "vn_mokhtasar" VALUES(42,50,'42:50','Hoặc Ngài cho ai có cả con trai và con gái theo ý của Ngài, và Ngài làm cho người nào vô sinh không thể sinh con theo ý của Ngài. Quả thật, Ngài Hằng Biết những gì trước đó và những điều trong tương lai, và đó là kiến thức toàn năng và sự sáng suốt của Ngài, không gì có thể che giấu được Ngài, và Ngài không bất lực trước bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(42,51,'42:51','Con người không phù hợp được Allah đàm thoại trực tiếp với y, một khi Ngài muốn ra lệnh con người điều gì thì Ngài mặc khải cho người đó qua giấc mơ hoặc bằng cách khác, hoặc Ngài nói chuyện với người đó nhưng chỉ cho nghe âm thanh chứ không thấy được Ngài, hoặc Ngài gửi Thiên Thần xuống làm Sứ Giả như là Đại Thiên Thần Jibril đến truyền đạt cho Thiên Sứ là người phàm theo ý muốn của Allah về những điều Ngài muốn mặc khải. Quả thật, Ngài là Đấng Tối Cao từ trong chính bản thân và trong chính thuộc tính của Ngài, Đấng Sáng Suốt trong việc tạo hóa, trong quyền năng, và trong luật pháp của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(42,52,'42:52','Và như những gì TA đã mặc khải cho các Sứ Giả trước Ngươi - hỡi Thiên Sứ - TA đã mặc khải cho Ngươi Qur''an từ nơi TA. Trước đó Ngươi không biết gì về các Kinh sách được ban xuống cho các Thiên Sứ cũng như Ngươi vốn không biết gì về đức tin Iman là gì cả? Nhưng Ta đã ban Kinh Qur''an này xuống làm nguồn ánh sáng để dẫn dắt người nào TA muốn trong đám bầy tôi của TA, và quả thật Ngươi là người chỉ cho nhân loại biết con đường ngay chính, và đó chính là tôn giáo Islam');
INSERT INTO "vn_mokhtasar" VALUES(42,53,'42:53','Con đường của Allah, Đấng mà tất cả những gì trong các tầng trời và tất cả những gì dưới đất đều thuộc về Ngài duy nhất gồm sự tạo hóa, uy quyền, và sự quản lý, tất cả đều thuộc về Allah duy nhất, tất nhiên mọi thứ sẽ trở về nơi Ngài để Ngài định đoạt và quản lý');
INSERT INTO "vn_mokhtasar" VALUES(43,1,'43:1','(Ha.Mim) đã được trình bày ở phần đầu của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(43,2,'43:2','Allah thề bởi Qur''an, Kinh Sách minh bạch cho con đường hướng dẫn đến chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(43,3,'43:3','Chắc chắn, TA đã thiên khải Thiên Kinh Qur''an bằng ngôn ngữ Ả-rập, hy vọng các ngươi hiểu được Nó - hỡi nhóm người nói chuyện bằng tiếng Qur''an - biết về ý nghĩa của Nó, và nhận thức về Nó để các ngươi truyền lại cho những cộng đồng khác.');
INSERT INTO "vn_mokhtasar" VALUES(43,4,'43:4','Và quả thật, Thiên Kinh Qur''an này được trích từ Tập Kinh Mẹ rất cao cả, uyên thâm, quả thật TA đã làm nên luật lệ từ những câu Kinh của Nó trong việc thực thi và cấm đoán.');
INSERT INTO "vn_mokhtasar" VALUES(43,5,'43:5','Chẳng lẻ TA phải ngừng việc bạn xuống Qur''an cho các ngươi bởi đa số các ngươi chống đối bằng việc phạm tội Shirk và nhiều tội lỗi khác ư? Không thể nào, ngược lại do long thương xót của TA giành cho các ngươi khiến Ta thực hiện điều ngược lại.');
INSERT INTO "vn_mokhtasar" VALUES(43,6,'43:6','Và biết bao Thiên Sứ đã được TA cử phái đến với các cộng đồng trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(43,7,'43:7','Thế nhưng không một Sứ Giả nào từ Allah đến với những cộng đồng trước đây mà lại không bị người dân chế giễu.');
INSERT INTO "vn_mokhtasar" VALUES(43,8,'43:8','Rồi TA đã tiêu diệt những người mạnh nhất của họ từ những cộng đồng đó, và tất nhiên những kẻ yếu hơn không thể gây khó dễ việc TA tiêu diệt họ, và hình ảnh hay cách thức hủy diệt những cộng đồng trước đó đã được ghi chép vào Thiên Kinh Qur''an, như dân A''d, dân Thamud, cộng đồng của Lut, và dân Madyan.');
INSERT INTO "vn_mokhtasar" VALUES(43,9,'43:9','Và nếu Ngươi - hỡi Thiên Sứ - hỏi đám người thờ đa thần phủ nhận: Ai đã tạo ra các tầng trời và ai đã tạo ra trái đất? Thì chắc chắn họ sẽ trả lời Ngươi: Quả thật Đấng Toàn Năng mà không ai có thể vượt trội hơn Ngài, Đấng Toàn Tri biết hết trong tất cả mọi thứ đã tạo ra chúng.');
INSERT INTO "vn_mokhtasar" VALUES(43,10,'43:10','Allah là Đấng đã thiết kế trái đất bằng phẳng cho các ngươi để các ngươi dễ dàng dẫm chân bước trên đó, và Ngài vạch cho các ngươi những con đường men theo truyền núi và các thung lũng. Hy vọng các ngươi được hướng dẫn trong hành trình của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(43,11,'43:11','Và Ngài đã ban nước mưa từ trên trời xuống theo định lượng vừa đủ dùng cho các ngươi, và vừa đủ dùng cho những vật nuôi và các cánh đồng của các ngươi. Rồi TA đã làm sống lại vùng đất khô cằn mà cây cối không thể sinh sống nổi, và tương tự như việc TA đã làm sống lại vùng đất khô cằn đó bằng nhiều thảo mộc, TA cũng sẽ làm các ngươi sống lại sau khi đã chết.');
INSERT INTO "vn_mokhtasar" VALUES(43,12,'43:12','Và Ngài đã tạo hóa tất cả mọi thứ theo từng cặp, như ban đêm và ban ngày, giống đực và giống cái, và những thứ khác. Và Ngài đã tạo ra cho các ngươi những chiếc thuyền buồm và những con vật nuôi để các ngươi sử dụng hay cưỡi chúng trong các chuyến đi của các ngươi, rồi các ngươi láy những chiếc thuyền trên biển cả cũng như cưỡi những con vật trên mặt đất.');
INSERT INTO "vn_mokhtasar" VALUES(43,13,'43:13','Ngài đã chế ngự tất cả những thứ đó cho các ngươi. Hy vọng khi các ngươi ngồi được trên lưng chúng trong các chuyến đi xa, thì sau đó các ngươi nhớ đến ân huệ của Thượng Đế của các ngươi, Ngài đã chế ngự cho các ngươi an toàn trên lưng của chúng, rồi các ngươi nói bằng chính lưỡi của mình: Thật quang vinh và thiêng liêng thay Đấng đã ban cho và đã thuần phục cho bầy tôi những con vật này rồi cho bầy tôi được kiểm soát chúng, và bầy tôi đã không thể chế ngự được chúng nếu Allah không chế ngự dùm.');
INSERT INTO "vn_mokhtasar" VALUES(43,14,'43:14','Và chắc chắn chúng tôi sẽ trở về gặp Ngài duy nhất Thượng Đế của bầy tôi sau khi bầy tôi đã chết để nhận lấy sự xét xử và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(43,15,'43:15','Và những kẻ thờ đa thần tuyên bố rằng một số tạo vật là con cái của Đấng Tạo Hóa, khi họ bảo: Các thiên thần là con gái của Allah, quả thật người nào nói những lời nói như vậy thì họ thật vong ân khi đã kết hợp giữa sự phủ nhận và lầm lạc');
INSERT INTO "vn_mokhtasar" VALUES(43,16,'43:16','Phải chăng các ngươi - hỡi những kẻ thờ đa thần - nói rằng: Allah tự chọn con gái cho bản thân Ngài trong số tạo vật mà Ngài đã tạo hóa, và các ngươi giành riêng cho mình những đứa con trai ư? Dựa vào đâu mà các ngươi lại xác định là có sự phân chia như vậy?');
INSERT INTO "vn_mokhtasar" VALUES(43,17,'43:17','Và khi một người trong bọn họ nhận được thông tin về đứa con gái vừa mới hạ sanh giống như họ đã vu khống như thế cho Allah thì gương mặt của hắn trở nên xám xịt, hiện rõ một nổi lo lắng và u sầu, và nhìn như là hắn đang vô cùng giận dữ. Rồi sẽ như thế nào khi hắn quy cho Thượng Đế của hắn điều mà làm hắn thất vọng khi nhận được thông tin đó?');
INSERT INTO "vn_mokhtasar" VALUES(43,18,'43:18','Chẳng lẻ họ lại qui cho Thượng Đế của họ người phụ tá được nuôi dưỡng bằng trang sức và khi tranh luật thì lời nói không rõ ràng như là phụ nữ hay sao?!');
INSERT INTO "vn_mokhtasar" VALUES(43,19,'43:19','Và họ lại đặt cho các Thiên Thần vốn là bầy tôi của Đấng Khoan Dung là nữ giới. Phải chăng họ đã ở đó chứng kiến khi Allah tạo hóa ra Thiên Thần, nên họ mới khẳng định Thiên Thần là giới nữ?! Các vị Thiên Thần sẽ ghi chép sự xác nhận này của họ, và họ sẽ được tra hỏi vào Ngày Phục Sinh, và hình phạt sẽ dành cho sự dối trá của họ.');
INSERT INTO "vn_mokhtasar" VALUES(43,20,'43:20','Và họ đã đổ thừa cho tiền định mà nói: Nếu như Allah muốn chúng tôi không thờ Thiên Thần thì làm sao chúng tôi có thể thờ phượng Họ được cơ chứ. Sự việc như thế này thể hiện sự hài lòng của Ngài, nhưng câu nói của họ nào được xuất phát từ sự thiếu hiểu biết, họ chỉ nói những điều dối trá mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(43,21,'43:21','Hay là TA đã ban cho những kẻ thờ đa thần Kinh Sách trước Thiên Kinh Qur''an, cho phép họ thờ phượng thần linh khác ngoài Allah?! Rồi họ bám chặt theo Kinh Sách đó.');
INSERT INTO "vn_mokhtasar" VALUES(43,22,'43:22','Không! không phải như vậy, mà những họ diện lý do truyền thống: Quả thật, chúng tôi thấy cha ông chúng tôi theo một tín ngưỡng và tôn giáo, và trước đây tổ tiên của chúng tôi đã thờ phượng các bụt tượng, nên chúng tôi là những người đi theo dấu chân của họ trong việc thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(43,23,'43:23','Và cũng như những kẻ đã dối trá, họ lấy truyền thống của cha ông của họ ra để kháng cự, không một người báo trước nào trước Ngươi - hỡi Thiên Sứ - đã được TA cử phái đến cho một thị trấn để cảnh báo họ mà những người đứng đầu và những kẻ quyền cao chức trọng trong bọn họ lại không nói: Quả thật, chúng tôi đã thấy cha ông chúng tôi theo một tôn giáo và tính ngưỡng nào đó, và chắc chắn chúng tôi là những người đi theo dấu chân của họ, chứ cộng đồng của Ngươi không hề đi theo tôn giáo hay tính ngưỡng mới mẻ nào trong đó cả.');
INSERT INTO "vn_mokhtasar" VALUES(43,24,'43:24','Thiên Sứ của họ nói với họ: Chẳng lẻ các ngươi vẫn theo cha ông các ngươi khi Ta mang đến cho các ngươi điều tốt đẹp hơn tôn giáo của cha ông các ngươi luôn ư? Họ đáp lại: Quả thật, chúng tôi không tin những gì Ngươi và các Thiên Sứ trước Ngươi mang đến cho chúng tôi.');
INSERT INTO "vn_mokhtasar" VALUES(43,25,'43:25','Bởi thế, TA đã trừng phạt những cộng đồng đã phủ nhận các Thiên Sứ trước Ngươi, và TA đã hủy diệt họ. Vì vậy, Ngươi hãy nhìn kỹ kết cuộc của những kẻ phủ nhận những điều mặc khải cho họ, quả thật họ có một kết cuộc vô cùng đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(43,26,'43:26','Và Ngươi hãy nhớ - hỡi Thiên Sứ - khi Ibrahim nói với cha và người dân của Người: Quả thật, Ta vô can với những gì mà các vị đang thờ phượng từ những bụt tượng mà không phải là Allah.');
INSERT INTO "vn_mokhtasar" VALUES(43,27,'43:27','Ngoại trừ Allah Đấng đã tạo hóa ra Ta, rồi Ngài sẽ dẫn lối Ta đến với những gì mà nơi đó hữu ích cho Ta từ việc đi theo tôn giáo chính trực của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(43,28,'43:28','Và Ibrahim đã làm cho Câu Nói Tawhid (không có Thượng Đế nào thực sự ngoài Allah) tồn tại trong dòng tộc của Người cho đến thế hệ sau này, họ vẫn tiếp tục thờ phượng Allah duy nhất không tổ hợp với Ngài bất cứ thứ gì. Với hy vọng khi họ được trở về với Allah bởi sự sám hối từ tội Shirk và những tội lỗi khác');
INSERT INTO "vn_mokhtasar" VALUES(43,29,'43:29','TA không vội tiêu diệt những kẻ đa thần phủ nhận đó, mà TA để họ ở lại trần gian và cha ông của họ trước đó cũng như vậy cho đến khi Qur''an được ban đến cho họ, và một Sứ Giả minh bạch đó là Muhammad - cầu xin Allah ban sự bình an đến Người - đến với họ.');
INSERT INTO "vn_mokhtasar" VALUES(43,30,'43:30','Rồi khi Qur''an được mang đến với họ chính là chân lý không có gì phải nghi ngờ thì họ lại nói: Đây quả là mà thuật, Muhammad đã làm phù phép đối với họ bọn ta, và chắc chắn bọn ta phủ nhận và sẽ không tin tưởng nơi Y.');
INSERT INTO "vn_mokhtasar" VALUES(43,31,'43:31','Và những kẻ thờ đa thần phủ nhận nói: Tại sao Allah lại không ban Qur''an xuống cho một trong hai người có quyền lực nhất ở Makkah hoặc ở Ta-if, và hai người đó là Al-Waleed bin ''Uqbah và ''Urwah bin Mas-ud thay thế cho Muhammad một người nghèo khổ, mồ côi.');
INSERT INTO "vn_mokhtasar" VALUES(43,32,'43:32','Chẳng lẻ họ đứng ra phân chia hồng ân của Thượng Đế của Ngươi - hỡi Thiên Sứ - rồi họ đưa nó cho ai mà họ muốn và ngăn cản nó cho ai mà họ muốn, điều đó là theo ý họ hay theo ý muốn của Allah? Allah đã phân chia giữa họ các hồng ân trên trần thế, và ban cho họ sự giàu nghèo, để Ngài ban cho người này có thể sai bảo hay thuê mướn người kia làm việc. Và hồng ân mà Ngài dành cho bầy tôi của Ngài vào Ngày Sau sẽ tốt hơn tất cả những gì họ có được ở trên trần gian tạm bợ này.');
INSERT INTO "vn_mokhtasar" VALUES(43,33,'43:33','Và nếu như không ngăn chặng nhân loại trở thành một cộng đồng thống nhất trong việc phủ nhận đức tin thì TA đã tạo ra những ngôi nhà cho người nào phủ nhận Allah có mái nhà bằng bạc và tạo cho họ những nấc thang để họ đi lên.');
INSERT INTO "vn_mokhtasar" VALUES(43,34,'43:34','Và TA tạo cho họ những ngôi nhà có những cánh cửa, và tạo cho họ chiếc giường để họ ngã lưng nghỉ ngơi.');
INSERT INTO "vn_mokhtasar" VALUES(43,35,'43:35','Và TA đã tạo vàng cho họ, nhưng tất cả chỉ là những vật dụng ở trên cuộc sống trần tục, nó chỉ là sự hưởng thụ ngắn ngủi không hề tồn tại mãi mãi, còn những gì ở Đời Sau từ những ân huệ tốt đẹp nơi Thượng Đế của Ngươi - hỡi Thiên Sứ - dành cho những người kính sợ Allah bằng việc tuân theo mệnh lệnh của Ngài và tránh xa những điều Ngài ngăn cấm.');
INSERT INTO "vn_mokhtasar" VALUES(43,36,'43:36','Còn ai làm ngơ không để mắt đến Qur''an thì điều đó sẽ dẫn y đến một hình phạt là Shaytan sẽ làm bạn đồng hành với y cũng như tăng thêm sự quyến rũ đối với y');
INSERT INTO "vn_mokhtasar" VALUES(43,37,'43:37','Và những kẻ đồng hành này luôn quản thúc nhân loại, xúi dục họ việc quay lưng lại với Qur''an để cản trở họ đến với tôn giáo của Allah, rồi họ không tuân theo mệnh lệnh của Ngài, và không tránh xa những điều Ngài ngăn cấm. Tuy nhiên, họ vẫn luôn nghĩ họ là những người được hướng dẫn đến với chân lý, và rồi đa số họ không hối cải về việc lầm lạc của họ.');
INSERT INTO "vn_mokhtasar" VALUES(43,38,'43:38','Cho đến khi đám người nghoãn mặt với việc tụng niệm Allah bị mang ra trình diện vào Ngày Tận Thế thì họ khát khao rằng: Ước gì giữa ta và ngươi - hỡi kẻ đồng hành - ở xa nhau bằng khoảng cách giữa phương đông và phương tây, thật tồi tệ thay người bạn đồng hành như vậy!.');
INSERT INTO "vn_mokhtasar" VALUES(43,39,'43:39','Allah nói với những kẻ phủ nhận vào Ngày Phục Sinh: Và ngày hôm nay, lời trách móc không giúp ích được gì cho các ngươi - trước đây các ngươi đã bất công với chính bản thân các ngươi bằng tội Shirk và nhiều tội lỗi khác - các ngươi hãy cùng nhau san sẻ hình phạt vì những thần linh mà các ngươi đã tổ hợp trước đây sẽ không gánh vác bất cứ hình phạt nào của các ngươi cả.');
INSERT INTO "vn_mokhtasar" VALUES(43,40,'43:40','Với đám người điếc không thể nghe thấy chân lý, những người mù không thể nhìn thấy chân lý, vậy Ngươi - hỡi Thiên Sứ - làm sao có khả năng làm cho người điếc nghe thấy, hoặc hướng dẫn người mù, hay là hướng dẫn người mà rõ ràng y đang lạc lối ra khỏi con đường ngay thẳng cho được?!.');
INSERT INTO "vn_mokhtasar" VALUES(43,41,'43:41','Dù rằng TA có rút hồn Ngươi chết trước - khi TA trừng phạt bọn họ - thì chắc chắn họ vẫn sẽ bị trừng phạt ở đời này và Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(43,42,'43:42','Hoặc TA cho Ngươi thấy một vài hình phạt mà TA đã hứa với họ thì chắc chắn TA thừa năng lực thực hiện nó, không có bất cứ điều gì có thể ngăn cản được TA.');
INSERT INTO "vn_mokhtasar" VALUES(43,43,'43:43','Vậy nên, Ngươi - hỡi Thiên Sứ - hãy bám chắc vào những gì Thượng Đế của Ngươi mặc khải cho Ngươi. Quả thật Ngươi đang đi đúng con đường chính đạo không hề có nhầm lẫn trong nó.');
INSERT INTO "vn_mokhtasar" VALUES(43,44,'43:44','Và quả thật, Kinh Qur''an này là niềm tự hào dành cho Ngươi và cho người dân của Ngươi. Và các ngươi sẽ bị chất vấn vào Ngày Phục Sinh về đức tin đối với Nó và việc đi theo sự hướng dẫn của Nó, và kêu gọi nhân loại đến với Nó.');
INSERT INTO "vn_mokhtasar" VALUES(43,45,'43:45','Và Ngươi - hỡi Thiên Sứ - hãy hỏi những Sứ Giả được cử phái đến trước Ngươi: Rằng TA có vạch ra đấng thờ phượng nào ngoài Đấng Al-Rahman cho Họ thờ phượng hay không?');
INSERT INTO "vn_mokhtasar" VALUES(43,46,'43:46','Và quả thật, TA đã cử phải Musa bằng các dấu hiệu của TA đến với Fir-''awn và những tên chức quyền từ cộng đồng của hắn, rồi Musa nói với họ: Quả thật, Ta là Sứ Giả của Thượng Đế của tất cả mọi tạo vật.');
INSERT INTO "vn_mokhtasar" VALUES(43,47,'43:47','Nhưng khi Người mang những dấu hiệu của TA ra trình bày cho họ thấy thì họ giễu cợt và nhạo báng');
INSERT INTO "vn_mokhtasar" VALUES(43,48,'43:48','Và TA cho Fir-''awn và đám đầu sỏ thuộc cộng đồng của Musa lần lượt thấy được những bằng chứng mà TA đã ban cho Musa, mỗi cái tiếp theo được mang đến điều uy nghi vĩ đại hơn cái trước đó. Và TA đã bắt phạt họ ở đời này với hy vọng họ biết quay trở lại với Ngài mà từ bỏ việc vô đức tin, thế nhưng không có hiệu quả gì đối với họ.');
INSERT INTO "vn_mokhtasar" VALUES(43,49,'43:49','Rồi khi một số hình phạt giáng xuống họ thì họ nói với Musa: Này hỡi tên phù thủy! Ngươi hãy cầu xin Thượng Đế của Ngươi theo những gì Ngài đã giao ước với Người giúp cho chúng tôi thoát khỏi hình phạt khi chúng tôi sẽ tin tưởng nơi Ngài, quả thật chúng tôi là những người được hướng dẫn khi được Ngài giải thoát.');
INSERT INTO "vn_mokhtasar" VALUES(43,50,'43:50','Nhưng khi TA loại bỏ hình phạt ra khỏi họ thì họ lại phá vỡ lời hứa của họ, họ không còn nghe lời Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(43,51,'43:51','Và Fir-''awn đã tuyên bố với thần dân của hắn với giọng điệu tự hào và ngạo mạn về quyền lực của mình: Hỡi thần dân! Chẳng phải ta là vua của Ai Cập hay sao? Chẳng phải dòng sông Nin này đang chảy dưới tòa lâu đài của ta ư? Chẳng lẻ các ngươi không thấy quyền lực và biết rõ sự vĩ đại của ta à?.');
INSERT INTO "vn_mokhtasar" VALUES(43,52,'43:52','Và chắc chắn ta tốt hơn Musa, một tên ranh con yếu đuối mà ăn nói lại không được lưu loát êm tai.');
INSERT INTO "vn_mokhtasar" VALUES(43,53,'43:53','Sao Y không được Allah ban cho vòng bằng vàng để chứng tỏ Y là Thiên Sứ của Ngài, hoặc sao Y không được các Thiên Thần đi theo tháp tùng để chứng nhận sứ mạng của Y là thật?!.');
INSERT INTO "vn_mokhtasar" VALUES(43,54,'43:54','Bởi thế, Fir-''awn đã hiệu lệnh cho người dân của hắn khiến họ tuân theo sự lạc lối của hắn. Quả thật, họ là cộng đồng rời khỏi việc tuân theo mệnh lệnh của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(43,55,'43:55','Rồi khi họ làm TA nổi giận khi họ vẫn cứ tiếp tục không tin tưởng, và TA đã trừng phạt họ bằng cách nhấn chìm tất cả bọn họ dưới biển.');
INSERT INTO "vn_mokhtasar" VALUES(43,56,'43:56','Rồi TA đã lấy câu chuyện của Fir-''awn và đám đầu não của hắn ra làm gương để nhân loại và nhóm người bất tin trong cộng đồng của Ngươi thấy mà rút kinh nghiệm, và bọn họ cũng trở thành một bài học cho người nào biết suy xét, để họ không làm những việc làm như đám người đó rồi lại bị trừng phạt như thế.');
INSERT INTO "vn_mokhtasar" VALUES(43,57,'43:57','Và khi những kẻ thờ đa thần nghĩ rằng Ysa, người đang bị những tín đồ Thiên Chúa thờ phượng được nhắc nhở trong lời phán Allah với ý nghĩa tổng thể: {Chính các ngươi và những vật mà các ngươi tôn thờ ngoài Allah sẽ là chất đốt của Hỏa Ngục, nơi mà các ngươi sẽ sa xuống.} Và chắc chắn Allah nghiêm cấm thờ phượng Ysa giống như việc Ngài nghiêm cấm việc thờ phượng các bụt tượng khi cộng đồng của Ngươi - hỡi Thiên Sứ - la lối và cải vả trong tranh chấp, nói rằng: Chúng tôi hài lòng mong muốn thượng đế của chúng tôi được như là Ysa. Rồi Allah đã gửi lời hồi đáp đến họ: {Bởi vì, những ai mà cái tốt nhất của TA đã được gởi đi trước cho chúng thì sẽ được đưa ra khỏi Hỏa Ngục}.');
INSERT INTO "vn_mokhtasar" VALUES(43,58,'43:58','Và họ nói: Những thần linh của chúng tôi thờ phượng tốt hơn hay là Ysa tốt hơn? Họ đưa ra cho Ngươi vấn đề về Ibnu Al-Zib''ara và những ví dụ tương tự không phải là họ muốn tìm đến sự thật, mà họ chỉ muốn tranh cải, vì họ là nhóm người cải vã yêu thích sự tranh chấp.');
INSERT INTO "vn_mokhtasar" VALUES(43,59,'43:59','Ysa con trai Maryam chỉ là một bề tôi trong đám bầy tôi của Allah, được Ngài ban ân huệ cho Y bằng việc giao cho Y sứ mệnh Thiên Sứ. Và TA lấy Y làm một ví dụ cho con cháu Israel, Y là một bằng chứng cho thấy quyền năng của Allah khi Ngài tạo hóa Y không hề có cha như việc Ngài đã tạo hóa Adam mà không hề có cha và mẹ.');
INSERT INTO "vn_mokhtasar" VALUES(43,60,'43:60','Và nếu muốn hủy diệt các ngươi - hỡi con cháu Adam - là TA đã hủy diệt các ngươi rồi, và TA thay thế các ngươi bằng nhóm Thiên Thần để ho quản lý trái đất, họ chuyên tâm chỉ thờ phượng Allah không tổ hợp với Ngài bất cứ thần linh nào.');
INSERT INTO "vn_mokhtasar" VALUES(43,61,'43:61','Và quả thật Ysa là một trong những đại dấu hiệu báo trước Giờ Tận Thế khi Y xuống là vào lúc thời kỳ cuối của trần giân, vì thế Ngươ đừng có sinh nghi gì về việc Giờ Tận Thế sẽ xảy ra. Vậy nên, các ngươi hãy theo TA khi TA đưa các ngươi những thứ đến từ Allah, thứ mà TA đưa đến các ngươi là con đường ngay thẳng không bị biến dạng.');
INSERT INTO "vn_mokhtasar" VALUES(43,62,'43:62','Và các ngươi đừng để sự lôi kéo và quyến rũ của Shaytan làm cho các ngươi bị dịch chuyển khỏi con đường ngay thẳng, quả thật hắn là kẻ thù công khai của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(43,63,'43:63','Và khi Ysa mang đến cho cộng đồng của Người những bằng chứng rõ ràng về việc chắc chắn Người là Thiên Sứ, Người nói: Quả thật, Ta đến với các ngươi là từ sự sáng suốt của Allah, và Ta đến để làm rõ cho các ngươi một số vấn đề mà các ngươi bất đồng trong tôn giáo. Bởi thế, hãy kính sợ Allah bằng cách tuân theo những mệnh lệnh của Ngài, hãy tránh xa những gì Ngài ngăn cấm, và các ngươi hãy nghe theo những gì Ta ra lệnh và ngưng thực hiện những gì Ta ngăn cấm.');
INSERT INTO "vn_mokhtasar" VALUES(43,64,'43:64','Quả thật, Allah là Thượng Đế của Ta và của các ngươi, không có Thượng Đế nào khác ngoài Ngài cả. Bởi thế, hãy thành tâm thờ phượng Ngài duy nhất. Và đây là thuyết Tawhid, là con đường Chính Đạo mà không có sự quanh co trong đó');
INSERT INTO "vn_mokhtasar" VALUES(43,65,'43:65','Nhưng những nhóm người trong Thiên chúa giáo đã bất đồng về tình trạng của Ysa, một nhóm người trong họ bảo: Ysa là thượng đế, một số khác lại bảo: Người là con của Allah, còn số khác nữa lại bảo: Người và thân mẫu của Người là hai thượng đế. Rồi thật khốn khổ cho những ai sai quấy từ chính bản thân họ - về việc mô tả Ysa là thượng đế, hoặc Người tự xuống mặc khải, hay là Người là người thứ ba trong ba người (chúa cha, mẹ, và chúa con) - hình phạt sẽ chờ đợi họ vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(43,66,'43:66','Chẳng lẻ những kẻ bất đồng về tình trạng của Ysa không chờ gì ngoài Giờ Tận Thế, rồi điều đó sẽ đến một cách bất ngờ khi họ không cảm nhận được khi đó đến?! Rồi khi Nó đến họ vẫn trong tình trạng vô đức tin thì số phận của họ phải đối diện với hình phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(43,67,'43:67','Những người thân tình và những người bạn hữu phủ nhận đức tin và lầm lạc sẽ trở thành kẻ thù của nhau vào Ngày Phục Sinh ngoại trừ những người ngay chính biết kính sợ Allah bằng cách tuân theo những mệnh lệnh của Ngài và tránh xa những gì Ngài ngăn cấm thì họ sẽ là những người anh em thân tình của nhau mãi mãi không bao giờ chia cắt.');
INSERT INTO "vn_mokhtasar" VALUES(43,68,'43:68','Và Allah nói với họ: Hỡi bầy tôi của TA! Ngày hôm nay các ngươi không còn sợ những gì mà các ngươi sẽ nhận được nó, và cũng sẽ không buồn phiền về những gì mà các ngươi đã bỏ qua ở trên cõi đời');
INSERT INTO "vn_mokhtasar" VALUES(43,69,'43:69','Những ai tin tưởng vào Qur''an được ban xuống cho các Thiên Sứ, và phục tùng theo Qur''an thực hiện những gì Nó ra lệnh và tránh xa những điều Nó ngăn cấm.');
INSERT INTO "vn_mokhtasar" VALUES(43,70,'43:70','Các ngươi và những ai có đức tin hãy vào Thiên Đàng, các ngươi sẽ vui sướng với những hồng ân nơi đó mà những hồng ân đó không bao giờ biến mất và không bị đứt đoạn.');
INSERT INTO "vn_mokhtasar" VALUES(43,71,'43:71','Những chiếc đĩa và những chiếc tách uống bằng vàng sẽ chảy đến bên họ để được phục vụ, và trong Thiên Đàng có tất cả những món vật mà họ mong ước, mọi thứ điều nhìn đẹp mắt, và các ngươi cư ngụ trong nó muôn đời, không bao giờ bị đuổi ra ngoài.');
INSERT INTO "vn_mokhtasar" VALUES(43,72,'43:72','Đấy là Thiên Đàng đã được mô tả cho các ngươi mà Allah để lại cho người nào được chiếu cố đến cũng như có những việc làm ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(43,73,'43:73','Trong đó có rất nhiều hoa quả không bao giờ hết mùa để cho các ngươi dùng.');
INSERT INTO "vn_mokhtasar" VALUES(43,74,'43:74','Quả thật, những tên tội đồ với việc phủ nhận đức tin và làm những điều sai quấy sẽ bị phạt trong Hỏa Ngục vào Ngày Phục Sinh và họ sẽ ở trong đó mãi mãi.');
INSERT INTO "vn_mokhtasar" VALUES(43,75,'43:75','Họ sẽ không được giảm nhẹ hình phạt, và ở trong đó họ cũng không nhận được lòng thương xót của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(43,76,'43:76','TA không hề bất công khi tống họ vào Hỏa Ngục, mà chính bản thân họ là những kẻ bất công làm điều sai quấy với việc phủ nhận đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(43,77,'43:77','Và họ kêu la với Thiên Thần quản Hỏa Ngục: Thưa ngài cai quản, xin ngài hãy xin với Thượng Đế của ngài là hãy giết quách chúng tôi đi, để chúng tôi chấm dứt phần còn lại của hình phạt. Rồi vị cai ngục đáp lại họ: Quả thật, các ngươi sẽ ở đây và chịu hình phạt mãi mãi không bao giờ chết, và hình phạt cũng không bao giờ bị gián đoạn.');
INSERT INTO "vn_mokhtasar" VALUES(43,78,'43:78','Quả thật, TA đã mang Chân Lý đến cho các ngươi ở trần gian nhưng các ngươi không ngó ngàng đến, đã thế các ngươi còn căm ghét Nó.');
INSERT INTO "vn_mokhtasar" VALUES(43,79,'43:79','Rồi họ ghét bỏ Sứ Giả - cầu xin sự bình an đến Người - và những kẻ thù của Người có âm mưu hãm hại Người, rồi quả thật TA đã sắp đặt và giải quyết những âm mưu của họ.');
INSERT INTO "vn_mokhtasar" VALUES(43,80,'43:80','Hay là họ nghĩ TA không nghe được những điều bí mật mà họ đã cất giấu trong trái tim của họ, hoặc những điều bí mật mà họ mật nghị với nhau. Không, quả thật TA nghe thấy tất cả điều đó, và các Thiên Thần luôn bên cạnh họ để ghi chép tất cả những việc họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(43,81,'43:81','Ngươi - hỡi Thiên Sứ - hãy nói với những người đã qui những đứa con gái cho Allah. Allah Toàn Năng, Ngài hoàn toàn vô can về những điều họ vu khống: Allah không có con, Ngài trong sáng với mọi thứ đã gán ghép cho Ngài. Ta là người đầu tiên thờ phượng Allah Toàn Năng, trong sáng khỏi mọi quấy bẩn.');
INSERT INTO "vn_mokhtasar" VALUES(43,82,'43:82','Quang vinh thay Thượng Đế của các tầng trời và trái đất, và Thượng Đế của Ngai Vương, Ngài vượt hẳn về những điều mà những kẻ đa thần đã qui cho Ngài là có đối tác, có bạn đồng hành, và có con cái.');
INSERT INTO "vn_mokhtasar" VALUES(43,83,'43:83','Vậy nên, Ngươi - hỡi Thiên Sứ - hãy cứ mặc họ lao vào những điều hư hỏng, cứ để họ vui chơi cho đến khi họ giáp mặt với Ngày mà họ được hứa, và đó chính là Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(43,84,'43:84','Và Ngài là Đấng quang vinh, Đấng xứng đáng được thờ phượng trong các tầng trời cũng như xứng đáng được thờ phượng ở dưới mặt đất. Và Ngài là Đấng Chí Minh trong việc tạo hóa, đánh giá, và quản lý. Đấng Toàn Tri biết hết mọi hoàn cảnh của đám bầy tôi của Ngài, không gì có thể che giấu được Ngài');
INSERT INTO "vn_mokhtasar" VALUES(43,85,'43:85','Và Allah tăng thêm điều tốt đẹp và hồng phúc thật vinh quang thay Ngài!, Ngài là Đấng duy nhất, nắm quyền cai trị các tầng trời và trái đất cũng như Ngài nắm quyền cai trị vạn vật nằm giữa các tầng trời và đất, và chỉ duy nhất Ngài biết rõ Giờ Tận Thế sẽ được dựng lên khi nào vào Ngày Phục Sinh, không ai biết điều đó ngoài Ngài cả. Và tất cả các ngươi sẽ trở về với Ngài vào Ngày Sau để được xét xử và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(43,86,'43:86','Và những kẻ mà những tên đa thần thờ phượng ngoài Allah không có quyền can thiệp giúp đỡ ở nơi Allah, ngoại trừ người nào xác nhận không có Thượng Đế nào đích thực ngoài Allah, và y biết rõ những gì y xác nhận, như: Ysa, ''Uzair, và các Thiên Thần.');
INSERT INTO "vn_mokhtasar" VALUES(43,87,'43:87','Và nếu Ngươi hỏi họ: Ai đã tạo hóa ra họ? thì chắc chắn họ sẽ đáp: Allah đã tạo hóa chúng tôi. Thế sao họ lại lánh xa việc thờ phượng Ngài sau khi đã công nhận điều đó ?!');
INSERT INTO "vn_mokhtasar" VALUES(43,88,'43:88','Và ở nơi Allah vinh quang, Ngài biết rõ lời than vãn của Thiên Sứ của Ngài về việc cộng đồng của Người phủ nhận Người mà nói: Lạy Thượng Đế của Bề Tôi! Quả thật, đám dân làng này đã không tin tưởng vào những gì Bề Tôi gởi đến họ.');
INSERT INTO "vn_mokhtasar" VALUES(43,89,'43:89','Vậy Ngươi hãy lánh xa họ, và hãy nói với họ những gì giúp họ đẩy lùi cái xấu - và điều đó đã có trong Makkah - rồi họ sẽ biết những hình phạt giành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(44,1,'44:1','(Ha.Mim) đã được trình bày ở phần đầu của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(44,2,'44:2','Allah thề bởi Qur''an, Kinh Sách minh bạch cho con đường hướng dẫn đến chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(44,3,'44:3','Quả thật, TA đã ban Kinh Qur''an xuống trong Đêm Định Mệnh, và đó là đêm có nhiều ân phước. Quả thật, TA cảnh báo cho các ngươi bằng Quyển Kinh Qur''an này.');
INSERT INTO "vn_mokhtasar" VALUES(44,4,'44:4','Trong đêm này, mọi việc công minh liên quan đến sinh kế, thời hạn và những điều khác được liệt kê từ những gì mà Allah nhắc đến trong năm đó.');
INSERT INTO "vn_mokhtasar" VALUES(44,5,'44:5','Tất cả mọi việc điều được liệt kê để thực hiện theo chỉ thị của TA. Quả thật, TA là Đấng Hằng Cử Phái các Sứ Giả.');
INSERT INTO "vn_mokhtasar" VALUES(44,6,'44:6','Việc Ngài cử phái các Sứ Giả là một hồng ân từ Thượng Đế của Ngươi - hỡi Thiên Sứ - cho những ai mà Ngài đã gửi đến họ. Quả thật, thật vinh quang thay Ngài! Ngài là Đấng Hằng Nghe những lời nói từ bầy tôi của Ngài, Đấng Hằng Biết những việc làm và sự định tâm của họ, không gì có thể che giấu được Ngài về điều đó');
INSERT INTO "vn_mokhtasar" VALUES(44,7,'44:7','Thượng Đế của các tầng trời, Thượng Đế của trái đất, và là Thượng Đế của vạn vật giữa trời và đất, nếu các ngươi có niềm tin vững chắc về điều đó thì hãy tin tưởng vào Sứ giả của TA');
INSERT INTO "vn_mokhtasar" VALUES(44,8,'44:8','Không có ai xứng đáng được thờ phượng ngoài Ngài. Ngài là Đấng ban sự sống và cái chết cũng như không ai tạo ra sự sống và cái chết ngoài Ngài cả. Thượng Đế của các ngươi và là Thượng Đế của cha ông các ngươi trước đó.');
INSERT INTO "vn_mokhtasar" VALUES(44,9,'44:9','Những kẻ đa thần này không hề có niềm tin vững chắc về điều đó, mà họ là đám người chỉ biết mãi mê với thú vui của họ trong những việc làm hư đốn.');
INSERT INTO "vn_mokhtasar" VALUES(44,10,'44:10','Bởi thế, Ngươi hãy đợi xem - hỡi Thiên Sứ - hình phạt dành cho cộng đồng của Ngươi đang gần kề, vào Ngày mà bầu trời sẽ tỏa ra một lớp khói rõ rệt, họ sẽ nhìn thấy rõ bằng đôi mắt của họ từ sự đau đớn tột cùng.');
INSERT INTO "vn_mokhtasar" VALUES(44,11,'44:11','Bao phủ cả cộng đồng của Ngươi, Và có lời bảo họ: Đây là hình phạt mà TA giáng xuống các ngươi một hình phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(44,12,'44:12','Rồi họ than vãn cầu xin Thượng Đế của họ: Lạy Thượng Đế của bầy tôi! Xin Ngài hãy dịch chuyển đi hình phạt mà Ngài đã giáng xuống cho bầy tôi. Quả thật, bầy tôi sẽ tin tưởng nơi Ngài và tin tưởng Thiên Sứ của Ngài nếu Ngài dịch chuyển nó cho bầy tôi.');
INSERT INTO "vn_mokhtasar" VALUES(44,13,'44:13','Làm sao mà họ có thể tiếp thu và đáp lại Thượng Đế của họ trong khi một Sứ Giả đã truyền đạt rõ lời mặc khải đến với họ, và họ biết rõ Sứ Giả là Người trung thực và đáng tin cậy ?!');
INSERT INTO "vn_mokhtasar" VALUES(44,14,'44:14','Sau đó họ bác bỏ với việc xác thực của Y, rồi họ nói về Y: Y chỉ là một kẻ học mót do người khác dạy lại chứ Y không phải là Thiên sứ. Rồi họ lại nói về Y: Y là kẻ điên.');
INSERT INTO "vn_mokhtasar" VALUES(44,15,'44:15','Quả thật, TA tạm thời dịch chuyển hình phạt của các ngươi một chút, nhưng chắc chắn các ngươi sẽ trở lại với việc phủ nhận và dối trá của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(44,16,'44:16','Ngươi hãy chờ xem họ - hỡi Thiên Sứ - vào Ngày mà TA sẽ túm lấy những kẻ phủ nhận trong cộng đồng của Ngươi một cách hung tợn như trận chiến Badr. Quả thật, TA sẽ trừng phạt họ về sự phủ nhận Allah và không tin vào Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(44,17,'44:17','Và quả thật, TA đã thử thách trước họ là cộng đồng của Fir-''awn, và đã đến với họ một Sứ Giả vinh dự của Allah để kêu gọi họ đến với sự tin tưởng Allah duy nhất và thờ phượng Ngài, và Người chính là Musa.');
INSERT INTO "vn_mokhtasar" VALUES(44,18,'44:18','Musa nói với Fir-''awn và thần dân của hắn: Xin bệ hạ hãy cứ để mặc cho Thần cùng với con cháu của Israel, vì họ là bầy tôi của Allah, bệ hạ không có quyền bắt họ làm nô lệ. Quả thật, Thần là một Sứ Giả của Allah, được Ngài tín nhiễm ra lệnh cho Thần đến truyền đạt cho bệ hạ mệnh lệnh của Ngài, Thần không thêm cũng không bớt bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(44,19,'44:19','Và bệ hạ chớ tự cao trước Allah bằng việc từ bỏ sự thờ phượng Ngài cũng như kiêu ngạo trước đám bầy tôi của Ngài. Quả thật, Thần đến với bệ hạ cùng với một minh chứng rất rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(44,20,'44:20','Và Thần cầu xin Thượng Đế của Thần và Thượng Đế của bệ hạ che chở khỏi bị bệ hạ giết chết bằng việc ném đá.');
INSERT INTO "vn_mokhtasar" VALUES(44,21,'44:21','Còn nếu bệ hạ không tin tưởng những gì Thần mang đến thì hãy để Thần yên, và mong chớ tiếp cận Thần với ý đồ xấu.');
INSERT INTO "vn_mokhtasar" VALUES(44,22,'44:22','Bởi thế, Musa đã cầu nguyện Thượng Đế của Y: Quả thật, cộng đồng này - Fir-''awn và đám chức sắc của hắn - là đám người tội lỗi, họ xứng đáng bị trừng phạt ngay lập tức.');
INSERT INTO "vn_mokhtasar" VALUES(44,23,'44:23','Rồi Allah ra lệnh cho Musa bí mật đưa cộng đồng của Người trốn đi vào ban đêm, và Ngài thông báo cho Người biết rằng Fir-''awn và đám người của hắn sẽ truy sát họ.');
INSERT INTO "vn_mokhtasar" VALUES(44,24,'44:24','Và Ngài ra lệnh cho Người chẻ biển ra làm đôi để Người cùng con cháu của Israel băng qua biển, rồi cứ để mặc cho nước biển ở hiện trạng chẻ đôi đó, quả thật Fir-''awn và binh lính của hắn sẽ bị tiêu diệt bằng cách bị nhấn chìm dưới biển cả');
INSERT INTO "vn_mokhtasar" VALUES(44,25,'44:25','Fir-''awn và đám người của hắn đã để lại vô số vườn tược và dòng suối chảy!');
INSERT INTO "vn_mokhtasar" VALUES(44,26,'44:26','Và họ cũng để lại vô số vườn tược và các địa vị tốt đẹp!');
INSERT INTO "vn_mokhtasar" VALUES(44,27,'44:27','Và họ bỏ lại vô số thú vui của cuộc sống mà họ đã từng hưởng thụ!');
INSERT INTO "vn_mokhtasar" VALUES(44,28,'44:28','Đúng như thế, những gì đã xảy ra với họ TA đã mô tả cho các ngươi, và TA đã để lại những ngôi vườn, những cánh đồng, và những địa vị của họ cho cộng đồng khác đó là con cháu của Israel.');
INSERT INTO "vn_mokhtasar" VALUES(44,29,'44:29','Bởi thế, trời và đất chẳng ai khóc thương cho Fir-''awn và đám người của hắn khi bị nhấm chìm, và họ cũng không được trì hoãn hình phạt kể cả khi họ sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(44,30,'44:30','Và quả thật, TA đã giải cứu con cháu của Israel khỏi sự áp bức nhục nhã, khi mà Fir-''awn và đồng bọn của hắn giết chết những đứa con trai và chỉ để lại những phụ nữ để làm giúp việc của họ.');
INSERT INTO "vn_mokhtasar" VALUES(44,31,'44:31','TA đã giải cứu họ khỏi sự hành hạ của Fir-''awn, quả thật hắn là một kẻ tự cao ngạo mạn trước mệnh lệnh của Allah và tôn giáo của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(44,32,'44:32','Và quả thật, TA đã chọn con cháu của Israel nổi trội về kiến thức và sự hiểu biết vào thời đại của họ với việc vcử đến cho họ nhiều Sứ Giả.');
INSERT INTO "vn_mokhtasar" VALUES(44,33,'44:33','Và TA đã ban cho họ những bằng chứng và chứng cứ mà đích thân TA ban nó cho Musa, trong đó là những đặc ân rõ ràng cho họ như mật ong, chim cút và nhiều thứ khác.');
INSERT INTO "vn_mokhtasar" VALUES(44,34,'44:34','Rõ thật những kẻ đa thần dối trá này tuyên bố bác bỏ về việc phục sinh:');
INSERT INTO "vn_mokhtasar" VALUES(44,35,'44:35','Nó chỉ là cái chết đầu tiên của chúng tôi và không có sự sống nào sau đó, chúng tôi sẽ không được dựng sống lại sau cái chết.');
INSERT INTO "vn_mokhtasar" VALUES(44,36,'44:36','Vậy Ngươi - hỡi Muhammad - và những người theo Ngươi hãy đem tổ tiên của bọn ta đã chết sống trở lại đi, nếu Ngươi là người nói thật về những gì Ngươi tuyên bố rằng Allah dựng sống người chết để xét xử và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(44,37,'44:37','Phải chăng những kẻ đa thần không tin vào Ngươi - hỡi Thiên Sứ - họ tốt hơn về sức mạnh và sự phòng vệ, hay cộng đồng của Tubb''a và những cộng đồng trước họ như dân ''Ad và dân Thamud, TA đã tiêu diệt họ tất cả. Quả thật, họ là những kẻ tội đồ.');
INSERT INTO "vn_mokhtasar" VALUES(44,38,'44:38','Và TA đã không tạo hóa các tầng trời và đất và vạn vật trong nó để vui chơi với việc tạo hóa đó.');
INSERT INTO "vn_mokhtasar" VALUES(44,39,'44:39','Những gì TA đã tạo hóa ở các tầng trời và đất để cho thấy một trí tuệ vĩ đại, nhưng đa số những kẻ thờ đa thần lại không biết điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(44,40,'44:40','Quả thật, vào Ngày Phục Sinh là Ngày mà Allah phân loại đám bầy tôi của Ngài, tất cả đã được Ngài ấn định, đó là Ngày được Allah tập hợp tất cả lại.');
INSERT INTO "vn_mokhtasar" VALUES(44,41,'44:41','Ngày mà những người họ hàng, những người bạn thân thiết cũng không giúp ích được gì cho nhau, và cũng không bảo vệ nhau khỏi hình phạt của Allah. Bởi vì mọi quyền hành vào Ngày đó thuộc về Allah, không một ai có thể cầu xin Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(44,42,'44:42','Ngoại trừ người nào được Allah khoan dung do y được hưởng lợi từ việc làm thiện tốt. Quả thật, Allah Đấng Toàn Năng mà không ai có thể vượt qua Ngài, Đấng Khoan Dung cho đám bầy tôi nào biết sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(44,43,'44:43','Quả thật, cây Zaqqum mà Allah đã làm nẩy mầm nó trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(44,44,'44:44','Là thức ăn của kẻ phạm trọng tội, và đó là kẻ phủ nhận đức tin, hắn sẽ ăn loại trái cây kinh tởm đó.');
INSERT INTO "vn_mokhtasar" VALUES(44,45,'44:45','Loại trái cây này giống như một loại dầu màu đen, sôi sùng sục trong bụng của họ với sức nóng khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(44,46,'44:46','Như loại nước sôi với sức nóng đỉnh điểm.');
INSERT INTO "vn_mokhtasar" VALUES(44,47,'44:47','Và có tiếng nói vọng lại với những người Hỏa Ngục: Hãy túm lấy hắn rồi kéo hắn và ném mạnh vào giữa đám lửa cháy bùng.');
INSERT INTO "vn_mokhtasar" VALUES(44,48,'44:48','Sau đó xối lên đầu kẻ tội đồ này nước sôi rồi hình phạt của hắn sẽ không bị phá vỡ.');
INSERT INTO "vn_mokhtasar" VALUES(44,49,'44:49','Và hắn bị mỉa mai: Ngươi hãy nếm thử hình phạt đau đớn này đi! quả thật ngươi quyền thế không ai sánh bằng, ngươi cao quý trong cộng đồng ngươi cơ mà!.');
INSERT INTO "vn_mokhtasar" VALUES(44,50,'44:50','Quả thật, hình phạt này các ngươi đã hoài nghi về sự tồn tại của nó vào Ngày Phục Sinh. Vậy thì chắc chắn, các ngươi không còn nghi ngờ nữa khi đối diện nó.');
INSERT INTO "vn_mokhtasar" VALUES(44,51,'44:51','Quả thật, những người kính sợ Thượng Đế của họ bằng cách tuân theo những mệnh lệnh của Ngài và tránh xa những điều Ngài ngăn cấm thì nơi cư ngụ của họ sẽ được an toàn từ mọi điều xấu xa giáng xuống họ.');
INSERT INTO "vn_mokhtasar" VALUES(44,52,'44:52','Trong những vườn tược và những dòng suối chảy.');
INSERT INTO "vn_mokhtasar" VALUES(44,53,'44:53','Cư dân trong Thiên Đàng, họ được mặc áo lụa mịn màng và gấm thiêu, họ ở đối diện nhau, và không ai nhìn vào đối phương mà nói này nọ.');
INSERT INTO "vn_mokhtasar" VALUES(44,54,'44:54','Cũng như những gì TA ân sủng họ đã được nhắc đến, trong Thiên Đàng TA kết duyên cho họ với những người phụ nữ xinh đẹp có đôi mắt to tròn trắng tin và đen huyền.');
INSERT INTO "vn_mokhtasar" VALUES(44,55,'44:55','Trong đó, họ yêu cầu phục vụ để được mang đến mọi loại hoa quả mà họ muốn mà không bao giờ bị gián đoạn, và không bị gây hại.');
INSERT INTO "vn_mokhtasar" VALUES(44,56,'44:56','Họ sẽ ở trong đó muôn đời, sẽ không nếm thêm bất cứ cái chết nào ngoại trừ cái chết đầu tiên ở cuộc sống trần gian, và Thượng Đế của họ sẽ bảo vệ họ khỏi hình phạt Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(44,57,'44:57','Một ân điển và sự chiêu đãi từ Thượng Đế của họ, điều đã được nhắc nhở đó -những ai được Ngài cho vào Thiên Đàng, và bảo vệ họ khỏi Hỏa Ngục - nó là một chiến thắng vĩ đại mà không có chiến thắng nào so sánh được');
INSERT INTO "vn_mokhtasar" VALUES(44,58,'44:58','Quả thật, TA làm cho Qur''an này dễ hiểu và đơn giản với việc ban xuống bằng ngôn ngữ của Ngươi, ngôn ngữ Ả-rập - hỡi Thiên Sứ - may ra họ ghi nhớ.');
INSERT INTO "vn_mokhtasar" VALUES(44,59,'44:59','Vậy Ngươi hãy chờ xem chiến thắng của ngươi và thất bại của họ, quả thật họ cũng đang chờ đợi cái chết của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(45,1,'45:1','(Ha.Mim) đã được trình bày ở phần đầu của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(45,2,'45:2','Kinh Qur''an được ban xuống từ Allah, Đấng Toàn Năng mà không một ai có thể vượt qua Ngài, Đấng Toàn Tri trong việc tạo hóa, định đoạt và ban hành giáo luật của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(45,3,'45:3','Quả thật, trong các tầng trời và trái đất là minh chứng cho quyền năng của Allah duy nhất dành cho những người có đức tin, bởi quả thật chúng là những người biết xem xét các dấu hiệu.');
INSERT INTO "vn_mokhtasar" VALUES(45,4,'45:4','Và trong việc tạo hóa các ngươi - hỡi nhân loại - từ một hòn máu sau đó là cục thịt rồi thành hình hài, cũng như trong việc tạo hóa mà những gì Allah đã rải những loài động vật khắp nơi trên mặt đất được chứng minh về tính Độc Tôn của ngài cho những người có đức tin vững chắc rằng quả thật Allah là Đấng Tạo Hóa.');
INSERT INTO "vn_mokhtasar" VALUES(45,5,'45:5','Và trong việc ban đêm và ban ngày chuyển tiếp nhau, rồi trong việc Allah ban cơn mưa từ trên trời xuống để ban sự sống cho mặt đất sau khi đất đai đã khô cằn cây cối không thể mọc, và trong việc xoay chuyển hướng gió lúc thì theo chiều này, lúc thì theo hướng kia để giúp ích cho các ngươi. Các dấu hiệu đó giành cho những người biết suy nghĩ, để họ suy luận về sự Độc Tôn của Allah và năng lực của Ngài về sự phục sinh, và sự toàn năng của Ngài trên tất cả mọi việc.');
INSERT INTO "vn_mokhtasar" VALUES(45,6,'45:6','Đây là những câu Kinh và các dấu hiệu mà TA đọc cho Ngươi - hỡi Thiên Sứ - nghe chính là chân lý. Bởi quả thật, họ không tin vào lời nói của Allah được ban xuống cho bề tôi của Ngài và cho lập luận của Ngài, vậy thì họ sẽ tin nơi lời trình bày nào sau Ngài cũng như với lời lập luận nào sau Ngài khiến họ tin?!.');
INSERT INTO "vn_mokhtasar" VALUES(45,7,'45:7','Hình phạt từ Allah và sự hủy diệt dành cho tất cả những kẻ dối trá và mang nhiều tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(45,8,'45:8','Người vô đức tin đã nghe rõ các câu Kinh của Allah trong Qur''an đã được đọc cho y, rồi y vẫn cứ tiếp tục những hành động như y đã từng làm lúc trước về sự phủ nhận và phạm tội. Y tỏ ra kiêu ngạo về việc đi theo Chân Lý, giống như rằng y chưa từng được nghe những câu Kinh đó bao giờ. Thế, Ngươi hãy cho y biết - hỡi Thiên Sứ - về hậu quả của y vào Ngày Sau và đó là một hình phạt đau đớn đang chờ đợi y vào Ngày đó.');
INSERT INTO "vn_mokhtasar" VALUES(45,9,'45:9','Và khi y được truyền đạt điều gì đó từ Qur''an thì y đem nó ra làm trò cười để giễu cợt. Những kẻ mang tính cách đó, với sự giễu cợt về Qur''an họ sẽ nhận một hình phạt nhục nhã vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(45,10,'45:10','Trước mặt họ là Hỏa Ngục đang chờ đợi họ vào Ngày Sau, và những thứ tài sản mà họ đã kiếm được không giúp ích được gì cho họ trước Allah, và cũng chẳng giúp ích được gì cho họ từ những bụt tượng mà họ đem thờ phượng ngoài Ngài. Và vào Ngày Phục Sinh họ sẽ chịu một sự trừng phạt vô cùng khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(45,11,'45:11','Quyển kinh này mà TA đã ban xuống cho Thiên Sứ của TA là Muhammad đã chỉ đạo đến với con đường Chân Lý, và những kẻ phủ nhận những câu kinh mà Thượng Đế của họ ban xuống cho Thiên Sứ thì hình phạt dành cho họ vô cùng đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(45,12,'45:12','Allah là Đấng duy nhất chế ngự cho các ngươi - hỡi nhân loại - biển cả để cho thuyền buồm lưu thông theo sự chỉ đạo của Ngài, và để các ngươi tìm kiếm thiên lộc với các loại lợi nhuận cho phép. Và hy vọng các ngươi biết ơn hông ân của Allah đã ban cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(45,13,'45:13','Và Đấng Hiển Vinh đã chế ngự cho các ngươi vạn vật trong các tầng trời từ mặt trời, mặt trăng, và các vì sao. Và vạn vật trên mặt đất từ các dòng sông, cây cối, núi đồi và những thứ khác. Quả thật, việc chế ngự đó cho các ngươi là những dấu hiệu về quyền năng của Allah duy nhất, để cho nhóm người biết suy nghĩ trong các dấu hiệu đó, rồi họ ngẫm nghĩ về nó');
INSERT INTO "vn_mokhtasar" VALUES(45,14,'45:14','Ngươi hãy bảo - hỡi Thiên Sứ - những người tin tưởng vào Allah và công nhận Thiên Sứ của Ngài: Các ngươi hãy tha thứ cho người nào đã gây hại đến các ngươi trong số những người phủ nhận đức tin mà họ không quan tâm đến hồng ân của Allah và sự trừng phạt của Ngài. Bởi quả thật, Allah sẽ ban thưởng cho tất cả những người có đức tin biết nhẫn nại, và những người phủ nhận đức tin lầm lỗi họ sẽ nhận được hậu quả từ những điều họ đã làm trên trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(45,15,'45:15','Ai làm điều thiện tốt thì nhận được kết quả tốt đẹp cho y, vì Allah vô cùng giàu có vốn không cần đến việc làm của y. Còn ai làm điều xấu và tội lỗi thì hậu quả cho những việc làm xấu xa thì sẽ phải gánh hậu quả cho việc làm đó, vì Allah không bao giờ gây hại và làm điều xấu với bề tôi của Ngài. Rồi các ngươi sẽ trở về với duy nhất Ngài vào Ngày Sau để Ngài thưởng phạt bằng tất cả những điều xứng đáng.');
INSERT INTO "vn_mokhtasar" VALUES(45,16,'45:16','Và quả thật, TA đã ban cho con cháu của Israel Kinh Sách Tawrah và ưu đãi họ hơn nhân loại về trí tuệ, và TA tạo cho họ các vị Sứ Giả vĩ đại từ dòng tộc của Ibrahim, và TA cũng đã ban cho họ các loại bổng lộc tốt đẹp, và ưu đãi họ hơn nhân loại về kiến thức trong thời đại của họ.');
INSERT INTO "vn_mokhtasar" VALUES(45,17,'45:17','Và TA đã ban cho họ các bằng chứng rõ ràng để giải thích sự thật từ sự giả dối, nhưng chỉ từ sau lúc có được những bằng chứng được ban xuống cho Muhammad - cầu xin Allah ban sự bình an đến Người - thì họ đâm ra ganh tị và chia rẽ lẫn nhau trong vấn đề tranh giành quyền lực và địa vị. Quả thật, Thượng Đế của Ngươi - hỡi Thiên Sứ - sẽ phân chia giữa họ vào Ngày Phục Sinh về những điều mà họ đã chia rẻ lẫn nhau trên trần gian, rồi họ sẽ được làm rõ đâu là sự thật và đâu là sự giả dối.');
INSERT INTO "vn_mokhtasar" VALUES(45,18,'45:18','Sau đó TA đã đặt Ngươi trên con đường và lề lối theo giáo luật của TA, giáo luật mà TA đã cũng đã ra lệnh cho các Thiên Sứ trước Ngươi kêu gọi nhân loại đến với đức tin và hành đạo. Ngươi hãy bước theo giáo luật này và chớ đi theo những điều ước muốn của những kẻ không biết sự thật, vì ước muốn của họ lạc lối khỏi Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(45,19,'45:19','Quả thật, những kẻ không biết sự thật sẽ không thể giúp đỡ bất cứ điều gì cho Ngươi để thoát khỏi hình phạt của Allah khi Ngươi đã làm theo họ. Và quả thật, những kẻ sai quấy có cùng tín ngưỡng và cùng tư tưởng luôn là đồng đảng của nhau, tương trở lẫn nhau để chống đối người có đức tin. Còn Allah là Đấng Bảo Vệ những người biết kính sợ Ngài bằng cách tuân theo những mệnh lệnh của Ngài và tránh xa những điều Ngài ngăn cấm.');
INSERT INTO "vn_mokhtasar" VALUES(45,20,'45:20','Thiên Kinh Qur''an này được ban xuống cho Thiên Sứ của TA để soi rọi cho nhân loại biết phân biệt giữa sự thật và sự giả dối, chỉ dẫn đến chân lý, và cũng là một mồng ân dành cho nhóm người có đức tin vững chắc. Bởi quả thật, họ là những người được Qur''an dẫn dắt đến con đường ngay thẳng để họ được sự hài lòng từ Thượng Đế của họ, rồi Ngài cho họ vào Thiên Đàng và đẩy họ ra khỏi Hỏa Ngục');
INSERT INTO "vn_mokhtasar" VALUES(45,21,'45:21','Phải chăng những kẻ phủ nhận đức tin và phạm nhiều tội lỗi khác nghĩ rằng TA đặt họ ở vị trí giống như vị trí của những người tin tưởng vào Allah và làm những việc ngoan đạo, cụ thể như tất cả đều được xếp ngang hàng ở cõi đời này và Cõi đời sau ư? Thật xấu xa thay cho sự phán xét của họ!.');
INSERT INTO "vn_mokhtasar" VALUES(45,22,'45:22','Allah đã tạo hóa các tầng trời và đất với sự khôn ngoan tuyệt đỉnh, Ngài không tạo hóa họ một cách vô ích, với mục đích để thưởng phạt xứng đáng cho mỗi người với những điều tốt xấu họ đã làm. Và Allah không bao giờ bất công với họ bằng cách cắt bớt những điều tốt đẹp của họ, và cũng không tăng thêm những điều xấu xa nào cả');
INSERT INTO "vn_mokhtasar" VALUES(45,23,'45:23','Ngươi hãy nhìn xem - hỡi Thiên Sứ - kẻ làm theo ham muốn của bản thân và đặt nó như một thứ để hắn thờ phượng không dám làm trái lại nó. Quả thật, Allah đã bỏ mặc cho hắn lạc lối, bởi hắn rất xứng đáng bị lầm đường lạc lối, và Ngài đã niêm kính con tim của hắn để hắn không nghe được điều bổ ích, và Allah đặt trước tầm nhìn của hắn một bức chắn để ngăn hắn không nhìn thấy chân lý, vậy thì ai sẽ cho hắn có được chân lý sau khi Allah đã đánh lạc hướng hắn?! Chẳng lẻ các ngươi không nhớ tác hại của việc đi theo ham muốn của bản thân, và lợi ích của việc đi theo giáo lý của Allah?!.');
INSERT INTO "vn_mokhtasar" VALUES(45,24,'45:24','Và những kẻ vô đức tin phủ nhận việc phục sinh nói: Không có cuộc sống nào khác ngoài cuộc sống của chúng tôi trên trần gian này mà thôi, không có sự sống nào sau nó cả, một thế hệ chết đi sẽ không quay trở lại và thế hệ khác tiếp tục sống, và chúng tôi sẽ không chết trừ khi đêm và ngày kết thúc. Và họ không có một chút kiến thức nào cho việc phủ nhận sự phục sinh. Quả thật, họ chỉ suy đoán về điều đó, và sự suy đoán thì không có bất cứ điều gì là đúng sự thật cả.');
INSERT INTO "vn_mokhtasar" VALUES(45,25,'45:25','Và khi các lời mặc khải rõ rệt của TA được đọc ra cho những kẻ đa thần phủ nhận sự phục sinh thì họ không có lý lẽ nào để tranh luận ngoại trừ vài lời biện luận ngoan cố của họ với Thiên Sứ - cầu xin Allah ban sự bình an đến Người - và những bạn hữu của Người: Quý vị hãy làm sống lại cha ông đã khuất của chúng tôi nếu quý vị là những người nói thật trong vấn đề phục sinh sau cái chết.');
INSERT INTO "vn_mokhtasar" VALUES(45,26,'45:26','Ngươi hãy bảo họ - hỡi Thiên Sứ -: Allah làm cho các ngươi sống bằng việc tạo hóa các ngươi, rồi làm cho các ngươi chết, rồi sau khi chết Ngài tập hợp các ngươi lại vào Ngày Phục Sinh để xét xử và thưởng phạt. Ngày đó chắc chắn Nó sẽ đến không có chi phải nghi ngờ cả, thế nhưng đa số nhân loại lại không biết. Bởi vậy, họ không chuẩn bị cho Ngày đó bằng việc làm thiện tốt.');
INSERT INTO "vn_mokhtasar" VALUES(45,27,'45:27','Và Allah là Đấng duy nhất nắm quyền cai trị các tầng trời và trái đất, vậy nên không có bất cứ thứ gì giữa chúng xứng đáng được thờ phượng ngoài Ngài, và vào Ngày mà Giờ Tận Thế được thiết lập, Allah sẽ phục sinh lại cái chết để xét xử và thưởng phạt, Ngày đó nhóm người làm điều gian dối khi họ thờ phượng thần linh khác ngoài Allah sẽ thất bại, và họ tìm cách hầu dập tắt được sự thật và ấn định điều giả dối');
INSERT INTO "vn_mokhtasar" VALUES(45,28,'45:28','Ngươi sẽ thấy - hỡi Thiên Sứ - vào Ngày đó mỗi cộng đồng sẽ quỳ gối chờ đợi xem những gì y làm, mỗi cộng đồng sẽ được gọi đến để xem quyển sổ ghi chép các hành động mà các Thiên Thần đã lưu giữ trong đó. Ngày này các ngươi sẽ được đền đáp xứng đáng - hỡi nhân loại - với những gì mà các ngươi đã làm trên trần gian dù là việc thiện hay việc ác.');
INSERT INTO "vn_mokhtasar" VALUES(45,29,'45:29','Quyển sổ này của TA - quyển sổ mà các Thiên Thần của TA đã ghi chép trong đó những hành động của các ngươi - Nó được đọc lên sẽ làm bằng chứng nói lên sự thật về các ngươi. Quả thật, TA ra lệnh cho ghi chép lưu giữ những gì các ngươi đã làm trên trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(45,30,'45:30','Bởi thế, đối với những người có đức tin và hành đạo tốt sẽ được Thượng Đế của họ cho vào Thiên Đàng bằng sự khoan dung của Ngài. Đó là phần thưởng mà chỉ có Allah mới có thể ban cho họ, đó là sự thắng lợi vẻ vang không có sự thắng lợi nào so sánh được');
INSERT INTO "vn_mokhtasar" VALUES(45,31,'45:31','Và ngược lại, đối với những ai phủ nhận Allah thì họ sẽ bị chế giễu: Há các lời mặc khải của TA đã không được đọc ra cho các ngươi để mang đức tin đến với các ngươi hay sao? Tuy nhiên trước đây các ngươi là nhóm người sai quấy, các ngươi đã tích cóp sự vô đức tin và nhiều tội lỗi khác ?!');
INSERT INTO "vn_mokhtasar" VALUES(45,32,'45:32','Và khi có lời bảo họ: Quả thật, lời hứa của Allah - lời hứa mà Ngài đã hứa với bầy tôi của Ngài rằng chắc chắn Ngài sẽ phục sinh và thưởng phạt họ - là sự thật không có gì phải nghi ngờ trong đó, và Giờ Tận Thế là sự thật không nghi ngờ gì cả, vậy nên hãy thực hiện cho Ngày đó, thì các ngươi lại nói: chúng tôi không biết Giờ Tận Thế là gì, quả thật chúng tôi nghĩ khả năng Nó diễn ra là rất ít, và chúng tôi không thực sự tin rằng Nó sẽ diễn ra.');
INSERT INTO "vn_mokhtasar" VALUES(45,33,'45:33','Những tội lỗi mà chúng đã làm trên thế gian từ sự vô đức tin và trái lệnh sẽ được phơi bày cho chúng thấy và sự trừng phạt sẽ giáng xuống chúng bởi những gì mà chúng đã chế giễu khi chúng được cảnh báo.');
INSERT INTO "vn_mokhtasar" VALUES(45,34,'45:34','Allah phán với họ: Ngày hôm nay, TA bỏ mặc các ngươi trong Hỏa Ngục giống như việc các ngươi đã quên mất cuộc gặp gỡ Ngày này bởi các ngươi đã không chuẩn bị cho nó bằng đức tin iman và những việc làm ngoan đạo. Và giờ đây chỗ cư ngụ vĩnh viễn của các ngươi chính là Hỏa Ngục và các ngươi sẽ không tìm thấy một ai có thể giúp các ngươi tránh khỏi sự trừng phạt của TA.');
INSERT INTO "vn_mokhtasar" VALUES(45,35,'45:35','Đó là sự trừng phạt mà các ngươi phải hứng chịu do các ngươi đã lấy các lời phán của Allah làm trò đùa, và cuộc sống trần gian đã đánh lừa các ngươi bởi sự ngọt ngào và khoái lạc của nó. Bởi thế, Ngày hôm nay, những kẻ vô đức tin đã chế giễu các lời phán của Allah này đây không được trở ra khỏi Hỏa Ngục mà chúng phải vĩnh viễn ở trong đó, chúng sẽ không được đưa trở lại trần gian để làm việc ngoan đạo cũng như Thượng Đế của chúng sẽ không hài lòng về chúng.');
INSERT INTO "vn_mokhtasar" VALUES(45,36,'45:36','Bởi thế, mọi lời ca ngợi và tán dương đều dâng lên một mình Allah, Thượng Đế của các tầng trời và trái đất, và là Thượng Đế của vũ trụ và vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(45,37,'45:37','Allah sở hữu mọi Quyền Uy và sự Tối Cao trong các tầng trời và trái đất, và Ngài là Đấng Toàn Năng không một ai có thể vượt qua Ngài, và Ngài là Đấng Sáng Suốt và Khôn Ngoan trong việc tạo hóa, định đoạt, chi phối và ban hành.');
INSERT INTO "vn_mokhtasar" VALUES(46,1,'46:1','(Ha.Mim) Ý nghĩa của các chữ cái mở đầu này đã được trình bày ở phần đầu của chương Al-Baqarah.');
INSERT INTO "vn_mokhtasar" VALUES(46,2,'46:2','Kinh Qur''an được ban xuống từ Allah, Đấng Toàn Năng mà không một ai có thể vượt qua Ngài, Đấng Toàn Tri trong việc tạo hóa, định đoạt và ban hành giáo luật của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(46,3,'46:3','TA (Allah) đã không tạo ra trời đất và vạn vật giữa trời đất một cách vô nghĩa mà TA đã tạo ra tất cả những thứ đó vì chân lý mang giá trí lớn lao, trong đó, tiêu biểu như để đám bề tôi nhận biết TA qua các tạo vật rồi thờ phượng một mình TA, không tổ hợp với TA bất cứ thứ gì khác; để chúng có trách nhiệm với những gì mà chúng được giao quyền trông coi trên trái đất cho tới thời khắc được ấn định - thời khắc mà chỉ một mình Ngài biết rõ nó. Tuy nhiên, những kẻ vô đức tin nơi Allah quay lưng với những gì mà chúng được cảnh báo trong Kinh Sách của Ngài rồi chúng không bận lòng quan tâm đến sự cảnh báo đó.');
INSERT INTO "vn_mokhtasar" VALUES(46,4,'46:4','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ đa thần tin đã quay lưng với chân lý: Các người hãy cho ta biết về các thần linh bục tượng của các người mà các ngươi đã thờ phượng chúng ngoài Allah rằng chúng đã tạo ra được những gì trên trái đất này? Có phải chúng đã tạo ra núi non hùng vĩ ? Có phải chúng đã tạo ra sông ngòi dày đặc? Hoặc chúng là những đối tác chia sẻ với Allah trong việc tạo ra các tầng trời? Các người hãy mang đến cho ta một kinh sách được ban xuống từ nơi Allah trước Qur''an này hoặc những kiến thức để lại từ người xưa nếu các người nói thật trong việc các người đã khẳng định các thần linh bục tượng của các người xứng đáng được thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(46,5,'46:5','Không ai lầm lạc hơn những kẻ thờ phượng ngoài Allah các thần linh bục tượng, những thứ mà chúng không thể đáp lại lời khẩn cầu của y cho tới Ngày Phục Sinh. Những cái bục tượng được thờ phượng này không hề biết gì đến sự khấn vái của những kẻ thờ phượng chúng, chúng không mang lợi ích đến cho họ cũng chẳng gây hại được họ.');
INSERT INTO "vn_mokhtasar" VALUES(46,6,'46:6','Trên thế gian, các thần linh bục tượng không thể mang lại hữu ích gì cho họ và khi được triệu tập vào Ngày Phục Sinh thì chúng trở thành kẻ thù của những ai thờ phượng chúng, chúng sẽ xác nhận vô can với họ, phủ nhận việc họ đã từng thờ phượng chúng.');
INSERT INTO "vn_mokhtasar" VALUES(46,7,'46:7','Và khi các lời Mặc Khải của TA mà TA ban xuống cho các vị Thiên Sứ của TA được đọc ra thì những kẻ vô đức tin nơi Qur''an nói về những gì được ban xuống cho các vị Thiên Sứ của TA: Đây rõ ràng trò ma thuật chứ không phải là Mặc Khải từ nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(46,8,'46:8','Những kẻ thờ đa thần này nói rằng Muhammad đã sáng tác ra Qur''an rồi gán cho Allah? Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với chúng: Nếu Ta dối lừa Allah thì chắc chắn các ngươi không thể cứu thoát Ta khỏi sự phạt của Ngài nếu Ngài muốn trừng phạt Ta. Cho nên, tại sao Ta lại đem bản thân mình mạo hiểm trước sự trừng phạt của Ngài bằng việc lừa dối Ngài chứ?! Quả thật, Allah biết rõ những gì các ngươi đang giễu cợt với Qur''an và phỉ báng Ta. Nhưng Allah, một mình Ngài thôi đủ làm chứng cho Ta và các ngươi; Ngài là Đấng Hằng Tha Thứ tội lỗi cho những bề tôi nào quay về sám hối với Ngài, Ngài là Đấng khoan dung và thương xót họ.');
INSERT INTO "vn_mokhtasar" VALUES(46,9,'46:9','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần đã phủ nhận sứ mạng Nabi của Ngươi này đây: Ta không phải là vị Thiên Sứ đầu tiên được Allah phái đến mà các ngươi tỏ thái độ kinh ngạc bởi quả thật trước Ta đã có nhiều vị Thiên Sứ đã được cử phái đến; Ta không hề biết những gì Allah sẽ làm Ta và cũng không biết Ngài sẽ làm gì các ngươi ở trên cõi trần này. Thật ra Ta chỉ đi theo những gì mà Allah đã mặc khải cho Ta, Ta không nói cũng như không làm bất cứ điều gì ngoài những điều được mặc khải, và Ta thực sự chỉ là một người cảnh báo các ngươi về sự trừng phạt của Allah và trình bày nó cho các ngươi một cách rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(46,10,'46:10','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ phủ nhận này: Các ngươi hãy cho Ta biết, nếu Kinh Qur''an đến từ nơi Allah nhưng các ngươi lại phủ nhận Nó trong khi một nhân chứng thuộc con cháu Israel đã xác nhận rằng Nó đích thực đến từ nơi Allah dựa theo những gì được nói trong Kinh Cựu Ước (Tawrah) và đã tin nơi Nó còn các ngươi thì tự cao tự đại không chịu tin, như thế chẳng phải các ngươi là những kẻ làm điều sai quấy hay sao?! Quả thật, Allah không phù hộ đám người làm điều sai quấy đến được với chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(46,11,'46:11','Và những kẻ phủ nhận nơi Qur''an và nơi những gì mà vị Thiên Sứ của họ mang đến nói với những người có đức tin: Nếu những gì Muhammad mang đến là thật, là chân lý dẫn đến điều tốt đẹp thì chắc chắn bọn ta đã đến với Nó trước những kẻ nô lệ, nghèo hèn và yếu thế này. Bởi vì chúng không muốn được hướng dẫn bởi những vì mà vị Thiên Sứ của chúng mang đến cho chúng nên chúng nói: Đây chỉ là những điều giả dối của người xưa và bọn Ta không đi theo điều giả dối.');
INSERT INTO "vn_mokhtasar" VALUES(46,12,'46:12','Và trước Kinh Qur''an này đã có Kinh Tawrah được Allah ban xuống cho Musa (Moses) làm nguồn Chỉ Đạo đến với chân lý và là một Hồng Ân của Allah cho những ai trong con cháu của Israel có đức tin và đi theo. Và Kinh Qur''an này đây là Kinh Sách được ban xuống cho Muhammad bằng tiếng Ả Rập xác nhận lại những gì được nói trong Kinh Sách trước đó; và Kinh Qur''an này đến để cảnh báo những kẻ làm điều sai quấy bởi những việc làm Shirk và tội lỗi với Allah đồng thời để báo tin vui cho những người làm tốt trong mối quan hệ giữa họ với Đấng Tạo Hóa của họ và mối quan hệ giữa họ với những tạo vật của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(46,13,'46:13','Quả thật những người nói "Thượng Đế của chúng tôi là Allah, không có Đấng nào khác ngoài Ngài" rồi thẳng bước trên đức tin và những việc làm thiện tốt thì họ sẽ không cần lo sợ cho bản thân họ về những gì mà họ phải đối diện ở Đời Sau, và họ cũng không cần lo sợ bị mất mát những gì trên cõi trần cũng như những gì mà họ đã bỏ lại sau lưng.');
INSERT INTO "vn_mokhtasar" VALUES(46,14,'46:14','Những người được mô tả với những đặc điểm đó sẽ là những người bạn của Thiên Đàng, họ sẽ sống trong đó đời đời. Đó là một phần thưởng dành cho họ về những điều thiện tốt mà họ đã làm trên cõi trần.');
INSERT INTO "vn_mokhtasar" VALUES(46,15,'46:15','TA (Allah) đã ra lệnh cho con người hiếu thảo và ăn ở tử tế với cha mẹ của mình, phải yêu thương và chăm sóc họ một cách chu đáo lúc họ còn sống và làm những điều tốt đẹp cho họ (những điều không trái với giáo luật Islam) sau khi họ chết. Đặc biệt là đối với mẹ của y bởi bà đã mang nặng đẻ đau và cho y bú đến khi dứt sữa trong ba mươi tháng, rồi tiếp tục nuôi nấng y đến trưởng thành. Cho đến khi y trường thành toàn diện và chuẩn chạc trong suy nghĩ ở độ tuổi thì y cầu nguyện: Lạy Thượng Đế của bề tôi, xin Ngài ban cho bề tôi khả năng và sức mạnh để bề tôi tạ ơn các Ân Huệ của Ngài đã ban cho bề tôi và cho cha mẹ của bề tôi. Xin Ngài ban cho bề tôi khả năng và sức mạnh để bề tôi làm những việc làm thiện tốt mà Ngài hài lòng; xin Ngài chấp nhận các việc làm tốt đẹp từ bề tôi, xin Ngài cải thiện con cháu của bề tôi, bề tôi xin quay đầu sám hối với Ngài về tội lỗi của bề tôi,bề tôi xin tuân lệnh và thần phục một mình Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(46,16,'46:16','Những người đó là những người mà TA (Allah) chấp nhận những việc làm tốt nhất mà họ đã làm, xí xóa cho những điều xấu của họ, không bắt tội họ,bởi chúng là những người nằm trong thành phần cư dân của Thiên Đàng. Đây là lời hứa chân thật dành cho họ, và nó chắc chắn sẽ xảy đến đúng như vậy.');
INSERT INTO "vn_mokhtasar" VALUES(46,17,'46:17','Và kẻ đã nói với cha mẹ mình: Khốn nạn cho ông bà, ông bà hù dọa tôi rằng tôi sẽ được phục sinh sống lại từ cõi mộ sau khi đã chết qua bao thế kỷ trôi qua và những người xưa đã chết trong khi chẳng ai trong số họ được sống lại cả?! Cha mẹ của y cầu xin Allah phù hộ và che chở, xin Ngài hướng dẫn đứa con của mình đến với đức tin, cả hai nói: Thật khốn khổ cho con, nếu con không tin sự phục sinh thì giờ con hãy tin, quả thật lời hứa phục sinh của Allah là sự thật không có gì phải hoài nghi. Nhưng y khăng khăng bảo: Đây chỉ là những câu chuyện cổ tích của người xưa chứ không được khẳng định từ nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(46,18,'46:18','Những kẻ đó là những kẻ mà sự trừng phạt chắc chắn xảy đến với chúng, chúng nằm trong các cộng đồng vô đức tin trước chúng từ loài Jinn và loài người. Quả thật chúng là những kẻ thất bại bởi chúng khiến bản thân chúng và gia đình chúng đi vào Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(46,19,'46:19','Tất cả hai nhóm - nhóm của Thiên Đàng và nhóm của Hỏa Ngục - có những bậc cấp tùy theo các việc làm của họ. Nhóm của Thiên Đàng sẽ được ban cho những bậc cấp ở trên cao còn nhóm của Hỏa Ngục bị đày xuống các tầng cấp bên dưới. Và Allah sẽ thưởng phạt cho các việc làm của họ một cách thỏa đáng, không ai trong số họ bị đối xử bất công vào Ngày Phán Xét, không có công đức nào bị giảm đi và cũng không có tội lỗi nào bị tăng thêm.');
INSERT INTO "vn_mokhtasar" VALUES(46,20,'46:20','Và vào Ngày mà những kẻ phủ nhận nơi Allah và phủ nhận các vị Thiên Sứ của Ngài được đưa đến trước Hỏa Ngục để chịu hình phạt trong đó, có tiếng bảo khiển trách họ: Các ngươi đã lãng phí mất những phúc lành trong cuộc sống cõi trần của các ngươi, các ngươi đã hưởng thụ chúng, cho nên, Ngày hôm nay, các ngươi sẽ bị trừng phạt một cách nhục nhã bởi sự tự cao tự đại của các người trên trái đất, không chịu chấp nhận điều chân lý, và bởi sự bất tuân của các ngươi qua việc vô đức tin và phạm tội');
INSERT INTO "vn_mokhtasar" VALUES(46,21,'46:21','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ về Hud, người anh em của dân tộc ''Ad đã đến cảnh báo người dân của Y về một sự trừng phạt Allah sẽ xảy đến với họ, và họ sinh sống tại Al-Ahqaf, tên của một khu vực nằm ở phía nam của bán đảo Ả Rập. Quả thật, trước và sau Hud đã có các vị Thiên Sứ đến cảnh báo họ: Các ngươi chớ thờ phượng ai khác ngoài Allah, hãy thờ phượng một mình Ngài thôi, bởi quả thật Ta lo sợ cho các ngươi - hỡi người dân - về sự trừng phạt khủng khiếp vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(46,22,'46:22','Đám dân của Y nói: Có phải ngươi đến để bắt bọn ta từ bỏ việc thờ phượng các thần linh của bọn ta? Chắc chắn ngươi không bao giờ làm được điều đó. Bởi thế, ngươi cứ mang đến cho bọn ta thấy hình phạt mà ngươi đã hăm dọa bọn ta xem nếu ngươi nói thật.');
INSERT INTO "vn_mokhtasar" VALUES(46,23,'46:23','Hud nói: Quả thật, kiến thức về giờ khắc trừng phạt là ở nơi Allah còn ta thì không biết gì cả, ta chỉ là Thiên Sứ được cử phái đến truyền đạt lại cho các ngươi. Tuy nhiên, ta thấy các ngươi là đám người không hiểu biết về những gì có lợi cho các ngươi và những gì có hại cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(46,24,'46:24','Khi mà sự trừng phạt mà chúng hối thúc đến với chúng, chúng nhìn thấy một đám mây rộng lớn trên bầu trời đang kéo về hướng thung lũng của chúng thì chúng bảo: đám mây này sẽ mang mưa đến cho chúng ta. Hud nói với chúng: sự việc không phải như các ngươi đã nghĩ đâu, đó không phải là đám mây mang mưa đến cho các ngươi, đó là sự trừng phạt mà các ngươi đã hối thúc nó mau đến, đó là một trận cuồng phong bão táp mang một hình phạt rất đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(46,25,'46:25','Trận cuồng phong bão táp đã tàn phá tất cả mọi thứ mà nó đi qua theo lệnh của Allah. Thế là sáng ra không còn thấy bất cứ thứ gì ngoài những căn nhà đổ nát. Đây là phần đền đáp đầy đau đớn mà Allah dành cho những kẻ tội lỗi ngoan cố trên sự vô đức tin và trái lệnh của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(46,26,'46:26','Quả thật, TA (Allah) đã ban cho người dân của Hud những khả năng và sức mạnh, cái mà TA không cho các ngươi. Và TA đã ban cho họ thính giác để nghe, thị giác để nhìn và quan sát, và trái tim để hiểu. Tuy nhiên, thính giác, thị giác và trái tim của họ không giúp ích được họ tránh khỏi sự trừng phạt của Allah, họ cứ mãi phủ nhận các lời Mặc Khải của Allah, và sự trừng phạt vây kín họ bởi những gì họ đã giễu cợt khi Hud cảnh báo họ về nó.');
INSERT INTO "vn_mokhtasar" VALUES(46,27,'46:27','Quả thật, TA (Allah) đã trừng phạt các thị trấn xung quanh các ngươi - hỡi dân Makkah. TA đã hủy diệt đám dân ''Ad, Thamud, đám dân của Lut và đám dân của thị trấn Madyan. TA đã trình bày cho họ các lý lẽ và những bằng chứng bằng nhiều cách, mong rằng họ quay đầu tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(46,28,'46:28','Thế sao các bục tượng mà họ nhận lấy chúng làm các thần linh của họ và họ thờ cúng và dâng tế đến chúng không đến giúp họ?! Chắc chắn chúng không giúp được họ bởi chúng sẽ từ họ ra đi mất dạng. Đó thật ra là sự bịa đặt và lừa dối của họ để làm an lòng bản thân họ rằng các bục tượng này sẽ giúp ích và can thiệp cho họ trước Allah.');
INSERT INTO "vn_mokhtasar" VALUES(46,29,'46:29','Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại lúc TA (Allah) gửi một nhóm Jinn đến nghe Ngươi xướng Kinh Qur''an. Khi chúng đến nghe, chúng đã bảo nhau: các ngươi hãy giữ yên lặng để chúng ta có thể lắng nghe rõ ràng. Rồi khi Thiên Sứ Muhammad kết thúc cuộc đọc xướng thì chúng quay trở về cảnh báo đám dân của chúng về sự trừng phạt của Allah nếu chúng không tin nơi Qur''an này.');
INSERT INTO "vn_mokhtasar" VALUES(46,30,'46:30','Chúng nói với đám dân của chúng: Này hỡi dân ta, chúng tôi đã nghe được Kinh Sách được Allah ban xuống sau thời của Musa. Kinh Sách này xác nhận lại những điều trong các Kinh Sách được Allah ban xuống trước đây. Kinh Sách mà chúng tôi nghe được này đây hướng dẫn đến với chân lý và con đường ngay chính, và đó là con đường Islam.');
INSERT INTO "vn_mokhtasar" VALUES(46,31,'46:31','Hỡi dân ta, các ngươi hãy đáp lại lời mời gọi đến với chân lý của Muhammad, các ngươi hãy tin Người thực sự là vị Thiên Sứ của Allah rồi Allah sẽ tha thứ tội lỗi cho các ngươi và sẽ cho các ngươi an toàn thoát khỏi sự trừng phạt đau đơn đang chờ đợi các ngươi nếu các ngươi không đáp lại lời mời gọi của Người cũng nhưng không tin rằng Người là vị Thiên Sứ của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(46,32,'46:32','Và ai không đáp lại Muhammad để đến với những gì mà Y kêu gọi thì kẻ đó không bao giờ chạy trốn khỏi Allah trên trái đất này và sẽ không có ai làm người bảo hộ giúp y tránh được sự trừng phạt của Allah. Những người đó là những kẻ ở trong sự lầm lạc quá rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(46,33,'46:33','Há những kẻ thờ đa thần phủ nhận sự Phục Sinh không nhận thấy rằng Allah là Đấng đã táo hóa các tầng trời và trái đất và Ngài đã không hề gặp bất kỳ trở ngại nào trong việc tạo chúng ra bao la và vĩ đại như thế lại không có khả năng làm sống lại các cơ thể đã chết để Ngài phán xét và thưởng phạt hay sao?! Chắc chắn Ngài thừa sức cho sự việc đó bởi Ngài là Đấng Toàn Năng trên tất cả mọi thứ.Bởi thế,không ai có thể làm Ngài bất lực trước việc phục sinh người chết sống lại');
INSERT INTO "vn_mokhtasar" VALUES(46,34,'46:34','Và vào Ngày mà Hỏa Ngục được trưng ra cho những kẻ phủ nhận nơi Allah và các vị Thiên Sứ của Ngài để trừng phạt họ, Allah phán bảo họ bằng lời khiển trách: Sao sự trừng phạt này mà các ngươi nhìn thấy có thật không hay chỉ là điều giả dối như các ngươi đã nói trên cõi trần?! Họ đáp: Thưa Thượng Đế của bầy tôi, nó là sự thật. Allah phán bảo: Thế thì các ngươi hãy nếm sự trừng phạt bởi việc các ngươi đã phủ nhận nơi Allah');
INSERT INTO "vn_mokhtasar" VALUES(46,35,'46:35','Bởi thế - hỡi Thiên Sứ Muhammad, Ngươi hãy kiên nhẫn chịu đựng trước sự phủ nhận của cộng đồng Ngươi giống như các vị Thiên Sứ đầy cương nghị trước Ngươi: Nuh, Ibrahim, Musa và Ysa. Và Ngươi chớ nôn nóng về sự trừng phạt dành cho họ bởi rồi đây vào Ngày mà họ nhìn thấy sự trừng phạt ở cõi Đời Sau thì cứ có cảm tưởng rằng họ chỉ ở trên gian trong một giờ của ban ngày. Kinh Qur''an được ban xuống cho Muhammad này đây là một Thông Điệp đầy trọn vẹn cho loài người và loài Jinn. Quả thật, sự trừng phạt chỉ dành cho những kẻ bất tuân Allah bằng việc phủ nhận và phạm tội');
INSERT INTO "vn_mokhtasar" VALUES(47,1,'47:1','Những kẻ phủ nhận nơi Allah và lôi kéo nhân loại ra khỏi tôn giáo của Allah thì những việc làm thiện của chúng sẽ không có giá trị đối với Allah');
INSERT INTO "vn_mokhtasar" VALUES(47,2,'47:2','Và những người có đức tin nơi Allah và làm những việc ngoan đạo, tin những gì được Allah ban xuống cho Thiên sứ của Ngài là Muhammad, và tin Nó là chân lý đến từ Thượng Đế của họ thì họ là những người sẽ được Allah xóa tội và được Ngài cải thiện mọi vụ việc của thế tục cũng như của Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(47,3,'47:3','Phần đền bù dành cho hai nhóm người được đề cập đó là do những ngươi phủ nhận nơi Allah luôn đi theo điều ngụy tạo còn những người có đức tin nơi Allah và Thiên Sứ của Ngài thì luôn đi theo chân lý từ nơi Thượng Đế của họ. Bởi thế, phần đền bù có sự khác biệt tùy theo con đường họ đi như Allah đã phân xử về hai nhóm: Một nhóm có đức tin và một nhóm vô đức tin.Allah dùng hình ảnh của họ để làm thí dụ cho nhân loại với mục đích để nhân loại lấy đó làm bài học.');
INSERT INTO "vn_mokhtasar" VALUES(47,4,'47:4','Hỡi những người có đức tin, khi các ngươi giáp mặt với những kẻ vô đức tin trên chiến trường thì các ngươi hãy đánh vào cổ của chúng bằng thanh gươm của các ngươi, các ngươi cứ tiếp tục chiến đấu với chúng cho đến khi các ngươi thắng được chúng, và khi các ngươi đã thắng được chúng thì các ngươi hãy trói chúng lại làm tù binh, và khi các ngươi đã bắt chúng làm tù binh thì các ngươi được quyền chọn lựa xử lý chúng tùy theo những gì mang tính cải thiện, các ngươi có thể thả chúng tự do mà không đòi tiền chuộc hoặc các ngươi có thể đòi tiền chuộc nếu các ngươi muốn, cứ như thế cho đến khi cuộc chiến chấm dứt. Việc Allah thử thách những người có đức tin bởi những người vô đức tin thông qua sự chiến đấu và người này thắng người kia là lệnh truyền của Allah. Quả thật, nếu muốn Ngài sẽ giúp những người vô đức tin giành chiến thắng mà không đến cuộc chiến, tuy nhiên bởi vì đó luật Jihad để Ngài thử thách người này trước người kia, thử thách người có đức tin đi chiến đấu và người có đức tin không tham gia chiến đấu. Bởi thế, người có đức tin bị giết sẽ đi vào Thiên Đàng còn kẻ vô đức tin bị giết sẽ đi vào Hỏa Ngục. Và những ai hy sinh trên con đường của Allah thì Ngài sẽ không bao giờ làm mất công lao của họ.');
INSERT INTO "vn_mokhtasar" VALUES(47,5,'47:5','Ngài (Allah) sẽ phù hộ họ đi theo chân lý trong cuộc sống trần tục của họ và Ngài sẽ cải thiện các vụ việc của họ.');
INSERT INTO "vn_mokhtasar" VALUES(47,6,'47:6','Và vào Ngày Phán Xét, Ngài sẽ thu nhận họ vào Thiên Đàng, nơi mà Ngài đã trình bày về một số đặc điểm của nó cho họ biết và Ngài đã cho họ biết chỗ ở của họ trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(47,7,'47:7','Hỡi những người có đức tin nơi Allah và làm những gì Ngài đã sắc lệnh cho các ngươi, nếu các ngươi giúp Allah bằng cách giúp vị Nabi và tôn giáo của Ngài cũng như chiến đấu với những kẻ vô đức tin thì Ngài sẽ giúp các ngươi giành chiến thắng trước kẻ thù của các ngươi và Ngài sẽ làm vững chắc bàn chân của các ngươi trên chiến trường khi giáp mặt với kẻ thù.');
INSERT INTO "vn_mokhtasar" VALUES(47,8,'47:8','Những kẻ phủ nhận nơi Allah và Thiên Sứ của Ngài thì sẽ thất bại và bị tiêu diệt và Allah sẽ không ban cho họ bất kỳ ân phước nào từ những việc làm thiện tốt của họ');
INSERT INTO "vn_mokhtasar" VALUES(47,9,'47:9','Sự trừng phạt đó xảy ra cho họ là bởi vì họ đã ghét những điều Allah ban xuống cho vị Thiên Sứ của Ngài trong Qur''an - một Kinh Sách chứa đựng sự kêu gọi đến với việc tôn thờ duy nhất một mình Allah - nên Allah sẽ không chấp nhận những việc làm thiện tốt của họ. Bởi thế, họ sẽ mất mát ở trên đời này và kể cả cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(47,10,'47:10','Chẳng lẽ những kẻ vô đức tin này không đi khắp mọi miền đất nước để quan sát và suy ngẫm về kết cục của những kẻ đã phủ nhận Allah trước họ như thế nào ư? Quả thật, những kẻ đã phủ nhận trước họ đã có một kết cuộc đau đớn, Allah đã tàn phá nhà cửa của họ, hủy diệt họ, tài sản và con cái của họ;và chắc chắn những kẻ vô đức tin trong mọi thời đại đều sẽ gặp kết cuộc giống như thế.');
INSERT INTO "vn_mokhtasar" VALUES(47,11,'47:11','Phần đền bù cho hai nhóm người được đề cập đó là bởi vì Allah là Đấng Bảo Hộ cho những người có đức tin trong khi những kẻ vô đức tin thì không có một vị bảo hộ nào giúp đỡ họ cả.');
INSERT INTO "vn_mokhtasar" VALUES(47,12,'47:12','Quả thật Allah sẽ thu nhận những ai tin tưởng nơi Allah và vị Thiên Sứ của Ngài đồng thời làm việc thiện tốt vào Thiên Đàng của Ngài với những Ngôi Vườn có các dòng sông chảy phía dưới các tòa lâu đài. Ngược lại, những kẻ phủ nhận nơi Allah và vị Thiên Sứ của Ngài, họ được hưởng thụ trên cõi trần, họ cứ ăn uống giống như thú vật bởi cái quan trọng mà họ quan tâm chỉ có cái bụng và nhu cầu tình dục của họ.Và vào Ngày Phục Sinh, họ sẽ đời đời kiếp kiếp sống trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(47,13,'47:13','Đã bao nhiêu thị trấn của các cộng đồng trước đây giàu có và hùng mạnh hơn rất nhiều về con cái và tài sản so với trị trấn Makkah, nơi đã trục xuất Ngươi (Muhammad) rời bỏ gia đình và người thân của Ngươi, bị TA (Allah) hủy diệt vì tội đã phủ nhận các vị Thiên Sứ của họ và họ đã không có một vị cứu tinh nào giúp họ tránh khỏi sự trừng phạt một khi nó đến?! Bởi thế, việc hủy diệt dám dân của thị trấn Makkah chẳng làm khó khăn cho TA một khi TA muốn.');
INSERT INTO "vn_mokhtasar" VALUES(47,14,'47:14','Một người có được các bằng chứng rõ rệt từ Thượng Đế của y và y thờ phượng Ngài trên những bằng chứng rõ ràng đó có giống hay không với một kẻ bị Shaytan bày vẻ bởi những việc làm tội lỗi và hắn đi theo dục vọng của bản thân trong việc thờ phượng các bục tượng và phủ nhận các vị Thiên Sứ?!');
INSERT INTO "vn_mokhtasar" VALUES(47,15,'47:15','Đặc điểm của Thiên Đàng mà Allah đã hứa với những người ngoan đạo - thực hiện các mệnh lệnh của Allah và tránh xa những điều Ngài nghiêm cấm - là một nơi trong đó có những con sông nước ngọt mãi mãi không đổi mùi, những con sông sữa không đổi vị, những con sông rượu tuyệt hảo dành cho người thích uống, và những con sông mật ong tinh khiết và thơm ngon. Trong đó, còn có đủ loại trái cây mà họ ưa thích, và trên hết những thứ đó là họ được Allah tha thứ tội lỗi, Ngài sẽ không bắt tội họ nữa. Thế những người được ban cho phần thưởng này có ngang bằng với những kẻ đời đời kiếp kiếp sống trong Hỏa Ngục và bị cho uống với một loại nước cực nóng cắt đứt ruột gan trong bụng của họ hay không?!');
INSERT INTO "vn_mokhtasar" VALUES(47,16,'47:16','Hỡi Thiên Sứ Muhammad, một số những kẻ giả tạo đức tin Munafiq lắng nghe Ngươi (đọc Qur''an và thuyết giảng) một cách chú ý nhưng chúng không đón nhận mà còn quay lưng bỏ đi nữa, khi chúng rời khỏi Ngươi thì chúng nói với những kẻ mà Allah đã ban cho họ kiến thức: y nói gì mà chẳng hiểu chi hết? Chúng muốn nói rằng chúng không đón nhận, chúng chính là những kẻ đã bị Allah đóng dấu niêm kín trái tim của chúng để chúng không đón nhận được điều tốt lành, chúng chỉ biết đi theo dục vọng của chúng và không hề quan tâm gì đến điều chân lý cả');
INSERT INTO "vn_mokhtasar" VALUES(47,17,'47:17','Những ai được hướng dẫn đến chân lý, đi theo những gì mà Thiên Sứ Muhammad mang đến cho họ thì sẽ được Thượng Đế của họ tăng thêm sự hướng dẫn cho họ và phù hộ họ điều tốt lành và giúp họ tránh được Hỏa Ngục ở cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(47,18,'47:18','Có phải những kẻ vô đức tin đang đợi giờ khắc tận thế đến với chúng một cách đột ngột mà chúng không ngờ tới?! Quả thật những dấu hiệu báo trước về giờ khắc tận thế đã xảy ra, trong đó là việc Thiên Sứ Muhammad nhận lãnh sứ mạng, mặt trăng chẻ làm đôi, vậy làm sao chúng nhớ khi giờ tận thế đến với chúng?');
INSERT INTO "vn_mokhtasar" VALUES(47,19,'47:19','Hỡi Thiên Sứ Muhammad, Ngươi phải chắc chắn một cách kiên định rằng không có thần linh nào xứng đáng được thờ phượng ngoài Allah và Ngươi hãy xin Ngài tha thứ tội lỗi cho Ngươi, cho các những người có đức tin nam nữ. Quả thật, Allah biết rất rõ mọi hành động và sinh hoạt của các ngươi trong ban ngày của các ngươi và chỗ cư ngụ của các ngươi trong ban đêm; không có bất cứ thứ gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(47,20,'47:20','Và những người có đức tin nơi Allah nói - với ý mong ước Allah ban xuống cho vị Thiên Sứ của Ngài một chương Kinh chứa đựng luật chiến đấu -: Sao Allah không ban xuống một chương Kinh trong đó nói về việc chiến đấu chứ?! Nhưng rồi khi Allah ban chương Kinh trình bày các giáo luật về việc chiến đấu. Hỡi Thiên sứ - Ngươi sẽ thấy những kẻ mà lòng của chúng luôn mang sự nghi ngờ của những kẻ giả tạo đức tin nhìn Ngươi với cái nhìn thất thần vì quá sợ hãi. Bởi thế Allah cảnh báo sự trừng phạt dành cho chúng vì lý do chúng sợ hãi và từ bỏ việc chiến đấu');
INSERT INTO "vn_mokhtasar" VALUES(47,21,'47:21','Việc chúng vâng lệnh Allah và nói lời tử tế không chứa ý nghĩa bất tuân là điều tốt cho chúng, và khi nào việc chiến đấu bắt buộc phải diễn ra thì nếu chúng chân thật với Allah trong đức tin Iman của chúng và vâng lời Ngài thì điều đó tốt cho chúng hơn việc giả tạo đức tin và bất tuân Allah.');
INSERT INTO "vn_mokhtasar" VALUES(47,22,'47:22','Nếu các ngươi quay lưng, không tin nơi Allah và không tuân lệnh Ngài thì có lẽ việc các ngươi làm điều thối nát trên trái đất bằng sự vô đức tin và tội lỗi của các ngươi cũng như việc các ngươi cắt đứt tình bà con ruột thịt là điều chắc chắn; tình trạng đó cũng sẽ giống như tình trạng của các ngươi trong thời Jahiliyah (thời tiền Islam).');
INSERT INTO "vn_mokhtasar" VALUES(47,23,'47:23','Những kẻ được mô tả với những việc làm thối nát trên trái đất cũng như cắt đứt tình bà con ruột thịt là những kẻ mà Allah đã đẩy chúng xa khỏi lòng thương xót của Ngài. Chính vì thế, Ngài đã làm cho đôi tai của chúng không nghe được điều chân lý và làm cho mắt của chúng như mù không nhìn thấy điều đúng đắn');
INSERT INTO "vn_mokhtasar" VALUES(47,24,'47:24','Sao những kẻ quay lưng không chịu suy ngẫm Qur''an?! Nếu chúng chịu suy ngẫm thì chắc chắn Qur''an sẽ hướng dẫn chúng đến mọi điều tốt đẹp và sẽ giúp chúng tránh xa mọi điều xấu,hoặc phải chăng trái tim của chúng đã bị niêm kín nên sự khuyên răn không đến được với chúng cũng như lời nhắc nhở không giúp ích được gì cho chúng?!');
INSERT INTO "vn_mokhtasar" VALUES(47,25,'47:25','Quả thật, những kẻ rời bỏ đức tin Iman để quay lại với sự vô đức tin và sự giả tạo sau khi các bằng chứng tố cáo chúng đã được thiết lập và sự chân thật của Thiên Sứ Muhammad đã trình bày rất rõ ràng cho chúng, thì chúng là những kẻ đã bị Shaytan xúi giục đến với sự vô đức tin và sự giả tạo và đã bị nó hứa những điều hão huyền.');
INSERT INTO "vn_mokhtasar" VALUES(47,26,'47:26','Sự lầm lạc xảy đến với chúng,bởi vì chúng đã nói bí mật với những kẻ đa thần ganh ghét điều được mặc khải cho Thiên Sứ Muhammad: Chúng tôi sẽ tuân theo các người ở một phần của sự việc chẳng hạn khuyến khích chiến đấu. Và Allah biết rõ những gì mà chúng bí mật và che giấu,không có điều gì có thể giấu giếm được Ngài; Ngài sẽ phơi bày những gì Ngài muốn cho vị Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(47,27,'47:27','Rồi đây, Ngươi - Thiên Sứ Muhammad - sẽ thấy hoàn cảnh đau đớn khủng khiếp của chúng như thế nào khi chúng bị các Thiên Thần rút hồn lúc chết. Các Thiên Thần sẽ đánh vào mặt và lưng của chúng bằng những chiếc gậy sắt');
INSERT INTO "vn_mokhtasar" VALUES(47,28,'47:28','Sự trừng phạt đó là bởi vì chúng đã đi theo những điều làm Allah phẫn nộ từ sự vô đức tin, giả tạo đức tin, và chống đối Allah và Thiên Sứ của Ngài; chúng đã ghét những điều làm Allah hài lòng từ đức tin nơi Allah và đi theo vị Thiên Sứ của Ngài. Bởi vậy, Allah không công nhận các việc làm thiện tốt của chúng và không ban thưởng cho các việc làm đó của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(47,29,'47:29','Có phải những kẻ mà trong lòng của chúng luôn có sự hoài nghi vì căn bệnh giả tạo đức tin rằng Allah không bao giờ phơi bày sự oán ghét trong lòng của chúng ra ngoài để phân biệt đức tin chân thành với đức tin giả dối ư?!');
INSERT INTO "vn_mokhtasar" VALUES(47,30,'47:30','Nếu muốn, TA (Allah) sẽ phơi bày cho Ngươi - hỡi Thiên Sứ Muhammad - biết ai là những kẻ giả tạo đức tin. Tuy nhiên, Ngươi có thể nhận biết chúng qua những đặc điểm và bản chất thường thấy của chúng cũng như Ngươi sẽ nhận ra chúng qua giọng nói của chúng.Và chắc chắn Allah biết rõ các việc làm của các ngươi, không một điều gì có thể che giấu được Ngài và Ngài sẽ thưởng phạt xứng đáng cho các ngươi');
INSERT INTO "vn_mokhtasar" VALUES(47,31,'47:31','Hỡi những người có đức tin, chắc chắn TA (Allah) sẽ thử thách các ngươi qua việc đấu tranh và đánh kẻ thù cho đến khi TA biết rõ ai trong các ngươi là người chiến đấu thực sự cho con đường chính nghĩa của TA, ai trong các ngươi là những người kiên nhẫn chịu đựng trong việc chiến đấu với kẻ thù cả Ngài, và TA thử thách các ngươi để biết trong các ngươi ai là người trung thực và ai là kẻ giả dối.');
INSERT INTO "vn_mokhtasar" VALUES(47,32,'47:32','Quả thật những kẻ phủ nhận nơi Allah và Thiên Sứ của Ngài,luôn cản trở tôn giáo của Ngài bằng vũ lực của họ và bằng sức lực của người khác, và chống lại Thiên Sứ của Ngài sau khi đã rõ rằng Muhammad đích thực là vị Thiên Sứ của Allah, không bao giờ có thể hại được Allah bất cứ điều gì, mà ngược lại, họ chỉ làm hại bản thân họ, và Allah sẽ không chấp nhận các việc làm thiện tốt của họ và sẽ không ban cho họ bất cứ phần ân thưởng nào từ các việc làm thiện tốt đó.');
INSERT INTO "vn_mokhtasar" VALUES(47,33,'47:33','Hỡi những người có đức tin nơi Allah, các ngươi hãy chấp hành những điều Ngài ban hành, tuân lệnh Ngài và vâng lời vị Thiên Sứ của Ngài, và các ngươi chớ làm mất đi giá trị việc làm của các ngươi bằng sự vô đức tin và Riya'' (sự phô trương, không hoàn toàn hướng về Allah).');
INSERT INTO "vn_mokhtasar" VALUES(47,34,'47:34','Quả thật, những ai phủ nhận nơi Allah và cản trở bản thân cũng như cản trở thiên hạ không đi theo tôn giáo của Ngài, rồi họ chết trên tình trạng vô đức tin trước khi sám hối thì Allah sẽ không bao giờ tha thứ tội lỗi cho họ, Ngài sẽ bắt tội họ và tống cổ họ vào Hỏa Ngục để chịu sự trừng phạt muôn đời');
INSERT INTO "vn_mokhtasar" VALUES(47,35,'47:35','Bởi thế hỡi những người có đức tin, các ngươi chớ yếu hèn khi đối mặt với kẻ thù của các ngươi, và các ngươi chớ kêu gọi chúng đến với hòa bình trước khi chúng kêu gọi các ngươi trong lúc các ngươi là những người nắm ưu thế. Allah luôn ở cạnh các ngươi để giúp đỡ và phù hộ các ngươi, và chắc chắn Ngài sẽ không bao giờ cắt giảm bất cứ ân phước nào từ các việc làm của các ngươi.Ngược lại, Ngài sẽ gia tăng thêm ân phước cho các ngươi cũng như ưu ái cho các ngươi');
INSERT INTO "vn_mokhtasar" VALUES(47,36,'47:36','Quả thật, cuộc sống trần gian là cuộc vui chơi phù phiếm. Vì vậy, người khôn ngoan luôn nhận thức đúng đắn sẽ không bận tâm nhiều tới cuộc sống trần gian này mà quên mất cuộc sống cõi Đời Sau. Nếu các ngươi có đức tin nơi Allah và Thiên Sứ của Ngài cũng như kính sợ Allah bằng cách tuân theo mệnh lệnh của Ngài cũng như tránh xa các điều cấm thì Ngài sẽ ban thưởng cho các ngươi một cách trọn vẹn,và Ngài sẽ không đòi hỏi các ngươi tài sản mà Ngài chỉ yêu cầu các ngươi trích một phần cho nghĩa vụ Zakah.');
INSERT INTO "vn_mokhtasar" VALUES(47,37,'47:37','Nếu Ngài đòi các ngươi toàn bộ tài sản của các ngươi thì điều đó sẽ gây áp lực cho các ngươi và chắc chắn các ngươi sẽ trở nên keo kiệt mà cố ôm giữ tài sản của mình. Tuy nhiên, Ngài đã lấy khỏi con tim các ngươi nỗi cay ghét về việc chi dùng cho con đường chính nghĩa của Ngài. Ngài không đòi hỏi toàn bộ tài sản của các ngươi là vì Ngài thương xót các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(47,38,'47:38','Các ngươi là những người được kêu gọi chi dùng một phần tài sản của các ngươi cho con đường chính nghĩa của Allah, Ngài không đòi hỏi các ngươi chi dùng toàn bộ tài sản của các ngươi. Tuy nhiên, trong các ngươi có người không chịu chi dùng theo yêu cầu vì sự keo kiệt. Và ai keo kiệt chi dùng một phần tài sản của mình cho con đường chính nghĩa của Allah thì thật ra người đó chỉ keo kiệt với chính bản thân mình mà thôi bởi vì y đã tự ngăn cấm mình hưởng ân phước của việc chi dùng đó. Và Allah là Đấng Giàu Có, Ngài bất cần đến việc chi dùng tài sản của các ngươi, trong khi các ngươi là những kẻ nghèo hèn rất cần đến Ngài. Nếu các ngươi quay lưng với Islam để trở về sự vô đức tin thì Ngài sẽ tiêu diệt các ngươi, rồi Ngài sẽ mang đến một cộng đồng khác để thay thế các ngươi, nhưng họ sẽ không giống như các ngươi mà sẽ là những người tuân lệnh và vâng lời Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(48,1,'48:1','Hỡi Thiên Sứ Muhammad, quả thật TA (Allah) đã giúp Ngươi giành chiến thắng bởi một cuộc thắng lợi rõ ràng, đó là Hiệp ước Al-Hudaibiyah.');
INSERT INTO "vn_mokhtasar" VALUES(48,2,'48:2','Để Allah tha thứ cho Ngươi (Muhammad) về tội lỗi của Ngươi trước sự thắng lợi này cũng như những tội lỗi sau đó; và để Ngài hoàn tất ân huệ của Ngài cho Ngươi qua việc làm cho tôn giáo của Ngươi thắng lợi, và để Ngài hướng dẫn Ngươi trên Con Đường Ngày Chính, đó là tôn giáo Islam.');
INSERT INTO "vn_mokhtasar" VALUES(48,3,'48:3','Allah đã giúp Ngươi giành thắng lợi trước kẻ thù của Ngươi bằng một sự trợ giúp hùng mạnh, không ai có thể chống lại nó.');
INSERT INTO "vn_mokhtasar" VALUES(48,4,'48:4','Allah là Đấng ban sự an bình và thanh thản trong lòng của những người có đức tin để gia tăng thêm đức tin Iman của họ. Và Allah là Đấng duy nhất có các binh lính ở trên các tầng trời và các binh lính ở dưới mặt đất, Ngài điều khiển các binh lính của Ngài để ủng hộ và trợ giúp ai mà Ngài muốn trong đám bề tôi của Ngài. Và Allah là Đấng hằng biết điều cải thiện đám bề tôi của Ngài và Ngài là Đấng Sáng Suốt trong việc triển khai và điều hành sự trợ giúp và ủng hộ.');
INSERT INTO "vn_mokhtasar" VALUES(48,5,'48:5','Để Ngài thu nhận những người có đức tin nơi Ngài và Thiên Sứ của Ngài vào các Ngôi Vườn Thiên Đàng, bên trong có những dòng sông chảy phía dưới các tòa lâu đài; và để Ngài xóa bỏ các điều xấu của họ, Ngài không bắt tội họ. Và tất cả những ân thưởng đó - từ việc được thu nhận vào Thiên Đàng, được xóa đi các tội lỗi - ở nơi Allah là một chiến thắng rất vĩ đại mà không có chiến thắng nào so sánh được');
INSERT INTO "vn_mokhtasar" VALUES(48,6,'48:6','Ngài sẽ trừng phạt những kẻ giả tạo đức tin, nam cũng như nữ; trừng phạt những kẻ thờ đa thần, nam cũng như nữ, những kẻ mà chúng nghĩ rằng Allah không mang đến sự chiến thắng cho tôn giáo của Ngài, không đề cao lời phán của Ngài cho nên vòng xoáy trừng phạt quay trở lại với chúng. Và Allah giận dữ chúng vì sự phủ nhận của chúng cũng như việc chúng nghĩ xấu về Ngài; Ngài đuổi chúng ra xa khỏi lòng thương xót của Ngài; và Ngài đã chuẩn bị cho chúng Hỏa Ngục làm nơi cư ngụ muôn đời vào Đời sau.Đó là một điểm đến vô cùng tồi tệ sẽ dành cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(48,7,'48:7','Và Allah có các binh lính trên các tầng trời và các binh lính ở dưới mặt đất để ủng hộ và trợ giúp những ai mà Ngài muốn trong đám bề tôi của Ngài. Và Allah là Đấng Toàn Năng không ai có thể chiến thắng được Ngài và Ngài là Đấng Sáng Suốt trong việc tạo hóa, định đoạt và điều hành.');
INSERT INTO "vn_mokhtasar" VALUES(48,8,'48:8','Hỡi Thiên Sứ Muhammad, quả thật, TA (Allah) đã cử phái Ngươi đến để làm một nhân chứng cho cộng đồng tín đồ của Ngươi vào Ngày Phán Xét; để làm một vị báo tin mừng cho những người có đức tin về những điều mà TA đã chuẩn bị cho họ trên cõi trần từ việc ban cho họ sự thắng lợi và khả năng thích ứng và chinh phục cũng như những gì mà TA đã chuẩn bị cho họ ở Đời Sau từ Thiên Đàng Hạnh Phúc; đồng thời TA cử phái Ngươi đến để làm một người cảnh báo cho những kẻ vô đức tin về những thất bại và sự nhục nhã mà TA đã chuẩn bị cho chúng trên cõi trần dưới tay của những người có đức tin, và về những gì mà TA đã chuẩn bị cho chúng nơi cõi Đời Sau từ sự trừng phạt đau đớn, cái mà nó đang chờ đợi chúng.');
INSERT INTO "vn_mokhtasar" VALUES(48,9,'48:9','Mong rằng các ngươi tin nơi Allah, tin nơi Thiên Sứ của Ngài, tôn vinh Thiên Sứ của Ngài và ủng hộ Y, và hy vọng rằng các ngươi biết tán dương và ca tụng Allah sáng chiều.');
INSERT INTO "vn_mokhtasar" VALUES(48,10,'48:10','Hỡi Thiên Sứ Muhammad, quả thật những ai thề trung thành với Ngươi trong việc sẽ cùng Ngươi chiến đấu tới cùng với những người thờ đa thần Makkah chính là những người thề trung thành với Allah bởi Ngài là Đấng đã ra lệnh cho họ phải chiến đấu với những kẻ thờ đa thần và Ngài là Đấng sẽ ban thưởng cho họ. Tay của Allah nằm bên trên tay của họ trong lúc họ thề trung thành với Ngươi. Và Allah luôn quan sát và theo dõi họ, không có điều gì từ nơi họ có thể che giấu được Ngài. Bởi vậy, ai nuốt lời thề, không thực hiện đúng với những gì đã thề trong việc ủng hộ tôn giáo của Ngài thì việc nuốt lời đó chỉ làm hại bản thân y mà thôi bởi không có gì có thể làm hại được Allah. Tuy nhiên, ai thực hiện đúng với lời giao ước trong việc ủng hộ tôn giáo của Ngài thì Ngài sẽ tưởng thưởng cho y một phần thưởng vô cùng to lớn, đó chính là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(48,11,'48:11','Hỡi Thiên Sứ Muhammad, những người Ả Rập vùng sa mạc đã không chịu cùng Ngươi trong chuyến đi đến Makkah sẽ nói với Ngươi về lý do của họ: "Chúng tôi bận trông coi tài sản và con cái của chúng tôi nên đã không đi cùng Người được. Xin Người hãy xin Allah tha thứ cho chúng tôi." Chúng nói bằng chiếc lưỡi của chúng những điều không có trong lòng của chúng về việc nhờ Thiên Sứ Muhammad cầu xin Allah tha thứ cho chúng bởi vì chúng không hề ăn năn sám hối cho tội lỗi của chúng. Ngươi hãy nói với chúng: "Không ai có thể làm được gì một khi Allah muốn điều tốt hay điều xấu cho các ngươi. Allah thông toàn những gì các ngươi làm, không một điều gì từ việc làm của các ngươi có thể che giấu được Ngài, cho dù các ngươi có giấu kín thế nào đi chăng nữa"');
INSERT INTO "vn_mokhtasar" VALUES(48,12,'48:12','Các ngươi lấy lý do bận rộn việc chăm sóc và trông coi tài sản và con cái nên không thể đi cùng với Thiên Sứ Muhammad, đó không phải là lý do, mà thực chất là bởi vì các ngươi nghĩ rằng Thiên Sứ Muhammad và những người đồng hành cùng với Y sẽ bị tiêu diệt tất cả, họ sẽ không thể quay trở về với gia đình của họ ở Madinah. Shaytan đã trang hoàng lỗng lẫy trong trái tim của các ngươi và các ngươi đã nghĩ xấu về Thượng Đế của các ngươi rằng Ngài không phù hộ cho vị Nabi của Ngài. Các ngươi mới là đám người sẽ bị tiêu diệt bởi tư tưởng xấu về Allah cũng như việc các ngươi ở lại nhà không đi cùng với Thiên Sứ Muhammad.');
INSERT INTO "vn_mokhtasar" VALUES(48,13,'48:13','Và ai không có đức tin nơi Allah và Thiên Sứ của Ngài thì kẻ đó là Kafir, và quả thật TA đã chuẩn bị cho những kẻ Kafir Ngục Lửa với sức nóng khủng khiếp để trừng phạt chúng.');
INSERT INTO "vn_mokhtasar" VALUES(48,14,'48:14','Và chỉ duy nhất Allah nắm quyền thống trị các tầng trời và trái đất. Ngài tha thứ tội lỗi cho ai mà Ngài muốn trong đám bề tôi của Ngài, Ngài thu nhận họ vào Thiên Đàng của Ngài bởi hồng phúc của Ngài cũng như Ngài trừng phạt ai mà Ngài muốn trong đám bề tôi của Ngài bằng luật công bằng của Ngài. Và Allah là Đấng hằng tha thứ tội lỗi cho những bầy tôi của Ngài biết quay về sám hối,và Ngài là Đấng rất mực nhân từ đối với họ.');
INSERT INTO "vn_mokhtasar" VALUES(48,15,'48:15','Hỡi những người có đức tin, những kẻ ở lại nhà không cùng tham gia chinh chiến với các ngươi sẽ nói với các ngươi khi các ngươi đi thu nhặt chiến lợi phẩm mà Allah đã hứa ban cho các ngươi sau hiệp ước Al-Hudaibiyah: "Hãy để chúng tôi ra đi cùng với các người." Những kẻ ở lại nhà thật ra chỉ muốn được chia phần từ chiến lợi phẩm. Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với chúng: "Các ngươi không bao giờ được đi cùng với chúng tôi đến phần chiến lợi phẩm đó, bởi quả thật Allah đã hứa chỉ ban nó cho những ai có mặt tại Al-Hudaibiyah." Chúng nói: "Các người đã ngăn cản chúng tôi đi theo các người đến với chiến lợi phẩm không phải là lệnh của Allah mà là các ngươi ganh tị với chúng tôi." Sự việc không như những kẻ không chịu tham chiến này đã khẳng định bởi vì chúng không hiểu các mệnh lệnh cũng như những điều cấm của Allah ngoại trừ một chút ít; chính vì vậy mà chúng đã trái lệnh Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(48,16,'48:16','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những người Ả Rập vùng sa mạc đã không cùng đi với Ngươi trong chuyến đi đến Makkah để kiểm tra chúng: "Các ngươi được gọi đi chiến đấu với một đám người hùng mạnh trong chiến đấu, các ngươi sẽ chiến đấu với họ tới cùng vì con đường chính nghĩa của Allah hoặc họ sẽ vào Islam mà không có cuộc đánh chiến. Nếu các ngươi tuân lệnh Allah về những gì mà Ngài đã kêu gọi các ngươi trong cuộc đánh chiến với họ thì Ngài sẽ ban thưởng cho các ngươi một phần thưởng tốt đẹp, đó là Thiên Đàng; nhưng nếu các ngươi quay lưng, không tuân lệnh Ngài giống như việc các ngươi đã không chịu cùng với Thiên Sứ Muhammad trong chuyến đi đến Makkah thì Ngài sẽ trừng phạt các ngươi bằng một hình phạt vô cùng đau đớn."');
INSERT INTO "vn_mokhtasar" VALUES(48,17,'48:17','Người mù, người què và người bệnh sẽ không bị bắt tội nếu không tham gia cuộc chiến vì con đường chính nghĩa của Allah. Ai tuân lệnh Allah và Thiên Sứ của Ngài thì sẽ được vào các Ngôi Vườn Thiên Đàng có các dòng sông chảy bên dưới các tòa lâu đài; và ai bất tuân Allah và Thiên Sứ của Ngài thì Allah sẽ trừng phạt y bởi một sự trừng phạt vô cùng đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(48,18,'48:18','Quả thật, Allah đã hài lòng với những người có đức tin khi họ tuyên thề trung thành với Ngươi (Muhammad) tại khu vực Al-Hudaibiyah bên dưới tàn cây. Allah biết rõ đức tin, sự thành tâm và trung thực trong lòng của họ, nên Ngài đã ban sự bình an và thanh thản trong con tim của họ, và Ngài sẽ tưởng thưởng cho họ về điều đó bằng một cuộc thắng lợi gần kề, đó là cuộc thắng lợi ở Khaibar, như một phần đền bù lại cho những gì đã mất trong chuyến đi vào Makkah.');
INSERT INTO "vn_mokhtasar" VALUES(48,19,'48:19','Và Allah đã ban cho họ nhiều chiến lợi phẩm từ cư dân Khaibar. Và Allah là Đấng Toàn Năng không ai có thể chiến thắng được Ngài, và Ngài là Đấng Sáng Suốt trong việc tạo hóa, định đoạt và điều hành.');
INSERT INTO "vn_mokhtasar" VALUES(48,20,'48:20','Hỡi những người có đức tin, Allah đã hứa với các ngươi rằng các ngươi sẽ thu gom được nhiều chiến lợi phẩm từ các cuộc thắng lợi của Islam trong tương lai. Ngài đã ban cho các ngươi chiến lợi phẩm từ cư dân Khaibar, Ngài đã ngăn cản những cánh tay của những kẻ Do Thái khi chúng muốn gây hại đến gia đình của các ngươi đằng sau các ngươi và Ngài muốn để những chiến lợi phẩm này đến mau với các ngươi như một dấu hiệu cho các ngươi về sự trợ giúp và phù hộ của Ngài dành cho các ngươi; và Ngài hướng dẫn các ngươi đi theo con đường ngay chính, đó là Chính Đạo.');
INSERT INTO "vn_mokhtasar" VALUES(48,21,'48:21','Và Allah đã hứa với các ngươi về những chiến lợi phẩm khác mà các ngươi chưa đạt được trong thời gian này nhưng Allah là Đấng Duy Nhất có toàn năng trên chúng, và chúng nằm trong sự chi phối và an bài của Ngài, bởi Allah là Đấng Toàn Năng trên tất cả mọi thứ, không có bất thứ gì làm Ngài bất lực cả.');
INSERT INTO "vn_mokhtasar" VALUES(48,22,'48:22','Hỡi những người có đức tin, nếu những kẻ phủ nhận nơi Allah và Thiên Sứ của Ngài - tấn công các ngươi thì chắc chắn chúng sẽ là những kẻ phải tháo lui trước các ngươi; rồi chúng sẽ không tìm thấy được một vị bảo hộ và cứu tinh nào giúp đỡ chúng trong việc giành chiến thắng trước các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(48,23,'48:23','Việc những người có đức tin thắng những kẻ vô đức tin đều được khẳng định trong mọi thời đại và mọi nơi. Đó là đường lối của Allah đối với những cộng đồng đã qua trước những kẻ phủ nhận này; và Ngươi - Thiên Sứ Muhammad - sẽ không bao giờ thấy đường lối của Allah thay đổi.');
INSERT INTO "vn_mokhtasar" VALUES(48,24,'48:24','Và Allah là Đấng đã ngăn cản tay của những kẻ thờ đa thần lại khỏi các ngươi khi chúng gồm người muốn làm điều xấu với các ngươi tại Al-Hudaibiyah, và Ngài đã giữ tay của các ngươi lại khỏi chúng để các ngươi không giết chúng và làm hại chúng mà thả chúng sau khi các ngươi chiếm ưu thế đối với họ. Và Allah là Đấng nhìn thấy tất cả những điều các ngươi làm, không một điều gì có thế che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(48,25,'48:25','Những kẻ phủ nhận nơi Allah và Thiên Sứ của Ngài đã ngăn cấm các ngươi vào nhà nguyện Al-Masjid Al-Haram (tại Makkah) và đã giữ các con vật tế của các ngươi không cho các ngươi mang đến chỗ tế; nếu như những người có đức tin nam và những người có đức tin nữ không sống trà trộn với những người vô đức tin khiến các ngươi không nhận ra họ để rồi các ngươi phải giết lầm thì Allah đã cho phép các ngươi tấn công nhưng Ngài giữ các ngươi lại vì muốn khoan dung với những người có đức tin ở Makkah; và nếu như những người có đức tin sống tách biệt với những kẻ vô đức tin thì chắc chắn Allah đã trừng phạt những kẻ vô đức tin nơi Allah và Thiên Sứ của Ngài bằng một hình phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(48,26,'48:26','Khi những kẻ phủ nhận nơi Allah và Thiên Sứ của Ngài bộc lộ trong lòng của chúng niềm tự hào và tự cao tự đại của thời kỳ ngu muội trước Islam, cái mà nó khinh thường điều chân lý và chỉ biết đi theo dục vọng. Chúng đã từ chối việc đi vào của Thiên Sứ Muhammad vào năm Al-Hudaibiyah vì sợ Y chế giễu chúng bởi Y đã thắng chúng. Thế là Allah đã ban sự trầm tĩnh cho Thiên Sứ của Ngài và những người có đức tin để họ không phẫn nộ và giận dữ khi đối diện với những kẻ thờ đa thần. Allah đã bắt những người có đức tin phải tôn trọng Lời Chân Lý, đó là không có Thượng Đế đích thực nào khác ngoài Allah, và phải thực hiện đúng theo những gì mà Lời Chân Lý này yêu cầu. Và những người có đức tin xứng đáng với Lời này hơn những ai khác, và Allah là Đấng hằng biết tất cả mọi thứ, không có điều gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(48,27,'48:27','Quả thật, Allah đã xác thực giấc mơ của Thiên Sứ Muhammad khi Người nhìn thấy trong lúc ngủ và đã nói lại cho các bạn đạo Người nghe. Giấc mơ đó là Người và các bạn đạo của Người sẽ đi vào Ngôi Nhà của Allah Al-Haram một cách an toàn, một số họ sẽ cạo đầu và một số thì cắt tóc ngắn khi đã hoàn tất nghi thức Hajj. Hỡi những người có đức tin, Allah biết rõ những gì tốt cho các ngươi trong khi các ngươi không biết. Bởi thế Ngài đã cho giấc mơ của Thiên Sứ Muhammad thành hiện thực bằng một cuộc đi vào Makkah trong năm đó, và nó đã cho ra một hiệp ước Al-Hudaibiyah, và theo sau đó là cuộc thắng lợi ở Khaibar, và chiến lợi phẩm của Khaibar về tay của những người có đức tin đã có mặt trong hiệp ước Al-Hudaibiyah.');
INSERT INTO "vn_mokhtasar" VALUES(48,28,'48:28','Allah là Đấng đã gửi Thiên Sứ của Ngài là Muhammad, mang đến nguồn Chỉ Đạo rất rõ ràng cùng với tôn giáo chân lý,đó là tôn giáo Islam với mục đích để nâng cao Islam bên trên các tôn giáo trái nghịch với Nó. Và quả thật, Allah là Đấng làm chứng cho sự việc đó và chỉ một mình Allah thôi đã đủ làm chứng.');
INSERT INTO "vn_mokhtasar" VALUES(48,29,'48:29','Muhammad làThiên Sứ của Allah và những bạn đạo của Y là những người luôn sát cánh bên Y luôn nghiêm khắc với những kẻ vô đức tin đối đầu trên chiến trường nhưng lại rất thương xót và nhân nhượng với nhau. Ngươi - hỡi người nhìn - sẽ thấy họ luôn cúi mình và quỳ lạy xuống để phủ phục Allah vì muốn tìm sự tha thứ và phần ân thưởng tốt đẹp nơi Ngài cũng như muốn được sự hài lòng của Ngài. Vết tích của họ từ những tì vết của việc quá nhiều lần quỳ cúi đầu xuống phủ phục Allah đã nổi hằn trên trán của họ. Đó là đặc điểm của họ được mô tả trong Kinh Tawrah (Cựu ước) được ban xuống cho Musa (Moses) và Kinh Injil (Tân ước) được ban xuống cho Ysa (Jesus hay Giê-su). Và họ giúp đỡ và hỗ trở lẫn nhau cũng như sự hoàn hảo của họ giống như mảnh vườn trông cho ra những cây non rồi từ phát triển thành những cây trồng to lớn và khỏe chắc làm mát dạ người gieo trồng nhưng lại làm cho những kẻ vô đức tin căm ghét và thù hằn. Allah đã hứa với những người có đức tin nơi Ngài và làm việc thiện tốt một sự tha thứ tội lỗi. Không những Ngài không bắt tội họ mà còn tưởng thưởng cho họ phần thưởng vĩ đại ở nơi Ngài, và đó là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(49,1,'49:1','Hỡi những người có đức tin nơi Allah, các ngươi hãy đi theo những gì Ngài ban sắc lệnh, chớ qua mặt Ngài và Thiên Sứ của Ngài bằng lời nói hay hành động. Các ngươi hãy kính sợ Allah bằng cách thực hiện và chấp hành các mệnh lệnh của Ngài cũng như những gì Ngài ngăn cấm. Quả thật Allah là Đấng nghe rõ toàn bộ lời nói của các ngươi và thông toàn tất cả những hành động của các ngươi; không có bất cứ điều gì có thể nằm ngoài tầm kiểm soát của Ngài, và rồi đây Ngày sẽ tưởng thưởng xứng đáng cho mọi việc làm của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(49,2,'49:2','Hỡi những người có đức tin nơi Allah, các ngươi hãy đi theo những gì Allah đã ban sắc lệnh, các ngươi hãy lễ phép với Thiên Sứ của Ngài, các ngươi chớ đừng để tiếng nói của các ngươi cao giọng và to tiếng hơn tiếng nói của Thiên Sứ Muhammad khi nói chuyện với Y, và các ngươi chớ xưng gọi Y bằng tên của Y giống như cách các ngươi xưng gọi nhau mà các ngươi hãy xưng gọi Y bằng chức vụ và sứ mạng của Y với lời lẽ nhẹ nhàng và trang trọng đầy tôn kính. Bởi vì TA (Allah) e rằng sự xưng gọi và cách nói chuyện vô lễ sẽ làm việc làm ngoan đạo và thiện tốt của các ngươi sẽ không được ban cho ân phước trong lúc các ngươi không hề hay biết.');
INSERT INTO "vn_mokhtasar" VALUES(49,3,'49:3','Quả thật những ai ăn nói nhỏ giọng với Thiên Sứ của Allah chính là những người được Allah ban cho họ lòng Taqwa (kính sợ và ngoan đạo) cũng như lòng chân thành "Ikhlas" đối với Ngài, rồi đây vào Ngày Phán Xét họ sẽ được Ngài tha thứ tội lỗi, không bị Ngài bắt tội và sẽ được Ngài tưởng thưởng cho một phần thưởng vô cùng to lớn, đó là Ngài sẽ thu nhận họ vào Thiên Đàng của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(49,4,'49:4','Hỡi Thiên Sứ Muhammad, quả thật những ai hô gọi Ngươi từ bên ngoài nội phòng của Ngươi, đa phần là những kẻ thiếu hiểu biết.');
INSERT INTO "vn_mokhtasar" VALUES(49,5,'49:5','Nếu những người kêu gọi Ngươi - hỡi Thiên Sứ Muhammad - từ bên ngoài nội phọng chịu khó nhẫn nại, không hô gọi Ngươi như thế cho tới khi Ngươi đi ra để tiếp họ thì điều đó tốt hơn cho họ, bởi lẽ như vậy là lễ phép và kính trọng Ngươi. Và Allah là Đấng hằng tha thứ tội lỗi cho những ai quay đầu sám hối với Ngài và Ngài là Đấng rất mực khoan dung đối với họ.');
INSERT INTO "vn_mokhtasar" VALUES(49,6,'49:6','Hỡi những người có đức tin nơi Allah và làm theo những gì Ngài sắc lệnh và ban hành, nếu có một kẻ gây rối nào đó mang tin tức về một cộng đồng nào đó đến cho các ngươi thì các ngươi phải kiểm tra cẩn thận để xác định thông tin có đúng sự thực không, các ngươi chớ vội tin kẻ đó, sợ rằng các ngươi có thể gây ra điều xấu cho một nhóm người trong lúc các ngươi không hiểu biết vấn đề, để rồi sau đó các ngươi đâm ra hối hận cho những điều các ngươi đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(49,7,'49:7','Hỡi những người có đức tin, các ngươi hãy biết rằng đang sống cùng với các ngươi là một vị Thiên Sứ của Allah, vị mà Lời mặc khải của Ngài được ban xuống cho Y. Bởi thế, các ngươi hãy coi chừng đừng để phải nói dối để rồi Lời mặc khải được ban xuống cho Y về sự dối trá của các ngươi. Y biết rõ những gì sẽ cải thiện điều tốt đẹp cho các ngươi. Nếu Y nghe theo các ngươi trong nhiều vấn đề mà các ngươi đề nghị thì chắc chắn các ngươi đã gặp nhiều khó khăn. Tuy nhiên, nhờ hồng phúc của Allah, Ngài đã làm cho các ngươi thấm nhuần đức tin, Ngài đã trang hoàng và làm đẹp trái tim của các ngươi để các ngươi có đức tin và ghét sự vô đức tin cũng như ghét việc bất tuân Ngài. Những người được mô tả đó chính là những người được hướng dẫn đúng đường.');
INSERT INTO "vn_mokhtasar" VALUES(49,8,'49:8','Và những gì các ngươi đạt được từ những điều tốt đẹp trong trái tim của các ngươi thật ra là hồng phúc và ân huệ mà Allah đã ban cho các ngươi. Allah biết rõ những bề tôi nào biết ơn Ngài nên Ngài phù hộ cho họ, và Ngài sáng suốt trong việc đặt để một điều gì đó vào đúng vị trí thích hợp của nó.');
INSERT INTO "vn_mokhtasar" VALUES(49,9,'49:9','Hỡi những người có đức tin, nếu có hai nhóm thuộc những người có đức tin giao chiến với nhau thì các ngươi hãy đứng ra can thiệp và giải hòa giữa họ bằng giáo luật của Allah. Nếu một trong hai không chịu giải hòa và ngoan cố lấn át và vượt mức giới hạn thì các ngươi hãy đánh họ cho tới khi họ quay trở lại với giáo luật của Allah. Và nếu họ đã quay trở lại với giáo luật của Allah thì các ngươi hãy giải hòa giữa họ một cách công bằng và hợp lý. Quả thật Allah yêu thương những ai công bằng và hợp lý trong việc phân xử.');
INSERT INTO "vn_mokhtasar" VALUES(49,10,'49:10','Quả thật, chỉ những người có đức tin mới đích thực là anh em với nhau trong Islam. Đã là anh em với nhau trong Islam thì các ngươi - hỡi những người có đức tin - hãy giải hoa giữa hai người anh em đang giao chiến và tranh chấp của các ngươi. Các ngươi hãy kính sợ Allah mà thực hiện đúng với các mệnh lệnh của Ngài cũng như tránh xa những điều Ngài ngăn cấm, mong rằng Ngài sẽ thương xót và yêu thương các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(49,11,'49:11','Hỡi những người có đức tin nơi Allah và làm theo những gì Ngài sắc lệnh và ban hành, một nhóm người này chớ chế nhạo một nhóm người kia, biết đâu nhóm người bị chế nhạo tốt hơn ở nơi Allah. Tương tự, một nhóm phụ nữ này chớ chế nhạo một nhóm phụ nữ kia, biết đâu nhóm phụ nữ bị chế nhạo tốt hơn ở nơi Allah. Các ngươi chớ gây xấu hổ cho người anh em của các ngươi và các ngươi chớ bêu tên xấu của nhau ra trước công chúng giống như tình trạng của một số người Ansar trước khi Thiên Sứ Muhammad đến. Ai trong các ngươi làm điều đó thì y là kẻ xấu xa và thối nát. Thật là một điều tồi tệ cho những thuộc tính xấu xa sau khi đã có đức tin. Và ai không chịu ăn năng, sám hối thì họ chính là những kẻ làm điều sai quấy vì đã muốn bản thân mình bị diệt vong.');
INSERT INTO "vn_mokhtasar" VALUES(49,12,'49:12','Hỡi những người có đức tin nơi Allah và làm theo những gì mà Ngài đã sắc lệnh và ban hành, các ngươi hãy tránh xa việc cáo buộc hay kết tội người khác mà không dựa trên bất cứ lý do hay bằng chứng nào, các ngươi hãy tránh xa việc làm đó càng nhiều càng tốt. Quả thật, một số sự nghi ngờ là tội lỗi giống như nghĩ xấu cho một người bên ngoài có biểu hiện tốt lành. Và các ngươi chớ dọ thám và deo dõi về những điều riêng tư của những người có đức tin và chớ nhắc đến họ về những điều mà họ không thích ở sau lưng họ bởi quả thật việc nói sau lưng ngươi khác về những điều họ không thích giống như ăn thịt xác chết. Chẳng lẽ ai đó trong các ngươi lại thích ăn thịt người anh em của mình đã chết ư?! Các ngươi hãy kính sợ Allah mà chấp hành các mệnh lệnh của Ngài cũng như tránh xa những điều cầm của Ngài. Quả thật, Allah là Đấng sẽ đón nhận sự sám hối của đám bề tôi biết quay đầu sám hối với Ngài và Ngài rất đỗi thương xót họ.');
INSERT INTO "vn_mokhtasar" VALUES(49,13,'49:13','Hỡi nhân loại, quả thật TA (Allah) đã tạo hóa các ngươi từ một người nam, đó là người cha của các ngươi - Adam, và một người nữ, đó là người mẹ của các ngươi - Hauwa''. Bởi thế, các ngươi đều có chung một tổ tiên. Cho nên, các ngươi chớ đừng vênh mặt tự hào về xuất thân của các ngươi. Việc TA làm cho các ngươi thành những quốc gia và các bộ lạc không phải là để nhóm người này kênh kiệu với nhóm người kìa mà là để các ngươi nhận biết lẫn nhau (mình thuộc quốc gia nào hay bộ lạc nào) bởi lẽ sự vượt trội lẫn nhau là ở lòng Taqwa (ngay chính, ngoan đạo và sợ Allah). Ai có lòng Taqwa nhất thì người đó chính là người cao quý và vinh dự nhất nơi Allah. Quả thật, Allah hằng biết tất cả mọi hoàn cảnh và tình trạng của các ngươi, Ngài thông toàn tất cả mọi mặt của các ngươi, hoàn hảo hay khiếm khuyết, không có bất cứ điều gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(49,14,'49:14','Một số cư dân vùng sa mạc nói khi đến gặp Thiên Sứ Muhammad: Tôi đã tin nơi Allah và Thiên Sứ của Ngài. Ngươi - hỡi Thiên Sứ Muhammad - hãy nói với họ: Các ngươi chưa thực sự có đức tin, các ngươi hãy nói rằng chúng tôi đã vân phục và quy thuận Islam. Các ngươi hãy chỉ nói thế thôi bởi vì đức tin chưa thấm nhuần vào trong trái tim của các ngươi. Tuy nhiên, nếu các ngươi - những người cư dân vùng sa mạc - tuân lệnh Allah và Thiên Sứ của Ngài bằng đức tin, làm điều thiện tốt, và tránh xa những điều cấm của Ngài thì Allah sẽ không làm mất đi bất cứ điều gì từ ân phước của các việc làm thiện tốt mà các ngươi đã làm. Quả thật, Allah là Đấng hằng tha thứ tội lỗi cho những bề tôi quay đầu sám hối với Ngài và Ngài rất đỗi thương xót họ.');
INSERT INTO "vn_mokhtasar" VALUES(49,15,'49:15','Quả thật những người có đức tin đích thực là những người tin tưởng nơi Allah và Thiên Sứ của Ngài rồi không hòa lẫn vào đức tin của mình với sự hoài nghi hoặc ngờ vực,và hết mình chiến đấu cho con đường Chính Nghĩa của Allah bằng cả tài sản lẫn sinh mạng. Những người đó mới là những người chân thật trong đức tin của mình.');
INSERT INTO "vn_mokhtasar" VALUES(49,16,'49:16','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những cư dân sa mạc này: Chẳng lẽ các ngươi muốn dạy Allah về tôn giáo của các ngươi chăng ? trong khi Allah là Đấng biết rõ mọi điều trong các tầng trời và mọi thứ trên trái đất. Allah thông toàn tất cả mọi thứ, không có gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(49,17,'49:17','Hỡi Thiên Sứ Muhammd, những kẻ vùng sa mạc này coi việc họ theo Islam như là một ân huệ ban cho Ngươi. Ngươi hãy nói với họ: Các ngươi không hề mang lại ân huệ nào cho Ta bởi sự gia nhập của các ngươi vào tôn giáo của Allah. Ngược lại, đó là ân huệ mà Allah đã ban cho các ngươi khi các ngươi quy thuận Islam. Nếu các ngươi nói như thế thì đó mới là chân thật rằng các ngươi đã vào Islam.');
INSERT INTO "vn_mokhtasar" VALUES(49,18,'49:18','Quả thật Allah biết những điều vô hình nơi các tầng trời và trên trái đất. Không có bất cứ điều gì có thể che giấu được Ngài. Ngài thấy tất cả những gì các ngươi làm và Ngài sẽ ban thưởng thích đáng cho những việc làm thiện tốt của các ngươi và sẽ trừng phạt công bằng cho những việc xấu của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(50,1,'50:1','{Qaf} ý nghĩa của nó (các chữ cái mở đầu chương) đã được nói ở phần mở đầu của chương Al-Baqarah. Allah thề bởi Kinh Qur''an Thiêng Liêng bởi vì trong Nó chứa đựng các ý nghĩa và giá trị, chứa đựng nhiều điều phúc lành, chứa đựng những thông tin về Ngày Phán Xét và sự thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(50,2,'50:2','Nguyên nhân chúng từ chối không phải là vì Ngươi - Thiên Sứ Muhammad - nói dối chúng bởi chúng biết rõ Ngươi nói thật, nhưng chúng ngạc nhiên tại sao vị Sứ Giả đến với chúng lại xuất thân từ đồng loại giống như chúng mà không phải là từ các Thiên Thần. Chúng nói với thái độ ngạc nhiên: Việc một vị Thiên Sứ thuộc người phàm được cử đến cho chúng ta thật là một điều lạ lùng!');
INSERT INTO "vn_mokhtasar" VALUES(50,3,'50:3','Có thật là chúng tôi sẽ được phục sinh khi mà chúng tôi đã chết và đã biến thành cát bụi?! Sự phục sinh đó cũng như việc sự sống trở lại với thân xác chúng tôi sau khi đã bị phân hủy quả là một điều xa vời, không thể nào xảy ra.');
INSERT INTO "vn_mokhtasar" VALUES(50,4,'50:4','Quả thật, TA (Allah) thừa biết việc đất sẽ tiêu hủy thân xác của chúng sau khi chúng chết đi, những điều đó không làm khó được ta chút nào, ở nơi TA có một quyển sổ bộ về tất cả những gì mà TA toàn năng đối với chúng lúc còn sống cũng như lúc đã chết.');
INSERT INTO "vn_mokhtasar" VALUES(50,5,'50:5','Không, những kẻ thờ đa thần này đã phủ nhận Qur''an khi vị Thiên Sứ của Allah mang Nó đến cho chúng. Chúng lúng túng, bối rối và mất phương hướng, không biết đâu là chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(50,6,'50:6','Sao những kẻ phủ nhận sự Phục Sinh này không chịu quan sát bầu trời nằm bên trên chúng mà suy ngẫm xem TA (Allah) đã tạo dựng và trang hoàn nó như thế nào?! TA đã đặt trong bầu trời các vì sao và bầu trời không hề có một lỗ hở. Bởi thế, Đấng đã tạo ra bầu trời này không bất lực trước việc làm cho người chết sống lại.');
INSERT INTO "vn_mokhtasar" VALUES(50,7,'50:7','Trái đất, TA đã làm cho nó trải rộng để các cư ngụ trên đó yên ổn, TA đã đặt lên nó những quả núi giúp nó không bị rung chuyển, và TA đã làm mọc ra từ bề mặt của nó đủ loại cây cối và thảo mộc với những quang cảnh tuyệt đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(50,8,'50:8','TA đã tạo hóa ra tất cả những thứ đó để quan sát và nhắc nhở tất cả những bề tôi nào quay đầu sám hối với Thượng Đế của y.');
INSERT INTO "vn_mokhtasar" VALUES(50,9,'50:9','Và TA ban nước mưa từ trên trời xuống mang lại nhiều hồng phúc và tốt lành. TA đã dùng nước mưa để làm mọc ra các vườn tược cũng như các loại hạt vào mua gặt hái.');
INSERT INTO "vn_mokhtasar" VALUES(50,10,'50:10','Và TA dùng nước mưa làm mọc ra cây chà là cao to có trái đâm ra chồng lên nhau.');
INSERT INTO "vn_mokhtasar" VALUES(50,11,'50:11','Những gì TA làm mọc ra đó dùng để làm nguồn lương thực cho đám bề tôi của TA, và TA đã dùng nước mưa làm sống lại những mảnh đất chết khô với đủ loại cây cối xanh tươi, và việc phục sinh cũng tương tự như vậy.');
INSERT INTO "vn_mokhtasar" VALUES(50,12,'50:12','Hỡi Thiên Sứ Muhammad, trước những kẻ phủ nhận này, cũng đã có các đám dân đã phủ nhận các vị Thiên Sứ của họ. Đám dân của Nuh, những cư dân của giếng nước và dám dân Thamud đã phủ nhận điều chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(50,13,'50:13','Đám dân ''Ad, Fir''awn và đám dân của Lut cũng đã phủ nhận chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(50,14,'50:14','Đám dân của Shu''aib - những người dân của Aykah (khu rừng ngập mặn) - và đám dân của Tubba'', một vị vua của Yemen đã phủ nhận các vị Thiên Sứ của họ nên lời hứa trừng phạt họ của Allah đã được thực thi.');
INSERT INTO "vn_mokhtasar" VALUES(50,15,'50:15','Chẳng phải TA (Allah) đã không bất lực trong việc tạo hóa các ngươi trong lần đầu, thế thì làm sao TA lại bất lực trong việc phục sinh các ngươi trở lại?! Không đâu, chúng chỉ bối rối trước việc tạo hóa mới sau khi chúng đã được tạo hóa lần đầu.');
INSERT INTO "vn_mokhtasar" VALUES(50,16,'50:16','Quả thật, TA (Allah) đã tạo hóa con người và TA biết rõ bản thân y nói gì và nghĩ gì. Quả thật TA ở gần với y hơn cả tĩnh mạch nơi cổ của y được kết nối với trái tim của y.');
INSERT INTO "vn_mokhtasar" VALUES(50,17,'50:17','Khi hai vị Thiên Thần phụ trách việc ghi chép các việc làm của y, một vị ngồi bên phải và một vị ngồi bên trái chuẩn bị sẵn sàng ghi chép.');
INSERT INTO "vn_mokhtasar" VALUES(50,18,'50:18','Bất cứ lời nói nào mà y thốt ra đều được vị Thiên Thần theo dõi ghi nhận, (không sót một điều gì)');
INSERT INTO "vn_mokhtasar" VALUES(50,19,'50:19','Và tình trạng đau đớn của cái chết, cái mà không ai tránh khỏi, thực sự đến. Đó là cái mà ngươi hỡi con người xao lãng đã cố chạy trốn khỏi nó.');
INSERT INTO "vn_mokhtasar" VALUES(50,20,'50:20','Và khi Thiên Thần phụ trách thổi Còi từ chiếc Sừng hụ lên tiếng còi thứ hai. Đó là Ngày Phục Sinh, Ngày mà những kẻ vô đức tin, những kẻ tội lỗi đã được hứa về sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(50,21,'50:21','Vào Ngày đó, mỗi linh hồn sẽ bị dẫn đến trình diện Thượng Đế, đi theo là một vị Thiên Thần dẫn đường và một vị Thiên Thần làm nhân chứng cho các việc làm của y.');
INSERT INTO "vn_mokhtasar" VALUES(50,22,'50:22','Allah phán với con người được dẫn đến trình diện trước Ngài: Quả thật ở trên trần gian, ngươi đã vô tâm với cái Ngày này do chỉ biết đi theo dục vọng và sự vui thú khoái lạc của ngươi. Bởi vậy, TA vén bỏ tấm màn khỏi ngươi và để ngươi hứng chịu sự trừng phạt và tai họa, và đôi mắt của ngươi thấy rõ những gì mà ngươi đã lơ là và phớt lờ.');
INSERT INTO "vn_mokhtasar" VALUES(50,23,'50:23','Thiên Thần làm chứng cho việc làm của y thưa với Allah: Đây là sổ ghi chép về các việc làm của y ở nơi bề tôi, nó không thiếu cũng như không thừa bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(50,24,'50:24','Allah phán với hai vị Thiên Thần dẫn đường và làm chứng: hai ngươi hãy ném tất cả những tên vô đức tin nơi điều Chân Lý vào Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(50,25,'50:25','Những kẻ đã ngăn cản những nghĩa vụ mà Allah đã bắt buộc, những kẻ đã vượt quá giới hạn của Allah, và những kẻ luôn hoài nghi và ngờ vực về những điều mà Allah đã thông tin cho biết từ lời hứa ban thưởng và lời đe dọa trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(50,26,'50:26','Kẻ đã dựng lên một thần linh ngoài Allah để thờ phượng. Hai ngươi hãy ném những kẻ này vào chỗ trừng phạt khắc nghiệt.');
INSERT INTO "vn_mokhtasar" VALUES(50,27,'50:27','Bạn đồng hành của kẻ vô đức tin và tội lỗi (Shaytan) thưa với Allah: Lạy Thượng Đế của bề tôi, bề tôi đã không hề làm cho y lạc lối mà chính bản thân y đã tự đưa mình vào sự lầm lạc rời xa chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(50,28,'50:28','Allah phán: Các ngươi chớ tranh cãi trước mặt TA bởi điều đó chỉ là vô ích. Quả thật, TA đã gửi cho các ngươi trên cõi trần các vị Thiên Sứ của TA mang những lời cảnh báo mạnh mẽ cho những ai vô đức tin và nghịch lại TA.');
INSERT INTO "vn_mokhtasar" VALUES(50,29,'50:29','Allah phán cho biết rằng lời phán của Ngài không thay đổi, Ngài không làm trái lới hứa của Ngài và Ngài cũng không bất công với bất kỳ người bề tôi nào, Ngài không giảm bớt phần ban thưởng cũng không tăng thêm tội cho ai, Ngài chỉ trừng phạt đúng với tội lỗi mà họ đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(50,30,'50:30','Vào Ngày mà Allah sẽ phán với Hỏa Ngục: Ngươi đã đầy chưa, còn chỗ chứa những kẻ vô đức tin và tội lỗi nữa không? Hỏa Ngục bẩm với Allah: Thưa còn nữa không? Hỏa Ngục đòi thêm vì giận dữ thay cho Thượng Đế của Nó.');
INSERT INTO "vn_mokhtasar" VALUES(50,31,'50:31','Thiên Đàng được mang đến gần cho những người ngoan đạo đối với Thượng Đế của họ qua việc chấp hành các mệnh lệnh của Ngài và tránh những điều Ngài ngăn cấm. Họ sẽ nhìn thấy những niềm vui và hưởng thụ nơi Thiên Đàng trong một khoảng cách không xa.');
INSERT INTO "vn_mokhtasar" VALUES(50,32,'50:32','Họ được bảo: Đây là những gì mà Allah đã hứa với các người, những ai luôn biết quay đầu sám hối với Ngài khi lỡ làm điều sai trái và luôn duy trì những nghĩa vụ mà Ngài qui định cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(50,33,'50:33','Ai sợ Allah một cách thầm kín trong lúc không ai nhìn thấy y ngoại trừ Allah và trở về trình diện Allah bằng trái tim tốt lành được đón nhận nơi Allah qua việc luôn biết ăn năn sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(50,34,'50:34','Họ được mời gọi: Các người hãy đi vào Thiên Đàng một cách bằng an. Đây là Ngày mà các người sẽ có cuộc sống hạnh phúc vĩnh hằng, sẽ không bao giờ có sự diệt vong và kết thúc sau Ngày này.');
INSERT INTO "vn_mokhtasar" VALUES(50,35,'50:35','Trong Thiên Đàng, họ sẽ có mọi thứ họ mong muốn và yêu thích, và sự hạnh phúc trong đó là bất tận. Và ở nơi TA (Allah) còn có một phần thêm nữa dành cho họ từ niềm hạnh phúc, cái mà chưa từng có đôi mắt nào từng nhìn thấy, chưa từng có đôi tai nào từng nghe qua và chưa từng có trái tim người phàm nào hình dung đến; đó là được nhìn ngắm Allah.');
INSERT INTO "vn_mokhtasar" VALUES(50,36,'50:36','Đã có biết bao cộng đồng đã bị TA hủy diệt trước những kẻ đa thần vô đức tin ở Makkah và tất cả chúng đều không tìm thấy chỗ nương thân nào trước sự trừng phạt của TA.');
INSERT INTO "vn_mokhtasar" VALUES(50,37,'50:37','Quả thật trong sự việc các cộng đồng trước bị hủy diệt là một bài học nhắc nhở và khuyên răn đến những ai có trái tim biết suy ngẫm và nhận thức hoặc biết dùng đôi tai để lắng nghe bằng cả tấm lòng, không phớt lờ và xao lãng.');
INSERT INTO "vn_mokhtasar" VALUES(50,38,'50:38','Quả thật, TA (Allah) đã tạo các tầng trời và trái đất cũng như tất cả vạn vật trong trời đất trong sáu ngày mặc dù TA toàn năng tạo chúng ra trong một khoảnh khắc. Và TA không hề mệt mỏi trong sự việc đó như những kẻ Do Thái đã nói.');
INSERT INTO "vn_mokhtasar" VALUES(50,39,'50:39','Bởi thế, Ngươi - hỡi Thiên Sứ Muhammad - hãy kiên nhẫn chịu đựng trước những lời mà những kẻ Do Thái và những ai khác thốt ra. Ngươi hãy dâng lễ nguyện Salah thờ phượng Thượng Đế của Ngươi và hãy tán dương ca tụng Ngài sáng chiều - lễ nguyện Salah Fajr trước khi mặt trời mọc và lễ nguyện Salah Asr trước khi mặt trời lặn.');
INSERT INTO "vn_mokhtasar" VALUES(50,40,'50:40','Bởi thế, Ngươi hãy lễ nguyện Salah trong đêm và hãy tán dương ca tụng Ngài sau các buổi lễ nguyện Salah bắt buộc.');
INSERT INTO "vn_mokhtasar" VALUES(50,41,'50:41','Hỡi Thiên Sứ Muhammad, Ngươi hãy lắng nghe vào Ngày mà vị Thiên Thần phụ trách thổi Còi sẽ hụ tiếng còi thứ hai từ một nơi rất gần.');
INSERT INTO "vn_mokhtasar" VALUES(50,42,'50:42','Ngày mà tất cả mọi sinh linh nghe thấy tiếng Gầm Thét báo hiệu của giờ Phục Sinh, một sự thật không có gì phải hoài nghi. Đó là Ngày mà tất cả mọi cơ thể chết sẽ đi ra khỏi các ngôi mộ để chịu sự phán xét và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(50,43,'50:43','Quả thật, TA (Allah) là Đấng ban cho sự sống và làm cho chết, không ai (vật) ngoài TA có quyền năng đó. Và vào Ngày Phục Sinh các ngươi sẽ phải trở về trình diện TA để TA phán xét và thưởng phạt, chỉ một mình TA thôi có quyền đó.');
INSERT INTO "vn_mokhtasar" VALUES(50,44,'50:44','Ngày mà trái đất sẽ bị nứt ra và những kẻ trong nó sẽ nhanh vội đi ra, đó là một cuộc triệu tập vô cùng đơn giản đối với TA.');
INSERT INTO "vn_mokhtasar" VALUES(50,45,'50:45','TA (Allah) biết điều mà những kẻ phủ nhận này đã nói, và Ngươi - Thiên Sứ Muhammad - không phải là người ép buộc chúng đến với đức tin, Ngươi chỉ là một người truyền đạt của Allah. Do đó, Ngươi hãy dùng Qur''an cảnh báo những ai biết sợ lời đe dọa dành cho những kẻ vô đức tin và tội lỗi bở vì chỉ có những kẻ biết sợ mới chịu đón nhận lời khuyên và sự nhắc nhở.');
INSERT INTO "vn_mokhtasar" VALUES(51,1,'51:1','Allah thề bởi những cơn gió thổi cát bụi bay đi.');
INSERT INTO "vn_mokhtasar" VALUES(51,2,'51:2','Allah thề bởi những đám mây chứa nước mưa.');
INSERT INTO "vn_mokhtasar" VALUES(51,3,'51:3','Allah thề bởi những con tàu, thuyền vượt trùng dương một cách nhẹ nhàng, êm ái.');
INSERT INTO "vn_mokhtasar" VALUES(51,4,'51:4','Allah thề bởi các vị Thiên Thần được giao nhiệm vụ phân phối theo mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(51,5,'51:5','Quả thật, điều mà Thượng Đế của các ngươi đã hứa với các người từ việc phán xét, thưởng phạt là thật, chắc chắn sẽ xảy ra, không có gì phải hoài nghi và ngờ vực cả.');
INSERT INTO "vn_mokhtasar" VALUES(51,6,'51:6','Quả thật việc phán xử các bề tôi vào Ngày Phục Sinh là điều chắc chắn xảy ra.');
INSERT INTO "vn_mokhtasar" VALUES(51,7,'51:7','Allah thề bởi bầu trời tốt đẹp chứa đầy những con đường.');
INSERT INTO "vn_mokhtasar" VALUES(51,8,'51:8','Hỡi cư dân Makkah, quả thật các ngươi không có sự thống nhất trong lời nói. Có lúc các ngươi nói Qur''an là thứ bùa chú, có lúc các ngươi nói Qur''an là thơ ca; có lúc các ngươi nói Muhammad là một tên thầy bùa và có lúc các ngươi lại nói Y là một nhà thơ.');
INSERT INTO "vn_mokhtasar" VALUES(51,9,'51:9','Những kẻ lánh xa Qur''an và Thiên Sứ Muhammad là những kẻ lánh xa sự chỉ đạo và hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(51,10,'51:10','Những kẻ phủ nhận, những kẻ mà chúng đã nói không đúng về Qur''an và Thiên Sứ Muhammad, bị nguyền rủa.');
INSERT INTO "vn_mokhtasar" VALUES(51,11,'51:11','Những kẻ mà chúng không quan tâm và nghĩ đến cuộc sống cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(51,12,'51:12','Chúng hỏi bao giờ Ngày Thưởng Phạt sẽ đến ý muốn phủ nhận.');
INSERT INTO "vn_mokhtasar" VALUES(51,13,'51:13','Allah trả lời cho câu hỏi của chúng: Đó là Ngày mà chúng sẽ bị trừng phạt trên Lửa.');
INSERT INTO "vn_mokhtasar" VALUES(51,14,'51:14','Rồi đây sẽ có lời bảo chúng: Các ngươi hãy nếm mùi của sự trừng phạt dành cho các ngươi. Đây là cái mà các ngươi cứ thường hối thúc nó mau đến bằng những câu hỏi chế giễu khi các ngươi được cảnh báo.');
INSERT INTO "vn_mokhtasar" VALUES(51,15,'51:15','Quả thật những người kính sợ Thượng Đế của họ qua việc chấp hành các mệnh lệnh của Ngài và tránh xa những điều Ngài nghiêm cấm, vào Ngày Phán Xét, sẽ được ở trong các Ngôi Vườn bên những con suối chảy nơi Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(51,16,'51:16','Họ sẽ thụ hưởng những gì mà Thượng Đế của họ ban thưởng cho họ. Quả thật, trên thế gian họ đã làm tốt và đó là phần thưởng dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(51,17,'51:17','Họ thường dâng lễ nguyện Salah trong đêm, họ chỉ ngủ một chút ít trong đêm.');
INSERT INTO "vn_mokhtasar" VALUES(51,18,'51:18','Và vào khoảng thời gian cuối của đêm trước lễ nguyện Salah Fajr, họ thường cầu xin Allah tha thứ cho tội lỗi của họ.');
INSERT INTO "vn_mokhtasar" VALUES(51,19,'51:19','Họ luôn trích một phần trong tài sản của họ như một nghĩa vụ bắt buộc dành cho những người ăn xin trong thiên hạ cũng như những người túng thiếu nhưng không xin xỏ thiên hạ.');
INSERT INTO "vn_mokhtasar" VALUES(51,20,'51:20','Và trên trái đất, nơi mà Allah đã đặt lên đó núi non, biển cả, sông ngòi, cây cối, động vật, là những bằng chứng về quyền năng của Allah cho những người có đức tin kiên định rằng Allah là Đấng Tạo Hóa.');
INSERT INTO "vn_mokhtasar" VALUES(51,21,'51:21','Hỡi những nhân loại, các bằng chứng về quyền năng của Allah có cả trong bản thân của các ngươi, thế các ngươi không nhìn thấy và suy ngẫm ư?!');
INSERT INTO "vn_mokhtasar" VALUES(51,22,'51:22','Và trên bầu trời có những nguồn bổng lộc cho cuộc sống thế tục và cho tôn giáo của các ngươi, và còn có những thứ mà các ngươi đã được hứa từ điều xấu và điều tốt.');
INSERT INTO "vn_mokhtasar" VALUES(51,23,'51:23','Do vậy, thề bởi Thượng Đế của bầu trời và trái đất, quả thật sự Phục Sinh là sự thật, nó chắc chắn sẽ xảy ra, không phải nghi ngờ gì về việc đó, giống như không có sự nghi ngờ trong lời nói của các ngươi khi các ngươi nói chuyện.');
INSERT INTO "vn_mokhtasar" VALUES(51,24,'51:24','Hỡi Thiên Sứ Muhammad, Ngươi có biết về câu chuyện của những vị khách của Ibrahim được cử phái đến với Y từ các Thiên Thần vinh dự chưa?');
INSERT INTO "vn_mokhtasar" VALUES(51,25,'51:25','Khi họ (các Thiên Thần) vào nhà Ibrahim, họ đã chào Salam và Ibrahim đáp lại lời Salam. Ibrahim đã nói trong lòng: những người này lạ quá, mình không biết họ.');
INSERT INTO "vn_mokhtasar" VALUES(51,26,'51:26','Sau đó, Ibrahim lặng lẽ vào gặp người nhà rồi mang ra một con bò non mập béo để tiếp đãi các vị khách vì nghĩ rằng họ người phàm tục.');
INSERT INTO "vn_mokhtasar" VALUES(51,27,'51:27','Ibrahim mang con bò ra tiếp đãi họ (nhưng họ không ăn) nên Ibrahim nói: sao quí ngài không ăn?');
INSERT INTO "vn_mokhtasar" VALUES(51,28,'51:28','Khi thấy họ không ăn, Ibrahim lo sợ trong lòng. Các Thiên Thần trấn an: Ngươi đừng sợ, chúng tôi là các Sứ Giả được Allah cử đến. Rồi họ báo tin vui cho Ibrahim về đứa con trai hiểu biết mà Allah sẽ ban cho Y, đó là Is-haq.');
INSERT INTO "vn_mokhtasar" VALUES(51,29,'51:29','Khi nghe báo tin, vợ Ibrahim bước lên và lớn tiếng trong sự vui mừng thốt lên một lời lẽ ngạc nhiên: một bà lão già nua và hiếm muộn sẽ sinh con ư?!');
INSERT INTO "vn_mokhtasar" VALUES(51,30,'51:30','Các Thiên Thần nói với vợ Ibrahim: Những gì chúng tôi thông báo cho bà ta là những gì mà Thượng Đế của bà ta đã phán, và những gì Ngài đã phán là chắc chắn. Quả thật Ngài là Đấng Sáng Suốt trong việc tạo hóa và định đoạt, và Ngài là Đấng Hằng Biết điều gì cải thiện cho đám bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(51,31,'51:31','Ibrahim hỏi các Thiên Thần: Các ngài có nhiệm vụ gì? Các ngài đến đây với mục đích gì?');
INSERT INTO "vn_mokhtasar" VALUES(51,32,'51:32','Các Thiên Thần đáp: Allah cử phái chúng tôi đến với đám người tội lỗi làm những điều thối nát.');
INSERT INTO "vn_mokhtasar" VALUES(51,33,'51:33','Để đổ lên họ trận mưa đá bằng đất nung.');
INSERT INTO "vn_mokhtasar" VALUES(51,34,'51:34','Đã được Thượng Đế của Ngươi - hỡi Ibrahim - giáng xuống trừng phạt đám người vượt quá giới luật của Allah về sự vô đức tin và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(51,35,'51:35','Bọn ta cứu những người có đức tin bình an rời khỏi ngôi làng của Lut, họ không bị trừng phạt cùng những đám người tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(51,36,'51:36','Tiếc là bọn ta chỉ tìm thấy một gia đình Muslim duy nhất, đó là gia đình của Nabi Lut.');
INSERT INTO "vn_mokhtasar" VALUES(51,37,'51:37','Và TA (Allah) đã bỏ lại ngôi làng mà dân chúng của Lut đã ở dấu hiệu của hình phạt, chứng minh hình phạt đã giáng lên họ để làm bài học cho những ai biết sợ hình phạt kinh hoàng đã giáng lên dân chúng nơi đó để họ không làm những hành động mà dân chúng đó đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(51,38,'51:38','Và Musa, TA đã cử phái Y đến gặp Fir''awn với các bằng chứng rõ ràng, dấu hiệu đó dành cho người biết khiếp sợ về hình phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(51,39,'51:39','Fir''awn dựa vào sức mạnh và quân lực đã phủ nhận chân lý, hắn nói về Musa: Đây là tên phù thủy chuyên mê hoặc thiên hạ hoặc là tên thần kinh không kiểm soát được mình đang nói gì.');
INSERT INTO "vn_mokhtasar" VALUES(51,40,'51:40','Thế là TA đã túm bắt lấy hắn và đám thuộc hạ của hắn, ném tất cả chúng xuống biển cho chúng chết chìm, và Fir''awn là tên đáng bị khiển trách vì ngông cuồng tự xưng là thượng đế.');
INSERT INTO "vn_mokhtasar" VALUES(51,41,'51:41','Và về ''Ad dân chúng của Hud là bài học cho ai biết lo sợ về hình phạt đau đớn khi TA gởi đến họ một cơn giông tố, không mang mưa đến, không giúp cây thụ phấn được cũng không mang đến bất cứ điều tốt nào.');
INSERT INTO "vn_mokhtasar" VALUES(51,42,'51:42','Nó không chừa lại bất cứ thứ gì, con người, tài sản hay những thứ gì khác, mỗi khi nó đi qua.');
INSERT INTO "vn_mokhtasar" VALUES(51,43,'51:43','Và về Thamud, dân chúng của Saleh, là một bài học dành cho những ai biết lo sợ về hình phạt đau đớn, khi có lời bảo họ: Các ngươi hãy tận dụng cuộc đời mình làm điều hữu ích trước khi hết tuổi thọ.');
INSERT INTO "vn_mokhtasar" VALUES(51,44,'51:44','Nhưng họ đã ngông cuồng kháng lại lệnh của Thượng Đế của họ, thể hiện sự tự cao, vô đức tin và bất tuân, thế là họ đã bị lưỡi tầm sét giáng xuống trong lúc họ chỉ biết trố mắt ra nhìn một cách bất lực. Bởi trước đó ba ngày họ được cho biết rằng sự trừng phạt sẽ đến.');
INSERT INTO "vn_mokhtasar" VALUES(51,45,'51:45','Dù đã biết trước hình phạt sắp đến nhưng họ vẫn bất lực tránh khỏi hình phạt, cũng như không đủ sức mạnh ngăn cản hình phạt đang dần đến hủy diệt họ.');
INSERT INTO "vn_mokhtasar" VALUES(51,46,'51:46','Và TA cũng đã tiêu diệt cộng đồng của Nuh bằng trận Đại Hồng Thủy trước đám người đã được nhắc trên đây. Quả thật, chúng là đám người không biết tuân lệnh Allah và chúng đáng bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(51,47,'51:47','Và bầu trời do TA tạo dựng nó, TA trang hoàng nó được chỉnh chu bằng sức mạnh và TA kéo dãn nó ra rộng bao la.');
INSERT INTO "vn_mokhtasar" VALUES(51,48,'51:48','Và mặt đất được TA trải bằng phẳng cho cư dân trú ngụ tựa như chiếc thảm, thật hồng phúc thay cho việc trải rộng đó.');
INSERT INTO "vn_mokhtasar" VALUES(51,49,'51:49','Và tất cả mọi thứ TA đều tạo ra theo từng cặp như: bầu trời và trái đất, đại dương và đất liền mong rằng các ngươi biết tin tưởng vào Allah duy nhất, Đấng đã tạo ra mọi thứ đều có cặp đôi cũng như mong rằng các ngươi biết ghi nhớ quyền năng của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(51,50,'51:50','Vì vậy các ngươi hãy chạy khỏi hình phạt của Allah để đến với phần thưởng của Ngài, các ngươi hãy phục tùng Ngài, các ngươi chớ đừng bất tuân Ngài, quả thật, Ta đối với các ngươi - hỡi nhân loại - chỉ là người cảnh báo về hình phạt của Ngài mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(51,51,'51:51','Và các ngươi đừng có tạo dựng cùng với Allah một thần linh để thờ phượng thay vì thờ Ngài duy nhất, quả thật, Ta đối với các ngươi chỉ là người công khai cảnh báo các ngươi về hình phạt của Ngài mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(51,52,'51:52','Các thế hệ trước đây cũng phủ nhận sứ mạng của Thiên Sứ giống như thị dân Makkah vậy, bất cứ Thiên Sứ nào được Allah phái đến gặp họ đều bị họ bảo là tên phù thủy hoặc là thằng điên.');
INSERT INTO "vn_mokhtasar" VALUES(51,53,'51:53','Hay là các thế hệ trước truyền di chúc lại cho thế hệ sau là phải phủ nhận Thiên Sứ thì phải? Không, đúng hơn tất cả bọn chúng là đám người ngông cuồng quá mức.');
INSERT INTO "vn_mokhtasar" VALUES(51,54,'51:54','Do vậy, Ngươi - hỡi Thiên Sứ - hãy lánh xa những kẻ phủ nhận đó đi. Ngươi không bị khiển trách gì đâu, bởi Ngươi đã truyền đạt hết thông tin mà Ngươi đã nhận.');
INSERT INTO "vn_mokhtasar" VALUES(51,55,'51:55','Và không cấm Ngươi nhắc nhở họ, Ngươi hãy khuyên bảo họ bởi quả thật lời nhắc nhở rất hữu dụng đối với tín đồ có đức tin nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(51,56,'51:56','Và TA đã tạo ra loài Jinn và loài người chỉ với mục đích để chúng thờ phượng TA duy nhất, chứ không phải để chúng tổ hợp cùng với TA.');
INSERT INTO "vn_mokhtasar" VALUES(51,57,'51:57','TA không cần họ ban bổng lộc hay nuôi ăn TA.');
INSERT INTO "vn_mokhtasar" VALUES(51,58,'51:58','Quả thật, Allah là Đấng cung cấp bổng lộc cho đám nô lệ của Ngài, tất cả vạn vật đều cần đến bổng lộc của Ngài để sống, Đấng có sức mạnh tuyệt đối không thế lực nào thắng được Ngài, tất cả loài Jinn và loài người đều hạ mình phủ phục trước sức mạnh của Ngài Toàn Năng.');
INSERT INTO "vn_mokhtasar" VALUES(51,59,'51:59','Đối với đám người tự bất công với bản thân họ bằng cách phủ nhận sứ mạng của Ngươi - hỡi Thiên Sứ - họ sẽ bị trừng phạt giống như đám người của những thế hệ trước đây, họ sẽ có một giới hạn nhất định, thế nên họ đừng vội vàng thúc giục TA ban hình phạt đến với họ sớm hơn thời gian đã định.');
INSERT INTO "vn_mokhtasar" VALUES(51,60,'51:60','Đáng bị tiêu diệt và bị thất bại đối với đám người phủ nhận đức tin nơi Allah và bác bỏ sứ mạng của Thiên Sứ của Ngài, những người mà họ đã được hứa hẹn về một sự trừng phạt giáng lên họ trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(52,1,'52:1','Allah thề bởi ngọn núi Tur, nơi mà Ngài đã nói chuyện với Musa.');
INSERT INTO "vn_mokhtasar" VALUES(52,2,'52:2','Ngài thề bởi quyển Kinh Qur''an đã được chép.');
INSERT INTO "vn_mokhtasar" VALUES(52,3,'52:3','Trên những miếng da thuộc được mở ra tựa như những Kinh Sách được thiên khải.');
INSERT INTO "vn_mokhtasar" VALUES(52,4,'52:4','Và Ngài thề bởi Ngôi Đền ở trên trời nơi mà các Thiên Thần vào đó thờ phượng Allah.');
INSERT INTO "vn_mokhtasar" VALUES(52,5,'52:5','Và Ngài thề bởi bầu trời được nâng cao làm mái vòm cho trái đất.');
INSERT INTO "vn_mokhtasar" VALUES(52,6,'52:6','Và Ngài thề bởi biển biển cả mênh mông nước.');
INSERT INTO "vn_mokhtasar" VALUES(52,7,'52:7','Quả thật, hình phạt của Thượng Đế Ngươi - hỡi Thiên Sứ - chắc chắn sẽ xảy ra đối với người vô đức tin. Đó là điều không tránh khỏi.');
INSERT INTO "vn_mokhtasar" VALUES(52,8,'52:8','Không có bất cứ ai giúp y thoát khỏi hình phạt hoặc ngăn chặn hình phạt cho y.');
INSERT INTO "vn_mokhtasar" VALUES(52,9,'52:9','Ngày mà bầu trời sẽ bị rung chuyển bởi sự chấn động dữ dội của giờ Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(52,10,'52:10','Và những quả núi kiên cố bị dời đi khỏi vị trí của nó.');
INSERT INTO "vn_mokhtasar" VALUES(52,11,'52:11','Sự diệt vong và thất bại vào Ngày hôm đó dành cho đám người phủ nhận sẽ diễn ra như Allah đã hứa trừng phạt đám người vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(52,12,'52:12','Họ là những ai chỉ biết vui chơi với những thú vui phù phiếm, không màng đến Ngày Phục Sinh và Triệu Tập.');
INSERT INTO "vn_mokhtasar" VALUES(52,13,'52:13','Ngày mà họ bị quẳng vào Lửa của Hỏa Ngục một cách không thương tiếc.');
INSERT INTO "vn_mokhtasar" VALUES(52,14,'52:14','Và có lời nói xỉ nhục họ: Đây chính là Lửa mà các ngươi từng bác bỏ khi giới Thiên Sứ cảnh báo các ngươi về nó.');
INSERT INTO "vn_mokhtasar" VALUES(52,15,'52:15','Há lẽ nào sự trừng phạt lại là màn ảo thuật hay chẳng lẽ các ngươi không nhận biết được?');
INSERT INTO "vn_mokhtasar" VALUES(52,16,'52:16','Các ngươi hãy vào trong đó mà nếm cái nóng của Lửa Hỏa Ngục, hãy cố mà chịu đựng đi nhưng dù cho các ngươi có cố gắng chịu đựng sức nóng của nó hay không thì cũng như nhau, bởi lẽ hình phạt của Ngày hôm nay chính là hậu quả của những việc mà các ngươi đã từng làm ở trên thế gian từ sự vô đức tin và phạm tội.');
INSERT INTO "vn_mokhtasar" VALUES(52,17,'52:17','Quả thật, người biết kính sợ Thượng Đế của họ - qua việc phục tùng mọi mệnh lệnh của Ngài - sẽ được ở trong Thiên Đàng và được hưởng những khoái lạc và niềm vui bất tận.');
INSERT INTO "vn_mokhtasar" VALUES(52,18,'52:18','Họ vui vẻ trong niềm hạnh phúc với những phần thưởng vô tận được Allah ban tặng từ thức ăn, thức uống và người bạn đời. Ngoài ra, họ còn được Thượng Đế bảo vệ thoát khỏi hình phạt của Hỏa Ngục, họ đạt được những gì họ khát khao mong đợi và được bảo vệ thoát khỏi mọi điều mà họ tránh né.');
INSERT INTO "vn_mokhtasar" VALUES(52,19,'52:19','Có lời bảo họ: Các ngươi hãy ăn uống thỏa thích những thứ mà các ngươi ưa thích, xin chúc mừng, các ngươi không còn phải lo sợ bất cứ điều gì gây hại cho các ngươi qua việc ăn uống. Đó là một phần thưởng cho việc làm tốt đẹp của các ngươi trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(52,20,'52:20','Họ ngồi hưởng lạc trên những chiếc giường đối mặt được xếp cạnh nhau, và những người vợ của họ là những nàng trinh nữ với làn da trắng và đôi mắt to tuyệt đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(52,21,'52:21','Và những ai đã tin tưởng và con cháu của họ cũng tin theo đức tin của họ, TA sẽ cho con cháu họ đoàn tụ với họ để họ được hài lòng dẫu cho việc làm của con cháu họ chưa đạt được mức độ của họ và không một việc làm nào của họ bị bỏ sót cả. Mỗi người tự chịu trách nhiệm cho việc xấu mình đã làm, không phải gánh tội cho ai khác cả.');
INSERT INTO "vn_mokhtasar" VALUES(52,22,'52:22','Và cư dân của Thiên Đàng được TA ban thêm cho đủ loại trái cây và đủ loại thịt mà họ ưa thích.');
INSERT INTO "vn_mokhtasar" VALUES(52,23,'52:23','Họ được trao cho những tách rượu trong Thiên Đàng không có kéo theo những hệ lụy xấu như rượu ở trần gian, không lời nói xàm bậy và tội lỗi do say xỉn.');
INSERT INTO "vn_mokhtasar" VALUES(52,24,'52:24','Có những đứa trẻ con đi loanh quanh họ để họ sai khiến, đám trẻ con trông như những viên ngọc trai trắng tinh được cất giữ rất cẩn thận.');
INSERT INTO "vn_mokhtasar" VALUES(52,25,'52:25','Cư dân Thiên Đàng hướng mặt về nhau, họ thường chuyện trò với nhau về cuộc sống của họ ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(52,26,'52:26','Họ đáp lời nhau: Quả thật, trước đây trên trần gian lúc còn sống với gia đình chúng tôi rất lo sợ về hình phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(52,27,'52:27','Rồi chúng tôi được Allah ban ân, Ngài hướng dẫn chúng tôi đến với Islam và Ngài đã giải cứu chúng tôi thoát khỏi hình phạt của cái nóng dữ dội.');
INSERT INTO "vn_mokhtasar" VALUES(52,28,'52:28','Quả thật, trước đây lúc còn sống trên trần gian chúng tôi đã thờ phượng Ngài, cầu xin Ngài bảo vệ chúng tôi khỏi hình phạt của Hỏa Ngục. Quả thật, Ngài là Đấng tốt đẹp, uy tín trong lời hứa với đám nô lệ của Ngài, Ngài khoan dung với họ và trong những điều tốt đẹp và sự khoan dung của Ngài qua việc Ngài đã hướng dẫn chúng tôi đến với đức tin Iman rồi thu nhận chúng tôi vào Thiên Đàng và bảo vệ chúng tôi khỏi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(52,29,'52:29','Ngươi - hỡi Thiên Sứ - hãy nhắc nhở bằng Qur''an, với những ân huệ mà Thượng Đế ban cho Ngươi từ niềm tin Iman và trí tuệ nên Ngươi chẳng phải là tên thầy bói đoán mò dựa dẫm vào loài Jinn và cũng không phải là tên thần kinh.');
INSERT INTO "vn_mokhtasar" VALUES(52,30,'52:30','Hay là đám người phủ nhận đó cho rằng: Muhammad chẳng phải là Thiên Sứ mà Y chính là một nhà thơ, chúng ta cứ chờ đợi đến khi Y chết thì chúng ta sẽ tránh được phiền phức từ Y.');
INSERT INTO "vn_mokhtasar" VALUES(52,31,'52:31','Ngươi - hỡi Thiên Sứ - hãy nói với chúng: Các ngươi hãy đợi về cái chết của Ta còn Ta thì đợi những hình phạt sẽ giáng lên các ngươi với lý do các ngươi đã phủ nhận sứ mạng của Ta.');
INSERT INTO "vn_mokhtasar" VALUES(52,32,'52:32','Chắc là đầu óc của họ đã bảo họ nói rằng Y là tên thầy bói và tên thần kinh thì phải? Đây là hai thuộc tính không thể có trong một con người, đúng hơn họ là đám người vượt quá giới luật.');
INSERT INTO "vn_mokhtasar" VALUES(52,33,'52:33','Hay chúng cho rằng, chính Muhammad đã bịa ra Qur''an chứ Y nào được ai mặc khải đâu? Không, bọn họ chỉ vì tự cao nên đã không tin vào Y và đã nói: Y đã bịa ra Nó.');
INSERT INTO "vn_mokhtasar" VALUES(52,34,'52:34','Vậy chúng hãy mang đến một lời nói giống như Qur''an này nếu Y thực sự bịa ra giống như lời chúng đã cho rằng Y đã bịa đặt.');
INSERT INTO "vn_mokhtasar" VALUES(52,35,'52:35','Hay phải chăng họ không được Đấng Tạo Hóa tạo ra hoặc phải chẳng họ tự tạo ra bản thân họ? Không thể nào tồn tại tạo vật mà không có Đấng Tạo Hóa, và lại càng không có tạo vật tự tạo ra bản thân nó, thế tại sao họ vẫn không tôn thờ Đấng đã tạo ra họ vậy?');
INSERT INTO "vn_mokhtasar" VALUES(52,36,'52:36','Hay các tầng trời và trái đất là do họ đã tạo ra? Không, họ không tin rằng Allah đã tạo hóa ra họ, bởi nếu họ tin là họ đã tôn thờ duy nhất một mình Ngài và đã tin vào Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(52,37,'52:37','Hay phải chắng họ nắm giữ trong tay tất cả kho tàng của Thượng Đế Ngươi từ bổng lộc để rồi họ muốn ban phát thế nào tùy thích, ngay cả sứ mạng Nabi họ muốn ban cho ai và không ban cho ai là quyền của họ? Hay phải chăng họ tự do tự tung tự tác làm bất cứ điều gì họ muốn?');
INSERT INTO "vn_mokhtasar" VALUES(52,38,'52:38','Hay phải chăng họ có con đường liên thông lên trời để lắng nghe lời thiên khải của Allah rằng Ngài đã thiên khải cho biết rằng họ đang ở trên chân lý. Thế hãy mang đến kẻ đã nghe được điều đó với bằng chứng xác đáng rằng lời tuyên bố của họ là thật.');
INSERT INTO "vn_mokhtasar" VALUES(52,39,'52:39','Hay phải chăng Đấng Toàn Năng chỉ có toàn con gái, điều mà họ đã không ưa thích và con trai là của họ, điều mà họ luôn thích thú đó ư?');
INSERT INTO "vn_mokhtasar" VALUES(52,40,'52:40','Hay phải chẳng Ngươi - hỡi Thiên Sứ - đã yêu cầu họ tiền công cho việc Ngươi truyền tải thông điệp của Allah đến với họ, nên đã khiến họ không có khả năng trả được phần tiền thù lao đó?');
INSERT INTO "vn_mokhtasar" VALUES(52,41,'52:41','Hay phải chăng bọn họ có kiến thức về điều huyền bí nên họ đã viết nó ra cho nhân loại biết như thế nào tùy theo ý của họ?');
INSERT INTO "vn_mokhtasar" VALUES(52,42,'52:42','Hay phải chăng đám người phủ nhận này muốn lập kế hoạch hại Ngươi và tôn giáo của Ngươi? Ngươi hãy tin tuyệt đối vào Allah, bởi đám người vô đức tin nơi Allah và Thiên Sứ của ngài mới bị sập phải cái bẫy đó, còn Ngươi thì không.');
INSERT INTO "vn_mokhtasar" VALUES(52,43,'52:43','Hay phải chăng bọn họ có một thần linh đích thực khác ngoài Allah? Thật vô can thay, trong sạch thay Allah, Ngài hoàn toàn vô can với mọi thứ mà họ đã cố gán ghép cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(52,44,'52:44','Và cho dù bọn họ có nhìn thấy một khối khổng lồ rơi từ trời xuống thì họ cũng sẽ nói: Đó chẳng qua là những đám mây kết tụ lại với nhau giống như thường lệ mà thôi, họ chẳng biết sợ hay tin tưởng gì cả.');
INSERT INTO "vn_mokhtasar" VALUES(52,45,'52:45','Thế nên, Ngươi - hỡi Thiên Sứ - cứ để mặc bọn họ sống trong ương ngạnh và chống đối cho đến ngày mà họ phải đối mặt với sự trừng phạt trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(52,46,'52:46','Ngày mà tất cả kế hoạch lớn nhỏ đều là thứ vô dụng và cũng không có ai giúp đỡ họ thoát khỏi hình phạt cho được.');
INSERT INTO "vn_mokhtasar" VALUES(52,47,'52:47','Và đám người bất công với chính bản thân mình qua việc tổ hợp (thần linh cùng với Allah) và những điều trái lệnh thì sẽ gánh chịu một sự trừng phạt trước Ngày Tận Thế. Đó là ngay trên thế gian, họ sẽ bị giết chóc và tù đày, trong cõi mộ sẽ bị trừng phạt. Tuy nhiên, đa số bọn họ không biết được điều đó nên họ cứ dửng dưng trong sự vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(52,48,'52:48','Và Ngươi - hỡi Thiên Sứ - cố mà kiên nhẫn để Allah phân xử và vì giáo luật của Ngài, chắc chắn Ngươi đang trong tầm nhìn và sự bảo vệ của Ngài, và Ngươi hãy tụng niệm Thượng Đế của Ngươi sau khi Ngươi thức dậy từ giấc ngủ.');
INSERT INTO "vn_mokhtasar" VALUES(52,49,'52:49','Và trong đêm, Ngươi hãy tụng niệm Thượng Đế của Ngươi bằng cách hành lễ Salah lúc rạng đông và lúc các vì sao bị mất dần bởi ánh sáng của ban ngày.');
INSERT INTO "vn_mokhtasar" VALUES(53,1,'53:1','Đấng Toàn Năng thề bởi vì sao khi nó rơi.');
INSERT INTO "vn_mokhtasar" VALUES(53,2,'53:2','Muhammad đã không đi lạc khỏi con đường chỉ đạo, cũng không phải là người có đức tin sàm bậy mà đích thực Người đang được hướng dẫn.');
INSERT INTO "vn_mokhtasar" VALUES(53,3,'53:3','Và Y không hề lấy Qur''an ra nói theo sở thích của Y.');
INSERT INTO "vn_mokhtasar" VALUES(53,4,'53:4','Quả thật, Qur''an chính là lời phán của Allah đã thiên khải xuống cho Người qua trung gian Đại Thiên Thần Jibril.');
INSERT INTO "vn_mokhtasar" VALUES(53,5,'53:5','Đại Thiên Thần Jibril đầy quyền lực đã dạy cho Người.');
INSERT INTO "vn_mokhtasar" VALUES(53,6,'53:6','Jibril có dáng hình đẹp đẽ và ngài ngự bên trên theo bản chất tạo hóa mà Allah đã tạo ra cho ngài.');
INSERT INTO "vn_mokhtasar" VALUES(53,7,'53:7','Và Jibril xuất hiện tại phía chân trời cao từ hướng mặt trời mọc.');
INSERT INTO "vn_mokhtasar" VALUES(53,8,'53:8','Sau đó Jibril đến gần Thiên Sứ, rồi đến gần hơn nữa.');
INSERT INTO "vn_mokhtasar" VALUES(53,9,'53:9','Jibril đến gần Thiên Sứ với khoảng cách như hai đầu của cây cung hoặc gần hơn thế nữa.');
INSERT INTO "vn_mokhtasar" VALUES(53,10,'53:10','Như thế đó, Jibril đã truyền đạt lại lời mặc khải mà ngài đã tiếp nhận từ Allah lại cho người bề tôi của Ngài - Muhammad.');
INSERT INTO "vn_mokhtasar" VALUES(53,11,'53:11','Con tim của Muhammad không hề dối những gì con mắt của Người đã nhìn thấy.');
INSERT INTO "vn_mokhtasar" VALUES(53,12,'53:12','Phải chăng các ngươi - hỡi những kẻ đa thần - muốn tranh luận với Muhammad về những gì Y đã được Allah cho nhìn thấy trong đêm Y được đưa lên trời ư?!');
INSERT INTO "vn_mokhtasar" VALUES(53,13,'53:13','Và quả thật, Muhammad đã tận mắt nhìn thấy Jibril bằng hình dạng thật trong đêm Người được hộ tống thăng thiên.');
INSERT INTO "vn_mokhtasar" VALUES(53,14,'53:14','Tại cây Sidrah Al-Muntaha, đó là một cái cây khổng lồ ở tầng trời thứ bảy.');
INSERT INTO "vn_mokhtasar" VALUES(53,15,'53:15','Tại cái cây đó chính là Thiên Đàng vĩnh cữu.');
INSERT INTO "vn_mokhtasar" VALUES(53,16,'53:16','Cây Sidrah được Allah phủ kín những điều vĩ đại chỉ có Allah mới biết rõ bí mật trên trong nó.');
INSERT INTO "vn_mokhtasar" VALUES(53,17,'53:17','Thiên Sứ không dám đảo mắt nhìn bên phải hay bên trái và không nhìn vượt mức qui định.');
INSERT INTO "vn_mokhtasar" VALUES(53,18,'53:18','Chắc chắn Muhammad đã tận mắt nhìn thấy một số dấu hiệu vĩ đại của Thượng Đế của Người vào đêm thăng thiên chứng minh về quyền năng của Ngài, Người đã thấy Thiên Đàng, thấy Hỏa Ngục và những thứ khác.');
INSERT INTO "vn_mokhtasar" VALUES(53,19,'53:19','Thế các ngươi - hỡi những kẻ đa thần - có thấy những bục tượng Al-Lat và Al-''Uzza được thờ phượng thay vì Allah không?');
INSERT INTO "vn_mokhtasar" VALUES(53,20,'53:20','Và luôn cả Manah, vị thần linh thư ba từ các bục tượng của các ngươi, hãy cho Ta biết chúng có giúp ích hay hãm hại các ngươi được không?');
INSERT INTO "vn_mokhtasar" VALUES(53,21,'53:21','Phải chăng các ngươi - hỡi những kẻ thờ đa thần - có được con trai thứ mà các ngươi ưa thích còn Đấng Toàn Năng có con gái thứ mà các ngươi căm ghét?!');
INSERT INTO "vn_mokhtasar" VALUES(53,22,'53:22','Cách phân chia mà các ngươi đã chia theo sở thích của các ngươi rất bất công.');
INSERT INTO "vn_mokhtasar" VALUES(53,23,'53:23','Các bục tượng chỉ mang những cái tên rỗng không có ý nghĩa thực sự, chúng chẳng có tí thuộc tính nào của thượng đế cả. Các tên gọi đó do chính các ngươi và tổ tiên của mình đặt cho chúng chứ Allah đã không ban cho chúng thẩm quyền bao giờ. Những kẻ đa thần chỉ mù quáng đi theo niềm tin do chính bản thân họ tưởng tượng ra và để thỏa mãn ham muốn của bản thân họ dưới sự bày trò của Shaytan trong tiềm thức của họ. Trong khi đó Thượng Đế của họ đã thực sự gởi đến họ sự chỉ đạo thông qua chiếc lưỡi của Thiên Sứ của Ngài, vậy mà họ vẫn không theo.');
INSERT INTO "vn_mokhtasar" VALUES(53,24,'53:24','Hay con người ước muốn được các bục tượng đó can thiệp bảo hộ cho họ trước Allah?!');
INSERT INTO "vn_mokhtasar" VALUES(53,25,'53:25','Không, con người chẳng được thứ mà y mong muốn bởi Đời Sau và cả đời này cũng đều thuộc về một mình Allah, Ngài muốn ban phát và cấm đoán như thế nào là tùy Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(53,26,'53:26','Và có được mấy Thiên Thần trên các tầng trời có được thẩm quyền biện hộ cho người khác, ngoại trừ ai đó trong họ được Allah cho phép biện hộ và hài lòng về đối tượng được họ biện hộ. Tất nhiên Allah sẽ không cho phép người tạo dựng một đối tượng ngang hàng với Ngài quyền biện hộ đó và cũng chẳng hài lòng về kẻ được hưởng biện hộ là kẻ thờ phượng vật thể khác ngoài Allah.');
INSERT INTO "vn_mokhtasar" VALUES(53,27,'53:27','Thật vậy đối với những ai không có đức tin vào sự phục sinh ở Đời Sau đều tin rằng các Thiên Thần là nữ giới, là những đứa con gái của Allah, quả thật Allah tối cao và vĩ đại hơn những gì họ nói về Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(53,28,'53:28','Việc họ gọi các Thiên Thần là nữ giới là không dựa trên bất cứ cơ sở nào, chẳng qua họ chỉ làm theo trí tưởng tượng và sở thích của bản thân, trong khi những điều này không thay đổi được sự thật bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(53,29,'53:29','Vì vậy, Ngươi - hỡi Thiên Sứ - hãy rời xa những ai quay lưng với lời nhắc nhở của Allah và không quan tâm đến nó, y chỉ muốn mỗi cuộc sống trần gian này, y không hề đầu tư cho Đời Sau bởi y đã không tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(53,30,'53:30','Với câu nói của người đa thần - gọi Thiên Thần là nữ giới - là sự giới hạn của kiến thức bởi họ vốn là những kẻ ngu muội, họ không đến được với sự kiên định. Quả thât, Thượng Đế của Ngươi - hỡi Thiên Sứ - luôn tận tường ai là kẻ chống lại con đường chân lý và ai là người được hướng dẫn đến với con đường đó và không một điều gì có thể giấu giếm được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(53,31,'53:31','Vạn vật ở trên trời và dưới đất từ đều thuộc về vương quyền của Ngài từ sự tạo hóa, quản lý và chi phối, để Ngài trừng phạt những kẻ đã làm tội lỗi bằng hình phạt thích đáng và để trọng thưởng cho những người tin tưởng đã làm tốt những việc làm của họ bằng cách cho vào Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(53,32,'53:32','Đối với những ai không vi phạm những tội lớn và những tội lỗi đáng xấu hổ (như tình dục ngoài hôn nhân) nhưng không tránh được những tội nhỏ, mong rằng những tội lỗi nhỏ đó sẽ được xí xóa khi biết từ bỏ những đại tội và hành đạo nhiều hơn, chắc chắn Thượng Đế của Ngươi - hỡi Thiên Sứ - có tấm lòng tha thứ bao la, Ngài sẵn sàng tha thứ cho đám Bầy Tôi của Ngài khi họ biết ăn năn sám hối với Ngài. Ngài luôn biết rõ mọi hoàn cảnh và mọi việc làm của các ngươi, bởi vì Ngài đã tạo ông tổ Adam của các ngươi từ đất và Ngài đã nắn hình thể của các ngươi khi các ngươi còn là bào thai trong bụng mẹ của các ngươi, không gì giấu được Ngài. Vì vậy, các ngươi đừng tự đề cao mình là người có lòng kính sợ Allah trong khi Ngài luôn biết rõ ai mới thật sự là người biết kính sợ và phục tùng theo mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(53,33,'53:33','Ngươi có thấy sự xấu xa của kẻ ngoảnh mặt với Islam sau khi y rất gần với nó.');
INSERT INTO "vn_mokhtasar" VALUES(53,34,'53:34','Trong khi y chỉ cho đi một ít tiền của rồi không cho nữa, bởi bản tính vốn keo kiệt của y, vậy mà y lại tự đề cao bản thân.');
INSERT INTO "vn_mokhtasar" VALUES(53,35,'53:35','Phải chăng y biết được điều vô hình nên y thấy được những gì đang xảy ra trong tương lai?');
INSERT INTO "vn_mokhtasar" VALUES(53,36,'53:36','Hay y nói dối rồi đổ thừa cho Allah rằng Ngài đã không báo trước điều đó trong Kinh Sách trước đây đã thiên khải cho Musa?');
INSERT INTO "vn_mokhtasar" VALUES(53,37,'53:37','Và trong các tờ Kinh của Ibrahim, một người đã làm tròn mọi trọng trách mà Thượng Đế đã giao phó.');
INSERT INTO "vn_mokhtasar" VALUES(53,38,'53:38','Rằng con người không chịu tội thay cho người khác.');
INSERT INTO "vn_mokhtasar" VALUES(53,39,'53:39','Và con người không hưởng được gì ngoài những điều mà y đã từng làm trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(53,40,'53:40','Và rồi việc làm của y sẽ sớm được nhìn thấy tận mắt trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(53,41,'53:41','Rồi y sẽ được ban thưởng cho việc làm của mình một cách đầy đủ không thiếu sót.');
INSERT INTO "vn_mokhtasar" VALUES(53,42,'53:42','Và thật ra nơi Thượng Đế của Ngươi - hỡi Thiên Sứ - là nơi trở về cuối cùng của con người sau khi chết.');
INSERT INTO "vn_mokhtasar" VALUES(53,43,'53:43','Và Ngài là Đấng muốn bất cứ ai vui mừng đến bật cười hoặc muốn ai đau khổ đến khóc tùy ý Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(53,44,'53:44','Và Ngài là Đấng kết thúc sự sống bằng cái chết ở trần gian và phục sinh cái chết bằng cách dựng sống lại.');
INSERT INTO "vn_mokhtasar" VALUES(53,45,'53:45','Và Ngài là Đấng đã tạo ra hai loài giống đực và giống cái.');
INSERT INTO "vn_mokhtasar" VALUES(53,46,'53:46','Từ tinh dịch khi được đặt vào dạ con của người phụ nữ.');
INSERT INTO "vn_mokhtasar" VALUES(53,47,'53:47','Và Ngài phải phục sinh tất cả tạo vật sau khi đã bị diệt vong.');
INSERT INTO "vn_mokhtasar" VALUES(53,48,'53:48','Và Ngài chính là Đấng muốn cho ai giàu có thì y sẽ được giàu bằng tài sản mà Ngài ban cho và khiến y hài lòng những gì đã ban cho.');
INSERT INTO "vn_mokhtasar" VALUES(53,49,'53:49','Và Ngài chính là Thượng Đế của sao Thiên Lang mà người đa thần thờ phượng ngoài Allah.');
INSERT INTO "vn_mokhtasar" VALUES(53,50,'53:50','Và Ngài chính là Đấng đã hủy diệt toàn bộ dân tộc ''Ad trước đây, một dân tộc thuộc cộng đồng của Nabi Hud khi mà họ vẫn ngoan cố ở trên sự vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(53,51,'53:51','Và Ngài hủy diệt luôn cả dân tộc Thamud thuộc cộng đồng của Nabi Saleh, không ai trong họ sống sót.');
INSERT INTO "vn_mokhtasar" VALUES(53,52,'53:52','Và trước ''Ad và Thamud Ngài đã tiêu diệt cộng đồng của Nuh. Quả thật cộng đồng Nuh gây ra sự bất công quá độ, ngông cuồng hơn cả ''Ad và Thamud, bởi Nabi Nuh đã ở cùng họ đến chín trăm năm mươi năm để kêu gọi họ mỗi một điều - đó là tôn thờ duy nhất một mình Allah - nhưng họ không đáp lại lời kêu gọi của Người.');
INSERT INTO "vn_mokhtasar" VALUES(53,53,'53:53','Và những thành phố của cộng đồng Nabi Lut đã bị Ngài đưa cao lên trời rồi lật úp chúng xuống đất trở lại.');
INSERT INTO "vn_mokhtasar" VALUES(53,54,'53:54','Rồi Ngài tiếp tục trút lên chúng cơn mưa đá sau khi chúng đã bị nâng cao lên trời rồi lật úp xuống đất.');
INSERT INTO "vn_mokhtasar" VALUES(53,55,'53:55','Hãy chỉ xem đâu là dấu hiệu nói về quyền năng của Thượng Đế ngươi hỡi con người khiến ngươi hoài nghi, phủ nhận nó?');
INSERT INTO "vn_mokhtasar" VALUES(53,56,'53:56','Đây chính là Thiên Sứ được gởi đến cho các ngươi thuộc tầng lớp Thiên Sứ trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(53,57,'53:57','Ngày Tận Thế đang tiến đến rất gần.');
INSERT INTO "vn_mokhtasar" VALUES(53,58,'53:58','Không ai có thể ngăn chặn được nó xảy ra cũng như biết trước được bao giờ nó xảy ra, ngoại trừ một mình Allah.');
INSERT INTO "vn_mokhtasar" VALUES(53,59,'53:59','Phải chăng với Qur''an đang xướng đọc cho các ngươi nghe này đây khiến các ngươi ngạc nhiên khi biết Nó đến từ Allah?!');
INSERT INTO "vn_mokhtasar" VALUES(53,60,'53:60','Và các ngươi bật cười chế nhạo về điều đó và không hề biết khóc khi nghe được những lời cảnh báo.');
INSERT INTO "vn_mokhtasar" VALUES(53,61,'53:61','Và rồi các ngươi cười khinh khi chẳng thèm quan tâm.');
INSERT INTO "vn_mokhtasar" VALUES(53,62,'53:62','Thế nên các ngươi hãy quỳ lạy Allah duy nhất và thành tâm thờ phượng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(54,1,'54:1','Thời khắc tận thế đang tiến rất gần, và mặt trăng đã từng bị chẻ đôi trong thời của Thiên Sứ, việc chẻ đôi đó chính là một trong các dấu hiệu mà Thiên Sứ đã trình bày.');
INSERT INTO "vn_mokhtasar" VALUES(54,2,'54:2','Thế nhưng mỗi khi người đa thần chứng kiến bằng chứng được trình bày rõ ràng khẳng định sứ mạng của Thiên Sứ thì họ vẫn không chấp nhận, ngược lại còn nói: Với những bằng chứng mà chúng tôi đã nhìn thấy toàn là những thứ bịa đặt, giả dối.');
INSERT INTO "vn_mokhtasar" VALUES(54,3,'54:3','Và họ đã phủ nhận mọi chân lý được mang đến cho họ bằng sự mù quáng thuận theo sở thích của họ và rồi mọi việc - dù tốt hay xấu - sẽ đều được phơi bày sự thật trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(54,4,'54:4','Trong khi đó họ đã nhận được nguồn tin xác thực về những cộng đồng trước đây bị Allah tiêu diệt vì tội phủ nhận đức tin, hành động bất công của họ, lẽ nào lại không đủ răn đe cho hành động vô đức tin và bất công của họ ư?!');
INSERT INTO "vn_mokhtasar" VALUES(54,5,'54:5','Và nguồn tin được mang đến là bằng chứng cụ thể, xác đáng, tuy nhiên không đủ sức răn đe đám người không tin tưởng Allah và Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(54,6,'54:6','Nếu như họ không được hướng dẫn thì Ngươi - hỡi Thiên Sứ - hãy bỏ mặc họ mà chờ đợi Ngày mà Thiên Thần thổi còi được lệnh thổi còi kêu gọi tập trung để tất cả đối diện với sự thật kinh hoàng.');
INSERT INTO "vn_mokhtasar" VALUES(54,7,'54:7','Cặp mắt của họ thể hiện rõ nỗi nhục nhã lúc họ rời khỏi mồ của mình, họ hướng đến nơi tập trung chịu thanh toán tựa như bầy cào cào đang bay tứ tán.');
INSERT INTO "vn_mokhtasar" VALUES(54,8,'54:8','Họ vội vã chạy theo lời gọi triệu tập, người vô đức tin sẽ nói: Hôm nay đúng là Ngày khó khăn bởi phải đối diện với biết bao là gian truân và nỗi sợ.');
INSERT INTO "vn_mokhtasar" VALUES(54,9,'54:9','Trước đám người phủ nhận sứ mạng của Ngươi - hỡi Thiên Sứ - dân chúng của Nuh đã phủ nhận Người khi được gởi đến với họ, và họ bảo: hắn là kẻ thần kinh. Và họ không ngừng mắng chửi, hạ nhục Người bằng mọi cách có thể, thậm chí còn hăm dọa nếu Người không dừng việc mời gọi.');
INSERT INTO "vn_mokhtasar" VALUES(54,10,'54:10','Thế là Nuh khấn vái Thượng Đế của Người: Quả thật, dân chúng đã chiếm ưu thế trên bề tôi, họ không nghe lời gọi của bề tôi, xin Ngài giúp bề tôi giáng lên họ sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(54,11,'54:11','Vì vậy, TA đã mở toang các cửa trời để nước ồ ạt trút xuống không ngừng.');
INSERT INTO "vn_mokhtasar" VALUES(54,12,'54:12','Và TA mở đất cho nước phun trào lên thành dòng chảy mạnh. Thế là hai dòng nước gặp nhau, nước mưa từ trời xuống và nước từ đất lên tuân theo sắc lệnh mà Allah đã tiền định, thế là nó đã nhấn chìm tất cả ngoại trừ ai Allah muốn.');
INSERT INTO "vn_mokhtasar" VALUES(54,13,'54:13','Và TA đã chuyên chở Nuh trên chiếc tàu được đóng bởi ván gỗ và đinh, thế là TA đã cứu vớt Y và những ai cùng Y thoát khỏi chết chìm.');
INSERT INTO "vn_mokhtasar" VALUES(54,14,'54:14','Con tàu được TA trông chừng bảo vệ bình an trước những con sóng to liên hồi. Ta giúp đỡ Nuh chiến thắng trước những ai phủ nhận Y và vô đức tin với những gì được gởi đến từ Allah.');
INSERT INTO "vn_mokhtasar" VALUES(54,15,'54:15','Và thật ra TA đã lưu truyền việc trừng phạt đám người đó để làm bài học cho thế hệ sau, thế có ai biết lấy đó làm gương chăng?');
INSERT INTO "vn_mokhtasar" VALUES(54,16,'54:16','TA đã trừng phạt thế nào đối với đám người phủ nhận và lời cảnh báo hủy diệt dành cho chúng đã ra sao?');
INSERT INTO "vn_mokhtasar" VALUES(54,17,'54:17','Và quả thật TA đã làm cho Qur''an dễ dàng để ghi nhớ và để răn đe, vậy có ai biết khắc ghi những lời răn đe đó chăng?');
INSERT INTO "vn_mokhtasar" VALUES(54,18,'54:18','Người dân ''Ad đã phủ nhận vị Nabi Hud của họ, các ngươi - hỡi dân Makkah - hãy suy ngẫm xem sự trừng phạt của TA ra sao đối với kẻ phủ nhận? Và lời răn đe về việc hủy diệt họ đã như thế nào?');
INSERT INTO "vn_mokhtasar" VALUES(54,19,'54:19','Chắc chắn TA đã trừng phạt họ bằng trận gió buốt giá trong ngày đại nạn xảy ra liên tiếp với họ cho đến khi Hỏa Ngục được mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(54,20,'54:20','Con người bị bốc bay lên khỏi mặt đất, và bị ném đi nơi khác chôn đầu xuống đất tựa như những thân cây chà là trơ trụi không còn ngọn.');
INSERT INTO "vn_mokhtasar" VALUES(54,21,'54:21','Các ngươi - hỡi dân Makkah - hãy suy ngẫm xem sự trừng phạt của TA đã ra sao đối với kẻ phủ nhận? Và lời răn đe về việc hủy diệt họ đã như thế nào?');
INSERT INTO "vn_mokhtasar" VALUES(54,22,'54:22','Và quả thật TA đã làm cho Qur''an dễ dàng để ghi nhớ và để răn đe, vậy có ai biết khắc ghi những lời răn đe đó chăng?');
INSERT INTO "vn_mokhtasar" VALUES(54,23,'54:23','Dân chúng Thamud đã phủ nhận lời khuyên bảo và đe dọa của Thiên Sứ Saleh.');
INSERT INTO "vn_mokhtasar" VALUES(54,24,'54:24','Họ quyết khăng khăng chối bỏ, nói: Chúng ta phải nghe theo một con người phàm tục giống như chúng ta hay sao? Nếu chúng ta theo y trong trường hợp này là chúng ta đã lệch khỏi chân lý và sẽ bị bất ổn.');
INSERT INTO "vn_mokhtasar" VALUES(54,25,'54:25','Lẽ nào chỉ chọn một mình Y để mặc khải thay vì ai đó trong chúng ta ư? Không đúng hơn Y là kẻ khoác lác tự cao.');
INSERT INTO "vn_mokhtasar" VALUES(54,26,'54:26','Rồi vào Ngày Tận Thế, họ sẽ sớm biết ai mới thật sự là kẻ khoác lác tự cao, Saleh hay chính họ?');
INSERT INTO "vn_mokhtasar" VALUES(54,27,'54:27','Quả thật, TA rút con lạc đà cái ra từ tảng đá và gởi đến họ để thử thách họ, cho nên Ngươi - hỡi Saleh - hãy chờ đợi, quan sát xem họ sẽ làm gì nó và nó sẽ làm gì họ và Ngươi hãy cố nhẫn nại trước mọi khó khăn họ gây ra cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(54,28,'54:28','Và Ngươi (Saleh) hãy cho họ biết nước giếng của họ phải chia đôi với con lạc đà, một ngày của nó và một ngày của họ, vào ngày của bên nào thì chỉ bên đó mới được lấy nước.');
INSERT INTO "vn_mokhtasar" VALUES(54,29,'54:29','Rồi họ kêu gọi một người của họ đến giết con lạc đà, và y đã cầm kiếm sát hại con lạc đà, thực thi theo lệnh của dân chúng của y.');
INSERT INTO "vn_mokhtasar" VALUES(54,30,'54:30','Các ngươi - hỡi dân Makkah - hãy suy ngẫm xem sự trừng phạt của TA ra sao đối với kẻ phủ nhận? Và lời răn đe về việc hủy diệt họ đã như thế nào?');
INSERT INTO "vn_mokhtasar" VALUES(54,31,'54:31','Và rồi TA đã giáng lên họ một tiếng gầm thét, thế là đã tiêu diệt sạch bọn họ, hình ảnh của họ giống như những cây cỏ khô trên những cánh đồng cho súc vật ăn vậy.');
INSERT INTO "vn_mokhtasar" VALUES(54,32,'54:32','Và quả thật TA đã làm cho Qur''an dễ dàng để ghi nhớ và để răn đe, vậy có ai biết khắc ghi những lời răn đe đó chăng?');
INSERT INTO "vn_mokhtasar" VALUES(54,33,'54:33','Dân chúng của Lut đã phủ nhận lời khuyên bảo và răn đe của Thiên Sứ Lut dành cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(54,34,'54:34','Quả thật, TA đã trừng phạt họ bằng cơn gió mang theo đá ném vào họ, ngoại trừ gia đình của Lut không bị trừng phạt, TA đã cứu họ được bình an lúc cuối đêm.');
INSERT INTO "vn_mokhtasar" VALUES(54,35,'54:35','Việc TA cứu vớt họ thoát khỏi Hỏa Ngục là một ân huệ dành cho họ, tương tự như phần thưởng dành cho Lut, TA cũng ban thưởng cho những ai biết tri ơn ân huệ mà Allah đã ban.');
INSERT INTO "vn_mokhtasar" VALUES(54,36,'54:36','Và trước đây họ đã được Lut cảnh báo về hình phạt của TA nhưng họ đã không tin và đã phủ nhận nó.');
INSERT INTO "vn_mokhtasar" VALUES(54,37,'54:37','Và chúng đã từng yêu cầu Lut để mặc chúng và những vị khách của Người là nhóm Thiên Thần để chúng tự do làm chuyện đồi bại, thế là họ bị TA cướp đi đôi mắt khiến chúng không thấy gì và TA bảo chúng: Các ngươi hãy nếm hình phạt của TA và hậu quả của lời cảnh báo các ngươi của TA đến với các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(54,38,'54:38','Và TA đã trừng phạt chúng vào buổi sáng tinh mơ, nó liên hồi kéo dài mãi đến sự trừng phạt ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(54,39,'54:39','Và có lời bảo chúng: Các ngươi hãy nếm hình phạt mà TA đã giáng lên các ngươi và hậu quả cho lời cảnh báo của Lut dành cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(54,40,'54:40','Và quả thật TA đã làm cho Qur''an dễ dàng để ghi nhớ và để răn đe, vậy có ai biết khắc ghi những lời răn đe đó chăng?');
INSERT INTO "vn_mokhtasar" VALUES(54,41,'54:41','Và bè lũ của Fir''awn đã được Musa và Harun cảnh báo chúng.');
INSERT INTO "vn_mokhtasar" VALUES(54,42,'54:42','Chúng đã bác bỏ hết tất cả bằng chứng được gởi đến từ TA, nên chúng đã bị TA trừng phạt về tội bác bỏ đó bằng sức mạnh của Đấng Toàn Năng, không ai cưỡng lại được, và không ai tránh khỏi.');
INSERT INTO "vn_mokhtasar" VALUES(54,43,'54:43','Phải chẳng đám người đa thần các ngươi - hỡi dân Makkah - tốt hơn những đám người vô đức tin đã được đề cập như dân của Nuh, dân của ''Ad, dân của Thamud, dân của Lut, cả tên Fir''awn và bè lũ của hắn ư? Hoặc phải chăng các ngươi vô can với những hình phạt của Allah được ghi chép trong các Kinh Sách đã được thiên khải trước đây?');
INSERT INTO "vn_mokhtasar" VALUES(54,44,'54:44','Phải chăng đám người đa thần ở Makkah cho là tất cả chúng là một tập thể đoàn kết chống lại mọi thế lực muốn gây hại và chia rẽ chúng ư?');
INSERT INTO "vn_mokhtasar" VALUES(54,45,'54:45','Rồi khối liên minh đa thần này sẽ sớm thất bại và quay lưng bỏ chạy trước mặt những người tin tưởng như đã từng xảy ra trong Ngày Badr.');
INSERT INTO "vn_mokhtasar" VALUES(54,46,'54:46','Không đâu, Giờ Tận Thế mà họ đã phủ nhận chính là điểm hẹn để trừng phạt bọn họ, và Giờ Tận Thế sẽ khắc nghiệt hơn, khủng khiếp hơn hình phạt ở trần gian ở Ngày Badr.');
INSERT INTO "vn_mokhtasar" VALUES(54,47,'54:47','Chắc hẳn đám người tội lỗi và vô đức tin sẽ bị lệch khỏi chân lý và chịu sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(54,48,'54:48','Ngày mà họ sẽ lôi úp mặt vào Hỏa Ngục và có lời bảo miệt thị họ: Các ngươi hãy nếm đi hình phạt của lửa.');
INSERT INTO "vn_mokhtasar" VALUES(54,49,'54:49','Tất cả vạn vật trong vũ trụ đều được TA tạo hóa dựa trên sự tiền định, tất cả đều đúng như ý muốn và kế hoạch của TA, tất cả mọi thứ đều trùng khớp với mọi thứ TA ghi chép trong Quyển Sổ Mẹ (Bản Văn Lưu Trữ).');
INSERT INTO "vn_mokhtasar" VALUES(54,50,'54:50','Và khi TA muốn bất cứ gì thì chỉ cần TA phán một từ duy nhất, đó là: "Kun" (hãy thành) thì lập tức nó sẽ thành lập tức, nhanh như một cái chớp mắt.');
INSERT INTO "vn_mokhtasar" VALUES(54,51,'54:51','Và trước đây TA đã tiêu diệt những thế hệ vô đức tin như các ngươi, vậy có ai biết lấy đó làm bài học mà tránh đi hành động vô đức tin không?');
INSERT INTO "vn_mokhtasar" VALUES(54,52,'54:52','Và tất cả những gì con người làm đều được ghi chép lại chi tiết, không thiếu sót bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(54,53,'54:53','Và tất cả mọi việc nhỏ như lời nói hay hành động, cũng như việc lớn đều được ghi chép vào sổ lưu trữ và trong Quyển Sổ mẹ, rồi dựa vào đó Allah sẽ thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(54,54,'54:54','Quả thật, những người kính sợ Thượng Đế của họ qua việc phục tùng mọi mệnh lệnh của Ngài sẽ được ở trong các Ngôi Vườn Thiên Đàng với những dòng sông chảy, họ sẽ hưởng thụ trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(54,55,'54:55','Tại nơi toàn những lời chân thật, không có lời nói nhảm nhí và tội lỗi, được ở cùng Đấng Chúa Tể Toàn Năng nắm mọi vương quyền, Quyền Năng của Ngài vượt lên trên tất cả, không ai bị tra hỏi về sự hưởng thụ trường tồn trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(55,1,'55:1','Đấng Rất Mực Độ Lượng, có lòng thương xót bao la.');
INSERT INTO "vn_mokhtasar" VALUES(55,2,'55:2','Ngài đã cho con người thuộc lòng Qur''an và hiểu được ý nghĩa của Nó dễ dàng.');
INSERT INTO "vn_mokhtasar" VALUES(55,3,'55:3','Ngài đã tạo ra con người với hình hài tốt đẹp nhất.');
INSERT INTO "vn_mokhtasar" VALUES(55,4,'55:4','Ngài đã dạy con người cách diễn giải mọi thứ như đã suy nghĩ bằng lời nói và chữ viết.');
INSERT INTO "vn_mokhtasar" VALUES(55,5,'55:5','Mặt trời và mặt trăng được sắp xếp bơi theo từng vĩ đạo cố định của riêng mỗi cái để con người nhận biết về năm tháng và cách tính toán.');
INSERT INTO "vn_mokhtasar" VALUES(55,6,'55:6','Tất cả tinh tú trên trời và tất cả cây cối dưới đất đều quỳ lạy Allah, đều phục tùng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(55,7,'55:7','Và Ngày đã nâng bầu trời lên cao làm vòm che cho trái đất và Ngài đặt công lý ở trên trái đất rồi ra lệnh cho những đám bầy tôi của Ngài phải tuân theo.');
INSERT INTO "vn_mokhtasar" VALUES(55,8,'55:8','Ngài đã thiết lập công lý để các ngươi không được bất công - hỡi nhân loại - và không được gian lận trong việc cân đong và đo lường.');
INSERT INTO "vn_mokhtasar" VALUES(55,9,'55:9','Và các ngươi hãy cân, đong, đo cho đúng khi mua bán giữa các ngươi, chớ gian lận khi đong, cân cho người khác.');
INSERT INTO "vn_mokhtasar" VALUES(55,10,'55:10','Và trái đất được Ngài trải rộng làm chổ cư ngụ cho vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(55,11,'55:11','Từ đất cho mọc lên cây cối cho ra hoa quả từng chùm và những cây chà là đầy trái quả.');
INSERT INTO "vn_mokhtasar" VALUES(55,12,'55:12','Và các loại hạt như lúa mì, lúa mạch và những loại cây có hương thơm.');
INSERT INTO "vn_mokhtasar" VALUES(55,13,'55:13','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho hai loài các ngươi - hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,14,'55:14','Ngài đã tạo ra Adam từ đất sét, một loại đất khi khô phát ra âm thanh giống như loại đất làm đồ gốm sứ.');
INSERT INTO "vn_mokhtasar" VALUES(55,15,'55:15','Và Ngài đã tạo ra Jaan - tổ tiên của loài Jinn - từ lửa ngọn không có khói.');
INSERT INTO "vn_mokhtasar" VALUES(55,16,'55:16','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho hai loài các ngươi - hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,17,'55:17','Ngài là Thượng Đế của hai hướng của mặt trời mọc và hai hướng của mặt trời lặn vào hai mùa đông và mùa hạ.');
INSERT INTO "vn_mokhtasar" VALUES(55,18,'55:18','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,19,'55:19','Allah đã để hai nguồn biển (mặn và ngọt) giao nhau mà mắt có thể nhìn thấy.');
INSERT INTO "vn_mokhtasar" VALUES(55,20,'55:20','Giữa hai biển mặn và ngọt có một bức chắn vô hình không cho hai chúng xâm phạm lẫn nhau mục đích để bên ngọt vẫn giữ được cái ngọt của mình và bên mặn vẫn giữ được cái mặn của mình.');
INSERT INTO "vn_mokhtasar" VALUES(55,21,'55:21','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và con người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,22,'55:22','Từ hai loại biển đó cho ra ngọc trai và san hô to nhỏ các loại.');
INSERT INTO "vn_mokhtasar" VALUES(55,23,'55:23','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi - hỡi loài Jinn và con người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,24,'55:24','Duy chỉ Đấng Toàn Năng đã làm cho những con tàu khổng lồ di chuyển trên biển tựa như những quả núi di động.');
INSERT INTO "vn_mokhtasar" VALUES(55,25,'55:25','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và con người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,26,'55:26','Tất cả những ai (những gì) trên trái đất chắc chắn đều sẽ phải tiêu vong.');
INSERT INTO "vn_mokhtasar" VALUES(55,27,'55:27','Và chỉ còn lại mỗi sắc diện Thượng Đế Ngươi - hỡi Thiên Sứ -, Ngài là Đấng Tối Cao, Đấng Tử Tế và Hồng Phúc đối với đám nô lệ của Ngài. Ngài không bao giờ đối mặt với sự tiêu vong.');
INSERT INTO "vn_mokhtasar" VALUES(55,28,'55:28','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã cho các ngươi - hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,29,'55:29','Tất cả những ai trên các tầng trời như Thiên Thần và tất cả những ai dưới đất như loài Jinn và con người đều cầu xin Ngài về nhu cầu của họ. Mỗi ngày, Ngài luôn ở trên công việc của Ngài trong việc quản lý và trông coi các sự việc của đám bề tôi của Ngài như làm cho sống, làm cho chết, ban phát bổng lộc và các công việc khác.');
INSERT INTO "vn_mokhtasar" VALUES(55,30,'55:30','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,31,'55:31','TA sẽ dành thời gian riêng biệt để tính sổ với hai ngươi - hỡi loài người và loài Jinn - TA sẽ thưởng phạt cả hai một cách thỏa đáng.');
INSERT INTO "vn_mokhtasar" VALUES(55,32,'55:32','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,33,'55:33','Vào Ngày Tận Thế, sau khi tập trung toàn thể loài Jinn và loài người thì Allah phán: Này hỡi toàn thể loài Jinn và loài người, nếu các ngươi có thể tìm được lối thoát khỏi các tầng trời và trái đất thì các ngươi hãy làm đi, tuy nhiên, các ngươi chắc chắn không làm được ngoại trừ có sức mạnh và năng lực tuyệt đối, nhưng làm sao các ngươi có được những thứ đó.');
INSERT INTO "vn_mokhtasar" VALUES(55,34,'55:34','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,35,'55:35','Các ngươi - hỡi loài Jinn và loài người - được gửi đến lửa không khói và đồng nấu chảy (hoặc khói không lửa) mà không một ai có thể ngăn cản nó cho được.');
INSERT INTO "vn_mokhtasar" VALUES(55,36,'55:36','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,37,'55:37','Khi bầu trời nứt ra để cho Thiên Thần giáng trần, nó tựa như tấm da màu đỏ (hoặc dầu đang sôi).');
INSERT INTO "vn_mokhtasar" VALUES(55,38,'55:38','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,39,'55:39','Trong Ngày vĩ đại đó không ai tra hỏi tội lỗi của bất cứ con người cũng như bất cứ tên Jinn nào bởi Allah am tường mọi hành động của họ.');
INSERT INTO "vn_mokhtasar" VALUES(55,40,'55:40','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,41,'55:41','Vào Ngày Phục Sinh, đám người tội lỗi sẽ được nhận biết bằng những dấu hiệu riêng đó là gương mặt đen sạm và mắt xanh dương, rồi họ bị túm lấy chùm tóc trước trán và đôi bàn chân ném vào Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(55,42,'55:42','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,43,'55:43','Và có lời nói xỉ nhục họ: Đây chính là Hỏa Ngục mà đám người tội lỗi từng phủ nhận trước đây ở trần gian, giờ nó ở ngay trước mặt họ khiến họ không thể chối bỏ.');
INSERT INTO "vn_mokhtasar" VALUES(55,44,'55:44','Họ phải bước qua lại giữa lửa và nước sôi cực nóng vô cùng khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(55,45,'55:45','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,46,'55:46','Và những ai biết sợ việc trình diện Thượng Đế của y vào Ngày Sau, họ đã tin tưởng và thực hiện việc hành đạo thì sẽ nhận được hai Ngôi Vườn Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(55,47,'55:47','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,48,'55:48','Hai Ngôi Vườn đó có rất nhiều cây với tán xòe rộng cùng hoa quả.');
INSERT INTO "vn_mokhtasar" VALUES(55,49,'55:49','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,50,'55:50','Trong hai ngôi vườn đó có hai dòng nước chảy.');
INSERT INTO "vn_mokhtasar" VALUES(55,51,'55:51','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,52,'55:52','Trong hai ngôi vườn có vô số loại trái cây, mỗi loại trái được chia thành hai nhóm.');
INSERT INTO "vn_mokhtasar" VALUES(55,53,'55:53','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi - hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,54,'55:54','Họ ngồi tựa lưng trên những chiếc giường với thảm trải được thêu trổ tuyệt đẹp, cạnh bên những chùm trái cây lủng lẳng vừa tầm tay hái dù đứng hay ngồi đều với hái được.');
INSERT INTO "vn_mokhtasar" VALUES(55,55,'55:55','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi - hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,56,'55:56','Phụ nữ trong đó chỉ biết nhìn mỗi chồng của họ và chọ chưa từng nhìn bất cứ ai dù là loài người hay loài Jinn trước khi nhìn chồng của họ.');
INSERT INTO "vn_mokhtasar" VALUES(55,57,'55:57','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,58,'55:58','Những nàng trinh nữ nơi Thiên Đàng tựa như những viên hồng ngọc và ngọc trai xinh đẹp và thuần khiết.');
INSERT INTO "vn_mokhtasar" VALUES(55,59,'55:59','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi - hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,60,'55:60','Chẳng phải người ngoan đạo, biết tuân lệnh Allah thì phải được Ngài ban thưởng tử tế đó sao?');
INSERT INTO "vn_mokhtasar" VALUES(55,61,'55:61','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,62,'55:62','Và ngoài hai ngôi vườn Thiên Đàng được kể đó, có thêm hai ngôi vườn Thiên Đàng khác nữa.');
INSERT INTO "vn_mokhtasar" VALUES(55,63,'55:63','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi - hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,64,'55:64','Hai ngôi vườn có màu xanh lục rất đậm.');
INSERT INTO "vn_mokhtasar" VALUES(55,65,'55:65','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,66,'55:66','Trong hai ngôi vườn đó có hai dòng nước chảy không ngừng.');
INSERT INTO "vn_mokhtasar" VALUES(55,67,'55:67','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,68,'55:68','Trong hai ngôi vườn có rất nhiều trái cây, có chà là khổng lồ và trái lựu.');
INSERT INTO "vn_mokhtasar" VALUES(55,69,'55:69','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi - hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,70,'55:70','Trong Thiên Đàng có những người vợ không những đức hạnh và còn xinh đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(55,71,'55:71','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,72,'55:72','Các nàng trinh nữ luôn được bảo vệ cẩn thận trong đài các.');
INSERT INTO "vn_mokhtasar" VALUES(55,73,'55:73','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi - hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,74,'55:74','Chưa từng có bất cứ ai dù con người hay Jinn đã đến gần họ trước chồng của họ.');
INSERT INTO "vn_mokhtasar" VALUES(55,75,'55:75','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi - hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,76,'55:76','Họ ngồi tựa lưng trên những cái gối có màu xanh lá và những tấm thảm trải xinh xắn.');
INSERT INTO "vn_mokhtasar" VALUES(55,77,'55:77','Vậy đâu là ân huệ trong vô số ân huệ mà Allah đã ban cho các ngươi- hỡi loài Jinn và loài người - mà hai ngươi đã phủ nhận?');
INSERT INTO "vn_mokhtasar" VALUES(55,78,'55:78','Thật là đại phúc cho Đại Danh của Thượng Đế của Ngươi, Đấng Vĩ Đại, Uy Quyền, Rộng Lượng đối với đám bầy tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(56,1,'56:1','Khi Ngày Tận Thế được thiết lập, không sao tránh khỏi.');
INSERT INTO "vn_mokhtasar" VALUES(56,2,'56:2','Sẽ không có bất cứ một ai còn phủ nhận về sự kiện đó như ở trần gian nữa.');
INSERT INTO "vn_mokhtasar" VALUES(56,3,'56:3','Hạ nhục người vô đức tin bằng cách ném họ vào Hỏa Ngục và nâng cao người có đức tin biết kính sợ ( Allah) bằng cách cho họ vào Thiên đàng.');
INSERT INTO "vn_mokhtasar" VALUES(56,4,'56:4','Khi trái đất bị chấn động cực mạnh chưa từng có.');
INSERT INTO "vn_mokhtasar" VALUES(56,5,'56:5','Làm cho tất cả núi non đều vỡ vụn.');
INSERT INTO "vn_mokhtasar" VALUES(56,6,'56:6','Các quả núi phân mảnh thành bụi bay tứ tán.');
INSERT INTO "vn_mokhtasar" VALUES(56,7,'56:7','Vào Ngày đó các ngươi được chia thành ba nhóm.');
INSERT INTO "vn_mokhtasar" VALUES(56,8,'56:8','Nhóm những người của tay phải sẽ nhận được các quyển sổ ghi chép của họ từ bên phải, họ sẽ có được một nơi ở cao nhất và vĩ đại nhất.');
INSERT INTO "vn_mokhtasar" VALUES(56,9,'56:9','Nhóm những người của tay trái sẽ nhận được các quyển sổ ghi chép của họ từ bên trái, họ sẽ phải ở một nơi thấp nhất và tồi tệ nhất.');
INSERT INTO "vn_mokhtasar" VALUES(56,10,'56:10','Và những người tiên phong hành đạo, làm việc tốt ở trần gian là nhóm được ưu tiên vào Thiên Đàng ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(56,11,'56:11','Ba nhóm người đó là những người được ở cận kề bên Allah.');
INSERT INTO "vn_mokhtasar" VALUES(56,12,'56:12','Trong các Ngôi Vườn Thiên Đàng hạnh phúc, họ sẽ hưởng thụ đầy đủ niềm vui.');
INSERT INTO "vn_mokhtasar" VALUES(56,13,'56:13','Một đoàn thể thời kỳ đầu của cộng đồng này và của những cộng đồng trước.');
INSERT INTO "vn_mokhtasar" VALUES(56,14,'56:14','Và một ít người của thời cuối của thế gian, và họ là nhóm người tiên phong cận kề.');
INSERT INTO "vn_mokhtasar" VALUES(56,15,'56:15','Họ được nằm nghỉ trên những chiếc giường được làm bằng vàng.');
INSERT INTO "vn_mokhtasar" VALUES(56,16,'56:16','Họ ngồi tựa mình trên những chiếc giường này. Họ ngồi mặt đối mặt nhau, người này không nhìn lưng của người kia.');
INSERT INTO "vn_mokhtasar" VALUES(56,17,'56:17','Đi vòng quanh họ để phục vụ cho họ là những đứa trẻ mãi không già và không chết.');
INSERT INTO "vn_mokhtasar" VALUES(56,18,'56:18','Những đứa trẻ phục vụ đó đi vòng quanh họ với những cái bình, chiếc tách, ly để rót rượu cho họ. Và rượu được lấy từ dòng sông rượu của Thiên Đàng không bao giờ cạn.');
INSERT INTO "vn_mokhtasar" VALUES(56,19,'56:19','Rượu nơi Thiên Đàng không giống như rượu của trần gian, nó không làm người uống bị nhức đầu và mất trí.');
INSERT INTO "vn_mokhtasar" VALUES(56,20,'56:20','Những đứa trẻ đó đi vòng quanh họ mang theo đủ loại trái cây để họ tha hồ lựa chọn.');
INSERT INTO "vn_mokhtasar" VALUES(56,21,'56:21','Những đứa trẻ đó đi vòng quanh họ tay bưng các loại thịt chim để họ thỏa thích chọn lựa.');
INSERT INTO "vn_mokhtasar" VALUES(56,22,'56:22','Trong Thiên Đàng, họ được cho các nàng trinh nữ có làng da trắng mịn với đôi mắt to tròn tuyệt đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(56,23,'56:23','Các nàng trinh nữ tựa như những viên ngọc trai được cất giữ rất kỹ.');
INSERT INTO "vn_mokhtasar" VALUES(56,24,'56:24','Một phần thưởng dành cho họ vì những việc làm ngoan đạo mà họ đã hoàn thành ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(56,25,'56:25','Trong Thiên Đàng, con người không còn nghe thấy lời lẽ tục tĩu và tội lỗi nữa.');
INSERT INTO "vn_mokhtasar" VALUES(56,26,'56:26','Trong Thiên Đàng, mọi người chỉ còn nghe mỗi tiếng chào bằng an của các Thiên Thần đến với họ và lời chào bằng an giữa họ với nhau.');
INSERT INTO "vn_mokhtasar" VALUES(56,27,'56:27','Và nhóm những người của tay phải, nhóm những người của tay phải là ai? Họ có vị trí cao quý đối với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(56,28,'56:28','Giữa cây táo đã bị triệt sạch gai, không còn sự gây hại nào nữa.');
INSERT INTO "vn_mokhtasar" VALUES(56,29,'56:29','Giữa cây chuối có buồng trái xếp chồng lên nhau.');
INSERT INTO "vn_mokhtasar" VALUES(56,30,'56:30','Có bóng mát tỏa rộng không hề mất.');
INSERT INTO "vn_mokhtasar" VALUES(56,31,'56:31','Và dòng nước chảy liên tục không dứt.');
INSERT INTO "vn_mokhtasar" VALUES(56,32,'56:32','Và trái cây thì nhiều vô hạn.');
INSERT INTO "vn_mokhtasar" VALUES(56,33,'56:33','Nguồn trái cây dồi dào không dứt, không còn giới hạn theo mùa, trái cây có trong mọi thời gian khi họ muốn.');
INSERT INTO "vn_mokhtasar" VALUES(56,34,'56:34','Và những chiếc giường được trải thảm bên trên.');
INSERT INTO "vn_mokhtasar" VALUES(56,35,'56:35','Quả thật, TA đã tạo các trinh nữ Hur''ain như đã đề cập ở trên theo cách rất đặc biệt.');
INSERT INTO "vn_mokhtasar" VALUES(56,36,'56:36','TA đã tạo các trinh nữ đó luôn trinh nguyên như chưa từng ai chạm vào các nữ trước đó.');
INSERT INTO "vn_mokhtasar" VALUES(56,37,'56:37','Họ đồng trang lứa và họ chỉ biết yêu thương mỗi chồng của họ.');
INSERT INTO "vn_mokhtasar" VALUES(56,38,'56:38','TA tạo hóa các trinh nữ đó dành riêng cho những người thuộc nhóm người của tay phải, đó là dấu hiệu của nhóm người hạnh phúc.');
INSERT INTO "vn_mokhtasar" VALUES(56,39,'56:39','Họ là một tập thể thuộc tín đồ của những vị Nabi trước đây.');
INSERT INTO "vn_mokhtasar" VALUES(56,40,'56:40','Và một tập thể thuộc tín đồ của Muhammad và đây là một cộng đồng cuối cùng.');
INSERT INTO "vn_mokhtasar" VALUES(56,41,'56:41','Còn nhóm người của tay trái, họ lại ai? Họ là những người sẽ có hoàn cảnh rất tồi tệ và kết cuộc rất xấu xa.');
INSERT INTO "vn_mokhtasar" VALUES(56,42,'56:42','Họ phải ở giữa gió cực nóng và nước cực sôi.');
INSERT INTO "vn_mokhtasar" VALUES(56,43,'56:43','Và trong đám khói rất đen.');
INSERT INTO "vn_mokhtasar" VALUES(56,44,'56:44','Cơn gió không làm cho dễ chịu và chỗ ở cũng không ưa nhìn.');
INSERT INTO "vn_mokhtasar" VALUES(56,45,'56:45','Quả thật họ đã từng sống hưởng thụ trên trần gian, họ đã từng chỉ quan tâm đến sự hưởng lạc và dục vọng của họ.');
INSERT INTO "vn_mokhtasar" VALUES(56,46,'56:46','Và họ đã cố tình phủ nhận Allah mà thờ phượng những bục tượng thay vì thờ phượng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(56,47,'56:47','Họ đã từng chế nhạo việc phục sinh mà nói: Lẽ nào khi chúng tôi trở thành cát bụi và xương khô thì chúng tôi được dựng sống lại thêm lần nữa ư?');
INSERT INTO "vn_mokhtasar" VALUES(56,48,'56:48','Phải chăng cả tổ tiên của chúng tôi đã chết trước đây cũng được dựng sống lại luôn ư?');
INSERT INTO "vn_mokhtasar" VALUES(56,49,'56:49','Hãy nói - hỡi Thiên Sứ - với đám người phủ nhận sự phục sinh: Thật vậy, tất cả loài người từ thời sơ khai đến thời cuối cùng đều được dựng sống lại toàn bộ.');
INSERT INTO "vn_mokhtasar" VALUES(56,50,'56:50','Tất cả sẽ được triệu tập vào Ngày Tận Thế để thanh toán và thưởng phạt, không sao tránh khỏi.');
INSERT INTO "vn_mokhtasar" VALUES(56,51,'56:51','Sau đó đến các ngươi - hỡi đám người phủ nhận sự phục sinh - đã đi lệch khỏi con đường chính đạo.');
INSERT INTO "vn_mokhtasar" VALUES(56,52,'56:52','Trong Ngày Tận Thế các ngươi phải ăn loại trái của cây Zaqqum, đó là cây gớm ghiếc và hôi thối nhất.');
INSERT INTO "vn_mokhtasar" VALUES(56,53,'56:53','Họ sẽ nhét loại trái đắng đó vào cho đầy cái bụng rỗng của mình.');
INSERT INTO "vn_mokhtasar" VALUES(56,54,'56:54','Rồi phải uống tiếp loại nước sôi cực nóng.');
INSERT INTO "vn_mokhtasar" VALUES(56,55,'56:55','Họ cắm đầu uống loại nước sôi đó tựa như con lạc đà bị chết khát vậy.');
INSERT INTO "vn_mokhtasar" VALUES(56,56,'56:56','Việc đề cập đến loại thức ăn đắng và nước sôi là cách chiêu đãi dành cho họ trong Ngày Thưởng Phạt đó.');
INSERT INTO "vn_mokhtasar" VALUES(56,57,'56:57','Chính TA (Allah) đã tạo hóa ra các ngươi - hỡi đám người phủ nhận - sau khi các ngươi không là cái gì cả, thế tại sao các ngươi vẫn không tin việc TA phục sinh các ngươi sau khi đã chết vậy?');
INSERT INTO "vn_mokhtasar" VALUES(56,58,'56:58','Sao, các ngươi - hỡi con người - có thấy tinh dịch mà các ngươi xuất vào tử cung của các bà vợ của các ngươi chăng?');
INSERT INTO "vn_mokhtasar" VALUES(56,59,'56:59','Chính các ngươi đã tạo ra tinh dịch đó hay chính TA mới là Đấng tạo ra nó?');
INSERT INTO "vn_mokhtasar" VALUES(56,60,'56:60','TA đã định cái chết giữa các ngươi, mỗi người trong các ngươi đã được định cho một tuổi thọ nhất định, y sẽ không chết sớm hơn hay trễ hơn và TA không hề bất lực để thực hiện điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(56,61,'56:61','Trong việc thay đổi ngoại hình của các ngươi với những điều các ngươi biết được và thay đổi các ngươi theo hình dạng mà các ngươi không hề biết.');
INSERT INTO "vn_mokhtasar" VALUES(56,62,'56:62','Và các ngươi đã biết rõ việc TA tạo hóa các ngươi lần đầu như thế nào kia mà, sao các ngươi vẫn không hiểu được rằng Đấng đã tạo các ngươi ở lần đầu chắc chắc thừa khả năng phục sinh các ngươi sau khi đã chết.');
INSERT INTO "vn_mokhtasar" VALUES(56,63,'56:63','Các ngươi có nhìn thấy hạt giống mà các ngươi gieo xuống đất không?');
INSERT INTO "vn_mokhtasar" VALUES(56,64,'56:64','Chính các ngươi đã làm hạt giống mà các ngươi gieo xuống đất đâm chồi hay là do TA làm cho nó đâm chồi vậy?');
INSERT INTO "vn_mokhtasar" VALUES(56,65,'56:65','Nếu TA muốn, TA đã làm hư các hạt giống mà các ngươi đã gieo trồng sau khi nó gần như đâm chồi, điều đó chắc hẳn khiến các ngươi vô cùng ngạc nhiên với những gì đã xảy ra.');
INSERT INTO "vn_mokhtasar" VALUES(56,66,'56:66','Các ngươi sẽ than: Quả thật, chúng ta đã bị mất trắng không còn gì.');
INSERT INTO "vn_mokhtasar" VALUES(56,67,'56:67','Không, chúng ta đã bị tước mất quyền hưởng bổng lộc.');
INSERT INTO "vn_mokhtasar" VALUES(56,68,'56:68','Vậy các ngươi có thấy nước mà các ngươi uống lúc khát hay không?');
INSERT INTO "vn_mokhtasar" VALUES(56,69,'56:69','Do các ngươi trút nó xuống từ đám mây trên trời hay do TA trút nó xuống?');
INSERT INTO "vn_mokhtasar" VALUES(56,70,'56:70','Nếu như TA muốn, TA đã biến nước đó trở thành mặn chát không thể uống cũng không thể tưới tiêu, thế sao các ngươi vẫn không biết cám ơn TA bởi lòng nhân từ khi TA đã trút xuống cho các ngươi nguồn nước ngọt?');
INSERT INTO "vn_mokhtasar" VALUES(56,71,'56:71','Các ngươi có thấy lửa mà các ngươi đốt lên để sử dụng không?');
INSERT INTO "vn_mokhtasar" VALUES(56,72,'56:72','Có phải các ngươi đã tạo ra loại cây phát ra lửa hay là do chính TA đã tạo ra nó vì thương xót các ngươi?');
INSERT INTO "vn_mokhtasar" VALUES(56,73,'56:73','TA đã lấy lửa đó để nhắn nhủ với các ngươi về lửa ở Đời Sau và TA đã biến nó trở trành vật hữu ích cho việc các ngươi đi đường xa.');
INSERT INTO "vn_mokhtasar" VALUES(56,74,'56:74','Bởi thế, Ngươi - hỡi Thiên Sứ - hãy thanh lọc mọi thứ không xứng đáng ra khỏi Thượng Đế vĩ đại của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(56,75,'56:75','Allah thề bởi những vị trí mọc, lặn của các vì sao.');
INSERT INTO "vn_mokhtasar" VALUES(56,76,'56:76','Và chắc rằng lời thề bởi những vị trí của các vì sao - nếu các ngươi biết - nó rất vĩ đại, bởi đó là những Dấu hiệu và những bài học không có giới hạn.');
INSERT INTO "vn_mokhtasar" VALUES(56,77,'56:77','Quả thật, quyển Qur''an được xướng đọc cho các ngươi nghe - hỡi con người - là Qur''an Thiêng Liêng, bởi Nó hàm chứa biết bao lợi ích vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(56,78,'56:78','Kinh Sách đó che khỏi tầm mắt con người, đó là Al-Lawhul Mahfuzth (Quyển Kinh Mẹ).');
INSERT INTO "vn_mokhtasar" VALUES(56,79,'56:79','Không ai được phép chạm vào Nó ngoại trừ đó là Các Thiên Thần trong sạch không tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(56,80,'56:80','Do Thượng Đế của muôn loài Thiên khải xuống cho vị Nabi Muhammad của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(56,81,'56:81','Lẽ nào với Qur''an này đây, các ngươi - hỡi những kẻ đa thần - lại không tin ư?');
INSERT INTO "vn_mokhtasar" VALUES(56,82,'56:82','Và đáng lẽ ra các ngươi phải tạ ơn Allah với những bổng lộc mà Ngài đã ban cho các ngươi thì các ngươi lại phủ nhận nó, các ngươi cho rằng mưa là do vì sao ban cho, nên các ngươi đã nói: Chúng ta được vì sao này, vì sao này ban cho mưa.');
INSERT INTO "vn_mokhtasar" VALUES(56,83,'56:83','Vậy tại sao khi linh hồn lên đến tận cổ họng?');
INSERT INTO "vn_mokhtasar" VALUES(56,84,'56:84','Trong khi các ngươi tại thời điểm đó chỉ biết trố mắt nhìn một cách bất lực người đang hấp hối trước mặt của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(56,85,'56:85','Và kiến thức, khả năng và Thiên Thần của TA ở gần với người chết của các ngươi hơn các ngươi, tuy nhiên, các ngươi không tài nào thấy được những Thiên Thần.');
INSERT INTO "vn_mokhtasar" VALUES(56,86,'56:86','Nếu các ngươi tự cho rằng mình không bị phục sinh sống lại để thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(56,87,'56:87','Sao không kéo linh hồn của người chết của các ngươi trở lại nếu các ngươi nói thật? Nhưng các ngươi không thể làm điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(56,88,'56:88','Đối với người chết thuộc nhóm người tiên phong hành đạo và làm việc tốt.');
INSERT INTO "vn_mokhtasar" VALUES(56,89,'56:89','Y sẽ được thư thản không mệt mỏi trong nguồn bổng lộc dồi dào, ở lòng nhân từ của Allah, y sẽ ở nơi Thiên Đàng tận hưởng mọi thứ với những gì y mong muốn.');
INSERT INTO "vn_mokhtasar" VALUES(56,90,'56:90','Đối với những người chết thuộc nhóm những người của tay phải sẽ được chào đón trong sự an lành và bình an.');
INSERT INTO "vn_mokhtasar" VALUES(56,91,'56:91','Đối với những người chết thuộc nhóm những người của tay phải sẽ được chào đón bằng lời chào an lành.');
INSERT INTO "vn_mokhtasar" VALUES(56,92,'56:92','Còn nếu như người chết thuộc nhóm phủ nhận những gì được Thiên Sứ mang đến, những kẻ mà họ đã lệch khỏi con đường Chính Đạo.');
INSERT INTO "vn_mokhtasar" VALUES(56,93,'56:93','Thì y sẽ được chào đón và tiếp đãi bằng nước sôi cực nóng.');
INSERT INTO "vn_mokhtasar" VALUES(56,94,'56:94','Rồi y bị đốt cháy trong lửa của Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(56,95,'56:95','Chắc rằng điều mà TA đã kể cho Ngươi nghe - hỡi Thiên Sứ - chính là chân lý không còn gì phải nghi ngờ cả.');
INSERT INTO "vn_mokhtasar" VALUES(56,96,'56:96','Thế nên Ngươi hãy thanh lọc Đại Danh của Thượng Đế vĩ đại của Ngươi sạch khỏi mọi thứ không xứng đáng với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(57,1,'57:1','Allah tôn vinh và vinh danh Ngài khỏi mọi thứ không xứng đáng với Ngài có trong các tầng trời và trái đất, và Ngài là Đấng Toàn Năng, không gì có thể vượt qua quyền năng của Ngài, Ngài là Đấng Sáng Suốt trong Tạo hóa và Quản lý.');
INSERT INTO "vn_mokhtasar" VALUES(57,2,'57:2','Mọi vương quyền trong trời đất đều thuộc về một mình Ngài, Ngài muốn vật gì sống thì nó sống, muốn vật nào chết thì nó chết và Ngài toàn năng trên tất cả mọi việc, không gì làm Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(57,3,'57:3','Ngài là Đấng Đầu Tiên, Đấng mà không có bất cứ thứ gì tồn tại trước Ngài; Ngài là Đấng Cuối Cùng, Đấng mà không có bất cứ thứ gì tồn tại sau Ngài; Ngài là Đấng Bên Trên mọi thứ, Đấng mà không có bất cứ thứ gì bên trên Ngài, Ngài là Đấng không có bất cứ thứ gì nằm ngoài tầm kiểm soát của Ngài; và Ngài là Đấng thông toàn tất cả mọi thứ, không gì nằm ngoài sự hiểu biết của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(57,4,'57:4','Ngài là Đấng đã tạo hóa ra các tầng trời và trái đất trong thời gian sáu ngày, bắt đầu vào chủ nhật và kết thúc vào thứ sáu trong khi Ngài thừa sức tạo hóa tất cả nhanh hơn chớp mắt. Xong Ngài an vị bên trên chiếc ''Arsh (Ngai Vương) theo bản chất siêu phàm và vĩ đại của Ngài. Ngài am tường mọi thứ chui vào lòng đất như nước mưa hoặc các thứ khác, am tường mọi thứ chui lên từ đất như sự đâm chồi, kho báu, quặng mỏ và các thứ khác, am tường mọi thứ rơi xuống từ trời như nước mưa, lời mặc khải và các thứ khác, am tường mọi thứ thăng lên trời như Thiên Thần, hành động của con người, linh hồn của người chết. Và Ngài luôn đồng hành cùng với các ngươi - hỡi con người - bằng kiến thức của Ngài, không điều gì của các ngươi giấu được Ngài, Ngài luôn thấy rõ nhất cử nhất động của các ngươi, theo đó Ngài sẽ thưởng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(57,5,'57:5','Ngài là Đấng duy nhất toàn quyền quản lý các tầng trời và trái đất, rồi cũng Ngài duy nhất mà tất cả vạn vật phải trở lại trình diện trước Ngài trong Ngày Tận Thế để chịu sự thưởng phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(57,6,'57:6','Ngài nhập ban đêm vào ban ngày để tạo ra bóng tối và để con người ngủ nghỉ, Ngài nhập ban ngày vào ban đêm để tạo ra ánh sáng và để con người lao động, và Ngài luôn tận tường mọi tâm tư ẩn sâu trong lòng ngực của đám nô lệ của Ngài, không gì giấu giếm được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(57,7,'57:7','Các ngươi hãy tin tưởng vào Allah, hãy tin tưởng vào Thiên Sứ của Ngài và hãy xuất tài sản mà Allah đã ủy quyền cho các ngươi sử dụng nó, hãy phân chia nó phù hợp với luật lệ đã qui định cho các ngươi. Đối với ai trong các ngươi đã tin tưởng Allah và đã xuất tài sản cho chính nghĩa của Allah thì họ sẽ hưởng được phần thưởng vĩ đại, đó là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(57,8,'57:8','Và điều gì đã cản các ngươi tin tưởng vào Allah?! Trong khi Thiên Sứ mời gọi các ngươi đến với Allah với niềm hi vọng các ngươi biết tin tưởng vào Thượng Đế của các ngươi và Ngài đã nhận lấy lời giao ước của các ngươi lúc các ngươi tuyên bố tin tưởng Ngài ngay trước mặt của ông tổ của các ngươi, nếu các ngươi thật sự là người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(57,9,'57:9','Ngài là Đấng đã Thiên khải cho Nô Lệ của Ngài - Muhammad một số Dấu hiệu hiển nhiên, để Ngài đưa các ngươi ra khỏi bóng tối của sự vô đức tin và ngu muội để đến với ánh sáng của đức tin và kiến thức. Thực sự Allah rất nhân từ, Ngài luôn đoái hoài đến các ngươi khi Ngài gởi Thiên Sứ của Ngài đến hướng dẫn và báo tin mừng cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(57,10,'57:10','Và điều gì đã cấm cản các ngươi chi dùng vì con đường của Allah vậy? Trong lúc tất cả các tầng trời và đất là tài sản của Allah. Chắc chăn không hề ngang nhau trong các ngươi - hỡi những người có đức tin - đối với những ai đã chi dùng tài sản của mình vì con đường của Allah để làm hài lòng Ngài vào thời gian trước cuộc chinh phục Makkah và đã anh dũng đánh kẻ thù để tôn vinh Islam, so với những ai đã chi dùng và đã đánh kẻ thù sau cuộc chinh phục Makkah. Chắc chắn những người chi dùng và anh đánh kẻ thù trước cuộc chinh phục Makkah tốt hơn những người đã chi dùng và và anh đánh kẻ thù sau cuộc chinh phục Makkah. Tuy nhiên, cả hai nhóm đều được Allah hứa hẹn ban cho Thiên Đàng, và Allah luôn biết rõ hành động của các ngươi, không hành vi nào của các ngươi giấu được Ngài và dựa vào đó Ngài sẽ thưởng phạt cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(57,11,'57:11','Ai sẵn lòng chi dùng tài sản của mình vì làm hài lòng Allah sẽ được Ngài ban thưởng gấp nhiều lần số so với những gì y đã xuất ra và trong Ngày Tận Thế y sẽ được thêm phần thưởng vinh quang, đó là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(57,12,'57:12','Vào Ngày mà Ngươi sẽ thấy được những người có đức tin nam và nữ được ánh sáng ở trước mặt và bên phải của họ, có lời bảo họ vào Ngày hôm đó: Ngày nay xin báo tin mừng cho các người với những ngôi vườn đầy cây cối ở Thiên Đàng cùng với những dòng sông chảy bên dưới những tòa lâu đài, các người sẽ được ở trong đó mãi mãi. Phần thưởng đó là một sự thắng lợi vĩ đại, phần thưởng mà không có thắng lợi nào so sánh được.');
INSERT INTO "vn_mokhtasar" VALUES(57,13,'57:13','Đấy cũng là Ngày mà người đạo đức giả cả nam lẫn nữ nài nỉ những người có đức tin: Xin các anh hãy chờ đợi chúng tôi với, xin hãy cho chúng tôi ít ánh sáng của các anh để chúng tôi dễ dàng qua cầu Sirat. Lúc đó có lời đáp lại bằng giọng giễu cợt họ: "Các anh hãy quay lại phía sau các anh để lấy ánh sáng đi." Thế rồi một vách chắn có cửa chia cắt hai nhóm họ, bên trong vách là những người có đức tin có được lòng nhân từ của Allah và bên ngoài là của đám người Munafiq phải gánh chịu cực hình.');
INSERT INTO "vn_mokhtasar" VALUES(57,14,'57:14','Đám người đạo đức giả cố gọi nhóm người có đức tin: Há chẳng phải chúng ta đều theo Islam và cùng hành đạo hay sao? Nhóm người Muslim đáp: Đúng, các anh đã ở cùng chúng tôi nhưng các anh đã tự hại mình bằng việc ngụy tạo đức tin, các anh đã bên cạnh những người có đức tin để chờ cơ hội khi họ bị thất thế thì các anh công khai bản tính vô đức tin của mình và các anh đã nghi ngờ về việc Allah sẽ ban chiến thắng cho họ, các anh đã phủ nhận việc phục sinh sau khi chết, các anh cứ mù quáng đi theo những ước mơ vô thực mãi đến khi cái chết túm lấy các anh và rồi các anh đã bị Shaytan lường gạt mình."');
INSERT INTO "vn_mokhtasar" VALUES(57,15,'57:15','Ngày hôm nay đây, không còn nhận bất cứ gì từ các ngươi - hỡi nhóm người Munafiq - để các ngươi thoát khỏi hình phạt của Allah và chung số phận với các ngươi là những ai công khai phủ nhận Allah, lối đi dành cho tất cả các ngươi chính là Hỏa Ngục, nó phù hợp với các ngươi và đó đúng là một nơi ở tồi tệ nhất.');
INSERT INTO "vn_mokhtasar" VALUES(57,16,'57:16','Lẽ nào vẫn chưa phải là lúc để cho những người đã tin tưởng Allah và Thiên Sứ của Ngài mềm lòng và an tâm khi được nhắc về Allah và những điều mà Qur''an hứa hẹn và răn đe hay sao? Chớ trở thành như đám người Do Thái đã được ban cho Tawrah cũng như đám người Thiên Chúa được ban cho Injil, những kẻ mà chúng chỉ vì thời gian quá dài giữa chúng với sứ mạng của vị Nabi kế tiếp đã khiến con tim của chúng bị chai cứng và đa số bọn chúng đã rời khỏi việc phục tùng Allah đến với việc bất tuân Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(57,17,'57:17','Các ngươi hãy nhận thức rõ rằng chính Allah là Đấng làm cho mảnh đất chết khô sống lại bằng việc cho cây cỏ mọc lên trở lại. Quả thâtj Ngài đã trình bày rõ cho các ngươi - hỡi con người - những bằng chứng về quyền năng của Allah cũng như tính duy nhất của Ngài mong rằng các ngươi biết suy nghĩ mà nhận ra rằng việc Đấng có thể làm sống lại mảnh đất đã chết khô thừa khả năng phục sinh các ngươi sau khi các ngươi đã chết và thừa khả năng làm mềm con tim của các ngươi sau khi nó đã chai cứng.');
INSERT INTO "vn_mokhtasar" VALUES(57,18,'57:18','Thật ra đối với những ai dù nam hay nữ đã xuất tài sản của mình theo cách mà bản thân hài lòng, không kèm theo lời làm tổn thương và miệt thị thì phần thưởng dành cho họ sẽ được nhân lên, cứ mỗi một ân phước được nhân lên từ mười cho đến bảy trăm lần và nhiều hơn thế, và họ còn được phần thưởng vinh dự nơi Allah nữa, đó chính là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(57,19,'57:19','Đối với những ai đã tin tưởng Allah và các Thiên Sứ của Ngài và không có sự chia rẽ giữa họ thì họ là nhóm người chân thật; và những người Shaheed (chết vì chính đạo của Allah) sẽ được Allah chuẩn bị phần thưởng dành cho họ và Ngày Tận Thế họ còn được ban cho ánh sáng đi ở trước mặt và ở phía bên phải. Còn đối với những ai không tin tưởng Allah và các Thiên Sứ của Ngài và bác bỏ những Lời Mặc Khải mà TA đã Thiên khải cho Thiên Sứ của Ngài thì chúng chính là cư dân của Hỏa Ngục. Vào Ngày Tận chúng sẽ phải vào đó ở mãi mãi không có ngày trở ra.');
INSERT INTO "vn_mokhtasar" VALUES(57,20,'57:20','Các ngươi cần phải ý thức rằng cuộc sống trần gian này vốn chỉ là thú vui phù phiếm, là sự tô điểm, khoe khoang tâng bốc lẫn nhau giữa các ngươi về tài sản, quyền lực, con cái giống như hình ảnh nước mưa làm vườn tược xanh tươi khiến chủ vườn mát dạ. Nhưng chẳng bao lâu sau đó màu xanh tươi kia lại từ từ bị héo hon, rồi chính ngươi - hỡi người nhìn - thấy nó trở nên vàng úa và vỡ vụn. Và ở Đời Sau, hình phạt đang chờ đón người vô đức tin và người Munafiq nhưng đồng thời có sự tha thứ và sự hài lòng của Allah dành cho đám nô lệ có đức tin của Ngài. Tóm lại, cuộc sống trần gian này chỉ là thứ vật chất tạm bợ nhanh chóng tiêu tan. Thế nên ai cố ôm lấy thứ vật chất tạm bợ đó thay vì sự hưởng thụ vĩnh hằng ở Đời Sau thì y là kẻ thất bại thảm hại.');
INSERT INTO "vn_mokhtasar" VALUES(57,21,'57:21','Các ngươi hãy thi đua - hỡi con người - bằng việc hành đạo để tội lỗi của các ngươi được tha thứ như việc tự ăn năn sám hối và những việc ngoan đạo khác và để là cư dân của Thiên Đàng nơi mà khoảng rộng của nó bằng khoảng rộng của trời đất. Thiên Đàng đó đã được Allah dành sẵn cho người tin tưởng Allah va các Thiên Sứ của Ngài. Allah muốn ban nó cho ai Ngài muốn trong đám nô lệ của Ngài và Ngài có vô số thiên lộc dành cho đám nô lệ tin tưởng của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(57,22,'57:22','Không một tai nạn nào xảy ra với loài người ở trên thế gian này như hạn hán chẳng hạn hay là tai nạn xảy ra riêng với từng người, ngoại trừ đã được định sẵn trước đó trong Lawhul Mahfudh (quyển sổ mẹ) trước khi vạn vật được tạo hóa. Quả thật mọi thứ đối với Allah rất dễ dàng.');
INSERT INTO "vn_mokhtasar" VALUES(57,23,'57:23','Sở dĩ như thế là để các ngươi - hỡi con người - không đau xót với điều gì đã mất và cũng không mừng cuống quýt khi được Ngài ban cho điều phúc lành. Quả thật Allah không ưa thích những kẻ tự cao khoác lác trước thiên hạ bằng những thiên lôc mà Allah đã ban cho y.');
INSERT INTO "vn_mokhtasar" VALUES(57,24,'57:24','Đối với những kẻ keo kiệt với điều đáng lẽ ra y phải có trách nhiệm, vậy mà y còn ra lệnh thiên hạ keo kiệt giống như y. Tuy nhiên, ai không biết tuân lệnh Allah thì chẳng làm Allah thiệt thòi điều gì mà chính bản thân y phải bị thiệt thòi. Quả thật, Allah là Đấng Giàu Có không cần đến việc vâng lời của con người và là Đấng đáng được ca tụng trong mọi hoàn cảnh.');
INSERT INTO "vn_mokhtasar" VALUES(57,25,'57:25','Quả thật, TA đã gởi các Thiên Sứ của TA cùng với nhiều bằng chứng rõ ràng. TA cũng ban cho mỗi người trong Họ một Kinh Sách và cán cân để thiên hạ duy trì nền công lý. TA cũng đã ban sắt xuống có độ cứng, mạnh mà con người dùng làm nhiều vật dụng hữu ích như vũ khí, để Allah nhận ra ai là người công khai giúp đỡ Ngài dù không nhìn thấy Ngài. Quả thật, Allah rất mạnh mẽ toàn năng, không gì chiến thắng Ngài được và không gì làm Ngài bất lực cả.');
INSERT INTO "vn_mokhtasar" VALUES(57,26,'57:26','Và rõ ràng TA đã gởi Nuh và Ibrahim xuống làm Thiên Sứ, TA đã chọn trong đám con cháu của họ tiếp tục thừa kế sứ mạng và Kinh Sách. Vậy mà chỉ có một số trong đám con cháu của hai người họ được hướng dẫn đến với con đường Chính đạo còn đa số họ đã rời khỏi việc phục tùng Allah.');
INSERT INTO "vn_mokhtasar" VALUES(57,27,'57:27','Sau đó, TA liên tiếp gởi xuống rất nhiều Thiên Sứ cho từng cộng đồng, trong đó TA đã gởi đến chúng Ysa con trai của Maryam và TA đã ban xuống cho Y Kinh Injil (Tân Ước), TA đã gieo vào con tim của những ai đã tin tưởng và noi theo Y lòng thiện cảm và biết thương xót, thế là họ biết sống hòa thuận, biết thương yêu lẫn nhau, rồi dần dần họ trở nên thái quá trong thờ phượng khi họ tự cấm những thứ Allah cho phép như kết hôn và hưởng thụ, họ biến tấu thêm nhiều hình thức thờ phượng khác trong khi họ không được yêu cầu làm vậy, Ngài chỉ ra lệnh họ đi đúng chuẩn mực để được Ngài hài lòng thế mà họ vẫn bất tuân. Mặc dù vậy Allah vẫn ban thưởng đầy đủ cho những ai đã tin tưởng trong số họ, nhưng đa số bọn họ đã vượt khỏi sự phục tùng Allah, nhất là khi họ phủ nhận những gì Thiên Sứ Muhammad của Ngài mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(57,28,'57:28','Hỡi những người đã tin tưởng Allah và tuân theo giáo luật của Ngài, các ngươi hãy kính sợ Allah mà tuân thủ đúng mọi mệnh lệnh mà Ngài đã ban cũng như tránh xa những điều Ngài ngăn cấm và hãy tin tưởng Thiên Sứ của Ngài, Ngài sẽ ban phần thưởng cho các ngươi gấp đôi bởi các ngươi vừa tin tưởng Muhammad và tin tưởng luôn những Thiên Sứ trước đây. Ngoài ra, Ngài còn cho các ngươi ánh sáng để cho các ngươi soi rọi trong cuộc sống và soi rọi khi băng qua cây cầu Sirat trong Ngày Tận Thế, Ngài còn tha thứ tội lỗi cho các ngươi, che đậy lỗi lầm cho các ngươi, vì Allah Toàn Năng rất nhân từ và độ lượng với đám nô lệ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(57,29,'57:29','Và TA đã trình bày rõ cho các ngươi - hỡi tín đồ luôn tin tưởng - về những gì TA đã chuẩn bị ban thưởng trọng hậu cho các ngươi vượt xa công sức mà các ngươi đã bỏ ra để khẳng định với dân Kinh Sách thuộc Do Thái và Thiên Chúa rằng họ hoàn toàn không có quyền hạn gì để can thiệp vào thiên ân của Allah cả, tất cả đều do Allah quyết định, Ngài muốn ban cho ai tùy Ngài bởi Ngài có vô vàn thiên ân.');
INSERT INTO "vn_mokhtasar" VALUES(58,1,'58:1','Chắc chắn, Allah đã nghe thấy lời của Khawlah con gái của Tha’labah đã khiếu nại chồng của nữ, Aws bin Assamit với Ngươi - hỡi Thiên Sứ - về việc y ta đã có hành vị Zhihar với nữ (đó là y ta đã nói với nữ: “Cô đối với tôi giống như cái lưng của mẹ tôi vậy, ý nói không thể “ăn nằm” nữa). Nữ đã cầu nguyện than thở với Allah xin Ngài giải quyết chuyện không lành của nữ. Và quả thật Allah hằng nghe thấy lời của hai người họ bởi lẽ Allah là Đấng hằng nghe hết mọi lời nói và nhìn thấy hết mọi sự việc, không có điều gì có thể giấu giếm được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(58,2,'58:2','Những ai trong số các ngươi nói Zhihar với vợ của mình, có nghĩa là người đàn ông nói với vợ của y: “Cô đối với tôi giống như cái lưng của mẹ tôi” ngụ ý tôi không thể ăn nằm với cô. Đây rõ là lời nói điêu ngoa, bởi vợ của họ sự thật không phải là mẹ của họ mà là những người bạn đời của họ, những người không sinh đẻ ra họ thì làm sao là mẹ của họ được. Quả thật, những người đã Zhihar vợ của họ là những người đã nói lời sàm bậy và dối trá. Tuy nhiên, Allah hằng tha thứ cho những ai sai phạm nhưng biết quay đầu thành tâm sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(58,3,'58:3','Những ai tự nghiêm cấm bản thân mình ăn nằm với vợ của họ bằng cách Zhihar với vợ của họ rồi sau đó muốn rút lại lời nói để được ăn nằm với vợ bình thường trở lại thì người chồng – người đã Zhihar – phải phóng thích một người nô lệ (nam hoặc nữ) trước khi họ ăn nằm trở lại với vợ của họ. Đó là luật của Allah đối với những ai đã buông lời Zhihar với vợ của họ. Quả thật, không có bất cứ điều gì hay một hành động nào có thể giấu giếm được Allah.');
INSERT INTO "vn_mokhtasar" VALUES(58,4,'58:4','Nhưng đối với ai không tìm thấy người nô lệ để phóng thích tự do cho họ thì bắt buộc y phải nhịn chay suốt hai tháng liền trước khi quay lại ăn nằm với vợ của y, còn nếu như y không có khả năng nhịn chay trong suốt hai tháng liền như vậy (với ly do chính đáng hợp giáo luật như bệnh tật, già yếu, ...) thì y phải bố thí thức ăn cho sáu mươi người nghèo, mỗi người một bữa ăn no. Đó là những gì TA muốn nói rõ cho các ngươi biết về giáo luật cho hành vi Zhihar, mục đích để các ngươi tin tưởng Allah và Thiên sứ của Ngài mà chấp hành đúng theo giáo luật được Ngài quy định, từ bỏ những sai phạm mà các ngươi lỡ lầm. Và những điều luật được nói là mệnh lệnh và giới hạn của Allah, cho nên các ngươi không được phép vượt quá giới luật của Ngài. Và những kẻ vô đức tin có những giáo luật và giới hạn riêng hứa hẹn sẽ bị trừng phạt họ một cách đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(58,5,'58:5','Quả thật, những ai tìm cách cản trở Allah và Thiên sứ của Ngài nhằm muốn trái nghịch mệnh lệnh của Ngài thì họ sẽ bị hạ nhục giống như những người thời trước họ từ những cộng đồng trước đây đã bị hạ nhục. Và quả thật, TA đã ban xuống các lời mặc khải rõ ràng để minh chứng rằng những điều quy định và giới luật của Allah đều là minh bạch. Do đó, kẻ vô đức tin với Allah, với Thiên Sứ của Ngài và với các lời mặc khải của Ngài sẽ bị trừng phạt nhục nhã.');
INSERT INTO "vn_mokhtasar" VALUES(58,6,'58:6','Vào Ngày Phục Sinh, Allah sẽ làm sống lại tất cả những người đã chết, từ thuở ban đầu cho đến thời khắc cuối cùng của thế gian, tại một nơi duy nhất. Tại đây, Ngài sẽ cho họ biết tất cả những điều tốt xấu mà họ đã làm trước kia trên thế gian. Bởi lẽ, Allah đã cho ghi chép tất cả mọi hành vi của con người trong bản văn lưu trữ không sót bất cứ điều gì, nhưng có lẽ con người đã quên mất những gì họ đã làm, và Allah sẽ làm chứng cho mọi thứ, không có điều gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(58,7,'58:7','Chẳng lẽ Ngươi - hỡi Thiên Sứ - không nhìn thấy rằng Allah, Đấng Tối Cao am tường hết mọi điều trong các tầng trời và dưới đất ư? Quả thật, không một cuộc nói chuyện thầm kín nào từ ba người về một điều bí mật nào đó mà Ngài không là người thứ tư biết rõ tường tận về cuộc nói chuyện đó, và nếu cuộc nói chuyện gồm năm người thì Ngài sẽ là người thứ sáu và cho dù số lượng người nhiều hay ít trong cuộc mật nghị thì Ngài luôn ở cùng với họ ở bất cứ nơi nào. Không một điều gì có thể giấu giếm được Ngài. Rồi sau đó, Ngài sẽ cho họ biết tất cả mọi hành động và việc làm tốt xấu mà họ đã làm trên thế gian. Quả thật, Allah là Đấng hiểu biết và am tường mọi vật và mọi sự việc, không gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(58,8,'58:8','Chẳng lẽ Ngươi - hỡi Thiên Sứ - không thấy rằng những người Do Thái đã bị nghiêm cấm mật nghị với nhau về những ngờ vực cho những người có đức tin rồi chúng lại tái phạm, chúng đã mật nghị với nhau về những điều tội lỗi, những điều gây thù hằn và tìm cách làm trái lệnh của Thiên Sứ? Rồi khi chúng, những người Do thái đó, đến với Ngươi thì lại cố tình chào Ngươi bằng lời chào không phải là lời chào mà Allah đã dùng để chào Ngươi, thay vì chúng nên chào Ngươi với lời: (السلام عليك) – (Assalamualayka: Chào an lành đến Ngươi!) thì chúng lại chào Ngươi bằng lời chào mà Allah không thích nó: (السام عليك) – (Assamualayka: Ngươi sẽ bị tiêu diệt!). Chúng còn bảo nhau rằng: “Nếu Muhammad đích thực là Thiên Sứ vậy sao Allah không trừng phạt chúng ta về những gì mà chúng ta đã nói cho hắn”. TA (Allah) không cần trừng phạt chúng vì sự trừng phạt nơi Hỏa Ngục đã quá đủ cho chúng, rồi chúng sẽ biết được mùi vị trong Hỏa Ngục, nó tồi tệ như thế nào.');
INSERT INTO "vn_mokhtasar" VALUES(58,9,'58:9','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Khi nào các ngươi có nói chuyện thầm kín với nhau thì các ngươi đừng bàn tính chuyện tội lỗi, gây hận thù và oán ghét lẫn nhau hoặc bàn tính chuyện làm trái lệnh của Thiên Sứ như đám người Do Thái đã từng, mà các ngươi hãy nên nói chuyện tốt lành, hãy khuyên bảo nhau chấp hành theo mệnh lệnh của Allah và tránh xa những điều Ngài nghiêm cấm, bởi lẽ, tất cả các ngươi sẽ được Ngài cho triệu tập trình diện trước Ngài để Ngài xét xử mọi hành động, mọi lời nói của các ngươi và Ngài sẽ thưởng phạt một cách công bằng và nghiêm khắc.');
INSERT INTO "vn_mokhtasar" VALUES(58,10,'58:10','Quả thật, việc nói chuyện thầm kín với nhau để bàn tính chuyện tội lỗi, gây hận thù cho nhau là một việc làm do Shaytan xúi bẩy. Shaytan, nó muốn làm cho những người có đức tin buồn phiền, tuy nhiên, điều đó không làm hại họ được trừ phi Allah cho phép. Như vậy, người có đức tin nên tin nơi Allah mà phó thác mọi sự việc cho Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(58,11,'58:11','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Khi nào các ngươi được ai đó yêu cầu các ngươi hãy nới rộng chỗ trong buổi tọa đàm hay trong buổi ngồi lắng nghe thuyết giảng hoặc trong buổi họp mặt nào đó thì các ngươi hãy nới rộng ra, rồi Allah sẽ nới rộng cho các ngươi trên thế gian và ở Đời Sau; và khi nào các ngươi được yêu cầu đứng dậy nhường cho người có vị trí hơn thì các ngươi hãy đứng dậy. Allah nâng những người có đức tin, những người hiểu biết và ngoan đạo lên một cấp bậc với nhiều ân phước và sự hài lòng của Ngài. Và Allah là Đấng biết rõ tường tận mọi hành động của các ngươi, không một điều gì có thể giấu giếm được Ngài, và Ngài sẽ thưởng phạt mọi hành động của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(58,12,'58:12','Này hỡi những người có đức tin! Khi nào các ngươi muốn nói chuyện riêng với Thiên Sứ một cách thầm kín giữa các người với Người thì trước khi nói chuyện các ngươi hãy bố thí cho những người nghèo và khó khăn. Điều đó sẽ tốt cho các ngươi vì các ngươi sẽ được ban phước, đồng thời nó sẽ tẩy sạch bản thân các ngươi khỏi tội lỗi, vì khi các ngươi phụng mệnh Allah điều đó khiến Ngài hài lòng. Tuy nhiên, nếu các ngươi không có khả năng để bố thì cũng không sao, Allah sẽ không bắt tội các ngươi bởi Ngài là Đấng luôn bao dung cho những bầy tôi tin tưởng.');
INSERT INTO "vn_mokhtasar" VALUES(58,13,'58:13','Có phải các ngươi sợ bị nghèo khi phải đem tiền bố thí cho người nghèo trước khi các ngươi muốn tham khảo ý kiến riêng với Thiên Sứ? Nhưng nếu các ngươi không làm theo mệnh lệnh của Allah tức không bố thí thì Allah vẫn tha thứ và bỏ qua cho các ngươi. Nhưng dù thế nào, các ngươi phải dâng lễ nguyện Salah chu đáo và đóng đầy đủ Zakah (thuế an sinh cho người nghèo), phải tuân lệnh Allah và Thiên Sứ của Ngài. Và Allah am tường tất cả mọi hành động và việc làm của các ngươi, và Ngài sẽ thưởng phạt công minh.');
INSERT INTO "vn_mokhtasar" VALUES(58,14,'58:14','Chẳng lẽ Ngươi - hỡi Thiên Sứ - không nhìn thấy những kẻ Munafiq (ngụy tạo đức tin) đã kết bạn với những người Do Thái hay sao? Đây là nhóm người đã bị Allah giận dữ. Những kẻ Munafiq đó thực chất không phải là những người Muslim, cũng chẳng phải là đồng bọn của những người Do Thái, họ lưỡng lự không biết theo phía này hay phía kia và họ thề thốt trước mặt Ngươi rằng họ là những người Muslim, họ đã không truyền thông tin Muslim cho người Do Thái bao giờ trong khi chúng biết rõ họ đang thề thốt dối trá.');
INSERT INTO "vn_mokhtasar" VALUES(58,15,'58:15','Allah đã chuẩn bị cho những kẻ Munafiq một sự trừng phạt vô cùng đau đớn ở Đời Sau bởi họ đã hành động xấu xa, họ bị vùi xuống tận đáy của Hỏa Ngục xứng đáng cho hành động vô đức tin tồi tệ của họ ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(58,16,'58:16','Những kẻ ngụy tạo đức tin đã dùng sự thề thốt để làm bình phong che chắn khỏi bị giết do sự vô đức tin của họ, họ làm thế để khỏi bị những người Muslim giết chết họ cũng như khỏi lấy tài sản của họ. Vì lẽ đó, họ đã cản trở bản thân mình và những người khác trên con đường chính nghĩa của Allah, đó là con đường Islam. Cho nên, họ sẽ phải bị trừng phạt đau đớn và nhục nhã bởi hành động xấu xa và tồi tệ của họ.');
INSERT INTO "vn_mokhtasar" VALUES(58,17,'58:17','Tiền bạc và con cái của những kẻ ngụy tạo đức tin sẽ không thể giúp ích được gì cho họ. Chắc chắn họ sẽ làm bạn với lửa của Hỏa Ngục, họ sẽ ở trong đó đời đời không có ngày trở ra, và hình phạt sẽ không bao giờ bị gián đoạn.');
INSERT INTO "vn_mokhtasar" VALUES(58,18,'58:18','Vào Ngày Phục Sinh, Allah sẽ làm sống lại toàn bộ đám người Munafiq, không ai thoát khỏi, để chịu sự thanh toán và thưởng phạt, họ sẽ thề thốt với Ngài rằng họ trước kia trên thế gian đích thực là những người có đức tin luôn làm hài lòng Allah, họ thề thốt với Ngài giống như họ đã từng thề thốt với các ngươi - hỡi những người có đức tin - trên thế gian. Họ cứ tưởng điều đó sẽ giúp chúng chạy tội trước Allah giống như chúng đã dùng nó che mắt những người Muslim trên thế gian. Quả thật, chúng đã nói dối quá mức, chúng đã thề giả dối ở trần gian, vậy mà còn thề giả dối ở Đời Sau nữa.');
INSERT INTO "vn_mokhtasar" VALUES(58,19,'58:19','Chúng đã bị Shaytan khống chế và điều khiển nên chúng đã quên hết mọi mệnh lệnh của Allah, chẳng những không làm hài lòng Allah mà chúng còn làm Allah giận dữ thêm nữa. Đám người mang tính cách được miêu tả đó là tay say của tên Iblis và những ai theo hắn. Há không phải những tên tay sai của Shaytan chắc chắn sẽ bị thua thiệt trên cõi đời này và cõi Đời Sau hay sao? Chúng đã bán sự chỉ đạo lấy sự lầm lạc và đổi Thiên Đàng để lấy Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(58,20,'58:20','Chắc chắn những ai nghịch lại mệnh lệnh của Allah và Thiên Sứ của Ngài đều thuộc đám người bị Allah hạ nhục một cách thấp hèn trên cõi đời này và Đời Sau giống như đám người vô đức tin trước đây đã bị hạ nhục.');
INSERT INTO "vn_mokhtasar" VALUES(58,21,'58:21','Allah đã định và ghi trong văn bản lưu trữ trên cao nơi Ngài rằng Ngài tự giúp chính Ngài và Thiên Sứ của Ngài chiến thắng trước kẻ thù bằng chính bằng chứng và sức mạnh. Quả thật, Allah là Đấng đầy quyền lực phù hộ Thiên Sứ của Ngài, và toàn năng đập tan mọi kẻ thù.');
INSERT INTO "vn_mokhtasar" VALUES(58,22,'58:22','Ngươi - hỡi Thiên Sứ - sẽ không thấy một nhóm người nào tin tưởng Allah, tin tưởng vào Ngày Sau lại thích kết thân với những ai chống đối và nghịch lại Allah và luôn làm trái lệnh của Ngài và Thiên Sứ của Ngài, dù đó là cha mẹ, con cái, anh em và người thân thuộc của họ đi chăng nữa. Những người đó là những người luôn kết thân vì Allah và sẽ thù hằn vì Ngài, trong tim họ luôn được Allah làm cho đức tin vững chắc, Ngài sẽ ủng hộ và giúp họ giành chiến thắng đánh bại kẻ thù của họ trên trần gian, rồi vào Ngày Sau họ sẽ được Ngài thu nhận vào Thiên Đàng của Ngài với những ngôi vườn xanh tươi bên dưới có các dòng sông chảy, họ sẽ được sống trong đó đời đời với niềm hạnh phúc vô tận. Allah sẽ hài lòng về họ và sẽ không giận dữ với họ, và họ cũng hài lòng và toại nguyện về những gì mà Thượng Đế của họ ban cho họ từ những ân huệ và phúc lộc thật vĩ đại nơi Thiên Đàng, ngoài ra họ còn được cơ hội ngắm nhìn Đấng Toàn Năng. Họ đích thực là những người thuộc bè phái của Allah và sẽ được thắng lợi và thành công trên cõi đời này và ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(59,1,'59:1','Allah là Đấng mà mọi vạn vật trong các tầng trời và trái đất đều phải ca tụng và tán dương. Ngài là Đấng Toàn Năng, không có thứ gì có thể vượt qua quyền năng của Ngài, Ngài là Đấng Sáng Suốt với nguồn kiến thức vô tận trong việc tạo hóa, trong giáo luật của Ngài, trong việc điều hành và ban lệnh.');
INSERT INTO "vn_mokhtasar" VALUES(59,2,'59:2','Ngài (Allah) là Đấng đã trục xuất những kẻ đã vô đức tin nơi Allah và phủ nhận sứ mạng của Sứ Giả Muhammad trong số những người dân Kinh Sách ra khỏi chỗ ở của chúng. Chúng là những người Do Thái thuộc bộ tộc Al-Nadhir, chúng là những cư dân láng giềng với những người Muslim xung quanh Madinah. Đó là cuộc trục xuất đầu tiên dành cho chúng ra khỏi bán đảo Ả-rập để đến với vùng đất Sham (nay được chia thành bốn quốc gia: Li Băng, Jordan, Syria và Palestine), với lý do chúng đã bội ước và còn liên minh với bọn đa thần. Các ngươi - hỡi những người có đức tin - không nghĩ rằng chúng sẽ rời bỏ chỗ ở của chúng một cách nhục nhã và hèn hạ như thế bởi sự kiên cố và sức mạnh của chúng, và chúng cũng nghĩ rằng thành lũy của chúng thật kiên cố không một ai có khả năng đánh vỡ nó. Tuy nhiên, Allah đã gây ra cho chúng chúng không hề ngờ tới được, Ngài đã ném vào lòng của chúng nỗi sợ hãi khiến chúng phải bỏ đi, chúng đã tự đập phá nhà cửa của mình cùng với những người tin tưởng. Và đó là bài học dành cho những người có cái nhìn và nhận thức đúng mực về những gì xảy ra cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(59,3,'59:3','Nếu như Allah không trục xuất chúng ra khỏi chỗ ở của chúng thì chắc chắn Ngài sẽ trừng phạt chúng trên trần gian qua sự giết chóc và tù đầy giống như những gì đã xảy ra với đám anh em của chúng - bộ tộc Quraizhah - và Ngày Sau chúng lại sẽ phải chịu sự trừng phạt muôn đời trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(59,4,'59:4','Đó là kết quả do chúng đã tạo ra bởi chúng đã chống đối và phản nghịch Allah và Thiên Sứ của Ngài bằng sự vô đức tin và quy phạm giao ước, và những ai đối nghịch với Allah sẽ bị Ngài trừng phạt thích đáng và y sẽ gánh chịu cực hình.');
INSERT INTO "vn_mokhtasar" VALUES(59,5,'59:5','Những cây chà là mà các ngươi - hỡi những người có đức tin - đã chặt ngã xuống hoặc cây nào các ngươi chừa lại, tất cả những việc làm đó đều do ý chỉ của Allah. Đó không phải là hành động hủy hoại trái đất như họ đã khẳng định. Sở dĩ, Ngài cho phép các ngươi làm thế vì Ngài muốn hạ nhục những kẻ đã rời xa sự tuân phục Ngài cũng như luôn làm trái nghịch mệnh lệnh của Ngài và đã chọn con đường phản bội.');
INSERT INTO "vn_mokhtasar" VALUES(59,6,'59:6','Và những gì từ chiến lời phẩm mà Allah đã ban cho vị Thiên Sứ của Ngài từ nguồn tài sản và của cải của những người Do Thái thuộc bộ tộc Al-Nadhir thì các ngươi (những người có đức tin) không cần phải tốn công sức cưỡi ngựa hay lạc đà để giành lấy nó. Allah sẽ ban quyền kiểm soát và chế ngự cho vị Thiên Sứ của Ngài đối với người nào Ngài muốn trong số kẻ thù của Ngài, rồi chúng sẽ giao nộp tài sản của chúng cho các ngươi mà không cần đến cuộc chiến nào cả. Bởi quả thật, Allah là Đấng Toàn năng trên mọi thứ, không một điều gì có thể vượt qua quyền năng của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(59,7,'59:7','Nguồn lợi phẩm mà Allah thu hồi cho Thiên Sứ của Ngài từ nguồn tài sản của những kẻ đa thần từ các thị trấn mà không cần đến cuộc chinh chiến là của Allah, đó là Ngài muốn ban cho ai tùy Ngài và Thiên Sứ của Ngài toàn quyền sở hữu. Và nguồn chiến lợi phẩm đó dành cho bà con ruột thịt của Thiên Sứ thuộc hai dòng con cháu Hashim và con cháu Al-Muttalib, bù đắp cho điều khoản rằng họ không được phép nhận tiền bố thí; dành cho các trẻ mồ côi (những đứa trẻ nghèo không cha); cho những người khó khăn (những người nghèo, những người có hoàn cảnh túng thiếu); và cho những người lỡ đường (những người gặp trắc trở khi đi xa nhà). Allah quy định như vậy vì không muốn nguồn tài sản đó rơi vào tay của những người giàu có mà bỏ mặc những người nghèo khổ và túng thiếu. Và những gì mà Thiên Sứ mang đến cho các ngươi từ nguồn tài sản thì các ngươi - hỡi những người có đức tin - hãy nhận lấy, cũng như những gì Y ra lệnh cho các ngươi thì các ngươi hãy thi hành, còn những gì mà Y cấm các ngươi thì các ngươi hãy từ bỏ và không được đến gần. Các ngươi hãy kính sợ Allah mà chấp hành mọi mệnh lệnh của Y, bởi quả thật, Allah rất nghiêm khắc trong việc trừng phạt những ai chống đối và trái nghịch mệnh lệnh của Y.');
INSERT INTO "vn_mokhtasar" VALUES(59,8,'59:8','Trong nguồn lợi phẩm thu được cũng có phần của những người nghèo thuộc những người Muhajir (những người từ Makkah dời cư đến Madinah) bởi vì họ đã bị những kẻ vô đức tin ở Makkah trục xuất rời bỏ nhà cửa của họ, họ đã bỏ lại tài sản và của cải vì muốn tìm ân phúc của Allah ở đời này và ở Đời Sau, và họ cũng đã tận tâm tận sức ủng hộ tôn giáo của Allah và đã cùng Thiên Sứ của Ngài chinh chiến cho con đường chính nghĩa của Ngài. Họ thực sự là những người chân thật trong lời nói và hành động.');
INSERT INTO "vn_mokhtasar" VALUES(59,9,'59:9','Và những người thuộc cư dân Madinah, những người đã tin tưởng trước cuộc di cư của những người Muhajir (những người Al-Ansar), họ đã hết lòng yêu thương những người Muhajir, họ đã chia sớt tài sản cho những người Muhajir, họ không hề cảm thấy trong lòng sự ghen tị và ganh ghét về những gì họ cho đi, lúc nào họ cũng ưu tiên cho những người Muhajir mặc dù họ cũng không kém phần thiếu thốn và khó khăn. Và người nào giữ được tấm lòng khỏi sự keo kiệt và ích kỷ thì chắc chắn sẽ thành đạt và thắng lợi.');
INSERT INTO "vn_mokhtasar" VALUES(59,10,'59:10','Và những người có đức tin sau những người Al-Ansar và những người Muhajir của thuở đầu đã cầu nguyện: Lạy Thượng Đế của bầy tôi, xin Ngài hãy tha thứ tội lỗi cho bầy tôi và cho những người anh em đã tin tưởng trước bầy tôi, xin Ngài đừng để trong lòng bầy tôi nỗi oán hận và sự ganh ghét một ai trong những người tin tưởng. Lạy Thượng Đế của bầy tôi, quả thật Ngài là Đấng luôn thương xót và bao dung với bầy tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(59,11,'59:11','Lẽ nào Ngươi - hỡi Thiên Sứ - không nghe thấy những kẻ Munafiq đã nói với những anh em vô đức tin của chúng từ những người Do Thái, hậu duệ của Kinh Tawrah bị chỉnh sửa hay sao? Chúng nói: “Nếu Muhammad và đồng bọn của hắn trục xuất các người khỏi nhà cửa của các người thì chúng tôi sẽ ra đi cùng với các người, chúng tôi sẽ không nghe theo lời của bất cứ ai nếu họ có yêu cầu chúng tôi tiếp tay chèn ép các người hay bảo chúng tôi đứng ra đi cùng với các người; còn nếu họ chiến đấu với các người thì chúng tôi sẽ trở giúp các người hết mình”. Nhưng Allah xác nhận rằng quả thật những kẻ ngụy tạo đức tin là những tên nói dối về những gì mà chúng đã hứa với những người Do Thái thuộc bộ tộc Al-Nadhir.');
INSERT INTO "vn_mokhtasar" VALUES(59,12,'59:12','Nếu người Muslim trục xuất bọn Do Thái thì chắc chắn chúng, những tên Munafiq sẽ không ra đi cùng với họ, còn nếu họ bị tấn công thì chúng cũng sẽ không bao giờ trợ giúp. Mà nếu chúng có trợ giúp họ đi chăng nữa nhưng rồi cũng sẽ có ngày chúng quay lưng lại với họ. Và rồi rốt cuộc, chúng cũng là những kẻ không được ai giúp đỡ, đúng hơn chúng sẽ bị Allah hạ nhục mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(59,13,'59:13','Quả thật, trong lòng của những kẻ Munafiq và đám người Do Thái - hỡi những người có đức tin - sợ hãi các ngươi hơn cả Allah. Sở dĩ như vậy là vì họ là một đám người vô trí thức, không am hiểu về sự vĩ đại của Allah, nếu họ có nhận thức thì chính Allah mới đáng sợ hơn, đáng chạy trốn hơn, bởi Ngài là Đấng cho các ngươi quyền chế ngự họ.');
INSERT INTO "vn_mokhtasar" VALUES(59,14,'59:14','Những người Do Thái sẽ không hợp sức lại đánh các ngươi đâu - hỡi những người có đức tin - mà chúng chỉ cố thủ trong những vùng có thành lũy kiên cố, hoặc là chúng chỉ có thể dám đánh các ngươi từ phía sau những bức tường, chúng rất nhát gan không đủ can đảm đối đầu trực diện với các ngươi. Quả thật sự thù hằn và oán ghét nhau giữa bọn chúng rất dữ dội, Ngươi cứ tưởng chúng luôn đoàn kết, nhưng thực chất tấm lòng của chúng lại chia rẽ nhau. Sở dĩ như vậy là vì chúng là một đám người không biết suy nghĩ và cũng không hiểu được lời mặc khải của Allah, nếu chúng biết nhận thức thì chúng đã theo chân lý mất rồi và đã không chia rẽ như thế.');
INSERT INTO "vn_mokhtasar" VALUES(59,15,'59:15','Hình ảnh của những người Do Thái bị trừng phạt thích đáng giống như những kẻ vô đức tin Quraish trong khoảng thời gian gần đây, chúng đã trả giá trong ngày Badr, một số đã bị giết và số khác đã bị bắt làm tù binh, và Ngày Sau chúng sẽ nhận lấy một hình phạt thật đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(59,16,'59:16','Hình ảnh của những người Do Thái nghe theo lời của đám người Munafiq giống như hình ảnh của Shaytan khi nó bảo con người phủ nhận đức tin và rồi khi con người đã vô đức tin thì nó lại bảo: Ta không dính dáng gì đến các ngươi cả, quả thật ta rất sợ Allah, Đấng Chủ Tể của vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(59,17,'59:17','Và kết cuộc của cả hai (Shaytan là kẻ xúi và con người là kẻ nghe theo) vào Ngày Tận Thế đều sẽ phải sống trong Hỏa Ngục đời đời không có ngày trở ra. Đó là phần thưởng thích đáng dành cho những kẽ vượt quá giới hạn của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(59,18,'59:18','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Các ngươi hãy kính sợ Allah mà nhất nhất tuân thủ theo mệnh lệnh của Ngài, mỗi người các ngươi phải xem xét lại những việc làm mà các ngươi đã gởi đi trước cho Ngày Phán Xét. Các ngươi hãy kính sợ Allah, quả thật, Allah là Đấng am tường mọi điều các ngươi làm, không một điều gì có thể che giấu được Ngài, và Ngài sẽ thưởng phạt công bằng cho các việc làm và hành động của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(59,19,'59:19','Các ngươi đừng giống như những kẻ quên đi Allah khi họ không tuân thủ theo mệnh lệnh của Ngài rồi họ bị Allah quên lãng khiến họ không nhận biết cách cứu họ thoát khỏi sự trừng phạt và giận dữ của Allah. Và những người đó bị xem là những kẻ đã quên Allah - họ không theo lệnh Allah bảo và không biết tránh những gì Ngài cấm - họ đã tách khỏi sự tuân phục của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(59,20,'59:20','Những người bị đày trong Hỏa Ngục và những người được sống ở nơi Thiên Đàng không hề như nhau, phần thưởng dành cho họ khác nhau giống như hành động của họ đã khác nhau ở trên trần gian vậy, những người nơi Thiên Đàng là những người đã giành được mọi điều như mong muốn và được cứu rỗi thoát khỏi những gì họ lo lắng.');
INSERT INTO "vn_mokhtasar" VALUES(59,21,'59:21','Nếu TA truyền khải Kinh Qur’an này xuống cho một quả núi nào đó thì Ngươi - hỡi Thiên Sứ - sẽ thấy dù núi rất kiên cố, rắn chắc và hùng dũng vẫn trở nên yếu ớt đến nỗi vỡ vụn ra vì quá sợ Allah, bởi Qur''an chứa đựng những lời cảnh báo kinh hãi và rùng rợn. Đó là thí dụ mà TA muốn cho con người hiểu mong rằng họ biết suy ngẫm về quyền năng và sự vĩ đại của Allah như thế nào.');
INSERT INTO "vn_mokhtasar" VALUES(59,22,'59:22','Ngài là Allah, Đấng Tối Cao đáng được thờ phượng, không có Thượng Đế đích thực nào khác ngoài Ngài, Ngài biết rõ mọi điều thầm kín và công khai, những điều hữu hình và vô hình, Ngài là Đấng tràn đầy tình thương xót và lòng thương xót của Ngài bao trùm lên vạn vật ở trần gian và Đời Sau, và Ngài là Đấng khoan dung với những người có đức tin nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(59,23,'59:23','Ngài là Allah, Thượng Đế đích thực đáng được thờ phượng, không có Đấng thờ phượng nào khác ngoài Ngài, Ngài là Đức Vua của muôn loài và vạn vật, Ngài là Đấng chi phối và điều hành, Đấng giữ trật tự cho mọi sự vật, Đấng trông coi và quan sát mọi hành động của con người, Đấng Toàn Năng trên mọi thứ không có bất cứ điều gì có thể vượt qua quyền năng của Ngài, Đấng chế ngự mọi thứ, Đấng tự hào về sự vĩ đại và tối cao của Ngài. Ngài là Allah, Đấng khác xa và vượt trội hơn hẳn những gì mà những kẻ thờ đa thần đã gán cho Ngài trong việc thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(59,24,'59:24','Allah là Đấng Tạo hóa và định lượng cho tất cả vạn vật, Đấng khởi sự cho mọi nguồn sống và tồn tại của mọi sự vật, Đấng định đoạt và sáng tạo hình thể cho mọi tạo vật theo ý của Ngài, Ngài là Đấng với những đại danh hoàn mỹ và thuộc tính tối cao tuyệt đối, tất cả vạn vật trong các tầng trời và vạn vật dưới đất đều tán dương và ca ngợi Ngài bởi Ngài là Đấng Toàn Năng trong việc trừng trị những kẻ chống đối Ngài và là Đấng Sáng Suốt và Khôn Ngoan trong việc quản lý và điều hành mọi sự việc.');
INSERT INTO "vn_mokhtasar" VALUES(60,1,'60:1','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Các ngươi đừng kết bạn với những ai là kẻ thù của Allah và là kẻ thù của các ngươi, đừng thể hiện tình cảm với họ và bảo hộ cho họ. Bởi thật, họ đã phủ nhận điều chân lý đã đến với các ngươi do Thiên Sứ của Ngài truyền tải về tôn giáo và họ đã trục xuất Thiên Sứ và cả các ngươi ra khỏi Makkah, quê hương của các ngươi. Họ đã không nghĩ đến tình máu mủ, không thương xót các ngươi hoặc bất cứ điều gì chỉ bởi vì các ngươi tin tưởng Allah là Thượng Đế duy nhất của các ngươi. Nếu các ngươi thực sự đi chiến đấu vì con đường chính đạo của TA (Allah) để tìm sự hài lòng nơi TA thì các ngươi chớ kết tình hữu nghị với họ bằng cách âm thầm thể hiện tình cảm với họ qua việc truyền thông tin mật của người Muslim cho họ. Quả thật, TA biết hết những điều các ngươi giấu kín và những điều các ngươi công khai. Và người nào trong các ngươi làm thế thì quả thật đó là điều sai lệch với chân lý và lạc khỏi con đường ngay chính.');
INSERT INTO "vn_mokhtasar" VALUES(60,2,'60:2','Nếu họ, những kẻ đã được các ngươi âm thầm thể hiện tình hữu nghị với họ, giành được chiến thắng thì họ sẽ giăng tay của họ ra để giết và tiêu diệt các ngươi, họ sẽ dùng chiếc lưỡi của họ để xỉ nhục và chửi rủa các ngươi, và họ chỉ luôn mong rằng các ngươi sẽ phủ nhận đức tin giống như họ.');
INSERT INTO "vn_mokhtasar" VALUES(60,3,'60:3','Anh em, con cái và bà con ruột thịt của các ngươi sẽ không giúp ích một điều gì cho các ngươi khi mà các ngươi đã kết bạn với những kẻ vô đức tin vì họ. Vào Ngày Phục Sinh, Allah sẽ chia cách giữa các ngươi để phân loại: Những người tuân lệnh Ngài sẽ được vào Thiên Đàng và những người phản nghịch sẽ vào Hỏa Ngục, không ai có thể giúp ích được ai. Bởi lẽ, Allah luôn am tường và thấy hết tất cả những gì các ngươi làm, không có bất cứ điều gì từ lời nói và hành động của các ngươi có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(60,4,'60:4','Rõ ràng trong các ngươi - hỡi những người có đức tin - đã có một tấm gương tốt đẹp cho các ngươi noi theo, đó là tấm gương của Nabi Ibrahim và những người theo Y về những điều họ đã nói với người dân vô đức tin của họ: Quả thật, chúng tôi vô can với các người và với những gì mà các người thờ phượng khác ngoài Allah từ những thần linh của các người, chúng tôi phủ nhận các người và phủ nhận những gì các người đã làm một cách vô đức tin, giữa chúng tôi với các người sẽ luôn tồn tại sự thù hằn cho tới khi nào các người quay lại tin tưởng nơi một mình Allah duy nhất. Còn việc Ibrahim cầu xin Allah tha thứ tội lỗi cho cha của Y không phải là tấm gương cho các ngươi bởi việc làm đó của Y là trước khi Y nhận ra rằng cha của mình là kẻ thù của Allah, nhưng sau khi được rõ rằng cha của Y thực sự là kẻ thù của Allah thì Y đã không dính líu tới cha của Y nữa. Vì người có đức tin không thể cầu xin (Allah) tha thứ cho người thờ đa thần, và (nếu có cầu xin) lời cầu xin đó cũng không giúp hắn thoát khỏi hình phạt từ Allah, và Y đã cậu nguyện thưa: Lạy Thượng Đế của bầy tôi, bầy tôi xin phó thác cho Ngài và bầy tôi xin quay về sám hối với Ngài và bầy tôi sẽ trở về gặp Ngài vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(60,5,'60:5','Lạy Thượng Đế của bầy tôi, xin Ngài đừng biến bầy tôi thành mục tiêu thử thách cho những kẻ vô đức tin khi Ngài cho quyền những kẻ vô đức tin chế ngự bầy tôi, họ sẽ bảo: “Nếu bọn chúng đang ở trên chân lý sao lại bị trừng phạt như thế”. Lạy Thượng Đế của bầy tôi, xin Ngài hãy tha thứ tội lỗi cho bầy tôi, quả thật, Ngài là Đấng Toàn Năng không có ai (vật gì) có thể vượt qua quyền năng của Ngài, và Ngài là Đấng Sáng Suốt và Khôn Ngoan trong lời nói và hành động.');
INSERT INTO "vn_mokhtasar" VALUES(60,6,'60:6','Với tấm gương này chỉ có những ai muốn đạt được điều tốt đẹp từ nơi Allah ở trên đời này và cõi Đời Sau. Và người nào quay lưng với những vị Nabi đại diện cho Ngài thì quả thật Allah vẫn không ảnh hưởng gì, Ngài vẫn là Đấng giàu có không cần đến bất cứ thứ gì từ bề tôi của Ngài, và Ngài vẫn là Đấng đáng được ca ngợi và tán dương bởi những thuộc tính và bản chất Tối Cao và Vĩ Đại của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(60,7,'60:7','Có thể, Allah sẽ gắn kết các ngươi - hỡi những người có đức tin - và những người mà các ngươi đã xem họ như kẻ thù khi Ngài khiến những người đó tiếp nhận Islam, lúc đó các ngươi là anh em đồng đạo của nhau rồi. Quả thật, Allah là Đấng Toàn Năng trên mọi nhứ và là Đấng hằng tha thứ tội lỗi cho bề tôi của Ngài bởi vì Ngài luôn khoan dung cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(60,8,'60:8','Allah không hề ngăn cấm các ngươi có mối quan hệ và đối xử tử tế với những người vô đức tin không giáo chiến với các ngươi và không tham gia trục xuất các ngươi khỏi nhà cửa của các ngươi. Các ngươi hãy đối xử tử tế và công bằng với họ giống như Asma con gái của Abu Bakr Al-Siddiq đã hỏi ý kiến Thiên Sứ khi mẹ của bà vẫn còn là người vô đức tin đến thăm bà, Người ra lệnh bà gắn kết tình thân với mẹ, bởi quả thật Allah yêu thương những ai luôn công bằng trong lời nói và hành động của họ.');
INSERT INTO "vn_mokhtasar" VALUES(60,9,'60:9','Quả thật, Allah chỉ cấm các ngươi giao ban với những ai vì vấn đề tôn giáo mà giao chiến với các ngươi và trục xuất các ngươi khỏi nhà cửa của các ngươi, hoặc chỉ cấm các ngươi giao hữu với những kẻ đã tiếp tay trục xuất các ngươi. Và ai kết thân với họ và ủng hộ họ chống lại những người có đức tin thì những người đó là những kẻ đã bất công với chính bản thân họ vì đã tự đẩy bản thân nghịch lại mệnh lệnh của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(60,10,'60:10','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Khi nào có những người phụ nữ có đức tin chạy đến tị nạn với các ngươi từ nơi của những người vô đức tin thì các ngươi hãy kiểm tra các nữ để xác thực đức tin của các nữ rằng các nữ thực sự tin tưởng hay không, và Allah mới là Đấng biết rõ sự thật đức tin của các nữ, nhưng nếu các ngươi đã xác định các nữ thực sự là những người có đức tin dựa theo những gì các ngươi nhìn thấy qua biểu hiện của các nữ thì các ngươi chớ đừng trả các nữ về với những người chồng không có đức tin của các nữ, bởi những phụ nữ tin tưởng không được phép kết hôn với những người vô đức tin và những người vô đức tin cũng không được phép kết hôn với những người phụ nữ có đức tin, và các ngươi hãy trao trả cho những người chồng của những phụ nữ tin tưởng này những gì mà chúng đã chi ra cho họ từ tiền cưới hỏi (Mahar), và các ngươi sẽ không bị bắt tội nếu các ngươi kết hôn với họ miễn là sau khi hết thời gian ''Iddah và các ngươi trao cho các nữ tiền cưới (Mahar) đàng hoàng. Và đừng cố tình níu kéo cuộc sống hôn nhân khi những người vợ không có đức tin, bởi cuộc hôn nhân của các ngươi đã bị hủy bởi sự vô đức tin của các bà vợ; các ngươi có quyền đòi lại tiền cưới mà các ngươi đã chi ra cho các nữ sau khi bỏ đạo, và hãy để những kẻ vô đức tin đòi lại tiền cưới mà họ đã chi ra cho những người vợ của họ đã quy thuận Islam. Đó là điều luật của Allah đã quy định cho các ngươi trong việc giải quyết vấn đề được nói, do đó, các ngươi đừng làm trái lệnh của Ngài. Và Allah luôn am tường mọi sự việc, không có điều gì có thể giấu kín được Ngài và Ngài là Đấng Sáng Suốt và Khôn Ngoan trong lời nói và hành động.');
INSERT INTO "vn_mokhtasar" VALUES(60,11,'60:11','Và nếu một số người vợ của các ngươi bỏ đạo để đến với những kẻ vô đức tin và những kẻ vô đức tin không trao trả cho các ngươi tiền cưới mà các ngươi đã chi trả cho các nữ trước đây, rồi sau đó, các ngươi giành được thắng lợi từ những kẻ vô đức tin này, các ngươi đã đánh bại họ, lúc đó các ngươi hãy đưa cho những người Muslim đã bị những người vợ rời xa để đến với những kẻ vô đức tin được nói trên từ nguồn chiến lợi phẩm giống như những gì mà họ đã trao cho vợ của họ trước đó. Các ngươi hãy kính sợ Allah, Đấng mà các ngươi đã tin tưởng nơi Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(60,12,'60:12','Này hỡi Nabi! Khi nào có những người phụ nữ có đức tin đến với Ngươi và tuyên bố trước Ngươi - giống như sau khi Makkah được giải phóng - rằng họ sẽ không làm điều Shirk với Allah bất cứ một điều gì, họ sẽ không trộm cắp, không phạm tội Zina (ngoại tình và thông dâm), không giết con cái của mình, không gán cho chồng của họ những đứa con mà chúng không phải là con của chồng họ, và họ sẽ không làm trái lệnh của Ngươi về điều thiện tốt mà Ngươi ra lệnh giống như không khóc gào thét, tự tát má, xé quần áo, thì Ngươi hãy chấp nhận lời giao ước đó của họ. Ngươi hãy cầu xin Allah tha thứ tội lỗi cho họ, quả thật Allah là Đấng hằng tha thứ tội lỗi cho những người bề tôi biết quay đầu sám hối, và Ngài là Đấng luôn khoan dung với họ.');
INSERT INTO "vn_mokhtasar" VALUES(60,13,'60:13','Này hỡi những người có đức tin nơi Allah và tuân theo mệnh lệnh của Ngài! Các ngươi đừng kết thân với những kẻ mà Allah đã giận dữ với họ, quả thật họ đã tuyệt vọng với những phần thưởng của Allah ở Đời Sau giống như những kẻ vô đức tin trong cõi mộ đã tuyệt vọng về lòng khoan dung của Allah ở cõi Đời Sau khi mà họ đã nhìn thấy sự thật và biết chính xác rằng họ sẽ không được gì cả; hoặc giống như những kẻ vô đức tin đã tuyệt vọng về việc họ được phục sinh từ cõi chết trở lại khi mà lúc trước họ đã không tin điều này.');
INSERT INTO "vn_mokhtasar" VALUES(61,1,'61:1','Allah cho chúng ta biết rằng tất cả mọi vạn vật trong các tầng trời và mọi vạn vật dưới đất đều tán dương ca ngợi Ngài bởi Ngài là Đấng Toàn Năng, không có ai (vật gì) có thể vượt qua quyền năng của Ngài và Ngài là Đấng Sáng Suốt và Khôn Ngoan trong lời nói và hành động.');
INSERT INTO "vn_mokhtasar" VALUES(61,2,'61:2','Này hỡi những người tin tưởng Allah! Sao các ngươi lại nói: Chúng tôi đã làm điều gì đó, trong khi thực tế các ngươi đã không làm?! Giống như một kẻ đã nói: Tôi đã chiến đấu bằng thanh kiếm của mình và tôi đã dùng nó để chém kẻ thù nhưng thật ra y không làm như vậy.');
INSERT INTO "vn_mokhtasar" VALUES(61,3,'61:3','Điều mà Allah ghét nhất là việc các ngươi nói những điều các ngươi không làm. Người có đức tin thực thụ là người luôn thật thà với Allah, hành động của y phải đi đôi với lời nói.');
INSERT INTO "vn_mokhtasar" VALUES(61,4,'61:4','Quả thật, Allah yêu thương những người có đức tin anh dũng chiến đấu vì con đường chính đạo của Allah hồng làm Ngài hài lòng, họ xông pha ra trận chiến theo một đội ngũ vững chắc như thể họ là một thành lũy kiên cố và chắc chắn, đứng san sát nhau không một kẻ hở.');
INSERT INTO "vn_mokhtasar" VALUES(61,5,'61:5','Ngươi - hỡi Thiên Sứ - hãy nhớ lại lời của Musa khi Y nói với dân chúng của mình: Sao các ngươi lại làm tổn thương Ta khi các ngươi nghịch lại mệnh lệnh của Ta trong khi các ngươi biết rõ rằng Ta đích thực là vị Thiên Sứ của Allah được phái đến cho các ngươi? Và một khi chúng đã lệch khỏi chân lý mà chúng biết rất rõ về nó và chúng vẫn cứ ngoan cố trên sự sai lệch đó, chúng đã bị Allah làm cho tấm lòng của chúng không thể chấp nhận sự hướng dẫn và phục tùng. Và quả thật, Allah không hướng dẫn và soi sáng cho những kẻ rời xa sự tuân lệnh và phục tùng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(61,6,'61:6','Ngươi - hỡi Thiên Sứ - hãy nhớ lại lời của Ysa (Giê-su) con trai của Maryam nói với người dân của Y: “Hỡi con cháu Israel, chắc chắn Ta chính là Thiên Sứ của Allah được Ngài phái đến cho các ngươi, mục đích nhằm xác nhận lại những gì đến trước Ta từ trong kinh Tawrah (Kinh Cựu ước), Ta không phải kẻ mạo danh thiên sứ và để Ta làm chứng xác thực về một vị Thiên Sứ sẽ xuất hiện sau Ta tên là Ahmad, và đó chính là một tên khác của Muhammad”. Vậy mà khi Ysa đến với họ mang những lời mặc khải rõ rệt thì họ lại bảo: “Đây là một trò phù thủy hiển hiện, chúng tôi không nghe theo.”');
INSERT INTO "vn_mokhtasar" VALUES(61,7,'61:7','Và không một ai là kẻ bất công và đáng ghét hơn những kẻ đã lừa dối Allah, đã gán cùng với Ngài một thần linh đồng đẳng trong việc thờ phượng trong khi y được mời gọi vào Islam và tôn thờ một mình Allah duy nhất. Quả thật, Allah sẽ không hướng dẫn những kẻ phủ nhận đức tin, làm điều Shirk đến với sự thành công.');
INSERT INTO "vn_mokhtasar" VALUES(61,8,'61:8','Những kẻ vô đức tin tội lỗi muốn dập tắt Ánh Sáng của Allah bằng chính những lời nói bịa đặt và điêu ngoa của họ để thay đổi chân lý, nhưng cho dù họ có cố chống đối và phủ nhận như thế nào đi chăng nữa thì Allah vẫn hoàn thiện Ánh Sáng của Ngài từ đông sang tây và giơ cao lời phán của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(61,9,'61:9','Allah là Đấng đã cử phái vị Thiên Sứ của Ngài, Muhammad - cầu xin Allah bang sự bình an đến Người - mang tôn giáo Islam đến, tôn giáo của sự chỉ đạo và hướng dẫn đến với điều tốt đẹp, tôn giáo của kiến thức hữu ích và việc làm đức hạnh, để Nó lấn áp tất cả các tôn giáo khác, Nó khiến những kẻ thờ đa thần ghét Nó khi Nó có vị thế trên mặt đất.');
INSERT INTO "vn_mokhtasar" VALUES(61,10,'61:10','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Các ngươi có muốn TA chỉ cho các ngươi cách trao đổi mua bán có thể giải thoát các ngươi khỏi sự trừng phạt đau đớn hay không?');
INSERT INTO "vn_mokhtasar" VALUES(61,11,'61:11','Cuộc đổi chác đầy lợi nhuận đó là các ngươi tin tưởng Allah và Thiên Sứ của Ngài, rồi các ngươi chiến đấu vì con đường chính nghĩa của Allah bằng cách chi xuất tài sản của các ngươi và hi sinh tính mạng của các ngươi để làm hài lòng Allah. Đó là một sự đổi chác tốt đẹp nhất trên thế gian nếu các ngươi hiểu. Bởi thế các ngươi hãy nhanh chân đến với nó.');
INSERT INTO "vn_mokhtasar" VALUES(61,12,'61:12','Lợi nhuận của cuộc đổi chác này là Allah tha thứ tội lỗi cho các ngươi và Ngài thu nhận các ngươi vào các Ngôi Vườn Thiên Đàng với các dòng sông chảy bên dưới các tòa lâu đài. Đó là một thành tựu mà không có thành tựu nào khác vĩ đại hơn.');
INSERT INTO "vn_mokhtasar" VALUES(61,13,'61:13','Một lợi nhuận khác của cuộc đổi chác này khiến các ngươi sẽ rất thích nó, đó là TA (Allah) sẽ giúp các ngươi chiến thắng kẻ thù của các ngươi và một sự chinh phục gần kề sắp đạt được, đó là cuộc chinh phục Makkah. Do đó, Ngươi - hỡi Thiên Sứ - hãy báo tin vui cho những người có đức tin về sự phù hộ và cuộc thắng lợi trên thế gian và thắng lợi Thiên Đàng ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(61,14,'61:14','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Hãy là những người ủng hộ và giúp đỡ con đường chính nghĩa của Allah giống như điều mà Ysa (Giê-su) đã ủng hộ và giúp đỡ con đường chính nghĩa của Allah. Nabi Ysa đã bảo tông đồ của Người: Ai trong các ngươi ủng hộ Ta trong việc đến gần Allah? Họ liền nói: Chúng tôi là những người ủng hộ tôn giáo của Allah. Nhưng chỉ có một số tin tưởng còn một số khác thì không, và TA đã phù hộ và trợ giúp cho những người có đức tin nơi Allah và Thiên Sứ của Ngài, TA đã giúp họ đánh bại kẻ thù.');
INSERT INTO "vn_mokhtasar" VALUES(62,1,'62:1','Allah tự thanh lọc bản thân Ngài khỏi mọi thứ không xứng đáng với Ngài, tất cả vạn vận trong các tầng trời và vạn vật dưới đất đều tán dương ca ngợi Ngài bởi Ngài là Đấng Toàn Năng, không ai (vật gì) có thể vượt qua quyền năng của Ngài, và Ngài là Đấng Sáng Suốt và Khôn Ngoan trong lời nói và hành động.');
INSERT INTO "vn_mokhtasar" VALUES(62,2,'62:2','Allah là Đấng đã cử phái từ trong đám người mù chữ tức những người Ả-rập không biết đọc cũng không biết viết một vị Thiên Sứ xuất thân từ họ đến với toàn nhân loại để đọc cho họ nghe Kinh Qur’an, và tẩy sạch họ khỏi những tín ngưỡng sai lệch, những tính cách xấu xa, và dạy cho họ Qur’an và Sunnah. Quả thật, trước đó họ đã lầm lạc khỏi chân lý khi họ thờ phượng bục tượng, gây đổ máu và đoạn tuyệt dòng họ.');
INSERT INTO "vn_mokhtasar" VALUES(62,3,'62:3','Và Ngài cũng cử phải Y đến với những cộng đồng khác đã chưa nhập cùng với họ tức chưa gia nhập Islam từ những người Ả-rập và không phải Ả-rập. Và Allah là Đấng Toàn Năng không một ai (vật gì) có thể vượt qua quyền năng của Ngài, Ngài là Đấng Sáng Suốt và Khôn Ngoan trong lời nói và hành động.');
INSERT INTO "vn_mokhtasar" VALUES(62,4,'62:4','Việc Allah cử phái vị Thiên Sứ đến cho cộng đồng người Ả-rập và cộng đồng không phải người Ả-rập là một hồng ân vĩ đại từ nơi Allah, và ân phúc của Ngài Ngài muốn cho ai tùy Ngài. Quả thật, Allah là Đấng của những hồng ân vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(62,5,'62:5','Hình ảnh của những người Do Thái đã được giao phó trách nhiệm gánh vác Kinh Tawrah (Cựu ước) mà lại chối bỏ trách nhiệm giống như hình ảnh của những con lừa được chuyên chở trên lưng chúng bao nhiêu là sách nhưng con vật nào có biết nó đang chở gì đâu: đó là sách hay là thứ gì khác? Thật xấu xa và thảm hại cho đám người phủ nhận các lời mặc khải của Allah và quả thật Allah không bao giờ ban sự thành công cho những kẻ làm điều sai quấy đã vượt quá giới hạn của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(62,6,'62:6','Ngươi hãy nói - hỡi Thiên Sứ -: Hỡi những ai vẫn còn trên tôn giáo Do Thái sau khi đã bóp méo, nếu các người cho rằng các người là những người được Allah bảo hộ còn thiên hạ thì không, thế thì các người hãy cầu mong được chết đi để các người sớm được hưởng những thứ dành riêng cho các người - như các người đã khẳng định -, để xem các người có trung thực giống như lời các người đã tuyên bố hay không.');
INSERT INTO "vn_mokhtasar" VALUES(62,7,'62:7','Những người Do Thái đó sẽ không bao giờ dám mong muốn cái chết, ngược lại, họ luôn tham vọng được sống mãi trên thế gian bởi trong thâm tâm họ thừa biết những gì làm từ sự vô đức tin, sai trái và tội lỗi, bóp méo và thay đổi Kinh Tawrah của họ sẽ có kết cục như thế nào. Và Allah am tường hết mọi hành động của những kẻ làm điều sai quấy, không một điều gì có thể che giấu được Ngài, Ngài sẽ ban thưởng cho họ một cách xứng đáng.');
INSERT INTO "vn_mokhtasar" VALUES(62,8,'62:8','Ngươi hãy nói - hỡi Thiên Sứ - với đám người Do Thái: Cái chết mà các ngươi đang cố chạy trốn hầu thoát khỏi nó chắc chắn sẽ giáp mặt các ngươi, không bao giờ các ngươi có thể tránh khỏi nó, chỉ là sớm hay muộn mà thôi. Rồi các ngươi sẽ được đưa quay về trình diện với Allah trong Ngày Tận Thế, Ngài là Đấng hiểu biết về mọi điều vô hình và hữu hình, Ngài sẽ cho các ngươi biết tất cả mọi hành động của các ngươi và Ngài sẽ thưởng phạt các ngươi một cách công minh.');
INSERT INTO "vn_mokhtasar" VALUES(62,9,'62:9','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Khi nào tiếng của người Mu-azdzdin cất lên để loan báo đến giờ dâng lễ nguyện Salah vào ngày thứ sáu sau khi người đọc thuyết giảng đã bước lên bục giảng thì các ngươi hãy mau mau đến Masjid để nghe bài thuyết giảng và cùng dâng lễ nguyện Salah tập thể. Các ngươi hãy tạm gác lại việc mua bán kinh doanh sang một bên, điều đó tốt hơn cho các ngươi bởi các ngươi sẽ được Allah tha thứ tội lỗi và được ban thưởng nhiều hồng phúc. Nếu các ngươi biết điều đó là điều cải thiện cho bản thân các ngươi - hỡi những người có đức tin - thì các ngươi hãy thi hành mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(62,10,'62:10','Khi nào các ngươi đã nghe xong bài thuyết giảng và đã thực hiện xong lễ nguyện Salah tập thể của ngày thứ sáu thì các ngươi hãy ra đi khắp nơi trên trái đất mà tìm kiếm thiên lộc của Allah bằng những cách Halal (hợp pháp). Những các ngươi hãy luôn nhớ đến Allah thật nhiều trong mọi hoàn cảnh dù ở bất cứ nơi nào, đừng để việc bận rộn tìm kiếm bổng lộc khiến các ngươi quên đi lời tụng niệm Ngài, mong rằng các ngươi sẽ đạt được nhiều điều tốt đẹp mà các ngươi mong muốn và tránh được những điều các ngươi không ưa thích.');
INSERT INTO "vn_mokhtasar" VALUES(62,11,'62:11','Một số những người Muslim nhìn thấy việc mua bán và vui chơi thì họ vội vã ra đi đến đó và bỏ Ngươi - hỡi Thiên Sứ - đứng một mình thuyết giảng trên bục giảng. Ngươi hãy nói với họ: Những gì ở nơi Allah là những hồng ân tốt đẹp nhất, hơn hẳn những việc mua bán, vui chơi, và Allah là Đấng ban thiên lộc tốt nhất. Cho nên các ngươi cứ từ tốn mà tìm kiếm thiên lộc của Ngài và hãy tuân lệnh Ngài rồi Ngài sẽ ban thiên lộc cho các ngươi trên cõi đời này và cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(63,1,'63:1','Khi đám người Munafiq, những kẻ thể hiện Islam ở bên ngoài và che giấu sự vô đức tin bên trong đến gặp Ngươi - hỡi Thiên Sứ - nói "Chúng tôi xin xác nhận Người thật sự là Thiên Sứ của Allah" trong khi Allah biết rõ đích thực Ngươi là Thiên Sứ của Ngài, và Allah xác nhận những gì mà những kẻ Munafiq đã nói trong lời tuyên bố của chúng là một sự giả dối, chiếc lưỡi của chúng thề thốt xác nhận đức tin nhưng lòng chúng hoàn toàn phủ nhận nó.');
INSERT INTO "vn_mokhtasar" VALUES(63,2,'63:2','Những kẻ Munafiq cố tình dùng lời thề để làm bình phong che mắt những người có đức tin để khỏi bị giết và tù đầy, chúng tự ngăn cản bản thân chúng và ngăn cản mọi người đến với con đường ngay chính của Allah, quả thật, chúng là những tên xấu xa và tồi tệ trong những hành động mà chúng đã làm với việc ngụy tạo trong đạo đức và dối trá trong đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(63,3,'63:3','Sở dĩ như thế là vì chúng đã biểu hiện đức tin ở vẻ bề ngoài còn trong lòng chúng là sự phủ nhận và phản nghịch. Vì vậy, Allah đã niêm kín trái tim của chúng bởi sự vô đức tin của chúng khiến chúng chẳng hiểu điều gì có thể cải thiện được chúng.');
INSERT INTO "vn_mokhtasar" VALUES(63,4,'63:4','Và khi ngươi - hỡi người nhìn - thấy những kẻ Munafiq thì ngay lập tức bị họ thu hút bởi dáng vẻ lịch thiệp và đàng hoàng của họ, và khi họ nói chuyện khiến ngươi im lặng lắng nghe lời nói của họ bởi ngôn từ văn chương và mạch lạc nhưng thật ra trong tim họ lại trống rỗng với đức tin, trí tuệ và kiến thức của họ giống như những khúc gỗ được dùng làm rào chắn chứ không có hồn, họ luôn lo nghĩ và thấp thỏm cho mỗi một tiếng thét hướng về họ bởi họ sợ bị phát hiện sự thật được giấu kín trong lòng họ. Ngươi hãy cảnh giác họ, Allah sẽ nguyền rủa họ và trục xuất họ khỏi lòng thương xót và bao dung của Ngài. Tại sao họ lại quay mặt với chân lý để đến với sự ngụy tạo và lầm lạc như thế?');
INSERT INTO "vn_mokhtasar" VALUES(63,5,'63:5','Và khi những kẻ Munafiq này được bảo: Các người hãy đến gặp Thiên Sứ của Allah mà thành khẩn ăn năn sám hối về những gì mà các người đã làm từ lời nói và hành đồng, Thiên Sứ của Allah sẽ cầu xin Allah tha thứ tội lỗi cho các người. Họ liền quay mặt bỏ đi một cách ngạo mạn và bất cần đến Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(63,6,'63:6','Cho dù Ngươi - hỡi Thiên Sứ - có cầu xin tha thứ cho chúng hay không thì cũng như nhau bởi Allah sẽ không bao giờ tha thứ cho chúng. Chúng đã ngoan cố trong sự vô đức tin của chúng, chúng dám ngụy tạo đức tin ngay cả đối với Allah. Quả thật, Allah sẽ không chấp nhận đức tin của những kẻ phủ nhận Ngài, những kẻ đã nằm ngoài sự tuân phục Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(63,7,'63:7','Những kẻ Munafiq là những người đã nói: Các người đừng chi tiêu tài sản cho những người nghèo bên cạnh Thiên Sứ của Allah, những người Ả-rập ở xung quanh Madinah cho đến khi họ bỏ đi hết. Nhưng chúng lại không biết rằng các kho lương thực trong các tầng trời và trái đất đều thuộc về Allah, Ngài sẽ ban phát cho ai Ngài muốn và Ngài sẽ không cho ai Ngài muốn. Những kẻ ngụy tạo đức tin thực sự không hiểu biết chút nào.');
INSERT INTO "vn_mokhtasar" VALUES(63,8,'63:8','Tên đầu đảng của họ, ''Abdullah bin Ubai tuyên bố: “Nếu chúng ta trở về Madinah, thì những người quyền thế - ám chỉ hắn và bè lũ của hắn - sẽ đánh đuổi những kẻ hèn hạ như Muhammad và bằng hữu của Y”. Quả thật, quyền thế thuộc về một mình Allah duy nhất, thuộc về Thiên Sứ của Ngài và tín đồ có đức tin chứ không thuộc về tên ''Abdullah bin Ubai và bè lũ của hắn, nhưng những kẻ ngụy tạo đức tin không biết điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(63,9,'63:9','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Các ngươi chớ để tài sản, con cái của các ngươi làm cho các ngươi quên mất bổn phận lễ nguyện Salah và những điều bắt buộc khác trong Islam. Ai để cho con cái và tài sản làm cho họ xao lãng bổn phận mà Allah đã bắt buộc thì chắc chắn đó là đám người thua thiệt cho chính bản thân họ và gia đình của họ trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(63,10,'63:10','Các ngươi hãy chi dùng tài sản mà Allah ban cấp cho các ngươi vào con đường thiện tốt trước khi mỗi người các ngươi đối mặt với cái chết. Bằng không, mỗi người các ngươi sẽ nói trong sự nuối tiếc muộn màng: “Lạy Thượng Đế của bầy tôi, hãy trì hoãn cái chết cho bầy tôi, hãy cho bầy tôi thêm ít thời gian nữa, để bầy tôi có cơ hội chi dùng tài sản của bầy tôi cho con đường từ thiện mà Ngài hài lòng, và bầy tôi sẽ là những người ngoan đạo và kính sợ Ngài”.');
INSERT INTO "vn_mokhtasar" VALUES(63,11,'63:11','Allah không bao giờ trì hoãn một khoảnh khắc nào cho linh hồn một khi đã mãn hạn tức đã hết tuổi đời của nó trên thế gian. Và Allah là Đấng luôn am tường hết mọi điều tốt xấu mà các ngươi làm, và Ngài sẽ thưởng phạt công minh cho những việc làm đó của các ngươi. Ai làm tốt được tốt và ai làm xấu gặp xấu.');
INSERT INTO "vn_mokhtasar" VALUES(64,1,'64:1','Allah tự thanh lọc khỏi Ngài những thuộc tính không xứng đáng dành cho Ngài, tất cả vạn vật trong các tầng trời và vạn vật dưới đất đều tán dương ca ngợi Ngài bởi Ngài là Đấng chi phối vạn vật đáng được ca ngợi và tán dương, Ngài là Đấng Toàn, Năng không có ai (vật gì) có thể vượt qua quyền năng của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(64,2,'64:2','Allah là Đấng đã tạo hóa các ngươi - hỡi nhân loại -, dù vậy trong các ngươi có người không tin thì sẽ bị tống cổ vào Hỏa Ngục và có người tin tưởng sẽ được dẫn lối vào Thiên Đàng. Và Allah là Đấng nhìn thấy mọi điều tốt xấu các ngươi làm, và Ngài sẽ thưởng phạt cho những việc làm của các ngươi một cách công minh.');
INSERT INTO "vn_mokhtasar" VALUES(64,3,'64:3','Allah đã tạo hóa ra các tầng trời và trái đất có một ý nghĩa vô cùng vĩ đại, và Ngài đã tạo hóa ra các ngươi trong một hình thể đẹp nhất, nếu muốn Ngài đã tạo ra các ngươi dưới hình dạng xấu xí nhất; và vào Ngày Phục Sinh các ngươi sẽ được trở lại trình diện Ngài để Ngài xét xử và thưởng phạt những việc làm và hành động của các ngươi. Ai làm tốt sẽ được hưởng điều tốt và ai làm xấu sẽ nhận điều xấu.');
INSERT INTO "vn_mokhtasar" VALUES(64,4,'64:4','Allah biết rõ tất cả mọi điều trong trời đất và Ngài biết rõ những gì các ngươi – con người – che giấu và những gì các ngươi công khai. Quả thật, Allah là Đấng thấu hiểu và am tường tất cả mọi điều tốt xấu trong lòng của các ngươi, không có bất cứ điều gì có thể giấu giếm được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(64,5,'64:5','Chẳng lẽ các ngươi - hỡi những người đa thần - vẫn không hay biết gì về những cộng đồng không tin tưởng thời trước các ngươi sao, giống như cộng đồng của Nuh (Noah), ''Ad, Thamud và những cộng đồng khác, họ thực sự đã phải chịu một hậu quả tồi tệ trên thế gian vì sự vô đức tin của họ ở trần gian này, và Ngày Sau họ sẽ phải chịu một hình phạt vô cùng đau đớn? Không, nguồn tin thật sự đã đến với các ngươi, các ngươi hãy lấy đó làm bài học và hãy ăn năn sám hối với Allah trước khi hình phạt túm lấy các ngươi như đã diễn ra với họ.');
INSERT INTO "vn_mokhtasar" VALUES(64,6,'64:6','Họ bị hậu quả như thế trên thế gian và sẽ phải chịu sự trừng phạt vào Đời Sau là vì họ đã phủ nhận tất cả các lời mặc khải cùng với những bằng chứng màu nhiệm xác thực được các Thiên Sứ của Allah mang đến cho họ, họ nói với thái độ hống hách: “Chẳng lẽ một con người phàm tục giống như chúng ta lại hướng dẫn chúng thế nào là chân lý ư?”. Với ý nghĩ đó, họ đã phủ nhận Allah và phủ nhận thông điệp được các Thiên Sứ của Ngài mang đến từ nơi Ngài, họ đã chống đối và phản lại chân lý. Nhưng Allah vẫn là Đấng giàu có, vẫn là Đấng quyền uy đáng được ca tụng, sự lệch lạc và sai lầm của họ không gây tác hại và chẳng ảnh hưởng gì đến Ngài cả.');
INSERT INTO "vn_mokhtasar" VALUES(64,7,'64:7','Những kẻ vô đức tin nơi Allah đã khẳng định Allah sẽ không phục sinh họ sống lại từ cõi chết. Hãy nói - hỡi Thiên Sứ - với đám người phủ nhận sự Phục Sinh đó: Không đâu, Ta xin thề với Thượng Đế của Ta, chắc chắn các người sẽ được cho sống lại trong Ngày Tận Thế, rồi sau đó, Ngài sẽ cho các người biết tất cả những gì các người đã làm trên thế gian, và điều đó quả thật rất dễ dàng đối với Ngài, chính Ngài đã tạo hóa các người ở lần đầu tiên thì Ngài thừa năng lực phục sinh các người sau khi các người đã chết để thanh toán và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(64,8,'64:8','Do đó, các ngươi – hỡi nhân loại – hãy tin tưởng nơi Allah và Thiên Sứ của Ngài, hãy đi theo sự hướng dẫn của Kinh Qur’an mà TA đã ban xuống cho vị Thiên Sứ của TA. Và Allah luôn am tường và thấu rõ mọi điều các ngươi làm, không một hành động nào có thể che giấu được Ngài và Ngài sẽ thưởng phạt cho mỗi hành động và việc làm của các ngươi vào Ngày Phán xét.');
INSERT INTO "vn_mokhtasar" VALUES(64,9,'64:9','Ngươi - hỡi Thiên Sứ - hãy nhớ đến Ngày Triệu Tập, Ngày mà Allah sẽ triệu tập tất cả nhân loại từ thuở ban đầu cho đến thời cuối cùng của thế gian. Trong Ngày hôm đó sẽ có người thắng kẻ thua, những người có đức tin sẽ là những người chiến thắng và những kẻ vô đức tin, làm điều sai quấy sẽ là những kẻ thua thiệt và thất bại. Do đó, người nào tin tưởng Allah và thi hành theo lệnh của Ngài thì y sẽ được xóa hết tội lỗi và sẽ an lành đi vào Thiên Đàng với những Ngôi Vườn bên dưới có các dòng sông chảy, với những tòa lâu đài và cây cối, y sẽ sống trong đó đời đời, và sự vĩnh hằng trong Thiên Đàng chính là một sự thắng lợi vĩ đại không có một sự thắng lợi nào khác vĩ đại hơn.');
INSERT INTO "vn_mokhtasar" VALUES(64,10,'64:10','Trái lại, với những ai phủ nhận Allah và bác bỏ các lời mặc khải mà TA đã thiên khải cho Thiên Sứ của TA, những kẻ đó sẽ bị tống vào Hỏa Ngục đời đời kiếp kiếp. Và đó quả thật là một nơi quay về thật tồi tệ và thảm hại.');
INSERT INTO "vn_mokhtasar" VALUES(64,11,'64:11','Bất cứ tai họa nào xảy ra với con người về tài sản hoặc con cái đều là do Allah định đoạt và an bài. Người nào tin tưởng Allah cũng như sự Tiền Định thì Ngài sẽ hướng dẫn con tim của y luôn được thanh thản và hài lòng với định mệnh. Và Allah luôn hiểu biết hết mọi điều, không có gì giấu giếm được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(64,12,'64:12','Các ngươi hãy tuân lệnh Allah và tuân lệnh Thiên Sứ, còn nếu các ngươi quay mặt bỏ đi, không tuân thủ những gì Thiên Sứ của Ngài mang đến thì các ngươi gánh tội cho hành động quay lưng đó. Thiên Sứ của TA chỉ là Người truyền đạt các khẩu vụ theo mệnh lệnh của TA, bởi quả thật nhiệm vụ của Y chỉ là truyền đạt thông tin mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(64,13,'64:13','Allah là Đấng Thờ Phượng đích thực, không có Đấng Thờ Phượng nào xứng đáng được thờ phượng ngoài Ngài. Do đó, chỉ duy nhất nơi Ngài những người có đức tin tin tưởng phó thác tất cả mọi việc của họ.');
INSERT INTO "vn_mokhtasar" VALUES(64,14,'64:14','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Quả thật, trong số những người vợ và con cái của các ngươi có người là kẻ thù ngăn cản các ngươi trên con đường chính nghĩa của Allah và tuân lệnh Ngài. Do đó các ngươi hãy cảnh giác và đừng nghe theo họ, và nếu họ có vượt quá giới hạn với những hành động tội lỗi và xấu xa thì các ngươi hay bỏ qua cho họ, bởi quả thật Allah sẽ tha thứ tội lỗi cho các ngươi và nhân từ với các ngươi, và phần thưởng sẽ đến từ chính hành động đó.');
INSERT INTO "vn_mokhtasar" VALUES(64,15,'64:15','Chẳng qua tài sản và con cái của các ngươi chỉ là một sự thử thách dành cho các ngươi, đôi khi chúng đẩy các ngươi phải kiếm tiền bằng cách Haram (bị cấm) và bất tuân Allah, còn ở nơi Allah sẽ có những phần thưởng vô cùng to lớn đối với ai tuân thủ theo sự chỉ đạo của Ngài và chi tiêu tài sản đúng với những gì Ngài qui định, và phần thưởng vĩ đại đó chính là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(64,16,'64:16','Các ngươi hãy kính sợ Allah bằng cách thực hiện những điều Ngài ra lệnh và tránh xa những điều Ngài ngăn cấm theo khả năng của các ngươi trong bất kỳ mọi hoàn cảnh. Các ngươi hãy biết nghe lời và tuân lệnh theo Allah và Thiên Sứ của Ngài, và các ngươi hãy chi dùng tài sản mà Allah đã ban cấp cho các ngươi vào con đường mang lại điều tốt đẹp cho các ngươi. Và người nào giữ bản thân tránh khỏi sự keo kiệt thì đó là những người chiến thắng được mọi điều y muốn và tránh được mọi điều y lo sợ.');
INSERT INTO "vn_mokhtasar" VALUES(64,17,'64:17','Nếu các ngươi cho Allah mượn một cái mượn tốt đẹp bằng việc chi dùng tài sản của các ngươi cho con đường chính đạo của Allah thì Ngài sẽ gia tăng ân phước gấp bội cho những gì các ngươi đã chi dùng, cứ mỗi ân phước được nhân lên từ mười đến bảy trăm lần hoặc nhiều hơn thế, Ngài sẽ tha thứ tội lỗi cho các ngươi, bởi Allah là Đấng luôn ghi công làm ít được hưởng nhiều và luôn chịu đựng không vội trừng phạt kẻ phạm tội.');
INSERT INTO "vn_mokhtasar" VALUES(64,18,'64:18','Allah, thật Vinh Quang thay Ngài! Ngài biết hết tất cả mọi điều vô hình, Ngài biết rõ những điều hữu hình, không có bất cứ điều gì có thể che giấu được Ngài, Ngài là Đấng Toàn Năng, không có gì có thể vượt qua quyền năng của Ngài, và Ngài là Đấng sáng suốt và khôn ngoan trong mọi lời nói và hành động.');
INSERT INTO "vn_mokhtasar" VALUES(65,1,'65:1','Này hỡi Nabi, khi nào Ngươi hoặc ai đó trong tín đồ của Ngươi muốn ly dị vợ của mình thì y hãy ly dị cô ta ngay thời kỳ đầu của ‘Iddah (tức trong thời gian người vợ có thể trạng sạch (không có kinh) và không ân ái. Các ngươi hãy đảm bảo thời gian ‘Iddah này khi thực hiện ly dị để biết rõ thời gian được phép quay lại nếu các ngươi muốn quay lại với các bà vợ của các ngươi, các ngươi hãy kính sợ Allah, Thượng Đế của các ngươi mà tuân thủ đúng giáo luật của Ngài, các ngươi chớ xua đuổi các bà vợ của các ngươi ra khỏi nhà nơi họ đang ở và các bà vợ cũng không tự ý rời khỏi nhà cho đến khi đã hết thời gian ‘Iddah ngoại trừ họ đã làm một điều gì đó vượt quá giới hạn của Allah như Zina chẳng hạn. Đó là giáo luật của Allah được Ngài quy định cho đám bầy tôi của Ngài. Do đó, người nào phạm giới luật của Allah là đã bất công với chính bản thân y và y đang đưa bản thân mình vào con đường hủy diệt. Này hỡi những ai muốn ly dị, e rằng sau khi ly dị, Allah khiến các ngươi muốn quay lại thì ‘Iddah là thời gian cho các ngươi cơ hội đó.');
INSERT INTO "vn_mokhtasar" VALUES(65,2,'65:2','Như vậy, khi thời gian ‘Iddah gần hết thì hoặc là các ngươi quay lại với các bà vợ nếu muốn tiếp tục sống chung hoặc là các ngươi quyết không ở lại cùng vợ thì để cho thời gian ''Iddah hoàn toàn chấm dứt. Nếu các ngươi quay lại với họ thì hãy đối xử tử tế với họ và chu cấp cho họ đàng hoàng còn nếu các ngươi quyết định chia tay họ thì các ngươi hãy chia tay họ một cách đảm bảo quyền lợi của họ; và khi các ngươi ở lại hay chia tay thì các ngươi hãy tìm hai người đàn ông làm chứng cho các ngươi và những ai làm chứng hãy làm chứng một cách trung thực vì Allah. Đó là mệnh lệnh của Allah dành cho những ai tin tưởng Ngài và tin tưởng vào Đời Sau. Và người nào kính sợ Allah luôn làm theo những gì Ngài chỉ bảo và tránh xa những gì Ngài cấm đoán thì Ngài sẽ luôn mở một lối thoát cho y.');
INSERT INTO "vn_mokhtasar" VALUES(65,3,'65:3','Và sẽ tạo điều kiện thuận lợi để y tìm thấy thiên lộc của Ngài một cách dễ dàng mà y không ngờ tới được; và người nào dựa dẫm vào Allah thì một mình Ngài đã đủ bảo hộ cho y trong mọi vụ việc trong mọi hoàn cảnh. Quả thật, Allah sẽ thực hiện mục đích của Ngài, Ngài không bỏ lỡ bất cứ điều gì cũng không có điều gì khó khăn với Ngài và Ngài là Đấng đã định mức lượng cho mọi vật và mọi sự việc dù dễ dàng hay khó khăn và một trong hai điều này không ở mãi với con người.');
INSERT INTO "vn_mokhtasar" VALUES(65,4,'65:4','Và người phụ nữ nào đã mãn kinh, và những người chưa có kinh vì còn nhỏ thì thời gian ‘Iddah của họ là ba tháng trọn vẹn, riêng đối với người phụ nữ mang thai thì thời gian ‘Iddah của họ cho việc ly hôn hoặc chồng chết (muốn tái giá) sẽ kéo dài cho đến khi hạ sanh. Và người nào kính sợ Allah luôn thực hiện theo Chỉ đạo của Ngài thì Ngài sẽ tạo điều kiện thuận lợi và dễ dàng trong mọi vụ việc của y trên Đời này và cả Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(65,5,'65:5','Những gì được nói về vấn đề ly dị, ly thân, và hạn định ‘Iddah là mệnh lệnh của Allah được Ngài ban xuống làm Chỉ đạo cho các ngươi – hỡi những người có đức tin – để các ngươi biết rõ. Và người nào kính sợ Allah mà tránh xa những điều nghịch đạo và luôn thực hiện đúng bổn phận được quy định thì Ngài sẽ bôi xóa tội lỗi cho y và sẽ ban thưởng cho y ở Đời Sau, đó là Ngài sẽ thu nhận y vào Thiên Đàng vĩ đại của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(65,6,'65:6','Các ngươi hãy để cho các bà vợ các ngươi ở chỗ nào mà các ngươi đang ở một cách tử tế, đàng hoàng tùy theo điều kiện kinh tế của các ngươi trong thời gian ‘Iddah, các ngươi không được gây khó khăn để chèn ép họ tự ý bỏ chỗ ở. Nếu họ là những người đang mang thai thì các ngươi hãy chu cấp đầy đủ cho họ khi vẫn đang trong thời gian ‘Iddah cho đến khi họ hạ sanh. Nếu họ giúp các ngươi cho con bú thì các ngươi hãy trả tiền thù lao cho họ và hãy trao đổi bàn bạc tham khảo ý kiến lẫn nhau một cách có tình có nghĩa thật hài hòa, còn trường hợp người chồng keo kiệt không chịu trả tiền công cho vợ để cho con của anh ta bú thì anh ta được quyền tìm người phụ nữ khác để cho con bú.');
INSERT INTO "vn_mokhtasar" VALUES(65,7,'65:7','Người chồng hãy chu cấp cho vợ con của mình theo điều kiện kinh tế của y trong khoảng thời gian li thân, còn người nào eo hẹp thì cứ chi tiêu theo hoàn cảnh và khả năng của mình, bởi Allah không bắt ai gánh vác quá sức, Ngài không bắt người nghèo phải chi tiêu như người giàu. Quả thật, sau những khó khăn và khốn cùng Allah sẽ làm cho các ngươi giàu có và tốt hơn.');
INSERT INTO "vn_mokhtasar" VALUES(65,8,'65:8','Và quả thật, có rất nhiều thị trấn mà người dân của nó đã làm trái lệnh của Allah và Thiên Sứ của Ngài và đã bị TA trừng phạt bởi việc làm tội lỗi của họ, TA đã trừng phạt tất cả bọn họ bằng một sự trừng phạt khủng khiếp ở trần gian và Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(65,9,'65:9','Bởi thế, chúng đã nếm hậu quả tai hại cho những hành động sai trái, và kết cuộc cho những hành động sai trái đó là sự mất mát và thua thiệt ngay ở trần gian và cả Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(65,10,'65:10','Allah đã chuẩn bị một sự trừng phạt thật nặng, các ngươi hãy kính sợ Allah - hỡi những người có đầu óc biết suy nghĩ và tin tưởng nơi Thiên Sứ của Ngài - mà tuân thủ mọi mệnh lệnh của Ngài để các ngươi tránh được hình phạt khủng khiếp giống như hậu quả mà những kẻ trước đây đã được Allah kể về họ khi họ quyết nghịch lại mệnh lệnh của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(65,11,'65:11','Lời nhắc nhở này đã được vị Thiên Sứ của Ngài đọc cho các ngươi nghe qua những câu kinh trình bày rõ ràng để phân biệt giữa điều chân lý và điều ngụy tạo, hồng giúp giải thoát cho những người tin tưởng Allah, những người tín nhiệm Thiên Sứ của Ngài, và những người làm việc thiện tốt từ nơi tăm tối của bóng đêm lầm lạc ra ngoài ánh sáng của đức tin Iman và chân lý. Do đó, người nào tin tưởng Allah và làm việc thiện tốt thì sẽ được Ngài cho vào Thiên Đàng với các ngôi vườn bên dưới có các dòng sông chảy, họ sẽ sống trong đó đời đời và được Allah ban cho thiên lộc tốt nhất không bao giờ dứt.');
INSERT INTO "vn_mokhtasar" VALUES(65,12,'65:12','Allah là Đấng duy nhất tạo ra bảy tầng trời và bảy trái đất giống như việc đã tạo ra bảy tầng trời, Allah tự ban hành mệnh lệnh một cách phổ biến và hợp pháp chúng giữa các tầng trời và trái đất, hi vọng các ngươi nhận biết được rằng Allah có quyền năng trên vạn vật, không có điều gì có thể khiến Ngài bất lực, và quả thật Allah, Đấng Vinh Quang bao trùm mọi sự việc và vạn vật bằng kiến thức của Ngài, không việc gì dù ở trên trời hay ở dưới đất có thể che giấu được Allah.');
INSERT INTO "vn_mokhtasar" VALUES(66,1,'66:1','Này hỡi Thiên Sứ, sao Ngươi lại tự cấm bản thân Ngươi dùng những gì được Allah cho phép vì muốn làm vừa lòng các bà vợ của Ngươi, chỉ vì lòng ghen tuông của Mariyah? Và Allah sẽ tha thứ cho Ngươi và yêu thương Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(66,2,'66:2','Quả thật Allah đã ban hành điều luật để xóa bỏ lời thề nguyện bằng cách thực hiện Kaffa-rah (đó là nuôi ăn mười người nghèo hoặc cung cấp quần áo cho họ, hoặc dùng tiền chuộc một người nô lệ, hoặc nhịn chay ba ngày). Và Allah là Đấng bảo hộ và giúp đỡ các ngươi, và Ngài biết rõ điều gì sẽ cải thiện các ngươi nên mới sắc lệnh cho các ngươi, bởi Ngài là Đấng Sáng suốt và khôn ngoan trong lời nói và hành động.');
INSERT INTO "vn_mokhtasar" VALUES(66,3,'66:3','Và khi Nabi tâm sự thầm kín với vợ Người, Hafsah, việc Người sẽ không gần gũi bà Mariyah nữa thì Hafsah đã nói lại với ‘A-ishah. Allah đã tiết lộ cho Người biết và Người xác nhận một phần và im lặng một phần khác, rồi khi Người nói cho bà thì bà hỏi: “Ai đã cho Mình biết điều này?” Người nói: Allah, Đấng Toàn Năng và Am Tường đã cho Ta biết.');
INSERT INTO "vn_mokhtasar" VALUES(66,4,'66:4','Bắc buộc hai ngươi (Hafsah và ‘A-ishah) phải quay về sám hối cùng với Allah về những gì mà hai ngươi đã sai phạm. Nếu làm được như vậy thì coi như tấm lòng của hai ngươi biết hạ mình sửa lỗi với Thiên Sứ của Allah, còn nếu hai ngươi vẫn con ngoan cố tiếp tay nhau chống lại Y thì Allah là Đấng bảo hộ và giúp đỡ Y và sau đó là Đại Thiên Thần Jibril, những người có đức tin ngoan đạo cùng với các thiên thần sẽ là những vị bảo hộ và giúp đỡ chống lại những ai làm tổn thương Y.');
INSERT INTO "vn_mokhtasar" VALUES(66,5,'66:5','Nếu Y ly dị các ngươi thì e rằng Thượng Đế của Y, Đấng Vinh Quang sẽ đổi lại cho Y những người vợ khác tốt đẹp hơn, họ sẽ là những người phụ nữ tin tưởng Allah và Thiên Sứ của Ngài, họ luôn vâng lệnh Ngài, họ sẽ là những người ngoan đạo, họ sẽ nhịn chay, một số họ đã có một đời chồng và một số vẫn còn trinh nguyên, nhưng Y đã không ly dị các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(66,6,'66:6','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Các ngươi hãy trông coi và gìn giữ bản thân các ngươi cũng như người thân của các ngươi luôn chấp hành theo chỉ đạo của Allah để có thể tránh khỏi sự trừng phạt nơi Hỏa Ngục khủng khiếp, nơi mà lửa của nó được đốt lên bởi con người và đá, và Hỏa Ngục sẽ được các vị Thiên Thần lạnh lùng, nghiêm nghị canh gác. Các vị Thiên Thần này không bao giờ chống lại mệnh lệnh của Allah, họ tuyệt đối thi hành theo lệnh truyền của Ngài, tuyệt đối không dám trì trệ.');
INSERT INTO "vn_mokhtasar" VALUES(66,7,'66:7','Những người không có đức tin vào Ngày Sau sẽ được bảo rằng: Các ngươi chớ hầu chạy tội trong ngày hôm nay, các ngươi sẽ bị trừng trị thích đáng về những việc tội lỗi mà cá ngươi đã làm trên thế gian vì các ngươi đã phủ nhận Allah và sứ mạng của các Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(66,8,'66:8','Này hỡi những người tin tưởng Allah và luôn chấp hành theo giáo luật của Ngài! Các ngươi hãy quay về sám hối với Allah về những tội lỗi và trái lệnh của các ngươi, mong rằng Ngài sẽ xóa tội lỗi của các ngươi và thu nhận các ngươi vào Thiên Đàng của Ngài, nơi có những ngôi vườn bên dưới có các dòng sông chảy. Vào Ngày mà sẽ Ngài không trừng phạt Nabi và những người có đức tin cùng với Người bằng Hỏa Ngục, ánh sáng của họ sẽ tỏa sáng phía đằng trước và ở phía bên phải của họ, họ sẽ thưa: Lạy Thượng Đế, xin Ngài hãy hoàn thiện ánh sáng của bầy tôi để bầy tôi qua khỏi chiếc cầu Sirat mà đến được với Thiên Đàng, xin Ngài hãy tha thứ cho bầy tôi bởi quả thật Ngài là Đấng có quyền năng trên mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(66,9,'66:9','Này hỡi Thiên Sứ, Ngươi hãy chiến đấu chống lại những kẻ phủ nhận đức tin một cách công khai, hãy giết chúng bằng lưỡi gươm, và hãy chiến đấu chống lại những kẻ phủ nhận đức tin một cách thầm kín, đó là những kẻ ngụy tạo đức tin, hãy thật nghiêm khắc với chúng đừng nương tay với chúng trong lúc Ngươi chiến đấu. Và quả thật, những kẻ đó, chúng chắc chắn sẽ bị đày vào trong Hỏa Ngục, một nơi tồi tệ nhất mà chúng phải quay về.');
INSERT INTO "vn_mokhtasar" VALUES(66,10,'66:10','Allah đã trình bày một thí dụ của những người vô đức tin vào Allah và các Thiên Sứ của Ngài - mặc dù họ có mối quan hệ thân thiết với người có đức tin nhưng không giúp ích gì được họ - về hai người vợ của hai vị Nabi Nuh và Nabi Lut. Hai người phụ nữ này là hai người vợ của hai vị Nô Lệ đức hạnh của Ngài nhưng hai người đó vẫn là những người vô đức tin chống đối lại con đường của Allah, cả hai ra sức giúp đỡ dân chúng vô đức tin của mình, cho nên mối quan hệ của hai người họ với Nuh và Lut chẳng thể giúp ích được gì. Và họ được bảo: Hai ngươi hãy cút vào Hỏa Ngục cùng với những kẻ hư đốn, vô đức tin đi.');
INSERT INTO "vn_mokhtasar" VALUES(66,11,'66:11','Và Allah đưa ra một thí dụ khác về những người có đức tin vào Allah và Thiên Sứ của Ngài, luôn sống và thi hành đúng theo sự Chỉ Đạo của Allah mặc dù phải sống cùng với những kẻ vô đức tin. Đó là thí dụ về người vợ của Fir’awn (Pharaon), bà ta là người phụ nữ đầy đức tin sống dưới quyền của một người vô đức tin và tàn bạo, bà ta vẫn giữ vững đức tin và luôn cầu nguyện Allah: Lạy Thượng Đế, xin Ngài hãy xây cho bề tôi một ngôi nhà nơi Thiên Đàng, xin Ngài giải thoát bề tôi khỏi quyền hành của Fir’awn và khỏi những hành vi tội lỗi của hắn, xin Ngài giải thoát bề tôi khỏi đám người lầm lạc và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(66,12,'66:12','Và Allah đưa ra một hình ảnh thí dụ về Maryam, con gái của ''Imran, bà là người phụ nữ đã giữ gìn trinh tiết, không bao giờ phạm vào chuyện dâm loạn. Do đó, Allah đã sai Đại Thiên Thần Jibril thổi vào người của bà và cái thổi đó đã đến nơi dạ con của bà và thế là bà mang thai Nabi Ysa mà không cần đến người cha. Bà đã tin tưởng những lời mặc khải của Thượng Đế của bà, bà luôn thực hiện theo giáo luật mà Ngài đã sắc lệnh cho bà, và quả thật bà là một người phụ nữ ngoan đạo và đức hạnh.');
INSERT INTO "vn_mokhtasar" VALUES(67,1,'67:1','Allah là Đấng ban phúc lành, Đấng Vĩ Đại, Đấng mà mọi vương quyền thống trị và chi phối vũ trụ, vạn vật đều nằm trong tay một mình Ngài; và Ngài toàn năng trên tất cả mọi thứ, mọi sự việc, không có bất cứ điều gì làm Ngài bất lực.');
INSERT INTO "vn_mokhtasar" VALUES(67,2,'67:2','Allah là Đấng đã tạo ra cái chết cũng như sự sống để thử thách các ngươi - hỡi nhân loại - để xem ai trong các ngươi hành động tốt nhất (ai làm đúng và thành tâm vì Ngài nhất); và Ngài là Đấng Quyền Lực và Toàn Năng không ai (vật gì) có thể vượt qua Ngài, và Ngài là Đấng Hằng Tha Thứ tội lỗi cho những bề tôi nào quay về sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(67,3,'67:3','Allah là Đấng đã tạo ra bảy tầng trời, tầng này bên trên tầng kia nhưng giữa chúng không có sự tiếp xúc. Ngươi - hỡi người quan sát - sẽ không nhìn thấy bất cứ khiếm khuyết và lỗ hổng nào trong sự tạo hóa của Ngài. Và nếu ngươi quan sát lại một lần nữa hầu để tìm ra lỗ hổng và lỗi thì chắc chắn ngươi cũng sẽ không bao giờ thấy bất cứ lỗi và lỗ hổng nào, ngược lại, ngươi sẽ chỉ thấy một sự tạo hóa hoàn hảo và tinh vi.');
INSERT INTO "vn_mokhtasar" VALUES(67,4,'67:4','Rồi nếu ngươi cứ nhìn lần này hết lần khác để quan sát, ngươi càng nhìn thì cái nhìn của ngươi càng trở nên mờ nhạt đi và nó sẽ trở nên mệt mỏi và bơ phờ.');
INSERT INTO "vn_mokhtasar" VALUES(67,5,'67:5','Quả thật, TA (Allah) đã trang hoàng cho tầng trời hạ giới (tầng trời gần với trái đất nhất) những ngôi sao lấp lánh. TA làm cho những ngôi sao đó thành những hỏa tiễn để ném những tên Shaytan tiếp cận nghe trộm thông tin trên trời; và TA đã chuẩn bị cho chúng một sự trừng phạt trong Ngục Lửa vào cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(67,6,'67:6','Và những kẻ vô đức tin nơi Thượng Đế của họ, vào Ngày Sau, họ cũng sẽ bị trừng phạt nơi Hỏa Ngục, một nơi quay về tồi tệ nhất mà họ phải quay về.');
INSERT INTO "vn_mokhtasar" VALUES(67,7,'67:7','Khi họ bị ném vào trong Hỏa Ngục, họ sẽ nghe được tiếng Lửa của Nó rít lên dữ dội và ghê sợ; và Lửa của Nó sôi giống như sự sôi của chiếc nồi hơi.');
INSERT INTO "vn_mokhtasar" VALUES(67,8,'67:8','Hỏa Ngục gần như muốn nổ tung ra vì tức giận cho những ai bị ném vào trong Nó. Mỗi lần một nhóm người vô đức tin bị ném vào, các vị Thiên Thần quản lý và trông coi Hỏa Ngục hỏi họ mang ý quở trách: Chẳng lẽ trên cõi trần không có một vị Thiên Sứ nào đến cảnh báo các ngươi về sự trừng phạt của Allah hay sao?!');
INSERT INTO "vn_mokhtasar" VALUES(67,9,'67:9','Những kẻ vô đức tin sẽ đáp: "Vâng, có. Quả thật, đã có một vị Thiên Sứ đến cảnh báo chúng tôi về sự trừng phạt của Allah nhưng chúng tôi đã phủ nhận y, chúng tôi nói với y: Allah chẳng ban xuống bất cứ điều Mặc Khải nào, các vị hỡi những Thiên Sứ chỉ đang lầm lạc khỏi điều chân lý."');
INSERT INTO "vn_mokhtasar" VALUES(67,10,'67:10','Những kẻ vô đức tin sẽ nói: Phải chi chúng tôi chịu nghe những lời khuyên hữu ích và chịu suy nghĩ để phân biệt giữa điều chân lý và sai trái thì chúng tôi đâu phải trở thành những người bạn của Hỏa Ngục. Đáng lẽ ra chúng tôi nên tin nơi các vị Thiên Sứ, chúng tôi nên tin những điều họ mang đến thì chúng tôi sẽ là những người bạn của Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(67,11,'67:11','Họ đã thừa nhận bản thân họ tội lỗi bởi sự vô đức tin và phủ nhận. Bởi thế, họ bị đày vào Hỏa Ngục là thích đáng, và những cư dân của Hỏa Ngục đã ở quá xa khỏi (lòng Thương Xót của Allah).');
INSERT INTO "vn_mokhtasar" VALUES(67,12,'67:12','Quả thật những người kính sợ Allah, Thượng Đế của họ lúc họ ở một mình, sẽ được tha thứ tội lỗi và được một phần thưởng vô cùng to lớn, đó là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(67,13,'67:13','Hỡi nhân loại, dù các ngươi có che giấu hoặc công khai lời nói của các ngươi thì Allah vẫn luôn biết rõ, bởi quả thật Ngài là Đấng hằng biết những gì nằm trong trái tim các bề tôi của Ngài, không có bất cứ điều gì có thể giấu giếm được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(67,14,'67:14','Chẳng lẽ Đấng đã tạo hóa những thứ còn bí ẩn hơn những điều bí mật lại không biết trong khi Ngài tinh tường và thông toàn tất cả mọi sự việc của đám bề tôi của Ngài, chẳng có một điều gì nằm ngoài sự hiểu biết và tầm kiểm soát của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(67,15,'67:15','Ngài (Allah) là Đấng đã tạo ra cho các ngươi trái đất thành một nơi dễ dàng và thuận tiện cho các ngươi cư ngụ. Bởi thế, các ngươi hãy đi lại trên các nẻo đường của nó, hãy ăn từ nguồn bổng lộc mà Ngài đã ban cấp cho các ngươi, và rồi đây các ngươi sẽ phải trở về trình diện Ngài để Ngài xét xử và thưởng phạt các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(67,16,'67:16','Chẳng lẽ các ngươi cảm thấy mình an toàn trước Allah, Đấng ở trên trời khi Ngài làm cho đất dưới chân các ngươi nứt ra nuốt các ngươi, giống như Ngài đã làm cho nó nứt ra nuốt Qarun, sau khi Ngài làm cho nó dễ dàng và an bình cho các ngươi cư ngụ ư?');
INSERT INTO "vn_mokhtasar" VALUES(67,17,'67:17','Hay chẳng lẽ các ngươi cảm thấy an toàn trước Allah, Đấng ở trên trời khi Ngài gửi cơn lốc xoáy mang theo đá từ trên trời xuống giống như Ngài đã gửi nó xuống trừng phạt cộng đồng của Lut ư?! Rồi đây các ngươi sẽ biết khi các ngươi tận mắt chứng kiến sự trừng phạt của TA được cảnh báo cho các ngươi, nhưng các ngươi chắc chắn không bao giờ có thể quay đầu sau khi đã tận mắt chứng kiến sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(67,18,'67:18','Và quả thật, các cộng đồng trước những kẻ thờ đa thần này đã phủ nhận Lời Mặc Khải của Allah và TA đã giáng xuống họ sự trừng phạt bởi những điều mà chúng ngoan cố trên sự vô đức tin và phủ nhận của chúng. Thế nên hãy xem sự trừng phạt của TA đối với họ như thế nào?! Quả thật, sự trừng phạt đó của TA rất khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(67,19,'67:19','Chẳng lẽ những kẻ phủ nhận này không quan sát thấy những con chim bay bên trên chúng trong không trung với những đôi cánh đôi lúc dang ra và đôi lúc xếp lại ư? Quả thật, ngoài Allah ra không một ai có thể giữ được chúng lơ lửng trên không mà không bị rơi xuống đất. Quả thật, Ngài là Đấng nhìn thấy tất cả mọi thứ, không có thứ gì có thể ẩn khuất khỏi tầm nhìn của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(67,20,'67:20','Hỡi những kẻ vô đức tin, chẳng có một đạo quân nào của các ngươi có thể giúp các ngươi ngăn lại sự trừng phạt của Allah nếu như Ngài đã muốn trừng phạt các ngươi. Quả thật, những kẻ vô đức tin chỉ đang bị đánh lừa bởi Shaytan.');
INSERT INTO "vn_mokhtasar" VALUES(67,21,'67:21','Không ai có thể ban cấp bổng lộc cho các ngươi nếu như Allah không cho nguồn bổng lộc của Ngài đến được với các ngươi. Không, những kẻ vô đức tin vẫn hiểu và biết được điều này nhưng họ vẫn cứ ngoan cố và chạy trốn điều chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(67,22,'67:22','Thế, một người gục mặt bước đi với bộ dạng đáng khinh - người đa thần - được hướng dẫn đúng đường hay người có đức tin bước đi an nhiên trên con đường ngay chính mới được hướng dẫn?!');
INSERT INTO "vn_mokhtasar" VALUES(67,23,'67:23','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần: Allah là Đấng đã tạo cho các ngươi thính giác để các ngươi nghe, thị giác để các người nhìn và quan sát, và trái tim để các ngươi suy nghĩ và nhận thức nhưng các ngươi rất ít tạ ơn Ngài về các ân huệ mà Ngài đã ban cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(67,24,'67:24','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần: Allah là Đấng đã tạo ra các ngươi và làm cho các ngươi đông đảo trên trái đất, chứ các thần linh bục tượng của các ngươi không tạo ra bất cứ thứ gì; và rồi đây vào Ngày Phục Sinh, các ngươi sẽ được triệu tập trình diện trước Ngài để xét xử và thưởng phạt các ngươi, chỉ một mình Ngài có quyền hạn đó còn các thần linh bục tượng của các ngươi thì không, bởi thế, các ngươi hãy sợ Ngài và thờ phượng một mình Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(67,25,'67:25','Những kẻ phủ nhận nói về sự Phục Sinh mang ý phủ nhận: Này Muhammad và những người bạn của Ngươi, bao giờ lời hứa cho việc Phục Sinh mà các ngươi hứa hẹn với bọn ta sẽ đến nếu các ngươi là những người nói thật?!');
INSERT INTO "vn_mokhtasar" VALUES(67,26,'67:26','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với bọn chúng: Quả thật, kiến thức về giờ khắc Tận Thế là ở nơi Allah, chỉ một mình Ngài mới biết rõ nó; còn Ta thật ra chỉ là một người cảnh báo, Ta chỉ có nhiệm vụ cảnh báo một cách rõ ràng.');
INSERT INTO "vn_mokhtasar" VALUES(67,27,'67:27','Bởi vậy, khi lời hứa được thực hiện, những kẻ vô đức tin tận mắt nhìn thấy sự trừng phạt đến gần, đó là vào Ngày Phục Sinh, thì gương mặt của họ biến sắc thành sậm tối vì lo sợ; và có lời bảo họ: "Đây chính là điều mà các ngươi hằng mong đợi trên thế gian và hối thúc nó đến."');
INSERT INTO "vn_mokhtasar" VALUES(67,28,'67:28','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần ngoan cố này: Các ngươi hãy cho ta biết nếu Allah làm cho ta và những người theo ta chết đi để trở về với Ngài thì ai sẽ bảo vệ những kẻ vô đức tin thoát khỏi sự trừng phạt?! Chắc chắn không bao giờ có bất kỳ ai có thể làm được điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(67,29,'67:29','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần này: Allah là Đấng Độ Lượng, Đấng kêu gọi các ngươi thờ phượng một mình Ngài, chúng tôi có đức tin nơi Ngài và chúng tôi luôn phó thác cho Ngài mọi sự việc của chúng tôi. Rồi đây, các ngươi sẽ sớm biết ai mới là những kẻ lầm lạc rõ rệt và ai mới là những người được hướng dẫn trên chính đạo.');
INSERT INTO "vn_mokhtasar" VALUES(67,30,'67:30','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những người thờ đa thần này: Các ngươi hãy cho Ta biết nếu vào một buổi sáng nào đó, nguồn nước mà các ngươi uống không còn trên trái đất nữa thì ai có thể mang nguồn nước đến cho các ngươi?! Chắc chắn là không có ai ngoài Allah cả.');
INSERT INTO "vn_mokhtasar" VALUES(68,1,'68:1','Nun (Một trong các chữ cái tiếng Ả Rập, ý nghĩa của nó đã được giảng giải ở phần mở đầu của chương Al-Baqarah). Allah thề bởi cây viết và thề bởi những gì mà nhân loại ghi chép với những cây viết của họ.');
INSERT INTO "vn_mokhtasar" VALUES(68,2,'68:2','Hỡi Thiên Sứ Muhammad, Ngươi chắc chắn không phải là một kẻ điên qua những hồng ân mà Allah ban cho Ngươi từ Sứ Mạng Nabi. Ngươi không phải là một người điên mà những kẻ thờ đa thần đã gán cho Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(68,3,'68:3','Quả thật, Ngươi - hỡi Thiên Sứ Muhammad - chắc chắn sẽ được ban thưởng một phần thưởng không bao giờ hết bởi những gì mà Ngươi đã chịu đựng trong sứ mạng rao truyền Bức Thông Điệp của Allah cho nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(68,4,'68:4','Quả thật, Ngươi - hỡi Thiên Sứ Muhammad - được phú cho những phẩm chất đạo đức vĩ đại mà Qur''an mang đến. Bởi vậy, Ngươi là một người mang những phẩm chất đạo đức tốt đẹp và hoàn hảo nhất.');
INSERT INTO "vn_mokhtasar" VALUES(68,5,'68:5','Rồi đây, Ngươi - hỡi Thiên Sứ Muhammad - sẽ sớm thấy và những kẻ phủ nhận này cũng sẽ sớm thấy.');
INSERT INTO "vn_mokhtasar" VALUES(68,6,'68:6','Khi chân lý được phơi bày thì sẽ rõ ai trong các ngươi mới là kẻ mắc chứng điên khùng.');
INSERT INTO "vn_mokhtasar" VALUES(68,7,'68:7','Hỡi Thiên Sứ Muhammad, quả thật Thượng Đế của Ngươi biết rất rõ ai là kẻ lạc khỏi con đường Chính Đạo của Ngài và Ngài hoàn toàn biết rõ ai là người được hướng dẫn đúng với con đường đó. Bởi thế, Ngài biết rõ chính họ - những kẻ thờ đa thần - là những kẻ lạc khỏi con đường Chính Đạo còn Ngươi - Thiên Sứ Muhammad - là người được hướng dẫn đến với con đường Chính Đạo.');
INSERT INTO "vn_mokhtasar" VALUES(68,8,'68:8','Thế nên, Ngươi - hỡi Thiên Sứ Muhammad - chớ đi theo những kẻ phủ nhận những gì Ngươi mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(68,9,'68:9','Chúng - những kẻ thờ đa thần - mong muốn rằng nếu Ngươi nhân nhượng để đạt đến một sự thỏa thuận về tôn giáo thì họ cũng sẽ nhân nhượng với Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(68,10,'68:10','Và Ngươi - Thiên Sứ Muhammad - chớ nghe lời của từng tên thề thốt giả dối và đáng khinh.');
INSERT INTO "vn_mokhtasar" VALUES(68,11,'68:11','Kẻ luôn nói xấu sau lưng người khác và chuyên tới lui mách lẻo giữa mọi người để gây sự xung đột.');
INSERT INTO "vn_mokhtasar" VALUES(68,12,'68:12','Kẻ chuyên ngăn cản điều thiện tốt, xâm phạm đến tài sản, danh dự và tính mạng của thiên hạ, và làm nhiều tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(68,13,'68:13','Kẻ xấu xa quá mức và còn hạ lưu thấp kém.');
INSERT INTO "vn_mokhtasar" VALUES(68,14,'68:14','Vì y giàu có và nhiều con cái nên y mới tự cao tự đại cự tuyệt đức tin nơi Allah và Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(68,15,'68:15','Khi mà những Lời Phán của TA (Allah) được đọc lên thì y nói rằng đây chỉ là những câu chuyện cổ tích của người xưa.');
INSERT INTO "vn_mokhtasar" VALUES(68,16,'68:16','TA (Allah) sẽ đóng con dấu lên cái mõm của y (bao gồm mũi, miệng và cằm).');
INSERT INTO "vn_mokhtasar" VALUES(68,17,'68:17','Quả thật, TA (Allah) đã thử thách những kẻ thờ đa thần này bằng hạn hán và nạn đói giống như TA đã thử thách những chủ nhân của một ngôi vườn khi chúng thề rằng chắc chắn chúng sẽ hái hết trái quả của ngôi vườn chỉ trong một buổi sáng với ý định không bố thí cho những người nghèo.');
INSERT INTO "vn_mokhtasar" VALUES(68,18,'68:18','Những chủ nhân của ngôi vườn đã không nói "Insha-Allah" có nghĩa là "Nếu Allah muốn".');
INSERT INTO "vn_mokhtasar" VALUES(68,19,'68:19','Bởi thế, Allah đã gửi lửa đến thiêu rụi ngôi vườn trong lúc những người chủ khu vườn đang say giấc không thể kịp thời đẩy lùi đàm cháy (và cho dù họ có đối mặt trực diện thì họ cũng không thể chống cự lại được một khi Allah đã muốn).');
INSERT INTO "vn_mokhtasar" VALUES(68,20,'68:20','Vậy là sáng ra cả ngôi vườn biến thành một màu đen giống như bóng tối của màn đêm.');
INSERT INTO "vn_mokhtasar" VALUES(68,21,'68:21','Rồi khi trời sáng, những người chủ khu vườn đi gọi nhau thức dậy.');
INSERT INTO "vn_mokhtasar" VALUES(68,22,'68:22','Họ bảo nhau lúc kêu gọi nhau thức dậy: Các anh phải đi ra vườn cho sớm trước khi những người nghèo kéo đến nếu các anh thực sự muốn hái trái.');
INSERT INTO "vn_mokhtasar" VALUES(68,23,'68:23','Những người chủ ngôi vườn nhanh chân đi ra vườn, họ vừa đi vừa nói khẽ với nhau:');
INSERT INTO "vn_mokhtasar" VALUES(68,24,'68:24','Họ bảo nhau: "Hôm nay, các anh chớ để bất kỳ người nghèo nào vào vườn gặp các anh (ở trong đó trong lúc các anh đang hái trái kẻo họ sẽ xin và các anh sẽ khó từ chối).');
INSERT INTO "vn_mokhtasar" VALUES(68,25,'68:25','Họ nhanh chân ra vườn rất sớm trong ý định không để những người nghèo đến xin xỏ.');
INSERT INTO "vn_mokhtasar" VALUES(68,26,'68:26','Khi những người chủ của ngôi vườn nhìn thấy ngôi vườn bị thiêu rụi thành một màu đen thì họ không tin vào mắt mình, họ bảo nhau: Chắc chúng ta đã đi lạc rồi (đây không phải là vườn của chúng ta).');
INSERT INTO "vn_mokhtasar" VALUES(68,27,'68:27','Không, chúng ta đã bị tước mất hết trái quả rồi bởi việc chúng ta không muốn chia sẻ cho những người nghèo.');
INSERT INTO "vn_mokhtasar" VALUES(68,28,'68:28','Người tốt nhất trong bọ họ (về đạo và sự hiểu biết) nói: Chẳng phải tôi đã nói với các anh lúc các anh quyết định không muốn chia sẻ cho những người nghèo rằng các anh hãy tán dương Allah và sám hối với Ngài ư?!');
INSERT INTO "vn_mokhtasar" VALUES(68,29,'68:29','Họ nói: Vinh quang thay Thượng Đế của bầy tôi, bầy tôi thực sự đã sai và bất công với chính bản thân mình lúc bầy tôi quyết định không để cho những người nghèo tiếp cận trái quả trong vườn của bầy tôi.');
INSERT INTO "vn_mokhtasar" VALUES(68,30,'68:30','Rồi họ quay lại trách móc và than thở với nhau.');
INSERT INTO "vn_mokhtasar" VALUES(68,31,'68:31','Họ than trong nuối tiếc và hối hận: Ôi, chúng ta đã thua thiệt cho việc chúng ta vượt quá giới hạn về việc ngăn những người nghèo hưởng quyền lợi của họ.');
INSERT INTO "vn_mokhtasar" VALUES(68,32,'68:32','Hy vọng Thượng Đế của chúng ta sẽ ban cho chúng ta một ngôi vườn khác tốt hơn, chúng ta hãy chỉ luôn cầu xin một mình Allah, chúng ta hãy hy vọng Ngài tha thứ, xí xóa và khẩn xin Ngài ban điều tốt lành.');
INSERT INTO "vn_mokhtasar" VALUES(68,33,'68:33','Sự trừng phạt cho việc ngăn chặn nguồn bổng lộc của người nghèo là như thế, và sự trừng phạt ở Đời Sau còn lớn hơn, mạnh và khủng khiếp hơn và sẽ là đời đời kiếp kiếp nếu họ biết.');
INSERT INTO "vn_mokhtasar" VALUES(68,34,'68:34','Quả thật những người kính sợ Allah qua việc chấp hành mệnh lệnh của Ngài cũng như tránh xa những điều Ngài ngăn cấm sẽ được Thượng Đế của họ ban cho Thiên Đàng Hạnh Phúc, họ sẽ sống và hưởng thụ với niềm hạnh phúc bất tận trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(68,35,'68:35','Chẳng lẽ TA (Allah) lại đối xử với những người Muslim tương tự những kẻ vô đức tin giống như những kẻ đa thờ Makkah đã khẳng định ư?!');
INSERT INTO "vn_mokhtasar" VALUES(68,36,'68:36','Hỡi những kẻ thờ đa thần, các ngươi phân xử sao vậy, sao lại phân xử bất công như thế chứ?!');
INSERT INTO "vn_mokhtasar" VALUES(68,37,'68:37','Hay phải chăng các ngươi có Kinh Sách mà các ngươi đọc trong đó thấy rằng có sự ngang bằng giữa người vâng lệnh và kẻ bất tuân ư?!');
INSERT INTO "vn_mokhtasar" VALUES(68,38,'68:38','Hay phải chăng trong quyển Kinh Sách đó có những điều để các ngươi lựa chọn cho bản thân các ngươi ở cõi Đời Sau?!');
INSERT INTO "vn_mokhtasar" VALUES(68,39,'68:39','Hay phải chăng các ngươi nhận được giao ước chắc chắn bởi lời thề từ nơi TA (Allah) rằng các ngươi được phân xử theo bất cứ điều gì các ngươi muốn sao?!');
INSERT INTO "vn_mokhtasar" VALUES(68,40,'68:40','Hỡi Thiên Sứ Muhammad, Ngươi hãy hỏi những kẻ nói lời này rằng ai trong bọn họ đảm bảo nó?!');
INSERT INTO "vn_mokhtasar" VALUES(68,41,'68:41','Hay phải chăng họ có những thần linh mà họ đã dựng lên ngoài Allah làm cho họ ngang bằng với những người có đức tin trong phần thưởng?! Nếu họ thực sự có những thần linh đó thì họ hãy mang chúng đến và kêu chúng làm cho họ ngang bằng với những người có đức tin nếu họ nói thật.');
INSERT INTO "vn_mokhtasar" VALUES(68,42,'68:42','Vào Ngày Phục Sinh, ống chân của Thượng Đế sẽ được để lộ ra và nhân loại được gọi đến phủ phục trước Ngài, những người có đức tin quỳ xuống phủ phục riêng những người vô đức tin và những kẻ giả tạo đức tin sẽ không thể quỳ xuống được.');
INSERT INTO "vn_mokhtasar" VALUES(68,43,'68:43','Cái nhìn của họ gục xuống trong sự hèn hạ và hối hận, và quả thật trên thế gian, họ đã được yêu cầu quỳ lạy phủ phục Allah trong lúc họ đang khỏe mạnh, chứ không giống hiện trạng của Ngày Hôm Nay (Ngày Phục Sinh).');
INSERT INTO "vn_mokhtasar" VALUES(68,44,'68:44','Hỡi Thiên Sứ Muhammad, Ngươi hãy để mặc TA (Allah) xử trí những kẻ phủ nhận Qur''an được ban xuống cho Ngươi. TA sẽ dần dần kéo chúng đến sự trừng phạt một cách mà chúng không hề hay biết.');
INSERT INTO "vn_mokhtasar" VALUES(68,45,'68:45','Và TA (Allah) tạm tha cho chúng một thời gian để chúng gia tăng thêm tội lỗi. Quả thật, kế hoạch của TA đối với những kẻ vô đức tin và phủ nhận vô cùng vững mạnh. Chúng chẳng thể thoát được TA và không bao giờ an toàn khỏi sự trừng phạt của TA.');
INSERT INTO "vn_mokhtasar" VALUES(68,46,'68:46','Hỡi Thiên Sứ Muhammad, phải chăng Ngươi đã đòi chúng tiền công cho việc Ngươi mời gọi chúng đến với Allah nên chúng đã quay lưng với Ngươi vì chúng không chịu nổi khoản nợ quá lớn?! Thực tế hoàn toàn khác sự việc đó, Ngươi - Thiên Sứ Muhammad - không hề đòi chúng tiền công nhưng điều gì ngăn cản chúng đi theo Ngươi?!');
INSERT INTO "vn_mokhtasar" VALUES(68,47,'68:47','Hay phải chăng chúng biết những điều vô hình để rồi chúng viết ra những gì là được phép đối với chúng bằng những lý lẽ mà chúng đã tranh luận với Ngươi - hỡi Thiên Sứ Muhammad?!');
INSERT INTO "vn_mokhtasar" VALUES(68,48,'68:48','Hỡi Thiên Sứ Muhammad, Ngươi hãy kiên nhẫn với những gì mà Thượng Đế của Ngươi đã định trong việc kéo chúng từ từ đến với sự trừng phạt của Ngài. Ngươi chớ nên giống như Người Bạn của cá voi - Yunus - đã chán nản và bất mãn đối với cộng đồng của Y, Y đã cầu cứu Thượng Đế của Y khi gặp họa trong bóng tối của biển cả và bóng tối trong bụng của con cá voi.');
INSERT INTO "vn_mokhtasar" VALUES(68,49,'68:49','Nếu không nhờ lòng thương xót của Allah (sau khi Yunus đã biết lỗi và sám hối với Ngài) thì chắc chắn đã bị con cá voi nôn Y ra từ trong bụng và trôi dạt vào một vùng đất hoang vắng trong lúc Y vẫn còn là người đang bị khiển trách.');
INSERT INTO "vn_mokhtasar" VALUES(68,50,'68:50','Rồi Thượng Đế của Y đã chọn Y và làm cho Y thành một người bề tôi ngoan đạo và đức hạnh.');
INSERT INTO "vn_mokhtasar" VALUES(68,51,'68:51','Và những kẻ vô đức tin nơi Allah và phủ nhận Thiên Sứ của Ngài muốn làm cho Ngươi - hỡi Thiên Sứ Muhammad - trệch khỏi sứ mạng của Ngươi bằng cái nhìn thù hằn và căm phẫn của chúng. Khi chúng nghe Kinh Qur''an này, cái được ban xuống cho Ngươi thì chúng bảo - vì muốn theo dục vọng của chúng và quay lưng với điều chân lý -: Tên Thiên Sứ đó đích thực là một kẻ điên.');
INSERT INTO "vn_mokhtasar" VALUES(68,52,'68:52','Quả thật, Qur''an được ban xuống cho Ngươi là lời nhắc nhở và khuyên răn dành cho con người và loài Jinn.');
INSERT INTO "vn_mokhtasar" VALUES(69,1,'69:1','(Al-Haqqah có nghĩa là "Điều chắc chắc xảy ra", đây là một trong các tên gọi của giờ Tận Thế). Allah đề cập đến giờ khắc Tận Thế bằng tên gọi này bởi vì nó là sự thật sẽ xảy ra cho tất cả.');
INSERT INTO "vn_mokhtasar" VALUES(69,2,'69:2','Allah dùng câu hỏi để nói lên sự trọng đại của Nó. Al-Haqqah là cái gì thế?');
INSERT INTO "vn_mokhtasar" VALUES(69,3,'69:3','Và điều gì nhắc nhở ngươi về Al-Haqqa - sự kiện chắc chắn xảy ra - này?');
INSERT INTO "vn_mokhtasar" VALUES(69,4,'69:4','Thamud - đám dân của Saleh và ''Ad - đám dân của Hud đã phủ nhận Al-Qa-ri''ah (nỗi kinh hoàng) - tên gọi của Ngày Tận Thế. (Nó được gọi như vậy là bởi vì trong Ngày Hôm Đó, nhân loại sẽ kinh hoàng trước những diễn biến đầy nỗi sợ hãi xảy ra).');
INSERT INTO "vn_mokhtasar" VALUES(69,5,'69:5','Đối với đám dân Thamud, Allah đã hủy diệt họ bằng một trận nổ kinh thiên động địa.');
INSERT INTO "vn_mokhtasar" VALUES(69,6,'69:6','Còn đối với đám dân ''Ad, Allah đã tiêu diệt họ bằng một trận gió lớn mang cái lạnh vô cùng khắc nghiệt.');
INSERT INTO "vn_mokhtasar" VALUES(69,7,'69:7','Allah đã cho trận gió lớn khủng khiếp và khắc nghiệt đó diễn ra liên tiếp trong ngày bảy đêm. Sau trận cuồng phong đó, (nếu ngươi nhìn thấy) ngươi thấy dám dân chết nằm la liệt tại nơi của họ giống như những cây cha là bị bứng gốc ngã rạp xuống đất.');
INSERT INTO "vn_mokhtasar" VALUES(69,8,'69:8','Chắc chắn ngươi sẽ không thấy một sinh mạng nào còn sống sót sau sự trừng phạt đó cả.');
INSERT INTO "vn_mokhtasar" VALUES(69,9,'69:9','Đến lượt Fir''awn và những cộng đồng trước hắn cùng với những thị trấn đã bị lật ngược từ trên cao xuống - đó là đám dân của Lut do bởi hành động tội lỗi từ việc Shirk và trái lệnh.');
INSERT INTO "vn_mokhtasar" VALUES(69,10,'69:10','Tất cả bọn chúng đều làm trái lệnh các vị Thiên Sứ của Allah và phủ nhận họ khi được gửi đến với chúng. Vì vậy, Allah đã tiêu diệt chúng bằng một sự túm bắt cường bạo.');
INSERT INTO "vn_mokhtasar" VALUES(69,11,'69:11','Quả thật, chính TA (Allah), khi nước dâng lên cao, đã chở các ngươi và con cháu của các ngươi trên con thuyền trôi nổi, con thuyền mà Nuh (Noah) đã đóng theo lệnh truyền của TA.');
INSERT INTO "vn_mokhtasar" VALUES(69,12,'69:12','TA đã làm cho con thuyền chuyên chở các ngươi (vượt qua con nước lũ đó) cũng như câu chuyện của nó là một bài học rằng những kẻ vô đức tin sẽ bị trừng phạt như thế còn những người có đức tin sẽ được cứu rỗi như thế và để làm sự nhắc nhở cho những ai có tai biết nghe.');
INSERT INTO "vn_mokhtasar" VALUES(69,13,'69:13','Bởi vậy, khi vị Thiên Thần được giao trách nhiệm thổi Còi bằng chiếc Sừng (báo hiệu giờ Tận Thế và Phục Sinh - đó là đại Thiên Thần Israfil) thổi tiếng hú đầu tiên (đó là tiếng còi để hủy diệt thế giới - tiếng còi Tận Thế).');
INSERT INTO "vn_mokhtasar" VALUES(69,14,'69:14','Bằng một cơn chấn động dữ dội làm Trái đất và núi non tan tành và mất dạng.');
INSERT INTO "vn_mokhtasar" VALUES(69,15,'69:15','Ngày Hôm Đó, tất cả mọi diễn biến chính là giờ Tận Thế đang xảy ra.');
INSERT INTO "vn_mokhtasar" VALUES(69,16,'69:16','Vào Ngày Hôm Đó, bầu trời sẽ bị chẻ làm đôi để các Thiên Thần đi xuống. Và bầu trời trong Ngày Hôm Đó trở nên mong manh mặc dù trước đó nó đã từng vững chắc.');
INSERT INTO "vn_mokhtasar" VALUES(69,17,'69:17','Các Thiên Thần đi xuống và xếp thành hàng ngũ chỉnh tề, và Ngày Hôm đó, có tám vị Thiên Thần phụ trách vác trên vai họ chiếc Ngai Vương của Allah, Thượng Đế của Ngươi (Muhammad); và họ là những vị Thiên Thần cận kề Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(69,18,'69:18','Hỡi nhân loại, vào Ngày Hôm Đó, các ngươi sẽ được triệu tập trước Allah để Ngài xét xử, không có một điều bí mật nào của các ngươi có thể che giấu trong Ngày Hôm Đó, Allah sẽ mang ra xét xử tất cả.');
INSERT INTO "vn_mokhtasar" VALUES(69,19,'69:19','Đối với ai được trao cho quyển sổ ghi chép các việc làm của mình từ phía cánh tay phải thì y sẽ nói trong niềm vui mừng: mọi người hãy đọc quyển sổ ghi chép của tôi đây này!');
INSERT INTO "vn_mokhtasar" VALUES(69,20,'69:20','Trên thế gian tôi đã làm và tôi luôn chắc chắn mình sẽ được phục sinh và sẽ phải đối diện với sự phán xét.');
INSERT INTO "vn_mokhtasar" VALUES(69,21,'69:21','Vậy là y sẽ có được đời sống hài lòng và toại nguyện bởi những gì mà y nhìn thấy từ niềm hạnh phúc và hưởng thụ bất tận.');
INSERT INTO "vn_mokhtasar" VALUES(69,22,'69:22','Trong Thiên Đàng có các ngôi vườn trên cao.');
INSERT INTO "vn_mokhtasar" VALUES(69,23,'69:23','Trái quả của các ngôi vườn nơi Thiên Đàng sẽ luôn ở gần tầm tay hái của các cư dân của nó.');
INSERT INTO "vn_mokhtasar" VALUES(69,24,'69:24','Có lời nghinh đón và chúc mừng: Quí vị hãy ăn và uống một cách thỏa thích bởi những việc làm ngoan đạo trong những ngày ở nơi trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(69,25,'69:25','Đối với những ai được trao cho quyển sổ ghi chép các việc làm của họ từ phía cánh tay trái thì họ sẽ than trong sự hối hận muộn màng: Ôi ước gì mình không được trao cho quyển sổ ghi chép các việc làm xấu và tội lỗi này để không bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(69,26,'69:26','Ôi ước gì mình không biết gì về việc phán xét này.');
INSERT INTO "vn_mokhtasar" VALUES(69,27,'69:27','Ôi ước gì mình chết đi giống như cái chết mình đã từng (trên thế gian) và không bao giờ được phục sinh trở lại.');
INSERT INTO "vn_mokhtasar" VALUES(69,28,'69:28','Ôi, tiền bạc và của cải của mình chẳng giúp mình được gì cho mình trong việc chạy tội để thoát khỏi sự trừng phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(69,29,'69:29','Ôi quyền hành mà mình từng dựa vào nó giờ đây đã bỏ mình đi đâu mất.');
INSERT INTO "vn_mokhtasar" VALUES(69,30,'69:30','Rồi có lệnh truyền: Hỡi các Thiên Thần, các ngươi hãy túm bắt lấy hắn và trói tay hắn cùng với cổ của hắn.');
INSERT INTO "vn_mokhtasar" VALUES(69,31,'69:31','Xong, các ngươi hãy ném hắn vào trong Hỏa Ngục để hắn nếm cái nóng của Nó.');
INSERT INTO "vn_mokhtasar" VALUES(69,32,'69:32','Rồi các ngươi hãy dùng sợi dây xích dài bảy mươi khuỷu tay (của các Thiên Thần) xiềng hắn lại.');
INSERT INTO "vn_mokhtasar" VALUES(69,33,'69:33','Quả thật, hắn đã vô đức tin nơi Allah, Đấng Vĩ Đại.');
INSERT INTO "vn_mokhtasar" VALUES(69,34,'69:34','Và hắn đã không kêu người khác nuôi ăn những người khó khăn túng thiếu.');
INSERT INTO "vn_mokhtasar" VALUES(69,35,'69:35','Bởi vậy, trong ngày phán xét hôm nay, hắn không có bất cứ người bạn nào hầu có thể giúp hắn chạy thoát khỏi sự trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(69,36,'69:36','Sẽ không có bất cứ loại thức ăn nào ngoại trừ máu và mủ của vết thương từ các cơ thể của cư dân Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(69,37,'69:37','Thức ăn (chất máu và mủ từ các vết thương của cơ thể các cư dân Hỏa Ngục) đó là thức ăn chỉ dành cho những kẻ tội lỗi và bất tuân.');
INSERT INTO "vn_mokhtasar" VALUES(69,38,'69:38','TA (Allah) thề bởi những gì các ngươi (con người) nhìn thấy (bằng mắt thường của các ngươi).');
INSERT INTO "vn_mokhtasar" VALUES(69,39,'69:39','Và TA (Allah) thề bởi những gì các ngươi (con người) không nhìn thấy (bằng mắt thường của các ngươi).');
INSERT INTO "vn_mokhtasar" VALUES(69,40,'69:40','Quả thật, Qur''an là lời phán của Allah được vị Thiên Sứ vinh dự đọc cho nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(69,41,'69:41','Qur''an không phải là lời lẽ của một nhà thơ, bởi Nó không theo một lề lối và quy củ nào của thơ ca, mà các ngươi lại không mấy tin.');
INSERT INTO "vn_mokhtasar" VALUES(69,42,'69:42','Và Qur''an cũng không phải là lời lẽ của tên bói toán (thường dối gạt và lừa phỉnh) mà các ngươi không mấy lưu tâm.');
INSERT INTO "vn_mokhtasar" VALUES(69,43,'69:43','(Qur''an không phải là lời của một nhà thơ cũng không phải là lời của một tên thầy bói) mà Nó là lời phán được mặc khải xuống từ Thượng Đế của vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(69,44,'69:44','Nếu Muhammad dám bịa đặt ra một số lời lẽ nào đó mà TA (Allah) không mặc khải xuống.');
INSERT INTO "vn_mokhtasar" VALUES(69,45,'69:45','(Nếu Muhammad làm thế) thì chắc chắn TA (Allah) sẽ túm bắt lấy Y bằng một cú bắt thật mạnh bằng sức mạnh và quyền năng của TA.');
INSERT INTO "vn_mokhtasar" VALUES(69,46,'69:46','Rồi sau đó TA cắt động mạch chủ của trái tim Y.');
INSERT INTO "vn_mokhtasar" VALUES(69,47,'69:47','Nhưng không một ai trong các ngươi có khả năng bảo vệ Y (Muhammad) khỏi sự trừng phạt của TA (Allah) (nếu Y làm như thế). Do đó, Y sẽ không bịa đặt bất cứ một lời nào trong lời phán của TA vì các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(69,48,'69:48','Và quả thật Qur''an là lời khuyên răn dành cho những người biết kính sợ Allah qua việc tuân thủ theo mệnh lệnh của Ngài cũng như tránh xa những gì Ngài cấm.');
INSERT INTO "vn_mokhtasar" VALUES(69,49,'69:49','Và TA biết chắc chắn trong các ngươi có những người phủ nhận Kinh Qur''an này.');
INSERT INTO "vn_mokhtasar" VALUES(69,50,'69:50','Và quả thật việc phủ nhận Qur''an sẽ là điều hối tiếc lớn lao đối với những kẻ vô đức vào Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(69,51,'69:51','Và quả thật Qur''an là Chân Lý tuyệt đối và kiên định đến từ nơi Allah, không có gì phải ngờ vực cả.');
INSERT INTO "vn_mokhtasar" VALUES(69,52,'69:52','Bởi thế, hỡi Thiên Sứ Muhammad, Ngươi hãy luôn tán dương và nhớ Thượng Đế của Ngươi, Đấng Vĩ Đại và Tối Cao.');
INSERT INTO "vn_mokhtasar" VALUES(70,1,'70:1','Một số người trong số những người thờ đa thần đã đòi sự trừng phạt xảy đến với bản thân họ hoặc cộng đồng của họ. Đây là một sự nhạo báng vì không tin sự trừng phạt; và sự trừng phạt chắc chắn sẽ xảy ra vào Ngày Phán Xét.');
INSERT INTO "vn_mokhtasar" VALUES(70,2,'70:2','Những kẻ vô đức tin sẽ không thể tránh khỏi sự trừng phạt này.');
INSERT INTO "vn_mokhtasar" VALUES(70,3,'70:3','(Sự trừng phạt mà họ đòi hỏi) đến từ nơi Allah, Đấng sở hữu sự tối cao với các ân huệ và những phúc lành.');
INSERT INTO "vn_mokhtasar" VALUES(70,4,'70:4','Các Thiên Thần cùng Ruh (Đại Thiên Thần Jibril) đi lên chầu Ngài (Allah) trong một Ngày mà thời gian của nó tương đương năm mươi ngàn năm (thời gian trên cõi trần).');
INSERT INTO "vn_mokhtasar" VALUES(70,5,'70:5','Bởi thế, hỡi Thiên Sứ Muhammad, Ngươi hãy thực sự kiên nhẫn, chớ buồn lo cũng chớ than phiền.');
INSERT INTO "vn_mokhtasar" VALUES(70,6,'70:6','Quả thật, chúng (những kẻ vô đức tin) thấy sự trừng phạt rất xa vời như là một điều không bao giờ xảy ra.');
INSERT INTO "vn_mokhtasar" VALUES(70,7,'70:7','Nhưng TA (Allah) thì thấy sự trừng phạt gần xảy ra và chắc chắn sẽ xảy ra.');
INSERT INTO "vn_mokhtasar" VALUES(70,8,'70:8','Ngày mà bầu trời sẽ giống như đồng, vàng hoặc những kim loại khác được nấu chảy.');
INSERT INTO "vn_mokhtasar" VALUES(70,9,'70:9','Và những quả núi trở nên nhẹ giống như những sợi lông cừu dễ dàng thổi bay đi.');
INSERT INTO "vn_mokhtasar" VALUES(70,10,'70:10','(Ngày Hôm Đó) sẽ không có một người thân quen nào hỏi thăm một người thân quen nào, không ai hỏi ai cả, tất cả đều chỉ bận tâm đến bản thân mình.');
INSERT INTO "vn_mokhtasar" VALUES(70,11,'70:11','Vào Ngày Hôm Đó, tất cả mỗi con người đều nhìn thấy người thân quen của mình. Mặc dù vậy, không ai hỏi thăm ai trong hoàn cảnh đó. Họ chỉ quan tâm đến bản thân mình đến nỗi một người đáng bị vào Hỏa Ngục muốn dâng con cái thay tội cho mình để bản thân mình được thoát tội.');
INSERT INTO "vn_mokhtasar" VALUES(70,12,'70:12','Y muốn dâng cả vợ và anh em của mình để thế mạng cho mình.');
INSERT INTO "vn_mokhtasar" VALUES(70,13,'70:13','Y muốn dâng cả những người thân thuộc đã từng ủng hộ và giúp đỡ y trong hoạn nạn.');
INSERT INTO "vn_mokhtasar" VALUES(70,14,'70:14','Y muốn dâng tất cả mọi thứ trên trái đất từ loài người, loài Jinn và sinh vật khác hầu để bản thân mình được an toàn khỏi sự trừng phạt nơi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(70,15,'70:15','Sự việc sẽ không như những kẻ tội lỗi mong muốn mà Lửa của Hỏa Ngục sẽ tràn ngập thiêu đốt họ.');
INSERT INTO "vn_mokhtasar" VALUES(70,16,'70:16','Sức nóng của lửa Hỏa Ngục làm tróc da đầu.');
INSERT INTO "vn_mokhtasar" VALUES(70,17,'70:17','Lửa Hỏa Ngục sẽ gọi những ai quay lưng với Chân Lý, những ai tránh xa, không tin Nó và không làm theo.');
INSERT INTO "vn_mokhtasar" VALUES(70,18,'70:18','Và những kẻ tom góp tài sản của cải nhưng không chịu chi dùng cho con đường chính nghĩa của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(70,19,'70:19','Quả thật, con người được tạo với bản chất luôn muốn giữ bên mình không muốn chi ra.');
INSERT INTO "vn_mokhtasar" VALUES(70,20,'70:20','Khi gặp điều dữ từ bệnh tật và nghèo khó thì thường ít kiên nhẫn, hay oán than.');
INSERT INTO "vn_mokhtasar" VALUES(70,21,'70:21','Và khi gặp được niềm vui từ sự giàu có và dồi dào của cải thì trở nên keo kiệt, không muốn chi dùng cho chính nghĩa của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(70,22,'70:22','Ngoại trừ những người thực hiện bổn phận lễ nguyện Salah, họ sẽ an toàn khỏi những thuộc tính đáng trách đó.');
INSERT INTO "vn_mokhtasar" VALUES(70,23,'70:23','Những người mà họ luôn gìn giữ bổn phận lễ nguyện Salah của mình, họ không vì những việc khác mà trễ nải lễ nguyện Salah của họ, họ luôn thực hiện nó trong thời gian được quy định.');
INSERT INTO "vn_mokhtasar" VALUES(70,24,'70:24','Những người mà họ luôn chừa một phần tài sản bắt buộc từ nguồn tài sản của mình.');
INSERT INTO "vn_mokhtasar" VALUES(70,25,'70:25','Họ đưa phần tài sản mà họ trích ra đó cho những người nghèo đói và thiếu thốn cả người xin và người không xin họ.');
INSERT INTO "vn_mokhtasar" VALUES(70,26,'70:26','Những người mà họ tin nơi Ngày Phán Xét, Ngày mà Allah sẽ thưởng hoặc phạt tất cả mỗi người một cách thích đáng tùy theo việc làm của họ.');
INSERT INTO "vn_mokhtasar" VALUES(70,27,'70:27','Những người mà họ khiếp sợ sự trừng phạt của Thượng Đế của họ, họ cố gắng làm những điều tốt đẹp và ngoan đạo để tránh (sự phẫn nộ của Ngài).');
INSERT INTO "vn_mokhtasar" VALUES(70,28,'70:28','Quả thật sự trừng phạt của Thượng Đế của họ rất khủng khiếp và đáng sợ mà người có ý thức không nên cảm thấy an toàn trước nó.');
INSERT INTO "vn_mokhtasar" VALUES(70,29,'70:29','Những người mà họ che đậy phần kín của họ và giữ cho nó không phạm Zina (tình dục ngoài hôn nhân).');
INSERT INTO "vn_mokhtasar" VALUES(70,30,'70:30','Họ không bị bắt tội trong hành vi nhục dục của mình với các bà vợ hoặc với các nữ nô lệ nằm dưới quyền của họ.');
INSERT INTO "vn_mokhtasar" VALUES(70,31,'70:31','Ai có hành vi nhục dục với những ai khác ngoài vợ và những nữ nô lệ dưới quyền của họ thì họ là những kẻ vượt mức giới hạn của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(70,32,'70:32','Những người mà họ luôn thực hiện đúng theo sự ủy thác mà mọi người đã ủy thác cho họ từ tài sản cũng như những điều bí mật và những thứ khác; và luôn thực hiện đúng giao ước với mọi người.');
INSERT INTO "vn_mokhtasar" VALUES(70,33,'70:33','Những người mà họ luôn đứng lên làm chứng khi được yêu cầu cho việc đúng và chân lý, họ không bị tác động bởi mối qua hệ bà con thân thích hay sự hận thù trong lòng.');
INSERT INTO "vn_mokhtasar" VALUES(70,34,'70:34','Những người mà họ chu đáo gìn giữ lễ nguyện Salah của họ, họ thực hiện nó đùng giờ giấc quy định, họ tẩy sạch thân thể và thực hiện nó một cách nghiêm trang và không bị phân tâm.');
INSERT INTO "vn_mokhtasar" VALUES(70,35,'70:35','Những người được mô tả với những thuộc tính đó sẽ là những người vinh dự được sống trong các Ngôi Vườn Thiên Đàng, họ sẽ hưởng thụ cuộc sống khoái lạc và tiện nghi trường tồn và sẽ được ngắm nhìn gương mặt của Allah, Đấng Tuyệt Đẹp và Phúc Đức.');
INSERT INTO "vn_mokhtasar" VALUES(70,36,'70:36','Điều gì đã lôi kéo những kẻ thờ đa thần thuộc đám dân của Ngươi chạy nhanh đến trước mặt Ngươi để phủ nhận Ngươi - hỡi Thiên Sứ Muhammad?!');
INSERT INTO "vn_mokhtasar" VALUES(70,37,'70:37','Chúng quay quanh Ngươi, bên phải và bên trái, từng nhóm từng nhóm một.');
INSERT INTO "vn_mokhtasar" VALUES(70,38,'70:38','Há từng tên trong bọn chúng - những kẻ thờ đa thần vô đức tin nơi Allah - mong ước được Allah thu nhận vào Thiên Đàng Hạnh Phúc của Ngài để chúng sống hưởng lạc trường tồn trong đó trong khi chúng vẫn ở trên sự vô đức tin của chúng ư?!');
INSERT INTO "vn_mokhtasar" VALUES(70,39,'70:39','Chắc chắn sự việc sẽ không như chúng tưởng tượng. Quả thật, TA (Allah) đã tạo chúng ra từ thứ mà chúng đều biết rõ. TA thực sự đã tạo chúng ra từ một loại nước đáng khinh (tinh dịch xuất ra từ dương vật của người đàn ông), và chúng thực sự yếu ớt không nắm được bất cứ một điều lợi gì cho bản thân chúng cũng như chẳng có khả năng mang lại tai họa cho ai, vậy tại sao chúng lại tự cao tự đại?!');
INSERT INTO "vn_mokhtasar" VALUES(70,40,'70:40','Allah thề với Thượng Đế của các điểm hướng đông và hướng tây của mặt trời và mặt trăng - và đó chính là Ngài - rằng Ngài là Đấng Toàn Năng.');
INSERT INTO "vn_mokhtasar" VALUES(70,41,'70:41','Allah là Đấng Toàn Năng trên việc thay thế một nhóm người khác tốt hơn, một nhóm người mà họ sẽ luôn tuân lệnh Ngài. Và không ai có thể ngăn cản Ngài một khi Ngài muốn hủy diệt chúng (những kẻ vô đức tin) và thay thế chúng bởi một nhóm người khác tốt hơn.');
INSERT INTO "vn_mokhtasar" VALUES(70,42,'70:42','Hỡi Thiên Sứ Muhammad, Ngươi cứ để mặc chúng đắm chìm trong sai trái và lầm lạc, cử để mặc chúng vui chơi trong cuộc sống trần gian cho tới khi chúng giáp mặt với Ngày Phục Sinh, Ngài mà chúng đã được hứa trong Qur''an.');
INSERT INTO "vn_mokhtasar" VALUES(70,43,'70:43','Ngày mà họ sẽ đi ra từ mộ của họ một cách hối hả giống như họ tranh nhau chạy đến một mục tiêu đã định sẵn cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(70,44,'70:44','Cặp mắt của họ nhìn xuống đầy hèn hạ trong Ngày Hôm Đó, và đó là Ngày mà họ đã được hứa khi họ còn sống trên thế gian nhưng họ lại không màng quan tâm.');
INSERT INTO "vn_mokhtasar" VALUES(71,1,'71:1','Quả thật, TA (Allah) đã chọn Nuh (Noah) làm một vị Sứ Giả và cử Y đến kêu gọi đám dân của Y và cảnh báo họ trước khi TA giáng xuống sự trừng phạt đau đớn họ vì tội Shirk (gán ghép với Allah các thần linh ngang vai) của chúng đối với TA.');
INSERT INTO "vn_mokhtasar" VALUES(71,2,'71:2','Nuh đã nói với đám dân của mình: Này hỡi dân ta, quả thật ta là một người cảnh báo được cử đến trình bày rõ ràng cho các người về sự trừng phạt đang chờ đợi các người nếu các người không quay đầu sám hối với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(71,3,'71:3','Nuh đã nói với cộng đồng người dân của mình: Các người hãy thờ phượng một mình Allah thôi, chớ đừng Shirk với Ngài bất cứ điều gì, các người hãy sợ Ngài mà chấp hành các mệnh của Ngài cũng như tránh xa những điều Ngài ngăn cấm, và các người hãy vâng lời ta về những gì ta ra lệnh và sai bảo các người.');
INSERT INTO "vn_mokhtasar" VALUES(71,4,'71:4','Nuh nói với cộng đồng người dân của mình: Quả thật nếu các ngươi làm như thế, Allah sẽ tha thứ tội lỗi cho các người, tội lỗi mà nó không liên quan đến quyền lợi của đám bề tôi của Ngài, và Ngài sẽ để các ngươi sống đến thời hạn mà Ngài đã ấn định cho các ngươi theo kiến thức và sự hiểu biết của Ngài, Ngài sẽ để các ngươi an cư trên trái đất. Quả thật, cái chết một khi nó đến thì sẽ không trì hoãn thêm bất cứ thời khắc nào, nếu các ngươi biết điều đó thì các ngươi hãy mau đặt niềm tin nơi Allah, mau mau quay về sám hối với Ngài về những điều Shirk và lầm lạc mà các ngươi đã phạm.');
INSERT INTO "vn_mokhtasar" VALUES(71,5,'71:5','Nuh thưa với Allah: Lạy Thượng Đế của bề tôi, quả thật bề tôi đã kêu gọi người dân của bề tôi đến với sự thờ phượng một mình Ngài cả ngày lẫn đêm một cách xuyên suốt.');
INSERT INTO "vn_mokhtasar" VALUES(71,6,'71:6','Lời kêu gọi của bề tôi chẳng làm tăng thêm được gì cho họ ngoài việc chỉ làm cho họ càng ngày càng chạy xa hơn khỏi điều mà bề tôi kêu gọi họ.');
INSERT INTO "vn_mokhtasar" VALUES(71,7,'71:7','Quả thật, mỗi lần bề tôi kêu gọi họ đến với những gì mà Ngài sẽ dùng làm nguyên nhân tha thứ tội lỗi cho họ - từ việc thờ phượng một mình Ngài và vâng lời vị Thiên Sứ của Ngài - thì họ lấy các ngón tay bịt tai họ lại để không nghe lời kêu gọi của bề tôi, họ lấy áo phủ kín mặt để không nhìn thấy bề tôi, họ vẫn cứ ngoan cố trên những việc làm Shirk và vẫn cứ tự cao tự đại không chịu đón nhận lời kêu gọi của bề tôi.');
INSERT INTO "vn_mokhtasar" VALUES(71,8,'71:8','Lạy Thượng Đế, sau đó bề tôi đã kêu gọi một cách công khai.');
INSERT INTO "vn_mokhtasar" VALUES(71,9,'71:9','Rồi bề tôi cũng đã có lúc kêu gọi họ công khai và có lúc kêu gọi họ một cách âm thầm, bề tôi đã kêu gọi bằng nhiều cách khác nhau.');
INSERT INTO "vn_mokhtasar" VALUES(71,10,'71:10','Và bề tôi đã nói với họ: Này hỡi dân ta, các người hãy cầu xin Thượng Đế của các ngươi sự tha thứ bằng việc sám hối của các người bởi quả thật Ngài là Đấng Hằng Tha Thứ cho những bề tôi nào quay đầu sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(71,11,'71:11','Nếu các ngươi làm đúng như thế thì Allah sẽ ban xuống cho các ngươi lượng mưa dồi dào mỗi khi các ngươi cần và các ngươi sẽ không gặp phải sự hạn hán.');
INSERT INTO "vn_mokhtasar" VALUES(71,12,'71:12','Allah sẽ ban cho các ngươi nhiều của cải và con cái, Ngài sẽ ban cho các ngươi các khu vườn để các ngươi ăn trái quả, và Ngài sẽ ban cho các ngươi những con sông để các ngươi và gia súc của các ngươi uống và để tưới tiêu vườn tược của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(71,13,'71:13','Này hỡi dân ta, cớ vì sao các ngươi lại không kính sợ sự vĩ đại và quyền uy của Allah khi mà các ngươi làm trái lệnh của Ngài?!');
INSERT INTO "vn_mokhtasar" VALUES(71,14,'71:14','Quả thật, Allah đã tạo hóa các ngươi theo từng giai đoạn: từ tinh dịch rồi đến hòn máu đặc rồi đến cục thịt.');
INSERT INTO "vn_mokhtasar" VALUES(71,15,'71:15','Chẳng lẽ các ngươi không nhìn thấy Allah đã tạo ra bảy tầng trời, tầng này chồng lên tầng kia như thế nào ư?!');
INSERT INTO "vn_mokhtasar" VALUES(71,16,'71:16','Và Ngài đã tạo trong tầng trời trần gian mặt trăng như một thứ phát sáng nhưng không nóng và mặt trời như một chiếc đèn chiếu sáng mang cái nóng cho cư dân trái đất.');
INSERT INTO "vn_mokhtasar" VALUES(71,17,'71:17','Allah đã tạo hóa các ngươi từ đất, đó là Ngài đã tạo ra người cha của các ngươi - Adam - từ đất bụi rồi các ngươi được nuôi dưỡng bằng những gì mọc ra từ đất.');
INSERT INTO "vn_mokhtasar" VALUES(71,18,'71:18','Rồi đây, Allah sẽ hoàn các ngươi trở lại vào đất sau khi các ngươi chết và sau đó đưa toàn bộ các ngươi trở ra bằng sự phục sinh.');
INSERT INTO "vn_mokhtasar" VALUES(71,19,'71:19','Allah đã tạo ra trái đất cho các ngươi như một tấm thảm bằng phẳng để các ngươi dễ dàng cư trú.');
INSERT INTO "vn_mokhtasar" VALUES(71,20,'71:20','Để các ngươi dễ dàng đi lại trên những con đường rộng lớn trong việc tìm kiếm nguồn bổng lộc Halal.');
INSERT INTO "vn_mokhtasar" VALUES(71,21,'71:21','Nuh thưa với Allah: Lạy Thượng Đế của bề tôi, quả thật đám dân của bề tôi đã không nghe bề tôi trong việc thờ phượng một mình Ngài mà đám dân của bề tôi lại đi theo những kẻ mà họ được Ngài ban cho nhiều của cải và con cái nhưng những thứ đó chẳng gia tăng thêm được gì cho họ ngoại trừ sự thua thiệt.');
INSERT INTO "vn_mokhtasar" VALUES(71,22,'71:22','Và đã vạch định một âm mưu lớn để chống lại Nuh.');
INSERT INTO "vn_mokhtasar" VALUES(71,23,'71:23','Họ bảo những người đi theo họ: Các ngươi chớ từ bỏ việc thờ phượng các thần linh của các ngươi; các ngươi chớ từ bỏ các thần bục tượng của các ngươi: thần Wadd, thần Suwa'', thần Yaghuth, thần Ya''uq và thần Nasr.');
INSERT INTO "vn_mokhtasar" VALUES(71,24,'71:24','Quả thật, họ dùng các thần linh bục tượng của họ dắt nhiều người lầm lạc. Vì thế, lạy Thượng Đế của bề tôi, xin Ngài đừng ban thêm cho những kẻ làm điều tội lỗi bất cứ điều gì ngoại trừ sự lầm lạc khỏi điều chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(71,25,'71:25','Vì tội lỗi mà họ đã phạm nên họ đã bị nhấn chìm trong trận Đại Hồng Thủy trên thế gian, và sau khi chết họ phải bị đày vào Hỏa Ngục ngay lập tức. Và khi Allah trừng phạt họ thì họ không tìm thấy bất cứ ai có thể giúp đỡ họ.');
INSERT INTO "vn_mokhtasar" VALUES(71,26,'71:26','Nuh thưa với Allah sau khi Ngài cho Người biết rằng đám dân của Người sẽ không bao giờ có đức tin nơi Người ngoài những ai đã tin: Lạy Thượng Đế của bề tôi, xin Ngài đừng chừa lại bất cứ ai trong đám vô đức tin trên trái đất này.');
INSERT INTO "vn_mokhtasar" VALUES(71,27,'71:27','Lạy Thượng Đế của bầy tôi, nếu Ngài chừa họ lại thì họ sẽ dẫn đám bề tôi có đức tin của Ngài đi lạc khỏi con đường chính đạo, và họ sẽ sinh con đẻ cháu chỉ toàn là những kẻ xấu xa và tội lỗi, những kẻ vô đức tin và không biết tri ân.');
INSERT INTO "vn_mokhtasar" VALUES(71,28,'71:28','Lạy Thượng Đế của bề tôi, xin Ngài tha thứ tội lỗi cho bề tôi, xin Ngài tha thức cho cha mẹ của bề tôi, xin Ngài tha thứ cho những ai đi vào nhà của bề tôi một cách có đức tin, xin Ngài tha thứ cho những người có đức tin nam và những người có đức tin nữ; và xin Ngài chớ gia tăng gì thêm cho những kẻ sai quấy bởi sự vô đức tin và tội lỗi của họ ngoại trừ sự diệt vong và mất mát.');
INSERT INTO "vn_mokhtasar" VALUES(72,1,'72:1','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với cộng đồng của Ngươi: Allah đã mặc khải cho Ta biết rằng đã có một nhóm Jinn lắng nghe Ta đọc Qur''an tại thân cây chà là. Sau khi chúng nghe xong chúng quay về với cộng đồng của chúng và bảo: Quả thật, chúng tôi đã nghe được một cuộc xướng đọc thật tuyệt diệu và lôi cuốn.');
INSERT INTO "vn_mokhtasar" VALUES(72,2,'72:2','Những lời mà chúng tôi đã nghe từ cuộc đọc xướng (Qur''an) dẫn chúng tôi đến với chân lý về niềm tin, lời nói và hành động. Vì thế, chúng tôi đã tin nơi những lời đó và chúng tôi sẽ không bao giờ tổ hợp bất cứ một thần linh nào cùng với Thượng Đế của chúng tôi, Đấng đã ban những lời đó xuống.');
INSERT INTO "vn_mokhtasar" VALUES(72,3,'72:3','Chúng tôi tin Thượng Đế của chúng tôi là Đấng Tối Cao và Vĩ Đại nhất, Ngài không có vợ và con cái giống như những kẻ thờ đa thần đã khẳng định.');
INSERT INTO "vn_mokhtasar" VALUES(72,4,'72:4','Quả thật, kẻ điên rồ trong chúng tôi - Iblis - đã nói cho Allah những điều quá đáng về Ngài, y nói rằng Allah có vợ và con cái (nhưng Ngài tối cao và vĩ đại vượt xa những điều đó).');
INSERT INTO "vn_mokhtasar" VALUES(72,5,'72:5','Và chúng tôi cứ tưởng rằng những kẻ thờ đa thần từ loài người và loài Jinn không bao giờ nói lời dối gian cho Allah khi mà họ đã khẳng định rằng Ngài có người bạn đời và con cái nên chúng tôi đã tin lời nói của họ và đi theo họ.');
INSERT INTO "vn_mokhtasar" VALUES(72,6,'72:6','Quả thật trong thời Jahiliyah, có những đàn ông loài người đã cầu xin những đàn ông loài Jinn phù hộ mỗi khi họ dừng chân tại một điểm nào đó mà họ cảm thấy sợ hãi. Một trong số họ thường nói "Tôi cầu xin vị đứng đầu của thung lũng này phù hộ tránh khỏi điều xấu từ những kẻ thô lỗ trong cộng đồng của ngài!". Tuy nhiên, việc làm đó chỉ làm cho những đàn ông loài người tăng thêm nỗi sợ hãi trước những đàn ông loài Jinn.');
INSERT INTO "vn_mokhtasar" VALUES(72,7,'72:7','Và loài người đã nghĩ giống như suy nghĩ của các ngươi - hỡi loại Jinn - rằng Allah sẽ không bao giờ dựng sống lại một ai sau khi đã chết cho việc phán xét và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(72,8,'72:8','Và chúng tôi đã từng đi lên tầng trời hạ giới (tầng trời của trần gian) để nghe trộm thông tin nhưng chúng tôi đã thấy trên đó đầy những cảnh vệ binh hung tợn từ các Thiên Thần đang canh gác nghiêm ngặt cùng với những ngôi sao lửa dùng để ném bất cứ ai tiếp cận.');
INSERT INTO "vn_mokhtasar" VALUES(72,9,'72:9','Trước đây, chúng tôi đã từng thường lên tầng trời hạ giới và thượng chọn lấy một chỗ ngồi trên đó để nghe lén tin tức mà các Thiên Thần truyền cho nhau và về lại trái đất mách cho các tên thầy bói của loài người. Nhưng bây giờ, sự việc đã thay đổi, ai trong chúng tôi lên đó nghe trộm thì sẽ bắt gặp những ngôi sao lửa đang mai phục, hễ ai tiếp cận y sẽ bị ném bởi những ngôi sao lửa đó và bị thiêu cháy.');
INSERT INTO "vn_mokhtasar" VALUES(72,10,'72:10','Và quả thật, chúng tôi không còn biết Allah muốn điều xấu cho cư dân trái đất hay muốn điều tốt cho họ qua việc tầng trời hạ giới được canh gác nghiêm ngặt như thế, vì tất cả thông tin trên trời giờ đây đã bị cắt đứt hoàn toàn khỏi chúng tôi.');
INSERT INTO "vn_mokhtasar" VALUES(72,11,'72:11','Và trong chúng tôi - tập thể loài Jinn - có kẻ ngoan đạo và đức hạnh nhưng có kẻ vô đức tin và tội lỗi. Chúng tôi gồm nhiều nhóm phái khác nhau và đi theo những con đường khác nhau.');
INSERT INTO "vn_mokhtasar" VALUES(72,12,'72:12','Và chúng tôi đã tin chắc rằng chúng tôi không bao giờ có thể cản trở được Allah về bất cứ điều gì một khi Ngài đã muốn nó xảy đến với chúng tôi và chúng tôi cũng không bao giờ có thể chạy trốn được Ngài một khi Ngài đã vây hãm chúng tôi.');
INSERT INTO "vn_mokhtasar" VALUES(72,13,'72:13','Quả thật, khi chúng tôi nghe Qur''an, một nguồn Chỉ Đạo đến với điều chân lý, thì chúng tôi đã tin nơi Nó. Và tin nơi Thượng Đế của y thì y sẽ không lo sợ bị giảm mất phần ân phước của mình và cũng không lo sợ phải tăng thêm tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(72,14,'72:14','Và quả thật, trong chúng tôi có người quy phục Allah nhưng cũng có người là những kẻ bất tuân Ngài và đi lạc khỏi con đường chính đạo của Ngài. Bởi thế, những ai hạ mình quy phục Allah và làm những việc làm thiện tốt thì họ là những người thực sự đã hướng tới sự chỉ đạo và chân lý.');
INSERT INTO "vn_mokhtasar" VALUES(72,15,'72:15','Ngược lại, đối với những kẻ đi lạc khỏi con đường chính đạo thì vào Ngày Phán Xét họ sẽ trở thành củi đốt nơi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(72,16,'72:16','Nếu nhóm Jinn đã nghe được những lời mặc khải rồi đi ngay thẳng trên con đường chính đạo Islam và làm đúng theo con đường đó thì sẽ được Allah ban cho cơn mưa dồi dạo cùng với nhiều ân huệ khác.');
INSERT INTO "vn_mokhtasar" VALUES(72,17,'72:17','Để thử thách họ xem họ có tri ân hay vô ơn với các ân huệ của Allah? Và ai quay lưng với Qur''an và với những lời khuyên răn trong Nó thì Thượng Đế của y sẽ đày y vào sự trừng phạt với cơn đau đớn tột cùng.');
INSERT INTO "vn_mokhtasar" VALUES(72,18,'72:18','Các Masjid đều là của Allah chứ không phải của ai khác, bởi vậy, các ngươi không được phép cầu xin ai (vật gì) ngoài Ngài. Nếu các ngươi làm như thế thì các ngươi đã hành động giống như hành động của những người Do Thái và Thiên Chúa trong các nhà thờ của họ.');
INSERT INTO "vn_mokhtasar" VALUES(72,19,'72:19','Và khi người bề tôi của Allah - Muhammad - đứng thờ phượng Thượng Đế của Y tại thân cây chà là thì một nhóm đông đảo Jinn chen nhau lắng nghe Y đọc Qur''an.');
INSERT INTO "vn_mokhtasar" VALUES(72,20,'72:20','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần này: quả thật Ta chỉ cầu nguyện một mình Thượng Đế của Ta và không tổ hợp với Ngài bất kỳ ai (vật) ngoài Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(72,21,'72:21','Ngươi - Thiên Sứ Muhammad - hãy nói với họ: quả thật Ta không sở hữu bất kỳ quyền năng để ngăn cản điều xấu khi Ngài muốn nó xảy đến cho các ngươi và Ta cũng không có quyền năng mang lại lợi ích cho các ngươi nếu Ngài đã muốn ban cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(72,22,'72:22','Ngươi - Muhammad - hãy nói với họ: không một ai có thể cứu Ta thoát khỏi sự trừng phạt của Allah nếu Ta trái lệnh Ngài và Ta cũng không bao giờ tìm thấy bất cứ ai ngoài Ngài để Ta nương thân.');
INSERT INTO "vn_mokhtasar" VALUES(72,23,'72:23','Ta không dám trái lệnh Ngài mà ngược lại Ta phải truyền đạt những gì Ngài ra lệnh và Bức Thông Điệp mà Ngài cử Ta mang đến cho các ngươi. Ai trái lệnh Allah và Thiên Sứ của Ngài thì quả thật điểm đến cuối cùng của y là Hỏa Ngục, nơi mà y sẽ vào sống trong đó đời đời kiếp kiếp, không bao giờ có ngày trở ra.');
INSERT INTO "vn_mokhtasar" VALUES(72,24,'72:24','Và những kẻ vô đức tin vẫn ở trên sự vô đức tin của họ cho tới khi vào Ngày Phục Sinh họ đối mặt với sự trừng phạt mà họ đã được hứa trên thế gian. Lúc đó, họ sẽ biết ai mới là kẻ yếu thế và ai mới là kẻ ít được sự giúp đỡ hơn.');
INSERT INTO "vn_mokhtasar" VALUES(72,25,'72:25','Hỡi Thiên Sứ Muhammad, Ngươi hãy nói với những kẻ thờ đa thần phủ nhận Ngày Phục Sinh: Ta không biết sự trừng phạt được hứa với các ngươi đã đến gần hay chưa hoặc Ta cũng không biết liệu Allah có trì hoãn nó lâu hơn nữa hay không?');
INSERT INTO "vn_mokhtasar" VALUES(72,26,'72:26','Allah mới là Đấng biết rõ những gì của thế giới vô hình, không một điều gì có thể che giấu được Ngài, bởi thế, Ngài không tiết lộ cho ai về điều vô hình của Ngài và những điều vô hình là của riêng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(72,27,'72:27','Ngoại trừ vị Thiên Sứ nào mà Ngài đã hài lòng thì Ngài mới tiết lộ cho Y biết theo ý muốn của Ngài và Ngài sẽ cử các Thiên Thần làm cảnh vệ cho Y để thông tin không bị tiết lộ với ai khác ngoài Y.');
INSERT INTO "vn_mokhtasar" VALUES(72,28,'72:28','Hy vọng vị Thiên Sứ đó biết rằng các vị Thiên Sứ trước Y đã thực sự truyền đạt các Bức Thông Điệp của Thượng Đế của họ theo đúng lệnh của Ngài bởi Ngài luôn quan sát và theo dõi họ. Và Allah bao quát tất cả mọi sự việc ở nơi các Thiên Thần cũng như ở nơi các vị Thiên Sứ của Ngài, bởi thế, không có điều gì có thể che giấu được Ngài, và mọi thứ đều được Ngài đếm số lượng rõ ràng cho nên không có thứ gì nằm ngoài tầm kiểm soát của Ngài cả.');
INSERT INTO "vn_mokhtasar" VALUES(73,1,'73:1','Hỡi người đang cuộn mình trong chiếc áo của y (tức Thiên Sứ Muhammad)!');
INSERT INTO "vn_mokhtasar" VALUES(73,2,'73:2','Ngươi hãy thức đêm mà đứng dâng lễ nguyện Salah trừ một ít thời gian nghỉ ngơi thôi.');
INSERT INTO "vn_mokhtasar" VALUES(73,3,'73:3','Ngươi hãy dâng lễ nguyện Salah đến nửa đêm nếu muốn hoặc hãy dâng lễ nguyện Salah với thời gian ít hơn thế hoặc ít nhất là một phần ba thời gian của đếm.');
INSERT INTO "vn_mokhtasar" VALUES(73,4,'73:4','Hoặc Ngươi hãy dâng lễ nguyện Salah trong đêm với thời gian nhiều hơn thế (3 thời gian của đêm), và khi đọc Qur''an Ngươi hãy đọc một cách nhịp nhàng và rõ chữ.');
INSERT INTO "vn_mokhtasar" VALUES(73,5,'73:5','Hỡi Thiên Sứ Muhammad, quả thật TA (Allah) sẽ sớm mặc khải xuống cho Ngươi Qur''an và Nó là lời phán nghiêm trọng bởi vì trong Nó có những bổn phận bắt buộc, những giới hạn, những luật lệ, những lễ nghĩa và đạo đức, ...');
INSERT INTO "vn_mokhtasar" VALUES(73,6,'73:6','Quả thật thức đêm để dâng lễ nguyện Salah là việc làm nặng nhọc và khó khăn nhưng sẽ làm trái tim sáng suốt và lời nói đúng đắn.');
INSERT INTO "vn_mokhtasar" VALUES(73,7,'73:7','Quả thật, vào ban ngày Ngươi phải bận rộn với nhiều công việc khiến Ngươi không có thì giờ cho việc đọc Qur''an, vì vậy, Ngươi hãy dâng lễ nguyện Salah ban đêm.');
INSERT INTO "vn_mokhtasar" VALUES(73,8,'73:8','Ngươi hãy tụng niệm Allah với các lời tụng niệm khác nhau, và hãy gác bỏ mọi chuyện đời mà toàn tâm thờ phượng Allah.');
INSERT INTO "vn_mokhtasar" VALUES(73,9,'73:9','Allah là Thượng Đế của phương Đông và Thượng Đế của phương Tây, không có Đấng nào đáng được tôn thờ ngoại trừ Ngài duy nhất. Bởi thế, Ngươi - Thiên Sứ Muhammad - hãy nhận lấy Ngài làm Đấng Bảo Hộ cho Ngươi trong tất cả mọi sự việc.');
INSERT INTO "vn_mokhtasar" VALUES(73,10,'73:10','Ngươi hãy kiên nhẫn và chịu đựng trước điều những kẻ phủ nhận đã nói từ những lời giễu cợt và xúc phạm, Ngươi cứ bỏ mặc họ một cách tốt đẹp đừng gây phiền hạ cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(73,11,'73:11','Ngươi chớ quan tâm đến vụ việc của những kẻ vô đức tin giàu có đang hưởng lạc thú trên cõi trần, Ngươi cứ để mặc TA (Allah) xử lý chúng (theo cách của TA), cứ để mặc chúng thêm một thời gian ít ỏi cho tới khi kỳ hạn của chúng chấm dứt.');
INSERT INTO "vn_mokhtasar" VALUES(73,12,'73:12','Quả thật vào Ngày Sau, ở nơi TA có những sợi dây xích nặng trịch cùng với lửa bừng bừng cháy.');
INSERT INTO "vn_mokhtasar" VALUES(73,13,'73:13','Và có thức ăn làm nghẹn cổ họng vì vị cực đắng của nó, có sự trừng phạt vô cùng đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(73,14,'73:14','Những kẻ phủ nhận sẽ đối mặt với sự trừng phạt của Ngày mà trái đất và núi non bị chấn động dữ dội, vào Ngày đó, những quả núi trở nên giống như những đồi cát tuôn xuống.');
INSERT INTO "vn_mokhtasar" VALUES(73,15,'73:15','Quả thật TA (Allah) đã cử đến với các ngươi một vị Thiên Sứ làm nhân chứng cho những việc làm của các ngươi vào Ngày Phán Xét giống như TA đã cử một vị Thiên Sứ đến với Fir''awn (Pha-ra-ông) - đó là Musa (Moses).');
INSERT INTO "vn_mokhtasar" VALUES(73,16,'73:16','Nhưng tên Fir''awn đã bất tuân vị Thiên Sứ mà Thượng Đế của hắn cử phái đến cho hắn. Cho nên, TA (Allah) đã trừng phạt hắn một cách mạnh bạo trên thế gian này, đó là đã nhấn chìm hắn xuống biển (hồng hải), và vào Ngày Sau, chắc chắn hắn sẽ bị trừng phạt trong Hỏa Ngục. Do đó, các ngươi chớ bất tuân vị Thiên Sứ được cử đến với các ngươi để rồi các ngươi sẽ gặp phải sự trừng phạt giống như hắn.');
INSERT INTO "vn_mokhtasar" VALUES(73,17,'73:17','Vậy các ngươi sẽ làm thế nào để giúp bản thân mình tránh khỏi những gì khủng khiếp xảy ra trong Ngày mà những đứa trẻ trở nên bạc đầu vì khiếp sợ nếu các ngươi cứ vô đức tin nơi Allah và phủ nhận Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(73,18,'73:18','Ngày hôm đó, bầu trời sẽ bị chẻ làm đôi từ cơn chấn động; và Ngày hôm đó, lới hứa của Allah trở thành hiện thực.');
INSERT INTO "vn_mokhtasar" VALUES(73,19,'73:19','Quả thật đây là sự khuyên răn chứa đựng những điều kinh hoàng xảy ra trong Ngày Phán Xét, nó sẽ mang lại lợi ích cho những người có đức tin. Bởi vậy, ai muốn thì hãy chọn lấy cho mình một con đường tốt đẹp để trở về với Thượng Đế của y một cách bằng an.');
INSERT INTO "vn_mokhtasar" VALUES(73,20,'73:20','Hỡi Thiên Sứ Muhammad, quả thật Thượng Đế của Ngươi biết rằng Ngươi dâng lễ nguyện Salah có lúc trong hai phần ba đêm, có lúc đến tận nửa đêm và có lúc trong một phần ba đêm. Và một nhóm người có đức tin đi theo Ngươi cũng như vậy. Và Allah là Đấng đã sắp xếp và an bày ngày và đêm, Ngài biết rõ từng thời khắc của chúng, Ngài biết các ngươi không có khả năng thức thâu đêm để dâng lễ nguyện Salah và xác định thời gian một cách tuyệt đối nên Ngài không bắt các ngươi làm nhiều hơn yêu cầu. Cho nên, Ngài đã tha thứ cho các ngươi. Bởi vậy, các ngươi hãy dâng lễ nguyện Salah trong đêm với những thời gian mà các ngươi thấy dễ dàng. Quả thật, Allah biết trong các ngươi - hỡi những người có đức tin - có người đau ốm, có ngươi phải đi đường xa để tìm kiếm bổng lộc của Allah, có người đi chiến đấu vì muốn giơ cao lời phán của Allah và họ sẽ khó khăn trong việc dâng lễ nguyện Salah ban đêm. Vì vậy, các ngươi hãy dâng lễ nguyện Salah một ít thời gian trong đêm nếu có thể, nhưng các ngươi phải chu đáo duy trì các lễ nguyện Salah bắt buộc, hãy xuất Zakah, hãy chi dùng tài sản của các ngươi cho con đường chính nghĩa của Allah; và bất cứ điều tốt nào các ngươi đã làm cho bản thân các ngươi đều sẽ được tìm thấy ở nơi Allah; các ngươi hãy cầu xin Allah tha thứ bởi quả thật Allah là Đấng Hằng Tha Thứ và Khoan Dung với những bề tôi biết quay về sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(74,1,'74:1','Hỡi người đang cuộn mình trong chiếc áo của y (tức Thiên Sứ Muhammad)!');
INSERT INTO "vn_mokhtasar" VALUES(74,2,'74:2','Ngươi hãy đứng lên và cảnh báo nhân loại về sự trừng phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(74,3,'74:3','Và Ngươi hãy tôn vinh Thượng Đế của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(74,4,'74:4','Và Ngươi hãy tẩy sạch bản thân Ngươi khỏi tội lỗi và tẩy sạch y phục của Ngươi khỏi những vết bẩn.');
INSERT INTO "vn_mokhtasar" VALUES(74,5,'74:5','Và Ngươi hãy tránh xa việc thờ phượng các bục tượng.');
INSERT INTO "vn_mokhtasar" VALUES(74,6,'74:6','Và Ngươi chớ mong Thượng Đế của Ngươi trả ơn cho việc Ngươi làm nhiều việc thiện tốt.');
INSERT INTO "vn_mokhtasar" VALUES(74,7,'74:7','Ngươi hãy kiên nhẫn chịu đựng vì Allah trước những trở ngại và khó khăn.');
INSERT INTO "vn_mokhtasar" VALUES(74,8,'74:8','Khi tiếng Còi thứ hai được hụ lên - tiếng Còi cho sự Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(74,9,'74:9','Đó sẽ là Ngày khắc nghiệt.');
INSERT INTO "vn_mokhtasar" VALUES(74,10,'74:10','Đối với những kẻ vô đức tin nơi Allah và các vị Thiên Sứ của Ngài chắc chắn sẽ không dễ dàng trong Ngày hôm đó.');
INSERT INTO "vn_mokhtasar" VALUES(74,11,'74:11','Hỡi Thiên Sứ Muhammad, Ngươi cứ để mặc TA (Allah) với kẻ mà TA đã tạo y ra trong đơn độc khi sinh ra tức không tiền bạc và không con cái - ý nói ông Al-Waleed bin Al-Mughairah).');
INSERT INTO "vn_mokhtasar" VALUES(74,12,'74:12','Và sau đó TA ban cho y nhiều tiền của.');
INSERT INTO "vn_mokhtasar" VALUES(74,13,'74:13','Và TA đã ban cho y con cái đầy đàn luôn bên cạnh y bì khối tài sản khối tài sản khổng lồ của y.');
INSERT INTO "vn_mokhtasar" VALUES(74,14,'74:14','Và TA đã làm cho y dễ dàng trong cuộc sống, bổng lộc và con cái.');
INSERT INTO "vn_mokhtasar" VALUES(74,15,'74:15','Rồi với sự vô đức tin của y, y còn muốn TA ban thêm cho y nhiều hơn sau khi TA đã ban cho y tất cả những thứ đó.');
INSERT INTO "vn_mokhtasar" VALUES(74,16,'74:16','Sự việc sẽ không như y đã suy nghĩ và mong muốn, quả thật y đã chống đối và phủ nhận các Lời Mặc Khải của TA đã ban xuống cho vị Thiên Sứ của TA.');
INSERT INTO "vn_mokhtasar" VALUES(74,17,'74:17','TA sẽ gây khó khăn cho y từ sự trừng phạt mà y không có khả năng chịu đựng.');
INSERT INTO "vn_mokhtasar" VALUES(74,18,'74:18','Quả thật, kẻ vô đức tin này, kẻ mà đã được ban cho các ân huệ đó suy tính để nói về Qur''an nhằm vô hiệu Nó và y đã mưu đồ trong bản thân y.');
INSERT INTO "vn_mokhtasar" VALUES(74,19,'74:19','Do đó, y chỉ bị nguyền rủa và bị trừng phạt dù có mưu đồ và tính toán ra sao.');
INSERT INTO "vn_mokhtasar" VALUES(74,20,'74:20','Rồi y chỉ bị nguyền rủa và bị trừng phạt dù có mưu đồ và tính toán ra sao.');
INSERT INTO "vn_mokhtasar" VALUES(74,21,'74:21','Rôi y quay lại nhìn để xem xét những gì y nói.');
INSERT INTO "vn_mokhtasar" VALUES(74,22,'74:22','Rồi y cau mày và nhăn mặt khi không tìm thấy điều nào để phỉ báng Qur''an.');
INSERT INTO "vn_mokhtasar" VALUES(74,23,'74:23','Rồi y quay lưng với đức tin Iman và ngạo mạn không chịu theo Thiên Sứ Muhammad.');
INSERT INTO "vn_mokhtasar" VALUES(74,24,'74:24','Y nói: Qur''an được Muhammad mang đến không phải là lời phán của Allah mà thật ra chỉ là bùa mê hoặc được truyền lại từ những người trước.');
INSERT INTO "vn_mokhtasar" VALUES(74,25,'74:25','Y bảo: Qur''an không phải là lời phán của Allah mà thật ra chỉ là lời của con người.');
INSERT INTO "vn_mokhtasar" VALUES(74,26,'74:26','TA (Allah) sẽ đày kẻ bất tin này vào một tầng của Hỏa Ngục, đó là Saqar, để y nếm cái nóng của nó.');
INSERT INTO "vn_mokhtasar" VALUES(74,27,'74:27','Hỡi Muhammad, Ngươi có biết Saqar là gì không?');
INSERT INTO "vn_mokhtasar" VALUES(74,28,'74:28','Saqar là nơi mà lửa của nó sẽ không chừa và bỏ sót bất cứ thứ gì từ cơ thể của người bị trừng phạt trong đó, nó sẽ đốt cháy tất cả thịt và xương, rồi sau đó thịt và xương sẽ trở lại như cũ, sự trừng phạt cứ tiệp tục như thế.');
INSERT INTO "vn_mokhtasar" VALUES(74,29,'74:29','Nó sẽ đốt cháy và làm biến dạng lớp da.');
INSERT INTO "vn_mokhtasar" VALUES(74,30,'74:30','Có mười chín vị Thiên Thần cai quản tầng Saqar.');
INSERT INTO "vn_mokhtasar" VALUES(74,31,'74:31','Và TA (Allah) chỉ dùng các Thiên Thần làm các vị cai quản Hỏa Ngục bởi con người không có khả năng chống lại họ. Và quả thật, Abu Jahal là kẻ nói dối khi hắn cho rằng hắn và đồng bọn của hắn có khả năng gây ép các Thiên Thần để họ đưa chúng ra khỏi Hỏa Ngục. Và TA ấn định số lượng của họ chỉ để thử thách những người vô đức tin nơi Allah bắt họ phải chịu sự trừng phạt được nhân lên cho điều họ đã nói; để thuyết phục những người Do Thái được ban cho Kinh Tawrah và những người Thiên Chúa được ban cho kinh Injil khi mà Qur''an được ban xuống để xác thực những điều trong hai Kinh sách đó; để gia tăng đức tin cho những người có đức tin khi những người của Kinh Sách tương đồng với họ; và để những người vẫn còn có sự nghi ngờ trong đức tin cũng như những người vô đức tin nói: Allah muốn điều gì ở việc đưa ra con số kỳ quặc thế này?! Giống như Ngài muốn làm lầm lạc những kẻ phủ nhận số lượng này đồng thời hướng dẫn những người có đức tin nơi nó. Allah làm ai lầm lạc tùy ý Ngài và hướng dẫn ai tùy ý Ngài. Và không ai biết rõ số lượng của các Thiên Thần của Ngài ngoại trừ một mình Ngài. Bởi thế, Abu Jahal - kẻ đã nói một cách khinh thường và phủ nhận: "Chẳng lẻ Muhammad chỉ có vị hỗ trợ thôi sao?!" - hãy biết rằng đó chỉ là lời nhắc nhở loài người để họ ý thức được sự vĩ đại của Allah như thế nào mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(74,32,'74:32','Thật sự không giống như lời mà một số kẻ thờ đa thần đã khẳng định rằng chúng đủ sức chống lại những vị cai quản Hỏa Ngục. Allah thề bởi mặt trăng.');
INSERT INTO "vn_mokhtasar" VALUES(74,33,'74:33','Allah thề bởi ban đêm khi nó rời đi.');
INSERT INTO "vn_mokhtasar" VALUES(74,34,'74:34','Allah thề bởi bình minh sáng tỏ.');
INSERT INTO "vn_mokhtasar" VALUES(74,35,'74:35','Quả thật, Hỏa Ngục là một đại họa.');
INSERT INTO "vn_mokhtasar" VALUES(74,36,'74:36','Một sự cảnh báo đáng sợ đến loài người.');
INSERT INTO "vn_mokhtasar" VALUES(74,37,'74:37','Hỡi nhân loại, để ai trong các ngươi muốn tiên phong bởi đức tin nơi Allah và làm viện thiện tốt hoặc lùi về phía sau bởi sự vô đức tin và bất tuân Allah.');
INSERT INTO "vn_mokhtasar" VALUES(74,38,'74:38','Mỗi con người sẽ bị nhốt lại trong những việc làm xấu của họ, cho dù họ có làm điều tốt đi chăng nữa thì cũng không thoát khỏi được sự trừng phạt ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(74,39,'74:39','Ngoại trừ những người có đức tin mới không bị bắt tội bởi những tội lỗi của họ. Chỉ có họ mới được xí xóa tội lỗi bởi những việc làm thiện tốt của họ.');
INSERT INTO "vn_mokhtasar" VALUES(74,40,'74:40','Vào Ngày Phán Xét, ở trong các Ngôi Vườn Thiên Đàng, họ hỏi nhau.');
INSERT INTO "vn_mokhtasar" VALUES(74,41,'74:41','Những người ở trong các Ngôi Vườn Thiên Đàng hỏi nhau về những người vô đức tin, những người đã hủy diệt bản thân họ bởi các việc làm bất tuân của họ.');
INSERT INTO "vn_mokhtasar" VALUES(74,42,'74:42','Những người trong Thiên Đàng hỏi những người trong Hỏa Ngục: điều gì đã đưa các người vào Hỏa Ngục vậy?');
INSERT INTO "vn_mokhtasar" VALUES(74,43,'74:43','Những kẻ vô đức tin trả lời: Bởi vì chúng tôi đã không duy trì lễ nguyện Salah bắt buộc trên cuộc sống trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(74,44,'74:44','Và bởi vì chúng tôi đã không nuôi ăn những người nghèo từ bổng lộc mà Allah đã ban cho chúng tôi.');
INSERT INTO "vn_mokhtasar" VALUES(74,45,'74:45','Và bởi vì chúng tôi đã thường ở cùng với những kẻ sai quấy và chúng tôi đã thường cùng những kẻ lầm lạc nói chuyện tầm phào vô bổ.');
INSERT INTO "vn_mokhtasar" VALUES(74,46,'74:46','Và chúng tôi luôn không tin nơi Ngày Thưởng Phạt.');
INSERT INTO "vn_mokhtasar" VALUES(74,47,'74:47','Chúng tôi cứ vô đức và bất tuân cho tới khi cái chết đến với chúng tôi, và chúng tôi đã không còn cơ hội quay đầu sám hối.');
INSERT INTO "vn_mokhtasar" VALUES(74,48,'74:48','Vào Ngày Phán Xét, sẽ không có một sự can thiệp nào có thể giúp được họ, dù đó là các vị ngoan đạo đức hạnh, các vị Thiên Sứ, hay các vị Thiên thần đi chăng nữa; bởi lẽ điều kiện được chấp nhận sự can thiệp là có sự hài lòng của Đấng Độ Lượng cho phép.');
INSERT INTO "vn_mokhtasar" VALUES(74,49,'74:49','Vậy điều gì đã làm cho những kẻ thờ đa thần này quay lưng với Qur''an kia chứ?');
INSERT INTO "vn_mokhtasar" VALUES(74,50,'74:50','Chúng quay lưng bỏ chạy khỏi Qur''an giống như những con ngựa vằn khiếp đảm.');
INSERT INTO "vn_mokhtasar" VALUES(74,51,'74:51','Tháo chạy trước con sư tử vì khiếp sợ.');
INSERT INTO "vn_mokhtasar" VALUES(74,52,'74:52','Không, mỗi tên trong những kẻ thờ đa thần này muốn ngay trên đầu của chúng có từng tờ Kinh được trải ra thông tin cho chúng biết rằng Muhammad là Thiên Sứ được Alalh phái đến. Đó không phải là do ít bằng chứng hoặc thiếu cơ sở để chứng mình mà thật ra chỉ vì chúng tự cao và ngạo mạn.');
INSERT INTO "vn_mokhtasar" VALUES(74,53,'74:53','Sự việc không phải như thế, nguyên nhân là bởi vì chúng ngoan cố trong sự lầm lạc, bởi vì chúng không tin có sự trừng phạt của Ngày Sau, nên chúng cứ ở trên sự vô đức tin của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(74,54,'74:54','Không, quả thật Qur''an này đây là một sự khuyên răn và nhắc nhở.');
INSERT INTO "vn_mokhtasar" VALUES(74,55,'74:55','Bởi thế, ai muốn thì hãy đọc Qur''an và ghi nhớ những lời khuyên răn và nhắc nhở của Nó.');
INSERT INTO "vn_mokhtasar" VALUES(74,56,'74:56','Và không ai được khuyên răn và nhắc nhở từ Qur''an ngoài trừ những ai mà Allah muốn, Ngài là Đấng phải được kính sợ để thực hiện theo mệnh lệnh của Ngài và tránh xa những điều Ngài nghiêm cấm, và Ngài là Đấng tha thứ các tội lỗi cho đám bề tôi của Ngài khi họ thành tâm quay về sám hối với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(75,1,'75:1','Allah thề bởi Ngày Phục Sinh, Ngày mà nhân loại sẽ đứng dậy (từ cõi chết) để trình diện Thượng Đế của vũ trụ và muôn loài.');
INSERT INTO "vn_mokhtasar" VALUES(75,2,'75:2','Allah thề bởi linh hồn tốt lành thường trách cứ thân xác của nó về việc lơ là, bê tha các việc làm ngoan đạo và thiện tốt, về việc đã làm những điều xấu và tội lỗi. Ngài thề bởi hai thứ này rằng Ngài sẽ phục sinh nhân loại cho sự phán xét và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(75,3,'75:3','Chẳng lẽ con người tưởng rằng TA (Allah) không thể nào tập hợp được xương cốt của y lại sau khi y chết đi để phục sinh y lần nữa y?!');
INSERT INTO "vn_mokhtasar" VALUES(75,4,'75:4','Đúng vậy, chắc chắn TA (Allah) thừa sức tập hợp lại tất cả các bộ phận của cơ thể y, TA thừa sức lắp ráp lại từng đầu ngón tay, ngón chân của y một cách hoàn hảo như ban đầu.');
INSERT INTO "vn_mokhtasar" VALUES(75,5,'75:5','Không, thật ra con người phủ nhận sự phục sinh vì chỉ muốn ngoan cố tiếp tục trên tội lỗi mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(75,6,'75:6','Con người thường hỏi để khẳng định Ngày Phục Sinh rất xa vời (hoặc viển vông): bao giờ nó đến?');
INSERT INTO "vn_mokhtasar" VALUES(75,7,'75:7','Bởi thế, khi cặp mắt lúng túng và kinh ngạc trước việc diễn ra những điều mà họ đã phủ nhận.');
INSERT INTO "vn_mokhtasar" VALUES(75,8,'75:8','Và khi mặt trăng bị mất đi ánh sáng.');
INSERT INTO "vn_mokhtasar" VALUES(75,9,'75:9','Và khi mặt trời và mặt trăng nhập lại với nhau.');
INSERT INTO "vn_mokhtasar" VALUES(75,10,'75:10','Vào Ngày hôm đó, con người tội lỗi sẽ nói trong hối tiếc muộn màng: có nơi nào để thoát thân đây?');
INSERT INTO "vn_mokhtasar" VALUES(75,11,'75:11','Vào Ngày hôm đó chắc chắn sẽ không có nơi nào để thoát thân cả, kẻ tội lỗi sẽ không có bất cứ nơi nào để nương thân.');
INSERT INTO "vn_mokhtasar" VALUES(75,12,'75:12','Hỡi Thiên Sứ Muhammad, vào Ngày hôm đó, chỉ có một nơi duy nhất mà các ngươi phải có mặt chính là trình diện Thượng Đế của Ngươi để Ngài phán xét và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(75,13,'75:13','Vào Ngày hôm đó, con người sẽ được cho biết những việc làm mà y đã gởi đi trước cho cuộc sống của y ở Đời Sau và những việc làm mà y còn bỏ lại tức không làm.');
INSERT INTO "vn_mokhtasar" VALUES(75,14,'75:14','Không, thật ra con ngươi thấy rõ bản thân mình nhất vì y luôn chứng kiến thân xác của y phạm tội như thế nào.');
INSERT INTO "vn_mokhtasar" VALUES(75,15,'75:15','Cho dù con người cứ quanh co chối tội thì chẳng ích lợi gì, bởi vì đó chỉ là viện cơ chạy tội thôi.');
INSERT INTO "vn_mokhtasar" VALUES(75,16,'75:16','Hỡi Thiên Sứ Muhammad, Ngươi chớ đừng hoạt động chiếc lưỡi của Ngươi quá để đọc Qur''an vì sợ Nó vuột mất.');
INSERT INTO "vn_mokhtasar" VALUES(75,17,'75:17','Ngươi - Muhammad - chớ hấp tấp trong việc đọc để ghi nhớ Qur''an, bởi vì chính TA (Allah) có nhiệm vụ phải tập hợp Nó lại trong lòng của Ngươi và sẽ để Nó có thể được đọc trên chiếc lưỡi của Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(75,18,'75:18','Khi nào Jibril đọc Qur''an cho Ngươi nghe thì Ngươi hãy im lặng lắng nghe và đọc theo là được.');
INSERT INTO "vn_mokhtasar" VALUES(75,19,'75:19','Rồi, TA (Allah) sẽ giảng giải cho Ngươi về nội dung ý nghĩa của Nó.');
INSERT INTO "vn_mokhtasar" VALUES(75,20,'75:20','Không, vấn đề sẽ không giống như những gì các ngươi đã khẳng định rằng sự phục sinh khó xảy ra, bởi vì các ngươi thừa biết - hỡi con người - rằng Allah toàn năng trong việc tạo hóa các ngươi lúc ban đầu chắc chắn thừa sức dựng các ngươi sống lại từ cái chết, tuy nhiên, nguyên nhân các ngươi phủ nhận sự phục sinh là chỉ vì các ngươi quá yêu cuộc sống trần gian với nhịp độ nhanh này mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(75,21,'75:21','Các ngươi chối bỏ cuộc sống cõi Đời Sau bằng cách không thực hiện những gì Allah ra lệnh và không chịu từ bỏ những gì Ngài nghiêm cấm.');
INSERT INTO "vn_mokhtasar" VALUES(75,22,'75:22','Vào Ngày hôm đó, gương mặt của những người vô đức tin sẽ sáng ngời vì hạnh phúc và vì được ban cho ánh hào quang.');
INSERT INTO "vn_mokhtasar" VALUES(75,23,'75:23','Họ được ngắm nhìn Thượng Đế của họ, họ sẽ được hưởng thụ trong sự việc đó.');
INSERT INTO "vn_mokhtasar" VALUES(75,24,'75:24','Vào Ngày hôm đó, gương mặt của những kẻ vô đức tin cũng như những kẻ tội lỗi sẽ nhăn nhó và buồn thảm vì hoang mang và lo sợ.');
INSERT INTO "vn_mokhtasar" VALUES(75,25,'75:25','Họ lo sợ hình phạt đau đớn sắp giáng lên họ.');
INSERT INTO "vn_mokhtasar" VALUES(75,26,'75:26','Sự việc không giống như những gì mà những kẻ thờ đa thần đã tưởng tượng ra rằng họ sẽ không bị trừng phạt khi chết đi. Nhưng rồi khi linh hồn đi lên đến tận quay xanh (thì họ sẽ biết).');
INSERT INTO "vn_mokhtasar" VALUES(75,27,'75:27','Lúc đó, một số người bảo nhau: Ai là thầy thuốc, hãy chữa trị cho y mong rằng y sẽ khỏi?!');
INSERT INTO "vn_mokhtasar" VALUES(75,28,'75:28','Và lúc này, y mới chắc chắn mình ly biệt thế giới trần gian bởi cái chết.');
INSERT INTO "vn_mokhtasar" VALUES(75,29,'75:29','Sự tiếp giáp của những cơn đau cuối cùng của thế gian với sự bắt đầu của thế giới cõi Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(75,30,'75:30','Khi xảy ra điều đó (hai cẳng chân nhập vào nhau - tức không đi được nữa) thì đấy là cái chết đưa một người trở về với Thượng Đế của y.');
INSERT INTO "vn_mokhtasar" VALUES(75,31,'75:31','Kẻ vô đức tin không tin những gì mà Thiên Sứ của Allah mang đến cũng như không chịu dâng lễ nguyện Salah thờ phượng Allah.');
INSERT INTO "vn_mokhtasar" VALUES(75,32,'75:32','Ngược lại, y còn phủ nhận những gì Thiên Sứ của Allah mang đến và tìm cách chống đối Người.');
INSERT INTO "vn_mokhtasar" VALUES(75,33,'75:33','Sau đó, kẻ vô đức tin này trở về với gia đình trong điệu bộ tự cao tự đại.');
INSERT INTO "vn_mokhtasar" VALUES(75,34,'75:34','Allah cảnh báo kẻ vô đức tin rằng sự trừng phạt chắc chắn xảy ra và đang đến gần với y.');
INSERT INTO "vn_mokhtasar" VALUES(75,35,'75:35','Lặp lại câu nói để khẳng định sự việc trừng phạt chắc chắn sẽ đến với y.');
INSERT INTO "vn_mokhtasar" VALUES(75,36,'75:36','Chẳng lẽ con người tưởng rằng Allah bỏ mặc y muốn làm gì thì làm mà không ban hành một giới hạn giáo luật nào sao?');
INSERT INTO "vn_mokhtasar" VALUES(75,37,'75:37','Chẳng lẽ con người đã quên rằng đã từng có một ngày y chỉ là giọt tinh dịch được xuất ra và trôi vào tử cung của người phụ nữ hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(75,38,'75:38','Rồi sau đó, từ tinh dịch biến thành một cục máu đặc rồi Allah tạo hóa thành một hình hài con ngươi hoàn chỉnh.');
INSERT INTO "vn_mokhtasar" VALUES(75,39,'75:39','Và từ gọt tinh dịch Allah làm ra hai giới tính: nam và nữ.');
INSERT INTO "vn_mokhtasar" VALUES(75,40,'75:40','Chẳng lẽ Đấng đã tạo ra con người từ giọt tinh dịch rồi thành cục máu đặc như thế lại không có khả năng làm sống lại người chết để Ngài phán xét và thưởng phạt ư? Không, chắc chắn Ngài thừa sức làm điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(76,1,'76:1','Quả thật, con người đã từng trải qua một thời kỳ dài không là cái gì được nhắc đến cả.');
INSERT INTO "vn_mokhtasar" VALUES(76,2,'76:2','Quả thật, TA (Allah) đã tạo con người từ một dạng dung dịch: tinh dịch của người đàn ông và tinh dịch của người phụ nữ. TA thử thách con người bởi những điều mà TA qui định cho y. Thế nên, TA đã tạo cho y khả năng nghe và thấy để y chấp hành những gì mà TA đã qui định.');
INSERT INTO "vn_mokhtasar" VALUES(76,3,'76:3','Quả thật, TA (Allah) đã trình bày cho con người trên chiếc lưỡi của các vị Thiên Sứ của TA về con đường được hướng dẫn và cảnh báo về con đường lầm lạc. Nhưng sau đó, hoặc là con người được hướng dẫn đến con đường ngay chính và trở thành một bề tôi có đức tin đầy lòng biết ơn đối với TA, hoặc là con người đi theo con đường lầm lạc và trở thành một bề tôi vô đức tin phủ nhận các lời mặc khải của TA.');
INSERT INTO "vn_mokhtasar" VALUES(76,4,'76:4','Quả thật, TA (Allah) đã chuẩn bị cho những kẻ vô ơn, phủ nhận Allah và các Thiên Sứ của Ngài những sợi dây xích để trói chúng và kéo chúng vào Hỏa Ngục, những cái gông cùm để còng cổ và tay chúng lại với nhau, và lửa cực mạnh để thiêu đốt chúng.');
INSERT INTO "vn_mokhtasar" VALUES(76,5,'76:5','Những người có đức tin tuân lệnh Allah vào Ngày Phán Xét sẽ được uống từ tách rượu pha có hương thơm Kafur (Long não) để có mùi thơm tuyệt diệu.');
INSERT INTO "vn_mokhtasar" VALUES(76,6,'76:6','Loại thức uống được chuẩn bị cho những người tuân lệnh này đây (hỗn hợp rượu mang hương thơm Kafur) được lấy từ con suối rất thuận tiện để múc. Các bề tôi ngoan đạo của Allah sẽ thoải mãi uống từ con suối này. Và con suối này sẽ phun ra từ bất cứ nơi nào họ muốn.');
INSERT INTO "vn_mokhtasar" VALUES(76,7,'76:7','Và thuộc tính của những bề tôi được diễm phúc uống từ con suối đó: thực hiện những gì mà họ đã nguyện thề từ các việc làm ngoan đạo, sợ cho ngày mà những điều xấu sẽ lan tràn khắp nơi; và đó là Ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(76,8,'76:8','Và họ nuôi ăn người nghèo, trẻ mồ côi và tù binh các loại thức ăn mà bản thân họ yêu thích vì họ cần đến nó và thèm khát nó, (nhưng vì Allah) họ chấp nhận chia sẻ nó với những người cần nó hơn.');
INSERT INTO "vn_mokhtasar" VALUES(76,9,'76:9','Và bản thân họ cảm thấy vui sướng trong việc nuôi ăn những người đó là vì muốn được sự hài lòng của Allah, chứ họ không hề mong muốn những người đó biết ơn và trả ơn hoặc muốn những lời khen ngợi và ca tụng từ những người đó.');
INSERT INTO "vn_mokhtasar" VALUES(76,10,'76:10','(Họ nói): Quả thật chúng tôi sợ Thượng Đế của chúng tôi vào một Ngày mà những gương mặt sẽ trở nên buồn thảm trước những nỗi sợ hãi.');
INSERT INTO "vn_mokhtasar" VALUES(76,11,'76:11','Cho nên Allah, với hồng phúc của Ngài, sẽ cứu rỗi họ khỏi những điều xấu diễn ra của Ngày hôm đó, Ngài sẽ ban cho họ ánh hào quang trên gương mặt, một sự ưu đãi dành riêng cho họ và Ngài làm cho họ cảm thấy vui sướng trong trái tim của họ.');
INSERT INTO "vn_mokhtasar" VALUES(76,12,'76:12','Allah sẽ ban thưởng cho họ Thiên Đàng để họ hưởng thụ và lụa là để họ mặc bởi họ đã kiên nhẫn chịu đựng đối với những điều tuân lệnh Ngài cũng như trên điều mà Ngài đã an bài và định đoạt.');
INSERT INTO "vn_mokhtasar" VALUES(76,13,'76:13','Trong Thiên Đàng, họ sẽ nằm nghỉ trên những chiếc trang kỷ được phủ lên bởi những tấm vải tuyệt đẹp. Trong Thiên Đàng họ sẽ không còn nhìn thấy cái nóng của mặt trời cũng như những cơn lạnh rét buốt, mà trong đó lúc nào cũng chỉ là bóng râm và sự mát mẻ dễ chịu.');
INSERT INTO "vn_mokhtasar" VALUES(76,14,'76:14','Bóng mát của ngôi vườn Thiên Đàng che họ gần trên đầu, trái của nó thì lủng lẳng gần tầm tay hái, họ có thể hái trái ăn lúc nào tùy thích và thuận tiện, họ đứng, ngồi hay nằm đều có thể với tay hái được một cách dễ dàng.');
INSERT INTO "vn_mokhtasar" VALUES(76,15,'76:15','Những người hầu hạ và phục dịch sẽ mang những chiếc bình bạc và những chiếc ly thủy tinh đi quanh họ để họ uống khi họ muốn.');
INSERT INTO "vn_mokhtasar" VALUES(76,16,'76:16','Những chiếc ly thật ra có chất liệu từ bạc nhưng màu sắc của nó lại giống pha lê do sự trong suốt của chúng, và những chiếc lý này đã được định lượng một cách vừa đủ cho người uống cảm thấy thoải mái nhất, không quá nhiều cũng không quá ít.');
INSERT INTO "vn_mokhtasar" VALUES(76,17,'76:17','Những người vinh dự này (cư dân Thiên Đàng) sẽ được rót cho uống những chiếc cốc rượu được pha chế từ gừng.');
INSERT INTO "vn_mokhtasar" VALUES(76,18,'76:18','Và trong Thiên Đàng, có một loại thức uống nữa được múc từ một con suối có tên gọi là Salsabil.');
INSERT INTO "vn_mokhtasar" VALUES(76,19,'76:19','Trong Thiên Đàng, họ có những thiếu niên làm công việc phục vụ và hầu hà đi vòng quanh. Những thiếu niên mãi mãi luôn trẻ và rất xinh đẹp, khi Ngươi (Muhammad) nhìn thấy chúng Ngươi cứ ngỡ cúng là những hạt trân châu rải rác khắp nơi vì chúng đẹp và nhiều.');
INSERT INTO "vn_mokhtasar" VALUES(76,20,'76:20','Và khi Ngươi (Muhammad) nhìn thấy những gì trong Thiên Đàng thì chắc chắn Ngươi sẽ thấy đó là một niềm hạnh phúc bất tận và là một vương quốc vô cùng vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(76,21,'76:21','Y phục của họ là lụa mịn màu xanh lá cây rất quý phái, họ sẽ đeo những chiếc vòng bạc và Allah sẽ cho họ uống những loại thức uống tinh khiết và thơm ngon.');
INSERT INTO "vn_mokhtasar" VALUES(76,22,'76:22','Và có tiếng bảo đầy vinh dự cho họ: Quả thật, ân huệ mà các ngươi được ban cho này đây là một phần thưởng vì những việc làm ngoan đạo của các ngươi, và những việc làm của các ngươi đã được chấp nhận nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(76,23,'76:23','Hỡi Thiên Sứ Muhammad, quả thật, TA (Allah) đã ban Qur''an xuống cho Ngươi theo từng giai đoạn chứ TA không ban Nó xuống trong một lần duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(76,24,'76:24','Bởi thế, Ngươi (Muhammad) hãy kiên nhẫn chịu đựng trên những gì mà Allah đã qui định và ban hành, Ngươi chớ nghe theo bất cứ tên tội lỗi nào kêu gọi Ngươi và Ngươi chớ đi theo bất kỳ kẻ vô đức tin nào kêu gọi Ngươi đến với sự vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(76,25,'76:25','Và Ngươi (Muhammad) hãy tụng niệm Thượng Đế của Ngươi bằng lễ nguyện Salah Fajr của buổi sáng sớm và lễ nguyện Salah Zhuhr và Asr của buổi chiều.');
INSERT INTO "vn_mokhtasar" VALUES(76,26,'76:26','Và Ngươi hãy tụng niệm Allah bằng lễ nguyện Salah của đêm: lễ nguyện Salah Maghrib, lễ nguyện Salah ''I-sha'' và các lễ nguyện Salah Tahajjud.');
INSERT INTO "vn_mokhtasar" VALUES(76,27,'76:27','Quả thật, những kẻ thờ đa thân này yêu thích và chỉ quan tâm cuộc sống trần tục này, họ bỏ Ngày Phục Sinh ở phía sau lưng họ trong khi đó là Ngày rất nặng nề mà họ phải đối mặt.');
INSERT INTO "vn_mokhtasar" VALUES(76,28,'76:28','TA (Allah) đã tạo ra chúng và ban cho chúng sức mạnh từ xương khớp và các bộ phận cơ thể của chúng. Và nếu TA muốn TA sẽ hủy diệt chúng và tạo ra một nhóm khác thay thế chúng.');
INSERT INTO "vn_mokhtasar" VALUES(76,29,'76:29','Quả thật chương Kinh này là lời nhắc nhở và cảnh tỉnh. Bởi thế, ai muốn lấy một con đường để đến với sự hài lòng của Allah thì hãy nắm bắt lấy nó.');
INSERT INTO "vn_mokhtasar" VALUES(76,30,'76:30','Việc các ngươi muốn lấy một con đường để đến với sự hài lòng của Allah chắc chắn sẽ không thành trừ phi Ngài muốn điều đó cho các ngươi, bởi lẽ tất cả mọi sự việc đều phụ thuộc nơi Ngài. Quả thật, Allah là Đấng Hằng Biết điều gì cải thiện đám bề tôi của Ngài và điều gì tốt cho họ, Ngài là Đấng Sáng Suốt, Cao Minh và Thông Thái nhất trong việc sắp đặt, an bài và định đoạt.');
INSERT INTO "vn_mokhtasar" VALUES(76,31,'76:31','Ngài thu nhận vào Thiên Đàng của Ngài đám bề tôi nào Ngài muốn, nếu Ngài muốn đám bề tôi nào thì Ngài sẽ phù hộ họ đến với đức tin và việc làm ngoan đạo; và Ngài đã chuẩn bị cho những kẻ làm điều bất công với chính bản thân họ bằng sự vô đức tin và trái lệnh bởi một sự trừng phạt vô cùng đau đớn ở cõi Đời Sau, đó là sự trừng phạt nơi Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(77,1,'77:1','Allah thề bởi những luồng gió được lùa đi, luồng này tiếp theo sau luồng kia.');
INSERT INTO "vn_mokhtasar" VALUES(77,2,'77:2','Allah thề bởi những trận gió mạnh mang sự tàn phá và hủy diệt.');
INSERT INTO "vn_mokhtasar" VALUES(77,3,'77:3','Allah thề bởi các luồng gió gieo mưa (đến nơi nào Ngài muốn).');
INSERT INTO "vn_mokhtasar" VALUES(77,4,'77:4','Allah thề bởi các Thiên Thần mang những điều phân biệt giữa chân lý và sự ngụy tạo.');
INSERT INTO "vn_mokhtasar" VALUES(77,5,'77:5','Allah thề bởi các Thiên Thần mang Lời Mặc Khải của Ngài phổ biến khắp nơi.');
INSERT INTO "vn_mokhtasar" VALUES(77,6,'77:6','Các Lời Mặc Khải của Allah được ban xuống như một cái cớ từ nơi Allah đến với nhân loại và như một sự cảnh báo về sự trừng phạt của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(77,7,'77:7','Quả thật những gì mà các ngươi đã được hứa từ việc phục sinh, phán xét và thưởng phạt chắc chắn sẽ diễn ra đúng như vậy.');
INSERT INTO "vn_mokhtasar" VALUES(77,8,'77:8','Khi các vì sao mất đi ánh sáng.');
INSERT INTO "vn_mokhtasar" VALUES(77,9,'77:9','Khi bầu trời bị chẻ làm đôi để các Thiên Thần đi xuống.');
INSERT INTO "vn_mokhtasar" VALUES(77,10,'77:10','Và khi những quả núi bị bứng đi khỏi vị trí của nó và bị thổi bay tứ tán như những hạt bụi.');
INSERT INTO "vn_mokhtasar" VALUES(77,11,'77:11','Và Khi các vị Thiên Sứ được tập hợp trong một thời điểm được ấn định.');
INSERT INTO "vn_mokhtasar" VALUES(77,12,'77:12','Cho một ngày vĩ đại đã được ấn định để làm chứng cho các cộng đồng của họ.');
INSERT INTO "vn_mokhtasar" VALUES(77,13,'77:13','Cho Ngày phân loại giữa đám bề tôi, ai thực sự theo chân lý và ai thực sự đi theo điều ngụy tạo, ai thực sự hạnh phúc và ai thực sự là những kẻ bất hạnh.');
INSERT INTO "vn_mokhtasar" VALUES(77,14,'77:14','Và điều gì cho Ngươi - hỡi Thiên Sứ Muhammad - biết về Ngày Phân Loại?!');
INSERT INTO "vn_mokhtasar" VALUES(77,15,'77:15','Ngày hôm đó là ngày của sự hủy diệt, trừng phạt và sự thất bại thảm hại cho những kẻ phủ nhận những điều mà các vị Thiên Sứ mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(77,16,'77:16','Chẳng phải TA (Allah) đã hủy diệt những cộng đồng trước đây bởi chúng đã vô đức tin nơi Allah và phủ nhận các vị Thiên Sứ của Ngài đó sao?!');
INSERT INTO "vn_mokhtasar" VALUES(77,17,'77:17','Sau đó, TA tiếp tục trừng phạt những kẻ phủ nhận sau này, TA sẽ tiêu diệt chúng giống như đã tiêu diệt những người trước.');
INSERT INTO "vn_mokhtasar" VALUES(77,18,'77:18','TA sẽ tiêu diệt những kẻ phủ nhận những gì Muhammad -cầu xin Allah bang sự bình an đến Người- mang đến giống như TA đã tiêu diệt những kẻ tội lỗi của các cộng đồng trước.');
INSERT INTO "vn_mokhtasar" VALUES(77,19,'77:19','Sự hủy diệt, sự trừng phạt và sự thất bại thảm hại trong Ngày hôm đó là dành cho những kẻ phủ nhận lời cảnh báo và đe dọa của Allah về sự trừng phạt những kẻ tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(77,20,'77:20','Hỡi con người, chẳng phải TA đã tạo các ngươi từ một loại chất dịch ít ỏi đáng khinh - tinh dịch của người đàn ông và tinh dịch của người phụ nữ - đó sao?!');
INSERT INTO "vn_mokhtasar" VALUES(77,21,'77:21','TA (Allah) đã đặt loại dung dịch đó tại một nơi an toàn, đó là tử cung của người phụ nữ.');
INSERT INTO "vn_mokhtasar" VALUES(77,22,'77:22','Đến một kỳ hạn ấn định, đó là thời gian mang thai.');
INSERT INTO "vn_mokhtasar" VALUES(77,23,'77:23','TA đã định đoạt các thuộc tính của đứa trẻ, TA qui định cho nó màu da và những đặc điểm khác. Bởi quả thật TA là Đấng định đoạt tất cả mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(77,24,'77:24','Sự hủy diệt, sự trừng phạt và sự thất bại thảm hại trong Ngày hôm đó là dành cho những kẻ phủ nhận quyền năng của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(77,25,'77:25','Chẳng phải TA đã làm cho trái đất như một chiếc túi chứa tất cả nhân loại đó sao?');
INSERT INTO "vn_mokhtasar" VALUES(77,26,'77:26','Trái đất chứa đựng toàn nhân loại: chứa đựng người sống bằng những chỗ trú ngụ trên bề mặt (nhà cửa) và chứa đựng người chết bởi sự chôn cất vào trong lòng đất.');
INSERT INTO "vn_mokhtasar" VALUES(77,27,'77:27','Và TA (Allah) đã tạo ra những quả núi cao vững chắc để giữ cho mặt đất không bị rung chuyển, và TA đã ban cho các ngươi - hỡi con người - nguồn nước ngọt. Cho nên, Đấng đã tạo ra điều đó sẽ không bất lực trước việc phục sinh các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(77,28,'77:28','Sự hủy diệt, sự trừng phạt và sự thất bại thảm hại trong Ngày hôm đó là dành cho những kẻ phủ nhận hồng ân của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(77,29,'77:29','Và có tiếng bảo dành cho những kẻ phủ nhận những điều mà các vị Thiên Sứ của Allah mang đến: Hỡi những kẻ phủ nhận, các ngươi hãy đi đến sự trừng phạt (Hỏa Ngục) cái mà các ngươi đã phủ nhận!');
INSERT INTO "vn_mokhtasar" VALUES(77,30,'77:30','Các ngươi hãy đi đến bóng tối của khói Hỏa Ngục từ ba ống trụ.');
INSERT INTO "vn_mokhtasar" VALUES(77,31,'77:31','Trong đó, không có sự mát dịu của bóng râm và cũng không có gì để làm dịu cơn nóng.');
INSERT INTO "vn_mokhtasar" VALUES(77,32,'77:32','Quả thật, Hỏa Ngục hà lửa ra và mỗi ngọn lửa được Nó hà ra giống như chiến pháo đài khổng lồ.');
INSERT INTO "vn_mokhtasar" VALUES(77,33,'77:33','Mỗi ngọn lửa được Hỏa Ngục hà ra, màu đen vàng của nó giống như con là đà đen.');
INSERT INTO "vn_mokhtasar" VALUES(77,34,'77:34','Sự hủy diệt, sự trừng phạt và sự thất bại thảm hại trong Ngày hôm đó là dành cho những kẻ phủ nhận hình phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(77,35,'77:35','Ngày hôm đó, chúng sẽ không còn khả năng nói chuyện bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(77,36,'77:36','Họ sẽ không được phép khiếu nại với Thượng Đế của họ bất cứ điều gì cho sự vô đức tin và việc làm tội lỗi của họ.');
INSERT INTO "vn_mokhtasar" VALUES(77,37,'77:37','Sự hủy diệt, sự trừng phạt và sự thất bại thảm hại trong Ngày hôm đó là dành cho những kẻ phủ nhận những thông tin của Ngày này.');
INSERT INTO "vn_mokhtasar" VALUES(77,38,'77:38','Đây là Ngày phân loại giữa nhân loại, nhóm của các ngươi và cả các cộng đồng trước đây tại cùng một nơi triệu tập.');
INSERT INTO "vn_mokhtasar" VALUES(77,39,'77:39','Nếu các ngươi có mưu định nào đó thì các ngươi hãy dùng nó cứu rỗi các ngươi khỏi sự trừng phạt của Allah, các ngươi hãy mưu định với TA đi!');
INSERT INTO "vn_mokhtasar" VALUES(77,40,'77:40','Sự hủy diệt, sự trừng phạt và sự thất bại thảm hại trong Ngày hôm đó là dành cho những kẻ phủ nhận Ngày Phân Loại.');
INSERT INTO "vn_mokhtasar" VALUES(77,41,'77:41','Quả thật những người ngoan đạo sợ Allah, luôn chấp hành các mệnh lệnh của Ngài cũng như tránh xa những điều cấm của Ngài, sẽ ở nơi bóng mát của Ngôi Vườn Thiên Đàng cùng với các dòng suối nước ngọt.');
INSERT INTO "vn_mokhtasar" VALUES(77,42,'77:42','Có đủ loại trái cây mà họ ưa thích để họ ăn hả hê.');
INSERT INTO "vn_mokhtasar" VALUES(77,43,'77:43','Và có tiếng bảo họ: Các ngươi hãy ăn những thức ăn tốt lành, hãy uống những thức uống thơm ngon một cách thỏa thích bởi những việc làm thiện tốt mà các ngươi đã làm trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(77,44,'77:44','Quả thật, giống như phần thưởng mà TA dùng làm phần thưởng cho các ngươi như thế này, TA sẽ trọng thưởng cho những ai làm những việc tốt đẹp đúng như thế.');
INSERT INTO "vn_mokhtasar" VALUES(77,45,'77:45','Sự hủy diệt, sự trừng phạt và sự thất bại thảm hại trong Ngày hôm đó là dành cho những kẻ phủ nhận những gì mà Allah đã chuẩn bị cho những người ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(77,46,'77:46','Những kẻ phủ nhận được bảo: các ngươi cứ ăn uống và hưởng thụ cuộc đời thế tục trong một thời gian ngắn ngủi, rồi đây chắc chắn các ngươi sẽ là những kẻ tội lỗi vì đã phủ nhận Allah và các vị Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(77,47,'77:47','Sự hủy diệt, sự trừng phạt và sự thất bại thảm hại trong Ngày hôm đó là dành cho những kẻ phủ nhận sự ban thưởng của Ngày này.');
INSERT INTO "vn_mokhtasar" VALUES(77,48,'77:48','Và khi những kẻ phủ nhận này được bảo hãy dâng lễ nguyện Salah thờ phượng Allah thì chúng không chịu lễ nguyện Salah thờ phượng Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(77,49,'77:49','Ngày hôm đó là ngày của sự hủy diệt, trừng phạt và sự thất bại thảm hại cho những kẻ phủ nhận những điều mà các vị Thiên Sứ mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(77,50,'77:50','Nếu họ không tin Qur''an này được ban xuống từ Thượng Đế của họ thì họ sẽ tin vào lời lẽ nào sau Nó nữa chứ?!');
INSERT INTO "vn_mokhtasar" VALUES(78,1,'78:1','Những kẻ thờ đa thần Quraish đang hỏi nhau về điều gì sau khi Allah gởi Thiên Sứ (cầu xin sự bình an đến Người) của Ngài đến cho họ?!');
INSERT INTO "vn_mokhtasar" VALUES(78,2,'78:2','Chúng đang hỏi nhau về nguồn tin vĩ đại, đó là Qur’an thiêng liêng thông điệp về sự Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(78,3,'78:3','Qur''an là cái khiến họ bất đồng với nhau, họ mô tả Nó là bùa chú hoặc thơ ca hoặc ma thuật hoặc những mẫu chuyện cổ tích.');
INSERT INTO "vn_mokhtasar" VALUES(78,4,'78:4','Không phải như những gì họ đã khẳng định. Rồi đây những kẻ phủ nhận Qur''an sẽ sớm biết hậu quả cho việc phủ nhận của mình.');
INSERT INTO "vn_mokhtasar" VALUES(78,5,'78:5','Rồi đây chúng chắc chắn sẽ sớm biết điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(78,6,'78:6','Chẳng lẽ TA đã không làm cho trái đất bằng phẳng như một tấm thảm ư?');
INSERT INTO "vn_mokhtasar" VALUES(78,7,'78:7','Và TA làm cho những quả núi như những cái trụ cột giữ cho mặt đất không bị lung lay và chấn động.');
INSERT INTO "vn_mokhtasar" VALUES(78,8,'78:8','Và TA đã tạo các ngươi - hỡi con người - thành hai giới nam nữ.');
INSERT INTO "vn_mokhtasar" VALUES(78,9,'78:9','Và TA đã làm giấc ngủ của các ngươi thành một sự nghỉ ngơi cho cơ thể các ngươi, giúp các ngươi duy trì sức khỏe và an nghỉ một cách yên bình.');
INSERT INTO "vn_mokhtasar" VALUES(78,10,'78:10','Và TA đã tạo ra ban đêm như một tấm chăn đắp che phủ các ngươi tựa như quần áo che phần kín của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(78,11,'78:11','Và TA đã tạo ra ban ngày để các ngươi tản mát khắp nơi tìm thiên lộc và phúc lợi cho cuộc sống của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(78,12,'78:12','Và TA đã dựng bên trên các ngươi bảy tầng trời vững chắc, không bị nứt cũng không bị thủng một lỗ nào.');
INSERT INTO "vn_mokhtasar" VALUES(78,13,'78:13','Và TA đã tạo ra mặt trời như một chiếc đèn mang lại ánh sáng cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(78,14,'78:14','Và TA cho mưa xuống dồi dào từ những đám mây mưa.');
INSERT INTO "vn_mokhtasar" VALUES(78,15,'78:15','Để TA làm mọc ra đủ loại trái hạt và đủ loại cây cối.');
INSERT INTO "vn_mokhtasar" VALUES(78,16,'78:16','Và để TA cho mọc ra cây cối thành những khu vườn xanh tươi và đủ loại cho các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(78,17,'78:17','Quả thật, Ngày Phân Loại giữa tạo vật là Ngày đã được ấn định không bao giờ được thay đổi.');
INSERT INTO "vn_mokhtasar" VALUES(78,18,'78:18','Ngày đó, khi vị Thiên thần thổi tiếng còi lần hai thì toàn nhân loại lần lượt đứng dậy và đi thành từng đoàn, từng đoàn một.');
INSERT INTO "vn_mokhtasar" VALUES(78,19,'78:19','Và bầu trời sẽ được mở ra như những cánh cửa mở.');
INSERT INTO "vn_mokhtasar" VALUES(78,20,'78:20','Và những quả núi kiên cố và vững chắc bị dời đi một cách nhẹ nhàng tưởng chừng trước kia những quả núi đó chỉ là ảo ảnh không thực.');
INSERT INTO "vn_mokhtasar" VALUES(78,21,'78:21','Quả thật, vào Ngày đó, Hỏa Ngục sẽ mai phục.');
INSERT INTO "vn_mokhtasar" VALUES(78,22,'78:22','Dành cho tất cả những kẻ sai quấy và tội lỗi đến cư ngụ.');
INSERT INTO "vn_mokhtasar" VALUES(78,23,'78:23','Tại đó, chúng sẽ ở trong đó đời đời không có ngày trở ra.');
INSERT INTO "vn_mokhtasar" VALUES(78,24,'78:24','Chúng sẽ không tìm thấy được không khí mát lạnh để làm dịu cái nóng thiêu đốt thật dữ dội và càng không tìm được thức uống để giải cơn khát.');
INSERT INTO "vn_mokhtasar" VALUES(78,25,'78:25','Họ sẽ không được cho ăn uống ngoài nước cực sôi và chất mủ hôi tanh chảy ra từ cư dân của Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(78,26,'78:26','Đó là kết quả cho những hành động tội lỗi và phủ nhận đức tin của chúng trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(78,27,'78:27','Quả thật, trên thế gian trước kia, chúng đã không sợ cái Ngày bị Allah mang chúng ra thanh toán và xét xử này bao giờ, bởi chúng đã không tin vào sự Phục Sinh, giá như chúng biết sợ sự Phục Sinh thì chúng đã tin tưởng và đã ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(78,28,'78:28','Chúng đã phủ nhận những Lời Mặc Khải mà TA đã thiên khải cho Thiên Sứ của TA.');
INSERT INTO "vn_mokhtasar" VALUES(78,29,'78:29','Và tất cả mọi việc làm, mọi hành động đều được TA cho ghi chép vào quyển số một cách đầy đủ và chi tiết.');
INSERT INTO "vn_mokhtasar" VALUES(78,30,'78:30','Do đó, các ngươi – những kẻ ngông cuồng – hãy mãi mãi nếm lấy hình phạt xứng đáng, TA sẽ không bao giờ thêm thắt gì cho các ngươi ngoài sự trừng phạt đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(78,31,'78:31','Quả thật, những ai kính sợ Thượng Đế của họ bằng việc thực hiện các mệnh lệnh của Ngài và tránh xa các điều cấm của Ngài sẽ giành được thắng lợi, đó là được vào Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(78,32,'78:32','Với những ngôi vườn cây trái và vườn nho.');
INSERT INTO "vn_mokhtasar" VALUES(78,33,'78:33','Với các trinh nữ đồng trang lứa làm bạn tình.');
INSERT INTO "vn_mokhtasar" VALUES(78,34,'78:34','Với những ly rượu tràn đầy ngon tuyệt.');
INSERT INTO "vn_mokhtasar" VALUES(78,35,'78:35','Trong Thiên Đàng, họ sẽ không còn nghe thấy lời nói sàm bậy hay những lời nói dối lừa gạt lẫn nhau nữa.');
INSERT INTO "vn_mokhtasar" VALUES(78,36,'78:36','Tất cả là phần thưởng đầy đủ mà Allah dành cho những bề tôi ngoan đạo của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(78,37,'78:37','Ngài là Thượng Đế của các tầng trời, trái đất và mọi vạn vật trong đó, Ngài luôn độ lượng đối với trần gian và Đời Sau, Ngài là Đấng nắm quyền thống trị toàn bộ trời đất, không ai được quyền đôi co ngoại trừ có sự cho phép của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(78,38,'78:38','Vào Ngày Phán xét, Đại Thiên Thần Jibril và các Thiên thần khác đứng thành hàng ngũ, không ai được phép cầu xin ân xá cho ai ngoại trừ được Đấng Nhân Từ cho phép, và y chỉ nói toàn lời thật tựa như lời Tawhid.');
INSERT INTO "vn_mokhtasar" VALUES(78,39,'78:39','Do đó, nếu ai muốn được cứu rỗi thì hãy chọn lấy con đường quay về với Thượng Đế của y bằng các việc làm ngoan đạo và đức hạnh.');
INSERT INTO "vn_mokhtasar" VALUES(78,40,'78:40','Quả thật, TA đã cảnh báo các ngươi - hỡi con người - về sự trừng phạt đang rất gần, Ngày mà tất cả mọi người sẽ nhìn thấy tất cả việc làm của họ trên thế gian. Và những kẻ vô đức tin luôn khát khao được thoát khỏi hình phạt, họ sẽ nói: Ôi, ước gì mình được biến thành cát bụi giống như loài súc vật khi chúng được lệnh trong Ngày Tận Thế: Hãy trở thành cát bụi.');
INSERT INTO "vn_mokhtasar" VALUES(79,1,'79:1','Allah, Đấng Tối Cao thề bởi các vị Thiên Thần rút mạnh linh hồn của những kẻ vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(79,2,'79:2','Ngài thề bởi các vị Thiên Thần rút nhẹ linh hồn của những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(79,3,'79:3','Ngài thề bởi các Thiên Thần lên xuống giữa các tầng trời và trái đất như bơi theo lệnh của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(79,4,'79:4','Ngài thề bởi các Thiên Thần luôn tiên phong và nhanh chóng thi hành mệnh lệnh của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(79,5,'79:5','Ngài thề bởi các Thiên Thần phụng mệnh Allah chẳng hạn như các Thiên Thần trông coi các việc làm của đám bề tôi của Ngài; Ngài thề như thế để khẳng định rằng tất cả nhân loại sẽ được phục sinh và được xét xử thưởng phạt công minh.');
INSERT INTO "vn_mokhtasar" VALUES(79,6,'79:6','Vào Ngày tiếng còi thứ nhất Ra-jifah được hú lên làm kết thúc mọi sự tồn tại của thế giới trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(79,7,'79:7','Tiếp theo là tiếng còi thứ hai Ra-difah, tiếng còi cho sự Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(79,8,'79:8','Vào Ngày đó, trái tim của một số người sẽ đập mạnh vì sợ hãi.');
INSERT INTO "vn_mokhtasar" VALUES(79,9,'79:9','Nỗi lo sợ hiện rõ trên cặp mắt của họ.');
INSERT INTO "vn_mokhtasar" VALUES(79,10,'79:10','Những kẻ phủ nhận sự Phục Sinh thường nói: Há chúng tôi sẽ được sống lại sau khi chúng tôi đã chết ư?');
INSERT INTO "vn_mokhtasar" VALUES(79,11,'79:11','Kể cả khi xương cốt chúng tôi đã bị rã mục nhưng vẫn có thể sống lại lần nửa ư?');
INSERT INTO "vn_mokhtasar" VALUES(79,12,'79:12','Họ nói giễu cợt: Nếu như vậy thì chúng tôi thực sự đã thất bại thảm hại rồi.');
INSERT INTO "vn_mokhtasar" VALUES(79,13,'79:13','Quả thật, sự Phục Sinh rất đơn giản chỉ cần một tiếng còi duy nhất được hụ lên do Thiên Thần thổi còi đảm nhận.');
INSERT INTO "vn_mokhtasar" VALUES(79,14,'79:14','Ngay lúc đó, tất cả những ai từng sống trên mặt đất sống trở lại sau khi chúng đã ở trong lòng đất.');
INSERT INTO "vn_mokhtasar" VALUES(79,15,'79:15','Ngươi - hỡi Thiên Sứ - có nghe về câu chuyện của Musa với Thượng Đế Y và với kẻ thù của Y là Fir''awn chưa?');
INSERT INTO "vn_mokhtasar" VALUES(79,16,'79:16','Tại thung lũng linh thiêng Tuwa, Allah đã gọi Musa.');
INSERT INTO "vn_mokhtasar" VALUES(79,17,'79:17','Ngươi hãy đi gặp Fir’awn (bạo chúa Pharaon), quả thật hắn đã vượt quá mức giới hạn trong tội lỗi và ngạo mạn.');
INSERT INTO "vn_mokhtasar" VALUES(79,18,'79:18','Ngươi hãy nói với hắn: Ngài - hỡi Fir''awn - có muốn được tẩy sạch tội lỗi không?');
INSERT INTO "vn_mokhtasar" VALUES(79,19,'79:19','Ta sẽ hướng dẫn bệ hạ trở về gặp Thượng Đế của mình, Đấng đã tạo ra bệ hạ, nuôi dưỡng bệ hạ. Sự hướng dẫn sẽ khiến bệ hạ biết kính sợ Thượng Đế mà biết làm Ngài hài lòng và biết tránh xa điều làm Ngài giận dữ.');
INSERT INTO "vn_mokhtasar" VALUES(79,20,'79:20','Musa đã cho Fir’awn thấy các phép lạ vĩ đại chứng minh sứ mạng của Y, đó là cây gậy và bàn tay.');
INSERT INTO "vn_mokhtasar" VALUES(79,21,'79:21','Nhưng tên bạo chúa Fir’awn đã phủ nhận các dấu lạ này và không theo điều mà Musa mời gọi.');
INSERT INTO "vn_mokhtasar" VALUES(79,22,'79:22','Không những thế, hắn còn tìm đủ mọi cách để chống lại với Musa.');
INSERT INTO "vn_mokhtasar" VALUES(79,23,'79:23','Fir’awn, hắn đã triệu tập người dân cùng với quân thần của hắn lại để thể hiện trước Musa.');
INSERT INTO "vn_mokhtasar" VALUES(79,24,'79:24','Hắn nói: Ta chính là thượng đế của các ngươi, không được tuân lệnh ai khác ngoài ta.');
INSERT INTO "vn_mokhtasar" VALUES(79,25,'79:25','Thế là Allah đã trừng phạt hắn trên thế gian, đó là Ngài nhấn chìm hắn trong biển; còn ở Đời Sau, Ngài sẽ tống hắn vào hình phạt khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(79,26,'79:26','Quả thật, qua việc trừng phạt Fir''awn ở trần gian và Đời Sau là bài học cho những ai biết kính sợ sự trừng phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(79,27,'79:27','Việc mà Allah làm cho các ngươi - hỡi những người phủ nhận sự Phục Sinh - sống lại sau cái chết khó hay việc TA đã tạo dựng bầu trời khó hơn?');
INSERT INTO "vn_mokhtasar" VALUES(79,28,'79:28','TA đã nâng bầu trời lên cao bên trên các ngươi và cái trần của nó làm không khí không có một lỗ thủng nào.');
INSERT INTO "vn_mokhtasar" VALUES(79,29,'79:29','TA làm cho nó thành đêm tối khi mặt trời lặn khuất và làm cho nó sáng trở lại khi mặt trời mọc.');
INSERT INTO "vn_mokhtasar" VALUES(79,30,'79:30','Và trái đất, TA làm cho nó bằng phẳng và đặt trong nó nhiều điều hồng phúc.');
INSERT INTO "vn_mokhtasar" VALUES(79,31,'79:31','TA làm nước phún ra và làm mọc ra đủ loại cây trái và hoa màu từ nó, phục vụ việc chăn nuôi.');
INSERT INTO "vn_mokhtasar" VALUES(79,32,'79:32','Và TA cho những quả núi nhô lên như những trụ cột kiên cố giữ cho nó không bị rung chuyển.');
INSERT INTO "vn_mokhtasar" VALUES(79,33,'79:33','Với tất cả nguồn lợi đó là cho các ngươi đó - hỡi con người - và cho cả gia súc của các ngươi. Há lẽ nào Đấng đã tạo hóa ra tất cả những thứ đó lại bất lực trong việc làm cho tất cả các ngươi sống lại lần nữa ư?!');
INSERT INTO "vn_mokhtasar" VALUES(79,34,'79:34','Khi tiếng còi thứ hai được thổi lên thì đại họa xảy đến, mọi thứ sẽ bị hủy diệt và Sự Phục Sinh được dựng lên.');
INSERT INTO "vn_mokhtasar" VALUES(79,35,'79:35','Ngày đó, con người sẽ được cho thấy tất cả các việc làm và mọi hành động tốt xấu của mình.');
INSERT INTO "vn_mokhtasar" VALUES(79,36,'79:36','Và Hỏa Ngục sẽ được mang ra phơi bày trước mặt.');
INSERT INTO "vn_mokhtasar" VALUES(79,37,'79:37','Đối với người nào vượt quá mức giới hạn cho phép trong lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(79,38,'79:38','Và yêu thích cuộc sống trần gian tạm bợ này hơn cuộc sống vĩnh cửu ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(79,39,'79:39','Thì Hỏa Ngục sẽ là nơi cư ngụ mãi mãi của y.');
INSERT INTO "vn_mokhtasar" VALUES(79,40,'79:40','Đối với ai luôn lo sợ bị thanh toán trước Allah vào Ngày Phán Xét mà luôn giữ gìn bản thân tránh xa những dục vọng thấp hèn tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(79,41,'79:41','- 41, Đối với ai luôn lo sợ bị thanh toán trước Allah vào Ngày Phán Xét mà luôn giữ gìn bản thân tránh xa những dục vọng thấp hèn tội lỗi thì Thiên Đàng sẽ là nơi ở mãi mãi của họ.');
INSERT INTO "vn_mokhtasar" VALUES(79,42,'79:42','Những người phủ nhận sự Phục Sinh thường hỏi Ngươi – hỡi Thiên Sứ – bao giờ thời khắc của Ngày Phục Sinh xảy đến?');
INSERT INTO "vn_mokhtasar" VALUES(79,43,'79:43','Điều đó không nằm trong kiến thức của Ngươi, vả lại Ngươi cũng không có thẩm quyền, Ngươi chỉ có bổn phận chuẩn bị mà thôi.');
INSERT INTO "vn_mokhtasar" VALUES(79,44,'79:44','Chỉ một mình Thượng Đế của Ngươi mới biết được bao giờ thời khắc Phục Sinh sẽ đến.');
INSERT INTO "vn_mokhtasar" VALUES(79,45,'79:45','Thật ra Ngươi chỉ là người truyền đạt và cảnh tỉnh những ai biết sợ Ngày ấy.');
INSERT INTO "vn_mokhtasar" VALUES(79,46,'79:46','Và khi chúng nhìn thấy giờ khắc tận thế đã đến thì chúng cứ tưởng như chúng chỉ ở trần gian mới chỉ được một buổi chiều hay một buổi sáng ngắn ngủi.');
INSERT INTO "vn_mokhtasar" VALUES(80,1,'80:1','Một sự biểu hiện khó chịu trên gương mặt của Thiên sứ và xoay sang hướng khác trước một người mù đã đến gặp Người.');
INSERT INTO "vn_mokhtasar" VALUES(80,2,'80:2','Người mù đó chính là Abdullah bin Ummu Maktum, y đến gặp Người để xin được chỉ dẫn nhưng lúc đó Người đang bận thuyết giảng và truyền đạt cho những người có địa vị của Quraish về Islam.');
INSERT INTO "vn_mokhtasar" VALUES(80,3,'80:3','Và điều gì cho Ngươi - hỡi Thiên Sứ - biết sự thật của sự việc? Biết đâu, người đàn ông mù kia sẽ được tẩy sạch?');
INSERT INTO "vn_mokhtasar" VALUES(80,4,'80:4','Hoặc lời nhắc nhở mà y nghe được từ Ngươi sẽ mang lại cho y điều hữu ích.');
INSERT INTO "vn_mokhtasar" VALUES(80,5,'80:5','Đối với kẻ tự mãn luôn cho bản thân đã giàu tiền của không cần đến đức tin Iman mà Người mang đến.');
INSERT INTO "vn_mokhtasar" VALUES(80,6,'80:6','Thì Ngươi lại đón tiếp ân cần, Ngươi tiếp đón với lời lẽ nhẹ nhàng.');
INSERT INTO "vn_mokhtasar" VALUES(80,7,'80:7','Và điều gì sẽ xảy ra cho Ngươi nếu người mù kia không được tẩy sạch sự vô đức tin của y?');
INSERT INTO "vn_mokhtasar" VALUES(80,8,'80:8','Đối với ai thực sự muốn gặp Ngươi tìm kiếm điều tốt đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(80,9,'80:9','Và y biết kính sợ Thượng Đế mình.');
INSERT INTO "vn_mokhtasar" VALUES(80,10,'80:10','Thì Ngươi lại lơ là mà cố bận rộn với những lãnh đạo đa thần.');
INSERT INTO "vn_mokhtasar" VALUES(80,11,'80:11','Vấn đề không phải như Ngươi nghĩ, chẳng qua đó là lời khuyên và lời nhắc nhở dành cho người biết đón nhận.');
INSERT INTO "vn_mokhtasar" VALUES(80,12,'80:12','Bởi thế, người nào muốn tưởng nhớ Allah thì hãy lưu nhớ những lời mặc khải này của Qur''an.');
INSERT INTO "vn_mokhtasar" VALUES(80,13,'80:13','Qur''an này thuộc những bộ Kinh danh giá ở nơi các Thiên Thần.');
INSERT INTO "vn_mokhtasar" VALUES(80,14,'80:14','Cao quý ở tại nơi cao nhất, tinh khiết không có chút vết bẩn nào.');
INSERT INTO "vn_mokhtasar" VALUES(80,15,'80:15','Đó là bàn tay của các Thiên Sứ thuộc giới các Thiên Thần.');
INSERT INTO "vn_mokhtasar" VALUES(80,16,'80:16','Họ được Thượng Đế của mình tôn trọng, họ tận tụy hoàn thành mọi công việc được giao.');
INSERT INTO "vn_mokhtasar" VALUES(80,17,'80:17','Người vô đức tin bị nguyền rủa, tại sao hắn lại dám phủ nhận Allah?');
INSERT INTO "vn_mokhtasar" VALUES(80,18,'80:18','Chẳng lẽ hắn không quan sát thấy hắn được Allah tạo ra từ cái gì ư? Để rồi hắn trở nên cao ngạo trên mặt đất và phủ nhận Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(80,19,'80:19','Ngài đã tạo hắn từ một ít nước tinh dịch và định cho nó hình thành qua các giai đoạn.');
INSERT INTO "vn_mokhtasar" VALUES(80,20,'80:20','Sau đó Ngài vạch ra cho hắn con đường rời khỏi bụng mẹ sau khi trải qua đủ các giai đoạn.');
INSERT INTO "vn_mokhtasar" VALUES(80,21,'80:21','Rồi Ngài định cho y tuổi thọ để sống và cho y chết, rồi cho y xuống mộ chờ đến ngày Phục Sinh.');
INSERT INTO "vn_mokhtasar" VALUES(80,22,'80:22','Rồi đến khi Ngài muốn, Ngài cho y sống lại để thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(80,23,'80:23','Sự việc chắc chắn không phải như những kẻ vô đức tin mong tưởng và khẳng định, chúng đã không hoàn thành bổn phận mà Allah đã bắt buộc chúng phải làm.');
INSERT INTO "vn_mokhtasar" VALUES(80,24,'80:24','Người vô đức tin hãy nhìn vào thức ăn mà y ăn có được từ đâu?');
INSERT INTO "vn_mokhtasar" VALUES(80,25,'80:25','Quả thật, TA đã xối mạnh nước mưa xuống từ bầu trời.');
INSERT INTO "vn_mokhtasar" VALUES(80,26,'80:26','Rồi TA chẻ đất ra làm cho thảo mộc đâm chồi.');
INSERT INTO "vn_mokhtasar" VALUES(80,27,'80:27','TA làm mọc ra các loại hạt, nho và các loại rau, ô liu, chà là và các hạt khác.');
INSERT INTO "vn_mokhtasar" VALUES(80,28,'80:28','TA làm mọc ra cho các ngươi các vườn tược xanh tươi với đủ loại dùng làm lương thực cho vật nuôi.');
INSERT INTO "vn_mokhtasar" VALUES(80,29,'80:29','TA làm cho mọc ra ô liu và chà là.');
INSERT INTO "vn_mokhtasar" VALUES(80,30,'80:30','TA làm cho mọc ra các khu vườn với nhiều cây cối.');
INSERT INTO "vn_mokhtasar" VALUES(80,31,'80:31','Và TA làm cho mọc ra cây trái và cho mọc ra đồng cỏ cho súc vật.');
INSERT INTO "vn_mokhtasar" VALUES(80,32,'80:32','Dùng làm thức ăn, lương thực cho các ngươi và vật nuôi của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(80,33,'80:33','Khi tiếng còi hụ kinh hoàng được thổi lên một cách chát chúa làm chấn động mọi vật, đó là tiếng còi thứ hai.');
INSERT INTO "vn_mokhtasar" VALUES(80,34,'80:34','Ngày đó mỗi người sẽ chạy bỏ anh em mình.');
INSERT INTO "vn_mokhtasar" VALUES(80,35,'80:35','Y bỏ luôn cả cha mẹ.');
INSERT INTO "vn_mokhtasar" VALUES(80,36,'80:36','Y sẽ bỏ vợ và con cái mình.');
INSERT INTO "vn_mokhtasar" VALUES(80,37,'80:37','Bởi quá lo âu cho bản thân trước sự kiện kinh hoàng đã khiến mỗi người không còn nhớ đến ai nữa, mỗi người chỉ biết lo cho bản thân mình mà không còn nghĩ đến ai khác.');
INSERT INTO "vn_mokhtasar" VALUES(80,38,'80:38','Ngày đó, gương mặt tươi cười sáng rỡ.');
INSERT INTO "vn_mokhtasar" VALUES(80,39,'80:39','Vui cười mừng với điều mà Allah chuẩn bị từ lòng nhân từ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(80,40,'80:40','Ngược lại, gương mặt của những kẻ bất hạnh vào Ngày đó sẽ dính đầy bụi và tối sầm.');
INSERT INTO "vn_mokhtasar" VALUES(80,41,'80:41','Bị ê chề, nhục nhã.');
INSERT INTO "vn_mokhtasar" VALUES(80,42,'80:42','Họ là những kẻ đã phủ nhận Allah và các Lời mặc khải của Ngài, họ đã ung dung trong tội lỗi và lầm lạc.');
INSERT INTO "vn_mokhtasar" VALUES(81,1,'81:1','Khi mặt trời cuộn tròn và mất đi ánh sáng của nó.');
INSERT INTO "vn_mokhtasar" VALUES(81,2,'81:2','Khi các vì sao rơi, ánh sáng của chúng bị lu mờ và biến mất.');
INSERT INTO "vn_mokhtasar" VALUES(81,3,'81:3','Khi những quả núi di chuyển khỏi vị trí của chúng.');
INSERT INTO "vn_mokhtasar" VALUES(81,4,'81:4','Khi những con lạc đà cái mang thai, một thứ tài sản giá trị nhất, bị bỏ rơi không có ai quan tâm chăm sóc.');
INSERT INTO "vn_mokhtasar" VALUES(81,5,'81:5','Khi các động vật hoang dã tập trung lại tại một nơi cùng với con người.');
INSERT INTO "vn_mokhtasar" VALUES(81,6,'81:6','Khi các dại dương bị lửa đốt nóng ở phía dưới đến dâng trào dữ dội.');
INSERT INTO "vn_mokhtasar" VALUES(81,7,'81:7','Khi những linh hồn được nhập lại vào chính thể xác của nó, linh hồn tội lỗi vào xác tội lỗi và linh hồn ngoan đạo vào xác ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(81,8,'81:8','Khi những đứa bé gái bị chôn sống được Allah xét hỏi.');
INSERT INTO "vn_mokhtasar" VALUES(81,9,'81:9','Đã phạm phải tội gì mà bị giết như thế?');
INSERT INTO "vn_mokhtasar" VALUES(81,10,'81:10','Khi các tờ sớ ghi chép các việc làm tốt xấu được phơi bày, mỗi người tự đọc tờ sớ hành động mình đã làm.');
INSERT INTO "vn_mokhtasar" VALUES(81,11,'81:11','Khi bầu trời bị chẻ làm đôi tựa như da cừu bị lột.');
INSERT INTO "vn_mokhtasar" VALUES(81,12,'81:12','Khi lửa Hỏa Ngục được đốt cháy.');
INSERT INTO "vn_mokhtasar" VALUES(81,13,'81:13','Và khi Thiên Đàng được mang đến gần những người ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(81,14,'81:14','Lúc đó, mỗi linh hồn con người sẽ biết được những điều mà y mang đến từ những việc làm tốt xấu của mình trước kia trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(81,15,'81:15','Allah thề bởi những ngôi sao ẩn trước khi chúng xuất hiện vào ban đêm.');
INSERT INTO "vn_mokhtasar" VALUES(81,16,'81:16','Những hành tinh di chuyển rất nhanh và vội vàng biến mất lúc bình minh ló dạng giống như loài linh dương chui vào hang.');
INSERT INTO "vn_mokhtasar" VALUES(81,17,'81:17','Ngài thề bởi ban đêm khi mang bóng tối đến và đi.');
INSERT INTO "vn_mokhtasar" VALUES(81,18,'81:18','Ngài thề bởi ban mai lúc sáng sớm.');
INSERT INTO "vn_mokhtasar" VALUES(81,19,'81:19','Quả thật, Qur’an được thiên khải cho Muhammad là lời phán của Allah qua trung gian vị Thiên Thần trung trực Jibril được Allah vinh danh.');
INSERT INTO "vn_mokhtasar" VALUES(81,20,'81:20','Vị Thiên Thần có quyền lực và địa vị nơi Chủ Nhân của Ngai Vương.');
INSERT INTO "vn_mokhtasar" VALUES(81,21,'81:21','Vị Thiên Thần được cư dân trên trời nghe lệnh và được Allah tin cậy giao phó truyền đạt lời mặc khải của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(81,22,'81:22','Muhammad mà các ngươi quen biết tự bao giờ vốn là người đáng tin, trung thực không phải là một người điên loạn như các ngươi nói.');
INSERT INTO "vn_mokhtasar" VALUES(81,23,'81:23','Quả thật người bạn của các ngươi đã nhìn thấy chân tướng của đại Thiên Thần Jibril tại chân trời sáng tỏ.');
INSERT INTO "vn_mokhtasar" VALUES(81,24,'81:24','Và Người bạn của các ngươi không phải là tên keo kiệt muốn giữ lại cho riêng mình điều gì đó trong sứ mạng truyền đạt củaY và Ycũng không lấy thù lao như những tên thầy bói.');
INSERT INTO "vn_mokhtasar" VALUES(81,25,'81:25','Và Qur’an không phải là lời của Shaytan bị trục xuất khỏi sự nhân từ của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(81,26,'81:26','Còn con đường nào để các ngươi phủ nhận nó không phải là của Allah sau những bằng chứng thế này?');
INSERT INTO "vn_mokhtasar" VALUES(81,27,'81:27','Quả thật, Qur’an chỉ là lời nhắc nhở và cảnh tỉnh cho loại Jinn và loài người.');
INSERT INTO "vn_mokhtasar" VALUES(81,28,'81:28','Cho những ai muốn đi đúng trên con đường ngay chính của Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(81,29,'81:29','Nhưng những gì các ngươi muốn sẽ không thành ngoại trừ ý muốn của các ngươi đồng thuận với ý muốn của Allah, Đấng Chủ Tể của muôn loài.');
INSERT INTO "vn_mokhtasar" VALUES(82,1,'82:1','Khi bầu trời bị chẻ làm đôi để giới Thiên Thần đi xuống.');
INSERT INTO "vn_mokhtasar" VALUES(82,2,'82:2','Khi các vì sao rơi rải rác.');
INSERT INTO "vn_mokhtasar" VALUES(82,3,'82:3','Khi các đại dương và biển cả dâng trào cuồn cuộn hòa lẫn vào nhau.');
INSERT INTO "vn_mokhtasar" VALUES(82,4,'82:4','Khi các ngôi mộ bị lật ngược lên để phục sinh những linh hồn từ cõi chết.');
INSERT INTO "vn_mokhtasar" VALUES(82,5,'82:5','Đấy là lúc mỗi linh hồn sẽ biết được tất cả mọi việc làm mà mình đã gửi đi và những gì còn bỏ lại chưa làm.');
INSERT INTO "vn_mokhtasar" VALUES(82,6,'82:6','Này hỡi con người phủ nhận Thượng Đế của mình, điều gì khiến các ngươi nghịch lại mệnh lệnh của Ngài khi Ngài cố trì hoãn không trừng phạt các ngươi vì sự rộng lượng của Ngài?!');
INSERT INTO "vn_mokhtasar" VALUES(82,7,'82:7','Ngài là Đấng đã tạo hóa ngươi từ không là gì và ban cho ngươi hình thể tốt đẹp và cân đối.');
INSERT INTO "vn_mokhtasar" VALUES(82,8,'82:8','Ngài muốn tạo hình hài ngươi ra sao Ngài muốn, trong khi Ngài đã ưu ái không tạo ngươi bằng hình dạng con lừa, con khỉ, con chó hoặc bất cứ con vật nào khác.');
INSERT INTO "vn_mokhtasar" VALUES(82,9,'82:9','Sự việc không như các ngươi nghĩ đâu - hỡi những kẻ tự cao - vì các ngươi đã phủ nhận Ngày Thanh Toán trong khi các ngươi không biết về Nó.');
INSERT INTO "vn_mokhtasar" VALUES(82,10,'82:10','Quả thật, bên cạnh các ngươi luôn có các vị Thiên Thần theo dõi hành động của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(82,11,'82:11','Bhững vị ghi chép hành động của các ngươi là các Thiên Thần vinh dự ở nơi Allah.');
INSERT INTO "vn_mokhtasar" VALUES(82,12,'82:12','Họ biết rõ mọi hành động của các ngươi, dù nhỏ hay lớn, dù công khai hay thầm kín, họ đều ghi chép rất đầy đủ và tỉ mỉ.');
INSERT INTO "vn_mokhtasar" VALUES(82,13,'82:13','Quả thật, những người ngoan đạo, đức hạnh, biết vâng lời sẽ mãi mãi ở trong Thiên Đàng hạnh phúc của Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(82,14,'82:14','Còn những kẻ tội lỗi chắc chắn sẽ bị đày vào Hỏa Ngục bùng cháy.');
INSERT INTO "vn_mokhtasar" VALUES(82,15,'82:15','Vào Ngày thưởng phạt chúng sẽ phải vào trong đó để nếm sức nóng của nó.');
INSERT INTO "vn_mokhtasar" VALUES(82,16,'82:16','Chúng sẽ không chết cũng không có ngày trở ra mà muôn đời trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(82,17,'82:17','Và điều gì cho Ngươi - hỡi Thiên Sứ - biết về Ngày Phán Xét?');
INSERT INTO "vn_mokhtasar" VALUES(82,18,'82:18','Rồi điều gì cho Ngươi biết về Ngày Phán Xét?');
INSERT INTO "vn_mokhtasar" VALUES(82,19,'82:19','Đó là Ngày mà không có một ai có thể giúp đỡ cho ai. Mọi sự việc và mọi quyền hành của Ngày hôm đó đều thuộc quyền hạn của Allah, Ngài quyết định sao tùy Ngài, không ai có thẩm quyền.');
INSERT INTO "vn_mokhtasar" VALUES(83,1,'83:1','Một sự trừng phạt rất nặng dành cho những ai là kẻ gian lận bòn rút trong cân đong và đo đạc.');
INSERT INTO "vn_mokhtasar" VALUES(83,2,'83:2','Họ là những kẻ khi mua thì muốn người cân đong thật đủ cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(83,3,'83:3','Còn khi bán cho người thì tìm cách và thủ đoạn để cân đong thiếu hụt cho người. Đó là thực trạng của dân cư của Madinah lúc Thiên Sứ di cư đến.');
INSERT INTO "vn_mokhtasar" VALUES(83,4,'83:4','Phải chăng những kẻ gian lận như thế nghĩ rằng chúng sẽ không được Allah phục sinh để trình diện trước Ngài?!');
INSERT INTO "vn_mokhtasar" VALUES(83,5,'83:5','Để xét xử mọi việc làm vào Ngày Phán xét vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(83,6,'83:6','Ngày Phán xét là ngày toàn nhân loại sẽ đứng trước Đấng Chủ Tể của muôn loài để Ngài xét xử và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(83,7,'83:7','Vấn đề không như các ngươi tưởng rằng sẽ không có phục sinh sau khi chết, quả thật quyển sách tội lỗi của những kẻ hư đốn thuộc nhóm người vô đức tin và Munafiq thuộc loại thấp kém nhất.');
INSERT INTO "vn_mokhtasar" VALUES(83,8,'83:8','Và điều gì cho Ngươi - hỡi Thiên Sứ - biết Sijjin là gì?');
INSERT INTO "vn_mokhtasar" VALUES(83,9,'83:9','Đó là quyến sổ ghi chép đầy đủ mọi thứ, không thêm cũng không bị xóa bớt.');
INSERT INTO "vn_mokhtasar" VALUES(83,10,'83:10','Ngày hôm đó sẽ là một sự trừng phạt khắc nghiệt, sự thất bại dành cho những kẻ phủ nhận sự thật.');
INSERT INTO "vn_mokhtasar" VALUES(83,11,'83:11','Những kẻ đã không tin vào Ngày Phán Xét và thưởng phạt, Ngày mà Allah phán xét và thưởng phạt công minh hết tất cả hành động của con người ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(83,12,'83:12','Không ai phủ nhận Ngày đó ngoại trừ những kẻ vượt quá giới luật của Allah và đầy tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(83,13,'83:13','Khi các lời phán của TA đã thiên khải cho Thiên Sứ của TA được đọc lên cho y nghe thì y bảo: “Toàn là chuyển cổ nhân chứ không phải của Allah.”');
INSERT INTO "vn_mokhtasar" VALUES(83,14,'83:14','Chắc chắn sự việc không như những gì mà nhóm người phủ nhận nghĩ, không, dục vọng chúng đã chiến thắng nên lý trí của chúng bị che khuất khiến chúng chỉ biết tội lỗi và không nhìn thấy Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(83,15,'83:15','Rồi đây, vào Ngày Sau chúng sẽ không nhìn thấy Thượng Đế, chúng sẽ bị che khuất khỏi Thượng Đế.');
INSERT INTO "vn_mokhtasar" VALUES(83,16,'83:16','Rồi chúng sẽ bị đày vào Hỏa Ngục, để nếm mùi vị của cái nóng thiêu đốt của Nó.');
INSERT INTO "vn_mokhtasar" VALUES(83,17,'83:17','Vào Ngày Tận Thế, chúng sẽ bị sỉ nhục: “Đây chính là điều mà các ngươi đã thường phủ nhận ở trần gian, điều mà Thiên Sứ đã báo cho các ngươi.”');
INSERT INTO "vn_mokhtasar" VALUES(83,18,'83:18','Chắc chắn vấn đề không như các người nghĩ rằng sẽ không có thưởng phạt gì cả, bởi quyển sách ghi những người ngoan đạo và đức hạnh sẽ ở trong ‘Ili-yun.');
INSERT INTO "vn_mokhtasar" VALUES(83,19,'83:19','Và điều gì cho Ngươi - hỡi Thiên Sứ - biết ‘Ili-yun là gì?');
INSERT INTO "vn_mokhtasar" VALUES(83,20,'83:20','Đó là quyến sổ ghi chép đầy đủ mọi thứ, không thêm cũng không bị xóa bớt.');
INSERT INTO "vn_mokhtasar" VALUES(83,21,'83:21','Do các vị Thiên Thần gần kề Allah xác nhận.');
INSERT INTO "vn_mokhtasar" VALUES(83,22,'83:22','Quả thật, những người ngoan đạo, đức hạnh, biết vâng lời sẽ mãi mãi ở trong Thiên Đàng hạnh phúc của Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(83,23,'83:23','Họ sẽ ngồi, nằm trên những chiếc tràng kỷ để ngắm nhìn Thượng Đế của mình và tận hưởng cảnh đẹp với vẻ mặt tràn đầy hạnh phúc và kiêu hãnh.');
INSERT INTO "vn_mokhtasar" VALUES(83,24,'83:24','Ngươi sẽ nhìn thấy gương mặt của họ hiện rõ niềm hạnh phúc, vui sướng, mừng rỡ khôn xiết.');
INSERT INTO "vn_mokhtasar" VALUES(83,25,'83:25','Họ sẽ được cho uống một loài thức uống từ rượu được cất rất kỹ.');
INSERT INTO "vn_mokhtasar" VALUES(83,26,'83:26','Mang mùi thơm của xạ hương đến giọt cuối cùng, đó là sự huy hoàng và hạnh phúc vĩnh hằng cho những ai nỗ lực thi đua và phấn đấu làm điều ngoan đạo và đức hạnh để tìm sự hai lòng của Allah và tránh xa điều Ngài giận dữ.');
INSERT INTO "vn_mokhtasar" VALUES(83,27,'83:27','Thức uống từ rượu được nói là một hỗn hợp được pha với nước suối lấy từ một ngọn suối trong Thiên Đàng có tên Tasnim.');
INSERT INTO "vn_mokhtasar" VALUES(83,28,'83:28','Một ngọn suối ở tầng cao nhất trong Thiên Đàng, những người gần Allah nhất sẽ uống nguyên chất tinh khiết từ ngọn suối đó, còn những người có đức tin còn lại sẽ được uống pha với chất khác.');
INSERT INTO "vn_mokhtasar" VALUES(83,29,'83:29','Quả thật, lúc ở trên thế gian, những kẻ vô đức tin và tội lỗi đã thường cười nhạo và tỏ vẻ khinh khi những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(83,30,'83:30','Mỗi khi chúng đi ngang qua họ thì chúng nháy mắt nhau giễu cợt về họ.');
INSERT INTO "vn_mokhtasar" VALUES(83,31,'83:31','Và khi chúng trở về với đồng bọn của chúng thì chúng mang về điều chế giễu những người có đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(83,32,'83:32','Mỗi khi chúng nhìn thấy những người Muslim thì chúng lại buông lời miệt thị: Đây là bọn lầm lạc vì đã bỏ đi tôn giáo của ông bà mình.');
INSERT INTO "vn_mokhtasar" VALUES(83,33,'83:33','Trong khi Allah không giao phó cho chúng việc giám sát hành động của họ để chúng lại nói những lời như thế.');
INSERT INTO "vn_mokhtasar" VALUES(83,34,'83:34','Do đó, vào Ngày Sau, những người có đức tin sẽ cười nhạo báng lại những kẻ vô đức tin giống như chúng đã từng cười nhạo báng họ trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(83,35,'83:35','Những người ngoan đạo và đức hạnh sẽ ngồi trên những chiếc tràng kỷ tận hưởng những gì được ban thưởng nơi Thiên Đàng với niềm vui mãi mãi.');
INSERT INTO "vn_mokhtasar" VALUES(83,36,'83:36','Còn đối với những kẻ vô đức tin thì chúng bị trừng trị thích đáng về những gì mà chúng đã làm trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(84,1,'84:1','Khi bầu trời bị chẻ làm đôi để giới Thiên Thần đi xuống.');
INSERT INTO "vn_mokhtasar" VALUES(84,2,'84:2','Bởi làm theo lệnh Thượng Đế của họ và đó là lẽ đương nhiên.');
INSERT INTO "vn_mokhtasar" VALUES(84,3,'84:3','Và khi trái đất bị Allah trải bằng tựa như kéo tấm thuộc da.');
INSERT INTO "vn_mokhtasar" VALUES(84,4,'84:4','Và nó tống hết mọi thứ ra khỏi nó từ kho báu, xác chết và nó trở nên trống rỗng.');
INSERT INTO "vn_mokhtasar" VALUES(84,5,'84:5','Bởi làm theo lệnh Thượng Đế của nó và đó là lẽ đương nhiên.');
INSERT INTO "vn_mokhtasar" VALUES(84,6,'84:6','Này hỡi con người, quả thật ngươi đã vất vả và nhọc nhằn làm điều tốt hoặc điều xấu, sau đó, ngươi trở về trình diện Allah vào Ngày Phán Xét để Ngài xét xử việc làm của ngươi rồi ban thưởng hay trừng phạt ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(84,7,'84:7','Người nào nhận được quyển sổ ghi chép các việc làm của mình từ phía cánh tay phải.');
INSERT INTO "vn_mokhtasar" VALUES(84,8,'84:8','Thì họ chỉ bị Allah thanh toán rất dễ dàng, chỉ phơi bày tội lỗi cho y thấy chứ không bắt tội y.');
INSERT INTO "vn_mokhtasar" VALUES(84,9,'84:9','Rồi sau đó được trở về với người thân một cách vui sướng và hạnh phúc.');
INSERT INTO "vn_mokhtasar" VALUES(84,10,'84:10','Những người nhận lấy quyển số ghi chép các việc làm của mình bằng tay trái từ phía sau lưng.');
INSERT INTO "vn_mokhtasar" VALUES(84,11,'84:11','Thì y sẽ kêu than ước được chết cho xong.');
INSERT INTO "vn_mokhtasar" VALUES(84,12,'84:12','Họ bị đày vào trong Hỏa Ngục với những ngọn lửa cháy bùng dữ dội.');
INSERT INTO "vn_mokhtasar" VALUES(84,13,'84:13','Bởi quả thật trước kia trên thế gian y đã từng vui vẻ bên ngươi thân của y một cách kiêu hãnh và ngạo mạn trong trạng vô đức tin và tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(84,14,'84:14','Và y luôn cho rằng y sẽ không trở lại cuộc sống sau khi đã chết.');
INSERT INTO "vn_mokhtasar" VALUES(84,15,'84:15','Nhưng không, chắc rằng Allah sẽ kéo y trở lại cuộc sống giống như Ngài đã tạo y ra ở lần đầu tiên. Quả thật, Thượng Đế của y luôn am tường và biết hết mọi điều về y, không có gì che giấu được Ngài và dựa vào đó Ngài sẽ ban thưởng hoặc trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(84,16,'84:16','Allah thề bởi ánh hoàng hôn ở nơi chân trời khi mặt trời lặn khuất.');
INSERT INTO "vn_mokhtasar" VALUES(84,17,'84:17','Và Ngài thề bởi ban đêm khi mọi vật đã yên nghỉ.');
INSERT INTO "vn_mokhtasar" VALUES(84,18,'84:18','Và Ngài thề bởi vầng trăng khi nó tròn.');
INSERT INTO "vn_mokhtasar" VALUES(84,19,'84:19','Rằng con người các ngươi phải trải qua từng giai đoạn và những chặn đường nối tiếp nhau, từ giọt tinh dịch đến cục máu đặc rồi đến cục thịt, sau đó được thổi linh hồn vào, rồi sau đó là cái chết, rồi được phục sinh trở lại từ cõi chết và cuối cùng là sự tập trung.');
INSERT INTO "vn_mokhtasar" VALUES(84,20,'84:20','Bởi điều gì mà nhóm người vô đức lại phủ nhận Allah và Ngày Sau vậy?');
INSERT INTO "vn_mokhtasar" VALUES(84,21,'84:21','Tại sạo chúng lại không phủ phục trước Allah khi chúng nghe đọc Qur’an?');
INSERT INTO "vn_mokhtasar" VALUES(84,22,'84:22','Không, chúng đã chối bỏ hết mọi thứ được Thiên Sứ mang đến cho chúng.');
INSERT INTO "vn_mokhtasar" VALUES(84,23,'84:23','Và Allah biết rất rõ những điều chúng che giấu trong lòng của chúng, không một hành động nào của chúng qua mặt được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(84,24,'84:24','Do đó, Ngươi hãy báo tin mừng cho chúng – hỡi Thiên Sứ – rằng có một sự trừng phạt rất đau đớn đang chờ đợi chúng.');
INSERT INTO "vn_mokhtasar" VALUES(84,25,'84:25','Còn những ai có đức tin nơi Allah và luôn chấp hành theo mọi chỉ đạo của Allah ban hành thì họ sẽ nhận được một phần thưởng bất tận, đó là Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(85,1,'85:1','Allah thể bởi bầu trời chứa đầy các chòm sao, bao gồm cả mặt trời, mặt trăng.');
INSERT INTO "vn_mokhtasar" VALUES(85,2,'85:2','Ngài thề bởi Ngày Phục Sinh được Ngài hứa hẹn tập trung tất cả vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(85,3,'85:3','Ngài thề bởi người làm chứng chẳng hạn như Nabi làm chứng cho cộng đồng tín đồ của Người và điều được chứng kiến chẳng hạn như cộng đồng tín đồ của Người được sự làm chứng của vị Nabi của họ.');
INSERT INTO "vn_mokhtasar" VALUES(85,4,'85:4','Nguyền rủa đám người đã đào cái hào khổng lồ.');
INSERT INTO "vn_mokhtasar" VALUES(85,5,'85:5','Đã đốt lửa trong cái hào để thiêu sống những người có đức tin trong đó.');
INSERT INTO "vn_mokhtasar" VALUES(85,6,'85:6','Những kẻ ngồi cạnh cái hố đầy lửa.');
INSERT INTO "vn_mokhtasar" VALUES(85,7,'85:7','Chúng thản nhiên chứng kiến cảnh tượng hành hình những người có đức tin trong cái hào đó.');
INSERT INTO "vn_mokhtasar" VALUES(85,8,'85:8','Thật ra, lý do mà đám người vô đức tin đối xử dã man với những người có đức tin chỉ vì họ tin tưởng Allah, Đấng Toàn năng và Tối cao, Đấng mà không gì chiến thắng được Ngài, Đấng đáng được ca ngợi và tán dương.');
INSERT INTO "vn_mokhtasar" VALUES(85,9,'85:9','Ngài là Đấng Chủ Tể của toàn vũ trụ, Đấng làm chứng cho tất cả mọi sự việc, không có điều gì có thể che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(85,10,'85:10','Chắc chắn những kẻ thiêu sống những người có đức tin nam và nữ bằng lửa để họ từ bỏ đức tin vào Allah duy nhất, nhưng sau đó không biết hối cải với Allah về tội lỗi đã phạm thì vào Ngày Tận Thế, chúng sẽ bị trừng phạt đau đớn trong Hỏa Ngục, chúng sẽ bị thiêu đốt giống như chúng đã thiêu đốt những người có đức tin bằng lửa vậy.');
INSERT INTO "vn_mokhtasar" VALUES(85,11,'85:11','Những người có đức tin nơi Allah và luôn làm điều thiện tốt thì sẽ được thu nhận vào Thiên Đàng với những ngôi vườn có các dòng sông chảy bên dưới những tòa lâu đài. Đó đích thực là sự thành công vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(85,12,'85:12','Chắc chắn sự túm lấy của Thượng Đế Ngươi - hỡi Thiên Sứ - đối với kẻ phản nghịch - đôi khi có trì hoãn - nhưng rất dữ dội.');
INSERT INTO "vn_mokhtasar" VALUES(85,13,'85:13','Ngài là Đấng khởi tạo vạn vật và trừng phạt rồi sau đó sẽ tái dựng lại toàn bộ.');
INSERT INTO "vn_mokhtasar" VALUES(85,14,'85:14','Ngài là Đấng hằng tha thứ cho những ai quay đầu sám hối, Ngài là Đấng tràn đầy tình thương đối với những ai theo chỉ đạo của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(85,15,'85:15','Ngài là Chủ nhân của chiếc Ngai vương oai nghiêm và vĩ đại.');
INSERT INTO "vn_mokhtasar" VALUES(85,16,'85:16','Và Ngài là Đấng tha thứ cho bất cứ tội lỗi nào cho bất cứ ai Ngài muốn, và Ngài muốn trừng phạt ai theo ý Ngài, không ai ép buộc được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(85,17,'85:17','Có đến với Ngươi chưa - hỡi Thiên Sứ - thông tin về những lực lượng hùng hậu tập trung chiến đấu với Chân Lý và ngăn chặn nó?');
INSERT INTO "vn_mokhtasar" VALUES(85,18,'85:18','Thuộc cộng đồng của Fir’awn (Pharaon) và Thamud, người dân của Saleh?');
INSERT INTO "vn_mokhtasar" VALUES(85,19,'85:19','Điều cấm cản đức tin của nhóm người này, không phải họ chưa nghe đến thông tin của những cộng đồng thời trước đã bị tiêu diệt về tội phủ nhận. Điều khiến họ không tin những gì Thiên Sứ mang đến cho họ là vì họ đi theo dục vọng bản thân.');
INSERT INTO "vn_mokhtasar" VALUES(85,20,'85:20','Và Allah biết rõ hết mọi hành động của họ, không gì qua mặt được Ngài và dựa vào đó Ngài sẽ thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(85,21,'85:21','Và Qur’an không phải là những bài thơ cũng không phải là một trò ma thuật như chúng đã bảo, mà nó là lời kinh vĩ đại và thiêng liêng.');
INSERT INTO "vn_mokhtasar" VALUES(85,22,'85:22','Được ghi trong văn bản lưu trữ trên cao, không thay đổi, không chỉnh sửa, không thêm và không bớt.');
INSERT INTO "vn_mokhtasar" VALUES(86,1,'86:1','Allah thề bởi bầu trời, bởi vì sao xuất hiện trong đêm.');
INSERT INTO "vn_mokhtasar" VALUES(86,2,'86:2','Và điều gì cho Ngươi - hỡi Thiên Sứ - vai trò vĩ đại của vì sao này?');
INSERT INTO "vn_mokhtasar" VALUES(86,3,'86:3','Nó là vì sao sáng rực trên bầu trời.');
INSERT INTO "vn_mokhtasar" VALUES(86,4,'86:4','Không một linh hồn nào mà không được Allah cử một Thiên Thần theo giám sát để ghi chép hành động của y làm bằng chứng thanh toán trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(86,5,'86:5','Con người hãy ngẫm nghĩ xem mình được Allah tạo ra từ cái gì, để y hoàn toàn phủ phục quyền năng của Allah cũng như thừa nhận sự bất lực của mình.');
INSERT INTO "vn_mokhtasar" VALUES(86,6,'86:6','Allah đã tạo y ra từ một loại nước (tinh dịch) phóng nhanh vào tử cung.');
INSERT INTO "vn_mokhtasar" VALUES(86,7,'86:7','Một loại nước được chiết xuất từ giữa các đốt xương sống và xương ngực của người đàn ông.');
INSERT INTO "vn_mokhtasar" VALUES(86,8,'86:8','Quả thật, Đấng - khi đã tạo con người từ một loại nước đáng khinh này - sẽ thừa khả năng tái tạo lại y một lần nữa từ cõi chết để thanh toán và thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(86,9,'86:9','Ngày mà mọi điều bị mật sẽ được công khai, mọi thứ trong lòng như sự định tâm, niềm tin ... người ngoan đạo và người phản nghịch sẽ được phân loại.');
INSERT INTO "vn_mokhtasar" VALUES(86,10,'86:10','Vào Ngày đó, con người sẽ không tìm thấy một nguồn lực nào có thể giúp y thoát khỏi sự trừng phạt của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(86,11,'86:11','Allah thề bởi bầu trời có mưa, bởi nó mưa theo hướng của nó, một lần rồi đến một lần.');
INSERT INTO "vn_mokhtasar" VALUES(86,12,'86:12','Ngài thề bởi đất bị chẻ ra để thảo mộc, cây cối mọc lên.');
INSERT INTO "vn_mokhtasar" VALUES(86,13,'86:13','Quả thật, Qur''an được thiên khải cho Muhammad là lời phan biệt giữa Chân Lý và ngụy tạo; giữa trung thực và dối trá.');
INSERT INTO "vn_mokhtasar" VALUES(86,14,'86:14','Nó không phải là lời nói đùa vô bổ mà Nó là sự thật, là Chân Lý.');
INSERT INTO "vn_mokhtasar" VALUES(86,15,'86:15','Quả thật, nhóm người phủ nhận những gì do Thiên Sứ mang đến đang lên rất nhiều kế hoạch ngăn chặn con đường truyền đạo của Người và chúng đã dùng thủ đoạn để kết thúc nó.');
INSERT INTO "vn_mokhtasar" VALUES(86,16,'86:16','Và TA cũng có kế hoạch phơi bày tôn giáo và xóa tan sự dối trá.');
INSERT INTO "vn_mokhtasar" VALUES(86,17,'86:17','Thế nên, Ngươi hãy mặc nhóm người vô đức tin đó đi - hỡi Thiên Sứ - chớ vội vã trừng phạt và tiêu diệt họ.');
INSERT INTO "vn_mokhtasar" VALUES(87,1,'87:1','Ngươi hãy tẩy sạch Thượng Đế của Ngươi, Đấng Tối Cao bên trên tất cả vạn vật, hãy nhắc tên Ngài khi Ngươi nhớ đến Ngài và tán dương Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(87,2,'87:2','Ngài là Đấng đã tạo ra con người và ban cho y hình thể.');
INSERT INTO "vn_mokhtasar" VALUES(87,3,'87:3','Là Đấng định cho mọi vạn vật về hình dạng, thể loại, thuộc tính và hướng dẫn mọi tạo vật đến với điều phù hợp nhất với nó.');
INSERT INTO "vn_mokhtasar" VALUES(87,4,'87:4','Ngài là Đấng làm mọc ra cây cỏ xanh tươi để các ngươi chăn nuôi thú nuôi của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(87,5,'87:5','Và cũng chính Ngài làm cho chúng trở nên những cộng rạ khô sau khi đã xanh tươi.');
INSERT INTO "vn_mokhtasar" VALUES(87,6,'87:6','TA sẽ đọc cho Ngươi nghe - hỡi Thiên Sứ - về Qur''an, TA sẽ gom Nó đặt cố định trong lòng Ngươi, khiến Người sẽ không bao giờ quên Nó, kể cả Jibril không thể cố nhớ lâu hơn Ngươi mặc dù rất cố gắng không cho quên.');
INSERT INTO "vn_mokhtasar" VALUES(87,7,'87:7','Trừ những gì Allah muốn cho Ngươi quên vì một ý nghĩa nào đó. Quả thật, Ngài là Đấng biết tất cả những điều công khai và những điều thầm kín, không có điều gì che giấu được Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(87,8,'87:8','Và TA luôn tạo mọi sự dễ dàng và thuận lợi cho Ngươi làm những điều hài lòng Allah khiến Ngươi vào Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(87,9,'87:9','Ngươi hãy nhắc nhở thiên hạ bằng những điều được thiên khải cho Ngươi trong Qur''an, Ngươi hãy cố gắng nhắc nhở họ khi lời nhắc nhở đang được lắng nghe.');
INSERT INTO "vn_mokhtasar" VALUES(87,10,'87:10','Lời cảnh tỉnh của Ngươi khiến những ai kính sợ Thượng Đế biết quay đầu.');
INSERT INTO "vn_mokhtasar" VALUES(87,11,'87:11','Người vô đức tin tự xa lánh lời nhắc nhở này, bởi họ thuộc nhóm người bất hạnh nhất ở Đời Sau phải vào Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(87,12,'87:12','Rồi đây chúng sẽ bị đày vào ngọn lửa khổng lồ ở Đời Sau, bị cái nóng của nó thiêu đốt muôn đời.');
INSERT INTO "vn_mokhtasar" VALUES(87,13,'87:13','Vào trong đó họ sẽ không chết hầu tránh được sự đau đớn của hình phạt nhưng cũng không thể sống một cách thoải mái.');
INSERT INTO "vn_mokhtasar" VALUES(87,14,'87:14','Chắc chắn, chiến thắng sẽ thuộc về người biết giữ mình sạch khỏi tội Shirk và tội lỗi khác.');
INSERT INTO "vn_mokhtasar" VALUES(87,15,'87:15','Luôn tưởng nhớ đến Allah bằng mọi hình thức cho phép tưởng nhớ và luôn dâng lễ nguyện Salah một cách chu đáo.');
INSERT INTO "vn_mokhtasar" VALUES(87,16,'87:16','Không, các ngươi thường chỉ bận tâm với cuộc sống trần giàn này mà không màng tới cuộc sống hạnh phúc trường tồn ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(87,17,'87:17','Cuộc sống ở cõi Đời Sau mới là cuộc sống hạnh phúc hơn, tốt đẹp hơn cuộc sống của thế giới trần gian, và nó sẽ là cuộc sống trường tồn vĩnh viễn.');
INSERT INTO "vn_mokhtasar" VALUES(87,18,'87:18','Quả thật, những gì được nói trong chương này cũng được nói trong các tờ Kinh được ban xuống trước Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(87,19,'87:19','Đó là các tờ Kinh được ban xuống cho Ibrahim và cho Musa.');
INSERT INTO "vn_mokhtasar" VALUES(88,1,'88:1','Này hỡi Thiên Sứ, Ngươi có nghe về Ngày Tận Thế, ngày bao trùm tất cả toàn nhân loại chưa?');
INSERT INTO "vn_mokhtasar" VALUES(88,2,'88:2','Vào Ngày đó, tức Ngày Phục sinh con người sẽ được chia thành hai nhóm hoặc là hạnh phúc hoặc là bất hạnh, gương mặt của những người bất hạnh sẽ trở nên thấp hèn đầy nhục nhã.');
INSERT INTO "vn_mokhtasar" VALUES(88,3,'88:3','Mệt mỏi bị đóng cùm, bị xiềng xích trói chặt kéo đi.');
INSERT INTO "vn_mokhtasar" VALUES(88,4,'88:4','Những gương mặt đó bị đày vào Hỏa Ngục với ngọn lửa cháy dữ dội.');
INSERT INTO "vn_mokhtasar" VALUES(88,5,'88:5','Sẽ được cho uống nước lấy từ một ngọn suối cực nóng.');
INSERT INTO "vn_mokhtasar" VALUES(88,6,'88:6','Người bị trừng phạt sẽ không tìm thấy bất cứ loại thức ăn nào ngoài một loại thức ăn kinh tởm có tên Shibriq (thuộc họ đậu) khi khô sẽ chứa đựng độc tố.');
INSERT INTO "vn_mokhtasar" VALUES(88,7,'88:7','Thức ăn này không mang lại dinh dưỡng cũng chẳng làm cho hết cơn đói.');
INSERT INTO "vn_mokhtasar" VALUES(88,8,'88:8','Vào Ngày hôm đó, những gương mặt hạnh phúc sẽ tươi cười rạng rỡ khi thấy được Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(88,9,'88:9','Đó là phần thưởng dành cho họ vì đã nỗ lực hành đạo ở trên thế gian và được hài lòng. Họ sẽ thấy được phần thưởng được nhân lên nhiều lần.');
INSERT INTO "vn_mokhtasar" VALUES(88,10,'88:10','Trong Thiên Đàng có các ngôi vườn trên cao.');
INSERT INTO "vn_mokhtasar" VALUES(88,11,'88:11','Trong Thiên Đàng sẽ không nghe thấy lời nói sàm bậy cũng như lời bẩn thỉu nào.');
INSERT INTO "vn_mokhtasar" VALUES(88,12,'88:12','Trong Thiên Đàng có những con suối chảy được tùy thích sử dụng.');
INSERT INTO "vn_mokhtasar" VALUES(88,13,'88:13','Trong Thiên Đàng có các bệ ngồi cao.');
INSERT INTO "vn_mokhtasar" VALUES(88,14,'88:14','Với những cốc thức uống tuyệt hảo được dọn sẵn.');
INSERT INTO "vn_mokhtasar" VALUES(88,15,'88:15','Có những chiếc gội để tựa mình êm ái được xếp dựa vào nhau.');
INSERT INTO "vn_mokhtasar" VALUES(88,16,'88:16','Trên những bệ ngồi được trải khắp nơi bằng những tấm thảm được thêu trổ xinh đẹp.');
INSERT INTO "vn_mokhtasar" VALUES(88,17,'88:17','Há chúng không suy ngẫm về việc Allah đã tạo ra lạc đà và khống chế nó cho con người sử dụng hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(88,18,'88:18','Há chúng không nhìn thấy bầu trời được nhấc lên trên cao, nó trở trành vòm che cố định và không bị rơi thế nào ư?');
INSERT INTO "vn_mokhtasar" VALUES(88,19,'88:19','Há chúng không nhìn thấy những quả núi được dựng đứng một cách kiên cô và vững chắc như thế nào, để phục vụ nhu cầu của con người?');
INSERT INTO "vn_mokhtasar" VALUES(88,20,'88:20','Há chúng không nhìn trái đất được trải rộng thênh thang và bằng phẳng như thế nào sao?');
INSERT INTO "vn_mokhtasar" VALUES(88,21,'88:21','Hỡi Thiên Sứ, Ngươi hãy nhắc nhở và khuyên bảo những kẻ này về hình phạt của Allah, Ngươi chỉ là một người nhắc nhở và cảnh tỉnh, không yêu cầu Ngươi ngoài điều đó, còn việc họ có tin hay không thì nằm trong tay của Allah duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(88,22,'88:22','Chứ Ngươi không phải là người cưỡng ép bắt chúng đến với đức tin Iman.');
INSERT INTO "vn_mokhtasar" VALUES(88,23,'88:23','Người nào quay mặt với đức tin và phủ nhận Allah và Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(88,24,'88:24','Allah sẽ trừng phạt y với một sự trừng phạt vô cùng đau đớn và mãi mãi nơi Hỏa Ngục trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(88,25,'88:25','Quả thật, họ sẽ phải quay trở lại gặp TA sau khi chết.');
INSERT INTO "vn_mokhtasar" VALUES(88,26,'88:26','Rồi TA sẽ thanh toán tức sẽ trừng phạt chúng một cách thích đáng về những gì chúng đã làm, chứ không phải nhà Người cũng không phải ai khác.');
INSERT INTO "vn_mokhtasar" VALUES(89,1,'89:1','Allah Hiển Vinh đã thề bởi rạng đông.');
INSERT INTO "vn_mokhtasar" VALUES(89,2,'89:2','Ngài thề bởi mười đêm đầu của tháng Zdul Hijjah.');
INSERT INTO "vn_mokhtasar" VALUES(89,3,'89:3','Ngài thề bởi sự chẵn và lẻ của mọi thứ.');
INSERT INTO "vn_mokhtasar" VALUES(89,4,'89:4','Và Ngài thề bởi ban đêm khi bóng tối của nó đến và đi; và những lời thề này nhằm khẳng định chắc chắn rằng mọi hành động của các ngươi sẽ bị tính sổ.');
INSERT INTO "vn_mokhtasar" VALUES(89,5,'89:5','Lẽ nào những gì được Ngài thề không đủ thuyết phục cho những người có khối óc biết suy nghĩ chăng?');
INSERT INTO "vn_mokhtasar" VALUES(89,6,'89:6','Này Thiên Sứ, Ngươi không thấy Thượng Đế của Ngươi đã trừng phạt cộng đồng của ''Ad - dân của Hud - khi chúng phủ nhận Thiên Sứ của Ngài ư?!');
INSERT INTO "vn_mokhtasar" VALUES(89,7,'89:7','Một bộ lạc của ''Ad được gọi theo tên của ông tổ có thân hình cao to, khỏe mạnh.');
INSERT INTO "vn_mokhtasar" VALUES(89,8,'89:8','Nơi mà không có nơi nào hùng mạnh giống như nó cả.');
INSERT INTO "vn_mokhtasar" VALUES(89,9,'89:9','Và Ngươi đã không thấy Thượng Đế của Ngươi đã trừng phạt như thế nào với Thamud, cộng đồng của Saleh, những người được ban cho khả năng đục đá làm nhà trong thung lũng đó ư?!');
INSERT INTO "vn_mokhtasar" VALUES(89,10,'89:10','Và Ngươi đã không thấy Thượng Đế của Ngươi đã trừng phạt như thế nào với Fir’awn (Pharaon), một tên bạo chúa của Ai Cập, dùng quyền lực đàn áp thiên hạ?!');
INSERT INTO "vn_mokhtasar" VALUES(89,11,'89:11','Tất cả bọn chúng đã tàn bạo và gian ác trong xứ sở.');
INSERT INTO "vn_mokhtasar" VALUES(89,12,'89:12','Chúng toàn làm điều xấu xa, tội lỗi và vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(89,13,'89:13','Do đó, Allah cho chúng nếm sự trừng phạt kinh hoàng, xóa sạch chúng khỏi trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(89,14,'89:14','Quả thật, Thượng Đế của Người - hỡi Thiên Sứ - luôn quan sát và theo dõi việc làm và hành động của nhân loại để Ngài sẽ ban thưởng Thiên Đàng cho người làm tốt và trừng phạt kẻ xấu trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(89,15,'89:15','Con người mỗi khi được thử thách với nhiều ân huệ và nhiều bổng lộc thì y nghĩ rằng đó là điều cao quý và vinh dự mà Thượng Đế dành cho y, và y nói: Thượng Đế của tôi đã thực sư ưu đãi tôi.');
INSERT INTO "vn_mokhtasar" VALUES(89,16,'89:16','Và khi con người được Thượng Đế thử thách với sự thu hẹp bổng lộc thì y nghĩ rằng đó là sự hạ nhục mà Allah dành cho y, y bảo: Thượng Đế của tôi đã hạ nhục tôi.');
INSERT INTO "vn_mokhtasar" VALUES(89,17,'89:17','Sự việc không phải như con người nghĩ, rằng người được Allah ưu đãi với nhiều ân huệ là người được Ngài hài lòng, còn người bị thu hẹp bổng lộc là người bị Ngài hạ nhục. Thực tế, là họ đã không nuôi dưỡng trẻ mồ côi bằng bổng lộc mà Allah cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(89,18,'89:18','Cũng không khuyến khích nhau nuôi ăn và giúp đỡ người nghèo khó và túng thiếu.');
INSERT INTO "vn_mokhtasar" VALUES(89,19,'89:19','Các người luôn tham lam, toan tính chiếm đoạt quyền lợi người yếu thế như trẻ mồ côi, phụ nữ một cách vô nhân đạo.');
INSERT INTO "vn_mokhtasar" VALUES(89,20,'89:20','Các ngươi yêu thích tài sản đến cuồng si và keo kiệt trong việc chi dùng cho con đường chính nghĩa của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(89,21,'89:21','Đáng lẽ ra các ngươi không nên làm như vậy, hãy nhớ rằng rồi đây, khi trái đất bị chấn động và tiêu vong.');
INSERT INTO "vn_mokhtasar" VALUES(89,22,'89:22','Rồi Thượng Đế của Ngươi - hỡi Thiên Sứ - đến để phân xử đám nô lệ của Ngài và các Thiên Thần sẽ đến trình diện đứng xếp thành rất nhiều hàng nghiêm trang.');
INSERT INTO "vn_mokhtasar" VALUES(89,23,'89:23','Vào Ngày Phán Xét, Hỏa Ngục được kéo đến với bảy mươi ngàn khoen cầm, mỗi khoen cầm có bảy mươi ngàn Thiên Thần nắm kéo. Ngày đó, con người sẽ tỉnh ngộ về việc y đã không hoàn thành bổn phận với Allah nhưng sự tỉnh ngộ đó không giúp ích được gì nữa, bởi đó là Ngày thưởng phạt chứ không phải là ngày tích lũy.');
INSERT INTO "vn_mokhtasar" VALUES(89,24,'89:24','Y than thở trong sự hối hận: Ôi, giá như trước kia trên thế gian mình làm điều thiện tốt và ngoan đạo dành cho cuộc sống đích thực này!');
INSERT INTO "vn_mokhtasar" VALUES(89,25,'89:25','Ngày hôm đó không ai trừng phạt như Allah trừng phạt cả, bởi hình phạt của Allah khủng khiếp nhất và mãi mãi.');
INSERT INTO "vn_mokhtasar" VALUES(89,26,'89:26','Và cũng không một ai có khả năng trói buộc bằng xiềng xích giống như cách trói buộc của Ngài dành cho nhóm người vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(89,27,'89:27','Đối với linh hồn có đức tin sẽ được trấn an trong lúc chết và trong Ngày Tận Thế: Này hỡi linh hồn thanh thản và yên tâm với niềm tin và việc làm ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(89,28,'89:28','Hãy trở về với Thượng Đế của ngươi một cách toại nguyện với những gì Allah ban cho ngươi và được Ngài hài lòng qua việc làm ngoan đạo của ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(89,29,'89:29','Hãy vào cùng với những bề tôi ngoan đạo của TA.');
INSERT INTO "vn_mokhtasar" VALUES(89,30,'89:30','Và hãy đi vào Thiên Đàng của TA cùng với họ, nơi được TA chuẩn bị cho họ.');
INSERT INTO "vn_mokhtasar" VALUES(90,1,'90:1','Allah thề bởi xứ sở Haram tức Makkah thiêng liêng.');
INSERT INTO "vn_mokhtasar" VALUES(90,2,'90:2','Và Ngươi – hỡi Thiên Sứ – là cư dân tự do được phép làm bất cứ gì trong xứ sở thiêng liêng này, Ngươi được quyền giết ai đáng bị giết, được quyền giam cầm ai đáng bị giam cầm.');
INSERT INTO "vn_mokhtasar" VALUES(90,3,'90:3','Allah thề bởi tổ phụ của loài người đó là Adam và các thế hệ dòng dõi của Người.');
INSERT INTO "vn_mokhtasar" VALUES(90,4,'90:4','Quả thật, TA đã tạo con người và y phải chịu sự nhọc nhằn và đau khổ về tâm trí lẫn thể xác trên thế gian.');
INSERT INTO "vn_mokhtasar" VALUES(90,5,'90:5','Há con người nghĩ rằng khi y làm tội lỗi thì không ai có quyền cấm cản hay trừng phạt y ư, kể cả Thượng Đế đã tạo ra y luôn?');
INSERT INTO "vn_mokhtasar" VALUES(90,6,'90:6','Con người thường nói quá: Tôi đã chi dùng rất nhiều tài sản.');
INSERT INTO "vn_mokhtasar" VALUES(90,7,'90:7','Há tên khoe khoang này nghĩ rằng những gì có lợi cho y sẽ giúp y thoát được tầm nhìn của Allah ư? Chẳng lẽ y nghĩ rằng y sẽ không bị thanh toán bởi tiền bạc đó rằng y đã kiếm được từ đâu và chi dùng ra sao ư?');
INSERT INTO "vn_mokhtasar" VALUES(90,8,'90:8','Há TA đã không tạo cho con người cặp mắt để nhìn ngắm và quan sát?');
INSERT INTO "vn_mokhtasar" VALUES(90,9,'90:9','cùng chiếc lưỡi và đôi môi để nói chuyện hay sao?');
INSERT INTO "vn_mokhtasar" VALUES(90,10,'90:10','Và vạch ra cho y biết rõ hai con đường tốt và con đường xấu?');
INSERT INTO "vn_mokhtasar" VALUES(90,11,'90:11','Yêu cầu y phải vượt qua sự trở ngại đó đã chia cắt y với Thiên Đàng bằng việc bố thí và việc làm ngoan đạo.');
INSERT INTO "vn_mokhtasar" VALUES(90,12,'90:12','Và Ngươi - hỡi Thiên Sứ - có biết sự trở ngại mà y vượt qua nó để vào Thiên Đàng là gì không?');
INSERT INTO "vn_mokhtasar" VALUES(90,13,'90:13','Đó là trả tự do cho một nô lệ dù nam hay nữ.');
INSERT INTO "vn_mokhtasar" VALUES(90,14,'90:14','Hoặc nuôi ăn vào một ngày đói khát.');
INSERT INTO "vn_mokhtasar" VALUES(90,15,'90:15','Cho trẻ mồ côi không cha có quan hệ họ hàng.');
INSERT INTO "vn_mokhtasar" VALUES(90,16,'90:16','Hoặc cho người nghèo đói không sở hữu bất cứ gì.');
INSERT INTO "vn_mokhtasar" VALUES(90,17,'90:17','Những người luôn làm điều thiện tốt và luôn hướng đức tin của mình nơi Allah, khuyên nhủ nhau kiên nhẫn, vâng lệnh Allah, tránh xá điều tội lỗi, kết nối tình yêu thương nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(90,18,'90:18','Những người có những thuộc tính vừa nêu là nhóm người bạn của tay phải.');
INSERT INTO "vn_mokhtasar" VALUES(90,19,'90:19','Những kẻ vô đức tin phủ nhận những gì mà TA đã thiên khải cho Thiên Sứ của TA sẽ là những người bạn của tay trái.');
INSERT INTO "vn_mokhtasar" VALUES(90,20,'90:20','Phần thưởng của chúng là bị lửa bao vây và thiêu đốt trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(91,1,'91:1','Allah thề bởi mặt Trời và bởi ánh nắng chiếu sáng rực của nó sau khi nó đã mọc lên cao.');
INSERT INTO "vn_mokhtasar" VALUES(91,2,'91:2','Ngài thề bởi mặt Trăng khi đuổi theo mặt Trời sau khi mặt Trời lặn.');
INSERT INTO "vn_mokhtasar" VALUES(91,3,'91:3','Ngài thề bởi ban ngày khi nó chiếu sáng bề mặt trái đất bằng ánh sáng của nó.');
INSERT INTO "vn_mokhtasar" VALUES(91,4,'91:4','Ngài thề bởi ban đêm khi nó bị che phủ tối mịt.');
INSERT INTO "vn_mokhtasar" VALUES(91,5,'91:5','Ngài thề bởi bầu trời và thề bởi công trình tinh vi của nó.');
INSERT INTO "vn_mokhtasar" VALUES(91,6,'91:6','Ngài thề bởi trái đất và thề bởi sự trải rộng thênh thang của nó để con người cư ngụ.');
INSERT INTO "vn_mokhtasar" VALUES(91,7,'91:7','Ngài thề bởi tất cả linh hồn và bởi tạo vật của Allah với hình dạng hoàn chỉnh.');
INSERT INTO "vn_mokhtasar" VALUES(91,8,'91:8','Ngài làm cho nó ý thức biết được điều xấu để tránh và điều tốt để thực hiện.');
INSERT INTO "vn_mokhtasar" VALUES(91,9,'91:9','Rằng quả thật, chắc chắn những ai biết giữ linh hồn trong sạch với những điều thiện tốt và biết tránh xa những điều xấu thì sẽ thành công.');
INSERT INTO "vn_mokhtasar" VALUES(91,10,'91:10','Còn những ai làm cho nó dơ bẩn bởi những tội lỗi thì sẽ bị thua thiệt và thất bại.');
INSERT INTO "vn_mokhtasar" VALUES(91,11,'91:11','Bằng sự vướt mức giới hạn trong việc pham điều tội lỗi, người dân Thamud đã chối bỏ Nabi của mình - Saleh.');
INSERT INTO "vn_mokhtasar" VALUES(91,12,'91:12','Một nhóm người trong bọn chúng đứng dậy muốn gây hại cho con lạc đà.');
INSERT INTO "vn_mokhtasar" VALUES(91,13,'91:13','Saleh, vị Thiên Sứ của Allah được gởi đến cho họ bảo: Hãy để mặc con lạc đà của Allah, các người hãy để mặc nó uống nước vào ngày của nó, chớ gây hại đến nó.');
INSERT INTO "vn_mokhtasar" VALUES(91,14,'91:14','Nhưng chúng đã cho đó là lời nói dối nên ngoan cố sát hại nó, tất cả bọn chúng đồng lòng phạm tội, thế nên Allah đã phẫn nộ và trừng phạt chúng với một sự trừng phạt thật đau đớn.');
INSERT INTO "vn_mokhtasar" VALUES(91,15,'91:15','Và Allah không hề lo sợ hậu quả cho việc trừng phạt chúng như thế.');
INSERT INTO "vn_mokhtasar" VALUES(92,1,'92:1','Allah thề bởi ban đêm khi nó bao trùm trời đất bằng bóng tối của nó.');
INSERT INTO "vn_mokhtasar" VALUES(92,2,'92:2','Ngài thề bởi ban ngày khi nó xuất hiện.');
INSERT INTO "vn_mokhtasar" VALUES(92,3,'92:3','Ngài thề bởi sự tạo hóa ra hai giới đực cái.');
INSERT INTO "vn_mokhtasar" VALUES(92,4,'92:4','Quả thật, việc làm của các ngươi - hỡi con người - khác nhau, có người ngoan đạo sẽ được vào Thiên Đàng và có người làm tội lỗi sẽ bị vào Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(92,5,'92:5','Đối với ai cho đi thứ bắt buộc như Zakah, cấp dưỡng, chuộc tội và biết kính sợ mà tránh điều bị Allah cấm.');
INSERT INTO "vn_mokhtasar" VALUES(92,6,'92:6','Và luôn tin tưởng những gì được Allah hứa hẹn ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(92,7,'92:7','TA sẽ tạo điều kiện dễ dàng cho y trong việc hành đạo và làm việc thiện cũng như chi dùng tiền của cho con đường của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(92,8,'92:8','Đối với ai keo kiệt, ôm lấy tài sản không chịu chi dùng cho những điều bắt buộc đối với bản thân y và tự cho mình đã đầy đủ không cần xin Allah bất cứ gì.');
INSERT INTO "vn_mokhtasar" VALUES(92,9,'92:9','Và bác bỏ những gì được Allah hứa hẹn ở Đời Sau và phần thưởng dành cho tài sản chi dùng vì con đường của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(92,10,'92:10','TA sẽ tạo điều kiện thuận lợi giúp y dễ dàng làm điều xấu những lại rất khó khăn khi làm việc thiện.');
INSERT INTO "vn_mokhtasar" VALUES(92,11,'92:11','Và vào Ngày đó, tài sản và của cải của họ chẳng giúp ích được gì cho họ khi họ bị trừng phạt trong Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(92,12,'92:12','Quả thật, bổn phận của TA trình bày rõ ràng con đường Chân Lý và con đường ngụy tạo và lệch lạc.');
INSERT INTO "vn_mokhtasar" VALUES(92,13,'92:13','Chính Ngài mới là Đấng có quyền chi phối thế giới trần gian và Đời Sau. Ngoài Ngài không một ai có thẩm quyền đó cả.');
INSERT INTO "vn_mokhtasar" VALUES(92,14,'92:14','Ta cảnh báo các ngươi - hỡi nhân loại - về ngọn lửa cháy dữ dội dành cho những ai nghịch lại Allah.');
INSERT INTO "vn_mokhtasar" VALUES(92,15,'92:15','Trong Hỏa Ngục, không ai bị đày vào đó ngoài những kẻ bất hạnh, đó là nhóm người vô đức tin.');
INSERT INTO "vn_mokhtasar" VALUES(92,16,'92:16','Đó là những kẻ phủ nhận Thiên Sứ và không phục tùng mệnh lệnh của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(92,17,'92:17','Những ai kính sợ Allah sẽ được Ngài cứu thoát khỏi Hỏa Ngục điển hình như Abu Bakr.');
INSERT INTO "vn_mokhtasar" VALUES(92,18,'92:18','Người đã chi dùng tài sản cho con đường chính nghĩa của Allah để được xóa tội.');
INSERT INTO "vn_mokhtasar" VALUES(92,19,'92:19','Và y bố thí cho người nhưng không mong được bất cứ ai trả ơn mình.');
INSERT INTO "vn_mokhtasar" VALUES(92,20,'92:20','Với tài sản cho đi y chỉ mong được Allah Tối Cao ban thưởng ở Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(92,21,'92:21','Đo dó, Allah sẽ làm y toại nguyện hài lòng với những phần thưởng Ngài ban cho.');
INSERT INTO "vn_mokhtasar" VALUES(93,1,'93:1','Allah thề bởi buổi ban mai.');
INSERT INTO "vn_mokhtasar" VALUES(93,2,'93:2','Ngài thề bởi ban đêm khi mọi vật đều tĩnh lặng.');
INSERT INTO "vn_mokhtasar" VALUES(93,3,'93:3','Rằng Ngài sẽ không bỏ rơi Ngươi - hỡi Thiên Sứ - cũng như Ngài chẳng ghét bỏ Ngươi khi Ngài cho gián đoạn lời mặc khải trong một thời gian khiến Ngươi bị nhóm đa thần chỉ trích.');
INSERT INTO "vn_mokhtasar" VALUES(93,4,'93:4','Cuộc sống Đời Sau tốt hơn cuộc sống trần gian này, với sự hưởng thụ mãi mãi và bất tận.');
INSERT INTO "vn_mokhtasar" VALUES(93,5,'93:5','Và Ngươi và tín đồ của Ngươi sẽ được Ngài ban cho nhiều ân huệ và hồng phúc ở Đời Sau và chắc chắn Ngươi và họ đều sẽ hài lòng và toại nguyện.');
INSERT INTO "vn_mokhtasar" VALUES(93,6,'93:6','Chẳng phải trước đó, Ngươi là trẻ mồ côi không cha và Allah đã ban cho Ngươi một chỗ nương tựa và cưu mang Ngươi như ông nội Ngươi ''Abdul Muttalib, rồi đến bác Ngươi là Abu Talib.');
INSERT INTO "vn_mokhtasar" VALUES(93,7,'93:7','Ngài thấy Ngươi không biết gì về Kinh Sách cũng như đức tin Iman, thế là Ngài đã dạy Ngươi thứ mà Ngươi chưa từng biết.');
INSERT INTO "vn_mokhtasar" VALUES(93,8,'93:8','Ngài thấy Ngươi nghèo khó nên Ngài đã ban cho Ngươi nhiều bổng lộc, giàu sang.');
INSERT INTO "vn_mokhtasar" VALUES(93,9,'93:9','Đối với trẻ mồ côi Ngươi hãy đối xử tử tế với chúng, đừng ruồng bỏ chúng, khinh miệt chúng.');
INSERT INTO "vn_mokhtasar" VALUES(93,10,'93:10','Đối với người ăn xin thì Ngươi đừng la mắng và nhục mạ.');
INSERT INTO "vn_mokhtasar" VALUES(93,11,'93:11','Và đối với ân huệ mà Allah ban cho Ngươi thì Ngươi hãy công khai tuyên bố.');
INSERT INTO "vn_mokhtasar" VALUES(94,1,'94:1','Quả thật Allah đã mở rộng tấm lòng của Ngươi để Ngươi sẵn lòng đón nhận lời mặc khải.');
INSERT INTO "vn_mokhtasar" VALUES(94,2,'94:2','Và TA đã xóa hết cho Ngươi mọi tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(94,3,'94:3','Điều đã đeo bám Ngươi khiến lưng Ngươi sắp bị gãy.');
INSERT INTO "vn_mokhtasar" VALUES(94,4,'94:4','Và TA đã đưa Ngươi lên cao với danh tiếng và địa vị của Ngươi qua lời Azdan, qua lời Iqamah và qua các điều khác.');
INSERT INTO "vn_mokhtasar" VALUES(94,5,'94:5','Quả thật, sự khó khăn, cơ cực luôn đồng hành cùng dễ dàng và rộng mở.');
INSERT INTO "vn_mokhtasar" VALUES(94,6,'94:6','Quả thật, sự khó khăn, cơ cực luôn đồng hành cùng dễ dàng và rộng mở. Khi Ngươi biết được điều đó thì Ngươi không còn hoang mang trước việc bị dân chúng mình cấm cản Ngươi kêu gọi đến với Allah.');
INSERT INTO "vn_mokhtasar" VALUES(94,7,'94:7','Cho nên, khi nào Ngươi hoàn thành công việc thì hãy năng nổ hành đạo thờ phượng Thượng Đế của Ngươi Ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(94,8,'94:8','Hãy hướng tấm lòng và khao khát của Người đến Allah duy nhất.');
INSERT INTO "vn_mokhtasar" VALUES(95,1,'95:1','Allah đã thề bởi câu Sung và cây Ô Liu và nơi mọc ra hai loại cây này ở đất Palestine, nơi mà Ysa nhận lãnh sứ mạng.');
INSERT INTO "vn_mokhtasar" VALUES(95,2,'95:2','Ngài thề bởi ngọn núi Saina'' nơi Allah đã nói chuyện với Musa.');
INSERT INTO "vn_mokhtasar" VALUES(95,3,'95:3','Ngài cũng thề bởi xứ sở Haram, nơi bình an dành cho những ai vào trong nó, là nơi mà Thiên Sứ Muhammad nhận lãnh sứ mạng, đó là Makkah thiêng liêng.');
INSERT INTO "vn_mokhtasar" VALUES(95,4,'95:4','Rằng quả thật, TA đã tạo con người với một vóc dạng cân đối và tốt đẹp nhất.');
INSERT INTO "vn_mokhtasar" VALUES(95,5,'95:5','Sau đó, TA lại đẩy con người xuống vị trí thấp kém và hèn hạ nhất ở chốn trần gian, không gì giúp ích cho y khi y bị đày vào Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(95,6,'95:6','Ngoại trừ những ai có đức tin và làm việc thiện thì họ sẽ được ban thưởng với một một phần thưởng vĩ đại và vô tận ở nơi Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(95,7,'95:7','Này con người, nếu mọi việc được trình bày rõ như vậy thì điều gì đã khiến ngươi phủ nhận đức tin, phủ nhận sự phục sinh và thưởng phạt?');
INSERT INTO "vn_mokhtasar" VALUES(95,8,'95:8','Há không phải việc Allah - thiết lập Ngày Tận Thế để thưởng phạt - là sự phân định công bằng nhất hay sao? Lẽ nào Allah lại bỏ mặc đám nô lệ của Ngài sống sao tùy thích, không phân xử họ, không, Ngài sẽ thưởng trọng hậu cho người ngoan đạo và trừng phạt thích đáng kẻ bất tuân.');
INSERT INTO "vn_mokhtasar" VALUES(96,1,'96:1','Hãy đọc đi - hỡi Thiên Sứ - những gì Allah đã mặc khải cho Ngươi, hãy bắt đầu bằng Bismillah - nhân danh Allah - Đấng đã tạo hóa tất cả vạn vật.');
INSERT INTO "vn_mokhtasar" VALUES(96,2,'96:2','Ngài đã tạo hóa con người từ hòn máu đặc sau khi nó chỉ là giọt tinh dịch.');
INSERT INTO "vn_mokhtasar" VALUES(96,3,'96:3','Này hỡi Nabi, Ngươi hãy đọc những gì được ban xuống cho Ngươi bởi Thượng Đế của Ngươi rất rộng lượng và bao dung.');
INSERT INTO "vn_mokhtasar" VALUES(96,4,'96:4','Ngài là Đấng đã dạy nhân loại chữ viết bằng cây viết.');
INSERT INTO "vn_mokhtasar" VALUES(96,5,'96:5','Ngài dạy con người những điều y vốn chưa từng biết.');
INSERT INTO "vn_mokhtasar" VALUES(96,6,'96:6','Thực tế, kẻ hư đốn như Abu Jahal đã vượt quá giới hạn của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(96,7,'96:7','Bởi vì y tự mãn về tài sản phong phú và quyền lực hiện tại.');
INSERT INTO "vn_mokhtasar" VALUES(96,8,'96:8','Nhưng ngươi - hỡi con người - sẽ phải quay trở lại với Allah để Ngài xét xử và thanh toán trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(96,9,'96:9','Há Ngươi còn thấy việc nào lạ đời hơn việc Abu Jahal đã cấm cản.');
INSERT INTO "vn_mokhtasar" VALUES(96,10,'96:10','Nô lệ Muhammad của TA khi Y hành lễ Salah tại Ka''bah hay không?');
INSERT INTO "vn_mokhtasar" VALUES(96,11,'96:11','Lẽ nào tên cấm cản đó lại được Thượng Đế chỉ thị y làm thế ư?');
INSERT INTO "vn_mokhtasar" VALUES(96,12,'96:12','Hay là kẻ ra lệnh mọi người biết kính sợ Allah, biết phục tùng Ngài lại có hành động cấm cản thế sao?');
INSERT INTO "vn_mokhtasar" VALUES(96,13,'96:13','Lẽ nào kẻ quay lưng với sự thật và ngăn cản điều mà Thiên Sứ truyền đạt lại không biết sợ Allah ư?');
INSERT INTO "vn_mokhtasar" VALUES(96,14,'96:14','Hay là kẻ cấm cản nô lệ của Ngài hành lễ Salah cho rằng Allah không nhìn thấy hành động của hắn ư, trong khi không gì giấu được Ngài cả?');
INSERT INTO "vn_mokhtasar" VALUES(96,15,'96:15','Chắc chắn sự việc sẽ không như tên Abu Jahal đã nghĩ, nếu hắn không chịu dừng lại thì TA sẽ túm mạnh lấy chùm tóc láo khoét của hắn mà ném vào Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(96,16,'96:16','Kẻ bị nắm chùm tóc đó là tên nói bậy và làm sai.');
INSERT INTO "vn_mokhtasar" VALUES(96,17,'96:17','Cứ để mặc hắn kêu gọi đồng bọn của hắn, gia đình hắn đến giúp đỡ khi hắn bị túm đầu kéo vào Hỏa Ngục chịu trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(96,18,'96:18','TA cho gọi các Thiên Thần gác Hỏa Ngục luôn lạnh lùng không dám nghịch lệnh Allah khi Ngài ra lệnh Họ trừng phạt hắn, xem ai sẽ mạnh hơn.');
INSERT INTO "vn_mokhtasar" VALUES(96,19,'96:19','Nhưng thực tế, kẻ bất công này không thể thực hiện điều mà hắn muốn hãm hại Ngươi, do đó, Ngươi chớ nghe theo những gì hắn kêu gọi và cấm cản mà hãy năng cúi đầu quỳ lạy Allah, hãy năng phục tùng Ngài để được đến gần Ngài hơn.');
INSERT INTO "vn_mokhtasar" VALUES(97,1,'97:1','Quả thật, TA đã ban toàn bộ Qur’an xuống tầng trời hạ giới, cũng như việc TA bắt đầu thiên khải Nó xuống cho Thiên Sứ vào đêm định mệnh của tháng Ramadan.');
INSERT INTO "vn_mokhtasar" VALUES(97,2,'97:2','Này Nabi, Ngươi biết đêm định mệnh thiêng liêng này có hồng phúc thế nào không?');
INSERT INTO "vn_mokhtasar" VALUES(97,3,'97:3','Đêm định mệnh này là đêm có hồng phúc tốt hơn một ngàn tháng dành cho ai đứng Salah bằng đức tin và lòng kỳ vọng.');
INSERT INTO "vn_mokhtasar" VALUES(97,4,'97:4','Vào đêm định mệnh, sẽ có nhiều Thiên Thần và cả Đại Thiên Thần Jibril đi xuống, mang theo Quyết định của Allah về mọi điều cho năm đó như bổng lộc hoặc chết chóc, hoặc sinh nở, hoặc các tiền định khác theo chỉ dụ của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(97,5,'97:5','Nguyên đêm hồng phúc đó chỉ toàn là sự bằng an, không có một điều xấu nào xảy đến, nó kéo dài cho tới hừng đông.');
INSERT INTO "vn_mokhtasar" VALUES(98,1,'98:1','Những kẻ vô đức tin trong số những người Do thái, Thiên Chúa, và những người đa thần đã không chịu từ bỏ sự vô đức tin của họ cho đến khi nào những dấu hiệu được nói trong các Kinh Sách trước đây đến với họ.');
INSERT INTO "vn_mokhtasar" VALUES(98,2,'98:2','Bằng chứng rõ ràng và thuyết phục đó là Thiên Sứ của Allah, được cử phái đến để đọc Qur’an trong các tờ Kinh trong sạch chỉ có người sạch sẽ mới chạm được Nó.');
INSERT INTO "vn_mokhtasar" VALUES(98,3,'98:3','Trong các tờ Kinh đó là những nguồn tin trung thực, những mệnh lệnh công bằng, sự chỉ đạo đến với Chân Lý, với con đường ngay chính.');
INSERT INTO "vn_mokhtasar" VALUES(98,4,'98:4','Nhóm Do Thái đã được ban cho Kinh Tawrah (Cựu Ước) và nhóm Thiên Chúa đã được ban cho Kinh Injil (Tân ước) đã không bất đồng và chia rẽ nhau cho tới khi Allah gởi Nabi của Ngài đến với họ. Trong họ có người gia nhập theo Islam và có người vẫn tiếp tục vô đức tin mặc dù họ luôn biết sứ mạng của Người là thật.');
INSERT INTO "vn_mokhtasar" VALUES(98,5,'98:5','Tất cả các hệ thống giáo luật từ Allah dù đối với Do Thái, với Thiên Chúa và cả Qur''an đều ra lệnh bảo phải thờ phượng một mình Ngài duy nhất, phải toàn tâm hướng về Ngài trong thờ phượng, phải tuyệt đối loại trừ Shirk để đến với đức tin Iman, phải chu đáo dâng lễ nguyện Salah, và phải đóng Zakah. Như thế mới là tôn giáo ngay chính, không quanh co, uốn khúc.');
INSERT INTO "vn_mokhtasar" VALUES(98,6,'98:6','Những người Do Thái, Thiên Chúa và những người đa thần sẽ bị trừng phạt trong Hỏa Ngục trong Ngày Tận Thế, họ sẽ ở trong đó đời đời không có ngày trở ra, họ là tạo vật xấu xa nhất trong các tạo vật của Ngài vì tội phủ nhận Allah và chối bỏ sứ mạng của Thiên Sứ của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(98,7,'98:7','Những ai tin Allah và làm việc ngoan đạo, thiện tốt thì họ là tạo vật tốt đẹp nhất trong các tạo vật của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(98,8,'98:8','Phần thưởng của họ vào Đời Sau là Thiên Đàng với những ngôi vườn xanh tươi bên dưới có các dòng sông chảy, họ sẽ được sống trong đó đời đời với nguồn ân huệ và bổng lộc vô tận, họ sẽ được Allah hài lòng về đức tin của họ nơi Ngài, phục tùng Ngài và họ sẽ hài lòng về Rahmah của Ngài dành cho họ và phần Rahmah này chỉ cho ai biết sợ Thượng Đế của mình và tuân lệnh theo Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(99,1,'99:1','Khi trái đất bị chấn động dữ dội, một sự kiện của Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(99,2,'99:2','Và nó sẽ trút hết gánh nặng của nó tức trút hết những xác chết và mọi vật trong lòng nó.');
INSERT INTO "vn_mokhtasar" VALUES(99,3,'99:3','Và lúc bấy giờ con người sẽ hoảng sợ, nói: Có chuyện gì thế này, sao đất lại rung chuyển mạnh thế?');
INSERT INTO "vn_mokhtasar" VALUES(99,4,'99:4','Vào Ngày Vĩ Đại đó, Trái đất sẽ tường thuật lại hết mọi sự việc đã xảy ra trên lưng của nó cả điều tốt và điều xấu.');
INSERT INTO "vn_mokhtasar" VALUES(99,5,'99:5','Bởi Allah đã dạy cho nó và bắt nó phải làm điều đó.');
INSERT INTO "vn_mokhtasar" VALUES(99,6,'99:6','Ngày Vĩ Đại đó, con người sẽ đứng dậy, đi ra khỏi ngôi mộ và phân thành từng đoàn đến nhìn mọi việc làm tốt xấu của họ đã từng làm ở trần gian.');
INSERT INTO "vn_mokhtasar" VALUES(99,7,'99:7','Do đó, người nào làm việc tốt dù chỉ nhỏ bằng hạt nguyên tử thì vào Ngày đó cũng sẽ thấy nó ở trước mặt.');
INSERT INTO "vn_mokhtasar" VALUES(99,8,'99:8','Còn người nào làm việc xấu dù chỉ nhỏ như hạt nguyên tử thì cũng sẽ thấy nó ở trước mặt.');
INSERT INTO "vn_mokhtasar" VALUES(100,1,'100:1','Allah thề bởi những con ngựa chiến đang hùng hậu xông pha trên sa trường nghe được tiếng vó ngựa phi.');
INSERT INTO "vn_mokhtasar" VALUES(100,2,'100:2','Ngài thề bởi những con chiến mã phát ra lửa khi các móng chân hùng hậu của chúng chạy trên sa trường đã cọ xát mạnh vào đá làm bắn ra những tia lửa.');
INSERT INTO "vn_mokhtasar" VALUES(100,3,'100:3','Ngài thề bởi những con chiến mã hăng hái tấn công kẻ thù vào buổi sớm mai.');
INSERT INTO "vn_mokhtasar" VALUES(100,4,'100:4','Chúng xông mạnh vào kẻ thù làm tung bụi mịt mù.');
INSERT INTO "vn_mokhtasar" VALUES(100,5,'100:5','Tất cả đều tiến thẳng vào lòng quân địch một cách hùng hồn.');
INSERT INTO "vn_mokhtasar" VALUES(100,6,'100:6','Quả thật, con người đã vô ơn với biết bao ân huệ mà Allah ban cho y.');
INSERT INTO "vn_mokhtasar" VALUES(100,7,'100:7','Và sự vô ơn của y sẽ do bản thân y xác nhận và làm chứng khiến y không thể chối cãi.');
INSERT INTO "vn_mokhtasar" VALUES(100,8,'100:8','Và y thường chỉ biết yêu thích tiền tài và của cải và y luôn keo kiệt.');
INSERT INTO "vn_mokhtasar" VALUES(100,9,'100:9','Há con người không biết là rồi đây vào Ngày Phục Sinh, Allah sẽ cho tất cả sống lại và đi ra từ cõi mộ đến một nơi trình diện trước Allah để Ngài xét xử và thưởng phạt ư?');
INSERT INTO "vn_mokhtasar" VALUES(100,10,'100:10','Tất cả mọi điều trong tim như sự định tâm, niềm tin và các điều khác đều bị phơi bày.');
INSERT INTO "vn_mokhtasar" VALUES(100,11,'100:11','Quả thật, Allah biết rõ hết mọi điều, vào Ngày đó, không có điều gì có thể che giấu được Ngài, dựa vào đó Ngài sẽ thưởng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(101,1,'101:1','Thời khắc làm kinh hoàng các con tim của con người.');
INSERT INTO "vn_mokhtasar" VALUES(101,2,'101:2','Thời khắc làm các con tim con người phải kinh hoàng là gì vậy?');
INSERT INTO "vn_mokhtasar" VALUES(101,3,'101:3','Điều gì cho Ngươi biết - hỡi Thiên Sứ - về Thời khắc làm các con tim con người phải kinh hoàng đó? Đó chính là Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(101,4,'101:4','Vào Ngày đó, con người hoảng sợ chạy tán loạn như những con bươm bướm bay tứ tán.');
INSERT INTO "vn_mokhtasar" VALUES(101,5,'101:5','Những quả núi sẽ biến mất, hình ảnh của nó giống như những sợi lông cừu bị thổi tung lên và bay mất dạng.');
INSERT INTO "vn_mokhtasar" VALUES(101,6,'101:6','Đối với người nào mà những việc làm thiện tốt của y có trọng lượng nặng hơn việc tội lỗi.');
INSERT INTO "vn_mokhtasar" VALUES(101,7,'101:7','Y sẽ sống một cuộc sống vô cùng hạnh phúc và toại nguyện nơi Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(101,8,'101:8','Đối với người nào mà những việc làm tội lỗi của y có trọng lượng nặng hơn việc thiện tốt.');
INSERT INTO "vn_mokhtasar" VALUES(101,9,'101:9','Thì chỗ ở của y vào Ngày Tận Thế sẽ là Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(101,10,'101:10','Và điều gì cho Ngươi - hỡi Thiên Sứ - biết nó là thứ gì?');
INSERT INTO "vn_mokhtasar" VALUES(101,11,'101:11','Đó là nơi của những ngọn lửa có sức nóng khủng khiếp.');
INSERT INTO "vn_mokhtasar" VALUES(102,1,'102:1','Các ngươi - hỡi con người - đã bỏ bê và lơ là việc tuân lệnh Allah bởi vì quá bận rộn với việc muốn có nhiều tiền của và con cái.');
INSERT INTO "vn_mokhtasar" VALUES(102,2,'102:2','Mãi đến khi các ngươi đã đi vào trong mộ của các ngươi.');
INSERT INTO "vn_mokhtasar" VALUES(102,3,'102:3','Rồi đây, các ngươi chắc chắn sẽ sớm biết rằng lẽ ra không nên quá bận rộn với con cái và tiền của mà quên mất Allah như vậy, các ngươi sẽ sớm biết những gì ở Đời Sau sẽ tốt hơn những gì trên thế gian này.');
INSERT INTO "vn_mokhtasar" VALUES(102,4,'102:4','Rồi các ngươi sẽ sớm biết kết cục của mình.');
INSERT INTO "vn_mokhtasar" VALUES(102,5,'102:5','Đáng lẽ ra các ngươi phải biết chắc chắn một điều là các ngươi được phục sinh để trình diện Allah, Ngài sẽ thưởng phạt cho việc làm của các ngươi với việc các ngươi bận rộn khoe khoang, tự hào về tài sản và con cái.');
INSERT INTO "vn_mokhtasar" VALUES(102,6,'102:6','Thề bởi Allah, rồi các ngươi sẽ thấy Hỏa Ngục bằng mắt thật của các ngươi trong Ngày Tận Thế.');
INSERT INTO "vn_mokhtasar" VALUES(102,7,'102:7','Rồi chắc chắn các ngươi sẽ tận mắt nhìn thấy Hỏa Ngục, không còn có sự nghi ngờ nào nữa.');
INSERT INTO "vn_mokhtasar" VALUES(102,8,'102:8','Rồi trong Ngày hôm đó, Allah sẽ hạch hỏi các ngươi hết tất cả những lạc thú mà các ngươi đã hưởng thụ như sức khỏe, phú quý và mọi thứ khác.');
INSERT INTO "vn_mokhtasar" VALUES(103,1,'103:1','Đấng Hiển Vinh thề bởi thời gian của buổi chiều.');
INSERT INTO "vn_mokhtasar" VALUES(103,2,'103:2','Rằng con người chắc chắn sẽ bị thất bại và hủy diệt.');
INSERT INTO "vn_mokhtasar" VALUES(103,3,'103:3','Ngoại trừ những người có đức tin nơi Allah và Thiên Sứ của Ngài rồi làm việc thiện tốt và khuyên bảo nhau giữ gìn và duy trì Chân Lý cũng như khuyên bảo nhau kiên nhẫn trong việc tuân lệnh Ngài. Những người với các mô tả nay là những người thành công ở trần gian và Đời Sau.');
INSERT INTO "vn_mokhtasar" VALUES(104,1,'104:1','Những kẻ vu khống và nói xấu người khác sẽ bị nguyền rủa và sẽ bị trừng phạt.');
INSERT INTO "vn_mokhtasar" VALUES(104,2,'104:2','Kẻ chỉ biết lo toan cho việc thu nhặt tiền tài và chỉ biết đếm tiền để xem được bao nhiêu rồi.');
INSERT INTO "vn_mokhtasar" VALUES(104,3,'104:3','Y tưởng rằng bản thân y sẽ được tiền tài đảm bảo được sống mãi ở nơi trần gian, không phải bị chết.');
INSERT INTO "vn_mokhtasar" VALUES(104,4,'104:4','Chắc chắn sẽ không như y đã tưởng. Chắc chắn y sẽ bị đày trong Hỏa Ngục, nó sẽ đập nát và bẻ gãy hết mọi thứ bị quẳng vào nó vì sự hung tợn của nó.');
INSERT INTO "vn_mokhtasar" VALUES(104,5,'104:5','Điều gì cho Ngươi biết - hỡi Thiên Sứ - sự thật về Hutamah?');
INSERT INTO "vn_mokhtasar" VALUES(104,6,'104:6','Đó là ngọn lửa của Allah được đốt cháy lên rất dữ dội.');
INSERT INTO "vn_mokhtasar" VALUES(104,7,'104:7','Để thiêu đốt thân thể đến tận trái tim.');
INSERT INTO "vn_mokhtasar" VALUES(104,8,'104:8','Lửa sẽ bao vây xung quanh chúng.');
INSERT INTO "vn_mokhtasar" VALUES(104,9,'104:9','Và chúng sẽ bị nhốt bên trong các cột lửa không thể thoát ra được.');
INSERT INTO "vn_mokhtasar" VALUES(105,1,'105:1','Này Thiên Sứ, há Ngươi chưa từng nghe về việc Thượng Đế của Ngươi đã trừng phạt đoàn quân cưỡi voi do Vua tên Abrahah chỉ huy tiến về Makkah muốn hủy hoại Ngôi Đền thiêng liêng Ka’bah như thế nào ư?');
INSERT INTO "vn_mokhtasar" VALUES(105,2,'105:2','Allah đã đập tan kế hoạch xấu xa của hắn, hắn đã không làm được bất cứ gì mặc dù mọi người đã bỏ Ka''bah mà chạy trốn.');
INSERT INTO "vn_mokhtasar" VALUES(105,3,'105:3','Ngài gởi đến họ từng đàn chim nối tiếp nhau.');
INSERT INTO "vn_mokhtasar" VALUES(105,4,'105:4','Gắp những viên đá được đun nóng tiếp nối nhau thả xuống họ.');
INSERT INTO "vn_mokhtasar" VALUES(105,5,'105:5','Allah tiêu diệt họ toàn bộ và họ nằm la liệt giống như những cọng rạ khô cho súc vật ăn và sau khi bị giẫm đạp.');
INSERT INTO "vn_mokhtasar" VALUES(106,1,'106:1','Để cải thiện cuộc sống của bộ tộc Quraish.');
INSERT INTO "vn_mokhtasar" VALUES(106,2,'106:2','TA đã tạo điều kiện thuận lợi cho họ di chuyển đến Yemen vào mùa đông và đến Sham (nay được chia thành bốn quốc gia: Jordan, Sirya, Palestine và Li-Băng) vào mùa hè.');
INSERT INTO "vn_mokhtasar" VALUES(106,3,'106:3','Do đó, họ nên tạ ơn mà thờ phượng Allah, Chủ Nhân của Ngôi Đền thiêng này – đó là Ka’bah – Đấng đã tạo mọi dễ dàng cho họ, ngoài Ngài chúng không được tạ ơn bất cứ ai khác.');
INSERT INTO "vn_mokhtasar" VALUES(106,4,'106:4','Đấng đã cung cấp thức ăn cho họ khi họ đói và làm cho họ an toàn khỏi những tai ách. Ngài đã khiến con tim người Ả-rập tôn trọng vùng đất thiêng và dân cư nơi đó.');
INSERT INTO "vn_mokhtasar" VALUES(107,1,'107:1','Ngươi có biết ai là những kẻ phủ nhận sự Phục sinh và thưởng phạt không?');
INSERT INTO "vn_mokhtasar" VALUES(107,2,'107:2','Đó là những kẻ đối xử tệ bạc với trẻ mồ côi, không thực hiện đúng quyền lợi của trẻ mồ côi.');
INSERT INTO "vn_mokhtasar" VALUES(107,3,'107:3','Và những kẻ đã không thúc bản thân mình hay người khác nuôi ăn người túng thiếu.');
INSERT INTO "vn_mokhtasar" VALUES(107,4,'107:4','Đáng bị tiêu diệt và trừng phạt một cách nghiêm khắc đối với những kẻ dâng lễ Salah.');
INSERT INTO "vn_mokhtasar" VALUES(107,5,'107:5','Những người mà họ không chu đáo dâng lễ nguyện Salah, thường lơ là và bê tha nó.');
INSERT INTO "vn_mokhtasar" VALUES(107,6,'107:6','Những kẻ mà chúng chỉ muốn khoe cho thiên hạ nhìn thấy lễ nguyện Salah và việc làm thiện tốt của mình, chứ không hoàn toàn thành tâm vì Allah.');
INSERT INTO "vn_mokhtasar" VALUES(107,7,'107:7','Và chúng thường không chịu giúp đỡ người khác mặc việc giúp đỡ không gây thiệt hại gì bản thân chúng.');
INSERT INTO "vn_mokhtasar" VALUES(108,1,'108:1','Quả thật TA đã ban cho Ngươi - hỡi Thiên Sứ - rất nhiều ân huệ tốt đẹp ở trên thế gian và cõi Đời Sau, và một trong những ân huệ đó là con sông Kawthar nơi Thiên Đàng.');
INSERT INTO "vn_mokhtasar" VALUES(108,2,'108:2','Do đó Ngươi hãy thành tâm tạ ơn Allah mà dâng lễ nguyện Salah và hãy vì Ngài mà giết tế súc vật, đừng giống như những người đa thần đã giết cho các bục tượng của họ.');
INSERT INTO "vn_mokhtasar" VALUES(108,3,'108:3','Quả thật, những ai ghét bỏ Ngươi sẽ bị cắt đứt mọi điều tốt và bị quên lãng, nếu có nhắc chỉ nhắc đến điều xấu của hắn.');
INSERT INTO "vn_mokhtasar" VALUES(109,1,'109:1','Hãy nói - hỡi Thiên Sứ -: Này hỡi những kẻ phủ nhận Allah!');
INSERT INTO "vn_mokhtasar" VALUES(109,2,'109:2','Ta không thờ các pho tượng và các thần linh ngụy tạo mà các ngươi đang thờ phượng, không hiện tại và cũng không tương lai.');
INSERT INTO "vn_mokhtasar" VALUES(109,3,'109:3','Và các ngươi cũng không thờ phượng Thượng Đế mà Ta thờ phượng, đó là Allah.');
INSERT INTO "vn_mokhtasar" VALUES(109,4,'109:4','Và từ nào đến giờ Ta chưa từng thờ phượng các pho tượng cũng như các thần linh ngụy tạo mà các ngươi đã thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(109,5,'109:5','Và các ngươi cũng không thờ phượng Thượng Đế mà Ta thờ phượng, đó là Allah.');
INSERT INTO "vn_mokhtasar" VALUES(109,6,'109:6','Các người có tôn giáo mà các người đã sáng tạo, còn Ta có tôn giáo mà Allah đã thiên khải cho Ta.');
INSERT INTO "vn_mokhtasar" VALUES(110,1,'110:1','Này hỡi Thiên Sứ của Allah, khi nào Allah ban thắng lợi cho Ngươi, giúp Ngươi đánh bại những kẻ vô đức tin Quraish, và giúp Ngươi chinh phục Makkah.');
INSERT INTO "vn_mokhtasar" VALUES(110,2,'110:2','Và khi nào Ngươi thấy thiên hạ vào Islam hết nhóm này đến nhóm khác.');
INSERT INTO "vn_mokhtasar" VALUES(110,3,'110:3','Ngươi hãy biết đó là dấu hiệu báo rằng sứ mạng của Ngươi gần chấm dứt. Bởi thế, Ngươi hãy tán dương Thượng Đế của Ngươi, hãy tạ ơn Ngài về hồng ân ban cho Ngươi chiến thắng và chiếm lãnh được Makkah, song cầu xin Ngài tha thứ cho Ngươi, bởi vì Ngài là Đấng nhân từ và bao dung, Ngài sẽ luôn đón nhận sự sám hối thành tâm của đám bề tôi của Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(111,1,'111:1','Đôi tay người bác của Thiên Sứ ông Abu Lahab bin ''Abdul Muttalib bị tiêu diệt bởi hành động gây hại đến Thiên Sứ và gài bẫy Người.');
INSERT INTO "vn_mokhtasar" VALUES(111,2,'111:2','Sự giàu sang phú quý và con cái của hắn sẽ không thể giải thoát cho hắn khỏi sự trừng phạt và không thể đưa hắn đến lòng thương xót của Allah.');
INSERT INTO "vn_mokhtasar" VALUES(111,3,'111:3','Vào Ngày Tận Thế, hắn sẽ bị đày trong Hỏa Ngục bùng bùng cháy.');
INSERT INTO "vn_mokhtasar" VALUES(111,4,'111:4','Và vợ của hắn cũng thế sẽ cùng hội cùng thuyền với hắn trong Hỏa Ngục bởi mụ ta đã vác củi gai rải trên đường đi để làm hại Thiên Sứ.');
INSERT INTO "vn_mokhtasar" VALUES(111,5,'111:5','Mụ ta sẽ bị trói cổ bởi sợi dây được xe từ xơ rất cứng và chắc rồi bị lôi đi và ném vào Hỏa Ngục.');
INSERT INTO "vn_mokhtasar" VALUES(112,1,'112:1','Ngươi hãy nói - hỡi Thiên Sứ -: Allah là Đấng duy nhất được tôn thờ, không có bất cứ thượng đế nào xứng đáng ngoài Ngài được thờ phượng.');
INSERT INTO "vn_mokhtasar" VALUES(112,2,'112:2','Allah là Đấng không cần bất cứ thứ gì cũng chẳng lệ thuộc bất cứ điều gì, Ngài là Đấng mà vạn vật luôn cần đến Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(112,3,'112:3','Ngài không có con cũng chẳng có đấng sinh thành nào.');
INSERT INTO "vn_mokhtasar" VALUES(112,4,'112:4','Thuộc tính của Ngài, tên của Ngài và các hành động của Ngài đều là siêu việt, không có một ai (cái gì) có thể so sánh với Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(113,1,'113:1','Ngươi hãy nói - hỡi Thiên Sứ -: Bề tôi cầu xin sự che chở và cứu rỗi từ nơi Thượng Đế của bình minh.');
INSERT INTO "vn_mokhtasar" VALUES(113,2,'113:2','Tránh khỏi tất cả mọi điều xấu mà Ngài đã tạo hóa.');
INSERT INTO "vn_mokhtasar" VALUES(113,3,'113:3','Và tránh khỏi mọi điều xấu trong đêm tối từ động vật và trộm cướp.');
INSERT INTO "vn_mokhtasar" VALUES(113,4,'113:4','Tránh khỏi mọi điều hãm hại của các bùa phép và ma thuật được thổi vào trong những cái gút thắt.');
INSERT INTO "vn_mokhtasar" VALUES(113,5,'113:5','Khỏi những sự ganh ghét và hãm hại của người đời khi được Allah ban cho những ân huệ.');
INSERT INTO "vn_mokhtasar" VALUES(114,1,'114:1','Hãy nói đi - hỡi Thiên Sứ -: Bề tôi cầu xin sự che chở từ Thượng Đế của nhân loại.');
INSERT INTO "vn_mokhtasar" VALUES(114,2,'114:2','Vị Vua của toàn nhân loại có quyền năng chi phối mọi việc và không lệ thuộc bất cứ điều gì.');
INSERT INTO "vn_mokhtasar" VALUES(114,3,'114:3','Đấng đáng được nhân loại thờ phượng, không có ai (vật gì) khác ngoài Ngài có quyền đó.');
INSERT INTO "vn_mokhtasar" VALUES(114,4,'114:4','Tránh khỏi sự hãm hại và quấy nhiễu của Shaytan, những điều mà nó thì thào xúi bẩy khiến sao lãng việc tụng niệm Allah hoặc lơ là khi được nhắc đến Ngài.');
INSERT INTO "vn_mokhtasar" VALUES(114,5,'114:5','Tránh khỏi những lời thì thào xúi bẩy vào lòng con người.');
INSERT INTO "vn_mokhtasar" VALUES(114,6,'114:6','Từ những Shaytan thuộc loài Jinn và loài người.');
