INSERT INTO "vn_ruwwad" VALUES(1,1,'1:1','Nhân danh Allah, Đấng Độ Lượng, Đấng Khoan Dung');
INSERT INTO "vn_ruwwad" VALUES(1,2,'1:2','Alhamdulillah (xin tạ ơn, ca ngợi và tán dương Allah), Thượng Đế của vũ trụ và vạn vật.');
INSERT INTO "vn_ruwwad" VALUES(1,3,'1:3','Đấng Độ Lượng, Đấng Khoan Dung');
INSERT INTO "vn_ruwwad" VALUES(1,4,'1:4','Đấng Toàn Quyền của Ngày Thưởng Phạt');
INSERT INTO "vn_ruwwad" VALUES(1,5,'1:5','Duy chỉ Ngài bầy tôi thờ phượng và duy chỉ Ngài bầy tôi cầu xin phù hộ và trợ giúp.');
INSERT INTO "vn_ruwwad" VALUES(1,6,'1:6','Xin Ngài hướng dẫn bầy tôi đến với con đường ngay chính,');
INSERT INTO "vn_ruwwad" VALUES(1,7,'1:7','Con đường của những người đã được Ngài ban cho ân huệ, không phải con đường của những kẻ đã bị Ngài giận dữ và cũng không phải con đường của những kẻ lạc lối.');
INSERT INTO "vn_ruwwad" VALUES(2,1,'2:1','Alif. Lam. mim');
INSERT INTO "vn_ruwwad" VALUES(2,2,'2:2','Đây là Kinh Sách mà trong đó không có gì phải nghi ngờ, một nguồn hướng dẫn dành cho những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(2,3,'2:3','Những người mà họ có đức tin vào điều vô hình, duy trì lễ nguyện Salah và chi dùng bổng lộc đã được TA ban cấp (cho con đường chính nghĩa của TA).');
INSERT INTO "vn_ruwwad" VALUES(2,4,'2:4','Những người mà họ có đức tin vào những điều đã được thiên khải cho Ngươi (Muhammad), cho các vị Thiên Sứ( trước Ngươi và có niềm tin kiên định vào Đời Sau.');
INSERT INTO "vn_ruwwad" VALUES(2,5,'2:5','Đấy là nhóm người theo đúng chỉ đạo của Thượng Đế của họ và đó là nhóm người thành đạt.');
INSERT INTO "vn_ruwwad" VALUES(2,6,'2:6','Quả thật, những ai cố ý phủ nhận đức tin thì cho dù Ngươi có cảnh báo họ hay không thì họ vẫn không tin tưởng.');
INSERT INTO "vn_ruwwad" VALUES(2,7,'2:7','Bởi Allah đã đóng dấu lên trái tim của họ, còn tai và mắt của họ đã bị bịt kín, rồi họ sẽ phải chịu một sự trừng phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(2,8,'2:8','Trong nhân loại, có người nói chúng tôi đã tin vào Allah và Đời Sau, nhưng thực chất họ không hề tin gì cả.');
INSERT INTO "vn_ruwwad" VALUES(2,9,'2:9','Họ lừa dối Allah và những người có đức tin nhưng thật ra họ chỉ lừa dối chính bản thân họ trong khi họ không nhận thấy điều đó.');
INSERT INTO "vn_ruwwad" VALUES(2,10,'2:10','Trong trái tim của họ mang căn bệnh (dối trá) nên Allah làm cho căn bệnh đó của họ thêm trầm trọng, và rồi đây họ sẽ phải chịu một sự trừng phạt đau đớn về những gì họ đã lừa dối.');
INSERT INTO "vn_ruwwad" VALUES(2,11,'2:11','Khi có lời bảo họ: “Các người chớ hủy hoại trái đất!”, họ nói: “Quả thật, chúng tôi là những người cải thiện mà.”');
INSERT INTO "vn_ruwwad" VALUES(2,12,'2:12','Không, chính họ là những kẻ tàn phá nhưng họ lại không nhận ra điều đó.');
INSERT INTO "vn_ruwwad" VALUES(2,13,'2:13','Khi có lời bảo họ: “Các người hãy có đức tin giống như những người đã có đức tin”, họ nói: “Lẽ nào chúng tôi lại tin tưởng giống như những kẻ khờ khạo đó đã tin hay sao?” Không, chính họ mới là những kẻ khờ khạo nhưng họ không biết đấy thôi.');
INSERT INTO "vn_ruwwad" VALUES(2,14,'2:14','Khi đối diện với những người có đức tin thì họ nói: “Chúng tôi đã có đức tin.” Nhưng khi ở cùng với những tên Shaytan của họ thì họ lại bảo: “Chúng tôi cùng phe với các người, chẳng qua chúng tôi chỉ bỡn cợt với họ mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(2,15,'2:15','Allah cũng bỡn cợt với họ và Ngài bỏ mặc cho họ lang thang vơ vẩn trong sự thái quá của họ.');
INSERT INTO "vn_ruwwad" VALUES(2,16,'2:16','Đó là những kẻ đã mua sự lầm lạc bằng sự chỉ đạo, do đó, cuộc đổi chác của họ chẳng mang lại lợi ích gì cho họ, và họ không được hướng dẫn.');
INSERT INTO "vn_ruwwad" VALUES(2,17,'2:17','Hình ảnh của họ giống như hình ảnh của một người nhúm lên ngọn lửa, khi ngọn lửa soi sáng mọi vật xung quanh, Allah lấy đi mất ánh sáng của họ, Ngài bỏ mặc họ trong tăm tối mù mịt, họ chẳng nhìn thấy gì.');
INSERT INTO "vn_ruwwad" VALUES(2,18,'2:18','Điếc, câm và mù nên họ không thể quay về (với chân lý).');
INSERT INTO "vn_ruwwad" VALUES(2,19,'2:19','Hoặc (hình ảnh của họ) giống như hình ảnh của cơn mưa trút xuống từ bầu trời kèm theo bóng tối, sấm sét và tia chớp; họ lấy các ngón tay bịt đôi tai để khỏi nghe tiếng sấm sét vì sợ chết. Tuy nhiên, Allah bao vây những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(2,20,'2:20','Tia chớp gần như làm mắt họ chẳng nhìn thấy gì, mỗi khi ánh sáng lóe lên thì họ lần mò bước đi, và khi trời tối đen thì họ đứng lại. Nếu muốn, Allah đã lấy mất thính giác và thị giác của họ, bởi Allah toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(2,21,'2:21','Hỡi nhân loại, các ngươi hãy thờ phượng Thượng Đế của các ngươi, Đấng đã tạo hóa ra các ngươi và các thế hệ trước các ngươi, mong rằng các ngươi biết kính sợ Ngài.');
INSERT INTO "vn_ruwwad" VALUES(2,22,'2:22','Ngài đã tạo cho các ngươi trái đất như một tấm thảm và bầu trời như một chiếc lọng che, Ngài ban nước mưa từ trên trời xuống làm mọc ra trái quả thành nguồn bổng lộc cho các ngươi. Cho nên, các ngươi chớ đừng dựng những thần linh ngang hàng với Allah trong khi các ngươi biết rõ sự thật.');
INSERT INTO "vn_ruwwad" VALUES(2,23,'2:23','Còn nếu như các ngươi hoài nghi về Qur’an mà TA đã mặc khải cho người Bề Tôi của TA (Muhammad) thì các ngươi hãy mang đến một chương Kinh giống như Nó, và các ngươi hãy mời gọi các nhân chứng của các ngươi ngoài Allah đến xác nhận nếu các ngươi trung thực.');
INSERT INTO "vn_ruwwad" VALUES(2,24,'2:24','Nhưng nếu các ngươi không làm được và chắc chắn các ngươi sẽ không bao giờ làm được thì các ngươi hãy sợ Hỏa Ngục, nơi mà chất đốt của Nó là con người và đá được chuẩn bị sẵn cho những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(2,25,'2:25','Ngươi (Muhammad) hãy báo tin mừng cho những người có đức tin và hành thiện rằng họ sẽ được ban thưởng cho Những Ngôi Vườn Thiên Đàng, bên dưới có các dòng sông chảy. Cứ mỗi lần họ được ban cấp cho trái quả, họ đều nói: “Ðây là thứ mà chúng tôi đã được ban cho trước đây” nhưng thật ra họ chỉ được ban cho những thứ giông giống mà thôi. Và trong Thiên Đàng, họ sẽ được ban cho những người vợ thuần khiết và họ sẽ ở trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(2,26,'2:26','Chắc chắn Allah không hề e ngại khi Ngài đưa ra hình ảnh thí dụ về con muỗi hay một sinh vật khác lớn hơn (hoặc nhỏ hơn). Bởi lẽ, đối với những người có đức tin, họ luôn biết rõ điều đó là chân lý đến từ Thượng Đế của họ; ngược lại, đối với những kẻ vô đức tin thì họ nói: “Allah muốn ngụ ý điều gì qua hình ảnh thí dụ này đây chứ?” Thế nên, với thí dụ này Ngài làm cho nhiều kẻ lầm lạc nhưng cũng đồng thời dẫn dắt nhiều người đi đúng đường. Tuy nhiên, Ngài chỉ dùng nó để làm lệch lạc những kẻ tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(2,27,'2:27','Những kẻ mà họ bội ước với Allah sau khi đã giao kết với Ngài, cắt đứt điều (tình máu mủ ruột thịt) mà Allah ra lệnh phải hàn gắn và tàn phá trái đất. Đó là những kẻ thất bại.');
INSERT INTO "vn_ruwwad" VALUES(2,28,'2:28','Sao các ngươi lại vô đức tin nơi Allah trong khi Ngài đã khởi tạo các ngươi từ không là gì, rồi Ngài cho các ngươi chết, rồi Ngài phục sinh các người trở lại, sau đó các ngươi được triệu tập về trình diện Ngài?!');
INSERT INTO "vn_ruwwad" VALUES(2,29,'2:29','Ngài là Đấng đã tạo tất cả mọi thứ trên trái đất cho các ngươi. Sau đó, Ngài hướng về bầu trời và tạo chúng thành bảy tầng trời; và Ngài là Đấng biết rõ tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(2,30,'2:30','Ngươi hãy nhớ lại (hỡi Muhammad) khi Thượng Đế của Ngươi thông báo với các Thiên Thần: “TA sẽ đặt (con người) làm đại diện của TA trên trái đất.” Các Thiên Thần nói: “Lẽ nào Ngài lại muốn tạo thêm một tên hủy diệt, gây đổ máu trên trái đất lần nữa trong khi bầy tôi vẫn luôn tụng niệm, tạ ơn và vinh danh Ngài?!” (Allah) phán: “Chắc chắn TA biết rõ những điều mà các ngươi không hề biết.”');
INSERT INTO "vn_ruwwad" VALUES(2,31,'2:31','Rồi Ngài đã dạy cho Adam tên gọi của tất cả mọi thứ, sau đó Ngài trưng bày mọi thứ ra trước mặt các Thiên Thần, phán: “Các ngươi hãy cho TA biết tên của các thứ này xem nếu các ngươi thật sự biết!”');
INSERT INTO "vn_ruwwad" VALUES(2,32,'2:32','(Các Thiên Thần) đồng thưa: “Vinh quang thay Ngài, bầy tôi nào biết ngoài những điều mà Ngài đã dạy cho bầy tôi. Quả thật, chỉ một mình Ngài mới là Đấng Hằng Biết, Đấng Sáng Suốt.”');
INSERT INTO "vn_ruwwad" VALUES(2,33,'2:33','(Allah) phán bảo (Adam): “Này Adam, Ngươi hãy cho Họ biết tên gọi của chúng.” Rồi khi Adam cho các Thiên Thần biết tên gọi của tất cả mọi thứ, Allah phán (với các Thiên Thần): “Chẳng phải TA đã bảo các ngươi rằng TA biết điều vô hình của các tầng trời và trái đất và TA biết rõ điều các ngươi phơi bày và giấu kín hay sao?!”');
INSERT INTO "vn_ruwwad" VALUES(2,34,'2:34','Ngươi hãy nhớ lại (hỡi Muhammad) khi TA ra lệnh cho các Thiên Thần: “Các ngươi hãy quỳ lạy chào hỏi Adam”, lập tức, tất cả đều quỳ lạy ngoại trừ Iblis, hắn đã cãi lệnh và tự cao tự đại, và hắn đã trở thành một tên vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(2,35,'2:35','Và TA đã phán bảo (Adam): “Này Adam, Ngươi và vợ của Ngươi hãy sống trong Thiên Đàng và hai Ngươi hãy ăn thỏa thích những gì mình muốn, nhưng hai Ngươi chớ đến gần cây này kẻo hai Ngươi trở thành những kẻ làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(2,36,'2:36','Tuy nhiên, Shaytan đã làm cho cả hai rời khỏi Thiên Đàng, hắn đã làm cho hai người họ bị trục xuất khỏi nơi đang sinh sống. Và TA đã phán bảo (vợ chồng Adam và Iblis): “Các ngươi hãy xuống trái đất mà sống, các ngươi sẽ là kẻ thù của nhau; và trên trái các ngươi sẽ có một cuộc sống tạm đến một thời hạn nhất định.”');
INSERT INTO "vn_ruwwad" VALUES(2,37,'2:37','Nhưng rồi Adam đã hối cải với Thượng Đế bằng lời lẽ học được từ Ngài, và Ngài đã tha thứ tội lỗi cho Y bởi Ngài là Đấng Hằng Chấp Nhận sự sám hối, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(2,38,'2:38','TA phán: “Tất cả các ngươi (vợ chồng Adam và Iblis) hãy đi xuống khỏi nơi đó (Thiên Đàng). Rồi khi nào có chỉ đạo từ nơi TA đến với các ngươi, ai biết đi theo chỉ đạo của TA thì họ sẽ không lo và sẽ không buồn phiền.”');
INSERT INTO "vn_ruwwad" VALUES(2,39,'2:39','“Còn ai không tin tưởng và bác bỏ các Lời Mặc Khải của TA thì họ sẽ là cư dân của Hỏa Ngục, họ sẽ ở trong đó đời đời.”');
INSERT INTO "vn_ruwwad" VALUES(2,40,'2:40','Hỡi người dân Israel (tức dòng dõi của Nabi Ya’qub), các Ngươi hãy ghi nhớ các ân huệ mà TA đã đặc ân cho các ngươi và các ngươi hãy thực hiện Giao Ước của TA thì TA sẽ thực hiện Giao Ước của các ngươi, và các ngươi hãy chỉ kính sợ một mình TA thôi.');
INSERT INTO "vn_ruwwad" VALUES(2,41,'2:41','Các ngươi hãy tin tưởng vào (Qur’an) mà TA đã mặc khải (cho Muhammad) nhằm khẳng định lại những gì đang ở nơi các ngươi (Kinh Cựu Ước, Kinh Tân Ước), bởi thế các ngươi chớ là những kẻ đầu tiên phủ nhận Nó, chớ lấy các Lời Mặc Khải của TA đem bán với cái giá ít ỏi, và các ngươi hãy chỉ kính sợ một mình TA thôi.');
INSERT INTO "vn_ruwwad" VALUES(2,42,'2:42','Các ngươi chớ pha trộn chân lý với cái ngụy tạo, chớ cố tình che giấu chân lý trong khi các ngươi biết rõ sự thật.');
INSERT INTO "vn_ruwwad" VALUES(2,43,'2:43','Các ngươi hãy duy trì lễ nguyện Salah, hãy xuất Zakah và hãy cúi mình cùng với những người cúi mình (thờ phượng Allah).');
INSERT INTO "vn_ruwwad" VALUES(2,44,'2:44','Chẳng lẽ các ngươi bảo thiên hạ sống đạo đức nhưng các ngươi lại quên mất chính bản thân các ngươi trong khi các ngươi là những người đọc Kinh Sách ư? Lẽ nào các ngươi không biết suy ngẫm?');
INSERT INTO "vn_ruwwad" VALUES(2,45,'2:45','Các ngươi hãy cầu xin sự phù hộ bằng tấm lòng kiên nhẫn và lễ nguyện Salah. Quả thật lễ nguyện Salah chắn hẳn là điều nặng nề và khó khăn nhưng đối với những người thực sự kính sợ (Allah thì nó không khó khăn và nặng nề chút nào).');
INSERT INTO "vn_ruwwad" VALUES(2,46,'2:46','Họ là những người luôn nghĩ rằng họ sẽ phải trở về trình diện Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(2,47,'2:47','Hỡi người dân Israel, các ngươi hãy ghi nhớ ân huệ mà TA đã đặc ân cho các ngươi và quả thật TA đã ưu đãi các ngươi hơn thiên hạ.');
INSERT INTO "vn_ruwwad" VALUES(2,48,'2:48','Các ngươi hãy lo sợ cho Ngày mà không ai giúp được ai cả, không có sự can thiệp nào cũng như không có sự bồi thường nào được chấp nhận, và họ sẽ không được giúp đỡ.');
INSERT INTO "vn_ruwwad" VALUES(2,49,'2:49','(Các ngươi hãy nhớ lại) việc TA đã giải cứu các ngươi khỏi đám quân lính của Fir’awn (Pha-ra-ông), họ đã đàn áp các ngươi bằng cực hình man rợ, họ đã tàn sát con trai của các ngươi và tha cho con gái của các ngươi. Trong sự việc đó là sự thử thách to lớn từ Thượng Đế của các ngươi dành cho các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(2,50,'2:50','(Các ngươi hãy nhớ lại) khi TA (Allah) chẻ đôi biển (Hồng Hải) cho các ngươi (lúc Fir’awn đuổi cùng giết tận các ngươi), TA đã cứu các ngươi và nhấn chìm đám quân lính của hắn trước sự chứng kiến của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(2,51,'2:51','(Các ngươi hãy nhớ lại) lúc TA hẹn gặp Musa (Môi-se) trong bốn mươi đêm, rồi trong thời gian Y đi vắng, các ngươi đã tạo ra tượng con bò nghé để thờ phượng và các ngươi trở thành những kẻ sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(2,52,'2:52','Rồi TA đã xí xóa tội lỗi đó cho các ngươi hi vọng các ngươi biết tri ân.');
INSERT INTO "vn_ruwwad" VALUES(2,53,'2:53','(Các ngươi hãy nhớ lại) khi TA ban cho Musa Kinh Sách (Tawrah – Kinh Cựu Ước) và Tiêu Chuẩn Phân Biệt Phúc Tội mong rằng các ngươi được hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(2,54,'2:54','(Các ngươi hãy nhớ lại) khi Musa bảo người dân của Y: “Hỡi người dân của Ta! Quả thật các người đã tự bất công với chính mình qua việc tạo ra tượng con bò nghé, để chuộc tội với Đấng Tạo Hóa của các người thì các người phải tự kết liễu mạng sống của các người, đó là cách tốt nhất để chuộc tội với Đấng đã tạo hóa ra các người, có thế Ngài mới chấp nhận việc sám hối của các người. Quả thật, Ngài là Đấng Hằng Chấp Nhận Sám Hối, Đấng Khoan Dung.”');
INSERT INTO "vn_ruwwad" VALUES(2,55,'2:55','(Các ngươi hãy nhớ lại) việc các ngươi đã nói: “Này Musa, chúng tôi không thể nào tin vào Người cho đến khi chúng tôi tận mắt thấy được Allah xuất hiện.” Thế là tia sét đã trừng phạt các ngươi trong lúc các ngươi chỉ biết trơ mắt ra nhìn.');
INSERT INTO "vn_ruwwad" VALUES(2,56,'2:56','Sau đó, TA đã làm các ngươi sống lại sau cái chết của các ngươi, mong rằng các ngươi biết tri ân.');
INSERT INTO "vn_ruwwad" VALUES(2,57,'2:57','Và TA đã dùng mây che mát cho các ngươi, ban xuống cho các ngươi Mann (loại thức uống giống như mật ong) và chim cút, và (TA phán): “Các ngươi hãy ăn thực phẩm tốt lành mà TA đã ban cấp cho các ngươi.” Thật ra họ chẳng hề gây hại được TA mà ngược lại họ đã tự làm hại chính bản thân họ.');
INSERT INTO "vn_ruwwad" VALUES(2,58,'2:58','(Các ngươi hãy nhớ lại) khi TA bảo: “Các ngươi hãy vào thị trấn này (Palestine), các ngươi hãy tự do ăn uống thỏa thích và các ngươi hãy vào cổng trong sự phủ phục và cầu nguyện (TA): “Xin Ngài tha thứ tội lỗi cho bầy tôi” thì TA sẽ tha thứ tội lỗi cho các ngươi và TA sẽ ban thêm công đức cho những người làm tốt (trong các ngươi).');
INSERT INTO "vn_ruwwad" VALUES(2,59,'2:59','Tuy nhiên, những kẻ làm điều sai quấy đã thay đổi lời nói đó thành lời khác với lời đã bảo ban họ, thế là TA đã giáng tai họa từ trời xuống trừng phạt những kẻ làm điều sai quấy bởi những hành động tội lỗi của họ.');
INSERT INTO "vn_ruwwad" VALUES(2,60,'2:60','(Các ngươi hãy nhớ lại) khi Musa thỉnh cầu được ban nước uống cho người dân của Y, TA bảo: “Ngươi hãy dùng gậy của Ngươi đánh lên tảng đá”, rồi mười hai ngọn nước phún ra từ đó, mỗi bộ lạc (trong mười hai bộ lạc) đều biết được đâu là nguồn nước của mình. (Allah phán:) Các ngươi hãy ăn uống thiên lộc của Allah và chớ đừng tàn phá trái đất.');
INSERT INTO "vn_ruwwad" VALUES(2,61,'2:61','(Các ngươi hãy nhớ lại) việc mình đã đề nghị: “Hỡi Musa, chúng tôi không thể nào chịu nổi khi chỉ phải ăn mãi một loại thức ăn thế này, Người hãy cầu xin Thượng Đế của Người ban cho chúng tôi thực phẩm mọc ra từ đất như rau xanh, dưa leo, tỏi, đậu và hành.” Musa hỏi: “Sao, các người lại muốn lấy cái tốt đổi cái tệ kia chứ? Các người hãy đi đến bất cứ nơi nào (của Ai Cập), các người sẽ có được những thứ mà các người đòi hỏi.” Thế là sự hèn hạ, nghèo khổ đã bám lấy họ và họ phải sống trong cơn giận dữ của Allah. Họ bị như thế là vì họ đã vô đức tin nơi các Lời Mặc Khải của Allah và giết hại các vị Nabi một cách không chính đáng. Đó là bởi vì họ đã trái lệnh và vượt quá giới hạn (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(2,62,'2:62','Quả thật, những người có đức tin (trong cộng đồng Islam) và các cộng đồng người Do Thái, người Thiên Chúa và người Sobi-un (Sabian)(, ai trong số họ đã tin tưởng vào Allah, tin vào Đời Sau và hành thiện thì họ đã đạt được phần thưởng từ Thượng Đế của mình, họ sẽ không phải lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "vn_ruwwad" VALUES(2,63,'2:63','(Các ngươi hãy nhớ lại) khi TA đã nhận giao ước của các ngươi và TA đã nâng ngọn núi lên trên các ngươi mà phán: “Các ngươi hãy nắm chắc những gì TA đã ban cho các ngươi (trong Kinh Tawrah), và các ngươi hãy ghi nhớ mọi điều răn dạy trong đó hi vọng các ngươi sẽ ngoan đạo.”');
INSERT INTO "vn_ruwwad" VALUES(2,64,'2:64','Nhưng rồi các ngươi lại quay mặt làm ngơ, nếu Allah không ưu ái và thương xót các ngươi thì chắc chắn các ngươi đã trở thành những kẻ thất bại mất rồi.');
INSERT INTO "vn_ruwwad" VALUES(2,65,'2:65','Và chắc hẳn các ngươi đã biết rõ về câu chuyện của nhóm người trong các ngươi đã phạm giới luật cấm của ngày thứ bảy, TA đã phán cho họ: “Các ngươi hãy thành loài khỉ đáng khinh!”');
INSERT INTO "vn_ruwwad" VALUES(2,66,'2:66','TA đã lấy sự trừng phạt đó làm bài học cho các thế hệ đang sống trong thời đại của họ và các thế hệ sau họ và làm lời răn dạy cho những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(2,67,'2:67','(Các ngươi hãy nhớ lại) khi Musa bảo người dân của Y: “Quả thật Allah đã ra lệnh cho các người cắt tiết một con bò cái.” Họ nói: “Có phải Người đùa với chúng tôi chăng?” (Musa) nói: “Ta cầu xin Allah che chở tránh khỏi việc trở thành một kẻ ngu muội (dám đùa cợt với mệnh lệnh của Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(2,68,'2:68','Họ (yêu cầu Musa), nói: “(Nếu đúng như vậy,) Người hãy cầu nguyện Thượng Đế của Người mô tả rõ cho chúng tôi biết con bò cái đó ra sao?” (Musa) bảo: “Ngài phán rằng đó là con bò cái, không quá già cũng không quá non mà là ở giữa hai mức đó. Nào các người hãy mau thực hiện theo điều mà các người đã được lệnh!”');
INSERT INTO "vn_ruwwad" VALUES(2,69,'2:69','Họ (lại yêu cầu Musa), nói: “Vậy Người hãy cầu nguyện Thượng Đế của Người trình bày rõ cho chúng tôi biết nó màu gì?” (Musa) bảo: “Ngài phán rằng đó là con bò cái vàng hung, màu của nó làm cho người nhìn thích thú.”');
INSERT INTO "vn_ruwwad" VALUES(2,70,'2:70','Họ (lại tiếp tục yêu cầu Musa), nói: “Người hãy cầu nguyện Thượng Đế của Người trình bày chi tiết hơn nữa về nó, bởi con bò cái đều giông giống nhau, chúng tôi khó phân biệt, In-Sha-Allah( lần này chúng tôi sẽ được chỉ đúng.”');
INSERT INTO "vn_ruwwad" VALUES(2,71,'2:71','(Musa) bảo: “Ngài phán rằng đó là con bò cái chưa từng cày ruộng cũng chưa từng kéo nước tưới rẫy, nó lành lặn (khỏe mạnh và không khiếm khuyết) và nó hoàn toàn vàng hung (không có một đốm trắng nào).” (Đến đây) họ mới nói: “Giờ thì Người mới trình bày rõ sự thật.” Rồi (cuối cùng) họ cũng đã cắt tiết nó sau khi gần như không muốn làm.');
INSERT INTO "vn_ruwwad" VALUES(2,72,'2:72','(Các ngươi hãy nhớ lại hỡi dân Israel) khi các ngươi giết một sinh mạng nhưng các ngươi đổ thừa lẫn nhau về (vụ án mạng đó), nhưng rồi Allah cũng đã phơi bày điều bí ẩn mà các ngươi đã cố giấu giếm.');
INSERT INTO "vn_ruwwad" VALUES(2,73,'2:73','TA đã phán: “Các ngươi hãy lấy xương (của con bò đã cắt tiết) đánh vào nó (thi thể của nạn nhân).” Với cách đó, Allah làm cho người chết sống lại (chỉ ra kẻ sát nhân), đồng thời để Ngài trình bày cho các ngươi thấy các phép lạ của Ngài, mong rằng các ngươi biết suy nghĩ.');
INSERT INTO "vn_ruwwad" VALUES(2,74,'2:74','Nhưng rồi sau đó, con tim của các ngươi lại chai cứng tựa như đá hoặc cứng hơn thế. Bởi lẽ có loại đá từ đó phun ra các con sông, có loại đá nứt làm đôi cho nước chảy ra và có loại đá rơi từ trên núi xuống vì khiếp sợ Allah. Và Allah không làm ngơ trước bất cứ hành động nào của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(2,75,'2:75','Lẽ nào các ngươi (hỡi những người có đức tin) vẫn còn hy vọng rằng họ (những người Do Thái) sẽ tin tưởng các ngươi khi một nhóm (tu sĩ) trong số họ quả thật đã nghe rõ lời phán của Allah rồi sau đó bóp méo nó sau khi họ đã hiểu rõ nó trong lúc họ biết (điều họ làm) ư?');
INSERT INTO "vn_ruwwad" VALUES(2,76,'2:76','Mỗi khi họ gặp những người có đức tin, họ bảo: “Chúng tôi đã có đức tin” nhưng khi họ ở riêng với nhau thì lại trách móc nhau: “Tại sao các người lại tuyên bố với họ (những người có đức tin) điều mà Allah đã bảo các người (phải tin vào Muhammad) để họ có bằng chứng thưa các người trước Thượng Đế của các người, sao các người không chịu suy nghĩ?”');
INSERT INTO "vn_ruwwad" VALUES(2,77,'2:77','Lẽ nào họ lại không biết rằng Allah luôn thông tường hết những gì họ đã cố giấu giếm và những gì họ công khai?');
INSERT INTO "vn_ruwwad" VALUES(2,78,'2:78','Trong số họ (những người Do Thái) có những tên mù chữ không biết gì về Kinh Sách ngoại trừ điều mơ mộng viển vông và những kẻ đó chỉ toàn đoán chừng mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(2,79,'2:79','Bởi thế, thật đáng bị hủy diệt đối với những kẻ đã tự tay mình viết Kinh Sách rồi lại bảo rằng đó là Kinh Sách do Allah ban xuống hầu mang Nó đi bán với cái giá ít ỏi. Họ thật đáng bị hủy diệt bởi những gì họ đã tự tay viết và những gì họ đã tích cóp (từ việc làm đó).');
INSERT INTO "vn_ruwwad" VALUES(2,80,'2:80','Họ (những người Do Thái) nói: “Hỏa Ngục không chạm đến chúng tôi được lâu đâu mà chỉ vài ngày ngắn ngủi thôi.” Ngươi (hỡi Muhammad) hãy bảo họ: “Có phải các người đã nhận được lời đảm bảo từ Allah rồi chăng, nếu đúng như thế thì Allah sẽ không bao giờ bội ước; hay là các người chỉ nói cho Allah điều mà các người không biết?”');
INSERT INTO "vn_ruwwad" VALUES(2,81,'2:81','Không đâu, ai đã tích cóp tội lỗi thì sẽ bị tội lỗi đó túm lấy họ, họ sẽ là đám cư dân của Hỏa Ngục và phải ở trong nó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(2,82,'2:82','Riêng những ai có đức tin và hành thiện, họ sẽ là cư dân của Thiên Đàng, họ sẽ định cư trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(2,83,'2:83','(Các ngươi hãy nhớ lại) khi TA đã nhận giao ước từ người dân Israel các ngươi, (TA phán bảo các ngươi): “Các ngươi không được thờ phượng ai ngoài Allah, các ngươi phải hiếu thảo với cha mẹ, phải cư xử tử tế với bà con họ hàng, với trẻ mồ côi, với những người thiếu thốn, các ngươi phải nói năng nhã nhặn với mọi người, phải duy trì lễ nguyện Salah và phải xuất Zakah.” Nhưng rồi chỉ có một ít trong các ngươi tuân thủ còn đa số các ngươi đều đã ngoảnh mặt quay lưng.”');
INSERT INTO "vn_ruwwad" VALUES(2,84,'2:84','(Các ngươi hãy nhớ lại) khi các ngươi đã giao ước với TA là không giết hại lẫn nhau, không xua đuổi nhau ra khỏi nhà cửa của các ngươi và điều đó được các ngươi đồng thống nhất và xác nhận.');
INSERT INTO "vn_ruwwad" VALUES(2,85,'2:85','Vậy mà các ngươi vẫn giết hại lẫn nhau, cố tình gây tội lỗi và hận thù để có cớ trục xuất người trong các ngươi rời khỏi nhà cửa của họ. Và nếu họ lọt vào tay các ngươi như tù binh thì các ngươi đòi họ chuộc mạng trong khi việc trục xuất họ là điều cấm đối với các ngươi. Chẳng lẽ các ngươi chỉ tin một phần của Kinh Sách và phủ nhận phần còn lại ư? Thế thì hình phạt dành cho những ai trong các ngươi dám làm điều đó không gì khác hơn là sự nhục nhã ở đời này; rồi vào Đời Sau, họ phải chuốc lấy cực hình khủng khiếp, và Allah không làm ngơ trước bất cứ điều gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(2,86,'2:86','Đó là những kẻ đã mua đời này bằng cái giá của Đời Sau. Bởi thế, sự trừng phạt dành cho họ sẽ không được giảm nhẹ và họ cũng sẽ không được ai cứu giúp.');
INSERT INTO "vn_ruwwad" VALUES(2,87,'2:87','Quả thật, TA đã ban cho Musa Kinh Sách và đã cử các vị Thiên Sứ kế nhiệm sau Y. Và TA cũng đã ban cho Ysa (Giê-su) con trai của Maryam (Maria) một loạt phép màu rõ ràng và để Jibril (Gabriel) danh dự làm trợ thủ cho Y. Phải chăng, mỗi khi một Thiên Sứ nào đó đến gặp các ngươi (dân Israel) để thông báo về sắc lệnh của TA, khi các ngươi không thích điều gì đó thì các ngươi trở nên ngạo mạn để rồi một nhóm thì phủ nhận (các Thiên Sứ) còn một nhóm thì giết hại Họ?');
INSERT INTO "vn_ruwwad" VALUES(2,88,'2:88','(Người Do Thái) bảo: “Con tim của chúng tôi đã bị khóa chặt rồi (không tiếp nhận thêm bất cứ điều gì nữa).” Đúng hơn là Allah đã nguyền rủa( họ bởi tội vô đức tin của họ nên họ chỉ tin rất ít.');
INSERT INTO "vn_ruwwad" VALUES(2,89,'2:89','Và khi có một Kinh Sách (Qur’an) được Allah gởi đến cho họ nhằm khẳng định lại những điều ở nơi họ (trong Kinh Tawrah và Injil), và trước đó họ đã luôn mong giành chiến thắng những người vô đức tin (khi có một vị Nabi được gửi đến). Vậy mà khi điều họ thừa nhận đến với họ thì họ lại phủ nhận và bác bỏ. Cho nên Allah nguyền rủa những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(2,90,'2:90','Thật tệ hại thay, chỉ vì lòng ganh tị và đố kỵ mà họ bán rẻ thân mình khi họ phủ nhận những gì được Allah thiên khải; (họ mỉa mai) rằng Allah ban hồng phúc của Ngài xuống cho bất cứ ai Ngài muốn trong số đám bề tôi của Ngài (thay vì ban xuống cho Muhammad). Thế nên họ phải chuốc lấy hết cơn thịnh nộ này đến cơn thịnh nộ khác (của Allah), và những kẻ vô đức tin sẽ bị trừng phạt một cách nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(2,91,'2:91','Khi có lời bảo họ (dân Do Thái): “Các người hãy tin vào những gì Allah ban xuống (trong Qur’an)”. Họ nói: “Chúng tôi chỉ tin vào những gì đã thiên khải cho chúng tôi (từ Tawrah) mà thôi.” Và họ bác bỏ hoàn toàn mọi điều được thiên khải sau đó trong khi Qur’an là chân lý xác nhận lại những gì đang trong tay họ. Ngươi (hỡi Muhammad) hãy hỏi họ: “Nếu thật sự các người đã có đức tin thì tại sao trước đây các người lại giết chết những vị Nabi của Allah?”');
INSERT INTO "vn_ruwwad" VALUES(2,92,'2:92','Và quả thật, Musa cũng đã mang đến cho các ngươi nhiều bằng chứng rõ rệt, vậy mà sau đó các ngươi vẫn mù quáng đúc tượng con bò để thờ phượng và các ngươi là những kẻ làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(2,93,'2:93','(Các ngươi hãy nhớ lại) khi TA nhận lấy giao ước của các ngươi và TA đã nâng ngọn núi lên bên trên các ngươi, (TA phán): “Các ngươi hãy nắm thật chặt những điều TA đã mang đến cho các ngươi và hãy nghe theo (những điều mặc khải đó).” Họ đáp: “Chúng tôi đã nghe nhưng không làm theo.” Niềm tin vào con bò (đúc) là thần linh đã thấm sâu vào tim họ. Ngươi hãy bảo họ (hỡi Muhammad): “Thật xấu xa thay cho đức tin (lầm lạc) của các người đã sai khiến các người nếu các người có đức tin (nơi bất cứ sự việc gì).”');
INSERT INTO "vn_ruwwad" VALUES(2,94,'2:94','Ngươi hãy bảo họ (hỡi Muhammad): “Nếu các người (hỡi những người Do Thái) khẳng định cuộc sống Đời Sau là dành riêng cho các người, không dành cho bất cứ ai khác trong nhân loại, vậy thì các người hãy cầu xin được chết ngay đi, nếu các người nói thật?”');
INSERT INTO "vn_ruwwad" VALUES(2,95,'2:95','Nhưng chắc chắn họ sẽ không bao giờ dám cầu xin được chết đâu bởi họ hiểu rõ những gì họ đã làm; và Allah luôn tận tường mọi hành động của những kẻ làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(2,96,'2:96','Và chắc chắn Ngươi (Muhammad) sẽ thấy họ là những kẻ tham sống (sợ chết) nhất, (họ tham sống) hơn cả những kẻ thờ đa thần, thậm chí từng người trong bọn họ còn ước muốn được sống đến cả ngàn năm, nhưng cho dù có được sống thọ đến chừng ấy tuổi đi chăng nữa thì vẫn không thoát khỏi được sự trừng phạt (của Allah), bởi Allah luôn thấy rõ mọi điều họ làm.');
INSERT INTO "vn_ruwwad" VALUES(2,97,'2:97','Ngươi (Muhammad) hãy nói với họ: “Ai đã từng là kẻ thù của (đại Thiên Thần) Jibril thì hãy biết rằng Y đã mang Qur’an đặt vào trái tim của Ngươi bằng phép của Allah nhằm xác nhận lại những điều đã được ban xuống trước đó, và Nó (Qur’an) là nguồn chỉ đạo và là tin mừng dành cho những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(2,98,'2:98','Ai từng là kẻ thù của Allah, của các Thiên Thần của Ngài, của các vị Thiên Sứ của Ngài, của (đại Thiên Thần) Jibril và (đại Thiên Thần) Mika-il (Michael) thì (hãy biết rằng) quả thật Allah là kẻ thù của những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(2,99,'2:99','Quả thật, TA (Allah) đã thiên khải cho Ngươi (Muhammad) những lời mặc khải rõ ràng nhưng chỉ những kẻ phản nghịch, bất tuân mới không tin mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(2,100,'2:100','Cứ mỗi lần họ lập giao ước nào đó thì một nhóm trong số họ thường gạt bỏ nó sang một bên. Không, đúng hơn là (bởi vì) đa số bọn họ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(2,101,'2:101','Và cứ mỗi khi một vị Thiên Sứ nào đó được Allah cử phái đến với họ để xác nhận lại những điều ở nơi họ thì một nhóm người được ban cho Kinh Sách lại gạt bỏ Kinh Sách của Allah ra sau lưng họ như thể là họ không hề hay biết gì cả.');
INSERT INTO "vn_ruwwad" VALUES(2,102,'2:102','Họ (người Do Thái) đã đi theo những điều mà những tên Shaytan đọc (sai lệch) về quyền lực của Sulayman (Solomon). Và Sulayman đã không vô đức tin mà chính những tên Shaytan mới vô đức tin. Bọn chúng đã dạy loài người Sihr (bùa ngải, ma thuật) và những gì được ban xuống cho hai Thiên Thần Harut và Marut tại Babil (thành phố Babylon của I-rắc). Tuy nhiên, hai Thiên Thần này không hề dạy cho một ai mà không cảnh báo trước: “Quả thật chúng tôi là một sự thử thách (cám dỗ), cho nên các ngươi chớ vô đức tin.” Mặc dù vậy, loài người vẫn học lấy từ hai Thiên Thần đó các loại (Sihr) dùng để chia rẽ vợ chồng, và họ đã không thể dùng nó hại được bất cứ ai trừ phi Allah cho phép. Và họ học lấy điều gây hại cho bản thân họ chứ nào mang lại lợi ích gì cho họ. Quả thật, họ đã biết rằng ai mua bán (Sihr) sẽ không được hưởng bất cứ điều tốt lành nào ở Đời Sau. Thật tồi tệ thay cho cái mà vì nó họ đã bán rẻ bản thân mình. Ôi giá như họ nhận biết được điều đó!');
INSERT INTO "vn_ruwwad" VALUES(2,103,'2:103','Nếu họ có đức tin và ngoan đạo thì chắc chắn họ sẽ được ban thưởng nhiều điều tốt đẹp hơn ở nơi Allah, giá như họ biết điều đó!');
INSERT INTO "vn_ruwwad" VALUES(2,104,'2:104','Hỡi những người có đức tin, các ngươi chớ nói (với Thiên Sứ Muhammad) bằng tiếng Raa’ina(, đổi lại các ngươi hãy nói (với Y) bằng tiếng Unzhurna, và các ngươi hãy lắng nghe (lời Y dạy). Còn những kẻ vô đức tin (rồi đây) sẽ phải chịu sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(2,105,'2:105','Không một người vô đức tin nào thuộc dân Kinh Sách (Do Thái và Thiên Chúa) kể cả dân đa thần lại muốn Thượng Đế của các ngươi ban điều tốt xuống cho các ngươi bao giờ (hỡi những người có đức tin). Tuy nhiên, với lòng thương xót của Ngài, Ngài dành đặc ân cho ai Ngài muốn bởi Allah là chủ nhân của thiên ân vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(2,106,'2:106','Bất cứ câu Kinh nào TA (Allah) xóa bỏ hoặc chỉ xóa đi giáo lý (vẫn giữ nguyên câu Kinh) cũng đều được TA mang đến câu Kinh khác tốt hơn hoặc tương đương. Lẽ nào Ngươi không biết Allah toàn năng trên tất cả mọi thứ ư?');
INSERT INTO "vn_ruwwad" VALUES(2,107,'2:107','Lẽ nào Ngươi không biết vương quyền cai trị các tầng trời và trái đất đều thuộc về Allah hay sao? Và ngoài Allah, các ngươi sẽ không có bất cứ vị bảo hộ cũng như vị giúp đỡ nào.');
INSERT INTO "vn_ruwwad" VALUES(2,108,'2:108','Hoặc có phải các ngươi (những người có đức tin) muốn đòi hỏi Thiên Sứ của các ngươi giống như Musa đã bị (người dân của mình) đòi hỏi trước đây ư? Người nào mang đức tin để đổi lấy sự vô đức tin thì quả thật y đã lạc khỏi con đường (chính đạo).');
INSERT INTO "vn_ruwwad" VALUES(2,109,'2:109','Rất nhiều dân Kinh Sách chỉ vì lòng ganh tị mà họ muốn kéo các ngươi trở lại tình trạng vô đức tin như trước đây, mặc dù chân lý đã phơi bày rõ ràng trước mặt họ. Nhưng thôi, các ngươi hãy lượng thứ và bỏ qua cho họ đến khi Allah ban hành sắc lệnh của Ngài xuống. Quả thật, Allah toàn năng trên tất cả mọi việc.');
INSERT INTO "vn_ruwwad" VALUES(2,110,'2:110','Các ngươi hãy duy trì lễ nguyện Salah và xuất Zakah; và bất cứ điều (tốt) nào mà các ngươi đã gởi đi trước cho bản thân các ngươi, các ngươi sẽ tìm thấy chúng (đầy đủ) ở nơi Allah bởi quả thật Allah luôn thấy rõ mọi điều các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(2,111,'2:111','Và họ (những người Do Thái và Thiên Chúa) nói: “Không ai được vào Thiên Đàng trừ người Do Thái hoặc Thiên Chúa.” Đó chẳng qua chỉ là mộng tưởng của họ mà thôi. Ngươi hãy bảo họ (hỡi Muhammad): “Vậy các người hãy đưa ra bằng chứng chứng minh lời nói của các người là sự thật đi nào!”');
INSERT INTO "vn_ruwwad" VALUES(2,112,'2:112','Không (như lời họ nói), chỉ có những ai phủ phục Allah và là người làm tốt (trong thờ phượng cũng như trong hành thiện) thì họ mới được phần thưởng từ Thượng Đế của họ và họ sẽ không còn phải lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "vn_ruwwad" VALUES(2,113,'2:113','Những người Do Thái thì bảo những người Thiên Chúa chẳng có gì (làm cơ sở cho tôn giáo của mình) còn những người Thiên Chúa thì lại nói những người Do Thái chẳng có gì (làm cơ sở cho tôn giáo của mình) trong khi cả hai nhóm đều đọc cùng một Kinh Sách. Tương tự như vậy, những kẻ không hiểu biết cũng nói ra những lời giống như họ. Nhưng rồi đây vào Đời Sau, Allah sẽ phân xử giữa họ về những điều họ đã từng tranh cãi với nhau.');
INSERT INTO "vn_ruwwad" VALUES(2,114,'2:114','Thế còn ai sai quấy hơn những kẻ đã ngăn cản thiên hạ tụng niệm Allah trong các Masjid( của Ngài cũng như không ngừng tìm mọi cách đập phá cho bằng được (các Masjid). Những kẻ đó, phải làm cho họ sợ mỗi khi họ muốn vào các Masjid. Họ phải bị hạ nhục ở trần gian này còn ở Đời Sau họ sẽ phải đối mặt với sự trừng phạt vô cùng khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(2,115,'2:115','Hướng đông và hướng tây đều là của Allah, cho nên, dù các ngươi có quay mặt về hướng nào đi chăng nữa thì cũng đều hướng đến Allah cả. Quả thật, Allah là Đấng Bao La, Đấng Hiểu Biết.');
INSERT INTO "vn_ruwwad" VALUES(2,116,'2:116','Họ (những người Do Thái, Thiên Chúa và đa thần) bảo rằng Allah có con trai. Thật trong sạch thay Ngài! Không phải như thế, bởi tất cả vạn vật trong các tầng trời và trái đất đều là của Ngài, tất cả đều phủ phục trước Ngài (với vai trò là những tạo vật của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(2,117,'2:117','Ngài (Allah) đã sáng tạo ra các tầng trời và trái đất, (đối với Ngài) mỗi khi Ngài định điều gì, Ngài chỉ cần phán “Hãy thành”, (ngay lập tức) nó sẽ thành (theo đúng ý của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(2,118,'2:118','Những kẻ thiếu hiểu biết (thuộc những người Do Thái, Thiên Chúa và đa thần) bảo: “Tại sao Allah lại không nói chuyện trực tiếp với chúng tôi hoặc phơi bày cho chúng tôi thấy một dấu hiệu để chứng minh?” Tương tự, những cộng đồng trước họ cũng đã nói giống như lời của họ, đúng là tất cả họ đều có con tim tương đồng nhau. Quả thật, TA đã phơi bày rõ ràng các dấu hiệu cho những người có niềm tin kiên định.');
INSERT INTO "vn_ruwwad" VALUES(2,119,'2:119','Quả thật, TA (Allah) đã cử phái Ngươi (Muhammad) đến để báo tin mừng (về Thiên Đàng) và cảnh báo (về Hỏa Ngục) bằng sự thật, và Ngươi sẽ không bị chất vất về những người bạn của Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(2,120,'2:120','Những người Do Thái và những người Thiên Chúa sẽ không bao giờ bằng lòng với Ngươi (Muhammad) ngoại trừ Ngươi chịu theo tôn giáo của họ. Ngươi hãy nói với họ: “Quả thật, sự hướng dẫn của Allah mới là chỉ đạo (đúng đắn)”. Nhưng nếu như Ngươi đi theo họ sau khi Ngươi đã tiếp nhận được kiến thức thì chắc chắn Ngươi sẽ không có ai che chở và giúp đỡ cả.');
INSERT INTO "vn_ruwwad" VALUES(2,121,'2:121','Đối với những ai được ban cho Kinh Sách rồi đọc Nó theo cách mà Nó phải được đọc thì đấy là những người thực sự có đức tin nơi (Kinh Sách đó); còn ai phủ nhận Nó thì sẽ là những kẻ thất bại.');
INSERT INTO "vn_ruwwad" VALUES(2,122,'2:122','Hỡi người dân Israel, các ngươi hãy ghi nhớ ân huệ mà TA đã đặc ân cho các ngươi và quả thật TA đã ưu đãi các ngươi hơn thiên hạ.');
INSERT INTO "vn_ruwwad" VALUES(2,123,'2:123','Các ngươi hãy sợ Ngày mà không ai gánh chịu thay cho ai, Ngày mà sự bồi thường không được chấp nhận, sự can thiệp trở nên vô ích và không ai cứu giúp được ai.');
INSERT INTO "vn_ruwwad" VALUES(2,124,'2:124','Ngươi (Muhammad) hãy nhớ lại khi Ibrahim (Abraham) hoàn thành tốt tất cả thử thách mà Thượng Đế của Y đã thử thách. Allah phán: “Rằng TA sẽ cử Ngươi làm một nhà lãnh đạo gương mẫu cho nhân loại.” (Ibrahim) thưa: “Và xin được như thế trong dòng tộc của bề tôi.” (Allah) đáp: “Tuy nhiên, lời hứa của TA không dành cho những kẻ sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(2,125,'2:125','Ngươi hãy nhớ lại khi TA đã chỉ định Ngôi đền (Ka’bah) linh thiêng làm một nơi an toàn cho thiên hạ tới lui thăm viếng và (TA ra lệnh cho những ai đến viếng): “Các ngươi hãy lấy chỗ đứng của Ibrahim làm chỗ dâng lễ nguyện Salah.” Và TA bắt Ibrahim và Isma’il cam kết giữ Ngôi Đền của TA luôn trong sạch cho những người đến để Tawaf (đi vòng quanh Nó), những người đến để I’tikaf (chuyên tu trong Nó) và những người đến để lễ nguyện Salah trong Nó.');
INSERT INTO "vn_ruwwad" VALUES(2,126,'2:126','Ngươi hãy nhớ lại khi Ibrahim cầu nguyện: “Lạy Thượng Đế của bề tôi, xin Ngài làm cho thành phố (Makkah) này được an toàn và phồn vinh, xin Ngài ban bố đủ loại trái quả làm nguồn thực phẩm cho cư dân của nó, những ai mà họ đã tin tưởng nơi Allah và Đời Sau.” (Allah) phán: “Còn ai vô đức tin thì TA sẽ cho y hưởng thụ trong chốc lát rồi TA sẽ tống cổ y đến với cực hình của Hỏa Ngục, một chốn ngụ cuối cùng thật tồi tệ.”');
INSERT INTO "vn_ruwwad" VALUES(2,127,'2:127','Ngươi hãy nhớ lại khi Ibrahim và Isma’il đặt nền móng cho Ngôi Đền, cả hai cầu nguyện: “Lạy Thượng Đế của bầy tôi, xin Ngài chấp nhận việc làm này của bầy tôi, bởi vì Ngài là Đấng Hằng Nghe, Đấng Hiểu Biết.”');
INSERT INTO "vn_ruwwad" VALUES(2,128,'2:128','“Lạy Thượng Đế của bầy tôi, xin Ngài khiến hai cha con bầy tôi thành hai người Muslim và cả dòng tộc của bầy tôi thành một cộng đồng Muslim, xin Ngài chỉ dạy cho bầy tôi những nghi thức thờ phượng và xin Ngài lượng thứ cho những lầm lỡ của bầy tôi, quả thật Ngài là Đấng Hằng Chấp Nhận Sám Hối, Đấng Khoan Dung.”');
INSERT INTO "vn_ruwwad" VALUES(2,129,'2:129','“Lạy Thượng Đế của bầy tôi, xin Ngài hãy dựng lên trong con cháu của bầy tôi một Thiên Sứ xuất thân từ chúng để y đọc cho chúng nghe những lời thiên khải của Ngài, để y dạy chúng Kinh Sách và sự khôn ngoan, và để y thanh lọc chúng. Quả thật, Ngài là Đấng Toàn Năng, Đấng Sáng Suốt.”');
INSERT INTO "vn_ruwwad" VALUES(2,130,'2:130','Chỉ người nào (bất công với chính bản thân mình) bởi sự ngu muội của y mới chối bỏ tôn giáo của Ibrahim, bởi TA đã chọn Y (làm một vị lãnh đạo gương mẫu) ở đời này và ở Đời Sau chắc chắn Y sẽ thuộc hàng ngũ những người đức hạnh.');
INSERT INTO "vn_ruwwad" VALUES(2,131,'2:131','Ngươi hãy nhớ lại khi Thượng Đế của Y (Ibrahim) bảo Y: “Ngươi hãy thần phục TA” thì Y liền đáp: “Bề tôi xin thần phục Thượng Đế của vũ trụ và vạn vật.”');
INSERT INTO "vn_ruwwad" VALUES(2,132,'2:132','Ibrahim đã căn dặn các con của Y điều đó, và Ya’qub (Jacob) cũng căn dặn các con của mình như vậy, (Y nói): “Này các con, Allah đã chọn cho các con tôn giáo (Islam), vậy nên, các con hãy đừng chết trừ phi các con là những người Muslim.”');
INSERT INTO "vn_ruwwad" VALUES(2,133,'2:133','Hay các ngươi (hỡi người dân Israel) đã có mặt lúc Ya’qub bảo các con của Y trước lúc lâm chung khi Y hỏi: “Các con sẽ thờ phượng ai sau khi cha ra đi?” (Các con của Y) đồng trả lời: “Chúng con chỉ thờ phượng Thượng Đế của tổ tiên của cha là Ibrahim, Isma’il và Is-haq (Isaac), chúng con chỉ thờ một Thượng Đế duy nhất và chúng con là những tín đồ Muslim.”');
INSERT INTO "vn_ruwwad" VALUES(2,134,'2:134','Đó là cộng đồng đã qua, họ sẽ hưởng thành quả cho những gì họ làm, còn các ngươi sẽ hưởng thành quả tùy theo công lao của các ngươi, cho nên, các ngươi sẽ không bị chất vất về những gì họ đã làm.');
INSERT INTO "vn_ruwwad" VALUES(2,135,'2:135','Họ (dân Kinh Sách) bảo (tín đồ Muslim): “Nếu muốn được hướng dẫn đúng đường thì các người hãy là người Do Thái hoặc tín đồ Thiên Chúa.” Ngươi hãy bảo họ (hỡi Muhammad): “Không, tôn giáo thuần túy của Ibrahim (mới thực sự được hướng dẫn đúng đường) và Người không phải là kẻ thờ đa thần.”');
INSERT INTO "vn_ruwwad" VALUES(2,136,'2:136','Các ngươi hãy nói với họ (hỡi những người Muslim!): “Chúng tôi tin tưởng vào Allah, tin tưởng vào những gì đã được mặc khải cho chúng tôi, cho Ibrahim, cho Isma’il, cho Is-haq, cho Ya’qub, cho mười hai bộ lạc của Israel; chúng tôi tin vào những gì đã được thiên khải cho Musa, cho Ysa và cho tất cả các vị Nabi được Thượng Đế của Họ cử phái đến, chúng tôi không hề phân biệt bất cứ ai trong số Họ và chúng tôi là những tín đồ Muslim (thần phục Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(2,137,'2:137','Nếu như họ tin như các ngươi đã tin thì quả thật họ đã được hướng dẫn, còn nếu như họ quay lưng thì chính họ là những kẻ khơi nguồn chia rẽ và thù hận. Và một mình Allah đủ giúp Ngươi (Muhammad) đối đầu với họ bởi Allah là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(2,138,'2:138','(Đó là) tôn giáo của Allah, (các ngươi hãy bám chặt lấy tôn giáo của Ngài), bởi không có tôn giáo nào vượt trội hơn tôn giáo của Allah cả và (các ngươi hãy nói): “Chúng tôi chỉ thờ phượng riêng Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(2,139,'2:139','Ngươi hãy nói với họ: “Phải chăng các người muốn tranh luận với chúng tôi về Allah trong khi Ngài là Thượng Đế của chúng tôi và cũng là Thượng Đế của các người ư? Chúng tôi lo phần việc của chúng tôi còn các người cứ lo phần việc của các người; và chúng tôi một lòng (thờ phượng Ngài).');
INSERT INTO "vn_ruwwad" VALUES(2,140,'2:140','Hay là các ngươi cho rằng Ibrahim, Isma’il, Is-haq, Ya’qub cũng như mười hai bộ lạc của Israel trước đây đều là Do Thái hoặc là Thiên Chúa hay sao?” Ngươi hãy nói với họ: “Phải chăng các người biết rõ hơn Allah ư?” Thế còn ai tội lỗi hơn kẻ cố tình che giấu sự thật từ nơi Allah (được khẳng định trong Kinh Sách) mà họ đang nắm giữ?! Quả thật, Allah không hề thờ ơ trước bất cứ hành động nào của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(2,141,'2:141','Đó là cộng đồng đã qua, họ sẽ hưởng thành quả cho những gì họ làm, còn các ngươi sẽ hưởng thành quả tùy theo công lao của các ngươi, cho nên, các ngươi sẽ không bị chất vất về những gì họ đã làm.');
INSERT INTO "vn_ruwwad" VALUES(2,142,'2:142','Những kẻ hiểu biết kém cỏi trong thiên hạ (thuộc đám người Do Thái) sẽ nói: “Điều gì khiến họ (người Muslim) phải thay đổi Qiblah( mà họ thường hướng về đó trước đây?” Ngươi hãy nói (hỡi Muhammad): “Hướng đông lẫn hướng tây đều thuộc về Allah, Ngài hướng dẫn ai Ngài muốn đến với con đường ngay thẳng (chính đạo).”');
INSERT INTO "vn_ruwwad" VALUES(2,143,'2:143','Tương tự như thế, TA (Allah) đã thiết lập các ngươi thành một cộng đồng dung hòa để các ngươi làm nhân chứng cho thiên hạ, còn Thiên Sứ (Muhammad) sẽ là nhân chứng cho các ngươi. Quả thật, việc TA thay đổi Qiblah mà các ngươi đã thường quay về đó là chỉ vì TA muốn biết rõ ai là người thật lòng theo Thiên Sứ và ai là kẻ ngoảnh mặt với Y. Mặc dù đó là một thay đổi lớn nhưng đối với những ai được Allah hướng dẫn thì không cảm thấy có gì là quá nặng nề và nghiêm trọng. Chắc chắn Allah sẽ không làm mất đi đức tin (công sức hành đạo) trước đây của các ngươi, bởi Allah là Đấng Thương Xót đối với nhân loại.');
INSERT INTO "vn_ruwwad" VALUES(2,144,'2:144','Quả thật, TA đã thấy Ngươi (hỡi Thiên Sứ Muhammad) thường ngước mặt trên trời (với khao khát đổi Qiblah về ngôi đền Ka’bah). Cho nên, TA cho phép Ngươi chuyển Qiblah về hướng mà Ngươi hài lòng, vì vậy, Ngươi hãy quay mặt mình về hướng Masjid Haram (ở Makkah); và cho dù các ngươi ở bất cứ đâu cũng phải hướng mặt về đó (để dâng lễ Salah). Những ai đã được ban cho Kinh Sách chắc chắn đều biết rõ đó là chân lý từ nơi Thượng Đế của họ và Allah không làm ngơ trước những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(2,145,'2:145','Dẫu cho Ngươi có trình bày tất cả bằng chứng cho dân Kinh Sách thấy thì họ cũng không theo hướng Qiblah của Ngươi và Ngươi không theo Qiblah của họ. Cả hai bên, không ai theo Qiblah của ai. Nhưng nếu như Ngươi dám chiều theo sở thích của họ sau khi Ngươi đã tiếp nhận được chân lý thì Ngươi chính là kẻ làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(2,146,'2:146','Những người mà TA (Allah) ban cho Kinh Sách đều nhận biết Y (Muhammad) giống như họ nhận biết con cái của họ. Tuy nhiên, một thành phần trong số họ cố tình che giấu sự thật trong khi họ biết rõ (điều đó).');
INSERT INTO "vn_ruwwad" VALUES(2,147,'2:147','Chân lý đến từ Thượng Đế của Ngươi nên Ngươi chớ sinh lòng hoài nghi.');
INSERT INTO "vn_ruwwad" VALUES(2,148,'2:148','Mỗi một cộng đồng có một Qiblah riêng để hướng mặt về đó. Nhưng dù sao đi nữa thì các ngươi hãy thi đua nhau mà hành thiện và dẫu cho các ngươi có ở đâu thì Allah cũng triệu tập toàn bộ các ngươi về để trình diện, quả thật Allah toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(2,149,'2:149','Cho dù Ngươi (hỡi Thiên Sứ) có đi đâu về đâu thì cũng hãy quay mặt của Ngươi về hướng Masjid Haram (khi hành lễ Salah), bởi quả thật đó là chân lý đến từ Thượng Đế của Ngươi và Allah không làm ngơ trước những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(2,150,'2:150','Cho dù Ngươi có đi đâu về đâu thì cũng hãy quay mặt của Ngươi về hướng Masjid Haram, và cho dù các ngươi đang ở bất kỳ nơi đâu cũng phải hướng mặt của các ngươi về hướng đó để thiên hạ không có cớ chống lại các ngươi ngoại trừ đám người lệch lạc. Vì vậy, các ngươi chớ sợ họ mà hãy sợ TA; và để TA hoàn thiện ân huệ của TA cho các ngươi, hi vọng các ngươi được hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(2,151,'2:151','Tương tự, TA đã gởi đến các ngươi một vị Thiên Sứ xuất thân từ các ngươi, Y đọc cho các ngươi nghe các lời mặc khải của TA, thanh lọc các ngươi, dạy các ngươi Kinh Sách (Qur’an) và sự khôn ngoan (Sunnah), và dạy các ngươi những điều các ngươi chưa từng biết.');
INSERT INTO "vn_ruwwad" VALUES(2,152,'2:152','Vì vậy, các ngươi hãy nhớ đến TA, TA sẽ nhớ đến các ngươi; các ngươi hãy tri ân TA và chớ là những kẻ phụ ơn.');
INSERT INTO "vn_ruwwad" VALUES(2,153,'2:153','Hỡi những người có đức tin, các ngươi hãy cầu xin sự giúp đỡ (của Allah) bằng lòng kiên nhẫn và lễ nguyện Salah. Quả thật, Allah luôn bên cạnh những người kiên nhẫn.');
INSERT INTO "vn_ruwwad" VALUES(2,154,'2:154','Các ngươi đừng nói với những ai đã hy sinh cho con đường chính nghĩa của Allah rằng họ đã chết bởi đích thực họ vẫn đang còn sống nhưng các ngươi không cảm nhận được mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(2,155,'2:155','TA chắc chắn thử thách các ngươi với nỗi sợ hãi, với cơn đói khát, với việc mất mát tài sản, với sự nguy hại tính mạng và với việc thiệt hại trái quả (thất bát mùa màng). Tuy nhiên, Ngươi (hỡi Thiên Sứ) hãy báo tin vui cho những người biết kiên nhẫn.');
INSERT INTO "vn_ruwwad" VALUES(2,156,'2:156','Những ai mà họ khi gặp phải tai ương thì nói: “Quả thật, bầy tôi thuộc về Allah và bầy tôi phải quay về với Ngài!”(');
INSERT INTO "vn_ruwwad" VALUES(2,157,'2:157','Đó là những người sẽ được phúc lành và sự thương xót từ Thượng Đế của họ và đó là những người được hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(2,158,'2:158','Hai ngọn núi Safa và Marwah là một trong các biểu hiệu của Allah. Bởi thế, ai hành hương Hajj hoặc ‘Umrah sẽ không bị bắt tội khi Sa-‘i (đi qua lại) giữa hai ngọn núi đó, còn ai (Sa-‘i) tự nguyện thì quả thật Allah là Đấng Ghi Công, Đấng Hiểu Biết.');
INSERT INTO "vn_ruwwad" VALUES(2,159,'2:159','Quả thật, những ai giấu giếm những bằng chứng và sự hướng dẫn mà TA (Allah) đã thiên khải xuống sau khi TA đã trình bày rõ ràng cho thiên hạ trong Kinh Sách thì những người đó là những kẻ bị Allah và vạn vật nguyền rủa.');
INSERT INTO "vn_ruwwad" VALUES(2,160,'2:160','Ngoại trừ, những ai biết hối cải, phục thiện và công khai sự thật thì đó là những người được TA chấp nhận sự ăn năn sám hối của họ, bởi quả thật TA là Đấng Hằng Chấp Nhận sự sám hối, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(2,161,'2:161','Quả thật những ai vô đức tin và chết trong lúc họ vẫn vô đức tin như thế thì tất cả họ là những kẻ bị Allah và các vị Thiên Thần cùng toàn bộ loài người nguyền rủa.');
INSERT INTO "vn_ruwwad" VALUES(2,162,'2:162','(Lời nguyền rủa) mãi mãi bao trùm lấy họ, hình phạt dành cho họ không được giảm nhẹ và họ sẽ không được xem xét và buông tha.');
INSERT INTO "vn_ruwwad" VALUES(2,163,'2:163','Thượng Đế của các ngươi vốn chỉ là một Thượng Đế duy nhất, không có Thượng Đế đích thực nào ngoài Ngài, Ngài là Đấng Độ Lượng, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(2,164,'2:164','Quả thật, việc tạo hóa trời đất, việc luân chuyển nối tiếp nhau của ban đêm và ban ngày, việc những chiếc tàu băng qua đại dương chuyên chở nguồn lợi cho con người, việc Allah ban nước mưa từ trên trời xuống làm sống lại những mảnh đất đã chết khô, việc Ngài phân bố đủ loại sinh vật trên trái đất, và việc Ngài chế ngự các luồng gió cùng các đám mây khiến chúng dịch chuyển giữa khoảng rộng của trời đất, (tất cả) là những dấu hiệu (nhận biết Ngài) dành cho đám người biết suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(2,165,'2:165','Tuy nhiên, trong nhân loại có những kẻ dựng lên các thần linh để thờ phượng thay vì Allah, họ yêu thương các thần linh đó giống như yêu thương Allah; riêng những người có đức tin thì họ yêu thương Allah mãnh liệt hơn (mọi thứ). Và nếu như những kẻ sai quấy đó tận mắt nhìn thấy hình phạt thì lúc đó họ mới ngộ ra rằng tất cả quyền lực đều thuộc về Allah và Ngài quả thật rất nghiêm khắc trong sự trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(2,166,'2:166','Khi nhìn thấy hình phạt, những kẻ được thờ phượng tuyên bố vô can với tất cả những ai đã thờ phượng chúng và mọi quan hệ giữa chúng đều bị cắt đứt hoàn toàn.');
INSERT INTO "vn_ruwwad" VALUES(2,167,'2:167','Những kẻ thờ phượng sẽ nói: “Ôi giá như chúng tôi có cơ hội trở lại trần gian lần nữa thì chúng tôi sẽ tuyên bố vô can với họ giống như họ đã tuyên bố vô can với chúng tôi lúc này.” Như thế đó, Allah cho họ thấy hậu quả của việc làm của họ trước đây toàn là những ân hận và nuối tiếc (trong muộn màng) và họ sẽ không bao giờ thoát khỏi Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(2,168,'2:168','Hỡi loài người, các ngươi hãy ăn những gì được phép và tốt lành trên trái đất, và các ngươi chớ đi theo lối mòn của Shaytan bởi hắn đích thực là kẻ thù công khai của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(2,169,'2:169','Hắn chuyên xúi giục các ngươi làm điều xấu xa và đồi bại, hắn xúi bẩy các ngươi nói cho Allah về những điều mà các ngươi không biết.');
INSERT INTO "vn_ruwwad" VALUES(2,170,'2:170','(Thế nhưng) khi có lời khuyên bảo họ (những người vô đức tin): “Các ngươi hãy noi theo những điều được Allah mặc khải.” Họ đáp: “Không, chúng tôi chỉ đi theo những gì được tổ tiên chúng tôi truyền lại.” Chẳng lẽ (họ theo) ngay cả khi tổ tiên của họ không hiểu cũng như không được hướng dẫn ư?');
INSERT INTO "vn_ruwwad" VALUES(2,171,'2:171','Hình ảnh những người vô đức tin đó giống như hình ảnh của những con cừu khi (người chăn chúng kêu gọi chúng) thì chúng không nghe được gì ngoài tiếng réo và kêu vang (của y chứ không hiểu y nói gì). Họ điếc, câm và mù nên chẳng hiểu được gì.');
INSERT INTO "vn_ruwwad" VALUES(2,172,'2:172','Hỡi những người có đức tin, các ngươi hãy ăn những thực phẩm tốt sạch mà TA đã ban bố cho các ngươi; và các ngươi hãy tri ân Allah nếu các ngươi chỉ thờ phượng riêng Ngài.');
INSERT INTO "vn_ruwwad" VALUES(2,173,'2:173','Quả thật, Ngài (Allah) chỉ cấm các ngươi ăn xác chết, máu (phun ra ngoài cơ thể của con vật), thịt heo và những con vật được nhân danh các thần linh khác ngoài Allah (khi giết). Tuy nhiên, ai rơi vào tình huống bất đắc dĩ phải ăn (những món cấm đó) ngoài ý muốn và không quá độ thì y không bị bắt tội. Bởi Allah là Đấng Tha Thứ, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(2,174,'2:174','Quả thật những ai giấu giếm những gì được Allah mặc khải xuống trong Kinh Sách (báo trước về sứ mạng của Muhammad) và đem bán nó với giá cỏn con là những kẻ chỉ ăn lửa vào bụng của mình; và vào Đời Sau Allah sẽ không nói chuyện với họ, không thanh lọc (tội lỗi cho) họ và họ sẽ phải chịu hình phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(2,175,'2:175','Đó là những kẻ đã mua lấy sự lầm lạc bằng chỉ đạo, đã mua lấy sự trừng phạt bằng sự tha thứ. Họ đúng thật là can đảm khi dám đương đầu với Hỏa Ngục!');
INSERT INTO "vn_ruwwad" VALUES(2,176,'2:176','(Họ bị như thế là do họ cố tình che giấu) sự thật được Allah thiên khải trong Kinh Sách; và quả thật những ai bất đồng về Kinh Sách chắc chắn là những kẻ lạc lối rất xa.');
INSERT INTO "vn_ruwwad" VALUES(2,177,'2:177','Sự ngoan đạo không phải ở việc các ngươi quay mặt về hướng đông hay hướng tây mà sự ngoan đạo là một người phải có đức tin nơi Allah, nơi cõi Đời Sau, nơi các vị Thiên Thần, nơi Kinh Sách và nơi các vị Nabi, đồng thời bố thí tài sản mà y yêu thích cho bà con ruột thịt, cho các trẻ mồ côi, cho những người thiếu thốn, cho những người lỡ đường, cho những người ăn xin, chuộc tự do cho nô lệ; chu đáo duy trì lễ nguyện Salah, xuất Zakah, thực hiện đúng giao ước khi đã giao ước; kiên nhẫn chịu đựng trong khó khăn (đói khổ), trong biến cố (bệnh tật), và lúc chinh chiến với kẻ thù. Đó mới là những người chân thật (trong đức tin) và đó mới là những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(2,178,'2:178','Hỡi những người có đức tin, Qisas( đã được sắc lệnh thành luật cho các ngươi trong vấn đề giết chóc, nó được quy định một mạng đền một mạng: người tự do bằng người tự do; người nô lệ bằng người nô lệ; một người nữ bằng một người nữ. Tuy nhiên, nếu người phạm nhân nào được người anh em của mình (từ bên phía nạn nhân) xí xóa (không đòi Qisas) thì y hãy cư xử sao cho phải lẽ và y hãy bồi thường cho nạn nhân một cách tốt nhất. Đó là sự giảm nhẹ và lòng thương xót từ Thượng Đế của các ngươi. Cho nên sau các qui định được nêu trên, ai vượt quá giới hạn thì sẽ bị trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(2,179,'2:179','Và trong luật Qisas, các ngươi hỡi những người hiểu biết sẽ có (một sự đảm bảo) về cuộc sống, mong rằng các ngươi sống ngay chính và ngoan đạo (qua giáo luật này).');
INSERT INTO "vn_ruwwad" VALUES(2,180,'2:180','Các ngươi được sắc lệnh: khi ai đó trong các ngươi sắp từ trần, nếu y để lại tài sản thì y phải lập di chúc cho cha mẹ và họ hàng thân thuộc một cách hợp lý.( Đó là bổn phận của những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(2,181,'2:181','Ai sửa đổi lời di chúc sau khi đã nghe rõ nó thì chỉ những kẻ sửa đổi mới phải chịu tội. Quả thật, Allah là Đấng Hằng Nghe, Đấng Hiểu Biết.');
INSERT INTO "vn_ruwwad" VALUES(2,182,'2:182','Tuy nhiên, ai sợ người để lại di chúc có sự thiên vị hoặc vi phạm điều tội lỗi (trong di chúc) nên đứng ra dàn xếp giữa họ (những người can hệ) cho phải lẽ thì y không bị bắt tội, bởi Allah là Đấng Tha Thứ, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(2,183,'2:183','Hỡi những người có đức tin, việc nhịn chay đã được sắc lệnh cho các ngươi giống như nó đã được sắc lệnh cho các thế hệ trước các ngươi, mong rằng các ngươi trở thành những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(2,184,'2:184','(Nhịn chay chỉ bắt buộc trong) một số ngày nhất định. Tuy nhiên, ai trong các ngươi bị bệnh hoặc là khách lữ hành thì y hãy nhịn bù lại vào những ngày khác tương ứng với số ngày đã không nhịn. Và ai có khả năng nhịn chay nhưng không muốn nhịn thì hãy nuôi ăn một người nghèo (thay thế cho một ngày nhịn chay),( còn ai tự nguyện (bố thí) thì đó là điều tốt đẹp cho y. Tuy nhiên, việc các ngươi nhịn chay sẽ tốt hơn cho các ngươi cho các ngươi nếu các ngươi biết được (giá trị của nhịn chay).');
INSERT INTO "vn_ruwwad" VALUES(2,185,'2:185','Tháng Ramadan là tháng mà Qur’an được ban xuống làm nguồn hướng dẫn cho nhân loại, trình bày rõ ràng về sự hướng dẫn và là tiêu chuẩn phân biệt (phúc và tội, chân lý và ngụy tạo). Vì vậy, ai trong các ngươi chứng kiến tháng này (khi đang ở nhà) thì phải nhịn chay nguyên tháng, còn ai bị bệnh hoặc đi xa nhà thì y hãy nhịn bù lại vào những ngày khác tương ứng với số ngày đã không nhịn. Allah muốn điều dễ dàng cho các ngươi chứ không hề muốn gây khó khăn cho các ngươi; và Ngài muốn các ngươi hoàn thành tốt (việc nhịn chay) trong số ngày được quy định và Ngài muốn các ngươi tán dương sự vĩ đại của Ngài về việc Ngài đã hướng dẫn các ngươi, mong rằng các ngươi biết tri ân.');
INSERT INTO "vn_ruwwad" VALUES(2,186,'2:186','Và khi bầy tôi của TA hỏi Ngươi (hỡi Thiên Sứ) về TA thì Ngươi hãy bảo họ rằng quả thật TA ở rất gần, TA luôn đáp lại lời cầu xin của ai cầu xin TA. Do đó, họ hãy đáp lại (lời gọi của) TA và hãy tin nơi TA, mong rằng họ được hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(2,187,'2:187','Các ngươi được phép gần gũi vợ của các ngươi vào ban đêm (của tháng) nhịn chay,( họ là y phục của các ngươi và các ngươi là y phục của họ. Allah đã biết việc các ngươi thường lén lút (gần gũi họ) nên Ngài đoái thương mà lượng thứ cho các ngươi. Giờ đây các ngươi được tự do gần gũi họ và hãy tìm kiếm những gì được Allah an bày cho các ngươi. Các ngươi được tự do ăn uống trong đêm cho đến khi các ngươi phân biệt được sợi chỉ trắng với sợi chỉ đen lúc rạng đông, khi đó các ngươi lại tiếp tục cuộc nhịn chay cho đến đêm. Các ngươi chớ gần gũi vợ của các ngươi trong suốt thời gian các ngươi đang ‘Etikaf (lánh trần tu tịnh) trong các Masjid. Đấy là những giới cấm của Allah, các ngươi chớ đến gần. Như thế đó, Allah đã trình bày rõ ràng các lời mặc khải của Ngài cho nhân loại, mong rằng họ biết sợ Ngài.');
INSERT INTO "vn_ruwwad" VALUES(2,188,'2:188','Các ngươi chớ đừng ăn tài sản của nhau một cách bất chính và cũng chớ đừng lấy đó mà kiện tụng ra tòa hầu ăn tài sản của thiên hạ một cách tội lỗi trong khi các ngươi biết rõ (đó là sai trái).');
INSERT INTO "vn_ruwwad" VALUES(2,189,'2:189','Họ hỏi Ngươi (hỡi Thiên Sứ) về các vầng trăng lưỡi liềm. Ngươi hãy bảo họ: “Các vầng trăng lưỡi liềm là những móc thời gian để con người tính toán ngày tháng và việc thi hành Hajj.” Việc ngoan đạo không thể hiện qua hành động đi vào nhà của mình từ nóc nhà (sau khi đã vào tình trạng Ehram),( mà sự ngoan đạo được thể hiện ở lòng kính sợ (Allah). Cho nên, các ngươi hãy vào nhà từ các cửa chính và các ngươi hãy kính sợ Allah, mong rằng các ngươi được thành công.');
INSERT INTO "vn_ruwwad" VALUES(2,190,'2:190','Các ngươi hãy vì chính nghĩa của Allah mà chiến đấu chống lại những kẻ khai chiến với các ngươi nhưng các ngươi chớ đừng quá mức giới hạn (sát hại phụ nữ, người già và trẻ con) bởi Allah không ưa thích những kẻ vượt quá giới hạn.');
INSERT INTO "vn_ruwwad" VALUES(2,191,'2:191','Và các ngươi hãy giết họ (những người vô đức tin) ở bất cứ nơi đâu mà các ngươi bắt được họ và các ngươi hãy trục xuất họ ra khỏi nơi (Makkah) mà họ từng trục xuất các ngươi trước đây. Tuy nhiên, việc quấy nhiễu (lôi kéo một người có đức tin trở lại với sự vô đức tin) còn nghiêm trọng hơn cả việc giết chóc. Các ngươi chớ khai chiến với họ trong Masjid Haram trừ phi họ khai chiến trước tiên, nếu họ khai chiến trước thì các ngươi hãy giết họ. Sự đáp trả đối với những kẻ vô đức tin là như thế.');
INSERT INTO "vn_ruwwad" VALUES(2,192,'2:192','Nhưng nếu họ ngưng chiến thì quả thật Allah là Đấng Tha Thứ, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(2,193,'2:193','Các ngươi hãy tiếp tục đánh họ cho đến khi nào họ chấm dứt hành động quấy nhiễu (ngăn cản mọi người đến với tôn giáo Islam) và chịu để tôn giáo của Allah được tự do. Nhưng nếu họ chịu ngưng chiến thì không còn mối hận thù nào đối với họ nữa ngoại trừ những kẻ làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(2,194,'2:194','Tháng cấm kỵ được bù lại bằng tháng cấm kỵ, và những hành động xâm hại vẫn phải thi hành theo luật Qisas. Bởi thế, ai xâm hại các ngươi thì các ngươi hãy đáp trả lại y giống như việc y đã xâm hại các ngươi. Nhưng các ngươi hãy kính sợ Allah, quả thật Ngài luôn ở cùng với những người biết kính sợ Ngài.');
INSERT INTO "vn_ruwwad" VALUES(2,195,'2:195','Các ngươi hãy chi dùng (tài sản của các ngươi) vào con đường chính nghĩa của Allah; các ngươi chớ đẩy bản thân mình đến chỗ tự hủy bằng chính đôi tay của các ngươi; và các ngươi hãy hành thiện bởi quả thật Allah yêu thương những người hành thiện.');
INSERT INTO "vn_ruwwad" VALUES(2,196,'2:196','Và các ngươi hãy hoàn thành Hajj và ‘Umrah vì Allah. Trường hợp các ngươi bị cản trở (do bệnh hoặc một điều gì đó không thể tiếp tục hành hương) thì hãy giết một con vật tế để chịu phạt tùy theo khả năng; các ngươi không được cạo đầu cho đến khi con vật tế đã được mang đến nơi qui định. Ai trong các ngươi bị bệnh hoặc bị nấm da đầu (cần phải cạo) thì phải chịu phạt bằng cách nhịn chay (ba ngày) hoặc bố thí (thức ăn cho sáu người nghèo ở khu vực Haram) hoặc giết tế (con cừu chia thịt cho người nghèo ở khu vực Haram). Đối với ai muốn tiếp tục làm ‘Umrah rồi đến Hajj (được gọi là Hajj Tamadtua’ hoặc Hajj Qiran) thì phải giết tế tùy theo khả năng, còn ai không có khả năng giết tế thì phải nhịn chay ba ngày trong thời gian hành hương Hajj và nhịn bảy ngày còn lại sau khi trở về quê nhà, đó là mười ngày trọn vẹn. Giáo luật này chỉ đối với những ai định cư ngoài khu vực của Masjid Haram. Các ngươi hãy hết lòng kính sợ Allah và hãy biết rằng quả thật Allah rất nghiêm khắc trong trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(2,197,'2:197','Việc hành hương Hajj chỉ (được thực hiện) trong những tháng nhất định. Cho nên, ai thực hiện cuộc hành hương Hajj thì chớ dâm dục, chớ làm điều tội lỗi cũng như chớ cãi vã trong suốt thời gian hành hương. Và bất cứ việc tốt đẹp nào mà các ngươi làm, Allah đều biết rõ (Ngài sẽ ghi nhận tất cả). Và các ngươi hãy mang theo lương thực (cho chuyến đi) nhưng lương thực tốt nhất chính là Taqwa (sự ngay chính, ngoan đạo và sợ Allah). Bởi thế, các ngươi hãy Taqwa đối với TA hỡi những người hiểu biết!');
INSERT INTO "vn_ruwwad" VALUES(2,198,'2:198','Các ngươi không bị bắt tội khi tìm kiếm bổng lộc từ Thượng Đế của các ngươi (qua việc mua bán trao đổi trong thời gian thực hiện Hajj). Rồi khi các ngươi rời khu vực ‘Arafat (để đến khu vực Muzdalifah) thì các ngươi hãy tụng niệm Allah tại Al-Mash’ar Al-Haram, các ngươi hãy tụng niệm Ngài theo đúng cách mà Ngài đã dạy cho các ngươi bởi trước đây các ngươi là những kẻ lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(2,199,'2:199','Sau đó, các ngươi hãy rời khỏi (Muzdalifah) cùng với những người hành hương, đồng thời hãy cầu xin Allah tha thứ tội lỗi (cho những điều thiếu sót), quả thật Allah là Đấng Tha Thứ, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(2,200,'2:200','Khi nào các ngươi hoàn thành các nghi thức hành hương thì các ngươi hãy tụng niệm Allah giống như việc các ngươi thường hay nhắc về cha mẹ (vì hãnh diện) hoặc nhiều hơn nữa. Trong nhân loại, có những người chỉ cầu xin “Lạy Thượng Đế của bầy tôi, xin hãy ban cho bầy tôi những điều tốt đẹp của cuộc sống trần gian” nên ở Đời Sau họ sẽ không có bất cứ điều tốt đẹp nào.');
INSERT INTO "vn_ruwwad" VALUES(2,201,'2:201','Và trong nhân loại, có những người cầu xin “Lạy Thượng Đế của bầy tôi, xin Ngài hãy ban cho bầy tôi mọi điều tốt đẹp ở trần gian này và mọi điều tốt đẹp ở Đời Sau; và xin Ngài hãy bảo vệ bầy tôi thoát khỏi hình phạt của Hỏa Ngục”.');
INSERT INTO "vn_ruwwad" VALUES(2,202,'2:202','Đây đích thực là những người sẽ hưởng được phần thưởng từ những điều tốt mà họ đã làm và Allah rất nhanh nhẹn trong phán xét.');
INSERT INTO "vn_ruwwad" VALUES(2,203,'2:203','Các ngươi hãy tụng niệm Allah vào những ngày nhất định,( nhưng ai vội vã rời đi sau hai ngày (ngày 11 và 12) thì không có tội và ai ở lại (cho đến ngày 13) thì cũng không có tội (bởi điều này) dành cho người ngoan đạo. Các ngươi hãy kính sợ Allah và các ngươi hãy biết rằng các ngươi phải trở về trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(2,204,'2:204','Trong thiên hạ, có người đã khiến Ngươi trầm trồ trước lời nói của y về cuộc sống trần gian, y còn mang Allah ra làm chứng cho những điều nằm trong trái tim của y trong khi y là kẻ không ngừng tranh luận (hầu phủ nhận sự thật).');
INSERT INTO "vn_ruwwad" VALUES(2,205,'2:205','Và khi y quay đi, y toàn làm chuyện tội lỗi trên trái đất, y phá hoại mùa màng và giết hại vật nuôi. Và Allah không hề ưa thích hành động phá hoại.');
INSERT INTO "vn_ruwwad" VALUES(2,206,'2:206','Rồi khi có lời khuyên bảo y “Hãy kính sợ Allah” thì y lại hãnh diện với tội lỗi mà y đã làm, y đáng bị đày vào Ngục Lửa, một chốn cư ngụ thật tồi tệ.');
INSERT INTO "vn_ruwwad" VALUES(2,207,'2:207','Tuy nhiên, trong nhân loại, cũng có người bán thân mình để tìm kiếm sự hài lòng của Allah, và Allah rất mực thương xót đám bầy tôi của Ngài.');
INSERT INTO "vn_ruwwad" VALUES(2,208,'2:208','Hỡi những người có đức tin, các ngươi hãy gia nhập hoàn toàn vào Islam và chớ đừng theo những vết chân của Shaytan, bởi quả thật hắn là kẻ thù công khai của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(2,209,'2:209','Nếu như các ngươi trượt chân (khỏi chân lý) sau khi các bằng chứng đã đến với các ngươi một cách rõ ràng thì các ngươi hãy biết rằng Allah là Đấng Toàn Năng, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(2,210,'2:210','Chẳng lẽ họ (những kẻ lầm lạc) chờ đợi Allah và các vị Thiên Thần cưỡi mây đến gặp riêng họ? (Nếu sự việc đúng như thế) thì vấn đề đã được giải quyết (họ đã bị trừng phạt và vấn đề đã được kết thúc), tuy nhiên, mọi việc đều được mang về trình diện Allah (để Ngài định đoạt).');
INSERT INTO "vn_ruwwad" VALUES(2,211,'2:211','Ngươi (hỡi Thiên Sứ) hãy hỏi dân Israel rằng đã bao lần TA (Allah) đã ban cho họ dấu hiệu rõ ràng (để nhận biết về sứ mạng của Thiên Sứ vậy mà họ vẫn ngoan cố bác bỏ sự thật). Đối với ai đáp lại ân huệ của Allah (bằng sự vong ân) sau khi nó đã được trình bày rõ cho y thì quả thật Allah rất nghiêm khắc trong trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(2,212,'2:212','Sự lộng lẫy, hào nhoáng của cuộc sống trần tục đã lôi cuốn những kẻ vô đức tin, đâm ra họ chê cười những người có đức tin cũng như những người sợ Đấng bên trên họ vào Ngày Phán Xét. Tuy nhiên, Allah muốn ban phát bổng lộc vô hạn cho ai tùy ý Ngài.');
INSERT INTO "vn_ruwwad" VALUES(2,213,'2:213','Thoạt đầu loài người vốn là một cộng đồng duy nhất (rất ngoan đạo và chỉ theo tôn giáo của ông tổ Adam, rồi họ dần dần bị lệch lạc) nên Allah lần lượt gởi đến họ các vị Nabi với sứ mạng mang tin mừng và cảnh báo. Ngài đã ban cho Họ Kinh Sách làm cơ sở chân lý để Họ phân xử mọi điều mâu thuẫn trong thiên hạ. Nhưng thật ra họ không mâu thuẫn về Nó (Kinh Sách) ngoại trừ những kẻ được ban cho (Kinh Sách) sau khi họ đã tiếp thu được các bằng chứng rõ rệt bởi lòng đố kỵ giữa họ. Do đó, dưới phép của Allah, Ngài đã phù hộ những người có đức tin đến với chân lý về những điều mà họ đã bất đồng và mâu thuẫn. Và Allah muốn hướng dẫn ai Ngài muốn đến với con đường ngay chính.');
INSERT INTO "vn_ruwwad" VALUES(2,214,'2:214','Hoặc lẽ nào các ngươi (hỡi những người có đức tin) nghĩ rằng các ngươi sẽ vào Thiên Đàng mà không phải trải qua những thử thách tương tự (những thử thách) mà các thế hệ trước các ngươi đã phải trải ư? Họ đã từng trải qua những nghịch cảnh và hoạn nạn và họ đã dao động tình thần đến nỗi vị Thiên sứ (Muhammad) và những tín đồ cùng với Y đều phải than: “Bao giờ sự cứu giúp của Allah mới đến?” Quả thật sự cứu giúp của Allah rất gần kề!');
INSERT INTO "vn_ruwwad" VALUES(2,215,'2:215','Họ (các bằng hữu của Ngươi) hỏi Ngươi (hỡi Thiên Sứ) cần phải bố thí loại tài sản nào (và cho ai?). Ngươi hãy đáp: “Các ngươi hãy bố thí tài sản tốt sạch (kiếm được bằng con đường Halal và tốt nhất là bố thí) cho cha mẹ, cho người thân, cho trẻ mồ côi, cho người nghèo, và cho người lỡ đường. Và những điều thiện tốt mà các ngươi từng làm thì Allah đều luôn biết rõ (Ngài sẽ ghi nhận ở nơi Ngài không sót bất cứ điều gì).');
INSERT INTO "vn_ruwwad" VALUES(2,216,'2:216','Lệnh chiến đấu đã được ban hành cho các ngươi và đó là điều mà các ngươi ghét. Nhưng biết đâu điều mà các ngươi ghét lại tốt cho các ngươi còn điều mà các ngươi yêu thích lại xấu cho các ngươi; quả thật chỉ Allah mới biết rõ (đâu là tốt và đâu là xấu) còn các ngươi chẳng biết gì.');
INSERT INTO "vn_ruwwad" VALUES(2,217,'2:217','Họ (nhân loại) hỏi Ngươi (hỡi Thiên Sứ) về việc chiến đấu trong tháng cấm kỵ.( Ngươi hãy trả lời họ: “Việc chiến đấu trong tháng cấm kỵ là vi phạm nghiêm trọng, nhưng việc ngăn cản con đường chính đạo của Allah, việc phủ nhận Ngài, việc cấm mọi người vào Masjid Haram cũng như việc trục xuất dân cư của (Makkah) ra khỏi (vùng đất của họ) còn nghiêm trọng hơn đối với Allah; và sự quấy nhiễu còn nghiêm trọng hơn cả sự giết chóc. Mặc dù vậy, những người đa thần vẫn không ngừng chiến đấu với các ngươi cho đến khi nào họ kéo các ngươi rời khỏi tôn giáo của các ngươi bằng mọi cách có thể. Ai trong các ngươi bỏ đạo và chết trong tình trạng vô đức tin thì mọi việc hành thiện của y bị xóa sạch ở đời này lẫn Đời Sau, họ sẽ là những người bạn của Hỏa Ngục, và họ sẽ ở trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(2,218,'2:218','Những người có đức tin, những người di cư và anh dũng chiến đấu vì chính nghĩa của Allah đích thực là những người đang kỳ vọng nơi lòng thương xót của Allah. Quả thật Allah là Đấng Tha Thứ, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(2,219,'2:219','Họ (những người bạn đạo của Ngươi) hỏi Ngươi (hỡi Thiên Sứ Muhammad) về rượu và cờ bạc. Ngươi hãy trả lời họ: “Trong hai thứ vừa có tội lớn vừa có lợi ích cho con người nhưng tội của chúng lớn hơn lợi ích mà chúng mang lại.” Và họ hỏi Ngươi “nên bố thí loại tài sản nào”. Ngươi hãy trả lời họ: “(Các ngươi hãy bố thí) phần tài sản còn lại (sau khi các ngươi đã sử dụng cho nhu cầu cần thiết).”( Như thế đó Allah đã trình bày rõ cho các ngươi các lời mặc khải của Ngài mong rằng các ngươi biết suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(2,220,'2:220','(Để mang lại lợi ích) ở đời này và ở Đời Sau. Họ hỏi Ngươi về trẻ mồ côi.( Ngươi hãy trả lời họ: “Cải thiện (đời sống) cho chúng là điều tốt nhất, nhưng nếu các ngươi hùng hạp (tài sản) với chúng thì chúng vốn là anh em (đồng đạo) với các ngươi, Allah biết rõ ai là kẻ phá hoại và ai là người muốn cải thiện. Nếu muốn, Allah thừa sức gây khó khăn cho các ngươi, bởi Allah là Đấng Toàn Năng, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(2,221,'2:221','Các ngươi (những người có đức tin) chớ đừng kết hôn với những phụ nữ thờ đa thần cho đến khi nào họ đã có đức tin, bởi lẽ một người nữ nô lệ có đức tin tốt hơn một người nữ thờ đa thần ngay cả khi nữ ta có hấp dẫn và lôi cuốn các ngươi đi chăng nữa. Và các ngươi chớ đừng gả (con gái của các ngươi) cho những người đàn ông thờ đa thần cho đến khi nào họ đã có đức tin, bởi lẽ một người nam nô lệ có đức tin tốt hơn một người nam thờ đa thần ngay cả khi y có hấp dẫn và lôi cuốn các ngươi đi chăng nữa. Họ (những kẻ thờ đa thần) kêu gọi các ngươi đến với Hỏa Ngục trong khi Allah mời gọi các ngươi đến với Thiên Đàng và sự tha thứ theo phép của Ngài. Allah đã trình bày rõ ràng các lời mặc khải của Ngài cho nhân loại mong rằng họ biết lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(2,222,'2:222','Họ (các bạn đạo của Ngươi) hỏi Ngươi (hỡi Thiên Sứ Muhammad) về kinh nguyệt của phụ nữ. Ngươi hãy trả lời họ: “Nó gây hại (cho các ngươi khi quan hệ), cho nên các ngươi đừng đến gần vợ của các ngươi trong suốt thời gian kinh nguyệt cho đến khi nào họ đã sạch sẽ trở lại (dứt kinh). Khi nào họ đã (tắm Junub) sạch sẽ thì các ngươi hãy đến với họ như Allah đã cho phép các ngươi. Quả thật, Allah rất yêu thương những người biết ăn năn sám hối và những người giữ mình sạch sẽ.');
INSERT INTO "vn_ruwwad" VALUES(2,223,'2:223','Những người phụ nữ (vợ) của các ngươi là mảnh đất trồng của các ngươi. Cho nên, các ngươi hãy đến với mảnh đất trồng của các ngươi theo cách mà các ngươi thích; và các ngươi hãy (làm thiện tốt) cho bản thân mình. Các ngươi hãy kính sợ Allah và hãy biết rằng các ngươi sẽ phải trở về trình diện Ngài và Ngươi (Thiên Sứ Muhammad) hãy báo tin vui cho những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(2,224,'2:224','Các ngươi chớ đừng lấy Allah ra thề thốt để làm bức màn ngăn cản các ngươi hành thiện, làm điều ngoan đạo và hòa giải giữa mọi người, bởi Allah là Đấng Hằng Nghe, Đấng Hiểu Biết.');
INSERT INTO "vn_ruwwad" VALUES(2,225,'2:225','Allah không bắt tội việc các ngươi thề thốt như một thói quen trên đầu môi, nhưng Ngài sẽ bắt tội khi các ngươi (thề thốt) có chủ định trong tim của các ngươi và Allah là Đấng Tha Thứ, Đấng Chịu Đựng.');
INSERT INTO "vn_ruwwad" VALUES(2,226,'2:226','Những ai thề thốt không giao hợp với phụ nữ (vợ) của họ nữa thì để cho y bốn tháng suy nghĩ. Nếu như y giao hợp lại (với vợ trong khoảng thời gian đó) thì chắc chắn Allah là Đấng Tha Thứ, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(2,227,'2:227','Còn nếu họ kiên quyết li dị (vợ) thì chắc chắn Allah là Đấng Hằng Nghe, Đấng Hiểu Biết.');
INSERT INTO "vn_ruwwad" VALUES(2,228,'2:228','Những người vợ li dị (vì quyền lợi của bản thân) phải ở vậy trong ba kỳ kinh (ba tháng) và họ không được phép giấu giếm thứ mà Allah đã tạo hóa trong bụng của họ (bào thai) nếu họ có đức tin nơi Allah và Đời Sau. Và chồng của họ được quyền ưu tiên tái hợp với họ nếu cả hai muốn cải thiện cuộc sống (vợ chồng). Phụ nữ được hưởng quyền lợi giống như trách nhiệm (của họ đối với chồng) theo lẽ truyền thống (của từng nơi), tuy nhiên nam giới cao hơn phụ nữ một bậc (về trách nhiệm gánh vác gia đình). Quả thật, Allah là Đấng Toàn Năng, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(2,229,'2:229','Việc li dị( chỉ được phép hai lần. (Sau mỗi lần li dị), hoặc là người chồng giữ vợ lại một cách tử tế hoặc là trả tự do cho vợ một cách tốt đẹp. Và các ngươi (hỡi những người chồng) không được phép lấy lại bất cứ gì từ tiền cưới đã trao tặng vợ, ngoại trừ trường hợp cả hai sợ bản thân không duy trì được giới luật của Allah(13) thì cả hai không mắc tội khi (người vợ) dùng tiền cưới chuộc sự tự do cho bản thân. Đó là những giới luật của Allah, các ngươi chớ đừng vi phạm. Ai vi phạm các giới luật của Allah thì họ chính là những kẻ sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(2,230,'2:230','Nếu (người chồng) li dị vợ (lần thứ ba) thì người vợ không còn là vợ hợp pháp của y nữa cho đến khi cô vợ kết hôn với người chồng khác.( Sau này (người chồng mới) lại li dị cô ta thì cô ta và người chồng cũ không bị tội nếu cả hai muốn quay lại với nhau, nhất là khi cả hai tin rằng sẽ duy trì được giới luật của Allah. Đó là những giới luật của Allah, Ngài trình bày rõ ràng cho những người hiểu biết.');
INSERT INTO "vn_ruwwad" VALUES(2,231,'2:231','Sau khi các ngươi li dị vợ và thời hạn ở vậy của họ sắp kết thúc, (lúc đó,) hoặc là các ngươi chung sống trở lại tử tế với họ hoặc là các ngươi trả tự do cho họ một cách tốt đẹp; các ngươi chớ đừng cố giữ họ lại với ý định gây khó khăn cho họ, ai làm thế là đã tự bất công với chính bản thân mình và các ngươi chớ đừng lấy các lời mặc khải của Allah ra để bỡn cợt, các ngươi hãy nhớ về ân huệ của Allah đã ban cho các ngươi và những gì được thiên khải trong Kinh Sách (Qur’an) và điều khôn ngoan (Sunnah) mà Ngài dùng khuyên bảo các ngươi. Các ngươi hãy kính sợ Allah và hãy biết rằng Allah luôn biết hết mọi việc.');
INSERT INTO "vn_ruwwad" VALUES(2,232,'2:232','Khi các ngươi li dị vợ (ít hơn ba lần) và thời gian ở vậy của vợ đã chấm dứt thì các ngươi (người đại diện bên vợ) không được cấm người chồng của họ tái hôn với họ nhất là khi cả hai tự nguyện trở lại chung sống với nhau đàng hoàng. Đó là lời khuyên dành cho những ai trong các ngươi tin vào Allah và Đời Sau, điều đó tốt lành và trong sạch hơn cho các ngươi bởi Allah luôn biết rõ còn các ngươi thì không biết gì.');
INSERT INTO "vn_ruwwad" VALUES(2,233,'2:233','Những bà mẹ (sau li dị) sẽ cho con bú trong hai năm tròn đối với (người cha nào) muốn cho con bú đủ kỳ hạn. Với khoảng ấy thời gian người cha phải có trách nhiệm cấp dưỡng tiền bạc và quần áo cho người mẹ một cách tử tế phù hợp (với lẽ truyền thống). Mỗi linh hồn không bị ép làm điều gì đó quá khả năng, không người mẹ nào bị làm khổ bởi đứa con của mình và cũng không người cha nào bị làm khổ bởi đứa con của y, và người thừa kế cũng phải có trách nhiệm như thế. Trường hợp hai vợ chồng đồng thuận với nhau việc thôi cho con bú trước kỳ hạn hai năm thì cả hai không mắc tội. Nếu các ngươi muốn tìm vú nuôi khác cho con bú sữa thì các ngươi cũng không bị mắc tội một khi các ngươi trả đủ tiền thù lao cho người vú nuôi đó một cách phù hợp. Các ngươi hãy kính sợ Allah và hãy biết rằng Allah thông toàn mọi việc các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(2,234,'2:234','Đối với những người chết bỏ lại vợ, bắt buộc các bà vợ phải ở vậy bốn tháng và mười ngày (vì quyền lợi của bản thân).( Sau khi hết thời gian ở vậy, các ngươi (những người bảo hộ các góa phụ đó) không bị mắc tội về các hành động hợp lẽ và phải đạo mà họ làm cho bản thân mình. Quả thật, Allah thông toàn mọi điều các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(2,235,'2:235','Các ngươi không mang tội khi nói lời ám chỉ( về việc đính hôn với các phụ nữ (đang trong thời gian ở vậy) hoặc khi các ngươi giấu kín ý định đó trong lòng. Allah biết các ngươi sẽ nghĩ đến họ nhưng các ngươi không được phép âm thầm hứa hẹn với họ ngoại trừ những câu nói thăm dò với lời lẽ lịch thiệp và đúng mực. Các ngươi không được tiến hành hôn ước với họ cho đến khi thời gian ở vậy của họ hoàn toàn kết thúc. Các ngươi hãy biết rằng Allah luôn hiểu rõ mọi tâm tư trong lòng của các ngươi, cho nên hãy thận trọng đối với Ngài; và các ngươi hãy biết rằng Allah là Đấng Tha Thứ, Đấng Chịu Đựng.');
INSERT INTO "vn_ruwwad" VALUES(2,236,'2:236','Các ngươi không có tội khi li dị vợ trước khi động phòng hoặc chưa định phần tiền cưới bắt buộc cho họ. Tuy nhiên các ngươi phải tặng họ quà cưới thích hợp, người giàu tùy theo phương tiện của người giàu và người nghèo tùy theo phương tiện của người nghèo. Đây là trách nhiệm của những người làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(2,237,'2:237','Và nếu như các ngươi li dị vợ trước khi động phòng và đã định rõ ràng phần tiền cưới thì bắt buộc các ngươi phải trả cho họ một nửa phần tiền cưới đã thống nhất. Trừ phi họ tự nguyện không nhận hoặc người giám hộ rộng lượng khước từ. Tuy nhiên, tấm lòng vị tha gần với lòng kính sợ (Allah) hơn. Các ngươi chớ đừng quên việc (Allah) đã ưu ái người này hơn người kia. Quả thật, Allah luôn thấy rõ mọi hành động của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(2,238,'2:238','Các ngươi hãy giữ gìn và duy trì các lễ nguyện Salah, nhất là cuộc lễ chính giữa (Salah ‘Asr), và các ngươi hãy đứng dâng lễ vì Allah một cách thành tâm cùng với tinh thần đầy sùng kính.');
INSERT INTO "vn_ruwwad" VALUES(2,239,'2:239','Một khi các ngươi sợ (bị tấn công hoặc điều tương tự) thì các ngươi hãy hành lễ lúc đi bộ (hành quân) hoặc trên lưng vật cưỡi (phương tiện đi lại). Đến khi các ngươi đã an toàn thì các ngươi h ãy tụng niệm Allah giống như cách mà Ngài đã dạy các ngươi lúc các ngươi chưa biết.');
INSERT INTO "vn_ruwwad" VALUES(2,240,'2:240','Đối với những người chết bỏ lại vợ, bắt buộc các ngươi phải để lại di chúc cấp dưỡng (nhà ở và tiền bạc) cho họ một năm và không được đuổi họ ra khỏi nhà. Trường hợp họ tự nguyện rời khỏi nhà (trước thời hạn) thì các ngươi không bị tội về các hành động mà họ làm cho bản thân mình một cách hợp lẽ và phải đạo. Quả thật, Allah là Đấng Toàn Năng, Đấng Sáng Suốt.(');
INSERT INTO "vn_ruwwad" VALUES(2,241,'2:241','Những phụ nữ sau li dị vẫn còn quyền được hưởng cấp dưỡng phù hợp (theo lẽ truyền thống), đây là trách nhiệm của những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(2,242,'2:242','Như thế đó, Allah đã trình bày rõ ràng cho các ngươi thấy các lời mặc khải của Ngài, mong rằng các ngươi hiểu.');
INSERT INTO "vn_ruwwad" VALUES(2,243,'2:243','Phải chăng Ngươi (hỡi Thiên Sứ Muhammad) chưa biết đến việc có hàng ngàn người (dân Israel) đã bỏ nhà cửa ra đi vì sợ chết (bởi dịch bệnh hoặc điều gì đó). Lúc đó, Allah phán bảo họ: Các ngươi hãy chết đi (và họ liền chết hết), sau đó Ngài phục sinh tất cả họ sống trở lại. Quả thật, Allah luôn nhân từ với loài người nhưng đa số nhân loại không biết ơn (Ngài).');
INSERT INTO "vn_ruwwad" VALUES(2,244,'2:244','Các ngươi (hỡi những người có đức tin) hãy chiến đấu cho chính nghĩa của Allah và hãy biết rằng Allah là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(2,245,'2:245','Ai cho Allah mượn một món mượn tốt đẹp (bố thí vì Allah) thì phần lãi của y sẽ được nhân lên rất nhiều lần. Quả thật, Allah muốn thu hẹp hoặc nới rộng bổng lộc là tùy ý Ngài và rồi các ngươi phải trở về trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(2,246,'2:246','Lẽ nào Ngươi (hỡi Nabi) chưa biết thông tin về nhóm thượng lưu trong đám dân Israel thời sau Musa đã đề xướng với Nabi của họ: Người hãy đề bạc cho chúng tôi một vị vua đi, chúng tôi quyết sẽ sát cánh với vị vua đó chiến đấu cho chính nghĩa của Allah. (Nabi) đáp: Ta e là khi được (Allah) hạ lệnh chiến đấu thì các ngươi lại không tuân theo. Họ nói: Điều gì cấm chúng tôi đi chiến đấu vì chính nghĩa của Allah cơ chứ, trong khi kẻ thù đã trục xuất chúng tôi ra khỏi quê hương của mình và (giam giữ) con cháu của chúng tôi. Vậy mà khi họ được lệnh phải chiến đấu thì hầu hết bọn họ đã ngoảnh mặt làm ngơ ngoại trừ một số ít trong bọn họ. Quả thật, Allah biết rõ về đám người lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(2,247,'2:247','Và Nabi của họ bảo: Allah đã đề cử Talut lên làm vua của các ngươi. Họ (không phục) bảo: Làm sao Y xứng đáng làm vua của chúng tôi cho được trong khi chúng tôi xứng đáng hơn y kia mà, vả lại Y vốn không có nhiều tài sản. (Nabi) nói: Allah đã lựa chọn Y cho các ngươi và Ngài ban cho Y kiến thức và sức mạnh, Allah muốn ban quyền vua chúa cho ai tùy ý Ngài, bởi Allah là Đấng Bao La, Đấng Hiểu Biết.');
INSERT INTO "vn_ruwwad" VALUES(2,248,'2:248','Và Nabi của họ bảo: Dấu hiệu chứng minh chắc chắn cho vương quyền thuộc về Y là Y sẽ mang đến cho các ngươi xem một cái rương, trong đó mang đến sự bình an và những thứ còn sót lại từ hai dòng dõi của Musa và Harun được các vị Thiên Thần hộ tống mang đến. Chắc chắn điều đó là bằng chứng rõ ràng cho các ngươi nếu các ngươi thật sự là những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(2,249,'2:249','Khi Talut xuất binh, Y bảo quân lính: Allah thử thách các ngươi về một con sông, ai uống nước của con sông đó thì y không còn là người của Ta (tức không được xuất binh chiến đấu) còn ai không uống nước của con sông đó hoặc chỉ uống một ngụm nước bằng tay cho đỡ khát thì y người của Ta (tức được ra chiến trường). Thế nhưng đa số quân lính uống nước của con sông đó ngoại trừ một số ít. Rồi khi Y cùng đoàn quân có đức tin còn lại qua khỏi con sông thì quân lính nói: Ngày nay làm sao chúng ta đủ lực lượng để chiến đấu với đoàn quân của Jalut (Goliath) đây. Lúc này, những người có đức tin nơi việc họ sẽ trình diện Allah (ở Đời Sau) lên tiếng: Đã có biết bao lần một lực lượng nhỏ bé đã đánh bại một lực lượng hùng mạnh khi đã được Allah phù hộ! Quả thật, Allah luôn sát cánh với những người kiên nhẫn.');
INSERT INTO "vn_ruwwad" VALUES(2,250,'2:250','Khi đối mặt với đoàn quân của Jalut thì họ (quân của Talut) cầu nguyện: “Lạy Thượng Đế của bầy tôi, xin Ngài hãy gia tăng sự kiên nhẫn trong lòng bầy tôi, xin Ngài hãy giữ vững đôi chân của bầy tôi và xin hãy phù hộ bầy tôi chiến thắng trước đám người vô đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(2,251,'2:251','Cuối cùng đoàn quân có đức tin đã tiêu diệt được kẻ thù dưới sự phù hộ của Allah. Còn Dawood đã giết chết (bạo chúa) Jalut, rồi Y được Allah ban cho vương quyền và sứ mạng Nabi, ngoài ra Ngài còn dạy Y biết bao kiến thức Ngài muốn. Nếu như Allah không dùng người này để ngăn chặn (hành động phá hoại) của người kia thì trái đất sẽ loạn và rối tung, tuy nhiên Allah nhân từ với vũ trụ và vạn vật.');
INSERT INTO "vn_ruwwad" VALUES(2,252,'2:252','Đó là những lời phán của Allah, TA đọc cho Ngươi nghe bằng sự thật (hỡi Thiên Sứ Muhammad) và quả thật Ngươi đích thực là một trong các vị Thiên Sứ (của TA).');
INSERT INTO "vn_ruwwad" VALUES(2,253,'2:253','Đó là những vị Thiên Sứ; TA (Allah) đã ưu đãi một số vị này hơn một số vị kia. Trong số họ, có vị được Allah nói chuyện trực tiếp (như Thiên Sứ Musa), có vị được nâng trội hơn vài cấp bậc (như Thiên Sứ Muhammad), và TA đã ban cho Ysa con trai của Mar-yam một loạt phép màu rõ ràng và để Al-Ruh Al-Qudus (Jibril) làm trợ thủ cho Y. Nếu Allah muốn thì các thế hệ sống sau họ (các vị Thiên Sứ và Nabi) đã không giết chóc lẫn nhau sau khi các bằng chứng rõ ràng đã đến với họ, nhưng họ đã tranh cãi và bất đồng, cho nên, trong số họ có người tin và có người phủ nhận. Và nếu Allah muốn thì họ đã không giết chóc lẫn nhau, tuy nhiên, Allah làm những gì Ngài muốn.');
INSERT INTO "vn_ruwwad" VALUES(2,254,'2:254','Hỡi những người có đức tin, các ngươi hãy chi dùng từ những bổng lộc mà TA đã ban cấp cho các ngươi (vào con đường chính nghĩa của Allah) trước khi xảy ra Ngày mà sẽ không có việc mua bán đổi chác, sẽ không có tình bằng hữu (để bao che cho nhau) và cũng sẽ không có sự can thiệp nào (để biện hộ và giúp đỡ). Và những kẻ vô đức tin chính là những kẻ sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(2,255,'2:255','Allah (là Thượng Đế), không có Thượng Đế (đích thực) nào ngoài Ngài, Đấng Hằng Sống, Đấng Bất Diệt, Ngài không ngủ và cũng không buồn ngủ. Tất cả vạn vật trong các tầng trời và tất cả vạn vật trong trái đất đều thuộc về Ngài. Không ai có quyền can thiệp (biện minh, cầu xin ân xá cho ai) trước Ngài trừ phi Ngài cho phép. Ngài biết hết mọi điều xảy ra phía trước họ và đằng sau họ, không một ai đủ năng lực biết được kiến thức của Ngài ngoại trừ điều nào Ngài muốn cho y biết. Kursi( (Bệ gác chân) của Ngài bao trùm hết cả các tầng trời và trái đất, và việc quản lý trời đất không làm Ngài mỏi mệt bởi Ngài là Đấng Tối Thượng, Vĩ Đại.');
INSERT INTO "vn_ruwwad" VALUES(2,256,'2:256','Không có sự cưỡng bức (đức tin) trong tôn giáo. Quả thật, chân lý, lẽ đúng luôn khác biệt với điều ngụy tạo và sai trái. Cho nên, ai phủ nhận Taghut (mọi thứ được thờ phượng ngoài Allah) và có đức tin nơi Allah thì y đã nắm chắc sợi dây cứu rỗi không bao giờ đứt, bởi Allah là Đấng Hằng Nghe, Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(2,257,'2:257','Allah là Đấng Bảo Hộ của những người có đức tin, Ngài đưa họ ra khỏi nơi tăm tối đến với ánh sáng. Riêng những kẻ vô đức tin, đấng bảo hộ của họ là Taghut; chúng đưa họ từ nơi ánh sáng đến chỗ tối tăm. Những kẻ đó sẽ làm bạn với Hỏa Ngục, họ sẽ phải sống trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(2,258,'2:258','Ngươi (hỡi Thiên Sứ Muhammad) có thấy kẻ đã tranh luận với Ibrahim về Thượng Đế của Y, kẻ mà hắn đã được Allah ban cho vương quyền (vua Nimrud)? Khi Ibrahim nói: “Thượng Đế của tôi là Đấng làm cho sống và làm cho chết.” (Nimrud) bảo: “Ta cũng có thể làm cho sống và làm cho chết.”( Ibrahim nói: “Quả thật, Allah là Đấng làm cho mặt trời mọc lên ở hướng đông, vậy bệ hạ hãy làm cho nó mọc lên ở hướng tây xem nào.” Thế là kẻ vô đức tin đã bối rối (vì đuối lý). Quả thật, Allah không hướng dẫn đám người sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(2,259,'2:259','Hoặc (Ngươi có thấy ai) giống như kẻ (‘Uzair)( đã đi ngang qua một ngôi làng tan hoang và điêu tàn, y bảo: “Làm thế nào Allah có thể dựng sống lại ngôi làng này sau khi nó đã chết?” Thế là Allah đã làm cho y chết trong thời gian một trăm năm rồi sau đó dựng y sống lại. Ngài hỏi Y: “Ngươi đã ngủ bao lâu rồi?” Y đáp: “Thưa, bề tôi chỉ ngủ một ngày hay một buổi gì đó.” Ngài bảo Y: “Không, ngươi đã ngủ cả một trăm năm rồi đấy. Ngươi hãy nhìn vào thức ăn và thức uống của mình đi, tất cả vẫn còn nguyên vẹn không bị hư với ngần ấy thời gian. Ngươi hãy nhìn vào con lừa của ngươi (nó đã chết thành đóng xương khô), TA lấy ngươi làm một dấu lạ cho nhân loại, ngươi hãy nhìn vào những khúc xương xem TA sắp xếp chúng rồi làm cho thịt bao chúng lại như thế nào.” Do đó, sau khi mọi thứ đã được phô bày một cách rõ ràng cho y (Y đã nhận thức được thực tại), y liền thốt lên: “Giờ đây bề tôi đã biết rõ Allah toàn năng trên tất cả mọi thứ.”');
INSERT INTO "vn_ruwwad" VALUES(2,260,'2:260','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại) khi Ibrahim khẩn cầu (Allah): “Lạy Thượng Đế của bề tôi, xin Ngài cho bề tôi thấy cách mà Ngài làm sống lại cái chết!” (Allah) phán bảo (Ibrahim): “Phải chăng Ngươi không tin?” (Ibrahim) thưa: “Bẩm không phải như vậy, bề tôi chỉ muốn củng cố tấm lòng của mình mà thôi.” (Allah) phán bảo (Ibrahim): “Vậy thì Ngươi hãy bắt bốn con chim và băm chúng ra thành từng mảnh rồi trộn lại với nhau, sau đó Ngươi hãy để mỗi phần thịt của chúng trên mỗi ngọn núi. Xong, Ngươi hãy gọi chúng, chúng sẽ (trở lại thành những con chim như ban đầu) bay nhanh đến Ngươi. Và Ngươi hãy biết rằng Allah là Đấng Toàn Năng, Sáng Suốt.”');
INSERT INTO "vn_ruwwad" VALUES(2,261,'2:261','Hình ảnh của những người chi dùng tài sản của họ cho con đường chính nghĩa của Allah giống như hình ảnh của một hạt giống trổ ra bảy nhánh bông, mỗi nhánh bông trổ ra một trăm hạt. Và Allah sẽ nhân lên thêm nữa cho những ai Ngài muốn bởi vì Allah là Đấng Quảng Đại, Bao La, Hằng Biết hết mọi việc.');
INSERT INTO "vn_ruwwad" VALUES(2,262,'2:262','Những người chi dùng tài sản của họ cho con đường chính nghĩa của Allah rồi không kèm theo bất cứ lời nhắc khéo nào về sự rộng rãi của mình cũng như không làm tổn thương (đến danh dự của người được họ bố thí và trợ giúp) thì họ sẽ có được phần thưởng của họ ở nơi Thượng Đế của họ và họ sẽ không sợ hãi cũng sẽ không buồn phiền, lo âu.');
INSERT INTO "vn_ruwwad" VALUES(2,263,'2:263','Lời nói tử tế và lòng bao dung tốt hơn việc bố thí có kèm theo (lời lẽ hay hành động) làm tổn thương (danh dự của người được bố thí và trợ giúp). Quả thật, Allah là Đấng Giàu Có, Chịu Đựng.');
INSERT INTO "vn_ruwwad" VALUES(2,264,'2:264','Hỡi những người có đức tin, các ngươi chớ hủy hoại sự bố thí của các ngươi bởi những lời nhắc khéo về tấm lòng của các ngươi hay bởi việc làm tổn thương (danh dự của người được các ngươi bố thí và giúp đỡ). (Nếu các ngươi làm thế thì hình ảnh của các ngươi) giống như kẻ chi dùng tài sản (cho con đường chính nghĩa của Allah) chỉ muốn phô trương trước thiên hạ chứ không hề có đức tin nơi Allah và Đời Sau. Hình ảnh của kẻ này giống như hình ảnh một tảng đá to bằng phẳng trơn láng có lớp bụi phủ đầy bên trên (y cứ tưởng lớp bụi đó là mảnh đất phì nhiêu, y hồ hởi gieo hạt) nhưng rồi một cơn mưa lớn trút xuống quét sạch (lớp bụi đó đi) bỏ lại tảng đá nằm một mình trơ trọi. Quả thật, Allah không hướng dẫn đám người vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(2,265,'2:265','Và những người chi dùng tài sản của mình chỉ vì muốn làm hài lòng Allah cũng như chỉ vì họ tin một cách kiên định (nơi lời hứa ban thưởng của Ngài), hình ảnh của họ giống như hình ảnh của một ngôi vườn trên một gò đất màu mỡ, phì nhiêu; khi gặp được mưa lớn, ngôi vườn tăng vụ mùa gấp bội; nhưng nếu như không có cơn mưa lớn nào thì cơn mưa rươi cũng đủ làm cho ngôi vườn phát triển tươi tốt. Quả thật, Allah luôn quan sát những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(2,266,'2:266','Lẽ nào ai đó trong các ngươi lại khao khát muốn (tình trạng của y giống như một người) rằng y có một ngôi vườn chà là và nho với trái quả xum xuê, bên dưới có những dòng sông chảy, nhưng y đã đến tuổi già và con cái của y thì vẫn còn thơ dại, rồi một cơn gió mang theo cái nóng như lửa bất chợt ào đến thiêu rụi toàn bộ ngôi vườn?! Allah trình bày cho các ngươi những lời phán của Ngài đúng như thế, mong các ngươi biết suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(2,267,'2:267','Hỡi những người có đức tin, các ngươi hãy chi dùng (cho con đường chính nghĩa của Allah) từ những bổng lộc tốt lành mà các ngươi kiếm được cũng như từ những bổng lộc mà TA đã xuất ra cho các ngươi từ đất đai. Các ngươi chớ tìm lấy những món vật xấu để chi dùng (cho con đường chính nghĩa của Allah), những món vật mà ngay cả bản thân các ngươi cũng không muốn nhận ngoại trừ các ngươi nhắm mắt làm ngơ. Các ngươi hãy biết rằng Allah là Đấng Giàu Có, Đáng Được Ca Tụng.');
INSERT INTO "vn_ruwwad" VALUES(2,268,'2:268','Shaytan làm cho các ngươi lo sợ sự nghèo khó và bảo các ngươi làm điều tội lỗi, ngược lại, Allah hứa tha thứ cho các ngươi và làm cho các ngươi phát đạt với thiên lộc của Ngài. Quả thật, Allah là Đấng Quảng đại, Bao la và Am tường mọi sự việc.');
INSERT INTO "vn_ruwwad" VALUES(2,269,'2:269','(Allah) ban sự khôn ngoan cho ai Ngài muốn, và ai được ban cho sự khôn ngoan thì quả thật y đã được ban cho biết bao điều tốt đẹp; tuy nhiên, không ai hiểu được điều đó ngoại trừ những người có kiến thức và chịu suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(2,270,'2:270','Những gì các ngươi đã chi dùng (cho con đường chính nghĩa của Allah) hoặc những gì các ngươi đã nguyện thực hiện, Allah chắc chắn đều biết rõ. Và những kẻ làm điều sai quấy chắc chắn sẽ không được ai giúp đỡ.');
INSERT INTO "vn_ruwwad" VALUES(2,271,'2:271','Nếu các ngươi công khai những món vật bố thí thì điều đó cũng tốt (bởi vì các ngươi đã công khai điều tốt); nhưng nếu các ngươi giấu kín và âm thầm trao các món vật bố thí đó đến tận tay những người nghèo thì điều đó tốt hơn cho các ngươi, nó sẽ xóa đi khỏi các ngươi tội lỗi cho các ngươi. Và Allah là Đấng Thông Toàn những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(2,272,'2:272','(Hỡi Thiên Sứ Muhammad), việc hướng dẫn họ (đến với chính đạo) không thuộc quyền của Ngươi mà chính Allah mới có quyền hướng dẫn ai Ngài muốn. Và những gì các ngươi chi dùng cho điều thiện tốt thì thật ra đó là những thứ các ngươi chi dùng cho chính bản thân các ngươi; và những gì các ngươi chi dùng là chỉ nhằm làm hài lòng Allah; và những gì các ngươi chi dùng cho điều thiện tốt thì nó sẽ được (Allah) trả lại cho các ngươi và chắc chắn các ngươi sẽ không bị đối xử thiệt thòi bất cứ thứ gì.');
INSERT INTO "vn_ruwwad" VALUES(2,273,'2:273','(Của bố thí là dành) cho những người nghèo, nhất là những người vì con đường chính nghĩa của Allah đã bị hạn chế việc đi lại trên mặt đất (để tìm kiếm thiên lộc của Ngài). Qua tư cách và lòng tự trọng của họ, người không biết cứ tưởng rằng họ giàu có; nhưng Ngươi (hỡi Thiên Sứ Muhammad) có thể nhận biết họ qua những nét biểu hiện đặc biệt trên gương mặt họ; họ không xin xỏ thiên hạ một cách trơ trẽn và thiếu lòng tự trọng. Và bất cứ món tốt nào mà các ngươi chi dùng (cho con đường chính nghĩa của Allah), Allah chắc chắn biết rõ nó.');
INSERT INTO "vn_ruwwad" VALUES(2,274,'2:274','Những người chi dùng tài sản của họ (cho con đường chính nghĩa của Allah) cả đêm lẫn ngày dù kín đáo hay công khai đều sẽ nhận được phần thưởng của họ nơi Thượng Đế của họ (nếu họ thành tâm vì Ngài), và họ sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "vn_ruwwad" VALUES(2,275,'2:275','Những người ăn tiền lời cho vay (vào Ngày Sau) sẽ đứng không vững giống như một kẻ bị Shaytan nhập vào người và làm cho điên cuồng. Họ bị như thế là vì họ đã nói: “Kinh doanh mua bán thật ra cũng giống như hình thức cho vay lấy lãi mà thôi”. Tuy nhiên, Allah cho phép kinh doanh mua bán và cấm hình thức cho vay lấy lãi. Do đó, ai đã nhận được lệnh cảnh báo từ Thượng Đế của y và chấm dứt (việc cho vay lấy lãi) thì y được hưởng những gì đã qua (từ việc cho vay lấy lãi trước đó) và vụ việc của y sẽ do Allah quyết định. Ngược lại, người nào ngoan cố tái phạm thì sẽ làm bạn với Hỏa Ngục, họ sẽ sống trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(2,276,'2:276','Allah không ban phúc cho hình thức vay lãi nhưng Ngài làm phát đạt các hình thức Sadaqah (bố thí, trợ giúp). Quả thật, Allah không yêu thương những kẻ vô đức tin và tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(2,277,'2:277','Quả thật, những người có đức tin và hành thiện, chu đáo duy trì lễ nguyện Salah và xuất Zakah thì họ sẽ nhận được ân phước của họ ở nơi Thượng Đế của họ, họ sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "vn_ruwwad" VALUES(2,278,'2:278','Hỡi những người có đức tin, các ngươi hãy sợ Allah mà bỏ đi phần còn lại của tiền lời cho vay, nếu các ngươi thật sự có đức tin nơi Ngài.');
INSERT INTO "vn_ruwwad" VALUES(2,279,'2:279','Nhưng nếu các ngươi không phục tùng theo mệnh lệnh thì xem như các ngươi đã khai chiến với Allah và Thiên Sứ của Ngài. Tuy nhiên, nếu các ngươi biết ăn năn hối cải thì các ngươi được thu hồi tiền vốn của mình, đây là cách các ngươi không gây bất công cho ai và cũng không bị thiệt thòi.');
INSERT INTO "vn_ruwwad" VALUES(2,280,'2:280','(Trường hợp) người thiếu nợ gặp khó khăn không thể hoàn nợ (lúc đến kỳ hạn) thì hãy gia hạn cho y đến lúc y cảm thấy dễ dàng hơn (cho việc trả nợ); tuy nhiên, nếu các ngươi bố thí khoản nợ đó thì điều đó tốt hơn cho các ngươi nếu các ngươi biết.');
INSERT INTO "vn_ruwwad" VALUES(2,281,'2:281','Các ngươi hãy sợ Ngày mà các ngươi được đưa trở về trình diện Allah, lúc đó mỗi linh hồn sẽ được đền bù xứng đáng về mọi điều mà nó đã từng làm (trên thế gian), và chắc chắn không một ai bị đối xử bất công.');
INSERT INTO "vn_ruwwad" VALUES(2,282,'2:282','Hỡi những người có đức tin, khi các ngươi mượn tiền bạc của nhau đến một thời hạn ấn định thì các ngươi hãy viết khoản nợ đó ra (thành một văn bản rõ ràng), các ngươi hãy tìm một người ghi chép viết lại (đầy đủ các điều khoản thỏa thuận) giữa các ngươi với nhau một cách công bằng. Bản thân người ghi chép không được từ chối viết giống như những gì Allah đã dạy y. Cho nên, người ghi chép hãy viết, còn người mắc nợ hãy đọc ra khoản nợ (một cách chính xác), y hãy kính sợ Allah, Thượng Đế của y, và không được bớt đi bất cứ thứ gì từ khoản nợ đó. Nếu người mắc nợ là người khờ khạo hoặc nhỏ tuổi (hay già yếu) hoặc không có khả năng (đọc ra khoản nợ) thì người giám hộ của y sẽ là người đại diện đọc ra khoản nợ theo đúng sự thật và công bằng. Các ngươi hãy tìm hai người đàn ông trong các ngươi đứng ra làm nhân chứng cho giấy nợ đó, nếu không có đủ hai người đàn ông thì các ngươi tìm một người đàn ông và hai người phụ nữ mà các ngươi hài lòng để họ làm nhân chứng, (mục đích) để người phụ nữ này nhắc người phụ nữ kia (nếu một trong hai có người sai sót hay nhầm lẫn). Những người được mời làm nhân chứng không được từ chối. Các ngươi chớ ái ngại việc phải viết khoản nợ đó ra (thành văn bản) dù nó ít hay nhiều sau khi (đôi bên) đã thống nhất đến kỳ hạn. Đối với Allah (việc viết giấy nợ) là lẽ công bằng và chính xác nhất khi được đưa ra làm chứng và nhằm để ngăn chặn việc tranh chấp sau này. Trừ trường hợp các ngươi trao đổi mua bán tại chỗ (bằng tiền mặt) thì các ngươi không bị tội nếu các ngươi không viết nó ra (thành văn bản) cũng như không gọi người đứng ra làm chứng khi các ngươi thỏa thuận mua bán. Các ngươi không được làm hại đến người viết (giấy nợ) cũng như người làm chứng; nếu các ngươi làm thế thì chắc chắn đó là một hành động xấu xa và tội lỗi của các ngươi. Các ngươi hãy kính sợ Allah, Allah dạy các ngươi (những điều tốt đẹp), và Allah luôn biết rõ tất cả mọi sự việc.');
INSERT INTO "vn_ruwwad" VALUES(2,283,'2:283','Trường hợp các ngươi đi xa nhà, không tìm được người viết giùm (giấy nợ) thì chỉ cần thế chấp cho chủ nợ một tín vật. Tuy nhiên, nếu các ngươi có lòng tin lẫn nhau thì người được giao tín vật phải thực hiện (việc giao lại tín vật cho chủ nhân của nó khi được hoàn nợ) và y hãy kính sợ Allah, Thượng Đế của y. Và các ngươi không được che giấu bằng chứng; ai che giấu nó thì quả thật tấm lòng của y mang tội, bởi Allah luôn biết rõ những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(2,284,'2:284','Vạn vật trong các tầng trời và vạn vật trong trái đất đều là của Allah, dù các ngươi bộc lộ hay giấu kín những điều trong lòng của các ngươi thì Allah vẫn xét xử các ngươi về chúng. Tuy nhiên, Ngài sẽ tha thứ cho ai Ngài muốn và sẽ trừng phạt ai Ngài muốn, bởi Allah toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(2,285,'2:285','Thiên Sứ (Muhammad) và những người có đức tin đều tin vào những điều được mặc khải xuống cho Y từ Thượng Đế của Y. Tất cả họ đều có đức tin nơi Allah, nơi các Thiên Thần của Ngài, nơi các Kinh Sách của Ngài, và nơi các vị Thiên Sứ của Ngài. Họ cùng nói câu: “Bầy tôi không phân biệt giữa các vị Thiên Sứ của Ngài, bầy tôi xin nghe và phục tùng mệnh lệnh. Xin Ngài hãy tha thứ tội lỗi cho bầy tôi hỡi Thượng Đế của bầy tôi, và bầy tôi chắc chắn phải quay trở về trình diện Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(2,286,'2:286','Allah không bắt một linh hồn nào gánh vác trách nhiệm quá khả năng của nó. Mỗi linh hồn sẽ hưởng được thành quả của điều tốt mà mình đã làm và sẽ phải gánh hậu quả về tội lỗi đã phạm. (Người có đức tin cần cầu nguyện Allah): “Lạy Thượng Đế của bầy tôi, xin Ngài đừng bắt tội bầy tôi nếu bầy tôi quên hay sai sót; lạy Thượng Đế của bầy tôi, xin Ngài đừng bắt bầy tôi gánh vác nặng nề giống như Ngài đã đặt gánh nặng lên những người trước bầy tôi; lạy Thượng Đế của bầy tôi, xin Ngài đừng bắt bầy tôi gánh lấy những gì vượt quá khả năng của bầy tôi, xin Ngài hãy lượng thứ cho bầy tôi, tha thứ tội lỗi cho bầy tôi và thương xót bầy tôi, Ngài là Đấng Bảo Hộ của bầy tôi, xin Ngài phù hộ bầy tôi giành chiến thắng trước những kẻ vô đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(3,1,'3:1','Alif. La-m. Mi-m.');
INSERT INTO "vn_ruwwad" VALUES(3,2,'3:2','Allah (là Thượng Đế), không có Thượng Đế (đích thực) nào ngoài Ngài, Đấng Hằng Sống, Bất Diệt.');
INSERT INTO "vn_ruwwad" VALUES(3,3,'3:3','Ngài đã ban xuống cho Ngươi (Thiên Sứ Muhammad) Kinh Sách (Qur’an). (Nó) là chân lý đích thực (không có gì phải hoài nghi) dùng để xác định lại những điều được ban xuống trước (Nó). Và Ngài đã ban xuống Kinh Tawrah (Cựu Ước) và Kinh Injil (Tân Ước).');
INSERT INTO "vn_ruwwad" VALUES(3,4,'3:4','(Kinh Tawrah và Kinh Injil) được ban xuống trước làm nguồn chỉ đạo cho nhân loại và (tiếp sau đó) Ngài ban xuống Furqan( (Kinh Qur’an). Quả thật, những kẻ đã phủ nhận các lời mặc khải của Allah sẽ phải gánh lấy sự trừng phạt khủng khiếp. Quả thật, Allah là Đấng Toàn Năng, Đấng toàn quyền trừng phạt (không thể kháng cự).');
INSERT INTO "vn_ruwwad" VALUES(3,5,'3:5','Quả thật không có bất cứ thứ gì ở trên trái đất cũng như ở trên trời có thể ẩn khuất khỏi Allah.');
INSERT INTO "vn_ruwwad" VALUES(3,6,'3:6','Ngài là Đấng đã tạo hình hài cho các ngươi trong các dạ con (của các bà mẹ) như thế nào tùy theo ý muốn của Ngài. Không có Thượng Đế (đích thực) nào khác ngoài Ngài, Đấng Toàn Năng, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(3,7,'3:7','Ngài là Đấng đã ban xuống cho Ngươi (Thiên Sứ Muhammad) Kinh Sách (Qur’an), trong đó, có những câu Muhkam (mang ý cụ thể một cách rõ ràng), chúng là nền tảng của Kinh Sách, còn những câu khác Mushtabihah (mang ý tổng quát cho nhiều vấn đề tương đồng). Cho nên, những kẻ mà trong trái tim của họ có tư tưởng lệch lạc thường đi theo những câu mang ý tổng quát cho nhiều vấn đề tương đồng hầu (khơi dậy làn sóng) chia rẽ (cộng đồng) và suy diễn (chúng) theo ý riêng của mình; và chỉ một mình Allah mới biết rõ ý nghĩa thật sự của chúng. Ngược lại, những người thực sự có kiến thức vững chắc thì bảo: “Chúng tôi hoàn toàn tin tưởng vào nó (Qur’an), toàn bộ (Qur’an) đều đến từ Thượng Đế của chúng tôi.” Quả thật chỉ những người có hiểu biết vững chắc mới nắm được lời nhắc nhở (của Qur’an).');
INSERT INTO "vn_ruwwad" VALUES(3,8,'3:8','(Những người thực sự có kiến thức vững chắc thường cầu nguyện): “Lạy Thượng Đế của bầy tôi, xin Ngài đừng làm trái tim của bầy tôi lệch lạc sau khi Ngài đã hướng dẫn bầy tôi, xin Ngài hãy ban cho bầy tôi hồng phúc từ nơi Ngài. Quả thật, Ngài là Đấng Hằng Ban Phát.”');
INSERT INTO "vn_ruwwad" VALUES(3,9,'3:9','“Lạy Thượng Đế của bầy tôi, chắc chắn Ngài sẽ triệu tập nhân loại vào một Ngày (mà Ngài đã định), điều đó không có gì phải hoài nghi, bởi quả thật, Allah không bao giờ làm trái với điều đã hứa.”');
INSERT INTO "vn_ruwwad" VALUES(3,10,'3:10','Quả thật, con cái và tài sản của những kẻ vô đức tin không giúp ích được gì cho họ trước (sự trừng phạt của) Allah và họ chính là củi đốt của Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(3,11,'3:11','(Họ) giống như đồng bọn của Pha-ra-ông và những kẻ trước họ. Tất cả đều đã phủ nhận các lời mặc khải của TA nên họ đã bị Allah bắt phạt bởi tội lỗi họ đã gây ra. Quả thật, Allah rất nghiêm khắc trong việc trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(3,12,'3:12','Ngươi (hỡi Thiên Sứ Muhammad) hãy nói với những kẻ vô đức tin: “Rồi đây các người sẽ bị đánh bại (bởi những người có đức tin) và các người sẽ được tập trung vào Hỏa Ngục, một nơi ở tồi tệ (dành cho các người).”');
INSERT INTO "vn_ruwwad" VALUES(3,13,'3:13','Quả thật, các ngươi đã có bằng chứng (rõ ràng) qua cuộc giao tranh giữa hai đoàn quân (tại Badr)(, một bên là đoàn quân (Muslim) anh dũng chiến đấu vì chính nghĩa của Allah và bên còn lại là đoàn quân vô đức tin. Chúng thấy rõ quân lực của chúng đông hơn (đoàn quân Muslim) gấp bội. Tuy nhiên, Allah muốn phù hộ bất cứ ai (giành chiến thắng) là tùy ý Ngài. Quả thật, trong sự việc (số lượng ít đánh thắng số lượng đông) đó là bài học dành cho những ai biết suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(3,14,'3:14','Nhân loại thường bị mê hoặc bởi những thú vui từ phụ nữ, con cái, kho tàng vàng bạc chất đống, giống ngựa tốt, gia súc, và đất đai trồng trọt, mặc dù đó chỉ là sự hưởng thụ (tạm bợ) của đời sống trần tục trong khi ở nơi Allah có một chỗ quay về vô cùng tốt đẹp.');
INSERT INTO "vn_ruwwad" VALUES(3,15,'3:15','Ngươi (hỡi Thiên Sứ Muhammad) hãy nói: “Các người có muốn ta cho các người biết thứ tốt đẹp hơn những thứ đó không? Những người ngoan đạo sẽ được Thượng Đế của họ ban cho các ngôi vườn Thiên Đàng, bên dưới có các dòng sông chảy, họ sẽ ở trong đó mãi mãi cùng với những người vợ thanh khiết và họ sẽ có được sự hài lòng của Allah. Quả thật, Allah luôn thấy rõ đám bầy tôi (của Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(3,16,'3:16','(Những người ngoan đạo được ban cho các Ngôi Vườn Thiên Đàng muốn nói đến) là những người thường cầu nguyện: “Lạy Thượng Đế của bầy tôi, quả thật bầy tôi đã có đức tin, xin Ngài hãy tha thứ tội lỗi cho bầy tôi và xin Ngài hãy cứu bầy tôi thoát khỏi sự trừng phạt nơi Hỏa Ngục.”');
INSERT INTO "vn_ruwwad" VALUES(3,17,'3:17','(Những người ngoan đạo được ban cho các Ngôi Vườn Thiên Đàng muốn nói đến) là những người biết kiên nhẫn chịu đựng (trước gian nan và tại họa), những người chân thật (trong lời nói và hành động), những người kiên định (trong đức tin), những người chi dùng (tài sản cho con đường chính nghĩa của Allah), và những người thường cầu xin Allah tha thứ vào lúc đêm khuya (một phần ba cuối của đêm).');
INSERT INTO "vn_ruwwad" VALUES(3,18,'3:18','Allah đã chứng nhận không có Thượng Đế (đích thực) nào khác ngoài Ngài, các Thiên Thần và những người hiểu biết đang duy trì nền công lý cũng chứng nhận (như thế). Quả thật, không có Thượng Đế nào khác ngoài Ngài, Đấng Toàn Năng, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(3,19,'3:19','Quả thật, tôn giáo được chấp nhận nơi Allah chỉ có Islam. Những ai được ban cho Kinh Sách (Do Thái và Thiên Chúa) đã không tranh cãi nhau (về điều này) cho đến khi họ biết rõ sự thật (Islam được mang đến không bởi một người thuộc cộng đồng của họ) thì họ mới đâm ra đố kỵ và chia rẽ. Và ai vô đức tin nơi các lời mặc khải của Allah thì quả thật Allah mau chóng tính sổ (họ).');
INSERT INTO "vn_ruwwad" VALUES(3,20,'3:20','Nếu như bọn họ tranh cãi với Ngươi (hỡi Thiên Sứ Muhammad về chân lý mà Ngươi mang đến) thì Ngươi hãy bảo họ: “Ta và những ai theo Ta đã một lòng thần phục (Allah).” Và Ngươi hãy nói với những ai được ban cho Kinh Sách lẫn những kẻ thất học (những kẻ đa thần): “Các người có chịu quy phục Islam không?” Nếu họ quy phục Islam thì họ đã được hướng đến chính đạo, còn nếu họ quay lưng thì Ngươi (Thiên Sứ Muhammad) thật ra chỉ là người truyền đạt bức thông điệp mà thôi. Quả thật, Allah luôn thấy rõ (mọi động thái của) đám bầy tôi (của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(3,21,'3:21','Quả thật, những kẻ vô đức tin nơi các lời mặc khải của Allah và cố tình giết chết các vị Nabi một cách bất chấp thị phi và giết luôn những ai ra lệnh duy trì công lý thì Ngươi (Thiên Sứ Muhammad) hãy báo cho họ về một sự trừng phạt đau đớn (đang chờ đợi họ).');
INSERT INTO "vn_ruwwad" VALUES(3,22,'3:22','Họ là những kẻ mà mọi việc làm (hành thiện và ngoan đạo) của họ sẽ bị xóa sạch ở trần gian lẫn Đời Sau và họ sẽ không có bất cứ ai giúp đỡ (thoát khỏi sự trừng phạt).');
INSERT INTO "vn_ruwwad" VALUES(3,23,'3:23','(Hỡi Thiên Sứ Muhammad), lẽ nào Ngươi không nhìn thấy những kẻ đã được ban cho Kinh Sách (trong số người Do Thái), khi họ được kêu gọi trở lại với Kinh Sách của Allah (Kinh Cựu Ước) để phân xử (tranh chấp) giữa họ, (thay vì chấp hành theo) thì một nhóm (học giả, lãnh đạo) trong số họ lại quay lưng ngoảnh đi.');
INSERT INTO "vn_ruwwad" VALUES(3,24,'3:24','Như thế là vì họ nói: “Hỏa Ngục sẽ không bao giờ chạm đến chúng tôi ngoại trừ một số ngày ít ỏi” Những điều mà họ bịa đặt trong tôn giáo của họ đã đánh lừa họ.');
INSERT INTO "vn_ruwwad" VALUES(3,25,'3:25','Thế thì họ sẽ ra sao khi TA (Allah) triệu tập họ vào một Ngày không có gì phải hoài nghi về (sự diễn ra của nó), (lúc đó) mỗi linh hồn sẽ được đáp trả đầy đủ về những điều mà họ đã từng làm (dù tốt hay xấu) và họ sẽ không bị đối xử bất công.');
INSERT INTO "vn_ruwwad" VALUES(3,26,'3:26','Ngươi (hỡi Thiên Sứ Muhammad) hãy nói: “Lạy Allah, Đức Vua nắm mọi vương quyền! Ngài muốn ban vương quyền cho ai và lấy lại vương quyền từ ai là tùy ý Ngài, Ngài muốn ban vinh dự cho ai và hạ nhục bất cứ ai là tùy ý Ngài. Mọi điều tốt lành đều nằm trong tay Ngài; quả thật, Ngài toàn năng trên tất cả mọi thứ.”');
INSERT INTO "vn_ruwwad" VALUES(3,27,'3:27','“Ngài nhập ban đêm vào ban ngày và nhập ban ngày vào ban đêm, Ngài rút cái sống ra từ cái chết và rút cái chết ra từ cái sống. Và Ngài ban phát bổng lộc vô kể cho ai Ngài muốn.”');
INSERT INTO "vn_ruwwad" VALUES(3,28,'3:28','Những người có đức tin không được phép lấy những kẻ vô đức tin làm những người bảo hộ thay vì những người có đức tin. Người nào làm vậy thì Allah hoàn toàn vô can với y ngoại trừ trường hợp các ngươi lo sợ các mối họa từ họ. Allah cảnh báo các ngươi về bản thân Ngài. Quả thật, các ngươi phải trở về trình diện Allah.');
INSERT INTO "vn_ruwwad" VALUES(3,29,'3:29','Ngươi (hỡi Thiên Sứ Muhammad) hãy nói với họ: “Dẫu các ngươi có giấu giếm hay tiết lộ bất cứ điều gì trong lòng của các ngươi thì Allah vẫn luôn biết tường tận bởi Ngài thông toàn hết mọi thứ trong các tầng trời và mọi thứ trong trái đất. Quả thật, Allah toàn năng trên tất cả mọi thứ.”');
INSERT INTO "vn_ruwwad" VALUES(3,30,'3:30','Vào Ngày mà mỗi linh hồn sẽ đối diện với những điều thiện tốt mà y đã làm cũng như những điều tội lỗi mà y đã phạm, (mỗi linh hồn) sẽ ước ao giá như giữa y và (những điều tội lỗi) có một khoảng cách thật xa. Allah đã cảnh báo các ngươi về bản thân Ngài, tuy nhiên Allah luôn nhân từ với đám bầy tôi (của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(3,31,'3:31','Ngươi (hỡi Thiên Sứ Muhammad) hãy nói với họ: “Nếu các người thực sự yêu thương Allah thì các người hãy theo ta rồi Allah sẽ yêu thương các người và tha thứ tội lỗi cho các người. Quả thật, Allah hằng tha thứ, hằng xót thương.”');
INSERT INTO "vn_ruwwad" VALUES(3,32,'3:32','Ngươi hãy nói với họ: “Các người hãy tuân lệnh Allah và Thiên Sứ (Muhammad), nhưng nếu các ngươi ngoảnh đi thì (các ngươi hãy biết rằng) quả thật Allah không thương yêu đám người vô đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(3,33,'3:33','Quả thật, Allah đã chọn Adam, Nuh, dòng dõi của Ibrahim và dòng dõi của ‘Imran vượt trội hơn thiên hạ (trong thời đại của họ).');
INSERT INTO "vn_ruwwad" VALUES(3,34,'3:34','Họ là con cháu lẫn nhau (cùng chung tôn giáo: chỉ tôn thờ một mình Allah duy nhất). Quả thật, Allah hằng nghe, hằng biết.');
INSERT INTO "vn_ruwwad" VALUES(3,35,'3:35','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại) khi vợ của ‘Imran cầu nguyện: “Lạy Thượng Đế của bề tôi, bề tôi nguyện dâng hiến đứa con trong bụng của bề tôi cho Ngài để nó toàn tâm thờ phượng Ngài, xin Ngài hãy chấp nhận (sự hiến dâng này) từ bề tôi, quả thật Ngài là Đấng Hằng Nghe, Hằng Biết”.');
INSERT INTO "vn_ruwwad" VALUES(3,36,'3:36','Sau khi hạ sinh, (vợ của ‘Imran) lại cầu nguyện: “Lạy Thượng Đế của bề tôi, bề tôi đã hạ sinh con gái, chỉ Ngài mới biết rõ về đứa con mà bề tôi đã hạ sinh, chắc rằng con trai không giống như con gái (về mặt hành đạo), bề tôi đặt tên cho nó là Mar-yam, bề tôi khẩn xin Ngài phù hộ cho nó và con cháu của nó tránh khỏi lũ Shaytan đã bị nguyền rủa.”');
INSERT INTO "vn_ruwwad" VALUES(3,37,'3:37','Thế là Thượng Đế của Nữ (vợ của ‘Imran) chấp nhận sự hiến dâng của Nữ một cách tốt lành, Ngài đã phù hộ cho con gái (Mar-yam) của Nữ trưởng thành tốt đẹp dưới sự bảo hộ của Zakariya. Cứ mỗi lần Zakariya đi vào nội phòng (nơi hành đạo) của Mar-yam, Y luôn thấy bên cạnh Mar-yam nhiều thực phẩm (trái mùa). Y hỏi (trong ngạc nhiên): “Này Mar-yam, con có thức ăn này từ đâu?”. Mar-yam đáp: “Dạ, nó từ nơi Allah, quả thật, Allah ban phát bổng lộc vô kể cho bất kỳ ai Ngài muốn.”');
INSERT INTO "vn_ruwwad" VALUES(3,38,'3:38','Từ đó (nó thôi thúc) Zakariya cầu nguyện: “Lạy Thượng Đế bề tôi, xin Ngài hãy rủ lòng thương ban cho bề tôi một hậu duệ tốt đẹp, chắc chắn Ngài nghe thấy rõ lời nguyện cầu.”');
INSERT INTO "vn_ruwwad" VALUES(3,39,'3:39','Thế là các Thiên Thần đã gọi (Zakariya) lúc Y đang đứng cầu nguyện trong phòng tu: “Quả thật, Allah báo cho ngươi một tin vui về (việc Ngài sẽ ban cho ngươi một đứa con trai tên là) Yahya, nhằm xác nhận lại lời phán( từ Allah. (Yahya) sẽ là một lãnh đạo, một người trong sạch và là một vị Nabi đạo hạnh.”');
INSERT INTO "vn_ruwwad" VALUES(3,40,'3:40','(Zakariya) nói: “Lạy Thượng Đế của bề tôi, làm sao bề tôi có được đứa con trai khi mà bề tôi đã già yếu còn vợ của bề tôi thì lại là một phụ nữ hiếm muộn?” (Allah) phán: “(Sự việc sẽ xảy ra) đúng như thế bởi Allah làm bất cứ điều gì Ngài muốn.”');
INSERT INTO "vn_ruwwad" VALUES(3,41,'3:41','(Zakariya) thỉnh cầu: “Lạy Thượng Đế của bề tôi, xin Ngài ban cho bề tôi một dấu hiệu nhận biết (vợ bề tôi mang thai)”. (Allah) phán: “Dấu hiệu cho Ngươi là khi Ngươi không thể nói chuyện với mọi người suốt ba ngày liền ngoài trừ việc ra dấu, lúc đó Ngươi hãy tụng niệm Thượng Đế của Ngươi cho thật nhiều và hãy tán dương Ngài sáng chiều.”');
INSERT INTO "vn_ruwwad" VALUES(3,42,'3:42','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại) khi các Thiên thần bảo: “Hỡi Mar-yam! Quả thật, Allah đã chọn ngươi, Ngài tẩy sạch ngươi và chọn ngươi vượt trội hơn thiên hạ.”');
INSERT INTO "vn_ruwwad" VALUES(3,43,'3:43','“Hỡi Mar-yam! Ngươi hãy hết lòng thờ phượng Thượng Đế của ngươi, ngươi hãy quỳ lạy và cúi mình cùng với những người cúi mình (thần phục Allah).”');
INSERT INTO "vn_ruwwad" VALUES(3,44,'3:44','Đó là những nguồn tin (về Zakariya và Mar-yam) thuộc thể loại vô hình mà TA (Allah) đã mặc khải cho Ngươi (Thiên Sứ Muhammad), bởi Ngươi không có mặt lúc họ cùng nhau bắt thăm xem ai trong số họ được quyền ưu tiên bảo hộ Mar-yam và Ngươi cũng không có mặt lúc họ tranh luận nhau về vấn đề này.');
INSERT INTO "vn_ruwwad" VALUES(3,45,'3:45','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại) khi các Thiên Thần bảo: “Này Mar-yam! Quả thật, Allah báo cho ngươi tin vui về lời phán từ Ngài (rằng ngươi mang thai đứa con trai), tên của nó là Masih Ysa (Giê-su), con trai của Mar-yam, nó sẽ được vinh danh ở đời này lẫn Đời Sau và nó sẽ là một trong số những người ở kế cận (Allah).”');
INSERT INTO "vn_ruwwad" VALUES(3,46,'3:46','“Nó sẽ nói chuyện với dân chúng khi vẫn còn nằm nôi và lúc trưởng thành và nó sẽ là một người đạo hạnh”.');
INSERT INTO "vn_ruwwad" VALUES(3,47,'3:47','(Mar-yam) nói: “Lạy Thượng Đế của bề tôi! Làm sao bề tôi có được một đứa con khi mà chưa từng có một người đàn ông nào chạm đến thân thể của bề tôi?” (Allah) phán: “(Sự việc sẽ xảy ra) đúng như thế bởi Allah làm bất cứ điều gì Ngài muốn. Khi Ngài quyết định một việc gì thì Ngài chỉ cần phán “Hãy thành!”, lập tức nó sẽ thành (đúng theo ý của Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(3,48,'3:48','“(Allah) sẽ dạy cho (Ysa) Kinh Sách, sự khôn ngoan cùng với Tawrah (Kinh Cựu Ước) và Injil (Kinh Tân Ước).”');
INSERT INTO "vn_ruwwad" VALUES(3,49,'3:49','“(Ysa) sẽ được cử làm một vị Thiên Sứ đến với người dân Israel (với bức thông điệp): ‘Quả thật, Ta đến với các người cùng với một dấu lạ từ Thượng Đế của các người. Ta có thể tạo ra cho các người một con chim từ đất sét bằng cách hà hơi vào nó để nó trở thành một con chim thật sự theo phép của Allah; ta có thể làm sáng mắt cho người mù bẩm sinh; ta có thể chữa lành cho người bệnh bạch biến; ta có thể làm cho người chết sống lại theo phép của Allah; và ta có thể cho các người biết loại thực phẩm nào các người phải ăn (liền) và loại thực phẩm nào các người dự trữ (lâu dài) trong nhà. Quả thật, trong sự việc đó là một bằng chứng cho các người (về sứ mạng của ta) nếu các người là những người có đức tin’.”');
INSERT INTO "vn_ruwwad" VALUES(3,50,'3:50','‘Và Ta (được cử đến) để xác nhận lại những điều (đã được mặc khải) trước Ta trong Kinh Tawrah và để xóa bỏ một số điều cấm mà các người bị cấm trước đây. Ta đến với các người mang theo một bằng chứng từ Thượng Đế của các người, bởi thế, các người hãy kính sợ Allah và vâng lời Ta.’');
INSERT INTO "vn_ruwwad" VALUES(3,51,'3:51','‘Allah đích thực là Thượng Đế của Ta và cũng là Thượng Đế của các người. Cho nên, các người hãy thờ phượng Ngài, đó là con đường chính đạo.’');
INSERT INTO "vn_ruwwad" VALUES(3,52,'3:52','Khi Ysa nhận thấy sự vô đức tin ở nơi họ thì Y đã lên tiếng hỏi: “Ai sẽ là người phò trợ cho Ta (trong việc kêu gọi) đến với Allah?” Các tông đồ đáp: “Chúng tôi sẽ là những người phò trợ cho (tôn giáo của) Allah, chúng tôi đã có đức tin nơi Allah, và chúng tôi chứng nhận mình là những người Muslim (thần phục Allah).”');
INSERT INTO "vn_ruwwad" VALUES(3,53,'3:53','(Các tông đồ của Ysa cầu nguyện): “Lạy Thượng Đế của bầy tôi, bầy tôi đã có đức tin nơi điều mà Ngài đã mặc khải và bầy tôi nguyện đi theo vị Thiên Sứ (của Ngài). Bởi vậy, xin Ngài hãy ghi tên bầy tôi cùng với những người chứng nhận (của sự thật này)”.');
INSERT INTO "vn_ruwwad" VALUES(3,54,'3:54','(Những kẻ vô đức tin) đã mưu toan lập kế hoạch (hãm hại Ysa) và Allah cũng hoạch định mưu kế (của Ngài). Tuy nhiên, Allah là Đấng hoạch định mưu kế siêu việt.');
INSERT INTO "vn_ruwwad" VALUES(3,55,'3:55','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại) khi Allah phán: “Hỡi Ysa, nay TA sẽ làm cho Ngươi chết (tạm), rồi mang (cả hồn lẫn xác) Ngươi lên cùng với TA và TA sẽ tẩy sạch Ngươi khỏi (mọi lời cáo buộc của) những kẻ vô đức tin và TA sẽ phù hộ cho những ai theo Ngươi cao hơn những kẻ vô đức tin cho đến Ngày Phục Sinh. Rồi đây các ngươi được đưa về trình diện TA để TA phân xử mọi điều mà các ngươi tranh chấp lẫn nhau.”');
INSERT INTO "vn_ruwwad" VALUES(3,56,'3:56','“Những ai không có đức tin, TA sẽ trừng phạt họ bằng một sự trừng phạt khủng khiếp ở đời này lẫn Đời Sau và họ không sẽ được ai cứu giúp.”');
INSERT INTO "vn_ruwwad" VALUES(3,57,'3:57','“Riêng những người có đức tin và hành thiện, họ sẽ được trả cho đầy đủ phần công lao của ho. Quả thật, Allah không ưa thích những kẻ làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(3,58,'3:58','Đó là những lời mặc khải mà TA (Allah) đọc cho Ngươi nghe (hỡi Thiên Sứ Muhammad) và cũng là sự nhắc nhở khôn ngoan (cho Ngươi và những ai theo Ngươi).');
INSERT INTO "vn_ruwwad" VALUES(3,59,'3:59','Quả thật, việc tạo hóa Ysa đối với Allah cũng giống như (việc Ngài đã tạo ra) Adam. Ngài đã tạo (Adam) từ đất sét rồi phán: “Hãy thành!” thế là (Adam) thành (con người hoàn chỉnh đúng như thế).');
INSERT INTO "vn_ruwwad" VALUES(3,60,'3:60','(Hỡi Thiên Sứ Muhammad), chân lý thực sự đến từ Thượng Đế của Ngươi, cho nên, Ngươi chớ đem lòng hoài nghi.');
INSERT INTO "vn_ruwwad" VALUES(3,61,'3:61','Nếu có ai tranh luận với Ngươi về (việc khẳng định Ysa là Thượng Đế) sau khi Ngươi đã tiếp thu được kiến thức (đúng thực rằng Ysa chỉ là một bề tôi của Allah) thì Ngươi hãy bảo họ: “Nào, các người hãy đến đây và gọi luôn cả vợ và các con của chúng tôi cũng như cả vợ và các con của các người, rồi mỗi người trong chúng ta thật lòng cầu xin lời nguyền rủa của Allah giáng lên (đầu) những kẻ nói dối.”');
INSERT INTO "vn_ruwwad" VALUES(3,62,'3:62','Đây thực sự là những câu chuyện có thật. Quả thật, không có Thượng Đế đích thực nào khác ngoài Allah và Allah là Đấng Toàn Năng, Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(3,63,'3:63','Cho nên, những ai ngoảnh mặt bỏ đi thì quả thật Allah luôn biết rõ về đám người hủy hoại (trái đất).');
INSERT INTO "vn_ruwwad" VALUES(3,64,'3:64','Ngươi (hỡi Thiên Sứ) hãy nói: “Hỡi dân Kinh Sách (Do Thái và Thiên Chúa), các người hãy đến với một lời phán như nhau giữa chúng tôi và các người rằng chúng ta chỉ thờ phượng một mình Allah, không tổ hợp với Ngài bất cứ ai (vật gì) và không ai trong chúng ta nhận lấy ai khác ngoài Allah làm Thượng Đế.” Nhưng nếu họ ngoảnh mặt bỏ đi thì Ngươi hãy nói với họ: “Vậy thì các người hãy chứng thực rằng chúng tôi là những người Muslim (thần phục Allah).”');
INSERT INTO "vn_ruwwad" VALUES(3,65,'3:65','Hỡi dân Kinh Sách, tại sao các ngươi lại tranh luận về Ibrahim trong khi Kinh Tawrah và Kinh Injil đều được mặc khải xuống sau Y? Chẳng lẽ các ngươi không hiểu ư?');
INSERT INTO "vn_ruwwad" VALUES(3,66,'3:66','Chẳng phải các ngươi thường có thói quen tranh luận về những điều mà các ngươi thực sự có kiến thức nhưng giờ sao lại tranh luận về những điều mà các ngươi không chút hiểu biết nào? Quả thật, Allah là Đấng biết (rõ sự thật) còn các ngươi thì chẳng biết gì.');
INSERT INTO "vn_ruwwad" VALUES(3,67,'3:67','Ibrahim không phải là người Do Thái cũng không phải là người Thiên Chúa mà là một người Muslim thuần túy (tôn thờ một mình Allah). Y thực sự không phải là người thờ đa thần.');
INSERT INTO "vn_ruwwad" VALUES(3,68,'3:68','Trong nhân loại, những người thực sự xứng đáng (cận kề) với Ibrahim nhất là những ai đã noi theo (đường lối của) Y và vị Nabi này (Muhammad) và những người có đức tin. Và Allah là Ðấng Bảo Hộ của những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(3,69,'3:69','Một nhóm trong số dân Kinh Sách đã mong muốn dẫn dắt các ngươi (những người có đức tin) rơi vào lầm lạc. Tuy nhiên, họ không khiến được ai rơi vào lầm lạc ngoại trừ chính bản thân họ nhưng họ không nhận thấy.');
INSERT INTO "vn_ruwwad" VALUES(3,70,'3:70','Này hỡi dân Kinh Sách, tại sao các ngươi lại phủ nhận các lời mặc khải của Allah trong khi các ngươi là nhân chứng cho chúng?');
INSERT INTO "vn_ruwwad" VALUES(3,71,'3:71','Này hỡi dân Kinh Sách, tại sao các ngươi lại lấy điều ngụy tạo khoác lên điều chân lý, tại sao các ngươi lại che giấu sự thật trong khi các ngươi biết rõ về nó?');
INSERT INTO "vn_ruwwad" VALUES(3,72,'3:72','Một nhóm dân Kinh Sách bảo nhau: “Các người hãy giả vờ tin nơi những gì đã được thiên khải cho nhóm người có đức tin vào buổi sáng rồi đến chiều các người hãy phủ nhận niềm tin đó, hi vọng họ (những người Muslim) sẽ trở lại (tôn giáo đa thần trước đây).”');
INSERT INTO "vn_ruwwad" VALUES(3,73,'3:73','“Và các người hãy chỉ tin những ai theo tôn giáo của các người.” Ngươi (hỡi Thiên Sứ Muhammad) hãy nói với họ: “Quả thật, nguồn chỉ đạo đích thực (hướng đến chân lý) là nguồn chỉ đạo của Allah. (Lẽ nào các người e sợ) ai đó được ban cho điều giống như điều đã được ban cho các người hoặc (lẽ nào các người e sợ) họ sẽ tranh luận với các người trước Thượng Đế của các người?” Ngươi hãy nói với họ: “Quả thật, mọi hồng phúc đều nằm trong tay Allah, Ngài muốn ban cho ai tùy ý Ngài. Allah là Đấng bao la, Đấng thông toàn.”');
INSERT INTO "vn_ruwwad" VALUES(3,74,'3:74','Ngài đặc biệt dành hồng ân của Ngài cho người nào Ngài muốn. Quả thật, Allah có hồng ân vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(3,75,'3:75','Trong số dân Kinh Sách có người khi Ngươi (Thiên Sứ Muhammad) tin tưởng giao cho họ một đống (tài sản) thì họ (uy tín) hoàn lại cho Ngươi đầy đủ, tuy nhiên, trong họ cũng có người dù Ngươi chỉ giao cho họ một đồng vàng (duy nhất) thì họ cũng quyết không hoàn lại cho Ngươi trừ phi Ngươi cố đòi cho được. Sở dĩ như thế vì họ cho rằng: “Chúng tôi không phải chịu trách nhiệm cho đám người mù chữ (dân Ả Rập)”. Họ đã nói dối cho Allah trong lúc họ biết rõ sự thật.');
INSERT INTO "vn_ruwwad" VALUES(3,76,'3:76','Không. (Sự thật không như họ nói) mà chỉ có những ai làm tròn giao ước và có lòng ngoan đạo thì quả thật Allah thương yêu những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(3,77,'3:77','Những kẻ bán giao ước của Allah cũng như lời thề thốt của họ (với Ngài) với giá ít ỏi thì vào Ngày Phán Xét họ sẽ không có được bất cứ thứ gì, Allah sẽ không nói chuyện với họ, không nhìn đến họ, không thanh lọc tội lỗi cho họ, và họ sẽ bị trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(3,78,'3:78','Quả thật, một nhóm trong bọn họ đã bóp méo Kinh Sách bằng chiếc lưỡi của họ. (Cho nên khi đọc), các ngươi cứ tưởng đó là điều nằm trong Kinh Sách nhưng thực chất nó không thuộc về Kinh Sách. Họ bảo đó là điều từ nơi Allah nhưng thực chất không phải đến từ nơi Allah. Họ nói dối cho Allah trong lúc họ biết rõ (sự thật).');
INSERT INTO "vn_ruwwad" VALUES(3,79,'3:79','Không một người phàm nào được Allah ban cho Kinh Sách, sự khôn ngoan và sứ mạng Nabi lại đi bảo thiên hạ: “Mọi người hãy là bầy tôi của ta thay vì Allah”. Ngược lại, (y phải nói): “Các người hãy là những bề tôi tôn thờ Thượng Đế (Allah) bởi điều các người răn dạy cho (người khác) từ Kinh Sách cũng như những gì các người đã học được.”');
INSERT INTO "vn_ruwwad" VALUES(3,80,'3:80','Y cũng không hề sai bảo các ngươi chọn các Thiên Thần và các vị Nabi làm các thượng đế. Lẽ nào y lại ra lệnh cho các ngươi làm điều vô đức tin sau khi các ngươi đã là tín đồ Muslim (thần phục Allah)?!');
INSERT INTO "vn_ruwwad" VALUES(3,81,'3:81','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại) khi Allah lấy giao ước với các vị Nabi rằng: “Cho dù TA có ban cho các ngươi Kinh Sách và sự khôn ngoan ra sao nhưng một khi Thiên Sứ (Muhammad) được cử đến kế nhiệm thì bắt buộc các ngươi phải hết lòng tin tưởng Y và hết lòng phò trợ Y.” (Allah) hỏi họ: “Các ngươi có chấp nhận và đảm bảo với TA về giao ước này không?” Tất cả đều đáp: “Bầy tôi xin chấp nhận.” (Allah) phán: “Thế thì các ngươi hãy chứng nhận và TA cũng sẽ chứng nhận cùng các ngươi.”');
INSERT INTO "vn_ruwwad" VALUES(3,82,'3:82','Cho nên, những ai quay mặt ngoảnh đi sau đó thì là những kẻ sai quấy, bất tuân.');
INSERT INTO "vn_ruwwad" VALUES(3,83,'3:83','Lẽ nào họ muốn chọn lấy một tôn giáo khác tôn giáo của Allah trong khi vạn vật trong các tầng trời và trái đất dù muốn hay không cũng đều thần phục Ngài và họ sẽ phải trở về (trình diện) Ngài?!');
INSERT INTO "vn_ruwwad" VALUES(3,84,'3:84','Ngươi (hỡi Thiên Sứ Muhammad) hãy nói với họ: “Chúng tôi đã tin nơi Allah, nơi những gì được thiên khải xuống cho chúng tôi, cho Ibrahim (Abraham), cho Isma’il, cho Is-haq (Isaac), cho Ya’qub (Jacob), và cho những vị Nabi (thuộc con cháu của Ya’qub); chúng tôi tin nơi những gì được thiên khải xuống cho Musa, cho Ysa cũng như toàn bộ các vị Nabi được Thượng Đế phái đến; chúng tôi không phân biệt bất cứ ai trong sô họ; và chúng tôi là những tín đồ Muslim (thần phục Allah).”');
INSERT INTO "vn_ruwwad" VALUES(3,85,'3:85','“Ai chọn lấy một tôn giáo nào khác ngoài tôn giáo Islam thì việc làm đó của y sẽ không bao giờ được (Allah) chấp nhận và ở Đời Sau y sẽ là một trong những kẻ thua thiệt.”');
INSERT INTO "vn_ruwwad" VALUES(3,86,'3:86','Làm thế nào mà Allah lại hướng dẫn một nhóm người chối bỏ đức tin sau khi đã tin tưởng, đã xác nhận Thiên Sứ (Muhammad) là sự thật, và mọi bằng chứng rõ ràng đã đến với họ?! Quả thật, Allah không bao giờ hướng dẫn đám người làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(3,87,'3:87','Những kẻ đó, phần thưởng dành cho chúng là lời nguyền rủa của Allah, các Thiên Thần và toàn nhân loại.');
INSERT INTO "vn_ruwwad" VALUES(3,88,'3:88','Chúng sẽ ở trong (Hỏa Ngục) mãi mãi, chúng không được giảm nhẹ hình phạt và cũng không được ân xá.');
INSERT INTO "vn_ruwwad" VALUES(3,89,'3:89','Trừ những ai biết ăn năn sám hối sau đó và hành thiện thì quả thật, Allah hằng tha thứ, nhân từ.');
INSERT INTO "vn_ruwwad" VALUES(3,90,'3:90','Đối với những ai đã bác bỏ đức tin sau khi đã tin tưởng rồi sự vô đức tin của y ngày càng mãnh liệt hơn (cho đến cuối đời) thì chắc chắn Allah không bao giờ chấp nhận sự ăn năn sám hối của họ (lúc họ hấp hối) và họ chính là đám người lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(3,91,'3:91','Quả thật, những ai vô đức tin và chết trong tình trạng vô đức tin đó thì dẫu ai đó trong số họ có mang cả trái đất đầy vàng đến để chuộc tội thì cũng không được chấp nhận. Họ sẽ phải chịu hình phạt đau đớn và sẽ không được một ai giúp đỡ.');
INSERT INTO "vn_ruwwad" VALUES(3,92,'3:92','Các ngươi (những người có đức tin) sẽ chẳng đạt được sự ngoan đạo cho đến khi các ngươi chi dùng (cho con đường chính nghĩa của Allah) những gì được các ngươi yêu thích nhất. Và bất cứ điều gì các ngươi (các ngươi chi dùng cho con đường chính nghĩa của Allah) thì Allah đều biết rõ.');
INSERT INTO "vn_ruwwad" VALUES(3,93,'3:93','Tất cả thức ăn đều Halal cho người dân Israel ngoại trừ những gì Israel( tự cấm bản thân Y trước khi Kinh Tawrah được thiên khải xuống. Ngươi (hỡi Thiên Sứ Muhammad) bảo họ: “(Nếu không đúng như vậy) thì các người hãy mang Tawrah ra đây và hãy đọc nó lên (để kiểm chứng) nếu các người là những người nói thật.”');
INSERT INTO "vn_ruwwad" VALUES(3,94,'3:94','“Cho nên, ai đặt điều nói dối cho Allah sau đó thì họ chính là đám người làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(3,95,'3:95','Ngươi (Thiên Sứ Muhammad) hãy nói với họ: “Allah đã phán sự thật, thế nên, các người hãy tuân theo tín ngưỡng thuần túy của Ibrahim bởi Người không phải là kẻ thờ đa thần.”');
INSERT INTO "vn_ruwwad" VALUES(3,96,'3:96','Quả thật, ngôi đền đầu tiên được xây dựng cho nhân loại (để thờ phượng Allah) là Ngôi Đền tại Bakkah (Makkah) hồng phúc và là sự chỉ đạo cho toàn vũ trụ và muôn loài.');
INSERT INTO "vn_ruwwad" VALUES(3,97,'3:97','Tại đó có những chứng tích rõ ràng như chỗ đứng của Ibrahim, và ai đã vào trong đó là đã được an toàn. Và nhân loại phải đi hành hương đến ngôi đền (Ka’bah) để (thờ phượng) Allah khi có đủ khả năng cho sự việc đó. Còn ai phủ nhận (không tuân theo) thì quả thật, Allah rất giàu có vốn không cần đến vũ trụ và vạn vật.');
INSERT INTO "vn_ruwwad" VALUES(3,98,'3:98','Ngươi (Thiên Sứ Muhammad) hãy nói: “Hỡi dân Kinh Sách, tại sao các người lại phủ nhận các lời mặc khải của Allah trong khi Allah là nhân chứng cho mọi điều các ngươi làm?”');
INSERT INTO "vn_ruwwad" VALUES(3,99,'3:99','Ngươi (hỡi Thiên Sứ) hãy nói: “Hỡi dân Kinh Sách, tại sao các người lại ngăn chặn những người có đức tin đi theo con đường của Allah và làm cho nó cong quẹo trong khi các người là những nhân chứng (cho sự thật trong Kinh Sách của các người) và tất nhiên Allah không hề lơ là trước những gì các người làm.”');
INSERT INTO "vn_ruwwad" VALUES(3,100,'3:100','Hỡi những người có đức tin, nếu các ngươi tuân theo một nhóm người được ban cho Kinh Sách (Do Thái và Thiên Chúa), chắc chắn họ sẽ kéo các ngươi trở lại vô đức tin sau khi các ngươi đã tin tưởng.');
INSERT INTO "vn_ruwwad" VALUES(3,101,'3:101','Sao các ngươi lại có thể phủ nhận trong khi các ngươi được đọc cho nghe các lời phán của Allah và Thiên Sứ của Ngài vẫn đang sống cùng các ngươi? Ai bám chặt vào Allah( thì quả thật y đã được hướng dẫn đến con đường ngay thẳng (chính đạo).');
INSERT INTO "vn_ruwwad" VALUES(3,102,'3:102','Hỡi những người có đức tin, các ngươi hãy kính sợ Allah bằng lòng kính sợ thực sự; và các ngươi hãy đừng chết ngoại trừ các ngươi đã là những người Muslim (thần phục Allah).');
INSERT INTO "vn_ruwwad" VALUES(3,103,'3:103','Các ngươi (những người có đức tin) hãy bám chặt lấy sợi dây (tôn giáo Islam) của Allah và chớ đừng chia rẽ. Và các ngươi hãy nhớ lại ân huệ của Allah dành cho các ngươi: lúc các ngươi là kẻ thù của nhau, Ngài đã kết nối trái tim của các ngươi lại, nhờ ân huệ đó của Ngài mà các ngươi trở thành anh em của nhau; và lúc các ngươi ở trên bờ của hố lửa (xung đột và chiến tranh), Ngài đã giải cứu các ngươi thoát khỏi (tình trạng) đó. Như thế đấy, Allah trình bày rõ các lời mặc khải của Ngài cho các ngươi mong rằng các ngươi được hướng dẫn.');
INSERT INTO "vn_ruwwad" VALUES(3,104,'3:104','Trong các ngươi, cần phải có một nhóm đứng lên kêu gọi (thiên hạ) đến với điều tốt, bảo ban họ hành thiện và ngăn cản họ làm điều xấu. Những người đó là những người thành công.');
INSERT INTO "vn_ruwwad" VALUES(3,105,'3:105','Các ngươi chớ giống như những kẻ đã chia rẽ và bất đồng nhau (dân Kinh Sách) sau khi các bằng chứng rõ rệt đã đến với họ. Họ chắc chắn sẽ nhận lấy một sự trừng phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(3,106,'3:106','Vào Ngày mà có những gương mặt sẽ sáng ngời và rạng rỡ cũng như có những gương mặt sẽ tối sầm và đen sịt. Đối với những ai có gương mặt tối sầm và đen sịt thì (có lời bảo họ): “Có phải các ngươi đã vô đức tin sau khi đã tin tưởng?! Thế (thì thật đáng đời cho các ngươi), các ngươi hãy nếm lấy sự trừng phạt cho những điều mà các ngươi đã vô đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(3,107,'3:107','Đối với những ai là người có gương mặt sáng ngời và rạng rỡ thì họ sẽ ở trong sự nhân từ của Allah (Thiên Đàng), họ sẽ được ở trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(3,108,'3:108','(Hỡi Thiên Sứ Muhammad), đó là những lời mặc khải của Allah, TA đọc chúng cho Ngươi nghe bằng sự thật. Quả thật, Allah không hề muốn bất cứ ai trong vũ trụ gặp điều bất công.');
INSERT INTO "vn_ruwwad" VALUES(3,109,'3:109','Vạn vật trong các tầng trời và vạn vật trong trái đất đều thuộc về Allah; và tất cả mọi sự việc đều sẽ được đưa về trình Allah.');
INSERT INTO "vn_ruwwad" VALUES(3,110,'3:110','Các ngươi (những người Muslim) là một cộng đồng tốt đẹp nhất được dựng lên (để làm tấm gương) cho nhân loại. Các ngươi bảo ban mọi người hành thiện, ngăn cản mọi người làm điều xấu, và các ngươi có đức tin nơi Allah. Nếu dân Kinh Sách cũng có đức tin giống như các ngươi thì điều đó đã tốt cho họ, tuy nhiên, chỉ có số ít trong bọn họ là những người có đức tin còn đa số là những kẻ bất tuân, tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(3,111,'3:111','(Dân Kinh Sách) không bao giờ có thể làm hại được các ngươi ngoại trừ chút ít phiền hà (từ cái lưỡi của họ, như: nói xấu, xúc phạm, nhạo báng...). Nếu họ đánh các ngươi thì họ cũng sẽ quay lưng bỏ chạy mà thôi, rồi họ sẽ không được ai giúp đỡ cả.');
INSERT INTO "vn_ruwwad" VALUES(3,112,'3:112','Sự hèn hạ bao trùm lấy họ ở bất cứ nơi nào họ có mặt ngoại trừ họ được sự đảm bảo từ Allah hoặc từ nhân loại. Họ phải nhận lấy cơn thịnh nộ của Allah và bị sự đói nghèo đeo bám. Sở dĩ họ bị như thế là vì họ đã phủ nhận các lời mặc khải của Allah và giết hại các vị Nabi một cách vô cớ. Đó là (hậu quả) cho việc bất tuân và vượt mức giới hạn.');
INSERT INTO "vn_ruwwad" VALUES(3,113,'3:113','Tất nhiên, không phải họ đều giống nhau, bởi trong số đám dân Kinh Sách có những người biết tuân thủ đúng (giáo luật của Allah), họ đọc các lời phán của Allah và quỳ lạy (Ngài) thâu đêm.');
INSERT INTO "vn_ruwwad" VALUES(3,114,'3:114','Họ có đức tin nơi Allah và nơi Đời Sau, họ kêu gọi bảo ban mọi người hành thiện và ngăn cản mọi người làm điều xấu, họ thường đua nhau làm điều tốt. Họ là những người đức hạnh.');
INSERT INTO "vn_ruwwad" VALUES(3,115,'3:115','Bất cứ điều tốt nào họ làm (dù nhiều hay ít) sẽ không bao giờ bị vứt bỏ (mà sẽ được đền đáp đầy đủ cho công sức của họ), bởi Allah biết rõ người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(3,116,'3:116','Quả thật, những kẻ vô đức tin, con cái và tài sản của họ sẽ không giúp ích được gì cho họ trước (sự trừng phạt của) Allah. Họ chính là cư dân của Hỏa Ngục, họ sẽ sống trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(3,117,'3:117','Hình ảnh chi dùng tài sản của họ trong cuộc sống trần gian này giống như hình ảnh của cơn gió mang theo khí lạnh buốt tàn phá vườn tược của những kẻ đã bất công với chính mình. Quả thật, Allah không hề bất công với họ mà chính họ đã bất công với bản thân mình.');
INSERT INTO "vn_ruwwad" VALUES(3,118,'3:118','Hỡi những người có đức tin, các ngươi chớ đừng chọn ai khác ngoài các ngươi làm bạn thân tín (bởi) những kẻ đó không bỏ cơ hội chống phá các ngươi. Họ luôn mong các ngươi gặp phải điều bất trắc. Quả thật, sự hiểm độc đã xuất hiện trên cửa miệng của họ, tuy nhiên sự hiểm độc nằm trong lòng họ còn dữ tợn hơn nhiều. TA (Allah) thực sự đã trình bày rõ ràng các lời mặc khải (của TA) cho các ngươi nếu các ngươi có khối óc biết suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(3,119,'3:119','Phải chăng, các ngươi (những người có đức tin) lại thương yêu họ trong khi họ chẳng yêu thương gì các ngươi, các ngươi đã tin toàn bộ Kinh Sách (kể cả Kinh Sách của họ nhưng họ nào có tin vào Kinh Sách đã gởi cho Thiên Sứ của các ngươi). Khi họ gặp các ngươi, họ nói “Chúng tôi đã có đức tin” nhưng khi họ ở riêng với nhau thì họ lại bộc lộ nỗi căm hờn các ngươi đến mức cắn các ngón tay. Ngươi (hỡi Thiên Sứ Muhammad) hãy bảo họ: “Các ngươi cứ ôm lấy nỗi căm hờn đó mà chết đi. Quả thật, Allah luôn biết rõ mọi điều giấu kín trong lòng.”');
INSERT INTO "vn_ruwwad" VALUES(3,120,'3:120','Nếu các ngươi gặp được điều tốt lành thì họ bực tức khó chịu trong lòng nhưng nếu các ngươi gặp phải điều xấu thì họ lại vui mừng. Tuy nhiên, nếu các ngươi biết kiên nhẫn và kính sợ (Alalh) thì mọi âm mưu và thủ đoạn của họ chẳng hại được các ngươi bất cứ điều gì. Quả thật, Allah bao quát hết những gì họ làm.');
INSERT INTO "vn_ruwwad" VALUES(3,121,'3:121','Ngươi (hỡi Thiên Sứ Muhammad) hãy nhớ lại lúc Ngươi rời gia đình (xuất binh từ Madinah) vào buổi sáng. (Khi đến núi Uhud) Ngươi đã sắp xếp vị trí chiến đấu cho những người có đức tin, tất nhiên (tất cả mọi điều đó) Allah hằng nghe, hằng biết.');
INSERT INTO "vn_ruwwad" VALUES(3,122,'3:122','Ngươi (hỡi Thiên Sứ Muhammad) hãy nhớ lại về việc hai đội quân trong các ngươi (bộ tộc Salamah và bộ tộc Harithah) đã có ý định bỏ ngũ trong khi Allah là Đấng Bảo Hộ cho cả hai đội quân đó. Bởi thế, những người có đức tin hãy phó thác cho Allah.');
INSERT INTO "vn_ruwwad" VALUES(3,123,'3:123','Quả thật, Allah đã ban chiến thắng cho các ngươi tại Badr trong lúc các ngươi chỉ là đội quân nhỏ yếu. Bởi vậy, các ngươi hãy kính sợ Allah, mong rằng các ngươi biết tạ ơn Ngài.');
INSERT INTO "vn_ruwwad" VALUES(3,124,'3:124','Ngươi (hỡi Thiên Sứ Muhammad) hãy nhớ lại khi Ngươi nói với những người có đức tin: “Chẳng lẽ vẫn chưa đủ cho các người hay sao khi Thượng Đế của các người đã cử ba ngàn Thiên Thần xuống hỗ trợ cho các ngươi?!”');
INSERT INTO "vn_ruwwad" VALUES(3,125,'3:125','Đúng vậy, nếu các ngươi kiên nhẫn và kính sợ (Allah) thì cho dù quân địch có bất ngờ tấn công các ngươi, Thượng Đế của các ngươi chắc chắn sẽ tăng cường cho các ngươi năm ngàn Thiên Thần mang dấu hiệu đặc biệt hạ trần.');
INSERT INTO "vn_ruwwad" VALUES(3,126,'3:126','Chẳng qua Allah làm điều đó chỉ để báo tin mừng cho các ngươi và để làm cho các ngươi an lòng mà thôi, chứ mọi sự thắng lợi đều do Allah cả, Ngài là Đấng Quyền Năng, Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(3,127,'3:127','(Với chiến thắng đó) Ngài muốn tiêu diệt một đám người trong những kẻ vô đức tin hoặc muốn hạ nhục họ để họ phải quay về trong sự thất bại thảm hại và nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(3,128,'3:128','Ngươi (Thiên Sứ Muhammad) không có bất cứ thẩm quyền nào trong vụ việc (của họ), hoặc Ngài sẽ tha thứ cho họ hoặc Ngài sẽ trừng phạt họ bởi vì họ là đám người làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(3,129,'3:129','Vạn vật trong các tầng trời và vạn vật trong trái đất đều thuộc về Allah, Ngài tha thứ cho ai Ngài muốn và trừng phạt bất kỳ ai Ngài muốn, bởi Allah hằng tha thứ, khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(3,130,'3:130','Hỡi những người có đức tin, các ngươi chớ đừng ăn tiền cho vay lấy lãi, gấp đôi hay nhiều hơn, các ngươi hãy kính sợ Allah mong rằng các ngươi thành đạt.');
INSERT INTO "vn_ruwwad" VALUES(3,131,'3:131','Các ngươi hãy sợ Hỏa Ngục, nơi được chuẩn bị cho những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(3,132,'3:132','Các ngươi hãy vâng lệnh Allah và Thiên Sứ (Muhammad) mong rằng các ngươi được thương xót.');
INSERT INTO "vn_ruwwad" VALUES(3,133,'3:133','Các ngươi hãy mau mau đến với lòng tha thứ của Thượng Đế của các ngươi và Thiên Đàng, nơi mà khoảng rộng của nó như các tầng trời và trái đất, được chuẩn bị cho những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(3,134,'3:134','Những người mà họ chi dùng (tài sản của mình cho chính nghĩa của Allah) trong lúc giàu có lẫn lúc cơ hàn, họ biết kiềm chế cơn giận và biết lượng thứ cho người khác. Quả thật Allah rất thương yêu những người làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(3,135,'3:135','Những người mà khi họ đã lỡ làm điều tội lỗi hoặc bất công với chính mình thì họ liền nhớ đến Allah và cầu xin Ngài tha thứ cho tội lỗi của họ (bởi vì họ biết) không ai có quyền tha thứ tội lỗi ngoại trừ Allah. Và họ không tái phạm những điều đã làm vì họ biết rõ (đó là sai trái).');
INSERT INTO "vn_ruwwad" VALUES(3,136,'3:136','Những người đó, phần thưởng dành cho họ là sự tha thứ từ Thường Đế của họ cùng với những Ngôi Vườn Thiên Đàng bên dưới có các dòng sông chảy, họ sẽ sống trong đó mãi mãi. Thật phúc thay về phần thưởng dành cho những người làm việc (thiện tốt).');
INSERT INTO "vn_ruwwad" VALUES(3,137,'3:137','Quả thật, trước các ngươi đã có những đường lối (của những kẻ vô đức tin) được thể hiện. Cho nên, các ngươi hãy đi đây đó trên trái đất để xem kết cuộc của những kẻ phủ nhận đức tin (đã bị trừng phạt thích đáng) như thế nào.');
INSERT INTO "vn_ruwwad" VALUES(3,138,'3:138','(Qur’an) này đây là một sự trình bày rõ ràng cho nhân loại, Nó đồng thời là nguồn chỉ đạo và là lời răn dạy dành cho những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(3,139,'3:139','(Do đó, các ngươi, hỡi những người có đức tin) chớ đừng yếu lòng cũng như chớ đừng buồn rầu bởi các ngươi sẽ giành chiến thắng nếu các ngươi thực sự có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(3,140,'3:140','Nếu các ngươi bị thiệt hại và tổn thất thì quả thật đám người (vô đức tin) cũng bị thiệt hại và tổn thất giống như vậy. Đó là những ngày (được và mất) mà TA (Allah) xoay chuyển giữa nhân loại để Allah biết rõ ai thực sự có đức tin và đón nhận những người tử đạo trong các ngươi. Quả thật, Allah không hề yêu thương những kẻ làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(3,141,'3:141','Và để Allah tẩy xóa (tội lỗi) cho những người thực sự có đức tin đồng thời tiêu diệt những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(3,142,'3:142','(Hỡi những người có đức tin,) phải chăng các ngươi nghĩ rằng các ngươi sẽ vào Thiên Đàng (mà không phải trải qua cuộc thử thách) để Allah biết rõ ai trong các ngươi là người thực sự đã chiến đấu (cho chính nghĩa của Ngài) và để Ngài biết rõ ai là những người kiên nhẫn chịu đựng ư?!');
INSERT INTO "vn_ruwwad" VALUES(3,143,'3:143','Quả thật, các ngươi đã từng mong muốn được chết (trên chiến trường như một tử đạo cho chính nghĩa của Allah) trước khi các ngươi gặp nó (cái chết). Giờ đây, các ngươi thực sự đã nhìn thấy nó trong lúc các ngươi đang đứng nhìn (trận chiến).');
INSERT INTO "vn_ruwwad" VALUES(3,144,'3:144','Thật ra Muhammad chỉ là một Thiên Sứ giống như bao Thiên Sứ trước Y mà thôi. Lẽ nào khi Y chết hoặc bị giết thì các ngươi lại quay gót bỏ chạy? Người nào quay gót bỏ chạy thì (điều đó) chẳng làm hại được Allah bất cứ điều gì. Quả thật, Allah sẽ ban thưởng cho những người biết tri ân (Ngài).');
INSERT INTO "vn_ruwwad" VALUES(3,145,'3:145','Không một linh hồn nào sẽ chết trừ phi có phép của Allah, nó được ghi sẵn thời hạn một cách nhất định. Người nào ham muốn lợi lộc của trần gian này thì TA (Allah) sẽ ban cho y, và người nào ham muốn lợi lộc ở Đời Sau thì TA sẽ ban cho y. Quả thật, TA sẽ ban thưởng cho những người biết tri ân (TA).');
INSERT INTO "vn_ruwwad" VALUES(3,146,'3:146','Có biết bao vị Nabi cùng nhiều tín đồ ngoan đạo đã chiến đấu anh dũng trên con đường chính nghĩa của Allah, họ không yếu lòng, không nhục chí cũng như không chùn bước khi gặp gian nan. Quả thật Allah yêu thương những người kiên nhẫn chịu đựng.');
INSERT INTO "vn_ruwwad" VALUES(3,147,'3:147','Họ không nói gì khác ngoài những lời: “Lạy Thượng Đế của bầy tôi, xin Ngài xí xóa tội lỗi cho bầy tôi, những sai phạm quá mức của bầy tôi, xin Ngài hãy giữ vững đôi chân của bầy tôi và phù hộ bầy tôi giành chiến thắng trước đám người vô đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(3,148,'3:148','Cho nên, Allah đã ban cho họ phần thưởng ở trần gian (được thắng lợi vẻ vang) và phần thưởng tốt đẹp ở Đời Sau (Thiên Đàng vĩnh cửu). Quả thật, Allah yêu thương những người làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(3,149,'3:149','Hỡi những người có đức tin, nếu các ngươi tuân theo những kẻ vô đức tin, họ sẽ kéo các ngươi lùi lại ở phía sau khiến các ngươi lui về thảm bại.');
INSERT INTO "vn_ruwwad" VALUES(3,150,'3:150','Ngược lại, (nếu các ngươi nghe theo Allah) thì Allah mới là Đấng Bảo Hộ đích thực của các ngươi và Ngài là Đấng Ưu Việt trong việc phù hộ giành chiến thắng.');
INSERT INTO "vn_ruwwad" VALUES(3,151,'3:151','TA (Allah) sẽ gieo nỗi khiếp sợ vào con tim của họ bởi điều mà họ đã Shirk với Allah, điều mà Ngài không cho bất cứ thẩm quyền nào. Và rồi đây chỗ ở của họ chính là Hỏa Ngục, một nơi ở vô cùng tồi tệ cho đám người sai trái.');
INSERT INTO "vn_ruwwad" VALUES(3,152,'3:152','Quả thật, Allah đã thực hiện đúng lời hứa (ban chiến thắng) của Ngài cho các ngươi. Tuy nhiên, khi các ngươi gần đánh bại họ (kẻ thù tại Uhud) dưới sự cho phép của Ngài, các ngươi không còn tự chủ dẫn đến tranh cãi và kháng lại mệnh lệnh (của Thiên Sứ) sau khi các ngươi được Ngài phơi bày cho thấy những thứ (chiến lợi phẩm) mà các ngươi ham muốn. Trong các ngươi, có người ham muốn đời sống trần tục này và có người chỉ ham muốn Đời Sau, rồi Ngài đã khiến họ (kẻ thù) đánh úp các ngươi (lúc các ngươi thu gom chiến lợi phẩm) để Ngài thử thách các ngươi. Nhưng rồi Ngài cũng đã tha thứ tội lỗi cho các ngươi (về tội nghịch lại mệnh lệnh của Thiên Sứ). Quả thật, Allah luôn nhân từ và khoan dung đối với những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(3,153,'3:153','(Hỡi những người có đức tin, các ngươi hãy nhớ lại ngày ở Uhud) lúc các ngươi chạy lên đồi cao không quan tâm đến ai khác, mặc cho Thiên Sứ cố réo gọi các ngươi (hãy đến bên Ta, hỡi các nô lệ của Allah). Ngài (Allah) đã cho các ngươi nếm lấy đau khổ này đến đau khổ khác để các ngươi không còn buồn bã cho điều đã vụt mất (chiến lợi phẩm) cũng như nạn kiếp mà các ngươi phải trải qua. Quả thật, Allah thông toàn những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(3,154,'3:154','Sau lần nạn kiếp đó, Ngài (Allah) gieo vào (tấm lòng) của các ngươi sự bằng an giúp một nhóm người (có đức tin) trong các ngươi vững lòng mà thiếp đi, còn nhóm người còn lại (nhóm giả tạo đức tin) thì cảm giác bất an cho bản thân mình, nó khiến họ suy nghĩ không đúng về Allah như những người thời tiền Islam đã từng nghĩ, họ nói: “Chẳng lẽ chúng tôi không được có bất cứ ý kiến nào trong vụ việc này ư?!” Ngươi (Thiên Sứ Muhammad) hãy nói với họ: “Quả thật, vụ việc này đều thuộc về một mình Allah.” Họ (đám người giả tạo đức tin) đã cố giấu kín trong lòng không muốn phơi bày cho Ngươi biết, họ nói: “Giá như chúng tôi được quyền có ý kiến thì chúng tôi đã không phải bị giết chết (oan uổng) tại đây rồi.” Ngươi hãy nói với họ: “Dẫu cho các ngươi có trốn trong nhà của mình thì những ai là người đã được an bày đối mặt với cái chết thì cái chết vẫn tìm đến tận giường ngủ của họ”. Allah muốn thử thách tấm lòng của các ngươi và thanh lọc con tim của các ngươi. Quả thật, Allah là Đấng biết rõ mọi thứ được giấu kín trong lòng (của các ngươi).');
INSERT INTO "vn_ruwwad" VALUES(3,155,'3:155','Quả thật, những ai trong các ngươi quay lưng bỏ chạy vào ngày hai đoàn quân (những người có đức tin và những kẻ vô đức tin) giáp mặt nhau (tại Uhud), chẳng qua là do đã bị Shaytan gây nhiễu lòng. Tuy nhiên, họ đã được Allah quãng đại khoan hồng sau đó. Quả thật Allah hằng dung thứ, hằng chịu đựng.');
INSERT INTO "vn_ruwwad" VALUES(3,156,'3:156','Hỡi những người có đức tin, các ngươi chớ đừng cư xử như những kẻ vô đức tin và chớ như những ai đã nói với các anh em đồng đạo của mình khi họ đi xa (tìm kiếm bổng lộc đây đó) trên mặt đất hoặc xuất chinh: “Giá như họ ở lại với chúng mình thì đâu đến nỗi bị chết hoặc bị giết”. Allah đã khiến điều đó (tư tưởng lệch lạc) trở thành điều tiếc nuối khôn nguôi trong lòng của họ. Quả thật, Allah là Đấng ban sự sống và cái chết. Quả thật, Ngài thấy rõ hết mọi việc các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(3,157,'3:157','Nếu các ngươi bị giết trên con đường chính nghĩa của Allah hoặc chết đi (do hết tuổi) thì sự tha thứ và lòng thương xót của Allah sẽ tốt hơn những gì họ tích góp.');
INSERT INTO "vn_ruwwad" VALUES(3,158,'3:158','Nếu các ngươi chết đi (do hết tuổi) hoặc bị giết thì các ngươi đều được phục sinh để trình diện Allah.');
INSERT INTO "vn_ruwwad" VALUES(3,159,'3:159','Do đó, với lòng nhân từ của Allah thì Ngươi (hỡi Thiên Sứ Muhammad) nên đối xử hiền hòa với họ. Nếu Ngươi quá khắt khe với họ thì chắc chắn họ sẽ rời bỏ Ngươi. Cho nên, Ngươi hãy khoan hồng đại lượng với họ, hãy cầu xin Allah tha thứ cho họ và hãy bàn bạc tham khảo ý kiến với họ. Và khi Ngươi đã quyết định thì hãy phó thác cho Allah, quả thật Allah luôn thương yêu những người biết phó thác (cho Ngài).');
INSERT INTO "vn_ruwwad" VALUES(3,160,'3:160','Nếu Allah phù hộ các ngươi giành chiến thắng thì chắc chắn không có thế lực nào thắng được các ngươi, còn nếu Allah bỏ mặc các ngươi thì chắc chắn không một ai có thể giúp các ngươi giành lấy chiến thắng. Vì vậy, những người có đức tin hãy phó thác cho Allah.');
INSERT INTO "vn_ruwwad" VALUES(3,161,'3:161','Không một Nabi nào được phép giấu trộm chiến lợi phẩm. Người nào giấu trộm chiến lợi phẩm thì y sẽ mang vật giấu trộm đó ra trình diện Allah vào Ngày Phán Xét. Rồi đây mỗi linh hồn đều được trả lại đầy đủ cho mọi thứ mà y đã tom góp (trước đây) và không ai bị đối xử bất công.');
INSERT INTO "vn_ruwwad" VALUES(3,162,'3:162','Phải chăng người theo đuổi sự hài lòng của Allah lại giống như kẻ trở về trong cơn thịnh nộ của Ngài và nơi ở của y sẽ là Ngục Lửa, một nơi ở tồi tệ?!');
INSERT INTO "vn_ruwwad" VALUES(3,163,'3:163','Họ hoàn toàn khác nhau về cấp bậc ở nơi Allah. Quả thật, Allah hằng thấy mọi việc họ làm.');
INSERT INTO "vn_ruwwad" VALUES(3,164,'3:164','Allah quả thật đã ban hồng phúc cho những người có đức tin khi Ngài đã gởi đến với họ một vị Thiên Sứ xuất thân từ họ để đọc cho họ nghe các lời mặc khải của Ngài, để thanh lọc họ, để dạy họ Kinh Sách (Qur’an) và sự khôn ngoan (Sunnah) trong khi trước đây họ hoàn toàn sống trong sự lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(3,165,'3:165','Lẽ nào sau khi các ngươi (những người có đức tin) bị thảm bại (tại Uhud khiến các ngươi nản lòng) mặc dầu trước đó (tại Badr) các ngươi đã đánh bại (kẻ thù) với sự thiệt hại gấp đôi, các ngươi lại nói: “Tai họa này đến từ đâu?” Ngươi (hỡi Thiên Sứ Muhammad) hãy bảo họ: “Tai họa đó là do chính bản thân các ngươi gây ra (khi các ngươi nghịch lại mệnh lệnh của Thiên Sứ rời khỏi núi Rumah).” Quả thật, Allah toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(3,166,'3:166','Những gì đã xảy ra với các ngươi (từ sự thiệt hại và tổn thất nặng nề) vào ngày mà hai đoàn quân chạm trán nhau (tại Uhud) là do sự cho phép của Allah để Ngài biết ai là những người có đức tin (thật sự).');
INSERT INTO "vn_ruwwad" VALUES(3,167,'3:167','Và để Ngài biết ai là những kẻ giả tạo đức tin. Những kẻ mà khi có lời gọi họ “Các người hãy đến đây cùng chiến đấu vì con đường chính nghĩa của Allah hoặc hãy đến đây để phòng thủ.” thì họ bảo “Nếu chúng tôi thật sự biết đánh trận thì chúng tôi chắc chắn đã đi theo mọi người.” (Tình trạng của) họ vào ngày hôm đó gần với sự vô đức tin hơn là có đức tin, họ chỉ thốt ra từ cửa miệng những điều không có trong lòng, tuy nhiên, Allah luôn biết rõ điều họ cố giấu giếm.');
INSERT INTO "vn_ruwwad" VALUES(3,168,'3:168','Những người mà họ nói với anh em của mình trong lúc họ ngồi ở nhà (không tham chiến): “Giá như họ nghe lời chúng mình thì đâu bị giết”. Ngươi (hỡi Thiên Sứ Muhammad) hãy nói với bọn họ: “Vậy thì các người hãy cố mà xua đuổi cái chết khỏi bản thân của các ngươi nếu các ngươi đã nói sự thật.”');
INSERT INTO "vn_ruwwad" VALUES(3,169,'3:169','Ngươi (hỡi Thiên Sứ Muhammad) chớ đừng nghĩ rằng những người đã bị giết vì con đường chính nghĩa của Allah đã chết. Ngược lại, họ vẫn đang sống ở nơi Thượng Đế của họ và họ được ban bố bổng lộc (thật đầy đủ).');
INSERT INTO "vn_ruwwad" VALUES(3,170,'3:170','Họ thực sự đang vui hưởng hồng phúc mà Allah ban cấp cho họ và họ báo tin mừng cho những ai chưa nhập đoàn với họ ở đằng sau họ rằng (những người hy sinh vì con đường chính nghĩa của Allah) sẽ không phải lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "vn_ruwwad" VALUES(3,171,'3:171','Họ báo tin mừng về hồng phúc và thiên ân của Allah, và rằng Allah không hề làm mất công lao của những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(3,172,'3:172','Những người mà họ đáp lại lời kêu gọi của Allah và Thiên Sứ (Muhammad) sau khi đã bị thương tích (tại Uhud). Và những người làm tốt và biết kính sợ (Allah) trong số họ sẽ được phần thưởng vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(3,173,'3:173','Những người mà khi thiên hạ nói với họ “Quả thật, những người (ngoại đạo) đang tập hợp tấn công các người, các người hãy nên sợ bọn họ” thì đức tin của họ tăng thêm và họ nói (một cách kiên định): “Một mình Allah đã đủ giúp chúng tôi bởi vì Ngài là Đấng Bảo Hộ Ưu Việt”.');
INSERT INTO "vn_ruwwad" VALUES(3,174,'3:174','Rồi họ đã trở về nhà với hồng phúc và ân huệ của Allah, chẳng hề có một chút rủi ro nào chạm đến họ, và họ thực sự đi theo sự hài lòng của Allah. Quả thật, Allah sở hữu nguồn thiên lộc vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(3,175,'3:175','Quả thật, đó chẳng qua là mưu kế của bọn Shaytan nhằm hù dọa đồng bọn của hắn mà thôi. Vì vậy, các ngươi chớ đừng sợ chúng mà hãy sợ TA (Allah) nếu các ngươi là những người có đức tin (thật sự).');
INSERT INTO "vn_ruwwad" VALUES(3,176,'3:176','Ngươi (Thiên Sứ Muhammad) đừng để cho bản thân bị buồn rầu trước việc những kẻ nhanh chân lao thân vào sự vô đức tin. Quả thật họ sẽ không bao giờ gây hại được Allah. Allah không muốn cho họ hưởng bất cứ gì ở Đời Sau và họ phải đối mặt với một sự trừng phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(3,177,'3:177','Quả thật, những kẻ đã lấy đức tin để mua sự vô đức tin sẽ chẳng bao giờ gây hại được Allah, (ngược lại) họ sẽ bị trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(3,178,'3:178','Những kẻ vô có đức tin chớ đừng nghĩ rằng việc TA trì hoãn cho họ (sống thọ) là điều tốt dành cho bản thân họ. Chẳng qua TA trì hoãn (việc trừng phạt) là để cho họ gia tăng thêm tội lỗi để rồi đây họ sẽ phải chịu một sự trừng phạt nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(3,179,'3:179','Allah sẽ không bỏ mặc những người có đức tin các ngươi trong hoàn cảnh mà các ngươi đang hiện sống mà Ngài sẽ tách biệt cái xấu ra khỏi cái tốt. Allah cũng không cho các ngươi biết được điều vô hình, tuy nhiên, Ngài muốn lựa chọn ai trong số các Thiên Sứ của Ngài (tiếp thu một số điều vô hình) là tùy ý Ngài. Cho nên, các ngươi hãy tin nơi Allah và các Thiên Sứ của Ngài. Nếu các ngươi có đức tin và ngoan đạo thì các ngươi được ban thưởng một phần thưởng vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(3,180,'3:180','Những kẻ keo kiệt chớ đừng nghĩ rằng việc họ ôm trọn hết thiên lộc mà Allah đã ban cho họ (không chịu xuất Zakah) là điều tốt cho họ, ngược lại, đó là điều xấu cho họ. Rồi đây vào Ngày Phán Xét, những gì mà họ đã keo kiệt giữ lại sẽ được mang ra treo lủng lẳng trên cổ họ. Quả thật, mọi di sản trong các tầng trời và trái đất đều thuộc về một mình Allah. Quả thật, Allah thông toàn mọi điều các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(3,181,'3:181','Quả thật, Allah đã nghe lời của những kẻ (Do Thái) đã nói: “Allah nghèo còn chúng tôi giàu.” TA (Allah) sẽ ghi lại điều mà họ đã nói cũng như việc họ giết các vị Nabi một cách vô cớ, và (rồi đây) TA sẽ phán bảo: “Các ngươi hãy nếm lấy hình phạt thiêu đốt.”');
INSERT INTO "vn_ruwwad" VALUES(3,182,'3:182','Đấy là (hậu quả) cho những gì mà đôi bàn tay của các ngươi đã gửi đi trước và Allah không hề bất công với bất cứ người bề tôi nào (của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(3,183,'3:183','Những kẻ đã nói: “Quả thật, Allah đã giao ước với chúng tôi rằng chúng tôi đừng nên (vội vàng) tin nơi bất cứ vị Thiên Sứ nào trừ phi vị đó mang đến trình trước chúng tôi một vật hiến tế (cho Allah) và được lửa (phóng xuống từ trời) thiêu đốt nó.” Ngươi (hỡi Thiên Sứ Muhammad) hãy bảo họ: “Quả thật, các Thiên Sứ trước Ta đã đến với các người, họ đã mang đến cho các người các bằng chứng rõ rệt và luôn cả điều mà các người đã nói, nếu các người đã nói lời thật lòng thế thì tại sao các người (vẫn cố tình) sát hại họ?!”');
INSERT INTO "vn_ruwwad" VALUES(3,184,'3:184','Nếu như họ (những kẻ vô đức tin) phủ nhận Ngươi (Thiên Sứ Muhammad) thì quả thật các vị Thiên Sứ trước Ngươi cũng đã bị phủ nhận (giống như vậy) mặc dù họ đã mang đến biết bao bằng chứng rõ rệt cùng với Kinh Zabur và Kinh Sách rõ ràng, minh bạch.');
INSERT INTO "vn_ruwwad" VALUES(3,185,'3:185','Mỗi linh hồn đều phải nếm cái chết. Quả thật vào Ngày Phán Xét, các ngươi mới được trao cho đầy đủ phần công lao của mình. Do đó, ai được cứu khỏi Hỏa Ngục và được thu nhận vào Thiên Đàng thì y thực sự đã thành công. Và đời sống trần tục này chỉ là một sự hưởng thụ chóng tàn, đầy dối trá.');
INSERT INTO "vn_ruwwad" VALUES(3,186,'3:186','Tất nhiên các ngươi (những người có đức tin) phải trải qua thử thách về tài sản và thể xác của các ngươi, các ngươi phải nghe rất nhiều lời lẽ xấu xa từ những kẻ được ban cho Kinh Sách thời trước các ngươi và cả những kẻ đa thần. Tuy nhiên, nếu các ngươi biết kiên nhẫn và ngoan đạo thì đó là nguyên tố (thành công) của mọi vấn đề.');
INSERT INTO "vn_ruwwad" VALUES(3,187,'3:187','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại) việc TA (Allah) đã lấy giao ước với những người được ban cho Kinh Sách: “Các ngươi phải công bố Kinh Sách cho thiên hạ và tuyệt đối không được giấu giếm (những bằng chứng báo trước về Muhammad) có trong đó”. Tuy nhiên, họ đã ném lời giao ước ra sau lưng và dùng nó mua lấy phần lợi lộc ít ỏi (của trần gian). Thật tồi tệ thay cho việc đổi chác của họ.');
INSERT INTO "vn_ruwwad" VALUES(3,188,'3:188','Ngươi (hỡi Thiên Sứ Muhammad) đừng nghĩ rằng những kẻ hớn hở vui mừng với điều được ban cho (lúc họ làm tội lỗi) và yêu thích được mọi người ca tụng về những điều mà họ không làm (là điều tốt cho họ). Ngươi cũng chớ đừng tưởng họ sẽ thoát khỏi sự trừng phạt, ngược lại, họ sẽ gặp phải sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(3,189,'3:189','Quyền thống trị và chế ngự các tầng trời và trái đất đều thuộc về một mình Allah, và Allah toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(3,190,'3:190','Quả thật, việc tạo hóa các tầng trời và trái đất cũng như việc luân chuyển ban đêm và ban ngày là những dấu hiệu dành cho những người hiểu biết.');
INSERT INTO "vn_ruwwad" VALUES(3,191,'3:191','Họ là những người (thường xuyên) tụng niệm Allah lúc đứng, lúc ngồi và ngay cả lúc nằm nghiêng một bên, họ thường suy ngẫm (về quyền năng của Allah) trong việc tạo hóa các tầng trời và trái đất (khiến họ phải thốt lên): “Lạy Thượng Đế của bầy tôi, Ngài đã không tạo hóa những thứ này một cách vô nghĩa. Quang vinh và trong sạch thay Ngài! Xin Ngài hãy cứu bầy tôi tránh khỏi sự trừng phạt nơi Hỏa Ngục.”');
INSERT INTO "vn_ruwwad" VALUES(3,192,'3:192','“Lạy Thượng Đế của bầy tôi, người nào đã bị Ngài tống vào Hỏa Ngục thì đó là kẻ đã bị Ngài sỉ nhục. Quả thật, những kẻ làm điều sai trái sẽ không được ai giúp đỡ.”');
INSERT INTO "vn_ruwwad" VALUES(3,193,'3:193','“Lạy Thượng Đế của bầy tôi, bầy tôi đã nghe vị mời gọi kêu gọi đến với đức tin rằng hãy tin nơi Thượng Đế của các người và bầy tôi đã tin tưởng. Lạy Thượng Đế của bầy tôi, xin Ngài hãy tha thứ tội lỗi cho bầy tôi, xin hãy xí xóa mọi việc làm sai trái mà bầy tôi đã phạm, và xin hãy cho bầy tôi được chết cùng với những người đức hạnh.”');
INSERT INTO "vn_ruwwad" VALUES(3,194,'3:194','“Lạy Thượng Đế của bầy tôi, xin Ngài ban cho bầy tôi những thứ mà Ngài đã hứa với bầy tôi qua (lời nói của) các vị Thiên Sứ của Ngài và xin đừng sỉ nhục bầy tôi vào Ngày Phán Xét. Quả thật, Ngài không bao giờ thất hứa.”');
INSERT INTO "vn_ruwwad" VALUES(3,195,'3:195','Thế là họ được Thượng Đế của họ đáp lại lời cầu nguyện đó, (Ngài phán): “TA nhất định không làm mất bất cứ việc hành đạo nào mà các ngươi đã từng làm, (không phân biệt) nam hay nữ, tất cả đều được ban thưởng như nhau. Cho nên, những ai đã di cư hoặc đã bị trục xuất ra khỏi nhà cửa của mình và đã chịu hoạn nạn đau khổ vì chính nghĩa của TA, và họ đã anh dũng chiến đấu và đã hi sinh thì chắc chắn họ sẽ được TA xóa bỏ hết mọi tội lỗi và được thu nhận vào những Ngôi Vườn Thiên Đàng bên dưới có các dòng sông chảy, một phần thưởng xứng đáng từ Allah. Quả thật, ở nơi Allah luôn có những phần thưởng tốt nhất.”');
INSERT INTO "vn_ruwwad" VALUES(3,196,'3:196','Ngươi (hỡi Thiên Sứ Muhammad) chớ đừng bị mắc lừa bởi việc đi lại hí hửng của những kẻ ngoại đạo trên mặt đất.');
INSERT INTO "vn_ruwwad" VALUES(3,197,'3:197','(Đó chỉ là) một sự hưởng thụ tạm bợ và chóng tàn, rồi đây chốn ở của họ sẽ là Hỏa Ngục và đó là một nơi cư ngụ vô cùng tồi tệ.');
INSERT INTO "vn_ruwwad" VALUES(3,198,'3:198','Ngược lại, những ai ngoan đạo với Thượng Đế của họ thì sẽ được ban thưởng những Ngôi Vườn Thiên Đàng bên dưới có các dòng sông chảy, họ sẽ ở trong đó mãi mãi, một sự chiêu đãi đặc biệt từ nơi Allah. Quả thật, những gì ở nơi Allah là tốt nhất cho những người đức hạnh.');
INSERT INTO "vn_ruwwad" VALUES(3,199,'3:199','Trong số những người dân Kinh Sách vẫn có người tin tưởng nơi Allah, tin tưởng vào điều đã mặc khải cho các ngươi (những người có đức tin) và tin vào điều đã được mặc khải cho họ (trước đây), họ đã hạ mình khiêm tốn trước Allah, họ không bán các lời mặc khải của Allah với cái giá ít ỏi (của trần gian). Đó là những người mà phần thưởng của họ ở nơi Thượng Đế của họ. Quả thật Allah rất nhanh chóng trong việc tính sổ (thưởng phạt).');
INSERT INTO "vn_ruwwad" VALUES(3,200,'3:200','Hỡi những người có đức tin, các ngươi hãy thực sự kiên nhẫn, hãy thực sự kiên trì, hãy kiên định vững chắc, và hãy kính sợ Allah, mong rằng các ngươi sẽ thành công.');
INSERT INTO "vn_ruwwad" VALUES(4,1,'4:1','Hỡi con người, hãy kính sợ Thượng Đế của các ngươi, Đấng đã tạo hóa các ngươi từ một cá thể duy nhất (ông tổ Adam của các ngươi) và từ Y Ngài đã tạo ra người vợ (Hauwa) của Y, rồi từ hai người họ, Ngài đã rải ra khắp nơi (trên trái đất) vô số đàn ông và phụ nữ. Các ngươi hãy kính sợ Allah, Đấng mà với Ngài các ngươi đòi hỏi (quyền và lẽ phải) lẫn nhau( và các ngươi (hãy tôn trọng) mối quan hệ thân tộc(4), quả thật Allah luôn giám sát (mọi hành vi của) các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(4,2,'4:2','Các ngươi hãy giao lại cho trẻ mồ côi tài sản của chúng (khi chúng đã trưởng thành). Các ngươi chớ đừng (vì tham lam) mà tráo vật xấu của các ngươi để đối lấy vật tốt của chúng. Các ngươi cũng đừng ăn chặn (tài sản của chúng bằng cách) nhập chung tài sản của chúng vào tài sản của các ngươi, quả thật việc làm đó là một đại trọng tội.');
INSERT INTO "vn_ruwwad" VALUES(4,3,'4:3','Nếu các ngươi lo sợ không công bằng với (phụ nữ) mồ côi (khi cưới họ làm vợ)( thì các ngươi hãy cưới những phụ nữ khác (ngoài họ) mà các ngươi hài lòng; (các ngươi có thể cưới) hai, ba hoặc bốn (vợ). Tuy nhiên, nếu các ngươi lo sợ không thể (đối xử) công bằng (với các bà vợ) thì các ngươi hãy nên cưới một (vợ) hoặc (các ngươi có thể ăn nằm) với những phụ nữ dưới tay của các ngươi(6). Đó là cách tốt nhất để các ngươi tránh gây bất công.');
INSERT INTO "vn_ruwwad" VALUES(4,4,'4:4','Các ngươi phải trao tiền cưới bắt buộc cho phụ nữ (khi kết hôn). Tuy nhiên, nếu họ vui lòng tặng lại các ngươi một phần nào (từ tiền cưới bắt buộc đó) thì các ngươi hãy vui hưởng nó một cách hữu ích.');
INSERT INTO "vn_ruwwad" VALUES(4,5,'4:5','Các ngươi (những người giám hộ) chớ đừng giao cho những người thiếu khôn ngoan( tài sản mà Allah đã cho các ngươi quyền quản lý. Các ngươi hãy dùng tài sản đó cấp dưỡng cho họ, cấp quần áo cho họ và hãy nói năng tử tế với họ.');
INSERT INTO "vn_ruwwad" VALUES(4,6,'4:6','Các ngươi hãy kiểm tra( các trẻ mồ côi khi chúng đã đến tuổi trưởng thành, nếu các ngươi nhận thấy chúng đã khôn ngoan thì các ngươi hãy giao lại tài sản của chúng cho chúng. Các ngươi chớ đừng tiêu xài tài sản của chúng một cách phung phí vì lo sợ chúng lấy lại tài sản sau khi đã trưởng thành. (Người giám hộ) nào đã giàu có (hoặc dư dả) thì chớ tiêu pha nó, còn (người giám hộ) nào nghèo khó thì hãy chi dùng nó một cách vừa phải (không tiêu pha lãng phí). Khi các ngươi giao lại tài sản của chúng cho chúng thì các ngươi hãy tìm những người làm nhân chứng cho cuộc giao trả đó. Quả thật, một mình Allah đã đủ thanh toán và xét xử (mọi việc làm của các ngươi).');
INSERT INTO "vn_ruwwad" VALUES(4,7,'4:7','Đàn ông được quyền hưởng gia tài của cha mẹ và bà con ruột thịt để lại (sau khi họ qua đời), và phụ nữ cũng được quyền hưởng gia tài của cha mẹ và bà con ruột thịt để lại (sau khi họ qua đời), dù ít hay nhiều(. (Đó là) giáo luật bắt buộc (từ Allah).');
INSERT INTO "vn_ruwwad" VALUES(4,8,'4:8','Trong lúc các ngươi phân chia gia tài để lại, nếu có mặt của bà con họ hàng, trẻ mồ côi và người nghèo thì các ngươi hãy chia cho họ một ít (từ gia tài đó) và các ngươi hãy nói năng tử tế với họ.');
INSERT INTO "vn_ruwwad" VALUES(4,9,'4:9','Những người (đứng ra chia gia tài thừa kế) cũng phải nên sợ rằng mai này (mình chết đi) bỏ lại con cái thơ dại (không ai chăm sóc và trông nom). Cho nên, họ hãy kính sợ Allah mà nói điều ngay thẳng và đúng đắn.');
INSERT INTO "vn_ruwwad" VALUES(4,10,'4:10','Những kẻ ăn tài sản của trẻ mồ côi một cách bất công, thật ra là họ đang nuốt lửa vào bụng của mình; rồi đây họ sẽ sớm bị thiêu đốt trong Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(4,11,'4:11','Allah quy định cho các ngươi về việc con cái của các ngươi (hưởng gia tài thừa kế) như sau: Con trai hưởng gấp đôi phần của con gái. Trường hợp (người chết bỏ lại con cái) đều là gái, nếu từ hai người trở lên thì tất cả cùng hưởng hai phần ba (3) gia tài để lại, còn nếu chỉ có một người duy nhất thì sẽ hưởng một nửa gia tài để lại. Nếu (người chết) có con thì cha mẹ, mỗi người hưởng một phần sáu (1/6) tài sản để lại. Nếu (người chết) không có con thì cha mẹ hưởng toàn bộ gia tài để lại, mẹ hưởng một phần ba (1/3), (còn lại là của cha). Trường hợp (người chết) có anh em thì mẹ của y hưởng một phần sáu (1/6)(10). (Tuy nhiên, việc phân chia này) được thực hiện sau khi đã hoàn tất xong phần di chúc(11) hoặc nợ. Cha mẹ của các ngươi, con cái của các ngươi, các ngươi không hề biết được ai trong số họ mới thật sự hữu ích cho các ngươi (ở trần gian và Đời Sau). (Đó là) luật định từ Allah. Quả thật, Allah là Đấng Hằng Biết, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(4,12,'4:12','Các ngươi (những người chồng) hưởng một nửa (2) gia tài mà các người vợ để lại nếu họ không có con, nếu họ có con thì các ngươi hưởng một phần tư (1/4) gia tài họ để lại; (tuy nhiên, việc phân chia này) được thực hiện sau khi đã hoàn tất xong phần di chúc hoặc nợ. Các bà vợ hưởng một phần tư (1/4) gia tài mà các ngươi để lại nếu các ngươi không có con, nếu các ngươi có con thì họ hưởng một phần tám (1/8) gia tài mà các ngươi để lại; (tuy nhiên, việc phân chia này) được thực hiện sau khi đã hoàn tất xong phần di chúc hoặc nợ. Trường hợp một người đàn ông hoặc một người phụ nữ qua đời (cả hai đều không có con cái lẫn cha mẹ) mà chỉ có một người anh (em) trai và một người chị (em) gái thì mỗi người hưởng một phần sáu (1/6); nếu số lượng anh chị em nhiều hơn (hai người) thì tất cả cùng hưởng chung một phần ba (1/3)(12), (tuy nhiên, việc phân chia này) được thực hiện sau khi đã hoàn tất xong phần di chúc hoặc nợ, miễn sao không gây thiệt thòi cho những người thừa kế. (Đó là) luật định từ Allah. Quả thật, Allah là Đấng Hằng Biết, Đấng Hằng Chịu Đựng.');
INSERT INTO "vn_ruwwad" VALUES(4,13,'4:13','Đó là giới luật của Allah. Người nào tuân lệnh Allah và Thiên Sứ (Muhammad) của Ngài thì y sẽ được thu nhận vào những Ngôi Vườn Thiên Đàng bên dưới có các dòng sông chảy, họ sẽ ở trong đó mãi mãi, và đó là một thành tựu vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(4,14,'4:14','Ngược lại, kẻ nào nghịch Allah và Thiên Sứ (Muhammad) của Ngài, vi phạm các giới luật của Ngài thì sẽ bị tống vào Hỏa Ngục, y sẽ ở trong đó mãi mãi và sẽ bị trừng phạt một cách nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(4,15,'4:15','Những ai trong số phụ nữ của các ngươi làm điều ô uế (thông dâm) thì các ngươi hãy đưa ra bốn nhân chứng để buộc tội họ. Nếu (cả bốn nhân chứng) đều đồng xác nhận thì các ngươi hãy giam họ trong nhà cho đến chết( hoặc Allah sẽ mở cho họ một lối thoát khác.');
INSERT INTO "vn_ruwwad" VALUES(4,16,'4:16','Hai người (đàn ông và đàn bà) trong các ngươi phạm tội thông dâm thì các ngươi hãy phạt họ thật nặng(. Tuy nhiên, khi cả hai biết ăn năn hối cải và chịu sửa mình thì các ngươi hãy bỏ mặc họ. Quả thật Allah hằng chấp nhận sự sám hối, Ngài là Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(4,17,'4:17','Thật ra Allah chỉ chấp nhận sự ăn năn hối cải của những ai làm tội lỗi trong lúc không nhận thức rõ vấn đề, còn khi (họ nhận thức được sự sai trái) thì họ liền ăn năn sám hối. Đó là những người sẽ được Allah tha thứ cho họ, bởi Allah là Đấng Hằng Biết, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(4,18,'4:18','Ăn năn hối cải sẽ không được chấp nhận đối với những ai cứ ngoan cố làm điều tội lỗi cho tới khi sắp chết thì mới nói: “Bây giờ bề tôi xin ăn năn sám hối”; và (ăn năn hối cải) cũng không được chấp nhận đối với những người chết trong tình trạng vô đức tin. Đó là những kẻ mà TA đã chuẩn bị một hình phạt đau đớn dành cho họ.');
INSERT INTO "vn_ruwwad" VALUES(4,19,'4:19','Hỡi những người có đức tin, các ngươi không được thừa hưởng các phụ nữ một cách cưỡng bức(, các ngươi cũng không được giam họ lại hầu để lấy lại phần tiền cưới mà các ngươi đã trao cho họ trừ phi họ công khai làm điều ô uế, còn không thì các ngươi phải đối xử tử tế với họ. Bởi lẽ nếu các ngươi ghét bỏ họ thì e rằng các ngươi đã ghét bỏ một thứ mà Allah đã ban cho nó bao điều tốt đẹp.');
INSERT INTO "vn_ruwwad" VALUES(4,20,'4:20','Khi các ngươi (những người chồng) muốn li hôn vợ (hiện tại) để cưới người vợ khác và các ngươi đã trao tặng mỗi người vợ một đống vàng thì các ngươi không được lấy lại bất cứ thứ gì từ tiền cưới đó. Lẽ nào các ngươi muốn lấy lại nó bằng cách vu khống họ hoặc công khai phạm tội ư?');
INSERT INTO "vn_ruwwad" VALUES(4,21,'4:21','Làm sao mà các ngươi có thể lấy lại phần tiền cưới đó trong khi các ngươi đã ăn nằm với họ và họ đã nhận lấy lời giao ước long trọng( của các ngươi?!');
INSERT INTO "vn_ruwwad" VALUES(4,22,'4:22','Các ngươi không được kết hôn với những người phụ nữ mà cha của các ngươi đã cưới (trước đây), ngoại trừ sự việc đã xảy ra (trước lệnh cấm này). Quả thật, đó là một con đường ô uế và bẩn thỉu.');
INSERT INTO "vn_ruwwad" VALUES(4,23,'4:23','Các ngươi bị cấm (cưới các phụ nữ sau đây): mẹ; con gái; chị (em) gái; cô; dì; cháu gái - con anh (em) trai hoặc chị (em) gái; mẹ nuôi (đã cho bú vú); chị (em) gái nuôi bú cùng một vú; mẹ vợ; con gái riêng của vợ mà các ngươi đã ăn nằm, dù đang sống cùng nhà với các ngươi (hoặc ở riêng bên ngoài), trường hợp các ngươi chưa ăn nằm với người vợ đó thì các ngươi không có tội (nếu li hôn cô ta để cưới con riêng của cô ta); con dâu - vợ của con ruột. (Và các ngươi bị cấm) cưới hai chị em cùng lúc (hoặc hai cô cháu hoặc hai dì cháu ruột), ngoại trừ trường hợp đã lỡ xảy ra trong quá khứ. Quả thật, Allah là Đấng Nhân Từ, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(4,24,'4:24','Và (cấm các ngươi cưới) các phụ nữ đã có chồng ngoại trừ những nữ tù binh trong tay của các ngươi(. Đó là lệnh cấm của Allah qui định cho các ngươi. Ngoài những phụ nữ đã liệt kê thì các ngươi được phép cưới bất kỳ phụ nữ nào khác miễn sao các ngươi hài lòng cưới họ bằng tài sản của các ngươi một cách đàng hoàng (hợp thức luật hôn nhân mà Allah qui định) chứ không bằng hình thức ô uế (tình dục ngoài hôn nhân). Một khi các ngươi đã hưởng lạc từ (vợ của các ngươi) thì các ngươi phải trao tặng cho họ phần tiền cưới bắt buộc. Tuy nhiên, các ngươi sẽ không bị tội nếu đôi bên tự nguyện (cho thêm hoặc giảm bớt phần nào) số tiền cưới bắt buộc sau khi đã thống nhất trước đó. Quả thật, Allah là Đấng Hăng Biết, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(4,25,'4:25','Ai trong các ngươi (do nghèo) không đủ khả năng cưới các phụ nữ tự do có đức tin thì các ngươi hãy cưới các phụ nữ nô lệ có đức tin nằm trong tay của các ngươi (hoặc trong tay người khác), Allah biết rõ đức tin của các ngươi nhất là khi các ngươi đều là tín đồ (Muslim). Do đó, các ngươi hãy cưới các nữ nô lệ đó dưới sự đồng ý từ chủ nhân của họ và các ngươi hãy trao tặng họ tiền cưới đàng hoàng đúng lễ nghĩa giống như những phụ nữ được cưới hỏi đàng hoàng chứ không như phụ nữ ngoại tình hay tình nhân lén lút. Trường hợp các nữ nô lệ đó làm điều ô nhục sau khi đã kết hôn thì họ chỉ bị trừng phạt bằng một nửa hình phạt của những phụ nữ tự do. (Việc cho phép cưới nữ nô lệ) là giải pháp dành cho những ai trong các ngươi sợ bản thân mình phạm điều tội lỗi (quan hệ tình dục không hợp thức), tuy nhiên, nếu các ngươi biết kiềm chế bản thân mình thì điều đó sẽ tốt hơn cho các ngươi. Quả thật, Allah hằng tha thứ, hằng khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(4,26,'4:26','Allah muốn trình bày rõ ràng cho các ngươi hiểu (các giáo luật của Ngài) và Ngài muốn hướng dẫn các ngươi đến với các chỉ đạo (của các vị Nabi) thời trước các ngươi và Ngài muốn tha thứ cho các ngươi. Quả thật, Allah hằng biết, hằng sáng suốt.');
INSERT INTO "vn_ruwwad" VALUES(4,27,'4:27','Allah thực sự muốn tha thứ cho các ngươi nhưng những kẻ theo đuổi dục vọng thì muốn kéo các ngươi sa ngã quá mức.');
INSERT INTO "vn_ruwwad" VALUES(4,28,'4:28','Allah thực sự muốn giảm nhẹ (tội lỗi) khỏi các ngươi bởi (Ngài biết rõ) con người vốn được tạo ra yếu đuối (dễ sa ngã).');
INSERT INTO "vn_ruwwad" VALUES(4,29,'4:29','Hỡi những người có đức tin, các ngươi chớ đừng ăn tài sản lẫn nhau một cách bất chính trừ phi đó là cuộc mua bán có sự đồng ý giữa đôi bên. Và các ngươi chớ đừng giết hại bản thân các ngươi bởi quả thật Allah luôn nhân từ với các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(4,30,'4:30','Người nào làm thế (ăn chặn tài sản của người khác một cách bất chính) do thù hằn và bất công thì sẽ sớm bị TA tống cổ vào Ngục Lửa, và điều đó đối với Allah rất dễ dàng.');
INSERT INTO "vn_ruwwad" VALUES(4,31,'4:31','Nếu các ngươi tránh được những đại tội đã nghiêm cấm thì TA sẽ xí xóa cho các ngươi những tội lỗi khác và sẽ thu nhận các ngươi vào một nơi cư ngụ vô cùng vinh dự (Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(4,32,'4:32','Các ngươi chớ đừng thèm khát những thứ mà Allah dùng để ưu đãi người này hơn người kia. Những người đàn ông sẽ hưởng phần mà họ đã tích lũy được và những người phụ nữ cũng sẽ hưởng phần mà họ đã tích lũy được, và các ngươi hãy cầu xin Allah thiên lộc của Ngài, quả thật, Allah hằng biết tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(4,33,'4:33','Mỗi người đều được TA quy định cho phần thừa kế mà cha mẹ và họ hàng (chết) để lại; và những ai mà các ngươi đã thề thốt với nhau thì các ngươi hãy đưa cho họ phần thừa kế(. Quả thật, Allah chứng giám cho tất cả mọi sự việc.');
INSERT INTO "vn_ruwwad" VALUES(4,34,'4:34','Đàn ông là trụ cột (của gia đình) hơn phụ nữ (một bậc) bởi những gì Allah đã ban cho người này vượt trội hơn người kia (về sức lực) và bởi đàn ông phải chịu trách nhiệm chu cấp (cho gia đình) từ nguồn tài sản của họ. Cho nên, người phụ nữ đức hạnh là người biết vâng lời chồng và trông coi (tài sản của chồng) khi chồng vắng mặt dưới sự phù hộ và giúp đỡ của Allah. Còn các phụ nữ mà các ngươi lo sợ cho sự bướng bỉnh của họ (có thể dẫn đến hành vi sai trái) thì trước hết các ngươi hãy khuyên bảo họ, (nếu họ không thay đổi) thì các người hãy từ chối ăn nằm với họ, còn (nếu như sau đó họ vẫn ngoan cố) các ngươi hãy đánh đòn họ (nhưng không đánh vào vùng mặt hoặc gây thương tích); nhưng nếu họ đã chịu nghe lời các ngươi thì các ngươi chớ gây chuyện với họ. Quả thật, Allah là Đấng Tối Thượng, Đấng Vĩ Đại.');
INSERT INTO "vn_ruwwad" VALUES(4,35,'4:35','Nếu các ngươi (những người thân của hai vợ chồng) lo sợ hai (vợ chồng) chia tay thì các ngươi hãy cử một đại diện của bên chồng và một đại diện của bên vợ đến hòa giải. Nếu cả hai (vợ chồng) thật lòng muốn hòa thuận với nhau thì Allah sẽ phù hộ cho họ hòa thuận với nhau trở lại. Quả thật, Allah là Đấng Hằng Biết, Đấng Thông Toàn.');
INSERT INTO "vn_ruwwad" VALUES(4,36,'4:36','Các ngươi hãy thờ phượng Allah và chớ Shirk với Ngài bất cứ thứ gì; các ngươi hãy ăn ở tử tế với cha mẹ, với bà con ruột thịt, với trẻ mồ côi, với người thiếu thốn, với xóm giềng gần xa, với bạn bè, với người lỡ đường và với những nô lệ trong tay các ngươi. Quả thật, Allah không yêu thương những kẻ kiêu căng, tự phụ.');
INSERT INTO "vn_ruwwad" VALUES(4,37,'4:37','Những kẻ keo kiệt và xúi giục thiên hạ keo kiệt (giống như mình) và luôn che giấu những thiên lộc được Allah ban cho họ (thì hãy biết rằng) TA đã chuẩn bị cho những kẻ vô đức tin một sự trừng phạt nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(4,38,'4:38','(Và sự trừng phạt còn dành cho) những ai chi dùng tài sản của mình (cho con đường chính nghĩa của Allah) chỉ muốn phô trương cho thiên hạ thấy chứ không thực sự có đức tin nơi Allah và Đời Sau. Và người nào chọn lấy Shaytan làm bạn thì (hãy biết rằng) hắn là một người bạn xấu nhất.');
INSERT INTO "vn_ruwwad" VALUES(4,39,'4:39','Họ nào có thiệt thòi gì đâu nếu họ thực sự có đức tin nơi Allah và Đời Sau cũng như chi dùng những bổng lộc mà Allah đã ban cho họ (vào con đường chính nghĩa của Allah)?! Quả thật, Allah hằng biết về họ.');
INSERT INTO "vn_ruwwad" VALUES(4,40,'4:40','Chắc chắn Allah không hề bất công (cho ai về bất cứ điều gì) dù (điều đó) chỉ nhỏ bằng hạt nguyên tử đi chăng nữa. Nếu có được một điều tốt nào, Ngài sẽ nhân nó lên gấp bội và Ngài sẽ ban thêm một phần thưởng vĩ đại khác.');
INSERT INTO "vn_ruwwad" VALUES(4,41,'4:41','Thế (vào Ngày Tận Thế) sẽ như thế nào khi TA mang ra cho mỗi cộng đồng một nhân chứng và TA mang Ngươi (Thiên Sứ Muhammad) ra làm nhân chứng cho đám người này (những kẻ vô đức tin)?!');
INSERT INTO "vn_ruwwad" VALUES(4,42,'4:42','Vào Ngày (Phán Xét), những kẻ vô đức tin và nghịch lại Thiên Sứ (Muhammad) sẽ ước ao được biến thành (cát bụi) hòa lẫn vào đất; và không một lời nói (cũng như hành vi) nào của họ có thể giấu giếm được Allah.');
INSERT INTO "vn_ruwwad" VALUES(4,43,'4:43','Hỡi những người có đức tin, các ngươi chớ đến gần lễ nguyện Salah trong lúc các ngươi đang say rượu cho đến khi các ngươi biết rõ điều các ngươi nói ra (tỉnh táo trở lại); các ngươi cũng chớ (dâng lễ nguyện Salah và ở trong Masjid) lúc còn trong tình trạng Junub( ngoại trừ việc chỉ đi ngang qua (Masjid mà thôi) cho đến khi các ngươi đã tắm rửa. Trường hợp các ngươi bị bệnh hoặc đang trên đường đi xa hoặc ai đó trong các ngươi từ nhà vệ sinh trở ra hoặc đã ăn nằm với vợ nhưng không tìm thấy nước (để tắm) thì hãy Tayammum(4) trên đất bụi sạch bằng cách lau mặt và hai tay của các ngươi. Quả thật, Allah hằng khoan dung, hằng tha thứ.');
INSERT INTO "vn_ruwwad" VALUES(4,44,'4:44','Lẽ nào Ngươi (hỡi Thiên Sứ Muhammad) không biết về những kẻ được ban cho (một ít) kiến thức về Kinh Sách (người Do Thái) đã (lấy nguồn chỉ đạo) mua sự lầm lạc và muốn lôi kéo các ngươi (những người có đức tin) lệch khỏi con đường (chân lý) ư?!');
INSERT INTO "vn_ruwwad" VALUES(4,45,'4:45','Allah biết rõ kẻ thù của các ngươi hơn cả các ngươi (hỡi những người có đức tin). Chỉ một mình Allah thôi đã đủ sức bảo hộ và chỉ một mình Allah thôi đã đủ sức giúp (các ngươi giành chiến thắng trước kẻ thù).');
INSERT INTO "vn_ruwwad" VALUES(4,46,'4:46','Trong cộng đồng Do Thái, có những người đã cố ý bóp méo lời phán (của Allah) lệch khỏi (ý nghĩa mà Ngài đã mặc khải). Họ nói: “Chúng tôi đã nghe nhưng không tuân theo; hãy nghe nhưng hãy nghe khác đi”; và họ nói: “Raa’ina”( với chiếc lưỡi của họ nhằm chế nhạo tôn giáo (Islam). Phải chỉ họ: “Chúng tôi đã nghe và vâng lệnh” (hoặc phải chỉ họ nói): “Xin hãy lắng nghe (chúng tôi)” thì điều đó tốt hơn cho họ và đúng đắn hơn. Tuy nhiên, bởi sự vô đức tin của họ, Allah đã nguyền rủa(6) họ nên họ đã không có đức tin ngoại trừ một số ít.');
INSERT INTO "vn_ruwwad" VALUES(4,47,'4:47','Hỡi những kẻ được ban cho Kinh Sách (Do Thái và Thiên Chúa), các ngươi hãy tin tưởng vào điều mà TA đã mặc khải (cho Thiên Sứ Muhammad) nhằm xác nhận lại những gì đã được mặc khải ở nơi các ngươi (Kinh Cựu Ước và Tân Ước) trước khi TA đổi gương mặt của các ngươi và xoay chúng ra phía sau lưng của các ngươi hoặc TA sẽ nguyền rủa họ giống như TA đã nguyền rủa đám người của ngày thứ bảy. Quả thật, mệnh lệnh của Allah chắc chắn phải được thi hành.');
INSERT INTO "vn_ruwwad" VALUES(4,48,'4:48','Chắc chắn Allah không tha thứ cho kẻ phạm tội Shirk với Ngài nhưng Ngài sẽ tha thứ các tội lỗi khác ngoài tội đó cho bất cứ ai Ngài muốn. Người nào phạm tội Shirk với Allah thì quả thật y đã phạm phải một đại trọng tội.');
INSERT INTO "vn_ruwwad" VALUES(4,49,'4:49','Lẽ nào Ngươi (hỡi Thiên Sứ Muhammad) lại không nhìn thấy những kẻ đã tự cho mình trong sạch (khỏi tội lỗi) ư? Không, chỉ Allah mới (có quyền) thanh lọc (tội lỗi) cho ai Ngài muốn (bởi Ngài biết rõ tấm lòng của y) và họ sẽ không bị đối xử bất công một chút nào.');
INSERT INTO "vn_ruwwad" VALUES(4,50,'4:50','Ngươi (hỡi Thiên Sứ Muhammad) hãy nhìn xem những kẻ đã nói khống về Allah như thế nào! Quả thật, chỉ cần mỗi việc đó thôi đã đủ là một tội công khai rồi.');
INSERT INTO "vn_ruwwad" VALUES(4,51,'4:51','Lẽ nào Ngươi (hỡi Thiên Sứ Muhammad) không nhìn thấy những kẻ được ban cho chút ít kiến thức từ Kinh Sách (Do Thái)? Họ đã tin vào các bục tượng và Taghut( và họ bảo những người vô đức tin: “Những người (tin vào các thần linh bục tường) này còn được hướng dẫn đúng đường hơn cả những người (Muslim) có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(4,52,'4:52','Đó là những kẻ đã bị Allah nguyền rủa và ai bị Allah nguyền rủa thì sẽ không bao giờ tìm được người giúp đỡ.');
INSERT INTO "vn_ruwwad" VALUES(4,53,'4:53','Lẽ nào họ có được một phần của vương quyền? Nếu thật sự là vậy thì chắc chắn họ sẽ không chia nó cho thiên hạ dù chỉ là một ít.');
INSERT INTO "vn_ruwwad" VALUES(4,54,'4:54','Hoặc lẽ nào họ đem lòng ganh tị với thiên hạ (Thiên Sứ Muhammad và các vị bạn đạo của Y) về ân huệ mà Allah ban cho họ trong khi trước đây TA đã ban cho hậu duệ của Ibrahim Kinh Sách và sự khôn ngoan và TA đã ban cho họ vương quyền vĩ đại?!');
INSERT INTO "vn_ruwwad" VALUES(4,55,'4:55','Tuy nhiên, trong số họ có người đã tin tưởng nơi Y (Thiên Sứ Muhammad) và có người thì quay lưng với Y. Và chắc chắn Hỏa Ngục đủ để trừng phạt (những kẻ vô đức tin).');
INSERT INTO "vn_ruwwad" VALUES(4,56,'4:56','Những kẻ đã phủ nhận những lời mặc khải của TA chắc chắn sẽ sớm bị TA thiêu đốt trong Ngục Lửa (vào Ngày Phán Xét). Cứ mỗi lần lớp da của họ bị thiêu cháy thì TA sẽ thay cho họ một lớp da mới để họ tiếp tục nếm lấy sự trừng phạt. Quả thật, Allah là Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(4,57,'4:57','Ngược lại, những ai có đức tin và hành thiện, TA sẽ TA thu nhận họ vào những Ngôi Vườn Thiên Đàng bên dưới có các dòng sông chảy, họ sẽ sống trong đó mãi mãi. Trong đó, họ sẽ có những người vợ luôn thanh khiết (không kinh nguyệt và mãi trinh tiết). Và TA sẽ cho họ vào ở bên dưới bóng cây mát rượi.');
INSERT INTO "vn_ruwwad" VALUES(4,58,'4:58','Quả thật, Allah đã ra lệnh cho các ngươi phải giao trả đầy đủ tín vật về lại cho chủ, và khi các ngươi phân xử (các vụ việc) giữa thiên hạ thì các ngươi phải phân xử công bằng. Allah thực sự đã rất nhân từ khi Ngài răn dạy các ngươi điều này. Quả thật, Allah là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "vn_ruwwad" VALUES(4,59,'4:59','Hỡi những người có đức tin, các ngươi hãy tuân lệnh Allah, hãy vâng lời Thiên Sứ (Muhammad) và cấp lãnh đạo của các ngươi. Trường hợp các ngươi bất đồng ý kiến nhau về một điều gì đó thì các ngươi hãy đưa điều đó trở về với Allah (Qur’an) và với Thiên Sứ (Muhammad) (tức Sunnah) nếu các ngươi thật sự có đức tin nơi Allah và Ngày Phán Xét Cuối Cùng. Đó là cách giải trình tốt nhất và đúng nhất.');
INSERT INTO "vn_ruwwad" VALUES(4,60,'4:60','Phải chăng Ngươi (hỡi Thiên Sứ Muhammad) không nhìn thấy những kẻ khẳng định họ đã có đức tin vào điều đã được mặc khải cho Ngươi và điều đã được mặc khải vào thời trước Ngươi ư? Họ muốn tìm đến Taghut (tà thần) để nhờ nó xét xử cho bọn họ trong khi họ được lệnh phải loại bỏ (tà thần). Và Shaytan luôn muốn dắt họ đi lạc thật xa.');
INSERT INTO "vn_ruwwad" VALUES(4,61,'4:61','Và khi có lời bảo họ: “Các người hãy đến với điều mà Allah đã mặc khải và các người hãy đến với Thiên Sứ (Muhammad để Người phân xử cho các người)”, Ngươi sẽ thấy những tên giả tạo đức tin sẽ quay lưng tránh xa Ngươi.');
INSERT INTO "vn_ruwwad" VALUES(4,62,'4:62','Là như thế nào (những kẻ giả tạo đức tin) khi gặp phải hậu quả do những gì mà đôi tay của họ đã làm rồi lại đến gặp Ngươi (Thiên Sứ Muhammad) thề thốt nhân danh Allah nói: “Chắc chắn chúng tôi chỉ muốn điều lành và hòa thuận mà thôi.”?!');
INSERT INTO "vn_ruwwad" VALUES(4,63,'4:63','Đó là những kẻ mà Allah biết rõ điều (xấu) trong thâm tâm họ. Thế nên, Ngươi (hỡi Thiên Sứ Muhammad) hãy tránh xa họ, hãy cảnh báo họ và hãy nói với họ bằng lời lẽ sâu sắc và thấm thía vào bản thân họ.');
INSERT INTO "vn_ruwwad" VALUES(4,64,'4:64','TA đã không gởi bất cứ vị Thiên Sứ nào đến (cho thiên hạ) ngoài mục đích để được phục tùng và vâng lời dưới sự ưng thuận của Allah. Tuy nhiên, nếu họ bất công với bản thân mình (bởi hành động tội lỗi) rồi họ tìm đến Ngươi (hỡi Thiên Sứ Muhammad) nhờ Ngươi cầu xin Allah tha thứ và Thiên Sứ (Muhammad) cầu xin tha thứ cho họ thì họ sẽ thấy Allah hằng tha thứ, hằng khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(4,65,'4:65','TA xin thề với Thượng Đế của Ngươi (hỡi Thiên Sứ Muhammad), họ sẽ không có đức tin thực sự cho đến khi nào họ tự đến yêu cầu Ngươi phân xử việc tranh chấp giữa họ rồi không cảm thấy trong lòng uất ức về quyết định của Ngươi và họ hoàn toàn chịu qui phục.');
INSERT INTO "vn_ruwwad" VALUES(4,66,'4:66','Nếu TA ra lệnh cho họ: “Các ngươi hãy tự kết liễu hoặc hãy rời khỏi nhà cửa của các ngươi ra đi (chinh chiến)” thì họ không chịu thi hành ngoại trừ một số ít trong bọn họ; và giá như họ chịu thi hành theo lệnh phán thì điều đó đã tốt cho họ và giúp họ được kiên định hơn.');
INSERT INTO "vn_ruwwad" VALUES(4,67,'4:67','Và lúc đó chắc chắn TA đã ban cho họ một phần thưởng vĩ đại từ nơi TA.');
INSERT INTO "vn_ruwwad" VALUES(4,68,'4:68','Và TA cũng đã hướng dẫn họ đi đúng con đường chính đạo.');
INSERT INTO "vn_ruwwad" VALUES(4,69,'4:69','Ai tuân lệnh Allah và Thiên Sứ (Muhammad) thì họ là những người sẽ được ở cùng với những người đã được Allah ban cho ân huệ thuộc hàng ngũ các vị Nabi, các vị Siddeeq (chính trực), những người Shaheed( và những người ngoan đạo. Thật tốt đẹp thay cho sự đồng hành với những người đó!');
INSERT INTO "vn_ruwwad" VALUES(4,70,'4:70','Đó là thiên ân của Allah và chỉ cần Allah là đã đủ am tường (tất cả mọi việc).');
INSERT INTO "vn_ruwwad" VALUES(4,71,'4:71','Hỡi những người có đức tin, các ngươi hãy luôn đề cao cảnh giác (trước kẻ thù của các ngươi), các ngươi hãy xông ra trận từng nhóm hoặc toàn quân.');
INSERT INTO "vn_ruwwad" VALUES(4,72,'4:72','Quả thật, trong các ngươi, có kẻ (giả tạo đức tin) đã chậm trễ ở phía sau (khi xuất chinh). Cho nên, nếu như các ngươi gặp phải tai họa (bại trận) thì hắn bảo: “Allah thực sự đã ban phúc cho tôi khi tôi đã không hiện diện cùng với họ.”');
INSERT INTO "vn_ruwwad" VALUES(4,73,'4:73','Ngược lại, khi các ngươi được Allah ban cho ân huệ (chiến thắng và giành được chiến lợi phẩm) thì chắc chắn hắn (tỏ thái độ tiếc nuối) như thể giữa các ngươi và hắn chẳng có tình cảm kết giao nào cả, nói: “Ôi giá như tôi đã xuất binh cùng họ thì giờ đây tôi đã được thắng lợi vinh quang rồi.”');
INSERT INTO "vn_ruwwad" VALUES(4,74,'4:74','Thế nên, những ai dùng cuộc sống trần gian này để mua lấy cuộc sống Đời Sau hãy anh dũng chiến đấu vì con đường chính nghĩa của Allah. Người nào anh dũng chiến đấu vì con đường chính nghĩa của Allah, dù bị giết hay giành chiến thắng, TA đều sẽ ban cho y phần thưởng vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(4,75,'4:75','Điều gì đã cấm cản các ngươi (những người có đức tin) không đi chiến đấu cho con đường chính nghĩa của Allah để giải phóng cho những người đàn ông, những người phụ nữ và đám trẻ thơ đang cầu nguyện: “Lạy Thượng Đế của bầy tôi, xin hãy giải cứu bầy tôi thoát khỏi thị trấn (Makkah) này bởi cư dân của nó là kẻ áp bức và bất công, xin Ngài hãy chỉ định ai đó đứng ra làm người bảo hộ và giúp đỡ bầy tôi.”');
INSERT INTO "vn_ruwwad" VALUES(4,76,'4:76','Những người có đức tin thì chiến đấu cho con đường chính nghĩa của Allah còn những kẻ vô đức tin thì chiến đấu cho con đường của tà thần. Do đó, các ngươi (những người có đức tin) hãy chiến đấu chống lại bè lũ của Shaytan bởi quả thật, mưu kế của Shaytan rất kém cỏi.');
INSERT INTO "vn_ruwwad" VALUES(4,77,'4:77','Phải chăng Ngươi (hỡi Thiên Sứ Muhammad) không nhìn thấy những người khi có lời bảo: “Các người hãy ngưng chiến, hãy chu đáo thiết lập lễ nguyện Salah và hãy xuất Zakah”.( Nhưng rồi sau đó, khi có lệnh bắt buộc họ phải chiến đấu thì một thành phần trong số họ sợ thiên hạ giống như sợ Allah, thậm chí còn sợ hơn. họ than: “Lạy Thượng Đế của bầy tôi, tại sao Ngài lại ban lệnh chiến đấu ngay lúc này vậy, phải chi Ngài trì hoãn cho bầy tôi thêm một thời gian nữa (để bầy tôi được hưởng thụ cuộc sống).” Ngươi (hỡi Thiên Sứ Muhammad) hãy nói với họ: “Vật chất của cuộc sống trần gian này chỉ là ít ỏi trong khi cõi Đời Sau tốt đẹp hơn dành cho người ngoan đạo; và các ngươi sẽ không bị đối xử bất công một chút nào.”');
INSERT INTO "vn_ruwwad" VALUES(4,78,'4:78','Dẫu cho các ngươi có ở đâu thì cái chết cũng sẽ túm bắt các ngươi (khi tuổi thọ đã hết), cho dù các ngươi có ẩn náu trong thành lũy kiên cố thế nào đi nữa. Nếu họ gặp được điều tốt lành thì họ nói: “Điều (tốt) này đến từ Allah” và nếu họ gặp phải điều xấu thì họ nói: “Điều (xấu) này đến từ ngươi (Muhammad)”. Ngươi (hỡi Thiên Sứ Muhammad) hãy hãy nói với họ: “Cả hai điều (tốt và xấu) đều đến từ Allah”. Tuy nhiên, đám người này hầu như không hiểu bất cứ lời nói nào.');
INSERT INTO "vn_ruwwad" VALUES(4,79,'4:79','(Này hỡi con người), điều tốt nào gặp được là từ nơi Allah, còn điều xấu nào ngươi gặp phải là do bản thân ngươi gây nên. Và TA đã gửi Ngươi (Muhammad) đến với nhân loại làm một vị Thiên Sứ (truyền tải bức Thông Điệp của TA). Và chỉ cần một mình Allah đã đủ làm chứng (cho điều đó).');
INSERT INTO "vn_ruwwad" VALUES(4,80,'4:80','Ai vâng lời Thiên Sứ (Muhammad) là tuân lệnh Allah. Ai quay lưng (bất tuân Ngươi - Muhammad) thì (Ngươi hãy biết rằng) TA không gửi Ngươi đến để canh chừng họ.');
INSERT INTO "vn_ruwwad" VALUES(4,81,'4:81','(Những kẻ giả tạo đức tin) nói: “Chúng tôi tuân lệnh” nhưng khi họ rời khỏi Ngươi thì một thành phần trong bọn họ âm thầm bàn tính trong đêm làm điều khác với điều mà họ đã nói. Allah sẽ ghi chép hết mọi điều mà họ đã mưu tính trong đêm. Cho nên, Ngươi hãy lánh xa bọn họ và hãy phó thác cho Allah, quả thât, một mình Allah đã đủ làm một Đấng Phó Thác (cho Ngươi).');
INSERT INTO "vn_ruwwad" VALUES(4,82,'4:82','Tại sao họ không chịu nghiền ngẫm về Qur’an? Nếu như Nó đến từ một ai khác ngoài Allah thì chắc chắn họ đã tìm thấy trong đó rất nhiều điều mâu thuẫn, đối lập nhau.');
INSERT INTO "vn_ruwwad" VALUES(4,83,'4:83','(Những kẻ giả tạo đức tin) khi thấy tin tức liên quan đến vấn đề trị an hoặc một điều gì đó lo sợ thì bọn họ liền phổ biến nó ra ngoài. Nếu bọn họ chịu đệ trình điều đó lên Thiên Sứ và lên cấp lãnh đạo thì những người có khả năng đã có thể nghiên cứu, phân tích và biết được nó đến từ đâu rồi! Quả thật, nếu như không nhờ hồng phúc và sự nhân từ của Allah dành cho các ngươi thì toàn bộ các ngươi đã đi theo Shaytan ngoại trừ một số ít.');
INSERT INTO "vn_ruwwad" VALUES(4,84,'4:84','Do đó, Ngươi (hỡi Thiên Sứ Muhammad) hãy chiến đấu cho con đường chính nghĩa của Allah. Ngài không bắt buộc ai ngoài Ngươi và Ngươi hãy động viên tinh thần của những người có đức tin. Có thể Allah sẽ ngăn chặn sức mạnh của những kẻ vô đức tin, bởi Allah hùng mạnh trên tất cả về sức lực và sự trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(4,85,'4:85','Ai tận lực can thiệp mang lại điều tốt cho người khác thì y sẽ được ân phước cho việc đó còn ai tận lực can thiệp mang lại điều xấu cho người khác thì y phải gánh vác phần tội lỗi về việc đó. Quả thật, Allah giám sát tất cả mọi sự việc.');
INSERT INTO "vn_ruwwad" VALUES(4,86,'4:86','Khi các ngươi được chào hỏi lịch thiệp thì các ngươi hãy đáp lại bằng lời chào tốt hơn hoặc bằng lời chào tương tự(. Quả thật, Allah là Đấng thanh toán hết tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(4,87,'4:87','Allah là Đấng không có Thượng Đế đích thực nào khác ngoài Ngài. Ngài sẽ triệu tập toàn bộ các ngươi (loài người) vào Ngày Phán Xét, Ngày mà không có gì phải hoài nghi. Còn ai thật hơn Allah trong lời nói?!');
INSERT INTO "vn_ruwwad" VALUES(4,88,'4:88','Làm sao các ngươi (những người có đức tin) chỉ vì đám người giả tạo đức tin mà tách thành hai nhóm (đối lập) trong khi Allah đã lật đổ điều (xấu) mà họ đã làm? Lẽ nào các ngươi muốn hướng dẫn kẻ đã bị Allah làm cho lầm lạc? Quả thật, ai đã bị Allah làm cho lầm lạc thì sẽ không tìm được con đường (chân lý) cho mình.');
INSERT INTO "vn_ruwwad" VALUES(4,89,'4:89','Bọn họ (những người giả tạo đức tin) đã ao ước giá như các ngươi cũng vô đức tin giống như bọn họ để đôi bên tương đồng nhau. Do đó, các ngươi chớ đừng kết thân với họ cho đến khi họ di cư vì con đường chính nghĩa của Allah. Còn nếu bọn họ quay mặt bỏ đi thì các ngươi hãy tóm bắt và xử tử họ ở bất cứ đâu các ngươi bắt gặp được họ, và các ngươi cũng chớ đừng chọn lấy họ làm bạn hoặc làm người hỗ trợ.');
INSERT INTO "vn_ruwwad" VALUES(4,90,'4:90','Ngoại trừ những ai đã kịp hội nhập vào nhóm người mà giữa các ngươi và họ có hiệp ước (ngừng chiến) hoặc những ai chủ động tìm đến các ngươi với tấm lòng (không muốn) chiến đấu với các ngươi hoặc chiến đấu với nhóm (đa thần) của họ nữa. Và nếu muốn, Allah đã để họ khống chế các ngươi. Tuy nhiên, nếu họ tự động lui quân, không đánh các ngươi nữa và đề nghị hòa bình với các ngươi thì Allah không cho các ngươi lý do nào chiến đấu với họ.');
INSERT INTO "vn_ruwwad" VALUES(4,91,'4:91','Một nhóm (giả tạo đức tin) khác (thể hiện đức tin trước các ngươi vì) muốn được yên ổn với các ngươi và với đám dân của họ (những kẻ đa thần Quraish), các ngươi sẽ thấy khi họ quay trở lại với đám người đa thần thì họ hào hứng và thích thú. Do đó, nếu như bọn họ không rút lui, không đề nghị hòa bình với các ngươi và không ngừng chiến đấu với các ngươi thì các ngươi hãy bắt và giết họ ở bất cứ đâu các ngươi tìm thấy họ. Đối với đám người đó, Allah cho các ngươi quyền (trừng phạt họ).');
INSERT INTO "vn_ruwwad" VALUES(4,92,'4:92','Người có đức tin không được giết người có đức tin khác trừ phi chỉ vì vô ý. Ai vô ý giết chết một người có đức tin thì (sẽ chuộc tội bằng cách) phóng thích một nô lệ có đức tin và bồi thường cho gia đình nạn nhân, trừ phi họ từ chối nhận tiền bồi thường (vì lòng nhân đạo). Trường hợp người chết là người có đức tin thuộc phe kẻ thù của các ngươi thì (chuộc tội bằng cách) phóng thích một nô lệ có đức tin là đủ. Trường hợp nạn nhân là người thuộc phe mà giữa các ngươi và họ có hiệp ước (hòa bình) thì phải bồi thường cho gia đình nạn nhân đồng thời phải phóng thích một nô lệ có đức tin. Đối với phạm nhân nào không có khả năng (bồi thường hoặc phóng thích nô lệ có đức tin) thì y phải nhịn chay hai tháng liên tiếp để sám hối tạ tội với Allah. Quả thật, Allah là Đấng Hằng Biết, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(4,93,'4:93','Người nào cố tình giết một người có đức tin thì hình phạt dành cho y là Hỏa Ngục, y sẽ mãi mãi ở trong đó, Allah sẽ giận dữ với y, Ngài sẽ nguyền rủa y và chuẩn bị cho y một sự trừng phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(4,94,'4:94','Hỡi những người có đức tin, khi các ngươi xuất binh chiến đấu cho con đường chính nghĩa của Allah thì các ngươi hãy xác định rõ đối phương trước khi tấn công (xem có phải là người Muslim hay không), và các ngươi chớ đừng đáp lại lời chào Salam của ai đó nói: “Ngươi đâu phải là người có đức tin” với ý định ham muốn lợi lộc của cuộc sống trần gian trong khi ở nơi Allah có vô số chiến lợi phẩm (tốt đẹp). Trước đây, các ngươi cũng lâm vào cảnh giống như thế, rồi các ngươi được Allah ban cho ân huệ (thành người Muslim), cho nên các ngươi hãy xác định rõ ràng (trước khi tấn công). Quả thật, Allah thông toàn mọi hành động của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(4,95,'4:95','Trong số những người có đức tin, ngoài những người có lý do không tham chiến (các phế binh, người bệnh tật, già yếu) thì không hề ngang bằng nhau giữa những người ngồi lại ở nhà (không tham chiến) và những người đi chinh chiến cho con đường chính nghĩa của Allah bằng tài sản và sinh mạng của họ. Allah sẽ nâng cấp bậc cho những người đi chinh chiến bằng tài sản và sinh mạng của họ hơn những người ngồi lại ở nhà. Cả hai nhóm đều được hứa hẹn điều tốt đẹp. Tuy nhiên, nhóm người đi chinh chiến được Allah ưu đãi hơn nhóm người ở lại nhà bởi phần thưởng vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(4,96,'4:96','Các cấp bậc cao cùng với sự tha thứ và lòng nhân từ đều từ nơi Ngài. Quả thật, Allah hằng tha thứ, hằng khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(4,97,'4:97','Quả thật, những ai bị các Thiên Thần rút hồn trong lúc họ làm điều bất công với chính mình (không chịu di cư), (các Thiên Thần) sẽ bảo họ: “Các ngươi đã sống như thế nào?” Họ đáp: “Chúng tôi luôn bị áp bức trên vùng đất này.” (Các Thiên Thần) bảo: “Phải chăng đất đai của Allah không rộng đủ để cho các ngươi di cư (lánh nạn) sao?!” Những người như thế, chỗ ở của họ sẽ là Hỏa Ngục, một nơi đến cuối cùng thật tồi tệ.');
INSERT INTO "vn_ruwwad" VALUES(4,98,'4:98','Ngoại trừ những người yếu đuối và cô thế trong số những người đàn ông, phụ nữ và trẻ con, họ không có phương tiện (để di cư) cũng như không được ai dẫn đường.');
INSERT INTO "vn_ruwwad" VALUES(4,99,'4:99','Những người đó mới là những người may ra được Allah tha thứ cho họ bởi Allah hằng lượng thứ, khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(4,100,'4:100','Người nào di cư vì con đường chính nghĩa của Allah chắc chắn sẽ tìm thấy trên trái đất có nhiều chỗ lánh nạn, bao la. Người nào rời bỏ nhà cửa của mình để di cư đến với Allah và Thiên Sứ của Ngài rồi chết (trên đường) thì phần thưởng của y chắc chắn ở nơi Allah. Quả thật, Allah hằng lượng thứ, khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(4,101,'4:101','Khi các ngươi đi xa trên trái đất, các ngươi sẽ không bị tội cho việc các ngươi rút ngắn lễ nguyện Salah (từ bốn Rak’ah thành hai Rak’ah) vì sợ bị những kẻ vô đức tin có thể tấn công. Những kẻ vô đức tin thực sự là kẻ thù công khai của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(4,102,'4:102','Khi Ngươi (hỡi Thiên Sứ Muhammad) ở cùng với họ (trên sa trường) thì Ngươi hãy làm Imam dẫn lễ nguyện Salah cho họ. Ngươi hãy để một nhóm trong số họ dâng lễ nguyện Salah cùng Ngươi và mang theo vũ khí bên mình. Khi các ngươi quỳ lạy (xong Rak’ah thứ nhất) thì họ sẽ lùi về phía sau để nhóm còn lại vẫn chưa hành lễ bước đến dâng lễ cùng Ngươi (ở Rak’ah thứ hai) và họ cần đề phòng và cũng mang theo vũ khí bên mình. Những kẻ vô đức tin vẫn luôn chờ đợi giây phút lơ là của các ngươi về vũ khí và hành trang hầu thừa cơ hội đánh úp các ngươi một trận. Các ngươi không có tội nếu các ngươi đặt vũ khí xuống vì gặp khó khăn do trời đổ mưa hoặc do đau bệnh nhưng các ngươi phải luôn đề cao cảnh giác. Chắc chắn, Allah đã chuẩn bị sẵn cho đám người vô đức tin một sự trừng phạt nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(4,103,'4:103','Khi các ngươi đã hoàn thành xong lễ nguyện Salah thì các ngươi hãy tụng niệm, tán dương Allah (như được truyền dạy theo Sunnah) trong mọi lúc dù đang đứng, đang ngồi hoặc đang nằm nghiêng một bên. Sau khi các ngươi được bình an (không còn sợ hãi nữa) thì các ngươi hãy thiết lập lễ nguyện Salah (cho thật chu đáo). Quả thật, lễ nguyện Salah đã được ban hành cho những người có đức tin vào giờ giấc ấn định.');
INSERT INTO "vn_ruwwad" VALUES(4,104,'4:104','Các ngươi chớ đừng chậm trễ trong việc đuổi bắt địch. Nếu các ngươi đau đớn (bởi thương vong) thì bọn họ cũng bị đau đớn (bởi thương vong) tương tự như các ngươi, có điều các ngươi có niềm hy vọng nơi Allah còn bọn họ chẳng có niềm hy vọng nào. Và Allah là Đấng Hằng Biết, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(4,105,'4:105','TA thực sự đã ban xuống cho Ngươi (Thiên Sứ Muhammad) Thiên Kinh (Qur’an) bằng sự thật để Ngươi dùng Nó phân xử thiên hạ đúng theo điều Allah muốn; và Ngươi chớ đừng a tòng với đám người gian dối, phản bội.');
INSERT INTO "vn_ruwwad" VALUES(4,106,'4:106','Ngươi hãy cầu xin Allah tha thứ, quả thật Allah hằng tha thứ, hằng khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(4,107,'4:107','Ngươi chớ đừng bênh vực cho những ai gian dối phản bội bản thân, quả thật Allah không yêu thương kẻ gian dối tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(4,108,'4:108','(Những kẻ gian dối, phản bội) tìm cách trốn thiên hạ nhưng họ không trốn được Allah, bởi Ngài luôn ở cùng (giám sát) họ khi họ âm thầm bàn bạc trong đêm điều không được Ngài hài lòng. Quả thật, Allah là Đấng thâu tóm mọi hành vi của bọn họ.');
INSERT INTO "vn_ruwwad" VALUES(4,109,'4:109','Những người này, các ngươi có thể bênh vực cho họ trong cuộc sống trần gian này nhưng ai sẽ đứng ra bênh vực cho họ trước Allah vào Ngày Phán Xét và ai sẽ đứng ra bảo lãnh cho họ?');
INSERT INTO "vn_ruwwad" VALUES(4,110,'4:110','Người nào làm điều xấu hoặc bất công với chính mình (bởi điều tội lỗi), sau đó (ăn năn) cầu xin Allah tha thứ thì y sẽ tìm thấy Allah hằng tha thứ, hằng khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(4,111,'4:111','Ai làm điều tội lỗi thì thật ra y chỉ tự hại bản thân mình. Quả thật, Allah là Đấng Hằng Biết, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(4,112,'4:112','Người nào phạm điều sai trái hoặc tội lỗi rồi đổ tội cho người khác thì quả thật y đã mang tội vu khống và tội công khai.');
INSERT INTO "vn_ruwwad" VALUES(4,113,'4:113','Nếu không nhờ phúc và lòng thương xót của Allah dành cho Ngươi (hỡi Thiên Sứ Muhammad) thì chắc chắn một nhóm của họ đã đẩy Ngươi vào lầm lạc. Họ đã không làm cho ai lầm lạc ngoại trừ bản thân họ, họ đã không gây hại được Ngươi bất cứ điều gì. Allah đã ban cho Ngươi Thiên Kinh (Qur’an) và sự khôn ngoan (Sunnah) và Ngài đã dạy cho Ngươi những điều Ngươi chưa từng biết trước đây. Quả thật, ân huệ mà Allah đã ban cho Ngươi thật vĩ đại biết bao.');
INSERT INTO "vn_ruwwad" VALUES(4,114,'4:114','Hầu hết các cuộc mật đàm của họ chẳng có gì tốt đẹp cả ngoại trừ ai đó bảo ban việc bố thí, hành thiện hoặc hòa giải mọi người. Ai làm những điều này với mong muốn làm hài lòng Allah thì họ sẽ sớm được TA ban cho một phần thưởng thật to lớn.');
INSERT INTO "vn_ruwwad" VALUES(4,115,'4:115','Người nào gây khó khăn, chống đối Thiên Sứ (của Allah) sau khi nguồn chỉ đạo và hướng dẫn đã được trình bày rõ cho y và y vẫn ngoan cố đi theo con đường khác với con đường của những người có đức tin, TA (Allah) sẽ bỏ mặc y đi theo cái mà y đã quay đi và TA sẽ nướng y trong Hỏa Ngục, một nơi đến cuối cùng thật tồi tệ.');
INSERT INTO "vn_ruwwad" VALUES(4,116,'4:116','Chắc chắn Allah không tha thứ cho kẻ phạm tội Shirk với Ngài, tuy nhiên, Ngài sẵn sàng tha thứ các tội lỗi khác cho bất cứ ai Ngài muốn. Người nào phạm tội Shirk với Allah thì quả thật y đã lầm lạc rất xa.');
INSERT INTO "vn_ruwwad" VALUES(4,117,'4:117','(Những người đa thần Makkah) chỉ cầu nguyện những nữ thần bục tượng thay vì Ngài (Allah) và họ chỉ cầu nguyện Shaytan phản nghịch.');
INSERT INTO "vn_ruwwad" VALUES(4,118,'4:118','Allah đã nguyền rủa hắn (Shaytan). Và hắn đã nói: “Bề tôi quyết sẽ bắt đi trong đám bầy tôi của Ngài một phần bắt buộc .”(');
INSERT INTO "vn_ruwwad" VALUES(4,119,'4:119','“Và bề tôi quyết sẽ dắt họ đi lạc (khỏi chính đạo), bề tôi quyết cám dỗ họ theo đuổi những ham muốn sai trái, bề tôi quyết xúi giục họ khiến họ cắt lỗ tai của gia súc (để cấm họ thứ Allah cho phép) và bề tôi quyết sai khiến họ thay đổi hình dạng mà Allah đã tạo”. Người nào nhận lấy Shaytan làm kẻ bảo hộ thay vì Allah thì chắc chắn y đã hoàn toàn thất bại một cách rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(4,120,'4:120','(Shaytan) hứa hẹn với họ và cám dỗ họ ham muốn những điều sai trái, tuy nhiên, Shaytan chỉ hứa hẹn với họ điều gian dối mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(4,121,'4:121','Đám người đó (những ai theo Shaytan), chỗ ở của họ sẽ là Hỏa Ngục và họ sẽ không tìm thấy con đường để thoát thân.');
INSERT INTO "vn_ruwwad" VALUES(4,122,'4:122','Riêng những ai có đức tin và hành thiện, TA (Allah) sẽ thu nhận họ vào những Ngôi Vườn Thiên Đàng bên dưới có các dòng sông chảy, họ sẽ sống mãi mãi trong đó. Lời hứa của Allah là sự thật. Vậy còn có ai nói thật hơn Allah nữa chăng?!');
INSERT INTO "vn_ruwwad" VALUES(4,123,'4:123','(Lời hứa của Allah được thể hiện) không phải theo ước muốn của các ngươi (những người có đức tin) và cũng không phải theo ước muốn của dân Kinh Sách. Người nào làm điều xấu sẽ phải lãnh đủ hậu quả cho việc làm xấu đó, và ngoài Allah y sẽ không tìm thấy vị bảo hộ và giúp đỡ nào cả.');
INSERT INTO "vn_ruwwad" VALUES(4,124,'4:124','Người nào hành thiện, dù nam hay nữ, đồng thời là người có đức tin thì sẽ được vào Thiên Đàng, họ sẽ không bị đối xử bất công một tí nào.');
INSERT INTO "vn_ruwwad" VALUES(4,125,'4:125','Ai tốt hơn người đã dâng trọn (cả thể xác lẫn tâm hồn của mình) cho Allah đồng thời là một người đức hạnh và luôn đi theo tín ngưỡng thuần túy của Ibrahim?! Quả thật, Allah đã chọn Ibrahim làm vị Khaleel( (của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(4,126,'4:126','Mọi vật trong các tầng trời và mọi vật trong trái đất đều thuộc về một mình Allah và Allah là Đấng thâu tóm toàn bộ mọi vật.');
INSERT INTO "vn_ruwwad" VALUES(4,127,'4:127','Họ hỏi Ngươi (Thiên Sứ Muhammad) về (vấn đề của) phụ nữ. Ngươi hãy nói: “Allah đã quy định rõ cho các người về họ qua lời xướng đọc trong Thiên Kinh (Qur’an) về các phụ nữ (xuất thân là) trẻ mồ côi mà các người đã không trao đủ cho họ (quyền lợi) đã được quy định cho họ (như tiền cưới và quyền thừa kế); và (vì lòng tham chiếm đoạt tài sản của họ khiến) các người muốn cưới họ. Đối với những đứa trẻ yếu thế, các người phải đứng ra bênh vực những đứa trẻ mồ côi vì lẽ công bằng. Và mọi điều tốt mà các người đã làm, quả thật Allah đều biết rõ tận tường.”');
INSERT INTO "vn_ruwwad" VALUES(4,128,'4:128','Trường hợp người phụ nữ sợ người chồng đối xử tệ bạc hoặc bỏ rơi thì hai bên không có tội nếu chịu hòa giải với nhau( và việc hòa giải luôn là điều tốt. Quả thật, lòng tham và ích kỷ đã tồn tại trong bản thân của mỗi người. Tuy nhiên, nếu các ngươi làm tốt và ngoan đạo thì quả thật Allah thông toàn tất cả những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(4,129,'4:129','Các ngươi (những người chồng) sẽ chẳng thể nào công bằng (tuyệt đối trong việc đối xử) với các người vợ (của các ngươi) cho dù các ngươi có cố gắng ra sao. Tuy nhiên, các ngươi cũng chớ đừng nghiêng hẳn về người (vợ) này và bỏ mặc người (vợ) kia như là một án phạt tù treo(. Do đó, nếu (vợ chồng) các ngươi biết cải thiện và biết kính sợ (Allah) thì chắc chắn Allah hằng tha thứ, khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(4,130,'4:130','Còn nếu hai vợ chồng (nhất định) phải li hôn thì Allah sẽ ban cho đôi bên được giàu có với thiên lộc của Ngài, bởi Allah bao la, sáng suốt.');
INSERT INTO "vn_ruwwad" VALUES(4,131,'4:131','Vạn vật trong các tầng trời và vạn vật trong trái đất đều thuộc về một mình Allah. Quả thật, TA (Allah) đã truyền lệnh đến những ai được ban cho Kinh Sách trước các ngươi và luôn cả các ngươi (hỡi tín đồ Muslim) rằng các ngươi hãy kính sợ Allah. Nếu như các ngươi vô đức tin thì vạn vật trong các tầng trời cũng như mọi vật trong trái đất vẫn thuộc về một mình Allah. Quả thật, Allah là Đấng Giàu Có, Đấng đáng được ca tụng.');
INSERT INTO "vn_ruwwad" VALUES(4,132,'4:132','Vạn vật trong các tầng trời và vạn vật trong trái đất đều thuộc về một mình Allah. Và một mình Allah đã đủ chi phối và quản lý tất cả.');
INSERT INTO "vn_ruwwad" VALUES(4,133,'4:133','Nếu muốn, Ngài thừa sức tiêu diệt toàn bộ các ngươi, hỡi con người, rồi mang đến một loại tạo vật khác (chỉ biết làm Ngài hài lòng). Quả thật, Allah thừa khả năng làm điều đó.');
INSERT INTO "vn_ruwwad" VALUES(4,134,'4:134','Người nào mong muốn phần thưởng của đời sống trần gian thì (hãy biết rằng) ở nơi Allah có phần thưởng của đời sống trần gian và cả Đời Sau. Quả thật, Allah hằng nghe, hằng thấy.');
INSERT INTO "vn_ruwwad" VALUES(4,135,'4:135','Hỡi những người có đức tin, các ngươi hãy hiên ngang đứng lên bênh vực công lý như là những nhân chứng của Allah, cho dù điều đó có thể chống lại bản thân các ngươi hoặc chống lại cha mẹ và bà con ruột thịt của các ngươi. Dù (ai đó) giàu hay nghèo thì (các ngươi cũng đừng quan tâm) bởi Allah quan tâm đến hai người đó nhiều hơn các ngươi.( Cho nên, các ngươi chớ đừng chiều theo dục vọng của các ngươi để rồi các ngươi rời xa công lý. Nếu các ngươi làm trái hoạc ngoảnh mặt với (sự thật) thì (các ngươi hãy biết rằng) quả thật Allah thông toàn mọi điều các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(4,136,'4:136','Hỡi những người có đức tin, các ngươi hãy tin nơi Allah, nơi Thiên Sứ của Ngài, nơi Kinh Sách (Qur’an) mà Ngài đã ban xuống cho Thiên Sứ của Ngài cũng như các Kinh Sách mà Ngài đã ban xuống trước đây. Người nào phủ nhận Allah, các Thiên Thần của Ngài, các Kinh Sách của Ngài, các Thiên Sứ của Ngài và Ngày Phán Xét Cuối Cùng thì quả thật y đã lầm lạc quá xa.');
INSERT INTO "vn_ruwwad" VALUES(4,137,'4:137','Quả thật, những ai đã có đức tin rồi phủ nhận đức tin sau đó lại có đức tin rồi phủ nhận đức tin lần nữa và sự vô đức tin càng tăng cường hơn thì chắc chắn Allah sẽ không tha thứ cho họ và sẽ không hướng dẫn họ đến con đường (chân lý).');
INSERT INTO "vn_ruwwad" VALUES(4,138,'4:138','Ngươi (hỡi Thiên Sứ Muhammad) hãy báo tin cho những kẻ giả tạo đức tin rằng bọn họ sẽ bị trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(4,139,'4:139','Những ai nhận lấy những kẻ vô đức tin làm người bảo hộ thay vì những người có đức tin, lẽ nào họ muốn tìm quyền vinh ở nơi bọn người đó chăng? Quả thật, mọi quyền lực và vinh quang (thật sự) đều chỉ thuộc về một mình Allah.');
INSERT INTO "vn_ruwwad" VALUES(4,140,'4:140','Quả thật, Ngài đã ban lệnh cho các ngươi trong Thiên Kinh (Qur’an) rằng khi các ngươi nghe thấy các lời mặc khải của Allah bị phủ nhận hoặc bị chế giễu thì các ngươi chớ đừng tiếp tục ngồi cùng với bọn họ nữa cho tới khi bọn họ chuyển sang đề tài khác, còn không thì chắc chắn các ngươi cũng giống như bọn họ. Allah chắc chắn sẽ triệu tập toàn bộ những kẻ giả tạo đức tin và những kẻ vô đức tin vào trong Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(4,141,'4:141','Những ai đứng ngoài quan sát các ngươi, khi các ngươi được Allah ban cho sự thắng lợi thì bọn họ (liền đến để được chia phần) bảo: “Chẳng phải bọn tôi là người cùng phe với các vị hay sao?” Và khi những người vô đức tin chiếm phần thắng thì bọn họ cũng lại đến (với những kẻ vô đức tin đó), bảo: “Không phải bọn tôi đã giúp đỡ các người và ngăn cản các người khỏi tay của những người có đức tin đó sao?” Rồi đây vào Ngày Phán Xét Cuối Cùng, Allah sẽ phân xử giữa các ngươi (những người có đức tin và những kẻ giả tạo đức tin), và tất nhiên Allah không bao giờ mở đường cho những kẻ vô đức tin thắng được những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(4,142,'4:142','Quả thật, những kẻ giả tạo đức tin tìm cách lừa dối Allah nhưng chính Ngài đã đánh lừa họ. Và mỗi khi họ đứng dậy hành lễ Salah thì họ chỉ đứng dậy một cách lười biếng, họ chỉ muốn phô trương cho mọi người nhìn thấy, và họ rất ít khi nhớ đến Allah.');
INSERT INTO "vn_ruwwad" VALUES(4,143,'4:143','Họ luôn ở trong tâm thái lưỡng lự và do dự, không biết đi theo bên này hay theo bên nọ. Người nào mà Allah đã làm cho y lầm lạc thì Ngươi (Thiên Sứ Muhammad) sẽ không bao giờ tìm được cho y một con đường (đúng đắn).');
INSERT INTO "vn_ruwwad" VALUES(4,144,'4:144','Hỡi những người có đức tin, các ngươi chớ đừng nhận lấy những kẻ vô đức tin làm người bảo hộ thay vì những người có đức tin, chẳng lẽ các ngươi muốn Allah lấy đó làm cơ sở để trừng phạt các ngươi hay sao?!');
INSERT INTO "vn_ruwwad" VALUES(4,145,'4:145','Chắc chắn, những kẻ giả tạo đức tin sẽ bị đày xuống tận đáy của Ngục Lửa và Ngươi sẽ không bao giờ tìm được cho họ một vị cứu tinh nào.');
INSERT INTO "vn_ruwwad" VALUES(4,146,'4:146','Ngoại trừ những ai biết ăn năn sám hối và sửa mình rồi bám chặt vào Allah, một lòng dâng trọn tôn giáo của họ cho Allah thì họ sẽ ở cùng với những người có đức tin; và Allah sẽ sớm ban cho những người có đức tin phần thưởng vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(4,147,'4:147','Allah không trừng phạt các ngươi khi các ngươi biết ơn và có đức tin (nơi Ngài), bởi Allah là Đấng Tri Ân, Đấng Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(4,148,'4:148','Allah không yêu thích việc la lối giữa công chúng với những lời xấu xa ngoại trừ ai đó bị đối xử bất công (thì được phép giải trình để lấy lại công bằng). Quả thật, Allah hằng nghe, hằng biết.');
INSERT INTO "vn_ruwwad" VALUES(4,149,'4:149','Nếu các ngươi công khai hoặc giấu giếm việc làm tốt hoặc lượng thứ cho ai làm xấu với mình thì quả thật Allah là Đấng hằng lượng thứ, Đấng toàn năng.');
INSERT INTO "vn_ruwwad" VALUES(4,150,'4:150','Quả thật, những kẻ vô đức tin nơi Allah và các Thiên Sứ của Ngài đồng thời muốn chia rẽ giữa Allah và các Thiên Sứ của Ngài; họ nói: “Chúng tôi chỉ có đức tin nơi một số Thiên Sứ và phủ nhận một số khác.” Họ muốn đi giữa con đường đó.');
INSERT INTO "vn_ruwwad" VALUES(4,151,'4:151','Họ đích thực là những kẻ vô đức tin. TA đã chuẩn bị sẵn cho những kẻ vô đức tin một sự trừng phạt nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(4,152,'4:152','Ngược lại, những ai có đức tin nơi Allah và các Thiên Sứ của Ngài, không phân biệt bất cứ ai trong số các vị Thiên Sứ đó của Ngài thì họ là những người sớm được TA ban cho phần thưởng. Quả thật, Allah hằng tha thứ, hằng khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(4,153,'4:153','Người dân Kinh Sách (Do Thái) yêu cầu Ngươi (Thiên Sứ Muhammad) mang xuống cho họ một quyển Kinh từ trên trời. (Điều này không lạ gì với bản tính của bọn họ) bởi trước đây, bọn họ đã từng yêu cầu Musa điều to tát hơn thế, bọn họ bảo: “Người hãy cho chúng tôi được nhìn thấy Allah”, thế là cả bọn họ bị đánh bởi lưỡi tầm sét do tội đại nghịch của họ. Sau đó bọn họ còn đúc ra một con bò nghé để thờ sau khi đã chứng kiến biết bao bằng chứng rõ rệt. Tuy nhiên, TA (Allah) vẫn lượng thứ cho bọn họ và TA đã ban cho Musa một quyền hạn rõ rệt.');
INSERT INTO "vn_ruwwad" VALUES(4,154,'4:154','Và TA đã nâng ngọn núi Al-Tur lên trên đầu bọn họ (để họ hoảng sợ mà hoàn thành) giao ước của họ; TA ra lệnh bọn họ hãy bước vào cửa (của Masjid Al-Aqsa) mà cúi đầu (nhưng họ lại quay lưng đi vào) và TA đã ra lệnh cho họ không được vi phạm lệnh cấm của ngày thứ bảy và TA đã lấy lời giao ước trịnh trọng của bọn họ.');
INSERT INTO "vn_ruwwad" VALUES(4,155,'4:155','(TA đã nguyền rủa họ) bởi vì họ đã phản lại giao ước, phủ nhận các dấu hiệu của Allah, giết các vị Nabi một cách vô cớ và nói: “Con tim của chúng tôi đã được bao kín lại rồi”. Không, chính Allah đã đóng lên con tim của họ sự vô đức tin nên họ không có đức tin ngoại trừ một số ít.');
INSERT INTO "vn_ruwwad" VALUES(4,156,'4:156','Và (TA đã nguyền rủa họ) vì sự vô đức tin của họ cũng như vì câu nói vu khống nặng nề của họ về Mar-yam.');
INSERT INTO "vn_ruwwad" VALUES(4,157,'4:157','Và (TA đã nguyền rủa họ) vì lời nói của họ rằng chúng tôi đích thực đã giết chết Masih Ysa (Giê-su) con trai của Mar-yam, Thiên Sứ của Allah. Tuy nhiên, họ thực sự đã không giết được Y và cũng không hề đóng đinh được Y lên cây thập tự mà đó chỉ là một sự tương tự dành cho họ mà thôi. Những người tranh cãi nhau về sự việc đó đều không thoát được nỗi hoài nghi, họ đã không hề biết rõ về sự việc đó mà chỉ là sự phỏng đoán mà thôi. Họ chắc chắn đã không giết được Ysa.');
INSERT INTO "vn_ruwwad" VALUES(4,158,'4:158','Đúng vậy, (họ đã không giết được Ysa) bởi Allah đã đưa Y lên nơi Ngài. Quả thật, Allah là Đấng Toàn Năng, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(4,159,'4:159','Tuy nhiên, không một người dân Kinh Sách nào trước khi chết lại không tin (rằng Ysa sẽ giáng trần trở lại). Và vào Ngày Phục Sinh, (Ysa) sẽ là nhân chứng cho (mọi việc làm của) họ.');
INSERT INTO "vn_ruwwad" VALUES(4,160,'4:160','Do hành vi sai trái bất công của người Do Thái và việc họ nhiều lần ngăn chặn con đường của Allah, TA đã cấm họ những thực phẩm tốt sạch mà họ đã được phép dùng (trước đó).');
INSERT INTO "vn_ruwwad" VALUES(4,161,'4:161','Và (do tội) họ đã cho vay lấy lãi trong khi nó đã bị cấm và do việc họ ăn chặn tài sản bất chính của thiên hạ. TA đã chuẩn bị sẵn cho những kẻ vô đức tin trong bọn họ sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(4,162,'4:162','Riêng những người thông thạo kiến thức trong số (những người Do Thái) và những người có đức tin đã (hết lòng) tin tưởng vào những điều được ban xuống cho Ngươi (hỡi Thiên Sứ Muhammad) cũng như những điều được ban xuống trước Ngươi, họ thiết lập lễ nguyện Salah, thực thi bổn phận Zakah, có đức tin nơi Allah và cõi Đời Sau, thì sẽ sớm được TA ban cho phần thưởng vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(4,163,'4:163','TA thực sự đã mặc khải cho Ngươi (Thiên Sứ Muhammad) giống như việc TA đã mặc khải cho Nuh và các vị Nabi sau Y; và TA đã mặc khải cho Ibrahim, Isma‘il, Is-haq (Isaac), Ya’qub (Jacob), và các bộ lạc (của Israel); và TA đã mặc khải cho Ysa, Ayyub (Job), Yunus, Harun (Aaron) và Sulayman (Solomon), và TA cũng đã ban cho Dawood (David) Kinh Zabur (Thánh Thi).');
INSERT INTO "vn_ruwwad" VALUES(4,164,'4:164','Có một số Thiên Sứ, trước đây TA đã kể cho Ngươi (Thiên Sứ Muhammad) nghe về Họ nhưng có một số Thiên Sứ TA đã không kể cho Ngươi nghe về Họ. Và Allah đã nói chuyện trực tiếp với Musa.');
INSERT INTO "vn_ruwwad" VALUES(4,165,'4:165','Các vị Thiên Sứ vừa là những người báo tin mừng (cho người có đức tin) vừa là những người cảnh báo (đến những kẻ vô đức tin) để nhân loại không còn có lý do khiếu nại trước Allah sau khi các vị Thiên Sứ được phái đến (gặp họ). Quả thật, Allah là Đấng Toàn Năng, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(4,166,'4:166','(Những người Do Thái đã phủ nhận Ngươi – Thiên Sứ Muhammad) nhưng một mình Allah đã đủ làm chứng cho những điều mà Ngài đã ban xuống cho Ngươi. Ngài đã ban Nó (Qur’an) xuống (cho Ngươi) bằng kiến thức của Ngài, và các vị Thiên Thần (của Ngài) làm chứng (cho điều đó). Quả thật, một mình Allah thôi đã đủ làm chứng (cho điều đó).');
INSERT INTO "vn_ruwwad" VALUES(4,167,'4:167','Chắc chắn những kẻ vô đức tin và ngăn chặn con đường chính nghĩa của Allah đã lầm lạc quá xa.');
INSERT INTO "vn_ruwwad" VALUES(4,168,'4:168','Chắc chắn những kẻ vô đức tin và làm điều sai quấy chẳng bao giờ được Allah tha thứ và cũng không được Ngài hướng dẫn đến với bất cứ con đường (đúng đắn) nào.');
INSERT INTO "vn_ruwwad" VALUES(4,169,'4:169','(Họ sẽ không được hướng dẫn đến bất cứ con đường nào) ngoài con đường dẫn họ đến Hỏa Ngục, nơi mà họ sẽ mãi mãi ở trong đó. Quả thật, sự việc đó thật dễ dàng đối với Allah.');
INSERT INTO "vn_ruwwad" VALUES(4,170,'4:170','Hỡi nhân loại, chắc chắn Thiên Sứ (Muhammad) đã đến với các ngươi mang theo Chân Lý từ Thượng Đế của các ngươi. Bởi thế, các ngươi hãy tin nơi Y, điều đó tốt cho các ngươi, còn nếu các ngươi phủ nhận Y thì quả thật vạn vật trong các tầng trời và trái đất đều thuộc về Allah. Và Allah là Đấng Hằng Biết, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(4,171,'4:171','Hỡi dân Kinh Sách (Thiên Chúa), các người chớ đừng thái quá trong tôn giáo của mình, chớ đừng nói về Allah ngoại trừ đúng theo chân lý. Quả thật, Masih Ysa con trai của Mar-yam chỉ là một Sứ Giả của Allah và (Ngài tạo ra Y từ) một lời phán được thổi vào Mar-yam và linh hồn từ Ngài. Cho nên các ngươi hãy tin vào Allah, tin vào các Thiên Sứ của Ngài và chớ đừng nói chúa ba ngôi, các ngươi hãy dừng lại ngay, điều đó tốt cho các ngươi. Quả thật, Allah là Đấng duy nhất, Ngài Siêu Việt vượt bên trên việc Ngài có một đứa con, vạn vật trong các tầng trời và vạn vật trong trái đất đều thuộc về Ngài. Quả thật, một mình Allah thôi đã đủ điều hành và chi phối mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(4,172,'4:172','Masih (Ysa) chẳng có gì phải e ngại khi là một người bề tôi của Allah, và các vị Thiên thần cận kề (Ngài) cũng vậy. Người nào xem thường việc thờ phượng Ngài và tỏ thái độ ngạo mạn thì Ngài sẽ triệu tập họ toàn bộ trình diện trước Ngài.');
INSERT INTO "vn_ruwwad" VALUES(4,173,'4:173','Đối với những ai có đức tin và hành thiện thì Ngài (Allah) sẽ ban cho họ đầy đủ phần công đức của họ và Ngài sẽ ban thêm cho họ phần thiên lộc của Ngài. Ngược lại, đối với những ai xem thường và tỏ ra ngạo mạn thì Ngài sẽ trừng phạt họ bằng hình phạt đau đớn, và họ sẽ không tìm được ai ngoài Allah làm người bảo hộ và giúp đỡ.');
INSERT INTO "vn_ruwwad" VALUES(4,174,'4:174','Hỡi nhân loại, quả thật đã có một bằng chứng xác thực từ Thượng Đế của các ngươi đến với các ngươi; và TA đã ban xuống cho các ngươi một nguồn ánh sáng rõ rệt (Qur’an).');
INSERT INTO "vn_ruwwad" VALUES(4,175,'4:175','Cho nên, đối với những ai có đức tin nơi Allah và bám chặt vào (Qur’an) thì Ngài sẽ thu nhận họ vào lòng thương xót và thiên lộc của Ngài (Thiên Đàng) và Ngài sẽ hướng dẫn họ đến với Ngài trên con đường ngay chính.');
INSERT INTO "vn_ruwwad" VALUES(4,176,'4:176','Họ hỏi Ngươi (Thiên Sứ Muhammad) về quyền thừa kế của người không cha mẹ và con cái. Ngươi hãy nói: “Allah quy định cho các ngươi như sau: Trường hợp người chết không có con cái (để thừa kế) mà chỉ có mỗi chị (em) gái ruột (hoặc chị em cùng cha) thì người này được hưởng một nửa (2) tài sản của y để lại. Và (anh em trai) được thừa kế tài sản của (chị em gái) chết đi nhưng không có con. Trường hợp người chết có hai chị (em) gái thì hai người này cùng hưởng hai phần ba (2/3) số tài sản mà y để lại. Trường hợp người chết có nhiều anh chị em gồm cả nam và nữ thì chia theo quy tắc nam thừa kế gấp đôi nữ.” Allah đã giảng giải rõ ràng cho các ngươi như thế để các ngươi không bị sai lạc (khi chia tài sản). Quả thật, Allah là Đấng hằng biết mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(5,1,'5:1','Hỡi những người có đức tin, các ngươi hãy thực hiện đúng các giao ước. Các ngươi được phép ăn (thịt) các loại gia súc ngoại trừ những loại nào đã công bố cấm các ngươi; và các ngươi không được phép ăn thịt các thú săn trong lúc các ngươi đang trong tình trạng hãm mình (làm Hajj hoặc ‘Umrah). Quả thật, Allah quy định bất cứ điều gì Ngài muốn.');
INSERT INTO "vn_ruwwad" VALUES(5,2,'5:2','Hỡi những người có đức tin, các ngươi chớ phạm những biểu hiệu của Allah(, chớ phạm các tháng cấm kỵ(2), chớ phạm đến các con vật tế cũng như những con vật đeo vòng cổ (làm dấu chúng là những con vật tế) và chớ (vi phạm sự an toàn) đến những người hướng đến Ngôi Nhà Al-Haram (Ngôi Đền Ka’bah) để tìm kiếm hồng phúc và sự hài lòng của Thượng Đế của họ. Rồi khi nào các ngươi hoàn thành xong việc hành hương thì các ngươi được tự do săn bắt. Các ngươi chớ đừng vì việc bị nhóm người nào đó ngăn cản các ngươi đến Masjid Al-Haram khiến các ngươi có hành động phạm giới. Các ngươi hãy giúp nhau làm điều đạo đức và Taqwa (ngay chính, sợ Allah) và chớ tiếp tay nhau làm điều tội lỗi và gây hận thù. Các ngươi hãy kính sợ Allah, quả thật Allah thực sự nghiêm khắc trong việc trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(5,3,'5:3','Các ngươi bị cấm ăn động vật chết, máu (tiết), thịt heo, những con vật cúng tế (cho ai khác) ngoài Allah (hoặc không nhân danh Allah lúc cắt tiết), những con vật bị giết bằng cách làm cho chết ngạt, những con vật bị giết bằng cách đánh đập, những con vật bị giết bằng cách thả rơi từ trên cao xuống, những con vật bị giết bằng cách cho chúng húc nhau, những con vật bị thú dữ ăn thịt trừ phi các ngươi kịp thời cắt tiết chúng (lúc chúng vẫn chưa chết); các ngươi bị cấm ăn thịt những con vật được giết cúng tế trên đá (cho các thần linh ngoài Allah) và các ngươi bị cấm chia phần bằng hình thức xin xăm. Quả thật, tất cả những thứ (bị cấm) đó đều bẩn thỉu, ô uế. Ngày nay, những kẻ vô đức tin đã mất hết vi vọng (trong việc phá hoại) tôn giáo của các ngươi, cho nên, các ngươi chớ đừng sợ bọn họ mà hãy sợ TA. Ngày nay, TA đã hoàn chỉnh tôn giáo cho các ngươi, TA đã hoàn tất ân huệ của TA cho các ngươi, và TA đã hài lòng chọn Islam làm tôn giáo cho các ngươi. Tuy nhiên, người nào do quá đói (phải ăn những thứ cấm kia để sinh tồn) chứ không có khuynh hướng phạm tội thì quả thật Allah là Đấng Hằng Lượng Thứ, Hằng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(5,4,'5:4','Họ (Các vị Sahabah() hỏi Ngươi (hỡi Thiên Sứ) thực phẩm nào họ được phép dùng, Ngươi hãy bảo họ: “Các ngươi được phép dùng những loại thực phẩm tốt sạch. Những thú săn mà các ngươi đã huấn luyện chúng đúng theo những gì Allah đã dạy các ngươi, các ngươi hãy ăn thịt những con vật mà chúng bắt được cho các ngươi, tuy nhiên, các ngươi hãy nhân danh Allah khi thả chúng.(4)” Các ngươi hãy kính sợ Allah, bởi quả thật, Allah rất nhanh chóng trong việc thanh toán (phán xét và thưởng phạt).');
INSERT INTO "vn_ruwwad" VALUES(5,5,'5:5','Ngày nay, các ngươi được phép ăn các thực phẩm tốt sạch. Và thức ăn của dân Kinh Sách (Do Thái và Thiên Chúa)( halal (được phép ăn) cho các ngươi và thức ăn của các ngươi halal cho họ. (Các ngươi được phép cưới) những phụ nữ đoan chính (tự do) trong số những phụ nữ có đức tin và những phụ nữ đoan chính thuộc dân Kinh Sách (Do Thái và Thiên Chúa) thời trước các ngươi với điều kiện các ngươi phải trao tặng tiền cưới đàng hoàng cho họ vì họ là vợ của các ngươi chứ không phải món đồ tình dục hay người tình lén lút. Người nào phủ nhận đức tin thì quả thật mọi việc làm (thiện tốt) của y bị xóa sạch và ở Đời Sau y sẽ là một kẻ thất bại.');
INSERT INTO "vn_ruwwad" VALUES(5,6,'5:6','Hỡi những người có đức tin, khi các ngươi muốn dâng lễ nguyện Salah thì các ngươi (phải làm Wudu’ bằng cách) rửa mặt của các ngươi, hai tay của các ngươi đến khỏi cùi chỏ, các ngươi hãy (dùng hai bàn tay thắm nước) vuốt đầu của các ngươi, (sau đó) các ngươi rửa hai bàn chân của các ngươi đến mắt cá. Trường hợp các ngươi trong tình trạng Junub( thì các ngươi phải rửa toàn thân (tắm). Trường hợp các ngươi bị bệnh hoặc đang trên đường đi xa hoặc sau khi đại tiểu tiện hoặc sau khi chung đụng (ăn nằm) với vợ nhưng không tìm thấy nước (để tẩy rửa) thì các ngươi hãy Tayammum(7) trên đất sạch, các ngươi hãy lau mặt và hai tay của các ngươi. Allah không hề muốn gây khó khăn cho các ngươi mà Ngài chỉ muốn tẩy sạch các ngươi, Ngài chỉ muốn hoàn tất ân huệ của Ngài cho các ngươi, mong rằng các ngươi biết tri ân.');
INSERT INTO "vn_ruwwad" VALUES(5,7,'5:7','Các ngươi hãy nhớ ân huệ của Allah đã ban cho các ngươi và (các ngươi hãy nhớ) giao ước mà các ngươi đã cam kết với Ngài khi các ngươi nói “chúng tôi xin nghe và tuân lệnh”. Các ngươi hãy kính sợ Allah, quả thật Allah hằng biết mọi điều trong lòng (của các ngươi).');
INSERT INTO "vn_ruwwad" VALUES(5,8,'5:8','Hỡi những người có đức tin, các ngươi hãy là những người đứng lên làm những nhân chứng cho Allah một cách công bằng. Các ngươi chớ đừng để cho lòng hận thù đối với một đám người nào đó khiến các ngươi hành động bất công, các ngươi hãy công bằng vô tư, điều đó gần với sự ngoan đạo nhất. Các ngươi hãy kính sợ Allah, quả thật, Allah là Đấng Thông Toàn mọi việc các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(5,9,'5:9','Allah đã hứa với những người có đức tin và hành thiện rằng họ sẽ được tha thứ và được ban thưởng phần thưởng vĩ đại (Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(5,10,'5:10','Và những kẻ vô đức tin và phủ nhận các lời mặc khải của TA thì sẽ là những người bạn của Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(5,11,'5:11','Hỡi những người có đức tin, các ngươi hãy nhớ lại ân huệ của Allah đã ban cho các ngươi khi có một đám người muốn ra tay hãm hại các ngươi, Ngài đã ngăn họ khỏi các ngươi. Do đó, các ngươi hãy kính sợ Allah; và những người có đức tin hãy nên phó thác cho Allah.');
INSERT INTO "vn_ruwwad" VALUES(5,12,'5:12','Quả thật, Allah đã nhận lấy giao ước của dân Israel và dựng lên trong số họ mười hai vị lãnh đạo. Allah phán bảo họ: “TA chắc chắn sẽ ở cùng với các ngươi nếu các ngươi duy trì lễ nguyện Salah, đóng Zakah, tin nơi các Thiên Sứ của TA, phò trợ Họ và cho Allah mượn( một cái mượn tốt thì TA sẽ bôi xóa tội lỗi cho các ngươi và thu nhận các ngươi vào những Ngôi Vườn Thiên Đàng bên dưới có các dòng sông chảy. Nhưng ai trong các ngươi vô đức tin sau đó thì quả thật y đã lạc khỏi con đường (ngay chính của Islam).');
INSERT INTO "vn_ruwwad" VALUES(5,13,'5:13','Bởi vì họ đã phá vỡ giao ước nên TA đã nguyền rủa họ và TA đã làm cho con tim của họ trở nên chai cứng. Họ bóp méo các lời (của Allah trong Kinh Sách bằng lời lẽ của riêng họ) và họ đã quên mất một phần mà họ được nhắc nhở trong đó. Và Ngươi (hỡi Thiên Sứ) sẽ vẫn thấy sự gian dối của họ ngoại trừ một số ít. Nhưng thôi, Ngươi hãy bỏ qua và đừng tính toán với họ; quả thật, Allah yêu thương những người làm điều tốt.');
INSERT INTO "vn_ruwwad" VALUES(5,14,'5:14','Trong số những ai đã nói chúng tôi là những tín đồ Thiên Chúa, TA (Allah) đã nhận giao ước của họ, nhưng họ đã quên mất một phần mà họ được nhắc nhở trong đó. Thế là TA đã gây ra giữa họ sự hận thù và oán ghét lẫn nhau cho đến Ngày Tận Thế. Và Allah sẽ cho họ biết những gì mà họ đã từng làm.');
INSERT INTO "vn_ruwwad" VALUES(5,15,'5:15','Hỡi người dân Kinh Sách (Do Thái và Thiên Chúa), quả thật Thiên Sứ (Muhammad) của TA đã đến gặp các ngươi trình bày rõ ràng cho các ngươi nhiều điều mà các ngươi đã che giấu trong Kinh Sách (được ban xuống cho các ngươi) và lờ đi nhiều điều. Quả thật đã đến với các ngươi nguồn ánh sáng và Kinh Sách minh bạch (Qur’an) từ nơi Allah.');
INSERT INTO "vn_ruwwad" VALUES(5,16,'5:16','Với (Qur’an), Allah hướng dẫn những ai đi theo sự hài lòng của Ngài đến với những con đường bình an (thoát khỏi sự trừng phạt) và đưa họ ra khỏi nơi tăm tối đến với ánh sáng theo sự ưng thuận của Ngài; và Ngài hướng dẫn họ đến con đường ngay chính.');
INSERT INTO "vn_ruwwad" VALUES(5,17,'5:17','Quả thật, những ai nói “Allah chính là Masih (Giê-su) con trai của Mar-yam (Maria)” là những kẻ vô đức tin. Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Vậy thì ai có quyền ngăn Allah khi Ngài muốn tiêu diệt Masih con trai của Mar-yam và mẹ của Y cùng toàn thể loài người trên trái đất?!” Quả thật, Allah nắm quyền thống trị các tầng trời và trái đất và vạn vật giữa chúng, Ngài tạo ra bất cứ thứ gì Ngài muốn, và Allah là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(5,18,'5:18','Người Do Thái cũng như tín đồ Thiên Chúa đều bảo: “Chúng tôi là những đứa con của Allah và là những người được Ngài thương yêu.” Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Nếu đúng như vậy, sao các ngươi lại bị Ngài trừng phạt bởi tội lỗi của các ngươi kia chứ?! Không, các ngươi cũng chỉ là những con người phàm tục mà Ngài đã tạo ra mà thôi. Ngài muốn tha thứ cho ai là tùy ý Ngài và muốn trừng phạt ai cũng tùy ý Ngài. Bởi Allah nắm quyền thống trị các tầng trời và trái đất và vạn vật giữa chúng; mọi vật đều phải quay về (trình diện) Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(5,19,'5:19','Hỡi dân Kinh Sách, quả thật Thiên Sứ (Muhammad) của TA đã đến gặp các ngươi trình bày rõ cho các ngươi về việc (ngừng gửi) các Sứ Giả trong một khoảng thời gian, kẻo các ngươi nói: “Chẳng có một (vị Sứ Giả) nào đến báo tin mừng cũng như cảnh báo chúng tôi cả!” Do đó, (Thiên Sứ Muhammad), một vị vừa báo tin mừng đồng thời vừa cảnh báo đã đến gặp các ngươi. Quả thật, Allah là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(5,20,'5:20','Ngươi (hỡi Thiên Sứ) hãy nhớ lại khi Musa (Môi-sê) nói với dân của mình: “Này hỡi dân ta, các người hãy nhớ về ân huệ mà Allah đa ban cho các người khi Ngài đã lựa chọn trong các người làm những vị Nabi, Ngài đã làm cho các người trở thành vua chúa( và Ngài đã ban cho các người những thứ(10) mà Ngài đã không ban cho bất kỳ ai trong thiên hạ (trong thời của các người).”');
INSERT INTO "vn_ruwwad" VALUES(5,21,'5:21','“Này hỡi dân ta, các người hãy đi vào vùng đất linh thiêng (Jerusalem) đã được Ngài an bày cho các người (giành chiến thắng) và các người chớ đừng quay lưng trở lui kẻo các người trở thành đám người thua thiệt (ở trần gian và cả Đời Sau).”');
INSERT INTO "vn_ruwwad" VALUES(5,22,'5:22','(Người dân của Musa) đáp: “Hỡi Musa, quả thật trong (Jerusalem) đó có một đám dân có sức mạnh phi thường(. Chúng tôi sẽ không bao giờ vào đó cho tới khi nào họ rời đi. Nếu họ rời khỏi nơi đó thì chúng tôi mới đi vào.”');
INSERT INTO "vn_ruwwad" VALUES(5,23,'5:23','Hai người đàn ông( trong số những người biết kính sợ Allah, (cả hai) được Allah ban cho ân huệ, nói: “Các người cứ việc tiến vào cổng (chính), các người chỉ cần tiến vào được bên trong thì các người chắc chắn sẽ giành chiến thắng, các người hãy phó thác cho Allah nếu các người là những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(5,24,'5:24','(Người dân của Musa) nói: “Này Musa, quả thật chúng tôi sẽ không bao giờ đi vào khi mà họ (đám người phi thường) vẫn còn ở trong đó. Thế nên, ngươi và Thượng Đế của ngươi hãy đi (vào trong đó) mà chiến đấu còn chúng tôi sẽ ngồi (đợi) ở đây.”');
INSERT INTO "vn_ruwwad" VALUES(5,25,'5:25','(Musa) cầu nguyện: “Lạy Thượng Đế của bề tôi, quả thật bề tôi (không quản lý được ai) ngoài bản thân bề tôi và người anh em (Harun) của bề tôi. Xin Ngài hãy tách bầy tôi ra khỏi đám người bất tuân kia!”');
INSERT INTO "vn_ruwwad" VALUES(5,26,'5:26','Allah phán (với Musa): “Vậy thì nơi đó (vùng đất linh thiêng Jerusalem) là vùng đất cấm đối với họ trong suốt thời gian bốn mươi năm, họ phải sống lang thang khắp nơi trên trái đất. Cho nên, Ngươi (Musa) chớ đừng thương tiếc cho đám người dấy loạn đó.”');
INSERT INTO "vn_ruwwad" VALUES(5,27,'5:27','Ngươi (hỡi Thiên Sứ) hãy đọc cho họ (dân Do Thái) nghe thông tin thật về hai đứa con trai của Adam(, khi cả hai cùng hiến dâng lễ vật (lên Allah) thì chỉ một người trong hai người họ được chấp nhận còn người kia thì không. (Thế là Qabil tức giận nói với Habil): “Tao phải giết mày.” (Habil) đáp: “Quả thật, Allah chỉ chấp nhận từ những người ngoan đạo mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(5,28,'5:28','“Dù anh có ra tay giết chết tôi thì tôi cũng sẽ không ra tay giết trả lại anh bởi vì tôi thực sự sợ Allah, Thượng Đế của vũ trụ và vạn vật.”');
INSERT INTO "vn_ruwwad" VALUES(5,29,'5:29','“Hơn nữa, tôi muốn anh gánh tội (giết) tôi và cả tội (nghịch lại Allah) của anh (trước đó) để anh sẽ làm bạn với Hỏa Ngục, và đó là hậu quả dành cho đám người sai quấy bất công.”');
INSERT INTO "vn_ruwwad" VALUES(5,30,'5:30','Bản ngã (hướng về điều xấu trong con người của Qabil) không ngừng xúi giục y nên y đã giết( đứa em của mình, thế là y đã trở thành một kẻ thua thiệt.');
INSERT INTO "vn_ruwwad" VALUES(5,31,'5:31','(Sau khi giết chết Habil, Qabil không biết phải xử trí thế nào). Rồi Allah gửi đến một con quạ( bới đất để cho y thấy cách chôn đi cái xác người em của y. (Nhìn thấy cảnh cảnh con quạ bới đất, Qabil) nói: “Mình thật khốn nạn, lẽ nào mình còn không bằng cả con quạ kia đến nỗi không biết cách chôn xác em của mình?!” Thế là y trở nên hối hận.');
INSERT INTO "vn_ruwwad" VALUES(5,32,'5:32','Vì lẽ đó, TA (Allah) đã qui định cho con cháu Israel rằng ai giết chết một sinh mạng không phải để đền mạng (theo luật Qisas) hoặc không phải là kẻ phá hoại trái đất thì giống như y đã giết toàn bộ nhân loại; và ai cứu một mạng người thì giống như y đã cứu toàn bộ nhân loại. Quả thật, các Thiên Sứ của TA đã mang đến cho họ những bằng chứng rõ rệt nhưng rồi sau đó đa số họ vẫn là những kẻ phá hoại trên trái đất.');
INSERT INTO "vn_ruwwad" VALUES(5,33,'5:33','Quả thật, hình phạt thích đáng dành cho những kẻ gây chiến với Allah và Thiên Sứ của Ngài cũng như nỗ lực phá hoại trên trái đất chỉ có việc: hoặc giết hoặc đóng đinh trên thập tự giá hoặc chặt tay này chân kia hoặc trục xuất ra khỏi lãnh thổ. Đó là một sự trừng phạt nhục nhã cho họ ở trần gian, còn ở Đời Sau họ sẽ phải chịu một sự trừng phạt khủng khiếp (hơn).');
INSERT INTO "vn_ruwwad" VALUES(5,34,'5:34','Ngoại trừ kẻ nào biết ăn năn sám hối trước khi các ngươi chinh phục được họ. Các ngươi hãy biết rằng quả thật Allah là Đấng Hằng Tha Thứ, Hằng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(5,35,'5:35','Hỡi những người có đức tin, các ngươi hãy kính sợ Allah, các ngươi hãy tìm phương tiện đến gần Ngài (qua việc làm đúng theo điều Ngài bảo và tránh xa điều Ngài cấm) và các ngươi hãy chiến đấu cho con đường chính nghĩa của Ngài, mong rằng các ngươi thành công.');
INSERT INTO "vn_ruwwad" VALUES(5,36,'5:36','Những kẻ vô đức tin, cho dù họ có sở hữu mọi thứ trong trái đất và có thêm những gì giống như thế để chuộc tội hầu thoát khỏi sự trừng phạt của Ngày Phán Xét Cuối Cùng thì chắc chắn cũng sẽ không được chấp nhận, chắc chắn họ sẽ phải bị trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(5,37,'5:37','Họ muốn thoát khỏi Hỏa Ngục nhưng họ sẽ không thoát được khỏi nơi đó, họ sẽ bị trừng phạt đời đời kiếp kiếp.');
INSERT INTO "vn_ruwwad" VALUES(5,38,'5:38','Đối với người trộm cặp, nam cũng như nữ (khi đã đủ bằng chứng kết tội cũng như các tình tiết không thể không thi hành bản án) thì các ngươi hãy chặt bàn tay (phải) của họ, một sự trừng phạt về tội mà họ đã phạm, một hình thức răn đe từ Allah (dành cho hành động trộm cắp). Quả thật, Allah là Đấng Toàn Năng, Anh Minh.');
INSERT INTO "vn_ruwwad" VALUES(5,39,'5:39','Tuy nhiên, ai ăn năn sám hối sau hành động sai trái của mình và chịu sửa đổi thì chắc chắn Allah sẽ chấp nhận sự sám hối của y, quả thật Allah hằng tha thứ, hằng khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(5,40,'5:40','Phải chăng Ngươi (Thiên Sứ) không biết Allah nắm quyền thống trị các tầng trời và trái đất, Ngài muốn trừng phạt ai tùy ý Ngài và tha thứ cho ai cũng tùy ý Ngài?! Quả thật, Allah toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(5,41,'5:41','Hỡi Thiên Sứ (Muhammad), Ngươi chớ đừng buồn rầu cho những ai đua nhau chạy đến với sự vô đức tin trong số những kẻ chỉ nói trên đầu môi ‘chúng tôi đã có đức tin’ còn trái tim thì không hề có đức tin. Và (Ngươi cũng đừng buồn rầu cho) đám người Do Thái, những người mà họ chỉ biết nghe theo lời giả dối của đám người khác, không chịu đến gặp Ngươi, cố ý bóp méo lời lẽ trong (Tawrah được mặc khải cho họ), họ nói: “Nếu (Muhammad) mang đến cho các ngươi những gì phù hợp với ý thích của các ngươi thì các ngươi hãy nhận lấy còn những gì khác thì hãy coi chừng.”( Ai mà Allah muốn cho y lầm lạc thì Ngươi (Thiên Sứ) sẽ không bao giờ có thể giúp được gì cho y. Đám người đó đã không được Allah tẩy sạch tấm lòng của họ, họ sẽ bị hạ nhục ở trần gian này còn ở Đời Sau họ sẽ phải chịu một sự trừng phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(5,42,'5:42','Họ thường nghe điều giả dối và thường ăn những thứ cấm (như đồng tiền cho vay, đồng tiền gian lận, hối lộ, ...). Nếu như họ đến gặp Ngươi (Thiên Sứ) yêu cầu Ngươi phân xử các vụ việc giữa họ) thì hoặc Ngươi phân xử cho họ hoặc Ngươi từ chối (tùy ý Ngươi). Nếu Ngươi từ chối phân xử cho họ thì điều đó chẳng gây hại gì cho Ngươi còn nếu Ngươi phân xử cho họ thì Ngươi hãy phân xử một cách vô tư và công bằng, quả thật Allah yêu thương những người công minh chính trực.');
INSERT INTO "vn_ruwwad" VALUES(5,43,'5:43','Sao họ yêu cầu Ngươi phân xử (các vụ việc giữa họ) trong khi ở nơi họ đã có Kinh Tawrah chứa đựng giáo luật của Allah để rồi sau đó họ lại quay lưng?! Thực chất họ không phải là những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(5,44,'5:44','Quả thật, TA đã ban xuống Kinh Tawrah (cho Musa – Môi-sê), trong đó có nguồn chỉ đạo và ánh sáng (cho dân Israel). Các vị Nabi phục tùng mệnh lệnh (của Allah), những người Do Thái, những tu sĩ, những người ngoan đạo mẫu mực đã dùng Nó (Tawrah) để phân xử cho những ai là người Do Thái vì họ được ủy thác việc bảo quản Kinh Sách của Allah cũng như được ủy thác làm những nhân chứng cho Nó. Thế nên, các ngươi (những người Do Thái) chớ đừng sợ hãi trước thiên hạ mà hãy kính sợ TA và chớ đừng bán những lời mặc khải của TA với cái giá ít ỏi. Những ai không phân xử theo những gì được Allah ban xuống thì họ là những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(5,45,'5:45','Và TA đã quy định cho (người Do Thái) trong (Tawrah): “Mạng phải đền mạng, mắt phải đền mắt, mũi phải đền mũi, tai phải đền tai, răng phải đền răng và các thương tích phải đền bằng các thương tích theo luật Qisas”. Tuy nhiên, người nào vì tích công đức mà lượng thứ thì đó là cách xóa tội cho chính y, và người nào không phân xử theo những gì mà Allah ban xuống thì những người đó là những kẻ sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(5,46,'5:46','Rồi TA đã cử phái Ysa con trai của Mar-yam nối gót (các vị Nabi trước) xác nhận lại Kinh Tawrah đã mặc khải trước đó. TA đã ban xuống cho Y Kinh Injil (Tân Ước), trong đó chứa đựng nguồn chỉ đạo và ánh sáng, một sự chứng nhận cho những điều đã được mặc khải trong Kinh Tawrah trước. Và (Kinh Injil) là nguồn chỉ đạo đồng thời cũng là lời cảnh tỉnh cho những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(5,47,'5:47','Cho nên, người dân của Kinh Injil (tức Thiên Chúa) hãy phân xử theo những gì được Allah đã ban xuống trong đó. Người nào không phân xử theo những điều được Allah đã ban xuống thì họ là những kẻ dấy loạn bất tuân.');
INSERT INTO "vn_ruwwad" VALUES(5,48,'5:48','TA (Allah) đã ban xuống cho Ngươi (Thiên Sứ) Kinh (Qur’an) bằng sự thật. Nó chứng thực lại mọi điều có trong Kinh Sách trước đây cũng như bảo tồn (nội dung) trong các Kinh Sách đó. Do đó, Ngươi (hỡi Thiên Sứ) hãy phân xử (các vụ việc) giữa họ theo những điều mà Allah đã ban xuống, Ngươi chớ chạy theo lòng ham muốn của họ rồi ruồng bỏ chân lý đã đến với Ngươi. TA đã qui định cho mỗi (cộng đồng) trong các ngươi một hệ thống luật pháp và một lề lối riêng biệt. Nếu muốn, Ngài đã làm cho các ngươi thành một cộng đồng duy nhất, nhưng (Ngài đã không làm thế) bởi vì Ngài muốn thử thách các ngươi qua những điều đã ban xuống cho các ngươi. Do đó, các ngươi hãy đua nhau làm điều thiện tốt, (bởi lẽ rồi đây) tất cả các ngươi sẽ phải trở về trình diện Allah, rồi Ngài sẽ cho các ngươi biết rõ về những điều mà các ngươi từng bất đồng nhau.');
INSERT INTO "vn_ruwwad" VALUES(5,49,'5:49','Ngươi (hỡi Thiên Sứ) hãy phân xử mọi người theo những điều mà Allah đã ban xuống và chớ đừng đi theo ý muốn của họ, Ngươi hãy coi chừng kẻo bị họ làm cho Ngươi nghịch lại với một phần những điều mà Allah đã ban xuống cho Ngươi. Nếu như họ ngoảnh mặt thì Ngươi hãy biết rằng quả thật Allah chỉ muốn trừng phạt họ về một số tội lỗi của họ. Quả thật đa số nhân loại là những kẻ bất tuân.');
INSERT INTO "vn_ruwwad" VALUES(5,50,'5:50','Lẽ nào họ mong muốn được phân xử theo luật lệ của thời kỳ đen tối (thời tiền Islam)?! Và ai tốt hơn Allah về việc qui định giới luật dành cho đám người có đức tin kiên định?!');
INSERT INTO "vn_ruwwad" VALUES(5,51,'5:51','Hỡi những người có đức tin, các ngươi chớ đừng lấy những người Do Thái và Thiên Chúa làm đồng mình. Họ là đồng minh của nhau. Ai trong các ngươi lấy họ làm đồng minh thì y thuộc phe của họ. Quả thật, Allah không hướng dẫn đám người làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(5,52,'5:52','Ngươi (hỡi Thiên Sứ) sẽ nhìn thấy những kẻ mà trong con tim của họ mang chứng bệnh (giả tạo đức tin) thường tới lui giữa họ (Do Thái và Thiên Chúa), nói: “Chúng tôi sợ chúng tôi sẽ gặp phải điều không tốt lành (nếu những người Muslim sẽ giành chiến thắng và toàn quyền kiểm soát).” Bởi thế, nếu Allah ban cho (Thiên Sứ và những người có đức tin) giành thắng lợi hoặc một quyết định (tốt đẹp) nào đó từ nơi Ngài thì họ (đám giả tạo đức tin) sẽ đâm ra tiếc nuối về những điều mà họ đã ẩn giấu trong lòng của họ.');
INSERT INTO "vn_ruwwad" VALUES(5,53,'5:53','Những người có đức tin nói: “Chẳng phải đám người (giả tạo đức tin) này đã thề rất mạnh mẽ với Allah rằng họ luôn sát cánh với các người (những người có đức tin) hay sao?! Việc hành đạo của bọn họ đã trở nên vô nghĩa thế là họ trở thành đám người thất bại.');
INSERT INTO "vn_ruwwad" VALUES(5,54,'5:54','Hỡi những người có đức tin, ai trong các ngươi rời bỏ tôn giáo của mình thì Allah sẽ mang đến một nhóm người khác (thay thế các ngươi), Ngài sẽ yêu thương họ và họ cũng sẽ yêu thương Ngài; họ nhân từ đối với những người có đức tin nhưng lại rất cứng rắn đối với những kẻ vô đức tin; họ anh dũng chiến đấu vì chính nghĩa của Allah và không sợ sự trách cứ của kẻ chỉ trích. Đấy là hồng phúc của Allah, Ngài ban cho ai Ngài muốn, và Allah là Đấng Rộng Rãi Bao La, hằng biết (tất cả mọi sự việc).');
INSERT INTO "vn_ruwwad" VALUES(5,55,'5:55','Quả thật, đồng minh thật sự của các ngươi chỉ có Allah, Thiên Sứ (Muhammad) của Ngài và những người có đức tin, những người mà họ chu đáo duy trì lễ nguyện Salah, xuất Zakah và cúi mình (thần phục Ngài).');
INSERT INTO "vn_ruwwad" VALUES(5,56,'5:56','Ai nhận Allah, Thiên Sứ của Ngài và những người có đức tin làm đồng minh thì quả thật phe của Allah chắc chắn sẽ giành chiến thắng.');
INSERT INTO "vn_ruwwad" VALUES(5,57,'5:57','Hỡi những người có đức tin, các ngươi chớ nhận lấy những kẻ mang tôn giáo của các ngươi ra nhạo báng và giễu cợt từ những người được ban cho Kinh Sách thời trước các ngươi (Do Thái và Thiên Chúa) và những người vô đức tin làm đồng minh. Các ngươi hãy kính sợ Allah nếu các ngươi thực sự là những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(5,58,'5:58','Khi các ngươi cất lời (Azan) gọi nhau đến lễ nguyện Salah thì bọn họ lấy điều đó ra nhạo báng và giễu cợt. Đó là bởi vì bọn họ là một đám người không hiểu gì cả.');
INSERT INTO "vn_ruwwad" VALUES(5,59,'5:59','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Hỡi người dân Kinh Sách, lẽ nào các người quấy rối chúng tôi chỉ vì chúng tôi đã tin nơi Allah, nơi những điều được ban xuống cho chúng tôi cũng như những điều đã được ban xuống trước đây. Quả thật, đa số các người là những kẻ quấy rối, bất tuân.”');
INSERT INTO "vn_ruwwad" VALUES(5,60,'5:60','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Các người (dân Kinh Sách) có muốn Ta báo cho các người biết một thứ còn tệ hại hơn điều đó và đáng bị trừng phạt ở nơi Allah? Đó là những kẻ mà Allah đã nguyền rủa, đã phẫn nộ, những kẻ mà Ngài biến một số tên trong bọn họ thành khỉ, heo và những kẻ mà Ngài đã khiến họ thờ phượng Taghut (tà thần). Họ là những kẻ sẽ có được một nơi cư ngụ tồi tệ nhất (ở cõi Đời Sau) là một đám người lạc xa con đường (chân lý).”');
INSERT INTO "vn_ruwwad" VALUES(5,61,'5:61','Khi họ (những kẻ giả tạo đức tin trong đám người Do Thái) đến gặp các ngươi (những người có đức tin), họ nói: “Chúng tôi đã có đức tin”. Quả thật, họ đã vào (Islam) bằng sự vô đức tin và quả thật họ cũng đã trở ra giống như thế. Tất nhiên, Allah biết rõ những gì họ che giấu.');
INSERT INTO "vn_ruwwad" VALUES(5,62,'5:62','Và Ngươi (hỡi Thiên Sứ) thấy đa số bọn họ hăng say lao vào tội lỗi, hăng say gieo rắc hận thù và hăng say ăn đồ cấm. Thật tồi tệ và xấu xa cho những gì họ đã làm.');
INSERT INTO "vn_ruwwad" VALUES(5,63,'5:63','Tại sao những người thông tỏ giáo luật và các tu sĩ (Do Thái) lại không ngăn cản lời nói tội lỗi của họ cũng như việc họ ăn những thứ cấm?! Thật tồi tệ và xấu xa cho những gì họ đã làm.');
INSERT INTO "vn_ruwwad" VALUES(5,64,'5:64','Người Do Thái nói: “Tay của Allah đã bị xiềng xích.” Tay của bọn họ mới bị xiềng xích và bọn họ bị nguyền rủa bởi điều (phạm thượng) đã nói ra. Không (giống như những gì bọn họ nói), đôi tay của Ngài đều mở rộng, Ngài muốn ban phát (thiên lộc của Ngài) như thế nào tùy ý. Và chắc chắn bọn họ sẽ càng thái quá và vô đức tin hơn nữa bởi những gì mà Thượng Đế của Ngươi đã ban xuống cho Ngươi (hỡi Thiên Sứ). TA gieo thêm vào lòng họ sự thù hận và ganh ghét cho đến Ngày Tận Thế. Cứ mỗi lần bọn họ châm ngòi cho ngọn lửa chiến tranh thì Allah dập tắt nó. Họ nỗ lực phá hoại trên trái đất; và Allah không yêu thương những kẻ phá hoại.');
INSERT INTO "vn_ruwwad" VALUES(5,65,'5:65','Giá như người dân Kinh Sách (Do Thái và Thiên Chúa) có đức tin (nơi những điều mà Muhammad mang đến) và kính sợ (Allah) thì chắc chắn TA đã xóa tội cho họ và (vào Ngày Sau) TA sẽ thu nhận họ vào những Ngôi Vườn Thiên Đàng vĩnh cữu.');
INSERT INTO "vn_ruwwad" VALUES(5,66,'5:66','Giá như họ thực thi và chấp hành theo Tawrah và Injil cũng như những gì mà Thượng Đế của họ đã ban xuống cho họ (trong Qur’an) thì chắc chắn họ đã hưởng (vô số) thiên lộc từ bên trên họ cũng như từ dưới chân của họ. Trong số họ, có một số ít đi theo chân lý còn đa số toàn làm điều thối tha (tội lỗi).');
INSERT INTO "vn_ruwwad" VALUES(5,67,'5:67','Hỡi Thiên Sứ (Muhammad), Ngươi hãy truyền đạt toàn bộ thông điệp mà Thượng Đế của Ngươi đã ban xuống cho Ngươi. Nếu như Ngươi không làm vậy thì quả thật Ngươi đã không truyền đạt thông điệp của Ngài. Allah sẽ bảo vệ Ngươi khỏi (sự hãm hại của) thiên hạ. Quả thật, Allah không bao giờ hướng dẫn đám người vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(5,68,'5:68','Ngươi (hỡi Thiên Sứ) hãy nói: “Hỡi dân Kinh Sách, các người không có bất cứ cơ sở nào (cho tôn giáo của mình) cho đến khi các người tuân thủ theo Tawrah và Injil và những điều được ban xuống cho các người từ Thượng Đế của các người (Qur’an). Chắc chắn bọn họ (dân Kinh Sách) sẽ càng thái quá và vô đức tin hơn nữa qua những gì mà Thượng Đế của Ngươi đã ban xuống cho Ngươi (hỡi Thiên Sứ), cho nên Ngươi chớ thương tiếc cho đám người vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(5,69,'5:69','Quả thật, những người có đức tin và các cộng đồng người Do Thái, người Sabi-un( (Sabian) và người Thiên Chúa, ai trong số họ có đức tin nơi Allah, nơi cõi Đời Sau và hành thiện thì họ sẽ không phải lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "vn_ruwwad" VALUES(5,70,'5:70','Quả thật, TA đã nhận lấy giao ước trịnh trọng của dân Israel và TA đã cử phái các vị Thiên Sứ đến với họ. Tuy nhiên, cứ mỗi lần vị Thiên Sứ (của TA) đến gặp họ với những điều không phù hợp với ý muốn của họ thì họ phủ nhận một số (các vị Thiên Sứ) và giết chết một số khác.');
INSERT INTO "vn_ruwwad" VALUES(5,71,'5:71','Họ cứ tưởng (việc hủy bỏ giao ước) không gây hại đến (họ), họ giả mù giả điếc (trước điều chân lý) rồi Allah đã tha thứ cho họ, nhưng sau đó đa số họ lại tiếp tục giả mù giả điếc. Và tất nhiên Allah thấy rõ mọi hành vi của họ.');
INSERT INTO "vn_ruwwad" VALUES(5,72,'5:72','Quả thật, những ai đã nói “Allah chính là Masih con trai của Mar-yam” là những kẻ vô đức tin. (Bởi lẽ) Masih đã nói: “Hỡi dân Israel, các người hãy tôn thờ Allah, Thượng Đế của Ta và của các người. Ai tổ hợp thần linh cùng với Allah thì Ngài cấm kẻ đó vào Thiên Đàng và nơi ở của y sẽ là Hỏa Ngục, và đám người sai quấy sẽ không được ai giúp đỡ.”');
INSERT INTO "vn_ruwwad" VALUES(5,73,'5:73','Quả thật, những ai đã nói “Allah là ngôi thứ ba trong thuyết Ba Ngôi(” là những kẻ vô đức tin, (bởi lẽ) chỉ có một Thượng Đế duy nhất (là Allah). Nếu như bọn họ không chấm dứt ngay những lời nói (phạm thượng) đó thì chắc chắn những kẻ vô đức tin trong bọn họ sẽ phải bị một sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(5,74,'5:74','Sạo bọn họ không chịu (thu lại lời nói) mà ăn năn sám hối với Ngài và cầu xin Ngài tha thứ trong khi Allah là Đấng Hằng Tha Thứ, Khoan Dung?');
INSERT INTO "vn_ruwwad" VALUES(5,75,'5:75','Masih con trai của Mar-yam không là gì ngoài thân phận Thiên Sứ như bao vị Thiên Sứ trước Y và mẹ của Y cũng chỉ là một phụ nữ có niềm tin kiên định (vào Allah), cả hai đều ăn thức ăn (như bao người phàm khác). Ngươi (hỡi Thiên Sứ) hãy nhìn xem TA trình bày cho bọn họ các lời mặc khải (của TA) như thế nào, rồi Ngươi hãy nhìn xem bọn họ tránh xa (chân lý) ra sao?!');
INSERT INTO "vn_ruwwad" VALUES(5,76,'5:76','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Lẽ nào các người lại tôn thờ ngoài Allah những thứ không thể hãm hại hoặc giúp đỡ được các người trong khi Allah là Ðấng Hằng Nghe, Hằng Biết?!”');
INSERT INTO "vn_ruwwad" VALUES(5,77,'5:77','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Hỡi dân Kinh Sách (Thiên Chúa), các người chớ đừng thái quá trong tôn giáo của mình một cách không đúng sự thật. Các người chớ đi theo dục vọng của đám người lầm lạc trước đây (Do Thái), họ đã làm cho nhiều người lầm lạc và họ đã lệch xa khỏi con đường (chân lý).”');
INSERT INTO "vn_ruwwad" VALUES(5,78,'5:78','Đám người vô đức tin của dân Israel đã bị nguyền rủa trên chiếc lưỡi của Dawood và Ysa con trai Mar-yam. Sở dĩ bọn họ bị như thế là vì họ đã bất tuân và vượt quá giới hạn.');
INSERT INTO "vn_ruwwad" VALUES(5,79,'5:79','Bọn họ đã không ngăn cản nhau làm điều sai trái mà bọn đã từng làm. Thật xấu xa thay cho những gì bọn họ đã làm.');
INSERT INTO "vn_ruwwad" VALUES(5,80,'5:80','Ngươi (hỡi Thiên Sứ) sẽ thấy rằng sẽ có rất nhiều người trong bọn họ (Do Thái) cấu kết với những người vô đức tin (nhằm hãm hại tín đồ Muslim), thật xấu xa thay cho hành vi mà họ đã gởi đi trước cho bản thân mình, đó là bọn họ đã bị Allah phẫn nộ và bọn họ sẽ phải bị trừng phạt mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(5,81,'5:81','Và nếu như bọn họ (Do Thái) có đức tin nơi Allah, nơi Nabi (Muhammad) và những gì được ban xuống cho Y thì bọn họ đã không nhận những người vô đức tin làm đồng minh. Tuy nhiên, đa số bọn họ là những kẻ bất tuân, dấy loạn.');
INSERT INTO "vn_ruwwad" VALUES(5,82,'5:82','Chắc chắn Ngươi (hỡi Thiên Sứ) sẽ thấy kẻ thù thâm độc nhất đối với những người có đức tin chính là người Do Thái và những kẻ thờ đa thần. Và chắc chắn Ngươi sẽ thấy người có tình cảm gần gũi với người có đức tin nhất chính là những ai đã nói: “Chúng tôi là Thiên Chúa”. Đó là bởi vì trong họ có những học giả và các bậc tu hành không tự cao tự đại.');
INSERT INTO "vn_ruwwad" VALUES(5,83,'5:83','(Những người này – chẳng hạn như vua Al-Najashi (Negus) và quần thần của ông), khi họ nghe những điều được ban xuống cho Thiên Sứ (Muhammad), Ngươi (hỡi Thiên Sứ) thấy đôi mắt của họ rơi lệ khi nhận ra được chân lý, họ nói: “Lạy Thượng Đế của bầy tôi, bầy tôi đã tin tưởng, xin Ngài hãy ghi bầy tôi cùng với những người chứng nhận (sự thật), (đó là Muhammad và các tín đồ của Người).”');
INSERT INTO "vn_ruwwad" VALUES(5,84,'5:84','“Làm sao bầy tôi lại không có đức tin nơi Allah trong khi chân lý đã thật sự đến với bầy tôi (thông qua Thiên Sứ Muhammad), bầy tôi tham được Thượng Đế của mình thu nhận vào cùng với nhóm người đức hạnh (nơi Thiên Đàng).”');
INSERT INTO "vn_ruwwad" VALUES(5,85,'5:85','Bởi những điều họ cầu nguyện, Allah đã ban thưởng cho họ những Ngôi Vườn Thiên Đàng bên dưới có các dòng sông chảy, họ sống muôn đời trong đó. Đấy là phần thưởng dành cho người làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(5,86,'5:86','Riêng những kẻ vô đức tin và phủ nhận các lời mặc khải của TA, họ sẽ làm bạn với Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(5,87,'5:87','Hỡi những người có đức tin, các ngươi chớ đừng tự ý cấm những thứ tốt lành mà Allah đã cho phép các ngươi. Tuy nhiên, các ngươi chớ vượt quá giới hạn, quả thật, Allah không yêu thương những kẻ vượt quá giới hạn.');
INSERT INTO "vn_ruwwad" VALUES(5,88,'5:88','Các ngươi hãy ăn những thực phẩm Halal tốt sạch mà Allah đã ban cấp cho các ngươi và các ngươi hãy kính sợ Allah, Đấng mà các ngươi thật sự có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(5,89,'5:89','Allah không bắt tội các ngươi về những lời thề thốt do quen miệng mà Ngài chỉ bắt tội các ngươi về những lời thề thực sự có chủ ý. Tuy nhiên, để chuộc tội cho lời thề đã phạm( thì phải nuôi ăn mười người nghèo theo mức lượng trung bình mà các ngươi dùng để nuôi gia đình hoặc cấp quần áo cho họ hoặc trả tự do cho một người nô lệ (có đức tin). Đối với người nào không có khả năng thì phải nhịn chay ba ngày liên tục. Đó là cách chuộc tội cho điều đã phạm trong lời thề, và các ngươi hãy giữ lời thề của mình. Allah trình bày rõ ràng các lời mặc khải của Ngài cho các ngươi như thế, mong rằng các ngươi biết tạ ơn (Ngài).');
INSERT INTO "vn_ruwwad" VALUES(5,90,'5:90','Hỡi những người có đức tin! Quả thật (uống) rượu, cờ bạc, thờ cúng trên bàn thờ đá và xin xăm là những thứ ô uế thuộc hành vi của Shaytan. Bởi thế, các ngươi hãy tránh xa chúng mong rằng các ngươi thành đạt.');
INSERT INTO "vn_ruwwad" VALUES(5,91,'5:91','Quả thật, Shaytan chỉ muốn gieo lòng hận thù và hiềm khích giữa các ngươi qua (việc uống) rượu và cờ bạc. Thật ra (Shaytan) chỉ muốn cản trở các ngươi tưởng nhớ Allah và muốn các ngươi (sao lãng) lễ nguyện Salah mà thôi. Vậy các ngươi không chịu ngưng ư?!');
INSERT INTO "vn_ruwwad" VALUES(5,92,'5:92','Các ngươi hãy tuân lệnh Allah, vâng lời Thiên Sứ (Muhammad) và các ngươi hãy coi chừng (chớ kháng lại lệnh của Y). Nếu như các ngươi quay lưng với Y thì các ngươi hãy biết rằng quả thật Thiên Sứ của TA chỉ có nhiệm vụ truyền đạt rõ ràng mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(5,93,'5:93','Những người có đức tin và hành thiện sẽ không bị bắt tội về những gì (haram) mà họ đã ăn (uống) trong quá khứ (chẳng hạn như uống rượu trước khi có lệnh cấm) nếu (hiện tại) họ giữ mình (không phạm điều haram), có đức tin và hành thiện; sau đó, họ giữ mình hơn, có đức tin nhiều hơn, rồi họ giữ mình hơn nữa và làm tốt. Quả thật, Allah thương yêu những người làm tốt(.');
INSERT INTO "vn_ruwwad" VALUES(5,94,'5:94','Hỡi những người có đức tin, Allah chắc chắn thử thách các ngươi qua con thú săn trong tầm tay và mũi giáo của các ngươi (khi các ngươi đang trong thời gian hành hương Hajj hoặc ‘Umrah), mục đích để Allah biết rõ ai là người sợ Ngài một cách vô hình. Do đó, người nào vi phạm sau đó thì y sẽ phải chịu sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(5,95,'5:95','Hỡi những người có đức tin, các ngươi chớ đừng giết thú săn khi các ngươi đang trong thời gian hãm mình (của nghi thức Hajj hoặc ‘Umrah). Ai trong các ngươi cố tình giết thú săn (khi đang trong tình trạng hãm mình như thế) thì hình phạt dành cho y là y phải đền một con gia súc tương đương với con thú săn mà y đã giết dưới giám sát của hai người đàn ông công bằng trong các ngươi, (con vật phải) được dắt đến Ka’bah để giết (và chia thịt cho cư dân nghèo ở đó), hoặc y phải chuộc tội bằng cách nuôi ăn( những người nghèo hoặc bằng cách nhịn chay tương đương với việc nuôi ăn đó, mục đích để cho y nếm hậu quả của hành vi (giết thú săn) của y. Allah sẽ lượng thứ cho việc đã qua nhưng ai tái phạm thì Allah sẽ trừng phạt y. Quả thật, Allah là Đấng Toàn Năng, Đấng toàn quyền trừng phạt (không quyền lực nào có thể kháng cự).');
INSERT INTO "vn_ruwwad" VALUES(5,96,'5:96','Các ngươi được phép săn bắt (động vật) trên biển và được phép dùng thức ăn của nó (xác chết trên biển) làm thực phẩm cho các ngươi và cho khách bộ hành của sa mạc. Tuy nhiên, các ngươi không được phép săn bắt động vật trên cạn lúc các ngươi đang trong (thời gian) hãm mình (của nghi thức Hajj hoặc ‘Umrah). Các ngươi hãy kính sợ Allah, Đấng mà các ngươi sẽ được triệu tập (trình diện Ngài vào Ngày Phán Xét).');
INSERT INTO "vn_ruwwad" VALUES(5,97,'5:97','Allah đã qui định Ka’bah, Ngôi đền thiêng, làm một nơi để nhân loại đến hành đạo (như Salah, hành hương Hajj và ‘Umrah); (Ngài quy định) những tháng cấm (giết chóc)(, (qui định) vật giết tế và việc đeo vòng cổ (lên con vật được chọn giết tế). (Ngài qui định) như thế là để các ngươi biết rằng Allah hằng biết rõ mọi thứ trong trời đất; quả thật Allah là Đấng hằng biết mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(5,98,'5:98','(Hỡi nhân loại), các ngươi hãy biết rằng Allah rất nghiêm khắc trong việc trừng phạt (những kẻ bất tuân Ngài), và (các ngươi cũng hãy biết rằng) Allah hằng tha thứ, khoan dung (đối với những ai quay về sám hối với Ngài).');
INSERT INTO "vn_ruwwad" VALUES(5,99,'5:99','Thiên Sứ (của Allah) chỉ có nhiệm vụ truyền đạt (thông điệp của Ngài chứ không có quyền năng hướng dẫn một ai). Và Allah là Đấng biết rõ những gì các ngươi phơi bày và những gì các ngươi che giấu.');
INSERT INTO "vn_ruwwad" VALUES(5,100,'5:100','Ngươi (hỡi Thiên Sứ) hãy nói: “Cái xấu và cái tốt không hề ngang bằng nhau mặc dù sự phong phú của cái xấu có thể gây ấn tượng với các ngươi (bởi lẽ nhiều không phải là bằng chứng khẳng định giá trị của một sự việc). Cho nên, các ngươi hãy kính sợ Allah, hỡi những người thông hiểu, mong rằng các ngươi thắng lợi và thành công.');
INSERT INTO "vn_ruwwad" VALUES(5,101,'5:101','Hỡi những người có đức tin, các ngươi chớ đừng hỏi về những điều mà nếu chúng được chỉ ra cho các ngươi sẽ làm các ngươi khốn đốn. Nhưng nếu các ngươi hỏi về chúng trong lúc Kinh Qur’an được ban xuống thì chúng sẽ được trình bày rõ cho các ngươi. (Tuy nhiên) Allah đã cho qua những điều đó (khi Ngài im lặng, không đề cập chúng trong Qur’an của Ngài). Quả thật, Allah hằng tha thứ, hằng chịu đựng.');
INSERT INTO "vn_ruwwad" VALUES(5,102,'5:102','Thật ra, trước các ngươi đã có một đám người cũng đã hỏi về những điều đó, rồi sau đó họ đã trở thành những kẻ vô đức tin bởi chúng.');
INSERT INTO "vn_ruwwad" VALUES(5,103,'5:103','Allah không hề quy định về Bahirah, Sa-ibah, Wasilah hay Ham gì cả( mà chính những kẻ vô đức tin đã bịa đặt điều giả dối cho Allah. Quả thật, đa số bọn họ chẳng hiểu gì.');
INSERT INTO "vn_ruwwad" VALUES(5,104,'5:104','(Những kẻ đặt điều giả dối cho Allah này), khi có lời bảo “Các ngươi hãy đến với điều mà Allah đã ban xuống (Qur’an) và đến với Thiên Sứ (Sunnah) thì họ nói: “Những gì chúng tôi thấy tổ tiên chúng tôi làm đã quá đủ cho chúng tôi.” Lẽ nào (họ đi theo tổ tiên của họ) ngay cả khi tổ tiên của họ không biết gì cũng như không được hướng dẫn ư?!');
INSERT INTO "vn_ruwwad" VALUES(5,105,'5:105','Hỡi những người có đức tin, các ngươi phải chịu trách nhiệm cho bản thân các ngươi. Những kẻ lầm lạc sẽ không gây hại được các ngươi khi các ngươi đi đúng con đường chân lý. Rồi đây, tất cả các ngươi sẽ phải trở về trình diện Allah, Ngài sẽ cho các ngươi biết tất cả những gì các ngươi đã từng làm (trên thế gian).');
INSERT INTO "vn_ruwwad" VALUES(5,106,'5:106','Hỡi những người có đức tin, khi ai đó trong các ngươi lập di chúc trước lúc lâm chung (thì các ngươi) hãy để hai ngươi đàn ông công bằng (chính trực và trung thực) trong các ngươi đứng ra làm chứng cho các ngươi hoặc để hai người khác ngoài các ngươi (không phải Muslim) làm chứng cho các ngươi trong trường hợp các ngươi đang trên đường đi xa và cái chết bất ngờ đến với các ngươi. Nếu các ngươi nghi ngờ (về sự trung thực của) hai nhân chứng thì sau lễ nguyện Salah, các ngươi hãy bắt hai người họ thề với Allah: “Chúng tôi không bán sự làm chứng với bất cứ giá trị nào ngay cả đối với bà con thân thuộc, và chúng tôi sẽ không giấu giếm bằng chứng của Allah (vì nếu chúng tôi làm thế thì) chắc chắn chúng tôi là những kẻ tội lỗi.”');
INSERT INTO "vn_ruwwad" VALUES(5,107,'5:107','Nếu phát hiện cả hai (nhân chứng) là kẻ tội lỗi (khai man, phạm lời thề hoặc lừa gạt) thì hãy để hai người khác (thuộc thân nhân người chết) đứng ra thế chỗ cho hai nhân chứng đã phạm tội, và yêu cầu họ thề với Allah: “Sự làm chứng của chúng tôi trung thực hơn sự làm chứng của hai người trước và chúng tôi không vi phạm. (Nếu chúng tôi vi phạm) thì chúng tôi sẽ là những kẻ làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(5,108,'5:108','(Việc yêu cầu hai nhân chứng thề sau lễ nguyện Salah) là cách phù hợp nhất để có được một sự làm chứng đúng thật hoặc vì sợ lời thề bị (người thừa kế) bác bỏ sau khi họ đã thề. Các ngươi hãy kính sợ Allah và hãy nghe (lệnh của Ngài). Quả thật, Allah không bao giờ hướng dẫn đám người bất tuân.');
INSERT INTO "vn_ruwwad" VALUES(5,109,'5:109','(Hỡi nhân loại, các ngươi hãy nhớ đến) Ngày mà Allah sẽ tập kết toàn bộ các vị Thiên Sứ (của Ngài) rồi hỏi Họ: “Các cộng đồng của Các Ngươi đã đáp lại (sứ mạng của) Các Ngươi những gì?” Họ đáp: “Quả thật, bầy tôi không biết gì bởi lẽ chỉ có Ngài mới là Đấng biết rõ mọi điều vô hình.”');
INSERT INTO "vn_ruwwad" VALUES(5,110,'5:110','(Ngươi - hỡi Thiên Sứ hãy nhớ lại) khi Allah phán: “Hỡi Ysa con trai của Mar-yam, Ngươi hãy nhớ lại hồng ân mà TA đã ban cho Ngươi (khi TA tạo ra Ngươi từ người mẹ mà không cần đến người cha), và cho mẹ của Ngươi (khi TA chọn Nữ làm người phụ nữ tốt nhất trong thời đại của Nữ); (Ngươi hãy nhớ lại hồng ân mà TA đã ban cho Ngươi) khi TA đã cho Jibril đến phò trợ Ngươi, cho Ngươi nói chuyện được với thiên hạ lúc Ngươi vẫn còn nằm nôi và lúc Ngươi đã trưởng thành; (Ngươi hãy nhớ lại hồng ân mà TA đã ban cho Ngươi) khi TA đã dạy Ngươi chữ viết, lẽ khôn ngoan, Kinh Tawrah và Kinh Injil; (Ngươi hãy nhớ lại hồng ân mà TA đã ban cho Ngươi) khi Ngươi có thể tạo ra một con chim thật từ con chim đất sét được nắn hình bằng cách hà hơi vào nó dưới sự chấp thuận của TA, Ngươi có thể chữa khỏi bệnh mù bẩm sinh và bệnh bạch biến dưới phép của TA; (Ngươi hãy nhớ lại hồng ân mà TA đã ban cho Ngươi) khi Ngươi có thể cứu người chết sống lại dưới phép của TA; và (Ngươi hãy nhớ lại hồng ân mà TA đã ban cho Ngươi) khi TA đã bảo vệ Ngươi thoát khỏi sự hãm hại của dân Israel lúc Ngươi mang đến cho họ những bằng chứng rõ rệt nhưng những kẻ vô đức tin trong số họ bảo: “Đây rõ ràng chỉ trò ma thuật!”.');
INSERT INTO "vn_ruwwad" VALUES(5,111,'5:111','(Ngươi - hỡi Thiên Sứ hãy nhớ lại) khi TA (Allah) mặc khải cho các tông đồ (của Ysa) phán bảo: “Các ngươi hãy có đức tin nơi TA và nơi vị Thiên Sứ của TA. Họ nói: “Bầy tôi đã có đức tin và xin Ngài hãy chứng giám rằng bầy tôi là những tín đồ Muslim.”');
INSERT INTO "vn_ruwwad" VALUES(5,112,'5:112','(Ngươi - hỡi Thiên Sứ hãy nhớ lại) khi các tông đồ (của Ysa) nói: “Hỡi Ysa con trai của Mar-yam, Thượng Đế của Người có thể ban xuống cho chúng tôi một bàn thức ăn từ trên trời không?! (Ysa) bảo: “Các người hãy kính sợ Allah (mà bỏ đi yêu cầu quá đáng đó) nếu các người là những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(5,113,'5:113','(Các tông đồ) nói (với Ysa): “Chúng tôi muốn ăn từ chiếc bàn đầy thức ăn đó để con tim của chúng tôi vững tin hơn (về quyền năng của Allah) cũng như để biết rõ Người đã nói thật với chúng tôi; và chúng tôi sẽ là những nhân chứng cho chiếc bàn ăn đó.”');
INSERT INTO "vn_ruwwad" VALUES(5,114,'5:114','Ysa con trai của Mar-yam khẩn cầu: “Lạy Allah, Thượng Đế của bầy tôi, xin Ngài hãy ban xuống cho bầy tôi một chiếc bàn đầy thức ăn từ trên trời để làm một bàn tiệc mừng vui cho bầy tôi như là một phép màu của Ngài cho bầy tôi từ người đầu tiên cho đến người cuối cùng, xin Ngài hãy ban bổng lộc cho bầy tôi bởi Ngài là Đấng Ban bổng lộc tốt nhất.”');
INSERT INTO "vn_ruwwad" VALUES(5,115,'5:115','Allah phán: “TA sẽ ban nó (bàn thức ăn) xuống cho các ngươi. Tuy nhiên, nếu ai trong các ngươi vô đức tin sau đó thì chắc chắn TA sẽ trừng phạt kẻ đó bằng một sự trừng phạt mà TA chưa từng trừng phạt ai trên đời này.”');
INSERT INTO "vn_ruwwad" VALUES(5,116,'5:116','(Ngươi - hỡi Thiên Sứ hãy nhớ lại) khi Allah phán bảo: “Hỡi Ysa con trai của Mar-yam, phải chăng nhà Ngươi đã bảo thiên hạ ‘các người hãy tôn vinh Ta và mẹ của Ta như hai thượng đế thay vì Allah’ đúng không? (Ysa) thưa: “Thật vinh quang và tối cao thay Ngài, làm sao bề tôi dám nói ra điều mà bề tôi không có thẩm quyền. Nếu như bề tôi thực sự đã nói như thế thì chắc chắn Ngài đã biết bởi vì Ngài biết rõ những gì trong bản thân bề tôi còn bề tôi thì không biết những điều trong bản thân Ngài. Quả thật, Ngài là Đấng thông toàn mọi điều vô hình.”');
INSERT INTO "vn_ruwwad" VALUES(5,117,'5:117','“Bề tôi chỉ nói với họ những gì Ngài đã ra lệnh cho bề tôi, (đó là): ‘Các người hãy thờ phượng Allah, Thượng Đế của Ta và Thượng Đế của các người.’ Bề tôi là nhân chứng cho họ trong suốt thời gian bề tôi còn sống bên họ, nhưng sau khi Ngài đưa bề tôi lên cùng với Ngài thì Ngài là Đấng Giám Sát họ và Ngài làm chứng cho tất cả mọi việc.”');
INSERT INTO "vn_ruwwad" VALUES(5,118,'5:118','“Nếu Ngài trừng phạt họ thì bởi vì họ vốn dĩ là đám bầy tôi của Ngài nhưng nếu Ngài lượng thứ cho họ thì bởi vì Ngài là Đấng Toàn Năng, Sáng Suốt.”');
INSERT INTO "vn_ruwwad" VALUES(5,119,'5:119','(Allah) phán (với Ysa): “Đây là ngày mà những người trung thực sẽ được hưởng lợi từ sự trung thực của họ. Họ sẽ có được những Ngôi Vườn Thiên Đàng bên dưới có dòng sông chảy, họ sẽ sống trong đó đời đời, Allah sẽ hài lòng về họ và họ cũng sẽ hài lòng về Ngài. Đó là sự thành tựu vĩ đại.”');
INSERT INTO "vn_ruwwad" VALUES(5,120,'5:120','Quyền thống trị và chế ngự các tầng trời và trái đất và vạn vật trong trời đất đều thuộc về một mình Allah. Và Ngài là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(6,1,'6:1','Alhamdulillah, Đấng đã tạo hóa các tầng trời và trái đất và làm ra bóng tối (ban đêm) và ánh sáng (ban ngày). Tuy nhiên, những kẻ vô đức tin cứ ngang nhiên dựng lên các đối tác ngang vai cùng với Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(6,2,'6:2','(Hỡi nhân loại!) Ngài là Đấng đã tạo hóa (Adam, cha của) các ngươi từ đất sét, rồi Ngài ấn định thời hạn (cho mỗi người các ngươi trên trần gian), và Ngài ấn định một thời hạn khác nữa (cho các ngươi nơi cõi mộ), điều đó thuộc về riêng Ngài (không ai biết rõ thời hạn của nó ngoại trừ một mình Ngài). Tuy nhiên, các ngươi vẫn hoài nghi (về Sự Phục Sinh).');
INSERT INTO "vn_ruwwad" VALUES(6,3,'6:3','Ngài chính là Allah (Đấng đáng được thờ phượng) trong các tầng trời và trái đất, Ngài biết điều thầm kín và điều công khai của các ngươi, và Ngài biết rõ mọi điều các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(6,4,'6:4','(Những kẻ thờ đa thần), bất cứ dấu hiệu nào trong các dấu hiệu của Thượng Đế đến với họ thì họ đều quay mặt ngoảnh đi.');
INSERT INTO "vn_ruwwad" VALUES(6,5,'6:5','Quả thật, họ đã phủ nhận Chân Lý khi Nó đến với họ. Rồi đây họ sẽ biết rõ (chân tướng) về điều mà họ đã chế giễu.');
INSERT INTO "vn_ruwwad" VALUES(6,6,'6:6','Lẽ nào họ không thấy TA (Allah) đã hủy diệt biết bao thế hệ trước họ? TA (đã hủy diệt trước họ rất nhiều cộng đồng) mà TA đã cho định cự (vững chắc) trên trái đất, (sự vững chắc) mà TA không cho các ngươi định cư (giống như thế). TA đã gửi từ trên trời xuống dòng nước (mưa rào) trừng phạt họ, TA đã tạo ra những dòng sông chảy (mạnh) bên dưới họ, TA đã hủy diệt họ toàn bộ vì tội lỗi của họ và TA đã tạo ra sau họ các thế hệ khác.');
INSERT INTO "vn_ruwwad" VALUES(6,7,'6:7','Dẫu TA có ban xuống cho Ngươi (Thiên Sứ) một Kinh Sách bằng văn bản trên các trang viết mà họ có thể sờ nó bằng tay thì chắc chắn những kẻ vô đức tin vẫn sẽ nói: “Đây rõ ràng chỉ là một trò ma thuật.”');
INSERT INTO "vn_ruwwad" VALUES(6,8,'6:8','(Những kẻ vô đức tin này) nói: “Sao không phái một Thiên Thần xuống cùng với Y (Muhammad)?!” Nhưng nếu như TA (Allah) phái xuống một Thiên Thần (theo đúng yêu cầu của họ) thì vấn đề đã được giải quyết xong, (họ đã bị hủy diệt ngay khi không có đức tin) chứ làm gì có chuyện trì hoãn dành cho họ.');
INSERT INTO "vn_ruwwad" VALUES(6,9,'6:9','Và nếu TA có gửi xuống một Thiên Thần (làm Sứ Giả) thì TA vẫn sẽ phải làm cho y (xuất hiện như) một người đàn ông (phàm tục, bởi lẽ họ không thể gặp trực diện nguyên hình của Thiên Thần). Và (lúc đó) họ cũng sẽ hoài nghi giống như họ thường hoài nghi.');
INSERT INTO "vn_ruwwad" VALUES(6,10,'6:10','Quả thật, các Sứ Giả trước Ngươi (hỡi Thiên Sứ) đã từng bị giễu cợt và nhạo báng nhưng (sự trừng phạt) sẽ vây hãm những kẻ chế giễu bởi những điều mà họ đã thường bỡn cợt và trêu đùa.');
INSERT INTO "vn_ruwwad" VALUES(6,11,'6:11','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ vô đức tin thường nhạo báng): “Các người hãy đi chu du khắp trái đất rồi nhìn xem những kẻ phủ nhận đó đã có kết cuộc như thế nào?!”');
INSERT INTO "vn_ruwwad" VALUES(6,12,'6:12','Ngươi (hỡi Thiên Sứ) hãy hỏi họ: “Vạn vận trong các tầng trời và trái đất nằm dưới quyền thống trị của ai?” Ngươi hãy nói: “(Tất cả nằm dưới quyền thống trị) của Allah” Ngài đã qui định cho chính Ngài lòng nhân từ. Rồi đây vào Ngày Phán Xét, TA chắc chắn sẽ triệu tập tất cả các ngươi lại, điều đó không có gì phải hoài nghi. Và những người gây thiệt cho bản thân (vào Ngày đó) không hề có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(6,13,'6:13','Mọi vật ẩn mình vào ban đêm và ban ngày đều nằm dưới quyền chi phối của riêng Ngài và Ngài là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "vn_ruwwad" VALUES(6,14,'6:14','Ngươi (hỡi Thiên Sứ) hãy nói với (những kẻ đa thần): “Lẽ nào Ta lại chọn lấy ai khác ngoài Allah làm đấng bảo hộ trong khi Ngài là Đấng đã tạo hóa trời đất, là Đấng nuôi dưỡng (vạn vật) chứ không phải là vật được nuôi dưỡng?!" Ngươi hãy bảo: “Quả thật, Ta được lệnh phải là người đầu tiên thần phục (Allah) và không được trở thành kẻ đa thần.”');
INSERT INTO "vn_ruwwad" VALUES(6,15,'6:15','Ngươi (hỡi Thiên Sứ) hãy nói: “Quả thật, Ta rất sợ hình phạt của Ngày Vĩ Đại nếu Ta nghịch lại Thượng Đế của Ta.”');
INSERT INTO "vn_ruwwad" VALUES(6,16,'6:16','Ai được (Allah) cứu khỏi nó (hình phạt) vào Ngày hôm đó thì quả thật y đã được Ngài thương xót, và đó là một thành tựu vẻ vang.');
INSERT INTO "vn_ruwwad" VALUES(6,17,'6:17','Nếu Allah giáng tai họa lên ngươi (hỡi con người) thì chẳng ai có thể giúp ngươi thoát khỏi nó ngoại trừ một mình Ngài còn nếu Ngài ban cho Ngươi điều tốt lành thì Ngài vốn dĩ là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(6,18,'6:18','Ngài là Đấng Thống Trị bên trên đám bầy tôi của Ngài và Ngài là Đấng Sáng Suốt, Đấng Thông Toàn.');
INSERT INTO "vn_ruwwad" VALUES(6,19,'6:19','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ đa thần phủ nhận Ngươi): “Sự chứng nhận nào là lớn nhất (cho sự thật về sứ mạng của Ta)?!” Ngươi hãy nói: “Allah là Đấng chứng giám giữa Ta và các ngươi. Ngài đã mặc khải cho Ta Kinh Qur’an này để Ta cảnh báo các ngươi và những ai mà Nó tiếp xúc (trong loài người và loài Jinn). Lẽ nào các ngươi chứng nhận bên cạnh Allah có các thần linh khác nữa ư?!” Ngươi hãy nói: “Còn Ta không chứng nhận điều đó.” Ngươi hãy nói: “Quả thật, chỉ có Ngài (Allah) là Thượng Đế duy nhất và Ta vô can với những gì các ngươi tổ hợp cùng với Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(6,20,'6:20','Những ai mà TA đã ban cho Kinh Sách (Do Thái và Thiên Chúa) biết rõ về Y (Muhammad) giống như họ biết rõ ràng về con cái của họ. (Đó là) những người gây thiệt cho bản thân (vì đã đưa bản thân vào Hỏa Ngục), và họ không hề có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(6,21,'6:21','Còn ai sai quấy hơn kẻ đã đặt điều giả dối cho Allah hoặc còn ai sai quấy hơn kẻ đã phủ nhận các lời mặc khải của Ngài?! Chắc chắn những kẻ sai quấy không bao giờ thành đạt.');
INSERT INTO "vn_ruwwad" VALUES(6,22,'6:22','Vào Ngày (Phán Xét), TA sẽ triệu tập họ toàn bộ, rồi TA sẽ hỏi những kẻ đa thần: “Đâu rồi những thần linh mà các ngươi đã từng khẳng định (rằng chúng là các đối tác ngang vai cùng với Allah)?!”');
INSERT INTO "vn_ruwwad" VALUES(6,23,'6:23','Rồi họ không còn lời nào để chạy tội ngoại trừ phải nói: “Xin thề với Allah, Thượng Đế của bầy tôi, trước đây bầy tôi không phải là những người đa thần.”');
INSERT INTO "vn_ruwwad" VALUES(6,24,'6:24','Ngươi (hỡi Thiên Sứ) hãy nhìn xem bọn họ đã dối trá với chính họ như thế nào?! Và những gì mà họ bịa đặt đã bỏ mặc họ.');
INSERT INTO "vn_ruwwad" VALUES(6,25,'6:25','(Những người đa thần), trong số họ có người đã (giả vờ) tìm đến lắng nghe Ngươi (xướng đọc Qur’an) nhưng TA đã bọc kín con tim của họ lại khiến họ không hiểu được Nó và (TA làm cho) tai của họ (như thể) có một vật cản trong đó (khiến họ khó nghe được điều lợi). Và cho dù họ có tận mắt nhìn thấy tất cả các dấu hiệu thì họ cũng không có đức tin, (sự vô đức tin của họ) đến mức khi họ đến gặp Ngươi thì họ sẽ tranh luận với Ngươi, và những kẻ vô đức tin nói: “Đây chỉ là những câu chuyện cổ tích của người xưa.”');
INSERT INTO "vn_ruwwad" VALUES(6,26,'6:26','Họ lánh xa Y (Thiên Sứ) và ngăn cản (người khác tin) vào Y, và họ không thể hủy hoại được ai ngoại trừ chính bản thân họ, tuy nhiên họ không nhận thấy điều đó.');
INSERT INTO "vn_ruwwad" VALUES(6,27,'6:27','Nếu như Ngươi (Thiên Sứ) thấy được cảnh lúc họ (những người vô đức tin) bị bắt đứng trên lửa (của Hỏa Ngục), (Ngươi sẽ thấy) họ than: “Ước gì chúng mình được trở lại (cuộc sống trần gian), chúng mình sẽ không phủ nhận các lời mặc khải của Thượng Đế của chúng mình và chúng mình sẽ là những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(6,28,'6:28','Không đâu, (họ sẽ không có đức tin như lời đã nói), những gì mà họ giấu giếm trước đây sẽ lộ ra. Cho dù họ được cho trở lại (cuộc sống trần gian) thì họ sẽ vẫn tái phạm những điều cấm bởi quả thật họ là những kẻ gian dối.');
INSERT INTO "vn_ruwwad" VALUES(6,29,'6:29','(Những kẻ thờ đa thần này) nói: “Thật ra, chẳng có cuộc sống nào ngoài cuộc sống của chúng ta trên trần gian này, chúng ta sẽ không được phục sinh trở lại (cho việc phán xét và thưởng phạt).”');
INSERT INTO "vn_ruwwad" VALUES(6,30,'6:30','Nếu như Ngươi (Thiên Sứ) thấy cảnh (những người vô đức tin) bị bắt đứng trình diện trước Thượng Đế của họ (thì Ngươi sẽ thấy tình trạng tồi tệ của họ) khi (Allah) bảo họ: “Lẽ nào điều này (sự Phục Sinh) không phải là sự thật?” Họ đáp: “Thưa Thượng Đế của bầy tôi, nó là sự thật.” (Allah) phán: “Vậy thì các ngươi hãy nếm lấy hình phạt bởi những gì mà các ngươi đã từng phủ nhận.”');
INSERT INTO "vn_ruwwad" VALUES(6,31,'6:31','Những kẻ phủ nhận việc gặp Allah chắc chắn sẽ thua thiệt. Mãi đến khi Giờ Tận Thế bất ngờ xảy đến với họ thì họ mới nói: “Thật khổ thân cho chúng ta về điều mà chúng ta đã thường làm ngơ!” Họ phải mang trên lưng tội lỗi của mình. Thật tồi tệ cho tội lỗi mà họ phải gánh lấy!');
INSERT INTO "vn_ruwwad" VALUES(6,32,'6:32','Cuộc sống trần gian này chỉ là trò vui chơi và thú tiêu khiển (ngắn ngủi cho những ai không muốn làm hài lòng Allah), và cõi Đời Sau thực sự tốt đẹp (trường tồn) cho những người ngoan đạo (luôn cố gắng làm Allah hài lòng). Lẽ nào các ngươi không hiểu hỡi những kẻ đa thần)?!');
INSERT INTO "vn_ruwwad" VALUES(6,33,'6:33','TA thực sự biết rõ những gì họ nói làm Ngươi (Thiên Sứ) buồn rầu. Quả thật họ không phủ nhận Ngươi, tuy nhiên, những kẻ sai quấy thường chống đối các Lời Mặc Khải của Allah.');
INSERT INTO "vn_ruwwad" VALUES(6,34,'6:34','Quả thật, các vị Thiên Sứ trước Ngươi (hỡi Thiên Sứ Muhammad) cũng đã bị phủ nhận nhưng Họ đã kiên nhẫn chịu đựng trước sự phủ nhận và gây hại (của những kẻ phủ nhận chống đối) cho đến khi TA (Allah) mang đến cho Họ sự chiến thắng. Quả thật, lời (hứa ban cho sự chiến thắng) của Allah không hề thay đổi. Ngươi đích thực đã nhận được các nguồn tin của các vị Thiên Sứ (trước Ngươi).');
INSERT INTO "vn_ruwwad" VALUES(6,35,'6:35','Nếu sự quay lưng của (những kẻ đa thần) là điều trở ngại lớn cho Ngươi (hỡi Thiên Sứ) thì với khả năng của Ngươi, hãy đào một đường hầm dưới đất hoặc hãy bắt một cái thang lên trời để mang đến cho họ một dấu hiệu (để thuyết phục họ). Quả thật, nếu Allah muốn thì chắc chắn Ngài đã tập hợp toàn bộ họ đi trên chính đạo. Cho nên, Ngươi chớ là kẻ ngu muội.(');
INSERT INTO "vn_ruwwad" VALUES(6,36,'6:36','Quả thật chỉ những ai nghe (và hiểu được lời nói) mới đáp lại (lời kêu gọi của Ngươi – hỡi Thiên Sứ), còn (những kẻ vô đức tin là) những kẻ đã chết (bởi trái tim của họ đã chết), rồi đây (vào Ngày Phán Xét) Allah sẽ phục sinh họ và họ sẽ được đưa trở về trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(6,37,'6:37','(Những kẻ đa thần) nói: “Sao Y (Muhammad) không được ban cho một phép lạ từ Thượng Đế của mình (để làm minh chứng cho sứ mạng của Y cơ chứ?) Ngươi (Thiên Sứ) hãy nói: “Chắc chắn Allah thừa khả năng ban xuống một phép lạ (theo ý muốn của họ) nhưng đa số bọn họ lại không biết (Allah chỉ ban phép lạ xuống theo mục đích Ngài muốn chứ không theo ý của họ).');
INSERT INTO "vn_ruwwad" VALUES(6,38,'6:38','Bất cứ loài động vật nào sống trên trái đất và (bất cứ) loài chim nào bay bằng đôi cánh của chúng cũng đều (sống thành) những cộng đồng giống như các ngươi (hỡi con người). TA đã không bỏ sót bất cứ điều gì trong quyển Kinh (Mẹ - Lawhu Al-Mahfuzh). Rồi đây, tất cả đều được triệu tập trình diện Thượng Đế của họ (để Ngài xét xử và thưởng phạt).');
INSERT INTO "vn_ruwwad" VALUES(6,39,'6:39','Những ai phủ nhận các Lời Mặc Khải của TA, (hình ảnh của họ giống như) kẻ vừa điếc vừa câm mắc kẹt trong bóng tối. Allah muốn đẩy ai vào lầm lạc là tùy ý Ngài và Ngài muốn hướng dẫn ai đến con đường ngay chính cũng tùy ý Ngài.');
INSERT INTO "vn_ruwwad" VALUES(6,40,'6:40','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ đa thần này): “Nếu các ngươi nói thật (rằng các thần linh của các người có thể mang lại điều lợi và đẩy lùi điều dữ) thì các người hãy cho ta biết nếu hình phạt của Allah túm lấy các người hoặc Giờ Tận Thế (bất ngờ) xảy đến, có phải các người sẽ cầu xin ai khác ngoài Allah chứ?!”');
INSERT INTO "vn_ruwwad" VALUES(6,41,'6:41','“Không đâu, (vào lúc nguy cấp đó) chắc chắn các người chỉ cầu xin mỗi mình (Allah) mà thôi. Nếu Ngài muốn, Ngài sẽ lấy khỏi các người (những tai họa) mà các người cầu xin Ngài và các ngươi sẽ quên bẵng (các thần linh) mà các người tổ hợp (cùng với Allah).”');
INSERT INTO "vn_ruwwad" VALUES(6,42,'6:42','Quả thật TA đã gởi đến các thế hệ trước Ngươi (hỡi Thiên Sứ) các vị Sứ Giả (nhưng tất cả đã bị người dân phủ nhận) thế là TA đã trừng phạt họ bằng sự đói khổ và bệnh tật mong họ biết hạ mình (thần phục TA).');
INSERT INTO "vn_ruwwad" VALUES(6,43,'6:43','Đáng lẽ ra họ phải biết hạ mình (thần phục TA) khi tai họa của TA giáng xuống họ, tuy nhiên, con tim của họ đã chai sạn và Shaytan đã trang hoàng những điều (tội lỗi) họ làm trở nên đẹp đẽ đối với họ.');
INSERT INTO "vn_ruwwad" VALUES(6,44,'6:44','Cho nên, khi họ quên bẵng những điều họ được nhắc nhở thì TA mở toang cho họ các cánh cửa (phồn vinh) về mọi thứ (trong cuộc sống trần tục của họ) mãi đến khi họ chỉ biết vui và tự hào về những gì mà họ được ban cho thì lúc đó TA bất ngờ trừng phạt họ khiến họ trở nên tuyệt vọng.');
INSERT INTO "vn_ruwwad" VALUES(6,45,'6:45','Thế là (tất cả) đám người có hành vi sai trái đều đã bị tiêu diệt hoàn toàn. Alhamdulillah, Thượng Đế của toàn vũ trụ và vạn vật.');
INSERT INTO "vn_ruwwad" VALUES(6,46,'6:46','Ngươi (hỡi Thiên Sứ) hãy nói với (những kẻ đa thần): “Các người hãy cho ta biết nếu Allah lấy đi thính giác và thị giác của các người đồng thời đóng dấu lên con tim của các người (để các người không thể nhận thức được gì nữa) thì thần linh nào ngoài Allah có thể phục hồi những thứ đó cho các người?” Ngươi (hỡi Thiên Sứ) hãy xem TA đã trình bày rõ ràng các lời mặc khải (của TA) như thế nào, vậy mà họ vẫn ngoảnh mặt quay đi!');
INSERT INTO "vn_ruwwad" VALUES(6,47,'6:47','Ngươi (hỡi Thiên Sứ) hãy nói với (những kẻ đa thần): “Các người hãy cho ta biết nếu hình phạt của Allah đến với các người một cách bất ngờ hoặc một cách công khai (thì liệu các người có thoát được không)? Chắc chắn những kẻ làm điều sai quấy đều sẽ bị tiêu diệt toàn bộ.”');
INSERT INTO "vn_ruwwad" VALUES(6,48,'6:48','TA (Allah) đã không cử phái các vị Thiên Sứ đến ngoại trừ vì mục đích báo tin mừng (về Thiên Đàng cho những người có đức tin) và cảnh báo (về Hỏa Ngục cho những kẻ vô đức tin). Do đó, ai có đức tin và cải thiện bản thân thì họ sẽ không phải lo sợ cũng sẽ không phải buồn phiền.');
INSERT INTO "vn_ruwwad" VALUES(6,49,'6:49','Những ai phủ nhận các Lời Mặc Khải của TA thì sự trừng phạt sẽ chạm đến họ bởi những điều mà họ đã bất tuân và dấy loạn.');
INSERT INTO "vn_ruwwad" VALUES(6,50,'6:50','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ đa thần): “Ta không hề nói với các người rằng Ta nắm giữ các kho tàng của Allah, Ta không hề nói với các ngươi rằng Ta biết điều vô hình, và Ta cũng không hề nói với các người rằng Ta chính là Thiên Thần, (mà Ta chỉ nói với các ngươi rằng) Ta chỉ làm theo điều được mặc khải cho Ta mà thôi.” Ngươi hãy nói (với họ): “Lẽ nào người mù và người sáng mắt lại ngang bằng nhau ư? Lẽ nào các người không suy ngẫm?!”');
INSERT INTO "vn_ruwwad" VALUES(6,51,'6:51','Ngươi (hỡi Thiên Sứ) hãy lấy (Qur’an) cảnh báo những ai biết sợ việc họ sẽ bị triệu tập trình diện Thượng Đế của họ, (và rằng) chắc chắn họ sẽ không được ai ngoài Ngài bảo hộ và can thiệp, mong rằng họ biết kính sợ (Allah).');
INSERT INTO "vn_ruwwad" VALUES(6,52,'6:52','Ngươi (hỡi Thiên Sứ) chớ đừng xua đuổi những người (nghèo khổ) cầu nguyện Thượng Đế của họ vào sáng tối bằng chân tâm (muốn Ngài hài lòng). (Bởi lẽ) Ngươi không gánh tội thay cho họ về bất cứ điều gì (họ phạm) và họ cũng không gánh tội thay cho Ngươi về bất cứ điều gì (Ngươi phạm). Cho nên, việc Ngươi xua đuổi họ sẽ khiến Ngươi trở thành kẻ sai quấy bất công.');
INSERT INTO "vn_ruwwad" VALUES(6,53,'6:53','Như thế đó, TA đã dùng những người này thử thách những người kia để (những kẻ vô đức tin giàu có) nói (về những người nghèo có đức tin): “Lẽ nào những kẻ (nghèo khổ) này được Allah đặc ân hơn chúng ta sao?” Chẳng phải Allah mới là Đấng biết rõ ai là người tri ân (Ngài) hay sao?!');
INSERT INTO "vn_ruwwad" VALUES(6,54,'6:54','Khi những người có đức tin nơi những Lời Mặc Khải của TA đến gặp Ngươi (hỡi Thiên Sứ) thì Ngươi hãy nói với họ: “Chào bằng an đến các người, Thượng Đế của các người đã qui định cho bản thân Ngài lòng nhân từ: rằng ai trong các người lỡ làm điều tội lỗi vì thiếu hiểu biết rồi sau đó ăn năn hối cải và sửa mình thì quả thật Ngài là Đấng Hằng Tha Thứ, Đấng Khoan Dung.”');
INSERT INTO "vn_ruwwad" VALUES(6,55,'6:55','TA trình bày rõ các Lời Mặc Khải (của TA) như thế đó và để con đường của những kẻ tội lỗi trở nên rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(6,56,'6:56','Ngươi (hỡi Thiên Sứ) hãy nói: “Ta bị cấm thờ phượng những thứ mà các ngươi cầu nguyện ngoài Allah.” Ngươi hãy nói: “Ta không làm theo ham muốn của các ngươi. (Nếu Ta làm theo của các ngươi) thì chắc chắn Ta đã lầm lạc và lúc đó Ta sẽ không còn được hướng dẫn nữa.”');
INSERT INTO "vn_ruwwad" VALUES(6,57,'6:57','Ngươi (hỡi Thiên Sứ) hãy nói (với những người đa thần): “Ta quả thật đang ở trên bằng chứng rõ ràng từ Thượng Đế của Ta còn các người thì đã phủ nhận nó. Cái (hình phạt) mà các người hối thúc không nằm ở nơi Ta, mọi quyết định đều chỉ thuộc về một mình Allah, Ngài nói sự thật (và phán quyết trên sự thật) và Ngài là Đấng phân biệt (giữa điều chân lý và ngụy tạo) tốt nhất."');
INSERT INTO "vn_ruwwad" VALUES(6,58,'6:58','Ngươi (hỡi Thiên Sứ) hãy nói (với họ): “Nếu cái (hình phạt) mà các người hối thúc nằm ở nơi Ta thì vấn đề giữa Ta và các người chắc chắn đã được giải quyết. Quả thật, Allah biết rõ (khi nào trừng phạt và khi nào tạm tha cho) những kẻ làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(6,59,'6:59','Mọi chìa khóa (kho tàng) của cõi vô hình đều nằm ở nơi (Allah), không ai biết rõ chúng ngoại trừ một mình Ngài. Ngài biết rõ mọi vật trên đất liền cũng như trong biển cả, không một chiếc lá nào rơi rụng cũng như không một hạt nào trong bóng tối của lòng đất mà Ngài không biết, cái xanh tươi hay sự khô héo, tất cả đều được ghi sẵn trong một quyển sổ định mệnh (Lawhu Al-Mahfuzh) rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(6,60,'6:60','Ngài là Đấng rút hồn các ngươi vào ban đêm (lúc ngủ) và Ngài biết rõ từng hành vi của các ngươi vào ban ngày. Rồi vào ban ngày, Ngài trả hồn lại cho các ngươi (lúc thức dậy) để các ngươi tiếp tục sống đến hết tuổi thọ đã định. Sau đó, các ngươi sẽ được đưa trở về trình diện Ngài, rồi Ngài sẽ cho các ngươi biết những gì mà các ngươi đã làm.');
INSERT INTO "vn_ruwwad" VALUES(6,61,'6:61','Ngài là Đấng Thống Trị bên trên đám bầy tôi của Ngài và Ngài gởi đến các ngươi (hỡi nhân loại) các vị Thiên Thần ghi chép (mọi hành vi của các ngươi) cho đến khi ai đó trong các ngươi đối diện với cái chết (lúc y hết tuổi thọ) thì các Thiên Thần của TA sẽ bắt hồn y và Họ không bao giờ lơ là (trong nhiệm vụ).');
INSERT INTO "vn_ruwwad" VALUES(6,62,'6:62','Sau đó, (tất cả linh hồn) đều được đưa trở về trình diện Allah, Đấng Chủ Nhân đích thực của họ. Quả thật, mọi phán quyết đều thuộc về một mình Ngài và Ngài rất nhanh chóng trong việc xét xử và thưởng phạt.');
INSERT INTO "vn_ruwwad" VALUES(6,63,'6:63','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ đa thần): “Ai sẽ giải cứu các người (được bình an) khỏi các bóng tối của đất liền và biển cả khi các người hạ mình âm thầm thành khẩn khấn vái: nếu Ngài cứu bầy tôi khỏi nạn kiếp này thì chắc chắn bầy tôi sẽ là những người biết ơn?”');
INSERT INTO "vn_ruwwad" VALUES(6,64,'6:64','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Allah giải cứu các người (bình an) khỏi nạn kiếp đó và tất cả mọi tai họa khác nhưng rồi sau đó các người vẫn Shirk (tổ hợp các thần linh) với Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(6,65,'6:65','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “(Allah) thừa khả năng gửi hình phạt đến các người từ bên trên các người hoặc từ dưới chân các người hoặc làm cho các người xung đột bè phái và để các người nếm mùi của sự sát phạt lẫn nhau.” Ngươi nhìn xem, TA đã giảng giải các lời mặc khải của TA bằng nhiều cách với hy vọng họ hiểu được (thông điệp).');
INSERT INTO "vn_ruwwad" VALUES(6,66,'6:66','Nhưng người dân của Ngươi (hỡi Thiên Sứ) đã phủ nhận nó trong khi nó là sự thật. Ngươi hãy nói: “Ta không được cử đến để trông chừng các người (mà Ta chỉ là người cảnh báo các người về sự trừng phạt khủng khiếp mà thôi).”');
INSERT INTO "vn_ruwwad" VALUES(6,67,'6:67','Tất cả mọi nguồn tin (cảnh báo) đều đã được ấn định thời khắc và các người sẽ sớm biết thôi.');
INSERT INTO "vn_ruwwad" VALUES(6,68,'6:68','Khi Ngươi (hỡi Thiên Sứ) thấy những kẻ (vô đức tin) nhạo báng các Lời Mặc Khải của TA thì Ngươi hãy tránh xa họ cho đến khi nào họ chuyển sang chủ đề khác. Còn nếu Ngươi bị Shaytan làm cho quên mà ngồi cùng với họ thì sau khi nhớ lại, Ngươi chớ đừng tiếp tục ngồi cùng với đám người sai quấy đó nữa.');
INSERT INTO "vn_ruwwad" VALUES(6,69,'6:69','Những người ngoan đạo không hề có bất cứ trách nhiệm nào cho hành động và việc làm của họ, tuy nhiên, (những người ngoan đạo) hãy nhắc nhở họ mong rằng họ sẽ ngoan đạo (kính sợ Allah.)');
INSERT INTO "vn_ruwwad" VALUES(6,70,'6:70','Ngươi (hỡi Thiên Sứ) hãy mặc kệ những kẻ đã mang tôn giáo của họ ra làm trò đùa và thú tiêu khiển và bị mê hoặc bởi cuộc sống (hào nhoáng) của trần gian. Ngươi hãy nhắc nhở họ bằng Qur’an vì bản thân họ có thể bị hủy hoại bởi những tội lỗi họ đã làm, (và họ cần biết rằng) sẽ không có ai ngoài Allah đứng ra bảo hộ cũng như can thiệp cho họ. Cho dù họ có dâng mọi thứ tương đương với (tội đã phạm) để xin được chuộc tội thì cũng sẽ không được chấp nhận. Họ là những kẻ sẽ phải bị hủy hoại bởi những tội lỗi đã làm. (Rồi đây), họ sẽ được cho uống một loại nước sôi cực nóng cùng với hình phạt đau đớn bởi vì họ đã vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(6,71,'6:71','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ đa thần): “Lẽ nào chúng tôi lại cầu xin ngoài Allah những thứ hoàn toàn không mang lợi cũng chẳng gây hại cho chúng tôi để rồi chúng tôi bị đẩy trở về với (sự vô đức tin) sau khi Allah đã hướng dẫn chúng tôi ư? (Lẽ nào chúng tội lại) giống như một kẻ bị các Shaytan cám dỗ dẫn đi lang thang trên trái đất trong lúc những người bạn của y cố réo gọi y đến với sự chỉ đạo: Anh hãy đến đây với chúng tôi (nhưng y cứ ngoảnh đi)?” Ngươi hãy nói: “Thật ra sự hướng dẫn của Allah mới thực sự là nguồn chỉ đạo đúng đắn và chúng tôi được lệnh phải thần phục Thượng Đế của vũ trụ và vạn vật.”');
INSERT INTO "vn_ruwwad" VALUES(6,72,'6:72','“(Và chúng tôi được lệnh): Các ngươi hãy thiết lập lễ nguyện Salah và kính sợ Ngài bởi Ngài là Đấng mà các ngươi phải trở về (trình diện vào Ngày Phán Xét.)”');
INSERT INTO "vn_ruwwad" VALUES(6,73,'6:73','Ngài là Đấng đã tạo hóa các tầng trời và trái đất vì chân lý. Vào Ngày mà Ngài phán (khi muốn điều gì đó) “Hãy thành” thì nó lập tức sẽ thành, (đó là Ngày Tận Thế, Allah phán bảo các ngươi hãy đứng dậy thì tất cả nhân loại đều đứng dậy). Lời phán của Ngài là sự thật. Ngài nắm toàn quyền trong Ngày mà tiếng còi được thổi lên. Ngài biết điều vô hình và hữu hình và Ngài là Đấng Chí Minh, Đấng Thông Toàn.');
INSERT INTO "vn_ruwwad" VALUES(6,74,'6:74','(Ngươi - hỡi Thiên Sứ - hãy nhớ lại) khi Ibrahim thưa với cha của Y – Azar, nói: “Sao cha lại nhận các bục tượng làm thần linh? Quả thật, con thấy cha và người dân của cha rõ ràng đang trong sự lầm lạc.”');
INSERT INTO "vn_ruwwad" VALUES(6,75,'6:75','Tương tự (như việc cho Ibrahim thấy sự lầm lạc của cha Y và người dân), TA cho Ibrahim thấy thêm việc chi phối các tầng trời và trái đất (để Y nhận thức được quyền năng vô biên của Allah) và để cho Y trở thành một người có đức tin kiên định.');
INSERT INTO "vn_ruwwad" VALUES(6,76,'6:76','Khi đêm xuống, (Ibrahim) thấy một vì sao, Y bảo: “Đây là Thượng Đế của Ta.” Nhưng khi vì sao lặn khuất, Y bảo: “Ta không thích những thứ lặn khuất.”');
INSERT INTO "vn_ruwwad" VALUES(6,77,'6:77','Khi thấy mặt trăng mọc lên, (Ibrahim) bảo: “Đây là Thượng Đế của Ta.” Nhưng khi mặt trăng lặn khuất, Y bảo: “Nếu Thượng Đế của Ta không hướng dẫn Ta thì Ta chắc chắn đã trở thành kẻ lầm lạc.”');
INSERT INTO "vn_ruwwad" VALUES(6,78,'6:78','Khi thấy mặt trời mọc lên, (Ibrahim) bảo: “Đây mới thật là Thượng Đế của Ta, vị này lớn nhất.” Nhưng khi mặt trời lặn khuất, Y nói: “Này hỡi người dân, Ta hoàn toàn vô can với mọi thứ mà các người tổ hợp (với Allah).”');
INSERT INTO "vn_ruwwad" VALUES(6,79,'6:79','“Ta toàn tâm hướng mặt mình về Đấng đã tạo hóa các tầng trời và trái đất và Ta không là kẻ thờ đa thần.”');
INSERT INTO "vn_ruwwad" VALUES(6,80,'6:80','Người dân của Y tranh cãi với Y. Y bảo: “Lẽ nào các người tranh cãi với Ta về Allah trong khi Ngài đã hướng dẫn Ta? Ta không sợ những thứ mà các người tổ hợp với Ngài (bởi lẽ chắc chắn chúng sẽ không thể hại được Ta) ngoại trừ điều nào mà Thượng Đế của Ta đã muốn. Kiến thức của Thượng Đế Ta bao trùm tất cả mọi thứ. Lẽ nào các người vẫn chưa giác ngộ ư?!”');
INSERT INTO "vn_ruwwad" VALUES(6,81,'6:81','“Tại sao Ta lại phải sợ những thứ mà các người tổ hợp (với Allah) trong khi các người không sợ việc các người đã tổ hợp với Allah những thứ mà Ngài đã không cho các người bất cứ thẩm quyền nào? Cho nên, trong hai nhóm, nhóm nào xứng đáng được an toàn (khỏi sự trừng phạt của Allah) nếu các người (thật sự) biết rõ?”');
INSERT INTO "vn_ruwwad" VALUES(6,82,'6:82','Những người có đức tin và không làm bẩn (đức tin thuần túy của mình) bởi điều sai trái (Shirk) thì họ là những người được bình an và được hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(6,83,'6:83','(Cho nên, trong hai nhóm, nhóm nào xứng đáng được an toàn (khỏi sự trừng phạt của Allah) nếu các người (thật sự) biết rõ?) Đó là lập luận mà TA đã ban cho Ibrahim để Y đối đáp với người dân của Y. TA muốn nâng cấp bậc cho ai là tùy ý TA. Quả thật, Thượng Đế của Ngươi (Thiên Sứ) là Đấng Sáng Suốt, Đấng Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(6,84,'6:84','TA đã ban cho (Ibrahim) (đứa con trai) Is-haq (Isaac) và (đứa cháu nội) Ya’qub (Jacob). Cả hai đều được TA hướng dẫn (đến với chính đạo). Trước đây, Nuh (Nô-ê) cũng đã được TA hướng dẫn, và cả trong dòng dõi của Y như Dawood (David), Sulayman (Solomon), Ayyub (Job), Yusuf (Joseph), Musa, Harun (Aaron); TA ban thưởng cho những người làm tốt như thế.');
INSERT INTO "vn_ruwwad" VALUES(6,85,'6:85','(TA đã hướng dẫn) Zakariya (Zacharias), Yahya, Ysa, Ilyas (Elias), tất cả đều là những người đức hạnh.');
INSERT INTO "vn_ruwwad" VALUES(6,86,'6:86','(TA đã hướng dẫn) Isma’il, Al-Yasa’ (Elisha), Yunus, Lut, tất cả đều được TA ban phúc hơn thiên hạ.');
INSERT INTO "vn_ruwwad" VALUES(6,87,'6:87','Và trong số cha mẹ, con cái và anh em của họ cũng được TA phù hộ và hướng dẫn theo Chính Đạo.');
INSERT INTO "vn_ruwwad" VALUES(6,88,'6:88','Đó là chỉ đạo của Allah, Ngài đã dùng nó để hướng dẫn ai Ngài muốn trong số bầy tôi của Ngài. Nhưng nếu họ Shirk thì mọi việc làm (ngoan đạo và hành thiện) của họ trở nên hoài công vô ích.');
INSERT INTO "vn_ruwwad" VALUES(6,89,'6:89','Đó là những (vị Sứ Giả và Nabi) đã được TA ban cho Kinh Sách, sự thông thái và sứ mạng tiên tri. Cho nên, nếu như đám dân này (của Ngươi – hỡi Thiên Sứ) phủ nhận (cả ba điều này) thì chắc chắn TA sẽ ban các điều này cho một đám người khác (những người Muhajir và dân Ansar và những đi theo họ đến ngày Tận Thế), họ sẽ không phủ nhận.');
INSERT INTO "vn_ruwwad" VALUES(6,90,'6:90','Đó là những người đã được Allah ban cho sự chỉ đạo, cho nên, các ngươi hãy noi theo sự chỉ đạo của họ. Ngươi (hỡi Thiên Sứ) hãy nói: “Ta không hề yêu cầu các người trả tiền thù lao cho Ta về (Qur’an). Quả thật, Nó là Thông Điệp Nhắc Nhở dành cho thiên hạ (loài người và loài Jinn).”');
INSERT INTO "vn_ruwwad" VALUES(6,91,'6:91','(Những kẻ đa thần) đã đánh giá Allah không đúng về Ngài khi bọn họ bảo: “Allah đã không ban xuống bất cứ gì cho người phàm cả.” Ngươi (hỡi Thiên Sứ) hãy hỏi họ: “Vậy ai đã ban xuống Kinh (Tawrah), Kinh Sách mà Musa đã mang đến như một nguồn ánh sáng và sự chỉ đạo cho nhân loại? (Tuy nhiên), các người (những người Do Thái) chỉ phơi bày ra một số (điều phù hợp với ý muốn của các người) trên các miêng da thuộc và giấu đi nhiều điều (nghịch lại với dục vọng của các người). Các người đã được dạy cho những điều mà cả các người và cha mẹ của các người đã không từng biết.” Ngươi hãy nói: “Allah (đã ban xuống).” Rồi Ngươi hãy mặc kệ bọn họ vui đùa trong sự lầm lạc đó.');
INSERT INTO "vn_ruwwad" VALUES(6,92,'6:92','Kinh (Qur’an) này, TA đã ban Nó xuống cho Ngươi (hỡi Thiên Sứ) như là một hồng phúc, Nó xác nhận lại những điều (đã ban xuống) trước Nó, và để cho Ngươi dùng cảnh báo (người dân) của thành phố mẹ (Makkah) và những ai xung quanh nó. Những ai có đức tin nơi Đời Sau sẽ có đức tin nơi Nó và họ luôn chu đáo giữ gìn lễ nguyện Salah của họ.');
INSERT INTO "vn_ruwwad" VALUES(6,93,'6:93','Còn ai sai trái hơn kẻ đã đặt điều dối trá cho Allah hoặc kẻ đã nói “Tôi đã nhận được sự mặc khải” nhưng thực chất y chẳng được mặc khải bất cứ điều gì! Và (còn ai sai quấy hơn) kẻ đã nói: “Ta sẽ ban xuống điều giống như điều Allah đã ban xuống”! Và phải chi Ngươi (hỡi Thiên Sứ) có thể nhìn thấy cảnh tượng của những kẻ làm điều sai quấy giẫy giụa đau đớn vào lúc sắp chết khi các Thiên Thần đưa tay rút hồn của họ, bảo: “Hãy ra khỏi thể xác của các ngươi, ngày hôm nay các ngươi phải chịu sự trừng phạt nhục nhã bởi những điều mà các ngươi đã nói không đúng về Allah cũng như bởi việc các ngươi ngạo mạn trước các lời mặc khải của Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(6,94,'6:94','Quả thật, các ngươi phải một mình trở về trình diện TA giống như TA đã tạo hóa các ngươi lần đầu. Các ngươi phải bỏ lại sau lưng mọi thứ mà TA đã ban cấp cho các ngươi (trên trần gian), và TA không thấy bên cạnh các ngươi những kẻ can thiệp mà các ngươi đã từng khẳng định rằng chúng là đối tác (của Allah). Quả thật, mối quan hệ giữa các ngươi bị cắt đứt hoàn toàn và (những thần linh) mà các ngươi đã từng khẳng định sẽ bỏ các ngươi mất dạng.');
INSERT INTO "vn_ruwwad" VALUES(6,95,'6:95','Quả thật, Allah là Đấng đã chẻ hạt giống và hạt chà là (để chúng nẩy mầm). Ngài rút cái sống ra từ cái chết và rút cái chết ra từ cái sống. Đó là Allah (Thượng Đế của các ngươi). Thế các ngươi ngoảnh đi đâu (hỡi những kẻ đa thần)?');
INSERT INTO "vn_ruwwad" VALUES(6,96,'6:96','Ngài chẻ (ánh sáng) của bình minh (từ bóng tối của ban đêm), Ngài tạo ra ban đêm để nghỉ ngơi, mặt trời và mặt trăng bơi theo quỹ đạo riêng (của chúng). Đó là sự sắp đặt và an bày của Đấng Toàn Năng, Đấng Toàn Tri.');
INSERT INTO "vn_ruwwad" VALUES(6,97,'6:97','Ngài là Đấng đã tạo ra cho các ngươi các vì sao, dựa vào chúng các ngươi có thể xác định được phương hướng trong các bóng tối của đất liền và biển cả. Quả thật, TA đã giải thích các dấu hiệu (của TA) cho đám người hiểu biết.');
INSERT INTO "vn_ruwwad" VALUES(6,98,'6:98','Ngài là Đấng đã tạo hóa các ngươi từ một cơ thể duy nhất (Adam). (Ngài đã tạo cho các ngươi) một chỗ định cư (trong tử cung của người mẹ) và một nơi lưu trữ (xương sống của người cha). Quả thật, TA đã giải thích các dấu hiệu (của TA) cho đám người thông hiểu.');
INSERT INTO "vn_ruwwad" VALUES(6,99,'6:99','Ngài là Đấng đã ban nước (mưa) từ trên trời xuống. (Với nước mưa đó), TA (Allah) cho mọc ra đủ loại cây cối và thảo mộc; cho mọc ra những cộng xanh trổ hạt kết chồng lên nhau (như bông lúa); (cho mọc ra cây chà là), từ thân và nhánh của nó trổ ra trái từng chùm cao thấp; và cho mọc ra những vườn nho, vườn ô liu, vườn lựu, (ô liu và lựu tuy lá) giống nhau nhưng (trái) lại khác nhau. Ngươi (hỡi Thiên Sứ) hãy nhìn ngắm trái quả của chúng lúc mới trổ và lúc chín mộng, quả thật trong sự việc đó là các dấu hiệu dành cho đám người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(6,100,'6:100','Tuy nhiên, (những kẻ đa thần) đã tôn loài Jinn làm các thần linh cùng với Allah trong khi Ngài tạo ra chúng. Họ đã gán cho Ngài những đứa con trai và những đứa con gái một cách không hiểu biết. Vinh quang thay Ngài, Ngài tối cao vượt xa những gì họ đã gán cho Ngài.');
INSERT INTO "vn_ruwwad" VALUES(6,101,'6:101','Ngài đã tạo các tầng trời và trái đất. Làm sao Ngài có thể có con trong khi Ngài không có bạn đời, và (làm sao lại qui cho Ngài có con) trong khi Ngài là Đấng đã tạo ra tất cả vạn vật và Ngài là Đấng Hằng Biết mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(6,102,'6:102','Đó là Allah, Thượng Đế của các ngươi (hỡi nhân loại). Không có Thượng Ðế đích thực nào khác ngoài Ngài. Ngài là Ðấng tạo hóa tất cả vạn vật nên các ngươi hãy thờ phượng riêng Ngài. Quả thật, Ngài là Đấng bảo hộ và trông coi tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(6,103,'6:103','Không một ánh nhìn nào bắt kịp được Ngài trong lúc Ngài bắt kịp mọi ánh nhìn, bởi Ngài là Đấng Tinh Tế, Đấng Thông Toàn.');
INSERT INTO "vn_ruwwad" VALUES(6,104,'6:104','(Hỡi nhân loại,) quả thật, các bằng chứng từ nơi Thượng Đế của các ngươi đã đến với các ngươi. Do đó, người nào nhìn thấy được thì tốt cho bản thân y còn người nào mù lòa (không nhìn thấy) thì bất lợi cho y; và Ta (Muhammad) không phải là người giám hộ và trông coi các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(6,105,'6:105','Như thế đó, TA giải thích rõ ràng các Lời Mặc Khải (của TA) để (những kẻ đa thần khỏi) nói rằng Ngươi (Thiên Sứ) đã học (được điều đó từ dân Kinh Sách) và để TA làm sáng tỏ vấn đề cho đám người hiểu biết.');
INSERT INTO "vn_ruwwad" VALUES(6,106,'6:106','Ngươi (hỡi Thiên Sứ) hãy đi theo những gì được mặc khải cho Ngươi từ Thượng Đế của Ngươi, Đấng mà không có Thượng Đế đích thực nào khác ngoài Ngài, và Ngươi hãy tránh xa những kẻ đa thần.');
INSERT INTO "vn_ruwwad" VALUES(6,107,'6:107','Nếu Allah muốn, không ai trong số họ phạm phải Shirk; và TA đã không phái Ngươi (Thiên Sứ) đến để giám hộ và trông chừng họ. Quả thật, Ngươi không phải là người trông chừng (các hành động và việc làm) của họ.');
INSERT INTO "vn_ruwwad" VALUES(6,108,'6:108','Các ngươi (những người có đức tin) chớ đừng xúc phạm (thần linh) mà họ cầu nguyện ngoài Allah, bởi lẽ (như thế) thì họ (có cớ) xúc phạm Allah một cách thiếu hiểu biết. Như thế đó, mỗi cộng đồng, TA tô điểm cho hành vi của họ (để họ tưởng đó là tốt), rồi sau đó họ sẽ trở về trình diện Thượng Đế của họ, và Ngài sẽ cho họ biết những gì họ đã từng làm.');
INSERT INTO "vn_ruwwad" VALUES(6,109,'6:109','(Những kẻ đa thần) đã thề thốt bằng sự thề thốt trịnh trọng rằng nếu như (Muhammad) mang một dấu lạ đến cho họ thì chắc chắn họ sẽ có đức tin. Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Quả thật, các dấu lạ đều ở nơi Allah.” (Hỡi những người có đức tin), các ngươi có biết không ngay cả khi các dấu lạ xuất hiện, họ cũng sẽ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(6,110,'6:110','TA lật trở con tim và cái nhìn của họ (lệch khỏi chân lý) giống như việc họ đã không tin (nơi Qur’an) lần đầu; và TA bỏ mặc họ lang thang trong sự thái quá của họ.');
INSERT INTO "vn_ruwwad" VALUES(6,111,'6:111','Dẫu TA có phái các vị Thiên Thần xuống gặp họ, cho người chết nói chuyện được với họ và tập trung tất cả mọi thứ (mà họ yêu cầu) trước mặt họ thì họ vẫn sẽ không tin tưởng trừ phi Allah muốn (hướng dẫn họ), bởi đa số bọn họ là những kẻ ngu muội.');
INSERT INTO "vn_ruwwad" VALUES(6,112,'6:112','Như thế đó, TA (Allah) đã tạo cho mỗi vị Nabi một kẻ thù Shaytan từ loài người và loài Jinn. Bọn chúng truyền cảm hứng cho nhau bằng những lời lẽ hoa mỹ hầu lừa gạt (thiên hạ). Nhưng nếu như Thượng Đế của Ngươi (hỡi Thiên Sứ) muốn (khác đi) thì bọn chúng không thể làm được điều đó. Vì vậy, Ngươi cứ mặc kệ bọn chúng với những điều mà chúng bịa đặt.');
INSERT INTO "vn_ruwwad" VALUES(6,113,'6:113','Cứ để (lời lẽ hoa mỹ đó) mê hoặc trái tim của những kẻ vô đức tin nơi Đời Sau, cứ để họ hả hê với nó và cứ để họ tha hồ tích lũy những điều (tội lỗi) mà họ ham muốn kiếm chác.');
INSERT INTO "vn_ruwwad" VALUES(6,114,'6:114','(Hỡi Thiên Sứ, Ngươi hãy nói với những kẻ đa thần:) “Lẽ nào Ta lại phải tìm một giáo luật nào đó ngoài (giáo luật của) Allah để phân xử (giữa Ta và các ngươi) trong lúc Ngài là Đấng đã ban xuống cho các ngươi Kinh Sách (Qur’an) chứa đựng những lời giải thích cặn kẽ?” Những kẻ đã được TA ban cho Kinh Sách (Do Thái và Thiên Chúa) đều biết rõ rằng Nó (Qur’an) đích thực là (Kinh Sách) được ban xuống từ Thượng Đế của Ngươi, vì vậy, Ngươi chớ đừng là kẻ hoài nghi (về điều mà TA đã mặc khải cho Ngươi).');
INSERT INTO "vn_ruwwad" VALUES(6,115,'6:115','Lời phán của Thượng Đế của Ngươi (hỡi Thiên Sứ) đã hoàn tất trọn vẹn về chân lý và công lý. Không ai có thể thay đổi lời nói của Ngài. Và Ngài là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(6,116,'6:116','Nếu Ngươi (hỡi Thiên Sứ) hùa theo số đông người trên trái đất thì chắc chắn bọn họ sẽ đưa Ngươi lệch khỏi con đường (ngay chính) của Allah, bởi lẽ họ chỉ làm theo sự phỏng đoán và họ chỉ đoán chừng mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(6,117,'6:117','Quả thật, Thượng Đế của Ngươi (hỡi Thiên Sứ) biết rõ ai là kẻ lạc khỏi con đường của Ngài và Ngài biết rõ ai là người được hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(6,118,'6:118','Bởi thế, các ngươi (hỡi nhân loại) hãy ăn thịt (những con vật) được nhân danh Allah (lúc cắt tiết) nếu như các ngươi thực sự có đức tin nơi các Lời Mặc Khải (của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(6,119,'6:119','Sao các ngươi (hỡi những người có đức tin) không ăn thịt (những con vật) được nhân danh Allah (lúc cắt tiết)? Quả thật, Ngài đã trình bày cụ thể cho các ngươi những thực phẩm mà Ngài đã cấm các ngươi dùng ngoại trừ vì nhu cầu sống còn. Quả thật, có rất nhiều người dẫn (người khác) đi lạc do dục vọng của họ chứ không do kiến thức hiểu biết. Quả thật, Thượng Đế của Ngươi (hỡi Thiên Sứ) luôn biết rõ những kẻ vượt quá giới hạn.');
INSERT INTO "vn_ruwwad" VALUES(6,120,'6:120','Các ngươi (hỡi nhân loại) cứ tha hồ làm điều tội lỗi một cách công khai hay kín đáo. (Rồi đây), chắc chắn những kẻ làm điều tội lỗi sẽ được đáp trả xứng đáng cho những tội lỗi mà họ đã phạm.');
INSERT INTO "vn_ruwwad" VALUES(6,121,'6:121','Các ngươi (hỡi những người Muslim) chớ ăn thịt (những con vật) không được nhân danh Allah (lúc cắt tiết), bởi đó là (hành động) bất tuân. Quả thật, những tên Shaytan thường truyền cảm hứng cho các đồng minh của chúng để tranh luận với các ngươi. Nếu các ngươi nghe theo chúng thì chắc chắn các ngươi sẽ trở thành những kẻ đa thần.');
INSERT INTO "vn_ruwwad" VALUES(6,122,'6:122','Lẽ nào một người đã chết (do chưa được hướng dẫn về tâm linh) rồi được TA cứu sống lại và ban cho ánh sáng nhờ đó y có thể bước đi thanh thản giữa thiên hạ lại ngang bằng với một kẻ mắc kẹt trong bóng tối không thể nào thoát ra được?! Như thế đó, những việc làm tội lỗi của những kẻ vô đức tin luôn thu hút họ.');
INSERT INTO "vn_ruwwad" VALUES(6,123,'6:123','Cũng như thế, TA (Allah) đã đặt trong mỗi thị trấn những tên tội phạm lớn nhất của nó để chúng hoạch định âm mưu trong đó. Tuy nhiên, chúng chỉ mưu hại chính bản thân chúng trong lúc chúng không nhận thấy (điều đó).');
INSERT INTO "vn_ruwwad" VALUES(6,124,'6:124','Cứ mỗi khi có một dấu hiệu nào đó được mang đến trình bày cho chúng thì chúng nói: “Bọn ta không bao giờ tin cho đến khi nào bọn ta được ban cho giống như các vị Thiên Sứ của Allah.” Allah biết rõ nhất đâu là nơi phù hợp để Ngài đặt Bức Thông Điệp của Ngài. (Rồi đây) những kẻ tội lỗi sẽ bị hạ nhục ở nơi Allah và sẽ phải chịu sự trừng phạt khắc nghiệt bởi những gì mà họ đã âm mưu.');
INSERT INTO "vn_ruwwad" VALUES(6,125,'6:125','Do đó, người nào mà Allah muốn hướng dẫn y thì Ngài sẽ mở lòng ngực của y để y tiếp nhận Islam còn người nào mà Ngài muốn làm cho y lầm lạc thì Ngài sẽ làm cho lòng ngực của y trở nên chặt cứng và khó khăn như thể y đang đi lên trời. Allah đặt sự ô uế lên những kẻ vô đức tin như thế đấy.');
INSERT INTO "vn_ruwwad" VALUES(6,126,'6:126','Đây là con đường thẳng tắp (chính đạo) của Thượng Đế của Ngươi (hỡi Thiên Sứ). Quả thật, TA đã giải trình rõ ràng các Lời Mặc Khải (của TA) cho đám người biết lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(6,127,'6:127','Họ sẽ có được ngôi nhà bằng an (Thiên Đàng) nơi Thượng Đế của họ. Ngài là Ðấng Bảo Hộ của họ do những điều (tốt đẹp) mà họ đã làm (trên thế gian).');
INSERT INTO "vn_ruwwad" VALUES(6,128,'6:128','(Hỡi Thiên Sứ, Ngươi hãy nghĩ đến) Ngày (mà Allah) triệu tập tất cả (hai loài: con người và Jinn) và phán: “Hỡi loài Jinn, quả thật các ngươi đã dẫn nhiều (linh hồn) trong loài người (đi lạc).” Những tên đầu sỏ trong loài người nói: “Lạy Thượng Đế của bầy tôi, tất cả bầy tôi đã tận hưởng lẫn nhau ( nhưng rồi bầy tôi đã mãn kỳ hạn mà Ngài đã ấn định cho bầy tôi.” (Allah) phán: “Vậy thì Ngục Lửa là nơi cư ngụ muôn đời cho tất cả các ngươi trừ phi Allah muốn khác hơn.” Quả thật, Thượng Đế của Ngươi (hỡi Thiên Sứ) là Đấng Chí Minh, Đấng Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(6,129,'6:129','Như thế đó, TA khiến những kẻ làm điều sai quấy kết thân với nhau bởi những điều (tội lỗi) mà chúng đã làm.');
INSERT INTO "vn_ruwwad" VALUES(6,130,'6:130','Hỡi loài Jinn và loài người, lẽ nào đã không có các vị Thiên Sứ xuất thân từ trong các ngươi đến xướng đọc cho các ngươi nghe các Lời Mặc Khải của TA và cảnh báo các ngươi về sự trình diện của Ngày (Phán Xử) hôm nay? Họ nói: “Bầy tôi xin làm chứng cho bản thân mình (rằng đích thực có các vị Thiên Sứ đã đến).” Tuy nhiên, đời sống trần gian đã đánh lừa chúng (nên giờ đây) chúng tự xác nhận chính chúng từng là những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(6,131,'6:131','(Việc gởi Thiên Sứ đến) như thế là để Thượng Đế của Ngươi (hỡi Thiên Sứ) không tiêu diệt các thị trấn một cách bất công trong lúc cư dân của chúng không biết gì.');
INSERT INTO "vn_ruwwad" VALUES(6,132,'6:132','Tất cả sẽ (chịu sự trừng phạt) với các mức độ tương ứng theo (tội lỗi) mà họ đã làm. Và Thượng Đế của Ngươi (hỡi Thiên Sứ) không phải không biết những điều họ làm.');
INSERT INTO "vn_ruwwad" VALUES(6,133,'6:133','Thượng Đế của Ngươi (hỡi Thiên Sứ) là Đấng Giàu Có có lòng nhân từ. Nếu muốn, Ngài sẽ tiêu diệt các ngươi (những kẻ nghịch lại Ngài) và sẽ mang đến một thế hệ khác sau các ngươi giống như việc Ngài đã tạo ra các ngươi từ một đám dân khác.');
INSERT INTO "vn_ruwwad" VALUES(6,134,'6:134','(Hỡi những kẻ vô đức tin,) những điều mà các ngươi được hứa (từ sự phục sinh, sự phán xét và sự thưởng phạt) chắc chắn sẽ xảy ra, và các ngươi chắc chắn sẽ không bao giờ trốn thoát.');
INSERT INTO "vn_ruwwad" VALUES(6,135,'6:135','Ngươi (hỡi Thiên Sứ) hãy nói: “Hỡi dân Ta, các ngươi cứ ở trên tình trạng (vô đức tin và lầm lạc) của các ngươi còn Ta sẽ vẫn làm (theo sứ mạng của mình). Rồi đây các ngươi sẽ biết ai sẽ có được kết quả tốt đẹp (ở Đời Sau). Quả thật, những kẻ làm điều sai quấy sẽ không bao giờ thành đạt.”');
INSERT INTO "vn_ruwwad" VALUES(6,136,'6:136','(Những kẻ đa thần) đã tự bịa ra việc phải cúng tế cho Allah một phần nông sản và thú nuôi mà (Allah) đã tạo ra cho chúng, chúng nói theo ý của chúng: “Đây là của Allah còn đây là của những thần linh của chúng tôi.” Những gì dành cho các thần linh của chúng là để khỏi đến được với Allah (qua việc chia cho người nghèo, người khó khăn), còn phần của Allah thì sẽ đến được với các thần linh của chúng. Thật xấu xa và tội lỗi cho cách mà chúng đã hành xử.');
INSERT INTO "vn_ruwwad" VALUES(6,137,'6:137','Tương tự, những thần linh (Shaytan) đã làm nhiều kẻ đa thần thích thú trong việc sát hại con cái của chúng mục đích là để đẩy chúng đến sự hủy diệt và làm xáo trộn tôn giáo của chúng. Tuy nhiên, nếu Allah muốn khác đi thì chúng đã không làm điều đó. Nhưng thôi, Ngươi (hỡi Thiên Sứ) hãy cứ mặc chúng với điều mà chúng bịa đặt.');
INSERT INTO "vn_ruwwad" VALUES(6,138,'6:138','(Những kẻ đa thần) nói theo ý của chúng: “Đây là thú nuôi và nông sản bị cấm sử dụng ngoại trừ những ai mà bọn ta cho phép. Còn đây là những thú vật( bị cấm dùng để chuyên chở, và đây là thú nuôi không được nhân danh Allah lúc cắt tiết.” (Đó toàn là) sự bịa đặt cho (Allah). Rồi đây, Ngài sẽ đáp trả lại chúng (sự trừng phạt xứng đáng) bởi những gì chúng đã bịa đặt.');
INSERT INTO "vn_ruwwad" VALUES(6,139,'6:139','Chúng bảo: “Những gì trong bụng của những con thú nuôi này (nếu sinh ra còn sống) là dành riêng cho nam giới chúng ta và là điều cấm đối với các bà vợ của chúng ta, còn nếu sinh ra đã chết thì tất cả cùng hưởng chung.” Rồi đây, (Allah) sẽ đáp trả chúng (sự trừng phạt xứng đáng) cho sự bịa đặt của chúng. Quả thật, Allah là Đấng Anh Minh, Đấng Toàn Tri.');
INSERT INTO "vn_ruwwad" VALUES(6,140,'6:140','Những ai đã giết con cái của mình trong sự dại dột và điên rồ cũng như tự cấm mình dùng những thứ mà Allah đã ban cấp cho họ - một sự bịa đặt cho Allah - đích thực là những kẻ thua thiệt. Họ thực sự đã đi lạc đường và không được hướng dẫn.');
INSERT INTO "vn_ruwwad" VALUES(6,141,'6:141','Ngài (Allah) là Đấng đã tạo ra các loại vườn tược, có vườn là những loại cây thân leo trên giàn và có vườn là những loại cây không cần giàn; (Ngài là Đấng đã tạo ra) cây chà là, các loại cây trồng cho trái quả khác nhau; (Ngài là Đấng đã tạo ra) cây ô liu, cây lựu (có lá) giống nhau nhưng trái lại khác nhau. Các ngươi (hỡi con người) hãy ăn trái quả của chúng khi đã chín và các ngươi hãy xuất phần thuế (Zakah) vào ngày các ngươi thu hoạch; và các ngươi chớ phung phí bởi quả thật (Allah) không yêu thương những kẻ phung phí.');
INSERT INTO "vn_ruwwad" VALUES(6,142,'6:142','Và trong các loại thú nuôi, có loại dùng để chuyên chở và có loại dùng để ăn thịt. Các ngươi (hỡi con người) hãy ăn những thứ mà Allah đã ban cấp cho các ngươi nhưng chớ đi theo bước chân của Shaytan, bởi quả thật (Shaytan) chính là kẻ thù công khai của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(6,143,'6:143','(Allah đã tạo ra cho các ngươi) tám loại thú nuôi gồm đực cái, cừu hai con đực cái và dê hai con đực cái. Ngươi (hỡi Thiên Sứ) hãy hỏi (những kẻ đa thần): “(Allah) cấm ăn hai con đực hay hai con cái hoặc Ngài cấm luôn cả (con đực và cái) nằm trong bụng hai con cái? Các ngươi hãy nói cho Ta biết bằng kiến đúng thật nếu các ngươi là người chân thật.”');
INSERT INTO "vn_ruwwad" VALUES(6,144,'6:144','Và lạc đà hai con đực cái và bò hai con đực cái. Ngươi (hỡi Thiên Sứ) hãy hỏi (những kẻ đa thần): “(Allah) cấm ăn hai con đực hay hai con cái hoặc Ngài cấm luôn cả (con đực và cái) nằm trong bụng hai con cái? Hoặc lẽ nào chính các ngươi là những kẻ tận mắt chứng kiến Allah ban hành lệnh cấm này sao? Vậy còn ai sai quấy hơn kẻ đặt điều dối trá rồi gán cho Allah hầu kéo thiên hạ đi lạc một cách thiếu hiểu biết. Chắc chắn Allah không bao giờ hướng dẫn đám người sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(6,145,'6:145','Ngươi (hỡi Thiên Sứ) hãy nói: “Ta không thấy trong những điều được mặc khải cho Ta có khoản nào cấm (tín đồ Muslim) ăn ngoại trừ động vật chết; hoặc máu tuôn ra (khỏi cơ thể con vật); hoặc thịt heo (lợn) vì nó là loài vật ô uế, không sạch; hoặc những con vật được dâng cúng (các thần linh) ngoài Allah.” Tuy nhiên, ai rơi vào tình huống bất đắc dĩ phải ăn (những món cấm đó) ngoài ý muốn và không quá độ thì quả thật, Thượng Đế của Ngươi hằng tha thứ, hằng khoan dung.');
INSERT INTO "vn_ruwwad" VALUES(6,146,'6:146','Đối với những người Do Thái, TA đã cấm ăn tất cả động vật có móng (liền nhau như lạc đà, đà điểu) và TA cấm họ dùng mỡ bò, mỡ cừu và dê ngoại trừ mỡ dính ở lưng hoặc ở ruột hoặc mỡ dính ở xương. Đó là hình phạt TA dành cho họ bởi hành vi sai trái mà họ đã làm. Quả thật, TA luôn nói sự thật.');
INSERT INTO "vn_ruwwad" VALUES(6,147,'6:147','Nếu như họ phủ nhận Ngươi (hỡi Thiên Sứ) thì Ngươi hãy nói: “Thượng Đế của các ngươi có lòng thương xót bao la. Tuy nhiên, đám người tội lỗi sẽ không thể tránh khỏi sự trừng phạt của Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(6,148,'6:148','Những kẻ thờ đa thần sẽ nói: “Nếu như Allah muốn thì bọn ta và cả tổ tiên của bọn ta chắc chắn đã không thể tổ hợp với Ngài. Quả thật Ngài đã không cấm bọn ta bất cứ điều gì.” Tương tự, những kẻ thời trước họ cũng đã phủ nhận (chân lý) cho đến khi bọn họ nếm lấy sự trừng phạt của TA. Ngươi (hỡi Thiên Sứ) hãy nói với những kẻ thờ đa thần này: “Các ngươi có cơ sở nào (cho điều các người đã nói) hay không, (nếu có) thì các ngươi hãy mang nó đến trình bày cho Ta xem! Thật ra các ngươi chỉ làm theo sự phỏng đoán và tưởng tượng, thật ra các ngươi chỉ là những kẻ khéo bịa chuyện mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(6,149,'6:149','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Chỉ một mình Allah mới có bằng chứng chắc chắn, nếu Ngài thực sự muốn thì chắc chắn Ngài đã hướng dẫn toàn bộ các ngươi.”');
INSERT INTO "vn_ruwwad" VALUES(6,150,'6:150','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Nào, các ngươi hãy đưa ra các nhân chứng của các ngươi về việc Allah đã cấm điều này.” Nếu họ chứng nhận thì Ngươi chớ đừng chứng nhận cùng với họ, Ngươi chớ đừng hùa theo sở thích của những kẻ đã phủ nhận các Lời Mặc Khải của TA cũng như chớ hùa theo những kẻ vô đức tin nơi Đời Sau bởi họ đã tôn thờ cùng với Thượng Đế của họ (các thần linh khác).');
INSERT INTO "vn_ruwwad" VALUES(6,151,'6:151','Ngươi (hỡi Thiên Sứ) hãy nói (với thiên hạ): “(Này hỡi mọi người) hãy cùng đến đây, Ta sẽ đọc cho các ngươi những điều mà Thượng Đế của các ngươi đã cấm các ngươi, đó là: các ngươi không được tổ hợp với Ngài bất cứ thứ gì; các ngươi hãy hiếu thảo với cha mẹ; các ngươi không được giết con cái của các ngươi vì sợ cái nghèo bởi TA (Allah) mới là Đấng Cung Dưỡng cho các ngươi và bọn chúng; các ngươi không được đến gần những hành vi ô uế (tình dục ngoài hôn nhân) dù công khai hay kín đáo; các ngươi không được giết một sinh mạng mà Allah đã qui định bất khả xâm phạm trừ phi vì lý do chính đáng.( Đó là những điều cấm mà Ngài đã sắc lệnh cho các ngươi mong rằng các ngươi hiểu (mà tuân thủ chấp hành).”');
INSERT INTO "vn_ruwwad" VALUES(6,152,'6:152','“Các ngươi không được đến gần tài sản của trẻ mồ côi trừ phi các ngươi muốn cải thiện đời sống cho chúng đến khi chúng lớn khôn (tự quản lý được tải sản riêng); các ngươi hãy đong, đo và cân cho đúng; TA không bắt bất cứ linh hồn nào làm quá khả năng của nó; khi các ngươi nói thì các ngươi hãy nói lời lẽ công bằng cho dù (lời lẽ công bằng đó có nghịch lại) với bà con ruột thịt (của các ngươi); các ngươi hãy thực hiện cho đúng giao ước của Allah. Đó là những điều lệnh mà Ngài đã ban hành cho các ngươi mong rằng các ngươi ghi nhớ (mà tuân thủ chấp hành).”');
INSERT INTO "vn_ruwwad" VALUES(6,153,'6:153','(Các ngươi hãy biết) rằng đây là con đường ngay chính của TA, vì vậy, các ngươi hãy đi theo nó và chớ đi theo những con đường khác để rồi các ngươi đi lạc xa khỏi con đường của Ngài. Đó là những điều mà Ngài đã khuyên bảo các ngươi mong rằng các ngươi biết ngoan đạo (với Ngài).');
INSERT INTO "vn_ruwwad" VALUES(6,154,'6:154','Rồi TA đã ban cho Musa Kinh Sách (Tawrah), hoàn thành (ân huệ của TA) cho người đã làm điều tốt và (Kinh Sách đó là) một sự giảng giải chi tiết về tất cả mọi thứ, và (trong đó) còn có sự chỉ đạo và lòng thương xót (của TA), mong rằng họ có đức tin nơi cuộc gặp gỡ với Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(6,155,'6:155','Và Kinh (Qur’an) này đây, TA ban Nó xuống (và Nó là) một hồng phúc (vĩ đại). Bởi thế, các ngươi hãy theo Nó và các ngươi hãy kính sợ (TA) mong rằng các ngươi được thương xót.');
INSERT INTO "vn_ruwwad" VALUES(6,156,'6:156','(Việc TA ban Kinh Qur’an xuống là) để cho các ngươi (hỡi những kẻ đa thần Ả-rập) không nói: “Quả thật Kinh Sách (Tawrah và Injil) chỉ được ban xuống cho hai nhóm người (Do thái và Thiên Chúa) trước chúng tôi, còn chúng tôi đối với việc học hỏi Kinh Sách của họ thì không biết gì (bởi nó được ban xuống không bằng tiếng nói của chúng tôi).”');
INSERT INTO "vn_ruwwad" VALUES(6,157,'6:157','Hoặc để các ngươi không nói: “Nếu như chúng tôi cũng được ban cho Kinh Sách (giống như những người Do Thái và Thiên Chúa), chắc chắn chúng tôi đã được hướng dẫn hơn họ.” Cho nên, (giờ đây) một bằng chứng, một nguồn chỉ đạo và một hồng phúc từ Thượng Đế của các ngươi quả thật đã đến với các ngươi. Vì vậy, còn ai sai trái hơn kẻ đã phủ nhận và quay lưng với các Lời Mặc Khải của Allah? TA (Allah) sẽ trừng phạt những kẻ quay lưng với các Lời Mặc Khải của TA với hình phạt tồi tệ nhất vì tội chúng đã quay lưng.');
INSERT INTO "vn_ruwwad" VALUES(6,158,'6:158','Lẽ nào họ đang chờ đợi các Thiên Thần (đảm nhận việc rút hồn) đến gặp họ? Hay lẽ nào họ muốn đích thân Thượng Đế của Ngươi (hỡi Thiên Sứ) đến gặp riêng họ? Hay lẽ nào họ muốn xuất hiện một vài dấu hiệu từ Thượng Đế của Ngươi? (Hãy biết rằng) vào Ngày mà một vài dấu hiệu của Thượng Đế của Ngươi xuất hiện (để báo trước cho Giờ Tận Thế), đức tin của một người (vào thời điểm đó) sẽ không giúp ích được gì cho linh hồn đã không có đức tin trước đó hoặc đã không tích lũy điều tốt nào trong đức tin của mình. Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ thờ đa thần phủ nhận Giờ Tận Thế): “Các ngươi hãy chờ đợi, quả thật Ta cũng đang chờ đợi (giống các ngươi).”');
INSERT INTO "vn_ruwwad" VALUES(6,159,'6:159','Quả thật, những kẻ đã phân chia tôn giáo và thành lập giáo phái riêng (như những người Do Thái và Thiên Chúa) thì Ngươi (hỡi Thiên Sứ) hoàn toàn vô can với mọi hành động (lầm lạc) của họ. Vấn đề của họ chỉ liên quan đến Allah, rồi Ngài sẽ cho họ biết về mọi thứ mà họ đã từng làm.');
INSERT INTO "vn_ruwwad" VALUES(6,160,'6:160','Ai mang việc tốt lành đến (trình diện Allah vào Ngày Phán Xét) thì y sẽ được nhân lên mười phần phước (cho việc tốt đó), còn ai mang việc xấu đến (trình diện Ngài) thì y chỉ bị trừng phạt tương ứng với điều xấu mà y đã làm; và họ sẽ không bị đối xử bất công.');
INSERT INTO "vn_ruwwad" VALUES(6,161,'6:161','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ đa thần): “Quả thật, Ta được Thượng Đế của Ta hướng dẫn đến với con đường ngay chính, một tôn giáo đúng đắn, tín ngưỡng thuần túy của Ibrahim; và (Ibrahim) không phải là kẻ thờ đa thần.”');
INSERT INTO "vn_ruwwad" VALUES(6,162,'6:162','Ngươi (hỡi Thiên Sứ) hãy nói: “Quả thật, lễ nguyện Salah của Ta, việc giết tế của Ta, cuộc sống và cái chết của Ta đều dành trọn cho một mình Allah, Thượng Đế của vũ trụ và vạn vật.”');
INSERT INTO "vn_ruwwad" VALUES(6,163,'6:163','“(Allah) không có đối tác ngang vai. Đó là điều mà Ta được lệnh và Ta là một tín đồ Muslim đầu tiên.”');
INSERT INTO "vn_ruwwad" VALUES(6,164,'6:164','Ngươi (hỡi Thiên Sứ) hãy nói: “Lẽ nào Ta phải đi tìm một thượng đế nào khác ngoài Allah trong lúc Ngài đích thực là Thượng Đế của vạn vật? Tất cả mỗi linh hồn chỉ phải chịu trách nhiệm cho việc mình đã làm, không ai phải gánh tội cho ai. Rồi đây các ngươi sẽ phải trở về trình diện Thượng Đế của các ngươi để Ngài cho các ngươi biết về những gì mà các ngươi đã từng tranh cãi.”');
INSERT INTO "vn_ruwwad" VALUES(6,165,'6:165','Và (Allah) là Đấng đã để các ngươi thành những đại diện (của Ngài) trên trái đất. Ngài đã nâng nhóm người này vượt trội hơn nhóm người kia mục đích để thử thách các ngươi bằng những thứ mà Ngài đã ban cho các ngươi. Quả thật, Thượng Đế của Ngươi (hỡi Thiên Sứ) rất nhanh trong việc trừng phạt nhưng quả thật Ngài cũng là Đấng hằng lượng thứ, đầy lòng nhân từ.');
INSERT INTO "vn_ruwwad" VALUES(7,1,'7:1','Alif. Lam. Mim. Sad');
INSERT INTO "vn_ruwwad" VALUES(7,2,'7:2','Kinh (Qur’an) đã được ban xuống cho Ngươi (hỡi Thiên Sứ). Vì vậy, chớ đừng để lòng ngực của Ngươi có sự khó khăn (hay sự hoài nghi) về Nó. (Nó được bạn xuống cho Ngươi) là để Ngươi cảnh báo (nhân loại) và nhắc nhở những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(7,3,'7:3','Các ngươi (hỡi con người) hãy đi theo những điều đã được ban xuống cho các ngươi từ Thượng Đế của các ngươi và các ngươi chớ đừng đi theo những kẻ bảo hộ ngoài (Allah). (Các ngươi được khuyên bảo như thế) nhưng tiếc thay các ngươi chẳng mấy lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(7,4,'7:4','Đã không biết bao thị trấn bị TA hủy diệt (do tội lỗi của dân chúng). Sự trừng phạt của TA đã đến với họ một cách bất ngờ vào ban đêm hoặc ban ngày lúc họ đang nghỉ trưa.');
INSERT INTO "vn_ruwwad" VALUES(7,5,'7:5','Cho nên, khi hình phạt của TA đến với họ thì họ chỉ biết nói: “Quả thật, bầy tôi là những kẻ làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(7,6,'7:6','Rồi đây (vào Ngày Phán Xét) TA chắc chắn sẽ tra hỏi những ai tiếp nhận được Thông Điệp và TA chắc chắn cũng sẽ tra hỏi các Thiên Sứ.');
INSERT INTO "vn_ruwwad" VALUES(7,7,'7:7','TA chắc chắn sẽ kể lại cho họ nghe mọi thứ mà họ đã từng làm (trên thế gian) bằng sự hiểu biết (của TA) bởi TA không hề vắng mặt (bất cứ lúc nào).');
INSERT INTO "vn_ruwwad" VALUES(7,8,'7:8','Việc cân đo (các việc làm tốt xấu) của Ngày hôm đó là sự thật. Vì vậy, ai mà (các việc làm thiện tốt của y) có trọng lượng nặng hơn (trọng lượng các việc làm xấu của y) thì họ sẽ là những người chiến thắng.');
INSERT INTO "vn_ruwwad" VALUES(7,9,'7:9','Còn ai mà (các việc làm thiện tốt của y) có trọng lượng nhẹ hơn (trọng lượng các việc làm xấu của y) thì họ sẽ là những kẻ thiệt thân do đã chối bỏ các Lời Mặc Khải của TA.');
INSERT INTO "vn_ruwwad" VALUES(7,10,'7:10','Quả thật, TA (Allah) đã định cư các ngươi (loài người) trên trái đất và đã tạo cho các ngươi biết bao phương tiện để sinh sống. Tuy nhiên, các ngươi rất ít tri ân (TA)!');
INSERT INTO "vn_ruwwad" VALUES(7,11,'7:11','Quả thật, TA (Allah) đã tạo hóa các ngươi (hỡi loài người) rồi ban cho các ngươi hình thể. TA đã ra lệnh cho các Thiên Thần quỳ lạy chào Adam (tổ tiên của các ngươi). Tất cả (các Thiên Thần) đều quỳ lạy ngoại trừ Iblis, hắn không chịu quỳ lạy.');
INSERT INTO "vn_ruwwad" VALUES(7,12,'7:12','(Allah) phán bảo (Iblis): “Điều gì đã cản nhà ngươi không chịu quỳ lạy khi TA ban lệnh cho ngươi (hỡi Iblis)?” Hắn nói: “Vì bề tôi tốt hơn Y (Adam), Ngài tạo bề tôi từ lửa và Ngài tạo Y từ đất sét.”');
INSERT INTO "vn_ruwwad" VALUES(7,13,'7:13','(Allah) phán: “Nhà ngươi hãy xuống khỏi đó (Thiên Đàng), nhà ngươi không được ngạo mạn trong đó. Nhà ngươi hãy đi ra, nhà ngươi quả là một tên đáng khinh.”');
INSERT INTO "vn_ruwwad" VALUES(7,14,'7:14','(Iblis) khẩn cầu: “Xin Ngài hãy tạm tha cho bề tôi được sống cho đến Ngày Phục Sinh.”');
INSERT INTO "vn_ruwwad" VALUES(7,15,'7:15','(Allah) phán: “Được, nhà ngươi sẽ được tạm tha (cho đến ngày tiếng còi đầu tiên được cất lên).”');
INSERT INTO "vn_ruwwad" VALUES(7,16,'7:16','(Iblis) nói: “Vì Ngài xua đuổi bề tôi, bề tôi quyết sẽ lôi kéo chúng (con cháu của Adam) lệch khỏi con đường ngay chính của Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(7,17,'7:17','“Bề tôi quyết sẽ tấn công chúng từ phía đằng trước, đằng sau, bên phải và bên trái, rồi Ngài sẽ thấy đa số bọn chúng là những kẻ không biết ơn (Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(7,18,'7:18','(Allah) phán: “Nhà ngươi hãy cút ra khỏi đó (Thiên Đàng), thứ đáng khinh và đáng tống cổ đi nơi khác. Ai trong bọn họ đi theo nhà ngươi thì tất cả sẽ bị TA nhét đầy vào Hỏa Ngục.”');
INSERT INTO "vn_ruwwad" VALUES(7,19,'7:19','(Allah) phán: “Này Adam, Ngươi và vợ của Ngươi hãy sống trong Thiên Đàng, hai Ngươi hãy ăn thỏa thích những gì mình muốn, nhưng hai Ngươi chớ đừng đến gần cây này kẻo hai Ngươi trở thành những kẻ làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(7,20,'7:20','Tuy nhiên, Shaytan (Iblis) đã thì thầm xúi giục hai người hầu làm cho hai người họ lộ ra phần đáng xấu hổ của mình, hắn nói: “Sở dĩ Thượng Đế cấm hai người đến gần cái ‘Cây’ này là vì không muốn hai người trở thành Thiên Thần hoặc trở thành người sống bất tử ấy thôi!”');
INSERT INTO "vn_ruwwad" VALUES(7,21,'7:21','Hắn đã thề thốt với hai người họ, bảo: “Thật ra ta chỉ là một người cố vấn thật lòng cho hai người thôi.”');
INSERT INTO "vn_ruwwad" VALUES(7,22,'7:22','Thế là hắn đã dụ dỗ được hai người họ thông qua sự lường gạt và lừa dối. Ngay sau khi hai người họ nếm (trái của) ‘Cây cấm’ đó thì phần xấu hổ của cả hai liền lộ ra, hai người phải lượm lá cây trong Thiên Đàng che kín thân thể của mình lại. (Lúc đó), Thượng Đế gọi hai người họ, phán: “Chẳng phải TA đã cấm hai ngươi đến gần cái Cây đó và bảo hai ngươi rằng Shaytan là kẻ thù công khai của hai ngươi đó sao?!”');
INSERT INTO "vn_ruwwad" VALUES(7,23,'7:23','Cả hai đồng thưa: “Lạy Thượng Đế của bầy tôi, quả thật bầy tôi đã bất công với chính bản thân mình, nếu Ngài không tha thứ và đoái thương bầy tôi thì chắc chắn bầy tôi sẽ trở thành những kẻ thua thiệt.”');
INSERT INTO "vn_ruwwad" VALUES(7,24,'7:24','(Allah) phán: “Cả ba nhà ngươi (Adam, Hauwa và Iblis) hãy (rời khỏi Thiên Đàng) đi xuống (trái đất), các ngươi sẽ là kẻ thù của nhau. Trên trái đất, các ngươi sẽ có một nơi cư ngụ và phương tiện sinh sống cho đến thời hạn ấn định.”');
INSERT INTO "vn_ruwwad" VALUES(7,25,'7:25','(Allah) phán: “Các ngươi sẽ sống trên trái đất và chết ở đó và cũng từ đó các ngươi sẽ được phục sinh trở lại.”');
INSERT INTO "vn_ruwwad" VALUES(7,26,'7:26','Hỡi con cháu của Adam (loài người), quả thật, TA đã ban xuống cho các ngươi y phục để các ngươi che đậy phần xấu hổ của mình và để các ngươi làm đẹp; tuy nhiên, y phục tốt nhất là lòng Taqwa(. Đấy là một trong các dấu hiệu của Allah, mong rằng họ biết lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(7,27,'7:27','Hỡi con cháu Adam (loài người), các ngươi chớ để Shaytan lừa gạt các ngươi giống như hắn đã từng (lường gạt và) trục xuất cha mẹ của các ngươi ra khỏi Thiên Đàng. Hắn đã lột y phục của hai người họ để cả hai lộ ra phần xấu hổ của cơ thể. Quả thật, hắn và bộ lạc của hắn nhìn thấy các ngươi trong khi các ngươi không nhìn thấy chúng. Quả thật, TA đã để những tên Shaytan làm những vị bảo hộ cho những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(7,28,'7:28','Mỗi lần (những kẻ đa thần) làm điều ố uế (như thờ bục tượng, đi vòng quanh ngôi đền Ka’bah với thân thể trần truồng, ...), chúng bảo: “Chúng tôi đã thấy ông bà của mình làm như thế, và Allah đã ra lệnh cho chúng tôi làm như vậy.” Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Quả thật, Allah không hề bảo làm điều ô uế. Lẽ nào các ngươi cáo buộc cho Allah những điều mà các ngươi không biết?!”');
INSERT INTO "vn_ruwwad" VALUES(7,29,'7:29','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ đa thần): “Thượng Đế của Ta chỉ sai bảo toàn điều công lý; (Ngài bảo) các ngươi phải hướng mặt (về Allah) trong mỗi nơi thờ phượng; (Ngài bảo) các ngươi hãy chân thành cầu nguyện Ngài. (Rồi đây các ngươi sẽ được phục sinh để trở về với Ngài) giống như việc Ngài đã khởi tạo các ngươi lúc ban đầu.”');
INSERT INTO "vn_ruwwad" VALUES(7,30,'7:30','(Allah đã làm cho nhân loại thành hai nhóm:) một nhóm được Ngài hướng dẫn đúng đường và một nhóm bị lầm lạc. (Nhóm lầm lạc là) bởi vì họ đã chọn lấy Shaytan làm những kẻ bảo hộ thay vì Allah và họ tưởng rằng mình đã được hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(7,31,'7:31','Hỡi con cháu của Adam (loài người), các ngươi hãy mặc y phục chỉnh tề khi đến Masjid và các ngươi hãy ăn, uống thỏa thích nhưng không được phung phí, bởi lẽ Allah không yêu thương những kẻ phung phí.');
INSERT INTO "vn_ruwwad" VALUES(7,32,'7:32','Ngươi (hỡi Thiên Sứ) hãy nói với (những kẻ đa thần): “Ai đã cấm những thứ làm đẹp cũng như các loại (thực phẩm tốt sạch mà Allah đã tạo ra cho đám bề tôi của Ngài?” Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Tất cả những thứ đó là dành cho những người có đức tin ở cuộc sống trần tục này và dành riêng cho họ ở Đời Sau.” Như thế đó, TA (Allah) đã giải thích rõ các Lời Mặc Khải của TA cho nhóm người hiểu biết.');
INSERT INTO "vn_ruwwad" VALUES(7,33,'7:33','Ngươi (hỡi Thiên Sứ) hãy nói: “Quả thật, Thượng Đế của Ta chỉ cấm những điều ô uế dù công khai hay kín đáo; chỉ cấm điều tội lỗi và áp bức (người khác) một cách bất công; Ngài chỉ cấm việc tổ hợp (các thần linh) cùng với Allah trong thờ phượng, điều mà Ngài không ban xuống (cho ai) bất cứ thẩm quyền nào; và Ngài chỉ cấm các ngươi nói về Allah khi các ngươi không biết gì (về Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(7,34,'7:34','Mỗi cộng đồng đều có một thời hạn nhất định. Cho nên, khi thời hạn ấn định đã đến, họ sẽ không thể trì hoãn thêm bất cứ giờ (khắc) nào cũng như không thể ra đi sớm hơn.');
INSERT INTO "vn_ruwwad" VALUES(7,35,'7:35','Hỡi con cháu Adam, khi các Sứ Giả xuất thân từ các ngươi đến gặp các ngươi thuật lại các Lời Mặc Khải của TA cho các ngươi nghe, người nào ngoan đạo và cải thiện bản thân thì họ sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "vn_ruwwad" VALUES(7,36,'7:36','Ngược lại, những ai phủ nhận các Lời Mặc Khải của TA và ngạo mạn xem thường chúng thì sẽ là những người bạn của Hỏa Ngục, họ sẽ ở trong đó muôn đời.');
INSERT INTO "vn_ruwwad" VALUES(7,37,'7:37','Bởi thế, còn ai sai quấy hơn kẻ cáo buộc điều dối trá cho Allah hoặc phủ nhận các Lời Mặc Khải của Ngài. Những kẻ đó chỉ hưởng được phần (lộc) đã an bày cho họ trong quyển Kinh Mẹ (Lawhu Al-Mahfuzh) cho tới khi các Sứ Giả (Thiên Thần) của TA đến rút hồn họ. (Lúc đó, các Thiên Thần sẽ hỏi họ), nói: “Đâu rồi những thần linh mà các ngươi đã cầu nguyện ngoài Allah?” Họ đáp “Chúng đã bỏ chúng tôi đi mất” và họ tự xác nhận mình chính là những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(7,38,'7:38','(Các Thiên Thần) bảo: “Các ngươi (hỡi những kẻ đa thần) hãy đi vào Hỏa Ngục cùng với các cộng đồng loài Jinn và loài người thuộc các thế hệ trước các ngươi.” Cứ mỗi lần một cộng đồng mới đi vào thì nó liền nguyền rủa cộng đồng đã vào trước đó. Đến khi tất cả cùng gặp mặt nhau trong Hỏa Ngục thì nhóm vào sau (thuộc tầng lớp đi theo) lên tiếng đổ tội cho nhóm vào trước (thuộc tầng lớp lãnh đạo), nói: “Lạy Thượng Đế của bầy tôi, chính đám người này đã lôi kéo làm bầy tôi đi lạc, khẩn xin Ngài trừng phạt họ gấp đôi trong Hỏa Ngục.” (Allah) phán: “Tất cả đều bị trừng phạt gấp đôi như nhau nhưng các ngươi không biết đó thôi.”');
INSERT INTO "vn_ruwwad" VALUES(7,39,'7:39','Nhóm vào trước lên tiếng đáp lại nhóm vào sau: “Thật ra các ngươi cũng đâu có gì tốt hơn bọn ta đây, thôi các ngươi hãy nếm lấy sự trừng phạt bởi những gì các ngươi đã tích lũy.”');
INSERT INTO "vn_ruwwad" VALUES(7,40,'7:40','Quả thật, những kẻ phủ nhận các Lời Mặc Khải của TA (Allah) và ngạo mạn với chúng, sẽ không có cổng trời nào được mở ra cho họ, và họ sẽ không vào được Thiên Đàng trừ phi con lạc đà chui qua được cái lỗ của chiếc kim may. TA trừng phạt những kẻ tội lỗi đúng như thế đó.');
INSERT INTO "vn_ruwwad" VALUES(7,41,'7:41','Họ sẽ có những chiếc giường lửa cùng với những tấm phủ bằng lửa bên trên. TA trừng phạt đám người sai quấy đúng như thế đó.');
INSERT INTO "vn_ruwwad" VALUES(7,42,'7:42','Riêng những người có đức tin và hành thiện – TA (Allah) không bắt linh hồn gánh vác quá khả năng của nó –, họ sẽ là những người bạn của Thiên Đàng, họ sẽ ở trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(7,43,'7:43','TA sẽ xóa khỏi lòng của họ sự ghen tỵ và thù hằn, sẽ có những con sông chảy bên dưới họ, và họ sẽ nói: “Alhamdulillah, Đấng đã hướng dẫn bầy tôi đến với (hồng phúc) này, nếu không nhờ Allah hướng dẫn thì bầy tôi đã không được hướng dẫn đúng đường. Các vị Thiên Sứ của Thượng Đế của bầy tôi quả thật đã mang đến điều chân lý.” Rồi họ được hô gọi, bảo: “Đó là Thiên Đàng, các ngươi sẽ được thừa hưởng Nó bởi những điều (tốt đẹp) mà các ngươi đã làm.');
INSERT INTO "vn_ruwwad" VALUES(7,44,'7:44','Cư dân Thiên Đàng gọi cư dân Hỏa Ngục, hỏi: “Chúng tôi thực sự đã thấy những gì mà Thượng Đế của chúng tôi đã hứa với chúng tôi (trước đây), nó đã xảy ra thật, vậy các người đã thấy những gì mà Thượng Đế của các người đã hứa với các người (trước đây) không, nó đã xảy ra thật không?” Họ đáp: “Đúng là sự thật.” Thế rồi vị (Thiên Thần) hô gọi lớn tiếng hô vang giữa họ: “Allah nguyền rủa những kẻ làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(7,45,'7:45','Những ai mà họ ngăn cản con đường chính đạo của Allah cũng như mong muốn làm nó trở nên cong quẹo và không có đức tin ở nơi Đời Sau.');
INSERT INTO "vn_ruwwad" VALUES(7,46,'7:46','Giữa họ (cư dân Thiên Đàng và cư dân Hỏa Ngục) là một vách ngăn cách (được gọi là các gò cao). Trên các gò cao (này), có những người( biết rõ từng người của họ (cư dân Thiên Đàng và cư dân Hỏa Ngục) qua các dấu vết (trên gương mặt) của họ. (Những người trên các gò cao này) gọi những người bạn của Thiên Đàng, chúc: “Mong bằng an cho quí vị.” (Những người trên các gò cao) vẫn chưa được vào trong đó (Thiên Đàng) nhưng họ thèm khát được vào.');
INSERT INTO "vn_ruwwad" VALUES(7,47,'7:47','Khi họ (những người trên các gò cao) nhìn về phía những người bạn của Hỏa Ngục thì họ liền cầu khẩn: “Lạy Thượng Đế của bầy tôi, xin Ngài đừng nhập bầy tôi cùng với đám người làm điều sai quấy đó.”');
INSERT INTO "vn_ruwwad" VALUES(7,48,'7:48','Những người của các gò cao gọi những người (của Hỏa Ngục) mà họ nhận biết được qua nét đặc trưng (trên gương mặt) của họ: “Những gì mà các người tom góp (từ tài sản, quyền lực, con cái) cũng như những gì mà các người từng tự hào và kiêu ngạo về chúng có giúp ích được gì cho các người không?!”');
INSERT INTO "vn_ruwwad" VALUES(7,49,'7:49','(Allah phán với những kẻ vô đức tin:) “Có phải các ngươi đã thề thốt rằng những người (có đức tin này) sẽ không được lòng khoan dung của Allah?” (Allah phán bảo những người có đức tin:) “Các ngươi hãy bước vào Thiên Đàng, các ngươi sẽ không còn lo sợ và cũng sẽ không buồn phiền.”');
INSERT INTO "vn_ruwwad" VALUES(7,50,'7:50','Những người bạn của Hỏa Ngục réo gọi những người bạn của Thiên Đàng, van xin: “Xin quí vị hãy rót xuống cho chúng tôi một ít nước hoặc bất cứ món lộc nào mà Allah đã ban cấp cho quí vị.” (Những người bạn của Thiên Đàng) đáp: “Quả thật, Allah đã cấm hai thứ đó đối với những kẻ vô đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(7,51,'7:51','Những người đã lấy tôn giáo của mình làm phương tiện để giải trí, mua vui và bị đời sống trần tục này đánh lừa thì ngày hôm nay, TA quên lãng họ giống như họ đã từng quên lãng ngày trình diện này của họ và vì họ đã chối bỏ các Lời Mặc Khải của TA.');
INSERT INTO "vn_ruwwad" VALUES(7,52,'7:52','Quả thật, TA đã mang đến cho họ quyển Kinh (Qur’an), TA đã giải thích Nó rõ ràng bằng kiến thức (của TA), (và Nó là) nguồn chỉ đạo và là hồng phúc dành cho đám người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(7,53,'7:53','Lẽ nào (những kẻ vô đức tin) lại chờ đợi biến cố xảy ra như (Qur’an) đã cảnh báo? Vào Ngày mà biến cố xảy ra như đã cảnh báo, những kẻ đã phớt lờ nó trước đây sẽ nói: “Quả thật, các Thiên Sứ của Thượng Đế của chúng tôi đã mang đến sự thật, vậy chúng tôi có được người biện hộ nào đứng ra biện hộ cho chúng tôi không hoặc có thể nào chúng tôi được trả về (cuộc sống trần gian) để chúng tôi có cơ hội làm khác với việc mà chúng tôi đã từng làm.” Quả thật, họ đã làm thiệt thân của chính mình và những thứ mà họ đã bịa đặt gian dối đã bỏ mặc họ đi mất dạng.');
INSERT INTO "vn_ruwwad" VALUES(7,54,'7:54','Quả thật, Thượng Đế của các ngươi (loài người) chính là Allah, Ngài đã tạo các tầng trời và trái đất trong sáu ngày, rồi Ngài an vị trên Ngai Vương (của Ngài). Ngài luân chuyển ngày đêm một cách nhanh chóng; mặt trời, mặt trăng và các tinh tú đều vận hành theo mệnh lệnh của Ngài. Việc tạo hóa và quyền chi phối là của riêng một mình Ngài. Thật phúc thay Allah, Thượng Đế của vũ trụ và vạn vật.');
INSERT INTO "vn_ruwwad" VALUES(7,55,'7:55','Các ngươi (hỡi những người có đức tin) hãy cầu nguyện Thượng Đế của các ngươi trong sự khiêm nhường và riêng tư (âm thầm, kín đáo). Quả thật, Ngài không yêu thương những kẻ vượt quá giới hạn.');
INSERT INTO "vn_ruwwad" VALUES(7,56,'7:56','Các ngươi chớ đừng hủy hoại trái đất sau khi nó đã được cải thiện. Các ngươi hãy cầu nguyện Ngài bằng nỗi sợ hãi và niềm hy vọng. Quả thật, lòng thương xót của Allah luôn gần kề với những người làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(7,57,'7:57','Ngài là Đấng gởi các luồng gió báo trước tin mừng về lòng khoan dung của Ngài. Đến khi nào các đám mây nặng trịch (do chứa nước) tập trung lại, TA chuyển chúng đến mảnh đất chết khô, TA ban mưa xuống làm mọc ra đủ loại trái quả (hoa màu). Tương tự, TA sẽ làm người chết sống lại như thế, mong rằng các ngươi lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(7,58,'7:58','Mảnh đất tốt sẽ mọc ra cây cối (dồi dào và tươi tốt) dưới sự cho phép của Allah, còn mảnh đất xấu chỉ mọc ra cây cối thưa thớt và khẳng khiu. Như thế đó, TA giải thích rõ ràng các Lời Mặc Khải (của TA) cho nhóm người biết tri ân.');
INSERT INTO "vn_ruwwad" VALUES(7,59,'7:59','Quả thật, TA (Allah) đã cử phái Nuh (Nô-ê) đến với người dân của Y. Y đã bảo (dân của mình): “Này hỡi dân ta, các người hãy thờ phượng một mình Allah thôi, các người không hề có thần linh nào khác ngoài Ngài. Ta thực sự lo sợ giùm cho các người về sự trừng phạt vào một ngày vĩ đại!”');
INSERT INTO "vn_ruwwad" VALUES(7,60,'7:60','Những tên lãnh đạo và cầm quyền trong đám dân của (Nuh) bảo Y: “Bọn ta thấy ngươi mới thực sự là kẻ lầm lạc.”');
INSERT INTO "vn_ruwwad" VALUES(7,61,'7:61','(Nuh) đáp: “Hỡi người dân của Ta, Ta không phải là kẻ lầm lạc. Ta đích thực chính là Thiên Sứ do Thượng Đế của vũ trụ và vạn vật cử phái đến.”');
INSERT INTO "vn_ruwwad" VALUES(7,62,'7:62','“Ta truyền đạt lại cho các người các thông điệp của Thượng Đế Ta. Ta là người đến khuyên răn các người, bởi lẽ Ta biết từ nơi Allah những điều mà các người không biết.”');
INSERT INTO "vn_ruwwad" VALUES(7,63,'7:63','“Phải chăng các người ngạc nhiên về việc lời nhắc nhở của Thượng Đế của các người do một người xuất thân từ các người mang đến hầu để các người ngoan đạo và được thương xót ư?!”');
INSERT INTO "vn_ruwwad" VALUES(7,64,'7:64','Người dân (của Nuh) đã phủ nhận Y nên TA đã cứu Y và những ai theo Y trên con tàu. TA đã nhấn chìm những kẻ phủ nhận các dấu hiệu của TA, quả thật họ là đám người mù quáng.');
INSERT INTO "vn_ruwwad" VALUES(7,65,'7:65','Về người dân ‘Ad, (TA đã cử) Hud, người anh em của họ đến với họ. (Hud) nói: “Này hỡi dân ta, các người hãy thờ phượng một mình Allah thôi, các người không hề có thần linh nào khác ngoài Ngài. Lẽ nào các người không kính sợ Ngài?”');
INSERT INTO "vn_ruwwad" VALUES(7,66,'7:66','Những tên lãnh đạo và cầm quyền trong đám dân của (Hud) bảo Y: “Bọn ta thấy ngươi là kẻ điên rồ. Bọn ta nghĩ ngươi là một tên nói dối.”');
INSERT INTO "vn_ruwwad" VALUES(7,67,'7:67','(Hud) đáp: “Hỡi người dân của Ta, Ta không phải là kẻ điên rồ. Ta đích thực là Sứ Giả do Thượng Đế của vũ trụ và vạn vật cử phái đến.”');
INSERT INTO "vn_ruwwad" VALUES(7,68,'7:68','“Ta truyền đạt lại cho các người các thông điệp của Thượng Đế Ta. Ta là người khuyên răn chân thành cho các người.”');
INSERT INTO "vn_ruwwad" VALUES(7,69,'7:69','“Phải chăng các người ngạc nhiên về việc lời nhắc nhở của Thượng Đế của các người do một người xuất thân từ các người mang đến cảnh báo các người ư? Các người hãy nhớ lại khi Ngài làm cho các người kế vị sau người dân của Nuh và ban thêm cho các người (ân huệ) qua việc tạo cho các ngươi có dáng vóc to lớn và vạm vỡ. Các người hãy nhớ đến hồng phúc mà Allah đã ban cho các người hi vọng các người là những người thành đạt.”');
INSERT INTO "vn_ruwwad" VALUES(7,70,'7:70','(Người dân của Hud) nói: “(Này Hud), ngươi đến là để bảo bọn ta chỉ tôn thờ một mình Allah duy nhất và từ bỏ hết các thần linh mà tổ tiên bọn ta đã tôn thờ trước đây đúng không? Vậy thì ngươi hãy mang đến điều (hình phạt) mà ngươi đã cảnh báo bọn ta xem nào, nếu như ngươi là kẻ nói thật.”');
INSERT INTO "vn_ruwwad" VALUES(7,71,'7:71','(Nuh) nói: “Chắc chắn sự trừng phạt và cơn thịnh nộ của Thượng Đế của các người sẽ giáng xuống các người. Lẽ nào các người lại tranh luận với ta về những cái tên (thần linh) do chính các người và tổ tiên của các người bịa ra chứ Allah không hề ban cho bất cứ thẩm quyền nào? Vậy thì các người hãy chờ xem, Ta cũng sẽ chờ xem cùng với các người.”');
INSERT INTO "vn_ruwwad" VALUES(7,72,'7:72','Thế rồi TA đã cứu vớt (Hud) cùng với những ai đi theo Y bằng lòng thương xót nơi TA và TA đã loại bỏ toàn bộ những kẻ đã phủ nhận các dấu hiệu của TA vì chúng đã không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(7,73,'7:73','Về người dân Thamud, (TA đã cử) Saleh, người anh em của họ đến với họ. (Saleh) nói: “Hỡi dân ta, các người hãy thờ phượng một mình Allah thôi, các người không hề có thần linh nào khác ngoài Ngài. Quả thật, một bằng chứng rõ ràng từ Thượng Đế của các người đã đến với các người. Đây là con lạc đà cái của Allah, một dấu hiệu dành cho các người. Cho nên, các người hãy để nó được tự do ăn trên đất của Allah và chớ đừng đụng chạm đến nó bằng bất cứ điều xấu nào kẻo các người bị trừng phạt đau đớn.”');
INSERT INTO "vn_ruwwad" VALUES(7,74,'7:74','“Các người hãy nhớ lại khi Ngài làm cho các người kế vị sau người dân của ‘Ad và ban cho các người cuộc sống (hưởng thụ) trên trái đất khi các người đào đất làm lâu đài và đục núi làm nhà. Các người hãy nhớ về các hồng ân của Allah và chớ đừng tàn phá trên trái đất.”');
INSERT INTO "vn_ruwwad" VALUES(7,75,'7:75','Những tên lãnh đạo và cầm quyền đầy kiêu ngạo trong đám dân của (Saleh) nói với những người yếu thế có đức tin: “Các người có biết chắc Saleh là Sứ Giả được Thượng Đế của Y gửi đến không?” (Những người yếu thế) đáp: “Chúng tôi tin vào điều mà Người mang đến.”');
INSERT INTO "vn_ruwwad" VALUES(7,76,'7:76','Những tên kiêu ngạo kia bảo: “Bọn ta phủ nhận điều mà các người đã tin.”');
INSERT INTO "vn_ruwwad" VALUES(7,77,'7:77','Vì vậy, họ đã sát hại con lạc đà cái bất chấp lệnh cấm của Thượng Đế của họ và (thách thức) bảo: “Này Saleh, ngươi hãy mang đến (hình phạt) mà ngươi đã dọa bọn ta xem nào nếu ngươi thực sự là Sứ Giả.”');
INSERT INTO "vn_ruwwad" VALUES(7,78,'7:78','Thế là một trận động đất dữ dội đã bắt phạt họ vào buổi sáng khiến họ trở thành những cái xác trong chính ngôi nhà của họ.');
INSERT INTO "vn_ruwwad" VALUES(7,79,'7:79','Rồi (Saleh) rời bỏ họ, nói: “Này người dân của Ta, quả thật, Ta đã truyền đạt cho các người bức thông điệp của Thượng Đế của Ta và Ta cũng đã khuyên bảo các người, nhưng các người vốn không ưa thích lời khuyên bảo bao giờ.”');
INSERT INTO "vn_ruwwad" VALUES(7,80,'7:80','Và Lut, khi Y bảo người dân của mình: “Phải chăng các người làm điều đồi bại mà chưa từng ai trên đời này đã làm trước đây?”');
INSERT INTO "vn_ruwwad" VALUES(7,81,'7:81','“Các người tìm đến đàn ông để giải tỏa nhục dục thay vì với phụ nữ. Không, các người thực sự là một đám người vượt quá giới hạn.”');
INSERT INTO "vn_ruwwad" VALUES(7,82,'7:82','Câu trả lời của đám dân của (Lut) không gì khác ngoài lời: “Mọi người hãy trục xuất chúng (Lut và những người có đức tin) ra khỏi thị trấn của các người, bởi chúng là những người muốn giữ mình trong sạch!”');
INSERT INTO "vn_ruwwad" VALUES(7,83,'7:83','Bởi thế, TA đã giải cứu (Lut) và gia đình của Y ngoại trừ vợ của Y. Ả nằm trong đám người bị tiêu diệt kia.');
INSERT INTO "vn_ruwwad" VALUES(7,84,'7:84','TA đã trừng phạt họ bằng một trận mưa kinh hoàng. Ngươi (hỡi Thiên Sứ) hãy nhìn xem hậu quả của đám người tội lỗi đã xảy ra thế nào?');
INSERT INTO "vn_ruwwad" VALUES(7,85,'7:85','Về người dân Madyan (Madian), (TA đã cử) Shu’aib, người anh em của họ đến với họ. (Shu’aib) nói: “Các người hãy thờ phượng một mình Allah thôi, các người không hề có thần linh nào khác ngoài Ngài. Quả thật, một bằng chứng rõ ràng từ Thượng Đế của các người đã đến với các người. Cho nên, các người hãy đong đo cho đúng, cân cho đủ, các người chớ đừng gian lận thiên hạ bất cứ điều gì, và chớ đừng tàn phá trái đất (bằng tội lỗi) sau khi nó đã được lập trật tự. Điều đó tốt nhất cho các người nếu các người là những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(7,86,'7:86','“Các người chớ đừng ngồi trên tất cả các nẻo đường mà hăm dọa (giết chóc) và ngăn chặn con đường chính nghĩa của Allah đối với ai đã tin tưởng, và tìm cách bẻ cong con đường đó (của Ngài). Các người hãy nhớ lại khi trước đây các người (với số lượng) ít ỏi rồi được Ngài gia tăng số lượng thêm đông, các người hãy nhìn xem những kẻ tàn phá đã có kết cuộc như thế nào?”');
INSERT INTO "vn_ruwwad" VALUES(7,87,'7:87','“Nếu như một nhóm trong các người đã tin tưởng vào điều mà Ta mang đến và nhóm còn lại thì không tin tưởng thì các người (những vô đức tin) hãy kiên nhẫn cho đến khi Allah phân xử giữa chúng ta, bởi lẽ Ngài là vị Thẩm Phán Ưu Việt.”');
INSERT INTO "vn_ruwwad" VALUES(7,88,'7:88','Những tên lãnh đạo kiêu căng trong đám dân của Y nói: “Này Shu’aib, chắc chắn bọn ta sẽ đuổi Ngươi và những kẻ có đức tin cùng với Ngươi ra khỏi thị trấn của bọn ta hoặc các ngươi phải trở lại tín ngưỡng của bọn ta.” (Shu’aib) đáp: “Ngay cả khi chúng tôi không thích ư?”');
INSERT INTO "vn_ruwwad" VALUES(7,89,'7:89','“Chắc chắn chúng tôi (sẽ là những kẻ) đã đặt điều nói dối cho Allah nếu chúng tôi trở lại tín ngưỡng của các người sau khi Allah đã cứu chúng tôi khỏi nó. Chúng tôi không trở lại với nó trừ phi Allah – Thượng Đế của chúng tôi muốn (khác đi). Thượng Đế của chúng tôi có kiến thức bao trùm tất cả mọi thứ, chúng tôi nguyện phó thác cho Ngài. Lạy Thượng Đế của bầy tôi, xin Ngài hãy phân xử giữa bầy tôi và đám dân của bầy tôi bằng công lý, bởi Ngài là Đấng phân xử ưu việt nhất.”');
INSERT INTO "vn_ruwwad" VALUES(7,90,'7:90','Những tên lãnh đạo vô đức tin trong đám dân của Y nói: “Nếu như các ngươi đi theo Shu’aib thì chắc chắn các ngươi sẽ thất bại.”');
INSERT INTO "vn_ruwwad" VALUES(7,91,'7:91','Thế là một trận động đất kinh hoàng đã bắt lấy chúng khiến chúng trở thành những cái xác trong chính ngôi nhà của chúng.');
INSERT INTO "vn_ruwwad" VALUES(7,92,'7:92','Những kẻ đã phủ nhận Shu’aib giống như họ đã chưa từng sống nơi đó bao giờ; những kẻ đã phủ nhận Shu’aib là những kẻ đã thất bại.');
INSERT INTO "vn_ruwwad" VALUES(7,93,'7:93','Vậy là (Shu’aib) rời bỏ họ ra đi, nói: “Này người dân của Ta! Quả thật, Ta đã truyền đạt cho các người thông điệp của Thượng Đế của Ta và Ta cũng đã khuyên bảo các người. Làm sao Ta có thể đau buồn cho đám người vô đức tin!”');
INSERT INTO "vn_ruwwad" VALUES(7,94,'7:94','Không một thị trấn nào (phủ nhận) vị Nabi được TA cử phái đến với họ mà TA lại không bắt dân cư của nó nếm trải đau khổ và bệnh tật, mong là (qua sự việc đó) họ biết hạ mình (thần phục TA).');
INSERT INTO "vn_ruwwad" VALUES(7,95,'7:95','Rồi TA đã thay điều xấu thành điều tốt đẹp cho đến khi họ được hưng thịnh thì lại nói: “Trước đây tổ tiên của chúng tôi cũng đã từng trải qua gian khổ và sung sướng thế này.” Thế là TA bất ngờ bắt phạt họ trong lúc họ không hay biết.');
INSERT INTO "vn_ruwwad" VALUES(7,96,'7:96','Nếu như cư dân của các thị trấn có đức tin và kính sợ (TA) thì chắc chắn TA đã mở ra cho họ các phúc lành từ trời đất. Tuy nhiên, họ đã phủ nhận đức tin nên TA đã bắt phạt họ về những gì mà họ đã làm.');
INSERT INTO "vn_ruwwad" VALUES(7,97,'7:97','Phải chăng cư dân của các thị trấn cảm thấy an toàn khi sự trừng phạt của TA (bất ngờ) chụp bắt họ vào ban đêm lúc họ đang say giấc?!');
INSERT INTO "vn_ruwwad" VALUES(7,98,'7:98','Phải chăng họ cảm thấy an toàn khi sự trừng phạt của TA (bất ngờ) túm bắt họ lúc mặt trời lên cao khi họ đang vui chơi?!');
INSERT INTO "vn_ruwwad" VALUES(7,99,'7:99','Phải chăng họ cảm thấy an toàn trước kế hoạch (trừng phạt bí mật) của Allah?! Không ai cảm thấy an toàn trước kế hoạch của Allah ngoại trừ đám người thua thiệt.');
INSERT INTO "vn_ruwwad" VALUES(7,100,'7:100','Lẽ nào không là bài học cho những kẻ thừa kế trái đất sau khi cư dân (của các thị trấn trước đây đã bị tiêu diệt)? Nếu muốn, TA có thể tiêu diệt chúng bởi tội lỗi của chúng, TA có thể niêm kín con tim của chúng để chúng không nghe được (chân lý).');
INSERT INTO "vn_ruwwad" VALUES(7,101,'7:101','Đó là những thị trấn (của Nuh, Hud, Saleh, Lut, Shu’aib) mà TA đã kể cho Ngươi (hỡi Thiên Sứ) về thông tin của họ. Quả thật, các Sứ Giả xuất thân từ họ đã đến gặp họ với bằng chứng rõ ràng nhưng họ đã không có đức tin nơi những điều mà họ đã phủ nhận trước đó. Như thế đó, Allah đã niêm kín con tim của đám người vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(7,102,'7:102','TA không thấy đa số bọn họ giữ đúng giao ước mà TA chỉ thấy đa số bọn họ là những kẻ dấy loạn, bất tuân.');
INSERT INTO "vn_ruwwad" VALUES(7,103,'7:103','Rồi sau bọn họ, TA đã phái Musa mang những phép lạ của TA đến gặp Pha-ra-ông và quần thần của hắn nhưng chúng đã phủ nhận các phép lạ đó. Thế nên, Ngươi (hỡi Thiên Sứ) hãy xem hậu quả của những kẻ đại nghịch đã xảy ra thế nào!');
INSERT INTO "vn_ruwwad" VALUES(7,104,'7:104','Musa nói: “Hỡi Pha-ra-ông, quả thật Ta chính là Sứ Giả của Thượng Đế của vũ trụ và vạn vật.”');
INSERT INTO "vn_ruwwad" VALUES(7,105,'7:105','“Ta chỉ được phép nói sự thật về Allah. Quả thật, Ta mang đến cho quí ngài bằng chứng rõ ràng từ Thượng Đế của quí ngài, vì vậy, hãy cho người dân Israel đi theo Ta.”');
INSERT INTO "vn_ruwwad" VALUES(7,106,'7:106','(Pha-ra-ông) bảo: “Nếu Ngươi mang đến một dấu lạ thì Ngươi hãy trưng bày nó ra xem nếu Ngươi nói thật!”');
INSERT INTO "vn_ruwwad" VALUES(7,107,'7:107','Thề là (Musa) ném chiếc gậy của Y xuống, nó lập tức biến thành một con rắn thực sự.');
INSERT INTO "vn_ruwwad" VALUES(7,108,'7:108','Và khi Y rút bàn tay của Y ra (sau khi áp nó bên sườn trong chiếc áo), nó trở nên trắng sáng trước mặt những người chứng kiến.');
INSERT INTO "vn_ruwwad" VALUES(7,109,'7:109','Các quần thần của Pha-ra-ông lên tiếng bảo: “Rõ ràng tên này là một phù thủy tài giỏi.”');
INSERT INTO "vn_ruwwad" VALUES(7,110,'7:110','“Hắn muốn đuổi quí ngài ra khỏi lãnh thổ của quí ngài.” (Pha-ra-ông bảo:) “Vậy các khanh có ý xử trí hắn ra sao?”');
INSERT INTO "vn_ruwwad" VALUES(7,111,'7:111','(Các quần thần của Pha-ra-ông) thưa: “Hãy tạm tha cho hắn và người anh em của hắn, hãy cho người cáo thị tập hợp các pháp sư trong các thành phố lại.”');
INSERT INTO "vn_ruwwad" VALUES(7,112,'7:112','“Tất cả những nhà phù thủy tài giỏi sẽ đến trình diện bệ hạ.”');
INSERT INTO "vn_ruwwad" VALUES(7,113,'7:113','(Thế là) các phù thủy đến trình diện Pha-ra-ông, tâu: “Nếu chúng thần thắng thì chúng thần có được ban thưởng không?”');
INSERT INTO "vn_ruwwad" VALUES(7,114,'7:114','(Pha-ra-ông) bảo: “Chắc chắn có, nhất định lúc đó các ngươi sẽ trở thành các cận thần của ta.”');
INSERT INTO "vn_ruwwad" VALUES(7,115,'7:115','Các nhà phù thủy nói: “Này Musa! Ngươi ném (bửu bối của Ngươi) xuống trước hay bọn ta sẽ là những người ném xuống trước”.');
INSERT INTO "vn_ruwwad" VALUES(7,116,'7:116','(Musa) bảo: “Các người hãy ném xuống trước!” Thế là những tên phù thủy ném bửu bối của mình xuống. (Với trò ảo thuật), họ đã làm hoa mắt và gây ra nỗi sợ hãi cho mọi người. Họ thực sự đã mang đến một trò ảo thuật vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(7,117,'7:117','Rồi TA đã mặc khải cho Musa: “Hãy ném cây gậy của Ngươi xuống!” (Musa ném cây gậy của Y xuống), ngay lập tức, nó nuốt chửng toàn bộ những gì mà (các nhà phù thủy) đang làm giả.');
INSERT INTO "vn_ruwwad" VALUES(7,118,'7:118','Sự thật đã được thiết lập, mọi thứ mà họ đã làm giả trở nên vô hiệu.');
INSERT INTO "vn_ruwwad" VALUES(7,119,'7:119','Vậy là chúng (Pha-ra-ông và quần thần của hắn) đã thua cuộc và bị xem thường.');
INSERT INTO "vn_ruwwad" VALUES(7,120,'7:120','Các nhà phù thủy đã quỳ xuống phủ phục (khi chứng kiến sự thật).');
INSERT INTO "vn_ruwwad" VALUES(7,121,'7:121','Họ nói: “Chúng tôi đã có đức tin nơi Thượng Đế của vũ trụ và vạn vật.”');
INSERT INTO "vn_ruwwad" VALUES(7,122,'7:122','“Thượng Đế của Musa và Harun.”');
INSERT INTO "vn_ruwwad" VALUES(7,123,'7:123','Pha-ra-ông (tức giận) quát: “Các ngươi dám có đức tin nơi hắn (Musa) trước khi ta cho phép các ngươi ư? Rõ ràng đây là âm mưu mà các ngươi đã thông đồng với nhau trong thành phố hầu trục xuất cư dân của nó đi nơi khác. Các ngươi sẽ sớm biết (hậu quả việc làm này của các ngươi).”');
INSERT INTO "vn_ruwwad" VALUES(7,124,'7:124','“Ta chắc chắn sẽ chặt tay này và chân kia của các ngươi, rồi ta chắc chắn sẽ đóng đinh toàn bộ các ngươi trên thập tự giá.”');
INSERT INTO "vn_ruwwad" VALUES(7,125,'7:125','(Các nhà phù thủy) trả lời (Pha-ra-ông): “Dù gì chúng tôi cũng sẽ trở về trình diện Thượng Đế của chúng tôi, (cho nên, chúng tôi không quan tâm việc ngài sẽ làm gì chúng tôi).”');
INSERT INTO "vn_ruwwad" VALUES(7,126,'7:126','“Ngài không oán giận chúng tôi ngoại trừ vì chúng tôi đã có đức tin nơi các dấu hiệu của Thượng Đế chúng tôi khi chúng đến với chúng tôi. Lạy Thượng Đế của bầy tôi, xin Ngài xối lên bầy tôi sự kiên nhẫn và xin hãy để bầy tôi chết như những tín đồ Muslim.”');
INSERT INTO "vn_ruwwad" VALUES(7,127,'7:127','Đám quần thần của Pha-ra-ông lên tiếng nói: “Lẽ nào bệ hạ để mặc Musa và đám dân của Y tự do làm loạn trong xứ và ruồng bỏ bệ hạ cũng như thần linh của bệ hạ?” (Pha-ra-ông) nói: “Ta sẽ giết con trai của chúng và tha mạng cho phụ nữ của chúng (để làm nô lệ cho ta), chắc chắn ta đây luôn có quyền lực bên trên bọn chúng.”');
INSERT INTO "vn_ruwwad" VALUES(7,128,'7:128','Musa nói với người dân của mình: “Mọi người hãy cầu cứu Allah và hãy kiên nhẫn. Quả thật, trái đất này là của Allah, Ngài muốn ban quyền thừa kế cho ai trong đám bầy tôi của Ngài là tùy ý Ngài; và kết cuộc (tốt đẹp) sẽ thuộc về những người ngoan đạo (sợ Allah).”');
INSERT INTO "vn_ruwwad" VALUES(7,129,'7:129','(Người dân thưa với Musa) nói: “Chúng tôi bị đàn áp trước và sau khi Người đến.” (Musa) nói: “Có lẽ, Thượng Đế của các người sẽ tiêu diệt kẻ thù của các người và cho các người nối nghiệp họ trên vùng đất này, và Ngài muốn xem các người sẽ hành động như thế nào (tri ân hay vong ơn Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(7,130,'7:130','Quả thật, TA đã trừng phạt đám thuộc hạ của Pha-ra-ông bằng những năm hạn hán và mất mùa, mong là qua đó họ biết lưu tâm (mà tỉnh ngộ).');
INSERT INTO "vn_ruwwad" VALUES(7,131,'7:131','(Đám thuộc hạ của Pha-ra-ông), khi gặp được điều tốt lành, chúng bảo: “Đây là phần mà chúng tôi đáng có”, còn khi gặp phải điều xấu, chúng lại đổ lỗi cho Musa và những ai theo Y đã mang đến điều xui xẻo. Quả thật, những điều xui xẻo mà chúng gặp phải đều do Allah an bài nhưng đa số bọn chúng không biết.');
INSERT INTO "vn_ruwwad" VALUES(7,132,'7:132','(Đám quần thần của Pha-ra-ông) nói với (Musa): “Cho dù ngươi có mang đến bất cứ phép lạ nào để làm mê hoặc bọn ta thì bọn ta cũng không có đức tin nơi Ngươi.”');
INSERT INTO "vn_ruwwad" VALUES(7,133,'7:133','Vì vậy, TA (Allah) đã gởi xuống bọn chúng lũ lụt, châu chấu, chấy (rận), cóc và máu (trừng phạt bọn chúng). (Tất cả đều là) các dấu hiệu rõ ràng, từng cái tiếp nối nhau (xảy đến với chúng), tuy nhiên, chúng vẫn kiêu căng, ngạo mạn và chúng là đám người tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(7,134,'7:134','Cứ mỗi khi đối diện với hình phạt thì chúng (liền hạ mình nài nỉ Musa) nói: “Này Musa, ngươi hãy khẩn cầu Thượng Đế của ngươi cho bọn ta qua những gì mà Ngài đã hứa với ngươi. Nếu ngươi giải được kiếp nạn này cho bọn ta, chắc chắn bọn ta sẽ có đức tin nơi ngươi và chắc chắn bọn ta sẽ để cho dân Israel ra đi theo ngươi.”');
INSERT INTO "vn_ruwwad" VALUES(7,135,'7:135','Nhưng sau khi TA (Allah) gỡ bỏ hình phạt khỏi chúng đến một thời hạn khác thì ngay lập tức chúng lật lọng (lời đã hứa).');
INSERT INTO "vn_ruwwad" VALUES(7,136,'7:136','Do đó, TA đã trừng phạt một số bọn chúng bằng cách nhấn chìm chúng trong biển (hồng hải) bởi chúng đã phủ nhận các dấu hiệu của TA và chúng là đám người thờ ơ (với chân lý).');
INSERT INTO "vn_ruwwad" VALUES(7,137,'7:137','Rồi TA cho đám người yếu thế thừa kế vùng đất (Sham) nơi mà từ đông sang tây đều được TA ban phúc. Lời tốt đẹp mà Thượng Đế của Ngươi (hỡi Thiên Sứ) đã hứa với dân Israel do bởi họ đã kiên nhẫn chịu đựng đã được hoàn thành. Và TA đã phá tan bao công trình mà Pha-ra-ông và đám thuộc hạ của hắn đã xây cất và gầy dựng.');
INSERT INTO "vn_ruwwad" VALUES(7,138,'7:138','TA đã cho dân Israel bình an băng qua biển (hồng hải). Sau đó, họ đi ngang qua một đám người đang thờ phượng các bục tượng (thần linh), họ bảo: “Này Musa, xin Người hãy tạo cho chúng tôi một thần linh (để chúng tôi có thần linh) giống như họ.” (Musa) đáp: “Các ngươi thật đúng là đám người ngu muội!”');
INSERT INTO "vn_ruwwad" VALUES(7,139,'7:139','Quả thật, (những kẻ thờ phượng các bục tượng) này sẽ bị hủy diệt về điều chúng đang làm, và mọi việc làm của chúng là thứ vô giá trị.');
INSERT INTO "vn_ruwwad" VALUES(7,140,'7:140','(Musa) nói với (người dân Israel): “Lẽ nào Ta lại chọn cho các ngươi một thần linh khác ngoài Allah trong khi Ngài đã ban hồng phúc cho các ngươi hơn thiên hạ?!”');
INSERT INTO "vn_ruwwad" VALUES(7,141,'7:141','(Các ngươi hãy nhớ lại) việc TA đã giải cứu các ngươi khỏi đám quân lính của Pha-ra-ông, họ đã đàn áp các ngươi bằng cực hình man rợ, họ đã tàn sát con trai của các ngươi và tha cho con gái của các ngươi. Trong sự việc đó là sự thử thách to lớn từ Thượng Đế của các ngươi dành cho các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(7,142,'7:142','TA (Allah) đã hẹn gặp Musa trong ba mươi đêm (tại núi Sinai) và TA đã gia hạn thêm mười đêm. Vì vậy, thời hạn (cho cuộc gặp gỡ) của Thượng Đế của Y được hoàn thành trong bốn mươi đêm. (Trước khi đi) Musa đã căn dặn Harun, người anh (em) của mình: “Hãy thay ta quản lý người dân của ta, hãy chấn chỉnh và cải thiện họ và chớ đừng đi theo những kẻ thối nát.”');
INSERT INTO "vn_ruwwad" VALUES(7,143,'7:143','Khi Musa đến điểm hẹn gặp TA và Thượng Đế của Y tiếp chuyện với Y. (Musa) đã khẩn cầu: “Lạy Thượng Đế của bề tôi, xin Ngài cho bề tôi được nhìn thấy Ngài.” (Allah) phán: “Ngươi không thể nào nhìn thấy được TA. Tuy nhiên, Ngươi hãy nhìn vào ngọn núi kia, nếu như nó vẫn còn nguyên vẹn tại vị trí của nó thì lúc đó Ngươi sẽ nhìn thấy được TA.” Nhưng khi Thượng Đế của Y hiện ra, ngọn núi liền vỡ tan (do ánh sáng của Ngài) còn Musa thì té xuống bất tỉnh. Sau khi tỉnh lại, (Musa) liền nói: “Quang Vinh và Tối Cao thay Ngài, bề tôi xin sám hối với Ngài và bề tôi là người đầu tiên có đức tin (nơi Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(7,144,'7:144','(Allah) phán: “Này Musa, quả thật, TA đã chọn Ngươi (và ban hồng phúc cho Ngươi) hơn ai khác trong nhân loại qua Bức Thông Điệp và Lời Phán của TA. Cho nên, Ngươi hãy nhận lấy điều mà TA đã ban cho Ngươi và Ngươi hãy là người biết tri ân.”');
INSERT INTO "vn_ruwwad" VALUES(7,145,'7:145','TA đã ghi cho (Musa) trên những tấm (gỗ, đá) những lời răn và chi tiết về mọi thứ. (TA phán bảo Musa): “Ngươi hãy nhận lấy (những lời răn này) bằng tất cả sức mạnh của Ngươi, và Ngươi hãy ra lệnh cho người dân của Ngươi tận dụng những lời răn tốt nhất, (rồi) TA sẽ cho Ngươi thấy ngôi nhà của những kẻ bất tuân.”');
INSERT INTO "vn_ruwwad" VALUES(7,146,'7:146','TA sẽ khiến cho những kẻ kiêu căng, ngạo mạn bất chấp lẽ phải trên trái đất quay lưng với các dấu hiệu của TA. Nếu chúng nhìn thấy bất kỳ dấu hiệu nào, chúng cũng không có đức tin; nếu chúng nhìn thấy con đường chân lý, chúng sẽ không lấy đó làm con đường (đúng đắn để đi); nhưng nếu chúng nhìn thấy con đường lệch lạc, chúng sẽ lấy đó làm con đường (như một lối đi tốt đẹp). Sở dĩ như thế là bởi vì chúng đã phủ nhận các dấu hiệu của TA và đã thường thờ ơ và tỏ ra lơ là.');
INSERT INTO "vn_ruwwad" VALUES(7,147,'7:147','Những kẻ đã phủ nhận các dấu hiệu của TA cũng như cuộc gặp gỡ (TA) ở Đời Sau thì mọi việc làm (tốt đẹp) của chúng đều không có giá trị. Chắc chắn chúng sẽ được đền đáp đúng với những gì chúng đã làm!');
INSERT INTO "vn_ruwwad" VALUES(7,148,'7:148','Và người dân của Musa, sau khi Y (rời đi trình diện Allah), đã làm ra từ đồ trang sức của họ một con bê có thân hình (rỗng bên trong) phát ra được âm thanh (như tiếng rống của bò). Lẽ nào họ không nhận thức được (con bê đó) không thể nói chuyện với họ và cũng không thể hướng dẫn họ đến con đường (chính đạo)?! Họ đã lấy (con bê đó làm thần linh của họ) và thế là họ đã trở thành những kẻ làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(7,149,'7:149','Khi họ hối hận về việc đã làm và thấy rằng mình đã đi lạc, họ nói: “Nếu như Thượng Đế của bầy tôi không thương xót bầy tôi và tha thứ cho bầy tôi thì chắc chắn bầy tôi sẽ nằm trong số những kẻ thua thiệt.”');
INSERT INTO "vn_ruwwad" VALUES(7,150,'7:150','Khi Musa trở về với người dân của mình, tức giận và đau buồn, nói: “Thật tồi tệ cho việc các người đã nghịch lại Ta sau khi Ta rời đi khỏi các người, sao các người lại nôn nóng về mệnh lệnh của Thượng Đế của các người chứ?” Rồi (Musa) ném các tấm (ghi các lời răn) xuống và nắm lấy đầu của người anh (em) của Y kéo lại gần mình. (Harun) nói: “Này con trai của mẹ ta, người dân quả thật đã đàn áp ta và suýt giết ta, cho nên, chớ đừng trách phạt ta làm kẻ thù vui mừng và chớ đừng liệt ta vào đám người sai trái kia.”');
INSERT INTO "vn_ruwwad" VALUES(7,151,'7:151','(Musa) cầu nguyện (Allah): “Lạy Thượng Đế của bề tôi, xin Ngài hãy tha thứ cho bề tôi và người anh (em) của bề tôi, xin Ngài hãy thương xót bầy tôi bởi Ngài là Đấng có lòng thương xót nhất.”');
INSERT INTO "vn_ruwwad" VALUES(7,152,'7:152','Những kẻ đã lấy con bê (làm thần linh) chắc chắn sẽ phải gánh chịu cơn thịnh nộ của Thượng Đế của chúng và chắc chắn sẽ bị hạ nhục trong cuộc sống trần gian này. TA trừng phạt những kẻ bịa đặt điều gian dối đúng như thế.');
INSERT INTO "vn_ruwwad" VALUES(7,153,'7:153','Riêng những ai đã làm điều tội lỗi rồi quay đầu sám hối sau đó và có đức tin (thì họ sẽ thấy) Thượng Đế của Ngươi (hỡi Thiên Sứ) là Đấng Hằng Tha Thứ, Đấng Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(7,154,'7:154','Rồi khi cơn giận đã nguôi ngoai, Musa nhặt lại các tấm (ghi các lời răn), và trong các tấm (ghi các lời răn) đó có sự hướng dẫn và hồng phúc cho những người kính sợ Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(7,155,'7:155','Musa đã lựa chọn bảy mươi người trong số người dân của Y đưa chúng đến chỗ hẹn của TA.( Rồi khi trận động đất bắt phạt chúng. (Musa) cầu nguyện: “Lạy Thượng Đế của bề tôi, nếu muốn Ngài có thể đã tiêu diệt họ và cả bề tôi trước khi đến đây. Lẽ nào Ngài lại tiêu diệt bầy tôi bởi hành động của những kẻ ngu ngốc trong bầy tôi? Chẳng qua đó chỉ là sự thử thách mà Ngài đã dùng nó để làm lạc đường kẻ nào Ngài muốn và hướng dẫn ai đi đúng đường Ngài muốn. Ngài là Đấng Bảo Hộ của bầy tôi, xin Ngài hãy tha thứ cho bầy tôi và thương xót bầy tôi, bởi Ngài là Đấng Tha Thứ tốt nhất!”');
INSERT INTO "vn_ruwwad" VALUES(7,156,'7:156','“Xin Ngài an bài cho bầy tôi những điều tốt đẹp ở trần gian và ở Đời Sau, quả thật bầy tôi nguyện quay về sám hối với Ngài.” (Allah) phán: “Hình phạt của TA, (TA dùng nó) để trừng trị kẻ nào TA muốn, tuy nhiên, lòng thương xót của TA bao trùm tất cả mọi thứ. Vì vậy, TA sẽ an bài những điều tốt đẹp cho những người ngoan đạo và xuất Zakah và những người có đức tin nơi các Lời Mặc Khải của TA.”');
INSERT INTO "vn_ruwwad" VALUES(7,157,'7:157','Những người mà họ đi theo vị Sứ Giả (Muhammad), vị Nabi mù chữ, vị mà họ tìm thấy Y được nhắc đến trong Tawrah (Cựu Ước) và Injil (Tân Ước) của họ. Y ra lệnh cho họ làm điều thiện và ngăn cấm họ làm điều xấu, Y cho phép họ dùng những thứ tốt lành và cấm họ dùng những thứ ô uế, Y trút bỏ gánh nặng và xiềng xích đang đè nặng lên họ. Vì vậy, những ai có đức tin nơi Y, ủng hộ Y, giúp đỡ Y và đi theo theo Ánh Sáng đã được ban xuống cho Y là những người thành công.');
INSERT INTO "vn_ruwwad" VALUES(7,158,'7:158','(Hỡi Thiên Sứ!) Ngươi hãy nói: “Hỡi nhân loại! Ta đích thực là Sứ Giả của Allah được cử phái đến cho tất cả các ngươi. Ngài là Đấng thống trị các tầng trời và trái đất, không có Thượng Đế đích thực nào khác ngoài Ngài, Ngài là Đấng làm cho sống và làm cho chết. Vì vậy, các ngươi (hỡi nhân loại) hãy có đức tin nơi Allah và nơi Sứ Giả của Ngài, một vị Nabi mù chữ có đức tin nơi Ngài và nơi các lời Mặc Khải của Ngài. Các ngươi hãy đi theo Y để may ra các ngươi được hướng dẫn (đúng đường).”');
INSERT INTO "vn_ruwwad" VALUES(7,159,'7:159','Trong đám dân của Musa (Israel) có một cộng đồng hướng dẫn đúng sự thật và phân xét công bằng dựa trên sự thật.');
INSERT INTO "vn_ruwwad" VALUES(7,160,'7:160','TA đã chia (dân Israel) thành mười hai bộ lạc. TA đã mặc khải cho Musa khi Y cầu xin ban nước cho người dân của mình bằng lời phán: “Ngươi hãy đánh cây gậy của mình lên tảng đá!” (Sau khi Musa đánh gậy lên tảng đá), nó liền phun ra mười hai ngọn suối, mỗi bộ lạc đều biết nguồn nước uống của mình. TA đã dùng mây che mát cho họ và ban xuống cho họ Mann (loại thức uống giống như mật ong) và chim cút (với lời phán): “Các ngươi hãy ăn thực phẩm tốt lành mà TA đã ban cấp cho các ngươi!” (Nhưng họ không biết hài lòng và tri ân), và họ không gây thiệt hại cho TA (về điều đó) mà ngược lại họ chỉ gây thiệt hại cho chính bản thân mình.');
INSERT INTO "vn_ruwwad" VALUES(7,161,'7:161','(Hỡi Thiên Sứ, Ngươi hãy nhớ lại khi Allah phán bảo dân Israel): “Các ngươi hãy vào thị trấn (Jerusalem) này, các ngươi hãy tự do ăn (thực phẩm) bất cứ nơi nào của thị trấn tùy thích và các ngươi hãy cầu nguyện (TA): ‘Xin Ngài tha thứ tội lỗi cho bầy tôi’ và các ngươi hãy vào cổng trong sự phủ phục, TA sẽ tha thứ tội lỗi cho các ngươi, và TA sẽ ban thêm (phần thưởng) cho những người làm tốt.”');
INSERT INTO "vn_ruwwad" VALUES(7,162,'7:162','Tuy nhiên, những kẻ làm điều sai quấy đã thay đổi lời phán đã được truyền cho họ, vì vậy, TA đã giáng tai họa từ trời xuống trừng phạt chúng bởi những điều sai quấy mà chúng đã làm.');
INSERT INTO "vn_ruwwad" VALUES(7,163,'7:163','(Ngươi - hỡi Thiên Sứ) hãy hỏi (dân Do Thái) về một thị trấn nằm ven biển đã vi phạm (giới cấm) của ngày thứ bảy - khi cá của họ đến với họ một cách công khai vào ngày thứ bảy của họ, còn vào ngày ngoài ngày thứ bảy thì chúng không đến với họ. Như thế đó, TA (Allah) đã thử thách họ bởi những điều mà họ đã bất tuân và dấy loạn.(');
INSERT INTO "vn_ruwwad" VALUES(7,164,'7:164','(Ngươi – hỡi Thiên Sứ hãy nhớ lại khi một nhóm trong số họ khuyên can nhóm người vi phạm giới cấm vào ngày thứ bảy) thì một nhóm (khác) trong số họ nói: “Tại sao các người lại đi khuyên can một đám người mà Allah muốn hủy diệt hoặc muốn trừng phạt họ bằng hình phạt khủng khiếp?” (Nhóm người đó) bảo: “Chúng tôi muốn có lý do trước Thượng Đế của các người (rằng chúng tôi đã hết trách nhiệm) và hy vọng họ biết kính sợ (Allah)!”');
INSERT INTO "vn_ruwwad" VALUES(7,165,'7:165','Do đó, khi họ (đám người vi phạm giới cấm của ngày thứ bảy) quên đi mọi lời nhắc nhở, TA cứu vớt những người ngăn cản (người khác) làm điều sai trái và bắt phạt những kẻ làm điều sai quấy bằng hình phạt khủng khiếp bởi những gì mà họ đã bất tuân.');
INSERT INTO "vn_ruwwad" VALUES(7,166,'7:166','Cho nên, khi bọn họ công khai làm điều cấm thì TA phán bảo họ: “Đám người các ngươi hãy biến thành loài khỉ hèn hạ!”');
INSERT INTO "vn_ruwwad" VALUES(7,167,'7:167','(Ngươi - hỡi Thiên Sứ hãy nhớ lại) khi Thượng Đế của Ngươi tuyên bố rằng Ngài chắc chắn sẽ phục sinh bọn họ vào Ngày Tận Thế, kẻ tội lỗi trong số họ sẽ bị trừng phạt nghiêm khắc. Quả thật, Thượng Đế của Ngươi rất nhanh chóng trong việc trừng phạt, song Ngài là Đấng Tha Thứ, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(7,168,'7:168','TA đã chia cắt họ (Do Thái) thành các cộng đồng sống rải rác trên trái đất. Trong số họ, có những người lương thiện và cũng có những kẻ không như thế. TA đã thử thách họ cả điều tốt lẫn điều xấu hi vọng họ biết quay về (với chân lý).');
INSERT INTO "vn_ruwwad" VALUES(7,169,'7:169','Rồi sau họ, có một thế hệ thừa hưởng Kinh Sách (Tawrah). Tuy nhiên, thế hệ này nhận lấy món lợi (của trần gian cho việc bóp méo Kinh Sách) và chúng bảo: “Rồi chúng ta sẽ được tha thứ thôi.” (Cứ như thế), nếu có món lợi tương tự đến với chúng (một lần nữa) thì chúng cũng sẽ liền nhận nó (giống như thế). Lẽ nào giao ước trong Kinh Sách không ràng buộc họ rằng họ chỉ được phép nói sự thật về Allah và lẽ nào họ đã không đọc những điều răn trong đó?! Quả thật, ngôi nhà ở cõi Đời Sau tốt đẹp hơn cho những người ngoan đạo, lẽ nào các ngươi không hiểu được (điều đó)?!');
INSERT INTO "vn_ruwwad" VALUES(7,170,'7:170','Những ai nắm chặt Kinh Sách (Tawrah) và thiết lập lễ nguyện Salah (một cách chu đáo) thì chắc chắn TA (Allah) không làm mất phần thưởng của những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(7,171,'7:171','Và (Ngươi - hỡi Thiên Sứ, hãy nhớ lại) khi TA nhấc ngọn núi lên bên trên (dân Israel) tựa như nó là tán che (trên đầu của họ) và họ tưởng nó sẽ rơi xuống đè họ. (TA) phán bảo: “Các ngươi hãy nắm chặt những gì TA đã ban cho các ngươi và các ngươi hãy ghi nhớ các điều răn dạy trong đó, mong rằng các ngươi sẽ ngoan đạo.”');
INSERT INTO "vn_ruwwad" VALUES(7,172,'7:172','(Ngươi - Thiên Sứ Muhammad – hãy nhớ lại) khi Thượng Đế của Ngươi bắt con cháu của Adam, hậu duệ của họ được tạo ra từ các xương sống của họ, xác nhận đối với chính bản thân họ (trước câu hỏi): “Có phải TA là Thượng Đế của các ngươi?” Họ đáp: “Bẩm, đúng vậy (Ngài chính là Thượng Đế của bầy tôi). Bầy tôi xin xác nhận.” (Việc TA hỏi các ngươi như thế là bởi vì e rằng) vào Ngày Phán Xét Cuối Cùng, các ngươi sẽ nói: “Bầy tôi thực sự đã không biết điều này.”');
INSERT INTO "vn_ruwwad" VALUES(7,173,'7:173','Hoặc các ngươi sẽ nói: “Quả thật chỉ tại trước đó cha mẹ của bầy tôi thờ phượng nhiều thần linh còn bầy tôi chỉ là hậu duệ đi theo sau họ mà thôi. Lẽ nào Ngài sẽ hủy diệt bầy tôi bởi những điều do những kẻ ngụy tạo đã làm?!”');
INSERT INTO "vn_ruwwad" VALUES(7,174,'7:174','TA (Allah) đã trình bày rõ các Lời Mặc Khải như thế đó, mong rằng chúng sẽ quay về (với chính đạo).');
INSERT INTO "vn_ruwwad" VALUES(7,175,'7:175','Ngươi (hỡi Thiên Sứ) hãy xướng đọc cho họ nghe chuyện về một người đàn ông( mà TA đã ban cho y (kiến thức về) một vài Dấu Hiệu của TA nhưng y đã gạt bỏ chúng nên Shaytan đã bám theo y, thế là y trở thành kẻ lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(7,176,'7:176','Nếu TA muốn, TA có thể nâng y lên (với kiến thức về các Dấu Hiệu của TA mà TA đã ban cho y). Tuy nhiên, y cứ muốn bấu víu mãi (cuộc sống trên) trái đất và theo đuổi dục vọng của bản thân. Hình ảnh của y giống như hình ảnh của con chó: dù Ngươi đánh đuổi nó hoặc không đánh đuổi nó thì nó vẫn cứ lè lưỡi ra. Đó là hình ảnh thí dụ về đám người phủ nhận các dấu hiệu của TA. Ngươi (hỡi Thiên Sứ) hãy kể lại những câu chuyện đó mong rằng họ sẽ suy ngẫm (về bản thân của họ).');
INSERT INTO "vn_ruwwad" VALUES(7,177,'7:177','Thật tồi tệ cho hình ảnh của đám người đã phủ nhận các dấu hiệu của TA và chính họ đã làm hại bản thân mình.');
INSERT INTO "vn_ruwwad" VALUES(7,178,'7:178','Ai mà Allah hướng dẫn thì sẽ đi đúng đường (đến chân lý) còn ai mà Ngài làm cho lầm lạc thì đó là những kẻ thất bại.');
INSERT INTO "vn_ruwwad" VALUES(7,179,'7:179','Quả thật, TA (Allah) đã tạo cho Hỏa Ngục đa số loài Jinn và con người. Họ có trái tim nhưng không hiểu, họ có đôi mắt nhưng không nhìn thấy, họ có đôi tai nhưng không nghe được. Họ giống như loài súc vật, thậm chí còn tệ hơn nữa là đằng khác. Họ là những kẻ thờ ơ và vô tâm (với Đời Sau).');
INSERT INTO "vn_ruwwad" VALUES(7,180,'7:180','Allah có các tên gọi tốt đẹp nhất, bởi thế, các ngươi hãy cầu nguyện Ngài với những tên gọi đó. Các ngươi hãy mặc kệ những kẻ bóp méo các tên gọi của Ngài, rồi đây chúng sẽ nhận lấy hậu quả cho những điều chúng làm.');
INSERT INTO "vn_ruwwad" VALUES(7,181,'7:181','Trong số những người TA đã tạo, có một nhóm người hướng dẫn (người khác) bằng chân lý và dựa vào đó mà đối xử công bằng (với mọi người).');
INSERT INTO "vn_ruwwad" VALUES(7,182,'7:182','Những kẻ phủ nhận các dấu hiệu của TA, TA sẽ cuốn chúng vào con đường hủy diệt nhưng chúng lại không biết.');
INSERT INTO "vn_ruwwad" VALUES(7,183,'7:183','TA tạm tha cho chúng, quả thật kế hoạch của TA rất chắc chắn.');
INSERT INTO "vn_ruwwad" VALUES(7,184,'7:184','Lẽ nào chúng vẫn không chịu suy ngẫm?! Người bạn của chúng (Muhammad) không phải là một gã điên mà đích thực là một vị cảnh báo công khai.');
INSERT INTO "vn_ruwwad" VALUES(7,185,'7:185','Lẽ nào chúng không quan sát thấy sự thống trị trời đất và mọi thứ mà Allah đã tạo ra? Có thể tuổi thọ của họ đã sắp kết thúc. Thế còn lời nói nào ngoài (Qur’an) mà họ sẽ tin đây!');
INSERT INTO "vn_ruwwad" VALUES(7,186,'7:186','Ai mà Allah làm cho lầm lạc sẽ không được ai hướng dẫn và Ngài sẽ bỏ mặc họ chơ vơ, vất vưởng trong sự thái quá của họ.');
INSERT INTO "vn_ruwwad" VALUES(7,187,'7:187','(Những kẻ phủ nhận Đời Sau) hỏi Ngươi (hỡi Thiên Sứ) về Ngày Tận Thế bao giờ xảy đến. Ngươi hãy nói: “Chỉ Thượng Đế của Ta mới biết rõ, không ai có thể tiết lộ thời khắc đó ngoại trừ một mình Ngài.” (Thời khắc đó) sẽ rất nặng nề đối với (vạn vật) trong các tầng trời và trái đất. Nó sẽ đến với các ngươi một cách bất ngờ. Chúng hỏi Ngươi như thể Ngươi rất đỗi quen thuộc với thời khắc đó lắm vậy. Ngươi hãy nói với chúng: “Chỉ Thượng Đế của Ta mới biết rõ, tuy nhiên, đa số nhân loại lại không biết (điều đó).”');
INSERT INTO "vn_ruwwad" VALUES(7,188,'7:188','Ngươi (hỡi Thiên Sứ) hãy nói: “Ta không có quyền năng chi phối điều lợi hoặc điều hại cho bản thân mình ngoại trừ những gì Allah muốn. Nếu Ta thực sự biết điều vô hình thì Ta đã thu gom cho bản thân mình nhiều điều tốt đẹp và đã không có bất kỳ điều xấu nào chạm đến Ta. Quả thật, Ta chỉ là một người cảnh báo (về Hỏa Ngục) và một người báo tin mừng (về Thiên Đàng) cho những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(7,189,'7:189','“Ngài là Đấng đã tạo ra các ngươi (hỡi nhân loại) từ một cá thể duy nhất (Adam), rồi từ (xương sườn của) Y Ngài tạo ra cho Y người vợ (Hauwa) để Y sống yên bình cùng với vợ của mình. Thế là (theo quy luật) khi người chồng ôm phủ lấy người vợ của mình thì người vợ mang thai, nhưng (trong thời gian đầu người vợ chưa cảm nhận được vì) cái thai còn nhẹ). Đến khi cái thai trở nên nặng nề thì cả hai vợ chồng (thường) cầu nguyện Allah, Thượng Đế của hai người: “Lạy Thượng Đế của bầy tôi, nếu Ngài ban cho bầy tôi một đứa con tốt lành (về mọi mặt) thì bầy tôi chắc chắn vô cùng biết ơn (Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(7,190,'7:190','Tuy nhiên, khi Ngài ban cho hai vợ chồng họ đứa con tốt lành (như họ cầu mong) thì họ lại dựng lên những thần linh ngang vai với Ngài( về đứa con mà Ngài đã ban cho vợ chồng họ. Nhưng Allah luôn Tối Thượng vượt bên trên mọi thứ mà họ qui cho Ngài.');
INSERT INTO "vn_ruwwad" VALUES(7,191,'7:191','Lẽ nào chúng (những kẻ tổ hợp thần linh cùng với Allah) tổ hợp cùng với Ngài những thứ không tạo ra được bất cứ vật gì trong khi những thứ đó lại là những tạo vật được (Allah) tạo ra?!');
INSERT INTO "vn_ruwwad" VALUES(7,192,'7:192','(Những thứ mà chúng tổ hợp cùng với Allah) không thể giúp đỡ chúng cũng không thể tự giúp mình.');
INSERT INTO "vn_ruwwad" VALUES(7,193,'7:193','Nếu các ngươi (những kẻ thờ phượng các thần linh ngoài Allah) có gọi chúng đến với nguồn chỉ đạo thì chúng cũng không theo các ngươi, dù các ngươi có gọi chúng hay các ngươi im lặng thì cũng bằng thừa mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(7,194,'7:194','Thật ra những thứ mà các ngươi cầu nguyện ngoài Allah cũng chỉ là những bề tôi (của Allah) giống như các ngươi mà thôi. Do đó, các ngươi cứ hãy cầu nguyện đến chúng xem chúng có đáp lại lời nguyện cầu của các ngươi không nếu các ngươi nói thật!');
INSERT INTO "vn_ruwwad" VALUES(7,195,'7:195','Lẽ nào (những thứ được thờ phượng ngoài Allah) có chân biết đi, có tay biết cầm nắm, có mắt nhìn thấy, hoặc có tai có thể nghe được? Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ đa thần): “Các người cứ hãy cầu nguyện những thứ mà các người đã tổ hợp (cùng với Allah), rồi các người hãy cùng nhau lên kế hoạch hãm hại Ta xem nào, đừng để Ta có thời gian nghĩ ngơi gì cả!”');
INSERT INTO "vn_ruwwad" VALUES(7,196,'7:196','“Quả thật, Đấng Bảo Hộ của Ta chính là Allah, Đấng đã ban xuống Kinh (Qur’an cho Ta) và Ngài bảo hộ những người đức hạnh và lương thiện.”');
INSERT INTO "vn_ruwwad" VALUES(7,197,'7:197','“Còn những thứ mà các ngươi cầu nguyện ngoài Ngài không có khả năng giúp đỡ các ngươi và cũng không thể tự giúp bản thân chúng.”');
INSERT INTO "vn_ruwwad" VALUES(7,198,'7:198','Dẫu các ngươi (hỡi những kẻ thờ cúng bục tượng) có kêu gọi chúng (các thần linh của các ngươi) đến với nguồn chỉ đạo thì chúng cũng không nghe. Ngươi (hỡi Thiên Sứ) sẽ thấy (các thần linh của những kẻ đa thần đó) hướng mắt về Ngươi nhưng thật ra chúng không nhìn thấy gì cả.');
INSERT INTO "vn_ruwwad" VALUES(7,199,'7:199','Ngươi (hỡi Thiên Sứ) hãy lượng thứ, hãy bảo ban điều đúng và hãy tránh xa những kẻ ngu dốt.');
INSERT INTO "vn_ruwwad" VALUES(7,200,'7:200','Nếu như có sự quấy nhiễu từ Shaytan (vào tâm trí Ngươi) thì Ngươi hãy cầu xin Allah phù hộ che chở, quả thật, Ngài là Đấng Hằng Nghe, Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(7,201,'7:201','Quả thât, những người ngoan đạo (sợ Allah) khi có nhóm Shaytan nào đó đến quấy nhiễu, họ liền nhớ (đến Allah) thì họ sẽ thấy (ánh sáng chỉ đạo trở lại).');
INSERT INTO "vn_ruwwad" VALUES(7,202,'7:202','Những anh em của (Shaytan) không ngừng tìm cách lôi kéo họ đi lạc, và chúng sẽ không bỏ ngang (ý đồ đó).');
INSERT INTO "vn_ruwwad" VALUES(7,203,'7:203','Khi Ngươi (hỡi Thiên Sứ) không mang đến cho họ (những kẻ đa thần) một Dấu lạ thì họ bảo: “Sao ngươi không tự bịa ra một dấu lạ nào đó đi?!” Ngươi hãy nói: “Quả thật, Ta chỉ làm theo điều đã được Thượng Đế của Ta mặc khải cho Ta. Đây (Qur’an) là bằng chứng rõ ràng từ Thượng Đế của các người. Nó là nguồn chỉ đạo và là hồng ân cho đám người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(7,204,'7:204','Khi Qur’an được xướng đọc, các ngươi hãy lắng nghe và hãy giữ yên lặng, mong rằng các ngươi được (Allah) thương xót.');
INSERT INTO "vn_ruwwad" VALUES(7,205,'7:205','Ngươi (hỡi Thiên Sứ) hãy nhớ đến Thượng Đế của Ngươi trong lòng bằng sự hạ mình (và bằng lời tụng niệm) nhỏ khẽ, không to tiếng, vào sáng chiều, và Ngươi chớ đừng như những kẻ lơ là, thờ ơ (không nhớ tới Allah)!');
INSERT INTO "vn_ruwwad" VALUES(7,206,'7:206','Quả thật (các Thiên Thần) ở nơi Thượng Đế của Ngươi (hỡi Thiên Sứ) không hề tự cao tự đại trước việc thờ phượng Ngài; Họ luôn tán dương và quỳ lạy Ngài.');
INSERT INTO "vn_ruwwad" VALUES(8,1,'8:1','Họ (các vị Sahabah) hỏi Ngươi (hỡi Thiên Sứ) về chiến lợi phẩm. Ngươi hãy bảo họ: “Chiến lợi phẩm thuộc về Allah và Thiên Sứ (của Ngài). Vì vậy, các ngươi (những người có đức tin) hãy kính sợ Allah và hãy cư xử hài hòa giữa các ngươi; và các ngươi hãy tuân lệnh Allah và Thiên Sứ của Ngài nếu các ngươi thực sự là những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(8,2,'8:2','Quả thật, những người có đức tin là những người mà khi nghe nhắc đến Allah thì con tim của họ sợ hãi, còn khi nghe xướng đọc các Lời Mặc Khải của Ngài thì họ tăng thêm đức tin và họ hoàn toàn phó thác cho Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(8,3,'8:3','(Những người có đức tin) là những người chu đáo duy trì lễ nguyện Salah và chi dùng bổng lộc mà TA ban cấp (cho con đường chính nghĩa của TA).');
INSERT INTO "vn_ruwwad" VALUES(8,4,'8:4','Những người đó mới là những người có đức tin đích thực. (Rồi đây) họ sẽ có được những cấp bậc vinh dự ở nơi Thượng Đế của họ, cùng với sự tha thứ và bổng lộc dồi dào (từ nơi Ngài).');
INSERT INTO "vn_ruwwad" VALUES(8,5,'8:5','Tương tự (như mệnh lệnh chia chiến lợi phẩm), việc Thượng Đế của Ngươi (hỡi Thiên Sứ) đã ra lệnh cho Ngươi rời khỏi nhà (đi chinh chiến với kẻ thù) là điều đúng đắn. Tuy nhiên, có một nhóm tín đồ có đức tin lại ghét (việc cùng Ngươi đi chinh chiến).');
INSERT INTO "vn_ruwwad" VALUES(8,6,'8:6','Họ tranh luận với Ngươi về sự đúng đắn (của việc đi chinh chiến) sau khi nó đã được trình bày rõ ràng (cho họ). (Họ lo sợ) như thể họ đang bị dẫn đến cái chết và họ đang nhìn thấy (cảnh chết trước mặt họ).');
INSERT INTO "vn_ruwwad" VALUES(8,7,'8:7','(Các ngươi - những người có đức tin hãy nhớ lại) việc Allah đã hứa với các ngươi (về sự thắng lợi) cho dù các ngươi đối đầu với đoàn nào trong hai đoàn (của địch). (Tuy nhiên), trong thâm tâm, các ngươi mong được chạm trán với (đoàn thương buôn) không trang bị vũ khí (vì các ngươi lo lắng cho kết cuộc xấu) còn Allah thì lại muốn khẳng định sự thật bằng Lời Phán của Ngài đồng thời Ngài muốn chặt đứt gốc rễ của những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(8,8,'8:8','Mục đích để Ngài chứng minh (với các ngươi) rằng chân lý vẫn là chân lý và sự ngụy tạo vẫn là sự ngụy tạo, dẫu cho những kẻ tội lỗi căm ghét (điều đó).');
INSERT INTO "vn_ruwwad" VALUES(8,9,'8:9','(Các ngươi - những người có đức tin, hãy nhớ lại lúc ở Badr) khi các ngươi cầu xin Thượng Đế của các ngươi (cứu giúp). Ngài đã đáp lại lời cầu nguyện của các ngươi (với lời phán): “Chắc chắn TA sẽ trợ giúp các ngươi với một ngàn Thiên Thần xếp thành hàng (phía sau các ngươi).”');
INSERT INTO "vn_ruwwad" VALUES(8,10,'8:10','Quả thật, Allah làm điều đó chẳng qua chỉ để (tinh thần của) các ngươi phấn khởi và làm cho các ngươi an lòng, chứ mọi sự thắng lợi đều do Allah cả, Ngài là Đấng Quyền Năng, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(8,11,'8:11','(Các ngươi - những người có đức tin, hãy nhớ lại lúc ở Badr) khi Ngài (Allah) làm cho các ngươi ngủ thiếp đi một giấc để trấn an (nỗi sợ hãi của các ngươi) và Ngài cho mưa từ trời xuống để thanh lọc các ngươi, để tẩy sạch vết bẩn của Shaytan, để củng cố tấm lòng của các ngươi thêm kiên cường và để giữ vững bàn chân của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(8,12,'8:12','(Ngươi - hỡi Thiên Sứ, hãy nhớ lại) khi Thượng Đế của Ngươi đã mặc khải cho các Thiên Thần: “Quả thật, TA luôn bên cạnh các ngươi, vì vậy, các ngươi hãy làm vững lòng những người có đức tin. TA sẽ gieo nỗi sợ hãi vào lòng của những kẻ vô đức tin. Do đó, các ngươi hãy đánh vào cổ và hãy đánh từng ngón tay của chúng.”');
INSERT INTO "vn_ruwwad" VALUES(8,13,'8:13','Sở dĩ chúng bị hậu quả như thế là do chúng đã chống đối Allah và Thiên Sứ của Ngài. Và kẻ nào dám chống đối Allah và Thiên Sứ của Ngài (thì hãy biết rằng) quả thật Allah rất nghiêm khắc trong việc trừng trị.');
INSERT INTO "vn_ruwwad" VALUES(8,14,'8:14','Đó là (hình phạt) dành cho các ngươi (hỡi những kẻ chống đối Allah và Thiên Sứ của Ngài). Bởi thế, các ngươi hãy nếm lấy nó và (các ngươi hãy nên biết) rằng những kẻ vô đức tin chắc chắn sẽ phải bị trừng phạt nơi Hỏa Ngục (vào Đời Sau).');
INSERT INTO "vn_ruwwad" VALUES(8,15,'8:15','Hỡi những người có đức tin, khi các ngươi chạm trán với kẻ thù nơi sa trường thì các ngươi chớ quay lưng bỏ chạy.');
INSERT INTO "vn_ruwwad" VALUES(8,16,'8:16','Ai quay lưng bỏ chạy vào ngày (chạm trán) đó, trừ phi chỉ để dụ địch vào bẫy hoặc chỉ để rút lui về nhập với đoàn quân của mình, bằng không, chắc chắn y sẽ phải hứng chịu cơn thịnh nộ của Allah và nơi ở của y chắc chắn sẽ là Hỏa Ngục, một nơi ở vô cùng tồi tệ.');
INSERT INTO "vn_ruwwad" VALUES(8,17,'8:17','(Thật ra) các ngươi (hỡi những người có đức tin) không giết chúng (những kẻ đa thần tại Badr) mà chính Allah đã giết chúng, và không phải Ngươi (hỡi Thiên Sứ) đã ném khi Ngươi ném (một nắm đất tại Badr) mà chính Allah đã ném (làm cho họ mờ mắt). (Việc Ngài để các ngươi với số lượng quân binh ít chạm trán với địch có số lượng quân binh nhiều và hùng mạnh hơn) là để Ngài thử thách những người có đức tin bằng một sự thử thách tốt đẹp từ nơi Ngài. Quả thật, Allah hằng nghe, hằng biết.');
INSERT INTO "vn_ruwwad" VALUES(8,18,'8:18','(Sứ thắng lợi) đó của các ngươi là do Allah đã làm suy giảm kế hoạch của những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(8,19,'8:19','Nếu các ngươi (những kẻ đa thần) yêu cầu sự phân định rạch ròi thì nó đã xảy ra với các ngươi (tại Badr). Tuy nhiên, nếu các ngươi dừng lại thì sẽ tốt hơn cho các ngươi, còn nếu các ngươi tái diễn (việc tấn công) thì TA (Allah) cũng sẽ tái diễn (việc phù hộ họ chiến thắng và các ngươi vẫn tiếp tục thất bại) và lực lượng của các ngươi không giúp ích được gì cho các ngươi dẫu các ngươi có đông bao nhiêu đi chăng nữa, bởi quả thật Allah luôn sát cánh với những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(8,20,'8:20','Hỡi những người có đức tin, các ngươi hãy tuân lệnh Allah và Thiên Sứ của Ngài, các ngươi chớ đừng quay lưng với Y trong khi các ngươi vẫn đang nghe (các câu Kinh Qur’an được đọc cho các ngươi).');
INSERT INTO "vn_ruwwad" VALUES(8,21,'8:21','Các ngươi chớ đừng hành xử như những kẻ chỉ biết nói: “Chúng tôi đã nghe” nhưng thật ra họ không nghe.');
INSERT INTO "vn_ruwwad" VALUES(8,22,'8:22','Quả thật, loài vật xấu xa nhất đối với Allah là kẻ điếc, câm không hiểu gì.');
INSERT INTO "vn_ruwwad" VALUES(8,23,'8:23','Nếu Allah biết ở họ thực sự có điều tốt thì chắc chắn Ngài đã cho họ nghe (nhưng họ chẳng có điều tốt đẹp nào nên) cho dù Ngài có cho họ nghe (chân lý) thì họ vẫn ngoảnh đi.');
INSERT INTO "vn_ruwwad" VALUES(8,24,'8:24','Hỡi những người có đức tin! Các ngươi hãy đáp lại lời mời gọi của Allah và Thiên Sứ (của Ngài) khi Y mời gọi các ngươi đến với điều giúp các ngươi sống (tốt lành). Các ngươi hãy biết rằng Allah can thiệp giữa một người và trái tim của y và rồi đây các ngươi sẽ phải quay về trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(8,25,'8:25','Các ngươi (hỡi những người có đức tin) hãy coi chừng tai họa (của Allah giáng xuống) bởi lẽ không chỉ riêng những kẻ tội lỗi trong các ngươi sẽ phải hứng chịu (mà ngay cả các ngươi cũng phải vạ lây). Các ngươi hãy biết rằng Allah rất nghiêm khắc trong việc trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(8,26,'8:26','(Hỡi những người có đức tin), các ngươi hãy nhớ lại lúc các ngươi còn ở vùng đất (Makkah) chỉ là một nhóm thiểu số yếu thế luôn lo sợ bị vây bắt thủ tiêu. Thế rồi (Allah) đã ban cho các ngươi một nơi lánh nạn, tăng cường sức mạnh cho các ngươi qua sự trợ giúp của Ngài và ban cho các ngươi bổng lộc tốt sạch, mong rằng các ngươi biết tạ ơn (Ngài).');
INSERT INTO "vn_ruwwad" VALUES(8,27,'8:27','Hỡi những người có đức tin! Các ngươi đừng lừa dối Allah và Sứ Giả (Muhammad), và các ngươi chớ gian lận những tín vật được ủy thác cho các ngươi trong lúc các ngươi biết rõ (đó là lừa dối và bất tín).');
INSERT INTO "vn_ruwwad" VALUES(8,28,'8:28','Các ngươi hãy biết rằng tài sản và con cái của các ngươi thật ra chỉ là một sự thử thách, nhưng ở nơi Allah chắc chắn sẽ có một phần thưởng vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(8,29,'8:29','Hỡi những người có đức tin, nếu các ngươi sợ Allah, Ngài sẽ phú cho các ngươi tiêu chuẩn phân biệt (lẽ phải) đồng thời sẽ xóa tội lỗi của các ngươi và tha thứ cho các ngươi. Quả thật, Allah có vô vàn thiên ân vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(8,30,'8:30','(Ngươi - hỡi Thiên Sứ Muhammad, hãy nhớ lại) khi những kẻ vô đức tin mưu định giam cầm Ngươi hoặc sát hại Ngươi hoặc trục xuất Ngươi ra khỏi xứ. Chúng hoạch định mưu kế và Allah cũng hoạch định (mưu kế của Ngài), tuy nhiên, Allah là Đấng hoạch định ưu việt.');
INSERT INTO "vn_ruwwad" VALUES(8,31,'8:31','(Những kẻ đa thần), khi các Lời Mặc khải của TA được xướng đọc cho chúng thì chúng bảo: “Thật ra chúng tôi đã từng nghe (những điều này). Nếu chúng tôi muốn, chúng tôi có thể sáng tác những lời tương tự. Đây chẳng qua chỉ là những câu chuyện cổ tích thời xưa.”');
INSERT INTO "vn_ruwwad" VALUES(8,32,'8:32','(Ngươi - hỡi Thiên Sứ Muhammad, hãy nhớ lại) khi chúng (những kẻ đa thần) cầu nguyện (mang tính thách thức): “Lạy Allah, nếu như điều (mà Muhammad mang đến) là sự thật thì xin Ngài hãy cho trận mưa đá từ trên trời trút xuống đè chúng tôi hoặc xin Ngài hãy mang đến cho chúng tôi một hình phạt đau đớn!”');
INSERT INTO "vn_ruwwad" VALUES(8,33,'8:33','(Quả thật), Allah không trừng phạt (cộng đồng của Ngươi) khi Ngươi (Thiên Sứ Muhammad) vẫn đang sống giữa bọn họ, và Allah cũng không trừng phạt họ khi họ vẫn còn cầu xin (TA) tha thứ.');
INSERT INTO "vn_ruwwad" VALUES(8,34,'8:34','Sao Allah không trừng phạt chúng (những kẻ đa thần) khi mà chúng ngăn cản (thiên hạ) vào Masjid Haram (Makkah). Chúng đâu phải là những người bảo quản Nó. Quả thật, chỉ những người ngoan đạo (sợ Allah) mới là những người bảo quản Nó. Tuy nhiên, đa số bọn chúng không biết (điều đó).');
INSERT INTO "vn_ruwwad" VALUES(8,35,'8:35','Lễ nguyện Salah của chúng (những kẻ đa thần) tại Ngôi Đền (Ka’bah) không gì hơn ngoài việc huýt sao và vỗ tay. Vì vậy, các ngươi hãy nếm lấy hình phạt bởi những gì các ngươi đã phủ nhận.');
INSERT INTO "vn_ruwwad" VALUES(8,36,'8:36','Quả thật, những kẻ vô đức tin thường chi dùng tài sản của chúng vào việc ngăn chặn con đường chính đạo của Allah. Vì vậy, chúng sẽ tiếp tục chi dùng tài sản của chúng (vào việc đó), rồi đây chúng sẽ phải hối tiếc và cuối cùng chúng sẽ thất bại. Quả thật, những kẻ vô đức tin sẽ bị đày vào Hỏa Ngục (ở Đời Sau).');
INSERT INTO "vn_ruwwad" VALUES(8,37,'8:37','Mục đích để Allah tách biệt (những kẻ vô đức tin) bẩn thỉu ra khỏi (những người có đức tin) tốt lành và để Ngài nhập chung tất cả những kẻ bẩn thỉu lại thành một đống chất chồng lên nhau trong Hỏa Ngục. Chúng là những kẻ thất bại và thua thiệt.');
INSERT INTO "vn_ruwwad" VALUES(8,38,'8:38','Ngươi (hỡi Thiên Sứ Muhammad) hãy bảo những kẻ vô đức tin nếu chúng biết dừng lại (việc ngăn chặn Islam và có đức tin nơi Allah) thì tội lỗi của chúng trong quá khứ sẽ được tha thứ, còn nếu chúng tiếp tục ngoan cố thì quả thật tiền lệ của các dân tộc [nổi loạn] trước đây (sẽ là sự cảnh báo dành cho chúng).”');
INSERT INTO "vn_ruwwad" VALUES(8,39,'8:39','Các ngươi (những người có đức tin) hãy tiếp tục đánh chúng cho đến khi nào chúng chấm dứt hành động quấy nhiễu (ngăn cản mọi người đến với tôn giáo Islam) và cho đến khi tôn giáo hoàn toàn thuộc về (một mình) Allah. Nhưng nếu chúng chịu dừng lại thì chắc chắn Allah hằng thấy rõ mọi hành động của chúng.');
INSERT INTO "vn_ruwwad" VALUES(8,40,'8:40','Còn nếu chúng từ chối thì các ngươi hãy biết rằng Allah chính là Đấng Bảo Hộ của các ngươi. Đấng Bảo Hộ ưu việt, Đấng Trợ Giúp ưu việt.');
INSERT INTO "vn_ruwwad" VALUES(17,1,'17:1','Quang vinh và trong sạch thay Đấng đã đưa Người Bề Tôi của Ngài (Thiên Sứ Muhammad) du hành trong đêm từ Masjid Al-Haram (tại Makkah) đến Masjid Al-Aqsa (tại Jerusalem), một vùng đất mà TA (Allah) đã ban phúc, mục đích để TA phô bày cho Y thấy những Dấu Hiệu của TA. Quả thật Ngài là Ðấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "vn_ruwwad" VALUES(17,2,'17:2','TA đã ban cho Musa (Môi-sê) Kinh Sách (Tawrah) và dùng Nó làm nguồn chỉ đạo cho người dân Israel (với mệnh lệnh): “Các ngươi không được nhận ai (vật gì) ngoài TA làm Đấng Bảo Hộ.”');
INSERT INTO "vn_ruwwad" VALUES(17,3,'17:3','(Các ngươi) là đám hậu duệ được TA (Allah) cứu vớt cùng với Nuh (trên chiếc thuyền trong trận đại hồng thủy). Quả thật, Y (Nuh) là một người bề tôi biết ơn.');
INSERT INTO "vn_ruwwad" VALUES(17,4,'17:4','TA đã sắc lệnh cho người dân Israel trong Kinh Sách rằng các ngươi sẽ hai lần làm tội ác trên trái đất và các ngươi sẽ trở nên cao ngạo và hống hách.');
INSERT INTO "vn_ruwwad" VALUES(17,5,'17:5','Khi lời hứa đầu tiên trong hai lời hứa đó đến, TA (Allah) sẽ gởi một đám bề tôi của TA có sức mạnh hùng hậu đến tàn phá nhà cửa của các ngươi. Và lời hứa (của TA) chắc chắn phải được thể hiện.');
INSERT INTO "vn_ruwwad" VALUES(17,6,'17:6','Sau đó, TA ban cho các ngươi (hỡi dân Israel) nhiều tài sản và đông con cái trở lại (sau khi các ngươi biết ăn năn hối cải) và TA nhân số lượng các ngươi đông đúc hơn (kẻ thù).');
INSERT INTO "vn_ruwwad" VALUES(17,7,'17:7','Nếu các ngươi (dân Israel) làm điều tốt thì thật ra chỉ tốt cho bản thân các ngươi, còn nếu các ngươi làm điều xấu thì các ngươi phải gặp điều xấu trở lại. Rồi khi lời hứa lần sau đến, (TA dựng lên một đám bề tôi khác) làm xấu mặt các ngươi và họ đi vào Masjid (Al-Aqsa) giống như lần đầu tiên họ đi vào và họ tàn phá hầu như toàn bộ.');
INSERT INTO "vn_ruwwad" VALUES(17,8,'17:8','Mong rằng Thượng Đế của các ngươi (dân Israel) đoái hoài đến các ngươi (khi các ngươi biết ăn năn và sám hối). Còn nếu các ngươi (ngoan cố) tái phạm (lần ba hoặc hơn nữa) thì TA (Allah) sẽ vẫn tiếp tục (trừng phạt các ngươi) và TA đã chuẩn bị sẵn Ngục Lửa để nhốt những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(17,9,'17:9','Qur’an này đích thực hướng dẫn (nhân loại) đến với điều chân chính nhất đồng thời báo tin mừng cho những người có đức tin và hành thiện rằng họ sẽ được phần thưởng vô cùng to lớn (nơi Allah).');
INSERT INTO "vn_ruwwad" VALUES(17,10,'17:10','Riêng những ai không có đức tin nơi Đời Sau, TA đã chuẩn bị sẵn cho họ một hình phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(17,11,'17:11','Con người thường vội vã cầu xin điều xấu (cho bản thân, tài sản hoặc cho con cái của mình) giống như y vội vã cầu xin điều tốt vậy; con người vốn thật hấp tấp và nóng vội.');
INSERT INTO "vn_ruwwad" VALUES(17,12,'17:12','TA (Allah) tạo ban đêm và ban ngày thành hai dấu hiệu (chứng minh quyền năng của TA như kéo dài ban ngày và thu ngắn ban đêm và ngược lại...), rồi TA xóa đi (ánh sáng) của dấu hiệu ban đêm (để các ngươi - con người - ngủ nghỉ) và TA làm cho dấu hiệu ban ngày sáng tỏ để các ngươi tìm kiếm thiên lộc của Thượng Đế của các ngươi và để các ngươi nhận biết năm tháng, tính toán mùa vụ; và tất cả mọi thứ đều được TA phân giải chi tiết.');
INSERT INTO "vn_ruwwad" VALUES(17,13,'17:13','Tất cả mỗi người đều bị TA cột quyển sổ (ghi chép việc làm thiện ác vào cổ của y) và vào Đời Sau quyển sổ đó được mở sẵn (đặt trước mặt y).');
INSERT INTO "vn_ruwwad" VALUES(17,14,'17:14','(Có lệnh bảo): “Ngươi hãy tự đọc quyển sổ của ngươi đi! Ngày hôm nay, chính ngươi tự thanh toán bản thân mình.”');
INSERT INTO "vn_ruwwad" VALUES(17,15,'17:15','Ai đi theo chỉ đạo thì việc theo đó có lợi cho bản thân y; còn ai lầm lạc thì y chỉ làm hại bản thân mình mà thôi. Không ai gánh chịu tội lỗi của người khác. Và TA không bao giờ trừng phạt (một ai) cho đến khi TA đã cử phái (đến với họ) một vị Thiên Sứ (cảnh báo trước).');
INSERT INTO "vn_ruwwad" VALUES(17,16,'17:16','Trước khi TA (Allah) muốn tiêu diệt một thị trấn nào đó thì TA ra lệnh cho thị dân nào của nó sống sung túc (hãy thần phục TA và sống lương thiện), tuy nhiên, họ ngoan cố làm điều thối nát (cho đến khi số lượng người a tòng với tội lỗi nhiều hơn người ngoan đạo) thì lúc đó họ xứng đáng bị trừng phạt, và lúc đó TA mới tiêu diệt họ toàn bộ.');
INSERT INTO "vn_ruwwad" VALUES(17,17,'17:17','Đã biết bao thế hệ sau Nuh (Noah) bị TA tiêu diệt (chẳng hạn như ‘Ad, Thamud, ...) và Thượng Đế của Ngươi (Thiên Sứ Muhammad) đủ biết, đủ thấy tội lỗi của đám bề tôi của Ngài.');
INSERT INTO "vn_ruwwad" VALUES(17,18,'17:18','Ai muốn đời sống chớp nhoáng (của thế giới trần tục này) thì TA sẽ sớm ban cho đối với người nào TA muốn, nhưng rồi đây (ở Đời Sau) TA sẽ quẳng y vào Ngục Lửa, Nó sẽ nướng y một cách nhục nhã đáng khinh.');
INSERT INTO "vn_ruwwad" VALUES(17,19,'17:19','Còn ai muốn Đời Sau, cố gắng hành đạo và hết lòng tin tưởng thì công sức cũng như những nỗ lực của họ sẽ được đền đáp xứng đáng.');
INSERT INTO "vn_ruwwad" VALUES(17,20,'17:20','Cả hai, nhóm này cũng như nhóm kia đều được Thượng Đế của Ngươi (hỡi Thiên Sứ Muhammad) ban bố cho (thiên lộc của Ngài) và sự ban bố của Thượng Đế của Ngươi không giới hạn (với bất cứ ai dù là ngoan đạo hay hư đốn).');
INSERT INTO "vn_ruwwad" VALUES(17,21,'17:21','Ngươi (Thiên Sứ Muhammad) hãy nhìn xem việc TA đã ưu đãi người này hơn người kia (về bổng lộc và địa vị) như thế nào, tuy nhiên, ở Đời Sau thì cấp bậc đó còn cao hơn và được ưu đãi lớn hơn.');
INSERT INTO "vn_ruwwad" VALUES(17,22,'17:22','Ngươi (hỡi người bề tôi) chớ dựng lên cùng với Allah một thần linh nào khác, kẻo ngươi sẽ phải ngồi (trong Hỏa Ngục) một cách hèn hạ đáng khinh.');
INSERT INTO "vn_ruwwad" VALUES(17,23,'17:23','Và Thượng Đế của Ngươi (Thiên Sứ Muhammad) sắc lệnh cho các ngươi phải thờ phượng duy nhất một mình Ngài và phải đối xử tử tế với cha mẹ. Nếu một trong hai người họ hoặc cả hai sống chung với các ngươi đến tuổi già thì các ngươi chớ buông tiếng ‘uf’ (âm thanh biểu hiện sự bất kính) với hai người họ, và chớ xua đuổi hai người họ, mà các ngươi phải ăn nói với hai người họ bằng lời lẽ đầy tôn kính.');
INSERT INTO "vn_ruwwad" VALUES(17,24,'17:24','Các ngươi hãy đối xử khiêm nhường bằng lòng nhân từ đối với họ, và các ngươi hãy cầu nguyện: “Lạy Thượng Đế của bề tôi, xin Ngài rủ lòng thương cha mẹ của bề tôi giống như hai người họ đã thương yêu, nuôi nấng và chăm sóc bề tôi lúc hãy còn bé.”');
INSERT INTO "vn_ruwwad" VALUES(17,25,'17:25','Thượng Đế của các ngươi (hỡi con người) luôn hiểu thấu đáo mọi tâm tư nằm sâu trong lòng các ngươi. Nếu các ngươi là những người đạo hạnh thì quả thật Ngài sẵn sàng tha thứ cho những ai biết quay về sám hối.');
INSERT INTO "vn_ruwwad" VALUES(17,26,'17:26','Ngươi (hỡi người có đức tin) hãy trao tặng phần quà (xứng đáng) cho họ hàng ruột thịt, cho người thiếu thốn khó khăn và cho khách lỡ đường nhưng chớ đừng hoang phí.');
INSERT INTO "vn_ruwwad" VALUES(17,27,'17:27','Quả thật, những kẻ hoang phí là anh em của Shaytan và Shaytan đối với Thượng Đế của hắn luôn là kẻ vong ơn.');
INSERT INTO "vn_ruwwad" VALUES(17,28,'17:28','Còn (khi họ đến xin Ngươi hỡi Thiên Sứ Muhammad) nhưng Ngươi không (có gì) cho họ vì cũng đang hi vọng vào lòng thương xót (sự ban bố bổng lộc) từ Thượng Đế của Ngươi thì ngươi hãy nói với họ bằng lời lẽ nhẹ nhàng và tử tế.');
INSERT INTO "vn_ruwwad" VALUES(17,29,'17:29','Ngươi chớ siết chặt bàn tay của mình vào cổ (như một kẻ keo kiệt) và cũng chớ đừng vung thẳng tay (một cách hoang phí) để rồi ngươi bị chê bai và trở nên bần cùng.');
INSERT INTO "vn_ruwwad" VALUES(17,30,'17:30','Quả thật, Thượng Đế của Ngươi muốn nới rộng hay thu hẹp bổng lộc cho ai tùy ý Ngài, bởi Ngài luôn thông toàn về đám bầy tôi của Ngài.');
INSERT INTO "vn_ruwwad" VALUES(17,31,'17:31','Các ngươi chớ đừng giết con cái của mình vì sợ nghèo khổ (ở tương lai), (bởi) chính TA (Allah) mới là Đấng ban phát bổng lộc cho chúng và cho cả các ngươi. Quả thật, việc giết chúng là một đại trọng tội.');
INSERT INTO "vn_ruwwad" VALUES(17,32,'17:32','Các ngươi chớ đến gần Zina (tình dục ngoài hôn nhân), bởi quả thật đó là hành vi ô uế và là một con đường xấu xa.');
INSERT INTO "vn_ruwwad" VALUES(17,33,'17:33','Các ngươi không được giết hại bất cứ một sinh mạng nào mà Allah đã cấm ngoại trừ với lý do chính đáng (theo giáo luật). Đối với người bị giết oan thì người thân của y được TA ban cho thẩm quyền (đòi lại công bằng: yêu cầu đền mạng hoặc tha mạng và nhận bồi thường hoặc không nhận bồi thường theo giáo luật), nhưng chớ vượt quá mức giới hạn trong việc giết chóc (như phanh thây chẳng hạn) bởi y sẽ được (luật pháp Islam) giúp đỡ.');
INSERT INTO "vn_ruwwad" VALUES(17,34,'17:34','Các ngươi chớ đến gần tài sản của trẻ mồ côi( ngoại trừ vì mục đích tốt đẹp cho đến khi chúng trưởng thành. Các ngươi hãy thực hiện đúng theo giao ước (với Allah hoặc với con người), quả thật cuộc giao ước sẽ bị tra hỏi.');
INSERT INTO "vn_ruwwad" VALUES(17,35,'17:35','Các ngươi hãy đong cho đủ và cân cho đúng. Điều đó tốt đẹp (cho các ngươi ở đời này) và tốt đẹp cả Đời Sau.');
INSERT INTO "vn_ruwwad" VALUES(17,36,'17:36','Và ngươi (hỡi con người) chớ đừng làm chứng cho điều gì đó mà ngươi không biết rõ về nó, bởi lẽ tất cả (mọi giác quan từ) cái nghe, cái nhìn và cả con tim đều phải chịu trách nhiệm cho sự làm chứng đó.');
INSERT INTO "vn_ruwwad" VALUES(17,37,'17:37','Và ngươi cũng chớ bước đi với dáng điệu ngạo mạn trên mặt đất, bởi chắc chắn các ngươi chẳng thể chẻ đôi được trái đất và cũng chẳng thể đứng cao bằng núi được.');
INSERT INTO "vn_ruwwad" VALUES(17,38,'17:38','Đối với Thượng Đế của ngươi (hỡi con người), tất cả những điều đó đều xấu và đáng ghét.');
INSERT INTO "vn_ruwwad" VALUES(17,39,'17:39','(Với những gì TA đã trình bày cho ngươi – hỡi con người), đó là sự khôn ngoan mà TA đã thiên khải cho ngươi, cho nên ngươi chớ dựng lên một thần linh nào đó cùng với Allah kẻo ngươi bị ném vào Ngục Lửa, bị sỉ nhục và bị bỏ xó.');
INSERT INTO "vn_ruwwad" VALUES(17,40,'17:40','Lẽ nào Thượng Đế của các ngươi (hỡi người đa thần) đã đặc ân cho các ngươi với những đứa con trai còn Ngài thì chọn lấy các vị Thiên Thần làm những đứa con gái của Ngài hay sao? Các ngươi quả thật đã thốt ra lời lẽ xúc phạm vô cùng nghiêm trọng.');
INSERT INTO "vn_ruwwad" VALUES(17,41,'17:41','Quả thật TA (Allah) đã giảng giải rõ ràng trong Qur’an này để họ lưu tâm, tuy nhiên, nó chẳng thêm thắt gì cho họ ngoại trừ sự xa lánh (chân lý).');
INSERT INTO "vn_ruwwad" VALUES(17,42,'17:42','Ngươi (hỡi Thiên Sứ Muhammad) hãy bảo (người đa thần): “Nếu thực sự có các thần linh khác cùng với Ngài (Allah) giống như các người tuyên bố thì chắc chắn các thần linh đó đã tìm đường đến trình diện Đấng Chủ Nhân của chiếc ngai vương.”');
INSERT INTO "vn_ruwwad" VALUES(17,43,'17:43','Vinh quang và trong sạch thay Ngài, Ngài tối cao và vĩ đại vượt xa những điều họ nói.');
INSERT INTO "vn_ruwwad" VALUES(17,44,'17:44','Bảy tầng trời và trái đất cùng tất cả vạn vật giữa trời đất đều tán dương Ngài (Allah), không một vật thể nào lại không tán dương, ca ngợi Ngài, chỉ có điều các ngươi không hiểu được lời tán dương của chúng mà thôi. Quả thật, Ngài hằng chịu đựng, hằng tha thứ.');
INSERT INTO "vn_ruwwad" VALUES(17,45,'17:45','Khi Ngươi (Thiên Sứ Muhammad) xướng đọc Qur’an, TA (Allah) đặt một bức màn vô hình ngăn cách giữa Ngươi và những kẻ vô đức tin nơi Đời Sau.');
INSERT INTO "vn_ruwwad" VALUES(17,46,'17:46','TA trùm kín con tim của họ khiến họ không hiểu được nó (Qur’an) và TA làm cho đôi tai của họ bị điếc (không nghe được lời hữu ích), nên khi Ngươi nhắc về Thượng Đế của Ngươi duy nhất trong Qur’an (mà không nhắc đến những thần linh khác của họ) thì họ liền quay lưng bỏ đi.');
INSERT INTO "vn_ruwwad" VALUES(17,47,'17:47','TA biết rõ việc họ (những tên lãnh đạo của những người đa thần) tập trung lắng nghe nó (Qur’an), họ không nghe để hiểu (chân lý mà là để tìm sơ hở) nhất là lúc họ họp kín với nhau, khi mà những kẻ sai quấy tuyên bố: “Các ngươi (hỡi mọi người) chỉ đang đi theo một kẻ đã bị bùa ếm.”');
INSERT INTO "vn_ruwwad" VALUES(17,48,'17:48','Ngươi (hỡi Thiên Sứ Muhammad) hãy xem cách họ ví Ngươi, (đó là lý do khiến) họ lầm lạc không thể tìm được lối (đến với chân lý).');
INSERT INTO "vn_ruwwad" VALUES(17,49,'17:49','Và họ bảo: “Lẽ nào sau khi chúng tôi trở thành những khúc xương và mảnh vụn thì chúng tôi sẽ lại được phục sinh thành một tạo hóa mới ư?!”');
INSERT INTO "vn_ruwwad" VALUES(17,50,'17:50','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Dẫu cho các người có trở thành đá hay sắt”');
INSERT INTO "vn_ruwwad" VALUES(17,51,'17:51','“Hoặc trở thành bất cứ vật thể nào mà trong lòng của các ngươi cho là lớn nhất (thì chắc chắn cũng sẽ được dựng cho sống lại.” Rồi họ sẽ lại bảo: “Ai sẽ làm chúng tôi sống lại?” Ngươi hãy bảo họ: “Chính Đấng đã tạo hóa các người ban đầu (từ cái không là gì sẽ làm các người sống lại).” Rồi họ sẽ lắc đầu (tỏ vẻ ngao ngán) với Ngươi và hỏi: “Vậy khi nào điều đó xảy ra?” Ngươi hãy bảo họ: “Ta e rằng điều đó rất gần kề.”');
INSERT INTO "vn_ruwwad" VALUES(17,52,'17:52','“(Đó là) Ngày mà Ngài gọi các người (từ cõi mộ) rồi các người sẽ liền (đứng dậy) đáp lại lời gọi bằng lời tạ ơn Ngài và (khi ấy) các người cứ tưởng chừng mình sống (ở trần gian) chỉ trong chốc lát.”');
INSERT INTO "vn_ruwwad" VALUES(17,53,'17:53','Ngươi (Thiên Sứ Muhammad) hãy bảo đám bầy tôi của TA chỉ nói điều tốt đẹp kẻo Shaytan thừa cơ hội (nhảy vào) chia rẽ bọn họ, bởi quả thật Shaytan vốn là kẻ thù công khai của loài người.');
INSERT INTO "vn_ruwwad" VALUES(17,54,'17:54','Thượng Đế của các ngươi (hỡi con người) biết rõ về (mọi hành động của) các ngươi, nếu muốn Ngài sẽ nhân từ với các ngươi và nếu muốn Ngài sẽ trừng phạt các ngươi; và TA (Allah) không gởi Ngươi (Thiên Sứ Muhammad) đến với họ với vai trò là người bảo hộ.');
INSERT INTO "vn_ruwwad" VALUES(17,55,'17:55','Thượng Đế của Ngươi biết rõ những ai ở trong các tầng trời và trái đất. Quả thật, TA (Allah) đã ưu ái một số vị Nabi này hơn một số vị khác và TA đã ban cho Dawood Kinh Zabur (Sách Thánh Thi).');
INSERT INTO "vn_ruwwad" VALUES(17,56,'17:56','Ngươi (hỡi Thiên Sứ Muhammad) hãy bảo (những kẻ thờ đa thần): “Các người hãy cầu nguyện những gì mà các người đã khẳng định chúng là thần linh ngoài Allah, rồi các người sẽ thấy chúng không có khả năng giải nạn cho các người và cũng không thể chuyển nạn kiếp cho người khác được”.');
INSERT INTO "vn_ruwwad" VALUES(17,57,'17:57','Những thần linh mà họ khấn vái cũng tìm mọi cách (thi đua) xem ai trong số họ được gần Ngài hơn, họ cũng hy vọng nơi lòng thương xót của Ngài đồng thời cũng sợ sự trừng phạt của Ngài. Quả thật, hình phạt của Thượng Đế của Ngươi (hỡi Thiên Sứ Muhammad) là thứ đáng phải khiếp sợ.');
INSERT INTO "vn_ruwwad" VALUES(17,58,'17:58','Không một ngôi làng (hay thị trấn) nào mà sẽ không bị TA (Allah) tiêu diệt hoặc trừng phạt bằng một cực hình đau đớn trước Ngày Tận Thế. (Bởi) đó là điều (đã được ghi) trong Quyển Sổ Mẹ (Al-Lawhu Al-Mahfuzh).');
INSERT INTO "vn_ruwwad" VALUES(17,59,'17:59','Nguyên nhân khiến TA (Allah) không gởi những dấu hiệu lạ xuống (theo yêu cầu của những kẻ đa thần) là bởi vì các thế hệ trước đây đã phủ nhận chúng, (điển hình) như TA đã gởi xuống cho đám dân Thamud con lạc đà làm minh chứng nhưng họ lại sát hại nó. Và TA gởi các dấu hiệu lạ xuống chỉ nhằm mục đích răn đe.');
INSERT INTO "vn_ruwwad" VALUES(17,60,'17:60','(Ngươi, hỡi Thiên Sứ Muhammad, hãy nhớ lại) việc TA đã nói với Ngươi rằng Thượng Đế của Ngươi đã thâu tóm toàn bộ nhân loại (trong tay Ngài), việc TA đã cho Ngươi nhìn thấy những điều xảy ra (trong đêm Isra’ và Me’raj) cũng như việc nhắc đến loại cây đáng nguyền rủa trong Qur’an (Zaqqum), (tất cả) đều chỉ nhằm mục đích thử thách nhân loại (xem ai tin và ai không tin). TA muốn làm cho họ sợ, tuy nhiên, nó không thêm thắt được gì cho họ ngoại trừ sự ngông cuồng quá mức.');
INSERT INTO "vn_ruwwad" VALUES(17,61,'17:61','(Ngươi hãy nhớ lại) khi TA phán bảo các Thiên Thần hãy cúi đầu quỳ lạy (chào) Adam thì tất cả đều cúi đầu quỳ lạy ngoại trừ Iblis, hắn bảo: “Chẳng lẽ bề tôi phải quỳ lạy một kẻ chỉ được Ngài tạo ra từ đất sét ư?”');
INSERT INTO "vn_ruwwad" VALUES(17,62,'17:62','(Iblis) đòi (Allah): “Ngài thấy sao? Kẻ mà Ngài ban cho vinh dự hơn bề tôi này đây, nếu Ngài gia hạn cho bề tôi sống đến Ngày Phục Sinh, chắc chắn bề tôi sẽ cám dỗ con cháu của Y lầm lạc ngoại trừ một số ít.”');
INSERT INTO "vn_ruwwad" VALUES(17,63,'17:63','(Allah) phán: “Ngươi hãy cút đi, ai theo ngươi trong số họ, chắc chắn phần thưởng dành (chung) cho các ngươi chính là Ngục Lửa, một phần thưởng xứng đáng.”');
INSERT INTO "vn_ruwwad" VALUES(17,64,'17:64','“Ngươi cứ tận hết sức của ngươi lôi kéo (bất cứ) ai trong số họ (con người) bằng âm thanh (quyến rũ) của ngươi, ngươi có thể dùng luôn cả kỵ binh và bộ binh của ngươi, ngươi có thể chia sẽ cùng họ về tiền bạc, con cái (xúi giục họ tiêu tiền vào điều tội lỗi, xúi giục họ làm Zina) và ngươi hãy hứa hẹn với họ.” Tuy nhiên, Shaytan chỉ hứa hão với họ mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(17,65,'17:65','“Chắc chắn với đám bầy tôi (ngoan đạo) của TA thì ngươi (Iblis) không có thẩm quyền (dụ dỗ) được họ và chỉ cần Thượng Đế của ngươi đủ làm Đấng Bảo Hộ cho họ.”');
INSERT INTO "vn_ruwwad" VALUES(17,66,'17:66','Thượng Đế của các ngươi (con người) là Đấng đã chế ngự cho các ngươi tàu thuyền lênh đênh trên biển để các ngươi tìm kiếm bổng lộc của Ngài, quả thật Ngài đối với các ngươi rất nhân từ.');
INSERT INTO "vn_ruwwad" VALUES(17,67,'17:67','Khi các ngươi gặp điều dữ trên biển (như mưa to gió lớn) thì các thần linh mà các ngươi thường cầu xin khấn vái chúng lại không thấy tăm tích, chỉ có mỗi mình Ngài (Allah) (với các ngươi). Nhưng rồi sau khi Ngài cứu các ngươi (bình an) trở về đất liền thì các ngươi lại ngoảnh mặt quay lưng (với Ngài). Con người quả thật thường vong ân.');
INSERT INTO "vn_ruwwad" VALUES(17,68,'17:68','Lẽ nào sau khi lên bờ, các ngươi tưởng mình sẽ an toàn khỏi việc Ngài làm cho (đất) sụp xuống (nuốt chửng các ngươi) hoặc khỏi việc Ngài gởi xuống trận mưa đá trừng phạt các ngươi rồi các ngươi không tìm được ai giúp đỡ?');
INSERT INTO "vn_ruwwad" VALUES(17,69,'17:69','Hay các ngươi tưởng mình sẽ an toàn khỏi việc Ngài đẩy các ngươi trở lại (biển khơi) lần nữa rồi Ngài gởi cơn giông tố đến nhấn chìm các ngươi bởi sự vong ơn của các ngươi; rồi các ngươi không tìm thấy ai chống trả được TA?');
INSERT INTO "vn_ruwwad" VALUES(17,70,'17:70','Quả thật, TA đã ưu đãi cho con cháu của Adam (khi TA ra lệnh bảo các Thiên Thần quỳ lạy chào ông tổ Adam của họ) và TA đã chuyên chở họ trên đất liền (bằng động vật cũng như các phương tiện khác) và trên biển khơi (bằng tàu bè), TA đã cấp cho họ biết bao bổng lộc tốt đẹp, và TA đã làm cho họ vượt trội hơn nhiều tạo vật khác mà TA đã tạo.');
INSERT INTO "vn_ruwwad" VALUES(17,71,'17:71','Vào một Ngày (Ngày Tận Thế) TA (Allah) sẽ gọi từng nhóm một cùng với lãnh đạo của họ (ra trình diện), thế nên những ai được trao cho quyển sổ (ghi chép tội phước) của mình bằng tay phải thì họ sẽ (mừng rỡ) đọc quyển sổ của mình và họ không bị đối xử bất công.');
INSERT INTO "vn_ruwwad" VALUES(17,72,'17:72','Ai là kẻ mù lòa ở đời này (không chấp nhận chân lý) thì sẽ mù lòa ở Đời Sau và là kẻ lầm lạc nhất.');
INSERT INTO "vn_ruwwad" VALUES(17,73,'17:73','Họ (người đa thần) gần như kéo được Ngươi (Thiên Sứ Muhammad) rời xa điều mà TA (Allah) đã mặc khải cho Ngươi và xúi giục Ngươi bịa ra một điều khác chống lại TA. Và nếu được như thế thì chắc chắn họ sẽ nhận Nguơi làm một người thân tín.');
INSERT INTO "vn_ruwwad" VALUES(17,74,'17:74','Và nếu TA không giữ vững đức tin của Ngươi (thêm kiên định) thì suýt nữa Ngươi đã xiêu lòng nghe theo họ.');
INSERT INTO "vn_ruwwad" VALUES(17,75,'17:75','(Nếu Ngươi nghe theo họ) thì chắc chắn TA đã cho Ngươi nếm gấp đôi hình phạt ở đời này và gấp đôi hình phạt của Đời Sau, rồi Ngươi không thể tìm ra ai đó giúp đỡ Ngươi chống lại TA được.');
INSERT INTO "vn_ruwwad" VALUES(17,76,'17:76','Họ (Quraish) đã tìm đủ mọi cách để trục xuất Ngươi khỏi vùng đất đó (Makkah) (nhưng mọi kế hoạch của họ đều bất thành), và nếu (họ thành công trong việc trục xuất Ngươi) thì họ cũng chỉ được ở lại đó sau khi (Ngươi rời đi) một thời gian ngắn ngủi mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(17,77,'17:77','(Đó là) đường lối( (mà TA) đã quy định cho các vị Thiên Sứ trong số các vị Thiên Sứ của TA được TA cử phái đến trước Ngươi (Thiên Sứ Muhammad) và Ngươi sẽ không tìm thấy sự thay đổi trong đường lối của TA.');
INSERT INTO "vn_ruwwad" VALUES(17,78,'17:78','Ngươi (hỡi Thiên Sứ Muhammad) hãy chu đáo dâng lễ nguyện Salah lúc mặt trời nghiêng bóng cho đến đêm tối( và Ngươi hãy đọc Qur’an lúc hừng đông(4) bởi quả thật, việc đọc Qur’an lúc hừng đông được (hai nhóm Thiên Thần ban đêm và ban ngày) chứng giám.');
INSERT INTO "vn_ruwwad" VALUES(17,79,'17:79','Ngươi (Thiên Sứ Muhammad) hãy thức một phần trong đêm để Tahajjud(, một việc làm thêm dành cho Ngươi(6), mong rằng Ngươi sẽ được Thượng Đế của Ngươi nâng Ngươi lên một vị trí vinh dự đáng ca ngợi.');
INSERT INTO "vn_ruwwad" VALUES(17,80,'17:80','Ngươi hãy cầu xin: “Lạy Thượng Đế của bề tôi, xin Ngài đưa bề tôi vào (Madinah) bằng lối vào chân chính, xin Ngài đưa bề tôi ra khỏi (Makkah) bằng lối ra chân chính và xin Ngài ban cho bề tôi sức mạnh giúp bề tôi chiến thắng được (kẻ thù).');
INSERT INTO "vn_ruwwad" VALUES(17,81,'17:81','Ngươi hãy nói (với người đa thần): “Chân lý đã đến thì điều ngụy tạo phải tan biến. Quả thật, mọi điều ngụy tạo đều tiêu tan (vì không đứng vững trước chân lý).');
INSERT INTO "vn_ruwwad" VALUES(17,82,'17:82','TA (Allah) ban xuống trong Qur’an phương thuốc chữa lành (bệnh tật) và hồng phúc cho những người có đức tin, tuy nhiên, nó không thêm thắt được gì cho những kẻ sai quấy ngoại trừ sự thua thiệt.');
INSERT INTO "vn_ruwwad" VALUES(17,83,'17:83','Con người khi được TA (Allah) ban cho ân huệ thì quay lưng và rời xa (TA) còn khi gặp điều xấu thì oán than, tuyệt vọng (nơi lòng thương xót của TA).');
INSERT INTO "vn_ruwwad" VALUES(17,84,'17:84','Ngươi (Thiên Sứ Muhammad) hãy nói (với những kẻ đa thần): “Mỗi người hành động theo tín ngưỡng và đường lối của riêng mình, và Thượng Đế của các người mới biết rõ ai được hướng dẫn đúng đường.”');
INSERT INTO "vn_ruwwad" VALUES(17,85,'17:85','(Dân Kinh Sách) hỏi Ngươi về linh hồn, Ngươi hãy trả lời họ: “Linh hồn thuộc về kiến thức Thượng Đế của Ta, còn kiến thức mà các ngươi được ban cho chỉ là ít ỏi”.');
INSERT INTO "vn_ruwwad" VALUES(17,86,'17:86','Nếu muốn, TA (Allah) lấy đi hết những gì đã thiên khải cho Ngươi (Thiên Sứ Muhammad), rồi Ngươi sẽ không tìm được một vị bảo hộ nào giúp Ngươi ngăn cản TA (về điều này).');
INSERT INTO "vn_ruwwad" VALUES(17,87,'17:87','Nhưng vì lòng thương xót của Thượng Đế Ngươi (nên Ngài đã không lấy nó đi), quả thật hồng phúc mà Ngài đã ban cho Ngươi vô cùng to lớn.');
INSERT INTO "vn_ruwwad" VALUES(17,88,'17:88','Ngươi hãy nói với họ: “Dẫu cho loài người lẫn loài Jinn (ma quỉ) có hợp sức với nhau để sáng tác ra một kinh sách giống như kinh Qur’an này đây thì chắc chắn họ cũng không thể mang đến một quyển giống như nó ngay cả họ có nỗ lực và cố gắng đến mấy đi chăng nữa.”');
INSERT INTO "vn_ruwwad" VALUES(17,89,'17:89','Quả thật, TA (Allah) đã trình bày cho nhân loại trong Qur’an này đủ các hình ảnh thí dụ, tuy nhiên, đa số nhân loại phủ nhận và vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(17,90,'17:90','(Những kẻ đa thần) nói (với Thiên Sứ Muhammad): “Bọn ta sẽ không bao giờ tin Ngươi trừ phi Ngươi làm nguồn nước phụt lên từ đất (Makkah này) cho bọn ta.”');
INSERT INTO "vn_ruwwad" VALUES(17,91,'17:91','“Hoặc Ngươi tạo cho riêng Ngươi một ngôi vườn chà là và một ngôi vườn nho rồi giữa chúng Người làm ra những dòng sông chảy tràn đầy nước.”');
INSERT INTO "vn_ruwwad" VALUES(17,92,'17:92','“Hoặc Ngươi làm bầu trời trút hình phạt xuống (hành hạ bọn ta) giống như Ngươi luôn khẳng định hoặc Ngươi hãy gọi Allah và cả Thiên Thần xuất hiện (cho bọn ta tận mắt nhìn thấy).”');
INSERT INTO "vn_ruwwad" VALUES(17,93,'17:93','“Hoặc Ngươi sở hữu một ngôi nhà bằng vàng hoặc Ngươi thăng thiên lên trời và tất nhiên bọn ta không bao giờ tin vào việc thăng thiên đó của Ngươi cho đến khi Ngươi mang xuống cho bọn ta một Kinh Sách mà bọn ta có thể đọc được.” Ngươi (hỡi Thiên Sứ Muhammad) hãy đáp lại họ: “Quang vinh và trong sạch thay Thượng Đế của Ta, chẳng phải Ta vốn cũng chỉ là một người phàm được cử làm Thiên Sứ (của Allah) đó sao?!”');
INSERT INTO "vn_ruwwad" VALUES(17,94,'17:94','Chẳng có điều gì ngăn cản loài người tin tưởng khi sự Chỉ Đạo đến với họ ngoài việc họ thắc mắc: “Lẽ nào Allah lại phái một người phàm làm Sứ Giả (của Ngài) ư?!”');
INSERT INTO "vn_ruwwad" VALUES(17,95,'17:95','Ngươi (hỡi Thiên Sứ) hãy đáp (lại họ): “Nếu như trên mặt đất có các vị Thiên Thần ung dung đi lại thì chắc chắn TA (Allah) đã phái một Thiên Thần từ trên trời xuống làm Sứ Giả rồi.”');
INSERT INTO "vn_ruwwad" VALUES(17,96,'17:96','Ngươi hãy bảo họ: “Một mình Allah đã đủ làm nhân chứng giữa Ta và các người, quả thật Ngài am tường và thông toàn hết (mọi tình trạng) của đám bầy tôi của Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(17,97,'17:97','(Cho nên) ai được Allah phù hộ (hướng đến chân lý) thì xem như y đã được hướng dẫn, còn ai bị bỏ mặc cho lầm lạc thì Ngươi (Thiên Sứ) sẽ không thấy bất cứ ai có thể hướng dẫn họ (đến với chân lý) ngoài Ngài. Rồi vào Ngày Phán Xét. TA (Allah) sẽ phục sinh họ trở lại với gương mặt mù, câm và điếc, chỗ ngụ của họ sẽ là Hỏa Ngục. Mỗi lần ngọn lửa giảm xuống thì TA sẽ tăng nó lên để trừng phạt họ.');
INSERT INTO "vn_ruwwad" VALUES(17,98,'17:98','Đó là sự trừng phạt dành cho họ bởi họ đã phủ nhận các dấu hiệu của TA (Allah) và bảo: “Lẽ nào sau khi bọn ta trở thành xương khô và đã rã mục thì bọn ta lại được cho sống trở lại lần nữa ư?!”');
INSERT INTO "vn_ruwwad" VALUES(17,99,'17:99','Lẽ nào họ (những kẻ phủ nhận việc phục sinh) không thấy rằng Allah là Ðấng đã tạo hóa các tầng trời và trái đất thừa khả năng tạo hóa (con người lần nữa) giống như họ (lúc ban đầu) hay sao? Ngài đã ấn định tuổi thọ cho họ, điều mà không có gì phải ngờ vực, tuy nhiên, những kẻ làm điều sai quấy chỉ ngoan cố phủ nhận nó mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(17,100,'17:100','Ngươi (Thiên Sứ) hãy bảo họ: “Nếu các người nắm giữ các kho bổng lộc của Thượng Đế của Ta thì chắc chắn các người sẽ giữ hết cho riêng mình (không bao giờ ban phát ra) do sợ (nghèo khổ) bởi lẽ con người vốn keo kiệt.”');
INSERT INTO "vn_ruwwad" VALUES(17,101,'17:101','Quả thật, TA (Allah) đã ban cho Musa (Môi-sê) chín phép lạ rõ rệt(. Ngươi (Thiên Sứ) hãy hỏi người dân Israel xem (có phải) khi Y (Musa) mang (các phép lạ đó) đến trình họ thì Pha-ra-ông đã bảo Y: “Này Musa, quả thật ta đoán Ngươi chính là tên phù thủy”.');
INSERT INTO "vn_ruwwad" VALUES(17,102,'17:102','(Musa) đã nói với (Pha-ra-ông): “Chắc chắn ngài biết rõ không ai có đủ khả năng ban những thứ này xuống hiện rõ trước mắt (như thế này) ngoại trừ Thượng Đế của các tầng trời và trái đất và quả thật, tôi nghĩ rằng ngài, hỡi Pha-ra-ông, sắp bị tiêu diệt.”');
INSERT INTO "vn_ruwwad" VALUES(17,103,'17:103','Thế là hắn (Pha-ra-ông) muốn loại trừ họ (Musa và dân Israel) ra khỏi vùng đất (Ai Cập), (nhưng hắn chưa kịp) thì TA đã nhấn chìm hắn cùng tất cả quân lính của hắn.');
INSERT INTO "vn_ruwwad" VALUES(17,104,'17:104','Và TA (Allah) đã phán bảo với dân Israel sau (khi tiêu diệt) hắn: “Các ngươi hãy định cư tại vùng đất (Sham)( cho đến khi lời hứa sau cùng xảy đến, TA sẽ tập trung các ngươi lại, trộn lẫn với nhau.”');
INSERT INTO "vn_ruwwad" VALUES(17,105,'17:105','Chỉ vì chân lý mà TA đã thiên khải nó (Qur’an) xuống (cho Thiên Sứ Muhammad) và cũng vì chân lý mà nó được bảo vệ (không bị mai một, chỉnh sửa), và TA (Allah) chỉ cử Ngươi đến làm người mang tin mừng và cảnh báo mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(17,106,'17:106','Và TA đã chia Nó (Qur’an) ra từng phần để Ngươi (Thiên Sứ) có thể đọc cho nhân loại nghe theo từng giai đoạn (của sự kiện xảy ra) và TA đã lần lượt thiên khải Nó xuống từng ít một (trong suốt năm).');
INSERT INTO "vn_ruwwad" VALUES(17,107,'17:107','Ngươi (Thiên Sứ) hãy bảo họ: “Dù cho các người có tin hay không tin nơi Nó (Qur’an) thì quả thật đối với người được ban cho kiến thức (qua việc học được Kinh Sách) trước Nó chắc chắn sẽ cúi đầu quỳ lạy mỗi khi Nó được xướng đọc cho họ nghe.');
INSERT INTO "vn_ruwwad" VALUES(17,108,'17:108','Họ sẽ nói: “Trong sạch và vinh quang thay Thượng Đế của bầy tôi, quả thật lời hứa của Thượng Đế của bầy tôi đã hoàn tất.”');
INSERT INTO "vn_ruwwad" VALUES(17,109,'17:109','Họ sẽ úp mặt quỳ lạy trong tiếng khóc và nó làm cho họ tăng thêm lòng thành kính.');
INSERT INTO "vn_ruwwad" VALUES(17,110,'17:110','Ngươi (Thiên Sứ Muhammad) hãy bảo họ: “Dẫu cho các người có cầu xin Allah hay cầu xin Đấng Độ Lượng thì tất cả đều là các tên tốt đẹp của Ngài.” Và Ngươi chớ đừng quá to tiếng trong lễ nguyện Salah cũng như đừng quá khẽ mà Ngươi hãy ở giữa hai mức độ đó.');
INSERT INTO "vn_ruwwad" VALUES(17,111,'17:111','Và Ngươi hãy nói: “Alhamdulillah, Đấng đã không nhận bất cứ ai làm con trai (của Ngài) cũng như không có đối tác chia sẻ trong vương quyền; Ngài không yếu đuối để cần đến vị bảo hộ. Hãy tán dương sự vĩ đại của Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(18,1,'18:1','Alhamdulillah, Đấng đã ban xuống Kinh Sách cho người bề tôi của Ngài và không làm cho Nó quanh co.');
INSERT INTO "vn_ruwwad" VALUES(18,2,'18:2','(Một quyển Kinh) ngay thẳng, rõ ràng, dùng để cảnh báo về sự trừng phạt nghiêm khắc và để báo tin mừng đến những người có đức tin, những người làm việc thiện tốt rằng họ sẽ được ban cho phần thưởng tốt đẹp (Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(18,3,'18:3','Họ sẽ ở trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(18,4,'18:4','Và để cảnh báo những kẻ đã nói Allah có một đứa con trai.');
INSERT INTO "vn_ruwwad" VALUES(18,5,'18:5','Họ và cha mẹ của họ không hề có một chút kiến thức nào về điều đó cả. Thật nghiêm trọng thay cho lời nói từ cửa miệng của họ. Họ thực sự chỉ nói điều giả dối.');
INSERT INTO "vn_ruwwad" VALUES(18,6,'18:6','Có lẽ Ngươi (Muhammad) khổ tâm, đau buồn khiến bản thân chết dần chết mòn trên lối mòn của họ nếu họ không có đức tin vào Lời Nói này (Qur’an).');
INSERT INTO "vn_ruwwad" VALUES(18,7,'18:7','Quả thật, TA (Allah) đã tạo ra những gì trên trái đất là để tô điểm, trang hoàng mục đích thử thách họ xem ai trong số họ là người làm tốt nhất.');
INSERT INTO "vn_ruwwad" VALUES(18,8,'18:8','Và (rồi đây) TA chắc chắn sẽ làm cho nó thành đồi trọc, chẳng còn gì.');
INSERT INTO "vn_ruwwad" VALUES(18,9,'18:9','Hoặc có phải Ngươi (Muhammad) đã nghĩ rằng những người bạn của hang núi và bia ký là một trong các dấu lạ của TA?!');
INSERT INTO "vn_ruwwad" VALUES(18,10,'18:10','(Ngươi hãy nhớ lại) khi nhóm thanh niên ẩn náu trong hang núi, họ cầu nguyện: “Lạy Thượng Đế của bầy tôi, xin Ngài hãy thương xót bầy tôi và xin Ngài hãy sắp xếp vụ việc của bầy tôi theo hướng tốt đẹp.”');
INSERT INTO "vn_ruwwad" VALUES(18,11,'18:11','Thế là TA (Allah) đã bịt tai họ lại trong hang núi nhiều năm dài (để họ không nghe thấy gì và ngủ say).');
INSERT INTO "vn_ruwwad" VALUES(18,12,'18:12','Sau đó, TA đánh thức họ dậy để xem ai trong hai nhóm của họ tính toán chính xác nhất về thời gian mà họ đã ở lại (trong hang núi).');
INSERT INTO "vn_ruwwad" VALUES(18,13,'18:13','TA kể cho Ngươi (Muhammad) nghe thông tin của họ bằng sự thật. Quả thật, họ là một nhóm thanh niên có đức tin nơi Thượng Đế của họ và TA đã ban thêm cho họ sự hướng dẫn.');
INSERT INTO "vn_ruwwad" VALUES(18,14,'18:14','Và TA đã giữ vững trái tim của họ (trong đức tin) khi họ đứng cầu nguyện: “Lạy Thượng Đế của bầy tôi, Thượng Đế của các tầng trời và trái đất, bầy tôi sẽ không bao giờ khấn vái cầu xin bất cứ thần linh nào khác ngoài Ngài. (Nếu như bầy tôi làm như thế) thì quả thật bầy tôi đã quá sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(18,15,'18:15','“Đám dân của bầy tôi, những người này, đã tôn thờ những thần linh khác ngoài Ngài nhưng họ không có bằng chứng rõ ràng nào (cho việc làm đó của họ). Bởi vậy còn ai sai quấy hơn những kẻ đã bịa đặt điều dối trá cho Allah?!”');
INSERT INTO "vn_ruwwad" VALUES(18,16,'18:16','(Nhóm thanh niên bảo nhau): “Và khi các anh rời bỏ họ và những thần linh mà họ thờ phượng ngoài Allah thì các anh hãy chạy đến hang núi mà ẩn náu rồi Thượng Đế của các anh sẽ thương xót các anh và sắp xếp vụ việc của các anh theo hướng dễ dàng và tốt đẹp.”');
INSERT INTO "vn_ruwwad" VALUES(18,17,'18:17','Và Ngươi (Muhammad) thấy mặt trời khi nó mọc thì nó mọc lệch về phía bên phải cái hang của họ rồi khi nó lặn thì nó đi ngang qua họ về phía bên trái trong lúc họ nằm ngủ tại khoảng trống giữa hang. Đó là một trong những dấu lạ của Allah. Người nào được Allah hướng dẫn thì y sẽ tìm thấy nguồn chỉ đạo còn người nào bị Ngài làm cho lầm lạc thì sẽ không bao giờ tìm thấy vị bảo hộ hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(18,18,'18:18','(Khi nhìn thấy họ) Ngươi cứ ngỡ họ đang thức nhưng kỳ thật họ đang ngủ. Và TA lật trở họ sang bên phải rồi sang bên trái, và con chó của họ thì (vẫn trong tư thế) duỗi hai chân trước ra trên bậc thềm (của cái hang như đang canh gác). Nếu Ngươi nhìn thấy cảnh tượng đó của họ thì chắc chắn Ngươi sẽ tháo chạy và sẽ rất kinh hãi trước hình ảnh của họ.');
INSERT INTO "vn_ruwwad" VALUES(18,19,'18:19','Và tương tự như thế, TA đã đánh thức họ dậy để họ hỏi nhau. Một người trong bọn họ nói: “Các anh đã ngủ được bao lâu rồi?” Họ bảo: “Có lẽ chúng ta đã ngủ được một ngày hay một buổi gì đó.” Rồi họ lại nói: “Thượng Đế của các anh biết rõ hơn hết rằng các anh đã ngủ được bao lâu; thôi, các anh hãy cử một người cầm số tiền này của các anh xuống phố tìm mua thức ăn mang về cho các anh và anh ta hãy nhớ chú ý cẩn thận chớ để ai đó phát giác.”');
INSERT INTO "vn_ruwwad" VALUES(18,20,'18:20','“Quả thật, nếu họ phát hiện ra các anh, họ sẽ ném đá giết các anh, hoặc sẽ bắt các anh trở lại với tín ngưỡng của họ và nếu như thế thì các anh sẽ không bao giờ thành đạt.”');
INSERT INTO "vn_ruwwad" VALUES(18,21,'18:21','Và như thế (với cách đó) TA đã cho lộ tung tích của họ (nhóm thanh niên trong hang núi) để họ (thiên hạ) biết rằng lời hứa của Allah là sự thật và rằng giờ Tận Thế (cũng là sự thật) không có gì phải ngờ vực. (Ngươi hãy nhớ lại) lúc họ (người dân trong thành phố) tranh cãi nhau về vụ việc của họ (nhóm thanh niên trong hang núi). Một số bảo: “Các người hãy xây lên chỗ của họ một tượng đài, Thượng Đế của họ biết rõ về họ.” Những người chiếm ưu thế trong cuộc tranh cãi nói: “Chúng ta chắc chắn sẽ xây một Masjid bên trên chỗ của họ.”');
INSERT INTO "vn_ruwwad" VALUES(18,22,'18:22','(Một số người) sẽ nói: “(Nhóm thanh niên trong hang núi) gồm ba người, con chó của họ nữa là bốn.” Một số khác sẽ nói: “Họ gồm năm người, con cho của họ nữa làm sáu.” Toàn là lời nói vô căn cứ! Và một số khác thì bảo: “Họ gồm bảy người, con chó của họ nữa là tám.” Ngươi (Muhammad) hãy nói: “Thượng Đế của ta biết rõ nhất về số lượng của họ, chỉ ít người biết về họ. Do đó, chớ tranh cãi về họ trừ phi thực sự biết rõ sự việc và cũng chớ hỏi một ai về chuyện của họ.”');
INSERT INTO "vn_ruwwad" VALUES(18,23,'18:23','Và Ngươi (Muhammad) chớ nói về bất cứ điều gì: “Quả thật tôi sẽ làm điều đó vào ngày mai.”');
INSERT INTO "vn_ruwwad" VALUES(18,24,'18:24','Trừ phi (Ngươi kèm theo lời): “Insha-Allah – Nếu Allah muốn”. Và Ngươi hãy tụng niệm Thượng Đế của Ngươi lúc Ngươi đã quên và Ngươi hãy nói: “Có thể Thượng Đế của Ta sẽ hướng dẫn Ta đến gần với chân lý hơn điều này.”');
INSERT INTO "vn_ruwwad" VALUES(18,25,'18:25','Và họ (nhóm thanh niên ẩn náu trong hang núi) đã lưu lại trong cái hang của họ ba trăm lẻ chín năm.');
INSERT INTO "vn_ruwwad" VALUES(18,26,'18:26','Ngươi (Muhammad) hãy nói: “Allah biết rõ nhất họ đã lưu lại bao lâu (trong cái hang đó), những điều vô hình của các tầng trời và trái đất đều nằm trong sự hiểu biết của Ngài, Ngài nhìn thấy và nghe tất cả. Họ không có Đấng Bảo Hộ nào khác ngoài Ngài và không có ai (vật gì) chia sẻ với Ngài trong luật lệ của Ngài.');
INSERT INTO "vn_ruwwad" VALUES(18,27,'18:27','Ngươi (Muhammad) hãy xướng đọc những gì được mặc khải cho Ngươi từ Kinh Sách nơi Thượng Đế của Ngươi. Không ai có thể thay đổi các lời phán của Ngài và Ngươi sẽ không bao giờ tìm thấy được chỗ nương tựa nào khác ngoài Ngài.');
INSERT INTO "vn_ruwwad" VALUES(18,28,'18:28','Ngươi (Muhammad) hãy kiên nhẫn cùng với những người thường cầu nguyện Thượng Đế của họ sáng chiều với mong muốn làm đẹp lòng Ngài. Ngươi chớ đừng không ngó ngàng gì tới họ chỉ vì thèm muốn vẻ hào nhoáng của cuộc sống trần tục, và Ngươi cũng chớ đừng nghe theo kẻ mà TA (Allah) đã đóng chặt trái tim của y khỏi sự tưởng nhở đến TA và chỉ biết chạy theo dục vọng của bản thân và hành động của y thường quá mức giới hạn.');
INSERT INTO "vn_ruwwad" VALUES(18,29,'18:29','Ngươi (Muhammad) hãy nói: “Chân lý thực sự đến từ nơi Thượng Đế của các người. Bởi thế, ai muốn tin thì hãy tin còn ai muốn vô đức tin thì cứ phủ nhận.” Quả thật, TA (Allah) đã chuẩn bị cho những kẻ làm điều sai quấy Ngục Lửa có rào bao quanh nhốt họ lại. Nếu họ van xin (nước để làm dịu đi một chút cơn nóng) thì họ sẽ được ban cho một loại nước giống như dầu sôi, nó (sẽ được đổ lên người họ) lột hết da mặt của họ. Ôi, loại nước uống thật khủng khiếp và nơi trú ngụ thật tồi tệ!');
INSERT INTO "vn_ruwwad" VALUES(18,30,'18:30','Riêng những người có đức tin và hành thiện, chắc chắn TA (Allah) sẽ không làm mất đi công lao của những ai làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(18,31,'18:31','Những người đó sẽ được ban cho các Ngôi Vườn Thiên Đàng vĩnh cửu, bên dưới có các dòng sông chảy, trong đó họ sẽ được cho đeo các vòng vàng và sẽ được cho mặc y phục màu xanh lục từ gấm lụa được thêu trổ tuyệt đẹp, trong đó họ sẽ được ngồi tựa mình trên những chiếc tràng kỷ. Ôi, một phần thưởng thật đầy ân huệ và một nơi trú ngụ vô cùng tốt đẹp!');
INSERT INTO "vn_ruwwad" VALUES(18,32,'18:32','Và Ngươi (Muhammad) hãy đưa ra cho họ một hình ảnh thí dụ về hai người đàn ông. Một trong hai được TA ban cho hai ngôi vườn nho, cả hai ngôi vườn được TA bao quanh bằng các cây chà là và chính giữa hai ngôi vườn được TA đặt cho một miếng rẫy.');
INSERT INTO "vn_ruwwad" VALUES(18,33,'18:33','Cả hai ngôi vườn đều xum xuê trĩu quả, không có bất cứ một trái lép nào; và TA đã cho phún ra một dòng sông chính giữa hai ngôi vườn đó.');
INSERT INTO "vn_ruwwad" VALUES(18,34,'18:34','Y (chủ của hai ngôi vườn) được mùa trái quả. Y tranh luận với người bạn của mình trong lúc trò chuyện, nói (một cách đầy kiêu ngạo): “Tôi nhiều hơn anh về tài sản và mạnh hơn anh vì được đông con cái.”');
INSERT INTO "vn_ruwwad" VALUES(18,35,'18:35','Y vào ngôi vườn của mình và tự làm bản thân mình sai quấy khi nói: “Tôi nghĩ rằng ngôi vườn này sẽ không thể nào tiêu tan được.”');
INSERT INTO "vn_ruwwad" VALUES(18,36,'18:36','“Và tôi cũng nghĩ rằng giờ tận thế sẽ không xảy đến. Và nếu tôi thực sự được đưa trở về với Thượng Đế của mình thì chắc chắn tôi cũng sẽ tìm được điều tốt đẹp hơn ngôi vườn này.”');
INSERT INTO "vn_ruwwad" VALUES(18,37,'18:37','Người bạn của y tranh luận với y, nói: “Phải chẳng anh đã không có đức tin nơi Đấng đã tạo hóa ra anh từ đất bụi rồi từ tinh dịch rồi sau đó Ngài đã làm cho anh thành một người đàn ông hoàn chỉnh?!”');
INSERT INTO "vn_ruwwad" VALUES(18,38,'18:38','“Riêng đối với tôi thì Ngài là Allah, là Thượng Đế của tôi, tôi sẽ không Shirk (gán ghép đối tác) với Thượng Đế của tôi bất kỳ ai.”');
INSERT INTO "vn_ruwwad" VALUES(18,39,'18:39','“Phải chi khi bước vào ngôi vườn của mình, anh nói: ‘Masha-Allah! – Đây là những gì Allah muốn! Không có sức mạnh nào ngoài Allah (thì có lẽ sẽ tốt cho anh). Nếu anh thấy tôi ít hơn anh về tài sản và con cái,”');
INSERT INTO "vn_ruwwad" VALUES(18,40,'18:40','“Thì biết đâu (sau này) Thượng Đế của tôi sẽ ban cho tôi thứ tốt đẹp hơn ngôi vườn của anh; biết đâu Ngài sẽ gửi từ trên trời xuống những lưỡi tầm sét tàn phá ngôi vườn của anh, biến nó thành mảnh đất trơ trụi,”');
INSERT INTO "vn_ruwwad" VALUES(18,41,'18:41','“Hoặc nguồn nước của ngôi vườn sẽ cạn mất khiến anh không thể nào tìm được nguồn nước trở lại.”');
INSERT INTO "vn_ruwwad" VALUES(18,42,'18:42','Thế rồi mùa màng trái quả của y bị tàn phá toàn bộ, lúc đó, y xoa hai lòng bàn tay (tỏ vẻ tiếc nuối và xót) cho những gì đã chi tiêu vào ngôi vườn (từ công sức và tiền bạc); và (trước cảnh) toàn bộ những giàn nho ngã rạp nằm ngổn ngang, y (hối hận) nói: “Ôi, phải chi mình đã không Shirk với Thượng Đế của mình!”');
INSERT INTO "vn_ruwwad" VALUES(18,43,'18:43','Và ngoài Allah, y không có một phái đảng nào giúp đỡ y và y cũng không tự cứu lấy được mình.');
INSERT INTO "vn_ruwwad" VALUES(18,44,'18:44','Ở kia (Ngày Phán Xét Cuối Cùng), quyền bảo hộ chỉ thuộc về một mình Allah, Đấng Chân Lý. Ngài là Đấng ban thưởng tốt nhất và cũng là Đấng ban cho kết quả cuối cùng tốt nhất.');
INSERT INTO "vn_ruwwad" VALUES(18,45,'18:45','Và Ngươi (Muhammad) hãy đưa ra hình ảnh thí dụ về đời sống trần tục rằng nó giống như nước mưa mà TA (Allah) đã ban từ trên trời xuống làm cho cây cối, thảo mộc trên mặt đất mọc lên tươi tốt nhưng rồi sau đó nó dần dần trở thành những cọng rạ khô héo để những cơn gió thổi chúng bay đi khắp nơi. Và Allah là Đấng Định Đoạt tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(18,46,'18:46','Của cải và con cái là những thứ trang hoàng của đời sống trần tục này (rồi sẽ sớm mất đi) và chỉ những việc làm ngoan đạo và thiện tốt mới còn mãi, chỉ có những việc làm ngoan đạo và thiện tốt mới được hưởng phần thưởng tốt đẹp nhất ở nơi Allah và mới là niềm hy vọng tốt đẹp nhất.');
INSERT INTO "vn_ruwwad" VALUES(18,47,'18:47','Vào Ngày mà TA sẽ di chuyển các quả núi đi mất dạng và Ngươi (Muhammad) sẽ thấy mặt đất trống trải bằng phẳng, và TA sẽ triệu tập họ lại và không bỏ sót bất kỳ một ai.');
INSERT INTO "vn_ruwwad" VALUES(18,48,'18:48','Họ sẽ được dẫn ra đứng thành hàng trình diện trước Thượng Đế của Ngươi. TA (Allah) chắc chắn sẽ mang các ngươi đến giống như TA đã tạo hóa các ngươi lần đầu. Tuy nhiên, các ngươi đã cho rằng TA không bao giờ thực hiện được lời hứa này với các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(18,49,'18:49','Và quyển sổ (ghi chép các việc làm) sẽ được mang ra đặt trước mặt. Rồi Ngươi (Muhammad) sẽ thấy những kẻ tội lỗi hoảng sợ về những điều được ghi trong đó. Và họ sẽ than: “Ôi, thật khổ thân! Quyển sổ gì như thế này! Sao nó ghi không sót một điều gì dù nhỏ hay lớn vậy.” Và họ sẽ nhìn thấy trước mặt họ tất cả những điều mà mình đã làm. Và Thượng Đế của Ngươi sẽ không đối xử bất công đối với một ai.');
INSERT INTO "vn_ruwwad" VALUES(18,50,'18:50','Và khi TA phán với các Thiên Thần: “Các ngươi hãy cúi đầu quỳ lạy Adam” thì tất cả đều cúi xuống quỳ lạy ngoại trừ Iblis thuộc loài Jinn. Nó đã nghịch lại mệnh lệnh Thượng Đế của nó. Vậy phải chăng các ngươi chấp nhận nó và con cháu của nó làm kẻ bảo hộ của các ngươi thay vì TA trong lúc chúng là kẻ thù của các ngươi ư?! Thật xấu xa và tệ hại thay cho việc đổi chác của những kẻ làm điều sai quấy!');
INSERT INTO "vn_ruwwad" VALUES(18,51,'18:51','TA đã không bắt chúng (Jinn) chứng kiến việc tạo hóa các tầng trời và trái đất và cũng không bắt chúng chứng kiến ngay cả việc tạo hóa ra bản thân chúng; và TA đã không lấy những kẻ dẫn thiên hạ đi lạc lối làm người ủng hộ.');
INSERT INTO "vn_ruwwad" VALUES(18,52,'18:52','Vào Ngày mà Ngài sẽ bảo (những kẻ thờ các thần linh ngoài Allah): “Các ngươi hãy gọi các thần linh mà các ngươi đã khẳng định chúng là những vị đối tác của TA đến!” Họ đã gọi chúng nhưng chúng không trả lời. Thế là TA đặt bức màn phân cách giữa họ.');
INSERT INTO "vn_ruwwad" VALUES(18,53,'18:53','Những kẻ tội lỗi nhìn thấy Hỏa Ngục thì họ biết chắc rằng mình sẽ phải sa vào đó và sẽ không thể nào tìm thấy một lối thoát để trốn khỏi nơi đó.');
INSERT INTO "vn_ruwwad" VALUES(18,54,'18:54','Quả thật TA (Allah) đã trình bày cho nhân loại trong Qur’an này đủ các hình ảnh thí dụ, tuy nhiên, con người thường hay tranh cãi về rất nhiều vấn đề.');
INSERT INTO "vn_ruwwad" VALUES(18,55,'18:55','Chẳng có gì ngăn cản (đa số) nhân loại tin tưởng và cầu xin Thượng Đế của họ tha thứ ngoại trừ (họ mong đợi một trong hai điều), hoặc là đường lối của những người đi trước (từ các hình phạt mà họ đã hứng chịu) sẽ xảy đến hoặc là sự trừng phạt giáng xuống trực diện họ.');
INSERT INTO "vn_ruwwad" VALUES(18,56,'18:56','Và TA (Allah) chỉ gửi các vị Sứ Giả đến để báo tin mừng và cảnh cáo (nhân loại) mà thôi. Tuy nhiên, những kẻ vô đức tin thường dùng sự ngụy tạo đầy sai quấy làm luận cứ để tranh cãi hầu đánh đổ Chân Lý. Họ đã lấy các lời mặc khải của TA cũng như những gì được cảnh cáo làm trò bỡn cợt, trêu đùa.');
INSERT INTO "vn_ruwwad" VALUES(18,57,'18:57','Còn ai sai quấy hơn kẻ khi được nhắc cho các lời phán từ Thượng Đế của y thì y quay mặt lánh xa và quên mất những gì mà bàn tay của y đã gửi đi trước (từ tội lỗi của y)! Quả thật, TA (Allah) đã phủ kín trái tim của họ để họ không thể hiểu và tiếp thu Nó (Qur’an) và TA đã làm cho tai của họ trở nên điếc (không nghe thấy gì), cho nên nếu Ngươi (Muhammad) có kêu gọi họ đến với Chỉ Đạo thì họ cũng sẽ không bao giờ được hướng dẫn.');
INSERT INTO "vn_ruwwad" VALUES(18,58,'18:58','Và Thượng Đế của Ngươi (Muhammad) là Đấng Tha Thứ tràn đầy lòng thương xót. Nếu Ngài bắt tội họ với những gì mà họ đã phạm thì chắc chắn Ngài sẽ nhanh chóng giáng hình phạt xuống họ. Nhưng vì lời hứa (trừng phạt) dành cho họ (đã được ấn định ở Đời Sau), lúc đó chắc chắn họ sẽ không bao giờ tìm thấy nơi nào để thoát nạn.');
INSERT INTO "vn_ruwwad" VALUES(18,59,'18:59','Các thị trận đó (dân ‘Ad, Thamud,...), TA đã hủy diệt họ khi họ làm điều sai quấy và TA đã ấn định giờ hẹn cho việc hủy diệt họ.');
INSERT INTO "vn_ruwwad" VALUES(18,60,'18:60','Và (hãy nhớ lại) khi Musa nói với cậu bé (học trò cùng đồng hành) của Y: “Ta vẫn phải tiếp tục cuộc hành trình cho đến khi tới được nơi tiếp giáp của hai vùng biển bằng không chẳng biết đến bao giờ Ta mới tới đó được”.');
INSERT INTO "vn_ruwwad" VALUES(18,61,'18:61','Nhưng khi hai thầy trò đến được nơi tiếp giáp của hai vùng biển thì cả hai đã quên mất con cá của họ, nó đã nhảy xuống biển lội đi mất như đi trong đường hầm.');
INSERT INTO "vn_ruwwad" VALUES(18,62,'18:62','Cho nên, khi hai thầy trò họ đi quá nơi hẹn, (Musa) bảo cậu học trò: “Con hãy dọn bữa trưa của chúng ta ra đi, quả thật chúng ta đã thấm mệt bởi một chuyến đi dài!”');
INSERT INTO "vn_ruwwad" VALUES(18,63,'18:63','(Cậu học trò) nói: “Thưa thầy, thầy có nhớ lúc chúng ta đến được mũi đá không, con đã quên mất chuyện con cá, Shaytan đã làm con quên nói với thầy về chuyện con cá, con cá đã tìm cách nhảy xuống biển một cách thật kỳ diệu”.');
INSERT INTO "vn_ruwwad" VALUES(18,64,'18:64','(Musa) bảo: “Đấy chính là địa điểm mà chúng ta cần tìm”. Vậy là hai thầy trò lần theo lối cũ mà quay trở lại nơi hẹn.');
INSERT INTO "vn_ruwwad" VALUES(18,65,'18:65','Họ đã gặp được một người bề tôi (Al-Khudhir) của TA, người mà TA đã ban cho Y hồng ân và đã dạy Y kiến thức từ nơi TA.');
INSERT INTO "vn_ruwwad" VALUES(18,66,'18:66','Musa nói với Y (Al-Khudhir): “Tôi có thể theo Ông để Ông dạy Tôi điều đúng đắn mà Ông đã được (Allah) chỉ dạy hay không?');
INSERT INTO "vn_ruwwad" VALUES(18,67,'18:67','(Al-Khudhir) nói: “Chắc chắn Ngươi sẽ không bao giờ có thể kiên nhẫn được cùng với Ta đâu.”');
INSERT INTO "vn_ruwwad" VALUES(18,68,'18:68','“Làm sao Ngươi có thể kiên nhẫn trên điều mà Ngươi không có kiến thức về nó (điều ẩn bên trong sự việc) được chứ?!”');
INSERT INTO "vn_ruwwad" VALUES(18,69,'18:69','(Musa) nói: “Insha-Allah, Ông sẽ thấy Tôi nhẫn nại và Tôi sẽ không cãi lại Ông bất cứ việc gì.”');
INSERT INTO "vn_ruwwad" VALUES(18,70,'18:70','(Al-Khudhir) bảo: “Thôi được, nếu Ngươi đi theo Ta thì ngươi chớ hỏi Ta về bất cứ điều gì cho tới khi Ta tự nói ra điều đó cho Ngươi biết.”');
INSERT INTO "vn_ruwwad" VALUES(18,71,'18:71','Thế là hai người họ ra đi cho đến khi cả hai lên một con thuyền thì (Al-Khudhir) đột nhiên đục cái lỗ làm thủng nó. (Musa) liền nói: “Sao Ông lại đục thủng con thuyền nhấn chìm mọi người trên nó chứ? Quả thật, Ông đã làm một việc quá ư là sai trái.”');
INSERT INTO "vn_ruwwad" VALUES(18,72,'18:72','(Al-Khudhir) nói: “Chẳng phải Ta đã bảo rằng Ngươi không bao giờ có thể kiên nhẫn được cùng với Ta sao?”');
INSERT INTO "vn_ruwwad" VALUES(18,73,'18:73','(Musa) nói: “Xin Ông chớ bắt tội vì điều tôi đã lỡ trót quên và xin Ông chớ đừng nghiêm khắc cho việc (học hỏi) của Tôi.”');
INSERT INTO "vn_ruwwad" VALUES(18,74,'18:74','Rồi hai người họ lại tiếp tục lên đường, cho đến khi cả hai gặp được một cậu bé thì (Al-Khudhir) đã giết chết cậu bé đó. (Musa) nói: “Sao Ông lại giết một mạng người vô tội như vậy chứ? Quả thật Ông đã làm một điều quá ư là tội lỗi.”');
INSERT INTO "vn_ruwwad" VALUES(18,75,'18:75','(Al-Khudhir) nói: “Chẳng phải Ta đã bảo rằng Ngươi không bao giờ có thể kiên nhẫn được cùng với Ta sao?”');
INSERT INTO "vn_ruwwad" VALUES(18,76,'18:76','(Musa) nói: “Sau lần này, nếu Tôi còn thắc mắc bất cứ điều gì thì Ông hãy không cho tôi đi cùng Ông nữa, bởi lúc đó Ông chắc chắn có đủ lý do để không chấp nhận (lời khẩn xin) của Tôi.”');
INSERT INTO "vn_ruwwad" VALUES(18,77,'18:77','Vậy là hai người họ lại tiếp tục đi, mãi cho tới khi đến được một thị trấn, hai người hỏi xin thức ăn của thị dân nơi đó nhưng họ đã từ chối tiếp đãi hai người. Rồi hai người họ thấy trong thị trấn có một bức tường sắp ngã, (Al-Khudhair) đã liền dựng nó đứng thẳng trở lại. (Musa) lên tiếng: “Nếu muốn, Ông có thể đòi thị dân trả công về việc này mà!”');
INSERT INTO "vn_ruwwad" VALUES(18,78,'18:78','(Al-Khudhir) nói: “Đây là lúc Ta và Ngươi phải chia tay. Bây giờ Ta trình bày cho Ngươi biết ý nghĩa về những điều mà Ngươi đã không thể nhẫn nại.”');
INSERT INTO "vn_ruwwad" VALUES(18,79,'18:79','“Đối với con thuyền, đó là tài sản của những người nghèo lao động trên biển. Sở dĩ Ta muốn làm cho nó hư đi là bởi vì phía sau những người nghèo đó có một ông vua thường hay dùng cường quyền để chiếm đoạt tất cả các con thuyền (lành lặn và tốt).');
INSERT INTO "vn_ruwwad" VALUES(18,80,'18:80','“Còn đối với đứa trẻ, bởi vì cha mẹ của nó là những người có đức tin, ta sợ rằng sự thái quá và vô đức tin của nó (đối với Allah) sẽ khiến cha mẹ của nó phạm tội.”');
INSERT INTO "vn_ruwwad" VALUES(18,81,'18:81','“Vì thế, Ta mong rằng Thượng Đế của cha mẹ nó sẽ ban lại cho hai người họ một đứa con trai khác trong sạch và gần hơn với sự khoan dung (của Allah).”');
INSERT INTO "vn_ruwwad" VALUES(18,82,'18:82','“Riêng đối với bức tường, bởi vì đó là tài sản của hai đứa trẻ mồ côi trong thị trấn và bên dưới bức tường là một kho tàng của chúng (do cha chúng để lại cho chúng) và cha của chúng là một người ngoan đạo nên Thượng Đế của Ngươi muốn rằng khi hai đứa trẻ ấy trưởng thành chúng sẽ lấy kho tàng của chúng ra, như là một Hồng Ân của Thượng Đế của Ngươi. Và thực sự Ta đã không tự ý hành động những việc làm đó. Đó là ý nghĩa về những điều mà Ngươi đã không thể nhẫn nại được.”');
INSERT INTO "vn_ruwwad" VALUES(18,83,'18:83','Và họ hỏi Ngươi (Muhammad) về Zdul-Qarnain(. Ngươi hãy bảo họ: “Ta sẽ đọc cho các người nghe về một phần câu chuyện của Người.”');
INSERT INTO "vn_ruwwad" VALUES(18,84,'18:84','Quả thật, TA (Allah) đã để Y (Zdul-Qarnain) định cư trên trái đất và TA đã ban cho Y đầy đủ phương tiện cần thiết về mọi mặt.');
INSERT INTO "vn_ruwwad" VALUES(18,85,'18:85','Thế là Y dùng các phương tiện được ban cho ra đi (hướng về phía tây để thi hành điều công lý).');
INSERT INTO "vn_ruwwad" VALUES(18,86,'18:86','(Y ra đi) cho đến khi tới được một nơi ở hướng mặt trời lặn. Y thấy mặt trời như đang lặn xuống một dòng suối đục ngầu và Y bắt gặp một đám dân (vô đức tin) sống nơi đó. TA (Allah) phán bảo Y: “Này hỡi Zdul-Qarnain! (Ngươi được phép lựa chọn): hoặc ngươi trừng phạt họ hoặc ngươi đối xử nhân đạo với họ.”');
INSERT INTO "vn_ruwwad" VALUES(18,87,'18:87','(Zdul-Qarnain) bảo: “Đối với ai làm điều sai quấy thì Ta sẽ trừng trị y rồi y sẽ được đưa trở về trình diện Thượng Đế của y để Ngài trừng phạt y với hình phạt vô cùng khủng khiếp.”');
INSERT INTO "vn_ruwwad" VALUES(18,88,'18:88','“Còn đối với ai có đức tin và làm việc thiện tốt thì y sẽ được một phần thưởng tốt đẹp và Ta sẽ nói chuyện với y về mệnh lệnh của Ta bằng lời lẽ nhẹ nhàng và tử tế.”');
INSERT INTO "vn_ruwwad" VALUES(18,89,'18:89','Rồi Y (Zdul-Qurnain) lại đi hướng đến một nơi khác (về phía đông).');
INSERT INTO "vn_ruwwad" VALUES(18,90,'18:90','(Y ra đi) cho đến khi tới được một nơi ở hướng mặt trời mọc và Y thấy mặt trời mọc lên trên một đám dân mà TA (Allah) đã không ban cho họ bất kỳ một tấm phủ nào để che chắn (cái nóng) của nó (tức không nhà cửa, không cây cối).');
INSERT INTO "vn_ruwwad" VALUES(18,91,'18:91','Giống như thế, TA thực sự đã quán triệt mọi điều ở nơi Y.');
INSERT INTO "vn_ruwwad" VALUES(18,92,'18:92','Rồi Y lại đi hướng đến một nơi khác (ở giữa phía đông và phía tây).');
INSERT INTO "vn_ruwwad" VALUES(18,93,'18:93','(Y ra đi) cho đến khi tới được một nơi giữa hai ngọn núi và thấy gần hai ngọn núi có một đám dân hầu như không hiểu được tiếng nói (của người ngoài).');
INSERT INTO "vn_ruwwad" VALUES(18,94,'18:94','Họ nói: “Hỡi Zdul-Qamain! Quả thật, Yajuj và Majuj là những kẻ tàn phá trên trái đất. Liệu chúng tôi sẽ dâng lễ vật cho ngài để ngài giúp chúng tôi dựng lên một bức tường thành ngăn cách giữa chúng tôi với bọn họ được không?”');
INSERT INTO "vn_ruwwad" VALUES(18,95,'18:95','(Zdul-Qarnain) bảo: “Những gì mà Thượng Đế của Ta ban cho Ta còn tốt hơn. Do đó, các người chỉ cần giúp Ta sức lực thôi, Ta sẽ dựng một bức tường thành ngăn cách các người khỏi bọn họ.”');
INSERT INTO "vn_ruwwad" VALUES(18,96,'18:96','“Các người hãy mang đến cho Ta những thỏi sắt.” (Thế là họ mang thỏi sắt đến) cho tới khi lắp bằng chỗ trũng của hai sườn núi, Y (Zdul-Qurnain) bảo: “Các người hãy thổi.” Cho đến khi Y làm cho những thỏi sắt thành lửa, Y bảo: “Các người hãy mang đến cho Ta loại đồng nấu chảy để Ta đổ lên nó.”');
INSERT INTO "vn_ruwwad" VALUES(18,97,'18:97','Vậy là chúng (Ya’juj và Ma’juj) không thể trèo qua bức tường thành và cũng không thể đào thủng nó.');
INSERT INTO "vn_ruwwad" VALUES(18,98,'18:98','(Zdul-Qarnain) bảo: “Đây là hồng phúc từ Thượng Đế của Ta. Tuy nhiên, một khi Lời Hứa của Thượng Đế của Ta đã đến thì Ngài sẽ làm cho nó (bức tường thành) ngã đổ. Và Lời hứa của Thượng Đế của Ta là sự thật.”');
INSERT INTO "vn_ruwwad" VALUES(18,99,'18:99','Và vào Ngày hôm đó, TA (Allah) sẽ để cho họ nổi lên như làn sóng chồng lên nhau, và tiếng còi hụ sẽ được thổi lên và TA sẽ tập trung tất cả bọn họ.');
INSERT INTO "vn_ruwwad" VALUES(18,100,'18:100','Và vào Ngày hôm đó, TA sẽ phơi bày Hỏa Ngục cho những kẻ vô đức tin nhìn thấy tận mắt.');
INSERT INTO "vn_ruwwad" VALUES(18,101,'18:101','Những kẻ mà cặp mắt của họ bị che khỏi Lời Nhắc Nhở của TA, và họ đã không thể nghe được (Nó).');
INSERT INTO "vn_ruwwad" VALUES(18,102,'18:102','Phải chăng những kẻ vô đức tin nghĩ rằng họ được quyền nhận đám bầy tôi của TA làm những kẻ bảo hộ thay vì TA hay sao? Quả thật, TA đã chuẩn bị Hỏa Ngục làm chốn định cư cho những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(18,103,'18:103','Ngươi (Muhammad) hãy nói: “(Hỡi nhân loại), có phải các người muốn chúng tôi cho các người biết ai là những kẻ thất bại nhất trong việc làm và hành động ư?”');
INSERT INTO "vn_ruwwad" VALUES(18,104,'18:104','“(Đó là) những kẻ đã phí mất công sức nơi cuộc sống trần gian nhưng cứ tưởng rằng họ đang được điều tốt lành trong việc làm và hành động của họ.”');
INSERT INTO "vn_ruwwad" VALUES(18,105,'18:105','Những kẻ đó chính là những kẻ đã phủ nhận các dấu hiệu của Thượng Đế của họ cũng như đã phủ nhận việc gặp gỡ lại Ngài. Cho nên, việc làm của họ đã trở thành vô nghĩa. Vì thế vào Ngày Phục Sinh, TA (Allah) sẽ không trả cho họ bất cứ giá trị nào.');
INSERT INTO "vn_ruwwad" VALUES(18,106,'18:106','Phần thưởng Hỏa Ngục dành cho họ như thế là bởi vì họ đã vô đức tin và bởi vì họ đã chế nhạo các lời mặc khải cũng như các vị Sứ Giả của TA.');
INSERT INTO "vn_ruwwad" VALUES(18,107,'18:107','Quả thật, những ai có đức tin và hành thiện thì sẽ được ân thưởng các Ngôi Vườn Firdaws (nơi Thiên Đàng) làm chốn cư ngụ.');
INSERT INTO "vn_ruwwad" VALUES(18,108,'18:108','Họ sẽ sống trong đó đời đời và sẽ không mong chuyển đến nơi khác.');
INSERT INTO "vn_ruwwad" VALUES(18,109,'18:109','Ngươi (Muhammad) hãy nói: “Nếu lấy nước biển làm mực (để viết) những Lời Phán của Thượng Đế của Ta thì chắc chắn nước biển sẽ cạn hết trước khi dứt Lời Phán của Thượng Đế của Ta và dẫu cho chúng ta có mang đến những đại dương khác tương tự đi chăng nữa.”');
INSERT INTO "vn_ruwwad" VALUES(18,110,'18:110','Ngươi (Muhammad) hãy nói: “Ta thật ra cũng chỉ là một người phàm giống như các người nhưng Ta được mặc khải cho biết rằng quả thật Thượng Đế của các người chỉ có một Đấng Duy nhất. Bởi thế, ai mong được gặp gỡ Thượng Đế của y thì hãy làm việc thiện tốt và ngoan đạo; và chớ đừng dựng một ai (vật gì) làm đối tác của Ngài trong việc thờ phượng Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(19,1,'19:1','Kaf. Ha. Ya. ''Ain. Sad.');
INSERT INTO "vn_ruwwad" VALUES(19,2,'19:2','Nhắc lại hồng ân Thượng Đế của Ngươi được ban cho người bề tôi của Ngài, Zakariya(.');
INSERT INTO "vn_ruwwad" VALUES(19,3,'19:3','Khi Y âm thầm thành tâm khẩn nguyện Thượng Đế của Y.');
INSERT INTO "vn_ruwwad" VALUES(19,4,'19:4','Y khấn: “Lạy Thượng Đế của bề tôi! Quả thật, bề tôi đã yếu xương cốt, mái đầu thì đã bạc nhiều, nhưng lạy Thượng Đế, bề tôi vẫn không hề nản lòng trong việc cầu xin Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(19,5,'19:5','“Và bề tôi thật sự lo lắng cho dòng dõi của bề tôi sau khi bề tôi (chết đi) trong lúc vợ của bề tôi lại là một người phụ nữ hiếm muộn. Bởi thế, xin Ngài ban cho bề tôi một đứa con từ nơi Ngài để tiếp tục dòng dõi.”');
INSERT INTO "vn_ruwwad" VALUES(19,6,'19:6','“Nó sẽ kế thừa bề tôi và kế thừa dòng dõi của Ya’qub (Jacob), và lạy Thượng Đế của bề tôi, xin Ngài làm cho Nó thành người bề tôi được Ngài hài lòng.”');
INSERT INTO "vn_ruwwad" VALUES(19,7,'19:7','Này hỡi Zakariya! Quả thật, TA (Allah) báo tin mừng cho Ngươi về một đứa con trai; tên của Nó là Yahya, cái tên mà từ trước đến nay TA chưa hề đặt cho ai.');
INSERT INTO "vn_ruwwad" VALUES(19,8,'19:8','Y thưa: “Lạy Thượng Đế của bề tôi! Làm sao bề tôi có thể có con khi mà vợ của bề tôi là một người phụ nữ hiếm muộn, hơn nữa bề tôi đã tuổi già sức yếu”.');
INSERT INTO "vn_ruwwad" VALUES(19,9,'19:9','(Thiên Thần) nói: “Thượng Đế của Ngươi đã phán đúng như thế: Chuyện đó rất đơn giản đối với TA. Quả thật TA đã tạo ra Ngươi trước đây trong lúc Ngươi không là gì cả.”');
INSERT INTO "vn_ruwwad" VALUES(19,10,'19:10','(Zakariya) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài làm cho bề tôi một dấu hiệu (về tin mừng đó).” (Allah) phán: “Dấu hiệu của Ngươi sẽ là: Ngươi sẽ không nói chuyện được với mọi người trong ba đêm liền (mặc dầu Ngươi hãy còn khỏe mạnh bình thường).”');
INSERT INTO "vn_ruwwad" VALUES(19,11,'19:11','Rồi (Zakariya) bước ra khỏi phòng cầu nguyện đến gặp người dân của Y và ra dấu bảo họ tán dương (Allah) sáng chiều.');
INSERT INTO "vn_ruwwad" VALUES(19,12,'19:12','Này hỡi Yahya! Ngươi hãy nhận lấy Kinh Sách bằng sức mạnh, và TA đã ban cho Ngươi sự khôn ngoan mặc dầu Ngươi hãy còn nhỏ tuổi.');
INSERT INTO "vn_ruwwad" VALUES(19,13,'19:13','TA đã thương xót Y bằng lòng nhân từ nơi TA và thanh lọc Y trong sạch; và Y là một người ngay chính, ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(19,14,'19:14','Y hiếu thảo với cha mẹ và không ngạo mạn cũng không phản nghịch.');
INSERT INTO "vn_ruwwad" VALUES(19,15,'19:15','Và sự bình an được ban cho Y vào ngày Y sinh ra đời, vào ngày Y chết đi và vào ngày Y được phục sinh trở lại!');
INSERT INTO "vn_ruwwad" VALUES(19,16,'19:16','Và Ngươi (Muhammad) hãy nhớ lại trong Kinh Sách về Mar-yam khi Nữ rời gia đình đi lánh mặt tại một nơi về phía Đông.');
INSERT INTO "vn_ruwwad" VALUES(19,17,'19:17','Nữ che một bức màn để họ không nhìn thấy (trong lúc Nữ thờ phượng Thượng Đế của Nữ). Thế là TA cử Đại Thiên Thần Jibril của TA đến gặp Nữ. (Jibril) hiện ra trước mặt Nữ trên hình hài của một người đàn ông phàm tục toàn diện.');
INSERT INTO "vn_ruwwad" VALUES(19,18,'19:18','(Trước sự xuất hiện của người lạ), Nữ (lo sợ) nói: “Tôi cầu xin Đấng Độ Lượng che chở khỏi ông. (Xin ông chớ lại gần tôi) nếu ông thực sự là người ngoan đạo.”');
INSERT INTO "vn_ruwwad" VALUES(19,19,'19:19','(Jibril) liền bảo: “Ta thật ra là một vị Sứ Giả của Thượng Đế của Nữ, (Ta đến báo cho Nữ biết rằng Ngài) sẽ ban cho Nữ một đứa con trai thanh khiết.”');
INSERT INTO "vn_ruwwad" VALUES(19,20,'19:20','(Mar-yam không khỏi ngạc nhiên) nói: “Làm sao tôi có thể có được một đứa con trai khi mà chưa từng có một người phàm nào chạm đến người tôi. Hơn nữa, tôi đâu phải là một người phụ nữ hư đốn.”');
INSERT INTO "vn_ruwwad" VALUES(19,21,'19:21','(Jibril) bảo: “Thượng Đế của Nữ đã phán đúng như thế: Việc đó rất đơn giản đối với TA. Và chắc chắn TA sẽ làm cho đứa con trai đó thành một dấu lạ cho nhân loại và là một hồng ân từ nơi TA. Đó là điều đã được định đoạt.”');
INSERT INTO "vn_ruwwad" VALUES(19,22,'19:22','Thề rồi Nữ đã thụ thai và tạm lánh mặt ở một nơi xa.');
INSERT INTO "vn_ruwwad" VALUES(19,23,'19:23','Rồi những cơn đau đẻ đã đưa Nữ đến dưới một thân cây chà là. Nữ (rên rỉ:) “Phải chi mình chết phứt cho xong trước việc này và bị quên bẵng đi!”');
INSERT INTO "vn_ruwwad" VALUES(19,24,'19:24','(Lúc đó, bỗng có tiếng) gọi Nữ từ bên dưới bảo: “Mẹ chớ buồn phiền. Chắc chắn Thượng Đế của mẹ sẽ tạo một mạch nước bên dưới mẹ.”');
INSERT INTO "vn_ruwwad" VALUES(19,25,'19:25','“Và mẹ hãy lắc thân cây chà là về phía mẹ, trái chín tươi sẽ rụng xuống ngay chỗ mẹ.”');
INSERT INTO "vn_ruwwad" VALUES(19,26,'19:26','“Mẹ hãy ăn, hãy uống và hãy vui lên. Nhưng khi thấy một người phàm nào thì mẹ hãy nói: quả thật tôi đã nguyện nhịn chay với Đấng Ar-Rahman (Đấng Độ Lượng), cho nên ngày hôm nay tôi nhất định không nói chuyện với bất kỳ ai.”');
INSERT INTO "vn_ruwwad" VALUES(19,27,'19:27','Rồi Nữ ẳm đứa con về nhà gặp lại người dân của mình (sau khi đã hạ sinh). Thấy thế, người dân bảo: “Này Mar-yam! Cô thực sự đã mang về một điều quá ư là tồi tệ.”');
INSERT INTO "vn_ruwwad" VALUES(19,28,'19:28','“Này em gái của Harun! Cha của cô không phải là một kẻ xấu và mẹ của cô cũng chẳng phải là một phụ nữ hư đốn.”');
INSERT INTO "vn_ruwwad" VALUES(19,29,'19:29','Rồi (Mar-yam) lấy tay chỉ vào đứa bé. (Người dân) liền bảo: “Làm sao mà bọn ta có thể nói chuyện được với một đứa bé còn nằm trong nôi chứ?”');
INSERT INTO "vn_ruwwad" VALUES(19,30,'19:30','(Ngay lúc đó, đứa bé) lên tiếng: “Quả thật Ta là một người bề tôi của Allah. Ngài ban cho Ta Kinh Sách và chọn Ta làm một vị Nabi.”');
INSERT INTO "vn_ruwwad" VALUES(19,31,'19:31','“Ngài ban phúc cho Ta bất cứ nơi nào Ta có mặt và ra lệnh cho Ta phải dâng lễ nguyện Salah và đóng Zakah suốt thời gian Ta sống trên đời.”');
INSERT INTO "vn_ruwwad" VALUES(19,32,'19:32','“(Ngài ra lệnh cho Ta) phải hiếu thảo với mẹ của Ta; và Ngài không chỉ định Ta thành kẻ ngạo mạn và vô phúc.”');
INSERT INTO "vn_ruwwad" VALUES(19,33,'19:33','“Và sự bình an được ban cho Ta vào ngày Ta sinh ra đời, vào ngày Ta chết đi và vào ngày Ta được phục sinh trở lại!”');
INSERT INTO "vn_ruwwad" VALUES(19,34,'19:34','Đó là Ysa con trai của Mar-yam, lời nói thật mà (những kẻ lầm lạc) hãy còn hoài nghi.');
INSERT INTO "vn_ruwwad" VALUES(19,35,'19:35','(Thật không phù hợp với Quyền Năng Siêu Việt) của Allah khi cho rằng Ngài có một đứa con trai. Thật quang vinh và trong sạch thay Ngài! (Đối với Ngài), một khi Ngài định đoạt điều gì, Ngài chỉ cần phán với nó “Hãy thành!” thì nó sẽ lập tức thành (đúng như ý của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(19,36,'19:36','(Ysa bảo): “Quả thật, Allah là Thượng Đế của Ta và cũng là Thượng Đế của các người. Bởi vậy, các người hãy tôn thờ Ngài. Đây mới chính là con đường ngay thẳng (dẫn đến sự hài lòng của Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(19,37,'19:37','Tuy nhiên, những nhóm phái đã bất đồng với nhau (về sự việc của Ysa). Bởi thế, sẽ thật khốn khổ cho những kẻ vô đức tin khi chứng kiến (những gì xảy ra) trong Ngày Trọng Đại!');
INSERT INTO "vn_ruwwad" VALUES(19,38,'19:38','Vào Ngày mà họ sẽ đến trình diện TA, họ sẽ được cho nghe và thấy rõ (mọi thứ của Ngày Hôm Đó). Nhưng ngày hôm nay những kẻ làm điều sai quấy vẫn cứ (thản nhiên) công khai lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(19,39,'19:39','Ngươi (hỡi Muhammad), hãy cảnh báo họ về Ngày Hối Tiếc khi sự việc đã được quyết định trong lúc họ vẫn còn thờ ơ và không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(19,40,'19:40','Quả thật TA (Allah) sẽ thừa hưởng trái đất và những ai sống trên đó và họ sẽ được đưa trở về trình diện TA.');
INSERT INTO "vn_ruwwad" VALUES(19,41,'19:41','Và Ngươi (Muhammad) hãy nhớ lại trong Kinh Sách (Qur’an được ban xuống cho Ngươi) về Ibrahim. Y đích thực là một người tuyệt đối tin tưởng, một vị Nabi (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(19,42,'19:42','Khi Y nói với cha của mình: “Thưa cha! Tại sao cha lại thờ những vật không thể nghe, không thể thấy và cũng chẳng mang lợi về cho cha?”');
INSERT INTO "vn_ruwwad" VALUES(19,43,'19:43','“Thưa cha! Quả thật con đã lĩnh hội được kiến thức mà cha không có; thế nên, con xin cha hãy nghe con, con sẽ dẫn cha đến một con đường ngay thẳng.”');
INSERT INTO "vn_ruwwad" VALUES(19,44,'19:44','“Thưa cha! Cha đừng thờ cúng Shaytan nữa, quả thật, Shaytan chính là một tên phản nghịch đã từng chống đối Đấng Độ Lượng.”');
INSERT INTO "vn_ruwwad" VALUES(19,45,'19:45','“Thưa cha! Con lo sợ Đấng Độ Lượng sẽ trừng phạt cha để rồi cha trở thành bạn của Shaytan (cùng hội cùng thuyền với nó trong sự trừng phạt của Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(19,46,'19:46','(Cha của Ibrahim) bảo: “Này Ibrahim, hẳn mày ghét những thần linh của tao lắm thì phải! Nếu mày không dừng lại, tao sẽ ném đá giết chết mày; mày hãy mau cút đi đâu đó cho khuất mắt tao, hãy đi cho thật lâu (và đừng nói chuyện với tao nữa).”');
INSERT INTO "vn_ruwwad" VALUES(19,47,'19:47','(Ibrahim) nói: “Chào bằng an tạm biệt cha. Con sẽ cầu nguyện Thượng Đế của con tha thứ cho cha. Quả thật, Ngài Hằng Rộng Lượng với con.”');
INSERT INTO "vn_ruwwad" VALUES(19,48,'19:48','(Ibrahim) nói với người dân: “Tôi xin từ biệt các người và những kẻ mà các người cầu nguyện ngoài Allah; còn tôi sẽ chỉ cầu nguyện Thượng Đế của tôi; tôi hy vọng sẽ không đến nỗi vô phúc qua việc cầu nguyện Thượng Đế của tôi.”');
INSERT INTO "vn_ruwwad" VALUES(19,49,'19:49','Rồi khi Ibrahim đã từ biệt họ và những vật mà họ thờ phượng ngoài Allah, TA đã ban cho Y (một đứa con trai tên) Ishaq (Isaac) và (một đứa cháu trai tên) Ya’qub (Jacob); và cả hai đều đã được TA chọn làm Nabi.');
INSERT INTO "vn_ruwwad" VALUES(19,50,'19:50','Và TA đã ban hồng ân cho họ (con cháu của Ibrahim) và làm vinh danh họ qua tiếng nói trung thực.');
INSERT INTO "vn_ruwwad" VALUES(19,51,'19:51','Và Ngươi (Muhammad) hãy nhắc lại trong Kinh Sách (Qur’an được ban xuống cho Ngươi) về Musa. Quả thật, Y là một người thành tâm, là một Sứ Giả và là một Nabi.');
INSERT INTO "vn_ruwwad" VALUES(19,52,'19:52','TA đã gọi Y từ phía bên phải của ngọn núi và đưa Y xích lại gần để (TA) nói nhỏ.');
INSERT INTO "vn_ruwwad" VALUES(19,53,'19:53','TA đã ban thêm hồng phúc của TA cho Y bằng việc chọn người em trai của Y, Harun, làm một vị Nabi (phò tá cho Y).');
INSERT INTO "vn_ruwwad" VALUES(19,54,'19:54','Và Ngươi (Muhammad) hãy nhắc lại trong Kinh Sách (Qur’an được ban xuống cho Ngươi) về Isma''il. Quả thật, Y là một người trung thực, giữ lời hứa; và là một Sứ Giả, một vị Nabi.');
INSERT INTO "vn_ruwwad" VALUES(19,55,'19:55','Y đã thường bảo ban người nhà dâng lễ nguyện Salah và đóng Zakah, và Y là người được Thượng Đế của Y hài lòng.');
INSERT INTO "vn_ruwwad" VALUES(19,56,'19:56','Và Ngươi (Muhammad) hãy nhớ lại trong Kinh Sách (Qur’an được ban xuống cho Ngươi) về Idris (Enoch). Quả thật, Y là một người tin tưởng tuyệt đối, là một vị Nabi.');
INSERT INTO "vn_ruwwad" VALUES(19,57,'19:57','TA đã đưa Y lên một nơi trên cao.');
INSERT INTO "vn_ruwwad" VALUES(19,58,'19:58','Những người đó đều là những người đã được Allah ban ân trong số các vị Nabi xuất thân từ con cháu của Adam, từ những người đã được TA chuyên chở trên chiếc tàu cùng với Nuh, từ con cháu của Ibrahim, của Israel, và từ những người đã được TA hướng dẫn và chọn lọc. Khi những lời mặc khải của Đấng Độ Lượng được đọc lên cho họ nghe thì họ phủ phục quỳ lạy và bật khóc.');
INSERT INTO "vn_ruwwad" VALUES(19,59,'19:59','Nhưng thời sau họ là một hậu thế bỏ bê lễ nguyện Salah và chạy theo dục vọng thấp hèn, nên họ sẽ phải đối diện với Ghaiya (tên của thung lũng trong Hỏa Ngục).');
INSERT INTO "vn_ruwwad" VALUES(19,60,'19:60','Trừ những ai biết ăn năn sám hối, có đức tin, và làm việc thiện tốt thì sẽ vào Thiên Đàng và sẽ không bị đối xử bất công bất cứ điều gì.');
INSERT INTO "vn_ruwwad" VALUES(19,61,'19:61','Các Ngôi Vườn Thiên Đàng Vĩnh Cửu mà Đấng Độ Lượng đã hứa với các bề tôi của Ngài tuy vô hình nhưng Lời Hứa của Ngài là điều chắc chắn sẽ được thể hiện.');
INSERT INTO "vn_ruwwad" VALUES(19,62,'19:62','Trong Thiên Đàng, họ sẽ không còn nghe thấy bất cứ lời lẽ khiếm nhã và thô tục nào mà toàn chỉ là những lời chúc tụng “Bằng an”. Và trong đó, họ sẽ được ban cho đầy đủ bổng lộc cả sáng lẫn chiều.');
INSERT INTO "vn_ruwwad" VALUES(19,63,'19:63','Đó là Thiên Đàng, nơi mà TA sẽ cho những người ngoan đạo trong đám bề tôi của TA thừa hưởng.');
INSERT INTO "vn_ruwwad" VALUES(19,64,'19:64','(Này Jibril, Ngươi hãy nói với Muhammad): “Chúng tôi (các Thiên Thần) chỉ xuống trần theo Mệnh Lệnh của Thượng Đế của Ngươi. Điều gì trước chúng tôi và điều gì sau chúng tôi và cả những gì ở giữa hai phần đó đều thuộc quyền định đoạt của Ngài. Và Thượng Đế của Ngươi không hề quên (bất cứ điều gì).”');
INSERT INTO "vn_ruwwad" VALUES(19,65,'19:65','Ngài là Thượng Đế của các tầng trời, trái đất và những gì giữa chúng. Bởi thế, các ngươi hãy thờ phượng Ngài, và các ngươi hãy kiên nhẫn trong việc thờ phượng Ngài. Quả thật, không có một ai (vật gì) sánh vai với Ngài (trong thờ phượng).');
INSERT INTO "vn_ruwwad" VALUES(19,66,'19:66','Và con người (vô đức tin nơi sự phục sinh) lên tiếng (bỡn cợt): “Chẳng lẽ khi chết rồi tôi sẽ được dựng sống lại thật ư?”');
INSERT INTO "vn_ruwwad" VALUES(19,67,'19:67','Chẳng lẽ con người không nhớ rằng chính TA đã tạo hóa ra y trước đây trong lúc y không là gì cả đó sao?');
INSERT INTO "vn_ruwwad" VALUES(19,68,'19:68','Thề bởi Thượng Đế của Ngươi (hỡi Muhammad), chắc chắn TA sẽ triệu tập họ lại và cả những tên Shaytan; rồi TA sẽ mang họ ra quỳ gối xung quanh Hỏa Ngục (một cách nhục nhã).');
INSERT INTO "vn_ruwwad" VALUES(19,69,'19:69','Rồi TA sẽ lôi cổ từng tên cầm đầu bướng bỉnh nhất từ trong mỗi nhóm phái đã chống đối Đấng Độ Lượng (ra trình diện TA).');
INSERT INTO "vn_ruwwad" VALUES(19,70,'19:70','Rồi TA là Đấng biết rõ nhất ai trong bọn họ là kẻ đáng bị nướng (trong Hỏa Ngục).');
INSERT INTO "vn_ruwwad" VALUES(19,71,'19:71','Và chắc chắn không ai trong các ngươi (hỡi nhân loại) tránh khỏi việc đi qua chiếc cầu Sirat (được bắt ngang qua Hỏa Ngục). Quyết định của Thượng Đế của Ngươi chắc chắn sẽ phải được thể hiện.');
INSERT INTO "vn_ruwwad" VALUES(19,72,'19:72','Rồi TA sẽ cứu rỗi những ai ngoan đạo và bỏ mặc những kẻ làm điều sai quấy quỳ gối trong đó (Hỏa Ngục).');
INSERT INTO "vn_ruwwad" VALUES(19,73,'19:73','Và khi những Lời Mặc Khải Minh Bạch của TA được đọc cho họ nghe, những kẻ vô đức tin bảo những người có đức tin: “Trong hai nhóm chúng ta, nhóm nào là nhóm có địa vị tốt và đáng kính trọng nhất (trong thành phố Makkah này)?”');
INSERT INTO "vn_ruwwad" VALUES(19,74,'19:74','Trước họ đã có không biết bao thế hệ giàu có và hào nhoáng hơn đã bị TA hủy diệt.');
INSERT INTO "vn_ruwwad" VALUES(19,75,'19:75','Ngươi (Muhammad) hãy nói: “Những ai lầm lạc, Đấng Độ Lượng sẽ mở rộng (sự lầm lạc) thêm cho họ mãi tới khi họ nhìn thấy điều mà họ đã được hứa, hoặc sẽ là hình phạt hoặc sẽ là Giờ Khắc Tận thế; lúc đó họ sẽ biết ai là kẻ có địa vị tồi tệ nhất và có quân lực yếu nhất.”');
INSERT INTO "vn_ruwwad" VALUES(19,76,'19:76','Allah sẽ ban thêm nguồn chỉ đạo cho những ai được hướng dẫn đúng đường. Và những việc làm thiện tốt sẽ mang lại phần thưởng tốt đẹp nhất cũng như sẽ mang lại kết quả phúc lành nhất ở nơi Thượng Đế của Ngươi (Muhammad).');
INSERT INTO "vn_ruwwad" VALUES(19,77,'19:77','(Chắc có lẽ) Ngươi (hỡi Muhammad,) đã thấy kẻ vô đức tin nơi những Lời Mặc Khải của TA khẳng định: “Chắc chắn tôi sẽ được ban cấp của cải và con cái (sau khi được phục sinh)”.');
INSERT INTO "vn_ruwwad" VALUES(19,78,'19:78','Há y đã nhìn thấu điều vô hình hay phải chẳng y đã nhận được một Lời Cam Kết nào đó từ nơi Đấng Độ Lượng?');
INSERT INTO "vn_ruwwad" VALUES(19,79,'19:79','Sự việc chắc chắn không phải như thế! TA sẽ cho ghi sổ điều mà y nói và sẽ tăng thêm hình phạt đối với y.');
INSERT INTO "vn_ruwwad" VALUES(19,80,'19:80','TA sẽ thừa kế y về những gì y nói và y sẽ phải đến trình diện TA đơn độc một mình.');
INSERT INTO "vn_ruwwad" VALUES(19,81,'19:81','Họ (những kẻ đa thần) đã nhận lấy các thần linh khác ngoài Allah mục đích nhờ chúng phù hộ cho họ có được quyền uy và thế lực.');
INSERT INTO "vn_ruwwad" VALUES(19,82,'19:82','Sự việc chắc chắn không như họ đã tưởng! Những thần linh đó sẽ phủ nhận sự thờ phượng của họ và chúng sẽ trở thành những kẻ chống lại họ.');
INSERT INTO "vn_ruwwad" VALUES(19,83,'19:83','Chẳng lẽ Ngươi (Muhammad) không thấy việc TA đã gửi những tên Shaytan đến với những kẻ vô đức tin hầu xúi giục họ làm điều phản nghịch hay sao?');
INSERT INTO "vn_ruwwad" VALUES(19,84,'19:84','Cho nên, Ngươi (Muhammad) chớ vội (cầu mong sự trừng phạt) giáng lên họ. Thật ra TA chỉ gia hạn cho họ một số ngày nhất định mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(19,85,'19:85','Vào Ngày mà TA sẽ triệu tập những người ngoan đạo thành một phái đoàn đến trình diện Đấng Độ Lượng.');
INSERT INTO "vn_ruwwad" VALUES(19,86,'19:86','TA sẽ lùa những kẻ tội lỗi đến Hỏa Ngục như một đàn thú.');
INSERT INTO "vn_ruwwad" VALUES(19,87,'19:87','Họ không có quyền can thiệp cho nhau ngoại trừ người nào đã nhận từ Đấng Độ Lượng sự Giao Ước (đức tin nơi Ngài và nơi vị Thiên Sứ của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(19,88,'19:88','Và họ (Do Thái, Thiên Chúa và một số kẻ đa thần) nói: “Đấng Độ Lượng đã có một đứa con trai.”');
INSERT INTO "vn_ruwwad" VALUES(19,89,'19:89','Quả thật các ngươi (hỡi những kẻ đã nói lời đó) đã mang đến một điều sàm bậy quá mức.');
INSERT INTO "vn_ruwwad" VALUES(19,90,'19:90','Các tầng trời gần như muốn vỡ tung, trái đất gần như muốn nứt ra làm hai và những quả núi gần như muốn sụp đổ từ lời nói đó.');
INSERT INTO "vn_ruwwad" VALUES(19,91,'19:91','Họ đã bịa đặt cho Đấng Độ Lượng có con trai.');
INSERT INTO "vn_ruwwad" VALUES(19,92,'19:92','Và thật không hợp lẽ chút nào khi cho rằng Đấng Độ Lượng có một đứa con trai.');
INSERT INTO "vn_ruwwad" VALUES(19,93,'19:93','Bởi quả thật tất cả những ai trong các tầng trời và trái đất đều đến trình diện Đấng Độ Lượng như một người bề tôi.');
INSERT INTO "vn_ruwwad" VALUES(19,94,'19:94','Quả thật, Ngài đã kiểm kê và đánh số họ một cách chính xác, (không điều gì của họ che giấu được Ngài).');
INSERT INTO "vn_ruwwad" VALUES(19,95,'19:95','Và vào Ngày Phục Sinh, từng người đơn lẻ đến trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(19,96,'19:96','Quả thật, những người có đức tin và làm điều thiện tốt sẽ được Đấng Độ Lượng yêu thương.');
INSERT INTO "vn_ruwwad" VALUES(19,97,'19:97','Quả thật, TA làm cho Nó (Qur’an) thành dễ dàng với chiếc lưỡi của Ngươi (Muhammad) là để Ngươi dùng Nó báo tin mừng cho những người ngoan đạo và cảnh báo đám người hay tranh cãi (hầu phủ nhận sự thật).');
INSERT INTO "vn_ruwwad" VALUES(19,98,'19:98','Đã có không biết bao thế hệ trước họ đã bị TA hủy diệt. Chắc chắn Ngươi không tìm thấy một người nào của bọn họ hoặc nghe một lời xầm xì nào của họ.');
INSERT INTO "vn_ruwwad" VALUES(20,1,'20:1','Ta. Ha.');
INSERT INTO "vn_ruwwad" VALUES(20,2,'20:2','(Hỡi Muhammad), TA (Allah) ban Qur’an xuống cho Ngươi không phải để làm Ngươi buồn khổ.');
INSERT INTO "vn_ruwwad" VALUES(20,3,'20:3','Mà để làm lời nhắc nhở cho những ai biết sợ (TA).');
INSERT INTO "vn_ruwwad" VALUES(20,4,'20:4','Nó được Mặc Khải từ Đấng đã tạo hóa trái đất và các tầng trời cao.');
INSERT INTO "vn_ruwwad" VALUES(20,5,'20:5','(Đó là) Đấng Độ Lượng an vị trên chiếc Ngai Vương.');
INSERT INTO "vn_ruwwad" VALUES(20,6,'20:6','Đấng mà mọi vật trong các tầng trời và mọi vật trên trái đất cũng như vạn vật nằm giữa chúng và những gì nằm sâu dưới lòng đất đều là của Ngài.');
INSERT INTO "vn_ruwwad" VALUES(20,7,'20:7','Dẫu Ngươi (Muhammad) có nói lớn tiếng (hay nhỏ tiếng) thì Ngài vẫn luôn biết rõ những điều bí mật và thầm kín.');
INSERT INTO "vn_ruwwad" VALUES(20,8,'20:8','Allah là Đấng mà Không có Thượng Đế đích thực nào khác ngoài Ngài. Ngài mang những tên gọi tốt đẹp nhất.');
INSERT INTO "vn_ruwwad" VALUES(20,9,'20:9','(Hỡi Thiên Sứ Muhammad), Ngươi có biết về câu chuyện của Musa (Moses) không?');
INSERT INTO "vn_ruwwad" VALUES(20,10,'20:10','Khi (Musa) nhìn thấy một ngọn lửa (ở đằng xa), Y bảo gia đình của mình: “Mọi người hãy tạm dừng chân ở đây. Ta sẽ đến chỗ ngọn lửa biết đâu có thể mang về cho mọi người một cục than hồng hoặc biết đâu có thể tìm thấy nơi đống lửa đó một nguồn Chỉ Đạo.”');
INSERT INTO "vn_ruwwad" VALUES(20,11,'20:11','Nhưng khi Y tiến đến ngọn lửa thì bỗng có tiếng gọi: “Này hỡi Musa!”');
INSERT INTO "vn_ruwwad" VALUES(20,12,'20:12','“Quả thật TA là Thượng Đế của Ngươi. Nào, Ngươi hãy cởi giày của Ngươi ra bởi đích thực Ngươi đang ở nơi thung lũng Tuwa linh thiêng.”');
INSERT INTO "vn_ruwwad" VALUES(20,13,'20:13','“Và TA đã chọn Ngươi. Vì vậy, Ngươi hãy lắng nghe điều mặc khải.”');
INSERT INTO "vn_ruwwad" VALUES(20,14,'20:14','“Quả thật, TA là Allah. Không có Thượng Đế đích thực nào khác ngoài TA cả. Cho nên, Ngươi hãy thờ phụng TA và hãy dâng lễ nguyện Salah để tưởng nhớ TA.”');
INSERT INTO "vn_ruwwad" VALUES(20,15,'20:15','“Quả thật Giờ Tận Thế đang tiến đến nhưng TA giữ kín nó để cho mỗi linh hồn sẽ được ân thưởng tùy theo sự nỗ lực và phấn đấu của nó.”');
INSERT INTO "vn_ruwwad" VALUES(20,16,'20:16','“Vì vậy, Ngươi chớ để cho ai không có đức tin nơi Giờ Tận Thế và theo đuổi dục vọng của bản thân làm cho Ngươi xao lãng nó để rồi Ngươi sẽ bị hủy diệt.”');
INSERT INTO "vn_ruwwad" VALUES(20,17,'20:17','(Allah phán bảo Musa:) “Vật gì trong tay phải của Ngươi vậy, hỡi Musa?”');
INSERT INTO "vn_ruwwad" VALUES(20,18,'20:18','(Musa) nói: “Thưa, đó là chiếc gậy của bề tôi, bề tôi thường dùng nó để chống khi đi lại, bề tôi cũng dùng nó để đập rụng lá cây cho đàn cừu của bề tôi và bề tôi dùng nó cho nhiều việc khác nữa.”');
INSERT INTO "vn_ruwwad" VALUES(20,19,'20:19','(Allah) phán bảo: Này Musa, Ngươi hãy ném chiếc gậy của Ngươi xuống!');
INSERT INTO "vn_ruwwad" VALUES(20,20,'20:20','Musa ném chiếc gậy xuống, nó liền biến thành một con rắn bò nhanh nhẹn.');
INSERT INTO "vn_ruwwad" VALUES(20,21,'20:21','(Allah) phán bảo (Musa): “Ngươi hãy cầm lấy nó và đừng sợ. TA sẽ làm cho nó trở lại nguyên trạng ban đầu.”');
INSERT INTO "vn_ruwwad" VALUES(20,22,'20:22','“Giờ Ngươi hãy áp bàn tay của Ngươi sát vào ba sườn của Ngươi, nó sẽ hiện ra trắng toát, không có gì đau đớn cả. (Đó là) một phép lạ khác nữa (mà TA ban cho Ngươi).”');
INSERT INTO "vn_ruwwad" VALUES(20,23,'20:23','“TA muốn trưng cho Ngươi thấy một số phép mầu vĩ đại của TA.”');
INSERT INTO "vn_ruwwad" VALUES(20,24,'20:24','“Ngươi hãy đi gặp Fir’awn (Pha-ra-ông). Hắn thực sự đã ngông cuồng quá mức.”');
INSERT INTO "vn_ruwwad" VALUES(20,25,'20:25','(Musa) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài nới rộng lòng ngực của bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(20,26,'20:26','“Xin Ngài làm cho công việc của bề tôi được thuận lợi.”');
INSERT INTO "vn_ruwwad" VALUES(20,27,'20:27','“Xin Ngài tháo cái gút (ngọng) ra khỏi chiếc lưỡi của bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(20,28,'20:28','“Để họ có thể hiểu được lời nói của bề tôi (khi bề tôi truyền đạt).”');
INSERT INTO "vn_ruwwad" VALUES(20,29,'20:29','“Và xin Ngài chỉ định từ gia đình của bề tôi một người phò tá cho bề tôi (trong công việc này).”');
INSERT INTO "vn_ruwwad" VALUES(20,30,'20:30','“Harun, người anh em của bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(20,31,'20:31','“Xin Ngài tăng cường sức mạnh cho bề tôi qua người anh em của bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(20,32,'20:32','“Và xin Ngài hãy để người anh em của bề tôi chia sẻ công việc cùng với bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(20,33,'20:33','“Để cả hai bề tôi có thể tán dương Ngài thật nhiều.”');
INSERT INTO "vn_ruwwad" VALUES(20,34,'20:34','“Cũng như để tưởng nhớ Ngài thật nhiều.”');
INSERT INTO "vn_ruwwad" VALUES(20,35,'20:35','“Quả thật Ngài luôn quan sát thấy bầy tôi.”');
INSERT INTO "vn_ruwwad" VALUES(20,36,'20:36','(Allah) phán: “Hỡi Musa, quả thật những điều Ngươi yêu cầu đã được chấp thuận.”');
INSERT INTO "vn_ruwwad" VALUES(20,37,'20:37','“Quả thật, TA (Allah) đã ban ân huệ cho Ngươi (Musa) trong một lần khác.”');
INSERT INTO "vn_ruwwad" VALUES(20,38,'20:38','“Khi TA mặc khải cho mẹ của Ngươi những gì được mặc khải (về việc bảo vệ Ngươi khỏi kế hoạch giết chóc của Fir’awn):”');
INSERT INTO "vn_ruwwad" VALUES(20,39,'20:39','“Ngươi hãy đặt nó (Musa) vào chiếc rương và hãy thả nó xuống sông; dòng sông sẽ đẩy nó dạt vào bờ; kẻ thù của TA và cũng là kẻ thù của nó (Fir’awn) sẽ vớt lấy nó.” TA đã ban cho Ngươi (Musa) tình thương từ nơi TA và để Ngươi được nuôi dưỡng dưới mắt của TA.');
INSERT INTO "vn_ruwwad" VALUES(20,40,'20:40','“Khi người chị của Ngươi lẽo đẽo đi theo và nói: Quí ngài có muốn tôi chỉ cho quí ngài một người chăm sóc tốt cho đứa bé không? Thế là TA đã mang Ngươi trả lại cho mẹ của Ngươi để đôi mắt của mẹ Ngươi trở lại vui tươi và không buồn lo nữa. Và Ngươi đã giết chết một người nhưng TA đã cứu Ngươi ra khỏi cảnh nguy khốn đó và TA đã thử thách Ngươi với một sự thử thách lớn. Ngươi đã phải sống lưu vong nhiều năm cùng với người dân Madyan, rồi (giờ đây) Ngươi đến đây như đã định, hỡi Musa!”');
INSERT INTO "vn_ruwwad" VALUES(20,41,'20:41','“TA đã chọn Ngươi cho công việc rao truyền Bức Thông Điệp của TA.”');
INSERT INTO "vn_ruwwad" VALUES(20,42,'20:42','“(Này Musa), Ngươi và người anh em của Ngươi hãy lên đường mang theo các phép lạ của TA, và cả hai chớ đừng xao lãng việc tưởng nhớ TA.”');
INSERT INTO "vn_ruwwad" VALUES(20,43,'20:43','“Hai ngươi hãy đi gặp Fir’awn. Quả thật, hắn đã ngông cuồng quá mức.”');
INSERT INTO "vn_ruwwad" VALUES(20,44,'20:44','“Tuy nhiên, hai ngươi hãy ăn nói với hắn bằng lời lẽ nhã nhặn mong rằng hắn sẽ lưu ý đến lời cảnh tỉnh hoặc sẽ biết kính sợ.”');
INSERT INTO "vn_ruwwad" VALUES(20,45,'20:45','Cả hai (Musa và Harun) thưa: “Lạy Thượng Đế của bầy tôi! Quả thật, bầy tôi sợ rằng y (Fir’awn) sẽ có hành động quá quắt hoặc tàn bạo với bầy tôi.”');
INSERT INTO "vn_ruwwad" VALUES(20,46,'20:46','(Allah) phán: “Hai ngươi đừng sợ! Quả thật TA luôn ở cùng với hai ngươi. TA nghe và thấy (hết mọi việc).”');
INSERT INTO "vn_ruwwad" VALUES(20,47,'20:47','“Nào, hai ngươi hãy đi gặp hắn và bảo: Quả thật chúng tôi là Sứ Giả của Thượng Đế của ngài. Xin ngài hãy để cho con cháu của Israel ra đi với chúng tôi và xin ngài đừng trừng phạt họ (bằng việc tàn sát những đứa con trai của họ). Chúng tôi thực sự đến gặp ngài với một phép lạ từ Thượng Đế của ngài. Và sự bằng an đến với ai đi theo chỉ đạo.”');
INSERT INTO "vn_ruwwad" VALUES(20,48,'20:48','“Chúng tôi thực sự đã được mặc khải cho biết rằng sự trừng phạt sẽ giáng xuống những ai phủ nhận và quay lưng (với những gì mà các vị Thiên Sứ của Ngài mang đến).”');
INSERT INTO "vn_ruwwad" VALUES(20,49,'20:49','(Fir’awn) nói: “Thế ai là Thượng Đế của hai ngươi, hỡi Musa?”');
INSERT INTO "vn_ruwwad" VALUES(20,50,'20:50','Musa đáp: Thượng Đế của chúng tôi là Đấng đã ban cho vạn vật hình thể rồi hướng dẫn.');
INSERT INTO "vn_ruwwad" VALUES(20,51,'20:51','(Fir’awn) bảo: “Thế đối với những người (phủ nhận) thuộc thế hệ trước thì sao?”');
INSERT INTO "vn_ruwwad" VALUES(20,52,'20:52','(Musa) đáp: “Sự việc đó thuộc về kiến thức ở nơi Thượng Đế của Tôi được ghi chép trong Kinh Sách được lưu trữ ở nơi Ngài; Thượng Đế của Tôi không nhầm lẫn cũng không hề quên.”');
INSERT INTO "vn_ruwwad" VALUES(20,53,'20:53','(Ngài là) Đấng đã làm trái đất thành một nơi an cư cho các ngươi, Đấng đã mở ra cho các ngươi những con đường thông thương qua lại trên trái đất và đã ban nước mưa từ trên trời xuống cho các ngươi; TA dùng nó để làm mọc ra đủ loại cây cối và thảo mộc, từng cặp khác nhau.');
INSERT INTO "vn_ruwwad" VALUES(20,54,'20:54','Các ngươi hãy ăn và nuôi gia súc của các ngươi. Quả thật, trong sự việc đó là các dấu hiệu cho những người thông hiểu.');
INSERT INTO "vn_ruwwad" VALUES(20,55,'20:55','Từ đất bụi TA đã tạo hóa các ngươi, TA sẽ hoàn các ngươi trở lại thành đất bụi và cũng từ đất bụi TA sẽ đưa các ngươi trở ra một lần nữa.');
INSERT INTO "vn_ruwwad" VALUES(20,56,'20:56','Và quả thật TA đã phơi bày cho hắn (Fir’awn) thấy tất cả phép lạ và bằng chứng của TA nhưng hắn đã phủ nhận và chối bỏ.');
INSERT INTO "vn_ruwwad" VALUES(20,57,'20:57','Hắn bảo: “Này Musa, có phải ngươi mang các pháp thuật này đến với ý định muốn đuổi bọn ta ra khỏi lãnh thổ của bọn ta đúng không?”');
INSERT INTO "vn_ruwwad" VALUES(20,58,'20:58','“Bọn ta cũng có thể mang đến pháp thuật tương tự để so tài với Ngươi. Nào, Ngươi hãy định một điểm hẹn tại một nơi cho bọn ta và Ngươi, bọn ta và Ngươi chớ đừng lỡ hẹn!”');
INSERT INTO "vn_ruwwad" VALUES(20,59,'20:59','(Musa) nói: “Hẹn quí ngài vào Ngày Đại Hội và quí ngài hãy triệu tập dân chúng vào lúc mặt trời đã lên cao khoảng một sào.”');
INSERT INTO "vn_ruwwad" VALUES(20,60,'20:60','Vậy là Fir’awn lui về, tính toán kế hoạch rồi đến nơi hẹn.');
INSERT INTO "vn_ruwwad" VALUES(20,61,'20:61','Musa bảo họ (những pháp sư của Fir’awn): “Quí ngài hãy coi chừng, chớ đặt điều nói dối rồi đổ thừa cho Allah sợ rằng Ngài sẽ tiêu diệt quí ngài bằng một hình phạt. Quả thật kẻ ngụy tạo chắc chắn sẽ thất bại.”');
INSERT INTO "vn_ruwwad" VALUES(20,62,'20:62','Rồi họ bàn cãi với nhau về công việc của họ và giấu kín cuộc mật nghị.');
INSERT INTO "vn_ruwwad" VALUES(20,63,'20:63','Họ bảo nhau: “Hai tên này đúng thật là hai tên phù thủy, chúng muốn dùng pháp thuật để đuổi các người ra khỏi lãnh thổ của các người và chúng muốn xóa bỏ lối sống cao quý của các người.”');
INSERT INTO "vn_ruwwad" VALUES(20,64,'20:64','“Bởi thế, các người hãy hợp sức của các người lại trong kế hoạch rồi tiến lên sắp xếp theo đội ngũ chỉnh tề. Hôm nay, ai cao tay hơn chắc chắn sẽ là kẻ chiến thắng.”');
INSERT INTO "vn_ruwwad" VALUES(20,65,'20:65','(Những tên pháp sư của Fir’awn) nói (với Musa): “Này Musa, ngươi ném xuống trước hay bọn ta là người đầu tiên ném xuống?”');
INSERT INTO "vn_ruwwad" VALUES(20,66,'20:66','(Musa) đáp: “Không, quí vị hãy ném xuống trước.” Thế là bằng pháp thuật, họ đã gây ảo giác làm cho Musa nhìn thấy những sợi dây cũng như những chiếc gậy của họ đang cử động.');
INSERT INTO "vn_ruwwad" VALUES(20,67,'20:67','Musa đâm ra sợ hãi trong lòng trước (những gì họ thể hiện).');
INSERT INTO "vn_ruwwad" VALUES(20,68,'20:68','TA (Allah) phán bảo (Musa): “Ngươi đừng sợ! Chắc chắn Ngươi cao tay hơn.”');
INSERT INTO "vn_ruwwad" VALUES(20,69,'20:69','“Ngươi hãy ném chiếc gậy trong tay phải của ngươi xuống, nó sẽ nuốt hết những vật mà họ đã làm ra. Quả thật, vật mà họ đã làm chẳng qua chỉ là trò ảo thuật. Và nhà ảo thuật sẽ không thành công dù từ đâu đến đi chăng nữa.”');
INSERT INTO "vn_ruwwad" VALUES(20,70,'20:70','Thế rồi những tên pháp sư hạ mình xuống phủ phục. Họ nói: “Chúng tôi tin tưởng nơi Thượng Đế của Harun và Musa.”');
INSERT INTO "vn_ruwwad" VALUES(20,71,'20:71','(Thấy thế, Fir’awn giận dữ) quát: “Các ngươi bạo gan dám tin nơi hắn trước khi ta cho phép các ngươi ư? Chính hắn là tên cầm đầu của các ngươi nên hắn đã dạy các ngươi pháp thuật! Chắc chắn ta sẽ cho chặt tay và chân của các ngươi mỗi bên một cái; chắc chắn ta sẽ đóng đinh các ngươi trên thân cây chà là để các ngươi biết ai trong hai bên sẽ trừng phạt dữ dội và lâu hơn.”');
INSERT INTO "vn_ruwwad" VALUES(20,72,'20:72','(Các tên pháp sư của Fir’awn) đáp: “(Giờ) chúng tôi sẽ không bao giờ xem ngài trọng hơn những bằng chứng rõ rệt đã đến với chúng tôi và cũng không xem ngài trọng hơn Đấng đã sáng tạo ra chúng tôi. Cho nên, ngài cứ quyết định làm điều gì ngài muốn; bởi quả thật, ngài cũng chỉ có thể định đoạt điều gì liên quan đến đời sống trần tục này mà thôi!”');
INSERT INTO "vn_ruwwad" VALUES(20,73,'20:73','“Quả thật, chúng tôi đã tin nơi Thượng Đế của chúng tôi. Chúng tôi mong rằng Ngài sẽ tha thứ cho chúng tôi về tội lỗi của chúng tôi và những hành vi phù phép của trò ảo thuật mà ngài đã bắt ép chúng tôi làm. Bởi vì Allah là Đấng Ưu Việt và Vĩnh Cửu.”');
INSERT INTO "vn_ruwwad" VALUES(20,74,'20:74','Quả thật, người nào đến trình diện Thượng Đế của y như một kẻ tội lỗi thì chắc chắn Hỏa Ngục sẽ dành cho y, y sẽ không thể chết và cũng sẽ không thể sống trong đó.');
INSERT INTO "vn_ruwwad" VALUES(20,75,'20:75','Còn người nào đến trình diện Ngài như một người có đức tin và hành thiện thì họ sẽ là những người được ban thưởng cấp bậc ưu hạng.');
INSERT INTO "vn_ruwwad" VALUES(20,76,'20:76','Những Ngôi Vườn Thiên Đàng Vĩnh Cửu, bên dưới có các dòng sông chảy. Họ sẽ sống mãi trong đó. Và đó là phần thưởng của những ai đã thanh lọc bản thân mình (khỏi vết bẩn của tội lỗi).');
INSERT INTO "vn_ruwwad" VALUES(20,77,'20:77','Quả thật, TA đã mặc khải cho Musa: “Ngươi hãy đưa các bầy tôi của TA ra đi trong đêm và Ngươi hãy đánh (chiếc gậy của Ngươi xuống) để làm thành một con đường khô ráo cho họ băng qua biển. Ngươi chớ sợ bị đuổi kịp và cũng chớ lo (bị chết chìm).”');
INSERT INTO "vn_ruwwad" VALUES(20,78,'20:78','Rồi Fir’awn cùng quân lính của hắn đuổi theo họ (Musa và người dân Israel) nhưng sóng biển đã bao phủ và nhấn chìm toàn bộ (Fir’awn và quân lính của hắn).');
INSERT INTO "vn_ruwwad" VALUES(20,79,'20:79','Fir’awn đã đưa dân chúng của hắn lạc lối và đã không hướng dẫn họ.');
INSERT INTO "vn_ruwwad" VALUES(20,80,'20:80','Hỡi dân Israel! TA (Allah) đã giải cứu các ngươi khỏi kẻ thù của các ngươi, TA đã giao ước với các ngươi ở phía bên phải của ngọn núi At-Tur, và TA đã ban Al-man (loại thức uống như mật ong) và chim cút xuống cho các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(20,81,'20:81','Các ngươi hãy dùng các món thực phẩm tốt lành mà TA đã ban cấp cho các ngươi và các ngươi chớ quá mức trong sự việc đó để rồi cơn giận của TA sẽ giáng lên các ngươi; và người nào mà cơn giận của TA giáng nhằm phải y thì chắc chắn y sẽ bị diệt vong.');
INSERT INTO "vn_ruwwad" VALUES(20,82,'20:82','Nhưng TA chắc chắn sẽ tha thứ cho những ai biết ăn năn sám hối và có đức tin đồng thời làm việc thiện tốt rồi theo đúng sự hướng dẫn.');
INSERT INTO "vn_ruwwad" VALUES(20,83,'20:83','(Allah phán bảo khi Musa đến): “Điều gì làm Ngươi vội vã đến đây trước người dân của Ngươi, hỡi Musa?”');
INSERT INTO "vn_ruwwad" VALUES(20,84,'20:84','(Musa) thưa: “Lạy Thượng Đế của bề tôi, họ theo sát dấu chân của bề tôi; bề tôi nhanh chóng đến trình diện Ngài để mong Ngài hài lòng.”');
INSERT INTO "vn_ruwwad" VALUES(20,85,'20:85','(Allah) phán: “Quả thật, TA đã thử thách đám dân của Ngươi sau khi Ngươi ra đi bỏ lại họ, và tên Samiri đã dắt họ lạc lối.”');
INSERT INTO "vn_ruwwad" VALUES(20,86,'20:86','Thế là Musa quay trở về gặp người dân của Y trong tâm trạng vừa tức giận vừa buồn lo. Y bảo: “Này hỡi dân ta! Chẳng lẽ Thượng Đế của các người đã không hứa với các ngươi một lời hứa tốt đẹp hay sao? Phải chăng lời hứa của Ngài đã quá lâu đối với các người hay là các người muốn cơn tức giận của Thượng Đế của các người sẽ giáng xuống các người nên các người đã phản lại lời hứa với Ta?”');
INSERT INTO "vn_ruwwad" VALUES(20,87,'20:87','Họ đáp: “Chúng tôi nào đâu bội ước với Người nhưng vì chúng tôi bị bắt vác nặng nữ trang của toàn dân cho nên chúng tôi đã ném chúng (vào lò nấu kim loại) theo đề xuất của Samiri.”');
INSERT INTO "vn_ruwwad" VALUES(20,88,'20:88','Thế là y đúc ra cho họ một con bò tơ, thân hình của nó phát ra tiếng rống. Rồi họ bảo: “Đây là thần linh của các người và là thần linh của Musa nhưng (Musa) đã quên.”');
INSERT INTO "vn_ruwwad" VALUES(20,89,'20:89','Há họ đã không nhận thấy nó (con bò được đúc kia) không trả lời họ được tiếng nào, nó không thể hãm hại được họ và cũng không mang lợi gì được cho họ?');
INSERT INTO "vn_ruwwad" VALUES(20,90,'20:90','Và quả thật Harun đã bảo họ trước đó: “Hỡi dân ta! Các người chỉ bị thử thách bởi nó và quả thật Thượng Đế của các người là Đấng Độ Lượng. Bởi thế, các người hãy nghe theo Ta và tuân lệnh Ta.”');
INSERT INTO "vn_ruwwad" VALUES(20,91,'20:91','Họ nói: “Chúng tôi sẽ không bao giờ ngưng thờ nó (con bò) cho tới khi Musa trở về gặp chúng tôi.”');
INSERT INTO "vn_ruwwad" VALUES(20,92,'20:92','(Musa) bảo (người anh em của Y): “Này Harun! Điều gì cản anh ngăn họ khi thấy họ lầm lạc?”');
INSERT INTO "vn_ruwwad" VALUES(20,93,'20:93','“Sao anh không tuân theo Tôi? Có phải anh đã làm trái lệnh Tôi đúng không?”');
INSERT INTO "vn_ruwwad" VALUES(20,94,'20:94','(Harun) đáp: “Hỡi em trai của Ta! Em chớ nắm râu và đầu của Ta! Quả thật, Ta đã sợ việc em sẽ nói ‘Ta đã gây chia rẽ giữa con cháu của Israel và đã không thi hành lời dặn của em’.”');
INSERT INTO "vn_ruwwad" VALUES(20,95,'20:95','(Musa) bảo (Samiri): “Này Samiri, mục đích của ngươi là gì?”');
INSERT INTO "vn_ruwwad" VALUES(20,96,'20:96','(Samiri) nói: “Tôi đã thấy điều mà họ không thấy. Cho nên, tôi đã hốt một nắm (đất) nơi dấu chân (ngựa) của Thiên Sứ (Jibril) và ném nó (lên con bò đúc). Tâm trí của tôi đã xúi giục tôi như thế.”');
INSERT INTO "vn_ruwwad" VALUES(20,97,'20:97','(Musa) nói với (Samiri): “Ngươi hãy cút đi! Quả thật trong suốt cuộc đời này, ngươi sẽ nói “Chớ chạm đến mình tôi!”, (một hình phạt dành cho ngươi), và ngươi sẽ gặp một sự hứa hẹn (ở Ngày Sau về sự trừng phạt khủng khiếp) mà ngươi sẽ không bao giờ tránh khỏi. Và bây giờ ngươi hãy mở to mắt ra nhìn thần linh của ngươi, vật mà ngươi đã thờ cúng (ngoài Allah); chắc chắn bọn ta sẽ đốt nó thành tro rồi vãi tung nó xuống biển.”');
INSERT INTO "vn_ruwwad" VALUES(20,98,'20:98','Quả thật, Thượng Đế của các ngươi chỉ có một mình Allah, Ngài là Đấng mà ngoài Ngài không có một Thượng Đế đích thực nào khác. Và sự Hiểu Biết của Ngài bao quát hết mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(20,99,'20:99','Tương tự, TA (Allah) kể lại cho Ngươi (Muhammad) một vài thông tin về các sự việc xảy ra trước đây. Và quả thật, chính TA đã ban cho Ngươi từ nơi TA (Kinh Qur’an) làm Lời Nhắc Nhở.');
INSERT INTO "vn_ruwwad" VALUES(20,100,'20:100','Ai lánh xa Nó (Kinh Qur’an này) thì sẽ gánh nặng tội lỗi vào Ngày Phán Xét.');
INSERT INTO "vn_ruwwad" VALUES(20,101,'20:101','Họ sẽ mãi mãi ở trong (sự trừng phạt) đó. Thật tồi tệ cho họ vào Ngày Phán Xét khi phải gánh nặng tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(20,102,'20:102','Ngày mà tiếng Còi sẽ được hụ lên; Ngày đó, TA sẽ triệu tập những kẻ tội lỗi và họ trở nên xanh mặt (vì sợ hãi).');
INSERT INTO "vn_ruwwad" VALUES(20,103,'20:103','Họ xầm xì bàn tán: “Các anh ở lại (cõi Barzakh) chỉ khoảng chừng mười ngày thôi đúng không?”');
INSERT INTO "vn_ruwwad" VALUES(20,104,'20:104','TA biết rõ những điều họ xầm xì bàn tán. Và một người có óc suy đoán tốt nhất trong bọn họ bảo: “Có thể các anh đã ở lại (nơi cõi Barzakh này) chỉ khoảng một ngày thôi.”');
INSERT INTO "vn_ruwwad" VALUES(20,105,'20:105','Và họ hỏi Ngươi (Muhammad) về những quả núi. Ngươi hãy nói: “Thượng Đế của Ta sẽ bứng chúng lên và làm cho chúng thành bụi bay tứ tán.”');
INSERT INTO "vn_ruwwad" VALUES(20,106,'20:106','Rồi Ngài sẽ để cho nó (mặt đất) thành bình địa.');
INSERT INTO "vn_ruwwad" VALUES(20,107,'20:107','Nơi đó, ngươi (hỡi người quan sát) sẽ thấy không có gì quanh co và lồi lõm.');
INSERT INTO "vn_ruwwad" VALUES(20,108,'20:108','Vào Ngày đó, nhân loại sẽ đi theo vị Hô Gọi (đến nơi triệu tập), không ai có quyền không nghe theo, và những tiếng nói sẽ tắt lịm đi trước Đấng Độ Lượng cho nên (lúc đó) sẽ không còn nghe thấy gì ngoài tiếng xầm xì.');
INSERT INTO "vn_ruwwad" VALUES(20,109,'20:109','Vào Ngày đó, sự can thiệp sẽ trở nên vô ích ngoại trừ ai được Đấng Độ Lượng cho phép và được Ngài chấp thuận cho y mở lời (can thiệp).');
INSERT INTO "vn_ruwwad" VALUES(20,110,'20:110','Ngài (Allah) biết rõ điều gì trước họ (nhân loại) và điều gì sau họ nhưng họ thì không biết một tí gì về chúng cả.');
INSERT INTO "vn_ruwwad" VALUES(20,111,'20:111','Những gương mặt sẽ gục xuống trước Đấng Hằng Sống, Đấng Chi Phối và Nuôi Dưỡng; và ai gánh nặng tội lỗi trên lưng chắc chắn sẽ thua thiệt.');
INSERT INTO "vn_ruwwad" VALUES(20,112,'20:112','Ai làm việc thiện tốt và có đức tin thì sẽ không lo sợ bị đối xử bất công và mất phần.');
INSERT INTO "vn_ruwwad" VALUES(20,113,'20:113','Và TA (Allah) đã ban Qur’an xuống bằng tiếng Ả-rập đúng như thế và TA đã giảng giải cặn kẽ trong đó những lời cảnh báo mong rằng họ biết sợ (TA) hoặc Nó sẽ làm cho họ lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(20,114,'20:114','Allah là Đấng Tối Cao, Đức Vua Đích Thực. Và Ngươi (Muhammad) chớ hấp tấp với Qur''an trước khi việc mặc khải Nó sẽ hoàn tất cho Ngươi, mà Ngươi hãy cầu nguyện: “Lạy Thượng Đế của bề tôi! Xin Ngài ban thêm kiến thức cho bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(20,115,'20:115','Và quả thật, TA đã giao ước với Adam trước đây nhưng Y đã quên (nên đã ăn cây cấm); và TA thấy Y không cương quyết (trong việc duy trì giao ước).');
INSERT INTO "vn_ruwwad" VALUES(20,116,'20:116','Và Ngươi (Muhammad) hãy nhớ lại khi TA phán với các Thiên Thần: “Các ngươi hãy quỳ lạy trước Adam!” Họ liền quỳ lạy ngoại trừ Iblis, hắn đã từ chối (một cách ngạo mạn.)');
INSERT INTO "vn_ruwwad" VALUES(20,117,'20:117','Thế là TA phán bảo (Adam): “Hỡi Adam! Quả thật đây là kẻ thù của Ngươi và vợ của Ngươi. Vì vậy, chớ để cho hắn đưa hai ngươi ra khỏi Thiên Đàng để rồi Ngươi sẽ chịu đau khổ.”');
INSERT INTO "vn_ruwwad" VALUES(20,118,'20:118','“Quả thật, trong đó (Thiên Đàng) Ngươi sẽ không đói và cũng không trần truồng.”');
INSERT INTO "vn_ruwwad" VALUES(20,119,'20:119','Quả thật trong đó Ngươi sẽ không khát và cũng không chịu cảnh nóng nực.');
INSERT INTO "vn_ruwwad" VALUES(20,120,'20:120','Nhưng Shaytan đã thì thào với Y. Hắn bảo: “Hỡi Adam! Ngươi có muốn ta đưa ngươi đến Cây Vĩnh Cửu và một vương quốc sẽ không bao giờ điêu tàn không?”');
INSERT INTO "vn_ruwwad" VALUES(20,121,'20:121','Vậy là cả hai đã ăn trái của cây đó. Bởi thế phần kín đáo của hai người họ đã lộ ra. Lập tức hai người vội khâu lá cây của Thiên Đàng để che kín thân lại. Do Adam bất tuân Thượng Đế của Y nên đã lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(20,122,'20:122','Rồi Thượng Đế của Y chọn Y. Ngài đã quay lại tha thứ và hướng dẫn Y.');
INSERT INTO "vn_ruwwad" VALUES(20,123,'20:123','(Allah) phán với (Adam và Hauwa’): “Cả hai ngươi (và Iblis) hãy đi xuống khỏi đó (Thiên Đàng), thù hằn lẫn nhau. Nhưng nếu có Chỉ Đạo từ TA đến cho các ngươi, ai theo Chỉ Đạo của TA thì sẽ không lầm lạc cũng không bất hạnh.”');
INSERT INTO "vn_ruwwad" VALUES(20,124,'20:124','“Và ai quay lưng với thông điệp nhắc nhở của TA thì chắc chắn sẽ sống một cuộc sống eo hẹp (bất an, khổ tâm) và vào Ngày Phán Xét TA sẽ phục sinh y trở thành mù lòa.”');
INSERT INTO "vn_ruwwad" VALUES(20,125,'20:125','(Kẻ quay lưng với thông điệp nhắc nhở) sẽ nói: “Lạy Thượng Đế của bề tôi, tại sao Ngài dựng bề tôi sống lại mù lòa trong lúc trước đây bề tôi hãy còn nhìn thấy.”');
INSERT INTO "vn_ruwwad" VALUES(20,126,'20:126','(Allah) phán: “Như thế đó! TA đã mang đến cho ngươi các Lời Mặc Khải của TA nhưng ngươi đã quên Nó; và Ngày nay ngươi bị bỏ quên trở lại giống như thế.”');
INSERT INTO "vn_ruwwad" VALUES(20,127,'20:127','Và TA đáp trả xứng đáng cho ai vượt quá mức giới hạn và không tin tưởng nơi các Lời Mặc Khải của Thượng Đế của y đúng như thế. Và chắc chắn sự trừng phạt ở Đời Sau sẽ khắc nghiệt và dai dẳng hơn.');
INSERT INTO "vn_ruwwad" VALUES(20,128,'20:128','Há không là một Chỉ Đạo cho họ hay sao việc TA đã tiêu diệt không biết bao nhiêu thế hệ trước họ, chủ nhân của các căn nhà mà họ đang bước đi? Quả thật nơi sự việc đó là những dấu hiệu cho những người biết suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(20,129,'20:129','Nếu không vì một Lời phán của Thượng Đế của Ngươi đã được tuyên bố và một thời hạn đã được ấn định thì chắc chắn (hình phạt) đã xảy ra (cho họ) rồi.');
INSERT INTO "vn_ruwwad" VALUES(20,130,'20:130','Thế nên, Ngươi (Muhammad) hãy kiên nhẫn với những lời của họ và Ngươi hãy tán dương, ca ngợi Thượng Đế của Ngươi trước lúc mặt trời mọc cũng như trước lúc mặt trời lặn; và Ngươi hãy tán dương Ngài vào các thời khắc của ban đêm, vào giờ giấc đầu và cuối của ban ngày mong rằng Ngươi sẽ hài lòng (với phần thưởng từ nơi Allah).');
INSERT INTO "vn_ruwwad" VALUES(20,131,'20:131','Và Ngươi (Muhammad) chớ trố mắt nhìn những thứ mà TA đã ban cấp cho họ hưởng thụ, sự lộng lẫy hào nhoáng của đời sống trần tục chỉ là những thứ mà TA dùng để thử thách họ mà thôi. Quả thật, những thiên lộc từ nơi Thượng Đế của Ngươi mới tốt lành và bền vững.');
INSERT INTO "vn_ruwwad" VALUES(20,132,'20:132','Ngươi hãy ra lệnh cho người nhà của Ngươi dâng lễ nguyện Salah và Ngươi hãy kiên nhẫn. TA không đòi hỏi Ngươi bổng lộc; Chính TA cung cấp nó cho Ngươi. Và kết cuộc tốt đẹp chỉ dành cho những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(20,133,'20:133','(Những kẻ vô đức tin) nói: “Tại sao (Muhammad) không mang đến cho chúng ta một bằng chứng từ Thượng Đế của Y?” Chẳng lẽ những điều ghi trong các Tờ Kinh trước (Tawrah và Injil) đã không là một bằng chứng rõ rệt cho họ ư?');
INSERT INTO "vn_ruwwad" VALUES(20,134,'20:134','Nếu TA (Allah) hủy diệt họ bằng sự trừng phạt trước khi (Qur’an được mặc khải) thì chắc chắn họ sẽ nói: “Lạy Thượng Đế của bầy tôi! Sao Ngài không cử một Sứ Giả đến với bầy tôi để bầy tôi có dịp tuân theo các Lời Mặc Khải của Ngài trước khi bầy tôi bị hạ nhục và bị trừng phạt?”');
INSERT INTO "vn_ruwwad" VALUES(20,135,'20:135','Ngươi (Muhammad) hãy nói (với những kẻ phủ nhận này): “Tất cả đều mong đợi (sự phán xét của Allah); cho nên các người hãy cứ chờ đợi. Rồi đây các người sẽ sớm biết ai mới là người đi theo con đường bằng phẳng và được hướng dẫn đúng đường.”');
INSERT INTO "vn_ruwwad" VALUES(21,1,'21:1','Việc xét xử nhân loại (vào Ngày Phán Xét Cuối Cùng) đang đến gần nhưng họ vẫn ngoảnh mặt làm ngơ.');
INSERT INTO "vn_ruwwad" VALUES(21,2,'21:2','Bất cứ điều nhắc nhở mới nào từ Thượng Đế của họ đến với họ, họ đều chỉ nghe nó với thái độ bỡn cợt.');
INSERT INTO "vn_ruwwad" VALUES(21,3,'21:3','Trái tim của họ thờ ơ. Những kẻ sai quấy đã hội đàm bí mật: “Chẳng phải kẻ này cũng chỉ là một người phàm giống như các người sao? Chẳng lẽ các người bị mê hoặc bởi pháp thuật của hắn trong lúc các người nhận thức rõ (rằng hắn cũng chỉ là người phàm ư)?”');
INSERT INTO "vn_ruwwad" VALUES(21,4,'21:4','(Thiên Sứ Muhammad) bảo họ: “Thượng Đế của Ta biết rõ từng lời nói trong các tầng trời và trái đất bởi Ngài là Đấng Hằng Nghe, Đấng Hằng Biết.”');
INSERT INTO "vn_ruwwad" VALUES(21,5,'21:5','(Họ bối rối về những gì Thiên Sứ Muhammad mang đến, có lúc) họ bảo: “Toàn là những giấc mộng lộn xộn” (Có lúc họ bảo): “Không, hắn chỉ bịa đặt ra mà thôi” (Và có lúc họ lại bảo): “Không, hắn là một nhà thơ; chúng ta hãy bảo hắn mang đến cho chúng ta một phép lạ giống như những cái đã được ban cấp cho các vị Thiên Sứ trước kia (nếu như hắn nói thật).”');
INSERT INTO "vn_ruwwad" VALUES(21,6,'21:6','Trước họ, các thị trấn đã bị TA tiêu diệt đều đã không có đức tin. Chẳng lẽ họ sẽ có đức tin hay sao?');
INSERT INTO "vn_ruwwad" VALUES(21,7,'21:7','Và trước Ngươi (Thiên Sứ Muhammad), những Sứ Giả mà TA đã cử đến đều là những người đàn ông phàm tục được TA mặc khải. Cho nên, các ngươi hãy hỏi những người của Kinh Sách nếu các ngươi không biết.');
INSERT INTO "vn_ruwwad" VALUES(21,8,'21:8','Và TA đã không làm cho họ (các Sứ Giả của TA) thành một cơ thể không ăn uống và họ cũng không phải là những người bất tử.');
INSERT INTO "vn_ruwwad" VALUES(21,9,'21:9','Rồi TA đã hoàn tất Lời hứa đối với họ. TA đã cứu sống họ và những ai mà TA muốn. Và TA đã tiêu diệt những kẻ tội lỗi và phản nghịch.');
INSERT INTO "vn_ruwwad" VALUES(21,10,'21:10','Quả thật, TA đã ban xuống cho các ngươi một Kinh Sách (Qur’an), trong đó, là nguồn nhắc nhở cho các ngươi. Thế chẳng lẽ các ngươi không hiểu (điều đó) ư?');
INSERT INTO "vn_ruwwad" VALUES(21,11,'21:11','Đã biết bao thị trấn bị TA tiêu diệt vì đã làm điều sai quấy, và sau họ, TA đã tạo ra một đám người khác!');
INSERT INTO "vn_ruwwad" VALUES(21,12,'21:12','(Những kẻ bị tiêu diệt đó), khi họ thấy sự trừng phạt của TA thì họ sợ hãi bỏ chạy tán loạn.');
INSERT INTO "vn_ruwwad" VALUES(21,13,'21:13','Các ngươi đừng bỏ chạy, hãy trở lại với những thứ mà các ngươi đã sống xa hoa và những mái nhà tiện nghi của các ngươi, mong rằng các ngươi sẽ bị tra hỏi.');
INSERT INTO "vn_ruwwad" VALUES(21,14,'21:14','Họ kêu than: “Ôi thật khốn khổ thay! Chúng tôi thực sự là những kẻ đã làm điều sai quấy (vì đã vô đức tin nơi Allah).”');
INSERT INTO "vn_ruwwad" VALUES(21,15,'21:15','Tiếng kêu than của họ vẫn dai dẳng mãi cho tới khi TA san bằng họ như một cánh đồng sau mùa gặt hái.');
INSERT INTO "vn_ruwwad" VALUES(21,16,'21:16','TA không tạo hóa các tầng trời và trái đất cũng như vạn vật giữa chúng để chơi vui (một cách vô ích).');
INSERT INTO "vn_ruwwad" VALUES(21,17,'21:17','Nếu TA muốn có một thú tiêu khiển và TA thực sự muốn làm điều đó thì chắc chắn TA đã lấy nó từ nơi TA.');
INSERT INTO "vn_ruwwad" VALUES(21,18,'21:18','Không! TA lấy Chân Lý chọi sự ngụy tạo, Chân Lý đập mạnh vào sự ngụy tạo làm cho sự ngụy tạo vỡ tan. Thật khốn thay cho các ngươi về những điều mà các ngươi đã mô tả (không đúng về TA)!');
INSERT INTO "vn_ruwwad" VALUES(21,19,'21:19','Bất cứ ai trong các tầng trời và trái đất đều thuộc về Ngài (Allah). Và bất cứ ai ở nơi Ngài (các Thiên Thần) đều không ngạo mạn và không biết mệt mỏi trong việc thờ phượng Ngài.');
INSERT INTO "vn_ruwwad" VALUES(21,20,'21:20','Họ không ngừng ca tụng và tán dương Ngài cả đêm lẫn ngày (không biết chán nản).');
INSERT INTO "vn_ruwwad" VALUES(21,21,'21:21','Họ (những kẻ thờ đa thần) đã tôn thờ (ngoài Allah) các thần linh từ trái đất trong khi chúng không có bất cứ quyền năng phục sinh nào.');
INSERT INTO "vn_ruwwad" VALUES(21,22,'21:22','Nếu trong các tầng trời và trái đất thực sự có những thần linh khác ngoài Allah thì các tầng trời và trái đất chắc chắn sẽ sụp đổ. Cho nên, Allah, Thượng Đế của Chiếc Ngai Vương, tối cao và trong sạch khỏi những điều mà họ đã mô tả (về Ngài).');
INSERT INTO "vn_ruwwad" VALUES(21,23,'21:23','Ngài sẽ không bị ai chất vấn về việc Ngài làm còn họ mới là những kẻ sẽ bị Ngài chất vấn.');
INSERT INTO "vn_ruwwad" VALUES(21,24,'21:24','Họ đã thờ phượng những thần linh khác ngoài Ngài (Allah). Ngươi (hỡi Thiên Sứ) hãy bảo họ: “Các người hãy đưa ra bằng chứng của các người (về việc các thần linh đó đáng được thờ phượng) xem nào! Còn đây (Kinh Sách được ban xuống cho Ta) là Bức Thông Điệp nhắc nhở cho người nào theo Ta và là điều nhắc nhở cho những người trước Ta.” Không! Đa số bọn họ đều không biết sự thật nên họ đã quay lưng với nó.');
INSERT INTO "vn_ruwwad" VALUES(21,25,'21:25','Không một Sứ Giả nào được cử phái đến trước Ngươi mà lại không được TA mặc khải cho y rằng không có Thượng Đế đích thực nào khác ngoài TA nên chỉ hãy thờ phượng riêng một mình TA.');
INSERT INTO "vn_ruwwad" VALUES(21,26,'21:26','(Những kẻ thờ đa thần) nói: “(Các Thiên Thần) là những đứa con gái của Đấng Độ Lượng.” Ngài tối cao và trong sạch khỏi những điều họ nói! Không! Tất cả đều là những bề tôi vinh dự (của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(21,27,'21:27','Họ không dám qua mặt Ngài bất cứ lời nói nào và họ chỉ luôn làm theo mệnh lệnh của Ngài.');
INSERT INTO "vn_ruwwad" VALUES(21,28,'21:28','Ngài biết điều gì trước họ và điều gì sau họ và họ không can thiệp giùm cho ai được ngoại trừ cho người nào mà Ngài ưng thuận. Và họ luôn sợ Ngài.');
INSERT INTO "vn_ruwwad" VALUES(21,29,'21:29','Trong số họ, ai dám tự xưng “ta là thần linh ngoài Ngài” thì sẽ bị TA trừng phạt bằng Hoả Ngục. TA trừng phạt những kẻ làm điều sai quấy tương tự như thế.');
INSERT INTO "vn_ruwwad" VALUES(21,30,'21:30','Lẽ nào những kẻ vô đức tin không nhận thấy rằng các tầng trời và trái đất quyện lại với nhau trước khi TA chẻ chúng ra làm đôi hay sao? Và TA đã tạo mọi sinh vật từ nước, lẽ nào chúng không tin ư?');
INSERT INTO "vn_ruwwad" VALUES(21,31,'21:31','TA đã đặt trên trái đất những quả núi kiên cố để giữ trái đất không rung chuyển với họ (cư dân của trái đất) và TA đã làm ra nơi trái đất các con đường, mong rằng họ được hướng dẫn.');
INSERT INTO "vn_ruwwad" VALUES(21,32,'21:32','TA đã làm bầu trời như một cái lọng che được canh giữ kỹ càng. Nhưng họ (những kẻ thờ đa thần) ngoảnh mặt làm ngơ trước những dấu hiệu của nó.');
INSERT INTO "vn_ruwwad" VALUES(21,33,'21:33','Ngài (Allah) là Đấng đã tạo hóa ban đêm và ban ngày, mặt trời và mặt trăng, mỗi vật đều bơi trong quỹ đạo (được định sẵn).');
INSERT INTO "vn_ruwwad" VALUES(21,34,'21:34','Trước Ngươi (hỡi Thiên Sứ), TA đã không làm cho một người phàm nào sống bất tử cả. Lẽ nào Ngươi chết đi thì chúng vẫn sống bất tử ư?');
INSERT INTO "vn_ruwwad" VALUES(21,35,'21:35','Mỗi một linh hồn đều phải nếm cái chết; TA thử thách các ngươi với điều xấu và điều tốt. Và các ngươi đều sẽ được đưa trở về trình diện TA.');
INSERT INTO "vn_ruwwad" VALUES(21,36,'21:36','Khi những kẻ vô đức tin thấy Ngươi (hỡi Thiên Sứ Muhammad), họ chỉ muốn chế giễu Ngươi: “Đây có phải là kẻ đã miệt thị những thần linh của các người đúng không?” Và khi nghe nhắc đến Đấng Độ Lượng thì họ trở nên vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(21,37,'21:37','Con người được tạo ra vốn nôn nóng. TA sẽ sớm cho các ngươi (hỡi những kẻ thờ đa thần) thấy những bằng chứng (sự trừng phạt) của TA. Cho nên, các ngươi không cần giục nó mau đến làm gì.');
INSERT INTO "vn_ruwwad" VALUES(21,38,'21:38','Họ (những kẻ vô đức tin) nói với (những người Muslim): “Bao giờ lời hứa này sẽ xảy ra nếu các người nói thật?”');
INSERT INTO "vn_ruwwad" VALUES(21,39,'21:39','Nếu những kẻ vô đức tin biết được thời khắc mà họ không thể đưa mặt và lưng của họ tránh khỏi Hỏa Ngục cũng như không được ai giúp đỡ (thì chắc chắn họ sẽ không giục sự trừng phạt mau đến).');
INSERT INTO "vn_ruwwad" VALUES(21,40,'21:40','Chắc chắn (sự trừng phạt) sẽ xảy đến với họ một cách bất ngờ khiến họ bàng hoàng không kịp trở tay và chắc chắn họ không được trì hoãn thêm bất cứ thời khắc nào nữa.');
INSERT INTO "vn_ruwwad" VALUES(21,41,'21:41','Quả thật, các Sứ Giả trước Ngươi (hỡi Thiên Sứ) đã từng bị giễu cợt và nhạo báng nhưng (sự trừng phạt) sẽ vây hãm những kẻ chế giễu bởi những điều mà họ đã thường bỡn cợt và trêu đùa.');
INSERT INTO "vn_ruwwad" VALUES(21,42,'21:42','Ngươi hãy bảo họ (hỡi Thiên Sứ Muhammad!): “Ai sẽ canh giữ an toàn cho các người cả đêm lẫn ngày khỏi Đấng Độ Lượng (khi Ngài giáng xuống sự trừng phạt)?” Không, trước Lời Nhắc Nhở của Thượng Đế của họ, họ luôn ngoảnh mặt quay đi.');
INSERT INTO "vn_ruwwad" VALUES(21,43,'21:43','Hay lẽ nào họ có những thần linh ngoài TA đứng ra che chở họ tránh khỏi (sự trừng phạt của) TA? (Những thần linh đó) sẽ không thể tự cứu lấy mình và cũng không tự bảo vệ bản thân tránh khỏi TA được.');
INSERT INTO "vn_ruwwad" VALUES(21,44,'21:44','Không! TA chỉ để mặc những kẻ (vô đức tin) này và cha mẹ của họ hưởng lạc cho đến khi tuổi thọ của họ dài thêm mà thôi. Lẽ nào họ đã không thấy việc TA đã đến tận đất đai của họ và thu hẹp dần ranh giới của nó lại? Lẽ nào họ sẽ chiến thắng?');
INSERT INTO "vn_ruwwad" VALUES(21,45,'21:45','Ngươi hãy nói (hỡi Thiên Sứ Muhammad!): “Quả thật, ta chỉ cảnh báo các người (hỡi nhân loại) theo Lời Mặc Khải.” Tuy nhiên, kẻ điếc sẽ không nghe được tiếng gọi dù y có được cảnh báo đi chăng nữa.');
INSERT INTO "vn_ruwwad" VALUES(21,46,'21:46','Nhưng nếu có một làn hơi trừng phạt của Thượng Đế của Ngươi chạm đến họ thì chắc chắn họ sẽ kêu than: “Ôi, thật khổ thân chúng tôi! Chúng tôi thực sự đã là những kẻ sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(21,47,'21:47','Rồi đây TA sẽ thiết lập các bàn cân công lý vào Ngày Phán Xét. Lúc đó sẽ không có một linh hồn nào bị đối xử bất công về bất cứ điều gì, cho dù một điều gì đó chỉ bằng sức nặng của một hạt cải thì TA cũng sẽ mang nó ra (để phân xử công minh). Và một mình TA thôi đủ thanh toán (các việc làm của đám bầy tôi của TA).');
INSERT INTO "vn_ruwwad" VALUES(21,48,'21:48','Quả thật, TA đã ban cho Musa và Harun (Kinh Tawrah) làm tiêu chuẩn phân biệt đúng sai, làm nguồn anh sáng (để hướng dẫn) và làm một thông điệp nhắc nhở cho những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(21,49,'21:49','(Đó là) những người sợ Thượng Đế của họ dù không nhìn thấy Ngài và khiếp sợ Giờ Tận Thế.');
INSERT INTO "vn_ruwwad" VALUES(21,50,'21:50','Đây (Qur’an) là nguồn nhắc nhở ân phúc mà TA đã ban xuống. Lẽ nào các ngươi lại phủ nhận Nó?');
INSERT INTO "vn_ruwwad" VALUES(21,51,'21:51','Quả thật trước đây TA đã ban cho Ibrahim sự nhận thức đúng sai và TA biết rõ về Y.');
INSERT INTO "vn_ruwwad" VALUES(21,52,'21:52','Khi Y nói với cha và người dân của Y: “Đây, những thứ hình tượng mà các người sùng bái là gì chứ?”');
INSERT INTO "vn_ruwwad" VALUES(21,53,'21:53','Họ bảo: “Bọn ta thấy cha mẹ của bọn ta thờ cúng chúng.”');
INSERT INTO "vn_ruwwad" VALUES(21,54,'21:54','(Ibrahim) bảo: “Các người và cha mẹ của các người, tất cả, thật sự đều đã lầm lạc một cách quá rõ ràng.”');
INSERT INTO "vn_ruwwad" VALUES(21,55,'21:55','Họ nói: “Ngươi nói thật hay đùa giỡn với bọn ta đấy?”');
INSERT INTO "vn_ruwwad" VALUES(21,56,'21:56','(Ibrahim) nói: “Đúng vậy, Thượng Đế của các người là Thượng Đế của các tầng trời và trái đất, Đấng đã sáng tạo ra chúng (còn những gì các người thờ phượng chẳng có bất cứ quyền năng nào trong sự việc đó cả). Và tôi là một trong những người làm chứng cho điều đó.”');
INSERT INTO "vn_ruwwad" VALUES(21,57,'21:57','(Ibrahim) nói (trong lòng): “Thề bởi Allah, chắc chắn tôi sẽ lên kế hoạch đập phá những bức tượng của các người sau khi các người quay đi.”');
INSERT INTO "vn_ruwwad" VALUES(21,58,'21:58','(Đúng như kế hoạch) Ibrahim đã đập phá chúng, chỉ chừa lại bức tượng lớn nhất, (Y làm thế) để mong họ trở lại (hỏi nó về những điều mà Y đã làm).');
INSERT INTO "vn_ruwwad" VALUES(21,59,'21:59','(Khi họ quay lại, nhìn thấy các bức tượng đã bị đập phá tan tành), họ nói: “Ai đã làm điều này với những thần linh của chúng ta? Hắn thật là một kẻ sai quấy quá mức mà.”');
INSERT INTO "vn_ruwwad" VALUES(21,60,'21:60','Một số trong bọn họ bảo: “Chúng tôi có nghe một thanh niên tên Ibrahim đã nói chuyện ra vẻ khinh miệt họ (các thần linh của chúng ta).”');
INSERT INTO "vn_ruwwad" VALUES(21,61,'21:61','(Những kẻ cầm quyền trong bọn họ) bảo: “Vậy thì bắt nó đưa ra trước công chúng để người dân chứng kiến (điều tội lỗi mà nó đã làm).”');
INSERT INTO "vn_ruwwad" VALUES(21,62,'21:62','(Thế rồi họ bắt Ibrahim đến) và bảo: “Này Ibrahim! Có phải mầy đã làm điều này với các thần linh của bọn tao đúng không?”');
INSERT INTO "vn_ruwwad" VALUES(21,63,'21:63','Ibrahim (thản nhiên) đáp: “Không! Bức tượng lớn nhất này của chúng đã làm đấy chứ. Các người hãy hỏi chúng xem nếu chúng nói chuyện được.”');
INSERT INTO "vn_ruwwad" VALUES(21,64,'21:64','(Lúc đó) họ quay nhìn nhau (trong hổ thẹn vì biết bản thân mình đã thờ phượng những thứ vô dụng) rồi bảo nhau: “Các người mới thực sự là những kẻ sai.”');
INSERT INTO "vn_ruwwad" VALUES(21,65,'21:65','Rồi họ gục đầu (nói với Ibrahim trong xấu hổ): “Chắc chắn ngươi đã biết rõ những bức tượng này không nói chuyện được!”');
INSERT INTO "vn_ruwwad" VALUES(21,66,'21:66','Ibrahim (nhân cơ hội) liền bảo: “Sao các người lại tôn thờ ngoài Allah những vật chẳng mang lại lợi ích cũng chẳng hãm hại được gì đến các ngươi?”');
INSERT INTO "vn_ruwwad" VALUES(21,67,'21:67','“Thật đáng xấu hổ thay cho các người và cho những vật mà các người tôn thờ ngoài Allah! Lẽ nào các người không hiểu ra vấn đề?”');
INSERT INTO "vn_ruwwad" VALUES(21,68,'21:68','(Không còn lý để tranh luận), họ nói: “Các người hãy bắt nó thiêu sống để giúp những thần linh của các người (đòi lại công bằng) nếu các người thực sự muốn (trừng phat nó).”');
INSERT INTO "vn_ruwwad" VALUES(21,69,'21:69','(Sau khi họ ném Ibrahim vào trong hố lửa), TA phán: “Hỡi Lửa! Ngươi hãy nguội mát và bằng an cho Ibrahim”');
INSERT INTO "vn_ruwwad" VALUES(21,70,'21:70','Họ muốn mưu hại Ibrahim nhưng TA đã làm cho họ thất bại.');
INSERT INTO "vn_ruwwad" VALUES(21,71,'21:71','TA đã giải cứu Y (Ibrahim) và Lut và đưa họ đến một vùng đất mà TA đã ban phúc cho muôn loài.');
INSERT INTO "vn_ruwwad" VALUES(21,72,'21:72','TA đã ban cho Ibrahim (đứa con trai) Is-haq và (đứa cháu nội) Ya’qub như một món quà; và tất cả họ đều được TA làm thành những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(21,73,'21:73','Và TA đã làm cho họ thành các vị Imam (lãnh tụ) hướng dẫn theo Mệnh Lệnh của TA và TA đã mặc khải bảo họ làm việc thiện, dâng lễ nguyện Salah, đóng Zakah và thờ phượng chỉ riêng TA.');
INSERT INTO "vn_ruwwad" VALUES(21,74,'21:74','Và Lut, TA đã ban cho Y khả năng phán xét (trong việc phân xử tranh chấp) và kiến thức; và TA đã giải cứu Y ra khỏi thị trấn (Sodom) mà dân cư đã từng làm những điều ô uế. Quả thật, họ là một đám dân xấu xa, bất tuân.');
INSERT INTO "vn_ruwwad" VALUES(21,75,'21:75','TA đã thương xót Lut (khi cứu Y khỏi sự trừng phạt mà TA đã giáng xuống đám dân của Y), bởi quả thật Y là một người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(21,76,'21:76','Và Nuh, Y đã cầu nguyện trước đó nên TA đã đáp lời nguyện cầu của Y, TA đã cứu Y và gia đình của Y thoát khỏi cuộc đại nạn.');
INSERT INTO "vn_ruwwad" VALUES(21,77,'21:77','TA đã cứu Y thoát khỏi đám dân đã phủ nhận các bằng chứng của TA. Quả thật họ là một đám dân tội lỗi nên TA đã nhấn chìm họ toàn bộ.');
INSERT INTO "vn_ruwwad" VALUES(21,78,'21:78','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại) khi Dawood và Sulayman xét xử vụ một đám rẫy đã bị đàn cừu của một đám dân đột nhập vào tàn phá trong đêm. TA đã chứng kiến việc xét xử của họ.');
INSERT INTO "vn_ruwwad" VALUES(21,79,'21:79','TA đã làm cho Sulayman thông hiểu vấn đề triệt để hơn (Dawood) trong lúc cả hai đều được TA ban cho khả năng suy luận khôn ngoan và nguồn kiến thức vững chắc (về các giáo luật). Và TA đã bắt núi non lẫn chim chóc tán dương TA cùng với Dawood. Chính TA đã làm ra (những sự việc đó).');
INSERT INTO "vn_ruwwad" VALUES(21,80,'21:80','TA đã dạy Y (Dawood) cách làm áo giáp cho các ngươi để các ngươi tự bảo vệ trong những cuộc giao chiến. Thế các ngươi (hỡi nhân loại) biết ơn TA chăng?');
INSERT INTO "vn_ruwwad" VALUES(21,81,'21:81','Đối với Sulayman, TA đã bắt những luồng gió mạnh di chuyển theo mệnh lệnh của Y đến vùng đất mà TA đã ban phúc. Quả thật, TA là Đấng Thông Toàn mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(21,82,'21:82','Trong đám Shaytan (thuộc loài Jinn), có những tên (phụ trách công việc) lặn xuống biển cũng như các công việc khác để phục vụ cho Y (Sulayman). Chính TA là Đấng Trông Chừng (những tên Shaytan đó).');
INSERT INTO "vn_ruwwad" VALUES(21,83,'21:83','Và Ayyub, Y khẩn nguyện Thượng Đế của Y: “Bề tôi đã gặp nạn và Ngài là Đấng Nhân Từ nhất.”');
INSERT INTO "vn_ruwwad" VALUES(21,84,'21:84','TA đã đáp lời khẩn nguyện của Y, TA đã giải thoát Y khỏi nỗi thống khổ mà Y đã chịu đựng, và TA đã phục hồi cho Y gia đình của Y và đã ban thêm cho Y số lượng tương tự giống như họ bởi sự nhân từ và lòng thương xót từ nơi TA. Và đó là điều nhắc nhở cho những người thờ phượng TA.');
INSERT INTO "vn_ruwwad" VALUES(21,85,'21:85','Và Isma''il, Idris và Zdul-Kifli. Tất cả đều là những người kiên nhẫn và chịu đựng.');
INSERT INTO "vn_ruwwad" VALUES(21,86,'21:86','TA đã thương xót họ bởi họ thực sự là những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(21,87,'21:87','Và Zdan-nun (Yunus tức Jonah), khi Y nổi giận bỏ đi và đã nghĩ rằng TA sẽ không làm gì Y. Nhưng Y (đã kịp nhận ra lỗi lầm và liền sám hối ngay khi bị TA nhốt Y vào trong bụng cá voi), từ trong các bóng tối, Y khẩn nguyện: “Không có Thượng Đế đích thực nào khác ngoài Ngài; Quang vinh thay Ngài! Bề tôi thực sự đã sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(21,88,'21:88','TA đã đáp lại sự cầu xin của Y và cứu Y khỏi nạn kiếp đó; và tương tự, TA sẽ giải cứu những người có đức tin giống như vậy.');
INSERT INTO "vn_ruwwad" VALUES(21,89,'21:89','Và Zakariya, Y khẩn cầu Thượng Đế của Y: “Lạy Thượng Đế của bề tôi! Xin Ngài đừng bỏ mặc bề tôi một mình đơn độc (không con cái nối dõi) và Ngài là Đấng để lại di sản tốt nhất.”');
INSERT INTO "vn_ruwwad" VALUES(21,90,'21:90','TA đã đáp lời khẩn cầu của Y và đã ban cho Y (một đứa con trai) Yahya. TA cũng đã chữa lành bệnh cho người vợ của Y. Quả thật họ là những người thường tranh nhau làm phúc. Họ thường cầu nguyện TA bằng niềm hy vọng và lòng kính sợ. Và họ là những người hạ mình kính cẩn trước TA.');
INSERT INTO "vn_ruwwad" VALUES(21,91,'21:91','Và (Mar-yam), Nữ là người đã giữ trọn trinh tiết. TA đã cử Ruh của TA (đại Thiên Thần Jibril) đến thổi (linh hồn) vào (bụng) của Nữ (để Nữ mang thai), TA đã làm cho Nữ và đứa con trai của Nữ thành một dấu lạ cho nhân loại.');
INSERT INTO "vn_ruwwad" VALUES(21,92,'21:92','Thật ra cộng đồng này của các ngươi (hỡi nhân loại) là một cộng đồng thống nhất (đều thờ phượng và thần phục Allah) và TA là Thượng Đế của các ngươi nên các ngươi hãy chỉ thờ phượng riêng TA.');
INSERT INTO "vn_ruwwad" VALUES(21,93,'21:93','Nhưng họ (nhân loại) đã chia rẽ thành các nhóm phái. (Rồi đây) tất cả sẽ trở lại trình diện TA (để TA xét xử).');
INSERT INTO "vn_ruwwad" VALUES(21,94,'21:94','Do đó, người nào làm điều thiện tốt và có đức tin thì công sức của y sẽ không bị phủ nhận và chính TA sẽ ghi công lao cho y.');
INSERT INTO "vn_ruwwad" VALUES(21,95,'21:95','Và một lệnh cấm đã được ban hành cho (dân của) mỗi thị trấn mà TA đã tiêu diệt rằng họ sẽ không trở lại (thế gian để được quay lại sám hối).');
INSERT INTO "vn_ruwwad" VALUES(21,96,'21:96','Cho tới khi Ya’juj và Ma’juj được thả ra và chúng sẽ ồ ạt tràn xuống từ mỗi ngọn đồi.');
INSERT INTO "vn_ruwwad" VALUES(21,97,'21:97','(Lúc đó) lời hứa thật sự (Giờ Tận Thế) đang đến gần. Rồi khi Nó đến, cặp mắt của những kẻ vô đức tin sẽ mở to trừng trừng (trước cảnh tượng kinh hãi), (họ sẽ than): “Ôi thật khổ thân chúng tôi! Chúng tôi thực sự đã thờ ơ với điều này. Không! Chúng tôi là những kẻ sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(21,98,'21:98','Quả thật, các ngươi (hỡi những kẻ thờ đa thần) và những vật mà các ngươi thờ phượng ngoài Allah sẽ là chất đốt của Hỏa Ngục, nơi mà các ngươi sẽ phải đi vào.');
INSERT INTO "vn_ruwwad" VALUES(21,99,'21:99','Nếu những vật (được thờ phượng) này là những thần linh thực sự thì chúng sẽ không vào đó nhưng tất cả sẽ đi vào ở trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(21,100,'21:100','Trong đó, họ sẽ rên rỉ và sẽ không nghe thấy gì.');
INSERT INTO "vn_ruwwad" VALUES(21,101,'21:101','(Những kẻ thờ đa thần bảo: quả thật Ysa và các Thiên Thần đều sẽ vào Hỏa Ngục, nên Allah phán): Quả thật, những ai mà TA đã khẳng định họ tốt thì sẽ được đưa ra xa khỏi đó.');
INSERT INTO "vn_ruwwad" VALUES(21,102,'21:102','Họ sẽ không nghe thấy một tiếng xì xào nào của (Hỏa Ngục) và họ sẽ mãi mãi (sống trong Thiên Đàng) hưởng thụ những gì mà bản thân họ đã từng mong ước.');
INSERT INTO "vn_ruwwad" VALUES(21,103,'21:103','Họ sẽ không còn sợ hãi; và các Thiên Thần sẽ nghênh đón họ: “Đây là Ngày mà qúi vị đã được hứa.”');
INSERT INTO "vn_ruwwad" VALUES(21,104,'21:104','Ngày mà TA sẽ cuốn tròn bầu trời giống như các tờ kinh được cuốn tròn cho những quyển Kinh; TA sẽ tái lập nó lại giống như TA đã khởi tạo nó lần đầu, một Lời Hứa ràng buộc TA phải thực hiện. TA chắc chắn sẽ làm điều đó.');
INSERT INTO "vn_ruwwad" VALUES(21,105,'21:105','Quả thật TA đã ghi trong Kinh Zabur sau quyển Kinh Mẹ (Al-Lawhu Al-Mahfuzh) rằng trái đất sẽ do những bề tôi ngoan đạo của TA thừa hưởng.');
INSERT INTO "vn_ruwwad" VALUES(21,106,'21:106','Quả thật, trong cái này (Qur’an) là một Thông Điệp gởi đến nhóm người thờ phượng đích thực.');
INSERT INTO "vn_ruwwad" VALUES(21,107,'21:107','TA (Allah) cử Ngươi (hỡi Thiên Sứ) đến chỉ để mang lại hồng phúc cho vũ trụ và muôn loài.');
INSERT INTO "vn_ruwwad" VALUES(21,108,'21:108','Ngươi hãy bảo họ: “Quả thật Ta được mặc khải cho biết Thượng Đế của các người chỉ là một Thượng Đế duy nhất. Thế các người sẽ không là những người thần phục Ngài sao?”');
INSERT INTO "vn_ruwwad" VALUES(21,109,'21:109','Nhưng nếu họ ngoảnh mặt bỏ đi thì Ngươi hãy bảo họ: “Ta đã thông báo đều cho các người; và Ta thực sự không biết điều mà các người đã được hứa đang đến gần hay vẫn còn ở xa.”');
INSERT INTO "vn_ruwwad" VALUES(21,110,'21:110','“Quả thật, Ngài biết lời lẽ mà các người đã hô hào lớn tiếng và biết điều mà các người giấu kín.”');
INSERT INTO "vn_ruwwad" VALUES(21,111,'21:111','“Ta thực sự không biết liệu đó là một sự thử thách hay là một sự hưởng thụ ngắn ngủi dành cho các người.”');
INSERT INTO "vn_ruwwad" VALUES(21,112,'21:112','Y (Muhammad) cầu nguyện: “Lạy Thượng Đế của bề tôi! Xin Ngài phân xử bằng Chân Lý.” Và (Y nói với những kẻ thờ đa thần): “Thượng Đế của chúng tôi, Đấng Độ Lượng sẽ giúp chúng tôi đối phó với những điều mà các người đã qui cho Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(22,1,'22:1','Hỡi nhân loại! Các ngươi hãy ngoan đạo đối với Thượng Đế của các ngươi bởi lẽ cơn chấn động của Giờ Tận Thế sẽ là một điều kinh hoàng vô cùng khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(22,2,'22:2','Ngày mà các ngươi sẽ thấy mỗi bà mẹ sẽ quên mất đứa con đang bú và mỗi người phụ nữ mang thai sẽ trút bỏ gánh nặng của mình (sẩy thai) và Ngươi sẽ thấy nhân loại như đang say rượu nhưng thực chất họ không say (do rượu) mà là do sự trừng phạt khủng khiếp của Allah.');
INSERT INTO "vn_ruwwad" VALUES(22,3,'22:3','Trong nhân loại có kẻ tranh luận về Allah một cách không hiểu biết và y chỉ đi theo từng tên Shaytan phản nghịch.');
INSERT INTO "vn_ruwwad" VALUES(22,4,'22:4','(Những tên Shaytan phản nghịch) được quy định rằng ai làm bạn với chúng, chúng sẽ đưa người đó đi lạc (khỏi con đường chân lý) và sẽ dẫn y đến hình phạt nơi Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(22,5,'22:5','Hỡi nhân loại! Nếu các ngươi hoài nghi về sự phục sinh thì (các ngươi hãy biết rằng) quả thật TA đã tạo hóa các ngươi từ đất bụi rồi từ tinh dịch rồi từ một hòn máu đặc rồi từ một miếng thịt, (sau đó TA làm cho nó) thành một cơ thể hoàn chỉnh hoặc không thành (sẩy thai) mục đích để TA trưng bày cho các ngươi thấy (quyền năng của TA). Và TA đặt trong các dạ con những gì TA muốn đến một thời hạn ấn định; sau đó TA cho các ngươi ra đời thành những đứa bé; rồi để các ngươi trưởng thành; và trong các ngươi có người bị cho chết sớm và có người được cho sống đến tuổi già không còn biết gì sau khi đã biết rất nhiều. Và ngươi (hỡi con người) thấy đất đai nứt nẻ chết khô nhưng khi TA ban nước mưa xuống tưới lên nó thì nó cựa mình sống lại và mọc lên đủ loại cây cối từng cặp.');
INSERT INTO "vn_ruwwad" VALUES(22,6,'22:6','Đó là bởi vì Allah là Chân Lý tuyệt đối, chắc chắn Ngài làm cho người chết sống lại và chắc chắn Ngài toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(22,7,'22:7','Quả thật, Giờ Tận Thế đang đến, điều đó không có gì phải hoài nghi, và Allah sẽ phục sinh tất cả những ai trong mộ.');
INSERT INTO "vn_ruwwad" VALUES(22,8,'22:8','Trong nhân loại có kẻ (vô đức tin) đã tranh luận về Allah không một chút hiểu biết nào cũng như không có sự chỉ đạo hay bất cứ một kinh sách rõ ràng nào cả.');
INSERT INTO "vn_ruwwad" VALUES(22,9,'22:9','Y quay người sang một bên (thể hiện sự tự cao tự đại) hầu làm cho (thiên hạ) lạc khỏi con đường của Allah. Y sẽ bị hạ nhục ở thế gian này; và vào Ngày Phục Sinh, TA sẽ cho y nếm hình phạt của lửa cháy bùng (nơi Hỏa Ngục).');
INSERT INTO "vn_ruwwad" VALUES(22,10,'22:10','(Y sẽ được bảo): “(Hình phạt) đó là do những điều mà bàn tay của ngươi đã gởi đi trước, bởi Allah không hề đối xử bất công với bầy tôi của Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(22,11,'22:11','Trong nhân loại có kẻ thờ phượng Allah trong sự hoài nghi. Nếu gặp điều lành thì y sẽ an lòng (tiếp tục thờ phượng Ngài), còn nếu gặp điều dữ thì y sẽ quay mặt đi (không thờ phượng Ngài nữa); thế là y thất bại cả đời này lẫn Đời Sau. Đó thật sự là một thất bại hiển nhiên.');
INSERT INTO "vn_ruwwad" VALUES(22,12,'22:12','Y cầu nguyện ngoài Allah những kẻ không gây hại cũng không mang lợi gì cho y. Đó là vì y đi lạc quá xa.');
INSERT INTO "vn_ruwwad" VALUES(22,13,'22:13','Y (chỉ) cầu nguyện đến kẻ gây hại cho y chứ (thực chất) chẳng hề mang lợi gì cho y cả. Thật là một tên chủ xấu xa, một người bạn đồng hành tồi tệ!');
INSERT INTO "vn_ruwwad" VALUES(22,14,'22:14','Allah chắc chắn sẽ thu nhận những người có đức tin và hành thiện vào những Ngôi Vườn Thiên Đàng, bên dưới có các dòng sông chảy. Quả thật, Allah làm bất cứ điều gì Ngài muốn (không ai có quyền can thiệp).');
INSERT INTO "vn_ruwwad" VALUES(22,15,'22:15','Kẻ nào nghĩ rằng Allah sẽ không bao giờ giúp đỡ (Thiên Sứ của Ngài) ở đời này và ở Đời Sau thì y hãy cột một sợi dây vào trần nhà mà tự thắt cổ, để cho y xem liệu kế hoạch của y sẽ làm tiêu tan điều khiến y tức giận chăng?');
INSERT INTO "vn_ruwwad" VALUES(22,16,'22:16','Tương tự, TA đã ban Nó (Qur’an) xuống với những câu rõ ràng, rành mạch, và rằng Allah sẽ hướng dẫn người nào Ngài muốn.');
INSERT INTO "vn_ruwwad" VALUES(22,17,'22:17','Những người có đức tin, những người Do Thái, những người Sabian, những người Thiên Chúa, những tín đồ Bái Hỏa giáo và những người thờ đa thần, vào Ngày Phục Sinh, chắc chắn Allah sẽ phân xét giữa họ. Quả thật, Allah là Đấng chứng giám tất cả mọi sự việc.');
INSERT INTO "vn_ruwwad" VALUES(22,18,'22:18','Lẽ nào Ngươi (Thiên Sứ Muhammad) không thấy rằng Allah là Đấng mà bất cứ ai trong các tầng trời và bất cứ ai trong trái đất cùng với mặt trời, mặt trăng, các tinh tú, núi non, cây cối, thú vật và đa số nhân loại đều phải phủ phục quỳ lạy? Tuy nhiên, vẫn có nhiều kẻ đáng bị Ngài trừng phạt. Và ai bị Allah hạ nhục chắc chắn sẽ không tìm thấy một ai có thể làm cho y vinh danh. Quả thật, Allah làm bất cứ điều gì Ngài muốn (không ai có quyền can thiệp).');
INSERT INTO "vn_ruwwad" VALUES(22,19,'22:19','Hai nhóm này (những người có đức tin và những kẻ vô đức tin) tranh luận nhau về Thượng Đế của họ. Rồi đây, những kẻ vô đức tin sẽ mặc áo từ Lửa, và từ trên đầu của họ, một loại nước cực sôi sẽ được xối lên.');
INSERT INTO "vn_ruwwad" VALUES(22,20,'22:20','(Nước cực sôi đó) sẽ làm tan chảy nội tạng và làn da của họ.');
INSERT INTO "vn_ruwwad" VALUES(22,21,'22:21','Họ sẽ bị đánh bằng những cây búa sắt.');
INSERT INTO "vn_ruwwad" VALUES(22,22,'22:22','Mỗi khi họ muốn lẩn trốn khỏi hình phạt đau đớn đó thì sẽ bị bắt trở lại, (và được bảo): “Các ngươi hãy nếm lấy hình phạt của sự thiêu đốt!”');
INSERT INTO "vn_ruwwad" VALUES(22,23,'22:23','Chắn chắn Allah sẽ thu nhận những người có đức tin và hành thiện vào những Ngôi Vườn Thiên Đàng, bên dưới có các dòng sông chảy; trong đó, họ sẽ được đeo những chiếc vòng vàng và ngọc trai; và y phục của họ sẽ là tơ lụa gấm vóc.');
INSERT INTO "vn_ruwwad" VALUES(22,24,'22:24','Bởi (trên trần gian), họ đã được hướng dẫn đến những lời nói tốt đẹp và họ đã được hướng dẫn đến con đường của Đấng Đáng Ca Tụng (con đường Islam).');
INSERT INTO "vn_ruwwad" VALUES(22,25,'22:25','Quả thật những kẻ vô đức tin và ngăn người khác vào con đường của Allah (Islam) cũng như ngăn người khác vào Masjid Al-Haram nơi mà TA đã làm ra thành của chung cho mọi người (đến thờ phượng TA), cho dân cư của nó cũng như cho du khách thập phương, (sẽ nếm lấy hình phạt đau đớn). Và ai muốn làm điều sai quấy trong đó thì TA sẽ cho nếm một hình phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(22,26,'22:26','(Ngươi hỡi Thiên Sứ Muhammad hãy nhớ lại) khi TA chỉ định cho Ibrahim địa điểm xây dựng Ngôi Đền, (TA đã phán bảo Y): “Ngươi không được Shirk với TA bất cứ thứ gì và Ngươi hãy giữ Ngôi Đền của TA trong sạch cho những người đi Tawwaf và cho những người đứng, cúi mình và quỳ lạy (trong lễ nguyện Salah).”');
INSERT INTO "vn_ruwwad" VALUES(22,27,'22:27','“Ngươi hãy thông báo kêu gọi nhân loại đến hành hương (Ngôi Đền mà Ngươi đã xây). Họ sẽ đi bộ hoặc cưỡi từng con lạc đà gầy yếu đến với Ngươi từ mọi nẻo đường.”');
INSERT INTO "vn_ruwwad" VALUES(22,28,'22:28','“Mục đích để họ chứng kiến những điều lợi được ban cấp cho họ (từ sự tha thứ và phần thưởng ở nơi Allah), để họ tụng niệm tên của Allah trong một số ngày nhất định và trên những vật nuôi (được mang đi tế) mà Ngài đã ban cấp cho họ. Họ hãy ăn thịt của chúng và phân phát cho những người nghèo.”');
INSERT INTO "vn_ruwwad" VALUES(22,29,'22:29','“Rồi để họ kết thúc tình trạng “Ehram’ của họ, để họ thực hiện lời thề nguyện của họ và để họ đi vòng quanh Ngôi Đền cổ.”');
INSERT INTO "vn_ruwwad" VALUES(22,30,'22:30','Đó (là điều các ngươi được lệnh), người nào tôn trọng những giới cấm của Allah thì là điều tốt cho y ở nơi Thượng Đế của y. Ngài cho phép các ngươi ăn thịt của những con vật nuôi ngoại trừ những gì (cấm) đã được đọc ra cho các ngươi (trong Qur’an). Bởi thế, các ngươi hãy tránh sự ô uế của (việc thờ cúng) các bục tượng và các ngươi hãy tránh lời nói giả dối.');
INSERT INTO "vn_ruwwad" VALUES(22,31,'22:31','Các ngươi hãy dành trọn đức tin nơi Allah và thành tâm thờ phượng chỉ riêng Ngài, không tổ hợp những đối tác ngang vai cùng với Ngài. Bởi ai tổ hợp những đối tác ngang vai cùng với Allah thì giống như y bị rơi từ trên trời xuống rồi bị chim tha đi hoặc bị một cơn gió thổi đi đến một nơi xa.');
INSERT INTO "vn_ruwwad" VALUES(22,32,'22:32','Đó (là điều lệnh của Allah). Ai tôn trọng các biểu hiệu của Allah thì y thực sự thuộc những trái tim ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(22,33,'22:33','Các ngươi được hưởng những lợi ích (cưỡi, thịt, lông, sữa, ...) từ chúng (những con vật tế) trong một thời gian nhất định cho đến khi chúng được đến nơi gần Ngôi Đền cổ (để cắt tiết).');
INSERT INTO "vn_ruwwad" VALUES(22,34,'22:34','TA đã quy định cho mỗi cộng đồng một nghi thức giết tế để họ có thể tụng niệm tên của Allah trên những con vật nuôi mà Ngài đã ban cấp cho họ dùng làm thực phẩm. Nhưng (các ngươi – hỡi nhân loại – hãy biết rằng) Thượng Đế của các ngươi chỉ là một Thượng Đế duy nhất, cho nên, các ngươi hãy thần phục Ngài; và Ngươi (hỡi Thiên Sứ Muhammad) hãy báo tin mừng cho những người một lòng sợ (Ngài).');
INSERT INTO "vn_ruwwad" VALUES(22,35,'22:35','Những người mà khi nghe nhắc đến Allah, trái tim của họ rún động (vì khiếp sợ sự trừng phạt của Ngài), luôn kiên nhẫn chịu đựng trên những hoạn nạn xảy đến, chu đáo thực hiện lễ nguyện Salah và chi dùng những bổng lộc TA ban cấp (cho con đường chính nghĩa của TA).');
INSERT INTO "vn_ruwwad" VALUES(22,36,'22:36','Những con vật tế, TA đã làm cho chúng thành một trong các Biểu Hiệu của Allah dành cho các ngươi, để các ngươi có được các món tốt lành từ nơi chúng. Cho nên, khi chúng đứng thành hàng (để cắt tiết tế lễ), các ngươi hãy nhân danh Allah lên chúng. Rồi khi chúng đã ngã xuống nằm một bên, các ngươi hãy ăn một phần thịt của chúng và bố thí một phần cho những người nghèo biết sống hài lòng với số phận và những người ăn xin. TA đã chế phục chúng cho các ngươi như thế mong rằng các ngươi biết tạ ơn TA.');
INSERT INTO "vn_ruwwad" VALUES(22,37,'22:37','Thịt và máu của chúng sẽ không bao giờ đến tận Allah mà chính sự ngoan đạo của các ngươi mới đến tận nơi Ngài. Ngài chế ngự chúng cho các ngươi như thế là để các ngươi có dịp tán dương sự vĩ đại của Allah về những gì Ngài đã hướng dẫn các ngươi. Và Ngươi (hỡi Thiên Sứ Muhammad) hãy báo tin mừng cho những người làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(22,38,'22:38','Chắc chắn Allah sẽ bảo vệ những người có đức tin; và chắc chắn Allah sẽ không yêu thương những kẻ tráo trở vong ân.');
INSERT INTO "vn_ruwwad" VALUES(22,39,'22:39','Ngài (Allah) cho phép những người (có đức tin) bị (những kẻ đa thần) áp bức được quyền chiến đấu đánh trả lại. Quả thật, Allah toàn năng trong việc trợ giúp họ.');
INSERT INTO "vn_ruwwad" VALUES(22,40,'22:40','(Những người bị áp bức đó) là những người bị đuổi khỏi nhà cửa của họ một cách không chính đáng, (họ bị đuổi) chỉ vì họ nói: “Thượng Đế của chúng tôi là Allah!” Nếu Allah không dùng một số người này chặn đứng một số người kia thì những tu viện, các nhà thờ, các giáo đường và những Masjid nơi mà tên của Allah được tụng niệm nhiều, chắc chắn sẽ bị phá hại tan nát. Chắc chắn Allah sẽ giúp đỡ những ai giúp đỡ (chính nghĩa của) Ngài. Quả thật, Allah là Đấng Hùng Mạnh, Đấng Quyền Năng.');
INSERT INTO "vn_ruwwad" VALUES(22,41,'22:41','(Những người được TA hứa trợ giúp) là những ai mà nếu TA để họ an cư trên trái đất thì họ chu đáo duy trì lễ nguyện Salah, xuất Zakah, kêu gọi làm điều tốt, ngăn cản làm điều xấu. Và kết cuộc của mọi sự việc đều thuộc về quyền của Allah.');
INSERT INTO "vn_ruwwad" VALUES(22,42,'22:42','Và nếu họ (đám dân của Ngươi – hỡi Thiên Sứ Muhammad) phủ nhận Ngươi thì quả thật trước họ đám dân của Nuh, của ‘Ad và của Thamud cũng đã phủ nhận (giống như vậy).');
INSERT INTO "vn_ruwwad" VALUES(22,43,'22:43','- Và cả đám dân của Ibrahim và đám dân của Lut (cũng phủ nhận giống tương tự).');
INSERT INTO "vn_ruwwad" VALUES(22,44,'22:44','Đám dân của Madyan (cũng đã phủ nhận Shu’aib giống như thế), (tương tự) Musa cũng đã bị phủ nhận. Tuy nhiên, TA đã thường tạm tha cho những kẻ vô đức tin (một thời gian ngắn) rồi sau đó TA mới trừng phạt họ (một cách bất ngờ). Bởi thế, (hãy xem) sự trừng phạt của TA giáng xuống (họ) khủng khiếp như thế nào!');
INSERT INTO "vn_ruwwad" VALUES(22,45,'22:45','Đích thực đã có nhiều thị trấn bị TA tiêu diệt khi đang làm điều sai quấy đến nỗi mái nhà của họ nằm lật ngược ngổn ngang, nhiều giếng nước và lâu đài kiên cố trở thành hoang tàn.');
INSERT INTO "vn_ruwwad" VALUES(22,46,'22:46','Lẽ nào họ đã không đi du ngoạn trên trái đất để cho tâm trí thông hiểu về sự việc đó, để cho đôi tai có dịp nghe câu chuyện về (sự trừng phạt)? Thật ra không phải cặp mắt của họ mù lòa mà chính tâm trí của họ đã mù lòa.');
INSERT INTO "vn_ruwwad" VALUES(22,47,'22:47','Họ (đám dân vô đức tin) hối thúc Ngươi (hỡi Thiên Sứ) mang hình phạt đến trong lúc Allah không bao giờ làm trái Lời Hứa của Ngài bởi vì quả thật một Ngày ở nơi Thượng Đế của Ngươi bằng một ngàn năm theo niên kỷ mà các ngươi đếm (trên thế gian).');
INSERT INTO "vn_ruwwad" VALUES(22,48,'22:48','Và đã có nhiều thị trấn được TA tạm tha (trong một thời gian ngắn) khi họ làm điều sai quấy rồi sau đó TA mới bắt phạt họ; và tất cả đều sẽ phải trở về trình diện TA.');
INSERT INTO "vn_ruwwad" VALUES(22,49,'22:49','Ngươi (hỡi Thiên Sứ) hãy nói: “Hỡi nhân loại! Quả thật Ta chỉ là một người cảnh báo công khai cho các người mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(22,50,'22:50','Do đó, những ai có đức tin và hành thiện thì sẽ được tha thứ tội lỗi và sẽ được ưu đãi bổng lộc (nơi Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(22,51,'22:51','Ngược lại, đối với những ai tìm mọi cách chối bỏ những Lời Mặc Khải của TA thì họ sẽ làm bạn với Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(22,52,'22:52','Trước Ngươi (hỡi Thiên Sứ), không một vị Sứ Giả hay một vị Nabi nào được TA cử phái đến (mà không gặp phải điều): khi Y mong muốn một điều tốt nào thì Shaytan nhảy vào quấy nhiễu điều Y muốn. Tuy nhiên, Allah hủy bỏ điều mà Shaytan đã xúi bẩy, rồi Allah khẳng định những Lời Mặc Khải của Ngài bởi Allah là Đấng Toàn Tri, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(22,53,'22:53','Mục đích để Ngài dùng điều mà Shaytan đã xen vào làm một sự thử thách đối với những ai mang trong lòng chứng bệnh (hoài nghi và giả tạo) cũng như những ai mà trái tim của họ đã chai cứng. Và những kẻ làm điều sai quấy chắc chắn sẽ ở trong sự thù hằn (của Allah và Thiên Sứ của Ngài) và ở cách xa (chân lý và sự chỉ đạo).');
INSERT INTO "vn_ruwwad" VALUES(22,54,'22:54','Và để cho những ai có kiến thức biết rằng (Qur’an) là Chân Lý từ nơi Thượng Đế của Ngươi, để họ có đức tin nơi Nó và để trái tim của họ khiêm tốn trước Nó. Allah chắc chắn sẽ hướng những người có đức tin đến với con đường ngay thẳng (dẫn tới sự hài lòng của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(22,55,'22:55','Riêng những kẻ vô đức tin thì sẽ vẫn cứ hoài nghi về (Qur’an) cho đến lúc Giờ Tận Thế hoặc sự trừng phạt của Ngày Đại Nạn bất ngờ xảy đến cho họ.');
INSERT INTO "vn_ruwwad" VALUES(22,56,'22:56','Mọi quyền hành trong Ngày hôm đó đều thuộc về Allah, Ngài sẽ xét xử họ. Cho nên, những ai có đức tin và hành thiện thì sẽ ở trong những Ngôi Vườn Thiên Đàng hạnh phúc.');
INSERT INTO "vn_ruwwad" VALUES(22,57,'22:57','Còn những kẻ vô đức tin và phủ nhận những Lời Mặc Khải của TA thì sẽ phải hứng chịu một sự trừng phạt nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(22,58,'22:58','Đối với những ai di cư vì con đường của Allah rồi bị giết hoặc chết thì Allah chắc chắn sẽ ban cấp cho họ một nguồn bổng lộc tốt lành (nơi Thiên Đàng), bởi Allah đích thực là Đấng Ban Phát bổng lộc tốt nhất.');
INSERT INTO "vn_ruwwad" VALUES(22,59,'22:59','Ngài chắc chắn sẽ đưa họ vào một nơi mà họ sẽ vô cùng hài lòng. Quả thật, Allah là Đấng Hằng Biết, Hằng Chịu Đựng.');
INSERT INTO "vn_ruwwad" VALUES(22,60,'22:60','Sự việc sẽ như thế. Và ai trả đũa ngang bằng với hành vi xâm hại mà y đã hứng chịu rồi lại bị áp bức lần nữa thì Allah chắc chắn sẽ trợ giúp y bởi quả thật Allah là Đấng Hằng Xí Xóa, Hằng Tha Thứ.');
INSERT INTO "vn_ruwwad" VALUES(22,61,'22:61','Đó là vì Allah nhập ban đêm vào ban ngày và nhập ban ngày vào ban đêm và rằng Allah là Đấng Hằng Nghe, Hằng Thấy.');
INSERT INTO "vn_ruwwad" VALUES(22,62,'22:62','Đó là vì Allah chính là Chân Lý còn những kẻ mà họ van vái cầu nguyện ngoài Ngài chỉ là ngụy tạo; và quả thật Allah là Đấng Tối Cao, Đấng Vĩ Đại.');
INSERT INTO "vn_ruwwad" VALUES(22,63,'22:63','Lẽ nào Ngươi (hỡi Thiên Sứ) không thấy việc Allah ban nước mưa từ trên trời xuống làm cho đất đai trở nên xanh tươi sao? Quả thật, Allah là Đấng Tinh Thông, Đấng Am Tường.');
INSERT INTO "vn_ruwwad" VALUES(22,64,'22:64','Mọi vật trong các tầng trời và mọi vật trong trái đất đều thuộc về Allah. Quả thật, Allah là Đấng Giàu Có, Đấng Đáng Được Ca Ngợi.');
INSERT INTO "vn_ruwwad" VALUES(22,65,'22:65','Lẽ nào Ngươi (hỡi Thiên Sứ Muhammad) không thấy rằng Allah đã chế ngự cho các ngươi mọi vật trong trái đất cũng như những con tàu vượt biển theo lệnh của Ngài?! Lẽ nào Ngươi không thấy Ngài giữ bầu trời không cho nó rơi xuống trái đất trừ phi Ngài cho phép?! Quả thật, Allah nhân từ và thương xót loài người.');
INSERT INTO "vn_ruwwad" VALUES(22,66,'22:66','Và Ngài là Đấng ban cho các ngươi sự sống rồi làm cho các ngươi chết rồi sau đó làm cho các ngươi sống trở lại một lần nữa. Tuy nhiên, con người thực sự là những kẻ vong ân.');
INSERT INTO "vn_ruwwad" VALUES(22,67,'22:67','TA đã quy định cho mỗi cộng đồng một nghi thức thờ phượng để họ theo đó mà thực hiện. Cho nên, Ngươi (Thiên Sứ Muhammad) chớ để cho họ (những kẻ đa thần) tranh luận với Ngươi trong sự việc này. Tuy nhiên, Ngươi hãy kêu gọi họ đến với Thượng Đế của Ngươi bởi Ngươi thực sự đang ở trên Chính Đạo.');
INSERT INTO "vn_ruwwad" VALUES(22,68,'22:68','Nếu họ tranh luận với Ngươi thì Ngươi hãy nói: “Quả thật Allah biết rõ những gì các người đang làm.”');
INSERT INTO "vn_ruwwad" VALUES(22,69,'22:69','“Allah sẽ phân xử các người vào Ngày Phục Sinh về những vấn đề mà các người đang tranh luận và bất đồng.”');
INSERT INTO "vn_ruwwad" VALUES(22,70,'22:70','Lẽ nào Ngươi (hỡi Thiên Sứ) không biết rằng Allah am tường tất cả mọi thứ trên trời và dưới đất hay sao? Quả thật, tất cả những thứ đó đều nằm trong Quyển Kinh (Al-lawhu Al-Mahfuzh), quả thật điều đó đối với Allah vô cùng đơn giản.');
INSERT INTO "vn_ruwwad" VALUES(22,71,'22:71','Tuy nhiên, họ (những kẻ thờ đa thần) lại tôn thờ ngoài Allah những vật (các bục tượng) mà Ngài đã không ban cho chúng một chút thẩm quyền nào và họ cũng không hề có nguồn kiến thức nào làm cơ sở cho việc tôn thờ đó (mà chỉ là bắt chước theo ông bà của họ một cách mù quáng); và những kẻ sai quấy sẽ không được ai giúp đỡ.');
INSERT INTO "vn_ruwwad" VALUES(22,72,'22:72','Và khi những Lời Mặc Khải của TA được đọc lên cho họ, Ngươi sẽ nhận thấy vẻ phản đối hiện rõ trên gương mặt những kẻ vô đức tin. Họ gần như (tức giận) muốn xông tới tấn công những ai đọc những Lời Mặc Khải của TA cho họ nghe. Ngươi (hỡi Thiên Sứ Muhammad) hãy nói với họ: “Vậy để Ta cho các người biết về một điều còn tệ hơn cả việc (cau mặt cau mày của các người), đó là Hỏa Ngục, thứ mà Allah đã hứa dành cho những kẻ vô đức tin, một nơi đến cuối cùng rất tồi tệ.”');
INSERT INTO "vn_ruwwad" VALUES(22,73,'22:73','Hỡi nhân loại! Hình ảnh thí dụ được trình bày cho các ngươi, các ngươi hãy lắng nghe cho kỹ! Quả thật, những kẻ mà các ngươi khấn vái ngoài Allah sẽ không bao giờ tạo ra được một con ruồi cho dù chúng có hợp sức lại với nhau để làm chuyện đó đi chăng nữa; và nếu con ruồi nào đó giật lấy món gì của chúng thì chúng cũng sẽ không thể giành lại được. Kẻ khấn vái cũng như kẻ được khấn vái đều bất lực.');
INSERT INTO "vn_ruwwad" VALUES(22,74,'22:74','Họ (những kẻ thờ đa thần) đã không đánh giá Allah đúng với quyền năng đích thực của Ngài. Quả thật Allah là Đấng Toàn Lực, Đấng Toàn Năng.');
INSERT INTO "vn_ruwwad" VALUES(22,75,'22:75','Allah tuyển chọn các vị Sứ Giả trong giới Thiên Thần và trong nhân loại. Quả thật, Allah là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "vn_ruwwad" VALUES(22,76,'22:76','Ngài (Allah) biết rõ điều gì trước họ (các Thiên Thần và nhân loại) và điều gì sau họ. Và tất cả mọi sự việc đều được đưa về trình Allah.');
INSERT INTO "vn_ruwwad" VALUES(22,77,'22:77','Hỡi những người có đức tin! Các ngươi hãy cúi mình, hãy quỳ lạy và hãy thờ phượng Thượng Đế của các ngươi, và các ngươi hãy hành thiện, mong rằng các ngươi sẽ thành công.');
INSERT INTO "vn_ruwwad" VALUES(22,78,'22:78','Các ngươi hãy đấu tranh cho Allah như một nghĩa vụ đối với Ngài. Ngài đã chọn các ngươi nhưng Ngài đã không gây khó khăn cho các ngươi trong tôn giáo. (Tôn giáo này) là tôn giáo theo tín ngưỡng của tổ phụ Ibrahim của các ngươi. Ngài đã đặt tên cho các ngươi là ''Muslim'' từ trước và trong hiện tại để cho Sứ Giả trở thành một nhân chứng cho các ngươi và để các ngươi sau đó trở thành nhân chứng cho nhân loại. Bởi thế, các ngươi (hỡi những người Muslim) hãy dâng lễ nguyện Salah, xuất Zakah và hãy bám chắc vào Allah. Ngài là Đấng Bảo Hộ của các ngươi, Đấng Bảo Hộ Ưu Việt, Đấng Cứu Giúp Toàn Năng.');
INSERT INTO "vn_ruwwad" VALUES(23,1,'23:1','Quả thật, những người có đức tin đã thành công(.');
INSERT INTO "vn_ruwwad" VALUES(23,2,'23:2','Họ là những người kính cẩn trong lễ nguyện Salah của mình.');
INSERT INTO "vn_ruwwad" VALUES(23,3,'23:3','Họ là những người tránh xa điều vô ích và tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(23,4,'23:4','Họ là những người tích cực xuất Zakah.');
INSERT INTO "vn_ruwwad" VALUES(23,5,'23:5','Họ là những người biết giữ phần kín của mình (khỏi hành vi tình dục Haram).');
INSERT INTO "vn_ruwwad" VALUES(23,6,'23:6','Ngoại trừ với vợ hoặc với những nữ nô lệ dưới quyền của họ thì họ không bị khiển trách (khi thỏa mãn nhu cầu tình dục).');
INSERT INTO "vn_ruwwad" VALUES(23,7,'23:7','Do đó, những ai muốn thỏa mãn nhục dục ngoài (phạm vi cho phép) đó thì họ là những kẻ vượt mức giới hạn (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(23,8,'23:8','(Những người có đức tin thành công) là những người mà họ thực hiện đúng với sự được ủy thác và đúng với giao ước.');
INSERT INTO "vn_ruwwad" VALUES(23,9,'23:9','Những người mà họ luôn chu đáo duy trì các lễ nguyện Salah của họ.');
INSERT INTO "vn_ruwwad" VALUES(23,10,'23:10','Những người (được mô tả) đó thật sự là những người thừa hưởng.');
INSERT INTO "vn_ruwwad" VALUES(23,11,'23:11','Họ sẽ thừa hưởng Firdaws (nơi cao nhất của Thiên Đàng), họ sẽ sống trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(23,12,'23:12','Quả thật, TA (Allah) đã tạo con người (Adam) từ một loại hỗn hợp đất sét.');
INSERT INTO "vn_ruwwad" VALUES(23,13,'23:13','Sau đó, TA tạo ra con người (con cháu của Adam) từ tinh dịch được lưu trữ tại một nơi an toàn (dạ con của người mẹ).');
INSERT INTO "vn_ruwwad" VALUES(23,14,'23:14','Rồi TA tạo tinh dịch thành một hòn máu đặc, kế đến TA tạo hòn máu đặc thành một miếng thịt, rồi từ miếng thịt TA tạo hóa thành xương, sau đó TA lại bao xương bằng thịt rồi làm cho nó thành một tạo vật khác (có linh hồn và được ra đời). Bởi thế, thật phúc thay Allah, Đấng Tạo Hóa Ưu Việt!');
INSERT INTO "vn_ruwwad" VALUES(23,15,'23:15','Rồi các ngươi sau đó chắc chắn sẽ chết.');
INSERT INTO "vn_ruwwad" VALUES(23,16,'23:16','Rồi các ngươi chắc chắn sẽ được cho sống lại vào Ngày Phán Xét (để chịu sự xét xử về các việc làm của các ngươi).');
INSERT INTO "vn_ruwwad" VALUES(23,17,'23:17','Và quả thật, TA đã tạo bên trên các ngươi bảy quỹ đạo và TA không bao giờ làm ngơ hay quên mất sự tạo hóa (của TA).');
INSERT INTO "vn_ruwwad" VALUES(23,18,'23:18','TA đã ban mưa từ trên trời xuống theo mức lượng nhất định rồi giữ nó lại trong đất; và chắc chắn TA thừa khả năng tháo bỏ nó đi.');
INSERT INTO "vn_ruwwad" VALUES(23,19,'23:19','TA đã dùng nó làm ra cho các ngươi những mảnh vườn chà là và các vườn nho, trong đó, các ngươi sẽ có được nhiều loại trái quả để dùng.');
INSERT INTO "vn_ruwwad" VALUES(23,20,'23:20','Và có một loại cây (Ô-liu) mọc tại núi Saina'' (Sinai) cho ra dầu và nước chấm để ăn.');
INSERT INTO "vn_ruwwad" VALUES(23,21,'23:21','Quả thật trong loài gia súc có một bài học cho các ngươi. TA ban cho các ngươi một loại thức uống (sữa) từ bụng của chúng cùng với nhiều nguồn lợi khác từ chúng và các ngươi còn có thể ăn thịt của chúng nữa.');
INSERT INTO "vn_ruwwad" VALUES(23,22,'23:22','Và chúng (các gia súc) được dùng để chuyên chở và cả những con tàu nữa.');
INSERT INTO "vn_ruwwad" VALUES(23,23,'23:23','Quả thật, TA đã phái Nuh đến với người dân của Y, Y bảo họ: “Này hỡi dân ta, các người hãy tôn thờ Allah bởi các người không có Thượng Đế nào khác ngoài Ngài. Lẽ nào các người không sợ Ngài?”');
INSERT INTO "vn_ruwwad" VALUES(23,24,'23:24','Nhưng những kẻ quyền thế vô đức tin trong đám dân của Y bảo: “Người này cũng chỉ là một người phàm giống như các người mà thôi. Thật ra Y chỉ muốn nắm quyền trên các người, bởi nếu Allah thực sự muốn thì Ngài đã phái xuống các Thiên Thần (thay vì là người phàm), chúng ta chưa hề nghe điều này nơi tổ tiên của chúng ta bao giờ cả.”');
INSERT INTO "vn_ruwwad" VALUES(23,25,'23:25','“Y thực chất chỉ là một gã tâm thần. Cho nên, các người hãy ráng đợi trong một thời gian (để rõ sự tình)”.');
INSERT INTO "vn_ruwwad" VALUES(23,26,'23:26','Nuh cầu nguyện: “Lạy Thượng Đế của bề tôi, xin Ngài giúp đỡ bề tôi (trừng phạt họ) bởi điều mà họ đã phủ nhận bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(23,27,'23:27','Vậy là TA đã mặc khải cho Y: “Ngươi hãy đóng một chiếc tàu dưới mắt quan sát của TA và theo lời mặc khải của TA. Khi nào mệnh lệnh (trừng phạt) của TA đến thì nước sẽ phụt lên từ bếp lò, (khi nhìn thấy hiện tượng đó) Ngươi hãy mang lên tàu mỗi loài động vật một cặp đực cái, cùng với gia đình của Ngươi ngoại trừ những ai mà lời hứa trừng phạt của TA đã được tuyên bố trước cho y; và Ngươi không được phép kêu nài với TA xin giùm cho những ai làm điều sai quấy. Bởi quả thật bọn chúng sẽ phải bị nhấn chìm (trong trận lụt đó).”');
INSERT INTO "vn_ruwwad" VALUES(23,28,'23:28','“Khi nào Ngươi và những ai theo Ngươi lên tàu thì các ngươi hãy nói: Alhamdulillah vì đã giải thoát bầy tôi khỏi đám người làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(23,29,'23:29','“Và Ngươi hãy nói: Lạy Thượng Đế của bề tôi, xin Ngài để bề tôi cập bến tại một nơi mà Ngài ban phúc bởi vì Ngài là Đấng giúp đưa lên bờ ưu việt hơn hết.”');
INSERT INTO "vn_ruwwad" VALUES(23,30,'23:30','Quả thật, nơi sự việc đó là những dấu hiệu (về quyền năng của TA trong việc giúp đỡ những người có đức tin cũng như trong việc trừng phạt những kẻ vô đức tin). Và quả thật, TA luôn thử thách.');
INSERT INTO "vn_ruwwad" VALUES(23,31,'23:31','Rồi TA đã tạo ra một thế hệ khác sau họ.');
INSERT INTO "vn_ruwwad" VALUES(23,32,'23:32','Rồi TA đã cử đến với họ một Thiên Sứ xuất thân từ họ, Y bảo: “Các người hãy tôn thờ Allah, các người không có Thượng Đế nào khác ngoài Ngài. Lẽ nào các người không sợ Ngài?”');
INSERT INTO "vn_ruwwad" VALUES(23,33,'23:33','Tuy nhiên, trong đám dân của Y, những kẻ quyền thế vô đức tin (nơi TA) và phủ nhận việc gặp gỡ (TA) ở Đời Sau cùng với những ai đã được TA ban cho một đời sống phú quý ở trần thế này, bảo: “Thật ra người này chỉ là một người phàm giống như các người mà thôi. Y cũng ăn loại thức ăn mà các người ăn và uống loại thức uống mà các người uống.”');
INSERT INTO "vn_ruwwad" VALUES(23,34,'23:34','“Nếu các người nghe theo một kẻ phàm tục giống như các người thì các người đúng là những kẻ thất bại.”');
INSERT INTO "vn_ruwwad" VALUES(23,35,'23:35','“Có phải Y đã hứa với các người rằng khi các người chết đi biến thành cát bụi và xương khô thì sau đó các người sẽ được dựng sống trở lại?”');
INSERT INTO "vn_ruwwad" VALUES(23,36,'23:36','“Thật xa vời, thật viển vông cho điều mà các người đã được hứa!”');
INSERT INTO "vn_ruwwad" VALUES(23,37,'23:37','“Quả thật, chỉ có một cuộc sống thế gian này mà thôi. Chúng ta sẽ sống rồi chết, chúng ta sẽ không được phục sinh trở lại bao giờ.”');
INSERT INTO "vn_ruwwad" VALUES(23,38,'23:38','“Y thật ra chỉ là một người phàm tục. Y chỉ đặt điều rồi đổ thừa cho Allah và chúng ta sẽ không tin nơi Y.”');
INSERT INTO "vn_ruwwad" VALUES(23,39,'23:39','(Vị Thiên Sứ đó của TA) cầu nguyện: “Lạy Thượng Đế của bề tôi, xin Ngài giúp đỡ bề tôi (trừng phạt họ) bởi vì họ đã phủ nhận bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(23,40,'23:40','(Allah) phán: “Chỉ trong một thời gian ngắn nữa thôi, chắc chắn bọn chúng sẽ ân hận.”');
INSERT INTO "vn_ruwwad" VALUES(23,41,'23:41','Rồi một âm thanh kinh hoàng chụp bắt chúng đúng với sự thật. Và TA đã làm cho chúng thành một đống bọt biển. Thật đáng đời những kẻ làm điều sai quấy!');
INSERT INTO "vn_ruwwad" VALUES(23,42,'23:42','Rồi TA đã tạo ra một thế hệ khác sau họ.');
INSERT INTO "vn_ruwwad" VALUES(23,43,'23:43','Không một cộng đồng nào có thể hối thúc thời hạn của mình chấm dứt sớm hơn và cũng không thể yêu cầu nó kết thúc trễ hơn.');
INSERT INTO "vn_ruwwad" VALUES(23,44,'23:44','TA đã lần lượt phái các Thiên Sứ của TA đến, cứ mỗi lần Thiên Sứ đến thì họ phủ nhận Y. Thế là TA đã bắt phạt hết cộng đồng này đến cộng đồng kia; và TA đã làm cho họ trở thành những câu chuyện răn đời, thật đáng đời cho đám người vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(23,45,'23:45','TA đã cử Musa và người anh em của Y, Harun, mang theo các Phép Lạ của TA cùng với một thẩm quyền rõ rệt.');
INSERT INTO "vn_ruwwad" VALUES(23,46,'23:46','Đi gặp Pha-ra-ông và các quần thần của hắn. Nhưng chúng đã ngạo mạn và chúng thực sự là một đám tự cao ngông cuồng.');
INSERT INTO "vn_ruwwad" VALUES(23,47,'23:47','(Pha-ra-ông và các quần thần của hắn) bảo nhau: “Chẳng lẽ chúng ta phải tin vào hai tên người phàm giống như chúng ta trong khi đám dân của hai tên đó lại là những nô lệ của chúng ta ư?”');
INSERT INTO "vn_ruwwad" VALUES(23,48,'23:48','Thế nên chúng đã phủ nhận hai người họ và đã trở thành những kẻ bị tiêu diệt.');
INSERT INTO "vn_ruwwad" VALUES(23,49,'23:49','TA quả thật đã ban cho Musa Kinh Sách (Cựu Ước), mong rằng họ được hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(23,50,'23:50','TA đã làm cho con trai của Mar-yam (Ysa) và mẹ của Y thành một hiện tượng lạ và TA đã để hai mẹ con họ định cư ở vùng đất cao, một chỗ nghỉ an lành với dòng suối chảy.');
INSERT INTO "vn_ruwwad" VALUES(23,51,'23:51','Hỡi các Thiên Sứ, các ngươi hãy dùng thức ăn tốt và sạch, và các ngươi hãy làm việc thiện, bởi TA thực sự biết rõ mọi điều các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(23,52,'23:52','Thật ra cộng đồng này của các ngươi là một cộng đồng duy nhất và TA chính là Thượng Đế của các ngươi, cho nên, các ngươi hãy sợ TA (mà ngoan đạo).');
INSERT INTO "vn_ruwwad" VALUES(23,53,'23:53','Thế nhưng họ đã chia rẽ thành những giáo phái riêng biệt và mỗi giáo phái hân hoan với điều mình đang có.');
INSERT INTO "vn_ruwwad" VALUES(23,54,'23:54','Thế nên, Ngươi (hỡi Thiên Sứ Muhammad) hãy cứ để mặc họ đắm mình trong sự ngu muội đến lúc (sự trừng phạt được giáng xuống).');
INSERT INTO "vn_ruwwad" VALUES(23,55,'23:55','Có phải họ tưởng rằng việc TA gia tăng của cải và con cái thêm cho họ,');
INSERT INTO "vn_ruwwad" VALUES(23,56,'23:56','Là TA thúc đẩy điều tốt đến cho họ ư? Không, họ không nhận thấy (sự thật của vấn đề).');
INSERT INTO "vn_ruwwad" VALUES(23,57,'23:57','Quả thật, những người sợ Thượng Đế của họ,');
INSERT INTO "vn_ruwwad" VALUES(23,58,'23:58','Những người có đức tin nơi các lời mặc khải của Thượng Đế của họ,');
INSERT INTO "vn_ruwwad" VALUES(23,59,'23:59','Những người không tổ hợp những đối tác ngang vai cùng với Thượng Đế của họ,');
INSERT INTO "vn_ruwwad" VALUES(23,60,'23:60','Những người cho đi những gì đã cho và tấm lòng của họ luôn lo sợ việc họ sẽ trở về trình diện Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(23,61,'23:61','Những người đó mới thực sự là những người thi đua nhau làm việc thiện tốt, mới thực sự là những người tiên phong.');
INSERT INTO "vn_ruwwad" VALUES(23,62,'23:62','TA không bắt bất cứ linh hồn nào gánh vác quá khả năng của nó và TA đang giữ một tập hồ sơ ghi chép sự thật và họ sẽ không bị đối xử bất công.');
INSERT INTO "vn_ruwwad" VALUES(23,63,'23:63','Không, trái tim của họ đang thờ ơ với Qur’an này và họ còn những tội lỗi khác đang làm.');
INSERT INTO "vn_ruwwad" VALUES(23,64,'23:64','Cho tới khi TA trừng phạt những kẻ sống xa hoa trong bọn họ thì họ mới van xin.');
INSERT INTO "vn_ruwwad" VALUES(23,65,'23:65','Ngày hôm nay, các ngươi chớ khóc lóc van xin. Chắc chắn các ngươi sẽ không được TA cứu giúp.');
INSERT INTO "vn_ruwwad" VALUES(23,66,'23:66','Quả thật, những lời mặc khải của TA đã được xướng đọc cho các ngươi nhưng các ngươi đã ngoảnh mặt bỏ đi.');
INSERT INTO "vn_ruwwad" VALUES(23,67,'23:67','Các ngươi ngạo mạn xem thường Nó (Qur’an), các ngươi thức thâu đêm tán gẫu chuyện không đâu.');
INSERT INTO "vn_ruwwad" VALUES(23,68,'23:68','Tại sao họ không chịu suy ngẫm về lời phán hay phải chăng điều này chỉ đến với họ mà không đến với tổ tiên của họ trước đây?');
INSERT INTO "vn_ruwwad" VALUES(23,69,'23:69','Hay phải chăng họ không nhận biết vị Thiên Sứ của họ nên mới từ chối Y?');
INSERT INTO "vn_ruwwad" VALUES(23,70,'23:70','Hay phải chăng họ nói Y bị điên? Không, Y (Thiên Sứ Muhammad) đã mang Chân Lý đến cho họ nhưng đa số bọn họ ghét sự thật.');
INSERT INTO "vn_ruwwad" VALUES(23,71,'23:71','Nếu Chân Lý hùa theo những điều ham muốn của họ thì các tầng trời, trái đất và vạn vật giữa trời đất chắc chắn sẽ sụp đổ. Không, TA đã ban cho họ Lời Nhắc Nhở nhưng họ quay lưng với Lời Nhắc Nhở dành cho họ.');
INSERT INTO "vn_ruwwad" VALUES(23,72,'23:72','Hoặc lẽ nào Ngươi (Thiên Sứ Muhammad) đã đòi họ tiền công? (Chắc chắn điều đó không xảy ra) bởi phần trả công của Thượng Đế của Ngươi tốt hơn vì Ngài là Đấng Ban Phát tốt nhất.');
INSERT INTO "vn_ruwwad" VALUES(23,73,'23:73','Quả thật, Ngươi đang kêu gọi họ đến với Con Đường Ngay Thẳng (con đường được hướng dẫn: Islam).');
INSERT INTO "vn_ruwwad" VALUES(23,74,'23:74','Và quả thật, những ai không có đức tin nơi Đời Sau chắc chắn sẽ lạc xa con đường (Islam).');
INSERT INTO "vn_ruwwad" VALUES(23,75,'23:75','Dẫu TA có thương xót họ và cứu họ thoát khỏi hoạn nạn thì chắc chắn họ cũng lại tiếp tục vượt giới hạn và lang thang lây lất.');
INSERT INTO "vn_ruwwad" VALUES(23,76,'23:76','Quả thật, TA đã trừng phạt họ nhưng họ vẫn không chịu hạ mình trước Thượng Đế của họ và cũng không chịu van xin khẩn cầu.');
INSERT INTO "vn_ruwwad" VALUES(23,77,'23:77','Mãi đến khi TA mở cánh cửa trừng phạt khủng khiếp dành cho họ (Hỏa Ngục ở Đời Sau) thì họ mới đâm ra tuyệt vọng trong đó.');
INSERT INTO "vn_ruwwad" VALUES(23,78,'23:78','Ngài (Allah) là Đấng đã tạo cho các ngươi thính giác, thị giác và trái tim nhưng các ngươi lại rất ít tri ân Ngài.');
INSERT INTO "vn_ruwwad" VALUES(23,79,'23:79','Ngài là Đấng đã rải các ngươi đông đảo trên khắp trái đất (với sự khác biệt tiếng nói, màu da, diện mạo) và (rồi đây) các ngươi sẽ được triệu tập đưa về trình diện Ngài trở lại.');
INSERT INTO "vn_ruwwad" VALUES(23,80,'23:80','Ngài là Đấng làm cho sống và làm cho chết, Ngài làm cho ban đêm và ban ngày luân chuyển tiếp nối nhau. Vậy chẳng lẽ các ngươi không suy ngẫm để hiểu ư?');
INSERT INTO "vn_ruwwad" VALUES(23,81,'23:81','Không, (những kẻ phủ nhận sự Phục Sinh) nói những điều giống như những kẻ (phủ nhận sự Phục Sinh) của thời trước đã nói.');
INSERT INTO "vn_ruwwad" VALUES(23,82,'23:82','Họ nói: “Lẽ nào chúng tôi được dựng sống lại sau khi chúng tôi đã chết đi và đã trở thành cát bụi và xương khô?”');
INSERT INTO "vn_ruwwad" VALUES(23,83,'23:83','“Quả thật, chúng tôi cũng như ông bà cha mẹ của chúng tôi trước đây đều được hứa điều này, nhưng đó chỉ là truyện cổ tích của người xưa (không hề có thật).”');
INSERT INTO "vn_ruwwad" VALUES(23,84,'23:84','(Hỡi Thiên Sứ Muhammad!) Ngươi hãy nói với họ: “Trái đất và những ai sống trong đó thuộc về ai nếu các người là những người thực sự biết?”');
INSERT INTO "vn_ruwwad" VALUES(23,85,'23:85','Họ sẽ nói: “Của Allah” Ngươi hãy bảo họ: “Vậy các người không lưu tâm ư?”');
INSERT INTO "vn_ruwwad" VALUES(23,86,'23:86','Ngươi hãy hỏi họ: “Ai là Đấng Chủ Tể của bảy tầng trời và chiếc Ngai Vương Vĩ Đại?”');
INSERT INTO "vn_ruwwad" VALUES(23,87,'23:87','Họ sẽ trả lời: “Allah” Ngươi hãy bảo họ: “Vậy sao các ngươi không sợ Ngài?”');
INSERT INTO "vn_ruwwad" VALUES(23,88,'23:88','Ngươi hãy hỏi họ: “Ai nắm quyền chi phối vạn vật, toàn quyền thống trị mà không một sức mạnh nào cưỡng lại được nếu các người thực sự biết?”');
INSERT INTO "vn_ruwwad" VALUES(23,89,'23:89','Họ sẽ trả lời: “Allah” Ngươi hãy bảo họ: “Vậy sao các người mê muội (trong sự thờ phượng các thần linh hư cấu ngoài Ngài?)”.');
INSERT INTO "vn_ruwwad" VALUES(23,90,'23:90','Không, TA (Allah) đã mang đến cho họ sự thật nhưng chính họ là những kẻ nói dối.');
INSERT INTO "vn_ruwwad" VALUES(23,91,'23:91','Allah không nhận bất cứ ai làm con trai (của Ngài) và Ngài cũng không hề có bất cứ thần linh nào bên cạnh Ngài (để chia sẻ cùng Ngài) bởi vì (nếu có nhiều thần linh cùng với Ngài) thì chắc chắn mỗi vị sẽ lấy đi cái mà mỗi vị đã tạo ra và chắc chắn vị này sẽ muốn cai trị vị kia (gây ra sự náo loạn). Quả thật, Allah quang vinh và tối cao hơn những gì họ đã qui cho Ngài.');
INSERT INTO "vn_ruwwad" VALUES(23,92,'23:92','(Ngài là) Đấng biết điều vô hình và hữu hình. Cho nên, Ngài tối cao vượt xa hẳn những (thần linh) mà họ đã tổ hợp (cùng với Ngài).');
INSERT INTO "vn_ruwwad" VALUES(23,93,'23:93','(Hỡi Thiên Sứ Muhammad!) Ngươi hãy nói (trong lời cầu nguyện): “Lạy Thượng Đế của bề tôi, nếu Ngài cho bề tôi nhìn thấy điều (hình phạt) được hứa với họ,”');
INSERT INTO "vn_ruwwad" VALUES(23,94,'23:94','“Thì xin Ngài, lạy Thượng Đế của bề tôi, đừng để bề tôi ở cùng với đám người làm điều sai quấy kia.”');
INSERT INTO "vn_ruwwad" VALUES(23,95,'23:95','Quả thật, TA (Allah) thừa sức cho Ngươi (Thiên Sứ Muhammad) nhìn thấy điều (hình phạt) mà TA cảnh báo họ.');
INSERT INTO "vn_ruwwad" VALUES(23,96,'23:96','Ngươi hãy đáp lại điều xấu bằng điều tốt hơn. TA biết rõ những gì họ thốt ra.');
INSERT INTO "vn_ruwwad" VALUES(23,97,'23:97','Và Ngươi hãy (cầu nguyện) nói: “Lạy Thượng Đế của bề tôi, xin Ngài che chở bề tôi tránh xa khỏi những xúi bẩy của những tên Shaytan.”');
INSERT INTO "vn_ruwwad" VALUES(23,98,'23:98','“Lạy Thượng Đế của bề tôi, xin Ngài che chở bề tôi (bằng việc) ngăn chúng không đến gần bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(23,99,'23:99','Rồi đến khi một trong số họ đối diện với cái chết, y mới van nài kêu xin: “Lạy Thượng Đế của bề tôi, xin Ngài hãy cho bề tôi được quay lại (trần gian)!”');
INSERT INTO "vn_ruwwad" VALUES(23,100,'23:100','“Chắc chắn bề tôi sẽ hành đạo và làm điều thiện tốt mà bề tôi đã bỏ qua.” Không. Quả thật đó chỉ là lời nói thốt ra trên môi mà thôi. Thế rồi trước mặt họ là cõi Barzakh (ngăn cách họ với trần thế) cho đến Ngày họ được Phục Sinh.');
INSERT INTO "vn_ruwwad" VALUES(23,101,'23:101','Rồi khi tiếng còi (Tận Thế) được thổi, đó là Ngày sẽ chẳng còn tình máu mủ ruột thịt giữa bọn họ nữa và cũng sẽ không có ai hỏi han đến ai.');
INSERT INTO "vn_ruwwad" VALUES(23,102,'23:102','(Trong Ngày hôm đó), những ai mà bàn cân của họ có trọng lượng nặng (bởi những việc hành đạo và những việc làm thiện tốt) thì họ sẽ là những người thành công.');
INSERT INTO "vn_ruwwad" VALUES(23,103,'23:103','Còn những ai mà bàn cân của họ có trọng lượng nhẹ (bởi những việc hành đạo và những việc làm thiện tốt) thì họ sẽ là những kẻ đã làm bản thân mình thất bại. Họ sẽ phải ở trong Hỏa Ngục mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(23,104,'23:104','Lửa sẽ thiêu đốt gương mặt của họ và họ nhe răng trong đó.');
INSERT INTO "vn_ruwwad" VALUES(23,105,'23:105','(Allah phán với họ): “Chẳng phải những lời mặc khải của TA đã được đọc cho các ngươi nghe nhưng các ngươi đã phủ nhận đó sao?”');
INSERT INTO "vn_ruwwad" VALUES(23,106,'23:106','Họ nài nỉ van cầu: “Lạy Thượng Đế của bầy tôi, bầy tôi đã chịu cùng cực và khổ ải, quả thật bầy tôi thực sự là những kẻ lầm lạc.”');
INSERT INTO "vn_ruwwad" VALUES(23,107,'23:107','“Lạy Thượng Đế của bầy tôi, xin Ngài cho bầy tôi ra khỏi Hỏa Ngục, nếu bầy tôi tái phạm thì đích thực bầy tôi là những kẻ làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(23,108,'23:108','(Lúc đó, Allah) phán: “Các ngươi hãy cút vào trong đó (Hỏa Ngục) và chớ thưa bẩm gì với TA nữa!”');
INSERT INTO "vn_ruwwad" VALUES(23,109,'23:109','Quả thật trong số đám bầy tôi của TA, có một nhóm người thường nói: “Lạy Thượng Đế của bầy tôi, bầy tôi đã tin, xin Ngài tha thứ cho bầy tôi và xin Ngài thương xót bầy tôi bởi Ngài là Đấng thương xót tốt nhất.”');
INSERT INTO "vn_ruwwad" VALUES(23,110,'23:110','Nhưng các ngươi (những kẻ vô đức tin) đã nhạo báng họ đến nỗi (việc nhạo báng đó) làm cho các ngươi quên đi Thông Điệp Nhắc Nhở của TA và các ngươi cứ hả hê cười nhạo báng họ.');
INSERT INTO "vn_ruwwad" VALUES(23,111,'23:111','Ngày hôm nay, TA ban thưởng cho họ bởi những gì họ đã kiên nhẫn chịu đựng. Họ thực sự là những người chiến thắng.');
INSERT INTO "vn_ruwwad" VALUES(23,112,'23:112','(Allah phán bảo những kẻ vô đức tin và những kẻ tội lỗi): “Các ngươi đã ở lại trên trái đất bao năm?”');
INSERT INTO "vn_ruwwad" VALUES(23,113,'23:113','Họ đáp: “Bầy tôi đã ở lại đó một ngày hay nửa ngày gì đấy, xin hỏi những vị ghi chép.”');
INSERT INTO "vn_ruwwad" VALUES(23,114,'23:114','(Allah phán): “Các ngươi đã ở lại đó chỉ trong chốc lát mà thôi, phải chi các ngươi biết điều đó.”');
INSERT INTO "vn_ruwwad" VALUES(23,115,'23:115','“Có phải các ngươi nghĩ rằng TA đã tạo hóa các ngươi là để vui chơi vô ích và rằng các ngươi sẽ không quay trở lại trình diện TA chăng?”');
INSERT INTO "vn_ruwwad" VALUES(23,116,'23:116','Allah tối cao, Ngài là Đức Vua đích thực, không có Thượng Đế nào khác ngoài Ngài, Ngài là Thượng Đế của chiếc Ngai Vương Vinh Dự.');
INSERT INTO "vn_ruwwad" VALUES(23,117,'23:117','Ai cầu xin một thần linh nào đó cùng với Allah mà không có bất cứ bằng chứng nào (cho việc làm đó) thì việc tính sổ y thuộc về Thượng Đế của y. Quả thật những kẻ vô đức tin sẽ không thành đạt.');
INSERT INTO "vn_ruwwad" VALUES(23,118,'23:118','(Hỡi Thiên Sứ Muhammad!) Ngươi hãy cầu nguyện: “Lạy Thượng Đế của bề tôi, xin Ngài tha thứ cho bề tôi và thương xót bề tôi bởi Ngài là Đấng thương xót tốt nhất!”.');
INSERT INTO "vn_ruwwad" VALUES(24,1,'24:1','(Đây là) chương Kinh TA ban xuống và qui định thành luật. TA ban xuống trong chương này những câu Kinh rất rõ ràng mong rằng các ngươi có thể ghi nhớ.');
INSERT INTO "vn_ruwwad" VALUES(24,2,'24:2','Nam nữ phạm tội Zina(, các ngươi hãy đánh mỗi người một trăm roi. Các ngươi chớ động lòng thương hại họ trong việc chấp hành mệnh lệnh của Allah nếu các ngươi thực sự có đức tin nơi Allah và Ngày Sau. Và các ngươi hãy mời một nhóm người có đức tin đến chứng kiến việc trừng phạt họ.');
INSERT INTO "vn_ruwwad" VALUES(24,3,'24:3','Người đàn ông Zina (gian dâm) không quan hệ tình dục ngoại trừ với người phụ nữ Zina hoặc với người phụ nữ thờ đa thần. (Tương tự), người phụ nữ Zina không quan hệ tình dục ngoại trừ với người đàn ông Zina hoặc với người đàn ông thờ đa thần. Và điều đó bị cấm đối với những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(24,4,'24:4','Những ai cáo buộc những người phụ nữ trinh thục (làm điều Zina) nhưng lại không đưa ra đủ bốn nhân chứng (cho điều cáo buộc đó) thì các ngươi hãy đánh họ tám mươi roi và sau này các ngươi chớ bao giờ nhận lấy bất cứ bằng chứng nào từ nơi họ nữa bởi họ là những kẻ nói dối và bất tuân (Allah).');
INSERT INTO "vn_ruwwad" VALUES(24,5,'24:5','Ngoại trừ những ai sau đó đã ăn năn sám hối và cải thiện bản thân thì quả thật Allah hằng tha thứ và nhân từ.');
INSERT INTO "vn_ruwwad" VALUES(24,6,'24:6','Những ai cáo buộc vợ của họ (ngoại tình) nhưng không có ai làm nhân chứng (cho việc cáo buộc đó) ngoài bản thân họ thì mỗi người trong số họ phải thề bốn lần với Allah rằng mình là người nói thật.');
INSERT INTO "vn_ruwwad" VALUES(24,7,'24:7','Và trong lời thề lần thứ năm rằng y sẽ bị Allah nguyền rủa nếu như y là kẻ nói dối.');
INSERT INTO "vn_ruwwad" VALUES(24,8,'24:8','Người vợ sẽ tránh được hình phạt (bị ném đá đến chết) nếu nữ ta thề bốn lần với Allah rằng y ta (người chồng) là kẻ nói dối.');
INSERT INTO "vn_ruwwad" VALUES(24,9,'24:9','Và trong lời thề lần thứ năm rằng Allah sẽ giáng cơn thịnh nộ của Ngài lên nữ ta nếu chồng của nữ ta là người nói thật.');
INSERT INTO "vn_ruwwad" VALUES(24,10,'24:10','Nếu không nhờ hồng phúc và lòng thương xót của Allah dành cho các ngươi (thì quả thật các ngươi đã bị trừng phạt), tuy nhiên, Allah là Đấng Đại Lượng, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(24,11,'24:11','Quả thật, những kẻ vu khống (‘A-ishah vợ của Thiên Sứ Muhammad) chỉ gồm một phần tử nhỏ trong các ngươi. Các ngươi chớ nghĩ rằng đó là điều xấu cho các ngươi, mà (các ngươi hãy biết rằng) đó là điều tốt cho các ngươi. Mỗi người trong bọn họ (những kẻ vu khống) sẽ phải chịu sự trừng phạt cho tội mà họ đã gây ra, riêng kẻ chủ mưu sẽ phải chịu hình phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(24,12,'24:12','Đáng lẽ ra khi nghe lời vu khống đó, những người có đức tin nam cũng như nữ nên nghĩ tốt cho các tín hữu của mình và nói với (những kẻ vu khống): “Đây rõ ràng là một sự vu khống!”');
INSERT INTO "vn_ruwwad" VALUES(24,13,'24:13','Đáng lẽ ra họ phải đưa ra đủ bốn nhân chứng cho việc đó bởi lẽ khi họ không đưa ra đủ số lượng nhân chứng thì đối với Allah họ đích thực là những kẻ nói dối.');
INSERT INTO "vn_ruwwad" VALUES(24,14,'24:14','Nếu không nhờ hồng phúc và lòng thương xót của Allah dành cho các ngươi ở đời này và ở Đời Sau thì chắc chắn các ngươi đã phải chịu sự trừng phạt khủng khiếp về tội các ngươi đã loan (tin đồn thất thiệt).');
INSERT INTO "vn_ruwwad" VALUES(24,15,'24:15','Khi các ngươi lan truyền (tin thất thiệt đó) cho nhau bằng chiếc lưỡi của các ngươi và các ngươi nói bằng miệng mồm của các ngươi về điều mà các ngươi không hề biết và các ngươi cho rằng đó là điều nhỏ nhặt, cỏn con (không đáng bận tâm) thì trái lại, đối với Allah, nó thực sự là một điều vô cùng trọng đại.');
INSERT INTO "vn_ruwwad" VALUES(24,16,'24:16','Đáng lẽ ra khi nghe (tin đồn thất thiệt đó), các ngươi nên nói: “Bàn tán chuyện này không phải là việc của chúng tôi. Quang vinh và trong sạch thay Allah, đây quả là một sự vu khống hết sức nghiêm trọng!”');
INSERT INTO "vn_ruwwad" VALUES(24,17,'24:17','Allah khuyên các ngươi chớ đừng bao giờ tái phạm điều tương tự nếu các ngươi thực sự là những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(24,18,'24:18','Allah trình bày rõ ràng cho các ngươi những lời mặc khải (của Ngài) và Allah là Đấng Toàn Tri, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(24,19,'24:19','Quả thật những ai trong số những người có đức tin thích cho chuyện xấu (tin đồn thất thiệt, lời vu khống, lời gièm pha) được lan truyền rộng rãi thì sẽ phải chịu một sự trừng phạt đau đớn ở trần thế và cả Đời Sau. Và Allah biết rõ (chân tướng sự thật) trong khi các ngươi không biết gì.');
INSERT INTO "vn_ruwwad" VALUES(24,20,'24:20','Nếu không nhờ hồng phúc và lòng thương xót của Allah dành cho các ngươi (thì quả thật các ngươi đã bị trừng phạt), tuy nhiên, Allah là Đấng Khoan Dung, Đấng Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(24,21,'24:21','Hỡi những người có đức tin, các ngươi chớ đi theo bước đường của Shaytan. Ai đi theo bước đường của Shaytan thì nó sẽ xúi y làm chuyện xấu và tội lỗi. Nếu không nhờ hồng phúc và lòng thương xót của Allah dành cho các ngươi thì sẽ không một ai trong các ngươi được tẩy sạch tội lỗi, tuy nhiên, Allah tẩy sạch tội lỗi cho ai Ngài muốn. Quả thật, Allah là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(24,22,'24:22','Những người giàu có và dư dả trong các ngươi chớ thề một cách dứt khoát rằng họ sẽ không bố thí cho bà con họ hàng của mình, cho những người khó khăn, và cho những người di cư vì con đường của Allah (đã liên can trong vụ phao tin đồn xấu về ‘A-ishah). Họ hãy nên đại lượng và bỏ qua. Lẽ nào các ngươi không thích được Allah tha thứ cho các ngươi hay sao?! Quả thật, Allah là Đấng Hằng Tha Thứ, Đấng Nhân Từ.(');
INSERT INTO "vn_ruwwad" VALUES(24,23,'24:23','Quả thật những ai vu khống những người phụ nữ có đức tin trinh thục và ngay thơ (làm điều Zina) thì họ sẽ bị nguyền rủa trên trần thế và cả Đời Sau; và họ sẽ phải chịu sự trừng phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(24,24,'24:24','Vào Ngày mà chiếc lưỡi, bàn tay cũng như bàn chân của họ sẽ đứng ra làm chứng chống lại họ về những gì mà họ đã làm.');
INSERT INTO "vn_ruwwad" VALUES(24,25,'24:25','Vào Ngày hôm đó, Allah sẽ thanh toán mọi việc làm của họ và họ sẽ biết Allah đích thực là chân lý hiển hiện.');
INSERT INTO "vn_ruwwad" VALUES(24,26,'24:26','Những phụ nữ hư đốn xứng đôi với những đàn ông hư đốn và những đàn ông hư đốn xứng đôi với những phụ nữ hư đốn; những người phụ nữ trinh thục đoan chính xứng đối với đàn ông đứng đắn và đàn ông đứng đắn xứng đối với phụ nữ trinh thục đoan chính. Những người (đoan chính, đứng đắn) này vô can với những điều họ nói, họ sẽ được tha thứ và sẽ được ban cho bổng lộc vinh dự (Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(24,27,'24:27','Hỡi những người có đức tin! Các ngươi chớ đi vào nhà của người khác trừ phi các ngươi đã xin phép và chào Salam đến chủ nhà. Điều đó tốt cho các ngươi mong rằng các ngươi lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(24,28,'24:28','Nhưng nếu các ngươi không thấy ai trong nhà thì các ngươi cũng chớ vào cho đến khi các ngươi được cho phép; còn nếu các ngươi được bảo ra về thì các ngươi hãy ra về, điều đó trong sạch cho các ngươi hơn, và Allah biết rõ những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(24,29,'24:29','Các ngươi không bị tội khi vào những ngôi nhà vắng chủ nhưng trong đó có đồ đạc của các ngươi. Quả thật, Allah biết rõ những gì các ngươi phơi bày cũng như những gì các ngươi giấu kín.');
INSERT INTO "vn_ruwwad" VALUES(24,30,'24:30','(Hỡi Thiên Sứ Muhammad!) Ngươi hãy bảo những người có đức tin nam hạ thấp cái nhìn xuống và giữ gìn phần kín (cơ thể) của họ, điều đó trong sạch hơn cho họ. Quả thật, Allah thông toàn mọi điều họ làm.');
INSERT INTO "vn_ruwwad" VALUES(24,31,'24:31','Và Ngươi hãy bảo những người có đức tin nữ hạ thấp cái nhìn xuống và giữ phần kín (cơ thể) của họ; Ngươi hãy bảo họ chớ phô bày nét đẹp của họ ra ngoài ngoại trừ những bộ phận nào lộ ra tự nhiên (gương mặt, bàn tay); Ngươi hãy bảo họ phủ khăn xuống che ngực; Ngươi hãy bảo họ chớ phô bày nét đẹp của họ ra ngoại trừ đối với chồng, cha ruột, cha chồng, con trai ruột, con trai của chồng, anh em trai ruột, con trai của anh em trai ruột, con trai của chị em gái ruột, những người phụ nữ giúp việc, những người đàn ông nô lệ thuộc quyền sở hữu của họ, những người đàn ông phục dịch đã mãn dục, hoặc những bé trai chưa có cảm giác với phần kín của phụ nữ; và Ngươi hãy bảo họ chớ đánh chân mạnh xuống đất hầu để người khác biết được đồ trang sức được giấu trong người của họ. Và tất cả các ngươi hãy quay về sám hối với Allah, hỡi những người có đức tin, mong rằng các ngươi thành đạt.');
INSERT INTO "vn_ruwwad" VALUES(24,32,'24:32','Các ngươi (hỡi những người có đức tin nam nữ) hãy kết hôn với những người độc thân trong các ngươi và những người đức hạnh trong số những người nô lệ nam và nữ của các ngươi. Nếu họ nghèo thì Allah sẽ làm cho họ giàu bởi hồng phúc của Ngài, quả thật Allah là Đấng có bổng lộc bao la, là Đấng hằng biết (mọi việc).');
INSERT INTO "vn_ruwwad" VALUES(24,33,'24:33','Đối với những ai không có điều kiện để kết hôn thì hãy kiềm chế (bản thân không phạm điều Haram) cho đến khi Allah làm cho họ giàu có bởi hồng phúc của Ngài. Và những ai trong số những người nô lệ thuộc quyền sở hữu của các ngươi muốn một chứng thư (chuộc tự do cho bản thân họ) thì các ngươi hãy viết cho họ nếu các ngươi biết họ là những người tốt, và các ngươi hãy đưa cho họ (món quà) từ tài sản mà Allah đã ban cấp cho các ngươi. Các ngươi chớ vì lợi ích cuộc sống trần thế mà ép những phụ nữ nô lệ của các ngươi hành nghề mãi dâm nếu họ muốn giữ tiết hạnh. Và nếu ai cưỡng ép họ thì sau việc cưỡng ép đó, Allah vẫn tha thứ và khoan dung (đối với những nạn nhân bị cưỡng ép, tội của họ được qui cho những ai cưỡng ép họ).');
INSERT INTO "vn_ruwwad" VALUES(24,34,'24:34','Quả thật, TA (Allah) đã ban xuống cho các ngươi những lời mặc khải rõ ràng cùng với những bài học (được rút ra từ những câu chuyện) của những người trước các ngươi và lời khuyên răn dành cho những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(24,35,'24:35','Allah là Ánh Sáng của các tầng trời và trái đất. Hình ảnh Ánh Sáng của Ngài giống như một ngọn đèn được đặt ở một hốc tường, ngọn đèn nằm trong một chiếc lồng thủy tinh, lồng thủy tinh như một ngôi sao lấp lánh; ngọn đèn được thắp từ một loại dầu của cây ô liu ân phúc, không cần mặt trời ở hướng đông hay ở hướng tây, dầu của nó gần như muốn tỏa sáng mặc dù lửa vẫn chưa chạm đến; ánh sáng bên trên ánh sáng. Allah hướng dẫn đến Ánh Sáng của Ngài đối với ai Ngài muốn. Allah đưa ra hình ảnh thí dụ cho nhân loại bởi vì Allah là Đấng Hằng Biết mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(24,36,'24:36','(Ánh Sáng của Allah tỏa ra) trong các ngôi nhà (Masjid), nơi mà Allah cho phép được dựng lên, được giữ sạch, và được tôn vinh; và là nơi mà tên của Ngài được tụng niệm và được tán dương sáng tối.');
INSERT INTO "vn_ruwwad" VALUES(24,37,'24:37','Những người mà việc mua bán đổi chác cũng như việc làm ăn kinh doanh không làm họ xao lãng việc tưởng nhớ Allah, không làm họ xao lãng việc dâng lễ nguyện Salah và xuất Zakah; họ luôn sợ cho cái Ngày mà những con tim cũng như những cặp mắt sẽ đảo lộn (vì sợ hãi và hoảng loạn cho việc sẽ bị Allah xét xử).');
INSERT INTO "vn_ruwwad" VALUES(24,38,'24:38','(Những người đó) sẽ được Allah ban cho phần thưởng tốt đẹp về những gì mà họ đã làm và Ngài sẽ ban thêm cho họ từ hồng phúc nơi Ngài. Quả thật, Allah ban phát bổng lộc một cách vô kể cho bất cứ ai Ngài muốn.');
INSERT INTO "vn_ruwwad" VALUES(24,39,'24:39','Những người vô đức tin, việc làm của họ giống như ảo ảnh nơi sa mạc mà một người đang khát cứ tưởng đó là nước cho tới khi y đến đó thì y không thấy gì cả, y chỉ thấy Allah nơi đó đang đợi xét xử y. Quả thật, Allah nhanh chóng trong việc xét xử và thanh toán.');
INSERT INTO "vn_ruwwad" VALUES(24,40,'24:40','Hoặc (tình cảnh của người vô đức tin) giống như (y đang ở dưới) bóng tối trong lòng biển, bên trên bị bao phủ bởi một lớp sống và bên trên lớp sống lại là một đám mây, lớp tối này chồng lên lớp tối kia, khi y đưa tay ra thì y không thể nhìn thấy tay của mình. Và ai mà Allah không cho y ánh sáng thì y không thể nào có được ánh sáng.');
INSERT INTO "vn_ruwwad" VALUES(24,41,'24:41','Lẽ nào Ngươi (Thiên Sứ Muhammad) không thấy vạn vật trong các tầng trời và trái đất đều tán dương Allah ngay cả những loài chim đang xòe đôi cánh của chúng? Quả thật, mỗi vật đều biết cách dâng lễ nguyện Salah cũng như cách tán dương (Allah) của riêng mình; và Allah biết rõ những gì chúng làm.');
INSERT INTO "vn_ruwwad" VALUES(24,42,'24:42','Quyền thống trị các tầng trời và trái đất đều thuộc về một mình Allah và tất cả mọi vật đều phải trở về với Ngài.');
INSERT INTO "vn_ruwwad" VALUES(24,43,'24:43','Lẽ nào Ngươi (Thiên Sứ Muhammad) không thấy việc Allah di chuyển các đám mây rồi gom chúng lại thành một khối lớn và sau đó Ngươi sẽ thấy nước từ giữa khối mây đó rơi xuống? Ngài ban từ bầu trời xuống (những đám mây to như) những quả núi bên trong chứa mưa đá mà Ngài dùng để trừng phạt ai Ngài muốn và Ngài lấy nó đi khỏi ai Ngài muốn. Tia chớp của những đám mây đó gần như làm hoa mắt (con người).');
INSERT INTO "vn_ruwwad" VALUES(24,44,'24:44','Allah lật trở đêm ngày, quả thật trong sự việc đó là bài học cho những người biết quan sát và thấu hiểu.');
INSERT INTO "vn_ruwwad" VALUES(24,45,'24:45','Allah đã tạo ra mọi sinh vật từ nước. Có loài di chuyển bằng bụng, có loài di chuyển bằng hai chân, và có loài di chuyển bằng bốn chân. Allah tạo ra bất cứ thứ gì Ngài muốn, quả thật Allah là Đấng Toàn Năng trên mọi việc.');
INSERT INTO "vn_ruwwad" VALUES(24,46,'24:46','Quả thật, TA (Allah) đã ban xuống những lời mặc khải rất rõ ràng, và Allah hướng dẫn ai Ngài muốn đến với con đường ngay thẳng.');
INSERT INTO "vn_ruwwad" VALUES(24,47,'24:47','Họ (những kẻ giả tạo đức tin – Munafiq) nói “Chúng tôi đã có đức tin nơi Allah và Thiên Sứ và chúng tôi xin vâng lệnh” nhưng rồi sau đó một phần tử của họ quay đi. Quả thật, họ không phải là những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(24,48,'24:48','Khi họ được mời gọi đến với Allah và vị Thiên Sứ của Ngài để Y phân xử giữa bọn họ thì một thành phần trong bọn họ quay bỏ đi.');
INSERT INTO "vn_ruwwad" VALUES(24,49,'24:49','Nhưng nếu họ gặp phần phải thì họ mới liền đến gặp Thiên Sứ (Muhammad).');
INSERT INTO "vn_ruwwad" VALUES(24,50,'24:50','Có phải trong tim của họ có một chứng bệnh hoặc phải chăng họ hoài nghi hay phải chăng họ sợ Allah và Thiên Sứ của Ngài xử ép họ? Không phải vậy, mà bởi vì họ là những kẻ làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(24,51,'24:51','Quả thật, lời của những người có đức tin thực sự khi được mời gọi đến với Allah và Thiên Sứ của Ngài để Y phân xử các vụ việc giữa họ là: “Chúng tôi xin nghe và vâng lời”. Và họ chính là những người thành đạt.');
INSERT INTO "vn_ruwwad" VALUES(24,52,'24:52','Những ai tuân lệnh Allah và Thiên Sứ của Ngài, sợ Allah và ngoan đạo đối với Ngài thì họ là những người sẽ giành thắng lợi.');
INSERT INTO "vn_ruwwad" VALUES(24,53,'24:53','Họ (những kẻ giả dối) thề thốt với Allah bằng lời thề trọng đại rằng nếu Ngươi (Thiên Sứ Muhammad) ra lệnh bảo họ (chiến đấu) thì chắc chắn họ sẽ ra đi chiến đấu. Ngươi hãy bảo họ: “Các ngươi chớ thề thốt làm chi, sự vâng lời là điều đã được rõ. Quả thật Allah thông toàn tất cả mọi điều các ngươi làm”.');
INSERT INTO "vn_ruwwad" VALUES(24,54,'24:54','Ngươi hãy bảo họ (hỡi Thiên Sứ Muhammad): “Các ngươi hãy tuân lệnh Allah và tuân lệnh Thiên Sứ (của Ngài).” Nhưng nếu các ngươi ngoảnh đi (không chịu tuân lệnh) thì quả thật Y (Muhammad) chỉ chịu trách nhiệm về việc làm của Y còn các ngươi phải chịu trách nhiệm cho việc làm của các ngươi, ngược lại, nếu các ngươi tuân lệnh Y thì các ngươi sẽ được hướng dẫn đúng đường. (Và các ngươi hãy biết rằng) nhiệm vụ của Thiên Sứ chỉ là truyền đạt rõ ràng (bức Thông Điệp của Allah).');
INSERT INTO "vn_ruwwad" VALUES(24,55,'24:55','Allah đã hứa với những người có đức tin và hành thiện trong số các ngươi rằng Ngài sẽ làm cho họ trở thành những người quản lý và lãnh đạo trên trái đất giống như những người đã trở thành những người quản lý và lãnh đạo trước họ, và Ngài sẽ làm cho họ ổn định tôn giáo của họ, tôn giáo mà Ngài đã hài lòng cho họ, và Ngài sẽ đổi lại cho họ sự an ninh và yên bình sau cảnh phập phòng lo sợ (với điều kiện) họ chỉ thờ phượng một mình TA và không tổ hợp với TA bất cứ gì. Và ai vô đức tin sau đó thì họ là những kẻ quấy rối và đại nghịch.');
INSERT INTO "vn_ruwwad" VALUES(24,56,'24:56','Các ngươi hãy chu đáo duy trì lễ nguyện Salah, hãy xuất Zakah và hãy tuân lệnh vị Thiên Sứ (của Allah), mong rằng các ngươi được thương xót.');
INSERT INTO "vn_ruwwad" VALUES(24,57,'24:57','Ngươi (Thiên Sứ Muhammad) chớ nghĩ rằng những kẻ vô đức tin sẽ thoát khỏi (Allah) trên trái đất, rồi đây chỗ ngụ của họ sẽ là Hỏa Ngục, một nơi quay về vô cùng tồi tệ.');
INSERT INTO "vn_ruwwad" VALUES(24,58,'24:58','Hỡi những người có đức tin, những ai dưới quyền của các ngươi cũng như những đứa trẻ chưa đến tuổi dậy thì phải xin phép các ngươi (khi muốn vào phòng riêng của các ngươi) vào ba thời điểm: lúc trước cuộc lễ nguyện Salah Fajr, lúc các ngươi cởi trần để (nằm nghỉ) buổi trưa, và lúc sau cuộc lễ nguyện Salah ‘I-sha’. Đó là ba thời điểm riêng tư mà các ngươi thường ở trần để nghi ngơi. Các ngươi cũng như họ sẽ không bị tội ngoài ba thời điểm đó nếu các ngươi qua lại giao tế với nhau. Như thế đó, Allah trình bày các lời mặc khải của Ngài một cách rõ ràng cho các ngươi và Allah là Đấng Toàn Tri, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(24,59,'24:59','Và khi những đứa trẻ của các ngươi đến tuổi dậy thì, các ngươi hãy bắt chúng xin phép (khi muốn vào phòng riêng của các ngươi) giống như những người trước chúng đã xin phép. Như thế đó, Allah trình bày các lời mặc khải của Ngài một cách rõ ràng cho các ngươi và Allah là Đấng Toàn Tri, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(24,60,'24:60','Những người phụ nữ đã quá tuổi không còn ham muốn tình dục thì họ không bị tội nếu như họ cởi lớp áo ngoài của họ ra không vì mục đích phô bày nét đẹp thân thể, nhưng nếu họ không cởi thì sẽ tốt hơn. Quả thật, Allah là Đấng Hằng Nghe, Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(24,61,'24:61','Người mù, người què, người bệnh và cả bản thân các ngươi sẽ không bị tội nếu các ngươi dùng bữa tại nhà của các ngươi hoặc tại nhà của cha, của mẹ, của anh em ruột, của chị em ruột, của chú bác, của cô, của cậu, của dì, hoặc tại nhà mà các ngươi giữ chìa khóa, hoặc tại nhà của bạn. Các ngươi sẽ không bị tội nếu các ngươi ăn cùng nhau hay ăn riêng từng nhóm. Tuy nhiên, khi nào các ngươi muốn vào nhà thì các ngươi hãy chào Salam đến nhau bằng lời chào phúc lành mà Allah đã dạy cho các ngươi. Như thế đó, Allah trình bày các lời mặc khải của Ngài một cách rõ ràng cho các ngươi mong rằng các ngươi thấu hiểu.');
INSERT INTO "vn_ruwwad" VALUES(24,62,'24:62','Quả thật những người có đức tin thực sự chính là những người đã tin nơi Allah và Thiên Sứ của Ngài và khi ở cùng với Y (Thiên Sứ) để bàn công việc chung thì họ không ngang nhiên bỏ đi trừ phi đã xin phép Y. Quả thật, những ai xin phép Y là những người thực sự có đức tin nơi Allah và Thiên Sứ của Ngài. Do đó, nếu họ xin phép Ngươi (hỡi Thiên Sứ Muhammad) để đi lo việc riêng của họ thì Ngươi hãy chấp thuận cho ai Ngươi muốn trong số họ và Ngươi hãy cầu xin Allah tha thứ cho họ bởi Allah là Đấng hằng tha thứ, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(24,63,'24:63','Các ngươi chớ coi việc Thiên Sứ (Muhammad) mời gọi các ngươi giống như việc các ngươi mời gọi nhau.( Quả thật, Allah biết rõ những ai trong các ngươi lén lút bỏ đi. Do đó, những ai trái lệnh của Y hãy coi chừng gặp phải họa hoặc gặp phải sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(24,64,'24:64','Quả thật, vạn vật trong các tầng trời và trái đất đều là của Allah. Ngài chắc chắn biết rõ điều mà các ngươi định làm, (và rồi đây) vào một Ngày họ sẽ được trở về trình diện Ngài để Ngài cho họ biết về những gì họ đã làm. Quả thật, Allah biết rõ tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(25,1,'25:1','Hồng phúc thay Đấng đã ban xuống Tiêu Chuẩn phân biệt phúc tội cho người bề tôi (Muhammad) của Ngài để Y làm vị cảnh báo cho muôn loài.');
INSERT INTO "vn_ruwwad" VALUES(25,2,'25:2','Đấng mà quyền thống trị các tầng trời và trái đất đều thuộc về riêng một mình Ngài, Ngài không nhận (bất cứ ai) làm con trai và Ngài cũng không hề có bất cứ đối tác nào chia sẻ cùng Ngài trong việc ngự trị (vũ trụ); Ngài đã tạo hóa vạn vật và định cho mỗi vật với mức lượng nhất định.');
INSERT INTO "vn_ruwwad" VALUES(25,3,'25:3','(Những kẻ thờ đa thần) tôn thờ ngoài Ngài các thần linh không có khả năng tạo hóa ra bất cứ thứ gì và chính chúng lại là những tạo vật được tạo ra, chúng không có khả năng gây hại hay ban lợi gì cho bản thân chúng, chúng không có khả năng làm cho chết hay làm cho sống và cũng không có khả năng phục sinh.');
INSERT INTO "vn_ruwwad" VALUES(25,4,'25:4','Những kẻ vô đức tin nói: “Đây chỉ là điều dối trá mà Y (Muhammad) đã bịa đặt và một nhóm người nào đó đã giúp Y (làm ra Qur’an)”. Nhưng quả thật họ mới chính là những kẻ sai quấy và đặt điều dối trá.');
INSERT INTO "vn_ruwwad" VALUES(25,5,'25:5','Họ bảo: “Chỉ là chuyện cổ tích của người xưa được viết lại và đã đọc cho Y nghe sáng chiều mà thôi!”');
INSERT INTO "vn_ruwwad" VALUES(25,6,'25:6','Ngươi (hỡi Thiên Sứ Muhammad!) hãy nói với họ: “Nó (Qur’an) là do Đấng hằng biết những điều bí mật trong các tầng trời và trái đất ban xuống. Ngài đích thực là Đấng Hằng Tha Thứ, Đấng Nhân Từ.”');
INSERT INTO "vn_ruwwad" VALUES(25,7,'25:7','Họ (những kẻ vô đức tin) nói: “Thiên Sứ gì mà ăn thức ăn và đi rong giữa chợ thế kia! Đáng lẽ ra phải có một vị Thiên Thần nào đó được cử xuống làm vị cảnh báo cùng với Y thì mới phải lý!”');
INSERT INTO "vn_ruwwad" VALUES(25,8,'25:8','“Hoặc đáng lẽ ra Y phải được ban cho một kho tàng châu báu hoặc Y phải có một ngôi vườn để Y ăn trái quả từ nó, (thế mới là hợp lý)!” Và những kẻ sái quấy nói: “Các người chỉ đang nghe theo một tên bị bùa ếm!”');
INSERT INTO "vn_ruwwad" VALUES(25,9,'25:9','Ngươi hãy xem cách họ đưa ra các hình ảnh thí dụ về Ngươi. Họ đã lầm lạc nên không thể tìm thấy con đường (đúng đắn).');
INSERT INTO "vn_ruwwad" VALUES(25,10,'25:10','Thật hồng phúc thay cho Đấng mà nếu Ngài muốn Ngài sẽ làm ra cho Ngươi (Thiên Sứ Muhammad) những thứ còn tốt hơn những thứ đó. (Rồi đây Ngươi sẽ được ban cho) các ngôi vườn Thiên Đàng bên dưới có các dòng sông chảy và Ngài sẽ làm ra cho Ngươi các tòa lâu đài.');
INSERT INTO "vn_ruwwad" VALUES(25,11,'25:11','Không! (Những kẻ vô đức tin) phủ nhận Giờ Tận Thế, và TA (Allah) đã chuẩn bị cho những ai phủ nhận Giờ Tận Thế lửa ngọn cháy bùng.');
INSERT INTO "vn_ruwwad" VALUES(25,12,'25:12','Khi Hỏa Ngục nhìn thấy họ từ đằng xa, họ sẽ nghe thấy tiếng gào thét dữ dội của nó.');
INSERT INTO "vn_ruwwad" VALUES(25,13,'25:13','Rồi khi họ bị xích và bị ném vào trong một xó chật hẹp của Hỏa Ngục, họ sẽ kêu than và mong được chết phứt cho xong.');
INSERT INTO "vn_ruwwad" VALUES(25,14,'25:14','(Rồi có tiếng bảo họ): “Ngày hôm nay, các ngươi chớ kêu la cũng như chớ mong được chết phứt cho xong mà các ngươi hãy kêu la và mong được chết nhiều lần”.');
INSERT INTO "vn_ruwwad" VALUES(25,15,'25:15','Ngươi (hỡi Thiên Sứ Muhammad) hãy nói với họ: “Cái đó (hình phạt nơi Hỏa Ngục) tốt hơn hay Thiên Đàng Vĩnh Cửu được hứa cho những người ngoan đạo tốt hơn?” Thiên Đàng chắc chắn sẽ là phần thưởng và là nơi đến cuối cùng của họ.');
INSERT INTO "vn_ruwwad" VALUES(25,16,'25:16','Họ sẽ sống trong đó mãi mãi và họ sẽ có được bất cứ thứ gì họ muốn. Đó là lời hứa mà Thượng Đế của Ngươi (hỡi Thiên Sứ Muhammad) bắt buộc phải thực hiện.');
INSERT INTO "vn_ruwwad" VALUES(25,17,'25:17','Và vào ngày mà Allah sẽ tập trung họ cùng với những thứ mà họ thờ phượng ngoài Ngài, Ngài sẽ phán bảo (những kẻ được thờ phượng): “Các ngươi dẫn đám bề tồi của TA lạc lối hay tự bản thân họ lầm lạc?”');
INSERT INTO "vn_ruwwad" VALUES(25,18,'25:18','(Những kẻ được thờ phượng ngoài Allah) nói: “Quang vinh và trong sạch thay Ngài! Bầy tôi (và cả bọn họ) không ai được phép nhận lấy ngoài Ngài bất kỳ ai (vật gì) làm đấng bảo hộ. Tuy nhiên, Ngài đã ban cho họ cũng như cha mẹ của họ mọi thứ tiện nghi (của thế gian) để rồi họ đã quên mất Lời Nhắc Nhở (của Ngài) và đã trở thành những kẻ suy đốn.');
INSERT INTO "vn_ruwwad" VALUES(25,19,'25:19','Allah phán bảo (những kẻ thờ đa thần): “Quả thật, (những thần linh mà các ngươi thờ phượng ngoài TA) đã khẳng định các ngươi gian dối về những điều mà các ngươi nói. Cho nên, các ngươi không thể gở gạc cũng không được giúp đỡ.” Và người nào trong các ngươi làm điều sai quấy thì TA sẽ cho y nếm sự trừng phạt to lớn.');
INSERT INTO "vn_ruwwad" VALUES(25,20,'25:20','Tất cả các vị Thiên Sứ mà TA đã cử phái đến trước Ngươi (Thiên Sứ Muhammad) đều là (những người phàm), họ đều ăn thức ăn và đi rong giữa chợ. Quả thật, TA dùng một số người này thử thách một số người kia để xem các ngươi có kiên nhẫn chịu đựng hay không, và Thượng Đế của Ngươi là Đấng Hằng Thấy (hết tất cả mọi sự việc).');
INSERT INTO "vn_ruwwad" VALUES(25,21,'25:21','Những kẻ không hy vọng gặp TA (ở Đời Sau) nói: “Đáng lẽ ra phải có Thiên Thần nào đó được cử xuống cho bọn ta hoặc bọn ta phải nhìn thấy Thượng Đế của mình, (như thế mới hợp lý chứ).” Quả thật, họ đã tự cao và tự đại quá mức.');
INSERT INTO "vn_ruwwad" VALUES(25,22,'25:22','Vào ngày mà họ sẽ thấy các Thiên Thần, đó là ngày mà những kẻ tội lỗi sẽ không được một tin vui nào và các Thiên Thần sẽ bảo họ: “Dang ra, hãy dang ra xa!”');
INSERT INTO "vn_ruwwad" VALUES(25,23,'25:23','Và TA sẽ xét lại mọi việc mà họ đã làm, tất cả đều bị TA biến nó thành tro bụi bay tứ tán.');
INSERT INTO "vn_ruwwad" VALUES(25,24,'25:24','Vào ngày đó, những cư dân của Thiên Đàng (những người có đức tin) sẽ có một chỗ ngụ tốt đẹp và một nơi nghỉ tuyệt vời.');
INSERT INTO "vn_ruwwad" VALUES(25,25,'25:25','Và vào Ngày mà bầu trời và những đám mây sẽ bị tách ra, rồi các Thiên Thần sẽ được cử xuống đông đảo.');
INSERT INTO "vn_ruwwad" VALUES(25,26,'25:26','Vương quyền đích thực của Ngày hôm đó thuộc về một mình Đấng Độ Lượng, và đó là ngày đầy khó khăn và khốn khổ cho những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(25,27,'25:27','Và vào Ngày mà kẻ làm điều sai quấy sẽ vừa cắn đầu ngón tay vừa than thở (trong hối tiếc): “Ôi giá như mình đã đi theo con đường cùng với Thiên Sứ (Muhammad).”');
INSERT INTO "vn_ruwwad" VALUES(25,28,'25:28','“Ôi thật khốn khổ thay, ước gì mình đừng làm bạn với kẻ đó!”');
INSERT INTO "vn_ruwwad" VALUES(25,29,'25:29','“Quả thật, chính hắn đã đưa mình lạc khỏi Thông Điệp Nhắc Nhở sau khi mình đã tiếp thu Nó, quả thật Shaytan chính là một tên phản bội loài người.”');
INSERT INTO "vn_ruwwad" VALUES(25,30,'25:30','Và Thiên Sứ (Muhammad) nói: “Lạy Thượng Đế của bề tôi, quả thật người dân của bề tôi đã xao lãng Qur’an này!”');
INSERT INTO "vn_ruwwad" VALUES(25,31,'25:31','Như thế đó, TA đã lập cho mỗi vị Nabi một số kẻ thù trong đám người tội lỗi, nhưng Thượng Đế của Ngươi (Thiên Sứ Muhammad) đủ làm một Vị Hướng Dẫn và một Vị Cứu Tinh (cho Ngươi).');
INSERT INTO "vn_ruwwad" VALUES(25,32,'25:32','Những kẻ vô đức tin bảo: “Đáng lẽ ra Qur’an nên được mặc khải cho Y trong một lần mới đúng!” (TA mặc khải xuống) như thế là để TA củng cố tấm lòng của Ngươi (Muhammad); và TA đọc Nó dần dần theo từng giai đoạn.');
INSERT INTO "vn_ruwwad" VALUES(25,33,'25:33','Không một hình ảnh thí dụ nào do chúng mang đến hỏi Ngươi mà TA lại không mang sự thật và lời giải thích tốt đẹp nhất đến cho Ngươi (để đối đáp với họ).');
INSERT INTO "vn_ruwwad" VALUES(25,34,'25:34','Những kẻ bị tập trung úp mặt xuống để tống vào Hỏa Ngục là những kẻ có nơi ở xấu xa nhất và là những kẻ ở trên con đường lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(25,35,'25:35','Quả thật, TA đã ban Kinh Sách cho Musa và TA đã chỉ định người anh em của Y, Harun, làm một người phò tá cho Y.');
INSERT INTO "vn_ruwwad" VALUES(25,36,'25:36','TA đã phán bảo (cả hai): “Hai ngươi hãy ra đi gặp đám người đã phủ nhận những Phép Lạ của TA.” (Nhưng đám người đó đã phủ nhận hai người họ) nên TA đã tiêu diệt tất cả bọn chúng.');
INSERT INTO "vn_ruwwad" VALUES(25,37,'25:37','Người dân của Nuh, khi họ cho những Thiên Sứ (của TA) nói dối, TA đã nhấn chìm họ dưới nước và lấy họ làm một bài học cảnh cáo nhân loại. Và TA đã chuẩn bị cho những kẻ làm điều sai quấy hình phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(25,38,'25:38','Dân ‘Ad, dân Thamud, dân Rass và nhiều thế hệ giữa họ cũng đã chịu số phận tương tự.');
INSERT INTO "vn_ruwwad" VALUES(25,39,'25:39','TA đã trình bày cho mỗi thế hệ những hình ảnh thí dụ và TA đã tiêu diệt từng thế hệ một cách khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(25,40,'25:40','Quả thật họ (những người Quraish) đã đi ngang qua một thị trấn bị tàn phá bởi một trận mưa hiểm ác. Thế, họ đã không từng nhìn thấy nó hay sao? Không, họ không mong được phục sinh trở lại (cho việc xét xử).');
INSERT INTO "vn_ruwwad" VALUES(25,41,'25:41','Khi thấy Ngươi, họ chỉ chế giễu Ngươi, họ thường bảo: “Lẽ nào đây là kẻ mà Allah cử đến làm Thiên Sứ cho bọn mình chăng?”');
INSERT INTO "vn_ruwwad" VALUES(25,42,'25:42','“Quả thật, suýt nữa thì Y đã làm cho bọn mình từ bỏ những thần linh của bọn mình mất rồi nếu như bọn mình không thực sự kiên trì với các đấng ấy.” Rồi đây khi đối diện với sự trừng phạt thì họ sẽ biết ai thực sự mới là kẻ đã lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(25,43,'25:43','Ngươi (Thiên Sứ Muhammad) có thấy kẻ đã lấy dục vọng của mình làm thần linh của y hay không? Lẽ nào Ngươi sẽ là người chịu trách nhiệm cho việc làm của y?!');
INSERT INTO "vn_ruwwad" VALUES(25,44,'25:44','Lẽ nào Ngươi nghĩ đa số bọn họ nghe hoặc hiểu ư? Họ thật ra chẳng khác nào súc vật; không, họ thậm chí còn tệ hơn.');
INSERT INTO "vn_ruwwad" VALUES(25,45,'25:45','Lẽ nào Ngươi đã không nhìn thấy việc Thượng Đế của Ngươi đã kéo dài cái bóng như thế nào ư? Nếu muốn, Ngài có thể làm cho nó đứng yên tại chỗ. Nhưng rồi TA đã dùng mặt trời làm một chỉ điểm cho nó (khiến nó thay đổi dài ngắn khác nhau).');
INSERT INTO "vn_ruwwad" VALUES(25,46,'25:46','Rồi TA dần dần thu nó (cái bóng) lại một cách đơn giản (qua sự lên cao của mặt trời).');
INSERT INTO "vn_ruwwad" VALUES(25,47,'25:47','Ngài (Allah) là Đấng đã làm cho các ngươi ban đêm thành một tấm phủ, giấc ngủ thành một sự nghỉ ngơi và ban ngày thành (thời gian lý tưởng) để hoạt động cho cuộc sống.');
INSERT INTO "vn_ruwwad" VALUES(25,48,'25:48','Ngài là Đấng đã gởi những luồng gió làm tín hiệu báo trước cơn mưa của Ngài sắp đến. Và TA ban từ trên trời xuống nguồn nước tinh khiết.');
INSERT INTO "vn_ruwwad" VALUES(25,49,'25:49','Để TA dùng nó làm sống lại lớp đất chết khô và để giải khát động vật và loài người trong số tạo vật mà TA đã tạo ra rất nhiều.');
INSERT INTO "vn_ruwwad" VALUES(25,50,'25:50','Quả thật, TA đã phân phối số nước đó giữa họ để họ biết ghi nhớ, nhưng đa số nhân loại thường vô ơn.');
INSERT INTO "vn_ruwwad" VALUES(25,51,'25:51','Nếu muốn, TA có thể cử đến với mỗi thị trấn một người Cảnh Báo.');
INSERT INTO "vn_ruwwad" VALUES(25,52,'25:52','Bởi thế, Ngươi (Thiên Sứ Muhammad) chớ nghe theo những kẻ vô đức tin mà hãy dùng Qur’an đấu tranh chống lại họ bằng một cuộc đấu tranh mạnh mẽ.');
INSERT INTO "vn_ruwwad" VALUES(25,53,'25:53','Ngài là Đấng đã làm cho hai biển nước tự do chảy, một loại là nước ngọt dễ uống và một loại là nước mặn và chát. Ngài đã dựng một bức chắn tách biệt giữa chúng không cho chúng trộn lẫn với nhau.');
INSERT INTO "vn_ruwwad" VALUES(25,54,'25:54','Ngài là Đấng đã tạo hóa con người từ nước, rồi Ngài thiết lập cho y mối quan hệ ruột thịt, mối quan hệ hôn nhân. Và Thượng Đế của Ngươi là Đấng Toàn Năng.');
INSERT INTO "vn_ruwwad" VALUES(25,55,'25:55','Nhưng họ lại tôn thờ ngoài Allah những vật không giúp ích cũng như không gây hại được họ; và kẻ vô đức tin luôn (đi theo Shaytan) chống đối lại Thượng Đế của y.');
INSERT INTO "vn_ruwwad" VALUES(25,56,'25:56','TA (Allah) cử Ngươi (hỡi Thiên Sứ Muhammad) đến chỉ để làm một người báo tin mừng và một người cảnh báo.');
INSERT INTO "vn_ruwwad" VALUES(25,57,'25:57','Ngươi hãy nói (với họ): “Ta không đòi các người trả công cho Ta về sứ mạng này ngoại trừ việc ai tự nguyện chọn lấy con đường đi đến với Thượng Đế của y.”');
INSERT INTO "vn_ruwwad" VALUES(25,58,'25:58','Ngươi hãy phó thác cho Đấng Hằng Sống không bao giờ chết và Ngươi hãy tán dương ca tụng Ngài; và chỉ riêng Ngài đủ biết rõ tội lỗi của đám bầy tôi của Ngài.');
INSERT INTO "vn_ruwwad" VALUES(25,59,'25:59','Ngài là Đấng đã tạo các tầng trời, trái đất và vạn vật giữa trời đất trong sáu ngày rồi an vị trên chiếc Ngai Vương; Ngươi hãy hướng về Đấng Độ Lượng mà hỏi, Ngài thông toàn tất cả.');
INSERT INTO "vn_ruwwad" VALUES(25,60,'25:60','Khi có lời bảo họ: “Các người hãy phủ phục Đấng Độ Lượng”, họ đáp: “Nhưng Đấng Độ Lượng là ai? Bọn ta sẽ phải quỳ lạy Đấng mà Ngươi truyền lệnh cho bọn ta hay sao?” Và điều đó càng làm cho họ thêm xa lánh (đức tin nơi Allah).');
INSERT INTO "vn_ruwwad" VALUES(25,61,'25:61','Hồng phúc thay Đấng đã tạo ra các chùm sao trên trời và tạo ra trong đó mặt trời chiếu sáng và mặt trăng mang ánh hào quang.');
INSERT INTO "vn_ruwwad" VALUES(25,62,'25:62','Ngài là Đấng đã làm ra ban đêm và ban ngày luân chuyển tiếp nối nhau để cho ai muốn tụng niệm hoặc muốn tạ ơn (ân huệ của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(25,63,'25:63','Những người bề tôi của Đấng Độ Lượng là những ai đi lại trên mặt đất với điệu bộ khiêm tốn và khi bị người thiếu hiểu biết gây sự thì họ chỉ nói: “Xin cho được bằng an.”');
INSERT INTO "vn_ruwwad" VALUES(25,64,'25:64','Họ là những người thức đêm quỳ lạy và đứng cầu nguyện Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(25,65,'25:65','Họ là những người thường cầu nguyện: “Lạy Thượng Đế của bầy tôi, xin Ngài đưa bầy tôi ra xa khỏi hình phạt của Hỏa Ngục. Quả thật, sự trừng phạt nơi Hỏa Ngục là một sự trừng phạt dai dẳng.”');
INSERT INTO "vn_ruwwad" VALUES(25,66,'25:66','Quả thật Hỏa Ngục là một nơi cư ngụ và là một nơi nghỉ vô cùng tồi tệ.');
INSERT INTO "vn_ruwwad" VALUES(25,67,'25:67','Họ (những bề tôi của Allah) là những người khi tiêu dùng (tài sản của họ cho con đường chính nghĩa của Allah) thì không phung phí cũng không keo kiệt mà giữ mức trung bình giữa hai thái cực đó.');
INSERT INTO "vn_ruwwad" VALUES(25,68,'25:68','Họ là những người không cầu nguyện bất cứ thần linh nào khác cùng với Allah cũng không giết một sinh mạng vô tội trừ phi với lý do chính đáng; họ không làm điều Zina; và ai vi phạm những điều đó đáng bị tội.');
INSERT INTO "vn_ruwwad" VALUES(25,69,'25:69','Vào Ngày Tận Thế, sự trừng phạt được tăng gấp đôi và y sẽ phải mãi mãi chịu sự trừng phạt đó một cách nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(25,70,'25:70','Ngoại trừ những ai biết ăn năn sám hối, có đức tin và hành thiện thì họ sẽ được Allah thay thế tội lỗi của họ bằng điều phúc đức bởi Allah hằng tha thứ, Đấng Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(25,71,'25:71','Những ai ăn năn sám hối và siêng năng hành đạo và làm điều thiện tốt thì sự hối cải của y với Allah mới là thật lòng.');
INSERT INTO "vn_ruwwad" VALUES(25,72,'25:72','Họ (những bề tôi của Allah) là những người không làm chứng cho điều giả dối, và khi đi ngang qua những chuyện tầm phào thì họ bỏ đi một cách lịch sự.');
INSERT INTO "vn_ruwwad" VALUES(25,73,'25:73','Họ là những người khi được nhắc về những Lời Mặc Khải của Thượng Đế của họ thì họ không gục đầu tỏ vẻ tai điếc mắt mù.');
INSERT INTO "vn_ruwwad" VALUES(25,74,'25:74','Họ là những người thường cầu nguyện: “Lạy Thượng Đế của bầy tôi, xin Ngài làm cho vợ và con cái của bầy tôi thành một nguồn vui cho cặp mắt của bầy tôi, xin Ngài hãy để bầy tôi thành người lãnh đạo mẫu mực cho những người ngay chính.”');
INSERT INTO "vn_ruwwad" VALUES(25,75,'25:75','Những người đó sẽ được ban thưởng một nơi ở cao sang (Thiên Đàng) vì đã từng kiên nhẫn chịu đựng. Nơi đó, họ sẽ được đón chào tốt đẹp với lời chúc bằng an.');
INSERT INTO "vn_ruwwad" VALUES(25,76,'25:76','Họ sẽ ở trong đó mãi mãi, một nơi ngụ và một nơi nghỉ tốt đẹp nhất.');
INSERT INTO "vn_ruwwad" VALUES(25,77,'25:77','Ngươi (Thiên Sứ Muhammad) hãy nói (với những kẻ vô đức tin): “Nếu không vì lời cầu nguyện của các người thì Thượng Đế của Ta đã không quan tâm đến các người. Quả thật, các người đã phủ nhận (Ngài) nên rồi đây các người sẽ phải đối mặt (với sự trừng phạt của Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(26,1,'26:1','Ta. Sin. Mim.');
INSERT INTO "vn_ruwwad" VALUES(26,2,'26:2','Đó là những Câu Kinh của một Kinh Sách minh bạch (không mơ hồ và thần bí).');
INSERT INTO "vn_ruwwad" VALUES(26,3,'26:3','Có lẽ Ngươi (Thiên Sứ Muhammad) khổ tâm, đau buồn khiến bản thân chết dần chết mòn về việc họ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(26,4,'26:4','Nếu muốn, TA (Allah) có thể ban một phép lạ từ trên trời xuống khiến họ cúi cổ thần phục, (nhưng TA không làm thế vì để thử thách họ xem họ có tin vào điều vô hình hay không).');
INSERT INTO "vn_ruwwad" VALUES(26,5,'26:5','Bất cứ điều nhắc nhở nào được ban xuống cho họ từ Đấng Độ Lượng, họ đều quay lưng bỏ đi.');
INSERT INTO "vn_ruwwad" VALUES(26,6,'26:6','Quả thật, họ đã phủ nhận (những điều mà Thiên Sứ của họ đã mang đến), rồi đây họ sẽ nhận được tin về những điều mà họ đã từng chế giễu.');
INSERT INTO "vn_ruwwad" VALUES(26,7,'26:7','Lẽ nào họ đã không quan sát trái đất, nơi mà TA đã làm mọc ra biết bao cây cối tốt lành?');
INSERT INTO "vn_ruwwad" VALUES(26,8,'26:8','Quả thật, trong đó là một bằng chứng (cho việc TA thừa khả năng làm người chết sống lại), tuy nhiên, đa số bọn họ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(26,9,'26:9','Quả thật, Thượng Đế của Ngươi (hỡi Thiên Sứ Muhammad) là Đấng Quyền Lực, Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(26,10,'26:10','(Ngươi hãy nhớ lại) khi Thượng Đế của Ngươi gọi Musa phán bảo: “Ngươi hãy đi gặp một đám người làm điều sai quấy.”');
INSERT INTO "vn_ruwwad" VALUES(26,11,'26:11','“Đó là đám người của Pha-ra-ông. (Ngươi hãy bảo) họ sợ (Allah).”');
INSERT INTO "vn_ruwwad" VALUES(26,12,'26:12','(Musa) thưa: “Lạy Thượng Đế của bề tôi, bề tôi thực sự lo sợ rằng họ sẽ cho bề tôi nói dối.”');
INSERT INTO "vn_ruwwad" VALUES(26,13,'26:13','“Và lồng ngực của bề tôi thì chật hẹp, chiếc lưỡi của bề tôi thì nói không lưu loát, xin Ngài cử thêm Harun làm phò tá cho bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(26,14,'26:14','“Bề tôi đã phạm tội với họ (vì đã lỡ tay giết chết một người của họ) nên bề tôi sợ họ sẽ giết bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(26,15,'26:15','(Allah) phán bảo (Musa và Harun): “Sẽ không sao, hai ngươi cứ lên đường với những Phép Lạ của TA. Quả thật TA sẽ ở cùng với hai ngươi, TA luôn nghe thấy tất cả.”');
INSERT INTO "vn_ruwwad" VALUES(26,16,'26:16','“Hai Ngươi cứ đi gặp Pha-ra-ông, hai ngươi hãy nói với y: Chúng tôi là Thiên Sứ của Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "vn_ruwwad" VALUES(26,17,'26:17','“Xin ngài hãy để con cháu Israel ra đi với chúng tôi.”');
INSERT INTO "vn_ruwwad" VALUES(26,18,'26:18','(Pha-ra-ông) bảo (Musa): “Há ta đã không thương yêu và nuôi nấng Ngươi như con cái hay sao? Chẳng phải Ngươi đã ở với ta nhiều năm trong đời Ngươi hay sao?”');
INSERT INTO "vn_ruwwad" VALUES(26,19,'26:19','“Vậy mà Ngươi đã nhẫn tâm làm một việc đáng lẽ ra Ngươi không nên làm, Ngươi là một tên vong ơn bội nghĩa.”');
INSERT INTO "vn_ruwwad" VALUES(26,20,'26:20','(Musa) nói (với Pha-ra-ông): “Tôi đã làm điều đó lúc tôi là kẻ không biết gì (vì chưa nhận được sự mặc khải của Allah).”');
INSERT INTO "vn_ruwwad" VALUES(26,21,'26:21','“Vì sợ ngài nên Tôi đã bỏ trốn đi xa và Thượng Đế của Tôi đã ban cho Tôi sự khôn ngoan và cử Tôi làm Thiên Sứ (của Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(26,22,'26:22','“Đấy là ân xưa mà ngài đã nại ra để khiển trách Tôi rồi đã bắt người dân Israel làm nô lệ.”');
INSERT INTO "vn_ruwwad" VALUES(26,23,'26:23','Pha-ra-ông hỏi (Musa): “Thế Thượng Đế của vũ trụ và muôn loài là gì?”');
INSERT INTO "vn_ruwwad" VALUES(26,24,'26:24','(Musa) đáp: “Là Thượng Đế của các tầng trời, trái đất và vạn vật giữa trời đất, nếu quí ngài muốn có đức tin vững chắc (nơi Ngài thì quí ngài hãy chỉ thờ phượng một mình Ngài thôi).”');
INSERT INTO "vn_ruwwad" VALUES(26,25,'26:25','(Pha-ra-ông) nói với các cận thần: “Các khanh có nghe rõ (lời bịa đặt của Musa) chăng?”');
INSERT INTO "vn_ruwwad" VALUES(26,26,'26:26','(Musa) nói tiếp: “Đó là Thượng Đế của quí ngài và cả tổ tiên của quí ngài nữa.”');
INSERT INTO "vn_ruwwad" VALUES(26,27,'26:27','(Pha-ra-ông) bảo: “Chắc chắn Thiên Sứ được gởi đến cho các ngươi là một tên điên!”');
INSERT INTO "vn_ruwwad" VALUES(26,28,'26:28','(Musa) nói tiếp: “Đó là Thượng Đế của hướng đông, hướng tây và vạn vật giữa hai hướng đó, nếu quí ngài nhận thức được.”');
INSERT INTO "vn_ruwwad" VALUES(26,29,'26:29','(Pha-ra-ông) nói với (Musa): “Nếu nhà Ngươi quyết tôn thờ một Thượng Đế khác ngoài ta thì chắc chắn ta sẽ cho nhà Ngươi bị tù đày.”');
INSERT INTO "vn_ruwwad" VALUES(26,30,'26:30','(Musa) nói với (Pha-ra-ông): “Ngay cả khi tôi mang đến cho ngài một điều gì đó chứng minh rõ ràng ư?”');
INSERT INTO "vn_ruwwad" VALUES(26,31,'26:31','(Pha-ra-ông) nói: “Vậy nhà Ngươi hãy trình nó ra xem nếu nhà Ngươi đúng là một người nói thật.”');
INSERT INTO "vn_ruwwad" VALUES(26,32,'26:32','Thề là Musa ném chiếc gậy của Y xuống, nó lập tức biến thành một con rắn hiển hiện.');
INSERT INTO "vn_ruwwad" VALUES(26,33,'26:33','Và khi Y rút bàn tay của Y ra (sau khi áp nó vào ba sườn), bàn tay của Y trở nên trắng sáng trước những người chứng kiến.');
INSERT INTO "vn_ruwwad" VALUES(26,34,'26:34','(Pha-ra-ông) quay sang bảo các cận thần: “Tên này quả nhiên là một phù thủy tài giỏi.”');
INSERT INTO "vn_ruwwad" VALUES(26,35,'26:35','“Hắn định dùng pháp thuật để đánh đuổi các khanh ra khỏi lãnh thổ của các khanh. Vậy các khanh có ý xử trí hắn ra sao?”');
INSERT INTO "vn_ruwwad" VALUES(26,36,'26:36','(Các cận thần của Pha-ra-ông) thưa: “Hãy tạm tha cho hắn và người anh em của hắn, chúng ta sẽ tập hợp các pháp sư trong các thành phố lại.”');
INSERT INTO "vn_ruwwad" VALUES(26,37,'26:37','“Tất cả những nhà phù thủy tài giỏi sẽ đến trình diện bệ hạ.”');
INSERT INTO "vn_ruwwad" VALUES(26,38,'26:38','Vậy là các phù thủy được triệu tập tại một nơi hẹn vào một ngày đã được công bố rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(26,39,'26:39','Và mọi người bảo nhau: “Các người không đi dự đại hội (để xem ai thắng trong cuộc so tài giữa Musa và các phù thủy) sao?”');
INSERT INTO "vn_ruwwad" VALUES(26,40,'26:40','“Có lẽ chúng ta sẽ theo các nhà phù thủy nếu họ thắng cuộc.”');
INSERT INTO "vn_ruwwad" VALUES(26,41,'26:41','Khi đến nơi, những nhà phù thủy thưa với Pha-ra-ông: “Nếu chúng tôi thắng thì chúng tôi có được ban thưởng không?”');
INSERT INTO "vn_ruwwad" VALUES(26,42,'26:42','(Pha-ra-ông) bảo: “Chắc chắn có, nhất định lúc đó các ngươi sẽ trở thành các cận thần của ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,43,'26:43','Musa bảo (các phù thủy): “Quí ngài hãy ném bảo bối của quí ngài xuống.”');
INSERT INTO "vn_ruwwad" VALUES(26,44,'26:44','Thế là, họ ném những sợi dây và chiếc gậy của họ xuống, nói: “Với quyền lực của Pha-ra-ông, chúng tôi chắc chắn sẽ chiến thắng.”');
INSERT INTO "vn_ruwwad" VALUES(26,45,'26:45','Nhưng khi Musa ném chiếc gậy của Y xuống, nó nuốt mất những món vật mà họ đã làm giả.');
INSERT INTO "vn_ruwwad" VALUES(26,46,'26:46','Lúc đó, những tên phù thủy liền cúi xuống quỳ lạy.');
INSERT INTO "vn_ruwwad" VALUES(26,47,'26:47','Họ nói: “Chúng tôi đã có đức tin nơi Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "vn_ruwwad" VALUES(26,48,'26:48','“Thượng Đế của Musa và Harun.”');
INSERT INTO "vn_ruwwad" VALUES(26,49,'26:49','(Thấy thế), Pha-ra-ông quát (các nhà phù thủy): “Các ngươi dám tin hắn trước khi ta cho phép các ngươi ư? Hắn quả nhiên là tên cầm đầu của các ngươi; hắn đã dạy các ngươi pháp thuật; rồi các ngươi sẽ sớm biết hậu quả. Chắc chắn, ta sẽ cho chặt tay này và chân kia của các ngươi và sẽ cho đóng đinh toàn bộ các ngươi trên thập tự giá.”');
INSERT INTO "vn_ruwwad" VALUES(26,50,'26:50','(Các nhà phù thủy) trả lời (Pha-ra-ông): “Chẳng có gì thiệt hại cả, bởi vì rồi đây chúng tôi sẽ quay về gặp Thượng Đế của chúng tôi mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(26,51,'26:51','“Chúng tôi kỳ vọng Thượng Đế của chúng tôi sẽ tha thứ cho chúng tôi những lỗi lầm của chúng tôi bởi vì chúng tôi là những người có đức tin đầu tiên.”');
INSERT INTO "vn_ruwwad" VALUES(26,52,'26:52','TA đã mặc khải cho Musa, phán: “Ngươi hãy dẫn hết đám bề tôi của TA rời đi ban đêm, quả thật các ngươi sẽ bị truy sát.”');
INSERT INTO "vn_ruwwad" VALUES(26,53,'26:53','Rồi Pha-ra-ông cho quân lính loan tin đi khắp các thành phố.');
INSERT INTO "vn_ruwwad" VALUES(26,54,'26:54','“Quả thật, đám dân (Israel) này chỉ là một nhóm nhỏ bé.”');
INSERT INTO "vn_ruwwad" VALUES(26,55,'26:55','“Chúng thực sự đã phạm tội khi quân vì đã chọc giận ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,56,'26:56','“Nhưng ta đã luôn cảnh giác đề phòng chúng.”');
INSERT INTO "vn_ruwwad" VALUES(26,57,'26:57','Bởi thế, TA (Allah) đã tống bọn chúng (Pha-ra-ông và quân lính của hắn) ra khỏi hoa viên và suối nước.');
INSERT INTO "vn_ruwwad" VALUES(26,58,'26:58','Khỏi những kho tàng châu báu và địa vị cao sang.');
INSERT INTO "vn_ruwwad" VALUES(26,59,'26:59','Giống như thế, TA đã cho dân Israel thừa kế (những thứ tốt đẹp đó).');
INSERT INTO "vn_ruwwad" VALUES(26,60,'26:60','Rồi (Pha-ra-ông và quân lính của hắn) truy đuổi họ (dân Israel) vào lúc hừng đông.');
INSERT INTO "vn_ruwwad" VALUES(26,61,'26:61','Khi hai đoàn người đối diện nhau, các tông đồ của Musa nói: “Chắc chắn chúng ta sẽ bị đuổi kịp.”');
INSERT INTO "vn_ruwwad" VALUES(26,62,'26:62','(Musa) bảo: “Không đâu, Thượng Đế của Ta luôn sát cánh bên Ta, Ngài sẽ hướng dẫn Ta thôi.”');
INSERT INTO "vn_ruwwad" VALUES(26,63,'26:63','Ngay lúc đó, TA mặc khải cho Musa, phán: “Ngươi hãy đánh cây gậy của Ngươi xuống mặt biển.” (Y làm theo), tức khắc biển tách ra làm đôi thành những tảng nước hai bên như hai ngọn núi khổng lồ.');
INSERT INTO "vn_ruwwad" VALUES(26,64,'26:64','TA đẩy đám người kia (Pha-ra-ông và quân lính của hắn) đến gần nơi đó.');
INSERT INTO "vn_ruwwad" VALUES(26,65,'26:65','Và TA đã cứu nguy Musa và toàn bộ những ai theo Y.');
INSERT INTO "vn_ruwwad" VALUES(26,66,'26:66','Rồi TA nhấn chìm đám người kia.');
INSERT INTO "vn_ruwwad" VALUES(26,67,'26:67','Quả thật, trong sự việc đó là một bằng chứng (cho sự trung thực của Musa) nhưng đa số bọn họ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(26,68,'26:68','Và Thượng Đế của Ngươi đích thực là Đấng Quyền Lực, Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(26,69,'26:69','Ngươi (hỡi Thiên Sứ Muhammad) hãy đọc cho họ nghe về câu chuyện của Ibrahim.');
INSERT INTO "vn_ruwwad" VALUES(26,70,'26:70','Khi (Ibrahim) thưa với phụ thân và người dân của mình: “Các người đang thờ phượng gì thế kia?”');
INSERT INTO "vn_ruwwad" VALUES(26,71,'26:71','Họ đáp: “Bọn ta đang thờ các bục tượng, bọn ta rất sùng kính các thần linh này.”');
INSERT INTO "vn_ruwwad" VALUES(26,72,'26:72','(Ibrahim) hỏi: “Các bục tượng đó có nghe được các người khi các người cầu xin chúng không?”');
INSERT INTO "vn_ruwwad" VALUES(26,73,'26:73','“Hoặc chúng có giúp ích hay hãm hại được các người không?”');
INSERT INTO "vn_ruwwad" VALUES(26,74,'26:74','Họ đáp: “Không, nhưng bọn ta thấy cha mẹ của bọn ta đã làm như vậy.”');
INSERT INTO "vn_ruwwad" VALUES(26,75,'26:75','(Ibrahim) bảo: “Những thứ mà các người đang tôn thờ”');
INSERT INTO "vn_ruwwad" VALUES(26,76,'26:76','“Và cả những thứ mà tổ tiên xa xưa của các người đã tôn thờ”');
INSERT INTO "vn_ruwwad" VALUES(26,77,'26:77','“Đều là kẻ thù của Ta ngoại trừ Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "vn_ruwwad" VALUES(26,78,'26:78','“Ngài là Đấng đã tạo ra Ta rồi hướng dẫn Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,79,'26:79','“Ngài là Đấng đã cho Ta ăn và cho Ta uống.”');
INSERT INTO "vn_ruwwad" VALUES(26,80,'26:80','“Khi Ta bệnh thì Ngài là Đấng cho Ta khỏi bệnh.”');
INSERT INTO "vn_ruwwad" VALUES(26,81,'26:81','“Ngài là Đấng làm cho Ta chết rồi làm cho Ta sống lại.”');
INSERT INTO "vn_ruwwad" VALUES(26,82,'26:82','“Ngài là Đấng mà Ta hy vọng sẽ được tha thứ về những tội lỗi của Ta vào Ngày Phán Xét.”');
INSERT INTO "vn_ruwwad" VALUES(26,83,'26:83','“Lạy Thượng Đế của bề tôi, xin Ngài ban cho bề tôi sự khôn ngoan và xin Ngài nhập bề tôi cùng với những người đức hạnh.”');
INSERT INTO "vn_ruwwad" VALUES(26,84,'26:84','“Xin Ngài vinh danh bề tôi trên chiếc lưỡi của những thế hệ mai sau.”');
INSERT INTO "vn_ruwwad" VALUES(26,85,'26:85','“Xin Ngài làm cho bề tôi thành một người thừa kế Thiên Đàng Hạnh phúc.”');
INSERT INTO "vn_ruwwad" VALUES(26,86,'26:86','“Xin Ngài tha thứ cho phụ thân của bề tôi bởi ông ấy là một người lầm lạc.”');
INSERT INTO "vn_ruwwad" VALUES(26,87,'26:87','“Xin Ngài đừng hạ nhục bề tôi vào Ngày nhân loại được phục sinh.”');
INSERT INTO "vn_ruwwad" VALUES(26,88,'26:88','“Đó là Ngày mà của cải cũng như con cái sẽ không giúp ích được gì.”');
INSERT INTO "vn_ruwwad" VALUES(26,89,'26:89','“Ngoại trừ những ai đến với Allah bằng một tấm lòng trong sạch.”');
INSERT INTO "vn_ruwwad" VALUES(26,90,'26:90','Thiên Đàng sẽ được mang đến gần những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(26,91,'26:91','Hỏa Ngục sẽ được mang đến trước mặt những kẻ lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(26,92,'26:92','Có lời bảo họ: “Đâu là những thứ mà các ngươi đã tôn thờ”');
INSERT INTO "vn_ruwwad" VALUES(26,93,'26:93','“Thay vì Allah? Liệu chúng có khả năng giải cứu các ngươi hoặc tự cứu lấy bản thân chúng được không?”');
INSERT INTO "vn_ruwwad" VALUES(26,94,'26:94','Bởi thế, chúng cùng với đám quỷ quái sẽ bị lùa vào Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(26,95,'26:95','Và toàn bộ binh tướng của Iblis cũng thế.');
INSERT INTO "vn_ruwwad" VALUES(26,96,'26:96','Và trong lúc cãi vã nhau trong (Hỏa Ngục), họ sẽ nói:');
INSERT INTO "vn_ruwwad" VALUES(26,97,'26:97','“Thề với Allah, chúng tôi rõ ràng là nhóm người lầm lạc.”');
INSERT INTO "vn_ruwwad" VALUES(26,98,'26:98','“Khi mà chúng tôi đã suy tôn các ngươi ngang vai với Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "vn_ruwwad" VALUES(26,99,'26:99','“Và chỉ những tên tội lỗi mới dắt chúng tôi đi lạc.”');
INSERT INTO "vn_ruwwad" VALUES(26,100,'26:100','“Thế là chúng tôi chẳng có ai can thiệp.”');
INSERT INTO "vn_ruwwad" VALUES(26,101,'26:101','“Chúng tôi cũng chẳng có một người bạn chân tình nào cả.”');
INSERT INTO "vn_ruwwad" VALUES(26,102,'26:102','“Nếu có cơ hội trở lại (trần thế) lần nữa thì chúng tôi sẽ là những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(26,103,'26:103','Quả thật, trong sự việc đó là một dấu hiệu, nhưng đa số bọn họ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(26,104,'26:104','Quả thật, Thượng Đế của Ngươi là Đấng Quyền Lực, Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(26,105,'26:105','Người dân của Nuh đã chối bỏ tất cả Thiên Sứ (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(26,106,'26:106','Khi Nuh, người anh em của họ bảo họ: “Các người không sợ (Allah) sao?”');
INSERT INTO "vn_ruwwad" VALUES(26,107,'26:107','“Ta đúng thực là Thiên Sứ đáng tin được (Allah) phái đến cho các người.”');
INSERT INTO "vn_ruwwad" VALUES(26,108,'26:108','“Thế nên, các người hãy kính sợ Allah và vâng lời Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,109,'26:109','“Ta không đòi các người trả thù lao cho việc đó mà chính Thượng Đế của vũ trụ và muôn loài sẽ ân thưởng cho Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,110,'26:110','“Thế nên, các người hãy kính sợ Allah và vâng lời Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,111,'26:111','Họ (đáp lại lời kêu gọi của Nuh), nói : “Lẽ nào bọn ta lại phải tin ngươi trong khi những kẻ đi theo ngươi toàn là những tên bần cùng?!”');
INSERT INTO "vn_ruwwad" VALUES(26,112,'26:112','(Nuh) bảo: “Ta không biết điều họ đã từng làm.”(');
INSERT INTO "vn_ruwwad" VALUES(26,113,'26:113','“Việc phán xét họ là ở thẩm quyền nơi Thượng Đế của Ta, nếu các người cảm nhận được.”');
INSERT INTO "vn_ruwwad" VALUES(26,114,'26:114','“Ta không có quyền xua đuổi những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(26,115,'26:115','“Quả thật Ta chỉ là một người cảnh báo công khai mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(26,116,'26:116','Họ (dọa Nuh), bảo: “Này Nuh, nếu ngươi không chịu dừng lại, bọn ta sẽ ném đá giết chết ngươi.”');
INSERT INTO "vn_ruwwad" VALUES(26,117,'26:117','Nuh cầu nguyện: “Lạy Thượng Đế của bề tôi, đám dân của bề tôi đã phủ nhận bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(26,118,'26:118','“Xin Ngài mở lối giữa bề tôi và họ, xin Ngài giải cứu bề tôi và những người có đức tin đã đi theo bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(26,119,'26:119','Thế là TA đã giải cứu (Nuh) và những ai đi theo Y trên một chiếc tàu đầy ắp.');
INSERT INTO "vn_ruwwad" VALUES(26,120,'26:120','Rồi TA nhấn chìm toàn bộ đám người còn lại (trong biển nước của trận đại hồng thủy).');
INSERT INTO "vn_ruwwad" VALUES(26,121,'26:121','Quả thật, trong sự việc đó là một dấu hiệu (làm bài học cho những ai phủ nhận Thiên Sứ của Allah), tuy nhiên, đa số bọn họ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(26,122,'26:122','Quả thật, Thượng Đế của Ngươi là Đấng Quyền Lực, Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(26,123,'26:123','Người dân ‘Ad đã chối bỏ tất cả Thiên Sứ (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(26,124,'26:124','Khi Hud, người anh em của họ bảo họ: “Các người không sợ (Allah) sao?”');
INSERT INTO "vn_ruwwad" VALUES(26,125,'26:125','“Ta đúng thực là Thiên Sứ đáng tin được (Allah) phái đến cho các người.”');
INSERT INTO "vn_ruwwad" VALUES(26,126,'26:126','“Thế nên, các người hãy kính sợ Allah và vâng lời Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,127,'26:127','“Ta không đòi các người trả thù lao cho việc đó mà chính Thượng Đế của vũ trụ và muôn loài sẽ ân thưởng cho Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,128,'26:128','“Phải chăng các người đã xây cất trên cao (những ngôi nhà kiêng cố) để khoe khoang ư?”');
INSERT INTO "vn_ruwwad" VALUES(26,129,'26:129','“Lẽ nào các người xây cất những lâu đài kiên cố để mong ở trong đó vĩnh viễn?”');
INSERT INTO "vn_ruwwad" VALUES(26,130,'26:130','“Và khi các người đàn áp, các người đàn áp như những bạo chúa.”');
INSERT INTO "vn_ruwwad" VALUES(26,131,'26:131','“Các người hãy kính sợ Allah và vâng lời Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,132,'26:132','“Các người hãy sợ Đấng đã trợ giúp các người biết bao thiên ân mà các người biết.”');
INSERT INTO "vn_ruwwad" VALUES(26,133,'26:133','“Ngài đã ban cho các người gia súc và con cái.”');
INSERT INTO "vn_ruwwad" VALUES(26,134,'26:134','“(Ngài đã ban cho các người) vườn tược và suối nước.”');
INSERT INTO "vn_ruwwad" VALUES(26,135,'26:135','“Ta thật sự lo sợ cho các người về sự trừng phạt của một Ngày Vĩ Đại.”');
INSERT INTO "vn_ruwwad" VALUES(26,136,'26:136','(Đám dân của Hud đáp lại lời kêu gọi của Y), nói: “Dù ngươi có cảnh báo hay không cảnh báo, điều đó cũng bằng thừa đối với bọn ta mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(26,137,'26:137','“Đây đúng là phong cách và lối suy nghĩ của người xưa.”');
INSERT INTO "vn_ruwwad" VALUES(26,138,'26:138','“Và bọn ta chắc chắn sẽ không bị trừng phạt.”');
INSERT INTO "vn_ruwwad" VALUES(26,139,'26:139','Thế là họ chối bỏ Y nên đã bị tiêu diệt. Quả thật, trong sự việc đó là một dấu hiệu, nhưng đa số bọn họ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(26,140,'26:140','Quả thật Thượng Đế của Ngươi là Đấng Quyền Lực, Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(26,141,'26:141','Người dân Thamud đã chối bỏ tất cả Thiên Sứ (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(26,142,'26:142','Khi Saleh, người anh em của họ bảo họ: “Các người không sợ (Allah) sao?”');
INSERT INTO "vn_ruwwad" VALUES(26,143,'26:143','“Ta đúng thực là Thiên Sứ đáng tin được (Allah) phái đến cho các người.”');
INSERT INTO "vn_ruwwad" VALUES(26,144,'26:144','“Thế nên, các người hãy kính sợ Allah và vâng lời Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,145,'26:145','“Ta không đòi các người trả thù lao cho việc đó mà chính Thượng Đế của vũ trụ và muôn loài sẽ ân thưởng cho Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,146,'26:146','“Lẽ nào các người sẽ được an toàn hưởng thụ những tiện nghi hiện có nơi đây?”');
INSERT INTO "vn_ruwwad" VALUES(26,147,'26:147','“Trong những ngôi vườn và suối nước?”');
INSERT INTO "vn_ruwwad" VALUES(26,148,'26:148','“Với những nương rẫy và vườn chà là kết trái chín mộng?”');
INSERT INTO "vn_ruwwad" VALUES(26,149,'26:149','“Và các người giỏi đục núi xây nhà kiêng cố.”');
INSERT INTO "vn_ruwwad" VALUES(26,150,'26:150','“Các người hãy kính sợ Allah và vâng lời Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,151,'26:151','“Các người chớ nghe theo mệnh lệnh của những kẻ thái quá.”');
INSERT INTO "vn_ruwwad" VALUES(26,152,'26:152','“Họ là những kẻ làm điều xấu xa và tội lỗi khắp nơi nhưng không chịu sửa mình.”');
INSERT INTO "vn_ruwwad" VALUES(26,153,'26:153','(Đám dân của Saleh đáp lại lời kêu gọi của Y), nói: “Ngươi đúng là đã bị trúng tà.”');
INSERT INTO "vn_ruwwad" VALUES(26,154,'26:154','“Ngươi chẳng qua cũng chỉ là một người phàm như bọn ta. Nhưng nếu ngươi nói thật thì ngươi hãy mang ra cho bọn ta thứ gì đó chứng minh xem nào.”');
INSERT INTO "vn_ruwwad" VALUES(26,155,'26:155','(Saleh) bảo họ: “Đây là con lạc đà cái (mà Allah đã mang nó ra từ tảng đá), nó sẽ uống phần nước của nó và các người sẽ uống phần nước của các người vào ngày được ấn định.”');
INSERT INTO "vn_ruwwad" VALUES(26,156,'26:156','“Các người không được làm hại đến nó, nếu không các người sẽ bị trừng phạt vào một Ngày Vĩ Đại.”');
INSERT INTO "vn_ruwwad" VALUES(26,157,'26:157','Nhưng rồi họ đã đâm cổ nó và tỏ ra hối hận sau đó.');
INSERT INTO "vn_ruwwad" VALUES(26,158,'26:158','Thế là họ đã bị hình phạt túm lấy. Quả thật, trong sự việc đó là một dấu hiệu (bài học cho những ai phủ nhận và không vâng lời các vị Thiên Sứ của Allah); tuy nhiên, đa số bọn họ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(26,159,'26:159','Quả thật, Thượng Đế của Ngươi là Đấng Quyền Lực, Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(26,160,'26:160','Người dân của Lut đã chối bỏ tất cả Thiên Sứ (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(26,161,'26:161','Khi Lut, người anh em của họ bảo họ: “Các người không sợ (Allah) sao?”');
INSERT INTO "vn_ruwwad" VALUES(26,162,'26:162','“Ta đúng thực là Thiên Sứ đáng tin được (Allah) phái đến cho các người.”');
INSERT INTO "vn_ruwwad" VALUES(26,163,'26:163','“Thế nên, các người hãy kính sợ Allah và vâng lời Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,164,'26:164','“Ta không đòi các người trả thù lao cho việc đó mà chính Thượng Đế của vũ trụ và muôn loài sẽ ân thưởng cho Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,165,'26:165','“Sao các người lại tìm đến đàn ông trong vũ trụ này để thỏa mãn tình dục,”');
INSERT INTO "vn_ruwwad" VALUES(26,166,'26:166','“Và bỏ rơi những người mà Thượng Đế của các người đã tạo ra cho các người để làm vợ? Không, các người thực sự là những kẻ thái quá.”');
INSERT INTO "vn_ruwwad" VALUES(26,167,'26:167','(Đám dân của Lut dọa Y), bảo: “Này Lut, nếu ngươi không chịu dừng lại, bọn ta sẽ trục xuất ngươi ra khỏi xứ.”');
INSERT INTO "vn_ruwwad" VALUES(26,168,'26:168','(Lut) nói với họ: “Ta là một người căm ghét việc làm của các người.”');
INSERT INTO "vn_ruwwad" VALUES(26,169,'26:169','(Lut cầu nguyện): “Lạy Thượng Đế của bề tôi, xin Ngài giải cứu bề tôi và gia đình của bề tôi thoát khỏi hành vi xấu của họ.”');
INSERT INTO "vn_ruwwad" VALUES(26,170,'26:170','Thế là TA (Allah) đã giải cứu Lut và toàn bộ gia đình của Y.');
INSERT INTO "vn_ruwwad" VALUES(26,171,'26:171','Trừ vợ của Y (là kẻ vô đức tin), nữ ta sẽ ở cùng đám người bị trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(26,172,'26:172','Sau đó TA đã tiêu diệt toàn bộ đám người còn lại.');
INSERT INTO "vn_ruwwad" VALUES(26,173,'26:173','TA đã trừng phạt họ bằng một trận mưa khủng khiếp; đúng là một trận mưa kinh hoàng cho đám người được cảnh báo trước.');
INSERT INTO "vn_ruwwad" VALUES(26,174,'26:174','Quả thật, trong sự việc đó là một dấu hiệu (bài học cho những ai phủ nhận và không vâng lời các vị Thiên Sứ của Allah); tuy nhiên, đa số bọn họ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(26,175,'26:175','Quả thật, Thượng Đế của Ngươi là Đấng Quyền Lực, Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(26,176,'26:176','Dân chúng Aikah (một khu rừng gần địa phận Madyan) đã chối bỏ tất cả Thiên Sứ (Allah).');
INSERT INTO "vn_ruwwad" VALUES(26,177,'26:177','Khi Shu’aib bảo họ: “Các người không sợ (Allah) sao?”');
INSERT INTO "vn_ruwwad" VALUES(26,178,'26:178','“Ta đúng thực là Thiên Sứ đáng tin được (Allah) phái đến cho các người.”');
INSERT INTO "vn_ruwwad" VALUES(26,179,'26:179','“Thế nên, các người hãy kính sợ Allah và vâng lời Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,180,'26:180','“Ta không đòi các người trả thù lao cho việc đó mà chính Thượng Đế của vũ trụ và muôn loài sẽ ân thưởng cho Ta.”');
INSERT INTO "vn_ruwwad" VALUES(26,181,'26:181','“Các người hãy đong cho đủ, chớ là những kẻ làm cho người khác mất mát (gian lận, đong không đủ khi bán).”');
INSERT INTO "vn_ruwwad" VALUES(26,182,'26:182','“Và khi cân các người hãy để cho bàn cân được ngay thẳng.”');
INSERT INTO "vn_ruwwad" VALUES(26,183,'26:183','“Các người chớ gian lận thiên hạ bằng cách cân thiếu và cũng chớ đi khắp trái đất để gieo rắc tội lỗi.”');
INSERT INTO "vn_ruwwad" VALUES(26,184,'26:184','“Các người hãy sợ Allah, Đấng đã tạo hóa các người và những thế hệ trước.”');
INSERT INTO "vn_ruwwad" VALUES(26,185,'26:185','(Đám dân Aikah đáp lại lời kêu gọi của Shu’aib), nói : “Quả thật, ngươi đã bị trúng tà.”');
INSERT INTO "vn_ruwwad" VALUES(26,186,'26:186','“Ngươi chẳng qua cũng chỉ là một người phàm như bọn ta và bọn ta nghĩ ngươi là tên nói dối.”');
INSERT INTO "vn_ruwwad" VALUES(26,187,'26:187','“Nhưng nếu ngươi nói thật thì ngươi hãy khiến cho một phần của bầu trời rơi xuống đè bọn ta thử xem nào.”');
INSERT INTO "vn_ruwwad" VALUES(26,188,'26:188','(Shu’aib) nói với họ: “Thượng Đế của Ta, Ngài biết rõ điều mà các người làm.”');
INSERT INTO "vn_ruwwad" VALUES(26,189,'26:189','Họ đã phủ nhận (Shu’aib). Cho nên, họ đã bị hình phạt túm lấy vào một ngày tối trời, đó là sự trừng phạt của một Ngày Vĩ Đại.');
INSERT INTO "vn_ruwwad" VALUES(26,190,'26:190','Quả thật, trong sự việc đó là một dấu hiệu (bài học cho những ai phủ nhận và không vâng lời các vị Thiên Sứ của Allah); tuy nhiên, đa số bọn họ không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(26,191,'26:191','Quả thật, Thượng Đế của Ngươi là Đấng Quyền Lực, Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(26,192,'26:192','Quả thật, Nó (Qur’an) đích thực được Thượng Đế của vũ trụ ban xuống.');
INSERT INTO "vn_ruwwad" VALUES(26,193,'26:193','Do Ruh (đại Thiên Thần Jibril) đáng tin mang Nó xuống.');
INSERT INTO "vn_ruwwad" VALUES(26,194,'26:194','Đặt vào con tim của Ngươi (Thiên Sứ Muhammad) để Ngươi trở thành một Người cảnh báo.');
INSERT INTO "vn_ruwwad" VALUES(26,195,'26:195','Bằng tiếng Ả-rập rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(26,196,'26:196','Nó quả thật đã được nhắc đến trong những Kinh Sách trước.');
INSERT INTO "vn_ruwwad" VALUES(26,197,'26:197','Lẽ nào không có một dấu hiệu nào cho (những kẻ phủ nhận) trong khi các học giả của dân Israel biết rõ Nó?');
INSERT INTO "vn_ruwwad" VALUES(26,198,'26:198','Nếu TA thực sự đã thiên khải Nó xuống cho một số người không phải dân Ả-rập.');
INSERT INTO "vn_ruwwad" VALUES(26,199,'26:199','Rồi y đọc lại cho họ nghe thì chắc rằng họ đã không tin vào Nó.');
INSERT INTO "vn_ruwwad" VALUES(26,200,'26:200','Bằng cách đó, TA đã làm nó (sự vô đức tin) thấm nhuần vào tim của những kẻ tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(26,201,'26:201','Họ sẽ không tin tưởng Nó cho đến khi họ thấy được sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(26,202,'26:202','Nó sẽ bất ngờ túm lấy họ trong lúc họ không hay biết.');
INSERT INTO "vn_ruwwad" VALUES(26,203,'26:203','Lúc đó họ sẽ nói: “Chúng tôi có được gia hạn thêm không?”');
INSERT INTO "vn_ruwwad" VALUES(26,204,'26:204','Vậy sao họ lại hối thúc hình phạt mau đến?');
INSERT INTO "vn_ruwwad" VALUES(26,205,'26:205','Há Ngươi (hỡi Thiên Sứ Muhammad) có nhận thấy rằng việc TA cho họ hưởng lạc thêm nhiều năm nữa,');
INSERT INTO "vn_ruwwad" VALUES(26,206,'26:206','Rồi sau đó, hình phạt đã được hứa mới đến chụp bắt họ,');
INSERT INTO "vn_ruwwad" VALUES(26,207,'26:207','Thì việc hưởng thụ đó chẳng giúp ích gì cho họ cả?');
INSERT INTO "vn_ruwwad" VALUES(26,208,'26:208','TA đã không tiêu diệt bất cứ ngôi làng nào ngoại trừ đã có những người đến cảnh báo nó.');
INSERT INTO "vn_ruwwad" VALUES(26,209,'26:209','Như một sự nhắc nhở (đến cư dân của nó) bởi lẽ TA không hề bất công.');
INSERT INTO "vn_ruwwad" VALUES(26,210,'26:210','(Qur’an) không phải do những tên Shaytan mang xuống.');
INSERT INTO "vn_ruwwad" VALUES(26,211,'26:211','Việc làm này không thích hợp cho chúng và chúng cũng không có khả năng (cho việc này).');
INSERT INTO "vn_ruwwad" VALUES(26,212,'26:212','Quả thật chúng đã bị đuổi ra xa không được cho nghe (Qur’an).');
INSERT INTO "vn_ruwwad" VALUES(26,213,'26:213','Do đó, Ngươi chớ cầu nguyện bất cứ thần linh nào khác cùng với Allah, nếu không Ngươi sẽ cùng nhóm với đám người bị trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(26,214,'26:214','Ngươi (hỡi Thiên Sứ Muhammad) hãy cảnh báo họ hàng thân tộc của Ngươi.');
INSERT INTO "vn_ruwwad" VALUES(26,215,'26:215','Ngươi hãy cư xử từ tốn và nhẹ nhàng với những người có đức tin theo Ngươi.');
INSERT INTO "vn_ruwwad" VALUES(26,216,'26:216','Nếu họ bất tuân Ngươi thì Ngươi hãy nói: “Ta vô can với những điều các người làm.”');
INSERT INTO "vn_ruwwad" VALUES(26,217,'26:217','Ngươi hãy phó thác cho Đấng Quyền Lực, Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(26,218,'26:218','Đấng mà Ngài nhìn thấy Ngươi khi Ngươi đứng lễ nguyện Salah.');
INSERT INTO "vn_ruwwad" VALUES(26,219,'26:219','(Ngài nhìn thấy Ngươi) cả lúc Ngươi đổi động tác cúi xuống quỳ lạy.');
INSERT INTO "vn_ruwwad" VALUES(26,220,'26:220','ởi Ngài đích thực là Đấng Hằng Nghe, Toàn Tri.');
INSERT INTO "vn_ruwwad" VALUES(26,221,'26:221','Các ngươi có muốn TA nói cho biết ai là kẻ mà lũ Shaytan thường xuống gặp không?');
INSERT INTO "vn_ruwwad" VALUES(26,222,'26:222','(Shaytan) thường hay đến gặp những kẻ nói dối và tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(26,223,'26:223','Chúng nói lại những thứ nghe được nhưng đa phần là bịa đặt.');
INSERT INTO "vn_ruwwad" VALUES(26,224,'26:224','Và chỉ những kẻ lầm lạc mới nghe theo những tên thi sĩ.');
INSERT INTO "vn_ruwwad" VALUES(26,225,'26:225','Lẽ nào Ngươi không thấy họ lang thang vơ vẩn trong mỗi thung lũng để ngâm thơ hay sao?');
INSERT INTO "vn_ruwwad" VALUES(26,226,'26:226','Quả thật, họ chỉ nói những điều họ không làm.');
INSERT INTO "vn_ruwwad" VALUES(26,227,'26:227','Ngoại trừ (những thi sĩ) có đức tin, làm việc thiện tốt, tụng niệm Allah nhiều và đã giành được thắng lợi sau khi đã bị đối xử bất công. Và những kẻ sai quấy sẽ sớm biết đâu là nơi mà mình sẽ phải quay về.');
INSERT INTO "vn_ruwwad" VALUES(27,1,'27:1','Ta. Sin. Đây là những câu Kinh Qur’an, một Kinh Sách rõ ràng (không mơ hồ và thần bí).');
INSERT INTO "vn_ruwwad" VALUES(27,2,'27:2','Một nguồn Chỉ Đạo và tin mừng cho những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(27,3,'27:3','Những ai chu đáo dâng lễ nguyện Salah, xuất Zakat và tin kiên định nơi cõi Đời Sau.');
INSERT INTO "vn_ruwwad" VALUES(27,4,'27:4','Còn đối với những ai không có đức tin nơi cõi Đời Sau, TA (Allah) đã khiến cho việc làm của họ tỏ ra đẹp đẽ trong mắt của họ để họ cứ vẩn vơ lang thang.');
INSERT INTO "vn_ruwwad" VALUES(27,5,'27:5','Những kẻ đó sẽ phải chịu một sự trừng phạt nhục nhã và họ sẽ là đám người thất bại thảm hại ở Đời Sau.');
INSERT INTO "vn_ruwwad" VALUES(27,6,'27:6','Quả thật Ngươi (Thiên Sứ Muhammad) đã tiếp thu Qur’an từ Đấng Anh Minh, Toàn Tri.');
INSERT INTO "vn_ruwwad" VALUES(27,7,'27:7','(Ngươi hãy nhớ lại) khi Musa bảo người nhà của Y: “Ta đã nhìn thấy một ngọn lửa ở đằng xa, Ta sẽ mang về cho các người tin tức từ chỗ ngọn lửa đó hoặc có thể Ta sẽ mang về cho các người một khúc củi cháy để các người sưởi ấm.”');
INSERT INTO "vn_ruwwad" VALUES(27,8,'27:8','Rồi khi (Musa) đến chỗ ngọn lửa thì có tiếng gọi, bảo: “Phúc cho ai đang ở gần lửa và cho ai đang ở xung quanh nó; và quang vinh và trong sạch thay Allah, Thượng Đế của toàn vũ trụ và muôn loài.”');
INSERT INTO "vn_ruwwad" VALUES(27,9,'27:9','“Hỡi Musa, TA chính là Allah, Đấng Quyền Lực, Đấng Sáng Suốt.”');
INSERT INTO "vn_ruwwad" VALUES(27,10,'27:10','“Ngươi hãy ném chiếc gậy của Ngươi xuống.” (Musa liền làm theo) nhưng khi thấy nó động đậy giống như một con rắn, Y liền bỏ chạy không dám quay lại nhìn. (TA – Allah phán bảo Y): “Hỡi Musa, Ngươi đừng hoảng sợ, bởi chưa từng có vị Thiên Sứ nào hoảng sợ trước mặt TA cả.”');
INSERT INTO "vn_ruwwad" VALUES(27,11,'27:11','“Ngoại trừ những ai đã bất công với bản thân, tuy nhiên, sau đó họ làm điều thiện để chuộc lỗi lầm thì quả thật TA là Đấng Hằng Tha Thứ, Hằng Khoan Dung.”');
INSERT INTO "vn_ruwwad" VALUES(27,12,'27:12','“Ngươi hãy cho bàn tay Ngươi vào lòng ngực mình, khi rút ra bàn tay Ngươi sẽ trở nên trắng toát nhưng không phải là bệnh. Đó là một trong số chín phép lạ(, Ngươi hãy mang đi trình bày cho Pha-ra-ông và đám thuộc hạ của hắn. Quả thật chúng là một đám người ương ngạnh, đại nghịch và bất tuân.”');
INSERT INTO "vn_ruwwad" VALUES(27,13,'27:13','Nhưng khi những phép lạ của TA được mang đến cho chúng để chúng tận mắt chứng kiến thì chúng bảo: “Đây rõ ràng là một trò ảo thuật!”');
INSERT INTO "vn_ruwwad" VALUES(27,14,'27:14','Chúng đã chống đối và ngạo mạn mặc dù trong thâm tâm chúng đã thừa nhận (các phép lạ đó là sự thật). Bởi vậy, Ngươi hãy nhìn xem những kẻ thối nát đã gặp phải hậu quả như thế nào.');
INSERT INTO "vn_ruwwad" VALUES(27,15,'27:15','Quả thật, TA đã ban cho Dawood (David) và Sulayman (Solomon) nguồn kiến thức, và cả hai nói: Alhamdulillah, Đấng đã ưu đãi bầy tôi nhiều hơn đám bề tôi có đức tin khác của Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(27,16,'27:16','Sulayman đã kế thừa Dawood, Y bảo: “Hỡi nhân loại, Ta đã được dạy cho kiến thức về tiếng nói của loài chim và đã được ban cho đủ mọi thứ và đây rõ ràng là một hồng phúc.”');
INSERT INTO "vn_ruwwad" VALUES(27,17,'27:17','Đoàn quân của Sulayman gồm Jinn, con người và chim chóc đã được triệu tập trước mặt Y theo đội ngũ chỉnh tề.');
INSERT INTO "vn_ruwwad" VALUES(27,18,'27:18','Khi đoàn quân đến một thung lũng của loài kiến (thuộc địa phận của Sham), một con kiến lên tiếng: “Hỡi loài kiến, các ngươi hãy mau lui vào chỗ ở của các ngươi kẻo Sulayman và đoàn quân của Người (vô tình) giẫm nát các ngươi trong lúc họ không hề hay biết.”');
INSERT INTO "vn_ruwwad" VALUES(27,19,'27:19','Lấy làm vui thích trước lời nói của kiến, Sulayman mỉm cười và khấn vái: “Lạy Thượng Đế của bề tôi, xin Ngài hãy khiến cho bề tôi biết tạ ơn Ngài về những ân huệ mà Ngài đã ban cho bề tôi, cho cha mẹ của bề tôi, hãy khiến bề tôi làm việc thiện hồng làm hài lòng Ngài và xin lấy đức khoan dung của Ngài mà thâu nhận bề tôi vào đám bầy tôi sùng đạo của Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(27,20,'27:20','Rồi khi (Sulayman) kiểm tra đoàn chim, Y bảo: “Sao Ta không thấy con Hudhud( đâu cả, phải chăng nó vắng mặt?”');
INSERT INTO "vn_ruwwad" VALUES(27,21,'27:21','“Ta chắc chắn sẽ phạt nó thật nặng hoặc Ta sẽ giết nó hoặc nó sẽ phải cho Ta biết lý do rõ ràng (về việc nó đã vắng mặt).”');
INSERT INTO "vn_ruwwad" VALUES(27,22,'27:22','Không dám chậm trễ, con chim liền vội vàng bay về thưa (với Sulayman): “Hạ thần đã phát hiện ra một điều mà bệ hạ chưa biết và hạ thần đã mang về cho bệ hạ một nguồn tin chắc chắn từ xứ Saba'' (Sheba).”');
INSERT INTO "vn_ruwwad" VALUES(27,23,'27:23','“Hạ thần đã phát hiện thấy có một nữ vương cai trị dân chúng, nữ vương đó đã được cung phụng đầy đủ mọi thứ và làm chủ một ngai vương vĩ đại.”');
INSERT INTO "vn_ruwwad" VALUES(27,24,'27:24','“Hạ thần thấy nữ vương đó cùng dân chúng của mình quỳ lạy mặt trời thay vì Allah. Shaytan đã quyến rũ họ khiến họ thích thú với việc làm của mình, hắn đã ngăn cản họ xa lánh con đường Chân Lý nên họ không được hướng dẫn.”');
INSERT INTO "vn_ruwwad" VALUES(27,25,'27:25','“Để họ không quỳ lạy Allah, Đấng trưng bày những điều bí mật trong các tầng trời và đất, Đấng biết rõ mọi điều quí ngài giấu kín cũng như những điều quí ngài phơi bày.”');
INSERT INTO "vn_ruwwad" VALUES(27,26,'27:26','“Allah là Đấng mà Không có Thượng Đế (đích thực) nào khác ngoài Ngài. Ngài là Đấng Chủ Nhân của chiếc Ngai Vương vĩ đại.”');
INSERT INTO "vn_ruwwad" VALUES(27,27,'27:27','(Sulayman) nói với (con Hudhud): “Ta sẽ xem nhà ngươi nói thật hay nhà ngươi là một tên nói dối.”');
INSERT INTO "vn_ruwwad" VALUES(27,28,'27:28','“Nhà ngươi hãy mang bức thư này của Ta ném cho họ rồi lánh sang một bên để lắng nghe họ phúc đáp điều gì (về bức thư của Ta).”');
INSERT INTO "vn_ruwwad" VALUES(27,29,'27:29','(Nữ Vương nhận được bức thư) bảo: “Hỡi các tướng lĩnh, trẫm vừa nhận được một bức thư khả kính.”');
INSERT INTO "vn_ruwwad" VALUES(27,30,'27:30','“Nó đến từ Sulayman, nó bắt đầu bằng câu: Bismillahir rohmanir rohim (Nhân Danh Allah, Đấng Độ Lượng, Đấng Khoan Dung)”.');
INSERT INTO "vn_ruwwad" VALUES(27,31,'27:31','“(Nội dung của nó:) Các ngươi chớ cao ngạo đối với Ta và hãy đến trình diện Ta như một người Muslim (thần phục Allah).”');
INSERT INTO "vn_ruwwad" VALUES(27,32,'27:32','(Nữ Vương) nói (với các chư thần): “Hỡi các chư khanh, các chư khanh hãy cố vấn cho trẫm trong sự việc này. Trẫm không hề tự quyết định lấy bất kỳ sự việc nào mà không có mặt của các khanh cả.”');
INSERT INTO "vn_ruwwad" VALUES(27,33,'27:33','(Các quân thần) tâu: “Chúng ta vốn hùng mạnh, dày dặn kinh nghiệm chinh chiến, tuy nhiên, quyền quyết định là ở nơi bệ hạ. Xin bệ hạ xem xét mà ra lệnh.”');
INSERT INTO "vn_ruwwad" VALUES(27,34,'27:34','(Nữ Vương) bảo: “(Theo trẫm thấy), các vua chúa khi tiến vào một thị trấn nào đó (với ý định xâm lăng) thì họ sẽ tàn phá nó và bắt những người quyền thế nhất của nó xuống làm thứ dân thấp hèn nhất. Họ luôn làm như vậy (khi đã chiếm được nó).”');
INSERT INTO "vn_ruwwad" VALUES(27,35,'27:35','“Trẫm quyết định sai sứ thần mang lễ vật đi triều cống họ và đợi xem các sứ thần sẽ mang điều phúc đáp gì trở về.”');
INSERT INTO "vn_ruwwad" VALUES(27,36,'27:36','Khi các sứ thần đến yết kiến Sulayman, Y bảo họ: “Các ngươi định làm cho Ta giàu thêm chăng? Những gì Allah ban cho Ta tốt hơn những thứ Ngài ban cho các ngươi. Chắc các ngươi lấy làm vui thích với những cống vật của các ngươi lắm?”');
INSERT INTO "vn_ruwwad" VALUES(27,37,'27:37','“Các ngươi hãy trở về tâu lại với họ, Ta sẽ đem quân đến hỏi tội họ và chắc chắn họ sẽ không bao giờ địch nổi, Ta sẽ đuổi họ ra khỏi nơi đó một cách nhục nhã và chắc chắn họ sẽ phải quy hàng một cách hèn hạ.”');
INSERT INTO "vn_ruwwad" VALUES(27,38,'27:38','(Sulayman) hỏi (các tướng chỉ huy các đoàn quân): “Hỡi các tướng lĩnh, ai trong các ngươi có thể mang ngai vàng của nữ vương kia đến cho Ta trước khi họ đến qui hàng Ta?”');
INSERT INTO "vn_ruwwad" VALUES(27,39,'27:39','(Sulayman vừa dứt lời) thì ‘Ifrit, một tên thuộc loài Jinn liền tâu: “Hạ thần sẽ mang nó về cho bệ hạ trước khi bệ hạ rời khỏi chỗ ngồi của bệ hạ. Hạ thần thực sự đủ mạnh và đáng tin cho việc đó.”');
INSERT INTO "vn_ruwwad" VALUES(27,40,'27:40','(Ngay lúc đó), một người (ngoan đạo) thông thạo Kinh Sách( cũng liền tâu (với Sulayman): “Hạ thần sẽ mang nó về cho bệ hạ chỉ trong nháy mắt.” Bởi thế, khi (Sulayman) thấy (chiếc ngai vương) được đặt ngay trước mặt mình, Y bảo: “Đây quả thật là một thiên ân mà Thượng Đế của Ta dùng để thử thách xem Ta là một người biết ơn hay là một kẻ bội ơn. Bởi ai biết tạ ơn thì việc tạ ơn đó chỉ có lợi cho bản thân mình; ngược lại, ai bội ơn thì Thượng Đế của Ta vẫn luôn giàu có và quảng đại.”');
INSERT INTO "vn_ruwwad" VALUES(27,41,'27:41','(Sulayman) bảo (các chư thần): “Các ngươi hãy cải trang ngai vương của nữ ta để xem nữ ta có nhận ra được nó hay không?”');
INSERT INTO "vn_ruwwad" VALUES(27,42,'27:42','Khi nữ vương đến yết kiến, (Suylayman) liền hỏi: “Có phải ngai vương của nàng như thế này đúng không?” Nữ vương đáp: “Dường như chính là nó.” (Sulayman) bảo: “Ta (và thần dân của ta) đã được ban cho kiến thức trước nàng và (tất cả bọn ta đều) là những người Muslim (thần phục Allah).”');
INSERT INTO "vn_ruwwad" VALUES(27,43,'27:43','Quả thật những thứ mà nữ vương này đã từng thờ phượng ngoài Allah đã cản trở nữ ta khiến nữ ta trở thành một trong nhóm người vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(27,44,'27:44','Rồi có lời nghinh đón nữ ta: “Xin mời nữ vương bước vào cung điện.” Khi nhìn thấy (phần nền của cung điện), nữ ta cứ ngỡ đó là một hồ nước nên đã vén váy lên làm lộ ra cẳng chân của mình. (Sulayman) bảo: “Đây là một cung điện được lót bằng pha lê.” Nữ ta thốt lên: “Lạy Thượng Đế của bề tôi, bề tôi đã bất công với chính bản thân mình, giờ bề tôi xin nguyện cùng với Sulayman thần phục Allah, Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "vn_ruwwad" VALUES(27,45,'27:45','Quả thật trước đây, TA (Allah) đã cử đến với người dân Thamud người anh em của họ, Saleh, Y bảo họ: “Các người hãy thờ phượng Allah.” Nhưng lúc đó họ chia thành hai nhóm tranh chấp nhau.');
INSERT INTO "vn_ruwwad" VALUES(27,46,'27:46','(Saleh) bảo: “Hỡi dân Ta, sao các người lại cứ hối thúc điều dữ đến trước điều lành? Đáng lẽ ra các người nên cầu xin Allah tha thứ để may ra các người được khoan dung.”');
INSERT INTO "vn_ruwwad" VALUES(27,47,'27:47','Họ (đáp trả lời kêu gọi của Saleh), nói: “Bọn ta được báo rằng Ngươi và những ai cùng Ngươi là một điềm xấu.” (Saleh) bảo họ: “Điềm xấu của các người là do Allah quyết định. Không, các người là một đám người đang bị thử thách.”');
INSERT INTO "vn_ruwwad" VALUES(27,48,'27:48','Và trong thành phố, có chín người thường hay làm điều thối nát khắp nơi nhưng không chịu sửa mình.');
INSERT INTO "vn_ruwwad" VALUES(27,49,'27:49','Những kẻ đó bảo: “Chúng ta hãy cùng nhau thề với Allah rằng chúng ta nhất định sẽ giết (Saleh) và gia đình của Y vào ban đêm, rồi chúng ta sẽ nói với người thừa kế của Y rằng chúng ta không hề biết gì đến việc gia đình Y bị thủ tiêu và chúng ta hoàn toàn nói thật.”');
INSERT INTO "vn_ruwwad" VALUES(27,50,'27:50','Chúng đã mưu toan một kế hoạch và TA cũng đã sắp đặt một kế hoạch trong lúc chúng không hề hay biết.');
INSERT INTO "vn_ruwwad" VALUES(27,51,'27:51','Ngươi (Thiên Sứ Muhammad) hãy xem mưu đồ của chúng đã để lại kết cuộc cho chúng như thế nào, chẳng phải TA đã tiêu diệt chúng và toàn bộ đám dân của chúng đó sao?!');
INSERT INTO "vn_ruwwad" VALUES(27,52,'27:52','Đó, nhà cửa của chúng đã trở nên hoang tàn vì những điều sai trái mà chúng đã làm. Quả thật trong sự việc đó là một bài học cho đám người hiểu biết.');
INSERT INTO "vn_ruwwad" VALUES(27,53,'27:53','Và TA (Allah) đã giải cứu những người có đức tin bởi họ là những người ngoan đạo biết sợ TA.');
INSERT INTO "vn_ruwwad" VALUES(27,54,'27:54','Và khi Lut bảo người dân mình: “Lẽ nào các người làm điều ô uế trong lúc các người vẫn còn nhận thức được (điều phải trái)?”');
INSERT INTO "vn_ruwwad" VALUES(27,55,'27:55','“Sao các người lại tìm đến đàn ông để giải tỏa nhục dục thay vì với phụ nữ. Không, các người thực sự là một đám người ngu muội.”');
INSERT INTO "vn_ruwwad" VALUES(27,56,'27:56','Câu trả lời của đám dân của Y chẳng có gì khác hơn ngoài việc họ nói: “Các người hãy trục xuất gia đình của Lut ra khỏi thành phố của các người! Bởi Họ là những người muốn giữ mình trong sạch!”');
INSERT INTO "vn_ruwwad" VALUES(27,57,'27:57','Bởi thế, TA đã giải cứu (Lut) và gia đình của Y ngoại trừ vợ của Y. TA đã sắp đặt cho ả ở lại trong đám người bị tiêu diệt kia.');
INSERT INTO "vn_ruwwad" VALUES(27,58,'27:58','TA đã trừng phạt họ bằng một trận mưa kinh hoàng, đó là một trận mưa thật tồi tệ dành cho đám người từng được cảnh báo.');
INSERT INTO "vn_ruwwad" VALUES(27,59,'27:59','Ngươi (Thiên Sứ Muhammad) hãy nói: “Alhamdulillah và lời chúc ‘Bằng an’ cho những bầy tôi đã được Ngài chọn. Lẽ nào Allah không phải là Đấng Ưu Việt hơn mọi thứ mà họ đã tổ hợp với Ngài ư?!”');
INSERT INTO "vn_ruwwad" VALUES(27,60,'27:60','Thế ai đã tạo các tầng trời, trái đất và cho mưa từ trên trời xuống cho các ngươi? Chính TA dùng nó làm mọc ra những mảnh vườn xanh tươi đẹp đẽ, thứ mà các ngươi không thể làm mọc ra bất cứ loại cây nào của chúng cả. Lẽ nào lại có thần linh khác cùng với Allah? Không, họ là một đám người thường dựng lên những thần linh ngụy tạo (không có thực).');
INSERT INTO "vn_ruwwad" VALUES(27,61,'27:61','Thế ai đã làm trái đất thành một nơi cư ngụ vững chắc và đặt giữa nó những con sông và giữa hai biển một tấm chắn. Lẽ nào lại có thần linh khác cùng với Allah? Không, đa số bọn họ không biết gì.');
INSERT INTO "vn_ruwwad" VALUES(27,62,'27:62','Thế ai đã đáp lại người gặp nguy khi y van xin Ngài, ai đã xua tan mọi điều xấu và làm cho các ngươi thành những người đại diện trên trái đất. Lẽ nào lại có thần linh khác cùng với Allah? Thật ít thay điều các ngươi ghi nhớ!');
INSERT INTO "vn_ruwwad" VALUES(27,63,'27:63','Thế ai đã hướng dẫn các ngươi qua những lớp tăm tối của đất liền, của biển cả; và ai gởi những luồng gió loan tin mừng báo trước hồng ân của Ngài (mưa) sắp đến. Lẽ nào lại có thần linh khác cùng với Allah? Allah Tối Thượng, Ngài tối cao vượt bên trên mọi điều mà bọn họ qui cho Ngài.');
INSERT INTO "vn_ruwwad" VALUES(27,64,'27:64','Thế ai đã khởi tạo rồi tái diễn nó lại, và ai đã cung dưỡng cho các ngươi từ trên trời và cả dưới đất. Lẽ nào lại có thần linh khác cùng với Allah? Ngươi (Thiên Sứ Muhammad) hãy bảo (những kẻ thờ đa thần): “Các người hãy trưng ra các bằng chứng của các người nếu các người nói thật?”');
INSERT INTO "vn_ruwwad" VALUES(27,65,'27:65','Ngươi (Thiên Sứ Muhammad) hãy nói: “Không ai biết rõ điều vô hình trong các tầng trời và trái đất ngoại trừ Allah; và tất cả đều không biết thời khắc nào sẽ được phục sinh trở lại.”');
INSERT INTO "vn_ruwwad" VALUES(27,66,'27:66','Không, (những kẻ thờ đa thần) không thể biết về Đời Sau. Không, bọn họ hoài nghi về điều đó. Không, bọn họ thực sự mù tịt về điều đó.');
INSERT INTO "vn_ruwwad" VALUES(27,67,'27:67','Và những kẻ không có đức tin nói: “Lẽ nào chúng tôi và tổ tiên của chúng tôi khi đã trở thành cát bụi sẽ được phục sinh trở lại?”');
INSERT INTO "vn_ruwwad" VALUES(27,68,'27:68','“Quả thật, chúng tôi cũng như ông bà cha mẹ của chúng tôi trước đây đều được hứa điều này, nhưng đó chỉ là truyện cổ tích của người xưa mà thôi (không hề có thật).”');
INSERT INTO "vn_ruwwad" VALUES(27,69,'27:69','Ngươi (Thiên Sứ Muhammad) hãy nói (với những kẻ phủ nhận sự phục sinh): “Các người hãy đi chu du khắp trái đất để nhìn xem những kẻ tội lỗi đã có kết cuộc như thế nào?”');
INSERT INTO "vn_ruwwad" VALUES(27,70,'27:70','Ngươi (Thiên Sứ Muhammad) chớ buồn phiền cho điều bọn họ nói và cũng chớ khổ tâm cho những điều bọn họ đã âm mưu.');
INSERT INTO "vn_ruwwad" VALUES(27,71,'27:71','(Những kẻ vô đức tin và phủ nhận sự phục sinh) nói: “Chừng nào lời hứa này sẽ xảy ra nếu quí vị nói thật?”');
INSERT INTO "vn_ruwwad" VALUES(27,72,'27:72','Ngươi (Thiên Sứ Muhammad) hãy nói với họ: “Điều mà các người hối thúc có lẽ ở gần sát sau lưng các người.”');
INSERT INTO "vn_ruwwad" VALUES(27,73,'27:73','Quả thật, Thượng Đế của Ngươi đã đặc ân đối với loài người, nhưng đa số bọn họ thường không biết ơn.');
INSERT INTO "vn_ruwwad" VALUES(27,74,'27:74','Quả thật, Thượng Đế của Ngươi biết rõ điều họ giấu giếm trong lòng cũng như điều họ bộc lộ ra ngoài.');
INSERT INTO "vn_ruwwad" VALUES(27,75,'27:75','Không có một điều bí ẩn nào trên trời hay dưới đất mà lại không được ghi rõ ràng trong quyển sổ (ở nơi Ngài).');
INSERT INTO "vn_ruwwad" VALUES(27,76,'27:76','Kinh Qur’an này thực sự kể lại cho con cháu của Israel nhiều điều mà họ thường tranh chấp nhau trong đó.');
INSERT INTO "vn_ruwwad" VALUES(27,77,'27:77','Và (Qur’an) đích thực là chỉ đạo và hồng ân cho những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(27,78,'27:78','Quả thật, Thượng Đế của Ngươi sẽ xét xử họ theo quyết định của Ngài bởi vì Ngài là Đấng Toàn Năng, Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(27,79,'27:79','Do đó, Ngươi hãy phó thác cho Allah, quả thật Ngươi đang ở trên Chân Lý rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(27,80,'27:80','Ngươi chắc chắn không thể làm cho người chết nghe được và cũng không thể làm cho người điếc nghe tiếng gọi khi họ đã quay lưng bỏ đi.');
INSERT INTO "vn_ruwwad" VALUES(27,81,'27:81','Và Ngươi cũng không có khả năng hướng dẫn người mù ra khỏi tình trạng lạc lối của họ. Ngươi chỉ có thể làm cho những người có đức tin nơi những lời mặc khải của TA nghe được mà thôi, bởi họ là những người Muslim (thần phục TA).');
INSERT INTO "vn_ruwwad" VALUES(27,82,'27:82','Khi lời hứa về việc trừng phạt họ sắp được thể hiện, TA sẽ cho ra từ lòng đất một con thú biết nói chuyện với họ, bởi nhân loại đã không tin nơi các lời mặc khải của TA.');
INSERT INTO "vn_ruwwad" VALUES(27,83,'27:83','(Ngươi, hỡi Thiên Sứ Muhammad, hãy nghĩ tới) Ngày mà TA sẽ cho tập trung tất cả từ mỗi cộng đồng và tách nhóm người đã phủ nhận những lời mặc khải của TA thành một nhóm riêng; rồi họ sẽ bị bắt đứng thành hàng chờ đợi.');
INSERT INTO "vn_ruwwad" VALUES(27,84,'27:84','Khi toàn bộ đã đến tập họp, (Allah) hỏi họ: “Có phải các ngươi đã phủ nhận những lời mặc khải của TA trong lúc các ngươi không có kiến thức để tiếp thu chúng hay phải chăng các ngươi đã làm gì?”');
INSERT INTO "vn_ruwwad" VALUES(27,85,'27:85','Lời hứa về việc trừng phạt họ bởi những sai quấy mà họ đã làm chắc chắn sẽ được thể hiện, rồi lúc đó họ sẽ không thể nói được gì.');
INSERT INTO "vn_ruwwad" VALUES(27,86,'27:86','Lẽ nào họ đã không nhìn thấy việc TA đã làm ra ban đêm cho họ nghỉ ngơi và ban ngày để họ quan sát (được mọi vật)? Quả thật, trong sự việc đó là những dấu hiệu cho đám người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(27,87,'27:87','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nghĩ đến) ngày mà tiếng còi sẽ được thổi lên khiến những ai ở trên trời và những ai dưới đất đều phải bạt vía kinh hồn ngoại trừ ai mà Allah muốn dung tha. Và tất cả đều phải hạ mình khiêm tốn đến trình diện trước Ngài.');
INSERT INTO "vn_ruwwad" VALUES(27,88,'27:88','Rồi (lúc đó) Ngươi sẽ thấy những quả núi mà Ngươi đã nghĩ rằng chúng rất kiên cố vững chắc sẽ vỡ ra như những đám mây tan. Công trình tạo hóa của Allah, Đấng đã hoàn chỉnh mọi vật. Quả thật, Ngài luôn tường tận mọi điều các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(27,89,'27:89','Ai mang theo điều tốt thì sẽ được ban thưởng điều tốt hơn và sẽ được an toàn khỏi cảnh kinh hoàng của Ngày hôm đó.');
INSERT INTO "vn_ruwwad" VALUES(27,90,'27:90','Ai mang theo điều tội lỗi thì mặt của họ sẽ bị quẳng úp vào Hỏa Ngục, kèm theo lời phán: “Lẽ nào đây không phải là hình phạt tương xứng với điều mà các ngươi đã làm?”');
INSERT INTO "vn_ruwwad" VALUES(27,91,'27:91','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nói): “Quả thật Ta chỉ được lệnh thờ phượng Thượng Đế của thành phố (Makkah) này, nơi mà Ngài đã làm cho nó linh thiêng, và mọi thứ đều thuộc về Ngài; và Ta được lệnh phải là một tín đồ Muslim.”');
INSERT INTO "vn_ruwwad" VALUES(27,92,'27:92','“Ta được lệnh phải đọc Qur’an. Bởi thế ai được hướng dẫn thì quả thật việc được hướng dẫn đó chỉ có lợi cho bản thân y mà thôi; còn ai lạc lối thì thật ra Ta chỉ là một người cảnh báo!”');
INSERT INTO "vn_ruwwad" VALUES(27,93,'27:93','Và Ngươi hãy nói (hỡi Thiên Sứ Muhammad!): “Alhamdulillah, Đấng sẽ sớm cho các ngươi thấy những dấu hiệu của Ngài rồi các ngươi sẽ nhận ra chúng.” Và Thượng Đế của Ngươi không làm ngơ trước những điều các người làm.');
INSERT INTO "vn_ruwwad" VALUES(28,1,'28:1','Ta. Sin. Mim.');
INSERT INTO "vn_ruwwad" VALUES(28,2,'28:2','Đó là những câu Kinh của một Kinh Sách rõ ràng (không mơ hồ và bí ẩn).');
INSERT INTO "vn_ruwwad" VALUES(28,3,'28:3','TA (Allah) đọc cho Ngươi (Thiên Sứ Muhammad) nghe câu chuyện về Musa và Pha-ra-ông bằng sự thật, (một câu chuyện) dành cho đám người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(28,4,'28:4','Quả thật, Pha-ra-ông tự suy tôn mình (là chúa) tối cao trong xứ, (ép dân Israel làm nô lệ), chia họ thành nhiều nhóm nhỏ để làm họ suy yếu, giết chết các con trai của họ và tha sống các phụ nữ của họ. Quả thật, hắn là một tên tàn bạo quá mức.');
INSERT INTO "vn_ruwwad" VALUES(28,5,'28:5','TA (Allah) muốn ban ân cho những người yếu thế trong xứ, TA muốn làm cho họ thành những nhà lãnh đạo và thành những người kế thừa.');
INSERT INTO "vn_ruwwad" VALUES(28,6,'28:6','TA ban cho họ một nơi định cư trong xứ và TA làm cho Pha-ra-ông và Haman cùng binh lính của hai tên đó thấy những điều làm cho bọn họ lo sợ.');
INSERT INTO "vn_ruwwad" VALUES(28,7,'28:7','TA đã mặc khải cho mẹ của Musa: “Ngươi hãy cho Nó (Musa) bú sữa nhưng khi ngươi lo sợ cho Nó (bị sát hại) thì ngươi hãy ném Nó xuống dòng sông (Nile), ngươi chớ sợ hãi và cũng chớ buồn lo cho Nó, chắc chắc TA sẽ mang Nó trả lại cho ngươi và cử Nó làm một vị Thiên Sứ (của TA).”');
INSERT INTO "vn_ruwwad" VALUES(28,8,'28:8','Rồi người nhà của Pha-ra-ông đã nhặt (Musa từ dòng sông) mang về nuôi (đúng theo ý của Allah, bởi vì Ngài muốn) Musa trở thành kẻ thù và nguồn lo âu cho bọn họ. Quả thật, Pha-ra-ông, Haman và binh lính của bọn họ là những kẻ tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(28,9,'28:9','Vợ của Pha-ra-ông nói với (Pha-ra-ông): “Đứa bé này sẽ là một nguồn vui cho cặp mắt của thiếp và bệ hạ. Xin bệ hạ đừng giết Nó, biết đâu sau này Nó sẽ giúp ích cho chúng ta hoặc chúng ta sẽ nhận Nó làm con của chúng ta.” Nhưng bọn họ không nhận thấy (hậu quả của sự việc mình đang làm).');
INSERT INTO "vn_ruwwad" VALUES(28,10,'28:10','Trái tim của mẹ Musa trở nên trống trải (vì thương nhớ con), nữ ta gần như muốn tiết lộ câu chuyện về đứa con trai của mình nếu như TA không củng cố trái tim của nữ để cho nữ vẫn còn là một người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(28,11,'28:11','(Mẹ của Musa) đã bảo người chị của Musa: “Con hãy theo dõi em của con!” Thế là, người chị đứng nhìn đứa em từ đằng xa trong lúc quân lính không để ý.');
INSERT INTO "vn_ruwwad" VALUES(28,12,'28:12','Và ngay từ đầu, TA (Allah) đã khiến cho (Musa) không bú được sữa của những bà vú em, mãi đến lúc chị của Y thưa: “Quí ngài có muốn tiện nữ chỉ cho quí ngài một gia đình có thể chăm sóc đứa bé giùm cho quí ngài một cách chu đáo và họ luôn mong sự bình an cho Nó?”');
INSERT INTO "vn_ruwwad" VALUES(28,13,'28:13','Với cách đó, TA đã mang (Musa) trả lại cho mẹ của Y để cho nữ ta vui trở lại và không còn buồn lo nữa cũng như để cho nữ ta biết rằng Lời Hứa của Allah là sự thật. Tuy nhiên, đa số bọn họ không biết.');
INSERT INTO "vn_ruwwad" VALUES(28,14,'28:14','Rồi khi Musa đến tuổi trưởng thành và chính chắn, TA đã ban cho Y sự khôn ngoan và nguồn kiến thức. Đó là cách mà TA đãi ngộ những người làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(28,15,'28:15','(Có một lần) vào buổi ban trưa, (Musa) đi vào thành phố lúc thị dân không để ý, Y thấy hai người đàn ông ẩu đả nhau nơi đó; một người thuộc phe của Y (dân Israel) và người kia thuộc phe của kẻ thù của Y. Người thuộc phe của Y gọi Y tiếp tay đánh lại kẻ địch. Thế là Musa buông tay đánh kẻ thù và đã lỡ giết chết hắn. (Musa đã hối hận ngay sau đó), bảo: “Đây đúng là hành động của Shaytan và Shaytan rõ thật là kẻ thù (của con người) chuyên dẫn dắt (họ) đến với sự lầm lạc.”');
INSERT INTO "vn_ruwwad" VALUES(28,16,'28:16','(Musa) cầu nguyện: “Lạy Thượng Đế của bề tôi, bề tôi đã bất công với chính mình, xin Ngài tha thứ tội lỗi cho bề tôi.” Vậy là Y được Ngài tha thứ, bởi quả thật Ngài là Đấng Hằng Tha Thứ, Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(28,17,'28:17','(Musa) cầu nguyện: “Lạy Thượng Đế của bề tôi, với hồng ân mà Ngài đã ban cho bề tôi, bề tôi quyết sẽ không bao giờ tiếp tay cho những kẻ tội lỗi nữa.”');
INSERT INTO "vn_ruwwad" VALUES(28,18,'28:18','Rồi sáng ra, khi đang ở trong thành phố, (Musa) lo sợ và ngó chừng (quân lính của Pha-ra-ông) thì Y đột nhiên lại nghe tiếng cầu cứu của kẻ xin trợ giúp ngày hôm qua. Musa đã bảo kẻ đó: “Mày đúng thật là một tên chuyên dắt người ta lầm lạc mà!”');
INSERT INTO "vn_ruwwad" VALUES(28,19,'28:19','Khi (Musa) vừa định túm lấy kẻ thù của cả hai thì tên Israel (tưởng Musa cung tay đánh mình nên) vội lên tiếng: “Hỡi Musa, mày định giết tao như mày đã giết một người ngày hôm qua đúng không? Mày thật ra chỉ muốn trở thành một tên tàn bạo trong xứ chớ mày không muốn trở thành một người làm điều thiện.”');
INSERT INTO "vn_ruwwad" VALUES(28,20,'28:20','(Lúc đó), từ cuối phố, có một người chạy đến bảo: “Hỡi Musa, các vị tù trưởng đang họp bàn về chuyện của anh để giết anh. Tôi khuyên anh hãy mau chạy trốn.”');
INSERT INTO "vn_ruwwad" VALUES(28,21,'28:21','(Nghe xong, Musa) liền rời bỏ chỗ đó, Y vừa lo sợ vừa ngó chừng đề phòng (đám lính của Pha-ra-ông). Y cầu nguyện: “Lạy Thượng Đế của bề tôi, xin Ngài cứu bề tôi khỏi đám người làm điều sai quấy bất công kia!”');
INSERT INTO "vn_ruwwad" VALUES(28,22,'28:22','Khi chạy trốn về phía địa phận của Madyan, (Musa) nói thầm một mình: “Mong rằng Thượng Đế của ta sẽ dắt ta đi đến con đường đúng đắn.”');
INSERT INTO "vn_ruwwad" VALUES(28,23,'28:23','Rồi khi đến chỗ có nước thuộc địa phận Madyan, (Musa) thấy một đám người đang múc nước, và ngoài đám người đó, (Musa) còn thấy hai người phụ nữ đang đứng riêng lẻ. (Musa) đến hỏi hai người phụ nữ đó: “Hai cô có chuyện gì vậy?” Hai người nữ kia trả lời: “Chúng tôi không thể múc nước cho đàn cừu của mình uống trừ phi những người chăn cừu kia dắt đàn cừu của họ rời khỏi chỗ đó; và cha của chúng tôi thì đã lớn tuổi.”');
INSERT INTO "vn_ruwwad" VALUES(28,24,'28:24','Thế là, Musa giúp hai cô gái múc nước rồi lui về nghỉ dưới bóng cây. Sau đó, Y cầu nguyện: “Lạy Thượng Đế của bề tôi, quả thật bề tôi đang cần điều tốt đẹp Ngài ban cho.”');
INSERT INTO "vn_ruwwad" VALUES(28,25,'28:25','Lúc sau, một trong hai người phụ nữ kia bẽn lẽn bước đến gặp (Musa), nói: “Cha tôi mời anh đến gặp ông ấy để ông ấy đền đáp về việc anh đã giúp chúng tôi múc nước.” (Musa) đi theo cô gái đến gặp người cha và kể lại cho ông nghe câu chuyện của mình. (Người cha của hai cô gái) bảo: “Cậu chớ lo sợ, cậu thực sự đã thoát khỏi đám người làm điều sai quấy bất công kia rồi.”');
INSERT INTO "vn_ruwwad" VALUES(28,26,'28:26','Một trong hai cô gái nói (với cha của mình): “Thưa cha, cha hãy thuê anh ấy giúp việc. Người thực sự tốt nhất để cha thuê giúp việc nên là một người khỏe mạnh và trung thực.”');
INSERT INTO "vn_ruwwad" VALUES(28,27,'28:27','(Cha của hai cô gái) nói với (Musa): “Bác có ý gả một trong hai đứa con gái của bác cho cháu với điều kiện cháu phải làm thuê cho bác trong thời hạn tám năm; nhưng nếu cháu muốn làm thêm cho đủ mười năm thì đó là tùy cháu; bác không muốn gây khó khăn cho cháu; insha-Allah, rồi cháu sẽ thấy bác là một người lương thiện.”');
INSERT INTO "vn_ruwwad" VALUES(28,28,'28:28','(Musa) đáp: “Bác cháu ta cứ thỏa thuận với nhau như thế, dù cháu có thể hoàn tất bất cứ thời hạn nào trong hai thời hạn vừa nói thì bác vẫn không được hờn ghét cháu, Allah làm chứng cho điều chúng ta vừa nói.”');
INSERT INTO "vn_ruwwad" VALUES(28,29,'28:29','Sau khi kết thúc kỳ hạn thỏa thuận, (Musa) đã cùng gia đình của mình ra đi trong đêm. Từ xa, (Musa) nhìn thấy một ngọn lửa trên sườn núi Tur, Y bảo người nhà: “Hãy dừng lại! Ta thấy lửa ở đằng xa, hy vọng ta sẽ mang về cho mọi người tin tức từ đám lửa đó hoặc ta sẽ mang về một khúc củi cháy cho mọi người sưởi ấm.”');
INSERT INTO "vn_ruwwad" VALUES(28,30,'28:30','Rồi khi đến chỗ của ngọn lửa, có tiếng gọi từ phía bên phải của thung lũng tại một vùng đất được ban phúc từ một loại cây (Ô-liu): “Hỡi Musa, TA đây là Allah, là Thượng Đế đích thực của vũ trụ và vạn vật.”');
INSERT INTO "vn_ruwwad" VALUES(28,31,'28:31','“Ngươi hãy ném chiếc gậy của Ngươi xuống.” (Musa liền ném chiếc gậy của mình xuống), khi thấy nó động đậy giống như một con rắn thì Y quay lưng bỏ chạy không dám nhìn lại đằng sau. (Allah phán bảo): “Này Musa, ngươi hãy bước đến gần và chớ sợ, chắc chắn Ngươi an toàn.”');
INSERT INTO "vn_ruwwad" VALUES(28,32,'28:32','“Ngươi hãy áp bàn tay của ngươi vào lòng ngực của mình, nó sẽ trở thành trắng toát khi Ngươi rút tay ra nhưng chắc chắn không hề đau đớn gì và Ngươi hãy áp cánh tay của Ngươi vào cạnh sườn của mình để khỏi sợ. Đấy là hai dấu hiệu từ Thượng Đế của ngươi mang đến trình bày cho Pha-ra-ông và các quan thần của hắn. Bọn họ quả thật là một đám người đại nghịch (với Allah).”');
INSERT INTO "vn_ruwwad" VALUES(28,33,'28:33','(Musa) cầu nguyện: “Lạy Thượng Đế của bề tôi, quả thật, bề tôi đã giết một người của họ. Vì thế, bề tôi sợ họ sẽ giết bề tôi.”');
INSERT INTO "vn_ruwwad" VALUES(28,34,'28:34','“Người anh em của bề tôi, Harun, hoạt bát hơn bề tôi, cho nên, bề tôi xin Ngài cử y làm một người phụ tá cùng đi với bề tôi để xác nhận sứ mạng của bề tôi. Bề tôi thực sự sợ rằng họ sẽ cho bề tôi nói dối.”');
INSERT INTO "vn_ruwwad" VALUES(28,35,'28:35','(Allah) bảo (Musa): “TA sẽ cho người anh em của Ngươi làm trợ thủ cho Ngươi và TA sẽ ban cho cả hai anh em Ngươi quyền uy khiến bọn họ không làm hại được hai Ngươi. Với những Phép Lạ của TA, hai Ngươi và những người theo hai Ngươi chắc chắn sẽ giành chiến thắng.”');
INSERT INTO "vn_ruwwad" VALUES(28,36,'28:36','Rồi khi Musa mang những lời mặc khải rõ rệt của TA đến gặp Pha-ra-ông và đồng bọn của hắn thì họn họ bảo: “Đây quả thật chỉ là trò ảo thuật được bịa ra mà thôi chứ bọn ta chưa bao giờ nghe chuyện này từ nơi tổ tiên của bọn ta trước đây cả.”');
INSERT INTO "vn_ruwwad" VALUES(28,37,'28:37','Musa nói (với Pha-ra-ông): “Thượng Đế của Ta biết rõ ai là người mang nguồn chỉ đạo từ nơi Ngài và ai là người sẽ có được nơi ở cuối cùng tốt đẹp. Quả thật, những kẻ làm điều sai quấy bất công sẽ không bao giờ thành đạt.”');
INSERT INTO "vn_ruwwad" VALUES(28,38,'28:38','Pha-ra-ông nói (với các chư thần của hắn): “Hỡi quân thần, Ta không hề biết có chúa nào khác của các ngươi ngoài ta cả. Này Haman, ngươi hãy đốt lò nung gạch cho trẫm và xây cho trẫm một cái tháp để trẫm đi lên gặp Thượng Đế của Musa và trẫm nghĩ rằng (Musa) chỉ là một tên lừa bịp.”');
INSERT INTO "vn_ruwwad" VALUES(28,39,'28:39','(Pha-ra-ông) và đám thuộc hạ của hắn đã ngạo mạn kiêu căng trong xứ một cách ngông cuồng; bọn họ tưởng bọn họ sẽ không bị đưa trở về trình diện TA.');
INSERT INTO "vn_ruwwad" VALUES(28,40,'28:40','Do đó, TA đã trừng phạt (Pha-ra-ông) và đám thuộc hạ của hắn bằng cách nhấn chìm bọn họ xuống biển. Bởi vậy, Ngươi (Thiên Sứ Muhammad) hãy nhìn xem kết cuộc của những kẻ làm điều sai quấy bất công đã diễn ra như thế nào.');
INSERT INTO "vn_ruwwad" VALUES(28,41,'28:41','TA đã làm cho bọn họ thành những tên cầm đầu trong việc kêu gọi người ta đi vào Hỏa Ngục, rồi vào Ngày Phán Xét, bọn họ sẽ không được giúp đỡ.');
INSERT INTO "vn_ruwwad" VALUES(28,42,'28:42','TA đã để những lời nguyền rủa đi theo bọn họ suốt cuộc sống của thế gian; và vào Ngày Phán Xét, bọn họ sẽ là những kẻ xấu xa nhất.');
INSERT INTO "vn_ruwwad" VALUES(28,43,'28:43','Quả thật TA đã ban cho Musa Kinh Sách (Cựu Ước) dùng để soi sáng cho nhân loại sau khi TA đã tiêu diệt những thế hệ trước đó. (Kinh sách đó) vừa là một nguồn Chỉ Đạo vừa là một Hồng Ân cho (nhân loại) mong rằng họ biết lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(28,44,'28:44','Ngươi (hỡi Thiên Sứ Muhammad) đã không ở phía tây (của ngọn núi Tur) khi TA ban hành công việc cho Musa và Ngươi cũng không là một nhân chứng (cho sự việc đó).');
INSERT INTO "vn_ruwwad" VALUES(28,45,'28:45','Tuy nhiên, TA đã cho ra những thế hệ khác và để họ sống thọ hơn; và Ngươi (Thiên Sứ Muhammad) không phải là một người sống chung với người dân của Madyan và đọc cho họ về những Lời Mặc Khải của TA, nhưng chính TA là Đấng đã cử phái các Sứ Giả.');
INSERT INTO "vn_ruwwad" VALUES(28,46,'28:46','Ngươi (hỡi Thiên Sứ Muhammad) cũng đã không ở bên sườn núi Tur khi TA gọi (Musa) nhưng Ngươi là một Hồng Ân từ Thượng Đế của Ngươi để Ngươi có thể cảnh báo một dân tộc mà trước Ngươi đã không có một vị cảnh báo nào được cử đến với họ, mục đích để cho họ thức tỉnh.');
INSERT INTO "vn_ruwwad" VALUES(28,47,'28:47','Nếu như (TA) không (cử Ngươi, hỡi Thiên Sứ Muhammad, đến với dân Quraish) thì e rằng khi họ gặp phải tai họa do những điều mà bàn tay của họ đã gây ra, chắc chắn họ sẽ nói: “Lạy Thượng Đế của bầy tôi! Sao Ngài không cử một Sứ Giả đến với bầy tôi kia chứ? Nếu Ngài cử vị đó đến thì chắc chắn bầy tôi đã tuân theo những lời mặc khải của Ngài và bầy tôi chắc chắn đã trở thành những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(28,48,'28:48','Tuy nhiên, khi chân lý đến với họ thì họ lại bảo: “Tại sao Y không được ban cho điều giống như điều đã được ban cho Musa?” Phải chăng họ đã không có đức tin nơi những điều đã được ban cho Musa trước đây đó sao? Họ nói: “Hai loại phù phép này (Kinh Cựu Ước và Kinh Qurr’an) hỗ trợ lẫn nhau!” Và họ nói: “Chúng tôi không tin bất cứ cái nào.”');
INSERT INTO "vn_ruwwad" VALUES(28,49,'28:49','Ngươi (Thiên Sứ Muhammad) hãy bảo họ: “Vậy các ngươi hãy mang một Kinh Sách từ Allah đến làm Chỉ Đạo tốt hơn hai quyển này đi, Ta sẽ theo nó, nếu các ngươi là những người nói thật.”');
INSERT INTO "vn_ruwwad" VALUES(28,50,'28:50','Nếu họ không đáp lại lời Ngươi thì Ngươi hãy biết rằng họ chỉ chạy theo dục vọng của bản thân họ mà thôi. Và còn ai lầm lạc hơn kẻ chỉ biết chạy theo dục vọng của bản thân thay vì đi theo nguồn Chỉ Đạo của Allah. Quả thật, Allah không hướng dẫn đám người làm điều sai quấy bất công bao giờ.');
INSERT INTO "vn_ruwwad" VALUES(28,51,'28:51','Quả thật, TA đã mang đến cho họ Lời Lẽ rõ ràng (Qur’an) mong rằng họ biết lưu tâm mà thức tỉnh.');
INSERT INTO "vn_ruwwad" VALUES(28,52,'28:52','Những người đã được TA ban cho Kinh Sách trước (Qur’an) chắc chắn sẽ có đức tin nơi Nó.');
INSERT INTO "vn_ruwwad" VALUES(28,53,'28:53','Khi (Qur’an) được đọc ra cho họ thì họ liền nói: “Chúng tôi đã tin nơi Nó. Nó đích thực là Chân Lý từ Thượng Đế của chúng tôi. Chúng tôi đã là những người thần phục Allah trước Nó.”');
INSERT INTO "vn_ruwwad" VALUES(28,54,'28:54','Họ sẽ được ban thưởng hai lần bởi vì họ đã kiên nhẫn chịu đựng và họ dùng điều thiện tốt để xóa bỏ điều xấu và chi dùng bổng lộc mà TA đã ban cấp cho họ (vào con đường chính nghĩa của TA).');
INSERT INTO "vn_ruwwad" VALUES(28,55,'28:55','Khi nghe chuyện tầm phào, họ cáo lui bỏ đi và nói: “Chúng tôi có công việc của chúng tôi và quí vị có công việc của quí vị. Chào bằng an đến quí vị. Chúng tôi không tìm kiếm kẻ ngu dốt (để tranh cãi về tôn giáo và cuộc sống).”');
INSERT INTO "vn_ruwwad" VALUES(28,56,'28:56','(Hỡi Thiên Sứ Muhammad) Ngươi chắc chắn không thể hướng dẫn được bất cứ ai Ngươi yêu thương mà chính Allah mới là Đấng hướng dẫn người nào Ngài muốn. Quả thật, Ngài biết rõ nhất về những người được hướng dẫn (theo Chính Đạo).');
INSERT INTO "vn_ruwwad" VALUES(28,57,'28:57','(Những kẻ đa thần Quraish) nói: “Nếu bọn ta đi theo nguồn chỉ đạo cùng với ngươi thì bọn ta sẽ bị trục xuất ra khỏi xứ của bọn ta.” Lẽ nào TA (Allah) đã không thiết lập cho họ một thánh địa an toàn với đủ loại hoa quả được mang đến cho nó như là thiên lộc từ nơi TA? Tuy nhiên, đa số bọn họ không biết (điều đó).');
INSERT INTO "vn_ruwwad" VALUES(28,58,'28:58','Đã có không biết bao thị trấn bị TA tiêu diệt vì (cư dân của) nó chỉ biết hưởng lạc trong cuộc sống phồn thịnh của nó? Nhà cửa của họ trở nên tan hoang và điêu tàn sau khi đã bị hủy diệt chỉ trừ một số rất ít. Và TA là Đấng Thừa Kế di sản của họ.');
INSERT INTO "vn_ruwwad" VALUES(28,59,'28:59','(Hỡi Thiên Sứ Muhammad), Thượng Đế của Ngươi không tiêu diệt bất cứ thị trấn nào trừ phi Ngài đã cử đến cho mỗi thủ phủ của nó một vị Thiên Sứ (của Ngài) để Y đọc nhắc nhở dân chúng của nó về những lời mặc khải của TA. Và TA cũng không tiêu diệt các thị trấn đó trừ phi cư dân của chúng là những kẻ làm điều sai quấy bất công.');
INSERT INTO "vn_ruwwad" VALUES(28,60,'28:60','Những thứ mà các ngươi đã được ban cho chỉ là những thứ hưởng thụ tạm thời cũng như chỉ là những thứ trang hoàng của đời sống trần tục (ngắn ngủi) này mà thồi, và những gì ở nơi Allah mới là tốt nhất và còn mãi. Lẽ nào các ngươi không hiểu ư?');
INSERT INTO "vn_ruwwad" VALUES(28,61,'28:61','Lẽ nào một người mà TA đã hứa bằng một Lời Hứa tốt đẹp, cái mà y chắc chắn sẽ tiếp nhận nó, lại giống với kẻ mà TA đã cho hưởng thụ trong chốc lát của cõi trần tục (tạm bợ) này rồi sau đó vào Ngày Phán Xét y sẽ phải bị trừng phạt (trong Hỏa Ngục)?');
INSERT INTO "vn_ruwwad" VALUES(28,62,'28:62','Vào Ngày mà Ngài (Allah) sẽ gọi họ đến rồi Ngài sẽ phán: “Đâu là những thần linh ngang vai của TA mà các ngươi đã từng khẳng định trước đây?”');
INSERT INTO "vn_ruwwad" VALUES(28,63,'28:63','Những kẻ mà Lời (kết tội) đã được khẳng định đối với bọn họ sẽ thưa: “Lạy Thượng Đế của bầy tôi! Đây là những kẻ mà bầy tôi đã dắt đi lạc. Bầy tôi dắt bọn họ đi giống như việc tự bản thân bầy tôi đi lạc; bầy tôi vô can với bọn họ trước mặt Ngài; bọn họ đã không thờ phượng bầy tôi trước đây.”');
INSERT INTO "vn_ruwwad" VALUES(28,64,'28:64','Có lời phán bảo họ: “Các ngươi hãy gọi những thần linh của các ngươi!” Họ đã gọi những thần linh đó nhưng không ai trả lời họ và họ sẽ thấy hình phạt trước mặt mình. Giá như họ tuân theo Chỉ Đạo (thì ngày hôm nay họ đâu phải chịu cảnh như thế này!).');
INSERT INTO "vn_ruwwad" VALUES(28,65,'28:65','Vào Ngày mà Ngài sẽ gọi họ đến, phán: “Đâu là điều mà các ngươi đã đáp lại các vị Thiên Sứ (của TA)?”');
INSERT INTO "vn_ruwwad" VALUES(28,66,'28:66','Tuy nhiên, lý lẽ biện bạch sẽ mù mịt đối với họ vào Ngày hôm đó bởi họ không thể hỏi han vấn kế nhau (bất cứ điều gì).');
INSERT INTO "vn_ruwwad" VALUES(28,67,'28:67','Nhưng đối với ai biết ăn năn sám hối, có đức tin và hành thiện thì may ra sẽ là một trong những người thành công.');
INSERT INTO "vn_ruwwad" VALUES(28,68,'28:68','Thượng Đế của Ngươi (Thiên Sứ Muhammad) tạo hóa và lựa chọn bất cứ thứ gì Ngài muốn. Họ (những kẻ thờ đa thần) không có một sự lựa chọn nào (trong việc phản kháng lại Ngài). Quang vinh và trong sạch thay Allah, Ngài Tối Cao vượt hẳn những kẻ mà họ đã tổ hợp chúng với Ngài.');
INSERT INTO "vn_ruwwad" VALUES(28,69,'28:69','Thượng Đế của Ngươi biết rõ những điều họ giấu kín trong lòng của họ cũng như những điều họ bộc lộ ra bên ngoài.');
INSERT INTO "vn_ruwwad" VALUES(28,70,'28:70','Ngài là Allah, Đấng mà Không có Thượng Đế đích thực nào khác ngoài Ngài. Mọi lời ca ngợi và tán dương ở đời này và ở Đời Sau đều thuộc về một mình Ngài. Ngài nắm mọi quyền định đoạt trong xét xử; và các ngươi sẽ được đưa trở về trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(28,71,'28:71','Ngươi (Thiên Sứ Muhammad) hãy nói (với những người thờ đa thần): “Các ngươi thấy sao, nếu Allah làm cho ban đêm kéo dài mãi cho đến Ngày Phục Sinh thì có thượng đế nào khác ngoài Allah sẽ mang ánh sáng lại cho các ngươi không? Lẽ nào các ngươi không biết nghe?”');
INSERT INTO "vn_ruwwad" VALUES(28,72,'28:72','Ngươi hãy nói (với họ): “Các ngươi thấy sao, nếu Allah làm cho ban ngày kéo dài mãi cho đến Ngày Phục Sinh thì có thượng đế nào khác ngoài Allah sẽ mang bóng tối lại cho các ngươi để các ngươi có thể nghỉ ngơi trong đó không? Lẽ nào các ngươi không nhìn thấy?”');
INSERT INTO "vn_ruwwad" VALUES(28,73,'28:73','“Nhờ lòng nhân từ của Ngài, Ngài đã tạo ra ban đêm và ban ngày cho các ngươi để các ngươi có thể nghỉ ngơi (vào ban đêm) và tìm kế sinh nhai (vào ban ngày); hi vọng các ngươi biết tri ân Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(28,74,'28:74','Vào Ngày mà Ngài sẽ gọi họ (những kẻ đa thần) đến chất vấn, Ngài sẽ phán: “Đâu là những đối tác ngang vai của TA mà các ngươi đã từng xác nhận trước đây?”');
INSERT INTO "vn_ruwwad" VALUES(28,75,'28:75','TA sẽ đưa ra từ mỗi cộng đồng một nhân chứng rồi TA sẽ phán: “Các ngươi hãy trình ra bằng chứng của các ngươi!” Lúc đó, họ sẽ biết rằng mọi sự thật đều thuộc về Allah còn những kẻ (đối tác ngang vai với Ngài) mà họ đã từng bịa đặt sẽ bỏ họ đi mất dạng.');
INSERT INTO "vn_ruwwad" VALUES(28,76,'28:76','Qarun đúng thực xuất thân từ đám dân của Musa nhưng y đã cư xử kiêu ngạo với họ. TA đã ban cho (Qarun) kho tàng châu báu nhiều đến mức cần phải nhờ đến cả đại đội những người cơ bắp lực lưỡng mới vác nổi những chìa khóa kho của y. Khi người dân bảo y: “Ngươi chớ mừng vui đầy kiêu ngạo! Bởi quả thật Allah không yêu thương những kẻ vui mừng đầy kiêu ngạo (về sự giàu có của mình).”');
INSERT INTO "vn_ruwwad" VALUES(28,77,'28:77','“Ngươi hãy dùng của cải mà Allah đã ban cho ngươi để tìm kiếm một ngôi nhà ở Đời Sau và chớ quên mất phần (cho nhu cầu cuộc sống) của mình ở đời này, ngươi hãy đối xử tốt (với người khác) giống như việc Allah đối xử tốt với ngươi và ngươi chớ tìm cách làm điều thối nát trong xứ. Quả thật, Allah không yêu thương những kẻ làm điều thối nát.”');
INSERT INTO "vn_ruwwad" VALUES(28,78,'28:78','(Nghe được lời nhắc nhở, Qarun) đáp: “Thật ra, ta được giàu có là đều do sự hiểu biết của ta mà thôi.” Lẽ nào y đã không biết rằng Allah đã tiêu diệt những kẻ có nhiều quyền lực và giàu có hơn y trong những thế hệ trước y hay sao? Những kẻ tội lỗi sẽ không bị hạch hỏi ngay về những tội lỗi của họ.');
INSERT INTO "vn_ruwwad" VALUES(28,79,'28:79','Thế rồi, (Qarun) ra ngoài phô trương vẻ hào nhoáng và lộng lẫy (về sự giàu có) của mình trước dân chúng. (Trước sự phô trương về mức độ giàu có của Qarun), những kẻ ham muốn đời sống trần tục vội lên tiếng: “Ôi, ước gì mình được ban cho giống như những gì đã được ban cho Qarun. Y thực sự đã làm chủ một kho tàng quá vĩ đại.”');
INSERT INTO "vn_ruwwad" VALUES(28,80,'28:80','Những người được ban cho kiến thức nói: “Thật khốn khổ cho các người. Quả thật phần thưởng của Allah sẽ tốt hơn cho những ai có đức tin và hành thiện. Tuy nhiên, chỉ những người biết kiên nhẫn chịu đựng mới đạt được phần thưởng đó.”');
INSERT INTO "vn_ruwwad" VALUES(28,81,'28:81','Vì vậy, TA đã làm cho đất sụp xuống nuốt chửng y và cả nhà cửa của y. Và y đã chẳng có nhóm bè phái nào ngoài Allah giúp đỡ giải cứu y thoát khỏi hình phạt đó của Ngài và y cũng không thể tự cứu lấy mình.');
INSERT INTO "vn_ruwwad" VALUES(28,82,'28:82','Những kẻ đã ước ao và khao khát có một địa vị giống như địa vị của (Qarun) ngày hôm qua giờ lại nói: “Ôi! Quả thật Allah nới rộng và thu hẹp bổng lộc của Ngài cho ai Ngài muốn trong số bầy tôi của Ngài; nếu Allah không nhân từ đối với chúng tôi thì Ngài đã làm cho đất nuốt chúng tôi mất rồi. Ôi! Những kẻ vô đức tin sẽ không thành đạt.”');
INSERT INTO "vn_ruwwad" VALUES(28,83,'28:83','Đó là ngôi nhà (nơi Thiên Đàng) ở cõi Đời Sau mà TA đã tạo ra cho những ai không muốn suy tôn mình cao cả và cũng không làm những điều thối nát trên trái đất. Và kết cuộc tốt đẹp chỉ dành cho những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(28,84,'28:84','Ai mang theo điều thiện tốt thì sẽ được phần thưởng tốt hơn nhưng ai mang theo điều xấu thì những ai làm điều xấu chỉ sẽ bị phạt ngang bằng với điều xấu mà họ đã làm.');
INSERT INTO "vn_ruwwad" VALUES(28,85,'28:85','Quả thật, Đấng đã ban Qur’an cho Ngươi (Thiên Sứ Muhammad) sẽ đưa Ngươi trở lại cố hương. Ngươi hãy nói: “ Thượng Đế của Ta biết rõ ai thực sự là người mang đến nguồn Chỉ Đạo và ai thực sự là kẻ lầm đường lạc lối.”');
INSERT INTO "vn_ruwwad" VALUES(28,86,'28:86','Ngươi đã không mong ước việc Kinh Sách được ban xuống cho Ngươi nhưng đó là hồng phúc từ nơi Thượng Đế của Ngươi; vì vậy, Ngươi chớ đừng trợ giúp những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(28,87,'28:87','Ngươi đừng để (những kẻ đa thần này) làm cho Ngươi từ bỏ những lời mặc khải của Allah sau khi chúng đã được ban xuống cho Ngươi mà Ngươi hãy kêu gọi đến với Thượng Đế của Ngươi; và Ngươi hãy đừng trở thành một kẻ đa thần.');
INSERT INTO "vn_ruwwad" VALUES(28,88,'28:88','Ngươi chớ đừng cầu nguyện cùng với Allah một thần linh nào khác bởi lẽ không có Thượng Đế đích thực nào khác ngoài Ngài cả. Tất cả mọi vật đều sẽ tiêu vong ngoại trừ Sắc Diện của Ngài. Ngài nắm quyền xét xử và các ngươi sẽ được đưa trở về trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(29,1,'29:1','Alif. La-m. Mi-m.');
INSERT INTO "vn_ruwwad" VALUES(29,2,'29:2','Có phải nhân loại tưởng rằng họ sẽ được yên thân khi nói “Chúng tôi đã có đức tin” mà không bị thử thách ư?');
INSERT INTO "vn_ruwwad" VALUES(29,3,'29:3','Quả thật, TA đã thử thách những người trước họ. Allah muốn cho thấy rõ ai là người chân thật và ai là kẻ dối gian (qua cuộc thử thách).');
INSERT INTO "vn_ruwwad" VALUES(29,4,'29:4','Hoặc phải chăng những kẻ làm điều tội lỗi tưởng rằng họ có thể qua mặt được TA ư? Thật tệ hại cho điều mà họ xét đoán.');
INSERT INTO "vn_ruwwad" VALUES(29,5,'29:5','Người nào mong trở về gặp lại Allah thì quả thật thời hạn (ấn định cho việc gặp gỡ đó) của Allah chắc chắn sẽ đến. Quả thật, Ngài là Đấng Hằng Nghe, Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(29,6,'29:6','Người nào phấn đấu thì thật ra y chỉ phấn đấu cho bản thân mình. Quả thật, Allah là Đấng Giàu Có, không cần đến thế gian.');
INSERT INTO "vn_ruwwad" VALUES(29,7,'29:7','Những ai có đức tin và hành thiện, TA chắc chắn sẽ xóa bỏ những điều xấu xa và tội lỗi của họ, và TA chắc chắn sẽ ban cho họ phần thưởng tốt hơn những điều tốt mà họ đã làm.');
INSERT INTO "vn_ruwwad" VALUES(29,8,'29:8','TA đã sắc lệnh cho con người phải ăn ở tử tế với cha mẹ. Nhưng nếu cha mẹ đấu tranh với ngươi để buộc ngươi phạm Shirk với TA bởi những thứ mà ngươi không hề biết một tí gì thì ngươi chớ nghe theo lời của hai người họ. Rồi đây, các ngươi sẽ trở về trình diện TA để TA cho các ngươi biết về những gì các ngươi đã từng làm.');
INSERT INTO "vn_ruwwad" VALUES(29,9,'29:9','Những ai có đức tin và hành thiện, TA chắc chắn sẽ nhập họ vào cùng với đoàn những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(29,10,'29:10','Trong nhân loại có những kẻ tuyên bố “Chúng tôi đã có đức tin nơi Allah” nhưng khi bị áp bức vì Allah thì họ lại xem việc bị thiên hạ áp bức như một sự trừng phạt của Allah. Nhưng nếu có sự trợ giúp từ nơi Thượng Đế của Ngươi (Thiên Sứ Muhammad) thì họ lại bảo: “Quả thật chúng tôi đã luôn luôn sát cánh với các người.” Chẳng lẽ Allah lại không biết những điều nằm trong lòng của thiên hạ ư?');
INSERT INTO "vn_ruwwad" VALUES(29,11,'29:11','Allah chắc chắn biết rõ ai là những người đã có đức tin và Ngài chắc chắn biết rõ ai là những kẻ giả tạo đức tin.');
INSERT INTO "vn_ruwwad" VALUES(29,12,'29:12','Những kẻ vô đức tin nói với những người có đức tin rằng: “Các người hãy đi theo con đường của chúng tôi và chúng tôi sẽ gánh chịu mọi tội lỗi của các người.” Nhưng bọn họ sẽ không gánh bất cứ tội lỗi nào cho ai bởi bọn họ thực chất là những kẻ nói dối.');
INSERT INTO "vn_ruwwad" VALUES(29,13,'29:13','Bọn họ chắc chắn sẽ vác gánh nặng của mình và cả những gánh nặng ngoài gánh nặng của (những ai theo) bọn họ. Bọn họ chắc chắn sẽ bị tra hỏi vào Ngày Phán Xét về những điều mà bọn họ đã bịa đặt.');
INSERT INTO "vn_ruwwad" VALUES(29,14,'29:14','Quả thật, TA đã cử Nuh đến với người dân của Y. (Nuh) đã sống với họ thiếu năm mươi năm nữa là đủ một ngàn năm. Rồi, trận Đại Hồng Thủy đã túm bắt lấy họ khi họ là những kẻ làm điều sai quấy bất công.');
INSERT INTO "vn_ruwwad" VALUES(29,15,'29:15','TA đã giải cứu (Nuh) và những tín đồ theo Y trên một chiếc tàu và lấy đó làm một bài học cho thế gian.');
INSERT INTO "vn_ruwwad" VALUES(29,16,'29:16','(Hỡi Thiên Sứ Muhammad, Ngươi hãy nhớ lại) khi (Ibrahim) bảo người dân của mình “Các người hãy thờ phượng Allah và hãy sợ Ngài. Điều đó tốt nhất cho các người nếu các người biết được.”');
INSERT INTO "vn_ruwwad" VALUES(29,17,'29:17','“Thật ra các người chỉ thờ phượng những bục tượng thay vì Allah, các người thật ra chỉ bịa chuyện hoang đường mà thôi. Những thần linh mà các người thờ phượng thực chất không có khả năng ban bổng lộc cho các người. Bởi thế, các người hãy tìm bổng lộc nơi Allah, các người hãy thờ phượng Ngài, hãy tạ ơn Ngài, bởi rồi đây các người sẽ được đưa trở về trình diện Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(29,18,'29:18','“Nếu các người phủ nhận thì quả thật những cộng đồng trước các người cũng đã phủ nhận như thế; và nhiệm vụ của một Sứ Giả chỉ là truyền đạt một cách rõ ràng mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(29,19,'29:19','Lẽ nào (những kẻ phủ nhận này) đã không nhận xét thấy việc Allah khởi tạo rồi phục sinh như thế nào ư? Quả thật, điều đó đối với Allah vô cùng đơn giản.');
INSERT INTO "vn_ruwwad" VALUES(29,20,'29:20','Ngươi (Thiên Sứ Muhammad) hãy nói (với những kẻ phủ nhận sự phục sinh): “Các ngươi hãy đi khắp mọi nơi trên trái đất để quan sát việc Allah khởi tạo vạn vật rồi Ngài phục sinh Đời Sau như thế nào. Quả thật, Allah là Đấng Toàn Năng trên tất cả mọi thứ.”');
INSERT INTO "vn_ruwwad" VALUES(29,21,'29:21','Ngài trừng phạt ai Ngài muốn và khoan dung đối với ai Ngài muốn. Rồi đây các ngươi sẽ được đưa về trình điện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(29,22,'29:22','Các ngươi chắc chắn sẽ không thể trốn thoát cho dù các ngươi ở dưới đất hay ở trên trời. Các ngươi chắc chắn sẽ không tìm được người bảo hộ hay vị cứu tinh nào ngoài Allah.');
INSERT INTO "vn_ruwwad" VALUES(29,23,'29:23','Những kẻ phủ nhận những lời mặc khải của Allah cũng như phủ nhận việc trở lại trình diện Ngài là những kẻ đã tuyệt vọng nơi lòng khoan dung của TA. Họ chắc chắn sẽ đối mặt với một sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(29,24,'29:24','Đám dân của (Ibrahim) không trả lời điều nào khác ngoài điều bọn họ nói: “Hãy giết chết nó hoặc thiêu sống nó!” Nhưng Allah đã cứu sống (Ibrahim) khỏi hố lửa. Quả thật, trong sự việc đó là những bài học cho đám người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(29,25,'29:25','(Ibrahim) bảo (người dân của mình): “Các người đã thờ cúng những bục tượng thay vì Allah chẳng qua là vì các người yêu thương lẫn nhau ở đời này; rồi vào Ngày Phán Xét, các người sẽ phủ nhận và nguyền rủa lẫn nhau, và chốn ở cuối cùng của các người sẽ là Hỏa Ngục và các người sẽ không có ai giúp đỡ.”');
INSERT INTO "vn_ruwwad" VALUES(29,26,'29:26','Chỉ có Lut đã tin nơi (Ibrahim) và Y nói: “Ta sẽ dời cư đến với Thượng Đế của Ta, bởi quả thật, Ngài là Đấng Quyền Năng, Sáng Suốt.”');
INSERT INTO "vn_ruwwad" VALUES(29,27,'29:27','TA đã ban cho (Ibrahim) (đứa con trai) Is-haq và (đứa cháu nội) Ya’qub và chọn trong số con cháu của Y tiếp thu lời mặc khải và Kinh Sách, và TA đã ban cho Y phần thưởng ở trần gian; và vào Đời Sau, Y chắc chắn sẽ ở cùng với những người đức hạnh.');
INSERT INTO "vn_ruwwad" VALUES(29,28,'29:28','Và Lut, khi Y bảo người dân của mình: “Các người đã làm một việc làm ô uế nhất, thứ mà không một ai trong thiên hạ đã từng làm trước các người.”');
INSERT INTO "vn_ruwwad" VALUES(29,29,'29:29','“Sao các người lại giao hợp với đàn ông, lại chuyên đánh cướp những người đi đường và làm những điều đáng khinh bỉ trong những cuộc hội họp của các người?” Tuy nhiên, đám dân của (Lut) không trả lời mà lên tiếng thách thức: “Nếu ngươi nói thật thì ngươi hãy mang hình phạt của Allah đến (trừng phạt) bọn ta xem nào.”');
INSERT INTO "vn_ruwwad" VALUES(29,30,'29:30','(Lut) cầu nguyện (Allah): “Lạy Thượng Đế của bề tôi! Xin Ngài phù hộ bề tôi thắng đám người thối nát này.”');
INSERT INTO "vn_ruwwad" VALUES(29,31,'29:31','Khi các Sứ Giả của TA (các Thiên Thần) đến gặp Ibrahim với tin mừng, họ nói với (Ibrahim): “Chúng tôi được lệnh tiêu diệt dân cư của thị trấn này bởi dân cư của nó thực sự là những kẻ làm điều sai quấy bất công.”');
INSERT INTO "vn_ruwwad" VALUES(29,32,'29:32','Ibrahim nói (với các Thiên Thần sứ giả): “Nhưng Lut đang ở trong đó.” (Các Thiên Thần) bảo: “Bọn ta biết rõ ai ở trong đó. Chắc chắn, bọn ta sẽ giải cứu (Lut) và gia đình của Y ngoại trừ người vợ của Y, nữ ta sẽ là một trong những kẻ bị tiêu diệt.”');
INSERT INTO "vn_ruwwad" VALUES(29,33,'29:33','Rồi khi các Thiên Thần Sứ Giả của TA đến gặp Lut khiến Y lo âu cho Họ trước dân làng và Lut cảm thấy bất lực. (Các Thiên Thần Sứ Giả) bảo (Lut): “Ngươi đừng lo sợ và đừng buồn phiền. Bọn ta chắc chắn sẽ giải cứu Ngươi và gia đình của Ngươi ngoại trừ vợ của Ngươi, nữ ta sẽ là một trong số những kẻ bị tiêu diệt.”');
INSERT INTO "vn_ruwwad" VALUES(29,34,'29:34','“Bọn ta chắc chắn sẽ giáng một hình phạt từ trên trời xuống trừng phạt dân cư của thị trấn này bởi họ là những kẻ dấy loạn hư đốn.”');
INSERT INTO "vn_ruwwad" VALUES(29,35,'29:35','Quả thật, TA (Allah) đã để lại một chứng tích rõ rệt của việc trừng phạt đó cho đám người biết suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(29,36,'29:36','Đối với người dân Madyan, TA đã cử người anh em của họ, Shu’aib, đến với họ. Shu’aib bảo: “Hỡi dân ta! Các người hãy thờ phượng Allah và hãy sợ Ngày Cuối Cùng; Các người chớ đừng làm điều thối nát trên trái đất.”');
INSERT INTO "vn_ruwwad" VALUES(29,37,'29:37','Nhưng (đám dân của Shu’aib) đã phủ nhận Y. Bởi thế, một trận động đất đã túm bắt lấy họ, họ chết nằm la liệt ngay trong ngôi nhà của mình.');
INSERT INTO "vn_ruwwad" VALUES(29,38,'29:38','Người dân ‘Ad và Thamud cũng thế. Nhà cửa đổ nát của họ hãy còn là một chứng tích rõ ràng cho các ngươi về họ. Shaytan đã tô điểm cho việc làm của họ trở nên hấp dẫn đối với họ để nó ngăn cản họ đi xa khỏi con đường ngay chính trong khi họ là những người có cái nhìn thấu suốt (vì đã được các Thiên Sứ của họ hướng dẫn và chỉ dạy).');
INSERT INTO "vn_ruwwad" VALUES(29,39,'29:39','Qarun, Pha-ra-ông và Haman cũng vậy. Quả thật, Musa đã đến gặp bọn họ với những bằng chứng rõ rệt, nhưng bọn họ đã tỏ ra ngạo mạn trên trái đất nên bọn họ không thoát khỏi (sự trừng phạt của Allah).');
INSERT INTO "vn_ruwwad" VALUES(29,40,'29:40','TA (Allah) đã bắt phạt từng đứa một theo tội của riêng họ. Trong bọn họ, có kẻ đã bị TA bắt phạt bằng một trận mưa đá (như đối với người dân của Lut); có kẻ đã bị TA bắt phạt bằng một tiếng thét (như đối với người dân Thamud); có kẻ đã bị TA bắt phạt bằng cách làm cho đất sụp xuống (như Qarun); và có kẻ đã bị TA bắt phạt bằng cách nhấn chìm dưới nước (như Pha-ra-ông và Haman). Tuy nhiên, Allah không phải là Đấng đã bất công với họ mà chính bọn đã tự bất công với bản thân mình (bởi việc làm tỗi lỗi và thối nát của bọn họ).');
INSERT INTO "vn_ruwwad" VALUES(29,41,'29:41','Hình ảnh của những kẻ nhận lấy các vị bảo hộ khác ngoài Allah giống như hình ảnh của một con nhện giăng tơ làm nhà. Quả thật, ngôi nhà yếu ớt nhất chính là ngôi nhà của loài nhện, nếu như bọn họ biết rõ điều đó.');
INSERT INTO "vn_ruwwad" VALUES(29,42,'29:42','Quả thật, Allah biết rõ về những gì bọn họ cầu nguyện ngoài Ngài, bởi Ngài là Đấng Quyền Năng, Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(29,43,'29:43','Đó là những hình ảnh thí dụ mà TA (Allah) trình bày cho nhân loại. Tuy nhiên, chỉ những ai có kiến thức mới thấu hiểu được (ý nghĩa của những hình ảnh thí dụ đó).');
INSERT INTO "vn_ruwwad" VALUES(29,44,'29:44','Allah đã tạo hóa các tầng trời và trái đất là vì chân lý. Quả thật, trong (sự tạo hóa) đó là một minh chứng (rõ ràng về Allah) cho những ai có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(29,45,'29:45','Ngươi (hỡi Thiên Sứ Muhammad) hãy xướng đọc những điều đã được mặc khải cho Ngươi trong Kinh Sách (Qur’an) và Ngươi hãy chu đáo dâng lễ nguyện Salah. Quả thật, lễ nguyện Salah sẽ ngăn cản mọi điều ô uế và tội lỗi. Và việc tưởng nhớ Allah là điều lớn lao nhất, và Allah biết rõ mọi điều các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(29,46,'29:46','Các ngươi (hỡi những người có đức tin) chớ đừng tranh luận với người dân Kinh Sách ngoại trừ bằng thái độ nhã nhặn (và lời lẽ tử tế) và chỉ đối với những ai làm điều sai quấy bất công trong số họ. Các ngươi hãy nói với (người dân Kinh Sách): “Chúng tôi đã có đức tin nơi những điều đã được mặc khải cho chúng tôi và những điều đã được mặc khải cho các người, và Thượng Đế của chúng tôi lẫn Thượng Đế của các ngươi chỉ là Một Đấng duy nhất, tất cả chúng tôi đều thần phục Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(29,47,'29:47','Như thế đó, TA (Allah) đã ban Kinh Sách (Qur’an) xuống cho Ngươi (Thiên Sứ Muhammad). Cho nên, những ai được TA ban cho Kinh Sách đều có đức tin nơi Nó và một số người này cũng đã có đức tin nơi Nó và không một ai chống đối lại các Lời Mặc Khải của TA ngoại trừ đám người vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(29,48,'29:48','Trước khi Qur’an được ban xuống, Ngươi đã chưa từng đọc qua bất kỳ một Kinh Sách nào và Ngươi cũng chưa từng viết bất kỳ quyển kinh nào từ bàn tay phải của Ngươi. (Nếu Ngươi đã từng đọc và đã từng viết) thì chắc chắn những kẻ thiếu hiểu biết trong thiên hạ sẽ đâm ra hoài nghi (về Qur’an).');
INSERT INTO "vn_ruwwad" VALUES(29,49,'29:49','Không! (Kinh Qur’an) gồm những lời mặc khải minh bạch ở trong lòng của những ai đã được ban cho kiến thức. Không ai chống đối lại các lời mặc khải của TA ngoại trừ những kẻ sai quấy bất công.');
INSERT INTO "vn_ruwwad" VALUES(29,50,'29:50','(Những kẻ thờ đa thần) nói: “Tại sao Thượng Đế của Y (Muhammad) không ban cho Y những dấu lạ nào cơ chứ?!” Ngươi (Thiên Sứ Muhammad) hãy nói (với họ): “Quả thật, các dấu lạ đó chỉ ở nơi Allah, còn Ta chỉ là một người cảnh báo rõ ràng mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(29,51,'29:51','Lẽ nào việc TA đã ban Kinh Sách (Qur’an) xuống cho Ngươi để Ngươi xướng đọc cho họ không đủ cho họ hay sao? Quả thật, trong (Qur’an) là Hồng Ân và điều nhắc nhở cho đám người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(29,52,'29:52','Ngươi hãy nói (với họ): “Allah đủ làm chứng giữa Ta và các người. Ngài biết rõ mọi điều trong các tầng trời và trái đất. Những ai có đức tin vào điều ngụy tạo và vô đức tin nơi Allah thì là những kẻ thất bại.”');
INSERT INTO "vn_ruwwad" VALUES(29,53,'29:53','(Những kẻ thờ đa thần) hối thúc Ngươi (Thiên Sứ Muhammad) mang hình phạt đến cho họ. (Nhưng họ không biết rằng) nếu không vì một thời hạn nhất định đã được ấn định trước cho họ thì hình phạt chắc chắn đã túm lấy họ mất rồi. Chắc chắn (hình phạt) sẽ đến với họ một cách bất ngờ trong lúc họ không hề hay biết.');
INSERT INTO "vn_ruwwad" VALUES(29,54,'29:54','Họ hối thúc Ngươi mang hình phạt đến nhưng chắc chắn Hỏa Ngục sẽ bao vây những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(29,55,'29:55','Vào Ngày mà hình phạt sẽ bao vây họ từ phía bên trên họ lẫn phía dưới chân của họ, và (Allah) phán: “Các ngươi hãy nếm lấy (hậu quả về) những gì các ngươi đã làm.”');
INSERT INTO "vn_ruwwad" VALUES(29,56,'29:56','Hỡi các bầy tôi có đức tin của TA, quả thật đất đai của TA rộng thênh thang, cho nên, các ngươi hãy thờ phượng chỉ một mình TA thôi!');
INSERT INTO "vn_ruwwad" VALUES(29,57,'29:57','Tất cả mỗi cơ thể đều phải nếm cái chết. Rồi các ngươi sẽ được đưa trở về trình diện TA.');
INSERT INTO "vn_ruwwad" VALUES(29,58,'29:58','Những người có đức tin và hành thiện chắc chắn sẽ được TA ban thưởng cho Thiên Đàng với những tòa nhà cao, bên dưới có các dòng sông chảy. Họ sẽ sống mãi mãi trong đó. Ân phúc thay phần thưởng dành cho những người làm việc (thiện tốt).');
INSERT INTO "vn_ruwwad" VALUES(29,59,'29:59','Họ là những người đã kiên nhẫn chịu đựng và phó thác cho Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(29,60,'29:60','Có biết bao sinh vật đã không thể dự trữ bổng lộc của mình cho ngày mai. Chính Allah sẽ cấp dưỡng chúng và luôn cả các ngươi bởi vì Ngài là Đấng Hằng Nghe, Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(29,61,'29:61','Nếu Ngươi (Thiên Sứ Muhammad) hỏi (những kẻ thờ đa thần) ai đã tạo hóa các tầng trời và trái đất cũng như ai đã chế ngự mặt trời, mặt trăng thì chắc chắn họ sẽ đáp: “Allah!” Thế tại sao họ vẫn không chịu tin?!');
INSERT INTO "vn_ruwwad" VALUES(29,62,'29:62','Allah ban bổng lộc nhiều hay ít cho bất cứ ai Ngài muốn trong số bầy tôi của Ngài, quả thật, Allah là Đấng hằng biết hết mọi việc.');
INSERT INTO "vn_ruwwad" VALUES(29,63,'29:63','Nếu Ngươi hỏi họ ai đã ban nước mưa từ trên trời xuống dùng để làm sống lại lớp đất đã chết khô thì chắc chắn họ sẽ trả lời: “Allah!” Ngươi hãy nói: “Alhamdulillah”. Tuy nhiên, đa số bọn họ không suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(29,64,'29:64','Cuộc sống trần gian này chỉ là trò đùa và thú tiêu khiển còn cõi Đời Sau mới là nơi của cuộc sống đích thực, nếu họ biết điều đó.');
INSERT INTO "vn_ruwwad" VALUES(29,65,'29:65','Khi họ lên tàu ra khơi, họ khấn vái Allah một cách thành khẩn, nhưng khi Ngài cứu họ vào bờ an toàn thì họ tiếp tục làm điều Shirk (tổ hợp thần linh khác) với Ngài.');
INSERT INTO "vn_ruwwad" VALUES(29,66,'29:66','Họ làm thế để có thể phủ nhận những ân huệ mà TA đã ban cho họ và để họ tự do hưởng thụ. Nhưng rồi đây họ sẽ sớm biết (hậu quả khi cái chết đến với họ).');
INSERT INTO "vn_ruwwad" VALUES(29,67,'29:67','Lẽ nào họ không thấy việc TA đã làm cho Makkah thành một Thánh Địa an toàn bất khả xâm phạm trong lúc thiên hạ xung quanh Nó không ngừng xảy ra cảnh giết chóc? Lẽ nào với điều gian dối thì họ lại tin còn đối với ân huệ của Allah thì họ lại phủ nhận?');
INSERT INTO "vn_ruwwad" VALUES(29,68,'29:68','Thực sự còn ai sai quấy hơn kẻ đã đặt điều nói dối cho Allah hoặc còn ai sai quấy bất công hơn kẻ đã phủ nhận chân lý khi nó đến với y? Chắc chắn Hỏa Ngục sẽ là nơi cư ngụ cho đám người vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(29,69,'29:69','Và những ai anh dũng chiến đấu vì chính nghĩa của TA, chắc chắn sẽ được TA hướng dẫn đến những con đường của TA. Và chắc chắn, Allah luôn bên cạnh những người làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(30,1,'30:1','Alif. La-m. Mi-m.');
INSERT INTO "vn_ruwwad" VALUES(30,2,'30:2','Người La Mã đã bị đánh bại,');
INSERT INTO "vn_ruwwad" VALUES(30,3,'30:3','Tại một lãnh thổ lân cận (. Tuy nhiên, sau cuộc chiến bại đó, họ sẽ giành lại chiến thắng,');
INSERT INTO "vn_ruwwad" VALUES(30,4,'30:4','Trong vài năm sau. Mọi việc xảy ra trước và sau đều là lệnh của Allah. Và vào ngày đó (ngày mà người La Mã giành chiến thắng trở lại) những người có đức tin sẽ vui mừng,');
INSERT INTO "vn_ruwwad" VALUES(30,5,'30:5','Về sự phù hộ của Allah. Quả thật, Ngài ban chiến thắng cho người nào Ngài muốn bởi Ngài là Đấng Quyền Năng, Đấng Nhân Từ.(');
INSERT INTO "vn_ruwwad" VALUES(30,6,'30:6','(Sự chiến thắng đó là từ) Lời Hứa của Allah, Ngài không hề làm trái Lời Hứa của Ngài, tuy nhiên, đa số nhân loại không biết.');
INSERT INTO "vn_ruwwad" VALUES(30,7,'30:7','Họ chỉ biết những gì được nhìn thấy từ đời sống trần tục này còn đối với cuộc sống Đời Sau thì họ lại lơ là, không quan tâm.');
INSERT INTO "vn_ruwwad" VALUES(30,8,'30:8','Sao họ không chịu suy ngẫm về bản thân họ (và những tạo vật khác ngoài họ)? Quả thật, Allah đã không tạo hóa các tầng trời, trái đất và vạn vật giữa chúng ngoại trừ vì Chân Lý và (Ngài qui định cho chúng) một thời hạn nhất định. Và quả thật, đa số nhân loại đã phủ nhận việc gặp gỡ với Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(30,9,'30:9','Sao họ không chịu đi chu du khắp trái đất để nhìn xem hậu quả của những ai sống trước họ như thế nào? Đám người đó mạnh hơn họ về sức lực, về khả năng khai khẩn đất đai, về tuổi thọ, về số lượng và sự giàu có; và những Sứ Giả đã được cử phái đến với đám người đó mang theo những bằng chứng rõ rệt. Do đó, Allah đã không hề làm hại họ mà chính họ đã tự làm hại bản thân mình.');
INSERT INTO "vn_ruwwad" VALUES(30,10,'30:10','Rồi những kẻ làm điều xấu sẽ gặp phải hậu quả xấu bởi vì họ đã phủ nhận các Lời Mặc Khải của Allah và đã mang chúng ra giễu cợt.');
INSERT INTO "vn_ruwwad" VALUES(30,11,'30:11','Allah là Đấng bắt đầu sự tạo hóa, rồi Ngài sẽ tái tạo nó trở lại, và rồi đây các ngươi sẽ được đưa trở về trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(30,12,'30:12','Và vào Ngày mà Giờ Tận Thế sẽ được thiết lập, những kẻ tội lỗi sẽ tuyệt vọng.');
INSERT INTO "vn_ruwwad" VALUES(30,13,'30:13','Họ không được những thần linh mà họ từng thờ phượng đứng ra can thiệp, ngược lại, các thần linh đó còn phủ nhận sự thờ phượng của họ.');
INSERT INTO "vn_ruwwad" VALUES(30,14,'30:14','Vào Ngày mà thời khắc Tận Thế được dựng lên, đó là Ngày mà nhân loại sẽ được tách biệt (giữa những người có đức tin và những kẻ vô đức tin).');
INSERT INTO "vn_ruwwad" VALUES(30,15,'30:15','Đối với những ai có đức tin và hành thiện thì sẽ được sống (sung sướng, hưởng thụ niềm hạnh phúc bất tận) nơi Thiên Đàng.');
INSERT INTO "vn_ruwwad" VALUES(30,16,'30:16','Còn đối với những kẻ vô đức tin và phủ nhận các Lời Mặc Khải của TA, phủ nhận cuộc gặp gỡ với TA ở Đời Sau, thì sẽ là những kẻ ở trong sự trừng phạt muôn đời.');
INSERT INTO "vn_ruwwad" VALUES(30,17,'30:17','Do đó, các ngươi hãy tán dương Allah khi các ngươi bước vào buổi chiều và lúc bình minh ló dạng.');
INSERT INTO "vn_ruwwad" VALUES(30,18,'30:18','Mọi lời ca tụng trong các tầng trời và trái đất đều thuộc về Ngài, (cho nên, các ngươi hãy tán dương và ca tụng Ngài) lúc xế chiều (vào lễ nguyện Salah ‘Asr) và vào lúc ban trưa (lễ nguyện Salah Zhuhr).');
INSERT INTO "vn_ruwwad" VALUES(30,19,'30:19','Ngài mang sự sống ra từ cái chết và Ngài mang cái chết ra từ sự sống; Ngài hồi sinh lại mảnh đất đã chết khô và các ngươi sẽ được phục sinh tương tự như thế.');
INSERT INTO "vn_ruwwad" VALUES(30,20,'30:20','Và trong các dấu hiệu của (Allah) là Ngài đã tạo các ngươi từ đất bụi, rồi sau đó các ngươi trở thành những người phàm sống rải rác khắp nơi.');
INSERT INTO "vn_ruwwad" VALUES(30,21,'30:21','Và trong các dấu hiệu của (Allah) là Ngài đã tạo cho các ngươi từ bản thân các ngươi những người vợ để các ngươi chung sống yên bình với họ và Ngài đã đặt giữa các ngươi tình yêu thương và lòng bao dung. Trong sự việc đó quả thật là những dấu hiệu dành cho đám người biết suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(30,22,'30:22','Và trong các dấu hiệu của (Allah) là việc tạo hóa các tầng trời, trái đất cũng như sự khác biệt ngôn ngữ và màu da của các ngươi. Trong sự việc đó quả thật là các dấu hiệu cho những người hiểu biết.');
INSERT INTO "vn_ruwwad" VALUES(30,23,'30:23','Và trong các dấu hiệu của (Allah) là giấc ngủ của các ngươi vào ban đêm cũng như ban ngày và việc các ngươi tìm kiếm thiên ân của Ngài. Trong sự việc đó quả thật là các dấu hiệu cho đám người biết lắng nghe.');
INSERT INTO "vn_ruwwad" VALUES(30,24,'30:24','Và trong các dấu hiệu của (Allah) là Ngài làm cho các ngươi thấy tia chớp (để các ngươi) vừa lo sợ (sấm sét) vừa mong muốn (được mưa); và Ngài ban nước mưa từ trên trời xuống làm sống lại mảnh đất đã chết khô. Trong sự việc đó quả thật là những dấu hiệu cho đám người thông hiểu.');
INSERT INTO "vn_ruwwad" VALUES(30,25,'30:25','Và trong các dấu hiệu của (Allah) là bầu trời và trái đất đứng vững theo lệnh của Ngài; rồi khi Ngài gọi các ngươi bằng một tiếng Gọi duy nhất (vào Ngày Phục Sinh) thì các ngươi sẽ từ trong đất bước ra (để chịu sự xét xử và thưởng phạt của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(30,26,'30:26','Tất cả những ai trong các tầng trời và trái đất đều là tạo vật của (Allah). Tất cả đều qui phục (mệnh lệnh của) Ngài.');
INSERT INTO "vn_ruwwad" VALUES(30,27,'30:27','(Allah) là Đấng bắt đầu việc tạo hóa rồi Ngài sẽ tái lập nó và điều đó đối với Ngài rất đơn giản, và Ngài tối cao bên trên tất cả mọi thứ trong các tầng trời và trái đất. Quả thật, Ngài là Đấng Quyền Năng, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(30,28,'30:28','(Allah) đưa ra hình ảnh thí dụ từ bản thân các ngươi (những kẻ thờ đa thần). Lẽ nào các ngươi hài lòng cho những kẻ nô lệ của các ngươi cùng san sẻ với các ngươi tài sản mà TA đã ban cấp cho các ngươi? Các ngươi sợ sự ngang bằng đó giống như các ngươi sợ sự ngang bằng giữa các ngươi (những kẻ làm chủ) với nhau ư. Như thế đấy, TA giải thích rõ các lời mặc khải (của TA) cho đám người thông hiểu.');
INSERT INTO "vn_ruwwad" VALUES(30,29,'30:29','Không, những kẻ làm điều sai quấy đã chạy theo những dục vọng của bản thân một cách không hiểu biết. Vậy, ai sẽ hướng dẫn kẻ đã bị Allah làm cho lầm lạc?! Chắc chắn (những kẻ lầm lạc đó) sẽ không có ai giúp đỡ.');
INSERT INTO "vn_ruwwad" VALUES(30,30,'30:30','Bởi thế, Ngươi (hỡi Thiên Sứ) hãy hướng mặt mình về tôn giáo thuần túy (chỉ tôn thờ một mình Allah), Fitrah (tôn giáo tự nhiên)( của Allah, tôn giáo mà theo đó Ngài đã tạo ra loài người, không có bất cứ sự thay đổi nào trong tạo hóa của Allah. Đó là chính đạo nhưng đa số nhân loại không biết.');
INSERT INTO "vn_ruwwad" VALUES(30,31,'30:31','Các ngươi hãy quay về sám hối với (Allah), các ngươi hãy kính sợ Ngài, hãy chu đáo duy trì lễ nguyện Salah và chớ đừng trở thành những kẻ thờ đa thần.');
INSERT INTO "vn_ruwwad" VALUES(30,32,'30:32','(Và các ngươi chớ đừng trở thành) những kẻ đã thay đổi tôn giáo và chia rẽ thành những nhánh phái; mỗi nhánh phái vui vầy với điều (sai trái và ngụy tạo) của mình.');
INSERT INTO "vn_ruwwad" VALUES(30,33,'30:33','Con người khi gặp hoạn nạn thì cầu nguyện Thượng Đế của họ, họ thực sự quay về ăn năn sám hối với Ngài. Tuy nhiên, khi Thượng Đế của họ cho họ nếm mùi khoan dung từ nơi Ngài thì một nhóm trong số họ lại tổ hợp những thần linh khác cùng với Ngài.');
INSERT INTO "vn_ruwwad" VALUES(30,34,'30:34','(Họ làm như thế) là để phủ nhận những Ân huệ mà TA đã ban cho họ. Thế nên, các ngươi cứ hãy hưởng thụ (những thứ tạm bợ của cuộc sống trần tục này), rồi đây (vào Ngày Phán Xét) các ngươi sẽ sớm biết.');
INSERT INTO "vn_ruwwad" VALUES(30,35,'30:35','Hoặc lẽ nào TA (Allah) đã ban xuống một thẩm quyền nào đó cho họ để rồi họ (có thể mạnh dạn) nói về việc tổ hợp những đối tác ngang vai cùng với Ngài?');
INSERT INTO "vn_ruwwad" VALUES(30,36,'30:36','Con người, khi TA (Allah) cho họ nếm lấy sự nhân từ (của TA) thì họ mừng hớn hở, còn khi họ gặp điều xấu do chính bàn tay của họ đã gây ra trước kia thì họ lại tuyệt vọng.');
INSERT INTO "vn_ruwwad" VALUES(30,37,'30:37','Lẽ nào họ không biết việc Allah muốn nới rộng hay thu hẹp bổng lộc của Ngài đối với ai là tùy ý Ngài hay sao? Quả thật, trong sự việc đó là các dấu hiệu cho đám người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(30,38,'30:38','Bởi thế, ngươi (hỡi người Muslim) hãy trao cho bà con ruột thịt, người khó khăn và kẻ lỡ đường quyền lợi của họ. Đó là điều tốt đẹp cho những ai muốn tìm sự hài lòng của Allah; và những người đó là những người thành công.');
INSERT INTO "vn_ruwwad" VALUES(30,39,'30:39','Những gì các ngươi đưa cho (ai đó) dưới hình thức Riba (cho vay lấy lãi) với mục đích gia tăng phần lộc (của mình) trong tài sản của thiên hạ thì chắc chắn các ngươi sẽ chẳng được điều gì ở nơi Allah. Ngược lại, những gì các ngươi đưa cho (ai đó) dưới hình thức Zakah chỉ mong muốn làm hài lòng Allah (chứ không mong gì khác từ thiên hạ) thì các ngươi chính là những người sẽ được Ngài nhân thêm phần ân thưởng.');
INSERT INTO "vn_ruwwad" VALUES(30,40,'30:40','Allah là Đấng đã tạo hóa ra các ngươi, rồi Ngài cung dưỡng các ngươi, rồi Ngài làm cho các ngươi chết, sau đó Ngài làm cho các ngươi sống trở lại. Vậy, trong số những thần linh của các ngươi, ai có khả năng làm được ít nhất một điều trong những điều vừa nêu? Quả thật, (Allah) trong sạch và tối cao vượt trên tất cả mọi thứ mà (những kẻ thờ đa thần) đã tổ hợp với Ngài.');
INSERT INTO "vn_ruwwad" VALUES(30,41,'30:41','Sự hủy hoại và tai ương xảy ra khắp mọi nơi trên đất liền và ngoài biển cả đều là hậu quả do bàn tay con người. (Allah) cho họ nếm một số hậu quả mà họ đã gây ra mong rằng họ biết tỉnh ngộ mà quay đầu.');
INSERT INTO "vn_ruwwad" VALUES(30,42,'30:42','Ngươi (Thiên Sứ Muhammad) hãy nói với (những kẻ thờ đa thần): “Các ngươi hãy đi chu du khắp nơi trên trái đất để xem kết cuộc của những kẻ đã sống trước đây. Đa số bọn họ là những kẻ thờ đa thần.”');
INSERT INTO "vn_ruwwad" VALUES(30,43,'30:43','Do đó, Ngươi (Thiên Sứ Muhammad) hãy hướng mặt mình về tôn giáo ngay chính trước khi xảy ra cái Ngày không thể tránh khỏi từ nơi Allah. Vào Ngày đó, thiên hạ sẽ bị tách ra làm hai, (một nhóm sẽ ở nơi Thiên Đàng và một nhóm sẽ bị đày vào Hỏa Ngục).');
INSERT INTO "vn_ruwwad" VALUES(30,44,'30:44','Ai không có đức tin thì sẽ gánh chịu hậu quả cho sự vô đức tin đó của mình; còn ai (có đức tin) và hành thiện là tự chuẩn bị cho mình một chỗ cư ngụ tốt (nơi Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(30,45,'30:45','(Allah muốn) ban thưởng cho những người có đức tin và hành thiện từ hồng phúc và thiên ân của Ngài. Quả thật, Ngài không yêu thương đám người vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(30,46,'30:46','Và trong các dấu hiệu của (Allah) là Ngài gởi những luồng gió bay đi trước như điềm báo tin mừng, để cho các ngươi nếm mùi Nhân Từ của Ngài, để cho những chiếc tàu buồm lướt sóng theo lệnh của Ngài và để cho các ngươi tìm kiếm thiên lộc của Ngài, với hi vọng các ngươi biết tạ ơn Ngài.');
INSERT INTO "vn_ruwwad" VALUES(30,47,'30:47','Quả thật, TA (Allah) đã cử phái các Sứ Giả trước Ngươi (Thiên Sứ Muhammad) đến với người dân của Họ. Họ đã đến gặp người dân với những bằng chứng rõ rệt, (nhưng đám dân của Họ đã phủ nhận Họ) nên TA trừng phạt những kẻ tội lỗi. Tuy nhiên, TA phải có trách nhiệm giúp đỡ những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(30,48,'30:48','Allah là Đấng gởi các cơn gió bay đi lùa các đám mây di chuyển rồi trải nó rộng trên bầu trời theo ý muốn của Ngài và đập nó tan vụn ra; rồi ngươi (hỡi người quan sát) sẽ thấy nước mưa từ giữa (các đám mây đó) rơi xuống; rồi khi Ngài cho mưa rơi nhằm phải ai trong đám bề tôi của Ngài mà Ngài muốn thì họ vui mừng.');
INSERT INTO "vn_ruwwad" VALUES(30,49,'30:49','Và trước khi mưa được ban xuống cho họ thì họ đã trở nên tuyệt vọng (trong sự mong đợi nó).');
INSERT INTO "vn_ruwwad" VALUES(30,50,'30:50','Bởi thế, Ngươi (hỡi Thiên Sứ Muhammad) hãy nhìn xem chứng tích của lòng nhân từ của Allah về việc Ngài làm sống lại mảnh đất chết khô như thế nào. Đó quả thật là bằng chứng cho việc phục sinh người chết, bởi vì Ngài toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(30,51,'30:51','Nếu TA gởi một luồng gió đến, họ thấy hoa màu héo vàng thì họ vong ơn sau đó.');
INSERT INTO "vn_ruwwad" VALUES(30,52,'30:52','Ngươi (Thiên Sứ Muhammad) chắc chắn không thể làm cho người chết nghe được và cũng không thể làm cho người điếc nghe tiếng gọi khi họ đã quay lưng bỏ đi.');
INSERT INTO "vn_ruwwad" VALUES(30,53,'30:53','Ngươi cũng không thể hướng dẫn người mù ra khỏi tình trạng lầm lạc của họ. Ngươi chỉ có thể làm cho người có đức tin nơi những Lời mặc khải của TA nghe được mà thôi, bởi họ là những người Muslim (thần phục TA).');
INSERT INTO "vn_ruwwad" VALUES(30,54,'30:54','Allah là Đấng đã tạo hóa ra các ngươi (hỡi nhân loại) từ cái yếu ớt (tinh dịch), rồi sau đó từ cái yếu ớt (trẻ thơ) Ngài ban cho sức mạnh, rồi sau đó từ sức mạnh Ngài làm cho các ngươi trở lại yếu ớt và già nua. Ngài tạo hóa bất cứ thứ gì Ngài muốn và Ngài là Đấng Hằng Biết, Đấng Toàn Năng.');
INSERT INTO "vn_ruwwad" VALUES(30,55,'30:55','Vào Ngày mà Giờ Tận Thế được thiết lập, những kẻ tội lỗi sẽ thề thốt rằng họ đã ở (trong cõi mộ) chỉ một thời khắc ngắn ngủi mà thôi. Cũng như thế, họ đã ngoảnh mặt với chân lý (lúc còn trên thế gian).');
INSERT INTO "vn_ruwwad" VALUES(30,56,'30:56','Riêng những người đã được ban cho sự hiểu biết và đức tin thì sẽ nói: “Quả thật các người đã ở lại (nơi cõi mộ) cho đến Ngày Phục Sinh chiếu theo Kinh Sách của Allah. Bởi thế, đây là Ngày Phục Sinh nhưng các người không biết.”');
INSERT INTO "vn_ruwwad" VALUES(30,57,'30:57','Vào Ngày đó, mọi lý lẽ chạy tội của những kẻ tội lỗi đều không giúp ích gì được cho họ và họ cũng sẽ không được cho phép ăn ăn sám hối.');
INSERT INTO "vn_ruwwad" VALUES(30,58,'30:58','Quả thật, TA (Allah) đã trình bày cho con người trong Qur’an này đủ loại hình ảnh thí dụ; tuy nhiên, cho dù Ngươi (hỡi Thiên Sứ) có mang đến cho họ bất cứ dấu hiệu nào thì chắc chắn những kẻ vô đức tin sẽ nói: “Các ngươi thực sự chỉ là những kẻ bịa chuyện mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(30,59,'30:59','Như thế đó, Allah niêm kín trái tim của những kẻ thiếu hiểu biết.');
INSERT INTO "vn_ruwwad" VALUES(30,60,'30:60','Cho nên, Ngươi (hỡi Thiên Sứ) hãy kiên nhẫn bởi Lời Hứa của Allah chắc chắn là thật. Và Ngươi chớ để cho những kẻ không có đức tin vững chắc làm cho Ngươi ngã lòng (trở nên hấp tấp và không kiên nhẫn).');
INSERT INTO "vn_ruwwad" VALUES(31,1,'31:1','Alif. La-m. Mi-m.');
INSERT INTO "vn_ruwwad" VALUES(31,2,'31:2','Đây là những câu của Kinh Sách chí minh (Qur’an).');
INSERT INTO "vn_ruwwad" VALUES(31,3,'31:3','Là nguồn Chỉ Đạo và là một Hồng Ân dành cho những người làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(31,4,'31:4','(Đó là) những người chu đáo duy trì lễ nguyện Salah, xuất Zakah và có đức tin kiên định nơi cõi Đời Sau.');
INSERT INTO "vn_ruwwad" VALUES(31,5,'31:5','Họ là những người theo đúng nguồn chỉ đạo từ Thượng Đế của họ và họ là những người thành đạt.');
INSERT INTO "vn_ruwwad" VALUES(31,6,'31:6','Trong nhân loại, có kẻ đã mua bán lời giải trí hầu lôi kéo những người thiếu hiểu biết lạc xa khỏi con đường của Allah, họ chọn đó làm cách phỉ báng chính đạo của Ngài. Đó là những kẻ sẽ phải gánh chịu sự trừng phạt nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(31,7,'31:7','Khi những lời mặc khải của TA được đọc cho y nghe thì y quay lưng bỏ đi với dáng điệu cao ngạo làm như thể y chẳng hề nghe thấy gì, như thể hai tai của y bị điếc. Ngươi (hỡi Thiên Sứ) hãy báo cho y biết về một sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(31,8,'31:8','Những ai có đức tin và hành thiện chắc chắn sẽ được hưởng Thiên Đàng hạnh phúc.');
INSERT INTO "vn_ruwwad" VALUES(31,9,'31:9','Họ sẽ sống trong đó mãi mãi. Lời Hứa của Allah là sự thật; và Ngài là Đấng Quyền Năng, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(31,10,'31:10','Ngài đã khởi tạo các tầng trời không cần trụ chống đỡ như các ngươi đang nhìn thấy và Ngài đã cắm lên trên mặt đất những quả núi kiên cố, sợ trái đất sẽ rung chuyển với các ngươi và Ngài đã rải khắp nơi trên trái đất vô số động vật. Và TA (Allah) đã trút nước mưa từ trên trời xuống để TA cho mọc ra trên nó vô số loại cỏ cây quí giá, từng cặp và từng cặp.');
INSERT INTO "vn_ruwwad" VALUES(31,11,'31:11','Đây là sự tạo hóa của Allah. Vậy các ngươi (hỡi những kẻ thờ đa thần) hãy cho TA xem đâu là tạo vật mà những thần linh (các ngươi thờ phượng) ngoài Ngài đã tạo? Không, những kẻ sai quấy đang lầm lạc rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(31,12,'31:12','Quả thật, TA đã ban cho Luqman sự khôn ngoan, TA phán bảo: “Ngươi hãy tạ ơn Allah, bởi ai biết ơn thì thật ra y chỉ biết ơn chính bản thân mình; còn ai vô ơn thì (sự vô ơn đó không hề ảnh hưởng đến Allah) vì quả thật Ngài là Đấng Giàu Có, Đấng Đáng Được Ca Tụng.”');
INSERT INTO "vn_ruwwad" VALUES(31,13,'31:13','Ngươi (hỡi Thiên Sứ, hãy nhớ lại) khi Luqman răn dạy con trai, bảo: “Này con yêu của cha, con chớ tổ hợp thần linh cùng với Allah trong thờ phượng, quả thật, hành động tổ hợp là sai trái nghiêm trọng.”');
INSERT INTO "vn_ruwwad" VALUES(31,14,'31:14','TA (Allah) đã truyền lệnh cho con người phải hiếu thảo với cha mẹ. Mẹ của y đã cưu mang y từ đau yếu này chồng lên đau yếu khác và cho y bú sữa và dứt sữa y trong thời gian hai năm. Do đó, (hỡi con người,) ngươi hãy tạ ơn TA và biết ơn cha mẹ của ngươi, bởi chắc chắn ngươi sẽ trở về trình diện TA.');
INSERT INTO "vn_ruwwad" VALUES(31,15,'31:15','Nếu cha mẹ của ngươi đấu tranh bắt ngươi làm điều Shirk với TA với những gì mà ngươi không biết thì ngươi chớ nghe theo hai người họ, tuy nhiên, ngươi vẫn phải ăn ở tử tế với họ ở đời này. Ngươi hãy đi theo đường lối của những ai quay về với TA. Rồi đây, tất cả các ngươi đều quay về trình diện TA, và TA sẽ cho các ngươi biết mọi việc các ngươi đã từng làm.');
INSERT INTO "vn_ruwwad" VALUES(31,16,'31:16','(Luqman bảo con trai): “Này con yêu của cha! Cho dù (điều xấu hoặc điều tốt) có nhỏ chỉ bằng trọng lượng của một hạt nguyên tử nằm trong đá hoặc trong các tầng trời hoặc nằm dưới lòng đất đi chăng nữa thì (vào Ngày Phục Sinh) Allah cũng sẽ mang nó ra (để Ngài xét xử và thưởng phạt). Quả thật, Allah là Đấng Tinh Thông.');
INSERT INTO "vn_ruwwad" VALUES(31,17,'31:17','“Này con yêu của cha! Con hãy chu đáo duy trì lễ nguyện Salah, con hãy bảo ban kêu gọi người làm điều thiện tốt và ngăn cản người làm điều xấu. Con hãy kiên nhẫn chịu đựng trước mọi điều rủi ro xảy đến cho con bởi đó là một sự kiên quyết trong mọi sự việc.”');
INSERT INTO "vn_ruwwad" VALUES(31,18,'31:18','“Con chớ vênh mặt trước mọi người và chớ bước đi với dáng vẻ kênh kiệu trên mặt đất. Quả thật, Allah không yêu thương những kẻ cao ngạo khoác lác.”');
INSERT INTO "vn_ruwwad" VALUES(31,19,'31:19','“Con hãy đi đứng một cách khiêm tốn, hãy dịu giọng trong lời ăn tiếng nói bởi âm thanh thực sự khó chịu và đáng ghét nhất chính là tiếng hí của con lừa.”');
INSERT INTO "vn_ruwwad" VALUES(31,20,'31:20','Chẳng lẽ các ngươi không nhận thấy việc Allah đã chế ngự cho các ngươi mọi vật trong các tầng trời và mọi vật dưới đất (để các ngươi sử dụng) và Ngài đã hoàn tất ân huệ của Ngài cho các ngươi từ bên trong lẫn bên ngoài ư? Tuy nhiên, trong nhân loại, có kẻ thích tranh luận về Allah một cách không có kiến thức, không có bất cứ chỉ đạo nào làm cơ sở cũng như không dựa vào bất cứ Kinh Sách soi sáng nào.');
INSERT INTO "vn_ruwwad" VALUES(31,21,'31:21','(Những kẻ tranh luận về Allah), khi có lời bảo họ rằng các ngươi hãy đi theo những điều Allah ban xuống thì họ nói: “Không, chúng tôi chỉ đi theo những gì chúng tôi nhìn thấy tổ tiên chúng tôi đi theo mà thôi.” Lẽ nào (họ đi theo) ngay cả khi Shaytan kêu gọi họ đến với sự trừng phạt nơi Hỏa Ngục ư?');
INSERT INTO "vn_ruwwad" VALUES(31,22,'31:22','Người nào thần phục Allah và là người làm tốt (trong thờ phượng Ngài và trong hành thiện vì Ngài) thì y thực sự đã nắm lấy được sợi dây cứu rỗi vững chắc. Quả thật, kết quả của mọi sự việc đều do Allah quyết định.');
INSERT INTO "vn_ruwwad" VALUES(31,23,'31:23','Người nào vô đức tin thì Ngươi (Thiên Sứ) chớ để cho sự vô đức tin của kẻ đó làm cho Ngươi buồn lòng. Rồi đây, (những kẻ vô đức tin đó) sẽ trở lại trình diện TA, và TA sẽ cho họ biết về những việc mà họ đã làm. Quả thật, Allah là Đấng biết hết mọi điều được giấu kín trong lòng.');
INSERT INTO "vn_ruwwad" VALUES(31,24,'31:24','TA để mặc (những kẻ vô đức tin đó) hưởng lạc chút ít (trong thế giới trần tục tạm bợ này), sau đó TA sẽ lôi bọn họ đến với sự trừng phạt khắc nghiệt.');
INSERT INTO "vn_ruwwad" VALUES(31,25,'31:25','Nếu Ngươi (Thiên Sứ) hỏi (những kẻ thờ đa thần này) rằng ai đã tạo các tầng trời và trái đất thì chắc chắn họ sẽ nói: “Allah”. (Lúc đó) Ngươi hãy nói: “Alhamdulillah!” Tuy nhiên, đa số bọn họ không biết.');
INSERT INTO "vn_ruwwad" VALUES(31,26,'31:26','Mọi vật trong các tầng trời và trái đất đều thuộc về Allah. Quả thật, Allah là Đấng Giàu Có, Đấng Đáng Được Ca Tụng.');
INSERT INTO "vn_ruwwad" VALUES(31,27,'31:27','Nếu tất cả cây cối trên trái đất được dùng làm bút viết và biển cả được dùng làm mực viết và thêm bảy lần biển như thế nữa thì vẫn không sao viết hết Lời Phán của Allah. Quả thật Allah là Đấng Quyền Năng, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(31,28,'31:28','Việc tạo hóa cũng như việc phục sinh các ngươi (hỡi con người) chỉ giống như việc tạo ra một cá thể duy nhất mà thôi, (vô cùng đơn giản đối với Allah). Quả thật, Allah là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "vn_ruwwad" VALUES(31,29,'31:29','Lẽ nào ngươi (hỡi con người) không nhận thấy việc Allah nhập ban đêm vào ban ngày và nhập ban ngày vào ban đêm, lẽ nào ngươi không nhận thấy việc Ngài chế ngự mặt trời và mặt trăng để mỗi vật di chuyển theo quỹ đạo riêng của nó đến một thời hạn ấn định, và lẽ nào ngươi không nhận thấy rằng Allah thông toàn tất cả mọi việc các ngươi làm?!');
INSERT INTO "vn_ruwwad" VALUES(31,30,'31:30','Đó là vì Allah chính là Chân Lý còn những kẻ (vật) mà họ van vái cầu nguyện ngoài Ngài chỉ là ngụy tạo; và quả thật Allah là Đấng Tối Cao, Đấng Vĩ Đại.');
INSERT INTO "vn_ruwwad" VALUES(31,31,'31:31','Lẽ nào ngươi (hỡi con người) không nhận thấy rằng những con tàu lướt sóng vượt trùng dương là do hồng ân của Allah để Ngài cho các ngươi thấy các dấu hiệu của Ngài? Quả thật, trong sự việc đó là các dấu hiệu cho tất cả những ai biết kiên nhẫn và tri ân.');
INSERT INTO "vn_ruwwad" VALUES(31,32,'31:32','Khi những con sóng to giống như những quả núi kéo đến bao phủ họ, họ thành tâm van vái riêng một mình Allah. Nhưng khi họ được đưa vào bờ an toàn thì trong bọn họ có kẻ trở nên lưỡng lự (giữa đức tin và vô đức tin). Quả thật, chỉ có những kẻ cao ngạo ngông cuồng mới phủ nhận những lời mặc khải của TA.');
INSERT INTO "vn_ruwwad" VALUES(31,33,'31:33','Hỡi nhân loại! Các ngươi hãy kính sợ Thượng Đế của các ngươi và hãy sợ cái Ngày mà cha sẽ không cứu được con cái và con cái cũng sẽ không cứu được cha của mình. Lời Hứa của Allah tuyệt đối là thật. Do đó, các ngươi đừng để cuộc sống thế tục này đánh lừa các ngươi và đừng để bị đánh lừa về Allah.');
INSERT INTO "vn_ruwwad" VALUES(31,34,'31:34','Quả thật, chỉ một mình Allah mới biết rõ bao giờ thời khắc tận thế sẽ xảy ra. Ngài ban mưa xuống và Ngài biết những gì nằm trong các dạ con (của các bà mẹ). Không một ai biết được điều gì y sẽ kiếm được vào ngày mai, và không một ai biết được đâu là mảnh đất mà y sẽ chết. Allah thực sự là Đấng Hằng Biết, Đấng Thông Toàn.');
INSERT INTO "vn_ruwwad" VALUES(32,1,'32:1','Alif. La-m. Mi-m.');
INSERT INTO "vn_ruwwad" VALUES(32,2,'32:2','Không có gì phải hoài nghi về Thiên Kinh (Qur’an) do Thượng Đế của vũ trụ và vạn vật ban xuống.');
INSERT INTO "vn_ruwwad" VALUES(32,3,'32:3','Họ (những kẻ vô đức tin) nói rằng Y (Muhammad) đã bịa đặt ra Nó (Qur’an). Không, (Qur’an) đích thực là Chân Lý từ nơi Thượng Đế của Ngươi (hỡi Thiên Sứ) để Ngươi cảnh báo một đám dân mà trước Ngươi đã không có một người cảnh báo nào đến với họ, mong rằng họ được hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(32,4,'32:4','Allah là Đấng đã tạo hóa các tầng trời, trái đất và vạn vật giữa chúng trong sáu ngày rồi Ngài an vị trên ‘Arsh (Ngai Vương) của Ngài. (Hỡi nhân loại), ngoài Ngài, các ngươi không có bất cứ vị bảo hộ hay vị can thiệp nào. Dó đó, các ngươi cần luôn ghi nhớ (rằng các ngươi phải thờ phượng Allah – Đấng đã tạo ra các ngươi và chớ thờ phượng ngoài Ngài một ai, vật gì).');
INSERT INTO "vn_ruwwad" VALUES(32,5,'32:5','Ngài sắp đặt mọi việc từ trên trời xuống dưới đất rồi tất cả mọi việc sẽ được đưa lên trình Ngài trong một ngày mà thời gian của nó bằng một ngàn năm theo cách tính của các ngươi (hỡi con người).');
INSERT INTO "vn_ruwwad" VALUES(32,6,'32:6','(Đấng sắp đặt mọi việc) đó là Đấng hằng biết điều vô hình và điều hữu hình, là Đấng Quyền Năng, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(32,7,'32:7','Ngài hoàn thiện tốt đẹp mọi thứ mà Ngài đã tạo, và Ngài đã khởi tạo con người (Adam) từ đất (một sự tạo hóa chưa từng có trước đó).');
INSERT INTO "vn_ruwwad" VALUES(32,8,'32:8','Rồi, Ngài tạo ra dòng dõi của (Adam) từ một loại nước (tinh dịch) đáng khinh.');
INSERT INTO "vn_ruwwad" VALUES(32,9,'32:9','Sau khi hoàn chỉnh hình thể cho (Adam), Ngài cho thổi vào cơ thể của Y linh hồn từ nơi Ngài. Ngài đã tạo ra cho các ngươi (hỡi con người) thính giác, thị giác và trái tim. (Tuy nhiên), các ngươi rất ít tri ân Ngài.');
INSERT INTO "vn_ruwwad" VALUES(32,10,'32:10','Họ (những kẻ đa thần phủ nhận sự Phục Sinh) nói: “Lẽ nào sau khi (thân xác của) chúng tôi đã phân rã dưới lòng đất, chúng tôi sẽ được tạo hóa mới trở lại ư? (Điều đó thật không hợp lý, có vẻ hoang đường)” Không, họ chỉ phủ nhận việc gặp gỡ Thượng Đế của họ mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(32,11,'32:11','Ngươi (Thiên Sứ) hãy nói với họ: “Thần Chết, vị được giao nhiệm vụ (bắt hồn) các người, sẽ rút hồn các ngươi, rồi các ngươi sẽ được đưa về trình diện Thượng Đế của các ngươi.”');
INSERT INTO "vn_ruwwad" VALUES(32,12,'32:12','Giá như Ngươi (Thiên Sứ) có thể nhìn thấy tình cảnh của những kẻ tội lỗi gục đầu trước mặt Thượng Đế của họ (và van xin Ngài vào Ngày Phán Xét): “Lạy Thượng Đế của bầy tôi! Bầy tôi đã thấy và đã nghe, xin Ngài cho bầy tôi trở lại trần gian lần nữa, bầy tôi quyết sẽ hành thiện, bầy tôi chắc chắn sẽ có đức tin kiên định.”');
INSERT INTO "vn_ruwwad" VALUES(32,13,'32:13','Nếu muốn, TA đã ban cho tất cả linh hồn sự hướng dẫn, nhưng lời phán của TA (về những kẻ tội lỗi) phải được thể hiện đúng sự thật rằng TA sẽ nhét đầy loài Jinn và loài người vào trong Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(32,14,'32:14','(Vào Ngày Phán Xét, họ được bảo): “Các ngươi hãy nếm (hình phạt) cho những gì mà các ngươi đã lãng quên về cuộc gặp gỡ ngày hôm nay, TA (Allah) quả thật cũng đã quên các ngươi giống như thế. Thôi, các ngươi hãy nếm sự trừng phạt mãi mãi bởi những điều mà các ngươi đã làm.”');
INSERT INTO "vn_ruwwad" VALUES(32,15,'32:15','Người thực sự có đức tin nơi các lời mặc khải của TA chính là những người mà khi họ được nhắc về chúng thì họ liền quỳ lạy tán dương ca ngợi Thượng Đế của họ và họ sẽ không cao ngạo.');
INSERT INTO "vn_ruwwad" VALUES(32,16,'32:16','Họ rời giường ngủ cầu nguyện Thượng Đế của họ với tâm trạng vừa lo sợ (hình phạt của Ngài) vừa ham muốn (Thiên Đàng của Ngài); và họ chi dùng bổng lộc mà TA (Allah) đã ban cấp cho họ (vào con đường chính nghĩa của TA).');
INSERT INTO "vn_ruwwad" VALUES(32,17,'32:17','Không một linh hồn nào biết được niềm vui sướng đã được giấu kín( (nơi Thiên Đàng) dùng làm phần thưởng cho họ về những việc thiện mà họ đã từng làm.');
INSERT INTO "vn_ruwwad" VALUES(32,18,'32:18','Lẽ nào người có đức tin lại giống với kẻ bất tuân?! Họ chắc chắn không ngang bằng nhau.');
INSERT INTO "vn_ruwwad" VALUES(32,19,'32:19','Đối với những ai có đức tin và hành thiện thì sẽ được ban thưởng những Ngôi Vườn Thiên Đàng làm nơi cư ngụ (vĩnh hằng), một sự đãi ngộ cho những điều (thiện tốt) mà họ đã làm (trên thế gian).');
INSERT INTO "vn_ruwwad" VALUES(32,20,'32:20','Riêng những kẻ bất tuân và phạm giới thì chỗ ở của họ sẽ là Hỏa Ngục. Mỗi khi họ muốn thoát ra khỏi đó thì sẽ bị lôi vào trong trở lại và họ được bảo: “Các ngươi hãy nếm hình phạt của Hỏa Ngục, cái mà các ngươi đã phủ nhận (trên thế gian).”');
INSERT INTO "vn_ruwwad" VALUES(32,21,'32:21','TA (Allah) chắc chắn sẽ bắt họ nếm hình phạt (trên thế gian) trước khi đến với đại hình phạt (ở cõi Đời Sau), mong rằng họ biết quay về (với Thượng Đế của họ).');
INSERT INTO "vn_ruwwad" VALUES(32,22,'32:22','Còn ai sai quấy hơn kẻ quay lưng với những lời mặc khải của Thượng Đế của y khi y được nhắc về chúng! Chắc chắn TA sẽ trừng trị những kẻ tội lỗi đó.');
INSERT INTO "vn_ruwwad" VALUES(32,23,'32:23','Quả thật, TA đã ban cho Musa Kinh Sách (Tawrah). Do đó, Ngươi (Thiên Sứ) chớ hoài nghi về cuộc gặp gỡ Y (trong chuyến dạ hành và thăng thiên). Và TA đã làm cho (Kinh Tawrah) thành một nguồn Chỉ Đạo cho dân Israel.');
INSERT INTO "vn_ruwwad" VALUES(32,24,'32:24','TA đã bổ nhiệm trong họ (người dân Israel) làm các nhà lãnh đạo hướng dẫn thiên hạ theo Mệnh Lệnh của TA khi họ vẫn biết nhẫn nại và có đức tin kiên định nơi các lời mặc khải của TA.');
INSERT INTO "vn_ruwwad" VALUES(32,25,'32:25','Quả thật vào Ngày Tận Thế, Thượng Đế của Ngươi (hỡi Thiên Sứ) sẽ xét xử giữa họ về những điều mà họ đã thường tranh chấp trong Kinh Sách.');
INSERT INTO "vn_ruwwad" VALUES(32,26,'32:26','Chẳng lẽ việc TA đã hủy diệt bao thế hệ trước họ và họ đi (chứng kiến) các chỗ ở hoang phế đó không hướng dẫn họ ư?! Quả thật trong sự việc đó là những dấu hiệu. Lẽ nào họ không nghe?!');
INSERT INTO "vn_ruwwad" VALUES(32,27,'32:27','Chẳng lẽ họ không nhận thấy việc TA dẫn nước đến vùng đất khô cằn để TA làm mọc ra hoa màu và cây cỏ dùng làm thực phẩm cho gia súc của họ và cả bản thân họ nữa hay sao? Lẽ nào họ không quan sát?!');
INSERT INTO "vn_ruwwad" VALUES(32,28,'32:28','(Những kẻ phủ nhận sự Phục Sinh hối thúc sự trừng phạt) nói: “Vậy bao giờ sự trừng phạt này sẽ đến nếu các người nói thật?”');
INSERT INTO "vn_ruwwad" VALUES(32,29,'32:29','Ngươi (Thiên Sứ) hãy nói với họ: “Vào ngày mà sự trừng phạt xảy đến, đức tin của họ sẽ không giúp ích được gì cho họ và họ cũng sẽ không được gia hạn thêm (để có cơ hội sám hối).”');
INSERT INTO "vn_ruwwad" VALUES(32,30,'32:30','Bởi thế, Ngươi (Thiên Sứ) hãy mặc kệ họ và hãy đợi (xem kết cuộc của họ) và quả thật họ cũng đang đợi (sự trừng phạt được chuẩn bị dành cho họ).');
INSERT INTO "vn_ruwwad" VALUES(33,1,'33:1','Hỡi Nabi (Muhammad)! Ngươi hãy kính sợ Allah và chớ nghe theo những kẻ vô đức tin và những tên giả tạo đức tin. Quả thật, Allah là Đấng Hằng Biết, Đấng Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(33,2,'33:2','Ngươi hãy tuân theo những gì đã được mặc khải cho Ngươi từ Thượng Đế của Ngươi. Quả thật, Allah thông toàn mọi điều các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(33,3,'33:3','Và Ngươi hãy phó thác cho Allah bởi lẽ một mình Allah đã đủ làm Đấng Bảo Hộ (cho những ai phó thác cho Ngài).');
INSERT INTO "vn_ruwwad" VALUES(33,4,'33:4','Allah không đặt hai quả tim vào trong lòng của một người đàn ông. Ngài không làm cho những người vợ mà các ngươi Zhihar ( với họ thành mẹ ruột của các ngươi. Ngài cũng không làm cho con nuôi của các ngươi thành con ruột. Đó chỉ là lời nói thốt ra từ cửa miệng của các ngươi mà thôi, còn Allah thì nói sự thật và Ngài hướng dẫn đến con đường chân chính.');
INSERT INTO "vn_ruwwad" VALUES(33,5,'33:5','Các ngươi hãy gọi (con nuôi) theo tên cha ruột của chúng, đó là cách gọi công bằng nhất đối với Allah. Nhưng nếu các ngươi không biết cha của chúng là ai thì chúng là anh em đồng đạo của các ngươi và là người bảo hộ của nhau với các ngươi trong tôn giáo. Các ngươi không có tội nếu các ngươi nhầm lẫn trong việc xưng hô này, (các ngươi chỉ bị bắt tội về) điều mà các ngươi có chủ đích trong lòng mà thôi. Quả thật, Allah là Đấng Hằng Tha Thứ, Đấng Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(33,6,'33:6','Nabi (Muhammad) phải được quí mến đối với những người có đức tin hơn cả bản thân họ, và các bà vợ của Y được xem là các bà mẹ của họ. (Theo sắc lệnh của Allah) trong Kinh Sách của Ngài thì bà con ruột thịt được ưu tiên đối với nhau (về việc hưởng gia tài thừa kế) hơn so với những người có đức tin và những người Muhajir (di cư từ Makkah đến Madinah), trừ trường hợp các ngươi làm một hành động tử tế đối với những người thân thiết nhất của các ngươi. Đó là mệnh lệnh được ghi trong Kinh Sách (Văn bản lưu trữ hay quyển Kinh Mẹ ở nơi Allah).');
INSERT INTO "vn_ruwwad" VALUES(33,7,'33:7','Ngươi (hỡi Thiên Sứ, hãy nhớ lại) khi TA (Allah) giao ước với các vị Nabi và với Ngươi, với Nuh, với Ibrahim, với Musa và với Ysa con trai của Mar-yam, TA đã giao ước bằng cuộc giao ước trịnh trọng.');
INSERT INTO "vn_ruwwad" VALUES(33,8,'33:8','Để Ngài hỏi những người chân thật (các vị Nabi, các vị Thiên Sứ) về sự trung thực của họ (trong việc rao truyền Bức Thông Điệp của Allah) và Ngài đã chuẩn bị cho những kẻ vô đức tin một sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(33,9,'33:9','Hỡi những người có đức tin, các ngươi hãy nhớ ân huệ của Allah đã ban cho các ngươi. Khi liên quân địch kéo đến tấn công các ngươi (, TA đã gởi một trận gió lạnh và đoàn thiên binh mà các ngươi không thể nhìn thấy đến giúp các ngươi chống lại quân địch. Quả thật, Allah thấy rõ những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(33,10,'33:10','Khi (quân địch) đến tấn công các ngươi từ phía trên và cả phía dưới của các ngươi, lúc đó cặp mắt của các ngươi đảo lộn còn trái tim của các ngươi thì nhảy lên đến tận cổ (vì sợ hãi) khiến các ngươi đâm ra nghĩ xấu về Allah.');
INSERT INTO "vn_ruwwad" VALUES(33,11,'33:11','Vào thời khắc đó, những người có đức tin bị thử thách tột độ và tinh thần bị dao động dữ dội.');
INSERT INTO "vn_ruwwad" VALUES(33,12,'33:12','(Vào ngày hôm đó) những tên giả tạo đức tin và những kẻ mang chứng bệnh hoài nghi bảo: “Allah và Sứ Giả của Ngài chỉ hứa hão với chúng ta mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(33,13,'33:13','Ngươi (hỡi Thiên Sứ, hãy nhớ lại) khi một nhóm trong bọn họ (những kẻ giả tạo đức tin) lên tiếng bảo (dân Madinah): “Này hỡi người dân của Yathrib! Các ngươi không thể cầm cự nổi đâu, các ngươi hãy tháo lui.” Và một nhóm khác của bọn họ đến xin phép Nabi, nói “Nhà cửa của chúng tôi bỏ ngỏ” nhưng thực chất nhà cửa của họ không bỏ ngỏ; thật ra đám người này chỉ muốn đào tẩu mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(33,14,'33:14','Nếu quân địch tấn công bọn họ từ mọi ngõ ngách của thành phố, rồi bọn họ được yêu cầu trở lại với Shirk và sự vô đức tin nơi Allah thì chắc chắn bọn họ sẽ không ngần ngại làm điều đó và bọn họ chỉ ngập ngừng trong chốc lát mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(33,15,'33:15','Trong khi trước đó bọn họ thực sự đã cam kết với Allah rằng bọn họ sẽ không quay lưng bỏ chạy; sự cam kết với Allah chắc chắn sẽ được mang ra hạch hỏi (trước Ngài vào Ngày Phán Xét).');
INSERT INTO "vn_ruwwad" VALUES(33,16,'33:16','Ngươi (hỡi Thiên Sứ) hãy nói với bọn họ: “Nếu các ngươi đã chạy thoát được cái chết hoặc đã chạy khỏi được cảnh giết chóc thì việc bỏ chạy cũng chẳng mang lợi gì cho các ngươi cả, bởi vì các ngươi chỉ hưởng thụ được trong một chốc lát mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(33,17,'33:17','Ngươi (Thiên Sứ) hãy nói với bọn họ: “Ai sẽ là kẻ có thể che chở các ngươi khỏi Allah nếu Ngài muốn điều xấu xảy đến với các ngươi hoặc (ai sẽ cản trở) nếu Ngài muốn nhân từ với các ngươi?” Và ngoài Allah, bọn họ sẽ không tìm được bất cứ vị bảo hộ cũng như bất cứ vị cứu tinh nào.');
INSERT INTO "vn_ruwwad" VALUES(33,18,'33:18','Allah chắc chắn thừa biết ai trong các ngươi đã cản trở người khác đi tham chiến và nói với những người anh em của mình: “Các người hãy đến cùng với chúng tôi!” Bọn người này chẳng đi tham chiến ngoại trừ một số rất ít.');
INSERT INTO "vn_ruwwad" VALUES(33,19,'33:19','Bọn họ đê tiện với các ngươi (những người có đức tin). Khi xảy ra cảnh hãi hùng (của chiến tranh) thì Ngươi sẽ thấy bọn họ dáo dác nhìn Ngươi, cặp mắt của bọn họ đảo quanh giống như cặp mắt của một kẻ thất thần sắp chết. Nhưng khi cơn nguy khốn đã qua thì bọn họ mắng nhiếc các ngươi (những người có đức tin) bằng lời lẽ thậm tệ, chỉ muốn hưởng lợi từ chiến lợi phẩm. Bọn họ là những kẻ vô đức tin nên Allah làm cho mọi việc làm của bọn họ trở thành vô nghĩa, và điều đó đối với Allah thật đơn gian và dễ dàng.');
INSERT INTO "vn_ruwwad" VALUES(33,20,'33:20','Bọn họ tưởng rằng liên quân (của địch) vẫn chưa rút đi. Nhưng nếu liên quân (của địch) có trở lại lần nữa thì bọn họ sẽ mong được trà trộn với những người Ả-rập du mục trong vùng sac mạc để dọ thám tình hình của các ngươi. Và cho dù bọn họ có ở cùng với các ngươi đi chăng nữa thì bọn họ cũng chỉ tham chiến cho có hình thức mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(33,21,'33:21','Quả thật các ngươi (những người có đức tin) có được ở nơi vị Thiên Sứ của Allah một tấm gương tốt đẹp. (Một tấm gương tốt đẹp đó dành) cho những ai hy vọng (điều tốt đẹp nơi) Allah và Đời Sau và cho những ai tưởng nhớ Allah thật nhiều.');
INSERT INTO "vn_ruwwad" VALUES(33,22,'33:22','Khi những người có đức tin nhìn thấy liên quân (của địch) thì họ nói: “Đây là điều mà Allah và Thiên Sứ của Ngài đã hứa với chúng ta, Allah và Thiên Sứ của Ngài đã nói thật.” Và (việc đối mặt với liên quân của địch) chỉ làm tăng thêm đức tin và sự thần phục của họ (đối với Allah).');
INSERT INTO "vn_ruwwad" VALUES(33,23,'33:23','Trong số những người có đức tin, có những người giữ đúng giao ước với Allah, có người đã hoàn tất lời hứa (đã hy sinh), có người đang chờ đợi đến lượt của mình, và họ đã thực sự không thay đổi giao ước đó.');
INSERT INTO "vn_ruwwad" VALUES(33,24,'33:24','Allah sẽ ban thưởng cho những người thật lòng giữ trọn giao ước và Ngài sẽ trừng phạt những kẻ giả tạo đức tin; nếu muốn Ngài sẽ tha thứ cho bọn họ. Quả thật, Allah là Đấng Hằng Tha Thứ, Đấng Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(33,25,'33:25','Allah đã đẩy lui những kẻ vô đức tin trong cơn ấm ức vì không đạt được điều tốt như mong muốn. Và một mình Allah thôi đã đủ giúp những người có đức tin chiến đấu, quả thật, Allah là Đấng Toàn Lực, Đấng Quyền Năng.');
INSERT INTO "vn_ruwwad" VALUES(33,26,'33:26','Ngài đã đuổi trong đám dân Kinh Sách những kẻ đã tiếp tay liên quân của địch khỏi thành lũy của bọn họ và gieo nỗi khiếp đảm vào lòng của bọn họ, một số trong bọn họ đã bị các ngươi giết và một số khác đã bị các ngươi bắt làm tù binh.');
INSERT INTO "vn_ruwwad" VALUES(33,27,'33:27','Ngài đã để cho các ngươi thừa hưởng đất đai, nhà cửa, tài sản của bọn họ và một phần đất đai mà các ngươi chưa đặt chân đến. Quả thật, Allah là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(33,28,'33:28','Hỡi Nabi (Muhammad)! Ngươi hãy bảo các bà vợ của Ngươi: “Nếu các nàng muốn cuộc sống trần tục này và vẻ hào nhoáng của nó thì các nàng hãy đến đây, Ta sẽ để các nàng hưởng thụ và Ta sẽ trả tự do cho các nàng một cách tốt đẹp.”');
INSERT INTO "vn_ruwwad" VALUES(33,29,'33:29','“Còn nếu các nàng muốn Allah, muốn Thiên Sứ của Ngài và ngôi nhà ở Đời Sau thì quả thật Allah đã chuẩn bị cho những phụ nữ làm tốt trong các nàng một phần thưởng vĩ đại.”');
INSERT INTO "vn_ruwwad" VALUES(33,30,'33:30','Hỡi các bà vợ của Nabi (Muhammad)! Ai trong các nữ là người đã công khai phạm điều ô uế thì sẽ bị phạt gấp đôi; và điều đó đối với vô cùng đơn giản.');
INSERT INTO "vn_ruwwad" VALUES(33,31,'33:31','Và ai trong các ngươi (hỡi những người vợ của Thiên Sứ) là người cung kính Allah và Thiên Sứ của Ngài đồng thời hành thiện thì TA sẽ thưởng cho người đó gấp đôi và TA chuẩn bị cho người đó bổng lộc dồi dào (nơi Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(33,32,'33:32','Hỡi các bà vợ của Nabi (Muhammad)! Các ngươi không giống như những người phụ nữ khác. Nếu các ngươi sợ Allah thì các ngươi chớ đừng nói năng lả lơi, bởi người mang trong lòng một chứng bệnh (dâm dục) sẽ dễ phạm vào (hành vi tình dục Haram); các ngươi hãy nói năng đoan trang đứng đắn.');
INSERT INTO "vn_ruwwad" VALUES(33,33,'33:33','Các ngươi hãy ở trong nhà của mình và chớ phô trương vẻ đẹp theo lối chưng diện (của những phụ nữ) Jahiliyah (thời ngu muội tiền Islam); các ngươi hãy chu dáo duy trì lễ nguyện Salah, hãy xuất Zakah, hãy vâng lệnh Allah và Thiên Sứ của Ngài. Hỡi người nhà của (Thiên Sứ), Allah thật ra chỉ muốn xóa những thứ ô uế khỏi các ngươi cũng như Ngài chỉ muốn tẩy sạch các ngươi thành những người hoàn toàn thanh khiết.');
INSERT INTO "vn_ruwwad" VALUES(33,34,'33:34','Các ngươi (hỡi các bà vợ của Thiên Sứ) hãy ghi nhớ những điều nằm trong các lời mặc khải đã được đọc ra trong nhà của các ngươi cũng như điều khôn ngoan (Sunnah của Thiên Sứ). Quả thật, Allah là Đấng Tinh Tế, Thông Toàn.');
INSERT INTO "vn_ruwwad" VALUES(33,35,'33:35','Quả thật, những người Muslim, nam cũng như nữ; những người có đức tin, nam cũng như nữ; những người cung kính, nam cũng như nữ; những người chân thật, nam cũng như nữ; những người kiên nhẫn, nam cũng như nữ; những người khiêm nhường, nam cũng như nữ; những người bố thí, nam cũng như nữ; những người nhịn chay, nam cũng như nữ; những người giữ bản thân không phạm các hành vi tình dục Haram, nam cũng như nữ; những người tưởng nhớ nhiều đến Allah, nam cũng như nữ; Allah đã chuẩn bị cho họ sự tha thứ và phần thưởng vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(33,36,'33:36','Người có đức tin thực sự, nam cũng như nữ, không phải là những người khi mà Allah và Thiên Sứ của Ngài đã quyết định việc gì thì họ lại đòi quyền được lựa chọn trong công việc của họ. Ai bất tuân Allah và Thiên Sứ của Ngài thì quả thật y đã công khai lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(33,37,'33:37','Ngươi (hỡi Thiên Sứ) hãy nhớ lại khi Ngươi nói với người ( mà Allah và Ngươi đã ban ân: “Con hãy giữ vợ (2) của con lại và hãy sợ Allah.” Trong khi đó, Ngươi cố giấu kín trong lòng mình điều (3) mà Allah sẽ tuyên bố. Ngươi sợ thiên hạ bàn tán chê cười trong lúc Allah là Đấng mà Ngươi cần phải sợ hơn cả. Cho nên, sau khi Zaid (con trai của Harithah) đã chấm dứt hoàn toàn với vợ của y thì TA gả nữ ta cho Ngươi mục đích để những người có đức tin không gặp phải trở ngại trong việc kết hôn với những người vợ của những đứa con nuôi của mình khi chúng đã hoàn toàn chấm dứt với vợ của chúng. Và mệnh lệnh của Allah thì phải được thực thi và chấp hành.');
INSERT INTO "vn_ruwwad" VALUES(33,38,'33:38','Nabi (Muhammad) không phạm tội trong những điều Allah đã ra lệnh cho Y thi hành. Đấy là đường lối của Allah cho các thế hệ đã qua của các thời trước. Và mệnh lệnh của Allah đã được ban hành thành luật (bắt buộc phải tuân thủ).');
INSERT INTO "vn_ruwwad" VALUES(33,39,'33:39','(Các vị Nabi này) là những người rao truyền thông điệp của Allah và sợ Ngài; họ không sợ bất kỳ ai trừ một mình Allah. Bởi lẽ một mình Allah thôi đã đủ xét xử và thanh toán (các việc làm của đám bầy tôi).');
INSERT INTO "vn_ruwwad" VALUES(33,40,'33:40','Muhammad không phải là cha của bất kỳ ai trong số những người đàn ông của các ngươi mà Y chính là vị Thiên Sứ của Allah và là vị Nabi cuối cùng. Quả thật, Allah hằng biết tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(33,41,'33:41','Hỡi những người có đức tin! Các ngươi hãy nhớ đến Allah thật nhiều (bằng trái tim, chiếc lưỡi và việc làm của các ngươi).');
INSERT INTO "vn_ruwwad" VALUES(33,42,'33:42','Các ngươi hãy tán dương Ngài vào mỗi sáng và mỗi chiều.');
INSERT INTO "vn_ruwwad" VALUES(33,43,'33:43','Ngài (Allah) là Đấng luôn Salawat ( cho các ngươi và các Thiên Thần của Ngài cũng Salawat như thế, để Ngài đưa các ngươi từ nơi tối tăm ra chốn ánh sáng. Và Ngài khoan dung đối với những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(33,44,'33:44','Lời chào giữa họ (những người có đức tin) vào Ngày họ gặp được Ngài (Allah) là lời chào bằng an ‘Assalam’ và Ngài đã chuẩn bị cho họ một phần thưởng vinh dự (Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(33,45,'33:45','Hỡi Nabi! Quả thật, TA đã cử phái Ngươi (đến với nhân loại) làm một nhân chứng, một người mang tin mừng và một người cảnh báo,');
INSERT INTO "vn_ruwwad" VALUES(33,46,'33:46','Và làm một người kêu gọi (nhân loại) đến với Allah dưới sự ưng thuận của Ngài; và (Ngươi được cử phái đến) như một chiếc đèn soi sáng.');
INSERT INTO "vn_ruwwad" VALUES(33,47,'33:47','Ngươi hãy báo tin mừng cho những người có đức tin rằng họ sẽ có được hồng phúc to lớn từ nơi Allah.');
INSERT INTO "vn_ruwwad" VALUES(33,48,'33:48','Ngươi chớ đừng nghe lời những kẻ vô đức tin cũng như những kẻ giả tạo đức tin, Ngươi cứ mặc kệ thái độ xấc xược của chúng và phó thác cho Allah; quả thật một mình Allah thôi đã đủ phò trợ (cho Ngươi).');
INSERT INTO "vn_ruwwad" VALUES(33,49,'33:49','Hỡi những người có đức tin! Nếu các ngươi kết hôn với những phụ nữ có đức tin rồi ly dị họ trước khi chạm đến thân thể của họ thì các ngươi không cần phải dùng đến qui định ''Iddah (thời gian ở vậy) dành cho họ. Tuy nhiên, các ngươi hãy trao tặng họ một món quà và hãy trả tự do cho họ một cách tốt đẹp.');
INSERT INTO "vn_ruwwad" VALUES(33,50,'33:50','Hỡi Nabi (Muhammad), quả thật TA đã qui định Halal (hợp thức hóa) cho Ngươi các bà vợ mà Ngươi đã đưa tiền cưới; những nữ nô lệ trong tay của Ngươi trong số các nữ tù binh mà Allah đã ban cho Ngươi; con gái chú bác của Ngươi, con gái người cô của Ngươi; con gái người cậu của Ngươi; con gái người dì của Ngươi đã cùng di cư (đến Madinah) với Ngươi; và riêng phụ nữ có đức tin nào tự hiến mình cho Nabi nếu Nabi muốn kết hôn với nữ ta, điều này chỉ dành riêng cho Ngươi chứ không áp dụng cho những người có đức tin khác. TA biết rõ những gì mà TA đã qui định cho họ liên quan đến các bà vợ của họ cũng như những nữ tù binh nằm trong tay họ để Ngươi không gặp trở ngại (trong việc cưới vợ). Quả thật, Allah là Đấng Hằng Tha Thứ, Hằng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(33,51,'33:51','Ngươi có quyền dời (việc tiếp) người vợ nào Ngươi muốn và có quyền tiếp người vợ nào Ngươi muốn; và Ngươi cũng không bị khiển trách nếu Ngươi muốn tiếp người vợ nào mà Ngươi đã dời (lượt) của họ, như thế là để làm dịu mắt họ và để họ khỏi buồn sầu và hài lòng với những gì Ngươi đã mang đến cho tất cả họ. Quả thật, Allah biết rõ những gì nằm trong lòng của các ngươi bởi Allah là Đấng Hằng Biết, Hằng Chịu Đựng.');
INSERT INTO "vn_ruwwad" VALUES(33,52,'33:52','Ngươi không được phép cưới thêm vợ nữa và cũng không được thay đổi (ly dị) họ để cưới những phụ nữ khác làm vợ cho dù sắc đẹp của những phụ nữ đó có làm Ngươi thích thú đi chăng nữa, ngoại trừ nữ tù binh nào nằm trong tay Ngươi. Quả thật, Allah là Đấng Giám Sát mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(33,53,'33:53','Hỡi những người có đức tin! Các ngươi chớ đừng tự ý vào nhà của Nabi (Muhammad) trừ phi các ngươi được mời đến dùng bữa, và (khi được mời) thì các ngươi chớ đừng đến sớm hơn khiến phải ngồi chờ đợi. Tuy nhiên, khi được mời thì các ngươi hãy vào và sau khi dùng bữa xong thì các ngươi hãy tranh thủ ra về, chớ đừng nán lại tán gẫu thêm gì. Quả thật, cách xử sự đó sẽ làm phiền Nabi và Y sẽ cảm thấy e ngại cho việc yêu cầu các ngươi ra về; nhưng Allah thì không e ngại (trong việc ra lệnh) đúng sự thật. Và khi các ngươi muốn hỏi chuyện những người vợ (của Nabi) thì các ngươi hãy hỏi họ từ phía sau một bức màn. Cách hỏi chuyện như thế sẽ giữ sự trong sạch cho tấm lòng của các ngươi cũng như cho tấm lòng của họ hơn. Các ngươi không được phép quấy rầy Sứ Giả của Allah, cũng không bao giờ được phép cưới vợ của Y sau khi Y qua đời. Quả thật, điều đó là một đại trọng tội đối với Allah.');
INSERT INTO "vn_ruwwad" VALUES(33,54,'33:54','Cho dù các ngươi bộc lộ hay giấu kín bất cứ điều gì (thì các ngươi hãy biết rằng) Allah chắc chắn biết hết mọi điều.');
INSERT INTO "vn_ruwwad" VALUES(33,55,'33:55','Họ (những người vợ của Nabi) không mắc tội (khi không mặc Hijab) trước mặt cha, con trai, anh em trai, con trai của anh chị em ruột, những phụ nữ giúp việc, và những người nô lệ nằm dưới quyền quản lý của họ. (Hỡi những người vợ của Nabi!) Các ngươi hãy kính sợ Allah, quả thật, Allah luôn giám sát mọi việc.');
INSERT INTO "vn_ruwwad" VALUES(33,56,'33:56','Quả thật, Allah và các Thiên Thần của Ngài đều Salawat cho Nabi (Muhammad). Hỡi những người có đức tin, các ngươi hãy Salawat cho Y thật nhiều.');
INSERT INTO "vn_ruwwad" VALUES(33,57,'33:57','Quả thật, những kẻ gây phiền nhiễu đến Allah và Sứ Giả của Ngài, Allah sẽ nguyền rủa họ ở đời này và cả Đời Sau; và Ngài đã chuẩn bị cho họ một sự trừng phạt nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(33,58,'33:58','Và những kẻ gây phiền nhiễu đến những người có đức tin, nam cũng như nữ, một cách không thỏa đáng thì quả thật họ đã mang vào mình tội vu khống và tội danh rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(33,59,'33:59','Hỡi Nabi (Muhammad), Ngươi hãy bảo những người vợ của Ngươi, các con gái của Ngươi, và những người vợ của những người có đức tin phủ áo choàng che kín thân thể của họ. Như thế để dễ nhận biết họ và để họ không bị xúc phạm. Quả thật, Allah là Đấng Hằng Tha Thứ, Hằng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(33,60,'33:60','Nếu những kẻ giả tạo đức tin và những kẻ mang trong lòng một chứng bệnh (hoài nghi) cũng như những kẻ gây rối (phao tin giả) trong thành phố Madinah không chịu dừng lại thì chắc chắn TA (Allah) sẽ để cho Ngươi (hỡi Thiên Sứ) trừng phạt chúng, rồi chúng sẽ được tha cho tạm sống nơi đó với Ngươi như những người láng giềng trong một thời gian ngắn ngủi.');
INSERT INTO "vn_ruwwad" VALUES(33,61,'33:61','Chúng sẽ bị nguyền rủa, và bất cứ khi nào cũng như bất cứ đâu chúng bị phát giác thì chúng sẽ bị bắt và bị giết không thương xót.');
INSERT INTO "vn_ruwwad" VALUES(33,62,'33:62','(Đó là) đường lối của Allah dành cho những kẻ (giả tạo đức tin) đã qua của thế hệ trước và Ngươi sẽ không bao giờ thấy một sự thay đổi nào trong đường lối của Allah (đối với những kẻ bội tín và phản nghịch).');
INSERT INTO "vn_ruwwad" VALUES(33,63,'33:63','Thiên hạ hỏi Ngươi về Giờ Tận Thế. Ngươi hãy nói: “Quả thật, kiến thức về thời khắc đó thuộc về một mình Allah”. Nhưng Ngươi có biết không có lẽ Giờ Tận Thế đã gần kề.');
INSERT INTO "vn_ruwwad" VALUES(33,64,'33:64','Quả thật, Allah đã nguyền rủa những kẻ vô đức tin và Ngài đã chuẩn bị cho họ lửa ngọn (nơi Hỏa Ngục).');
INSERT INTO "vn_ruwwad" VALUES(33,65,'33:65','Họ sẽ mãi mãi ở trong đó; họ sẽ không tìm được một vị che chở hay một vị cứu tinh nào.');
INSERT INTO "vn_ruwwad" VALUES(33,66,'33:66','Vào Ngày mà bộ mặt của họ bị lật úp vào Hỏa Ngục, họ sẽ than: “Ôi, giá như mình đã tuân lệnh Allah và đã vâng lời Sứ Giả (của Ngài)”.');
INSERT INTO "vn_ruwwad" VALUES(33,67,'33:67','Họ nói: “Lạy Thượng Đế của bầy tôi! Bầy tôi đã nghe theo các vị cầm đầu và những ông lớn của bầy tôi, chính họ đã dắt bầy tôi lầm lạc.”');
INSERT INTO "vn_ruwwad" VALUES(33,68,'33:68','“Lạy Thượng Đế của bầy tôi! Xin Ngài trừng phạt họ gấp đôi và xin Ngài nguyền rủa họ bằng lời nguyền rủa thậm tệ nhất.”');
INSERT INTO "vn_ruwwad" VALUES(33,69,'33:69','Hỡi những người có đức tin! Các ngươi chớ đừng giống như những kẻ đã sách nhiễu Musa bởi Allah đã giải oan cho Y khỏi những điều họ đã cáo buộc. Quả thật, (Musa) là một người bề tôi chính trực.');
INSERT INTO "vn_ruwwad" VALUES(33,70,'33:70','Hỡi những người có đức tin! Các ngươi hãy kính sợ Allah và hãy nói lời chân thật.');
INSERT INTO "vn_ruwwad" VALUES(33,71,'33:71','Ngài sẽ cải thiện việc làm của các ngươi cho các ngươi và Ngài sẽ tha thứ tội lỗi cho các ngươi. Người nào tuân lệnh Allah và Sứ Giả của Ngài thì chắc chắn sẽ đạt được sự thành công to lớn.');
INSERT INTO "vn_ruwwad" VALUES(33,72,'33:72','Quả thật, TA đã giao Ama-nah ( cho các tầng trời và trái đất và núi non nhưng chúng từ chối vì chúng cả sợ nên đã tránh xa nó; nhưng con người đã nhận lãnh nó; rõ ràng (con người) hết sức sai quấy, ngu muội.');
INSERT INTO "vn_ruwwad" VALUES(33,73,'33:73','(Làm thế) để Allah trừng phạt những kẻ giả tạo đức tin nam và nữ, những kẻ thờ đa thần nam và nữ; đồng thời để Ngài tha thứ cho những người có đức tin nam và nữ. Quả thật, Allah là Đấng Hằng Tha Thứ, Hằng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(34,1,'34:1','Alhamdulillah, Đấng mà mọi vật trong các tầng trời và mọi vật trong trái đất đều là của Ngài, Đấng mà mọi lời ca ngợi và tán dương ở Đời Sau đều thuộc về một mình Ngài. Ngài là Đấng Sáng Suốt, Thông Toàn.');
INSERT INTO "vn_ruwwad" VALUES(34,2,'34:2','Những gì chui vào lòng đất, những gì nhô lên khỏi mặt đất, những gì rơi xuống từ trên trời và những gì bay lên trời, Ngài đều biết rõ về chúng. Và Ngài là Đấng Hằng Khoan Dung, Hằng Tha Thứ.');
INSERT INTO "vn_ruwwad" VALUES(34,3,'34:3','Những kẻ vô đức tin nói: “Giờ Tận Thế sẽ không xảy đến cho chúng tôi.” Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Không, thề bởi Thượng Đế của Ta, Đấng biết điều vô hình, chắc chắn Giờ Tận Thế sẽ xảy đến cho các người. Không một vật gì trong các tầng trời cũng như không một vật gì trong trái đất nằm ngoài tầm kiểm soát của Ngài, cho dù đó chỉ là sức nặng của một hạt nguyên tử; và không một vật gì dù nhỏ hay lớn hơn (hạt nguyệt tử) mà lại không được ghi sẵn trong một Quyển Sổ rõ ràng.”');
INSERT INTO "vn_ruwwad" VALUES(34,4,'34:4','(Mọi thứ được ghi trong quyển sổ) để Ngài ban thưởng cho những người có đức tin và hành thiện. Họ là những người sẽ được sự tha thứ và được ban cho bổng lộc thật vinh dự (Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(34,5,'34:5','Những ai cố gắng chống phá các lời mặc khải của TA thì chắc chắn sẽ phải chịu một sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(34,6,'34:6','Những ai được ban cho kiến thức thì thấy những điều được ban xuống cho Ngươi (hỡi Thiên Sứ) từ Thượng Đế của Ngươi là Chân Lý và hướng dẫn (nhân loại) đến con đường của Đấng Toàn Năng, Đáng Ca Tụng.');
INSERT INTO "vn_ruwwad" VALUES(34,7,'34:7','Những kẻ vô đức tin (bảo nhau về Thiên Sứ một cách chế giễu và nhạo báng) nói: “Các người có muốn chúng tôi chỉ cho các người một người đàn ông, Y sẽ nói cho các người biết rằng khi các người (chết đi, thân xác các người) đã hoàn toàn mục rã thì các người chắc chắn sẽ được tạo hóa mới trở lại?!”');
INSERT INTO "vn_ruwwad" VALUES(34,8,'34:8','(Họ bảo nhau): “Lẽ nào Y đã nói dối cho Allah hay lẽ nào Y bị tâm thần?” Không, những kẻ vô đức tin nơi cõi Đời Sau sẽ bị trừng phạt (vào Ngày Phán Xét) và họ đã lầm lạc rất xa (trên cõi đời này).');
INSERT INTO "vn_ruwwad" VALUES(34,9,'34:9','Lẽ nào họ không nhìn thấy những gì trước mắt và sau lưng họ từ trời đất?! Nếu muốn, TA đã cho đất nuốt chửng bọn họ hoặc cho một mảnh trời rơi xuống đè bọn họ. Quả thật, nơi sự việc đó là một dấu hiệu cho mỗi người bề tôi biết quay đầu ăn năn sám hối (với Allah).');
INSERT INTO "vn_ruwwad" VALUES(34,10,'34:10','Quả thật, TA đã ban cho Dawood hồng phúc từ nơi TA, (và TA đã phán với núi non): “Hỡi núi non! Các ngươi hãy cùng với Y tán dương Allah” và chim chóc (TA cũng phán bảo chúng như thế), và TA đã hóa sắt thành mềm cho Y.');
INSERT INTO "vn_ruwwad" VALUES(34,11,'34:11','(TA) bảo (Dawood): “Ngươi hãy chế áo giáp và ráp các khoen sắt lại cho thật cân bằng và Ngươi (và con cháu của Ngươi) hãy hành thiện. Quả thật, TA luôn thấy rõ những điều các ngươi làm.”');
INSERT INTO "vn_ruwwad" VALUES(34,12,'34:12','Và Sulayman (con trai của Dawood), (TA đã ban cho Y khả năng chế ngự) luồng gió; luồng gió lướt đi trong một buổi sáng (nhanh bằng thời gian di chuyển) của một tháng và trong một buổi chiều (nhanh bằng thời gian di chuyển) của một tháng. TA đã ban cho Y một dòng suối đồng thau; và trong loài Jinn có những tên phục dịch cho Y theo phép của Thượng Đế của Y; và nếu tên Jinn nào cãi lệnh TA thì TA sẽ bắt nó nếm lấy hình phạt của Lửa ngọn cháy bùng.');
INSERT INTO "vn_ruwwad" VALUES(34,13,'34:13','Chúng (các tên Jinn hầu dịch cho Sulayman) làm ra cho Y những vật mà Y muốn, từ các cung điện, các hình tượng, những chiếc chén khổng lồ như những bể nước và những chiếc chảo nấu cố định một chỗ. Hỡi dòng dõi của Dawood, các ngươi hãy làm việc với lòng tri ân (TA). Tuy nhiên, chỉ một số ít bầy tôi của TA tỏ lòng biết ơn (TA).');
INSERT INTO "vn_ruwwad" VALUES(34,14,'34:14','Khi TA cho (Sulayman) chết, chẳng có điều gì làm cho loài Jinn phát hiện ra cái chết của Y ngoại trừ côn trùng đất đã ăn mòn chiếc gậy của Y. Cho nên, khi Y ngã xuống, loài Jinn mới vỡ lẽ rằng nếu chúng biết được điều vô hình thì chúng đã không phải ở lại lâu trong sự trừng phạt nhục nhã đó.(');
INSERT INTO "vn_ruwwad" VALUES(34,15,'34:15','Quả thật, tại chỗ ở của dân Sheba là một dấu hiệu (cho quyền năng của Allah cũng như cho việc Ngài đã ban ân huệ cho họ). (Tại nơi ở của họ) có hai ngôi vườn nằm bên phải và bên trái. (Allah bảo họ): “Các ngươi hãy ăn bổng lộc của Thượng Đế của các ngươi và hãy biết ơn Ngài; (đây là) một xứ sở tốt đẹp, và (đây là) một Thượng Đế Hằng Tha Thứ (tội lỗi).');
INSERT INTO "vn_ruwwad" VALUES(34,16,'34:16','Nhưng họ đã ngoảnh đi. Vậy nên TA đã gởi một trận lũ (đến trừng phạt họ) từ một con đập, và TA đã thay thế hai ngôi vườn của họ bằng hai ngôi vườn chỉ toàn loại trái đắng, các cây bụi (giông giống loại thánh liễu) và một ít cây táo gai.');
INSERT INTO "vn_ruwwad" VALUES(34,17,'34:17','Vì tội vong ơn nên TA đã bắt phạt họ như thế. Và TA chỉ trừng phạt những kẻ vong ân.');
INSERT INTO "vn_ruwwad" VALUES(34,18,'34:18','Giữa họ (dân của Sheba) và những thị trấn (của Sham) mà TA đã ban phúc, TA đã đặt những thị trấn khác dễ nhìn từ đằng xa và TA sắp đặt nơi đó những chặng đường (ngắn, gần) để họ đi lại dễ dàng; (và TA phán bảo họ): “Các ngươi hãy đi lại (tùy thích) một cách an toàn cả ban đêm lẫn ban ngày qua những địa điểm đó.”');
INSERT INTO "vn_ruwwad" VALUES(34,19,'34:19','(Tuy nhiên, họ không hài lòng với những chặng đường gần và ngắn) nên họ nói: “Lạy Thượng Đế của bầy tôi! Xin Ngài làm cho những chặng đường đi lại của bầy tôi dài xa thêm.” Họ đã bất công với chính bản thân họ (qua mong muốn đó). Vậy nên, TA đã lấy họ làm thành những câu chuyện và đã phân tán toàn bộ họ tản mác khắp nơi. Quả thật, trong sự việc đó là những dấu hiệu (bài học) cho từng người kiên nhẫn, biết ơn.');
INSERT INTO "vn_ruwwad" VALUES(34,20,'34:20','Quả thật, Iblis đã khẳng định sự thật về họ cho điều mà hắn đã phỏng đoán (rằng hắn có thể dắt họ lạc khỏi điều chân lý). Cho nên, họ đã nghe theo hắn ngoại trừ một nhóm trong số những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(34,21,'34:21','(Iblis) không hề có một chút quyền hành nào đối với họ cả mà thật ra đó chỉ nhằm mục đích để TA biết rõ ai là người có đức tin nơi cõi Đời Sau trong số những kẻ mà trong lòng họ vẫn còn sự hoài nghi. Quả thật, Thượng Đế của Ngươi (hỡi Thiên Sứ) là Đấng bảo vệ và giữ gìn tất cả mọi điều.');
INSERT INTO "vn_ruwwad" VALUES(34,22,'34:22','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Các ngươi hãy cầu nguyện những kẻ mà các ngươi cho rằng (chúng là thần linh của các ngươi) ngoài Allah. Quả thật, chúng không hề nắm giữ và chi phối bất cứ vật gì trong các tầng trời và trái đất, cho dù đó chỉ là sức nặng của hạt nguyên tử; và chúng không hề có bất cứ phần chia nào trong trời đất này; và trong bọn chúng không hề có bất cứ tên nào là vị ủng hộ của Ngài cả.”');
INSERT INTO "vn_ruwwad" VALUES(34,23,'34:23','Ở nơi Ngài (Allah), việc can thiệp sẽ vô ích trừ phi đối với ai mà Ngài đã cho phép. (Sự vĩ đại và tối cao của Ngài là khi Ngài phán thì tất cả các Thiên Thần đều khúm núm sợ hãi) mãi đến khi lòng của họ hết sợ thì họ mới dám lên tiếng hỏi (đại Thiên Thần Jibril): “Thượng Đế của quý vị phán điều gì vậy?” (Jibril) đáp: “Ngài phán điều Chân Lý bởi vì Ngài là Đấng Tối Cao, Vĩ Đại.”');
INSERT INTO "vn_ruwwad" VALUES(34,24,'34:24','Ngươi (hỡi Thiên Sứ) hãy bảo (những kẻ thờ đa thần): “Ai là Đấng ban bổng lộc cho các người từ các tầng trời và trái đất?” Ngươi hãy nói với họ: “Là Allah! Vậy chúng tôi hay các người, ai là những người được hướng dẫn đúng đường và ai là những kẻ lạc lối?”');
INSERT INTO "vn_ruwwad" VALUES(34,25,'34:25','Ngươi hãy nói với họ: “Các người sẽ không bị tra hỏi về tội của chúng tôi và chúng tôi cũng sẽ không bị tra hỏi về những gì các người đã làm.”');
INSERT INTO "vn_ruwwad" VALUES(34,26,'34:26','Ngươi hãy nói với họ: “Thượng Đế của chúng ta sẽ triệu tập tất cả chúng ta rồi Ngài sẽ phán xét giữa chúng ta đúng theo chân lý bởi Ngài là Đấng Thẩm Phán Toàn Tri.”');
INSERT INTO "vn_ruwwad" VALUES(34,27,'34:27','Ngươi hãy nói với họ: “Các người hãy chỉ cho Ta đâu là những kẻ mà các người đã cho là những vị đối tác ngang vai của (Allah). Chắc chắn sẽ không có ai (là đối tác của Ngài cả) mà chỉ có Ngài là Allah (Đấng Duy Nhất), Ngài là Đấng Toàn Năng, Sáng Suốt.”');
INSERT INTO "vn_ruwwad" VALUES(34,28,'34:28','Và TA (Allah) cử phái Ngươi (hỡi Thiên Sứ) đến chỉ để làm một Người mang tin mừng và một Người cảnh báo cho toàn bộ nhân loại nhưng đa số nhân loại không biết.');
INSERT INTO "vn_ruwwad" VALUES(34,29,'34:29','(Những kẻ thờ đa thần hối thúc sự trừng phạt) bảo: “Lời hứa (về sự trừng phạt) này bao giờ sẽ xảy đến nếu các ngươi nói thật?”');
INSERT INTO "vn_ruwwad" VALUES(34,30,'34:30','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Cuộc hẹn cho các người đã được định sẵn vào Ngày mà nó sẽ không đến muộn cũng không đến sớm hơn một thời khắc nào.”');
INSERT INTO "vn_ruwwad" VALUES(34,31,'34:31','Những kẻ vô đức tin nói: “Chúng tôi sẽ không bao giờ tin vào Qur’an này và cũng không tin vào bất cứ Kinh Sách nào trước Nó.” Nếu Ngươi (hỡi Thiên Sứ) nhìn thấy được tình cảnh của những kẻ làm điều sai quấy bị bắt đứng trước mặt Thượng Đế của họ (thì Ngươi sẽ thấy) họ lời qua tiếng lại trách cứ nhau. Những kẻ yếu thế sẽ nói với những tên cường bạo: “Nếu các người không dẫn chúng tôi lầm lạc thì chúng tôi đã trở thành những người có đức tin rồi!”');
INSERT INTO "vn_ruwwad" VALUES(34,32,'34:32','Những tên cường bạo đáp lời những kẻ yếu thế, nói: “Há bọn ta đã ngăn cản các ngươi theo chỉ đạo sau khi nó đã đến với các ngươi chăng? Không, chính các ngươi mới là những kẻ tội lỗi.”');
INSERT INTO "vn_ruwwad" VALUES(34,33,'34:33','Những kẻ yếu thế đáp lời những tên cường bạo: “Đúng vậy, các người đã bày mưu cả đêm lẫn ngày khi bảo chúng tôi vô đức tin nơi Allah và dựng những đối tác ngang vai cùng với Ngài.” (Tất cả) bọn họ sẽ hối hận khi nhìn thấy hình phạt. TA sẽ xiềng những chiếc gông vào cổ của những kẻ vô đức tin. Quả thật bọn họ bị trừng phạt chỉ bởi những điều mà bọn họ đã làm.');
INSERT INTO "vn_ruwwad" VALUES(34,34,'34:34','(Quả thật) không một vị cảnh báo nào được TA cử phái đến bất kỳ một thị trấn nào mà lại không bị những kẻ giàu có của thị trấn đó bảo: “Bọn ta không tin nơi thông điệp mà các ngươi mang đến.”');
INSERT INTO "vn_ruwwad" VALUES(34,35,'34:35','(Những kẻ giàu có đó) nói: “Bọn ta có nhiều tiền của và đông con cái, và bọn ta chắc chắn sẽ không bị trừng phạt.”');
INSERT INTO "vn_ruwwad" VALUES(34,36,'34:36','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ mà Allah đã ban cho họ nhiều ân huệ về của cải cũng như con cái): “Quả thật, Thượng Đế của Ta nới rộng hay thu hẹp bổng lộc đối với ai là tùy ý Ngài muốn, tuy nhiên, đa số nhân loại không biết (tất cả mọi sự việc đều được sắp đặt theo sự sáng suốt và anh minh của Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(34,37,'34:37','(Các ngươi hãy biết rằng) của cải, con cái của các ngươi không phải là những thứ đưa các ngươi đến gần TA (Allah) hơn, mà chỉ những ai có đức tin và hành thiện thì mới là những người sẽ được ban thưởng gấp đôi cho những điều (tốt) mà họ đã từng làm, và họ sẽ an toàn ở trong những ngôi nhà cao tầng.');
INSERT INTO "vn_ruwwad" VALUES(34,38,'34:38','Những kẻ cố gắng tìm cách phá hỏng các lời mặc khải của TA thì sẽ bị dẫn tới chỗ trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(34,39,'34:39','Ngươi (hỡi Thiên Sứ) hãy nói: “Thượng Đế của Ta nới rộng hay thu hẹp bổng lộc đối với ai trong số bầy tôi của Ngài là tùy ý Ngài muốn. Bất cứ thứ gì các người đã chi dùng (cho con đường chính nghĩa của Ngài) thì đều sẽ được Ngài hoàn lại, và Ngài là Đấng ban bổng lộc tốt nhất.”');
INSERT INTO "vn_ruwwad" VALUES(34,40,'34:40','Vào Ngày mà Ngài (Allah) triệu tập tất cả bọn họ rồi phán bảo các Thiên Thần: “Có phải những kẻ này đã từng tôn thờ các ngươi?”');
INSERT INTO "vn_ruwwad" VALUES(34,41,'34:41','(Các Thiên Thần) bẩm: “Thật quang vinh thay Ngài, Ngài là Đấng Chủ Nhân của bầy tôi chứ không phải họ. Họ không thờ bầy tôi mà họ thờ Jinn. Đa số bọn họ đã tin nơi Jinn.”');
INSERT INTO "vn_ruwwad" VALUES(34,42,'34:42','Vào Ngày mà không ai được quyền giúp đỡ ai cũng như không ai có thể gây hại được cho ai, TA sẽ phán bảo những kẻ làm điều sai quấy: “Các ngươi hãy nếm lấy hình phạt của Hỏa Ngục, cái các ngươi đã từng phủ nhận Nó.”');
INSERT INTO "vn_ruwwad" VALUES(34,43,'34:43','Và khi các lời mặc khải rõ ràng của TA được đọc ra cho họ thì họ nói: “Người đàn ông này chỉ muốn cản trở các người tôn thờ những thần linh mà cha mẹ của các người đã tôn thờ mà thôi.” Họ nói: “Đây thật ra chỉ là điều giả dối khéo bịa đặt.” Và những kẻ vô đức tin nói về chân lý khi nó đến với họ: “Đây rõ ràng chỉ là bùa mê hoặc.”');
INSERT INTO "vn_ruwwad" VALUES(34,44,'34:44','TA (Allah) đã không ban cho họ bất cứ Kinh Sách nào để họ có thể học hỏi; và TA cũng không cử bất cứ người cảnh báo nào đến với họ trước Ngươi cả.');
INSERT INTO "vn_ruwwad" VALUES(34,45,'34:45','Những kẻ trước họ đã phủ nhận (chân lý). Quả thật, những gì mà (những kẻ đa thần Quraish) có được (từ tiền của, con cái, sức mạnh) không bằng một phần mười các ân huệ mà TA đã ban cho các thế hệ trước nhưng tất cả các thế hệ trước đều phủ nhận các vị Sứ Giả của TA. Bởi thế, (Ngươi – Thiên Sứ hãy xem) sự trừng phạt của TA dành cho họ đã khủng khiếp thế nào.');
INSERT INTO "vn_ruwwad" VALUES(34,46,'34:46','Ngươi (hỡi Thiên Sứ) hãy nói với (những kẻ thờ đa thần Quraish): “Ta chỉ khuyên các người một điều: các người hãy vì Allah mà đứng lên từng cặp hay từng người rồi suy ngẫm, (chắc chắn các người sẽ thấy) người bạn của các người (Muhammad) không phải là một gã điên mà đích thực là một vị cảnh báo các người về một sự trừng phạt khủng khiếp sắp đến.”');
INSERT INTO "vn_ruwwad" VALUES(34,47,'34:47','Ngươi (hỡi Thiên Sứ) hãy nói với (những kẻ thờ đa thần Quraish): “Ta không hề đòi các người bất cứ phần thưởng nào bởi phần thưởng của Ta là do Allah ban cho và Ngài là Đấng chứng giám tất cả mọi thứ.”');
INSERT INTO "vn_ruwwad" VALUES(34,48,'34:48','Ngươi (hỡi Thiên Sứ) hãy nói: “Quả thật, Thượng Đế của Ta ban Chân Lý xuống (để loại bỏ điều ngụy tạo) và Ngài là Đấng Thông Toàn mọi điều vô hình.”');
INSERT INTO "vn_ruwwad" VALUES(34,49,'34:49','Ngươi (hỡi Thiên Sứ) hãy nói: “Chân lý đã đến thì sự ngụy tạo không thể bắt đầu (bất cứ điều gì) cũng như không thể phục hồi được.”');
INSERT INTO "vn_ruwwad" VALUES(34,50,'34:50','Ngươi (hỡi Thiên Sứ) hãy nói với (những kẻ thờ đa thần Quraish): “Nếu Ta lầm lạc thì thật ra chỉ bản thân Ta lầm lạc mà thôi nhưng nếu Ta được hướng dẫn thì đó là do những gì mà Thượng Đế của Ta đã mặc khải cho Ta. Quả thật Ngài hằng nghe và Ngài ở gần kề.”');
INSERT INTO "vn_ruwwad" VALUES(34,51,'34:51','Nếu Ngươi (hỡi Thiên Sứ) có thể nhìn thấy được tình cảnh bọn họ sợ hãi (khi nhìn thấy sự trừng phạt), bọn họ vô phương trốn thoát và sẽ bị túm bắt từ một nơi rất gần,');
INSERT INTO "vn_ruwwad" VALUES(34,52,'34:52','Thì bọn họ nói: “Chúng tôi giờ đây đã có đức tin”, tuy nhiên, làm sao bọn họ có thể nhận được đức tin từ một nơi quá xa xôi!');
INSERT INTO "vn_ruwwad" VALUES(34,53,'34:53','Quả thật, họ đã phủ nhận chân lý từ trước và họ đã thường phỏng đoán về điều vô hình từ một nơi xa.');
INSERT INTO "vn_ruwwad" VALUES(34,54,'34:54','Giữa họ và những điều mà họ mong ước có một bức màn ngăn cách, tương tự trường hợp đã xảy ra cho những kẻ như họ trong quá khứ. Quả thật, họ là đám người đã luôn ở trong tình trạng hoài nghi.');
INSERT INTO "vn_ruwwad" VALUES(35,1,'35:1','Alhamdulillah, Đấng đã tạo hóa các tầng trời và trái đất; Ngài đã chỉ định các Thiên Thần làm các Sứ Giả (của Ngài), họ sở hữu những chiếc cánh: hai, ba, hoặc bốn. Ngài thêm vào sự tạo hóa bất cứ thứ gì Ngài muốn. Quả thật, Allah là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(35,2,'35:2','Bất cứ ân huệ nào mà Allah muốn ban cho nhân loại thì không ai có khả năng giữ nó lại; còn ân huệ nào mà Ngài muốn giữ lại thì không ai ngoài Ngài có khả năng ban phát nó. Và Ngài là Đấng Toàn Năng, Sáng Suốt.');
INSERT INTO "vn_ruwwad" VALUES(35,3,'35:3','Hỡi nhân loại! Các ngươi hãy nhớ ân huệ mà Allah đã ban cho các ngươi. Lẽ nào có một Đấng Tạo Hóa khác Allah đã ban bổng lộc cho các ngươi từ trời đất? Không có Thượng Đế nào ngoài Ngài cả. Vậy sao các ngươi lại lánh xa (điều chân lý)?');
INSERT INTO "vn_ruwwad" VALUES(35,4,'35:4','Nếu (người dân của Ngươi – hỡi Thiên Sứ) phủ nhận Ngươi thì quả thật các Sứ Giả trước Ngươi cũng đã bị phủ nhận như thế. Tuy nhiên, mọi vấn đề sẽ được đưa trở về Allah để Ngài định đoạt.');
INSERT INTO "vn_ruwwad" VALUES(35,5,'35:5','Hỡi nhân loại! Quả thật, lời hứa của Allah (về sự phục sinh, sự phát xét ở Ngày Sau) là sự thật. Do đó, các ngươi chớ đừng để cho đời sống trần tục này đánh lừa các ngươi và các ngươi chớ để cho tên đại bịp (Shaytan) lừa gạt các ngươi về Allah.');
INSERT INTO "vn_ruwwad" VALUES(35,6,'35:6','Quả thật, Shaytan là kẻ thù của các ngươi. Do đó, các ngươi hãy coi nó là kẻ thù (mà chiến đấu chống lại nó). Quả thật, (Shaytan) chuyên kêu gọi bè phái của nó để làm cho chúng trở thành những người bạn của Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(35,7,'35:7','Những kẻ vô đức tin sẽ bị trừng phạt nghiêm khắc; còn những ai có đức tin và hành thiện thì sẽ được sự tha thứ (từ nơi Allah) và sẽ được (Ngài) ban thưởng phần thưởng to lớn.');
INSERT INTO "vn_ruwwad" VALUES(35,8,'35:8','Lẽ nào một kẻ coi hành động tội lỗi của y đẹp mắt đối với y và y cho đó là điều tốt (lại ngang bằng với một người coi sự chỉ đạo của Allah là chân lý) ư? Quả thật, Allah muốn làm cho kẻ nào lầm lạc là tùy ý Ngài và Ngài muốn hướng dẫn ai cũng tùy ý Ngài. Do đó, Ngươi (hỡi Thiên Sứ) chớ vì đau buồn cho bọn họ mà khiến thân xác mình hao gầy. Quả thật, Allah hằng biết về những điều họ làm.');
INSERT INTO "vn_ruwwad" VALUES(35,9,'35:9','Allah là Đấng đã gởi các luồng gió bay đi, thổi đám mây di chuyển, để TA (đưa nước mưa của nó) tưới lên vùng đất khô cằn làm sống lại mảnh đất đã chết khô. Và việc phục sinh cũng tương tự như thế.');
INSERT INTO "vn_ruwwad" VALUES(35,10,'35:10','Người nào muốn vinh quyền thì (hãy biết rằng) mọi vinh quang và quyền uy đều thuộc về Allah. Mọi lời nói tốt đẹp sẽ được đưa lên Ngài và mọi việc làm thiện tốt đều sẽ được trình lên nơi Ngài. Riêng những kẻ âm mưu điều xấu thì sẽ bị trừng phạt nghiêm khắc. Và chắc chắn mưu đồ của những kẻ đó sẽ bị thất bại.');
INSERT INTO "vn_ruwwad" VALUES(35,11,'35:11','Allah đã tạo hóa các ngươi từ đất bụi, rồi từ một giọt tinh dịch; sau đó Ngài làm cho các ngươi thành đôi (vợ chồng). Không một người phụ nữ nào mang thai hoặc hạ sinh mà Ngài không biết. Không một người nào sống thọ hay đoản thọ mà không được ghi trong Quyển Kinh (Mẹ - Law-hul-mahfuzh). Quả thật, (tất cả) những sự việc đó thật đơn giản đối với Allah.');
INSERT INTO "vn_ruwwad" VALUES(35,12,'35:12','Hai biển nước không giống nhau. Một loại thì ngọt, dễ uống còn một loại thì mặn, chát (không thể uống). Tuy nhiên, các ngươi đều được ăn thịt tươi từ cả hai loại biển đó và từ chúng các ngươi có được đồ trang sức (từ ngọc trai và san hô). Và ngươi thấy những chiếc thuyền vượt các con sóng (của hai loại biển đó) đưa các ngươi đi tìm thiên lộc của Ngài. Mong rằng các ngươi biết tri ân Ngài.');
INSERT INTO "vn_ruwwad" VALUES(35,13,'35:13','Ngài (Allah) nhập ban đêm vào ban ngày và nhập ban ngày vào ban đêm; Ngài chế ngự mặt trời và mặt trăng, mỗi cái bơi (theo quỹ đạo riêng của nó) cho đến một thời hạn đã định. Đó là Allah, Thượng Đế của các ngươi, mọi quyền thống trị đều thuộc về Ngài. Còn những kẻ mà các ngươi cầu nguyện ngoài Ngài không nắm quyền chi phối (bất cứ thứ gì cho dù đó) chỉ là một lớp vỏ chà là.');
INSERT INTO "vn_ruwwad" VALUES(35,14,'35:14','Nếu các ngươi cầu nguyện chúng (các thần linh ngoài Allah) thì chúng sẽ không nghe được lời cầu nguyện của các ngươi, và cho dù chúng có nghe được đi chăng nữa thì chúng cũng không thể đáp lại lời cầu nguyện của các ngươi được. Vào Ngày Phán Xét, chúng sẽ phủ nhận việc các ngươi tổ hợp (chúng với Allah). Và không ai có thể nói cho Ngươi (hỡi Thiên Sứ) biết sự thật giống như (Allah), Đấng Thông Toàn cả.');
INSERT INTO "vn_ruwwad" VALUES(35,15,'35:15','Hỡi nhân loại, các ngươi toàn là những kẻ nghèo hèn luôn cần đến Allah còn Allah là Đấng Giàu Có, đáng được ca tụng.');
INSERT INTO "vn_ruwwad" VALUES(35,16,'35:16','Nếu muốn, Ngài sẽ tiêu diệt tất cả các ngươi và tạo ra một loại tạo vật mới (thay thế các ngươi).');
INSERT INTO "vn_ruwwad" VALUES(35,17,'35:17','Điều đó đối với Allah chẳng có gì nặng nề và khó khăn cả.');
INSERT INTO "vn_ruwwad" VALUES(35,18,'35:18','Không ai phải chịu trách nhiệm cho tội lỗi của người khác; nếu một người yêu cầu được gánh tội thay cho ai đó thì y cũng không thể gánh tội thay được, cho dù đó là bà con ruột thịt của y đi chăng nữa. Quả thật, Ngươi (hỡi Thiên Sứ) chỉ có thể cảnh báo những ai sợ Thượng Đế của họ về điều vô hình, cũng như những ai mà họ chu đáo duy trì lễ nguyện Salah. Ai tẩy sạch tội lỗi thì thật ra việc tẩy sạch đó chỉ có lợi cho bản thân y mà thôi. Và tất cả đều phải trở về trình diện Allah (vào Ngày Phục Sinh để Ngài phán xét và thưởng phạt).');
INSERT INTO "vn_ruwwad" VALUES(35,19,'35:19','Người mù và người sáng mắt không hề ngang bằng nhau.');
INSERT INTO "vn_ruwwad" VALUES(35,20,'35:20','Bóng tối và ánh sáng không ngang bằng nhau.');
INSERT INTO "vn_ruwwad" VALUES(35,21,'35:21','Bóng mát và cái nóng không như nhau.');
INSERT INTO "vn_ruwwad" VALUES(35,22,'35:22','Người sống và người chết không hề giống nhau. Quả thật, Allah có thể làm cho ai đó nghe được tùy Ngài muốn; nhưng Ngươi (hỡi Thiên Sứ) không thể làm cho bất cứ ai trong mộ nghe được.');
INSERT INTO "vn_ruwwad" VALUES(35,23,'35:23','Quả thật, Ngươi (hỡi Thiên Sứ) chỉ là một người cảnh báo.');
INSERT INTO "vn_ruwwad" VALUES(35,24,'35:24','Quả thật, TA (Allah) đã cử phái Ngươi (hỡi Thiên Sứ) đến bằng sự thật (không có gì phải hoài nghi) để làm một vị mang tin mừng đồng thời làm một vị cảnh báo. Và không một cộng đồng nào mà lại không có một Người cảnh báo được cử phái đến với họ.');
INSERT INTO "vn_ruwwad" VALUES(35,25,'35:25','Nếu họ (người dân của Ngươi – hỡi Thiên Sứ) phủ nhận Ngươi thì quả thật những kẻ trước họ cũng đã phủ nhận (các Sứ Giả được cử phái đến) giống như thế. Các Sứ Giả của họ đã đến gặp họ với những bằng chứng rõ rệt cùng với Kinh Zubur và Kinh Sách soi sáng.');
INSERT INTO "vn_ruwwad" VALUES(35,26,'35:26','Rồi TA đã bắt phạt những kẻ vô đức tin. Ngươi (hỡi Thiên Sứ) hãy xem sự trừng phạt của TA (dành cho họ) khủng khiếp thế nào!');
INSERT INTO "vn_ruwwad" VALUES(35,27,'35:27','Chẳng lẽ Ngươi (hỡi Thiên Sứ) không thấy Allah đã ban từ trên trời xuống nguồn nước (mưa) ư? TA (Allah) đã dùng nguồn nước đó làm mọc ra hoa quả và cây trái mang nhiều màu sắc khác nhau, và có những quả núi TA đã làm cho chúng có những đường rạch trắng và đỏ cũng như các màu sắc khác nhau và những thứ có màu đen sậm.');
INSERT INTO "vn_ruwwad" VALUES(35,28,'35:28','Tương tự như thế, loài người, động vật và gia súc cũng mang những màu sắc khác biệt. Quả thật, trong số bầy tôi của Ngài, chỉ những người hiểu biết mới thực sự sợ Allah. Allah đích thực là Đấng Toàn Năng, Hằng Tha Thứ.');
INSERT INTO "vn_ruwwad" VALUES(35,29,'35:29','Quả thật, những ai đọc Kinh Sách của Allah, chu đáo duy trì lễ nguyện Salah và chi dùng những bổng lộc mà TA (Allah) đã ban cấp (cho con đường chính nghĩa của TA) một cách kín đáo hay công khai với hy vọng cuộc đổi chác chỉ có lời không bao giờ thua lỗ.');
INSERT INTO "vn_ruwwad" VALUES(35,30,'35:30','Để Ngài có thể cho họ đầy đủ phần thưởng của họ và ban thêm cho họ từ hồng phúc của Ngài. Quả thật, Ngài là Đấng Hằng Tha Thứ, Hằng Tri Ân.');
INSERT INTO "vn_ruwwad" VALUES(35,31,'35:31','Những điều mà TA (Allah) đã Mặc Khải cho Ngươi (hỡi Thiên Sứ) trong Kinh Sách là chân lý, xác nhận lại những điều đã ban xuống trước Nó. Quả thật, Allah là Đấng Thông Toàn, Ngài thấy rõ các bầy tôi của Ngài.');
INSERT INTO "vn_ruwwad" VALUES(35,32,'35:32','Rồi TA đã để cho những người mà TA đã lựa chọn trong đám bầy tôi của TA thừa hưởng Kinh Sách (Qur’an). Tuy nhiên, trong số họ, có người tự làm hại bản thân mình; có người ở mức chính giữa; và có người là những người luôn tiên phong trong việc làm tốt dưới sự chấp thuận của Allah, đây là hồng phúc lớn nhất.');
INSERT INTO "vn_ruwwad" VALUES(35,33,'35:33','Những Ngôi Vườn Thiên Đàng vĩnh cửu, họ sẽ vào sống trong đó, nơi mà họ sẽ được cho đeo vòng vàng, ngọc trai và sẽ được mặc áo lụa.');
INSERT INTO "vn_ruwwad" VALUES(35,34,'35:34','(Sau khi vào Thiên Đàng) họ nói: “Alhamdulillah, Đấng đã lấy đi khỏi bầy tôi mọi ưu phiền. Quả thật, Thượng Đế của bầy tôi là Đấng Hằng Tha Thứ, Hằng Tri Ân.');
INSERT INTO "vn_ruwwad" VALUES(35,35,'35:35','“Đấng đã định cư bầy tôi nơi ngôi nhà vĩnh cửu từ hồng phúc của Ngài, một nơi ở mà bầy tôi không phải vất vả, khổ cực cũng như không phải hao tổn tâm sức.”');
INSERT INTO "vn_ruwwad" VALUES(35,36,'35:36','Riêng những kẻ vô đức tin, họ sẽ bị trừng phạt trong Hỏa Ngục. Họ sẽ không được cho chết phứt để xong chuyện và cũng không được giảm nhẹ hình phạt. TA bắt phạt tất cả những kẻ vô đức tin như thế đó.');
INSERT INTO "vn_ruwwad" VALUES(35,37,'35:37','Họ sẽ la hét trong đó: “Lạy Thượng Đế của bầy tôi! Xin Ngài thả bầy tôi ra (khỏi nơi này). Bầy tôi hứa sẽ làm điều tốt, khác với điều (tội lỗi) mà bầy tôi đã từng làm.” (Allah phán bảo họ): “Lẽ nào TA đã không cho các ngươi sống đủ lâu để nghe được lời nhắc nhở đối với ai muốn được nhắc nhở? Và chẳng phải đã có một vị cảnh báo đến gặp các ngươi sao? Thôi, các ngươi hãy nếm lấy (sự trừng phạt), bởi những kẻ làm điều sai quấy sẽ không được ai giúp đỡ.”');
INSERT INTO "vn_ruwwad" VALUES(35,38,'35:38','Quả thật, Allah là Đấng biết mọi điều vô hình của các tầng trời và trái đất. Quả thật, Ngài biết những gì được giấu kín trong lòng (con người).');
INSERT INTO "vn_ruwwad" VALUES(35,39,'35:39','Ngài là Đấng đã để các ngươi (hỡi con người) làm những người cai quản trên trái đất. Do đó, người nào vô đức tin thì sẽ bị bắt phạt cho sự vô đức tin của mình. Những kẻ vô đức tin, sự không tin của họ (chẳng gây hại được gì đến Allah) mà nó chỉ làm cho họ thêm đáng ghét ở nơi Thượng Đế của họ. Những kẻ vô đức tin, sự không tin của họ chỉ làm cho họ thêm thua thiệt.');
INSERT INTO "vn_ruwwad" VALUES(35,40,'35:40','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ thờ đa thần): “Những kẻ đối tác (thần linh) mà các người cầu nguyện ngoài Allah, các người hãy cho Ta biết đâu là thứ mà chúng đã tạo ra ở dưới đất, hoặc đâu là phần mà chúng đã chia sẻ (với Allah) trong các tầng trời?” Hay phải chăng họ được TA (Allah) ban cho Kinh Sách mà nhờ đó họ có được bằng chứng rõ rệt. Không, những kẻ làm điều sai quấy chỉ hứa điều lừa dối với nhau mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(35,41,'35:41','Quả thật, Allah nắm giữ các tầng trời và trái đất kẻo chúng sẽ dừng hoạt động. Nếu chúng thực sự dừng hoạt động thì ngoài Ngài chẳng ai có đủ quyền năng nắm giữ chúng (để chúng hoạt động trở lại). Quả thật, Ngài là Đấng Hằng Chịu Đựng, Hằng Tha Thứ.');
INSERT INTO "vn_ruwwad" VALUES(35,42,'35:42','(Những kẻ vô đức tin) thề với Allah bằng sự thề thốt long trọng rằng nếu có một người cảnh báo đến với họ thì họ chắc chắn sẽ được hướng dẫn hơn bất cứ cộng đồng nào khác. Tuy nhiên, khi một người cảnh báo đến với họ thì điều đó chẳng thêm thắt được gì cho họ ngoại trừ việc xa lánh và ngoảnh đi.');
INSERT INTO "vn_ruwwad" VALUES(35,43,'35:43','Họ ngạo mạn trong xứ và mưu đồ xấu xa. Nhưng mưu đồ xấu xa đó không hãm hại được ai ngoài kẻ chủ mưu. Có phải họ chỉ mong đợi đường lối (trừng phạt của Allah) dành cho các thế hệ trước họ? Do đó, Ngươi (hỡi Thiên Sứ) sẽ không thấy có một sự thay đổi nào trong đường lối (trừng phạt) của Allah (đối với những kẻ phản nghịch và bất tuân Ngài), và Ngươi cũng sẽ không bao giờ thấy có sự chuyển hướng nào trong đường lối (trừng phạt) của Allah.');
INSERT INTO "vn_ruwwad" VALUES(35,44,'35:44','Chẳng lẽ họ đã không du hành khắp nơi trên trái đất để chứng kiến kết cuộc của những kẻ trước họ mặc dầu những kẻ trước họ mạnh hơn họ về sức mạnh?! Quả thật, Allah không bất lực trước bất cứ thứ gì trong các tầng trời và trái đất bởi vì Ngài là Đấng Toàn Tri, Toàn Năng.');
INSERT INTO "vn_ruwwad" VALUES(35,45,'35:45','Nếu Allah bắt phạt nhân loại cho tội lỗi mà họ đã phạm thì Ngài đã không chừa trên mặt đất bất kỳ một loại sinh vật nào sống sót, tuy nhiên, Ngài gia hạn cho họ sống đến một thời khắc nhất định. Rồi khi nào thời hạn của họ đã mãn thì chắc chắn Allah sẽ thấy tất cả đám bầy tôi của Ngài.');
INSERT INTO "vn_ruwwad" VALUES(36,1,'36:1','Ya-Sin.');
INSERT INTO "vn_ruwwad" VALUES(36,2,'36:2','Thề bởi Qur’an anh minh (chứa đựng những điều khôn ngoan và đúng đắn).');
INSERT INTO "vn_ruwwad" VALUES(36,3,'36:3','Quả thật, Ngươi (hỡi Thiên Sứ) đích thực là một trong các Sứ Giả (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(36,4,'36:4','(Quả thật, Ngươi) đang ở trên con đường ngay chính (được Thượng Đế của Ngươi lựa chọn).');
INSERT INTO "vn_ruwwad" VALUES(36,5,'36:5','(Kinh Qur’an) được ban xuống từ Đấng Toàn Năng, Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(36,6,'36:6','(Kinh Qur’an được ban xuống cho Ngươi) để Ngươi dùng cảnh báo đến một dân tộc mà tổ tiên của họ trước đó đã không được cảnh báo nên họ xao lãng.');
INSERT INTO "vn_ruwwad" VALUES(36,7,'36:7','Quả thật, lời (hứa trừng phạt của Allah) đối với đa số họ là sự thật bởi vì họ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(36,8,'36:8','TA cho đóng những chiếc gông vào cổ của họ cao đến tận cằm khiến họ cứ ngẩng cổ mãi.');
INSERT INTO "vn_ruwwad" VALUES(36,9,'36:9','Và TA đã đặt một bức chắn trước mặt và sau lưng họ nên họ không nhìn thấy (chân lý).');
INSERT INTO "vn_ruwwad" VALUES(36,10,'36:10','Cho dù Ngươi (hỡi Thiên Sứ) cảnh báo hay không cảnh báo họ thì họ vẫn không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(36,11,'36:11','Quả thật, Ngươi chỉ có thể cảnh báo người nào đi theo Lời Nhắc Nhở (của Qur’an) và sợ Đấng Arrahman một cách vô hình. Do đó, Ngươi hãy báo tin mừng cho y về sự tha thứ và phần thưởng vinh dự (Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(36,12,'36:12','TA (Allah) chắc chắn làm sống lại người chết và ghi chép tất cả mọi điều mà họ đã gởi đi trước cũng như những dấu tích mà họ đã lưu lại. Và TA cho ghi tất cả mọi điều trong một quyển sổ (Lawhul-Mahfuzh) một cách rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(36,13,'36:13','Ngươi (hỡi Thiên Sứ) hãy trình bày cho họ (những kẻ phủ nhận) hình ảnh thí dụ về đám dân của một thị trấn khi các Sứ Giả (của Allah) đến gặp họ.');
INSERT INTO "vn_ruwwad" VALUES(36,14,'36:14','Khi TA phái hai Sứ Giả đến gặp họ, họ đã phủ nhận hai vị đó, rồi TA lại tăng cường thêm một vị thứ ba; cả ba vị đều nói với (đám dân của thị trấn): “Chúng tôi đích thực là những Sứ Giả được phái đến cho các người.”');
INSERT INTO "vn_ruwwad" VALUES(36,15,'36:15','(Đám dân của thị trấn) bảo: “Các ngươi chỉ là những người phàm tục giống bọn ta. Đấng Arrahman đã không ban bất cứ điều gì xuống cả. Các ngươi chỉ nói dối mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(36,16,'36:16','(Ba vị Sứ Giả) nói: “Thượng Đế của chúng tôi biết rõ chúng tôi đích thực là những Sứ Giả được phái đến cho các người.”');
INSERT INTO "vn_ruwwad" VALUES(36,17,'36:17','“Nhiệm vụ của chúng tôi chỉ là truyền đạt một cách rõ ràng mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(36,18,'36:18','(Đám dân của thị trấn) bảo: “Bọn ta thực sự thấy có điềm xấu từ các ngươi. Nếu các ngươi không chịu dừng lại thì chắc chắn bọn ta sẽ ném đá giết các ngươi, chắc chắn bọn ta sẽ dùng hình phạt đau đớn để xử lý các ngươi.”');
INSERT INTO "vn_ruwwad" VALUES(36,19,'36:19','(Ba vị Sứ Giả) nói: “Điềm xấu của các người sẽ đeo bám các người (bởi sự vô đức tin của các người). Lẽ nào các người cho là điềm xấu khi được nhắc nhở (về Allah) ư? Không, các người đúng là một đám người tội lỗi.”');
INSERT INTO "vn_ruwwad" VALUES(36,20,'36:20','Rồi (đột nhiên) có một người đàn ông từ cuối phố chạy đến, bảo: “Này hỡi người dân! Các người hãy tuân theo lời các vị Sứ Giả.”');
INSERT INTO "vn_ruwwad" VALUES(36,21,'36:21','“Các người hãy tuân theo những người không đòi các người bất cứ tiền thù lao nào và Họ đích thực là những người được hướng dẫn đúng đường.”');
INSERT INTO "vn_ruwwad" VALUES(36,22,'36:22','“Tại sao tôi lại không thờ phượng Đấng đã tạo ra tôi, Đấng mà các người sẽ được đưa về trình diện Ngài trở lại.”');
INSERT INTO "vn_ruwwad" VALUES(36,23,'36:23','“Lẽ nào tôi lại thờ phượng những thần linh khác ngoài Ngài ư? Nếu Đấng Arrahman muốn tôi gặp điều tai hại thì các thần linh đó chẳng giúp ích được gì cho tôi cả và cũng không cứu vớt được tôi.”');
INSERT INTO "vn_ruwwad" VALUES(36,24,'36:24','“Lúc đó, chắc chắn tôi thực sự là một kẻ lầm lạc rõ ràng.”');
INSERT INTO "vn_ruwwad" VALUES(36,25,'36:25','“Quả thật, tôi đã tin nơi Thượng Đế của tất cả các người. Do đó, các người hãy nghe (lời khuyên của tôi).”');
INSERT INTO "vn_ruwwad" VALUES(36,26,'36:26','Có Lời phán bảo: “Nhà ngươi hãy vào Thiên Đàng.” (Sau khi vào Thiên Đàng và thấy được những gì nơi đó) y nói: “Ôi ước gì, phải chi đám dân của tôi biết được”');
INSERT INTO "vn_ruwwad" VALUES(36,27,'36:27','“Việc Thượng Đế của tôi đã tha thứ và ban vinh dự cho tôi.”');
INSERT INTO "vn_ruwwad" VALUES(36,28,'36:28','Và sau y, TA (Allah) đã không gởi bất cứ đoàn thiên binh nào xuống trừng phạt đám dân của y và TA cũng chẳng cần gởi xuống như thế.');
INSERT INTO "vn_ruwwad" VALUES(36,29,'36:29','Chỉ cần một tiếng gầm duy nhất thôi thì họ bị tiêu diệt toàn bộ.');
INSERT INTO "vn_ruwwad" VALUES(36,30,'36:30','Thật đáng tiếc cho đám bề tôi (của TA)! Không một Sứ Giả nào (của TA) được cử đến với họ mà họ lại không bị chế giễu.');
INSERT INTO "vn_ruwwad" VALUES(36,31,'36:31','Lẽ nào họ không thấy TA đã tiêu diệt biết bao thế hệ trước họ? Những người của thế hệ đó sẽ không bao giờ trở lại gặp họ (trên thế gian này).');
INSERT INTO "vn_ruwwad" VALUES(36,32,'36:32','Từng người trong bọn họ chắc chắn sẽ được đưa đến trình diện TA.');
INSERT INTO "vn_ruwwad" VALUES(36,33,'36:33','(Những kẻ phủ nhận sự Phục Sinh), có một dấu hiệu cho họ (rằng sự Phục Sinh là sự thật, đó là) mảnh đất chết khô mà TA làm cho sống lại và làm mọc ra trái hạt để họ dùng làm thực phẩm.');
INSERT INTO "vn_ruwwad" VALUES(36,34,'36:34','TA đã tạo ra nơi (mảnh đất chết khô) đó những vườn cây chà là và những vườn nho, và TA đã cho các dòng suối chảy trong đó.');
INSERT INTO "vn_ruwwad" VALUES(36,35,'36:35','Để họ ăn trái quả của các ngôi vườn đó. Và bàn tay của họ đã không làm ra được các ngôi vườn như thế kia. Vậy sao họ không biết tri ân (TA)?');
INSERT INTO "vn_ruwwad" VALUES(36,36,'36:36','Quang Vinh thay Ngài (Allah)! Đấng đã tạo hóa tất cả (vạn vật) thành từng đôi (âm dương): từ những vật mà đất đai cho mọc ra, từ chính bản thân của họ và từ những vật mà họ không biết.');
INSERT INTO "vn_ruwwad" VALUES(36,37,'36:37','Có một dấu hiệu (về Allah) dành cho họ (nhân loại) là ban đêm; khi TA rút ban ngày ra khỏi ban đêm thì họ sẽ ở trong bóng tối.');
INSERT INTO "vn_ruwwad" VALUES(36,38,'36:38','Mặt trời di chuyển theo quỹ đạo đã được ấn định cho nó. Đó là sự định đoạt và an bài của Đấng Toàn Năng, Đấng Toàn Tri.');
INSERT INTO "vn_ruwwad" VALUES(36,39,'36:39','Và mặt trăng, TA đã ấn định cho nó các giai đoạn (tròn khuyết) cho đến khi nó trở lại hình dạng giống lưỡi liềm như cũ.');
INSERT INTO "vn_ruwwad" VALUES(36,40,'36:40','Mặt trời không đuổi kịp mặt trăng và ban đêm không qua mặt ban ngày; tất cả đều bơi trong quỹ đạo (đã được ấn định).');
INSERT INTO "vn_ruwwad" VALUES(36,41,'36:41','Có một dấu hiệu (về Allah) dành cho họ là việc TA đã chuyên chở con cháu của họ trên một con thuyền đầy.');
INSERT INTO "vn_ruwwad" VALUES(36,42,'36:42','TA đã tạo cho họ các loại phương tiện tương tự (con thuyền của Nuh) để họ dùng (di chuyển trên biển).');
INSERT INTO "vn_ruwwad" VALUES(36,43,'36:43','Nếu muốn, TA sẽ nhấn chìm họ và họ sẽ không thể kêu la cầu cứu với bất kỳ ai và họ sẽ không được cứu sống.');
INSERT INTO "vn_ruwwad" VALUES(36,44,'36:44','Trừ phi có sự khoan dung từ nơi TA và trừ phi TA chịu để cho hưởng lạc trong một thời hạn ấn định.');
INSERT INTO "vn_ruwwad" VALUES(36,45,'36:45','Khi có lời bảo họ: “Các ngươi hãy sợ những gì ở phía trước các ngươi (nơi cõi Đời Sau) và những gì ở phía sau các ngươi (nơi trần gian), mong rằng các ngươi được thương xót (nơi Allah).”');
INSERT INTO "vn_ruwwad" VALUES(36,46,'36:46','Không một dấu hiệu nào trong các dấu hiệu của Thượng Đế của họ đến với họ mà họ lại không quay lưng ngoảnh mặt.');
INSERT INTO "vn_ruwwad" VALUES(36,47,'36:47','Khi có lời bảo họ: “Các ngươi hãy chi dùng những gì Allah đã ban cấp cho các ngươi (cho con đường chính nghĩa của Ngài).” Những kẻ vô đức tin nói với những người có đức tin: “Lẽ nào chúng tôi phải nuôi ăn những kẻ mà nếu Allah muốn thì chính Ngài đã nuôi họ? Quả thật các người đang trong sự lầm lạc quá rõ ràng.”');
INSERT INTO "vn_ruwwad" VALUES(36,48,'36:48','Họ nói: “Vậy bao giờ lời hứa (về sự Phục Sinh) này sẽ xảy ra nếu các người (những người có đức tin) nói thật?”');
INSERT INTO "vn_ruwwad" VALUES(36,49,'36:49','Họ chỉ đang chờ đợi một tiếng Gầm duy nhất chụp bắt họ một cách đột ngột trong lúc họ đang bận rộn (với các sự việc của chốn trần gian).');
INSERT INTO "vn_ruwwad" VALUES(36,50,'36:50','Lúc đó, họ sẽ không kịp trăng trối và cũng sẽ không kịp quay về gặp gia đình của mình.');
INSERT INTO "vn_ruwwad" VALUES(36,51,'36:51','(Lúc) tiếng Còi (Phục Sinh) được thổi lên thì họ sẽ từ dưới mộ đi ra nhanh chân chạy đến trình diện Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(36,52,'36:52','(Lúc đó) họ (sẽ than), nói: “Thật khổ thân chúng tôi! Ai đã lôi chúng tôi dậy từ nơi yên nghỉ của chúng tôi thế này? Đây đúng là điều mà Đấng Arrahman đã hứa và các vị Thiên Sứ (của Ngài) đã xác nhận.”');
INSERT INTO "vn_ruwwad" VALUES(36,53,'36:53','Chỉ cần một tiếng gầm duy nhất thôi thì tất cả họ sẽ được triệu tập trình diện trước mặt TA.');
INSERT INTO "vn_ruwwad" VALUES(36,54,'36:54','Vào Ngày đó, không một linh hồn nào bị đối xử bất công bất cứ điều gì; và các ngươi sẽ được trả lại đầy đủ theo đúng với những điều mà các ngươi đã làm.');
INSERT INTO "vn_ruwwad" VALUES(36,55,'36:55','Quả thật, những người bạn của Thiên Đàng vào Ngày đó sẽ bận rộn với những hưởng thụ (không còn bận tâm đến điều gì khác).');
INSERT INTO "vn_ruwwad" VALUES(36,56,'36:56','Họ và những người vợ của họ sẽ nằm nghỉ tựa mình trên những chiếc tràng kỷ, dưới bóng mát (trong các Ngôi Vườn Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(36,57,'36:57','Trong đó, có đủ loại trái quả và mọi thứ mà họ yêu cầu.');
INSERT INTO "vn_ruwwad" VALUES(36,58,'36:58','(Và) có lời chào Salam từ Thượng Đế Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(36,59,'36:59','(Vào Ngày đó, sẽ có tiếng bảo những kẻ đa thần): “Hỡi những kẻ tội lỗi! Ngày hôm nay, các ngươi hãy dang ra xa (khỏi những người có đức tin).”');
INSERT INTO "vn_ruwwad" VALUES(36,60,'36:60','“Chẳng phải TA (Allah) đã bảo các ngươi, hỡi con cháu của Adam, chớ thờ phụng Shaytan bởi vì nó là kẻ thù công khai của các ngươi hay sao?”');
INSERT INTO "vn_ruwwad" VALUES(36,61,'36:61','“(Chẳng phải TA đã bảo các ngươi) hãy chỉ thờ phượng riêng TA. Đây là con đường ngay chính.”');
INSERT INTO "vn_ruwwad" VALUES(36,62,'36:62','“(Chẳng phải TA đã bảo các ngươi) rằng quả thật, nó (Shaytan) đã dẫn đa số các ngươi đi lạc. Vậy mà các ngươi không chịu hiểu!”');
INSERT INTO "vn_ruwwad" VALUES(36,63,'36:63','“Đây là Hỏa Ngục mà các ngươi đã được hứa.”');
INSERT INTO "vn_ruwwad" VALUES(36,64,'36:64','“Ngày hôm nay, các ngươi hãy đi vào Đó vì tội các ngươi đã phủ nhận.”');
INSERT INTO "vn_ruwwad" VALUES(36,65,'36:65','Vào Ngày đó, TA sẽ khóa miệng của chúng lại, bàn tay của chúng sẽ nói chuyện với TA còn chân của chúng sẽ làm chứng cho những gì mà chúng đã làm.');
INSERT INTO "vn_ruwwad" VALUES(36,66,'36:66','Nếu muốn, TA sẽ lấy đi cặp mắt của chúng rồi chúng tranh nhau qua cầu Sirat, xem chúng làm sao có thể nhìn thấy.');
INSERT INTO "vn_ruwwad" VALUES(36,67,'36:67','Và nếu muốn, TA sẽ biến hình dạng của chúng ngay tại chỗ để chúng không thể đi tới cũng không thể lùi lại.');
INSERT INTO "vn_ruwwad" VALUES(36,68,'36:68','Người nào mà TA cho y sống thọ, TA sẽ làm cho y trở lại (sự yếu đuối) trong việc tạo hóa. Chẳng lẽ họ không hiểu hay ư?');
INSERT INTO "vn_ruwwad" VALUES(36,69,'36:69','TA đã không dạy Y (Muhammad) thơ phú; và điều đó không nên đối với Y (bởi có thể các ngươi cho rằng Y là một nhà thơ). (Những gì TA dạy cho Y) thật ra chỉ là Lời Nhắc Nhở và là một Thiên Kinh Qur’an minh bạch.');
INSERT INTO "vn_ruwwad" VALUES(36,70,'36:70','Mục đích để Y cảnh báo những ai (mà trái tim của họ đang) sống (với chân lý) và để những kẻ vô đức tin không còn có lý do biện minh (khi đối diện với sự trừng phạt).');
INSERT INTO "vn_ruwwad" VALUES(36,71,'36:71','Lẽ nào họ đã không thấy việc TA đã tạo ra cho họ các loài gia súc mà họ làm chủ bằng chính đôi tay của TA?!');
INSERT INTO "vn_ruwwad" VALUES(36,72,'36:72','TA đã bắt các loài gia súc phục tùng họ; bởi thế, một số loài họ dùng để cưỡi và một số khác họ ăn thịt.');
INSERT INTO "vn_ruwwad" VALUES(36,73,'36:73','Và nơi các loài gia súc đó, họ có nhiều nguồn lợi khác; và có cả nguồn thức uống dành cho họ. Thế, họ không biết ơn (TA) hay sao?');
INSERT INTO "vn_ruwwad" VALUES(36,74,'36:74','(Những kẻ đa thần) đã thờ phượng các thần linh khác ngoài Allah với hy vọng được chúng phù hộ và trợ giúp.');
INSERT INTO "vn_ruwwad" VALUES(36,75,'36:75','Nhưng các thần linh đó không thể phù hộ và trợ giúp được gì cho họ. Họ và chúng, tất cả đều sẽ được triệu tập (để đối diện với sự trừng phạt).');
INSERT INTO "vn_ruwwad" VALUES(36,76,'36:76','Cho nên, Ngươi (hỡi Thiên Sứ) chớ để cho lời lẽ của họ làm Ngươi buồn phiền. Quả thật, TA biết rõ những gì họ che giấu và những gì họ công khai.');
INSERT INTO "vn_ruwwad" VALUES(36,77,'36:77','Lẽ nào con người (phủ nhận sự Phục Sinh) không nhận thấy việc TA đã tạo hóa y từ một giọt tinh dịch nên đã công khai tranh cãi?!');
INSERT INTO "vn_ruwwad" VALUES(36,78,'36:78','Y đưa ra hình ảnh thí dụ cho TA nhưng lại quên mất sự tạo hóa của mình (khi y đã được TA tạo ra từ cái không là gì). Y bảo: “Ai có thể làm sống lại những khúc xương khi chúng đã rã mục?”');
INSERT INTO "vn_ruwwad" VALUES(36,79,'36:79','Ngươi (Thiên Sứ) hãy nói: “Đấng đã tạo hóa chúng lúc ban đầu sẽ làm cho chúng sống lại. Bởi vì, Ngài thông toàn tất cả việc tạo hóa.”');
INSERT INTO "vn_ruwwad" VALUES(36,80,'36:80','“Đấng đã làm ra lửa cho các ngươi từ một loại cây tươi mà các ngươi dùng để nhúm lửa.”');
INSERT INTO "vn_ruwwad" VALUES(36,81,'36:81','Lẽ nào Đấng đã tạo hóa ra các tầng trời và trái đất lại không có khả năng tạo hóa ra cái giống như họ ư? Không đâu, Ngài là Đấng Tạo Hóa Toàn Tri.');
INSERT INTO "vn_ruwwad" VALUES(36,82,'36:82','Quả thật, khi Ngài muốn điều gì, Ngài chỉ cần phán “Hãy thành!” thì tức khắc nó sẽ thành (giống như ý của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(36,83,'36:83','Bởi thế, thật Quang Vinh thay Đấng mà quyền thống trị vạn vật nằm trong Tay Ngài; và (rồi đây) các ngươi sẽ được đưa trở về trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(37,1,'37:1','Thề bởi các Thiên Thần đứng thành hàng chỉnh tề (trong thờ phượng Allah).');
INSERT INTO "vn_ruwwad" VALUES(37,2,'37:2','Thề bởi các Thiên Thần di chuyển các đám mây mưa (theo ý chỉ của Allah).');
INSERT INTO "vn_ruwwad" VALUES(37,3,'37:3','Thề bởi các Thiên Thần đọc Lời Nhắc nhở (Lời phán của Allah).');
INSERT INTO "vn_ruwwad" VALUES(37,4,'37:4','Thượng Đế của các ngươi thực sự chỉ có một Đấng duy nhất.');
INSERT INTO "vn_ruwwad" VALUES(37,5,'37:5','(Đó là) Thượng Đế của các tầng trời, trái đất và vạn vật giữa trời đất và là Thượng Đế của các hướng (mặt trời mọc) ở phía Đông.');
INSERT INTO "vn_ruwwad" VALUES(37,6,'37:6','Quả thật! TA (Allah) đã làm đẹp tầng trời hạ giới bằng các vì sao.');
INSERT INTO "vn_ruwwad" VALUES(37,7,'37:7','Và (TA dùng các vì sao để) bảo vệ (tầng trời hạ giới) khỏi từng tên Shaytan phản nghịch.');
INSERT INTO "vn_ruwwad" VALUES(37,8,'37:8','(Những tên Shaytan muốn nghe trộm) sẽ không nghe được hội nghị trên cao và chúng sẽ bị ném từ mọi phía.');
INSERT INTO "vn_ruwwad" VALUES(37,9,'37:9','Chúng bị đuổi đi xa. Và (vào Ngày Sau) chúng sẽ bị trừng phạt đời đời kiếp kiếp.');
INSERT INTO "vn_ruwwad" VALUES(37,10,'37:10','Trừ tên nào nhanh nhẹn chụp lấy được (tin tức từ cuộc nói chuyện của các Thiên Thần) rồi chạy đi thì sẽ bị rượt đuổi bằng một hòn lửa sáng rực.');
INSERT INTO "vn_ruwwad" VALUES(37,11,'37:11','Ngươi (Thiên Sứ) hãy hỏi (những kẻ thờ đa thần phủ nhận sự Phục Sinh) phải chăng họ khó tạo hay ai khác mà TA đã tạo khó hơn. Quả thật, TA đã tạo họ từ một loại đất sét nhão, dính.');
INSERT INTO "vn_ruwwad" VALUES(37,12,'37:12','Không, Ngươi đã ngạc nhiên trước những gì họ chế giễu (trong việc phủ nhận sự Phục Sinh).');
INSERT INTO "vn_ruwwad" VALUES(37,13,'37:13','Khi được nhắc nhở thì họ không lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(37,14,'37:14','Khi họ thấy một dấu lạ nào đó (từ Thiên Sứ) thì họ mang nó ra giễu cợt.');
INSERT INTO "vn_ruwwad" VALUES(37,15,'37:15','Họ nói: “Đấy rõ ràng chỉ là trò ảo thuật!”');
INSERT INTO "vn_ruwwad" VALUES(37,16,'37:16','“Lẽ nào khi chúng tôi chết đi và đã biến thành đất bụi và xương tàn rồi chúng tôi thực sự được dựng sống lại?!”');
INSERT INTO "vn_ruwwad" VALUES(37,17,'37:17','“Kể cả tổ tiên xa xưa của chúng tôi, (họ cũng sẽ được cho sống lại sao)?”');
INSERT INTO "vn_ruwwad" VALUES(37,18,'37:18','Ngươi (hỡi Thiên Sứ) hãy nói (với họ): “Vâng, đúng vậy. Và các ngươi sẽ bị hạ nhục.”');
INSERT INTO "vn_ruwwad" VALUES(37,19,'37:19','Quả thật, chỉ một tiếng Gầm duy nhất thì lúc đó (tất cả) họ sẽ ngóng nhìn (những gì diễn ra của Ngày Phán Xét).');
INSERT INTO "vn_ruwwad" VALUES(37,20,'37:20','Họ sẽ nói: “Thật khốn khổ thay cho chúng ta, đây đúng thật là Ngày Phán Xét Cuối Cùng!”');
INSERT INTO "vn_ruwwad" VALUES(37,21,'37:21','(Rồi có tiếng bảo họ): “Đây chính là Ngày Phân Loại mà các ngươi đã từng phủ nhận.”');
INSERT INTO "vn_ruwwad" VALUES(37,22,'37:22','“Hãy tập trung những kẻ làm điều sai quấy cùng với bạn đồng hành của chúng và cả những vật mà chúng đã thờ phượng,”');
INSERT INTO "vn_ruwwad" VALUES(37,23,'37:23','“Thay vì Allah. bởi thế các ngươi hãy đưa chúng đến con đường tới Hỏa Ngục.”');
INSERT INTO "vn_ruwwad" VALUES(37,24,'37:24','“Nhưng các ngươi hãy bắt chúng dừng lại bởi chúng còn phải bị tra hỏi.”');
INSERT INTO "vn_ruwwad" VALUES(37,25,'37:25','(Rồi có tiếng bảo họ): “Các ngươi có chuyện gì mà sao không chịu giúp đỡ nhau?”');
INSERT INTO "vn_ruwwad" VALUES(37,26,'37:26','Không (chúng sẽ không làm được gì cho nhau) bởi Ngày đó chúng sẽ phải tự nạp mình (trước sự trừng phạt).');
INSERT INTO "vn_ruwwad" VALUES(37,27,'37:27','Rồi chúng quay lại trách móc nhau.');
INSERT INTO "vn_ruwwad" VALUES(37,28,'37:28','Chúng nói (với những kẻ mà chúng đi theo): “Quả thật, các người là những kẻ đã đến gặp chúng tôi từ bên phải.”');
INSERT INTO "vn_ruwwad" VALUES(37,29,'37:29','(Những kẻ mà chúng đi theo) nói (với chúng): “Không, các người mới là những kẻ không có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(37,30,'37:30','“Chúng tôi đâu có quyền bắt các người nghe theo. Tại các người là đám người vượt quá giới hạn mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(37,31,'37:31','“Giờ đây Lời Thượng Đế của chúng ta đã chứng thực với chúng ta. Chúng ta đành phải nếm hình phạt (của Ngài) mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(37,32,'37:32','“Chúng tôi đã dẫn các người đi lạc và chúng tôi cũng là những kẻ lầm lạc.”');
INSERT INTO "vn_ruwwad" VALUES(37,33,'37:33','Cho nên, vào Ngày đó chúng sẽ cùng nhau san sẻ hình phạt.');
INSERT INTO "vn_ruwwad" VALUES(37,34,'37:34','TA (Allah) thực sự sẽ xử trí những kẻ tội lỗi đúng như thế.');
INSERT INTO "vn_ruwwad" VALUES(37,35,'37:35','Quả thật, khi chúng được nhắc ‘Không có Thượng Đế nào khác ngoài Allah’ thì chúng ngạo mạn (từ chối).');
INSERT INTO "vn_ruwwad" VALUES(37,36,'37:36','Chúng bảo: “Chẳng lẽ chúng ta lại đi bỏ các thần linh của chúng ta chỉ vì một gã thi sĩ điên khùng ư?”');
INSERT INTO "vn_ruwwad" VALUES(37,37,'37:37','Không, (Muhammad không phải như các ngươi nói)! Y (đích thực) đã mang Chân Lý đến và xác nhận lại các Thiên Sứ (về những gì mà họ đã mang đến trước đó).');
INSERT INTO "vn_ruwwad" VALUES(37,38,'37:38','Quả thật, (rồi đây vào Ngày Phán Xét) các ngươi (hỡi những kẻ đa thần) sẽ phải nếm hình phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(37,39,'37:39','Và các ngươi sẽ bị trừng phạt chỉ bởi những điều (tội lỗi) mà các ngươi đã làm.');
INSERT INTO "vn_ruwwad" VALUES(37,40,'37:40','Trừ những bề tôi chân thành của Allah.');
INSERT INTO "vn_ruwwad" VALUES(37,41,'37:41','Họ sẽ có được bổng lộc được biết rõ (nơi Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(37,42,'37:42','(Trong đó, có đủ) loại trái quả, và họ là những người được ban cho vinh dự.');
INSERT INTO "vn_ruwwad" VALUES(37,43,'37:43','(Họ sẽ ở) trong những Ngôi Vườn tiện nghi.');
INSERT INTO "vn_ruwwad" VALUES(37,44,'37:44','Tựa mình trên những chiếc tràng kỷ, mặt đối mặt.');
INSERT INTO "vn_ruwwad" VALUES(37,45,'37:45','Những cốc rượu được múc từ một con suối sẽ được chuyển vòng quanh họ.');
INSERT INTO "vn_ruwwad" VALUES(37,46,'37:46','Nó trong vắt, ngon tuyệt, làm cho người uống sảng khoái.');
INSERT INTO "vn_ruwwad" VALUES(37,47,'37:47','Nó không làm cho họ nhức đầu và choáng váng.');
INSERT INTO "vn_ruwwad" VALUES(37,48,'37:48','Bên cạnh họ sẽ là những nàng trinh nữ tuyệt đẹp với đôi mắt to trữ tình đầy quyến rũ, chỉ biết ngắm nhìn (chồng của mình).');
INSERT INTO "vn_ruwwad" VALUES(37,49,'37:49','Họ giống như những quả trứng non được cất giữ kỹ càng.');
INSERT INTO "vn_ruwwad" VALUES(37,50,'37:50','Họ ngồi hàn huyên chuyện trò (nhắc lại chuyện xưa trên cõi trần).');
INSERT INTO "vn_ruwwad" VALUES(37,51,'37:51','Một người trong số họ nói: “Quả thật, tôi có một người bạn.”');
INSERT INTO "vn_ruwwad" VALUES(37,52,'37:52','“Người đó bảo (tôi): Anh thực sự tin (vào sự Phục Sinh) sao?”');
INSERT INTO "vn_ruwwad" VALUES(37,53,'37:53','“Lẽ nào khi chúng ta chết đi và đã trở thành đất bụi và xương tàn, chúng ta sẽ (được dựng sống lại) để chịu sự thưởng phạt?”');
INSERT INTO "vn_ruwwad" VALUES(37,54,'37:54','(Người kể) nói (với những người bạn của mình đang nghe): “Các anh hãy (cùng tôi) nhìn xuống xem (người đó hiện đang ở đâu)?”');
INSERT INTO "vn_ruwwad" VALUES(37,55,'37:55','Thế là y nhìn xuống và thấy người bạn (vô đức tin) của mình đang ở trong Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(37,56,'37:56','Y nói: “Thề bởi Allah! Suýt nữa anh đã hủy hoại tôi rồi!”');
INSERT INTO "vn_ruwwad" VALUES(37,57,'37:57','“Nếu không nhờ hồng ân của Thượng Đế của tôi thì chắc chắn tôi đã là một trong những người bị bắt dẫn đến (Hỏa Ngục).”');
INSERT INTO "vn_ruwwad" VALUES(37,58,'37:58','“Chúng tôi (cư dân Thiên Đàng) sẽ không còn đối mặt với cái chết nữa?”');
INSERT INTO "vn_ruwwad" VALUES(37,59,'37:59','“Chỉ có cái chết đầu tiên của chúng tôi mà thôi và chúng tôi sẽ không bị trừng phạt.”');
INSERT INTO "vn_ruwwad" VALUES(37,60,'37:60','Quả thật, đó là một sự thắng lợi vĩ đại!');
INSERT INTO "vn_ruwwad" VALUES(37,61,'37:61','Vì (phần thưởng) như thế này nên những người làm việc hãy cố gắng làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(37,62,'37:62','(Sự thắng lợi) đó tốt hay cây Zaqqum tốt hơn?');
INSERT INTO "vn_ruwwad" VALUES(37,63,'37:63','Quả thật, TA (Allah) đã tạo ra nó (cây Zaqqum) là để trừng phạt những kẻ làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(37,64,'37:64','Quả thật, nó là một loại cây mọc từ dưới đáy của Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(37,65,'37:65','Trái của nó trông giống như đầu của Shaytan.');
INSERT INTO "vn_ruwwad" VALUES(37,66,'37:66','Thật sự, chúng (những kẻ vô đức tin) sẽ ăn nó và nhét nó vào đầy bụng.');
INSERT INTO "vn_ruwwad" VALUES(37,67,'37:67','(Sau khi chúng nhét đầy bụng với trái Zaqqum), chúng sẽ được cho uống từ một loại nước cực sôi.');
INSERT INTO "vn_ruwwad" VALUES(37,68,'37:68','Rồi chúng được đưa trở lại Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(37,69,'37:69','Quả thật, chúng thấy cha mẹ của chúng là những kẻ lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(37,70,'37:70','Nhưng chúng vẫn ngoan cố đi theo vết xe đổ của họ.');
INSERT INTO "vn_ruwwad" VALUES(37,71,'37:71','Quả thật đa số người xưa thời trước chúng đã lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(37,72,'37:72','Quả thật, TA (Allah) đã gửi đến với họ những vị cảnh báo.');
INSERT INTO "vn_ruwwad" VALUES(37,73,'37:73','Do đó, Ngươi (hỡi Thiên Sứ) hãy nhìn xem kết cuộc của những kẻ đã được cảnh báo đã bị ra sao.');
INSERT INTO "vn_ruwwad" VALUES(37,74,'37:74','Trừ các bề tôi chân thành của Allah (mới được thoát khỏi sự trừng phạt của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(37,75,'37:75','Quả thật, Nuh đã cầu nguyện TA, (TA đã đáp lại lời nguyện cầu của Y) bởi TA là Đấng Đáp Lại Ưu Việt.');
INSERT INTO "vn_ruwwad" VALUES(37,76,'37:76','TA đã giải cứu Y và gia đình của Y thoát khỏi đại họa (trận đại hồng thủy).');
INSERT INTO "vn_ruwwad" VALUES(37,77,'37:77','TA đã làm cho dòng dõi của Y sống sót.');
INSERT INTO "vn_ruwwad" VALUES(37,78,'37:78','TA đã để lại cho Y nơi hậu thế (lời chúc phúc).');
INSERT INTO "vn_ruwwad" VALUES(37,79,'37:79','(Đó là) lời chúc phúc Bằng An cho Nuh trên thế gian.');
INSERT INTO "vn_ruwwad" VALUES(37,80,'37:80','Quả thật, TA trọng thưởng những người làm tốt đúng như thế.');
INSERT INTO "vn_ruwwad" VALUES(37,81,'37:81','Bởi Y đích thực là một trong những bề tôi có đức tin của TA.');
INSERT INTO "vn_ruwwad" VALUES(37,82,'37:82','Rồi TA đã nhấn chìm đám người (tội lỗi) kia.');
INSERT INTO "vn_ruwwad" VALUES(37,83,'37:83','Quả thật, Ibrahim đã thực sự thuộc về tôn giáo của Y (Nuh).');
INSERT INTO "vn_ruwwad" VALUES(37,84,'37:84','Khi (Ibrahim) đến với Thượng Đế của Y bằng một trái tim trong sạch (khỏi vết bẩn của Shirk).');
INSERT INTO "vn_ruwwad" VALUES(37,85,'37:85','Khi (Ibrahim) nói với phụ thân và người dân của mình: “Các người đang thờ phượng gì thế kia?!”');
INSERT INTO "vn_ruwwad" VALUES(37,86,'37:86','“Lẽ nào các người muốn thờ phượng các thần linh ngụy tạo thay vì Allah ư?”');
INSERT INTO "vn_ruwwad" VALUES(37,87,'37:87','“Vậy các người nghĩ sao về Thượng Đế của vũ trụ và vạn vật?”');
INSERT INTO "vn_ruwwad" VALUES(37,88,'37:88','Rồi (Ibrahim) ngước lên (trời) nhìn các vì sao.');
INSERT INTO "vn_ruwwad" VALUES(37,89,'37:89','Rồi (Ibrahim) bảo: “Tôi không khỏe (nên không thể đi chơi lễ hội cùng mọi người).”');
INSERT INTO "vn_ruwwad" VALUES(37,90,'37:90','Thế là mọi người ra đi, bỏ lại (Ibrahim một mình).');
INSERT INTO "vn_ruwwad" VALUES(37,91,'37:91','Thế là (Ibrahim) đã lén đi gặp các thần linh của họ. Y hỏi chúng: “Sao các ngươi không chịu ăn?”');
INSERT INTO "vn_ruwwad" VALUES(37,92,'37:92','“Có chuyện gì với các ngươi, sao các ngươi không nói chuyện?”');
INSERT INTO "vn_ruwwad" VALUES(37,93,'37:93','Rồi Y nhảy thẳng tới đánh bể chúng bằng tay phải.');
INSERT INTO "vn_ruwwad" VALUES(37,94,'37:94','Sau đó, (người dân) vội vã chạy đến gặp Y.');
INSERT INTO "vn_ruwwad" VALUES(37,95,'37:95','(Ibrahim) nói: “Lẽ nào các người lại thờ những bức tượng do chính các người nắn tạc ra chúng?”');
INSERT INTO "vn_ruwwad" VALUES(37,96,'37:96','“Trong khi Allah tạo ra các người và những thứ mà các người làm ra.”');
INSERT INTO "vn_ruwwad" VALUES(37,97,'37:97','(Người dân của Ibrahim) bảo: “Chúng ta hãy xây cho nó một lò lửa rồi ném nó vào trong lửa đang cháy!”');
INSERT INTO "vn_ruwwad" VALUES(37,98,'37:98','Họ muốn mưu hại Ibrahim nhưng TA đã làm cho họ thất bại nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(37,99,'37:99','(Ibrahim) nói: “Ta sẽ đi gặp Thượng Đế của Ta. Ngài sẽ hướng dẫn ta.”');
INSERT INTO "vn_ruwwad" VALUES(37,100,'37:100','“Lạy Thượng Đế của bề tôi! Xin Ngài ban cho bề tôi một đứa con đức hạnh.”');
INSERT INTO "vn_ruwwad" VALUES(37,101,'37:101','Thế là, TA đã ban cho Y tin mừng về một đứa con trai (Isma’il) biết kiên nhẫn chịu đựng.');
INSERT INTO "vn_ruwwad" VALUES(37,102,'37:102','Đến khi đứa con đến tuổi biết chạy, (Ibrahim) nói (với đứa con của mình): “Này con trai yêu của cha! Cha nằm mộng thấy rằng cha cần phải giết tế con. Thế con nghĩ sao?” Đứa con đáp: “Thưa cha! Cha cứ làm theo lệnh truyền rồi cha sẽ thấy con là một đứa con kiên nhẫn, Insha-Allah.”');
INSERT INTO "vn_ruwwad" VALUES(37,103,'37:103','Thế rồi, khi hai cha con cùng nạp mình thần phục mệnh lệnh (của Allah), người cha để đứa con nằm nghiêng (chuẩn bị giết tế).');
INSERT INTO "vn_ruwwad" VALUES(37,104,'37:104','(Ngay lúc đó,) TA (Allah) đã gọi Y: “Này hỡi Ibrahim!”');
INSERT INTO "vn_ruwwad" VALUES(37,105,'37:105','“Ngươi thực sự đã chứng thực điều trong giấc mộng.” Quả thật! TA ban thưởng cho những người làm tốt như thế.');
INSERT INTO "vn_ruwwad" VALUES(37,106,'37:106','Quả thật đây quả là một cuộc thử thách công khai cho (Ibrahim).');
INSERT INTO "vn_ruwwad" VALUES(37,107,'37:107','TA đã chuộc mạng (con trai của Ibrahim) bằng một con vật tế vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(37,108,'37:108','TA đã để lại (Ibrahim) nơi hậu thế (lời chúc phúc).');
INSERT INTO "vn_ruwwad" VALUES(37,109,'37:109','(Đó là) lời chúc phúc Bằng an cho Ibrahim.');
INSERT INTO "vn_ruwwad" VALUES(37,110,'37:110','TA ban thưởng cho những người làm tốt đúng như thế.');
INSERT INTO "vn_ruwwad" VALUES(37,111,'37:111','Quả thật, (Ibrahim) là một trong những bề tôi có đức tin của TA.');
INSERT INTO "vn_ruwwad" VALUES(37,112,'37:112','TA đã mang đến cho Y tin mừng về (đứa con trai) Is-haaq, (sau này sẽ trở thành) một vị Nabi đức hạnh.');
INSERT INTO "vn_ruwwad" VALUES(37,113,'37:113','TA đã ban phúc cho Y và Is-haaq, tuy nhiên, trong con cháu của hai người họ có một số làm tốt và có một số đã công khai bất công với bản thân mình.');
INSERT INTO "vn_ruwwad" VALUES(37,114,'37:114','Quả thật, TA đã ban ân cho Musa và Harun (với sứ mạng Nabi).');
INSERT INTO "vn_ruwwad" VALUES(37,115,'37:115','TA đã giải cứu hai người họ và đám dân của họ (Israel) thoát khỏi đại họa (sự đuổi cùng giết tận của Pha-ra-ông).');
INSERT INTO "vn_ruwwad" VALUES(37,116,'37:116','TA đã trợ giúp hai người họ giành lấy chiến thắng.');
INSERT INTO "vn_ruwwad" VALUES(37,117,'37:117','TA đã ban cho hai người họ một Kinh Sách sáng tỏ (Kinh Tawrah - Cựu Ước).');
INSERT INTO "vn_ruwwad" VALUES(37,118,'37:118','TA đã hướng dẫn hai người họ con đường Ngay Thẳng (dẫn đến sự hài lòng của TA).');
INSERT INTO "vn_ruwwad" VALUES(37,119,'37:119','TA đã để lại hai người họ nơi hậu thế (lời chúc phúc).');
INSERT INTO "vn_ruwwad" VALUES(37,120,'37:120','(Đó là) lời chúc bằng an cho Musa và Harun.');
INSERT INTO "vn_ruwwad" VALUES(37,121,'37:121','TA thực sự ban thưởng cho những người làm tốt đúng như thế.');
INSERT INTO "vn_ruwwad" VALUES(37,122,'37:122','Quả thật hai người họ thuộc những bề tôi có đức tin của TA.');
INSERT INTO "vn_ruwwad" VALUES(37,123,'37:123','Và Ilyas (Elias) đích thực là một trong các vị Thiên Sứ (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(37,124,'37:124','Khi Y bảo người dân của Y: “Các người không sợ Allah sao?”');
INSERT INTO "vn_ruwwad" VALUES(37,125,'37:125','“Lẽ nào các người cầu nguyện Ba''la (thần linh bục tượng) và bỏ rơi Đấng Tạo Hóa Ưu Việt?!”');
INSERT INTO "vn_ruwwad" VALUES(37,126,'37:126','“Allah là Thượng Đế của các người và cũng là Thượng Đế của tổ tiên xa xưa của các ngươi.”');
INSERT INTO "vn_ruwwad" VALUES(37,127,'37:127','Nhưng (đám dân của Ilyas) đã phủ nhận Y. Cho nên, họ sẽ phải bị dẫn đến (chỗ trừng phạt).');
INSERT INTO "vn_ruwwad" VALUES(37,128,'37:128','Trừ các bề tôi chân thành của Allah.');
INSERT INTO "vn_ruwwad" VALUES(37,129,'37:129','TA đã để lại Y nơi hậu thế (lời chúc phúc).');
INSERT INTO "vn_ruwwad" VALUES(37,130,'37:130','(Đó là) lời chúc Bằng an cho Ilyas.');
INSERT INTO "vn_ruwwad" VALUES(37,131,'37:131','TA thực sự ban thưởng cho những người làm tốt đúng như thế.');
INSERT INTO "vn_ruwwad" VALUES(37,132,'37:132','(Ilyas) đích thực là một những bề tôi có đức tin của TA.');
INSERT INTO "vn_ruwwad" VALUES(37,133,'37:133','Và Lut đích thực là một trong các vị Sứ Giả (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(37,134,'37:134','Khi TA giải cứu (Lut) và toàn bộ gia đình của Y.');
INSERT INTO "vn_ruwwad" VALUES(37,135,'37:135','Trừ vợ của Y (là kẻ vô đức tin), nữ ta sẽ ở cùng đám người bị trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(37,136,'37:136','Sau đó, TA đã hủy diệt đám người còn lại.-');
INSERT INTO "vn_ruwwad" VALUES(37,137,'37:137','Quả thật, các ngươi (hỡi dân Makkah) thường đi ngang qua (chỗ của) họ vào buổi sáng,');
INSERT INTO "vn_ruwwad" VALUES(37,138,'37:138','Và ban đêm. Nhưng sao các ngươi không chịu suy ngẫm?');
INSERT INTO "vn_ruwwad" VALUES(37,139,'37:139','Và Yunus đích thực là một trong các vị Sứ Giả (của Allah).');
INSERT INTO "vn_ruwwad" VALUES(37,140,'37:140','Khi (Yunus) bỏ chạy đến một chiếc tàu chở đầy (người và đồ đạc).');
INSERT INTO "vn_ruwwad" VALUES(37,141,'37:141','(Yunus) đã rút thăm và Y đã thua cuộc.(');
INSERT INTO "vn_ruwwad" VALUES(37,142,'37:142','Rồi một con cá đã nuốt chửng Y vì Y đã phạm một điều đáng trách.(');
INSERT INTO "vn_ruwwad" VALUES(37,143,'37:143','Nếu như (Yunus) không phải là người (biết ăn năn sám hối và) tán dương Allah,');
INSERT INTO "vn_ruwwad" VALUES(37,144,'37:144','Thì Y đã phải ở trong bụng của (con cá) cho đến ngày Phục Sinh.');
INSERT INTO "vn_ruwwad" VALUES(37,145,'37:145','Rồi TA đã cho (Yunus) ra khỏi bụng con cá và dạt vào một bờ biển hoang trong tình trạng yếu ớt.');
INSERT INTO "vn_ruwwad" VALUES(37,146,'37:146','TA đã cho một loại cây thuộc họ bầu (bí) mọc bao phủ thân người Y.');
INSERT INTO "vn_ruwwad" VALUES(37,147,'37:147','(Sau đó) TA đã cử Y đến với một trăm ngàn người dân hoặc nhiều hơn.');
INSERT INTO "vn_ruwwad" VALUES(37,148,'37:148','(Đám dân đó), họ đã có đức tin nên TA đã cho họ hưởng lạc (sống trên thế gian) đến một thời gian (ấn định).');
INSERT INTO "vn_ruwwad" VALUES(37,149,'37:149','Ngươi (Thiên Sứ) hãy hỏi (những kẻ thờ đa thần) phải chăng con gái thuộc về Thượng Đế của Ngươi còn con trai là của họ?');
INSERT INTO "vn_ruwwad" VALUES(37,150,'37:150','Hoặc phải chăng họ đã tận mắt chứng kiến việc TA đã tạo hóa các Thiên Thần đều là giống cái?');
INSERT INTO "vn_ruwwad" VALUES(37,151,'37:151','Họ quả thật đã nói dối khi bảo:');
INSERT INTO "vn_ruwwad" VALUES(37,152,'37:152','“Allah đã sinh con.” Họ đúng thực là những kẻ nói dối (phạm thượng).');
INSERT INTO "vn_ruwwad" VALUES(37,153,'37:153','Lẽ nào Ngài chọn con gái hơn con trai?');
INSERT INTO "vn_ruwwad" VALUES(37,154,'37:154','Các ngươi phân xử sao thế?');
INSERT INTO "vn_ruwwad" VALUES(37,155,'37:155','Lẽ nào các ngươi không nhớ (các ngươi đã sai thế nào) hay sao?');
INSERT INTO "vn_ruwwad" VALUES(37,156,'37:156','Hoặc lẽ nào các ngươi có đủ thẩm quyền rõ ràng chăng?');
INSERT INTO "vn_ruwwad" VALUES(37,157,'37:157','Nếu các ngươi nói thật thì các ngươi hãy mang kinh sách của các ngươi đến xem nào?');
INSERT INTO "vn_ruwwad" VALUES(37,158,'37:158','(Những kẻ thờ đa thần) đã bịa đặt mối quan hệ thân thuộc giữa Ngài và loài Jinn trong lúc loài Jinn biết rõ việc chúng sẽ phải trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(37,159,'37:159','Quang Vinh và trong sạch thay Allah, (sự tối cao và siêu việt của Ngài vượt hẳn) những điều mà họ mô tả về Ngài!');
INSERT INTO "vn_ruwwad" VALUES(37,160,'37:160','Trừ các bề tôi chân thành của Allah.');
INSERT INTO "vn_ruwwad" VALUES(37,161,'37:161','Các ngươi (những kẻ thờ đa thần) và những thứ mà các ngươi thờ phượng.');
INSERT INTO "vn_ruwwad" VALUES(37,162,'37:162','Các ngươi không thể quyến dụ được ai (lệch khỏi tôn giáo chân lý).');
INSERT INTO "vn_ruwwad" VALUES(37,163,'37:163','Ngoại trừ những ai muốn đi vào Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(37,164,'37:164','(Các Thiên Thần nói): “Không ai trong chúng tôi lại không có một vị trí được ấn định (trong thờ phượng Allah và tuân lệnh Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(37,165,'37:165','“Quả thật, chúng tôi đứng thành hàng.”');
INSERT INTO "vn_ruwwad" VALUES(37,166,'37:166','“Và quả thật, chúng tôi luôn tán dương Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(37,167,'37:167','Và dù (những kẻ thờ đa thần ở Makkah) đã thường nói:');
INSERT INTO "vn_ruwwad" VALUES(37,168,'37:168','“Phải chi chúng tôi có được một Lời Nhắc Nhở (những kinh sách chẳng hạn như Kinh Tawrah) từ những người thời trước,”');
INSERT INTO "vn_ruwwad" VALUES(37,169,'37:169','“Thì quả thật chúng tôi đã là những bề tôi chân thành của Allah rồi.”');
INSERT INTO "vn_ruwwad" VALUES(37,170,'37:170','(Và khi Kinh Qur’an được mang đến) thì họ phủ nhận Nó, rồi đây họ sẽ sớm biết (hình phạt đang đợi họ ở Ngày Phán Xét)!');
INSERT INTO "vn_ruwwad" VALUES(37,171,'37:171','Quả thật, Lời Phán của TA đã được phán cho các bề tôi của TA, họ là các vị Sứ Giả.');
INSERT INTO "vn_ruwwad" VALUES(37,172,'37:172','Quả thật, họ là những người được trợ giúp (từ nơi TA).');
INSERT INTO "vn_ruwwad" VALUES(37,173,'37:173','Quả thật, quân binh của TA luôn giành chiến thắng.');
INSERT INTO "vn_ruwwad" VALUES(37,174,'37:174','Do đó, Ngươi (Thiên Sứ) hãy bỏ mặc họ cho tới khi thời hạn (đã đến).');
INSERT INTO "vn_ruwwad" VALUES(37,175,'37:175','Và chờ xem (sự trừng phạt dành cho họ), rồi đây họ sẽ sớm thấy (sự trừng phạt đó)!');
INSERT INTO "vn_ruwwad" VALUES(37,176,'37:176','Có phải họ hối thúc sự trừng phạt của TA?');
INSERT INTO "vn_ruwwad" VALUES(37,177,'37:177','Nhưng khi (sự trừng phạt của TA) rơi xuống sân nhà của họ thì buổi sáng sẽ là hoàn cảnh thê thảm cho những kẻ đã được báo trước.');
INSERT INTO "vn_ruwwad" VALUES(37,178,'37:178','Cho nên, Ngươi (hỡi Thiên Sứ) hãy bỏ mặc họ cho tới khi thời hạn (đã đến).');
INSERT INTO "vn_ruwwad" VALUES(37,179,'37:179','Và chờ xem (sự trừng phạt dành cho họ), rồi đây họ sẽ sớm thấy (sự trừng phạt đó)!');
INSERT INTO "vn_ruwwad" VALUES(37,180,'37:180','Quang Vinh và tối cao thay Thượng Đế của Ngươi! Thượng Đế của Quyền Lực! Ngài siêu việt vượt xa những điều mà họ đã mô tả (về Ngài).');
INSERT INTO "vn_ruwwad" VALUES(37,181,'37:181','Sự Bằng an dành cho các vị Sứ Giả (vinh dự của Allah)!');
INSERT INTO "vn_ruwwad" VALUES(37,182,'37:182','Alhamdulillah, Đấng Chủ Tể của vũ trụ và vạn vật.');
INSERT INTO "vn_ruwwad" VALUES(38,1,'38:1','Sad. Thề bởi Qur’an chứa đựng Lời Nhắc Nhở.');
INSERT INTO "vn_ruwwad" VALUES(38,2,'38:2','Tuy nhiên, những kẻ vô đức tin cứ ngoan cố và ngạo mạn.');
INSERT INTO "vn_ruwwad" VALUES(38,3,'38:3','Đã có không biết bao thế hệ trước họ bị TA tiêu diệt! Họ cầu xin sự cứu rỗi (lúc đối mặt với sự trừng phạt) nhưng đã quá muộn.');
INSERT INTO "vn_ruwwad" VALUES(38,4,'38:4','Họ ngạc nhiên về việc một người cảnh báo xuất thân từ họ đến với họ. Những kẻ vô đức tin nói: “Đây là một tên phù thủy, một kẻ nói dối.”');
INSERT INTO "vn_ruwwad" VALUES(38,5,'38:5','“Lẽ nào hắn (Muhammad) nhập tất cả các thần linh thành một Thượng Ðế duy nhất, đây quả là một điều hết sức quái gở?”');
INSERT INTO "vn_ruwwad" VALUES(38,6,'38:6','(Những kẻ cầm đầu trong bọn họ nói:) “Các người hãy tiếp tục bước đi (trên con đường của các người), các người hãy vẫn bám chặt vào các thần linh của các người. Bởi đây chỉ là một mưu đồ (của hắn - Muhammad).”');
INSERT INTO "vn_ruwwad" VALUES(38,7,'38:7','“Chúng ta chưa từng nghe đến điều này trong tín ngưỡng sau cùng của tổ tiên chúng ta. Đây thực sự chỉ là một điều bịa đặt!”');
INSERT INTO "vn_ruwwad" VALUES(38,8,'38:8','“Lẽ nào Lời nhắc nhở chỉ được ban xuống cho hắn trong khi vẫn còn chúng ta ư?” Không, họ hoài nghi Lời nhắc nhở của TA. Không, bởi vì họ chưa nếm mùi trừng phạt của TA!');
INSERT INTO "vn_ruwwad" VALUES(38,9,'38:9','Hoặc lẽ nào (những kẻ thờ đa thần này) nắm giữ kho tàng Hồng Ân của Thượng Đế của Ngươi (hỡi Thiên Sứ), Đấng Toàn Năng, Đấng Ban Bố?');
INSERT INTO "vn_ruwwad" VALUES(38,10,'38:10','Hoặc lẽ nào quyền thống trị các tầng trời và trái đất và vạn vật giữa trời đất thuộc về họ? Nếu thực sự như thế thì họ hãy tìm cách đi lên đó (xem nào)!');
INSERT INTO "vn_ruwwad" VALUES(38,11,'38:11','Họ là một đội quân sẽ bị đánh bại giống như các đội quân (trước đó đã phủ nhận các vị Thiên Sứ của mình).');
INSERT INTO "vn_ruwwad" VALUES(38,12,'38:12','Trước họ, đám dân của Nuh, ‘Ad và Pha-ra-ông, chủ nhân của các cột trụ cũng đã phủ nhận.');
INSERT INTO "vn_ruwwad" VALUES(38,13,'38:13','Thamud, người dân của Lut và dân cư của khu rừng Aykah. Họ là liên quân.');
INSERT INTO "vn_ruwwad" VALUES(38,14,'38:14','Tất cả đều phủ nhận các Sứ Giả nên họ đáng phải bị trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(38,15,'38:15','Những kẻ (phủ nhận) này chỉ đang chờ đợi một tiếng gầm duy nhất. Khi đó, chắc chắn họ sẽ không thể quay lại.');
INSERT INTO "vn_ruwwad" VALUES(38,16,'38:16','Họ nói (một cách chế giễu): “Lạy Thượng Đế của bầy tôi! Xin Ngài mau cho bầy tôi thấy phần số (bị trừng phạt) của bầy tôi trước khi xảy ra Ngày Phán Xét.”');
INSERT INTO "vn_ruwwad" VALUES(38,17,'38:17','Ngươi (hỡi Thiên Sứ) hãy kiên nhẫn chịu đựng về những điều họ nói và Ngươi hãy nghĩ đến người bề tôi Dawood của TA, một con người kiên cường. Quả thật, Y là người hằng quay về sám hối.');
INSERT INTO "vn_ruwwad" VALUES(38,18,'38:18','Quả thật, TA đã chế ngự núi non cùng với (Dawood) tán dương TA đêm ngày.');
INSERT INTO "vn_ruwwad" VALUES(38,19,'38:19','Và chim chóc tụ tập thành đàn; tất cả cùng hợp đoàn tuân lệnh Y (tán dương TA).');
INSERT INTO "vn_ruwwad" VALUES(38,20,'38:20','TA đã củng cố quyền bính của (Dawood), ban cho Y sự khôn ngoan trong việc phán xét và ban cho y khả năng ăn nói quả quyết.');
INSERT INTO "vn_ruwwad" VALUES(38,21,'38:21','(Hỡi Thiên Sứ), Ngươi có biết về câu chuyện của hai người kiện nhau đã trèo tường đột nhập vào phòng tu (của Dawood) chưa?');
INSERT INTO "vn_ruwwad" VALUES(38,22,'38:22','Khi họ xông vào gặp Dawood (đột ngột như thế), Y đã không khỏi bàng hoàng. Họ nói: “Xin Người đừng sợ! Chúng tôi là hai người kiện nhau, trong đó một người đã hành động sai trái với người kia. Cho nên, xin Ngài hãy phân xử giữa hai người chúng tôi bằng công lý và chớ đừng bất công, và xin Người hướng dẫn chúng tôi đến con đường êm xuôi (bằng giải pháp chính đáng).”');
INSERT INTO "vn_ruwwad" VALUES(38,23,'38:23','“Quả thật, đây là người anh em (trong đạo) của tôi. Anh ấy có chín mươi chín con cừu cái còn tôi chỉ có một con cừu cái duy nhất. Tuy nhiên, anh ấy nói với tôi ‘hãy đưa nó cho ta’ và anh ấy đã thắng tôi trong phần tranh luận.”');
INSERT INTO "vn_ruwwad" VALUES(38,24,'38:24','(Dawood) liền nói (với người trình bày): “Chắc chắn, anh ta đã bất công với ngươi về việc đã đòi nhập con cừu của ngươi vào bầy cừu của anh ta. Quả thật, hầu hết các đối tác hùn hạp thường hành động sai trái với nhau ngoại trừ những ai có đức tin và hành thiện; và những người như thế thì ít.” Dawood đoán rằng chắc chắn TA (Allah) đã thử thách Y nên Y đã cầu xin Thượng Đế của Y tha thứ, cúi mình phủ phục và quay về sám hối (với Ngài).');
INSERT INTO "vn_ruwwad" VALUES(38,25,'38:25','Vậy là TA (Allah) đã tha thứ cho (Dawood) về điều đó. Quả thật ở nơi TA, Y có được một chỗ gần gũi và một nơi quay về tốt đẹp.');
INSERT INTO "vn_ruwwad" VALUES(38,26,'38:26','(Allah phán bảo): “Này hỡi Dawood! TA đã chỉ định Ngươi làm một Đại Diện trên trái đất. Do đó, Ngươi hãy phân xử giữa mọi người theo công lý và chớ làm theo dục vọng và sở thích của Ngươi, vì nó sẽ đưa Ngươi lạc khỏi con đường của Allah. Quả thật, những ai lạc khỏi con đường của Allah thì sẽ bị trừng phạt nặng nề vì đã quên mất Ngày Phán Xét (Cuối Cùng).”');
INSERT INTO "vn_ruwwad" VALUES(38,27,'38:27','TA (Allah) đã không tạo ra trời đất và vạn vật giữa chúng một cách vô ích và không mục đích. Đó chỉ là lối suy nghĩ của những kẻ vô đức tin mà thôi. Vì vậy, khốn cho những kẻ vô đức tin (bị trừng phạt) trong Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(38,28,'38:28','Lẽ nào TA lại đối xử với những người có đức tin và hành thiện giống như (đối với) những kẻ thối nát trên trái đất? Hoặc lẽ nào TA lại đối xứ với những người ngoan đạo giống như (đối với) những kẻ hư đốn và tội lỗi?');
INSERT INTO "vn_ruwwad" VALUES(38,29,'38:29','Kinh Sách (Qur’an) mà TA đã ban xuống cho Ngươi (hỡi Thiên Sứ) là một Kinh Sách đầy phúc lành để họ nghiền ngẫm về những câu Kinh của Nó và để cho những người thông hiểu lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(38,30,'38:30','TA đã ban cho Dawood đứa con trai, (đó là) Sulayman, (Y là) một người bề tôi ưu hạng, Y là người bề tôi hằng quay về sám hối.');
INSERT INTO "vn_ruwwad" VALUES(38,31,'38:31','Vào một buổi xế chiều, những con ngựa tốt có những bước chân nhanh nhẹn, được mang đến trình (Sulayman).');
INSERT INTO "vn_ruwwad" VALUES(38,32,'38:32','(Sulayman) nói: “Quả thật, Ta đã từng yêu thích món vật tốt hơn cả việc tưởng nhớ Thượng Đế của Ta cho đến khi mặt trời đã khuất bóng sau màn đêm (lỡ cả lễ nguyện Salah ‘Asr)”.');
INSERT INTO "vn_ruwwad" VALUES(38,33,'38:33','“Hãy dắt chúng (đàn ngựa) trở lại cho ta”, rồi (Sulayman) lấy kiếm đánh vào chân và cổ của chúng.');
INSERT INTO "vn_ruwwad" VALUES(38,34,'38:34','Quả thật, TA đã thử thách Sulayman. TA đã đặt lên ngai vàng của Y một con quỷ (và Y đã mất đi vương quyền trong một thời gian ngắn). Sau đó, (Sulayman) đã quay về sám hối (với TA và TA đã phục vương quyền trở lại cho Y).');
INSERT INTO "vn_ruwwad" VALUES(38,35,'38:35','(Sulayman) cầu nguyện: “Lạy Thượng Đế của bề tôi! Xin Ngài tha thứ cho bề tôi và ban cho bề tôi một vương quyền mà Ngài sẽ không ban cho một ai khác sau bề tôi. Quả thật, Ngài là Đấng Hằng Ban Bố.”');
INSERT INTO "vn_ruwwad" VALUES(38,36,'38:36','TA (Allah) đã chế ngự gió cho (Sulayman), bắt nó bay một cách nhẹ nhàng đến bất cứ nơi nào tùy theo lệnh của Y.');
INSERT INTO "vn_ruwwad" VALUES(38,37,'38:37','TA bắt Shaytan phục tùng (Sulayman), chúng bao gồm thợ xây và thợ lặn.');
INSERT INTO "vn_ruwwad" VALUES(38,38,'38:38','Còn những tên (Shaytan) khác thì bị xiềng xích lại.');
INSERT INTO "vn_ruwwad" VALUES(38,39,'38:39','(Allah phán bảo Sulayman): “(Này Sulayman), đây là phần ban phát của TA dành cho Ngươi. Ngươi chi dùng (bố thí cho ai) hoặc giữ lại (không cho ai) là tùy ý Ngươi, Ngươi sẽ không bị thanh toán (về việc đó).');
INSERT INTO "vn_ruwwad" VALUES(38,40,'38:40','Quả thật, (Sulayman) có nơi TA một chỗ gần gũi và một nơi quay trở về tốt đẹp.');
INSERT INTO "vn_ruwwad" VALUES(38,41,'38:41','Ngươi (hỡi Thiên Sứ) hãy nhớ đến người bề tôi Ayyub của TA khi Y khóc than gọi Thượng Đế của Y, cầu nguyện : “Quả thật, Shaytan đã làm cho bề tôi khổ tâm và đau đớn.”');
INSERT INTO "vn_ruwwad" VALUES(38,42,'38:42','(TA – Allah phán bảo Ayyub): “Ngươi hãy dùng chân của mình giẫm lên mặt đất. Nơi đây, sẽ có nước cho ngươi tắm rửa và uống.”');
INSERT INTO "vn_ruwwad" VALUES(38,43,'38:43','TA đã trả gia đình lại cho (Ayyub) và ban thêm cho Y Hồng Ân từ nơi TA và (đó) là một sự Nhắc Nhở cho những người thông hiểu.');
INSERT INTO "vn_ruwwad" VALUES(38,44,'38:44','(Lúc Ayyub giận vợ và thề sẽ đánh đòn vợ một trăm roi. Thế là TA – Allah phán bảo Y): “Ngươi hãy nắm trong tay một chùm cọng lá chà là, Ngươi dùng nó đánh (nữ ta) và Ngươi sẽ không phạm lời thề.” Quả thật, TA đã thấy Y hằng chịu đựng, kiên nhẫn. Y đích thực là một người bề tôi ưu hạng, Y đích thực là người bề tôi luôn biết quay đầu sám hối (với TA).');
INSERT INTO "vn_ruwwad" VALUES(38,45,'38:45','Ngươi (hỡi Thiên Sứ) hãy nhớ đến những người bề tôi của TA, Ibrahim, Is-haaq và Ya’qub. Họ là những người cương nghị và có cái nhìn thấu đáo sự việc.');
INSERT INTO "vn_ruwwad" VALUES(38,46,'38:46','Quả thật, TA đã chọn họ làm những người hết lòng tưởng nhớ Ngôi nhà (ở Đời Sau).');
INSERT INTO "vn_ruwwad" VALUES(38,47,'38:47','Đối với TA, họ thực sự là những thành phần ưu tú được tuyển chọn.');
INSERT INTO "vn_ruwwad" VALUES(38,48,'38:48','Ngươi (hỡi Thiên Sứ) hãy nhớ đến Isma’il, Al-Yasa’ (Elisha) và Zdul-kifl. Tất cả họ đều thuộc thành phần ưu tú được tuyển chọn.');
INSERT INTO "vn_ruwwad" VALUES(38,49,'38:49','Đây là Lời Nhắc Nhở. Quả thật những người ngoan đạo (sợ Allah) sẽ có một nơi trở về vô cùng tốt đẹp.');
INSERT INTO "vn_ruwwad" VALUES(38,50,'38:50','Những Ngôi Vườn Thiên Đàng Vĩnh Cửu với những cánh cửa luôn luôn được mở đón chờ họ.');
INSERT INTO "vn_ruwwad" VALUES(38,51,'38:51','Họ sẽ tựa mình thoải mái trong đó; họ sẽ gọi nhiều loại trái quả (để ăn) và nhiều loại nước giải khát (để uống) một cách tùy thích.');
INSERT INTO "vn_ruwwad" VALUES(38,52,'38:52','Bên cạnh họ sẽ là những nàng trinh nữ chỉ biết ngắm nhìn chồng của mình.');
INSERT INTO "vn_ruwwad" VALUES(38,53,'38:53','Đây là những gì mà các ngươi (hỡi những người ngoan đạo) đã được hứa vào Ngày Phán Xét (Cuối Cùng).');
INSERT INTO "vn_ruwwad" VALUES(38,54,'38:54','Đây là bổng lộc vô tận của TA (dành cho những người ngoan đạo).');
INSERT INTO "vn_ruwwad" VALUES(38,55,'38:55','Đây (là phần thưởng dành cho những người ngoan đạo). Riêng đối với những kẻ vượt quá (giới hạn của TA) thì sẽ có một nơi trở về vô cùng tồi tệ.');
INSERT INTO "vn_ruwwad" VALUES(38,56,'38:56','(Đó là) Hỏa Ngục, nơi mà họ sẽ bị thiêu đốt; một chốn ở hết mức tồi tệ.');
INSERT INTO "vn_ruwwad" VALUES(38,57,'38:57','Đây (là sự trừng phạt dành cho những kẻ vượt quá giới hạn). Bởi vậy, hãy để cho họ nếm nó: một loại nước cực sôi và một loại chất mủ hôi tanh.');
INSERT INTO "vn_ruwwad" VALUES(38,58,'38:58','(Ngoài những phình phạt đó) còn có các loại hình phạt tương xứng khác nữa.');
INSERT INTO "vn_ruwwad" VALUES(38,59,'38:59','(Khi cư dân Hỏa Ngục đi vào trong đó, họ mắng nhiếc nhau, nói): “Đây là một đoàn người cắm đầu chạy chung với các người, không được ai nghinh đón cả! Chắc chắn, họ sẽ bị nướng trong Lửa (giống như chúng ta).”');
INSERT INTO "vn_ruwwad" VALUES(38,60,'38:60','(Khi cư dân Hỏa Ngục đi vào trong đó, họ mắng nhiếc nhau, nói): “Đây là một đoàn người cắm đầu chạy chung với các người, không được ai nghinh đón cả! Chắc chắn, họ sẽ bị nướng trong Lửa (giống như chúng ta).”');
INSERT INTO "vn_ruwwad" VALUES(38,61,'38:61','(Những kẻ đi theo) thưa: “Lạy Thượng Đế của bầy tôi! Kẻ nào làm cho bầy tôi mang tội này thì xin Ngài trừng phạt kẻ đó gấp đôi trong Hỏa Ngục.”');
INSERT INTO "vn_ruwwad" VALUES(38,62,'38:62','(Những kẻ tự cao tự đại trong những thành phần vượt quá giới hạn) bảo: “Tại sao chúng tôi không thấy những kẻ mà chúng tôi đã liệt họ vào đám người xấu xa?”');
INSERT INTO "vn_ruwwad" VALUES(38,63,'38:63','“Lẽ nào chúng tôi đã chế giễu họ hay lẽ nào chúng tôi đã bị quáng mắt không nhìn thấy họ?”');
INSERT INTO "vn_ruwwad" VALUES(38,64,'38:64','Đó là sự thật về việc tranh cãi của các cư dân nơi Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(38,65,'38:65','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ vô đức trong đám dân của Ngươi): “Quả thật, Ta chỉ là một người cảnh báo, và thực sự không có Thượng Đế đích thực nào khác ngoài Allah, Đấng Duy Nhất, Đấng Tối Thượng.”');
INSERT INTO "vn_ruwwad" VALUES(38,66,'38:66','“(Ngài là) Thượng Đế của các tầng trời, trái đất và vạn vật giữa trời đất, và Ngài là Đấng Toàn Năng, Đầng Hằng Tha Thứ.”');
INSERT INTO "vn_ruwwad" VALUES(38,67,'38:67','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ phủ nhận này): “Nó (Qur’an) là một nguồn tin vĩ đại.”');
INSERT INTO "vn_ruwwad" VALUES(38,68,'38:68','“Cái mà các người thường quay mặt ngoảnh đi.”');
INSERT INTO "vn_ruwwad" VALUES(38,69,'38:69','“Ta không hề biết bất cứ điều gì về các vị trên cao (các Thiên Thần) khi họ tranh luận với nhau.”');
INSERT INTO "vn_ruwwad" VALUES(38,70,'38:70','“Quả thật, Ta chỉ được Mặc Khải cho biết rằng Ta là một người cảnh báo công khai.”');
INSERT INTO "vn_ruwwad" VALUES(38,71,'38:71','(Ngươi hãy nhớ lại) khi Thượng Đế của Ngươi tuyên bố với các Thiên Thần: “Quả thật, TA sắp tạo một người phàm (Adam) từ đất sét.”');
INSERT INTO "vn_ruwwad" VALUES(38,72,'38:72','Khi TA đã uốn nắn Y thành hình thể và đã thổi vào Y linh hồn từ nơi TA thì TA ra lệnh (cho các Thiên Thần): “Các ngươi hãy cúi xuống quỳ lạy Y.”');
INSERT INTO "vn_ruwwad" VALUES(38,73,'38:73','Thế là tất cả các Thiên Thần đều cúi xuống quỳ lạy.');
INSERT INTO "vn_ruwwad" VALUES(38,74,'38:74','Ngoại trừ Iblis. Nó tự cao tự đại (không tuân lệnh) nên đã trở thành một tên phản nghịch.');
INSERT INTO "vn_ruwwad" VALUES(38,75,'38:75','(Allah) phán: “Này Iblis! Điều gì ngăn nhà ngươi quỳ lạy thứ mà TA đã tạo ra từ chính đôi Bàn Tay của TA? Có phải nhà ngươi ngạo mạn (xem thường vật TA tạo) hoặc có phải nhà ngươi đã ngạo mạn và tự cao (đối với mệnh lệnh của TA từ trước)?”');
INSERT INTO "vn_ruwwad" VALUES(38,76,'38:76','(Iblis) nói: “Bề tôi tốt hơn Y (Adam) bởi lẽ Ngài đã tạo bề tôi từ lửa trong khi Ngài tạo Y từ đất sét.”');
INSERT INTO "vn_ruwwad" VALUES(38,77,'38:77','(Allah) phán: “Ngươi hãy cút khỏi (Thiên Đàng), quả thật nhà ngươi là một tên đã bị trục xuất.”');
INSERT INTO "vn_ruwwad" VALUES(38,78,'38:78','“Quả thật, nhà ngươi là kẻ bị nguyền rủa cho đến Ngày Thưởng Phạt Cuối Cùng.”');
INSERT INTO "vn_ruwwad" VALUES(38,79,'38:79','(Iblis) nói: “Lạy Thượng Đế của bề tôi, xin Ngài tạm tha bề tôi cho đến ngày (mà các bề tôi của Ngài) được dựng sống trở lại.”');
INSERT INTO "vn_ruwwad" VALUES(38,80,'38:80','(Allah) phán: “Thế thì nhà ngươi là kẻ được tạm tha,”');
INSERT INTO "vn_ruwwad" VALUES(38,81,'38:81','“Cho đến Ngày của thời khắc đã được ấn định.”');
INSERT INTO "vn_ruwwad" VALUES(38,82,'38:82','(Iblis) nói: “Với Quyền Năng của Ngài, bề tôi sẽ lôi kéo tất cả (con cháu của Adam) lầm lạc.”');
INSERT INTO "vn_ruwwad" VALUES(38,83,'38:83','“Ngoại trừ những bề tôi trung thực và thành tâm của Ngài trong số họ.”');
INSERT INTO "vn_ruwwad" VALUES(38,84,'38:84','(Allah) phán: “Sự Thật là từ nơi TA và TA luôn luôn nói sự Thật.”');
INSERT INTO "vn_ruwwad" VALUES(38,85,'38:85','“TA chắc chắn sẽ giam nhà ngươi cùng tất cả những ai trong số họ đi theo nhà ngươi vào Hỏa Ngục.”');
INSERT INTO "vn_ruwwad" VALUES(38,86,'38:86','Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ thờ đa thần): “Ta không hề đòi các người trả thù lao cho TA về sứ mạng này và Ta cũng không phải là một tên bịa chuyện.”');
INSERT INTO "vn_ruwwad" VALUES(38,87,'38:87','“Quả thật, Nó (Qur’an) chỉ là lời nhắc nhở và khuyên răn cho muôn loài (con người, Jinn và các tạo vật khác).”');
INSERT INTO "vn_ruwwad" VALUES(38,88,'38:88','“Chắc chắn các người sẽ biết Thông Tin của Nó (là sự thật) sau một thời gian (khi các người đối diện với cái chết).”');
INSERT INTO "vn_ruwwad" VALUES(39,1,'39:1','Kinh Sách (Qur’an) được ban xuống từ nơi Allah, Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(39,2,'39:2','Quả thật, TA (Allah) đã ban xuống cho Ngươi (hỡi Thiên Sứ) Kinh Sách (Qur’an) chứa đựng điều chân lý. Do đó, Ngươi hãy thờ phượng Allah, thành tâm qui phục một mình Ngài.');
INSERT INTO "vn_ruwwad" VALUES(39,3,'39:3','Lẽ nào việc chân thành qui phục không phải chỉ dành riêng cho một mình Allah duy nhất ư? Những kẻ đã nhận lấy ngoài Ngài các đấng bảo hộ (từ các thần linh bục tượng, các tà thần, nói): “Chúng tôi không thờ phượng họ mà thật ra chúng tôi chỉ muốn nhờ họ đưa chúng tôi đến gần Allah mà thôi.” Allah chắc chắn sẽ xét xử giữa họ về điều họ tranh cãi. Quả thật, Allah không hướng dẫn kẻ nói dối, vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(39,4,'39:4','Nếu Allah thực sự muốn nhận lấy một đứa con trai cho mình là Ngài đã chọn trong số tạo vật mà Ngài đã tạo một người vừa ý của Ngài. Tuy nhiên, Ngài Quang Vinh và Tối Cao (vượt xa điều đó)! Ngài là Allah, Đấng Duy Nhất, Đấng Quyền Năng.');
INSERT INTO "vn_ruwwad" VALUES(39,5,'39:5','Ngài đã tạo các tầng trời và trái đất vì chân lý (mang ý nghĩa và mục đích cao thâm). Ngài cuốn ban đêm bao phủ ban ngày và cuốn ban ngày bao phủ ban đêm. Ngài chế ngự mặt trời và mặt trăng. Mỗi vật đều di chuyển đến một thời hạn ấn định. Vậy lẽ nào Ngài không là Đấng Toàn Năng, Đấng Hằng Tha Thứ ư?');
INSERT INTO "vn_ruwwad" VALUES(39,6,'39:6','Ngài đã tạo hóa các ngươi (hỡi nhân loại) từ một cá thể duy nhất, (đó là Adam). Rồi từ Y, Ngài đã tạo ra người vợ của Y (Hauwa). Rồi Ngài ban xuống tám con gia súc cho các ngươi. Ngài tạo các ngươi trong dạ con của các bà mẹ của các ngươi theo từng giai đoạn, cái này tiếp nối theo sau cái kia, trong ba lớp màng tối. Đó là Allah, Thượng Đế của các ngươi. Mọi quyền thống trị đều thuộc về Ngài. Không có Thượng Đế đích thực nào khác ngoài Ngài. Vậy sao các ngươi lại lánh xa Ngài (không thờ phượng Ngài mà thờ phượng các tạo vật của Ngài)?');
INSERT INTO "vn_ruwwad" VALUES(39,7,'39:7','Nếu các ngươi vô đức tin thì quả thật Allah vẫn là Đấng Giàu Có, chẳng lệ thuộc gì các ngươi cả, chỉ có điều Ngài không hài lòng với sự vong ân của đám bầy tôi của Ngài. Nhưng nếu các ngươi biết tri ân thì Ngài sẽ hài lòng với các ngươi. Quả thật, không ai phải gánh tội cho người khác. Rồi đây, các ngươi sẽ quay về gặp Thượng Đế của các ngươi trở lại; rồi Ngài sẽ cho các ngươi biết về những điều mà các ngươi đã từng làm. Quả thật, Ngài là Đấng biết rõ những điều nằm trong lòng (của mỗi người các ngươi).');
INSERT INTO "vn_ruwwad" VALUES(39,8,'39:8','Con người khi gặp nạn thường khẩn cầu Thượng Đế của y, quay về sám hối với Ngài. Nhưng khi Ngài đã ban cho y Ân Huệ từ nơi Ngài, y lại quên mất điều mà y đã từng cầu xin Ngài trước đó, không những vậy, y còn dựng lên những thần linh cùng với Allah hầu mong dẫn thiên hạ đi lạc khỏi con đường của Ngài. Ngươi (hỡi Thiên Sứ) hãy nói (với những kẻ như thế): “Ngươi cứ hưởng thụ chốc lát bằng sự vô đức tin của ngươi. Chắc chắn (rồi đây) ngươi sẽ là một trong những người bạn của Hỏa Ngục.”');
INSERT INTO "vn_ruwwad" VALUES(39,9,'39:9','Lẽ nào một người thức khuya đứng lạy, lo sợ cho Đời Sau và hy vọng nơi lòng nhân từ của Thượng Đế của y (lại giống với kẻ thờ phượng Allah trong hoạn nạn và phủ nhận Ngài trong lúc phồn thịnh) hay sao? Ngươi (hỡi Thiên Sứ) hãy nói: “Lẽ nào những người hiểu biết lại ngang bằng với những kẻ không biết gì ư?” Quả thật chỉ những người thông hiểu mới lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(39,10,'39:10','Ngươi (hỡi Thiên Sứ) hãy nói: “Hỡi những bề tôi có đức tin! Các ngươi hãy sợ Thượng Đế của các ngươi. Những ai làm điều thiện tốt trên thế gian thì sẽ gặp điều tốt trở lại. Và (các ngươi hãy biết rằng) đất đai của Allah rộng thênh thang. Quả thật, chỉ những người kiên nhẫn mới được ban trọn phần thưởng một cách không tính toán.”');
INSERT INTO "vn_ruwwad" VALUES(39,11,'39:11','Ngươi (hỡi Thiên Sứ) hãy nói: “Quả thật, Ta được lệnh phải thờ phượng Allah, thành tâm thần phục một mình Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(39,12,'39:12','“Và Ta được lệnh phải trở thành một trong những người Muslim (thần phục Ngài) đầu tiên.”');
INSERT INTO "vn_ruwwad" VALUES(39,13,'39:13','Ngươi (hỡi Thiên Sứ) hãy nói: “Ta thực sự sợ hình phạt của Ngày Trọng Đại nếu Ta bất tuân Thượng Đế của Ta.”');
INSERT INTO "vn_ruwwad" VALUES(39,14,'39:14','Ngươi hãy nói: “Ta thờ phượng Allah, thành tâm thần phục một mình Ngài”.');
INSERT INTO "vn_ruwwad" VALUES(39,15,'39:15','Do đó, các ngươi (hỡi những kẻ thờ đa thần) hãy cứ thờ phượng ngoài Ngài những gì các ngươi yêu thích. Ngươi (hỡi Thiên Sứ) hãy nói: “Những kẻ thua thiệt là những ai đã làm bản thân và gia đình thất bại vào Ngày Phục Sinh. Đó thực sự là một sự thua thiệt rõ ràng!”');
INSERT INTO "vn_ruwwad" VALUES(39,16,'39:16','Họ sẽ bị các tấm màn bằng lửa bao phủ phía trên và phía dưới. Allah dùng nó để làm run sợ bầy tôi của Ngài như thế. Hỡi bầy tôi của TA, các ngươi hãy sợ TA.');
INSERT INTO "vn_ruwwad" VALUES(39,17,'39:17','Riêng những ai tránh xa các tà thần, không thờ phượng chúng và quay về sám hối với Allah thì sẽ được tin mừng. Ngươi (hỡi Thiên Sứ) hãy báo tin mừng cho đám bề tôi của TA.');
INSERT INTO "vn_ruwwad" VALUES(39,18,'39:18','Những ai nghe Lời Phán và tuân theo điều tốt nhất của nó. Họ là những người đã được Allah hướng dẫn và họ là những người thông hiểu.');
INSERT INTO "vn_ruwwad" VALUES(39,19,'39:19','Một người mà lời tuyên bố trừng phạt y đã được xác nhận thì Ngươi (hỡi Thiên Sứ) không còn cách nào có thể hướng dẫn được kẻ đó. Lẽ nào Ngươi lại muốn cứu vớt một người đang ở trong Hỏa Ngục?');
INSERT INTO "vn_ruwwad" VALUES(39,20,'39:20','Tuy nhiên, những ai sợ Thượng Đế của họ thì sẽ được ban cho các tòa nhà cao tầng chồng lên nhau, bên dưới có các dòng sông chảy. (Đó là) Lời Hứa của Allah và Allah không hề thất hứa.');
INSERT INTO "vn_ruwwad" VALUES(39,21,'39:21','Quả thật, ngươi (con người) quan sát thấy việc Allah cho mưa từ trên trời xuống để nó thấm vào lòng đất chảy thành mạch nước, rồi Ngài dùng nó làm mọc ra cây trái đủ màu sắc, rồi cây cối bắt đầu úa dần, ngươi thấy nó héo vàng, rồi Ngài làm cho nó khô héo vỡ vụn. Quả thật, trong sự việc đó là điều nhắc nhở dành cho những người thông hiểu.');
INSERT INTO "vn_ruwwad" VALUES(39,22,'39:22','Lẽ nào một người được Allah mở rộng tấm lòng để tiếp nhận Islam rồi bước đi trên ánh sáng của Thượng Đế của y (lại giống với một kẻ vô đức tin) ư? Thật khốn cho những kẻ mà trái tim của họ đã chai cứng trước Lời Nhắc Nhở của Allah! Những kẻ đó quả thật đang ở trong sự lầm lạc rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(39,23,'39:23','Allah đã ban xuống những Lời nói tốt đẹp nhất: Một Kinh Sách mang đại ý giống nhau và lặp đi lặp lại. Khi nghe Nó, lớp da của những ai sợ Thượng Đế của họ sẽ sởn gai ốc (vì run sợ). Rồi lớp da và trái tim của họ sẽ mềm đi khi nghĩ đến Allah. Đó là nguồn hướng dẫn của Allah, Ngài dùng Nó để hướng dẫn ai Ngài muốn; và ai mà Allah làm cho lầm lạc thì sẽ không có được người hướng dẫn.');
INSERT INTO "vn_ruwwad" VALUES(39,24,'39:24','Vậy, một người dùng gương mặt của mình để đỡ lấy hình phạt tồi tệ của Ngày Phán Xét (có bằng với người được vào Thiên Đàng hay không)? Rồi đây, những tên làm điều sai quấy sẽ được bảo: “Các ngươi hãy nếm (sự trừng phạt từ) những điều mà các ngươi đã làm ra.”');
INSERT INTO "vn_ruwwad" VALUES(39,25,'39:25','(Những kẻ thờ đa thần này), trước họ cũng đã có những kẻ phủ nhận và sự trừng phạt đã đến với những kẻ đó một cách mà chúng không thể ngờ tới.');
INSERT INTO "vn_ruwwad" VALUES(39,26,'39:26','Allah đã bắt họ nếm sự nhục nhã ở đời này nhưng sự trừng phạt dành cho họ ở Đời Sau chắc chắn sẽ còn to lớn hơn, nếu họ biết điều đó!');
INSERT INTO "vn_ruwwad" VALUES(39,27,'39:27','Quả thật, TA đã trình bày cho nhân loại trong Qur’an này tất cả các hình ảnh thí dụ, mong rằng họ biết ghi nhớ và lưu tâm.');
INSERT INTO "vn_ruwwad" VALUES(39,28,'39:28','(TA - Allah đã ban xuống) Kinh Qur’an bằng tiếng Ả-rập, không quanh co (mơ hồ), mong rằng họ ngoan đạo (sợ Allah).');
INSERT INTO "vn_ruwwad" VALUES(39,29,'39:29','Allah đưa ra hình ảnh thí dụ, một người phục vụ cho nhiều ông chủ gồm những kẻ hợp tác nhưng hay tranh chấp nhau, còn một người chỉ phục vụ cho một ông chủ duy nhất, hai người này có ngang bằng nhau không? Mọi lời ca ngợi và tán dương đều thuộc về Allah! Không, đa số bọn họ không biết gì (nên mới thờ phượng các thần linh ngoài Allah).');
INSERT INTO "vn_ruwwad" VALUES(39,30,'39:30','Quả thật, Ngươi (hỡi Thiên Sứ) sẽ chết và họ cũng sẽ chết.');
INSERT INTO "vn_ruwwad" VALUES(39,31,'39:31','Rồi vào Ngày Xét Xét Cuối Cùng, các ngươi sẽ đối chất với nhau trước Thượng Đế của các ngươi (để biết ai là người của chân lý và ai là kẻ của sự ngụy tạo và sai trái).');
INSERT INTO "vn_ruwwad" VALUES(39,32,'39:32','Vậy còn ai sai quấy hơn kẻ đã nói dối cho Allah và đã phủ nhận chân lý khi nó đến với y? Lẽ nào Hỏa Ngục không phải là nơi cư ngụ cho những kẻ vô đức tin?!');
INSERT INTO "vn_ruwwad" VALUES(39,33,'39:33','Những người mang chân lý đến và tin nơi nó, họ đích thực là những người ngoan đạo (ngay chính, sợ Allah).');
INSERT INTO "vn_ruwwad" VALUES(39,34,'39:34','Họ sẽ có được mọi điều mà họ ước muốn nơi Thượng Đế của họ. Đó là phần thưởng của những người làm tốt.');
INSERT INTO "vn_ruwwad" VALUES(39,35,'39:35','Mục đích để Allah xóa các tội lỗi mà họ đã phạm (và đã quay về sám hối với Ngài) đồng thời để ân thưởng cho họ bởi những điều tốt nhất mà họ đã từng làm.');
INSERT INTO "vn_ruwwad" VALUES(39,36,'39:36','Lẽ nào Allah không đủ giúp cho Người Bề Tôi (Muhammad) của Ngài?! Họ muốn làm cho Ngươi (hỡi Thiên Sứ) sợ những ai khác ngoài Ngài. Và ai mà Allah làm cho lầm lạc thì y sẽ không có người hướng dẫn đúng đường.');
INSERT INTO "vn_ruwwad" VALUES(39,37,'39:37','Người nào mà Allah hướng dẫn thì sẽ không bị bất cứ ai làm cho lầm lạc. Lẽ nào Allah không là Đấng Toàn Năng, Đấng Báo Thù (trừng phạt những ai nghịch lại)?!');
INSERT INTO "vn_ruwwad" VALUES(39,38,'39:38','Nếu Ngươi (hỡi Thiên Sứ) hỏi họ “Ai đã tạo các tầng trời và trái đất” thì chắc chắn họ sẽ trả lời “Allah”. Ngươi hãy nói với họ: “Các ngươi hãy cho Ta biết nếu Allah muốn tai họa xảy đến với Ta thì những (thần linh) mà các ngươi khấn vái ngoài Allah có thể giải cứu Ta khỏi tai họa đó hay không? Hoặc nếu Allah muốn thương xót Ta thì (những thần linh đó) có thể kiềm hãm lòng thương xót của Ngài hay không?” Ngươi hãy nói với họ: “Một mình Allah đã đủ phù hộ Ta. Những ai tin cậy (Allah) nên phó thác cho Ngài”.');
INSERT INTO "vn_ruwwad" VALUES(39,39,'39:39','Ngươi hãy nói: “Hỡi dân ta! Các ngươi cứ làm theo con đường (thờ cúng các thần linh khác ngoài Allah) của các ngươi còn Ta làm theo (những gì mà Thượng Đế của Ta đã ra lệnh cho Ta). Rồi đây các ngươi sẽ sớm biết:”');
INSERT INTO "vn_ruwwad" VALUES(39,40,'39:40','“Ai sẽ là kẻ bị trừng phạt nhục nhã và bị trừng phạt đời đời kiếp kiếp.”');
INSERT INTO "vn_ruwwad" VALUES(39,41,'39:41','Quả thật, TA đã ban Kinh Sách (Qur’an) xuống cho Ngươi (hỡi Thiên Sứ) là để cho nhân loại (chiếu theo) chân lý trong Nó. Do đó, ai tiếp thu nguồn chỉ đạo thì y được lợi cho bản thân mình; còn ai lầm lạc thì sự lầm lạc đó chỉ dẫn y tới sự bất lợi cho bản thân y mà thôi. Và Ngươi không phải là người quản lý trông coi họ.');
INSERT INTO "vn_ruwwad" VALUES(39,42,'39:42','Allah cho bắt lấy linh hồn khi nào nó đến lúc phải chết và khi nó chưa chết lúc trong giấc ngủ. Hồn của người nào Ngài quyết định cho chết thì sẽ bị giữ lại; và các hồn khác thì được gởi trở về thể xác đến một thời hạn ấn định. Quả thật, trong đó là các Dấu hiệu cho đám người biết suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(39,43,'39:43','Hay là ngoài Allah, (những kẻ thờ đa thần) đã nhận (các bục tượng) làm những vị can thiệp (cho họ). Ngươi (Thiên Sứ) hãy bảo họ: “Lẽ nào ngay cả khi các thần linh đó không có một chút quyền thống trị nào và chúng (chỉ là những vật vô tri) chẳng biết cũng chẳng hiểu gì nữa hay sao?”');
INSERT INTO "vn_ruwwad" VALUES(39,44,'39:44','Ngươi (hỡi Thiên Sứ) hãy nói với họ: “Mọi sự can thiệp đều thuộc về quyền của Allah. Ngài nắm quyền thống trị các tầng trời và trái đất. Rồi đây các ngươi sẽ được đưa về trình diện Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(39,45,'39:45','Khi nghe nhắc đến riêng Allah, lòng của những kẻ không có đức tin nơi cõi Đời Sau tràn đầy oán ghét, còn khi nhắc đến những kẻ (được tôn thờ khác) ngoài Ngài thì họ vui mừng ra mặt.');
INSERT INTO "vn_ruwwad" VALUES(39,46,'39:46','Ngươi (hỡi Thiên Sứ) hãy cầu nguyện, nói: “Lạy Allah, Đấng đã sáng tạo các tầng trời và trái đất, Đấng biết điều vô hình và hữu hình, xin Ngài hãy xét xử giữa bầy tôi của Ngài về những điều mà họ thường tranh cãi!”');
INSERT INTO "vn_ruwwad" VALUES(39,47,'39:47','Nếu những kẻ làm điều sai quấy có được tất cả mọi thứ trên trái đất và nhiều hơn thế thì chắc chắn họ sẽ dùng nó để chuộc lấy mình thoát khỏi hình phạt đau đớn vào Ngày Phán Xét Cuối Cùng (nhưng điều đó không bao giờ xảy ra). Rồi họ sẽ đón nhận từ nơi Allah (những hình phạt) mà họ không ngờ đến.');
INSERT INTO "vn_ruwwad" VALUES(39,48,'39:48','Và những tội lỗi mà họ đã làm sẽ xuất hiện trước họ và những điều mà họ đã thường giễu cợt và nhạo báng sẽ bao vây họ.');
INSERT INTO "vn_ruwwad" VALUES(39,49,'39:49','Con người khi gặp nạn, y cầu xin TA giải cứu; rồi sau khi TA ban ân huệ từ nơi TA cho y thì y lại nói: “Tôi có được là do sự hiểu biết của tôi mà thôi.” Không, đó là một sự thử thách nhưng hầu hết (những kẻ vô đức tin) không biết.');
INSERT INTO "vn_ruwwad" VALUES(39,50,'39:50','Quả thật, những kẻ trước họ cũng đã nói lời lẽ đó. Tuy nhiên, những thứ mà họ kiếm được chẳng giúp ích được gì cho họ.');
INSERT INTO "vn_ruwwad" VALUES(39,51,'39:51','Cho nên, hậu quả xấu xa của những gì họ kiếm được đã xảy đến với họ. Và những kẻ làm điều sai quấy trong số những người (đang sống) này sẽ phải chịu hậu quả xấu xa về những gì họ đã làm, và họ sẽ không bao giờ thoát khỏi (Allah).');
INSERT INTO "vn_ruwwad" VALUES(39,52,'39:52','Lẽ nào họ không biết việc Allah muốn nới rộng hay thu hẹp bổng lộc của Ngài đối với ai là tùy ý Ngài hay sao? Quả thật, trong đó là các dấu hiệu cho đám người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(39,53,'39:53','Ngươi (hỡi Thiên Sứ) hãy nói: “Hỡi các bầy tôi của TA! Những ai đã làm hại bản thân mình (bởi những điều tội lỗi) thì chớ đừng tuyệt vọng nơi lòng thương xót của Allah. Chắc chắn Allah sẽ tha thứ tất cả tội lỗi (cho những ai quay về sám hối với Ngài); quả thật Ngài là Đấng Hằng Tha Thứ, Đấng Nhân Từ.”');
INSERT INTO "vn_ruwwad" VALUES(39,54,'39:54','“Các ngươi hãy quay về sám hối với Thượng Đế của các ngươi và thần phục Ngài trước khi sự trừng phạt xảy đến với các ngươi; rồi (lúc đó) các ngươi sẽ không được ai cứu giúp.”');
INSERT INTO "vn_ruwwad" VALUES(39,55,'39:55','“Các ngươi hãy làm theo những gì tốt nhất mà Thượng Đế của các ngươi đã ban xuống cho các ngươi (trong Qur’an) trước khi sự trừng phạt bất ngờ xảy đến với các ngươi trong lúc các ngươi không hay biết.”');
INSERT INTO "vn_ruwwad" VALUES(39,56,'39:56','“(Các ngươi hãy làm điều đó để) linh hồn (của các ngươi khỏi phải) than vãn (trong sự hối hận muộn màng): Ôi thật hối tiếc cho việc mình đã lơ là đối với Allah và thật hối tiếc cho việc mình đã là một trong những kẻ thường hay nhạo báng (những người có đức tin).”');
INSERT INTO "vn_ruwwad" VALUES(39,57,'39:57','“Hoặc (để linh hồn của các ngươi khỏi phải) nói: Phải chỉ Allah hướng dẫn mình thì chắc mình đã là một người ngoan đạo.”');
INSERT INTO "vn_ruwwad" VALUES(39,58,'39:58','“Hoặc (để linh hồn của các ngươi khỏi phải) nói khi nhìn thấy sự trừng phạt: Giá mà được trở lại (trần gian một lần nữa) thì mình sẽ là một trong những người làm tốt.”');
INSERT INTO "vn_ruwwad" VALUES(39,59,'39:59','Không, (sự việc không như ngươi mong muốn). Quả thật, trước đây các Lời Mặc Khải của TA đã đến với ngươi nhưng ngươi đã phủ nhận chúng, ngươi đã tỏ ra ngạo mạn và ngươi đã là một kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(39,60,'39:60','Vào Ngày Phán Xét, ngươi sẽ thấy những kẻ nói dối cho Allah, gương mặt của họ sẽ trở nên đen sạm. Lẽ nào Hỏa Ngục không phải là chỗ ngụ cho những kẻ tự cao tự đại hay sao?');
INSERT INTO "vn_ruwwad" VALUES(39,61,'39:61','Và Allah sẽ cứu rỗi những người ngoan đạo (sợ Ngài) bằng thành tựu của họ. Không có một điều xấu xa nào chạm vào họ và họ sẽ không đau buồn.');
INSERT INTO "vn_ruwwad" VALUES(39,62,'39:62','Allah là Đấng Tạo Hóa tất cả vạn vật và Ngài trông coi và quản lý mọi vật.');
INSERT INTO "vn_ruwwad" VALUES(39,63,'39:63','Mọi chìa khóa (kho tàng) của các tầng trời và trái đất đều thuộc về Ngài; và những ai phủ nhận các Lời Mặc Khải của Allah thì đó là những kẻ thua thiệt.');
INSERT INTO "vn_ruwwad" VALUES(39,64,'39:64','Ngươi (Thiên Sứ) hãy nói (với những kẻ đa thần): “Lẽ nào các ngươi bảo Ta thờ phượng ai khác ngoài Allah, hỡi những kẻ ngu dốt?!”');
INSERT INTO "vn_ruwwad" VALUES(39,65,'39:65','Quả thật, Ngươi cũng như các vị (Sứ Giả) trước Ngươi đều đã được mặc khải rằng: “Nếu Ngươi tổ hợp (thần linh) cùng với Allah thì việc hành đạo của Ngươi sẽ trở nên vô nghĩa và Ngươi chắc chắn sẽ là một kẻ thất bại.”');
INSERT INTO "vn_ruwwad" VALUES(39,66,'39:66','“Thay vào đó, Ngươi hãy thờ phượng chỉ một mình Allah và Ngươi hãy là một người biết ơn (Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(39,67,'39:67','(Những kẻ đa thần) đã đánh giá Allah không đúng với giá trị và quyền năng của Ngài. Vào Ngày Phục Sinh, Ngài sẽ túm lấy toàn bộ trái đất và tất cả các tầng trời sẽ được cuộn lại trong tay phải của Ngài. Thật Quang Vinh thay Ngài! Ngài tối cao vượt hẳn những gì mà họ đã qui cho Ngài.');
INSERT INTO "vn_ruwwad" VALUES(39,68,'39:68','Khi Tiếng Còi (Tận Thế) được thổi lên, tất cả những ai trong các tầng trời và tất cả những ai trong trái đất đều sẽ chết ngoại trừ ai mà Allah muốn (cho sống). Rồi khi Tiếng Còi thứ hai được thổi lên thì họ sẽ (sống lại) đứng dậy và ngóng nhìn.');
INSERT INTO "vn_ruwwad" VALUES(39,69,'39:69','Trái đất sẽ tỏa sáng với ánh hào quang của Thượng Đế (khi Ngài xuất hiện) và quyển sổ ghi chép (hành động của mỗi người) sẽ được mang ra; các vị Nabi và các nhân chứng sẽ được triệu đến và họ sẽ được phán xử bằng công lý thực sự và họ sẽ không bị đối xử bất công (bất cứ điều gì).');
INSERT INTO "vn_ruwwad" VALUES(39,70,'39:70','Mỗi linh hồn đều sẽ được đền bù đầy đủ về những gì nó đã làm; và Ngài biết rõ về những gì họ làm.');
INSERT INTO "vn_ruwwad" VALUES(39,71,'39:71','Những kẻ vô đức tin sẽ bị lùa đến Hỏa Ngục theo từng nhóm, cho đến khi họ đến nơi, các cánh cổng của Hỏa Ngục đã được mở toang, và các Thiên Thần cai quản (Hỏa Ngục) sẽ hỏi họ: “Lẽ nào các vị Sứ Giả xuất thân từ các ngươi đã không đến đọc cho các ngươi các lời mặc khải của Thượng Đế của các ngươi và lẽ nào Họ đã không cảnh báo các ngươi về Ngày gặp gỡ này của các ngươi sao?!” Họ trả lời: “Vâng, có.” Tuy nhiên, lời phán trừng phạt đã được xác nhận đối với những kẻ vô đức tin.');
INSERT INTO "vn_ruwwad" VALUES(39,72,'39:72','Rồi họ được bảo: “Các ngươi hãy đi vào cổng Hỏa Ngục để ở vĩnh viễn trong đó. Thật tồi tệ cho nơi cư ngụ của những kẻ tự cao tự đại.”');
INSERT INTO "vn_ruwwad" VALUES(39,73,'39:73','Riêng những người sợ Thượng Đế của họ sẽ được dẫn đến Thiên Đàng theo từng nhóm, cho đến khi họ đến nơi, các cánh cổng Thiên Đàng đã được mở (để nghinh đón) họ và những vị Thiên Thần gác cổng (Thiên Đàng) sẽ chào đón họ, nói: “Xin chào bằng an đến quí vị! (Trái tim và việc làm của quí vị) thật tốt đẹp, xin hãy đi vào ở trong đó vĩnh viễn.”');
INSERT INTO "vn_ruwwad" VALUES(39,74,'39:74','(Những người có đức tin, sau khi vào Thiên Đàng) họ sẽ nói: “Alhamdulillah, Đấng đã giữ đúng lời hứa của Ngài với bầy tôi và cho bầy tôi thừa hưởng vùng đất này. (Alhamdulillah, Đấng đã cho) bầy tôi tự do đi đứng bất cứ nơi nào bầy tôi muốn trong Thiên Đàng (của Ngài). Đây quả là phần thưởng vô cùng hồng phúc cho những người làm (việc tốt)!”');
INSERT INTO "vn_ruwwad" VALUES(39,75,'39:75','Ngươi sẽ thấy các Thiên Thần vây quanh Ngai Vương (của Allah) khắp mọi phía tán dương và ca ngợi Thượng Đế của họ. Tất cả tạo vật đều được phân xử đúng với sự thật. (Rồi) sẽ có lời hoan hô: “Alhamdulillah, Thượng Đế của vũ trụ và vạn vật!”');
INSERT INTO "vn_ruwwad" VALUES(40,1,'40:1','Ha. Mim.');
INSERT INTO "vn_ruwwad" VALUES(40,2,'40:2','Kinh (Qur’an) được ban xuống từ nơi Allah, Đấng Toàn Năng, Đấng Toàn Tri.');
INSERT INTO "vn_ruwwad" VALUES(40,3,'40:3','Đấng tha thứ tội lỗi và chấp nhận sự sám hối, Đấng nghiêm khắc trong việc trừng phạt, Đấng hồng phúc và tử tế, không có Thượng Đế đích thực nào khác ngoài Ngài. Tất cả đều phải trở về trình diện Ngài (vào Ngày Phán Xét).');
INSERT INTO "vn_ruwwad" VALUES(40,4,'40:4','Không ai tranh cãi về các dấu hiệu của Allah ngoại trừ những kẻ vô đức tin. Do đó, Ngươi (Thiên Sứ) chớ để cho sự tung hoành của họ trong xứ đánh lừa Ngươi.');
INSERT INTO "vn_ruwwad" VALUES(40,5,'40:5','Trước họ, Người dân của Nuh đã phủ nhận, và sau Nuh, các liên minh cũng đã phủ nhận tương tự. Mỗi một cộng đồng (đó) đều âm mưu giết hại Sứ Giả của họ và tranh luận bằng cách dùng sự giả dối để vô hiệu hóa sự thật, nên TA đã bắt phạt họ. Ngươi hãy xem sự trừng phạt của TA (đối với họ) khủng khiếp như thế nào.');
INSERT INTO "vn_ruwwad" VALUES(40,6,'40:6','Giống như thế, Lời (hứa trừng phạt) của Thượng Đế Ngươi (hỡi Thiên Sứ) đối với những kẻ vô đức tin là thật rằng chúng sẽ là những người bạn của Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(40,7,'40:7','Các Thiên Thần gánh Ngai Vương của Allah và những Thiên Thần đứng hầu xung quanh Ngài đều tán dương ca tụng Thượng Đế của họ và đều có đức tin nơi Ngài; họ cầu xin sự tha thứ cho những ai có đức tin, nói: “Lạy Thượng Đế của bầy tôi! Lòng Nhân Từ và Kiến Thức của Ngài bao trùm tất cả mọi vật, xin Ngài tha thứ cho những ai quay về sám hối (với Ngài) và đi theo con đường của Ngài, và xin Ngài cứu vớt họ khỏi sự trừng phạt nơi Hỏa Ngục.”');
INSERT INTO "vn_ruwwad" VALUES(40,8,'40:8','“Lạy Thượng Đế của bầy tôi! Xin Ngài thu nhận họ vào những Ngôi Vườn Vĩnh Cửu (nơi Thiên Đàng) mà Ngài đã hứa với họ cũng như (đã hứa) với những ai ngoan đạo và đức hạnh trong số cha mẹ, vợ chồng và con cái của họ. Quả thật, Ngài là Đấng Toàn Năng, Đấng Sáng Suốt.”');
INSERT INTO "vn_ruwwad" VALUES(40,9,'40:9','“Xin Ngài bảo vệ họ khỏi mọi điều xấu. Ai tránh được điều xấu vào Ngày đó thì quả thật y đã được Ngài thương xót. Và đó là một sự thắng lợi vĩ đại.”');
INSERT INTO "vn_ruwwad" VALUES(40,10,'40:10','Quả thật, những kẻ vô đức tin sẽ được bảo: “Chắc chắn sự căm ghét của Allah đối với các ngươi còn lớn hơn sự căm ghét của các ngươi đối với nhau khi các ngươi được kêu gọi đến với đức tin nhưng các ngươi đã từ chối.”');
INSERT INTO "vn_ruwwad" VALUES(40,11,'40:11','Họ sẽ nói: “Lạy Thượng Đế của bầy tôi! Ngài đã làm cho bầy tôi chết hai lần và sống hai lần(. Giờ bầy tôi đã thú nhận tội lỗi của mình. Vậy có cách nào để thoát khỏi (Hỏa Ngục) không?”');
INSERT INTO "vn_ruwwad" VALUES(40,12,'40:12','(Họ sẽ được bảo): “Đó là bởi vì, khi Allah được cầu nguyện một mình thì các ngươi không tin còn nếu có các thần linh ngụy tạo được tổ hợp cùng với Ngài thì các ngươi lại tin. Do đó, sự phán xét chỉ thuộc quyền của một mình Allah, Đấng Tối Cao, Đấng Vĩ Đại.”');
INSERT INTO "vn_ruwwad" VALUES(40,13,'40:13','Ngài là Đấng đã phơi bày cho các ngươi thấy các dấu hiệu của Ngài và chính Ngài đã ban bổng lộc (mưa) từ trên trời xuống cho các ngươi. Nhưng không ai ghi nhớ điều đó ngoại trừ những người quay đầu sám hối.');
INSERT INTO "vn_ruwwad" VALUES(40,14,'40:14','Bởi thế, các ngươi hãy cầu nguyện Allah, thành tâm với Ngài trong tôn giáo mặc dù những kẻ vô đức tin ghét điều đó.');
INSERT INTO "vn_ruwwad" VALUES(40,15,'40:15','Đấng cao nhất về cấp bậc, Chủ Nhân của Ngai Vương. Ngài ban Ruh (Sự Mặc Khải) theo lệnh của Ngài xuống cho ai Ngài muốn trong số bầy tôi của Ngài để người đó cảnh báo nhân loại về Ngày Hội Ngộ.');
INSERT INTO "vn_ruwwad" VALUES(40,16,'40:16','Ngày mà tất cả họ sẽ phải trình diện. Không có bất cứ điều gì của họ có thể giấu giếm khỏi Allah. Quyền phán xét vào Ngày đó thuộc về ai? (Chắc chắn ngày đó) thuộc về Allah, Đấng Duy Nhất, Đấng Tối Thượng.');
INSERT INTO "vn_ruwwad" VALUES(40,17,'40:17','Ngày đó, mỗi linh hồn sẽ được đền bù (xứng đáng) cho những gì nó đã kiếm được. Ngày đó không có bất công. Quả thật, Allah rất nhanh trong việc thanh toán (phán xét và thưởng phạt).');
INSERT INTO "vn_ruwwad" VALUES(40,18,'40:18','Ngươi (hỡi Thiên Sứ) hãy cảnh báo họ về Ngày (Phán Xét) đang tiến đến gần, Ngày mà những quả tim (do kinh hoàng) sẽ nhảy lên nơi cổ họng, làm nghẹt thở. Những kẻ làm điều sai quấy sẽ không có bạn bè và cũng không có người can thiệp nào để nhờ cậy.');
INSERT INTO "vn_ruwwad" VALUES(40,19,'40:19','Ngài (Allah) biết rõ điều gian trá nơi cặp mắt của họ cũng như những gì được giấu kín trong lòng họ.');
INSERT INTO "vn_ruwwad" VALUES(40,20,'40:20','Allah sẽ phán quyết theo sự thật (không hề có sự bất công nào), còn những kẻ mà họ khấn vái ngoài Ngài hoàn toàn không quyết định được gì. Quả thật, Allah là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "vn_ruwwad" VALUES(40,21,'40:21','Lẽ nào chúng (những kẻ thờ đa thần này) không đi chu du khắp nơi trên trái đất để xem kết cuộc của những ai trước chúng như thế nào ư? Cộng đồng trước chúng đã mạnh hơn chúng về quyền lực và dấu tích của họ vẫn còn trên trái đất. Allah đã bắt phạt họ vì tội lỗi của họ và họ đã không có ai cứu giúp thoát khỏi sự trừng phạt của Allah.');
INSERT INTO "vn_ruwwad" VALUES(40,22,'40:22','Đó là bởi vì các Sứ Giả của họ đã đến gặp họ với những bằng chứng rõ ràng, nhưng họ đã không tin, cho nên Allah đã bắt phạt họ. Quả thật, Ngài mạnh mẽ và nghiêm khắc trong việc trừng phạt.');
INSERT INTO "vn_ruwwad" VALUES(40,23,'40:23','Quả thật, TA đã phái Musa cùng với các phép lạ của TA và một thẩm quyền rõ ràng,');
INSERT INTO "vn_ruwwad" VALUES(40,24,'40:24','Đến gặp Pha-ra-ông, Haman và Qarun nhưng chúng đều bảo Musa là một tên phù thủy lừa bịp.');
INSERT INTO "vn_ruwwad" VALUES(40,25,'40:25','Khi (Musa) mang chân lý từ nơi TA đến cho chúng thì chúng bảo: “Hãy giết các đứa con trai của những ai có đức tin đi theo Y và tha sống phụ nữ của chúng.” Tuy nhiên, mưu đồ của những kẻ vô đức tin chỉ rơi vào lầm lạc.');
INSERT INTO "vn_ruwwad" VALUES(40,26,'40:26','Pha-ra-ông bảo: “Để ta giết Musa và để nó gọi Thượng Đế của nó đến cứu. Ta thực sự sợ nó sẽ làm thay đổi tôn giáo của các ngươi hoặc nó sẽ gây nhiễu loạn trong xứ.”');
INSERT INTO "vn_ruwwad" VALUES(40,27,'40:27','Musa nói: “Ta cầu xin Thượng Đế của Ta và cũng là Thượng Đế của quí ngài che chở Ta tránh khỏi tất cả những kẻ tự cao tự đại không có đức tin nơi Ngày Phán Xét (Cuối Cùng).”');
INSERT INTO "vn_ruwwad" VALUES(40,28,'40:28','Một người đàn ông có đức tin trong gia tộc của Pha-ra-ông đã giấu kín đức tin của mình nói: “Lẽ nào quí ngài muốn giết một người chỉ vì y nói ‘Thượng Đế của tôi là Allah’ trong khi Y đã mang những bằng chứng rõ ràng từ Thượng Đế của quí ngài đến cho quí ngài sao? Nếu Y là một tên nói dối thì Y sẽ phải chịu tội về lời gian dối của Y. Nhưng nếu Y là một người nói thật thì điều mà Y cảnh báo quí ngài chắc chắn sẽ xảy đến với quí ngài. Quả thật, Allah không hướng dẫn kẻ phạm tội, kẻ gian dối.”');
INSERT INTO "vn_ruwwad" VALUES(40,29,'40:29','“Thưa quí ngài, ngày hôm nay mọi quyền hành đều thuộc về quí ngài. Nhưng ai sẽ cứu chúng ta khỏi sự trừng phạt của Allah nếu như nó xảy đến với chúng ta?” Pha-ra-ông nói: “Ta chỉ trình bày cho các ngươi thấy điều mà ta thấy và ta cũng chỉ muốn hướng dẫn các ngươi theo con đường chân chính mà thôi.”');
INSERT INTO "vn_ruwwad" VALUES(40,30,'40:30','(Người có đức tin đó) nói: “Thưa quí ngài! Tôi thực sự sợ giùm cho quí vị gặp phải (số phận) giống như (nó đã xảy ra) vào ngày của liên quân (tòng phạm!)”');
INSERT INTO "vn_ruwwad" VALUES(40,31,'40:31','“Giống như số phận người dân của Nuh, người dân của ‘Ad và Thamud cũng như những kẻ thời sau họ. Quả thật, Allah không hề bất công với đám bầy tôi của Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(40,32,'40:32','“Thưa quí ngài! Tôi thực sự sợ giùm cho quí ngài về Ngày réo gọi nhau.”');
INSERT INTO "vn_ruwwad" VALUES(40,33,'40:33','“Ngày mà quí ngài sẽ quay lưng tháo chạy (khi nhìn thấy Hỏa Ngục). (Ngày mà) quí ngài sẽ không có ai che chở khỏi (sự trừng phạt) của Allah. Ai mà Allah làm cho lầm lạc thì sẽ không có được người hướng dẫn.”');
INSERT INTO "vn_ruwwad" VALUES(40,34,'40:34','“Quả thật, trước đây Yusuf (Joseph) đã đến gặp quí ngài với những bằng chứng rõ ràng nhưng quí ngài đã luôn hoài nghi về những gì Người mang đến; rồi cho đến khi Người qua đời thì quí ngài lại nói rằng Allah sẽ không bao giờ dựng một Sứ Giả nào sau Người nữa. Như thế đó, Allah làm lầm lạc những ai là kẻ tội lỗi và hay hoài nghi.”');
INSERT INTO "vn_ruwwad" VALUES(40,35,'40:35','“Những người hay tranh cãi về các dấu hiệu của Allah nhưng lại không có bất cứ thẩm quyền (bằng chứng) nào (được mang đến cho họ), đối với Allah và đối với những người có đức tin, họ là những kẻ đáng ghét và đáng căm thù. Tương tự như thế, Allah niêm kín trái tim của từng tên tự cao tự đại (trước chân lý).”');
INSERT INTO "vn_ruwwad" VALUES(40,36,'40:36','Pha-ra-ông nói (với Haman, tể tướng của hắn): “Này Haman! Khanh hãy xây cho trẫm một cái tháp cao để trẫm có thể đi tới những con đường.”');
INSERT INTO "vn_ruwwad" VALUES(40,37,'40:37','“Những con đường của các tầng trời đưa trẫm đi diện kiến Thượng Đế của Musa. Nhưng trẫm thực sự nghĩ nó là một kẻ dối trá.” Như thế đó, hành động tội lỗi đã trở nên hấp dẫn đối với Pha-ra-ông và hắn bị cản trở khỏi con đường (của Allah). Và mưu đồ của Pha-ra-ông chỉ đưa đến thật bại.');
INSERT INTO "vn_ruwwad" VALUES(40,38,'40:38','Người có đức tin (thuộc gia tộc của Pha-ra-ông) đó nói: “Thưa quí ngài! Quí ngài hãy nghe lời tôi. Tôi sẽ hướng dẫn quí ngài con đường chân chính.”');
INSERT INTO "vn_ruwwad" VALUES(40,39,'40:39','“Thưa quí ngài! Quả thật, đời sống trần tục này chỉ là một sự hưởng thụ (tạm bợ và ngắn ngủi), Đời Sau mới thực sự là cõi trường tồn và vĩnh cửu.”');
INSERT INTO "vn_ruwwad" VALUES(40,40,'40:40','“Người nào làm điều xấu thì chỉ bị phạt tương ứng với tội đã phạm. Người nào làm điều thiện tốt, dù nam hay nữ, và là người có đức tin thì sẽ vào Thiên Đàng, nơi mà họ sẽ được ban cho vô vàn bổng lộc.”');
INSERT INTO "vn_ruwwad" VALUES(40,41,'40:41','“Thưa quí ngài! Sao thế, tôi kêu gọi quí ngài đến với sự cứu rỗi còn quí ngài thì kêu gọi tôi đến với Hỏa Ngục?!”');
INSERT INTO "vn_ruwwad" VALUES(40,42,'40:42','“Quí ngài kêu gọi tôi vô đức tin nơi Allah và tổ hợp thần linh cùng với Ngài điều mà tôi chẳng biết gì về nó trong lúc tôi kêu gọi quí ngài đến với Đấng Toàn Năng, Đấng Hằng Tha Thứ.”');
INSERT INTO "vn_ruwwad" VALUES(40,43,'40:43','“Chắc chắn thứ mà quí ngài kêu gọi tôi đến với nó không hề xứng đáng để cầu nguyện ở đời này và cả Đời Sau, trong khi tất cả chúng ta đều sẽ phải trở về trình diện Allah; quả thật những kẻ tội lỗi sẽ là làm bạn với Hỏa Ngục.”');
INSERT INTO "vn_ruwwad" VALUES(40,44,'40:44','“Rồi đây, quí ngài sẽ nhớ lại những điều tôi nói (hôm nay). Tôi giao phó việc của mình cho Allah bởi quả thật Allah là Đấng Hằng Thấy đám bầy tôi (của Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(40,45,'40:45','Bởi thế, Allah cứu y (người có đức tin) khỏi những điều xấu xa mà chúng đã âm mưu (giết hại y); và sự trừng phạt tồi tệ đã bao vây đám thuộc hạ của Pha-ra-ông.');
INSERT INTO "vn_ruwwad" VALUES(40,46,'40:46','(Sau khi chết) chúng chắc chắn sẽ chạm trán với Hỏa Ngục sáng chiều (nơi cõi mộ), và vào Ngày mà Giờ (Phục Sinh) được thiết lập, (Allah sẽ phán bảo các Thiên Thần): “Các ngươi hãy tống thuộc hạ của Pha-ra-ông vào chỗ trừng phạt khắc nghiệt nhất.”');
INSERT INTO "vn_ruwwad" VALUES(40,47,'40:47','(Ngươi – hỡi Thiên Sứ, hãy nghĩ đến cảnh) khi chúng tranh cãi nhau trong Hỏa Ngục, những kẻ yếu thế nói với những kẻ (lãnh đạo) kiêu ngạo: “Quả thật, chúng tôi đã đi theo các người, vậy các người có thể giải tỏa cho chúng tôi một phần của Hỏa Ngục không?”');
INSERT INTO "vn_ruwwad" VALUES(40,48,'40:48','Những kẻ (lãnh đạo) kiêu ngạo nói: “Toàn bộ chúng ta đều ở trong Hỏa Ngục cả (chẳng một ai có thể thoát được), bởi quả thật Allah đã phân xử (công bằng) giữa đám bầy tôi (của Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(40,49,'40:49','Những kẻ (bị trừng phạt) trong Hỏa Ngục nói với các Thiên Thần (cai quản Hỏa Ngục): “Quí ngài làm ơn xin Thượng Đế của quí ngài giảm bớt hình phạt cho chúng tôi, một ngày thôi cũng được?”');
INSERT INTO "vn_ruwwad" VALUES(40,50,'40:50','(Các Thiên Thần cai quản Hỏa Ngục) bảo: “Chẳng lẽ các vị Thiên Sứ của các ngươi đã không mang đến cho các ngươi những bằng chứng rõ rệt sao?” Chúng nói: “Vâng, có!” (Các Thiên Thần cai quản Hỏa Ngục) bảo chúng: “Vậy các ngươi hãy tự cầu xin.” Tuy nhiên, (vào Ngày hôm đó) lời cầu xin của những kẻ vô đức sẽ không bao giờ được chấp nhận.');
INSERT INTO "vn_ruwwad" VALUES(40,51,'40:51','TA (Allah) chắc chắn sẽ giúp các Sứ Giả của TA và những người có đức tin giành chiến thắng trên cuộc sống trần gian này cũng như vào Ngày mà các nhân chứng sẽ đứng ra làm chứng.');
INSERT INTO "vn_ruwwad" VALUES(40,52,'40:52','Ngày mà lời biện minh của những kẻ làm điều sai quấy sẽ không giúp ích được gì cho họ. Họ sẽ phải nhận lấy sự nguyền rủa và một chỗ ngụ tồi tệ.');
INSERT INTO "vn_ruwwad" VALUES(40,53,'40:53','Quả thật, TA đã ban cho Musa nguồn chỉ đạo và đã để cho con cháu của Israel thừa hưởng Kinh Sách.');
INSERT INTO "vn_ruwwad" VALUES(40,54,'40:54','Nguồn chỉ đạo và sự nhắc nhở dành cho những người thông hiểu.');
INSERT INTO "vn_ruwwad" VALUES(40,55,'40:55','Cho nên, Ngươi (Thiên Sứ) hãy kiên nhẫn, lời hứa của Allah chắc chắn là thật. Ngươi hãy cầu xin Thượng Đế của Ngươi tha thứ tội lỗi cho Ngươi và Ngươi hãy tán dương ca tụng Thượng Đế của Ngươi sáng chiều.');
INSERT INTO "vn_ruwwad" VALUES(40,56,'40:56','Quả thật, những ai tranh cãi về các dấu hiệu của Allah một cách không có bằng chứng làm cơ sở mà chỉ muốn tự cao tự đại trong lòng mình thì chúng sẽ không bao giờ đạt được. Do đó, Ngươi (Thiên Sứ) hãy xin Allah che chở bởi vì Ngài là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "vn_ruwwad" VALUES(40,57,'40:57','Việc tạo hóa các tầng trời và trái đất thực sự vĩ đại hơn việc tạo hóa loài người, nhưng đa số nhân loại không biết.');
INSERT INTO "vn_ruwwad" VALUES(40,58,'40:58','Kẻ mù và người sáng mắt không hề ngang bằng nhau; những người có đức tin và hành thiện với những kẻ làm điều xấu, tội lỗi cũng không ngang bằng nhau. Tuy nhiên, các ngươi chẳng mấy lưu tâm!');
INSERT INTO "vn_ruwwad" VALUES(40,59,'40:59','Giờ Tận Thế chắc chắn sẽ đến, không có gì phải hoài nghi về điều đó, nhưng đa số nhân loại không có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(40,60,'40:60','Thượng Đế của các ngươi phán: “Các ngươi hãy cầu nguyện TA, TA sẽ đáp lại các ngươi. Riêng những kẻ tự cao tự đại không chịu thờ phượng TA, họ chắc chắn sẽ vào Hỏa Ngục một cách đáng khinh.”');
INSERT INTO "vn_ruwwad" VALUES(40,61,'40:61','Allah là Đấng đã tạo ra cho các ngươi ban đêm để các ngươi nghỉ ngơi và ban ngày để các ngươi nhìn và quan sát. Quả thật, Allah có vô vàn thiên lộc cho loài người nhưng đa số nhân loại không biết tri ân.');
INSERT INTO "vn_ruwwad" VALUES(40,62,'40:62','Đó là Allah, Thượng Đế của các ngươi. Ngài là Đấng Tạo Hóa vạn vật, không có Thượng Đế đích thực nào ngoài Ngài. Vậy tại sao các ngươi lánh xa (không thờ phượng) Ngài?');
INSERT INTO "vn_ruwwad" VALUES(40,63,'40:63','Những kẻ phủ nhận các dấu hiệu của Allah thường lánh xa Ngài như thế.');
INSERT INTO "vn_ruwwad" VALUES(40,64,'40:64','Allah là Đấng đã tạo cho các ngươi trái đất làm nơi cư ngụ và bầu trời làm mái che; Ngài tạo hình thể của các ngươi và làm cho hình thể của các ngươi đẹp nhất (so với các sinh vật khác), và Ngài cung dưỡng các ngươi bằng thực phẩm tốt lành. Đó là Allah, Thượng Đế của các ngươi. Thật phúc thay Allah, Thượng Đế của vũ trụ và vạn vật!');
INSERT INTO "vn_ruwwad" VALUES(40,65,'40:65','Ngài là Đấng Sống Mãi, không có Thượng Đế đích thực nào khác ngoài Ngài. Bởi thế, hãy cầu nguyện Ngài, một lòng thờ phượng một mình Ngài. Alhamdulillah (xin tạ ơn, ca ngợi và tán dương Allah), Thượng Đế của vũ trụ và vạn vật.');
INSERT INTO "vn_ruwwad" VALUES(40,66,'40:66','Ngươi (Thiên Sứ) hãy nói: “Allah cấm Ta thờ phượng những thần linh mà các ngươi cầu nguyện ngoài Ngài sau khi Ta đã nhận được những bằng chứng rõ ràng từ Thượng Đế của Ta và Ta được lệnh phải thần phục Thượng Đế của vũ trụ và vạn vật.”');
INSERT INTO "vn_ruwwad" VALUES(40,67,'40:67','Ngài là Đấng đã tạo hóa các ngươi từ đất bụi, rồi từ giọt tinh dịch, rồi từ một hòn máu đặc, sau đó Ngài đưa các ngươi ra (khỏi bụng mẹ của các ngươi) thành những đứa bé, sau đó Ngài (làm cho các ngươi phát triển) đến (tuổi) trưởng thành, và sau đó Ngài làm cho các ngươi trở thành già yếu. Trong các ngươi, có người chết sớm; và có người sống đến thời hạn qui định; (qua sự việc đó) mong rằng các ngươi thông hiểu.');
INSERT INTO "vn_ruwwad" VALUES(40,68,'40:68','Ngài là Đấng làm cho sống và làm cho chết. Khi Ngài định một điều (hay vật) gì, Ngài chỉ cần phán “Hãy thành”, nó sẽ thành (tức thì theo đúng ý của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(40,69,'40:69','Lẽ nào Ngươi (Thiên Sứ) không nhận thấy những kẻ đã tranh cãi về các dấu hiệu của Allah xa lánh (Chân Lý) như thế nào ư?');
INSERT INTO "vn_ruwwad" VALUES(40,70,'40:70','Những kẻ đã phủ nhận Qur’an và những điều mà TA đã cử các Sứ Giả của TA mang đến. Rồi đây, chúng sẽ sớm biết (hậu quả của việc phủ nhận đó).');
INSERT INTO "vn_ruwwad" VALUES(40,71,'40:71','(Chúng sẽ biết hậu quả) khi những chiếc gông cùm được đóng vào cổ của chúng và những sợi dây xích (xiềng chân của chúng), chúng sẽ bị lôi đi.');
INSERT INTO "vn_ruwwad" VALUES(40,72,'40:72','(Chúng sẽ bị lôi đi) vào trong dòng nước cực sôi; rồi vào trong Lửa (cháy bùng), nơi mà chúng sẽ bị thiêu đốt.');
INSERT INTO "vn_ruwwad" VALUES(40,73,'40:73','Rồi có lời bảo chúng: “Đâu rồi những thần linh mà các ngươi đã thờ phượng');
INSERT INTO "vn_ruwwad" VALUES(40,74,'40:74','Ngoài Allah?” Chúng nói: “Họ đã bỏ chúng tôi đi mất dạng. Không, trước đây chúng tôi đã không cầu nguyện và tôn thờ ai xứng đáng cả.” Allah làm cho những kẻ vô đức tin lầm lạc như thế đó.');
INSERT INTO "vn_ruwwad" VALUES(40,75,'40:75','(Rồi có lời bảo chúng): “(Sự trừng phạt) đó là bởi vì các ngươi đã hả hê với những điều không chân lý và bởi vì các ngươi đã cư xử xấc xược trên trái đất.”');
INSERT INTO "vn_ruwwad" VALUES(40,76,'40:76','“Các ngươi hãy bước vào các cánh cổng của Hỏa Ngục để sống mãi mãi trong đó. Thật tồi tệ cho chỗ ngụ của những kẻ tự cao tự đại.”');
INSERT INTO "vn_ruwwad" VALUES(40,77,'40:77','Vì vậy, Ngươi hãy kiên nhẫn (hỡi Thiên Sứ) bởi lời hứa của Allah chắc chắn là thật. Và cho dù TA cho Ngươi thấy một số điều mà TA đã hứa với chúng hoặc TA bắt hồn Ngươi thì chúng vẫn phải trở về trình diện TA.');
INSERT INTO "vn_ruwwad" VALUES(40,78,'40:78','Quả thật, TA đã cử các Sứ Giả đến với nhân loại trước Ngươi (hỡi Thiên Sứ). Trong số đó có người TA đã kể cho Ngươi câu chuyện của Họ và có người TA đã không kể cho Ngươi. Bất kỳ Sứ Giả nào mang một dấu hiệu (hay một câu Kinh) đến cũng đều phải có sự cho phép của Allah. Vì vậy, khi mệnh lệnh của Allah đã được ban hành, sự việc sẽ được quyết định đúng với chân lý, và những kẻ đi theo điều ngụy tạo lúc đó sẽ mất (tất cả).');
INSERT INTO "vn_ruwwad" VALUES(40,79,'40:79','Allah là Đấng đã tạo ra cho các ngươi các loài gia súc, một số các ngươi dùng để cưỡi và một số các ngươi dùng để ăn thịt.');
INSERT INTO "vn_ruwwad" VALUES(40,80,'40:80','Và nơi (các loài gia súc đó) các ngươi còn được một số lợi ích khác nữa. Và nhờ chúng các ngươi có thể đạt được điều mong muốn trong lòng của các ngươi. Và các ngươi được chuyên chở trên lưng của chúng và trên (các) con tàu.');
INSERT INTO "vn_ruwwad" VALUES(40,81,'40:81','Ngài cho các ngươi thấy các dấu hiệu của Ngài, vậy đâu là các dấu hiệu của Allah mà các ngươi chối bỏ?');
INSERT INTO "vn_ruwwad" VALUES(40,82,'40:82','Lẽ nào chúng đã không đi chu du khắp nơi trên trái đất để quan sát kết cuộc của những kẻ trước chúng như thế nào? Các thế hệ trước chúng đông hơn và mạnh hơn chúng và những chứng tích của họ vẫn hãy còn trên trái đất. Nhưng những gì mà họ đã làm ra (từ sức mạnh cũng như số lượng đông của họ) chẳng giúp ích gì được cho họ cả.');
INSERT INTO "vn_ruwwad" VALUES(40,83,'40:83','Bởi lẽ khi những Sứ Giả của họ đến gặp họ với những bằng chứng rõ ràng thì họ lại chỉ biết hả hê với kiến thức (và tài năng) mà họ có được. Cho nên, (sự trừng phạt) đã vây hãm họ bởi những điều mà họ thường chế giễu.');
INSERT INTO "vn_ruwwad" VALUES(40,84,'40:84','Khi họ nhìn thấy sự trừng phạt của TA thì họ mới nói: “Bầy tôi đã có đức tin nơi Allah, Đấng Duy Nhất, và bầy tôi phủ nhận toàn bộ các thần linh mà bầy tôi đã tổ hợp với Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(40,85,'40:85','Tuy nhiên, đức tin của họ vào lúc họ nhìn thấy sự trừng phạt của TA chẳng thể giúp ích được gì cho họ. (Đó là) đường lối của Allah (trong việc) đối xử với đám bầy tôi (vô đức tin và bất tuân) của Ngài. (Cho nên), những kẻ vô đức tin cuối cùng sẽ thất bại và thua thiệt.');
INSERT INTO "vn_ruwwad" VALUES(41,1,'41:1','Ha. Mim.');
INSERT INTO "vn_ruwwad" VALUES(41,2,'41:2','(Qur’an này) được ban xuống từ Đấng Độ Lượng, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(41,3,'41:3','(Nó) là một Kinh Sách với các câu được giải thích chi tiết. (Nó) là Kinh Qur’an bằng tiếng Ả-rập dành cho những người hiểu biết.');
INSERT INTO "vn_ruwwad" VALUES(41,4,'41:4','(Nó) là nguồn tin mừng và là lời cảnh báo. Tuy nhiên, đa số bọn họ quay mặt ngoảnh đi nên không nghe được (sự chỉ đạo và hướng dẫn).');
INSERT INTO "vn_ruwwad" VALUES(41,5,'41:5','Họ bảo: “Trái tim của bọn ta đã được bao kín (không tiếp nhận) những điều Ngươi (Thiên Sứ) kêu gọi bọn ta, tai của bọn ta bị điếc và giữa bọn ta và Ngươi có một bức màn ngăn cách. Vì vậy, Ngươi hãy làm (theo đường lối của Ngươi) còn bọn ta (làm theo đường lối của bọn ta).”');
INSERT INTO "vn_ruwwad" VALUES(41,6,'41:6','Ngươi (Thiên Sứ) hãy nói với họ: “Quả thật, Ta chỉ là một người phàm như các người, tuy nhiên, Ta được mặc khải cho biết rằng Thượng Đế của các người chỉ là một Thượng Đế duy nhất. Vì vậy, các ngươi hãy theo con đường ngay chính dẫn đến Ngài và các ngươi hãy xin Ngài tha thứ. Thật khốn thay cho những kẻ thờ đa thần.”');
INSERT INTO "vn_ruwwad" VALUES(41,7,'41:7','(Đó là) những kẻ không xuất Zakah và vô đức tin nơi cõi Đời Sau.');
INSERT INTO "vn_ruwwad" VALUES(41,8,'41:8','Quả thật, những người có đức tin và hành thiện chắc chắn sẽ được ban thưởng một phần thưởng vô tận (Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(41,9,'41:9','Ngươi (Thiên Sứ) hãy nói với họ: “Lẽ nào các người phủ nhận Đấng đã tạo hóa trái đất trong hai ngày và lẽ nào các người lại dựng lên cùng với Ngài những thần linh ngang vai trong khi Ngài là Thượng Đế của vũ trụ và vạn vật ư?”');
INSERT INTO "vn_ruwwad" VALUES(41,10,'41:10','Ngài đã đặt trên bề mặt của trái đất những quả núi vững chắc, Ngài đã ban phúc cho trái đất và phân định lương thực cho dân cư của nó trong bốn ngày, đồng đều cho những người đòi hỏi.');
INSERT INTO "vn_ruwwad" VALUES(41,11,'41:11','Sau đó, Ngài hướng về bầu trời (lúc đó) chỉ là một lớp khói, Ngài phán bảo nó và trái đất: “Hai ngươi hãy đến (thành theo lệnh), thuận hay không thuận?” Cả hải cùng thưa: “Bầy tôi xin thuận đến (thành theo lệnh Ngài).”');
INSERT INTO "vn_ruwwad" VALUES(41,12,'41:12','Rồi Ngài đã hoàn tất việc tạo hóa bảy tầng trời trong hai ngày (thứ năm và thứ sáu) và Ngài mặc khải cho mỗi tầng trời công việc của riêng nó. Và TA đã trang hoàng tầng trời hạ giới với những chiếc đèn (là các vì sao) và (TA đã cử các Thiên Thần) canh giữ nó. Đó là sự an bày và định đoạt của Đấng Toàn Năng, Đấng Toàn Tri.');
INSERT INTO "vn_ruwwad" VALUES(41,13,'41:13','Nếu chúng ngoảnh đi thì Ngươi (hỡi Thiên Sứ) hãy nói (với chúng): “Ta cảnh báo các ngươi về tia sét giống như tia sét đã đánh dân ‘Ad và dân Thamud.”');
INSERT INTO "vn_ruwwad" VALUES(41,14,'41:14','(Điều đó xảy ra) khi các Sứ Giả (của Allah) đã đến với chúng từ phía trước lẫn phía sau, (nói): “Các ngươi hãy thờ phượng một mình Allah thôi.” Chúng bảo: “Nếu Thượng Đế của bọn ta thực sự muốn thì chắc chắn Ngài đã cử các Thiên Thần xuống (làm Sứ Giả). Vì vậy, bọn ta không tin nơi điều các người mang đến.”');
INSERT INTO "vn_ruwwad" VALUES(41,15,'41:15','Đối với ‘Ad (đám dân của Hud), chúng đã ngạo mạn trên trái đất một cách không chính đáng. Chúng nói: “Ai mạnh hơn bọn ta?” Lẽ nào chúng không nhận thấy Allah, Đấng đã tạo hóa chúng, mạnh hơn chúng hay sao? Quả thật, chúng đã thường gạt đi các dấu hiệu của TA.');
INSERT INTO "vn_ruwwad" VALUES(41,16,'41:16','Cho nên, TA đã gởi đến chúng một trận gió gào thét (cuồng phong) suốt những ngày bất hạnh để TA cho chúng nếm lấy hình phạt trong sự ô nhục trên cuộc sống trần gian; tuy nhiên, sự trừng phạt ở cõi Đời Sau còn nhục nhã hơn; và chúng sẽ không được ai cứu giúp.');
INSERT INTO "vn_ruwwad" VALUES(41,17,'41:17','Còn đối với Thamud (đám dân của Saleh). TA đã hướng dẫn chúng nhưng chúng thích sự mù quáng (lầm lạc) hơn sự hướng dẫn. Vì vậy, tia sét của hình phạt ô nhục đã đánh chúng bởi những tội lỗi mà chúng đã làm.');
INSERT INTO "vn_ruwwad" VALUES(41,18,'41:18','Và TA đã cứu những người có đức tin và kính sợ (TA).');
INSERT INTO "vn_ruwwad" VALUES(41,19,'41:19','Vào Ngày mà những kẻ thù của Allah sẽ được tập trung dẫn đến Hỏa Ngục; chúng sẽ được tập hợp thành từng hàng.');
INSERT INTO "vn_ruwwad" VALUES(41,20,'41:20','Mãi cho đến khi chúng đến (Hỏa Ngục) thì tai, mắt và da của chúng sẽ làm chứng chống lại chúng về những tội lỗi mà chúng đã làm.');
INSERT INTO "vn_ruwwad" VALUES(41,21,'41:21','Chúng sẽ nói với các lớp da của chúng: “Tại sao các ngươi làm chứng chống lại bọn ta?” Các lớp da trả lời: “Allah làm cho chúng tôi biết nói giống như Ngài làm cho mọi vật biết nói; và Ngài đã tạo ra các người lần đầu và các người sẽ được đưa trở về trình diện Ngài.”');
INSERT INTO "vn_ruwwad" VALUES(41,22,'41:22','“Các người đã không chịu che đậy bản thân mình để khỏi bị tai, mắt và da của các người khiến chúng làm chứng chống lại các người, mà ngược lại, các người đã nghĩ rằng Allah không biết nhiều về những điều các người làm.”');
INSERT INTO "vn_ruwwad" VALUES(41,23,'41:23','“Đó là ý nghĩ mà các người đã nghĩ về Thượng Đế của các người, nó đã hủy hoại các người. Cho nên, các người đã trở thành những kẻ thua thiệt và thất bại.”');
INSERT INTO "vn_ruwwad" VALUES(41,24,'41:24','Nhưng cho dù chúng có kiên nhẫn chịu đựng thì Hỏa Ngục vẫn là nơi cư trú của chúng; và nếu chúng có xin được giảm nhẹ (hình phạt) thì chúng cũng sẽ không thuộc những người được giảm nhẹ.');
INSERT INTO "vn_ruwwad" VALUES(41,25,'41:25','Và TA đã chỉ định cho chúng những người bạn đồng hành (những tên Shaytan). Những người bạn đồng hành này của chúng làm cho chúng thích thú với những tội lỗi đằng trước chúng và những sai trái đằng sau chúng. Và lời (hứa trừng phạt) đã có hiệu lực với các cộng đồng trước chúng từ loài Jinn và loài người. Thật vậy, (những cộng đồng trước chúng) đều là những kẻ thua thiệt và thất bại.');
INSERT INTO "vn_ruwwad" VALUES(41,26,'41:26','Những kẻ vô đức tin khuyên bảo nhau rằng “Mọi người đừng thèm nghe Qur’an này, hãy nói chuyện ồn ào và gây náo động (khi Nó được xướng lên), như thế có lẽ các ngươi sẽ nắm ưu thế (trong việc ngăn Muhammad xướng Nó và kêu gọi đến với Nó).”');
INSERT INTO "vn_ruwwad" VALUES(41,27,'41:27','Nhưng TA chắc chắn sẽ khiến những kẻ vô đức tin nếm lấy sự trừng phạt khắc nghiệt và TA chắc chắn sẽ đền bù điều tồi tệ nhất cho chúng bởi những gì mà chúng đã làm.');
INSERT INTO "vn_ruwwad" VALUES(41,28,'41:28','Đó là sự đền bù xứng đáng dành cho những kẻ thù của Allah. Hỏa Ngục, đối với chúng, sẽ là ngôi nhà vĩnh cửu, nó như là một sự bù đắp cho việc chúng đã chối bỏ các Lời Mặc Khải của TA.');
INSERT INTO "vn_ruwwad" VALUES(41,29,'41:29','Những kẻ vô đức tin sẽ nói: “Lạy Thượng Đế của bầy tôi! Xin Ngài cho bầy tôi thấy những kẻ đã dắt bầy tôi lầm lạc từ loài Jinn và loài người để bầy tôi chà đạp chúng dưới bàn chân của bầy tôi khiến chúng thành những kẻ thấp hèn nhất.”');
INSERT INTO "vn_ruwwad" VALUES(41,30,'41:30','Quả thật, những ai đã nói “Thượng Đế của chúng tôi là Allah” và sau đó vẫn đi đúng hướng, các Thiên Thần sẽ xuống động viên họ (lúc hấp hối): “Đừng sợ hãi và đừng đau buồn, các ngươi hãy vui lên với tin mừng về Thiên Đàng mà các ngươi đã được hứa!”');
INSERT INTO "vn_ruwwad" VALUES(41,31,'41:31','“Chúng tôi là đồng minh của các ngươi trên cuộc sống trần gian này và ở cõi Đời Sau. Rồi đây (trong Thiên Đàng) các ngươi sẽ có được bất cứ thứ gì mà bản thân các ngươi ước muốn và các ngươi sẽ có được bất cứ thứ gì các ngươi yêu cầu.”');
INSERT INTO "vn_ruwwad" VALUES(41,32,'41:32','“Một sự khoan đãi từ Đấng Tha Thứ, Đấng Thương Xót.”');
INSERT INTO "vn_ruwwad" VALUES(41,33,'41:33','Còn ai có lời nói tốt đẹp hơn người đã kêu gọi đến với Allah, hành thiện và nói “Tôi là một người Muslim.”');
INSERT INTO "vn_ruwwad" VALUES(41,34,'41:34','Điều tốt và điều xấu không ngang bằng nhau. Hãy dùng điều tốt đẩy lùi điều xấu; (nếu ngươi làm được như thế thì chắc chắn có một ngày), kẻ mang lòng thù hận Ngươi (Thiên Sứ) sẽ trở thành người bạn thân thiết của Ngươi thôi.');
INSERT INTO "vn_ruwwad" VALUES(41,35,'41:35','Tuy nhiên, không ai được phú cho cái đó ngoại trừ những người thực sự kiên nhẫn và không ai được phú cho cái đó ngoại trừ những người có phần phước lớn.');
INSERT INTO "vn_ruwwad" VALUES(41,36,'41:36','Nếu có một đề nghị từ Shaytan xúi Ngươi (hỡi Thiên Sứ - làm chuyện xấu và tội lỗi) thì Ngươi hãy cầu xin Allah che chở bởi vì Ngài là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(41,37,'41:37','Ban đêm và ban ngày, mặt trời và mặt trăng là các dấu hiệu (chứng minh quyền năng vĩ đại của Allah). Các ngươi chớ đừng quỳ lạy mặt trời hay mặt trăng mà hãy quỳ lạy Allah, Đấng đã tạo ra chúng nếu các ngươi thực sự thờ phượng Ngài.');
INSERT INTO "vn_ruwwad" VALUES(41,38,'41:38','Nhưng nếu chúng tự cao tự đại (không chịu quỳ lạy Allah) thì các Thiên Thần ở nơi Thượng Đế của Ngươi (Thiên Sứ) vẫn tán dương và ca tụng Ngài cả đêm lẫn ngày và họ không bao giờ chán ngán hay mỏi mệt.');
INSERT INTO "vn_ruwwad" VALUES(41,39,'41:39','Trong các dấu hiệu (chứng minh quyền năng) của Ngài là Ngươi (Thiên Sứ) sẽ thấy đất đai khô nứt nhưng khi TA ban nước (mưa) xuống thì nó cựa mình sống lại và mọc ra (cây cối và thảo mộc). Thật vậy, Đấng đã làm cho nó sống lại chắc chắn sẽ làm cho người chết sống lại (vào Ngày Phục Sinh). Quả thật, Ngài là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(41,40,'41:40','Quả thật, những kẻ đã chế giễu và bóp méo các Lời Mặc Khải của TA chắc chắn sẽ không trốn khỏi được TA. Vậy, kẻ bị ném vào Hỏa Ngục tốt hơn hay người an toàn vào Ngày Phán Xét tốt hơn? Các ngươi cứ làm những điều các ngươi muốn bởi lẽ (Allah) luôn thấy rõ những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(41,41,'41:41','Quả thật, những kẻ phủ nhận Lời Nhắc Nhở (Qur’an) khi Nó đến với chúng (thì chắc chắn sẽ bị trừng phạt vào Ngày Phán Xét). Quả thật, (Qur’an) là một Kinh Sách rất đỗi quyền năng (dưới sự bảo vệ của Allah).');
INSERT INTO "vn_ruwwad" VALUES(41,42,'41:42','Không một điều ngụy tạo nào có thể xâm nhập vào Nó dù từ đằng trước hay đằng sau. Bởi vì Nó do Đấng Chí Minh, Đấng Đáng Ca Tụng ban xuống.');
INSERT INTO "vn_ruwwad" VALUES(41,43,'41:43','Những điều chúng nói cho Ngươi (Thiên Sứ) chẳng khác với điều đã được nói cho các Sứ Giả trước Ngươi. Quả thật, Thượng Đế của Ngươi là Đấng nắm quyền tha thứ và là Đấng sở hữu sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(41,44,'41:44','Nếu TA đã ban Qur’an xuống bằng một thứ tiếng không phải tiếng Ả-rập thì chắc chắn chúng đã nói: “Tại sao các câu kinh của nó không được giải thích chi tiết (bằng tiếng nói của bọn ta chứ)?” Lẽ nào (Qur’an) bằng ngôn ngữ không phải tiếng Ả-rập còn (Sứ Giả truyền đạt Nó) lại là người Ả-rập ư? Ngươi (Thiên Sứ) hãy nói với chúng: “(Qur’an) là một nguồn chỉ đạo và là một phương thuốc chữa lành bệnh cho những ai có đức tin. Còn những kẻ vô đức tin, tai của chúng bị điếc và Qur’an là một sự mù lòa đối với chúng. Chúng giống như những kẻ được gọi từ một nơi rất xa xôi (nên chúng chẳng nghe được tiếng của người gọi).');
INSERT INTO "vn_ruwwad" VALUES(41,45,'41:45','Quả thật, TA đã ban cho Musa Kinh Sách, nhưng đã có những tranh cãi và bất đồng trong đó. Nếu không vì một lời phán của Thượng Đế của Ngươi (Thiên Sứ) đã được phán ra từ trước thì sự việc tranh cãi và bất đồng giữa chúng đã được giải quyết xong xuôi. Thật vậy, những kẻ vô đức tin vẫn cứ hoài nghi về (Qur’an).');
INSERT INTO "vn_ruwwad" VALUES(41,46,'41:46','Người nào làm điều thiện thì được phúc cho bản thân mình, còn người nào làm điều xấu thì sẽ gặp điều xấu (tương ứng). Quả thật, Thượng Đế của Ngươi (hỡi Thiên Sứ) không bao giờ bất công với người bề tôi (của Ngài).');
INSERT INTO "vn_ruwwad" VALUES(41,47,'41:47','Kiến thức về Giờ Tận Thế chỉ thuộc một mình (Allah). Không một trái cây nào lú ra khỏi vỏ của nó cũng như không một người phụ nữ nào mang thai và hạ sinh mà Ngài lại không biết. Vào Ngày mà Ngài sẽ gọi chúng đến hỏi: “Đâu rồi những thần linh mà các ngươi cho là những đối tác của TA?” Chúng sẽ nói: “Bầy tôi xin bảo đảm với Ngài không một ai trong bầy tôi làm chứng (cho điều đó).”');
INSERT INTO "vn_ruwwad" VALUES(41,48,'41:48','Những thần linh mà chúng đã từng van vái trước kia đã bỏ chúng mất dạng và chúng sẽ thấy mình chắc chắn không thể thoát (khỏi sự trừng phạt của Allah).');
INSERT INTO "vn_ruwwad" VALUES(41,49,'41:49','Con người thường không chán ngán và mệt mỏi trong việc cầu nguyện điều tốt lành nhưng nếu gặp điều xấu (tai họa) thì lại buông xuôi, tuyệt vọng.');
INSERT INTO "vn_ruwwad" VALUES(41,50,'41:50','Nếu TA cho y nếm trải lòng thương xót từ TA sau một nghịch cảnh mà y gặp phải, chắc chắn y sẽ nói (một cách tự cao tự đại): “Đây là phần của tôi (tôi đáng được thế), và tôi nghĩ sẽ chẳng có Giờ Tận Thế nào cả; nếu tôi thực sự có được đưa về trình diện Thượng Đế của tôi đi chăng nữa thì chắc chắn tôi cũng sẽ có được vô vàn cái tốt đẹp nơi Ngài mà thôi.” Chắc chắn TA sẽ cho những kẻ vô đức tin biết về những gì mà chúng đã làm và chắc chắn TA sẽ cho chúng nếm lấy sự trừng phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(41,51,'41:51','Khi TA (Allah) ban ân huệ cho con người thì y quay lưng và xa cách (TA) còn khi gặp hoạn nạn thì y lại khẩn cầu (TA) tha thiết.');
INSERT INTO "vn_ruwwad" VALUES(41,52,'41:52','Ngươi (hỡi Thiên Sứ) hãy nói với (những kẻ đa thần này): “Các ngươi có suy xét chăng nếu Qur’an thật sự do Allah ban xuống rồi các ngươi phủ nhận Nó thì ai sẽ là người lầm lạc hơn kẻ chống đối Nó từ xa?”');
INSERT INTO "vn_ruwwad" VALUES(41,53,'41:53','TA (Allah) sẽ cho chúng thấy các dấu hiệu của TA ở các chân trời và ngay trên bản thân của chúng cho đến khi chúng thấy rõ rằng đó là sự thật. Lẽ nào Thượng Đế của Ngươi (Thiên Sứ) không đủ là Nhân Chứng cho tất cả mọi việc ư?');
INSERT INTO "vn_ruwwad" VALUES(41,54,'41:54','Lẽ nào chúng vẫn hoài nghi về cuộc gặp gỡ với Thượng Đế của chúng? Chẳng phải Ngài luôn bao quát hết tất cả mọi thứ đó sao?!');
INSERT INTO "vn_ruwwad" VALUES(42,1,'42:1','Ha. Mim.');
INSERT INTO "vn_ruwwad" VALUES(42,2,'42:2','Ai-n. Si-n. Qa-f.');
INSERT INTO "vn_ruwwad" VALUES(42,3,'42:3','Như thế đó, (Allah) đã mặc khải cho Ngươi và cho các vị (Sứ Giả) trước Ngươi. Allah là Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(42,4,'42:4','Vạn vật trong các tầng trời và vạn vật trên trái đất đều là của Ngài. Ngài là Đấng Tối Cao, Đấng Vĩ Đại.');
INSERT INTO "vn_ruwwad" VALUES(42,5,'42:5','(Bởi sự vĩ đại và tối cao của Allah), các tầng trời gần như vỡ ra từ bên trên chúng, các Thiên Thần tán dương Thượng Đế của họ, và họ cầu xin (Allah) tha thứ cho những ai trên trái đất. Quả thật, Allah là Đấng Tha Thứ, Đấng Khoan Dung.');
INSERT INTO "vn_ruwwad" VALUES(42,6,'42:6','Những kẻ nhận những ai khác ngoài (Allah) làm những vị bảo hộ (nên biết) Allah là Đấng Trông Chừng họ; và Ngươi (hỡi Thiên Sứ) không phải là người chịu trách nhiệm cho (các việc làm) của họ.');
INSERT INTO "vn_ruwwad" VALUES(42,7,'42:7','Như thế đó, TA đã mặc khải cho Ngươi (hỡi Thiên Sứ) Kinh Qur’an bằng tiếng Ả-rập để Ngươi dùng cảnh báo thị trấn mẹ (Makkah) và những ai ở xung quanh nó; và để Ngươi cảnh báo về Ngày Triệu Tập, ngày mà không có gì phải hoài nghi (về sự xảy ra của nó). (Vào Ngày đó), một nhóm sẽ vào Thiên Đàng và một nhóm sẽ vào Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(42,8,'42:8','Nếu muốn, Allah thừa sức làm cho họ thành một cộng đồng duy nhất, tuy nhiên, Ngài muốn thu nhận ai vào lòng thương xót của Ngài (Thiên Đàng) là tùy ý Ngài. Những kẻ làm điều sai quấy sẽ không có ai che chở cũng như sẽ không có ai cứu giúp.');
INSERT INTO "vn_ruwwad" VALUES(42,9,'42:9','(Những kẻ đa thần) đã nhận những ai khác ngoài (Allah) làm người bảo họ trong khi Allah mới thực sự là Đấng Bảo Hộ, Ngài làm cho người chết sống lại và Ngài toàn năng trên tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(42,10,'42:10','Bất cứ điều gì các ngươi tranh chấp đều được trình lên cho Allah quyết định. (Hỡi Thiên Sứ, Ngươi hãy nói): “Đó là Allah, Thượng Đế của Ta, với Ngài Ta phó thác và với Ngài Ta quay về sám hối.”');
INSERT INTO "vn_ruwwad" VALUES(42,11,'42:11','(Allah là) Đấng tạo hóa các tầng trời và trái đất. Từ bản thân các ngươi, Ngài tạo ra những người vợ cho các ngươi; và từ các loại gia súc, Ngài tạo ra nhiều con cái. Bằng cách đó, Ngài làm cho các ngươi trở nên đông đúc. Không có cái gì giống Ngài. Và Ngài là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "vn_ruwwad" VALUES(42,12,'42:12','Mọi chìa khóa (kho tài nguyên) của các tầng trời và trái đất đều là của Ngài. Ngài muốn nới rộng hoặc thu hẹp bổng lộc cho ai là tùy ý Ngài. Quả thật, Ngài là Đấng Hằng Biết tất cả mọi thứ.');
INSERT INTO "vn_ruwwad" VALUES(42,13,'42:13','Ngài đã ban hành cho các ngươi tôn giáo (Islam) mà Ngài đã truyền xuống cho Nuh. Và những gì TA đã mặc khải cho Ngươi (hỡi Thiên Sứ) cũng chính là những gì TA đã truyền cho Ibrahim, cho Musa và cho Ysa, (đó là): “Các ngươi hãy thiết lập tôn giáo và chớ phân chia thành giáo phái trong đó”. Thật khó khăn cho những người thờ đa thần chấp nhận điều Ngươi mời gọi họ. Allah muốn chọn người nào hướng về Ngài là tùy ý Ngài và Ngài hướng dẫn về với Ngài người nào biết quay về sám hối (với Ngài).');
INSERT INTO "vn_ruwwad" VALUES(42,14,'42:14','Từ sau khi tiếp thu được nguồn kiến thức (tôn giáo), do sự thù hằn nên họ đã chia rẽ nhau. Nếu không vì một lời phán từ Thượng Đế của Ngươi tuyên bố (hoãn hình phạt) cho đến một thời hạn ấn định thì sự việc giữa họ đã được giải quyết. Và thực sự, những kẻ được trao quyền thừa kế Kinh Sách sau họ vẫn còn hoài nghi (về Qur’an).');
INSERT INTO "vn_ruwwad" VALUES(42,15,'42:15','Vì vậy, Ngươi hãy mời gọi (nhân loại đến với Islam); Ngươi hãy đứng vững như đã được lệnh; Ngươi chớ đi theo xu hướng của họ; và Ngươi hãy nói (với họ): “Ta tin nơi những điều mà Allah đã ban xuống trong Kinh Sách và Ta được lệnh phải thực thi công lý giữa các ngươi. Allah là Thượng Đế của Ta và là Thượng Đế của các ngươi. Ta chịu trách nhiệm cho việc làm của Ta và các ngươi chịu trách nhiệm cho việc làm của các ngươi, giữa chúng ta không còn gì phải tranh luận nữa. (Rồi đây) Allah sẽ triệu tập tất cả chúng ta lại và chúng ta phải quay trở về với Ngài (để chịu sự xét xử).”');
INSERT INTO "vn_ruwwad" VALUES(42,16,'42:16','Những kẻ tranh luận về (tôn giáo của) Allah sau khi nó đã được công nhận thì việc tranh luận của họ không có giá trị ở nơi Thượng Đế của họ. (Rồi đây) họ sẽ đón nhận cơn thịnh nộ (của Allah) và sự trừng phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(42,17,'42:17','Allah là Đấng đã ban Kinh (Qur’an) xuống bằng sự thật (không có gì phải hoài nghi) và (Ngài đã ban xuống) nền công lý. Và điều gì khiến cho Ngươi nhận biết có lẽ Giờ Tận Thế sắp gần kề?');
INSERT INTO "vn_ruwwad" VALUES(42,18,'42:18','Những kẻ không tin Giờ Tận Thế thường giục nó mau đến còn những người có đức tin thì sợ (thời khắc đó) bởi vì họ biết đó là sự thật. Quả thật, những kẻ tranh cãi về Giờ Tận Thế chắc chắn đi lạc rất xa.');
INSERT INTO "vn_ruwwad" VALUES(42,19,'42:19','Allah nhân từ đối với đám bầy tôi của Ngài. Ngài muốn ban bổng lộc cho người nào là tùy ý Ngài. Ngài là Đấng Toàn Lực, Đấng Toàn Năng.');
INSERT INTO "vn_ruwwad" VALUES(42,20,'42:20','Người nào muốn mảnh đất trồng để canh tác (cho cuộc sống) ở Đời Sau, TA sẽ gia tăng mảnh đất trồng để y canh tác; còn ai muốn mảnh đất trồng để canh tác (cho cuộc sống) trần tục, TA sẽ ban nó cho y nhưng ở Đời Sau y sẽ chẳng có một phần (tốt đẹp) nào.');
INSERT INTO "vn_ruwwad" VALUES(42,21,'42:21','Hoặc phải chăng (những kẻ thờ đa thần này) có những thần linh đã thiết lập cho chúng một tôn giáo mà Allah không đồng ý?! Nếu không vì một lời phán đã được tuyên bố thì sự việc giữa họ đã được giải quyết. Quả thật, những kẻ làm điều sai quấy sẽ phải chịu một sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(42,22,'42:22','Ngươi (hỡi Thiên Sứ) sẽ thấy những kẻ làm điều sai quấy lo sợ về những điều mà họ đã phạm và chắc chắn (sự trừng phạt) sẽ xảy đến với họ. Riêng những người có đức tin và hành thiện, họ sẽ ở trong các Ngôi Vườn Thiên Đàng, họ sẽ có được những thứ mà họ mong ước nơi Thượng Đế của họ; đó là hồng phúc to lớn.');
INSERT INTO "vn_ruwwad" VALUES(42,23,'42:23','Đó là điều mà Allah báo tin mừng cho đám bầy tôi của Ngài, những người mà họ có đức tin và hành thiện. Ngươi (hỡi Thiên Sứ) hãy nói (với họ): “Ta không đòi các người phần công lao về việc truyền đạt chân lý cho các người mà Ta chỉ mong sự thiện chí thông qua quan hệ họ hàng.” Người nào làm một điều tốt, TA sẽ tăng thêm cho y cái tốt trong đó. Quả thật, Allah là Đấng Hằng Tha Thứ, Hằng Tri Ân.');
INSERT INTO "vn_ruwwad" VALUES(42,24,'42:24','(Những kẻ thờ đa thần) nói (về Muhammad): “Y đã đặt điều nói dối cho Allah.” Nhưng nếu Allah muốn, Ngài có thể niêm kín trái tim của Ngươi lại (hỡi Thiên Sứ). Và Allah loại bỏ điều giả dối và thiết lập sự thật bằng lời phán của Ngài. Quả thật, Ngài biết rõ mọi điều thầm kín trong lòng (của mỗi con người).');
INSERT INTO "vn_ruwwad" VALUES(42,25,'42:25','Ngài là Đấng chấp nhận sự sám hối của đám bầy tôi của Ngài, Ngài xí xóa cho những hành vi xấu và Ngài biết rõ những gì các ngươi làm.');
INSERT INTO "vn_ruwwad" VALUES(42,26,'42:26','Ngài đáp lại lời nguyện cầu của những người có đức tin và hành thiện và Ngài gia tăng cho họ từ hồng phúc của Ngài. Trái lại, những kẻ vô đức tin sẽ phải chịu sự trừng phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(42,27,'42:27','Nếu Allah ban bổng lộc dồi dào cho đám bầy tôi của Ngài (quá mức), chắc chắn họ sẽ hành động xấc xược trên trái đất. Tuy nhiên, Ngài ban nó xuống theo định lượng mà Ngài muốn (một cách hợp lý). Quả thật, Ngài là Đấng Thông Toàn, Đấng Nhìn Thấy (tất cả mọi sự việc).');
INSERT INTO "vn_ruwwad" VALUES(42,28,'42:28','Ngài là Đấng ban mưa xuống (cho đám bề tôi của Ngài) sau khi họ đã tuyệt vọng và Ngài trải rộng lòng thương xót của Ngài. Quả thật, Ngài là Đấng Bảo Hộ, Đấng đáng được ca ngợi và tán dương.');
INSERT INTO "vn_ruwwad" VALUES(42,29,'42:29','Trong các dấu hiệu của Ngài là việc tạo hóa các tầng trời, trái đất và tất cả sinh vật mà Ngài phân tán trong trời đất. Quả thật, Ngài thừa khả năng tập trung tất cả chúng lại bất cứ khi nào Ngài muốn.');
INSERT INTO "vn_ruwwad" VALUES(42,30,'42:30','Bất cứ tai họa nào xảy đến với các ngươi (hỡi nhân loại) là do bàn tay của các ngươi đã gây ra, tuy nhiên, Ngài xí xóa (cho các ngươi) rất nhiều.');
INSERT INTO "vn_ruwwad" VALUES(42,31,'42:31','Các ngươi không thể trốn thoát khỏi (Thượng Đế của các ngươi nếu Ngài muốn trừng phạt các ngươi) trên trái đất. Ngoài Allah, các ngươi sẽ không có được một đấng bảo vệ hay một vị cứu tinh nào.');
INSERT INTO "vn_ruwwad" VALUES(42,32,'42:32','Trong các dấu hiệu của Ngài là những con thuyền (buồm) lênh đênh trên biển giống như những quả núi.');
INSERT INTO "vn_ruwwad" VALUES(42,33,'42:33','Nếu muốn, Ngài sẽ làm cho gió ngưng lại, (lúc đó) họ sẽ bất động trên mặt biển. Quả thật, trong sự việc đó là các dấu hiệu cho bất cứ ai có lòng kiên nhẫn và tri ân.');
INSERT INTO "vn_ruwwad" VALUES(42,34,'42:34','Hoặc Ngài có thể tiêu diệt các con thuyền (đó) vì điều (tội lỗi) mà họ (những người trên các con thuyền) đã làm, tuy nhiên, Ngài xí xóa cho họ rất nhiều.');
INSERT INTO "vn_ruwwad" VALUES(42,35,'42:35','Những kẻ thường tranh luận về các Lời Mặc Khải của TA nên biết là họ sẽ không có chốn dung thân.');
INSERT INTO "vn_ruwwad" VALUES(42,36,'42:36','Do đó, bất cứ thứ gì mà các ngươi (hỡi nhân loại) được ban cấp thì đó chỉ là sự tận hưởng (tạm bợ) của đời sống trần tục; còn những gì ở nơi Allah mới là những thứ tốt hơn và lâu dài hơn cho những ai có đức tin và phó thác cho Thượng Đế của họ.');
INSERT INTO "vn_ruwwad" VALUES(42,37,'42:37','Và những ai tránh xa các đại tội cũng như những hành động vô đạo đức (không đứng đắn), và khi nóng giận thì họ biết kiềm chế (cơn giận và sẵn sàng) tha thứ.');
INSERT INTO "vn_ruwwad" VALUES(42,38,'42:38','Và những ai đáp lại lời gọi của Thượng Đế của họ, chu đáo dâng lễ nguyện Salah, vụ việc của họ được quyết định bằng cách tham khảo ý kiến giữa họ, và họ thường chi dùng (cho con đường chính nghĩa của TA) những bổng lộc mà TA đã ban cấp cho họ.');
INSERT INTO "vn_ruwwad" VALUES(42,39,'42:39','Và những ai khi bị áp bức một cách bất công thì họ biết giúp đỡ nhau tự vệ.');
INSERT INTO "vn_ruwwad" VALUES(42,40,'42:40','Đáp trả hành động xấu bằng hành động xấu tương tự. Tuy nhiên, người nào lượng thứ bỏ qua và làm hòa thì phần thưởng của y ở nơi Allah. Quả thật, Ngài không yêu thương những kẻ làm điều làm điều sai quấy.');
INSERT INTO "vn_ruwwad" VALUES(42,41,'42:41','Người nào đã tự vệ sau khi bị áp bức thì sẽ không có lý do nào để khiển trách họ (bởi họ chỉ giành lấy lẽ phải của họ).');
INSERT INTO "vn_ruwwad" VALUES(42,42,'42:42','Lý do khiển trách chỉ đối với những kẻ bất công với người khác và bạo ngược trên trái đất một cách vô cớ. Chúng là những kẻ sẽ phải chịu một sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(42,43,'42:43','Người nào kiên nhẫn chịu đựng và tha thứ thì đó là một quyết tâm cao (đáng được khen ngợi).');
INSERT INTO "vn_ruwwad" VALUES(42,44,'42:44','Người nào mà Allah làm cho lạc lối thì sẽ không có ai che chở ngoài Ngài. Ngươi (hỡi Thiên Sứ) sẽ thấy những kẻ làm điều sai quấy khi chúng nhìn thấy hình phạt, nói rằng “Còn có con đường nào để trở về (thế giới trần gian) chăng?”');
INSERT INTO "vn_ruwwad" VALUES(42,45,'42:45','Và Ngươi sẽ thấy chúng bị đưa đến (Hỏa Ngục) với bộ dạng khúm núm một cách hèn hạ và với cái nhìn lấm lét sợ hãi. Những người có đức tin sẽ nói: “Thật vậy, những người thua cuộc là những kẻ đã đánh mất chính họ và gia đình vào Ngày Phán Xét. Quả thật, những kẻ làm điều sai quấy sẽ bị trừng phạt mãi mãi.”');
INSERT INTO "vn_ruwwad" VALUES(42,46,'42:46','Ngoài Allah, chúng sẽ không có vị bảo hộ nào giúp đỡ chúng cả. Và người nào mà Allah làm cho lạc lối thì sẽ không có con đường nào (dẫn đến chân lý).');
INSERT INTO "vn_ruwwad" VALUES(42,47,'42:47','(Hỡi nhân loại!) Các ngươi hãy đáp lại lời gọi của Thượng Đế của các ngươi trước khi xảy ra Ngày mà Nó không bao giờ hồi lại vì Allah (đã ra lệnh). Vào Ngày đó, các ngươi sẽ không có chỗ lánh nạn cũng không có lý do để chạy tội.');
INSERT INTO "vn_ruwwad" VALUES(42,48,'42:48','Nếu họ ngoảnh đi thì (Ngươi – hỡi Thiên Sứ, hãy biết rằng) TA đã không cử Ngươi đến để trông chừng (việc làm) của họ. Nhiệm vụ của Ngươi chỉ là truyền đạt (thông điệp của TA) mà thôi. Quả thật, khi TA cho con người nếm trải lòng thương xót của TA thì y vui mừng; nhưng nếu gặp phải điều xấu do bàn tay của y đã gây ra trước đó thì lúc đó con người thường vô ơn.');
INSERT INTO "vn_ruwwad" VALUES(42,49,'42:49','Quyền thống trị các tầng trời và trái đất thuộc về một mình Allah. Ngài tạo hóa bất cứ thứ gì Ngài muốn. Ngài muốn ban con gái cho ai là tùy ý Ngài và ban con trai cho ai cũng tùy ý Ngài.');
INSERT INTO "vn_ruwwad" VALUES(42,50,'42:50','Hoặc Ngài (muốn) ban cả hai, trai và gái (cho ai là tùy ý Ngài), và làm cho ai đó hiếm muộn cũng tùy ý Ngài (không ai có quyền can thiệp). Quả thật, Ngài là Đấng Toàn Tri, Đấng Toàn Năng.');
INSERT INTO "vn_ruwwad" VALUES(42,51,'42:51','Người phàm không phù hợp để Allah nói chuyện trực tiếp với y ngoại trừ bằng cách mặc khải hoặc từ sau một bức màn (vô hình) hoặc cử phái một Sứ Giả (Thiên Thần) truyền đạt lại điều Ngài muốn (cho vị sứ giả người phàm) theo phép của Ngài. Quả thật, Ngài là Đấng Tối Cao, Đấng Chí Minh.');
INSERT INTO "vn_ruwwad" VALUES(42,52,'42:52','Như thế đó, TA đã mặc khải cho Ngươi (hỡi Thiên Sứ) Ruh (Qur’an) từ Mệnh Lệnh của TA. Trước đây, Ngươi không hề biết Kinh Sách cũng như đức tin Iman là gì. Nhưng TA đã ban (Qur’an) xuống làm nguồn ánh sáng để TA hướng dẫn người nào TA muốn trong số đám bề tôi của TA. Quả thật, Ngươi đích thực là người hướng dẫn (nhân loại) đến con đường ngay chính.');
INSERT INTO "vn_ruwwad" VALUES(42,53,'42:53','(Đó là) con đường của Allah, Đấng mà tất cả vạn vật trong các tầng trời và tất cả vạn vật trên đất đều thuộc về Ngài. Tất cả mọi việc đều trở về với Allah (để Ngài định đoạt).');
INSERT INTO "vn_ruwwad" VALUES(43,1,'43:1','Ha. Mim.');
INSERT INTO "vn_ruwwad" VALUES(43,2,'43:2','Thề bởi Kinh (Qur’an) quang minh.');
INSERT INTO "vn_ruwwad" VALUES(43,3,'43:3','Quả thật, TA đã làm cho Nó thành một quyển Kinh để xướng đọc bằng tiếng Ả-rập, mong rằng các ngươi có thể hiểu.');
INSERT INTO "vn_ruwwad" VALUES(43,4,'43:4','Quả thật, (Qur’an này) nguyên nằm trong Tập Kinh Mẹ (Lawhu Al-Mahfuzh) ở nơi TA, rất cao thâm, uyên bác.');
INSERT INTO "vn_ruwwad" VALUES(43,5,'43:5','Lẽ nào TA phải lấy lại Lời Nhắc Nhở (Qur’an) khỏi các ngươi bởi vì các ngươi là đám người tội lỗi quá độ?! (TA không làm thế vì lòng nhân từ của TA dành cho các ngươi đòi hỏi điều ngược lại).');
INSERT INTO "vn_ruwwad" VALUES(43,6,'43:6','TA đã cử phái biết bao Nabi cho những cộng đồng thời trước!');
INSERT INTO "vn_ruwwad" VALUES(43,7,'43:7','Tuy nhiên, không một Nabi nào đến với họ mà lại không bị họ nhạo báng, giễu cợt.');
INSERT INTO "vn_ruwwad" VALUES(43,8,'43:8','Vì vậy, TA đã tiêu diệt những kẻ mạnh nhất trong bọn họ; và hình ảnh của người xưa đã đi vào dĩ vãng.');
INSERT INTO "vn_ruwwad" VALUES(43,9,'43:9','Nếu Ngươi (Thiên Sứ) hỏi (những kẻ thờ đa thần) “Ai đã tạo ra các tầng trời và trái đất?” thì chắc chắn chúng sẽ nói: “Đấng Toàn Năng, Đấng Toàn Tri đã tạo ra.”');
INSERT INTO "vn_ruwwad" VALUES(43,10,'43:10','(Allah là) Đấng đã làm trái đất như một chiếc giường ngủ cho các ngươi và đã làm ra trên nó những con đường cho các ngươi để đưa các ngươi đi đến (điểm cần đến).');
INSERT INTO "vn_ruwwad" VALUES(43,11,'43:11','(Ngài là) Đấng đã ban nước mưa từ trên trời xuống theo định lượng. TA (Allah) dùng (nước mưa) làm sống lại mảnh đất đã chết (khô cằn, nứt nẻ). (Rồi đây) các ngươi sẽ được dựng sống lại giống như thế.');
INSERT INTO "vn_ruwwad" VALUES(43,12,'43:12','(Ngài là) Đấng đã tạo tất cả mọi vật thành từng cặp (đực cái, trống mái, âm dương) và (Ngài là Đấng) đã tạo cho các ngươi những con tàu và các loại gia súc để các ngươi cưỡi (và chuyên chở đồ đạc).');
INSERT INTO "vn_ruwwad" VALUES(43,13,'43:13','(Những con tàu, các loại gia súc, Ngài tạo chúng cho các ngươi) để các ngươi có thể ngồi vững trên lưng của chúng và sau đó các ngươi nhớ đến hồng phúc của Thượng Đế của các ngươi và nói: “Quang vinh thay Đấng đã chế ngự (phương tiện) này cho bầy tôi, bởi quả thật bầy tôi không đủ khả năng chế ngự nó.”');
INSERT INTO "vn_ruwwad" VALUES(43,14,'43:14','“Và bầy tôi chắc chắn sẽ trở về trình diện Thượng Đế của bầy tôi!”');
INSERT INTO "vn_ruwwad" VALUES(43,15,'43:15','(Những kẻ đa thần) đã gán một số trong đám bề tôi của Ngài có phần chia sẻ (với Ngài khi họ nói: các Thiên Thần là những đứa con gái của Ngài). Quả thật, con người rõ ràng là kẻ vô ơn.');
INSERT INTO "vn_ruwwad" VALUES(43,16,'43:16','(Này những kẻ đa thần!) Lẽ nào (Allah) chọn lấy những vật mà Ngài đã tạo ra làm những đứa con gái (của Ngài) và ưu ái đặc biệt cho các ngươi những đứa con trai?!');
INSERT INTO "vn_ruwwad" VALUES(43,17,'43:17','Khi ai đó trong bọn chúng nhận được tin mừng (về sự ra đời một đứa con gái) mà chúng đã thường gán cho Đấng Al-Rahman thì gương mặt của y tối sầm lại, (lòng của y) ngập tràn sự đau thương.');
INSERT INTO "vn_ruwwad" VALUES(43,18,'43:18','Lẽ nào một người (phụ nữ) ( được nuôi dưỡng trong các món trang sức và ăn nói không rành mạch (lại là kẻ phò tá cho Allah)?!');
INSERT INTO "vn_ruwwad" VALUES(43,19,'43:19','Chúng đã qui cho các Thiên Thần - những bề tôi của Đấng Al-Rahman - là nữ giới. Lẽ nào chúng đã (tận mắt) chứng kiến việc (Ngài) đã tạo hóa các Thiên Thần?! Sự xác nhận của chúng sẽ được ghi lại và chúng sẽ bị tra hỏi.');
INSERT INTO "vn_ruwwad" VALUES(43,20,'43:20','Chúng nói: “Nếu Đấng Al-Rahman đã muốn thì bọn ta đã không tôn thờ họ rồi!” Chúng chẳng có một chút kiến thức nào về điều đó cả. Chúng chỉ đoán chừng mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(43,21,'43:21','Hoặc lẽ nào TA đã ban cho chúng một Kinh Sách trước (Qur’an này cho phép chúng thờ phượng ngoài TA) nên chúng cứ bám chắc theo đó?!');
INSERT INTO "vn_ruwwad" VALUES(43,22,'43:22','Không! Chúng thường bảo: “Bọn ta thấy cha ông của bọn ta theo một tôn giáo nào đó nên bọn ta đi theo dấu chân của họ.”');
INSERT INTO "vn_ruwwad" VALUES(43,23,'43:23','Tương tự như thế, không một vị cảnh báo nào trước Ngươi được TA cử phái đến cho một thị trấn mà những kẻ giàu có của thị trận đó lại không nói: “Bọn ta thấy cha ông của bọn ta theo một tôn giáo nào đó nên bọn ta đi theo dấu chân của họ.”');
INSERT INTO "vn_ruwwad" VALUES(43,24,'43:24','(Vị cảnh báo của chúng) đã nói (với chúng): “Lẽ nào ngay cả việc Ta mang đến cho các ngươi một nguồn hướng dẫn tốt hơn cái mà các ngươi thấy cha mẹ của các ngươi đang theo ư?” Chúng đáp: “Bọn ta không tin nơi điều mà các ngươi mang đến!”');
INSERT INTO "vn_ruwwad" VALUES(43,25,'43:25','Vì vậy, TA đã trừng phạt chúng. Ngươi hãy xem kết cuộc của những kẻ phủ nhận (các vị cảnh báo) đã xảy ra như thế nào!');
INSERT INTO "vn_ruwwad" VALUES(43,26,'43:26','(Ngươi hãy nhớ lại - hỡi Thiên Sứ) khi Ibrahim nói với cha và người dân của mình: “Tôi không can hệ với những thần linh mà các người đang tôn thờ.”');
INSERT INTO "vn_ruwwad" VALUES(43,27,'43:27','“Tôi chỉ tôn thờ Đấng đã tạo ra tôi, bởi quả thật, Ngài sẽ hướng dẫn tôi.”');
INSERT INTO "vn_ruwwad" VALUES(43,28,'43:28','(Ibrahim) đã làm cho nó (lời Tawhid: chỉ có Allah là Thượng Đế) thành một lời trường cửu cho hậu thế, mong rằng họ biết quay trở lại (với Ngài).');
INSERT INTO "vn_ruwwad" VALUES(43,29,'43:29','Không! (Ngay cả khi những kẻ đa thần bắt đầu tôn thờ những thần linh khác ngoài TA, TA đã không tiêu diệt chúng) TA đã để cho chúng và cha ông của chúng tận hưởng (cuộc sống trần gian) cho đến khi Chân Lý (Qur’an) và một Sứ Giả minh bạch đến với chúng.');
INSERT INTO "vn_ruwwad" VALUES(43,30,'43:30','Tuy nhiên, khi Chân Lý đến với chúng thì chúng lại nói: “Đây là trò ma thuật, bọn ta không tin nơi Nó.”');
INSERT INTO "vn_ruwwad" VALUES(43,31,'43:31','Và chúng nói: “Sao Qur’an này không được ban xuống cho những người (giàu có, chức quyền và tiếng tăm) của hai thị trấn lớn (như Makkah và Ta-if) kia chứ?!”');
INSERT INTO "vn_ruwwad" VALUES(43,32,'43:32','Lẽ nào chúng toàn quyền phân chia hồng ân của Thượng Đế của Ngươi?! Chính TA (Allah) mới toàn quyền phân bổ sinh kế của chúng giữa chúng với nhau trong đời sống trần tục này. TA đã nâng một số người này trội hơn một số người khác về cấp bậc để cho người này bắt người kia phục dịch. Và hồng ân của Thượng Đế của Ngươi tốt hơn của cải mà chúng tích lũy.');
INSERT INTO "vn_ruwwad" VALUES(43,33,'43:33','Nếu không vì cản nhân loại trở thành một cộng đồng duy nhất (về sự vô đức tin) thì TA đã làm cho những kẻ phủ nhận Đấng Al-Rahman có được những mái nhà bằng bạc và cả cầu thang để chúng đi lên;');
INSERT INTO "vn_ruwwad" VALUES(43,34,'43:34','Và những cửa nhà của chúng (cũng bằng bạc) và những chiếc tràng kỷ để chúng nằm nghỉ;');
INSERT INTO "vn_ruwwad" VALUES(43,35,'43:35','Và những món đồ trang trí bằng vàng. Nhưng tất cả những thứ đó chỉ là sự hưởng thụ (tạm bợ) của đời sống trần tục này. (Còn những gì của cuộc sống) Đời Sau ở nơi Thượng Đế của Ngươi mới là (sự hưởng thụ trường tồn) dành cho những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(43,36,'43:36','Người nào thờ ơ trước lời nhắc nhở của Đấng Al-Rahman, TA sẽ để Shaytan làm bạn với y.');
INSERT INTO "vn_ruwwad" VALUES(43,37,'43:37','Quả thật, (những tên Shaytan) này chắc chắn sẽ ngăn chúng khỏi con đường (của Allah), tuy nhiên, chúng cứ tưởng chúng đang được hướng dẫn.');
INSERT INTO "vn_ruwwad" VALUES(43,38,'43:38','Cho tới khi y đến trình diện TA, y bảo (người bạn Shaytan của mình): “Ước gì ta với ngươi ở cách xa nhau bằng khoảng cách của hai phương đông. Thật tồi tệ thay một người bạn như thế!”');
INSERT INTO "vn_ruwwad" VALUES(43,39,'43:39','(Allah phán với những kẻ vô đức tin vào Ngày Phán Xét:) “(Lời trách móc) chẳng giúp ích được gì cho các ngươi trong ngày hôm nay khi các ngươi đã làm điều sai quấy. Các ngươi phải cùng san sẻ trong sự trừng phạt.”');
INSERT INTO "vn_ruwwad" VALUES(43,40,'43:40','Làm sao Ngươi (hỡi Thiên Sứ) có thể khiến cho người điếc nghe được hoặc có thể hướng dẫn một kẻ mù lòa hoặc ai đó đang lạc lối rõ ràng?!');
INSERT INTO "vn_ruwwad" VALUES(43,41,'43:41','Dù TA có đưa Ngươi đi (bằng cái chết) trước (khi TA trừng phạt chúng) thì chắc chắn TA vẫn phải trừng phạt chúng (ở đời này và cả Đời Sau).');
INSERT INTO "vn_ruwwad" VALUES(43,42,'43:42','Hoặc dù TA cho Ngươi (sự trừng phạt) mà TA đã hứa với chúng thì TA thực sự thừa khả năng làm thế.');
INSERT INTO "vn_ruwwad" VALUES(43,43,'43:43','Vì vậy, Ngươi hãy nắm chắc những gì đã được mặc khải cho Ngươi. Quả thật, Ngươi đang ở trên con đường ngay thẳng (chính đạo).');
INSERT INTO "vn_ruwwad" VALUES(43,44,'43:44','Quả thật, Nó (Qur’an) là sự nhắc nhở dành cho Ngươi và người dân của Ngươi, và rồi đây các ngươi sẽ bị chất vấn (về Nó).');
INSERT INTO "vn_ruwwad" VALUES(43,45,'43:45','Ngươi hãy hỏi những Sứ Giả mà TA đã cử đến trước Ngươi xem TA có chỉ định những thần linh được tôn thờ ngoài Đấng Al-Rahman không?');
INSERT INTO "vn_ruwwad" VALUES(43,46,'43:46','Quả thật, TA đã cử Musa mang những phép lạ của TA đến gặp Pha-ra-ông và các thủ lĩnh của hắn, Musa nói với chúng: “Ta đích thực là Sứ Giả của Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "vn_ruwwad" VALUES(43,47,'43:47','Nhưng khi (Musa) mang những phép lạ của TA đến gặp chúng thì chúng cười nhạo cho những phép lạ đó.');
INSERT INTO "vn_ruwwad" VALUES(43,48,'43:48','TA lần lượt cho chúng thấy các phép lạ, cái sau lớn hơn cái trước, và TA đã trừng phạt chúng, mong chúng (biết thức tỉnh) mà quay về (với chính đạo).');
INSERT INTO "vn_ruwwad" VALUES(43,49,'43:49','Chúng bảo (Musa): “Này tên phù thủy! Ngươi hãy cầu xin Thượng Đế của Ngươi giúp bọn ta thoát khỏi những gì mà Ngài đã hứa với Ngươi, chắc chắn lúc đó bọn ta sẽ được hướng dẫn đúng đường.”');
INSERT INTO "vn_ruwwad" VALUES(43,50,'43:50','Tuy nhiên, khi TA lấy đi khỏi chúng sự trừng phạt thì chúng phá vỡ lời hứa (của chúng).');
INSERT INTO "vn_ruwwad" VALUES(43,51,'43:51','Pha-ra-ông đã tuyên bố với đám dân của hắn: “Này hỡi dân ta! Chẳng phải quyền thống trị xứ Ai Cập (này) cũng như những con sông đang chảy bên dưới ta đều nằm trong tay ta đó sao? Lẽ nào các ngươi không nhìn thấy?”');
INSERT INTO "vn_ruwwad" VALUES(43,52,'43:52','“Hoặc lẽ nào ta không trội hơn tên (Musa) này, một kẻ đáng khinh và ăn nói không được lưu loát?”');
INSERT INTO "vn_ruwwad" VALUES(43,53,'43:53','“Đúng ra y phải được ban cho vòng vàng hoặc phải được Thiên Thần tháp tùng, (như thế mới hợp lý)!”');
INSERT INTO "vn_ruwwad" VALUES(43,54,'43:54','(Pha-ra-ông) đã đánh lừa người dân của hắn khiến chúng theo hắn. Quả thật, chúng là một đám người bất tuân và nổi loạn.');
INSERT INTO "vn_ruwwad" VALUES(43,55,'43:55','Vì vậy, khi chúng chọc giận TA, TA đã bắt phạt chúng. TA đã nhấn chìm tất cả bọn chúng (trong biển hồng hải).');
INSERT INTO "vn_ruwwad" VALUES(43,56,'43:56','TA đã lấy chúng làm một tiền lệ và bài học cho hậu thế.');
INSERT INTO "vn_ruwwad" VALUES(43,57,'43:57','Khi con trai (Ysa) của Maryam được đưa ra làm một hình ảnh thí dụ ( thì người dân của Ngươi (Thiên Sứ) la ó (phản đối) về điều đó.');
INSERT INTO "vn_ruwwad" VALUES(43,58,'43:58','Chúng bảo: “Những thần linh của bọn ta tốt hay (Ysa) tốt hơn?” Chúng đưa vấn đề đó ra cho Ngươi chỉ vì muốn tranh cãi mà thôi. Không! Chúng đích thực là một đám người thích tranh cãi.');
INSERT INTO "vn_ruwwad" VALUES(43,59,'43:59','(Ysa) chỉ là một người bề tôi (của TA). TA đã ban ân cho Y (khi cử phái Y làm Sứ Giả của TA) và lấy Y làm một tấm gương cho con cháu của Israel.');
INSERT INTO "vn_ruwwad" VALUES(43,60,'43:60','Nếu muốn, TA đã thay thế các ngươi (hỡi con cháu Adam) bởi các Thiên Thần để Họ nối tiếp nhau trên trái đất.');
INSERT INTO "vn_ruwwad" VALUES(43,61,'43:61','(Ysa) thực sự là một dấu hiệu (báo trước) cho Giờ Tận Thế sắp xảy ra, vì vậy, các ngươi chớ đừng hoài nghi về điều đó mà hãy theo TA. Đây là con đường ngay chính.');
INSERT INTO "vn_ruwwad" VALUES(43,62,'43:62','Các ngươi đừng để Shaytan cản trở các ngươi (theo con đường ngay chính). Hắn thực sự là kẻ thù công khai của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(43,63,'43:63','Khi Ysa đến với những bằng chứng rõ ràng, Y nói: “Quả thật, Ta mang đến cho các người điều khôn ngoan và Ta đến để làm sáng tỏ một vài điểm mà các người bất đồng nhau. Vì vậy, các ngươi hãy sợ Allah và vâng lời Ta.”');
INSERT INTO "vn_ruwwad" VALUES(43,64,'43:64','“Thật vậy, Allah là Thượng Đế của Ta và là Thượng Đế của các người, vì vậy, các người hãy thờ phượng Ngài. Đây là con đường ngay thẳng (chính đạo).”');
INSERT INTO "vn_ruwwad" VALUES(43,65,'43:65','Tuy nhiên, các giáo phái trong số chúng đã bất đồng với nhau (về sự việc của Ysa). Vì vậy, thật khốn cho những kẻ làm điều sai quấy về sự trừng phạt của Ngày đau đớn!');
INSERT INTO "vn_ruwwad" VALUES(43,66,'43:66','Có phải chúng chỉ đang chờ đợi Giờ Tận Thế bất ngờ xảy đến trong lúc chúng không hay biết gì chăng?!');
INSERT INTO "vn_ruwwad" VALUES(43,67,'43:67','Những người bạn thân tình vào Ngày đó sẽ trở thành kẻ thù của nhau ngoại trừ những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(43,68,'43:68','(Allah sẽ phán bảo họ): “Hỡi bầy tôi của TA! Ngày hôm nay, các ngươi sẽ không lo sợ cũng sẽ không buồn phiền.”');
INSERT INTO "vn_ruwwad" VALUES(43,69,'43:69','“(Các ngươi là) những người đã có đức tin nơi các Lời Mặc Khải của TA và là những người Muslim (qui phục TA).”');
INSERT INTO "vn_ruwwad" VALUES(43,70,'43:70','“Các ngươi hãy vào Thiên Đàng cùng với vợ của các ngươi.”');
INSERT INTO "vn_ruwwad" VALUES(43,71,'43:71','Đĩa ăn và tách uống bằng vàng sẽ được chuyền đi vòng quanh họ. Trong đó, có bất cứ thứ gì linh hồn mong ước và (những gì) mê hoặc đôi mắt. Và các ngươi (hỡi những người ngoan đạo) sẽ ở trong đó mãi mãi.');
INSERT INTO "vn_ruwwad" VALUES(43,72,'43:72','Đó là Thiên Đàng mà các ngươi sẽ thừa hưởng bởi những việc (hành đạo) mà các ngươi đã từng làm (trên thế gian).');
INSERT INTO "vn_ruwwad" VALUES(43,73,'43:73','Trong đó, các ngươi sẽ có nhiều loại hoa quả để ăn.');
INSERT INTO "vn_ruwwad" VALUES(43,74,'43:74','Thật vậy, những tên tội lỗi sẽ ở trong sự trừng phạt của Hỏa Ngục, chúng vĩnh viễn (ở lại đó).');
INSERT INTO "vn_ruwwad" VALUES(43,75,'43:75','Trong đó, chúng sẽ không được giảm nhẹ hình phạt và chúng sẽ vô cùng tuyệt vọng.');
INSERT INTO "vn_ruwwad" VALUES(43,76,'43:76','TA đã không bất công với chúng mà chính chúng là những kẻ bất công (với bản thân chúng).');
INSERT INTO "vn_ruwwad" VALUES(43,77,'43:77','Chúng sẽ kêu la (van xin): “Hỡi vị cai quản (Hỏa Ngục)! Hãy xin Thượng Đế của ngài chấm dứt chúng tôi cho xong! (Vị cai quản Hỏa Ngục) đáp: “Chắc các ngươi sẽ phải vĩnh viễn ở lại đây.”');
INSERT INTO "vn_ruwwad" VALUES(43,78,'43:78','Quả thật, TA (Allah) đã mang Chân Lý đến cho các ngươi nhưng đa số các ngươi ghét Chân Lý.');
INSERT INTO "vn_ruwwad" VALUES(43,79,'43:79','Lẽ nào chúng sắp đặt công việc?! Không, chính TA mới là Đấng sắp đặt.');
INSERT INTO "vn_ruwwad" VALUES(43,80,'43:80','Hoặc lẽ nào chúng nghĩ rằng TA không nghe thấy những điều bí mật và những cuộc trò chuyện riêng tư của chúng?! Không, các Sứ Giả của TA luôn ở cạnh chúng không ngừng ghi chép lại.');
INSERT INTO "vn_ruwwad" VALUES(43,81,'43:81','Ngươi (Thiên Sứ) hãy nói với chúng: “Nếu Đấng Al-Rahman có một đứa con trai thì Ta sẽ là người thờ phượng đầu tiên.”');
INSERT INTO "vn_ruwwad" VALUES(43,82,'43:82','Quang vinh và trong sạch thay Thượng Đế của các tầng trời và trái đất, Thượng Đế của chiếc Ngai Vương, Ngài vượt hẳn những gì chúng đã qui cho Ngài.');
INSERT INTO "vn_ruwwad" VALUES(43,83,'43:83','Vì vậy, Ngươi hãy cứ để mặc chúng trò chuyện một cách vô ích và cứ để mặc chúng vui chơi thỏa thích cho đến khi chúng đối diện với Ngày mà chúng đã được hứa.');
INSERT INTO "vn_ruwwad" VALUES(43,84,'43:84','Ngài (Allah) là Thượng Đế (duy nhất) trên trời và là Thượng Đế (duy nhất) trên trái đất; Ngài là Đấng Chí Minh, Đấng Toàn Tri.');
INSERT INTO "vn_ruwwad" VALUES(43,85,'43:85','Phúc thay Đấng nắm quyền thống trị các tầng trời, trái đất và vạn vật giữa trời đất; Ngài biết rõ Giờ Tận Thế và tất cả các ngươi sẽ được đưa trở về trình diện Ngài.');
INSERT INTO "vn_ruwwad" VALUES(43,86,'43:86','Những kẻ mà chúng khấn vái ngoài Allah không có quyền can thiệp (ở nơi Allah) ngoại trừ những ai chứng nhận Chân Lý và hiểu biết rõ (về Nó).');
INSERT INTO "vn_ruwwad" VALUES(43,87,'43:87','Nếu Ngươi có hỏi chúng “Ai đã tạo hóa các người?” thì chúng chắc chắn sẽ trả lời “Allah!” Vậy, sao chúng lại lánh xa Ngài?!');
INSERT INTO "vn_ruwwad" VALUES(43,88,'43:88','(Allah thừa nhận) lời than của Thiên Sứ: “Lạy Thượng Đế của bề tôi! Dân chúng của bề tôi đã không có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(43,89,'43:89','Vì vậy, Ngươi hãy lánh xa chúng và nói: “Chào bằng an!” Rồi đây chúng sẽ biết (hình phạt mà chúng sẽ phải đối mặt).');
INSERT INTO "vn_ruwwad" VALUES(44,1,'44:1','Ha. Mim.');
INSERT INTO "vn_ruwwad" VALUES(44,2,'44:2','Thề bởi Kinh (Qur’an) quang minh.');
INSERT INTO "vn_ruwwad" VALUES(44,3,'44:3','Quả thật, TA (Allah) ban Nó xuống vào một đêm đầy ân phúc (đêm Al-Qadr – đêm Định Mệnh - của tháng Ramadan). Quả thật, TA là Đấng Cảnh Báo.');
INSERT INTO "vn_ruwwad" VALUES(44,4,'44:4','Trong đêm đó, mọi sự việc (liên quan đến bổng lộc, tuổi thọ và những gì khác) sẽ được phân định (cho một năm tới).');
INSERT INTO "vn_ruwwad" VALUES(44,5,'44:5','(Mọi) vấn đề (đều được tiến hành) theo chỉ thị của TA. Quả thật, TA là Đấng cử phái các vị Sứ Giả.');
INSERT INTO "vn_ruwwad" VALUES(44,6,'44:6','(Việc cử phái các vị Sứ Giả là) một Hồng Ân từ Thượng Đế của Ngươi (hỡi Thiên Sứ). Quả thật, Ngài là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "vn_ruwwad" VALUES(44,7,'44:7','(Ngài là) Thượng Đế của các tầng trời, trái đất và vạn vật giữa chúng nếu các ngươi có một đức tin vững chắc nơi Ngài.');
INSERT INTO "vn_ruwwad" VALUES(44,8,'44:8','Không có Thượng Đế đích thực nào ngoài Ngài. (Ngài là) Đấng làm cho sống và làm cho chết. (Ngài là) Thượng Đế của các ngươi và là Thượng Đế của ông bà tổ tiên các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(44,9,'44:9','(Những kẻ thờ đa thần không có đức tin vững chắn về điều đó), chúng chỉ biết đùa cợt trong sự hoài nghi.');
INSERT INTO "vn_ruwwad" VALUES(44,10,'44:10','Vì vậy, Ngươi (hỡi Thiên Sứ) hãy đợi xem Ngày mà bầu trời sẽ tỏa ra lớp khói mờ rõ rệt.');
INSERT INTO "vn_ruwwad" VALUES(44,11,'44:11','(Lớp khói mờ sẽ) bao trùm cả nhân loại. Đây là một sự trừng phạt đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(44,12,'44:12','(Lúc đó), chúng (những kẻ vô đức tin) sẽ nói: “Lạy Thượng Đế của bầy tôi! Xin Ngài hãy lấy sự trừng phạt này đi khỏi bầy tôi, bầy tôi (giờ đây) thực sự là những người có đức tin.”');
INSERT INTO "vn_ruwwad" VALUES(44,13,'44:13','Làm thế nào sẽ có cho chúng một lời nhắc nhở (tại thời điểm đó)? Quả thật, một Sứ Giả đã đến với chúng một cách công khai (để cảnh báo chúng).');
INSERT INTO "vn_ruwwad" VALUES(44,14,'44:14','Nhưng chúng đã quay lưng lại với Y và nói: “Đồ học mót, kẻ khùng điên.”');
INSERT INTO "vn_ruwwad" VALUES(44,15,'44:15','Quả thật, TA sẽ lấy sự trừng phạt đi khỏi các ngươi trong một thời gian ngắn; nhưng chắc chắn các ngươi sẽ trở lại (vô đức tin).');
INSERT INTO "vn_ruwwad" VALUES(44,16,'44:16','Vào Ngày mà TA sẽ túm bắt chúng mạnh bạo. Quả thật, TA sẽ trừng phạt chúng.');
INSERT INTO "vn_ruwwad" VALUES(44,17,'44:17','Quả thật, trước chúng, TA đã xử lý đám người của Pha-ra-ông và đã phái một Sứ Giả vinh dự đến gặp chúng.');
INSERT INTO "vn_ruwwad" VALUES(44,18,'44:18','(Musa) nói (với Pha-ra-ông và thuộc hạ của hắn): “Quí ngài hãy giao lại những người bề tôi của Allah cho tôi. Tôi thực sự là một Sứ Giả được cử đến gặp quí ngài.”');
INSERT INTO "vn_ruwwad" VALUES(44,19,'44:19','“Quí ngài chớ đừng xem mình tối cao hơn Allah. Tôi đến gặp quí ngài với một thẩm quyền rõ rệt.”');
INSERT INTO "vn_ruwwad" VALUES(44,20,'44:20','“Tôi thực sự đã cầu xin Thượng Đế của tôi và cũng là Thượng Đế của quí ngài che chở khỏi bị quí ngài ném đá.”');
INSERT INTO "vn_ruwwad" VALUES(44,21,'44:21','“Nếu quí ngài không tin tôi thì quí ngài hãy để tôi yên.”');
INSERT INTO "vn_ruwwad" VALUES(44,22,'44:22','Thế là (cuối cùng), Musa đã cầu nguyện Thượng Đế của Y rằng những người này là một đám người tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(44,23,'44:23','(Allah phán bảo Musa): “Ngươi hãy đưa bầy tôi của TA ra đi trong đêm, các ngươi sẽ bị truy sát.”');
INSERT INTO "vn_ruwwad" VALUES(44,24,'44:24','“Ngươi hãy rời biển, để mặc cho nó rẽ đôi. Quả thật, chúng sẽ là một đội quân bị nhấn chìm.”');
INSERT INTO "vn_ruwwad" VALUES(44,25,'44:25','Biết bao vườn tược, bao con suối chúng đã bỏ lại!');
INSERT INTO "vn_ruwwad" VALUES(44,26,'44:26','Biết bao đồng ruộng, bao biệt thự cao sang!');
INSERT INTO "vn_ruwwad" VALUES(44,27,'44:27','Biết bao ân huệ mà chúng đã từng hưởng thụ!');
INSERT INTO "vn_ruwwad" VALUES(44,28,'44:28','Như thế đó, TA đã làm cho đám người khác thừa hưởng chúng.');
INSERT INTO "vn_ruwwad" VALUES(44,29,'44:29','Trời đất chẳng khóc thương cho chúng, và chúng cũng không được tạm tha.');
INSERT INTO "vn_ruwwad" VALUES(44,30,'44:30','Quả thật, TA đã giải cứu con cháu Israel thoát khỏi một sự trừng phạt nhục nhã,');
INSERT INTO "vn_ruwwad" VALUES(44,31,'44:31','Từ Pha-ra-ông. Quả thật, hắn là tên ngạo mạn trong số những kẻ ác đức và tàn bạo.');
INSERT INTO "vn_ruwwad" VALUES(44,32,'44:32','Quả thật TA đã chọn (con cháu Israel và ban cho chúng) kiến thức trội hơn thiên hạ.');
INSERT INTO "vn_ruwwad" VALUES(44,33,'44:33','TA đã ban cho chúng những đặc ân mà trong đó là một sự thử thách rõ rệt.');
INSERT INTO "vn_ruwwad" VALUES(44,34,'44:34','Quả thật, (những kẻ đa thần) này đã nói:');
INSERT INTO "vn_ruwwad" VALUES(44,35,'44:35','“Quả thật, chỉ có cái chết đầu tiên của chúng ta mà thôi và không có chuyện chúng ta được dựng sống lại.”');
INSERT INTO "vn_ruwwad" VALUES(44,36,'44:36','“Thế, các ngươi (hỡi Muhammad và những ai đi theo Ngươi) hãy mang ông bà tổ tiên của bọn ta đến đây xem nào nếu các ngươi nói thật!”');
INSERT INTO "vn_ruwwad" VALUES(44,37,'44:37','Phải chăng chúng tốt hơn đám dân của Tubba’ và những ai sống trước chúng hay sao?! TA đã tiêu diệt bọn chúng bởi vì bọn chúng là những kẻ tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(44,38,'44:38','TA đã không tạo hóa các tầng trời và trái đất cũng như mọi vật giữa chúng để vui chơi một cách vô nghĩa.');
INSERT INTO "vn_ruwwad" VALUES(44,39,'44:39','TA đã tạo ra (trời đất và vạn vật giữa chúng) là vì Chân Lý. Tuy nhiên, đa số bọn chúng không biết đấy thôi.');
INSERT INTO "vn_ruwwad" VALUES(44,40,'44:40','Thật vậy, Ngày Phân Loại là thời điểm đã được ấn định cho tất cả bọn chúng.');
INSERT INTO "vn_ruwwad" VALUES(44,41,'44:41','Ngày mà người thân thích này không giúp ích được gì cho người thân thích khác và chúng sẽ không được ai giúp đỡ.');
INSERT INTO "vn_ruwwad" VALUES(44,42,'44:42','Trừ những ai được Allah thương xót bởi vì Ngài là Đấng Toàn Năng, Đấng Nhân Từ.');
INSERT INTO "vn_ruwwad" VALUES(44,43,'44:43','Quả thật, cây Zaqqum, (một loại cây mọc lên từ đáy của Hỏa Ngục)');
INSERT INTO "vn_ruwwad" VALUES(44,44,'44:44','Sẽ là thức ăn của những kẻ tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(44,45,'44:45','(Trái của nó) giống như loại dầu đen sịt, sôi sục trong bụng,');
INSERT INTO "vn_ruwwad" VALUES(44,46,'44:46','Như nước sôi.');
INSERT INTO "vn_ruwwad" VALUES(44,47,'44:47','(Có tiếng bảo các Thiên Thần thực thi hình phạt): “Hãy túm bắt hắn lôi vào giữa đám lửa cháy bùng”');
INSERT INTO "vn_ruwwad" VALUES(44,48,'44:48','“Rồi đổ lên đầu hắn hình phạt của nước sôi.”');
INSERT INTO "vn_ruwwad" VALUES(44,49,'44:49','“Và hãy nhục mạ: ‘Hãy nếm đi, (trước kia) ngươi uy quyền và cao quý lắm kia mà!”');
INSERT INTO "vn_ruwwad" VALUES(44,50,'44:50','“Quả thật, (sự trừng phạt) này là điều mà các ngươi đã luôn hoài nghi.”');
INSERT INTO "vn_ruwwad" VALUES(44,51,'44:51','Riêng những người ngoan đạo chắc chắn sẽ ở một nơi an toàn (Thiên Đàng).');
INSERT INTO "vn_ruwwad" VALUES(44,52,'44:52','Giữa những ngôi vườn và các dòng suối.');
INSERT INTO "vn_ruwwad" VALUES(44,53,'44:53','Họ sẽ mặc (y phục bằng) lụa mịn và gấm thêu, họ ngồi đối mặt nhau.');
INSERT INTO "vn_ruwwad" VALUES(44,54,'44:54','Tương tự như thế, TA sẽ cho họ kết hôn với các tiên nữ có đôi mắt to (tuyệt đẹp).');
INSERT INTO "vn_ruwwad" VALUES(44,55,'44:55','Trong (Thiên Đàng), họ tự do gọi đủ loại hoa quả an toàn để dùng.');
INSERT INTO "vn_ruwwad" VALUES(44,56,'44:56','Họ sẽ không nếm trải cái chết ở đó ngoại trừ cái chết đầu tiên (ở trần gian); và (Allah) sẽ bảo vệ họ khỏi sự trừng phạt của Hỏa Ngục.');
INSERT INTO "vn_ruwwad" VALUES(44,57,'44:57','(Đó là) hồng phúc từ Thượng Đế của Ngươi. Đó là thành tựu vĩ đại.');
INSERT INTO "vn_ruwwad" VALUES(44,58,'44:58','Quả thật, TA đã làm cho (Qur’an) dễ (hiểu) bằng chính ngôn ngữ của Ngươi (Thiên Sứ) để chúng có thể ghi nhớ.');
INSERT INTO "vn_ruwwad" VALUES(44,59,'44:59','Thôi! Ngươi hãy chờ đợi (sự thắng lợi của Ngươi và sự bị hủy diệt của chúng); quả thật chúng cũng chờ đợi (như Ngươi).');
INSERT INTO "vn_ruwwad" VALUES(45,1,'45:1','Ha. Mim.');
INSERT INTO "vn_ruwwad" VALUES(45,2,'45:2','Kinh Sách được ban xuống từ nơi Allah, Đấng Toàn Năng, Đấng Toàn Tri.');
INSERT INTO "vn_ruwwad" VALUES(45,3,'45:3','Quả thật, trong các tầng trời và trái đất là các dấu hiệu (về quyền năng và tính duy nhất của Allah) cho những người có đức tin.');
INSERT INTO "vn_ruwwad" VALUES(45,4,'45:4','Trong việc tạo hóa các ngươi (hỡi nhân loại) cũng như việc (Allah) phân tán các sinh vật khắp nơi là các dấu hiệu cho những người vững chắc (trong đức tin).');
INSERT INTO "vn_ruwwad" VALUES(45,5,'45:5','(Trong) sự xen kẽ của ban đêm và ban ngày, trong việc Allah ban bổng lộc (mưa) từ trên trời xuống mà Ngài dùng để làm sống lại mảnh đất đã chết khô và trong việc xoay chuyển những cơn gió, là các dấu hiệu cho những người thông hiểu.');
INSERT INTO "vn_ruwwad" VALUES(45,6,'45:6','Đó là những Lời Mặc Khải của Allah, TA đọc chúng cho Ngươi (Thiên Sứ) bằng sự thật. Nếu họ chối bỏ Allah và các Lời Mặc Khải của Ngài, họ sẽ tin vào cách diễn ngôn (thông điệp) nào?');
INSERT INTO "vn_ruwwad" VALUES(45,7,'45:7','Thật khốn thay cho từng kẻ dối trá, tội lỗi.');
INSERT INTO "vn_ruwwad" VALUES(45,8,'45:8','Hắn nghe các Lời Mặc Khải của Allah đã được đọc cho hắn rồi vẫn ngoan cố kiêu ngạo như thể hắn không nghe thấy gì. Vậy, hãy báo cho hắn biết về một sự trừng phạt đau đớn (ở Đời Sau).');
INSERT INTO "vn_ruwwad" VALUES(45,9,'45:9','Khi hắn biết được điều gì từ các Lời Mặc Khải của TA, hắn lại mang nó ra chế giễu đùa cợt. Những kẻ đó sẽ phải chịu một trừng phạt đầy nhục nhã.');
INSERT INTO "vn_ruwwad" VALUES(45,10,'45:10','Trước mặt chúng là Hỏa Ngục; bất cứ điều gì mà chúng kiếm được (trên thế gian) cũng như những kẻ mà chúng nhận làm vị bảo hộ ngoài Allah sẽ không giúp ích gì cho chúng. (Rồi đây) chúng sẽ phải chịu một sự trừng phạt khủng khiếp.');
INSERT INTO "vn_ruwwad" VALUES(45,11,'45:11','(Kinh Qur’an) này là một nguồn chỉ đạo. Và những kẻ vô đức tin nơi các Lời Mặc Khải của Thượng Đế của chúng sẽ phải chịu một sự trừng phạt vô cùng đau đớn.');
INSERT INTO "vn_ruwwad" VALUES(45,12,'45:12','Allah là Đấng đã chế ngự biển cả cho các ngươi để các con tàu có thể chạy trên đó theo lệnh của Ngài và để các ngươi tìm kiếm hồng phúc của Ngài, mong rằng các ngươi biết tri ân (Ngài).');
INSERT INTO "vn_ruwwad" VALUES(45,13,'45:13','Ngài đã chế ngự mọi thứ trong các tầng trời và mọi thứ trên trái đất cho các ngươi (sử dụng), toàn bộ đều từ Ngài cả. Quả thật, trong sự việc đó là các dấu hiệu cho những người biết suy ngẫm.');
INSERT INTO "vn_ruwwad" VALUES(45,14,'45:14','Ngươi (hỡi Thiên Sứ) hãy nói với những người có đức tin nên tha thứ cho những kẻ không mong đợi những Ngày (Phán Xét) của Allah, (rồi đây) Ngài sẽ đáp trả cho từng nhóm người tương thích với những gì mà chúng đã làm.');
INSERT INTO "vn_ruwwad" VALUES(45,15,'45:15','Ai làm điều thiện thì được lợi cho bản thân mình còn ai làm điều xấu thì sẽ gặp quả báo. Rồi đây, các ngươi sẽ được đưa trở về trình diện Thượng Đế của các ngươi.');
INSERT INTO "vn_ruwwad" VALUES(45,16,'45:16','Quả thật, TA đã ban cho con cháu của Israel Kinh Sách, luật lệ, và sứ mạng Nabi; TA đã ban cho họ bổng lộc tốt lành và TA đã ưu đãi họ hơn những ai khác trong thiên hạ.');
INSERT INTO "vn_ruwwad" VALUES(45,17,'45:17','TA đã mang đến cho họ những bằng chứng rõ rệt về mọi vấn đề. Nhưng chỉ từ sau khi tiếp thu được kiến thức, họ đâm ra ganh ghét và chia rẽ nhau. Quả thật, Thượng Đế của Ngươi (Thiên Sứ Muhammad) sẽ giải quyết trường hợp của họ vào Ngày Phán Xét về những điều mà họ đã tranh chấp.');
INSERT INTO "vn_ruwwad" VALUES(45,18,'45:18','Rồi TA đặt Ngươi trên con đường của công việc (tôn giáo). Vì vậy, Ngươi hãy theo nó và đừng làm theo khuynh hướng của những kẻ không biết.');
INSERT INTO "vn_ruwwad" VALUES(45,19,'45:19','Quả thật, họ sẽ không bao giờ giúp được Ngươi thoát khỏi sự trừng phạt của Allah. Những kẻ làm điều sai quấy là đồng minh của nhau. Và Allah là Đấng Bảo Hộ của những người ngoan đạo.');
INSERT INTO "vn_ruwwad" VALUES(45,20,'45:20','(Qur’an) này là sự khai sáng cho nhân loại, là nguồn chỉ đạo và là hồng ân cho đám người vững chắc (trong đức tin).');
INSERT INTO "vn_ruwwad" VALUES(45,21,'45:21','Hoặc lẽ nào những kẻ làm điều tội lỗi nghĩ rằng TA sẽ để chúng và những người có đức tin và hành thiện ngang bằng nhau về cuộc sống và cái chết ư? Thật tệ thay cho điều chúng phán xét!');
INSERT INTO "vn_ruwwad" VALUES(45,22,'45:22','Allah tạo hóa các tầng trời và trái đất là vì chân lý. (Ngài tạo ra chúng) để mỗi linh hồn có thể được đền bù xứng đáng về những gì nó đã làm ra; và (các linh hồn) sẽ không bị đối xử bất công.');
INSERT INTO "vn_ruwwad" VALUES(45,23,'45:23','Ngươi (Thiên Sứ Muhammad) có thấy kẻ đã lấy dục vọng của bản thân làm thần linh của mình không? (Quả thật), Allah đã để y lầm lạc, Ngài đã niêm kín thính giác và quả tim của y và Ngài đã lấy tấm màn che mắt của y lại. Vậy, sau khi y chối bỏ Allah thì ai sẽ hướng dẫn y? Lẽ nào các ngươi không lưu tâm?');
INSERT INTO "vn_ruwwad" VALUES(45,24,'45:24','(Những kẻ phủ nhận Sự Phục Sinh) nói: “Chẳng có đời sống nào ngoài đời sống trần tục này của chúng ta, chúng ta chết và sống, không có gì hủy hoại chúng ta ngoại trừ thời gian.” (Những kẻ phủ nhận Sự Phục Sinh) chẳng có một chút hiểu biết nào về điều đó cả, chúng chỉ suy đoán mà thôi.');
INSERT INTO "vn_ruwwad" VALUES(45,25,'45:25','Khi các Lời Mặc Khải của TA được đọc cho chúng nghe như một bằng chứng rõ ràng, lập luận chống chế của chúng chỉ có mỗi câu nói: “Nếu các ngươi nói thật thì hãy mang ông bà tổ tiên của bọn ta trở lại trần gian xem nào!”');
INSERT INTO "vn_ruwwad" VALUES(45,26,'45:26','Ngươi (Thiên Sứ) hãy nói với chúng: “Allah làm cho các ngươi sống rồi làm cho các ngươi chết, sau đó, Ngài sẽ triệu tập các ngươi lại vào Ngày Phán Xét. (Đây là điều chắc chắn) không có gì phải hoài nghi.” Tuy nhiên, đa số nhân loại không biết.');
INSERT INTO "vn_ruwwad" VALUES(45,27,'45:27','Quyền thống trị các tầng trời và trái đất đều thuộc về một mình Allah. Vào Ngày mà Giờ Tận Thế được thiết lập, Ngày đó, những kẻ làm điều gian dối sẽ thua thiệt.');
INSERT INTO "vn_ruwwad" VALUES(45,28,'45:28','(Rồi đây, vào Ngày đó) Ngươi (Thiên Sứ) sẽ thấy mỗi cộng đồng sẽ quỳ gối; mỗi cộng đồng sẽ được gọi đến xem quyển sổ (ghi chép hành động) của mình (và sẽ được bảo): “Ngày hôm nay, các ngươi sẽ được đền bù xứng đáng về những điều (tốt, xấu) mà các ngươi đã làm (trên thế gian).”');
INSERT INTO "vn_ruwwad" VALUES(45,29,'45:29','“Đây là quyển sổ của TA, nó sẽ nói sự thật về các ngươi. Quả thật, TA đã cho ghi chép lại (toàn bộ) những điều các ngươi đã từng làm.”');
INSERT INTO "vn_ruwwad" VALUES(45,30,'45:30','Vì vậy, đối với những người có đức tin và hành thiện, Thượng Đế của họ sẽ thu nhận họ vào (Thiên Đàng) bằng lòng thương xót của Ngài. Đó là một thành tựu rõ ràng.');
INSERT INTO "vn_ruwwad" VALUES(45,31,'45:31','Nhưng đối với những kẻ vô đức tin (thì sẽ có lời bảo chúng): “Các Lời Mặc Khải của TA đã được đọc cho các ngươi nghe nhưng các ngươi đã kiêu ngạo và các ngươi là một đám người tội lỗi.”');
INSERT INTO "vn_ruwwad" VALUES(45,32,'45:32','“Khi có lời bảo: ‘Quả thật, lời hứa của Allah là sự thật và Giờ Tận Thế cũng là sự thật, không có gì phải hoài nghi’ thì các ngươi nói: ‘Bọn ta không biết Giờ Tận Thế là gì cả. Bọn ta nghĩ rằng đó chỉ là sự tưởng tượng. Bọn ta không cảm thấy thuyết phục.’”');
INSERT INTO "vn_ruwwad" VALUES(45,33,'45:33','(Rồi) những tội lỗi mà chúng đã làm sẽ xuất hiện trước mặt chúng và những điều mà chúng đã thường giễu cợt và nhạo báng sẽ bao vây chúng.');
INSERT INTO "vn_ruwwad" VALUES(45,34,'45:34','Và sẽ có lời bảo: “Ngày hôm nay, TA quên các ngươi giống như việc các ngươi đã quên cuộc gặp gỡ Ngày này của các ngươi. Chỗ ở của các ngươi sẽ là Hỏa Ngục và các ngươi sẽ không có ai giúp đỡ.”');
INSERT INTO "vn_ruwwad" VALUES(45,35,'45:35','“Sở dĩ như thế là vì các ngươi đã thường chế giễu các Lời Mặc Khải của Allah và đời sống trần tục đã đánh lừa các ngươi.” Vì vậy, vào Ngày đó chúng sẽ không được đưa ra khỏi nơi đó và chúng sẽ không được trở lại trần gian để sám hối và sửa mình.');
INSERT INTO "vn_ruwwad" VALUES(45,36,'45:36','Mọi lời ca tụng đều dâng lên Allah, Thượng Đế của các tầng trời, Thượng Đế của trái đất, Thượng Đế của vũ trụ và vạn vật.');
INSERT INTO "vn_ruwwad" VALUES(45,37,'45:37','Mọi quyền uy trong các tầng trời và trái đất đều thuộc một mình Ngài; và Ngài là Đấng Toàn Năng, Đấng Chí Minh.');
